INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'KWA JINA LA MWENYEZI MUNGU MWINGI WA REHEMA MWENYE KUREHEMU.'),
(2, 1, 2, 'Sifa njema zote ni za Mwenyezi Mungu, Mola Mlezi wa viumbe vyote;'),
(3, 1, 3, 'Mwingi wa Rehema Mwenye Kurehemu;'),
(4, 1, 4, 'Mwenye Kumiliki Siku ya Malipo.'),
(5, 1, 5, 'Wewe tu tunakuabudu, na Wewe tu tunakuomba msaada.'),
(6, 1, 6, 'Tuongoe njia iliyo nyooka,'),
(7, 1, 7, 'Njia ya ulio waneemesha, siyo ya walio kasirikiwa, wala walio potea.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Alif Lam Mim.'),
(9, 2, 2, 'Hiki ni Kitabu kisichokuwa na shaka ndani yake; ni uwongofu kwa wachamungu,'),
(10, 2, 3, 'Ambao huyaamini ya ghaibu na hushika Sala, na hutoa katika tuliyo wapa.'),
(11, 2, 4, 'Na ambao wanayaamini yaliyo teremshwa kwako, na yaliyo teremshwa kabla yako; na Akhera wana yakini nayo.'),
(12, 2, 5, 'Hao wapo juu ya uwongofu utokao kwa Mola wao Mlezi, na hao ndio walio fanikiwa.'),
(13, 2, 6, 'Hakika wale walio kufuru ni sawa kwao ukiwaonya au usiwaonye, hawaamini.'),
(14, 2, 7, 'Mwenyezi Mungu amepiga muhuri juu ya nyoyo zao na juu ya masikio yao, na juu ya macho yao pana kifuniko. Basi watapata adhabu kubwa.'),
(15, 2, 8, 'Na katika watu, wako wasemao: Tumemwamini Mwenyezi Mungu na Siku ya Mwisho, wala wao si wenye kuamini.'),
(16, 2, 9, 'Wanatafuta kumdanganya Mwenyezi Mungu na wale walio amini, lakini hawadanganyi ila nafsi zao; nao hawatambui.'),
(17, 2, 10, 'Nyoyoni mwao mna maradhi, na Mwenyezi Mungu amewazidishia maradhi. Basi watakuwa na adhabu chungu kwa sababu ya kusema kwao uwongo.'),
(18, 2, 11, 'Na wanapo ambiwa: Msifanye uharibifu ulimwenguni. Husema: Bali sisi ni watengenezaji.'),
(19, 2, 12, 'Hakika wao ndio waharibifu, lakini hawatambui.'),
(20, 2, 13, 'Na wanapo ambiwa: Aminini kama walivyo amini watu. Husema: Tuamini kama walivyo amini wapumbavu? Hakika wao ndio wapumbavu, lakini hawajui tu.'),
(21, 2, 14, 'Na wanapo kutana na walio amini husema: Tumeamini. Na wanapo kuwa peke yao na mashet\'ani wao husema: Hakika sisi tu pamoja nanyi. Hakika sisi tunawadhihaki tu.'),
(22, 2, 15, 'Mwenyezi Mungu atawadhihaki wao na atawawacha katika upotofu wao wakitangatanga ovyo.'),
(23, 2, 16, 'Hao ndio walio nunua upotofu kwa uwongofu; lakini biashara yao haikupata tija, wala hawakuwa wenye kuongoka.'),
(24, 2, 17, 'Mfano wao ni kama mfano wa aliye koka moto, na ulipo tanda mwangaza wake kote kote Mwenyezi Mungu aliiondoa nuru yao na akawawacha katika giza, hawaoni.'),
(25, 2, 18, 'Viziwi, mabubu, vipofu; kwa hivyo hawatarejea.'),
(26, 2, 19, 'Au ni kama mvua kubwa itokayo mbinguni, ina giza na radi na umeme; wakawa wakitia vidole vyao masikioni mwao kwa ajili ya mingurumo, kwa kuogopa kufa. Na Mwenyezi Mungu amewazunguka makafiri.'),
(27, 2, 20, 'Unakaribia umeme kunyakua macho yao. Kila ukiwatolea mwangaza huenda, na unapo wafanyia giza husimama. Na angelitaka Mwenyezi Mungu angeliondoa kusikia kwao na kuona kwao. Hakika Mwenyezi Mungu ni Muweza wa kila kitu.'),
(28, 2, 21, 'Enyi watu! Muabuduni Mola wenu Mlezi aliye kuumbeni nyinyi na wale wa kabla yenu, ili mpate kuokoka.'),
(29, 2, 22, '(Mwenyezi Mungu) ambaye amekufanyieni hii ardhi kuwa kama tandiko, na mbingu kama paa, na akateremsha maji kutoka mbinguni, na kwayo akatoa matunda yawe riziki zenu. Basi msimfanyizie Mwenyezi Mungu washirika, na hali nyinyi mnajua.'),
(30, 2, 23, 'Na ikiwa mna shaka kwa hayo tuliyo mteremshia mja wetu basi leteni sura moja ya mfano wake, na muwaite mashahidi wenu badala ya Mwenyezi Mungu, ikiwa mnasema kweli.'),
(31, 2, 24, 'Na mkitofanya - na wala hamtofanya kamwe - basi uogopeni moto ambao kuni zake ni watu na mawe walio andaliwa hao wanao kanusha.'),
(32, 2, 25, 'Na wabashirie walio amini na wakatenda mema kwamba watapata mabustani yapitayo mito kati yake; kila watapo pewa matunda humo, watasema: Haya ndiyo kama tuliyo pewa mbele. Na wataletewa matunda yaliyo fanana; na humo watakuwa na wake walio takasika; na wao humo watadumu.'),
(33, 2, 26, 'Hakika Mwenyezi Mungu haoni haya kutoa mfano hata wa mbu na ulio wa zaidi yake. Ama wale walio amini hujua ya kwamba hiyo ni haki iliyo toka kwa Mola wao Mlezi, lakini wale walio kufuru husema: Ni nini analo kusudia Mwenyezi Mungu kwa mfano huu? Kwa mfano huu huwapoteza wengi na huwaongoa wengi; lakini hawapotezi ila wale wapotovu,'),
(34, 2, 27, 'Wanao vunja ahadi ya Mwenyezi Mungu baada ya kwisha ifunga, na wakayakata aliyo amrisha Mwenyezi Mungu kuungwa, na wakafanya uharibifu katika nchi; hao ndio wenye khasara.'),
(35, 2, 28, 'Vipi mnamkanusha Mwenyezi Mungu na hali mlikuwa wafu akakufufueni! Kisha atakufisheni, tena atakufufueni, kisha kwake mtarejeshwa?.'),
(36, 2, 29, 'Yeye ndiye aliye kuumbieni vyote vilivyomo katika ardhi. Tena akazielekea mbingu, na akazifanya mbingu saba. Naye ndiye Mjuzi wa kila kitu.'),
(37, 2, 30, 'Na pale Mola wako Mlezi alipo waambia Malaika: Mimi nitamweka katika ardhi Khalifa (mfwatizi), wakasema: Utaweka humo atakaye fanya uharibifu humo na kumwaga damu, hali sisi tunakutakasa kwa sifa zako na tunakutaja kwa utakatifu wako? Akasema: Hakika Mimi nayajua msiyo yajua.'),
(38, 2, 31, 'Na akamfundisha Adam majina ya vitu vyote, kisha akaviweka mbele ya Malaika, na akasema: Niambieni majina ya hivi ikiwa mnasema kweli.'),
(39, 2, 32, 'Wakasema: Subhanaka, Wewe umetakasika! Hatuna ujuzi isipokuwa kwa uliyo tufunza Wewe. Hakika Wewe ndiye Mjuzi Mwenye hikima.'),
(40, 2, 33, 'Akasema: Ewe Adam! Waambie majina yake. Basi alipo waambia majina yake alisema: Sikukwambieni kwamba Mimi ninajua siri za mbinguni na za duniani, na ninayajua mnayo yadhihirisha na mliyo kuwa mnayaficha?'),
(41, 2, 34, 'Na tulipo waambia Malaika: Msujudieni Adam, wakamsujudia wote isipo kuwa Iblis, alikataa na akajivuna na akawa katika makafiri.'),
(42, 2, 35, 'Na tulisema: Ewe Adam! Kaa wewe na mkeo katika Bustani, na kuleni humo maridhawa popote mpendapo, lakini msiukaribie mti huu tu; mkawa katika wale walio dhulumu.'),
(43, 2, 36, 'Lakini Shet\'ani aliwatelezesha hao wawili na akawatoa katika waliyo kuwamo, na tukasema: Shukeni, nanyi ni maadui nyinyi kwa nyinyi. Katika ardhi yatakuwa makaazi yenu na starehe kwa muda.'),
(44, 2, 37, 'Kisha Adam akapokea maneno kwa Mola wake Mlezi, na Mola wake Mlezi alimkubalia toba yake; hakika Yeye ndiye Mwingi wa kukubali toba na Mwenye kurehemu.'),
(45, 2, 38, 'Tukasema: Shukeni nyote; na kama ukikufikieni uwongofu utokao kwangu, basi watakao fuata uwongofu wangu huo haitakuwa khofu juu yao wala hawatahuzunika.'),
(46, 2, 39, 'Na wenye kukufuru na kuzikanusha ishara zetu, hao ndio watakao kuwa watu wa Motoni, humo watadumu.'),
(47, 2, 40, 'Enyi Wana wa Israili! Kumbukeni neema yangu niliyo kuneemesheni, na timizeni ahadi yangu, na Mimi nitatimiza ahadi yenu, na niogopeni Mimi tu.'),
(48, 2, 41, 'Na aminini niliyo yateremsha ambayo yanasadikisha mliyo nayo, wala msiwe wa kwanza kuyakataa. Wala msiuze Ishara zangu kwa thamani ndogo. Na niogopeni Mimi tu.'),
(49, 2, 42, 'Wala msichanganye kweli na uwongo na mkaificha kweli nanyi mnajua.'),
(50, 2, 43, 'Na shikeni Sala, na toeni Zaka, na inameni pamoja na wanao inama.'),
(51, 2, 44, 'Je! Mnawaamrisha watu mema na mnajisahau nafsi zenu, na hali nyinyi mnasoma Kitabu? Basi je, hamzingatii?'),
(52, 2, 45, 'Na tafuteni msaada kwa kusubiri na kwa kusali; na kwa hakika jambo hilo ni gumu isipokuwa kwa wanyenyekevu,'),
(53, 2, 46, 'Ambao wana yakini kuwa hakika watakutana na Mola wao Mlezi na ya kuwa hakika watarejea kwake.'),
(54, 2, 47, 'Enyi Wana wa Israili! Kumbukeni zile neema zangu nilizo kuneemesheni, na nikakuteuweni kuliko wote wengineo.'),
(55, 2, 48, 'Na iogopeni Siku ambayo mtu hatomfaa mtu kwa lolote, wala hayatakubaliwa kwake maombezi, wala hakitapokewa kikomboleo kwake; wala hawatanusuriwa.'),
(56, 2, 49, 'Na vile tulipo kuokoeni kwa watu wa Firauni walio kupeni adhabu mbaya, wakiwachinja wana wenu wa kiume na wakawawacha hai wanawake; na katika hayo ulikuwa ni mtihani mkubwa ulio toka kwa Mola wenu Mlezi.'),
(57, 2, 50, 'Na tulipo ipasua bahari kwa ajili yenu na tukakuokoeni, tukawazamisha watu wa Firauni, na huku mnatazama.'),
(58, 2, 51, 'Na tulipo muahidi Musa masiku arubaini, kisha mkachukua ndama (mkamuabudu) baada yake, na mkawa wenye kudhulumu.'),
(59, 2, 52, 'Kisha tukakusameheni baada ya hayo, ili mpate kushukuru.'),
(60, 2, 53, 'Na tulipo mpa Musa Kitabu na pambanuo ili mpate kuongoka.'),
(61, 2, 54, 'Na Musa alipo waambia watu wake: Enyi watu wangu! Hakika nyinyi mmezidhulumu nafsi zenu kwa kuchukua kwenu ndama (kumuabudu). Basi tubuni kwa Muumba wenu na ziueni nafsi zenu. Haya ni bora kwenu mbele ya Muumba wenu. Naye akapokea toba yenu. Hakika Yeye ndiye apokeaye toba na Mwenye kurehemu.'),
(62, 2, 55, 'Na mlipo sema: Ewe Musa! Hatutakuamini mpaka tumwone Mwenyezi Mungu waziwazi. Ikakunyakueni radi nanyi mnaangalia.'),
(63, 2, 56, 'Kisha tukakufufueni baada ya kufa kwenu, ili mpate kushukuru.'),
(64, 2, 57, 'Na tukakutilieni kivuli kwa mawingu na tukakuteremshieni Manna na Salwa; tukakwambieni: Kuleni vitu vizuri hivi tulivyo kuruzukuni. Nao hawakutudhulumu Sisi lakini walikuwa wanajidhulumu nafsi zao.'),
(65, 2, 58, 'Na tulipo sema: Ingieni mji huu, na humo mle mpendapo maridhawa, na ingieni katika mlango wake kwa unyenyekevu, na semeni: Tusamehe! Tutakusameheni makosa yenu, na tutawazidishia wema wafanyao wema.'),
(66, 2, 59, 'Lakini walio dhulumu waliibadili kauli isiyo kuwa waliyo ambiwa. Kwa hivyo tukaiteremsha juu ya wale walio dhulumu adhabu kutoka mbinguni kwa sababu ya walivyo kuwa wamepotoka.'),
(67, 2, 60, 'Na Musa alipo omba maji kwa ajili ya watu wake, tulimwambia: Lipige jiwe kwa fimbo yako. Mara zikatimbuka chemchem kumi na mbili; kila kabila ikajua mahali pake pa kunywea. Tukawaambia: Kuleni na mnywe riziki ya Mwenyezi Mungu, wala msiasi katika nchi mkafanya uharibifu.'),
(68, 2, 61, 'Na mlipo sema: Ewe Musa! Hatuwezi kuvumilia chakula cha namna moja tu, basi tuombee kwa Mola wako Mlezi atutolee vile vinavyo mea katika ardhi, kama mboga zake, na matango yake, na thom zake, na adesi zake, na vitunguu vyake. Akasema: Mnabadili kilicho duni kwa kilicho bora? Shukeni mjini, huko mtapata mlivyo viomba. Na wakapigwa na unyonge, na umasikini, na wakastahili ghadhabu ya Mwenyezi Mungu. Na haya ni kwa sababu walikuwa wakiyakataa maneno ya Mwenyezi Mungu, na wakiwauwa Manabii pasipo haki. Hayo ni kwa walivyo asi na wakapindukia mipaka.'),
(69, 2, 62, 'Hakika Walio amini, na Mayahudi na Wakristo, na Wasabai; yeyote atakaye muamini Mwenyezi Mungu na Siku ya Mwisho na akatenda mema basi watapata malipwa yao kwa Mola wao Mlezi, wala haitakuwa khofu juu yao, wala hawatahuzunika.'),
(70, 2, 63, 'Na tulipo chukua ahadi yenu, na tukaunyanyua mlima juu yenu (tukakwambieni): Shikeni kwa nguvu haya tuliyo kupeni, na yakumbukeni yaliyomo ndani yake ili mpate kujilinda.'),
(71, 2, 64, 'Kisha mligeuka baada ya haya. Na lau kuwa si fadhila za Mwenyezi Mungu juu yenu na rehema zake, mngeli kuwa miongoni mwa wenye kukhasirika.'),
(72, 2, 65, 'Na hakika mlikwisha yajua ya wale miongoni mwenu walio ivunja Sabato, (siku ya mapumziko, Jumaa Mosi) na tukawaambia: Kuweni manyani wadhalilifu.'),
(73, 2, 66, 'Kwa hivyo tukayafanya hayo kuwa onyo kwa wale walio kuwa katika zama zao na walio kuja baada yao, na mawaidha kwa wachamngu.'),
(74, 2, 67, 'Na Musa alipo waambia watu wake: Hakika Mwenyezi Mungu anakuamrisheni mchinje ng\'ombe. Wakasema: Je! Unatufanyia mzaha? Akasema: Audhubil lahi! Najikinga kwa Mwenyezi Mungu nisiwe miongoni mwa wajinga.'),
(75, 2, 68, 'Wakasema: Tuombee kwa Mola wako Mlezi atupambanulie ni ng\'ombe gani? Akasema: Hakika Yeye anasema kwamba ng\'ombe huyo si mpevu, wala si kinda, bali ni wa katikati baina ya hao. Basi fanyeni mnavyo amrishwa.'),
(76, 2, 69, 'Wakasema: Tuombee kwa Mola wako atupambanulie nini rangi yake? Akasema: Yeye anasema, kuwa ng\'ombe huyo ni wa manjano, na rangi yake imekoza, huwapendeza wanao mtazama.'),
(77, 2, 70, 'Wakasema: Tuombee kwa Mola wako Mlezi atupambanulie ni yupi? Hakika tunaona ng\'ombe wamefanana. Na kwa yakini, Inshaallah, Mwenyezi Mungu akipenda, tutaongoka.'),
(78, 2, 71, 'Akasema: Yeye anasema: Kuwa huyo ni ng\'ombe asiye tiwa kazini kulima ardhi wala kumwagia maji mimea, mzima, hana baka. Wakasema: Sasa umeleta haki. Basi wakamchinja, na walikaribia kuwa wasifanye hayo.'),
(79, 2, 72, 'Na mlipo muuwa mtu, kisha mkakhitalifiana kwayo, na Mwenyezi Mungu ni mwenye kuyatoa hayo mliyo kuwa mkiyaficha.'),
(80, 2, 73, 'Tukasema: Mpigeni kwa kipande chake (huyo ng\'ombe). Ndivyo hivi Mwenyezi Mungu huwahuisha wafu; na anakuonyesheni Ishara zake ili mpate kufahamu.'),
(81, 2, 74, 'Kisha nyoyo zenu zikawa ngumu baada ya hayo, hata zikawa kama mawe au ngumu zaidi; kwani kuna mawe yanayo timbuka mito, na kuna mengine yanayo pasuka yakatoka maji ndani yake, na kuna mengine huanguka kwa sababu ya khofu ya Mwenyezi Mungu. Na Mwenyezi Mungu si mwenye kughafilika na mnayo yafanya.'),
(82, 2, 75, 'Mnatumai watakuaminini na hali baadhi yao walikuwa wakisikia maneno ya Mwenyezi Mungu, kisha wakayabadili baada ya kuwa wameyafahamu, na hali wanajua?'),
(83, 2, 76, 'Na wanapo kutana na wale walio amini husema: Tumeamini. Na wanapo kuwa peke yao, wao kwa wao, husema: Mnawaambia aliyo kufunulieni Mwenyezi Mungu ili wapate kukuhojieni mbele ya Mola wenu Mlezi? Basi hamfahamu nyinyi?'),
(84, 2, 77, 'Hawajui ya kwamba Mwenyezi Mungu anayajua wanayo yaficha na wanayo yatangaza?'),
(85, 2, 78, 'Na wako miongoni mwao wasio jua kusoma; hawakijui Kitabu isipo kuwa uwongo wanao utamani, nao hawana ila ni kudhania tu.'),
(86, 2, 79, 'Basi ole wao wanao andika kitabu kwa mikono yao, kisha wakasema: Hiki kimetoka kwa Mwenyezi Mungu, ili wachumie kwacho pato dogo. Basi ole wao kwa yale iliyo andika mikono yao, na ole wao kwa yale wanayo yachuma.'),
(87, 2, 80, 'Na walisema: Hautatugusa Moto ila kwa siku chache tu. Sema: Mmechukua ahadi kwa Mwenyezi Mungu? Kweli Mwenyezi Mungu hatokwenda kinyume na ahadi yake. Au mnamsingizia Mwenyezi Mungu mambo msiyo yajua?'),
(88, 2, 81, 'Naam, anaye chuma ubaya - na makosa yake yakamzunguka - hao ndio watu wa Motoni; humo watadumu.'),
(89, 2, 82, 'Na wale walio amini na wakatenda mema, hao ndio watu wa Peponi, humo watadumu.'),
(90, 2, 83, 'Na tulipo funga agano na Wana wa Israili: Hamtamuabudu yeyote ila Mwenyezi Mungu; na muwafanyie wema wazazi na jamaa na mayatima na masikini, na semeni na watu kwa wema, na shikeni Sala, na toeni Zaka. Kisha mkageuka isipo kuwa wachache tu katika nyinyi; na nyinyi mnapuuza.'),
(91, 2, 84, 'Na tulipo chukua agano lenu kuwa hamtamwaga damu zenu, wala hamtatoana majumbani mwenu; nanyi mkakubali nanyi mnashuhudia.'),
(92, 2, 85, 'Kisha nyinyi kwa nyinyi mnauwana, na mnawatoa baadhi yenu majumbani kwao, mkisaidiana kwa dhambi na uadui. Na wakikujieni mateka mnawakomboa, na hali kuwatoa mmekatazwa. Je! Mnaamini baadhi ya Kitabu na mnakataa baadhi yake? Basi hana malipo mwenye kutenda hayo miongoni mwenu ila hizaya katika maisha ya duniani, na Siku ya Kiyama watapelekwa kwenye adhabu kali kabisa. Na Mwenyezi Mungu si mwenye kughafilika na yale mnayo yatenda.'),
(93, 2, 86, 'Hao ndio walio nunua uhai wa dunia kwa (uhai wa) Akhera; kwa hivyo hawatapunguziwa adhabu wala hawatanusuriwa.'),
(94, 2, 87, 'Na hakika tulimpa Musa Kitabu na tukafuatisha baada yake Mitume wengine. Na tukampa Isa, mwana wa Mariamu, hoja zilizo waziwazi, na tukamtia nguvu kwa Roho Takatifu. Basi kila walipo kufikieni Mitume kwa yale ambayo hayapendwi na nafsi zenu, mlijivuna; wengine mkawakanusha, na wengine mkawauwa.'),
(95, 2, 88, 'Na walisema: Nyoyo zetu zimefunikwa. Bali Mwenyezi Mungu amewalaani kwa kufuru zao: kwa hivyo ni kidogo tu wanayo yaamini.'),
(96, 2, 89, 'Na kilipo wajia Kitabu kitokacho kwa Mwenyezi Mungu kinacho thibitisha waliyo nayo - na wao walikuwa wakitafutia ushindi kuwashinda makafiri - yalipo wajia yale waliyo kuwa wakiyajua waliyakanusha. Basi laana ya Mwenyezi Mungu juu ya wakanushao!'),
(97, 2, 90, 'Kiovu kweli walicho jiuzia nafsi zao, nacho ni kule kukataa kwao aliyo yateremsha Mwenyezi Mungu, kwa ajili ya kuona maya kwa kuwa Mwenyezi Mungu humteremshia fadhila yake amtakaye miongoni mwa waja wake. Basi wamejiletea wenyewe ghadhabu juu ya ghadhabu. Na makafiri watapata mateso ya kudhalilisha.'),
(98, 2, 91, 'Na wanapo ambiwa: Yaaminini aliyo yateremsha Mwenyezi Mungu. Wao husema: Tunaamini tuliyo teremshiwa sisi. Na huyakataa yasiyo kuwa hayo. Na hali ya kuwa hii ndiyo Haki inayo thibitisha yale waliyo nayo wao. Sema: Mbona mliwauwa Manabii wa Mwenyezi Mungu hapo zamani ikiwa kweli mlikuwa Waumini?'),
(99, 2, 92, 'Na alikufikieni Musa na hoja zilizo waziwazi, kisha mkamchukua ndama (kumuabudu) baada yake, na mkawa wenye kudhulumu.'),
(100, 2, 93, 'Na tulipo chukua ahadi yenu na tukauinua mlima juu yenu (tukakwambieni): Kamateni kwa nguvu haya tuliyo kupeni na sikieni. Wakasema: Tumesikia na tumekataa! Na wakanyweshwa nyoyoni mwao (imani ya) ndama kwa kufuru yao. Sema: Ni uovu mno iliyo kuamrisheni imani yenu ikiwa ni wenye kuamini.'),
(101, 2, 94, 'Sema: Ikiwa nyumba ya Akhera iliyoko kwa Mwenyezi Mungu ni yenu tu bila ya watu wengine, basi tamanini mauti kama nyinyi mnasema kweli.'),
(102, 2, 95, 'Wala hawatayatamani kamwe kwa sababu ya yale yaliyo tangulizwa na mikono yao; na Mwenyezi Mungu ni mwenye kuwajua vyema wanao dhulumu.'),
(103, 2, 96, 'Na hakika utawaona ni wenye kuwashinda watu wote kwa pupa ya kuishi, na kuliko washirikina. Kila mmoja wao anatamani lau angeli pewa umri wa miaka elfu. Wala hayo ya kuzidishiwa umri hayamuondoshei adhabu; na Mwenyezi Mungu anayaona wanayo yatenda.'),
(104, 2, 97, 'Sema: Aliye kuwa ni adui wa Jibril, basi huyo ndiye aliye iteremsha Qur\'ani moyoni mwako kwa idhini ya Mwenyezi Mungu, inayo thibitisha yaliyo kuwa kabla yake, na ni uwongofu na bishara njema kwa Waumini.'),
(105, 2, 98, 'Aliye kuwa adui wa Mwenyezi Mungu na Malaika wake na Mitume wake na Jibril na Mikail, basi hakika Mwenyezi Mungu ni adui wa makafiri.'),
(106, 2, 99, 'Hakika tumekuteremshia Ishara zilizo wazi na hapana wanao zikataa ila wapotovu.'),
(107, 2, 100, 'Je! Ati ndio kila wanapo funga ahadi huwapo kikundi miongoni mwao kikaivunja? Bali wengi wao hawaamini.'),
(108, 2, 101, 'Na alipo wajia Mtume kutoka kwa Mwenyezi Mungu, mwenye kuthibitisha yale waliyo nayo, kundi moja miongoni mwa wale walio pewa Kitabu lilitupa Kitabu cha Mwenyezi Mungu hicho nyuma ya migongo yao kama kwamba hawajui.'),
(109, 2, 102, 'Na wakafuata yale waliyo zua mashet\'ani kuuzulia ufalme wa Su leiman. Na wala Suleiman hakukufuru, bali mashet\'ani ndio walio kufuru, wakiwafundisha watu uchawi, na yaliyo teremshwa kwa Malaika wawili, Haaruta na Maaruta katika Baabil. Wala hao hawakumfundisha yeyote mpaka wamwambie: Hakika sisi ni mtihani; basi usikufuru. Wakajifunza kwa hao wawili yale ya kumfarikisha mtu na mkewe. Wala hawawezi kumdhuru mtu ila kwa kutaka Mwenyezi Mungu. Na wanajifunza yatayo wadhuru wala hayawanufaishi. Na hakika wanajua kwamba aliye khiari haya hatakuwa na fungu lolote katika Akhera. Na hakika ni kiovu mno walicho jiuzia nafsi zao laiti wangelijua.'),
(110, 2, 103, 'Na lau kuwa wangeamini na wakaogopa, bila ya shaka malipo yatokayo kwa Mwenyezi Mungu yange kuwa bora. Laiti wangeli kuwa wanajua!'),
(111, 2, 104, 'Enyi mlio amini! Msiseme: "Raa\'ina", na semeni: "Ndhurna". Na sikieni! Na makafiri watapata adhabu chungu.'),
(112, 2, 105, 'Walio kufuru miongoni mwa watu wa Kitabu na washirikina hawapendi mteremshiwe kheri yoyote kutoka kwa Mola wenu Mlezi. Na Mwenyezi Mungu humkusudia kumpa rehema yake ampendaye. Na Mwenyezi Mungu ni mwenye fadhila kubwa.'),
(113, 2, 106, 'Ishara yoyote tunayo ifuta au tunayo isahaulisha tunailetea iliyo bora kuliko hiyo, au iliyo mfano wake. Hujui kwamba Mwenyezi Mungu ni Muweza wa kila kitu?'),
(114, 2, 107, 'Hujui kwamba Mwenyezi Mungu ana ufalme wa mbingu na ardhi? Nanyi hamna mlinzi wala msaidizi isipo kuwa Mwenyezi Mungu.'),
(115, 2, 108, 'Au mnataka kumwomba Mtume wenu kama alivyo ombwa Musa zamani? Na anaye badilisha Imani kwa ukafiri bila ya shaka huyo ameipotea njia iliyo sawa.'),
(116, 2, 109, 'Wengi miongoni mwa watu wa Kitabu wanatamani lau wange kurudisheni nyinyi muwe makafiri baada ya kuamini kwenu, kwa ajili ya uhasidi uliomo ndani ya nafsi zao, baada ya kwisha wapambanukia Haki. Basi sameheni na wachilieni mbali mpaka Mwenyezi Mungu atapo leta amri yake. Hakika Mwenyezi Mungu ni Muweza wa kila kitu.'),
(117, 2, 110, 'Na shikeni Sala na toeni Zaka; na kheri mtakazo jitangulizia nafsi zenu mtazikuta kwa Mwenyezi Mungu. Hakika Mwenyezi Mungu anayaona mnayo yafanya.'),
(118, 2, 111, 'Na walisema: Hataingia Peponi ila aliyekuwa Yahudi au Mkristo. Hayo ni matamanio yao. Sema: Leteni ushahidi wenu kama nyinyi ni wasema kweli.'),
(119, 2, 112, 'Sivyo hivyo! Yeyote anaye elekeza uso wake kwa Mwenyezi Mungu naye ni mtenda mema, basi ana malipo yake kwa Mola wake Mlezi, wala haitakuwa khofu juu yao wala hawatahuzunika.'),
(120, 2, 113, 'Na Mayahudi husema: Wakristo hawana lao jambo. Na Wakristo husema: Mayahudi hawana lao jambo. Na ilhali wote wanasoma Kitabu hicho hicho (Biblia). Na kadhaalika walisema wale wasio jua kitu mfano wa kauli yao hii. Basi Mwenyezi Mungu atahukumu baina yao Siku ya Kiyama katika yale waliyo kuwa wakikhitilafiana.'),
(121, 2, 114, 'Na ni nani dhaalimu mkubwa kuliko yule anaye zuia misikiti ya Mwenyezi Mungu kutajwa ndani yake jina lake na akajitahidi kuiharibu? Watu hao haitawafalia kuingia humo ila nao wanaogopa. Duniani watapata hizaya na Akhera watapata adhabu kubwa.'),
(122, 2, 115, 'Na mashariki na magharibi ni za Mwenyezi Mungu. Basi kokote mnako elekea, huko Mwenyezi Mungu yupo. Hakika Mwenyezi Mungu ni mwenye wasaa na Mjuzi.'),
(123, 2, 116, 'Na ati wanasema: Mwenyezi Mungu ana mwana. Subhanahu! Ametakasika na hayo. Bali vyote viliomo mbinguni na duniani ni vyake. Vyote vinamt\'ii Yeye.'),
(124, 2, 117, 'Yeye ndiye Muumba wa mbingu na ardhi pasina ruwaza; na anapo taka jambo basi huliambia tu: Kuwa! Nalo huwa.'),
(125, 2, 118, 'Na walisema wale wasio jua kitu: Mbona Mwenyezi Mungu hasemi nasi au zikatufikia Ishara. Kama hivyo walisema wale walio kuwa kabla yao mfano wa kauli yao hii. Nyoyo zao zimefanana. Hakika Sisi tumeziweka wazi Ishara kwa watu wenye yakini.'),
(126, 2, 119, 'Hakika Sisi tumekutuma kwa Haki uwe mbashiri, na mwonyaji. Na wala hutaulizwa juu ya watu wa Motoni.'),
(127, 2, 120, 'Mayahudi hawawi radhi nawe, wala Wakristo, mpaka ufuate mila yao. Sema: Hakika uwongofu wa Mwenyezi Mungu ndio uwongofu. Na kama ukifuata matamanio yao baada ya ujuzi ulio kwisha kujia, hutapata mlinzi wala msaidizi kwa Mwenyezi Mungu.'),
(128, 2, 121, 'Wale tulio wapa Kitabu, wakakisoma kama ipasavyo kusomwa, hao ndio kweli wanakiamini. Na wanao kikataa basi hao ndio wenye kukhasiri.'),
(129, 2, 122, 'Enyi Wana wa Israili! Kumbukeni neema yangu niliyo kuneemesheni, na hakika Mimi nikakufadhilisheni kuliko wengineo wote.'),
(130, 2, 123, 'Na iogopeni siku ambayo hatamfaa mtu mwenziwe kwa lolote, wala hakitakubaliwa kwake kikomboleo, wala maombezi hayatamfaa, wala hawatanusuriwa.'),
(131, 2, 124, 'Na Mola wake Mlezi alipo mjaribu Ibrahim kwa kumpa amri fulani, naye akazitimiza, akamwambia: Hakika Mimi nitakufanya uwe mwongozi wa watu. Akasema: Je, na katika vizazi vyangu pia? Akasema: Ahadi yangu haitawafikia wenye kudhulumu.'),
(132, 2, 125, 'Na kumbukeni tulipo ifanya ile Nyumba (ya Alkaaba) iwe pahala pa kukusanyikia watu na pahala pa amani. Na alipo kuwa akisimama Ibrahim pafanyeni pawe pa kusalia. Na tuliagana na Ibrahim na Ismail: Itakaseni Nyumba yangu kwa ajili ya wanao izunguka kwa kut\'ufu na wanao jitenga huko kwa ibada, na wanao inama na kusujudu.'),
(133, 2, 126, 'Na alipo sema Ibrahim: Ee Mola wangu Mlezi! Ufanye huu uwe mji wa amani, na uwaruzuku watu wake matunda, wale wanao muamini Mwenyezi Mungu na Siku ya Mwisho. Akasema: Na mwenye kukufuru pia nitamstarehesha kidogo, kisha nitamsukumiza kwenye adhabu ya Moto, napo ni pahala pabaya mno pa kurejea.'),
(134, 2, 127, 'Na kumbukeni Ibrahim na Ismail walipo inyanyua misingi ya ile Nyumba wakaomba: Ewe Mola Mlezi wetu! Tutakabalie! Hakika Wewe ndiye Msikizi Mjuzi.'),
(135, 2, 128, 'Ewe Mola Mlezi wetu! Tujaalie tuwe ni wenye kusilimu kwako, na pia miongoni mwa vizazi vyetu wawe umma ulio silimu kwako. Na utuonyeshe njia za ibada yetu na utusamehe. Bila shaka Wewe ndiye Mwenye kusamehe Mwenye kurehemu.'),
(136, 2, 129, 'Ewe Mola Mlezi wetu! Waletee Mtume anaye tokana na wao, awasomee Aya zako, na awafundishe Kitabu na hikima na awatakase. Hakika Wewe ndiye Mwenye nguvu, Mwenye hikima.'),
(137, 2, 130, 'Na nani atajitenga na mila ya Ibrahim isipo kuwa anayeitia nafsi yake katika upumbavu? Na kwa yakini Sisi tulimteuwa yeye katika dunia; na hakika yeye katika Akhera atakuwa miongoni mwa watu wema.'),
(138, 2, 131, 'Na Mola wake Mlezi alipo mwambia: Silimu, Nyenyekea! Alinena: Nimesilimu, nimenyenyekea kwa Mola wa walimwengu wote.'),
(139, 2, 132, 'Na Ibrahim akawausia wanawe, na pia Yaaqub: Enyi wanangu! Hakika Mwenyezi Mungu amekuteulieni Dini hii; basi msife ila nanyi mmekuwa Waislamu, wanyenyekevu.'),
(140, 2, 133, 'Je! Mlikuwapo yalipo mfikia Yaaqub mauti, akawaambia wanawe: Mtamuabudu nani baada yangu? Wakasema: Tutamuabudu Mungu wako na Mungu wa baba zako, Ibrahim na Ismail na Is- hak, Mungu Mmoja tu, na sisi tunasilimu kwake.'),
(141, 2, 134, 'Hao ni watu walio kwisha pita. Watapata waliyo yachuma, nanyi mtapata mtakayo yachuma; wala hamtaulizwa nyinyi waliyo kuwa wakiyafanya wao.'),
(142, 2, 135, 'Na wakasema: Kuweni Mayahudi au Wakristo ndio mtaongoka. Sema: Bali tunashika mila ya Ibrahim mwongofu, wala hakuwa katika washirikina.'),
(143, 2, 136, 'Semeni nyinyi: Tumemuamini Mwenyezi Mungu na yale tuliyo teremshiwa sisi, na yaliyo teremshwa kwa Ibrahim na Ismail na Is-hak na Yaakub na wajukuu zake, na waliyo pewa Musa na Isa, na pia waliyo pewa Manabii wengine kutoka kwa Mola wao Mlezi; hatutafautishi baina ya yeyote katika hao, na sisi tumesilimu kwake.'),
(144, 2, 137, 'Basi wakiamini kama mnavyo amini nyinyi, itakuwa kweli wameongoka. Na wakikengeuka basi wao wamo katika upinzani tu. Na Mwenyezi Mungu atakutosheni kukukingeni na shari yao, na Yeye ndiye Msikizi, Mjuzi.'),
(145, 2, 138, 'Huko ndiko kupaka rangi kwa Mwenyezi Mungu, na nani aliye mbora wa kupaka rangi kuliko Mwenyezi Mungu? Na sisi ni wenye kumuabudu Yeye tu.'),
(146, 2, 139, 'Sema: Mnahojiana nasi juu ya Mwenyezi Mungu, naye ni Mola wetu Mlezi na Mola wenu Mlezi, na sisi tuna vitendo vyetu na nyinyi mna vitendo vyenu? Nasi tunamsafia Yeye tu.'),
(147, 2, 140, 'Au mnasema kuwa Ibrahim na Ismail na Is-hak na Yaakub na wajukuu zao walikuwa Mayahudi au Wakristo? Sema: Je, nyinyi mnajua zaidi au Mwenyezi Mungu? Na ni nani mwenye kudhulumu zaidi kuliko yule aliye ficha ushahidi alio nao utokao kwa Mwenyezi Mungu? Na Mwenyezi Mungu si mwenye kughafilika na hayo myafanyayo.'),
(148, 2, 141, 'Hao ni watu walio kwisha pita. Wao watapata waliyo yachuma, na nyinyi mt apata mliyo yachuma, wala hamtaulizwa nyinyi yale waliyo kuwa wakifanya wao.'),
(149, 2, 142, 'WAPUMBAVU miongoni mwa watu watasema: Nini kilicho wageuza kutoka kibla chao walicho kuwa wakikielekea? Sema: Mashariki na Magharibi ni ya Mwenyezi Mungu; Yeye humwongoa amtakaye kwenye njia iliyo nyooka.'),
(150, 2, 143, 'Na vivyo hivyo tumekufanyeni muwe Umma wa wasitani, ili muwe mashahidi juu ya watu, na Mtume awe ni shahidi juu yenu. Na hatukukifanya kibla ulicho kuwa nacho ila tupate kumjua yule anaye mfuata Mtume na yule anaye geuka akarejea nyuma. Na kwa yakini hilo lilikuwa jambo gumu isipo kuwa kwa wale alio waongoa Mwenyezi Mungu. Na Mwenyezi Mungu hakuwa mwenye kuipoteza Imani yenu. Kwani Mwenyezi Mungu ni Mpole kwa watu na Mwenye kuwarehemu.'),
(151, 2, 144, 'Kwa yakini tukiona unavyo geuza geuza uso wako mbinguni. Basi tutakuelekeza kwenye Kibla ukipendacho. Basi elekeza uso wako upande wa Msikiti Mtakatifu; na popote mnapokuwa zielekezeni nyuso zenu upande huo; na hakika wale walio pewa Kitabu wanajua kwamba hiyo ni haki itokayo kwa Mola wao Mlezi. Na Mwenyezi Mungu si mwenye kughafilika na yale wanayo yatenda.'),
(152, 2, 145, 'Na hao walio pewa Kitabu, hata ukiwaletea hoja za kila namna, hawatafuata Kibla chako; wala wewe hutafuata kibla chao, wala baadhi yao hawatafuata kibla cha wengineo; na kama ukiyafuata matamanio yao baada ya kukufikia ujuzi, hakika hapo utakuwa miongoni mwa wenye kudhullumu.'),
(153, 2, 146, 'Wale tulio wapa Kitabu wanayajua haya kama wanavyo wajua watoto wao; na kuna kikundi katika wao huificha haki na hali wanajua.'),
(154, 2, 147, 'Haki inatoka kwa Mola wako Mlezi. Basi usiwe miongoni mwa wanao fanya shaka.'),
(155, 2, 148, 'Na kila mmoja anao upande anao elekea. Basi shindanieni mema. Popote mlipo Mwenyezi Mungu atakuleteni nyote pamoja. Hakika Mwenyezi Mungu ni Muweza wa kila kitu.'),
(156, 2, 149, 'Na popote wendako elekeza uso wako kwenye Msikiti Mtakatifu. Na hiyo ndiyo Haki itokayo kwa Mola wako Mlezi. Na Mwenyezi Mungu si mwenye kughafilika na mnayo yatenda.'),
(157, 2, 150, 'Na popote wendako elekeza uso wako kwenye Msikiti Mtakatifu. Na popote mlipo elekezeni nyuso zenu upande huo ili watu wasiwe na hoja juu yenu, isipokuwa wale walio dhulumu miongoni mwao. Basi msiwaogope wao, lakini niogopeni Mimi - na ili nikutimizieni neema yangu, na ili mpate kuongoka.'),
(158, 2, 151, 'Kama tulivyo mtuma Mtume kwenu anaye tokana na nyinyi, anakusomeeni Aya zetu na kukutakaseni na kukufundisheni Kitabu na hikima na kukufundisheni mliyo kuwa hamyajui.'),
(159, 2, 152, 'Basi nikumbukeni nitakukumbukeni, na nishukuruni wala msinikufuru.'),
(160, 2, 153, 'Enyi mlio amini! Takeni msaada kwa subira na Sala. Hakika Mwenyezi Mungu yu pamoja na wanao subiri.'),
(161, 2, 154, 'Wala msiseme kuwa wale walio uwawa katika Njia ya Mwenyezi Mungu ni maiti; bali hao ni wahai, lakini nyinyi hamtambui.'),
(162, 2, 155, 'Hapana shaka tutakujaribuni kwa chembe ya khofu, na njaa, na upungufu wa mali na watu na matunda. Na wabashirie wanao subiri,'),
(163, 2, 156, 'Wale ambao ukiwasibu msiba husema: Hakika sisi ni wa Mwenyezi Mungu, na kwake Yeye hakika tutarejea.'),
(164, 2, 157, 'Hao juu yao zitakuwa baraka zitokazo kwa Mola wao Mlezi na rehema. Nao ndio wenye kuongoka.'),
(165, 2, 158, 'Hakika vilima vya Safaa na Marwa ni katika alama za Mwenyezi Mungu. Basi anaye hiji kwenye Nyumba hiyo au akafanya Umra, si kosa kwake kuvizunguka. Na anaye jitendea mwenyewe kheri basi bila ya shaka Mwenyezi Mungu ni Mwenye shukrani na Mjuzi.'),
(166, 2, 159, 'Hakika wale wanao ficha tuliyo yateremsha -- nazo ni hoja zilizo wazi na uwongofu baada ya Sisi kuzibainisha kwa watu Kitabuni -- hao anawalaani Mwenyezi Mungu, na wanawalaani kila wenye kulaani.'),
(167, 2, 160, 'Ila wale walio tubu na wakatengeneza na wakabainisha, basi hao nitapokea toba yao, na Mimi ni Mwenye kupokea toba na Mwenye kurehemu.'),
(168, 2, 161, 'Hakika walio kufuru na wakafa hali ni makafiri, hao iko juu yao laana ya Mwenyezi Mungu na ya Malaika na ya watu wote.'),
(169, 2, 162, 'Watadumu humo. Hawata-punguziwa adhabu wala hawatapewa muda wa kupumzika.'),
(170, 2, 163, 'Na Mungu wenu ni Mungu mmoja tu, hapana mungu ila Yeye. Mwingi wa rehema, Mwenye kurehemu.'),
(171, 2, 164, 'Hakika katika kuumbwa kwa mbingu na ardhi, na kukhitalifiana usiku na mchana, na marikebu ambazo hupita baharini kwa viwafaavyo watu, na maji anayo yateremsha Mwenyezi Mungu kutoka mbinguni, na kwa hayo akaihuisha ardhi baada ya kufa kwake, na akaeneza humo kila aina ya wanyama; na katika mabadiliko ya pepo, na mawingu yanayo amrishwa kupita baina ya mbingu na ardhi, bila shaka zimo Ishara kwa watu wanao zingatia.'),
(172, 2, 165, 'Na katika watu wapo wanao chukua waungu wasio kuwa Mwenyezi Mungu. Wanawapenda kama kumpenda Mwenyezi Mungu. Lakini walio amini wanampenda Mwenyezi Mungu zaidi sana. Na laiti walio dhulumu wakajua watakapo iona adhabu kuwa nguvu zote ni za Mwenyezi Mungu na kuwa Mwenyezi Mungu ni mkali wa kuadhibu!'),
(173, 2, 166, 'Walio fuatwa watakapo wakataa wale walio wafuata, na hali ya kuwa wamekwisha iona adhabu; na yatakatika mafungamano yao.'),
(174, 2, 167, 'Na watasema wale walio fuata: Laiti tungeweza kurudi tukawakataa wao kama wanavyo tukataa sisi! Hivi ndivyo Mwenyezi Mungu atakavyo waonyesha vitendo vyao kuwa majuto yao; wala hawatakuwa wenye kutoka Motoni.'),
(175, 2, 168, 'Enyi watu! Kuleni vilivyomo katika ardhi, halali na vizuri, wala msifuate nyayo za Shet\'ani. Hakika yeye ni adui yenu aliye dhaahiri.'),
(176, 2, 169, 'Yeye anakuamrisheni maovu na machafu, na mumsingizie Mwenyezi Mungu mambo msiyo yajua.'),
(177, 2, 170, 'Na wanapo ambiwa: Fuateni aliyo yateremsha Mwenyezi Mungu; wao husema: Bali tutafuata tuliyo wakuta nayo baba zetu. Je, hata ikiwa baba zao walikuwa hawaelewi kitu, wala hawakuongoka?'),
(178, 2, 171, 'Na mfano wa walio kufuru ni kama mfano wa anaye mpigia kelele asiye sikia ila wito na sauti tu -- ni viziwi, mabubu, vipofu, kwa hivyo hawaelewi.'),
(179, 2, 172, 'Enyi mlio amini! Kuleni vizuri tulivyo kuruzukuni, na mumshukuru Mwenyezi Mungu, ikiwa kweli mnamuabudu Yeye tu.'),
(180, 2, 173, 'Yeye amekuharimishieni mzoga tu na damu na nyama ya nguruwe na kilicho tajiwa, katika kuchinjwa kwake, jina la asiye kuwa Mwenyezi Mungu. Lakini aliye fikiwa na dharura bila ya kutamani wala kupita kiasi, yeye hana dhambi. Hakika Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(181, 2, 174, 'Hakika wale wafichao aliyo yateremsha Mwenyezi Mungu katika Kitabu, wakanunua kwacho thamani ndogo, hao hawali matumboni mwao isipo kuwa moto, wala Mwenyezi Mungu hatawasemeza Siku ya Kiyama, wala hatawatakasa. Nao watapata adhabu chungu.'),
(182, 2, 175, 'Hao ndio walio nunua upotofu badala ya uwongofu, na adhabu badala ya maghfira. Ama wavumilivu wa Moto watu hawa!'),
(183, 2, 176, 'Hayo ni kwa sababu Mwenyezi Mungu ameteremsha Kitabu kwa haki. Na wale walio khitilafiana katika Kitabu wamo katika upinzani ulio mbali na haki.'),
(184, 2, 177, 'Sio wema kuwa mnaelekeza nyuso zenu upande wa mashariki na magharibi. Bali wema ni wa anaye muamini Mwenyezi Mungu na Siku ya Mwisho na Malaika na Kitabu na Manabii, na anawapa mali, kwa kupenda kwake, jamaa na mayatima na masikini na wasafiri, na waombao, na katika ugombozi, na akawa anashika Sala, na akatoa Zaka, na wanao timiza ahadi yao wanapo ahidi, na wanao vumilia katika shida na dhara na wakati wa vita; hao ndio walio sadikisha, na hao ndio wajilindao.'),
(185, 2, 178, 'Enyi mlio amini! Mmepewa ruhusa kulipa kisasi katika walio uwawa - muungwana kwa muungwana, na mtumwa kwa mtumwa, na mwanamke kwa mwanamke. Na anaye samehewa na ndugu yake chochote basi ashikwe kulipa kwa wema, na yeye alipe kwa ihsani. Huko ni kupunguziwa kuliko tokana na Mola wenu Mlezi, na ni rehema. Na atakaye vuka mipaka baada ya haya, basi yeye atapata adhabu chungu.'),
(186, 2, 179, 'Mtakuwa nao uhai mzuri katika kulipiza kisasi enyi wenye akili, ili msalimike.'),
(187, 2, 180, 'Mmeandikiwa -- mmoja wenu anapo fikwa na mauti, kama akiacha mali -- afanye wasia kwa wazazi wake na jamaa zake kwa namna nzuri inayo pendeza. Ni waajibu haya kwa wachamngu.'),
(188, 2, 181, 'Na atakaye ubadilisha wasia baada ya kuusikia, basi dhambi yake ni juu ya wale watakao ubadilisha; hakika Mwenyezi Mungu ni Mwenye kusikia Mwenye kujua.'),
(189, 2, 182, 'Na mwenye kumkhofia muusiaji kwenda kombo au kupata dhambi akasuluhisha baina yao, basi hatakuwa na dhambi. Hakika Mwenyezi Mungu ni Msamehevu na Mwenye kurehemu.'),
(190, 2, 183, 'Enyi mlio amini! Mmeandikiwa Saumu, kama waliyo andikiwa walio kuwa kabla yenu ili mpate kuchamngu.'),
(191, 2, 184, '(Mfunge) siku maalumu za kuhisabika. Na atakaye kuwa miongoni mwenu mgonjwa au yumo safarini basi atimize hisabu katika siku nyengine. Na wale wasio weza, watoe fidiya kwa kumlisha masikini. Na atakaye fanya wema kwa kujitolea, basi ni bora kwake. Na mkifunga ni bora kwenu, kama mnajua.'),
(192, 2, 185, 'Mwezi wa Ramadhani ambao imeteremshwa humo Qur\'ani kuwa ni uwongofu kwa watu, na hoja zilizo wazi za uwongofu na upambanuzi. Basi ataye kuwa mjini katika mwezi huu naafunge. Na mwenye kuwa mgonjwa au safarini, basi atimize hisabu katika siku nyengine. Mwenyezi Mungu anakutakieni yaliyo mepesi wala hakutakieni yaliyo mazito, na mtimize hiyo hisabu, na mumtukuze Mwenyezi Mungu kwa kuwa amekuongoeni ili mpate kushukuru.'),
(193, 2, 186, 'Na waja wangu watakapo kuuliza khabari zangu, waambie kuwa Mimi nipo karibu. Naitikia maombi ya mwombaji anapo niomba. Basi na waniitikie Mimi, na waniamini Mimi, ili wapate kuongoka.'),
(194, 2, 187, 'Mmehalalishiwa usiku wa Saumu kuingiliana na wake zenu. Wao ni vazi kwenu, na nyinyi ni vazi kwao. Mwenyezi Mungu anajua kwamba mlikuwa mkizikhini nafsi zenu. Kwa hivyo amekukubalieni toba yenu na amekusameheni. Basi sasa changanyikeni nao na takeni aliyo kuandikieni Mwenyezi Mungu. Na kuleni na kunyweni mpaka ubainike kwenu weupe wa alfajiri katika weusi wa usiku. Kisha timizeni Saumu mpaka usiku. Wala msichanganyike nao, na hali mnakaa Itikafu msikitini. Hiyo ni mipaka ya Mwenyezi Mungu, basi msiikaribie. Namna hivi Mwenyezi Mungu anabainisha Ishara zake kwa watu ili wapate kumcha.'),
(195, 2, 188, 'Wala msiliane mali zenu kwa baat\'ili na kuzipeleka kwa mahakimu ili mpate kula sehemu ya mali ya watu kwa dhambi, na hali mnajua.'),
(196, 2, 189, 'Wanakuuliza khabarai ya miezi. Sema: Hiyo ni vipimo vya nyakati kwa ajili ya watu na Hija. Wala sio wema kuziingia nyumba kwa nyuma. Bali mwema ni mwenye kuchamngu. Na ingieni majumbani kupitia milangoni. Na mcheni Mwenyezi Mungu ili mpate kufanikiwa.'),
(197, 2, 190, 'Na piganeni katika Njia ya Mwenyezi Mungu na wale wanao kupigeni, wala msianze uadui. Kwani Mwenyezi Mungu hawapendi waanzao uadui.'),
(198, 2, 191, 'Na wauweni popote mwakutapo, na muwatoe popote walipo kutoeni; kwani fitina ni mbaya zaidi kuliko kuuwa. Wala msipigane nao kwenye Msikiti Mtakatifu mpaka wakupigeni huko. Wakikupigeni huko basi nanyi pia wapigeni. Na hivi ndivyo yalivyo malipo ya makafiri.'),
(199, 2, 192, 'Lakini wakiacha basi Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(200, 2, 193, 'Na piganeni nao mpaka pasiwepo fitina, na Dini iwe ya Mwenyezi Mungu tu. Na kama wakiacha basi usiweko uadui ila kwa wenye kudhulumu.'),
(201, 2, 194, 'Mwezi mtakatifu kwa mwezi mtakatifu. Na vitu vitakatifu vimeekewa kisasi. Anaye kushambulieni nanyi mshambulieni, kwa kadiri alivyo kushambulieni. Na mcheni Mwenyezi Mungu. Na jueni kwamba Mwenyezi Mungu yu pamoja na wachamngu.'),
(202, 2, 195, 'Na toeni katika njia ya Mwenyezi Mungu, wala msijitie kwa mikono yenu katika maangamizo. Na fanyeni wema. Hakika Mwenyezi Mungu huwapenda wafanyao wema.'),
(203, 2, 196, 'Na timizeni Hija na Umra kwa ajili ya Mwenyezi Mungu. Na ikiwa mkizuiwa, basi (chinjeni) wanyama walio wepesi kupatikana. Wala msinyoe vichwa vyenu mpaka wanyama hao wafike machinjoni mwao. Na atakaye kuwa mgonjwa au ana vya kumuudhi kichwani mwake basi atoe fidiya kwa kufunga au kwa kutoa sadaka au kuchinja wanyama. Na mtakapo kuwa salama, basi mwenye kujistarehesha kwa kufanya Umra kisha ndio akahiji, basi achinje mnyama aliye mwepesi kumpata. Na asiye pata, afunge siku tatu katika Hija na siku saba mtakapo rudi; hizi ni kumi kaamili. Hayo ni kwa ajili ya yule ambaye watu wake hawako karibu na Msikiti Mtakatifu. Na mcheni Mwenyezi Mungu, na jueni Mwenyezi Mungu ni Mkali wa kuadhibu.'),
(204, 2, 197, 'Hija ni miezi maalumu. Na anaye kusudia kufanya Hija katika miezi hiyo, basi asiseme maneno machafu wala asifanye vitendo vichafu wala asibishane katika Hija. Na kheri yoyote mnayo ifanya Mwenyezi Mungu anaijua. Na jitengezeeni zawadi. Na hakika bora ya zawadi ni uchamngu. Na nicheni Mimi, enyi wenye akili!'),
(205, 2, 198, 'Si vibaya kwenu kuitafuta fadhila ya Mola wenu Mlezi. Na mtakapo miminika kutoka A\'rafat mtajeni Mwenyezi Mungu penye Masha\'ril Haram. Na mkumbukeni kama alivyo kuongoeni, ijapo kuwa zamani mlikuwa miongoni mwa walio potea.'),
(206, 2, 199, 'Kisha miminikeni kutoka pale wanapo miminika watu, na muombeni Mwenyezi Mungu msamaha: hakika Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(207, 2, 200, 'Na mkisha timiza ibada zenu basi mtajeni Mwenyezi Mungu kama mlivyo kuwa mkiwataja baba zenu au mtajeni zaidi. Na wapo baadhi ya watu wanao sema: Mola wetu Mlezi, tupe duniani! Naye katika Akhera hana sehemu yoyote.'),
(208, 2, 201, 'Na katika wao wapo wanao sema: Mola wetu Mlezi, tupe duniani mema, na Akhera mema, na utulinde na adhabu ya Moto!'),
(209, 2, 202, 'Hao ndio watakao pata sehemu yao kwa sababu ya yale waliyo yachuma. Na Mwenyezi Mungu ni Mwepesi wa kuhisabu.'),
(210, 2, 203, 'Na mtajeni Mwenyezi Mungu katika zile siku zinazo hisabiwa. Lakini mwenye kufanya haraka katika siku mbili (akarejea) si dhambi juu yake; na mwenye kukawia pia si dhambi juu yake, kwa mwenye kumchamngu. Na mcheni Mwenyezi Mungu, na jueni kwamba nyinyi mtakusanywa kwake.'),
(211, 2, 204, 'Na katika watu yupo yule ambaye maneno yake kwa maisha ya kidunia hukufurahisha; naye humshuhudisha Mwenyezi Mungu kwa yaliyomo moyoni mwake, na hali yeye ndiye mkubwa wa ukhasimu.'),
(212, 2, 205, 'Na anapo tawala hufanya juhudi katika nchi kwa kufisidi humo na kuteketeza mimea na viumbe. Na Mwenyezi Mungu hapendi ufisadi.'),
(213, 2, 206, 'Na akiambiwa: Mwogope Mwenyezi Mungu, hupandwa na mori wa kutenda madhambi. Basi huyo inamtosha Jahannam. Paovu mno hapo kwa mapumziko.'),
(214, 2, 207, 'Na katika watu yupo ambaye huiuza nafsi yake kwa kutaka radhi za Mwenyezi Mungu; na Mwenyezi Mungu ni Mpole kwa waja wake.'),
(215, 2, 208, 'Enyi mlio amini! Ingieni katika Uislamu kwa ukamilifu, wala msifuate nyayo za She\'tani; hakika yeye kwenu ni adui aliye wazi.'),
(216, 2, 209, 'Na ikiwa mkateleza baada ya kukufikieni hoja zilizo wazi, basi jueni kuwa Mwenyezi Mungu ni Mwenye nguvu na Mwenye hikima.'),
(217, 2, 210, 'Je wanangoja mpaka Mwenyezi Mungu awajie katika vivuli vya mawingu pamoja na Malaika, na hukumu iwe imekwisha tolewa? Na kwa Mwenyezi Mungu ndio hurejeshwa mashauri yote.'),
(218, 2, 211, 'Waulize Wana wa Israili: Tumewapa ishara ngapi zilizo wazi? Na anaye zibadili neema za Mwenyezi Mungu baada ya kumfikia basi hakika Mwenyezi Mungu ni mkali wa kuadhibu.'),
(219, 2, 212, 'Walio kufuru wamepambiwa maisha ya duniani; na wanawafanyia maskhara walio amini. Na wenye kuchamngu watakuwa juu yao Siku ya Kiyama. Na Mwenyezi Mungu humruzuku amtakaye bila ya hisabu.'),
(220, 2, 213, 'Watu wote walikuwa ni umma mmoja. Mwenyezi Mungu akapeleka Manabii wabashiri na waonyaji. Na pamoja nao akateremsha Kitabu kwa Haki, ili kihukumu baina ya watu katika yale walio khitalifiana. Na wala hawakukhitalifiana ila wale walio pewa Kitabu hicho baada ya kuwafikia hoja zilizo wazi, kwa sababu ya uhasidi baina yao. Ndipo Mwenyezi Mungu kwa idhini yake akawaongoa walio amini kwendea haki katika mambo waliyo khitalifiana. Na Mwenyezi Mungu humwongoa amtakaye kwenye Njia Iliyo Nyooka.'),
(221, 2, 214, 'Mnadhani kuwa mtaingia Peponi, bila ya kukujieni kama yaliyo wajia wale walio pita kabla yenu? Iliwapata shida na madhara na wakatikiswa hata Mtume na walio amini pamoja naye wakasema: Lini nusura ya Mwenyezi Mungu itakuja? Jueni kuwa nusura ya Mwenyezi Mungu ipo karibu.'),
(222, 2, 215, 'Wanakuuliza watoe nini? Sema: Kheri mnayo itoa ni kwa ajili ya wazazi na jamaa na mayatima na masikini na wasafiri. Na kheri yoyote mnayo ifanya Mwenyezi Mungu anaijua.'),
(223, 2, 216, 'Mmeandikiwa kupigana vita, navyo vinachusha kwenu. Lakini huenda mkachukia kitu nacho ni kheri kwenu. Na huenda mkapenda kitu nacho ni shari kwenu. Na Mwenyezi Mungu anajua na nyinyi hamjui.'),
(224, 2, 217, 'Wanakuuliza juu ya kupigana vita katika mwezi mtakatifu. Sema: Kupigana vita wakati huo ni dhambi kubwa. Lakini kuzuilia watu wasende katika Njia ya Mwenyezi Mungu na kumkanusha Yeye, na kuzuilia watu wasende kwenye Msikiti Mtakatifu na kuwatoa watu wake humo, ni makubwa zaidi mbele ya Mwenyezi Mungu. Na fitina ni mbaya zaidi kuliko kuuwa. Wala hawatoacha kupigana nanyi mpaka wakutoeni katika Dini yenu kama wakiweza. Na yeyote katika nyinyi akiacha Dini yake akafa naye ni kafiri, basi hao ndio ambao a\'mali zao zimeharibika duniani na Akhera. Na hao ndio watu wa Motoni; humo watadumu.'),
(225, 2, 218, 'Hakika wale walio amini na wale walio hama na wakapigania Njia ya Mwenyezi Mungu, hao ndio wanao taraji rehema za Mwenyezi Mungu. Na Mwenyezi Mungu ni Mwenye kusamehe na Mwenye kurehemu.'),
(226, 2, 219, 'Wanakuuliza juu ya ulevi na kamari. Sema: Katika hivyo zipo dhambi kubwa na manufaa kwa watu. Lakini dhambi zake ni kubwa zaidi kuliko manufaa yake. Na wanakuuliza watoe nini? Sema: Kilicho chepesi. Namna hivi Mwenyezi Mungu anakubainishieni Aya zake mpate kufikiri--'),
(227, 2, 220, '--katika mambo ya dunia na mambo ya Akhera. Na wanakuuliza juu ya mayatima. Sema: Kuwatengenezea ndio kheri. Na mkichanganyika nao basi ni ndugu zenu; na Mwenyezi Mungu anamjua mharibifu na mtengenezaji. Na Mwenyezi Mungu angeli penda angeli kutieni katika udhia. Hakika Mwenyezi Mungu ni Mwenye nguvu na Mwenye hikima.'),
(228, 2, 221, 'Wala msiwaoe wanawake washirikina mpaka waamini. Na mjakazi Muumini ni bora kuliko mshirikina hata akikupendezeni. Wala msiwaoze (binti zenu) wanaume washirikina mpaka waamini. Na mtumwa mwanamume Muumini ni bora kuliko mshirikina hata akikupendezeni. Hao wanaitia kwenye Moto, na Mwenyezi Mungu anaitia kwenye Pepo na maghafira kwa idhini yake. Naye huzibainisha Aya zake kwa watu ili wapate kukumbuka.'),
(229, 2, 222, 'Na wanakuuliza juu ya hedhi. Waambie, huo ni uchafu. Basi jitengeni na wanawake wakati wa hedhi. Wala msiwaingilie mpaka wat\'ahirike. Wakisha t\'ahirika basi waendeeni alivyo kuamrisheni Mwenyezi Mungu. Hakika Mwenyezi Mungu huwapenda wanao tubu na huwapenda wanao jisafisha.'),
(230, 2, 223, 'Wake zenu ni kama konde zenu. Basi ziendeeni konde zenu mpendavyo. Lakini jitangulizieni kheri nafsi zenu, na mcheni Mwenyezi Mungu, na jueni kuwa hakika nyinyi mtakutana naye. Na wape bishara njema Waumini.'),
(231, 2, 224, 'Wala msifanye jina la Mwenyezi Mungu katika viapo vyenu kuwa ni kisingizio cha kuacha kufanya wema na kuchamngu na kupatanisha baina ya watu. Na Mwenyezi Mungu ni Mwenye kusikia na Mwenye kujua.'),
(232, 2, 225, 'Mwenyezi Mungu hakushikeni kwa viapo vyenu vya upuuzi. Bali anakushikeni kwa yanayo chuma nyoyo zenu. Na Mwenyezi Mungu ni Mwenye kusamehe na Mpole.'),
(233, 2, 226, 'Kwa wanao apa kuwa watajitenga na wake zao, wangojee miezi mine. Wakirejea basi Mwenyezi Mungu hakika ni Mwenye kusamehe na Mwenye kurehemu.'),
(234, 2, 227, 'Na wakiazimia kuwapa talaka basi, Mwenyezi Mungu ni Mwenye kusikia na Mjuzi.'),
(235, 2, 228, 'Na wanawake walio achwa wangoje peke yao mpaka t\'ahara (au hedhi) tatu zipite. Wala haiwajuzii kuficha alicho umba Mwenyezi Mungu katika matumbo yao, ikiwa wanamuamini Mwenyezi Mungu na Siku ya Mwisho. Na waume wao wana haki ya kuwarejesha katika muda huo, kama wakitaka kufanya sulhu. Nao wanawake wanayo haki kwa Sharia kama ile haki iliyo juu yao. Na wanaume wana daraja zaidi kuliko wao. Na Mwenyezi Mungu ni Mwenye nguvu na Mwenye hikima.'),
(236, 2, 229, 'T\'alaka ni mara mbili. Kisha ni kukaa kwa wema au kuachana kwa vizuri. Wala si halali kwenu kuchukua chochote mlicho wapa wake zenu, ila ikiwa wote wawili wakiogopa ya kwamba hawataweza kushikamana na mipaka ya Mwenyezi Mungu. Basi mkiogopa kuwa hawataweza kushikamana na mipaka ya Mwenyezi Mungu hapo itakuwa hapana lawama ikiwa mwanamke atajikomboa. Hii ndiyo mipaka ya Mwenyezi Mungu; basi msiikiuke. Na watakao ikiuka mipaka ya Mwenyezi Mungu, hao ndio madhaalimu.'),
(237, 2, 230, 'Na kama amempa t\'alaka (ya tatu) basi si halali kwake baada ya hayo mpaka aolewe na mume mwengine. Na akiachwa basi hapana ubaya kwao kurejeana wakiona kuwa watashikamana na mipaka ya Mwenyezi Mungu. Na hii ni mipaka ya Mwenyezi Mungu anayo ibainisha kwa watu wanao jua.'),
(238, 2, 231, 'Na mtakapo wapa wanawake t\'alaka, nao wakafikia kumaliza eda yao, basi warejeeni kwa wema au waachilieni kwa wema. Wala msiwarejee kwa kuwadhuru mkafanya uadui. Na atakaye fanya hivyo, amejidhulumu nafsi yake. Wala msizichukulie Aya za Mwenyezi Mungu kwa maskhara. Na kumbukeni neema ya Mwenyezi Mungu juu yenu, na aliyo kuteremshieni katika Kitabu na hikima anacho kuonyeni kwacho. Na mcheni Mwenyezi Mungu, na jueni kwamba hakika Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(239, 2, 232, 'Na mtakapo wapa wanawake t\'alaka nao wakamaliza eda yao, basi msiwazuie kuolewa na waume zao endapo baina yao wamepatana kwa wema. Hayo anaonywa nayo yule miongoni mwenu anaye muamini Mwenyezi Mungu na Siku ya Mwisho. Hayo ni bora zaidi kwenu na safi kabisa. Na Mwenyezi Mungu anajua, lakini nyinyi hamjui.'),
(240, 2, 233, 'Na wazazi wanawake wawanyonyeshe watoto wao miaka miwili kaamili kwa anaye taka kutimiza kunyonyesha. Na juu ya baba yake chakula cha kina mama, na nguo zao kwa mujibu wa ada. Wala halazimishwi mtu ila kwa kadiri ya uwezo wake. Mama asitiwe taabuni kwa ajili ya mwanawe, wala baba kwa ajili ya mwanawe. Na juu ya mrithi mfano wa hivyo. Na kama wote wawili wakitaka kumwachisha ziwa kwa kuridhiana na kushauriana, basi si vibaya kwao. Na mkitaka kuwapatia watoto wenu mama wa kuwanyonyesha basi hapana ubaya kwenu mkitoa mlicho ahidi kwa mujibu wa ada. Na mcheni Mwenyezi Mungu, na jueni kwamba Mwenyezi Mungu anayaona yote mnayo yatenda.'),
(241, 2, 234, 'Na wale miongoni mwenu wanao kufa na wakaacha wake, hawa wake wangoje peke yao miezi mine na siku kumi. Na wanapo timiza eda yao basi hapana ubaya kwao kwa wanao jifanyia kwa mujibu wa ada. Na Mwenyezi Mungu anazo khabari za yote mnayo yatenda.'),
(242, 2, 235, 'Wala si vibaya kwenu katika kupeleka posa kwa ishara tu kwa wanawake waliomo edani au mkadhamiria katika nyoyo zenu. Mwenyezi Mungu anajua kwamba nyinyi mtawakumbuka. Lakini msiwaahidi kwa siri, ila mnene maneno mema. Wala msiazimie kufunga ndoa mpaka eda ifike mwisho wake. Na jueni kwamba Mwenyezi Mungu anajua yaliyomo katika nafsi zenu. Basi tahadharini naye. Na jueni kwamba Mwenyezi Mungu ni Mwenye kusamehe na Mpole.'),
(243, 2, 236, 'Hapana ubaya kwenu mkiwapa t\'alaka wanawake ambao hamjawagusa au kuwabainishia mahari yao. Lakini wapeni cha kuwaliwaza, mwenye wasaa kadiri awezavyo na mwenye dhiki kadiri awezavyo - maliwaza kwa mujibu wa dasturi kuwa ni haki juu ya watendao mema.'),
(244, 2, 237, 'Na mkiwapa t\'alaka kabla ya kuwagusa, na mmekwisha wawekea hayo mahari makhsusi, basi wapeni nusu ya mahari mlio agana, isipo kuwa ikiwa wanawake wenyewe wamesamehe, au mume ambaye kifungo cha ndoa ki mikononi mwake awe amesamehe. Na kusameheana ndiko kulio karibu zaidi na uchamngu. Wala msisahau fadhila baina yenu. Hakika Mwenyezi Mungu anayaona mnayo yatenda.'),
(245, 2, 238, 'Zilindeni Sala, na khasa Sala ya katikati, na simameni kwa ajili ya Mwenyezi Mungu nanyi ni wenye kuqunuti (kunyenyekea).'),
(246, 2, 239, 'Ikiwa mnakhofu (Salini) na hali mnakwenda kwa miguu au mmepanda. Na mtakapo kuwa katika amani, basi mkumbukeni Mwenyezi Mungu kama alivyo kufunzeni yale mliyo kuwa hamyajui.'),
(247, 2, 240, 'Na wale miongoni mwenu wanao kufa na wakawacha wake, na wausie kwa ajili ya wake zao kupata matumizi kwa mwaka mmoja bila ya kuwatoa nyumba. Na wanawake wenyewe wakiondoka, basi hapana ubaya kwenu kwa waliyo jifanyia wenyewe kwa mujibu wa Sharia. Na Mwenyezi Mungu ni Mwenye nguvu na Mwenye hikima.'),
(248, 2, 241, 'Na wanawake walio achwa wapewe cha kuwaliwaza kwa mujibu wa Sharia. Haya ni waajibu kwa wachamngu.'),
(249, 2, 242, 'Namna hivi anakubainishieni Mwenyezi Mungu Aya zake ili mpate kufahamu.'),
(250, 2, 243, 'Hukuwaona wale walio toka majumbani kwao kwa maelfu kwa kuogopa mauti? Mwenyezi Mungu akawaambia: Kufeni! Kisha akwahauisha. Hakika Mwenyezi Mungu ni Mwenye fadhila juu ya watu, lakini wengi wa watu hawashukuru.'),
(251, 2, 244, 'Na piganeni katika Njia ya Mwenyezi Mungu, na jueni kwamba Mwenyezi Mungu ni Msikizi na Mjuzi.'),
(252, 2, 245, 'Ni nani atakaye mkopesha Mwenyezi Mungu mkopo mwema ili amzidishie mzidisho mwingi, na Mwenyezi Mungu hukunja na hukunjua. Na Kwake Yeye mtarejea.'),
(253, 2, 246, 'Hukuwaona watukufu katika Wana wa Israili baada ya Musa, walipo mwambia Nabii wao: Tuwekee mfalme ili tupigane katika Njia ya Mwenyezi Mungu? Akasema: Je, haielekei ikiwa mtaandikiwa vita kuwa msipigane? Wakasema: Itakuwaje tusipigane katika Njia ya Mwenyezi Mungu, na hali tumetolewa majumbani kwetu na watoto wetu? Lakini walipo andikiwa kupigana waligeuka, isipo kuwa wachache miongoni mwao. Na Mwenyezi Mungu anawajua madhaalimu.'),
(254, 2, 247, 'Na Nabii wao akawaambia: Mwenyezi Mungu amekuteulieni Taluti (Sauli) kuwa ni mfalme. Wakasema: vipi atakuwa na ufalme juu yetu, na hali sisi tuna haki zaidi kupata ufalme kuliko yeye, naye hakupewa wasaa wa mali? Akasema: Mwenyezi Mungu amemteua yeye juu yenu na amemzidishia ukunjufu wa ilimu na kiwiliwili. Na Mwenyezi Mungu humpa ufalme wake amtakaye. Na Mwenyezi Mungu ni Mwenye wasaa na Mjuzi.'),
(255, 2, 248, 'Na Nabii wao akawaambia: Alama ya ufalme wake ni kukuleteeni lile sanduku ambalo mna ndani yake kituliza nyoyo zenu, kitokacho kwa Mola wenu Mlezi, na mna mabaki ya waliyo yaacha kina Musa na kina Harun, wanalichukua Malaika. Bila shaka katika hayo zimo dalili kwenu ikiwa nyinyi mnaamini.'),
(256, 2, 249, 'Basi Taluti alipo ondoka na majeshi alisema: Mwenyezi Mungu atakujaribuni kwa mto. Atakaye kunywa humo si pamoja nami, na asiye kunywa atakuwa pamoja nami: ila atakaye teka kiasi ya kitanga cha mkono wake. Lakini walikunywa humo isipo kuwa wachache tu miongoni mwao. Alipo vuka mto yeye na wale walio amini pamoja naye, walisema: Leo hatumwezi Jaluti (Goliati) na majeshi yake. Wakasema wale wenye yakini ya kukutana na Mwenyezi Mungu: Makundi mangapi madogo yameshinda makundi makubwa kwa idhini ya Mwenyezi Mungu. Na Mwenyezi Mungu yu pamoja na wenye kusubiri.'),
(257, 2, 250, 'Na walipo toka kupambana na Jaluti na majeshi yake walisema: Mola wetu Mlezi! Tumiminie subira, na isimamishe imara miguu yetu, na utusaidie tuwashinde watu Makafiri--'),
(258, 2, 251, '--Kwa idhini ya Mwenyezi Mungu waliwatimua, na Daudi akamuuwa Jaluti, na Mwenyezi Mungu akampa Daudi ufalme na hikima, na akamfundisha aliyo yapenda. Na lau kuwa Mwenyezi Mungu hawapambanishi watu kwa watu basi dunia ingeli haribika. Lakini Mwenyezi Mungu ni Mwenye fadhila juu ya walimwengu wote.'),
(259, 2, 252, 'Hizi ni Aya za Mwenyezi Mungu tunakusomea kwa haki. Na hakika wewe ni miongoni mwa Mitume.'),
(260, 2, 253, 'MITUME hao tumewatukuza baadhi yao juu ya wengineo. Katika wao wapo ambao Mwenyezi Mungu alisema nao, na wengine akawapandisha vyeo. Na tukampa Isa mwana wa Mariyam hoja zilizo wazi, na tukamtia nguvu kwa Roho Takatifu. Na lau kuwa Mwenyezi Mungu alipenda wasingeli pigana walio kuwa baada yao baada ya kuwajia hoja zilizo wazi. Lakini walikhitalifiana. Basi wapo kati yao walio amini, na wengine kati yao walio kufuru. Na lau kuwa Mwenyezi Mungu alipenda wasingeli pigana. Lakini Mwenyezi Mungu hutenda atakavyo.'),
(261, 2, 254, 'Enyi mlio amini! Toeni katika tulivyo kupeni kabla haijafika Siku ambayo hapatakuwapo biashara, wala urafiki, wala uombezi. Na makafiri ndio madhaalimu.'),
(262, 2, 255, 'Mwenyezi Mungu - hapana mungu ila Yeye Aliye hai, Msimamia mambo yote milele. Hashikwi na usingizi wala kulala. Ni vyake pekee vyote viliomo mbinguni na duniani. Ni nani huyo awezaye kuombea mbele yake bila ya idhini yake? Anayajua yaliyo mbele yao na yaliyo nyuma yao; wala wao hawajui chochote katika vilio katika ujuzi wake, ila kwa atakalo mwenyewe. Enzi yake imetanda mbinguni na duniani; na wala haemewi na kuvilinda hivyo. Na Yeye ndiye aliye juu, na ndiye Mkuu.'),
(263, 2, 256, 'Hapana kulazimisha katika Dini. Kwani Uwongofu umekwisha pambanuka na upotofu. Basi anaye mkataa Shet\'ani na akamuamini Mwenyezi Mungu bila ya shaka amekamata kishikio madhubuti, kisicho vunjika. Na Mwenyezi Mungu ni Mwenye kusikia, Mwenye kujua.'),
(264, 2, 257, 'Mwenyezi Mungu ni Mlinzi wa walio amini. Huwatoa gizani na kuwaingiza mwangazani. Lakini walio kufuru, walinzi wao ni Mashet\'ani. Huwatoa kwenye mwangaza na kuwaingiza gizani. Hao ndio watu wa Motoni, na humo watadumu.'),
(265, 2, 258, 'Hukumwona yule aliye hojiana na Ibrahim juu ya Mola wake Mlezi kwa sababu Mwenyezi Mungu alimpa ufalme? Ibrahim alipo sema: Mola wangu ni yule ambaye huhuisha na kufisha. Yeye akasema: Mimi pia nahuisha na kufisha. Ibrahim akasema: Mwenyezi Mungu hulichomozesha jua mashariki, basi wewe lichomozeshe magharibi. Akafedheheka yule aliye kufuru; na Mwenyezi Mungu hawaongoi watu madhaalimu.'),
(266, 2, 259, 'Au kama yule aliye pita karibu na mji ulio kwisha kuwa magofu tu, akasema: Ataufufuaje Mwenyezi Mungu mji huu baada ya kufa kwake? Basi Mwenyezi Mungu alimfisha yeye muda wa miaka mia. Kisha akamfufua. Akamuuliza: Je umekaa muda gani? Akajibu: Labda nimekaa siku moja au sehemu ya siku. Akamwambia: Bali umekaa miaka mia wewe. Hebu angalia chakula chako na vinywaji vyako, havikuharibika. Na muangalie punda wako. Na ili tukufanye uwe ni Ishara kwa watu iangalie hii mifupa yake jinsi tunavyo inyanyua kisha tunaivisha nyama. Basi yalipo mbainikia alinena: Najua kwamba Mwenyezi Mungu ni Mwenye uweza juu ya kila kitu.'),
(267, 2, 260, 'Na alipo sema Ibrahim: Mola wangu Mlezi! Nionyeshe vipi unavyo fufua wafu. Mwenyezi Mungu akasema: Kwani huamini? Akasema: Hasha! Lakini ili moyo wangu utue. Akamwambia: Twaa ndege wane na uwazoeshe kwako, kisha uweke juu ya kila kilima sehemu, kisha wete, watakujia mbio. Na ujue kwamba hakika Mwenyezi Mungu ni Mtukufu Mwenye nguvu, na Mwenye hikima.'),
(268, 2, 261, 'Mfano wa wanao tumia mali zao katika Njia ya Mwenyezi Mungu ni kama mfano wa punje moja iliyo chipuza mashuke saba. Katika kila shuke zimo punje mia. Na Mwenyezi Mungu humzidishia amtakaye, na Mwenyezi Mungu ni Mwenye wasaa na Mwenye kujua.'),
(269, 2, 262, 'Wanao toa mali zao kwa Njia ya Mwenyezi Mungu, kisha hawafuatishii masimbulizi wala udhia kwa walicho toa, wana ujira wao kwa Mola wao Mlezi, wala haitakuwa khofu juu yao wala hawatahuzunika.'),
(270, 2, 263, 'Kauli njema na usamehevu ni bora kuliko sadaka inayo fuatiliwa na maudhi. Na Mwenyezi Mungu ndiye Mwenye kujitosha na Mpole.'),
(271, 2, 264, 'Enyi mlio amini! Msiharibu sadaka zenu kwa masimbulizi na maudhi, kama anaye toa mali yake kwa kuwaonyesha watu, wala hamuamini Mwenyezi Mungu wala Siku ya Mwisho. Basi mfano wake ni kama mfano wa jabali ambalo juu yake pana udongo, kisha likapigwa na mvua kubwa, ikaliacha tupu. Basi hawatakuwa na uweza wowote kwa walivyo chuma. Na Mwenyezi Mungu hawaongoi watu makafiri.'),
(272, 2, 265, 'Na mfano wa wale wanao toa mali zao kwa kuitafuta radhi ya Mwenyezi Mungu na kuzipa nguvu roho zao ni kama mfano wa bustani iliyo mahali pa juu, ikaifikia mvua kubwa na ikaleta mazao yake mardufu; na hata kama haifikiwi na mvua kubwa basi manyunyu hutosha. Na Mwenyezi Mungu anayaona mnayo yatenda.'),
(273, 2, 266, 'Je! Yupo mmoja wenu anaye penda kuwa na kitalu cha mitende na mizabibu ipitayo mito kati yake, naye humo hupata mazao ya kila namna, na uzee ukamfikia naye hali ana watoto wanyonge, kikapigwa na kimbunga chenye moto, kikaungua? Hivi ndivyo Mwenyezi Mungu anavyo kubainishieni Ishara ili mpate kufikiri.'),
(274, 2, 267, 'Enyi mlio amini! Toeni katika vile vizuri mlivyo vichuma, na katika vile tulivyo kutoleeni katika ardhi. Wala msikusudie kutoa vilivyo vibaya, hali nyinyi wenyewe msinge vipokea ila ingekuwa mmefumba macho. Na jueni kwamba Mwenyezi Mungu ni Mwenye kujitosha na Msifiwa.'),
(275, 2, 268, 'Shet\'ani anakutieni khofu ya ufakiri na anakuamrisheni mambo machafu. Na Mwenyezi Mungu anakuahidini msamaha na fadhila zitokazo kwake. Na Mwenyezi Mungu ni Mwenye wasaa, Mwenye kujua.'),
(276, 2, 269, 'Yeye humpa hikima amtakaye; na aliye pewa hikima bila ya shaka amepewa kheri nyingi. Na hawakumbuki ila wenye akili.'),
(277, 2, 270, 'Na chochote mnacho toa au nadhiri mnazo weka basi hakika Mwenyezi Mungu anajua. Na walio dhulumu hawana wa kuwanusuru.'),
(278, 2, 271, 'Mkizidhihirisha sadaka ni vizuri; na mkizificha mkawapa mafakiri kwa siri basi hivyo ni kheri kwenu, na yatakuondoleeni baadhi ya maovu yenu. Na Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(279, 2, 272, 'Si juu yako wewe kuwaongoa, lakini Mwenyezi Mungu humwongoa amtakaye. Na kheri yoyote mnayo toa ni kwa nafsi zenu. Wala msitoe ila kwa kutafuta wajihi wa Mwenyezi Mungu. Na kheri yoyote mtakayo toa mtalipwa kwa ukamilifu, wala hamtadhulumiwa.'),
(280, 2, 273, 'Nawapewe mafakiri walio zuilika katika njia za Mwenyezi Mungu, wasio weza kusafiri katika nchi kutafuta riziki. Asiye wajua hali zao huwadhania kuwa ni matajiri kwa sababu ya kujizuia kwao. Utawatambua kwa alama zao; hawang\'ang\'analii watu kwa kuwaomba. Na kheri yoyote mnayo toa, basi kwa yakini Mwenyezi Mungu anaijua.'),
(281, 2, 274, 'Wale wanao toa mali zao usiku na mchana, kwa siri na dhaahiri, wana ujira wao kwa Mola wao Mlezi; wala haitakuwa khofu juu yao, wala hawatahuzunika.'),
(282, 2, 275, 'Wale walao riba hawasimami ila kama anavyo simama aliye zugwa na Shet\'ani kwa kumgusa. Hayo ni kwa kuwa wamesema: Biashara ni kama riba. Lakini Mwenyezi Mungu ameihalalisha biashara na ameiharimisha riba. Basi aliye fikiwa na mawaidha kutoka kwa Mola wake Mlezi, kisha akajizuia, basi yake ni yaliyo kwisha pita, na mambo yake yako kwa Mwenyezi Mungu. Na wenye kurudia basi hao ndio watu wa Motoni, humo watadumu.'),
(283, 2, 276, 'Mwenyezi Mungu huiondolea baraka riba, na huzibariki sadaka. Na Mwenyezi Mungu hampendi kila mwenye kukanya na afanyae dhambi.'),
(284, 2, 277, 'Hakika wale walio amini na wakatenda mema na wakashika Sala na wakatoa Zaka, wao watapata ujira wao kwa Mola wao Mlezi, wala haitakuwa khofu juu yao, wala hawatahuzunika.'),
(285, 2, 278, 'Enyi mlio amini! Mcheni Mwenyezi Mungu, na acheni riba zilizo bakia, ikiwa nyinyi ni Waumini.'),
(286, 2, 279, 'Basi mkitofanya jitangazieni vita na Mwenyezi Mungu na Mtume wake. Na mkitubu, basi haki yenu ni rasilmali zenu. Msidhulumu wala msidhulumiwe.'),
(287, 2, 280, 'Na akiwa (mdaiwa) ana shida, basi (mdai) angoje mpaka afarijike. Na mkiifanya deni kuwa ni sadaka, basi ni bora kwenu, ikiwa mnajua.'),
(288, 2, 281, 'Na iogopeni Siku ambayo mtarudishwa kwa Mwenyezi Mungu, kisha kila nafsi italipwa ilicho chuma. Nao hawatadhulumiwa.'),
(289, 2, 282, 'Enyi mlio amini! Mnapo kopeshana deni kwa muda ulio wekwa, basi andikeni. Na mwandishi aandike baina yenu kwa uadilifu, wala mwandishi asikatae kuandika. Aandike kama alivyo mfunza Mwenyezi Mungu. Na mwenye deni juu yake aandikishe; naye amche Mwenyezi Mungu, Mola wake Mlezi. Wala asipunguze chochote ndani yake. Na akiwa mwenye kudaiwa ni mtu aliye pumbaa au mnyonge au hawezi kuandikisha mwenyewe, basi aandikishe mlinzi wake kwa uadilifu. Na mshuhudishe mashahidi wawili katika wanaume wenu. Ikiwa hapana wanaume wawili, basi mwanamume mmoja na wanawake wawili katika mnao waridhia kuwa mashahidi. Ili ikiwa mmoja wao akipotea, mmoja wao amkumbushe mwengine. Na mashahidi wasikatae waitwapo. Wala msipuuze kuliandika deni likiwa dogo au kubwa mpaka muda wake. Hayo ndiyo haki zaidi mbele ya Mwenyezi Mungu, na ndiyo sawa zaidi kwa ushahidi, na vyema zaidi ili msiwe na shaka. Ila ikiwa ni biashara ya mkono-kwa-mkono baina yenu, basi hapo si vibaya kwenu msipo iandika. Lakini mnapo uziana wekeni mashahidi. Wala asitiwe matatani mwandishi wala shahidi. Na mkifanya hivyo basi hakika hilo ni kosa kwenu. Na mcheni Mwenyezi Mungu. Na Mwenyezi Mungu anakufundisheni. Na Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(290, 2, 283, 'Na mkiwa safarini na hamkupata mwandishi, yatosha kukabidhiwa rahani. Na mmoja wenu akimwekea amana mwenziwe basi aliye aminiwa airudishe amana ya mwenzake, na amche Mwenyezi Mungu, Mola wake Mlezi. Wala msifiche ushahidi. Na atakaye ficha basi hakika moyo wake ni wenye kutenda dhambi. Na Mwenyezi Mungu ni Mjuzi wa mnayo yatenda.'),
(291, 2, 284, 'Viliomo mbinguni na viliomo duniani ni vya Mwenyezi Mungu. Na mkidhihirisha yaliyomo katika nafsi zenu au mkayaficha, Mwenyezi Mungu atakuhisabuni kwayo. Kisha amsamehe amtakaye na amuadhibu amtakaye. Na Mwenyezi Mungu ni Mwenye uweza juu ya kila kitu.'),
(292, 2, 285, 'Mtume ameamini yaliyo teremshwa kwake kutoka kwa Mola Mlezi wake, na Waumini vile vile. Wote wamemuamini Mwenyezi Mungu, na Malaika wake, na Vitabu vyake na Mitume wake. Hatutafautishi baina ya yeyote katika Mitume wake, na (Waumini) husema: Tumesikia na tumet\'ii. Tunakutaka maghfira Mola Mlezi wetu! Na marejeo ni kwako.'),
(293, 2, 286, 'Mwenyezi Mungu haikalifishi nafsi yoyote ila kwa kadiri ya iwezavyho. Faida ya iliyo yachuma ni yake, na khasara ya iliyo yachuma ni juu yake pia. (Ombeni:) Mola wetu Mlezi! Usituchukulie tukisahau au tukikosea. Mola wetu Mlezi! Usitubebeshe mzigo kama ulio wabebesha wale walio kuwa kabla yetu. Mola wetu Mlezi Usitutwike tusiyo yaweza, na utusamehe, na utughufirie, na uturehemu. Wewe ndiye Mlinzi wetu. Basi tupe ushindi tuwashinde kaumu ya makafiri.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Alif Laam Miim.'),
(295, 3, 2, 'Mwenyezi Mungu, hakuna mungu ila Yeye Aliye Hai, Msimamizi wa yote milele.'),
(296, 3, 3, 'Amekuteremshia Kitabu kwa haki, kinacho sadikisha yaliyo kuwa kabla yake. Na aliteremsha Taurati na Injili'),
(297, 3, 4, 'Kabla yake, ziwe uwongofu kwa watu. Na akateremsha Furqani (Upambanuo). Hakika walio zikanusha Ishara za Mwenyezi Mungu watakuwa na adhabu kali. Na Mwenyezi Mungu ni Mwenye nguvu na Mwenye kulipa.'),
(298, 3, 5, 'Hakika Mwenyezi Mungu hakifichiki chochote kwake, duniani wala mbinguni.'),
(299, 3, 6, 'Yeye ndiye anaye kuundeni ndani ya matumbo ya wazazi kwa namna apendayo. Hapana mungu ila Yeye, Mwenye nguvu na Mwenye hikima.'),
(300, 3, 7, 'Yeye ndiye aliye kuteremshia Kitabu hichi. Ndani yake zimo Aya muhkam, zenye maana wazi. Hizo ndizo msingi wa Kitabu hichi. Na ziko nyengine za mifano. Ama wale ambao nyoyoni mwao umo upotovu wanafuata zile za mifano kwa kutafuta fitna, na kutafuta maana yake; na wala hapana ajuaye maana yake ila Mwenyezi Mungu. Na wale wenye msingi madhubuti katika ilimu husema: Sisi tumeziamini, zote zimetoka kwa Mola wetu Mlezi. Lakini hawakumbuki hayo isipo kuwa wenye akili.'),
(301, 3, 8, '(Na hao husema): Mola wetu Mlezi! Usizipotoe nyoyo zetu baada ya kwisha tuongoa, na utupe rehema itokayo kwako. Hakika Wewe ndiye Mpaji.'),
(302, 3, 9, 'Mola wetu Mlezi! Wewe ndiye Mkusanyaji wa watu kwa Siku isiyo na shaka ndani yake. Hakika Mwenyezi Mungu havunji miadi yake.'),
(303, 3, 10, 'Kwa hakika wale walio kufuru hazitowafaa kitu kwa Mwenyezi Mungu mali zao, wala watoto wao; nao hao ndio kuni za Motoni-'),
(304, 3, 11, '- Kama yaliyo wapata watu wa Firauni na wale walio kuwa kabla yao. Walikanusha Ishara zetu, basi Mwenyezi Mungu aliwashika kwa madhambi yao; na Mwenyezi Mungu ni Mkali wa kuadhibu.'),
(305, 3, 12, 'Waambie walio kufuru: Karibuni mtashindwa na mtakusanywa, mtiwe kwenye Jahannam; nako huko ni makao mabaya kabisa.'),
(306, 3, 13, 'Hakika ilikuwa ni Ishara kwenu katika yale majeshi mawili yalipo pambana. Jeshi moja likipigana katika Njia ya Mwenyezi Mungu, na jingine kafiri likiwaona zaidi kuliko wao mara mbili, kwa kuona kwa macho. Na Mwenyezi Mungu humuunga mkono amtakaye kwa nusura yake. Hakika katika hayo yapo mazingatio kwa wenye macho.'),
(307, 3, 14, 'Watu wamepambiwa kupenda matamanio ya wanawake, na wana, na mirundi ya dhahabu na fedha, na farasi asili, na mifugo, na mashamba. Hayo ni starehe ya maisha ya duniani; na kwa Mwenyezi Mungu ndio kwenye marejeo mema.'),
(308, 3, 15, 'Sema: Nikwambieni yaliyo bora kuliko hayo? Kwa wachamngu ziko Bustani kwa Mola wao zipitazo mito kati yake. Watadumu humo, na wake walio takaswa, na wanazo radhi za Mwenyezi Mungu. Na Mwenyezi Mungu ni Mwenye kuwaona waja wake,'),
(309, 3, 16, 'Ambao husema: Mola wetu Mlezi! Hakika sisi tumeamini, basi tufutie madhambi yetu, na tuepushe na adhabu ya Moto,'),
(310, 3, 17, 'Wanao subiri, wanao sema kweli, na wat\'iifu, na wanao toa sadaka, na wanao omba maghafira kabla ya alfajiri.'),
(311, 3, 18, 'Mwenyezi Mungu, na Malaika, na wenye ilimu, wameshuhudia kuwa hakika hapana mungu ila Yeye, ndiye Mwenye kusimamisha uadilifu; hapana mungu ila Yeye Mwenye nguvu na Mwenye hikima.'),
(312, 3, 19, 'Bila ya shaka Dini mbele ya Mwenyezi Mungu ni Uislamu. Na walio pewa Kitabu hawakukhitalifiana ila baada ya kuwajia ujuzi, kwa sababu ya uhasidi ulio kuwa baina yao. Na anaye zikataa Ishara za Mwenyezi Mungu basi hakika Mwenyezi Mungu ni Mwepesi wa kuhisabu.'),
(313, 3, 20, 'Na pindi wakikuhoji, basi sema: Mimi nimeusalimisha uso wangu kumwelekea Mwenyezi Mungu, na kadhaalika walio nifuata. Na waambie walio pewa Kitabu na wasio na kisomo: Je! Mmesilimu? Wakisilimu basi wameongoka. Na wakikengeuka basi juu yako ni kufikisha ujumbe tu. Na Mwenyezi Mungu ni Mwenye kuwaona waja wake.'),
(314, 3, 21, 'Hakika wanao zikataa Ishara za Mwenyezi Mungu, na wakawauwa Manabii pasipo haki, na wakawauwa watu waamrishao Haki wabashirie adhabu kali.'),
(315, 3, 22, 'Hao ndio ambao vitendo vyao vimeharibika duniani na Akhera. Nao hawatapata wa kuwanusuru.'),
(316, 3, 23, 'Huwaoni wale walio pewa sehemu ya Kitabu? Wanaitwa kukiendea Kitabu cha Mwenyezi Mungu ili kiwahukumu baina yao; kisha baadhi yao wanageuka wanakikataa.'),
(317, 3, 24, 'Hayo ni kwa sababu walisema: Hautatugusa Moto isipo kuwa kwa siku chache tu. Na yakawadanganya katika dini yao yale ambayo wenyewe waliyo kuwa wakiyazua.'),
(318, 3, 25, 'Basi itakuwaje tutakapo wakusanya Siku ambayo haina shaka kuja kwake? Na kila nafsi italipwa kama ilivyo chuma, bila ya kudhulumiwa.'),
(319, 3, 26, 'Sema: Ewe Mwenyezi Mungu uliye miliki ufalme wote! Wewe humpa ufalme umtakaye, na humwondolea ufalme umtakaye, na humtukuza umtakaye, na humuangusha umtakaye. Kheri yote iko mikononi mwako. Hakika Wewe ni Muweza wa kila kitu.'),
(320, 3, 27, 'Wewe huingiza usiku katika mchana, na huuingiza mchana katika usiku. Na humtoa hai kutokana na maiti, na humtoa maiti kutokana na aliye hai. Na unamruzuku umtakaye bila ya hisabu.'),
(321, 3, 28, 'Waumini wasiwafanye makafiri kuwa wapenzi wao badala ya Waumini. Na anaye fanya hivyo, basi hatakuwa chochote mbele ya Mwenyezi Mungu. Ila ikiwa kwa ajili ya kujilinda na shari zao. Na Mwenyezi Mungu anakuhadharisheni naye. Na marejeo ni kwa Mwenyezi Mungu.'),
(322, 3, 29, 'Sema: Mkificha yaliyomo vifuani mwenu au mkiyafichua Mwenyezi Mungu anayajua; na anayajua yaliyomo mbinguni na yaliyomo duniani. Na Mwenyezi Mungu ni Muweza wa kila kitu.'),
(323, 3, 30, 'Siku ambayo kila nafsi itakuta mema iliyo yafanya yamehudhurishwa, na pia ubaya ilio ufanya. Itapenda nafsi lau kungekuwako masafa marefu baina ya ubaya huo na yeye. Na Mwenyezi Mungu anakuhadharisheni naye. Na Mwenyezi Mungu ni Mpole kwa waja wake.'),
(324, 3, 31, 'Sema: Ikiwa nyinyi mnampenda Mwenyezi Mungu basi nifuateni mimi, Mwenyezi Mungu atakupendeni na atakufutieni madhambi yenu. Na Mwenyezi Mungu ni Mwenye kufuta madhambi na Mwenye kurehemu.'),
(325, 3, 32, 'Sema: Mt\'iini Mwenyezi Mungu na Mtume. Na wakigeuka basi Mwenyezi Mungu hawapendi makafiri.'),
(326, 3, 33, 'Hakika Mwenyezi Mungu alimteuwa Adam na Nuhu na ukoo wa Ibrahim na ukoo wa Imran juu ya walimwengu wote.'),
(327, 3, 34, 'Ni wazao wao kwa wao; na Mwenyezi Mungu ni Mwenye kusikia na Mwenye kujua.'),
(328, 3, 35, 'Alipo sema mke wa Imran: Mola wangu Mlezi! Nimekuwekea nadhiri kilichomo tumboni mwangu kuwa wakfu; basi nikubalie. Hakika Wewe ndiye Mwenye kusikia na Mwenye kujua.'),
(329, 3, 36, 'Basi alipo mzaa alisema: Mola wangu Mlezi! Nimemzaa mwanamke - na Mwenyezi Mungu anajua sana aliye mzaa - Na mwanamume si sawa na mwanamke. Na mimi nimemwita Maryamu. Nami namkinga kwako, yeye na uzao wake, uwalinde na Shet\'ani aliye laaniwa.'),
(330, 3, 37, 'Tena Mola wake Mlezi akampokea kwa mapokeo mema na akamkuza makuzo mema, na akamfanya Zakariya awe mlezi wake. Kila mara Zakariya alipo ingia chumbani kwake alimkuta na vyakula. Basi alimwambia: Ewe Maryamu! Unavipata wapi hivi? Naye akasema: Hivi vinatoka kwa Mwenyezi Mungu; na Mwenyezi Mungu humruzuku amtakaye bila ya hisabu.'),
(331, 3, 38, 'Pale pale Zakariya akamwomba Mola wake Mlezi, akasema: Mola wangu Mlezi! Nipe kutoka kwako uzao mwema. Wewe ndiye unaye sikia maombi.'),
(332, 3, 39, 'Alipo kuwa kasimama chumbani akisali, Malaika kamnadia: Hakika Mwenyezi Mungu anakubashiria Yahya, ataye kuwa mwenye kusadikisha neno litokalo kwa Mwenyezi Mungu, na ni bwana na mt\'awa na Nabii kwa watu wema.'),
(333, 3, 40, 'Akasema Zakariya: Mola wangu Mlezi! Vipi nitapata mwana na hali ukongwe umenifikia, na mke wangu ni tasa? Akasema: Ndivyo vivyo hivyo, Mwenyezi Mungu hufanya apendavyo.'),
(334, 3, 41, 'Akasema: Mola wangu Mlezi! Niwekee alama. Akasema: Alama yako ni kuwa hutasema na watu kwa siku tatu, isipo kuwa kwa kuashiria tu. Na mdhukuru Mola Mlezi wako kwa wingi na mtakase jioni na asubuhi.'),
(335, 3, 42, 'Na angalia pale Malaika walipo sema: Ewe Maryamu! Kwa hakika Mwenyezi Mungu amekuteuwa, na akakutakasa, na akakutukuza kuliko wanawake wote.'),
(336, 3, 43, 'Ewe Maryamu! Mnyenyekee Mola Mlezi wako na usujudu na uiname pamoja na wainamao.'),
(337, 3, 44, 'Hizi ni khabari za ghaibu tunazo kufunulia; nawe hukuwa nao walipo kuwa wakitupa kalamu zao nani wao atamlea Maryamu, na hukuwa nao walipo kuwa wakishindana.'),
(338, 3, 45, 'Na pale Malaika walipo sema: Ewe Maryamu! Hakika Mwenyezi Mungu anakubashiria (mwana) kwa neno litokalo kwake. Jina lake ni Masihi Isa mwana wa Maryamu, mwenye hishima katika dunia na Akhera, na miongoni mwa walio karibishwa (kwa Mwenyezi Mungu).'),
(339, 3, 46, 'Naye atazungumza na watu katika utoto wake na katika utuuzima wake, na atakuwa katika watu wema.'),
(340, 3, 47, 'Maryamu akasema: Mola wangu Mlezi! Vipi nitampata mwana na hali hajanigusa mwanaadamu? Mwenyezi Mungu akasema: Ndivyo vivyo hivyo, Mwenyezi Mungu huumba apendacho. Anapo hukumu jambo, huliambia: Kuwa! Likawa.'),
(341, 3, 48, 'Na atamfunza kuandika na Hikima na Taurati na Injili.'),
(342, 3, 49, 'Na ni Mtume kwa Wana wa Israili kuwaambia: Mimi nimekujieni na Ishara kutoka kwa Mola Mlezi wenu, ya kwamba nakuundieni kwa udongo kama sura ya ndege. Kisha nampuliza anakuwa ndege kwa idhini ya Mwenyezi Mungu. Na ninawaponesha vipofu wa tangu kuzaliwa na wakoma, na ninawafufua maiti kwa idhini ya Mwenyezi Mungu, na ninakwambieni mnacho kila na mnacho weka akiba katika nyumba zenu. Hakika katika haya ipo Ishara kwenu ikiwi nyinyi ni wenye kuamini.'),
(343, 3, 50, 'Na ninasadikisha yaliyo kuwa kabla yangu katika Taurati, na ili nikuhalalishieni baadhi ya yale mlio harimishiwa, na nimekujieni na Ishara kutokana na Mola Mlezi wenu. Kwa hivyo mcheni Mwenyezi Mungu na nit\'iini mimi.'),
(344, 3, 51, 'Hakika Mwenyezi Mungu ni Mola wangu Mlezi na ni Mola wenu Mlezi. Basi muabudni Yeye. Hii ndiyo Njia Iliyo Nyooka.'),
(345, 3, 52, 'Isa alipo hisi kuwa kati yao pana ukafiri alisema: Nani wasaidizi wangu kwa Mwenyezi Mungu? Wanafunzi wake wakasema: Sisi ni wasaidizi wa Mwenyezi Mungu. Tumemuamini Mwenyezi Mungu, na shuhudia kwamba sisi hakika ni Waislamu.'),
(346, 3, 53, 'Mola wetu Mlezi! Tumeyaamini uliyo yateremsha, na tumemfuata huyu Mtume, basi tuandike pamoja na wanao shuhudia.'),
(347, 3, 54, 'Na Makafiri walipanga mipango na Mwenyezi Mungu akapanga mipango, na Mwenyezi Mungu ndiye mbora wa wenye kupanga.'),
(348, 3, 55, 'Pale Mwenyezi Mungu alipo sema: Ewe Isa! Mimi nitakufisha, na nitakunyanyua kwangu, na nitakutakasa na wale walio kufuru, na nitawaweka wale walio kufuata juu ya wale walio kufuru, mpaka Siku ya Kiyama. Kisha marejeo yenu yatakuwa kwangu, nikuhukumuni katika yale mliyo kuwa mkikhitalifiana.'),
(349, 3, 56, 'Ama wale walio kufuru, nitawaadhibu adhabu kali katika dunia na Akhera, wala hawatapata wa kuwanusuru.'),
(350, 3, 57, 'Na ama wale walio amini na wakatenda mema basi Mwenyezi Mungu atawalipa ujira wao kaamili. Na Mwenyezi Mungu hawapendi madhaalimu.'),
(351, 3, 58, 'Haya tunayo kusomea ni katika Aya na Ukumbusho wenye hikima.'),
(352, 3, 59, 'Hakika mfano wa Isa kwa Mwenyezi Mungu ni kama mfano wa Adam; alimuumba kwa udongo kisha akamwambia: Kuwa! Basi akawa.'),
(353, 3, 60, 'Hii ni kweli itokayo kwa Mola wako Mlezi. Basi usiwe miongoni mwa wanao fanya shaka.'),
(354, 3, 61, 'Watakao kuhoji katika haya baada ya kukufikilia ilimu hii waambie: Njooni tuwaite watoto wetu na watoto wenu, na wake zetu na wake zenu, na sisi wenyewe na nyinyi wenyewe, kisha tuombe kwa unyenyekevu tutake laana ya Mwenyezi Mungu iwashukie waongo.'),
(355, 3, 62, 'Hakika haya ndiyo maelezo ya kweli. Na hakuna mungu ila Mwenyezi Mungu tu, na hakika Mwenyezi Mungu ndiye Mwenye nguvu na Mwenye hikima.'),
(356, 3, 63, 'Na kama wakigeuka basi Mwenyezi Mungu anawajua waharibifu.'),
(357, 3, 64, 'Sema: Enyi Watu wa Kitabu! Njooni kwenye neno lilio sawa baina yetu na nyinyi: Ya kwamba tusimuabudu yeyote ila Mwenyezi Mungu, wala tusimshirikishe na chochote; wala tusifanyane sisi kwa sisi kuwa Waola Walezi badala ya Mwenyezi Mungu. Na wakigeuka basi semeni: Shuhudieni ya kwamba sisi ni Waislamu.'),
(358, 3, 65, 'Enyi Watu wa Kitabu! Mbona mnabishana juu ya Ibrahim, na hali Taurati na Injili hazikuteremshwa ila baada yake? Basi hamzingatii?'),
(359, 3, 66, 'Angalieni! Nyinyi mlibishana katika yale mliyo yajua. Mbona sasa mnabishana katika yale msiyo yajua? Na Mwenyezi Mungu ndiye ajuaye, na nyinyi hamjui.'),
(360, 3, 67, 'Ibrahim hakuwa Yahudi wala Mkristo, lakini alikuwa mwongofu Muislamu, wala hakuwa katika washirikina.'),
(361, 3, 68, 'Watu wanao mkaribia zaidi Ibrahim ni wale walio mfuata yeye na Nabii huyu na walio amini. Na Mwenyezi Mungu ndiye Mlinzi wa Waumini.'),
(362, 3, 69, 'Kipo kikundi katika Watu wa Kitabu wanao penda kukupotezeni; lakini hawapotezi ila nafsi zao, nao wenyewe hawatambui.'),
(363, 3, 70, 'Enyi Watu wa Kitabu! Mbona mnazikataa Ishara za Mwenyezi Mungu ilhali nyinyi mnashuhudia?'),
(364, 3, 71, 'Enyi Watu wa Kitabu! Mbona kweli mnaivisha uwongo, na kweli mnaificha na hali mnajua?'),
(365, 3, 72, 'Na kikundi katika Watu wa Kitabu walisema: Yaaminini yale waliyo teremshiwa wenye kuamini mwanzo wa mchana, na yakataeni mwisho wake; huenda wakarejea.'),
(366, 3, 73, 'Wala msimuamini ila anaye fuata dini yenu. Sema: Hakika uwongofu ni uwongofu wa Mwenyezi Mungu, kuwa apewe mtu mfano wa mlio pewa nyinyi au wakuhojini mbele ya Mola wenu Mlezi. Sema: Hakika fadhila zimo mikononi mwa Mwenyezi Mungu. Humpa amtakaye. Na Mwenyezi Mungu ni Mwenye wasaa na Mjuzi.'),
(367, 3, 74, 'Yeye humchagua kumpa rehema zake amtakaye; na Mwenyezi Mungu ndiye Mwenye fadhila kubwa.'),
(368, 3, 75, 'Na miongoni mwa Watu wa Kitabu yupo ambaye ukimpa amana ya mrundi wa mali atakurudishia, na miongoni mwao yupo ambaye ukimpa amana ya dinari moja hakurudishii isipo kuwa ukimsimamia kumdai. Haya ni kwa kuwa wakisema: Hatuna lawama kwa ajili ya hawa wasio jua kusoma. Na wanamzulia uwongo Mwenyezi Mungu, hali nao wanajua.'),
(369, 3, 76, 'Sivyo hivyo, bali anaye timiza ahadi yake na akamchamngu basi Mwenyezi Mungu huwapenda wachamngu.'),
(370, 3, 77, 'Hakika wanao uza ahadi ya Mwenyezi Mungu na viapo vyao thamani ndogo, hao hawatakuwa na sehemu ya kheri yoyote katika Akhera, wala Mwenyezi Mungu hatasema nao wala hatawatazama Siku ya Kiyama, wala hatawatakasa, nao watapata adhabu chungu.'),
(371, 3, 78, 'Na wapo baadhi yao wanao pindua ndimi zao katika kusoma Kitabu ili mdhanie kuwa hayo yanatoka Kitabuni, na wala hayatoki Kitabuni. Na husema: Haya yametoka kwa Mwenyezi Mungu. Na wala hayatoki kwa Mwenyezi Mungu. Na wanamsingizia Mwenyezi Mungu uwongo na wao wanajua.'),
(372, 3, 79, 'Haiwezi kuwa mtu aliye pewa na Mwenyezi Mungu Kitabu na hikima na Unabii kisha awaambie watu: Kuweni wa kuniabudu mimi badala ya Mwenyezi Mungu. Bali atawaambia: Kuweni wenye kumuabudu Mola Mlezi wa viumbe vyote, kwa kuwa nyinyi mnafundisha Kitabu na kwa kuwa mnakisoma.'),
(373, 3, 80, 'Wala hatakuamrisheni kuwafanya Malaika na Manabii kuwa ni miungu. Je, atakuamrisheni ukafiri baada ya kuwa nyinyi Waislamu?'),
(374, 3, 81, 'Na pale Mwenyezi Mungu alipo chukua ahadi kwa Manabii: Nikisha kupeni Kitabu na hikima, kisha akakujieni Mtume mwenye kusadikisha mliyo nayo, ni juu yenu mumuamini na mumsaidie. Akasema: Je, mmekiri na mmekubali kushika agizo langu juu ya hayo? Wakasema: Tumekubali. Akasema: Basi shuhudieni, na Mimi ni pamoja nanyi katika kushuhudia.'),
(375, 3, 82, 'Watakao geuka baada ya haya basi hao ndio wapotovu.'),
(376, 3, 83, 'Je, wanataka dini isiyo kuwa ya Mwenyezi Mungu, na hali kila kilichomo mbinguni na katika ardhi kimesilimu kwa kumt\'ii Yeye kikipenda kisipende, na kwake Yeye watarejeshwa?'),
(377, 3, 84, 'Sema: Tumemuamini Mwenyezi Mungu, na tuliyo teremshiwa sisi, na aliyo teremshiwa Ibrahim, na Ismail, na Is-haak na Yaakub na wajukuu zao, na aliyo pewa Musa na Isa na Manabii wengine kutoka kwa Mola wao Mlezi. Hatubagui baina yao hata mmoja, na sisi ni wenye kusilimu kwake.'),
(378, 3, 85, 'Na anaye tafuta dini isiyo kuwa Uislamu haitakubaliwa kwake. Naye Akhera atakuwa katika wenye kukhasiri.'),
(379, 3, 86, 'Mwenyezi Mungu atawaongoaje watu walio kufuru baada ya kuamini kwao, na wakashuhudia ya kuwa Mtume huyu ni wa haki, na zikawafikia hoja zilizo wazi? Mwenyezi Mungu hawaongoi watu madhaalimu.'),
(380, 3, 87, 'Hao malipo yao ni kuwa ipo juu yao laana ya Mwenyezi Mungu na ya Malaika na ya watu wote.'),
(381, 3, 88, 'Humo watadumu. Hawatapunguziwa adhabu wala hawatapewa nafasi.'),
(382, 3, 89, 'Isipo kuwa wale walio tubu baada ya hayo na wakatengenea; kwani Mwenyezi Mungu ni Mwenye kusamehe na Mwenye kurehemu.'),
(383, 3, 90, 'Hakika wale walio kufuru baada ya kuamini kwao, kisha wakazidi kukufuru, toba yao haitakubaliwa, na hao ndio walio potea.'),
(384, 3, 91, 'Hakika wale walio kufuru, na wakafa hali ni makafiri haitakubaliwa kutoka kwa yeyote wao fidia ya dhahabu ya kujaza dunia nzima lau wangeli itoa. Hao watapata adhabu chungu, wala hawatakuwa na wa kuwanusuru.'),
(385, 3, 92, 'KABISA HAMTAFIKIA wema mpaka mtoe katika vile mnavyo vipenda. Na kitu chochote mnacho kitoa basi hakika Mwenyezi Mungu anakijua.'),
(386, 3, 93, 'Kila chakula kilikuwa halali kwa Wana wa Israili isipo kuwa alicho jizuilia Israili mwenyewe kabla haijateremshwa Taurati. Sema: Leteni Taurati muisome ikiwa nyinyi ni wasema kweli.'),
(387, 3, 94, 'Na wanao mzulia uwongo Mwenyezi Mungu baada ya hayo, basi hao ndio madhaalimu.'),
(388, 3, 95, 'Sema: Mwenyezi Mungu amesema kweli. Basi fuateni mila ya Ibrahim mwongofu, wala hakuwa miongoni mwa washirikina.'),
(389, 3, 96, 'Hakika Nyumba ya kwanza walio wekewa watu kwa ibada ni ile iliyoko Bakka, iliyo barikiwa na yenye uwongofu kwa walimwengu wote.'),
(390, 3, 97, 'Ndani yake zipo Ishara zilizo wazi - masimamio ya Ibrahim, na mwenye kuingia humo anakuwa katika amani. Na kwa ajili ya Mwenyezi Mungu imewajibikia watu wahiji kwenye Nyumba hiyo, kwa yule awezae njia ya kwendea. Na atakaye kanusha basi Mwenyezi Mungu si mhitaji kwa walimwengu.'),
(391, 3, 98, 'Sema: Enyi Watu wa Kitabu! Kwa nini mnakanusha ishara za Mwenyezi Mungu, na ilhali kuwa Mwenyezi Mungu ni Shahidi kwa yote mnayo yatenda?'),
(392, 3, 99, 'Sema: Enyi Watu wa Kitabu! Kwa nini mnamzuilia aliye amini Njia ya Mwenyezi Mungu mkiitafutia kosa, na hali nyinyi mnashuhudia? Na Mwenyezi Mungu si mwenye kughafilika na mnayo yatenda.'),
(393, 3, 100, 'Enyi mlio amini! Mkiwat\'ii baadhi ya walio pewa Kitabu watakurudisheni kuwa makafiri baada ya Imani yenu.'),
(394, 3, 101, 'Na vipi mkufuru hali nyinyi mnasomewa Aya za Mwenyezi Mungu na Mtume yuko kati yenu? Na mwenye kushikamana na Mwenyezi Mungu basi huyo ameongozwa kwenye Njia Iliyo Nyooka.'),
(395, 3, 102, 'Enyi mlio amini! Mcheni Mwenyezi Mungu kama ipasavyo kumcha; wala msife ila nanyi ni Waislamu.'),
(396, 3, 103, 'Na shikamaneni kwa Kamba ya Mwenyezi Mungu nyote pamoja, wala msifarikiane. Na kumbukeni neema ya Mwenyezi Mungu iliyo juu yenu: vile mlivyo kuwa nyinyi kwa nyinyi maadui naye akaziunganisha nyoyo zenu; kwa neema yake mkawa ndugu. Na mlikuwa ukingoni mwa shimo la Moto, naye akakuokoeni nalo. Namna hivi Mwenyezi Mungu anakubainishieni Ishara zake ili mpate kuongoka.'),
(397, 3, 104, 'Na uwe kutokana na nyinyi umma unao lingania kheri na unao amrisha mema na unakataza maovu. Na hao ndio walio fanikiwa.'),
(398, 3, 105, 'Wala msiwe kama wale walio farikiana na kukhitalifiana baada ya kuwafikia hoja zilizo wazi. Na hao ndio watakao kuwa na adhabu kubwa.'),
(399, 3, 106, 'Siku ambayo nyuso zitanawiri na nyuso zitasawijika. Ama hao ambao nyuso zao zitasawijika wataambiwa: Je! Mlikufuru baada ya kuamini kwenu? Basi onjeni adhabu kwa vile mlivyo kuwa mnakufuru.'),
(400, 3, 107, 'Ama wale ambao nyuso zao zitanawiri watakuwa katika rehema ya Mwenyezi Mungu. Wao humo watadumu.'),
(401, 3, 108, 'Hizi ni Aya za Mwenyezi Mungu tunakusomea kwa haki. Na Mwenyezi Mungu hataki kuwadhulumu walimwengu.'),
(402, 3, 109, 'Na kila kilichomo mbinguni na kilichomo ardhini ni cha Mwenyezi Mungu; na mambo yote yatarejea kwa Mwenyezi Mungu.'),
(403, 3, 110, 'Nyinyi mmekuwa bora ya umma walio tolewa watu, kwa kuwa mnaamrisha mema na mnakataza maovu, na mnamuamini Mwenyezi Mungu. Na lau kuwa Watu wa Kitabu nao wameamini ingeli kuwa bora kwao. Wapo miongoni mwao waumini, lakini wengi wao wapotovu.'),
(404, 3, 111, 'Maadui hawatakudhuruni ila ni maudhi tu. Na wakipigana nanyi watakupeni mgongo, na tena hawatanusuriwa.'),
(405, 3, 112, 'Wamepigwa na udhalili popote wanapo kutikana, isipo kuwa wakishika kamba ya Mwenyezi Mungu na kamba ya watu. Na wakastahiki ghadhabu ya Mwenyezi Mungu, na wamepigwa na unyonge. Hayo ni kwa sababu walikuwa wakizikataa Ishara za Mwenyezi Mungu, na wakiwauwa Manabii pasina haki; hayo ni kwa sababu waliasi na walikuwa wakiruka mipaka.'),
(406, 3, 113, 'Wote hao si sawa sawa. Miongoni mwa Watu wa Kitabu wamo watu walio simama baraabara, wanasoma Aya za Mwenyezi Mungu nyakati za usiku, na pia wanasujudu.'),
(407, 3, 114, 'Wanamuamini Mwenyezi Mungu na Siku ya Mwisho, na wanaamrisha mema na wanakataza maovu na wanakimbilia katika mambo ya kheri. Na hao ndio miongoni mwa watenda mema.'),
(408, 3, 115, 'Na kheri yoyote wanayo ifanya hawatanyimwa malipwa yake. Na Mwenyezi Mungu anawajua wachamngu.'),
(409, 3, 116, 'Hakika wale walio kufuru hazitawafaa kitu mali zao wala watoto wao mbele ya Mwenyezi Mungu. Na hao ndio watu wa Motoni - humo watadumu.'),
(410, 3, 117, 'Mfano wa vile wanavyo vitoa katika uhai wao wa duniani ni kama upepo ambao ndani yake ipo baridi ya barafu, ukalisibu shamba la watu walio dhulumu nafsi zao, ukaliteketeza. Na Mwenyezi Mungu hakuwadhulumu, bali wao wenyewe wanadhulumu nafsi zao.'),
(411, 3, 118, 'Enyi mlio amini! Msiwafanye wasiri wenu watu wasio kuwa katika nyinyi. Hawataacha kukufanyieni ubaya. Wanayapenda yanayo kudhuruni. Imekwisha fichuka chuki yao katika midomo yao. Na yanayo ficha vifua vyao ni makubwa zaidi. Tumekwisha kubainishieni Ishara ikiwa nyinyi mtayatia akilini.'),
(412, 3, 119, 'Hivyo basi nyinyi ndio mnawapenda watu hao, wala wao hawakupendeni. Nanyi mnaviamini Vitabu vyote. Na wanapo kutana nanyi husema: Tumeamini. Na wanapo kuwa peke yao wanakuumieni vidole kwa chuki. Sema: Kufeni kwa chuki yenu! Hakika Mwenyezi Mungu anayajua yaliomo vifuani.'),
(413, 3, 120, 'Ikikupateni kheri huwaudhi. Na ikikupateni shari wanafurahia. Na nyinyi mkisubiri na mkajizuilia, hila zao hazitakudhuruni kitu. Hakika Mwenyezi Mungu ni mwenye kuyazunguka yote wayatendayo.'),
(414, 3, 121, 'Na pale ulipo toka asubuhi, ukaacha ahali zako uwapange Waumini kwenye vituo kwa ajili ya vita - na Mwenyezi Mungu anasikia na anajua.'),
(415, 3, 122, 'Makundi mawili miongoni mwenu yakaingiwa na woga kuwa watashindwa - na hali Mwenyezi Mungu ndiye Mlinzi wao; na Waumini wamtegemee Mwenyezi Mungu tu.'),
(416, 3, 123, 'Na Mwenyezi Mungu alikwisha kunusuruni katika Badri nanyi mlikuwa wanyonge. Basi mcheni Mwenyezi Mungu ili mpate kushukuru.'),
(417, 3, 124, 'Ulipo waambia Waumini: Je, haitakutosheni ikiwa Mola wenu atakusaidieni kwa Malaika elfu tatu walio teremshwa?'),
(418, 3, 125, 'Kwani! Ikiwa mtavumilia na mkamchamngu na hata maadui wakikutokeeni kwa ghafla, basi hapo Mola wenu Mlezi atakusaidieni kwa Malaika elfu tano wanao shambulia kwa nguvu.'),
(419, 3, 126, 'Na Mwenyezi Mungu hakufanya haya ila kuwa ni bishara kwenu, na ili nyoyo zenu zipate kutua. Na msaada hautoki isipo kuwa kwa Mwenyezi Mungu, Aliye tukuka, Mwenye nguvu na Mwenye hikima.'),
(420, 3, 127, 'Haya ni kwa ajili awakate sehemu katika walio kufuru, au awahizi wapate kurejea nyuma nao wamepwelewa.'),
(421, 3, 128, 'Wewe huna lako jambo katika haya - ama atawahurumia au atawaadhibu, kwani wao ni madhaalimu.'),
(422, 3, 129, 'Na ni vya Mwenyezi Mungu vyote vilivyomo katika mbingu na vilivyomo katika ardhi. Yeye humsamehe na humuadhibu amtakaye. Na Mwenyezi Mungu ni Msamehevu Mwenye kurehemu.'),
(423, 3, 130, 'Enyi mlio amini! Msile riba mkizidisha juu kwa juu, na mcheni Mwenyezi Mungu ili mpate kufanikiwa.'),
(424, 3, 131, 'Na uogopeni Moto ambao umeandaliwa kwa ajili ya makafiri.'),
(425, 3, 132, 'Na mt\'iini Mwenyezi Mungu na Mtume ili mpate kurehemewa.'),
(426, 3, 133, 'Na yakimbilieni maghfira ya Mola wenu Mlezi, na Pepo ambayo upana wake ni mbingu na ardhi, iliyo wekwa tayari kwa wachamngu,'),
(427, 3, 134, 'Ambao hutoa wanapo kuwa na wasaa na wanapo kuwa na dhiki, na wanajizuia ghadhabu, na wasamehevu kwa watu; na Mwenyezi Mungu huwapenda wafanyao wema;'),
(428, 3, 135, 'Na ambao pindi wafanyapo uchafu au wakajidhulumu nafsi zao humkumbuka Mwenyezi Mungu na wakamwomba msamaha kwa dhambi zao - na nani anaye futa dhambi isipo kuwa Mwenyezi Mungu? - na wala hawaendelei na waliyo yafanya na hali wanajua.'),
(429, 3, 136, 'Hao malipo yao ni msamaha kwa Mola wao Mlezi, na Bustani zipitazo mito kati yake. Humo watadumu, na mwema mno ujira wa watendao.'),
(430, 3, 137, 'Kabla yenu zimepita nyendo nyingi; basi tembeeni katika ulimwengu muangalie vipi ulikuwa mwisho wa wanao kanusha.'),
(431, 3, 138, 'Hii ni bayana kwa watu wote, na ni uwongofu, na mawaidha kwa wachamngu.'),
(432, 3, 139, 'Wala msilegee, wala msihuzunike, kwani nyinyi ndio wa juu mkiwa ni Waumini.'),
(433, 3, 140, 'Kama yamekupateni majaraha, basi na hao watu wengine yamewapata majaraha mfano wa haya. Na siku za namna hii tunaweletea watu kwa zamu, ili Mwenyezi Mungu awapambanue walio amini na awateuwe miongoni mwenu mashahidi. Na Mwenyezi Mungu hawapendi madhaalimu;'),
(434, 3, 141, 'Na pia ili Mwenyezi Mungu awasafishe walio amini na awafutilie mbali makafiri.'),
(435, 3, 142, 'Je! Mnadhani mtaingia Peponi na hali Mwenyezi Mungu hajawapambanua wale miongoni mwenu walio pigana Jihadi, na hajawapambanua walio subiri?'),
(436, 3, 143, 'Na nyinyi mlikuwa mkitamani mauti kabla hamjakutana nayo. Basi sasa mmekwisha yaona na huku mnayatazama.'),
(437, 3, 144, 'Na Muhammad hakuwa ila ni Mtume tu. Wamekwisha pita kabla yake Mitume. Je, akifa au akauwawa ndiyo mtageuka mrudi nyuma? Na atakaye geuka akarudi nyuma huyo hatamdhuru kitu Mwenyezi Mungu. Na Mwenyezi Mungu atawalipa wanao mshukuru.'),
(438, 3, 145, 'Na haiwezi nafsi kuwa ife ila kwa idhini ya Mwenyezi Mungu kama ilivyo andikwa ajali yake. Na mwenye kutaka malipo ya duniani tutampa; na mwenye kutaka malipo ya Akhera tutampa. Na tutawalipa wenye kushukuru.'),
(439, 3, 146, 'Na Manabii wangapi walipigana na pamoja nao Waumini wengi wenye ikhlasi! Hawakufanya woga kwa yaliyo wasibu katika Njia ya Mwenyezi Mungu, wala hawakudhoofika wala hawakuwa wanyonge. Na Mwenyezi Mungu huwapenda wanao subiri.'),
(440, 3, 147, 'Wala kauli yao haikuwa ila ni kusema: Mola wetu Mlezi! Tughufirie madhambi yetu na kupita kwetu kiasi katika mambo yetu, na isimamishe imara miguu yetu na utunusuru tuwashinde kaumu ya makafiri.'),
(441, 3, 148, 'Basi Mwenyezi Mungu akawapa malipo ya duniani na bora ya malipo ya Akhera. Na Mwenyezi Mungu anawapenda wafanyao mema.'),
(442, 3, 149, 'Enyi mlio amini! Ikiwa mtawat\'ii walio kufuru watakurudisheni nyuma, na hapo mtageuka kuwa wenye kukhasiri.'),
(443, 3, 150, 'Bali Mwenyezi Mungu ndiye Mlinzi wenu, naye ndiye bora wa wasaidizi.'),
(444, 3, 151, 'Tutazitia khofu nyoyo za walio kufuru kwa vile walivyo mshirikisha Mwenyezi Mungu na washirika ambao hakuwateremshia hoja yoyote. Na makaazi yao ni Motoni; na maovu yaliyoje maskani ya wenye kudhulumu!'),
(445, 3, 152, 'Na Mwenyezi Mungu alikutimilizieni miadi yake, vile mlivyo kuwa mnawauwa kwa idhini yake, mpaka mlipo legea na mkazozana juu ya amri, na mkaasi baada ya Yeye kukuonyesheni mliyo yapenda. Wapo miongoni mwenu wanao taka dunia, na wapo miongoni mwenu wanao taka Akhera. Kisha akakutengeni nao (maadui) ili akujaribuni. Naye sasa amekwisha kusameheni. Na Mwenyezi Mungu ni Mwenye fadhila juu ya Waumini.'),
(446, 3, 153, 'Pale mlipo kuwa mkikimbia mbio wala hamumsikilizi yeyote, na Mtume anakuiteni, yuko nyuma yenu. Mwenyezi Mungu akakupeni dhiki juu ya dhiki, ili msisikitike kwa yaliyo kukoseeni wala kwa yaliyo kusibuni. Na Mwenyezi Mungu anazo khabari za yote mnayo yatenda.'),
(447, 3, 154, 'Kisha baada ya dhiki alikuteremshieni utulivu - usingizi ambao ulifunika kundi moja kati yenu. Na kundi jengine nafsi zao ziliwashughulisha hata wakamdhania Mwenyezi Mungu dhana isiyo kuwa ya haki, dhana ya kijinga. Wakisema: Hivyo tuna lolote sisi katika jambo hili? Sema: Mambo yote ni ya Mwenyezi Mungu. Wanaficha katika nafsi zao wasiyo kubainishia. Wanasema: Lau ingelikuwa tuna lolote katika jambo hili tusinge uliwa hapa. Sema: Hata mngeli kuwa majumbani mwenu, basi wangeli toka walio andikiwa kufa, wakenda mahali pao pa kuangukia wafe. (Haya ni hivyo) ili Mwenyezi Mungu ayajaribu yaliyomo vifuani mwenu, na asafishe yaliyomo nyoyoni mwenu. Na Mwenyezi Mungu anayajua yaliyomo vifuani.'),
(448, 3, 155, 'Hakika wale walio rudi nyuma miongoni mwenu siku yalipo pambana majeshi mawili, Shet\'ani ndiye aliye watelezesha kwa sababu ya baadhi ya makosa waliyo yafanya; na Mwenyezi Mungu amekwisha wasamehe. Hakika Mwenyezi Mungu ni Mwenye kusamehe, Mpole.'),
(449, 3, 156, 'Enyi mlio amini! Msiwe kama wale walio kufuru na wakawasema ndugu zao walipo safiri katika nchi au walipo kuwa vitani: Wangeli kuwa kwetu wasingeli kufa na wasingeli uwawa. Ili Mwenyezi Mungu afanye hayo kuwa ni majuto katika nyoyo zao. Na Mwenyezi Mungu huhuisha na hufisha. Na Mwenyezi Mungu ni Mwenye kuyaona myatendayo.'),
(450, 3, 157, 'Na pindi mkiuliwa katika Njia ya Mwenyezi Mungu, au mkifa, hakika maghfira na rehema zitokazo kwa Mwenyezi Mungu ni bora kuliko yote wanayo yakusanya.'),
(451, 3, 158, 'Na mkifa au mkiuliwa ni kwa Mwenyezi Mungu ndio mtakusanywa.'),
(452, 3, 159, 'Basi ni kwa sababu ya rehema itokayo kwa Mwenyezi Mungu ndio umekuwa laini kwao. Na lau ungeli kuwa mkali, mwenye moyo mgumu, bila ya shaka wangeli kukimbia. Basi wasamehe, na waombee maghfira, na shauriana nao katika mambo. Na ukisha kata shauri basi mtegemee Mwenyezi Mungu. Hakika Mwenyezi Mungu huwapenda wanao mtegemea.'),
(453, 3, 160, 'Akikunusuruni Mwenyezi Mungu hapana wa kukushindeni, na akikutupeni ni nani, basi, baada yake Yeye ataye kunusuruni? Na Waumini wamtegemee Mwenyezi Mungu tu.'),
(454, 3, 161, 'Na haiwezekani kwa Nabii yeyote kufanya khiyana. Na atakaye fanya khiyana, atayaleta Siku ya Kiyama aliyo yafanyia khiyana, kisha kila mtu atalipwa kwa aliyo yachuma, kwa ukamilifu; wala hawatadhulumiwa.'),
(455, 3, 162, 'Je! Aliye yafuata ya kumridhisha Mwenyezi Mungi ni kama yule aliye stahiki ghadhabu ya Mwenyezi Mungu, na makaazi yake yakawa Jahannamu? Napo ni mahala pabaya pa kurejea.'),
(456, 3, 163, 'Hao wana vyeo mbali mbali kwa Mwenyezi Mungu. Na Mwenyezi Mungu ni Mwenye kuyaona yote wayatendayo.'),
(457, 3, 164, 'Hakika Mwenyezi Mungu amewafanyia wema mkubwa Waumini vile alivyo waletea Mtume aliye miongoni mwao wenyewe, anaye wasomea Aya zake, na anawatakasa, na anawafunza Kitabu na hikima, ijapo kuwa kabla ya hapo walikuwa katika upotovu ulio wazi.'),
(458, 3, 165, 'Nyinyi - ulipo kusibuni msiba ambao nyinyi mmekwisha watia mara mbili mfano wa huo - mnasema: Umetoka wapi huu? Sema: Huo umetoka kwenu wenyewe. Hakika Mwenyezi Mungu ni Mwenye uweza juu ya kila kitu.'),
(459, 3, 166, 'Na yaliyo kusibuni siku yalipo pambana majeshi mawili yalikuwa kwa idhini ya Mwenyezi Mungu, na ili awajue Waumini.'),
(460, 3, 167, 'Na ili awapambanue walio kuwa wanaafiki, wakaambiwa: Njooni mpigane katika Njia ya Mwenyezi Mungu au lindeni. Wakasema: Tungeli jua kuna kupigana bila ya shaka tungeli kufuateni. Wao siku ile walikuwa karibu na ukafiri kuliko Imani. Wanasema kwa midomo yao yasiyo kuwamo nyoyoni mwao. Na Mwenyezi Mungu anajua kabisa wanayo yaficha.'),
(461, 3, 168, 'Hao ndio walio waambia ndugu zao na wao wenyewe wakakaa kitako: Lau wengeli tut\'ii wasingeli uliwa. Sema: Ziondoleeni nafsi zenu mauti kama mnasema kweli.'),
(462, 3, 169, 'Wala kabisa usiwadhanie walio uliwa katika Njia ya Mwenyezi Mungu kuwa ni maiti. Bali hao ni wahai, wanaruzukiwa kwa Mola wao Mlezi.'),
(463, 3, 170, 'Wanafurahia aliyo wapa Mwenyezi Mungu kwa fadhila yake, na wanawashangilia wale ambao bado hawajajiunga nao, walio nyuma yao, ya kwamba haitakuwa khofu juu yao wala hawatahuzunika.'),
(464, 3, 171, 'Wanashangilia neema na fadhila za Mwenyezi Mungu, na ya kwamba Mwenyezi Mungu hapotezi ujira wa Waumini,'),
(465, 3, 172, 'Walio mwitikia Mwenyezi Mungu na Mtume baada ya kwisha patwa na majaraha - kwa walio fanya wema miongoni mwao na wakamchamngu utakuwa ujira mkubwa -'),
(466, 3, 173, 'Walio ambiwa na watu: Kuna watu wamekukusanyikieni, waogopeni! Hayo yakawazidishia Imani, wakasema: Mwenyezi Mungu anatutosha, naye ni mbora wa kutegemewa.'),
(467, 3, 174, 'Basi wakarudi na neema na fadhila za Mwenyezi Mungu. Hapana baya lilio wagusa, na wakafuata yanayo mridhi Mwenyezi Mungu. Na Mwenyezi Mungu ni Mwenye fadhila kuu.'),
(468, 3, 175, 'Hakika huyo ni Shet\'ani anawatia khofu marafiki zake, basi msiwaogope, bali niogopeni Mimi mkiwa nyinyi ni Waumini.'),
(469, 3, 176, 'Wala wasikuhuzunishe wale wanao kimbilia ukafirini. Hakika hao hawamdhuru kitu Mwenyezi Mungu. Mwenyezi Mungu anataka asiwawekee sehemu yoyote katika Akhera, na yao wao adhabu kubwa.'),
(470, 3, 177, 'Hakika wale walio nunua ukafiri kwa Imani hawatamdhuru kitu Mwenyezi Mungu, na yao wao ni adhabu chungu.'),
(471, 3, 178, 'Wala wasidhani wale wanao kufuru kwamba huu muhula tunao wapa ni kheri yao. Hakika tunawapa muhula wazidi madhambi. Na yao wao ni adhabu ya kuwadhalilisha.'),
(472, 3, 179, 'Haiwi kwa Mwenyezi Mungu kuwaacha Waumini katika hali mliyo nayo mpaka apambanue wabaya na wema. Wala haiwi kwa Mwenyezi Mungu kukujuulisheni mambo ya ghaibu. Lakini Mwenyezi Mungu humteua katika Mitume wake amtakaye. Basi muaminini Mwenyezi Mungu na Mitume yake. Na mkiamini na mkamchamngu mtakuwa na ujira mkubwa.'),
(473, 3, 180, 'Wala wasidhani wale ambao wanafanya ubakhili katika aliyo wapa Mwenyezi Mungu katika fadhila zake kuwa ni kheri yao. Bali hiyo ni shari kwao. Watafungwa kongwa za yale waliyo yafanyia ubakhili Siku ya Kiyama. Na urithi wa mbingu na ardhi ni wa Mwenyezi Mungu. Na Mwenyezi Mungu anazo khabari za yote myatendayo.'),
(474, 3, 181, 'Mwenyezi Mungu amekwisha sikia kauli ya walio sema: Mwenyezi Mungu ni masikini, na sisi ni matajiri. Tumeyaandika waliyo yasema, na pia kuwauwa kwao Manabii bila ya haki, na tutawaambia Siku ya Kiyama: Onjeni adhabu ya kuungua.'),
(475, 3, 182, 'Haya ni kwa sababu ya iliyo tanguliza mikono yenu, na kwamba Mwenyezi Mungu si mwenye kuwadhulumu waja,'),
(476, 3, 183, 'Walio sema: Mwenyezi Mungu ametuahidi tusimuamini Mtume yeyote mpaka atuletee kafara inayo teketezwa na moto. Waambie: Walikujieni Mitume kabla yangu kwa hoja zilizo wazi na kwa hayo mnayo sema, basi kwa nini mkawauwa ikiwa mlikuwa wakweli?'),
(477, 3, 184, 'Na wakikukanusha basi walikanushwa Mitume wengine kabla yako walio kuja na hoja waziwazi na Vitabu vyenye hikima, na Kitabu chenye nuru.'),
(478, 3, 185, 'Kila nafsi itaonja mauti. Na bila ya shaka mtapewa ujira wenu kaamili Siku ya Kiyama. Na atakaye epushwa na Moto na akatiwa Peponi basi huyo amefuzu. Na maisha ya dunia si kitu ila ni starehe ya udanganyifu.'),
(479, 3, 186, 'Hapana shaka yoyote mtapata misukosuko katika mali zenu na nafsi zenu, na bila ya shaka yoyote mtasikia udhia mwingi kutokana na walio pewa Kitabu kabla yenu na wale walio shiriki. Na ikiwa mtasubiri na mkamchamngu basi hakika hayo ni katika mambo ya kuazimia.'),
(480, 3, 187, 'Na pale Mwenyezi Mungu alipo fungamana na walio pewa Kitabu kuwa: Lazima mtawabainishia watu, wala hamtokificha, walikitupa nyuma ya migongo yao, na wakanunua kwacho thamani duni. Basi ni kiovu hicho walicho nunua.'),
(481, 3, 188, 'Usiwadhanie kabisa wale wanao furahia waliyo yafanya, na wakapenda kusifiwa kwa wasiyo yatenda, usiwadhanie kuwa watasalimika na adhabu. Yao wao ni adhabu chungu.'),
(482, 3, 189, 'Na Ufalme wa mbingu na ardhi ni wa Mwenyezi Mungu; na Mwenyezi Mungu ni Mwenye uweza juu ya kila kitu.'),
(483, 3, 190, 'Hakika katika kuumbwa mbingu na ardhi na kukhitalifiana usiku na mchana ziko Ishara kwa wenye akili,'),
(484, 3, 191, 'Ambao humkumbuka Mwenyezi Mungu wakiwa wima na wakikaa kitako na wakilala, na hufikiri kuumbwa mbingu na ardhi, wakisema: Mola wetu Mlezi! Hukuviumba hivi bure. Subhanaka, Umetakasika! Basi tukinge na adhabu ya Moto.'),
(485, 3, 192, 'Mola wetu Mlezi! Hakika unaye mtia Motoni umemhizi; na walio dhulumu hawana wasaidizi.'),
(486, 3, 193, 'Mola wetu Mlezi! Hakika sisi tumemsikia mwenye kuita akiitia Imani kwamba: Muaminini Mola wenu Mlezi; nasi tukaamini. Basi tusamehe madhambi yetu, na utufutie makosa yetu, na utufishe pamoja na watu wema.'),
(487, 3, 194, 'Mola wetu Mlezi! Na utupe uliyo tuahidi kwa Mitume wako, wala usituhizi Siku ya Kiyama. Hakika Wewe huvunji miadi.'),
(488, 3, 195, 'Mola Mlezi wao, akayakubali maombi yao akajibu: Hakika sipotezi kazi ya mfanya kazi miongoni mwenu, akiwa mwanamume au mwanamke, kwani ni nyinyi kwa nyinyi. Basi walio hama, na walio tolewa makwao, na wakateswa katika Njia yangu, na wakapigana, na wakauliwa, kwa yakini Mimi nitawafutia makosa yao, na kwa yakini nitawaingiza katika Mabustani yanayo pita mito kati yake. Hayo ndiyo malipo yanayo toka kwa Mwenyezi Mungu. Na Mwenyezi Mungu kwake yapo malipo mema kabisa.'),
(489, 3, 196, 'Kabisa kusikudanganye kubakia kutangatanga kwa walio kufuru katika nchi.'),
(490, 3, 197, 'Hiyo ni starehe ndogo. Kisha makaazi yao yatakuwa Jahannamu. Na ni mahali pabaya mno pa kupumzikia.'),
(491, 3, 198, 'Lakini walio mcha Mola wao Mlezi watapata Mabustani yanayo pita mito kati yake. Watadumu humo. Hayo ni makaribisho yao yatokayo kwa Mwenyezi Mungu. Na vilioko kwa Mwenyezi Mungu ni bora kwa watu wema.'),
(492, 3, 199, 'Hakika miongoni mwa Watu wa Kitabu wapo wanao muamini Mwenyezi Mungu na yaliyo teremshwa kwenu na yaliyo teremshwa kwao, wakimnyenyekea Mwenyezi Mungu. Hawabadili Aya za Mwenyezi Mungu kwa thamani duni. Hao wana ujira wao kwa Mola wao Mlezi. Hakika Mwenyezi Mungu ni Mwepesi wa kuhisabu.'),
(493, 3, 200, 'Enyi mlio amini! Subirini, na shindaneni kusubiri, na kuweni macho, na mcheni Mwenyezi Mungu, ili mpate kufanikiwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Enyi watu! Mcheni Mola wenu Mlezi aliye kuumbeni kutokana na nafsi moja, na akamuumba mkewe kutoka nafsi ile ile. Na akaeneza kutokana na wawili hao wanaume na wanawake wengi. Na tahadharini na Mwenyezi Mungu ambaye kwaye mnaombana, na jamaa zenu. Hakika Mwenyezi Mungu ni Mwenye kuwaangalieni.'),
(495, 4, 2, 'Na wapeni mayatima mali yao. Wala msibadilishe kibaya kwa kizuri. Wala msile mali zao pamoja na mali zenu. Hakika yote hayo ni jukumu kubwa.'),
(496, 4, 3, 'Na ikiwa mnaogopa kuwa hamtowafanyia mayatima uadilifu, basi oeni mnao wapenda katika wanawake, wawili au watatu au wane. na mkiogopa kuwa hamwezi kufanya uadilifu, basi mmoja tu, au wale ambao mikono yenu ya kulia imewamiliki. Kufanya hivi ndiko kutapelekea msikithirishe wana.'),
(497, 4, 4, 'Na wapeni wanawake mahari yao hali ya kuwa ni kipawa. Lakini wakikutunukieni kitu katika mahari, kwa radhi yao, basi kileni kiwashuke kwa raha.'),
(498, 4, 5, 'Wala msiwape wasio na akili mali yenu ambayo Mwenyezi Mungu ameyajaalia yawe ni kiamu yenu. Walisheni katika hayo na muwavike, na mseme nao maneno mazuri.'),
(499, 4, 6, 'Na wajaribuni mayatima mpaka wafike umri wa kuoa. Mkiwaona ni wekevu, basi wapeni mali yao. Wala msiyale kwa fujo na pupa kwa kuwa watakuja kuwa watu wazima. Na aliye kuwa tajiri naajizuilie, na aliye fakiri basi naale kwa kadri ya ada. Na mtakapo wapa mali yao washuhudizieni. Na Mwenyezi Mungu anatosha kuwa Mhasibu.'),
(500, 4, 7, 'Wanaume wana sehemu katika wanayo yaacha wazazi na jamaa walio karibia. Na wanawake wanayo sehemu katika waliyo yaacha wazazi na jamaa walio karibia. Ikiwa kidogo au kingi. Hizi ni sehemu zilizo faridhiwa.'),
(501, 4, 8, 'Na wakati wa kugawanya wakihudhuria jamaa na mayatima na masikini, wapeni katika hayo mali ya urithi, na semeni nao maneno mema.'),
(502, 4, 9, 'Na wachelee wale ambao lau kuwa na wao wangeli acha nyuma yao watoto wanyonge wangeli wakhofia. Basi nao wamche Mwenyezi Mungu, na waseme maneno yaliyo sawa.'),
(503, 4, 10, 'Hakika wanao kula mali ya mayatima kwa dhulma, hapana shaka yoyote wanakula matumboni mwao moto, na wataingia Motoni.'),
(504, 4, 11, 'Mwenyezi Mungu anakuusieni juu ya watoto wenu: Fungu la mwanamume ni kama fungu la wanawake wawili. Na ikiwa wanawake zaidi ya wawili, basi watapata thuluthi mbili za alicho kiacha maiti. Lakini akiwa mtoto mwanamke ni mmoja, basi fungu lake ni nusu. Na wazazi wake wawili, kila mmoja wao apate sudusi ya alicho kiacha, ikiwa anaye mtoto. Akiwa hana mtoto, na wazazi wake wawili wamekuwa ndio warithi wake, basi mama yake atapata thuluthi moja. Na akiwa anao ndugu, basi mama yake atapata sudusi. Haya ni baada ya kutolewa alicho usia au kulipa deni. Baba zenu na watoto wenu, nyinyi hamjui ni nani baina yao aliye karibia zaidi kwenu kwa manufaa. Hiyo ni Sharia iliyo toka kwa Mwenyezi Mungu. Bila ya shaka Mwenyezi Mungu ni Mjuzi na Mwenye hikima.'),
(505, 4, 12, 'Na fungu lenu ni nusu walicho acha wake zenu ikiwa hawana mtoto. Wakiwa na mtoto basi fungu lenu ni robo ya walicho acha, baada ya wasia waliyo usia au kulipa deni. Na wake zenu watapata robo mlicho kiacha, ikiwa hamna mtoto. Mkiwa mna mtoto basi sehemu yao ni thumni ya mlicho kiacha, baada ya wasia mlio usia au kulipa deni. Na ikiwa mwanamume au mwanamke anaye rithiwa hana mtoto wala wazazi, lakini anaye ndugu mume au ndugu mke, basi kila mmoja katika hawa atapata sudusi. Na wakiwa zaidi kuliko hivyo basi watashirikiana katika thuluthi, baada ya wasia ulio usiwa au kulipa deni, pasio kuleta dhara. Huu ndio wasia ulio toka kwa Mwenyezi Mungu. Na Mwenyezi Mungu ni Mjuzi na Mpole.'),
(506, 4, 13, 'Hiyo ni mipaka ya Mwenyezi Mungu. Na anaye mt\'ii Mwenyezi Mungu na Mtume wake, Yeye atamtia katika Pepo zipitazo mito kati yake, wadumu humo. Na huko ndiko kufuzu kukubwa.'),
(507, 4, 14, 'Na anaye muasi Mwenyezi Mungu na Mtume wake, na akaipindukia mipaka yake, (Mwenyezi Mungu) atamtia Motoni adumu humo, na atapata adhabu ya kudhalilisha.'),
(508, 4, 15, 'Na ambao wanafanya uchafu miongoni mwa wanawake wenu, washuhudizieni watu wane katika nyinyi. Watakapo shuhudia, basi wazuieni majumbani mpaka wafishwe na mauti au Mwenyezi Mungu awatolee njia nyengine.'),
(509, 4, 16, 'Na wawili kati yenu wafanyao hayo waadhibuni. Na wakitubia wakatengenea basi waacheni. Hakika Mwenyezi Mungu ni Mwingi wa kupokea toba, Mwenye kurehemu.'),
(510, 4, 17, 'Hakika toba inayo kubaliwa na Mwenyezi Mungu ni ya wale wafanyao uovu kwa ujinga, kisha wakatubia kwa haraka. Hao ndio Mwenyezi Mungu huwakubalia toba yao, na Mwenyezi Mungu ni Mjuzi na ni Mwenye hikima.'),
(511, 4, 18, 'Wala hawana toba wale wafanyao maovu mpaka yakamfikia mmoja wao mauti, kisha hapo akasema: Hakika mimi sasa nimetubia. Wala wale wanao kufa na hali wao ni makafiri. Hao tumewaandalia adhabu chungu.'),
(512, 4, 19, 'Enyi mlio amini! Si halali kwenu kuwarithi wanawake kwa nguvu. Wala msiwadhikishe ili mwapokonye baadhi ya mlivyo wapa - isipo kuwa wakifanya uchafu ulio wazi. Na kaeni nao kwa wema, na ikiwa mmewachukia, basi huenda mkakichukia kitu, na Mwenyezi Mungu ametia kheri nyingi ndani yake.'),
(513, 4, 20, 'Na mkitaka kubadilisha mke mahali pa mke, na hali mmoja wao mmempa chungu ya mali, basi msichukue katika hayo kitu chochote. Je, mtachukua kwa dhulma na kosa lilio wazi?'),
(514, 4, 21, 'Na mtachukuaje na ilhali mmekwisha ingiliana nyinyi kwa nyinyi, na wao wanawake wamechukua kwenu ahadi iliyo madhubuti?'),
(515, 4, 22, 'Wala msiwaoe wake walio waoa baba zenu, ila yaliyo kwisha pita. Hakika huo ni uchafu na uchukizo na ni njia mbaya.'),
(516, 4, 23, 'Mmeharimishiwa mama zenu, na binti zenu, na dada zenu, na shangazi zenu, na khaalati zenu, na binti wa ndugu mume, na binti wa dada, na mama zenu walio kunyonyesheni, na dada zenu kwa kunyonya, na mama wa wake zenu, na watoto wenu wa kambo walio katika ulinzi wenu walio zaliwa na wake zenu mlio waingilia. Ikiwa hamkuwaingilia basi hapana lawama juu yenu. (Pia mmeharimishwa) wake wa watoto wenu walio toka katika migongo yenu. Na kuwaoa pamoja dada wawili isipo kuwa yale yaliyo kwisha pita. Hakika Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(517, 4, 24, 'NA WANAWAKE wenye waume, isipo kuwa walio milikiwa na mikono yenu ya kulia. Ndiyo Sharia ya Mwenyezi Mungu juu yenu. Na mmehalilishiwa wasio kuwa hao, mtafute kwa mali yenu kwa kuowa pasina kuzini. Kama mnavyo starehe nao, basi wapeni mahari yao kwa kuwa ni waajibu. Wala hapana lawama juu yenu kwa mtakacho kubaliana baada ya kutimiza waajibu. Hakika Mwenyezi Mungu ni Mwenye kujua na Mwenye hikima.'),
(518, 4, 25, 'Na asiyeweza kati yenu kupata mali ya kuolea wanawake wa kiungwana Waumini, na aoe katika vijakazi Waumini iliyo wamiliki mikono yenu ya kulia. Na Mwenyezi Mungu ni Mjuzi wa imani yenu. Nyinyi mmetokana wenyewe kwa wenyewe. Basi waoeni kwa idhini ya watu wao, na wapeni mahari yao kama ada, wawe wanawake wema, si makahaba wala si mahawara. Na wanapo olewa kisha wakafanya uchafu basi adhabu yao itakuwa ni nusu ya adhabu waliyo wekewa waungwana. Hayo ni kwa yule miongoni mwenu anaye ogopa kuingia katika zina. Na mkisubiri ndio bora kwenu. Na Mwenyezi Mungu ni Mwenye kusamehe na Mwenye kurehemu.'),
(519, 4, 26, 'Mwenyezi Mungu anataka kukubainishieni na kukuongozeni nyendo za walio kuwa kabla yenu, na akurejezeni kwenye ut\'iifu wake. Na Mwenyezi Mungu ni Mjuzi, Mwenye hikima.'),
(520, 4, 27, 'Na Mwenyezi Mungu anataka kukurejezeni kwenye ut\'iifu wake, na wanao taka kufuata matamanio wanataka mkengeuke makengeuko makubwa.'),
(521, 4, 28, 'Mwenyezi Mungu anataka kukupunguzieni taabu, na mwanaadamu ameumbwa dhaifu.'),
(522, 4, 29, 'Enyi mlio amini! Msiliane mali yenu kwa dhulma, isipo kuwa iwe biashara kwa kuridhiana wenyewe. Wala msijiuwe. Hakika Mwenyezi Mungu ni Mwenye kuwarehemuni.'),
(523, 4, 30, 'Na mwenye kutenda hayo kwa uadui na udhaalimu, basi huyo tutamwingiza Motoni. Na hilo ni jepesi kwa Mwenyezi Mungu.'),
(524, 4, 31, 'Mkiyaepuka makubwa mnayo katazwa, tutakufutieni makosa yenu madogo, na tutakuingizeni mahali patukufu.'),
(525, 4, 32, 'Wala msitamani alicho wafadhili Mwenyezi Mungu baadhi yenu kuliko wengine. Wanaume wana fungu katika walio vichuma, na wanawake wana fungu katika walio vichuma. Na muombeni Mwenyezi Mungu fadhila zake. Hakika Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(526, 4, 33, 'Na kila mmoja tumemwekea warithi katika waliyo yaacha wazazi wawili na jamaa. Na mlio fungamana nao ahadi wapeni fungu lao. Hakika Mwenyezi Mungu ni Shahidi wa kila kitu.'),
(527, 4, 34, 'Wanaume ni wasimamizi wa wanawake, kwa kufadhiliwa na Mwenyezi Mungu baadhi yao juu ya baadhi, na kwa mali yao wanayo yatoa. Basi wanawake wema ni wale wenye kut\'ii na wanao jilinda hata katika siri kwa kuwa Mwenyezi Mungu ameamrisha wajilinde. Na ambao mnachelea kutoka katika ut\'iifu wanasihini, muwahame katika malazi na wapigeni. Wakikut\'iini msiwatafutie njia ya kuwaudhi bure. Hakika Mwenyezi Mungu ndiye aliye juu na Mkuu.'),
(528, 4, 35, 'Na mkichelea kutakuwapo mfarakano baina ya mke na mume basi pelekeni muamuzi kutokana na jamaa za mume, na muamuzi kutokana na jamaa za mke. Wakitaka mapatano Mwenyezi Mungu atawawezesha. Hakika Mwenyezi Mungu ni Mjuzi Mwenye khabari.'),
(529, 4, 36, 'Muabuduni Mwenyezi Mungu wala msimshirikishe na chochote. Na wafanyieni wema wazazi wawili na jamaa na mayatima na masikini na jirani wa karibu na jirani wa mbali, na rafiki wa ubavuni na mpita njia, na walio milikiwa na mikono yenu ya kulia. Hakika Mwenyezi Mungu hawapendi wenye kiburi wanao jifakhiri,'),
(530, 4, 37, 'Ambao wanafanya ubakhili na wanaamrisha watu ubakhili, na wanaficha fadhila alizo wapa Mwenyezi Mungu. Na tumewaandalia makafiri adhabu ya fedheha,'),
(531, 4, 38, 'Na ambao hutoa mali yao kwa kuonyesha watu, wala hawamuamini Mwenyezi Mungu wala Siku ya Mwisho; na yule ambaye Shet\'ani amekuwa ndiye rafiki yake, basi ana rafiki mbaya mno.'),
(532, 4, 39, 'Na ingeli wadhuru nini wao lau wangeli muamini Mwenyezi Mungu na Siku ya Mwisho, na wakatoa katika aliyo waruzuku Mwenyezi Mungu? Na Mwenyezi Mungu ni Mwenye kuwajua vyema.'),
(533, 4, 40, 'Hakika Mwenyezi Mungu hadhulumu hata uzito wa chembe moja. Na ikiwa ni jambo jema basi Yeye hulizidisha na hutoa kutoka kwake malipo makubwa.'),
(534, 4, 41, 'Basi itakuwaje pindi tukiwaletea kutoka kila umma shahidi, na tukakuleta wewe kuwa shahidi wa hawa?'),
(535, 4, 42, 'Siku hiyo walio kufuru na wakamuasi Mtume watatamani ardhi isawazishwe juu yao. Wala hawataweza kumficha Mwenyezi Mungu neno lolote.'),
(536, 4, 43, 'Enyi mlio amini! Msikaribie Sala, hali mmelewa, mpaka myajue mnayo yasema, wala hali mna janaba - isipo kuwa mmo safarini - mpaka mkoge. Na mkiwa wagonjwa, au mmo safarini, au mmoja wenu ametoka chooni, au mmewagusa wanawake - na msipate maji, basi ukusudieni mchanga safi, mpake nyuso zenu na mikono yenu. Hakika Mwenyezi Mungu ni Msamehevu na Mwenye kughufiria.'),
(537, 4, 44, 'Huwaoni wale walio pewa sehemu ya Kitabu wananunua upotovu na wanakutakeni nanyi mpotee njia?'),
(538, 4, 45, 'Na Mwenyezi Mungu anawajua vyema maadui zenu. Na Mwenyezi Mungu anatosha kuwa ni Mlinzi, na anatosha kuwa ni Mwenye kukunusuruni.'),
(539, 4, 46, 'Miongoni mwa Mayahudi wamo ambao hubadilisha maneno kuyatoa mahala pake, na husema: Tumesikia na tumeasi, na sikia bila ya kusikilizwa. Na husema: "Raai\'naa", kwa kuzipotoa ndimi zao ili kuitukana Dini. Na lau kama wangeli sema: Tumesikia na tumet\'ii, na usikie na "Undhurna" (Utuangalie), ingeli kuwa ni kheri kwao na sawa zaidi. Lakini Mwenyezi Mungu amewalaani kwa kufuru yao; basi hawaamini ila wachache tu.'),
(540, 4, 47, 'Enyi mlio pewa Kitabu! Aminini tuliyo yateremsha yenye kusadikisha yale mliyo nayo, kabla hatujazigeuza nyuso tukazipeleka kisogoni, au tukawalaani kama tulivyo walaani watu wa Sabato (Jumaamosi). Na amri ya Mwenyezi Mungu ni lazima ifanyike.'),
(541, 4, 48, 'Hakika Mwenyezi Mungu hasamehe kushirikishwa, na husamehe yaliyo duni ya hilo kwa amtakaye. Na anaye mshirikisha Mwenyezi Mungu basi hakika amezua dhambi kubwa.'),
(542, 4, 49, 'Jee huwaoni wale wanao dai kuwa ati wametakasika? Bali Mwenyezi Mungu humtakasa amtakaye. Na hawatadhulumiwa hata kadiri ya kijiuzi kilichomo ndani ya uwazi wa kokwa ya tende.'),
(543, 4, 50, 'Tazama vipi wanavyo mzulia uwongo Mwenyezi Mungu, na haya yatosha kuwa dhambi iliyo dhaahiri.'),
(544, 4, 51, 'Huwaoni wale walio pewa sehemu ya Kitabu? Wanaamini masanamu na Shet\'ani! Na wanawasema walio kufuru, kuwa hao wameongoka zaidi katika njia kuliko Walio amini.'),
(545, 4, 52, 'Hao ndio Mwenyezi Mungu amewalaani. Na ambaye Mwenyezi Mungu amemlaani basi hutamwona kuwa na mwenye kumnusuru.'),
(546, 4, 53, 'Au wanayo sehemu ya utawala? Basi hapo wasingewapa watu hata tundu ya kokwa ya tende.'),
(547, 4, 54, 'Au wanawahusudu watu kwa yale aliyo wapa Mwenyezi Mungu kwa fadhila yake? Basi tuliwapa ukoo wa Ibrahim Kitabu na hikima na tukawapa utawala mkubwa.'),
(548, 4, 55, 'Basi wapo miongoni mwao waliyo yaamini, na wapo walio yakataa. Na Jahannamu yatosha kuwa ni moto wa kuwateketeza.'),
(549, 4, 56, 'Hakika wale walio zikataa Ishara zetu tutawaingiza Motoni. Kila zitapo wiva ngozi zao tutawabadilishia ngozi nyengine ili waionje hiyo adhabu. Hakika Mwenyezi Mungu ni Mwenye nguvu na Mwenye hikima.'),
(550, 4, 57, 'Na wale walio amini na wakatenda mema tutawaingiza katika Pepo zipitazo mito kati yake kwa kudumu humo milele. Huko watakuwa na wake walio takasika, na tutawaingiza katika vivuli vizuri kweli.'),
(551, 4, 58, 'Hakika Mwenyezi Mungu anakuamrisheni mzirudishe amana kwa wenyewe. Na mnapo hukumu baina ya watu mhukumu kwa uadilifu. Hakika haya anayo kuwaidhini Mwenyezi Mungu ni mazuri sana. Hakika Mwenyezi Mungu ndiye Mwenye kusikia, Mwenye kuona.'),
(552, 4, 59, 'Enyi mlio amini! Mt\'iini Mwenyezi Mungu, na mt\'iini Mtume na wenye madaraka katika nyinyi. Na mkizozana katika jambo basi lirudisheni kwa Mwenyezi Mungu na Mtume, ikiwa mnamuamini Mwenyezi Mungu na Siku ya Mwisho. Hayo ndiyo bora zaidi na ndiyo yenye mwisho mwema.'),
(553, 4, 60, 'Huwaoni wale wanao dai kwamba wameamini yale yaliyo teremshwa kwako na yaliyo teremshwa kabla yako? Wanataka wakahukumiwe kwa njia ya upotofu, na hali wameamrishwa wakatae hayo! Na Shet\'ani anataka kuwapotezelea mbali.'),
(554, 4, 61, 'Na wanapo ambiwa: Njooni kwenye yale aliyo yateremsha Mwenyezi Mungu, na njooni kwa Mtume; utawaona wanaafiki wanakukwepa kwa upinzani.'),
(555, 4, 62, 'Basi inakuwaje unapo wasibu msiba kwa sababu ya yale iliyo tanguliza mikono yao? Hapo tena hukujia wakiapa kwa Mwenyezi Mungu wakisema: Hatukutaka ila wema na mapatano.'),
(556, 4, 63, 'Hao ndio wale ambao Mwenyezi Mungu anayajua yaliyomo ndani ya nyoyo zao. Basi waachilie mbali, uwape mawaidha na uwaambie maneno ya kuathiri na kuingia katika nafsi zao.'),
(557, 4, 64, 'Na hatukumtuma Mtume yeyote ila at\'iiwe kwa idhini ya Mwenyezi Mungu. Na lau pale walipo jidhulumu nafsi zao wangeli kujia, wakamwomba Mwenyezi Mungu msamaha, na Mtume akawaombea msamaha, hapana shaka wangeli mkuta Mwenyezi Mungu ni Mwenye kupokea toba na Mwenye kurehemu.'),
(558, 4, 65, 'La! Naapa kwa Mola wako Mlezi! Hawataamini mpaka wakufanye wewe ndiye muamuzi katika yale wanayo khitalifiana, kisha wasione uzito katika nyoyo zao juu ya hukumu utayo toa, na wanyenykee kabisa.'),
(559, 4, 66, 'Na lau kuwa tumewaamrisha: Jiueni, au tokeni majumbani kwenu, wasingeli fanya hayo ila wachache tu miongoni mwao. Na lau kama wangeli fanya walio waidhiwa ingeli kuwa bora kwao na uthibitisho wa nguvu zaidi.'),
(560, 4, 67, 'Na hapo tunge wapa malipo makubwa kutoka kwetu.'),
(561, 4, 68, 'Na tungeli waongoa Njia iliyo nyooka.'),
(562, 4, 69, 'Na wenye kumt\'ii Mwenyezi Mungu na Mtume, hao wa pamoja na wale alio waneemesha Mwenyezi Mungu miongoni mwa Manabii, na Masiddiqi, na Mashahidi, na Watu wema. Na uzuri ulioje kuwa pamoja na watu hao!'),
(563, 4, 70, 'Hiyo ni fadhila itokayo kwa Mwenyezi Mungu. Na Mwenyezi Mungu ni Mjuzi wa kutosha.'),
(564, 4, 71, 'Enyi mlio amini! Chukueni hadhari yenu! Na mtoke kwa vikosi au tokeni nyote pamoja!'),
(565, 4, 72, 'Na yupo kati yenu anaye bakia nyuma; na ukikusibuni msiba husema: Mwenyezi Mungu kanifanyia kheri sikuwa nao.'),
(566, 4, 73, 'Na ikikufikieni fadhila kutoka kwa Mwenyezi Mungu husema - kama kwamba hapakuwa mapenzi baina yenu naye: Laiti ningeli kuwa pamoja nao nikafanikiwa mafanikio makubwa.'),
(567, 4, 74, 'Basi nawapigane katika Njia ya Mwenyezi Mungu wale ambao wanao uza uhai wa dunia kwa Akhera. Na anaye pigana katika Njia ya Mwenyezi Mungu kisha akauliwa au akashinda basi tutakuja mpa ujira mkubwa.'),
(568, 4, 75, 'Na mna nini msipigane katika Njia ya Mwenyezi Mungu na ya wale wanao onewa, wanaume na wanawake na watoto, ambao husema: Mola Mlezi wetu! Tutoe katika mji huu ambao watu wake ni madhaalimu, na tujaalie tuwe na mlinzi anaye toka kwako, na tujaalie tuwe na wa kutunusuru anaye toka kwako.'),
(569, 4, 76, 'Walio amini wanapigana katika Njia ya Mwenyezi Mungu, na walio kufuru wanapiana katika njia ya upotofu. Basi piganeni na marafiki wa Shet\'ani. Hakika hila za Shet\'ani ni dhaifu.'),
(570, 4, 77, 'Je, huwaoni wale walio ambiwa: Izuieni mikono yenu, na mshike Sala na mtoe Zaka. Na walipo amrishwa kupigana, mara kundi moja kati yao liliwaogopa watu kama kumwogopa Mwenyezi Mungu, au kwa khofu kubwa zaidi. Na wakasema: Mola Mlezi wetu! Kwa nini umetuamrisha kupigana? Laiti unge tuakhirishia kiasi ya muda kidogo hivi! Sema: Starehe ya dunia ni ndogo, na Akhera ni bora zaidi kwa mwenye kuchamngu. Wala hamtadhulumiwa hata uzi wa kokwa ya tende.'),
(571, 4, 78, 'Popote mlipo mauti yatakufikieni, na hata mkiwa katika ngome zilizo na nguvu. Na likiwafikilia jema wanasema: Hili limetoka kwa Mwenyezi Mungu. Na likiwafikilia ovu wanasema: Hili limetoka kwako wewe. Sema: Yote yanatokana na Mwenyezi Mungu. Basi wana nini watu hawa hata hawakaribii kufahamu maneno?'),
(572, 4, 79, 'Jema lilio kufikia limetokana na Mwenyezi Mungu. Na ovu lilio kusibu linatokana na nafsi yako. Nasi tumekutuma kwa watu uwe ni Mtume. Na Mwenyezi Mungu ni shahidi wa kutosha.'),
(573, 4, 80, 'Mwenye kumt\'ii Mtume basi ndio amemt\'ii Mwenyezi Mungu. Na anaye kengeuka, basi Sisi hatukukutuma wewe uwe ni mlinzi wao.'),
(574, 4, 81, 'Na wanasema: Tunat\'ii. Lakini wanapo toka kwako kundi moja miongoni mwao hupanga njama usiku kinyume na unayo yasema. Na Mwenyezi Mungu anayaandika yote hayo wanayo yapangia njama za usiku. Basi waachilie mbali, na umtegemee Mwenyezi Mungu. Na Mwenyezi Mungu ni Mtegemewa wa kutosha.'),
(575, 4, 82, 'Hebu hawaizingatii hii Qur\'ani? Na lau kuwa imetoka kwa asiye kuwa Mwenyezi Mungu bila ya shaka wangeli kuta ndani yake khitilafu nyingi.'),
(576, 4, 83, 'Na linapo wafikia jambo lolote lilio khusu amani au la kitisho wao hulitangaza. Na lau kuwa wangeli lipeleka kwa Mtume na kwa wenye mamlaka kati yao, wale wanao chunguza wangeli lijua. Na lau kuwa si fadhila ya Mwenyezi Mungu juu yenu na rehema yake mngeli mfuata Shet\'ani ila wachache wenu tu.'),
(577, 4, 84, 'Basi pigana katika Njia ya Mwenyezi Mungu. Hukalifishwi ila nafsi yako tu. Na wahimize Waumini. Huenda Mwenyezi Mungu akayazuia mashambulio ya walio kufuru. Na Mwenyezi Mungu ndiye Mkali zaidi wa kushambulia na Mkali zaidi wa kuadhibu.'),
(578, 4, 85, 'Mwenye kusaidia msaada mwema ana fungu lake katika hayo, na mwenye kusaidia msaada mwovu naye ana sehemu yake katika hayo. Na hakika Mwenyezi Mungu ni Mwenye uweza na ujuzi juu ya kila kitu.'),
(579, 4, 86, 'Na mnapo amkiwa kwa maamkio yoyote, basi nanyi itikieni kwa yaliyo bora kuliko hayo, au rejesheni hayo hayo. Hakika Mwenyezi Mungu ni Mwenye kuhisabu kila kitu.'),
(580, 4, 87, 'Mwenyezi Mungu, hapana mungu isipo kuwa Yeye. Kwa yakini atakukusanyeni Siku ya Kiyama. Halina shaka hilo. Na ni nani msema kweli kuliko Mwenyezi Mungu?'),
(581, 4, 88, 'Mmekuwaje kuwa makundi mawili kwa khabari ya wanaafiki, na hali Mwenyezi Mungu amewageuza kwa sababu ya yale waliyo yachuma? Je! Mnataka kumwona mwongofu ambaye Mwenyezi Mungu amemhukumu kuwa kapotea? Na aliye mhukumu Mwenyezi Mungu kuwa amekwisha potea wewe hutampatia njia.'),
(582, 4, 89, 'Wanapenda lau kuwa nanyi mnge kufuru kama walivyo kufuru wao, ili muwe sawa sawa. Basi msiwafanye marafiki miongoni mwao mpaka wahame kwa ajili ya Dini ya Mwenyezi Mungu. Na wakikengeuka basi wakamateni na wauweni popote mnapo wapata. Wala msimfanye rafiki katika wao wala msaidizi.'),
(583, 4, 90, 'Isipokuwa wale walio fungamana na watu ambao mna ahadi baina yenu na wao, au wanakujieni na vifua vyao vina dhiki kupigana nanyi, au kupigana na watu wao. Na lau angeli penda Mwenyezi Mungu angeli wasaliti juu yenu wakapigana nanyi. Basi wakijitenga nanyi, wasipigane nanyi, na wakakuleteeni amani, basi Mwenyezi Mungu hakukupeni njia kupigana nao.'),
(584, 4, 91, 'Mtawakuta wengine wanataka wapate salama kwenu na salama kwa watu wao. Kila wakirudishwa kwenye fitna hudidimizwa humo. Ikiwa hawajitengi nanyi, na wakakuleteeni salama, na wakazuia mikono yao, basi wakamateni na wauweni popote mnapo wakuta. Na juu ya hao ndio tumekupeni hoja zilizo wazi.'),
(585, 4, 92, 'Na haiwi Muumini kumuuwa Muumini ila kwa kukosea. Na mwenye kumuuwa Muumini kwa kukosea basi amkomboe mtumwa aliye Muumini, na atoe diya kuwapa jamaa za maiti, isipo kuwa waache wenyewe kuwa ni sadaka. Na akiwa ni jamaa wa maadui zenu, hali yeye ni Muumini, basi ni kumkomboa mtumwa aliye Muumini. Na akiwa ni miongoni wa watu ambao pana ahadi baina yenu na wao basi wapewe diya watu wake na akombolewe mtumwa Muumini. Na asiye pata, basi afunge miezi miwili mfululizo, kuwa ni toba kwa Mwenyezi Mungu. Na Mwenyezi Mungu ni Mjuzi na Mwenye hikima.'),
(586, 4, 93, 'Na mwenye kumuuwa Muumini kwa kukusudia, basi malipo yake ni Jahannamu humo atadumu, na Mwenyezi Mungu amemkasirikia, na amemlaani, na amemuandalia adhabu kubwa.'),
(587, 4, 94, 'Enyi mlio amini! Mnapo safiri katika Njia ya Mwenyezi Mungu basi hakikisheni, wala msimwambie anaye kutoleeni salamu: Wewe si Muumini; kwa kutaka manufaa ya maisha ya dunia hii, hali ya kuwa kwa Mwenyezi Mungu zipo ghanima nyingi. Hivyo ndivyo mlivyo kuwa nyinyi zamani, na Mwenyezi Mungu akakuneemesheni. Basi chunguzeni sawa sawa. Hakika Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(588, 4, 95, 'Hawawi sawa Waumini wanao kaa tu wala hawanadharura, na wale wanao pigana katika Njia ya Mwenyezi Mungu kwa mali yao na nafsi zao. Mwenyezi Mungu amewatukuza cheo wale wanao pigana kwa mali yao na nafsi zao kuliko wale wanao kaa tu. Ingawa Mwenyezi Mungu amewaahidi wote mashukio mema, lakini Mwenyezi Mungu amewafadhili wanao pigana kwa ujira mkubwa kuliko wanao kaa tu.'),
(589, 4, 96, 'Ni vyeo hivyo vinavyo toka kwake, na maghfira na rehema. Na Mwenyezi Mungu ni Mwingi wa maghfira na Mwenye kurehemu.'),
(590, 4, 97, 'Hakika Malaika watawaambia wale ambao wamewafisha nao wamejidhulumu nafsi zao: Mlikuwa vipi? Watasema: Tulikuwa tunaonewa. Watawaambia: Kwani ardhi ya Mwenyezi Mungu haikuwa na wasaa wa kuhamia humo? Basi hao makaazi yao ni Jahannamu, nayo ni marejeo mabaya kabisa.'),
(591, 4, 98, 'Isipo kuwa wale walio kuwa wanyonge miongoni mwa wanaume na wanawake na watoto wasio na uweza wa hila ya kuongoza njia ya kuhama.'),
(592, 4, 99, 'Basi hao huenda Mwenyezi Mungu akawasamehe. Na Mwenyezi Mungu ni Mwingi wa kusamehe, Mwingi wa maghfira.'),
(593, 4, 100, 'Na mwenye kuhama katika Njia ya Mwenyezi Mungu atapata pengi duniani pa kukimbilia, na atapata wasaa. Na anaye toka nyumbani kwake kuhama kwa ajili ya Mwenyezi Mungu na Mtume wake, kisha yakamfika mauti njiani basi umethibiti ujira wake kwa Mwenyezi Mungu. Na Mwenyezi Mungu ndiye Mwenye maghfira na Mwenye kurehemu.'),
(594, 4, 101, 'Na mnapo safiri katika nchi si vibaya kwenu kama mkifupisha Sala, iwapo mnachelea wasije wale walio kufuru wakakuleteeni maudhi. Hakika makafiri ni maadui zenu walio wazi wazi.'),
(595, 4, 102, 'Na unapo kuwa pamoja nao, ukawasalisha, basi kundi moja miongoni mwao wasimame pamoja nawe na wachukue silaha zao. Na watakapo maliza sijida zao, basi nawende nyuma yenu, na lije kundi jingine ambalo halijasali, lisali pamoja nawe. Nao wachukue hadhari yao na silaha zao. Walio kufuru wanapenda mghafilike na silaha zenu na vifaa vyenu ili wakuvamieni mvamio wa mara moja. Wala si vibaya kwenu ikiwa mnaona udhia kwa sababu ya mvua au mkawa wagonjwa, mkaziweka silaha zenu. Na chukueni hadhari yenu. Hakika Mwenyezi Mungu amewaandalia makafiri adhabu ya kudhalilisha.'),
(596, 4, 103, 'Mkisha sali basi mkumbukeni Mwenyezi Mungu mkisimama, na mkikaa, na mnapo jinyoosha kwa kulala. Na mtapo tulia basi shikeni Sala kama dasturi. Kwani hakika Sala kwa Waumini ni faradhi iliyo wekewa nyakati maalumu.'),
(597, 4, 104, 'Wala msifanye ulegevu kuwafukuzia kaumu ya maadui. Ikiwa mnaumia, basi nao pia wanaumia kama mnavyo umia nyinyi. Nanyi mnataraji kwa Mwenyezi Mungu wasio yataraji wao. Na hakika Mwenyezi Mungu ni Mjuzi na Mwenye hikima.'),
(598, 4, 105, 'Hakika Sisi tumekuteremshia Kitabu hiki kwa haki ili upate kuhukumu baina ya watu kwa alivyo kuonyesha Mwenyezi Mungu. Wala usiwe mtetezi wa makhaaini.'),
(599, 4, 106, 'Na muombe maghfira Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwingi wa maghfira, Mwenye kurehemu.'),
(600, 4, 107, 'Wala usiwatetee wanao khini nafsi zao. Hakika Mwenyezi Mungu hampendi aliye khaaini, mwenye dhambi.'),
(601, 4, 108, 'Wanataka kujificha kwa watu, wala hawataki kujificha kwa Mwenyezi Mungu, naye yu pamoja nao pale wanapo panga njama usiku kwa maneno asiyo yapenda. Na Mwenyezi Mungu anayajua vyema wanayo yatenda.'),
(602, 4, 109, 'Hivyo nyinyi mmewatetea hawa katika uhai huu wa duniani. Basi nani atakaye watetea kwa Mwenyezi Mungu Siku ya Kiyama? Au ni nani atakuwa wakili wao wa kumtegemea?'),
(603, 4, 110, 'Na anaye tenda uovu au akajidhulumu nafsi yake, kisha akaomba maghfira kwa Mwenyezi Mungu, atamkuta Mwenyezi Mungu ni Mwingi wa maghfira, Mwenye kurehemu.'),
(604, 4, 111, 'Na anaye chuma dhambi, basi ameichumia nafsi yake mwenyewe. Na Mwenyezi Mungu ni Mjuzi, Mwenye hikima.'),
(605, 4, 112, 'Na atendaye kosa au dhambi kisha akamsingizia asiye na kosa, basi amejitwika dhulma na dhambi iliyo wazi.'),
(606, 4, 113, 'Na lau kuwa si fadhila ya Mwenyezi Mungu juu yako na rehema yake, kundi moja kati yao linge dhamiria kukupoteza. Wala hawapotezi ila nafsi zao, wala hawawezi kukudhuru kwa lolote. Na Mwenyezi Mungu amekuteremshia Kitabu na hikima na amekufundisha uliyo kuwa huyajui. Na fadhila za Mwenyezi Mungu zilizo juu yako ni kubwa.'),
(607, 4, 114, 'Hakuna kheri katika mengi ya wanayo shauriana kwa siri, isipo kuwa kwa yule anaye amrisha kutoa sadaka, au kutenda mema, au kupatanisha baina ya watu. Na mwenye kufanya hayo kwa kutaka radhi ya Mwenyezi Mungu, basi tutakuja mpa ujira mkubwa.'),
(608, 4, 115, 'Na anaye mpinga Mtume baada ya kumdhihirikia uwongofu, na akafuata njia isiyo kuwa ya Waumini, tutamuelekeza aliko elekea, na tutamuingiza katika Jahannamu. Na hayo ni marejeo maovu.'),
(609, 4, 116, 'Hakika Mwenyezi Mungu hasamehi kushirikishwa na kitu. Lakini Yeye husamehe yasiyo kuwa hayo kwa amtakaye. Na anaye mshirikisha Mwenyezi Mungu basi huyo amepotea upotovu wa mbali.'),
(610, 4, 117, 'Wao hawawaombi badala yake Yeye ila wanawake, wala hawamuombi ila Shet\'ani aliye asi.'),
(611, 4, 118, 'Mwenyezi Mungu amemlaani. Naye Shet\'ani alisema: Kwa yakini nitawachukua sehemu maalumu katika waja wako.'),
(612, 4, 119, 'Na hakika nitawapoteza na nitawatia matamanio, na nitawaamrisha, basi watakata masikio ya wanyama, na nitawaamrisha, basi watabadili aliyo umba Mwenyezi Mungu. Na mwenye kumfanya Shet\'ani kuwa ni mlinzi wake badala ya Mwenyezi Mungu, basi huyo amekhasiri khasara ya dhaahiri.'),
(613, 4, 120, 'Anawaahidi na anawatia tamaa. Na Shet\'ani hawaahidi ila udanganyifu.'),
(614, 4, 121, 'Hao makao yao ni Jahannamu, na wala hawapati makimbilio kutoka humo.'),
(615, 4, 122, 'Na wale walio amini na wakatenda mema, tutawaingiza katika Bustani zipitayo mito kati yake. Watadumu humo milele. Ndiyo ahadi ya Mwenyezi Mungu iliyo kweli. Na nani mkweli zaidi kwa usemi kuliko Mwenyezi Mungu.'),
(616, 4, 123, 'Si kwa matamanio yenu, wala matamanio ya Watu wa Kitabu! Anaye fanya ubaya atalipwa kwalo, wala hatajipatia mlinzi wala wa kumnusuru, isipo kuwa Mwenyezi Mungu.'),
(617, 4, 124, 'Na anaye fanya mema, akiwa mwanamume au mwanamke, naye ni Muumini - basi hao wataingia Peponi wala hawatadhulumiwa hata kadiri ya tundu ya kokwa ya tende.'),
(618, 4, 125, 'Na nani aliye bora kwa dini kuliko yule aliye usilimisha uso wake kwa Mwenyezi Mungu, naye akawa mwema, na anafuata mila ya Ibrahim mwongofu? Na Mwenyezi Mungu alimfanya Ibrahim kuwa ni rafiki mwendani.'),
(619, 4, 126, 'Ni vya Mwenyezi Mungu tu vyote vilivyomo mbinguni na vilivyomo duniani. Na Mwenyezi Mungu ni mwenye kuvizunguka vitu vyote.'),
(620, 4, 127, 'Wanakuuliza nini sharia ya wanawake. Sema: Mwenyezi Mungu ana kutoleeni fatwa juu yao, na mnayo somewa humu Kitabuni kukhusu mayatima wanawake ambao hamwapi walicho andikiwa, na mnapenda kuwaoa, na kukhusu wanyonge katika watoto, na kwamba mwasimamie mayatima kwa uadilifu. Na kheri yoyote mnayo fanya Mwenyezi Mungu anaijua.'),
(621, 4, 128, 'Na mwanamke akichelea kutupwa au kutojaliwa na mumewe, basi si vibaya kwao wakisikizana kwa suluhu. Na suluhu ni bora. Na nafsi zimewekewa machoni mwake tamaa na choyo. Na mkifanya wema na mkamchamngu basi hakika Mwenyezi Mungu anazo khabari za yote mnayo yatenda.'),
(622, 4, 129, 'Wala hamtaweza kufanya uadilifu baina ya wake, hata mkikakamia. Kwa hivyo msimili moja kwa moja mkamwacha (mmojapo) kama aliye tundikwa. Na mkisikizana na mkamchamngu basi Mwenyezi Mungu ni Mwingi wa maghfira na Mwenye kurehemu.'),
(623, 4, 130, 'Na wakitengana Mwenyezi Mungu atamtosheleza kila mmoja katika wao kwa ukunjufu wake. Na Mwenyezi Mungu ni Mkunjufu Mwenye hikima.'),
(624, 4, 131, 'Na ni vya Mwenyezi Mungu tu vyote viliomo katik mbingu na viliomo katika dunia. Na kwa hakika tuliwausia walio pewa Kitabu kabla yenu, na nyinyi pia, kwamba mumche Mwenyezi Mungu. Na mkikataa, basi ni vya Mwenyezi Mungu viliomo mbinguni na viliomo duniani. Na Mwenyezi Mungu ni Mkwasi, anajitosha, na Msifiwa.'),
(625, 4, 132, 'Na ni vya Mwenyezi Mungu viliomo katika mbingu na viliomo katika ardhi. Na Mwenyezi Mungu anatosha kuwa Mlinzi.'),
(626, 4, 133, 'Akitaka atakuondoeni, enyi watu! Na awalete wengineo. Na Mwenyezi Mungu ni Muweza wa hayo.'),
(627, 4, 134, 'Anaye taka malipo ya dunia, basi kwa Mwenyezi Mungu yapo malipo ya dunia na Akhera. Na Mwenyezi Mungu ni Mwenye kusikia na Mwenye kuona.'),
(628, 4, 135, 'Enyi mlio amini! Kuweni wenye kusimamisha uadilifu, mtowao ushahidi kwa ajili ya Mwenyezi Mungu, ijapo kuwa ni juu ya nafsi zenu, au wazazi wawili na jamaa zenu. Akiwa tajiri au masikini Mwenyezi Mungu anawastahikia zaidi. Basi msifuate matamanio, mkaacha kufanya uadilifu. Na mkiupotoa au mkajitenga nao basi Mwenyezi Mungu anajua vyema mnayo yatenda.'),
(629, 4, 136, 'Enyi mlio amini! Muaminini Mwenyezi Mungu, na Mtume wake, na Kitabu alicho kiteremsha juu ya Mtume wake, na Kitabu alicho kiteremsha kabla yake. Na anaye mkataa Mwenyezi Mungu, na Malaika wake, na Vitabu vyake, na Mitume wake, na Siku ya Akhera, basi huyo amekwisha potelea mbali.'),
(630, 4, 137, 'Hakika walio amini, kisha wakakufuru, kisha wakaamini, kisha wakazidi ukafiri, Mwenyezi Mungu hakuwa wa kuwaghufiria wala wa kuwaongoa njia.'),
(631, 4, 138, 'Wabashirie wanaafiki kwamba watapata adhabu chungu,'),
(632, 4, 139, 'Wale ambao huwafanya makafiri kuwa ndio marafiki badala ya Waumini. Je! Wanataka wapate kwao utukufu? Basi hakika utukufu wote ni wa Mwenyezi Mungu.'),
(633, 4, 140, 'Naye amekwisha kuteremshieni katika Kitabu hiki ya kwamba mnapo sikia Aya za Mwenyezi Mungu zinakanushwa na zinafanyiwa kejeli, basi msikae pamoja nao, mpaka waingie katika mazungumzo mengine. Hivyo mtakuwa nanyi mfano wao hao. Hakika Mwenyezi Mungu atawakusanya wanaafiki na makafiri wote pamoja katika Jahannamu,'),
(634, 4, 141, 'Wale ambao wanakungojeeni, mkipata ushindi unao toka kwa Mwenyezi Mungu wao husema: Si tulikuwa pamoja nanyi vile? Na ikiwa ni sehemu ya makafiri kushinda wao huwaambia: Hatukuwa ni waweza wa kukushindeni, nasi tukakukingeni na hawa Waumini? Basi Mwenyezi Mungu atahukumu baina yenu Siku ya Kiyama, wala Mwenyezi Mungu hatawapa makafiri njia ya kuwashinda Waumini.'),
(635, 4, 142, 'Hakika wanaafiki wanamkhadaa Mwenyezi Mungu, na hali ni Yeye ndiye mwenye kuwakhadaa wao. Na wanapo inuka kusali huinuka kwa unyong\'onyo, wanajionyesha kwa watu tu, wala hawamdhukuru Mwenyezi Mungu ila kidogo tu.'),
(636, 4, 143, 'Wanayumba yumba baina ya huku na huko. Huku hawako na huko hawako. Na ambaye Mwenyezi Mungu amemwacha kupotea, kabisa hutompatia njia.'),
(637, 4, 144, 'Enyi mlio amini! Msiwafanye makafiri kuwa ndio marafiki badala ya Waumini. Je, mnataka Mwenyezi Mungu awe na hoja iliyo wazi juu yenu?'),
(638, 4, 145, 'Hakika wanaafiki watakuwa katika t\'abaka ya chini kabisa Motoni, wala hutompata yeyote wa kuwanusuru.'),
(639, 4, 146, 'Isipo kuwa wale walio tubu, na wakatengeneza na wakashikamana na Mwenyezi Mungu, na wakamtakasia Dini yao Mwenyezi Mungu. Basi hao ni pamoja na Waumini. Na Mwenyezi Mungu atakuja wapa Waumini ujira mkubwa.'),
(640, 4, 147, 'Mwenyezi Mungu hakuadhibuni mkimshukuru na mkamuamini. Na Mwenyezi Mungu ndiye Mwenye kupokea shukrani na ndiye Mwenye kujua.'),
(641, 4, 148, 'MWENYEZI MUNGU hapendi uovu uenezwe kwa maneno ila kwa mwenye kudhulumiwa. Na Mwenyezi Mungu ndiye Mwenye kusikia na Mwenye kujua.'),
(642, 4, 149, 'Mkidhihirisha wema au mkiuficha, au mkiyasamehe maovu, hakika Mwenyezi Mungu ni Mwingi wa maghfira na Muweza.'),
(643, 4, 150, 'Hakika wale wanao mkataa Mwenyezi Mungu na Mitume wake, na wanataka kufarikisha baina ya Mwenyezi Mungu na Mitume wake, kwa kusema: Wengine tunawaamini na wengine tunawakataa. Na wanataka kushika njia iliyo kati kati ya haya,'),
(644, 4, 151, 'Hao ndio makafiri kweli. Na tumewaandalia makafiri adhabu ya kudhalilisha.'),
(645, 4, 152, 'Na walio muamini Mwenyezi Mungu na Mitume wake, wala wasimfarikishe yeyote kati yao, hao atawapa ujira wao. Na Mwenyezi Mungu ni Mwingi wa maghfira, na Mwenye kurehemu.'),
(646, 4, 153, 'Watu wa Kitabu wanakutaka uwateremshia kitabu kutoka mbinguni. Na kwa hakika walikwisha mtaka Musa makubwa kuliko hayo. Walisema: Tuonyeshe Mwenyezi Mungu wazi wazi. Wakapigwa na radi kwa hiyo dhulma yao. Kisha wakamchukua ndama kumuabudu, baada ya kwisha wajia hoja zilizo wazi. Nasi tukasamehe hayo, na tukampa Musa nguvu zilizo dhaahiri.'),
(647, 4, 154, 'Na tukanyanyua mlima juu yao kwa kufanya agano nao. Na tukawaambia: Ingilieni mlangoni kwa unyenyekevu. Na tukawaambia: Msiivunje Siku ya Sabato (Jumaa mosi). Na tukachukua kwao ahadi iliyo madhubuti.'),
(648, 4, 155, 'Basi kwa sababu ya kuvunja kwao ahadi zao, na kuzikataa kwao ishara za Mwenyezi Mungu, na kuwauwa kwao Manabii bila ya haki, na kusema kwao: Nyoyo zetu zimefumbwa; bali Mwenyezi Mungu amezipiga muhuri kwa kufuru zao, basi hawaamini ila kidogo tu -'),
(649, 4, 156, 'Na pia kwa kufuru zao na kumsingizia kwao Maryamu uwongo mkubwa,'),
(650, 4, 157, 'Na kwa kusema kwao: Sisi tumemuuwa Masihi Isa, mwana wa Maryamu, Mtume wa Mwenyezi Mungu - nao hawakumuuwa wala hawakumsalibu, bali walifananishiwa tu. Na hakika walio khitalifiana katika haya wamo katika shaka nayo. Wao hawana ujuzi nayo wowote, ila ni kufuata dhana tu. Wala hawakumuuwa kwa yakini.'),
(651, 4, 158, 'Bali Mwenyezi Mungu alim- tukuza kwake, na hakika Mwenyezi Mungu ni Mwenye nguvu, Mwenye hikima.'),
(652, 4, 159, 'Na hawi katika Watu wa Kitabu ila hakika atamuamini yeye kabla ya kufa kwake. Naye Siku ya Kiyama atakuwa shahidi juu yao.'),
(653, 4, 160, 'Basi kwa dhulma yao Mayahudi tuliwaharimishia vitu vizuri walivyo halalishiwa. Na kwa sababu ya kuwazuilia kwao watu wengi na Njia ya Mwenyezi Mungu,'),
(654, 4, 161, 'Na kuchukua kwao riba, nao wamekatazwa, na kula kwao mali ya watu kwa dhulma. Basi tumewaandalia makafiri katika wao adhabu yenye uchungu.'),
(655, 4, 162, 'Lakini walio bobea katika ilimu miongoni mwao, na Waumini, wanayaamini uliyo teremshiwa wewe, na yaliyo teremshwa kabla yako. Na wanao shika Sala, na wakatoa Zaka, na wanao muamini Mwenyezi Mungu na Siku ya Mwisho - hao tutawapa malipo makubwa.'),
(656, 4, 163, 'Hakika Sisi tumekuletea wahyi wewe kama tulivyo wapelekea wahyi Nuhu na Manabii walio kuwa baada yake. Na tulimpelekea wahyi Ibrahim na Ismail na Is-haka na Yaakub na wajukuu zake, na Isa na Ayub na Yunus na Harun na Suleiman, na Daud tukampa Zaburi.'),
(657, 4, 164, 'Na Mitume tulio kwisha kukuhadithia kabla yake, na Mitume wengine hatukukuhadithia. Na Mwenyezi Mungu alinena na Musa kwa maneno.'),
(658, 4, 165, 'Mitume hao ni wabashiri na waonyaji, ili watu wasiwe na hoja juu ya Mwenyezi Mungu baada ya kuletewa Mitume. Na Mwenyezi Mungu ni Mwenye nguvu, Mwenye hikima.'),
(659, 4, 166, 'Lakini Mwenyezi Mungu anayashuhudia aliyo kuteremshia wewe. Ameyateremsha kwa kujua kwake - na Malaika pia wanashuhudia. Na Mwenyezi Mungu anatosha kuwa shahidi.'),
(660, 4, 167, 'Hakika walio kufuru na wakazuilia Njia ya Mwenyezi Mungu, wamekwisha potelea mbali.'),
(661, 4, 168, 'Hakika wale walio kufuru na wakadhulumu hawi Mwenyezi Mungu kuwasamehe wala kuwaongoa njia.'),
(662, 4, 169, 'Isipo kuwa njia ya Jahannamu. Humo watadumu milele. Na hayo kwa Mwenyezi Mungu ni mepesi.'),
(663, 4, 170, 'Enyi Watu! Amekwisha kujieni Mtume huyu na haki itokayo kwa Mola wenu Mlezi. Basi aminini, ndiyo kheri yenu. Na mkikakanusha basi hakika viliomo mbinguni na duniani ni vya Mwenyezi Mungu tu. Na hakika Mwenyezi Mungu ni Mjuzi Mwenye hikima.'),
(664, 4, 171, 'Enyi Watu wa Kitabu! Msipite kiasi katika dini yenu, wala msimseme Mwenyezi Mungu ila kwa lilio kweli. Hakika Masihi Isa mwana wa Maryamu ni Mtume wa Mwenyezi Mungu, na neno lake tu alilo mpelekea Maryamu, na ni roho iliyo toka kwake. Basi Muaminini Mwenyezi Mungu na Mitume wake. Wala msiseme: Utatu. Komeni! Itakuwa kheri kwenu. Hakika Mwenyezi Mungu ni Mungu mmoja tu. Ametukuka Yeye na kuwa na mwana. Ni vyake Yeye vyote viliomo katika mbingu na katika ardhi. Na Mwenyezi Mungu ni Mtegemewa wa kutosha.'),
(665, 4, 172, 'Masihi hataona uvunjifu kuwa mtumwa wa Mwenyezi Mungu, wala Malaika walio karibishwa. Na watakao ona uvunjifu utumwa wa Mwenyezi Mungu na kufanya kiburi, basi atawakusanya wote kwake.'),
(666, 4, 173, 'Na ama walio amini na wakatenda mema atawalipa ujira wao sawa sawa, na atawazidishia kwa fadhila yake. Na ama wale walio ona uvunjifu na wakafanya kiburi, basi atawaadhibu adhabu iliyo chungu. Wala hawatopata mlinzi wala wa kuwanusuru asiye kuwa Mwenyezi Mungu.'),
(667, 4, 174, 'Enyi watu! Umekufikieni ushahidi kutoka kwa Mola wenu Mlezi. Na tumekuteremshieni Nuru iliyo wazi.'),
(668, 4, 175, 'Ama wale walio muamini Mwenyezi Mungu, na wakashikamana naye, basi atawatia katika rehema yake na fadhila, na atawaongoa kwenye Njia Iliyo Nyooka.'),
(669, 4, 176, 'Wanakuomba uhukumu, sema: Mwenyezi Mungu anakupeni hukumu juu ya mkiwa. Ikiwa mtu amekufa, naye hana mtoto, lakini anaye ndugu wa kike, basi huyo atapata nusu alicho acha maiti. Na mwanamume atamrithi ndugu wa kike ikiwa hana mwana. Na ikiwa wao ni ndugu wa kike wawili, basi watapata thuluthi mbili za alicho acha maiti. Na wakiwa ndugu wanaume na wanawake, basi mwanamume atapata sehemu iliyo sawa na ya wanawake wawili. Mwenyezi Mungu anakubainishieni ili msipotee; na Mwenyezi Mungu ni Mjuzi wa kila kitu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Enyi mlio amini! Timizeni ahadi. Mmehalalishiwa wanyama wa mifugo, ila wale mnao tajiwa. Lakini msihalalishe kuwinda nanyi mmo katika Hija. Hakika Mwenyezi Mungu anahukumu apendavyo.'),
(671, 5, 2, 'Enyi mlio amini! Msivunje hishima ya alama ya Dini ya Mwenyezi Mungu wala mwezi mtakatifu, wala wanyama wanao pelekwa Makka kuchinjwa, wala vigwe vyao, wala wanao elekea kwendea Nyumba Takatifu, wakitafuta fadhila na radhi za Mola wao Mlezi. Na mkisha toka Hija yenu basi windeni. Wala kuwachukia watu kwa kuwa wali- kuzuilieni kufika Msikiti mtakatifu kusikupelekeeni kuwafanyia uadui. Na saidianeni katika wema na uchamngu. Wala msisaidiane katika dhambi na uadui. Na mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mkali wa kuadhibu.'),
(672, 5, 3, 'Mmeharimishiwa nyamafu, na damu, na nyama ya nguruwe, na mnyama aliye chinjwa si kwa ajili ya Mwenyezi Mungu, na aliye kufa kwa kunyongeka koo, na aliye kufa kwa kupigwa, na aliye kufa kwa kuanguka, na aliye kufa kwa kupigwa pembe, aliye liwa na mnyama, ila mkimdiriki kumchinja. Na pia ni haramu kula nyama aliye chinjiwa masanamu. Na ni haramu kupiga ramli. Hayo yote ni upotovu. Leo walio kufuru wamekata tamaa na Dini yenu; basi msiwaogope, bali niogopeni Mimi. Leo nimekukamiliishieni Dini yenu, na nimekutimizieni neema yangu, na nimekupendeleeni UISLAMU uwe ndiyo Dini. Na mwenye kushurutishwa na njaa, bila ya kupendelea dhambi, basi hakika Mwenyezi Mungu ni Msamehevu na Mwenye kurehemu.'),
(673, 5, 4, 'Wanakuuliza wamehalalishiwa nini? Sema: Mmehalalishiwa kila vilivyo vizuri na mlicho wafundisha wanyama kukiwinda. Mnawafundisha alivyo kufunzeni Mwenyezi Mungu. Basi kuleni walicho kukamatieni, na mkisomee jina la Mwenyezi Mungu. Na mcheni Mwenyezi Mungu; hakika Mwenyezi Mungu ni Mwepesi wa kuhisabu.'),
(674, 5, 5, 'Leo mmehalalishiwa kila vilivyo vizuri. Na chakula cha walio pewa Kitabu ni halali kwenu, na chakula chenu ni halali kwao. Na pia wanawake wema miongoni mwa Waumini, na wanawake wema miongoni mwa walio pewa Kitabu kabla yenu, mtakapo wapa mahari yao, mkafunga nao ndoa, bila ya kufanya uhasharati wala kuwaweka kinyumba. Na anaye kataa kuamini bila shaka a\'mali yake imepotea, naye katika Akhera atakuwa miongoni mwa wenye khasara.'),
(675, 5, 6, 'Enyi mlio amini! Mnapo simama kwa ajili ya Sala basi osheni nyuso zenu, na mikono yenu mpaka vifundoni, na mpake vichwa vyenu, na osheni miguu yenu mpaka vifundoni. Na mkiwa na janaba basi ogeni. Na mkiwa wagonjwa au mmo safarini, au mmoja wenu ametoka chooni au mmewagusa wanawake, na hamkupata maji, basi tayamamuni vumbi lilio safi, na mpake nyuso zenu na mikono yenu. Hapendi Mwenyezi Mungu kukutieni katika taabu; bali anataka kukutakaseni na kutimiza neema yake juu yenu ili mpate kushukuru.'),
(676, 5, 7, 'Na kumbukeni neema ya Mwenyezi Mungu iliyo juu yenu, na ahadi yake aliyo fungamana nanyi mlipo sema: Tumesikia na tumet\'ii. Na mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mjuzi sana wa yaliyomo vifuani.'),
(677, 5, 8, 'Enyi mlio amini! Kuweni wasimamizi madhubuti kwa ajili ya Mwenyezi Mungu mkitoa ushahidi kwa haki. Wala kuchukiana na watu kusikupelekeeni kutofanya uadilifu. Fanyeni uadilifu. Hivyo ndio kuwa karibu mno na uchamngu. Na mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(678, 5, 9, 'Mwenyezi Mungu amewaahidi walio muamini na wakatenda mema kwamba watapata maghfira na malipo makubwa.'),
(679, 5, 10, 'Na wale walio kufuru na wakakanusha Ishara zetu, hao ndio watu wa Motoni.'),
(680, 5, 11, 'Enyi mlio amini! Kumbukeni neema za Mwenyezi Mungu zilizo juu yenu, walipo taka watu kukunyooshieni mikono, naye akaizuilia mikono yao kukufikieni. Na mcheni Mwenyezi Mungu. Na Waumini wamtegemee Mwenyezi Mungu tu.'),
(681, 5, 12, 'Na Mwenyezi Mungu alifanya agano na Wana wa Israili. Na tukawateulia kutokana nao wakuu kumi na mbili. Na Mwenyezi Mungu akasema: Kwa yakini Mimi ni pamoja nanyi. Mkisali, na mkatoa Zaka, na mkawaamini Mitume wangu, na mkawasaidia, na mkamkopesha Mwenyezi Mungu mkopo mwema, hapana shaka nitakufutieni maovu yenu na nitakuingizeni katika mabustani yapitayo mito kati yake. Lakini atakaye kufuru miongoni mwenu baada ya hayo bila ya shaka atakuwa amepotea njia iliyo sawa.'),
(682, 5, 13, 'Basi kwa sababu ya kuvunja agano lao tuliwalaani, na tukazifanya nyoyo zao kuwa ngumu. Wanayabadilisha maneno kutoka pahala pake, na wamesahau sehemu ya yale waliyo kumbushwa. Na huachi kuvumbua khiana kutokana nao, isipo kuwa wachache miongoni mwao. Basi wasamehe na waache. Hakika Mwenyezi Mungu huwapenda wafanyao wema.'),
(683, 5, 14, 'Na kutoka kwa wale walio sema: Sisi ni Manasara, tulichukua ahadi yao, lakini wakasahau sehemu ya waliyo kumbushwa. Kwa hivyo tukaweka kati yao uadui na chuki mpaka Siku ya Kiyama. Na hapo Mwenyezi Mungu atawaambia waliyo kuwa wakiyafanya.'),
(684, 5, 15, 'Enyi Watu wa Kitabu! Amekwisha kujieni Mtume wetu anaye kufichulieni mengi mliyo kuwa mkiyaficha katika Kitabu, na anaye samehe mengi. Bila shaka imekujieni kutoka kwa Mwenyezi Mungu nuru na Kitabu kinacho bainisha.'),
(685, 5, 16, 'Mwenyezi Mungu huwaongoa wenye kufuata radhi yake katika njia za salama, na huwatoa katika giza kuwapeleka kwenye nuru kwa amri yake, na huwaongoa kwenye njia iliyo nyooka.'),
(686, 5, 17, 'Hakika wamekufuru walio sema: Mwenyezi Mungu ni Masihi mwana wa Maryamu. Sema: Ni nani mwenye kumiliki cho chote mbele ya Mwenyezi Mungu ikiwa Yeye angetaka kumuangamiza Masihi mwana wa Maryamu, na mama yake, na wote waliomo katika ardhi? Na mamlaka ya mbingu na ardhi na vilivyomo ndani yake ni vya Mwenyezi Mungu. Huumba apendavyo. Na Mwenyezi Mungu anao uweza juu ya kila kitu.'),
(687, 5, 18, 'Na Mayahudi na Wakristo wanasema: Sisi ni wana wa Mwenyezi Mungu na vipenzi vyake. Sema: Basi kwa nini anakuadhibuni kwa ajili ya dhambi zenu? Bali nyinyi ni watu tu kama wale wengine alio waumba. Humsamehe amtakaye na humuadhibu amtakaye. Na ni wa Mwenyezi Mungu tu ufalme wa mbingu na ardhi. Na marejeo ni kwake Yeye.'),
(688, 5, 19, 'Enyi Watu wa Kitabu! Bila ya shaka amekujilieni Mtume wetu akikubainishieni katika wakati usio kuwa na Mitume, msije mkasema: Hakufika kwetu mbashiri wala mwonyaji. Basi amekujieni mbashiri na mwonyaji. Na Mwenyezi Mungu anao uweza wa kila kitu.'),
(689, 5, 20, 'Na pale Musa alipo waambia watu wake: Enyi watu wangu! Kumbukeni neema za Mwenyezi Mungu zilizo juu yenu, alipo wateuwa Manabii kati yenu, na akakufanyeni watawala, na akakupeni ambayo hakuwapa wowote katika walimwengu.'),
(690, 5, 21, 'Enyi watu wangu! Ingieni katika nchi takatifu ambayo Mwenyezi Mungu amekuandikieni. Wala msirudi nyuma, msije mkawa wenye kukhasirika.'),
(691, 5, 22, 'Wakasema: Ewe Musa! Huko wako watu majabari. Nasi hatutaingia huko mpaka wao watoke humo. Wakitoka humo hapo tutaingia.'),
(692, 5, 23, 'Watu wawili miongoni mwa wachamngu ambao Mwenyezi Mungu amewaneemesha, walisema: Waingilieni kwa mlangoni. Mtakapo waingilia basi kwa yakini nyinyi mtashinda. Na mtegemeeni Mwenyezi Mungu ikiwa nyinyi ni Waumini.'),
(693, 5, 24, 'Wakasema: Ewe Musa! Sisi hatutaingia humo kamwe maadamu wao wamo humo. Basi nenda wewe na Mola wako Mlezi mkapigane. Sisi tutakaa hapa hapa.'),
(694, 5, 25, 'Akasema: Mola wangu Mlezi! Hakika mimi similiki ila nafsi yangu na ndugu yangu. Basi tutenge na hawa watu wapotovu.'),
(695, 5, 26, '(Mwenyezi Mungu) akasema: Basi nchi hiyo wameharimishiwa muda wa miaka arubaini, watakuwa wakitanga-tanga katika ardhi. Basi wewe usiwasikitikie watu wapotovu.'),
(696, 5, 27, 'Na wasomee khabari za wana wawili wa Adam kwa kweli. Walipo toa mhanga, ukakubaliwa wa mmoja wao, na wa mwengine haukukubaliwa. Akasema: Nitakuuwa. Akasema mwengine: Mwenyezi Mungu huwapokelea wachamngu.'),
(697, 5, 28, 'Ukininyooshea mkono wako kuniuwa, mimi sitakunyooshea mkono wangu kukuuwa. Hakika mimi namwogopa Mwenyezi Mungu, Mola wa walimwengu wote.'),
(698, 5, 29, 'Mimi nataka ubebe dhambi zangu na dhambi zako, kwani wewe utakuwa miongoni mwa watu wa Motoni. Na hayo ndiyo malipo ya wenye kudhulumu.'),
(699, 5, 30, 'Basi nafsi yake ikampelekea kumuuwa nduguye, akamuuwa na akawa miongoni mwa wenye kukhasirika.'),
(700, 5, 31, 'Hapo Mwenyezi Mungu akamleta kunguru anaye fukua katika ardhi ili amwonyeshe vipi kumsitiri nduguye. Akasema: Ole wangu! Nimeshindwa kuwa kama kunguru huyu nikamsitiri ndugu yangu? Basi akawa miongoni mwa wenye kujuta.'),
(701, 5, 32, 'Kwa sababu ya hayo tuliwaandikia Wana wa Israili ya kwamba aliye muuwa mtu bila ya yeye kuuwa, au kufanya uchafuzi katika nchi, basi ni kama amewauwa watu wote. Na mwenye kumuokoa mtu na mauti ni kama amewaokoa watu wote. Na hakika waliwajia Mitume wetu na hoja zilizo wazi. Kisha wengi katika wao baada ya haya wakawa waharibifu katika nchi.'),
(702, 5, 33, 'Basi malipo ya wale wanao mpiga vita Mwenyezi Mungu na Mtume wake na wakawania kufanya uchafuzi katika nchi, ni kuuwawa, au kusalibiwa, au kukatwa mikono yao na miguu yao kwa mabadilisho, au kutolewa nchi. Hii ndiyo hizaya yao katika dunia; na katika Akhera watapata adhabu kubwa.'),
(703, 5, 34, 'Isipo kuwa wale walio tubu kabla hamjawatia nguvuni. Na jueni kwamba Mwenyezi Mungu ni Mwenye maghfira na Mwenye kurehemu.'),
(704, 5, 35, 'Enyi mlio amini! Mcheni Mwenyezi Mungu, na tafuteni njia ya kumfikilia. Na wanieni kwa juhudi kwa ajili yake ili mpate kuokoka.'),
(705, 5, 36, 'Hakika walio kufuru lau wange kuwa na yote yaliyomo duniani, na mengine kama hayo, ili watoe fidia ya kuepukana na adhabu ya Siku ya Kiyama, yasingeli pokelewa kwao; na watapata adhabu chungu.'),
(706, 5, 37, 'Watataka watoke Motoni, lakini hawatatoka humo, na watakuwa na adhabu inayo dumu.'),
(707, 5, 38, 'Na mwizi mwanamume na mwizi mwanamke, ikateni mikono yao, hayo ni malipo ya waliyo yachuma, ndiyo adhabu ya mfano itokayo kwa Mwenyezi Mungu. Na Mwenyezi Mungu ni Mwenye nguvu na Mwenye hikima.'),
(708, 5, 39, 'Lakini mwenye kutubia baada ya dhulma yake, na akatengenea, basi Mwenyezi Mungu atapokea toba yake. Hakika Mwenyezi Mungu ni Mwenye maghfira na Mwenye kurehemu.'),
(709, 5, 40, 'Hujui ya kwamba Mwenyezi Mungu anao ufalme wa mbingu na ardhi? Humuadhibu amtakaye, na humsamehe amtakaye. Na Mwenyezi Mungu ni Muweza wa kila kitu.'),
(710, 5, 41, 'Ewe Mtume! Wasikuhuzunishe wa fanyao haraka kukufuru, miongoni mwa wanao sema kwa vinywa vyao: Tumeamini, na hali nyoyo zao hazikuamini, na miongoni mwa Mayahudi, wanao sikiliza kwa ajili ya uwongo, wanao sikiliza kwa ajili ya watu wengine wasio kufikia. Wao huyabadilisha maneno kutoka pahala pake. Wanasema: Mkipewa haya basi yashikeni, na msipo pewa haya tahadharini. Na mtu ambaye Mwenyezi Mungu anataka kumfitini huwezi kuwa na madaraka naye mbele ya Mwenyezi Mungu. Hao ndio ambao Mwenyezi Mungu hataki kuzitakasa nyoyo zao. Watakuwa na hizaya duniani, na Akhera watakuwa na adhabu kubwa.'),
(711, 5, 42, 'Hao ni wasikilizaji kwa ajili ya kusema uwongo, na ni walaji mno vya haramu! Basi wakikujia, wahukumu baina yao au jipuuze nao. Na ukijipuuza nao, basi wao hawatakudhuru kitu. Na ukiwahukumu, basi hukumu baina yao kwa uadilifu. Hakika Mwenyezi Mungu anawapenda waadilifu.'),
(712, 5, 43, 'Na huwaje wakufanye wewe hakimu nao wanayo Taurati yenye hukumu za Mwenyezi Mungu? Kisha baada ya hayo wanageuka. Na hao si wenye kuamini.'),
(713, 5, 44, 'Hakika Sisi tuliteremsha Taurati yenye uwongofu na nuru, ambayo kwayo Manabii walio nyenyekea kiislamu, na wachamngu, na wanazuoni, waliwahukumu Mayahudi; kwani walikabidhiwa kukihifadhi Kitabu cha Mwenyezi Mungu. Nao wakawa ni mashahidi juu yake. Basi msiwaogope watu, bali niogopeni Mimi. Wala msibadilishe Aya zangu kwa thamani chache. Na wasio hukumu kwa aliyo teremsha Mwenyezi Mungu, basi hao ndio makafiri.'),
(714, 5, 45, 'Na humo tuliwaandikia ya kwamba roho kwa roho, na jicho kwa jicho, na pua kwa pua, na sikio kwa sikio, na jino kwa jino, na kwa majaraha kisasi. Lakini atakaye samehe basi itakuwa ni kafara kwake. Na wasio hukumu kwa yale aliyo yateremsha Mwenyezi Mungu, basi hao ndio madhaalimu.'),
(715, 5, 46, 'Na tukawafuatishia hao Isa bin Maryamu kuyahakikisha yaliyo kuwa kabla yake katika Taurati, na tukampa Injili iliyomo ndani yake uwongofu, na nuru na inayo sadikisha yaliyo kuwa kabla yake katika Taurati, na uwongofu na mawaidha kwa wachamngu.'),
(716, 5, 47, 'Na wahukumu Watu wa Injili kwa yale aliyo teremsha Mwenyezi Mungu ndani yake. Na wasio hukumu kwa yale aliyo yateremsha Mwenyezi Mungu basi hao ndio wapotofu.'),
(717, 5, 48, 'Na tumekuteremshia wewe, kwa haki, Kitabu hichi kinacho sadikisha yaliyo kuwa kabla yake katika Vitabu na kuyalinda. Basi hukumu baina yao kwa aliyo yateremsha Mwenyezi Mungu, wala usifuate matamanio yao ukaacha Haki iliyo kujia. Kila mmoja katika nyinyi tumemwekea sharia yake na njia yake. Na lau kuwa Mwenyezi Mungu angeli taka ange kufanyeni nyote umma mmoja, lakini ni kukujaribuni kwa aliyo kupeni. Basi shindaneni kwa mambo ya kheri. Kwa Mwenyezi Mungu ndio marejeo yenu nyote, na atakuja kuwaambieni yale mliyo kuwa mkikhitalifiana.'),
(718, 5, 49, 'Na wahukumu baina yao kwa aliyo yateremsha Mwenyezi Mungu, wala usifuate matamanio yao. Nawe tahadhari nao wasije kukufitini ukaacha baadhi ya aliyo kuteremshia Mwenyezi Mungu. Na wakigeuka, basi jua kwamba hakika Mwenyezi Mungu anataka kuwasibu kwa baadhi ya dhambi zao. Na hakika wengi wa watu ni wapotofu.'),
(719, 5, 50, 'Je, wao wanataka hukumu za Kijahiliya? Na nani aliye mwema zaidi katika kuhukumu kuliko Mwenyezi Mungu kwa watu wenye yakini?'),
(720, 5, 51, 'Enyi mlio amini! Msiwafanye Mayahudi na Wakristo kuwa ni marafiki. Wao ni marafiki wao kwa wao. Na miongoni mwenu mwenye kufanya urafiki nao, basi huyo ni katika wao. Hakika Mwenyezi Mungu hawaongoi watu wenye kudhulumu.'),
(721, 5, 52, 'Utawaona wale wenye maradhi nyoyoni mwao wanakimbilia kwao wakisema: Tunakhofu yasitusibu mabadiliko. Huenda Mwenyezi Mungu akaleta ushindi au jambo jingine litokalo kwake wakawa wenye kujuta kwa waliyo yaficha katika nafsi zao.'),
(722, 5, 53, 'Na walio amini watasema: Hivyo hawa ndio wale walio apa ukomo wa viapo vyao kuwa wao wa pamoja nanyi? Vitendo vyao vitapotea bure, na wataamkia kuwa wenye kukhasirika.'),
(723, 5, 54, 'Enyi mlio amini! Atakaye iacha miongoni mwenu Dini yake, basi Mwenyezi Mungu atakuja leta watu anao wapenda nao wanampenda, wanyenyekevu kwa Waumini na wenye nguvu juu ya makafiri, wanapigania Jihadi katika Njia ya Mwenyezi Mungu, wala hawaogopi lawama ya anaye laumu. Hiyo ndiyo fadhila ya Mwenyezi Mungu, humpa amtakaye. Na Mwenyezi Mungu ni Mkunjufu na Mwenye kujua.'),
(724, 5, 55, 'Hakika rafiki yenu mlinzi ni Mwenyezi Mungu na Mtume wake na walio amini, ambao hushika Sala na hutoa Zaka nao wananyenyekea.'),
(725, 5, 56, 'Na atakaye fanya urafiki na Mwenyezi Mungu na Mtume wake na walio amini, basi hakika kundi la Mwenyezi Mungu ndilo lenye kushinda.'),
(726, 5, 57, 'Enyi mlio amini! Msiwafanye marafiki wale walio ifanyia kejeli na mchezo Dini yenu miongoni mwa walio pewa Kitabu kabla yenu, na makafiri wengine. Na mcheni Mwenyezi Mungu kama nyinyi ni wenye kuamini.'),
(727, 5, 58, 'Na mnapo adhinia Sala wao wanaichukulia maskhara na mchezo. Hayo ni kwa sababu wao ni watu wasio na akili.'),
(728, 5, 59, 'Sema: Enyi Watu wa Kitabu! Hivyo mnatuchukia kwa kuwa tumemuamini Mwenyezi Mungu na tuliyo teremshiwa na yaliyo teremshwa kabla, na kwa kuwa wengi katika nyinyi ni wapotofu?'),
(729, 5, 60, 'Sema: Je! Nikwambieni yule ambaye ni mwenye malipo mabaya zaidi kuliko hayo mbele ya Mwenyezi Mungu? Ni yule ambaye Mwenyezi Mungu amemlaani na amemkasirikia, na amewafanya miongoni mwao manyani na nguruwe, na wakamuabudu Shet\'ani. Hao ndio wenye mahali pabaya na wamepotea zaidi njia iliyo sawa.'),
(730, 5, 61, 'Na wanapo kujieni husema: Tumeamini. Lakini hakika wao wameingia na ukafiri wao, na wametoka nao pia. Na Mwenyezi Mungu ni Mjuzi kabisa wa yote wanayo yaficha.'),
(731, 5, 62, 'Na utawaona wengi katika wao wanakimbilia kwenye dhambi, na uadui, na ulaji wao vya haramu. Ni maovu mno hayo wayatendayo!'),
(732, 5, 63, 'Mbona hao wanazuoni na makohani wao hawawakatazi maneno yao ya dhambi, na ulaji wao vya haramu? Ni maovu mno hayo wayafanyayo!'),
(733, 5, 64, 'Na Mayahudi walisema: Mkono wa Mwenyezi Mungu umefumba. Mikono yao ndiyo iliyo fumba, na wamelaaniwa kwa sababu ya waliyo yasema. Bali mikono yake iwazi. Hutoa apendavyo. Kwa yakini yaliyo teremshwa kwako kutoka kwa Mola wako Mlezi yatawazidisha wengi katika wao uasi na kufuru. Na Sisi tumewatilia uadui na chuki baina yao mpaka Siku ya Kiyama. Kila mara wanapo washa moto wa vita, Mwenyezi Mungu anauzima. Na wanajitahidi kuleta uharibifu katika ardhi. Na Mwenyezi Mungu hawapendi waharibifu.'),
(734, 5, 65, 'Na lau kuwa Watu wa Kitabu wangeli amini na wakachamngu hapana shaka tungeli wafutia makosa yao, na tungeli waingiza katika Bustani zenye neema.'),
(735, 5, 66, 'Na lau kuwa wangeli ishika Taurati na Injili na yote waliyo teremshiwa kutokana na Mola wao Mlezi, basi hapana shaka wangeli kula vya juu yao na vya chini ya miguu yao. Wapo watu miongoni mwao walio sawa, lakini wengi wao wanayo yafanya ni mabaya mno.'),
(736, 5, 67, 'Ewe Mtume! Fikisha uliyo teremshiwa kutoka kwa Mola wako Mlezi. Na ikiwa hukufanya hayo basi hukufikisha ujumbe wake. Na Mwenyezi Mungu atakulinda na watu. Hakika Mwenyezi Mungu hawaongoi watu makafiri.'),
(737, 5, 68, 'Sema: Enyi Watu wa Kitabu! Hamna lenu jambo mpaka muishike Taurati na Injili na yote mlio teremshiwa kutokana na Mola wenu Mlezi. Na hakika ulio teremshiwa wewe kutokana na Mola wako Mlezi yatawazidishia wengi katika wao uasi na ukafiri. Basi usiwasikitikie watu makafiri.'),
(738, 5, 69, 'Hakika walio amini, na Mayahudi na Masabii na Wakristo, walio muamini Mwenyezi Mungu, na Siku ya mwisho, na wakatenda mema, basi hawatakuwa na khofu wala hawatahuzunika.'),
(739, 5, 70, 'Tulifanya agano na Wana wa Israili, na tukawatumia Mitume. Kila alipo wajia Mtume kwa wasio yapenda nafsi zao wengine waliwakanusha na wengine wakawauwa.'),
(740, 5, 71, 'Na wakadhani kuwa hapatakuwa majaribio; basi wakawa vipofu na viziwi. Kisha Mwenyezi Mungu akawapokelea toba zao. Kisha tena wengi katika wao wakawa vipofu na viziwi. Na Mwenyezi Mungu ni Mwenye kuyaona hayo wayatendayo.'),
(741, 5, 72, 'Hakika wamekufuru walio sema: Mwenyezi Mungu ni Masihi mwana wa Maryamu! Na hali Masihi mwenyewe alisema: Enyi Wana wa Israili! Muabuduni Mwenyezi Mungu, Mola wangu Mlezi na Mola wenu Mlezi. Kwani anaye mshirikisha Mwenyezi Mungu, hakika Mwenyezi Mungu atamharimishia Pepo, na mahala pake ni Motoni. Na walio dhulumu hawatakuwa na wa kuwanusuru.'),
(742, 5, 73, 'Kwa hakika wamekufuru walio sema: Mwenyezi Mungu ni wa tatu wa Utatu. Hali hakuna mungu ila Mungu Mmoja. Na ikiwa hawaachi hayo wanayo yasema, kwa yakini itawakamata adhabu chungu wale wanao kufuru.'),
(743, 5, 74, 'Je! Hawatubu kwa Mwenyezi Mungu na wakamwomba msamaha? Na Mwenyezi Mungu ni Mwenye maghfira na Mwenye kurehemu.'),
(744, 5, 75, 'Masihi mwana wa Maryam si chochote ila ni Mtume. Wamekwisha pita Mitume kabla yake. Na mama yake ni mwanamke mkweli. Wote wawili walikuwa wakila chakula. Angalia jinsi tunavyo wabainishia Aya, kisha angalia vipi wanavyo geuzwa.'),
(745, 5, 76, 'Sema: Je! Mnawaabudu, badala ya Mwenyezi Mungu, wale ambao hawawezi kukudhuruni wala kukufaeni? Na Mwenyezi Mungu ndiye Mwenye kusikia, Mwenye kujua.'),
(746, 5, 77, 'Sema: Enyi Watu wa Kitabu! Msipite kiasi katika dini yenu bila ya haki. Wala msifuate matamanio ya watu walio kwisha potea tangu zamani, na wakawapoteza wengi, na wenyewe wakapotea njia iliyo sawa.'),
(747, 5, 78, 'Walilaaniwa walio kufuru miongoni mwa Wana wa Israili kwa ulimi wa Daud na wa Isa mwana wa Maryamu. Hayo ni kwa sababu waliasi na wakawa wanapindukia mipaka.'),
(748, 5, 79, 'Walikuwa hawakatazani maovu waliyo kuwa wakiyafanya. Hakika ni maovu yalioje mambo waliyo kuwa wakiyafanya!'),
(749, 5, 80, 'Utawaona wengi wao wanafanya urafiki na walio kufuru. Hakika ni maovu yaliyo tangulizwa na nafsi zao hata Mwenyezi Mungu amewakasirikia, nao watadumu katika adhabu.'),
(750, 5, 81, 'Na lau wangeli kuwa wanamuamini Mwenyezi Mungu, na huyu Nabii, na yaliyo teremshwa kwake, wasingeli wafanya hao marafiki. Lakini wengi katika wao ni wapotofu.'),
(751, 5, 82, 'Hakika utawakuta walio shadidi kuliko watu wote katika uadui kwa walio amini ni Mayahudi na washirikina. Na utawakuta walio karibu mno kwa mapenzi ni wale wanao sema: Sisi ni Manasara. Hayo ni kwa sababu wapo miongoni mwao makasisi na wamonaki, na kwa sababu wao hawafanyi kiburi.'),
(752, 5, 83, 'NA WANAPO SIKIA yaliyo teremshwa kwa Mtume utaona macho yao yanachururika machozi kwa sababu ya Haki waliyo itambua. Wanasema: Mola wetu Mlezi! Tumeamini. Basi tuandike pamoja na wanao shuhudia.'),
(753, 5, 84, 'Na kwa nini tusimuamini Mwenyezi Mungu na Haki iliyo tujia, na hali tunatumai Mola wetu Mlezi atuingize pamoja na watu wema?'),
(754, 5, 85, 'Basi Mwenyezi Mungu atawalipa, kwa yale waliyo yasema, Bustani zipitazo mito katika yake; humo watadumu. Na haya ndiyo malipo ya wafanyao wema.'),
(755, 5, 86, 'Na wale walio kufuru na wakazikanusha Ishara zetu, hao ndio watu wa Motoni.'),
(756, 5, 87, 'Enyi mlio amini! Msiharimishe vizuri alivyo kuhalalishieni Mwenyezi Mungu, wala msikiuke mipaka. Hakika Mwenyezi Mungu hawapendi wakiukao mipaka.'),
(757, 5, 88, 'Na kuleni katika alivyo kuruzukuni Mwenyezi Mungu, halali na vizuri. Na mcheni Mwenyezi Mungu ambaye mnamuamini.'),
(758, 5, 89, 'Mwenyezi Mungu hatakushikeni kwa viapo vyenu vya upuuzi, lakini atakushikeni kwa mnavyo apa kweli kweli kwa makusudio. Basi kafara yake ni kuwalisha masikini kumi kwa chakula cha wastani mnacho walisha ahali zenu, au kuwavisha, au kumkomboa mtumwa. Asiye pata hayo, basi afunge siku tatu. Hii ndiyo kafara ya viapo vyenu mnapo apa. Na hifadhini yamini zenu. Namna hivyo Mwenyezi Mungu anakubainishieni Aya zake ili mpate kushukuru.'),
(759, 5, 90, 'Enyi mlio amini! Bila ya shaka ulevi, na kamari, na kuabudu masanamu, na kupiga ramli, ni uchafu katika kazi ya Shet\'ani. Basi jiepusheni navyo, ili mpate kufanikiwa.'),
(760, 5, 91, 'Hakika Shet\'ani anataka kutia kati yenu uadui na chuki kwa ulevi na kamari, na akuzuieni kumkumbuka Mwenyezi Mungu na kusali. Basi je, mmeacha?'),
(761, 5, 92, 'Na mt\'iini Mwenyezi Mungu, na mt\'iini Mtume, na tahadharini. Na ikiwa mtageuka basi jueni ya kuwa juu ya Mtume wetu ni kufikisha ujumbe tu ulio wazi.'),
(762, 5, 93, 'Si dhambi juu ya walio amini na wakatenda mema kwa walivyo vila (zamani) maadamu wakichamngu na wakiamini na wakitenda mema, kisha wakachamngu, na wakaamini, kisha wakachamngu na wakafanya mazuri. Na Mwenyezi Mungu huwapenda wafanyao mazuri.'),
(763, 5, 94, 'Enyi mlio amini! Mwenyezi Mungu atakujaribuni kidogo kwa wanyama wa kuwinda inayo wafikia mikono yenu na mikuki yenu, ili Mwenyezi Mungu amjue nani anaye mkhofu kwa ghaibu. Basi atakaye ruka mipaka baada ya hayo atapata adhabu kali.'),
(764, 5, 95, 'Enyi mlio amini! Msiue wanyama wa kuwinda nanyi mmeharimia katika Hija. Na miongoni mwenu atakaye muuwa makusudi, basi malipo yake yatakuwa kwa kuchinja kilicho sawa na alicho kiuwa, katika mifugo, kama wanavyo hukumu waadilifu wawili miongoni mwenu. Mnyama huyo apelekwe kwenye Al Kaaba, au kutoa kafara kwa kuwalisha masikini; au badala ya hayo ni kufunga, ili aonje ubaya wa jambo lake hili. Mwenyezi Mungu amekwisha yafuta yaliyo pita; lakini atakaye fanya tena Mwenyezi Mungu atampa adhabu. Na Mwenyezi Mungu ndiye Mwenye nguvu na Mwenye kuadhibu.'),
(765, 5, 96, 'Nyinyi mmehalalishiwa kuvua vinyama vya baharini na kuvila, kwa faida yenu na kwa wasafiri. Na mmeharimishiwa mawindo ya wanyama wa bara maadamu mmeharimia Hija. Na mcheni Mwenyezi Mungu ambaye mtakusanywa kwake.'),
(766, 5, 97, 'Mwenyezi Mungu ameifanya Al Kaaba, hii Nyumba Tukufu, ni simamio la watu, na kadhaalika Miezi Mitukufu, na wanyama wa dhabihu, na vigwe. Hayo ili mjue ya kwamba Mwenyezi Mungu anayajua yaliyomo mbinguni na yaliyomo katika ardhi, na ya kwamba Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(767, 5, 98, 'Jueni kwamba Mwenyezi Mungu ni Mkali wa kuadhibu, na ya kwamba Mwenyezi Mungu ni Mwenye maghfira, na Mwenye kurehemu.'),
(768, 5, 99, 'Haimlazimu Mtume ila kufikisha ujumbe tu. Na Mwenyezi Mungu anajua mnacho kidhihirisha na mnacho kificha.'),
(769, 5, 100, 'Sema: Haviwi sawa viovu na vyema ujapo kupendeza wingi wa viovu. Basi mcheni Mwenyezi Mungu, enyi wenye akili, ili mpate kufanikiwa.'),
(770, 5, 101, 'Enyi mlio amini! Msiulize mambo ambayo mkidhihirishiwa yatakuchukizeni. Na mkiyauliza inapo teremshwa Qur\'ani mtabainishiwa. Mwenyezi Mungu amesamehe hayo. Na Mwenyezi Mungu ni Mwenye maghfira Mpole.'),
(771, 5, 102, 'Waliyauliza hayo watu wa kabla yenu, kisha wakawa wenye kuyakataa.'),
(772, 5, 103, 'Mwenyezi Mungu hakujaalia uharamu wowote juu ya "Bahira" wala "Saiba" wala "Wasila" wala "Hami". Lakini walio kufuru humzulia uwongo Mwenyezi Mungu, na wengi wao hawatumii akili.'),
(773, 5, 104, 'Na wanapo ambiwa: Njooni kwenye yale aliyo yateremsha Mwenyezi Mungu, na kwa Mtume, husema: Yanatutosha tuliyo wakuta nayo baba zetu. Je, hata ikiwa baba zao walikuwa hawajui kitu wala hawakuongoka?'),
(774, 5, 105, 'Enyi mlio amini! Kilicho lazima juu yenu ni nafsi zenu. Hawakudhuruni walio potoka ikiwa nyinyi mmeongoka. Ni kwa Mwenyezi Mungu ndio marudio yenu nyote; basi atakwambieni yale mliyo kuwa mkiyatenda.'),
(775, 5, 106, 'Enyi mlio amini! Yanapo mfikia mauti mmoja wenu na akataka kutoa wasia, basi wawepo mashahidi wawili miongoni mwenu ambao ni waadilifu. Na mnapo kuwa safarini, na msiba wa mauti ukakusibuni, basi washuhudie wengineo wawili wasio kuwa katika nyinyi. Mtawazuia wawili hao baada ya Sala na waape kwa Mwenyezi Mungu, mkitilia shaka, wakisema: Hatutopokea thamani yoyote kwa haya hata kwa ajili ya jamaa; na wala hatutaficha ushahidi wa Mwenyezi Mungu; hakika hapo tutakuwa miongoni mwa wenye dhambi.'),
(776, 5, 107, 'Ikigundulikana kuwa wawili hao wamestahiki dhambi, basi wawili wengineo kutokana na warithi wenye kudai haki washike makamo ya wa mwanzo. Nao waape kwa Mwenyezi Mungu wakisema: Hakika ushahidi wetu ni wa haki zaidi kuliko ushahidi wa wale. Na sisi hatujafanya dhambi; hakika hapo tutakuwa miongoni mwa walio dhulumu.'),
(777, 5, 108, 'Hivi inaelekea zaidi ya kwamba watatoa ushahidi ulio sawa au wataogopa visije vikaletwa viapo vingine baada ya viapo vyao. Na mcheni Mwenyezi Mungu na msikie. Na hakika Mwenyezi Mungu hawaongoi wapotofu.'),
(778, 5, 109, 'Ile siku ambayo Mwenyezi Mungu atapo wakusanya Mitume awaambie: Mlijibiwa nini? Watasema: Hatuna ujuzi; hakika Wewe ndiye Mjuzi Mkubwa wa yote yaliyo fichikana.'),
(779, 5, 110, 'Na pale Mwenyezi Mungu atapo sema: Ewe Isa bin Maryamu! Kumbuka neema yangu juu yako, na juu ya mama yako, nilipo kutia nguvu kwa Roho Takatifu, ukazungumza na watu katika utoto na utuuzimani. Na nilivyo kufunza kuandika na hikima na Taurati na Injili. Na ulipo tengeneza udongo sura ya ndege, kwa idhini yangu, kisha ukapuliza ikawa ndege kwa idhini yangu; na ulipo waponesha vipofu na wakoma kwa idhini yangu; na ulipo wafufua wafu kwa idhini yangu; na nilipo kukinga na Wana wa Israili ulipo wajia na hoja zilizo wazi, na wakasema walio kufuru miongoni mwao: Haya si lolote ila ni uchawi mtupu!'),
(780, 5, 111, 'Na nilipo wafunulia Wanafunzi kwamba waniamini Mimi na Mtume wangu, wakasema: Tumeamini na shuhudia kuwa sisi ni Waislamu.'),
(781, 5, 112, 'Wanafunzi walipo sema: Ewe Isa bin Maryamu! Je, Mola wako Mlezi anaweza kututeremshia chakula kutoka mbinguni? Akasema: Mcheni Mwenyezi Mungu ikiwa nyinyi ni Waumini.'),
(782, 5, 113, 'Wakasema: Tunataka kukila chakula hicho, na nyoyo zetu zitue, na tujue kwamba umetuambia kweli, na tuwe miongoni mwa wanao shuhudia.'),
(783, 5, 114, 'Akasema Isa bin Maryamu: Ewe Mwenyezi Mungu, Mola Mlezi wetu! Tuteremshia chakula kutoka mbinguni ili kiwe Sikukuu kwa ajili ya wa mwanzo wetu na wa mwisho wetu, na kiwe ni Ishara itokayo kwako. Basi turuzuku, kwani Wewe ndiye mbora wa wanao ruzuku.'),
(784, 5, 115, 'Mwenyezi Mungu akasema: Hakika Mimi nitakuteremshieni hicho. Lakini yeyote katika nyinyi atakaye kanya baadae, basi hakika Mimi nitampa adhabu nisiyopata kumpa yeyote katika walimwengu.'),
(785, 5, 116, 'Na pale Mwenyezi Mungu atakapo sema: Ewe Isa bin Maryamu! Ati wewe uliwaambia watu: Nifanyeni mimi na mama yangu kuwa ni miungu badala ya Mwenyezi Mungu? (Na Isa) atasema: Subhanaka, Wewe umetakasika! Hainifalii mimi kusema ambayo si haki yangu. Ikiwa nilisema basi bila ya shaka umekwisha yajua. Wewe unayajua yaliyo ndani ya nafsi yangu, lakini mimi siyajui yaliyo katika nafsi yako. Hakika Wewe ndiye Mjuzi mkubwa wa yaliyo fichikana.'),
(786, 5, 117, 'Sikuwaambia lolote ila uliyo niamrisha, nayo ni: Muabuduni Mwenyezi Mungu, Mola wangu Mlezi na Mola wenu Mlezi. Na mimi nilikuwa shahidi juu yao nilipo kuwa nao. Na ulipo nifisha ukawa Wewe ndiye Muangalizi juu yao. Na Wewe ni shahidi juu ya kila kitu.'),
(787, 5, 118, 'Ukiwaadhibu basi hao ni waja wako. Na ukiwasamehe basi Wewe ndiye Mwenye nguvu na Mwenye hikima.'),
(788, 5, 119, 'Mwenyezi Mungu atasema: Hii ndiyo Siku ambayo wasemao kweli utawafaa ukweli wao. Wao watapata Bustani zipitazo mito kati yake. Humo watadumu milele. Mwenyezi Mungu amewawia radhi, nao wawe radhi naye. Huku ndiko kufuzu kukubwa.'),
(789, 5, 120, 'Mwenyezi Mungu ndiye Mwenye ufalme wa mbingu na ardhi na vilivyomo. Naye ni Muweza wa kila kitu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Sifa njema zote ni za Mwenyezi Mungu aliye umba mbingu na ardhi, na akafanya giza na mwangaza. Tena baada ya haya walio kufuru wanawafanya wengine sawa na Mola wao Mlezi.'),
(791, 6, 2, 'Yeye ndiye aliye kuumbeni kwa udongo, kisha akakuhukumieni ajali, na muda maalumu uko kwake tu. Tena baada ya haya nyinyi mnatia shaka.'),
(792, 6, 3, 'Na Yeye ndiye Mwenyezi Mungu mbinguni na ardhini. Anajua ya ndani yenu na ya nje yenu. Na anajua mnayo yachuma.'),
(793, 6, 4, 'Na haiwafikii hata ishara moja katika ishara za Mola wao Mlezi ila wao huwa wenye kuipa nyongo.'),
(794, 6, 5, 'Wameikanusha Haki ilipo wajia. Basi zitawajia khabari za yale waliyo kuwa wakiyakejeli.'),
(795, 6, 6, 'Jee, hawaoni vizazi vingapi tulivyo viangamiza kabla yao? Tuliwaweka katika nchi tusivyo kuwekeni nyinyi; tukawapelekea mvua nyingi na tukaifanya mito inapita chini yao. Mwishoe tukawaangamiza kwa madhambi yao; na tukaanzisha baada yao kizazi kingine.'),
(796, 6, 7, 'Na lau tungeli kuteremshia kitabu cha karatasi, wakakigusa kwa mikono yao, wangeli sema walio kufuru: Haya si chochote ila ni uchawi dhaahiri.'),
(797, 6, 8, 'Na wao husema: Mbona hakuteremshiwa Malaika? Na kama tungeli teremsha Malaika, basi bila ya shaka ingesha hukumiwa amri, tena hapo wasingeli pewa muhula.'),
(798, 6, 9, 'Na kama tungeli mfanya Malaika bila ya shaka tungeli mfanya kama binaadamu, na tungeli watilia matatizo yale wanayo yatatiza wao.'),
(799, 6, 10, 'Na hakika Mitume walio kabla yako walifanyiwa kejeli, lakini walio wafanyia kejeli wakaja kuzungukwa na yale yale waliyo kuwa wao wanayafanyia kejeli.'),
(800, 6, 11, 'Sema: Tembeeni ulimwenguni, kisha mtazame ulikuwaje mwisho wa wanao kanusha.'),
(801, 6, 12, 'Sema: Ni vya nani viliomo katika mbingu na katika ardhi? Sema: Ni vya Mwenyezi Mungu. Yeye amejilazimisha nafsi yake kurehemu. Hakika atakukusanyeni Siku ya Kiyama isiyo kuwa na shaka. Walio jikhasiri wenyewe hawaamini.'),
(802, 6, 13, 'Na ni vyake vilio tulia usiku na mchana. Naye ndiye Mwenye kusikia na Mwenye kujua.'),
(803, 6, 14, 'Sema: Je, nimchukue rafiki mlinzi asiye kuwa Mwenyezi Mungu, ambaye ndiye Muumba mbingu na ardhi, naye ndiye anaye lisha wala halishwi? Sema: Mimi nimeamrishwa niwe wa kwanza wa wenye kusilimu. Na wala kabisa usiwe miongoni wa washirikina.'),
(804, 6, 15, 'Sema: Mimi naogopa adhabu ya Siku kubwa hiyo ikiwa nitamuasi Mola wangu Mlezi.'),
(805, 6, 16, 'Siku hiyo yule atakaye epushwa nayo adhabu hiyo atakuwa Mwenyezi Mungu amemrehemu. Na huko ndiko kufuzu kulio wazi.'),
(806, 6, 17, 'Na ikiwa Mwenyezi Mungu akikugusisha madhara, basi hapana wa kukuondolea hayo ila Yeye. Na ikiwa akikugusisha kheri, basi Yeye ndiye Mwenye uweza wa kila kitu.'),
(807, 6, 18, 'Naye ndiye Mwenye nguvu juu ya waja wake, na ndiye Mwenye hikima na Mwenye khabari zote.'),
(808, 6, 19, 'Sema: Kitu gani ushahidi wake mkubwa kabisa? Sema: Mwenyezi Mungu. Yeye ndiye shahidi baina yangu na nyinyi. Na nimefunuliwa Qur\'ani hii ili kwayo nikuonyeni nyinyi na kila inayo mfikia. Ati kweli nyinyi mnashuhudia kuwa pamoja na Mwenyezi Mungu wapo miungu wengine? Sema: Mimi sishuhudii hayo. Sema: Hakika Yeye ni Mungu mmoja tu, nami ni mbali na mnao washirikisha.'),
(809, 6, 20, 'Wale Tulio wapa Kitabu wanayajua haya kama wanavyo wajua watoto wao. Wale walio zikhasiri nafsi zao hawaamini.'),
(810, 6, 21, 'Na nani dhaalimu zaidi kuliko anaye mzulia uwongo Mwenyezi Mungu, na akazikanusha Ishara zake? Hakika madhaalimu hawafanikiwi.'),
(811, 6, 22, 'Siku tutapo wakusanya wote pamoja, kisha tuwaambie walio shirikisha: Wako wapi washirikishwa wenu mlio kuwa mnadai?'),
(812, 6, 23, 'Kisha hautakuwa udhuru wao ila ni kusema: Wallahi! Mola wetu Mlezi! Hatukuwa washirikina.'),
(813, 6, 24, 'Tazama jinsi wanavyo jisemea uwongo wenyewe. Na yamewapotea waliyo kuwa wakiyazua.'),
(814, 6, 25, 'Na wapo miongoni mwao wanao kusikiliza; na tumezitia pazia nyoyo zao wasije kuyafahamu. Na upo uziwi masikioni mwao, na wakiona kila Ishara hawaiamini. Hata wakikujia kwa kujadiliana nawe, wanasema walio kufuru: Hizi si chochote ila hadithi za watu wa kale.'),
(815, 6, 26, 'Nao huwazuia watu, na wao wenyewe wanajitenga nayo. Nao hawaangamizi ila nafsi zao tu, wala wao hawatambui.'),
(816, 6, 27, 'Na unge ona watakapo simamishwa kwenye Moto, wakawa wanasema: Laiti tungeli rudishwa, wala hatutakanusha tena Ishara za Mola wetu Mlezi, na tutakuwa miongoni wa Waumini.'),
(817, 6, 28, 'Bali yamewadhihirikia waliyo kuwa wakiyaficha zamani. Na kama wangeli rudishwa bila ya shaka wange yarejea yale yale waliyo katazwa. Na hakika hao ni waongo.'),
(818, 6, 29, 'Na walisema: Hakuna mengine ila maisha yetu haya ya duniani, wala sisi hatutafufuliwa.'),
(819, 6, 30, 'Na lau ungeli ona watavyo simamishwa mbele ya Mola wao Mlezi, akawaambia: Je, si kweli haya? Na wao watasema: Kwani? Tunaapa kwa Mola Mlezi wetu ni kweli. Yeye atasema: Basi onjeni adhabu kwa sababu ya vile mlivyo kuwa mnakataa.'),
(820, 6, 31, 'Hakika wamekhasirika walio kanusha kukutana na Mwenyezi Mungu, mpaka ilipo wajia Saa kwa ghafla, wakasema: Ole wetu kwa tuliyo yapuuza! Nao watabeba mizigo yao juu ya migongo yao. Ni maovu hayo wanayo yabeba.'),
(821, 6, 32, 'Na maisha ya dunia si chochte ila ni mchezo na pumbao tu. Na hakika nyumba ya Akhera ni bora zaidi kwa wanao mcha Mungu. Basi, je, hamtii akilini?'),
(822, 6, 33, 'Tunajua ya kwamba yanakuhuzunisha wanayo yasema. Basi hakika wao hawakukanushi wewe, lakini hao madhaalimu wanazikataa Ishara za Mwenyezi Mungu.'),
(823, 6, 34, 'Na hakika walikanushwa Mitume wa kabla yako. Nao wakavumilia kule kukanushwa, na kuudhiwa, mpaka ilipo wafikia nusura yetu. Na hapana wa kubadilisha maneno ya Mwenyezi Mungu. Na bila ya shaka zimekujia baadhi ya khabari za Mitume hao.'),
(824, 6, 35, 'Na ikiwa ni makubwa kwako huku kukataa kwao, basi kama unaweza kutafuta njia ya chini kwa chini ya ardhi, au ngazi kwendea mbinguni ili uwaletee Ishara -- Na lau kuwa Mwenyezi Mungu angependa angeli wakusanya kwenye uwongofu. Basi usiwe miongoni mwa wasio jua.'),
(825, 6, 36, 'Hakika wanao kubali ni wale wanao sikia. Na ama wafu Mwenyezi Mungu atawafufua, na kisha kwake Yeye ndio watarejeshwa.'),
(826, 6, 37, 'Na wamesema: Kwa nini hakuteremshiwa muujiza kutoka kwa Mola wake Mlezi? Sema: Hakika Mwenyezi Mungu ni Muweza wa kuteremsha muujiza. Lakini wengi wao hawajui.'),
(827, 6, 38, 'Na hapana mnyama katika ardhi, wala ndege anaye ruka kwa mbawa zake mbili, ila ni umma kama nyinyi. Hatukupuuza Kitabuni kitu chochote. Kisha kwa Mola wao Mlezi watakusanywa.'),
(828, 6, 39, 'Na walio kanusha Ishara zetu ni viziwi na mabubu waliomo gizani. Mwenyezi Mungu humwachia kupotea amtakaye, na humweka katika Njia iliyo nyooka amtakaye.'),
(829, 6, 40, 'Sema: Mwaonaje ikikujieni adhabu ya Mwenyezi Mungu, au ikakufikieni hiyo Saa - mtamwomba asiye kuwa Mwenyezi Mungu, ikiwa nyinyi ni wakweli?'),
(830, 6, 41, 'Bali Yeye ndiye mtakaye mwomba, naye atakuondoleeni mnacho mwombea akipenda. Na mtasahau hao mnao wafanya washirika wake.'),
(831, 6, 42, 'Na kwa yakini tulipeleka Mitume kwa kaumu zilizo kuwa kabla yako, kisha tukazitia katika dhiki na mashaka ili zipate kunyenyekea.'),
(832, 6, 43, 'Kwa nini wasinyenyekee ilipo wafika adhabu yetu? Lakini nyoyo zao zilikuwa ngumu, na Shet\'ani akawapambia waliyo kuwa wakiyafanya.'),
(833, 6, 44, 'Basi walipo sahau walio kumbushwa tuliwafungulia milango ya kila kitu. Mpaka walipo furahia yale waliyo pewa tuliwashika kwa ghafla, na mara wakawa wenye kukata tamaa.'),
(834, 6, 45, 'Ikakatwa mizizi ya watu walio dhulumu - na wa kuhimidiwa ni Mwenyezi Mungu, Mola Mlezi wa viumbe vyote.'),
(835, 6, 46, 'Sema: Mwaonaje Mwenyezi Mungu akanyakua kusikia kwenu, na kuona kwenu, na akaziziba nyoyo zenu, ni mungu gani asiye kuwa Mwenyezi Mungu ataye kuleteeni hayo tena? Angalia vipi tunavyo zieleza Ishara, kisha wao wanapuuza.'),
(836, 6, 47, 'Sema: Mwaonaje, ikikufikieni adhabu ya Mwenyezi Mungu kwa ghafla, au kwa dhaahiri, jee wataangamizwa isipo kuwa walio dhulumu?'),
(837, 6, 48, 'Na hatuwatumi Mitume ila huwa ni wabashiri na waonyaji. Na wenye kuamini na wakatenda mema haitakuwa khofu juu yao, wala hawatahuzunika.'),
(838, 6, 49, 'Na walio kanusha Ishara zetu itawagusa adhabu kwa walivyo kuwa wakipotoka.'),
(839, 6, 50, 'Sema: Mimi sikwambiini kuwa ninazo khazina za Mwenyezi Mungu. Wala sijui mambo yaliyo fichikana. Wala sikwambiini kuwa mimi ni Malaika. Mimi sifuati ila yanayo funuliwa kwangu. Sema: Je, wanakuwa sawa, kipofu na mwenye kuona. Basi hamfikiri?'),
(840, 6, 51, 'Na waonye kwayo wanao ogopa kuwa watakusanywa kwa Mola wao Mlezi, hali kuwa hawana mlinzi wala mwombezi isipo kuwa Yeye, ili wapate kuchamngu.'),
(841, 6, 52, 'Wala usiwafukuze wanao mwomba Mola Mlezi wao asubuhi na jioni kwa kutaka radhi yake. Si juu yako hisabu yao hata kidogo, wala hisabu yako si juu yao hata kidogo, hata uwafukuze na uwe miongoni mwa wenye kudhulumu.'),
(842, 6, 53, 'Na hivi ndivyo tunavyo wajaribu wao kwa wao, ili waseme: Ni hao ndio Mwenyezi Mungu amewafadhilisha miongoni mwetu? Kwani Mwenyezi Mungu hawajui wanao shukuru?'),
(843, 6, 54, 'Na wanapo kujia wanao ziamini Ishara zetu waambie: Assalamu alaikum! Amani iwe juu yenu! Mola wenu Mlezi amejilazimisha rehema, ya kwamba atakaye fanya uovu miongoni mwenu kwa ujinga, kisha akatubu baada yake na akatengenea, basi hakika Yeye ni Mwenye maghfira na Mwenye kurehemu.'),
(844, 6, 55, 'Na ndio hivi tunavyo zieleza Ishara ili ibainike njia ya wakosefu.'),
(845, 6, 56, 'Sema: Mimi nimekatazwa kuwaabudu hao mnao waomba badala ya Mwenyezi Mungu. Sema: mimi sifuati matamanio yenu. Hivyo nitakuwa nimepotea, na sitakuwa miongoni mwa walio ongoka.'),
(846, 6, 57, 'Sema: Mimi nipo kwenye sharia iliyo wazi itokayo kwa Mola wangu Mlezi, nanyi mnaikanusha. Mimi sinacho hicho mnacho kihimiza. Hapana hukumu ila ya Mwenyezi Mungu. Yeye anasimulia yaliyo kweli; naye ni Mbora wa kuhukumu kuliko wote.'),
(847, 6, 58, 'Sema: Lau kuwa ninacho hicho mnacho kihimiza, ingeli kwisha katwa shauri baina yangu na nyinyi. Na Mwenyezi Mungu anawashinda wote kuwajua madhaalimu.'),
(848, 6, 59, 'Na ziko kwake funguo za ghaibu; hakuna azijuaye ila Yeye tu. Na Yeye anajua kilioko nchi kavu na baharini. Na halidondoki jani ila analijua. Wala punje katika giza la ardhi, wala kinyevu, wala kikavu ila kimo katika Kitabu kinacho bainisha.'),
(849, 6, 60, 'Naye ndiye anaye kufisheni usiku, na anakijua mlicho fanya mchana. Kisha Yeye hukufufueni humo mchana ili muda ulio wekwa utimizwe. Kisha kwake Yeye ndiyo marejeo yenu, na tena akwambieni mliyo kuwa mkiyafanya.'),
(850, 6, 61, 'Naye ndiye Mwenye nguvu za kushinda, aliye juu ya waja wake. Na hukupelekeeni waangalizi, mpaka mmoja wenu yakimjia mauti wajumbe wetu humfisha, nao hawafanyi taksiri.'),
(851, 6, 62, 'Kisha watarudishwa kwa Mwenyezi Mungu, Mola wao wa Haki. Hakika, hukumu ni yake. Naye ni Mwepesi kuliko wote wanao hisabu.'),
(852, 6, 63, 'Sema: Ni nani anaye kuokoeni katika giza la nchi kavu na baharini? Mnamwomba kwa unyenyekevu na kwa siri, mkisema: Kama akituokoa na haya bila ya shaka tutakuwa miongoni mwa wanao shukuru.'),
(853, 6, 64, 'Sema: Mwenyezi Mungu hukuokoeni kutoka hayo, na kutoka kila mashaka, na kisha nyinyi mnamshirikisha!'),
(854, 6, 65, 'Sema: Yeye ndiye Muweza wa kukuleteeni adhabu kutoka juu yenu au kutoka chini ya miguu yenu, au kukuleteeni fujo la mfarakano, na kuwaonjesha baadhi yenu jeuri ya wenzao. Tazama vipi tunavyo zieleza Aya ili wapate kufahamu.'),
(855, 6, 66, 'Na watu wako wameikanusha, nayo ni Haki. Sema: Mimi sikuwakilishwa juu yenu.'),
(856, 6, 67, 'Kila khabari ina kipindi chake. Nanyi mtakuja jua.'),
(857, 6, 68, 'Na unapo waona wanao ziingilia Aya zetu, basi jitenge nao mpaka waingilie mazungumzo mengine. Na kama Shet\'ani akikusahaulisha, basi baada ya kutanabahi usikae pamoja na watu madhaalimu.'),
(858, 6, 69, 'Wala wamchao Mungu hawana jukumu lolote kwao, lakini ni kukumbusha, asaa wapate kujiepusha.'),
(859, 6, 70, 'Waachilie mbali walio ifanya dini yao ni mchezo na pumbao, na uhai wa dunia ukawaghuri. Nawe kumbusha kwayo, isije nafsi ikaangamizwa kwa sababu ya iliyo yachuma, nayo haina mlinzi wala mwombezi ila Mwenyezi Mungu. Na ingatoa kila fidia haitokubaliwa. Hao ndio walio angamizwa kwa sababu ya yale waliyo yachuma. Wao watapata kinywaji cha maji ya moto kabisa, na adhabu chungu.'),
(860, 6, 71, 'Sema: Je, tumuombe asiye kuwa Mwenyezi Mungu ambae hatufai wala hatudhuru, na turejee nyuma baada ya Mwenyezi Mungu kwisha tuhidi? Tuwe kama ambao mashet\'ani wamempumbaza katika ardhi, amebabaika? Anao marafiki wanao mwita ende kwenye uwongofu, wakimwambia: Njoo kwetu! Sema: Hakika uwongofu wa Mwenyezi Mungu ndio Uwongofu. Nasi tumeamrishwa tusilimu kwa Mola Mlezi wa viumbe vyote,'),
(861, 6, 72, 'Na mshike Sala, na mcheni Yeye, na kwake Yeye ndiko mtako kusanywa.'),
(862, 6, 73, 'Naye ndiye aliye ziumba mbingu na ardhi kwa Haki. Na anaposema: Kuwa! Basi huwa. Kauli yake ni Haki. Na ufalme wote ni wake Siku litapo pulizwa barugumu, Mjuzi wa yaliyo fichikana na yanayo onekana. Naye ndiye Mwenye hikima na Mwenye khabari.'),
(863, 6, 74, 'Na pale Ibrahim alipo mwambia baba yake, Azar: Unayafanya masanamu kuwa ni miungu? Hakika mimi nakuona wewe na watu wako mmo katika opotofu ulio wazi.'),
(864, 6, 75, 'Na kadhaalika tulimwonyesha Ibrahim ufalme wa mbingu na ardhi, na ili awe miongoni mwa wenye yakini.'),
(865, 6, 76, 'Na ulipo muingilia usiku akiona nyota, akasema: Huyu ni Mola Mlezi wangu. Ilipo tua akasema: Siwapendi wanao tua.'),
(866, 6, 77, 'Alipo uona mwezi unachomoza alisema: Huyu ndiye Mola Mlezi wangu. Ulipotua akasema: Kama Mola Mlezi wangu hakuniongoa, nitakuwa katika kaumu walio potea.'),
(867, 6, 78, 'Na alipo liona jua linachomoza akasema: Huyu ndiye Mola Mlezi wangu. Huyu mkubwa kuliko wote. Lilipo tua, alisema: Enyi watu wangu! Mimi simo katika hayo mnayo fanyia ushirikina.'),
(868, 6, 79, 'Hakika mimi nimeuelekeza uso wangu sawa sawa kwa aliye ziumba mbingu na ardhi, wala mimi si miongoni mwa washirikina.'),
(869, 6, 80, 'Na watu wake wakamhoji. Akasema: Je, mnanihoji juu ya Mwenyezi Mungu, na hali Yeye ameniongoa? Wala siogopi hao mnao washirikisha naye, ila Mola wangu Mlezi akipenda kitu. Mola wangu Mlezi amekusanya ilimu ya kila kitu. Basi je, hamkumbuki?'),
(870, 6, 81, 'Na vipi nivikhofu hivyo mnavyo vishirikisha, hali nyinyi hamkhofu kuwa nyinyi mmemshirikisha Mwenyezi Mungu na kitu ambacho hakukiteremshia uthibitisho. Basi kundi gani katika mawili haya lina haki zaidi kupata amani, kama nyinyi mnajua?'),
(871, 6, 82, 'Wale ambao wameamini, na hawakuchanganya imani yao na dhulma - hao ndio watakao pata amani na wao ndio walio ongoka.'),
(872, 6, 83, 'Na hizo ndizo hoja zetu tulizo mpa Ibrahim kuhojiana na watu wake. Tunamnyanyua kwa vyeo tumtakaye. Hakika Mola Mlezi wako ni Mwenye hikima na Mwenye ujuzi.'),
(873, 6, 84, 'Na tukamtunukia (Ibrahim) Is-haq na Yaakub. Kila mmoja wao tulimwongoa. Na Nuhu tulimwongoa kabla. Na katika kizazi chake tulimwongoa Daud na Suleiman na Ayyub na Yusuf na Musa na Harun. Na hivi ndivyo tunavyo walipa wafanyao wema.'),
(874, 6, 85, 'Na Zakaria na Yahya na Isa na Ilyas. Wote walikuwa miongoni mwa watu wema.'),
(875, 6, 86, 'Na Ismail, na Al Yasaa, na Yunus, na Lut\'. Na wote tuliwafadhilisha juu ya walimwengu wote.'),
(876, 6, 87, 'Na kutokana na baba zao, na vizazi vyao na ndugu zao. Na tukawateuwa na tukawaongoa kwenye Njia iliyo nyooka.'),
(877, 6, 88, 'Hiyo ni hidaya ya Mwenyezi Mungu. Kwa hiyo humhidi amtakaye katika waja wake. Na lau wangeli mshirikisha yangeli waharibikia waliyo kuwa wakiyatenda.'),
(878, 6, 89, 'Hao ndio tulio wapa Vitabu na hukumu na Unabii. Ikiwa hawa watayakataa hayo, basi tumekwisha yawakilisha kwa watu wasio yakataa.'),
(879, 6, 90, 'Hao ndio ambao Mwenyezi Mungu amewahidi. Basi fuata hidaya yao. Sema: Mimi sikuombeni ujira. Haya hayakuwa ila ni mawaidha kwa walimwengu wote.'),
(880, 6, 91, 'Na hawakumkadiria Mwenyezi Mungu kwa haki ya kadri yake, walipo sema: Mwenyezi Mungu hakumteremshia mwanaadamu chochote. Sema: Nani aliyo teremsha Kitabu alicho kuja nacho Musa, chenye nuru na uwongofu kwa watu, mlicho kifanya kurasa kurasa mkizionyesha, na mengi mkiyaficha. Na mkafunzwa mlio kuwa hamyajui nyinyi wala baba zenu? Sema: Mwenyezi Mungu. Kisha waache wacheze katika porojo lao.'),
(881, 6, 92, 'Na hiki ni Kitabu tulicho kiteremsha, kilicho barikiwa, chenye kuhakikisha yaliyo tangulia, na ili uuwonye Mama wa Miji na walio pembezoni mwake. Na wenye kuamini Akhera wanakiamini hichi, nao wanazihifadhi Sala zao.'),
(882, 6, 93, 'Nani dhaalimu mkubwa kuliko yule anaye mzuliya uwongo Mwenyezi Mungu, au anaye sema: Mimi nimeletewa wahyi; na hali hakuletewa wahyi wowote. Na yule anaye sema: Nitateremsha kama alivyo teremsha Mwenyezi Mungu. Na lau ungeli waona madhaalimu wanavyo kuwa katika mahangaiko ya mauti, na Malaika wamewanyooshea mikono wakiwambia: Zitoeni roho zenu! Leo mtalipwa adhabu ya fedheha kwa sababu ya mliyo kuwa mkiyasema juu ya Mwenyezi Mungu yasiyo ya haki, na mlivyo kuwa mkizifanyia kiburi Ishara zake.'),
(883, 6, 94, 'Nanyi mlitujia wapweke kama tulivyo kuumbeni mara ya kwanza. Na mkayaacha nyuma yenu yote tuliyo kupeni, na wala hatuwaoni hao waombezi wenu, ambao mlidai kuwa ni washirika wa Mwenyezi Mungu kwenu. Yamekatika makhusiano baina yenu, na yamekupoteeni mliyo kuwa mkidai.'),
(884, 6, 95, 'Hakika Mwenyezi Mungu ndiye mpasuaji mbegu na kokwa, zikachipua. Humtoa aliye hai kutoka maiti, naye ni mtoaji maiti kutokana na aliye hai. Huyo ndiye Mwenyezi Mungu. Basi vipi mnageuzwa?'),
(885, 6, 96, 'Ndiye anaye pambazua mwangaza wa asubuhi; na ameufanya usiku kwa mapumziko na utulivu, na jua na mwezi kwenda kwa hisabu. Hayo ndiyo makadirio ya Aliye tukuka Mwenye nguvu, Mwenye ujuzi.'),
(886, 6, 97, 'Na Yeye ndiye aliye kuwekeeni nyota ili mwongoke kwazo katika kiza cha bara na bahari. Hakika tumezichambua Ishara hizi kwa watu wanao jua.'),
(887, 6, 98, 'Na Yeye ndiye aliye kuzalisheni kutokana na nafsi moja. Pako pahali pa kutulia na pa kupita njia. Hakika tumezichambua Ishara hizi kwa watu wanao fahamu.'),
(888, 6, 99, 'Na Yeye ndiye anaye teremsha maji kutoka mbinguni; na kwayo tunatoa mimea ya kila kitu. Kutokana na baadhi yao tukatoa mimea ya majani, tukatoa ndani yake punje zilizo pandana; na kutokana na mitende yakatoka kwenye makole yake mashada yaliyo inama; na bustani za mizabibu na mizaituni, na makomamanga, yaliyo fanana na yasiyo fanana. Angalieni matunda yake yanapo zaa na yakawiva. Hakika katika hayo pana Ishara kwa watu wanao amini.'),
(889, 6, 100, 'Bado wamemfanyia Mwenyezi Mungu majini kuwa washirika wake, na hali Yeye ndiye aliye waumba. Na wakamzulia, bila ya ujuzi wowote, kuwa ana wana wa kiume na wa kike. Subhanahu, Ametakasika na ametukuka juu ya hayo wanayo mbandikiza nayo!'),
(890, 6, 101, 'Yeye ndiye Muumbaji wa mbingu na ardhi bila ya ruwaza. Itakuwaje awe na mwana naye hakuwa na mke? Naye ndiye aliye umba kila kitu. Naye ni Mwenye kujua kila kitu.'),
(891, 6, 102, 'Huyo ndiye Allah, Mwenyezi Mungu, Mola Mlezi wenu, hapana mungu ila Yeye, Muumba wa kila kitu. Basi muabuduni Yeye tu. Naye ni Mtegemewa wa kila kitu.'),
(892, 6, 103, 'Macho hayamfikilii bali Yeye anayafikilia macho. Naye ni Mjuzi, Mwenye khabari.'),
(893, 6, 104, 'Zimekwisha kukujieni hoja wazi kutoka kwa Mola wenu Mlezi. Basi anaye ona ni kwa faida yake mwenyewe, na anaye pofuka basi ni khasara yake. Nami si mtunzaji wenu.'),
(894, 6, 105, 'Na namna hivyo tunazisarifu Aya, na wao wakwambie: Umesoma. Na ili tuyabainishe kwa watu wanao jua.'),
(895, 6, 106, 'Fuata uliyo funuliwa kutoka kwa Mola wako Mlezi. Hapana mungu ila Yeye. Na jitenge na washirikina.'),
(896, 6, 107, 'Na lau Mwenyezi Mungu angeli penda, wasingeli shiriki. Na Sisi hatukukufanya wewe uwe mtunzaji wao. Wala wewe si mlinzi juu yao.'),
(897, 6, 108, 'Wala msiwatukane hao wanao waomba badala ya Mwenyezi Mungu, wasije na wao wakamtukana Mwenyezi Mungu kwa jeuri bila ya kujua. Namna hivyo tumewapambia kila umma vitendo vyao. Kisha marejeo yao yatakuwa kwa Mola wao Mlezi, naye atawaambia waliyo kuwa wakiyatenda.'),
(898, 6, 109, 'Na waliapa kwa Mwenyezi Mungu kwa ukomo wa viapo vyao, kuwa ikiwafikia Ishara wataiamini. Sema: Ishara ziko kwa Mwenyezi Mungu. Na nyinyi hamjui kuwa zitapo kuja hawato amini.'),
(899, 6, 110, 'Nasi twazigeuza nyoyo zao na macho yao. Kama walivyo kuwa hawakuamini mara ya kwanza, tunawaacha katika maasi yao wakitangatanga.'),
(900, 6, 111, 'NA LAU kuwa tungeli wateremshia Malaika, na maiti wakazungumza nao, na tukawakusanyia kila kitu mbele yao, bado wasingeli amini, ila Mwenyezi Mungu atake. Lakini wengi wao wamo ujingani.'),
(901, 6, 112, 'Na kadhaalika tumemfanyia kila Nabii maadui mashet\'ani wa kiwatu, na kijini, wakifundishana wao kwa wao maneno ya kupamba-pamba, kwa udanganyifu. Na angeli penda Mola wako Mlezi wasingeli fanya hayo. Basi waache na wanayo yazua.'),
(902, 6, 113, 'Na ili nyoyo za wasio amini Akhera zielekee hayo, nao wayaridhie na wayachume wanayo yachuma.'),
(903, 6, 114, 'Je, nimtafute hakimu ghairi ya Mwenyezi Mungu, naye ndiye aliye kuteremshieni Kitabu kilicho elezwa waziwazi? Na hao tulio wapa Kitabu wanajua ya kwamba kimeteremshwa na Mola wako Mlezi kwa Haki. Basi usiwe katika wanao tia shaka.'),
(904, 6, 115, 'Na yametimia maneno ya Mola Mlezi wako kwa kweli na uadilifu. Hapana awezaye kuyabadilisha maneno yake. Na Yeye ndiye Mwenye kusikia na Mwenye kujua.'),
(905, 6, 116, 'Na ukiwat\'ii wengi katika hawa waliomo duniani watakupoteza na Njia ya Mwenyezi Mungu. Hawa hawafuati ila dhana tu, na hawakuwa ila ni wenye kusema uwongo tu.'),
(906, 6, 117, 'Hakika Mola wako Mlezi ndiye Mjuzi kushinda wote kuwajua walio potea Njia yake, na ndiye Mjuzi kushinda wote kuwajua walio hidika.'),
(907, 6, 118, 'Basi kuleni katika walio somewa jina la Mwenyezi Mungu, ikiwa mnaziamini Aya zake.'),
(908, 6, 119, 'Na kwa nini msile katika walio somewa jina la Mwenyezi Mungu, naye amekwisha kubainishieni alivyo kuharimishieni, isipo kuwa vile mnavyo lazimishwa? Na hakika wengi wanapoteza kwa matamanio yao bila ya kuwa na ilimu. Hakika Mola wako Mlezi ndiye Mjuzi kushinda wote kuwajua wanao pindukia mipaka.'),
(909, 6, 120, 'Na acheni dhambi zilizo dhaahiri na zilizo fichikana. Hakika wanao chuma dhambi watalipwa kwa waliyo kuwa wakiyachuma.'),
(910, 6, 121, 'Wala msile katika wale wasio somewa jina la Mwenyezi Mungu. Kwani huo ni upotofu. Na kwa yakini mashet\'ani wanawafunza marafiki zao kubishana nanyi. Na mkiwafuata basi hapana shaka nyinyi mtakuwa washirikina.'),
(911, 6, 122, 'Je, aliye kuwa maiti kisha tukamhuisha, na tukamjaalia nuru inakwenda naye mbele za watu, mfano wake ni kama aliyoko gizani akawa hata hawezi kutoka humo? Kama hivyo makafiri wamepambiwa waliyo kuwa wakiyafanya.'),
(912, 6, 123, 'Kadhaalika katika kila mji tumewajaalia wakubwa wa wakosefu wao wafanye vitimbi ndani yake. Na wala hawafanyii vitimbi isipo kuwa nafsi zao, nao hawatambui.'),
(913, 6, 124, 'Na inapo wajia Ishara, wao husema: Hatutoamini mpaka tupewe mfano wa walio pewa Mitume wa Mwenyezi Mungu. Mwenyezi Mungu ndiye Mjuzi kuliko wote kujua wapi anaweka ujumbe wake. Itawafikia hao walio kosa udhalili na adhabu kali kutoka kwa Mwenyezi Mungu kwa sababu ya vitimbi walivyo kuwa wakivifanya.'),
(914, 6, 125, 'Basi yule ambaye Mwenyezi Mungu anataka kumhidi humfungulia kifua chake kwa Uislamu. Na anaye taka apotee hukifanya kifua chake kina dhiki, kimebana, kama kwamba anapanda mbinguni. Namna hivi Mwenyezi Mungu anajaalia uchafu juu ya wasio amini.'),
(915, 6, 126, 'Na hii ndiyo Njia ya Mola wako Mlezi Iliyo nyooka. Tumezipambanua Aya kwa watu wanao kumbuka.'),
(916, 6, 127, 'Hao watapata nyumba ya salama kwa Mola Mlezi wao. Naye ndiye Rafiki Mlinzi wao kwa sababu ya waliyo kuwa wakiyatenda.'),
(917, 6, 128, 'Na ile Siku atapo wakusanya wote awaambie: Enyi makundi ya majini! Hakika nyinyi mmechukua wafuasi wengi katika wanaadamu. Na marafiki zao katika wanaadamu waseme: Mola Mlezi wetu! Tulinufaishana sisi na wao. Na tumefikia ukomo wetu ulio tuwekea. Basi Mwenyezi Mungu atasema: Moto ndio makaazi yenu, mtadumu humo, ila apendavyo Mwenyezi Mungu. Hakika Mola wako Mlezi ni Mwenye hikima, na Mwenye kujua.'),
(918, 6, 129, 'Ndio kama hivi tunawaelekeza madhaalimu wapendane wao kwa wao kwa sababu ya waliyo kuwa wakiyachuma.'),
(919, 6, 130, 'Enyi makundi ya majini na watu! Je, hawakukujieni Mitume kutokana na nyinyi wenyewe wakikubainishieni Aya zangu, na wakikuonyeni mkutano wa Siku yenu hii. Nao watasema: Tumeshuhudia juu ya nafsi zetu. Na yaliwadanganya maisha ya dunia. Nao watajishuhudia wenyewe ya kwamba walikuwa makafiri.'),
(920, 6, 131, 'Hayo ni kwa sababu ya kuwa Mola wako Mlezi hakuwa wa kuiangamiza miji kwa dhulma, hali wenyewe wameghafilika.'),
(921, 6, 132, 'Na wote wana daraja mbali mbali kutokana na yale waliyo yatenda. Na Mola wako Mlezi si mwenye kughafilika na yale wanayo yatenda.'),
(922, 6, 133, 'Na Mola wako Mlezi ndiye Mkwasi, Mwenye rehema. Akipenda atakuondoeni na awaweke wengine awapendao badala yenu, kama vile alivyo kutoeni kutokana na uzazi wa watu wengine.'),
(923, 6, 134, 'Bila ya shaka hayo mnayo ahidiwa yatafika tu, wala nyinyi hamtaweza kuyaepuka.'),
(924, 6, 135, 'Sema: Enyi watu wangu! Fanyeni muwezayo, na hakika mimi nafanya. Mtakuja jua nani atakuwa na makaazi mema mwishoni. Hakika madhaalimu hawatafanikiwa.'),
(925, 6, 136, 'Na wamemwekea sehemu Mwenyezi Mungu katika mimea na wanyama alio umba, nao husema: Hii ni sehemu ya Mwenyezi Mungu - kwa madai yao - na hii ni ya miungu tunao washirikisha. Basi vile walivyo wakusudia miungu yao havimfikii Mwenyezi Mungu, na vilivyo kuwa vya Mwenyezi Mungu huwafikia miungu yao. Ni uovu kabisa hayo wanayo yahukumu.'),
(926, 6, 137, 'Na kadhaalika hao washirika wao wamewapambia wengi katika washirikina kuwauwa watoto wao ili kuwaangamiza na kuwavurugia dini yao. Na lau kuwa Mwenyezi Mungu angeli penda wasinge fanya hayo. Basi waache na hayo wanayo yazua.'),
(927, 6, 138, 'Nao husema: Wanyama hawa na mimea hii ni mwiko. Hawatokula ila wale tuwapendao - kwa madai yao tu. Na wanyama hawa imeharimishwa migongo yao kupandwa. Na wanyama wengine hawalitaji jina la Mwenyezi Mungu juu yao. Wanamzulia uwongo tu Mwenyezi Mungu. Atawalipa kwa hayo wanayo mzulia.'),
(928, 6, 139, 'Na husema: Waliomo matumboni mwa wanyama hawa ni kwa ajili ya wanaume wetu tu, na wameharimishwa kwa wake zetu. Lakini ikiwa nyamafu basi wanashirikiana. Mwenyezi Mungu atawalipa kwa maelezo yao hayo. Hakika Yeye ni Mwenye hikima, Mwenye kujua.'),
(929, 6, 140, 'Hakika wamekhasirika wale ambao wamewauwa watoto wao kwa upumbavu pasipo kujua, na wakaharimisha alivyo waruzuku Mwenyezi Mungu kwa kumzulia Mwenyezi Mungu. Hakika wamepotea, wala hawakuwa wenye kuongoka.'),
(930, 6, 141, 'Na Yeye ndiye aliye ziumba bustani zenye kutambaa juu ya chanja, na zisio tambaa, na mitende, na mimea yenye matunda mbali mbali, na mizaituni na mikomamanga inayo fanana na isiyo fanana. Kuleni matunda yake inapo zaa, na toeni haki yake siku ya kuvunwa kwake. Wala msitumie kwa fujo. Hakika Yeye hawapendi watumiayo kwa fujo.'),
(931, 6, 142, 'Na katika wanyama amekujaalieni wenye kubeba mizigo na kutoa matandiko. Kuleni katika alivyo kuruzukuni Mwenyezi Mungu, wala msifuate nyayo za Shet\'ani. Hakika yeye ni adui yenu dhaahiri.'),
(932, 6, 143, 'Amekuumbieni namna nane za wanyama: Wawili katika kondoo, na wawili katika mbuzi. Sema je, ameharimisha yote madume wawili au majike wawili, au waliomo matumboni mwa majike yote mawili? Niambieni kwa ilimu ikiwa nyinyi mnasema kweli.'),
(933, 6, 144, 'Na wawili katika ngamia, na wawili katika ng\'ombe. Sema: Je, ameharimisha yote madume wawili au majike wawili, au waliomo matumboni mwa yote majike? Au, nyinyi mlikuwapo Mwenyezi Mungu alipo kuusieni haya? Basi ni nani dhaalimu mkubwa kuliko yule aliye mzulia Mwenyezi Mungu uwongo, kwa ajili ya kuwapoteza watu bila ya ilimu? Hakika Mwenyezi Mungu hawahidi watu madhaalimu.'),
(934, 6, 145, 'Sema: Sioni katika yale niliyo funuliwa mimi kitu kilicho harimishwa kwa mlaji kukila isipo kuwa kiwe ni nyamafu, au damu inayo mwagika, au nyama ya nguruwe, kwani hiyo ni uchafu; au kwa upotofu kimechinjwa kwa jina la asiye kuwa Mwenyezi Mungu. Lakini mwenye kushikwa na dharura, bila ya kutamani wala kupita mipaka, basi hakika Mola wako Mlezi ni Mwenye kusamehe, Mwenye kurehemu.'),
(935, 6, 146, 'Na wale walio fuata dini ya Kiyahudi tumewaharimishia kila mwenye kucha. Na katika ng\'ombe na kondoo na mbuzi tumewaharimishia shahamu yao, isipo kuwa ile iliyo beba migongo yao au matumbo yao, au iliyo gandamana na mifupa. Tuliwalipa hivyo kwa sababu ya uasi wao. Na bila ya shaka Sisi ndio wasema kweli.'),
(936, 6, 147, 'Wakikukanusha basi wewe sema: Mola Mlezi wenu ni Mwenye rehema iliyo enea. Wala haizuiliki adhabu yake kwa watu wakhalifu.'),
(937, 6, 148, 'Watasema walio shirikisha: Lau kuwa Mwenyezi Mungu ange taka tusingeli shiriki sisi, wala baba zetu; wala tusingeli harimisha kitu chochote. Vivi hivi walikanusha walio kuwa kabla yao mpaka walipo onja adhabu yetu. Sema: Je, nyinyi mnayo ilimu mtutolee? Nyinyi hamfuati ila dhana. Wala hamsemi ila uwongo tu.'),
(938, 6, 149, 'Sema: Basi Mwenyezi Mungu ndiye Mwenye hoja ya kukata. Na kama angeli penda angeli kuhidini nyote.'),
(939, 6, 150, 'Sema: Leteni mashahidi wenu watao shuhudia kuwa Mwenyezi Mungu ameharimisha hawa. Basi wakishuhudia, wewe usishuhudie pamoja nao. Wala usifuate matamanio ya walio zikanusha Ishara zetu, wala hawaiamini Akhera, na ambao wanamlinganisha Mola Mlezi wao na wengine.'),
(940, 6, 151, 'Sema: Njooni nikusomeeni aliyo kuharimishieni Mola wenu Mlezi. Nayo ni kuwa, msimshirikishe Yeye na chochote. Na wazazi wenu wafanyieni wema. Wala msiwauwe watoto wenu kwa sababu ya umasikini. Sisi tunakuruzukuni nyinyi na wao. Wala msikaribie mambo machafu, yanayo onekana, na yanayo fichikana. Wala msiuwe nafsi ambayo Mwenyezi Mungu ameharimisha kuiuwa, ila ikiwa kwa haki. Hayo amekuusieni ili myatie akilini.'),
(941, 6, 152, 'Wala msiyakaribie mali ya yatima ila kwa njia ya wema kabisa, mpaka afikilie utu-uzima. Na timizeni vipimo na mizani kwa uadilifu. Sisi hatumkalifishi mtu ila kwa kadiri ya uwezo wake. Na msemapo semeni kwa uadilifu ingawa ni jamaa. Na ahadi ya Mwenyezi Mungu itekelezeni. Hayo amekuusieni ili mpate kukumbuka.'),
(942, 6, 153, 'Na kwa hakika hii ndiyo Njia yangu Iliyo Nyooka. Basi ifuateni, wala msifuate njia nyingine, zikakutengeni na Njia yake. Haya amekuusieni ili mpate kuchamngu.'),
(943, 6, 154, 'Tena tulimpa Musa Kitabu kwa kumtimizia (neema) aliye fanya wema, na kuwa ni maelezo ya kila kitu, na uwongofu na rehema, ili wapate kuamini mkutano wao na Mola wao Mlezi.'),
(944, 6, 155, 'Na hichi ni Kitabu tulicho kiteremsha, kilicho barikiwa. Basi kifuateni, na mchenimngu, ili mrehemewe.'),
(945, 6, 156, 'Msije mkasema: Hakika mataifa mawili kabla yetu yameteremshiwa Kitabu; na sisi tulikuwa hatuna khabari ya waliyo kuwa wakiyasoma.'),
(946, 6, 157, 'Au mkasema: Lau kuwa tumeteremshiwa sisi Kitabu tungeli kuwa waongofu zaidi kuliko wao. Basi imekufikieni bayana kutoka kwa Mola wenu Mlezi, na uwongofu, na rehema. Basi nani aliye dhaalimu mkubwa zaidi kuliko yule anaye kanusha Ishara za Mwenyezi Mungu, na akajitenga nazo? Tutawalipa wanao jitenga na Ishara zetu adhabu kali kabisa kwa sababu ya kujitenga kwao.'),
(947, 6, 158, 'Wanangoja jengine ila wawafikie Malaika, au awafikie Mola wako Mlezi, au zifike baadhi ya Ishara za Mola wako Mlezi? Siku zitakapo fika baadhi ya Ishara za Mola wako Mlezi, kuamini hapo hakutomfaa mtu kitu, ikiwa hakuwa ameamini kabla yake, au amefanya kheri katika Imani yake. Sema: Ngojeni, nasi pia tunangoja.'),
(948, 6, 159, 'Hakika walio igawa Dini yao na wakawa makundi makundi, huna ukhusiano nao wowote. Bila ya shaka shauri yao iko kwa Mwenyezi Mungu; kisha atawaambia yale waliyo kuwa wakiyatenda.'),
(949, 6, 160, 'Afanyae wema atalipwa mfano wake mara kumi. na afanyae ubaya hatalipwa ila sawa nao tu. Na wao hawatadhulumiwa.'),
(950, 6, 161, 'Sema: Kwa hakika mimi Mola wangu Mlezi ameniongoa kwenye Njia Iliyo Nyooka, Dini iliyo sawa kabisa, ambayo ndiyo mila ya Ibrahim aliye kuwa mwongofu, wala hakuwa miongoni mwa washirikina.'),
(951, 6, 162, 'Sema: Hakika Sala yangu, na ibada zangu, na uhai wangu, na kufa kwangu, ni kwa ajili ya Mwenyezi Mungu, Mola Mlezi wa viumbe vyote.'),
(952, 6, 163, 'Hana mshirika wake. Na hayo ndiyo niliyo amrishwa, na mimi ni wa kwanza wa Waislamu.'),
(953, 6, 164, 'Sema: Je nitafute Mola Mlezi asiye kuwa Mwenyezi Mungu, na hali Yeye ndiye Mola Mlezi wa kila kitu? Na kila nafsi haichumii ila nafsi yake. Na habebi mwenye kubeba mzigo wa mwenziwe. Kisha marejeo yenu ni kwa Mola wenu Mlezi, naye atakuambieni mliyo kuwa mkikhitalifiana.'),
(954, 6, 165, 'Naye ndiye aliye kufanyeni makhalifa wa duniani, na amewanyanyua baadhi yenu juu ya wengine daraja kubwa kubwa, ili akujaribuni kwa hayo aliyo kupeni. Hakika Mola Mlezi wako ni Mwepesi wa kuadhibu, na hakika Yeye ni Mwenye kusamehe, Mwenye kurehemu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'ALIF LAM MYM \'SAAD'),
(956, 7, 2, 'Kitabu kilicho teremshwa kwako - basi isiwe dhiki kifuani kwako kwa ajili yake, upate kuonya kwacho, na kiwe ni ukumbusho kwa Waumini.'),
(957, 7, 3, 'Fuateni mliyo teremshiwa kutoka kwa Mola Mlezi wenu, wala msifuate rafiki au walinzi wengine badala yake. Ni machache mnayo yakumbuka.'),
(958, 7, 4, 'Na miji mingapi tuliiangamiza, ikaifikia adhabu yetu usiku au walipo kuwa wamelala adhuhuri.'),
(959, 7, 5, 'Basi hakikuwa kilio chao ilipo wafikia adhabu yetu, isipo kuwa kusema: Hakika sisi tulikuwa wenye kudhulumu.'),
(960, 7, 6, 'Na kwa yakini tutawauliza wale walio pelekewa Ujumbe, na pia tutawauliza hao Wajumbe.'),
(961, 7, 7, 'Tena tutawasimulia kwa ilimu; wala Sisi hatukuwa mbali.'),
(962, 7, 8, 'Na Siku hiyo kipimo kitakuwa kwa Haki. Basi watakao kuwa na uzani mzito, hao ndio watakao fanikiwa.'),
(963, 7, 9, 'Na watakao kuwa na uzani khafifu, basi hao ndio walio zitia khasarani nafsi zao kwa sababu ya kuzipinga Ishara zetu.'),
(964, 7, 10, 'Na hakika tumekuwekeni katika ardhi, na tumekujaalieni humo njia za kupatia maisha. Ni kuchache kushukuru kwenu.'),
(965, 7, 11, 'Na hakika tulikuumbeni, kisha tukakutieni sura, kisha tukawaambia Malaika: Msujudieni Adam. Basi wakasujudu isipo kuwa Iblisi, hakuwa miongoni mwa walio sujudu.'),
(966, 7, 12, 'Mwenyezi Mungu akasema: Nini kilicho kuzuia kumsujudia nilipo kuamrisha? Akasema: Mimi ni bora kuliko yeye. Umeniumba kwa moto, naye umemuumba kwa udongo.'),
(967, 7, 13, 'Akasema: Basi teremka kutoka humo! Haikufalii kufanya kiburi humo. Basi toka! Hakika wewe u miongoni mwa walio duni.'),
(968, 7, 14, 'Akasema: Nipe muhula mpaka siku watakapo fufuliwa.'),
(969, 7, 15, 'Akasema: Utakuwa miongoni mwa walio pewa muhula.'),
(970, 7, 16, 'Akasema: Kwa kuwa umenihukumia upotofu, basi nitawavizia katika Njia yako Iliyo Nyooka.'),
(971, 7, 17, 'Kisha nitawazukia mbele yao na nyuma yao na kuliani kwao na kushotoni kwao. Wala hutawakuta wengi wao wenye shukrani.'),
(972, 7, 18, 'Akasema: Toka humo, nawe umekwisha fedheheka, umekwisha fukuzwa. Hapana shaka atakaye kufuata miongoni mwao, basi nitaijaza Jahannam kwa nyinyi nyote.'),
(973, 7, 19, 'Na wewe, Adam! Kaa wewe na mkeo katika Bustani hii, na kuleni humo mpendapo. Wala msiukaribie mti huu, mkawa katika wale walio dhulumu.'),
(974, 7, 20, 'Basi Shet\'ani aliwatia wasiwasi ili kuwafichulia tupu zao walizo fichiwa, na akasema: Mola Mlezi wenu hakukukatazeni mti huu ila msije mkawa Malaika, au msije mkawa katika wanao ishi milele.'),
(975, 7, 21, 'Naye akawaapia: Kwa yakini mimi ni miongoni wa wanao kunasihini.'),
(976, 7, 22, 'Basi akawateka kwa khadaa. Walipo uonja ule mti, tupu zao zilifichuka na wakaingia kujibandika majani ya Bustanini. Mola wao Mlezi akawaita: Je, sikukukatazeni mti huo, na kukwambieni ya kwamba Shet\'ani ni adui yenu wa dhaahiri?'),
(977, 7, 23, 'Wakasema: Mola wetu Mlezi! tumejidhulumu nafsi zetu, na kama hutusamehe na kuturehemu, hakika tutakuwa katika wenye kukhasirika.'),
(978, 7, 24, 'Akasema: Teremkeni! Mtakuwa nyinyi kwa nyinyi maadui. Na kwenye ardhi itakuwa ndio makao yenu na starehe yenu mpaka ufike muda.'),
(979, 7, 25, 'Akasema: Humo mtaishi, na humo mtakufa, na kutoka humo mtatolewa.'),
(980, 7, 26, 'Enyi wanaadamu! Tumekuteremshieni nguo za kuficha tupu zenu, na nguo za pambo. Na nguo za uchamngu ndio bora. Hayo ni katika Ishara za Mwenyezi Mungu mpate kukumbuka.'),
(981, 7, 27, 'Eny wanaadamu! Asije Shet\'ani kukufitinini kama alivyo watoa wazazi wenu Peponi, akawavua nguo zao kuwaonyesha tupu zao. Hakika yeye na kabila yake wanakuoneni, na nyinyi hamuwaoni. Hakika Sisi tumewafanya Mashet\'ani kuwa ni marafiki wa wasio amini.'),
(982, 7, 28, 'Na wanapo fanya mambo machafu husema: Tumewakuta nayo baba zetu, na Mwenyezi Mungu ametuamrisha hayo. Sema: Hakika Mwenyezi Mungu haamrishi mambo machafu. Mnamzulia Mwenyezi Mungu msiyo yajua?'),
(983, 7, 29, 'Sema: Mola Mlezi wangu ameamrisha uadilifu, na mumuelekeze nyuso zenu kila mnapo sujudu, na muombeni Yeye kwa kumsafia Dini. Kama alivyo kuumbeni mwanzo ndivyo mtavyo rudi,'),
(984, 7, 30, 'Hali ya kuwa kundi moja amelihidi, na kundi jengine limethibitikiwa na upotofu. Kwa hakika hao waliwafanya mashet\'ani kuwa ndio marafiki walinzi badala ya Mwenyezi Mungu, na wanadhani kuwa wao wameongoka.'),
(985, 7, 31, 'Enyi wanaadamu! Chukueni pambo lenu kwenye kila pahala wakati wa ibada, na kuleni, na kunyweni na wala msifanye ubadhirifu. Kwa hakika Yeye hapendi wanao fanya israfu.'),
(986, 7, 32, 'Sema: Ni nani aliye harimisha pambo la Mwenyezi Mungu alilo watolea waja wake, na vilivyo vizuri katika riziki. Sema hivyo ni kwa walio amini katika uhai wa duniani, na Siku ya Kiyama vitakuwa vyao wao tu. Namna hivi tunazieleza Ishara kwa watu wanao jua.'),
(987, 7, 33, 'Sema: Mola Mlezi wangu ameharimisha mambo machafu ya dhaahiri na ya siri, na dhambi, na uasi bila ya haki, na kumshirikisha Mwenyezi Mungu na asicho kiletea uthibitisho, na kumzulia Mwenyezi Mungu msiyo yajua.'),
(988, 7, 34, 'Na kila umma una muda wake. Utakapo fika muda wao basi hawatakawia hata saa moja, wala hawatatangulia.'),
(989, 7, 35, 'Enyi wanaadamu! Watakapo kufikieni Mitume miongoni mwenu wakakuelezeni Ishara zangu, basi watakao mchamngu na wakatengenea haitakuwa khofu kwao, wala hawatahuzunika.'),
(990, 7, 36, 'Na wale watakao kanusha Ishara zetu na wakazifanyia kiburi, hao ni watu wa Motoni; humo watadumu.'),
(991, 7, 37, 'Basi ni nani dhaalimu mkubwa kuliko yule anaye mzulia Mwenyezi Mungu uwongo, au anaye zikanusha Ishara zake? Hao itawafikia sehemu yao waliyo andikiwa, mpaka watakapo wafikia wajumbe wetu kuwafisha, watawaambia: Wako wapi mlio kuwa mkiwaomba badala ya Mwenyezi Mungu? Watasema: Wametupotea! Na watajishuhudia wenyewe kwamba walikuwa makafiri.'),
(992, 7, 38, 'Mwenyezi Mungu atasema: Ingieni Motoni pamoja na umma zilizo pita kabla yenu za majini na watu. Kila utakapo ingia umma utawalaani wenzake. Mpaka watakapo kusanyika wote humo, wa mwisho wao watawasema wa mwanzo wao: Mola wetu Mlezi! Hawa ndio walio tupoteza; basi wape adhabu ya Motoni marudufu. Atasema: Itakuwa kwenu nyote marudufu, lakini nyinyi hamjui tu.'),
(993, 7, 39, 'Na wa mwanzo wao watawaambia wa mwisho wao: Basi nyinyi pia hamkuwa na ubora kuliko sisi. Basi onjeni adhabu kwa sababu ya mliyo kuwa mkiyachuma.'),
(994, 7, 40, 'Hakika wale wanao zikanusha Ishara zetu na wakazifanyia kiburi hawatafunguliwa milango ya mbingu, wala hawataingia Peponi mpaka apite ngamia katika tundu ya sindano. Na hivi ndivyo tunavyo walipa wakosefu.'),
(995, 7, 41, 'Jahannamu itakuwa kitanda chao na juu yao nguo za moto za kujifunika. Na hivi ndivyo tunavyo walipa madhaalimu.'),
(996, 7, 42, 'Na wale wanao amini na wakatenda mema - hatumkalifishi mtu yoyote ila kwa kiasi cha uwezo wake - hao ndio watu wa Peponi. Wao watadumu humo.'),
(997, 7, 43, 'Na tutaondoa chuki vifuani mwao, na mbele yao iwe inapita mito. Na watasema: Alhamdulillah! Kuhimidiwa ni kwa Mwenyezi Mungu, ambaye aliye tuhidi kufikia haya. Wala hatukuwa wenye kuhidika wenyewe ingeli kuwa Mwenyezi Mungu hakutuhidi. Hakika Mitume wa Mola wetu Mlezi walileta Haki. Na watanadiwa kwamba: Hiyo ndiyo Pepo mliyo rithishwa kwa sababu ya mliyo kuwa mkiyafanya.'),
(998, 7, 44, 'Na watu wa Peponi watawanadia watu wa Motoni: Sisi tumekuta aliyo tuahidi Mola wetu Mlezi kuwa ni kweli. Je, na nyinyi pia mmekuta aliyo kuahidini Mola wenu Mlezi kuwa ni kweli? Watasema: Ndiyo! Basi mtangazaji atawatangazia: Laana ya Mwenyezi Mungu iwapate walio dhulumu,'),
(999, 7, 45, 'Wale ambao walikuwa wakizuilia Njia ya Mwenyezi Mungu, na wakataka kuipotosha, nao wanaikanusha Akhera.'),
(1000, 7, 46, 'Na baina ya makundi mawili hayo patakuwapo pazia. Na juu ya Mnyanyuko patakuwa watu watakao wajua wote kwa alama zao, na wao watawaita watu wa Peponi: Assalamu Alaykum, Amani juu yenu! Wao bado hawajaingia humo, lakini wanatumai.'),
(1001, 7, 47, 'Na yanapo geuzwa macho yao kuelekea watu wa Motoni, watasema: Mola Mlezi wetu! Usitujaalie kuwa pamoja na watu walio dhulumu.'),
(1002, 7, 48, 'Na hao watu wa Mnyanyukoni watawaita watu wanao wajua kwa alama zao. Watasema: Kujumuika kwenu hakukufaini kitu, wala hicho mlicho kuwa mnafanyia kiburi.'),
(1003, 7, 49, 'Je, hawa sio wale mlio kuwa mkiwaapia kuwa Mwenyezi Mungu hatawafikishia rehema. Ingieni Peponi, hapana khofu kwenu, wala hamtahuzunika!'),
(1004, 7, 50, 'Na watu wa Motoni watawaita watu wa Peponi: Tumimieni maji, au chochote alicho kuruzukuni Mwenyezi Mungu. Nao watasema: Hakika Mwenyezi Mungu ameviharimisha hivyo kwa makafiri,'),
(1005, 7, 51, 'Ambao waliifanya dini yao kuwa ni pumbao na mchezo, na maisha ya dunia yakawadanganya. Basi leo Sisi tunawasahau kama walivyo usahau mkutano wa Siku yao hii, na kwa sababu ya kule kuzikataa Ishara zetu.'),
(1006, 7, 52, 'Na hakika tumewaletea Kitabu tulicho kipambanua kwa ilimu, uwongofu na rehema kwa watu wenye kuamini.'),
(1007, 7, 53, 'Je, lipo wanalo lingojea isipo kuwa matokeo yake? Siku yatapo fika matokeo yake watasema wale walio kisahau: Kweli Mitume wa Mola Mlezi wetu walileta Haki! Je, tunao waombezi ili watuombee? Au turudishwe ili tufanye yasiyo kuwa yale tuliyo kuwa tukiyafanya? Bila ya shaka wamejikhasiri nafsi zao, na yamewapotea waliyo kuwa wakiyazua.'),
(1008, 7, 54, 'Hakika Mola Mlezi wenu ni Mwenyezi Mungu aliye ziumba mbingu na ardhi katika siku sita. Kisha akatawala juu ya Kiti cha Enzi. Huufunika usiku kwa mchana, ufuatao upesi upesi. Na jua, na mwezi, na nyota zinazo tumika kwa amri yake. Fahamuni! Kuumba na amri ni zake. Ametukuka kabisa Mwenyezi Mungu, Mola Mlezi wa viumbe vyote.'),
(1009, 7, 55, 'Muombeni Mola wenu Mlezi kwa unyenyekevu na kwa siri. Hakika Yeye hawapendi warukao mipaka.'),
(1010, 7, 56, 'Wala msifanye uharibifu katika nchi baada ya kuwa imekwisha tengenezwa. Na muombeni kwa kuogopa na kwa kutumai. Hakika rehema ya Mwenyzi Mungu iko karibu na wanao fanya mema.'),
(1011, 7, 57, 'Na Yeye ndiye anaye peleka pepo (za kuvuma) kuwa ni bishara kabla ya kufika rehema yake. Hata hizo pepo zinapo beba mawingu mazito tunayachunga mpaka kwenye nchi iliyo kufa. Kisha tunateremsha kwayo maji, ndio kama hivyo tukatolesha kila aina ya matunda. Basi hali kadhaalika tunavyo wafufua wafu, ili mpate kukumbuka.'),
(1012, 7, 58, 'Na ardhi njema, yenye udongo wa rutba, hutoa mimea yenye kukua vizuri yenye uhai, kwa idhini ya Mola Mlezi wake. Na ardhi mbaya haitoi ila mimea michache, isiyo na faida, tena kwa shida. Na hivyo ndivyo tunavyo zipambanua Ishara (Aya) kwa watu wanao shukuru.'),
(1013, 7, 59, 'Tulimpeleka Nuhu kwa kaumu yake, naye akasema: Enyi kaumu yangu! Muabuduni Mwenyezi Mungu. Nyinyi hamna Mungu ila Yeye. Hakika mimi ninakukhofieni adhabu ya Siku iliyo kuu.'),
(1014, 7, 60, 'Wale watukufu katika kaumu yake wakasema: Hakika sisi tunakuona wewe umo katika upotofu ulio dhaahiri.'),
(1015, 7, 61, 'Akasema: Enyi watu wangu! Mimi simo katika upotofu, lakini mimi ni Mtume nitokaye kwa Mola Mlezi wa viumbe vyote.'),
(1016, 7, 62, 'Nakufikishieni Ujumbe wa Mola Mlezi wangu, na ninakunasihini; na ninayajua kwa Mwenyezi Mungu msiyo yajua nyinyi.'),
(1017, 7, 63, 'Je, mnastaajabu kukujieni mawaidha yanayotoka kwa Mola Mlezi wenu kwa njia ya mtu aliye ni mmoja katika nyinyi, ili akuonyeni na ili mumchemngu, na ili mpate kurehemewa?'),
(1018, 7, 64, 'Basi walimkanusha. Nasi tukamwokoa yeye na walio kuwa pamoja naye katika jahazi. Na tukawazamisha wale walio kanusha Ishara zetu. Hakika hao walikuwa watu vipofu.'),
(1019, 7, 65, 'Na kwa A\'adi tulimpeleka ndugu yao, Hud. Akasema: Enyi watu wangu! Muabuduni Mwenyezi Mungu. Nyinyi hamna mungu ila Yeye. Basi je, hamumchi?'),
(1020, 7, 66, 'Wakasema watukufu wa wale walio kufuru katika kaumu yake: Sisi tunakuona umo katika upumbavu, na hakika sisi tunakufikiria kuwa wewe ni katika waongo.'),
(1021, 7, 67, 'Akasema: Enyi watu wangu! Mimi sina upumbavu. Lakini mimi ni Mtume niliye toka kwa Mola Mlezi wa viumbe vyote.'),
(1022, 7, 68, 'Nakufikishieni ujumbe wa Mola wangu Mlezi. Na mimi kwenu ni mwenye kukunasihini, muaminifu.'),
(1023, 7, 69, 'Mnaona ajabu kukufikieni mawaidha yanayo toka kwa Mola wenu Mlezi kwa mtu aliye mmoja wenu nyinyi ili akuonyeni? Na kumbukeni alivyo kufanyeni mshike pahala pa kaumu ya Nuhu, na akakuzidisheni katika umbo. Basi zikumbukeni neema za Mwenyezi Mungu ili mpate kufanikiwa.'),
(1024, 7, 70, 'Wakasema: Je! Umetujia ili tumuabudu Mwenyezi Mungu peke yake, na tuyaache waliyo kuwa wakiyaabudu baba zetu? Basi tuletee hayo unayo tuahidi, ukiwa miongoni mwa wasemao kweli.'),
(1025, 7, 71, 'Akasema: Bila ya shaka adhabu na ghadhabu zimekwisha kukuangukieni kutoka kwa Mola wenu Mlezi. Mnabishana nami kwa ajili ya majina tu mliyo yaita nyinyi na baba zenu ambayo kwayo Mwenyezi Mungu hakuteremsha uthibitisho? Basi ngojeni, mimi pia ni pamoja nanyi katika wanao ngoja.'),
(1026, 7, 72, 'Basi tukamwokoa yeye na walio kuwa pamoja naye kwa rehema zetu, na tukakata mizizi ya wale walio zikanusha Ishara zetu, na hawakuwa wenye kuamini.'),
(1027, 7, 73, 'Na kwa Thamud tulimpeleka ndugu yao, Saleh. Akasema: Enyi watu wangu! Muabuduni Mwenyezi Mungu. Nyinyi hamna Mungu ila Yeye. Hakika imekwisha kukufikieni Ishara iliyo wazi kutoka kwa Mola wenu Mlezi. Huyu ni ngamia jike wa Mwenyezi Mungu aliye ni Ishara kwenu. Basi mwachieni ale kwenye ardhi ya Mwenyezi Mungu, wala msimdhuru, isije kukushukieni adhabu chungu.'),
(1028, 7, 74, 'Na kumbukeni alivyo kufanyeni wa kushika pahala pa A\'adi na akakuwekeni vizuri katika nchi, mnajenga majumba ya fakhari katika nyanda zake, na mnachonga majumba katika milima. Basi zikumbukeni neema za Mwenyezi Mungu wala msitende uovu katika nchi kwa ufisadi.'),
(1029, 7, 75, 'Waheshimiwa wa kaumu yake wanao jivuna waliwaambia wanao onewa wenye kuamini miongoni mwao: Je, mnaitakidi ya kwamba Saleh ametumwa na Mola Mlezi wake? Wakasema: Hakika sisi tunayaamini yale aliyo tumwa.'),
(1030, 7, 76, 'Wakasema wale wanao jivuna: Sisi tunayakataa hayo mnayo yaamini.'),
(1031, 7, 77, 'Na wakamuuwa yule ngamia, na wakaasi amri ya Mola Mlezi wao, na wakasema: Ewe Saleh! Tuletee unayo tuahidi ikiwa wewe ni miongoni mwa Mitume.'),
(1032, 7, 78, 'Basi tetemeko la ardhi likawanyakua, na kulipo kucha wakawa majumbani mwao kifudifudi wamekwisha kufa.'),
(1033, 7, 79, 'Basi Saleh akwaacha na akasema: Enyi watu wangu! Nilikufikishieni Ujumbe wa Mola wangu Mlezi, na nikakunasihini, lakini nyinyi hamwapendi wenye nasaha.'),
(1034, 7, 80, 'Na tulimtuma Lut\', alipo waambia watu wake: Je, mnafanya uchafu ambao hajakutangulieni yeyote kwa uchafu huo katika viumbe vyote!'),
(1035, 7, 81, 'Hivyo nyinyi mnawaingilia wanaume kwa matamanio mkwawacha wanawake? Kumbe nyinyi ni watu wafujaji!'),
(1036, 7, 82, 'Na haikuwa jawabu ya kaumu yake isipo kuwa kuambizana: Wafukuzeni katika mji wenu, maana hao ni watu wanao jitakasa.'),
(1037, 7, 83, 'Basi tukamwokoa yeye na watu wake, isipo kuwa mkewe, alikuwa miongoni mwa walio bakia nyuma.'),
(1038, 7, 84, 'Na tukawamiminia mvua. Basi tazama jinsi ulivyo kuwa mwisho wa wakosefu.'),
(1039, 7, 85, 'Na kwa watu wa Madyana tulimtuma ndugu yao Shua\'ib. Akasema: Enyi watu wangu! Muabuduni Mwenyezi Mungu. Hamna mungu mwengine isipo kuwa Yeye. Imekwisha kufikieni hoja wazi kutoka kwa Mola Mlezi wenu. Basi timizeni sawasawa vipimo na mizani, wala msiwapunje watu vitu vyao, wala msifanye uharibifu katika nchi, baada ya kuwa imekwisha tengenea. Hivi ndivyo bora kwenu ikiwa nyinyi ni Waumini.'),
(1040, 7, 86, 'Wala msikae katika kila njia mkitisha watu, na kuwazuilia na Njia ya Mwenyezi Mungu wale wenye kumuamini, na mkataka njia ipotoke. Na kumbukeni mlipo kuwa wachache, naye akakufanyeni kuwa wengi. Na tazameni jinsi ulivyo kuwa mwisho wa mafisadi.'),
(1041, 7, 87, 'Na kama liko kundi miongoni mwenu lililo amini haya niliyo tumwa, na kundi jengine halikuamini, basi subirini mpaka Mwenyezi Mungu ahukumu baina yetu. Naye ndiye Mbora wa wenye kuhukumu.'),
(1042, 7, 88, 'WAHESHIMIWA WALIO TAKABARI katika kaumu yake wakasema: Ewe Shua\'ib! Tutakutoa wewe pamoja na wale walio kuamini katika mji wetu, au mrejee katika mila yetu. Akasema: Je, ingawa tunaichukia?'),
(1043, 7, 89, 'Bila ya shaka itakuwa tumemzulia uwongo Mwenyezi Mungu tukirudi katika mila yenu baada ya kwisha tuokoa nayo Mwenyezi Mungu. Wala haiwi kwetu sisi kuirejea mila hiyo, ila akitaka Mwenyezi Mungu, Mola Mlezi wetu. Ujuzi wa Mola Mlezi wetu umeenea katika kila kitu. Kwa Mwenyezi Mungu tunategemea. Ewe Mola Mlezi wetu! Tuhukumie baina yetu na kaumu yetu kwa haki, nawe ndiye mbora wa wanao hukumu.'),
(1044, 7, 90, 'Na waheshimiwa walio kufuru katika kaumu yake wakasema: Mkimfuata Shua\'ib, basi hakika hapo nyinyi mtakuwa mlio khasiri.'),
(1045, 7, 91, 'Basi tetemeko la ardhi likawanyakua, na wakaamkia majumbani mwao wamejifudikiza, wamekwisha kufa.'),
(1046, 7, 92, 'Wale walio mkanusha Shua\'ib wakawa kama kwamba hawakuwako. Walio mkanusha Shua\'ib ndio walio kuwa wenye kukhasiri.'),
(1047, 7, 93, 'Basi akawaachilia mbali na akasema: Enyi kaumu yangu! Nimekufikishieni Ujumbe wa Mola Mlezi wangu, na nimekunasihini; basi vipi niwahuzunikie watu makafiri?'),
(1048, 7, 94, 'Na hatukumtuma Nabii yeyote katika mji ila tuliwapatiliza wakaazi wake kwa taabu na mashaka ili wapate kunyenyekea.'),
(1049, 7, 95, 'Kisha Sisi hubadilisha mahala pa ubaya kwa wema, hata wakazidi, na wakasema: Taabu na raha ziliwafikia baba zetu. Basi kwa ghafla tukawashika, hali ya kuwa hawajatambua.'),
(1050, 7, 96, 'Na lau kuwa watu wa miji wangeli amini na wakamchamngu, kwa yakini tungeli wafungulia baraka kutoka mbinguni na katika ardhi. Lakini walikanusha basi tukawapatiliza kwa sababu ya yale waliyo kuwa wakiyachuma.'),
(1051, 7, 97, 'Je, watu wa mijini wameaminisha ya kuwa adhabu yetu haitawafika usiku, nao wamelala?'),
(1052, 7, 98, 'Au watu wa mijini wameaminisha ya kuwa adhabu yetu haitawafika mchana, nao wanacheza?'),
(1053, 7, 99, 'Je, wamejiaminisha na mipango ya Mwenyezi Mungu? Kwani hawajiaminishi na mipango ya Mwenyezi Mungu ila watu wenye kukhasiri.'),
(1054, 7, 100, 'Je, hawahidiki hawa wanao rithi ardhi baada ya wale wenyewe kwamba tukitaka tutawapatiliza kwa madhambi yao, na tutapiga muhuri juu ya nyoyo zao, na kwa hivyo hawatasikia?'),
(1055, 7, 101, 'Miji hiyo, tunakusimulia baadhi ya khabari zake. Na hapana shaka Mitume wao waliwafikia kwa hoja zilizo waziwazi. Lakini hawakuwa wenye kuamini waliyo yakanusha zamani. Namna hivi Mwenyezi Mungu anazipiga muhuri nyoyo za makafiri.'),
(1056, 7, 102, 'Wala hatukuwaona wengi wao kuwa wanatimiza ahadi; bali kwa hakika tuliwakuta wengi wao ni wapotofu.'),
(1057, 7, 103, 'Kisha baada yao tukamtuma Musa na Ishara zetu kwa Firauni na waheshimiwa wake. Nao wakazikataa. Basi tazama ulikuwaje mwisho wa waharibifu.'),
(1058, 7, 104, 'Na Musa alisema: Ewe Firauni! Hakika mimi ni Mtume nitokaye kwa Mola Mlezi wa viumbe vyote.'),
(1059, 7, 105, 'Inafaa nisiseme juu ya Mwenyezi Mungu ila lilio la Haki. Nami hakika nimekujieni na dalili waziwazi kutokana na Mola Mlezi wenu. Basi waache Wana wa Israili wende nami.'),
(1060, 7, 106, 'Akasema Firauni: Ikiwa umekuja na Ishara, basi ilete ukiwa ni katika wasemao kweli.'),
(1061, 7, 107, 'Basi akaitupa fimbo yake, na mara ikawa nyoka dhaahiri.'),
(1062, 7, 108, 'Na akatoa mkono wake, mara ukawa mweupe kwa watazamao.'),
(1063, 7, 109, 'Wakasema waheshimiwa wa kaumu ya Firauni: Hakika huyo ni mchawi mjuzi.'),
(1064, 7, 110, 'Anataka kukutoeni katika nchi yenu. Basi mnatoa shauri gani?'),
(1065, 7, 111, 'Wakasema: Mwache kidogo yeye na nduguye, na watume mijini wakusanyao,'),
(1066, 7, 112, 'Wakuletee kila mchawi mjuzi.'),
(1067, 7, 113, 'Wakaja wachawi kwa Firauni, wakasema: Hakika tutapata ujira ikiwa tutashinda.'),
(1068, 7, 114, 'Akasema: Naam! Nanyi bila ya shaka mtakuwa katika wa karibu nami.'),
(1069, 7, 115, 'Wakasema: Ewe Musa! Je, utatupa wewe, au tutakuwa sisi tutao tupa?'),
(1070, 7, 116, 'Akasema: Tupeni! Basi walipo tupa waliyazuga macho ya watu na wakawatia khofu, na wakaleta uchawi mkubwa.'),
(1071, 7, 117, 'Nasi tukamfunulia Musa kumwambia: Tupa fimbo yako. Mara ikavimeza vyote vile walivyo vibuni.'),
(1072, 7, 118, 'Kweli ikathibiti na yakabat\'ilika waliyo kuwa wakiyatenda.'),
(1073, 7, 119, 'Kwa hivyo walishindwa hapo, na wakageuka kuwa wadogo.'),
(1074, 7, 120, 'Na wachawi wakapoomoka wakisujudu.'),
(1075, 7, 121, 'Wakasema: Tumemuamini Mola Mlezi wa viumbe vyote,'),
(1076, 7, 122, 'Mola Mlezi wa Musa na Haarun.'),
(1077, 7, 123, 'Akasema Firauni: Mmemuamini kabla sijakupeni idhini? Hizi ni njama mlizo panga mjini mpate kuwatoa wenyewe. Lakini karibu mtajua!'),
(1078, 7, 124, 'Lazima nitaikata mikono yenu na miguu yenu mbali mbali. Kisha nitakutundikeni misalabani.'),
(1079, 7, 125, 'Wakasema: Sisi ni wenye kurejea kwa Mola wetu Mlezi.'),
(1080, 7, 126, 'Nawe hukuudhika nasi ila kwa kuwa tumeziamini Ishara za Mola Mlezi wetu zilipo tujia. Ewe Mola Mlezi wetu! Tumiminie uvumilivu na utufishe hali ni Waislamu.'),
(1081, 7, 127, 'Wakasema waheshimiwa wa kaumu ya Firauni: Unamuacha Musa na watu wake walete uharibifu katika nchi na wakuache wewe na miungu yako? Akasema: Tutawauwa wavulana wao, na tutawaacha hai wanawake wao. Na bila ya shaka sisi ni wenye nguvu juu yao.'),
(1082, 7, 128, 'Musa akawaambia watu wake: Ombeni msaada kwa Mwenyezi Mungu, na subirini. Hakika ardhi ni ya Mwenyezi Mungu. Naye humrithisha amtakaye katika waja wake. Na mwisho ni wa wachamngu.'),
(1083, 7, 129, 'Wakasema: Tumeudhiwa kabla hujatufikia, na baada ya wewe kutujia! Musa akasema: Huenda Mola wenu Mlezi akamhiliki adui wenu, na akakufanyeni nyinyi ndio wa kufuatia kushika nchi, ili atazame mtavyo kuja tenda nyinyi.'),
(1084, 7, 130, 'Na hakika tuliwaadhibu watu wa Firauni kwa miyaka (ya ukame) na kwa upungufu wa mazao, ili wapate kukumbuka.'),
(1085, 7, 131, 'Likiwafika jema wakisema: Haya ni haki yetu. Na likiwafika ovu husema: Musa na walio pamoja naye ni wakorofi. Hakika ukorofi wao huo unao wafikia unatoka kwa Mwenyezi Mungu. Lakini wengi wao hawajui.'),
(1086, 7, 132, 'Na wakisema: Hata ukituletea Ishara yoyote kutuzuga hatuto kuamini.'),
(1087, 7, 133, 'Basi tukawapelekea tufani, na nzige, na chawa, na vyura, na damu, kuwa ni Ishara mbali mbali. Nao wakapanda kiburi, na wakawa watu wakosefu.'),
(1088, 7, 134, 'Na ilipo waangukia adhabu wakasema: Ewe Musa! Tuombee kwa Mola wako Mlezi kwa aliyo kuahidi. Ukituondolea adhabu hii hapana shaka tutakuamini, na tutawaachilia Wana wa Israili wende nawe.'),
(1089, 7, 135, 'Lakini tukiwaondolea adhabu mpaka muda fulani wao waufikie, mara wakivunja ahadi yao.'),
(1090, 7, 136, 'Basi tuliwapatiliza, tuka- wazamisha baharini kwa sababu walizikanusha Ishara zetu, na wakaghafilika nazo.'),
(1091, 7, 137, 'Na tukawarithisha watu walio kuwa wanadharauliwa mashariki na magharibi ya ardhi tuliyo ibariki. Na likatimia neno jema la Mola Mlezi wako juu ya Wana wa Israili, kwa vile walivyo subiri. Na tukayaangamiza aliyo kuwa akiyafanya Firauni na kaumu yake, na waliyo kuwa wakiyajenga.'),
(1092, 7, 138, 'Na tukawavusha bahari Wana wa Israili, wakafika kwa watu wanao abudu masanamu yao. Wakasema: Ewe Musa! Hebu tufanyie na sisi miungu kama hawa walivyo kuwa na miungu. Musa akasema: Hakika nyinyi ni watu msio jua kitu.'),
(1093, 7, 139, 'Hakika haya waliyo nayo watu hawa yatakuja angamia, na hayo waliyo kuwa wakiyafanya yamepotea bure.'),
(1094, 7, 140, 'Akasema: Je, nikutafutieni mungu asiye kuwa Mwenyezi Mungu, na Yeye ndiye aliye kufadhilisheni juu ya viumbe vyote?'),
(1095, 7, 141, 'Na pale tulipo kuokoeni kwa watu wa Firauni walio kupeni adhabu za mwisho wa uovu. Wakiwauwa wavulana wenu, na wakawaacha hai wanawake wenu. Na katika hayo yalikuwa majaribio makubwa kutokana na Mola wenu Mlezi.'),
(1096, 7, 142, 'Tulimuahidi Musa masiku thalathini na tukayatimiza kwa kumi; ikatimia miadi ya Mola wake Mlezi masiku arubaini. Na Musa akamwambia nduguye Haarun: Shika mahala pangu kwa watu wangu na utengeneze wala usifuate njia ya waharibifu.'),
(1097, 7, 143, 'Na alipo kuja Musa kwenye miadi yetu, na Mola wake Mlezi akamsemeza, alisema: Mola wangu Mlezi! Nionyeshe nikutazame. Mwenyezi Mungu akasema: Hutoniona. Lakini utazame huo mlima. Ukibaki pahala pake basi utaniona. Basi alipo jionyesha Mola Mlezi wake kwa mlima, aliufanya uvurugike, na Musa akaanguka chini amezimia. Alipo zindukana alisema: Subhanaka, Umetakasika! Natubu kwako, na mimi ni wa kwanza wa Waumini.'),
(1098, 7, 144, '(Mwenyezi Mungu) akasema: Ewe Musa! Mimi nimekuteuwa wende kwa watu na Ujumbe wangu na maneno yangu. Basi chukua niliyo kupa na uwe katika wanao shukuru.'),
(1099, 7, 145, 'Na tukamuandikia katika mbao kila kitu, mawaidha na maelezo ya mambo yote. (Tukamwambia): Basi yashike kwa nguvu na uwaamrishe watu wako wayashike kwa ubora wake. Nami nitakuonyesheni makaazi ya wapotofu.'),
(1100, 7, 146, 'Nitawatenga na Ishara zangu wale wanao fanya kiburi katika nchi bila ya haki. Na wao wakiona kila Ishara hawaiamini. Wakiiona njia ya uwongofu hawaishiki kuwa ndiyo njia; lakini wakiiona njia ya upotofu wanaishika kuwa ndiyo njia. Hayo ni kwa sababu wamezikanusha Ishara zetu, na wameghafilika nazo.'),
(1101, 7, 147, 'Na walio zikanusha Ishara zetu na mkutano wa Akhera a\'mali zao zimeharibika. Kwani watalipwa ila kwa yale waliyo kuwa wakiyatenda?'),
(1102, 7, 148, 'Na baada yake walimfanya ndama kutokana na mapambo yao (kumuabudu), kiwiliwili tu kilicho kuwa na sauti. Hivyo, hawakuona kuwa hawasemezi wala hawaongoi njia? Wakamuabudu, na wakawa wenye kudhulumu.'),
(1103, 7, 149, 'Na walipo juta na wakaona ya kwamba wamekwisha potea, walisema: Ikiwa Mola wetu Mlezi hakuturehemu na akatusamehe, bila ya shaka tutakuwa miongoni mwa walio khasiri.'),
(1104, 7, 150, 'Na alipo rudi Musa kwa watu wake, naye amekasirika na kuhuzunika, alisema: Ni maovu yalioje mlio nifanyia nyuma yangu! Je, mmeitangulia amri ya Mola wenu Mlezi? Na akaziweka chini zile mbao, na akamkamata kichwa ndugu yake akimvutia kwake. (Haarun) akasema: Ewe mwana wa mama yangu! Hakika hawa watu wamenidharau, na wakakaribia kuniuwa. Basi usiwafurahishe maadui juu yangu, wala usinifanye pamoja na watu madhaalimu.'),
(1105, 7, 151, '(Musa) akasema: Mola Mlezi wangu! Nisamehe mimi na ndugu yangu, na ututie katika rehema yako. Nawe ni Mwenye kurehemu kushinda wote wenye kurehemu.'),
(1106, 7, 152, 'Hakika wale walio muabudu ndama, itawapata ghadhabu ya Mola wao Mlezi na madhila katika maisha ya dunia. Na hivi ndivyo tunavyo walipa wazushi.'),
(1107, 7, 153, 'Na walio tenda maovu, kisha wakatubia baada yake na wakaamini, hapana shaka kuwa Mola Mlezi wako baada ya hayo ni Mwenye kusamehe Mwenye kurehemu.'),
(1108, 7, 154, 'Na Musa ilipo mtulia ghadhabu aliziokota zile mbao. Na katika maandiko yake mna uwongofu na rehema kwa wanao mwogopa Mola wao Mlezi.'),
(1109, 7, 155, 'Na Musa akawateuwa watu sabiini katika kaumu yake kwa miadi yetu. Na ulipo fika mtetemeko mkubwa alisema: Ewe Mola wangu Mlezi! Ungeli taka ungeli wahiliki wao na mimi zamani! Unatuhiliki kwa waliyo yafanya wajinga katika sisi? Haya si chochote ila ni mtihani wako, umpoteze umtakaye na umhidi umtakaye. Wewe ndiye Rafiki Mlinzi wetu. Basi tughufirie na uturehemu. Na Wewe ndiye mbora wa kughufiria.'),
(1110, 7, 156, 'Na utuandikie mema katika dunia hii na katika Akhera. Sisi tumerejea kwako. (Mwenyezi Mungu) akasema: Adhabu yangu nitamsibu nayo nimtakaye. Na rehema yangu imeenea kila kitu. Lakini nitawaandikia khasa wanao mchamngu, na wanao toa Zaka, na wale ambao wanaziamini Ishara zetu,'),
(1111, 7, 157, 'Ambao kwamba wanamfuata huyo Mtume, Nabii, asiye soma wala kuandika, wanaye mkuta kaandikwa kwao katika Taurati na Injili, anaye waamrisha mema na anawakanya maovu, na anawahalalishia vizuri, na ana waharimishia viovu, na anawaondolea mizigo yao na minyororo iliyo kuwa juu yao. Basi wale walio muamini yeye, na wakamhishimu, na wakamsaidia, na wakaifuata nuru iliyo teremshwa pamoja naye - hao ndio wenye kufanikiwa.'),
(1112, 7, 158, 'Sema: Enyi watu! Hakika mimi ni Mtume kwenu nyinyi nyote, niliye tumwa na Mwenyezi Mungu Mwenye ufalme wa mbingu na ardhi. Hapana mungu ila Yeye anaye huisha na anaye fisha. Basi muaminini Mwenyezi Mungu na Mtume wake asiyejua kusoma na kuandika, ambaye anamuamini Mwenyezi Mungu na maneno yake. Na mfuateni yeye ili mpate kuongoka.'),
(1113, 7, 159, 'Na katika kaumu ya Musa lipo kundi linao waongoa watu kwa haki na kwa haki wanafanya uadilifu.'),
(1114, 7, 160, 'Na tuliwagawanya katika makabila kumi na mbili, mataifa mbali mbali. Na tulimfunulia Musa walipo muomba maji watu wake kumwambia: Lipige hilo jiwe kwa fimbo yako. Mara zikatibuka humo chemchem kumi na mbili, na kila watu wakajua mahali pao pa kunywea. Na tukawafunika kivuli kwa mawingu, na tukawateremshia Manna na Salwa. Tukawaambia: Kuleni vizuri hivi tulivyo kuruzukuni. Wala hawakutu- dhulumu Sisi, bali wamejidhulumu wenyewe.'),
(1115, 7, 161, 'Na pale walipo ambiwa: Kaeni katika mji huu, na mle humo mpendapo, na semeni: Tufutie dhambi zetu. Na ingieni katika mlango wake kwa unyenyekevu, tupate kukusameheni makosa yenu. Walio wema tutawazidishia.'),
(1116, 7, 162, 'Lakini walio dhulumu miongoni mwao walibadilisha kauli, sio ile waliyo ambiwa. Basi tukawapelekea adhabu kutoka mbinguni kwa vile walivyo kuwa wakidhulumu.'),
(1117, 7, 163, 'Na waulize khabari za mji ulio kuwa kando ya bahari, wakiivunja Sabato (Jumaamosi ya mapumziko). Samaki walikuwa wakiwajia juu juu siku ya mapumziko yao, na siku zisio kuwa za kupumzika hawakuwa wakiwajia. Kwa namna hiyo tuliwajaribu kwa sababu walikuwa wakifanya upotovu.'),
(1118, 7, 164, 'Na kikundi kati yao walisema: Kwa nini mnawawaidhi watu ambao tangu hapo Mwenyezi Mungu atawateketeza au atawaadhibu adhabu kali? Wakasema: Ili uwe ni udhuru kwa Mola Mlezi wenu, na huenda nao wakamchamngu.'),
(1119, 7, 165, 'Basi walipo yasahau waliyo kumbushwa, tuliwaokoa walio kuwa wakikataza maovu, na tukawatesa walio dhulumu kwa adhabu mbaya kwa vile walivyo kuwa wakifanya upotovu.'),
(1120, 7, 166, 'Walipo jifakharisha katika waliyo kanywa tuliwaambia: Kuweni manyani wa kudharauliwa.'),
(1121, 7, 167, 'Na pale alipo tangaza Mola wako Mlezi kwamba hapana shaka atawaletea watu ambao watawaadhibu kwa adhabu mbaya mpaka Siku ya Kiyama. Hakika Mola wako Mlezi ni Mwepesi wa kuadhibu, na bila ya shaka ni Mwenye maghfira, na Mwenye kurehemu.'),
(1122, 7, 168, 'Na tuliwafarikisha katika ardhi makundi makundi. Wako kati yao walio wema, na wengine kinyume cha hivyo. Na tukawajaribu kwa mema na mabaya ili wapate kurejea.'),
(1123, 7, 169, 'Na wakafuatia baada yao kizazi kibaya walio rithi Kitabu. Wakashika anasa za haya maisha duni, na wakasema: Tutasamehewa! Na ikiwajia tena anasa kama hiyo wataishika pia. Je, hawakufanyiwa agano la Kitabu kuwa wasiseme juu ya Mwenyezi Mungu ila Haki tu? Nao wamekwisha soma yaliomo humo. Na nyumba ya Akhera ni bora kwa wanao jikinga na maasi. Basi je, hamtii akilini?'),
(1124, 7, 170, 'Na wale wanao kishikilia Kitabu, na wakashika Sala - hakika Sisi hatupotezi ujira wa watendao mema.'),
(1125, 7, 171, 'Na pale tulipo unyanyua mlima juu yao ukawa kama kwamba ni kiwingu kilicho wafunika, na wakadhani kuwa utawaangukia, (tukawaambia): Kamateni kwa nguvu tuliyo kupeni, na yakumbukeni yaliyomo ndani yake ili mpate kumcha Mungu.'),
(1126, 7, 172, 'Na pale Mola wako Mlezi alipo waleta katika wanaadamu kutoka migongoni mwao kizazi chao, na akawashuhudisha juu ya nafsi zao, akawaambia: Je, Mimi si Mola Mlezi wenu? Wakasema: Kwani! Tumeshuhudia. Msije mkasema Siku ya Kiyama sisi tulikuwa tumeghafilika na hayo.'),
(1127, 7, 173, 'Au mkasema: Hakika baba zetu ndio walio shirikisha kabla yetu, na sisi ni dhuriya zao tu baada yao. Basi utatuangamiza kwa sababu ya waliyo yafanya wapotovu?'),
(1128, 7, 174, 'Ndio kama hivyo tunavyo zipambanua Ishara, kwa kutaraji kuwa watarejea.'),
(1129, 7, 175, 'Na wasomee khabari za yule ambaye tulimpa Ishara zetu, naye akajivua nazo. Na Shet\'ani akamuandama, akawa miongoni walio potea.'),
(1130, 7, 176, 'Na tungelitaka tungeli mtukuza kwa hizo Ishara, lakini yeye aliushikilia ulimwengu na akafuata pumbao lake. Basi mfano wake ni mfano wa mbwa. Ukimhujumu hupumua na kutoa ulimi na ukimwacha pia hupumua na kutoa ulimi. Huo ni mfano wa kaumu wanao zikanusha Ishara zetu. Basi simulia hadithi, huenda wakatafakari.'),
(1131, 7, 177, 'Huu ni mfano muovu kabisa wa watu wanao kanusha Ishara zetu na wakajidhulumu nafsi zao.'),
(1132, 7, 178, 'Yule ambaye Mwenyezi Mungu amemhidi basi huyo amehidika; na alio waacha kupotea basi hao ndio walio khasirika.'),
(1133, 7, 179, 'Na tumeiumbia Jahannamu majini wengi na watu. Wana nyoyo, lakini hawafahamu kwazo. Na wana macho, lakini hawaoni kwayo. Na wana masikio, lakini hawasikii kwayo. Hao ni kama nyama howa, bali wao ni wapotofu zaidi. Hao ndio walio ghafilika.'),
(1134, 7, 180, 'Na Mwenyezi Mungu ana majina mazuri kabisa, basi muombeni kwa hayo. Na waacheni wale wanao haribu utakatifu wa majina yake. Hao watakuja lipwa waliyo kuwa wakiyatenda.'),
(1135, 7, 181, 'Na katika tulio waumba wako watu wanao ongoza kwa Haki, na kwayo wanafanya uadilifu.'),
(1136, 7, 182, 'Na wale walio kadhibisha Ishara zetu, tutawapururia pole pole kwa namna ambayo wenyewe hawaijui.'),
(1137, 7, 183, 'Nami nitawapa muda. Hakika mpango wangu ni madhubuti.'),
(1138, 7, 184, 'Je, hawafikiri? Huyu mwenzao hana wazimu. Hakuwa yeye ila ni mwonyaji aliye dhaahiri.'),
(1139, 7, 185, 'Je, hawatazami ufalme wa mbingu na ardhi, na vitu alivyo viumba Mwenyezi Mungu, na pengine ajali yao imekwisha karibia? Basi maneno gani baada ya haya watayaamini?'),
(1140, 7, 186, 'Aliye mwacha Mwenyezi Mungu kupotea hana wa kumwongoa. Atawaacha hao wakitangatanga katika upotofu wao.'),
(1141, 7, 187, 'Wanakuuliza hiyo Saa (ya mwisho) itakuwa lini? Sema: Kuijua kwake kuko kwa Mola Mlezi wangu. Haidhihirishi hiyo kwa wakati wake ila Yeye. Ni nzito katika mbingu na ardhi. Haikujiini ila kwa ghafla tu. Wanakuuliza kama kwamba wewe una pupa ya kuijua. Sema: Ujuzi wake uko kwa Mwenyezi Mungu. Lakini aghlabu ya watu hawajui.'),
(1142, 7, 188, 'Sema: Siimilikii nafsi yangu manufaa wala madhara, ila apendavyo Mwenyezi Mungu. Na lau kuwa ninayajua ya ghaibu ninge jizidishia mema mengi, wala ovu lisinge nigusa. Mimi si chochote ila ni mwonyaji na mbashiri kwa watu wanao amini.'),
(1143, 7, 189, 'Yeye ndiye aliye kuumbeni katika nafsi moja; na katika hiyo hiyo akamfanya mwenzi wake, ili akae naye kwa utulivu. Na anapo muingilia hubeba mzigo mwepesi, akitembea nao. Anapo kuwa mja mzito, wote wawili humwomba Mwenyezi Mungu, Mola Mlezi wao: Kama ukitupa mwana mwema tutakuwa katika wanao shukuru.'),
(1144, 7, 190, 'Basi anapo wapa mwana mwema, wanamfanyia washirikina Mwenyezi Mungu katika kile kile alicho wapa. Mwenyezi Mungu ametukuka na huko kushirikisha kwao.'),
(1145, 7, 191, 'Ati wanawafanya viumbe kuwa ni washirika wa Mwenyezi Mungu, nao hawaumbi, bali wao ndio wanaumbwa?'),
(1146, 7, 192, 'Wala hawana uwezo wa kuwanusuru wala wenyewe hawajinusuru.'),
(1147, 7, 193, 'Na mkiwaita kwenye uwongofu, hawakufuateni. Ni mamoja kwenu ikiwa mtawaita au mkinyamaza.'),
(1148, 7, 194, 'Hakika hao mnao waomba asiye kuwa Mwenyezi Mungu ni waja mfano wenu nyinyi. Hebu waombeni, nao wakuitikieni, kama mnasema kweli.'),
(1149, 7, 195, 'Je, wao wanayo miguu ya kwendea? Au wanayo mikono ya kutetea? Au wanayo macho ya kuonea? Au wanayo masikio ya kusikizia? Sema: Iiteni hio miungu yenu ya ushirikina. Kisha nifanyieni mimi vitimbi, wala msinipe muhula.'),
(1150, 7, 196, 'Hakika Mlinzi wangu ni Mwenyezi Mungu aliye teremsha Kitabu. Naye ndiye awalindae walio wema.'),
(1151, 7, 197, 'Na hao mnao waomba asiye kuwa Mwenyezi hawawezi kukunusuruni, wala hawajinusuru wenyewe.'),
(1152, 7, 198, 'Na mkiwaita kwenye uwongofu hawasikii. Na unawaona wanakutazama, nao hawaoni.'),
(1153, 7, 199, 'Shikamana na kusamehe, na amrisha mema, na jitenge na majaahili.'),
(1154, 7, 200, 'Na uchochezi wa Shet\'ani ukikuchochea basi omba ulinzi wa Mwenyezi Mungu. Hakika Yeye ni Msikizi, Mjuzi.'),
(1155, 7, 201, 'Hakika wale wamchao Mungu, zinapo wagusa pepesi za Shet\'ani, mara huzindukana na hapo huwa wenye kuiona haki.'),
(1156, 7, 202, 'Na ndugu zao wanawavutia kwenye upotofu, kisha wao hawaachi.'),
(1157, 7, 203, 'Na usipo waletea Ishara waitakayo husema: Kwa nini hukuibuni? Sema: mimi ninafuata yanayo funuliwa kwangu kutokana na Mola Mlezi wangu. Hii (Qur\'ani) ni hoja zinazo toka kwa Mola wenu Mlezi, na uwongofu na rehema kwa watu wanao amini.'),
(1158, 7, 204, 'Na isomwapo Qur\'ani isikilizeni na mnyamaze ili mpate kurehemewa.'),
(1159, 7, 205, 'Na mkumbuke Mola Mlezi wako nafsini kwako kwa unyenyekevu na khofu, na bila ya kupiga kelele kwa kauli, asubuhi na jioni. Wala usiwe miongoni wa walio ghafilika.'),
(1160, 7, 206, 'Hakika wale walioko kwa Mola wako Mlezi hawajivuni wakaacha kumuabdu, na wanamtakasa na wanamsujudia.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Wanakuuliza juu ya Ngawira. Sema: Ngawira ni ya Mwenyezi Mungu na Mtume. Basi mcheni Mwenyezi Mungu na suluhisheni mambo baina yenu, na mt\'iini Mwenyezi Mungu na Mtume wake ikiwa nyinyi ni Waumini.'),
(1162, 8, 2, 'Hakika Waumini ni wale ambao anapo tajwa Mwenyezi Mungu nyoyo zao hujaa khofu, na wanapo somewa Aya zake huwazidisha Imani, na wakamtegemea Mola wao Mlezi.'),
(1163, 8, 3, 'Hao ambao wanashika Sala na wanatoa katika yale tunayo waruzuku.'),
(1164, 8, 4, 'Hao kweli ndio Waumini. Wao wana vyeo, na maghfira, na riziki bora, kwa Mola wao Mlezi.'),
(1165, 8, 5, 'Kama Mola wako Mlezi alivyo kutoa nyumbani kwako kwa Haki, na hakika kundi moja la Waumini linachukia.'),
(1166, 8, 6, 'Wanajadiliana nawe kwa jambo la Haki baada ya kwisha bainika, kama kwamba wanasukumwa kwenye mauti nao wanaona.'),
(1167, 8, 7, 'Na Mwenyezi Mungu alipo kuahidini kuwa moja katika makundi mawili ni lenu. Nanyi mkapenda lisilo na nguvu ndio liwe lenu; na Mwenyezi Mungu anapenda ahakikishe Haki kwa maneno yake, na aikate mizizi ya makafiri.'),
(1168, 8, 8, 'Ili ahakikishe Haki na auvunje upotovu na wangachukia wakosefu.'),
(1169, 8, 9, 'Mlipo kuwa mkimuomba msaada Mola wenu Mlezi, naye akakujibuni kuwa: Kwa yakini Mimi nitakusaidieni kwa Malaika elfu wanao fuatana mfululizo.'),
(1170, 8, 10, 'Na Mwenyezi Mungu hakufanya haya ila kuwa ni bishara na ili nyoyo zenu zituwe. Na haupatikani ushindi ila kutoka kwa Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye nguvu, na Mwenye hikima.'),
(1171, 8, 11, 'Alipo kufunikeni kwa usingizi uwe ni uaminisho kutoka kwake, na akakuteremshieni maji kutoka mbinguni ili kukusafisheni kwayo na kukuondoleeni uchafu wa Shet\'ani, na kuzipa nguvu nyoyo zenu, na kuitia imara miguu yenu.'),
(1172, 8, 12, 'Mola wako Mlezi alipo wafunulia Malaika: Hakika Mimi ni pamoja nanyi, basi watieni nguvu walio amini. Nitatia woga katika nyoyo za walio kufuru. Basi wapigeni juu ya shingo na wapigeni kwenye kila ncha za vidole.'),
(1173, 8, 13, 'Hayo ni kwa sababu wamemuasi Mwenyezi Mungu na Mtume wake. Na mwenye kumuasi Mwenyezi Mungu na Mtume wake basi Mwenyezi Mungu ni Mkali wa kuadhibu.'),
(1174, 8, 14, 'Ndio hivyo! Basi ionjeni! Na bila ya shaka makafiri wana adhabu ya Moto.'),
(1175, 8, 15, 'Enyi Mlio amini! Mkikutana na walio kufuru vitani msiwageuzie mgongo.'),
(1176, 8, 16, 'Na atakaye wageuzia mgongo wake siku hiyo - isipo kuwa kwa mbinu za vita au kuungana na kikosi - basi atakuwa amestahiki ghadhabu ya Mwenyezi Mungu. Na pahala pake ni Jahannamu, na huo ni mwisho muovu.'),
(1177, 8, 17, 'Hamkuwauwa nyinyi lakini Mwenyezi Mungu ndiye aliye wauwa. Na wewe hukutupa, walakini Mwenyezi Mungu ndiye aliye tupa, ili awajaribu Waumini majaribio mema yatokayo kwake. Hakika Mwenyezi Mungu ndiye Msikizi na Mjuzi.'),
(1178, 8, 18, 'Ndio hivyo! Na hakika Mwenyezi Mungu ni Mwenye kudhoofisha vitimbi vya makafiri.'),
(1179, 8, 19, 'Kama mnataka hukumu basi hukumu imekwisha kujieni. Na mkiacha itakuwa ndio kheri kwenu. Na mkirejea Sisi pia tutarejea. Na jeshi lenu halitakufaeni kitu, japo likiwa kubwa. Kwani hakika Mwenyezi yu pamoja na Waumini.'),
(1180, 8, 20, 'Enyi mlio amini! Mt\'iini Mwenyezi Mungu na Mtume wake, wala msijiepushe naye, nanyi mnasikia.'),
(1181, 8, 21, 'Wala msiwe kama wale wanao sema: Tumesikia, na kumbe hawasikii.'),
(1182, 8, 22, 'Hakika vinyama viovu mbele ya Mwenyezi Mungu ni vile viziwi na bubu visio tumia akili zao.'),
(1183, 8, 23, 'Na Mwenyezi Mungu angeli jua wema wowote kwao ange wasikilizisha, na lau ange wasikilizisha wangeli geuka wakapuuza.'),
(1184, 8, 24, 'Enyi mlio amini! Muitikieni Mwenyezi Mungu na Mtume anapo kuitieni jambo la kukupeni uzima wa milele. Na jueni kuwa Mwenyezi Mungu huingia kati ya mtu na moyo wake, na kwamba hakika kwake Yeye tu mtakusanywa.'),
(1185, 8, 25, 'Na jikingeni na Fitna ambayo haitowasibu walio dhulumu peke yao kati yenu. Na jueni kuwa Mwenyezi Mungu ni Mkali wa kuadhibu.'),
(1186, 8, 26, 'Na kumbukeni mlipo kuwa wachache, mkionekana wanyonge katika nchi, mnaogopa watu wasikunyakueni, naye akakupeni pahala pazuri pa kukaa, na akakutieni nguvu kwa nusura yake, na akakupeni riziki nzuri ili mpate kushukuru.'),
(1187, 8, 27, 'Enyi mlio amini! Msimfanyie khiana Mwenyezi Mungu na Mtume, wala msikhini amana zenu, nanyi mnajua.'),
(1188, 8, 28, 'Na jueni ya kwamba mali zenu na wana wenu ni Fitna (mtihani), na kwamba kwa Mwenyezi Mungu upo ujira mkubwa.'),
(1189, 8, 29, 'Enyi mlio amini! Mkimcha Mwenyezi Mungu atakupeni kipambanuo, na atakufutieni makosa, na atakusameheni, na Mwenyezi Mungu ni Mwenye fadhila kubwa.'),
(1190, 8, 30, 'Na walipo kupangia mpango walio kufuru wakufunge, au wakuuwe, au wakutoe. Wakapanga mipango yao, na Mwenyezi Mungu akapanga yake. Na Mwenyezi Mungu ndiye mbora wa mipango.'),
(1191, 8, 31, 'Na wanapo somewa Aya zetu, wao husema: Tumesikia. Na lau tungeli penda tunge sema kama haya. Haya si chochote ila hadithi za watu wa kale tu.'),
(1192, 8, 32, 'Na walipo sema: Ee Mwenyezi Mungu! Ikiwa haya ni kweli itokayo kwako basi tunyeshee mawe kutoka mbinguni, au tuletee adhabu yoyote iliyo chungu.'),
(1193, 8, 33, 'Na Mwenyezi Mungu hakuwa wa kuwaadhibu nawe umo pamoja nao, wala Mwenyezi Mungu si wa kuwaadhibu na hali ya kuwa wanaomba msamaha.'),
(1194, 8, 34, 'Lakini wana jambo gani hata Mwenyezi Mungu asiwaadhibu, na hali ya kuwa wanawazuilia watu Msikiti Mtakatifu? Wala wao hawakuwa ndio walinzi wake, bali walinzi wake hawakuwa ila wacha-Mngu tu. Lakini wengi katika wao hawajui.'),
(1195, 8, 35, 'Na haikuwa ibada yao kwenye hiyo Nyumba (Al Kaaba) ila ni kupiga miunzi na makofi. Basi onjeni adhabu kwa vile mlivyo kuwa mkikufuru.'),
(1196, 8, 36, 'Hakika wale walio kufuru hutoa mali yao ili kuzuilia Njia ya Mwenyezi Mungu. Basi watayatoa, kisha yatakuwa juu yao majuto, na kisha watashindwa. Na wale walio kufuru watakusanywa kwenye Jahannam.'),
(1197, 8, 37, 'Ili Mwenyezi Mungu apate kuwa pambanua walio waovu na walio wema, na kuwaweka waovu juu ya waovu wengine, na kuwarundika wote pamoja, na kuwatupa katika Jahannam. Hao ndio walio khasirika.'),
(1198, 8, 38, 'Waambie wale walio kufuru: Wakikoma watasamehewa yaliyo kwisha pita. Na wakiyarudia basi imekwisha pita mifano ya wa zamani.'),
(1199, 8, 39, 'Na piganeni nao mpaka isiwepo fitna na mateso, na Dini yote iwe kwa ajili ya Mwenyezi Mungu. Lakini wakiacha, basi hakika Mwenyezi Mungu anayaona wanayo yatenda.'),
(1200, 8, 40, 'Na kama wakigeuka basi jueni kwamba Mwenyezi Mungu ndiye Mola wenu, ni Mola Mwema na Msaidizi Mwema.'),
(1201, 8, 41, 'NA JUENI ya kwamba ngawira mnayo ipata, basi khums (sehemu moja katika tano) ni kwa ajili ya Mwenyezi Mungu na Mtume, na jamaa, na mayatima, na masikini, na wasafiri, ikiwa nyinyi mmemuamini Mwenyezi Mungu na tuliyo yateremsha kwa mja wetu siku ya kipambanuo, siku yalipo kutana majeshi mawili. Na Mwenyezi Mungu ni Muweza wa kila kitu.'),
(1202, 8, 42, 'Kumbukeni pale mlipo kuwa nyinyi kwenye ng\'ambo ya karibu ya bonde, na wao wakawa ng\'ambo ya mbali, na msafara ulipo kuwa chini yenu. Na ingeli kuwa mmeagana mngeli khitalifiana katika miadi. Lakini (mkakutana) ili Mwenyezi Mungu atimize jambo lilio kuwa lazima litendeke, kwa sababu aangamie wa kuangamia kwa dalili zilizo dhaahiri, na asalimike wa kusalimika kwa dalili zilizo dhaahiri. Na Mwenyezi Mungu ni Mwenye kusikia Mwenye kujua.'),
(1203, 8, 43, 'Kumbuka alipo kuonyesha usingizini mwako kwamba wao ni wachache - na lau angeli kuonyesha kuwa ni wengi, mngeli ingiwa na woga, na mngeli zozana katika jambo hilo. Lakini Mwenyezi Mungu kakuvueni. Hakika Yeye ni Mwenye kuyajua yalio vifuani.'),
(1204, 8, 44, 'Na mlipo kutana akakuonyesheni machoni mwenu kuwa wao ni wachache, na akakufanyeni nyinyi ni wachache machoni mwao, ili Mwenyezi Mungu atimize jambo lilio kuwa liwe. Na mambo yote hurejezwa kwa Mwenyezi Mungu.'),
(1205, 8, 45, 'Enyi mlio amini! Mkikutana na jeshi, basi kueni imara, na mkumbukeni Mwenyezi Mungu sana ili mpate kufanikiwa.'),
(1206, 8, 46, 'Na mt\'iini Mwenyezi Mungu na Mtume wake, wala msizozane mkaingiwa woga, na zikapotea nguvu zenu. Na subirini. Hakika Mwenyezi Mungu yu pamoja na wanao subiri.'),
(1207, 8, 47, 'Wala msiwe kama wale walio toka majumbani mwao kwa fakhari na kujionyesha kwa watu, na wakawazuilia watu Njia ya Mwenyezi Mungu. Na Mwenyezi Mungu ameyazunguka yote wanayo yafanya.'),
(1208, 8, 48, 'Na pale Shet\'ani alipo wapambia vitendo vyao, na akawaambia: Hapana watu wa kukushindeni hii leo, na hali mimi ni mlinzi wenu. Yalipo onana majeshi mawili, akarudi nyuma, na akasema: Mimi si pamoja nanyi. Mimi naona msiyo yaona. Mimi namwogopa Mwenyezi Mungu, na Mwenyezi Mungu ni mkali wa kuadhibu.'),
(1209, 8, 49, 'Na walipo sema wanaafiki, na wale wenye ugonjwa nyoyoni mwao: Watu hawa dini yao imewadanganya. Na Mwenye kumtegemea Mwenyezi Mungu basi hakika Mwenyezi Mungu ni Mwenye nguvu Ametukuka, na Mwenye hikima.'),
(1210, 8, 50, 'Na laiti ungeli ona Malaika wanapo wafisha wale walio kufuru wakiwapiga nyuso zao na migongo yao, na kuwaambia: Ionjeni adhabu ya Moto!'),
(1211, 8, 51, 'Hayo ni kwa sababu ya yale yaliyo kwisha tangulizwa na mikono yenu, na hakika Mwenyezi Mungu si mwenye kuwadhulumu waja.'),
(1212, 8, 52, 'Kama ada ya watu wa Firauni na walio kuwa kabla yao - walizikataa Ishara za Mwenyezi Mungu. Basi Mwenyezi Mungu aliwashika kwa sababu ya madhambi yao. Na hakika Mwenyezi Mungu ni Mwenye nguvu na mkali wa kuadhibu.'),
(1213, 8, 53, 'Hayo ni kwa sababu Mwenyezi Mungu habadilishi kabisa neema alizo waneemesha watu, mpaka wao wabadilishe yaliyomo ndani ya nafsi zao. Na hakika Mwwnyezi Mungu ni Mwenye kusikia, Mwenye kujua.'),
(1214, 8, 54, 'Ni kama ada ya watu wa Firauni na walio kabla yao - walizikanusha Ishara za Mola wao Mlezi, basi tukawateketeza kwa madhambi yao. Na tukawazamisha watu wa Firauni. Na wote walikuwa wenye kudhulumu.'),
(1215, 8, 55, 'Hakika vinyama vilio viovu kabisa mbele ya Mwenyezi Mungu ni wale wanao kufuru, basi hao hawaamini;'),
(1216, 8, 56, 'Wale ambao miongoni mwao umepatana nao ahadi, kisha wanavunja ahadi yao kila mara, wala hawamchi Mungu.'),
(1217, 8, 57, 'Basi ukiwakuta vitani wakimbize walio nyuma yao ili wapate kukumbuka.'),
(1218, 8, 58, 'Na ukichelea khiana kwa watu fulani basi watupilie ahadi yao kwa usawa. Hakika Mwenyezi Mungu hawapendi makhaini.'),
(1219, 8, 59, 'Wala wasidhanie wale walio kufuru kwamba wao wametangulia mbele. La, wao hawatashinda.'),
(1220, 8, 60, 'Basi waandalieni nguvu kama muwezavyo, na kwa farasi walio fungwa tayari-tayari, ili kuwatishia maadui wa Mwenyezi Mungu na maadui zenu, na wengineo ambao hamwajui, lakini Mwenyezi Mungu anawajua. Na mkitoa chochote katika Njia ya Mwenyezi Mungu mtarudishiwa, na wala nyinyi hamtadhulumiwa.'),
(1221, 8, 61, 'Na wakielekea amani nawe pia elekea, na mtegemee Mwenyezi Mungu. Hakika Yeye ndiye Mwenye kusikia Mwenye kujua.'),
(1222, 8, 62, 'Na wakitaka kukukhadaa basi Mwenyezi Mungu atakutosheleza. Kwani Yeye ndiye aliye kuunga mkono kwa nusura yake na kwa Waumini.'),
(1223, 8, 63, 'Na akaziunga nyoyo zao. Na lau wewe ungeli toa vyote viliomo duniani usingeli weza kuziunga nyoyo zao, lakini Mwenyezi Mungu ndiye aliye waunganisha. Hakika Yeye ndiye Mtukufu Mwenye nguvu na Mwenye hikima.'),
(1224, 8, 64, 'Ewe Nabii! Mwenyezi Mungu anakutoshelezea wewe na Waumini walio kufuata.'),
(1225, 8, 65, 'Ewe Nabii! Wahimize Waumini wende vitani. Wakiwapo kati yenu ishirini wanao subiri watawashinda mia mbili. Na wakiwapo kati yenu mia watawashinda elfu moja katika walio kufuru. Kwa sababu wao ni watu wasio fahamu.'),
(1226, 8, 66, 'Sasa Mwenyezi Mungu amekupunguzieni, na anajua kuwa upo udhaifu kwenu. Kwa hivyo wakiwa wapo watu mia moja kati yenu wenye kusubiri watawashinda mia mbili. Na wakiwapo elfu moja watawashinda elfu mbili, kwa idhini ya Mwenyezi Mungu. Na Mwenyezi Mungu yu pamoja na wenye kusubiri.'),
(1227, 8, 67, 'Haimfalii Nabii yeyote kuwa na mateka mpaka awe ameshinda baraabara katika nchi. Mnataka vitu vya dunia, na Mwenyezi Mungu anataka Akhera. Na Mwenyezi Mungu ni Mtukufu Mwenye nguvu Mwenye hikima.'),
(1228, 8, 68, 'Lau isingeli kuwa hukumu iliyo kwisha tangulia kutoka kwa Mwenyezi Mungu ingeli kupateni adhabu kubwa kwa vile mlivyo chukua.'),
(1229, 8, 69, 'Basi kuleni katika mlivyo teka, ni halali na vizuri, na mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye kusamehe na Mwenye kurehemu.'),
(1230, 8, 70, 'Ewe Nabii! Waambie mateka waliomo mikononi mwenu: Kama Mwenyezi akiona kheri yoyote nyoyoni mwenu atakupeni bora kuliko vilivyo chukuliwa kwenu, na atakusameheni. Na Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(1231, 8, 71, 'Ikiwa wanataka kukufanyia khiana, wao walikwisha mfanyia khiana Mwenyezi Mungu kabla yake, na Yeye akakuwezesha kuwashinda. Na Mwenyezi Mungu ni Mwenye kujua Mwenye hikima.'),
(1232, 8, 72, 'Hakika wale walio amini na wakahama na wakapigania Njia ya Mwenyezi Mungu kwa mali yao na nafsi zao, na wale walio toa mahala pa kukaa, na wakanusuru, hao ni marafiki na walinzi wao kwa wao. Na wale walio amini lakini hawakuhama, nyinyi hamna waajibu wa ulinzi kwao hata kidogo mpaka wahame. Lakini wakiomba msaada kwenu katika Dini, basi ni juu yenu kuwasaidia, isipo kuwa juu ya watu ambao yapo mapatano baina yenu na wao. Na Mwenyezi Mungu anayaona mnayo yatenda.'),
(1233, 8, 73, 'Na wale walio kufuru ni marafiki wenyewe kwa wenyewe. Msipo fanya hivi itakuwako fitna katika nchi na fisadi kubwa.'),
(1234, 8, 74, 'Na walio amini wakahama na wakapigana Jihadi katika Njia ya Mwenyezi Mungu, na walio toa mahala pa kukaa na wakanusuru, hao ndio Waumini wa kweli. Watapata msamaha wa Mwenyezi Mungu na kuruzukiwa kwema.'),
(1235, 8, 75, 'Na watakao amini baadaye na wakahajiri, na wakapigana Jihadi pamoja nanyi, basi hao ni katika nyinyi. Na jamaa wa nasaba wanastahikiana wenyewe kwa wenyewe zaidi katika Kitabu cha Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye kujua kila kitu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Huku ni kujitoa Mwenyezi Mungu na Mtume wake kwenye jukumu la maagano na mlio ahidiana nao katika washirikina.'),
(1237, 9, 2, 'Basi tembeeni katika nchi miezi mine, na jueni kwamba nyinyi hamwezi kumshinda Mwenyezi Mung, na kwamba Mwenyezi Mungu ndiye anaye wahizi makafiri.'),
(1238, 9, 3, 'Na ni tangazo kutokana na Mwenyezi Mungu na Mtume wake kwa wote siku ya Hija Kubwa kwamba Mwenyezi Mungu na Mtume wake hawana jukumu lolote kwa washirikina. Basi mkitubu itakuwa kheri kwenu, na mkigeuka basi jueni kuwa nyinyi hamumshindi Mwenyezi Mungu. Na wabashirie makafiri kuwa watapata adhabu chungu.'),
(1239, 9, 4, 'Isipo kuwa washirikina ambao mliahidiana nao kisha wasikupunguzieni chochote, wala hawakumsaidia yeyote dhidi yenu, basi hao watimizieni ahadi yao mpaka muda wao. Hakika Mwenyezi Mungu huwapenda wacha-Mungu.'),
(1240, 9, 5, 'Na ikisha miezi mitukufu basi wauweni washirikina popote mwakutapo, na washikeni na wazungukeni, na wavizieni katika kila njia. Lakini wakitubu, wakashika Sala, na wakatoa Zaka, basi waachilieni. Hakika Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(1241, 9, 6, 'Na ikiwa mmoja wapo katika washirikina akikuomba ulinzi, basi mpe ulinzi apate kusikia maneno ya Mwenyezi Mungu. Kisha mfikishe pahala pake pa amani. Haya ni kwa kuwa hao ni watu wasiojua kitu.'),
(1242, 9, 7, 'Itakuwaje iwepo ahadi na washirikina mbele ya Mwenyezi Mungu na mbele ya Mtume wake, ila wale mlio ahidiana nao kwenye Msikiti Mtakatifu? Basi maadamu wanakwenda nanyi sawa, nanyi pia nendeni sawa nao. Hakika Mwenyezi Mungu huwapenda wachamngu'),
(1243, 9, 8, 'Itakuwaje! Nao wakikushindeni hawatazami kwenu udugu wala ahadi. Wanakufurahisheni kwa midomo yao tu, na nyoyo zao zinakataa hayo. Na wengi wao ni wapotovu.'),
(1244, 9, 9, 'Wamenunua thamani ndogo kwa Ishara za Mwenyezi Mungu, na wakazuilia watu na Njia yake. Hakika hao ni maovu waliyo kuwa wakiyatenda.'),
(1245, 9, 10, 'Hawatazami kwa Muumini udugu wala ahadi; basi hao ndio warukao mipaka.'),
(1246, 9, 11, 'Basi wakitubu na wakashika Sala na wakatoa Zaka, basi ni ndugu zenu katika Dini. Na tunazichambua Aya kwa watu wajuao.'),
(1247, 9, 12, 'Na wakivunja viapo vyao baada ya kuahidi kwao, na wakatukana Dini yenu, basi piganeni na waongozi wa ukafiri. Hakika hao hawana viapo vya kweli. Huenda (kwa hayo) wakaacha.'),
(1248, 9, 13, 'Je, hamtapigana na watu walio vunja viapo vyao na wakawa na hamu ya kumfukuza Mtume, nao ndio walio kuanzeni mara ya kwanza? Je, mnawaogopa? Basi Mwenyezi Mungu anastahiki zaidi mumwogope, ikiwa nyinyi ni Waumini.'),
(1249, 9, 14, 'Piganeni nao, Mwenyezi Mungu awaadhibu kwa mikono yenu, na awahizi na akunusuruni muwashinde, na avipoze vifua vya kaumu ya Waumini,'),
(1250, 9, 15, 'Na aondoe hasira ya nyoyo zao. Na Mwenyezi Mungu humkubalia toba ya amtakaye. Na Mwenyezi Mungu ni Mwenye kujua Mwenye hikima.'),
(1251, 9, 16, 'Je, mlidhani kuwa mtaachwa tu, bila ya Mwenyezi Mungu kuwajuulisha wale walio pigana Jihadi kati yenu, na wala hawakumfanya mwendani wao isipo kuwa Mwenyezi Mungu na Mtume wake na Waumini wenziwe? Na Mwenyezi Mungu anazo khabari za yote mnayo yatenda.'),
(1252, 9, 17, 'Haiwi kuwa makafiri ndio waamirishe misikiti ya Mwenyezi Mungu, hali wanajishuhudishia wenyewe ukafiri. Hao ndio ambao vitendo vyao vimeharibika, na katika Moto watadumu.'),
(1253, 9, 18, 'Hakika wanao amirisha misikiti ya Mwenyezi Mungu ni wale wanao muamini Mwenyezi Mungu, na Siku ya Mwisho, na wakashika Sala, na wakatoa Zaka, na wala hawamchi ila Mwenyezi Mungu. Basi huenda hao wakawa katika waongofu.'),
(1254, 9, 19, 'Je, mnafanya kuwanywesha maji Mahujaji na kuamirisha Msikiti Mtakatifu ni sawa na mwenye kumuamini Mwenyezi Mungu, na Siku ya Mwisho, na akapigana Jihadi katika Njia ya Mwenyezi Mungu? Hawawi sawa mbele ya Mwenyezi Mungu. Na Mwenyezi Mungu hawaongoi watu madhaalimu.'),
(1255, 9, 20, 'Wale walio amini, na wakahama, na wakapigana Jihadi katika Njia ya Mwenyezi Mungu kwa mali yao na nafsi zao, hao wana cheo kikubwa zaidi mbele ya Mwenyezi Mungu. Na hao ndio wenye kufuzu.'),
(1256, 9, 21, 'Mola wao Mlezi anawabashiria rehema zitokazo kwake, na radhi, na Bustani ambazo humo watapata neema za kudumu.'),
(1257, 9, 22, 'Watadumu humo milele. Hakika kwa Mwenyezi Mungu yapo malipo makubwa.'),
(1258, 9, 23, 'Enyi mlio amini! Msiwafanye baba zenu na ndugu zenu kuwa vipenzi vyenu ikiwa wanastahabu ukafiri kuliko Imani. Na katika nyinyi atakaye wafanya hao ndio vipenzi vyake, basi hao ndio madhaalimu.'),
(1259, 9, 24, 'Sema: Ikiwa baba zenu, na wenenu, na ndugu zenu, na wake zenu, na jamaa zenu, na mali mliyo yachuma, na biashara mnazo ogopa kuharibika, na majumba mnayo yapenda, ni vipenzi zaidi kwenu kuliko Mwenyezi Mungu na Mtume wake na Jihadi katika Njia yake, basi ngojeni mpaka Mwenyezi Mungu alete amri yake. Na Mwenyezi Mungu hawaongoi watu wapotovu.'),
(1260, 9, 25, 'Hakika Mwenyezi Mungu amekunusuruni katika mapigano mengi, na siku ya Hunayni ambapo wingi wenu ulikupandisheni kichwa, lakini haukukufaeni kitu. Na ardhi ikawa dhiki kwenu juu ya upana wake. Kisha mkageuka mkarudi nyuma.'),
(1261, 9, 26, 'Kisha Mwenyezi Mungu akateremsha utulivu wake juu ya Mtume wake na juu ya Waumini. Na akateremsha majeshi ambayo hamkuyaona, na akawaadhibu wale walio kufuru. Na hayo ndiyo malipo ya makafiri.'),
(1262, 9, 27, 'Kisha baada ya hayo Mwenyezi Mungu atawasamehe awatakao. Na Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(1263, 9, 28, 'Enyi mlio amini! Hakika washirikina ni najsi, kwa hivyo wasiukaribie Msikiti Mtakatifu baada ya mwaka wao huu. Na ikiwa mnakhofia umasikini, basi Mwenyezi Mungu atakutajirisheni kwa fadhila yake akipenda. Hakika Mwenyezi Mungu ni Mwenye kujua Mwenye hikima.'),
(1264, 9, 29, 'Piganeni na wasio muamini Mwenyezi Mungu wala Siku ya Mwisho, wala hawaharimishi alivyo harimisha Mwenyezi Mungu na Mtume, wala hawashiki Dini ya Haki, miongoni mwa walio pewa Kitabu, mpaka watoe kodi kwa khiari yao, hali wamet\'ii.'),
(1265, 9, 30, 'Na Mayahudi wanasema: Uzeir ni mwana wa Mungu. Na Wakristo wanasema: Masihi ni mwana wa Mungu. Hiyo ndiyo kauli yao kwa vinywa vyao. Wanayaiga maneno ya walio kufuru kabla yao. Mwenyezi Mungu awaangamize! Wanageuzwa namna gani hawa!'),
(1266, 9, 31, 'Wamewafanya makuhani wao na wamonaki wao kuwa ni marabi badala ya Mwenyezi Mungu, na pia Masihi bin Maryamu. Na wala hawakuamrishwa isipo kuwa wamuabudu Mungu Mmoja, hapana mungu ila Yeye. Subhanahu, Ametakasika na hayo wanayo mshirikisha nayo.'),
(1267, 9, 32, 'Wanataka kuizima Nuru ya Mwenyezi Mungu kwa vinywa vyao, na Mwenyezi Mungu anakataa ila aitimize Nuru yake ijapo kuwa makafiri watachukia.'),
(1268, 9, 33, 'Yeye ndiye aliye mtuma Mtume wake kwa uwongofu na Dini ya Haki ipate kushinda dini zote, ijapo kuwa washirikina watachukia.'),
(1269, 9, 34, 'Enyi mlio amini! Hakika wengi katika makuhani na wamonaki wanakula mali za watu kwa baat\'ili na wanazuilia Njia ya Mwenyezi Mungu. Na wanao kusanya dhahabu na fedha, wala hawazitumii katika Njia ya Mwenyezi Mungu, wabashirie khabari ya adhabu iliyo chungu.'),
(1270, 9, 35, 'Siku zitapo tiwa moto katika Moto wa Jahannamu, na kwazo vikachomwa vipaji vya nyuso zao na mbavu zao na migongo yao, wakaambiwa: Haya ndiyo mlio jilimbikia nafsi zenu, basi onjeni mliyo kuwa mkilimbika.'),
(1271, 9, 36, 'Hakika idadi ya miezi kwa Mwenyezi Mungu ni miezi kumi na mbili katika hukumu ya Mwenyezi Mungu tangu alipo umba mbingu na ardhi. Katika hiyo iko mine mitakatifu. Hiyo ndiyo Dini iliyo sawa. Basi msidhulumu nafsi zenu humo. Na piganeni na washirikina wote kama wao wnavyo pigana nanyi nyote. Na jueni kuwa Mwenyezi Mungu yu pamoja na wachamngu.'),
(1272, 9, 37, 'Bila ya shaka kuakhirisha (miezi) ni kuzidi katika kukufuru; kwa hayo hupotezwa walio kufuru. Wanauhalalisha mwaka mmoja na kuharimisha mwaka mwingine, ili wafanye kuwa sawa idadi ya ile (miezi) aliyo itukuza Mwenyezi Mungu. Kwa hivyo huhalalisha alivyo viharimisha Mwenyezi Mungu. Wamepambiwa ubaya wa vitendo vyao hivi. Na Mwenyezi Mungu hawaongoi watu makafiri.'),
(1273, 9, 38, 'Enyi mlio amini! Mna nini mnapo ambiwa: Nendeni katika Njia ya Mwenyezi Mungu, mnajitia uzito katika ardhi? Je, mmeridhia maisha ya dunia kuliko ya Akhera? Lakini starehe za maisha ya dunia kulinganisha na ya Akhera ni chache.'),
(1274, 9, 39, 'Kama hamwendi atakuadhibuni adhabu chungu, na atawaleta watu wengine, wala nyinyi hamtamdhuru chochote. Na Mwenyezi Mungu ni Muweza wa kila kitu.'),
(1275, 9, 40, 'Ikiwa nyinyi hamtamnusuru Mtume, basi Mwenyezi Mungu alikwisha mnusuru walipo mtoa walio kufuru, naye ni wa pili katika wawili walipo kuwa katika pango, naye akamwambia sahibu yake: Usihuzunike. Hakika Mwenyezi Mungu yu pamoja nasi. Mwenyezi Mungu akamteremshia utulivu wake, na akamuunga mkono kwa majeshi msiyo yaona, na akalifanya neno la walio kufuru kuwa chini, na Neno la Mwenyezi Mungu kuwa ndilo juu. Na Mwenyezi Mungu ndiye Mwenye nguvu Mwenye hikima.'),
(1276, 9, 41, 'Nendeni mtoke, mkiwa wepesi na wazito, na piganeni Jihadi kwa mali yenu na nafsi zenu katika Njia ya Mwenyezi Mungu. Haya ni kheri kwenu mkiwa mnajua.'),
(1277, 9, 42, 'Na lau ingeli kuwa ipo faida ya papo kwa papo, na safari yenyewe ni fupi, wangeli kufuata. Lakini wameona ni mbali na kuna mashaka. Nao wataapa kwa Mwenyezi Mungu: Tungeli weza bila ya shaka tungeli toka pamoja nanyi. Wanaziangamiza nafsi zao. Na Mwenyezi Mungu anajua kuwa hakika hao ni waongo.'),
(1278, 9, 43, 'Mwenyezi Mungu amekusamehe! Kwa nini ukawapa ruhusa kabla ya kubainikia kwako wanao sema kweli, na ukawajua waongo?'),
(1279, 9, 44, 'Hawatakuomba ruhusa wale wanao muamini Mwenyezi Mungu na Siku ya Mwisho, wasende kupigana Jihadi kwa mali yao na nafsi zao. Na Mwenyezi Mungu ni Mwenye kuwajua wachamngu.'),
(1280, 9, 45, 'Wasio muamini Mwenyezi Mungu na Siku ya Mwisho na nyoyo zao zikatia shaka, hao tu ndio wanao kutaka ruhusa. Basi hao wanataradadi katika shaka zao.'),
(1281, 9, 46, 'Na ingeli kuwa kweli walitaka kutoka bila ya shaka wangeli jiandalia maandalio, lakini Mwenyezi Mungu kachukia kutoka kwao, na kwa hivyo akawazuia na ikasemwa: Kaeni pamoja na wanao kaa!'),
(1282, 9, 47, 'Lau wangeli toka nanyi wasingeli kuzidishieni ila mchafuko, na wange tanga tanga kati yenu kukutilieni fitna. Na miongoni mwenu wapo wanao wasikiliza. Na Mwenyezi Mungu anawajua madhaalimu.'),
(1283, 9, 48, 'Tangu zamani walitaka kukutilieni fitna, na wakakupindulia mambo juu chini, mpaka ikaja Haki na ikadhihirika amri ya Mwenyezi Mungu, na wao wamechukia.'),
(1284, 9, 49, 'Na miongoni mwao wapo wanao sema: Niruhusu wala usinitie katika fitina. Kwa yakini wao hivyo wamekwisha tumbukia katika fitina. Na hakika Jahannamu imewazunguka.'),
(1285, 9, 50, 'Ukikupata wema unawachukiza, na ukikusibu msiba, wao husema: Sisi tuliangalia mambo yetu vizuri tangu kwanza. Na hugeuka kwenda zao nao wamefurahi.'),
(1286, 9, 51, 'Sema: Halitusibu ila alilo tuandikia Mwenyezi Mungu. Yeye ndiye Mola wetu Mlinzi. Na Waumini wamtegemee Mwenyezi Mungu tu!'),
(1287, 9, 52, 'Sema: Hivyo mnatutazamia litupate lolote isipo kuwa moja katika mema mawili? Na sisi tunakutazamieni kuwa Mwenyezi Mungu akufikishieni adhabu itokayo kwake, au kutokana na mikono yetu. Basi ngojeni, nasi tunangoja pamoja nanyi.'),
(1288, 9, 53, 'Sema: Toeni mkipenda msipende. Hakitopokelewa kitu kwenu, kwani nyinyi ni watu wapotovu.'),
(1289, 9, 54, 'Na hawakuzuiliwa kukubaliwa michango yao ila kwa kuwa walimkataa Mwenyezi Mungu na Mtume wake, wala hawaji kwenye Sala ila kwa uvivu, wala hawatoi michango ila nao wamechukia.'),
(1290, 9, 55, 'Yasikufurahishe mali yao wala wana wao. Hakika Mwenyezi Mungu anataka kuwaadhibu kwayo hapa duniani, na zitoke roho zao na hali wao ni makafiri.'),
(1291, 9, 56, 'Na wanaapa kwa Mwenyezi Mungu kwamba wao ni katika nyinyi, wala wao si katika nyinyi. Lakini ni watu wanao ogopa.'),
(1292, 9, 57, 'Lau kama wangeli pata pa kukimbilia au mapango au pahala pa kuingia basi wangeli fyatuka mbio kuelekea huko.'),
(1293, 9, 58, 'Na miongoni mwao wapo wanao kusengenya katika kugawa sadaka. Wanapo pewa wao huridhika. Na wakito pewa hukasirika.'),
(1294, 9, 59, 'Na laiti wao wangeli ridhia kile alicho wapa Mwenyezi Mungu na Mtume wake, na wakasema: Mwenyezi Mungu anatutosheleza, Mwenyezi Mungu atatupa katika fadhila yake - na pia Mtume wake; hakika sisi ni wenye kumtaka Mwenyezi Mungu!'),
(1295, 9, 60, 'Wa kupewa sadaka ni mafakiri, na masikini, na wanao zitumikia, na wa kutiwa nguvu nyoyo zao, na katika kukomboa watumwa, na wenye madeni, na katika Njia ya Mwenyezi Mungu, na wasafiri. Huu ni waajibu ulio faridhiwa na Mwenyezi Mungu. Na Mwenyezi Mungu ni Mwenye kujua Mwenye hikima.'),
(1296, 9, 61, 'Na miongoni mwao wapo wanao muudhi Nabii na kusema: Yeye huyu ni sikio tu. Sema: Basi ni sikio la kheri kwenu. Anamuamini Mwenyezi Mungu, na ana imani na Waumini, naye ni rehema kwa wanao amini miongoni mwenu. Na wanao muudhi Mtume wa Mwenyezi Mungu watapata adhabu chungu.'),
(1297, 9, 62, 'Wanakuapieni Mwenyezi Mungu ili kukuridhisheni nyinyi, hali ya kuwa wanao stahiki zaidi kuridhishwa ni Mwenyezi Mungu na Mtume wake, lau kama wao ni Waumini.'),
(1298, 9, 63, 'Je, hawajui ya kwamba anaye shindana na Mwenyezi Mungu na Mtume wake, basi huyo atapata Moto wa Jahannamu adumu humo? Hiyo ndiyo hizaya kubwa.'),
(1299, 9, 64, 'Wanaafiki wanaogopa isije teremshwa Sura itakayo watajia yaliyomo katika nyoyo zao. Sema: Fanyeni mzaha! Hakika Mwenyezi Mungu atayatoa nje hayo mnayo yaogopa.'),
(1300, 9, 65, 'Na ukiwauliza, wanasema: Sisi tulikuwa tukipiga porojo na kucheza tu. Sema: Mlikuwa mkimfanyia maskhara Mwenyezi Mungu na Ishara zake na Mtume wake?'),
(1301, 9, 66, 'Msitoe udhuru; mmekwisha kufuru baada ya kuamini kwenu! Tukilisamehe kundi moja kati yenu, tutaliadhibu kundi jingine kwa kuwa wao ni wakosefu.'),
(1302, 9, 67, 'Wanaume wanaafiki na wanawake wanaafiki, wote ni hali moja. Huamrisha maovu na huyakataza mema, na huifumba mikono yao. Wamemsahau Mwenyezi Mungu, basi na Yeye pia amewasahau. Hakika wanaafiki ndio wapotofu.'),
(1303, 9, 68, 'Mwenyezi Mungu amewaahidi wanaafiki wanaume na wanaafiki wanawake na makafiri Moto wa Jahannamu kudumu humo. Hayo yanawatosha, na Mwenyezi Mungu amewalaani; nao wana adhabu milele.'),
(1304, 9, 69, 'Ni kama wale walio kuwa kabla yenu. Walikuwa wana nguvu na mali na watoto zaidi kuliko nyinyi. Basi walistarehea fungu lao, na nyinyi mnastarehea fungu lenu, kama walivyo starehe kwa fungu lao wale walio kuwa kabla yenu. Na nyinyi mkazama katika maovu kama wao walivyo zama. Hao ndio ambao vitendo vyao vimeharibika katika dunia na Akhera; na hao ndio walio khasiri.'),
(1305, 9, 70, 'Je, hazikuwafikia khabari za walio kuwa kabla yao - kaumu ya Nuhu, na A\'ad, na Thamud, na kaumu ya Ibrahim, na watu wa Madyana, na miji iliyo pinduliwa chini juu? Mitume wao waliwafikia kwa hoja zilizo wazi. Basi Mwenyezi Mungu hakuwa mwenye kuwadhulumu, walakini wao walikuwa wakijidhulumu wenyewe.'),
(1306, 9, 71, 'Na Waumini wanaume na Waumini wanawake wao kwa wao ni marafiki walinzi. Huamrisha mema na hukataza maovu, na hushika Sala, na hutoa Zaka, na humt\'ii Mwenyezi Mungu na Mtume wake. Hao Mwenyezi Mungu atawarehemu. Hakika Mwenyezi Mungu ni Mtukufu Mwenye nguvu, Mwenye hikima.'),
(1307, 9, 72, 'Mwenyezi Mungu amewaahidi Waumini wanaume na Waumini wanawake Bustani zipitazo mito kati yake, wadumu humo. Na makaazi mema katika Bustani za kudumu, na Radhi za Mwenyezi Mungu ndio kubwa kuliko yote. Huko ndiko kufuzu kukubwa.'),
(1308, 9, 73, 'Ewe Nabii! Pambana na makafiri na wanaafiki, na wakazanie. Na makaazi yao ni Jahannamu, na huo ndio mwisho mbaya.'),
(1309, 9, 74, 'Wanaapa kwa Mwenyezi Mungu kuwa hawakusema; nao wamekwisha sema neno la ukafiri, na wakakufuru baada ya kusilimu kwao. Na wakajihimu kufanya ambayo hawakuweza kuyafikia. Na hawakuchukia ila kuwa Mwenyezi Mungu na Mtume wake amewatajirisha kutokana na fadhila zake. Wakitubu itakuwa kheri kwao, na wakigeuka Mwenyezi Mungu atawaadhibu adhabu chungu duniani na Akhera. Na wala hawana katika ardhi mlinzi wala wa kuwanusuru.'),
(1310, 9, 75, 'Na miongoni mwao wapo walio muahidi Mwenyezi Mungu kwa kusema: Akitupa katika fadhila yake hapana shaka tutatoa sadaka, na tutakuwa katika watendao mema.'),
(1311, 9, 76, 'Alipo wapa katika fadhila yake wakaifanyia ubakhili na wakageuka, na huku wakipuuza.'),
(1312, 9, 77, 'Basi akawalipa unaafiki kuutia nyoyoni mwao mpaka Siku watapo kutana naye, kwa sababu ya kuwa walimkhalifu Mwenyezi Mungu katika yale waliyo muahidi, na kwa sababu ya kusema kwao uwongo.'),
(1313, 9, 78, 'Je, hawajui kwamba Mwenyezi Mungu anajua siri zao na minong\'ono yao, na kwamba Mwenyezi Mungu ni Mwenye kuyajua sana ya ghaibu?'),
(1314, 9, 79, 'Wale wanao wabeuwa Waumini wanao toa sadaka nyingi na wasio nacho cha kutoa ila kadri ya juhudi yao, basi huwakejeli. Mwenyezi Mungu atawakejeli wao! Nao watapata adhabu chungu!'),
(1315, 9, 80, 'Waombee msamaha au usiwaombee. Hata ukiwaombea msamaha mara sabiini, Mwenyezi Mungu hatawasamehe. Hayo ni kwa sababu ya kuwa wao wamemkataa Mwenyezi Mungu na Mtume wake. Na Mwenyezi Mungu hawaongoi watu wapotofu.'),
(1316, 9, 81, 'Walifurahi walio achwa nyuma kwa kule kubakia kwao nyuma na kumuacha Mtume wa Mwenyezi Mungu. Na walichukia kupigana Jihadi kwa mali zao na nafsi zao, na wakasema: Msitoke nje katika joto! Sema: Moto wa Jahannamu una joto zaidi, laiti wangeli fahamu!'),
(1317, 9, 82, 'Basi nawacheke kidogo; watalia sana. Hayo ni malipo ya yale waliyo kuwa wakiyachuma.'),
(1318, 9, 83, 'Basi Mwenyezi Mungu akikurudisha kwenye kikundi kimoja miongoni mwao - na wakakutaka idhini ya kutoka - sema: Nyinyi hamtatoka pamoja nami kabisa, wala hamtapigana na adui pamoja nami. Nyinyi mliridhia kukaa nyuma mara ya kwanza, basi kaeni pamoja na hao watakao bakia nyuma.'),
(1319, 9, 84, 'Wala usimsalie kabisa yeyote katika wao akifa, wala usisimame kaburini kwake. Hakika hao wamemkataa Mwenyezi Mungu na Mtume wake, na wakafa na hali ni wapotofu.'),
(1320, 9, 85, 'Wala zisikushitue mali zao na watoto wao. Mwenyezi Mungu anataka kuwaadhibu kwa hayo katika dunia; na zitoke roho zao na hali wao ni makafiri.'),
(1321, 9, 86, 'Na inapo teremshwa Sura isemayo: Muaminini Mwenyezi Mungu na piganeni Jihadi pamoja na Mtume wake, wale wenye nguvu miongoni mwao hukutaka ruhusa na husema: Tuache tuwe pamoja na wanao kaa nyuma!'),
(1322, 9, 87, 'Wameridhia kuwa pamoja na wabakiao nyuma, na nyoyo zao zikapigwa muhuri; kwa hivyo hawafahamu.'),
(1323, 9, 88, 'Lakini Mtume na wale walio amini pamoja naye waliwania Jihadi kwa mali zao na nafsi zao. Hao ndio watakao pata kheri nyingi, na hao ndio wenye kufanikiwa.'),
(1324, 9, 89, 'Mwenyezi Mungu amewatengenezea Bustani zipitazo mito kati yake, wadumu humo. Huko ndiko kufuzu kukubwa.'),
(1325, 9, 90, 'Na walikuja wenye kutoa udhuru katika Mabedui ili wapewe ruhusa, na wakakaa wale walio mwambia Mwenyezi Mungu na Mtume wake uwongo. Itawafika walio kufuru katika wao adhabu chungu.'),
(1326, 9, 91, 'Hapana lawama kwa wanyonge, wagonjwa, na wasio pata cha kutumia, maadamu wanamsafia niya Mwenyezi Mungu na Mtume wake. Hapana njia ya kuwalaumu wanao fanya wema. Na Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(1327, 9, 92, 'Wala wale walio kujia ili uwachukue ukasema: Sina kipando cha kukuchukueni. Tena wakarudi na macho yao yanamiminika machozi kwa huzuni ya kukosa cha kutoa.'),
(1328, 9, 93, 'Ipo njia ya kuwalaumu wale wanao kuomba ruhusa wasende vitani na hali wao ni matajiri. Wameridhia kuwa pamoja na wanao bakia nyuma. Na Mwenyezi Mungu amewapiga muhuri juu ya nyoyo zao. Kwa hivyo hawajui.'),
(1329, 9, 94, 'WATAKUTOLEENI UDHURU mtapo warudia. Sema: Msitoe udhuru; hatukuaminini. Mwenyezi Mungu amekwisha tueleza khabari zenu. Na Mwenyezi Mungu na Mtume wake wataviona vitendo vyenu. Kisha mtarudishwa kwa Mjuzi wa ya siri na ya dhaahiri; naye atakuambieni mliyo kuwa mkiyatenda.'),
(1330, 9, 95, 'Watakuapieni kwa Mwenyezi Mungu mtakapo rudi kwao ili muwaachilie mbali. Basi waachilieni mbali. Hakika hao ni najsi, na makaazi yao ni Jahannamu, kuwa ni malipo kwa waliyo kuwa wakiyachuma.'),
(1331, 9, 96, 'Wanakuapieni ili muwe radhi nao. Basi hata mkiwa radhi nao nyiye, Mwenyezi Mungu hawi radhi na watu wapotofu.'),
(1332, 9, 97, 'Mabedui wamezidi sana katika ukafiri na unaafiki, na wameelekea zaidi kutojua mipaka ya aliyo yateremsha Mwenyezi Mungu juu ya Mtume wake. Na Mwenyezi Mungu ni Mjuzi Mwenye hikima.'),
(1333, 9, 98, 'Na katika Mabedui hao wapo wanao fikiri kuwa wanayo yatoa ni gharama ya bure, na wanakungojeleeni mambo yakugeukieni. Mageuko maovu yatakuwa juu yao! Na Mwenyezi Mungu ni Mwenye kusikia Mwenye kujua.'),
(1334, 9, 99, 'Na katika Mabedui wapo wanao muamini Mwenyezi Mungu na Siku ya Mwisho, na wanaitakidi kuwa wanayo yatoa ndiyo ya kuwasogeza kwa Mwenyezi Mungu na ya kuwapatia dua za Mtume. Naam! Hayo ni kweli mambo ya kuwasogeza. Mwenyezi Mungu atawaingiza katika rehema yake. Hakika Mwenyezi Mungu ni Mwenye maghfira Mwenye kurehemu.'),
(1335, 9, 100, 'Na wale walio tangulia, wa kwanza, katika Wahajiri na Ansari, na walio wafuata kwa wema, Mwenyezi Mungu ameridhika nao, na wao wameridhika naye; na amewaandalia Bustani zipitazo mito kati yake, wadumu humo milele. Huko ndiko kufuzu kukubwa.'),
(1336, 9, 101, 'Na katika mabedui walio jirani zenu wamo wanaafiki; na katika wenyeji wa Madina pia wapo walio bobea katika unaafiki. Wewe huwajui. Sisi tunawajua. Tutawaadhibu mara mbili, kisha watarudishwa kwenye adhabu kubwa.'),
(1337, 9, 102, 'Na wengine walikiri dhambi zao, wakachanganya vitendo vyema na vingine viovu. Asaa Mwenyezi Mungu akapokea toba zao. Hakika Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(1338, 9, 103, 'Chukua sadaka katika mali zao, uwasafishe na uwatakase kwazo, na uwaombee rehema. Hakika maombi yako ni utulivu kwao. Na Mwenyezi Mungu ni Mwenye kusikia Mwenye kujua.'),
(1339, 9, 104, 'Je! Hawajui ya kwamba Mwenyezi Mungu anapokea toba ya waja wake, na anazikubali sadaka, na kwamba Mwenyezi Mungu ni Mwingi wa kupokea toba na Mwenye kurehemu?'),
(1340, 9, 105, 'Na sema: Tendeni vitendo. Na Mwenyezi Mungu, na Mtume wake, na Waumini wataviona vitendo vyenu. Na mtarudishwa kwa Mwenye kujua siri na dhaahiri; naye atakuambieni mliyo kuwa mkiyatenda.'),
(1341, 9, 106, 'Na wapo wengine wanangojea amri ya Mwenyezi Mungu - ama atawaadhibu au atawasamehe. Na Mwenyezi Mungu ni Mwenye kujua Mwenye hikima.'),
(1342, 9, 107, 'Na wapo walio jenga msikiti kwa ajili ya madhara na ukafiri na kuwafarikisha Waumini, na pa kuvizia kwa walio mpiga vita Mwenyezi Mungu na Mtume wake hapo kabla. Na bila ya shaka wataapa kwamba: Hatukukusudia ila wema tu. Na Mwenyezi Mungu anashuhudia kuwa wao ni waongo.'),
(1343, 9, 108, 'Usisimame kwenye msikiti huo kabisa. Msikiti ulio jengwa juu ya msingi wa uchamngu tangu siku ya mwanzo unastahiki zaidi wewe usimame ndani yake. Humo wamo watu wanao penda kujitakasa. Na Mwenyezi Mungu anawapenda wanao jitakasa.'),
(1344, 9, 109, 'Je! Mwenye kuweka msingi wa jengo lake juu ya kumcha Mwenyezi Mungu na radhi zake ni bora au mwenye kuweka msingi wa jengo lake juu ya ukingo wa shimo linalo burugunyika, na likaburugunyika naye katika Moto wa Jahannamu? Na Mwenyezi Mungu hawaongoi watu madhaalimu.'),
(1345, 9, 110, 'Na jengo lao hilo walilo lijenga litakuwa sababu ya kutia wasiwasi nyoyoni mwao mpaka nyoyo zao hizo zikatike vipande vipande. Na Mwenyezi Mungu ni Mwenye kujua Mwenye hikima.'),
(1346, 9, 111, 'Hakika Mwenyezi Mungu amenunua kwa Waumini nafsi zao na mali zao kwa kuwa wao watapata Pepo. Wanapigana katika Njia ya Mwenyezi Mungu - wanauwa na wanauwawa. Hii ni ahadi aliyo jilazimisha kwa Haki katika Taurati na Injili na Qur\'ani. Na nani atimizae ahadi kuliko Mwenyezi Mungu? Basi furahini kwa biashara yenu mliyo fanya naye. Na huko ndiko kufuzu kukubwa,'),
(1347, 9, 112, '(Kwa) wanao tubia, wanao abudu, wanao himidi, wanao kimbilia kheri, wanao rukuu, wanao sujudu, wanao amrisha mema na wanao kataza maovu, na wanao linda mipaka ya Mwenyezi Mungu. Na wabashirie Waumini.'),
(1348, 9, 113, 'Haimpasii Nabii na wale walio amini kuwatakia msamaha washirikina, ijapo kuwa ni jamaa zao, baada ya kwisha bainika kuwa hao ni watu wa Motoni.'),
(1349, 9, 114, 'Wala haikuwa Ibrahim kumtakia msamaha baba yake ila kwa sababu wa ahadi aliyo fanya naye. Lakini ilipo mbainikia kwamba yeye ni adui ya Mwenyezi Mungu, alijiepusha naye. Hakika Ibrahim alikuwa mwingi wa kuomba, mnyenyekevu, mvumilivu.'),
(1350, 9, 115, 'Na haiwi kwa Mwenyezi Mungu kuwahisabu watu kuwa wamepotea baada ya kwisha waongoza mpaka awabainishie ya kujiepusha nayo. Hakika Mwenyezi Mungu ni Mwenye kujua kila kitu.'),
(1351, 9, 116, 'Hakika Mwenyezi Mungu anao ufalme wa mbingu na ardhi; huhuisha na hufisha. Nanyi hamna Mlinzi wala Msaidizi isipo kuwa Mwenyezi Mungu.'),
(1352, 9, 117, 'Mwenyezi Mungu amekwisha pokea toba ya Nabii na Wahajiri na Ansari walio mfuata katika saa ya dhiki, pale baada ya kukaribia nyoyo za baadhi yao kugeuka. Basi akapokea toba yao, kwani hakika Yeye kwao wao ni Mpole na ni Mwenye kuwarehemu.'),
(1353, 9, 118, 'Na pia wale watatu walio achwa nyuma hata dunia wakaiona dhiki juu ya ukunjufu wake, na nafsi zao zikabanika, na wakayakinika kuwa hapana pa kumkimbia Mwenyezi Mungu isipo kuwa kwake Yeye. Kisha akawaelekea kwa rehema yake ili nao waendelee kutubu. Hakika Mwenyezi Mungu ndiye Mwenye kupokea toba na Mwenye kurehemu.'),
(1354, 9, 119, 'Enyi mlio amini! Mcheni Mwenyezi Mungu, na kuweni pamoja na wakweli.'),
(1355, 9, 120, 'Haiwafalii watu wa Madina na Mabedui walio jirani zao kubakia nyuma wasitoke na Mtume, wala kujipendelea nafsi zao kuliko yeye. Hayo ni kwa kuwa hakiwapati kiu, wala machofu, wala njaa, kwa ajili ya Njia ya Mwenyezi Mungu, wala hawendi mahali panapo waghadhibisha makafiri, wala hakiwapati chochote kutokana na maadui, ila huandikiwa kuwa ni kitendo chema. Hakika Mwenyezi Mungu haupotezi ujira wa wanao fanya mema.'),
(1356, 9, 121, 'Wala hawatoi cha kutoa kidogo wala kikubwa, wala hawalivuki bonde, ila huandikiwa, ili Mwenyezi Mungu awalipe bora ya waliyo kuwa wakiya- tenda.'),
(1357, 9, 122, 'Wala haiwafalii Waumini kutoka wote. Lakini kwa nini hawatoki baadhi katika kila kundi miongoni mwao, wakajifunze vyema Dini, na kisha waje kuwaonya wenzao watakapo warejea, ili wapate kujihadharisha?'),
(1358, 9, 123, 'Enyi mlio amini! Piganeni na wale makafiri walio karibu yenu, na wakute ugumu kwenu. Na jueni ya kwamba Mwenyezi Mungu yu pamoja na wacha- Mngu.'),
(1359, 9, 124, 'Na inapo teremshwa Sura wapo miongoni mwao wasemao: Ni nani miongoni mwenu Sura hii imemzidishia Imani? Ama wale walio amini inawazidishia Imani, nao wanafurahi.'),
(1360, 9, 125, 'Ama wale wenye maradhi katika nyoyo zao, basi inawazidishia uovu juu ya uovu wao; na wanakufa hali ni makafiri.'),
(1361, 9, 126, 'Je! Hawaoni ya kwamba wanatiwa mtihanini kila mwaka mara moja au mara mbili? Kisha hawatubu, wala hawakumbuki.'),
(1362, 9, 127, 'Na ikiteremka Sura hutazamana wao kwa wao, (kama kwamba wanasema:) Je! Yuko mtu anakuoneni? Kisha hugeukilia mbali. Mwenyezi amezigeuza nyoyo zao kwa kuwa hao ni watu wasio fahamu.'),
(1363, 9, 128, 'Hakika amekwisha kujieni Mtume kutokana na nyinyi wenyewe; yanamhuzunisha yanayo kutaabisheni; anakuhangaikieni sana. Kwa Waumini ni mpole na mwenye huruma.'),
(1364, 9, 129, 'Basi wakigeuka, wewe sema: Mwenyezi Mungu ananitosheleza. Hapana mungu isipokuwa Yeye tu. Mimi namtegemea Yeye, na Yeye ndiye Mola Mlezi wa Kiti Kikuu cha Enzi.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Alif Lam Ra. Hizo ni Aya za Kitabu chenye Hikima.'),
(1366, 10, 2, 'Je! Imekuwa ajabu kwa watu kwamba tumemfunulia mmoja wao kuwa: Waonye watu, na wabashirie wale walio amini ya kuwa watakuwa na cheo cha juu kwa Mola wao Mlezi? Wakasema makafiri: Hakika huyu ni mchawi dhaahiri!'),
(1367, 10, 3, 'Hakika Mola wenu Mlezi ni Mwenyezi Mungu, ambaye ameziumba mbingu na ardhi kwa siku sita, na ametawala kwenye ufalme wake, anaendesha mambo yote. Hapana mwombezi ila baada ya idhini yake. Huyo ndiye Mwenyezi Mungu, Mola wenu Mlezi. Basi muabuduni Yeye. Je! Hamkumbuki?'),
(1368, 10, 4, 'Kwake Yeye tu ndio marejeo yenu nyote. Hii ni ahadi ya Mwenyezi Mungu iliyo ya kweli. Hakika Yeye ndiye aliye anzisha uumbaji, na kisha ataurejesha ili awalipe kwa uadilifu wale walio amini na wakatenda mema. Na walio kufuru, wao watapata vinywaji vinavyo chemka, na adhabu chungu kwa sababu ya walivyo kuwa wakikufuru.'),
(1369, 10, 5, 'Yeye ndiye aliye lijaalia jua kuwa na mwangaza, na mwezi ukawa na nuru, na akaupimia vituo ili mjue idadi ya miaka na hisabu. Mwenyezi Mungu hakuviumba hivyo ila kwa Haki. Anazipambanua Ishara kwa watu wanao jua.'),
(1370, 10, 6, 'Hakika katika kukhitalifiana usiku na mchana, na katika alivyo umba Mwenyezi Mungu katika mbingu na ardhi, zipo Ishara kwa watu wanao mcha-Mngu.'),
(1371, 10, 7, 'Hakika wasio taraji kukutana nasi, na wakaridhia maisha ya dunia na wakatua nayo, na walio ghafilika na Ishara zetu,'),
(1372, 10, 8, 'Hao, makaazi yao ni Motoni kwa sababu ya waliyo kuwa wakiyachuma.'),
(1373, 10, 9, 'Hakika walio amini na wakatenda mema, Mola wao Mlezi atawaongoa kwa sababu ya Imani yao. Itakuwa inapita mito kati yao katika Mabustani yenye neema.'),
(1374, 10, 10, 'Wito wao humo utakuwa: Subhanaka Llahumma "Umetakasika Ee Mwenyezi Mungu!" Na maamkio yao humo ni "Salama". Na mwisho wa wito wao ni: Alhamdulillahi Rabbil A\'lamiin "Kuhimidiwa kote ni kwa Mwenyezi Mungu, Mola Mlezi wa walimwengu wote."'),
(1375, 10, 11, 'Na lau kama Mwenyezi Mungu angeli wafanyia watu haraka kuwaletea shari kama wanavyo jihimizia kuletewa kheri, bila ya shaka wangelisha timiziwa ajali yao. Lakini tunawaacha wale wasio tumainia kukutana nasi wakihangaika katika upotovu wao.'),
(1376, 10, 12, 'Na mtu akiguswa na shida hutuomba naye kaegesha ubavu, au kakaa, au kasimama. Lakini tukimwondoshea shida yake huendelea kama kwamba hakupata kutuomba tumwondoshee shida iliyo mpata. Ndio namna hiyo wamepambiwa warukao mipaka yale waliyo kuwa wakiyatenda.'),
(1377, 10, 13, 'Na kwa yakini tumekwisha ziangamiza kaumu za kabla yenu walipo dhulumu, na waliwajia Mitume wao kwa Ishara zilizo wazi; lakini hawakuwa wenye kuamini. Ndio kama hivyo tunavyo walipa watu wakosefu.'),
(1378, 10, 14, 'Kisha tukakufanyeni nyiye ndio wenye kushika mahala pao baada yao katika ardhi ili tuone jinsi mtakavyo tenda.'),
(1379, 10, 15, 'Na wanapo somewa Aya zetu zilizo wazi, wale wasio taraji kukutana na Sisi husema: Lete Qur\'ani isiyo kuwa hii, au ibadilishe. Sema: Hainifalii mimi kuibadilisha kwa nitakavyo nafsi yangu. Mimi sifuati ila ninayo funuliwa kwa Wahyi. Hakika mimi naogopa, nikimuasi Mola wangu Mlezi, adhabu ya Siku iliyo kuu.'),
(1380, 10, 16, 'Sema: Mwenyezi Mungu angeli taka nisingeli kusomeeni, wala nisingeli kujuvyeni. Kwani nalikwisha kaa nanyi umri mzima kabla yake! Basi, je! Hamzingatii?'),
(1381, 10, 17, 'Basi ni nani aliye dhaalimu zaidi kuliko yule anaye mzulia uwongo Mwenyezi Mungu na akazikanusha Ishara zake? Hakika hawafanikiwi wakosefu.'),
(1382, 10, 18, 'Nao, badala ya Mwenyezi Mungu, wanaabudu wasio wadhuru wala kuwanufaisha, na wanasema: Hawa ndio waombezi wetu kwa Mwenyezi Mungu! Sema: Je! Mnamwambia Mwenyezi Mungu asiyo yajua ya katika mbingu wala katika ardhi? Subhanahu wa Taa\'la! Ametakasika na ametukuka na hao wanao mshirikisha naye.'),
(1383, 10, 19, 'Wala watu hawakuwa ila Umma mmoja tu. Kisha wakakhitalifiana. Na lau kuwa si neno lililo kwisha tangulia kutokana na Mola wako Mlezi, hapana shaka hukumu ingeli kwisha katwa baina yao katika hayo wanayo khitalifiana.'),
(1384, 10, 20, 'Na wanasema: Kwa nini hakuteremshiwa Ishara kutoka kwa Mola wake Mlezi? Sema: Mambo ya ghaibu ni ya Mwenyezi Mungu. Basi nyinyi ngojeni, na mimi ni pamoja nanyi katika wanao ngojea.'),
(1385, 10, 21, 'Na tunapo waonjesha watu rehema baada ya shida waliyo ipata, utawaona wana vitimbi kuzipangia Ishara zetu. Sema: Mwenyezi Mungu ni Mwepesi zaidi wa kupanga. Hakika wajumbe wetu wanayaandika mnayo yapanga.'),
(1386, 10, 22, 'Yeye ndiye anaye kuendesheni bara na baharini. Hata mnapo kuwa majahazini na yakawa yanakwenda nao kwa upepo mzuri wakaufurahia, upepo mkali ukawazukia, na yakawajia mawimbi kutoka kila upande, na wakaona wamesha zongwa, basi hapo humwomba Mwenyezi Mungu kwa kumsafishia niya: Ukituokoa na haya bila ya shaka tutakuwa miongoni mwa wanao shukuru.'),
(1387, 10, 23, 'Lakini akisha waokoa, mara wanafanya jeuri tena katika nchi bila ya haki. Enyi watu! Jeuri zenu zitakudhuruni wenyewe. Hii ni starehe ya maisha ya dunia tu. Kisha marejeo yenu ni kwetu, na hapo tutakuambieni mliyo kuwa mkiyatenda.'),
(1388, 10, 24, 'Hakika mfano wa maisha ya dunia ni kama maji tuliyo yateremsha kutoka mbinguni, kisha yakachanganyika na mimea ya ardhi wanayo ila watu na wanyama. Mpaka ardhi ilipo kamilika uzuri wake, na ikapambika, na wenyewe wakadhani wamesha iweza, iliifikia amri yetu usiku au mchana, tukaifanya kama iliyo fyekwa - kama kwamba haikuwapo jana. Namna hivi tunazipambanua Ishara zetu kwa watu wanao fikiri.'),
(1389, 10, 25, 'Na Mwenyezi Mungu anaita kwendea Nyumba ya Amani, na anamwongoa amtakaye kwenye Njia Iliyo Nyooka.'),
(1390, 10, 26, 'Kwa wafanyao wema ni wema na zaidi. Wala vumbi halitawafunika nyuso zao, wala madhila. Hao ndio watu wa Peponi. Humo watadumu.'),
(1391, 10, 27, 'Na wale walio chuma maovu, malipo ya uovu ni mfano wake vile vile, na yatawafika madhila. Hawatakuwa na wa kuwalinda na Mwenyezi Mungu. Nyuso zao kama kwamba zimefunikwa na vipande vya usiku wa giza. Hao ndio watu wa Motoni, wao humo watadumu.'),
(1392, 10, 28, 'Na siku tutakapo wakusanya wote, kisha tutawaambia walio shirikisha: Simameni mahali penu nyinyi na wale mlio wafanya washirika. Kisha tutawatenga baina yao. Na hao walio washirikisha watasema: Nyinyi hamkuwa mkituabudu sisi.'),
(1393, 10, 29, 'Mwenyezi Mungu anatosha kuwa ni shahidi baina yetu na nyinyi. Hakika sisi tulikuwa hatuna khabari ya ibada yenu.'),
(1394, 10, 30, 'Huko kila mtu atayajua aliyo yatanguliza. Na watarudishwa kwa Mwenyezi Mungu, Mola wao wa Haki, na yote waliyo kuwa wakiyazua yatawapotea.'),
(1395, 10, 31, 'Sema: Ni nani anaye kuruzukuni kutoka mbinguni na kwenye ardhi? Au ni nani anaye miliki kusikia na kuona? Na nani amtoaye hai kutoka maiti, na akamtoa maiti kutoka aliye hai? Na nani anaye yadabiri mambo yote? Watasema: Allah, Mwenyezi Mungu. Basi sema: Je! Hamchi?'),
(1396, 10, 32, 'Basi huyo ndiye Mwenyezi Mungu, Mola Mlezi wenu wa haki. Na kipo kitu gani baada ya haki, isipokuwa upotovu tu? Basi huwaje mkageuzwa?'),
(1397, 10, 33, 'Ndio kama hivyo kauli ya Mola wako Mlezi itakavyo wathibitikia wale walio potoka, ya kwamba hawatoamini.'),
(1398, 10, 34, 'Sema: Je! Yupo katika miungu yenu ya ushirikina aliye anzisha kuumba viumbe, na kisha akavirejesha? Huwaje, basi, mkadanganywa?'),
(1399, 10, 35, 'Sema: Je! Yupo katika miungu yenu ya ushirikina anaye ongoa kwendea Haki? Sema: Mwenyezi Mungu anaongoa kwendea Haki. Basi, je, anaye stahiki kufuatwa ni yule Mwenye kuongoa kwendea Haki au asiye ongoa ila aongozwe yeye? Basi nyinyi mna nini? Mnahukumu namna gani?'),
(1400, 10, 36, 'Na wengi wao hawafuati ila dhana tu. Na dhana haifai kitu mbele ya haki. Hakika Mwenyezi Mungu anajua yote wanayo yatenda.'),
(1401, 10, 37, 'Na haiwezekani Qur\'ani hii kuwa imetungwa na haitoki kwa Mwenyezi Mungu. Lakini hii inasadikisha yaliyo tangulia, na ni mfafanuo wa Kitabu kisicho na shaka ndani yake, kutokana na Mola Mlezi wa viumbe vyote.'),
(1402, 10, 38, 'Je! Ndiyo wanasema ameizua? Sema: Hebu leteni sura moja mfano wake na muwaite (kukusaidieni) muwawezao, isipo kuwa Mwenyezi Mungu, ikiwa nyinyi mnasema kweli.'),
(1403, 10, 39, 'Bali wameyakanusha wasio yaelewa ilimu yake kabla hayajawajia maelezo yake. Kadhaalika walio kabla yao walikanusha vile vile. Basi angalia jinsi ulivyo kuwa mwisho wa madhaalimu hao.'),
(1404, 10, 40, 'Na miongoni mwao wapo wanao iamini, na miongoni mwao wapo wasio iamini. Na Mola wako Mlezi anawajua vyema mafisadi.'),
(1405, 10, 41, 'Na wakikukanusha wewe, sema: Mimi nina a\'mali yangu, na nyinyi mna a\'mali yenu. Nyinyi hamna jukumu kwa niyatendayo, wala mimi sina jukumu kwa myatendayo.'),
(1406, 10, 42, 'Na wapo miongoni mwao wanao kusikiliza. Je, wewe unaweza kuwafanya viziwi wasikie ijapo kuwa hawafahamu?'),
(1407, 10, 43, 'Na wapo miongoni mwao wanao kutazama. Je, wewe utawaongoa vipofu ingawa hawaoni?'),
(1408, 10, 44, 'Hakika Mwenyezi Mungu hawadhulumu watu kitu chochote; lakini watu wenyewe wanajidhulumu nafsi zao.'),
(1409, 10, 45, 'Na Siku atakapo wakusanya, itakuwa kama kwamba hawakukaa ila saa moja tu ya mchana! Watatambuana. Hakika wamekhasirika walio kanusha kukutana na Mwenyezi Mungu, wala hawakuwa wenye kuongoka.'),
(1410, 10, 46, 'Na vyovyote vile, tukikuonyesha baadhi ya tunayo waahidi, au tukakufisha kabla yake, marejeo yao ni kwetu. Kisha Mwenyezi Mungu ni Shahidi wa wanayo yafanya.'),
(1411, 10, 47, 'Na kila umma una Mtume. Alipo wajia Mtume wao walihukumiwa baina yao kwa uadilifu, wala hawakudhulumiwa.'),
(1412, 10, 48, 'Na wanasema: Lini ahadi hii, ikiwa mnasema kweli?'),
(1413, 10, 49, 'Sema: Sijimilikii nafsi yangu shari wala kheri ila apendavyo Mwenyezi Mungu. Kila umma una muda wao walio wekewa. Ukifika muda wao hawakawii hata saa moja wala hawatangulii.'),
(1414, 10, 50, 'Sema: Mwaonaje ikikufikieni hiyo adhabu yake usiku au mchana, sehemu gani ya adhabu wanaihimiza wakosefu?'),
(1415, 10, 51, 'Tena je! Ikisha tokea mtaiamini? Je! Ndio sasa tena? Na nyinyi mlikuwa mkiihimiza.'),
(1416, 10, 52, 'Kisha walio dhulumu wataambiwa: Onjeni adhabu ya kudumu. Kwani mtalipwa isipo kuwa yale mliyo kuwa mkiyachuma?'),
(1417, 10, 53, 'Na wanakuuliza: Je! Ni kweli hayo? Sema: Ehe! Naapa kwa Mola wangu Mlezi! Hakika hayo ni kweli, na wala nyinyi hamshindi!'),
(1418, 10, 54, 'Na lau kuwa kila nafsi iliyo dhulumu inamiliki kila kiliomo duniani, bila ya shaka ingeli toa vyote kujikombolea. Na watakapo iona adhabu wataficha majuto. Na patahukumiwa baina yao kwa uadilifu, nao hawatadhulumiwa.'),
(1419, 10, 55, 'Jueni kuwa hakika vyote viliomo katika mbingu na katika ardhi ni vya Mwenyezi Mungu. Jueni kuwa hakika ahadi ya Mwenyezi Mungu ni ya haki. Lakini wengi wao hawajui.'),
(1420, 10, 56, 'Yeye ndiye anaye fufua na anaye fisha. Na kwake mtarejeshwa.'),
(1421, 10, 57, 'Enyi watu! Yamekujieni mawaidha kutoka kwa Mola wenu Mlezi, na poza kuponyesha yaliyomo vifuani, na uwongofu, na rehema kwa Waumini.'),
(1422, 10, 58, 'Sema: Kwa fadhila ya Mwenyezi Mungu na rehema yake! Basi nawafurahi kwa hayo. Haya ni bora kuliko hayo wanayo yakusanya.'),
(1423, 10, 59, 'Sema: Je! Mwaonaje zile riziki alizo kuteremshieni Mwenyezi Mungu, nanyi mkafanya katika hizo nyengine haramu na nyengine halali. Sema: Je! Mwenyezi Mungu amekuruhusuni, au mnamzulia Mwenyezi Mungu tu?'),
(1424, 10, 60, 'Na nini dhana ya wanao mzulia Mwenyezi Mungu kwa Siku ya Kiyama? Hakika Mwenyezi Mungu ana fadhila juu ya watu, lakini wengi wao hawashukuru.'),
(1425, 10, 61, 'Na huwi katika jambo lolote, wala husomi sehemu yoyote katika Qur\'ani, wala hamtendi kitendo chochote ila Sisi huwa ni mashahidi juu yenu mnapo shughulika nayo. Na hakifichikani kwa Mola wako Mlezi chenye uzito hata wa chembe katika ardhi na katika mbingu, wala kidogo kuliko hicho wala kikubwa ila kimo katika Kitabu kilicho wazi.'),
(1426, 10, 62, 'Jueni kuwa vipenzi vya Mwenyezi Mungu hawatakuwa na khofu wala hawatahuzunika.'),
(1427, 10, 63, 'Hao ni ambao wameamini na wakawa wanamcha Mungu.'),
(1428, 10, 64, 'Wao wana bishara njema katika maisha ya dunia na katika Akhera. Hapana mabadiliko katika maneno ya Mwenyezi Mungu. Huko ndiko kufuzu kukubwa.'),
(1429, 10, 65, 'Wala yasikuhuzunishe maneno yao. Hakika utukufu wote ni wa Mwenyezi Mungu. Yeye ndiye Mwenye kusikia Mwenye kujua.'),
(1430, 10, 66, 'Jueni kuwa ni vya Mwenyezi Mungu vyote viliomo mbinguni na ardhinii. Na wala hawawafuati hao wanao waomba badala ya Mwenyezi Mungu kuwa ni washirika wake. Wao hawafuati ila dhana tu, na hawasemi ila uwongo.'),
(1431, 10, 67, 'Yeye ndiye aliye kujaalieni usiku mpate kutulia humo, na mchana wa kuonea. Hakika katika haya zipo Ishara kwa watu wanao sikia.'),
(1432, 10, 68, 'Wamesema: Mwenyezi Mungu ana mwana. Subhanahu, Aliye takasika! Yeye ni Mkwasi, Mwenye Kujitosheleza. Vyote viliomo katika mbingu na katika ardhi ni vyake peke yake. Nyinyi hamna uthibitisho wowote wa haya! Mnamzulia Mwenyezi Mungu msiyo yajua?'),
(1433, 10, 69, 'Sema: Hao wanao mzulia uwongo Mwenyezi Mungu hawatafanikiwa.'),
(1434, 10, 70, 'Hiyo ni starehe ya katika dunia tu, kisha marejeo yao ni kwetu. Tena tutawaonjesha adhabu kali kwa sababu ya kukufuru kwao.'),
(1435, 10, 71, 'Wasomee khabari za Nuhu alipo waambia watu wake: Enyi watu wangu! Ikiwa kukaa kwangu nanyi na kukumbusha kwangu kwa Ishara za Mwenyezi Mungu kunakutieni mashaka basi mimi namtegemea Mwenyezi Mungu. Nanyi tengezeni mambo yenu pamoja na washirika wenu, na wala mambo yenu yasifichikane kwenu. Kisha nihukumuni, wala msinipe muhula.'),
(1436, 10, 72, 'Lakini mkigeuka, basi mimi sikukuombeni ujira. Ujira wangu hauko ila kwa Mwenyezi Mungu. Na nimeamrishwa niwe miongoni mwa Waislamu.'),
(1437, 10, 73, 'Wakamkanusha. Basi tukamwokoa, pamoja na walio kuwa naye, katika jahazi. Na tukawafanya wao ndio walio bakia, na tukawazamisha walio zikanusha Ishara zetu. Basi angalia vipi ulikuwa mwisho wa walio onywa.'),
(1438, 10, 74, 'Kisha baada yake tukawatuma Mitume kwa watu wao. Nao wakawajia kwa Ishara zilizo wazi. Lakini hawakuwa wenye kuyaamini waliyo yakanusha kabla yake. Ndio kama hivyo tunapiga muhuri juu ya nyoyo za warukao mipaka.'),
(1439, 10, 75, 'Kisha baada yao tukamtuma Musa na Haruni kwa Firauni na waheshimiwa wake, kwa Ishara zetu. Wakajivuna, na wakawa watu wakosefu.'),
(1440, 10, 76, 'Basi ilipo wajia Haki kutoka kwetu walisema: Hakika huu ni uchawi dhaahiri.'),
(1441, 10, 77, 'Akasema Musa: Mnasema hivi juu ya Haki ilipo kujieni? Huu ni uchawi? Na wachawi hawafanikiwi!'),
(1442, 10, 78, 'Wakasema: Je! Umetujia ili utuachishe tuliyo wakuta nayo baba zetu, na mpate nyinyi wawili ukubwa katika nchi? Wala sisi hatukuaminini nyinyi.'),
(1443, 10, 79, 'Na Firauni akasema: Nileteeni kila mchawi mjuzi!'),
(1444, 10, 80, 'Basi walipo kuja wachawi, Musa aliwaambia: Tupeni mnavyo tupa!'),
(1445, 10, 81, 'Walipo tupa, Musa alisema: Mliyo leta ni uchawi. Hakika Mwenyezi Mungu haitengenezi kazi ya waharibifu.'),
(1446, 10, 82, 'Na Mwenyezi Mungu huhakikisha Haki kwa maneno yake, hata wanga chukia wakosefu'),
(1447, 10, 83, 'Basi hawakumuamini Musa isipo kuwa baadhi ya vijana vya kaumu yake, kwa kumwogopa Firauni na wakuu wao wasiwatese. Kwani hakika Firauni alikuwa jeuri katika nchi. Na kwa yakini alikuwa miongoni mwa walio pita kiasi.'),
(1448, 10, 84, 'Na Musa alisema: Enyi watu wangu! Ikiwa nyinyi mmemuamini Mwenyezi Mungu, basi mtegemeeni Yeye, ikiwa nyinyi ni Waislamu.'),
(1449, 10, 85, 'Wakasema: Tunamtegemea Mwenyezi Mungu. Ewe Mola wetu Mlezi! Usitufanye wenye kutiwa misukosuko na hao watu madhaalimu.'),
(1450, 10, 86, 'Na utuokoe kwa rehema yako na watu makafiri.'),
(1451, 10, 87, 'Na tukampelekea Musa na ndugu yake wahyi huu: Watengenezeeni majumba watu wenu katika Misri na zifanyeni nyumba zenu ndio mwahali mwa ibada, na mshike Sala, na wabashirie Waumini.'),
(1452, 10, 88, 'Na Musa akasema: Mola wetu Mlezi! Hakika wewe umempa Firauni na wakuu wake mapambo na mali katika maisha ya dunia. Hivyo wanapoteza watu na Njia yako. Mola wetu Mlezi! Yafutilie mbali mali yao na zifunge nyoyo zao, wasiamini mpaka waione adhabu chungu.'),
(1453, 10, 89, 'Mwenyezi Mungu akasema: Maombi yenu yamekubaliwa. Basi simameni sawa sawa, wala msifuate njia za wale wasio jua.'),
(1454, 10, 90, 'Tukawavusha bahari Wana wa Israili, na Firauni na askari wake wakawafuatia kwa dhulma na uadui. Hata Firauni alipo kuwa anataka kuzama akasema: Naamini kuwa hapana mungu ila yule waliye muamini Wana wa Israili, na mimi ni miongoni mwa walio nyenyekea!'),
(1455, 10, 91, 'Ala! Sasa? Na hali uliasi kabla yake na ukawa miongoni mwa mafisadi!'),
(1456, 10, 92, 'Leo, basi, tutakuokoa kwa mwili wako, ili uwe Ishara kwa ajili ya walio nyuma yako. Na hakika watu wengi wameghafilika na Ishara zetu.'),
(1457, 10, 93, 'Na hakika tuliwaweka Wana wa Is raili kikao chema, na tukawaruzuku vitu vizuri. Nao hawakukhitalifiana mpaka ilipo wafikia ilimu. Hakika Mola wako Mlezi atahukumu baina yao Siku ya Kiyama katika yale waliyo kuwa wakikhitalifiana.'),
(1458, 10, 94, 'Na ikiwa unayo shaka katika tuliyo kuteremshia, basi waulize wasomao Kitabu kabla yako. Kwa yakini imekwisha kujia Haki kutoka kwa Mola wako Mlezi. Basi usiwe miongoni mwa wenye shaka.'),
(1459, 10, 95, 'Na kabisa usiwe miongoni mwa wale wanao zikanusha Ishara za Mwenyezi Mungu, usije kuwa katika walio khasiri.'),
(1460, 10, 96, 'Hakika wale ambao neno la Mola wako Mlezi limekwisha thibitika juu yao, hawataamini,'),
(1461, 10, 97, 'Ijapo kuwa itawajia kila Ishara, mpaka waione adhabu iliyo chungu.'),
(1462, 10, 98, 'Kwa nini usiwepo mji mmoja ukaamini na Imani yake ikawafaa - isipo kuwa kaumu Yunus? Waliamini na Sisi tukawaondolea adhabu ya hizaya katika maisha ya dunia, na tukawastarehesha kwa muda.'),
(1463, 10, 99, 'Angeli taka Mola wako Mlezi wangeli amini wote waliomo katika ardhi. Je, wewe utawalazimisha watu kwa nguvu mpaka wawe Waumini?'),
(1464, 10, 100, 'Na hawezi mtu kuamini ila kwa idhini ya Mwenyezi Mungu. Naye hujaalia adhabu iwafike wasio tumia akili zao.'),
(1465, 10, 101, 'Sema: Angalieni yaliomo mbinguni na kwenye ardhi! Na Ishara zote na maonyo hayawafai kitu watu wasio amini.'),
(1466, 10, 102, 'Basi, je! Wanangojea jingine ila kama yaliyo tokea siku za watu walio pita kabla yao? Ngojeni! Nami ni pamoja nanyi katika wanao ngoja.'),
(1467, 10, 103, 'Kisha Sisi huwaokoa Mitume wetu na walio amini. Ndio kama hivyo, inatustahiki kuwaokoa Waumini.'),
(1468, 10, 104, 'Sema: Enyi watu! Ikiwa nyinyi mnayo shaka katika Dini yangu, basi mimi siwaabudu mnao waabudu nyinyi badala ya Mwenyezi Mungu. Lakini mimi namuabudu Mwenyezi Mungu anaye kufisheni. Na nimeamrishwa niwe miongoni mwa Waumini.'),
(1469, 10, 105, 'Na uelekeze uso wako kwenye Dini ya Kweli, wala usiwe katika washirikina.'),
(1470, 10, 106, 'Na wala usiwaombe wasio kuwa Mwenyezi Mungu ambao hawakufai kitu wala hawakudhuru. Ukifanya hivyo basi utakuwa miongoni mwa walio dhulumu.'),
(1471, 10, 107, 'Na Mwenyezi Mungu akikugusisha madhara, basi hapana wa kukuondolea ila Yeye. Na akikutakia kheri, basi hapana awezaye kurudisha fadhila yake. Huifikisha kwa amtakaye katika waja wake, na Yeye ni Mwenye kusamehe, Mwenye kurehemu.'),
(1472, 10, 108, 'Sema: Enyi watu! Haki imekwisha kukujieni kutoka kwa Mola wenu Mlezi. Basi anaye ongoka anaongoka kwa faida ya nafsi yake, na anaye potea anapotea kwa khasara ya nafsi yake. Na mimi si mwakilishi juu yenu.'),
(1473, 10, 109, 'Na wewe fuata yanayo funuliwa kwako kwa wahyi. Na vumilia mpaka Mwenyezi Mungu ahukumu, na Yeye ndiye mbora wa mahakimu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Alif Lam Ra. Hiki ni Kitabu ambacho Aya zake zimewekwa wazi, kisha zikapambanuliwa, kilicho toka kwa Mwenye Hikima na Mwenye Khabari,'),
(1475, 11, 2, 'Ili msimuabudu isipo kuwa Allah, Mwenyezi Mungu. Hakika mimi kwenu ni mwonyaji na mbashiri nitokae kwake.'),
(1476, 11, 3, 'Na ili mumwombe msamaha Mola wenu Mlezi, kisha mtubie kwake. Atakustarehesheni starehe nzuri mpaka muda maalumu. Na atampa kila mwenye fadhila, fadhila yake. Na ikiwa mtakengeuka basi mimi nakukhofieni adhabu ya hiyo Siku Kubwa.'),
(1477, 11, 4, 'Marejeo yenu ni kwa Mwenyezi Mungu tu. Naye ni Muweza wa kila kitu.'),
(1478, 11, 5, 'Hebu angalia! Ati wanafunika vilivyomo vifuani mwao ili wamfiche Mwenyezi Mungu! Jueni kuwa wanapo jigubika nguo zao Yeye anajua wanayo yaficha na wanayo yatangaza. Hakika Yeye ni Mjuzi wa yaliyomo vifuani.'),
(1479, 11, 6, 'NA HAKUNA mnyama yoyote katika ardhi ila riziki yake iko kwa Mwenyezi Mungu. Naye anajua makao yake na mapitio yake. Yote yamo katika Kitabu chenye kubainisha.'),
(1480, 11, 7, 'Na Yeye ndiye aliye ziumba mbingu na ardhi katika siku sita. Na Kiti chake cha Enzi kilikuwa juu ya maji, ili akufanyieni mtihani ajuulikane ni nani miongoni mwenu mzuri zaidi wa vitendo. Na wewe ukisema: Nyinyi hakika mtafufuliwa baada ya kufa; wale walio kufuru husema: Hayakuwa haya ila ni uchawi uliyo wazi.'),
(1481, 11, 8, 'Na tukiwacheleweshea adhabu mpaka muda ulio kwisha hisabiwa wao husema: Nini kinacho izuia hiyo adhabu? Jueni! Siku itakapo wajia basi haitoondolewa hiyo kwao. Na yatawazunguka yale waliyo kuwa wakiyafanyia mzaha.'),
(1482, 11, 9, 'Na tukimwonjesha mtu rehema inayo toka kwetu, kisha tukamwondolea, hukata tamaa akakufuru.'),
(1483, 11, 10, 'Na tukimwonjesha neema baada ya madhara yaliyo mpata husema: Taabu zimekwisha niondokea. Hakika yeye hujitapa na kutafakhari.'),
(1484, 11, 11, 'Ila wale walio subiri wakatenda mema. Hao watapata msamaha na ujira mkubwa.'),
(1485, 11, 12, 'Basi labda utaacha baadhi ya yale yaliyo funuliwa kwako, na kifua kitaona dhiki kwa hayo, kwa sababu wanasema: Mbona hakuteremshiwa khazina, au wakaja naye Malaika? Wewe ni mwonyaji tu, na Mwenyezi Mungu ndiye Mlinzi wa kila kitu.'),
(1486, 11, 13, 'Au wanasema: Ameizua? Sema: Basi leteni Sura kumi zilizo zuliwa mfano wa hii, na waiteni muwawezao badala ya Mwenyezi Mungu, ikiwa mnasema kweli.'),
(1487, 11, 14, 'Na wasipo kuitikieni, basi jueni ya kwamba (hii Qur\'ani) imeteremshwa kwa ujuzi wa Mwenyezi Mungu, na kwamba hapana mungu ila Yeye. Basi je! Nyinyi ni Waislamu?'),
(1488, 11, 15, 'Wanao taka maisha ya dunia na pumbao lake tutawalipa humo vitendo vyao kaamili. Na wao humo hawatopunjwa.'),
(1489, 11, 16, 'Hao ndio ambao hawatakuwa na kitu Akhera ila Moto, na yataharibika waliyo yafanya, na yatapotea bure waliyo kuwa wakiyatenda.'),
(1490, 11, 17, 'Basi je, mtu aliye na dalili wazi itokayo kwa Mola wake Mlezi, inayo fuatwa na shahidi anaye toka kwake, na kabla yake kilikuwa Kitabu cha Musa kilicho kuwa mwongozi na rehema - hao wanamuamini, na anaye mkataa katika makundi, basi Moto ndio pahala pa miadi yao. Basi usiwe na shaka juu ya hayo. Hii ni Haki itokayo kwa Mola wako Mlezi; lakini watu wengi hawaamini.'),
(1491, 11, 18, 'Na nani aliye dhaalimu mkubwa kuliko yule anaye mzulia Mwenyezi Mungu uwongo? Hao watahudhurishwa mbele ya Mola wao Mlezi, na mashahidi watasema: Hawa ndio walio mtungia uwongo Mola wao Mlezi. Laana ya Mwenyezi Mungu iwapate walio dhulumu,'),
(1492, 11, 19, 'Ambao wanazuilia Njia ya Mwenyezi Mungu, na wanaitakia ipotoke, na wanaikataa Akhera.'),
(1493, 11, 20, 'Hao hawawezi kushinda katika ardhi, wala hawana walinzi isipo kuwa Mwenyezi Mungu. Watazidishiwa adhabu. Hawakuwa wakiweza kusikia wala hawakuwa wakiona.'),
(1494, 11, 21, 'Hao ndio walio zikhasiri nafsi zao, na yakawapotea waliyo kuwa wakiyazua.'),
(1495, 11, 22, 'Bila ya shaka hakika wao ndio wenye kukhasiri huko Akhera.'),
(1496, 11, 23, 'Hakika wale walio amini, na wakatenda mema, na wakanyenyekea kwa Mola wao Mlezi, hao ndio watu wa Peponi, na humo watadumu.'),
(1497, 11, 24, 'Mfano wa makundi mawili ni kama kipofu kiziwi, na mwenye kuona na anasikia. Je, wawili hawa wanakuwa sawa kwa kufananishwa? Basi, je, hamfikiri?'),
(1498, 11, 25, 'Na Sisi tulimtuma Nuhu kwa watu wake, akawaambia: Hakika mimi ni mwonyaji kwenu ninaye bainisha,'),
(1499, 11, 26, 'Ya kwamba msimuabudu isipo kuwa Mwenyezi Mungu tu. Hakika mimi ninakukhofieni adhabu ya Siku Chungu.'),
(1500, 11, 27, 'Na wakasema wakuu walio kufuru katika kaumu yake: Hatukuoni wewe ila ni mtu tu kama sisi, wala hatukuoni wamekufuata ila wale walio kuwa kwetu watu duni, wasio kuwa na akili. Wala hatukuoneni kuwa mna ubora wowote kutushinda sisi. Bali tuna hakika nyinyi ni waongo.'),
(1501, 11, 28, 'Akasema: Enyi watu wangu! Hebu fikirini! Ikiwa mimi ninayo hoja wazi iliyo toka kwa Mola wangu Mlezi, na amenipa rehema kutoka kwake, nayo ikakufichikieni; je, tukulazimisheni kuikubali hali nyinyi mnaichukia?'),
(1502, 11, 29, 'Na enyi watu wangu! Mimi sikuombeni mali kwa ajili ya haya. Mimi sina ujira ila kwa Mwenyezi Mungu; na mimi sitawafukuza walio amini. Hakika wao watakutana na Mola wao Mlezi, lakini mimi nakuoneni mnafanya ujinga.'),
(1503, 11, 30, 'Na enyi watu wangu! Ni nani atakaye nisaidia kwa Mwenyezi Mungu nikiwafukuza hawa? Basi je, hamfikiri?'),
(1504, 11, 31, 'Wala sikwambiini kuwa nina khazina za Mwenyezi Mungu; wala kuwa mimi najua mambo ya ghaibu; wala sisemi: Mimi ni Malaika. Wala siwasemi wale ambao yanawadharau macho yenu kuwa Mwenyezi Mungu hatawapa kheri - Mwenyezi Mungu anajua yaliomo katika nafsi zao - hapo bila ya shaka ninge kuwa miongoni mwa wenye kudhulumu.'),
(1505, 11, 32, 'Wakasema: Ewe Nuhu! Umejadiliana, na umekithirisha kutujadili. Basi tuletee hayo unayo tuahidi, ukiwa miongoni mwa wasemao kweli.'),
(1506, 11, 33, 'Akasema: Mwenyezi Mungu atakuleteeni akipenda. Wala nyinyi si wenye kumshinda.'),
(1507, 11, 34, 'Wala nasaha yangu haikufaini kitu nikitaka kukunasihini, ikiwa Mwenyezi Mungu anataka kukuachieni mpotee. Yeye ndiye Mola wenu Mlezi; na kwake mtarejeshwa.'),
(1508, 11, 35, 'Au ndio wanasema: Ameizua? Sema: Ikiwa nimeizua, basi kosa hilo ni juu yangu, na mimi sikhusiki na makosa myatendayo.'),
(1509, 11, 36, 'Na akafunuliwa Nuhu akaambiwa: Hataamini yeyote katika watu wako ila wale walio kwisha amini. Basi usisikitike kwa waliyo kuwa wakiyatenda.'),
(1510, 11, 37, 'Na unda jahazi mbele ya macho yetu na kwa mujibu wa ufunuo wetu. Wala usinisemeze kuwatetea walio dhulumu. Kwani hao bila ya shaka watazamishwa.'),
(1511, 11, 38, 'Na akawa anaunda jahazi, na kila wakipita wakuu wa kaumu yake wakimkejeli. Yeye akasema: Ikiwa nyinyi mnatukejeli na sisi tunakukejelini kama mnavyo tukejeli.'),
(1512, 11, 39, 'Nanyi mtakuja jua ni nani itakaye mfikia adhabu ya kumhizi, na itakaye mteremkia adhabu ya kudumu.'),
(1513, 11, 40, 'Hata ilipo kuja amri yetu, na tanuri ikafoka maji, tulisema: Pakia humo wawili wawili, dume na jike, kutoka kila aina, na ahali zako, isipo kuwa wale ambao imekwisha wapitia hukumu; na watu walio amini. Na hawakuamini pamoja naye ila wachache tu.'),
(1514, 11, 41, 'Na akasema: Pandeni humo kwa Bismillahi, Kwa Jina la Mwenyezi Mungu, kwenda kwake na kusimama kwake. Hakika Mola wangu Mlezi ni Mwenye kusamehe Mwenye kurehemu.'),
(1515, 11, 42, 'Ikawa inakwenda nao katika mawimbi kama milima. Na Nuhu akamwita mwanawe naye alikuwa mbali: Ewe mwanangu! Panda pamoja nasi, wala usiwe pamoja na makafiri.'),
(1516, 11, 43, 'Akasema: Nitakimbilia mlimani unilinde na maji. (Nuhu) akasema: Leo hapana wa kulindwa na amri ya Mwenyezi Mungu ila aliye mrehemu mwenyewe. Na wimbi likawatenganisha, akawa katika walio zama.'),
(1517, 11, 44, 'Na ikasemwa: Ewe ardhi! Meza maji yako. Na Ewe mbingu! Jizuie. Basi maji yakadidimia chini, na amri ikapitishwa, na (jahazi) likasimama juu ya (mlima) wa Al Juudiy. Na ikasemwa: Wapotelee mbali watu walio dhulumu!'),
(1518, 11, 45, 'Na Nuhu alimwomba Mola wake Mlezi: Ee Mola Mlezi wangu! Hakika mwanangu ni katika ahali zangu, na hakika ahadi yako ni haki. Na Wewe ni Mwenye haki kuliko mahakimu wote.'),
(1519, 11, 46, 'Akasema: Ewe Nuhu! Huyu si katika ahali zako. Mwendo wake si mwema. Basi usiniombe jambo usio na ujuzi nalo. Mimi nakuwaidhi usije ukawa miongoni mwa wajinga.'),
(1520, 11, 47, 'Nuhu akasema: Ewe Mola wangu Mlezi! Mimi najikinga kwako nisikuombe nisio na ujuzi nalo. Na kama hunisamehe na ukanirehemu, nitakuwa katika walio khasiri.'),
(1521, 11, 48, 'Ikasemwa: Ewe Nuhu! Shuka kwa salama itokayo kwetu, na baraka nyingi juu yako na juu ya watu walio pamoja nawe. Na zitakuwapo kaumu tutakazo zistarehesha, na kisha zitashikwa na adhabu chungu itokayo kwetu.'),
(1522, 11, 49, 'Hizi ni katika khabari za ghaibu tunazo kufunulia. Hukuwa ukizijua wewe, wala watu wako, kabla ya hii. Basi subiri! Hakika Mwisho ni wa wachamngu.'),
(1523, 11, 50, 'Na kwa kina A\'adi tulimtuma ndugu yao Hud. Akasema: Enyi watu wangu! Muabuduni Mwenyezi Mungu! Nyinyi hamna mungu isipo kuwa Yeye. Nyinyi si chochote ila ni wazushi tu.'),
(1524, 11, 51, 'Enyi watu wangu! Sikuombeni ujira kwa ajili ya haya. Haukuwa ujira wangu ila kwa Yule aliye niumba. Basi hamtumii akili?'),
(1525, 11, 52, 'Na enyi watu wangu! Muombeni msamaha Mola wenu Mlezi, kisha mtubie kwake. Atakuleteeni mbingu zenye kunyesha mvua ya kumiminika, na atakuzidishieni nguvu juu ya nguvu zenu. Wala msigeuke mkawa wakosefu.'),
(1526, 11, 53, 'Wakasema: Ewe Hud! Hujatuletea dalili wazi. Wala sisi hatuiachi miungu yetu kwa kufuata kauli yako. Na wala sisi hatukuamini wewe.'),
(1527, 11, 54, 'Sisi tunasema: Baadhi katika miungu yetu imekusibu kwa baa. Akasema: Hakika mimi namshuhudisha Mwenyezi Mungu, na nyinyi shuhudieni, ya kwamba mimi najitenga mbali na hao mnao wafanya washirika,'),
(1528, 11, 55, 'Mkamwacha Yeye. Basi nyote nyinyi nipangieni vitimbi, na kisha msinipe muhula!'),
(1529, 11, 56, 'Hakika mimi nimemtegemea Mwenyezi Mungu, Mola Mlezi wangu na Mola Mlezi wenu. Hapana kiumbe yeyote ila Yeye anamwendesha atakavyo. Hakika Mola wangu Mlezi yuko juu ya Njia Iliyo Nyooka.'),
(1530, 11, 57, 'Na ikiwa watarudi nyuma, basi mimi nimekwisha kufikishieni niliyo tumwa kwenu. Na Mola wangu Mlezi atawaleta watu wengine badala yenu, wala nyinyi hamumdhuru kitu Yeye. Hakika Mola wangu Mlezi ni Mwenye kuhifadhi kila kitu.'),
(1531, 11, 58, 'Ilipo fika amri yetu, tulimwokoa Hud na walio amini pamoja naye, kwa rehema itokayo kwetu. Na tukawaokoa na adhabu ngumu.'),
(1532, 11, 59, 'Na hao ndio kina A\'adi. Walizikanusha Ishara za Mola wao Mlezi, na wakawaasi Mitume wake, na wakafuata amri ya kila jabari mwenye inda.'),
(1533, 11, 60, 'Na wakafuatishiwa laana katika dunia hii na Siku ya Kiyama. Basi tambueni mtanabahi! Hakika kina A\'adi walimkufuru Mola wao Mlezi. Na tambueni mtanabahi kuwa hakika waliangamizwa kina A\'adi, kaumu ya Hud.'),
(1534, 11, 61, 'Na kina Thamud tuliwapelekea ndugu yao Saleh. Akasema: Enyi watu wangu! Muabuduni Mwenyezi Mungu. Nyinyi hamna Mungu ila Yeye. Yeye ndiye aliye kuumbeni katika ardhi, na akakuwekeni humo. Basi mwombeni msamaha, kisha mtubu kwake. Hakika Mola wangu Mlezi yupo karibu, anaitikia maombi.'),
(1535, 11, 62, 'Wakasema: Ewe Saleh! Hakika kabla ya haya ulikuwa unatarajiwa kheri kwetu. Je, unatukataza tusiwaabudu waliyo kuwa wakiwaabudu baba zetu? Na hakika sisi tuna shaka na wasiwasi kwa hayo unayo tuitia.'),
(1536, 11, 63, 'Akasema: Enyi watu wangu! Mwaonaje ikiwa ninazo dalili zilizo wazi kutokana na Mola wangu Mlezi, naye akawa kanipa rehema kutoka kwake - je, ni nani atakaye ninusuru kwa Mwenyezi Mungu nikimuasi? Basi nyinyi hamtanizidishia ila khasara tu.'),
(1537, 11, 64, 'Enyi watu wangu! Huyu ngamia wa Mwenyezi Mungu ni Ishara kwenu. Basi mwacheni ale katika ardhi ya Mwenyezi Mungu; wala msimguse kwa ubaya, isije ikakuangamizeni adhabu iliyo karibu.'),
(1538, 11, 65, 'Wakamchinja. Basi (Saleh) akasema: Stareheni katika mji wenu muda wa siku tatu. Hiyo ni ahadi isiyo kuwa ya uwongo.'),
(1539, 11, 66, 'Basi ilipo kuja amri yetu tulimwokoa Saleh na wale walio amini pamoja naye kwa rehema yetu kutokana na hizaya ya siku ile. Hakika Mola wako Mlezi ndiye Mwenye nguvu Mwenye kushinda.'),
(1540, 11, 67, 'Na ukelele uliwaangamiza wale walio dhulumu, wakapambazukiwa nao ni maiti majumbani mwao.'),
(1541, 11, 68, 'Kama kwamba hawakuwako huko. Hebu zingatieni! Hakika kina Thamud walimkufuru Mola wao Mlezi. Hebu zingatieni! Thamud wamepotelea mbali!'),
(1542, 11, 69, 'Na wajumbe wetu walimjia Ibrahim kwa bishara njema, wakasema: Salama! Naye akasema: Salama! Hakukaa ila mara akaleta ndama wa kuchoma.'),
(1543, 11, 70, 'Basi alipoona mikono yao haimfikilii aliwatilia shaka, na akawaogopa. Wakasema: Usiogope! Hakika sisi tumetumwa kwa kaumu ya Lut\'.'),
(1544, 11, 71, 'Na mkewe alikuwa kasimama wima, akacheka. Basi tukambashiria (kuzaliwa) Is-haq, na baada ya Is-haq Yaaqub.'),
(1545, 11, 72, 'Akasema: Je, hivyo mimi nizae, na hali yangu ni kikongwe na huyu mume wangu ni kizee? Hakika haya ni mambo ya ajabu!'),
(1546, 11, 73, 'Wakasema: Je, unastaajabia amri ya Mwenyezi Mungu? Rehema ya Mwenyezi Mungu na baraka zake ziko juu yenu, enyi watu wa nyumba hii! Hakika Yeye ndiye Msifiwa wa kutukuzwa.'),
(1547, 11, 74, 'Basi khofu ilipo mwondoka Ibrahim, na ikawa ile bishara imemfikia, alianza kujadiliana nasi juu ya kaumu ya Lut\'.'),
(1548, 11, 75, 'Hakika Ibrahim alikuwa mpole, ana huruma, na mwepesi wa kurejea kwa Mwenyezi Mungu.'),
(1549, 11, 76, '(Akaambiwa): Ewe Ibrahim! Wachilia mbali haya! Kwa hakika amri ya Mola wako Mlezi imekwisha kuja, na hakika hao itawafikia adhabu isiyo rudi nyuma.'),
(1550, 11, 77, 'Na wajumbe wetu walipo kuja kwa Lut\' aliwahuzunukia na akawaonea dhiki. Akasema: Hii leo ni siku ngumu!'),
(1551, 11, 78, 'Wakamjia kaumu yake mbio mbio. Na kabla ya haya walikuwa wakitenda maovu. Yeye akasema: Enyi watu wangu! Hawa binti zangu, ndio wametakasika zaidi kwenu. Basi mcheni Mwenyezi Mungu, wala msinihizi mbele ya wageni wangu. Hivyo, hamna hata mtu mmoja miongoni mwenu aliye ongoka?'),
(1552, 11, 79, 'Wakasema: Bila shaka umekwisha jua hatuna haki juu ya binti zako, na unayajua tunayo yataka.'),
(1553, 11, 80, 'Akasema: Laiti ningeli kuwa na nguvu kwenu, au nategemea kwenye nguzo yenye nguvu!'),
(1554, 11, 81, '(Wajumbe) wakasema: Ewe Lut\'! Sisi ni wajumbe wa Mola wako Mlezi. Hawa hawatakufikia. Na wewe ondoka pamoja na ahali zako usiku ungalipo. Wala yeyote miongoni mwenu asitazame nyuma, isipo kuwa mkeo, kwani yeye utamfika msiba utakao wafika hao. Hakika miadi yao ni asubuhi. Je, asubuhi si karibu?'),
(1555, 11, 82, 'Basi ilipo fika amri yetu tuliigeuza nchi juu chini, na tukawateremshia mvua ya changarawe za udongo mgumu ulio kamatana wa Motoni,'),
(1556, 11, 83, 'Zilizo tiwa alama kwa Mola wako Mlezi. Na haya hayako mbali na wenye kudhulumu wengineo.'),
(1557, 11, 84, 'Na kwa watu wa Madyana tuliwatumia ndugu yao Shua\'ib. Akasema: Enyi watu wangu! Muabuduni Mwenyezi Mungu. Nyinyi hamna Mungu ila Yeye, wala msipunguze vipimo na mizani. Mimi nakuoneni mmo katika hali njema, nami nakukhofieni adhabu ya Siku kubwa hiyo itakayo kuzungukeni.'),
(1558, 11, 85, 'Na enyi watu wangu! Timizeni vipimo na mizani kwa uadilifu wala msiwakhini watu vitu vyao; wala msieneze uovu katika nchi mkafanya uharibifu.'),
(1559, 11, 86, 'Alivyo kubakishieni Mwenyezi Mungu ndiyo bora kwa ajili yenu, ikiwa nyinyi ni Waumini. Wala mimi siye mlinzi wenu.'),
(1560, 11, 87, 'Wakasema: Ewe Shua\'ibu! Ni sala zako ndizo zinazo kuamrisha tuyaache waliyo kuwa wakiyaabudu baba zetu, au tuache kufanya tupendavyo katika mali zetu? Ama hakika wewe ni mstahamilivu kweli na mwongofu!'),
(1561, 11, 88, 'Akasema: Enyi watu wangu! Mwaonaje ikiwa ninayo dalili wazi inayo tokana na Mola wangu Mlezi, na ikawa Yeye ameniruzuku riziki njema kutoka kwake? Wala mimi sipendi kukukhalifuni nikafanya yale ninayo kukatazeni. Sitaki ila kutengeneza kiasi ninavyo weza. Na sipati kuwezeshwa haya ila na Mwenyezi Mungu. Kwake Yeye ninategemea, na kwake Yeye naelekea.'),
(1562, 11, 89, 'Na enyi watu wangu! Kukhalifiana nami kusikupelekeeni hata mkasibiwa kama walivyo sibiwa watu wa Nuhu, au watu wa Hud, au watu wa Saleh. Na watu wa Lut\' si mbali nanyi.'),
(1563, 11, 90, 'Na ombeni msamaha kwa Mola wenu Mlezi, tena tubuni kwake. Hakika Mola wangu Mlezi ni Mwenye kurehemu na Mwenye upendo.'),
(1564, 11, 91, 'Wakasema: Ewe Shua\'ibu! Mengi katika hayo unayo yasema hatuyafahamu. Na sisi tunakuona huna nguvu kwetu. Lau kuwa si jamaa zako tunge kupiga mawe, wala wewe si mtukufu kwetu.'),
(1565, 11, 92, 'Akasema: Enyi watu wangu! Kwani jamaa zangu ni watukufu zaidi kwenu kuliko Mwenyezi Mungu? Na Yeye mmemueka nyuma ya migongo yenu! Hakika Mola wangu Mlezi ni Mwenye kuyazunguka yote mnayo yatenda.'),
(1566, 11, 93, 'Na enyi watu wangu! Fanyeni mwezayo, na mimi pia ninafanya. Karibuni mtajua ni nani itakaye mfikia adhabu ya kumhizi, na nani aliye mwongo. Na ngojeni, mimi pia ninangoja pamoja nanyi.'),
(1567, 11, 94, 'Na ilipo fika amri yetu, tulimwokoa Shua\'ibu na wale walio amini pamoja naye kwa rehema yetu. Na ukelele uliwanyakua walio dhulumu, na wakapambaukiwa majumbani mwao wamekufa kifudifudi!'),
(1568, 11, 95, 'Kama kwamba hawakuwamo humo. Zingatia walivyo angamia watu wa Madyana, kama walivyo angamia watu wa Thamud!'),
(1569, 11, 96, 'Na hakika tulimtuma Musa pamoja na Ishara zetu na uthibitisho ulio wazi'),
(1570, 11, 97, 'Kwa Firauni na waheshimiwa wake. Lakini wao walifuata amri ya Firauni, na amri ya Firauni haikuwa yenye uwongofu.'),
(1571, 11, 98, 'Siku ya Kiyama atawatangulia watu wake na atawaingiza Motoni. Na muingio muovu ulioje huo!'),
(1572, 11, 99, 'Nao wamefuatishiwa laana hapa duniani na Siku ya Kiyama. Ni mabaya yalioje watakayo pewa!'),
(1573, 11, 100, 'Hizi ni katika khabari za miji, tunakusimulia. Mingine ipo bado na mingine imefyekwa.'),
(1574, 11, 101, 'Na Sisi hatukuwadhulumu, lakini wao wenyewe wamejidhulumu. Na miungu yao waliyo kuwa wakiiomba badala ya Mwenyezi Mungu haikuwafaa kitu ilipo kuja amri ya Mola wako Mlezi. Na hiyo miungu haikuwazidishia ila maangamizo tu.'),
(1575, 11, 102, 'Na ndio kama hivyo ndivyo inavyo kuwa Mola wako Mlezi anapo ikamata miji inapo kuwa imedhulumu. Hakika mkamato wake ni mchungu na mkali.'),
(1576, 11, 103, 'Hakika katika haya ipo ishara kwa yule anaye ogopa adhabu ya Akhera. Hiyo ndiyo Siku itakayo kusanyiwa watu, na hiyo ndiyo Siku itakayo shuhudiwa.'),
(1577, 11, 104, 'Na Sisi hatuiakhirishi ila kwa muda unao hisabiwa.'),
(1578, 11, 105, 'Siku hiyo itakapo fika, hatosema hata mtu mmoja ila kwa idhini yake Mwenyezi Mungu. Kati yao watakuwamo waliomo mashakani na wenye furaha.'),
(1579, 11, 106, 'Ama wale wa mashakani hao watakuwamo Motoni wakiyayayatika na kukoroma.'),
(1580, 11, 107, 'Watadumu humo muda wa kudumu mbingu na ardhi, isipo kuwa apendavyo Mola wako Mlezi. Hakika Mola wako Mlezi hutenda apendavyo.'),
(1581, 11, 108, 'Na ama wale walio bahatika, wao watakuwamo Peponi wakidumu humo muda wa kudumu mbingu na ardhi, isipo kuwa apendavyo Mola wako Mlezi. Hicho ni kipawa kisio na ukomo.'),
(1582, 11, 109, 'Basi usiwe na shaka juu ya wanayo yaabudu hao. Hawaabudu ila kama walivyo abudu baba zao zamani. Na hakika Sisi tutawatimilizia fungu lao bila ya kupunguzwa.'),
(1583, 11, 110, 'Na kwa yakini tulimpa Musa Kitabu; zikazuka khitilafu ndani yake. Na lau kuwa si neno lilio kwisha tangulia kutoka kwa Mola wako Mlezi, bila ya shaka ingeli hukumiwa baina yao. Na hakika wao wamo katika shaka juu yake inayo wahangaisha.'),
(1584, 11, 111, 'Na hakika Mola wako Mlezi atawatimilizia malipo ya vitendo vyao. Hakika Yeye anayo khabari ya wayatendayo.'),
(1585, 11, 112, 'Basi, simama sawa sawa kama ulivyo amrishwa, wewe na wale wanao elekea kwa Mwenyezi Mungu pamoja nawe; wala msikiuke mipaka. Hakika Yeye anayaona yote myatendayo.'),
(1586, 11, 113, 'Wala msiwategemee wanao dhulumu, usije ukakuguseni Moto. Wala nyinyi hamna walinzi badala ya Mwenyezi Mungu, wala tena hamtasaidiwa.'),
(1587, 11, 114, 'Na shika Sala katika ncha mbili za mchana na nyakati za usiku zilizo karibu na mchana. Hakika mema huondoa maovu. Huu ni ukumbusho kwa wanao kumbuka.'),
(1588, 11, 115, 'Na subiri, kwani Mwenyezi Mungu hapotezi ujira wa wanao fanya wema.'),
(1589, 11, 116, 'Basi mbona hawakuwamo katika watu wa kabla yenu wenye vyeo na wasaa wanao kataza uharibifu katika nchi, isipo kuwa wachache tu, ambao ndio tulio waokoa? Na walio dhulumu walifuata starehe zao, na wakawa ni wakosefu.'),
(1590, 11, 117, 'Wala hakuwa Mola wako Mlezi wa kuihiliki miji kwa dhulma tu na hali watu wake ni watenda mema.'),
(1591, 11, 118, 'Na Mola wako Mlezi angeli penda ange wafanya watu wote wakawa umma mmoja. Lakini hawaachi kukhitalifiana,'),
(1592, 11, 119, 'Isipo kuwa wale ambao Mola wako Mlezi amewarehemu; na kwa hiyo ndio Mwenyezi Mungu amewaumba. Na litatimia neno la Mola wako Mlezi: Kweli kweli nitaijaza Jahannam kwa majini na watu pamoja.'),
(1593, 11, 120, 'Na yote tunayo kusimulia katika khabari za Mitume ni ya kukupa nguvu moyo wako. Na katika haya imekujia haki na mawaidha na ukumbusho kwa Waumini.'),
(1594, 11, 121, 'Na waambie wale wasio amini: Fanyeni muwezavyo, nasi pia tunafanya.'),
(1595, 11, 122, 'Na ngojeni, na sisi tunangoja.'),
(1596, 11, 123, 'Na ni vya Mwenyezi Mungu tu vyote vilivyo fichikana katika ardhi na mbingu, na mambo yote yatarejezwa kwake. Basi muabudu Yeye na umtegemee Yeye. Na Mola wako Mlezi haghafiliki na yale mnayo yatenda.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Alif Lam Raa. Hizi ni Aya za Kitabu kinacho bainisha.'),
(1598, 12, 2, 'Hakika Sisi tumeiteremsha Qur\'ani kwa Kiarabu ili mpate kuzingatia.'),
(1599, 12, 3, 'Sisi tunasimulia simulizi nzuri kwa kukufunulia Qur\'ani hii. Na ijapo kuwa kabla ya haya ulikuwa miongoni mwa wasio jua.'),
(1600, 12, 4, 'Yusuf alipo mwambia baba yake: Ewe babaangu! Hakika mimi nimeota nyota kumi na moja, na jua, na mwezi. Nimeota zikinisujudia.'),
(1601, 12, 5, '(Baba) akasema: Ewe mwanangu! Usiwasimulie nduguzo ndoto yako, wasije wakakufanyia vitimbi. Hakika Shet\'ani ni adui aliye dhaahiri kwa mwanaadamu.'),
(1602, 12, 6, 'Na kadhaalika Mola wako Mlezi atakuteuwa na atakufundisha tafsiri ya mambo. Na atatimiza neema zake juu yako na juu ya ukoo wa Yaa\'qub, kama alivyo watimizia hapo zamani baba zako Ibrahim na Is-haq. Hakika Mola wako Mlezi ni Mjuzi Mwenye hikima.'),
(1603, 12, 7, 'Kwa yakini katika Yusuf na nduguze ziko ishara kwa wanao uliza.'),
(1604, 12, 8, 'Pale walipo sema: Hakika Yusuf na nduguye wanapendwa zaidi na baba yetu kuliko sisi, hali sisi ni kikundi chenye nguvu. Hakika baba yetu yumo katika upotofu wa dhaahiri.'),
(1605, 12, 9, 'Muuweni Yusuf, au mtupeni nchi mbali ili uso wa baba yenu ukuelekeeni nyinyi. Na baada ya haya mtakuwa watu wema.'),
(1606, 12, 10, 'Akasema msemaji kati yao: Msimuuwe Yusuf. Lakini mtumbukizeni ndani ya kisima. Wasafiri watakuja mwokota; kama nyinyi mna sharti ya kufanya jambo.'),
(1607, 12, 11, 'Wakasema: Ewe baba yetu! Una nini hata hutuamini kwa Yusuf? Na hakika sisi ni wenye kumtakia kheri!'),
(1608, 12, 12, 'Mpeleke kesho pamoja nasi, ale kwa furaha na acheze; na bila ya shaka sisi tutamhifadhi.'),
(1609, 12, 13, 'Akasem: Kwa hakika inanihuzunisha kwamba nyinyi mwende naye, na ninaogopa asije mbwa mwitu akamla nanyi mmeghafilika naye.'),
(1610, 12, 14, 'Wakasema: Ikiwa mbwa mwitu atamla na hali sisi ni kundi lenye nguvu kabisa, basi bila ya shaka sisi tutakuwa khasarani.'),
(1611, 12, 15, 'Basi walipo mchukua na wakakubaliana wamtumbukize ndani ya kisima, tulimfunulia Yusuf: Hapana shaka yoyote utakuja waambia kwa jambo lao hili, na wala wao hawatambui.'),
(1612, 12, 16, 'Wakaja kwa baba yao usiku wakilia.'),
(1613, 12, 17, 'Wakasema: Ewe baba yetu! Hakika tulikwenda kushindana na tukamwacha Yusuf penye vitu vyetu. Basi mbwa mwitu akamla. Lakini wewe hutuamini ijapo kuwa tunasema kweli.'),
(1614, 12, 18, 'Wakaja na kanzu yake ina damu ya uwongo. Akasema: Bali nafsi zenu zimekushawishini kutenda kitendo. Lakini subira ni njema; na Mwenyezi Mungu ndiye wa kuombwa msaada kwa haya mnayo yaeleza.'),
(1615, 12, 19, 'Ukafika msafara, wakamtuma mchota maji wao, naye akatumbukiza ndoo yake. Alisema: Kheri Inshallah! Huyu hapa mvulana! Wakamficha ili kumfanya ni bidhaa. Na Mwenyezi Mungu ni Mjuzi wa wanayo yatenda.'),
(1616, 12, 20, 'Na wakamuuza kwa thamani duni, kwa akali ya pesa. Wala hawakuwa na haja naye.'),
(1617, 12, 21, 'Na yule aliye mnunua huko Misri alimwambia mkewe: Mtengenezee makaazi ya hishima huyu; huenda akatufaa au tukamfanya mwenetu. Na kama hivyo tulimweka Yusuf katika nchi ili tumfundishe kufasiri mambo. Na Mwenyezi Mungu ndiye Mwenye kushinda katika jambo lake, lakini watu wengi hawajui.'),
(1618, 12, 22, 'Na alipo fikilia utu uzima tulimpa hukumu na ilimu. Na kama hivi tunawalipa wanao tenda mema.'),
(1619, 12, 23, 'Na yule bibi wa nyumba aliyo kuwamo Yusuf alimtamani kinyume cha nafsi yake, na akafunga milango. Akamwambia: Njoo! Yusuf akasema: Audhubillahi! Najikinga na Mwenyezi Mungu. Huyu bwana wangu kaniweka maskani nzuri, na hakika wenye kudhulumu hawatengenekewi.'),
(1620, 12, 24, 'Na hakika yule mwanamke alimtamani, na Yusuf angeli mtamani lau kuwa hakuona ishara ya Mola wake Mlezi. Hayo hivyo ni kwa ajili tumuepushe na uovu na uchafu. Hakika yeye alikuwa katika waja wetu walio safishwa.'),
(1621, 12, 25, 'Na wote wawili wakakimbilia mlangoni, na mwanamke akairarua kanzu yake Yusuf kwa nyuma. Na wakamkuta bwana wake mlangoni. Mwanamke akasema: Hapana malipo ya mwenye kutaka kumfanyia maovu mkeo isipo kuwa kufungwa au kupewa adhabu chungu.'),
(1622, 12, 26, 'Yusuf akasema: Yeye ndiye aliye nitaka bila ya mimi kumtaka. Na shahidi aliye kuwa katika jamaa za mwanamke alisema: Ikiwa kanzu yake imechanwa mbele basi mwanamke amesema kweli, naye Yusuf ni katika waongo.'),
(1623, 12, 27, 'Na ikiwa kanzu yake imechanwa nyuma, basi mwanamke amesema uwongo, naye Yusuf ni katika wakweli.'),
(1624, 12, 28, 'Basi yule bwana alipoona kanzu yake imechanwa kwa nyuma, alisema: Hakika haya ni katika vitimbi vyenu wanawake. Bila ya shaka vitimbi vyenu ni vikuu.'),
(1625, 12, 29, 'Yusuf! Wachilia mbali haya. Na wewe, mwanamke, omba msamaha kwa dhambi zako. Kwa hakika wewe ni katika walio fanya makosa.'),
(1626, 12, 30, 'Na wanawake wa mjini wakawa wanasema: Mke wa Mheshimiwa anamtamani mtumishi wake! Hakika amesalitika kwa mapenzi. Sisi hakika tunamwona yumo katika upotovu ulio dhaahiri.'),
(1627, 12, 31, 'Alipo sikia yule bibi masengenyo yao aliwaita, na akawawekea matakia ya kuegemea, na akampa kila mmoja wao kisu. Akamwambia Yusuf: Tokeza mbele yao. Basi walipo mwona waliona ni kitu kikubwa kabisa, na wakajikata mikono yao. Wakasema: Hasha Lillahi! Huyu si mwanaadamu. Hakuwa huyu ila ni Malaika mtukufu.'),
(1628, 12, 32, 'Yule bibi aksema: Huyu basi ndiye huyo mliye nilaumia! Na kweli mimi nilimtaka naye akakataa. Na akitofanya ninayo muamrisha basi hapana shaka atafungwa gerezani, na atakuwa miongoni mwa walio chini kabisa.'),
(1629, 12, 33, 'Yusuf akasema: Ee Mola Mlezi wangu! Nastahabu kifungo kuliko haya anayo niitia. Na usipo niondoshea vitimbi vya wanawake mimi nitamili kwao, na nitakuwa katika wajinga.'),
(1630, 12, 34, 'Basi Mola wake Mlezi akamwitikia, na akamwondoshea vitimbi vyao. Hakika Yeye ni Msikizi Mjuzi.'),
(1631, 12, 35, 'Basi ikawadhihirikia baada ya kuona Ishara kuwa wamfunge kwa muda.'),
(1632, 12, 36, 'Wakaingia gerezani pamoja naye vijana wawili. Mmoja wao akasema: Hakika mimi nimeota nakamua mvinyo. Na mwingine akasema: Mimi nimeota nimebeba mikate juu ya kichwa changu, na ndege wanaila. Hebu tuambie tafsiri yake, kwani sisi tunakuona wewe ni katika watu wema.'),
(1633, 12, 37, 'Akasema: Hakitakufikilieni chakula mtakacho pewa ila nitakwambieni hakika yake kabla hakijakufikieni. Haya ni katika yale aliyo nifundisha Mola wangu Mlezi. Hakika mimi nimeacha mila ya watu wasio muamini Mwenyezi Mungu, na wakawa hawaiamini Akhera.'),
(1634, 12, 38, 'Na nimefuata mila ya baba zangu, Ibrahim, na Is-haq, na Yaa\'qub. Hatuna haki ya kumshirikisha Mwenyezi Mungu na chochote. Hiyo ni katika fadhila ya Mwenyezi Mungu juu yetu na juu ya watu; lakini watu wengi hawashukuru.'),
(1635, 12, 39, 'Enyi wafungwa wenzangu wawili! Je, mabwana wengi wanao farikiana wao kwa wao ni bora au Mwenyezi Mungu Mmoja Mwenye nguvu?'),
(1636, 12, 40, 'Hamuabudu badala yake ila majina tu mliyo yapanga nyinyi na baba zenu. Mwenyezi Mungu hakuyateremshia hayo uthibitisho wowote. Hapana hukumu ila ya Mwenyezi Mungu tu. Yeye ameamrisha msimuabudu yeyote isipo kuwa Yeye tu. Hiyo ndiyo Dini Iliyo Nyooka. Lakini watu wengi hawajui.'),
(1637, 12, 41, 'Enyi wafungwa wenzangu wawili! Ama mmoja wenu atamnywesha bwana wake mvinyo. Na ama mwengine atasulubiwa, na ndege watamla kichwa chake. Imekwisha katwa hukumu ya hilo jambo mlilo kuwa mkiuliza.'),
(1638, 12, 42, 'Na akamwambia yule aliye jua kuwa atavuka katika wale wawili: Nikumbuke kwa bwana wako. Lakini Shet\'ani alimsahaulisha kumtaja kwa bwana wake. Basi Yusuf akakaa gerezani miaka kadhaa.'),
(1639, 12, 43, 'Na siku moja mfalme alisema: Hakika mimi nimeota ng\'ombe saba wanene wanaliwa na ng\'ombe saba walio konda. Na mashuke saba mabichi na mengine makavu. Enyi waheshimiwa! Nambieni maana ya ndoto zangu, ikiwa nyinyi mnaweza kuagua ndoto.'),
(1640, 12, 44, 'Wakasema: Hizi ni ndoto zilizo paraganyika, wala sisi sio wenye kujua tafsiri ya ndoto hizi.'),
(1641, 12, 45, 'Hapo akasema yule aliye okoka katika wale wawili akakumbuka baada ya muda: Mimi nitakwambieni tafsiri yake. Basi nitumeni.'),
(1642, 12, 46, 'Yusuf! Ewe mkweli! Tueleze nini maana ya ng\'ombe saba wanene kuliwa na ng\'ombe saba walio konda. Na mashuke saba mabichi na mengine makavu, ili nirejee kwa watu wapate kujua.'),
(1643, 12, 47, 'Akasema: Mtalima miaka saba kwa juhudi. Mtacho vuna kiwacheni katika mashuke yake, isipo kuwa kidogo mtacho kula.'),
(1644, 12, 48, 'Kisha itakuja baadaye miaka saba ya shida itakayo kula kile mlicho iwekea isipo kuwa kidogo mtacho kihifadhi.'),
(1645, 12, 49, 'Kisha baada ya haya utakuja mwaka ambao katika mwaka huo watu watasaidiwa, na watakamua.'),
(1646, 12, 50, 'Na mfalme akasema: Mleteni kwangu! Basi mjumbe alipo mjia Yusuf alisema: Rejea kwa bwana wako ukamuulize khabari ya wale wanawake walio jikata mikono yao. Hakika Mola wangu Mlezi anazijua vyema hila zao.'),
(1647, 12, 51, 'Mfalme alisema: Mlikuwa mna nini mlipo mtaka Yusuf kinyume na nafsi yake? Wakasema: Hasha Lillahi! Sisi hatukujua ubaya wowote kwake. Mke wa Mheshimiwa akasema: Sasa haki imedhihiri. Mimi ndiye niliye mtaka kinyume na nafsi yake, na hakika yeye ni katika wakweli.'),
(1648, 12, 52, 'Hayo ni apate kujua ya kwamba mimi sikumfanyia khiyana nyuma yake; na ya kwamba Mwenyezi Mungu haziongoi hila za makhaini.'),
(1649, 12, 53, 'NAMI sijitoi lawamani. Kwa hakika nafsi ni mno kuamrisha maovu, isipo kuwa ile ambayo Mola wangu Mlezi aliyo irehemu. Hakika Mola wangu Mlezi ni Msamehevu na Mwenye kurehemu.'),
(1650, 12, 54, 'Basi mfalme akasema: Mleteni kwangu, awe wangu mwenyewe khasa. Basi alipo msemeza alinena: Hakika wewe leo umekwisha tamakani kwetu na umeaminika.'),
(1651, 12, 55, 'Yusuf akasema: Nifanye mshika khazina za nchi. Kwani hakika mimi ni mlinzi mjuzi.'),
(1652, 12, 56, 'Basi namna hivi tukampa Yusuf cheo katika nchi; anakaa humo popote anapo penda. Tunamfikishia rehema zetu tumtakaye, wala hatupuuzi malipo ya wafanyao mema.'),
(1653, 12, 57, 'Na hakika malipo ya Akhera ni bora zaidi, kwa walio amini na wakawa wachamngu.'),
(1654, 12, 58, 'Wakaja ndugu zake Yusuf, na wakaingia kwake. Yeye akawajua na wao hawakumjua.'),
(1655, 12, 59, 'Na alipo watengenezea tayari haja yao alisema: Nileteeni ndugu yenu kwa baba. Je! Hamwoni ya kwamba mimi ninatimiza kipimo, nami ni mbora wa kukirimu?'),
(1656, 12, 60, 'Na msipo niletea basi hampati kipimo chochote kwangu, wala msinikurubie.'),
(1657, 12, 61, 'Wakasema: Sisi tutamrairai baba yake; na bila ya shaka tutafanya hayo.'),
(1658, 12, 62, 'Na Yusuf akawaambia watumishi wake: Tieni bidhaa zao katika mizigo yao, ili wazione watakapo rudi kwa watu wao wapate kurejea tena.'),
(1659, 12, 63, 'Basi walipo rejea kwa baba yao, walisema: Ewe baba yetu! Tumenyimwa chakula zaidi, basi mpeleke nasi ndugu yetu ili tupate kupimiwa; na yakini sisi tutamlinda.'),
(1660, 12, 64, 'Je, nikuaminini kwa huyu ila kama nilivyo kuaminini kwa nduguye zamani? Lakini Mwenyezi Mungu ndiye Mbora wa kulinda, naye ndiye Mbora wa kurehemu kuliko wenye kurehemu wote.'),
(1661, 12, 65, 'Na walipo fungua mizigo yao wakakuta bidhaa zao wamerudishiwa. Wakasema: Ewe baba yetu! Tutake nini zaidi? Hizi bidhaa zetu tumerudishiwa. Wacha tuwaletee watu wetu chakula, na tutamlinda ndugu yetu, na tutaongeza shehena ya ngamia. Na hicho ni kipimo kidogo.'),
(1662, 12, 66, 'Akasema: Sitampeleka nanyi mpaka mnipe ahadi kwa Mwenyezi Mungu kwamba lazima mtamrejeza kwangu, ila ikiwa mmezungukwa. Basi walipo mpa ahadi yao alisema: Mwenyezi Mungu ndiye Mtegemewa kwa tuyasemayo.'),
(1663, 12, 67, 'Na akasema: Enyi wanangu! Msiingie mlango mmoja, bali ingieni kwa milango mbali mbali. Wala mimi sikufaeni kitu mbele ya Mwenyezi Mungu. Hukumu haiko ila kwa Mwenyezi Mungu tu. Juu yake Yeye mimi nimetegemea, na juu yake Yeye wategemee wanao tegemea.'),
(1664, 12, 68, 'Na walipo ingia kama alivyo waamrisha baba yao, haikuwafaa kitu kwa Mwenyezi Mungu, isipo kuwa ni haja tu iliyo kuwa katika nafsi ya Yaa\'qub aliitimiza. Na hakika yeye alikuwa na ilimu kwa sababu tulimfundisha; lakini watu wengi hawajui.'),
(1665, 12, 69, 'Na walipo ingia kwa Yusuf alimchukua nduguye akasema: Mimi hakika ni nduguyo. Basi usihuzunike kwa waliyo kuwa wakiyafanya.'),
(1666, 12, 70, 'Na alipo kwisha watengenezea mahitaji yao akakitia kikopo cha kunywea maji katika mzigo wa nduguye huyo, kisha mnadi akanadi: Enyi wasafiri! Hapana shaka nyinyi ni wezi!'),
(1667, 12, 71, 'Wakasema nao wamewakabili: Kwani mmepoteza nini?'),
(1668, 12, 72, 'Wakasema: Tumepoteza kopo la mfalme. Na ataye lileta atapewa shehena nzima ya ngamia. Nami ni dhaamini wa hayo.'),
(1669, 12, 73, 'Wakasema: Wallahi! Mnajua vyema kwamba sisi hatukuja kufanya uharibifu katika nchi hii, wala sisi si wezi.'),
(1670, 12, 74, 'Wakasema: Basi malipo yake yatakuwa nini ikiwa nyinyi ni waongo?'),
(1671, 12, 75, 'Wakasema: Malipo yake ni yule ambaye ataonekana nalo yeye huyo ndiye malipo yake. Hivi ndivyo tunavyo walipa madhaalimu.'),
(1672, 12, 76, 'Basi akaanza kwenye mizigo yao kabla ya mzigo wa nduguye. Kisha akakitoa katika mzigo wa nduguye. Hivi ndivyo tulivyo mpangia Yusuf. Hakuweza kumzuia nduguye kwa sharia ya mfalme isipo kuwa alivyo taka Mwenyezi Mungu. Tunawatukuza kwenye vyeo tuwatakao; na juu ya kila ajuaye yupo ajuaye zaidi.'),
(1673, 12, 77, 'Wakasema: Ikiwa huyu ameiba, basi nduguye vile vile aliiba zamani. Lakini Yusuf aliyaweka siri moyoni mwake wala hakuwaonyesha. Akawaambia: Nyinyi mko katika hali mbaya zaidi, na Mwenyezi Mungu anajua zaidi hayo mnayo singizia.'),
(1674, 12, 78, 'Wakasema: Ewe Mheshimiwa! Huyu anaye baba mzee sana. Kwa hivyo mchukue mmoja wetu badala yake. Hakika sisi tunakuona wewe ni katika watu wema.'),
(1675, 12, 79, 'Akasema: Mwenyezi Mungu apishe mbali kumshika yeyote yule isipo kuwa tuliye mkuta naye kitu chetu. Hivyo basi tutakuwa wenye kudhulumu.'),
(1676, 12, 80, 'Walipo kata tamaa naye wakenda kando kunong\'ona. Mkubwa wao akasema: Je! Hamjui ya kwamba baba yenu amechukua ahadi kwenu kutokana na Mwenyezi Mungu, na kabla ya hapo mlimkosa katika agano lenu juu ya Yusuf? Basi mimi sitotoka nchi hii mpaka baba yangu anipe ruhusa au Mwenyezi Mungu anihukumie. Na Yeye ndiye Mbora wa mahakimu.'),
(1677, 12, 81, 'Rudini kwa baba yenu, na mwambieni: Ee baba yetu! Mwanao ameiba. Na sisi hatutoi ushahidi ila kwa tunayo yajua. Wala sisi hatukuwa wenye kuhifadhi mambo ya ghaibu.'),
(1678, 12, 82, 'Na waulize watu wa mji tulio kuwako, na msafara tulio kuja nao. Na hakika sisi tunasema kweli.'),
(1679, 12, 83, 'Akasema: Bali nafsi zenu zimekushawishini kwenye jambo fulani. Subira ni njema! Asaa Mwenyezi Mungu akaniletea wote pamoja. Hakika Yeye ni Mjuzi Mwenye hikima.'),
(1680, 12, 84, 'Naye akajitenga nao, na akasema: Ah! Masikini Yusuf! Na macho yake yakawa meupe kwa huzuni aliyo kuwa akiizuia.'),
(1681, 12, 85, 'Wakasema: Wallahi! Huachi kumkumbuka Yusuf mpaka uwe mgonjwa au uwe katika walio hiliki.'),
(1682, 12, 86, 'Akasema: Hakika mimi namshitakia Mwenyezi Mungu sikitiko langu na huzuni yangu. Na ninajua kwa Mwenyezi Mungu msiyo yajua nyinyi.'),
(1683, 12, 87, 'Enyi wanangu! Nendeni mkamtafute Yusuf na nduguye, wala msikate tamaa na faraji ya Mwenyezi Mungu. Hakika hawakati tamaa na faraji ya Mwenyezi Mungu isipo kuwa watu makafiri.'),
(1684, 12, 88, 'Basi walipo ingia kwa Yusuf walisema: Ewe Mheshimiwa! Imetupata shida, sisi na watu wetu. Na tumeleta mali kidogo. Basi tupimie kipimo na fanya kama unatupa sadaka. Hakika Mwenyezi Mungu huwalipa watoao sadaka.'),
(1685, 12, 89, 'Akasema: Je! Mmejua mliyo mfanyia Yusuf na nduguye mlipo kuwa wajinga?'),
(1686, 12, 90, 'Wakasema: Kumbe wewe ndiye Yusuf? Akasema: Mimi ndiye Yusuf, na huyu ni ndugu yangu. Mwenyezi Mungu ametufanyia hisani. Hakika anaye mcha Mungu na akasubiri, basi Mwenyezi Mungu haachi ukapotea ujira wa wafanyao mema.'),
(1687, 12, 91, 'Wakasema: Wallahi! Mwenyezi Mungu amekufadhilisha wewe kuliko sisi, na hakika sisi tulikuwa wenye kukosa.'),
(1688, 12, 92, 'Akasema: Leo hapana lawama juu yenu. Mwenyezi Mungu atakusameheni, naye ni Mwingi wa kurehemu kuliko wote wanao rehemu.'),
(1689, 12, 93, 'Nendeni na kanzu yangu hii na mumwekee usoni baba yangu, atakuwa mwenye kuona. Na mje nao watu wenu wote wa nyumbani.'),
(1690, 12, 94, 'Na ulipo ondoka tu msafara baba yao alisema: Hakika mimi nasikia harufu ya Yusuf, lau kuwa hamtanituhumu.'),
(1691, 12, 95, 'Wakasema: Wallahi! Hakika bado ungali katika upotovu wako wa zamani.'),
(1692, 12, 96, 'Basi alipo fika mbashiri na akaiweka kanzu usoni pake alirejea kuona. Akasema: Je! Sikukwambieni kuwa mimi najua ya Mwenyezi Mungu msiyo yajua nyinyi?'),
(1693, 12, 97, 'Wakasema: Ee baba yetu! Tuombee msamaha kwa dhambi zetu. Hapana shaka sisi tulikuwa na makosa.'),
(1694, 12, 98, 'Akasema: Nitakuja kuombeeni msamaha kwa Mola wangu Mlezi. Hakika Yeye ndiye Mwenye maghfira na Mwenye kusamehe.'),
(1695, 12, 99, 'Na walipo ingia kwa Yusuf aliwakumbatia wazazi wake na akasema: Ingieni Misri, Inshallah, mmo katika amani.'),
(1696, 12, 100, 'Na akawapandisha wazazi wake kwenye kiti cha enzi. Na wote wakaporomoka kumsujudia. Na akasema: Ewe baba yangu! Hii ndiyo tafsiri ya ile ndoto yangu ya zamani. Na Mwenyezi Mungu ameijaalia iwe kweli. Na Mwenyezi Mungu amenifanyia wema kunitoa gerezani, na kukuleteni kutoka jangwani baada ya Shet\'ani kuchochea baina yangu na ndugu zangu. Hakika Mola wangu Mlezi ni Mpole kwa alitakalo. Bila ya shaka Yeye ni Mjuzi Mwenye hikima.'),
(1697, 12, 101, 'Ewe Mola wangu Mlezi! Hakika umenipa utawala, na umenifunza tafsiri ya mambo. Ewe Muumba wa mbingu na ardhi! Wewe ndiye Mlinzi wangu katika dunia na Akhera. Nifishe hali ya kuwa ni Muislamu na nichanganyishe na watendao mema.'),
(1698, 12, 102, 'Hizi ni katika khabari za ghaibu tulizo kufunulia. Na hukuwa pamoja nao walipo azimia shauri yao, walipo fanya njama zao wakizua vitimbi.'),
(1699, 12, 103, 'Na wengi wa watu si wenye kuamini hata ukijitahidi vipi..'),
(1700, 12, 104, 'Wala wewe huwaombi ujira kwa haya. Hayakuwa haya ila ni mawaidha kwa walimwengu wote.'),
(1701, 12, 105, 'Na ishara ngapi katika mbingu na ardhi wanazo zipitia, na hali ya kuwa wanazipuuza.'),
(1702, 12, 106, 'Na wengi katika wao hawamuamini Mwenyezi Mungu pasina kuwa ni washirikina.'),
(1703, 12, 107, 'Je! Wanaaminisha kuwa haitowajia adhabu ya Mwenyezi Mungu ya kuwagubika, au haitawafikia Saa ya Kiyama kwa ghafla, na hali hawatambui?'),
(1704, 12, 108, 'Sema: Hii ndiyo Njia yangu - ninalingania kwa Mwenyezi Mungu kwa kujua - mimi na wanao nifuata. Na ametakasika Mwenyezi Mungu! Wala mimi si katika washirikina.'),
(1705, 12, 109, 'Na hatukuwatuma Mitume kabla yako isipo kuwa wanaume tulio wafunulia wahyi miongoni mwa watu wa mijini. Je! Hawatembei katika ardhi wakaona jinsi ulivyo kuwa mwisho wa walio kuwa kabla yao? Na hakika nyumba ya Akhera ni bora kwa wamchao Mungu. Basi hamfahamu?'),
(1706, 12, 110, 'Hata Mitume walipo kata tamaa na wakaona kuwa wamekadhibishwa, hapo ikawajia nusura yetu, wakaokolewa tuwatakao. Na adhabu yetu haitowawacha kaumu ya wakosefu.'),
(1707, 12, 111, 'Kwa hakika katika hadithi zao limo zingatio kwa wenye akili. Si maneno yaliyo zuliwa, bali ni ya kusadikisha yaliyo kabla yake, na ufafanuzi wa kila kitu, na ni uwongofu na rehema kwa kaumu yenye kuamini.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Alif Lam Mym Ra. (A. L. M. R.) Hizi ni Ishara za Kitabu. Na uliyo teremshiwa kutoka kwa Mola wako Mlezi ni Haki. Lakini wengi wa watu hawaamini.'),
(1709, 13, 2, 'Mwenyezi Mungu aliye ziinua mbingu bila ya nguzo mnazo ziona, ametawala kwenye Ufalme wake, na amefanya jua na mwezi yamt\'ii, kila kimoja kinakwenda kwa kiwango maalumu. Anayapanga mambo, na anazipambanua Ishara ili mpate kuwa na yakini kukutana na Mola wenu Mlezi.'),
(1710, 13, 3, 'Na ndiye aliye itandaza ardhi na akaweka humo milima na mito. Na katika kila matunda akafanya dume na jike. Huufunika usiku juu ya mchana. Hakika katika haya zimo Ishara kwa watu wanao fikiri.'),
(1711, 13, 4, 'Na katika ardhi vimo vipande vilivyo karibiana, na zipo bustani za mizabibu, na mimea mingine, na mitende yenye kuchipua kwenye shina moja na isio chipua kwenye shina moja, nayo inatiliwa maji yale yale. Na tunaifanya baadhi yao bora kuliko mengine katika kula. Hakika katika hayo zimo ishara kwa watu wanao tia mambo akilini.'),
(1712, 13, 5, 'Na kama ukistaajabu, basi cha ajabu zaidi ni huo usemi wao: Ati tukisha kuwa mchanga kweli tutakuwa katika umbo jipya? Hao ndio walio mkufuru Mola wao Mlezi. Na hao ndio watao kuwa na makongwa shingoni mwao, na hao ndio watu wa Motoni. Humo watadumu.'),
(1713, 13, 6, 'Na wanakuhimiza ulete maovu kabla ya mema, hali ya kuwa zimekwisha pita kabla yao adhabu za kupigiwa mfano. Na hakika Mola wako Mlezi ni Mwenye msamaha kwa watu juu ya udhalimu wao. Na hakika Mola wako Mlezi ni Mkali wa kuadhibu.'),
(1714, 13, 7, 'Na wale walio kufuru husema: Mbona hakuteremshiwa muujiza kutoka kwa Mola wake Mlezi? Hakika wewe ni Mwonyaji, na kila kaumu ina wa kuwaongoa.'),
(1715, 13, 8, 'Mwenyezi Mungu anajua mimba abebayo kila mwanamke, na kinacho punguka na kuzidi matumboni. Na kila kitu kwake ni kwa kipimo.'),
(1716, 13, 9, 'Yeye ndiye Mwenye kujua yanayo onekana na yasio onekana; Mkuu Aliye tukuka.'),
(1717, 13, 10, 'Ni sawa anaye ficha kauli yake kati yenu na anaye idhihirisha, na anaye jibanza usiku na anaye tembea jahara mchana.'),
(1718, 13, 11, 'Kila mtu analo kundi la malaika mbele yake na nyuma yake linamlinda kwa amri ya Mwenyezi Mungu. Hakika Mwenyezi Mungu habadili yaliyoko kwa watu mpaka wabadili wao yaliyomo naf- sini mwao. Na Mwenyezi Mungu anapo watakia watu adhabu hakuna cha kuzuia wala hawana mlinzi yeyote badala yake.'),
(1719, 13, 12, 'Yeye ndiye anaye kuonyesheni umeme kwa khofu na tamaa, na huyaleta mawingu mazito.'),
(1720, 13, 13, 'Na radi inamtakasa Mwenyezi Mungu kwa kumhimidi, na Malaika pia, kwa kumkhofu. Naye hupeleka mapigo ya radi yakampata amtakaye. Nao wanabishana juu ya Mwenyezi Mungu. Na Yeye ni Mwenye adhabu kali!'),
(1721, 13, 14, 'Wa kuombwa kweli ni Yeye tu. Na hao wanao waomba badala yake hawawajibu chochote; bali ni kama mwenye kunyoosha viganja vyake kwenye maji ili yafike kinywani mwake, lakini hayafiki. Na maombi ya makafiri hayako ila katika upotovu.'),
(1722, 13, 15, 'Na viliomo mbinguni na katika ardhi vinamsujudia Mwenyezi Mungu tu vikitaka visitake. Na pia vivuli vyao asubuhi na jioni.'),
(1723, 13, 16, 'Sema: Ni nani Mola Mlezi wa mbingu na ardhi? Sema: Mwenyezi Mungu. Sema: Basi je, mnawafanya wenginewe badala yake kuwa ni walinzi, nao hawajifai wenyewe kwa jema wala baya? Sema: Hebu kipofu na mwenye kuona huwa sawa? Au hebu huwa sawa giza na mwangaza? Au wamemfanyia Mwenyezi Mungu washirika walio umba kama alivyo umba Yeye, na viumbe hivyo vikawadanganyikia? Sema: Mwenyezi Mungu ndiye Muumba wa kila kitu. Na Yeye ni Mmoja Mwenye kushinda!'),
(1724, 13, 17, 'Ameteremsha maji kutoka mbinguni. Na mabonde yakamiminika maji kwa kadiri yake. Na mafuriko yakachukua mapovu yaliyo kusanyika juu yake. Na kutokana na wanavyo yayusha katika moto kwa kutaka mapambo au vyombo hutokea povu vile vile. Namna hivyo Mwenyezi Mungu ndivyo anavyo piga mifano ya Haki na baat\'ili. Basi lile povu linapita kama takataka tu basi. Ama kinacho wafaa watu hubakia kwenye ardhi. Hivi ndivyo Mwenyezi Mungu anavyo piga mifano.'),
(1725, 13, 18, 'Walio muitikia Mola wao Mlezi watapata wema. Na wasio muitikia, hata wangeli kuwa navyo vyote viliomo katika ardhi na mfano wa hivyo, bila ya shaka wangeli vitoa kujikombolea! Hao watapata hisabu mbaya kabisa, na makao yao ni Jahannamu. Na hapo ni pahala pabaya mno!'),
(1726, 13, 19, 'Je! Anaye jua ya kwamba yaliyo teremshwa kwako kutoka kwa Mola wako Mlezi ni Haki ni sawa na aliye kipofu? Wenye akili ndio wanao zingatia,'),
(1727, 13, 20, 'Wale ambao wanatimiza ahadi ya Mwenyezi Mungu, wala hawavunji maagano.'),
(1728, 13, 21, 'Na wale ambao huyaunga aliyo amrisha Mwenyezi Mungu yaungwe, na wanaikhofu hisabu mbaya.'),
(1729, 13, 22, 'Na ambao husubiri kwa kutaka radhi ya Mola wao Mlezi, na wakashika Sala, na wakatoa, kwa siri na kwa uwazi, katika tulivyo wapa; na wakayaondoa maovu kwa wema. Hao ndio watakao pata malipo ya Nyumba ya Akhera.'),
(1730, 13, 23, 'Nayo ni Bustani za milele, wataziingia wao na walio wema miongoni mwa baba zao, na wake zao, na vizazi vyao. Na Malaika wanawaingilia katika kila mlango.'),
(1731, 13, 24, '(Wakiwaambia) Assalamu Alaikum! Amani iwe juu yenu, kwa sababu ya mlivyo subiri! Basi ni mema mno malipo ya Nyumba ya Akhera.'),
(1732, 13, 25, 'Na wale wanao vunja ahadi ya Mwenyezi Mungu baada ya kuzifunga, na wanakata aliyo amrisha Mwenyezi Mungu yaungwe, na wanafanya fisadi katika nchi: hao ndio watakao pata laana, na watapata Nyumba mbaya.'),
(1733, 13, 26, 'Mwenyezi Mungu humkunjulia riziki amtakaye, na humkunjia kwa kipimo. Na wamefurahia maisha ya dunia. Na uhai wa dunia kwa kulingana na Akhera si kitu ila ni starehe ndogo.'),
(1734, 13, 27, 'Na wanasema walio kufuru: Kwa nini hakuteremshiwa muujiza kutoka kwa Mola wake Mlezi. Sema: Hakika Mwenyezi Mungu humwacha kupotea amtakaye, na humwongoa anaye elekea kwake,'),
(1735, 13, 28, 'Wale walio amini na zikatua nyoyo zao kwa kumkumbuka Mwenyezi Mungu. Hakika kwa kumkumbuka Mwenyezi Mungu ndio nyoyo hutua!'),
(1736, 13, 29, 'Wale walio amini na wakatenda mema watakuwa na raha na marejeo mazuri.'),
(1737, 13, 30, 'Ndio kama hivi tumekutuma kwa umma ambao wamekwsiha pita kabla yao umati nyengine, ili uwasomee tunayo kufunulia, na wao wanamkufuru Rahmani, Mwingi wa Rehema! Sema: Yeye ndiye Mola wangu Mlezi. Hapana mungu isipo kuwa Yeye. Juu yake nimetegemea, na kwake ndio marejeo yangu!'),
(1738, 13, 31, 'Na kama ingeli kuwako Qur\'ani ndiyo inayo endeshewa milima, na kupasuliwa ardhi, na kusemeshewa wafu, (basi ingeli kuwa Qur\'ani hii). Bali mambo yote ni ya Mwenyezi Mungu. Je, hawajajua walio amini kwamba lau kuwa Mwenyezi Mungu ange penda bila ya shaka angeli waongoa watu wote? Wala walio kufuru hawaachi kusibiwa na balaa kwa waliyo yatenda, au ikawateremkia karibu na nyumbani kwao, mpaka ifike ahadi ya Mwenyezi Mungu. Hakika Mwenyezi Mungu havunji miadi yake.'),
(1739, 13, 32, 'Na hakika walifanyiwa kejeli Mitume walio kuwa kabla yako. Na nikawapururia wale walio kufuru, kisha nikawashika! Basi ilikuwaje adhabu yangu!'),
(1740, 13, 33, 'Je! Anaye isimamia kila nafsi kwa yale iliyo yachuma...? Na wamemfanyia Mwenyezi Mungu kuwa na washirika! Sema watajeni. Au ndio mnampa khabari ya yale asiyo yajua katika ardhi; au ni maneno matupu? Bali walio kufuru wamepambiwa vitimbi vyao na wamezuiliwa njia. Na ambaye Mwenyezi Mungu amemwacha apotee basi hana wa kumwongoa.'),
(1741, 13, 34, 'Wanayo adhabu katika maisha ya dunia, na adhabu ya Akhera hapana shaka ina mashaka zaidi. Na wala hawatakuwa na wa kuwalinda na Mwenyezi Mungu.'),
(1742, 13, 35, 'Mfano wa Bustani waliyo ahidiwa wachamngu, kati yake inapita mito, matunda yake ni ya daima, na pia kivuli chake. Huu ndio mwisho wa wale walio walio jilinda. Na mwisho wa makafiri ni Moto.'),
(1743, 13, 36, 'Na tulio wapa Kitabu wanafurahia uliyo teremshiwa. Na katika makundi mengine wapo wanao yakataa baadhi ya haya. Sema: Nimeamrishwa nimuabudu Mwenyezi Mungu, na wala nisimshirikishe. Kuendea kwake Yeye ndiyo ninaita, na kwake Yeye ndio marejeo yangu.'),
(1744, 13, 37, 'Na ndio kama hivi tumeiteremsha Qur\'ani kuwa ni hukumu kwa lugha ya Kiarabu. Na ukifuata matamanio yao baada ya kukujia ilimu hii, hutakuwa na rafiki wala mlinzi mbele ya Mwenyezi Mungu.'),
(1745, 13, 38, 'Na hakika Sisi tulikwisha watuma Mitume kabla yako, na tukajaalia wawe na wake na dhuriya. Na haiwi kwa Mtume kuleta miujiza isipo kuwa kwa idhini ya Mwenyezi Mungu. Kila kipindi kina hukumu yake.'),
(1746, 13, 39, 'Mwenyezi Mungu hufuta na huthibitisha ayatakayo. Na asili ya hukumu zote iko kwake.'),
(1747, 13, 40, 'Na ikiwa tukikuonyesha baadhi ya tuliyo waahidi au tukakufisha kabla yake, juu yako wewe ni kufikisha ujumbe na juu yetu ni hisabu.'),
(1748, 13, 41, 'Je! Hawakuona kwamba tunaifikia ardhi tukiipunguza nchani mwake? Na Mwenyezi Mungu huhukumu, na hapana wa kupinga hukumu yake, naye ni Mwepesi wa kuhisabu.'),
(1749, 13, 42, 'Na walipanga walio kuwa kabla yao, lakini Mwenyezi Mungu ndiye Mwenye mipango yote. Yeye anajua inayo chuma kila nafsi. Na makafiri watajua ni ya nani nyumba ya mwisho Akhera!'),
(1750, 13, 43, 'Na walio kufuru wanasema: Wewe hukutumwa. Sema: Mwenyezi Mungu anatosha kuwa shahidi baina yangu na nyinyi, na pia yule mwenye ilimu ya Kitabu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Alif Lam Ra. (A. L. R.) Hichi ni Kitabu tulicho kiteremsha kwako ili uwatowe watu kwenye giza uwapeleke kwenye muwangaza, kwa idhini ya Mola wao Mlezi, uwafikishe kwenye Njia ya Mwenye nguvu, Msifiwa,'),
(1752, 14, 2, 'Naye ni Mwenyezi Mungu ambaye ni vyake Yeye vyote viliomo katika mbingu na ardhi. Na ole wao makafiri kwa adhabu kali!'),
(1753, 14, 3, 'Wale wanao fadhilisha maisha ya dunia kuliko Akhera, na wanawazuilia watu wasifuate Njia ya Mwenyezi Mungu, na wanataka kuipotosha. Hao wamepotelea mbali.'),
(1754, 14, 4, 'Na hatukumtuma Mtume ila kwa ulimi wa kaumu yake ili apate kuwabainishia. Basi Mwenyezi Mungu humuacha akapotea amtakaye, na akamwongoa amtakaye. Naye ni Mtukufu Mwenye nguvu na Mwenye hikima.'),
(1755, 14, 5, 'Na tulimtuma Musa pamoja na miujiza yetu, tukamwambia: Watoe watu wako kwenye giza uwapeleke kwenye mwangaza. Na uwakumbushe siku za Mwenyezi Mungu. Hakika katika hayo zipo ishara kwa kila mwenye kusubiri akashukuru.'),
(1756, 14, 6, 'Na Musa alipo waambia watu wake: Kumbukeni neema ya Mwenyezi Mungu aliyo kujaalieni, pale alipo kuokoeni kutokana na watu wa Firauni walio kupeni adhabu mbaya kabisa, wakiwachinja wenenu wanaume, na wakiwawacha hai wanawake. Na katika hayo ulikuwa mtihani mkubwa unao toka kwa Mola wenu Mlezi.'),
(1757, 14, 7, 'Na alipo tangaza Mola wenu Mlezi: Mkishukuru nitakuzidishieni; na mkikufuru, basi adhabu yangu ni kali.'),
(1758, 14, 8, 'Na Musa alisema: Mkikufuru nyinyi na wote waliomo duniani, hakika Mwenyezi Mungu ni Mkwasi, Anajitosha, Msifiwa.'),
(1759, 14, 9, 'Je! Hazikukufikieni khabari za walio kabla yenu? Kaumu ya Nuhu, na A\'adi, na Thamud, na walio baada yao, ambao hapana awajuaye ila Mwenyezi Mungu? Mitume wao waliwajia kwa hoja zilizo wazi, na wao wakarudisha mikono yao kwenye vinywa vyao, wakasema: Hakika sisi tumeyakataa hayo mliyo tumwa, na hakika sisi tuna shaka kubwa na mnayo tuitia.'),
(1760, 14, 10, 'Mitume wao wakasema: Ati pana shaka na Mwenyezi Mungu, Muumba mbingu na ardhi? Yeye anakuiteni apate kukufutieni madhambi yenu, na akupeni muhula mpaka muda ulio wekwa. Wakasema: Nyinyi si chochote ila ni wanaadamu kama sisi. Mnataka kutuzuilia na waliyo kuwa wakiabudu baba zetu. Basi tuleteeni hoja ilio wazi.'),
(1761, 14, 11, 'Mitume wao wakawaambia: Sisi kweli si chochote ila ni wanaadamu kama nyinyi. Lakini Mwenyezi Mungu humfanyia hisani amtakaye katika waja wake. Wala sisi hatuwezi kuleta uthibitisho ila kwa idhini ya Mwenyezi Mungu. Na juu ya Mwenyezi Mungu ndio wategemee Waumini.'),
(1762, 14, 12, 'Na tuna nini hata tusimtegemee Mwenyezi Mungu, na hali Yeye ametuongoa kwenye Njia zetu. Na hapana shaka sisi tutayavumilia hayo maudhi mnayo tuudhi. Na juu ya Mwenyezi Mungu wategemee wanao tegemea.'),
(1763, 14, 13, 'Na walio kufuru wakawaambia Mitume wao: Tutakutoeni katika nchi yetu, au mrudi katika mila yetu. Basi Mola wao Mlezi aliwaletea wahyi: Hakika tutawaangamiza walio dhulumu!'),
(1764, 14, 14, 'Na tutakuwekeni katika ardhi baada yao. Haya ni kwa anaye ogopa kusimamishwa mbele yangu, na akaogopa maonyo yangu.'),
(1765, 14, 15, 'Na wakaomba ushindi, na akashindwa kila jabari mkaidi,'),
(1766, 14, 16, 'Ambaye nyuma yake ipo Jahannamu, na atanywishwa maji ya usaha.'),
(1767, 14, 17, 'Awe anayagugumia, wala hawezi kuyameza. Na mauti yawe yanamjia kutoka kila upande, naye wala hafi. Na zaidi ya hayo ipo adhabu nyengine kali vile vile.'),
(1768, 14, 18, 'Mfano wa walio mkufuru Mola wao Mlezi - vitendo vyao ni kama jivu linalo peperushwa kwa nguvu za upepo katika siku ya mdharba. Hawawezi kupata chochote katika waliyo yafanya. Huko ndiko kupotelea mbali!'),
(1769, 14, 19, 'Huoni kuwa Mwenyezi Mungu ameziumba mbingu na ardhi kwa Haki? Akitaka atakuondoeni na alete viumbe wapya!'),
(1770, 14, 20, 'Na hilo kwa Mwenyezi Mungu si jambo gumu.'),
(1771, 14, 21, 'Na wote watahudhuria mbele ya Mwenyezi Mungu. Wanyonge watawaambia walio takabari: Sisi tulikuwa wafwasi wenu; basi hebu hamtuondolei kidogo hivi katika adhabu ya Mwenyezi Mungu? Watasema: Lau Mwenyezi Mungu angeli tuongoa basi hapana shaka nasi tungeli kuongoeni. Ni mamoja kwetu tukipapatika au tukisubiri; hatuna pa kukimbilia.'),
(1772, 14, 22, 'Na Shet\'ani atasema itapo katwa hukumu: Hakika Mwenyezi Mungu alikuahidini ahadi ya kweli. Nami nalikuahidini; lakini sikukutimizieni. Na sikuwa na mamlaka juu yenu, isipo kuwa nilikuiteni, nanyi mkaniitikia. Basi msinilaumu mimi, bali jilaumuni wenyewe. Mimi siwezi kuwa mtetezi wenu, wala nyinyi hamwezi kuwa watetezi wangu. Hakika mimi nilikataa tangu zamani kunishirikisha na Mwenyezi Mungu. Hakika madhaalimu watakuwa na adhabu chungu.'),
(1773, 14, 23, 'Na walio amini na wakatenda mema wataingizwa katika Mabustani yapitayo mito kati yake, wadumu humo kwa idhini ya Mola wao Mlezi. Maamkio yao humo yatakuwa: Salaam!'),
(1774, 14, 24, 'Hebu hukuona vipi Mwenyezi Mungu alivyo piga mfano wa neno zuri? Ni kama mti mzuri, mizizi yake ni imara, na matawi yake yako mbinguni.'),
(1775, 14, 25, 'Hutoa matunda yake kila wakati kwa idhini ya Mola wake Mlezi. Na Mwenyezi Mungu huwapigia watu mifano ili wapate kukumbuka.'),
(1776, 14, 26, 'Na mfano wa neno ovu ni kama mti muovu, ulio ng\'olewa juu ya ardhi. Hauna imara.'),
(1777, 14, 27, 'Mwenyezi Mungu huwatia imara wenye kuamini kwa kauli ya imara katika maisha ya dunia na katika Akhera. Na Mwenyezi Mungu huwaacha kupotea hao wenye kudhulumu. Na Mwenyezi Mungu hufanya apendavyo.'),
(1778, 14, 28, 'Hebu hukuwaona wale walio badilisha neema ya Mwenyezi Mungu kwa kufuru, na wakawafikisha watu wao kwenye nyumba ya maangamizo?'),
(1779, 14, 29, 'Nayo ni Jahannamu! Maovu yaliyoje makaazi hayo!'),
(1780, 14, 30, 'Na walimfanyia Mwenyezi Mungu washirika ili wapoteze watu kwenye Njia yake. Sema: Stareheni! Kwani marejeo yenu ni Motoni!'),
(1781, 14, 31, 'Waambie waja wangu walio amini, washike Sala, na watoe katika tulivyo waruzuku, kwa siri na dhaahiri, kabla haijafika Siku isiyo kuwa na biashara wala urafiki.'),
(1782, 14, 32, 'Mwenyezi Mungu ndiye aliye ziumba mbingu na ardhi, na akateremsha maji kutoka mbinguni. Na kwa hayo akatoa matunda kuwa ni riziki yenu. Na akafanya yakutumikieni majahazi yanayo pita baharini kwa amri yake, na akaifanya mito ikutumikieni.'),
(1783, 14, 33, 'Na akalifanya jua na mwezi kwa manufaa yenu daima dawamu, na akaufanya usiku na mchana kwa manufaa yenu.'),
(1784, 14, 34, 'Na akakupeni kila mlicho muomba. Na mkihisabu neema za Mwenyezi Mungu hamwezi kuzidhibiti. Hakika mwanaadamu ni dhaalimu mkubwa, mwenye kuzikufuru neema.'),
(1785, 14, 35, 'Na Ibrahim alipo sema: Ewe Mola wangu Mlezi! Ujaalie mji huu uwe wa amani, na uniepushe mimi na wanangu na kuabudu masanamu.'),
(1786, 14, 36, 'Ewe Mola wangu Mlezi! Hakika hayo yamewapoteza watu wengi. Basi aliye nifuata mimi huyo ni wangu, na aliye niasi, hakika Wewe ni Mwenye kusamehe Mwenye kurehemu.'),
(1787, 14, 37, 'Mola wetu Mlezi! Hakika mimi nimewaweka baadhi ya dhuriya zangu katika bonde lisilo kuwa na mimea, kwenye Nyumba yako Takatifu, ewe Mola wetu Mlezi, ili washike Sala. Basi zijaalie nyoyo za watu zielekee kwao, na waruzuku matunda, ili wapata kushukuru.'),
(1788, 14, 38, 'Ewe Mola wetu Mlezi! Hakika Wewe unajua tunayo yaficha na tunayo yatangaza. Na hapana kitu kinacho fichikana kwa Mwenyezi Mungu katika ardhi wala katika mbingu.'),
(1789, 14, 39, 'Alhamdulillahi! Himdi zote ni za Mwenyezi Mungu aliye nipa juu ya uzee wangu Ismail na Is-haq. Hakika Mola wangu Mlezi ni Mwenye kusikia maombi.'),
(1790, 14, 40, 'Mola wangu Mlezi! Nijaalie niwe mwenye kushika Sala, na katika dhuriya zangu pia. Ewe Mola wetu Mlezi, na ipokee dua yangu.'),
(1791, 14, 41, 'Mola wetu! Unighufirie mimi na wazazi wangu wote wawili, na Waumini, siku ya kusimama hisabu.'),
(1792, 14, 42, 'Wala usidhani Mwenyezi Mungu ameghafilika na wanayo yafanya madhaalimu. Hakika Yeye anawaakhirisha tu mpaka siku yatapo kodoka macho yao.'),
(1793, 14, 43, 'Nao wako mbioni, vichwa juu, na macho hayapepesi, na nyoyo zao tupu.'),
(1794, 14, 44, 'Na waonye watu siku itapo wajia adhabu, na walio dhulumu waseme: Ewe Mola wetu Mlezi! Tuakhirishe muda kidogo tuitikie wito wako na tuwafuate Mitume. Kwani nyinyi si mliapa zamani kwamba hamtaondokewa?'),
(1795, 14, 45, 'Na mkakaa katika maskani zile zile za walio zidhulumu nafsi zao. Na ikakudhihirikieni jinsi tulivyo watendea. Nasi tukakupigieni mifano mingi.'),
(1796, 14, 46, 'Na kwa hakika walifanya vitimbi vyao, na vitimbi vyao anavijua Mwenyezi Mungu. Wala vitimbi vyao si vya kuondosha milima.'),
(1797, 14, 47, 'Basi usimdhanie Mwenyezi Mungu kuwa ni mwenye kuwavunjia ahadi yake Mitume wake. Hakika Mwenyezi Mungu ni Mwenye kushinda, na ni Mwenye kulipiza.'),
(1798, 14, 48, 'Siku itapo geuzwa ardhi iwe ardhi nyengine, na mbingu pia. Nao watahudhuria mbele ya Mwenyezi Mungu Mmoja, Mwenye nguvu.'),
(1799, 14, 49, 'Na utaona wakhalifu siku hiyo wamefungwa katika minyororo;'),
(1800, 14, 50, 'Nguo zao zitakuwa za lami, na Moto utazigubika nyuso zao.'),
(1801, 14, 51, 'Ili Mwenyezi Mungu ailipe kila nafsi kwa yale iliyo yachuma. Hakika Mwenyezi Mungu ni Mwepesi wa kuhisabu.'),
(1802, 14, 52, 'Hili ni Tangazo liwafikie watu, liwaonye, na wapate kujua kuwa hakika Yeye ni Mungu Mmoja, na wapate kukumbuka wenye akili.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Alif Lam Ra. (A. L. R). Hizi ni Aya za Kitabu na Qur\'ani inayo bainisha.'),
(1804, 15, 2, 'HUENDA ikawa walio kufuru wakatamani wange kuwa Waislamu.'),
(1805, 15, 3, 'Waache wale, na wastarehe, na iwazuge tamaa. Watakuja jua.'),
(1806, 15, 4, 'Na hatukuuangamiza mji wowote ule ila ulikuwa na muda wake maalumu.'),
(1807, 15, 5, 'Hawawezi watu wowote kuitangulia ajali yao, wala kuchelewa.'),
(1808, 15, 6, 'Na walisema: Ewe uliyeteremshiwa Ukumbusho! Hakika wewe ni mwendawazimu.'),
(1809, 15, 7, 'Mbona hutuletei Malaika ikiwa wewe ni miongoni mwa wasemao kweli?'),
(1810, 15, 8, 'Sisi hatuwateremshi Malaika ila kwa sababu ya haki, na hapo hawatapewa muhula.'),
(1811, 15, 9, 'Hakika Sisi ndio tulio teremsha Ukumbusho huu, na hakika Sisi ndio tutao ulinda.'),
(1812, 15, 10, 'Na hakika tulikwisha watumia Mitume mataifa ya mwanzo.'),
(1813, 15, 11, 'Na hakuwafikia Mtume ila walimkejeli.'),
(1814, 15, 12, 'Na kama hivi ndivyo tunavyo ingiza katika nyoyo za wakosefu.'),
(1815, 15, 13, 'Hawayaamini haya, na hali ya kuwa umekwisha wapitia mfano wa watu wa kale.'),
(1816, 15, 14, 'Na lau tungeli wafungulia mlango wa mbingu, wakawa wanapanda,'),
(1817, 15, 15, 'Basi wangeli sema: Macho yetu yamelevywa, bali sisi wenyewe tumerogwa.'),
(1818, 15, 16, 'Na hakika tumeweka katika mbingu vituo vya sayari, na tumezipamba kwa wenye kuangalia.'),
(1819, 15, 17, 'Na tumezilinda na kila shetani afukuzwaye.'),
(1820, 15, 18, 'Isipo kuwa mwenye kusikiliza kwa kuibia, naye hufuatwa na kijinga cha moto kinacho onekana.'),
(1821, 15, 19, 'Na ardhi tumeitandaza na humo tumeweka milima, na tumeotesha kila kitu kwa kiasi chake.'),
(1822, 15, 20, 'Na tumekujaalieni humo vitu vya maisha yenu, na ya hao ambao nyinyi sio wenye kuwaruzuku.'),
(1823, 15, 21, 'Na hakuna chochote ila asili yake inatokana na Sisi; wala hatukiteremshi ila kwa kipimo maalumu.'),
(1824, 15, 22, 'Na tunazipeleka pepo za kupandishia, na tunateremsha kutoka mbinguni maji, kisha tukakunywesheni maji hayo. Wala si nyinyi mnayo yaweka.'),
(1825, 15, 23, 'Na Sisi ndio tunao huisha na tunao fisha. Na Sisi ndio Warithi.'),
(1826, 15, 24, 'Na tunawajua walio tangulia katika nyinyi, na tunawajua walio taakhari.'),
(1827, 15, 25, 'Na hakika Mola wako Mlezi ndiye atakaye wakusanya. Hakika Yeye ni Mwenye hikima na Mjuzi.'),
(1828, 15, 26, 'Na tulimuumba mtu kwa udongo unao toa sauti, unao tokana na matope yaliyo tiwa sura.'),
(1829, 15, 27, 'Na majini tuliwaumba kabla kwa moto wa upepo umoto.'),
(1830, 15, 28, 'Na Mola wako Mlezi alipo waambia Malaika: Hakika Mimi nitamuumba mtu kwa udongo unao toa sauti, unao tokana na matope yaliyo tiwa sura.'),
(1831, 15, 29, 'Basi nitakapo mkamilisha na nikampulizia roho yangu, basi mumwangukie kumsujudia.'),
(1832, 15, 30, 'Basi Malaika wote pamoja walimsujudia,'),
(1833, 15, 31, 'Isipo kuwa Iblisi. Yeye alikataa kuwa pamoja na walio sujudu.'),
(1834, 15, 32, '(Mwenyezi Mungu) akasema: Ewe Iblisi! Una nini hata hukuwa pamoja na walio sujudu?'),
(1835, 15, 33, 'Akasema: Haiwi mimi nimsujudie mtu uliye muumba kwa udongo unao toa sauti, unao tokana na matope yenye sura.'),
(1836, 15, 34, '(Mwenyezi Mungu) akasema: Basi toka humo, kwani hakika wewe ni maluuni!'),
(1837, 15, 35, 'Na hakika juu yako ipo laana mpaka Siku ya Malipo.'),
(1838, 15, 36, 'Akasema (Iblisi): Mola wangu Mlezi! Nipe muhula mpaka siku watapo fufuliwa.'),
(1839, 15, 37, '(Mwenyezi Mungu) akasema: Hakika wewe ni katika walio pewa muhula'),
(1840, 15, 38, 'Mpaka siku ya wakati maalumu.'),
(1841, 15, 39, 'Akasema: Mola wangu Mlezi! Ilivyo kuwa umenitia makosani, basi nahakikisha nitawazaini hapa duniani na nitawapoteza wote,'),
(1842, 15, 40, 'Ila waja wako walio safika.'),
(1843, 15, 41, 'Akasema: Hii Njia ya kujia kwangu Iliyo Nyooka.'),
(1844, 15, 42, 'Hakika waja wangu, wewe hutakuwa na mamlaka juu yao, isipo kuwa wale wapotofu walio kufuata.'),
(1845, 15, 43, 'Na bila shaka Jahannamu ndipo pahali pao walipo ahidiwa wote.'),
(1846, 15, 44, 'Ina milango saba; na kwa kila mlango iko sehemu walio tengewa.'),
(1847, 15, 45, 'Hakika wachamngu watakuwa katika Mabustani na chemchem.'),
(1848, 15, 46, '(Wataambiwa:) Ingieni kwa salama na amani.'),
(1849, 15, 47, 'Na tutaondoa chuki iliyo kuwamo vifuani mwao, wawe ndugu juu ya viti vya enzi wameelekeana.'),
(1850, 15, 48, 'Humo hayatawagusa machofu, wala hawatatolewa humo.'),
(1851, 15, 49, 'Waambie waja wangu ya kwamba Mimi ndiye Mwenye kusamehe, Mwenye kurehemu.'),
(1852, 15, 50, 'Na kwamba adhabu yangu ndio adhabu iliyo chungu!'),
(1853, 15, 51, 'Na uwape khabari za wageni wa Ibrahim.'),
(1854, 15, 52, 'Walipo ingia kwake na wakasema: Salama! Yeye akasema: Hakika sisi tunakuogopeni.'),
(1855, 15, 53, 'Wakasema: Usiogope. Sisi tunakubashiria kijana mwenye ujuzi.'),
(1856, 15, 54, 'Akasema: Mnanibashiria nami uzee umenishika! Basi kwa njia gani mnanibashiria?'),
(1857, 15, 55, 'Wakasema: Tunakubashiria kwa haki; basi usiwe miongoni mwa wanao kata tamaa.'),
(1858, 15, 56, 'Akasema: Na nani anaye kata tamaa na rehema ya Mola wake Mlezi ila wale walio potea?'),
(1859, 15, 57, 'Akasema: Hebu nini amri yenu, enyi wajumbe?'),
(1860, 15, 58, 'Wakasema: Hakika sisi tumetumwa kwa kaumu ya wakosefu!'),
(1861, 15, 59, 'Isipo kuwa walio mfuata Luut\'i. Bila ya shaka sisi tutawaokoa hao wote.'),
(1862, 15, 60, 'Ila mkewe. Tunakadiria huyo atakuwa miongoni watao bakia nyuma.'),
(1863, 15, 61, 'Basi wale wajumbe walipo fika kwa Luut\'i,'),
(1864, 15, 62, 'Alisema (Luut\'i): Hakika nyinyi ni watu msio juulikana.'),
(1865, 15, 63, 'Wakasema: Bali sisi tumekuletea yale waliyo kuwa wakiyafanyia shaka.'),
(1866, 15, 64, 'Na tumefika kwako kwa Haki, na hakika sisi tunasema kweli.'),
(1867, 15, 65, 'Basi ondoka na ahli zako usiku ungalipo, nawe ufuate nyuma yao, wala yeyote kati yenu asigeuke nyuma. Na mwende mnapo amrishwa.'),
(1868, 15, 66, 'Na tukamfunulia hukumu hiyo, ya kwamba hata wa mwisho wao hao ikifika asubuhi atakuwa kesha katiliwa mbali.'),
(1869, 15, 67, 'Na wakaja watu wa mji ule nao furahani.'),
(1870, 15, 68, 'Akasema: Hakika hawa wageni wangu, basi msinifedheheshe.'),
(1871, 15, 69, 'Na mcheni Mwenyezi Mungu, wala msinihizi.'),
(1872, 15, 70, 'Wakasema: Sisi hatukukukataza usimkaribishe yeyote?'),
(1873, 15, 71, 'Akasema: Hawa binti zangu, ikiwa nyinyi ni watendaji.'),
(1874, 15, 72, 'Naapa kwa umri wako! Hakika hao walikuwa katika ulevi wao, wakihangaika ovyo.'),
(1875, 15, 73, 'Ukelele ukawatwaa wakipambaukiwa.'),
(1876, 15, 74, 'Na tukaigeuza nchi juu chini, na tukawanyeshea mvua ya mawe ya udongo wa Motoni.'),
(1877, 15, 75, 'Hakika katika hayo zipo ishara kwa waaguzi.'),
(1878, 15, 76, 'Na miji hii ipo kwenye njia inayo pitiwa.'),
(1879, 15, 77, 'Hakika katika hayo ipo ishara kwa Waumini.'),
(1880, 15, 78, 'Na hakika watu wa Kichakani walikuwa wenye kudhulumu.'),
(1881, 15, 79, 'Kwa hivyo tukawaadhibu. Na nchi mbili hizi ziko kwenye njia ilio wazi.'),
(1882, 15, 80, 'Na bila ya shaka wakaazi wa Hijr waliwakanusha Mitume.'),
(1883, 15, 81, 'Na tuliwapa ishara zetu, nao wakazipuuza.'),
(1884, 15, 82, 'Nao walikuwa wakichonga majumba katika milima kwa amani.'),
(1885, 15, 83, 'Basi ukelele ukawatwaa asubuhi.'),
(1886, 15, 84, 'Hayakuwafaa waliyo kuwa wakiyachuma.'),
(1887, 15, 85, 'Na hatukuziumba mbingu na ardhi na vilivyomo ndani yake ila kwa ajili ya Haki. Na hakika Kiyama kitafika. Basi samehe msamaha mzuri.'),
(1888, 15, 86, 'Hakika Mola wako Mlezi ndiye Muumbaji Mjuzi.'),
(1889, 15, 87, 'Na tumekupa Aya saba zinazo somwa mara kwa mara, na Qur\'ani Tukufu.'),
(1890, 15, 88, 'Usivitumbulie macho yako vitu tulivyo waneemesha makundi fulani kati yao, wala usiwahuzunikie. Na wainamishie bawa lako (la huruma) Waumini.'),
(1891, 15, 89, 'Na sema: Hakika mimi ni mwonyaji mwenye kubainisha.'),
(1892, 15, 90, 'Kama hivyo tuliwateremshia walio gawa,'),
(1893, 15, 91, 'Ambao wakaifanya Qur\'ani vipande vipande.'),
(1894, 15, 92, 'Basi Naapa kwa Mola wako Mlezi! Tutawahoji wote,'),
(1895, 15, 93, 'Kwa waliyo kuwa wakiyatenda.'),
(1896, 15, 94, 'Basi wewe yatangaze uliyo amrishwa, na jitenge na washirikina.'),
(1897, 15, 95, 'Hakika Sisi tunatosha kukukinga na wanao kejeli.'),
(1898, 15, 96, 'Hao wanao fanya kuwa pamoja na Mwenyezi Mungu yupo mungu mwingine. Basi, watakuja jua!'),
(1899, 15, 97, 'Na Sisi tunajua kwa hakika kuwa wewe kifua chako kinaona dhiki kwa hayo wayasemayo.'),
(1900, 15, 98, 'Basi mtakase Mola wako Mlezi kwa kumhimidi, na uwe miongoni wanao msujudia.'),
(1901, 15, 99, 'Na muabudu Mola wako Mlezi mpaka ikufikie Yakini.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Amri ya Mwenyezi Mungu itafika tu. Basi msiihimize. Ametakasika na Ametukuka na wanayo mshirikisha nayo.'),
(1903, 16, 2, 'Huwateremsha Malaika na Roho kwa amri yake juu ya amtakaye katika waja wake ili nyinyi muonye kwamba hapana mungu ila Mimi, basi nicheni Mimi.'),
(1904, 16, 3, 'Ameziumba mbingu na ardhi kwa Haki. Ametukuka na wanayo shirikisha.'),
(1905, 16, 4, 'Amemuumba mtu kwa tone la manii. Naye mara amekuwa mshindani dhaahiri.'),
(1906, 16, 5, 'Na nyama hoa amekuumbieni. Katika hao mna vifaa vya kutia joto, na manufaa mengineyo, na baadhi yao mnawala.'),
(1907, 16, 6, 'Na wanakupeni furaha pale mnapo warudisha jioni na mnapo wapeleka malishoni asubuhi.'),
(1908, 16, 7, 'Na hubeba mizigo yenu kupeleka kwenye miji msiyo weza kuifikia ila kwa mashaka ya nafsi. Hakika Mola wenu Mlezi ni Mpole na Mwenye kurehemu.'),
(1909, 16, 8, 'Na farasi, na nyumbu, na punda ili muwapande, na kuwa ni pambo. Na ataumba msivyo vijua.'),
(1910, 16, 9, 'Na ni juu ya Mwenyezi Mungu kuelekeza Njia. Na zipo njia za upotovu. Na angeli penda angeli kuongoeni nyote.'),
(1911, 16, 10, 'Yeye ndiye anaye kuteremshieni maji kutoka mbinguni. Katika hayo nyinyi mnapata ya kunywa, na miti ya kulishia.'),
(1912, 16, 11, 'Kwa hayo Yeye anaizalisha kwa ajili yenu mimea, na mizaituni, na mitende, na mizabibu, na kila namna ya matunda. Hakika katika haya zipo ishara kwa watu wanao fikiri.'),
(1913, 16, 12, 'Na amefanya ukutumikieni usiku na mchana, na jua na mwezi, na nyota zikutumikieni kwa amri yake. Hakika katika hayo zipo ishara kwa watu wenye akili.'),
(1914, 16, 13, 'Na hivi alivyo vieneza katika ardhi vya rangi mbali mbali. Hakika katika hayo ipo ishara kwa watu wanao waidhika.'),
(1915, 16, 14, 'Yeye ndiye aliye ifanya bahari ikutumikieni, ili kutokana na humo mpate kula nyama laini, na mtoe humo mapambo mnayo yavaa. Na unaona marikebu zikikata maji humo, ili mtafute fadhila yake, na mpate kushukuru.'),
(1916, 16, 15, 'Na kaweka katika ardhi milima ili ardhi isiyumbe yumbe nanyi. Na mito, na njia ili mpate kuongoka.'),
(1917, 16, 16, 'Na alama nyengine. Na kwa nyota wao wanajiongoza.'),
(1918, 16, 17, 'Ati anaye umba ni kama asiye umba? Basi hebu, hamkumbuki?'),
(1919, 16, 18, 'Na mkizihisabu neema za Mwenyezi Mungu, hamwezi kuzidhibiti. Hakika Mwenyezi Mungu ni Mwenye maghfira, Mwenye kurehemu.'),
(1920, 16, 19, 'Na Mwenyezi Mungu anayajua mnayo yaficha na mnayo yatangaza.'),
(1921, 16, 20, 'Na wale wanao waomba wasio kuwa Mwenyezi Mungu hawaumbi kitu, bali wao wameumbwa.'),
(1922, 16, 21, 'Ni wafu si wahai, na wala hawajui watafufuliwa lini.'),
(1923, 16, 22, 'Mungu wenu ni Mungu Mmoja. Lakini wasio iamini Akhera nyoyo zao zinakataa, nao wanajivuna.'),
(1924, 16, 23, 'Hapana shaka kuwa hakika Mwenyezi Mungu anayajua wanayo yaficha na wanayo yatangaza. Kwa hakika Yeye hawapendi wanao jivuna.'),
(1925, 16, 24, 'Na wanapo ambiwa: Kateremsha nini Mola wenu Mlezi? Husema: Hadithi za kubuni za watu wa kale!'),
(1926, 16, 25, 'Ili wabebe mizigo yao kwa ukamilifu Siku ya Kiyama, na sehemu ya mizigo ya wale wanao wapoteza bila ya kujua. Angalia, ni maovu mno hayo wanayo yabeba!'),
(1927, 16, 26, 'Walipanga vitimbi walio kuwa kabla yao, Mwenyezi Mungu akayasukua majengo yao kwenye misingi, dari zikawaporomokea juu yao, na adhabu ikawajia kutoka wasipo kujua.'),
(1928, 16, 27, 'Kisha Siku ya Kiyama atawahizi na atasema: Wako wapi hao washirika wangu ambao kwao mkiwakutisha (Manabii) mashaka? Watasema walio pewa ilimu: Hakika leo ndiyo hizaya na adhabu itawashukia makafiri,'),
(1929, 16, 28, 'Ambao Malaika waliwafisha nao wamejidhulumu nafsi zao! Basi watasalimu amri, waseme: Hatukuwa tukifanya uovu wowote. (Wataambiwa): Kwani! Hakika Mwenyezi Mungu anajua sana mliyo kuwa mkiyatenda.'),
(1930, 16, 29, 'Basi ingieni milango ya Jahannamu, humo mdumu. Ni maovu mno makaazi ya wafanyao kiburi!'),
(1931, 16, 30, 'Na wataambiwa walio mcha Mungu: Mola wenu Mlezi kateremsha nini? Watasema: Kheri. Walio fanya wema katika dunia hii watapata wema. Na nyumba ya Akhera ni bora zaidi, na njema mno nyumba ya wachamngu.'),
(1932, 16, 31, 'Bustani za milele wataziingia; iwe inapita kati yake mito. Humo watapata watakacho. Hivi ndivyo Mwenyezi Mungu awalipavyo wenye kumcha.'),
(1933, 16, 32, 'Wale ambao Malaika huwafisha katika hali njema, wanawaambia: Amani iwe juu yenu! Ingieni Peponi kwa sababu ya yale mliyo kuwa mkiyatenda.'),
(1934, 16, 33, 'Wanangojea jengine hawa ila Malaika wawafikie, au iwafikie amri ya Mola wako Mlezi? Kama hivyo walitenda walio kuwa kabla yao. Na Mwenyezi Mungu hakuwadhulumu, lakini walijidhulumu wenyewe.'),
(1935, 16, 34, 'Basi ukawafikia uovu wa waliyo yatenda, na waliyo kuwa wakiyakejeli yakawazunguka.'),
(1936, 16, 35, 'Na washirikina wanasema: Mwenyezi Mungu angeli taka tusingeli abudu chochote badala yake, sisi wala baba zetu, wala tusingeli harimisha chochote bila ya Yeye. Kama hivyo walifanya walio kuwa kabla yao. Basi lipo lolote juu ya Mitume isipo kuwa kubalighisha kwa uwazi?'),
(1937, 16, 36, 'Na kwa hakika kwa kila umma tuliutumia Mtume kwamba: Muabuduni Mwenyezi Mungu, na muepukeni Shetani. Basi kati yao wapo alio waongoa Mwenyezi Mungu. Na kati yao wapo ambao ulio wathibitikia upotovu. Basi tembeeni katika ardhi, muangalie ulikuwaje mwisho wa wanao kanusha.'),
(1938, 16, 37, 'Hata ukiwa na pupa ya kutaka kuwaongoa, lakini Mwenyezi Mungu hamwongoi yule anaye shikilia kupotea. Wala hawatapata wa kuwanusuru.'),
(1939, 16, 38, 'Na wanaapa kwa Mwenyezi Mungu ukomo wa viapo vyao kwamba Mwenyezi Mungu hatomfufua aliye kufa. Kwani! Ni ahadi iliyo juu yake kikweli; lakini watu wengi hawajui.'),
(1940, 16, 39, 'Ili kuwabainishia yale waliyo khitalifiana, na ili wajue walio kufuru kwamba wao walikuwa ni waongo.'),
(1941, 16, 40, 'Kauli yetu kwa kitu tunacho kitaka kiwe, ni kukiambia: Kuwa! Basi kinakuwa.'),
(1942, 16, 41, 'Na wale walio hama kwa ajili ya Mwenyezi Mungu baada ya kudhulumiwa, bila ya shaka tutawaweka duniani kwa wema; na ujira wa Akhera ni mkubwa zaidi; laiti kuwa wanajua!'),
(1943, 16, 42, 'Wale wanao subiri na wakamtegemea Mola wao Mlezi.'),
(1944, 16, 43, 'Nasi hatukuwatuma kabla yako ila watu wanaume tulio wapa Wahyi (Ufunuo). Basi waulizeni wenye ukumbusho kama nyinyi hamjui'),
(1945, 16, 44, 'Kwa Ishara wazi na Vitabu. Nasi tumekuteremshia wewe Ukumbusho ili uwabainishie watu yaliyo teremshwa kwao, wapate kufikiri.'),
(1946, 16, 45, 'Je! Wameaminisha wanao fanya vitimbi vya uovu kwamba Mwenyezi Mungu hatawadidimiza katika ardhi, na haitawafika adhabu kutoka wasipo pajua?'),
(1947, 16, 46, 'Au hatawashika katika nyendo zao, na wala hawataponyoka?'),
(1948, 16, 47, 'Au hatawashika kwa kitisho na kuwapunguza kidogo kidogo? Hakika Mola wenu Mlezi ni Mwenye huruma, Mwenye kurehemu.'),
(1949, 16, 48, 'Je! Hawavioni vitu alivyo viumba Mwenyezi Mungu - vivuli vyao vinaelekea kushotoni na kuliani, kumsujudia Mwenyezi Mungu na vikinyenyekea?'),
(1950, 16, 49, 'Na vyote viliomo katika mbingu na katika ardhi, tangu wanyama mpaka Malaika, vinamsujuidia Mwenyezi Mungu, na wala havitakabari.'),
(1951, 16, 50, 'Vinamkhofu Mola wao Mlezi aliye juu yao, na vinafanya vinavyo amrishwa.'),
(1952, 16, 51, 'Na Mwenyezi Mungu amesema: Msiwe na miungu wawili! Hakika Yeye ni Mungu Mmoja. Basi niogopeni Mimi tu!'),
(1953, 16, 52, 'Na ni vyake Yeye viliomo katika mbingu na ardhi. Na Dini ni yake Yeye daima. Je! Mtamcha mwengine asiye kuwa Mwenyezi Mungu?'),
(1954, 16, 53, 'Na neema yoyote mliyo nayo inatoka kwa Mwenyezi Mungu. Kisha yakikuguseni madhara mnamyayatikia Yeye.'),
(1955, 16, 54, 'Na anapo kuondosheeni madhara mara kundi moja miongoni mwenu linamshirikisha Mola wao Mlezi,'),
(1956, 16, 55, 'Wakizikataa neema tulizo wapa. Basi stareheni. Mtakuja jua!'),
(1957, 16, 56, 'Na hao ambao hata hawawajui wanawawekea sehemu ya tunavyo waruzuku. Wallahi! Bila ya shaka mtasailiwa kwa hayo mliyo kuwa mnayazua!'),
(1958, 16, 57, 'Na wanamfanyia Mwenyezi Mungu ati ana mabinti, Subhanahu, Aliye takasika! Na wao wenyewe, ati ndio wawe na hayo wanayo yatamani!'),
(1959, 16, 58, 'Na mmoja wao akibashiriwa kwa msichana, uso wake unasawijika, naye kajaa chuki.'),
(1960, 16, 59, 'Anajificha asionekane na watu kwa ubaya wa aliyo bashiriwa! Je, akae naye juu ya fedheha hiyo au amfukie udongoni? Tazama uovu wa wanavyo hukumu!'),
(1961, 16, 60, 'Hali ya wasio iamini Akhera ni ovu; na Mwenyezi Mungu ndiye Mwenye sifa tukufu. Naye ni Mwenye nguvu na Mwenye hikima.'),
(1962, 16, 61, 'Na lau kuwa Mwenyezi Mungu angeli wachukulia watu kwa mujibu wa dhulma zao, basi asingeli mwacha hapa hata mnyama mmoja. Lakini anawaakhirisha mpaka muda ulio wekwa. Na unapo fika muda wao hawatakawilia hata saa moja wala hawatatangulia.'),
(1963, 16, 62, 'Na wanampa Mwenyezi Mungu wanavyo vichukia wao, na ndimi zao zinasema uwongo kwamba wao watapata mema. Hapana shaka hakika wamewekewa Moto, nao wataachwa humo.'),
(1964, 16, 63, 'Wallahi! Sisi tulituma Mitume kwa umati zilizo kuwa kabla yako, lakini Shetani aliwapambia vitendo vyao. Kwa hivyo leo yeye ndiye rafiki yao; nao watapata adhabu chungu.'),
(1965, 16, 64, 'Na hatukukuteremshia Kitabu isipo kuwa uwabainishie yale ambayo wanakhitalifiana, na kiwe Uwongofu na Rehema kwa watu wanao amini.'),
(1966, 16, 65, 'Na Mwenyezi Mungu ameteremsha maji kutoka mbinguni na kwa hayo akaihuisha ardhi baada ya kufa kwake. Kwa hakika katika hayo imo Ishara kwa watu wanao sikia.'),
(1967, 16, 66, 'Na hakika katika nyama hoa nyinyi mna mazingatio. Tunakunywesheni katika vile viliomo matumboni mwao, vikatoka baina ya mavi na damu, maziwa safi mazuri kwa wanywao.'),
(1968, 16, 67, 'Na kutokana na matunda ya mitende na mizabibu mnatengeneza ulevi na riziki nzuri. Hakika katika hayo ipo Ishara kwa wanao tumia akili.'),
(1969, 16, 68, 'Na Mola wako Mlezi amemfunulia nyuki: Jitengenezee majumba yako katika milima, na katika miti, na katika wanayo jenga watu.'),
(1970, 16, 69, 'Kisha kula katika kila matunda, na upite katika njia za Mola wako Mlezi zilizo fanywa nyepesi kuzipita. Na kutoka matumbo yao kinatoka kinywaji chenye rangi mbali mbali; ndani yake kina matibabu kwa wanaadamu. Hakika katika haya ipo Ishara kwa watu wanao fikiri.'),
(1971, 16, 70, 'Na Mwenyezi Mungu amekuumbeni; kisha anakufisheni. Na miongoni mwenu wapo wanao rudishwa kwenye umri mbaya kabisa, hata akawa asijue kitu baada ya ujuzi alio kuwa nao. Hakika Mwenyezi Mungu ndiye Mjuzi na Mweza.'),
(1972, 16, 71, 'Na Mwenyezi Mungu amewafadhilisha baadhi yenu kuliko wengine katika riziki. Na wale walio fadhilishwa hawarudishi riziki zao kwa wale ilio wamiliki mikono yao ya kulia ili wawe sawa katika riziki hiyo. Basi je, wanazikataa neema za Mwenyezi Mungu?'),
(1973, 16, 72, 'Na Mwenyezi Mungu amekuumbieni wake katika jinsi yenu, na akakujaalieni kutoka kwa wake zenu wana na wajukuu, na akakuruzukuni vitu vizuri vizuri. Basi je, wanaamini upotovu na wanazikataa neema za Mwenyezi Mungu?'),
(1974, 16, 73, 'Na badala ya Mwenyezi Mungu wanawaabudu wasio wamilikia riziki kutoka mbinguni wala kwenye ardhi, wala hawawezi kitu.'),
(1975, 16, 74, 'Basi msimpigie Mwenyezi Mungu mifano. Hakika Mwenyezi Mungu anajua, na nyinyi hamjui.'),
(1976, 16, 75, 'Mwenyezi Mungu anapiga mfano wa mtumwa aliye milikiwa, asiye weza kitu, na mwingine tuliye mruzuku riziki njema inayo toka kwetu, naye akawa anatoa katika riziki hiyo kwa siri na dhaahiri. Je, hao watakuwa sawa? Alhamdu Lillahi! Kuhimidiwa kote ni kwa Mwenyezi Mungu. Lakini wengi wao hawajui.'),
(1977, 16, 76, 'Na Mwenyezi Mungu anapiga mfano wa watu wawili. Mmoja wao ni bubu, hawezi chochote, naye ni mzigo kwa bwana wake. Popote anapo muelekeza haleti kheri. Je! Huyo anaweza kuwa sawa na yule anaye amrisha uadilifu, naye yuko juu ya Njia Iliyo Nyooka?'),
(1978, 16, 77, 'Na siri zote za katika mbingu na ardhi ziko kwa Mwenyezi Mungu. Wala halikuwa jambo la Saa (ya Kiyama) ila kama kupepesa kwa jicho, au akali ya hivyo. Hakika Mwenyezi Mungu ni Muweza wa kila kitu.'),
(1979, 16, 78, 'Na Mwenyezi Mungu amekutoeni matumboni mwa mama zenu, hali ya kuwa hamjui kitu, na amekujaalieni masikio na macho na nyoyo ili mpate kushukuru.'),
(1980, 16, 79, 'Je! Hawawaoni ndege walivyo wat\'iifu katika anga la mbingu? Hapana mwenye kuwashika isipo kuwa Mwenyezi Mungu. Hakika katika haya zipo ishara kwa watu wanao amini.'),
(1981, 16, 80, 'Na Mwenyezi Mungu amekujaalieni majumba yenu yawe ni maskani yenu, na amekujaalieni kutokana na ngozi za wanyama nyumba mnazo ziona nyepesi wakati wa safari zenu na wakati wa kutua kwenu. Na kutokana na sufi zao na manyoya yao na nywele zao mnafanya matandiko na mapambo ya kutumia kwa muda.'),
(1982, 16, 81, 'Na Mwenyezi Mungu amekufanyieni vivuli katika vitu alivyo viumba, na amekufanyieni maskani milimani, na amekufanyieni nguo za kukingeni na joto, na nguo za kukingeni katika vita vyenu. Ndio hivyo anakutimizieni neema zake ili mpate kut\'ii.'),
(1983, 16, 82, 'Basi wakikengeuka lilio juu yako wewe ni kufikisha ujumbe wazi wazi.'),
(1984, 16, 83, 'Wanazijua neema za Mwenyezi Mungu, kisha wanazikanusha; na wengi wao ni makafiri.'),
(1985, 16, 84, 'Na siku tutakapo wainua kutokana na kila umma shahidi, kisha hawataruhusiwa walio kufuru wala hawataachiliwa kutaka radhi.'),
(1986, 16, 85, 'Na walio dhulumu watakapo iona adhabu hawatapunguziwa hiyo adhabu, wala hawatapewa muhula.'),
(1987, 16, 86, 'Na walio shirikisha watakapo waona hao walio washirikisha na Mwenyezi Mungu, watasema: Mola wetu Mlezi! Hawa ndio washirikishwa wetu ambao tulikuwa tukiwaabudu badala yako. Ndipo wale watakapo watupia kauli: Hakika nyinyi ni waongo!'),
(1988, 16, 87, 'Na siku hiyo watasalimu amri mbele ya Mwenyezi Mungu, na yatapotea waliyo kuwa wakiyazua.'),
(1989, 16, 88, 'Wale walio kufuru na kuzuilia Njia ya Mwenyezi Mungu tutawazidishia adhabu juu ya adhabu, kwa walivyo kuwa wakifisidi.'),
(1990, 16, 89, 'Na (waonye) siku tutakapo wainua mashahidi katika kila umma wakiwashuhudia kwa yanayo tokana na wao, na tutakuleta wewe uwe shahidi juu ya hao. Na tumekuteremshia Kitabu hiki kinacho bainisha kila kitu, na ni uwongofu, na rehema, na bishara kwa Waislamu.'),
(1991, 16, 90, 'Hakika Mwenyezi Mungu anaamrisha kufanya uadilifu, na hisani, na kuwapa jamaa; na anakataza uchafu, na uovu, na dhulma. Anakuwaidhini ili mpate kukumbuka.'),
(1992, 16, 91, 'Na timizeni ahadi ya Mwenyezi Mungu mnapo ahidi, wala msivunje viapo baada ya kuvithibitisha, ilihali mmekwisha mfanya Mwenyezi Mungu ndiye mdhamini wenu. Hakika Mwenyezi Mungu anayajua myatendayo.'),
(1993, 16, 92, 'Wala msiwe kama mwanamke anaye uzongoa uzi wake baada ya kwisha usokota ukawa mgumu. Mnavifanya viapo vyenu kuwa ni njia ya kudanganyana baina yenu kwa kuwa ati taifa moja lina nguvu zaidi kuliko jengine? Hakika Mwenyezi Mungu anakujaribuni kwa njia hiyo. Na bila ya shaka atakubainishieni Siku ya Kiyama mliyo kuwa mkikhitalifiana.'),
(1994, 16, 93, 'Na Mwenyezi Mungu angeli taka, kwa yakini angeli kufanyeni umma mmoja. Lakini anamwachia kupotea anaye mtaka, na anamwongoa amtakaye. Na hakika mtaulizwa kwa yale mliyo kuwa mkiyafanya.'),
(1995, 16, 94, 'Wala msifanye viapo vyenu ni njia ya kudanganyana baina yenu. Usije mguu ukateleza badala ya kuthibiti, na mkaonja maovu kwa sababu ya kule kuzuilia Njia ya Mwenyezi Mungu, na mkapata adhabu kubwa.'),
(1996, 16, 95, 'Wala msinunue ahadi ya Mwenyezi Mungu kwa thamani ndogo. Hakika kilichoko kwa Mwenyezi Mungu ndicho bora kwenu, ikiwa mnajua.'),
(1997, 16, 96, 'Mlivyo navyo vitakwisha na vilivyoko kwa Mwenyezi Mungu ndivyo vitakavyo bakia. Na kwa yakini Sisi tutawapa walio subiri ujira wao kwa bora ya waliyo kuwa wakiyatenda.'),
(1998, 16, 97, 'Mwenye kutenda mema, mwanamume au mwanamke, naye akawa ni Muumini, tutamhuisha maisha mema; na tutawapa ujira wao kwa bora ya waliyo kuwa wakiyatenda.'),
(1999, 16, 98, 'Na ukisoma Qur\'ani mwombe Mwenyezi Mungu akulinde na Shetani maluuni.'),
(2000, 16, 99, 'Hakika yeye hana madaraka juu ya walio amini na wakamtegemea Mola wao Mlezi.'),
(2001, 16, 100, 'Hakika madaraka yake ni juu ya wanao mfanya rafiki yao, na wale wanao fanya ushirika naye.'),
(2002, 16, 101, 'Na tunapo badilisha Ishara pahala pa Ishara nyengine, na Mwenyezi Mungu anajua anayo teremsha, wao husema: Wewe ni mzushi. Bali wengi wao hawajui kitu.'),
(2003, 16, 102, 'Sema: Ameiteremsha hii Roho takatifu kutokana na Mola wako Mlezi kwa haki, ili awathibitishe wale walio amini, na kuwa ni uwongofu, na ni bishara kwa Waislamu.'),
(2004, 16, 103, 'Na Sisi hakika tunajua kwamba wanasema: Yuko mtu anaye mfundisha. Lugha ya huyo wanaye muelekezea ni ya kigeni, na hii ni lugha ya Kiarabu mbayana.'),
(2005, 16, 104, 'Hakika wasio ziamini Ishara za Mwenyezi Mungu, Mwenyezi Mungu hawaongoi, nao watapata adhabu chungu.'),
(2006, 16, 105, 'Wanao zua uwongo ni wale tu wasio ziamini Ishara za Mwenyezi Mungu. Na hao ndio waongo.'),
(2007, 16, 106, 'Anaye mkataa Mwenyezi Mungu baada ya kuamini kwake - isipo kuwa aliye lazimishwa na hali ya kuwa moyo wake umetua juu ya Imani, lakini aliye kifungulia kifua chake kukataa - basi hao ghadhabu ya Mwenyezi Mungu ipo juu yao, na wao watapata adhabu kubwa.'),
(2008, 16, 107, 'Hayo ni kwa sababu wameyapenda zaidi maisha ya duniani kuliko ya Akhera, na kwa sababu Mwenyezi Mungu hawaongoi watu makafiri.'),
(2009, 16, 108, 'Hao ndio Mwenyezi Mungu ameziziba nyoyo zao na masikio yao na macho yao. Na hao ndio walio ghafilika.'),
(2010, 16, 109, 'Hapana shaka ya kwamba hao ndio wenye kukhasiri Akhera.'),
(2011, 16, 110, 'Kisha hakika Mola wako Mlezi, kwa wale walio hama makwao baada ya kuteswa, kisha wakapigania Dini na wakasubiri, bila ya shaka Mola wako Mlezi baada ya hayo ni Mwenye kusamehe Mwenye kurehemu.'),
(2012, 16, 111, 'Siku ambayo kila nafsi itakuja jitetea, na kila nafsi italipwa sawa sawa na a\'mali ilizo zifanya, nao hawatadhulumiwa.'),
(2013, 16, 112, 'Na Mwenyezi Mungu amepiga mfano wa mji ulio kuwa na amani na utulivu, riziki yake ikiufikia kwa nafasi kutoka kila mahali. Lakini ukazikufuru neema za Mwenyezi Mungu; kwa hivyo Mwenyezi Mungu akauvika vazi la njaa na khofu kwa sababu ya yale waliyo kuwa wakiyafanya.'),
(2014, 16, 113, 'Na alikwisha wajia Mjumbe wa miongoni mwao wenyewe. Lakini wakamkanusha; basi iliwafika adhabu hali ya kuwa wamedhulumu.'),
(2015, 16, 114, 'Basi kuleni katika vile alivyo kupeni Mwenyezi Mungu, vilivyo halali na vizuri. Na shukuruni neema za Mwenyezi Mungu, ikiwa kweli mnamuabudu Yeye.'),
(2016, 16, 115, 'Amekuharimishieni nyamafu tu, na damu, na nyama ya nguruwe, na mnyama aliye chinjwa kwa ajili isiyo kuwa ya Mwenyezi Mungu. Lakini anaye lazimishwa bila ya kuasi, wala kuruka mipaka, basi hakika Mwenyezi Mungu ni Mwenye kusamehe na Mwenye kurehemu.'),
(2017, 16, 116, 'Wala msiseme uwongo, kwa kuropokwa na ndimi zenu: Hichi halali, na hichi haramu - mkimzulia uwongo Mwenyezi Mungu. Hakika wanao mzulia uwongo Mwenyezi Mungu hawatafanikiwa.'),
(2018, 16, 117, 'Ni starehe ndogo, nao watapata adhabu chungu.'),
(2019, 16, 118, 'Na Mayahudi tuliwaharimishia yale tuliyo kuhadithia zamani. Na Sisi hatukuwadhulumu, bali walikuwa wakijudhulumu wenyewe.'),
(2020, 16, 119, 'Kisha hakika Mola wako Mlezi kwa walio tenda uovu kwa ujinga, kisha wakatubia baada ya hayo na wakatenda mema; bila ya shaka Mola wako Mlezi baada ya hayo ni Mwenye kusamehe na Mwenye kurehemu.'),
(2021, 16, 120, 'Hakika Ibrahim alikuwa mfano mwema, mnyenyekevu kwa Mwenyezi Mungu, mwongofu, wala hakuwa miongoni mwa washirikina.'),
(2022, 16, 121, 'Mwenye kuzishukuru neema zake, Mwenyezi Mungu. Yeye kamteuwa, na akamwongoa kwenye Njia Iliyo Nyooka.'),
(2023, 16, 122, 'Na tukampa wema duniani, na hakika Akhera atakuwa miongoni mwa watu wema.'),
(2024, 16, 123, 'Kisha tukakufunulia ya kwamba ufuate mila ya Ibrahim, mwongofu, wala hakuwa miongoni mwa washirikina.'),
(2025, 16, 124, 'Hakika siku ya Sabato (Jumaamosi) waliwekewa wale walio khitalifiana kwa ajili yake. Na hakika Mola wako Mlezi bila ya shaka atahukumu baina yao Siku ya Kiyama katika yale waliyo kuwa wakikhitalifiana.'),
(2026, 16, 125, 'Waite waelekee kwenye Njia ya Mola wako Mlezi kwa hikima na mawaidha mema, na ujadiliane nao kwa namna iliyo bora. Hakika Mola wako Mlezi ndiye anaye mjua zaidi aliye ipotea Njia yake, na Yeye ndiye anaye wajua zaidi walio ongoka.'),
(2027, 16, 126, 'Na mkilipiza basi lipizeni sawa na vile mlivyo adhibiwa. Na ikiwa mtasubiri, basi hakika hivyo ni bora zaidi kwa wanao subiri.'),
(2028, 16, 127, 'Na subiri. Na kusubiri kwako kusiwe ila kwa sababu ya Mwenyezi Mungu tu. Wala usiwahuzunikie; wala usiwe katika dhiki kwa hila wanazo zifanya.'),
(2029, 16, 128, 'Hakika Mwenyezi Mungu yu pamoja na wenye kumcha, na watendao mema.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'SUBHANA, Ametakasika aliye mchukua mja wake usiku mmoja kutoka Msikiti Mtukufu mpaka Msikiti wa Mbali, ambao tumevibariki vilivyo uzunguka, ili tumuonyeshe baadhi ya Ishara zetu. Hakika Yeye ni Mwenye kusikia na Mwenye kuona.'),
(2031, 17, 2, 'Na tukampa Musa Kitabu, na tukakifanya uwongofu kwa Wana wa Israili. (Tukawambia): Msiwe na mtegemewa ila Mimi!'),
(2032, 17, 3, 'Enyi kizazi tuliyo wachukua pamoja na Nuhu! Hakika yeye alikuwa mja mwenye shukrani.'),
(2033, 17, 4, 'Na tukawahukumia Wana wa Israili katika Kitabu kwamba: Hakika nyinyi mtafanya fisadi katika nchi mara mbili, na kwa yakini mtapanda kiburi, kiburi kikubwa.'),
(2034, 17, 5, 'Basi itapo fika ahadi ya kwanza yake tutakupelekeeni waja wetu wa kali kwa vita. Watakuingilieni ndani ya majumba. Na hii ilikuwa ahadi iliyo timizwa.'),
(2035, 17, 6, 'Kisha tukakurudishieni nguvu zenu dhidi yao, na tukakusaidieni kwa mali na wana, na tukakujaalieni mkawa wengi zaidi.'),
(2036, 17, 7, 'Basi mkifanya wema, mnajifanyia wema nafsi zenu. Na mkifanya ubaya, mnajifanyia wenyewe. Na ikifika ahadi ya mwisho ili waziharibu nyuso zenu, na waingie Msikitini kama walivyo ingia mara ya kwanza, na waharibu kila walicho kiteka kwa uharibifu mkubwa.'),
(2037, 17, 8, 'Huenda Mola wenu Mlezi akakurehemuni. Na mkirudia na Sisi tutarudia. Na tumeifanya Jahannamu kuwa ni gereza kwa ajili ya makafiri.'),
(2038, 17, 9, 'Hakika hii Qur\'ani inaongoa kwenye ya yaliyo nyooka kabisa, na inawabashiria Waumini ambao wanatenda mema ya kwamba watapata malipo makubwa.'),
(2039, 17, 10, 'Na yakwamba wale wasio iamini Akhera tumewaandalia adhabu chungu.'),
(2040, 17, 11, 'Na mwanaadamu huomba shari kama vile aombavyo kheri, kwani mwanaadamu ni mwenye pupa.'),
(2041, 17, 12, 'Na tumefanya usiku na mchana kuwa ishara mbili. Tena tukaifuta ishara ya usiku, na tukaifanya ishara ya mchana ni yenye mwangaza ili mtafute fadhila itokayo kwa Mola wenu Mlezi, na mpate kujua idadi ya miaka na hisabu. Na kila kitu tumekifafanua waziwazi.'),
(2042, 17, 13, 'Na kila mtu tumemfungia a\'mali yake shingoni mwake. Na Siku ya Kiyama tutamtolea kitabu atakacho kikuta kiwazi kimekunjuliwa.'),
(2043, 17, 14, 'Ataambiwa: Soma kitabu chako! Nafsi yako inakutosha leo kukuhisabu.'),
(2044, 17, 15, 'Anaye ongoka basi anaongoka kwa ajili ya nafsi yake. Na anaye potea basi anapotea kwa khasara yake mwenyewe. Wala habebi mbebaji mzigo wa mwengine. Wala Sisi hatuadhibu mpaka tumpeleke Mtume.'),
(2045, 17, 16, 'Na pindi tukitaka kuuteketeza mji huwaamrisha wale wenye starehe na taanusi, lakini wao huendelea kutenda maovu humo. Basi hapo kauli huthibiti juu ya mji huo, nasi tukauangamiza kwa maangamizo makubwa.'),
(2046, 17, 17, 'Na kaumu ngapi tumeziangamiza baada ya Nuhu! Na Mola wako Mlezi anatosha kabisa kuwa ni Mwenye kuzijua na kuziona dhambi za waja wake.'),
(2047, 17, 18, 'Anaye taka yapitayo upesi upesi, tutafanya haraka kumletea hapa hapa tunayo yataka kwa tumtakaye. Kisha tumemwekea Jahannamu; ataingia humo hali ya kuwa ni mwenye kulaumiwa na kufurushwa.'),
(2048, 17, 19, 'Na anaye itaka Akhera, na akazifanyia juhudi a\'mali zake, naye ni Muumini, basi hao juhudi yao itakuwa ni ya kushukuriwa.'),
(2049, 17, 20, 'Wote hao tunawakunjulia - hawa na hao - katika neema za Mola wako Mlezi. Wala neema za Mola wako Mlezi hazizuiliki.'),
(2050, 17, 21, 'Angalia vipi tulivyo wafadhili baadhi yao kuliko wenginewe. Na hakika Akhera ni yenye daraja kubwa zaidi na fadhila kubwa zaidi.'),
(2051, 17, 22, 'Usimfanye mungu mwengine pamoja na Mwenyezi Mungu, usije ukawa wa kulaumiwa uliye tupika.'),
(2052, 17, 23, 'Na Mola wako Mlezi ameamrisha kuwa msimuabudu yeyote ila Yeye tu, na wazazi wawili muwatendee wema. Mmoja wao akifikia uzee, naye yuko kwako, au wote wawili, basi usimwambie hata: Ah! Wala usiwakemee. Na sema nao kwa msemo wa hishima.'),
(2053, 17, 24, 'Na uwainamishie bawa la unyenyekevu kwa kuwaonea huruma. Na useme: Mola wangu Mlezi! Warehemu kama walivyo nilea utotoni.'),
(2054, 17, 25, 'Mola wenu anajua kabisa yaliyo nyoyoni mwenu. Ikiwa nyinyi mtakuwa wema, basi hakika Yeye ni Mwenye kuwaghufiria wanao tubia kwake.'),
(2055, 17, 26, 'Na mpe aliye jamaa yako haki yake, na masikini, na msafiri; wala usitumie ovyo kwa fujo.'),
(2056, 17, 27, 'Hakika wabadhirifu ni ndugu wa Mashet\'ani. Na Shet\'ani ni mwenye kumkufuru Mola wake Mlezi.'),
(2057, 17, 28, 'Na ikiwa unajikurupusha nao, nawe unatafuta rehema ya Mola wako Mlezi unayo itaraji, basi sema nao maneno laini.'),
(2058, 17, 29, 'Wala usiufanye mkono wako kama ulio fungwa shingoni mwako, wala usiukunjue wote kabisa, utabaki ukilaumiwa muflisi.'),
(2059, 17, 30, 'Hakika Mola wako Mlezi humkunjulia riziki amtakaye, na humpimia amtakaye. Hakika Yeye kwa waja wake ni Mwenye kuwajua na kuwaona.'),
(2060, 17, 31, 'Wala msiwauwe wana wenu kwa kuogopa umasikini. Sisi tunawaruzuku wao na nyinyi. Hakika kuwaua hao ni khatia kubwa.'),
(2061, 17, 32, 'Wala msikaribie uzinzi. Hakika huo ni uchafu na njia mbaya.'),
(2062, 17, 33, 'Wala msiuwe nafsi ambayo Mwenyezi Mungu amekataza isipo kuwa kwa haki. Na aliye uliwa kwa kudhulumiwa basi tumempa madaraka mrithi wake. Lakini asipite mpaka katika kuuwa. Kwani yeye anasaidiwa.'),
(2063, 17, 34, 'Wala msiyakaribie mali ya yatima, isipo kuwa kwa njia iliyo bora, mpaka afike utuuzimani. Na timizeni ahadi. Kwa hakika ahadi itasailiwa.'),
(2064, 17, 35, 'Na timizeni kipimo mpimapo. Na pimeni kwa mizani zilizo sawa. Hayo ni wema kwenu na khatimaye ndio bora.'),
(2065, 17, 36, 'Wala usiyafuate usiyo na ujuzi nayo. Hakika masikio, na macho, na moyo - hivyo vyote vitasailiwa.'),
(2066, 17, 37, 'Wala usitembee katika ardhi kwa maringo. Hakika wewe huwezi kuipasua ardhi wala kufikia urefu wa milima.'),
(2067, 17, 38, 'Haya yote ubaya wake ni wenye kuchukiza kwa Mola wako Mlezi.'),
(2068, 17, 39, 'Haya ni katika hikima alizo kufunulia Mola wako Mlezi. Wala usimweke pamoja na Mwenyezi Mungu mungu mwengine, usije ukatupwa katika Jahannamu ukilaumiwa na kufurushwa.'),
(2069, 17, 40, 'Je! Mola wenu Mlezi amekuteulieni wavulana, na Yeye akawafanya Malaika ni banati zake? Kwa hakika nyinyi mnasema usemi mkubwa.'),
(2070, 17, 41, 'Na tumekwisha bainisha katika Qur\'ani hii ili wapate kukumbuka. Lakini haikuwazidisha ila kuwa mbali nayo.'),
(2071, 17, 42, 'Sema: Lau kuwa wangeli kuwa pamoja naye miungu mingine kama wasemavyo, basi wangeli tafuta njia ya kumfikia Mwenye Kiti cha Enzi.'),
(2072, 17, 43, 'Subhanahu Wa Taa\'la, Ametakasika na Ametukuka juu kabisa na hayo wanayo yasema.'),
(2073, 17, 44, 'Zinamtakasa zote mbingu saba na ardhi na vyote viliomo ndani yake. Na hapana kitu ila kinamtakasa kwa sifa zake. Lakini nyinyi hamfahamu kutakasa kwake. Hakika Yeye ni Mpole na Mwenye maghfira.'),
(2074, 17, 45, 'Na unapo soma Qur\'ani tunaweka baina yako na wale wasio iamini Akhera pazia linalo wafunika.'),
(2075, 17, 46, 'Na tunaweka vifuniko juu ya nyoyo zao wasije wakaifahamu, na tunaweka kwenye masikio yao uziwi. Na unapo mtaja katika Qur\'ani Mola wako Mlezi peke yake, basi wao hugeuka nyuma wakenda zao.'),
(2076, 17, 47, 'Sisi tunajua vyema kabisa sababu wanayo sikilizia wanapo kusikiliza, na wanapo nong\'ona. Wanapo sema hao madhaalimu: Nyinyi hamumfuati isipo kuwa mtu aliye rogwa.'),
(2077, 17, 48, 'Tazama vipi wanavyo kupigia mifano. Basi wamepotea. Kwa hivyo hawawezi kuipata njia.'),
(2078, 17, 49, 'Na walisema: Je! Tutapo kuwa mifupa na mapande yaliyo vurugika, tutafufuliwa kwa umbo jipya?'),
(2079, 17, 50, 'Sema: Kuweni hata mawe na chuma.'),
(2080, 17, 51, 'Au umbo lolote mnalo liona kubwa katika vifua vyenu. Watasema: Nani atakaye turudisha tena? Sema: Yule yule aliye kuumbeni mara ya kwanza! Watakutikisia vichwa vyao, na watasema: Lini hayo? Sema: A\'saa yakawa karibu!'),
(2081, 17, 52, 'Siku atakapo kuiteni, na nyinyi mkamuitikia kwa kumshukuru, na mkadhani kuwa hamkukaa ila muda mdogo tu.'),
(2082, 17, 53, 'Waambie waja wangu waseme maneno mazuri, maana Shet\'ani huchochea ugomvi baina yao. Hakika Shet\'ani ni adui aliye dhaahiri kwa mwanaadamu.'),
(2083, 17, 54, 'Mola wenu Mlezi anakujueni vilivyo. Akipenda atakurehemuni, na akipenda atakuadhibuni. Na hatukukutuma ili uwe mlinzi juu yao.'),
(2084, 17, 55, 'Na Mola wako Mlezi anawajua vilivyo waliomo mbinguni na katika ardhi. Na hakika tumewafadhilisha baadhi ya Manabii kuliko wengine; na Daudi tulimpa Zaburi.'),
(2085, 17, 56, 'Sema: Waombeni hao mnao wadaia badala yake Yeye. Hawawezi kukuondoleeni madhara, wala kuyaweka pengine.'),
(2086, 17, 57, 'Hao wanao waomba, wao wenyewe wanatafuta njia ya kwendea kwa Mola wao Mlezi - hata miongoni mwao walio karibu mno - na wanataraji rehema zake na wanaikhofu adhabu yake. Hakika adhabu ya Mola wako Mlezi yafaa kutahadhari nayo.'),
(2087, 17, 58, 'Na hapana mji wowote ila Sisi tutauteketeza kabla ya Siku ya Kiyama, au tutaupa adhabu kali. Haya yamekwisha andikwa katika Kitabu.'),
(2088, 17, 59, 'Na hapana kinacho tuzuia kupeleka miujiza ila ni kuwa watu wa zamani waliikanusha. Na tuliwapa Wathamudi ngamia jike kuwa ni Ishara iliyo dhaahiri, lakini walidhulumu kwaye. Nasi hatupeleki Ishara ila kwa ajili ya kuhadharisha.'),
(2089, 17, 60, 'Na tulipo kwambia: Hakika Mola wako Mlezi amekwisha wazunguka hao watu. Na hatukuifanya ndoto tulio kuonyesha ila ni kuwajaribu watu, na mti ulio laaniwa katika Qur\'ani. Na tunawahadharisha, lakini haiwazidishii ila uasi mkubwa.'),
(2090, 17, 61, 'Na pale tulipo waambia Malaika: Msujudieni Adam. Wakamsujudia isipo kuwa Iblisi. Akasema: Je! Nimsujudie uliye muumba kwa udongo?'),
(2091, 17, 62, 'Akasema: Hebu nambie khabari ya huyu uliye mtukuza juu yangu. Ukiniakhirisha mpaka Siku ya Kiyama, hapana shaka nitawaangamiza dhuriya zake wasipo kuwa wachache tu.'),
(2092, 17, 63, 'Akasema Mwenyezi Mungu: Ondokelea mbali! Atakaye kufuata katika wao, basi Jahannamu itakuwa ndiyo malipo yenu, malipo ya kutimia.'),
(2093, 17, 64, 'Na wachochee uwawezao katika wao kwa sauti yako, na wakusanyie jeshi lako la wapandao farasi na waendao kwa miguu. Na shirikiana nao katika mali na wana, na waahidi. Na Shet\'ani hawapi ahadi ila ya udanganyifu.'),
(2094, 17, 65, 'Hakika wewe huna mamlaka juu ya waja wangu. Na Mola wako Mlezi anatosha kuwa ni wa kutegemewa.'),
(2095, 17, 66, 'Mola wenu Mlezi ndiye anaye kuendesheeni marikebu katika bahari ili mtafute katika fadhila zake. Hakika Yeye ni Mwenye kukurehemuni nyinyi.'),
(2096, 17, 67, 'Na inapo kufikieni taabu katika bahari, hao mnao waomba wanapotea, isipo kuwa Yeye tu. Na anapo kuvueni mkafika nchi kavu, mnageuka. Ama mwanaadamu ni mwingi wa kukanusha.'),
(2097, 17, 68, 'Je! Mmeaminisha ya kuwa hatakudidimizeni upande wowote katika nchi kavu, au kwamba hatakuleteeni tufani la kokoto? Kisha msipate wa kumtegemea.'),
(2098, 17, 69, 'Au mmeaminisha ya kuwa hatakurudisheni humo mara nyengine na kukupelekeeni kimbunga cha upepo akakuzamisheni kwa mlivyo kufuru, na kisha msipate wa kukunusuruni nasi.'),
(2099, 17, 70, 'Na hakika tumewatukuza wanaadamu, na tumewapa vya kupanda nchi kavu na baharini, na tumewaruzuku vitu vizuri vizuri, na tumewafadhilisha kwa fadhila kubwa kuliko wengi miongoni mwa tulio waumba.'),
(2100, 17, 71, 'Siku tutapo waita kila kikundi cha watu kwa mujibu wa wakifuatacho - basi atakaye pewa kitabu chake kwa mkono wake wa kulia, basi hao watasoma kitabu chao wala hawatadhulumiwa hata chembe.'),
(2101, 17, 72, 'Na aliye kuwa hapa kipofu, basi atakuwa kipofu Akhera, na atakuwa aliye ipotea zaidi Njia.'),
(2102, 17, 73, 'Na hakika walikaribia kukushawishi uache tuliyo kufunulia ili utuzulie mengineyo. Na hapo ndio wangeli kufanya rafiki.'),
(2103, 17, 74, 'Na lau kuwa hatukukuweka imara ungeli karibia kuwaelekea kidogo.'),
(2104, 17, 75, 'Hapo basi bila ya shaka tungeli kuonjesha adhabu mardufu ya uhai, na adhabu mardufu ya kifo. Kisha usinge pata mtu wa kukunusuru nasi.'),
(2105, 17, 76, 'Na walitaka kukukera ili uitoke nchi. Na hapo wao wasingeli bakia humo ila kwa muda mchache tu.'),
(2106, 17, 77, 'Huo ndio mwendo kwa Mitume tulio watuma kabla yako. Wala hupati mabadiliko katika mwendo wetu.'),
(2107, 17, 78, 'Shika Sala jua linapo pinduka mpaka giza la usiku, na Qur\'ani ya al fajiri. Hakika Qur\'ani ya alfajiri inashuhudiwa daima.'),
(2108, 17, 79, 'Na amka usiku kwa ibada; ni ziada ya sunna khasa kwako wewe. Huenda Mola wako Mlezi akakunyanyua cheo kinacho sifika.'),
(2109, 17, 80, 'Na sema: Mola wangu Mlezi! Niingize muingizo mwema, na unitoe kutoka kwema. Na unipe nguvu zinazo toka kwako zinisaidie.'),
(2110, 17, 81, 'Na sema: Kweli imefika, na uwongo umetoweka. Hakika uwongo lazima utoweke!'),
(2111, 17, 82, 'Na tunateremsha katika Qur\'ani yaliyo ni matibabu na rehema kwa Waumini. Wala hayawazidishii madhaalimu ila khasara.'),
(2112, 17, 83, 'Na mwanaadamu tunapo mneemesha hugeuka na kujitenga kando. Na inapo mgusa shari hukata tamaa.'),
(2113, 17, 84, 'Sema: Kila mmoja anafanya kwa namna yake. Na Mola wenu Mlezi anajua aliye ongoka katika njia.'),
(2114, 17, 85, 'Na wanakuuliza khabari za Roho. Sema: Roho ni katika mambo ya Mola wangu Mlezi. Nanyi hamkupewa katika ilimu ila kidogo tu.'),
(2115, 17, 86, 'Na tungeli penda tungeli yaondoa tuliyo kufunulia. Kisha usingeli pata mwakilishi wa kukupigania kwetu kwa haya.'),
(2116, 17, 87, 'Isipo kuwa iwe rehema itokayo kwa Mola wako Mlezi. Hakika fadhila yake kwako ni kubwa.'),
(2117, 17, 88, 'Sema: Wangeli kusanyika watu na majini ili walete mfano wa hii Qur\'ani basi hawaleti mfano wake, hata wakisaidiana wao kwa wao.'),
(2118, 17, 89, 'Na hakika tumewaeleza watu katika hii Qur\'ani kwa kila mfano. Lakini watu wengi wanakataa isipo kuwa kukanusha tu.'),
(2119, 17, 90, 'Na walisema: Hatutakuamini mpaka ututimbulie chemchem katika ardhi hii.'),
(2120, 17, 91, 'Au uwe na kitalu cha mitende na mizabibu, na utupitishie mito kati yake ikimiminika.'),
(2121, 17, 92, 'Au utuangushie mbingu vipande vipande kama ulivyo dai; au utuletee Mwenyezi Mungu na Malaika uso kwa uso.'),
(2122, 17, 93, 'Au uwe na nyumba ya dhahabu, au upae mbinguni. Wala hatutaamini kupaa kwako mpaka ututeremshie kitabu tukisome. Sema: Subhana Rabbi, Ametakasika Mola wangu Mlezi! Kwani mimi ni nani isipo kuwa ni mwanaadamu na Mtume?'),
(2123, 17, 94, 'Na nini kilicho wazuilia watu kuamini ulipo wajia uwongofu isipo kuwa walisema: Je! Mwenyezi Mungu humtuma mwanaadamu kuwa ni Mtume?'),
(2124, 17, 95, 'Sema: Ingeli kuwa duniani wapo Malaika wanatembea kwa utulivu wao, basi bila ya shaka tungeli wateremshia Malaika kuwa ni Mtume wao.'),
(2125, 17, 96, 'Sema: Mwenyezi Mungu anatosha kuwa Shahidi baina yangu na nyinyi. Hakika Yeye anawajua vyema waja wake na anawaona.'),
(2126, 17, 97, 'Na anaye mhidi Mwenyezi Mungu basi huyo ndiye aliye hidika. Na anaye wapotoa basi huto wapatia walinzi badala yake. Na tutawakusanya Siku ya Kiyama hali wakikokotwa juu ya nyuso zao, nao ni vipofu na mabubu na viziwi. Na makaazi yao ni Jahannamu. Kila moto ukifanya kusinzia tutazidi kuuchochea uwake kwa nguvu.'),
(2127, 17, 98, 'Hayo ni malipo yao kwa sababu walizikataa ishara zetu, na wakasema: Hivyo tukisha kuwa mifupa na mapande yaliyo vurugika tutafufuliwa kwa umbo jipya?'),
(2128, 17, 99, 'Kwani hawakuona ya kwamba Mwenyezi Mungu aliye umba mbingu na ardhi ni Mwenye kuweza kuumba mfano wao? Na Yeye amewawekea muda usio na shaka yoyote. Lakini madhaalimu hawataki ila ukafiri.'),
(2129, 17, 100, 'Sema: Lau kuwa nyinyi mnazimiliki khazina za rehema za Mola wangu Mlezi, basi hapana shaka mngeli zuia kuzitumia kwa ajili ya kukhofu. Na mwanaadamu tangu hapo tabia yake ni mchoyo!'),
(2130, 17, 101, 'Na kwa yakini tulimpa Musa ishara tisa zilizo wazi. Waulize Wana wa Israili alipo wafikia, na Firauni akawaambia: Hakika mimi nakuona wewe Musa umerogwa!'),
(2131, 17, 102, 'Musa akasema: Wewe unajua bila ya shaka kuwa haya hakuyateremsha ila Mola Mlezi wa mbingu na ardhi ili kuwa ni ushahidi wa kuonekana. Na hakika mimi bila ya shaka nakuona wewe, Firauni, kuwa umekwisha angamia.'),
(2132, 17, 103, 'Basi Firauni akataka kuwafukuza katika nchi. Kwa hivyo tukamzamisha yeye na walio kuwa pamoja naye wote.'),
(2133, 17, 104, 'Na tukawaambia baada yake Wana wa Israili: Kaeni katika nchi. Itapo kuja ahadi ya Akhera tutakuleteni nyote pamoja.'),
(2134, 17, 105, 'Na kwa Haki tumeiteremsha, na kwa Haki imeteremka. Na hatukukutuma ila uwe mbashiri na mwonyaji.'),
(2135, 17, 106, 'Na Qur\'ani tumeigawanya sehemu mbali mbali ili uwasomee watu kwa kituo, na tumeiteremsha kidogo kidogo.'),
(2136, 17, 107, 'Sema: Iaminini au msiiamini. Hakika wale walio pewa ilimu kabla yake, wanapo somewa hii, huanguka kifudifudi wanasujudu.'),
(2137, 17, 108, 'Na wanasema: Subhana Rabbina, Ametakasika Mola wetu Mlezi! Hakika ahadi ya Mola wetu Mlezi lazima itimizwe!'),
(2138, 17, 109, 'Na huanguka kifudifudi na huku wanalia, na inawazidisha unyenyekevu.'),
(2139, 17, 110, 'Sema: Mwombeni Allah (Mwenyezi Mungu), au mwombeni Rahman (Mwingi wa Rehema), kwa jina lolote mnalo mwita. Kwani Yeye ana majina mazuri mazuri. Wala usitangaze Sala yako kwa sauti kubwa, wala usiifiche kwa sauti ndogo, bali shika njia ya kati na kati ya hizo.'),
(2140, 17, 111, 'Na sema: Alhamdulillah, Himdi zote ni za Mwenyezi Mungu ambaye hana mwana, wala hana mshirika katika ufalme, wala hana rafiki wa kumsaidia kwa sababu ya udhaifu wake. Na mtukuze kwa utukufu mkubwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Kuhimidiwa ni kwa Mwenyezi Mungu, ambaye amemteremshia mja wake Kitabu, wala hakukifanya kina upogo.'),
(2142, 18, 2, 'Kimenyooka sawa, ili kitoe onyo kali kutoka kwake, na kiwabashirie Waumini watendao mema kwamba watapata ujira mzuri.'),
(2143, 18, 3, 'Wakae humo milele.'),
(2144, 18, 4, 'Na kiwaonye wanao nena: Mwenyezi Mungu ana mwana.'),
(2145, 18, 5, 'Wao hawana ujuzi wa jambo hili, wala baba zao. Ni neno kuu hilo litokalo vinywani mwao. Hawasemi ila uwongo tu.'),
(2146, 18, 6, 'Huenda ukajihiliki nafsi yako kwa ajili yao, kuwasikitikia kwa kuwa hawayaamini mazungumzo haya!'),
(2147, 18, 7, 'Kwa hakika tumevifanya vilioko juu ya ardhi kuwa ni pambo lake ili tuwafanyie mtihani, ni nani kati yao wenye vitendo vizuri zaidi.'),
(2148, 18, 8, 'Na hakika Sisi tutavifanya vyote vilio juu ya ardhi kuwa kama nchi ilio pigwa na ukame.'),
(2149, 18, 9, 'Bali unadhani ya kwamba wale Watu wa Pangoni na Maandishi walikuwa ni ajabu miongoni mwa ishara zetu?'),
(2150, 18, 10, 'Vijana hao walipo kimbilia kwenye pango walisema: Mola wetu Mlezi! Tupe rehema itokayo kwako, na tutengezee uwongofu katika jambo letu.'),
(2151, 18, 11, 'Tukayaziba masikio yao kuwalaza humo pangoni kwa muda wa miaka kadhaa wa kadhaa.'),
(2152, 18, 12, 'Kisha tukawazindua ili tujue ni lipi katika makundi mawili lilio hisabu sawa muda walio kaa.'),
(2153, 18, 13, 'Sisi tunakusimulia khabari zao kwa kweli. Hakika wao walikuwa ni vijana walio muamini Mola wao Mlezi. Na Sisi tukawazidisha uwongofu.'),
(2154, 18, 14, 'Na tukazitia nguvu nyoyo zao walipo simama na wakasema: Mola wetu Mlezi ni Mola Mlezi wa mbingu na ardhi. Hatutamwita mwenginewe kabisa badala yake kuwa ni Mungu. Hivyo itakuwa tumesema jambo la kuvuka mpaka.'),
(2155, 18, 15, 'Hawa watu wetu wameshika miungu mingine badala yake Yeye. Kwa nini basi hawawatolei uthibitisho ulio dhaahiri? Na nani aliye dhaalimu zaidi kuliko anaye mzulia uwongo Mwenyezi Mungu?'),
(2156, 18, 16, 'Na mkijitenga nao na vile wanavyo viabudu badala ya Mwenyezi Mungu, basi kimbilieni pangoni, akufungulieni Mola wenu Mlezi rehema yake, na akutengenezeeni ya kukufaeni katika mambo yenu.'),
(2157, 18, 17, 'Na unaliona jua linapo chomoza linainamia kuliani mwao kutoka hapo pangoni; na linapo kuchwa linawakwepa kushotoni, na wao wamo katika uwazi wa pango. Hizo ni katika ishara za Mwenyezi Mungu. Ambaye Mwenyezi Mungu anamwongoa basi huyo ni mwenye kuongoka. Na anaye muacha kupotea basi hutampatia mlinzi wala mwongozi.'),
(2158, 18, 18, 'Nawe utawadhania wamacho, na hali wamelala. Nasi tunawageuza kulia na kushoto. Na mbwa wao amenyoosha miguu yake ya mbele kizingitini. Kama ungeli watokea hapana shaka ungeli geuka kuwakimbia, nawe umejaa khofu.'),
(2159, 18, 19, 'Na kwa namna hii tuliwainua usingizini wapate kuulizana wao kwa wao. Alisema msemaji katika wao: Mmekaa muda gani? Wakasema: Tumekaa siku moja au sehemu ya siku. Wakasema: Mola wenu Mlezi anajua zaidi muda mlio kaa. Hebu mtumeni mmoja wenu na hizi fedha zenu ende mjini, akatazame chakula chake kipi kilicho bora kabisa akuleteeni cha kukila. Naye afanye mambo hayo kwa busara, wala asikutajeni kabisa kwa yeyote.'),
(2160, 18, 20, 'Kwani wao wakikutambueni watakupigeni mawe, au watakurudisheni katika dini yao; na hapo hamtafanikiwa kabisa!'),
(2161, 18, 21, 'Na namna hivi tuliwa juulisha kwa watu wapate kujua ya kwamba ahadi ya Mwenyezi Mungu ni ya kweli, na kwamba Saa haina shaka. Walipo kuwa wakigombania jambo lao wao kwa wao walisema: Jengeni jengo juu yao. Mola wao Mlezi anawajua vyema. Wakasema walio shinda katika shauri yao: Bila ya shaka tutawajengea msikiti juu yao.'),
(2162, 18, 22, 'Watasema: Walikuwa watatu, wa nne wao ni mbwa wao. Na wanasema: Walikuwa watano, wa sita wao ni mbwa wao. Wanavurumisha ovyo. Na wanasema: Walikuwa saba, na wa nane wao ni mbwa wao. Sema: Mola wangu Mlezi ndiye anaye jua sawa sawa hisabu yao. Hawawajui ila wachache tu. Basi usibishane juu yao ila kwa mabishano ya juu juu tu hivi. Wala usiulize khabari zao kwa yeyote yule.'),
(2163, 18, 23, 'Wala usisema kamwe kwa jambo lolote lile: Hakika nitalifanya hilo kesho -'),
(2164, 18, 24, 'Isipo kuwa Mwenyezi Mungu akipenda. Na mkumbuke Mola wako Mlezi pale unapo sahau, na sema: Asaa Mola wangu Mlezi ataniongoa kwenye uwongozi ulio karibu zaidi kuliko huu.'),
(2165, 18, 25, 'Na walikaa katika pango lao miaka mia tatu, na wakazidisha tisa.'),
(2166, 18, 26, 'Sema: Mwenyezi Mungu anajua zaidi muda walio kaa. Ni zake tu siri za mbingu na ardhi. Kuona kulioje na kusikia kwake! Hawana mlinzi isipo kuwa Yeye, wala hamshirikishi yeyote katika hukumu yake.'),
(2167, 18, 27, 'Na soma uliyo funuliwa katika Kitabu cha Mola wako Mlezi. Hapana wa kubadilisha maneno yake. Wala nawe hutapata makimbilio isipo kuwa kwake.'),
(2168, 18, 28, 'Na isubirishe nafsi yako pamoja na wanao muomba Mola wao Mlezi asubuhi na jioni, hali ya kuwa wanataka radhi yake. Wala macho yako yasiwaruke kwa kutaka pambo la maisha ya dunia. Wala usimt\'ii tuliye mghafilisha moyo wake asitukumbuke, na akafuata matamanio yake yakawa yamepita mpaka.'),
(2169, 18, 29, 'Na sema: Hii ni kweli itokayo kwa Mola wako Mlezi. Basi atakaye, aamini. Na atakaye, akatae. Hakika Sisi tumewaandalia wenye kudhulumu Moto ambao utawazunguka kama khema. Na wakiomba msaada watasaidiwa kwa kupewa maji kama mafuta yaliyo tibuka. Yatayo wababua nyuso zao. Kinywaji hicho ni kiovu mno! Na matandiko hayo ya kupumzikia ni maovu mno!'),
(2170, 18, 30, 'Hakika wale walio amini na wakatenda mema - hakika Sisi hatupotezi ujira wa anaye tenda mema.'),
(2171, 18, 31, 'Hao watapata Bustani za milele, zinazo pita mito kati yake. Humo watapambwa kwa mavazi ya mikononi ya dhahabu, na watavaa nguo za kijani za hariri na at\'ilasi, huku wakiegemea humo juu ya makochi. Ni malipo bora hayo! Na matandiko mazuri mno ya kupumzikia!'),
(2172, 18, 32, 'Na wapigie mfano wa watu wawili: mmoja wao tulimpa vitalu viwili vya mizabibu, na tukavizungushia mitende, na kati yake tukatia mimea ya nafaka.'),
(2173, 18, 33, 'Na hivyo vitalu viwili vikitoa mazao yake, wala hapana kitu katika hayo kilicho tindikia. Na ndani yake tukapasua mito.'),
(2174, 18, 34, 'Naye alikuwa na mazao mengi. Basi akamwambia mwenzake naye akibishana naye: Mimi nimekushinda wewe kwa mali na nguvu za watu!'),
(2175, 18, 35, 'Na akaingia kitaluni kwake, naye hali anajidhulumu nafsi yake. Akasema: Sidhani kabisa kuwa haya yataharibika.'),
(2176, 18, 36, 'Wala sidhani kuwa hiyo Saa (ya Kyama) itatokea. Na ikiwa nitarudishwa kwa Mola wangu Mlezi, bila ya shaka nitapata marejeo bora zaidi kuliko haya.'),
(2177, 18, 37, 'Mwenzake akamwambia kwa kubishana naye: Je! Unamkufuru aliye kuumba kutokana na udongo, tena kutokana na tone la manii, kisha akakufanya mtu kaamili?'),
(2178, 18, 38, 'Lakini Yeye Mwenyezi Mungu ndiye Mola wangu Mlezi. Wala simshirikishi na yeyote.'),
(2179, 18, 39, 'Na lau kuwa ulipo ingia kitaluni kwako ungeli sema: Mashaallah! Alitakalo Mwenyezi Mungu huwa! Hapana nguvu ila kwa Mwenyezi Mungu. Ikiwa unaniona mimi nina mali kidogo na watoto kuliko wewe,'),
(2180, 18, 40, 'Basi huenda Mola wangu Mlezi akanipa kilicho bora kuliko kitalu chako, naye akapitisha kudra yake kutoka mbinguni kukilitea hicho chako, na kikageuka ardhi tupu inayo teleza.'),
(2181, 18, 41, 'Au maji yake yakadidimia wala usiweze kuyagundua.'),
(2182, 18, 42, 'Yakateketezwa matunda yake, akabaki akipindua pindua viganja vyake, kwa vile alivyo yagharimia, na miti imebwagika juu ya chanja zake. Akawa anasema: Laiti nisingeli mshiriki Mola wangu Mlezi na yeyote!'),
(2183, 18, 43, 'Wala hakuwa nacho kikundi cha kumsaidia badala ya Mwenyezi Mungu, wala mwenyewe hakuweza kujisaidia.'),
(2184, 18, 44, 'Huko ulinzi ni wa Mwenyezi Mungu wa Haki tu. Yeye ndiye mbora wa malipo, na mbora wa matokeo.'),
(2185, 18, 45, 'Na wapigie mfano wa maisha ya dunia. Hayo ni kama maji tunayo yateremsha kutoka mbinguni, yakachanganyika na mimea ya ardhi, kisha hiyo mimea ikawa vibuwa vinavyo peperushwa na upepo. Na Mwenyezi Mungu ni Mwenye uweza juu ya kila kitu.'),
(2186, 18, 46, 'Mali na wana ni pambo la maisha ya dunia. Na mema yanayo bakia ni bora mbele ya Mola wako Mlezi kwa malipo, na bora kwa matumaini.'),
(2187, 18, 47, 'Na siku tutapo iondoa milima na ukaiona ardhi iwazi, na tukawafufua - wala hatutamwacha hata mmoja kati yao -'),
(2188, 18, 48, 'Na wakahudhurishwa mbele ya Mola wako Mlezi kwa safu (wakaambiwa): Mmetujia kama tulivyo kuumbeni mara ya kwanza! Bali mlidai kwamba hatutakuwekeeni miadi.'),
(2189, 18, 49, 'Na kitawekwa kitabu. Basi utawaona wakosefu wanavyo ogopa kwa yale yaliomo humo. Na watasema: Ole wetu! Kitabu hichi kina nini! Hakiachi dogo wala kubwa ila huliandika? Na watayakuta yote waliyo yatenda yamehudhuria hapo. Na Mola wako Mlezi hamdhulumu yeyote.'),
(2190, 18, 50, 'Na tulipo waambia Malaika: Msujudieni Adam! Walimsjudia isipo kuwa Iblisi. Yeye alikuwa miongoni mwa majini, na akavunja amri ya Mola wake Mlezi. Je! Mnamshika yeye na dhuriya zake kuwa marafiki badala yangu, hali wao ni adui zenu? Ni ovu mno badala hii kwa wenye kudhulumu.'),
(2191, 18, 51, 'Sikuwashuhudisha kuumbwa kwa mbingu na ardhi, wala kuumbwa kwa nafsi zao. Wala sikuwafanya wapotezao kuwa ni wasaidizi.'),
(2192, 18, 52, 'Na siku atakapo sema (Mwenyezi Mungu): Waiteni hao mlio dai kuwa ni washirika wangu. Basi watawaita, na wao hawato waitikia. Nasi tutaweka baina yao maangamio.'),
(2193, 18, 53, 'Na wakhalifu watauona Moto na watajua ya kwamba wao lazima wataingia humo; wala hawatapata pa kuuepuka.'),
(2194, 18, 54, 'Na bila ya shaka tumewaelezea watu katika hii Qur\'ani kila namna ya mfano. Lakini mwanaadamu amezidi kuliko kila kitu kwa ubishi.'),
(2195, 18, 55, 'Na hapana kilicho wazuia watu kuamini ulipo wajia uwongofu na wakaomba maghfira kwa Mola wao Mlezi, isipo kuwa yawafikie ya wale wa zamani, au iwafikie adhabu jahara.'),
(2196, 18, 56, 'Na hatuwatumi Mitume ila wawe wabashiri na waonyaji. Na walio kufuru wanabishana kwa uwongo, ili kwa uwongo huo waivunje kweli. Na wanazifanya Ishara zangu na yale waliyo onywa kuwa ni mzaha.'),
(2197, 18, 57, 'Na ni nani dhaalimu mkubwa zaidi kuliko yule anaye kumbushwa Ishara za Mola wake Mlezi, naye akazipuuza na akasahau yaliyo tangulizwa na mikono yake? Hakika Sisi tumeweka vifuniko juu ya nyoyo zao ili wasifahamu, na uziwi kwenye masikio yao. Na ukiwaita kwenye uwongofu hawakubali kabisa kuongoka.'),
(2198, 18, 58, 'Na Mola wako Mlezi ni Msamehevu Mwenye rehema. Lau angeli wachukulia kwa mujibu waliyo yachuma, bila ya shaka angeli wafanyia haraka kuwaadhibu. Lakini wanayo miadi ambayo hawatapata makimbilio yoyote ya kuepukana nayo.'),
(2199, 18, 59, 'Na miji hiyo tuliwaangamiza watu wake walipo dhulumu. Na tukawawekea miadi ya maangamizo yao.'),
(2200, 18, 60, 'Na pale Musa alipo mwambia kijana wake: Sitoacha kuendelea mpaka nifike zinapo kutana bahari mbili, au nitaendelea kwa muda mrefu.'),
(2201, 18, 61, 'Basi wawili hao walipo fika zinapo kutana hizo bahari mbili walimsahau samaki wao, naye akashika njia yake kaporonyokea baharini.'),
(2202, 18, 62, 'Walipo kwisha pita alimwambia kijana wake: Tupe chakula chetu cha mchana. Kwa hakika tumepata machofu kweli kwa hii safari yetu.'),
(2203, 18, 63, 'Akasema (kijana): Waona! Pale tulipo pumzika penye jabali basi mimi nilimsahau yule samaki. Na hapana aliye nisahaulisha nisimkumbuke ila Shet\'ani tu. Naye akashika njia yake baharini kwa ajabu.'),
(2204, 18, 64, '(Musa) akasema: Hayo ndiyo tuliyo kuwa tunayataka. Basi wakarudi nyuma kwa kufuata njia waliyo jia.'),
(2205, 18, 65, 'Basi wakamkuta mja katika waja wangu tuliye mpa rehema kutoka kwetu, na tukamfunza mafunzo yaliyo toka kwetu.'),
(2206, 18, 66, 'Musa akamwambia: Nikufuate ili unifunze katika ule uwongofu ulio funzwa wewe?'),
(2207, 18, 67, 'Akasema: Hakika wewe hutaweza kuvumilia kuwa pamoja nami.'),
(2208, 18, 68, 'Na utawezaje kuvumilia yale usiyo yajua vilivyo undani wake?'),
(2209, 18, 69, 'Akasema: Inshaallah, Mwenyezi Mungu akipenda, utaniona mvumilivu, wala sitoasi amri yako.'),
(2210, 18, 70, 'Akasema: Basi ukinifuata usiniulize kitu mpaka mimi nianze kukutajia.'),
(2211, 18, 71, 'Basi wawili hao wakatoka, hata walipo panda jahazi (yule mtu) akaitoboa. (Musa) akasema: Unaitoboa uwazamishe watu wake? Hakika umefanya jambo baya.'),
(2212, 18, 72, 'Akasema: Sikukwambia hutaweza kuvumilia kuwa pamoja nami?'),
(2213, 18, 73, '(Musa) akasema: Usinichukulie kwa niliyo sahau, wala usinipe uzito kwa jambo langu.'),
(2214, 18, 74, 'Basi wakatoka wawili hao mpaka wakamkuta kijana, akamuuwa. (Musa) akasema: Ala! Umemuuwa mtu asiye na kosa, na wala hakuuwa! Ama hakika umefanya jambo baya kabisa!'),
(2215, 18, 75, 'AKASEMA: Je! Sikukwambia kwamba hakika wewe huwezi kuvumilia kuwa pamoja nami?'),
(2216, 18, 76, 'Akasema: Nikikuuliza kitu chochote baada ya haya usifuatane nami, kwani umekwisha pata udhuru kwangu.'),
(2217, 18, 77, 'Basi wakatoka mpaka wakawafikia watu wa mji mmoja. Wakawaomba watu wake wawape chakula, nao wakakataa kuwakaribisha. Hapo wakakuta ukuta unataka kuanguka, (yule mja mwema) akausimamisha. (Musa) akasema: Ungeli taka ungeli chukua ujira kwa haya.'),
(2218, 18, 78, '(Yule mtu) akasema: Huku ndio kufarikiana baina yangu na wewe. Sasa nitakueleza maana ya yale uliyo kuwa huwezi kuyastahamilia.'),
(2219, 18, 79, 'Ama ile jahazi ilikuwa ya masikini za Mungu wafanyao kazi baharini. Nilitaka kuiharibu, kwani nyuma yao alikuwako mfalme anaghusubu majahazi yote.'),
(2220, 18, 80, 'Na ama yule kijana, wazazi wake walikuwa ni Waumini. Tukakhofu asije watia mashakani kwa uasi na ukafiri.'),
(2221, 18, 81, 'Basi tulitaka Mola wao Mlezi awabadilishie aliye bora zaidi kuliko huyo kwa kutakasika, na aliye karibu zaidi kwa huruma.'),
(2222, 18, 82, 'Na ama ukuta, huo ulikuwa ni wa vijana wawili mayatima kule mjini. Na chini yake ilikuwako khazina yao; na baba yao alikuwa ni mtu mwema. Basi Mola wako Mlezi alitaka wafikie utu uzima na wajitolee khazina yao wenyewe, kuwa ni rehema itokayo kwa Mola wako Mlezi. Wala mimi sikutenda hayo kwa amri yangu. Hiyo ndiyo tafsiri ya hayo ambayo wewe hukuweza kuyasubiria.'),
(2223, 18, 83, 'Na wanakuuliza khabari za Dhul- Qarnaini. Waambie: Nitakusimulieni baadhi ya hadithi yake.'),
(2224, 18, 84, 'Sisi tulimtilia nguvu katika ardhi na tukampa njia za kila kitu.'),
(2225, 18, 85, 'Basi akaifuata njia.'),
(2226, 18, 86, 'Hata alipo fika machwea-jua aliliona linatua katika checmchem yenye matope meusi. Na hapo akawakuta watu. Tukasema: Ewe Dhul-Qarnaini! Ama uwaadhibu au watwae kwa wema.'),
(2227, 18, 87, 'Akasema: Ama aliye dhulumu basi tutamuadhibu, na kisha atarudishwa kwa Mola wake Mlezi amuadhibu adhabu isiyo juulikana.'),
(2228, 18, 88, 'Na ama aliye amini na akatenda mema basi atapata malipo mazuri. Nasi tutamwambia lilio jepesi katika amri yetu.'),
(2229, 18, 89, 'Kisha akaifuata njia.'),
(2230, 18, 90, 'Hata alipo fika matokeo ya jua aliliona linawachomozea watu tusio wawekea pazia la kuwakinga nalo.'),
(2231, 18, 91, 'Ndio hivyo hivyo. Na Sisi tulizijua vilivyo khabari zake zote.'),
(2232, 18, 92, 'Kisha akaifuata njia.'),
(2233, 18, 93, 'Hata alipo fika baina ya milima miwili, alikuta nyuma yake watu ambao takriban hawakuwa wanafahamu lolote.'),
(2234, 18, 94, 'Wakasema: Ewe Dhul-Qarnaini! Hakika Juju-wa-maajuju wanafanya uharibifu katika nchi. Basi je, tukulipe ujira ili utujengee baina yetu na wao ngome?'),
(2235, 18, 95, 'Akasema: Yale Mola wangu aliyo niwezesha ni bora zaidi. Lakini nyinyi nisaidieni kwa nguvu zenu. Nitakujengeeni kizuizi baina yenu na wao.'),
(2236, 18, 96, 'Nileteeni vipande vya chuma. Hata alipo ijaza nafasi iliyo kati ya milima miwili, akasema: Vukuteni. Hata alipo kifanya (chuma) kama moto akasema: Nileteeni shaba iliyo yayuka niimwagie.'),
(2237, 18, 97, 'Basi hawakuweza kuukwea, wala hawakuweza kuutoboa.'),
(2238, 18, 98, 'Akasema: Hii ni rehema itokayo kwa Mola wangu Mlezi. Na itapo fika ahadi ya Mola wangu Mlezi atauvunja vunja. Na ahadi ya Mola wangu Mlezi ni kweli tu.'),
(2239, 18, 99, 'Na siku hiyo tutawaacha wakisongana wao kwa wao. Na baragumu litapulizwa, na wote watakusanywa pamoja.'),
(2240, 18, 100, 'Na siku hiyo tutawadhihirishia wazi Jahannamu makafiri waione.'),
(2241, 18, 101, 'Wale ambao kwamba macho yao yalikuwa paziani hayanikumbuki, na wakawa hawawezi kusikia.'),
(2242, 18, 102, 'Je, wanadhani walio kufuru ndio wawafanye waja wangu ndio walinzi wao badala yangu Mimi? Hakika Sisi tumeiandaa Jahannamu iwe ndipo mahala pa kuteremkia makafiri.'),
(2243, 18, 103, 'Sema: Je, tukutajieni wenye khasara mno katika vitendo vyao?'),
(2244, 18, 104, 'Ni wale ambao juhudi yao katika maisha ya dunia imepotea bure, nao wanadhani kwamba wanafanya kazi nzuri.'),
(2245, 18, 105, 'Hao ni wale walio zikanusha Ishara za Mola wao Mlezi na kukutana naye. Kwa hivyo vitendo vyao vimepotea bure, na wala Siku ya Kiyama hatutawathamini kitu.'),
(2246, 18, 106, 'Hiyo Jahannamu ni malipo yao kwa walivyo kufuru na wakafanyia kejeli Ishara zangu na Mitume wangu.'),
(2247, 18, 107, 'Hakika wale walio amini na wakatenda mema mashukio yao yatakuwa kwenye Pepo za Firdausi.'),
(2248, 18, 108, 'Watadumu humo; hawatataka kuondoka.'),
(2249, 18, 109, 'Sema: Lau kuwa bahari ndio wino kwa maneno ya Mola wangu Mlezi, basi bahari ingeli malizika kabla ya kumalizika maneno ya Mola wangu Mlezi, hata tungeli ileta mfano wa hiyo kuongezea.'),
(2250, 18, 110, 'Sema: Mimi ni mwanaadamu kama nyinyi. Ninaletewa Wahyi kwamba Mungu wenu ni Mungu Mmoja. Mwenye kutaraji kukutana na Mola wake Mlezi basi naatende vitendo vyema, wala asimshirikishe yeyote katika ibada ya Mola wake Mlezi.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Kaf Ha Ya A\'yn S\'ad'),
(2252, 19, 2, 'Huu ni Ukumbusho wa rehema ya Mola wako Mlezi kwa mja wake, Zakariya.'),
(2253, 19, 3, 'Alipo mwita Mola wake Mlezi kwa siri.'),
(2254, 19, 4, 'Akasema: Mola wangu Mlezi! Mafupa yangu yamedhoofika, na kichwa kinameremeta kwa mvi; wala, Mola wangu Mlezi, sikuwa mwenye bahati mbaya kwa kukuomba Wewe.'),
(2255, 19, 5, 'Na hakika mimi nawakhofia jamaa zangu baada yangu. Na mke wangu ni tasa. Basi nipe mrithi kutoka kwako.'),
(2256, 19, 6, 'Atakaye nirithi mimi na awarithi ukoo wa Yaaqub. Ewe Mola wangu Mlezi! Na umjaalie awe mwenye kuridhisha.'),
(2257, 19, 7, '(Akaambiwa): Ewe Zakariya! Hakika Sisi tunakubashiria mwana; jina lake ni Yahya. Hatujapata kumpa jina hilo yeyote kabla yake.'),
(2258, 19, 8, 'Akasema: Ewe Mola wangu Mlezi! Vipi nitakuwa na mwana na hali mke wangu ni tasa, na mimi nimesha fikia ukongwe katika uzee?'),
(2259, 19, 9, 'Akasema: Ndio hivyo hivyo amesema Mola wako Mlezi: Haya ni mepesi kwangu. Na hakika Mimi nilikwisha kuumba wewe zamani na hali hukuwa kitu.'),
(2260, 19, 10, 'Akasema: Mola wangu Mlezi nijaalie Ishara. Akasema Ishara yako ni kuwa hutasema na watu kwa masiku matatu, na hali ni mzima.'),
(2261, 19, 11, 'Basi akawatokea watu wake kutoka mihirabuni. Akawaashiria: Msabihini Mwenyezi Mungu asubuhi na jioni.'),
(2262, 19, 12, 'Ewe Yahya! Kishike Kitabu kwa nguvu! Nasi tulimpa hikima angali mtoto.'),
(2263, 19, 13, 'Na tukampa huruma itokayo kwetu, na utakaso, na akawa mchamungu.'),
(2264, 19, 14, 'Na akawa mwema kwa wazazi wake, wala hakuwa jabari mua\'si.'),
(2265, 19, 15, 'Na amani iwe juu yake siku ya kuzaliwa, na siku ya kufa, na siku ya kufufuliwa.'),
(2266, 19, 16, 'Na mtaje Maryamu katika Kitabu, pale alipo jitenga na jamaa zake mahali upande wa mashariki;'),
(2267, 19, 17, 'Na akaweka pazia kujikinga nao. Tukampelekea Roho wetu, akajifananisha kwake sawa na mtu.'),
(2268, 19, 18, '(Maryamu) akasema: Hakika mimi najikinga kwa Mwingi wa Rehema aniepushe nawe, ukiwa ni mchamngu.'),
(2269, 19, 19, '(Malaika) akasema: Hakika mimi ni mwenye kutumwa na Mola wako Mlezi ili nikubashirie tunu ya mwana aliye takasika.'),
(2270, 19, 20, 'Akasema: Nitampataje mwana hali mwanaadamu yeyote hajanigusa, wala mimi si kahaba?'),
(2271, 19, 21, '(Malaika) akasema: Ndio hivyo hivyo! Mola wako Mlezi amesema: Haya ni mepesi kwangu! Na ili tumfanye kuwa ni Ishara kwa watu, na Rehema itokayo kwetu, na hilo ni jambo lilio kwisha hukumiwa.'),
(2272, 19, 22, 'Basi akachukua mimba yake, na akaondoka nayo mpaka mahali pa mbali.'),
(2273, 19, 23, 'Kisha uchungu ukampeleka kwenye shina la mtende; akasema: Laiti ningeli kufa kabla ya haya, na nikawa niliye sahaulika kabisa!'),
(2274, 19, 24, 'Pakatangazwa kutoka chini yake: Usihuzunike! Hakika Mola wako Mlezi amejaalia chini yako kijito kidogo cha maji!'),
(2275, 19, 25, 'Na litikise kwako hilo shina la mtende, utakuangushia tende nzuri zilizo mbivu.'),
(2276, 19, 26, 'Basi kula, na kunywa, na litue jicho lako. Na pindi ukimwona mtu yeyote basi sema: Hakika mimi nimeweka nadhiri kwa Mwenyezi Mungu ya kufunga; kwa hivyo leo sitasema na mtu.'),
(2277, 19, 27, 'Akenda naye (mwanawe) kwa jamaa zake amembeba. Wakasema: Ewe Maryamu! Hakika umeleta kitu cha ajabu!'),
(2278, 19, 28, 'Ewe dada yake Harun! Baba yako hakuwa mtu muovu, wala mama yako hakuwa kahaba.'),
(2279, 19, 29, 'Akawaashiria (mtoto). Wakasema: Vipi tumsemeze aliye bado mdogo yumo katika mlezi?'),
(2280, 19, 30, '(Mtoto) akasema: Hakika mimi ni mtumwa wa Mwenyezi Mungu. Amenipa Kitabu, na amenifanya Nabii.'),
(2281, 19, 31, 'Na amenijaalia ni mwenye kubarikiwa popote pale niwapo. Na ameniusia Sala na Zaka maadamu ni hai,'),
(2282, 19, 32, 'Na nimtendee wema mama yangu. Wala hakunifanya niwe jeuri, mwovu.'),
(2283, 19, 33, 'Na amani iko juu yangu siku niliyo zaliwa, na siku nitakayo kufa, na siku nitakayo fufuliwa kuwa hai.'),
(2284, 19, 34, 'Huyo ndiye Isa mwana wa Maryamu. Ndiyo kauli ya haki ambayo wanaifanyia shaka.'),
(2285, 19, 35, 'Haiwi kwa Mwenyezi Mungu kuwa na mwana, Subhanahu, Yeye ametakasika! Anapo lihukumia jambo basi huliambia tu: Kuwa! Likawa.'),
(2286, 19, 36, 'Na hakika Mwenyezi Mungu ni Mola wangu Mlezi, na Mola wenu Mlezi. Basi muabuduni Yeye. Hii ndiyo Njia Iliyo Nyooka.'),
(2287, 19, 37, 'Lakini makundi yakakhitalifiana wao kwa wao. Basi ole wao hao walio kufuru kwa kuhudhuria Siku iliyo kuu!'),
(2288, 19, 38, 'Watasikia na wataona vizuri vilioje Siku watakayo tufikia! Lakini madhaalimu sasa wamo katika upotofu ulio dhaahiri.'),
(2289, 19, 39, 'Na waonye siku ya majuto itapo katwa amri. Nao wamo katika ghafla, wala hawaamini.'),
(2290, 19, 40, 'Hakika Sisi tutairithi ardhi na walio juu yake. Na kwetu watarejeshwa.'),
(2291, 19, 41, 'Na mtaje katika Kitabu Ibrahim. Hakika yeye alikuwa mkweli, Nabii.'),
(2292, 19, 42, 'Alipo mwambia baba yake: Ewe baba yangu! Kwa nini unaabudu visivyo sikia, na visivyo ona, na visivyo kufaa chochote?'),
(2293, 19, 43, 'Ewe baba yangu! Kwa yakini imenifikia ilimu isiyo kufikia wewe. Basi nifuate mimi, nami nitakuongoza Njia Iliyo Sawa.'),
(2294, 19, 44, 'Ewe baba yangu! Usimuabudu Shet\'ani. Hakika Shet\'ani ni mwenye kumuasi Mwingi wa Rehema.'),
(2295, 19, 45, 'Ewe baba yangu! Hakika mimi naogopa isikupate adhabu inayo toka kwa Mwingi wa Rehema ukaja kuwa mwenziwe Shet\'ani.'),
(2296, 19, 46, '(Baba) akasema: Unaichukia miungu yangu, ewe Ibrahim? Kama huachi, basi lazima nitakupiga mawe. Na niondokelee mbali kwa muda!'),
(2297, 19, 47, '(Ibrahim) akasema: Salamun a\'laika! Amani iwe juu yako! Mimi nitakuombea msamaha kwa Mola wangu Mlezi. Hakika Yeye ananihurumia sana.'),
(2298, 19, 48, 'Nami najitenga nanyi na hayo mnayo yaabudu badala ya Mwenyezi Mungu. Na ninamwomba Mola wangu Mlezi; asaa nisiwe mwenye kukosa bahati kwa kumwomba Mola wangu Mlezi.'),
(2299, 19, 49, 'Basi alipo jitenga nao na yale waliyo kuwa wakiyaabudu badala ya Mwenyezi Mungu, tulimpa Is-haq na Yaaqub, na kila mmoja tukamfanya Nabii.'),
(2300, 19, 50, 'Na tukawapa rehema zetu na tukawajaalia kupewa sifa za kweli tukufu.'),
(2301, 19, 51, 'Na mtaje Musa katika Kitabu. Hakika yeye alikuwa ni mwenye kuchaguliwa, na alikuwa Mtume, Nabii.'),
(2302, 19, 52, 'Na tulimwita upande wa kulia wa mlima na tukamsogeza kunong\'ona naye.'),
(2303, 19, 53, 'Nasi kwa rehema zetu tukampa nduguye, Harun, awe Nabii.'),
(2304, 19, 54, 'Na mtaje katika Kitabu Ismail. Bila ya shaka yeye alikuwa ni mkweli wa ahadi, na alikuwa Mtume, Nabii.'),
(2305, 19, 55, 'Na alikuwa akiwaamrisha watu wake Sala na Zaka, na alikuwa mbele ya Mola wake Mlezi mwenye kuridhiwa.'),
(2306, 19, 56, 'Na mtaje katika Kitabu Idris. Hakika yeye alikuwa ni mkweli Nabii.'),
(2307, 19, 57, 'Na tulimuinua daraja ya juu.'),
(2308, 19, 58, 'Hao ndio alio waneemesha Mwenyezi Mungu miongoni mwa Manabii katika uzao wa Adam, na katika uzao wa wale tulio wapandisha pamoja na Nuhu, na katika uzao wa Ibrahim na Israil, na katika wale tulio waongoa na tukawateuwa. Wanapo somewa Aya za Mwingi wa Rehema huanguka kusujudu na kulia.'),
(2309, 19, 59, 'Lakini wakaja baada yao walio wabaya, wakaacha Sala, na wakafuata matamanio. Basi watakuja kuta malipo ya ubaya.'),
(2310, 19, 60, 'Isipo kuwa walio tubu, wakaamini, na wakatenda mema. Hao, basi, wataingia Peponi wala hawatadhulumiwa chochote.'),
(2311, 19, 61, 'Bustani za milele alizo waahidi Rahmani waja wake katika siri. Hakika ahadi yake hapana shaka itafika.'),
(2312, 19, 62, 'Humo hawatasikia upuuzi, ila Salama tu. Na watapata humo riziki zao asubuhi na jioni.'),
(2313, 19, 63, 'Hiyo ndiyo Pepo tutayo warithisha katika waja wetu walio kuwa wachamngu.'),
(2314, 19, 64, '(Nao watasema:) Wala hatuteremki ila kwa amri ya Mola wako Mlezi. Ni yake Yeye yaliyoko mbele yetu na yaliyoko nyuma yetu, na yaliyomo katikati ya hayo. Na Mola wako Mlezi si mwenye kusahau.'),
(2315, 19, 65, 'Mola Mlezi wa mbingu na ardhi na vilivyo baina yake. Basi muabudu Yeye tu, na udumu katika ibada yake. Je, mnamjua mwenye jina kama lake?'),
(2316, 19, 66, 'Na mwanaadamu husema: Hivyo, nitakapo kufa, ni kweli nitafufuliwa niwe hai tena?'),
(2317, 19, 67, 'Je! Hakumbuki mwanaadamu ya kwamba tulimuumba kabla, na hali hakuwa chochote?'),
(2318, 19, 68, 'Basi naapa kwa Mola wako Mlezi! Kwa yakini tutawakusanya wao pamoja na mashet\'ani; kisha tutawahudhurisha kuizunguka Jahannamu, nao wamepiga magoti!'),
(2319, 19, 69, 'Kisha kwa yakini tutawatoa katika kila kundi wale miongoni mwao walio zidi kumuasi Arrahmani, Mwingi wa Rahema..'),
(2320, 19, 70, 'Tena hakika Sisi tunawajua vyema zaidi wanao stahiki kuunguzwa humo.'),
(2321, 19, 71, 'Wala hapana yeyote katika nyinyi ila ni mwenye kuifikia. Hiyo ni hukumu ya Mola wako Mlezi ambayo lazima itimizwe.'),
(2322, 19, 72, 'Kisha tutawaokoa wale walio mchamngu; na tutawaacha madhaalimu humo wamepiga magoti.'),
(2323, 19, 73, 'Na wanapo somewa Aya zetu zilizo wazi, walio kufuru huwaambia walio amini: Lipi katika makundi mawili lenye cheo kizuri na lilio bora barazani?'),
(2324, 19, 74, 'Na kaumu ngapi tuliziangamiza zilizo kuwa na mapambo mazuri zaidi na wazuri zaidi kwa kuwatazama!'),
(2325, 19, 75, 'Sema: Walio katika upotofu basi Arrahmani Mwingi wa Rahema atawapururia muda mpaka wayaone waliyo onywa - ama ni adhabu au ni Saa. Hapo ndipo watapo jua ni nani mwenye makao mabaya na mwenye askari dhaifu.'),
(2326, 19, 76, 'Na Mwenyezi Mungu huwazidishia uwongofu wenye kushika uwongofu. Na mema yenye kudumu ni bora katika malipo mbele ya Mola wako Mlezi na yana mwisho mwema.'),
(2327, 19, 77, 'Je, umemwona aliye zikanya Ishara zetu na akasema: Kwa hakika mimi nitapewa mali na wana!'),
(2328, 19, 78, 'Kwani yeye amepata khabari za ghaibu, au amechukua ahadi kwa Arrahmani Mwingi wa Rehema?'),
(2329, 19, 79, 'Hasha! Tutaandika anayo yasema, tutamkunjulia muda wa adhabu.'),
(2330, 19, 80, 'Na tutamrithi hayo anayo yasema, na atatufikia mtupu peke yake!'),
(2331, 19, 81, 'Na wamechukua miungu mingine badala ya Mwenyezi Mungu ili ati iwape nguvu.'),
(2332, 19, 82, 'Sivyo kabisa! Wataikataa hiyo ibada yao, na watakuwa ndio dhidi yao.'),
(2333, 19, 83, 'Kwani huoni ya kwamba tumewatuma mashet\'ani kwa makafiri wawachochee kwa uchochezi?'),
(2334, 19, 84, 'Basi usiwafanyie haraka. Sisi tunawahisabia idadi ya siku zao.'),
(2335, 19, 85, 'Siku tutayo wakusanya wachamngu kuwapeleka kwa Arrahmani Mwingi wa Rehema kuwa ni wageni wake.'),
(2336, 19, 86, 'Na tukawachunga wakhalifu kuwapeleka Jahannamu hali ya kuwa wana kiu.'),
(2337, 19, 87, 'Hawatakuwa na mamlaka ya uombezi ila wale walio chukua ahadi kwa Arrahmani Mwingi wa Rehema..'),
(2338, 19, 88, 'Na wao ati husema kuwa Arrahmani Mwingi wa Rehema ana mwana!'),
(2339, 19, 89, 'Hakika mmeleta jambo la kuchusha mno!'),
(2340, 19, 90, 'Zinakaribia mbingu kutatuka kwa hilo, na ardhi kupasuka, na milima kuanguka vipande vipande.'),
(2341, 19, 91, 'Kwa kudai kwao kuwa Arrahmani Mwingi wa Rehema ana mwana.'),
(2342, 19, 92, 'Wala hahitajii Arrahmani Mwingi wa Rehema kuwa na mwana.'),
(2343, 19, 93, 'Hapana yeyote aliomo mbinguni na ardhini ila atafika kwa Arrahmani Mwingi wa Rehema kuwa ni mtumwa wake.'),
(2344, 19, 94, 'Yeye hakika amewadhibiti na amewahisabu sawa sawa.'),
(2345, 19, 95, 'Na kila mmoja katika wao atamfikia Siku ya Kiyama peke yake.'),
(2346, 19, 96, 'Hakika walio amini na wakatenda mema Arrahmani Mwingi wa Rehema atawajaalia mapenzi.'),
(2347, 19, 97, 'Na hakika tumeifanya nyepesi (Qur\'ani) kwa ulimi wako, ili uwabashirie kwayo wachamngu, na uwaonye kwayo watu wabishi.'),
(2348, 19, 98, 'Na kaumu ngapi tumezihiliki kabla yao. Je! Unawaona hata mmoja katika wao au unasikia hata mchakato wao?');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'T\'AHA!'),
(2350, 20, 2, 'Hatukukuteremshia Qur\'ani ili upate mashaka.'),
(2351, 20, 3, 'Bali ni mawaidha kwa wenye kunyenyekea.'),
(2352, 20, 4, 'Materemsho yatokayo kwa aliye umba ardhi na mbingu zilizo juu.'),
(2353, 20, 5, 'Arrahmani, Mwingi wa Rehema, aliye tawala juu ya Kiti cha Enzi.'),
(2354, 20, 6, 'Ni vyake vyote viliomo mbinguni na viliomo katika ardhi, na viliomo baina yao, na viliomo chini ya ardhi.'),
(2355, 20, 7, 'Na ukinyanyua sauti kwa kusema... basi hakika Yeye anajua siri na duni kuliko siri.'),
(2356, 20, 8, 'Mwenyezi Mungu! Hapana mungu isipo kuwa Yeye. Yeye ana majina mazuri kabisa.'),
(2357, 20, 9, 'Na je! Imekufikia hadithi ya Musa?'),
(2358, 20, 10, 'Alipo uona moto, akawaambia watu wake: Ngojeni! Mimi nimeuona moto, huenda nikakuleteeni kijinga kutoka huo moto, au nikapata uongofu kwenye moto.'),
(2359, 20, 11, 'Basi alipo ufikia akaitwa: Ewe Musa!'),
(2360, 20, 12, 'Hakika Mimi ndiye Mola wako Mlezi! Basi vua viatu vyako. Kwani upo katika bonde takatifu la T\'uwa.'),
(2361, 20, 13, 'Nami nimekuteua wewe; basi sikiliza unayo funuliwa.'),
(2362, 20, 14, 'Hakika Mimi ndiye Mwenyezi Mungu. Hapana mungu ila Mimi tu. Basi niabudu Mimi, na ushike Sala kwa ajili ya kunikumbuka Mimi.'),
(2363, 20, 15, 'Hakika Saa itakuja bila ya shaka. Nimekaribia kuificha, ili kila nafsi ilipwe kwa iliyo yafanya.'),
(2364, 20, 16, 'Kwa hivyo asikukengeushe nayo yule ambaye haiamini na akafuata pumbao lake ukaja kuhiliki.'),
(2365, 20, 17, 'Na nini hicho kilichomo mkononi mwako wa kulia, ewe Musa?'),
(2366, 20, 18, 'Akasema: Hii ni fimbo yangu; naiegemea na ninawaangushia majani kondoo na mbuzi wangu. Tena inanifaa kwa matumizi mengine.'),
(2367, 20, 19, 'Akasema: Itupe, ewe Musa!'),
(2368, 20, 20, 'Akaitupa. Mara ikawa nyoka anaye kwenda mbio.'),
(2369, 20, 21, 'Akasema: Ikamate, wala usiogope! Tutairudisha hali yake ya kwanza.'),
(2370, 20, 22, 'Na uambatishe mkono wako kwenye ubavu. Utatoka mweupe pasipo kuwa na madhara yoyote. Hiyo ni ishara nyengine.'),
(2371, 20, 23, 'Ili tukuonyeshe baadhi ya ishara zetu kubwa.'),
(2372, 20, 24, 'Nenda kwa Firauni; kwani hakika yeye amepindukia mipaka.'),
(2373, 20, 25, '(Musa) akasema: Ewe Mola wangu Mlezi! Nikunjulie kifua changu,'),
(2374, 20, 26, 'Na unifanyie nyepesi kazi yangu,'),
(2375, 20, 27, 'Na ulifungue fundo lililo katika ulimi wangu,'),
(2376, 20, 28, 'Wapate kufahamu maneno yangu.'),
(2377, 20, 29, 'Na nipe waziri katika watu wangu,'),
(2378, 20, 30, 'Harun, ndugu yangu.'),
(2379, 20, 31, 'Kwake yeye niongeze nguvu zangu.'),
(2380, 20, 32, 'Na umshirikishe katika kazi yangu.'),
(2381, 20, 33, 'Ili tukutakase sana.'),
(2382, 20, 34, 'Na tukukumbuke sana.'),
(2383, 20, 35, 'Hakika Wewe unatuona.'),
(2384, 20, 36, 'Akasema: Hakika Umepewa maombi yako, ewe Musa!'),
(2385, 20, 37, 'Na hakika tulikwisha kufanyia hisani mara nyengine...'),
(2386, 20, 38, 'Pale tulipo mfunulia mama yako yaliyo funuliwa,'),
(2387, 20, 39, 'Ya kuwa mtie katika kisanduku, na kisha kitie mtoni, na mto utamfikisha ufukweni. Atamchukua adui yangu naye ni adui yake. Na nimekutilia mapenzi yanayo tokana kwangu, na ili ulelewe machoni mwangu.'),
(2388, 20, 40, 'Dada yako alipo kwenda na akasema: Je! Nikujuulisheni mtu wa kuweza kumlea? Basi tukakurejesha kwa mama yako ili macho yake yaburudike, wala asihuzunike. Na ulimuuwa mtu, nasi tukakuokoa katika dhiki, na tukakujaribu kwa majaribio mengi. Ukakaa miaka kwa watu wa Madyana. Kisha ukaja kama ilivyo kadiriwa, ewe Musa!'),
(2389, 20, 41, 'Na nimekuteuwa kwa ajili ya nafsi yangu.'),
(2390, 20, 42, 'Nenda, wewe na ndugu yako, pamoja na ishara zangu, wala msichoke kunikumbuka.'),
(2391, 20, 43, 'Nendeni kwa Firauni. Hakika yeye amepindukia mipaka.'),
(2392, 20, 44, 'Mwambieni maneno laini, huenda akazingatia au akaogopa.'),
(2393, 20, 45, 'Wakasema: Ewe Mola wetu Mlezi! Hakika sisi tunaogopa asije akapindukia mipaka juu yetu au kufanya jeuri.'),
(2394, 20, 46, 'Akasema: Msiogope! Hakika Mimi ni pamoja nanyi. Nasikia na ninaona.'),
(2395, 20, 47, 'Basi mwendeeni, na mwambieni: Hakika sisi ni Mitume wa Mola wako Mlezi! Basi waache Wana wa Israili watoke nasi, wala usiwaadhibu. Sisi tumekuletea ishara itokayo kwa Mola wako Mlezi. Na amani itakuwa juu ya aliye fuata uwongofu.'),
(2396, 20, 48, 'Hakika tumefunuliwa sisi kwamba hapana shaka adhabu itamsibu anaye kadhibisha na akapuuza.'),
(2397, 20, 49, '(Firauni) akasema: Basi Mola wenu Mlezi ni nani, ewe Musa?'),
(2398, 20, 50, 'Akasema: Mola wetu Mlezi ni yule aliye kipa kila kitu umbo lake, kisha akakiongoa.'),
(2399, 20, 51, 'Akasema: Nini hali ya karne za kwanza?'),
(2400, 20, 52, 'Akasema: Ilimu yake iko kwa Mola wangu Mlezi katika Kitabu. Mola wangu Mlezi hapotei wala hasahau.'),
(2401, 20, 53, 'Ambaye amekufanyieni ardhi kuwa tandiko, na akakupitishieni humo njia, na akakuteremshieni kutoka mbinguni maji. Na kwa hayo tukatoa namna mbali mbali za mimema.'),
(2402, 20, 54, 'Kuleni, na mchunge wanyama wenu. Hakika katika hayo zipo ishara kwa wenye akili.'),
(2403, 20, 55, 'Kutokana nayo (ardhi) tumekuumbeni, na humo tunakurudisheni, na kutoka humo tutakutoeni mara nyengine.'),
(2404, 20, 56, 'Na hakika tulimwonyesha ishara zetu zote. Lakini alikadhibisha na akakataa.'),
(2405, 20, 57, 'Akasema: Ewe Musa! Hivyo umetujia kututoa katika nchi yetu kwa uchawi wako?'),
(2406, 20, 58, 'Basi sisi hakika tutakueletea uchawi kama huo. Basi weka miadi baina yetu na wewe, ambayo tusiivunje sisi wala wewe, mahali patapo kuwa sawa.'),
(2407, 20, 59, 'Akasema: Miadi yenu ni siku ya Sikukuu; na watu wakusanywe kabla ya adhuhuri.'),
(2408, 20, 60, 'Basi Firauni akarudi na akatengeneza hila yake, kisha akaja.'),
(2409, 20, 61, 'Musa akamwambia: Ole wenu! Msimzulie Mwenyezi Mungu uwongo, asije akakufutilieni mbali kwa adhabu. Na mwenye kuzua lazima ashindwe!'),
(2410, 20, 62, 'Basi wakazozana kwa shauri yao wenyewe kwa wenyewe, na wakanong\'ona kwa siri.'),
(2411, 20, 63, 'Wakasema: Hakika hawa wawili ni wachawi, wanataka kukutoeni katika nchi yenu kwa uchawi wao, na waondoe mila zenu zilizo bora kabisa.'),
(2412, 20, 64, 'Kwa hivyo kusanyeni hila zenu zote, kisha mfike kwa kujipanga safu, na kwa yakini atafuzu kweli kweli leo atakaye shinda.'),
(2413, 20, 65, 'Wakasema: Ewe Musa! Je! Utatupa wewe, au tutakuwa sisi wa kwanza kutupa?'),
(2414, 20, 66, 'Akasema: Bali tupeni nyinyi! Tahamaki kamba zao na fimbo zao zikaonekana mbele yake, kwa uchawi wao, kuwa zinapiga mbio.'),
(2415, 20, 67, 'Basi Musa akaingia khofu nafsi yake.'),
(2416, 20, 68, 'Tukasema: Usikhofu! Hakika wewe ndiye utakaye shinda.'),
(2417, 20, 69, 'Na kitupe kilicho katika mkono wako wa kulia; kitavimeza walivyo viunda. Hakika walivyo unda ni hila za mchawi tu, na mchawi hafanikiwi popote afikapo.'),
(2418, 20, 70, 'Basi wachawi wakaangushwa wanasujudu. Wakasema: Tumemuamini Mola Mlezi wa Harun na Musa!'),
(2419, 20, 71, '(Firauni) akasema: Oh! Mnamuamini kabla sijakupeni ruhusa? Hakika yeye ndiye mkubwa wenu aliye kufundisheni uchawi. Basi kwa yakini nitakukateni mikono yenu na miguu yenu kwa kutafautisha. Na nitakutundikeni misalabani katika vigogo vya mitende, na hapo kwa yakini mtajua ni nani katika sisi aliye mkali wa kuadhibu na kuiendeleza adhabu yake.'),
(2420, 20, 72, 'Wakasema: Hatutokukhiari wewe kuliko ishara waziwazi zilizo tujia, na kuliko yule aliye tuumba. Basi hukumu utavyo hukumu; kwani wewe unahukumu haya ya maisha ya duniani tu.'),
(2421, 20, 73, 'Hakika sisi tumemuamini Mola wetu Mlezi ili atusamehe makosa yetu na uchawi ulio tulazimisha kuufanya. Na Mwenyezi Mungu ndiye Mbora na Mwenye kudumu zaidi.'),
(2422, 20, 74, 'Hakika ataye mjia Mola wake Mlezi naye ni mkhalifu, basi kwa yakini, yake huyo ni Jahannamu. Hafi humo wala haishi.'),
(2423, 20, 75, 'Na atakaye mjia naye ni Muumini aliye tenda mema, basi hao ndio wenye vyeo vya juu.'),
(2424, 20, 76, 'Bustani za milele zipitazo mito kati yake, wadumu humo. Na hayo ni malipo ya mwenye kujitakasa.'),
(2425, 20, 77, 'Na tulimfunulia Musa: Toka usiku na waja wangu, na uwapigie njia kavu baharini. Usikhofu kukamatwa, wala usiogope.'),
(2426, 20, 78, 'Firauni akawafuata pamoja na majeshi yake. Basi kiliwafudikiza humo baharini kilicho wafudikiza.'),
(2427, 20, 79, 'Na Firauni aliwapoteza watu wake wala hakuwaongoa.'),
(2428, 20, 80, 'Enyi Wana wa Israili! Hakika tulikuokoeni na adui yenu, na tukakuahidini upande wa kulia wa mlima. Na tulikuteremshieni Manna na Salwa.'),
(2429, 20, 81, 'Kuleni katika vitu vizuri hivyo tulivyo kuruzukuni, wala msiruke mipaka katika hayo, isije ikakushukieni ghadhabu yangu. Na inaye mshukia ghadhabu yangu basi huyo ameangamia.'),
(2430, 20, 82, 'Na hakika Mimi ni Mwingi wa Kusamehe kwa anaye tubia, na akaamini, na akatenda mema, tena akaongoka.'),
(2431, 20, 83, 'Na nini kilicho kutia haraka ukawaacha watu wako, ewe Musa?'),
(2432, 20, 84, 'Akasema: Hao wapo nyuma yangu wananifuatia. Na nimefanya haraka kukujia, Mola wangu Mlezi, ili uridhike.'),
(2433, 20, 85, '(Mwenyezi Mungu) akasema: Sisi tumewatia mtihani watu wako baada yako, na Msamaria amewapoteza.'),
(2434, 20, 86, 'Musa akarudi kwa watu wake kwa ghadhabu na kusikitika. Akasema: Enyi watu wangu! Kwani Mola wenu Mlezi hakukuahidini ahadi nzuri? Je, umekuwa muda mrefu kwenu au mmetaka ikushukieni ghadhabu ya Mola wenu Mlezi, ndio maana mkavunja miadi yangu?'),
(2435, 20, 87, 'Wakasema: Sisi hatukuvunja miadi yako kwa khiari yetu, lakini tulibebeshwa mizigo ya mapambo ya watu, basi tukaitupa. Na hivyo ndivyo alivyo toa shauri Msamaria.'),
(2436, 20, 88, 'Na akawaundia ndama, kiwiliwili chenye sauti. Na wakasema: Huyu ndiye mungu wenu, na mungu wa Musa, lakini alisahau.'),
(2437, 20, 89, 'Je! Hawakuona kuwa hakuwarudishia neno, wala hakuweza kuwadhuru wala kuwafaa?'),
(2438, 20, 90, 'Na hakika Harun alikwisha waambia kabla yake: Enyi watu wangu! Hakika nyinyi mmetiwa mtihanini tu kwa kitu hiki. Na kwa yakini Mola wenu Mlezi ni Arrahmani, Mwingi wa Rehema. Basi nifuateni mimi, na t\'iini amri yangu!'),
(2439, 20, 91, 'Wakasema: Hatutaacha kumuabudu mpaka Musa atapo rejea kwetu.'),
(2440, 20, 92, '(Musa) akasema: Ewe Harun! Ni nini kilicho kuzuia, ulipo waona wamepotea,'),
(2441, 20, 93, 'Hata usinifuate? Je, umeasi amri yangu?'),
(2442, 20, 94, 'Akasema: Ewe mwana wa mama yangu! Usinishike ndevu zangu, wala kichwa changu. Kwa hakika naliogopa usije sema: Umewafarikisha Wana wa Israil, na hukungojea kauli yangu.'),
(2443, 20, 95, '(Musa) akasema: Ewe Msamaria! Unataka nini?'),
(2444, 20, 96, 'Akasema: Niliona wasiyo yaona wao, nikashika gao katika unyao wa Mtume. Kisha nikalitupa. Na hivi ndivyo ilivyo nielekeza nafsi yangu.'),
(2445, 20, 97, '(Musa) akasema: Basi ondoka! Na kwa yakini utakuwa katika maisha ukisema: Usiniguse. Na hakika una ahadi kwako isiyo vunjwa. Na mtazame huyo mungu wako uliye endelea kumuabudu - Sisi kwa hakika tutamchoma moto, kisha tutamtawanya baharini atawanyike.'),
(2446, 20, 98, 'Hakika Mungu wenu ni Allah, Mwenyezi Mungu, tu. Hapana Mungu isipo kuwa Yeye. Ujuzi wake umeenea kila kitu.'),
(2447, 20, 99, 'Namna hivi tunakuhadithia katika khabari za yaliyo tangulia. Na hakika tumekuletea kutoka kwetu mawaidha ya kukumbukwa.'),
(2448, 20, 100, 'Atakaye jitenga nayo, basi kwa yakini Siku ya Kiyama atabeba mzigo.'),
(2449, 20, 101, 'Wadumu humo. Na ni mzigo muovu kwao kuubeba Siku ya Kiyama!'),
(2450, 20, 102, 'Siku litakapo pulizwa barugumu, na tukawakusanya wakhalifu Siku hiyo, hali macho yao ya kibuluu.'),
(2451, 20, 103, 'Watanong\'onezana wao kwa wao: Hamkukaa ila siku kumi tu.'),
(2452, 20, 104, 'Sisi tunajua zaidi watakayo yasema, atakapo sema mbora wao katika mwendo: Nyinyi hamkukaa ila siku moja tu.'),
(2453, 20, 105, 'Na wanakuuliza khabari za milima. Waambie: Mola wangu Mlezi ataivuruga vuruga.'),
(2454, 20, 106, 'Na ataiacha tambarare, uwanda.'),
(2455, 20, 107, 'Hutaona humo mdidimio wala muinuko.'),
(2456, 20, 108, 'Siku hiyo watamfuata muitaji asiye na upotofu. Na sauti zote zitamnyenyekea Arrahmani Mwingi wa Rahema. Basi husikii ila mchakato na mnong\'ono.'),
(2457, 20, 109, 'Siku hiyo uombezi haufai kitu, ila wa aliye mruhusu Arrahmani Mwingi wa Rehema na akamridhia kusema.'),
(2458, 20, 110, 'Yeye anajua yaliyo mbele yao, na yaliyo nyuma yao. Wala wao hawawezi kumjua Yeye vilivyo.'),
(2459, 20, 111, 'Na zitadhalilika nyuso mbele ya Aliye Hai Milele, Mwangalizi Mkuu. Na atakuwa ameharibikiwa mwenye kubeba dhulma.'),
(2460, 20, 112, 'Na anaye tenda mema, naye ni Muumini, basi hatakhofu kudhulumiwa wala kupunjwa.'),
(2461, 20, 113, 'Na namna hivi tumeiteremsha Qur\'ani kwa Kiarabu, na tumekariri humo maonyo ili wapate kuogopa na kujilinda na iwaletee makumbusho.'),
(2462, 20, 114, 'Ametukuka Mwenyezi Mungu, Mfalme wa Haki. Wala usiifanyie haraka hii Qur\'ani, kabla haujamalizika ufunuo wake. Na sema: Mola wangu Mlezi! Nizidishie ilimu.'),
(2463, 20, 115, 'Na hapo zamani tuliagana na Adam, lakini alisahau, wala hatukuona kwake azma kubwa.'),
(2464, 20, 116, 'Na tulipo waambia Malaika: Msujudieni Adam! Wakasujudu, isipo kuwa Iblisi, alikataa.'),
(2465, 20, 117, 'Tukasema: Ewe Adam! Hakika huyu ni adui yako na wa mkeo. Basi asikutoeni katika Bustani hii, mkaingia mashakani.'),
(2466, 20, 118, 'Hakika humo hutakuwa na njaa wala hutakuwa uchi.'),
(2467, 20, 119, 'Na hakika hutapata kiu humo wala hutapata joto.'),
(2468, 20, 120, 'Lakini Shet\'ani alimtia wasiwasi, akamwambia: Ewe Adam! Nikujuulishe mti wa kuishi milele na ufalme usio koma?'),
(2469, 20, 121, 'Basi wakaula wote wawili, na uchi wao ukawadhihirikia na wakaanza kujibandika majani ya Bustani. Na Adam akamuasi Mola wake Mlezi, na akapotea njia.'),
(2470, 20, 122, 'Kisha Mola wake Mlezi akamteuwa, naye akamkubalia toba yake, naye akamwongoa.'),
(2471, 20, 123, 'Akasema: Ondokeni humu nyote, hali ya kuwa ni maadui nyinyi kwa nyinyi. Na ukikufikieni uwongofu kutoka kwangu basi atakaye ufuata uwongofu wangu, hatapotea wala hatataabika.'),
(2472, 20, 124, 'Na atakaye jiepusha na mawaidha yangu, basi kwa yakini atapata maisha yenye dhiki, na Siku ya Kiyama tutamfufua hali ya kuwa kipofu.'),
(2473, 20, 125, 'Aseme: Ewe Mola wangu Mlezi! Mbona umenifufua kipofu, nami nilikuwa nikiona?'),
(2474, 20, 126, '(Mwenyezi Mungu) atasema: Ndivyo vivyo hivyo. Zilikufikia ishara zetu, nawe ukazisahau; na kadhaalika leo unasahauliwa.'),
(2475, 20, 127, 'Na hivi ndivyo tutakavyo mlipa kila apitaye kiasi, na asiye amini ishara za Mola wake Mlezi. Na hakika adhabu ya Akhera ni kali zaidi, na inadumu zaidi.'),
(2476, 20, 128, 'Je! Haikuwabainikia tu; vizazi vingapi tuliviangamiza kabla yao, nao wanatembea katika maskani zao? Hakika katika hayo zimo ishara kwa wenye akili.'),
(2477, 20, 129, 'Na lau kuwa si neno lililo kwisha tangulia kutoka kwa Mola wako Mlezi na muda ulio wekwa, bila ya shaka inge fika adhabu (hapa hapa).'),
(2478, 20, 130, 'Yavumilie haya wayasemayo. Na umtakase Mola wako Mlezi kwa kumsifu kabla ya kuchomoza jua, na kabla halijachwa, na nyakati za usiku pia umtakase, na ncha za mchana ili upate ya kukuridhisha.'),
(2479, 20, 131, 'Wala usivikodolee macho tulivyo wastareheshea baadhi ya watu miongoni mwao, kwa ajili ya mapambo ya duniani, ili tuwajaribu. Na riziki ya Mola wako Mlezi ni bora na inadumu zaidi.'),
(2480, 20, 132, 'Na waamrishe watu wako kusali, na uendelee mwenyewe kwa hayo. Sisi hatukuombi wewe riziki, bali Sisi ndio tunakuruzuku. Na mwisho mwema ni kwa mchamngu.'),
(2481, 20, 133, 'Na walisema: Kwa nini hakutuletea muujiza kutoka kwa Mola wake Mlezi? Je! Haikuwafikilia dalili wazi ya yaliyomo katika Vitabu vya kale?'),
(2482, 20, 134, 'Na lau kuwa tuliwaangamiza kwa adhabu kabla yake, wangeli sema: Ewe Mola wetu Mlezi! Kwa nini usituletee Mtume tukazifuata ishara zako kabla hatujadhilika na kuhizika.'),
(2483, 20, 135, 'Sema: Kila mmoja anangoja. Basi ngojeni! Hivi karibuni mtajua nani mwenye njia sawa na nani aliye ongoka.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'IMEWAKARIBIA watu hisabu yao, nao wamo katika mghafala wanapuuza.'),
(2485, 21, 2, 'Hayawafikilii mawaidha mapya kutoka kwa Mola wao Mlezi ila huyasikiliza na huku wanafanya mchezo.'),
(2486, 21, 3, 'Zimeghafilika nyoyo zao. Na wale walio dhulumu hunong\'onezana kwa siri: Ni nani ati huyu isipo kuwa ni binaadamu tu kama nyinyi! Mnauwendea uchawi hali nanyi mnaona?'),
(2487, 21, 4, 'Alisema: Mola wangu Mlezi anayajua yasemwayo mbinguni na katika ardhi. Na Yeye ni Mwenye kusikia Mwenye kujua.'),
(2488, 21, 5, 'Lakini wao walisema: Hizo ni ndoto za ovyo ovyo, bali amezizua tu, bali huyo ni mtunga mashairi. Basi atuletee miujiza kama walivyo tumwa wale wa mwanzo.'),
(2489, 21, 6, 'Hawakuamini kabla yao watu wa mji tulio uangamiza. Basi, je, wataamini hawa?'),
(2490, 21, 7, 'Na kabla yako hatukuwatuma ila watu wanaume tulio wapa wahyi (ufunuo). Basi waulizeni wenye ilimu ikiwa nyinyi hamjui.'),
(2491, 21, 8, 'Wala hatukuwafanya miili isiyo kula chakula, wala hawakuwa wenye kuishi milele.'),
(2492, 21, 9, 'Kisha tukawatimizia miadi, na tukawaokoa wao na tulio wataka, na tukawateketeza walio pita mipaka.'),
(2493, 21, 10, 'Na hakika tumekuteremshieni Kitabu ambacho ndani yake yamo makumbusho yenu. Je! Hamzingatii?'),
(2494, 21, 11, 'Na mingapi katika miji iliyo kuwa ikidhulumu tumeiteketeza, na tukawasimamisha baada yao watu wengine.'),
(2495, 21, 12, 'Basi walipo ihisi adhabu yetu, mara wakaanza kukimbia.'),
(2496, 21, 13, 'Msikimbie! Na rejeeni kwenye zile zile starehe zenu, na maskani zenu, mpate kusailiwa!'),
(2497, 21, 14, 'Wakasema: Ole wetu! Hakika sisi tulikuwa madhaalimu.'),
(2498, 21, 15, 'Hakikuacha hicho kuwa kilio chao mpaka tukawafanya kama walio fyekwa, wamezimika.'),
(2499, 21, 16, 'Na hatukuziumba mbingu na ardhi na vilivyo baina yao kwa mchezo.'),
(2500, 21, 17, 'Kama tungeli taka kufanya mchezo tunge jifanyia Sisi wenyewe, lau kwamba tungeli kuwa ni wafanyao mchezo.'),
(2501, 21, 18, 'Bali tunaitupa kweli juu ya uwongo ikauvunja na mara ukatoweka. Na ole wenu kwa mnayo yazua.'),
(2502, 21, 19, 'Na ni vyake Yeye tu Mwenyezi Mungu vilivyomo mbinguni na katika ardhi. Na walioko kwake hawafanyi kiburi wakaacha kumuabudu, wala hawachoki.'),
(2503, 21, 20, 'Wanamtakasa usiku na mchana, wala hawanyong\'onyei.'),
(2504, 21, 21, 'Au wamepata miungu katika ardhi inayo fufua?'),
(2505, 21, 22, 'Lau wangeli kuwamo humo miungu wengine isipo kuwa Mwenyezi Mungu basi bila ya shaka hizo mbingu na ardhi zingeli fisidika. Subahana \'Llah Ametakasika Mwenyezi Mungu, Bwana wa A\'rshi (Kiti cha Enzi), na hayo wanayo yazua.'),
(2506, 21, 23, 'Yeye hahojiwi kwa ayatendayo, na wao ndio wanao hojiwa kwa wayatendayo.'),
(2507, 21, 24, 'Au wanawaabudu miungu mingine badala yake? Sema: Leteni ushahidi wenu! Haya ni ukumbusho wa hawa walio nami, na ukumbusho wa walio kabla yangu. Lakini wengi wao hawaijui kweli, na kwa hivyo wanapuuza.'),
(2508, 21, 25, 'Na hatukumtuma kabla yako Mtume yeyote ila tulimfunulia ya kwamba hapana mungu isipo kuwa Mimi. Basi niabuduni Mimi tu.'),
(2509, 21, 26, 'Na wanasema: Arrahman, Mwingi wa Rehema, ana mwana! Subhanahu, Ametakasika na hayo! Bali hao (wanao waita wana) ni watumwa walio tukuzwa.'),
(2510, 21, 27, 'Hawamtangulii kwa neno, nao wanafanya amri zake.'),
(2511, 21, 28, 'Anayajua yaliyo mbele yao na yaliyo nyuma yao. Wala hawamwombei yeyote ila yule anaye mridhia Yeye, nao kwa ajili ya kumwogopa wananyenyekea.'),
(2512, 21, 29, 'Na yeyote kati yao atakaye sema: Mimi ni mungu, badala yake, basi huyo tutamlipa malipo yake Jahannamu. Hivyo ndivyo tuwalipavyo wenye kudhulumu.'),
(2513, 21, 30, 'Je! Hao walio kufuru hawakuona kwamba mbingu na ardhi zilikuwa zimeambatana, kisha Sisi tukazibabandua? Na tukajaalia kwa maji kila kilicho hai? Basi je, hawaamini?'),
(2514, 21, 31, 'Na tukaweka katika ardhi milima iliyo thibiti ili isiwayumbishe, na tukaweka humo njia pana ili wapate kuongoka.'),
(2515, 21, 32, 'Na tukaifanya mbingu kuwa dari iliyo hifadhiwa, lakini wanazipuuza Ishara zake.'),
(2516, 21, 33, 'Na Yeye ndiye aliye umba usiku na mchana na jua na mwezi, vyote katika anga vinaogelea.'),
(2517, 21, 34, 'Nasi hatukumjaalia mwanaadamu yeyote kabla yako kuwa na maisha ya milele. Basi je! Ukifa wewe, wao wataishi milele?'),
(2518, 21, 35, 'Kila nafsi itaonja mauti; na tunakujaribuni kwa mtihani wa shari na kheri. Na kwetu Sisi mtarejeshwa.'),
(2519, 21, 36, 'Na wakikuona walio kufuru hawakufanyi wewe ila ni kitu cha maskhara tu, (wakisema): Je! Ndiye huyu anaye itaja miungu yenu? Na hali wao wanakataa kumkumbuka Arrahman, Mwingi wa Rehema!'),
(2520, 21, 37, 'Mwanaadamu ameumbwa na haraka. Nitakuonyesheni Ishara zangu. Basi msinihimize.'),
(2521, 21, 38, 'Na wanasema: Ahadi hii itatokea lini, ikiwa nyinyi mnasema kweli?'),
(2522, 21, 39, 'Lau wangeli jua wale walio kufuru wakati ambao hawatauzuia Moto kwenye nyuso zao wala migongo yao, na wala hawatanusuriwa!'),
(2523, 21, 40, 'Bali utawafikia kwa ghafla, na utawashitua na wala hawataweza kuurudisha, wala hawatapewa muhula!'),
(2524, 21, 41, 'Na Mitume walio kabla yako walikwisha dhihakiwa. Kwa hivyo wale walio wafanyia maskhara yaliwafika yale waliyo kuwa wakiyafanyia dhihaka.'),
(2525, 21, 42, 'Sema: Nani anaye kulindeni usiku na mchana na Arrahmani Mwingi wa Rehema? Bali wao wanapuuza kumkumbuka Mola wao Mlezi.'),
(2526, 21, 43, 'Au wao wanao miungu watao weza kuwakinga nasi? Hao hawawezi kujinusuru nafsi zao, wala hawatalindwa nasi!'),
(2527, 21, 44, 'Bali tuliwastarehesha hawa na baba zao mpaka umri ukawa mrefu kwao. Je, hawaoni ya kwamba Sisi tunaifikia ardhi tukiipunguza ncha zake? Basi je! Hao ni wenye kushinda?'),
(2528, 21, 45, 'Sema: Mimi nakuonyeni kwa Wahyi. Na viziwi hawasikii wito wanapo onywa.'),
(2529, 21, 46, 'Na wanapo guswa na mpulizo mmoja tu wa adhabu itokayo kwa Mola wako Mlezi, bila ya shaka wao husema: Ole wetu! Hakika tulikuwa wenye kudhulumu.'),
(2530, 21, 47, 'Nasi tutaweka mizani za uadilifu kwa Siku ya Kiyama. Basi nafsi haitadhulumiwa kitu chochote. Hata ikiwa ni uzito wa chembe ya khardali tutaileta. Nasi tunatosha kuwa washika hisabu.'),
(2531, 21, 48, 'Na kwa yakini tuliwapa Musa na Haaruni kipambanuzi, na mwangaza, na makumbusho kwa wachamngu,'),
(2532, 21, 49, 'Ambao wanamkhofu Mola wao Mlezi faraghani, na wanaiogopa Saa ya Kiyama.'),
(2533, 21, 50, 'Na haya ni makumbusho yaliyo barikiwa, tuliyo yateremsha. Basi je! Mnayakataa?'),
(2534, 21, 51, 'Na hakika tulikwisha mpa Ibrahim uwongofu wake zamani, na tulikuwa tunamjua.'),
(2535, 21, 52, 'Alipo mwambia baba yake na watu wake: Ni nini haya masanamu mnayo yashughulikia kuyaabudu?'),
(2536, 21, 53, 'Wakasema: Tumewakuta baba zetu wakiyaabudu.'),
(2537, 21, 54, 'Akasema: Bila ya shaka nyinyi na baba zenu mmekuwa katika upotofu ulio dhaahiri.'),
(2538, 21, 55, 'Wakasema: Je! Umetujia kwa maneno ya kweli au wewe ni katika wachezao tu?'),
(2539, 21, 56, 'Akasema: Bali Mola wenu Mlezi ni Mola Mlezi wa mbingu na ardhi ambaye ndiye aliye ziumba. Na mimi ni katika wenye kuyashuhudia hayo.'),
(2540, 21, 57, 'Na Wallahi! Nitayafanyia vitimbi masanamu yenu haya baada ya mkisha geuka kwenda zenu.'),
(2541, 21, 58, 'Basi akayavunja vipande vipande, ila kubwa lao, ili wao walirudie.'),
(2542, 21, 59, 'Wakasema: Nani aliye ifanyia haya miungu yetu? Hakika huyu ni katika walio dhulumu.'),
(2543, 21, 60, 'Wakasema: Tumemsikia kijana mmoja akiwataja. Anaitwa Ibrahim.'),
(2544, 21, 61, 'Wakasema: Mleteni mbele ya macho ya watu, wapate kumshuhudia!'),
(2545, 21, 62, 'Wakasema: Je! Wewe umeifanyia haya miungu yetu, ewe Ibrahim?'),
(2546, 21, 63, 'Akasema: Bali amefanya hayo huyu mkubwa wao. Basi waulizeni ikiwa wanaweza kutamka.'),
(2547, 21, 64, 'Basi wakajirudi nafsi zao, wakasema: Hakika nyinyi mlikuwa madhaalimu!'),
(2548, 21, 65, 'Kisha wakarejea kwenye ule ule upotovu wao wakasema: Wewe unajua kwamba hawa hawesemi.'),
(2549, 21, 66, 'Akasema: Basi nyinyi mnawaabudu asiye kuwa Mwenyezi Mungu wasio kufaini kitu wala kukudhuruni?'),
(2550, 21, 67, 'Aibu yenu nyinyi na hivyo mnavyo viabudu badala ya Mwenyezi Mungu! Basi nyinyi hamtii akilini?'),
(2551, 21, 68, 'Wakasema: Mchomeni moto, na muinusuru miungu yenu, ikiwa nyinyi ni watendao jambo!'),
(2552, 21, 69, 'Sisi tukasema: Ewe moto! Kuwa baridi na salama kwa Ibrahim!'),
(2553, 21, 70, 'Wao walimkusudia maovu, lakini Sisi tukawafanya wao ndio walio khasiri.'),
(2554, 21, 71, 'Na tukamwokoa yeye na Luut\'i tukawapeleka kwenye nchi tulio ibariki kwa ajili ya walimwengu wote.'),
(2555, 21, 72, 'Naye tukampa Is-haq, na Yaaqub kuwa ni ziada. Na wote tukawajaalia wawe watu wema.'),
(2556, 21, 73, 'Na tukawafanya maimamu wakiongoa watu kwa amri yetu. Na tukawafunulia watende kheri, na washike Sala, na watoe Zaka; na walikuwa wanatuabudu Sisi tu.'),
(2557, 21, 74, 'Na Luut\'i tukampa hukumu na ilimu na tukamwokoa na ule mji ulio kuwa ukifanya maovu. Hakika hao walikuwa watu wabaya, wachafu.'),
(2558, 21, 75, 'Na tukamuingiza katika rehema yetu. Hakika yeye ni miongoni mwa watenda mema.'),
(2559, 21, 76, 'Na Nuhu alipo ita zamani, nasi tukamuitikia, na tukamwokoa yeye na watu wake kutokana na shida kubwa.'),
(2560, 21, 77, 'Na tukamnusuru na watu walio zikanusha Ishara zetu. Hakika hao walikuwa watu wabaya. Basi tukawazamisha wote.'),
(2561, 21, 78, 'Na Daud na Suleiman walipo kata hukumu katika kadhiya ya konde walipo lisha humo mbuzi wa watu usiku. Na Sisi tulikuwa wenye kushuhudia hukumu yao hiyo.'),
(2562, 21, 79, 'Tukamfahamisha Suleiman. Na kila mmoja wao tulimpa hukumu na ilimu. Na tuliifanya milima na ndege kuwa pamoja na Daud imnyenyekee na imtakase Mwenyezi Mungu. Na Sisi ndio tulio fanya hayo.'),
(2563, 21, 80, 'Na tukamfundisha kuunda mavazi ya vita kwa ajili yenu ili yakuhifadhini katika kupigana kwenu. Je! Mtakuwa wenye kushukuru?'),
(2564, 21, 81, 'Na tukamsahilishia Suleiman upepo wa kimbunga wendao kwa amri yake kwenye ardhi tuliyo ibarikia. Na Sisi ndio tunao jua kila kitu.'),
(2565, 21, 82, 'Na pia mashet\'ani wanao mpigia mbizi na kufanya kazi nyenginezo. Na Sisi tulikuwa walinzi wao.'),
(2566, 21, 83, 'Na Ayyubu, alipo mwita Mola wake Mlezi, akasema: Mimi yamenipata madhara. Na Wewe ndiye unaye rehemu kuliko wote wanao rehemu.'),
(2567, 21, 84, 'Basi tukamwitikia, na tukamwondolea madhara aliyo kuwa nayo, na tukampa watu wake na mfano wao pamoja nao kuwa ni rehema inayo toka kwetu, na ukumbusho kwa wafanyao ibada.'),
(2568, 21, 85, 'Na Ismail, na Idris, na Dhulkifli - wote walikuwa miongoni mwa wanao subiri.'),
(2569, 21, 86, 'Na tukawaingiza katika rehema yetu. Hakika wao walikuwa katika watu wema.'),
(2570, 21, 87, 'Na Dhun-Nun alipoondoka hali ameghadhibika, na akadhani ya kwamba hatutakuwa na uwezo juu yake. Basi aliita katika giza: Hapana mungu isipo kuwa Wewe Subhanaka Uliye takasika. Hakika mimi nilikuwa miongoni mwa wenye kudhulumu.'),
(2571, 21, 88, 'Basi tukamwitikia na tukamwokoa kutokana na dhiki. Na hivyo ndivyo tunavyo waokoa Waumini.'),
(2572, 21, 89, 'Na Zakariya alipo mwita Mola wake Mlezi: Mola wangu Mlezi! Usiniache peke yangu, na Wewe ndiye Mbora wa wanao rithi.'),
(2573, 21, 90, 'Basi tukamwitikia, na tukampa Yahya na tukamponyeshea mkewe. Hakika wao walikuwa wepesi wa kutenda mema, na wakituomba kwa shauku na khofu. Nao walikuwa wakitunyenyekea.'),
(2574, 21, 91, 'Na mwanamke aliye linda uke wake, na tukampulizia katika roho yetu, na tukamfanya yeye na mwanawe kuwa ni Ishara kwa walimwengu.'),
(2575, 21, 92, 'Kwa hakika huu umma wenu ni umma mmoja, na Mimi ni Mola wenu Mlezi. Kwa hivyo niabuduni Mimi.'),
(2576, 21, 93, 'Nao wakalikata jambo lao hili mapande mapande baina yao. Wote watarudi kwetu.'),
(2577, 21, 94, 'Na atendaye mema naye ni Muumini basi haitakataliwa juhudi yake. Na hakika Sisi tunamwandikia.'),
(2578, 21, 95, 'Na haiwi kwa wana-mji tulio uangamiza, ya kwamba hawatarejea,'),
(2579, 21, 96, 'Mpaka watapo funguliwa Juju na Maajuju wakawa wanateremka kutoka kila mlima;'),
(2580, 21, 97, 'Na miadi ya haki ikakaribia. Hapo ndipo yatapo kodoka macho ya walio kufuru (na watasema:) Ole wetu! Bila ya shaka tulikuwa tumeghafilika na haya, bali tulikuwa wenye kudhulumu.'),
(2581, 21, 98, 'Hakika nyinyi na hao mnao waabudu badala ya Mwenyezi Mungu ni kuni za Jahannamu; huko mtaingia tu.'),
(2582, 21, 99, 'Lau kuwa hawa ni miungu, wasingeli ingia. Na wote watadumu humo.'),
(2583, 21, 100, 'Lao wao humo ni kupiga mayowe, na wala hawatasikia (jenginelo).'),
(2584, 21, 101, 'Ama wale ambao wema wetu umewatangulia, hao watatenganishwa na hayo.'),
(2585, 21, 102, 'Hawatausikia mvumo wake, na wao watadumu katika yale zinayo yatamani nafsi zao.'),
(2586, 21, 103, 'Hicho Kitisho Kikubwa hakito wahuzunisha. Na Malaika watawapokea (kwa kuwaambia): Hii ndiyo Siku yenu mliyo kuwa mkiahidiwa!'),
(2587, 21, 104, 'Siku tutakapo zikunja mbingu kama mkunjo wa karatasi za vitabu. Kama tulivyo anza umbo la mwanzo tutalirudisha tena. Ni ahadi iliyo juu yetu. Hakika Sisi ni watendao.'),
(2588, 21, 105, 'Na hakika tulikwisha andika katika Zaburi baada ya Kumbukumbu, ya kwamba nchi watairithi waja wangu walio wema.'),
(2589, 21, 106, 'Hakika katika haya yapo mawaidha kwa watu wafanyao ibada.'),
(2590, 21, 107, 'Nasi hatukukutuma ila uwe ni Rehema kwa walimwengu wote.'),
(2591, 21, 108, 'Sema: Hakika imefunuliwa kwangu ya kwamba hakika Mungu wenu ni Mungu Mmoja tu. Je! Mmesilimu?'),
(2592, 21, 109, 'Na kama wakigeuka, basi sema: Nimekutangazieni sawa sawa, wala sijui yako karibu au mbali hayo mliyo ahidiwa.'),
(2593, 21, 110, 'Hakika Yeye anajua kauli ya dhaahiri na anajua myafichayo.'),
(2594, 21, 111, 'Wala sijui pengine huu ni mtihani tu kwenu na starehe mpaka muda kidogo.'),
(2595, 21, 112, 'Sema: Mola wangu Mlezi! Hukumu kwa haki. Na Mola wetu Mlezi ni Mwingi wa Rehema, aombwaye msaada juu ya mnayo yazua.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Enyi watu! Mcheni Mola wenu Mlezi. Hakika tetemeko la Saa (ya Kiyama) ni jambo kuu.'),
(2597, 22, 2, 'Siku mtapo iona, kila mwenye kunyonyesha atamsahau amnyonyeshaye, na kila mwenye mimba ataharibu mimba yake. Na utaona watu wamelewa, na kumbe hawakulewa. Lakini adhabu ya Mwenyezi Mungu ni kali.'),
(2598, 22, 3, 'Na miongoni mwa watu wapo wanao bishana juu ya Mwenyezi Mungu bila ya ilimu, na wanamfuata kila shet\'ani aliye asi.'),
(2599, 22, 4, 'Ameandikiwa kwamba anaye mfanya kuwa rafiki, basi huyo hakika atampoteza na atamwongoza kwenye adhabu ya Moto mkali.'),
(2600, 22, 5, 'Enyi watu! Ikiwa nyinyi mna shaka ya kufufuliwa basi kwa hakika Sisi tulikuumbeni kutokana na udongo, kisha kutokana na manii, kisha kutokana na kipande cha damu ilio gandana, kisha kutokana na kipande cha nyama chenye umbo na kisicho kuwa na umbo, ili tukubainishieni. Nasi tunakiweka katika matumbo ya uzazi tunacho kitaka mpaka muda ulio wekwa. Kisha tunakutoeni kwa hali ya mtoto mchanga, kisha mfikie kutimia akili. Na wapo katika nyinyi wanao kufa, na wapo katika nyinyi wanao rudishwa kwenye umri wa unyonge kabisa, hata mtu awe hajui kitu baada ya kuwa anakijua. Na unaiona ardhi imetulia kimya, lakini tunapo yateremsha maji juu yake husisimka na kututumka, na kumea kila namna ya mimea mizuri.'),
(2601, 22, 6, 'Hayo ni kwa sababu hakika Mwenyezi Mungu ndiye Haki, na kwamba hakika Yeye ndiye mwenye kuhuisha wafu, na kwamba hakika Yeye ni Muweza wa kila kitu.'),
(2602, 22, 7, 'Na kwamba hakika Saa itakuja hapana shaka kwa hilo, na kwamba hakika Mwenyezi Mungu atawafufua walio makaburini.'),
(2603, 22, 8, 'Na katika watu wapo wanao bishana juu ya Mwenyezi Mungu bila ya ilimu, wala uwongofu, wala Kitabu chenye nuru.'),
(2604, 22, 9, 'Anaye geuza shingo yake ili kupoteza watu waache Njia ya Mwenyezi Mungu. Duniani atapata hizaya, na Siku ya Kiyama tutamwonjesha adhabu ya kuungua.'),
(2605, 22, 10, '(Ataambiwa): Hayo ni kwa sababu ya iliyo tanguliza mikono yako. Na hakika Mwenyezi Mungu si dhaalimu kwa waja.'),
(2606, 22, 11, 'Na katika watu wapo wanao muabudu Mwenyezi Mungu kwa ukingoni. Ikimfikia kheri hutulia kwayo, na ukimfikia msukosuko hugeuza uso wake. Amekhasiri dunia na Akhera; hiyo ndiyo khasara iliyo wazi.'),
(2607, 22, 12, 'Badala ya Mwenyezi Mungu, yeye huomba kisicho mdhuru wala kumnufaisha. Huko ndiko kupotolea mbali!'),
(2608, 22, 13, 'Humuomba yule ambaye bila ya shaka dhara yake ipo karibu zaidi kuliko nafuu yake. Kwa hakika huyo ni mlinzi muovu, na rafiki muovu.'),
(2609, 22, 14, 'Hakika Mwenyezi Mungu atawaingiza walio amini na wakatenda mema katika Bustani zipitazo mito kati yake. Hakika Mwenyezi Mungu hutenda atakayo.'),
(2610, 22, 15, 'Anaye dhani kwamba Mwenyezi Mungu hatamnusuru (Mtume) katika dunia na Akhera na afunge kamba kwenye dari kisha aikate. Atazame je hila yake itaondoa hayo yaliyo mkasirisha?'),
(2611, 22, 16, 'Na namna hivi tumeiteremsha (Qur\'ani) kuwa ni Aya zilizo wazi, na hakika Mwenyezi Mungu humwongoa amtakaye.'),
(2612, 22, 17, 'Hakika walio amini, na ambao ni Mayahudi na Wasabai na Wakristo na Majusi na wale walio shiriki - hakika Mwenyezi Mungu atawapambanua baina yao Siku ya Kiyama. Hakika Mwenyezi Mungu ni Shahidi wa kila kitu.'),
(2613, 22, 18, 'Je! Huoni kwamba vinamsujudia Mwenyezi Mungu viliomo mbinguni na viliomo katika ardhi, na jua, na mwezi, na nyota, na milima, na miti, na wanyama, na wengi miongoni mwa watu. Na wengi imewastahiki adhabu. Na anaye fedheheshwa na Mwenyezi Mungu hana wa kumhishimu. Hakika Mwenyezi Mungu hutenda apendayo.'),
(2614, 22, 19, 'Hawa wagomvi wawili walio gombana kwa ajili ya Mola wao Mlezi. Basi walio kufuru watakatiwa nguo za moto, na yatamiminwa juu ya vichwa vyao maji yanayo chemka.'),
(2615, 22, 20, 'Kwa maji hayo vitayayushwa viliomo matumboni mwao, na ngozi zao pia.'),
(2616, 22, 21, 'Na kwa ajili yao yatakuwapo marungu ya chuma.'),
(2617, 22, 22, 'Kila wakitaka kutoka humo kwa sababu ya uchungu watarudishwa humo humo, na wataambiwa: Onjeni adhabu ya kuungua!'),
(2618, 22, 23, 'Hakika Mwenyezi Mungu atawaingiza walio amini na wakatenda mema katika Bustani zipitiwazo na mito kati yake. Watapambwa humo kwa mapambo ya mikononi ya dhahabu na lulu. Na mavazi yao humo ni hariri.'),
(2619, 22, 24, 'Na wataongozwa kwenye maneno mazuri, na wataongozwa kwenye Njia ya Msifiwa.'),
(2620, 22, 25, 'Hakika wale walio kufuru na wakazuilia Njia ya Mwenyezi Mungu na Msikiti Mtakatifu, ambao tumeufanya kwa ajili ya watu wote sawasawa, kwa wakaao humo na wageni. Na kila atakaye taka kufanya upotofu humo kwa dhulma, basi tutamwonjesha adhabu chungu.'),
(2621, 22, 26, 'Na pale tulipomweka Ibrahim pahala penye ile Nyumba tukamwambia: Usinishirikishe na chochote; na isafishe Nyumba yangu kwa ajili ya wanao izunguka kwa kut\'ufu, na wanao kaa hapo kwa ibada, na wanao rukuu, na wanao sujudu.'),
(2622, 22, 27, 'Na watangazie watu Hija; watakujia kwa miguu na juu ya kila ngamia aliye konda, wakija kutoka katika kila njia ya mbali.'),
(2623, 22, 28, 'Ili washuhudie manufaa yao na walitaje jina la Mwenyezi Mungu katika siku maalumu juu ya nyama hoa alio waruzuku. Basi kuleni katika hao na mlisheni mwenye shida aliye fakiri.'),
(2624, 22, 29, 'Kisha wajisafishe taka zao, na waondoe nadhiri zao, na waizunguke Nyumba ya Kale.'),
(2625, 22, 30, 'Ndio hivyo iwe! Na anaye vitukuza vitakatifu vya Mwenyezi Mungu basi hayo ndiyo kheri yake mbele ya Mola wake Mlezi. Na mmehalalishiwa nyama hoa ila wale mlio somewa. Basi jiepusheni na uchafu wa masanamu, na jiepusheni na usemi wa uwongo.'),
(2626, 22, 31, 'Kwa kumtakasikia Imani Mwenyezi Mungu, bila ya kumshirikisha. Na anaye mshirikisha Mwenyezi Mungu ni kama kwamba ameporomoka kutoka mbinguni, kisha ndege wakamnyakua au upepo ukamtupa pahala mbali.'),
(2627, 22, 32, 'Ndio hivyo! Na anayetukuza ibada za Mwenyezi Mungu, basi hayo ni katika unyenyekevu wa nyoyo.'),
(2628, 22, 33, 'Katika hao nyinyi mna manufaa mpaka muda maalumu. Kisha pahala pa kuchinjiwa kwake ni kwenye Nyumba ya Kale.'),
(2629, 22, 34, 'Na kila umma tumewafanyia mahala pa kuchinjia mihanga ya ibada ili walitaje jina la Mwenyezi Mungu juu ya vile walivyo ruzukiwa katika wanyama wa mifugo. Basi Mungu wenu ni Mungu Mmoja tu. Jisalimishieni kwake tu, na wabashirie wanyenyekevu,'),
(2630, 22, 35, 'Ambao, anapo tajwa Mwenyezi Mungu nyoyo zao hutetemeka, na wanao vumilia kwa yanao wasibu, na wanao shika Sala, na wanatoa katika tulivyo waruzuku.'),
(2631, 22, 36, 'Na ngamia wa sadaka tumekufanyieni kuwa ni kudhihirisha matukuzo kwa Mwenyezi Mungu; kwa hao mna kheri nyingi. Basi litajeni jina la Mwenyezi Mungu juu yao wanapo simama kwa safu. Na waangukapo ubavu kuleni katika hao na walisheni walio kinai na wanao lazimika kuomba. Ndio kama hivi tumewafanya hawa wanyama dhalili kwenu ili mpate kushukuru.'),
(2632, 22, 37, 'Nyama zao hazimfikii Mwenyezi Mungu wala damu zao, lakini unamfikia uchamngu wenu. Namna hivi tumewadhalilisha kwenu ili mumtukuze Mwenyezi Mungu kwa alivyo kuongoeni. Na wabashirie wafanyao mema.'),
(2633, 22, 38, 'Hakika Mwenyezi Mungu huwakinga walio amini. Hakika Mwenyezi Mungu hampendi kila khaini mwingi wa kukanya mema.'),
(2634, 22, 39, 'Wameruhusiwa kupigana wale wanao pigwa vita kwa sababu wamedhulumiwa - na kwa yakini Mwenyezi Mungu ni Muweza wa kuwasaidia -'),
(2635, 22, 40, 'Wale ambao wametolewa majumbani mwao pasipo haki, ila kwa kuwa wanasema: Mola wetu Mlezi ni Mwenyezi Mungu! Na lau kuwa Mwenyezi Mungu hawakingi watu kwa watu, basi hapana shaka zingeli vunjwa nymba za wat\'awa, na makanisa, na masinagogi, na misikiti, ambamo ndani yake jina la Mwenyezi Mungu linatajwa kwa wingi. Na bila ya shaka Mwenyezi Mungu humsaidia yule anaye msaidia Yeye. Hakika Mwenyezi Mungu ni Mwenye nguvu Mtukufu.'),
(2636, 22, 41, 'Wale ambao tukiwapa madaraka katika nchi husimamisha Sala, na wakatoa Zaka, na wakaamrisha mema, na wakakataza mabaya. Na kwa Mwenyezi Mungu ndio marejeo ya mambo yote.'),
(2637, 22, 42, 'Na wakikukanusha basi walikwisha kanusha kabla yao watu wa Nuhu, na kina A\'ad na kina Thamud,'),
(2638, 22, 43, 'Na watu wa Ibrahim na watu wa Lut\'i'),
(2639, 22, 44, 'Na watu wa Madiana. Na Musa pia alikanushwa. Nikawapa muda makafiri, kisha nikawatia mkononi. Basi ilikuwaje adhabu yangu!'),
(2640, 22, 45, 'Miji mingapi tuliiangamiza iliyo kuwa ikidhulumu, ikawa imebaki magofu, na visima vilivyo achwa, na majumba yaliyo kuwa madhubuti?'),
(2641, 22, 46, 'Je! Hawatembei katika ardhi ili wapate kuwa na nyoyo zao za kuzingatia, au masikio ya kusikilia? Kwani hakika si macho yanayo pofuka, lakini zinazo pofuka ni nyoyo ziliomo vifuani.'),
(2642, 22, 47, 'Na wanakuhimiza ulete adhabu, lakini Mwenyezi Mungu hatakwenda kinyume na ahadi yake kabisa. Na hakika siku moja kwa Mola wako Mlezi ni kama miaka elfu mnavyo hisabu nyinyi.'),
(2643, 22, 48, 'Na miji mingapi niliipa muda na hali ilikuwa imedhulumu, na kisha nikaitia mkononi? Na kwangu Mimi ndio marudio yote.'),
(2644, 22, 49, 'Sema: Enyi watu! Hakika mimi ni mwonyaji kwenu niliye dhaahiri.'),
(2645, 22, 50, 'Basi walio amini na wakatenda mema watapata maghfira na riziki za ukarimu.'),
(2646, 22, 51, 'Na wanao jitahidi kuzipinga Aya zetu hao ndio watu wa Motoni.'),
(2647, 22, 52, 'Na hatukumtuma kabla yako Mtume wala Nabii ila anapo soma, Shet\'ani hutumbukiza katika masomo yake. Lakini Mwenyezi Mungu huyaondoa anayo yatia Shet\'ani; kisha Mwenyezi Mungu huzithibitisha Aya zake. Na Mwenyezi Mungu ni Mjuzi Mwenye hikima.'),
(2648, 22, 53, 'Hayo ni ili alifanye lile analo litia Shet\'ani liwe ni fitna kwa wale wenye maradhi ndani ya nyoyo zao, na wale ambao nyoyo zao ni ngumu. Na hakika madhaalimu wamo katika mfarakano wa mbali.'),
(2649, 22, 54, 'Na ili walio pewa ilimu wajue kuwa hayo ni Haki itokayo kwa Mola wako Mlezi na waamini, na zipate kutua nyoyo zao. Na hakika Mwenyezi Mungu ndiye anaye waongoa wenye kuamini kwenye Njia Iliyo Nyooka.'),
(2650, 22, 55, 'Na walio kufuru hawatoacha kuwa katika wasiwasi kwa hilo mpaka Saa iwafikie ghafla, au iwafikie adhabu ya Siku Tasa.'),
(2651, 22, 56, 'Ufalme wote siku hiyo utakuwa wa Mwenyezi Mungu. Atahukumu baina yao. Basi walio amini na wakatenda mema watakuwa katika Bustani zenye neema.'),
(2652, 22, 57, 'Na walio kufuru na kuzikanusha Ishara zetu, basi hao watapata adhabu ya kufedhehesha.'),
(2653, 22, 58, 'Na walio hama kwa ajili ya Mwenyezi Mungu, kisha wakauwawa au wakafa, bila ya shaka Mwenyezi Mungu atawaruzuku riziki njema. Na hakika Mwenyezi Mungu ni Mbora wa wanao ruzuku.'),
(2654, 22, 59, 'BIla ya shaka atawaingiza pahala watakapo paridhia. Na hakika Mwenyezi Mungu ni Mjuzi na Mpole.'),
(2655, 22, 60, 'Ndio namna hivi. Na anaye lipiza mfano wa alivyo adhibiwa, kisha akadhulumiwa, basi hapana shaka Mwenyezi Mungu atamsaidia. Hakika Mwenyezi Mungu ni Msamehevu Mwenye maghfira.'),
(2656, 22, 61, 'Hayo ni kwa kuwa Mwenyezi Mungu huingiza usiku katika mchana, na huuingiza mchana katika usiku, na ya kwamba Mwenyezi Mungu ni Mwenye kusikia Mwenye kuona.'),
(2657, 22, 62, 'Hayo ni kwa kuwa Mwenyezi Mungu ndiye Wa Kweli, na kwamba wale wanao waomba badala yake, ni baat\'ili, na hakika Mwenyezi Mungu ndiye Aliye Juu na ndiye Mkubwa.'),
(2658, 22, 63, 'Je! Huoni kwamba Mwenyezi Mungu huteremsha maji kutoka mbinguni, na mara ardhi inakuwa chanikiwiti? Hakika Mwenyezi Mungu ni Mpole na Mwenye kujua.'),
(2659, 22, 64, 'Ni vyake viliomo mbinguni na viliomo katika ardhi. Na hakika Mwenyezi Mungu ni Mwenye kujitosha na Mwenye kusifiwa.'),
(2660, 22, 65, 'Je! Huoni kwamba Mwenyezi Mungu amevidhalilisha kwenu viliomo katika ardhi, na marikebu zipitazo baharini kwa amri yake, na amezishika mbingu zisianguke juu ya ardhi ila kwa idhini yake. Hakika Mwenyezi Mungu ni Mwenye huruma kwa watu, Mwenye kurehemu.'),
(2661, 22, 66, 'Na Yeye ndiye aliye kuhuisheni kisha akakufisheni, na kisha atakufufueni. Hakika mwanaadamu hana fadhila.'),
(2662, 22, 67, 'Kila umma tumewajaalia na ibada zao wanazo zishika. Basi wasizozane nawe katika jambo hili. Na waite watu kwendea kwa Mola wako Mlezi. Hakika wewe uko kwenye Uwongofu Ulio Nyooka.'),
(2663, 22, 68, 'Na wakikujadili basi sema: Mwenyezi Mungu anajua zaidi mnayo yatenda.'),
(2664, 22, 69, 'Mwenyezi Mungu atahukumu baina yenu Siku ya Kiyama katika hayo mnayo khitalifiana.'),
(2665, 22, 70, 'Je! Hujui kwamba Mwenyezi Mungu anayajua yaliomo mbinguni na katika ardhi? Hakika hayo yamo Kitabuni. Hakika hayo kwa Mwenyezi Mungu ni mepesi.'),
(2666, 22, 71, 'Na wanaabudu badala ya Mwenyezi Mungu ambavyo hakuviteremshia ushahidi, na ambavyo hawana ujuzi navyo. Na madhaalimu hawatakuwa na wa kuwasaidia.'),
(2667, 22, 72, 'Na wanapo somewa Aya zetu zilizo wazi utaona chuki katika nyuso za walio kufuru. Hukaribia kuwavamia wale wanao wasomea hizo Aya zetu. Sema: Je! Nikwambieni yaliyo mabaya zaidi kuliko haya? Ni Moto. Mwenyezi Mungu amewaahidi walio kufuru, na ni marudio mabaya hayo.'),
(2668, 22, 73, 'Enyi watu! Unapigwa mfano, basi usikilizeni. Hakika wale mnao waomba badala ya Mwenyezi Mungu, hawatoumba hata nzi ijapo kuwa watajumuika kwa hilo. Na nzi akiwapokonya kitu hawawezi kukipata kwake. Amedhoofika kweli huyo mwenye kutaka na mwenye kutakiwa.'),
(2669, 22, 74, 'Hawakumuadhimisha Mwenyezi Mungu anavyo stahiki kuadhimishwa. Hakika Mwenyezi Mungu bila ya shaka ni Mwenye kushinda.'),
(2670, 22, 75, 'Mwenyezi Mungu huteuwa Wajumbe miongoni mwa Malaika na miongoni mwa watu. Hakika Mwenyezi Mungu ni Mwenye kusikia, Mwenye kuona.'),
(2671, 22, 76, 'Anayajua yaliyo mbele yao na yaliyo nyuma yao. Na kwa Mwenyezi Mungu yatarejeshwa mambo yote.'),
(2672, 22, 77, 'Enyi mlio amini! Rukuuni na msujudu, na muabuduni Mola wenu Mlezi, na tendeni mema, ili mfanikiwe.'),
(2673, 22, 78, 'Na fanyeni juhudi kwa ajili ya Mwenyezi Mungu kama inavyo stahiki jihadi yake. Yeye amekuteueni. Wala hakuweka juu yenu mambo mazito katika Dini. Nayo ni mila ya baba yenu Ibrahim. Yeye (Mwenyezi Mungu) alikuiteni Waislamu tangu zamani, na katika hii (Qur\'ani) pia, ili awe Mtume shahidi juu yenu, na nyinyi muwe mashahidi kwa watu. Basi shikeni Sala na toeni Zaka na shikamaneni na Mwenyezi Mungu. Yeye ndiye Mlinzi wenu, Mlinzi bora kabisa, na Msaidizi bora kabisa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'HAKIKA wamefanikiwa Waumini,'),
(2675, 23, 2, 'Ambao ni wanyenyekevu katika Sala zao,'),
(2676, 23, 3, 'Na ambao hujiepusha na mambo ya upuuzi,'),
(2677, 23, 4, 'Na ambao wanatoa Zaka,'),
(2678, 23, 5, 'Na ambao wanazilinda tupu zao,'),
(2679, 23, 6, 'Isipo kuwa kwa wake zao au kwa iliyo wamiliki mikono yao ya kulia. Kwani hao si wenye kulaumiwa.'),
(2680, 23, 7, 'Lakini anaye taka kinyume cha haya, basi hao ndio warukao mipaka.'),
(2681, 23, 8, 'Na ambao wanazitimiza amana zao na ahadi zao,'),
(2682, 23, 9, 'Na ambao Sala zao wanazihifadhi -'),
(2683, 23, 10, 'Hao ndio warithi,'),
(2684, 23, 11, 'Ambao watairithi Pepo ya Firdausi, wadumu humo.'),
(2685, 23, 12, 'Na kwa yakini tumemuumba mtu kutokana na asli ya udongo.'),
(2686, 23, 13, 'Kisha tukamjaalia awe tone ya mbegu ya uzazi katika kalio madhubuti.'),
(2687, 23, 14, 'Kisha tukaiumba tone kuwa damu iliyo ganda, na tukaiumba damu kuwa pande la nyama, kisha tukaliumba pande la nyama kuwa mifupa, na mifupa tukaivika nyama. Kisha tukamfanya kiumbe mwengine. Basi ametukuka Mwenyezi Mungu Mbora wa waumbaji.'),
(2688, 23, 15, 'Kisha hakika nyinyi baada ya hayo mtakufa.'),
(2689, 23, 16, 'Kisha hakika nyinyi Siku ya Kiyama mtafufuliwa.'),
(2690, 23, 17, 'Na kwa yakini tumeziumba juu yenu njia saba. Nasi hatukuwa wenye kughafilika na viumbe.'),
(2691, 23, 18, 'Na tumeteremsha kutoka mbinguni maji kwa kiasi na tukayatuliza katika ardhi. Na hakika Sisi tunaweza kuyaondoa.'),
(2692, 23, 19, 'Kwa maji hayo tukakufanyieni bustani za mitende na mizabibu, mnapata humo matunda mengi, na katika hayo mnakula;'),
(2693, 23, 20, 'Na mti utokao katika mlima wa Sinai, unatoa mafuta na kuwa kitoweo kwa walao.'),
(2694, 23, 21, 'Na hakika mnayo mazingatio makubwa katika nyama hoa - tunakunywesheni katika vile viliomo matumboni mwao, na nyinyi mnapata katika hao manufaa mengi, na pia mnawala.'),
(2695, 23, 22, 'Na juu yao na juu ya marikebu mnabebwa.'),
(2696, 23, 23, 'Na hakika tulimtuma Nuhu kwa watu wake, akasema: Enyi watu wangu! Muabuduni Mwenyezi Mungu. Nyinyi hamna mungu asiye kuwa Yeye. Basi je! Hamwogopi?'),
(2697, 23, 24, 'Walisema wale wakuu walio kufuru katika watu wake: Huyu si chochote ila ni mtu tu kama nyinyi. Anataka kujipa ubora juu yenu. Na lau kuwa Mwenyezi Mungu amependa basi yakini angeli teremsha Malaika. Hatukusikia haya kwa baba zetu wa zamani.'),
(2698, 23, 25, 'Huyu si lolote ila ni mtu mwenye wazimu. Basi mngojeeni tu kwa muda.'),
(2699, 23, 26, 'Akasema (Nuhu): Mola wangu Mlezi! Ninusuru kwa kuwa wamenikanusha.'),
(2700, 23, 27, 'Tukampa wahyi: Unda jahazi mbele ya macho yetu na uwongozi wetu! Basi itapo fika amri yetu na ikafurika tanuri, hapo waingize ndani yake wa kila namna dume na jike, na ahali zako, isipo kuwa yule ambaye katika wao juu yake imekwisha tangulia kauli. Wala usinitajie hao walio dhulumu. Kwani hao bila ya shaka watazamishwa.'),
(2701, 23, 28, 'Basi utakapo tulia wewe na walio pamoja nawe humo marikebuni, sema: Alhamdulillahi Sifa zote njema ni za Mwenyezi Mungu, aliye tuokoa na watu madhaalimu!'),
(2702, 23, 29, 'Na sema: Mola wangu Mlezi! Niteremshe mteremsho wenye baraka, na Wewe ni Mbora wa wateremshaji.'),
(2703, 23, 30, 'Hakika katika hayo yapo mazingatio. Na kwa yakini Sisi ni wenye kuwafanyia mtihani.'),
(2704, 23, 31, 'Kisha baada yao tukaanzisha kizazi kingine.'),
(2705, 23, 32, 'Tukawapelekea Mtume miongoni mwao, kuwaambia: Muabuduni Mwenyezi Mungu! Hamna mungu asiye kuwa Yeye. Jee, hamwogopi?'),
(2706, 23, 33, 'Na wakubwa katika watu wake walio kufuru na wakakanusha mkutano wa Akhera, na tukawadekeza kwa starehe katika maisha ya dunia, walisema: Huyu si chochote ila ni binaadamu kama nyinyi; anakula mlacho, na anakunywa mnywacho.'),
(2707, 23, 34, 'Na nyinyi mkimt\'ii mtu kama nyinyi basi hakika mtakuwa khasarani.'),
(2708, 23, 35, 'Hivyo anakuahidini ati ya kwamba mtakapo kufa na mkawa udongo na mifupa kuwa mtatolewa?'),
(2709, 23, 36, 'Hayawi! Hayawi hayo mnayo ahidiwa.'),
(2710, 23, 37, 'Hapana ila uhai wetu wa duniani tu, tunakufa na kuishi basi. Wala sisi hatutafufuliwa.'),
(2711, 23, 38, 'Huyu si lolote ila ni mtu anaye mzulia Mwenyezi Mungu uwongo, wala sisi sio wa kumuamini.'),
(2712, 23, 39, 'Akasema: Mola wangu Mlezi! Ninusuru kwa sababu wananikanusha.'),
(2713, 23, 40, '(Mwenyezi Mungu) akasema: Baada ya muda mchache watakuwa wenye kujuta.'),
(2714, 23, 41, 'Basi ukawanyakua ukelele kwa haki, na tukawafanya kama takataka zinazo elea juu ya maji. Ikapotelea mbali kaumu ya madhaalimu!'),
(2715, 23, 42, 'Kisha tukaleta baada yao vizazi vingine.'),
(2716, 23, 43, 'Hapana umma uwezao kutanguliza ajali yake wala kuikawiza.'),
(2717, 23, 44, 'Kisha tukawatuma Mitume wetu, mmoja baada ya mmoja. Kila umma alipo wafikia Mtume wao walimkanusha. Tukawafuatanisha hawa baada ya hawa, na tukawafanya ni hadithi za kusimuliwa. Wakapotelea mbali watu wasio amini.'),
(2718, 23, 45, 'Kisha tukamtuma Musa na nduguye, Haaruni, pamoja na ishara zetu na hoja zilizo wazi.'),
(2719, 23, 46, 'Kumwendea Firauni na wakuu wake. Lakini walijivuna, nao walikuwa watu majeuri.'),
(2720, 23, 47, 'Wakasema: Je, tuwaamini wanaadamu wawili hawa kama sisi, na ambao watu wao ni watumwa wetu?'),
(2721, 23, 48, 'Basi wakawakanusha, na wakawa miongoni mwa walio angamizwa.'),
(2722, 23, 49, 'Na hakika tulimpa Musa Kitabu ili wapate kuongoka.'),
(2723, 23, 50, 'Na tukamfanya mwana wa Mariamu na mama yake kuwa ni Ishara, na tukawapa makimbilio kwenda mahali palipo inuka penye utulivu na chemchem za maji.'),
(2724, 23, 51, 'Enyi Mitume! Kuleni vyakula vizuri na tendeni mema. Hakika Mimi ni Mjuzi wa mnayo yatenda.'),
(2725, 23, 52, 'Na kwa yakini huu Umma wenu ni Umma mmoja, na Mimi ni Mola wenu Mlezi, basi nicheni Mimi.'),
(2726, 23, 53, 'Lakini walikatiana jambo lao mapande mbali mbali. Kila kundi likifurahia kwa waliyo nayo.'),
(2727, 23, 54, 'Basi waache katika ghafla yao kwa muda.'),
(2728, 23, 55, 'Je! Wanadhani ya kuwa kwa vile tunavyo wapa mali na watoto'),
(2729, 23, 56, 'Ndio tunawahimizia kheri? Lakini wenyewe hawatambui.'),
(2730, 23, 57, 'Kwa hakika hao ambao kwa kumwogopa Mola wao Mlezi wananyenyekea,'),
(2731, 23, 58, 'Na wale ambao Ishara za Mola wao Mlezi wanaziamini,'),
(2732, 23, 59, 'Na wale ambao Mola wao Mlezi hawamshirikishi,'),
(2733, 23, 60, 'Na wale ambao wanatoa walicho pewa, na hali nyoyo zao zinagopa kwa kuwa watarejea kwa Mola wao Mlezi,'),
(2734, 23, 61, 'Basi wote hao ndio wanao kimbilia katika mambo ya kheri, na ndio watakao tangulia kuyafikia.'),
(2735, 23, 62, 'Na hatuilazimishi nafsi ila kwa kadri ya uwezo wake. Na tunacho kitabu kisemacho kweli. Nao hawatadhulumiwa.'),
(2736, 23, 63, 'Lakini nyoyo zao zimeghafilika na hayo, na wanavyo vitendo vinginevyo wanavyo vifanya.'),
(2737, 23, 64, 'Hata tutakapo watia katika adhabu wale walio dekezwa kwa starehe kati yao, hapo ndipo watapo yayatika.'),
(2738, 23, 65, 'Msiyayatike leo! Hakika nyinyi hamtanusurika nasi.'),
(2739, 23, 66, 'Hakika zilikuwa Aya zangu mkisomewa, nanyi mkirudi nyuma juu ya visigino vyenu,'),
(2740, 23, 67, 'Na huku mkitakabari na usiku mkiizungumza (Qur\'ani) kwa dharau.'),
(2741, 23, 68, 'Je, hawakuifikiri kauli, au yamewajia yasiyo wafikia baba zao wa zamani?'),
(2742, 23, 69, 'Au hawakumjua Mtume wao, ndio maana wanamkataa?'),
(2743, 23, 70, 'Au wanasema: Ana wazimu? Bali amewajia kwa Haki, na wengi wao wanaichukia Haki.'),
(2744, 23, 71, 'Na lau kuwa Haki ingeli fuata matamanio yao, basi zingeli haribika mbingu na ardhi na waliomo ndani. Bali tumewaletea ukumbusho wao, nao wanajitenga mbali na ukumbusho wao.'),
(2745, 23, 72, 'Au unawaomba ujira? Lakini ujira wa Mola wako Mlezi ni bora, na Yeye ndiye Mbora wa wanao ruzuku.'),
(2746, 23, 73, 'Na kwa yakini wewe unawaita kwenye Njia Iliyo Nyooka.'),
(2747, 23, 74, 'Na hakika wale wasio iamini Akhera wanajitenga na Njia hiyo.'),
(2748, 23, 75, 'Na lau tungeli warehemu na tukawaondolea shida waliyo nayo bila ya shaka wangeli endelea katika upotofu wao vile vile, wakitangatanga.'),
(2749, 23, 76, 'Na hakika tuliwatia adhabu kali; lakini hawakuelekea kwa Mola wao Mlezi wala hawakunyenyekea.'),
(2750, 23, 77, 'Mpaka tulipo wafungulia mlango wa adhabu kali, hapo ndipo wakawa wenye kukata tamaa.'),
(2751, 23, 78, 'Na Yeye ndiye aliye kuumbieni kusikia na kuona na nyoyo za kufahamu. Ni kuchache mno kushukuru kwenu.'),
(2752, 23, 79, 'Na Yeye ndiye aliye kuenezeni katika ardhi, na kwake Yeye mtakusanywa.'),
(2753, 23, 80, 'Na Yeye ndiye anaye huisha na kufisha, na yake Yeye mabadiliko ya usiku na mchana. Basi je! Hamfahamu?'),
(2754, 23, 81, 'Bali wanasema kama walivyo sema watu wa kwanza.'),
(2755, 23, 82, 'Walisema: Je! Tukisha kufa tukawa udongo na mifupa ndio tutafufuliwa?'),
(2756, 23, 83, 'Tuliahidiwa haya sisi na baba zetu zamani. Hayakuwa haya ila ni visa vya uwongo vya watu wa zamani.'),
(2757, 23, 84, 'Sema: Ni ya nani ardhi na viliomo ndani yake, kama nyinyi mnajua?'),
(2758, 23, 85, 'Watasema: Ni vya Mwenyezi Mungu! Sema: Basi, je, hamkumbuki?'),
(2759, 23, 86, 'Sema: Ni nani Mola Mlezi wa mbingu saba na Mola Mlezi wa A\'rshi Kuu?'),
(2760, 23, 87, 'Watasema: Ni vya Mwenyezi Mungu. Sema: Basi je, hamwogopi?'),
(2761, 23, 88, 'Sema: Ni nani mkononi mwake umo Ufalme wa kila kitu, naye ndiye anaye linda, wala hakilindwi asicho taka, kama mnajua?'),
(2762, 23, 89, 'Watasema: (Ufalme huo) ni wa Mwenyezi Mungu. Sema: Basi vipi mnadanganyika?'),
(2763, 23, 90, 'Bali tumewaletea Haki, na kwa yakini hao ni waongo.'),
(2764, 23, 91, 'Mwenyezi Mungu hana mwana yeyote, wala hanaye mungu mwengine. Inge kuwa hivyo basi kila mungu angeli chukua alivyo umba, na baadhi yao wangeli washinda wengine. Mwenyezi Mungu ameepukana na sifa wanazo msifu.'),
(2765, 23, 92, 'Mjuzi wa siri na dhaahiri, na ametukuka juu ya hayo wanayo mshirikisha nayo.'),
(2766, 23, 93, 'Sema: Mola wangu Mlezi! Ukinionyesha adhabu waliyo ahidiwa,'),
(2767, 23, 94, 'Mola wangu Mlezi! Usinijaalie katika watu madhaalimu hao.'),
(2768, 23, 95, 'Na hakika Sisi ni waweza wa kukuonyeshsa tuliyo waahidi.'),
(2769, 23, 96, 'Kinga maovu kwa kutenda yaliyo mema zaidi. Sisi tunajua wayasemayo.'),
(2770, 23, 97, 'Na sema: Mola wangu Mlezi! Najikinga kwako na wasiwasi wa mashet\'ani.'),
(2771, 23, 98, 'Na najikinga kwako, Mola wangu Mlezi, wasinikaribie.'),
(2772, 23, 99, 'Mpaka yanapo mfikia mmoja wao mauti, husema: Mola wangu Mlezi! Nirudishe.'),
(2773, 23, 100, 'Ili nitende mema sasa badala ya yale niliyo yaacha. Wapi! Hii ni kauli aisemayo yeye tu. Na nyuma yao kipo kizuizi mpaka siku watapo fufuliwa.'),
(2774, 23, 101, 'Basi litapo pulizwa baragumu hapo hautakuwapo ujamaa baina yao siku hiyo, wala hawataulizana.'),
(2775, 23, 102, 'Ama wale ambao mizani zao zitakuwa nzito, hao ndio wenye kufanikiwa.'),
(2776, 23, 103, 'Na wale ambao mizani zao zitakuwa nyepesi, hao ndio walio zitia khasarani nafsi zao, na katika Jahannamu watadumu.'),
(2777, 23, 104, 'Moto utababua nyuso zao, nao watakuwa na nyuso zilizo kunjana.'),
(2778, 23, 105, 'Je! Hazikuwa Aya zangu mkisomewa, na nyinyi mkizikanusha?'),
(2779, 23, 106, 'Mola wetu Mlezi! Tulizidiwa na uovu wetu na tukawa watu tulio potea.'),
(2780, 23, 107, 'Mola wetu Mlezi! Tutoe humu Motoni. Na tufanyapo tena basi kweli sisi ni wenye kudhulumu.'),
(2781, 23, 108, 'Atasema Mwenyezi Mungu: Tokomeeni humo, wala msinisemeze.'),
(2782, 23, 109, 'Bila ya shaka lilikuwapo kundi katika waja wangu likisema: Mola wetu Mlezi! Tumeamini; basi tusamehe na uturehemu, nawe ndiwe Mbora wa wanao rehemu.'),
(2783, 23, 110, 'Lakini nyinyi mliwakejeli hata wakakusahaulisheni kunikumbuka, na mlikuwa mkiwacheka.'),
(2784, 23, 111, 'Hakika Mimi leo nimewalipa kwa vile walivyo subiri. Bila ya shaka hao ndio wenye kufuzu.'),
(2785, 23, 112, 'Atasema: Mlikaa muda gani katika ardhi kwa hisabu ya miaka?'),
(2786, 23, 113, 'Watasema: Tulikaa siku moja au sehemu ya siku. Basi waulize wanao weka hisabu.'),
(2787, 23, 114, 'Atasema: Nyinyi hamkukaa huko duniani ila kidogo, laiti ingeli kuwa mnajua.'),
(2788, 23, 115, 'Je! Mlidhani ya kwamba tulikuumbeni bure na ya kwamba nyinyi kwetu hamtarudishwa?'),
(2789, 23, 116, 'Ametukuka Mwenyezi Mungu, Mfalme wa Haki, hapana mungu ila Yeye, Mola Mlezi wa A\'rshi Tukufu.'),
(2790, 23, 117, 'Na anaye muomba - pamoja na Mwenyezi Mungu - mungu mwenginewe hana ushahidi wa hili; basi bila ya shaka hisabu yake iko kwa Mola wake Mlezi. Kwa hakika makafiri hawafanikiwi.'),
(2791, 23, 118, 'Nawe sema: Mola wangu Mlezi! Samehe na urehemu nawe ni Mbora wa wanao rehemu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'HII NI SURA Tuliyo iteremsha na tukailazimisha; tukateremsha ndani yake Aya zilizo wazi ili mkumbuke.'),
(2793, 24, 2, 'Mzinifu mwanamke na mzinifu mwanamume, mtandikeni kila mmoja katika wao bakora mia. Wala isikushikeni huruma kwa ajili yao katika hukumu ya Mwenyezi Mungu, ikiwa nyinyi mnamuamini Mwenyezi Mungu na Siku ya Mwisho. Na lishuhudie adhabu yao kundi la Waumini.'),
(2794, 24, 3, 'Mwanamume mzinifu hamwoi ila mwanamke mzinifu au mwanamke mshirikina. Na mwanamke mzinifu haolewi ila na mwanamume mzinifu au mshirikina. Na hayo yameharimishwa kwa Waumini.'),
(2795, 24, 4, 'Na wanao wasingizia wanawake mahashumu, kisha wasilete mashahidi wane, basi watandikeni bakora thamanini, na msiwakubalie ushahidi wao tena. Na hao ndio wapotovu.'),
(2796, 24, 5, 'Isipo kuwa wale walio tubu baada ya hayo na wakatengenea; kwani bila ya shaka Mwenyezi Mungu ni Msamehevu, Mwenye kurehemu.'),
(2797, 24, 6, 'Na wale wanao wasingizia wake zao na hawana mashahidi ila nafsi zao, basi ushahidi wa mmoja wao utakuwa kushuhudilia mara nne kwa kiapo cha Mwenyezi Mungu ya kwamba hakika yeye ni katika wasema kweli.'),
(2798, 24, 7, 'Na mara ya tano kwamba laana ya Mwenyezi Mungu iwe juu yake ikiwa ni miongoni mwa waongo.'),
(2799, 24, 8, 'Na mke itamwondokea adhabu kwa kutoa shahada mara nne kwa kiapo cha Mwenyezi Mungu ya kwamba huyu mume ni miongoni mwa waongo.'),
(2800, 24, 9, 'Na mara ya tano ya kwamba ghadhabu ya Mwenyezi Mungu iwe juu yake kama mumewe yu miongoni mwa wanao sema kweli.'),
(2801, 24, 10, 'Na lau kuwa si fadhila ya Mwenyezi Mungu juu yenu na rehema yake, na ya kuwa Mwenyezi Mungu ni Mpokeaji toba....'),
(2802, 24, 11, 'Hakika wale walio leta uwongo ni kundi miongoni mwenu. Msifikiri ni shari kwenu, bali hiyo ni kheri kwenu. Kila mtu katika wao atapata aliyo yachuma katika madhambi. Na yule aliyejitwika sehemu yake kubwa miongoni mwao, atapata adhabu kubwa.'),
(2803, 24, 12, 'Kwa nini mlipo sikia khabari hii, wanaume Waumini na wanawake Waumini hawakuwadhania wenzao mema, na kusema: Huu ni uzushi dhaahiri?'),
(2804, 24, 13, 'Kwa nini hawakuleta mashahidi wane? Na ilivyo kuwa hawakuleta mashahidi wane basi hao mbele ya Mwenyezi Mungu ni waongo.'),
(2805, 24, 14, 'Na lau kuwa si fadhila ya Mwenyezi Mungu juu yenu na rehema yake katika dunia na Akhera, bila ya shaka ingeli kupateni adhabu kubwa kwa sababu ya yale mliyo jiingiza.'),
(2806, 24, 15, 'Mlipo yapokea kwa ndimi zenu na mkasema kwa vinywa vyenu msiyo yajua, na mlifikiri ni jambo dogo, kumbe mbele ya Mwenyezi Mungu ni kubwa.'),
(2807, 24, 16, 'Na kwa nini mlipo yasikia msiseme: Haitufalii kuzungumza haya. Subhanak Umetakasika! Huu ni uzushi mkubwa!'),
(2808, 24, 17, 'Mwenyezi Mungu anakuonyeni msirejee tena kufanya kama haya kabisa, ikiwa nyinyi ni Waumini!'),
(2809, 24, 18, 'Na Mwenyezi Mungu anakubainisheni Aya. Na Mwenyezi Mungu ni Mjuzi Mwenye hikima.'),
(2810, 24, 19, 'Kwa hakika wale wanao penda uenee uchafu kwa walio amini, watapata adhabu chungu katika dunia na Akhera. Na Mwenyezi Mungu anajua na nyinyi hamjui.'),
(2811, 24, 20, 'Na lau kuwa si fadhila ya Mwenyezi Mungu juu yenu na rehema yake, na kuwa hakika Mwenyezi Mungu ni Mpole, Mwenye kurehemu....'),
(2812, 24, 21, 'Enyi mlio amini! Msizifuate nyayo za Shet\'ani. Na atakaye fuata nyayo za Shet\'ani basi yeye huamrisha machafu na maovu. Na lau kuwa si fadhila ya Mwenyezi Mungu na rehema yake juu yenu, asingeli takasika miongoni mwenu kabisa hata mmoja. Lakini Mwenyezi Mungu humtakasa amtakaye, na Mwenyezi Mungu ni Mwenye kusikia, Mwenye kujua.'),
(2813, 24, 22, 'Na wale katika nyinyi wenye fadhila na wasaa wasiape kutowapa jamaa zao na masikini na walio hama kwa Njia ya Mwenyezi Mungu. Na wasamehe, na waachilie mbali. Je! Nyinyi hampendi Mwenyezi Mungu akusameheni? Na Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.'),
(2814, 24, 23, 'Hakika wanao wasingizia wanawake, wanao jihishimu, walio ghafilika, Waumini, wamelaaniwa duniani na Akhera, nao watapata adhabu kubwa.'),
(2815, 24, 24, 'Siku ambayo zitawashuhudia ndimi zao na mikono yao na miguu yao kwa waliyo kuwa wakiyatenda.'),
(2816, 24, 25, 'Siku hiyo Mwenyezi Mungu atawapa sawa sawa malipo yao ya haki, na watajua kwamba hakika Mwenyezi Mungu ndiye Haki Iliyo Wazi.'),
(2817, 24, 26, 'Wanawake waovu ni wa wanaume waovu, na wanaume waovu ni wa wanawake waovu. Na wanawake wema ni wa wanaume wema, na wanaume wema ni wa wanawake wema. Hao wameepushwa na hayo wanayo yasema; wao watapata msamaha na riziki ya ukarimu.'),
(2818, 24, 27, 'Enyi mlio amini! Msiingie nyumba zisio nyumba zenu mpaka mwombe ruhusa, na mwatolee salamu wenyewe. Hayo ni bora kwenu mpate kukumbuka.'),
(2819, 24, 28, 'Na msip o mkuta mtu humo basi msiingie mpaka mruhusiwe. Na mkiambiwa: Rudini! Basi rudini. Hivi ni usafi zaidi kwenu. Na Mwenyezi Mungu anajua mnayo yatenda.'),
(2820, 24, 29, 'Si vibaya kwenu kuingia nyumba zisio kaliwa ambazo ndani yake yamo manufaa yenu. Na Mwenyezi Mungu anajua mnayo yadhihirisha na mnayo yaficha.'),
(2821, 24, 30, 'Waambie Waumini wanaume wainamishe macho yao, na wazilinde tupu zao. Hili ni takaso bora kwao. Hakika Mwenyezi Mungu anazo khabari za wanayo yafanya.'),
(2822, 24, 31, 'Na waambie Waumini wanawake wainamishe macho yao, na wazilinde tupu zao, wala wasionyeshe uzuri wao isipo kuwa unao dhihirika. Na waangushe shungi zao juu ya vifua vyao, wala wasionyeshe uzuri wao ila kwa waume zao, au baba zao, au baba wa waume zao, au watoto wao, au watoto wa waume zao, au kaka zao, au wana wa kaka zao, au wana wa dada zao, au wanawake wenzao, au iliyo wamiliki mikono yao ya kuume, au wafwasi wanaume wasio na matamanio, au watoto ambao hawajajua mambo yaliyo khusu uke. Wala wasipige chini miguu yao ili yajuulikane mapambo waliyo yaficha. Na tubuni nyote kwa Mwenyezi Mungu, enyi Waumini, ili mpate kufanikiwa.'),
(2823, 24, 32, 'Na waozeni wajane miongoni mwenu na wema katika watumwa wenu na wajakazi wenu. Wakiwa mafakiri Mwenyezi Mungu atawatajirisha kwa fadhila yake. Na Mwenyezi Mungu ni Mwenye wasaa Mwenye kujua.'),
(2824, 24, 33, 'Na wajizuilie na machafu wale wasio pata cha kuolea, mpaka Mwenyezi Mungu awatajirishe kwa fadhila yake. Na wanao taka kuandikiwa uhuru katika wale ambao mikono yenu ya kulia imewamiliki, basi waandikieni kama mkiona wema kwao. Na wapeni katika mali ya Mwenyezi Mungu aliyo kupeni. Wala msiwashurutishe vijakazi vyenu kufanya zina kwa ajili ya kutafuta pato la maisha ya dunia, ilhali wao wanataka kujihishimu. Na atakaye walazimisha basi Mwenyezi Mungu baada ya kulazimishwa kwao huko, atawasamehe, kwani Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(2825, 24, 34, 'Na kwa yakini tumekuteremshieni Ishara zilizo wazi na mifano kutokana na walio tangulia kabla yenu na mawaidha kwa wachamngu.'),
(2826, 24, 35, 'Mwenyezi Mungu ni Nuru ya Mbingu na Ardhi. Mfano wa Nuru yake ni kama shubaka lenye kuwekwa ndani yake taa. Taa ile imo katika tungi. Tungi lile ni kama nyota inayo meremeta, inayo washwa kwa mafuta yanayo toka katika mti ulio barikiwa, mzaituni. Si wa mashariki wala magharibi. Yanakaribia mafuta yake kung\'aa wenyewe ingawa moto haujayagusa - Nuru juu ya Nuru. Mwenyezi Mungu humwongoa kwenye Nuru yake amtakaye. Na Mwenyezi Mungu huwapigia watu mifano. Na Mwenyezi Mungu ni Mwenye kujua kila kitu.'),
(2827, 24, 36, 'Katika Nyumba ambazo Mwenyezi Mungu ameamrisha zitukuzwe, na humo litajwe jina lake, wanamtakasa humo asubuhi na jioni'),
(2828, 24, 37, 'Watu ambao biashara wala kuuza hakuwashughulishi na kumdhukuru Mwenyezi Mungu, na kushika Sala, na kutoa Zaka. Wanaikhofu Siku ambayo nyoyo na macho yatageuka.'),
(2829, 24, 38, 'Ili Mwenyezi Mungu awalipe bora ya waliyo yatenda, na awazidishie katika fadhila zake. Na Mwenyezi Mungu humruzuku amtakaye bila ya hisabu.'),
(2830, 24, 39, 'Na walio kufuru vitendo vyao ni kama sarabi (mazigazi) uwandani. Mwenye kiu huyadhania ni maji. Hata akiyaendea hapati chochote. Na atamkuta Mwenyezi Mungu hapo naye amlipe hisabu yake sawa sawa. Na Mwenyezi Mungu ni Mwepesi wa kuhisabu.'),
(2831, 24, 40, 'Au ni kama giza katika bahari kuu, iliyo funikwa na mawimbi juu ya mawimbi, na juu yake yapo mawingu. Giza juu ya giza. Akiutoa mtu mkono wake anakaribia asiuone. Na ambaye Mwenyezi Mungu hakumjaalia kuwa na nuru hawi na nuru.'),
(2832, 24, 41, 'Je! Huoni kwamba Mwenyezi Mungu vinamtakasa vilivyomo katika mbingu na ardhi, na ndege wakikunjua mbawa zao? Kila mmoja amekwisha jua Sala yake na namna ya kumtakasa kwake. Na Mwenyezi Mungu ni Mwenye kuyajua wayatendayo.'),
(2833, 24, 42, 'Na ni wa Mwenyezi Mungu ufalme wa mbingu na ardhi, na kwa Mwenyezi Mungu ndio marejeo ya wote.'),
(2834, 24, 43, 'Je! Huoni ya kwamba Mwenyezi Mungu huyasukuma mawingu, kisha huyaambatisha, kisha huyafanya mirundi? Basi utaona mvua ikitoka kati yake. Na huteremsha kutoka juu kwenye milima ya mawingu mvua ya mawe, akamsibu nayo amtakaye na akamuepusha nayo amtakaye. Hukurubia mmetuko wa umeme wake kupofua macho.'),
(2835, 24, 44, 'Mwenyezi Mungu hubadilisha usiku na mchana. Hakika katika hayo yapo mazingatio kwa wenye kuona.'),
(2836, 24, 45, 'Na Mwenyezi Mungu ameumba kila kinyama kutokana na maji. Wengine katika wao huenda kwa matumbo yao, na wengine huenda kwa miguu miwili, na wengine huenda kwa miguu mine. Mwenyezi Mungu huumba ayatakayo. Hakika Mwenyezi Mungu ni Muweza wa kila kitu.'),
(2837, 24, 46, 'Kwa yakini tumeteremsha Ishara zinazo bainisha. Na Mwenyezi Mungu hummwongoa amtakaye kwenye Njia Iliyo Nyooka.'),
(2838, 24, 47, 'Na wanasema: Tumemuamini Mwenyezi Mungu na Mtume, na tumet\'ii. Kisha kikundi katika hao hugeuka baada ya hayo. Wala hao si wenye kuamini.'),
(2839, 24, 48, 'Na wanapo itwa kumuendea Mwenyezi Mungu na Mtume wake ili ahukumu baina yao, kipo kikundi katika wao kinakataa.'),
(2840, 24, 49, 'Na ikiwa haki ni yao, wanamjia kwa kut\'ii.'),
(2841, 24, 50, 'Je! Wana maradhi katika nyoyo zao, au wanaona shaka, au wanaogopa ya kuwa Mwenyezi Mungu na Mtume wake watawadhulumu? Bali hao ndio madhaalimu.'),
(2842, 24, 51, 'Haiwi kauli ya Waumini wanapo itwa kwa Mwenyezi Mungu na Mtume wake ili ahukumu baina yao, ila ni kusema: Tumesikia, na tumet\'ii! Na hao ndio wenye kufanikiwa.'),
(2843, 24, 52, 'Na wenye kumt\'ii Mwenyezi Mungu na Mtume wake, na wakamwogopa Mwenyezi Mungu na wakamcha, basi hao ndio wenye kufuzu.'),
(2844, 24, 53, 'Na wanaapa kwa Mwenyezi Mungu ukomo wa kiapo chao ya kwamba ukiwaamrisha kwa yakini watatoka. Sema: Msiape! Ut\'iifu unajuulikana. Hakika Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(2845, 24, 54, 'Sema: Mt\'iini Mwenyezi Mungu, na mt\'iini Mtume. Na mkigeuka, basi yaliyo juu yake ni aliyo bebeshwa, na yaliyo juu yenu ni mliyo bebshwa nyinyi. Na mkimt\'ii yeye mtaongoka. Na hapana juu ya Mtume ila kufikisha Ujumbe wazi wazi.'),
(2846, 24, 55, 'Mwenyezi Mungu amewaahidi wale walio amini miongoni mwenu na wakatenda mema, ya kwamba atawafanya makhalifa katika ardhi kama alivyo wafanya makhalifa wa kabla yao, na kwa yakini atawasimamishia Dini yao aliyo wapendelea, na atawabadilishia amani baada ya khofu yao. Wawe wananiabudu Mimi, wala wasinishirikishe na chochote. Na watao kufuru baada ya hayo, basi hao ndio wapotovu.'),
(2847, 24, 56, 'Na shikeni Sala, na toeni Zaka, na mt\'iini Mtume, ili mpate kurehemewa.'),
(2848, 24, 57, 'Msiwadhanie walio kufuru kwamba watashinda katika nchi. Na makaazi yao ni Motoni, na hakika ni maovu kweli marejeo yao.'),
(2849, 24, 58, 'Enyi mlio amini! Nawakutakeni idhini iliyo wamiliki mikono yenu ya kulia na walio bado kubaalighi miongoni mwenu mara tatu - kabla ya Sala ya alfajiri, na wakati mnapo vua nguo zenu adhuhuri, na baada ya Sala ya isha. Hizi ni nyakati tatu za faragha kwenu. Si vibaya kwenu wala kwao baada ya nyakati hizi, kuzungukiana nyinyi kwa nyinyi. Hivyo ndivyo Mwenyezi Mungu anavyo kuelezeni Aya zake, na Mwenyezi Mungu ni Mjuzi Mwenye hikima.'),
(2850, 24, 59, 'Na watoto wanapo fikilia umri wa kubaalighi basi nawatake ruhusa, kama walivyo taka ruhusa wa kabla yao. Hivyo ndivyo anavyo kubainishieni Mwenyezi Mungu Aya zake, na Mwenyezi Mungu ni Mjuzi Mwenye hikima.'),
(2851, 24, 60, 'Na wanawake wazee wasio taraji kuolewa, si vibaya kwao kupunguza nguo zao, bila ya kujishaua kwa mapambo. Na wakijihishimu ni bora kwao. Na Mwenyezi Mungu ni Mwenye kusikia, Mwenye kujua.'),
(2852, 24, 61, 'Si vibaya kwa kipofu, wala si vibaya kwa kiguru, wala si vibaya kwa mgonjwa, wala kwenu nyinyi, mkila katika nyumba zenu, au nyumba za baba zenu, au nyumba za mama zenu, au nyumba za kaka zenu, au nyumba za dada zenu, au nyumba za ami zenu, au nyumba za shangazi zenu, au nyumba za wajomba zenu, au nyumba za dada wa mama zenu, au za mlio washikia funguo zao, au rafiki yenu. Si vibaya kwenu mkila pamoja au mbali mbali. Na mkiingia katika nyumba, toleaneni salamu, kuwa ni maamkio yanayo toka kwa Mwenyezi Mungu, yenye baraka na mema. Hivyo ndivyo Mwenyezi Mungu anavyo kubainishieni Aya zake mpate kuelewa.'),
(2853, 24, 62, 'Hakika Waumini wa kweli ni walio muamini Mwenyezi Mungu na Mtume wake; na wanapo kuwa pamoja naye kwa jambo la kuwakhusu wote hawaondoki mpaka wamtake ruhusa. Kwa hakika wale wanao kuomba ruhusa, hao ndio wanao muamini Mwenyezi Mungu na Mtume wake. Na wakikuomba ruhusa kwa baadhi ya kazi zao, mruhusu umtakaye miongoni mwao, uwaombee msamaha kwa Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemmu.'),
(2854, 24, 63, 'Msifanye wito wa Mtume baina yenu kama wito wa nyinyi kwa nyinyi. Hakika Mwenyezi Mungu anawajua miongoni mwenu wanao ondoka kwa uficho. Basi nawatahadhari wanao khalifu amri yake, usije ukawapata msiba au ikawapata adhabu chungu.'),
(2855, 24, 64, 'Jueni kuwa hakika ni vya Mwenyezi Mungu vilivyomo mbinguni na ardhini. Hakika Yeye anajua mlio nayo. Na siku mtapo rudishwa kwake atawaeleza waliyo yafanya. Na Mwenyezi Mungu ni Mjuzi wa kila kitu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Ametukuka aliye teremsha Furqani kwa mja wake, ili awe mwonyaji kwa walimwengu wote.'),
(2857, 25, 2, 'Ambaye ni wake ufalme wa mbingu na ardhi, wala hakuwa na mwana, wala hakuwa na mshirika katika ufalme, na akaumba kila kitu na akakikadiria kwa kipimo.'),
(2858, 25, 3, 'Na wamechukua badala yake miungu ambayo haiumbi chochote, bali hiyo inaumbwa, haijimilikii nafsi zao madhara wala manufaa, wala haimiliki mauti, wala uhai, wala kufufuka.'),
(2859, 25, 4, 'Na wamesema walio kufuru: Haya si chochote ila ni uzushi alio uzua, na wamemsaidia kwa haya watu wengine! Hakika hawa wamekuja na dhulma na uwongo.'),
(2860, 25, 5, 'Na wakasema: Hivi ni visa vya watu wa kale alivyo viandikisha, anavyo somewa asubuhi na jioni.'),
(2861, 25, 6, 'Sema: Ameyateremsha haya ajuaye siri za katika mbingu na ardhi. Hakika Yeye ni Mwenye kusamehe Mwenye kurehemu.'),
(2862, 25, 7, 'Na wakasema: Ana nini Mtume huyu? Anakula chakula, na anatembea masokoni! Mbona hakuteremshiwa Malaika akawa mwonyaji pamoja naye?'),
(2863, 25, 8, 'Au akaangushiwa khazina juu yake, au akawa na bustani ale katika hiyo? Wakasema madhaalimu: Nyinyi hamumfuati ila mtu aliye rogwa.'),
(2864, 25, 9, 'Tazama jinsi wanavyo kupigia mifano. Basi wamepotea, wala hawataiweza Njia.'),
(2865, 25, 10, 'Ametukuka ambaye akitaka atakujaalia yaliyo bora kuliko hayo, nayo ni mabustani yapitayo mito kati yao, na atakujaalia majumba ya fakhari.'),
(2866, 25, 11, 'Bali wanaikanusha Saa (ya Kiyama). Na Sisi tumemuandalia Moto mkali kabisa huyo mwenye kuikanusha Saa..'),
(2867, 25, 12, 'Ule Moto ukiwaona tangu mahali mbali wao watasikia hasira yake na mngurumo wake.'),
(2868, 25, 13, 'Na watakapo tupwa humo mahali dhiki, hali wamefungwa, wataomba wafe.'),
(2869, 25, 14, 'Msiombe kufa mara moja tu, bali mfe mara nyingi!'),
(2870, 25, 15, 'Sema: Je! Haya ni bora au Pepo ya milele, ambayo wameahidiwa wachamngu, iwe kwao malipo na marejeo?'),
(2871, 25, 16, 'Watayapata humo wayatakayo daima dawamu. Hii ni ahadi juu ya Mola wako Mlezi, inayo ombwa.'),
(2872, 25, 17, 'Na siku atakapo wakusanya wao na hao wanao waabudu, na akasema: Je! Ni nyinyi mlio wapoteza waja wangu hawa, au wao wenyewe walipotea njia?'),
(2873, 25, 18, 'Watasema: Subhanak, Umetakasika na upungufu! Haikutupasia sisi kuchukulia walinzi badala yako, lakini Wewe uliwastarehesha wao na baba zao hata wakasahau kukumbuka, na wakawa watu walio angamia.'),
(2874, 25, 19, 'Basi walikukanusheni kwa mliyo yasema. Kwa hivyo hamwezi kujiondolea wala kujisaidia. Na atakaye dhulumu miongoni mwenu tutamwonjesha adhabu kubwa.'),
(2875, 25, 20, 'Na hatukuwatuma kabla yako Mitume wowote ila kwa hakika na yakini walikuwa wakila chakula, na wakenda masokoni. Na tumewajaalia baadhi yenu wawe ni majaribio kwa wengine; je! Mtasubiri? Na Mola wako Mlezi ni Mwenye kuona.'),
(2876, 25, 21, 'NA WALISEMA wale wasio taraji kukutana nasi: Mbona sisi hatuteremshiwi Malaika au hatumwoni Mola wetu Mlezi? Kwa yakini hawa wamejiona bora nafsi zao, na wamepanda kichwa vikubwa mno!'),
(2877, 25, 22, 'Siku watakayo waona Malaika haitakuwa furaha siku hiyo kwa wakosefu. Na watasema: Mungu apishe mbali!'),
(2878, 25, 23, 'Na tutayaendea yale waliyo yatenda katika vitendo vyao, tuvifanye kama mavumbi yaliyo tawanyika.'),
(2879, 25, 24, 'Watu wa Peponi siku hiyo watakuwa katika makaazi bora na mahali penye starehe nzuri.'),
(2880, 25, 25, 'Na siku zitapo funguka mbingu kwa mawingu, na wateremshwe Malaika kwa wingi,'),
(2881, 25, 26, 'Ufalme wa haki siku hiyo utakuwa wa Arrahman, Mwingi wa Rehema, na itakuwa siku ngumu kwa makafiri.'),
(2882, 25, 27, 'Na siku ambayo mwenye kudhulumu atajiuma mikono yake, na huku anasema: Laiti ningeli shika njia pamoja na Mtume!'),
(2883, 25, 28, 'Ee Ole wangu! Laiti nisingeli mfanya fulani kuwa rafiki!'),
(2884, 25, 29, 'Amenipoteza, nikaacha Ukumbusho, baada ya kwisha nijia, na kweli Shet\'ani ni khaini kwa mwanaadamu.'),
(2885, 25, 30, 'Na Mtume alikuwa akisema: Ee Mola wangu Mlezi! Hakika watu wangu wameifanya hii Qur\'ani ni kihame.'),
(2886, 25, 31, 'Na vivi hivi tumemfanyia kila Nabii adui miongoni mwa wakosefu, na Mola wako Mlezi anatosha kuwa Mwenye kuongoa na Mwenye kunusuru.'),
(2887, 25, 32, 'Na wakasema walio kufuru: Kwa nini hakuteremshiwa Qur\'ani kwa jumla moja? Hayo ni hivyo ili tuuthibitishe moyo wako, na ndio tumeisoma kwa mafungu.'),
(2888, 25, 33, 'Wala hawatakuletea mfano wowote, ila na Sisi tutakuletea (jawabu) kwa haki, na tafsiri iliyo bora.'),
(2889, 25, 34, 'Wale ambao watakao kusanywa na kubururwa kifudifudi mpaka kwenye Jahannamu, hao watakuwa na mahali pabaya, ndio wenye kuipotea sana njia.'),
(2890, 25, 35, 'Na hakika tulimpa Musa Kitabu na tukamweka pamoja naye nduguye, Harun, kuwa waziri.'),
(2891, 25, 36, 'Tukawaambia: Nendeni kwa watu walio kanusha Ishara zetu. Basi tukawateketeza kabisa.'),
(2892, 25, 37, 'Na watu wa Nuhu, walipo wakanusha Mitume, tuliwazamisha, na tukawafanya ni Ishara kwa watu. Na tumewaandalia wenye kudhulumu adhabu chungu.'),
(2893, 25, 38, 'Na tuliwaangamiza kina A\'di na Thamudi na watu wa Rass na vizazi vingi vilivyo kuwa kati yao.'),
(2894, 25, 39, 'Na wote tuliwapigia mifano, na wote tuliwaangamiza kabisa kabisa.'),
(2895, 25, 40, 'Na kwa yakini wao wanafika kwenye nchi ilio teremshiwa mvua mbaya. Basi, je, hawakuwa wakiiona? Bali walikuwa hawataraji kufufuliwa.'),
(2896, 25, 41, 'Na wanapo kuona hawakuchukulii ila ni mzaha tu, na (wanasema): Ati ndiye huyu Mwenyezi Mungu aliye mtuma kuwa Mtume?'),
(2897, 25, 42, 'Kwa hakika alikuwa karibu kutupoteza tuiache miungu yetu, ingeli kuwa hatukushikamana nayo kwa kuvumilia. Bado watakuja jua, watakapo iona adhabu, ni nani aliye potea njia.'),
(2898, 25, 43, 'Je! Umemwona aliye yafanya matamanio yake kuwa ndio mungu wake? Basi je, wewe utakuwa ni wakili wake?'),
(2899, 25, 44, 'Au wewe unadhani kwamba wengi wao wanasikia au wanaelewa? Hao hawakuwa ila ni kama nyama hoa tu, bali wao wamepotea zaidi njia.'),
(2900, 25, 45, 'Je! Huoni jinsi Mola wako Mlezi anavyo kitandaza kivuli. Na angeli taka angeli kifanya kikatulia tu. Kisha tumelifanya jua kuwa ni kiongozi wake.'),
(2901, 25, 46, 'Kisha tunakivutia kwetu kidogo kidogo.'),
(2902, 25, 47, 'Naye ndiye aliye kufanyieni usiku kuwa ni vazi, na usingizi kuwa mapumziko, na akakufanyieni mchana ni kufufuka.'),
(2903, 25, 48, 'Naye ndiye anaye zituma pepo kuwa bishara njema kabla ya rehema yake, na tunayateremsha kutoka mbinguni maji safi.'),
(2904, 25, 49, 'Ili kwa hayo tuihuishe nchi iliyo kufa, na tuwanyweshe wanyama na watu wengi tulio waumba.'),
(2905, 25, 50, 'Na kwa yakini tumeisarifu kati yao wapate kukumbuka. Lakini watu wengi wanakataa ila kukufuru.'),
(2906, 25, 51, 'Na tungeli taka tungeli peleka katika kila mji Mwonyaji.'),
(2907, 25, 52, 'Basi usiwat\'ii makafiri. Na pambana nao kwayo kwa Jihadi kubwa.'),
(2908, 25, 53, 'Naye ndiye aliye zipeleka bahari mbili, hii tamu mno, na hii ya chumvi chungu. Na akaweka baina yao kinga na kizuizi kizuiacho.'),
(2909, 25, 54, 'Naye ndiye aliye muumba mwanaadamu kutokana na maji, akamjaalia kuwa na nasaba na ushemeji. Na Mola wako Mlezi ni Muweza.'),
(2910, 25, 55, 'Na wanayaabudu badala ya Mwenyezi Mungu yasiyo wafaa wala yasiyo wadhuru. Na kafiri daima ni msaidizi wa kumpinga Mola wake Mlezi.'),
(2911, 25, 56, 'Nasi hatukukutuma ila kuwa ni Mbashiri na Mwonyaji.'),
(2912, 25, 57, 'Sema: Sikukuombeni ujira juu yake; ila atakaye na ashike njia iendayo kwa Mola wake Mlezi.'),
(2913, 25, 58, 'Na mtegemee Aliye Hai, ambaye hafi. Na umtukuze kwa sifa zake. Na yatosha kwake kuwa yeye ni Mwenye khabari za dhambi za waja wake.'),
(2914, 25, 59, 'Ambaye ameziumba mbingu na ardhi, na viliomo ndani yake kwa siku sita. Kisha akatawala juu ya A\'rshi, Arrahman, Mwingi wa Rehema! Uliza khabari zake kwa wamjuaye.'),
(2915, 25, 60, 'Na wanapo ambiwa: Msujudieni Arrahman! Wao husema: Ni nani Arrahman? Je! Tumsujudie unaye tuamrisha wewe tu? Na huwazidishia chuki.'),
(2916, 25, 61, 'Ametukuka aliye zijaalia nyota mbinguni, na akajaalia humo taa na mwezi unao ng\'ara.'),
(2917, 25, 62, 'Naye ndiye aliye fanya usiku na mchana kufuatana kwa nafuu ya anaye taka kukumbuka, au anaye taka kushukuru.'),
(2918, 25, 63, 'Na waja wa Arrahman Mwingi wa Rehema ni wale wanao tembea ulimwenguni kwa staha, na wajinga wakiwasemeza hujibu: Salama!'),
(2919, 25, 64, 'Na wale wanao kesha kwa ajili ya Mola wao Mlezi kwa kusujudu na kusimama.'),
(2920, 25, 65, 'Na wale wanao sema: Mola wetu Mlezi! Tuondolee adhabu ya Jahannamu. Hakika adhabu yake ikimpata mtu haimwachi.'),
(2921, 25, 66, 'Hakika hiyo ni kituo na makao mabaya.'),
(2922, 25, 67, 'Na wale ambao wanapo tumia hawatumii kwa fujo wala hawafanyi ubakhili, bali wanakuwa katikati baina ya hayo.'),
(2923, 25, 68, 'Na wale wasio mwomba mungu mwengine pamoja na Mwenyezi Mungu, wala hawaui nafsi aliyo iharimisha Mwenyezi Mungu isipo kuwa kwa haki, wala hawazini - na atakaye fanya hayo atapata madhara,'),
(2924, 25, 69, 'Atazidishiwa adhabu Siku ya Kiyama, na atadumu humo kwa kufedheheka.'),
(2925, 25, 70, 'Isipo kuwa atakaye tubu, na akaamini, na akatenda vitendo vyema. Basi hao Mwenyezi Mungu atayabadilisha maovu yao yawe mema. Na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(2926, 25, 71, 'Na aliye tubu na akafanya mema, basi hakika huyo ametubu kweli kweli kwa Mwenyezi Mungu.'),
(2927, 25, 72, 'Na wale ambao hawatoi ushahidi wa uwongo, na pindi wapitapo penye upuuzi, hupita kwa hishima yao.'),
(2928, 25, 73, 'Na wale ambao wanapo kumbushwa Ishara za Mola wao Mlezi hawajifanyi viziwi nazo, na vipofu.'),
(2929, 25, 74, 'Na wale wanao sema: Mola wetu Mlezi! Tupe katika wake zetu na wenetu yaburudishayo macho, na utujaalie tuwe waongozi kwa wachamngu.'),
(2930, 25, 75, 'Hao ndio watakao lipwa makao ya juu kwa kuwa walisubiri, na watakuta humo maamkio na salamu.'),
(2931, 25, 76, 'Wadumu humo - kituo na makao mazuri kabisa.'),
(2932, 25, 77, 'Sema: Mola wangu Mlezi asinge kujalini lau kuwa si kuomba kwenu. Lakini nyinyi mmemkadhibisha. Basi adhabu lazima iwe.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'T\'aa Siin Miim. (T\'. S. M.)'),
(2934, 26, 2, 'Hizi ni Aya za Kitabu kinacho bainisha.'),
(2935, 26, 3, 'Huenda labda ukajikera nafsi yako kwa kuwa hawawi Waumini.'),
(2936, 26, 4, 'Tunge penda tungeli wateremshia kutoka mbinguni Ishara zikanyenyekea shingo zao.'),
(2937, 26, 5, 'Wala hauwafikii ukumbusho mpya kutoka kwa Arrahman ila wao hujitenga nao.'),
(2938, 26, 6, 'Kwa yakini wamekanusha; kwa hivyo zitakuja wafikia khabari za yale waliyo kuwa wakiyafanyia mzaha.'),
(2939, 26, 7, 'Je! Hawakuiona ardhi, mimea mingapi tumeiotesha humo, ya kila namna nzuri?'),
(2940, 26, 8, 'Hakika katika haya zipo Ishara. Lakini wengi wao hawakuwa wenye kuamini.'),
(2941, 26, 9, 'Na hakika Mola wako Mlezi ndiye Yeye Mwenye nguvu Mwenye kurehemu.'),
(2942, 26, 10, 'Na Mola wako Mlezi, alipo mwita Musa, akamwambia: Fika kwa watu madhaalimu,'),
(2943, 26, 11, 'Watu wa Firauni. Hawaogopi?'),
(2944, 26, 12, 'Akasema: Hakika mimi nachelea wasinikanushe.'),
(2945, 26, 13, 'Na kifua changu kina dhiki, na ulimi wangu haukunjuki vyema. Basi mtumie ujumbe Harun.'),
(2946, 26, 14, 'Na wao wana kisasi juu yangu, kwa hivyo naogopa wasije kuniuwa.'),
(2947, 26, 15, 'Akasema: Siyo hivyo kabisa! Nendeni na miujiza yetu. Hakika Sisi tu pamoja nanyi, tunasikiliza.'),
(2948, 26, 16, 'Basi mfikieni Firauni na mwambieni: Hakika sisi ni Mitume wa Mola Mlezi wa walimwengu wote.'),
(2949, 26, 17, 'Waachilie Wana wa Israili wende nasi.'),
(2950, 26, 18, '(Firauni) akasema: Sisi hatukukulea wewe utotoni, na ukakaa kwetu katika umri wako miaka mingi?'),
(2951, 26, 19, 'Na ukatenda kitendo chako ulicho tenda, nawe ukawa miongoni mwa wasio na shukrani?'),
(2952, 26, 20, '(Musa) akasema: Nilitenda hayo hapo nilipo kuwa miongoni mwa wale walio potea.'),
(2953, 26, 21, 'Basi nikakukimbieni nilipo kuogopeni, tena Mola wangu Mlezi akanitunukia hukumu, na akanijaalia niwe miongoni mwa Mitume.'),
(2954, 26, 22, 'Na hiyo ndiyo neema ya kunisumbulia, na wewe umewatia utumwani Wana wa Israili?'),
(2955, 26, 23, 'Firauni akasema: Na nani huyo Mola Mlezi wa walimwengu wote?'),
(2956, 26, 24, 'Akasema: Ndiye Mola Mlezi wa mbingu na ardhi, na viliomo baina yao, ikiwa nyinyi ni wenye yakini.'),
(2957, 26, 25, '(Firauni) akawaambia walio mzunguka: Hamsikilizi?'),
(2958, 26, 26, '(Musa) akasema: Ndiye Mola wenu Mlezi, na Mola Mlezi wa baba zenu wa kwanza.'),
(2959, 26, 27, '(Firauni) akasema: Hakika huyu Mtume wenu aliye tumwa kwenu ni mwendawazimu.'),
(2960, 26, 28, '(Musa) akasema: Ndiye Mola Mlezi wa Mashariki na Magharibi na viliomo baina yao, ikiwa nyinyi mnatia akilini.'),
(2961, 26, 29, '(Firauni) akasema: Ukimfuata mungu mwengine asiye kuwa mimi, basi bila ya shaka nitakufunga gerezani.'),
(2962, 26, 30, 'Akasema: Je! Ijapo kuwa nitakuletea kitu cha kubainisha wazi?'),
(2963, 26, 31, 'Akasema: Kilete basi, kama wewe ni katika wasemao kweli.'),
(2964, 26, 32, 'Basi akaitupa fimbo yake, mara ikawa nyoka wa kuonekana dhaahiri.'),
(2965, 26, 33, 'Na akautoa mkono wake, na mara ukawa mweupe kwa watazamao.'),
(2966, 26, 34, '(Firauni) akawaambia waheshimiwa walio mzunguka: Hakika huyu ni mchawi mtaalamu.'),
(2967, 26, 35, 'Anataka kukutoeni katika nchi yenu kwa uchawi wake. Basi mna shauri gani?'),
(2968, 26, 36, 'Wakasema: Mpe muda yeye na nduguye na uwatume mijini wapigao mbiu ya mgambo.'),
(2969, 26, 37, 'Wakuletee kila mchawi bingwa mtaalamu.'),
(2970, 26, 38, 'Basi wakakusanywa wachawi wakati na siku maalumu.'),
(2971, 26, 39, 'Na watu wakaambiwa: Je! Mtakusanyika?'),
(2972, 26, 40, 'Huenda tutawafuata wachawi wakiwa wao ndio watakao shinda.'),
(2973, 26, 41, 'Basi walipo kuja wachawi wakamwambia Firauni: Je! Tutapata ujira tukiwa sisi ndio tulio shinda?'),
(2974, 26, 42, 'Naam! Na hakika mtakuwa katika watu wa mbele.'),
(2975, 26, 43, 'Musa akwaambia: Tupeni mnavyo taka kutupa.'),
(2976, 26, 44, 'Basi wakatupa kamba zao na fimbo zao, na wakasema: Kwa nguvu za Firauni hakika sisi hapana shaka ni wenye kushinda.'),
(2977, 26, 45, 'Musa tena akatupa fimbo yake, nayo mara ikavimeza walivyo vizua.'),
(2978, 26, 46, 'Hapo wachawi walipinduka wakasujudu.'),
(2979, 26, 47, 'Wakasema: Tunamuamini Mola Mlezi wa walimwengu wote.'),
(2980, 26, 48, 'Mola Mlezi wa Musa na Harun.'),
(2981, 26, 49, '(Firauni) akasema: Je! Mmemuamini kabla sijakuruhusuni? Bila ya shaka yeye basi ndiye mkubwa wenu aliye kufunzeni uchawi. Basi mtakuja jua! Nitakata mikono yenu na miguu yenu kwa kutafautisha. Na nitakubandikeni misalabani nyote.'),
(2982, 26, 50, 'Wakasema: Haidhuru, kwani sisi ni wenye kurejea kwa Mola wetu Mlezi.'),
(2983, 26, 51, 'Hakika sisi tunatumai Mola wetu Mlezi atatusamehe makosa yetu, kwa kuwa ndio wa kwanza wa kuamini.'),
(2984, 26, 52, 'Na tulimuamrisha Musa kwa wahyi: Nenda na waja wangu wakati wa usiku. Kwa hakika mtafuatwa.'),
(2985, 26, 53, 'Basi Firauni akawatuma mijini wapiga mbiu za mgambo.'),
(2986, 26, 54, '(Wakieneza): Hakika hawa ni kikundi kidogo.'),
(2987, 26, 55, 'Nao wanatuudhi.'),
(2988, 26, 56, 'Na sisi ni wengi, wenye kuchukua hadhari.'),
(2989, 26, 57, 'Basi tukawatoa katika mabustani na chemchem,'),
(2990, 26, 58, 'Na makhazina, na vyeo vya hishima,'),
(2991, 26, 59, 'Kadhaalika; na tukawarithisha hayo Wana wa Israili.'),
(2992, 26, 60, 'Basi wakawafuata lilipo chomoza jua.'),
(2993, 26, 61, 'Na yalipo onana majeshi mawili haya, watu wa Musa wakasema: Hakika sisi bila ya shaka tumepatikana!'),
(2994, 26, 62, '(Musa) akasema: Hasha! Hakika yu pamoja nami Mola wangu Mlezi. Yeye ataniongoa!'),
(2995, 26, 63, 'Tulimletea wahyi Musa tukamwambia: Piga bahari kwa fimbo yako. Basi ikatengana, na kila sehemu ikawa kama mlima mkubwa.'),
(2996, 26, 64, 'Na tukawajongeza hapo wale wengine.'),
(2997, 26, 65, 'Tukawaokoa Musa na walio kuwa pamoja naye wote.'),
(2998, 26, 66, 'Kisha tukawazamisha hao wengine.'),
(2999, 26, 67, 'Hakika bila ya shaka katika hayo ipo Ishara; lakini wengi wao si katika wenye kuamini.'),
(3000, 26, 68, 'Na kwa hakika Mola wako Mlezi ndiye Mwenye nguvu Mwenye kurehemu.'),
(3001, 26, 69, 'Na wasomee khabari za Ibrahim.'),
(3002, 26, 70, 'Alipo mwambia baba yake na kaumu yake: Mnaabudu nini?'),
(3003, 26, 71, 'Wakasema: Tunaabudu masanamu, daima tunayanyenyekea.'),
(3004, 26, 72, 'Akasema: Je! Yanakusikieni mnapo yaita?'),
(3005, 26, 73, 'Au yanakufaeni, au yanakudhuruni?'),
(3006, 26, 74, 'Wakasema: Bali tumewakuta baba zetu wakifanya hivyo hivyo.'),
(3007, 26, 75, 'Je! Mmewaona hawa mnao waabudu-'),
(3008, 26, 76, 'Nyinyi na baba zenu wa zamani?'),
(3009, 26, 77, 'Kwani hakika hao ni adui zangu, isipo kuwa Mola Mlezi wa walimwengu wote.'),
(3010, 26, 78, 'Ambaye ndiye aliye niumba, na Yeye ndiye ananiongoa,'),
(3011, 26, 79, 'Na ambaye ndiye ananilisha na kuninywesha.'),
(3012, 26, 80, 'Na ninapo ugua ni Yeye ndiye anaye niponesha.'),
(3013, 26, 81, 'Na ambaye atanifisha, na kisha atanihuisha.'),
(3014, 26, 82, 'Na ambaye ndiye ninaye mtumai kunisamehe makosa yangu Siku ya Malipo.'),
(3015, 26, 83, 'Mola wangu Mlezi! Nitunukie hukumu na uniunganishe na watendao mema.'),
(3016, 26, 84, 'Na unijaalie nitajwe kwa wema na watu watakao kuja baadaye.'),
(3017, 26, 85, 'Na unijaalie katika warithi wa Bustani za neema.'),
(3018, 26, 86, 'Na umsamehe baba yangu, kwani hakika alikuwa miongoni mwa wapotovu.'),
(3019, 26, 87, 'Wala usinihizi Siku watapo fufuliwa.'),
(3020, 26, 88, 'Siku ambayo kwamba mali hayato faa kitu wala wana.'),
(3021, 26, 89, 'Isipo kuwa mwenye kumjia Mwenyezi Mungu na moyo safi.'),
(3022, 26, 90, 'Na Pepo itasogezwa kwa wachamngu.'),
(3023, 26, 91, 'Na Jahannamu itadhihirishwa kwa wapotovu.'),
(3024, 26, 92, 'Na wataambiwa: Wako wapi mlio kuwa mkiwaabudu'),
(3025, 26, 93, 'Badala ya Mwenyezi Mungu? Je! Wanakusaidieni, au wanajisaidia wenyewe?'),
(3026, 26, 94, 'Basi watavurumizwa humo wao na hao wakosefu,'),
(3027, 26, 95, 'Na majeshi ya Ibilisi yote.'),
(3028, 26, 96, 'Watasema, na hali ya kuwa wanazozana humo:'),
(3029, 26, 97, 'Wallahi! Kwa yakini tulikuwa katika upotovu ulio dhaahiri,'),
(3030, 26, 98, 'Tulipo kufanyeni ni sawa na Mola Mlezi wa walimwengu wote.'),
(3031, 26, 99, 'Na hawakutupoteza ila wale wakosefu.'),
(3032, 26, 100, 'Basi hatuna waombezi.'),
(3033, 26, 101, 'Wala rafiki wa dhati.'),
(3034, 26, 102, 'Laiti tungeli pata kurejea tena tungeli kuwa katika Waumini.'),
(3035, 26, 103, 'Hakika katika haya ipo Ishara, lakini wengi wao hawakuwa wenye kuamini.'),
(3036, 26, 104, 'Na hakika Mola wako Mlezi ndiye Mwenye nguvu Mwenye kurehemu.'),
(3037, 26, 105, 'Kaumu ya Nuhu waliwakadhibisha Mitume.'),
(3038, 26, 106, 'Alipo waambia ndugu yao, Nuhu: Je! Hamchimngu?'),
(3039, 26, 107, 'Hakika mimi kwenu ni Mtume muaminifu.'),
(3040, 26, 108, 'Basi Mcheni Mwenyezi Mungu, na nit\'iini mimi.'),
(3041, 26, 109, 'Na sikutakini juu yake ujira, kwani ujira wangu hauko ila kwa Mola Mlezi wa walimwengu wote.'),
(3042, 26, 110, 'Basi Mcheni Mwenyezi Mungu, na nit\'iini mimi.'),
(3043, 26, 111, 'Wakasema: Je! Tukuamini wewe, hali wanao kufuata ni watu wa chini?'),
(3044, 26, 112, 'Akasema: Nayajuaje waliyo kuwa wakiyafanya?'),
(3045, 26, 113, 'Hisabu yao haiko ila kwa Mola wao Mlezi, laiti mngeli tambua!'),
(3046, 26, 114, 'Wala mimi si wa kuwafukuza Waumini.'),
(3047, 26, 115, 'Mimi si cho chote ila ni Mwonyaji wa dhaahiri shaahiri.'),
(3048, 26, 116, 'Wakasema: Ikiwa huachi, ewe Nuhu, bila ya shaka utapigwa mawe.'),
(3049, 26, 117, 'Akasema: Mola wangu Mlezi! Hakika watu wangu wamenikanusha.'),
(3050, 26, 118, 'Basi hukumu baina yangu na wao kwa hukumu yako, na uniokoe mimi na walio pamoja nami, Waumini.'),
(3051, 26, 119, 'Kwa hivyo tukamwokoa yeye na walio kuwa pamoja naye katika marikebu iliyo sheheni.'),
(3052, 26, 120, 'Kisha tukawazamisha baadaye walio bakia.'),
(3053, 26, 121, 'Hakika katika haya ipo Ishara, lakini si wengi wao walio kuwa Waumini.'),
(3054, 26, 122, 'Na hakika Mola wako Mlezi ndiye Yeye Mwenye nguvu Mwenye kurehemu.'),
(3055, 26, 123, 'Kina A\'d waliwakanusha Mitume.'),
(3056, 26, 124, 'Alipo waambia ndugu yao, Hud: Je! Hamchimngu?'),
(3057, 26, 125, 'Hakika mimi kwenu ni Mtume muaminifu.'),
(3058, 26, 126, 'Basi mcheni Mwenyezi Mungu, na nit\'iini mimi.'),
(3059, 26, 127, 'Wala sikutakeni juu yake ujira, kwani ujira wangu hauko ila kwa Mola Mlezi wa walimwengu wote.'),
(3060, 26, 128, 'Je! Mnajenga juu ya kila mnyanyuko kumbusho la kufanyia upuuzi?'),
(3061, 26, 129, 'Na mnajenga majengo ya fakhari kama kwamba mtaishi milele!'),
(3062, 26, 130, 'Na mnapo tumia nguvu mnatumia nguvu kwa ujabari.'),
(3063, 26, 131, 'Basi mcheni Mwenyezi Mungu, na nit\'iini.'),
(3064, 26, 132, 'Na mcheni aliye kupeni haya mnayo yajua.'),
(3065, 26, 133, 'Amekupeni wanyama wa kufuga na wana.'),
(3066, 26, 134, 'Na mabustani na chemchem.'),
(3067, 26, 135, 'Hakika mimi ninakukhofieni adhabu ya Siku Kubwa.'),
(3068, 26, 136, 'Wakasema: Ni mamoja kwetu, ukitupa mawaidha au hukuwa miongoni mwa watoao mawaidha.'),
(3069, 26, 137, 'Haya si chochote ila ni mtindo wa watu wa tokea zamani.'),
(3070, 26, 138, 'Wala sisi hatutaadhibiwa.'),
(3071, 26, 139, 'Wakamkanusha; nasi tukawaangamiza. Hakika bila ya shaka katika haya ipo Ishara. Lakini hawakuwa wengi wao wenye kuamini.'),
(3072, 26, 140, 'Na hakika Mola wako Mlezi ndiye Mwenye nguvu Mwenye kurehemu.'),
(3073, 26, 141, 'Kina Thamud waliwakanusha Mitume.'),
(3074, 26, 142, 'Alipo waambia ndugu yao Saleh: Je! Hamumchimngu?'),
(3075, 26, 143, 'Hakika mimi kwenu ni Mtume muaminifu.'),
(3076, 26, 144, 'Basi mcheni Mwenyezi Mungu, na nit\'iini.'),
(3077, 26, 145, 'Wala sikutakini ujira juu yake. Ujira wangu hauko ila kwa Mola Mlezi wa walimwengu wote.'),
(3078, 26, 146, 'Je! Mtaachwa salama usalimina katika haya yaliyopo hapa?'),
(3079, 26, 147, 'Katika mabustani, na chemchem?'),
(3080, 26, 148, 'Na konde na mitende yenye makole yaliyo wiva.'),
(3081, 26, 149, 'Na mnachonga milimani majumba kwa ustadi.'),
(3082, 26, 150, 'Basi mcheni Mwenyezi Mungu, na nit\'iini mimi.'),
(3083, 26, 151, 'Wala msit\'ii amri za walio pindukia mipaka,'),
(3084, 26, 152, 'Ambao wanafanya ufisadi katika nchi, wala hawatengenezi.'),
(3085, 26, 153, 'Wakasema: Hakika wewe ni miongoni mwa walio rogwa.'),
(3086, 26, 154, 'Wewe si chochote ila ni mtu kama sisi. Basi lete Ishara ukiwa wewe ni miongoni mwa wasemao kweli.'),
(3087, 26, 155, 'Akasema: Huyu ngamia jike; awe na zamu yake ya kunywa, na nyinyi muwe na zamu yenu ya kunywa katika siku maalumu.'),
(3088, 26, 156, 'Wala msimguse kwa uwovu, isije ikakushikeni adhabu ya Siku Kubwa.'),
(3089, 26, 157, 'Lakini wakamuuwa, na wakawa wenye kujuta.'),
(3090, 26, 158, 'Basi ikawapata adhabu. Hakika katika hayo ipo Ishara. Lakini hawakuwa wengi wao wenye kuamini.'),
(3091, 26, 159, 'Na hakika Mola wako Mlezi ndiye Mwenye nguvu Mwenye kurehemu.'),
(3092, 26, 160, 'Watu wa Lut\'i waliwakanusha Mitume.'),
(3093, 26, 161, 'Alipo waambia ndugu yao, Lut\'i: Je! Hamumchimngu?'),
(3094, 26, 162, 'Hakika mimi kwenu ni Mtume muaminifu kwenu.'),
(3095, 26, 163, 'Basi mcheni Mwenyezi Mungu na nit\'iini mimi.'),
(3096, 26, 164, 'Wala mimi sikutakini ujira juu yake; ujira wangu hauko ila kwa Mola Mlezi wa walimwengu wote.'),
(3097, 26, 165, 'Je! Katika viumbe vyote mnawaingilia wanaume?'),
(3098, 26, 166, 'Na mnaacha alicho kuumbieni Mola wenu Mlezi katika wake zenu? Ama kweli nyinyi ni watu mnao ruka mipaka!'),
(3099, 26, 167, 'Wakasema: Ewe Lut\'i! Usipo acha, hapana shaka utakuwa miongoni mwa wanao tolewa mji!'),
(3100, 26, 168, 'Akasema: Hakika mimi ni katika wanao kichukia hichi kitendo chenu.'),
(3101, 26, 169, 'Mola wangu Mlezi! Niokoe mimi na ahali zangu na haya wayatendayo.'),
(3102, 26, 170, 'Basi tukamwokoa yeye na ahali zake wote,'),
(3103, 26, 171, 'Isipo kuwa kikongwe katika walio kaa nyuma.'),
(3104, 26, 172, 'Kisha tukawaangamiza wale wengine.'),
(3105, 26, 173, 'Na tukawanyeshea mvua, basi ni ovu mno mvua ya waliyo onywa.'),
(3106, 26, 174, 'Hakika katika hayo ipo Ishara. Na hawakuwa wengi wao wenye kuamini.'),
(3107, 26, 175, 'Na hakika Mola wako Mlezi bila ya shaka ndiye Mwenye nguvu, Mwenye kurehemu.'),
(3108, 26, 176, 'Watu wa Machakani waliwakanusha Mitume.'),
(3109, 26, 177, 'Shuaibu alipo waambia: Je! Hamfanyi mkamchamngu?'),
(3110, 26, 178, 'Hakika mimi kwenu ni Mtume muaminifu.'),
(3111, 26, 179, 'Basi mcheni Mwenyezi Mungu, na nit\'iini mimi.'),
(3112, 26, 180, 'Wala sikutakini ujira juu yake. Ujira wangu hauko ila kwa Mola Mlezi wa walimwengu wote.'),
(3113, 26, 181, 'Timizeni kipimo sawa sawa, wala msiwe katika wanao punja.'),
(3114, 26, 182, 'Na pimeni kwa haki iliyo nyooka;'),
(3115, 26, 183, 'Wala msiwapunje watu vitu vyao, wala msifanye jeuri kwa ufisadi.'),
(3116, 26, 184, 'Na mcheni aliye kuumbeni nyinyi na vizazi vilivyo tangulia.'),
(3117, 26, 185, 'Wakasema: Hakika wewe ni katika walio rogwa.'),
(3118, 26, 186, 'Na wewe si chochote ila ni mtu tu kama sisi, na kwa yakini tunakuona wewe ni katika waongo.'),
(3119, 26, 187, 'Hebu tuangushie vipande kutoka mbinguni ikiwa wewe ni miongoni mwa wasemao kweli.'),
(3120, 26, 188, 'Akasema: Mola wangu Mlezi anajua zaidi mnayo yatenda.'),
(3121, 26, 189, 'Basi walimkanusha, na ikawashika adhabu ya siku ya kivuli. Hakika hiyo ilikuwa adhabu ya siku kubwa.'),
(3122, 26, 190, 'Hakika katika hayo ipo Ishara, lakini hawakuwa wengi wao wenye kuamini.'),
(3123, 26, 191, 'Na hakika Mola wako Mlezi bila ya shaka ndiye Yeye Mwenye nguvu Mwenye kurehemu.'),
(3124, 26, 192, 'Na bila ya shaka hii ni Uteremsho wa Mola Mlezi wa walimwengu wote.'),
(3125, 26, 193, 'Ameuteremsha Roho muaminifu,'),
(3126, 26, 194, 'Juu ya moyo wako, ili uwe katika Waonyaji,'),
(3127, 26, 195, 'Kwa ulimi wa Kiarabu ulio wazi.'),
(3128, 26, 196, 'Na hakika bila ya shaka haya yamo katika Vitabu vya kale.'),
(3129, 26, 197, 'Je! Haikuwa kwao ni Ishara kwamba wanayajua haya wanazuoni wa Wana wa Israili?'),
(3130, 26, 198, 'Na lau kuwa tungeli iteremsha juu ya mmoja wa wasio kuwa Waarabu,'),
(3131, 26, 199, 'Na akawasomea, wasingeli kuwa wenye kuamini.'),
(3132, 26, 200, 'Namna hivi tunaingiza katika nyoyo za wakosefu.'),
(3133, 26, 201, 'Hawataiamini mpaka waione adhabu chungu.'),
(3134, 26, 202, 'Basi itawafikia kwa ghafla, na hali hawana khabari.'),
(3135, 26, 203, 'Na watasema: Je, tutapewa muhula?'),
(3136, 26, 204, 'Basi, je, wanaihimiza adhabu yetu?'),
(3137, 26, 205, 'Waonaje kama tukiwastarehesha kwa miaka,'),
(3138, 26, 206, 'Kisha yakawafikia waliyo kuwa wakiahidiwa,'),
(3139, 26, 207, 'Yatawafaa nini yale waliyo stareheshewa?'),
(3140, 26, 208, 'Wala hatukuangamiza mji wo wote ila ulikuwa na waonyaji -'),
(3141, 26, 209, 'Kuwa ni ukumbusho. Wala Sisi hatukuwa wenye kudhulumu.'),
(3142, 26, 210, 'Wala Mashet\'ani hawakuteremka nayo,'),
(3143, 26, 211, 'Wala haitakiwi kwao, na wala hawawezi.'),
(3144, 26, 212, 'Hakika hao wametengwa na kusikia.'),
(3145, 26, 213, 'Basi usimwombe mungu mwingine pamoja na Mwenyezi Mungu ukawa miongoni mwa watakao adhibiwa.'),
(3146, 26, 214, 'Na uwaonye jamaa zako walio karibu nawe.'),
(3147, 26, 215, 'Na watwae kwa upole wanao kufuata miongoni mwa Waumini.'),
(3148, 26, 216, 'Na ikiwa watakuasi basi sema: Mimi najitenga mbali na hayo mnayo yatenda.'),
(3149, 26, 217, 'Na umtegemee Mtukufu Mwenye nguvu Mwenye kurehemu.'),
(3150, 26, 218, 'Ambaye anakuona unapo simama,'),
(3151, 26, 219, 'Na mageuko yako kati ya wanao sujudu.'),
(3152, 26, 220, 'Hakika Yeye ndiye Mwenye kusikia Mwenye kujua.'),
(3153, 26, 221, 'Je! Nikwambieni nani wanawashukia Mashet\'ani?'),
(3154, 26, 222, 'Wanamshukia kila mzushi mkubwa mwingi wa dhambi.'),
(3155, 26, 223, 'Wanawapelekea yale wanayo yasikia; na wengi wao ni waongo.'),
(3156, 26, 224, 'Na watungaji mashairi ni wapotofu ndio wanawafuata.'),
(3157, 26, 225, 'Je! Huwaoni kwamba wao wanatangatanga katika kila bonde?'),
(3158, 26, 226, 'Na kwamba wao husema wasiyo yatenda?'),
(3159, 26, 227, 'Isipo kuwa wale walio amini, na wakatenda mema, na wakamkumbuka Mwenyezi Mungu kwa wingi, na wakajitetea wanapo dhulumiwa. Na wanao dhulumu watakuja jua mgeuko gani watakao geuka.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'T\'aa Siin. (T\'. S.), Hizi ni Aya za Qur\'ani na Kitabu kinacho bainisha;'),
(3161, 27, 2, 'Uwongofu na bishara kwa Waumini,'),
(3162, 27, 3, 'Ambao wanashika Sala, na wanatoa Zaka, na Akhera wana yakini nayo.'),
(3163, 27, 4, 'Kwa hakika wale wasio iamini Akhera tumewapambia vitendo vyao, kwa hivyo wanatangatanga ovyo.'),
(3164, 27, 5, 'Hao ndio watapata adhabu mbaya kabisa, na hao katika Akhera ndio watapata khasara zaidi.'),
(3165, 27, 6, 'Na hakika wewe unafundishwa Qur\'ani inayo tokana kwake Mwenye hikima Mwenye kujua.'),
(3166, 27, 7, '(Kumbuka) Musa alipo waambia ahali zake: Hakika nimeona moto, nitakwenda nikuleteeni khabari, au nitakuleteeni kijinga kinacho waka ili mpate kuota moto.'),
(3167, 27, 8, 'Basi alipo fika ilinadiwa: Amebarikiwa aliomo katika moto huu na walioko pembezoni mwake. Na ametakasika Mwenyezi Mungu, Mola Mlezi wa walimwengu wote.'),
(3168, 27, 9, 'Ewe Musa! Hakika Mimi ndiye Mwenyezi Mungu, Mwenye nguvu, Mwenye hikima.'),
(3169, 27, 10, 'Na itupe fimbo yako! Alipo iona ikitikisika kama nyoka, aligeuka nyuma wala hakungoja. Ewe Musa! Usikhofu! Hakika hawakhofu mbele yangu Mitume.'),
(3170, 27, 11, 'Lakini aliye dhulumu, kisha akabadilisha wema baada ya ubaya, basi Mimi ni Mwenye kusamehe Mwenye kurehemu.'),
(3171, 27, 12, 'Na ingiza mkono wako katika mfuko wako, utatoka mweupe bila ya maradhi. Ni katika Ishara tisa za kumpelekea Firauni na kaumu yake. Hakika hao walikuwa watu waovu.'),
(3172, 27, 13, 'Basi zilipo wafikia Ishara zetu hizo zenye kuonyesha, wakasema: Huu ni uchawi dhaahiri.'),
(3173, 27, 14, 'Na wakazikataa kwa dhulma na kujivuna, na hali ya kuwa nafsi zao zina yakini nazo. Basi angalia ulikuwaje mwisho wa mafisadi!'),
(3174, 27, 15, 'Na hakika tuliwapa Daudi na Sulaiman ilimu, na wakasema: Alhamdu Lillahi, Kuhimidiwa kote ni kwa Mwenyezi Mungu aliye tufadhilisha kuliko wengi katika waja wake Waumini.'),
(3175, 27, 16, 'Na Sulaiman alimrithi Daudi, na akasema: Enyi watu! Tumefunzwa usemi wa ndege, na tumepewa kila kitu. Hakika hii ni fadhila iliyo dhaahiri.'),
(3176, 27, 17, 'Na alikusanyiwa Sulaiman majeshi yake ya majini, na watu, na ndege; nayo yakapangwa kwa nidhamu.'),
(3177, 27, 18, 'Mpaka walipo fika kwenye bonde la wadudu chungu, alisema mdudu chungu mmoja: Enyi wadudu chungu! Ingieni majumbani mwenu, asikupondeni Sulaiman na majeshi yake bila ya wao kutambua.'),
(3178, 27, 19, 'Basi (Sulaiman) akatabasamu akacheka kwa neno hili, na akasema: Ee Mola wangu Mlezi! Nizindue niishukuru neema yako uliyo nineemesha mimi na wazazi wangu, na nipate kutenda mema uyapendayo, na uniingize kwa rehema yako katika waja wako wema.'),
(3179, 27, 20, 'Na akawakagua ndege, na akasema: Imekuwaje, mbona simwoni Hud-hud, au amekuwa miongoni mwa walio ghibu?'),
(3180, 27, 21, 'Kwa yakini nitamuadhibu kwa adhabu kali, au nitamchinja, au aniletee hoja ya kutosha.'),
(3181, 27, 22, 'Basi (Hud-hud) akakaa si mbali na akasema: Nimegundua usilo gundua wewe, na ninakujia hivi kutoka nchi ya Sabai, na nakuletea khabari za yakini.'),
(3182, 27, 23, 'Hakika mimi nimemkuta mwanamke anawatawala, naye amepewa kila kitu, na anacho kiti cha enzi kikubwa.'),
(3183, 27, 24, 'Nimemkuta yeye na watu wake wanalisujudia jua badala ya Mwenyezi Mungu; na Shet\'ani amewapambia vitendo vyao na akawazuilia Njia. Kwa hivyo hawakuongoka,'),
(3184, 27, 25, 'Na hawamsujudii Mwenyezi Mungu ambaye huyatoa yaliyo fichikana katika mbingu na ardhi, na anayajua mnayo yaficha na mnayo yatangaza.'),
(3185, 27, 26, 'Mwenyezi Mungu - hapana mungu ila Yeye, Mola Mlezi wa A\'rshi tukufu..'),
(3186, 27, 27, 'Akasema (Sulaiman): Tutatazama umesema kweli au wewe ni miongoni mwa waongo.'),
(3187, 27, 28, 'Nenda na barua yangu hii na uwafikishie, kisha waache na utazame watarejesha nini.'),
(3188, 27, 29, '(Malkia) akasema: Enyi wahishimiwa! Hakika nimeletewa barua tukufu.'),
(3189, 27, 30, 'Imetoka kwa Sulaiman nayo ni: Kwa jina la Mwenyezi Mungu, Mwingi wa rehema, Mwenye kurehemu.'),
(3190, 27, 31, 'Msinifanyie jeuri, na fikeni kwangu nanyi mmekwisha kuwa wenye kusalimu amri.'),
(3191, 27, 32, 'Akasema: Enyi wahishimiwa! Nipeni shauri katika jambo langu hili, kwani mimi sikati shauri yoyote mpaka mhudhurie.'),
(3192, 27, 33, 'Wakasema: Sisi ni wenye nguvu na wakali kwa vita; na amri iko kwako, basi tazama ni nini unaamrisha.'),
(3193, 27, 34, 'Akasema: Hakika wafalme wanapo uingia mji wanauharibu, na wanawafanya wale watukufu wake kuwa wanyonge. Na hivyo ndivyo watendavyo.'),
(3194, 27, 35, 'Lakini mimi nitawapelekea zawadi, nami nitangoja watakayo rudi nayo wajumbe.'),
(3195, 27, 36, 'Basi alipo fika (mjumbe) kwa Sulaiman, alisema (Sulaiman): Hivyo ndio nyinyi mnanisaidia kwa mali? Aliyo nipa Mwenyezi Mungu ni bora zaidi kuliko aliyo kupeni nyinyi. Lakini nyinyi mnafurahia hii zawadi yenu.'),
(3196, 27, 37, 'Rejea kwao! Kwa yakini sisi tutawajia kwa majeshi wasio yaweza kuyakabili. Na hakika tutawatoa humo na hali wao wamekuwa madhalili na wanyonge.'),
(3197, 27, 38, 'Akasema: Enyi wahishimiwa! Ni nani kati yenu atakaye niletea kiti chake cha enzi kabla hawajanijia nao wamekwisha salimu amri.'),
(3198, 27, 39, 'Akasema: Afriti, katika majini: Mimi nitakuletea kabla hujainuka pahala pako hapo. Na mimi kwa hakika nina nguvu na muaminifu.'),
(3199, 27, 40, 'Akasema mwenye ilimu ya Kitabu: Mimi nitakuletea kabla ya kupepesa jicho lako. Basi alipo kiona kimewekwa mbele yake, akasema: Haya ni katika fadhila zake Mola wangu Mlezi, ili anijaribu nitashukuru au nitakufuru. Na mwenye kushukuru, kwa hakika, anashukuru kwa manufaa ya nafsi yake; na anaye kufuru, kwa hakika Mola wangu Mlezi ni Mkwasi na Karimu.'),
(3200, 27, 41, 'Akasema: Mgeuzieni kiti chake cha enzi hiki, tuone ataongoka, au atakuwa miongoni mwa wasio ongoka.'),
(3201, 27, 42, 'Basi (Malkia) alipo fika akaambiwa: Je! Kiti chako cha enzi ni kama hiki? Akasema: Kama kwamba ndicho hichi. (Sulaiman na watu wake wakasema): Na sisi tumepewa ilimu kabla yake (Malkia), na tukawa Waislamu.'),
(3202, 27, 43, 'Na yale aliyo kuwa akiyaabudu badala ya Mwenyezi Mungu yalimzuilia. Hakika yeye (Malkia) alikuwa katika kaumu ya makafiri.'),
(3203, 27, 44, 'Akaambiwa: Liingie behewa la hili jumba. Alipo liona alidhani ni maji, na akapandisha nguo mpaka kwenye miundi yake. (Sulaiman) akasema: Hakika hilo ni behewa lilio sakafiwa kwa viyoo! Akasema (Malkia): Mola wangu Mlezi! Mimi nimejidhulumu nafsi yangu, na sasa nanyenyekea pamoja na Sulaiman kwa Mwenyezi Mungu, Mola Mlezi wa walimwengu wote.'),
(3204, 27, 45, 'Na kwa kina Thamud tulimtuma ndugu yao Saleh kuwaambia: Muabuduni Mwenyezi Mungu. Basi wakawa makundi mawili yanayo gombana.'),
(3205, 27, 46, 'Akasema: Enyi watu wangu! Kwa nini mnauhimiza uwovu kabla ya wema? Kwa nini hamuombi msamaha kwa Mwenyezi Mungu ili mrehemewe?'),
(3206, 27, 47, 'Wakasema: Tuna kisirani nawe na wale walio pamoja nawe. Yeye akasema: Uaguzi wa ukorofi wenu uko kwa Mwenyezi Mungu; lakini nyinyi ni watu mnao jaribiwa.'),
(3207, 27, 48, 'Na walikuwamo mjini watu tisa wakifanya ufisadi katika nchi wala hawafanyi la maslaha.'),
(3208, 27, 49, 'Wakasema: Apishaneni kwa Mwenyezi Mungu tutamshambulia usiku yeye na ahali zake; kisha tutamwambia mrithi wake: Sisi hatukuona maangamizo ya watu wake, na sisi bila ya shaka tunasema kweli.'),
(3209, 27, 50, 'Basi wakapanga mipango yao, na Sisi tukapanga mipango yetu, na wao hawatambui.'),
(3210, 27, 51, 'Basi angalia ulivyo kuwa mwisho wa mipango yao, ya kwamba tuliwaangamiza wao pamoja na watu wao wote.'),
(3211, 27, 52, 'Basi hizo nyumba zao ni tupu kwa sababu ya walivyo dhulumu. Hakika bila ya shaka katika hayo ipo Ishara kwa watu wanao jua.'),
(3212, 27, 53, 'Na tukawaokoa wale walio amini na walio kuwa wanamchamngu.'),
(3213, 27, 54, 'Na Lut\'i alipo waambia watu wake: Je! Mnafanya uchafu nanyi mnaona?'),
(3214, 27, 55, 'Hivyo nyinyi mnawaingilia wanaume kwa matamanio badala ya wanawake? Hakika nyinyi ni watu mnao fanya ujinga kabisa!'),
(3215, 27, 56, 'Basi haikuwa jawabu ya watu wake ila ni kusema: Wafukuzeni wafwasi wa Lut\'i katika mji wenu. Hao ni watu wanao jitia usafi.'),
(3216, 27, 57, 'Basi tukamwokoa yeye na ahali zake, isipo kuwa mkewe; tukamkadiria katika walio baki nyuma.'),
(3217, 27, 58, 'Na tukawanyeshea mvua. Ni ovu kweli kweli hiyo mvua ya walio kwisha onywa.'),
(3218, 27, 59, 'Sema: Alhamdu Lillahi! Kuhimidiwa ni kwa Mwenyezi Mungu! Na amani ishuke juu ya waja wake alio wateuwa. Je! Mwenyezi Mungu ni bora, au wale wanao washirikisha naye?'),
(3219, 27, 60, 'AU NANI yule aliye ziumba mbingu na ardhi, na akakuteremshieni maji kutoka mbinguni, na kwa hayo tukaziotesha bustani zenye kupendeza? Nyinyi hamna uwezo wa kuiotesha miti yake. Je! Yupo mungu pamoja na Mwenyezi Mungu? Bali hao ni watu walio potoka.'),
(3220, 27, 61, 'Au nani yule aliye ifanya ardhi mahali pa kutua, na akajaalia ndani yake mito, na akaweka milima? Na akaweka baina ya bahari mbili kiziwizi? Je! Yupo mungu pamoja na Mwenyezi Mungu? Bali wengi wao hawajui.'),
(3221, 27, 62, 'Au nani yule anaye mjibu mwenye shida pale anapo mwomba, na akaiondoa dhiki, na akakufanyeni warithi wa ardhi? Je! Yupo mungu pamoja na Mwenyezi Mungu? Ni machache mnayo yazingatia.'),
(3222, 27, 63, 'Au nani yule anaye kuongoeni katika giza la bara na bahari, na akazipeleka pepo kuleta bishara kabla ya rehema zake? Je! Yupo mungu pamoja na Mwenyezi Mungu? Ametukuka Mwenyezi Mungu juu ya wanao washirikisha naye.'),
(3223, 27, 64, 'Au nani anaye uanzisha uumbaji, kisha akaurejesha? Na nani anaye kuruzukuni kutoka mbinguni na kwenye ardhi? Je! Yupo mungu pamoja na Mwenyezi Mungu? Sema: Leteni hoja zenu kama nyinyi ni wasema kweli.'),
(3224, 27, 65, 'Sema: Hapana katika mbingu na ardhi ajuaye ya ghaibu isipo kuwa Mwenyezi Mungu. Wala wao hawajui lini watafufuliwa.'),
(3225, 27, 66, 'Kwani huo ujuzi wao umefikilia kuijua Akhera? Bali wao wamo katika shaka nayo tu, bali wao ni vipofu nayo.'),
(3226, 27, 67, 'Na wakasema walio kufuru: Tutapo kuwa mchanga, sisi na baba zetu, hivyo kweli tutakuja tolewa?'),
(3227, 27, 68, 'Haya haya tuliahidiwa sisi na baba zetu zamani. Hayakuwa haya ila ni hadithi za uwongo za watu wa kale.'),
(3228, 27, 69, 'Sema: Nendeni katika ardhi, na tazameni ulikuwaje mwisho wa wakosefu.'),
(3229, 27, 70, 'Wala usiwahuzunukie wala usiwe katika dhiki kwa sababu ya hila zao wanazo zifanya.'),
(3230, 27, 71, 'Na wanasema: Ahadi hii itatokea lini, ikiwa mnasema kweli?'),
(3231, 27, 72, 'Sema: Asaa ni karibu kukufikieni sehemu ya yale mnayo yahimiza.'),
(3232, 27, 73, 'Na hakika Mola wako Mlezi ni Mwenye fadhila kwa watu, lakini wengi wao hawashukuru.'),
(3233, 27, 74, 'Na hakika Mola wako Mlezi anayajua yanayo ficha vifua vyao na wanayo yatangaza.'),
(3234, 27, 75, 'Na hakuna siri katika mbingu na ardhi ila imo katika Kitabu kinacho bainisha.'),
(3235, 27, 76, 'Hakika Qur\'ani hii inawasimulia Wana wa Israili mengi wanayo khitalifiana nayo.'),
(3236, 27, 77, 'Nayo hakika ni Uwongofu na Rehema kwa Waumini.'),
(3237, 27, 78, 'Hakika Mola wako Mlezi atawahukumu kwa hukumu yake, naye ni Mwenye nguvu Mjuzi.'),
(3238, 27, 79, 'Basi mtegemee Mweneyezi Mungu; hakika wewe uko juu ya Haki iliyo wazi.'),
(3239, 27, 80, 'Kwa hakika wewe huwezi kuwafanya maiti wasikie, wala kuwafanya viziwi wasikie wito, wanapo kwisha geuka kwenda zao.'),
(3240, 27, 81, 'Wala huwezi kuwaongoa vipofu waache upotovu wao. Huwezi kuwafanya wasikie isipo kuwa wenye kuziamini Ishara zetu; na hao ndio wat\'iifu.'),
(3241, 27, 82, 'Na itapo waangukia kauli juu yao, tutawatolea mnyama katika ardhi atakaye wasemeza, ya kuwa watu walikuwa hawana na yakini na Ishara zetu.'),
(3242, 27, 83, 'Na Siku tutapo kusanya kutoka kila umma kundi katika wanao kadhibisha Ishara zetu, nao watagawanywa mafungu mafungu.'),
(3243, 27, 84, 'Hata watakapo fika, atasema (Mwenyezi Mungu): Je! Nyinyi si mlizikanusha Ishara zangu pasipo kuzijua vyema? Au mlikuwa mkifanya nini?'),
(3244, 27, 85, 'Na kauli itawaangukia juu yao kwa vile walivyo dhulumu. Basi hao hawatasema lolote.'),
(3245, 27, 86, 'Je! Hawaoni kwamba sisi tumeufanya usiku ili watulie, na mchana wa kuangaza. Hakika katika hayo zipo Ishara kwa watu wanao amini.'),
(3246, 27, 87, 'Na Siku litapo pulizwa baragumu, watahangaika waliomo mbinguni na katika ardhi, ila amtakaye Mwenyezi Mungu. Na wote watamfikia nao ni wanyonge.'),
(3247, 27, 88, 'Na unaiona milima unaidhunia imetulia; nayo inakwenda kama mwendo wa mawingu. Huo ndio ufundi wa Mwenyezi Mungu aliye tengeneza vilivyo kila kitu. Hakika Yeye anazo khabari za yote myatendayo.'),
(3248, 27, 89, 'Watakao kuja na mema, watapata bora kuliko hayo. Nao watasalimika na mahangaiko ya Siku hiyo.'),
(3249, 27, 90, 'Na watakao kuja na uovu, basi zitasunukishwa nyuso zao Motoni. Je! Kwani mnalipwa isipo kuwa mliyo kuwa mkiyatenda?'),
(3250, 27, 91, 'Ama mimi nimeamrishwa nimuabudu Mola Mlezi wa mji huu aliye ufanya ni mtakatifu; na ni vyake Yeye tu vitu vyote. Na nimeamrishwa niwe miongoni mwa Waislamu, wenye kunyenyekea.'),
(3251, 27, 92, 'Na niisome Qur\'ani. Na mwenye kuongoka, basi ameongoka kwa faida ya nafsi yake; na aliye potea - basi sema: Hakika mimi ni miongoni mwa waonyaji.'),
(3252, 27, 93, 'Na sema: Alhamdu Lillahi, Sifa njema zote ni za Mwenyezi Mungu. Yeye atakuonyesheni Ishara zake, na mtazijua. Na Mola wako Mlezi si mwenye kughafilika na myatendayo.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'T\'A SIN MIM, (T\'. S. M.)'),
(3254, 28, 2, 'Hizi ni Aya za Kitabu kinacho bainisha.'),
(3255, 28, 3, 'Tunakusomea khabari za Musa na Firauni kwa Haki kwa ajili ya watu wanao amini.'),
(3256, 28, 4, 'Hakika Firauni alitakabari katika nchi, na akawagawa wananchi makundi mbali mbali. Akalidhoofisha taifa moja miongoni mwao, akiwachinja watoto wao wanaume na akiwaacha watoto wao wanawake. Hakika yeye alikuwa katika mafisadi.'),
(3257, 28, 5, 'Na tukataka kuwafadhili walio dhoofishwa katika nchi hiyo na kuwafanya wawe waongozi na kuwafanya ni warithi.'),
(3258, 28, 6, 'Na kuwapa nguvu katika nchi, na kutokana na wao kuwaonyesha kina Firauni na Hamana na majeshi yao mambo yale waliyo kuwa wakiyaogopa.'),
(3259, 28, 7, 'Tulimfunulia mama yake Musa kwa kumwambia: Mnyonyeshe. Na utakapo mkhofia basi mtie mtoni, na usikhofu wala usihuzunike. Hakika Sisi tutamrudisha kwako na tutamfanya miongoni mwa Mitume.'),
(3260, 28, 8, 'Basi wakamwokota watu wa Firauni, aje kuwa adui kwao na huzuni. Hakika Firauni na Hamana na majeshi yao walikuwa wenye makosa.'),
(3261, 28, 9, 'Na mkewe Firauni alisema: Atakuwa kiburudisho cha macho kwangu na kwako. Msimuuwe! Huenda akatufaa, au tumpange kuwa mwenetu. Wala wao hawakutambua.'),
(3262, 28, 10, 'Na moyo wa mama yake Musa ukawa mtupu. Alikuwa karibu kumdhihirisha, ingeli kuwa hatukumtia nguvu moyo wake ili awe miongoni mwa Waumini.'),
(3263, 28, 11, 'Naye akamwambia dada yake Musa: Mfuatie. Basi naye akawa anamuangalia kwa mbali bila ya wao kujua.'),
(3264, 28, 12, 'Na tukamjaalia awakatae wanyonyeshaji wote tangu mwanzo, mpaka dada yake akasema: Je! Nikuonyesheni watu wa nyumba watakao mlea kwa ajili yenu, nao pia watakuwa wema kwake?'),
(3265, 28, 13, 'Basi tukamrudisha kwa mama yake ili macho yake yaburudike, wala asihuzunike. Na ajue ya kwamba ahadi ya Mwenyezi Mungu ni ya kweli, lakini wengi wao hawajui.'),
(3266, 28, 14, 'Na Musa alipo fika utu-uzima baraabara, tulimpa akili na ilimu. Na hivi ndivyo tunavyo walipa walio wema.'),
(3267, 28, 15, 'Na akaingia mjini wakati wa kughafilika wenyeji wake, na akakuta humo watu wawili wanapigana - mmoja ni katika wenzake, na mwengine katika maadui zake. Yule mwenzake alimtaka msaada kumpiga adui yake. Musa akampiga ngumi, akamuuwa. Akasema: Hiki ni kitendo cha Shet\'ani; hakika yeye ni adui, mpotezaji dhaahiri.'),
(3268, 28, 16, 'Akasema: Mola wangu Mlezi! Hakika mimi nimejidhulumu nafsi yangu; basi nisamehe. Akamsamehe; hakika Yeye ni Mwenye kusamehe Mwenye kurehemu.'),
(3269, 28, 17, 'Akasema: Mola wangu Mlezi! Kwa ulivyo nineemesha, basi mimi sitakuwa kabisa msaidizi wa wakosefu.'),
(3270, 28, 18, 'Akapambazukiwa mjini asubuhi naye ana khofu, akiangalia huku na huku. Mara yule yule aliye mtaka msaada jana akawa anampigia kelele amsaidie. Musa akamwambia: Hakika wewe ni mgomvi kweli kweli.'),
(3271, 28, 19, 'Basi alipo taka kumshika kwa nguvu yule aliye adui wao wote wawili, akasema: Ewe Musa! Unataka kuniuwa kama ulivyo muuwa mtu jana? Wewe hutaki ila kuwa jabari katika nchi, wala hutaki kuwa miongoni mwa wenye maslaha..'),
(3272, 28, 20, 'Na akaja mtu kutoka mwisho wa mji akipiga mbio, akasema: Ewe Musa! Wakubwa wanashauriana kukuuwa. Basi toka! Hakika mimi ni katika wanao kupa nasaha.'),
(3273, 28, 21, 'Basi akatoka, naye ana khofu, akiangalia huku na huku. Akasema: Mola wangu Mlezi! Niokoe na watu madhaalimu.'),
(3274, 28, 22, 'Na alipo elekea upande wa Madyana alisema: Asaa Mola wangu Mlezi akaniongoa njia iliyo sawa.'),
(3275, 28, 23, 'Na alipo yafikia maji ya Madyana alikuta umati wa watu wananywesha (wanyama wao), na akakuta karibu yao wanawake wawili wanawazuia wanyama wao. Akasema: Mna nini? Wakasema: Hatunyweshi sisi mpaka wamalize hao wachunga. Na baba yetu ni mzee sana.'),
(3276, 28, 24, 'Basi Musa akawanyweshea; kisha akarudi kivulini, na akasema: Mola wangu Mlezi! Hakika mimi ni mhitaji wa kheri utakayo niteremshia.'),
(3277, 28, 25, 'Basi mmoja katika wale wanawake wawili akamjia, naye anaona haya. Akasema: Baba yangu anakwita akulipe ujira wa kutunyweshea. Basi alipo mjia na akamsimulia visa vyake, alisema: Usiogope; umekwisha okoka kwenye watu madhaalimu.'),
(3278, 28, 26, 'Akasema mmoja katika wale wanawake: Ewe baba yetu! Muajiri huyu. Hakika mbora wa kumuajiri ni mwenye nguvu na muaminifu.'),
(3279, 28, 27, 'Akasema: Mimi nataka kukuoza mmojawapo katika binti zangu hawa wawili kwa kunitumikia miaka minane. Ukitimiza kumi, khiari yako; lakini mimi sitaki kukutaabisha. Inshallah utanikuta miongoni mwa watu wema.'),
(3280, 28, 28, 'Musa akasema: Hayo yamekwisha kuwa baina yangu na wewe. Muda mmojapo nikiumaliza sitiwi ubayani. Na Mwenyezi Mungu ni Mlinzi juu ya haya tunayo yasema.'),
(3281, 28, 29, 'Basi alipo timiza muda wake na akasafiri na ahali zake, aliona moto upande wa Mlima T\'uri. Akawaambia ahali zake: Ngojeni! Mimi nimeona moto, labda nitakuleteeni kutoka huko khabari au kijinga cha moto ili mpate kuota moto.'),
(3282, 28, 30, 'Basi alipo ufikia aliitwa kutoka ng\'ambo ya bonde la kuliani katika eneo lilio barikiwa kutoka kwenye mti: Ewe Musa! Hakika Mimi ni Mwenyezi Mungu, Mola Mlezi wa walimwengu wote.'),
(3283, 28, 31, 'Na tupa chini fimbo yako. Basi alipo iona ikitikisika kama nyoka, akarudi nyuma wala hakutazama. (Akaambiwa): Ewe Musa! Njoo mbele wala usiogope. Hakika wewe ni miongoni mwa walio salimika.'),
(3284, 28, 32, 'Ingiza mkono wako kwenye mfuko wako, utatoka mweupe pasipo na ubaya. Na jiambatishe mkono wako ukihisi khofu. Basi hizi ni dalili mbili zitokazo kwa Mola wako Mlezi kumpelekea Firauni na wakuu wa umma wake. Hakika hao walikuwa watu mafaasiki.'),
(3285, 28, 33, 'Akasema: Mola wangu Mlezi! Hakika mimi nilimuuwa mtu katika wao, kwa hivyo naogopa wataniuwa.'),
(3286, 28, 34, 'Na ndugu yangu Harun ni fasihi zaidi ulimi wake kuliko mimi. Basi mtume ende nami kunisaidia, ili anisadikishe. Hakika mimi nina khofu watanikadhibisha.'),
(3287, 28, 35, '(Mwenyezi Mungu) akasema: Tutautia nguvu mkono wako kwa nduguyo, na tutakupeni madaraka, hata wasikufikilieni. Kwa sababu ya Ishara zetu nyinyi na watakao kufuateni mtashinda.'),
(3288, 28, 36, 'Basi alipo wafikia Musa na Ishara zetu, walisema: Haya si chochote ila ni uchawi ulio zuliwa. Na hatukuyasikia haya kwa baba zetu wa zamani.'),
(3289, 28, 37, 'Na Musa akasema: Mola wangu Mlezi ni Mwenye kujua kabisa nani anaye kuja na uwongofu unao toka kwake, na nani atakaye kuwa na mwisho mwema wa makaazi. Hakika wenye kudhulumu hawatafanikiwa.'),
(3290, 28, 38, 'Na Firauni akasema: Enyi waheshimiwa! Sijui kama mnaye mungu asiye kuwa mimi. Ewe Haman! Niwashie moto unichomee udongo, unijengee mnara nipate kumchungulia mungu wa Musa. Na hakika mimi namwona yeye ni katika waongo.'),
(3291, 28, 39, 'Na alipanda kiburi yeye na majeshi yake katika nchi bila ya haki, na wakadhania kuwa hawatarudishwa kwetu.'),
(3292, 28, 40, 'Basi tukamshika yeye na majeshi yake, na tukawatupa baharini. Basi hebu angalia ulikuwaje mwisho wa wenye kudhulumu?'),
(3293, 28, 41, 'Na tuliwafanya ni waongozi waitao kwenye Moto. Na Siku ya Kiyama hawatanusuriwa.'),
(3294, 28, 42, 'Na tukawafuatishia laana katika dunia hii, na Siku ya Kiyama watakuwa miongoni mwa wanao chusha.'),
(3295, 28, 43, 'Na hakika bila ya shaka tulimpa Musa Kitabu baada ya kwisha ziangamiza kaumu za mwanzo, ili kiwafumbue macho watu, na kiwe uwongofu, na rehema, ili wapate kukumbuka.'),
(3296, 28, 44, 'Wala wewe hukuwa upande wa magharibi tulipo mpa Musa amri, wala hukuwa katika walio hudhuria.'),
(3297, 28, 45, 'Lakini Sisi tuliziumba kaumu, na ukawa mrefu umri juu yao. Wala hukuwa mkaazi na watu wa Madyana ukiwasomea Aya zetu. Lakini ni Sisi tulio kuwa tukiwatuma Mitume.'),
(3298, 28, 46, 'Wala hukuwa kando ya mlima tulipo nadi. Lakini ni rehema itokayo kwa Mola wako Mlezi ili uwaonye watu wasio pata kufikiwa na mwonyaji kabla yako, huenda wakakumbuka.'),
(3299, 28, 47, 'Na usije kuwasibu msiba kwa iliyo yatanguliza mikono yao, wakasema: Ewe Mola wetu Mlezi! Mbona hukututumia Mtume nasi, tukafuata Ishara zako, na tukawa miongoni mwa Waumini?'),
(3300, 28, 48, 'Ilipo wajia Haki kutoka kwetu walisema: Mbona hakupewa kama aliyo pewa Musa? Je! Kwani wao hawakuyakataa aliyo pewa Musa zamani? Walisema: Ni wachawi wawili wanasaidiana. Na wakasema: Hakika sisi tunawakataa wote.'),
(3301, 28, 49, 'Sema: Basi leteni Kitabu kinacho toka kwa Mwenyezi Mungu chenye kuongoka zaidi kuliko hivi viwili tukifuate, kama nyinyi mnasema kweli.'),
(3302, 28, 50, 'Na ikiwa hawakuitikii, basi jua kuwa wanafuata pumbao lao tu. Na nani aliye potea zaidi kumshinda anaye fuata pumbao lake bila ya uwongofu utokao kwa Mwenyezi Mungu. Hakika Mwenyezi Mungu hawaongoi watu wenye kudhulumu.'),
(3303, 28, 51, 'Na sasa kwa yakini tumewafikishia Neno ili wapate kukumbuka.'),
(3304, 28, 52, 'Wale tulio wapa Kitabu kabla yake wanakiamini hiki.'),
(3305, 28, 53, 'Na wanapo somewa wanasema: Tunaiamini. Hakika hii ni Haki inayo toka kwa Mola wetu Mlezi. Hakika sisi kabla yake tulikuwa ni Waislamu, tulio nyenyekea.'),
(3306, 28, 54, 'Basi hao watapewa ujira wao mara mbili kwa walivyo vumilia, na wakaondoa ubaya kwa wema, na wakatoa katika tuliyo waruzuku.'),
(3307, 28, 55, 'Na wanapo sikia upuuzi hujitenga nao na husema: Sisi tuna vitendo vyetu, na nyinyi mna vitendo vyenu. Salamun Alaikum, Amani juu yenu! Sisi hatutaki kujibizana na wajinga.'),
(3308, 28, 56, 'Kwa hakika wewe humwongoi umpendaye, lakini Mwenyezi Mungu humwongoa amtakaye. Na Yeye ndiye anawajua zaidi waongokao.'),
(3309, 28, 57, 'Na wakasema: Tukiufuata uwongofu huu pamoja nawe tutanyakuliwa kutoka nchi yetu. Je! Kwani Sisi hatukuwaweka imara katika pahala patakatifu, penye amani, ambapo huletewa matunda ya kila aina kuwa ni riziki itokayo kwetu? Lakini wengi wao hawajui.'),
(3310, 28, 58, 'Na miji mingapi iliyo jifakharisha tumeiangamiza! Na hayo maskani yao hayakukaliwa tena baada yao, ila kwa uchache tu. Na Sisi ndio tumekuwa warithi wao.'),
(3311, 28, 59, 'Na Mola wako Mlezi haangamizi miji mpaka ampeleke Mtume katika miji yake mikuu awasomee Ishara zetu. Wala hatuiangamizi miji mpaka watu wake wawe madhaalimu.'),
(3312, 28, 60, 'Na chochote mlicho pewa ni matumizi ya maisha ya dunia na pambo lake. Na yaliyoko kwa Mwenyezi Mungu ndio bora na yatakayo baki zaidi. Basi je, hamfahamu?'),
(3313, 28, 61, 'Je! Yule tuliye muahidi ahadi nzuri, tena naye akayapata, ni kama tuliye mstarehesha kwa starehe za maisha ya duniani, na kisha Siku ya Kiyama akawa miongoni mwa wanao hudhurishwa?'),
(3314, 28, 62, 'Na siku atakapo waita na akasema: Wako wapi mlio kuwa mkidai kuwa ni washirika wangu?'),
(3315, 28, 63, 'Watasema wale iliyo thibiti juu yao kauli: Mola wetu Mlezi! Hawa ndio tulio wapoteza. Tuliwapoteza kama tulivyo potea sisi. Tunajitenga nao mbele yako. Hawakuwa wakituabudu sisi.'),
(3316, 28, 64, 'Na itasemwa: Waiteni hao miungu wa kishirikina wenu! Basi watawaita, lakini hawatawaitikia, na wataiona adhabu; laiti wangeli kuwa wameongoka!'),
(3317, 28, 65, 'Na siku atapo waita na akasema: Mliwajibu nini Mitume?'),
(3318, 28, 66, 'Basi zitawapotea khabari siku hiyo, nao hawataulizana.'),
(3319, 28, 67, 'Ama mwenye kutubu na akaamini na akatenda mema, huyo atakuwa miongoni mwa wenye kufanikiwa.'),
(3320, 28, 68, 'Na Mola wako Mlezi huumba na huteuwa atakavyo. Viumbe hawana khiari. Mwenyezi Mungu ametukuka na ametakasika na hao wanao washirikisha naye.'),
(3321, 28, 69, 'Na Mola wako Mlezi anayajua yaliyo ficha vifua vyao, na wanayo yatangaza.'),
(3322, 28, 70, 'Naye ni Mwenyezi Mungu ambaye hapana mungu isipo kuwa Yeye. Sifa zote njema ni zake Yeye, mwanzo na mwisho. Na hukumu ni yake Yeye. Na kwake Yeye mtarejeshwa.'),
(3323, 28, 71, 'Sema: Mwaonaje, Mwenyezi Mungu angeli ufanya usiku umekukalieni moja kwa moja mpaka Siku ya Kiyama, mungu gani asiye kuwa Mwenyezi Mungu atakaye kuleteeni mwangaza? Basi je, hamsikii?'),
(3324, 28, 72, 'Sema: Mwaonaje, Mwenyezi Mungu angeli ufanya mchana umekukalieni moja kwa moja mpaka Siku ya Kiyama, mungu gani asiye kuwa Mwenyezi Mungu ataye kuleteeni usiku mkapumzika humo? Basi je, hamwoni?'),
(3325, 28, 73, 'Na ni katika rehema zake amekufanyieni usiku na mchana mpate kupumzika humo na mtafute fadhila zake, na ili mpate kushukuru.'),
(3326, 28, 74, 'Na siku atakapo waita na akasema: Wako wapi hao mlio kuwa mkidai kuwa ni washirika wangu?'),
(3327, 28, 75, 'Na tutatoa shahidi katika kila umma, na tutasema: Leteni hoja zenu! Hapo watajua kwamba hakika kweli ni ya Mwenyezi Mungu, na yatawapotea yale waliyo kuwa wakiyazua.'),
(3328, 28, 76, 'Hakika Qaruni alikuwa katika watu wa Musa, lakini aliwafanyia dhulma. Na tulimpa khazina ambazo funguo zake zinawatopeza watu wenye nguvu kuzichukua. Walipo mwambia watu wake: Usijigambe! Hakika Mwenyezi Mungu hawapendi wanao jigamba.'),
(3329, 28, 77, 'Na utafute, kwa aliyo kupa Mwenyezi Mungu, makaazi ya Akhera. Wala usisahau fungu lako la dunia. Nawe fanya wema kama Mwenyezi Mungu alivyo kufanyia wema wewe. Wala usitafute kufanya ufisadi katika ardhi. Hakika Mwenyezi Mungu hawapendi mafisadi.'),
(3330, 28, 78, 'Akasema: Kwa hakika nimepewa haya kwa sababu ya ilimu niliyo nayo. Je! Hakujua kwamba Mwenyezi Mungu kesha waangamiza, katika vizazi vya walio kabla yake, watu walio kuwa wenye ngubvu zaidi kuliko yeye, na wenye makundi makubwa zaidi kuliko yake. Na wakosefu hawataulizwa khabari ya dhambi zao.'),
(3331, 28, 79, 'Basi akawatokea watu wake katika pambo lake. Wakasema wale walio kuwa wanataka maisha ya duniani: Laiti tungeli kuwa tunayo kama aliyo pewa Qaruni! Hakika yeye ni mwenye bahati kubwa.'),
(3332, 28, 80, 'Na wakasema wale walio pewa ilimu: Ole wenu! Malipo ya Mwenyezi Mungu ni bora kwa mwenye kuamini na akatenda mema. Wala hawatapewa hayo isipo kuwa wenye subira.'),
(3333, 28, 81, 'Basi tukamdidimiza yeye na nyumba yake katika ardhi; wala halikuwepo kundi lolote la kumnusuru kwa Mwenyezi Mungu, wala hakuwa miongoni mwa wanao jitetea.'),
(3334, 28, 82, 'Na wale walio tamani kuwa pahala pake jana, wakawa wanasema: Kumbe Mwenyezi Mungu humkunjulia riziki amtakaye katika waja wake, na humdhikisha amtakaye. Ingeli kuwa Mwenyezi Mungu hakutufanyia hisani angeli tudidimiza. Kumbe makafiri hawafanikiwi!'),
(3335, 28, 83, 'Hayo ndiyo makaazi ya Akhera, tumewafanyia wale wasio taka kujitukuza duniani wala ufisadi. Na mwisho mwema ni wa wachamngu.'),
(3336, 28, 84, 'Atakaye tenda wema atapata malipo bora kuliko huo wema alio utenda. Na atakaye tenda uovu, hawalipwi watendao uovu ila waliyo kuwa wakiyatenda.'),
(3337, 28, 85, 'Hakika aliye kulazimisha kuifuata Qur\'ani hapana shaka atakurudisha pahala pa marejeo. Sema: Mola wangu Mlezi ndiye anaye mjua zaidi mwenye kuja na uwongofu, na nani aliyomo katika upotofu ulio dhaahiri.'),
(3338, 28, 86, 'Nawe hukuwa unataraji kuletewa Kitabu; lakini ni rehema tu ya Mola wako Mlezi. Basi usiwe msaidizi wa makafiri.'),
(3339, 28, 87, 'Wala wasikuzuie kuzifuata Aya za Mwenyezi Mungu baada ya kuteremshiwa wewe. Na lingania kwa Mola wako Mlezi, wala usiwe miongoni mwa washirikina.'),
(3340, 28, 88, 'Wala usimwombe pamoja na Mwenyezi Mungu mungu mwenginewe. Hapana mungu ila Yeye. Kila kitu kitaangamia isipo kuwa Yeye. Hukumu iko kwake, na kwake mtarejezwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Alif Lam Mim (A. L. M.)'),
(3342, 29, 2, 'Je! Wanadhani watu wataachwa kwa kuwa wanasema: Tumeamini. Nao wasijaribiwe?'),
(3343, 29, 3, 'Hakika tuliwajaribu walio kuwa kabla yao, na kwa yakini Mwenyezi Mungu atawatambulisha walio wa kweli na atawatambulisha walio waongo.'),
(3344, 29, 4, 'Au wanadhani wanao tenda maovu kwamba watatushinda? Hukumu mbaya hiyo wanayo hukumu.'),
(3345, 29, 5, 'Mwenye kutaraji kukutana na Mwenyezi Mungu, basi hakika miadi ya Mwenyezi Mungu itafika bila ya shaka. Naye ni Mwenye kusikia, Mwenye kujua.'),
(3346, 29, 6, 'Na anaye fanya juhudi basi bila ya shaka anafanya juhudi kwa ajili ya nafsi yake. Hakika Mwenyezi Mungu si mhitaji wa walimwengu.'),
(3347, 29, 7, 'Na walio amini na wakatenda mema, kwa yakini tutawafutia makosa yao, na tutawalipa bora ya waliyo kuwa wakiyatenda.'),
(3348, 29, 8, 'Na tumemuusia mwanaadamu kuwafanyia wema wazazi wake. Na ikiwa watakushikilia unishirikishe Mimi na usiyo kuwa na ujuzi nayo, basi usiwat\'ii. Kwangu Mimi ndio marejeo yenu, na nitakwambieni mliyo kuwa mkiyatenda.'),
(3349, 29, 9, 'Na walio amini na wakatenda mema, bila ya shaka tutawatia miongoni mwa watu wema.'),
(3350, 29, 10, 'Na katika watu wapo wanao sema: Tumemuamini Mwenyezi Mungu. Lakini wanapo pewa maudhi kwa ajili ya Mwenyezi Mungu, wanayafanya mateso ya watu kama ni adhabu ya Mwenyezi Mungu. Na inapo kuja nusura kutoka kwa Mola wako Mlezi husema: Hakika sisi tulikuwa pamoja nanyi. Kwani Mwenyezi Mungu hayajui yaliomo vifuani mwa walimwengu?'),
(3351, 29, 11, 'Na hapana shaka Mwenyezi Mungu atawatambulisha walio amini, na atawatambulisha wanaafiki.'),
(3352, 29, 12, 'Na walio kufuru waliwaambia walio amini: Fuateni njia yetu, nasi tutayabeba makosa yenu. Wala wao hawatabeba katika makosa yao chochote kile. Hakika hao ni waongo.'),
(3353, 29, 13, 'Na hapana shaka wataibeba mizigo yao na mizigo mingine pamoja na mizigo yao. Na kwa yakini wataulizwa Siku ya Kiyama juu ya waliyo kuwa wakiyazua.'),
(3354, 29, 14, 'Na hakika tulimtuma Nuhu kwa watu wake, na akakaa nao miaka elfu kasoro miaka khamsini. Basi tufani liliwachukua, nao ni madhaalimu.'),
(3355, 29, 15, 'Nasi tukamwokoa yeye na wenziwe wa katika safina, na tukaifanya kuwa ni Ishara kwa walimwengu wote.'),
(3356, 29, 16, 'Na Ibrahim pale alipo waambia watu wake: Muabuduni Mwenyezi Mungu, na mcheni Yeye. Hiyo ndiyo kheri yenu, ikiwa nyinyi mnajua.'),
(3357, 29, 17, 'Hakika nyinyi mnaabudu masanamu badala ya Mwenyezi Mungu, na mnazua uzushi. Hakika hao mnao waabudu badala ya Mwenyezi Mungu hawakumilikiini riziki yoyote. Takeni riziki kwa Mwenyezi Mungu, na mumuabudu Yeye, na mumshukuru Yeye. Kwake Yeye ndio mtarudishwa.'),
(3358, 29, 18, 'Na mkikadhibisha, basi kaumu kadhaa wa kadhaa za kabla yenu zilikwisha kadhibisha. Na si juu ya Mtume ila kufikisha Ujumbe waziwazi.'),
(3359, 29, 19, 'Je! Wao hawaoni jinsi Mwenyezi Mungu anavyo anzisha uumbaji, na kisha akarudisha tena. Hakika hayo kwa Mwenyezi Mungu ni mepesi.'),
(3360, 29, 20, 'Sema: Tembeeni katika ardhi na tazameni jinsi alivyo anzisha uumbaji; kisha Mwenyezi Mungu ndiye anaye umba umbo la baadaye. Hakika Mwenyezi Mungu ni Muweza juu ya kila kitu.'),
(3361, 29, 21, 'Humuadhibu amtakaye, na humrehemu amtakaye. Na kwake Yeye mtapelekwa.'),
(3362, 29, 22, 'Na nyinyi si wenye kushinda katika ardhi wala katika mbingu. Wala nyinyi hamna mlinzi wala msaidizi isipo kuwa Mwenyezi Mungu.'),
(3363, 29, 23, 'Na walio zikanusha Ishara za Mwenyezi Mungu na kukutana naye, hao ndio wenye kukata tamaa na rehema yangu, na hao ndio wenye kupata adhabu chungu.'),
(3364, 29, 24, 'Basi haikuwa jawabu ya watu wake ila ni kusema: Muuweni au mchomeni moto! Mwenyezi Mungu akamwokoa na moto. Hakika katika hayo zipo Ishara kwa watu wanao amini.'),
(3365, 29, 25, 'Na alisema: Hakika nyinyi mmeyashika masanamu badala ya Mwenyezi Mungu kwa mapenzi yaliyo baina yenu katika uhai wa duniani. Kisha Siku ya Kiyama mtakataana nyinyi kwa nyinyi, na mtalaaniana nyinyi kwa nyinyi. Na makaazi yenu ni Motoni. Wala hamtapata wa kukunusuruni.'),
(3366, 29, 26, 'Lut\'i akamuamini, na akasema: Mimi nahamia kwa Mola wangu Mlezi. Hakika Yeye ndiye Mwenye nguvu, Mwenye hikima.'),
(3367, 29, 27, 'Na tulimtunukia (Ibrahim) Is-haq na Yaa\'qub. Na tukajaalia katika dhuriya zake Unabii na Kitabu, na tukampa ujira wake katika dunia; naye hakika katika Akhera bila ya shaka ni katika watu wema.'),
(3368, 29, 28, 'Na pale Lut\'i alipo waambia watu wake: Hakika nyinyi mnafanya uchafu, ambao hapana mmoja katika walimwengu aliye kutangulieni kwa hayo.'),
(3369, 29, 29, 'Je! Nyinyi mnawaingilia wanaume, na mnaikata njia? Na katika mikutano yenu mnafanya maovu? Basi haikuwa jawabu ya watu wake isipo kuwa kusema: Tuletee hiyo adhabu ya Mwenyezi Mungu ikiwa wewe ni katika wasemao kweli.'),
(3370, 29, 30, 'Akasema: Ewe Mola wangu Mlezi! Ninusuru na watu mafisadi hawa!'),
(3371, 29, 31, 'Na wajumbe wetu walipo mjia Ibrahim na bishara, walisema: Hakika sisi hapana shaka tutawahiliki watu wa mji huu, kwani watu wake hakika wamekuwa madhaalimu.'),
(3372, 29, 32, 'Akasema: Hakika humo yumo Lut\'i. Wao wakasema: Sisi tunajua zaidi nani yumo humo. Hapana shaka tutamwokoa yeye na ahali zake, isipo kuwa mkewe aliye miongoni mwa watao kaa nyuma.'),
(3373, 29, 33, 'Na wajumbe wetu walipo mfikia Lut\'i, alihuzunika kwa ajili yao, na moyo uliona dhiki kwa ajili yao. Wakasema: Usiogope, wala usihuzunike. Hakika sisi tutakuokoa wewe na ahali zako, ila mkeo aliye miongoni mwa watao kaa nyuma.'),
(3374, 29, 34, 'Kwa yakini sisi tutawateremshia watu wa mji huu adhabu kutoka mbinguni kwa sababu ya uchafu wanao ufanya.'),
(3375, 29, 35, 'Na tumeacha katika mji huo Ishara ilio wazi kwa watu wanao tumia akili zao.'),
(3376, 29, 36, 'Na kwa Madyana tulimtuma ndugu yao Shuaibu, naye akasema: Enyi watu wangu! Muabuduni Mwenyezi Mungu na iogopeni Siku ya Akhera, wala msende katika ardhi mkifisidi.'),
(3377, 29, 37, 'Lakini walimkanusha basi ukawatwaa mtikiso wa ardhi, wakapambazukiwa ndani ya nyumba zao nao wamefudikia.'),
(3378, 29, 38, 'Na pia kina A\'di na Thamud. Nanyi yamekwisha kubainikieni kutokana na maskani zao. Na Shet\'ani aliwapambia vitendo vyao, na akawazuilia Njia, na hali walikuwa wenye kuona.'),
(3379, 29, 39, 'Na pia Qaruni na Firauni na Hamana! Na Musa aliwajia kwa Ishara waziwazi, lakini walijivuna katika nchi, wala hawakuweza kushinda.'),
(3380, 29, 40, 'Basi kila mmoja tulimtesa kwa mujibu wa makosa yake. Kati yao wapo tulio wapelekea kimbunga cha changarawe; na kati yao wapo walio nyakuliwa na ukelele; na kati yao wapo ambao tulio wadidimiza katika ardhi; na kati yao wapo tulio wazamisha. Wala Mwenyezi Mungu hakuwa mwenye kuwadhulumu, lakini walikuwa wenyewe wakijidhulumu nafsi zao.'),
(3381, 29, 41, 'Mfano wa walio wafanya walinzi badala ya Mwenyezi Mungu, ni mfano wa buibui alivyo jitandia nyumba. Na hakika nyumba dhaifu mno kuliko zote ni jumba la buibui, laiti kuwa wanajua.'),
(3382, 29, 42, 'Kwa hakika Mwenyezi Mungu anajua kila kitu wanacho kiomba badala yake Yeye. Na Yeye ndiye Mtukufu Mwenye nguvu Mwenye hikima.'),
(3383, 29, 43, 'Na hiyo ni mifano tunawapigia watu, na hawaifahamu ila wenye ilimu.'),
(3384, 29, 44, 'Mwenyezi Mungu ameumba mbingu na ardhi kwa Haki. Kwa hakika katika hayo ipo Ishara kwa Waumini.'),
(3385, 29, 45, 'SOMA uliyo funuliwa katika Kitabu, na ushike Sala. Hakika Sala inazuilia mambo machafu na maovu. Na kwa yakini kumdhukuru Mwenyezi Mungu ndilo jambo kubwa kabisa. Na Mwenyezi Mungu anayajua mnayo yatenda.'),
(3386, 29, 46, 'Wala msijadiliane na Watu wa Kitabu ila kwa njia iliyo nzuri kabisa, isipo kuwa wale walio dhulumu miongoni mwao. Na semeni: Tumeyaamini yaliyo teremshwa kwetu na yaliyo teremshwa kwenu. Na Mungu wetu na Mungu wenu ni Mmoja. Na sisi ni wenye kusilimu kwake.'),
(3387, 29, 47, 'Na namna hivi tumekuteremshia Kitabu (cha Qur\'ani). Basi wale tulio wapa Kitabu (cha Biblia) wataiamini (Qur\'ani), na miongoni mwa hawa (washirikina) wapo watakao iamini. Na hawazikatai Ishara zetu isipo kuwa makafiri.'),
(3388, 29, 48, 'Na wewe hukuwa kabla yake unasoma kitabu chochote, wala hukukiandika kwa mkono wako wa kulia. Ingeli kuwa hivyo wangeli fanya shaka wavunjifu.'),
(3389, 29, 49, 'Bali hii (Qur\'ani) ni Ishara zilizo wazi katika vifua vya walio pewa ilimu. Na hawazikatai Ishara zetu isipo kuwa wenye kudhulumu.'),
(3390, 29, 50, 'Na walisema: Mbona hakuteremshiwa Ishara kutoka kwa Mola wake Mlezi? Sema: Ishara ziko kwake Mwenyezi Mungu; ama mimi kwa hakika ni mwonyaji mwenye kubainisha tu.'),
(3391, 29, 51, 'Je! Kwani hayakuwatosha ya kwamba tumekuteremshia Kitabu hiki wanacho somewa? Hakika katika hayo zipo rehema na mawaidha kwa watu wanao amini.'),
(3392, 29, 52, 'Sema: Mwenyezi Mungu anatosha kuwa ni Shahidi baina yangu na nyinyi. Anayajua yaliomo katika mbingu na ardhi. Na wale wanao amini upotovu na wakamkataa Mwenyezi Mungu, hao ndio walio khasiri.'),
(3393, 29, 53, 'Na wanakuhimiza waletewe adhabu kwa haraka. Na lau kuwa hapana wakati maalumu ulio wekwa, basi adhabu ingeli wajia, na ingeli watokea kwa ghafla, na hali wao hawana khabari.'),
(3394, 29, 54, 'Wanakuhimiza waletewe adhabu kwa haraka, na hali Jahannamu kwa hakika imewazunguka makafiri!'),
(3395, 29, 55, 'Siku itapo wafunika adhabu hiyo kutoka juu yao na chini ya miguu yao, na atasema: Onjeni hayo mliyo kuwa mkiyatenda!'),
(3396, 29, 56, 'Enyi waja wangu mlio amini! Kwa hakika ardhi yangu ina wasaa. Basi niabuduni Mimi peke yangu.'),
(3397, 29, 57, 'Kila nafsi itaonja mauti. Kisha mtarudishwa kwetu.'),
(3398, 29, 58, 'Na ambao wameamini na wakatenda mema, bila ya shaka tutawaweka katika maghorofa ya Peponi yenye kupitiwa chini yake mito, wakidumu humo. Ni mwema ulioje huo ujira wa watendao, '),
(3399, 29, 59, 'Ambao walisubiri, na wakamtegemea Mola wao Mlezi.'),
(3400, 29, 60, 'Na wanyama wangapi hawawezi kujimilikia riziki zao! Mwenyezi Mungu anawaruzuku hao na nyinyi pia. Na Yeye ndiye Mwenye kusikia Mwenye kujua.'),
(3401, 29, 61, 'Na ukiwauliza; Nani aliye umba mbingu na ardhi na akafanya jua na mwezi kuti\'ii amri yake? Bila ya shaka, watasema: Mwenyezi Mungu. Wapi basi wanako geuzwa?'),
(3402, 29, 62, 'Mwenyezi Mungu humkunjulia, na humdhikishia, riziki amtakaye katika waja wake. Kwa hakika Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(3403, 29, 63, 'Na ukiwauliza: Ni nani anaye teremsha maji kutoka mbinguni, na akaihuisha ardhi baada ya kufa kwake? Bila ya shaka watasema: Ni Mwenyezi Mungu. Sema: Alhamdu Lillahi, Sifa njema zote ni za Mwenyezi Mungu. Bali wengi katika wao hawafahamu.'),
(3404, 29, 64, 'Na haya maisha ya dunia si chochote ila ni pumbao na mchezo. Na nyumba ya Akhera ndiyo maisha khasa; laiti wangeli kuwa wanajua!'),
(3405, 29, 65, 'Na wanapo panda katika marikebu, humwomba Mwenyezi Mungu kwa kumsafia ut\'iifu. Lakini anapo wavua wakafika nchi kavu, mara wanamshirikisha.'),
(3406, 29, 66, 'Wapate kuyakanya tuliyo wapa, na wajistareheshe. Lakini watakuja jua!'),
(3407, 29, 67, 'Je! Hawaoni ya kwamba tumeifanya nchi takatifu ina amani, na hali watu wengine wananyakuliwa kote kwa majirani zao? Je! Wanaamini upotovu, na neema za Mwenyezi Mungu wanazikataa?'),
(3408, 29, 68, 'Na nani dhaalimu mkubwa kuliko yule anaye mzulia uwongo Mwenyezi Mungu au anaye kanusha Haki inapo mjia? Je! Si katika Jahannamu ndio yatakuwa makaazi ya makafiri?'),
(3409, 29, 69, 'Na wanao fanya juhudi kwa ajili yetu, Sisi tutawaongoa kwenye njia zetu. Na hakika Mwenyezi Mungu yu pamoja na watu wema.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Alif Lam Mim (A. L. M.)'),
(3411, 30, 2, 'Warumi wameshindwa,'),
(3412, 30, 3, 'Katika nchi iliyo karibu. Nao baada ya kushindwa kwao watashinda'),
(3413, 30, 4, 'Katika miaka michache. Amri ni ya Mwenyezi Mungu kabla yake na baada yake. Na siku hiyo Waumini watafurahi.'),
(3414, 30, 5, 'Kwa nusura ya Mwenyezi Mungu humnusuru amtakaye. Naye ndiye Mwenye nguvu Mwenye kurehemu.'),
(3415, 30, 6, 'Ni ahadi ya Mwenyezi Mungu. Mwenyezi Mungu havunji ahadi yake. Lakini watu wengi hawajui.'),
(3416, 30, 7, 'Wanajua hali ya dhaahiri ya maisha ya dunia, na wameghafilika na Akhera.'),
(3417, 30, 8, 'Je! Hawajifikirii nafsi zao? Mwenyezi Mungu hakuumba mbingu na ardhi na viliomo ndani yake ila kwa Haki na kwa muda maalumu. Na hakika watu wengi bila ya shaka ni wenye kukataa kuwa watakutana na Mola wao Mlezi.'),
(3418, 30, 9, 'Je! Hawatembei katika ardhi wakaona ulivyo kuwa mwisho wa walio kuwa kabla yao? Nao walikuwa na nguvu zaidi kuliko wao, na wakaitimbua ardhi na wakaistawisha zaidi kuliko walivyo istawisha wao. Na Mitume wao wakawajia na dalili zilizo wazi. Basi hakuwa Mwenyezi Mungu ni mwenye kuwadhulumu, lakini walikuwa wakijidhulumu nafsi zao.'),
(3419, 30, 10, 'Kisha ulikuwa mwisho wa walio fanya ubaya ni kuzikanusha Ishara za Mwenyezi Mungu, na wakawa wanazifanyia maskhara.'),
(3420, 30, 11, 'Mwenyezi Mungu huanzisha uumbaji, tena akaurudisha mara ya pili, na kisha mtarejeshwa kwake.'),
(3421, 30, 12, 'Na itapo simama Saa wakosefu watakata tamaa.'),
(3422, 30, 13, 'Wala hawatakuwa na waombezi miongoni mwa walio kuwa wakiwashirikisha na Mungu; na wao wenyewe watawakataa hao washirikishwa wao.'),
(3423, 30, 14, 'Na siku itapo simama Saa, siku hiyo watagawanyika.'),
(3424, 30, 15, 'Ama walio amini na wakatenda mema watafurahishwa katika Bustani.'),
(3425, 30, 16, 'Na ama walio kufuru na wakazikanusha Ishara zetu na mkutano wa Akhera, hao basi watahudhurishwa katika adhabu.'),
(3426, 30, 17, 'Basi Subhanallah! Mtakaseni Mwenyezi Mungu jioni na asubuhi,'),
(3427, 30, 18, 'Na sifa zote njema ni zake katika mbingu na ardhi, na alasiri na adhuhuri.'),
(3428, 30, 19, 'Hukitoa kilicho hai kutokana na kilicho kufa, na hukitoa kilicho kufa kutokana na kilicho hai. Na huihuisha ardhi baada ya kufa kwake. Na kama hivyo ndivyo mtavyo fufuliwa.'),
(3429, 30, 20, 'Na katika Ishara zake ni kuwa kakuumbeni kwa udongo. Kisha mmekuwa watu mlio tawanyika kote kote.'),
(3430, 30, 21, 'Na katika Ishara zake ni kuwa amekuumbieni wake zenu kutokana na nafsi zenu ili mpate utulivu kwao. Naye amejaalia mapenzi na huruma baina yenu. Hakika katika haya bila ya shaka zipo Ishara kwa watu wanao fikiri.'),
(3431, 30, 22, 'Na katika Ishara zake ni kuumba mbingu na ardhi, na kutafautiana ndimi zenu na rangi zenu. Hakika katika hayo zipo Ishara kwa wajuzi.'),
(3432, 30, 23, 'Na katika Ishara zake ni kulala kwenu usiku na mchana, na kutafuta kwenu fadhila zake. Hakika katika haya bila ya shaka zipo Ishara kwa watu wanao sikia.'),
(3433, 30, 24, 'Na katika Ishara zake hukuonyesheni umeme kwa kukutieni khofu na tamaa. Na hukuteremshieni maji kutoka mbinguni, na kwa hayo huifufua ardhi baada ya kufa kwake. Hakika katika hayo zipo Ishara kwa watu wanao zingatia.'),
(3434, 30, 25, 'Na katika Ishara zake ni kuwa mbingu na ardhi zimesimama kwa amri yake. Kisha atakapo kuiteni mwito mmoja tu, hapo mtatoka kwenye ardhi.'),
(3435, 30, 26, 'Na vyake Yeye vilivyo mbinguni na katika ardhi. Vyote vinamt\'ii Yeye.'),
(3436, 30, 27, 'Na Yeye ndiye anaye anzisha uumbaji, kisha ataurudisha mara nyengine. Na jambo hili ni jepesi zaidi kwake. Naye ndiye mwenye mfano bora katika mbingu na ardhi. Na Yeye ndiye Mwenye nguvu, Mwenye hikima.'),
(3437, 30, 28, 'Amekupigieni mfano kutokana na nafsi zenu. Je! Katika hao iliyo wamiliki mikono yenu ya kulia mnao washirika katika hivyo tulivyo kuruzukuni, mkawa nyinyi katika hivyo ni sawasawa, mnawaogopa kama mnavyo ogopana wenyewe kwa wenyewe? Namna hivi tunazipambanua Aya zetu kwa watu wenye akili.'),
(3438, 30, 29, 'Bali walio dhulumu wamefuata mapendo ya nafsi zao pasina kujua. Basi nani atamwongoa ambaye Mwenyezi Mungu amemuacha apotee? Wala hao hawatakuwa na wa kuwanusuru.'),
(3439, 30, 30, 'Basi uelekeze uso wako sawasawa kwenye Dini - ndilo umbile la Mwenyezi Mungu alilo waumbia watu. Hapana mabadiliko katika uumbaji wa Mwenyezi Mungu. Hiyo ndiyo Dini iliyo nyooka sawa. Lakini watu wengi hawajui.'),
(3440, 30, 31, 'Muwe wenye kutubia kwake, na mcheni Yeye, na shikeni Sala, na wala msiwe katika washirikina.'),
(3441, 30, 32, 'Katika wale ambao kwamba wameigawanya dini yao, na wakawa makundi makundi, kila kikundi kinafurahia kilicho nacho.'),
(3442, 30, 33, 'Yakiwapata watu madhara humwomba Mola wao Mlezi nao wametubu kwake. Kisha akiwaonjesha rehema itokayo kwake, mara kikundi miongoni mwao humshirikisha Mola wao Mlezi,'),
(3443, 30, 34, 'Kwa kuyakataa tuliyo wapa. Basi stareheni. Mtakuja jua!'),
(3444, 30, 35, 'Au tumewateremshia hoja ambayo kwayo ndio wanafanya ushirikina?'),
(3445, 30, 36, 'Na watu tunapo waonjesha rehema huifurahia, na ukiwasibu uovu kwa iliyo yatanguliza mikono yao wenyewe, mara wanakata tamaa.'),
(3446, 30, 37, 'Kwani hawaoni kwamba Mwenyezi Mungu humkunjulia riziki amtakaye na humdhikisha amtakaye? Hakika katika hayo zipo Ishara kwa watu wanao amini.'),
(3447, 30, 38, 'Basi mpe jamaa haki yake, na masikini, na msafiri. Hayo ni kheri kwa watakao radhi ya Mwenyezi Mungu. Na hao ndio wenye kufanikiwa.'),
(3448, 30, 39, 'Na mnacho kitoa kwa riba ili kiongezeke katika mali ya watu, basi hakiongezeki mbele ya Mwenyezi Mungu. Lakini mnacho kitoa kwa Zaka kwa kutaka radhi ya Mwenyezi Mungu, basi hao ndio watao zidishiwa.'),
(3449, 30, 40, 'Mwenyezi Mungu ndiye aliye kuumbeni, kisha akakuruzukuni, kisha anakufisheni, na kisha anakufufueni. Je! Yupo yeyote katika hao mnao washirikisha na Mwenyezi Mungu afanyae lolote katika hayo? Yeye ametakasika, na ametukuka na hayo mnayo mshirikisha naye.'),
(3450, 30, 41, 'Ufisadi umedhihiri bara na baharini kwa iliyo yafanya mikono ya watu, ili Mwenyezi Mungu awaonyeshe baadhi ya waliyo yatenda. Huenda wakarejea kwa kutubu.'),
(3451, 30, 42, 'Sema: Safirini duniani muangalie ulikuwaje mwisho wa walio tangulia. Wengi wao walikuwa washirikina.'),
(3452, 30, 43, 'Basi uelekeze uso wako kwenye Dini Iliyo Nyooka kabla ya kufika Siku hiyo isiyo zuilika, inayo toka kwa Mwenyezi Mungu. Siku hiyo watu watatengana.'),
(3453, 30, 44, 'Anaye kufuru basi ukafiri wake utakuwa juu yake mwenyewe, na anaye tenda mema, basi wanazitengezea nafsi zao.'),
(3454, 30, 45, 'Ili awalipe walio amini na wakatenda mema kutokana na fadhila yake. Hakika Yeye hawapendi makafiri.'),
(3455, 30, 46, 'Na katika Ishara zake ni kuzituma pepo ziletazo bishara njema, na ili kukuonjesheni baadhi ya rehema zake, na ili majahazi yende kwa amri yake; na mtafute kutokana na fadhila yake, na ili mumshukuru.'),
(3456, 30, 47, 'Na hakika tulikwisha watuma Mitume kwa kaumu zao, na wakawajia kwa hoja zilizo wazi. Tukawaadhibu wale walio fanya ukosefu. Na ilikuwa ni haki juu yetu kuwanusuru Waumini.'),
(3457, 30, 48, 'Mwenyezi Mungu ndiye anaye zituma pepo zikayatimua mawingu, kisha akayatandaza mbinguni kama apendavyo. Na akayafanya mapande mapande, basi ukaiona mvua inatoka ndani yake. Anapo wafikishia awatakao katika waja wake, mara hao huwa na furaha.'),
(3458, 30, 49, 'Na ingawa kabla ya kuteremshiwa walikuwa wenye kukata tamaa.'),
(3459, 30, 50, 'Basi ziangalie athari za rehema ya Mwenyezi Mungu, jinsi anavyo ihuisha ardhi baada ya kufa kwake. Basi hakika huyo ndiye bila ya shaka Mwenye kuhuisha wafu. Na Yeye ni Muweza wa kila kitu.'),
(3460, 30, 51, 'Na tunge utuma upepo na wakauona umekuwa rangi ya manjano, juu ya hayo wange endelea kukufuru.'),
(3461, 30, 52, 'Kwani wewe hakika huwafanyi maiti wakasikia, wala viziwi wakasikia wito wakisha geuka kukupa mgongo.'),
(3462, 30, 53, 'Wala wewe huwaongoi vipofu na upotovu wao. Huwasikilizishi ila wanao ziamini Aya zetu. Hao ndio Waislamu, walio nyenyekea.'),
(3463, 30, 54, 'Mwenyezi Mungu ndiye aliye kuumbeni kutokana na udhaifu kisha akajaalia nguvu baada ya unyonge, kisha akajaalia baada ya nguvu unyonge tena na ukongwe. Anaumba atakavyo. Naye ndiye Mjuzi, Mwenye uweza.'),
(3464, 30, 55, 'Na siku itapo simama Saa wakosefu wataapa kwamba hawakukaa duniani ila saa moja tu. Hivyo ndivyo walivyo kuwa wakipotozwa.'),
(3465, 30, 56, 'Na walio pewa ilimu na Imani watasema: Hakika nyinyi mlikwisha kaa katika hukumu ya Mwenyezi Mungu mpaka Siku ya kufufuliwa. Basi hii ndiyo Siku ya kufufuliwa! Lakini nyinyi mlikuwa hamuijui.'),
(3466, 30, 57, 'Basi Siku hiyo hautawafaa walio dhulumu udhuru wao, wala haitotakiwa toba yao.'),
(3467, 30, 58, 'Na hakika tulikwisha wapigia watu kila mfano katika hii Qur\'ani. Na ukiwaletea Ishara yoyote hapana shaka walio kufuru watasema: Nyinyi si chochote ila ni waongo.'),
(3468, 30, 59, 'Hivi ndivyo Mwenyezi Mungu anavyo piga muhuri kwenye nyoyo za wasio jua.'),
(3469, 30, 60, 'Basi subiri. Hakika ahadi ya Mwenyezi Mungu ni ya haki. Wala wasikupaparishe hao wasio kuwa na yakini.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Alif Lam Mim (A. L. M.)'),
(3471, 31, 2, 'Hizi ni Aya za Kitabu chenye hikima.'),
(3472, 31, 3, 'Kuwa ni Uwongofu na Rehema kwa wafanyao wema,'),
(3473, 31, 4, 'Wanao shika Sala, na wanatoa Zaka, nao wana yakini na Akhera.'),
(3474, 31, 5, 'Hao ndio walio na uwongofu utokao kwa Mola wao Mlezi, na hao ndio wenye kufanikiwa.'),
(3475, 31, 6, 'Na miongoni mwa watu wapo wanao nunua maneno ya upuuzi, ili wawapoteze watu na njia ya Mwenyezi Mungu pasipo kujua, na wanaichukulia ni maskhara. Hao watapata adhabu ya kuwafedhehesha.'),
(3476, 31, 7, 'Na (mtu kama huyo) anapo somewa Aya zetu huzipa mgongo kwa kujivuna, kama kwamba hakuzisikia, kama kwamba masikioni mwake mna uziwi. Basi mbashirie kuwa atapata adhabu chungu.'),
(3477, 31, 8, 'Hakika walio amini na wakatenda mema watakuwa nazo Bustani za neema.'),
(3478, 31, 9, 'Watadumu humo - ni ahadi ya Mwenyezi Mungu ya kweli. Na Yeye ndiye Mwenye nguvu, Mwenye hikima.'),
(3479, 31, 10, 'Ameziumba mbingu bila ya nguzo mnazo ziona; na ameweka katika ardhi milima ili ardhi isikuyumbisheni; na ametawanya humo kila namna ya wanyama; na tumeteremsha maji kutoka mbinguni, na tukaotesha katika ardhi mimea mizuri ya kila namna.'),
(3480, 31, 11, 'Huu ni uumbaji wa Mwenyezi Mungu. Basi nionyesheni wameumba nini hao wasio kuwa Yeye! Lakini madhaalimu wamo katika upotovu uliyo dhaahiri.'),
(3481, 31, 12, 'Na tulimpa Luqman hikima, tukamwambia: Mshukuru Mwenyezi Mungu. Na mwenye kushukuru basi hakika anashukuru kwa manufaa ya nafsi yake. Na aliye kufuru, basi Mwenyezi Mungu ni Mkwasi, Msifiwa.'),
(3482, 31, 13, 'Na Luqman alipo mwambia mwanawe kwa kumpa mawaidha: Ewe mwanangu! Usimshirikishe Mwenyezi Mungu. Kwani hakika ushirikina bila ya shaka ni dhulma iliyo kubwa.'),
(3483, 31, 14, 'Na tumemuusia mtu kwa wazazi wake wawili. Mama yake ameichukua mimba yake kwa udhaifu juu ya udhaifu, na kumwachisha ziwa baada ya miaka miwili. (Tumemuusia): Nishukuru Mimi na wazazi wako. Ni kwangu Mimi ndiyo marudio.'),
(3484, 31, 15, 'Na pindi wakikushikilia kunishirikisha na ambayo huna ilimu nayo, basi usiwat\'ii. Lakini kaa nao kwa wema duniani, nawe ishike njia ya anaye elekea kwangu. Kisha marejeo yenu ni kwangu Mimi, na Mimi nitakuambieni mliyo kuwa mkiyatenda.'),
(3485, 31, 16, 'Ewe mwanangu! Kikiwapo kitu cha uzito wa chembe ya khardali kikawa ndani ya jabali au ndani ya mbingu au ndani ya ardhi, basi Mwenyezi Mungu atakileta. Hakika Mwenyezi Mungu ni Mjuzi wa yaliyo fichika, Mwenye khabari za yote.'),
(3486, 31, 17, 'Ewe mwanangu! Shika Sala, na amrisha mema, na kataza maovu, na subiri kwa yanayo kupata. Hayo ni katika mambo ya kuazimiwa..'),
(3487, 31, 18, 'Wala usiwabeuwe watu, wala usitembee katika nchi kwa maringo. Hakika Mwenyezi Mungu hampendi kila anaye jivuna na kujifakhirisha.'),
(3488, 31, 19, 'Na ushike mwendo wa katikati, na teremsha sauti yako. Hakika katika sauti mbaya zote bila ya shaka iliyo zidi ni sauti ya punda.'),
(3489, 31, 20, 'Kwani hamwoni ya kwamba Mwenyezi Mungu amevifanya vikutumikieni viliomo mbinguni na kwenye ardhi, na akakujalizieni neema zake, za dhaahiri na za siri? Na miongoni mwa watu wapo wanao bishana juu ya Mwenyezi Mungu pasipo ilimu, wala uwongofu, wala Kitabu chenye nuru.'),
(3490, 31, 21, 'Na wanapo ambiwa: Fuateni aliyo yateremsha Mwenyezi Mungu, husema: Bali tunafuata tuliyo wakuta nayo baba zetu. Je! Ijapo kuwa Shet\'ani anawaita kwenye adhabu ya Moto uwakao?'),
(3491, 31, 22, 'Na mwenye kuuelekeza uso wake kwa Mwenyezi Mungu, naye akawa anafanya wema, basi huyo hakika amekwisha kamata fundo lilio madhubuti. Na mwisho wa mambo yote ni kwa Mwenyezi Mungu.'),
(3492, 31, 23, 'Na anaye kufuru, isikuhuzunishe kufuru yake. Kwetu ndio marudio yao, na hapo tutawaambia waliyo yatenda. Hakika Mwenyezi Mungu ni Mjuzi wa yaliyomo vifuani.'),
(3493, 31, 24, 'Tunawastarehesha kwa uchache, kisha tutawasukuma kwenye adhabu ngumu.'),
(3494, 31, 25, 'Na ukiwauliza: Nani aliye ziumba mbingu na ardhi? Bila ya shaka watasema: Mwenyezi Mungu. Wewe sema: Alhamdulillahi! Sifa njema zote ni za Mwenyezi Mungu. Lakini wengi wao hawajui.'),
(3495, 31, 26, 'Ni vya Mwenyezi Mungu vyote viliomo mbinguni na katika ardhi. Hakika Mwenyezi Mungu ndiye Mkwasi, Mwenye kusifiwa.'),
(3496, 31, 27, 'Na lau kuwa miti yote iliyomo duniani ikawa ni kalamu, na bahari (ikawa wino), na ikaongezewa juu yake bahari nyengine saba, maneno ya Mwenyezi Mungu yasingeli kwisha. Hakika Mwenyezi Mungu ni Mwenye nguvu, Mwenye hikima.'),
(3497, 31, 28, 'Hakukuwa kuumbwa kwenu, wala kufufuliwa kwenu ila ni kama nafsi moja tu. Hakika Mwenyezi Mungu ni Mwenye kusikia Mwenye kuona.'),
(3498, 31, 29, 'Kwani huoni kwamba Mwenyezi Mungu huuingiza usiku katika mchana, na huuingiza mchana katika usiku, na amedhalilisha jua na mwezi? Vyote hivyo vinakwenda mpaka wakati ulio wekwa. Na Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(3499, 31, 30, 'Hivi ni kwa sababu hakika Mwenyezi Mungu ndiye wa kweli, na hakika wanacho kiomba ni cha uwongo. Na kwamba hakika Mwenyezi Mungu ndiye Mtukufu na ndiye Mkubwa.'),
(3500, 31, 31, 'Kwani huoni kwamba marikebu hupita baharini zikichukua neema za Mwenyezi Mungu, ili kukuonyesheni baadhi ya Ishara zake? Hakika katika haya zipo Ishara kwa kila mwenye kusubiri, mwenye kushukuru.'),
(3501, 31, 32, 'Na wimbi linapo wafunika kama wingu, wao humwomba Mwenyezi Mungu kwa kumsafia dini. Lakini anapo waokoa wakafika nchi kavu, wapo baadhi yao huenda mwendo wa sawa. Wala hazikanushi Ishara zetu ila aliye khaini kafiri mkubwa.'),
(3502, 31, 33, 'Enyi watu! Mcheni Mola wenu Mlezi, na iogopeni siku ambayo mzazi hatamfaa mwana, wala mwana hatamfaa mzazi kwa lolote. Hakika ahadi ya Mwenyezi Mungu ni ya kweli. Basi yasikudanganyeni maisha ya dunia, wala asikudanganyeni na Mwenyezi Mungu mdanganyifu.'),
(3503, 31, 34, 'Hakika kuijua Saa (ya Kiyama) kuko kwa Mwenyezi Mungu. Na Yeye ndiye anaye iteremsha mvua. Na anavijua viliomo ndani ya matumbo ya uzazi. Na haijui nafsi yoyote itachuma nini kesho. Wala nafsi yoyote haijui itafia nchi gani. Hakika Mwenyezi Mungu ndiye Mwenye kujua, Mwenye khabari.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Alif Lam Mim (A. L. M.)'),
(3505, 32, 2, 'Huu ni mteremsho wa Kitabu kisicho kuwa na shaka yoyote kinacho toka kwa Mola Mlezi wa walimwengu wote.'),
(3506, 32, 3, 'Au wanasema: Amekizua? Bali hichi ni Kweli iliyo toka kwa Mola wako Mlezi ili uwaonye watu wasio fikiwa na mwonyaji kabla yako; huenda wakaongoka.'),
(3507, 32, 4, 'Mwenyezi Mungu ndiye aliye umba mbingu na ardhi na viliomo baina yao kwa siku sita, na akatawala kwenye Kiti cha Enzi. Nyinyi hamna mlinzi wala mwombezi isipo kuwa Yeye tu. Basi, je, hamkumbuki?'),
(3508, 32, 5, 'Anapitisha mambo yote yalio baina mbingu na ardhi, kisha yanapanda kwake kwa siku ambayo kipimo chake ni miaka elfu kwa mnavyo hisabu nyinyi.'),
(3509, 32, 6, 'Huyu ndiye Mwenye kuyajua yasiyo onekana na yanayo onekana. Mwenye nguvu, Mwenye kurehemu.'),
(3510, 32, 7, 'Ambaye ametengeneza vizuri umbo la kila kitu; na akaanzisha kumuumba mtu kwa udongo.'),
(3511, 32, 8, 'Na kisha akajaalia uzao wake utokane na kizazi cha maji madhaifu.'),
(3512, 32, 9, 'Kisha akamtengeneza, na akampulizia roho yake, na akakujaalieni kusikia na kuona, na nyoyo za kufahamu. Ni uchache mnavyo shukuru.'),
(3513, 32, 10, 'Nao husema: Tutapo kwisha potea chini ya ardhi, ni kweli tutarudishwa katika umbo jipya? Bali wao wanakanusha kwamba watakutana na Mola wao Mlezi.'),
(3514, 32, 11, 'Sema: Atakufisheni Malaika wa mauti aliye wakilishwa juu yenu; kisha mtarejeshwa kwa Mola wenu Mlezi.'),
(3515, 32, 12, 'Na ungeli waona wakosefu wanvyo inamisha vichwa vyao mbele ya Mola wao Mlezi, (wakisema): Mola wetu Mlezi! Tumesha ona, na tumesha sikia. Turejeshe tukatende mema, kwani hakika sisi tumekwisha kuwa na yakini sasa.'),
(3516, 32, 13, 'Na lau tungeli taka tunge mpa kila mtu uwongofu wake. Lakini imekwisha kuwa kauli iliyo toka kwangu: Kwa yakini nitaijaza Jahannamu kwa wote hawa, majini na watu.'),
(3517, 32, 14, 'Basi onjeni kwa vile mlivyo usahau mkutano wa Siku yenu hii. Na Sisi hakika tunakusahauni. Basi onjeni adhabu ya milele kwa yale mliyo kuwa mkiyatenda.'),
(3518, 32, 15, 'Hakika wanao ziamini Aya zetu ni hao tu ambao wakikumbushwa hizo, basi wao huanguka kusujudu, na humsabihi Mola wao Mlezi kwa kumhimidi, nao hawajivuni.'),
(3519, 32, 16, 'Mbavu zao zinaachana na vitanda kwa kumwomba Mola wao Mlezi kwa khofu na kutumaini, na hutoa kutokana na tulivyo waruzuku.'),
(3520, 32, 17, 'Nafsi yoyote haijui waliyo fichiwa katika hayo yanayo furahisha macho - ni malipo ya yale waliyo kuwa wakiyatenda.'),
(3521, 32, 18, 'Ati aliye Muumini atakuwa sawa na aliye mpotovu? Hawawi sawa.'),
(3522, 32, 19, 'Ama walio amini na wakatenda mema, watakuwa nazo Bustani za makaazi mazuri. Ndio pa kufikia kwa waliyo kuwa wakiyatenda.'),
(3523, 32, 20, 'Na ama wale walio tenda uovu, basi makaazi yao ni Motoni. Kila wakitaka kutoka humo watarudishwa humo humo. Na wataambiwa: Onjeni adhabu ya Moto ambayo mliyo kuwa mkiikanusha.'),
(3524, 32, 21, 'Na tutawaonjesha adhabu khafifu kabla ya adhabu kubwa, huenda labda watarejea.'),
(3525, 32, 22, 'Na ni nani dhaalimu mkubwa kuliko yule anaye kumbushwa kwa Ishara za Mola wake Mlezi, kisha akazikataa? Hakika Sisi tutajilipiza kwa wakosefu.'),
(3526, 32, 23, 'Hakika Sisi tulikwisha mpa Musa Kitabu. Basi usiwe na shaka kuwa kakipokea. Na tukakifanya kuwa ni uwongofu kwa Wana wa Israili.'),
(3527, 32, 24, 'Na tukawafanya miongoni mwao waongozi wanao ongoa watu kwa amri yetu, walipo subiri na wakawa na yakini na Ishara zetu.'),
(3528, 32, 25, 'Hakika Mola wako Mlezi ndiye atakaye fafanua baina yao Siku ya Kiyama katika waliyo kuwa wakikhitalifiana.'),
(3529, 32, 26, 'Je! Haijawabainikia kwamba kaumu ngapi tuliziangamiza kabla yao, nao wanapita katika maskani zao hizo? Hakika katika hayo zipo Ishara. Basi, je, hawasikii?'),
(3530, 32, 27, 'Je! Hawaoni ya kwamba tunayapeleka maji kwenye ardhi kavu, kisha kwayo tunaitoa mimea wanayo kula wanyama wao na wao wenyewe? Je! Hawaoni?'),
(3531, 32, 28, 'Wanasema: Ushindi huu ni lini, kama mnasema kweli?'),
(3532, 32, 29, 'Sema: Siku ya Ushindi, wale walio kufuru imani yao haitawafaa kitu, wala wao hawatapewa muhula.'),
(3533, 32, 30, 'Basi wapuuze, nawe ngonja; hakika wao wanangoja.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Ewe Nabii! Mche Mwenyezi Mungu wala usiwat\'ii makafiri na wanaafiki. Hakika Mwenyezi Mungu ni Mwenye kujua, Mwenye hikima.'),
(3535, 33, 2, 'Na fuata uliyo funuliwa kwa wahyi kutokana na Mola wako Mlezi. Hakika Mwenyezi Mungu anazo khabari ya mnayo yatenda.'),
(3536, 33, 3, 'Na mtegemee Mwenyezi Mungu. Na Mwenyezi Mungu anatosha kuwa ni wa kutegemewa.'),
(3537, 33, 4, 'Mwenyezi Mungu hakumwekea mtu yeyote kuwa ana nyoyo mbili ndani ya mwili wake. Wala hakuwafanya wake zenu - ambao mnawalinganisha migongo yao na migongo ya mama zenu - kuwa ni mama zenu. Wala hakuwafanya watoto wenu wa kupanga kuwa ni wana wenu khasa. Hayo ni maneno ya vinywa vyenu tu. Na Mwenyezi Mungu ndiye anaye sema kweli, naye ndiye anaye ongoa Njia.'),
(3538, 33, 5, 'Waiteni kwa baba zao, maana huo ndio uadilifu zaidi mbele ya Mwenyezi Mungu. Na ikiwa hamwajui baba zao, basi ni ndugu zenu katika Dini, na rafiki zenu. Wala si lawama juu yenu kwa mlivyo kosea. Lakini ipo lawama katika yale ziliyo fanya nyoyo zenu kwa makusudi. Na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(3539, 33, 6, 'Nabii ni bora zaidi kwa Waumini kuliko nafsi zao. Na wake zake ni mama zao. Na jamaa ni karibu wao kwa wao katika Kitabu cha Mwenyezi Mungu kuliko Waumini wengine na Wahamiaji. Ila muwe mnawafanyia wema marafiki zenu. Haya yamekwisha andikwa Kitabuni.'),
(3540, 33, 7, 'Na tulipo chukua ahadi kwa Manabii na kwako wewe, na Nuhu na Ibrahim na Isa mwana wa Mariamu, na tulichukua kwao ahadi ngumu,'),
(3541, 33, 8, 'Ili (Mwenyezi Mungu) awaulize wakweli juu ya ukweli wao. Na amewaandalia makafiri adhabu chungu.'),
(3542, 33, 9, 'Enyi mlio amini! Zikumbukeni neema za Mwenyezi Mungu zilio juu yenu. Pale yalipo kufikilieni majeshi, nasi tukayapelekea upepo, na majeshi msiyo yaona. Na Mwenyezi Mungu anayaona mnayo yatenda.'),
(3543, 33, 10, 'Walipo kujieni kutoka juu yenu, na kutoka chini yenu; na macho yalipo kodoka, na nyoyo zikapanda kooni, nanyi mkamdhania Mwenyezi Mungu dhana mbali mbali.'),
(3544, 33, 11, 'Hapo ndipo Waumini walipo jaribiwa, na wakatikiswa mtikiso mkali.'),
(3545, 33, 12, 'Na walipo sema wanaafiki na wale wenye maradhi katika nyoyo zao: Mwenyezi Mungu na Mtume wake hawakutuahidi ila udanganyifu tu.'),
(3546, 33, 13, 'Na kundi moja katika miongoni mwao lilipo sema: Enyi watu wa Yathrib! Hapana kukaa nyinyi! Rudini. Na kundi jingine miongoni mwao likaomba ruhusa kwa Mtume kwa kusema: Hakika nyumba zetu ni tupu. Wala hazikuwa tupu; hawakutaka ila kukimbia tu.'),
(3547, 33, 14, 'Na lau kuwa wangeli ingiliwa kwa pande zote, kisha wakatakiwa kufanya khiana, wangeli fanya, na wasingeli sita ila kidogo tu.'),
(3548, 33, 15, 'Na hakika walikwisha muahidi Mwenyezi Mungu kabla yake kwamba hawatageuza migongo yao. Na ahadi ya Mwenyezi Mungu ni yenye kuulizwa.'),
(3549, 33, 16, 'Sema: Kukimbia hakukufaeni kitu ikiwa mnakimbia mauti au kuuwawa, na hata hivyo hamtastareheshwa ila kidogo tu.'),
(3550, 33, 17, 'Sema: Ni nani wa kukulindeni na Mwenyezi Mungu pindi akikutakieni uovu, au akikutakieni rehema? Wala hawatapata mlinzi wala msaidizi isipo kuwa Mwenyezi Mungu.'),
(3551, 33, 18, 'Kwa hakika Mwenyezi Mungu anawajua hao wanao zuilia, na wanao waambia ndugu zao: Njooni kwetu! Wala hawaingii vitani ila kidogo tu.'),
(3552, 33, 19, 'Wana choyo juu yenu. Ikifika khofu utawaona wanakutazama na macho yao yanazunguka, kama yule ambaye aliye zimia kwa kukaribia mauti. Na khofu ikiondoka wanakuudhini kwa ndimi kali, nao ni mabakhili kwa kila la kheri. Hao hawakuamini, basi Mwenyezi Mungu amevibat\'ilisha vitendo vyao. Na hayo kwa Mwenyezi Mungu ni mepesi.'),
(3553, 33, 20, 'Wanafikiri yale makundi ya maadui hayajaondoka. Na yakija hayo makundi watatamani laiti wao wako majangwani pamoja na mabedui, wakiuliza khabari zenu. Na lau wakiwa pamoja nanyi hawapigani ila kidogo tu.'),
(3554, 33, 21, 'Hakika nyinyi mnayo ruwaza njema kwa Mtume wa Mwenyezi Mungu kwa anaye mtaraji Mwenyezi Mungu na Siku ya Mwisho, na akamkumbuka Mwenyezi Mungu sana.'),
(3555, 33, 22, 'Na Waumini walipo yaona makundi, walisema: Haya ndiyo aliyo tuahidi Mwenyezi Mungu na Mtume wake. Na Mwenyezi Mungu na Mtume wake wamesema kweli. Na hayo hayakuwazidisha ila Imani na ut\'iifu.'),
(3556, 33, 23, 'Miongoni mwa Waumini wapo watu walio timiza waliyo ahidiana na Mwenyezi Mungu. Baadhi yao wamekwisha kufa, na baadhi wanangojea, wala hawakubadilisha (ahadi) hata kidogo.'),
(3557, 33, 24, 'Ili Mwenyezi Mungu awalipe wakweli kwa sababu ya ukweli wao, na awaadhibu wanaafiki pindi akitaka, au apokee toba yao. Hakika Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(3558, 33, 25, 'Na Mwenyezi Mungu aliwarudisha nyuma makafiri juu ya ghadhabu yao; hawakupata kheri yoyote. Na Mwenyezi Mungu amewatosheleza Waumini katika vita. Na Mwenyezi Mungu ni Mwenye nguvu na uweza.'),
(3559, 33, 26, 'Na akawateremsha wale walio wasaidia (maadui) katika Watu wa Kitabu kutoka katika ngome zao; na akatia khofu katika nyoyo zao. Baadhi yao mkawa mnawauwa, na wengine mnawateka.'),
(3560, 33, 27, 'Na akakurithisheni ardhi zao na nyumba zao na mali zao, na ardhi msiyo pata kuikanyaga. Na Mwenyezi Mungu ni Mwenye uweza juu ya kila kitu.'),
(3561, 33, 28, 'Ewe Nabii! Waambie wake zako: Ikiwa mnataka maisha ya dunia na pambo lake, basi njooni, nitakupeni kitoka nyumba, na kukuacheni mwachano mzuri.'),
(3562, 33, 29, 'Na ikiwa mnamtaka Mwenyezi Mungu na Mtume wake na nyumba ya Akhera, basi Mwenyezi Mungu amewaandalia wafanyao mema, miongoni mwenu, malipo makubwa.'),
(3563, 33, 30, 'Enyi wake wa Nabii! Atakaye fanya uchafu dhaahiri miongoni mwenu, atazidishiwa adhabu mara mbili. Na hayo kwa Mwenyezi Mungu ni mepesi.'),
(3564, 33, 31, 'NA MIONGONI mwenu atakeye mt\'ii Mwenyezi Mungu na Mtume wake, na akatenda mema, tutampa malipo yake mara mbili, na tutamwandalia riziki ya ukarimu.'),
(3565, 33, 32, 'Enyi wake wa Nabii! Nyinyi si kama yeyote katika wanawake wengine. Kama mnamcha Mungu basi msiregeze sauti zenu, akaingia tamaa mwenye maradhi katika moyo wake. Na semeni maneno mema.'),
(3566, 33, 33, 'Na kaeni majumbani kwenu, wala msijishauwe kwa majishauwo ya kijahilia ya kizamani. Na shikeni Sala, na toeni Zaka, na mt\'iini Mwenyezi Mungu na Mtume wake. Hakika Mwenyezi Mungu anataka kukuondoleeni uchafu, enyi Watu wa Nyumba ya Mtume, na kukusafisheni baarabara.'),
(3567, 33, 34, 'Na kumbukeni yasomwayo majumbani mwenu katika Aya za Mwenyezi Mungu na hikima. Kwa hakika Mwenyezi Mungu ni Mjuzi wa mambo ya siri, na Mwenye khabari.'),
(3568, 33, 35, 'Hakika Waislamu wanaume na Waislamu wanawake, na Waumini wanaume na Waumini wanawake, na wat\'iifu wanaume na wat\'iifu wanawake, na wasemao kweli wanaume na wanawake, na wanao subiri wanaume na wanawake, na wanyenyekevu wanaume na wanawake, na watoao sadaka wanaume na wanawake, na wanao funga wanaume na wanawake, na wanao jihifadhi tupu zao wanaume na wanawake, na wanao mdhukuru Mwenyezi Mungu kwa wingi wanaume na wanawake, Mwenyezi Mungu amewaandalia msamaha na ujira mkubwa.'),
(3569, 33, 36, 'Haiwi kwa Muumini mwanamume wala Muumini mwanamke kuwa na khiari katika jambo, Mwenyezi Mungu na Mtume wake wanapo kata shauri katika jambo lao. Na mwenye kumuasi Mwenyezi Mungu na Mtume wake basi hakika amepotea upotofu ulio wazi.'),
(3570, 33, 37, 'Na ulipo mwambia yule Mwenyezi Mungu aliye mneemesha, nawe ukamneemesha: Shikamana na mkeo, na mche Mwenyezi Mungu. Na ukaficha nafsini mwako aliyo taka Mwenyezi Mungu kuyafichua, nawe ukawachelea watu, hali Mwenyezi Mungu ndiye mwenye haki zaidi kumchelea. Basi Zaid alipo kwisha haja naye tulikuoza wewe, ili isiwe taabu kwa Waumini kuwaoa wake wa watoto wao wa kupanga watapo kuwa wamekwisha timiza nao shuruti za t\'alaka. Na amri ya Mwenyezi Mungu ni yenye kutekelezwa.'),
(3571, 33, 38, 'Hapana ubaya kwa Nabii kufanya aliyo mhalalishia Mwenyezi Mungu. Huo ndio mwendo wa Mwenyezi Mungu kwa walio pita zamani. Na amri ya Mwenyezi Mungu ni kudura iliyo kwisha kadiriwa.'),
(3572, 33, 39, '(Hao wa zamani) walio kuwa wakifikisha risala za Mwenyezi Mungu na wanamwogopa Yeye, na wala hawamwogopi yeyote isipo kuwa Mwenyezi Mungu. Na Mwenyezi Mungu ndiye atoshaye kuhisabu.'),
(3573, 33, 40, 'Muhammad si baba wa yeyote katika wanaume wenu, bali ni Mtume wa Mwenyezi Mungu na Mwisho wa Manabii, na Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(3574, 33, 41, 'Enyi mlio amini! Mdhukuruni Mwenyezi Mungu kwa wingi wa kumdhukuru..'),
(3575, 33, 42, 'Na mtakaseni asubuhi na jioni.'),
(3576, 33, 43, 'Yeye na Malaika wake ndio wanakurehemuni ili kukutoeni gizani mwende kwenye nuru. Naye ni Mwenye kuwarehemu Waumini.'),
(3577, 33, 44, 'Maamkiano yao siku ya kukutana naye yatakuwa: Salama! Na amewaandalia malipo ya ukarimu.'),
(3578, 33, 45, 'Ewe Nabii! Hakika Sisi tumekutuma uwe shahidi na mbashiri na mwonyaji,'),
(3579, 33, 46, 'Na mwitaji kumwendea Mwenyezi Mungu kwa idhini yake, na taa yenye kutoa nuru.'),
(3580, 33, 47, 'Na wabashirie Waumini ya kwamba wana fadhila kubwa inayo toka kwa Mwenyezi Mungu.'),
(3581, 33, 48, 'Wala usiwat\'ii makafiri na wanaafiki, na usijali udhia wao. Nawe mtegemee Mwenyezi Mungu. Na Mwenyezi Mungu anatosha kuwa Mtegemewa.'),
(3582, 33, 49, 'Enyi mlio amini! Mkiwaoa wanawake, Waumini, kisha mkawapa t\'alaka kabla ya kuwagusa, basi hamna eda juu yao mtakayo ihisabu. Basi wapeni cha kuwaliwaza, na muwawache kwa kuwachana kwa wema.'),
(3583, 33, 50, 'Ewe Nabii! Tumekuhalalishia wake zako ulio wapa mahari yao, na ulio wamiliki kwa mkono wako wa kulia katika alio kupa Mwenyezi Mungu, na mabinti ami zako, na mabinti wa shangazi zako, na mabinti wa wajomba zako, na mabinti wa dada za mama yako walio hama pamoja nawe; na mwanamke Muumini akijitoa mwenyewe kwa Nabii, kama mwenyewe Nabii akitaka kumwoa. Ni halali kwako wewe tu, si kwa Waumini wengine. Sisi tunajua tuliyo wafaridhia wao katika wake zao na wanawake ilio wamiliki mikono yao ya kulia. Ili isiwe dhiki kwao. Na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(3584, 33, 51, 'Unaweza kuakhirisha zamu kwa umtakaye katika wao, na umsogeze umtakaye. Na kama ukimtaka yule uliye mtenga, basi si vibaya kwako. Kufanya hivi kutapelekea yaburudike macho yao, wala wasihuzunike, na wawe radhi juu ya kile unacho wapa wao wote. Na Mwenyezi Mungu anajua yaliyomo nyoyoni mwenu, na Mwenyezi Mungu ni Mwenye kujua, Mwenye hikima.'),
(3585, 33, 52, 'Baada ya hawa hawakuhalalikii wewe wanawake wengine, wala kuwabadilisha kwa wake wengine ingawa uzuri wao ukikupendeza; isipo kuwa yule uliye mmiliki kwa mkono wako wa kulia. Na Mwenyezi Mungu anachungua kila kitu.'),
(3586, 33, 53, 'Enyi mlio amini! Msiingie nyumba za Nabii ila mpewe ruhusa kwenda kula, sio kungojea kiwive. Lakini mtakapo itwa basi ingieni, na mkisha kula tawanyikeni, wala msiweke mazungumzo. Hakika hayo yanamuudhi Nabii naye anakustahini, lakini Mwenyezi Mungu hastahi kwa jambo la haki. Na mnapo wauliza wakeze haja waulizeni nyuma ya mapazia. Hivyo ndio usafi zaidi kwa nyoyo zenu na nyoyo zao. Wala haikufalieni kumuudhi Mtume wa Mwenyezi Mungu, wala kuwaoa wake zake baada yake kabisa. Hakika jambo hilo ni kubwa mbele ya Mwenyezi Mungu.'),
(3587, 33, 54, 'Mkidhihirisha chochote kile, au mkikificha, basi hakika Mwenyezi Mungu ni Mwenye kujua kila kitu.'),
(3588, 33, 55, 'Hapana ubaya kwao (wake za Mtume) kuonana na baba zao, wala watoto wao, wala ndugu zao, wala watoto wa ndugu zao wanaume, wala watoto wa ndugu zao wanawake, wala wanawake wenzao, wala wale iliyo wamiliki mikono yao ya kulia. Na enyi wanawake, mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye kushuhudia kila kitu.'),
(3589, 33, 56, 'Hakika Mwenyezi Mungu na Malaika wake wanamsalia Nabii. Enyi mlio amini! Msalieni na mumsalimu kwa salamu.'),
(3590, 33, 57, 'Hakika wanao muudhi Mwenyezi Mungu na Mtume wake, Mwenyezi Mungu amewalaani duniani na Akhera, na amewaandalia adhabu ya kufedhehi.'),
(3591, 33, 58, 'Na wale wanao waudhi Waumini wanaume na wanawake pasina wao kufanya kosa lolote, bila ya shaka wamebeba dhulma kubwa na dhambi zilio dhaahiri.'),
(3592, 33, 59, 'Ewe Nabii! Waambie wake zako, na binti zako, na wake za Waumini wajiteremshie nguo zao. Hivyo ni karibu zaidi kuweza kutambulikana wasiudhiwe. Na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(3593, 33, 60, 'Kama wanaafiki na wale wenye maradhi nyoyoni mwao, na waenezao fitna katika Madina hawatoacha, basi kwa yakini tutakusalitisha juu yao, kisha hawatakaa humo karibu yako ila muda mchache tu.'),
(3594, 33, 61, 'Wamelaanika! Popote watakapo onekana watakamatwa na watauliwa kabisa.'),
(3595, 33, 62, 'Hii ni ada ya Mwenyezi Mungu iliyo kuwa kwa wale walio pita zamani. Wala hutapata mabadiliko katika ada ya Mwenyezi Mungu.'),
(3596, 33, 63, 'Watu wanakuuliza khabari ya Saa (ya Kiyama). Sema: Ujuzi wake uko kwa Mwenyezi Mungu. Na nini kitakujuvya? Pengine hiyo Saa iko karibu.'),
(3597, 33, 64, 'Hakika Mwenyezi Mungu amewalaani makafiri na amewaandalia Moto unao waka kwa nguvu.'),
(3598, 33, 65, 'Watadumu humo milele. Hawampati mlinzi wala wa kuwanusuru.'),
(3599, 33, 66, 'Siku ambayo nyuso zao zitapinduliwa pinduliwa katika Moto. Watasema: Laiti tungeli mt\'ii Mwenyezi Mungu, na tungeli mt\'ii Mtume!'),
(3600, 33, 67, 'Na watasema: Mola wetu Mlezi! Hakika sisi tuliwat\'ii bwana zetu na wakubwa wetu; nao ndio walio tupoteza njia.'),
(3601, 33, 68, 'Mola wetu Mlezi! Wape wao adhabu mara mbili, na uwalaani laana kubwa.'),
(3602, 33, 69, 'Enyi mlio amini! Msiwe kama wale walio mtaabisha Musa, lakini Mwenyezi Mungu akamtakasa na waliyo yasema, naye alikuwa mwenye hishima mbele ya Mwenyezi Mungu.'),
(3603, 33, 70, 'Enyi mlio amini! Mcheni Mwenyezi Mungu na semeni maneno ya sawasawa.'),
(3604, 33, 71, 'Apate kukutengenezeeni vitendo vyenu na akusameheni madhambi yenu. Na anaye mt\'ii Mwenyezi Mungu na Mtume wake, bila ya shaka amefanikiwa mafanikio makubwa.'),
(3605, 33, 72, 'Kwa hakika Sisi tulikadimisha amana kwa mbingu na ardhi na milima; na vyote hivyo vikakataa kuichukua na vikaogopa. Lakini mwanaadamu akaichukua. Hakika yeye amekuwa dhaalimu mjinga.'),
(3606, 33, 73, 'Kwa hivyo Mwenyezi Mungu anawaadhibu wanaafiki wanaume, na wanaafiki wanawake, na washirikina wanaume, na washirikina wanawake; na anawapokelea toba Waumini wanaume, na Waumini wanawake. Na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Sifa njema zote ni za Mwenyezi Mungu, ambaye ni vyake vyote viliomo mbnguni na katika ardhi. Na sifa ni zake Yeye katika Akhera. Naye ni Mwenye hikima, Mwenye khabari.'),
(3608, 34, 2, 'Anajua yanayo ingia katika ardhi na yanayo toka humo, na yanayo teremka kutoka mbinguni, na yanayo panda huko. Na Yeye ni Mwenye kurehemu, Mwenye kusamehe.'),
(3609, 34, 3, 'Na walisema walio kufuru: Haitatufikia Saa (ya Kiyama). Sema: Kwani? Hapana shaka itakufikieni, naapa kwa haki ya Mola wangu Mlezi, Mwenye kujua ya ghaibu! Hapana kinacho fichikana kwake hata chenye uzito wa chembe tu, si katika mbingu wala katika ardhi, wala kilicho kidogo kuliko hivyo, wala kikubwa zaidi; ila vyote hivyo vimo katika Kitabu chake chenye kubainisha.'),
(3610, 34, 4, 'Ili awalipe walio amini na wakatenda mema. Hao watapata msamaha na riziki ya ukarimu.'),
(3611, 34, 5, 'Na wale walio jitahidi kuzipinga Ishara zetu wakaona watashinda, hao watapata adhabu mbaya yenye uchungu.'),
(3612, 34, 6, 'Na walio pewa ilimu wanaona ya kuwa ulio teremshiwa kutoka kwa Mola wako Mlezi ni haki, nayo huongoa kwendea njia ya Mwenye nguvu, Mwenye kusifiwa.'),
(3613, 34, 7, 'Na walio kufuru walisema: Je! Tukujuulisheni mtu anaye kuambieni kwamba mtakapo chambuliwa mapande mapande mtakuwa katika umbo jipya?'),
(3614, 34, 8, 'Ama amemzulia Mwenyezi Mungu uwongo, au ana wazimu. Bali wasio amini Akhera wamo adhabuni na upotofu wa mbali kabisa.'),
(3615, 34, 9, 'Kwani hawaoni yaliyo mbele yao na yaliyo nyuma yao ya mbingu na ardhi? Tungeli penda tungeli wadidimiza ndani ya ardhi, au tungeli wateremshia juu yao vipande vya mbingu. Hakika katika haya zipo Ishara kwa kila mja aliye tubia.'),
(3616, 34, 10, 'Na tulimpa Daudi fadhila kutoka kwetu, (tukasema): Enyi milima! Karirini kumtakasa Mwenyezi Mungu pamoja naye! Na ndege pia. Na tukamlainishia chuma.'),
(3617, 34, 11, '(Tukamwambia:) Tengeneza nguo za chuma pana, na kadiria sawa katika kuunganisha. Na tendeni mema. Hakika Mimi nayaona vyema mnayo yatenda.'),
(3618, 34, 12, 'Na Suleiman tuliufanya upepo umtumikie. Safari yake ya asubuhi ni mwendo wa mwezi mmoja, na safari yake ya jioni ni mwendo wa mwezi mmoja. Na tukamyayushia chemchem ya shaba. Na katika majini walikuwako walio kuwa wakifanya kazi mbele yake kwa idhini ya Mola wake Mlezi. Na kila anaye jitenga na amri yetu katika wao, tunamwonjesha adhabu ya Moto unao waka.'),
(3619, 34, 13, 'Walikuwa wakimfanyia alipendalo, kama mihrabu, na masanamu, na madeste makubwa kama mahodhi, na masufuria makubwa yasiyo ondolewa mahala pake. Enyi watu wa Daudi! Fanyeni kazi kwa kushukuru. Ni wachache katika waja wangu wanao shukuru.'),
(3620, 34, 14, 'Na tulipo mhukumia kufa, hapana aliye wajuulisha kufa kwake ila mnyama wa ardhi aliye kula fimbo yake. Na alipo anguka majini walitambua lau kuwa wangeli jua ya ghaibu wasingeli kaa katika adhabu hiyo ya kufedhehesha.'),
(3621, 34, 15, 'Hakika ilikuwapo Ishara kwa (watu wa nchi ya) Sabaa katika maskani yao - bustani mbili, kulia na kushoto. Kuleni katika riziki ya Mwenyezi Mungu, na mumshukuru. Mji mzuri na Mola Mlezi aliye Msamehevu.'),
(3622, 34, 16, 'Lakini wakaacha. Tukawapelekea mafuriko makubwa, na tukawabadilishia badala ya bustani zao hizo kwa bustani nyengine zenye matunda makali machungu, na mivinje, na kidogo katika miti ya kunazi.'),
(3623, 34, 17, 'Hayo tuliwalipa kwa sababu ya walivyo kufuru. Nasi kwani tunamuadhibu isipo kuwa anaye kufuru?'),
(3624, 34, 18, 'Na baina yao na miji mingine tuliyo ibariki tuliweka miji iliyo dhaahiri, na tukaweka humo vituo vya safari. Tukawaambia: Nendeni humo usiku na mchana kwa amani.'),
(3625, 34, 19, 'Lakini wakasema: Mola wetu Mlezi! Weka mwendo mrefu baina ya safari zetu. Na wakazidhulumu nafsi zao. Basi tukawafanya ni masimulizi tu, na tukawatawanya tawanya. Hakika katika hayo bila ya shaka zimo Ishara kwa kila mwenye kusubiri sana na akashukuru.'),
(3626, 34, 20, 'Na bila ya shaka Iblisi alihakikisha ile dhana yake juu yao. Nao walimfuata, isipo kuwa kundi la Waumini.'),
(3627, 34, 21, 'Na yeye hakuwa na mamlaka yoyote juu yao, ila kwa sababu ya kuwajaribu tujue nani mwenye kuamini Akhera, na nani anaye tilia shaka. Na Mola wako Mlezi ni Mwenye kuhifadhi kila kitu.'),
(3628, 34, 22, 'Sema: Waite mnao wadaia kuwa ni badala ya Mwenyezi Mungu. Hawamiliki hata uzito wa chembe hichi katika mbingu wala ardhi. Wala hawana ushirika wowote humo. Wala Yeye hana msaidizi miongoni mwao.'),
(3629, 34, 23, 'Wala uombezi mbele yake hautafaa kitu, isipo kuwa kwa aliye mpa idhini. Hata itapo ondolewa khofu kwenye nyoyo zao watasema: Mola wenu Mlezi kasema nini? Watasema: Yaliyo kweli! Na Yeye ndiye Aliye juu, Mkubwa.'),
(3630, 34, 24, 'Sema: Ni nani anaye kuruzukuni kutoka mbinguni na kwenye ardhi? Sema: Mwenyezi Mungu! Na hakika sisi au nyinyi bila ya shaka tuko kwenye uwongofu au upotofu ulio wazi.'),
(3631, 34, 25, 'Sema: Nyinyi hamtaulizwa kwa makosa tuliyo yafanya, wala sisi hatutaulizwa kwa mnayo yatenda nyinyi.'),
(3632, 34, 26, 'Sema: Mola wetu Mlezi atatukusanya, kisha atatuhukumu kwa haki; na Yeye ndiye Hakimu Mwenye kujua.'),
(3633, 34, 27, 'Sema: Nionyesheni mlio waunganisha naye kuwa washirika. Hasha! Bali Yeye ndiye Mwenyezi Mungu Mwenye nguvu, Mwenye hikima.'),
(3634, 34, 28, 'Na hatukukutuma ila kwa watu wote, uwe mbashiri, na mwonyaji. Lakini watu wengi hawajui.'),
(3635, 34, 29, 'Na wanasema: Ahadi hii itakuwa lini, ikiwa nyinyi mnasema kweli?'),
(3636, 34, 30, 'Sema: Nyinyi mnayo miadi ya Siku ambayo hamtaakhirika nayo kwa saa moja, wala hamtaitangulia.'),
(3637, 34, 31, 'Na walisema walio kufuru: Hatutaiamini Qur\'ani hii, wala yaliyo kuwa kabla yake. Na ungeli waona madhaalimu watapo simamishwa mbele ya Mola wao Mlezi, wakirudishiana maneno wao kwa wao! Wanyonge wakiwaambia walio takabari: Lau kuwa si nyinyi, bila ya shaka tungeli kuwa Waumini sisi.'),
(3638, 34, 32, 'Walio takabari watawaambia wanyonge: Kwani sisi ndio tulio kuzuieni na uwongofu baada ya kukujieni? Bali nyinyi wenyewe ni wakosefu.'),
(3639, 34, 33, 'Na wanyonge wakawaambia walio takabari: Bali ni vitimbi vyenu vya usiku na mchana, mlipo kuwa mkituamrisha tumkufuru Mwenyezi Mungu, na tumfanyie washirika. Nao wataficha majuto watakapo iona adhabu. Na tutaweka makongwa shingoni mwao walio kufuru. Kwani wanalipwa ila kwa waliyo kuwa wakiyatenda?'),
(3640, 34, 34, 'Na hatukumtuma mwonyaji yeyote kwenye mji, ila walisema walio jidekeza kwa starehe zao wa mji huo: Hakika sisi tunayakataa hayo mliyo tumwa nayo.'),
(3641, 34, 35, 'Na wakasema: Sisi tuna mali mengi zaidi na watoto, wala sisi hatutaadhibiwa.'),
(3642, 34, 36, 'Sema: Hakika Mola wangu Mlezi humkunjulia riziki na humdhikisha amtakaye. Lakini watu wengi hawajui.'),
(3643, 34, 37, 'Na si mali yenu wala watoto wenu watakao kukaribisheni kwetu muwe karibu, isipo kuwa aliye amini na akatenda mema. Hao basi watapata malipo mardufu kwa walio yafanya. Nao watakuwa salama katika maghorofa.'),
(3644, 34, 38, 'Na wanao jitahidi kuzipinga Ishara zetu, wakaona watashinda, hao watahudhurishwa kwenye adhabu.'),
(3645, 34, 39, 'Sema: Kwa hakika Mola wangu Mlezi humkunjulia riziki na humdhikisha amtakaye katika waja wake. Na chochote mtakacho kitoa Yeye atakilipa. Naye ni Mbora wa wanao ruzuku.'),
(3646, 34, 40, 'Na siku atakayo wakusanya wote, kisha atawaambia Malaika: Je! Hawa walikuwa wakikuabuduni?'),
(3647, 34, 41, 'Waseme: Subhanak, Umetakasika. Wewe ndiye kipenzi chetu si hao. Bali wao walikuwa wakiwaabudu majini; wengi wao wakiwaamini hao.'),
(3648, 34, 42, 'Hii leo hataweza yeyote kumletea nafuu wala madhara mwenzie. Na tutawaambia walio dhulumu: Onjeni adhabu ya Moto mlio kuwa mkiukanusha.'),
(3649, 34, 43, 'Na wanapo somewa Aya zetu zilizo wazi wanasema: Huyu si chochote ila ni mtu anaye taka kukuzuieni na waliyo kuwa wakiabudu baba zenu. Na wakasema: Haya si chochote ila ni uwongo ulio zuliwa. Na walio kufuru waliiambia Haki ilipo wajia: Haya si chochote ila ni uchawi ulio dhaahiri.'),
(3650, 34, 44, 'Wala hatukuwapa Vitabu wavisome, wala hatukuwatumia Mwonyaji kabla yako wewe.'),
(3651, 34, 45, 'Na walikadhibisha walio kuwa kabla yao. Na hawakufikilia hata sehemu moja katika kumi ya tulivyo wapa hao. Nao waliwakadhibisha Mitume wangu. Basi kuangamiza kwangu kulikuwa namna gani!'),
(3652, 34, 46, 'Sema: Mimi nakunasihini kwa jambo moja tu - ya kwamba msimame kwa ajili ya Mwenyezi Mungu, wawili-wawili na mmoja-mmoja; kisha mfikiri. Mwenzenu huyu hana wazimu. Yeye si chochote ila ni Mwonyaji kwenu kabla ya kufika adhabu kali.'),
(3653, 34, 47, 'Sema: Ujira nilio kuombeni ni wenu nyinyi. Sina ujira ila kwa Mwenyezi Mungu. Na Yeye ni Shahidi juu ya kila kitu.'),
(3654, 34, 48, 'Sema: Hakika Mola wangu Mlezi anaondoa upotovu kwa Haki. Yeye ndiye Mwenye kujua vyema yaliyo ghaibu.'),
(3655, 34, 49, 'Sema: Kweli imefika, na uwongo haujitokezi, wala haurudi.'),
(3656, 34, 50, 'Sema: Ikiwa mimi nimepotea, basi nimepotea kwa kudhuru nafsi yangu mwenyewe. Na ikiwa nimeongoka, basi ni kwa sababu ya kunifunulia Mola wangu Mlezi. Hakika Yeye ni Mwenye kusikia, Aliye karibu.'),
(3657, 34, 51, 'Na lau ungeli ona watapo babaika, basi hapana pa kukimbilia! Na watakamatwa mahala pa karibu.'),
(3658, 34, 52, 'Na watasema: Tunaiamini! Lakini wataipata wapi kutoka huko mahala mbali?'),
(3659, 34, 53, 'Na hapo kabla yake waliikataa, na wakiyatupilia mbali maneno ya ghaibu kutoka mahali mbali.'),
(3660, 34, 54, 'Na watatiliwa kizuizi baina yao na hayo wanayo yatamani, kama walivyo fanyiwa wenzao zamani. Hakika hao walikuwa na shaka ya kutia wasiwasi.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Sifa zote njema ni za Mwenyezi Mungu, Muumba wa mbingu na ardhi, aliye wafanya Malaika kuwa wajumbe wenye mbawa, mbili-mbili, na tatu- tatu, na nne-nne. Huzidisha katika kuumba apendavyo. Hakika Mwenyezi Mungu ni Muweza juu ya kila kitu.'),
(3662, 35, 2, 'Rehema ambayo Mwenyezi Mungu anawafungulia watu hapana wa kuizuia. Na anayo izuia hapana wa kuipeleka isipo kuwa Yeye. Naye ndiye Mwenye nguvu, Mwenye hikima.'),
(3663, 35, 3, 'Enyi watu! Kumbukeni neema za Mwenyezi Mungu juu yenu. Ati yupo muumba mwengine asiye kuwa Mwenyezi Mungu anaye kupeni riziki kutoka mbinguni na kwenye ardhi? Hapana mungu ila Yeye tu. Basi wapi mnako geuzwa?'),
(3664, 35, 4, 'Na wakikukanusha, basi walikwisha kanushwa Mitume kabla yako. Na mambo yote yatarudishwa kwa Mwenyezi Mungu.'),
(3665, 35, 5, 'Enyi watu! Hakika ahadi ya Mwenyezi Mungu ni ya kweli. Basi yasikudanganyeni maisha ya dunia, wala mdanganyifu asikudanganyeni juu ya Mwenyezi Mungu.'),
(3666, 35, 6, 'Hakika Shet\'ani ni adui yenu, basi mfanyeni kuwa ni adui. Kwani yeye analiita kundi lake liwe katika watu wa Motoni.'),
(3667, 35, 7, 'Walio kufuru watakuwa na adhabu kali; na walio amini na wakatenda mema watapata msamaha na ujira mkubwa.'),
(3668, 35, 8, 'Je! Yule aliye pambiwa a\'mali zake mbaya na akaziona ni njema - basi hakika Mwenyezi Mungu humwacha akapotea amtakaye, na humwongoa amtakaye. Basi nafsi yako isijihiliki kwa kuwasikitikia. Hakika Mwenyezi Mungu anajua wanayo yafanya.'),
(3669, 35, 9, 'Na Mwenyezi Mungu ndiye anaye zituma Pepo ziyatimue mawingu, nasi tukayafikisha kwenye nchi iliyo kufa, tukaifufua ardhi baada ya kufa kwake. Ndivyo kama hivyo kutakavyo kuwa kufufuliwa.'),
(3670, 35, 10, 'Mwenye kutaka utukufu basi utukufu wote uko kwa Mwenyezi Mungu. Kwake Yeye hupanda neno zuri, na a\'mali njema Yeye huitukuza. Na wanao panga vitimbi vya maovu watapata adhabu kali. Na vitimbi vya hao vitaondokea patupu.'),
(3671, 35, 11, 'Na Mwenyezi Mungu amekuumbeni kutokana na udongo. Kisha kwa tone la manii. Kisha akakufanyeni mwanamume na mwanamke. Na mwanamke yeyote hachukui mimba, wala hazai, ila kwa ilimu yake. Na wala hapewi umri mwenye umri mrefu, wala hapunguziwi katika umri wake, ila yamo hayo katika Kitabu. Hakika hayo ni mepesi kwa Mwenyezi Mungu.'),
(3672, 35, 12, 'Na bahari mbili haziwi sawa; haya ni matamu yenye ladha, mazuri kunywa. Na haya ni ya chumvi, machungu. Na kutokana na bahari zote mnakula nyama mpya (isiyo chacha). Na mnatoa mapambo mnayo yavaa. Na unaona ndani yake marikebu zinakata maji ili mtafute fadhila zake, na ili mpate kushukuru.'),
(3673, 35, 13, 'Anauingiza usiku katika mchana, na anauingiza mchana katika usiku. Na amelifanya jua na mwezi kutumikia. Kila kimojapo kinakwenda kwa muda maalumu. Huyo ndiye Mwenyezi Mungu Mola wenu Mlezi. Ufalme ni wake. Na hao mnao waomba badala yake hawamiliki hata ugozi wa kokwa ya tende.'),
(3674, 35, 14, 'Mkiwaomba hawasikii maombi yenu; na hata wakisikia hawakujibuni. Na Siku ya Kiyama watakataa ushirikina wenu. Na hapana atakaye kupa khabari vilivyo kama Yeye Mwenye khabari.'),
(3675, 35, 15, 'Enyi watu! Nyinyi ndio wenye haja kwa Mwenyezi Mungu, na Mwenyezi Mungu ndiye Mwenye kujitosha, Msifiwa.'),
(3676, 35, 16, 'Akitaka atakuondoeni na alete viumbe vipya.'),
(3677, 35, 17, 'Na hilo si gumu kwa Mwenyezi Mungu.'),
(3678, 35, 18, 'Na mbebaji habebi mzigo wa mwingine. Na aliye topewa na mzigo wake akiomba uchukuliwe hautachukuliwa hata kidogo, na angamwomba jamaa yake. Hakika wewe unawaonya wale wanao mcha Mola wao Mlezi kwa ghaibu, na wanashika Sala. Na anaye jitakasa, basi anajitakasa kwa ajili ya nafsi yake. Na marejeo ni kwa Mwenyezi Mungu.'),
(3679, 35, 19, 'Na kipofu na mwenye kuona hawalingani.'),
(3680, 35, 20, 'Wala giza na mwangaza.'),
(3681, 35, 21, 'Wala kivuli na joto.'),
(3682, 35, 22, 'Wala hawalingani walio hai na maiti. Hakika Mwenyezi Mungu humsikilizisha amtakaye. Wala wewe si wa kuwasikilizisha walio makaburini.'),
(3683, 35, 23, 'Hukuwa wewe ila ni mwonyaji.'),
(3684, 35, 24, 'Hakika Sisi tumekutuma wewe kwa Haki, kuwa ni mbashiri na mwonyaji. Na hapana umma wowote ila ulipata mwonyaji kati yao.'),
(3685, 35, 25, 'Na wakikukanusha basi walikwisha wakanusha walio kuwa kabla yao. Mitume wao waliwajia kwa dalili wazi wazi na kwa maandiko na Kitabu chenye nuru.'),
(3686, 35, 26, 'Kisha nikawakamata walio kufuru. Basi kuchukia kwangu kulikuwaje?'),
(3687, 35, 27, 'Je! Huoni kwamba Mwenyezi Mungu ameteremsha maji kutoka mbinguni, na kwayo tumeyatoa matunda yenye rangi mbali mbali. Na katika milima imo mistari myeupe na myekundu yenye rangi mbali mbali, na myeusi sana.'),
(3688, 35, 28, 'Na katika watu, na wanyama, na mifugo, pia rangi zao zinakhitalifiana. Kwa hakika wanao mcha Mwenyezi Mungu miongoni mwa waja wake ni wanazuoni. Hakika Mwenyezi Mungu ni Mwenye nguvu, Mwenye kusamehe.'),
(3689, 35, 29, 'Hakika wale wanao soma Kitabu cha Mwenyezi Mungu, na wakashika Sala, na wakatoa kwa siri na kwa dhaahiri katika tulivyo waruzuku, hao hutaraji biashara isiyo bwaga.'),
(3690, 35, 30, 'Ili Yeye awalipe ujira wao kwa ukamilifu, na awazidishie kutokana na fadhila zake. Hakika Yeye ni Mwenye kusamehe Mwenye shukrani.'),
(3691, 35, 31, 'Na hayo tuliyo kufunulia kutoka Kitabuni ni Kweli yenye kusadikisha yaliyo kuwa kabla yake. Hakika Mwenyezi Mungu kwa waja wake ni Mwenye khabari na Mwenye kuona.'),
(3692, 35, 32, 'Kisha tumewarithisha Kitabu hao ambao tuliwateuwa miongoni mwa waja wetu. Kati yao yupo aliye jidhulumu nafsi yake, na yupo wa katikati, na yupo aliye tangulia katika mambo ya kheri, kwa idhini ya Mwenyezi Mungu. Hiyo ndiyo fadhila kuu.'),
(3693, 35, 33, 'Mabustani ya milele watayaingia. Huko watavikwa vikuku vya dhahabu, na lulu, na nguo zao humo ni za hariri.'),
(3694, 35, 34, 'Na watasema: Alhamdulillahi, Sifa njema zote ni za Mwenyezi Mungu aliye tuondolea huzuni zote. Hakika Mola wetu Mlezi bila ya shaka ni Mwenye kusamehe, Mwenye shukrani.'),
(3695, 35, 35, 'Ambaye kwa fadhila yake ametuweka makao ya kukaa daima; humu haitugusi taabu wala humu hakutugusi kuchoka.'),
(3696, 35, 36, 'Na walio kufuru watakuwa na Moto wa Jahannamu, hawahukumiwi wakafa, wala hawatapunguziwa adhabu yake. Hivyo ndivyo tunavyo mlipa kila mwenye kuzidi ukafiri.'),
(3697, 35, 37, 'Na humo watapiga makelele: Mola wetu Mlezi! Tutoe tufanye mema sio yale tuliyo kuwa tukiyafanya. Kwani hatukukupeni umri wa kutosha kukumbuka mwenye kukumbuka? Na akakujieni Mwonyaji? Basi onjeni! Kwani walio dhulumu hawana wa kuwanusuru.'),
(3698, 35, 38, 'Kwa hakika Mwenyezi Mungu ni Mjuzi wa siri za mbinguni na ardhi. Hakika Yeye ni Mjuzi wa yaliomo vifuani.'),
(3699, 35, 39, 'Yeye ndiye aliye kufanyeni manaibu katika ardhi. Na anaye kufuru, basi kufuru yake ni juu yake; na kufuru za makafiri haziwazidishii kwa Mola wao Mlezi ila kuchukiwa. Wala kufuru za makafiri haziwazidishii ila khasara.'),
(3700, 35, 40, 'Sema: Mnawaona hawa miungu wenu wa kishirikina mnao waomba badala ya Mwenyezi Mungu! Nionyesheni wameumba sehemu gani katika ardhi? Au wana ushirika wowote katika mbingu? Au tumewapa Kitabu nao kwa hicho wakawa na hoja zilizo wazi? Lakini madhaalimu hawaahidiani wao kwa wao ila udanganyifu tu.'),
(3701, 35, 41, 'Hakika Mwenyezi Mungu ndiye anaye zuilia mbingu na ardhi zisiondoke. Na zikiondoka hapana yeyote wa kuzizuia isipo kuwa Yeye. Hakika Yeye ni Mpole Mwenye kusamehe.'),
(3702, 35, 42, 'Na waliapa kwa Mwenyezi Mungu ukomo wa kuapa kwao, kwamba akiwajia mwonyaji bila ya shaka watakuwa waongofu zaidi kuliko taifa jingine lolote. Lakini alipo wajia mwonyaji hakuwazidishia ila kuikimbia hiyo Haki,'),
(3703, 35, 43, 'Kwa kutakabari kwao katika nchi, na kufanya vitimbi vya uovu. Na vitimbi viovu havimsibu ila mwenyewe aliye vifanya. Hebu hawangojei yalio wasibu watu wa kale? Basi hutapata mabadiliko katika mtindo wa Mwenyezi Mungu. Wala hutapata mageuko katika mwendo wa Mwenyezi Mungu.'),
(3704, 35, 44, 'Je! Hawakusafiri katika ardhi wakaona vipi ulikuwa mwisho wa walio kuwa kabla yao? Na hao walikuwa wana nguvu zaidi kuliko wao. Na hapana kitu kinacho weza kumshinda Mwenyezi Mungu mbinguni wala katika ardhi. Hakika Yeye ni Mwenye kujua, Mwenye uweza.'),
(3705, 35, 45, 'Na lau kuwa Mwenyezi Mungu angeli wachukulia watu kwa waliyo yachuma, basi asingeli muacha juu ya ardhi hata mnyama mmoja. Lakini Yeye anawachukulia mpaka ufike muda maalumu. Basi ukifika muda wao basi hakika Mwenyezi Mungu ni Mwenye kuwaona waja wake baraabara.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Ya-Sin (Y. S.).'),
(3707, 36, 2, 'Kwa Haki ya Qur\'ani yenye hikima!'),
(3708, 36, 3, 'Hakika wewe ni miongoni mwa walio tumwa,'),
(3709, 36, 4, 'Juu ya Njia Iliyo Nyooka.'),
(3710, 36, 5, 'Uteremsho wa Mwenye nguvu, Mwenye kurehemu.'),
(3711, 36, 6, 'Ili uwaonye watu ambao baba zao hawakuonywa, basi wao wamekuwa wenye kughafilika.'),
(3712, 36, 7, 'Bila ya shaka kauli imekwisha thibiti juu ya wengi katika wao, kwa hivyo hawaamini.'),
(3713, 36, 8, 'Tumeweka makongwa shingoni mwao, yakawafika videvuni. Kwa hivyo vichwa vyao viko juu tu.'),
(3714, 36, 9, 'Tumeweka kizuizi mbele yao, na kizuizi nyuma yao, na tumewafunika macho yao; kwa hivyo hawaoni.'),
(3715, 36, 10, 'Ni sawa sawa kwao ukiwaonya au usiwaonye, hawataamini.'),
(3716, 36, 11, 'Hakika wewe unamwonya mwenye kufuata ukumbusho, na akamcha Arrahman, Mwingi wa Rehema, kwa ghaibu. Basi mbashirie huyo msamaha na ujira mwema.'),
(3717, 36, 12, 'Hakika Sisi tunawafufua wafu, na tunayaandika wanayo yatanguliza, na wanayo yaacha nyuma. Na kila kitu tumekihifadhi katika daftari asli lenye kubainisha.'),
(3718, 36, 13, 'Na wapigie mfano wa wakaazi wa mji walipo wafikia walio tumwa.'),
(3719, 36, 14, 'Tulipo watumia wawili, wakawakanusha. Basi tukawazidishia nguvu kwa mwingine wa tatu. Wakasema: Hakika sisi tumetumwa kwenu.'),
(3720, 36, 15, 'Wakasema: Nyinyi si chochote ila ni watu kama sisi. Na Mwingi wa Rehema hakuteremsha kitu. Nyinyi mnasema uwongo tu.'),
(3721, 36, 16, 'Wakasema: Mola wetu Mlezi anajua kwamba hakika sisi tumetumwa kwenu.'),
(3722, 36, 17, 'Wala si juu yetu ila kufikisha ujumbe ulio wazi.'),
(3723, 36, 18, 'Wakasema: Sisi tumeagua kuwa nyinyi ni wakorofi. Ikiwa hamtaacha basi kwa yakini tutakupigeni mawe, na mtapata adhabu chungu kutoka kwetu.'),
(3724, 36, 19, 'Wakasema: Ukorofi wenu mnao wenyewe! Je! Ni kwa kuwa mnakumbushwa? Ama nyinyi ni watu walio pindukia mipaka.'),
(3725, 36, 20, 'Na akaja mtu mbio kutokea upande wa mbali wa mjini, akasema: Enyi watu wangu! Wafuateni hawa walio tumwa.'),
(3726, 36, 21, 'Wafuateni ambao hawakutakini ujira, hali ya kuwa wenyewe wameongoka.'),
(3727, 36, 22, 'NA KWA NINI nisimuabudu yule aliye niumba na kwake mtarejeshwa?'),
(3728, 36, 23, 'Je! Niishike miungu mingine badala yake? Arrahmani, Mwingi wa Rehema, akinitakia madhara uombezi wa hao hautanifaa kitu, wala hawataniokoa.'),
(3729, 36, 24, 'Basi hakika mimi hapo nitakuwa katika upotovu ulio dhaahiri.'),
(3730, 36, 25, 'Hakika mimi nimemuamini Mola wenu Mlezi, basi nisikilizeni!'),
(3731, 36, 26, 'Akaambiwa: Ingia Peponi! Akasema: Laiti kuwa watu wangu wangeli jua'),
(3732, 36, 27, 'Jinsi Mola wangu Mlezi alivyo nisamehe, na akanifanya miongoni mwa walio hishimiwa.'),
(3733, 36, 28, 'Na hatukuwateremshia kaumu yake baada yake jeshi kutoka mbinguni, wala si wenye kuteremsha.'),
(3734, 36, 29, 'Hakukuwa ila ukelele mmoja tu; na mara walizimwa!'),
(3735, 36, 30, 'Nawasikitikia waja wangu. Hawajii Mtume ila wao humkejeli.'),
(3736, 36, 31, 'Je! Hawaoni umma ngapi tulizo ziangamiza kabla yao? Hakika hao hawarejei tena kwao.'),
(3737, 36, 32, 'Na hapana mmoja ila wote watakusanywa waletwe mbele yetu.'),
(3738, 36, 33, 'Na Ishara hiyo kwao - ardhi iliyo kufa, nasi tukaifufua, na tukatoa ndani yake nafaka, wakawa wanazila!'),
(3739, 36, 34, 'Na tukafanya ndani yake mabustani ya mitende na mizabibu, na tukatimbua chemchem ndani yake,'),
(3740, 36, 35, 'Ili wale matunda yake, na hayo hayakufanywa na mikono yao! Basi je, hawashukuru?'),
(3741, 36, 36, 'Subhana, Ametakasika, aliye umba dume na jike vyote katika vinavyo mea katika ardhi na katika nafsi zao, na katika wasivyo vijua.'),
(3742, 36, 37, 'Na usiku ni Ishara kwao. Tunauvua humo mchana, mara wao wanakuwa gizani.'),
(3743, 36, 38, 'Na jua linakwenda kwa kiwango chake. Hayo ni makadirio ya Mwenye nguvu, Mwenye kujua.'),
(3744, 36, 39, 'Na mwezi tumeupimia vituo, mpaka unakuwa kama karara kongwe.'),
(3745, 36, 40, 'Haliwi jua kuufikia mwezi, wala usiku kuupita mchana. Na vyote vinaogelea katika njia zao.'),
(3746, 36, 41, 'Na ni Ishara kwao kwamba Sisi tuliwapakia dhuriya zao katika jahazi ilio sheheni.'),
(3747, 36, 42, 'Na tukawaumbia kutoka mfano wake wanavyo vipanda.'),
(3748, 36, 43, 'Na tukitaka tunawazamisha, wala hapana wa kuwasaidia, wala hawaokolewi,'),
(3749, 36, 44, 'Isipo kuwa kwa rehema zitokazo kwetu na starehe kwa muda.'),
(3750, 36, 45, 'Na wanapo ambiwa: Jilindeni na yalioko mbele yenu na yalioko nyuma yenu, ili mpate kurehemewa...'),
(3751, 36, 46, 'Na haiwafikii Ishara yoyote katika Ishara za Mola wao Mlezi ila wao huwa ni wenye kuipuuza.'),
(3752, 36, 47, 'Na wanapo ambiwa: Toeni katika aliyo kupeni Mwenyezi Mungu, walio kufuru huwaambia walio amini: Je! Tuwalishe ambao Mwenyezi Mungu angependa angeli walisha mwenyewe? Nyinyi hammo ila katika upotofu ulio dhaahiri.'),
(3753, 36, 48, 'Na wanasema: Ahadi hii itatokea lini, ikiwa nyinyi ni wakweli?'),
(3754, 36, 49, 'Hawangojei ila ukelele mmoja tu utakao wachukua nao wamo kuzozana.'),
(3755, 36, 50, 'Basi hawataweza kuusia, wala kwa watu wao hawarejei.'),
(3756, 36, 51, 'Na litapulizwa barugumu, mara watatoka makaburini wakikimbilia kwa Mola wao Mlezi.'),
(3757, 36, 52, 'Watasema: Ole wetu! Nani aliye tufufua kwenye malazi yetu? Haya ndiyo aliyo yaahidi Mwingi wa Rehema na wakasema kweli Mitume.'),
(3758, 36, 53, 'Haitakuwa ila ukelele mmoja tu, mara wote watahudhuruishwa mbele yetu.'),
(3759, 36, 54, 'Basi leo nafsi yoyote haitadhulumiwa kitu, wala hamtalipwa ila yale mliyo kuwa mkiyatenda.'),
(3760, 36, 55, 'Hakika watu wa Peponi leo wamo shughulini, wamefurahi.'),
(3761, 36, 56, 'Wao na wake zao wamo katika vivuli wameegemea juu ya viti vya fakhari.'),
(3762, 36, 57, 'Watapata humo kila namna ya matunda na watapata kila watakacho kitaka.'),
(3763, 36, 58, '"Salama!" Hiyo ndiyo kauli itokayo kwa Mola Mlezi Mwenye kurehemu.'),
(3764, 36, 59, 'Na enyi wakosefu! Jitengeni leo!'),
(3765, 36, 60, 'Je! Sikuagana nanyi, enyi wanaadamu, kuwa msimuabudu Shet\'ani? Hakika yeye ni adui dhaahiri kwenu.'),
(3766, 36, 61, 'Na ya kwamba mniabudu Mimi? Hii ndiyo Njia Iliyo Nyooka.'),
(3767, 36, 62, 'Na bila ya shaka yeye amekwisha lipoteza kundi kubwa miongoni mwenu. Je, hamkuwa mkifikiri?'),
(3768, 36, 63, 'Hii basi ndiyo Jahannamu mliyo kuwa mkiahidiwa.'),
(3769, 36, 64, 'Ingieni leo kwa vile mlivyo kuwa mkikufuru.'),
(3770, 36, 65, 'Leo tunaviziba vinywa vyao, na iseme nasi mikono yao, na itoe ushahidi miguu yao kwa waliyo kuwa wakiyachuma.'),
(3771, 36, 66, 'Na tungeli penda tungeli yafutilia mbali macho yao, wakawa wanaiwania njia. Lakini wange ionaje?'),
(3772, 36, 67, 'Na tungeli taka tunge wageuza sura hapo hapo walipo, basi wasinge weza kwenda wala kurudi.'),
(3773, 36, 68, 'Na tunaye mzeesha tunamrudisha nyuma katika umbo. Basi je! hawazingatii?'),
(3774, 36, 69, 'Wala hatukumfundisha (Muhammad) mashairi, wala hayatakikani kwake hayo. Haukuwa huu ila ni ukumbusho na Qur\'ani inayo bainisha.'),
(3775, 36, 70, 'Ili imwonye aliye hai, na neno litimie juu ya makafiri.'),
(3776, 36, 71, 'Je! Hawaoni kwamba tumewaumbia kutokana na iliyo fanya mikono yetu wanyama wa mifugo, na wao wakawa wenye kuwamiliki.'),
(3777, 36, 72, 'Na Sisi tukawadhalilishia. Basi baadhi yao wako wanao wapanda, na baadhi yao wanawala.'),
(3778, 36, 73, 'Na wao wanapata kwao manufaa na vinywaji. Basi je, hawashukuru?'),
(3779, 36, 74, 'Na wameishika miungu mingine badala ya Mwenyezi Mungu ili ati wapate kusaidiwa!'),
(3780, 36, 75, 'Hawataweza kuwasaidia. Bali hao ndio watakuwa askari wao watakao hudhurishwa.'),
(3781, 36, 76, 'Basi maneno yao yasikuhuzunishe. Hakika Sisi tunayajua wanayo yaweka siri na wanayo yatangaza.'),
(3782, 36, 77, 'Kwani mwanaadamu haoni ya kwamba Sisi tumemuumba yeye kutokana na tone la manii? Kisha sasa yeye ndio amekuwa ndiye mgomvi wa dhaahiri!'),
(3783, 36, 78, 'Na akatupigia mfano, na akasahau kuumbwa kwake, akasema: Ni nani huyo atakaye ihuisha mifupa nayo imekwisha mung\'unyika?'),
(3784, 36, 79, 'Sema: Ataihuisha huyo huyo aliye iumba hapo mara ya mwanzo. Na Yeye ni Mjuzi wa kila kuumba.'),
(3785, 36, 80, 'Aliye kujaalieni kupata moto kutokana na mti wa kijani nanyi mkawa kwa huo mnawasha.'),
(3786, 36, 81, 'Kwani aliye ziumba mbingu na ardhi hawezi kuwaumba mfano wao? Kwani! Naye ndiye Muumbaji Mkuu, Mjuzi.'),
(3787, 36, 82, 'Hakika amri yake anapo taka kitu ni kukiambia tu: Kuwa! Na kikawa.'),
(3788, 36, 83, 'Basi Ametakasika yule ambaye mkononi mwake umo Ufalme wa kila kitu; na kwake Yeye mtarejeshwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Naapa kwa wanao jipanga kwa safu.'),
(3790, 37, 2, 'Na kwa wenye kukataza mabaya.'),
(3791, 37, 3, 'Na kwa wenye kusoma Ukumbusho.'),
(3792, 37, 4, 'Hakika Mungu wenu bila ya shaka ni Mmoja.'),
(3793, 37, 5, 'Mola Mlezi wa mbingu na ardhi, na yaliyomo kati yao, na ni Mola Mlezi wa mashariki zote.'),
(3794, 37, 6, 'Hakika Sisi tumeipamba mbingu ya karibu kwa pambo la nyota.'),
(3795, 37, 7, 'Na kulinda na kila shet\'ani a\'si.'),
(3796, 37, 8, 'Wasiweze kuwasikiliza viumbe watukufu. Na wanafukuzwa huko kila upande.'),
(3797, 37, 9, 'Wakifurushwa. Na wanayo adhabu ya kudumu.'),
(3798, 37, 10, 'Isipo kuwa anaye nyakua kitu kidogo, na mara humfwatia kimondo kinacho ng\'ara.'),
(3799, 37, 11, 'Hebu waulize: Je! Wao ni wenye umbo gumu zaidi au hao wengine tulio waumba. Hakika Sisi tuliwaumba wao kwa udongo unao nata.'),
(3800, 37, 12, 'Bali unastaajabu, na wao wanafanya maskhara.'),
(3801, 37, 13, 'Na wanapo kumbushwa hawakumbuki.'),
(3802, 37, 14, 'Na wanapo ona Ishara, wanafanya maskhara.'),
(3803, 37, 15, 'Na husema: Haya si chochote ila ni uchawi tu ulio dhaahiri.'),
(3804, 37, 16, 'Ati tukisha kufa na tukawa udongo na mifupa ndio kweli tutafufuliwa?'),
(3805, 37, 17, 'Hata baba zetu wa zamani?'),
(3806, 37, 18, 'Sema: Naam! Hali nanyi ni madhalili.'),
(3807, 37, 19, 'Huko utapigwa ukelele mmoja tu! Na hapo ndio wataona!'),
(3808, 37, 20, 'Watasema: Ole wetu! Hii ndiyo Siku ya Malipo.'),
(3809, 37, 21, 'Hii ndiyo Siku ya Hukumu mliyo kuwa mkiikadhibisha.'),
(3810, 37, 22, 'Wakusanyeni walio dhulumu, na wake zao, na hao walio kuwa wakiwaabudu -'),
(3811, 37, 23, 'Badala ya Mwenyezi Mungu. Waongozeni njia ya Jahannamu!'),
(3812, 37, 24, 'Na wasimamisheni. Hakika hao watasailiwa:'),
(3813, 37, 25, 'Mna nini? Mbona hamsaidiani?'),
(3814, 37, 26, 'Bali hii leo, watasalimu amri.'),
(3815, 37, 27, 'Watakabiliana wao kwa wao kuulizana.'),
(3816, 37, 28, 'Watasema: Kwa hakika nyinyi mlikuwa mkitujia upande wa kulia.'),
(3817, 37, 29, 'Watasema (wakubwa): Bali nyinyi wenyewe hamkuwa Waumini.'),
(3818, 37, 30, 'Sisi hatukuwa na mamlaka juu yenu, bali nyinyi wenyewe mlikuwa wapotovu.'),
(3819, 37, 31, 'Basi hukumu ya Mola wetu Mlezi imekwisha tuthibitikia. Hakika bila ya shaka tutaonja tu adhabu.'),
(3820, 37, 32, 'Tulikupotezeni kwa sababu sisi wenyewe tulikuwa wapotovu.'),
(3821, 37, 33, 'Basi wao kwa hakika siku hiyo watashirikiana katika adhabu pamoja.'),
(3822, 37, 34, 'Hivyo ndivyo tutakavyo wafanyia wakosefu.'),
(3823, 37, 35, 'Wao walipo kuwa wakiambiwa La ilaha illa \'Llahu Hapana mungu ila Mwenyezi Mungu tu, wakijivuna.'),
(3824, 37, 36, 'Na wakisema: Hivyo sisi tuiache miungu yetu kwa ajili ya huyu mtunga mashairi mwendawazimu?'),
(3825, 37, 37, 'Bali huyu amekuja kwa Haki, na amewasadikisha Mitume.'),
(3826, 37, 38, 'Hakika nyinyi bila ya shaka mtaionja adhabu chungu.'),
(3827, 37, 39, 'Wala hamlipwi ila hayo mliyo kuwa mkiyafanya.'),
(3828, 37, 40, 'Isipo kuwa waja wa Mwenyezi Mungu walio khitariwa.'),
(3829, 37, 41, 'Hao ndio watakao pata riziki maalumu,'),
(3830, 37, 42, 'Matunda, nao watahishimiwa.'),
(3831, 37, 43, 'Katika Bustani za neema.'),
(3832, 37, 44, 'Wako juu ya viti wamekabiliana.'),
(3833, 37, 45, 'Wanazungushiwa kikombe chenye kinywaji cha chemchem'),
(3834, 37, 46, 'Kinywaji cheupe, kitamu kwa wanywao.'),
(3835, 37, 47, 'Hakina madhara, wala hakiwaleweshi.'),
(3836, 37, 48, 'Na watakuwa nao wanawake wenye macho ya staha mazuri.'),
(3837, 37, 49, 'Hao wanawake kama mayai yaliyo hifadhika.'),
(3838, 37, 50, 'Waingie kuulizana wenyewe kwa wenyewe.'),
(3839, 37, 51, 'Aseme msemaji mmoja miongoni mwao: Hakika mimi nalikuwa na rafiki'),
(3840, 37, 52, 'Aliye kuwa akinambia: Hivyo wewe ni katika wanao sadiki'),
(3841, 37, 53, 'Ati tukisha kufa tukawa udongo na mafupa, ndio tutalipwa na kuhisabiwa?'),
(3842, 37, 54, 'Atasema: Je! Nyie mnawaona?'),
(3843, 37, 55, 'Basi atachungulia amwone katikati ya Jahannamu.'),
(3844, 37, 56, 'Atasema: Wallahi! Ulikaribia kunipoteza.'),
(3845, 37, 57, 'Na lau kuwa si neema ya Mola wangu Mlezi bila ya shaka ningeli kuwa miongoni mwa walio hudhurishwa.'),
(3846, 37, 58, 'Je! Sisi hatutakufa,'),
(3847, 37, 59, 'Isipo kuwa kifo chetu cha kwanza? Wala sisi hatutaadhibiwa.'),
(3848, 37, 60, 'Hakika huku bila ya shaka ndiko kufuzu kukubwa.'),
(3849, 37, 61, 'Kwa mfano wa haya nawatende watendao.'),
(3850, 37, 62, 'Je! Kukaribishwa hivi si ndio bora, au mti wa Zaqqum?'),
(3851, 37, 63, 'Hakika Sisi tumeufanya huo kuwa ni mateso kwa walio dhulumu.'),
(3852, 37, 64, 'Hakika huo ni mti unao toka katikati ya Jahannamu.'),
(3853, 37, 65, 'Mashada ya matunda yake kama kwamba vichwa vya mashet\'ani.'),
(3854, 37, 66, 'Basi hakika bila ya shaka hao watayala hayo, na wajaze matumbo.'),
(3855, 37, 67, 'Kisha juu yake wanyweshwe mchanganyo wa maji yamoto yaliyo chemka.'),
(3856, 37, 68, 'Kisha hakika marejeo yao bila ya shaka yatakuwa kwenye Jahannamu.'),
(3857, 37, 69, 'Hakika waliwakuta baba zao wamepotea.'),
(3858, 37, 70, 'Na wao wakafanya haraka kufuata nyayo zao.'),
(3859, 37, 71, 'Na bila ya shaka walikwisha potea kabla yao wengi wa watu wa zamani.'),
(3860, 37, 72, 'Na Sisi hakika tuliwapelekea waonyaji.'),
(3861, 37, 73, 'Basi hebu angalia ulikuwaje mwisho wa walio onywa.'),
(3862, 37, 74, 'Isipo kuwa waja wa Mwenyezi Mungu walio khitariwa.'),
(3863, 37, 75, 'Na hakika Nuhu alitwita, na Sisi ni bora wa waitikiaji.'),
(3864, 37, 76, 'Na tulimwokoa yeye na ahali zake kutokana na msiba mkubwa.'),
(3865, 37, 77, 'Na tuliwajaalia dhuriya zake ndio wenye kubakia.'),
(3866, 37, 78, 'Na tukamwachia (sifa njema) kwa walio kuja baadaye.'),
(3867, 37, 79, 'Iwe salama kwa Nuhu ulimwenguni kote!'),
(3868, 37, 80, 'Kwa hakika hivi ndivyo tunavyo walipa wanao fanya mema.'),
(3869, 37, 81, 'Hakika yeye ni katika waja wetu walio amini.'),
(3870, 37, 82, 'Kisha tukawazamisha wale wengine.'),
(3871, 37, 83, 'Na hakika Ibrahim alikuwa katika kundi lake,'),
(3872, 37, 84, 'Alipo mjia Mola wake Mlezi kwa moyo mzima.'),
(3873, 37, 85, 'Alimwambia baba yake na watu wake: Mnaabudu nini?'),
(3874, 37, 86, 'Kwa kuzua tu mnataka miungu mingine badala ya Mwenyezi Mungu?'),
(3875, 37, 87, 'Mnamdhania nini Mola Mlezi wa walimwengu wote?'),
(3876, 37, 88, 'Kisha akapiga jicho kutazama nyota.'),
(3877, 37, 89, 'Akasema: Hakika mimi ni mgonjwa!'),
(3878, 37, 90, 'Nao wakamwacha, wakampa kisogo.'),
(3879, 37, 91, 'Basi akaiendea miungu yao kwa siri, akaiambia: Mbona hamli?'),
(3880, 37, 92, 'Mna nini hata hamsemi?'),
(3881, 37, 93, 'Kisha akaigeukia kuipiga kwa mkono wa kulia.'),
(3882, 37, 94, 'Basi wakamjia upesi upesi.'),
(3883, 37, 95, 'Akasema: Hivyo mnaviabudu vitu mnavyo vichonga wenyewe?'),
(3884, 37, 96, 'Na hali Mwenyezi Mungu ndiye aliye kuumbeni nyinyi na hivyo mnavyo vifanya!'),
(3885, 37, 97, 'Wakasema: Mjengeeni jengo, na kisha mtupeni motoni humo!'),
(3886, 37, 98, 'Basi walitaka kumfanyia vitimbi, lakini tukawafanya wao ndio wa chini.'),
(3887, 37, 99, 'Na akasema: Hakika mimi nahama, nakwenda kwa Mola wangu Mlezi; Yeye ataniongoa.'),
(3888, 37, 100, 'Ewe Mola wangu Mlezi! Nitunukie aliye miongoni mwa watenda mema.'),
(3889, 37, 101, 'Basi tukambashiria mwana aliye mpole.'),
(3890, 37, 102, 'Basi alipo fika makamo ya kwenda na kurudi pamoja naye, alimwambia: Ewe mwanangu! Hakika mimi nimeona usingizini kuwa ninakuchinja. Basi angalia wewe, waonaje? Akasema: Ewe baba yangu! Tenda unavyo amrishwa, utanikuta mimi, Inshallah, katika wanao subiri.'),
(3891, 37, 103, 'Basi wote wawili walipo jisalimisha, na akamlaza juu ya kipaji.'),
(3892, 37, 104, 'Tulimwita: Ewe Ibrahim!'),
(3893, 37, 105, 'Umekwisha itimiliza ndoto. Hakika hivi ndivyo tunavyo walipa wanao tenda mema.'),
(3894, 37, 106, 'Hakika haya ni majaribio yaliyo dhaahiri.'),
(3895, 37, 107, 'Basi tukamtolea fidia kwa dhabihu mtukufu.'),
(3896, 37, 108, 'Na tukamwachia (sifa njema) kwa watu walio kuja baadaye.'),
(3897, 37, 109, 'Iwe salama kwa Ibrahim!'),
(3898, 37, 110, 'Hivi ndivyo tunavyo walipa wanao fanya mema.'),
(3899, 37, 111, 'Hakika yeye ni katika waja wetu walio amini.'),
(3900, 37, 112, 'Na tukambashiria kuzaliwa Is-haqa, naye ni Nabii miongoni mwa watu wema.'),
(3901, 37, 113, 'Na tukambarikia yeye na Is-haqa. Na miongoni mwa dhuriya zao walitokea wema na wenye kudhulumu nafsi zao waziwazi.'),
(3902, 37, 114, 'Na kwa hakika tuliwafanyia hisani Musa na Haruni.'),
(3903, 37, 115, 'Na tukawaokoa wao na watu wao kutokana na dhiki kubwa.'),
(3904, 37, 116, 'Na tukawanusuru; na wakawa wao ndio wenye kushinda.'),
(3905, 37, 117, 'Na tukawapa wawili hao Kitabu kinacho bainisha.'),
(3906, 37, 118, 'Na tukawaongoa kwenye Njia Iliyo Nyooka.'),
(3907, 37, 119, 'Na tukawaachia (sifa njema) katika watu walio kuja baadaye.'),
(3908, 37, 120, 'Iwe salama kwa Musa na Haruni!'),
(3909, 37, 121, 'Hakika hivyo ndivyo tunavyo walipa walio wema.'),
(3910, 37, 122, 'Hakika wawili hao ni katika waja wetu walio amini.'),
(3911, 37, 123, 'Na hakika Ilyas bila ya shaka alikuwa miongoni mwa Mitume.'),
(3912, 37, 124, 'Alipo waambia watu wake: Hamwogopi?'),
(3913, 37, 125, 'Mnamwomba Baa\'li na mnamwacha Mbora wa waumbaji,'),
(3914, 37, 126, 'Mwenyezi Mungu, Mola Mlezi wenu na Mola Mlezi wa baba zenu wa zamani?'),
(3915, 37, 127, 'Wakamkadhibisha. Basi kwa hakika watahudhurishwa;'),
(3916, 37, 128, 'Isipo kuwa waja wa Mwenyezi Mungu walio safishwa.'),
(3917, 37, 129, 'Na tumemwachia (sifa nzuri) kwa walio kuja baadaye.'),
(3918, 37, 130, 'Iwe salama kwa Ilyas.'),
(3919, 37, 131, 'Hakika hivyo ndivyo tunavyo walipa walio wema.'),
(3920, 37, 132, 'Hakika yeye ni katika waja wetu walio amini.'),
(3921, 37, 133, 'Na hakika Lut\'i bila ya shaka ni miongoni mwa Mitume.'),
(3922, 37, 134, 'Tulipo mwokoa yeye na ahali zake wote,'),
(3923, 37, 135, 'Isipo kuwa mwanamke mkongwe katika wale walio bakia nyuma.'),
(3924, 37, 136, 'Kisha tukawaangamiza wale wengineo.'),
(3925, 37, 137, 'Na hakika nyinyi mnawapitia wakati wa asubuhi,'),
(3926, 37, 138, 'Na usiku. Basi je! Hamyatii akilini?'),
(3927, 37, 139, 'Na hakika Yunus alikuwa miongoni mwa Mitume.'),
(3928, 37, 140, 'Alipo kimbia katika jahazi lilio sheheni.'),
(3929, 37, 141, 'Akaingia katika kupigiwa kura, na akawa katika walio shindwa.'),
(3930, 37, 142, 'Samaki akammeza hali ya kuwa ni mwenye kulaumiwa.'),
(3931, 37, 143, 'Na ingeli kuwa hakuwa katika wanao mtakasa Mwenyezi Mungu,'),
(3932, 37, 144, 'Bila ya shaka angeli kaa ndani yake mpaka siku ya kufufuliwa.'),
(3933, 37, 145, 'Lakini tulimtupa ufukweni patupu, hali yu mgonjwa.'),
(3934, 37, 146, 'Na tukauotesha juu yake mmea wa kabila ya mung\'unye.'),
(3935, 37, 147, 'Na tulimtuma kwa watu laki moja au zaidi.'),
(3936, 37, 148, 'Basi wakaamini, na tukawastarehesha kwa muda.'),
(3937, 37, 149, 'Basi waulize: Ati Mola wako Mlezi ndiye mwenye watoto wa kike, na wao wanao watoto wa kiume?'),
(3938, 37, 150, 'Au tumewaumba Malaika kuwa ni wanawake, na wao wakashuhudia?'),
(3939, 37, 151, 'Ati ni kwa uzushi wao ndio wanasema:'),
(3940, 37, 152, 'Mwenyezi Mungu amezaa! Ama hakika bila ya shaka hao ni waongo!'),
(3941, 37, 153, 'Ati amekhiari watoto wa kike kuliko wanaume?'),
(3942, 37, 154, 'Mna nini? Mnahukumu vipi nyinyi?'),
(3943, 37, 155, 'Hamkumbuki?'),
(3944, 37, 156, 'Au mnayo hoja iliyo wazi?'),
(3945, 37, 157, 'Basi leteni Kitabu chenu kama mnasema kweli.'),
(3946, 37, 158, 'Na wameweka makhusiano ya nasaba baina yake na majini; na majini wamekwisha jua bila ya shaka kuwa wao watahudhurishwa.'),
(3947, 37, 159, 'Subhana \'Llah Ametakasika Mwenyezi Mungu na hayo wanayo msingizia.'),
(3948, 37, 160, 'Isipo kuwa waja wa Mwenyezi Mungu walio safishwa.'),
(3949, 37, 161, 'Basi hakika nyinyi na mnao waabudu'),
(3950, 37, 162, 'Hamwezi kuwapoteza'),
(3951, 37, 163, 'Isipo kuwa yule atakaye ingia Motoni.'),
(3952, 37, 164, 'Na hapana katika sisi ila anapo mahali pake maalumu.'),
(3953, 37, 165, 'Na hakika bila ya shaka sisi ndio wajipangao safu.'),
(3954, 37, 166, 'Na hakika sisi ndio wenye kusabihi kutakasa.'),
(3955, 37, 167, 'Na walikuwapo walio kuwa wakisema:'),
(3956, 37, 168, 'Tungeli kuwa na Ukumbusho kama wa watu wa zamani,'),
(3957, 37, 169, 'Bila ya shaka tungeli kuwa ni waja wa Mwenyezi Mungu wenye ikhlasi.'),
(3958, 37, 170, 'Lakini waliukataa. Basi watakuja jua.'),
(3959, 37, 171, 'Na bila ya shaka neno letu lilikwisha tangulia kwa waja wetu walio tumwa.'),
(3960, 37, 172, 'Ya kuwa wao bila ya shaka ndio watakao nusuriwa.'),
(3961, 37, 173, 'Na kwamba jeshi letu ndilo litakalo shinda.'),
(3962, 37, 174, 'Basi waachilie mbali kwa muda.'),
(3963, 37, 175, 'Na watazame, nao wataona.'),
(3964, 37, 176, 'Je! Wanaihimiza adhabu yetu?'),
(3965, 37, 177, 'Basi itakapo shuka uwanjani kwao, itakuwa asubuhi mbaya kwa walio onywa.'),
(3966, 37, 178, 'Na waache kwa muda.'),
(3967, 37, 179, 'Na tazama, na wao wataona.'),
(3968, 37, 180, 'Subhana Rabbi\'l\'Izzat Ametakasika Mola Mlezi Mwenye enzi na yale wanayo mzulia.'),
(3969, 37, 181, 'Na Salamu juu ya Mitume.'),
(3970, 37, 182, 'Na sifa njema zote ni za Mwenyezi Mungu Mola Mlezi wa walimwengu wote.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'S\'ad, Naapa kwa Qur\'ani yenye mawaidha.'),
(3972, 38, 2, 'Lakini walio kufuru wamo katika majivuno na upinzani'),
(3973, 38, 3, 'Mataifa mangapi tumeyaangamiza kabla yao, na wakapiga kelele, lakini wakati wa kuokoka ulikwisha pita.'),
(3974, 38, 4, 'Na walistaajabu kuwajia mwonyaji anaye tokana nao wenyewe, na makafiri wakasema: Huyu ni mchawi, mwongo.'),
(3975, 38, 5, 'Amewafanya miungu wote kuwa ni Mungu Mmoja tu? Hakika hili ni jambo la ajabu.'),
(3976, 38, 6, 'Na wakaondoka wakubwa wao wakiwaambia: Nendeni zenu na dumuni na miungu yenu, kwani hili ni jambo lililo pangwa.'),
(3977, 38, 7, 'Sisi hatukusikia haya katika mila hii ya mwisho. Haya si chochote ila ni uzushi tu.'),
(3978, 38, 8, 'Ati yeye tu ndiye aliye teremshiwa mawaidha peke yake katika sisi? Lakini hao wana shaka na mawaidha yangu, bali hawajaionja adhabu yangu.'),
(3979, 38, 9, 'Au wanazo wao khazina za rehema za Mola wako Mlezi, Mwenye nguvu, Mpaji?'),
(3980, 38, 10, 'Au wao wana ufalme wa mbingu na ardhi na viliomo kati yao? Basi nawazipande njia za kwendea huko!'),
(3981, 38, 11, 'Hao ni askari watao shindwa miongoni mwa makundi yatayo shindwa.'),
(3982, 38, 12, 'Walikanusha kabla yao kaumu ya Nuhu, na kina A\'adi na Firauni mwenye majengo.'),
(3983, 38, 13, 'Na Thamud na kaumu Lut\'i na watu wa Machakani. Hayo ndiyo makundi.'),
(3984, 38, 14, 'Hao wote waliwakadhibisha Mitume; basi wakastahiki adhabu yangu.'),
(3985, 38, 15, 'Na hawa hawangojei ila ukelele mmoja tu usio na muda.'),
(3986, 38, 16, 'Na wao husema: Mola wetu Mlezi! Tuletee upesi sehemu yetu ya adhabu kabla ya Siku ya Hisabu.'),
(3987, 38, 17, 'Subiri kwa hayo wayasemayo, na umkumbuke mja wetu Daudi, mwenye nguvu. Hakika yeye alikuwa mwingi wa kutubia.'),
(3988, 38, 18, 'Hakika tuliidhalilisha milima pamoja naye ikisabihi jioni na asubuhi pamoja naye.'),
(3989, 38, 19, 'Na pia ndege walio kusanywa makundi kwa makundi; wote walikuwa wanyenyekevu kwake.'),
(3990, 38, 20, 'Na tukautia nguvu ufalme wake, na tukampa hikima, na kukata hukumu.'),
(3991, 38, 21, 'Na je! Imekufikia khabari ya wagombanao walipo pindukia ukuta kuingia chumbani?'),
(3992, 38, 22, 'Walipo muingilia Daudi, naye akawaogopa. Wakasema: Usiogope! Sisi ni wagombanao wawili, mmoja wetu amemdhulumu mwenziwe. Basi tuhukumu kwa haki, wala usipendelee. Na utuongoe kwenye njia iliyo sawa.'),
(3993, 38, 23, 'Hakika huyu ndugu yangu ana kondoo wa kike tisiini na tisa; na mimi ninaye kondoo mke mmoja. Akasema: Nipe huyo nikufugie! Na amenishinda kwa maneno.'),
(3994, 38, 24, 'Akasema: Kweli amekudhulumu kwa kumtaka kondoo wako mmoja kuongezea kwenye kondoo wake. Na hakika washirika wengi hudhulumiana wao kwa wao, isipo kuwa walio amini na wakatenda mema. Na hao ni wachache. Na Daudi akajua kuwa tumemfanyia mtihani. Basi akamwomba msamaha Mola wake Mlezi, na akaanguka kusujudu na kutubia.'),
(3995, 38, 25, 'Basi tukamsamehe kwa hayo. Naye kwa hakika anao mkaribisho mkubwa na marejeo mazuri kwetu.'),
(3996, 38, 26, 'Ewe Daudi! Hakika Sisi tumekufanya wewe uwe Khalifa katika nchi. Basi wahukumu watu kwa haki, wala usifuate matamanio yakakupoteza kwenye Njia ya Mwenyezi Mungu. Hakika wanao ipotea Njia ya Mwenyezi Mungu wakaiacha, watapata adhabu kali kwa sababu ya kusahau kwao Siku ya Hisabu.'),
(3997, 38, 27, 'Na hatukuziumba mbingu na ardhi na viliomo ndani yao bure. Hiyo ni dhana ya walio kufuru. Ole wao walio kufuru kwa Moto utao wapata.'),
(3998, 38, 28, 'Je! Tuwafanye walio amini na wakatenda mema kama wafanyao uharibifu katika nchi? Au tuwafanye wachamngu kama waovu?'),
(3999, 38, 29, 'Na hiki Kitabu, tumekuteremshia wewe, kimebarikiwa, ili wazizingatie Aya zake, na wawaidhike wenye akili.'),
(4000, 38, 30, 'Na Daudi tukamtunukia Suleiman. Alikuwa mja mwema. Na hakika alikuwa mwingi wa kutubia.'),
(4001, 38, 31, 'Alipo pelekewa jioni farasi wasimamao kidete, tayari kutoka shoti;'),
(4002, 38, 32, 'Basi akasema: Navipenda vitu vizuri kwa kumkumbuka Mola wangu Mlezi. Kisha wakafichikana nyuma ya boma.'),
(4003, 38, 33, '(Akasema:) Nirudishieni! Akaanza kuwapapasa miguu na shingo.'),
(4004, 38, 34, 'Na tulimtia mtihanini Suleiman, na tukauweka mwili juu ya kiti chake, kisha akarejea kwa kutubu.'),
(4005, 38, 35, 'Akasema: Mola wangu Mlezi! Nisamehe na unipe ufalme usio mwelekea yeyote baada yangu. Hakika Wewe ndiye Mpaji.'),
(4006, 38, 36, 'Basi tukaufanya upepo umtumikie, ukenda kwa amri yake, popote alipo taka kufika.'),
(4007, 38, 37, 'Na tukayafanya mashet\'ani yamtumikie, wote wajenzi na wapiga mbizi.'),
(4008, 38, 38, 'Na wengine wafungwao kwa minyororo.'),
(4009, 38, 39, 'Hichi ndicho kipawa chetu. Basi toa au zuia, bila ya hisabu.'),
(4010, 38, 40, 'Na hakika yeye kwetu Sisi ana cheo cha kukaribishwa kwetu, na pahala pazuri pa kurejea.'),
(4011, 38, 41, 'Na mkumbuke mja wetu Ayubu alipo mwita Mola wake Mlezi akasema: Kwa hakika Shet\'ani amenifikishia udhia na adhabu.'),
(4012, 38, 42, '(Akaambiwa:) Piga-piga ardhi kwa mguu wako! Basi haya maji baridi ya kuogea na ya kunywa.'),
(4013, 38, 43, 'Na tukampa ahali zake na wengine kama wao pamoja nao, kuwa ni rehema itokayo kwetu, na iwe kumbusho kwa watu wenye akili.'),
(4014, 38, 44, 'Na shika kicha cha vijiti tu mkononi mwako, kisha ndio upigie nacho, wala usivunje kiapo. Hakika tulimkuta mwenye subira, mbora wa waja, kwa hakika alikuwa mwingi wa kutubu.'),
(4015, 38, 45, 'Na wakumbuke waja wetu, Ibrahim na Is-haqa na Yaa\'qubu walio kuwa na nguvu na busara.'),
(4016, 38, 46, 'Sisi tumewakhusisha wao kwa sifa ya ukumbusho wa Akhera.'),
(4017, 38, 47, 'Na hakika wao kwetu sisi ni wateuliwa walio bora.'),
(4018, 38, 48, 'Na mkumbuke Ismail na Alyasaa na Dhulkifli, na wote hao ni katika walio bora.'),
(4019, 38, 49, 'Huu ni ukumbusho. Na hakika wachamngu wana marudio mazuri.'),
(4020, 38, 50, 'Bustani za milele zitazo funguliwa milango yao kwa ajili yao.'),
(4021, 38, 51, 'Humo wataegemea matakia, wawe wanaagiza humo matunda mengi na vinywaji.'),
(4022, 38, 52, 'Na pamoja nao wake zao wenye kutuliza macho, hirimu zao.'),
(4023, 38, 53, 'Haya ndiyo mliyo ahidiwa kwa Siku ya Hisabu.'),
(4024, 38, 54, 'Hakika hii ndiyo riziki yetu isiyo malizika.'),
(4025, 38, 55, 'Ndio hivi! Na hakika wenye kuasi bila ya shaka watapata marudio maovu kabisa;'),
(4026, 38, 56, 'Nayo ni Jahannamu! Wataingia humo. Nacho hicho ni kitanda kiovu mno cha kulalia.'),
(4027, 38, 57, 'Ndio hivi! Basi na wayaonje maji ya moto na ya usaha!'),
(4028, 38, 58, 'Na adhabu nyenginezo za namna hii.'),
(4029, 38, 59, 'Hili ndilo kundi litakalo ingia pamoja nanyi. Hapana makaribisho mema kwao. Hakika hao wanaingia Motoni.'),
(4030, 38, 60, 'Waseme: Lakini nyinyi! Hamna mapokezi mazuri! Nyinyi ndio mlio tusabibisha haya, napo ni pahala paovu kabisa!'),
(4031, 38, 61, 'Waseme: Mola wetu Mlezi! Aliye tusabibisha haya mzidishie adhabu mara mbili Motoni.'),
(4032, 38, 62, 'Watasema: Tuna nini hata hatuwaoni wale watu ambao tukiwahisabu ndio katika waovu?'),
(4033, 38, 63, 'Tulikosea tulipo wafanyia maskhara, au macho yetu tu hayawaoni?'),
(4034, 38, 64, 'Hakika hayo bila ya shaka ndiyo makhasimiano ya watu wa Motoni.'),
(4035, 38, 65, 'Sema: Hakika mimi ni mwonyaji tu; na hapana mungu ila Mwenyezi Mungu Mmoja, Mtenda nguvu,'),
(4036, 38, 66, 'Mola Mlezi wa mbingu na ardhi na viliomo baina yake, Mwenye nguvu, Mwenye kusamehe.'),
(4037, 38, 67, 'Sema: Hii ni khabari kubwa kabisa.'),
(4038, 38, 68, 'Ambayo nyinyi mnaipuuza.'),
(4039, 38, 69, 'Sikuwa na ilimu ya mambo ya viumbe wakuu watukufu walipo kuwa wakishindana.'),
(4040, 38, 70, 'Haikufunuliwa kwangu isipo kuwa ya kwamba hakika mimi ni mwonyaji dhaahiri.'),
(4041, 38, 71, 'Mola wako Mlezi alipo waambia Malaika: Hakika Mimi nitaumba mtu kutokana na udongo.'),
(4042, 38, 72, 'Na nitakapo mkamilisha na kumpulizia roho inayo tokana nami, basi muangukieni kwa kumt\'ii.'),
(4043, 38, 73, 'Basi Malaika wakat\'ii wote pamoja.'),
(4044, 38, 74, 'Isipo kuwa Iblisi; alijivuna na akawa katika makafiri.'),
(4045, 38, 75, 'Mwenyezi Mungu akamwambia: Ewe Iblisi! Kipi kilicho kuzuia kumt\'ii yule niliye muumba kwa mikono yangu? Je! Umejiona mkubwa, au umekuwa katika wakuu kweli?'),
(4046, 38, 76, 'Akasema: Mimi ni bora kuliko yeye. Umeniumba kwa moto, na yeye umemuumba kwa udongo.'),
(4047, 38, 77, 'Akasema: Basi toka humo, kwani hakika wewe umelaanika.'),
(4048, 38, 78, 'Na hakika laana yangu itakuwa juu yako mpaka Siku ya Malipo.'),
(4049, 38, 79, 'Akasema: Mola wangu Mlezi! Nipe muhula mpaka siku watayo fufuliwa viumbe.'),
(4050, 38, 80, 'Mwenyezi Mungu akasema: Basi umekwisha kuwa miongoni mwa walio pewa muhula,'),
(4051, 38, 81, 'Mpaka siku ya wakati maalumu.'),
(4052, 38, 82, 'Akasema (Iblisi): Naapa kwa utukufu wako, bila ya shaka nitawapoteza wote,'),
(4053, 38, 83, 'Isipo kuwa wale waja wako miongoni mwao walio khitariwa.'),
(4054, 38, 84, 'Akasema: Haki! Na haki ninaisema.'),
(4055, 38, 85, 'Bila ya shaka nitaijaza Jahannamu kwa wewe na kwa hao wote wenye kukufuata miongoni mwao.'),
(4056, 38, 86, 'Sema (Ewe Mtume): Sikuombeni ujira juu ya haya, wala mimi si katika wadanganyifu.'),
(4057, 38, 87, 'Hayakuwa haya ila ni mawaidha kwa walimwengu wote.'),
(4058, 38, 88, 'Na bila ya shaka mtajua khabari zake baada ya muda.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Mteremsho wa Kitabu hiki umetokana na Mwenyezi Mungu, Mwenye nguvu, Mwenye hikima.'),
(4060, 39, 2, 'Hakika Sisi tumekuteremshia Kitabu hiki kwa Haki. Basi muabudu Mwenyezi Mungu ukimsafia Dini Yeye tu.'),
(4061, 39, 3, 'Hakika Dini Safi ni ya Mwenyezi Mungu tu. Na wale wanao wafanya wenginewe kuwa ni walinzi wakisema: Sisi hatuwaabudu ila wapate kutujongeza tu kumkaribia Mwenyezi Mungu - Hakika Mwenyezi Mungu atahukumu baina yao katika wanayo khitalifiana. Hakika Mwenyezi Mungu hamwongoi aliye mwongo kafiri.'),
(4062, 39, 4, 'Lau kuwa Mwenyezi Mungu angeli taka kuwa na mwana, basi bila ya shaka angeli teua amtakaye katika alio waumba. Subhanahu, Ametakasika na hayo! Yeye ni Mwenyezi Mungu Mmoja, Mtenda atakalo.'),
(4063, 39, 5, 'Ameumba mbingu na ardhi kwa Haki. Hufunika usiku juu ya mchana, na hufunika mchana juu ya usiku. Na amefanya jua na mwezi vitumike. Kila kimojapo kinakwenda kwa kipindi kilicho wekewa. Jueni mtanabahi! Yeye ndiye Mwenye nguvu, Mwenye kusamehe!'),
(4064, 39, 6, 'Amekuumbeni kutokana na nafsi moja. Kisha akamfanya mwenziwe katika nafsi ile ile. Na akakuleteeni wanyama wa mifugo jozi nane. Anakuumbeni katika matumbo ya mama zenu, umbo baada ya umbo, katika viza vitatu. Huyu ndiye Mwenyezi Mungu Mola wenu Mlezi. Ufalme ni wake. Hapana mungu isipo kuwa Yeye. Basi nyinyi mnageuzwa wapi?'),
(4065, 39, 7, 'Mkikufuru basi Mwenyezi Mungu si mwenye haja nanyi, lakini hafurahii kufuru kwa waja wake. Na mkishukuru Yeye hufurahika nanyi. Wala mbebaji habebi mzigo wa mwingine. Kisha marejeo yenu ni kwa Mola wenu Mlezi. Hapo atakwambieni mliyo kuwa mkiyafanya. Hakika Yeye ni Mwenye kuyajua vyema yaliyomo vifuani.'),
(4066, 39, 8, 'Na taabu inapo mfikia mtu humwomba Mola wake Mlezi naye ameelekea kwake. Kisha akimpa neema kutoka kwake, husahau yale aliyo kuwa akimwitia zamani, na akamfanyia Mwenyezi Mungu washirika ili apoteze watu njia yake. Sema: Starehe na ukafiri wako kwa muda mchache. Kwani hakika wewe utakuwa miongoni mwa watu wa Motoni.'),
(4067, 39, 9, 'Je! Afanyae ibada nyakati za usiku kwa kusujudu na kusimama akitahadhari na Akhera, na akitaraji rehema za Mola wake Mlezi... Sema: Ati watakuwa sawa wale wanao jua na wale wasio jua? Hakika wanao kumbuka ni watu wenye akili.'),
(4068, 39, 10, 'Sema: Enyi waja wangu mlio amini! Mcheni Mola wenu Mlezi. Wale wafanyao wema katika dunia hii watapata wema. Na ardhi ya Mwenyezi Mungu ni kunjufu. Hakika wenye subira watapewa ujira wao bila ya hisabu.'),
(4069, 39, 11, 'Sema: Hakika mimi nimeamrishwa nimuabudu Mwenyezi Mungu kwa kumsafishia Dini Yeye tu.'),
(4070, 39, 12, 'Na nimeamrishwa niwe wa mwanzo wa Waislamu.'),
(4071, 39, 13, 'Sema: Hakika mimi ninaogopa adhabu ya Siku Kubwa nikimuasi Mola wangu Mlezi.'),
(4072, 39, 14, 'Sema: Mwenyezi Mungu tu namuabudu kwa kumsafishia Yeye tu Dini yangu.'),
(4073, 39, 15, 'Basi nyinyi abuduni mpendacho badala yake Yeye. Sema: Hakika walio khasirika ni wale walio jikhasiri nafsi zao na ahali zao Siku ya Kiyama. Tambueni kuwa hiyo ndiyo khasara iliyo dhaahiri.'),
(4074, 39, 16, 'Yatawekwa juu yao mat\'abaka ya moto, na chini yao mat\'abaka. Kwa hayo Mwenyezi Mungu anawakhofisha waja wake. Enyi waja wangu! Nicheni Mimi!'),
(4075, 39, 17, 'Na wale wanao jiepusha na ibada potovu, na wakarejea kwa Mwenyezi Mungu, watapata bishara njema. Basi wabashirie waja wangu.'),
(4076, 39, 18, 'Ambao husikiliza maneno, wakafuata lilio bora yao. Hao ndio alio waongoa Mwenyezi Mungu, na hao ndio wenye akili.'),
(4077, 39, 19, 'Je! Yule mwenye kustahiki hukumu ya adhabu, je, wewe unaweza kumwokoa aliyomo katika Moto?'),
(4078, 39, 20, 'Lakini walio mcha Mola wao Mlezi watapata ghorofa zilizo jengwa juu ya ghorofa; chini yake hupita mito. Ndiyo ahadi ya Mwenyezi Mungu. Mwenyezi Mungu havunji ahadi yake.'),
(4079, 39, 21, 'Je! Huoni kwamba Mwenyezi Mungu ameteremsha maji kutoka mbinguni. Kisha akayapitisha kwenye chemchem katika ardhi, kisha kwa maji hayo akatoa mimea yenye rangi mbali mbali? Kisha hunyauka ukaiona imekuwa kimanjano, na kisha huifanya mapepe. Bila ya shaka katika hayo upo ukumbusho kwa wenye akili.'),
(4080, 39, 22, 'Je! Yule ambaye Mwenyezi Mungu amemfungulia kifua chake kwa Uislamu, na akawa yuko kwenye nuru itokayo kwa Mola wake Mlezi (ni sawa na mwenye moyo mgumu?) Basi ole wao wenye nyoyo ngumu zisio mkumbuka Mwenyezi Mungu! Hao wamo katika upotofu wa dhaahiri.'),
(4081, 39, 23, 'Mwenyezi Mungu ameteremsha hadithi nzuri kabisa, Kitabu chenye kufanana na kukaririwa; husisimua kwacho ngozi za wenye kumkhofu Mola wao Mlezi. Kisha ngozi zao na nyoyo zao hulainika kwa kumkumbuka Mwenyezi Mungu. Huo ndio uwongofu wa Mwenyezi Mungu, na kwa huo humwongoa amtakaye. Na ambaye ameachwa na Mwenyezi Mungu kupotea, basi hapana wa kumwongoa.'),
(4082, 39, 24, 'Je! Atakaye kuwa anapambana kwa uso wake na adhabu mbaya kabisa Siku ya Kiyama (ni sawa na watenda wema?) Na wataambiwa wenye kudhulumu: Onjeni hayo mliyo kuwa mkiyachuma!'),
(4083, 39, 25, 'Walio kuwa kabla yao walikadhibisha, na ikawajia adhabu kutoka pahala wasipo patambua.'),
(4084, 39, 26, 'Basi Mwenyezi Mungu akawaonjesha hizaya katika uhai wa duniani. Na bila ya shaka adhabu ya Akhera ni kubwa zaidi; laiti wangeli jua!'),
(4085, 39, 27, 'Na bila ya shaka tumewapigia watu mifano ya kila namna katika hii Qur\'ani ili wapate kukumbuka.'),
(4086, 39, 28, 'Qur\'ani ya Kiarabu isiyo na upogo, ili wamche Mungu.'),
(4087, 39, 29, 'Mwenyezi Mungu amepiga mfano wa mtu mwenye mabwana washirika wanao gombana, na wa mtu mwengine aliye khusika na bwana mmoja tu. Je! Wako sawa katika hali zao? Alhamdulillahi, Sifa njema zote ni za Mwenyezi Mungu! Lakini wengi wao hawajui.'),
(4088, 39, 30, 'Kwa hakika wewe utakufa, na wao watakufa.'),
(4089, 39, 31, 'Kisha bila ya shaka mtagombana Siku ya Kiyama mbele ya Mola wenu Mlezi.'),
(4090, 39, 32, 'BASI NI NANI dhaalimu mkubwa kuliko yule aliye msingizia uwongo Mwenyezi Mungu na kuikanusha kweli imfikiapo? Je! Siyo katika Jahannamu makaazi ya hao makafiri?'),
(4091, 39, 33, 'Na aliye ileta Kweli na akaithibitisha - hao ndio wachamngu.'),
(4092, 39, 34, 'Watapata watakacho taka kwa Mola wao Mlezi. Hayo ndiyo malipo ya watendao mema.'),
(4093, 39, 35, 'Ili Mwenyezi Mungu awafutie ukomo wa uovu walio ufanya na awalipe ujira wao kwa mujibu wa ukomo wa wema walio kuwa wakiutenda.'),
(4094, 39, 36, 'Je! Mwenyezi Mungu si wa kumtosheleza mja wake? Na ati wanakutishia kwa hao wenginewe wasio kuwa Yeye! Na aliye hukumiwa na Mwenyezi Mungu kupotea hana wa kumwongoa.'),
(4095, 39, 37, 'Na ambaye Mwenyezi Mungu anamwongoa hana wa kumpotoa. Je! Mwenyezi Mungu si Mwenye nguvu anaye weza kulipiza?'),
(4096, 39, 38, 'Na ukiwauliza: Ni nani aliye ziumba mbingu na ardhi. Bila ya shaka watasema: Mwenyezi Mungu. Sema: Je! Mnawaonaje wale mnao waomba badala ya Mwenyezi Mungu, ikiwa Mwenyezi Mungu akitaka kunidhuru, wao wanaweza kuniondolea dhara yake? Au akitaka kunirehemu, je, wao wanaweza kuzuia rehema yake? Sema: Mwenyezi Mungu ananitosheleza. Kwake Yeye wategemee wanao tegemea.'),
(4097, 39, 39, 'Sema: Enyi watu wangu! Fanyeni mwezavyo, mimi pia nafanya. Basi mtakuja ona'),
(4098, 39, 40, 'Ni nani itakaye mfikia adhabu ya kumhizi, na itakaye mshukia adhabu ya daima.'),
(4099, 39, 41, 'Hakika Sisi tumekuteremshia Kitabu kwa ajili ya watu wote kwa Haki. Basi mwenye kuongoka ni kwa nafsi yake, na mwenye kupotoka bila ya shaka amepotoka kwa khasara yake. Na wewe si mlinzi juu yao.'),
(4100, 39, 42, 'MWENYEZI MUNGU huzipokea roho zinapo kufa. Na zile zisio kufa wakati wa kulala kwake, huzishika zilizo hukumiwa kufa, na huzirudisha nyengine mpaka ufike wakati uliowekwa. Hakika katika hayo bila ya shaka zipo Ishara kwa watu wanao fikiri.'),
(4101, 39, 43, 'Au ndio wanachukua waombezi badala ya Mwenyezi Mungu? Sema: Ingawa hawana mamlaka juu ya kitu chochote, wala hawatambui lolote?'),
(4102, 39, 44, 'Sema: Uombezi wote uko kwa Mwenyezi Mungu. Ni wake Yeye tu ufalme wa mbingu na ardhi. Kisha mtarejeshwa kwake.'),
(4103, 39, 45, 'Na anapo tajwa Mwenyezi Mungu peke yake nyoyo za wasio iamini Akhera huchafuka. Na wanapo tajwa wenginewe asiye kuwa Yeye basi wao hufurahi.'),
(4104, 39, 46, 'Sema: Ewe Mwenyezi Mungu! Muumba mbingu na ardhi! Mjuzi wa yasiyo onekana na yanayo onekana! Wewe utahukumu baina ya waja wako katika waliyo kuwa wakikhitalifiana.'),
(4105, 39, 47, 'Na lau kuwa walio dhulumu wangeli kuwa na vyote viliomo duniani na pamoja navyo vingine kama hivyo, bila ya shaka wangeli vitoa kujikomboa katika adhabu mbaya hiyo Siku ya Kiyama. Na yatawadhihirikia kwa Mwenyezi Mungu wasiyo kuwa wakiyatarajia.'),
(4106, 39, 48, 'Na utawadhihirikia ubaya wa waliyo yachuma, yatawazunguka waliyo kuwa wakiyafanyia mzaha.'),
(4107, 39, 49, 'Na dhara inapo mgusa mtu hutuomba. Kisha tunapo mpa neema zitokazo kwetu, husema: Nimepewa haya kwa sababu ya ilimu yangu. Sio! Huo ni mtihani! Lakini wengi wao hawajui!'),
(4108, 39, 50, 'Waliyasema haya waliyo kuwa kabla yao, lakini hayakuwafaa waliyo kuwa wakiyachuma.'),
(4109, 39, 51, 'Basi ukawasibu uovu wa waliyo yachuma. Na wale walio dhulumu miongoni mwa hawa utawasibu uovu wa waliyo yachuma vile vile. Nao si wenye kushinda.'),
(4110, 39, 52, 'Kwani wao hawajui kwamba Mwenyezi Mungu humkunjulia riziki amtakaye na akamkadiria? Hakika katika haya bila ya shaka zipo Ishara kwa watu wanao amini.'),
(4111, 39, 53, 'Sema: Enyi waja wangu walio jidhulumu nafsi zao! Msikate tamaa na rehema ya Mwenyezi Mungu. Hakika Mwenyezi Mungu husamehe dhambi zote. Hakika Yeye ni Mwenye kusamehe, Mwenye kurehemu.'),
(4112, 39, 54, 'Na rejeeni kwa Mola wenu Mlezi, na silimuni kwake, kabla ya kukujieni adhabu. Kisha hapo hamtanusuriwa.'),
(4113, 39, 55, 'Na fuateni yaliyo bora kabisa katika yale yaliyo teremshwa kwenu kutoka kwa Mola wenu Mlezi, kabla haijakujieni adhabu kwa ghafla, na hali hamtambui.'),
(4114, 39, 56, 'Isije ikasema nafsi: Ee majuto yangu kwa yale niliyo poteza upande wa Mwenyezi Mungu, na hakika nilikuwa miongoni mwa wanao fanya maskhara!'),
(4115, 39, 57, 'Au ikasema: Ingeli kuwa Mwenyezi Mungu ameniongoa, bila ya shaka ningeli kuwa miongoni mwa wenye kumchamngu.'),
(4116, 39, 58, 'Au ikasema inapo ona adhabu: Lau kuwa ningeli pata fursa nyengine, ningeli kuwa miongoni mwa wafanya mema.'),
(4117, 39, 59, 'Wapi! Bila ya shaka zilikujia Ishara zangu, nawe ukazikadhibisha, na ukajivuna, na ukawa miongoni mwa makafiri!'),
(4118, 39, 60, 'Na Siku ya Kiyama utawaona walio msingizia uwongo Mwenyezi Mungu nyuso zao zimesawijika. Je! Si katika Jahannamu makaazi ya wanao takabari?'),
(4119, 39, 61, 'Na Mwenyezi Mungu atawaokoa wenye kujikinga kwa ajili ya kufuzu kwao. Hapana uovu utao wagusa, wala hawatahuzunika.'),
(4120, 39, 62, 'Mwenyezi Mungu ndiye Muumba wa kila kitu, na Yeye ndiye Mlinzi juu ya kila kitu.'),
(4121, 39, 63, 'Yeye anazo funguo za mbingu na ardhi. Na wale walio zikataa Ishara za Mwenyezi Mungu, hao ndio wenye kukhasiri.'),
(4122, 39, 64, 'Sema: Je! Mnaniamrisha nimuabudu asiye kuwa Mwenyezi Mungu, enyi majaahili?'),
(4123, 39, 65, 'Na kwa yakini yamefunuliwa kwako na kwa walio kuwa kabla yako: Bila ya shaka ukimshirikisha Mwenyezi Mungu a\'mali zako zitaanguka, na lazima utakuwa miongoni mwa wenye kukhasiri.'),
(4124, 39, 66, 'Bali muabudu Mwenyezi Mungu tu, na uwe miongoni mwa wenye kushukuru.'),
(4125, 39, 67, 'Na wala hawakumhishimu Mwenyezi Mungu kama anavyo stahiki kadiri yake. Na Siku ya Kiyama ardhi yote itakuwa mkononi mwake, na mbingu zitakunjwa katika mkono wake wa kuume. Subhanahu wa Taa\'la Ametakasika na Ametukuka na hayo wanayo mshirikisha nayo.'),
(4126, 39, 68, 'Na litapulizwa barugumu, wazimie waliomo mbinguni na waliomo katika ardhi, isipo kuwa aliye mtaka Mwenyezi Mungu. Kisha litapulizwa mara nyengine. Hapo watainuka wawe wanangojea.'),
(4127, 39, 69, 'Na ardhi itang\'ara kwa Nuru ya Mola wake Mlezi, na Kitabu kitawekwa, na wataletwa Manabii na mashahidi, na patahukumiwa baina yao kwa haki, wala hawatadhulumiwa.'),
(4128, 39, 70, 'Na kila nafsi italipwa kwa yale iliyo yafanya, na Yeye anayajua sana wanayo yatenda.'),
(4129, 39, 71, 'Na walio kufuru wataongozwa kuendea Jahannamu kwa makundi. Mpaka watakapo ifikia itafunguliwa milango yake, na walinzi wake watawaambia: Kwani hawakukujilieni Mitume miongoni mwenu wakikusomeeni Aya za Mola wenu Mlezi na kukuonyeni mkutano wa siku yenu hii? Watasema: Kwani! Lakini limekwisha thibiti neno la adhabu juu ya makafiri.'),
(4130, 39, 72, 'Itasemwa: Ingieni milango ya Jahannamu mdumu humo. Basi nayo ni ubaya ulioje wa makaazi ya wanao takabari!'),
(4131, 39, 73, 'Na walio mcha Mola wao Mlezi wataongozwa kuendea Peponi kwa makundi, mpaka watakapo fikilia, nayo milango yake imekwisha funguliwa. Walinzi wake watawaambia: Salaam Alaikum, Amani iwe juu yenu! Mmet\'ahirika. Basi ingieni humu mkae milele.'),
(4132, 39, 74, 'Nao watasema: Alhamdulillah, Sifa njema zote ni za Mwenyezi Mungu aliye tutimizia ahadi yake, na akaturithisha ardhi, tunakaa katika Bustani popote tupendapo. Basi ni malipo mazuri yaliyoje ya watendao!'),
(4133, 39, 75, 'Na utawaona Malaika wakizunguka pembeni mwa Kiti cha enzi cha Mwenyezi Mungu, wakimtakasa na kumsifu Mola wao Mlezi. Na patahukumiwa baina yao kwa haki na patasemwa: Sifa zote njema ni za Mwenyezi Mungu, Mola Mlezi wa viumbe vyote!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'H\'a, Mim.'),
(4135, 40, 2, 'Uteremsho wa Kitabu umetoka kwa Mwenyezi Mungu Mwenye nguvu, Mwenye kujua.'),
(4136, 40, 3, 'Anaye samehe dhambi na anaye pokea toba, Mkali wa kuadhibu, Mwenye ukarimu, hakuna mungu ila Yeye; marejeo ni kwake.'),
(4137, 40, 4, 'Hawazibishi Ishara za Mwenyezi Mungu ila walio kufuru; basi kusikughuri wewe kutanga tanga kwao katika nchi.'),
(4138, 40, 5, 'Kabla yao walikadhibisha kaumu ya Nuhu na makundi mengine baada yao. Na kila taifa lilikuwa na hamu juu ya Mtume wao wamkamate. Na walibishana kwa upotovu ili kuipindua haki. Kwa hivyo niliwakamata. Basi ilikuwaje adhabu yangu!'),
(4139, 40, 6, 'Namna hivi limewathibitikia makafiri neno la Mola wako Mlezi ya kwamba wao ni watu wa Motoni.'),
(4140, 40, 7, 'Wale wanao beba A\'rshi, na wanao izunguka, wanamsabihi na kumhimidi Mola wao Mlezi, na wanamuamini, na wanawaombea msamaha walio amini kwa kusema: Mola wetu Mlezi! Umekienea kila kitu kwa rehema na ujuzi. Basi wasamehe walio tubu na wakaifuata Njia yako, na waepushe na adhabu ya Jahannamu.'),
(4141, 40, 8, 'Mola wetu Mlezi! Na waingize katika Bustani za Milele ulizo waahidi. Na uwape haya pia wale walio fanya mema miongoni mwa wazee wao na wake zao na dhuriya zao. Hakika Wewe ni Mwenye nguvu, Mwenye hikima.'),
(4142, 40, 9, 'Na waepushe na maovu; kwani umwepushaye maovu siku hiyo, hakika umemrehemu; na huko ndiko kufuzu kukubwa.'),
(4143, 40, 10, 'Kwa hakika wale walio kufuru watanadiwa: Bila ya shaka kukuchukieni Mwenyezi Mungu ni kukubwa kuliko kujichukia nyinyi nafsi zenu, mlipo kuwa mnaitwa kwenye Imani nanyi mkakataa.'),
(4144, 40, 11, 'Watasema: Mola wetu Mlezi! Umetufisha mara mbili, na umetuhuisha mara mbili! Basi tunakiri madhambi yetu. Basi je, ipo njia ya kutoka?'),
(4145, 40, 12, 'Hayo ni hivyo kwa sababu mlikuwa akiombwa Mwenyezi Mungu peke yake mnakataa. Na akishirikishwa mnaamini. Basi hukumu ni ya Mwenyezi Mungu Mtukufu Mkuu.'),
(4146, 40, 13, 'Yeye ndiye anaye kuonyesheni Ishara zake, na anakuteremshieni kutoka mbinguni riziki. Na hapana anaye kumbuka ila anaye rejea.'),
(4147, 40, 14, 'Basi mwombeni Mwenyezi Mungu mkimsafia Dini Yeye tu, na wangachukia makafiri.'),
(4148, 40, 15, 'Yeye ndiye Mwenye vyeo vya juu, Mwenye A\'rshi. Hupeleka Roho kwa amri yake juu ya amtakaye katika waja wake, ili kuonya kwa Siku ya Mkutano.'),
(4149, 40, 16, 'Siku watakayo dhihiri wao. Hapana kitacho fichikana chochote chao kwa Mwenyezi Mungu. Ufalme ni wa nani leo? Ni wa Mwenyezi Mungu Mmoja Mtenda nguvu.'),
(4150, 40, 17, 'Leo kila nafsi italipwa kwa iliyo chuma. Hapana dhulma leo. Hakika Mwenyezi Mungu ni Mwepesi wa kuhisabu.'),
(4151, 40, 18, 'Na waonye siku inayo kurubia, wakati nyoyo zitakapo fika kooni, nao wamejaa huzuni. Madhaalimu hawatakuwa na rafiki, wala mwombezi wa kuitikiwa.'),
(4152, 40, 19, '(Mwenyezi Mungu) anajua khiyana ya macho na yanayo ficha vifua.'),
(4153, 40, 20, 'Na Mwenyezi Mungu huhukumu kwa haki; lakini hao wanao waomba badala yake hawahukumu chochote. Hakika Mwenyezi Mungu ndiye Mwenye kusikia Mwenye kuona.'),
(4154, 40, 21, 'Kwani hawatembei katika ardhi wakaona jinsi ulivyo kuwa mwisho wa walio kuwa kabla yao? Hao walikuwa wamewazidi hawa kwa nguvu na athari katika nchi. Na Mwenyezi Mungu aliwatia mkononi kwa sababu ya madhambi yao. Na wala hapakuwa wa kuwalinda na Mwenyezi Mungu.'),
(4155, 40, 22, 'Hayo ni kwa sababu walikuwa wanawajia Mitume wao kwa hoja zilizo wazi, lakini wakawakataa, ndipo Mwenyezi Mungu akawakamata. Bila ya shaka Yeye ni Mwenye nguvu, Mkali wa kuadhibu.'),
(4156, 40, 23, 'Na kwa yakini tulimtuma Musa pamoja na Ishara zetu na uthibitisho ulio wazi,'),
(4157, 40, 24, 'Kumwendea Firauni na Hamana na Qaruni, wakasema: Huyu ni mchawi, mwongo mkubwa.'),
(4158, 40, 25, 'Basi alipo waletea Haki inayo toka kwetu, walisema: Waueni watoto wanaume wa wale walio muamini na wawacheni hai wanawake wao. Na haviwi vitimbi vya makafiri ila katika upotovu.'),
(4159, 40, 26, 'Na Firauni akasema: Niachieni nimuuwe Musa, naye amwite Mola wake Mlezi! Mimi nachelea asije kubadilishieni dini yenu, au akatangaza uharibifu katika nchi.'),
(4160, 40, 27, 'Na Musa akasema: Mimi najikinga kwa Mola wangu Mlezi na Mola wenu Mlezi anilinde na kila mwenye jeuri asiye iamini Siku ya Hisabu.'),
(4161, 40, 28, 'Na akasema mtu mmoja Muumini, aliye kuwa mmoja katika watu wa Firauni aliye ficha Imani yake: Mtamuuwa mtu kwa sababu anasema Mola wangu Mlezi ni Mwenyezi Mungu? Na hali naye amekujieni na hoja zilizo wazi? Na akiwa yeye mwongo basi uwongo wake ni juu yake mwenyewe, na akiwa mkweli yatakufikieni baadhi ya hayo anayo kuahidini. Hakika Mwenyezi Mungu hamwongoi apindukiaye mipaka, mwongo mkubwa.'),
(4162, 40, 29, 'Enyi watu wangu! Leo mna ufalme, mmeshinda katika nchi; basi ni nani atakaye nisaidia kutuokoa na adhabu ya Mwenyezi Mungu kama ikitufikia? Firauni akasema: Sikupeni shauri ila ile niliyo iona, wala sikuongozeni ila kwenye njia ya uwongofu.'),
(4163, 40, 30, 'Na akasema yule aliye amini: Enyi watu wangu! Hakika mimi nakukhofieni mfano wa siku za makundi,'),
(4164, 40, 31, 'Mfano wa hali ya watu wa Nuhu na A\'di na Thamudi na wale wa baada yao. Na Mwenyezi Mungu hataki kuwadhulumu waja.'),
(4165, 40, 32, 'Na enyi watu wangu! Hakika mimi nakukhofieni Siku ya mayowe.'),
(4166, 40, 33, 'Siku mtakapo geuka kurudi nyuma. Hamtakuwa na wa kukulindeni kwa Mwenyezi Mungu. Na mwenye kuhukumiwa na Mwenyezi Mungu kupotea, basi huyo hana wa kumwongoa.'),
(4167, 40, 34, 'Na alikwisha wajieni Yusuf zamani kwa dalili zilizo wazi, lakini nyinyi mliendelea katika shaka kwa yale aliyo kuleteeni; mpaka alipo kufa mkasema: Mwenyezi Mungu hataleta kabisa Mtume baada yake. Kama hivyo Mwenyezi Mungu humwacha kupotea anaye pindukia mipaka katika maasi anaye jitia shaka.'),
(4168, 40, 35, 'Hao ambao wanajadiliana katika Ishara za Mwenyezi Mungu pasipo ushahidi wowote ulio wafikia, ni chukizo kubwa mbele ya Mwenyezi Mungu na mbele ya walio amini. Hivi ndivyo Mwenyezi Mungu apigavyo muhuri juu ya kila moyo wa jeuri anaye jivuna.'),
(4169, 40, 36, 'Na Firauni akasema: Ewe Hamana! Nijengee mnara ili nipate kuzifikia njia,'),
(4170, 40, 37, 'Njia za mbinguni ili nikamwone Mungu wa Musa. Na kwa hakika mimi bila ya shaka namjua kuwa ni mwongo tu. Na hivi ndivyo Firauni alivyo pambiwa ubaya wa vitendo vyake, na akazuiliwa Njia. Na vitimbi vya Firauni havikuwa ila katika kuangamia tu.'),
(4171, 40, 38, 'Na yule aliye amini alisema: Enyi watu wangu! Nifuateni mimi, nitakuongozeni njia ya uwongozi mwema.'),
(4172, 40, 39, 'Enyi watu wangu! Hakika haya maisha ya dunia ni starehe ipitayo tu, na hakika Akhera ndiyo nyumba ya daima.'),
(4173, 40, 40, 'Mwenye kutenda uovu hatalipwa ila sawa na huo uwovu wake, na anaye tenda wema, akiwa mwanamume au mwanamke, naye ni Muumini, basi hao wataingia Peponi, waruzukiwe humo bila ya hisabu.'),
(4174, 40, 41, 'Na enyi watu wangu! Kwa nini mimi nakuiteni kwenye uwokofu, nanyi mnaniita kwenye Moto?'),
(4175, 40, 42, 'Mnaniita nimkufuru Mwenyezi Mungu na nimshirikishe na yule ambaye simjui, nami nakuiteni kwa Mwenye nguvu Mwenye kusamehe?'),
(4176, 40, 43, 'Bila ya shaka mnaye niitia kumuabudu hastahiki wito duniani wala Akhera. Na hakika marejeo yetu ni kwa Mwenyezi Mungu. Na wanao pindukia mipaka ndio watu wa Motoni!'),
(4177, 40, 44, 'Basi mtayakumbuka ninayo kuambieni. Nami namkabidhi Mwenyezi Mungu mambo yangu. Hakika Mwenyezi Mungu anawaona waja wake.'),
(4178, 40, 45, 'Basi Mwenyezi Mungu akamlinda na ubaya wa hila walizo zifanya. Na adhabu mbaya itawazunguka watu wa Firauni.'),
(4179, 40, 46, 'Wanaonyeshwa Moto asubuhi na jioni. Na itapo fika Saa ya Kiyama patasemwa: Waingizeni watu wa Firauni katika adhabu kali kabisa!'),
(4180, 40, 47, 'Na watapo hojiana huko Motoni, wanyonge watawaambia walio jitukuza: Hakika sisi tulikuwa wafuasi wenu, basi je, hamtuondolei sehemu ya huu Moto?'),
(4181, 40, 48, 'Watasema walio jitukuza: Hakika sote sisi tumo humo humo! Kwani Mwenyezi Mungu kesha hukumu baina ya waja!'),
(4182, 40, 49, 'Na walio Motoni watawaambia walinzi wa Jahannamu: Mwombeni Mola wenu Mlezi atupunguzie walau siku moja ya adhabu.'),
(4183, 40, 50, 'Nao watasema: Je! Hawakuwa wakikufikieni Mitume wenu kwa hoja zilio wazi? Watasema: Kwani? Watasema: Basi ombeni! Na maombi ya makafiri hayawi ila ni kupotea bure.'),
(4184, 40, 51, 'Hakika bila ya shaka Sisi tunawanusuru Mitume wetu na walio amini katika uhai wa duniani na siku watapo simama Mashahidi,'),
(4185, 40, 52, 'Siku ambayo hautawafaa madhaalimu udhuru wao, nao watapata laana, na watapata makaazi mabaya kabisa.'),
(4186, 40, 53, 'Na kwa hakika tulimpa Musa uwongofu, na tukawarithisha Wana wa Israili Kitabu,'),
(4187, 40, 54, 'Ambacho ni Uwongofu na Ukumbusho kwa wenye akili.'),
(4188, 40, 55, 'Basi subiri. Hakika ahadi ya Mwenyezi Mungu ni kweli. Na omba msamaha kwa dhambi zako, na umtakase Mola wako Mlezi kwa kumhimidi jioni na asubuhi.'),
(4189, 40, 56, 'Hakika hao wanao bishana katika Ishara za Mwenyezi Mungu bila ya uthibitisho wowote uliyo wajia, hawana vifuani mwao ila kutaka ukubwa tu, na wala huo hawaufikilii. Basi jikinge kwa Mwenyezi Mungu. Hakika Yeye ni Mwenye kusikia, Mwenye kuona.'),
(4190, 40, 57, 'Bila ya shaka kuumba mbingu na ardhi ni kukubwa zaidi kuliko kuwaumba watu. Lakini watu wengi hawajui.'),
(4191, 40, 58, 'Na kipofu na mwenye kuona hawalingani; na walio amini na watendao mema hawalingani na muovu. Ni machache mnayo yakumbuka.'),
(4192, 40, 59, 'Hakika Saa (ya Kiyama) itafika, nayo haina shaka. Lakini watu wengi hawaamini.'),
(4193, 40, 60, 'Na Mola wenu Mlezi anasema: Niombeni nitakuitikieni. Kwa hakika wale ambao wanajivuna na kuniabudu Mimi wataingia Jahannamu wadhalilike.'),
(4194, 40, 61, 'Mwenyezi Mungu aliye kufanyieni usiku ili mpate kutulia humo, na mchana wa kuonea. Hakika Mwenyezi Mungu bila ya shaka ni Mwenye fadhila juu ya watu, lakini watu wengi hawashukuru.'),
(4195, 40, 62, 'Huyo ndiye Mwenyezi Mungu Mola wenu Mlezi, Muumba wa kila kitu. Hapana mungu ila Yeye; basi mnageuzwa wapi?'),
(4196, 40, 63, 'Namna hivi ndivyo walivyo geuzwa walio kuwa wakizikataa Ishara za Mwenyezi Mungu.'),
(4197, 40, 64, 'Ni Mwenyezi Mungu aliye kufanyieni ardhi kuwa ni pahala pa kukaa, na mbingu kuwa dari. Na akakutieni sura, na akazifanya nzuri sura zenu, na akakuruzukuni vitu vizuri. Huyo ndiye Mwenyezi Mungu Mola wenu Mlezi. Basi ametukuka Mwenyezi Mungu Mola Mlezi wa walimwengu wote.'),
(4198, 40, 65, 'Yeye ndiye aliye Hai - hapana mungu isipo kuwa Yeye. Basi muabuduni Yeye mkimsafishia Dini Yeye. Sifa njema zote ni za Mwenyezi Mungu, Mola Mlezi wa walimwengu wote.'),
(4199, 40, 66, 'Sema: Mimi nimekatazwa kuwaabudu hao mnao waomba badala ya Mwenyezi Mungu, zilipo nijia hoja zilizo dhaahiri kutokana na Mola wangu Mlezi. Na nimeamrishwa nisilimu kwa Mola wa walimwengu wote.'),
(4200, 40, 67, 'Yeye ndiye aliye kuumbeni kwa udongo, kisha kwa tone la manii, kisha kwa pande la damu, kisha akakutoeni katika hali ya mtoto mchanga, kisha mpate kufikilia utu uzima, kisha mpate kuwa wazee. Na kati yenu wapo wanao kufa kabla, na ili mfikie muda ulio kwisha wekwa, na ili mpate kufahamu.'),
(4201, 40, 68, 'Yeye ndiye anaye huisha na anaye fisha. Akihukumu jambo liwe, basi huliambia: Kuwa! Likawa.'),
(4202, 40, 69, 'Je! Huwaoni wale wanao bishana katika Ishara za Mwenyezi Mungu? Wanageuziwa wapi?'),
(4203, 40, 70, 'Ambao wamekanusha Kitabu na yale tuliyo watuma Mitume wetu. Basi watakuja jua.'),
(4204, 40, 71, 'Zitakapo kuwa pingu shingoni mwao na minyororo, huku wanabururwa'),
(4205, 40, 72, 'Katika maji ya moto, kisha wanaunguzwa Motoni,'),
(4206, 40, 73, 'Tena wataambiwa: Wako wapi mlio kuwa mkiwashirikisha'),
(4207, 40, 74, 'Badala ya Mwenyezi Mungu? Watasema: Wametupotea, bali tangu zamani hatukuwa tukiomba kitu! Hivyo ndivyo Mwenyezi Mungu anawaachia makafiri wapotelee mbali.'),
(4208, 40, 75, 'Haya ni kwa sababu mlikuwa mkijitapa katika ardhi pasipo haki, na kwa sababu mlikuwa mkijishaua.'),
(4209, 40, 76, 'Ingieni kwenye milango ya Jahannamu, mdumu humo milele. Basi ni maovu yaliyoje makaazi ya wanao takabari!'),
(4210, 40, 77, 'Basi subiri, hakika ahadi ya Mwenyezi Mungu ni ya kweli. Na ama tukikuonyesha baadhi ya yale tuliyo waahidi au tukikufisha, lakini wao watarudishwa kwetu tu.'),
(4211, 40, 78, 'Na bila ya shaka tuliwatuma Mitume kabla yako. Wengine katika hao tumekusimulia khabari zao, na wengine hatukukusimulia. Na haiwi Mtume alete Ishara yoyote ila kwa idhini ya Mwenyezi Mungu. Basi ikija amri ya Mwenyezi Mungu huhukumiwa kwa Haki, na hapo wapotofu watakhasiri.'),
(4212, 40, 79, 'Mwenyezi Mungu aliye kujaalieni nyama hoa, mifugo, ili muwapande baadhi yao, na muwale baadhi yao.'),
(4213, 40, 80, 'Na katika hao mnayo manufaa kadhaa wa kadhaa, na mnapata kwao haja ziliomo vifuani mwenu; na mnachukuliwa juu yao, na juu ya marikebu.'),
(4214, 40, 81, 'Naye anakuonyesheni Ishara zake. Basi Ishara gani za Mwenyezi Mungu mnazo zikataa?'),
(4215, 40, 82, 'Kwani hawatembei katika ardhi wakaona ulikuwaje mwisho wa walio kuwa kabla yao? Wao walikuwa wengi kuliko hawa, na wamewashinda kwa nguvu na athari katika nchi. Wala hayakuwafaa hayo waliyo kuwa wakiyachuma.'),
(4216, 40, 83, 'Walipo wajia Mitume wao kwa dalili zilizo wazi walijitapa kwa ilimu waliyo kuwa nayo. Basi yakawazunguka waliyo kuwa wakiyafanyia maskhara.'),
(4217, 40, 84, 'Walipo iona adhabu yetu walisema: Tumemuamini Mwenyezi Mungu kuwa ni wa pekee, na tunaikataa miungu tuliyo kuwa tukiishirikisha naye.'),
(4218, 40, 85, 'Lakini imani yao haikuwa yenye kuwafaa kitu wakati ambao wamekwisha iona adhabu yetu. Huu ndio mwendo wa Mwenyezi Mungu ulio kuwa kwa waja wake. Na hapo walio kufuru walikhasiri.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'H\'a Mim'),
(4220, 41, 2, 'Uteremsho huu umetoka kwa Mwingi wa rehema, Mwenye kurehemu.'),
(4221, 41, 3, 'Hichi ni Kitabu kilicho pambanuliwa Aya zake, cha kusomwa kwa Kiarabu kwa watu wanao jua.'),
(4222, 41, 4, 'Kitoacho khabari njema, na chenye kuonya. Lakini wengi katika wao wamepuuza; kwa hivyo hawasikii.'),
(4223, 41, 5, 'Na wakasema: Nyoyo zetu zimo katika vifuniko kwa hayo unayo tuitia, na masikio yetu yana uziwi, na baina yetu na wewe lipo pazia. Basi wewe tenda, nasi tunatenda.'),
(4224, 41, 6, 'Sema: Hakika mimi ni mtu kama nyinyi; inafunuliwa kwangu ya kwamba Mungu wenu ni Mungu Mmoja tu. Basi nyookeni sawa kumwendea Yeye, na mumtake msamaha, na ole wao wanao mshirikisha,'),
(4225, 41, 7, 'Ambao hawatoi Zaka na wanaikataa Akhera.'),
(4226, 41, 8, 'Hakika wanao amini na wakatenda mema watakuwa na ujira usio na ukomo.'),
(4227, 41, 9, 'Sema: Hivyo nyinyi mnamkataa aliye umba ardhi katika siku mbili, na mnamfanyia washirika? Huyu ndiye Mola Mlezi wa walimwengu wote.'),
(4228, 41, 10, 'Na akaweka humo milima juu yake, na akabarikia humo na akakadiria humo chakula chake katika siku nne. Haya ni sawa kabisa kwa wanao uliza.'),
(4229, 41, 11, 'Kisha akazielekea mbingu, na zilikuwa moshi, akaziambia hizo na ardhi: Njooni, kwa khiari au kwa nguvu! Vyote viwili vikasema: Tumekuja nasi ni wat\'iifu.'),
(4230, 41, 12, 'Basi akazifanya mbingu saba kwa siku mbili, na akazipangia kila mbingu mambo yake. Na tukaipamba mbingu ya chini kwa mataa na kwa ulinzi. Hichi ndicho kipimo cha Mwenyezi Mungu Mwenye Kujua.'),
(4231, 41, 13, 'Basi wakipuuza wewe sema: Nakuhadharisheni adhabu mfano wa adhabu ya A\'di na Thamudi,'),
(4232, 41, 14, 'Walipo wajia Mitume mbele yao na nyuma yao wakawaambia: Msimuabudu ila Mwenyezi Mungu! Wakasema: Angeli taka Mola wetu Mlezi bila ya shaka angeli wateremsha Malaika. Basi sisi hakika tunayakataa hayo mliyo tumwa.'),
(4233, 41, 15, 'Ama kina A\'di walijivuna katika nchi bila ya haki, na wakasema: Nani aliye kuwa na nguvu kushinda sisi? Kwani wao hawakuona kwamba Mwenyezi Mungu aliye waumba ni Mwenye nguvu kushinda wao? Na wao wakawa wanazikataa Ishara zetu!'),
(4234, 41, 16, 'Basi tuliwapelekea upepo wa kimbunga katika siku za ukorofi, ili tuwaonjeshe adhabu ya kuwahizi katika uhai wa duniani, na bila ya shaka adhabu ya Akhera ina hizaya zaidi, na wala wao hawatanusuriwa.'),
(4235, 41, 17, 'Na ama Thamudi tuliwaongoza, lakini walipenda upofu kuliko uwongofu. Basi uliwachukua moto wa adhabu ya kuwafedhehi kwa sababu ya yale waliyo kuwa wakiyachuma.'),
(4236, 41, 18, 'Na tukawaokoa walio amini na wakawa wanamcha-mngu.'),
(4237, 41, 19, 'Na siku watakapo kusanywa maadui wa Mwenyezi Mungu kwenye Moto, nao wakikgawanywa kwa makundi.'),
(4238, 41, 20, 'Hata watakapo ufikia Moto yatawashuhudia masikio yao, na macho yao na ngozi zao kwa waliyo kuwa wakiyatenda.'),
(4239, 41, 21, 'Na wao wataziambia ngozi zao: Kwa nini mmetushuhudilia? Nazo zitasema: Ametutamkisha Mwenyezi Mungu ambaye ametamkisha kila kitu! Na Yeye ndiye aliye kuumbeni mara ya kwanza, na kwake Yeye tu mtarejeshwa.'),
(4240, 41, 22, 'Na hamkuwa wenye kujificha hata masikio yenu, na macho yenu, na ngozi zenu, zisikushuhudilieni. Lakini mlidhani kwamba Mwenyezi Mungu hayajui mengi katika mliyo kuwa mkiyatenda.'),
(4241, 41, 23, 'Basi hiyo dhana yenu mliyo kuwa mkimdhania Mola wenu Mlezi. Imekuangamizeni; na mmekuwa miongoni mwa walio khasiri.'),
(4242, 41, 24, 'Basi wakisubiri Moto ndio maskani yao. Na wakiomba radhi hawakubaliwi.'),
(4243, 41, 25, 'Na tukawawekea marafiki, na wakawapambia yaliyo mbele yao na nyuma yao. Basi ikawathibitikia kauli wawe pamoja na mataifa yaliyo pita kabla yao, miongoni mwa majini na watu. Hakika hao wamekuwa ni wenye kukhasirika.'),
(4244, 41, 26, 'Na walio kufuru walisema: Msiisikilize Qur\'ani hii, na timueni zogo, huenda mkashinda.'),
(4245, 41, 27, 'Basi hapana shaka tutawaonjesha hao walio kufuru adhabu kali, na hapana shaka tutawalipa malipo mabaya ya yale waliyo kuwa wakiyafanya.'),
(4246, 41, 28, 'Hayo ndiyo malipo ya Maadui wa Mwenyezi Mungu - Moto! Humo watakuwa na maskani ya kudumu, ndiyo malipo yao kwa sababu walikuwa wakizikataa Ishara zetu.'),
(4247, 41, 29, 'Na walio kufuru watasema: Mola wetu Mlezi! Tuonyeshe walio tupoteza miongoni mwa majini na watu ili tuwaweke chini ya miguu yetu, wapate kuwa miongoni wa walio chini kabisa.'),
(4248, 41, 30, 'Hakika walio sema: Mola wetu Mlezi ni Mwenyezi Mungu! Kisha wakanyooka sawa, hao huwateremkia Malaika wakawaambia: Msiogope, wala msihuzunike; nanyi furahini kwa Pepo mliyo kuwa mkiahidiwa.'),
(4249, 41, 31, 'Sisi ni vipenzi vyenu katika maisha ya dunia na katika Akhera, na humo mtapata kinacho tamaniwa na nafsi zenu, na humo mtapata mtakavyo vitaka.'),
(4250, 41, 32, 'Ni takrima itokayo kwa Mwenye kusamehe, Mwenye kurehemu.'),
(4251, 41, 33, 'Na ni nani mbora wa kusema kuliko aitaye kwa Mwenyezi Mungu na akatenda mema, na akasema: Hakika mimi ni katika Waislamu?'),
(4252, 41, 34, 'Mema na maovu hayalingani. Pinga uovu kwa lilio jema zaidi. Hapo yule ambaye baina yako naye pana uadui atakuwa kama rafiki jamaa wa kukuonea uchungu.'),
(4253, 41, 35, 'Lakini hawapewi wema huu ila wanao subiri, wala hawapewi ila wenye bahati kubwa.'),
(4254, 41, 36, 'Na Shet\'ani akikuchochea kwa uchochezi wewe jikinge kwa Mwenyezi Mungu. Hakika Yeye ni Mwenye kusikia, Mwenye kujua.'),
(4255, 41, 37, 'Na katika Ishara zake ni usiku na mchana, na jua na mwezi. Basi msilisujudie jua wala mwezi, bali msujudieni Mwenyezi Mungu aliye viumba, ikiwa nyinyi mnamuabudu Yeye tu.'),
(4256, 41, 38, 'Na ikiwa watajivuna, basi hao walioko kwa Mola wako Mlezi wanamtakasa Yeye usiku na mchana, na wao hawachoki.'),
(4257, 41, 39, 'Na katika Ishara zake ni kwamba unaiona ardhi nyonge, lakini tunapo iteremshia maji mara unaiona inataharaki na kuumuka. Bila ya shaka aliye ihuisha ardhi ndiye atakaye huisha wafu. Hakika Yeye ni Muweza wa kila kitu.'),
(4258, 41, 40, 'Hakika wale wanao upotoa ukweli uliomo katika Ishara zetu hawatufichikii Sisi. Je! Atakaye tupwa Motoni ni bora au atakaye kuja kwa amani Siku ya Kiyama? Tendeni mpendavyo, kwa hakika Yeye anayaona mnayo yatenda.'),
(4259, 41, 41, 'Kwa hakika wanayo yakataa mawaidha haya yanapo wajia (wataangamia), na haya bila ya shaka ni Kitabu chenye nguvu na utukufu.'),
(4260, 41, 42, 'Hautakifikia upotovu mbele yake wala nyuma yake. Kimeteremshwa na Mwenye hikima, Msifiwa.'),
(4261, 41, 43, 'Huambiwi ila yale yale waliyo ambiwa Mitume wa kabla yako. Hakika Mola wako Mlezi bila ya shaka ni Mwenye maghfira, na ni Mwenye adhabu chungu.'),
(4262, 41, 44, 'Na lau tungeli ifanya Qur\'ani kwa lugha ya kigeni wangeli sema: Kwanini Aya zake haziku pambanuliwa? Yawaje lugha ya kigeni na Mtume Mwaarabu? Sema: Hii Qur\'ani ni uwongofu na poza kwa wenye kuamini. Na wasio amini umo uziwi katika masikio yao, nayo kwao imezibwa hawaioni. Hao wanaitwa nao wako pahala mbali.'),
(4263, 41, 45, 'Na hakika tulimpa Musa Kitabu, lakini pakatokea khitilafu kati yake. Na lau kuwa halikwisha tangulia neno la Mola wako Mlezi wangeli hukumiwa. Na hakika wao wamo katika shaka yenye kuwatia wasiwasi.'),
(4264, 41, 46, 'Anaye tenda mema basi anajitendea mwenyewe nafsi yake, na mwenye kutenda uovu basi ni juu ya nafsi yake vile vile; wala Mola wako Mlezi si mwenye kuwadhulumu waja.'),
(4265, 41, 47, 'UJUZI wa kuijua Saa ya Kiyama unarudishwa kwake tu Mwenyezi Mungu. Na matunda hayatoki katika mafumba yake, wala mwanamke hachukui mimba, wala hazai, ila kwa kujua kwake. Na siku atakayo waita, akawaambia: Wako wapi washirika wangu? Hapo watasema: Tunakiri kwako ya kwamba hapana miongoni mwetu anaye shuhudia hayo.'),
(4266, 41, 48, 'Na wakawapotea wale walio kuwa wakiwaomba hapo kwanza, na wakawa na yakini kuwa hawana pa kukimbilia.'),
(4267, 41, 49, 'Mwanaadamu hachoki kuomba dua za kheri, na inapo mpata shari, mara huvunjika moyo akakata tamaa.'),
(4268, 41, 50, 'Na pindi tukimwonjesha rehema itokayo kwetu baada ya kumgusa dhara, bila ya shaka husema: Haya nayastahiki mimi, wala sidhani kuwa Kiyama kitakuja. Na hata nikirudishwa kwa Mola wangu Mlezi bila ya shaka nina mema yangu kwake! Basi kwa yakini tutawaelezea walio kufuru hayo waliyo yatenda, na kwa yakini tutawaonjesha adhabu ngumu.'),
(4269, 41, 51, 'Na tukimneemesha mwanaadamu hugeuka na kujitenga upande, na inapo mgusa shari, huwa na madua marefu marefu.'),
(4270, 41, 52, 'Sema: Mnaonaje! Ikiwa haya yanatoka kwa Mwenyezi Mungu, nanyi ikawa ndio mmeyakataa, ni nani aliye potea zaidi kuliko aliye katika upinzani wa mbali?'),
(4271, 41, 53, 'Tutawaonyesha Ishara zetu katika upeo wa mbali na katika nafsi zao wenyewe mpaka iwabainikie kwamba haya ni kweli. Je! Haikutoshi kwamba Mola wako Mlezi kuwa Yeye ni Shahidi wa kila kitu?'),
(4272, 41, 54, 'Ama wao wamo katika shaka ya kukutana na Mola wao Mlezi! Ama hakika Yeye amekizunguka kila kitu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'H\'a Mim'),
(4274, 42, 2, 'A\'yn Sin Qaf'),
(4275, 42, 3, 'Hivi ndivyo Mwenyezi Mungu Mwenye nguvu na Mwenye hikima anavyo kuletea Wahyi wewe na walio kabla yako.'),
(4276, 42, 4, 'Ni vyake viliomo mbinguni, na viliomo katika ardhi. Na Yeye ndiye Mtukufu, Mkuu.'),
(4277, 42, 5, 'Zinakaribia mbingu kupasuka juu huko, na Malaika wakimtakasa Mola wao Mlezi na kumhimidi, na wakiwaombea maghfira waliomo kwenye ardhi. Ama hakika Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(4278, 42, 6, 'Na wale walio wafanya walinzi wengine badala yake Yeye, Mwenyezi Mungu ni Mwangalizi juu yao. Wala wewe si wakili juu yao.'),
(4279, 42, 7, 'Na namna hivi tumekufunulia Qur\'ani kwa Kiarabu ili uwaonye watu wa Mama wa Miji na walio pembezoni mwake. Na uhadharishe na Siku ya Mkutano, haina shaka hiyo. Kundi moja litakuwa Peponi, na kundi jengine Motoni.'),
(4280, 42, 8, 'Na Mwenyezi Mungu angeli penda ange wafanya wote umma mmoja, lakini anamwingiza katika rehema yake amtakaye. Na wenye kudhulumu hawana mlinzi wala msaidizi.'),
(4281, 42, 9, 'Au wamechukua walinzi wengine badala yake! Lakini Mwenyezi Mungu ndiye Mlinzi khasa. Na Yeye ndiye anaye huisha wafu. Na Yeye ndiye Mweza wa kila kitu.'),
(4282, 42, 10, 'Na mkikhitalifiana katika jambo lolote, basi hukumu yake iko kwa Mwenyezi Mungu. Huyo Mwenyezi Mungu ndiye Mola wangu Mlezi, ninaye mtegemea na kwake Yeye narejea.'),
(4283, 42, 11, 'Yeye ndiye Muumba mbingu na ardhi, amekujaalieni mke na mume katika nafsi zenu, na katika nyama hoa dume na jike, anakuzidishieni namna hii. Hapana kitu kama mfano wake. Naye ni Mwenye kusikia Mwenye kuona.'),
(4284, 42, 12, 'Yeye ndiye Mwenye funguo za mbingu na ardhi. Humkunjulia riziki amtakaye, na humpimia amtakaye. Hakika Yeye ni Mjuzi wa kila kitu.'),
(4285, 42, 13, 'Amekuamrisheni Dini ile ile aliyo muusia Nuhu na tuliyo kufunulia wewe, na tuliyo wausia Ibrahim na Musa na Isa, kwamba shikeni Dini wala msifarikiane kwayo. Ni magumu kwa washirikina hayo unayo waitia. Mwenyezi Mungu humteua kwake amtakaye, na humwongoa kwake aelekeaye.'),
(4286, 42, 14, 'Na hawakufarikiana ila baada ya kuwajia ilimu kwa sababu ya husuda iliyo kuwa baina yao. Na lau kuwa haikwisha tangulia kauli kutoka kwa Mola wako Mlezi ya kuakhirisha mpaka muda maalumu, basi bila ya shaka palingeli hukumiwa baina yao. Na hakika walio rithishwa Kitabu baada yao wanakitilia shaka inayo wahangaisha.'),
(4287, 42, 15, 'Basi kwa haya waite! Nawe simama sawa sawa kama ulivyo amrishwa, wala usiyafuate matamanio yao. Na sema: Naamini aliyo teremsha Mwenyezi Mungu katika Vitabu. Na nimeamrishwa nifanye uadilifu baina yenu. Mwenyezi Mungu ni Mola wetu Mlezi, na Mola wenu Mlezi. Sisi tuna jukumu kwa vitendo vyetu, na nyinyi mna jukumu kwa vitendo vyenu. Hapana kuhojiana baina yetu na nyinyi. Na Mwenyezi Mungu atatukusanya pamoja, na marejeo ni kwake.'),
(4288, 42, 16, 'Na wanao hojiana juu ya Mwenyezi Mungu baada ya kukubaliwa, hoja za hawa ni baat\'ili mbele ya Mola wao Mlezi, na juu yao ipo ghadhabu, na itakuwa kwao adhabu kali.'),
(4289, 42, 17, 'Mwenyezi Mungu ndiye aliye teremsha Kitabu kwa Haki, na Mizani. Na nini kitakacho kujuulisha ya kwamba pengine Saa ya Kiyama ipo karibu?'),
(4290, 42, 18, 'Wasio amini huihimiza hiyo Saa ifike upesi; lakini wanao amini wanaiogopa, na wanajua kwamba hakika hiyo ni kweli. Ama kwa hakika hao wanao bishana katika khabari za hiyo Saa wamo katika upotofu wa mbali kabisa.'),
(4291, 42, 19, 'Mwenyezi Mungu ni Mpole kwa waja wake. Humruzuku ampendaye. Naye ni Mwenye nguvu, Mtukufu.'),
(4292, 42, 20, 'Mwenye kutaka mavuno ya Akhera tutamzidishia katika mavuno yake, na mwenye kutaka mavuno ya duniani tutampa katika hayo, lakini katika Akhera hana fungu.'),
(4293, 42, 21, 'Au hao wana miungu ya kishirikina walio watungia dini asiyo itolea idhini Mwenyezi Mungu? Na lau lisinge kuwako neno la kupambanua basi wangeli katiwa hukumu baina yao. Na hakika wenye kudhulumu watapata adhabu chungu.'),
(4294, 42, 22, 'Utawaona hao madhaalimu wanavyo kuwa na khofu kwa sababu ya waliyo yachuma, nayo yatawafika tu. Na walio amini na wakatenda mema watakuwamo katika mabustani ya Peponi. Humo watapata wayatakayo kwa Mola wao Mlezi. Hiyo ndiyo fadhila kubwa.'),
(4295, 42, 23, 'Hayo ndiyo aliyo wabashiria Mwenyezi Mungu waja wake walio amini na wakatenda mema. Sema: Sikuombeni malipo yoyote kwenu ila mapenzi katika kujikurubisha. Na anaye fanya wema tutamzidishia wema. Hakika Mwenyezi Mungu ni Msamehevu Mwenye shukrani.'),
(4296, 42, 24, 'Ati wanasema: Amemzulia Mwenyezi Mungu uwongo? Mwenyezi Mungu akipenda atapiga muhuri juu ya moyo wako. Na Mwenyezi Mungu anaufuta upotofu na anaithibitisha Haki kwa maneno yake. Hakika Yeye anayajua yaliomo vifuani.'),
(4297, 42, 25, 'Naye ndiye anaye pokea toba kwa waja wake, na anasamehe makosa, na anayajua mnayo yatenda.'),
(4298, 42, 26, 'Na anawaitikia wanao amini na wakatenda mema, na anawazidishia fadhila zake. Na makafiri watakuwa na adhabu chungu.'),
(4299, 42, 27, 'Na lau kuwa Mwenyezi Mungu angeli wakunjulia riziki waja wake, basi bila ya shaka wangeli panda kiburi katika dunia. Lakini anaiteremsha kwa kipimo akitakacho. Hakika Yeye anawajua vyema waja wake na anawaona.'),
(4300, 42, 28, 'Naye ndiye anaye iteremsha mvua baada ya wao kwisha kata tamaa, na hueneza rehema yake. Naye ndiye Mlinzi Mwenye kuhimidiwa.'),
(4301, 42, 29, 'Na katika Ishara zake ni kuumba mbingu na ardhi, na wanyama alio waeneza. Naye ni Mwenye uweza wa kuwakusanya apendapo.'),
(4302, 42, 30, 'Na misiba inayo kusibuni ni kwa sababu ya vitendo vya mikono yenu. Naye anasamehe mengi.'),
(4303, 42, 31, 'Na nyinyi hamwezi kushinda katika ardhi. Na wala nyinyi hamna mlinzi wala msaidizi badala ya Mwenyezi Mungu.'),
(4304, 42, 32, 'Na katika Ishara zake ni vyombo vinavyo kwenda na kurejea baharini kama vilima.'),
(4305, 42, 33, 'Akitaka, huutuliza upepo navyo hivyo vyombo husimama tutwe juu ya bahari. Hakika katika hayo zipo Ishara kwa kila mwenye kusubiri akashukuru.'),
(4306, 42, 34, 'Au huviangamiza hivyo vyombo kwa sababu ya waliyo yatenda hao watu. Naye husamehe mengi.'),
(4307, 42, 35, 'Na ili wajue wanao jadiliana katika Ishara zetu kwamba hawana pahala pa kukimbilia.'),
(4308, 42, 36, 'Basi vyote mlivyo pewa ni starehe ya maisha ya dunia tu, lakini kilichoko kwa Mwenyezi Mungu ni bora, na cha kudumu kwa ajili ya walio amini, na wakawa wanamtegemea Mola wao Mlezi,'),
(4309, 42, 37, 'Na wanayo yaepuka madhambi makubwa na mambo machafu, na wanapo kasirika wao husamehe,'),
(4310, 42, 38, 'Na wanao mwitikia Mola wao Mlezi, na wakashika Sala, na mambo yao yakawa ni kwa kushauriana baina yao, na kutokana na tulicho waruzuku wakawa wanatoa,'),
(4311, 42, 39, 'Na ambao wanapo fanyiwa jeuri hujitetea.'),
(4312, 42, 40, 'Na malipo ya uovu ni uovu mfano wa huo. Lakini mwenye kusamehe, na akasuluhisha, basi huyo malipo yake yapo kwa Mwenyezi Mungu. Hakika Yeye hawapendi wenye kudhulumu.'),
(4313, 42, 41, 'Na wanao jitetea baada ya kudhulumiwa, hao hapana njia ya kuwalaumu.'),
(4314, 42, 42, 'Bali lawama ipo kwa wale wanao wadhulumu watu, na wanafanya jeuri katika nchi bila ya haki. Hao watapata adhabu iliyo chungu.'),
(4315, 42, 43, 'Na anaye subiri, na akasamehe, hakika hayo ni katika mambo ya kuazimiwa.'),
(4316, 42, 44, 'Na ambaye Mwenyezi Mungu amemwacha kupotea, hana mlinzi baada yake. Na utawaona wenye kudhulumu watakapo iona adhabu wanasema: Je! Ipo njia ya kurudi?'),
(4317, 42, 45, 'Na utawaona wanapelekwa kwenye Moto, nao wamenyenyekea kwa unyonge, wanatazama kwa mtazamo wa kificho. Na walio amini watasema: Hakika wapatao khasara ni hao walio khasiri nafsi zao na ahali zao Siku ya Kiyama. Ama hakika wenye kudhulumu watakuwa katika adhabu ya daima.'),
(4318, 42, 46, 'Wala hawatakuwa na walinzi wa kuwanusuru mbele ya Mwenyezi Mungu. Na ambaye Mwenyezi Mungu amemwacha kupotea, hana njia yoyote.'),
(4319, 42, 47, 'Muitikieni Mola wenu Mlezi kabla haijafika siku isiyo epukika itokayo kwa Mwenyezi Mungu. Siku hiyo hamtakuwa pa kukimbilia wala hamtakuwa na cha kukingia.'),
(4320, 42, 48, 'Na wakipuuza, basi Sisi hatukukupeleka ili uwe mwangalizi wao. Si juu yako ila kufikisha Ujumbe tu. Na hakika Sisi tukimwonjesha mtu rehema kutoka kwetu, huifurahia. Na akisibiwa na ovu kwa sababu ya iliyo yatanguliza mikono yao, basi hakika mtu huyu anakufuru.'),
(4321, 42, 49, 'Ufalme wa mbingu na ardhi ni wa Mwenyezi Mungu; anaumba apendavyo, anamtunukia amtakaye watoto wa kike, na anamtunukia amtakaye watoto wa kiume,'),
(4322, 42, 50, 'Au huwachanganya wanaume na wanawake, na akamfanya amtakaye tasa. Hakika Yeye ni Mjuzi Mwenye uweza.'),
(4323, 42, 51, 'Na haikuwa kwa mwanaadamu kwamba Mwenyezi Mungu amsemeze ila kwa Wahyi (Ufunuo), au kwa nyuma ya pazia, au kumtuma Mjumbe. Naye humfunulia ayatakayo kwa idhini yake. Hakika Yeye ni Mtukufu, Mwenye hikima.'),
(4324, 42, 52, 'Na namna hivi tumekufunulia Qur\'ani kwa amri yetu. Ulikuwa hujui Kitabu ni nini, wala Imani. Lakini tumekifanya kuwa ni Nuru ambayo kwayo tunammwongoa tumtakaye katika waja wetu. Na hakika wewe unaongoa kwenye Njia Iliyo Nyooka.'),
(4325, 42, 53, 'Njia ya Mwenyezi Mungu ambaye ni vyake Yeye tu viliomo mbinguni na katika ardhi. Jueni kuwa mambo yote yanarudi kwa Mwenyezi Mungu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'H\'a Mim'),
(4327, 43, 2, 'Naapa kwa Kitabu kinacho bainisha.'),
(4328, 43, 3, 'Hakika Sisi tumeifanya Qur\'ani kwa Kiarabu ili mfahamu.'),
(4329, 43, 4, 'Na hakika hiyo imo katika Asili ya Maandiko yalioko kwetu, ni tukufu na yenye hikima.'),
(4330, 43, 5, 'Je! Tuache kukukumbusheni kabisa kwa kuwa nyinyi ni watu mlio pita mipaka kwa ukafiri?'),
(4331, 43, 6, 'Na Manabii wangapi tuliwatuma kwa watu wa zamani!'),
(4332, 43, 7, 'Na hawajii Nabii yeyote ila walikuwa wakimkejeli.'),
(4333, 43, 8, 'Na tuliwaangamiza walio kuwa na nguvu kushinda wao. Na mfano wa watu wa zamani umekwisha pita.'),
(4334, 43, 9, 'Na ukiwauliza: Nani aliye ziumba mbingu na ardhi? Bila ya shaka watasema: Kaziumba Mwenye nguvu, Mjuzi,'),
(4335, 43, 10, 'Ambaye amekufanyieni ardhi kama tandiko, na akakufanyieni ndani yake njia mpate kuongoka.'),
(4336, 43, 11, 'Na ambaye ndiye aliye teremsha kutoka mbinguni maji kwa kiasi, na kwa hayo tukaifufua nchi iliyo kufa. Na namna hivi ndivyo mtakavyo tolewa.'),
(4337, 43, 12, 'Na ambaye ndiye aliye umba katika kila kitu jike na dume, na akakufanyieni marikebu na wanyama mnao wapanda.'),
(4338, 43, 13, 'Ili mkae vizuri migongoni mwao, kisha mkumbuke neema za Mola wenu Mlezi mnapokaa sawa sawa juu yao na mseme: Ametakasika aliye mfanya huyu atutumikie, na tusingeli weza kufanya haya wenyewe.'),
(4339, 43, 14, 'Na hakika sisi tutarudi kwa Mola wetu Mlezi.'),
(4340, 43, 15, 'Na wanamfanyia katika waja wake fungu. Kwa hakika mwanaadamu ni mtovu wa fadhila aliye dhaahiri.'),
(4341, 43, 16, 'Au amejichukulia watoto wanawake katika vile alivyo viumba, na akakuteulieni nyinyi watoto wanaume?'),
(4342, 43, 17, 'Na anapo bashiriwa mmoja wao kwa yale aliyo mpigia mfano Mwenyezi Mungu, uso wake husawijika na hujaa hasira.'),
(4343, 43, 18, 'Ati aliye lelewa katika mapambo, na katika mabishano hawezi kusema kwa bayana...?'),
(4344, 43, 19, 'Na wakawafanya Malaika, ambao ni waja wa Mwingi wa Rehema, ni wanawake! Kwani wameshuhudia kuumbwa kwao? Ushahidi wao utaandikwa na wao watahojiwa!'),
(4345, 43, 20, 'Nao husema: Angeli penda Mwingi wa Rehema tusingeli waabudu sisi. Hawana ujuzi wowote wa hayo! Hawana ila kusema uwongo tu!'),
(4346, 43, 21, 'Kwani tuliwapa Kitabu kabla ya hichi, na ikawa wao wanakishikilia hicho?'),
(4347, 43, 22, 'Bali wanasema: Hakika sisi tuliwakuta baba zetu wanashika dini makhsusi, basi na sisi tunaongoza nyayo zao.'),
(4348, 43, 23, 'Na kadhaalika hatukumtuma mwonyaji kwenye mji wowote ila watu wake walio deka kwa starehe walisema: Hakika sisi tuliwakuta baba zetu wanashika dini makhsusi, na sisi tunafuata nyayo zao.'),
(4349, 43, 24, 'Akasema (Mwonyaji): Hata nikikuleteeni yenye uwongofu bora kuliko mlio wakuta nao baba zenu? Wakasema: Sisi tunayakataa tu hayo mliyo tumwa.'),
(4350, 43, 25, 'Basi tukawalipizia. Angalia ulikuwaje mwisho wa walio kadhibisha!'),
(4351, 43, 26, 'Na pale Ibrahim alipo mwambia baba yake na kaumu yake: Hakika mimi ninajitenga mbali na hayo mnayo yaabudu,'),
(4352, 43, 27, 'Isipo kuwa yule aliye niumba, kwani Yeye ataniongoa.'),
(4353, 43, 28, 'Na akalifanya hili liwe neno lenye kubaki katika vizazi vyake ili warejee.'),
(4354, 43, 29, 'Bali tuliwastarehesha hawa na baba zao mpaka ikawafikia Haki na Mtume aliye bainisha.'),
(4355, 43, 30, 'Na ilipo wafikia Haki wakasema: Huu ni uchawi, na sisi hakika tunaukataa.'),
(4356, 43, 31, 'Na walisema: Kwa nini Qur\'ani hii haikuteremshwa kwa mtu mkubwa katika miji miwili hii?'),
(4357, 43, 32, 'Kwani wao ndio wanao gawa rehema za Mola wako Mlezi? Sisi tumewagawanyia baina yao maisha yao katika uhai wa duniani, na tukawanyanyua baadhi yao juu ya wengine kwa daraja nyingi; kwa hivyo baadhi yao wanawafanya wenginewe wawatumikie. Na rehema za Mwenyezi Mungu ni bora kuliko hayo wanayo yakusanya.'),
(4358, 43, 33, 'Na lau isinge kuwa watu watakuwa kundi moja tungeli wajaalia wanao mkufuru Rahmani wana nyumba zao zina dari ya fedha, na ngazi zao pia wanazo pandia,'),
(4359, 43, 34, 'Na milango ya nyumba zao na vitanda wanavyo egemea juu yake,'),
(4360, 43, 35, 'Na mapambo. Lakini hayo si chochote ila ni starehe ya maisha ya dunia tu, na Akhera iliyoko kwa Mola wako Mlezi ni ya wenye kumchamngu.'),
(4361, 43, 36, 'Anaye yafanyia upofu maneno ya Rahmani tunamwekea Shet\'ani kuwa ndiye rafiki yake.'),
(4362, 43, 37, 'Na hakika wao wanawazuilia Njia na wenyewe wanadhani kuwa wameongoka.'),
(4363, 43, 38, 'Hata atakapo tujia atasema: Laiti ungeli kuwako baina yangu na wewe kama umbali baina ya mashariki na magharibi. Rafiki mwovu mno wewe!'),
(4364, 43, 39, 'Na kwa kuwa mlidhulumu, haitakufaeni kitu leo kwamba nyinyi mnashirikiana katika adhabu.'),
(4365, 43, 40, 'Je! Unaweza kuwasikilizisha viziwi, au unaweza kuwaongoa vipofu na waliomo katika upotofu ulio wazi?'),
(4366, 43, 41, 'Na hata tukikuondoa, Sisi tutalipiza kisasi chetu kwao.'),
(4367, 43, 42, 'Au tutakuonyesha tulio waahidi. Nasi bila ya shaka tuna uweza juu yao.'),
(4368, 43, 43, 'Basi wewe yashike yaliyo funuliwa kwako. Hakika wewe uko kwenye Njia Iliyo Nyooka.'),
(4369, 43, 44, 'Na hakika haya ni ukumbusho kwako na kwa kaumu yako. Na mtakuja ulizwa.'),
(4370, 43, 45, 'Na waulize Mitume wetu tulio watuma kabla yako: Je! Tulifanya miungu mingine iabudiwe badala ya Rah\'mani?'),
(4371, 43, 46, 'Na bila ya shaka tulimtuma Musa kwa Ishara zetu ende kwa Firauni na waheshimiwa wake, na akasema: Hakika mimi ni Mtume wa Mola Mlezi wa walimwengu wote!'),
(4372, 43, 47, 'Lakini alipo wajia na Ishara zetu wakaingia kuzicheka.'),
(4373, 43, 48, 'Na hatukuwaonyesha Ishara yoyote ila hiyo ilikuwa kubwa zaidi kuliko nyenginewe. Na tukawakamata kwa adhabu ili warejee.'),
(4374, 43, 49, 'Na wakasema: Ewe mchawi! Tuombee kwa Mola wako Mlezi kwa ile ahadi aliyo kuahidi; hakika sisi tutakuwa wenye kuongoka.'),
(4375, 43, 50, 'Basi tulipo waondolea adhabu hiyo, mara wakaingia kuvunja ahadi.'),
(4376, 43, 51, 'Na Firauni alitangaza kwa watu wake, akisema: Enyi watu wangu! Kwani mimi sinao huu ufalme wa Misri, na hii mito inapita chini yangu? Je! Hamwoni?'),
(4377, 43, 52, 'Au mimi si bora kuliko huyu aliye mnyonge, wala hawezi kusema waziwazi?'),
(4378, 43, 53, 'Basi mbona hakuvikwa vikuku vya dhahabu, au hawakuja Malaika pamoja naye wakamwandama?'),
(4379, 43, 54, 'Basi aliwachezea watu wake, na wakamt\'ii. Kwa hakika hao walikuwa watu wapotovu.'),
(4380, 43, 55, 'Walipo tukasirisha tuliwapatiliza tukawazamisha wote!'),
(4381, 43, 56, 'Kisha tukawafanya kuwa watangulizi na mfano kwa wa baadaye.'),
(4382, 43, 57, 'Na alipo pigiwa mfano Mwana wa Mariamu, watu wako waliupiga ukelele.'),
(4383, 43, 58, 'Wakasema: Miungu yetu kwani si bora kuliko huyu? Hawakukupigia mfano huo ila kwa ubishi tu. Bali hao ni watu wagomvi!'),
(4384, 43, 59, 'Hakuwa yeye (Isa) ila ni Mtumwa tuliye mneemesha na tukamfanya ni mfano kwa Wana wa Israili.'),
(4385, 43, 60, 'Na tungeli penda tungeli wafanyia miongoni mwenu Malaika katika ardhi wakifuatana.'),
(4386, 43, 61, 'Na kwa hakika yeye ni alama ya Saa ya Kiyama. Basi usiitilie shaka, na nifuateni. Hii ndiyo Njia Iliyo Nyooka.'),
(4387, 43, 62, 'Wala asikuzuilieni Shet\'ani. Hakika yeye ni adui yenu wa dhaahiri.'),
(4388, 43, 63, 'Na alipo kuja Isa na dalili zilizo wazi, alisema: Nimekujieni na hikima, na ili nikuelezeni baadhi ya yale mliyo khitalifiana. Basi mcheni Mwenyezi Mungu, na mnit\'ii mimi.'),
(4389, 43, 64, 'Kwa hakika Mwenyezi Mungu ni Mola wangu Mlezi, na ni Mola wenu Mlezi. Basi Muabuduni Yeye tu. Hii ndiyo Njia Iliyo Nyooka.'),
(4390, 43, 65, 'Lakini makundi kwa makundi yao yalikhitalifiana wao kwa wao. Ole wao walio dhulumu kwa adhabu ya siku chungu.'),
(4391, 43, 66, 'Je! Nini wanangojea ila Saa iwajie kwa ghafla na wala wao hawatambui?'),
(4392, 43, 67, 'Siku hiyo marafiki watakuwa ni maadui, wao kwa wao, isipo kuwa wachamngu.'),
(4393, 43, 68, 'Enyi waja wangu! Hamtakuwa na khofu siku hiyo, wala hamtahuzunika.'),
(4394, 43, 69, 'Ambao waliziamini Ishara zangu na walikuwa Waislamu.'),
(4395, 43, 70, 'Ingieni Peponi, nyinyi na wake zenu; mtafurahishwa humo.'),
(4396, 43, 71, 'Watakuwa wanapitishiwa sahani za dhahabu na vikombe; na vitakuwamo ambavyo nafsi zinavipenda na macho yanavifurahia, na nyinyi mtakaa humo milele.'),
(4397, 43, 72, 'Na hiyo ni Pepo mliyo rithishwa kwa hayo mliyo kuwa mkiyafanya.'),
(4398, 43, 73, 'Mnayo humo matunda mengi mtakayo yala.'),
(4399, 43, 74, 'Kwa hakika wakosefu watakaa katika adhabu ya Jahannamu.'),
(4400, 43, 75, 'Hawatapumzishwa nayo na humo watakata tamaa.'),
(4401, 43, 76, 'Wala Sisi hatukuwadhulumu, bali wao ndio walio kuwa madhaalimu.'),
(4402, 43, 77, 'Nao watapiga kelele waseme: Ewe Malik! Na atufishe Mola wako Mlezi! Naye aseme: Hakika nyinyi mtakaa humo humo!'),
(4403, 43, 78, 'Kwa yakini tulikuleteeni Haki, lakini wengi katika nyinyi mlikuwa mnaichukia Haki.'),
(4404, 43, 79, 'Au waliweza kupitisha amri yao? Bali ni Sisi ndio tunao pitisha.'),
(4405, 43, 80, 'Au wanadhani kwamba hatusikii siri zao na minong\'ono yao? Wapi! Na wajumbe wetu wapo karibu nao, wanayaandika.'),
(4406, 43, 81, 'Sema: Ingeli kuwa Rahmani ana mwana, basi mimi ningeli kuwa wa kwanza kumuabudu.'),
(4407, 43, 82, 'Ametakasika Mola Mlezi wa mbingu na ardhi, Mola Mlezi wa A\'rshi, na hayo wanayo msifia.'),
(4408, 43, 83, 'Basi waache wapige porojo wakicheza mpaka wakutwe na hiyo siku yao waliyo ahidiwa.'),
(4409, 43, 84, 'Na Yeye ndiye Mungu mbinguni, na ndiye Mungu katika ardhi. Naye ndiye Mwenye hikima, Mwenye ujuzi.'),
(4410, 43, 85, 'Na ametukuka Mwenye ufalme wa mbingu na ardhi na viliomo ndani yake. Na uko kwake ujuzi wa Saa ya Kiyama, na kwake Yeye mtarudishwa.'),
(4411, 43, 86, 'Wala hao mnao waomba badala yake Yeye hawana uweza wa kumwombea mtu, isipo kuwa anaye shuhudia kwa haki, na wao wanajua.'),
(4412, 43, 87, 'Na ukiwauliza ni nani aliye waumba? Bila ya shaka watasema: Mwenyezi Mungu! Basi ni wapi wanako geuziwa?'),
(4413, 43, 88, 'Na usemi wake (Mtume) ni: Ewe Mola wangu Mlezi! Hakika hawa ni watu wasio amini.'),
(4414, 43, 89, 'Basi wasamehe, na uwambie maneno ya salama. Watakuja jua.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'H\'a Mim'),
(4416, 44, 2, 'Naapa kwa Kitabu kinacho bainisha,'),
(4417, 44, 3, 'Hakika tumekiteremsha katika usiku ulio barikiwa. Hakika Sisi ni Waonyaji.'),
(4418, 44, 4, 'Katika usiku huu hubainishwa kila jambo la hikima,'),
(4419, 44, 5, 'Jambo litokalo kwetu. Hakika Sisi ndio wenye kutuma.'),
(4420, 44, 6, 'Ni rehema itokayo kwa Mola wako Mlezi. Hakika Yeye ni Mwenye kusikia Mwenye kujua.'),
(4421, 44, 7, 'Mola Mlezi wa mbingu na ardhi na vilio baina yao, ikiwa nyinyi mna yakini.'),
(4422, 44, 8, 'Hakuna mungu ila Yeye; anahuisha na anafisha- Mola Mlezi wenu na Mola Mlezi wa baba zenu wa mwanzo.'),
(4423, 44, 9, 'Lakini wao wanacheza katika shaka.'),
(4424, 44, 10, 'Basi ingoje siku ambayo mbingu zitakapo leta moshi ulio dhaahiri,'),
(4425, 44, 11, 'Utakao wafunika watu: Hii ni adhabu chungu!'),
(4426, 44, 12, 'Mola wetu Mlezi! Tuondolee adhabu hii. Hakika tutaamini.'),
(4427, 44, 13, 'Nini faida ya kukumbuka kwao? Na alikwisha wafikia Mtume mwenye kubainisha.'),
(4428, 44, 14, 'Na wao wakamgeuzia uso, na wakasema: Huyu amefunzwa, naye ni mwendawazimu.'),
(4429, 44, 15, 'Hakika sisi tutaiondoa adhabu kidogo, lakini nyinyi kwa yakini mtarejea vile vile!'),
(4430, 44, 16, 'Siku tutayo yashambulia mashambulio makubwa, bila ya shaka Sisi ni wenye kutesa.'),
(4431, 44, 17, 'Na hakika kabla ya hawa tuliwafanyia mtihani watu wa Firauni, na aliwafikia Mtume Mtukufu.'),
(4432, 44, 18, 'Akasema: Nipeni waja wa Mwenyezi Mungu; kwa hakika mimi kwenu ni Mtume Muaminifu.'),
(4433, 44, 19, 'Na msimfanyie kiburi Mwenyezi Mungu; hakika mimi nitakuleteeni uthibitisho ulio wazi.'),
(4434, 44, 20, 'Nami najikinga kwa Mola wangu Mlezi na ndiye Mola wenu Mlezi pia, ili msinipige mawe.'),
(4435, 44, 21, 'Na ikiwa hamniamini, basi jitengeni nami.'),
(4436, 44, 22, 'Ndipo akamwomba Mola wake Mlezi: Hakika watu hawa ni wakosefu.'),
(4437, 44, 23, 'Mwenyezi Mungu akasema: Basi nenda na waja wangu usiku. Kwa yakini mtafuatwa.'),
(4438, 44, 24, 'Na iache bahari vivyo hivyo imeachana, hakika wao hao ni jeshi litakalo zamishwa.'),
(4439, 44, 25, 'Mabustani mangapi, na chemchem ngapi waliziacha!'),
(4440, 44, 26, 'Na mimea na vyeo vitukufu!'),
(4441, 44, 27, 'Na neema walizo kuwa wakijistareheshea!'),
(4442, 44, 28, 'Ndio hivyo! Na tukawarithisha haya watu wenginewe.'),
(4443, 44, 29, 'La mbingu wala ardhi hazikuwalilia, wala hawakupewa muhula.'),
(4444, 44, 30, 'Na bila ya shaka tuliwaokoa Wana wa Israili katika adhabu ya kuwadhalilisha,'),
(4445, 44, 31, 'Ya Firauni. Hakika yeye alikuwa jeuri katika wenye kupindukia mipaka.'),
(4446, 44, 32, 'Na tuliwakhiari kwa ujuzi wetu kuliko walimwengu wenginewe.'),
(4447, 44, 33, 'Na tukawapa katika ishara zenye majaribio yaliyo wazi.'),
(4448, 44, 34, 'Hakika hawa wanasema:'),
(4449, 44, 35, 'Hapana ila kufa kwetu mara ya kwanza tu, wala sisi hatufufuliwi.'),
(4450, 44, 36, 'Basi warudisheni baba zetu, ikiwa nyinyi mnasema kweli.'),
(4451, 44, 37, 'Je! Ni wao bora au watu wa Tubbaa\' na walio kuwa kabla yao? Tuliwaangamiza. Hakika hao walikuwa wakosefu.'),
(4452, 44, 38, 'Na hatukuziumba mbingu na ardhi na vilivyomo baina yake kwa mchezo.'),
(4453, 44, 39, 'Hatukuviumba hivyo ila kwa Haki, lakini wengi wao hawajui.'),
(4454, 44, 40, 'Hakika siku ya Uamuzi ni wakati ulio wekwa kwa wao wote.'),
(4455, 44, 41, 'Siku ambayo rafiki hatamfaa rafiki yake kwa chochote, wala hawatanusuriwa.'),
(4456, 44, 42, 'Isipo kuwa atakaye mrehemu Mwenyezi Mungu. Hakika Yeye ndiye Mwenye nguvu, Mwenye kurehemu.'),
(4457, 44, 43, 'Hakika Mti wa Zaqqum,'),
(4458, 44, 44, 'Ni chakula cha mwenye dhambi.'),
(4459, 44, 45, 'Kama shaba iliyo yayushwa, hutokota matumboni'),
(4460, 44, 46, 'Kama kutokota kwa maji ya moto.'),
(4461, 44, 47, '(Itasemwa:) Mkamateni na mtupeni katikati ya Jahannamu!'),
(4462, 44, 48, 'Kisha mmwagieni juu ya kichwa chake adhabu ya maji yanayo chemka.'),
(4463, 44, 49, 'Onja! Ati wewe ndiye mwenye nguvu, mtukufu!'),
(4464, 44, 50, 'Hakika haya ndiyo mliyo kuwa mkiyatilia shaka.'),
(4465, 44, 51, 'Hakika wachamngu watakuwa katika mahali pa amani,'),
(4466, 44, 52, 'Katika mabustani na chemchem,'),
(4467, 44, 53, 'Watavaa hariri nyepesi na hariri nzito wakikabiliana,'),
(4468, 44, 54, 'Hivi ndivyo itakavyo kuwa, na tutawaoza mahurilaini.'),
(4469, 44, 55, 'Humo watataka kila aina ya matunda, na wakae kwa amani.'),
(4470, 44, 56, 'Humo hawataonja mauti ila mauti yale ya kwanza, na (Mwenyezi Mungu) atawalinda na adhabu ya Jahannamu,'),
(4471, 44, 57, 'Kuwa ni fadhila zitokazo kwa Mola wako Mlezi. Huko ndiko kufuzu kukubwa.'),
(4472, 44, 58, 'Basi tumeifanya nyepesi hii Qur\'ani kwa ulimi wako, ili wapate kukumbuka.'),
(4473, 44, 59, 'Ngoja tu, na wao wangoje pia.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'H\'a Mim'),
(4475, 45, 2, 'Uteremsho wa Kitabu umetoka kwa Mwenyezi Mungu, Mwenye nguvu, Mwenye hikima.'),
(4476, 45, 3, 'Hakika katika mbingu na ardhi ziko Ishara kwa Waumini.'),
(4477, 45, 4, 'Na katika umbo lenu na katika wanyama alio watawanya zimo Ishara kwa watu wenye yakini.'),
(4478, 45, 5, 'Na kupishana usiku na mchana, na riziki anayo iteremsha Mwenyezi Mungu kutoka mbinguni, na kwayo akaifufua ardhi baada ya kufa kwake, na mabadiliko ya upepo, ni Ishara kwa watu wenye akili.'),
(4479, 45, 6, 'Hizi Aya za Mwenyezi Mungu tunakusomea kwa haki; basi hadithi gani watakayo iamini baada ya Mwenyezi Mungu na Aya zake?'),
(4480, 45, 7, 'Ole wake kila mzushi mwenye dhambi!'),
(4481, 45, 8, 'Anaye sikia Aya za Mwenyezi Mungu akisomewa, kisha anashikilia yale yale aliyo katazwa, na anajivuna, kama kwamba hakuzisikia. Basi mbashirie adhabu chungu!'),
(4482, 45, 9, 'Na anapo kijua kitu kidogo katika Aya zetu hukifanyia mzaha. Watu hao ndio watakao kuwa na adhabu ya kufedhehesha.'),
(4483, 45, 10, 'Na nyuma yao ipo Jahannamu. Na walio yachuma hayatawafaa hata kidogo, wala walinzi walio washika badala ya Mwenyezi Mungu. Na watapata adhabu kubwa.'),
(4484, 45, 11, 'Huu ni uwongofu. Na wale wale walio zikataa Ishara za Mola wao Mlezi watapata adhabu inayo tokana na ghadhabu iliyo chungu.'),
(4485, 45, 12, 'Mwenyezi Mungu ndiye aliye ifanya bahari ikutumikieni, ili humo zipite marikebu kwa amri yake, na ili mtafute fadhila yake, na mpate kushukuru.'),
(4486, 45, 13, 'Na amefanya vikutumikieni vilivyomo mbinguni na vilivyomo katika ardhi, vyote vimetoka kwake. Hakika katika hayo zimo Ishara kwa watu wanao fikiri.'),
(4487, 45, 14, 'Waambie walio amini wawasamehe wale wasio zitaraji siku za Mwenyezi Mungu, ili awalipe kwa waliyo kuwa wakiyachuma.'),
(4488, 45, 15, 'Mwenye kutenda mema basi ni kwa ajili ya nafsi yake, na mwenye kutenda uovu ni juu yake mwenyewe. Kisha mtarudishwa kwa Mola wenu Mlezi.'),
(4489, 45, 16, 'Na hakika tuliwapa Wana wa Israili Kitabu na hukumu na Unabii, na tukawaruzuku vitu vizuri vizuri, na tukawafadhilisha kuliko walimwengu wote.'),
(4490, 45, 17, 'Na tukawapa maelezo wazi ya amri. Basi hawakukhitalifiana ila baada ya kuwajia ujuzi, kwa ajili ya uhasidi uliyo kuwa baina yao. Hakika Mola wako Mlezi atahukumu baina yao Siku ya Kiyama katika mambo waliyo kuwa wakikhitalifiana.'),
(4491, 45, 18, 'Kisha tukakuweka wewe juu ya Njia ya haya mambo, basi ifuate, wala usifuate matamanio ya wasio jua kitu.'),
(4492, 45, 19, 'Kwa hakika hao hawatakufaa kitu mbele ya Mwenyezi Mungu. Na hakika wenye kudhulumu ni marafiki wao kwa wao. Na Mwenyezi Mungu ni rafiki wa wamchao.'),
(4493, 45, 20, 'Hizi ni dalili zilizo wazi kwa watu wote, na ni uwongofu, na rehema kwa watu wanao yakinisha.'),
(4494, 45, 21, 'Je! Wanadhani wanao tenda maovu kuwa tutawafanya kama walio amini, na wakatenda mema, sawa sawa uhai wao na kufa kwao? Ni hukumu mbaya wanayo ihukumu!'),
(4495, 45, 22, 'Na Mwenyezi Mungu ameziumba mbingu na ardhi kwa haki, na ili kila nafsi ilipwe yale iliyo yachuma. Nao hawatadhulumiwa.'),
(4496, 45, 23, 'Je! Umemwona aliye fanya matamanio yake kuwa ndiye mungu wake, na Mwenyezi Mungu akamwacha apotee pamoja na kuwa ana ujuzi, na akapiga muhuri juu ya masikio yake, na moyo wake, na akambandika vitanga machoni mwake? Basi nani atamwongoa huyo baada ya Mwenyezi Mungu? Hamkumbuki?'),
(4497, 45, 24, 'Na walisema: Hapana ila huu uhai wetu wa duniani - twafa na twaishi, na hapana kinacho tuhiliki isipo kuwa dahari. Lakini wao hawana ilimu ya hayo, ila wao wanadhani tu.'),
(4498, 45, 25, 'Na wakisomewa Aya zetu zilizo wazi hawana hoja ila kusema: Waleteni baba zetu, ikiwa nyinyi ni wasemao kweli.'),
(4499, 45, 26, 'Sema: Mwenyezi Mungu anakuhuisheni, kisha anakufisheni, kisha anakukusanyeni Siku ya Kiyama isiyo na shaka. Lakini watu wengi hawajui.'),
(4500, 45, 27, 'Na Mwenyezi Mungu ndiye Mwenye ufalme wa mbingu na ardhi. Na siku itapo simama Saa ya Kiyama, siku hiyo wataangamia wenye kushikamana na upotovu.'),
(4501, 45, 28, 'Na utauona kila umma umepiga magoti, na kila umma utaitwa kwenda soma kitabu chake: Leo mtalipwa mliyo kuwa mkiyatenda.'),
(4502, 45, 29, 'Hichi kitabu chetu kinasema juu yenu kwa haki. Hakika Sisi tulikuwa tukiyaandika mliyo kuwa mkiyatenda.'),
(4503, 45, 30, 'Ama walio amini na wakatenda mema, Mola wao Mlezi atawatia katika rehema yake. Huko ndiko kufuzu kulio wazi.'),
(4504, 45, 31, 'Na ama walio kufuru, wataambiwa: Kwani hamkuwa mkisomewa Aya zangu nanyi mkapanda kichwa, na mkawa watu wakosefu?'),
(4505, 45, 32, 'Na ikisemwa: Hakika ahadi ya Mwenyezi Mungu ni kweli, na Saa haina shaka, nyinyi mkisema: Hatuijui hiyo Saa, tunadhania dhana tu, wala hatuna yakini.'),
(4506, 45, 33, 'Basi ubaya wa waliyo yatenda utawadhihirikia, na yatawazunguka waliyo yafanyia maskhara.'),
(4507, 45, 34, 'Na itasemwa: Leo tunakusahauni kama nyinyi mlivyo sahau mkutano wa siku yenu hii; na mahali penu ni Motoni, wala hamna wa kukunusuruni.'),
(4508, 45, 35, 'Hayo ni kwa sababu nyinyi mlizifanyia mzaha Aya za Mwenyezi Mungu; na maisha ya dunia yakakudanganyeni. Basi leo hawatatolewa humo wala hao hawatakubaliwa udhuru wao.'),
(4509, 45, 36, 'Basi sifa njema zote ni za Mwenyezi Mungu, Mola Mlezi wa mbingu, na Mola Mlezi wa ardhi, na Mola Mlezi wa walimwengu wote.'),
(4510, 45, 37, 'Na ukubwa ni wake Yeye tu, mbinguni na katika ardhi, naye ni Mwenye nguvu, Mwenye hikima.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'H\'A MIM'),
(4512, 46, 2, 'Uteremsho wa Kitabu umetoka kwa Mwenyezi Mungu, Mwenye nguvu, Mwenye hikima.'),
(4513, 46, 3, 'Hatukuziumba mbingu na ardhi wala yaliyomo baina yake ila kwa haki na kwa muda maalumu. Na walio kufuru wanayapuuza yale wanayo onywa.'),
(4514, 46, 4, 'Je! Mwawaona wale mnao waomba badala ya Mwenyezi Mungu? Nionyesheni wameumba nini katika ardhi; au hao wanayo shirika katika mbingu? Nileteeni Kitabu kilicho kuwa kabla ya hiki, au alama yoyote ya ilimu, ikiwa mnasema kweli.'),
(4515, 46, 5, 'Na nani mpotofu mkubwa kuliko hao wanao waomba, badala ya Mwenyezi Mungu, ambao hawatawaitikia mpaka Siku ya Kiyama, na wala hawatambui maombi yao.'),
(4516, 46, 6, 'Na ambao watu watakapo kusanywa watakuwa maadui zao, na wataikataa ibada yao.'),
(4517, 46, 7, 'Na wanapo somewa Aya zetu zilizo wazi, walio kufuru husema juu ya haki inapo wajia: Huu ni uchawi dhaahiri.'),
(4518, 46, 8, 'Au wanasema: Ameizua mwenyewe! Sema: Ikiwa nimeizua mimi, basi nyinyi hamwezi kunifaa chochote mbele ya Mwenyezi Mungu. Yeye anajua zaidi hayo mnayo ropokwa; anatosha kuwa shahidi baina yangu na nyinyi. Na Yeye ndiye Mwenye kusamehe, Mwenye kurehemu.'),
(4519, 46, 9, 'Sema: Mimi si kiroja miongoni mwa Mitume. Wala sijui nitakavyo fanywa wala nyinyi. Mimi nafuata niliyo funuliwa tu kwa Wahyi, wala mimi si chochote ila ni mwonyaji mwenye kudhihirisha wazi.'),
(4520, 46, 10, 'Sema: Mwaonaje yakiwa haya ni kweli yametoka kwa Mwenyezi Mungu, nanyi mmeyakataa, na akashuhudia shahidi miongoni mwa Wana wa Israili juu ya mfano wa haya, na akaamini, na nyinyi mnafanya kiburi? Hakika Mwenyezi Mungu hawaongoi watu wenye kudhulumu.'),
(4521, 46, 11, 'Na walio kufuru waliwaambia walio amini: Lau kuwa hii ni kheri, wasingeli tutangulia. Na walipo kosa kuongoka wakasema: Huu ni uzushi wa zamani.'),
(4522, 46, 12, 'Na kabla yake kilikuwapo Kitabu cha Musa, kuwa chenye uwongozi na rehema. Na hichi ni Kitabu cha kusadikisha na kilicho kuja kwa lugha ya Kiarabu, ili kiwaonye walio dhulumu, na kiwe ni bishara kwa watendao mema.'),
(4523, 46, 13, 'Hakika walio sema: Mola wetu Mlezi ni Mwenyezi Mungu; kisha wakatengenea, hawatakuwa na khofu, wala hawatahuzunika.'),
(4524, 46, 14, 'Hao ndio watu wa Peponi, watadumu humo, kuwa ni malipo kwa waliyo kuwa wakiyatenda.'),
(4525, 46, 15, 'Na tumemuusia mwanaadamu awatendee wema wazazi wake wawili. Mama yake amechukua mimba yake kwa taabu, na akamzaa kwa taabu. Na kuchukua mimba kwake hata kumwachisha ziwa ni miezi thalathini. Hata anapo fika utu uzima wake, na akafikilia miaka arubaini, husema: Mola wangu Mlezi! Niwezeshe nishukuru neema zako ulizo nineemesha mimi na wazazi wangu, na nitende mema unayo yapenda. Na unitengenezee dhuriya zangu. Hakika mimi nimetubu kwako, na mimi ni miongoni mwa Waislamu.'),
(4526, 46, 16, 'Hao ndio tunao wapokelea bora ya vitendo vyao walivyo vitenda, na tunayasamehe makosa yao. Watakuwa miongoni mwa watu wa Peponi. Miadi ya kweli hiyo walio ahidiwa.'),
(4527, 46, 17, 'Na ambaye amewafyonya wazazi wake na akawaambia: Ati ndio mnanitisha kuwa nitafufuliwa, na hali vizazi vingi vimekwisha pita kabla yangu! Na hao wazazi humwomba msaada Mwenyezi Mungu (na humwambia mtoto wao): Ole wako! Amini! Hakika ahadi ya Mwenyezi Mungu ni kweli. Na yeye husema: Hayakuwa haya ila ni visa vya watu wa kale.'),
(4528, 46, 18, 'Hao ndio ambao imehakikikishwa hukumu juu yao kama mataifa yaliyo kwisha pita kabla yao miongoni mwa majini na watu. Hakika hao wamekuwa wenye kukhasiri.'),
(4529, 46, 19, 'Na wote watakuwa na daraja zao mbali mbali kwa waliyo yatenda, na ili awalipe kwa vitendo vyao, na wala hawatadhulumiwa.'),
(4530, 46, 20, 'Na siku watakapo letwa walio kufuru kwenye Moto wataambiwa: Nyinyi mlitwaa vitu vyenu vizuri katika uhai wa duniani, na mkastareheshwa navyo. Basi leo ndio mnalipwa adhabu ya fedheha kwa mlivyo kuwa mkitakabari katika ardhi bila ya haki, na kwa sababu mlikuwa mkifanya upotovu.'),
(4531, 46, 21, 'Na mtaje ndugu wa kina A\'di, alipo waonya watu wake kwenye vilima vya mchanga. Na kwa yakini waonyaji wengi walitokea kabla yake na baada yake kuwaambia: Msimuabudu isipo kuwa Mwenyezi Mungu. Hakika mimi nakukhofieni isikupateni adhabu ya siku iliyo kuu.'),
(4532, 46, 22, 'Wakasema: Je! Umetujia ili ututenge mbali na miungu yetu? Basi tuletee hayo unayo tuahidi ikiwa wewe ni miongoni mwa wasemao kweli.'),
(4533, 46, 23, 'Akasema: Hakika ujuzi uko kwa Mwenyezi Mungu. Mimi nakufikishieni niliyo tumwa. Lakini nakuoneni kuwa nyinyi mnafanya ujinga.'),
(4534, 46, 24, 'Basi walipo liona wingu likielekea kwenye mabonde yao, walisema: Wingu hili la kutunyeshea mvua! Kumbe sivyo, haya ni hayo mliyo yahimiza, upepo ambao ndani yake imo adhabu chungu!'),
(4535, 46, 25, 'Unaangamiza kila kitu kwa amri ya Mola wake Mlezi. Wakawa hawaonekani ila nyumba zao tu. Hivyo ndivyo tuwalipavyo wakosefu!'),
(4536, 46, 26, 'Na bila ya shaka tuliwaweka vizuri sio kama tulivyo kuwekeni nyinyi; na tuliwapa masikio, na macho, na nyoyo. Na hayakuwafaa kitu masikio yao, wala macho yao, wala nyoyo zao kwa lolote. Kwani hao walikuwa wakizikataa Ishara za Mwenyezi Mungu, na yale waliyo kuwa wakiyafanyia maskhara ndiyo yakawazunguka.'),
(4537, 46, 27, 'Na hakika tuliiangamiza miji ilio jirani zenu, na tulizitumia Ishara ili wapate kurejea.'),
(4538, 46, 28, 'Basi mbona wale walio washika badala ya Mwenyezi Mungu, kuwa ati hao ndio wawakurubishe, hawakuwanusuru? Bali waliwapotea! Na huo ndio uwongo walio kuwa wakiuzua.'),
(4539, 46, 29, 'Na wakati tulipo waleta kundi la majini kuja kwako kusikiliza Qur\'ani. Basi walipo ihudhuria walisema: Sikilizeni! Na ilipo kwisha somwa, walirudi kwa kaumu yao kwenda kuwaonya.'),
(4540, 46, 30, 'Wakasema: Enyi kaumu yetu! Sisi tumesikia Kitabu kilicho teremshwa baada ya Musa, kinacho sadikisha yaliyo kuwa kabla yake, na kinacho ongoza kwenye Haki na kwenye Njia Iliyo Nyooka.'),
(4541, 46, 31, 'Enyi kaumu yetu! Muitikieni Mwenye kuitia kwa Mwenyezi Mungu, na muaminini, Mwenyezi Mungu atakusameheni, na atakukingeni na adhabu chungu.'),
(4542, 46, 32, 'Na wasio mwitikia Mwitaji wa Mwenyezi Mungu basi hao hawatashinda katika ardhi, wala hawatakuwa na walinzi mbele yake. Hao wamo katika upotovu ulio dhaahiri.'),
(4543, 46, 33, 'Je! Hawaoni ya kwamba Mwenyezi Mungu, aliye ziumba mbingu na ardhi, na hakuchoka kwa kuziumba, kuwa ni Muweza wa kuwafufua wafu? Kwani? Hakika Yeye ni Muweza wa kila kitu.'),
(4544, 46, 34, 'Na siku watakapo letwa makafiri Motoni wakaambiwa: Je! Haya si kweli? Watasema: Kwani? Tunaapa kwa Mola wetu Mlezi, ni kweli! Atasema: Basi onjeni adhabu kwa sababu ya kule kukataa kwenu.'),
(4545, 46, 35, 'Basi subiri, kama walivyo subiri Mitume wenye stahmala kubwa, wala usiwafanyie haraka. Siku watakayo yaona waliyo ahidiwa itakuwa kama kwamba hawakukaa ulimwenguni ila saa moja ya mchana. Huu ndio ufikisho! Kwani huangamizwa wengine isipo kuwa walio waovu tu?');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Walio kufuru na wakazuilia njia ya Mwenyezi Mungu, Yeye atavipotoa vitendo vyao.'),
(4547, 47, 2, 'Na walio amini, na wakatenda mema, na wakaamini aliyo teremshiwa Muhammad - nayo ni Haki iliyo toka kwa Mola wao Mlezi - atawafutia makosa yao na ataitengeneza hali yao.'),
(4548, 47, 3, 'Hayo ni kwa sababu walio kufuru wamefuata upotovu, na walio amini wamefuata Haki iliyo toka kwa Mola wao Mlezi. Hivyo ndivyo Mwenyezi Mungu anavyo wapigia watu mifano yao.'),
(4549, 47, 4, 'Basi mnapo wakuta walio kufuru wapigeni shingoni mwao, mpaka mkiwadhoofisha wafungeni pingu. Tena waachilieni kwa hisani au wajikomboe, mpaka vita vipoe. Ndio hivyo. Na lau angeli taka Mwenyezi Mungu angeli washinda mwenyewe, lakini haya ni kwa sababu akutieni mitihani nyinyi kwa nyinyi. Na walio uliwa katika njia ya Mwenyezi Mungu hatazipoteza a\'mali zao.'),
(4550, 47, 5, 'Atawaongoza na awatengezee hali yao.'),
(4551, 47, 6, 'Na atawaingiza katika Pepo aliyo wajuulisha.'),
(4552, 47, 7, 'Enyi mlio amini! Mkimnusuru Mwenyezi Mungu naye atakunusuruni na ataithibitisha miguu yenu.'),
(4553, 47, 8, 'Na walio kufuru, basi kwao ni maangamizo, na atavipoteza vitendo vyao.'),
(4554, 47, 9, 'Hayo ni kwa sababu waliyachukia aliyo yateremsha Mwenyezi Mungu, basi akaviangusha vitendo vyao.'),
(4555, 47, 10, 'Kwani hawakutembea katika ardhi wakaona ulikuwaje mwisho wa walio kuwa kabla yao? Mwenyezi Mungu aliwaangamiza, na kwa makafiri hawa itakuwa mfano wa hayo.'),
(4556, 47, 11, 'Hayo ni kwa sababu Mwenyezi Mungu ni Mlinzi wa walio amini. Na makafiri hawana mlinzi.'),
(4557, 47, 12, 'Hakika Mwenyezi Mungu atawaingiza walio amini na wakatenda mema katika Bustani zipitiwazo na mito kati yake. Na walio kufuru hujifurahisha na hula kama walavyo wanyama, na Moto ndio makaazi yao.'),
(4558, 47, 13, 'Na miji mingapi iliyo kuwa na nguvu zaidi kuliko mji wako huu ulio kutoa; nao tuliwateketeza na wala hawakuwa wa kuwanusuru.'),
(4559, 47, 14, 'Je! Mwenye kuwa na bayana kutoka kwa Mola wake Mlezi ni kama aliye pambiwa uovu wa vitendo vyake, na wakafuata pumbao lao?'),
(4560, 47, 15, 'Mfano wa Pepo walio ahidiwa wachamngu ina mito ya maji yasiyo vunda, na mito ya maziwa isiyo haribika ladha yake, na mito ya mvinyo yenye ladha kwa wanywao, na mito ya asali iliyo safishwa. Na wao humo watakuwa na kila namna ya matunda, na maghfira kutoka kwa Mola wao Mlezi. Basi hao ni kama watakao dumu Motoni na wakinyweshwa maji yanayo chemka ya kuwakata matumbo yao?'),
(4561, 47, 16, 'Na wapo miongoni mwao wanao kusikiliza, mpaka wakiondoka kwako huwauliza walio pewa ilimu: Amesema nini sasa hivi? Hao ndio ambao Mwenyezi Mungu amepiga muhuri juu ya nyoyo zao, na wakafuata pumbao lao.'),
(4562, 47, 17, 'Na wale walio ongoka anawazidishia uongofu na anawapa uchamngu wao.'),
(4563, 47, 18, 'Kwani wanangojea jengine isipo kuwa iwafikie Saa (ya Kiyama) kwa ghafla? Na hakika alama zake zimekwisha kuja. Na itapo wajia kutawafaa wapi kukumbuka hapo?'),
(4564, 47, 19, 'Basi jua ya kwamba hapana mungu ila Mwenyezi Mungu, na omba maghufira kwa dhambi zako na za Waumini wanaume na Waumini wanawake. Na Mwenyezi Mungu anajua mahali penu pa kwenda na kurudi, na mahali penu pa kukaa.'),
(4565, 47, 20, 'Na Waumini wanasema: Kwa nini haiteremshwi Sura? Na inapo teremshwa Sura madhubuti na ikatajwa ndani yake khabari ya vita, utawaona wenye maradhi katika nyoyo zao wanakutazama mtazamo wa anaye zimia kwa mauti. Basi ni bora kwao'),
(4566, 47, 21, 'Ut\'iifu na kauli njema. Na jambo likisha azimiwa, basi wakiwa ni wakweli kwa Mwenyezi Mungu, bila ya shaka itakuwa ni kheri kwao.'),
(4567, 47, 22, 'Basi yanayo tarajiwa kwenu mkitawala ndio mfisidi katika nchi na mwatupe jamaa zenu?'),
(4568, 47, 23, 'Hao ndio Mwenyezi Mungu alio walaani, na akawatia uziwi, na akawapofoa macho yao.'),
(4569, 47, 24, 'Je! Hawaizingatii hii Qur\'ani? Au kwenye nyoyo (zao) zipo kufuli?'),
(4570, 47, 25, 'Kwa hakika wanao rudi nyuma baada ya kwisha wabainikia uwongofu, Shetani huyo amewashawishi na amewaghuri.'),
(4571, 47, 26, 'Hayo ni kwa sababu waliwaambia walio chukia aliyo yateremsha Mwenyezi Mungu: Tutakut\'iini katika baadhi ya mambo. Na Mwenyezi Mungu anajua siri zao.'),
(4572, 47, 27, 'Basi itakuwaje Malaika watakapo wafisha wakawa wanawapiga nyuso zao na migongo yao!'),
(4573, 47, 28, 'Hayo ni kwa sababu wao waliyafuata yaliyo mchukiza Mwenyezi Mungu na wakachukia yanayo mridhisha, basi akaviangusha vitendo vyao.'),
(4574, 47, 29, 'Je! Wenye maradhi nyoyoni mwao wanadhani kwamba Mwenyezi Mungu hatazidhihirisha chuki zao?'),
(4575, 47, 30, 'Na tungeli penda tungeli kuonyesha hao na ungeli watambua kwa alama zao; lakini bila ya shaka utawajua kwa namna ya msemo wao. Na Mwenyezi Mungu anavijua vitendo vyenu.'),
(4576, 47, 31, 'Na bila ya shaka tutakujaribuni mpaka tuwadhihirishe wapignao Jihadi katika nyinyi na wanao subiri. Nasi tutazifanyia mtihani khabari zenu.'),
(4577, 47, 32, 'Kwa hakika walio kufuru na wakazuilia njia ya Mwenyezi Mungu na wakapinzana na Mtume baada ya kwisha wabainikia uwongofu, hao hawatamdhuru Mwenyezi Mungu kwa lolote. Naye ataviangusha vitendo vyao.'),
(4578, 47, 33, 'Enyi mlio amini! Mt\'iini Mwenyezi Mungu, na mt\'iini Mtume, wala msiviharibu vitendo vyenu.'),
(4579, 47, 34, 'Hakika walio kufuru na wakazuilia njia ya Mwenyezi Mungu, kisha wakafa na hali ni makafiri, Mwenyezi Mungu hatawasamehe makosa yao.'),
(4580, 47, 35, 'Basi msiregee na kutaka suluhu, maana nyinyi ndio mtakao shinda. Na Mwenyezi Mungu yu pamoja nanyi, wala hatakunyimeni malipo ya vitendo vyenu.'),
(4581, 47, 36, 'Hakika uhai wa duniani ni mchezo na pumbao. Na mkiamini na mkamchamngu Mwenyezi Mungu atakupeni ujira wenu, wala hakutakeni mali yenu.'),
(4582, 47, 37, 'Na akikutakeni hayo na kukushikilieni mtafanya ubakhili, na atatoa undani wa chuki zenu.'),
(4583, 47, 38, 'Angalieni! Nyinyi mnaitwa mtumie katika njia ya Mwenyezi Mungu, na wapo katika nyinyi wanao fanya ubakhili. Na anaye fanya ubakhili basi anajifanyia ubakhili mwenyewe. Na Mwenyezi Mungu si mhitaji, na nyinyi ndio wahitaji. Na mkigeuka atawaleta watu wengine badala yenu, nao hawatakuwa kama nyinyi.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Hakika tumekufungulia Ushindi wa dhaahiri'),
(4585, 48, 2, 'Ili Mwenyezi Mungu akusamehe makosa yako yaliyo tangulia na yajayo, na akutimizie neema zake, na akuongoe katika Njia Iliyo Nyooka,'),
(4586, 48, 3, 'Na Mwenyezi Mungu akunusuru nusura yenye nguvu -'),
(4587, 48, 4, 'Yeye ndiye aliye teremsha utulivu katika nyoyo za Waumini ili wazidi Imani juu ya Imani yao. Na Mwenyezi Mungu ndiye mwenye majeshi ya mbingu na ardhi. Na Mwenyezi Mungu ni Mwenye ujuzi, Mwenye hikima -'),
(4588, 48, 5, 'Ili awaingize Waumini wanaume na Waumini wanawake katika Bustani zipitazo mito kati yake, wadumu humo, na awafutie makosa yao. Na huko ndiko kufuzu kukubwa mbele ya Mwenyezi Mungu.'),
(4589, 48, 6, 'Na awape adhabu wanaafiki wanaume na wanaafiki wanawake, na washirikina wanaume na washirikina wanawake, wanao mdhania Mwenyezi Mungu dhana mbaya. Utawafikia mgeuko mbaya, na Mwenyezi Mungu awakasirikie, na awalaani, na awaandalie Jahannamu. Na hayo ni marudio maovu kabisa.'),
(4590, 48, 7, 'Na Mwenyezi Mungu ana majeshi ya mbingu na ardhi. Na Mwenyezi Mungu ni Mwenye nguvu, Mwenye hikima.'),
(4591, 48, 8, 'Hakika tumekutuma wewe uwe Shahidi, na Mbashiri, na Mwonyaji,'),
(4592, 48, 9, 'Ili mumuamini Mwenyezi Mungu na Mtume wake, na mumsaidie, na mumhishimu, na mumtakase asubuhi na jioni.'),
(4593, 48, 10, 'Bila ya shaka wanao fungamana nawe, kwa hakika wanafungamana na Mwenyezi Mungu. Mkono wa Mwenyezi Mungu uko juu ya mikono yao. Basi avunjaye ahadi hizi anavunja kwa kuidhuru nafsi yake; na anaye tekeleza aliyo muahadi Mwenyezi Mungu, Mwenyezi Mungu atamlipa ujira mkubwa.'),
(4594, 48, 11, 'Watakuambia mabedui walio baki nyuma: Yametushughulisha mali yetu na ahali zetu, basi tuombee msamaha. Wanasema kwa ndimi zao yasiyo kuwamo katika nyoyo zao. Sema: Ni nani awezaye kukusaidieni chochote kwa Mwenyezi Mungu akitaka Yeye kukudhuruni au akitaka kukunufaisheni? Bali Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(4595, 48, 12, 'Lakini mlidhani kwamba Mtume na Waumini hawatarudi kabisa kwa ahali zao, na mkapambiwa hayo katika nyoyo zenu, na mkadhania dhana mbaya, na mkawa watu wanao angamia.'),
(4596, 48, 13, 'Na asiye muamini Mwenyezi Mungu na Mtume wake, basi kwa hakika Sisi tumewaandalia makafiri Moto mkali.'),
(4597, 48, 14, 'Na Mwenyezi Mungu ndiye mwenye ufalme wa mbingu na ardhi. Humsamehe amtakaye, na humuadhibu amtakaye. Na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(4598, 48, 15, 'Walio baki nyuma watasema: Mtapo kwenda kuchukua ngawira, tuacheni tukufuateni! Wanataka kuyabadili maneno ya Mwenyezi Mungu. Sema: Hamtatufuata kabisa. Mwenyezi Mungu alikwisha sema kama hivi zamani. Hapo watasema: Bali nyinyi mnatuhusudu. Siyo, bali walikuwa hawafahamu ila kidogo.'),
(4599, 48, 16, 'Waambie walio achwa nyuma katika mabedui: Mtakuja itwa kwenda pigana na watu wakali kwa vita, mpigane nao au wasalimu amri. Basi mkimt\'ii, Mwenyezi Mungu atakupeni ujira mzuri. Na mkigeuka kama mlivyo geuka kwanza, atakuadhibuni kwa adhabu iliyo chungu.'),
(4600, 48, 17, 'Kipofu hana lawama, wala kiguru hana lawama, wala mgonjwa hana lawama. Na mwenye kumt\'ii Mwenyezi Mungu na Mtume wake, atamuingiza katika Mabustani yapitayo mito kati yake. Na atakaye geuka upande atamuadhibu kwa adhabu chungu.'),
(4601, 48, 18, 'Kwa hakika Mwenyezi Mungu amewapa radhi Waumini walipo fungamana nawe chini ya mti, na alijua yaliyomo nyoyoni mwao. Basi akateremsha utulivu juu yao, na akawalipa kwa Ushindi wa karibu.'),
(4602, 48, 19, 'Na ngawira nyingi watakazo zichukua. Na Mwenyezi Mungu ni Mwenye nguvu, Mwenye hikima.'),
(4603, 48, 20, 'Mwenyezi Mungu anakuahidini ngawira nyingi mtakazo zichukua, basi amekutangulizieni hizi kwanza, na akaizuia mikono ya watu isikufikieni, na ili hayo yawe ni Ishara kwa Waumini, na akakuongozeni Njia Iliyo Nyooka.'),
(4604, 48, 21, 'Na mengine hamwezi kuyapata bado, Mwenyezi Mungu amekwisha yazingia. Na Mwenyezi Mungu ni Muweza wa kila kitu.'),
(4605, 48, 22, 'Na lau makafiri wangeli pigana nanyi basi bila ya shaka wangeli geuza migongo, kisha wasingeli pata mlinzi wala msaidizi.'),
(4606, 48, 23, 'Huo ndio mwendo wa Mwenyezi Mungu ulio kwisha pita zamani, wala hutapata mabadiliko katika mwendo wa Mwenyezi Mungu.'),
(4607, 48, 24, 'Na Yeye ndiye aliyeizuia mikono yao kwenu, na mikono yenu kwao, katika bonde la Makka baada ya kukupeni Ushindi juu yao. Na Mwenyezi Mungu ni Mwenye kuyaona myatendayo.'),
(4608, 48, 25, 'Hao ndio walio kufuru na wakakuzuieni msiingie Msikiti Mtakatifu, na wakawazuilia dhabihu kufika mahala pao. Na lau si wanaume Waumini na wanawake Waumini msio wajua mkawasaga na mkaingia makosani kutokana nao bila ya kujua... Mwenyezi Mungu (amefanya haya) ili amuingize amtakaye katika rehema yake. Lau wangeli tengana bila ya shaka tungeli waadhibu walio kufuru kwa adhabu chungu.'),
(4609, 48, 26, 'Pale walio kufuru walipo tia katika nyoyo zao hasira, hasira za kijinga, Mwenyezi Mungu aliteremsha utulivu juu ya Mtume wake na juu ya Waumini, na akawalazimisha neno la kuchamngu. Na wao walikuwa ndio wenye haki nalo na wenye kustahili. Na Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(4610, 48, 27, 'Hakika Mwenyezi Mungu amemtimizia Mtume wake ndoto ya haki. Bila ya shaka nyinyi mtauingia Msikiti Mtakatifu, Inshallah, kwa amani, na hali mmenyoa vichwa vyenu na mmepunguza nywele. Hamtakuwa na khofu. Yeye anajua msiyo yajua. Basi atakupeni kabla ya haya Ushindi karibuni.'),
(4611, 48, 28, 'Yeye ndiye aliye mtuma Mtume wake kwa uwongofu na Dini ya Haki, ili aitukuze juu ya dini zote. Na Mwenyezi Mungu anatosha kuwa Shahidi.'),
(4612, 48, 29, 'Muhammad ni Mtume wa Mwenyezi Mungu. Na walio pamoja naye wana nguvu mbele ya makafiri, na wanahurumiana wao kwa wao. Utawaona wakiinama na kusujudu wakitafuta fadhila na radhi za Mwenyezi Mungu. Alama zao zi katika nyuso zao, kwa athari ya kusujudu. Huu ndio mfano wao katika Taurati. Na mfano wao katika Injili ni kama mmea ulio toa chipukizi lake, kisha ukalitia nguvu, ukawa mnene, ukasimama sawa juu ya ubuwa wake, ukawafurahisha wakulima, ili kuwakasirisha kwa ajili yao makafiri. Mwenyezi Mungu amewaahidi walio amini na wakatenda mema katika wao msamaha na ujira mkubwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Enyi mlio amini! Msitangulie mbele ya Mwenyezi Mungu na Mtume wake, na mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye kusikia, Mwenye kujua.'),
(4614, 49, 2, 'Enyi mlio amini! Msinyanyue sauti zenu kuliko sauti ya Nabii, wala msiseme naye kwa kelele kama mnavyo semezana nyinyi kwa nyinyi, visije vitendo vyenu vikaharibika, na hali hamtambui.'),
(4615, 49, 3, 'Kwa hakika wanao teremsha sauti zao mbele ya Mtume wa Mwenyezi Mungu, hao ndio Mwenyezi Mungu amezisafisha nyoyo zao kwa uchamngu. Hao watakuwa na maghfira na ujira mkubwa.'),
(4616, 49, 4, 'Hakika hao wanao kuita nawe uko nyuma ya vyumba, wengi wao hawana akili.'),
(4617, 49, 5, 'Na lau wao wangeli ngojea mpaka uwatokee ingeli kuwa kheri kwao. Na Mwenyezi Mungu ni Mwenye maghfira, Mwenye kurehemu.'),
(4618, 49, 6, 'Enyi mlio amini! Akikujieni mpotovu na khabari yoyote, ichunguzeni, msije mkawasibu watu kwa kuto jua, na mkawa tena wenye kujuta kwa mliyo yatenda.'),
(4619, 49, 7, 'Na jueni kuwa Mtume wa Mwenyezi Mungu yuko nanyi. Lau angeli kut\'iini katika mambo mengi, bila ya shaka mngeli taabika. Lakini Mwenyezi Mungu amekupendezeeni Imani, na akaipamba katika nyoyo zenu, na amekufanyeni muuchukie ukafiri, na upotovu, na uasi. Hao ndio walio ongoka,'),
(4620, 49, 8, 'Kwa fadhila za Mwenyezi Mungu na neema zake. Na Mwenyezi Mungu ni Mwenye kujua, Mwenye hikima.'),
(4621, 49, 9, 'Na ikiwa makundi mawili katika Waumini yanapigana, basi yapatanisheni. Na likiwa moja la hao linamdhulumu mwenzie, basi lipigeni linalo dhulumu mpaka lirejee kwenye amri ya Mwenyezi Mungu. Na likirudi basi yapatanisheni kwa uadilifu. Na hukumuni kwa haki. Hakika Mwenyezi Mungu anawapenda wanao hukumu kwa haki.'),
(4622, 49, 10, 'Hakika Waumini ni ndugu, basi patanisheni baina ya ndugu zenu, na mcheni Mwenyezi Mungu ili mrehemewe.'),
(4623, 49, 11, 'Enyi mlio amini! Wanaume wasiwadharau wanaume wenzao, wala wanawake wasiwadharau wanawake wenzao. Huenda hao wakawa bora kuliko wao. Wala msivunjiane hishima, wala msiitane kwa majina ya kejeli. Ni uovu kutumia jina baya baada ya kwisha amini. Na wasio tubu, hao ndio wenye kudhulumu.'),
(4624, 49, 12, 'Enyi mlio amini! Jiepusheni na dhana nyingi, kwani baadhi ya dhana ni dhambi. Wala msipelelezane, wala msisengenyane nyinyi kwa nyinyi. Je! Yupo katika nyinyi anaye penda kuila nyama ya nduguye aliye kufa? Mnalichukia hilo! Na mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye kupokea toba, Mwenye kurehemu.'),
(4625, 49, 13, 'Enyi watu! Hakika Sisi tumekuumbeni kutokana na mwanamume na mwanamke. Na tumekujaalieni kuwa ni mataifa na makabila ili mjuane. Hakika aliye mtukufu zaidi kati yenu kwa Mwenyezi Mungu ni huyo aliye mchamngu zaidi katika nyinyi. Hakika Mwenyezi Mungu ni Mwenye kujua, Mwenye khabari.'),
(4626, 49, 14, 'Mabedui wamesema: Tumeamini. Sema: Hamjaamini, lakini semeni: Tumesilimu. Kwani Imani haijaingia katika nyoyo zenu. Na mkimt\'ii Mwenyezi Mungu na Mtume wake, hatakupunguzieni chochote katika vitendo vyenu. Hakika Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(4627, 49, 15, 'Hakika Waumini ni wale walio muamini Mwenyezi Mungu na Mtume wake tena wasitie shaka kabisa, na wakapigana Jihadi katika Njia ya Mwenyezi Mungu kwa mali yao na nafsi zao. Hao ndio wakweli.'),
(4628, 49, 16, 'Sema: Ati ndio mnamfundisha Mwenyezi Mungu Dini yenu, na hali Mwenyezi Mungu anayajua ya katika mbingu na ardhi, na Mwenyezi Mungu anajua kila kitu?'),
(4629, 49, 17, 'Wanakusimbulia kuwa wamesilimu! Sema: Msinisimbulie kwa kusilimu kwenu. Bali Mwenyezi Mungu ndiye aliye kufanyieni hisani kwa kukuongoeni kwenye Imani, ikiwa nyinyi ni wakweli.'),
(4630, 49, 18, 'Hakika Mwenyezi Mungu anajua siri za mbingu na ardhi. Na Mwenyezi Mungu anayaona myatendayo.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Qaaf. Naapa kwa Qur\'ani tukufu!'),
(4632, 50, 2, 'Bali wanastaajabu kwamba amewafikia mwonyaji kutoka miongoni mwao, na wakasema makafiri: Hili ni jambo la ajabu!'),
(4633, 50, 3, 'Ati tukifa na tukawa udongo...? Kurejea huko kuko mbali!'),
(4634, 50, 4, 'Hakika Sisi tunajua kiasi cha kila kinacho punguzwa na ardhi kutokana nao. Na kwetu kipo Kitabu kinacho hifadhi yote.'),
(4635, 50, 5, 'Lakini waliikanusha Haki ilipo wajia; kwa hivyo wamo katika mambo ya mkorogo.'),
(4636, 50, 6, 'Je! Hawaangalii mbingu zilizo juu yao! Vipi tulivyo zijenga, na tukazipamba. Wala hazina nyufa.'),
(4637, 50, 7, 'Na ardhi tumeitandaza na tukaiwekea milima, na tukaiotesha mimea mizuri ya kila namna.'),
(4638, 50, 8, 'Yawe haya ni kifumbua macho na ukumbusho kwa kila mja mwenye kuelekea.'),
(4639, 50, 9, 'Na tumeteremsha kutoka mbinguni maji yaliyo barikiwa, na kwa hayo tukaotesha mabustani na nafaka za kuvunwa.'),
(4640, 50, 10, 'Na mitende mirefu yenye makole yaliyo zaa kwa wingi,'),
(4641, 50, 11, 'Ili iwe ni riziki kwa waja wangu. Na tukaifufua kwa maji nchi iliyo kuwa imekufa. Kama hivyo ndivyo utakavyo kuwa ufufuaji.'),
(4642, 50, 12, 'Kabla yao walikadhibisha kaumu ya Nuhu na wakaazi wa Rassi na Thamudi.'),
(4643, 50, 13, 'Na A\'di na Firauni na kaumu ya Lut\'i.'),
(4644, 50, 14, 'Na wakaazi wa Machakani, na watu wa Tubbaa\'. Wote hao waliwakanusha Mitume, kwa hivyo onyo likathibitika juu yao.'),
(4645, 50, 15, 'Kwani tulichoka kwa kuumba mara ya kwanza. Bali wao wamo katika shaka tu juu ya umbo jipya.'),
(4646, 50, 16, 'Na hakika tumemuumba mtu, nasi tunayajua yanayo mpitikia katika nafsi yake. Na Sisi tuko karibu naye kuliko mshipa wa shingoni mwake.'),
(4647, 50, 17, 'Wanapo pokea wapokeaji wawili, wanao kaa kuliani na kushotoni.'),
(4648, 50, 18, 'Hatamki neno ila karibu yake yupo mwangalizi tayari.'),
(4649, 50, 19, 'Na uchungu wa kutoka roho utamjia kwa haki. Hayo ndiyo uliyo kuwa ukiyakimbia.'),
(4650, 50, 20, 'Na litapulizwa baragumu. Hiyo ndiyo Siku ya Miadi.'),
(4651, 50, 21, 'Na kila nafsi itakuja na pamoja nayo mchungaji na shahidi.'),
(4652, 50, 22, '(Aambiwe): Kwa hakika ulikuwa umeghafilika na haya; basi leo tumekuondolea pazia lako, na kwa hivyo kuona kwako leo ni kukali.'),
(4653, 50, 23, 'Na mwenzake atasema: Huyu niliye naye mimi kesha tayarishwa.'),
(4654, 50, 24, 'Mtupeni katika Jahannamu kila kafiri mwenye inda,'),
(4655, 50, 25, 'Akatazaye kheri, arukaye mipaka, mwenye kutia shaka,'),
(4656, 50, 26, 'Aliye weka mungu mwengine pamoja na Mwenyezi Mungu. Basi mtupeni katika adhabu kali.'),
(4657, 50, 27, 'Mwenzake aseme: Ee Mola wetu Mlezi! Sikumpoteza mimi, bali yeye mwenyewe alikuwa katika upotovu wa mbali.'),
(4658, 50, 28, '(Mwenyezi Mungu) aseme: Msigombane mbele yangu. Nilikuleteeni mbele onyo langu.'),
(4659, 50, 29, 'Mbele yangu haibadilishwi kauli, wala Mimi siwadhulumu waja wangu.'),
(4660, 50, 30, 'Siku tutapo iambia Jahannamu: Je! Umejaa? Nayo itasema: Je! Kuna ziada?'),
(4661, 50, 31, 'Na Pepo italetwa karibu kwa ajili ya wachamngu, haitakuwa mbali.'),
(4662, 50, 32, 'Haya ndiyo mnayo ahidiwa kwa kila mwenye kurejea kwa Mwenyezi Mungu na akajilinda.'),
(4663, 50, 33, 'Mwenye kumwogopa Mwingi wa Rehema hali kuwa hamwoni, na akaja kwa moyo ulio elekea-'),
(4664, 50, 34, '(Ataambiwa) ingieni kwa salama. Hiyo ndiyo siku ya daima dawamu.'),
(4665, 50, 35, 'Humo watapata wakitakacho, na kwetu yako ya ziada.'),
(4666, 50, 36, 'Na kaumu ngapi tuliziangamiza kabla yao walio kuwa na nguvu zaidi kuliko hawa! Nao walitanga tanga katika nchi nyingi. Je! Walipata pa kukimbilia?'),
(4667, 50, 37, 'Hakika katika hayo upo ukumbusho kwa mwenye moyo au akatega sikio naye yupo anashuhudia.'),
(4668, 50, 38, 'Na bila ya shaka Sisi tumeziumba mbingu na ardhi na vilio baina yao mnamo siku sita; na wala hayakutugusa machofu.'),
(4669, 50, 39, 'Basi vumilia kwa hayo wasemayo, na mtakase kwa kumsifu Mola wako Mlezi kabla ya kuchomoza jua na kabla ya kuchwa.'),
(4670, 50, 40, 'Na katika usiku pia mtakase, na baada ya kusujudu.'),
(4671, 50, 41, 'Na sikiliza siku atapo nadi mwenye kunadi kutoka pahala karibu.'),
(4672, 50, 42, 'Siku watakapo sikia ukelele wa haki. Hiyo ndiyo siku ya kufufuka.'),
(4673, 50, 43, 'Hakika Sisi tunahuisha na tunafisha, na kwetu Sisi ndio marejeo.'),
(4674, 50, 44, 'Siku itapo wapasukia ardhi mbio mbio! Mkusanyo huo kwetu ni mwepesi.'),
(4675, 50, 45, 'Sisi tunajua kabisa wayasemayo. Wala wewe si mwenye kuwatawalia kwa ujabari. Basi mkumbushe kwa Qur\'ani anaye liogopa onyo.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Naapa kwa pepo zinazo tawanya,'),
(4677, 51, 2, 'Na zinazo beba mizigo,'),
(4678, 51, 3, 'Na zinazo kwenda kwa wepesi.'),
(4679, 51, 4, 'Na zinazo gawanya kwa amri,'),
(4680, 51, 5, 'Hakika mnayo ahidiwa bila ya shaka ni kweli,'),
(4681, 51, 6, 'Na kwa hakika malipo bila ya shaka yatatokea.'),
(4682, 51, 7, 'Naapa kwa mbingu zenye njia,'),
(4683, 51, 8, 'Hakika nyinyi bila ya shaka mmo katika kauli inayo khitalifiana.'),
(4684, 51, 9, 'Anageuzwa kutokana na haki mwenye kugeuzwa.'),
(4685, 51, 10, 'Wazushi wameangamizwa.'),
(4686, 51, 11, 'Ambao wameghafilika katika ujinga.'),
(4687, 51, 12, 'Wanauliza: Ni lini hiyo siku ya malipo?'),
(4688, 51, 13, 'Hiyo ni siku watakayo adhibiwa Motoni.'),
(4689, 51, 14, 'Onjeni adhabu yenu! Haya ndiyo mliyo kuwa mkiyahimiza.'),
(4690, 51, 15, 'Hakika wachamngu watakuwa katika Mabustani na chemchem.'),
(4691, 51, 16, 'Wanapokea aliyo wapa Mola wao Mlezi. Kwa hakika hao walikuwa kabla ya haya wakifanya mema.'),
(4692, 51, 17, 'Walikuwa wakilala kidogo tu usiku.'),
(4693, 51, 18, 'Na kabla ya alfajiri wakiomba maghfira.'),
(4694, 51, 19, 'Na katika mali yao ipo haki ya mwenye kuomba na asiye omba.'),
(4695, 51, 20, 'Na katika ardhi zipo Ishara kwa wenye yakini.'),
(4696, 51, 21, 'Na pia katika nafsi zenu - Je! Hamwoni?'),
(4697, 51, 22, 'Na katika mbingu ziko riziki zenu na mliyo ahidiwa.'),
(4698, 51, 23, 'Basi naapa kwa Mola Mlezi wa mbingu na ardhi, hakika haya ni kweli kama ilivyo kweli kuwa nyinyi mnasema.'),
(4699, 51, 24, 'Je! Imekufikia hadithi ya wageni wa Ibrahim wanao hishimiwa?'),
(4700, 51, 25, 'Walipo ingia kwake wakasema: Salama! Na yeye akasema: Salama! Nyinyi ni watu nisio kujueni.'),
(4701, 51, 26, 'Akenda kwa ahali yake na akaja na ndama aliye nona.'),
(4702, 51, 27, 'Akawakaribisha, akasema: Mbona hamli?'),
(4703, 51, 28, 'Akahisi kuwaogopa katika nafsi yake. Wakasema: Usiwe na khofu, nao wakambashiria kupata kijana mwenye ilimu.'),
(4704, 51, 29, 'Ndipo mkewe akawaelekea, na huku anasema na akijipiga usoni kwa mastaajabu, na kusema: Mimi ni kikongwe na tasa!'),
(4705, 51, 30, 'Wakasema: Ndivyo vivyo hivyo alivyo sema Mola wako Mlezi. Hakika Yeye ni Mwenye hikima na Mwenye kujua.'),
(4706, 51, 31, 'AKASEMA: Basi ujumbe wenu ni nini, enyi mlio tumwa?'),
(4707, 51, 32, 'Wakasema: Hakika sisi tumetumwa kwa watu wakosefu,'),
(4708, 51, 33, 'Tuwatupie mawe ya udongo,'),
(4709, 51, 34, 'Yaliyo tiwa alama kutoka kwa Mola wako Mlezi kwa ajili ya wanao pindukia mipaka.'),
(4710, 51, 35, 'Kwa hivyo tutawatoa katika hao wale walio amini.'),
(4711, 51, 36, 'Lakini hatukupata humo ila nyumba moja tu yenye Waislamu!'),
(4712, 51, 37, 'Na tukaacha humo Ishara kwa ajili ya wanao iogopa adhabu chungu.'),
(4713, 51, 38, 'Na katika khabari za Musa, tulipo mtuma kwa Firauni na hoja wazi.'),
(4714, 51, 39, 'Lakini alipuuza kwa sababu ya nguvu zake na akasema: Huyu ni mchawi au mwendawazimu!'),
(4715, 51, 40, 'Basi tukamkamata yeye na majeshi yake na tukawatupa baharini, na yeye ndiye wa kulaumiwa.'),
(4716, 51, 41, 'Na katika khabari za A\'di tulipo wapelekea upepo wa kukata uzazi.'),
(4717, 51, 42, 'Haukuacha chochote ulicho kifikia ila ulikifanya kama kilio nyambuka.'),
(4718, 51, 43, 'Na katika khabari za Thamudi walipo ambiwa: Jifurahisheni kwa muda mdogo tu.'),
(4719, 51, 44, 'Wakaasi amri ya Mola wao Mlezi. Basi uliwanyakua moto wa radi nao wanaona.'),
(4720, 51, 45, 'Basi hawakuweza kusimama wala hawakuwa wenye kujitetea.'),
(4721, 51, 46, 'Na kaumu ya Nuhu hapo mbele. Hakika hao walikuwa watu wapotovu.'),
(4722, 51, 47, 'Na mbingu tumezifanya kwa nguvu na uwezo na hakika Sisi bila ya shaka ndio twenye uwezo wa kuzitanua.'),
(4723, 51, 48, 'Na ardhi tumeitandaza; basi watandazaji wazuri namna gani Sisi!'),
(4724, 51, 49, 'Na katika kila kitu tumeumba kwa jozi ili mzingatie.'),
(4725, 51, 50, 'Basi kimbilieni kwa Mwenyezi Mungu, hakika mimi ni mwonyaji kwenu wa kubainisha nitokae kwake.'),
(4726, 51, 51, 'Wala msifanye kuwa kuna mungu mwengine pamoja na Mwenyezi Mungu. Hakika mimi ni mwonyaji kwenu wa kubainisha nitokae kwake.'),
(4727, 51, 52, 'Basi ndio hivyo hivyo, hakuwajia kabla yao Mtume ila walisema: Huyu ni mchawi au mwendawazimu.'),
(4728, 51, 53, 'Je! Wameambizana kwa haya? Bali hawa ni watu waasi.'),
(4729, 51, 54, 'Basi waachilie mbali, nawe hulaumiwi.'),
(4730, 51, 55, 'Na kumbusha, kwani ukumbusho huwafaa Waumini.'),
(4731, 51, 56, 'Nami sikuwaumba majini na watu ila waniabudu Mimi.'),
(4732, 51, 57, 'Na sitaki kwao riziki, wala sitaki wanilishe.'),
(4733, 51, 58, 'Hakika Mwenyezi Mungu ndiye Mwenye kuruzuku, Mwenye nguvu, Madhubuti.'),
(4734, 51, 59, 'Hakika walio dhulumu wana fungu lao la adhabu kama fungu la wenzao. Basi wasinihimize.'),
(4735, 51, 60, 'Ole wao walio ikanusha siku yao waliyo ahidiwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Naapa kwa mlima wa T\'ur,'),
(4737, 52, 2, 'Na Kitabu kilicho andikwa'),
(4738, 52, 3, 'Katika ngozi iliyo kunjuliwa,'),
(4739, 52, 4, 'Na kwa Nyumba iliyo jengwa,'),
(4740, 52, 5, 'Na kwa dari iliyo nyanyuliwa,'),
(4741, 52, 6, 'Na kwa bahari iliyo jazwa,'),
(4742, 52, 7, 'Hakika adhabu ya Mola wako Mlezi hapana shaka itatokea.'),
(4743, 52, 8, 'Hapana wa kuizuia.'),
(4744, 52, 9, 'Siku zitakapo tikisika mbingu kwa mtikiso,'),
(4745, 52, 10, 'Na milima iwe inakwenda kwa mwendo mkubwa.'),
(4746, 52, 11, 'Basi ole wao siku hiyo hao wanao kadhibisha,'),
(4747, 52, 12, 'Ambao wanacheza katika mambo ya upuuzi.'),
(4748, 52, 13, 'Siku watapo sukumwa kwenye Moto kwa msukumo wa nguvu,'),
(4749, 52, 14, '(Waambiwe): Huu ndio ule Moto mlio kuwa mkiukanusha!'),
(4750, 52, 15, 'Je! Huu ni uchawi, au hamwoni tu?'),
(4751, 52, 16, 'Uingieni, mkistahamili au msistahamili - ni mamoja kwenu. Hakika mnalipwa kwa mliyo kuwa mkiyatenda.'),
(4752, 52, 17, 'Hakika wachamngu watakuwa katika Mabustani na neema,'),
(4753, 52, 18, 'Wakifurahi kwa yale aliyo wapa Mola wao Mlezi. Na Mola wao Mlezi atawalinda na adhabu ya Motoni.'),
(4754, 52, 19, 'Kuleni na kunyweni kwa raha kabisa kwa sababu ya mliyo kuwa mkiyatenda.'),
(4755, 52, 20, 'Watakuwa wameegemea juu ya viti vya enzi vilivyo pangwa kwa safu. Na tutawaoza mahuru-l-aini.'),
(4756, 52, 21, 'Na walio amini na dhuriya zao wakawafuata kwa Imani tutawakutanisha nao dhuriya zao, na wala hatutawapunja hata kidogo katika vitendo vyao. Kila mtu lazima atapata alicho kichuma.'),
(4757, 52, 22, 'Na tutawapa matunda, na nyama kama watavyo penda.'),
(4758, 52, 23, 'Watapeana humo bilauri zisio na vinywaji vya kuleta maneno ya upuuzi wala dhambi.'),
(4759, 52, 24, 'Iwe wanawapitia watumishi wao kama kwamba ni lulu zilizomo katika chaza.'),
(4760, 52, 25, 'Wataelekeana wakiulizana.'),
(4761, 52, 26, 'Waseme: Tulikuwa zamani pamoja na ahali zetu tukiogopa;'),
(4762, 52, 27, 'Basi Mwenyezi Mungu akatufanyia hisani na akatulinda na adhabu ya upepo wa Moto.'),
(4763, 52, 28, 'Hakika sisi zamani tulikuwa tukimwomba Yeye tu. Hakika Yeye ndiye Mwema Mwenye kurehemu.'),
(4764, 52, 29, 'Basi kumbusha! Kwani wewe, kwa neema ya Mola wako Mlezi, si kuhani wala mwendawazimu.'),
(4765, 52, 30, 'Au wanasema: Huyu ni mtunga mashairi, tunamtarajia kupatilizwa na dahari.'),
(4766, 52, 31, 'Sema: Tarajieni, na mimi pia ni pamoja nanyi katika kutarajia.'),
(4767, 52, 32, 'Au hizo akili zao ndio zinawaamrisha haya, au wao basi ni watu majeuri tu?'),
(4768, 52, 33, 'Au ndio wanasema: Ameitunga hii! Bali basi tu hawaamini!'),
(4769, 52, 34, 'Basi nawalete masimulizi kama haya ikiwa wao wanasema kweli.'),
(4770, 52, 35, 'Au wao wameumbwa pasipo kutokana na kitu chochote, au ni wao ndio waumbaji?'),
(4771, 52, 36, 'Au wao wameziumba mbingu na ardhi? Bali hawana na yakini.'),
(4772, 52, 37, 'Au wanazo khazina za Mola wako Mlezi au wao ndio wenye madaraka?'),
(4773, 52, 38, 'Au wanazo ngazi za kusikilizia? Basi huyo msikilizaji wao na alete hoja ilio wazi!'),
(4774, 52, 39, 'Au Yeye Mwenyezi Mungu ana wasichana, na nyinyi ndio mna wavulana?'),
(4775, 52, 40, 'Au wewe unawaomba ujira, na kwa hivyo ndio wanaelemewa na uzito wa gharama?'),
(4776, 52, 41, 'Au wanayo ilimu ya ghaibu, nao wameandika?'),
(4777, 52, 42, 'Au wanataka kufanya vitimbi tu? Lakini hao walio kufuru ndio watakao tegeka.'),
(4778, 52, 43, 'Au wanae mungu asiye kuwa Mwenyezi Mungu? Subhanallah! Ametaksika Mwenyezi Mungu na hao wanao washirikisha naye.'),
(4779, 52, 44, 'Na hata wange ona pande linatoka mbinguni linaanguka wange sema: Ni mawingu yaliyo bebana.'),
(4780, 52, 45, 'Basi waache mpaka wakutane na siku yao watakapo hilikishwa.'),
(4781, 52, 46, 'Siku ambayo hila zao hazitawafaa hata kidogo, wala wao hawatanusuriwa.'),
(4782, 52, 47, 'Na hakika walio dhulumu watapata adhabu nyengine isiyo kuwa hii, lakini wengi wao hawajui.'),
(4783, 52, 48, 'Na ingojee hukumu ya Mola wako Mlezi. Kwani wewe hakika uko mbele ya macho yetu, na mtakase kwa kumsifu Mola wako Mlezi unapo simama,'),
(4784, 52, 49, 'Na usiku pia mtakase, na zinapo kuchwa nyota.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Naapa kwa nyota inapo tua,'),
(4786, 53, 2, 'Mwenzenu huyu hakupotea, wala hakukosea.'),
(4787, 53, 3, 'Wala hatamki kwa matamanio.'),
(4788, 53, 4, 'Hayakuwa haya ila ni ufunuo ulio funuliwa;'),
(4789, 53, 5, 'Amemfundisha aliye mwingi wa nguvu,'),
(4790, 53, 6, 'Mwenye kutua, akatulia,'),
(4791, 53, 7, 'Naye yuko juu kabisa upeo wa macho.'),
(4792, 53, 8, 'Kisha akakaribia na akateremka.'),
(4793, 53, 9, 'Akawa ni kama baina ya mipinde miwili, au karibu zaidi.'),
(4794, 53, 10, 'Akamfunulia mja wake (Mwenyezi Mungu) alicho mfunulia.'),
(4795, 53, 11, 'Moyo haukusema uwongo uliyo yaona.'),
(4796, 53, 12, 'Je! Mnabishana naye kwa aliyo yaona?'),
(4797, 53, 13, 'Na akamwona mara nyingine,'),
(4798, 53, 14, 'Penye Mkunazi wa mwisho.'),
(4799, 53, 15, 'Karibu yake ndiyo ipo Bustani inayo kaliwa.'),
(4800, 53, 16, 'Kilipo ufunika huo Mkunazi hicho kilicho ufunuika.'),
(4801, 53, 17, 'Jicho halikuhangaika wala halikuruka mpaka.'),
(4802, 53, 18, 'Kwa yakini aliona katika Ishara kubwa kabisa za Mola wake Mlezi.'),
(4803, 53, 19, 'Je! Mmemuona Lata na Uzza?'),
(4804, 53, 20, 'Na Manaat, mwingine wa tatu?'),
(4805, 53, 21, 'Je! Nyinyi mnao wana wanaume na Yeye ndio awe na wanawake?'),
(4806, 53, 22, 'Huo ni mgawanyo wa dhulma!'),
(4807, 53, 23, 'Hayo hayakuwa ila ni majina mliyo wapa nyinyi na baba zenu. Mwenyezi Mungu hakuleta uthibitisho wowote juu ya hayo. Hawafuati ila dhana tu na kipendacho nafsi. Na kwa yakini uwongofu ulikwisha wafikia kutoka kwa Mola wao Mlezi.'),
(4808, 53, 24, 'Ati mtu anakipata kila anacho kitamani?'),
(4809, 53, 25, 'Mwenyezi Mungu tu ndiye Mwenye dunia na Akhera.'),
(4810, 53, 26, 'Na wako Malaika wangapi mbinguni, ambao uombezi wao hautafaa chochote isipo kuwa baada ya Mwenyezi Mungu kutoa idhini kwa amtakaye na kumridhia.'),
(4811, 53, 27, 'Hakika wasio amini Akhera bila ya shaka wao ndio wanawaita Malaika kwa majina ya kike.'),
(4812, 53, 28, 'Nao hawana ujuzi wowote kwa hayo isipo kuwa wanafuata dhana tu. Na dhana haisaidii chochote mbele ya haki.'),
(4813, 53, 29, 'Basi mwachilie mbali anaye upa kisogo ukumbusho wetu, na wala hataki ila maisha ya dunia.'),
(4814, 53, 30, 'Huo ndio mwisho wao wa ujuzi. Hakika Mola wako Mlezi ndiye anaye mjua zaidi mwenye kuipotea njia yake, na Yeye ndiye anaye mjua zaidi mwenye kuongoka.'),
(4815, 53, 31, 'Ni vya Mwenyezi Mungu vyote viliomo mbinguni na viliomo katika ardhi, ili awalipe walio tenda ubaya kwa waliyo yatenda, na walio tenda mema awalipe mema.'),
(4816, 53, 32, 'Ambao wanajiepusha na madhambi makuu na vitendo vichafu, isipo kuwa makosa khafifu. Hakika Mola wako Mlezi ni Mkunjufu wa maghfira, naye anakujueni sana tangu alipo kuumbeni kutoka kwenye ardhi, na mlipo kuwa mimba matumboni mwa mama zenu. Basi msijisifu usafi. Yeye anamjua sana mwenye kujikinga na maovu.'),
(4817, 53, 33, 'Je! Umemwona yule aliye geuka?'),
(4818, 53, 34, 'Na akatoa kidogo, kisha akajizuia?'),
(4819, 53, 35, 'Je! Anayo huyo ilimu ya ghaibu, basi ndio anaona?'),
(4820, 53, 36, 'Au hakuambiwa yaliyomo katika Vitabu vya Musa?'),
(4821, 53, 37, 'Na vya Ibrahimu aliye timiza ahadi?'),
(4822, 53, 38, 'Ya kwamba hakika nafsi iliyo beba madhambi haibebi madhambi ya mwengine?'),
(4823, 53, 39, 'Na ya kwamba hatapata mtu ila aliyo yafanya mwenyewe?'),
(4824, 53, 40, 'Na kwamba vitendo vyake vitaonekana?'),
(4825, 53, 41, 'Kisha ndio atalipwa malipo yake kwa ukamilifu.'),
(4826, 53, 42, 'Na kwamba kwao Mola wako Mlezi ndio mwisho.'),
(4827, 53, 43, 'Na kwamba Yeye ndiye anaye leta kicheko na kilio.'),
(4828, 53, 44, 'Na kwamba Yeye ndiye anaye fisha na kuhuisha.'),
(4829, 53, 45, 'Na kwamba Yeye ndiye aliye umba jozi, dume na jike'),
(4830, 53, 46, 'Kutokana na mbegu ya uzazi inapo miminwa.'),
(4831, 53, 47, 'Na kwamba ni juu yake ufufuo mwengine.'),
(4832, 53, 48, 'Na kwamba ni Yeye ndiye anaye tosheleza na kukinaisha.'),
(4833, 53, 49, 'Na kwamba hakika Yeye ndiye Mola Mlezi wa nyota ya Shii\'ra.'),
(4834, 53, 50, 'Na kwamba Yeye ndiye aliye waangamiza A\'di wa kwanza,'),
(4835, 53, 51, 'Na Thamudi hakuwabakisha,'),
(4836, 53, 52, 'Na kabla yao kaumu ya Nuhu. Na hao hakika walikuwa ni madhaalimu zaidi, na waovu zaidi;'),
(4837, 53, 53, 'Na miji iliyo pinduliwa, ni Yeye aliye ipindua.'),
(4838, 53, 54, 'Vikaifunika vilivyo funika.'),
(4839, 53, 55, 'Basi neema gani ya Mola wako Mlezi unayo ifanyia shaka?'),
(4840, 53, 56, 'Hili ni Onyo katika maonyo yale yale ya zamani.'),
(4841, 53, 57, 'Kiyama kimekaribia!'),
(4842, 53, 58, 'Hapana wa kukifichua isipo kuwa Mwenyezi Mungu.'),
(4843, 53, 59, 'Je! Mnayastaajabia maneno haya?'),
(4844, 53, 60, 'Na mnacheka, wala hamlii?'),
(4845, 53, 61, 'Nanyi mmeghafilika?'),
(4846, 53, 62, 'Basi msujudieni Mwenyezi Mungu, na mumuabudu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Saa imekaribia, na mwezi umepasuka!'),
(4848, 54, 2, 'Na wakiona Ishara hugeuka upande na husema: Huu uchawi tu unazidi kuendelea.'),
(4849, 54, 3, 'Na wamekanusha na wamefuata matamanio yao. Na kila jambo ni lenye kuthibiti.'),
(4850, 54, 4, 'Na bila ya shaka zimewajia khabari zenye makaripio.'),
(4851, 54, 5, 'Hikima kaamili; lakini maonyo hayafai kitu!'),
(4852, 54, 6, 'Basi jiepushe nao. Siku atakapo ita mwitaji kuliendea jambo linalo chusha;'),
(4853, 54, 7, 'Macho yao yatainama; watatoka makaburini kama nzige walio tawanyika,'),
(4854, 54, 8, 'Wanamkimbilia mwitaji, na makafiri wanasema: Hii ni siku ngumu.'),
(4855, 54, 9, 'Kabla yao kaumu ya Nuhu walikanusha; wakamkanusha mja wetu, na wakasema ni mwendawazimu, na akakaripiwa.'),
(4856, 54, 10, 'Basi akamwomba Mola wake Mlezi akasema: Kwa hakika mimi nimeshindwa, basi ninusuru!'),
(4857, 54, 11, 'Basi tukaifungua milango ya mbingu kwa maji yanayo miminika.'),
(4858, 54, 12, 'Na tukazipasua ardhi kwa chemchem, yakakutana maji kwa jambo lilio kadiriwa.'),
(4859, 54, 13, 'Na tukamchukua kwenye safina ya mbao na kamba.'),
(4860, 54, 14, 'Ikawa inakwenda kwa nadhari yetu, kuwa ni malipo kwa alivyo kuwa amekanushwa.'),
(4861, 54, 15, 'Na bila ya shaka tuliiacha iwe ni Ishara. Lakini je, yupo anaye kumbuka?'),
(4862, 54, 16, 'Basi ilikuwaje adhabu yangu, na maonyo yangu.'),
(4863, 54, 17, 'Na bila ya shaka Sisi tumeifanya Qur\'ani iwe nyepesi kufahamika. Lakini yupo anaye kumbuka?'),
(4864, 54, 18, 'Kina A\'di walikanusha. Basi ilikuwaje adhabu yangu na maonyo yangu?'),
(4865, 54, 19, 'Hakika Sisi tuliwapelekea upepo wa kimbunga katika siku korofi mfululizo,'),
(4866, 54, 20, 'Ukiwang\'oa watu kama vigogo vya mitende vilio ng\'olewa.'),
(4867, 54, 21, 'Basi ilikuwaje adhabu yangu na maonyo yangu?'),
(4868, 54, 22, 'Na bila ya shaka Sisi tumeifanya Qur\'ani nyepesi kufahamika; lakini yupo anaye kumbuka?'),
(4869, 54, 23, 'Thamudi waliwakanusha Waonyaji.'),
(4870, 54, 24, 'Wakasema: Ati tumfuate binaadamu mmoja katika sisi? Basi hivyo sisi tutakuwa katika upotofu na kichaa!'),
(4871, 54, 25, 'Ni yeye tu aliye teremshiwa huo ukumbusho kati yetu sote? Bali huyu ni mwongo mwenye kiburi!'),
(4872, 54, 26, 'Kesho watajua ni nani huyo mwongo mwenye kiburi.'),
(4873, 54, 27, 'Hakika Sisi tutawapelekea ngamia jike ili kuwajaribu, basi watazame tu na ustahamili.'),
(4874, 54, 28, 'Na waambie kwamba maji yatagawanywa baina yao; kila sehemu ya maji itahudhuriwa na aliye khusika.'),
(4875, 54, 29, 'Basi wakamwita mtu wao akaja akamchinja.'),
(4876, 54, 30, 'Basi ilikuwaje adhabu yangu na maonyo yangu!'),
(4877, 54, 31, 'Hakika Sisi tuliwapelekea ukelele mmoja tu, wakawa kama mabuwa ya kujengea uwa.'),
(4878, 54, 32, 'Na bila ya shaka Sisi tumeifanya Qur\'ani iwe nyepesi kwa kukumbuka, lakini yupo akumbukaye?'),
(4879, 54, 33, 'Kaumu Lut\'i nao waliwakadhibisha Waonyaji.'),
(4880, 54, 34, 'Hakika Sisi tukawapelekea kimbunga cha vijiwe, isipo kuwa wafuasi wa Lut\'i. Hao tuliwaokoa karibu na alfajiri.'),
(4881, 54, 35, 'Kwa neema inayo toka kwetu. Hivyo ndivyo tumlipavyo anaye shukuru.'),
(4882, 54, 36, 'Na hakika yeye aliwaonya adhabu yetu; lakini wao waliyatilia shaka hayo maonyo.'),
(4883, 54, 37, 'Na walimtaka awape wageni wake. Tukayapofua macho yao, na tukawaambia: Onjeni basi adhabu na maonyo yangu!'),
(4884, 54, 38, 'Na iliwafikia wakati wa asubuhi adhabu yangu ya kuendelea.'),
(4885, 54, 39, 'Basi onjeni adhabu na maonyo yangu!'),
(4886, 54, 40, 'Na hakika tumeisahilisha Qur\'ani kuikumbuka. Lakini yupo anaye kumbuka?'),
(4887, 54, 41, 'Na Waonyaji waliwafikia watu wa Firauni.'),
(4888, 54, 42, 'Walizikadhibisha Ishara zetu zote, nasi tukawashika kama anavyo shika Mwenye nguvu Mwenye uweza.'),
(4889, 54, 43, 'Je! Makafiri wenu ni bora kuliko hao, au yameandikwa vitabuni kuwa nyinyi hamtiwi makosani?'),
(4890, 54, 44, 'Au ndio wanasema: Sisi ni wengi tutashinda tu.'),
(4891, 54, 45, 'Wingi wao huu utashindwa, na wao watasukumwa nyuma.'),
(4892, 54, 46, 'Bali Saa ya Kiyama ndio miadi yao, na Saa hiyo ni nzito zaidi na chungu zaidi.'),
(4893, 54, 47, 'Hakika wakosefu wamo katika upotofu na wazimu.'),
(4894, 54, 48, 'Siku watakapo kokotwa Motoni kifudifudi waambiwe: Onjeni mguso wa Jahannamu!'),
(4895, 54, 49, 'Kwa hakika Sisi tumekiumba kila kitu kwa kipimo.'),
(4896, 54, 50, 'Na amri yetu haikuwa ila ni moja tu, kama kupepesa jicho.'),
(4897, 54, 51, 'Na bila ya shaka tumekwisha waangamiza wenzenu. Lakini yupo anaye kumbuka?'),
(4898, 54, 52, 'Na kila jambo walilo lifanya limo vitabuni.'),
(4899, 54, 53, 'Na kila kidogo na kikubwa kimeandikwa.'),
(4900, 54, 54, 'Hakika wachamngu watakuwa katika Mabustani na mito.'),
(4901, 54, 55, 'Katika makalio ya haki kwa Mfalme Mwenye uweza.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Arrah\'man, Mwingi wa Rehema'),
(4903, 55, 2, 'Amefundisha Qur\'ani.'),
(4904, 55, 3, 'Amemuumba mwanaadamu,'),
(4905, 55, 4, 'Akamfundisha kubaini.'),
(4906, 55, 5, 'Jua na mwezi huenda kwa hisabu.'),
(4907, 55, 6, 'Na mimea yenye kutambaa, na miti, inanyenyekea.'),
(4908, 55, 7, 'Na mbingu ameziinua, na ameweka mizani,'),
(4909, 55, 8, 'Ili msidhulumu katika mizani.'),
(4910, 55, 9, 'Na wekeni mizani kwa haki, wala msipunje katika mizani.'),
(4911, 55, 10, 'Na ardhi ameiweka kwa ajili ya viumbe.'),
(4912, 55, 11, 'Humo yamo matunda na mitende yenye mafumba.'),
(4913, 55, 12, 'Na nafaka zenye makapi, na rehani.'),
(4914, 55, 13, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha'),
(4915, 55, 14, 'Amemuumba mtu kwa udongo wa kinamo..'),
(4916, 55, 15, 'Na akawaumba majini kwa ulimi wa moto.'),
(4917, 55, 16, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha'),
(4918, 55, 17, 'Mola Mlezi wa mashariki mbili na wa magharibi mbili.'),
(4919, 55, 18, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4920, 55, 19, 'Anaziendesha bahari mbili zikutane;'),
(4921, 55, 20, 'Baina yao kipo kizuizi, zisiingiliane.'),
(4922, 55, 21, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4923, 55, 22, 'Katika hizo bahari mbili zinatoka lulu na marijani.'),
(4924, 55, 23, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4925, 55, 24, 'Na ni vyake Yeye hivi viendavyo baharini vilivyo undwa kama vilima.'),
(4926, 55, 25, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4927, 55, 26, 'Kila kilioko juu yake kitatoweka.'),
(4928, 55, 27, 'Na atabakia Mwenyewe Mola wako Mlezi Mwenye utukufu na ukarimu.'),
(4929, 55, 28, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4930, 55, 29, 'Vinamwomba Yeye vilivyomo katika mbingu na ardhi. Kila siku Yeye yumo katika mambo.'),
(4931, 55, 30, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4932, 55, 31, 'Tutakuhisabuni enyi makundi mawili.'),
(4933, 55, 32, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4934, 55, 33, 'Enyi makundi ya majini na watu! Mkiweza kupenya kwenye mbingu na ardhi, basi penyeni! Hamtapenya ila kwa kupewa madaraka.'),
(4935, 55, 34, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4936, 55, 35, 'Mtapelekewa muwako wa moto na shaba; wala hamtashinda.'),
(4937, 55, 36, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4938, 55, 37, 'Itakapo pasuka mbingu ikawa nyekundu kama mafuta.'),
(4939, 55, 38, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4940, 55, 39, 'Siku hiyo hataulizwa dhambi zake mtu wala jini.'),
(4941, 55, 40, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4942, 55, 41, 'Watajuulikana wakosefu kwa alama zao, basi watashikwa kwa nywele zao za utosini na kwa miguu.'),
(4943, 55, 42, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4944, 55, 43, 'Hii ndiyo Jahannamu ambayo wakosefu wakiikanusha.'),
(4945, 55, 44, 'Watakuwa wakizunguka baina ya hiyo na maji ya moto yanayo chemka.'),
(4946, 55, 45, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4947, 55, 46, 'Na mwenye kuogopa kusimamishwa mbele ya Mola wake Mlezi atapata Bustani mbili.'),
(4948, 55, 47, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4949, 55, 48, 'Bustani zenye matawi yaliyo tanda.'),
(4950, 55, 49, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4951, 55, 50, 'Ndani yake zimo chemchem mbili zinazo pita.'),
(4952, 55, 51, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4953, 55, 52, 'Humo katika kila matunda zimo namna mbili.'),
(4954, 55, 53, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4955, 55, 54, 'Wawe wameegemea matandiko yenye bit\'ana ya hariri nzito; na matunda ya Bustani hizo yapo karibu.'),
(4956, 55, 55, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4957, 55, 56, 'Humo watakuwamo wanawake watulizao macho yao; hajawagusa mtu kabla yao wala jini.'),
(4958, 55, 57, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4959, 55, 58, 'Kama kwamba wao ni yakuti na marijani.'),
(4960, 55, 59, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha.'),
(4961, 55, 60, 'Ati yaweza kuwa malipo ya ihsani ila ihsani?'),
(4962, 55, 61, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4963, 55, 62, 'Na zaidi ya hizo zipo Bustani nyengine mbili.'),
(4964, 55, 63, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4965, 55, 64, 'Za kijani kibivu.'),
(4966, 55, 65, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4967, 55, 66, 'Na chemchem mbili zinazo furika.'),
(4968, 55, 67, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4969, 55, 68, 'Imo humo miti ya matunda, na mitende na mikomamanga.'),
(4970, 55, 69, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4971, 55, 70, 'Humo wamo wanawake wema wazuri.'),
(4972, 55, 71, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4973, 55, 72, 'Wanawake wazuri wanao tawishwa katika makhema.'),
(4974, 55, 73, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4975, 55, 74, 'Hajawagusa mtu wala jini kabla yao.'),
(4976, 55, 75, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4977, 55, 76, 'Wameegemea juu ya matakia ya kijani na mazulia mazuri.'),
(4978, 55, 77, 'Basi ni ipi katika neema za Mola wenu Mlezi mnayo ikanusha?'),
(4979, 55, 78, 'Limetukuka jina la Mola wako Mlezi Mwenye utukufu na ukarimu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Litakapo tukia hilo Tukio'),
(4981, 56, 2, 'Hapana cha kukanusha kutukia kwake.'),
(4982, 56, 3, 'Literemshalo linyanyualo,'),
(4983, 56, 4, 'Itakapo tikiswa ardhi kwa mtikiso,'),
(4984, 56, 5, 'Na milima itapo sagwasagwa,'),
(4985, 56, 6, 'Iwe mavumbi yanayo peperushwa,'),
(4986, 56, 7, 'Na nyinyi mtakuwa namna tatu:-'),
(4987, 56, 8, 'Basi watakuwepo wa kuliani; je, ni wepi wa kuliani?'),
(4988, 56, 9, 'Na wa kushotoni; je, ni wepi wa kushotoni?'),
(4989, 56, 10, 'Na wa mbele watakuwa mbele.'),
(4990, 56, 11, 'Hao ndio watakao karibishwa'),
(4991, 56, 12, 'Katika Bustani zenye neema.'),
(4992, 56, 13, 'Fungu kubwa katika wa mwanzo,'),
(4993, 56, 14, 'Na wachache katika wa mwisho.'),
(4994, 56, 15, 'Watakuwa juu ya viti vya fakhari vilivyo tonewa.'),
(4995, 56, 16, 'Wakiviegemea wakielekeana.'),
(4996, 56, 17, 'Wakitumikiwa na wavulana wa ujana wa milele,'),
(4997, 56, 18, 'Kwa vikombe na mabirika na bilauri za vinywaji kutoka chemchem safi.'),
(4998, 56, 19, 'Hawataumwa kichwa kwa vinywaji hivyo wala hawatoleweshwa.'),
(4999, 56, 20, 'Na matunda wayapendayo,'),
(5000, 56, 21, 'Na nyama za ndege kama wanavyo tamani.'),
(5001, 56, 22, 'Na Mahurulaini,'),
(5002, 56, 23, 'Walio kama mfano wa lulu zilio hifadhiwa.'),
(5003, 56, 24, 'Ni malipo kwa waliyo kuwa wakiyatenda.'),
(5004, 56, 25, 'Humo hawatasikia porojo wala maneno ya dhambi,'),
(5005, 56, 26, 'Isipo kuwa maneno ya Salama, Salama.'),
(5006, 56, 27, 'Na wale wa mkono wa kulia, ni yepi yatakuwa ya wa kuliani?'),
(5007, 56, 28, 'Katika mikunazi isiyo na miba,'),
(5008, 56, 29, 'Na migomba iliyo pangiliwa,'),
(5009, 56, 30, 'Na kivuli kilicho tanda,'),
(5010, 56, 31, 'Na maji yanayo miminika,'),
(5011, 56, 32, 'Na matunda mengi,'),
(5012, 56, 33, 'Hayatindikii wala hayakatazwi,'),
(5013, 56, 34, 'Na matandiko yaliyo nyanyuliwa.'),
(5014, 56, 35, 'Hakika Sisi tutawaumba (Mahurulaini) upya,'),
(5015, 56, 36, 'Na tutawafanya vijana,'),
(5016, 56, 37, 'Wanapendana na waume zao, hirimu moja.'),
(5017, 56, 38, 'Kwa ajili ya watu wa kuliani.'),
(5018, 56, 39, 'Fungu kubwa katika wa mwanzo,'),
(5019, 56, 40, 'Na fungu kubwa katika wa mwisho.'),
(5020, 56, 41, 'Na watu wa kushotoni; je ni yepi ya wa kushotoni?'),
(5021, 56, 42, 'Katika upepo wa moto, na maji yanayo chemka,'),
(5022, 56, 43, 'Na kivuli cha moshi mweusi,'),
(5023, 56, 44, 'Si cha kuburudisha wala kustarehesha.'),
(5024, 56, 45, 'Hakika hao walikuwa kabla ya haya wakiishi maisha ya anasa.'),
(5025, 56, 46, 'Na walikuwa wakishikilia kufanya madhambi makubwa,'),
(5026, 56, 47, 'Na walikuwa wakisema: Tutakapo kufa na tukawa udongo na mafupa, ati ndio tutafufuliwa?'),
(5027, 56, 48, 'Au baba zetu wa zamani?'),
(5028, 56, 49, 'Sema: Hakika wa zamani na wa mwisho'),
(5029, 56, 50, 'Bila ya shaka watakusanywa kwa wakati wa siku maalumu.'),
(5030, 56, 51, 'Kisha nyinyi, mlio potea, mnao kanusha,'),
(5031, 56, 52, 'Kwa yakini mtakula mti wa Zaqqumu.'),
(5032, 56, 53, 'Na kwa mti huo mtajaza matumbo.'),
(5033, 56, 54, 'Na juu yake mtakunywa maji yanayo chemka.'),
(5034, 56, 55, 'Tena mtakunywa kama wanavyo kunywa ngamia wenye kiu.'),
(5035, 56, 56, 'Hiyo ndiyo karamu yao Siku ya Malipo.'),
(5036, 56, 57, 'Sisi tumekuumbeni; basi hamsadiki?'),
(5037, 56, 58, 'Je! Mnaiona mbegu ya uzazi mnayo idondokeza?'),
(5038, 56, 59, 'Je! Mnaiumba nyinyi, au ni Sisi ndio Waumbaji?'),
(5039, 56, 60, 'Sisi tumekuwekeeni mauti; na wala Sisi hatushindwi'),
(5040, 56, 61, 'Kuwaleta wengine badala yenu na kukuumbeni nyinyi kwa umbo msilo lijua.'),
(5041, 56, 62, 'Na bila ya shaka mlikwisha jua umbo la kwanza, basi kwa nini hamkumbuki?'),
(5042, 56, 63, 'Je! Mnaona makulima mnayo yapanda?'),
(5043, 56, 64, 'Je! Ni nyinyi mnayo yaotesha au ni Sisi ndio wenye kuotesha?'),
(5044, 56, 65, 'Tungeli taka tungeli yafanya yakawa mapepe, mkabaki mnastaajabu,'),
(5045, 56, 66, 'Mkisema: Hakika sisi tumegharimika;'),
(5046, 56, 67, 'Bali sisi tumenyimwa.'),
(5047, 56, 68, 'Je! Mnayaona maji mnayo yanywa?'),
(5048, 56, 69, 'Je! Ni nyinyi mnayo yateremsha kutoka mawinguni au ni Sisi ndio wenye kuyateremsha?'),
(5049, 56, 70, 'Tungeli penda tungeli yafanya ya chumvi. Basi mbona hamshukuru?'),
(5050, 56, 71, 'Je! Mnauona moto mnao uwasha?'),
(5051, 56, 72, 'Ni nyinyi mlio uumba mti wake au Sisi ndio Waumbaji?'),
(5052, 56, 73, 'Sisi tumeufanya uwe ni ukumbusho na manufaa kwa walioko nyikani.'),
(5053, 56, 74, 'Basi litakase jina la Mola wako Mlezi aliye Mkuu.'),
(5054, 56, 75, 'Basi naapa kwa maanguko ya nyota,'),
(5055, 56, 76, 'Na hakika bila ya shaka hichi ni kiapo kikubwa, laiti mngeli jua!'),
(5056, 56, 77, 'Hakika hii bila ya shaka ni Qur\'ani Tukufu,'),
(5057, 56, 78, 'Katika Kitabu kilicho hifadhiwa.'),
(5058, 56, 79, 'Hapana akigusaye ila walio takaswa.'),
(5059, 56, 80, 'Ni uteremsho unao toka kwa Mola Mlezi wa walimwengu wote.'),
(5060, 56, 81, 'Ni maneno haya ndiyo nyinyi mnayapuuza?'),
(5061, 56, 82, 'Na badala ya kushukuru kwa riziki yenu mnafanya kuwa mnakadhibisha?'),
(5062, 56, 83, 'Yawaje basi itakapo fika roho kwenye koo,'),
(5063, 56, 84, 'Na nyinyi wakati huo mnatazama!'),
(5064, 56, 85, 'Na Sisi tuko karibu zaidi naye kuliko nyinyi.'),
(5065, 56, 86, 'Na lau kuwa nyinyi hamumo katika mamlaka yangu,'),
(5066, 56, 87, 'Kwanini hamuirudishi hiyo roho, ikiwa nyinyi mnasema kweli?'),
(5067, 56, 88, 'Basi akiwa miongoni mwa walio karibishwa,'),
(5068, 56, 89, 'Basi ni raha, na manukato, na Bustani zenye neema.'),
(5069, 56, 90, 'Na akiwa katika watu wa upande wa kulia,'),
(5070, 56, 91, 'Basi ni Salamu kwako uliye miongoni mwa watu wa upande wa kulia.'),
(5071, 56, 92, 'Na ama akiwa katika walio kadhibisha wapotovu,'),
(5072, 56, 93, 'Basi karamu yake ni maji yanayo chemka,'),
(5073, 56, 94, 'Na kutiwa Motoni.'),
(5074, 56, 95, 'Hakika hii ndiyo haki yenye yakini.'),
(5075, 56, 96, 'Basi litakase jina la Mola wako Mlezi aliye Mkubwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Kinamsabihi kumtakasa Mwenyezi Mungu kila kiliomo ndani ya mbingu na ardhi. Na Yeye ndiye Mwenye nguvu, Mwenye hikima.'),
(5077, 57, 2, 'Yeye ndiye Mwenye ufalme wa mbingu na ardhi. Anahuisha na anafisha. Na Yeye ni Mwenye uweza wa kila kitu.'),
(5078, 57, 3, 'Yeye ndiye wa Mwanzo na ndiye wa Mwisho, naye ndiye wa Dhaahiri na wa Siri, naye ndiye Mjuzi wa kila kitu.'),
(5079, 57, 4, 'Yeye ndiye aliye ziumba mbingu na ardhi katika siku sita; kisha akakaa vyema juu ya enzi. Anayajua yanayo ingia katika ardhi na yanayo toka humo, na yanayo teremka kutoka mbinguni, na yanayo panda humo. Naye yu pamoja nanyi popote mlipo. Na Mwenyezi Mungu ni Mwenye kuyaona mnayo yatenda.'),
(5080, 57, 5, 'Ufalme wa mbingu na ardhi ni wake. Na mambo yote yanarejeshwa kwa Mwenyezi Mungu.'),
(5081, 57, 6, 'Anaingiza usiku katika mchana, na anaingiza mchana katika usiku. Na Yeye ni Mwenye kuyajua yaliomo vifuani.'),
(5082, 57, 7, 'Muaminini Mwenyezi Mungu na Mtume wake; na toeni katika alivyo kufanyeni nyinyi kuwa ni waangalizi wake. Basi walio amini miongoni mwenu, na wakatoa, wana malipo makubwa.'),
(5083, 57, 8, 'Na mna nini hata hamumuamini Mwenyezi Mungu na hali Mtume anakuiteni mumuamini Mola wenu Mlezi? Naye amekwisha chukua ahadi yenu, ikiwa nyinyi ni Waumini.'),
(5084, 57, 9, 'Yeye ndiye anaye mteremshia mja wake Aya zinazo bainisha wazi ili akutoeni gizani muingie kwenye nuru. Na hakika Mwenyezi Mungu kwenu ni Mpole, Mwenye kurehemu.'),
(5085, 57, 10, 'Na mna nini hata hamtoi katika Njia ya Mwenyezi Mungu, na hali urithi wa mbingu na ardhi ni wa Mwenyezi Mungu? Hawawi sawa miongoni mwenu wenye kutoa kabla ya Ushindi na wakapigana vita. Hao wana daraja kubwa zaidi kuliko wale ambao walio toa baadae na wakapigana. Na wote hao Mwenyezi Mungu amewaahidia wema. Na Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(5086, 57, 11, 'Ni nani atakaye mkopesha Mwenyezi Mungu mkopo mwema, ili amrudishie mardufu, na apate malipo ya ukarimu.'),
(5087, 57, 12, 'Siku utakapo waona Waumini wanaume na Waumini wanawake, nuru yao iko mbele yao, na kuliani kwao: Furaha yenu leo - Bustani zipitazo mito kati yake mtakaa humo milele. Huko ndiko kufuzu kukubwa.'),
(5088, 57, 13, 'Siku wanaafiki wanaume na wanaafiki wanawake watapo waambia walio amini: Tungojeni ili tupate mwangaza katika nuru yenu. Waambiwe: Rejeeni nyuma yenu mkaitafute nuru! Utiwe baina yao ukuta wenye mlango - ndani yake mna rehema, na nje upande wake wa mbele kuna adhabu.'),
(5089, 57, 14, 'Watawaita wawaambie: Kwani vile hatukuwa pamoja nanyi? Watawaambia: Ndiyo, lakini mlijifitini wenyewe, na mkasitasita, na mkatia shaka, na matamanio ya nafsi zenu yakakudanganyeni. mpaka ilipo kuja amri ya Mwenyezi Mungu, na mdanganyifu akakudanganyeni msimfuate Mwenyezi Mungu.'),
(5090, 57, 15, 'Basi leo haitapokelewa kwenu fidia, wala kwa wale walio kufuru. Makaazi yenu ni Motoni, ndio bora kwenu; na ni marejeo maovu yalioje!'),
(5091, 57, 16, 'Je! Wakati haujafika bado kwa walio amini zikanyenyekea nyoyo zao kwa kumkumbuka Mwenyezi Mungu na haki iliyo teremka? Wala wasiwe kama walio pewa Kitabu kabla yao, muda wao ukawa mrefu, kwa hivyo nyoyo zao zikawa ngumu, na wengi wao wakawa wapotovu.'),
(5092, 57, 17, 'Jueni kwamba Mwenyezi Mungu huifufua ardhi baada ya kufa kwake. Tumekubainishieni Ishara ili mpate kuzingatia.'),
(5093, 57, 18, 'Kwa hakika wanaume wanao toa sadaka, na wanawake wanao toa sadaka, na wakamkopesha Mwenyezi Mungu mkopo mwema, watazidishiwa mardufu na watapata malipo ya ukarimu.'),
(5094, 57, 19, 'Na walio muamini Mwenyezi Mungu na Mitume wake, hao ndio Masidiqi na Mashahidi mbele ya Mola wao Mlezi. Wao watapata malipo yao na nuru yao. Na walio kufuru na wakakadhibisha Ishara zetu, hao ndio watu wa Motoni.'),
(5095, 57, 20, 'Jueni ya kwamba maisha ya dunia ni mchezo, na pumbao, na pambo, na kujifakhirisha baina yenu na kushindana kwa wingi wa mali na watoto. Mfano wake ni kama mvua ambayo huwafurahisha wakulima mimea yake, kisha hunyauka ukayaona yamepiga manjano kisha yakawa mabua. Na akhera kuna adhabu kali na maghfira kutoka kwa Mwenyezi Mungu na radhi. Na maisha ya dunia si chochote ila ni starehe ya udanganyifu.'),
(5096, 57, 21, 'Kimbilieni msamaha wa Mola wenu Mlezi, na Pepo ambayo upana wake ni kama upana wa mbingu na ardhi, iliyo wekewa walio muamini Mwenyezi Mungu na Mitume wake. Hiyo ndiyo fadhila ya Mwenyezi Mungu, humpa amtakaye. Na Mwenyezi Mungu ni Mwenye fadhila kuu.'),
(5097, 57, 22, 'Hautokei msiba katika ardhi wala katika nafsi zenu ila umekwisha andikwa katika Kitabu kabla hatujauumba. Hakika hayo kwa Mwenyezi Mungu ni mepesi.'),
(5098, 57, 23, 'Ili msihuzunike kwa kilicho kupoteeni, wala msijitape kwa alicho kupeni. Na Mwenyezi Mungu hampendi kila anaye jivuna akajifakhirisha.'),
(5099, 57, 24, 'Ambao wanafanya ubakhili, na wanaamrisha watu wafanye ubakhili. Na anaye geuka, basi Mwenyezi Mungu ni Mwenye kujitosha, Msifiwa.'),
(5100, 57, 25, 'Kwa hakika tuliwatuma Mitume wetu kwa dalili waziwazi, na tukateremsha Kitabu pamoja nao na Mizani, ili watu wasimamie uadilifu, na tumeteremsha chuma chenye nguvu nyingi na manufaa kwa watu, na ili Mwenyezi Mungu amjue anaye mnusuru Yeye na Mitume wake kwa ghaibu. Hakika Mwenyezi Mungu ni Mwenye nguvu Mwenye kushinda.'),
(5101, 57, 26, 'Na bila ya shaka tulimtuma Nuhu na Ibrahim, na tukaweka katika dhuriya zao Unabii na Kitabu. Basi wapo miongoni mwao walio ongoka, na wengi katika wao ni wapotovu.'),
(5102, 57, 27, 'Tena tukafuatisha nyuma yao Mitume wengine, na tukamfuatisha Isa bin Mariamu, na tukampa Injili. Na tukajaalia katika nyoyo za walio mfuata upole na rehema. Na umonaki (utawa wa mapadri) wameuzua wao. Sisi hatukuwaandikia hayo, ila kutafuta radhi za Mwenyezi Mungu. Lakini hawakuufuata inavyo takiwa kuufuata. Basi wale walio amini katika wao tuliwapa ujira wao. Na wengi wao ni wapotovu.'),
(5103, 57, 28, 'Enyi mlio amini! Mcheni Mwenyezi Mungu, na muaminini Mtume wake, atakupeni sehemu mbili katika rehema yake, na atakujaalieni muwe na nuru ya kwenda nayo. Na atakusameheni. Na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(5104, 57, 29, 'Ili watu wa Kitabu wajue kwamba wao hawana uweza wowote juu ya fadhila za Mwenyezi Mungu. Na fadhila zote zimo mikononi mwa Mwenyezi Mungu. Humpa amtakaye. Na Mwenyezi Mungu ndiye Mwenye fadhila kuu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'MWENYEZI MUNGU amekwisha sikia usemi wa mwanamke anaye jadiliana nawe juu ya mumewe, na anamshitakia Mwenyezi Mungu. Na Mwenyezi Mungu anayasikia majibizano yenu. Hakika Mwenyezi Mungu ni Mwenye kusikia, Mwenye kuona.'),
(5106, 58, 2, 'Wale miongoni mwenu wanao watenga wake zao, hao si mama zao. Hawakuwa mama zao ila wale walio wazaa. Na hakika hao wanasema neno linalo chusha, na la uwongo. Na Mwenyezi Mungu ni Mwenye kughufiria, Mwenye kusamehe.'),
(5107, 58, 3, 'Na wale wanao jitenga na wake zao, kisha wakarudia katika yale waliyo yasema, basi wamkomboe mtumwa kabla hawajagusana. Mnapewa maonyo kwa hayo. Na Mwenyezi Mungu anayajua yote mnayo yatenda.'),
(5108, 58, 4, 'Na asiye pata mtumwa kumkomboa, basi na afunge miezi miwili mfululizo kabla hawajagusana. Na asiye weza hayo basi awalishe masikini sitini. Hayo ni hivyo ili mumuamini Mwenyezi Mungu na Mtume wake. Na hiyo ndiyo mipaka ya Mwenyezi Mungu. Na kwa makafiri iko adhabu chungu.'),
(5109, 58, 5, 'Hakika wanao pinzana na Mwenyezi Mungu na Mtume wake, watadhalilishwa kama walivyo dhalilishwa wale walio kuwa kabla yao. Na tulikwisha teremsha Ishara zilizo wazi. Na makafiri watapata adhabu ya kufedhehesha.'),
(5110, 58, 6, 'Siku atapo wafufua Mwenyezi Mungu, na awaambie yale waliyo yatenda. Mwenyezi Mungu ameyadhibiti, na wao wameyasahau! Na Mwenyezi Mungu ni Mwenye kushuhudia kila kitu.'),
(5111, 58, 7, 'Kwani huoni kwamba Mwenyezi Mungu anajua vilivyomo katika mbingu na vilivyomo katika ardhi? Hauwi mnong\'ono wa watu watatu ila Yeye huwa ni wane wao, wala wa watano ila Yeye huwa ni wa sita wao. Wala wa wachache kuliko hao, wala walio wengi zaidi, ila Yeye yu pamoja nao popote pale walipo. Kisha Siku ya Kiyama atawaambia waliyo yatenda. Hakika Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(5112, 58, 8, 'Kwani huwaoni wale walio katazwa kunong\'onezana kisha wakayarudia yale yale waliyo katazwa, na wakanong\'ona juu ya mambo ya dhambi, na ya uadui, na ya kumuasi Mtume? Na wakikujia hukuamkia sivyo anavyo kuamkia Mwenyezi Mungu. Na husema katika nafsi zao: Mbona Mwenyezi hatuadhibu kwa haya tuyasemayo? Basi Jahannamu itawatosha; wataingia humo! Na hayo ndiyo marejeo maovu yaliyoje!'),
(5113, 58, 9, 'Enyi mlio amini! Mnapo nong\'ona msinong\'one kwa sababu ya mambo ya madhambi, na uadui, na kumuasi Mtume. Bali nong\'onezane kwa kutenda mema na kuacha maovu. Na mcheni Mwenyezi Mungu ambaye kwake mtakusanywa nyote.'),
(5114, 58, 10, 'Kwa hakika minong\'ono hiyo inatokana na Shetani ili awahuzunishe walio amini, na wala haitawadhuru chochote ila kwa idhini ya Mwenyezi Mungu. Na juu ya Mwenyezi Mungu tu ndio wategemee Waumini.'),
(5115, 58, 11, 'Enyi mlio amini! Mkiambiwa: Fanyeni nafasi katika mabaraza, basi fanyeni nafasi. Na Mwenyezi Mungu atakufanyieni nyinyi nafasi. Na mkiambiwa: Ondokeni, basi ondokeni. Mwenyezi Mungu atawainua walio amini miongoni mwenu, na walio pewa ilimu daraja za juu. Na Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(5116, 58, 12, 'Enyi mlio amini! Mnapo sema siri na Mtume, basi tangulizeni sadaka kabla ya kusemezana kwenu. Hiyo ni kheri kwenu na usafi zaidi. Na ikiwa hamkupata cha kutoa, basi Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(5117, 58, 13, 'Mnachelea kutanguliza hiyo sadaka kabla ya kusemezana kwenu? Ikiwa hamkufanya hayo, na Mwenyezi Mungu akapokea toba yenu, basi shikeni Sala, na toeni Zaka, na mt\'iini Mwenyezi Mungu na Mtume wake. Na Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(5118, 58, 14, 'Huwaoni wale walio fanya urafiki na watu ambao Mwenyezi Mungu amewakasirikia? Hao si katika nyinyi, wala si katika wao. Na huapa kwa uwongo, na hali kuwa wanajua.'),
(5119, 58, 15, 'Mwenyezi Mungu amewaandalia adhabu kali. Kwa hakika waliyo kuwa wakiyatenda ni maovu kabisa.'),
(5120, 58, 16, 'Wamefanya viapo vyao ni ngao, na wakaipinga Njia ya Mwenyezi Mungu. Basi hao watapata adhabu ya kufedhehesha.'),
(5121, 58, 17, 'Hayatawafaa kitu mali yao wala watoto wao mbele ya Mwenyezi Mungu. Hao ndio watu wa Motoni. Humo watadumu milele.'),
(5122, 58, 18, 'Siku atakayo wafufua Mwenyezi Mungu wote, wamuwapie kama wanavyo kuwapieni nyinyi. Na watadhani kuwa wamepata kitu! Kwa hakika hao ndio waongo.'),
(5123, 58, 19, 'Shetani amewatawala na akawasahaulisha kumkumbuka Mwenyezi Mungu. Hao ndio kundi la Shetani. Kweli hakika kundi la Shetani ndilo lenye kukhasirika.'),
(5124, 58, 20, 'Hakika wanao mpinga Mwenyezi Mungu na Mtume wake, hao ndio miongoni mwa madhalili wa mwisho.'),
(5125, 58, 21, 'Mwenyezi Mungu ameandika: Hapana shaka Mimi na Mtume wangu tutashinda. Hakika Mwenyezi Mungu ni Mwenye nguvu, Mwenye kushinda.'),
(5126, 58, 22, 'Huwakuti watu wanao muamini Mwenyezi Mungu na Siku ya Mwisho kuwa wanawapenda wanao mpinga Mwenyezi Mungu na Mtume wake, hata wakiwa ni baba zao, au watoto wao, au ndugu zao, au jamaa zao. Hao ameandika katika nyoyo zao Imani, na amewapa nguvu kwa Roho itokayo kwake. Na atawaingiza katika Mabustani yapitayo mito kati yake. Humo watakaa daima. Mwenyezi Mungu awe radhi nao, na wao wawe radhi naye. Hao ndio Hizbullahi, Kundi la Mwenyezi Mungu. Hakika Kundi la Mwenyezi Mungu ndilo lenye kufanikiwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Vinamsabihi, Vinamtakasa Mwenyezi Mungu viliomo katika mbingu na viliomo katika ardhi. Naye ni Mwenye nguvu, Mwenye hikima.'),
(5128, 59, 2, 'Yeye ndiye aliye watoa walio kufuru miongoni mwa Watu wa Kitabu katika nyumba zao wakati wa uhamisho wa kwanza. Hamkudhani kuwa watatoka, nao walidhani kuwa ngome zao zitawalinda na Mwenyezi Mungu. Lakini Mwenyezi Mungu aliwafikia kwa mahali wasipo patazamia, na akatia woga katika nyoyo zao. Wakawa wanazibomoa nyumba zao kwa mikono yao na mikono ya Waumini. Basi zingatieni enyi wenye macho!'),
(5129, 59, 3, 'Na lau kuwa Mwenyezi Mungu asingeli waandikia kutoka, angeli waadhibu katika dunia. Na katika Akhera watapata adhabu ya Moto.'),
(5130, 59, 4, 'Hayo ni kwa sababu walimpinga Mwenyezi Mungu na Mtume wake. Na mwenye kumpinga Mwenyezi Mungu, basi hakika Mwenyezi Mungu ni Mkali wa kuadhibu.'),
(5131, 59, 5, 'Mtende wowote mlio ukata au mlio uacha unasimama vile vile juu ya mashina yake, basi ni kwa idhini ya Mwenyezi Mungu, na kwa kuwahizi wapotovu.'),
(5132, 59, 6, 'Na mali aliyo leta Mwenyezi Mungu kwa Mtume wake kutoka kwao hamkuyakimbilia mbio kwa farasi wala ngamia. Lakini Mwenyezi Mungu huwapa mamlaka Mitume wake juu ya wowote awatakao. Na Mwenyezi Mungu ni Muweza juu ya kila kitu.'),
(5133, 59, 7, 'Mali aliyo leta Mwenyezi Mungu kwa Mtume wake kutoka kwa watu wa hivi vijiji ni kwa ajili ya Mwenyezi Mungu, na kwa ajili ya Mtume, na jamaa, na mayatima, na masikini, na msafiri, ili yasiwe yakizunguka baina ya matajiri tu miongoni mwenu. Na anacho kupeni Mtume chukueni, na anacho kukatazeni jiepusheni nacho. Na mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mkali wa kuadhibu.'),
(5134, 59, 8, 'Wapewe mafakiri Wahajiri walio tolewa majumbani mwao na mali yao kwa ajili ya kutafuta fadhila kutoka kwa Mwenyezi Mungu na radhi zake, na wanamsaidia Mwenyezi Mungu na Mtume wake. Hao ndio wa kweli.'),
(5135, 59, 9, 'Na walio na maskani zao na Imani yao kabla yao, wanawapenda walio hamia kwao, wala hawaoni choyo katika vifua vyao kwa walivyo pewa (Wahajiri), bali wanawapendelea kuliko nafsi zao, ingawa wao wenyewe ni wahitaji. Na mwenye kuepushwa uchoyo wa nafsi yake, basi hao ndio wenye kufanikiwa.'),
(5136, 59, 10, 'Na walio kuja baada yao wanasema: Mola wetu Mlezi! Tughufirie sisi na ndugu zetu walio tutangulia kwa Imani, wala usijaalie ndani ya nyoyo zetu undani kwa walio amini. Mola wetu Mlezi! Hakika Wewe ni Mpole na Mwenye kurehemu.'),
(5137, 59, 11, 'Huwaoni wanao fanya unaafiki wanawaambia ndugu zao walio kufuru katika Watu wa Kitabu: Mkitolewa na sisi lazima tutatoka pamoja nanyi, wala hatutamt\'ii yeyote kabisa dhidi yenu. Na mkipigwa vita lazima tutakusaidieni. Na Mwenyezi Mungu anashuhudia kuwa hao hakika bila ya shaka ni waongo.'),
(5138, 59, 12, 'Pindi wakitolewa hawatatoka pamoja nao, na wakipigwa vita hawatawasaidia. Na kama wakiwasaidia basi watageuza migongo; kisha hawatanusuriwa.'),
(5139, 59, 13, 'Hakika nyinyi ni kitisho zaidi katika vifua vyao kuliko Mwenyezi Mungu. Hayo ni kwa kuwa hao ni watu wasio fahamu kitu.'),
(5140, 59, 14, 'Hawatapigana nanyi kwa mkusanyiko isipo kuwa katika vijiji vilivyo zatitiwa kwa ngome, au nyuma ya kuta. Wao kwa wao vita vyao ni vikali. Utawadhania kuwa wako pamoja, kumbe nyoyo zao ni mbali mbali. Hayo ni kwa kuwa wao ni watu wasio na akili.'),
(5141, 59, 15, 'Ni kama mfano wa walio kuwa kabla yao hivi karibuni. Walionja uovu wa mambo yao. Nao watapata adhabu iliyo chungu.'),
(5142, 59, 16, 'Ni kama mfano wa Shetani anapo mwambia mtu: Kufuru. Na akikufuru humwambia: Mimi si pamoja nawe. Hakika mimi namwogopa Mwenyezi Mungu, Mola Mlezi wa walimwengu wote.'),
(5143, 59, 17, 'Basi ikawa mwisho wa wote wawili hao ni kuingia Motoni, wadumu humo daima; na hiyo ndiyo jaza ya madhaalimu.'),
(5144, 59, 18, 'Enyi mlio amini! Mcheni Mwenyezi Mungu, na kila nafsi iangalie inayo yatanguliza kwa ajili ya Kesho. Na mcheni Mwenyezi Mungu. Hakika Mwenyezi Mungu anazo khabari ya mnayo yatenda.'),
(5145, 59, 19, 'Wala msiwe kama wale walio msahau Mwenyezi Mungu, na Yeye akawasahaulisha nafsi zao. Hao ndio wapotovu.'),
(5146, 59, 20, 'Hawawi sawa watu wa Motoni na watu wa Peponi. Watu wa Peponi ndio wenye kufuzu.'),
(5147, 59, 21, 'Lau kuwa tumeiteremsha hii Qur\'ani juu ya mlima, basi bila ya shaka ungeli uona ukinyenyekea, ukipasuka kwa khofu ya Mwenyezi Mungu. Na hiyo mifano tunawapigia watu ili wafikiri.'),
(5148, 59, 22, 'Yeye ndiye Mwenyezi Mungu, ambaye hapana mungu isipo kuwa Yeye tu. Mwenye kuyajua yaliyo fichikana na yanayo onekana. Yeye ndiye Mwingi wa rehema, Mwenye kurehemu.'),
(5149, 59, 23, 'Yeye ndiye Mwenyezi Mungu ambaye hapana mungu isipo kuwa Yeye tu. Mfalme, Mtakatifu, Mwenye salama, Mtoaji wa amani, Mwenye kuyaendesha Mwenyewe mambo yake, Mwenye nguvu, Anaye fanya analo litaka, Mkubwa, Ametakasika Mwenyezi Mungu na hayo wanayo mshirikisha nayo.'),
(5150, 59, 24, 'Yeye ndiye Mwenyezi Mungu, Muumbaji, Mtengenezaji, Mtia sura, Mwenye majina mazuri kabisa. Kila kilioko katika mbingu na ardhi kinamtakasa, naye ni Mwenye kushinda, Mwenye hikima.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Enyi mlio amini! Msiwafanye adui zangu na adui zenu kuwa marafiki mkiwapa mapenzi, na hali wao wamekwisha ikataa haki iliyo kujieni, wakamfukuza Mtume na nyinyi kwa sababu mnamuamini Mwenyezi Mungu, Mola Mlezi wenu. Mnapotoka kwa ajili ya Jihadi katika Njia yangu na kutafuta radhi yangu, mnafanya urafiki nao kwa siri, na Mimi nayajua mnayo yaficha na mnayo dhihirisha. Na mwenye kufanya hayo kati yenu basi ameipotea njia ya sawa.'),
(5152, 60, 2, 'Wakikuwezeni wanakuwa maadui zenu, na wanakukunjulieni mikono yao na ndimi zao kwa uovu. Na wanapenda muwe makafiri.'),
(5153, 60, 3, 'Hawatakufaeni jamaa zenu, wala watoto wenu Siku ya Kiyama. Mwenyezi Mungu atapambanua baina yenu, na Mwenyezi Mungu anayaona mnayo yatenda.'),
(5154, 60, 4, 'Hakika nyinyi mna mfano mzuri kwenu kwa Ibrahim na wale walio kuwa pamoja naye, walipo waambia watu wao: Hakika sisi tumejitenga nanyi na hayo mnayo yaabudu badala ya Mwenyezi Mungu. Tunakukataeni; na umekwisha dhihiri uadui na chuki baina yetu na nyinyi mpaka mtakapo muamini Mwenyezi Mungu peke yake. Isipo kuwa kauli ya Ibrahim kumwambia baba yake: Hakika nitakuombea msamaha, wala similiki chochote kwa ajili yako mbele ya Mwenyezi Mungu. Mola wetu Mlezi! Juu yako tumetegemea, na kwako tumeelekea, na kwako ndio marejeo.'),
(5155, 60, 5, 'Mola wetu Mlezi! Usitufanyie mtihani kwa walio kufuru. Na tusamehe, Mola wetu Mlezi. Hakika Wewe ndiye Mwenye nguvu, Mwenye hikima.'),
(5156, 60, 6, 'Kwa yakini umekuwa mfano mzuri kwenu katika mwendo wao, kwa anaye mtarajia Mwenyezi Mungu na Siku ya Mwisho. Na mwenye kugeuka basi hakika Mwnyezi Mungu ni Mwenye kujitosha, Msifiwa.'),
(5157, 60, 7, 'Asaa Mwenyezi Mungu akatia mapenzi baina yenu na hao maadui zenu, na Mwenyezi Mungu ni Mweza, na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(5158, 60, 8, 'Mwenyezi Mungu hakukatazini kuwafanyia wema na uadilifu wale ambao hawakukupigeni vita, wala hawakukutoeni makwenu. Hakika Mwenyezi Mungu huwapenda wafanyao uadilifu.'),
(5159, 60, 9, 'Hakika Mwenyezi Mungu anakukatazeni kufanya urafiki na wale walio kupigeni vita, na wakakutoeni makwenu, na wakasaidia katika kufukuzwa kwenu. Na wanao wafanya hao marafiki basi hao ndio madhaalimu.'),
(5160, 60, 10, 'Enyi mlio amini! Wakikujilieni wanawake Waumini walio hama, basi wafanyieni mtihani - Mwenyezi Mungu ndiye Mwenye kujua zaidi Imani yao. Mkiwa mnawajua kuwa ni Waumini basi msiwarudishe kwa makafiri. Wanawake hao si halali kwa hao makafiri, wala hao makafiri hawahalalikii wanawake Waumini. Na wapeni hao wanaume mahari walio toa. Wala hapana makosa kwenu kuwaoa mkiwapa mahari yao. Wala msiwaweke wanawake makafiri katika kifungo cha ndoa zenu. Na takeni mlicho kitoa, na wao watake walicho kitoa. Hiyo ndiyo hukumu ya Mwenyezi Mungu anayo kuhukumuni. Na Mwenyezi Mungu ni Mwenye kujua, Mwenye hikima.'),
(5161, 60, 11, 'Na akitoroka yeyote katika wake zenu kwenda kwa makafiri, tena ikatokea mkapata ngawira, basi wapeni walio kimbiwa na wake zao kiasi cha mahari waliyo yatoa. Na mcheni Mwenyezi Mungu ambaye mnamuamini.'),
(5162, 60, 12, 'Ewe Nabii! Watakapo kujia wanawake Waumini wanakuahidi ya kwamba hawatamshirikisha Mwenyezi Mungu na chochote, wala hawataiba, wala hawatazini, wala hawatawauwa watoto wao, wala hawataleta uzushi wanao uzua tu wenyewe baina ya mikono yao na miguu yao, wala hawatakuasi katika jambo jema, basi peana nao ahadi, na uwatakie maghfira kwa Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwingi maghfira, Mwenye kurehemu.'),
(5163, 60, 13, 'Enyi mlio amini! Msiwafanye rafiki zenu watu ambao Mwenyezi Mungu amewakasirikia. Hao wamekwisha kata tamaa ya Akhera, kama makafiri walivyo wakatia tamaa watu wa makaburini.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Vinamtakasa Mwenyezi Mungu viliomo katika mbingu na katika ardhi. Na Yeye ni Mwenye nguvu, Mwenye hikima.'),
(5165, 61, 2, 'Enyi mlio amini! Kwa nini mnasema msiyo yatenda?'),
(5166, 61, 3, 'Yanachukiza vikubwa mno kwa Mwenyezi Mungu kuwa mnasema msiyo yatenda.'),
(5167, 61, 4, 'Kwa hakika Mwenyezi Mungu anawapenda wanao pigana katika Njia yake kwa safu kama jengo lilio kamatana.'),
(5168, 61, 5, 'Na Musa alipo waambia watu wake: Enyi watu wangu! Kwa nini mnaniudhi, nanyi mnajua kuwa hakika mimi ni Mtume wa Mwenyezi Mungu niliye tumwa kwenu? Walipo potoka, Mwenyezi Mungu aliziachia nyoyo zao zipotoke. Na Mwenyezi Mungu hawaongoi watu wapotovu.'),
(5169, 61, 6, 'Na Isa bin Mariamu alipo sema: Enyi Wana wa Israili! Hakika mimi ni Mtume wa Mwenyezi Mungu kwenu, ninaye thibitisha yaliyo kuwa kabla yangu katika Taurati, na mwenye kubashiria kuja Mtume atakaye kuja baada yangu; jina lake ni Ahmad! Lakini alipo waletea hoja zilizo wazi, walisema: Huu ni uchawi ulio dhaahiri!'),
(5170, 61, 7, 'Na ni nani mwenye kudhulumu zaidi kuliko yule anaye mzulia Mwenyezi Mungu uwongo, naye anaitwa kwenye Uislamu? Na Mwenyezi Mungu hawaongoi watu madhaalimu.'),
(5171, 61, 8, 'Wanataka kuizima nuru ya Mwenyezi Mungu kwa vinywa vyao. Na Mwenyezi Mungu atakamilisha nuru yake ijapo kuwa makafiri watachukia.'),
(5172, 61, 9, 'Yeye ndiye aliye mtuma Mtume wake kwa uwongofu na Dini ya haki ili ipate kuzishinda dini zote, ijapo kuwa washirikina watachukia.'),
(5173, 61, 10, 'Enyi mlio amini! Nikuonyesheni biashara itakayo kuokoeni na adhabu iliyo chungu?'),
(5174, 61, 11, 'Muaminini Mwenyezi Mungu na Mtume wake, na piganeni Jihadi katika Njia ya Mwenyezi Mungu kwa mali yenu na nafsi zenu. Haya ni bora kwenu, ikiwa nyinyi mnajua.'),
(5175, 61, 12, 'Atakusameheni dhambi zenu, na atakutieni katika Mabustani yapitiwayo na mito kati yake, na maskani nzuri nzuri katika Bustani za milele. Huko ndio kufuzu kukubwa.'),
(5176, 61, 13, 'Na kinginecho mkipendacho, nacho ni nusura itokayo kwa Mwenyezi Mungu, na ushindi ulio karibu! Na wabashirieni Waumini!'),
(5177, 61, 14, 'Enyi mlio amini! Kuweni wasaidizi wa Mwenyezi Mungu, kama alivyo sema Isa bin Mariamu kuwaambia wanafunzi wake: Nani wasaidizi wangu kwa Mwenyezi Mungu? Wakasema wanafunzi: Sisi ni wasaidizi wa Mwenyezi Mungu! Basi taifa moja la Wana wa Israili liliamini, na taifa jingine lilikufuru. Basi tukawaunga mkono walio amini dhidi ya maadui zao, wakawa wenye kushinda.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Viliomo mbinguni na viliomo katika ardhi vinamtakasa Mwenyezi Mungu, Mfalme, Mtakatifu, Mwenye nguvu, Mwenye hikima.'),
(5179, 62, 2, 'Yeye ndiye aliye mpeleka Mtume kwenye watu wasio jua kusoma, awasomee Aya zake na awatakase, na awafunze Kitabu na hikima, ijapo kuwa kabla ya haya walikuwa katika upotofu ulio dhaahiri.'),
(5180, 62, 3, 'Na kwa wengine ambao bado hawajaungana nao. Na Yeye ndiye Mwenye nguvu, Mwenye hikima.'),
(5181, 62, 4, 'Hiyo ni fadhila ya Mwenyezi Mungu anayo mpa amtakaye. Na Mwenyezi Mungu ni Mwenye fadhila kubwa.'),
(5182, 62, 5, 'Mfano wa walio bebeshwa Taurati kisha wasiibebe, ni mfano wa punda anaye beba vitabu vikubwa vikubwa. Mfano muovu mno wa watu walio kadhibisha Ishara za Mwenyezi Mungu, na Mwenyezi Mungu hawaongoi watu madhaalimu.'),
(5183, 62, 6, 'Sema: Enyi mlio Mayahudi! Ikiwa nyinyi mnadai kuwa ni vipenzi vya Mwenyezi Mungu pasipo kuwa watu wengine, basi yatamanini mauti, ikiwa mnasema kweli.'),
(5184, 62, 7, 'Wala hawatayatamani kabisa, kwa sababu ya iliyo kwisha yatanguliza mikono yao. Na Mwenyezi Mungu anawajua walio dhulumu.'),
(5185, 62, 8, 'Sema: Hayo mauti mnayo yakimbia, bila ya shaka yatakukuteni. Kisha mtarudishwa kwa Mwenye kuyajua yaliyo fichikana na yanayo onekana. Hapo atakwambieni mliyo kuwa mkiyatenda.'),
(5186, 62, 9, 'Enyi mlio amini! Ikiadhiniwa Sala siku ya Ijumaa, nendeni upesi kwenye dhikri ya Mwenyezi Mungu, na wacheni biashara. Hayo ni bora kwenu, lau kama mnajua.'),
(5187, 62, 10, 'Na itakapo kwisha Sala, tawanyikeni katika nchi mtafute fadhila za Mwenyezi Mungu, na mkumbukeni Mwenyezi Mungu kwa wingi ili mpate kufanikiwa.'),
(5188, 62, 11, 'Na wanapo iona biashara au pumbao wanayakimbilia hayo na wakakuacha umesimama. Sema: Yaliyoko kwa Mwenyezi Mungu ni bora kuliko pumbao na biashara. Na Mwenyezi Mungu ni Mbora wa watoa riziki.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Wanapo kujia wanaafiki husema: Tunashuhudia ya kuwa wewe ni Mtume wa Mwenyezi Mungu. Na Mwenyezi Mungu anajua kuwa wewe ni Mtume wa Mwenyezi Mungu, na Mwenyezi Mungu anashuhudia ya kuwa hakika wanaafiki ni waongo.'),
(5190, 63, 2, 'Wamevifanya viapo vyao ni kinga, na wao wakajizuia kuifuata Njia ya Mwenyezi Mungu. Hakika ni mabaya kabisa waliyo kuwa wakiyafanya.'),
(5191, 63, 3, 'Hayo ni kwa sababu ya kuwa waliamini, kisha wakakufuru; kwa hivyo umepigwa muhuri juu ya nyoyo zao. Kwa hivyo hawafahamu lolote.'),
(5192, 63, 4, 'Na unapo waona, miili yao inakupendeza, na wakisema, unasikiliza usemi wao. Lakini wao ni kama magogo yaliyo egemezwa. Wao hudhania kila ukelele unao pigwa ni kwa ajili yao. Hao ni maadui; tahadhari nao. Mwenyezi Mungu awaangamizilie mbali! Vipi wanapotoka na haki?'),
(5193, 63, 5, 'Na wanapo ambiwa: Njooni ili Mtume wa Mwenyezi Mungu akuombeeni maghfira, huvigeuza vichwa vyao, na unawaona wanageuka nao wamejaa kiburi.'),
(5194, 63, 6, 'Sawa sawa kwao, ukiwatakia maghfira au hukuwatakia maghfira, Mwenyezi Mungu hatawaghufiria. Hakika Mwenyezi Mungu hawaongoi watu wapotovu.'),
(5195, 63, 7, 'Hao ndio wanao sema: Msitoe mali kwa ajili ya walioko kwa Mtume wa Mwenyezi Mungu, ili waondokelee mbali! Na Mwenyezi Mungu ndiye Mwenye khazina za mbingu na ardhi, lakini wanaafiki hawafahamu.'),
(5196, 63, 8, 'Wanasema: Tukirejea Madina mwenye utukufu zaidi bila ya shaka atamfukuza aliye mnyonge. Na Mwenyezi Mungu ndiye Mwenye utukufu - Yeye, na Mtume wake, na Waumini. Lakini wanaafiki hawajui.'),
(5197, 63, 9, 'Enyi mlio amini! Yasikusahaulisheni mali yenu, wala watoto wenu, kumkumbuka Mwenyezi Mungu. Na wenye kufanya hayo ndio walio khasiri.'),
(5198, 63, 10, 'Na toeni katika tulicho kupeni kabla hayajamfikia mmoja wenu mauti, tena hapo akasema: Mola wangu Mlezi! Huniakhirishi muda kidogo nipate kutoa sadaka, na niwe katika watu wema?'),
(5199, 63, 11, 'Wala Mwenyezi Mungu hataiakhirisha nafsi yoyote inapo fika ajali yake; na Mwenyezi Mungu anazo khabari za mnayo yatenda.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Kinamtakasa Mwenyezi Mungu kila kilichomo katika mbingu na katika ardhi. Ufalme ni wake, na sifa njema ni zake. Na Yeye ni Mwenye uweza juu ya kila kitu.'),
(5201, 64, 2, 'Yeye ndiye aliye kuumbeni. Miongoni mwenu yupo aliye kafiri, na yupo aliye Muumini. Na Mwenyezi Mungu anayaona mnayo yatenda.'),
(5202, 64, 3, 'Ameziumba mbingu na ardhi kwa haki, na akakupeni sura, na akazifanya nzuri sura zenu. Na marudio ni kwake.'),
(5203, 64, 4, 'Anajua viliomo katika mbingu na ardhi, na anajua mnayo yaficha na mnayo yatangaza. Na Mwenyezi Mungu ni Mjuzi wa yaliomo vifuani.'),
(5204, 64, 5, 'Kwani haikukujieni khabari ya walio kufuru kabla, wakaonja matokeo mabaya ya mambo yao? Na wao wana adhabu iliyo chungu.'),
(5205, 64, 6, 'Hayo ni kwa kuwa walikuwa wakiwajia Mitume wao kwa hoja zilizo wazi, nao wakasema: Hivyo binaadamu ndio atuongoe? Basi wakakufuru, na wakageuka upande. Mwenyezi Mungu akawa hana haja nao. Na Mwenyezi Mungu ni Mkwasi, si mhitaji, naye ni Msifiwa.'),
(5206, 64, 7, 'Walio kufuru wanadai kuwa hawatafufuliwa. Sema: Kwani? Naapa kwa Mola wangu Mlezi! Hapana shaka mtafufuliwa, na kisha hapana shaka mtaambiwa mliyo yatenda. Na hayo ni mepesi kwa Mwenyezi Mungu.'),
(5207, 64, 8, 'Basi muaminini Mwenyezi Mungu na Mtume wake, na Nuru tuliyo iteremsha. Na Mwenyezi Mungu anazo khabari za mnayo yatenda.'),
(5208, 64, 9, 'Siku atakayo kukusanyeni kwa ajili ya Siku ya Mkusanyiko; hiyo ni siku ya kupunjana. Na anaye muamini Mwenyezi Mungu na akatenda mema, atamfutia maovu yake, na atamuingiza katika Mabustani yenye kupitiwa kati yake na mito, watadumu humo milele. Huko ndiko kufuzu kukubwa.'),
(5209, 64, 10, 'Na walio kufuru na wakakadhibisha Ishara zetu, hao ni watu wa Motoni, watadumu humo milele. Na hayo ndiyo marudio mabaya.'),
(5210, 64, 11, 'Haufiki msiba wowote ila kwa idhini ya Mwenyezi Mungu. Na mwenye kumuamini Mwenyezi Mungu huuongoa moyo wake. Na Mwenyezi Mungu ni Mjuzi wa kila kitu.'),
(5211, 64, 12, 'Na mt\'iini Mwenyezi Mungu, na mt\'iini Mtume. Mkigeuka, basi hakika juu ya Mtume wetu ni kufikisha (Ujumbe wake) kwa uwazi tu.'),
(5212, 64, 13, 'Mwenyezi Mungu, hapana mungu isipo kuwa Yeye. Na juu ya Mwenyezi Mungu nawategemee Waumini.'),
(5213, 64, 14, 'Enyi mlio amini! Hakika miongoni mwa wake zenu na watoto wenu wamo maadui zenu. Basi tahadharini nao. Na mkisamehe, na mkapuuza, na mkaghufiria basi hakika Mwenyezi Mungu ni Mwingi wa maghfira, Mwenye kurehemu.'),
(5214, 64, 15, 'Hakika mali yenu na watoto wenu ni jaribio. Na kwa Mwenyezi Mungu upo ujira mkubwa.'),
(5215, 64, 16, 'Basi mcheni Mwenyezi Mungu kama mwezavyo, na sikieni, na t\'iini, na toeni, itakuwa kheri kwa nafsi zenu. Na mwenye kuepushwa na uchoyo wa nafsi yake, basi hao ndio walio fanikiwa.'),
(5216, 64, 17, 'Mkimkopesha Mwenyezi Mungu mkopo mzuri, atakuzidishieni mardufu, na atakusameheni. Na Mwenyezi Mungu ni Mwingi wa shukra, Mpole.'),
(5217, 64, 18, 'Mwenye kujua siri na dhaahiri, Mwenye nguvu, Mwenye hikima.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Ewe Nabii! Mtakapo wapa talaka wanawake, basi wapeni talaka katika wakati wa eda zao. Na fanyeni hisabu ya eda. Na mcheni Mwenyezi Mungu, Mola wenu Mlezi. Msiwatoe katika nyumba zao, wala wasitoke wenyewe, ila wakifanya jambo la uchafu ulio wazi. Hiyo ndiyo mipaka ya Mwenyezi Mungu, msiikiuke. Na mwenye kuikiuka mipaka ya Mwenyezi Mungu, basi amejidhulumu nafsi yake. Hujui; labda Mwenyezi Mungu ataleta jambo jengine baada ya haya.'),
(5219, 65, 2, 'Basi wanapo fikia muda wao, ima warejeeni muwaweke kwa wema, au farikianeni nao kwa wema. Na mshuhudishe mashahidi wawili waadilifu miongoni mwenu. Na simamisheni ushahidi kwa ajili ya Mwenyezi Mungu. Hivyo ndivyo anavyo agizwa anaye muamini Mwenyezi Mungu na Siku ya Mwisho. Na anaye mcha Mwenyezi Mungu humtengezea njia ya kutokea.'),
(5220, 65, 3, 'Na humruzuku kwa jiha asiyo tazamia. Na anaye mtegemea Mwenyezi Mungu Yeye humtosha. Hakika Mwenyezi Mungu anatimiza amri yake. Mwenyezi Mungu kajaalia kila kitu na kipimo chake.'),
(5221, 65, 4, 'Na wale walio sita hedhi miongoni mwa wanawake wenu, ikiwa mnayo shaka, basi muda wa eda yao ni miezi mitatu, pamoja na ambao hawapati hedhi. Na wenye mimba eda yao mpaka watakapo zaa. Na anaye mcha Mwenyezi Mungu, Mwenyezi Mungu humfanyia mambo yake kuwa mepesi.'),
(5222, 65, 5, 'Hiyo ni amri ya Mwenyezi Mungu amekuteremshieni. Na anaye mcha Mwenyezi Mungu atamfutia maovu yake, na atampa ujira mkubwa.'),
(5223, 65, 6, 'Wawekeni humo humo mnamo kaa nyinyi kwa kadiri ya pato lenu, wala msiwaletee madhara kwa kuwadhiki. Na wakiwa wana mimba, wagharimieni mpaka wajifungue. Na wakikunyonyesheeni, basi wapeni ujira wao, na shaurianeni kwa wema. Na mkiona uzito kati yenu, basi amnyonyeshee mwanamke mwengine.'),
(5224, 65, 7, 'Mwenye wasaa atoe kadiri ya wasaa wake, na mwenye dhiki atoe katika alicho mpa Mwenyezi Mungu. Mwenyezi Mungu hamkalifishi mtu ila kwa kadiri ya alicho mpa. Mwenyezi Mungu atajaalia baada ya dhiki faraji.'),
(5225, 65, 8, 'Na miji mingapi iliyo vunja amri ya Mola wake Mlezi na Mitume wake, basi tuliihisabia hisabu ngumu na tukaiadhibu kwa adhabu kali.'),
(5226, 65, 9, 'Basi ikauonja ubaya wa mambo yake; na mwisho wa mambo yao ilikuwa khasara.'),
(5227, 65, 10, 'Mwenyezi Mungu aliwaandalia adhabu kali. Basi mcheni Mwenyezi Mungu, enyi wenye akili, mlio amini! Hakika Mwenyezi Mungu amekuteremshieni Ukumbusho,'),
(5228, 65, 11, 'Mtume anaye kusomeeni Aya za Mwenyezi Mungu zinazo bainisha, ili kuwatoa walio amini na wakatenda mema kutoka gizani kuwapeleka kwenye nuru. Na mwenye kumuamini Mwenyezi Mungu, na akatenda mema, atamwingiza katika Bustani zipitazo mito kati yake, wadumu humo milele. Mwenyezi Mungu amekwisha mpa riziki nzuri.'),
(5229, 65, 12, 'Mwenyezi Mungu ni yule ambaye ameziumba mbingu saba, na ardhi kwa mfano wa hizo. Amri zake zinashuka baina yao, ili mjue kwamba Mwenyezi Mungu ni Mwenye uweza juu ya kila kitu, na kwamba Mwenyezi Mungu amekizunguka kila kitu kukijua vilivyo kwa ilimu yake.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Ewe Nabii! Kwa nini unaharimisha alicho kuhalilishia Mwenyezi Mungu? Unatafuta kuwaridhi wake zako. Na Mwenyezi Mungu ni Mwenye kusamehe, Mwenye kurehemu.'),
(5231, 66, 2, 'Hakika Mwenyezi Mungu kakupeni Sharia ya kufungua viapo vyenu, na Mwenyezi Mungu ni Mola wenu. Na Yeye ni Mwenye kujua, Mwenye hikima.'),
(5232, 66, 3, 'Na Nabii alipo mwambia mmoja wa wake zake jambo la siri. Basi alipo litangaza yule mke, na Mwenyezi Mungu akamdhihirishia Mtume, alimjuvya sehemu na akaacha sehemu nyengine. Alipo mwambia hayo, huyo mke akasema: Nani aliye kwambia haya? Mtume akasema: Kaniambia Mjuzi Mwenye khabari zote!'),
(5233, 66, 4, 'Kama nyinyi wawili hamkutubia kwa Mwenyezi Mungu, basi nyoyo zenu zimekwisha elekea huko. Na mkisaidiana dhidi yake (Mtume), basi hakika Mwenyezi Mungu ndiye kipenzi chake, na Jibrili, na Waumini wema, na zaidi ya hayo Malaika pia watasaidia.'),
(5234, 66, 5, 'Asaa Mola wake Mlezi, akikupeni talaka, akampa wake wengine badala yenu nyinyi, walio bora kuliko nyinyi, wanynyekevu, Waumini, wat\'awa, walio tubu, wenye kushika ibada, wafanyao kheri, wajane na wanawari.'),
(5235, 66, 6, 'Enyi mlio amini! Jilindeni nafsi zenu na ahali zenu na Moto ambao kuni zake ni watu na mawe. Wanausimamia Malaika wakali, wenye nguvu, hawamuasi Mwenyezi Mungu kwa anayo waamrisha, na wanatenda wanayo amrishwa.'),
(5236, 66, 7, 'Enyi mlio kufuru! Msilete udhuru leo. Hakika mnalipwa mliyo kuwa mkiyatenda.'),
(5237, 66, 8, 'Enyi mlio amini! Tubuni kwa Mwenyezi Mungu toba iliyo ya kweli! Asaa Mola wenu Mlezi akakufutieni maovu yenu na akakuingizeni katika Pepo zipitazo mito kati yake, siku ambayo Mwenyezi Mungu hatamdhalilisha Nabii na walio amini pamoja naye. Nuru yao inakwenda mbele yao na pande zao za kulia, na huku wanasema: Mola wetu Mlezi! Tutimilizie nuru yetu, na utughufirie! Hakika Wewe ni Mwenye uweza juu ya kila kitu.'),
(5238, 66, 9, 'Ewe Nabii! Pambana na makafiri na wanaafiki, na kuwa mgumu kwao! Na makaazi yao ni Jahannamu. Na hayo ni marejeo mabaya.'),
(5239, 66, 10, 'Mwenyezi Mungu amewapigia mfano walio kufuru: mke wa Nuhu na mke wa Lut\'i. Walikuwa chini ya waja wetu wema wawili miongoni mwa waja wetu. Lakini wakawakhuni waume zao, nao wasiwafae kitu. Na ikasemwa: Ingieni Motoni pamoja na wanao ingia!'),
(5240, 66, 11, 'Na Mwenyezi Mungu amewapigia mfano walio amini - mkewe Firauni, alipo sema: Mola wangu Mlezi! Nijengee kwako nyumba Peponi, na uniokoe na Firauni na vitendo vyake, na uniokoe na watu madhaalimu.'),
(5241, 66, 12, 'Na Mariamu binti wa Imrani, aliye linda ubikira wake, na tukampulizia humo kutoka roho yetu, na akayasadiki maneno ya Mola wake Mlezi na Vitabu vyake, na alikuwa miongoni mwa wat\'iifu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'AMETUKUKA ambaye mkononi mwake umo Ufalme wote; na Yeye ni Mwenye uweza juu ya kila kitu.'),
(5243, 67, 2, 'Ambaye ameumba mauti na uhai ili kukujaribuni ni nani miongoni mwenu mwenye vitendo vizuri zaidi. Na Yeye ni Mwenye nguvu na Mwenye msamaha.'),
(5244, 67, 3, 'Aliye umba mbingu saba kwa matabaka. Huoni tafauti yoyote katika uumbaji wa Mwingi wa Rehema. Hebu rudisha nadhari! Unaona kosa lolote?'),
(5245, 67, 4, 'Tena rudisha nadhari mara mbili, nadhari yako itakurejea mwenyewe hali ya kuwa imehizika nayo imechoka.'),
(5246, 67, 5, 'Na kwa hakika tumeipamba mbingu ya karibu kwa mataa, na tumeyafanya ili kuwapigia mashetani, na tumewaandalia adhabu ya Moto uwakao kwa nguvu.'),
(5247, 67, 6, 'Na kwa walio mkufuru Mola wao Mlezi ipo adhabu ya Jahannamu. Na ni marejeo maovu yalioje hayo!'),
(5248, 67, 7, 'Watakapo tupwa humo watausikia mngurumo wake na huku inafoka.'),
(5249, 67, 8, 'Inakaribia kupasuka kwa hasira. Kila mara likitupwa kundi humo walinzi wake huwauliza: Kwani hakukujieni mwonyaji?'),
(5250, 67, 9, 'Watasema: Kwani! Ametujia mwonyaji, lakini tulimkadhibisha, na tukasema: Mwenyezi Mungu hakuteremsha chochote. Nyinyi hamumo ila katika upotovu mkubwa!'),
(5251, 67, 10, 'Na watasema: Lau kuwa tungeli sikia, au tungeli kuwa na akili, tusingeli kuwa katika watu wa Motoni!'),
(5252, 67, 11, 'Wakakiri dhambi zao. Basi kuangamia ni kwa watu wa Motoni!'),
(5253, 67, 12, 'Hakika wanao mwogopa Mola wao Mlezi kwa ghaibu, watapata maghfira na ujira mkubwa.'),
(5254, 67, 13, 'Na ficheni kauli zenu, au zitangazeni; hakika Yeye ni Mjuzi wa yaliyomo vifuani.'),
(5255, 67, 14, 'Asijue aliye umba, naye ndiye Mjua siri, Mwenye khabari?'),
(5256, 67, 15, 'Yeye ndiye aliye idhalilisha ardhi kwa faida yenu, basi tembeeni katika pande zake, na kuleni katika riziki zake. Na kwake Yeye ndio kufufuliwa.'),
(5257, 67, 16, 'Mnadhani mko salama kwa alioko juu kuwa hatakudidimizeni kwenye ardhi, na tahamaki hiyo inatikisika!'),
(5258, 67, 17, 'Au mnadhani mko salama kwa alioko juu ya kuwa Yeye hakupelekeeni kimbunga chenye changarawe? Mtajua vipi maonyo yangu?'),
(5259, 67, 18, 'Na bila ya shaka walikwisha kanusha walio kuwa kabla yao; basi kulikuwaje kukasirika kwangu?'),
(5260, 67, 19, 'Kwani hawawaoni ndege walioko juu yao namna wanavyo zikunjua mbawa zao, na kuzikunja? Hawawashikilii ila Mwingi wa rehema. Hakika Yeye ni Mwenye kuona kila kitu.'),
(5261, 67, 20, 'Au ni lipi hilo jeshi lenu la kukunusuruni badala ya Mwingi wa Rehema? Hakika makafiri hawamo ila katika udanganyifu.'),
(5262, 67, 21, 'Au ni nani huyo ambaye atakupeni riziki kama Yeye akizuia riziki yake? Bali wao wanakakamia tu katika jeuri na chuki.'),
(5263, 67, 22, 'Je! Anaye kwenda akisinukia juu ya uso wake ni mwongofu zaidi, au yule anaye kwenda sawasawa katika Njia Iliyo Nyooka?'),
(5264, 67, 23, 'Sema: Yeye ndiye aliye kuumbeni tangu mwanzo, na akakupeni masikio na macho, na nyoyo. Ni kidogo kabisa shukrani zenu.'),
(5265, 67, 24, 'Sema: Yeye ndiye aliye kutawanyeni katika ardhi, na kwake mtakusanywa.'),
(5266, 67, 25, 'Na wanasema: Lini ahadi hii, ikiwa mnasema kweli?'),
(5267, 67, 26, 'Sema: Hakika ujuzi wake uko kwa Mwenyezi Mungu; na hakika mimi ni mwonyaji tu mwenye kubainisha.'),
(5268, 67, 27, 'Lakini watakapo iona karibu, nyuso za walio kufuru zitahuzunishwa, na itasemwa: Hayo ndiyo mliyo kuwa mkiyaomba.'),
(5269, 67, 28, 'Sema: Mwaonaje, ikiwa Mwenyezi Mungu atanihiliki mimi na walio pamoja nami, au akiturehemu, ni nani atakaye walinda makafiri na adhabu chungu?'),
(5270, 67, 29, 'Sema: Yeye ndiye Mwingi wa Rehema; tunamuamini Yeye, na juu yake tunategemea. Mtakuja jua ni nani aliye katika upotovu ulio dhaahiri.'),
(5271, 67, 30, 'Sema: Mwaonaje, yakiwa maji yenu yamedidimia chini, nani atakueleteeni maji yanayo miminika?');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nuun. Naapa kwa kalamu na yale wayaandikayo,'),
(5273, 68, 2, 'Kwa neema ya Mola wako Mlezi wewe si mwendawazimu.'),
(5274, 68, 3, 'Na kwa hakika wewe una malipo yasiyo katika.'),
(5275, 68, 4, 'Na hakika wewe una tabia tukufu.'),
(5276, 68, 5, 'Karibu utaona, na wao wataona,'),
(5277, 68, 6, 'Ni nani kati yenu aliye pandwa na wazimu.'),
(5278, 68, 7, 'Hakika Mola wako Mlezi ndiye Yeye ajuaye zaidi nani aliye ipotea Njia yake, na Yeye ndiye anaye wajua zaidi walio ongoka.'),
(5279, 68, 8, 'Basi usiwat\'ii wanao kadhibisha.'),
(5280, 68, 9, 'Wanatamani lau unge lainisha ili nao wakulainishie.'),
(5281, 68, 10, 'Wala usimt\'ii kila mwingi wa kuapa wa kudharauliwa,'),
(5282, 68, 11, 'Mtapitapi, apitaye akifitini,'),
(5283, 68, 12, 'Mwenye kuzuia kheri, dhaalimu, mwingi wa madhambi,'),
(5284, 68, 13, 'Mkavu, na juu ya hayo, mshari, amejipachika tu.'),
(5285, 68, 14, 'Ati kwa kuwa ana mali na watoto!'),
(5286, 68, 15, 'Anapo somewa Aya zetu, husema: Hizi ni simulizi za uwongo za watu wa zamani!'),
(5287, 68, 16, 'Tutamtia kovu juu ya pua yake.'),
(5288, 68, 17, 'Hakika tumewajaribu hawa kama tulivyo wajaribu wale wenye shamba, walipo apa kwamba watayavuna mazao yake itakapo kuwa asubuhi.'),
(5289, 68, 18, 'Wala hawakusema: Mungu akipenda!'),
(5290, 68, 19, 'Basi lilitokea tukio juu yake kutoka kwa Mola wako Mlezi, nao wamelala!'),
(5291, 68, 20, 'Likawa kama usiku wa giza.'),
(5292, 68, 21, 'Asubuhi wakaitana.'),
(5293, 68, 22, 'Ya kwamba nendeni kondeni mwenu ikiwa mnataka kuvuna.'),
(5294, 68, 23, 'Basi walikwenda na huku wakinong\'onezana,'),
(5295, 68, 24, 'Ya kuwa leo hata masikini mmoja asikuingilieni.'),
(5296, 68, 25, 'Na walikwenda asubuhi, nao wameshikilia azimio hilo.'),
(5297, 68, 26, 'Basi walipo liona, wakasema: Hakika tumepotea!'),
(5298, 68, 27, 'Bali tumenyimwa!'),
(5299, 68, 28, 'Mbora wao akasema: Sikukwambieni, kwa nini hamumtakasi Mwenyezi Mungu?'),
(5300, 68, 29, 'Wakasema: Ametakasika Mola wetu Mlezi! Hakika sisi tulikuwa wenye kudhulumu.'),
(5301, 68, 30, 'Basi wakakabiliana kulaumiana wao kwa wao.'),
(5302, 68, 31, 'Wakasema: Ole wetu! Tulikuwa tumeikiuka mipaka!'),
(5303, 68, 32, 'Asaa Mola wetu Mlezi akatubadilishia lilio bora kuliko hili. Hakika sisi ni wenye kurejea kwa Mola wetu Mlezi.'),
(5304, 68, 33, 'Namna hivi yanakuwa mateso, na bila ya shaka mateso ya Akhera ni makubwa zaidi, laiti wangeli jua!'),
(5305, 68, 34, 'Hakika wachamngu watakuwa na Bustani za neema kwa Mola wao Mlezi.'),
(5306, 68, 35, 'Kwani tutawafanya Waislamu kama wakosefu?'),
(5307, 68, 36, 'Mna nini? Mnahukumu vipi?'),
(5308, 68, 37, 'Au mnacho kitabu ambacho ndani yake mnasoma?'),
(5309, 68, 38, 'Kuwa mtapata humo mnayo yapenda?'),
(5310, 68, 39, 'Au mnazo ahadi za viapo juu yetu za kufika Siku ya Kiyama ya kuwa hakika mtapata kila mnayo jihukumia?'),
(5311, 68, 40, 'Waulize: Ni nani miongoni mwao dhamini wa haya?'),
(5312, 68, 41, 'Au wanao washirika? Basi wawalete washirika wao wakiwa wanasema kweli.'),
(5313, 68, 42, 'Siku utakapo wekwa wazi mundi, na wataitwa kusujudu, lakini hawataweza,'),
(5314, 68, 43, 'Macho yao yatainama chini, fedheha itawafunika. Na hakika walikuwa wakiitwa wasujudu walipo kuwa wazima -'),
(5315, 68, 44, 'Basi niache na wanao kadhibisha maneno haya! Tutawavutia kidogo kidogo kwa mahali wasipo pajua.'),
(5316, 68, 45, 'Na ninawapururia muhula; hakika hila zangu ni imara.'),
(5317, 68, 46, 'Au unawaomba ujira, na wao wanaemewa na gharama hiyo?'),
(5318, 68, 47, 'Au iko kwao siri, basi wao wanaiandika?'),
(5319, 68, 48, 'Basi ingojee hukumu ya Mola wako Mlezi, wala usiwe kama mmezwa na samaki, alipo nadi naye kazongwa.'),
(5320, 68, 49, 'Kama isingeli mfikia neema kutoka kwa Mola wake Mlezi, bila ya shaka angeli tupwa ufukweni naye ni mwenye kulaumiwa.'),
(5321, 68, 50, 'Lakini Mola wake Mlezi alimteua na akamfanya miongoni mwa watu wema.'),
(5322, 68, 51, 'Na walio kufuru hukaribia kukutelezesha kwa macho yao, wanapo sikia mawaidha, na wanasema: Hakika yeye ni mwendawazimu.'),
(5323, 68, 52, 'Na hayakuwa haya, ila ni ukumbusho kwa walimwengu wote.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Tukio la haki.'),
(5325, 69, 2, 'Nini hilo Tukio la haki?'),
(5326, 69, 3, 'Na nini kitakujuulisha nini hilo Tukio la haki?'),
(5327, 69, 4, 'Thamudi na A\'di waliukadhibisha Msiba unao situsha.'),
(5328, 69, 5, 'Basi Thamudi waliangamizwa kwa balaa kubwa mno.'),
(5329, 69, 6, 'Na ama A\'di waliangamizwa kwa upepo mkali usio zuilika.'),
(5330, 69, 7, 'Alio wapelekea masiku saba, na siku nane, mfululizo bila ya kusita. Utaona watu wamepinduka kama kwamba ni magongo ya mitende yalio wazi ndani.'),
(5331, 69, 8, 'Basi je! Unamwona mmoja wao aliye baki?'),
(5332, 69, 9, 'Na Firauni na walio mtangulia, na miji iliyo pinduliwa chini juu, walileta khatia.'),
(5333, 69, 10, 'Wakamuasi Mtume wa Mola wao Mlezi, ndipo Yeye Mola akawakamata kwa mkamato ulio zidi nguvu.'),
(5334, 69, 11, 'Maji yalipo furika Sisi tulikupandisheni katika safina,'),
(5335, 69, 12, 'Ili tuyafanye hayo kuwa ni waadhi kwenu na kila sikio linalo sikia liyahifadhi.'),
(5336, 69, 13, 'Na litapo pulizwa barugumu mpulizo mmoja tu,'),
(5337, 69, 14, 'Na ardhi na milima ikaondolewa, na ikavunjwa kwa mvunjo mmoja,'),
(5338, 69, 15, 'Siku hiyo ndio Tukio litatukia.'),
(5339, 69, 16, 'Na mbingu zitapasuka, kwani siku hiyo zitakuwa dhaifu kabisa.'),
(5340, 69, 17, 'Na Malaika watakuwa pembezoni mwa mbingu; na wanane juu ya hawa watakuwa wamebeba Kiti cha Enzi cha Mola wako Mlezi.'),
(5341, 69, 18, 'Siku hiyo mtahudhurishwa - haitafichika siri yoyote yenu.'),
(5342, 69, 19, 'Basi ama atakaye pewa kitabu chake kwa mkono wake wa kulia, atasema: Haya someni kitabu changu!'),
(5343, 69, 20, 'Hakika nalijua ya kuwa nitapokea hisabu yangu.'),
(5344, 69, 21, 'Basi yeye atakuwa katika maisha ya kupendeza,'),
(5345, 69, 22, 'Katika Bustani ya juu,'),
(5346, 69, 23, 'Matunda yake yakaribu.'),
(5347, 69, 24, 'Waambiwe: Kuleni na mnywe kwa furaha kwa sababu ya mlivyo tanguliza katika siku zilizo pita.'),
(5348, 69, 25, 'Na ama atakaye pewa kitabu chake katika mkono wake wa kushoto, basi yeye atasema: Laiti nisingeli pewa kitabu changu!'),
(5349, 69, 26, 'Wala nisingeli jua nini hisabu yangu.'),
(5350, 69, 27, 'Laiti mauti ndiyo yangeli kuwa ya kunimaliza kabisa.'),
(5351, 69, 28, 'Mali yangu hayakunifaa kitu.'),
(5352, 69, 29, 'Madaraka yangu yamenipotea.'),
(5353, 69, 30, '(Pasemwe): Mshikeni! Mtieni pingu!'),
(5354, 69, 31, 'Kisha mtupeni Motoni!'),
(5355, 69, 32, 'Tena mtatizeni katika mnyororo wenye urefu wa dhiraa sabiini!'),
(5356, 69, 33, 'Kwani huyo hakika alikuwa hamuamini Mwenyezi Mungu Mtukufu,'),
(5357, 69, 34, 'Wala hahimizi kulisha masikini.'),
(5358, 69, 35, 'Basi leo hapa hana jamaa wa kumwonea uchungu,'),
(5359, 69, 36, 'Wala hana chakula ila usaha wa watu wa Motoni.'),
(5360, 69, 37, 'Chakula hicho hawakili ila wakosefu.'),
(5361, 69, 38, 'Basi naapa kwa mnavyo viona,'),
(5362, 69, 39, 'Na msivyo viona,'),
(5363, 69, 40, 'Kwa hakika hii ni kauli iliyo letwa na Mjumbe mwenye hishima.'),
(5364, 69, 41, 'Wala si kauli ya mtunga mashairi. Ni machache sana mnayo yaamini.'),
(5365, 69, 42, 'Wala si kauli ya kuhani. Ni machache mnayo yakumbuka.'),
(5366, 69, 43, 'Ni uteremsho utokao kwa Mola Mlezi wa walimwengu wote.'),
(5367, 69, 44, 'Na lau kama angeli tuzulia baadhi ya maneno tu,'),
(5368, 69, 45, 'Bila ya shaka tungeli mshika kwa mkono wa kulia,'),
(5369, 69, 46, 'Kisha kwa hakika tungeli mkata mshipa mkubwa wa moyo!'),
(5370, 69, 47, 'Na hapana yeyote katika nyinyi ambaye angeli weza kutuzuia.'),
(5371, 69, 48, 'Kwa hakika hii ni mawaidha kwa wachamngu.'),
(5372, 69, 49, 'Na hakika Sisi bila ya shaka tunajua kwamba miongoni mwenu wapo wanao kadhibisha.'),
(5373, 69, 50, 'Na hakika bila ya shaka itakuwa ni majuto kwa wanao kataa.'),
(5374, 69, 51, 'Na hakika hii bila ya shaka ni haki ya yakini.'),
(5375, 69, 52, 'Basi litakase jina la Mola wako Mlezi Mtukufu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Muulizaji aliuliza juu ya adhabu itakayo tokea,'),
(5377, 70, 2, 'Kwa makafiri - ambayo hapana awezaye kuizuia -'),
(5378, 70, 3, 'Kutoka kwa Mwenyezi Mungu Mwenye mbingu za daraja.'),
(5379, 70, 4, 'Malaika na Roho hupanda kwendea kwake katika siku ambayo kadiri yake ni miaka khamsini elfu!'),
(5380, 70, 5, 'Basi subiri kwa subira njema.'),
(5381, 70, 6, 'Hakika wao wanaiona iko mbali,'),
(5382, 70, 7, 'Na Sisi tunaiona iko karibu.'),
(5383, 70, 8, 'Siku ambayo mbingu zitakapo kuwa kama maadeni iliyo yayushwa.'),
(5384, 70, 9, 'Na milima itakuwa kama sufi.'),
(5385, 70, 10, 'Wala jamaa hatamuuliza jamaa yake.'),
(5386, 70, 11, 'Ijapo kuwa wataonyeshwa waonane. Atatamani mkosefu lau ajikomboe na adhabu ya siku hiyo kwa kuwatoa fidia wanawe,'),
(5387, 70, 12, 'Na mkewe, na nduguye,'),
(5388, 70, 13, 'Na jamaa zake walio kuwa wakimkimu,'),
(5389, 70, 14, 'Na wote waliomo duniani, kisha aokoke yeye.'),
(5390, 70, 15, 'La, hasha! Kwa hakika huo ni Moto mkali kabisa,'),
(5391, 70, 16, 'Unao babua ngozi ya kichwa!'),
(5392, 70, 17, 'Utamwita kila aliye geuza mgongo na akageuka.'),
(5393, 70, 18, 'Na akakusanya mali, kisha akayahifadhi.'),
(5394, 70, 19, 'Hakika mtu ameumbwa na papara.'),
(5395, 70, 20, 'Inapo mgusa shari hupapatika.'),
(5396, 70, 21, 'Na inapo mgusa kheri huizuilia.'),
(5397, 70, 22, 'Isipo kuwa wanao sali,'),
(5398, 70, 23, 'Ambao wanadumisha Sala zao,'),
(5399, 70, 24, 'Na ambao katika mali yao iko haki maalumu'),
(5400, 70, 25, 'Kwa mwenye kuomba na anaye jizuilia kuomba;'),
(5401, 70, 26, 'Na ambao wanaisadiki Siku ya Malipo,'),
(5402, 70, 27, 'Na ambao wanaiogopa adhabu itokayo kwa Mola wao Mlezi.'),
(5403, 70, 28, 'Hakika adhabu ya Mola wao Mlezi si ya kuaminika nayo.'),
(5404, 70, 29, 'Na ambao wanahifadhi tupu zao.'),
(5405, 70, 30, 'Isipo kuwa kwa wake zao, au iliyo wamiliki mikono yao ya kuume, basi hao hawalaumiwi -'),
(5406, 70, 31, 'Lakini wanao taka kinyume ya haya, basi hao ndio wanao ruka mipaka.'),
(5407, 70, 32, 'Na ambao wanazichunga amana zao na ahadi zao,'),
(5408, 70, 33, 'Na ambao wanasimama imara katika ushahidi wao,'),
(5409, 70, 34, 'Na ambao wanazihifadhi Sala zao.'),
(5410, 70, 35, 'Hao ndio watakao hishimiwa Peponi.'),
(5411, 70, 36, 'Wana nini wale walio kufuru wanakutumbulia macho tu?'),
(5412, 70, 37, 'Makundi kwa makundi upande wa kulia na upande wa kushoto!'),
(5413, 70, 38, 'Kwani kila mmoja wao anatumai kuingizwa katika Pepo ya neema?'),
(5414, 70, 39, 'La, hasha! Hakika Sisi tumewaumba kutokana na wanacho kijua.'),
(5415, 70, 40, 'Basi naapa kwa Mola Mlezi wa mashariki zote na magharibi zote kwamba Sisi tunaweza'),
(5416, 70, 41, 'Kuwabadili kwa walio bora kuliko wao; na Sisi hatushindwi.'),
(5417, 70, 42, 'Basi waache wapige porojo na wacheze, mpaka wakutane na siku yao wanayo ahidiwa,'),
(5418, 70, 43, 'Siku watakapotoka makaburini kwa upesi kama kwamba wanakimbilia mfundo,'),
(5419, 70, 44, 'Macho yao yatainama, fedheha itawafunika. Hiyo ndiyo Siku waliyo kuwa wakiahidiwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Hakika Sisi tulimtuma Nuh\'u kwa watu wake: Uwaonye kaumu yako kabla ya kuwafikia adhabu chungu.'),
(5421, 71, 2, 'Akasema: Enyi watu wangu! Kwa hakika mimi ni mwonyaji wa dhaahiri kwenu,'),
(5422, 71, 3, 'Ya kwamba mumuabudu Mwenyezi Mungu, na mumche Yeye, na mumt\'ii.'),
(5423, 71, 4, 'Atakughufirieni madhambi yenu, na atakuakhirisheni mpaka muda ulio wekwa. Hakika muda wa Mwenyezi Mungu utapo fika haucheleweshwi. Laiti mngejua!'),
(5424, 71, 5, 'Akasema: Ee Mola wangu Mlezi! Hakika mimi nimewaita watu wangu usiku na mchana,'),
(5425, 71, 6, 'Lakini wito wangu haukuwazidisha ila kukimbia.'),
(5426, 71, 7, 'Na hakika mimi kila nilipo waita ili upate kuwaghufiria, walijiziba masikio yao kwa vidole vyao, na wakajigubika nguo zao, na wakakamia, na wakatakabari vikubwa mno!'),
(5427, 71, 8, 'Tena niliwaita kwa uwazi,'),
(5428, 71, 9, 'Kisha nikawatangazia kwa kelele, tena nikasema nao kwa siri.'),
(5429, 71, 10, 'Nikasema: Ombeni msamaha kwa Mola wenu Mlezi; hakika Yeye ni Mwingi wa kusamehe.'),
(5430, 71, 11, 'Atakuleteeni mvua inyeshe mfululizo.'),
(5431, 71, 12, 'Na atakupeni mali na wana, na atakupeni mabustani na atakufanyieni mito.'),
(5432, 71, 13, 'Mna nini hamweki heshima ya Mwenyezi Mungu?'),
(5433, 71, 14, 'Na hali Yeye kakuumbeni daraja baada ya daraja?'),
(5434, 71, 15, 'Kwani hamwoni jinsi Mwenyezi Mungu alivyo ziumba mbingu saba kwa matabaka?'),
(5435, 71, 16, 'Na akaufanya mwezi ndani yake uwe nuru, na akalifanya jua kuwa taa?'),
(5436, 71, 17, 'Na Mwenyezi Mungu amekuotesheni katika ardhi kama mimea.'),
(5437, 71, 18, 'Kisha atakurudsheni humo na atakutoeni tena.'),
(5438, 71, 19, 'Na Mwenyezi Mungu amekukunjulieni ardhi kama busati.'),
(5439, 71, 20, 'Ili mtembee humo katika njia zilizo pana.'),
(5440, 71, 21, 'Nuh\'u akasema: Mola wangu Mlezi! Hakika hao wameniasi, na wamemfuata yule ambaye mali yake na wanawe hawakumzidishia ila khasara.'),
(5441, 71, 22, 'Na wakapanga vitimbi vikubwa.'),
(5442, 71, 23, 'Na wakasema: Msiwaache miungu yenu, wala msimwache Wadda wala Suwaa\' wala Yaghutha, wala Yau\'qa, wala Nasra.'),
(5443, 71, 24, 'Na hao walikwisha wapoteza wengi, wala usiwazidishie walio dhulumu ila kupotea.'),
(5444, 71, 25, 'Basi kwa ajili ya makosa yao waligharikishwa wakaingizwa Motoni, wala hawakuwapata wa kuwanusuru badala ya Mwenyezi Mungu.'),
(5445, 71, 26, 'Na Nuh\'u akasema: Mola wangu Mlezi! Usimwache juu ya ardhi mkaazi wake yeyote katika makafiri!'),
(5446, 71, 27, 'Hakika ukiwaacha watawapoteza waja wako, wala wao hawatazaa ila waovu makafiri.'),
(5447, 71, 28, 'Mola wangu Mlezi! Nighufirie mimi na wazazi wangu, na kila aliye ingia nyumbani mwangu kuwa ni mwenye kuamini, na Waumini wote wanaume, na Waumini wanawake. Na wala usiwazidishie walio dhulumu ila kupotea.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Sema: Imefunuliwa kwangu ya kuwa kundi moja la majini lilisikiliza, na likasema: Hakika sisi tumeisikia Qur\'ani ya ajabu!'),
(5449, 72, 2, 'Inaongoza kwenye uwongofu, kwa hivyo tumeiamini, wala hatutamshirikisha yeyote na Mola wetu Mlezi.'),
(5450, 72, 3, 'Na kwa hakika utukufu wa Mola wetu Mlezi umetukuka kabisa; hana mke wala mwana.'),
(5451, 72, 4, 'Na kwa hakika wapumbavu miongoni mwetu walikuwa wakisema uwongo ulio pindukia mipaka juu ya Mwenyezi Mungu.'),
(5452, 72, 5, 'Nasi tulidhani kuwa watu na majini hawamzulii uwongo Mwenyezi Mungu.'),
(5453, 72, 6, 'Na hakika walikuwako wanaume katika watu walio kuwa wakitafuta kujikinga kwa wanaume wa kijini; kwa hivyo wakawazidisha madhambi.'),
(5454, 72, 7, 'Na kwa hakika wao walidhani, kama mlivyo dhani nyinyi, ya kuwa Mwenyezi Mungu hatamleta Mtume.'),
(5455, 72, 8, 'Nasi tulizigusa mbingu, na tukaziona zimejaa walinzi wenye nguvu na vimondo.'),
(5456, 72, 9, 'Na hakika tulikuwa tukikaa humo katika baadhi ya makao ili kusikiliza; lakini sasa anaye taka kusikiliza atakuta kimondo kinamvizia!'),
(5457, 72, 10, 'Nasi hatujui kama wanatakiwa shari wale wanao kaa kwenye ardhi au Mola wao Mlezi anawatakia uwongofu.'),
(5458, 72, 11, 'Na hakika katika sisi wamo walio wema, na wengine wetu ni kinyume na hivyo. Tumekuwa njia mbali mbali.'),
(5459, 72, 12, 'Nasi tulijua kuwa hatutomshinda Mwenyezi Mungu duniani, wala hatuwezi kumponyoka kwa kukimbia.'),
(5460, 72, 13, 'Nasi tulipo usikia uwongofu tuliuamini. Basi mwenye kumuamini Mola wake Mlezi basi haogopi kupunjwa wala kudhulumiwa.'),
(5461, 72, 14, 'Na hakika wamo katika sisi Waislamu, na wamo kati yetu wanao acha haki. Basi walio silimu, hao ndio walio tafuta uwongofu.'),
(5462, 72, 15, 'Na ama wanao acha haki, hao watakuwa kuni za Jahannamu.'),
(5463, 72, 16, 'Na lau kama wangeli simama sawasawa juu ya njia tungeli wanywesha maji kwa wingi,'),
(5464, 72, 17, 'Ili tuwajaribu kwa hayo. Na anaye puuza kumkumbuka Mola wake Mlezi atamsukuma kwenye adhabu ngumu.'),
(5465, 72, 18, 'Na hakika misikiti ni ya Mwenyezi Mungu, basi msimuabudu yeyote pamoja na Mwenyezi Mungu.'),
(5466, 72, 19, 'Na hakika Mja wa Mwenyezi Mungu alipo simama kumwomba, wao walikuwa karibu kumzonga!'),
(5467, 72, 20, 'Sema: Hakika mimi namwomba Mola wangu Mlezi, wala simshirikishi Yeye na yeyote.'),
(5468, 72, 21, 'Sema: Mimi sina mamlaka ya kukudhuruni wala kukuongozeni.'),
(5469, 72, 22, 'Sema: Hakika hapana yeyote awezae kunilinda na Mwenyezi Mungu, wala sitapata pa kukimbilia isipo kuwa kwake Yeye tu.'),
(5470, 72, 23, 'Ila nifikishe Ujumbe utokao kwa Mwenyezi Mungu na risala zake. Na wenye kumuasi Mwenyezi Mungu na Mtume wake, basi hakika hao watapata Moto wa Jahannamu wadumu humo milele.'),
(5471, 72, 24, 'Hata watakapo yaona wanayo ahidiwa, ndipo watakapo jua ni nani mwenye msaidizi dhaifu, na mchache wa idadi.'),
(5472, 72, 25, 'Sema: Sijui kama yapo karibu hayo mnayo ahidiwa, au Mola wangu Mlezi atayawekea muda mrefu.'),
(5473, 72, 26, 'Yeye ndiye Mwenye kujua ya siri, wala hamdhihirishii yeyote siri yake,'),
(5474, 72, 27, 'Isipo kuwa Mtume wake aliye mridhia. Naye huyo humwekea walinzi mbele yake na nyuma yake.'),
(5475, 72, 28, 'Ili Yeye ajue kwamba wao wamefikisha ujumbe wa Mola wao Mlezi, na Yeye anayajua vyema yote walio nayo, na amedhibiti idadi ya kila kitu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Ewe uliye jifunika!'),
(5477, 73, 2, 'Kesha usiku kucha, ila kidogo tu!'),
(5478, 73, 3, 'Nusu yake, au ipunguze kidogo.'),
(5479, 73, 4, 'Au izidishe - na soma Qur\'ani kwa utaratibu na utungo.'),
(5480, 73, 5, 'Hakika Sisi tutakuteremshia kauli nzito.'),
(5481, 73, 6, 'Hakika kuamka usiku kunawafikiana zaidi na moyo, na maneno yake yanatua zaidi.'),
(5482, 73, 7, 'Kwani hakika mchana una shughuli nyingi.'),
(5483, 73, 8, 'Na lidhukuru jina la Mola wako Mlezi, na ujitolee kwake kwa ukamilifu.'),
(5484, 73, 9, 'Mola Mlezi wa mashariki na magharibi, hapana mungu isipo kuwa Yeye, basi mfanye kuwa Mtegemewa wako.'),
(5485, 73, 10, 'Na vumilia hayo wayasemayo, na jitenge nao kwa vizuri.'),
(5486, 73, 11, 'Na niache Mimi na hao wanao kanusha, walio neemeka; na wape muhula kidogo!'),
(5487, 73, 12, 'Hakika Sisi tunazo pingu nzito na Moto unao waka kwa ukali kabisa!'),
(5488, 73, 13, 'Na chakula kinacho kwama kooni, na adhabu inayo umiza.'),
(5489, 73, 14, 'Siku ambayo ardhi itatikiswa na milima pia, na milima itakuwa kama tifutifu la mchanga!'),
(5490, 73, 15, 'Hakika Sisi tumemtuma kwenu Mtume aliye shahidi juu yenu, kama tulivyo mtuma Mtume kwa Firauni.'),
(5491, 73, 16, 'Lakini Firauni alimuasi huyo Mtume, basi tukamshika mshiko wa mateso.'),
(5492, 73, 17, 'Basi nyinyi mkikufuru, mtawezaje kujikinga na Siku ambayo itawafanya watoto wadogo waote mvi?'),
(5493, 73, 18, 'Hapo mbingu zitapasuka! Ahadi yake itakuwa imetekelezwa.'),
(5494, 73, 19, 'Kwa hakika hili ni kumbusho. Mwenye kutaka atashika njia ya kwendea kwa Mola wake Mlezi.'),
(5495, 73, 20, 'Hakika Mola wako Mlezi anajua ya kuwa hakika wewe unakesha karibu na thuluthi mbili za usiku, na nusu yake, na thuluthi yake. Na baadhi ya watu walio pamoja nawe kadhaalika. Na Mwenyezi Mungu ndiye anaye ukadiria usiku na mchana. Anajua kuwa hamwezi kuweka hisabu, basi amekusameheni. Basi someni kilicho chepesi katika Qur\'ani. Anajua ya kuwa baadhi yenu watakuwa wagonjwa, na wengine wanasafiri katika ardhi wakitafuta fadhila ya Mwenyezi Mungu, na wengine wanapigana katika Njia ya Mwenyezi Mungu. Kwa hivyo someni kilicho chepesi humo, na mshike Sala, na toeni Zaka, na mkopesheni Mwenyezi Mungu mkopo mwema. Na kheri yoyote mnayo itanguliza kwa ajili ya nafsi zenu mtaikuta kwa Mwenyezi Mungu, imekuwa bora zaidi, na ina thawabu kubwa sana. Na mtakeni msamaha Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye kusamehe Mwenye kurehemu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Ewe uliye jigubika!'),
(5497, 74, 2, 'Simama uonye!'),
(5498, 74, 3, 'Na Mola wako Mlezi mtukuze!'),
(5499, 74, 4, 'Na nguo zako, zisafishe.'),
(5500, 74, 5, 'Na yaliyo machafu yahame!'),
(5501, 74, 6, 'Wala usitoe kwa kutaraji kuzidishiwa.'),
(5502, 74, 7, 'Na kwa ajili ya Mola wako Mlezi, subiri!'),
(5503, 74, 8, 'Basi litapo pulizwa barugumu,'),
(5504, 74, 9, 'Siku hiyo, basi, itakuwa siku ngumu.'),
(5505, 74, 10, 'Kwa makafiri haitakuwa nyepesi.'),
(5506, 74, 11, 'Niache peke yangu na niliye muumba;'),
(5507, 74, 12, 'Na nikamjaalia awe na mali mengi,'),
(5508, 74, 13, 'Na wana wanao onekana,'),
(5509, 74, 14, 'Na nikamtgenezea mambo vizuri kabisa.'),
(5510, 74, 15, 'Kisha anatumai nimzidishie!'),
(5511, 74, 16, 'Hasha! Kwani hakika yeye alikuwa akizifanyia inadi Aya zetu!'),
(5512, 74, 17, 'Nitamtesa kwa mateso yasio wezekana.'),
(5513, 74, 18, 'Kwani hakika yeye alifikiri na akapima.'),
(5514, 74, 19, 'Basi ameangamia! Vipi alivyo pima!'),
(5515, 74, 20, 'Tena ameangamia! Vipi alivyo pima!'),
(5516, 74, 21, 'Kisha akatazama,'),
(5517, 74, 22, 'Kisha akakunja kipaji, na akanuna.'),
(5518, 74, 23, 'Kisha akaipa kisogo haki, na akatakabari.'),
(5519, 74, 24, 'Na akasema: Haya si chochote ila ni uchawi ulio nukuliwa.'),
(5520, 74, 25, 'Haya si chochote ila kauli ya binaadamu.'),
(5521, 74, 26, 'Nitakuja mtia kwenye Moto wa Saqar.'),
(5522, 74, 27, 'Na nini kitakujuulisha ni nini huo Moto wa Saqar?'),
(5523, 74, 28, 'Haubakishi wala hausazi.'),
(5524, 74, 29, 'Unababua ngozi iwe nyeusi.'),
(5525, 74, 30, 'Juu yake wapo kumi na tisa.'),
(5526, 74, 31, 'Na hatukuwafanya walinzi wa Motoni ila ni Malaika, wala hatukuifanya idadi yao hiyo ila kuwatatanisha walio kufuru, wapate kuwa na yakini walio pewa Kitabu, na wazidi Imani wale walio amini, wala wasiwe na shaka walio pewa Kitabu na Waumini, na wapate kusema wenye maradhi katika nyoyo zao na makafiri: Mwenyezi Mungu amekusudia nini kwa mfano huu? Ndio kama hivyo Mwenyezi Mungu humwacha apotee amtakaye, na humwongoa amtakaye. Na hapana yeyote anaye jua majeshi ya Mola wako Mlezi ila Yeye tu. Na haya si chochote ila ni ukumbusho kwa binaadamu.'),
(5527, 74, 32, 'Hasha! Naapa kwa mwezi!'),
(5528, 74, 33, 'Na kwa usiku unapo kucha!'),
(5529, 74, 34, 'Na kwa asubuhi inapo pambazuka!'),
(5530, 74, 35, 'Hakika huo Moto ni katika mambo yaliyo makubwa kabisa!'),
(5531, 74, 36, 'Ni onyo kwa binaadamu,'),
(5532, 74, 37, 'Kwa anaye taka miongoni mwenu kutangulia au kuchelewa.'),
(5533, 74, 38, 'Kila nafsi imo rahanini kwa mabaya iliyo yachuma.'),
(5534, 74, 39, 'Isipo kuwa watu wa kuliani.'),
(5535, 74, 40, 'Hao watakuwa katika Mabustani, wawe wanaulizana'),
(5536, 74, 41, 'Khabari za wakosefu:'),
(5537, 74, 42, 'Ni nini kilicho kupelekeni Motoni?'),
(5538, 74, 43, 'Waseme: Hatukuwa miongoni walio kuwa wakisali.'),
(5539, 74, 44, 'Wala hatukuwa tukiwalisha masikini.'),
(5540, 74, 45, 'Na tulikuwa tukizama pamoja na walio zama katika maovu.'),
(5541, 74, 46, 'Na tulikuwa tukiikanusha siku ya malipo.'),
(5542, 74, 47, 'Mpaka yakini ilipo tufikia.'),
(5543, 74, 48, 'Basi hautawafaa kitu uombezi wa waombezi.'),
(5544, 74, 49, 'Basi wana nini hata wanapuuza onyo hili?'),
(5545, 74, 50, 'Kama kwamba wao ni mapunda walio timuliwa,'),
(5546, 74, 51, 'Wanao mkimbia simba!'),
(5547, 74, 52, 'Ati kila mmoja katika wao anataka apewe nyaraka zilizo funuliwa.'),
(5548, 74, 53, 'Hasha! Kwani wao hawaiogopi Akhera?'),
(5549, 74, 54, 'Hasha! Kwa hakika huu ni ukumbusho!'),
(5550, 74, 55, 'Basi anaye taka atakumbuka.'),
(5551, 74, 56, 'Na hawatakumbuka isipo kuwa Mwenyezi Mungu atake. Uchamngu ni kwake Yeye, na msamaha ni wake Yeye.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Ninaapa kwa Siku ya Kiyama!'),
(5553, 75, 2, 'Na ninaapa kwa nafsi inayo jilaumu!'),
(5554, 75, 3, 'Anadhani mtu kuwa Sisi hatutaikusanya mifupa yake?'),
(5555, 75, 4, 'Kwani! Sisi tunaweza hata kuziweka sawa sawa ncha za vidole vyake!'),
(5556, 75, 5, 'Lakini mtu anataka tu kuendelea na maasi kwa siku zilioko mbele yake.'),
(5557, 75, 6, 'Anauliza: Lini itakuwa hiyo Siku ya Kiyama?'),
(5558, 75, 7, 'Basi jicho litapo dawaa,'),
(5559, 75, 8, 'Na mwezi utapo patwa,'),
(5560, 75, 9, 'Na likakusanywa jua na mwezi,'),
(5561, 75, 10, 'Siku hiyo mtu atasema: Yako wapi makimbilio?'),
(5562, 75, 11, 'La! Hapana pa kukimbilia!'),
(5563, 75, 12, 'Siku hiyo pa kutua ni kwa Mola wako Mlezi tu.'),
(5564, 75, 13, 'Siku hiyo ataambiwa aliyo yatanguliza na aliyo yaakhirisha.'),
(5565, 75, 14, 'Bali mtu ni hoja juu ya nafsi yake.'),
(5566, 75, 15, 'Na ingawa atatoa chungu ya udhuru.'),
(5567, 75, 16, 'Usiutikisie huu wahyi ulimi wako kwa kuufanyia haraka.'),
(5568, 75, 17, 'Hakika ni juu yetu kuukusanya na kuusomesha.'),
(5569, 75, 18, 'Tunapo usoma, basi nawe fuatiliza kusoma kwake.'),
(5570, 75, 19, 'Kisha ni juu yetu kuubainisha.'),
(5571, 75, 20, 'Hasha! Bali nyinyi mnapenda maisha ya duniani,'),
(5572, 75, 21, 'Na mnaacha maisha ya Akhera.'),
(5573, 75, 22, 'Zipo nyuso siku hiyo zitao ng\'ara,'),
(5574, 75, 23, 'Zinamwangallia Mola wao Mlezi.'),
(5575, 75, 24, 'Na zipo nyuso siku hiyo zitakao kunjana.'),
(5576, 75, 25, 'Zitajua ya kuwa zitafikiwa na livunjalo uti wa mgongo.'),
(5577, 75, 26, 'La, hasha! (Roho) itakapo fikia kwenye mafupa ya koo,'),
(5578, 75, 27, 'Na pakasemwa: Nani wa kumganga?'),
(5579, 75, 28, 'Na mwenyewe akajua kwa hakika kuwa huko ndiko kufariki;'),
(5580, 75, 29, 'Na utapo ambatishwa muundi kwa muundi,'),
(5581, 75, 30, 'Siku hiyo ndiyo kuchungwa kupelekwa kwa Mola wako Mlezi!'),
(5582, 75, 31, 'Kwa sababu hakusadiki, wala hakusali.'),
(5583, 75, 32, 'Bali alikanusha, na akageuka.'),
(5584, 75, 33, 'Kisha akenda kwa ahali zake kwa matao.'),
(5585, 75, 34, 'Ole wako, ole wako!'),
(5586, 75, 35, 'Kisha Ole wako, ole wako!'),
(5587, 75, 36, 'Ati anadhani binaadamu kuwa ataachwa bure?'),
(5588, 75, 37, 'Kwani hakuwa yeye tone ya manii lilio shushwa?'),
(5589, 75, 38, 'Kisha akawa kidonge cha damu, tena Mwenyezi Mungu akamuumba na akamtengeneza vilivyo.'),
(5590, 75, 39, 'Kisha akamfanya namna mbili, mwanamume na mwanamke.'),
(5591, 75, 40, 'Je! Huyo hakuwa ni Muweza wa kufufua wafu?');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Hakika kilimpitia binaadamu kipindi katika zama ambacho kwamba hakuwa kitu kinacho tajwa.'),
(5593, 76, 2, 'Hakika Sisi tumemuumba mtu kutokana na mbegu ya uhai iliyo changanyika, tumfanyie mtihani. Kwa hivyo tukamfanya mwenye kusikia, mwenye kuona.'),
(5594, 76, 3, 'Hakika Sisi tumembainishia Njia. Ama ni mwenye kushukuru, au mwenye kukufuru.'),
(5595, 76, 4, 'Hakika Sisi tumewaandalia makafiri minyororo na pingu na Moto mkali.'),
(5596, 76, 5, 'Hakika watu wema watakunywa katika vinywaji vilivyo changanyika na kafuri,'),
(5597, 76, 6, 'Ni chemchem watakao inywa waja wa Mwenyezi Mungu, wakiifanya imiminike kwa wingi.'),
(5598, 76, 7, 'Wanatimiza ahadi, na wanaiogopa siku ambayo shari yake inaenea sana,'),
(5599, 76, 8, 'Na huwalisha chakula, juu ya kukipenda kwake, masikini, na yatima, na wafungwa.'),
(5600, 76, 9, 'Hakika sisi tunakulisheni kwa wajihi wa Mwenyezi Mungu. Hatutaki kwenu malipo wala shukrani.'),
(5601, 76, 10, 'Hakika sisi tunaiogopa kwa Mola wetu Mlezi hiyo siku yenye shida na taabu.'),
(5602, 76, 11, 'Basi Mwenyezi Mungu atawalinda na shari ya siku hiyo, na atawakutanisha na raha na furaha.'),
(5603, 76, 12, 'Na atawajazi Bustani za Peponi na maguo ya hariri kwa vile walivyo subiri.'),
(5604, 76, 13, 'Humo wataegemea juu ya viti vya enzi, hawataona humo jua kali wala baridi kali.'),
(5605, 76, 14, 'Na vivuli vyake vitakuwa karibu yao, na mashada ya matunda yataning\'inia mpaka chini.'),
(5606, 76, 15, 'Na watapitishiwa vyombo vya fedha na vikombe vya vigae,'),
(5607, 76, 16, 'Vya fedha safi kama kiyoo, wamevipima kwa vipimo.'),
(5608, 76, 17, 'Na humo watanyweshwa kinywaji kilicho changanyika na tangawizi.'),
(5609, 76, 18, 'Hiyo ni chemchem iliyo humo inaitwa Salsabil.'),
(5610, 76, 19, 'Na watawazungukia kuwatumikia wavulana wasio pevuka, ukiwaona utawafikiri ni lulu zilizo tawanywa.'),
(5611, 76, 20, 'Na utakapo yaona, utakuwa umeona neema na ufalme mkubwa.'),
(5612, 76, 21, 'Juu yao zipo nguo za hariri laini za kijani kibichi, na hariri nzito ya at\'ilasi. Na watavikwa vikuku vya fedha, na Mola wao Mlezi atawanywesha kinywaji safi kabisa.'),
(5613, 76, 22, 'Hakika haya ni malipo yenu; na juhudi zenu zimekubaliwa.'),
(5614, 76, 23, 'Hakika Sisi tumekuteremshia Qur\'ani kidogo kidogo.'),
(5615, 76, 24, 'Basi ngojea hukumu ya Mola wako Mlezi wala usimt\'ii miongoni mwao mwenye dhambi au mwenye kufuru.'),
(5616, 76, 25, 'Na likumbuke jina la Mola wako Mlezi asubuhi na jioni;'),
(5617, 76, 26, 'Na usiku msujudie Yeye, na umtakase usiku, wakati mrefu.'),
(5618, 76, 27, 'Kwa hakika watu hawa wanapenda maisha ya kidunia, na wanaiacha nyuma yao siku nzito.'),
(5619, 76, 28, 'Sisi tumewaumba, na tukavitia nguvu viungo vyao. Na tukitaka tutawabadilisha mfano wao wawe badala yao.'),
(5620, 76, 29, 'Hakika haya ni mawaidha; basi anaye taka atashika Njia ya kwenda kwa Mola wake Mlezi.'),
(5621, 76, 30, 'Wala hamwezi kutaka ila atakapo Mwenyezi Mungu. Hakika Mwenyezi Mungu ni Mwenye ilimu, Mwenye hikima.'),
(5622, 76, 31, 'Humuingiza amtakaye katika rehema yake. Na wenye kudhulumu amewawekea adhabu iliyo chungu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Naapa kwa zinazo tumwa kwa upole!'),
(5624, 77, 2, 'Na zinazo vuma kwa kasi!'),
(5625, 77, 3, 'Na zikaeneza maeneo yote!'),
(5626, 77, 4, 'Na zinazo farikisha zikatawanya!'),
(5627, 77, 5, 'Na zinazo peleka mawaidha!'),
(5628, 77, 6, 'Kwa kuudhuru au kuonya,'),
(5629, 77, 7, 'Hakika mnayo ahidiwa bila ya shaka yatakuwa!'),
(5630, 77, 8, 'Wakati nyota zitakapo futwa,'),
(5631, 77, 9, 'Na mbingu zitakapo pasuliwa,'),
(5632, 77, 10, 'Na milima itakapo peperushwa,'),
(5633, 77, 11, 'Na Mitume watakapo wekewa wakati wao,'),
(5634, 77, 12, 'Kwa siku gani hiyo wamewekewa muda huo?'),
(5635, 77, 13, 'Kwa siku ya kupambanua!'),
(5636, 77, 14, 'Na nini kitakacho kujuulisha siku ya kupambanua ni nini?'),
(5637, 77, 15, 'Ole wao, siku hiyo hao wanao kanusha!'),
(5638, 77, 16, 'Kwani hatukuwaangamiza walio tangulia?'),
(5639, 77, 17, 'Kisha tukawafuatilizia walio fuatia?'),
(5640, 77, 18, 'Ndio kama hivi tuwatendavyo wakosefu!'),
(5641, 77, 19, 'Ole wao siku hiyo hao wanao kanusha!'),
(5642, 77, 20, 'Kwani hatukukuumbeni kwa maji ya kudharauliwa?'),
(5643, 77, 21, 'Kisha tukayaweka mahali pa utulivu madhubuti?'),
(5644, 77, 22, 'Mpaka muda maalumu?'),
(5645, 77, 23, 'Tukakadiria na Sisi ni wabora wa kukadiria.'),
(5646, 77, 24, 'Ole wao siku hiyo hao wanao kanusha!'),
(5647, 77, 25, 'Kwani hatukuifanya ardhi yenye kukusanya'),
(5648, 77, 26, 'Walio hai na maiti?'),
(5649, 77, 27, 'Na ndani yake tukaweka milima mirefu yenye kuthibiti, na tunakunywesheni maji matamu?'),
(5650, 77, 28, 'Ole wao siku hiyo hao wanao kanusha!'),
(5651, 77, 29, 'Nendeni kwenye adhabu mliyo kuwa mkiikanusha!'),
(5652, 77, 30, 'Nendeni kwenye kivuli chenye mapande matatu!'),
(5653, 77, 31, 'Hakikingi moto, wala hakiwaepushi na mwako.'),
(5654, 77, 32, 'Hakika Moto huo unatoa macheche kama majumba!'),
(5655, 77, 33, 'Kama kwamba ni ngamia wa rangi ya manjano!'),
(5656, 77, 34, 'Ole wao siku hiyo hao wanao kanusha!'),
(5657, 77, 35, 'Hii ni siku ambayo hawatatamka kitu,'),
(5658, 77, 36, 'Wala hawataruhusiwa kutoa udhuru.'),
(5659, 77, 37, 'Ole wao siku hiyo hao walio kanusha!'),
(5660, 77, 38, 'Hii ndiyo siku ya kupambanua. Tumekukusanyeni nyinyi na walio tangulia.'),
(5661, 77, 39, 'Ikiwa mnayo hila, nifanyieni hila Mimi!'),
(5662, 77, 40, 'Ole wao siku hiyo hao walio kanusha!'),
(5663, 77, 41, 'Hakika wachamngu watakuwa katika vivuli na chemchem,'),
(5664, 77, 42, 'Na matunda wanayo yapenda,'),
(5665, 77, 43, 'Kuleni na kunyweni kwa furaha kwa yale mliyo kuwa mkiyatenda.'),
(5666, 77, 44, 'Hakika ndio kama hivyo tunavyo walipa watendao mema.'),
(5667, 77, 45, 'Ole wao siku hiyo hao wanao kanusha!'),
(5668, 77, 46, 'Kuleni na mjifurahishe kidogo tu. Hakika nyinyi ni wakosefu!'),
(5669, 77, 47, 'Ole wao siku hiyo hao wanao kanusha!'),
(5670, 77, 48, 'Na wakiambiwa: Inameni! Hawainami.'),
(5671, 77, 49, 'Ole wao siku hiyo hao wanao kanusha!'),
(5672, 77, 50, 'Basi maneno gani baada ya haya watayaamini?');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'WANAULIZANA nini?'),
(5674, 78, 2, 'Ile khabari kuu,'),
(5675, 78, 3, 'Ambayo kwayo wanakhitalifiana.'),
(5676, 78, 4, 'La! Karibu watakuja jua.'),
(5677, 78, 5, 'Tena la! Karibu watakuja jua.'),
(5678, 78, 6, 'Kwani hatukuifanya ardhi kama tandiko?'),
(5679, 78, 7, 'Na milima kama vigingi?'),
(5680, 78, 8, 'Na tukakuumbeni kwa jozi?'),
(5681, 78, 9, 'Na tukakufanya kulala kwenu ni mapumziko?'),
(5682, 78, 10, 'Na tukaufanya usiku ni nguo?'),
(5683, 78, 11, 'Na tukaufanya mchana ni wa kuchumia maisha?'),
(5684, 78, 12, 'Na tukajenga juu yenu saba zenye nguvu?'),
(5685, 78, 13, 'Na tukaifanya taa yenye mwanga na joto;'),
(5686, 78, 14, 'Na tukateremsha maji yanayo anguka kwa kasi kutoka mawinguni,'),
(5687, 78, 15, 'Ili tutoe kwayo nafaka na mimea,'),
(5688, 78, 16, 'Na mabustani yenye miti iliyo kamatana.'),
(5689, 78, 17, 'Hakika siku ya uamuzi imewekewa wakati wake,'),
(5690, 78, 18, 'Siku litapo pulizwa barugumu, nanyi mtakuja kwa makundi,'),
(5691, 78, 19, 'Na mbingu zitafunguliwa, ziwe milango,'),
(5692, 78, 20, 'Na milima itaondolewa na itakuwa kama sarabi.'),
(5693, 78, 21, 'Hakika Jahannamu inangojea!'),
(5694, 78, 22, 'Kwa walio asi ndio makaazi yao,'),
(5695, 78, 23, 'Wakae humo karne baada ya karne,'),
(5696, 78, 24, 'Hawataonja humo chochote kibaridi wala kinywaji,'),
(5697, 78, 25, 'Ila maji yamoto sana na usaha,'),
(5698, 78, 26, 'Ndio jaza muwafaka.'),
(5699, 78, 27, 'Hakika hao hawakuwa wakitaraji kuwa kuna hisabu.'),
(5700, 78, 28, 'Na wakikanusha Aya zetu kwa nguvu.'),
(5701, 78, 29, 'Na kila kitu Sisi tumekidhibiti kwa kukiandika.'),
(5702, 78, 30, 'Basi onjeni! Nasi hatutakuzidishieni ila adhabu!'),
(5703, 78, 31, 'Hakika wachamngu wanastahiki kufuzu,'),
(5704, 78, 32, 'Mabustani na mizabibu,'),
(5705, 78, 33, 'Na wake walio lingana nao,'),
(5706, 78, 34, 'Na bilauri zilizo jaa,'),
(5707, 78, 35, 'Hawatasikia humo upuuzi wala uwongo -'),
(5708, 78, 36, 'Malipo kutoka kwa Mola wako Mlezi, kipawa cha kutosha.'),
(5709, 78, 37, 'Mola Mlezi wa mbingu na ardhi na vilio baina yao, Arrahman, Mwingi wa rehema; hawamiliki usemi mbele yake!'),
(5710, 78, 38, 'Siku atakapo simama Roho na Malaika kwa safu. Hawatasema ila Mwingi wa rehema aliye mruhusu, na atasema yaliyo sawa tu.'),
(5711, 78, 39, 'Hiyo ndiyo Siku ya haki. Basi anaye taka na ashike njia arejee kwa Mola wake Mlezi.'),
(5712, 78, 40, 'Hakika tumekuhadharisheni adhabu iliyo karibu kufika; Siku ambayo mtu atakapo ona yaliyo tangulizwa na mikono yake; na kafiri atasema: Laiti ningeli kuwa udongo!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Naapa kwa wanao komoa kwa nguvu,'),
(5714, 79, 2, 'Na kwa wanao toa kwa upole,'),
(5715, 79, 3, 'Na wanao ogelea,'),
(5716, 79, 4, 'Wakishindana mbio,'),
(5717, 79, 5, 'Wakidabiri mambo.'),
(5718, 79, 6, 'Siku kitapo tetemeka cha kutetemeka,'),
(5719, 79, 7, 'Kifuate cha kufuatia.'),
(5720, 79, 8, 'Siku hiyo nyoyo zitapiga piga,'),
(5721, 79, 9, 'Macho yatainama chini.'),
(5722, 79, 10, 'Sasa wanasema: Ati kweli tutarudishwa kwenye hali ya kwanza?'),
(5723, 79, 11, 'Hata tukiwa mifupa iliyo bunguliwa?'),
(5724, 79, 12, 'Wanasema: Basi marejeo hayo ni yenye khasara!'),
(5725, 79, 13, 'Kwa hakika hayo ni ukelele mmoja tu,'),
(5726, 79, 14, 'Mara watakuwa kwenye uwanda wa mkutano!'),
(5727, 79, 15, 'Je! Imekufikia hadithi ya Musa?'),
(5728, 79, 16, 'Mola wake Mlezi alipo mwita katika bonde takatifu la T\'uwaa, akamwambia:'),
(5729, 79, 17, 'Nenda kwa Firauni. Hakika yeye amezidi jeuri.'),
(5730, 79, 18, 'Umwambie: Je! Unapenda kujitakasa?'),
(5731, 79, 19, 'Nami nitakuongoa ufike kwa Mola wako Mlezi, upate kumcha.'),
(5732, 79, 20, 'Basi alimwonyesha Ishara kubwa.'),
(5733, 79, 21, 'Lakini aliikadhibisha na akaasi.'),
(5734, 79, 22, 'Kisha alirudi nyuma, akaingia kufanya juhudi.'),
(5735, 79, 23, 'Akakusanya watu akanadi.'),
(5736, 79, 24, 'Akasema: Mimi ndiye Mola wenu Mlezi mkuu kabisa.'),
(5737, 79, 25, 'Basi hapo Mwenyezi Mungu akamshika kumuadhibu kwa la mwisho na la mwanzo.'),
(5738, 79, 26, 'Kwa hakika katika haya yapo mazingatio kwa wanao ogopa.'),
(5739, 79, 27, 'Je! Ni vigumu zaidi kukuumbeni nyinyi au mbingu? Yeye ndiye aliye ijenga!'),
(5740, 79, 28, 'Akainua kimo chake, na akaitengeneza vizuri.'),
(5741, 79, 29, 'Na akautia giza usiku wake, na akautokeza mchana wake.'),
(5742, 79, 30, 'Na juu ya hivyo ameitandaza ardhi.'),
(5743, 79, 31, 'Akatoa ndani yake maji yake na malisho yake,'),
(5744, 79, 32, 'Na milima akaisimamisha,'),
(5745, 79, 33, 'Kwa nafuu yenu na mifugo yenu.'),
(5746, 79, 34, 'Basi itakapo fika hiyo balaa kubwa,'),
(5747, 79, 35, 'Siku ambayo mtu atakumbuka aliyo yafanya,'),
(5748, 79, 36, 'Na Jahannamu itadhihirishwa kwa mwenye kuona,'),
(5749, 79, 37, 'Basi ama yule aliye zidi ujeuri,'),
(5750, 79, 38, 'Na akakhiari maisha ya dunia,'),
(5751, 79, 39, 'Kwa hakika Jahannamu ndiyo makaazi yake!'),
(5752, 79, 40, 'Na ama yule anaye ogopa kusimamishwa mbele ya Mola wake Mlezi, na akajizuilia nafsi yake na matamanio,'),
(5753, 79, 41, 'Basi huyo, Pepo itakuwa ndiyo makaazi yake!'),
(5754, 79, 42, 'Wanakuuliza Saa (ya Kiyama) itakuwa lini?'),
(5755, 79, 43, 'Una nini wewe hata uitaje?'),
(5756, 79, 44, 'Kwa Mola wako Mlezi ndio mwisho wake.'),
(5757, 79, 45, 'Kwa hakika wewe ni mwonyaji tu kwa mwenye kuikhofu.'),
(5758, 79, 46, 'Ni kama kwamba wao siku watapo iona (hiyo Saa) hawakukaa ila jioni moja tu, au mchana wake.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Alikunja kipaji na akageuka,'),
(5760, 80, 2, 'Kwa sababu alimjia kipofu!'),
(5761, 80, 3, 'Na nini kitakujuulisha pengine yeye atatakasika?'),
(5762, 80, 4, 'Au atawaidhika, na mawaidha yamfae?'),
(5763, 80, 5, 'Ama ajionaye hana haja,'),
(5764, 80, 6, 'Wewe ndio unamshughulikia?'),
(5765, 80, 7, 'Na si juu yako kama hakutakasika.'),
(5766, 80, 8, 'Ama anaye kujia kwa juhudi,'),
(5767, 80, 9, 'Naye anaogopa,'),
(5768, 80, 10, 'Ndio wewe unampuuza?'),
(5769, 80, 11, 'Sivyo hivyo! Huku ni kukumbushana.'),
(5770, 80, 12, 'Basi anaye penda akumbuke.'),
(5771, 80, 13, 'Yamo katika kurasa zilizo hishimiwa,'),
(5772, 80, 14, 'Zilizo inuliwa, zilizo takaswa.'),
(5773, 80, 15, 'Zimo mikononi mwa Malaika waandishi,'),
(5774, 80, 16, 'Watukufu, wema.'),
(5775, 80, 17, 'Ameangamia mwanaadamu! Nini kinacho mkufurisha?'),
(5776, 80, 18, 'Kwa kitu gani amemuumba?'),
(5777, 80, 19, 'Kwa tone la manii, akamuumba na akamkadiria.'),
(5778, 80, 20, 'Kisha akamsahilishia njia.'),
(5779, 80, 21, 'Kisha akamfisha, akamtia kaburini.'),
(5780, 80, 22, 'Kisha apendapo atamfufua.'),
(5781, 80, 23, 'La! Hajamaliza aliyo muamuru.'),
(5782, 80, 24, 'Hebu mtu na atazame chakula chake.'),
(5783, 80, 25, 'Hakika Sisi tumemimina maji kwa nguvu,'),
(5784, 80, 26, 'Tena tukaipasua pasua ardhi kwa nguvu,'),
(5785, 80, 27, 'Kisha tukaotesha humo nafaka,'),
(5786, 80, 28, 'Na zabibu, na mimea ya majani,'),
(5787, 80, 29, 'Na mizaituni, na mitende,'),
(5788, 80, 30, 'Na bustani zenye miti iliyo songana baraabara,'),
(5789, 80, 31, 'Na matunda, na malisho ya wanyama;'),
(5790, 80, 32, 'Kwa manufaa yenu na mifugo yenu.'),
(5791, 80, 33, 'Basi utakapo kuja ukelele,'),
(5792, 80, 34, 'Siku ambayo mtu atamkimbia nduguye,'),
(5793, 80, 35, 'Na mamaye na babaye,'),
(5794, 80, 36, 'Na mkewe na wanawe -'),
(5795, 80, 37, 'Kila mtu miongoni mwao siku hiyo atakuwa na lake la kumtosha.'),
(5796, 80, 38, 'Siku hiyo ziko nyuso zitazo nawiri,'),
(5797, 80, 39, 'Zitacheka, zitachangamka;'),
(5798, 80, 40, 'Na nyuso siku hiyo zitakuwa na mavumbi,'),
(5799, 80, 41, 'Giza totoro litazifunika,'),
(5800, 80, 42, 'Hao ndio makafiri watenda maovu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Jua litakapo kunjwa,'),
(5802, 81, 2, 'Na nyota zikazimwa,'),
(5803, 81, 3, 'Na milima ikaondolewa,'),
(5804, 81, 4, 'Na ngamia wenye mimba pevu watakapo achwa wasishughulikiwe,'),
(5805, 81, 5, 'Na wanyama wa mwituni wakakusanywa,'),
(5806, 81, 6, 'Na bahari zikawaka moto,'),
(5807, 81, 7, 'Na nafsi zikaunganishwa,'),
(5808, 81, 8, 'Na msichana aliye zikwa hai atapo ulizwa,'),
(5809, 81, 9, 'Kwa kosa gani aliuliwa?'),
(5810, 81, 10, 'Na madaftari yatakapo enezwa,'),
(5811, 81, 11, 'Na mbingu itapo tanduliwa,'),
(5812, 81, 12, 'Na Jahannamu itapo chochewa,'),
(5813, 81, 13, 'Na Pepo ikasogezwa,'),
(5814, 81, 14, 'Kila nafsi itajua ilicho kihudhurisha.'),
(5815, 81, 15, 'Naapa kwa nyota zinapo rejea nyuma,'),
(5816, 81, 16, 'Zinazo kwenda, kisha zikajificha,'),
(5817, 81, 17, 'Na kwa usiku unapo pungua,'),
(5818, 81, 18, 'Na kwa asubuhi inapo pambazuka,'),
(5819, 81, 19, 'Kwamba hakika bila ya shaka hii ni kauli ya Mjumbe mtukufu,'),
(5820, 81, 20, 'Mwenye nguvu na mwenye cheo kwa huyo Mwenye Kiti cha Enzi,'),
(5821, 81, 21, 'Anaye t\'iiwa, tena muaminifu.'),
(5822, 81, 22, 'Na wala huyu mwenzenu hana wazimu.'),
(5823, 81, 23, 'Na hakika yeye alimwona kwenye upeo wa macho ulio safi.'),
(5824, 81, 24, 'Wala yeye si bakhili kwa mambo ya ghaibu.'),
(5825, 81, 25, 'Wala hii si kauli ya Shetani maluuni.'),
(5826, 81, 26, 'Basi mnakwenda wapi?'),
(5827, 81, 27, 'Haikuwa hii ila ni ukumbusho kwa walimwengu wote.'),
(5828, 81, 28, 'Kwa yule miongoni mwenu anaye taka kwenda sawa.'),
(5829, 81, 29, 'Wala nyinyi hamtataka isipo kuwa atake Mwenyezi Mungu Mola Mlezi wa walimwengu wote.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Mbingu itapo chanika,'),
(5831, 82, 2, 'Na nyota zitapo tawanyika,'),
(5832, 82, 3, 'Na bahari zitakapo pasuliwa,'),
(5833, 82, 4, 'Na makaburi yatapo fukuliwa,'),
(5834, 82, 5, 'Hapo kila nafsi itajua ilicho tanguliza, na ilicho bakisha nyuma.'),
(5835, 82, 6, 'Ewe mwanaadamu! Nini kilicho kughuri ukamwacha Mola wako Mlezi Mtukufu?'),
(5836, 82, 7, 'Aliye kuumba, akakuweka sawa, akakunyoosha,'),
(5837, 82, 8, 'Katika sura yoyote aliyo ipenda akakujenga.'),
(5838, 82, 9, 'Sivyo hivyo! Bali nyinyi mnaikanusha Siku ya Malipo.'),
(5839, 82, 10, 'Na hakika bila ya shaka wapo walinzi juu yenu,'),
(5840, 82, 11, 'Waandishi wenye hishima,'),
(5841, 82, 12, 'Wanayajua mnayo yatenda.'),
(5842, 82, 13, 'Hakika wema bila ya shaka watakuwa katika neema,'),
(5843, 82, 14, 'Na hakika waovu bila ya shaka watakuwa Motoni;'),
(5844, 82, 15, 'Wataingia humo Siku ya Malipo.'),
(5845, 82, 16, 'Na hawatoacha kuwamo humo.'),
(5846, 82, 17, 'Na nini kitakacho kujuulisha Siku ya Malipo ni siku gani?'),
(5847, 82, 18, 'Tena nini kitakacho kujuulisha Siku ya Malipo ni siku gani?'),
(5848, 82, 19, 'Siku ambayo nafsi haitakuwa na madaraka yoyote juu ya nafsi; na amri yote siku hiyo ni ya Mwenyezi Mungu tu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Ole wao hao wapunjao!'),
(5850, 83, 2, 'Ambao wanapo jipimia kwa watu hudai watimiziwe.'),
(5851, 83, 3, 'Na wao wanapo wapimia watu kwa kipimo au mizani hupunguza.'),
(5852, 83, 4, 'Kwani hawadhani hao kwamba watafufuliwa'),
(5853, 83, 5, 'Katika Siku iliyo kuu,'),
(5854, 83, 6, 'Siku watapo msimamia watu Mola Mlezi wa walimwengu wote?'),
(5855, 83, 7, 'Hasha! Hakika maandiko ya wakosefu bila ya shaka yamo katika Sijjin.'),
(5856, 83, 8, 'Unajua nini Sijjin?'),
(5857, 83, 9, 'Kitabu kilicho andikwa.'),
(5858, 83, 10, 'Ole wao siku hiyo kwa wanao kadhibisha!'),
(5859, 83, 11, 'Ambao wanaikadhibisha Siku ya Malipo.'),
(5860, 83, 12, 'Wala haikadhibishi ila kila mwenye kuruka mipaka, mwenye dhambi.'),
(5861, 83, 13, 'Anapo somewa Aya zetu husema: Ni visa vya watu wa kale!'),
(5862, 83, 14, 'Hasha! Bali yametia kutu juu ya nyoyo zao hao walio kuwa wakiyachuma.'),
(5863, 83, 15, 'Hasha! Hakika hao siku hiyo bila ya shaka watazuiliwa na neema za Mola wao Mlezi.'),
(5864, 83, 16, 'Kisha wataingia Motoni!'),
(5865, 83, 17, 'Kisha waambiwe: Haya ndiyo mliyo kuwa mkiyakadhabisha.'),
(5866, 83, 18, 'Hasha! Hakika maandiko ya watu wema bila ya shaka yamo katika I\'liyyin.'),
(5867, 83, 19, 'Na nini kitakacho kujuvya nini I\'liyyin?'),
(5868, 83, 20, 'Kitabu kilicho andikwa.'),
(5869, 83, 21, 'Wanakishuhudia walio karibishwa.'),
(5870, 83, 22, 'Hakika watu wema bila ya shaka watakuwa katika neema.'),
(5871, 83, 23, 'Wakae juu ya viti vya enzi wakiangalia.'),
(5872, 83, 24, 'Utatambua katika nyuso zao mng\'aro wa neema,'),
(5873, 83, 25, 'Watanyweshwa kinywaji safi kiliyo tiwa muhuri,'),
(5874, 83, 26, 'Muhuri wake ni miski. Na katika hayo washindanie wenye kushindana.'),
(5875, 83, 27, 'Na mchanganyiko wake ni Tasniim,'),
(5876, 83, 28, 'Chemchem watakayo inywa walio kurubishwa.'),
(5877, 83, 29, 'Kwa hakika wale walio kuwa wakosefu walikuwa wakiwacheka walio amini.'),
(5878, 83, 30, 'Na wanapo pita karibu yao wakikonyezana.'),
(5879, 83, 31, 'Na wanapo rudi kwa watu wao hurudi nao wamefurahi.'),
(5880, 83, 32, 'Na wanapo waona husema: Hakika hawa ndio khasa walio potea.'),
(5881, 83, 33, 'Na wao hawakutumwa wawe walinzi juu yao.'),
(5882, 83, 34, 'Basi leo walio amini ndio watawacheka makafiri,'),
(5883, 83, 35, 'Nao wako juu ya viti vya enzi wakiangalia.'),
(5884, 83, 36, 'Je! makafiri wamelipwa malipo ya yale waliyo kuwa wakiyatenda?');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Itapo chanika mbingu,'),
(5886, 84, 2, 'Na ikamsikiliza Mola wake Mlezi, na ikapasiwa kumsikiliza,'),
(5887, 84, 3, 'Na ardhi itakapo tanuliwa,'),
(5888, 84, 4, 'Na kuvitoa vilivyo kuwa ndani yake, ikawa tupu,'),
(5889, 84, 5, 'Na ikamsikiliza Mola wake Mlezi, na ikapasiwa kumsikiliza,'),
(5890, 84, 6, 'Ewe mtu! Hakika wewe unajikusuru kwa juhudi kumwendea Mola wako Mlezi, basi utamkuta.'),
(5891, 84, 7, 'Ama atakaye pewa daftari lake kwa mkono wa kulia,'),
(5892, 84, 8, 'Basi huyo atahisabiwa hisabu nyepesi,'),
(5893, 84, 9, 'Na arudi kwa ahali zake na furaha.'),
(5894, 84, 10, 'Na ama atakaye pewa daftari lake kwa nyuma ya mgongo wake,'),
(5895, 84, 11, 'Basi huyo ataomba kuteketea.'),
(5896, 84, 12, 'Na ataingia Motoni.'),
(5897, 84, 13, 'Hakika alikuwa furahani kati ya jamaa zake.'),
(5898, 84, 14, 'Huyo hakika alidhani kuwa hatarejea tena.'),
(5899, 84, 15, 'Kwani? Hakika Mola wake Mlezi alikuwa akimwona!'),
(5900, 84, 16, 'Basi ninaapa kwa wekundu wa jua linapo kuchwa,'),
(5901, 84, 17, 'Na kwa usiku na unavyo vikusanya,'),
(5902, 84, 18, 'Na kwa mwezi unapo pevuka,'),
(5903, 84, 19, 'Lazima mtapanda t\'abaka kwa t\'abaka!'),
(5904, 84, 20, 'Basi wana nini hawaamini?'),
(5905, 84, 21, 'Na wanapo somewa Qur\'ani hawasujudu?'),
(5906, 84, 22, 'Bali walio kufuru wanakanusha tu.'),
(5907, 84, 23, 'Na Mwenyezi Mungu anajua wanayo yadhamiria.'),
(5908, 84, 24, 'Basi wabashirie adhabu chungu!'),
(5909, 84, 25, 'Isipo kuwa wale walio amini na wakatenda mema; hao watakuwa na ujira usio malizika.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Naapa kwa mbingu yenye Buruji!'),
(5911, 85, 2, 'Na kwa siku iliyo ahidiwa!'),
(5912, 85, 3, 'Na kwa shahidi na kinacho shuhudiwa!'),
(5913, 85, 4, 'Wameangamizwa watu wa makhandaki'),
(5914, 85, 5, 'Yenye moto wenye kuni nyingi,'),
(5915, 85, 6, 'Walipo kuwa wamekaa hapo,'),
(5916, 85, 7, 'Na wao ni mashahidi wa yale waliyo kuwa wakiwafanyia Waumini.'),
(5917, 85, 8, 'Nao hawakuona baya lolote kwao ila kuwa wakimuamini Mwenyezi Mungu, Mwenye nguvu, Msifiwa,'),
(5918, 85, 9, 'Ambaye anao ufalme wa mbingu na ardhi; na Mwenyezi Mungu ni shaahidi wa kila kitu.'),
(5919, 85, 10, 'Hakika walio wafitini Waumini wanaume na Waumini wanawake, kisha hawakutubu, basi watapata adhabu ya Jahannamu na watapata adhabu ya kuungua.'),
(5920, 85, 11, 'Hakika walio amini na wakatenda mema watapata Bustani zenye mito ipitayo kati yake. Huko ndiko kufuzu kukubwa.'),
(5921, 85, 12, 'Hakika kutesa kwa Mola wako Mlezi ni kukali.'),
(5922, 85, 13, 'Yeye ndiye anaye anzisha na ndiye anaye rejeza tena,'),
(5923, 85, 14, 'Naye ni Mwenye kusamehe, Mwenye mapenzi,'),
(5924, 85, 15, 'Mwenye Kiti cha Enzi, Mtukufu,'),
(5925, 85, 16, 'Atendaye ayatakayo.'),
(5926, 85, 17, 'Je! Zimekuwasilia khabari za majeshi?'),
(5927, 85, 18, 'Ya Firauni na Thamudi?'),
(5928, 85, 19, 'Lakini walio kufuru wamo katika kukadhibisha.'),
(5929, 85, 20, 'Na Mwenyezi Mungu nyuma yao amewazunguka.'),
(5930, 85, 21, 'Bali hii ni Qur\'ani tukufu'),
(5931, 85, 22, 'Katika Ubao Ulio Hifadhiwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Naapa kwa mbingu na Kinacho kuja usiku!'),
(5933, 86, 2, 'Na nini kitakacho kujuulisha ni nini hicho Kinacho kuja usiku?'),
(5934, 86, 3, 'Ni Nyota yenye mwanga mkali.'),
(5935, 86, 4, 'Hapana nafsi ila inayo mwangalizi.'),
(5936, 86, 5, 'Hebu naajitazame mwanaadamu ameumbwa kwa kitu gani?'),
(5937, 86, 6, 'Ameumbwa kwa maji yatokayo kwa kuchupa,'),
(5938, 86, 7, 'Yatokayo baina ya mifupa ya mgongo na mbavu.'),
(5939, 86, 8, 'Hakika Yeye ana uweza wa kumrudisha.'),
(5940, 86, 9, 'Siku zitakapo dhihirishwa siri.'),
(5941, 86, 10, 'Basi hatakuwa na nguvu wala msaidizi.'),
(5942, 86, 11, 'Naapa kwa mbingu yenye marejeo!'),
(5943, 86, 12, 'Na kwa ardhi inayo pasuka!'),
(5944, 86, 13, 'Hakika hii ni kauli ya kupambanua.'),
(5945, 86, 14, 'Wala si mzaha.'),
(5946, 86, 15, 'Hakika wao wanapanga mpango.'),
(5947, 86, 16, 'Na Mimi napanga mpango.'),
(5948, 86, 17, 'Basi wape muhula makafiri - wape muhula pole pole.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Litakase jina la Mola wako Mlezi aliye juu kabisa,'),
(5950, 87, 2, 'Aliye umba, na akaweka sawa,'),
(5951, 87, 3, 'Na ambaye amekadiria na akaongoa,'),
(5952, 87, 4, 'Na aliye otesha malisho,'),
(5953, 87, 5, 'Kisha akayafanya makavu, meusi.'),
(5954, 87, 6, 'Tutakusomesha wala hutasahau,'),
(5955, 87, 7, 'Ila akipenda Mwenyezi Mungu. Hakika Yeye anayajua yaliyo dhaahiri na yaliyo fichikana.'),
(5956, 87, 8, 'Na tutakusahilishia yawe mepesi.'),
(5957, 87, 9, 'Basi kumbusha, kama kukumbusha kunafaa.'),
(5958, 87, 10, 'Atakumbuka mwenye kuogopa.'),
(5959, 87, 11, 'Na atajitenga mbali nayo mpotovu,'),
(5960, 87, 12, 'Ambaye atauingia Moto mkubwa.'),
(5961, 87, 13, 'Tena humo hatakufa wala hawi hai.'),
(5962, 87, 14, 'Hakika amekwisha fanikiwa aliye jitakasa.'),
(5963, 87, 15, 'Na akakumbuka jina la Mola wake Mlezi, na akasali.'),
(5964, 87, 16, 'Lakini nyinyi mnakhiari maisha ya dunia!'),
(5965, 87, 17, 'Na Akhera ni bora na yenye kudumu zaidi.'),
(5966, 87, 18, 'Hakika haya yamo katika Vitabu vya mwanzo,'),
(5967, 87, 19, 'Vitabu vya Ibrahimu na Musa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Je! Imekufikia khabari ya msiba wa kufudikiza?'),
(5969, 88, 2, 'Siku hiyo nyuso zitainama,'),
(5970, 88, 3, 'Zikifanya kazi, nazo taabani.'),
(5971, 88, 4, 'Ziingie katika Moto unao waka -'),
(5972, 88, 5, 'Zikinyweshwa kutoka chemchem inayo chemka.'),
(5973, 88, 6, 'Hawatakuwa na chakula isipo kuwa kichungu chenye miba.'),
(5974, 88, 7, 'Hakinenepeshi wala hakiondoi njaa.'),
(5975, 88, 8, 'Siku hiyo nyuso nyengine zitakuwa kunjufu.'),
(5976, 88, 9, 'Zitakuwa radhi kwa juhudi yao,'),
(5977, 88, 10, 'Katika Bustani ya juu.'),
(5978, 88, 11, 'Hawatasikia humo upuuzi.'),
(5979, 88, 12, 'Humo imo chemchem inayo miminika.'),
(5980, 88, 13, 'Humo vimo viti vilivyo nyanyuliwa,'),
(5981, 88, 14, 'Na bilauri zilizo pangwa,'),
(5982, 88, 15, 'Na matakia safu safu,'),
(5983, 88, 16, 'Na mazulia yaliyo tandikwa.'),
(5984, 88, 17, 'Je! Hawamtazami ngamia jinsi alivyo umbwa?'),
(5985, 88, 18, 'Na mbingu jinsi ilivyo inuliwa?'),
(5986, 88, 19, 'Na milima jinsi ilivyo thibitishwa?'),
(5987, 88, 20, 'Na ardhi jinsi ilivyo tandazwa?'),
(5988, 88, 21, 'Basi kumbusha! Hakika wewe ni Mkumbushaji.'),
(5989, 88, 22, 'Wewe si mwenye kuwatawalia.'),
(5990, 88, 23, 'Lakini anaye rudi nyuma na kukataa,'),
(5991, 88, 24, 'Basi Mwenyezi Mungu atamuadhibu kwa adhabu iliyo kubwa kabisa!'),
(5992, 88, 25, 'Hakika ni kwetu Sisi ndio marejeo yao.'),
(5993, 88, 26, 'Kisha hakika ni juu yetu Sisi hisabu yao!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Naapa kwa alfajiri,'),
(5995, 89, 2, 'Na kwa masiku kumi,'),
(5996, 89, 3, 'Na kwa viwili viwili na kimoja kimoja,'),
(5997, 89, 4, 'Na kwa usiku unapo pita,'),
(5998, 89, 5, 'Je! Hapana katika haya kiapo kwa mwenye akili?'),
(5999, 89, 6, 'Kwani hukuona jinsi Mola wako Mlezi alivyo wafanya kina A\'di?'),
(6000, 89, 7, 'Wa Iram, wenye majumba marefu?'),
(6001, 89, 8, 'Ambao haukuumbwa mfano wake katika nchi?'),
(6002, 89, 9, 'Na Thamudi walio chonga majabali huko bondeni?'),
(6003, 89, 10, 'Na Firauni mwenye vigingi?'),
(6004, 89, 11, 'Ambao walifanya jeuri katika nchi?'),
(6005, 89, 12, 'Wakakithirisha humo ufisadi?'),
(6006, 89, 13, 'Basi Mola wako Mlezi aliwapiga mjeledi wa adhabu.'),
(6007, 89, 14, 'Hakika Mola wako Mlezi yupo kwenye mavizio anawavizia.'),
(6008, 89, 15, 'Ama mtu anapo jaribiwa na Mola wake Mlezi, akamkirimu na akamneemesha, husema: Mola wangu Mlezi amenikirimu!'),
(6009, 89, 16, 'Na ama anapo mjaribu akampunguzia riziki yake, husema: Mola wangu Mlezi amenitia unyonge!'),
(6010, 89, 17, 'Sivyo hivyo! Bali nyinyi hamuwakirimu mayatima,'),
(6011, 89, 18, 'Wala hamhimizani kulisha masikini;'),
(6012, 89, 19, 'Na mnakula urithi kwa ulaji wa pupa,'),
(6013, 89, 20, 'Na mnapenda mali pendo la kupita kiasi.'),
(6014, 89, 21, 'Sivyo hivyo! Itakapo vunjwa ardhi vipande vipande,'),
(6015, 89, 22, 'Na akaja Mola wako Mlezi na Malaika safu safu,'),
(6016, 89, 23, 'Na ikaletwa Jahannamu siku hiyo, siku hiyo mtu atakumbuka! Lakini kukumbuka huko kutamfaa nini?'),
(6017, 89, 24, 'Atasema: Laiti ningeli jitangulizia kwa uhai wangu!'),
(6018, 89, 25, 'Basi siku hiyo hataadhibu yeyote namna ya kuadhibu kwake.'),
(6019, 89, 26, 'Wala hatafunga yeyote kama kufunga kwake.'),
(6020, 89, 27, 'Ewe nafsi iliyo tua!'),
(6021, 89, 28, 'Rejea kwa Mola wako Mlezi umeridhika, na umemridhisha.'),
(6022, 89, 29, 'Basi ingia miongoni mwa waja wangu,'),
(6023, 89, 30, 'Na ingia katika Pepo yangu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Naapa kwa Mji huu!'),
(6025, 90, 2, 'Nawe unaukaa Mji huu.'),
(6026, 90, 3, 'Na naapa kwa mzazi na alicho kizaa.'),
(6027, 90, 4, 'Hakika tumemuumba mtu katika taabu.'),
(6028, 90, 5, 'Ati anadhani hapana yeyote ataye muweza?'),
(6029, 90, 6, 'Anasema: Nimeteketeza chungu ya mali.'),
(6030, 90, 7, 'Ati anadhani ya kuwa hapana yeyote anaye mwona?'),
(6031, 90, 8, 'Kwani hatukumpa macho mawili?'),
(6032, 90, 9, 'Na ulimi, na midomo miwili?'),
(6033, 90, 10, 'Na tukambainishia zote njia mbili?'),
(6034, 90, 11, 'Lakini hakujitoma kwenye njia ya vikwazo vya milimani.'),
(6035, 90, 12, 'Na nini kitakujuvya ni nini njia ya vikwazo vya milimani?'),
(6036, 90, 13, 'Kumkomboa mtumwa;'),
(6037, 90, 14, 'Au kumlisha siku ya njaa'),
(6038, 90, 15, 'Yatima aliye jamaa,'),
(6039, 90, 16, 'Au masikini aliye vumbini.'),
(6040, 90, 17, 'Tena awe miongoni mwa walio amini na wakausiana kusubiri na wakausiana kuhurumiana.'),
(6041, 90, 18, 'Hao ndio watu wa kheri wa kuliani.'),
(6042, 90, 19, 'Lakini walio zikataa Ishara zetu, hao ndio watu wa shari wa kushotoni.'),
(6043, 90, 20, 'Juu yao utakuwa Moto ulio fungiwa kila upande.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Naapa kwa jua na mwangaza wake!'),
(6045, 91, 2, 'Na kwa mwezi unapo lifuatia!'),
(6046, 91, 3, 'Na kwa mchana unapo lidhihirisha!'),
(6047, 91, 4, 'Na kwa usiku unapo lifunika!'),
(6048, 91, 5, 'Na kwa mbingu na kwa aliye ijenga!'),
(6049, 91, 6, 'Na kwa ardhi na kwa aliye itandaza!'),
(6050, 91, 7, 'Na kwa nafsi na kwa aliye itengeneza!'),
(6051, 91, 8, 'Kisha akaifahamisha uovu wake na wema wake,'),
(6052, 91, 9, 'Hakika amefanikiwa aliye itakasa,'),
(6053, 91, 10, 'Na hakika amekhasiri aliye iviza.'),
(6054, 91, 11, 'Kina Thamudi walikadhibisha kwa sababu ya upotofu wao,'),
(6055, 91, 12, 'Alipo simama mwovu wao mkubwa,'),
(6056, 91, 13, 'Hapo Mtume wa Mwenyezi Mungu alipo waambia: Huyu ni ngamia wa Mwenyezi Mungu, mwacheni anywe maji fungu lake.'),
(6057, 91, 14, 'Lakini walimkadhibisha na wakamchinja ngamia, kwa hivyo Mola wao Mlezi aliwaangamiza kwa sababu ya dhambi zao na akawafuta kabisa.'),
(6058, 91, 15, 'Wala Yeye haogopi matokeo yake.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Naapa kwa usiku unapo funika!'),
(6060, 92, 2, 'Na mchana unapo dhihiri!'),
(6061, 92, 3, 'Na kwa Aliye umba dume na jike!'),
(6062, 92, 4, 'Hakika juhudi zenu bila ya shaka ni mbali mbali.'),
(6063, 92, 5, 'Ama mwenye kutoa na akamchamngu,'),
(6064, 92, 6, 'Na akaliwafiki lilio jema,'),
(6065, 92, 7, 'Tutamsahilishia yawe mepesi.'),
(6066, 92, 8, 'Na ama mwenye kufanya ubakhili, na asiwe na haja ya wenzake,'),
(6067, 92, 9, 'Na akakanusha lilio jema,'),
(6068, 92, 10, 'Tutamsahilishia yawe mazito!'),
(6069, 92, 11, 'Na mali yake yatamfaa nini atapo kuwa anadidimia?'),
(6070, 92, 12, 'Hakika ni juu yetu kuonyesha uwongofu.'),
(6071, 92, 13, 'Na hakika ni yetu Sisi Akhera na dunia.'),
(6072, 92, 14, 'Basi nakuonyeni na Moto unao waka!'),
(6073, 92, 15, 'Hatauingia ila mwovu kabisa!'),
(6074, 92, 16, 'Anaye kadhibisha na kupa mgongo.'),
(6075, 92, 17, 'Na mchamngu ataepushwa nao,'),
(6076, 92, 18, 'Ambaye hutoa mali yake kwa ajili ya kujitakasa.'),
(6077, 92, 19, 'Na wala si kwa kuwa yupo yeyote aliye mfanyia hisani ndio anamlipa.'),
(6078, 92, 20, 'Ila ni kutaka radhi ya Mola wake Mlezi aliye juu kabisa.'),
(6079, 92, 21, 'Naye atakuja ridhika!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Naapa kwa mchana!'),
(6081, 93, 2, 'Na kwa usiku unapo tanda!'),
(6082, 93, 3, 'Mola wako Mlezi hakukuacha, wala hakukasirika nawe.'),
(6083, 93, 4, 'Na bila ya shaka wakati ujao utakuwa bora kwako kuliko ulio tangulia.'),
(6084, 93, 5, 'Na Mola wako Mlezi atakupa mpaka uridhike.'),
(6085, 93, 6, 'Kwani hakukukuta yatima akakupa makaazi?'),
(6086, 93, 7, 'Na akakukuta umepotea akakuongoa?'),
(6087, 93, 8, 'Akakukuta mhitaji akakutosheleza?'),
(6088, 93, 9, 'Basi yatima usimwonee!'),
(6089, 93, 10, 'Na anaye omba au kuuliza usimkaripie!'),
(6090, 93, 11, 'Na neema za Mola wako Mlezi zisimlie.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Hatukukunjulia kifua chako?'),
(6092, 94, 2, 'Na tukakuondolea mzigo wako,'),
(6093, 94, 3, 'Ulio vunja mgongo wako?'),
(6094, 94, 4, 'Na tukakunyanyulia utajo wako?'),
(6095, 94, 5, 'Basi kwa hakika pamoja na uzito upo wepesi,'),
(6096, 94, 6, 'Hakika pamoja na uzito upo wepesi.'),
(6097, 94, 7, 'Na ukipata faragha, fanya juhudi.'),
(6098, 94, 8, 'Na Mola wako Mlezi ndio mshughulikie.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Naapa kwa tini na zaituni!'),
(6100, 95, 2, 'Na kwa Mlima wa Sinai!'),
(6101, 95, 3, 'Na kwa mji huu wenye amani!'),
(6102, 95, 4, 'Bila ya shaka tumemuumba mtu kwa umbo lilio bora kabisa.'),
(6103, 95, 5, 'Kisha tukamrudisha kuwa chini kuliko walio chini!'),
(6104, 95, 6, 'Lakini wale walio amini na wakatenda mema, hao watapata ujira usio kwisha.'),
(6105, 95, 7, 'Basi ni kipi baadaye kitacho kukukadhibishia malipo?'),
(6106, 95, 8, 'Kwani Mwenyezi Mungu si muadilifu kuliko mahakimu wote?');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Soma kwa jina la Mola wako Mlezi aliye umba,'),
(6108, 96, 2, 'Amemuumba binaadamu kwa tone la damu,'),
(6109, 96, 3, 'Soma! Na Mola wako Mlezi ni Karimu kushinda wote!'),
(6110, 96, 4, 'Ambaye amefundisha kwa kalamu.'),
(6111, 96, 5, 'Kamfundisha mtu aliyo kuwa hayajui.'),
(6112, 96, 6, 'Kwani! Hakika mtu bila ya shaka huwa jeuri'),
(6113, 96, 7, 'Akijiona katajirika.'),
(6114, 96, 8, 'Hakika kwa Mola wako Mlezi ndio marejeo.'),
(6115, 96, 9, 'Umemwona yule anaye mkataza'),
(6116, 96, 10, 'Mja anapo sali?'),
(6117, 96, 11, 'Umeona kama yeye yuko juu ya uwongofu?'),
(6118, 96, 12, 'Au anaamrisha uchamngu?'),
(6119, 96, 13, 'Umeona kama yeye akikanusha na anarudi nyuma?'),
(6120, 96, 14, 'Hajui ya kwamba Mwenyezi Mungu anaona?'),
(6121, 96, 15, 'Kwani! Kama haachi, tutamkokota kwa shungi la nywele!'),
(6122, 96, 16, 'Shungi la uwongo, lenye makosa!'),
(6123, 96, 17, 'Basi na awaite wenzake!'),
(6124, 96, 18, 'Nasi tutawaita Mazabania!'),
(6125, 96, 19, 'Hasha! Usimt\'ii! Nawe sujudu na ujongee!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Hakika Sisi tumeiteremsha Qur\'ani katika Laylatul Qadri, Usiku wa Cheo Kitukufu.'),
(6127, 97, 2, 'Na nini kitacho kujuulisha nini Laylatul Qadri?'),
(6128, 97, 3, 'Laylatul Qadri ni bora kuliko miezi elfu.'),
(6129, 97, 4, 'Huteremka Malaika na Roho katika usiku huo kwa idhini ya Mola wao Mlezi kwa kila jambo.'),
(6130, 97, 5, 'Amani usiku huo mpaka mapambazuko ya alfajiri.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Hawakuwa walio kufuru miongoni mwa Watu wa Kitabu na washirikina waache walio nayo mpaka iwajie bayana,'),
(6132, 98, 2, 'Yaani Mtume aliye toka kwa Mwenyezi Mungu anaye wasomea kurasa zilizo takasika,'),
(6133, 98, 3, 'Ndani yake mna maandiko yaliyo nyooka.'),
(6134, 98, 4, 'Wala hawakufarikiana walio pewa Kitabu ila baada ya kuwajia hiyo bayana.'),
(6135, 98, 5, 'Nao hawakuamrishwa kitu ila wamuabudu Mwenyezi Mungu kwa kumtakasia Dini, wawe waongofu, na washike Sala, na watoe Zaka. Na hiyo ndiyo Dini madhubuti.'),
(6136, 98, 6, 'Hakika walio kufuru miongoni mwa Watu wa Kitabu na washirikina wataingia katika Moto wa Jahannamu wadumu milele humo. Hao ndio viumbe waovu kabisa.'),
(6137, 98, 7, 'Hakika walio amini na wakatenda mema, hao ndio bora wa viumbe.'),
(6138, 98, 8, 'Malipo yao kwa Mola wao Mlezi ni Bustani za daima, zipitazo mito kati yake. Wakae humo milele. Mwenyezi Mungu yu radhi nao, na wao waradhi naye. Hayo ni kwa anaye mwogopa Mola wake Mlezi.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Itakapo tetemeshwa ardhi kwa mtetemeko wake!'),
(6140, 99, 2, 'Na itakapo toa ardhi mizigo yake!'),
(6141, 99, 3, 'Na mtu akasema: Ina nini?'),
(6142, 99, 4, 'Siku hiyo itahadithia khabari zake.'),
(6143, 99, 5, 'Kwa sababu Mola wake Mlezi ameifunulia!'),
(6144, 99, 6, 'Siku hiyo watu watatoka kwa mfarakano wakaonyweshwe vitendo vyao!'),
(6145, 99, 7, 'Basi anaye tenda chembe ya wema, atauona!'),
(6146, 99, 8, 'Na anaye tenda chembe ya uovu atauona!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Naapa kwa farasi wendao mbio wakipumua,'),
(6148, 100, 2, 'Na wakitoa moto kwa kupiga kwato zao chini,'),
(6149, 100, 3, 'Wakishambulia wakati wa asubuhi,'),
(6150, 100, 4, 'Huku wakitimua vumbi,'),
(6151, 100, 5, 'Na wakijitoma kati ya kundi,'),
(6152, 100, 6, 'Hakika mwanaadamu ni mtovu wa fadhila kwa Mola wake Mlezi!'),
(6153, 100, 7, 'Na hakika yeye mwenyewe bila ya shaka ni shahidi wa hayo!'),
(6154, 100, 8, 'Naye hakika bila ya shaka ana nguvu za kupenda mali!'),
(6155, 100, 9, 'Kwani hajui watakapo fufuliwa waliomo makaburini?'),
(6156, 100, 10, 'Na yakakusanywa yaliomo vifuani?'),
(6157, 100, 11, 'Kuwa hakika Mola wao Mlezi siku hiyo bila ya shaka atakuwa na khabari zao wote!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Inayo gonga!'),
(6159, 101, 2, 'Nini Inayo gonga?'),
(6160, 101, 3, 'Na nini kitacho kujuilisha nini Inayo gonga?'),
(6161, 101, 4, 'Siku ambayo watu watakuwa kama nondo walio tawanyika;'),
(6162, 101, 5, 'Na milima itakuwa kama sufi zilizo chambuliwa!'),
(6163, 101, 6, 'Basi yule ambaye mizani yake itakuwa nzito,'),
(6164, 101, 7, 'Huyo atakuwa katika maisha ya kupendeza.'),
(6165, 101, 8, 'Na yule ambaye mizani yake itakuwa khafifu,'),
(6166, 101, 9, 'Huyo maskani yake yatakuwa Moto wa Hawiya!'),
(6167, 101, 10, 'Na nini kitacho kujuilisha nini hiyo?'),
(6168, 101, 11, 'Ni Moto mkali!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Kumekushughulisheni kutafuta wingi,'),
(6170, 102, 2, 'Mpaka mje makaburini!'),
(6171, 102, 3, 'Sivyo hivyo! Mtakuja jua!'),
(6172, 102, 4, 'Tena sivyo hivyo! Mtakuja jua!'),
(6173, 102, 5, 'Sivyo hivyo! Lau mngeli jua kwa ujuzi wa yakini,'),
(6174, 102, 6, 'Basi bila ya shaka mtaiona Jahannamu!'),
(6175, 102, 7, 'Tena, bila ya shaka, mtaiona kwa jicho la yakini.'),
(6176, 102, 8, 'Tena bila ya shaka mtaulizwa siku hiyo juu ya neema.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Naapa kwa Zama!'),
(6178, 103, 2, 'Hakika binaadamu bila ya shaka yumo katika khasara,'),
(6179, 103, 3, 'Ila wale walio amini, na wakatenda mema, na wakausiana kwa haki, na wakausiana kusubiri.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Ole wake kila safihi, msengenyaji!'),
(6181, 104, 2, 'Aliye kusanya mali na kuyahisabu.'),
(6182, 104, 3, 'Anadhani ya kuwa mali yake yatambakisha milele!'),
(6183, 104, 4, 'Hasha! Atavurumishwa katika H\'ut\'ama.'),
(6184, 104, 5, 'Na nani atakujuvya ni nini H\'ut\'ama?'),
(6185, 104, 6, 'Moto wa Mwenyezi Mungu ulio washwa.'),
(6186, 104, 7, 'Ambao unapanda nyoyoni.'),
(6187, 104, 8, 'Hakika huo utafungiwa nao'),
(6188, 104, 9, 'Kwenye nguzo zilio nyooshwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Kwani hukuona jinsi Mola wako Mlezi alivyo watenda wale wenye tembo?'),
(6190, 105, 2, 'Kwani hakujaalia vitimbi vyao kuharibika?'),
(6191, 105, 3, 'Na akawapelekea ndege makundi kwa makundi,'),
(6192, 105, 4, 'Wakiwatupia mawe ya udongo wa Motoni,'),
(6193, 105, 5, 'Akawafanya kama majani yaliyo liwa!');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Kwa walivyo zoea Maqureshi,'),
(6195, 106, 2, 'Kuzoea kwao safari za siku za baridi na siku za joto.'),
(6196, 106, 3, 'Basi nawamuabudu Mola Mlezi wa Nyumba hii,'),
(6197, 106, 4, 'Ambaye anawalisha wasipate njaa, na anawalinda na khofu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Je! Umemwona anaye kadhibisha Malipo?'),
(6199, 107, 2, 'Huyo ndiye anaye msukuma yatima,'),
(6200, 107, 3, 'Wala hahimizi kumlisha masikini.'),
(6201, 107, 4, 'Basi, ole wao wanao sali,'),
(6202, 107, 5, 'Ambao wanapuuza Sala zao;'),
(6203, 107, 6, 'Ambao wanajionyesha,'),
(6204, 107, 7, 'Nao huku wanazuia msaada.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Hakika tumekupa kheri nyingi.'),
(6206, 108, 2, 'Basi sali na uchinje kwa ajili ya Mola wako Mlezi.'),
(6207, 108, 3, 'Hakika anaye kuchukia ndiye aliye mpungufu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Sema: Enyi makafiri!'),
(6209, 109, 2, 'Siabudu mnacho kiabudu;'),
(6210, 109, 3, 'Wala nyinyi hamuabudu ninaye muabudu.'),
(6211, 109, 4, 'Wala sitaabudu mnacho abudu.'),
(6212, 109, 5, 'Wala nyinyi hamuabudu ninaye muabudu.'),
(6213, 109, 6, 'Nyinyi mna dini yenu, nami nina Dini yangu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Itakapo kuja nusura ya Mwenyezi Mungu na ushindi,'),
(6215, 110, 2, 'Na ukaona watu wanaingia katika Dini ya Mwenyezi Mungu kwa makundi,'),
(6216, 110, 3, 'Zitakase sifa za Mola wako Mlezi, na umwombe msamaha; hakika Yeye ndiye anaye pokea toba.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Imeangamia mikono ya Abu Lahab, na yeye pia ameangamia.'),
(6218, 111, 2, 'Hayatamfaa mali yake, wala alivyo vichuma.'),
(6219, 111, 3, 'Atauingia Moto wenye mwako.'),
(6220, 111, 4, 'Na mkewe, mchukuzi wa kuni,'),
(6221, 111, 5, 'Shingoni mwake iko kamba iliyo sokotwa.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Sema: Yeye Mwenyezi Mungu ni wa pekee.'),
(6223, 112, 2, 'Mwenyezi Mungu Mkusudiwa.'),
(6224, 112, 3, 'Hakuzaa wala hakuzaliwa.'),
(6225, 112, 4, 'Wala hana anaye fanana naye hata mmoja.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Sema: Najikinga kwa Mola Mlezi wa mapambazuko,'),
(6227, 113, 2, 'Na shari ya alivyo viumba,'),
(6228, 113, 3, 'Na shari ya giza la usiku liingiapo,'),
(6229, 113, 4, 'Na shari ya wanao pulizia mafundoni,'),
(6230, 113, 5, 'Na shari ya hasidi anapo husudu.');
INSERT INTO `sw_barwani` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Sema: Ninajikinga kwa Mola Mlezi wa wanaadamu,'),
(6232, 114, 2, 'Mfalme wa wanaadamu,'),
(6233, 114, 3, 'Mungu wa wanaadamu,'),
(6234, 114, 4, 'Na shari ya wasiwasi wa Shetani, Khannas,'),
(6235, 114, 5, 'Anaye tia wasiwasi katika vifua vya watu,'),
(6236, 114, 6, 'Kutokana na majini na wanaadamu.');
