INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Ба номи Худованди бахшояндаи меҳрубон!'),
(2, 1, 2, 'Ситоиш Худоро, ки Парвардигори ҷаҳониён аст.'),
(3, 1, 3, 'Бахшояндаи меҳрубон,'),
(4, 1, 4, 'Фармонравои рӯзи ҷазо.'),
(5, 1, 5, 'Танҳо Туро мепарастем ва танҳо аз Ту ёри меҷӯем.'),
(6, 1, 6, 'Моро ба роҳи рост ҳидоят кун:'),
(7, 1, 7, 'роҳи касоне, ки онҳоро неъмат додаи, на хашмгирифтагонӣ бар онҳо ва на гумроҳон.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Алиф. Лом. Мим'),
(9, 2, 2, 'Ин аст ҳамон китобе, ки дар он ҳеҷ шакке нест. Парҳезгоронро роҳнамост:'),
(10, 2, 3, '«онон, ки ба ғайб имон меоваранд ва намоз мегузоранд ва аз он чи рӯзияшон додаем, инфоқ (харҷ) мекунанд».'),
(11, 2, 4, 'ва онон, ки ба он чи бар ту ва бар паёмбарони пеш аз ту нозил шудааст, имон меоваранд ва ба охират яқин доранд.'),
(12, 2, 5, 'Эшон аз сӯи Парвардигорашон қарини ҳидоятанд ва худ наҷот ёфтагонанд.'),
(13, 2, 6, 'Кофиронро Хоҳ битарсони ё натарсони, фарқашон накунад, имон намеоваранд.'),
(14, 2, 7, 'Худо бар дилҳояшон ва бар гушашон муҳр ниҳода ва бар рӯи чашмонашон пардаест ва барояшон азобест бузург.'),
(15, 2, 8, 'Баъзе аз мардум мегӯянд: «Ба Худо ва рӯзи қиёмат имон овардаем». Ҳолл он ки имон наовардаанд.'),
(16, 2, 9, 'Инон Худову мӯъминонро мефиребанд ва намедонанд, ки танҳо худро фиреб медиҳанд.'),
(17, 2, 10, 'Дар дилҳояшон маразест ва Худо низ бар маразашон бияфзудааст ва ба ҷазои дуруғе, ки гуфтаанд, барояшон азобест дардовар.'),
(18, 2, 11, 'Чун ба онҳо гуфта шавад, ки дар замин фасод накунед, мегӯянд: «Мо муслеҳонем» (некӯкоронем).'),
(19, 2, 12, 'Огоҳ бошед, ки инҳо худ фасодкоронанду намедонанд.'),
(20, 2, 13, 'Ва чун ба онон гуфта шавад, ки шумо низ ҳамонанди дигар мардумон имон биёваред, мегӯянд: «Оё мо низ ҳамонанди бехирадон имон биёварем?» Огоҳ бошед, ки онон худ бехирадонанду намедонанд.'),
(21, 2, 14, 'Ва чун ба мӯъминон мерасанд, мегӯянд: «Имон овардем». Ва чун бо шайтонҳои хеш хилват мекунанд, мегӯянд: «Мо бо шумо ҳастем, мо масқараашон мекунем».'),
(22, 2, 15, 'Худост ки онҳоро масхара мекунад ва онҳоро вомегузорад то ҳамчунон дар туғёни хеш саргардон бимонанд.'),
(23, 2, 16, 'Инҳо гумроҳиро ба ҳидоят хариданд, пас тоҷираташон суд накард ва дар шумори ҳидоятёфтагон дарнаёмадаанд.'),
(24, 2, 17, 'Мисолашон мисли он касест, ки оташе афрӯхт, чун атрофашро равшан сохт, Худо рӯшнои аз онон бозгирифт ва нобино дар торики раҳояшон кард.'),
(25, 2, 18, 'Каронанд, гунгонанд ва бознамегарданд!'),
(26, 2, 19, 'Ё чун бороне сахт дар зулмат ҳамроҳ бо раъду барқ аз осмон фуруд ояд, то мабод, ки аз бонги раъд бимиранд, ангуштони хеш дар гушҳо кунанд. Ва Худо бар кофирон иҳота дорад. (бо илми худ).'),
(27, 2, 20, 'Наздик бошад, ки барқ дидагонашонро нобино созад. Хар гоҳ равшан шавад, чанд қадаме бармедоранд ва чун хомуш шавад, аз рафтан бозистанд. Агар Худо мехост, гушҳояшонро кару чашмонашонро кур месохт, ки ӯ ба ҳар коре тавоност!'),
(28, 2, 21, 'Эй мардум, Парвардигоратонро, ки шумо ва пешиниёнатонро биёфаридааст, бипарастед. Бошад ки парҳезгор шавед!'),
(29, 2, 22, 'Он Худованде, ки заминро чун гилеме бигустурд ва осмонро чун биное бияфрохт ва аз осмон обе фиристод ва бо он об барои рӯзии шумо аз замин ҳар гуна самарае бируёнид ва худ медонед, ки набояд барои Худо шариконе қарор диҳед!'),
(30, 2, 23, 'Ва агар дар он чӣ бар бандаи хеш нозил кардаем дар шак ҳастед, сурае ҳамонанди он биёваред ва ғайра Худо ҳамаи ҳозиронатонро фаро хонед агар рост мегӯед.'),
(31, 2, 24, 'Ва ҳар гоҳ чунин накардаед, ки ҳаргиз натавонед кард, пас битарсед аз оташе, ки барои кофирон муҳайё шуда ҳезуми он мардумон ва сангҳо ҳастанд.'),
(32, 2, 25, 'Ба онон, ки имон овардаанд ва корҳои шоиста кардаанд, мужда деҳ, ки барояшон биҳиштҳоест, ки дар он наҳрҳо ҷорист. Ва ҳар гоҳ, ки аз меваҳои он бархурдор шаванд, гӯянд: «Пеш аз ин дар дунё аз чунин меваҳое бархурдор шуда будем, ки ин меваҳо монанд ба якдигаранд». Ва низ дар он ҷо ҳамсароне покиза доранд ва дар он ҷо ҷовидона бошанд.'),
(33, 2, 26, 'Худо ибое надорад, ки ба пашша ва камтар аз он мисол биёрад. Онон, ки имон овардаанд, медонанд, ки он мисол дуруст ва аз ҷониби Парвардигори онҳост. Ва аммо кофирон мегӯянд, ки Худо аз ин мисол чи мехостааст? Бисёреро ба он гумроҳ мекунад ва бисёреро ҳидоят. Аммо танҳо фосиқонро гумроҳ мекунад.'),
(34, 2, 27, 'Касоне, ки паймони Худоро пас аз бастани он мешикананд ва он чиро, ки Худо ба пайвастани он фармон дода, мебуранд ва дар замин фасод мекунанд зиёнкоронанд.'),
(35, 2, 28, 'Чӣ гуна Худоро инкор мекунед, дар ҳоле ки мурда будед ва ӯ шуморо зинда сохт, боз мемиронад ва зинда мекунад ва он гоҳ ба назди Ӯ бозмегардед.'),
(36, 2, 29, 'Ӯст, ки ҳамаи чизҳоеро, ки дар рӯи замин аст, бароятон биёфарид, он гоҳ ба осмон пардохт ва ҳар ҳафт осмонро барафрошт ва Ӯ аз ҳар чизе огоҳ аст!'),
(37, 2, 30, 'Ва чун Парвардигорат ба фариштагон гуфт: «Ман дар замин халифае меофаринам», гуфтанд: «Оё касеро меофарини, ки дар он ҷо фасод кунад ва хунҳо бирезад ва ҳол он ки мо ба ситоиши Ту тасбеҳ мегӯем ва Туро ба поки иқрор мекунем?». Гуфт: «Ман он донам, ки шумо намедонед».'),
(38, 2, 31, 'Ва ноҳоро ба тамомӣ ба Одам биёмухт. Сипас онҳоро ба фариштагон арза кард. Ва гуфт: «Агар рост мегӯед, Маро ба номҳои инҳо хабар диҳед».'),
(39, 2, 32, 'Гуфтанд: «Муназзаҳӣ (покӣ) Ту. Моро ҷуз он чӣ Худ ба мо омӯхтаӣ, донише нест. Тӯӣ донои ҳаким!»'),
(40, 2, 33, 'Гуфт: «Эй Одам, онҳоро аз номҳояшон огоҳ кун!» Чун аз он номҳо огаҳашон кард, Худо гуфт: «Оё ба шумо нагуфтам, ки манн ниҳони осмонҳову заминро медонам ва бар он чӣ ошкор мекунед ва пинҳон медоштед, огаҳам?».'),
(41, 2, 34, 'Ва ба фариштагон гуфтем: «Одамро саҷда кунед!» Ҳама саҷда карданд ҷуз Иблис, ки рӯй гардонд ва бартарӣ ҷуст. Ва ӯ аз кофирон буд.'),
(42, 2, 35, 'Ва гуфтем: «Эй Одам, худ ва занат дар биҳишт ҷой гиред. Ва ҳар чӣ хоҳед, ва ҳар ҷо, ки хоҳед, аз самароти он ба хушӣ бихӯред. Ва ба ин дарахт наздик машавед, ки ба гурӯҳи ситамкорон дароед».'),
(43, 2, 36, 'Пас шайтон он дуро ба хато водошт ва аз биҳиште, ки дар он буданд, берун ронд. Гуфтем: «Поин равед, баъзе душмани баъзеи дигар, ва кароргоҳу ҷои бархурдории шумо то рӯзи қиёмат дар замин бошад».'),
(44, 2, 37, 'Одам аз Парвардигораш калимае чанд таълим гирифт. Пас Худо тавбаи ӯро пазируфт, зеро тавбапазир ва меҳрубон аст!'),
(45, 2, 38, 'Гуфтем: «Ҳама аз биҳишт поён шавед: пас агар аз ҷониби Ман роҳнамоӣ бароятон омад, бар онҳо, ки аз роҳнамоии Ман пайравӣ кунанд, биме нахоҳад буд ва худ андӯҳнок намешаванд».'),
(46, 2, 39, 'Касоне, ки кофир шаванд ва оёти Худоро дуруғ бароранд, худ аҳли ҷаҳаннаманд ва ҷовидона дар он ҷо хоҳад буд.'),
(47, 2, 40, 'Эй банӣ-Исроил, неъматеро, ки ба шумо арзонӣ доштам, ба ёд биёваред. Ва ба аҳди Ман вафо кунед, то ба аҳдатон вафо кунам. Ва аз Ман битарсед!'),
(48, 2, 41, 'Ва ба он чӣ нозил кардаам ва китоби шуморо тасдиқ мекунад, имон биёваред ва аз нахустин касоне, ки инкораш мекунанд, мабошед. Ва оёти Маро ба баҳои андак нафурӯшед ва аз Ман битарсед!'),
(49, 2, 42, 'Ҳақро ба ботил маёмезед ва бо он ки ҳақиқатро медонед, пинҳонаш макунед!'),
(50, 2, 43, 'Ва намозро барпой доред ва закот бидиҳед ва бо рукӯъкунандагон рукӯъ кунед!'),
(51, 2, 44, 'Оё дар ҳоле ки китобро мехонед, мардумро ба некӣ фармон медиҳед ва худро фаромӯш мекунед? Оё ақлро кор намефармоед!'),
(52, 2, 45, 'Аз сабр ва намоз ёрӣ ҷӯед. Ва ин ду, коре душворанд, ҷуз барои аҳли хушӯъ (хоксорон, фурӯтанон).'),
(53, 2, 46, 'Онон, ки бегумон медонанд, ки бо Парвардигори худ дидор хоҳанд кард ва назди ӯ бозмегарданд.'),
(54, 2, 47, 'Эй банӣ - Исроил, неъматеро, ки бар шумо арзонӣ доштам ва шуморо бар ҷаҳониён бартарӣ додам, ба ёд биёваред.'),
(55, 2, 48, 'Ва битарсед аз рӯзе, ки ҳеҷ кас дигареро ба кор наёяд ва ҳеҷ шафоъате аз касе пазируфта нагардад ва аз касе ивазе наситонанд ва касеро ёрӣ накунанд.'),
(56, 2, 49, 'Ва ба ёд оред он гоҳ, ки шуморо аз фиръавниён раҳонидем: шуморо шиканҷаҳои сахт мекарданд, писаронатонро мекуштанд ва занонатонро зинда мегузоштанд. Ва дар ин озмуне бузург аз сӯи Парвардигоратон буд.'),
(57, 2, 50, 'Ва он ҳангомро, ки дарёро бароятон шикофтем ва шуморо раҳонидем ва фиръавниёнро дар баробари чашмонатон ғарқ сохтем.'),
(58, 2, 51, 'Ва он ҳангомро, ки чиҳил шаб бо Мӯсо ваъда ниҳодем ва шумо, ки ситамкорон будед, баъд аз ӯ гӯсоларо парастидед.'),
(59, 2, 52, 'Пас гуноҳонатонро афв кардем, шояд, ки сипосгузор бошед!'),
(60, 2, 53, 'Ва ба ёд оред он ҳангомро, ки ба Мӯсо китоб ва фурқон додем, бошад, ки ҳидоят шавед.'),
(61, 2, 54, 'Ва он ҳангомро, ки Мӯсо ба қавми худ гуфт: «Эй қавми ман, шумо ба он сабаб, ки гӯсоларо парастидед, бар худ ситам раво доштед: инак ба даргоҳи Офаридгоратон тавба куне два якдигарро бикушед, ки чунин коре дар назди Офаридгоратон беҳтар аст». Пас Худо тавбаи шуморо пазируфт, зеро тавбапазир ва меҳрубон аст.'),
(62, 2, 55, 'Ва он ҳамгонро, ки гуфтед: «Эй Мӯсо, мо то Худоро ба ошкор набинем, ба ту имон намеоварем». Ва ҳамчунон ки менигаристед, соӣиқа (оташак, барқ) шуморо фурӯ гирифт.'),
(63, 2, 56, 'Ва шуморо пас аз мурдан зинда сохтем, шояд сипосгузор шавед!'),
(64, 2, 57, 'Ва абрро соябонатон гардонидем ва бароятон манну салво фиристодем: «Бихӯред аз ин чизҳои покиза, ки шуморо рӯзӣ додаем! Ва онон бар Мо ситам накарданд, балки бар худ ситам мекарданд».'),
(65, 2, 58, 'Ва ба ёд оред он замонро, ки ба шумо гуфтем: «Ба ин қария дароед ва аз неъматҳои он ҳар чӣ ва ҳар ҷо, ки хоста бошед, ба фаровонӣ бихӯред! Вале саҷдакунон аз дарвоза дохил шавед ва бигӯед: «Боре гуноҳ аз мо кам кун». То хатоҳои шуморо биёмурзем ва ба подоши некӯкорон бияфзоем.'),
(66, 2, 59, 'Аммо ситамкорон он суханро дигар карданд ва бар онон ба ҷазои гуноҳе, ки карда буданд, азобе осмонӣ фуруд овардем.'),
(67, 2, 60, 'Ва ба ёд оред он гоҳро, ки Мӯсо барои қавми худ об хост. Гуфтем: «Асоятро бар он санг бизан». Пас дувоздаҳ чашма аз он баромад. Ҳар гурӯҳе маҳалли обнӯшии худро бидонист. Аз рӯзии Худо бихӯреду биёшомед ва дар рӯи замин ба фасод саркашӣ макунед!'),
(68, 2, 61, 'Ва он гоҳро, ки гуфтед: «Эй Мӯсо, мо бар як навъи таъом натавонем сабр кард, аз Парвардигорат бихоҳ то барои мо аз он чӣ аз замин мерӯяд, чун сабзӣ, бодирингу сир ва наску пиёз бируёнад». Мӯсо гуфт: «Оё мехоҳед он чиро, ки беҳтар аст ба он чӣ пасттар аст, иваз кунед? Ба шаҳре бозгардед, ки дар он ҷо ҳар чӣ хоҳед, ба шумо бидиҳанд. Муқаррар шуд бар онҳо хориву бечорагӣ ва бо хашми Худо қарин шуданд! Ва ин бад-он сабаб буд, ки ба оёти Худо кофир шуданд ва паёмбаронро ба ноҳақ куштанд ва нофармонӣ карданд ва таҷовуз намуданд».'),
(69, 2, 62, 'Касоне, ки имон овардаанд ва касоне, ки дини яҳудон ва тарсоён ва собионро гирифтанд, агар ба Худо ва рӯзи бозпасин имон дошта бошанд ва коре шоиста кунанд, Худо ба онҳо ҷазои нек медиҳад ва на бимнок мешаванд ва на ғамгин.'),
(70, 2, 63, 'Ва ба ёд оред он замонро, ки бо шумо паймон бастем ва кӯҳи Турро бар болои саратон бидоштем. Он чиро, ки ба шумо додаем, мустаҳкам бигиред ва он чиро, ки дар он аст, ба хотир бидоред! Бошад, ки парҳезгор шавед!'),
(71, 2, 64, 'Вале з-он пас аз фармон Сар тобидед ва агар фазлу раҳмати Худо набуд, аз зиёнкорон мебудед.'),
(72, 2, 65, 'Ва шинохтед он гурӯҳро, ки дар он рӯзи шанбе аз ҳадди худ таҷовуз карданд, пас ба онҳо хитоб кардем: «Бӯзинагоне хору хомӯш гардед!».'),
(73, 2, 66, 'Ва онҳоро ибрати муосирон ва ояндагон ва панде барои парҳезгорон гардонидем.'),
(74, 2, 67, 'Ва ба ёд оред он ҳангомро, ки Мӯсо ба қавми худ гуфт: «Худо фармон медиҳад, ки говеро бикушед!» Гуфтанд: «Оё Моро маскара мекунӣ?» Гуфт: «Ба Худо паноҳ мебарам ки аз нодонон бошам».'),
(75, 2, 68, 'Гуфтанд: «Барои мо Парвардигоратро бихон то баён кунад, ки он чӣ гуна говест?» Гуфт: «Мегӯяд: «Говест на сахт пиру аз кор афтода, на ҷавону корнокарда, миёнасол». Акнун бикунед он чӣ шуморо мефармоянд!'),
(76, 2, 69, 'Гуфтанд: «Барои мо Парвардигоратро бихон то бигӯяд, ки ранги он чист?» Гуфт: «Мегӯяд: «Говест зарди сахт, ки рангаш бинандагонро шодмон месозад».'),
(77, 2, 70, 'Гуфтанд: «Барои мо Парвардигоратро бихон то бигӯяд он чӣ гуна говест? Ки он гов бар мо муштабеҳ шудааст ва агар Худо бихоҳад мо, ба он роҳ меёбам».'),
(78, 2, 71, 'Гуфт: «Мегӯянд: «Аз он говон нест, ки ром бошад ва заминро ҷуфт кунад ва киштаро об диҳад. Беайб асту якранг». Гуфтанд: «Акнун ҳақиқатро гуфтӣ». Пас онро куштанд, ҳарчанд ки наздик буд, ки аз он кор рӯй гардонанд.'),
(79, 2, 72, 'Ва ба ёд оред он ҳангомро, ки касеро куштед ва бар якдигар бӯҳтон задед ва низоъ даргирифтед ва Худо он чиро, ки пинҳон мекардед, ошкор сохт.'),
(80, 2, 73, 'Сипас гуфтем: «Порае аз онро бар он кушта бизанед. Худо мурдагонро инчунин зинда месозад ва нишонаҳои қудрати хешро инчунин ба шумо менамоёнад, бошад, ки ба ақл дарёбед!».'),
(81, 2, 74, 'Пас аз он дилҳои шумо чун санг сахт гардид, ҳатто сахттар аз санг, ки аз санг гоҳ ҷӯйҳо равон шаванд ва чун шикофта шавад, об аз он берун ҷаҳад ва гоҳ аз тарси Худо аз боло ба нишеб фурӯ ғалтад ва Худо аз он чӣ мекунед, ғофил нест!'),
(82, 2, 75, 'Оё тамаъ медоред, ки ба шумо имон биёваранд ва ҳол он ки гурӯҳе аз онҳо каломи Худоро мешуниданд ва бо он ки ҳақиқати онро меёфтанд, тағйираш мекарданд ва аз кори хеш огоҳ буданд?'),
(83, 2, 76, 'Ва чун бо мӯъминон дидор кунанд, гӯянд: «Мо ҳам имон овардем». Ва чун бо якдигар хилват кунанд, гӯянд: «Оё бо онон аз донише, ки Худо ба шумо арзонӣ дошта сухан мегӯед, то ба ёрии он дар назди Парвардигоратон бар шумо ҳуҷҷат оранд? Оё ба ақл дарнамеёбед?».'),
(84, 2, 77, 'Оё намедонанд, ки ҳар чиро, ки пинҳон медоранд ва ҳар чиро, ки ошкор месозанд, Худо медонад?'),
(85, 2, 78, 'Баъзе аз онҳо бесаводоне ҳастанд, ки намедонанд дар китоб чист, ҷуз орзуҳои дурӯғ, ки шунидаанд. Инҳо танҳо пойбанди гумони худанд.'),
(86, 2, 79, 'Пас вой бар онҳое, ки китобро худ ба дасти худ менависанд ва то суде андак баранд, мегӯянд, ки аз ҷониби Худо нозил шуда. Пас вой бар онҳо аз он чӣ навиштанд ва вой бар онҳо аз суде, ки мебаранд!'),
(87, 2, 80, 'Ва гуфтанд: «Оташ ҷуз чанд рӯзе Моро насӯзонад». Бигӯ: «Оё бо Худо чунин паймоне бастаед, то ӯ хилофи паймони худ накунад? Ё он ки аз рӯи нодонӣ чунин нисбате ба Худо медиҳед?».'),
(88, 2, 81, 'Оре, Онон, ки муртакиби кори зишт шуданд ва гуноҳашон гирд бар гирдашон бигирифт, аҳли ҷаҳаннаманд ва ҷовидона дар он монанд.'),
(89, 2, 82, 'Ва онон, ки имон овардаанд ва корҳои шоиста кардаанд, аҳли биҳиштанд ва дар он ҷовидонанд.'),
(90, 2, 83, 'Ба ёд оред он ҳангомро, ки аз банӣ-Исроил паймон гирифтем, ки ғайри Худоро напарастед ва ба падару модару хешовандон ва ятимону дарвешон некӣ кунед ва ба мардумон сухани нек гӯед ва намоз бихонед ва закот бидиҳед! Вале ҷуз андаке аз шумо пушт кардед ва шумоед рӯйгардондагон.'),
(91, 2, 84, 'Ва он ҳангомро, ки бо шумо паймон ниҳодем, ки хуни ҳам нарезед ва якдигарро аз хонумон овора масозед ва шумо ба паймон гардан ниҳодед ва худ бар он гувоҳ ҳастед.'),
(92, 2, 85, 'Пас шумо чунин ҷамоъате ҳастед, ки якдигарро мекушед ва гурӯҳе аз худро аз хонумонашон овора мекунед ва бар зидди онҳо ба гуноҳ ва бедод ба ҳамдастии якдигар бармехезед ва агар ба асорати шумо дароянд, дар баробари озодияшон фидя мегиред ва ҳол он ки берун ронданашон бар шумо ҳаром буд. Оё ба баъзе аз китоб имон меоваред ва баъзе дигарро инкор мекунед? Ҷазои касе, ки чунин кунад, дар дунё ҷуз хорӣ нест ва дар рӯзи қиёмат ба сахттарин тарзе шиканҷа мешавад ва ба Худо аз он чӣ мекунед, ғофил нест!'),
(93, 2, 86, 'Инҳо ҳамон касонанд, ки охиратро донанд ва зиндагии дунёро хариданд. Аз азобашон кам нагардад ва кас ёриашон накунад.'),
(94, 2, 87, 'Ба таҳқиқ Мӯсоро китоб додем ва аз паи ӯ паёмбарон фиристодем. Ва ба Исо бинни Марям далелҳои равшан иноят кардем ва ӯро бо рӯҳулқудс қувват додем. Ва ҳар гоҳ паёмбаре омад ва чизҳое овард, ки писанди нафси шумо набуд, саркашӣ кардед ва гурӯҳеро дурӯғгӯ хондед ва гурӯҳеро куштед.'),
(95, 2, 88, 'Гуфтанд: «Дилҳои мо дар парда аст». На, Худо ононро ба сабаби куфре, ки меварзанд, нафрин кардааст ва чӣ андак имон меоваранд!'),
(96, 2, 89, 'Ва чун онҳоро аз ҷониби Худо китобе омад ва ӯро шинохтанд, ҳарчанд китобашро ҳам тасдиқ карда буд ва бо он ки з-он пеш хостори пирӯзи бар кофирон буданд, ба ӯ имон наёварданд, ки лаънати Худо бар кофирон буд!'),
(97, 2, 90, 'Бо худ бадмуомилагӣ карданд, он гоҳ ки аз ҳасад ба китоби Худо кофир шуданд ва аз ин ки Худо фазлу карами хешро ба ҳар кас аз бандагони худ, ки бихоҳад, арзонӣ медорад, ҳасад бурданд ва қарини хашме афзун бар хашми дигар шуданд ва кофиронро азобест хоркунанда!'),
(98, 2, 91, 'Ва чун ба онҳо гуфта шавад, ки ба он чи Худо нозил кардааст, имон биёваред, мегӯянд: «Мо ба он чи бар худамон нозил шудааст, имон меоварем». Ва ба ғайри он ҳарчанд бо ҳақиқат ҳамроҳ бошад ва китобашро ҳам тасдиқ кунад, имон намеоваранд. Бигӯ: «Агар шумо имон оварда будед, аз чӣ сабаб паёмбарони Худоро пеш аз ин мекуштед?».'),
(99, 2, 92, 'Мӯсо бо далелҳои равшани хеш ба ҳидояти шумо омад ва шумо, ситамкорон, пас аз ӯ ба гӯсола имон овардед.'),
(100, 2, 93, 'Ва бо шумо паймон бастем ва кӯҳи Турро бар болои саратон бидоштем. Акнун он чиро, ки бароятон фиристодем, ба имони устувор бигиред ва каломи Худоро бишнавед. Гуфтанд: «Шунидем ва ба кор нахоҳем баст». Бар асари куфрашон ишқи гӯсола дар дилашон ҷой гирифт. Бигӯ: «Агар ба он чӣ мегӯед, бовар доред, боваратон шуморо ба бадкорӣ вомедорад!».'),
(101, 2, 94, 'Бигӯ: «Агар рост мегӯед, ки олами охират назди Худо махсуси шумост, на мардуми дигар, пас орзуи марг кунед».'),
(102, 2, 95, 'Вале ба сабаби аъмоле, ки кардаанд, ҳаргиз орзуи марг нахоҳанд кард. Худо ситамкоронро мешиносад!'),
(103, 2, 96, 'Ононро аз мардуми дигар, ҳатто мушрикон, ба зиндагии инҷаҳонӣ ҳаристар хоҳӣ ёфт ва баъзе аз кофирон дӯст доранд, ки ҳазор сол дар ин дунё зист кунанд ва ин умри дароз азоби Худоро аз онон дур нахоҳад сохт, ки Худо ба аъмолашон биност!'),
(104, 2, 97, 'Ба онон, ки ба Ҷабраил душманӣ меварзанд, бигӯ: «Ӯст, ки ин оётро ба фармони Худо бар дили ту нозил кардааст то китобҳои дигари осмониро тасдиқ кунад ва барои мӯъминон раҳнамун ва хушхабар бошад».'),
(105, 2, 98, 'Ҳарки душмани Ходою фариштагони ӯ ва паёмбаронаш ваҶабраилу Микоил бошад, Худо ҳам душмани кофирон аст!'),
(106, 2, 99, 'Бе шакк бар ту оёте равшан нозил кардем. Ба ҷуз фосиқон касе мункири онҳо нахоҳад шуд.'),
(107, 2, 100, 'Оё ҳар бор, ки бо Худо паймоне бастанд, гурӯҳе аз эшон паймоншиканӣ карданд? Оре, бештарашон имон нахоҳанд овард!'),
(108, 2, 101, 'Ва гурӯҳе аз аҳли китоб чун паёмбаре аз ҷониби Худо бар онон фиристода шуд, ки ба китобашон ҳам гувоҳӣ медод, китоби Худоро, чунон ки гӯӣ аз он бехабаранд, паси пушт афканданд.'),
(109, 2, 102, 'Ва ба афсуне, ки девҳо ба рӯзгори подшоҳии Сулаймон мехонданд, пайравӣ карданд ва Сулаймон кофир набуд, вале девҳо, ки мардумро ҷодугарӣ меомӯхтанд, кофир буданд. Ва низ он афсун, ки бар он ду фаришта ---- Ҳорут ва Морут дар Бобул нозил шуд, дар ҳоле, ки, он ду ба ҳар кас, ки ҷодугарӣ меомӯхтанд, мегуфтанд: «Кори мо фитна аст, мабод кофир шавӣ». Ва мардум аз он ду ҷодуҳе меомӯхтанд, ки метавонистанд миёни зану шӯй ҷудоӣ афкананд ва онон ҷуз ба фармони Худо ба касе зиёне намерасониданд ва он чӣ мардум меомӯхтанд, ба онҳо зиён мерасонид, на суд. Ва худ медонистанд, ки харидорони он ҷодуро дар охират баҳрае нест. Худро ба бад чизе фурӯхтанд, агар медонистанд!'),
(110, 2, 103, 'Агар имон оварда ва парҳезгор шуда будаанд, подоше, ки аз ҷониби Худо ба онҳо дода мешуд, аз ҳар чизи дигаре некӯтар мебуд, агар медонистанд!'),
(111, 2, 104, 'Эй касоне, ки имон овардаед, магӯед «роъино», бигӯед «унзурно». Ва гӯш фаро доред, ки барои кофирон азобест дардовар!'),
(112, 2, 105, 'Аз миёни аҳли китоб онон, ки кофир шуданд ва низ мушрикон дӯст намедоранд, ки аз ҷониби Парвардигор ба шумо хайре бирасад. Ва ҳол он ки Худо ҳар касро, ки бихоҳад, ба бахшоиши хеш махсус медорад. Худо соҳиби фазле бузург аст!'),
(113, 2, 106, 'Ҳеҷ ояеро бекор ё тарк намекунем, магар он ки беҳтар аз он ё ҳамонанди онро меоварем. Оё намедонӣ, ки Худо бар коре тавоност?'),
(114, 2, 107, 'Оё намедонӣ, ки Худо фармонравои осмонҳо ва замин аст ва шуморо ҷуз ӯ ёреву ёваре нест?'),
(115, 2, 108, 'Оё мехоҳед аз паёмбари худ чизе бипурсед, ҳамчунон қи қавми Мӯсо пеш аз ин аз Мӯсо пурсида буданд? Он кас, ки куфрро ба ҷои имон ихтиёр кунад, чун касест, ки роҳи ростро гум карда бошад.'),
(116, 2, 109, 'Бисёре аз аҳли китоб, бо он ки ҳақиқат бар онҳо ошкор шуда, аз рӯӣ ҳасад дӯст доранд шуморо пас аз имон оварданатон ба куфр бозгардонанд. Афв кунед ва гузашт кунед то Худо фармонашро биёварад, ки ӯ бар ҳар коре тавоност!'),
(117, 2, 110, 'Намоз бигзоред ва закот бидиҳед! Ҳар некиро, ки пешопеш барои худ равона медоред, назди Худояш хоҳед ёфт. Албатта Худо ба корҳое, ки мекунед биност.'),
(118, 2, 111, 'Гуфтанд: «Ғайра яҳудон ва тарсоён касе ба биҳишт намеравад». Ин орзуи онҳост. Бигӯ: «Агар рост мегӯед, ҳуҷҷати хеш биёваред».'),
(119, 2, 112, 'Оре, ҳар касс, ки аз рӯи ихлос рӯ ба Худо кунад ва некӯкор бувад, муздашро аз Парвардигораш хоҳад гирифт ва дастхуши биму андӯҳ намешавад.'),
(120, 2, 113, 'Бо ин ки китоб Худоро мехонанд, яҳудон гуфтанд, ки тарсоён барҳақ наянд ва тарсоён гуфтанд, ки яҳудон барҳақ наянд. Ҳамчунин онҳо, ки ноогоҳанд сухане чун сухани онон гӯянд. Худо дар рӯзи қиёмат дар бораи он чӣ дар он ихтилоф мекунанд, миёнашон ҳукм хоҳад кард.'),
(121, 2, 114, 'Кист ситамкортар аз он ки нагзошт, ки номи Худо дар масҷидҳои Худо бурда шавад ва дар вайрон сохтани онҳо кӯшид? Раво нест дар он масҷидҳо ҷуз бимноку тарсон дохил шаванд ва насибашон дар дунё хорӣ ва дар охират азобе бузург аст!'),
(122, 2, 115, 'Машриқу Мағриб аз они Худост. Пас бар ҳар ҷое, ки рӯ кунед ҳамон ҷо рӯ ба Худост. Худо васеъ раҳмат ва доност!'),
(123, 2, 116, 'Гуфтанд, ки Худо фарзанде гирифт. По каст ӯ! Балки ҳар чӣ дар осмонҳо ва замин аст, аз они Ӯст ва ҳама фармонбардори Ӯянд!'),
(124, 2, 117, 'Офаринандаи осмонҳо ва замин аст. Чун иродаи чизе кунад, мегӯянд: «Мавҷуд шав!» Ва он чиз мавҷуд мешавад.'),
(125, 2, 118, 'Нодонон гуфтанд: «Чаро Худо бо мо сухан намегӯяд? Ё мӯъҷизае бар мо намеояд?» Пешиниёнашон низ чунин суханоне мегуфтанд. Дилҳошон ҳамонанди якдигар аст. Мо барои онон, ки ба яқии расидаанд, оятҳоро баён кардаем.'),
(126, 2, 119, 'Мо туро ки сазовор ҳастӣ ба паёмбари фиристодем то мужда диҳӣ ва битарсонӣ. Ту масъули дӯзахиён нестӣ.'),
(127, 2, 120, 'Яҳудон ва тарсоён аз ту хушнуд намешаванд, то ба динашон гардан ниҳӣ. Бигӯ: «Ҳидоят ҳидоятест, ки аз ҷониби Худо бошад». Агар аз он пас ки Худо туро огоҳ кардааст, ба хостаи онҳо пайравӣ кунӣ, ҳеҷ ёвару мададгоре аз ҷониби ӯ нахоҳӣ дошт.'),
(128, 2, 121, 'Касоне, ки китобашон додаем ва ончунон, ки сазовор аст, онро мехонанд, ба он имон доранд ва онон, ки ба он имон надоранд, зиёнкорон ҳастанд.'),
(129, 2, 122, 'Эй банӣ-Исроил, аз он неъмате, ки бар шумо ато кардам ва шуморо бар ҷаҳониён бартарӣ бузургӣ додам ёд кунед!'),
(130, 2, 123, 'Ва битарсед аз рӯзе, ки ҳеҷ касс дигареро ба кор наёяд ва фидя пазируфта нашавад ва шафоъат нафъашон накунад ва касе ба ёрияшон барнахезад!'),
(131, 2, 124, 'Ва Парвардигори Иброҳим ӯро ба коре чанд биёзмуд ва Иброҳим он корҳоро ба тамомӣ ба анҷом расонид. Худо гуфт: «Ман туро пешвои мардум гардонидам». Гуфт: «Фарзандонамро ҳам?» Гуфт: «Паймони ман ситамкоронро дар бар нагирад».'),
(132, 2, 125, 'Ва Каъбаро ҷои ҷамъшавӣ ва макони амни мардум сохтем. Мақоми Иброҳимро намозгоҳи хеш гиред. Мо Иброҳим ва Исмоилро фармон додем: «Хонаи маро барои тавофкунандагону муқимон ва рокеъону соҷидон покиза доред!»'),
(133, 2, 126, 'Ва Иброҳим гуфт: «Эй Парвардигори ман, ин шаҳрро ҷои амне гардон ва аз мардумаш ононро, ки ба Худо ва рӯзи қиёмат имон доранд, аз ҳар Самара рӯзӣ соз!» Гуфтем: «Ҳар кас, ки кофир шуд, ӯро андак бархӯрдорӣ диҳам, сипас ба азоби оташ дучораш гардонам», ки бад ҷои бозгаштест!'),
(134, 2, 127, 'Ва чун Иброҳим ва Исроил пояҳои хонаро боло бурданд, гуфтанд: «Эй Парвардигори мо, аз мо бипазир, ки ту шунаво ва доно ҳастӣ!»'),
(135, 2, 128, 'Эй Парвардигори мо, моро фармонбардори хеш соз ва низ фарзандони моро фармонбардори хеш гардон ва тариқаи ҳаҷамонро ба мо биёмуз ва тавбаи мо бипазир, ки ту тавбапазиранда ва меҳрубон ҳастӣ!'),
(136, 2, 129, 'Эй Парвардигори мо, аз миёнашон паёмбаре бар онҳо бифирист, то оёти туро бар онҳо бихонад ва ба онҳо китобу ҳикмат биёмӯзад ва онҳоро покиза созад ва ту пирӯзманду ҳаким ҳастӣ!'),
(137, 2, 130, 'Чӣ касе аз дини Иброҳим рӯй барметобад, ҷуз он ки худро бехирад сохта бошад? Иброҳимро дар дунё баргузидем ва ӯ дар охират низ аз шоистагон аст.'),
(138, 2, 131, 'Ва Парвардигораш ба ӯ гуфт: «Таслим шав». Гуфт: «Ман дар баробари Парвардигори ҷаҳониён таслимам».'),
(139, 2, 132, 'Иброҳим ба фарзандони худ васият кард, ки дар баробари Худо таслим шаванд. Ва Яъқуб ба фарзандони худ гуфт: «Эй фарзандони ман, Худо барои шумо ин динро баргузидааст, мабод бимиред, бе он ки ба он гардан ниҳода бошед».'),
(140, 2, 133, 'Оё шумо ҳозир будед, он гоҳ, ки марги Яъқуб фаро расид ва ба фарзандонаш гуфт: «Пас аз ман чӣ чизро мепарастед?» Гуфтанд: «Худои ту ва Худои аҷдоди ту --- Иброҳиму Исмоил ва Исҳоқро ба яктоӣ хоҳем парастид ва дар баробари Ӯ таслим ҳастем».'),
(141, 2, 134, 'Онҳо умматҳое будаанд, ки акнун даргузаштаанд. Он чӣ карда буданд, аз они онҳост ва он чӣ шумо кунед, аз они шумост ва шуморо аз аъмоле, ки онҳо мекардаанд, намепурсанд.'),
(142, 2, 135, 'Гуфтанд: «Яҳудӣ ё насронӣ шавед, то ба роҳи рост афтед». «Бигӯ: Мо дини Иброҳимро баргузидем ва ӯ мушрик набуд».'),
(143, 2, 136, 'Бигӯед: «Мо ба Худо ва оёте, ки бар мо нозил шуда ва низ он чӣ бар Иброҳиму Исмоил ва Исҳоқу Яъқуб ва сибтҳо нозил омада ва низ он чӣ ба Мӯсо ва Исо фиристода шуда ва он чӣ бар паёмбарони дигар аз ҷониби Парвардигорашон омадааст, имон овардем. Миёни ҳеҷ як аз паёмбарон фарқе намениҳем ва ҳама дар баробари Худо таслим ҳастем».'),
(144, 2, 137, 'Агар ба он чӣ шумо имон овардед, онон низ имон биёваранд, ҳидоят ёфтаанд. Аммо агар рӯй бартофтанд, пас бо ту дар душманиву хилофанд ва дар баробари онҳо Худо туро кофист, ки Ӯ шунаво ва доност!'),
(145, 2, 138, 'Ин ранги Худост ва ранги чӣ касе аз ранги Худо беҳтар аст. Мо парастандагони Ӯ ҳастем.'),
(146, 2, 139, 'Бигӯ: «Оё дар бораи Худо бо мо баҳсу муҷодала мекунед? Ӯ Парвардигори мову шумост. Аъмоли мо азони мо ва аъмоли шумо азони шумост ва мо Ӯро ба покдилӣ мепарастем».'),
(147, 2, 140, 'Оё мегӯед, ки Иброҳиму Исмоил ва Исҳоқу Яъқуб ва сибтҳо яҳудӣ ё насронӣ буданд? Бигӯ: «Оё шумо огоҳтаред ё Худо? Ситамкортар аз касе, ки гувоҳии худро аз Худо пинҳон мекунад, кист? Ва Худованд аз корҳое, ки мекунед, ғофил нест».'),
(148, 2, 141, 'Онон умматҳое будаанд, ки акнун даргузаштаанд. Он чӣ онҳо карданд, азони онҳост ва он чӣ шумо мекунед, азони шумост ва шуморо аз аъмоли онҳо намепурсанд.'),
(149, 2, 142, 'Аз мардум онон, ки камхираданд, хоҳанд гуфт: «Чӣ чиз онҳоро аз қиблае, ки рӯ ба рӯи он меистоданд, баргардонид?» Бигӯ: «Машриқу Мағриб азони Худост ва Худо ҳар касро, ки бихоҳад, ба роҳи рост ҳидоят мекунад!».'),
(150, 2, 143, 'Оре чуни наст, ки шуморо беҳтарини умматҳо гардонидем, то бар мардумон гувоҳ бошед ва паёмбар бар шумо гувоҳ бошад. Ва он қиблаеро, ки рӯ ба рӯи он меистодӣ, дигаргун накардем, ҷуз ба он сабаб, ки ононро, ки аз паёмбар пайравӣ мекунанд, аз онон, ки пайравӣ намекунанд, бозшиносем. Ҳарчанд, ки ин амр бар ғайри ҳидоятёфтагон душвор менамуд. Худо имони шуморо табоҳ намекунад. Ӯ бар мардумон меҳрубон ва бахшоянда аст!'),
(151, 2, 144, 'Нигаристанатро ба атрофи осмон мебинем, Туро ба сӯи қиблае, ки меписандӣ, мегардонем. Пас рӯй ба ҷониби Масҷидулҳаром кун. Ва ҳар ҷо, ки бошед, рӯй ба он ҷониб кунед. Аҳли китоб медонанд, ки ин дигаргунӣ ба ҳақ ва аз ҷониби Парвардигорашон будааст. Ва Худо аз он чӣ мекунед, ғофил нест!'),
(152, 2, 145, 'Барои аҳли китоб ҳар бурҳону нишонае, ки биёвари, аз қиблаи ту пайравӣ нахоҳанд кард ва ту низ аз қиблаи онҳо пайравӣ намекунӣ ва онҳо ҳам худ пайравӣ қиблаи якдигар нахоҳанд буд. Ҳар гоҳ пас аз огоҳӣ паи хоҳишҳои эшон биравӣ, аз ситамкорон хоҳӣ буд.'),
(153, 2, 146, 'Аҳли китоб ҳамчунон, ки фарзандони худро мешиносанд, ӯро мешиносанд, вале гурӯҳе аз эшон дар айни огоҳӣ ҳақиқатро пинҳон медоранд.'),
(154, 2, 147, 'Он чӣ аз ҷониби Худо бар ту нозил шуда, ҳақ ҳамон аст: шубҳа макун!'),
(155, 2, 148, 'Ҳар касеро ҷонибест, ки ба он рӯй меоварад. Пас дар некӣ кардан бар якдигар пешӣ гиред. Ҳар ҷо, ки бошед, Худо шуморо ҳозир меоварад, ки Ӯ бар ҳар коре тавоност!'),
(156, 2, 149, 'Аз ҳар ҷое, ки берун шавӣ, рӯи худ ба ҷониби Масҷидулҳаром кун. Ҳақ ҳамон аст, ки Парвардигорат ба он амр фармуда. Ва Худо аз корҳоятон ноогоҳ нест!'),
(157, 2, 150, 'Аз ҳар ҷое, ки берун шавӣ, рӯи хеш ба ҷониби Масҷидулҳаром кун. Ва ҳар ҷо, ки бошед рӯй ба он сӯй кунед то ҳеҷ касро, ҷуз ситамкорон, бо шумо баҳсу муҷодалае набошад. Аз онҳо натарсед, аз ман битарсед то неъмати хеш бар шумо комил кунам, бошад, ки ҳидоят шавед.'),
(158, 2, 151, 'Ҳамчунон, ки паёмбаре аз худи шуморо бар шумо фиристодем, то оёти моро бароятон бихонад ва шуморо покиза гардонад ва китобу ҳикмат омӯзад ва он чиро, намедонистед, ба шумо ёд диҳад.'),
(159, 2, 152, 'Пас моро ёд кунед, то шуморо ёд кунам. Маро сипос гӯед ва носипосии ман накунед.'),
(160, 2, 153, 'Эй касоне, ки имон овардаед, аз сабру намоз мадад ҷӯед, ки Худо бо сабркунандагон аст.'),
(161, 2, 154, 'Ононро, ки дар роҳи Худо кушта мешаванд, мурда наҳисобед. Онҳо зиндаанд ва лекин шумо ҳис намекунед.'),
(162, 2, 155, 'Албатта, шуморо ба андаке тарс ва гуруснагиву бенавоӣ ва бемориву нуқсон дар зироот меозмоем. Ва сабркунандагонро хушхабар деҳ.'),
(163, 2, 156, 'Касоне, ки чун мусибате ба онҳо расид, гуфтанд: «Мо аз они Худо ҳастем ва ба назди Ӯ бозмегардем».'),
(164, 2, 157, 'Салавот ва раҳмати Парвардигорашон бар онон бод, ки ҳидоятёфтагонанд.'),
(165, 2, 158, 'Сафо ва Марва аз шиорҳои Худост. Пас касоне, ки ҳаҷи хонаро ба ҷой меоваранд ё умра мегузоранд, агар бар он ду кӯҳ тавоф кунанд, сазовори гуноҳе нашудаанд. Пас ҳар кӣ кори некеро ба рағбат анҷом диҳад, бидонад, ки Худо шукргузорандае доност!'),
(166, 2, 159, 'Касонеро, ки далелҳои равшан ва ҳидояткунандаи моро пас аз он ки дар китоб барои мардум баёнашон кардаем, пинҳон мекунанд, ҳам Худо лаънат мекунад ва ҳам дигар лаънаткунандагон.'),
(167, 2, 160, 'Лекин онҳо, ки тавба карданд ва ба салоҳ омаданд ва он чӣ пинҳон дошта буданд, ошкор сохтанд, тавбаашонро мепазирам ва ман тавбапазир ва меҳрубонам!'),
(168, 2, 161, 'Бар онон, ки кофир буданд ва дар кофирӣ мурданд, лаънати Худо ва фариштагону ҳамаи мардум бод!'),
(169, 2, 162, 'Ҷовидона дар лаънатанд ва дар азобашон сабукӣ дода нашавад ва лаҳзае мӯҳлаташон надиҳанд.'),
(170, 2, 163, 'Худои шумо Худоест якто. Худое ғайри Ӯ нест бахшоянда ва меҳрубон!'),
(171, 2, 164, 'Дар офариниши осмонҳову замин ва дар омадушуди шабу рӯз ва дар киштиҳое, ки дар дарё мераванд ва сабаби нафъи мардуманд ва дар бороне, ки Худо аз осмон фурӯ мефиристад то замини мурдаро ба он зинда созад ва ҷунбандагонро дар он пароканда кунад ва дар ҳаракати бодҳо ва абрҳои ромшуда миёни замину осмон, барои хирадмандоне, ки дармеёбанд, нишонаҳост!'),
(172, 2, 165, 'Баъзе аз мардум Худоро шариконе ихтиёр мекунанд ва онҳоро чунон дӯст медоранд, ки гӯё Худоянд. Вале онон, ки имон овардаанд, Худоро бештар дӯст медоранд. Ва он гоҳ, ки ин ситампешагон азобро бубинанд, дарёбанд, ки ҳамаи қудрат аз они Худост. Албатта Худо ба сахтӣ уқубат мекунад!'),
(173, 2, 166, 'Он гоҳ, ки пешвоён азобро бингаранд ва аз фармонбарони хеш безорӣ ҷӯянд ва пайванди миёни онҳо бурида гардад.'),
(174, 2, 167, 'Ва он пайравон гӯянд: «Кош бори дигар бозмегаштем, то он чунон ки аз мо безорӣ ҷустаанд, аз онҳо безорӣ меҷустем. Худованд кирдорҳояшонро инчунин сабаби ҳасраташон созад ва онон аз оташ раҳоӣ наёбанд!».'),
(175, 2, 168, 'Эй мардум, аз он чизҳои ҳалолу покиза, ки дар замин аст, бихӯред ва пой ба ҷои пои шайтон магузоред, ки душмани ошкори шумост.'),
(176, 2, 169, 'Ӯ шуморо ба бадиву зиштӣ фармон медиҳад ва мехоҳад, ки дар бораи Худо чизҳое бигӯед, ки ба он огоҳ нестед.'),
(177, 2, 170, 'Чун ба онҳо гуфта шавад, ки ба он чӣ Худо нозил кардааст, пайравӣ кунед, гӯянд: «На, мо ба ҳамон роҳе меравем, ки падаронамон мерафтанд». Ҳатто агар падаронашон бехираду гумроҳ буданд.'),
(178, 2, 171, 'Мисоли кофирон мисли ҳайвонест, ки касе дар гӯши ӯ овоз кунад ва ӯ ҷуз бонгеву овозе нашнавад. Инҳо каронанд, гунгонанд, кӯронанд ва ҳеҷ намефаҳманд.'),
(179, 2, 172, 'Эй касоне, имон овардаед, аз он чизҳои покизае, ки рӯзии шумо кардаем, бихӯред ва агар Худоро мепарастед, шукрашро ба ҷой оваред.'),
(180, 2, 173, 'Ҷуз ин нест, ки мурдору хунро ва гӯшти хукро ва он чиро, ки ба ҳангоми забҳ номи ғайри Худо бар он бихонанд, бар шумо ҳаром кард. Аммо касе, ки ночор шавад, ҳар гоҳ ки бемайли ҷӯяд ва аз ҳад нагузаронад, гуноҳе анҷом надода аст, ки Худо омурзанда ва бахшоянда аст!'),
(181, 2, 174, 'Онон, ки китоберо, ки Худо нозил кардааст, пинҳон медоранд, то баҳои андаке биситонанд, шикамҳои худро ҷуз аз оташ пур намесозанд. Ва Худо дар рӯзи қиёмат ба онҳо сухан нагӯяд ва покашон насозад ва насиби онҳо азобе дардовар аст!'),
(182, 2, 175, 'Инҳо гумроҳиро ба ҷои ҳидоят хариданд ва азобро ба ҷои омурзиш. Чӣ чиз бар оташ босабрашон сохта?'),
(183, 2, 176, 'Зеро, ки Худо китобро ба ҳақ нозил кард ва касоне, ки дар китоби Худо ихтилоф мекунанд, дар мухолифате (зиддияте) дур аз савобанд.'),
(184, 2, 177, 'Некӣ он нест, ки рӯи худ ба ҷониби машриқу мағриб кунед, балки некӯкор касест, ки ба Худо ва рӯзи қиёмат ва фариштагон ва китоби Худо ва паёмбарон имон оварад. Ва моли худро, бо он ки дӯсташ дорад, ба хешовандону ятимон ва дармондагону мусофирон ва гадоёну дарбандмондагон бубахшад. Ва намоз бигузорад закот бидиҳад. Ва низ касоне ҳастанд, ки чун аҳде мебанданд, ба он вафо мекунанд. Ва онон, ки дар бенавоиву беморӣ ва ҳангоми ҷанг сабр мекунанд, инҳо ростгӯён ва парҳезгоронанд.'),
(185, 2, 178, 'Эй касоне, ки имон овардаед, дар бораи куштагон бар шумо қасос муқаррар шуд: озод дар баробари озод ва банда дар баробари банда ва зан дар баробари зан. Пас ҳар кас, ки аз ҷониби бародари худ афв гардад, бояд, ки бо хушнудӣ аз паи адои хунбаҳо равад ва онро ба тарзе некӯ ба ӯ пардозад. Ин ҳукм сабукиву раҳмест аз ҷониби Парвардигоратон ва ҳар кӣ аз он рӯй гардонад, насиби ӯ азобест дардовар!'),
(186, 2, 179, 'Эй хирадмандон, шуморо дар қасос кардан зиндагист. Шояд ки битарсед!'),
(187, 2, 180, 'Ҳар гоҳ яке аз шуморо марг фаро расад ва моле бар ҷой гузорад, муқаррар шуд, ки дар бораи падару модар ва хешовандон аз рӯи инсоф васият кунад. Ва ин шоиста парҳезгорон аст.'),
(188, 2, 181, 'Пас ҳар кас, ки он васиятро бишнавад ва он гоҳ дигаргунаш созад, гуноҳаш бар он касест, ки онро дигаргун сохтааст. Худо шунаво ва доност!'),
(189, 2, 182, 'Ҳар гоҳ хавфи он равад, ки васияткунандае дар васияти худ дастхуши ситам ё гуноҳе шуда бошад, агар касе ба ислоҳи миёни онон пардозад, муртакиби гуноҳе нашудааст, ки Худо омурзанда ва меҳрубон аст!'),
(190, 2, 183, 'Эй касоне, имон овардаед, рӯза доштан бар шумо муқаррар шуд, ҳамчунон, ки бар касоне, ки пеш аз шумо будаанд, муқаррар шуда буд то парҳезгор шавед!'),
(191, 2, 184, 'Рӯзҳое башумор. Ҳар кас, ки аз шумо бемор ё дар сафар бошад, ба ҳамон адад аз рӯзҳои дигар рӯза бидорад. Ва онон, ки тавоноии он надоранд, ҳар рӯзро ба таъом додани мискине бозхаранд. Ва ҳар кӣ ба ихтиёр дар он бияфзояд, барояш беҳтар аст ва агар мехоҳед бидонед, беҳтар он аст, ки худ рӯза бидоред.'),
(192, 2, 185, 'Моҳи Рамазон, ки дар он барои роҳнамоии мардум ва баёни роҳи равшани ҳидоят ва ҷудо сохтани ҳақ аз ботил Қуръони нозил шудааст, пас ҳар кас, ки ин моҳро дарёбад, бояд ки дар он рӯза бидорад. Ва ҳар кас, ки бемор ё дар сафар бошад, ба ҳамон адад аз рӯзҳои дигар. Худо барои шумо хостгори осонӣ аст на сахтӣ ва то он шуморо комил созед. Ва Худоро ба он сабаб, ки роҳнамоиятон кардааст, ба бузургӣ ёд кунед ва шукр гӯед!'),
(193, 2, 186, 'Чун бандагони Ман дар бораи Ман аз ту бипурсанд, Ман наздикам ва ба нидои касе, ки Маро бихонад, ҷавоб медиҳам. Пас ба нидои Ман посух диҳанд ва ба Ман имон оваранд, то роҳи рост ёбанд!'),
(194, 2, 187, 'Ҳамбистарӣ бо занонатон дар шаби моҳи рӯза бар шумо ҳалол шуд. Онҳо пӯшиши шумоянд ва шумо пӯшиши онҳоед. Худо медонист, ки шумо бо хештан хиёнат меварзед, пас тавбаи шуморо пазируфт ва шуморо афв кард. Акнун бо онҳо ҳамбистар шавед ва он чиро ки Худо бар шумо муқаррар гардонидааст, талаб кунед ва бихӯреду биёшомед то риштаи равшани субҳдам дар торикии шаб ошкор шавад. Ва рӯзаро ба шаб бирасонед. Ва чун дар масҷид чилланишинӣ кунед, бо занон ҳамбистар нашавед. Инҳо ҳудуди фармони Худост, ба он наздик машавед. Худо оёти худро инчунин баён мекунад, бошад, ки ба парҳезгорӣ бирасанд!'),
(195, 2, 188, 'Молҳои, якдигарро ба ношоист махӯред ва онро ба ришват ба ҳакимон мадиҳед то бад-он сабаб молҳои гурӯҳе дигарро ба ноҳақ бихӯред. Ва шумо худ медонед.'),
(196, 2, 189, 'Аз ту дар бораи ҳилолҳои моҳ мепурсанд, бигӯ: «Барои он аст, мардум вақти корҳои хеш ва замони ҳаҷро бишиносанд». Ва писандида нест, ки аз пушти хонаҳо ба онҳо дохил шавед, вале писандида роҳи касонест, ки Худо тарсӣ кунанд ва аз дарҳо ба хонаҳо дароед ва аз Худо битарсед то наҷот ёбед!'),
(197, 2, 190, 'Бо касоне, ки бо шумо ҷанг мекунанд, дар роҳи Худо биҷангед ва аз ҳад нагузаред. Зеро Худо аз ҳад гузарандагонро дӯст надорад!'),
(198, 2, 191, 'Ҳар ҷо, ки онҳоро биёбед, бикушед ва аз он ҷо, ки шуморо рондаанд, биронедашон, ки фитна аз қатл бадтар аст. Ва дар Масҷидулҳаром бо онҳо наҷангед, магар он ки бо шумо биҷанганд. Ва чун бо шумо ҷангиданд, бикушедашон, ки ин аст ҷазои кофирон!'),
(199, 2, 192, 'Ва агар бозистоданд, Худо омурзанда ва меҳрубон аст!'),
(200, 2, 193, 'Бо онҳо биҷангед то дигар фитнае набошад ва дин танҳо дини Худо шавад. Вале агар аз дини хеш даст бардоштанд, таҷовуз ҷуз бар ситамкорон раво нест!'),
(201, 2, 194, 'Ин моҳи ҳаром дар муқобили он моҳи ҳаром ва шикастан моҳҳои ҳаромро қасос аст. Пас ҳар кас бар шумо дастдарозӣ кунад, ба ҳамон андоза бар ӯ дастдарозӣ кунед ва аз Худо битарсед ва бидонед, ки Ӯ бо парҳезгорон аст!'),
(202, 2, 195, 'Дар роҳи Худо харҷ кунед ва худатонро ба дасти хеш ба ҳалокат маяндозед ва некӣ кунед, ки Худо некӯкоронро дӯст дорад.'),
(203, 2, 196, 'Ҳаҷ ва умраро барои Худо комил ба ҷой оред. Ва агар шуморо аз ҳаҷ боздоштанд, он қадар, ки муяссар аст, қурбонӣ кунед ва сар матарошед то қурбонии шумо ба қурбонгоҳаш бирасад. Агар яке аз шумо бемор ё дар сараш озоре бувад, ба унвони фидя рӯза бидорад ё садақа диҳад, ё қурбонӣ кунад. Ва чун эмин шавад, ҳар ки аз умраи таматтӯъ ба ҳаҷ бозояд, он қадар ки ӯро муяссар аст, қурбонӣ кунад. Ва ҳар киро қурбонӣ муяссар нашуд, се рӯз дар ҳаҷ рӯза бидорад ва ҳафт рӯз чун аз ҳаҷ бозгардад, то даҳ рӯзи комил шавад. Ва ин ҳукм барои касест, ки аз мардуми Макка набошад. Аз Худо битарсед ва бидонед, ки Худо ба сахтӣ уқубат мекунад!'),
(204, 2, 197, 'Ҳаҷ дар моҳҳои муъайянест. Ҳар кӣ дар он моҳҳо ин фаризаро адо кунад, бояд, ки дар аснои он ҷимоъ накунад ва фисқе аз ӯ сар назанад ва хусумат накунад. Ҳар кори неке, анҷом медиҳед, Худо аз он огоҳ аст. Ва тӯша бардоред, ки беҳтарини тӯшаҳо парҳезгорист, Эй хирадмандон, аз Ман битарсед!'),
(205, 2, 198, 'Агар ба ҳангоми ҳаҷ толиби рӯзии Худо бошед, гунаҳгор нашудаед ва чун аз Арафот бозгаштаед, Худоро дар Машъарулҳаром ёд кунед: аз он рӯ ки шуморо ҳидоят карда, дар ҳоле ки пеш аз он гумроҳ будаед.'),
(206, 2, 199, 'Сипас аз он ҷо, ки дигар мардум бозмегарданд, шумо низ бозгардед ва аз Худо омурзиш бихоҳед, ки омурзанда ва меҳрубон аст!'),
(207, 2, 200, 'Чун маносикатонро ба ҷой овардаед, ҳамчунон ки падарони хешро ёд мекардед,--- ҳатто бештар аз он,----- Худойро ёд кунед. Баъзе аз мардум мегӯянд: «Эй Парвардигори мо, моро ҳам дар дунё чизе бахш». Инҳоро дар охират насибе нест.'),
(208, 2, 201, 'Ва баъзе аз мардум мегӯянд: «Эй Парвардигори мо, моро ҳам дар дунё хайре бахш ва ҳам дар охират ва моро аз азоби оташ нигаҳ дор».'),
(209, 2, 202, 'Инҳо аз он чӣ хостаанд, баҳраманд мешаванд ва Худо басуръат ба ҳисоби ҳар кас мерасад!'),
(210, 2, 203, 'Худовандро дар рӯзҳое чан дёд кунед ва ҳар кӣ дар ду рӯз шитоб кунад, содиркунандаи гуноҳе нашудааст. Ҳар парҳезгоре ҳам, ки таърих кунад, гуноҳе накардааст. Аз Худо битарсед ва бидонед, ки ҳамагон ба пешгоҳи Вай гирд меоед!'),
(211, 2, 204, 'Дар ин дунё касест аз мардум, ки Худоро ба дурустии эътиқоди хеш гувоҳ мегирад ват уро суханаш дар бораи зиндагии ин дунё ба тааҷҷуб меорад, дар ҳоле ки сахттарини душманон аст.'),
(212, 2, 205, 'Чун аз назди ту бозгардад, дар замин фасод кунад ва киштзорҳо ва домҳоро нобуд созад ва Худо фасодро дӯст надорад!'),
(213, 2, 206, 'Ва чун ба ӯ гӯянд, ки аз Худо битарс, худхоҳиаш ӯро ба гуноҳ кашонанд. Ҷаҳаннам --- он ҷойгоҳи бад ӯро бас бошад!'),
(214, 2, 207, 'Касе дигар аз мардум барои ҷустани хушнудии Худо ҷони хешро фидо кунад. Худо бар ин бандагон меҳрубон аст!'),
(215, 2, 208, 'Эй касоне, ки имон овардаед, ҳамагон ба итоъат дароед ва пой ба ҷои пои шайтон магузоред, ки ӯ душмани ошкори шумост.'),
(216, 2, 209, 'Ҳар гоҳ пас аз он ки ин оёти равшани Худо ба шумо расид, дар имони хеш лағзише ёфтед, бидонед, ки Худо пирӯзманд ва ҳаким аст.'),
(217, 2, 210, 'Оё инҳо мунтазири он ҳастанд, ки Худо бо фариштагон дар зери соябоне аз абр наздашон биёяд ва кор яксара шавад? Ҳол он ки ҳамаи корҳо ба сӯи Худо бармегарданд.'),
(218, 2, 211, 'Аз банӣ-Исроил бипурс, ки эшонро чӣ оёти равшане додем. Ҳар кас, ки неъматеро, ки Худо ба ӯ иноят кардааст, (додааст) дигаргун созад, бидонад, ки уқубати ӯ сахт аст!'),
(219, 2, 212, 'Барои кофирон зиндагии инҷаҳонӣ ороста шудааст ва мӯъминонро ба масхара мегиранд. Онон, ки аз Худо метарсанд, дар рӯзи қиёмат болотар аз кофирон ҳастанд ва Худо ҳар касро, ки бихоҳад, беҳисоб рӯзӣ медиҳад!'),
(220, 2, 213, 'Мардум як уммат буданд, пас Худо паёмбарони хушхабардиҳанда ва тарсонандаро бифиристод ва бар онҳо китоби барҳақ нозил кард, то он китоб дар он чӣ мардум ихтилоф доранд, миёнашон ҳукм кунад, вале ғайри касоне, ки китоб бар онҳо нозил шуда ва ҳуҷҷатҳо ошкор гашта буд, аз рӯи ҳасаде, ки нисбат ба ҳам меварзиданд, дар он ихтилоф накарданд. Ва Худо мӯъминонро ба иродаи худ дар он ҳақиқате, ки ихтилоф мекарданд, роҳ намуд, ки Худо ҳар касро, ки бихоҳад, ба роҳи рост ҳидоят мекунад!'),
(221, 2, 214, 'Мепиндоред, ки ба биҳишт хоҳед рафт? Ва ҳанӯз он чӣ бар сари пешиниёни шумо омада, бар сари шумо наёмада? Ба онҳо сахтиву ранҷ расид ва такон дода шуданд, то он ҷо, ки маёмбар ва мӯъминоне, ки бо ӯ буданд, гуфтанд: «Пас ёрии Худо кай хоҳад расид?» Бидон, ки ёрии Худо наздик аст!'),
(222, 2, 215, 'Аз ту мепурсанд, ки чӣ нафақа кунанд? Бигӯ: «Он чӣ аз моли худ нафақа мекунед, барои падару модар ва хешовандону ятимон ва мискинону мусофирон бошад ва ҳар кори неке, ки кунед, Худо ба он огоҳ аст».'),
(223, 2, 216, 'Ҷанг бар шумо муқаррар шуд, дар ҳоле ки онро нохуш доред. Шояд чизеро нохуш бидоред ва дар он хайри шумо бошад ва шояд чизеро дӯст дошта бошед ва бароятон нописанд афтад. Худо медонад ва шумо намедонед!'),
(224, 2, 217, 'Туро аз ҷанг кардан дар моҳи ҳаром мепурсанд. Бигӯ: «Ҷанг кардан дар он моҳ гуноҳе бузург аст. Аммо боздоштани мардум аз роҳи Ҳақ ва кофир шудан ба ӯ ва Масҷидулҳаром ва берун рондани мардумаш аз он ҷо дар назди Худованд гуноҳе бузургтар аст ва ширк аз қатл бузургтар аст!». Онҳо бо шумо меҷанганд, то агар битавонанд, шуморо аз динатон бозгардонанд. Аз миёни шумо онҳо, ки аз дини худ бозгарданд ва кофир бимиранд, аъмолашон дар дунё ва охират нобуд шуда ва ҷовидона дар ҷаҳаннам бошанд!'),
(225, 2, 218, 'Касоне, ки имон овардаанд ва онон, ки ватанро тарк кардаанд ва дар роҳи Худо ҷиҳод намуданд, ба раҳмати Худо умед медоранд ва Худо бахшоянда ва меҳрубон аст!'),
(226, 2, 219, 'Туро аз шаробу қимор мепурсанд. Бигӯ: «Дар он ду гуноҳе бузург ва нафъҳоест барои мардум. Ва гуноҳашон аз нафъашон бештар аст». Ва аз ту мепурсанд: «Чӣ чиз нафақа кунанд? Бигӯ: «Он чӣ афзун ояд». Худо оётро инчунин барои шумо баён мекунад, бошад, ки дар кори дунё ва охират бияндешед.'),
(227, 2, 220, 'Туро аз ятимон мепурсанд. Бигӯ: «Ислоҳи ҳолашон беҳтар аст». Ва агар бо онҳо омезиш мекунед, чун бародарони шумо бошанд. Худованд табаҳкорро аз некӯкор бозмешиносад ва агар хоҳад бар шумо сахт мегирад, ки пирӯзманд ва ҳаким аст!'),
(228, 2, 221, 'Занони мушрикаро то имон наёваранд, ба занӣ магиред ва канизи мӯъмина беҳтар аз озодзани мушрика аст, ҳарчанд шуморо аз ӯ хуш ояд. Ва ба мардони мушрик то имон наёваранд, зани мӯъмина мадиҳед. Ва бандаи мӯъмин беҳтар аз мушрик аст, ҳарчанд шуморо аз ӯ хуш ояд. Инҳо ба сӯи оташ даъват мекунанд ва Худо ба ҷониби биҳишту омурзиш. Ва оёти худро ошкор баён мекунад, бошад, ки бияндешанд!'),
(229, 2, 222, 'Туро аз ҳайзи занон мепурсанд. Бигӯ: «Ҳайз нопокист. Пас дар айёми ҳайз аз занон дури бигиред ва ба онҳо наздик машавед то пок гарданд. Ва чун пок шуданд, аз он ҷо, ки Худо фармон додааст, бо онҳо наздикӣ кунед. Албатта Худо тавбакунандагон ва покизагонро дӯст дорад!».'),
(230, 2, 223, 'Занатон киштзори шумо ҳастанд. Ҳар ҷо, ки хоҳед, ба киштзори худ дароед. Ва барои хеш аз пеш чизе фиристед ва аз Худо битарсед ва бидонед, ки ба назди Ӯ хоҳед рафт. Ва мӯъминонро хушхабар деҳ!'),
(231, 2, 224, 'Худоро василаи савгандҳои хеш қарор мадиҳед то аз некӯкориву тақво ва ислоҳ дар миёни мардум бозистед, ки Худо шунаво ва доност!'),
(232, 2, 225, 'Худо шуморо ба сабаби савгандҳои беҳудаатон бозхост намекунад, балки ба хотири нияте, ки дар дил ниҳон медоред, бозхост мекунад. Худо омурзанда ва бурдбор аст!'),
(233, 2, 226, 'Барои касоне, ки савганд мехӯранд, ки бо занони хеш наёмезанд, ҷаҳор моҳ мӯҳлат аст. Пас агар боз оянд, Худо омурзанда ва меҳрубон аст.'),
(234, 2, 227, 'Ва агар азми талоқ карданд, Худованд шунаво ва доност!'),
(235, 2, 228, 'Бояд, ки занони талоқшуда то се бор пок шудан аз шавҳар кардан бозистанд. Ва агар ба Худо ва рӯзи қиёмат имон доранд, раво нест, ки он чиро, ки Худо дар раҳми онон офаридааст, пинҳон доранд. Ва дар айёми агар шавҳаронашон қасди ислоҳ дошта бошанд, ба бозгардониданашон сазовортаранд. Ва барои занон ҳуқуқе шоиста аст, ҳамонанди вазифае, ки бар ӯҳдаи онҳост, вале мардонро бар занон мартабаест. Ва Худо пирӯзманду ҳаким аст!'),
(236, 2, 229, 'Ин талоқ ду бор аст ва аз он пас ё ба некӯ тарзе нигаҳ доштани ӯст ё ба некӯ тарзе раҳо сохтанаш. Ва ҳалол нест, ки аз он чӣ ба занон додаед, чизе бозситонед, магар он ки бидонанд, ки ҳудуди Худоро риоят намекунанд. Аммо ҳар гоҳ донистед, ки он ду ҳудуди Худоро риоят намекунанд, агар зан худро аз шӯй бозхарад, гуноҳе бар он ду нест. Инҳо ҳудуди Худо аст. Аз он таҷовуз макунед, ки ситамкорон аз ҳудуди Худо таҷовуз мекунанд.'),
(237, 2, 230, 'Пас агар боз занро талоқ, дод, дигар бар ӯ ҳалол нест, то он ки ба никоҳи марде дигар дарояд (ва бо он мард ҷимоъ кунад), ва ҳар гоҳ он мард занро талоқ диҳад, агар медонанд, ки ҳудуди Худоро риоят мекунанд руҷӯъашонро гуноҳе нест. Инҳо ҳудуди Худост, ки барои мардуме доно баён мекунад.'),
(238, 2, 231, 'Ҳар гоҳ занонро талоқ додед ва мӯҳлаташон қариб омад ё ононро ба некӯ тарзе нигаҳ доред ё ба некӯ тарзе раҳо созед. Ва то бар онҳо зиён бирасонед, ё ситам бикунед, нигоҳашон мадоред. Ва ҳар кас, ки чунин кунад, ба худаш ситам карда аст. Ва оёти Худоро ба масхара магиред ва аз неъмате, ки Худо ба шумо додааст ва аз оёту ҳикмате, ки барои панди шумо фиристодааст, ёд кунед ва аз Худо битарсед ва бидонед, ки ӯ ба ҳама чиз огоҳ аст!'),
(239, 2, 232, 'Ва чун занонро талоқ додед ва мӯҳлаташон гузашт, манъ накунед, ки ба никоҳи ҳамсарони худ — ҳар гоҳ ки миёнашон ризояте ҳосил шуда бошад — дароянд. Касе, ки аз шумо ба Худо ва рӯзи қиёмат имон оварда бошад, инчунин панд гирад. Ва ин шуморо беҳтар ва ба покӣ наздиктар аст. Худо медонад ва шумо намедонед!'),
(240, 2, 233, 'Модароне, ки мехоҳанд шир доданро ба фарзандони худ комил созанд, ду соли тамом ширашон бидиҳанд. Хӯроку либоси онон ба ваҷҳе некӯ бар ӯҳдаи соҳиби фарзанд аст ва ҳеҷ кас беш аз қудраташ таклиф намешавад. Набояд ҳеҷ модаре ба хотири фарзандаш зиёне бубинад ва ҳеҷ падаре ба хотири фарзандаш. Ва вориси падар низ чунин бар ӯҳда дорад. Ва агар падару модар бихоҳанд, бо ризоят ва машварати якдигар фарзандашонро аз шир бозгиранд, муртакиби гуноҳе нашудаанд. Ва ҳар гоҳ бихоҳед касе дигарро ба шир додани фарзандатон гиред, агар музде некӯ бипардозед, гуноҳе нест. Аз Худо битарсед ва бидонед, ки Ӯ ба коре, ки мекунед, биност!'),
(241, 2, 234, 'Касоне, ки аз шумо бимиранд ва заноне бар ҷой гузоранд, он занон бояд,, ки чаҳор моҳу даҳ рӯз интизор кашанд; ва чун муддаташон тамом шуд, агар дар бораи хеш коре шоиста кунанд, бар шумо гуноҳе нест, ки Худо ба корҳое, ки мекунед, огоҳ аст!'),
(242, 2, 235, 'Ва агар ба киноят аз он занон хостгорӣ кунед ё андешаи худ дар дил ниҳон доред, гуноҳе бар шумо нест. Зеро Худо медонад, ки аз онҳо ба зудӣ ёд хоҳед кард. Вале дар ниҳон бо онон ваъда наниҳед, магар он ки ба тарзе некӯ сухан гӯед; ва оҳанги бастани никоҳ макунед то он муддат тамом шавад ва бидонед, ки Худо ба он чӣ дар дил доред, огоҳ аст. Аз Ӯ битарсед ва бидонед, ки Ӯ омурзанда ва пурсабр аст!'),
(243, 2, 236, 'Агар занонеро, ки бо онхо наздикӣ накардаед ва маҳре барояшон муқаррар надоштаед, талоқ гӯед, гуноҳе накардаед. Вале онҳоро ба чизе баҳраманд созед: тавонгар ба қадри тавонаш ва дарвеш ба қадри тавонаш. Ин корест шоистаи некӯкорон.'),
(244, 2, 237, 'Агар барояшон маҳре муайян кардаед ва пеш аз наздикӣ талоқашон мегӯед, нисфи он чӣ муқаррар кардаед, бипардозед; магар он ки эшон худ ё касе, ки ақди никоҳ ба дасти ӯст, онро бубахшад. Ва бахшидани шумо ба парҳезгорӣ наздиктар аст. Ва фазилатро миёни худ фаромӯш макунед, ки Худо ба корҳое, ки мекунед, биност!'),
(245, 2, 238, 'Намозҳо ва намози миёнаро пос доред ва бо итоат барои Худо қиём кунед.'),
(246, 2, 239, 'Ва агар аз душман бимнок будед, пиёда ё савора намоз кунед. Ва чун эмин шудед, Худоро ёд кунед, зеро ба шумо чизҳое омухт, ки намедонистед.'),
(247, 2, 240, 'Мардоне аз шумо, ки мемиранд ва заноне бар ҷой мегузоранд, бояд ки дар бораи занони худ васият кунанд, ки нафақаи онҳоро ба муддати як сол бидиҳанд ва аз хона берунашон накунанд. Пас агар худ хориҷ шаванд, бо нияти шоистае, ки барои худ мекунанд, гуноҳе бар шумо нест. Ва Худо ғолибу ҳаким аст.'),
(248, 2, 241, 'Барои занони талоқшуда насибест шоиста, ки бар гардани мардони парҳезгор бошад.'),
(249, 2, 242, 'Худо оёташро барои шумо инчунин баён мекунад, бсшад, ки таъаққул (фаҳм) кунед!'),
(250, 2, 243, 'Оё он ҳазорон танро надидаӣ, ки аз бими марг аз хонаҳои хеш берун рафтанд? Сипас, Худо ба онҳо гуфт: «Бимиред!» Он гоҳ, ҳамаро зинда сохт. Худо ба мардум неъмат медиҳад, вале бештари мардум шукри неъмат ба ҷой намеоваранд!'),
(251, 2, 244, 'Дар роҳи Худо ҷанг кунед ва бидонед, ки Худо шунаво ва доност!'),
(252, 2, 245, 'Кист, ки ба Худо қарзи нек диҳад, то Худо бар он чанд баробар афзояд? Худо тангдастӣ диҳад ва тавонгарӣ бахшад ва шумо ба сӯи Ӯ бозгардонда мешавед!'),
(253, 2, 246, 'Оё он гурӯҳ аз банӣ-Исроилро пас аз Мӯсо надидӣ, ки ба яке аз паёмбарони худ гуфтанд: «Барои мо подшоҳе қарор кун, то дар роҳи Худо биҷангем». Гуфт: «Напиндоред, ки агар ҷанг бар шумо муқаррар шавад, аз он сар бозхоҳед зад?» Гуфтанд: «Чаро дар роҳи Худо наҷангем, дар ҳоле, ки мо аз сарзаминамон берун ронда шудаем ва аз фарзандонамон ҷудо афтодаем?» Ва чун ҷанг бар онҳо муқаррар шуд, ҷуз андаке аз он рӯй бартофтанд. Худо ба ситамкорон огоҳ аст!'),
(254, 2, 247, 'Пайғамбарашон ба онҳо гуфт: «Худо Толутро подшоҳи шумо кард». Гуфтанд: «Чӣ гуна ӯро бар мо подшоҳӣ бошад? Мо сазовортар аз ӯ ба подшоҳӣ ҳастем ва уро дороии чандоне надодаанд». Гуфт: «Худо ӯро бар шумо баргузидааст ва ба донишу тавони ӯ бияфзудааст ва Худо подшоҳияшро ба хар кӣ хоҳад, диҳад, ки Худо дарбаргиранда ва доност!»'),
(255, 2, 248, 'Пайғамбарашон гуфт, ки нишони подшохии ӯ ин аст, ки сандуқе, ки оромии дил аз Парвардигоратон ва боқии мероси хонадони Мӯсо ва хонадони Ҳорун дар он аст ва фариштагонаш мебардоранд, назди шумо ояд. Агар мӯъмин бошед, ин барон шумо ибратест.'),
(256, 2, 249, 'Чун Толут сипоҳашро ба роҳ андохт, гуфт: «Худо шуморо ба ҷӯи обе меозмояд: ҳар кӣ аз он бихӯрад, аз ман нест ва ҳар кӣ аз он нахӯрад ё танҳо кафе биёшомад, аз ман аст». Ҳама ҷуз андаке аз он нӯшиданд. Чун ӯ ва мӯъминоне, ки ҳамроҳаш буданд, аз наҳр гузаштанд, гуфтанд: «Имрӯз моро тавони Ҷолут ва сипоҳаш нест». Ононе, ки медонистанд, ки бо Худо дидор хоҳанд кард, гуфтанд; «Ба хости Худо чӣ бисёр гурӯҳи андаке, ки бар гурӯҳи бисёре ғалаба кунад», ки Худо бо касонест, ки собиранд.'),
(257, 2, 250, 'Чун бо Ҷолут ва сипоҳаш рӯ ба рӯ шуданд, гуфтанд: «Эй Парвардигори мо, бар мо сабр бидеҳу моро собитқадам гардон ва бар кофирон пирӯз соз!»'),
(258, 2, 251, 'Пас ба хости Худо онҳоро бишикастанд ва Довуд Ҷолутро бикушт ва Худо ба ӯ подшоҳиву ҳикмат дод ва он чӣ мехост, ба ӯ биёмухт. Ва агар Худо баъзе аз мардумро ба василаи баъзе дигар дафъ намекард, замин фасод мешуд, вале Худо бар ҷахониён фазлу карами хешро арзонӣ медорад.'),
(259, 2, 252, 'Ин аст оёти Худо, ки ба роста бар ту мехонем ва ту аз паёмбарон ҳастӣ!'),
(260, 2, 253, 'Баъзе аз ин паёмбаронро бар баъзе дигар бартарӣ додем. Худо бо баъзе сухан гуфт ва баъзеро ба дараҷоте боло кард. Ва ба Исо бинни Марям; мӯъҷизаҳо додем ва ӯро ба рӯҳулқудус ёрӣ кардем. Ва агар Худо мехост, мардуме, ки баъд аз онҳо буданд, пас аз он, ки хуҷҷатҳо бар онон ошкор шуда буд, бо якдигар куштор намекарданд. Вале онон ихтилоф карданд: баъзе мӯъмин буданд ва баъзе кофир шуданд. Ва агар Худо мехост, бо ҳам қитол (куштор) намекарданд, вале Худо ҳар чӣ хоҳад мекунад.'),
(261, 2, 254, 'Эй касоне, ки имон овардаед, пеш аз он ки он рӯзе фаро расад, ки на дар он хариду фурӯше бошад ва на дӯстиву шафоъате, аз он чӣ ба шумо рузӣ додаем, садақа кунед. Ва кофирон худ ситамкоронанд.'),
(262, 2, 255, 'Оллоҳ худоест, ки ҳеҷ худое ҷуз Ӯ нест. Зиндаву поянда аст. На хоби сабук Ӯро фаро мегирад ва на хоби сангин. Аз они Ӯст, ҳар чӣ дар осмонҳо ва замин аст. Чӣ касе ҷуз ба иҷозати Ӯ дар назди Ӯ шафоъат кунад? Он чиро, ки пеши рӯ ва он чиро, ки пушти сарашон аст, медонад ва ба илми Ӯ ҷуз он чӣ Худ хоҳад, иҳота натавонанд ёфт. Курит Ӯ осмонҳо ва заминро иҳота дорад. Нигаҳдории онҳо бар Ӯ душвор нест. Ӯ баландпояву бузург аст!'),
(263, 2, 256, 'Дар дин ҳеч маҷбурие нест. Ҳидоят ва гумроҳӣ равшан шудааст. Пас ҳар кас, ки ба тоғут кофир шавад ва ба Худой имон оварад, ба чунон риштаи устуворе чанг зада, ки канданиаш набошад. Худо шунаво ва доност!'),
(264, 2, 257, 'Худо ёвари мӯъминон аст. Онҳоро аз торикиҳо ба равшанӣ мебарад. Вале онон, ки кофир шудаанд, тоғут (бутҳо ва худоҳои дурӯғин) ёвари онҳост, ки, онҳоро аз равшанн ба торикиҳо мекашанд. Инҳо ҷаҳаннамиёнанд ва ҳамеша дар он хоҳанд буд.'),
(265, 2, 258, 'Он касеро, ки Худо ба ӯ подшоҳӣ арзонӣ карда буд, надидӣ, ки бо Иброҳим дар бораи Парвардигораш ҳуҷҷаторӣ мекард? Он гоҳ, ки Иброҳим гуфт: «Парвардигори ман зинда мекунад ва мемиронад». Ӯ гуфт: «Ман низ зинда мекукам ва мемиронам». Иброзҳим гуфт: «Худо хуршедро аз машриқ, бармеоварад, ту онро аз мағриб баровар». Он кофир ҳайрон шуд. Зеро Худо ситамкорокро ҳидоят намекунад!'),
(266, 2, 259, 'Ё монанди он кас, ки ба деҳе расид. Деҳе, ки сақфҳои биноҳояш фурӯ рехта буд. Гуфт: «Аз куҷо Худо ин мурдагонро зинда кунад?» Худо ӯро ба муддати сад сол миронд. Он гоҳ зиндааш кард. Ва гуфт: «Чӣ муддат дар ин ҷо будӣ?» Гуфт; «Як рӯз ё қисмате аз рӯз». Гуфт: «На, сад сол аст, ки дар ин ҷо будаӣ. Ба таъому обат бингар, ки тағйир накардааст ва ба харат бингар, мехоҳем туро барои мардумол ибрате гардонем, бингар, ки устухонҳоро чӣ гуна ба ҳам мепайвандем ва гӯшт бар он мепӯшонем». Чун қудрати Худо бар ӯ ошкор шуд, гуфт: «Медонам, ки Худо бар ҳар коре тавоност!'),
(267, 2, 260, 'Иброҳим гуфт: «Эй Парвардигори ман, ба ман бинамой, ки мурдагонро чӣ гуна зинда месозӣ». Гуфт: «Оё ҳанӯз имон наёвардаӣ?» Гуфт; «Бале, валекин мехоҳам, ки дилам ором ёбад». Гуфт; «Чаҳор парранда бигиру гӯшти онҳоро ба ҳам биёмез ва ҳар ҷузъе аз онҳоро бар кӯҳе бинеҳ. Пас онҳоро фарёд кун. Шитобон назди ту меоянд ва бидон, ки Худо пирӯзманд ва ҳаким аст».'),
(268, 2, 261, 'Мисоли онҳо ки моли худро дар роҳи Худо харч мекунанд, мисоли донаест, ки ҳафт хӯша бароварад ва дар ҳар хӯшае сад дона бошад. Худо подоши ҳар киро, ки бихоҳад, чанд баробар мекунад. Худо кушоишдиҳанда ва доност!'),
(269, 2, 262, 'Музди касоне, ки молҳои худро дар роҳи Худо харҷ мекунанд ва пас аз харҷ миннате намениҳанд ва озоре намерасонанд, бо Парвардигорашон аст. На бимнок мешаванд ва на андӯҳгин.'),
(270, 2, 263, 'Гуфтори некӯ ва бахшоиш беҳтар аз садақаест, ки озоре ба дунбол дошта бошад. Худо бениёз ва бурдбор аст!'),
(271, 2, 264, 'Эй касоне, ки имон овардаед, моианди он кас, ки амволи худро аз рӯи риё ва худнамоӣ харҷ мекунад ва ба Худову рӯзи қиёмат имон надорад, садақаҳои хешро ба миннат ниҳодан ва озор расонидан ботил макунед. Масали ӯ мисли санги софест, ки бар рӯи он хок нишаста бошад. Ба ногоҳ бороне тунд фурӯ борад ва он сангро ҳамчунон киштнопазир боқӣ гузорад. Чунин касон аз он чӣ кардаанд, нафъе намебаранд, ки Худо кофиронро ҳидоят намекунад.'),
(272, 2, 265, 'Ва мисоли касоне, ки амволи хешро барои талаби ризой Худо аз рӯи яқин ва эътиқод харҷ мекунанд, мисли бӯстонест бар теппае, ки бороне сахт бар он борад ва дучандон мева диҳад ва агар на бороне сахт, нарм бороне бар он борад. Худо ба корҳои шумо биност!'),
(273, 2, 266, 'Оё аз миёни шумо касе ҳает, ки дӯст дошта бошад, ки ӯро бӯстоне аз хурмову ангур буда бошад ва ҷӯйҳо дар пои дарахтонаш ҷорӣ бошад ва ҳар гуна мевае диҳад ва худ пир шуда ва фарзандоне нотавон дошта бошад, ба ногоҳ, гирдбоде оташнок дар он бӯстон афтад ва бисӯзад? Худо оёти худро барои шумо инчунин баён мекунад, бошад, ки бияндешед!'),
(274, 2, 267, 'Эй касоне, ки имон овардаед, аз дастовардҳои некӯи хеш ва аз он чӣ бароятон аз замин рӯёнидем, харҷ кунед, на аз чизҳои нопоку бад, ки худ онҳоро ҷуз аз рӯи чашмпӯшӣ намеситонед. Ва бидонед, ки Худо бениёз ва сазовори ситоиш аст.'),
(275, 2, 268, 'Шайтон шумор аз бенавоӣ метарсонад ва ба корҳои зишт вомедорад, дар ҳоле ки Худо шуморо ба омурзиши хеш ва афзунӣ ваъда медиҳад. Худо кушоишдиҳанда ва доност!'),
(276, 2, 269, 'Ба ҳар ки хоҳад, ҳикмат медиҳад. Ва ба ҳар ки ҳикмат медиҳад, некии фаровон дода шуда. Ва ҷуз хирадмандон панд напазиранд.'),
(277, 2, 270, 'Хар чиро садақа ё назр кардаед, Худо ба он огоҳ аст. Ва ситамкоронро ҳеҷ ёреву мададгоре нест!'),
(278, 2, 271, 'Агар ошкоро садақа диҳед, коре некуст ва агар пинҳонӣ ба бенавоён садақа диҳед, некӯтар аст ва гуноҳони шуморо дур созад. Ва Худо ба корҳое, ки мекунед, огоҳ аст!'),
(279, 2, 272, 'Ҳидоят ёфтани онон бар ӯҳдаи ту нест, балки Худост, ки ҳар киро, ки бихоҳад, ҳидоят мекунад. Ва ҳар моле, ки садақа мекунед, савобаш азони худи шумост ва ҷуз барои хушнудии Худо чизе харҷ макунед ва ҳар чӣ харҷ кунед, мукофоти он ба шумо мерасад ва бар шумо ситам нахоҳад шуд.'),
(280, 2, 273, 'Ин садақот аз они бенавоёнест, ки худро дар тоъати Ҳақ банд кардаанд ва дар талаби қувват нотавонанд ва ончунон дар пардаи ифофанд (худдорӣ), ки ҳар кӣ ҳоли эшон надонад, пиндорад, ки аз тавонгаронанд. Ононро аз симояшон мешиносӣ, ки ба исрор (бо хирагӣ) аз касе чизе нахоҳанд. Ва ҳар моле, ки хайр мекунед, Худо ба он огоҳ аст!'),
(281, 2, 274, 'Онон, ки амволи хешро дар шабу рӯз, дар пинҳону ошкор хайр мекунанд аҷрашон бо Парвардигорашон аст, на бимнок мешаванд ва на ғамгин!'),
(282, 2, 275, 'Онон, ки рибо мехӯранд, дар қиёмат чун касоне аз қабр бармехезанд, ки ба афсуни шайтон девона шуда бошанд. Ва ин ба ҷазои он аст, ки гуфтанд: «Рибо низ чун хариду фурӯш аст». Дар ҳоле, ки Худо хариду фурӯшро ҳалол ва риборо ҳаром кардааст. Ҳар кас, ки панди Худо ба ӯ расид ва аз рибохӯрӣ бозистод, Худо аз гуноҳони пешини ӯ даргузарад ва кораш ба Худо вогузор шавад. Ва онон, ки бад-он кор бозгарданд, аҳли ҷаҳаннаманд ва ҷовидона дар он хоҳанд буд!'),
(283, 2, 276, 'Худованд риборо ночиз мегардонад ва садақотро афзунӣ медиҳад ва ҳеҷ куфронкунандаи гунаҳкорро дӯст надорад!'),
(284, 2, 277, 'Онон, ки имон овардаанд, ва корҳои некӯ кардаанд ва намоз хондаанду закот додаанд, муздашон бо Парвардигорашон аст. На бимнок мешаванд ва на ғамгин!'),
(285, 2, 278, 'Эй касоне, ки имон овардаед, аз Худо битарсед ва агар имон овардаед, аз рибо ҳар чӣ боқӣ мондааст, тарк кунед.'),
(286, 2, 279, 'Ва ҳар гоҳ чунин накунед, пас ҷанг бо Худову расули Ӯро эълом кунед. Ва агар тавба кунед, асли сармоя аз они шумост. Дар ин ҳол на ситам кардаед ва на тан ба ситам додаед!'),
(287, 2, 280, 'Ва агар қарздор тангдаст бувад, мӯҳлате бояд то тавонгар гардад. Ва агар доно бошед, донед, ки чун бар ӯ бубахшоед, бароятон беҳтар аст!'),
(288, 2, 281, 'Аз он рӯз, ки сӯи Худо бозмегардед ва ба ҳар кас подоши амалаш ба тамомӣ дода мешавад ва ситаме намебинад, битарсед.'),
(289, 2, 282, 'Эй касоне, ки имон овардаед, чун қарзе то муддате муъайян ба якдигар дихед, онро бинависед. Ва бояд дар байни шумо котибе бошад, ки онро ба дурустӣ бинависад, Ва котиб набояд, ки дар навиштан аз он чӣ Худо ба ӯ омӯхтааст, сарпечӣ кунад. Ва мадюн (қарздор) бояд, ки бар котиб имло кунад ва аз Оллоҳ Парвардигори худ битарсад ва аз он хеҷ накоҳад. Агар қарздор нодон ё сағир бувад ё худ навиштан наметавонад, валии ӯ аз рӯи адолат бинависад. Ва ду шоҳиди мард ба шаҳодат гиред. Агар ду мард набувад, як марду ду зан, ки ба онҳо ризоият диҳед, шаҳодат бидиҳанд, то агар яке фаромӯш кард, дигаре ба ёдаш биёварад. Ва шоҳидон чун ба шаҳодат даъват шаванд, набояд, ки аз шаҳодат худдорӣ кунанд. Ва аз навиштани муддати дайки (карзи) худ, чӣ хурд ва чӣ бузург, малӯл машавед. Ин равиш дар назди Худо одилонатар аст ва шаҳодатро устувордорандатар ва шакку шубҳаро дуркунандатар. Ва ҳар гоҳ муомалаи нақдӣ бошад, агар барои он санаде нанависед, муртакиби гуноҳе нашудаед. Ва чун муомалае кунед, шоҳиде гиред. Ва набояд ба котибу шоҳид зиёне бирасонод, ки агар чунин кунед, нофармонӣ кардаед. Аз Худой битарсед. Худо шуморо таълим медиҳад ва Ӯ бар ҳар чизе огоҳ аст!'),
(290, 2, 283, 'Ҳар гоҳ дар сафар будед ва котибе наёфтед, бояд чизе ба гарав гирифта шавад ва агар касе аз шумо дигареро боварӣ кард, он кас, ки боварӣ карда шуд, амонатро боз диҳад ва бояд аз Оллоҳ, Парвардигораш битарсад. Ва шоҳидиро пинҳон макунед. Ҳар кас, ки шоҳидиро пинҳон кунад, ба дил гуноҳкор аст ва Худо аз коре, ки мекунед, огоҳ аст!'),
(291, 2, 284, 'Аз они Худост, ҳар чӣ дар осмонҳову замин аст. Он чиро, ки дар дил доред, хоҳ, ошкораш созед ё пушидааш доред, Худо шуморо ба он бозхост хоҳад кард. Пас ҳар киро, ки бихоҳад, меомурзад ва ҳар киро бихоҳад, азоб мекунад. Ва Худо бар ҳар коре тавоност!'),
(292, 2, 285, 'Паёмбар худ ба он чӣ аз ҷониби Парвардигораш ба ӯ нозил шуда, имон дорад. Ва ҳамаи мӯъминон ба Худову фариштагонаш ва китобҳояш ва паёмбаронаш имон доранд. Миёни ҳеҷ як паёмбаронаш фарқе намениҳем. Гуфтанд: «Шунидем ва итоъат кардем, эй Парвардигори мо, омурзиши туро хосторем, ки бозгашти ҳама ба сӯи туст!»'),
(293, 2, 286, 'Худо ҳеҷ касро ҷуз ба андозаи тоқаташ амр намекунад. Некиҳои ҳар кас аз они худи уст ва бадиҳояш аз они худи ӯст. «Эй Парвардигори мо, агар фаромӯш кардаем ё хатое кар даем, моро бозхост макун! Эй Парвардигори мо, он гуна ки бар умматҳои пеш аз мо таклифи вазнин ниҳодӣ, таклифи вазнин бар мо манеҳ ва он чиро, ки тоқати он надорем, бар мо таклиф макун! Гуноҳи мо бубахш ва моро биёмурз ва бар мо раҳмат овар! Ту хоҷаи мо хастӣ. Пас моро бар гурӯҳи кофирон ғолиб гардон!»');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Алиф. Лом. Мим.'),
(295, 3, 2, 'Оллоҳ аст, ки ҳеҷ худое ғайри Ӯ нест. Зинда асту поянда аст!'),
(296, 3, 3, 'Ин китобро, ки тасдиқкунандаи китобҳои пеш аз он аст, ба ҳак бар ту нозил кард ва қабл аз он Тавротро ва Инҷилро фиристод.'),
(297, 3, 4, 'барои ҳидояти мардум фиристод ва Фурқонро нозил кард. Барои онон, ки ба оёти Худо имон намеоранд, азобе сахт муҳайёст ва Худо пирӯзманду интиқомгиранда аст.'),
(298, 3, 5, 'Ҳеҷ чиз дар замину осмон бар Худо пӯшида нест.'),
(299, 3, 6, 'Ӯст, ки шуморо дар батии модароп ба ҳар сон, ки хоста бошад, сурат мебандад. Нест худое ҷуз Ӯ, ки пирӯзманду ҳаким аст!'),
(300, 3, 7, 'Ӯст, ки ин китобро бар ту нозил кард. Баъзе аз ояҳо муҳкамотанд, ин ояҳо уммулкитобанд ва баъзе ояҳо муташобеҳотанд. Аммо онҳо, ки дар дилашон майл ба ботил аст, ба сабаби фитнаҷӯӣ ва майл ба маъно аз муташобеҳот пайравй мекунанд, дар ҳоле ки маънои онро ҷуз Худо намедонад. Ва окон, ки қадам дар дониш устувор кардаанд, мегӯянд: «Мо бад-он имон овардаем. Ҳама аз ҷониби Парвардигори мост». Ва хирадмандон панд мегиранд.'),
(301, 3, 8, 'Эй Парвардигори мо, аз он пас, ки моро ҳидоят кардаӣ, дилҳои моро ба ботил моил масоз ва раҳмати худро бар мо ато бикун, ки ту бахшояндаӣ!'),
(302, 3, 9, 'Эй Парвардигори мо, ту мардумро дар он рӯзе, ки ҳеҷ шакке дар он нест, гирд меоварӣ. Ба яқин, ки Худо аз ваъда хилоф накунад!'),
(303, 3, 10, 'Кофиронро дороиҳо ва фарзандонашон ҳаргиз аз азоби Худо нараҳонад. Онҳо худ ҳезуми оташи ҷаҳаннаманд.'),
(304, 3, 11, 'Ба мисли Оли Фиръавн ва пешиниёнашон оёти моро такзиб карданд (дурӯғ бароварданд). Пас Худо ононро ба ҷазои гуноҳонашон бозхост кард. Ва уқубяти Худо сахт яст!'),
(305, 3, 12, 'Ба кофирон бигӯй: «Ба зудӣ мағлуб хоҳед шуд ва дар ҷаҳаннам, он оромгоҳи бад, гирд хоҳед омад!»'),
(306, 3, 13, 'Дар он ду гурӯҳ, ки ба ҳам расиданд, барои шумо ибрате буд: Гурӯҳе дар роҳи Худо меҷангиданд ва гурӯҳе дигар кофир буданд. Ононро ба чашми худ дучандони хеш медиданд. Худо ҳар касро, ки бихоҳад, ёрӣ диҳад. Ва соҳибназаронро дар ин ибратест!'),
(307, 3, 14, 'Дар чашми мардум ороиш ёфтааст ишқ ба орзуҳои нафсонӣ ва дӯст доштани занону фарзандон ва ҳамёнҳои зару сим ва аспони нишонадор ва чорпоён ва зироат. Ҳамаи инҳо матоъи зиндагии инҷаҳонӣ ҳастанд, дар ҳоле, ки бозгаштангоҳи хуб назди Худост!'),
(308, 3, 15, 'Бигӯ: «Оё шуморо ба чизҳое беҳтар аз инҳо огоҳ кунам?» Барои онон, ки парҳезгорӣ пеша кунанд, дар назди Парвардигорашон биҳиштҳоест, ки наҳрҳо дар он равон аст. Инон бо занони покиза дар айни хушнудии Худо ҷовидона дар он ҷо хоҳанд буд. Ва Худо аз ҳоли бандагон огоҳ аст:'),
(309, 3, 16, 'Касоне, ки мегӯянд: «Эй Парвардигори мо, имон овардем. Гуноҳони моро биёмурз ва моро аз азоби оташ ҳифз кун»;'),
(310, 3, 17, 'собирону ростгӯён ва фармонбардорону нафақакунандагон ва онон, ки дар саҳаргоҳон омурзиш металабанд.'),
(311, 3, 18, 'Оллоҳ ҳукм кард ва фариштагону донишмандон низ, ки ҳеҷ худое барпойдорандаи адл ҷуз Ӯ нест. Худое ҷуз Ӯ нест, ки пирӯзманду ҳаким аст!'),
(312, 3, 19, 'Албатта дин дар назди Худо дини ислом аст. Ва аҳли китоб муқобили он рафтанд, аз он пас, ки ба ҳаққонияти он дин огоҳ, шуданд ва низ аз рӯи ҳасад. Онон, ки ба оёти Худо кофир шуданд, бидонанд, ки Ӯ ба зудӣ ба ҳисобҳо хоҳад расид!'),
(313, 3, 20, 'Агар бо ту ба доварӣ бархезанд, бигӯй: «Ману пайравонам дар дини хеш ба Худо ихлос намудем». Ба аҳли китоб ва мушрикон бигӯ: «Оё шумо ҳам ба Худо ихлос намудед?» Агар ихлос варзидаанд, пас ҳидоят ёфтаанд ва агар рӯйгардон шудаанд, бар ту таблиғ асту бас ва Худо бандагонро мебинад.'),
(314, 3, 21, 'Касоне, ки ба оёти Худо имон намеоваранд ва паёмбаронро ба ноҳақ мекушанд ва мардумеро, ки аз рӯи адл фармон медиҳанд, мекушанд, ба азобе дардовар хабар деҳ!'),
(315, 3, 22, 'Аъмоли инҳо дар дунё ва охират нобуд шудааст ва ҳеҷ ёваре надоранд!'),
(316, 3, 23, 'Оё ононро надидӣ, ки аз китоб баҳрае ёфта буданд, чун даъват шуданд то китоби Худо дар бораи онҳо ҳукм кунад, гурӯҳе аз онҳо бозгаштанд ва рӯй гардонӣ карданд?'),
(317, 3, 24, 'Ва ин бад-он сабаб буд, ки мегуфтанд: «Ҷуз чанд рӯзе оташи дӯзах ба мо нарасад». Ва ин дурӯғ, ки бар худ баста буданд, дар дини худ фиребашон дод.'),
(318, 3, 25, 'Ҳолашон чӣ гуна хоҳад буд дар он рӯз бешубҳа, вақте, ки ҳамаро гирд оварем, то подоши амали ҳар кас дода шавад, бе он ки бар касе ситаме равад?'),
(319, 3, 26, 'Бигӯ: «Бор Худоё, Туӣ - - дорандаи мулк! Ба ҳар кӣ бихоҳӣ, мулк медиҳӣ ва аз ҳар кӣ бихоҳӣ, мулк меситонӣ! Ҳар касро, ки бихоҳӣ, иззат медиҳӣ ва ҳар касро, ки бихоҳӣ, хорӣ медиҳӣ! Ҳамаи некиҳо ба дасти туст ва ту бар ҳар коре тавоноӣ!'),
(320, 3, 27, 'Аз шаб мекоҳӣ (кӯтоҳ мекунӣ) ва ба рӯз меафзоӣ ва аз рӯз мекоҳӣ ва ба шаб меафзоӣ! Зиндаро аз мурда берун меоварӣ ва мурдаро аз зинда. Ва ба ҳар ки бихоҳӣ, беҳисоб рӯзӣ медиҳӣ!'),
(321, 3, 28, 'Набояд мӯъминон кофиронро ба ҷои мӯъминон ба дӯстӣ гиранд. Пас ҳар кӣ чунин кунад, ӯро бо Худо робитае нест. Магар ин ки аз онҳо бимнок бошед. Ва Худо шуморо аз худаш метарсонад, ки бозгашт ба сӯӣ Ӯст».'),
(322, 3, 29, 'Бигӯ: «Ҳар чӣ дар дил доред, чӣ пинҳонаш кунед ва чӣ ошкораш созед. Худо ба он огох, аст. Ӯ ҳар чиро, ки дар осмонҳо ва замин аст, медонад ва бар ҳар коре тавоност!'),
(323, 3, 30, 'Рӯзе, ки ҳар кас корҳои нек ва корҳои бади худро дар баробари худ ҳозир бинад, орзу кунад, ки эй кош, миёни ӯ ва кирдори бадаш масофае бузург бувад. Худованд шуморо аз Худаш метарсонад. Ва Худо ба бандагонаш меҳрубон аст!»'),
(324, 3, 31, 'Бигӯ: Агар Худоро дӯст медоред, ба ман пайравӣ кунед, то Ӯ низ шуморо дӯст бидорад ва гуноҳонатонро биёмурзад, ки омурзанда ва меҳрубон аст!»'),
(325, 3, 32, 'Бигӯ: «Аз Худо ва расулаш фармон бибаред. Пас агар рӯйгардон шуданд, бидоканд, ки Худо кофиронро дӯст надорад!»'),
(326, 3, 33, 'Худо Одаму Нӯҳ, ва хонадони Иброҳиму хонадони Имронро бар ҷаҳониён бартарӣ дод:'),
(327, 3, 34, 'Фарзандоне буданд баъзе аз насли баъзе дигар падидомада. Ва Худо шунаво ва доност!'),
(328, 3, 35, 'Ва зани Имрон гуфт: «Эй Парвардигори ман, назр кардам, ки он чӣ дар шикам дорам, аз кори инҷаҳонӣ озоду танҳо дар хидмати Ту бошад. Ин назрро аз ман бипазир, ки Ту шунаво ва доноӣ».'),
(329, 3, 36, 'Чун фарзанди хеш бизод, гуфт: «Эй Парвардигори ман, ин ки зойидаам, духтар аст — ва Худо ба он чӣ зоида буд, донотар аст — ва писар чун духтар нест. Ӯро Марям ном ниҳодам. Ӯ ва фарзандашро аз шайтони раҷим дар паноҳи Ту месупорам».'),
(330, 3, 37, 'Пас Парвардигораш он духтарро ба некӣ аз ӯ пазируфт ва ба тарзе писандида парваришаш дод ва Закариёро ба сарпарастии ӯ вобаста кард. Ва ҳар вақт, ки Закариё ба меҳроб назди ӯ мерафт, пеши ӯ хӯрданӣ меёфт, Мегуфт: «Эй Марям, инҳо барои ту аз куҷо мерасад?» Марям мегуфт: «Аз ҷониби Худо;» зеро Ӯ ҳар касро, ки бихоҳад, беҳисоб рӯзӣ медиҳад.'),
(331, 3, 38, 'Дар он ҷо Закариё Парвардигорашро нидо дод ва гуфт: «Эй Парвардигори ман, маро аз ҷониби худ фарзанде покиза ато кун, албатта Ту дуъоро мешунавӣ».'),
(332, 3, 39, 'Пас-ҳамчунон, ки дар меҳроб ба намоз истода буд, фариштагон нидояш доданд: «Худо туро ба Яҳё хушхабар медиҳад. Ӯ калимаи Худоро тасдиқ мекунад ва худ бузургест безор аз занон ва паёмбарест аз шоистагон».'),
(333, 3, 40, 'Гуфт: «Эй Парвардигори ман, чӣ гуна маро писаре бошад, дар ҳоле ки ба пирӣ расидаам ва занам нозост?» Гуфт; Ба он сон, ки Худо ҳар чӣ бихоҳад, мекунад».'),
(334, 3, 41, 'Гуфт: «Эй Парвардигори ман, барон мая нишонае падидор кун». Гуфт: «Нишони ту ин аст, ки се рӯз бо мардум сухан нагӯӣ, магар ба ишора ва Парвардигоратро фаровон ёд кун ва дар шабонгоҳу бомдод Ӯро шукр гӯй.'),
(335, 3, 42, 'Ва фариштагон гуфтанд: «Эй Марям, Худо туро инхитоб кард ва покиза сохт ва бар занони ҷаҳон бартарӣ дод.'),
(336, 3, 43, 'Эй Марям, аз Парвардигорат итоъат кун ва саҷда кун ва бо намозгузорон намоз бихон!»'),
(337, 3, 44, 'Инҳо аз хабарҳои ғайб аст, ки ба ту ваҳй мекунем. Вагарна он гоҳ, ки қуръа заданд, то чӣ касе аз миёнашон ӯҳдадори нигаҳдории Марям шавад ва он гоҳ ки корашон ба низоъ кашид, ту дар наздашон набудӣ.'),
(338, 3, 45, 'Фариштагон гуфтанд; «Эй Марям, Худо туро ба калимаи Худ мужда медиҳад: Номи ӯ Масеҳ — Исо писари Марям аст. Дар дунё ва охират обрӯманд ва аз наздикон аст.'),
(339, 3, 46, 'Бо мардум ҳамчунон, ки дар бузургӣ, дар гаҳвора сухан мегӯяд ва аз шоистагон аст».'),
(340, 3, 47, 'Марям гуфт: «Эй Парвардигори ман, чӣ гуна маро фарзанде бошад, дар ҳоле ки одамӣ ба ман даст назадааст?» Гуфт; «Ба он сон, ки Худо ҳар чӣ бихоҳад, меофаринад. Чун иродаи чизе кунад, ба ӯ гӯяд: «Мавҷуд шав!» — пас мавҷуд мешавад.'),
(341, 3, 48, 'Худо ба ӯ китобу ҳикмат ва Тавроту Инҷил меомӯзад.'),
(342, 3, 49, 'Ва ба пайғамбарӣ бар банӣ-Исроилаш мефиристад», ки ман ба мӯъҷизае аз Парвардигоратон назди шумо омадаам. Бароятон аз лой чизе чун парранда месозам ва дар он медамам, ба амри Худо паррандае шавад ва кӯри модарзодро ва пес гирифтаро шифо медиҳам. Ва ба фармони Худо мурдаро зинда мекунам. Ва ба шумо мегӯям, ки чӣ хурдаед ва дар хонаҳои худ чӣ захира кардаед. Агар аз мӯъминон бошед, инҳо барои шумо нишонаҳои ҳаққонияти ман аст!'),
(343, 3, 50, 'Тавроти ҳозирро тасдиқ мекунам ва баъзе аз чизҳоеро, ки бар шумо ҳаром шуда, ҳалол мекунам. Бо нишонӣ аз Парвардигоратон назди шумо омадаам. Аз Худо битарсед ва ба ман итоат кунед!'),
(344, 3, 51, 'Оллоҳ Парвардигори ману шумост. Бипарастедаш, ки роҳи рост ин аст!'),
(345, 3, 52, 'Чун Исо дарёфт, ки ба ӯ имон намеоваранд, гуфт: «Чӣ касоне дар роҳи Худо ёрони мананд?» Ҳавориён гуфтанд: «Мо ёрони Худоем. Ба Худо имон овардем. Шаҳодат деҳ ки мо таслим ҳастем.'),
(346, 3, 53, 'Эй Парвардигори мо, ба он чӣ нозил кардаӣ, имон овардем ва ба расул пайравӣ кардем, моро дар шумори гувоҳидиҳандагон бинавис!»'),
(347, 3, 54, 'Онон макр карданд ва Худо ҳам макр кард ва Худо беҳтарини макркунандагон аст.'),
(348, 3, 55, 'Он гоҳ Худо гуфт: «Эй Исо, ман туро мемиронам ва ба сӯи худ бармеоварам ва аз кофирон дур месозам ва то рӯзи қиёмат ононро, ки ба ту паиравӣ кунанд, болои кофирон қарор хоҳам дод. Сипас бозгашти ҳамаи шумо ба сӯи Ман аст ва Ман дар он чӣ ихтилоф мекардед, миёнатон ҳукм мекунам.'),
(349, 3, 56, 'Аммо ононро, ки кофир шуданд, дар дунёву охират ба сахтӣ азоб хоҳам кард ва онҳоро мададгороне нест!»'),
(350, 3, 57, 'Аммо онон, ки имон оварданд ва корҳои нек карданд, Худо муздашонро ба тамомӣ хоҳад дод. Худо ситамкорокро дӯст надорад!'),
(351, 3, 58, 'Инҳо, ки бар ту мехонем, аз оёт ва паидҳои ҳикматомез аст.'),
(352, 3, 59, 'Мисоли Исо дар назди Худо чун мисоли Одам аст, ки ӯро аз хок биёфарид ва ба ӯ гуфт: «Мавҷуд шав». Пас мавҷуд шуд.'),
(353, 3, 60, 'Ин сухапи ҳақ аз ҷониби Парвардигори, ту аст. Аз шаккунандагон мабош.'),
(354, 3, 61, 'Аз он пас, ки ба огоҳӣ расидаӣ, ҳар кас, ки дар бораи ӯ бо ту ҷидол кунад, бигӯ: «Биёед, то ҳозир оварем мо фарзандони худро ва шумо фарзандони худро, мо занони худро ва шумо занони худро, мо бародарони худро ва шумо бародарони худро. Он гоҳ, дуову тазарруъ кунем ва лаънати Худоро бар дурӯғгӯён бифиристем!»'),
(355, 3, 62, 'Ҳамоно ин достокест росту дуруст. "Ҷуз Оллоҳ ҳеҷ худое нест. Ӯ Худои пирӯзманду ҳаким аст!'),
(356, 3, 63, 'Агар рӯйгардон шаванд, Худованд муфсидонро мешиносад!'),
(357, 3, 64, 'Бигӯ: «Эй аҳли китоб, биёед ба он калимае, ки пазируфтаи мову шумост, пайравӣ кунем, он ки ҷуз Худоро напарастем ва ҳеҷ чизро шарики Ӯ насозем ва баъзе аз мо баъзе дигарро ғайри Худо ба парастиш нагирад. Агар онон рӯйгардон шуданд, бигӯ: «Шоҳид бошед, ки мо мусалмон ҳастем».'),
(358, 3, 65, 'Эй аҳли китоб, чаро дар бораи Иброҳим ҷидол мекунед, дар ҳоле ки Тавроту Инҷил баъд аз ӯ нозил шуда аст? Магар намеандешед?'),
(359, 3, 66, 'Шумо ҳамон касоне ҳастед, ки дар он ба он илм доред, баҳсу ҷидол мекардед, чаро дар он чӣ ба он нлм надоред, ҷидол мекунед? Дар ҳоле, ки Худо медонад ва шумо намедонед!'),
(360, 3, 67, 'Иброҳим на яҳудӣ буд, на насронӣ, балки ҳанифии\'" мусалмон буд. Ва аз мушрикон набуд.'),
(361, 3, 68, 'Наздиктарин касон ба Иброҳим ҳамоно пайравони ӯ ва ин паёмбару мӯъминон ҳастанд. Ва Худо ёвари мӯъминон аст.'),
(362, 3, 69, 'Гурӯҳе аз аҳли китоб дӯст доранд, ки шуморо гумроҳ кунанд ва ҳол он ки намедонанд, кн танҳо худро гумроҳ мекунанд!'),
(363, 3, 70, 'Эй аҳли китоб, бо он ки худ ба оёти Худо шоҳидӣ медиҳед, чаро инкораш мекунед?'),
(364, 3, 71, 'Эй аҳли китоб, бо он ки аз ҳақиқат огоҳед, чаро ҳақро ба ботил меомезед ва ҳақиқатро пинҳон мекунед?»'),
(365, 3, 72, 'Гурӯҳе аз аҳли китоб гуфтанд; «Дар аввали рӯз ба он чӣ бар мӯъминон нозил шудааст, имон биёваред ва дар охири рӯз инкораш кунед, то шояд аз эътиқоди хеш бозгарданд».'),
(366, 3, 73, 'Ва гуфтанд; «Ғайри пайравони дини худро тасдиқ накунед». Бигӯ; «Ҳидоят хидояти худоист». Ва агар гӯянд, ки ба дигарон ҳамон чизҳое дода шавад, ки ба шумо дода шудааст ё агар гӯянд, ки фардо дар назди Парвардигоратон бо шумо ба ҳуҷҷат меистанд, бигӯ: «Фазилат ба дасти Худост, ба ҳар кӣ хоҳад, онро ато мекунад, ки Ӯ бахшояндаву доност!»'),
(367, 3, 74, 'Ҳар киро бихоҳад хоси раҳмати худ мекунад ва Худо соҳибкарамест бисёр бузург!'),
(368, 3, 75, 'Аз миёни аҳли китоб касест, ки агар ӯро амин шуморӣ ва қинторе ба ӯ бисупорӣ, онро ба ту бозмегардонад ва аз онҳо кассет, ки агар аминаш шуморӣ ва диноре ба ӯ бисупорӣ, ҷуз ба тақозо ва талаб онро бознагардонад. Зеро мегӯяд: «Роҳи омиёни мардуми Макка бар мо баста аст ва кас моро маломат накунад». Инҳо худ медонанд, ки ба Худо дурӯғ мебанданд.'),
(369, 3, 76, 'Оре, ҳар кас, ки ба аҳди хеш вафо кунад ва парҳезгор бошад, Худо парҳезгоронро дӯст медорад.'),
(370, 3, 77, 'Касоне, ки аҳди Худо ва савгандҳои худро ба баҳои андак мефурӯшанд, дар охират насибе надоранд ва Худо дар рӯзи қиёмат на ба онон сухан мегӯяд ва на ба онон менигарад ва на ононро покиза месозад. Ва барояшон азобе дардовар аст!'),
(371, 3, 78, 'Ва аз миёни онҳо гурӯҳе ҳастанд, ки ба шеваи китоби Худо сухан мегӯянд, то пиндоред, ки он чӣ мегӯянд, аз китоби Худост, дар ҳоле ки аз китоби Худо нест. Ва мегӯянд, ки аз чониби Худо омада ва ҳол он ки аз ҷониби Худо наёмада аст. Ва худ медонанд, ки бар Худо дурӯғ мебанданд.'),
(372, 3, 79, 'Нашояд ҳеҷ башареро, ки Худо ба ӯ китобу ҳикмат ва нубувват дода бошад, он гоҳ ба мардум бигӯяд, ки бандагони ман бошед, на бандагони Худо. Ва ҳол он ки паёмбарон мегӯянд: «Ҳамчунон, ки аз китоби Худо меомӯзед ва дар он мехонед, парастандагони Худо бошед».'),
(373, 3, 80, 'Ва низ ба шумо намефармояд, ки фариштагону паёмбаронро худоён пиндоред. Оё пас аз он ки ислом овардаед, шуморо ба куфр фармон медиҳад?'),
(374, 3, 81, 'Ва Худо аз паёмбарон паймон гирифт, ки шуморо китобу ҳикмат додаам; ба паёмбаре, ки дини шуморо тасдиқ мекунад ва ба паём назди шумо меояд, имон оред ва ёриаш кунед. Гуфт: «Оё иқрор кардед ва аҳди Маро пазируфтед?» Гуфтанд: «Иқрор кардем». Гуфт; «Пас шаҳодат диҳед ва Ман низ бо шумо аз шоҳидонам».'),
(375, 3, 82, 'Ва ҳар кӣ аз он пас сарпечӣ кунад, аз фосиқон аст.'),
(376, 3, 83, 'Оё дине ҷуз дини Худо меҷӯянд, ҳол он ки он чӣ дар осмонҳо ва замин аст, хоҳу нохоҳ таслими фармони Ӯ ҳастанд ва ба назди Ӯ бозгардонда мешавед!'),
(377, 3, 84, 'Бигӯ: «Ба Худо ва он чӣ бар мо ва бар Иброҳиму Исмоил ва Исҳоқу Яъқуб ва фарзандони ӯ ва низ он чӣ бар Мӯсову Исо ва паёмбарони дигар аз ҷониби Парвардигорашон нозил шудааст, имон овардем. Миёни ҳеч як аз эшон фарқе намениҳем ва ҳама таслими иродаи Ӯ ҳастем».'),
(378, 3, 85, 'Ва ҳар кас, ки дине ҷуз ислом ихтиёр кунад, аз ӯ пазируфта нахоҳад шуд ва дар охират аз зиёндидагон хоҳад буд.'),
(379, 3, 86, 'Чӣ гуна Худо ҳидоят кунад қавмеро, ки аввал имон оварданд ва ба ҳаққонияти расул шаҳодат доданд ва далоилу оёти равшанро мушоҳида карданд, сипас кофир шуданд? Худо ситамкоронро ҳидоят намекунад!'),
(380, 3, 87, 'Ҷазояшон ин аст, ки лаънати Худо ва фариштагон ва ҳамаи мардум бар онон аст.'),
(381, 3, 88, 'Абадӣ дар лаънат бимонанд ва аз азобашон кам нашавад ва мӯҳлаташон надиҳанд,'),
(382, 3, 89, 'магар онон, ки аз он пас тавба кунанд ва ислоҳ шаванд, зеро Худо омурзанда ва меҳрубон аст!'),
(383, 3, 90, 'Тавбаи касоне, ки пас аз имон овардан кофир шуданд ва бар куфри худ афзуданд, пазируфта нахоҳад шуд. Инҳо гумроҳонанд!'),
(384, 3, 91, 'Онон, ки кофир шуданд ва кофир мурданд, агар бихоҳанд, ба андозаи ҳамаи замин тилло диҳанд ва хештанро аз азоб бозхаранд, аз онҳо пазируфта нахоҳад шуд. Барояшон азобе дарднок аст ва онҳоро ҳеҷ ёваре нест.'),
(385, 3, 92, 'Некиро дарнахоҳед ёфт, то он гоҳ ки аз он чӣ дӯст медоред, инфоқ (нафақа) кунед. Ва ҳар чӣ инфоқ мекунед, Худо ба он огоҳ аст.'),
(386, 3, 93, 'Ҳамаи таъомҳо бар банӣ-Исроил ҳалол буд, ғайри онҳо, ки пеш аз нузули Таврот Исроил бар худ ҳаром карда буд. Бигӯ: «агар рост мегӯед, Тавротро биёвареду бихонед».'),
(387, 3, 94, 'Аз он пас ҳар кӣ бар Худо дурӯғ бандад, аз ситамкорон аст.'),
(388, 3, 95, 'Бигӯ: «Худо рост мегӯяд. Дини ҳанифи Иброҳимро пайравӣ кунед ва ӯ аз мушрикон набуд.'),
(389, 3, 96, 'Нахустин хонае, ки барои мардум бино шуда, ҳамон аст, ки дар Макка аст. Хонае, ки ҷаҳониёнро сабаби баракат ва ҳидоят аст!'),
(390, 3, 97, 'Дар он ҷост оёти равшан ва мақоми Иброҳим. Ва хар кӣ ба он дохил шавад, эмин аст. Барои Худо ҳаҷҷи он хона бар касоне, ки қудрати рафтан ба онро дошта бошанд, воҷиб аст. Ва ҳар кӣ роҳи куфр пеш гирад, бидонад, кн Худо бар ҷаҳониён мӯҳтоҷ нест».'),
(391, 3, 98, 'Бигӯ: «Эй аҳли китоб, чаро оёти Худоро инкор яекунед? Худо нозир бар аъмоли шумост!»'),
(392, 3, 99, 'Бигӯ: «Эй аҳли китоб, ба чӣ сабаб онҳоро, ки имон овардаанд, аз роҳи Худо бозмедоред ва мехоҳед, ки ба роҳи каҷ раванд? Ва шумо худ ба зиштии кори хеш огоҳед ва Худо низ аз он чӣ мекунед, ғофил нест!'),
(393, 3, 100, 'Эй касоне, ки имон овардаед, агар ба гурӯҳе аз аҳли китоб итоъат кунед, шуморо аз имонатон ба куфр бозмегардонанд.'),
(394, 3, 101, 'Чӣ гуна кофир мешавед, дар ҳоле, ки оёти Худо бар шумо хонда мешавад ва расули Ӯ дар миени шумост? Ва ҳар кӣ ба Худо тамассук (наздикӣ) ҷӯяд, ба роҳи рост ҳидоят шудааст.'),
(395, 3, 102, 'Эй касоне, ки имон овардаед, он чунон ки шоистаи тарс аз Худост, аз Ӯ битарсед ва дар ҳолати мусалмонӣ миред.'),
(396, 3, 103, 'Ва ҳамагон даст дар ресмони Худо занед ва пароканда нашавед ва аз неъмате, ки Худо бар шумо арзонӣ доштааст, ёд кунед: он ҳангом, ки душмани якдигар будед ва Ӯ дилҳоятонро ба ҳам меҳрубон сохт ва ба лутфи Ӯ бародар шудед. Ва бар лаби чоҳе аз оташ будед, Худо шуморо аз он бираҳонид. Худо оёти худро барои шумо инчунин баён мекунад, шояд ҳидоят ёбед.'),
(397, 3, 104, 'Бояд, ки аз миёни шумо гурӯҳе бошанд, ки ба хайр даъват кунанд ва амр ба маъруф ва наҳй аз мункар кунанд. Инҳо наҷот ёфтагонанд.'),
(398, 3, 105, 'Ба монанди он касоне мабошед, ки пас аз он ки оёти равшани Худо бар онҳо ошкор шуд, пароканда гаштанд ва бо якдигар ихтилоф (зиддият) варзиданд, албатта, барои инҳо азобе бузург хоҳад буд.'),
(399, 3, 106, 'Он рӯз, ки гурӯҳе сафедрӯй ва гурӯҳе сияҳрӯй шаванд, ба онон, ки сияҳрӯй шудаанд, мегӯянд: «Оё шумо пас аз имон оварданатон кофир шудед? Ба сабаби кофир шуданатон бичашед азоби Худоро!»'),
(400, 3, 107, 'Аммо онон, ки сафедрӯй шудаанд, ҳамеша ғарқ дар раҳмати Парвардигор бошанд.'),
(401, 3, 108, 'Инҳо оёти Худост, ки ба ҳақ бар ту мехонем. Ва Худо ба мардуми ҷаҳон ситам раво намедорад!'),
(402, 3, 109, 'Аз они Худост ҳар чӣ дар осмонҳову замин аст ва корҳо ба ӯ бозгардонида шавад.'),
(403, 3, 110, 'Шумо беҳтарин уммате ҳастед аз миёни мардум падидомада, ки амр ба маъруф ва наҳй аз мункар мекунед ва ба Худо имон доред. Агар аҳли китоб низ имон биёваранд, барояшон беҳтар аст. Баъзе аз онҳо мӯъминанд, вале бештарин фосиқонанд.'),
(404, 3, 111, 'Ба шумо ҷуз андак озор дигар осебе нарасонанд. Агар бо шумо ҳарб кунанд, пушт кунанд ва рӯй ба гурез раванд ва пирӯз нагарданд.'),
(405, 3, 112, 'Ҳ, ар ҷо, ки бошанд, мӯҳри хори бар онҳо зада шуда аст, магар он ки дар амони Худо ва дар амони мардум бошанд. Ва бо хашми Худо қарин шудаанд ва мӯҳри бадбахтӣ бар онҳо ниҳодаанд, зеро ба оёти Худо кофир шуданд ва паёмбаронро ба ноҳақ, куштанд. Ва ин бад-он сабаб буд, ки исён (нофармонӣ) варзиданд ва таҷовуз карданд.'),
(406, 3, 113, 'Аҳли китоб ҳама яксон нестанд. Гурӯҳе ба тоъати Худо истодаанд ва оёти Худоро дар дили шаб тиловат мекунанд ва саҷда ба ҷой меоваранд.'),
(407, 3, 114, 'Ва ба Худо ва рӯзи қиёмат имон доранд ва амр ба маъруф ва наҳй аз мункар мекунанд ва дар корҳои нек шитоб меварзанд ва аз ҷумлаи солеҳонанд.'),
(408, 3, 115, 'Ва ҳар кори нек, ки кунанд, ношукрӣ карда нашавад, зеро Худо ба парҳезгорон огоҳ аст!'),
(409, 3, 116, 'Кофиронро амволу авлодашон ҳеҷ аз азоби Худо нараҳонад ва онон аҳли оташанд, ҷовидона дар ҷаҳаннам бошанд!'),
(410, 3, 117, 'Он чӣ инҳо дар ин ҷаҳон хайр мекунанд, ҳамонанди он аст, ки тундбоде сард бар киштзори қавме бар хеш ситам карда бивазад ва он киштаро нобуд созад. Худо бар онҳо ситам раво надошт. Онон худ бар худ ситам карданд.'),
(411, 3, 118, 'Эй касоне, ки имон овардаед, дӯсти ҳамрозе ғайр аз ҳамдинони худ магиред, ки дигарон аз ҳеҷ фасоде дар хаққи шумо кӯтоҳӣ намекунанд ва хостори ранҷу машаққати шумоянд. Ва душманӣ аз гуфторашон ошкор аст ва он кина, ки дар дил доранд, бештар аст аз он чӣ ба забон меоваранд. Оётро бароятон ошкор сохтем, агар ба ақл дарёбед!'),
(412, 3, 119, 'Огоҳ бошед, ки шумо ононро дӯст медоред ва ҳол он ки онҳо шуморо дӯст надоранд. Шумо ба ҳамаи ин китоб имон овардаед. Чун шуморо бубинанд, гӯянд: «Мо ҳам имон овардаем». Ва чун хилват кунанд, аз ғояти кинае, ки ба Шумо доранд, сарангушти худ ба дандон газанд. Бигӯ: «Дар кинаи хеш бимиред! Албатта Худо ба он чӣ дар дилҳост, огоҳ аст».'),
(413, 3, 120, 'Агар хайре ба шумо расад, андӯҳгин шаванд ва агар ба мусибате гирифтор оед, шодмоп гарданд. Агар сабр варзед ва парҳезгорӣ кунед, аз макрашон ба шумо зиёне наррсад, кк Худо бар ҳар коре, ки мекунанд, огоҳӣ дорад!'),
(414, 3, 121, 'Ва бомдодон аз миёни касони хеш берун омадӣ, то мӯъминонро дар он ҷойҳо, ки мебоист биҷанганд, биншонӣ ва Худо шунавову доност!'),
(415, 3, 122, 'Ду гурӯҳ аз шумо нияти он карданд, ки дар ҷанг сустӣ намоянд ва Худо ёварашон буд, пас мӯъминон бояд, ки бар Худо таваккал кунанд!'),
(416, 3, 123, 'Албатта Худо шуморо дар Бадр ёрӣ кард ва ҳол он ки нотавон будед. Пас аз Худо битарсед, бошад, ки шукргузор шавед!'),
(417, 3, 124, 'Он гоҳ, ки ба мӯъминон мегуфтӣ, ки агар Худо се ҳазор фаришта ба ёриатон фурӯ фиристад, оё шуморо кофӣ нахоҳад буд?'),
(418, 3, 125, 'Бале, агар пойдорӣ кунед ва парҳезгор бошед, чун душманон тохтутоз оваранд, Худо бо панҷ ҳазор аз фариштагони аломатдор шуморо ёрӣ кунад.'),
(419, 3, 126, 'Ва Худованд ин корро танҳо барои шодмонӣ ва дилгармии шумо кард. Ва нест ёрие, магар аз сӯи Худои пирӯзманду доно,'),
(420, 3, 127, 'то гурӯҳе аз кофиронро ҳалок кунад ё хор гардонад. Он гоҳ навмед бозгарданд.'),
(421, 3, 128, 'Ё эшонро ба тавба водорад ё он ситамкоронро азоб кунад ва туро дар ин корҳо дасте нест.'),
(422, 3, 129, 'Аз они Худост ҳар чӣ дар осмонҳо ва замин аст. Ҳар киро бихоҳад, меомурзад ва ҳар киро бихоҳад, азоб мекунад ва Худо омурзандаву меҳрубон аст!'),
(423, 3, 130, 'Эй касоне, ки имон овардаед, рибо махӯред ба афзуданҳои пай дар пай. Ва аз Худой битарсед, то растагор шавед!'),
(424, 3, 131, 'Ва битарсед аз оташе, ки барои кофирон муҳайё шудааст!'),
(425, 3, 132, 'Ва аз Худову расул итоат кунед то шояд раҳмат карда шавед!'),
(426, 3, 133, 'Бар якдигар пешӣ гиред барои омурзиши Парвардигори хеш ва расидан ба он биҳишт, ки паҳнояш ба қадри ҳамаи осмонҳову замин аст ва барон парҳезгорон мухайё шудааст,'),
(427, 3, 134, 'он касон, ки дар тавонгариву тангдастӣ эҳсон мекунанд ва хашми худ фурӯ мехӯранд ва аз хатои мардум дармегузаранд. Худо накӯкоронро дӯст дорад!'),
(428, 3, 135, 'Ва он касон, ки чун коре зишт кунанд ё ба худ ситаме кунанд, Худоро ёд мекунанд ва барои гуноҳони хеш омурзиш мехоҳанд ва кист ҷуз Худо, ки гуноҳонро биёмурзад? Ва чун ба зиштии гуноҳ огоҳанд, дар он чӣ мекарданд, пой нафушуранд.'),
(429, 3, 136, 'Ҷазои инҳо омурзиши Парвардигорашон аст ва низ биҳиштҳое ки дар он наҳрҳо ҷорист. Дар он ҷо ҷовидонанд ва чӣ некӯст ҷазои некӯкорон!'),
(430, 3, 137, 'Пеш аз шумо воқеъаҳое будааст, пас бар рӯи замин бигардед ва бингаред, ки поёни кори онҳо, паёмбаронро ба дурӯғгӯӣ нисбат медоданд, чӣ будааст?'),
(431, 3, 138, 'Ин барои мардум далеле равшан ва барои парҳезгорон роҳнамову пандест.'),
(432, 3, 139, 'Сустӣ макунед ва андӯҳгин мабошед, зеро агар имон оварда бошед, шумо ғолиб ҳастед.'),
(433, 3, 140, 'Агар бар шумо захме расид, ба он қавм низ ҳамчунон захме расидааст. Ва ин рӯзгор аст, ки ҳар дам онро бар муроди касе мегардонем, то Худо касонеро, ки имон овардаанд, бишиносад ва аз шумо гувоҳон гирад. Ва Худо ситамкоронро дӯст надорад,'),
(434, 3, 141, 'ва то мӯъминонро покиза гардонад ва кофиронро нобуд созад.'),
(435, 3, 142, 'Оё гумон доред, ки ба биҳишт хоҳед рафт ва ҳол он ки ҳанӯз Худо маълум накардааст, ки аз миёни шумо чӣ касоне ҷиҳод мекунанд ва чӣ касоне сабр мекунанд?'),
(436, 3, 143, 'Пеш аз он ки маргатон фаро расад, орзуи марг макардед, инак маргро дидед ва дар он менигаред!'),
(437, 3, 144, 'Шубҳае нест, ки Муҳаммад паёмбарест, ки пеш аз ӯ паёмбароне дигар будаанд. Оё агар бимирад ё кушта шавад, шумо ба дини пешини худ бозмегардед? Ҳар кас, ки бозгардад, ҳеҷ зиёне ба Худо нахоҳад расонид. Худо шукргузоронро подош (мукофот) хоҳад дод.'),
(438, 3, 145, 'Ҳеч кас бе фармони Худо намемирад. Муддат навишта шудааст. Ҳар кас хоҳони савоби инҷаҳонӣ бошад, ба ӯ медиҳем ва ҳар кас хоҳони савоби онҷаҳонӣ бошад, ба ӯ медиҳем ва шокиронро подош хоҳем дод!'),
(439, 3, 146, 'Чӣ басо паёмбароне, ки худодӯстони бисер ҳамроҳи онон ба ҷанг рафтанд ва дар роҳи Худо, ҳарчӣ ба онҳо расид, сустӣ накарданд ва нотавон нашуданд ва сар фуруд наёварданд ва Худо сабркунандагонро дӯст дорад.'),
(440, 3, 147, 'Суханашон ҷуз ин набуд, ки мегуфтанд: «Эй Парвардигори мо, гуноҳони моро ва зиёдаравиҳои моро дар корҳо биёмурз ва моро собитқадам гардон ва дар баробари кофирон ёрӣ кун».'),
(441, 3, 148, 'Худо подоши инҷаҳонӣ ва подоши неки онҷаҳониро ба онҳо арзонӣ дошт ва Худо накӯкоронро дуст дорад!'),
(442, 3, 149, 'Эй касоне, ки имон овардаед, агар ба кофирон пайрави кунед, шуморо ба дини пешина бармегардонанд, пас зиёндида бозмегардед.'),
(443, 3, 150, 'На, ёрикунандаи шумо Худованд аст, ки беҳгарини ёрикунандагон аст!'),
(444, 3, 151, 'Дар дили кофирон тарс хоҳем афканд. Зеро чизеро, ки аз осмон барои он ҳуҷҷате нафиристодааст, шарики Худо гирифтанд. Ҷои онон ҷаҳаннам аст, ки барои ситамкорон макони бадест!'),
(445, 3, 152, 'Худо ба ваъдае, ки бо шумо ниҳода буд, вафо кард, он гоҳ ки ба амри Ӯ душманро мекуштед. Ва чун ғаниматеро, ки ҳаваси онро дар сар доштед, ба шумо нишон дод, сустӣ кардед ва дар он амр ба низоъ пардохтед ва нофармонӣ варзидед. Баъзе хостори дунё шудед ва баъзе хостсри охират. Сипас то шуморо ба балое мубтало кунад, ба гурехтан водошт! Инак шуморо бибахшид, ки Ӯро ба мӯъминон бахшоишест!'),
(446, 3, 153, 'Он гоҳ ки мегурехтед ва ба касе нигоҳ намекардед ва паёмбар шуморо аз пушти сар садо медод. Пас ба ҷазо ғаме бар ғами шумо афзуд. Акнун андӯҳи он чиро, ки аз даст додаед, ё ранҷеро, ки ба шумо расидааст, нахӯред. Худо ба ҳар коре, ки мекунед, огоҳ аст!'),
(447, 3, 154, 'Он гоҳ пас аз он андӯҳ, Худо ба шумо эминӣ арзонӣ дошт, чукон ки гурӯҳеро хоби ором фурӯ гирифт. Аммо гурӯҳе дигар, ки чун мардуми асри ҷоҳилӣ ба Худо гумони ботил доштанд, ҳанӯз дар фикри андӯҳи хеш буданд ва мегуфтанд: «Оё ҳаргиз кор ба дасти мо хоҳад афтод?» Бигӯ: «Ҳамаи корҳо ба дасти Худост». Онон дар дили худ чизеро пинҳон медоранд, ки намехоҳанд барои ту ошкораш созанд. Мегӯянд: «Агар моро ихтиёре буд, ин ҷо кушта намешудем». Бигӯ: «Агар дар хонаҳои худ ҳам мебудед, касоне, ки кушта шудан бар онҳо муқаррар шудааст, аз хона ба қатлгоҳашон берун мерафтанд. Худо он чиро, ки дар сина доред, меозмояд ва дилҳоятонро пок мегардонад». Ва Худо ба он чӣ дар дилҳост, огоҳ аст!'),
(448, 3, 155, 'Аз миёни шумо онон, ки дар рӯзи муқобалаи он ду гурӯҳ, бигурехтанд, ба сабаби баъзе аз амалҳояшон шайтон онҳоро ба хато афканда буд. Инак Худованд афвашон кард, ки Ӯ омурзанда ва бурдбор аст!'),
(449, 3, 156, 'Эй касоне, ки имон овардаед, монанди он кофирон мабошед, ки дар бораи бародарони худ, ки ба сафар ё ба ҷанг рафта буданд, мегуфтанд: «Агар назди по монда буданд, намемурданд, ё кушта намешуданд». Худо ин гумонро чун ҳасрате дар дили онҳо ниҳод. Ва Худост, ки зинда мекунад ва мемиронад ва Ӯст, ки корҳои шуморо мебинад!'),
(450, 3, 157, 'Агар дар роҳи Худо кушта шавед, ё бимиред, омурзиш ва раҳмати Худо аз он чӣ дар ин ҷаҳон чамъ меоваред, беҳтар аст.'),
(451, 3, 158, 'Ва агар бимиред, ё кушта шавед, албатта дар пешгоҳи Худованд маҳшур (хамсӯҳбат) мешавед.'),
(452, 3, 159, 'Ба сабаби раҳмати Худост, ки ту бо онҳо инчунин хушхӯю меҳрубон ҳастӣ. Агар тундхӯву сахтдил мебудӣ, аз гирди ту пароканда мешуданд. Пас бар онҳо бубахшой ва бар онҳо омурзиш бихоҳ ва дар корҳо бо онҳо машварат кун ва чун қасди коре кунӣ, бар Худой таваккул кун, ки Худо таваккулкунандагонро дӯст дорад.'),
(453, 3, 160, 'Агар Худо шуморо ёрӣ кунад, ҳеҷ кас бар шумо ғалаба нахоҳад кард. Ва агар шуморо хор дорад, чӣ касе аз он пас ёриатон хоҳад кард? Пас мӯъминон бояд ки бар Худо таваккал кунанд!'),
(454, 3, 161, 'Ҳеҷ паёмбаре хиёнат накунад ва ҳар кӣ ба чизе хиёнат кунад, онро дар рӯзи қиёмат бо худ оварад. Сипае ҷазои амали ҳар кас ба тамоми дода хоҳад шуд ва бар касе ситаме нашавад!'),
(455, 3, 162, 'Оё он кас, ки ба роҳи хушнудии Худо меравад, ҳамонанди касест, ки мӯҷиби хашми ӯ мешавад ва макони ӯ ҷаҳаннам, он саранҷоми бад аст?'),
(456, 3, 163, 'З Ин ду гурӯҳро назди Худо дараҷаҳоест гуногун ва Ӯ ба корҳояшон огоҳ аст!'),
(457, 3, 164, 'Худо бар мӯъминон инъом фармуд, он гоҳ, ки аз худашон ба миёни худашон паёмбаре равон кард то оёташро бар онҳо бихонад ва покашон созад ва китобу ҳикматашон биёмузад, ҳарчанд аз он пеш дар гумроҳии ошкоре буданд.'),
(458, 3, 165, 'Ва оё ҳангоме ки осебе ба шумо расид, ки шумо худ дучанди он расонда будед, гуфтед: «Ин осеб аз куҷо расид?» Бигӯ: «Аз ҷониби худатон». Албатта Худо бар ҳар чизе тавоност!'),
(459, 3, 166, 'Он чӣ дар рӯзи бархурди он ду гурӯҳ, ба шумо расид, ба изни (ризой) Худо буд, то мӯъминонро маълум дорад.'),
(460, 3, 167, 'Ва оконро низ, ки нифоқ варзиданд маълум дорад. Ба онҳо гуфта мешуд: «Биёед, дар роҳи Худо ҷанг кунед ё ба дифоъ пардозед». Мегуфтанд: «Агар яқин доштем, ки ҷанге дармегирад, бо шумо меомадем». Онон ба куфр наздиктаранд, то ба имон. Ба забои чизҳое мегӯянд, ки ба дил эътиқод надоранд ва Худо ба он чӣ дар дил пинҳон медоранд, огоҳтар аст!'),
(461, 3, 168, 'Ба онон, ки аз ҷанг бозистоданд ва дар бораи бародарони худ гуфтанд, ки агар сухани моро шунида буданд кушта намешуданд, бигӯ: «Агар рост мегӯед, маргро аз худ биронед!»'),
(462, 3, 169, 'Касонеро, ки дар роҳи Худо кушта шудаанд, мурда мапиндор, балки зиндаанд ва назди Парвардигорашон рӯзӣ дода мешаванд.'),
(463, 3, 170, 'Аз фазилате, ки Худо насибашон кардааст, шодмонанд. Ва барои онҳо, ки дар паяшон ҳастанд ва ҳанӯз ба онҳо напайвастаанд, хушдиланд, ки биме бар онҳо нест ва андӯҳгин намешаванд!'),
(464, 3, 171, 'Ононро муждаи неъмату фазли Худо медиҳанд ва Худо подоши мӯъминонро бекор намекунад.'),
(465, 3, 172, 'Аз миёни он касон, ки пас аз захм хӯрдан боз ҳам фармони Худо ва расулашро иҷобат карданд, онон, ки некӯкор бошанд ва аз Худой битарсанд, музде бузург доранд.'),
(466, 3, 173, 'Касоне, ки мардум гуфтандашон, ки мардум барои ҷанг бо шумо гирд омадаанд, аз онҳо битарсед ва ин сухан бар имонашон бияфзуд ва гуфтанд: «Худо моро басанда аст ва чӣ некӯ ёварест!»'),
(467, 3, 174, 'Пас аз ҷанг бозгаштанд, дар ҳоле ки неъмату фазли Худоро ба ҳамроҳ доштанд ва ҳеҷ осебе ба онҳо нарасида буд. Инҳо ба роҳи хушнудии Худо рафтанд ва Худоро бахшоише азим аст!'),
(468, 3, 175, 'Он шайтон аст, ки дар дили дӯстони худ бим меафканад. Агар имон овардаед, аз онҳо матарсед, аз ман битарсед!'),
(469, 3, 176, 'Онон, ки ба куфр мешитобанд, туро ғамгин насозанд. Инҳо ҳеҷ зиёне ба Худо намерасонанд. Худо мехоҳад онҳоро дар охират бебаҳра гардонад ва барояшон азобест бузург!'),
(470, 3, 177, 'Ҳар оина албатта онон, ки имон доданд ва куфр хариданд, ҳеҷ зиёне ба Худо намерасонанд ва барояшон азобе дарднок аст!'),
(471, 3, 178, 'Кофирон напиндоранд, ки дар мӯҳлате, ки ба онҳо медиҳем, хайри онҳост. Ба онҳо мӯҳлат медиҳем, то бештар ба гуноҳонашон бияфзоянд ва барои онҳост азобе хоркунанда!'),
(472, 3, 179, 'Худо бар он нест, ки шумо мӯъминонро бад-ия ҳол, ки акнун ҳастед, раҳо кунад. Меозмояд, то нопокро аз пок ҷудо созад. Ва Худо бар он нест, ки шуморо аз ғайб биогоҳонад, вале баъзе аз паёмбаронашро, ки худ бихоҳад, бармегузинад. Пас ба Худо ва паёмбаронаш имон биёваред. Ва агар имон биёваред ва парҳезгорӣ кунед, аҷре азим ёбед!'),
(473, 3, 180, 'Онон, ки дар неъмате, ки Худо ба онҳо ато кардааст, бахилӣ мекунанд, напиндоранд, ки дар бахилӣ кардан барояшон хайр аст. На, шарр аст! Дар рӯзи қиёмат он чиро, ки дар бахшиданаш бухл меварзиданд, чун тавқе ба гарданашон хоҳанд озехт. Ва аз они Худост мероси осмонҳову замин ва Ӯ ба ҳар коре, ки мекунед, огоҳ аст!'),
(474, 3, 181, 'Албатта Худо шунид сухани он касонро, ки мегуфтанд: «Худо бенавост ва мо тавонгарем». Гуфторашонро ва низ ин ки паёмбаронро ба ноҳақ мекуштанд, хоҳем навишт ва гӯем: «Бичашед азоби оташи сӯзонро!»'),
(475, 3, 182, 'Ин ҷазои амалҳоест, ки пешопеш ба ҷой овардед вагарна Худованд ба бандагонаш ситам раво намедорад.'),
(476, 3, 183, 'Касоне гуфтанд: «Моро бо Худо аҳдест, ки ба ҳеҷ паёмбаре имон наёварем, магар барои мо қурбоние биёварад, ки оташ онро бихӯрад». Бигӯ: «Пеш аз ман паёмбароне бо мӯъҷизаҳо ва он чи акнун мехоҳед, омадаанд, агар рост мегӯед, чаро онҳоро куштед?»'),
(477, 3, 184, 'Агар туро такзиб карданд, паёмбароне ҳам, ки пеш аз ту бо муъҷизаҳо ва навиштаҳо ва китоби равшангар омада буданд, такзиб шудаанд.'),
(478, 3, 185, 'Ҳама кас маргро мечашад ва дар хақиқат дар рӯзи қиёмат музди аъмоли шуморо ба камол хоҳанд дод. Ва ҳар касро аз оташ дур созанд ва ба бихишт дароваранд, ба пирӯзӣ расидааст. Ва ин зиндагли дунё ҷуз матоъе фиребанда нест.'),
(479, 3, 186, 'Шуморо ба молу ҷон озмоиш хоҳанд кард. Ва аз забони аҳли китоб ва мушрикон озори фаровон хоҳед шунид. Агар сабр кунед ва парҳезгор бошед, пас ин корест бузург,'),
(480, 3, 187, 'Худо аз аҳли китоб паймон гирифт, ки китоби Худоро барои мардум ошкор созанд ва пинҳонаш накунанд, вале онҳо паси пушташон афканданд ва дар ивазаш баҳои андаке гирифтанд. Чӣ бад муъомалае карданд!'),
(481, 3, 188, 'Ононро, ки аз корҳое, ки кардаанд, шодмон шудаанд ва дӯст доранд ба сабаби корҳои нокардаи хеш ҳам мавриди ситоиш қарор гиранд, мапиндор, ки дар паноҳгоҳе дур аз азоби Худо бошанд. Барояшон азобе дардовар муҳайёст!'),
(482, 3, 189, 'Аз они Худост фармонравоии осмонҳову замин ва Худо бар ҳар чизе тавоност!'),
(483, 3, 190, 'Албатта дар офариниши осмонҳову замин ва омадушуди шабу рӯз хирадмандонро ибратҳост,'),
(484, 3, 191, 'онҳо, ки Худоро истодаву нишаста ва ба паҳлӯ хуфта ёд мекунанд ва дар офариниши осмонҳову замин меандешанд: «Эй Парвардигори мо, ин ҷаҳонро ба бехуда наёфаридаӣ, ту покӣ, моро аз азоби оташ боздор!'),
(485, 3, 192, 'Эй Парвардигори мо, ҳар касро, ки ба оташ дарорӣ, расвояш кардаӣ ва золимонро ҳеҷ ёваре нест!'),
(486, 3, 193, 'Эй Парвардигори мо, шунидем, ки нидокунандае ба имон фаро мехонд, ки ба Парвардигоратон имон биёваред! Ва мо имон овардем. Пас, эй Парвардигори мо, гуноҳони моро биёмурз ва бадиҳои моро аз мо дур кун ва моро бо некон бимирон!'),
(487, 3, 194, 'Эй Парвардигори мо, ато кун ба мо он чиро, ки ба забони паёмбаронат ба мо ваъда додаӣ ва моро дар рӯзи қиёмат расво макун, ки Ту ваъдаи хеш хилоф намекунӣ».'),
(488, 3, 195, 'Парвардигорашон дуояшонро иҷобат фармуд, ки Ман кори ҳеҷ коргузореро аз шумо, чӣ зан ва чӣ мард — ҳама аз якдигаред — ночиз намесоеам. Пас гуноҳони касонеро, ки муҳоҷират кардаанд ва аз хонаҳояшон ронда шудаанд ва дар роҳи Ман озор дидаанд ва ҷангидаанд ва кушта шудаанд, мағфират мекунам ва ононро дар биҳиштҳое, ки дар он наҳрҳо ҷорист, дохил мекунам. Ин мукофот аз ҷониби Худо ва подоши некӣ назди Худост.'),
(489, 3, 196, 'Омадурафти кофирон дар шаҳрҳо туро нафиребад!'),
(490, 3, 197, 'Ин бархурдорип андакест. Пас аз он ҷойгоҳашон ҷаҳаннам аст ва чаҳаннам бад оромгоҳест!'),
(491, 3, 198, 'Аммо барои онон, ки аз Парвардигорашон метарсанд, биҳиштҳоест, ки дар он наҳрҳо ҷорист. Ҳамеша дар он ҷо меҳмони Худо ҳастанд, Ва он чӣ назди Худост, барои некон беҳтар аст!'),
(492, 3, 199, 'Баъзе аз аҳли китоб ба Худо ва китобе, ки бар шумо нозил шуда ва китобе, ки бар худашон нозил шудааст, имон доранд. Мутеъи фармони Худоянд. Оёти Худоро ба нархи андак намефурӯшанд. Музди эшон назди Парвардигорашон аст. Албатта Худо ҳисобҳоро зуд хоҳад кард.'),
(493, 3, 200, 'Эй касоне, ки имон овардаед, бурдбор бошед ва дигаронро ба бурдборӣ даъват кунед ва дар ҷангҳо пойдорӣ кунед ва аз Худо битарсед, шояд, ки наҷот ёбед!.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Эй мардум, битарсед аз Парвардигоратон, он, ки шуморо аз як тан биёфарид ва аз он як тан ҳамсари ӯро ва аз он ду мардону занони бисёр падид овард. Ва битарсед аз он Худое, ки бо савганд ба номи Ӯ аз якдигар чизе мехоҳед ва зинҳор аз хешовандон мабуред. Албатта Худо назораткукандаи шумост!'),
(495, 4, 2, 'Моли ятимонро ба ятимон диҳед ва ҳаромро бо ҳалол иваз накунед. Ва молҳои онҳоро ҳамроҳ бо молҳои хеш махӯред, ки ин гуноҳе бузург аст!'),
(496, 4, 3, 'Агар шуморо бими он аст, ки дар кори ятимон адолат накунед, аз занон ҳар чӣ шуморо писанд афтад, ду-ду ва се-се ва чаҳор-чаҳор ба никоҳ, дароваред. Ва агар бими он доред, ки ба адолат рафтор накунед, танҳо як зан бигиред ё ҳар чӣ молики он шавед. Ин роҳе беҳтар аст то ки зулму ситам накунед.'),
(497, 4, 4, 'Маҳри занонро ба тиби (майли) хотир ба онҳо бидиҳед. Ва агар баъзе аз онро ба ризоят ба шумо бахшиданд, бигиред, ки ҳалолу покизаатон хоҳад буд.'),
(498, 4, 5, 'Молҳоятонро, ки Худо сабаби зиндагии шумо сохтааст, ба дасти беақлҳо мадиҳед, вале аз он хӯроку либосашон диҳед ва бо онон сухан ба некӣ гӯед.'),
(499, 4, 6, 'Ятимонро биёзмоед, то он гоҳ, ки ба синни заношӯӣ расанд, пас агар дар онон рушде ёфтед, молҳояшонро ба худашон бисупоред, Ва аз бими он, ки мабод ба синни рушд расанд, молҳояшонро ба ноҳақ ва шитоб мехӯред. Ҳар кӣ тавонгар аст, бипарҳезад ва ҳар кӣ бенавост, ба он миқдор, ки урф тасдиқ кунад, бихӯрад. Ва чун амволашонро таслимашон кардед, касонеро бар онон ба шоҳидӣ гиред ва Худо барои ҳисоб кардан кофист.'),
(500, 4, 7, 'Аз ҳар чӣ падару модар ва хешопандон ба мерос мегузоранд, мардонро насибест. Ва аз он чӣ падару модар ва хешовандон ба мерос мегузоранд, чӣ андак ва чӣ бисёр занонро низ насибест. Насибе муайян.'),
(501, 4, 8, 'Ва чун ба ҳангоми тақсим хешовандону ятимон ва мискинон ҳозир омаданд, ба онон низ чизе садақа кунед ва бо онҳо ба некӯӣ сухан гӯед.'),
(502, 4, 9, 'Бояд аз Худо битарсанд касоне, ки агар пас аз худ фарзандоне нотавон бар ҷой мегузоранд, аз сарнавишти онҳо тарсонанд. Бояд, ки аз Худо битарсанд ва сухани одилона ва басавоб гӯянд!'),
(503, 4, 10, 'Онҳо, ки молҳои ятнмонпо ба ситам мехӯранд, шиками хеш пур аз оташ мекунанд ва ба оташе фурузон хоҳанд афтод!'),
(504, 4, 11, 'Худо дар бораи фарзандонатон ба шумо супориш мекунад, ки саҳми писар баробари сахми ду духтар аст. Ва агар духтар бошанд ва беш аз ду тан, ду саввуми мерос аз онҳост. Ва агар як духтар бувад, нисф барад ва агар мурдаро фарзанде бошад, ҳар як аз падару модар як шашуми меросро барад. Ва агар фарзанде надошта бошад ва меросбарон танҳо падару модар бошанд, модар як саввуми дороиро барад. Аммо агар бародарон дошта бошад, саҳми модар пас аз анҷоми васийяте, ки карда ва баъди пардохти қарзи ӯ як шашум бошад. Ва шумо намедонед, ки аз падарону писаронатон кадом як шуморо фоиданоктар аст. Инҳо ҳукми Худост, ки Худо донову ҳаким аст!'),
(505, 4, 12, 'Агар занонатон фарзанде надоштанд, пас аз анҷом додани васияте, ки кардаанд ва пас аз пардохти қарзи онҳо нисфи меросашон аз они шумост. Ва агар фарзанде доштанд, як чаҳоруми он. Ва агар шуморо фарзанде набуд, пас аз анҷом додани васияте, ки кардаед ва пас аз пардохти қарзҳоятон як чаҳоруми меросатон аз они занонатон аст. Ва агар дорои фарзанде будед, як ҳаштуми он. Ва агар марде ё зане бимирад ва меросбари вай на падар бошад ва на фарзанди ӯ, агар ӯро бародар ё хоҳаре бошад, ҳар як аз он ду як шашум барад. Ва агар беш аз яке буданд, ҳама дар як саввуми мол — пас аз анҷом додани васияте, ки кардааст, бе он ки барои ворисатон зиёнманд бошад ва низ пас аз адои қарзашон — шарик ҳастанд. Ин пандест аз Худо ба шумо ва Худо донову бурдбор аст!'),
(506, 4, 13, 'Инҳо аҳкоми Худост. Ҳар кас аз Худо ва паёмбараш фармон барад, ӯро ба биҳиштҳое, ки дар он наҳрҳо ҷорист, дароварад ва ҷовидон дар он ҷо хоҳад буд ва ин комёбии бузургест!'),
(507, 4, 14, 'Ва ҳар кӣ аз Худову расулаш фармон набарад ва аз аҳкоми ӯ таҷовуз кунад, ӯро дохил дар оташ кунад ва ҷовидон дар он ҷо хоҳад буд ва барои ӯст азобе хоркунанда.'),
(508, 4, 15, 'Ва аз занони шумо онон, ки муртакпби фаҳшо (гуноҳ) мешаванд, чаҳор тан аз худатон бар зидди онҳо шаҳодат бихоҳед. Агар шоҳидӣ доданд, занонро дар хона маҳбус доред, то маргашон фаро расад ё Худо роҳе барояшон муқаррар кунад.'),
(509, 4, 16, 'Ва он ду танро, ки муртакиби он амал шудаанд, биёзоред. Ва чун тавба кунанд ва ба салоҳ оянд, аз озорашон даст бардоред, зеро Худо тавбапазиру меҳрубон аст.'),
(510, 4, 17, 'Фақат ва фақат қабули тавба барои касонест, ки ба нодонӣ содиркунандаи коре зишт мешаванд ва зуд тавба мекунанд. Худо тавбаи онҳоро мепазирад ва Худо донову ҳаким аст.'),
(511, 4, 18, 'Тавбаи касоне, ки корҳои зишт мекунанд ва чун маргашон фаро мерасад, мегӯянд, ки акнун тавба кардем ва киз онон, ки кофир бимиранд, пазируфта нахоҳад шуд. Барои инон азобе дардовар муҳайё кардаем.'),
(512, 4, 19, 'Эй касоне, ки имон овардаед, шуморо ҳалол нест, ки занонро бар хилофи майлашон мерос бибаред. Ва то қисмате аз он чиро, ки ба онҳо додаед, бозпас ситонед, бар онҳо сахт магиред, ғайри он ки гуноҳи фаҳши исботшуда карда бошанд. Ва бо онон ба некӯӣ рафтор кунед. Ва агар шуморо аз занон хуш наёмад, чӣ басо чизҳо, ки шуморо аз он хуш намеояд, дар ҳоле, ки Худо хайри бисёре дар он ниҳода бошад.'),
(513, 4, 20, 'Агар хостед, зане ба ҷои зане дигар бигиред ва ӯро моли бисёре додаед, набояд чизе аз ӯ бозситонед, Оё ба занон тӯҳмат мезанед, то маҳрашонро бозпас гиред? Ин гуноҳе ошкор аст!'),
(514, 4, 21, 'Ва чӣ гуна он молро бозпас мегиред ва ҳол он ки ҳар як аз шумо аз дигаре баҳраманд шудааст ва занон аз шумо паймоне устувор гирифтаанд.'),
(515, 4, 22, 'Бо заноне, ки падаронатон ба ақди хеш даровардаанд заношӯӣ макунед, ғайрӣ он ки пеш аз ип чунон карда бошед. Зеро ин кор зино ва мавриди хашми Худост ва роҳест нописанд.'),
(516, 4, 23, 'Модаронатон ва духтаронатон ва хоҳаронатон ва аммаҳоятон ва холаҳоятон ва духтарони бародарон ва духтарони хоҳаронатон ва заноне, ки шуморо шир додаанд ва хоҳарони шириятон ва модарони занонатон бар шумо ҳаром шудаанд. Ва духтарони занонатон, ки дар канори шумо ҳастанд, ҳар гоҳ бо он занон ҳамбистар шудаед, бар шумо ҳаром шудаанд. Вале агар ҳамбистар нашудаед, гуноҳе муртакиб нашудаед. Ва низ занони писароне, ки аз пушти шумо ҳастанд, бар шумо ҳаром шудаанд. Ва набояд ду хоҳарро дар як замон ба занӣ гиред, ғайри он ки пеш аз ин чунин карда бошед. Албатта Худо омурзарда ва меҳрубон аст.'),
(517, 4, 24, 'Ва низ занони шавҳардор бар шумо ҳаром шудаанд, магар онҳо, ки ба тасарруфи шумо даромада бошанд. Аз китоби Худо пайравӣ кунед. Ва ҷуз инҳо занони дигар ҳар гоҳ дар талаби онон аз моли хеш маҳре бипардозед ва онҳоро ба никоҳ дароваред на ба зино, бар шумо ҳалол шудаанд. Ва занонеро, ки аз онҳо лаззат мегиред, воҷиб аст, ки маҳрашонро бидиҳед. Ва пас аз маҳри муъайян дар қабули ҳар чӣ ҳар ду ба он ризо бидиҳед, гуноҳе нест. Ҳар оина Худо донову ҳаким аст!'),
(518, 4, 25, 'Ҳар касро, ки тавонгарӣ набошад, то озодзанони мӯъминаро ба никоҳи худ дароварад, аз канизони мӯъминае, ки молнки онҳо ҳастед, ба занӣ гиред. Ва Худо ба имони шумо огоҳтар аст. Ҳама аз ҷинси якдигаред. Пас бандагонро ба изни соҳи бонашон никоҳ кунед ва маҳрашонро ба тарзи шоистае бидиҳед. Ва бояд, ки покдоман бошанд на зинокор ва на аз онҳо, ки ба пинҳон дӯст мегиранд. Ва чун шавҳар карданд, ҳар гоҳ муртакиби фаҳшо шаванд, шиканҷаи онон нисфи шиканҷаи озодзанон аст. Ва ин барои касонест аз шумо, ки бим доранд, ки ба гуноҳ афтанд. Бо ин ҳама агар сабр кунед, бароятон беҳтар аст ва Худо омурзандаву меҳрубон аст.'),
(519, 4, 26, 'Худо мехоҳад барои шумо ҳама чизро ошкор кунад ва ба суннатҳои пешиниёнатон роҳ бинамояд ва тавбаи шуморо бипазирад, ки Худо донову ҳаким аст.'),
(520, 4, 27, 'Худо мехоҳад тавбаи шуморо бипазирад. Вале онон, ки аз паи шаҳват мераванд, мехоҳанд, ки шумо ба каҷравии азиме афтед.'),
(521, 4, 28, 'Худо мехоҳад бори шуморо сабук кунад, зеро одамӣ нотавон офарида шудааст.'),
(522, 4, 29, 'Эй касоне, ки имон овардаед, моли якдигарро ба ноҳак, махӯред, ғайри молҳои тиҷоратӣ, ки ҳар ду тараф ба он ризоят дода бошед. Ва худатонро макушед. Албатта Худо бо шумо меҳрубон аст!'),
(523, 4, 30, 'Ва ҳар кӣ ин корҳо аз рӯи таҷовузу ситам кунад, ӯро дар оташ хоҳем афканд ва ин бар Худо осон аст!'),
(524, 4, 31, 'Агар аз гуноҳони бузурге, ки шуморо аз он манъ кардаанд, парҳез кунед, аз дигар гуноҳонатон дармегузарем ва шуморо ба маконе некӯ дармеоварем.'),
(525, 4, 32, 'Орзӯ макунед он чизҳоеро, ки ба онҳо Худо баъзе аз шуморо бар баъзе дигар бартарӣ додааст. Мардонро аз он чӣ кунанд, насибест ва занонро аз он чӣ кунанд, насибе. Ва рӯзӣ аз Худо хоҳед, ки Худо бар ҳар чизе огоҳ аст!'),
(526, 4, 33, 'Барон ҳама, дар он чӣ падару модар ва хешовандони наздик ба мерос мегузоранд, меросбароне қарор додаем. Ва насиби ҳар касро, ки бо ӯ қароре кардаед, бипардозед, ки Худо бар ҳар чизе гувоҳ аст!'),
(527, 4, 34, 'Мардон аз он ҷиҳат, ки Худо баъзеро бар баъзе бартарӣ додааст ва аз он ҷиҳат, ки аз моли худ нафақа медиҳанд, бар занон сардорӣ доранд. Пас занони шоиста фармонбардоранд ва дар ғайбати шӯй афифанд (пок) ва фармони Худоро нигоҳ медоранд. Ва он занонро, ки аз нофармонияшон бим доред, панд диҳед ва аз хобгоҳашон дурӣ кунед ва бизанедашон. Агар фармонбардорӣ карданд, аз он пас дигар роҳи зулм пеш нагиред. Ва Худо баландпояву бузург аст!'),
(528, 4, 35, 'Агар аз нотинҷии миёни зану шӯй огоҳ шудед, доваре аз касони мард ва доваре аз касони зан бифиристед. Агар он дуро қасди ислоҳ бошад, Худо миёнашон мувофиқат пайдо мекунад, ки Худо донову огоҳ аст!'),
(529, 4, 36, 'Худоро бипарастед ва ҳеҷ чиз шарики Ӯ масозед ва ба падару модар ва хешовандону ятимон ва бенавоёну ҳамсояи хешованд ва ҳамсояи бегона ва ёри мусоҳибу мусофири раҳгузар ва бандагони худ некӣ кунед. Албатта Худо мутакаббирону фахрфурӯшонро дӯст надорад,'),
(530, 4, 37, 'онон, ки бахилӣ мекунанд ва мардумро ба бухл мефармоянд ва молеро, ки Худо ба онҳо додааст, пинҳон мекунанд. Ва мо барои кофирон азобе хоркунанда муҳайё сохтаем.'),
(531, 4, 38, 'Ва низ касонеро, ки амволи хеш барои худнамоӣ харҷ мекунанд ва ба Худову рӯзи қиёмат имон намеоваранд. Ва ҳар кӣ шайтон қарини ӯ бошад, қарине бад дорад!'),
(532, 4, 39, 'Чӣ зиён дорадашон, агар ба Худову рӯзи қиёмат имон оваранд ва аз он чӣ Худо ба онҳо рӯзӣ додааст, эҳсон кунанд? Худо ба корашон доност!'),
(533, 4, 40, 'Худо заррае ҳам ситам намекунад. Агар некие бошад, онро ду баробар мекунад ва аз ҷониби худ музде бузург медиҳад.'),
(534, 4, 41, 'Чӣ гуна хоҳад буд он рӯз, ки аз ҳар уммате гувоҳе биёварем ва туро бар ин уммат ба гувоҳӣ биёварем?'),
(535, 4, 42, 'Дар он рӯз кофирон ва онон, ки бар расул фармон набурданд, орзӯ кунанд, ки бо хоки замин яксон мебуданд. Ва ҳеҷ суханеро аз Худо пинҳон натавонанд кард!'),
(536, 4, 43, 'Эй касоне, ки имон овардаед, он гоҳ, ки маст ҳастед, гирди намоз магардед, то бидонед, ки чӣ мегӯед. Ва низ дар ҳоли ҷанобат, то ғусл кунед, магар он ки роҳгузар бошед. Ва агар бемор ё дар сафар будед, ё аз макони қазои ҳочат бозгаштаед, ё бо занон ҷимоъ кардаед ва об наёфтед, бо хоки пок таяммум кунед ва рую дастҳоятонро бо он хок масҳ кунед. Албатта Худо авфкунанда ва омурзанда аст!'),
(537, 4, 44, 'Оё он касонеро, ки аз китоб баҳрае дода шудаанд, надидаӣ, ки гумроҳӣ мехаранд ва мехоҳанд, ки шумо низ гумроҳ шавед?'),
(538, 4, 45, 'Худо душманони шуморо беҳтар мешиносад ва дӯстии Ӯ шуморо кифоят хоҳад кард ва ёрии Ӯ шуморо басанда аст.'),
(539, 4, 46, 'Баъзе аз ҷуҳудон калимоти Худоро ба маънӣ дигаргун мекунанд ва мегӯянд: «Шунидем ва нофармонӣ мекунем ва бишнав ва кош ношунаво гардӣ ва «роъино». Ба луғати хеш забон мегардонанд ва ба дини ислом таъна мезананд. Агар мегуфтанд, ки шунидем ва итоъат кардем ва «Унзурно»", барояшон беҳтару басавобтар буд. Худо ононро ба сабаби куфрашон лаънат карда ва ҷуз андаке имон наёваранд.'),
(540, 4, 47, 'Эй касоне, ки шуморо китоб дода шуда, ба китобе, ки нозил кардаем ва китоби шуморо низ тасдиқ мекунад, имон биёваред; пеш аз он ки нақши чеҳраҳоеро нест кунем ва рӯйҳоро ба қафо баргардонем. Ё ҳамчунон, ки асҳоби сабтро лаънат кардем, шуморо ҳам лаънат кунем. Ва фармони Худо шуданист!'),
(541, 4, 48, 'Албатта Худо гуноҳи касонеро, ки ба Ӯ ширк оваранд, намебахшад ва гуноҳони дигарро барои ҳар кӣ бихоҳад, меомурзад. Ва ҳар кӣ ба Худо ширк оварад, дурӯғе сохта ва гуноҳе бузург муртакиб шудааст.'),
(542, 4, 49, 'Оё ононро надидаӣ, ки худро поку беайб ҷилва медиҳанд? Оре, Худост, ки ҳар киро, ки хоҳад, аз айб пок гардонад. Ва ба ҳеҷ кас ҳатто ба қадри риштае, ки дар шикофи донаи хурмост, ситам нашавад.'),
(543, 4, 50, 'Бингар, ки чӣ гуна ба Худо дурӯғ мебанданд ва ҳамин дурӯғ гуноҳе ошкорро басандааст.'),
(544, 4, 51, 'Оё касонеро, ки насибе аз китоб дода шудаанд, надидаӣ, ки ба Ҷибту Тоғут имон меоваравд ва дар бораи кофирон мегӯянд, ки роҳи инон аз роҳи мӯъминон ба ҳидоят наздиктар аст?'),
(545, 4, 52, 'Инон он касонанд, ки Худо лаънаташон кардааст; ва ҳар касро, ки Худо лаънат кунад, барои ӯ хеҷ ёваре наёбӣ!'),
(546, 4, 53, 'Ё аз подшоҳӣ насибе бурдаанд, ки дар ин сурат ба қадри он пӯсти тунуке, ки бар пушти донаи хурмост, ба мардум суде намерасонанд?'),
(547, 4, 54, 'Ё бар мардум ба хотири неъмате, ки Худо аз фазли хеш ба онон додааст, ҳасад мебаранд? Дар ҳоле, ки мо ба хонадони Иброҳим китобу ҳикмат додем ва фармонравоии бузург арзонӣ доштем.'),
(548, 4, 55, 'Баъзе ба он имон оварданд ва баъзе аз он рӯй гардонданд. Дӯзах — он оташи афрӯхта онҳоро бас!'),
(549, 4, 56, 'Ононро, ки ба оёти Мо кофир шуданд, ба оташ хоҳем андохт. Ҳар гоҳ пӯсти танашон бнпазад, пӯсте дигарашон диҳем, то азоби Худоро бичашанд. Худо пирӯзманду хаким аст!'),
(550, 4, 57, 'Ва ононро, ки имон оварда ва корҳои некӯ кардаанд, ба биҳиштҳое, ки дар он наҳрҳо ҷорист, дароварем, то абад дар он ҷо хоханд буд. Ва дар он ҷо соҳиби занони поку беайб шаванд ва дар сояҳои пайваста ва хунук ҷояшон медиҳем.'),
(551, 4, 58, 'Худо ба шумо фармон медиҳад, ки амонатҳоро ба соҳибонашон бозгардонед. Ва чун дар миёни мардум ба доварӣ нишинед, ба адл доварӣ кунед. Худо шуморо чӣ некӯ панд медиҳад. Албатта Ӯ шунаво ва биност!'),
(552, 4, 59, 'Эй касоне, ки имон овардаед, Худоро итоъат кунед ва ба расул ва сардорони хеш фармон баред. Ва чун дар коре ихтилоф кардед, агар ба Худову рӯзи қиёмат имон доред, ба Худову паёмбар руҷӯъ кунед. Дар ин хайри шумост ва саранҷоме беҳтар дорад.'),
(553, 4, 60, 'Оё ононро намебинӣ, ки мепиндоранд, ки ба он чӣ бар ту нозил шуда ва он чӣ пеш аз ту нозил шудааст, имон овардаанд, вале мехоҳанд, ки бутро довар қарор диҳанд, дар ҳоле ки ба онон гуфтаанд, ки бутро рад кунанд. Шайтон мехоҳад гумроҳашон созад ва аз ҳақ дур гардонад.'),
(554, 4, 61, 'Ва чун онҳоро гӯянд, ки ба он чӣ Худо нозил карда ва ба паёмбараш рӯй оред, мунофиқонро мебинӣ, ки сахт аз ту рӯйгардон мешаванд.'),
(555, 4, 62, 'Пас чӣ гуна аст, ки чун ба ҷазои корҳое, ки кардаанд, мусибате ба онҳо расад, назди ту меоянд ва ба Худо савганд мехӯранд, ки мо ҷуз эҳсону мувофиқат қасди дигаре надоштаем?'),
(556, 4, 63, 'Худованд аз дилҳояшон огоҳ аст. Аз онон рӯйгардон шав ва пандашон бидеҳ ба чунон сахтӣ, ки дар вуҷудашон таъсир кунад!'),
(557, 4, 64, 'Ҳеҷ паёмбареро нафиристодем, ҷуз он ки дигарон ба амри Худо бояд мутеъи фармони Ӯ шаванд. Ва агар ҳангоме, ки гуноҳе содир карданд, назди ту омада буданд ва аз Худо омурзиш хоста буданд ва паёмбар барояшон омурзиш хоста буд. Худоро тавбапазир ва меҳрубон меёфтанд.'),
(558, 4, 65, 'На, савганд ба Парвардигорат, ки имон наёваранд, магар он, ки дар низоъе, ки миёни онҳост, туро довар қарор диҳанд ва аз ҳукме, ки ту медиҳӣ, ҳеҷ нохушнуд нашаванд ва саросар таслими он гарданд.'),
(559, 4, 66, 'Ва агар ба онон фармон дода будем, ки худро бикушед ё аз хонаҳоятон берун равед, андаке аз онон фармон мебурданд. Ва ҳол он ки агар пандеро, ки ба онон дода шудааст, кор мебастанд, барояшон беҳтар ва бар асосе устувортар буд.'),
(560, 4, 67, 'Он гоҳ аз ҷониби Худ ба онон музде бузург медодем,'),
(561, 4, 68, 'ва ононро ба роҳи рост ҳидоят мекардем.'),
(562, 4, 69, 'Ва ҳар кӣ аз Худову паёмбараш итоъат кунад, ҳамроҳ бо касоне хоҳад буд, ки Худо неъматашон додааст, чун анбиё ва сиддиқон ва шаҳидону солеҳон ва инҳо чӣ некӯ рафиқонанд!'),
(563, 4, 70, 'Ин фазилатест аз ҷониби Худо ва басандааст Худой доно!'),
(564, 4, 71, 'Эй касоне, ки имон овардаед, силоҳ бигиред ва он гоҳ ҳушёрона гурӯҳ-гурӯҳ, ё якбора ба ҷанг равед.'),
(565, 4, 72, 'Ва аз миёни шумо касест, ки аз майдони ҷанг худдорӣ мекунад. Ва чун ба шумо балое расад, мегӯяд: «Худо дар хаққи ман чӣ некие кард, ки дар он рӯз ҳамроҳашон набудам».'),
(566, 4, 73, 'Ва чун моле аз ҷониби Худо насибатон шавад, чунон ки гӯӣ миёнатон ҳеҷ гуна дӯстие набудааст, гӯяд; «Эй кош, ман низ бо онҳо мебудам ва ба фондае бузург даст меёфтам!»'),
(567, 4, 74, 'Пас онон, ки зиндагии дунёро додаанд ва охиратро харидаанд, бояд ки дар роҳи Худо биҷанганд. Ва ҳар кӣ дар роҳи Худо биҷангад, чӣ кушта шавад. чӣ пирӯз гардад, музде бузург ба ӯ хоҳем дод.'),
(568, 4, 75, 'Чаро дар роҳи Худо ва ба хотири мардону занон ва кӯдакони нотавоне, ки мегӯянд: «Эй Парвардигори мо, моро аз ин деҳаи ситамкорон берун ор ва аз ҷониби худ ёру мададгоре қарор деҳ», намеҷангед?'),
(569, 4, 76, 'Онон, ки имон овардаанд, дар роҳи Худо меҷанганд ва онон, ки кофир шудаанд, дар роҳи шайтон. Пас бо ҳаводорони шайтон чанг кунед, ки макри шайтон ночиз аст!'),
(570, 4, 77, 'Оё надидӣ касонеро, ки ба онҳо гуфта шуд, ки акнун аз ҷанг бозистед ва намоз бихонед ва закот бидиҳед, ки чун ҷангидан бар онон муқаррар шуд, гурӯҳе чунон аз мардум тарсиданд, ки бояд аз Худо метарсиданд? Ҳатто тарсе бештар аз тарси Худо. Ва гуфтанд: «Эй Парвардигори мо, чаро ҷангро бар мо воҷиб кардаӣ ва моро мӯҳлат намедиҳӣ, то ба марги худ, ки наздик аст, бимирем?» Бигӯ: «Ма- тоъи инҷаҳони андак аст ва охират аз они парҳезгорон аст ва ба шумо ҳатто ба қадри риштае, ки дар миёни донаи хурмост, ситам намешавад».'),
(571, 4, 78, 'Дар ҷо, ки бошед, агарчӣ дар ҳисорҳои сахт устувор бошед, марг шуморо дармеёбад. Ва агар хайре ба онҳо расад, мегӯянд, ки аз ҷониби Худо буд ва агар шарре ба онҳо расад, мегӯянд, ки аз ҷониби ту буд. Бигӯ: «Ҳама аз ҷониби Худост». Чӣ бар сари ин қавм омадааст, ки ҳеҷ суханеро намефаҳманд?'),
(572, 4, 79, 'Ҳар хайре, ки ба ту расад, аз ҷониби Худост ва ҳар шаре, ки ба ту расад, аз ҷониби худи туст. Туро ба пайғамбарӣ ба сӯи мардум фиристодем ва Худо ба шоҳидӣ кифоя аст.'),
(573, 4, 80, 'Ҳар кӣ ба паёмбар итоъат кунад, ба Худо итоъат кардааст. Ва онҳое, ки рӯй гардонанд, пас Мо туро ба нигаҳбонии онҳо нафиристодаем.'),
(574, 4, 81, 'Мегӯянд: «Фармонбардорем!» Ва чун аз назди ту берун шаванд, гурӯҳе аз онҳо ба шаб зндди он чӣ ту мегӯӣ, андешае дар дил мепарваранд. Ва Худо он чиро шаб дар хотир гирифтаанд, менависад. Пас, аз онҳо рӯй гардон ва бар Худой таваккал кун, ки Ӯ корсозиро кофист.'),
(575, 4, 82, 'Оё дар Қуръон намеандешанд? Ҳар гоҳ аз суй ғайри Худо мебуд, дар он зиддияте бисёр меёфтанд.'),
(576, 4, 83, 'Ва чун хабаре, чӣ эминӣ ва чӣ тарс ба онҳо расад, онро дар ҳама ҷо фош мекунанд. Ва ҳол он ки агар дар он ба паёмбар ва пешвоёнашон руҷӯъ мекарданд, ҳақиқати корро аз онок дармеёфтанд, Ва агар фазлу раҳмати Худо набуд, ҷуз акдаке ҳамагон ба шайтон пайравӣ мекардед.'),
(577, 4, 84, 'Пас дар роҳи Худо ҷиҳод кун, ки ҷуз бар худат таклифшуда нестӣ ва мӯъминонро ба ҷанг барангез. Шояд Худо бадии кофиронро аз шумо боздорад ва хашму азоби Худо аз ҳар хашму азоби дигаре сахттар аст!'),
(578, 4, 85, 'Ҳар кас дар кори неке миёнарав шавад, ӯро аз он насибест ва ҳар кас дар кори баде миёнарав шавад, ӯро аз он баҳраест. Ва Худо нигаҳбон бар ҳар чизест!'),
(579, 4, 86, 'Чун шуморо ба дуруде салом доданд, ба дуруде беҳтар аз он ё монанди он ҷавоб гӯед. Албатта Худо ҳисобгари ҳар чизест!'),
(580, 4, 87, 'Оллоҳ худоест, ки ҳеҷ худое ҷуз Ӯ нест. Ба таҳқиқ ҳамаи шуморо дар рӯзи қиёмат, ки ҳеҷ шакке дар он нест, гирд месварад ва чӣ касе аз Худо ба гуфтор ростгӯтар аст?'),
(581, 4, 88, 'Чист шуморо, ки дар бораи мунофиқон ду гурӯҳ шудаед ва ҳол он ки Худо ононро ба сабаби кирдорашон ронда сохтааст? Оё мехоҳед касеро, ки Худо гумроҳ кардааст, ҳидоят кунед? Ва ту роҳе барои касе, ки Худованд гумроҳаш кардааст, натавонӣ ёфт!'),
(582, 4, 89, 'Дӯст доранд ҳамчунон, ки худ ба роҳи куфр мераванд, шумо низ кофир шавед, то баробар гардед. Пас бо ҳеҷ як аз онон дӯстӣ макунед, то он гоҳ ки дар роҳи Худо муҳоҷират кунанд. Ва агар рӯй гардонанд, дар ҳар ҷо ки онҳоро биёбед, бигиреду бикушед ва ҳеҷ як аз онҳоро ба дӯстиву ёрӣ интихоб накунед.'),
(583, 4, 90, 'Ғайри касоне, ки ба қавме, ки миёни шумо ва онҳо паимонест, мепайванданд ё худ назди шумо меоянд, дар ҳоле, ки аз ҷангидан бо шумо ё ҷангидан бо қавми худ малул шуда бошанд. Ва агар Худо мехост, бар шумо пирӯзашон месохт ва бо шумо ба ҷанг бармехостанд. Пас ҳар гоҳ канора гирифтанд ва бо шумо наҷангиданд ва ба шумо пешниҳоди сулҳ карданд, Худо ҳеҷ роҳе барои шумо бар зидди онҳо накушода аст.'),
(584, 4, 91, 'Гурӯҳи дигареро хоҳед ёфт, ки мехоҳанд аз шумо ва қавми худ дар амон бошанд, инҳо хар гоҳ, ки ба куфр даъват шаванд, ба он бозгарданд. Пас агар худро ба каноре накашанд ва сулҳ накунанд ва аз аъмоли хеш бознаистанд, ононро ҳар ҷо, ки ёфтед, бигиреду бикушед, ки шуморо бар онон ҳуҷҷате ошкор додаем!'),
(585, 4, 92, 'Ҳеҷ мӯъминеро нарасад, ки мӯъмини дигарро ҷуз ба хато бикушад. Ва ҳар кас, ки мӯьминеро ба хато бикушад, бояд ки бандае мӯъминро озод кунад ва хунбаҳояшро ба хонаводааш таслим кунад, магар он, ки хунбаҳоро бибахшанд. Ва агар кушташуда мӯъмин ва аз қавмест, ки душмани шумост, фақат бандаи мӯъминеро озод кунад ва агар аз қавмест, ки бо шумо паймон бастаанд, хунбаҳо ба хонаводааш пардохт шавад ва бандаи мӯъминеро озод кунад ва ҳар кас, ки бандае наёбад, барои тавба ду моҳ пай дар пай рӯза бигирад. Ва Худо донову ҳаким аст!'),
(586, 4, 93, 'Ва ҳар мӯъминеро барқасд бикушад, ҷазои у ҷаҳаннам аст, ки дар он абадӣ хоҳад буд ва Худо бар ӯ хашм гирад ва лаънаташ кунад ва барояш азобе бузург омода созад!'),
(587, 4, 94, 'Эй касоне, ки имон овардаед, чун барои ҷиҳод раҳсипор шавед, нек тафаҳҳус (санҷиш) кунед. Ва ба он кас, ки ба, р шумо салом гӯяд, магӯед, ки мӯъмин нести. Шумо бархӯрдорӣ аз зиндагии дунёро меҷӯед ва ҳол он ки ғаниматҳои бисёр назди Худост. Шумо пеш аз ин чунон будед, вале Худо бар шумо миннат ниҳод. Пас санҷиш кунед, ки Худо бар амалҳоятон огоҳ аст!'),
(588, 4, 95, 'Мӯъминоне, ки бе ҳеҷ рангу осебе аз ҷанг сар метобанд, бо касоне, ки ба молу ҷони хеш дар роҳи Худо ҷиҳод мекунанд, баробар нестанд. Худо касонеро, ки ба молу ҷони хеш ҷиҳод мекунанд, бар онон, ки аз ҷанг сар метобанд, ба дараҷоте бартарӣ додааст. Ва Худо ҳамаро ваъдаҳои некӯ додааст. Ва ҷиҳодкунандагонро бар онҳо, ки аз ҷиҳод сар метобанд, ба музде бузург фазилат додааст.'),
(589, 4, 96, 'Дараҷаҳое аз ҷониби Худо ва омурзишу раҳмате, ки Ӯ омурзанда ва меҳрубон аст!'),
(590, 4, 97, 'Касоне ҳастанд, ки фариштагон ҷонашонро меситонанд, дар ҳоле ки бар хештан ситам карда буданд. Аз онҳо мепурсанд: «Дар чӣ коре будед?» Гӯянд: «Мо дар рӯи замин мардуме будем бечора». Фариштагон гӯянд: «Оё замини Худо паҳновар набуд, ки дар он муҳоҷират кунед?» Макони инҳо ҷаҳаннам аст ва саранҷомашон бад!'),
(591, 4, 98, 'Ғайри мардону занон ва кӯдакони кори нотавоне, ки ҳеҷ чорае наёбанд ва ба ҳеҷ ҷо роҳ набаранд.'),
(592, 4, 99, 'Шояд, ки Худояшон афв кунад, ки Худо афвкунандаву омурзанда аст!'),
(593, 4, 100, 'Он кас, ки дар роҳи Худо тарки ватан кунад, дар рӯи замин бархӯрдориҳои бисёр ва кушоишҳо хоҳад ёфт. Ва ҳар кас, ки аз хонаи хеш берун ояд, то ба сӯи Худо ва расулаш ҳиҷрат кунад ва он гоҳ марг ӯро дарёбад, муздаш бар ӯҳдаи Худост ва Худо омурзандаву меҳрубон аст!'),
(594, 4, 101, 'Ва чун дар замин сафар кунед, гуноҳе нест, ки агар бими он доштед, ки кофирон ба шумо зиён расонанд, намози хеш кутоҳ кунед. Зеро кофирон душмани ошкори шумо ҳастанд!'),
(595, 4, 102, 'Ва чун ту дар миёнашон бошӣ ва барояшон намоз барпо кунӣ, бояд, ки гурӯҳе аз онҳо бо ту ба намоз биистанд ва силоҳҳои хеш бардоранд. Ва чун саҷда ба поён бурданд, баробари душман шаванд, то гурӯҳи дигар, ки намоз нахондаанд, биёянд ва бо ту намоз бихонанд. Онон низ ҳушёр бошанд ва силоҳҳои хеш бигиранд. Зеро кофирон дӯст доранд, ки шумо аз силоҳҳо ва матоъи худ ғофил шавед, то якбора бар шумо битозанд. Ва гуноҳе, содир накардаед, ҳар гоҳ аз борон дар ранҷ будед ё бемор будед, силоҳҳои худ бигзорэд, вале ҳушёрона дар фикри душман бошед. Албатта Худо барои кофирон азобе хсркунанда омода сохтааст!'),
(596, 4, 103, 'Ва чун намозро ба поён бурдед, Худоро истода ё нишаста ва ё ба паҳлӯ хобида ёд кунед. Ва чуч аз душман эмин гаштед, намозро тамом адо кунед, ки намоз бар мӯъминон дар вақтҳои муайян воҷиб гашта аст.'),
(597, 4, 104, 'Ва дар даст ёфтан ба он қавм (кофирон) сустӣ накунед. Агар шумо озор мебинед, онҳо низ чун шумо озор мебинанд, вале шумо аз Худо чизеро умед доред, ки онон умед надоранд. Ва Худо донову ҳаким аст!'),
(598, 4, 105, 'Мо ин китобро ба ростӣ бар ту нозил кардем, то ба он чизе, ки Худо ба ту омӯхтааст, миёни мардум доварӣ кунӣ ва ба нафъи хоинон ба мухосамат (хусумат) бармахез!'),
(599, 4, 106, 'Ва аз Худо бахшиш бихоҳ, ки Ӯ бахшанда ва меҳрубон аст.'),
(600, 4, 107, 'Ва ба хотири касоне, ки ба худ хиёнат меварзанд, ҷонибдорӣ макун, ки Худо хоинони гӯнаҳкорро дӯст надорад.'),
(601, 4, 108, 'Аз мардум пинҳон медоранд ва аз Худо пинҳон намедоранд, зеро он гоҳ ки шабҳангом суханоне, ки Худо аз он нохушнуд бувад, мегуфтанд, Худо бо онҳо буд ва Худо бар ҳар чӣ мекарданд, иҳота дошт. (яъне медонист).'),
(602, 4, 109, 'Огоҳ бошед, ин шумоед, ки дар ин ҷаҳон аз онон сахт ҷонибдорӣ кардед, кист, ки дар рӯзи қиёмат аз онон дар баробари Худо ҷонибдорӣ кунад ё чӣ касе вакили онҳо хоҳад буд?'),
(603, 4, 110, 'Ва ҳар кӣ коре нописанд кунад ё ба худ ситам раво дорад, он гоҳ аз Худо омурзиш хоҳад, Худоро омурзанда ва меҳрубон хоҳад ёфт.'),
(604, 4, 111, 'Ва ҳар кӣ гуноҳе кунад, он гуноҳ ба зиёни худ кардааст ва Худо донову ҳаким аст?'),
(605, 4, 112, 'Ва ҳар кӣ хато ё гуноҳе кунад, он гоҳ бегуноҳеро ба он муттаҳам созад, албатта бори тӯҳмату гуноҳе ошкорро бар дӯши худ гирифтааст.'),
(606, 4, 113, 'Агар фазлу раҳмати Худо шомили ҳоли ту набуд, гурӯҳе аз кофирон қасди он доштанд, ки туро гумроҳ кунанд, вале онон ҷуз худро гумроҳ накунанд ва хеҷ зиёне ба ту нарасонанд. Ва Худо бар ту китобу ҳикмат нозил кард ва чизҳое ба ту омӯхт, ки аз ин пеш намедонистӣ ва лутфи Худо бар ту бузург аст!.'),
(607, 4, 114, 'Дар бисёре аз машваратҳои пинҳониашон фоидае нест, ғайри суханони онон, ки ба садақа додан ё некӣ кардан ва ё оштиҷӯӣ фармон медиҳанд. Ва он касро, ки барои хушнудии Худо чунин кунад, музди бузурге хоҳем дод.'),
(608, 4, 115, 'Ҳар кӣ пас аз ошкор шудани роҳи ҳидоят бо паёмбар мухолифат варзад ва аз роҳе ғайри роҳи мӯъминон пайравӣ кунад, ба он сӯе, ки писанди ӯст, бигардонем ва ба ҷаҳаннамаш партоем. Ва ҷаҳанрам саранҷоми бадест!'),
(609, 4, 116, 'Худо касеро, ки барои ӯ шарике қарор диҳад, намебахшад ва ҷуз он ҳар гуноҳеро барои ҳар кӣ хоҳад, мебахшад. Ва ҳар кас, ки барои Худо шарике қарор диҳад, сахт ба гумроҳӣ афтодаст.'),
(610, 4, 117, 'Намепарастанд ғайри Оллоҳ магар бутҳоро ва намехонанд ғайри Оллоҳ магар шайтоне саркашро.'),
(611, 4, 118, 'Худояш лаънат кард. Ва шайтон гуфт: «Гурӯҳе муайян аз бандагонатро ба фармони хеш мегирам.'),
(612, 4, 119, 'Ва албатта гумроҳашон мекунам ва орзуҳои ботил дар дилашон меафканам ва ба онон фармон медиҳам то гушҳои чорпоёнро бишикофанд. Ва ба онон фармон медиҳам, то хилқати Худоро дигаргун созанд». Ва ҳар кас, ки ба ҷои Худо шайтонро ба дӯстӣ қабул кунад, зиёне ошкор кардааст!'),
(613, 4, 120, 'Ба онҳо ваъда медиҳад ва ба орзушон меафканад ва шайтон онҳоро ғайри ба фиреб ваъда надиҳад!'),
(614, 4, 121, 'Маконашон дар ҷаҳаннам аст ва дар он ҷо роҳи гурезе нахоҳанд ёфт!'),
(615, 4, 122, 'Ва касонеро, ки имон овардаанд ва корҳои некӯ кардаанд, ба биҳиштҳое медароварем, ки дар он наҳрҳо равон аст ва дар он ҷо ҷовидонанд. Ваъдаи барҳаққи Худованд аст ва чӣ касе аз Ӯ ростгӯйтар аст?'),
(616, 4, 123, 'На мувофиқи муроди шумост ва на музофиқи муроди аҳли китоб, ки ҳар кас, кн содиркунандаи кори баде шавад, ҷазояшро бубинад ва ғайри Худо барон худ дӯсту ёваре наёбад.'),
(617, 4, 124, 'Ва ҳар кас, ки кори шоистае кунад, чӣ зан ва чӣ мард, агар мӯъмин бошад, ба биҳишт меравад ва ба қадри он пӯсте, ки бар пушти донаи хурмост, ба кас ситам намешавад.'),
(618, 4, 125, 'Дини чӣ касс беҳтар аз дини касест, ки ба ихлос рӯй ба ҷониби Худо кард ва некӯкор буд ва аз дини ҳанифи Иброҳим пайравӣ кард? Ва Худо Иброҳимро ба дӯстии худ баргузид (гирифт).'),
(619, 4, 126, 'Аз они Худост ҳар чӣ дар осмонҳову замин аст ва Худо бар ҳар чизе бо илмаш иҳота дорад.'),
(620, 4, 127, 'Аз ту дар бораи занон фатво мехоҳанд, бигӯ: «Худо дар бораи онон ба он чӣ дар ин китоб бар шумо хонда мешавад, фатво додааст. Ин фатво дар боби онҳо ва занони падармурдаест, ки хаққи муқаррарашонро намепардозед ва мехоҳед онҳоро ба никоҳи худ дароваред ва низ дар боби кӯдакони нотавон аст. Ва бояд, ки дар бораи ятимон ба адолат рафтор кунед ва ҳар кори неке, ки анҷом медиҳед, Худо ба он огоҳ аст.'),
(621, 4, 128, 'Агар зане дарёфт, ки шавҳараш бо ӯ бемеҳр ва аз ӯ безор шудааст, боке нест, ки ҳар ду дар миёни худ тарҳи оштӣ афкананд, ки оштӣ беҳтар аст. Ва бахиливу мумсиқӣ бар нафсҳои мардум ғалаба дорад. Ва агар некӣ ва парҳезгорӣ кунед, Худо ба ҳар чӣ мекунед, огоҳ аст!'),
(622, 4, 129, 'Ҳарчанд бикушед, ҳаргиз натавонед, ки дар миёни занон ба адолат рафтор кунед. Лекин якбора ба сӯи яке майл накунед то дигареро саргашта раҳо карда бошед. Агар аз дари оштӣ дароед ва парҳезгорӣ кунед, Худо омурзанда ва меҳрубон аст!'),
(623, 4, 130, 'Ва агар он ду аз якдигар ҷудо шаванд. Худо ҳар дуро ба камоли фазли хеш бениёз созад, ки Худо кушоишдиҳанда ва ҳаким аст!'),
(624, 4, 131, 'Аз они Худост он чӣ дар осмонҳо ва замин аст. Ва албатта аҳли китобро, ки пеш аз шумо буданд ва низ шуморо супориш кардем, ки аз Худо битарсед, агар ҳам куфр варзед, боз ҳам он чӣ дар осмонҳо ва он чӣ дар замин аст, аз они Худост ва Ӯст бениёз ва лоиқи ситоиш!'),
(625, 4, 132, 'Ва аз они Худост он чӣ дар осмонҳо ва замин аст ва Худо корсозиро кофист!'),
(626, 4, 133, 'Эй мардум, агар Ӯ бихоҳад, ҳамаи шуморо аз миён мебарад ва мардуме дигарро меоварад, ки Худо бар ин кор қодир аст!'),
(627, 4, 134, 'Ҳар кас, ки савоби инҷаҳониро металабад, бидонад, ки савоби инҷаҳониву онҷаҳонӣ дар назди Худост. Ва Ӯ шунаво ва биност!'),
(628, 4, 135, 'Эй касоне, ки имон овардаед, ба адолат фармонраво бошед ва барои Худо шоҳидӣ диҳед, ҳарчанд ба зиёни худ ё падару модар ё хешовандони шумо — чӣ тавонгар ва чӣ дарвеш бошад. Зеро Худо ба он ду сазовортар аст. Пас ба ҳавои нафс паправӣ макунед, то аз шаҳодати ҳақ рӯй гардонед. Чӣ забонбозӣ кунед ё аз он рӯй тобед, Худо ба ҳар чӣ мекунед, огоҳ аст!'),
(629, 4, 136, 'Эй касоне, ки имон овардаед, ба Худову паёмбараш ва ин китоб, ки бар паёмбараш нозил карда ва он китоб, ки пеш аз он нозил карда, ба ҳақиқат имон биёваред. Ва ҳар кӣ ба Худову фариштагонаш ва китобҳояш ва паёмбаронаш ва ба рӯзи қиёмат кофир шавад, сахт дар гумроҳӣ афтодааст.'),
(630, 4, 137, 'Албатта Худованд ононро, ки имон овардаанд, сипас кофир шуданд ва боз имон оварданд, сипас кофир шуданд ва ба куфри хеш афзуданд, нахоҳад бахшид ва ба роҳи рост ҳидоят нахоҳад кард.'),
(631, 4, 138, 'Мунофиқонро хабар деҳ, ки азобе дардовар барояшон омода шудаст,'),
(632, 4, 139, 'касоне, ки ба ҷои мӯъминон кофиронро ба дӯстӣ мегиранд, оё иззату тавоноиро назди онон меҷӯянд, дар ҳоле, ки иззат ба тамомӣ аз они Худост?'),
(633, 4, 140, 'Ва аз ин пеш, дар ин китоб бар шумо нозил кардаем, ки чун шунидед, касоне оёти Худоро инкор мекунанд па онро ба масхара мегиранд, бо онҳо манишинед, то он гоҳ, ки ба сухане дигар пардозанд. Вагарна шумо низ ҳамонанди онҳо хоҳед буд. Ва Худо ҳамаи мунофиқону кофиронро дар ҷаҳаннам гирд меоварад.'),
(634, 4, 141, 'Касоне, ки ҳамеша интизори шумо ҳастанд, пас агар аз ҷониби Худо пирӯзӣ насибашон шавад, мегӯянд: «Магар мо хамроҳи шумо набудем?» Ва агар пирӯзӣ насиби кофирон шавад, мегӯянд: «Оё чунин: набуд, ки бар шумо ғалаба ёфта будем ва мӯъминонро аз осеб расонидан ба шумо боздоштем?» Дар рӯзи қиёмат Худо миёни шумо ҳукм мекунад ва Ӯ ҳаргиз барои кофирон ба зиёни мусалмонон роҳе накушодааст.'),
(635, 4, 142, 'Мунофикон Худоро фиреб медиҳанд ва ҳол он ки Худо онҳоро фиреб медиҳад. Ва чун ба намоз бархезанд, бо сустӣ бархезанд ва барои худнамоӣ намоз кунанд ва дар намоз, ҷуз андаке, Худоро ёд накунанд.'),
(636, 4, 143, 'Саргаштагони миёни куфру имонанд: на бо инҳо ва на бо онҳо. Он кӣ Худо гумроҳаш кунад, ҳеҷ роҳе барои ӯ нахоҳӣ ёфт.'),
(637, 4, 144, 'Эй касоне, ки имон овардаед, ба ҷои мӯъминон кофиронро ба дӯстӣ магиред. Оё коре мекунед, ки барои Худо ба зиёни худ хуҷҷате ошкор эчод кунед?'),
(638, 4, 145, 'Албатта мунофиқон дар поёнтарин табақоти оташ ҳастанд ва ҳаргиз барояшон ёваре намеёбӣ,'),
(639, 4, 146, 'ғайри онҳо, ки тавба кардаанд ва худро ислоҳ карданд ва ба Худо тавассул ҷӯстаанд ва барои Худо аз рӯи ихлос ба дин гаравидаанд. Инҳо дар зумраи мӯъминонанд ва Худо ба мӯъминон савобе азим хоҳад дод!'),
(640, 4, 147, 'Чаро Худо шуморо азоб кунад, агар сипосгузор бошед ва имон оваред? Дар ҳоле ки шукрпазир ва доност!'),
(641, 4, 148, 'Худо баланд кардани садоро ба бадгӯӣ дӯст надорад, магар аз он кас, ки ба ӯ ситаме шуда бошад ва Худо шунаво ва доност!'),
(642, 4, 149, 'Агар кори некеро ба ошкор анҷом диҳед ё ба пинҳон, ё кирдоре нописандро даргузаред. Худо афвкунанда ва тавоност!'),
(643, 4, 150, 'Касоне ҳастанд, ки ба Худо ва паёмбаронаш кофир мешаванд ва мехоҳанд миёни Худову паёмбаронаш ҷудоӣ афкананд ва мегӯянд, ки баъзеро мепазирем ва баъзеро намепазирем ва мехоҳанд дар мобайни куфру имон роҳ гиред.'),
(644, 4, 151, 'Инҳо дар хақиқат кофиронанд ва мо барои кофирон азобе хоркунанда омода сохтаем.'),
(645, 4, 152, 'Ва касоне, ки ба Худо ва паёмбаронаш имон овардаанд ва. миёни паёмбаронаш ҷудоӣ наандохтаанд, савобашонро Худо хоҳад дод ва Худо омурзандаву меҳрубон аст.'),
(646, 4, 153, 'Аҳли китоб аз ту мехоҳанд, ки барояшон китобе аз осмон нозил кунӣ. Инҳо бузургтар аз инро аз Мӯсо талаб карданд ва гуфтанд: «Худоро ба ошкор ба мо нишон деҳ». Ба сабаби ин сухани куфромезашон оташак онҳоро фурӯ гирифт. Ба пас аз он ки мӯъҷизаҳое барояшоя омада буд, гӯсолаеро ба худоӣ гирифтанд ва Мо ононро бахшидем ва Мӯсоро мӯъҷизае ошкор додем.'),
(647, 4, 154, 'Ва ба хотири паймоне, ки бо онҳо баста будем, кӯҳи Турро бар болои сарашон бидоштем ва гуфтем: «Саҷдакунон аз он дар дохил шавед ва дар рӯзи шанбе аз ҳад нагузаред». Ва аз онҳо паймоне сахт гирифтем.'),
(648, 4, 155, 'Пас ба сабаби паймон шикастанашон ва кофир шуданашон ба оёти Худо ва ба ноҳақ куштани паёмбарон ва ин, ки гуфтанд: «Дилҳои мо бастааст». Худо бар дилҳояшон мӯҳр ниҳодааст ва ғайри андаке имон намеоваранд'),
(649, 4, 156, 'ва низ ба сабаби куфрашон ва он тӯҳмати бузург, ки ба Марям заданд'),
(650, 4, 157, 'ва низ ба он сабаб, ки гуфтанд; Мо Масеҳ — писари Марям -- паёмбари Худоро куштем». Ва ҳол он, ки онон Масеҳро накуштанд ва бар дор накарданд, балки кор барояшон монанд шуд. Албатта онҳо, ки дар бораи ӯ ихтилоф мекарданд, худ дар шакку шубҳа буданд ва ба он яқин надоштанд. Танҳо пайрави гумони худ буданд ва Исоро ба яқин накушта буданд,'),
(651, 4, 158, 'балки Худованд ӯро ба назди худ боло бурд. ки Худо пирӯзманду ҳаким аст!'),
(652, 4, 159, 'Ва ҳеҷ як аз аҳли китоб нест, магар он ки пеш аз маргаш ба ӯ имон оварад ва Исо дар рӯзи қиёмат ба имонашон гувоҳӣ хоҳад дод'),
(653, 4, 160, 'ва ба ҷазои ситаме, ки яҳудон раво доштанд ва манъ кардани бисёрашон аз роҳи Худо, он чизҳои покизаро, ки бар онон ҳалол буд, ҳаром кардем.'),
(654, 4, 161, 'Ва низ ба сабаби рибохорияшон ва ҳол он ки аз он манъ шуда буданд ва хӯрдани молҳои мардумро ба ботил. Ва мо барои кофиронашон азобе дардовар муҳайё кардаем.'),
(655, 4, 162, 'Вале донишмандонашон ва он мӯъминонеро, ки ба он чӣ бар ту ва ба он чӣ пеш аз ту нозил шуда, имон доранд ва намозгузорону закотдиҳандагон ва мӯъминон ба Худову рӯзи қиёматро аҷри бузурге хоҳем дод!'),
(656, 4, 163, 'Мо ба ту ваҳй кардем, ҳамчунон ки ба Нӯҳ ва паёмбарони баъд аз ӯ ваҳй кардаем ва ба Иброҳиму Исмоил ва Исҳоқу Яъқуб ва наберагони Яъқуб ва Исо ва Айюб ва Юнус ва Ҳорун ва Сулаймон ваҳй кардаем ва ба Довуд «Забур»-ро арзонӣ доштем.'),
(657, 4, 164, 'Ва паёмбароне, ки пеш аз ин достонҳояшонро барои ту гуфтаем ва онон, ки достонҳояшонро барои ту нагуфтаем. Ва Худо бо Мӯсо сухан гуфт, сухан гуфтане бевосита.'),
(658, 4, 165, 'Паёмбароне муждадиҳанда ва бимдиҳанда, то аз он пас мардумро бар Худо ҳуҷҷате набошад ва Худо пирӯзманду ҳаким аст!'),
(659, 4, 166, 'Вале Худо ба он чӣ бар ту нозил кард, шоҳидӣ медиҳад, ки ба илми худ нозил кардааст ва фариштагон низ шоҳидӣ медиҳанд ва Худо шоҳидиро басанда аст!'),
(660, 4, 167, 'Албатта онон, ки кофир шудаанд ва аз роҳи Худо рӯй гардонидаанд, сахт ба гумроҳӣ афтоданд.'),
(661, 4, 168, 'Касонеро, ки кофир шудаанд ва ситам кардаанд, Худованд намебахшад ва ба ҳеҷ роҳе ҳидоят намекунад,'),
(662, 4, 169, 'магар ба роҳи ҷаҳаннам, ки абадӣ дар он бошанд! Ва ин кор бар Худо осон аст.'),
(663, 4, 170, 'Эй мардум, паёмбаре ба ҳақ аз ҷониби Худо бар шумо фиристода шуд, пас ба ӯ имон биёваред, ки хайри шумо дар он аст. Ва агар ҳам кофир шавед, аз они Худост ҳар чӣ дар осмонҳову замин аст. Ва Ӯ донову ҳаким аст!'),
(664, 4, 171, 'Эй аҳли китоб, дар дини хеш аз ҳад нагузаред ва дар бораи Худо ҷуз сухани ҳақ магӯед. Албатта Исо — писари Марям, паёмбари Худо ва калимаи Ӯ буд, ки ба Марямаш афканд ва рӯҳе аз Ӯ буд. Пас ба Худо ва паёмбарокаш имон биёваред ва магӯед, ки се аст. Аз ин андешаҳо бозистед, ки хайри шумо дар он хоҳад буд. Ҷуз ин нест, ки Оллоҳ худоест якто. Пок аст аз ин, ки соҳиби фарзанде бошад. Аз они Ӯст он чӣ дар осмонҳову замин аст ва Худо корсозиро кофист!'),
(665, 4, 172, 'Масеҳ нанг надошт, ки яке аз бандагони Худо бошад ва малоикаи муқарраб низ нанг надоранд. Ҳар кӣ аз парастиши Худованд рӯй гардонад, ва саркашӣ кунад, бидонад, ки Худо ҳамаро дар назди худ маҳшур (ҳамсӯҳбат) хоҳад сохт.'),
(666, 4, 173, 'Аммо онон, ки имон овардаанд ва корҳои нек кардаанд, муздашонро ба тамомӣ хоҳад дод ва аз фазли хеш бар он хоҳад афзуд. Аммо касонеро, ки ибову саркашӣ кардаанд, ба азобе дардовар азоб хоҳад кард ва барои худ ҷуз Худо ҳеҷ дӯсту ёваре нахоҳанд ёфт.'),
(667, 4, 174, 'Эй мардум, аз ҷониби Парвардигоратон бар шумо хуҷҷате омад ва барои шумо нуре ошкор нозил кардаем.'),
(668, 4, 175, 'Аммо ононро, ки ба Худо имон овардаанд ва ба Ӯ тавассул ҷустаанд, ба остони фазлу раҳмати хеш дароварад ва ба роҳе рост ҳидоят кунад.'),
(669, 4, 176, 'Аз ту фатво мехоҳанд, бигӯй, ки Худо дар бораи калола бароятон фатво медиҳад: Ҳар гоҳ марде, ки фарзанде надошта бошад, бимирад ва ӯро хоҳаре бошад, ба он хоҳар нисфи мероси ӯ мерасад. Агар хоҳарро низ фарзанде набошад, бародар аз ӯ мерос мебарад. Агар он хоҳарон ду тан буданд, ду сеяки дороиро мерос мебаранд. Ва агар чанд бародару хоҳар буданд, ҳар мард баробари ду зан мебарад. Худо барои шумо баён мекунад то гумроҳ нашавед ва Ӯ аз ҳар чизе огоҳ аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Эй касоне, ки имон овардаед, ба паймонҳо вафо кунед. Ҳайвоноти чаҳорпо, ғайри онҳое, ки аз ин пас бароятон гуфта мешавад, бар шумо ҳалол шудаанд ва он чиро, ки дар ҳоли эҳром сайд мекунед, ҳалол нашуморед. Худо ба ҳар чӣ мехоҳад, ҳукм мекунад!'),
(671, 5, 2, 'Эй касоне, ки имон овардаед, шиорҳои Худо ва моҳи ҳарому қурбониро чӣ холӣ аз қилода ва чӣ бо қилода (гарданбанд) ҳурмат нашканед ва озори ононро, ки ба талаби рӯзӣ ва хушнудии Парвардигорашон нияти байтулҳаром кардаанд, раво мадоред. Ва чун аз эҳром берун омадед, сайд кунед ва бадбинӣ бо қавме, ки шуморо аз масҷидулҳаром боздоштанд, водоратон насозад, ки аз ҳадди хеш таҷовуз кунед ва дар некӯкорӣ ва парҳез ҳамкорӣ кунед, на дар гуноҳу таҷовуз. Ва аз Худой битарсед, ки Ӯ ба сахтӣ уқубат мекунад.'),
(672, 5, 3, 'Ҳаром шуд бар шумо мурда ва хун ва гӯшти хук ва ҳар ҳайвоне, ки ба ҳангоми куштанаш номи дигаре ҷуз Оллоҳро бар ӯ бигӯянд ва он чӣ хафа шуда бошад ё ба санг зада бошанд ё аз боло дарафтода бошад ё ба шохи ҳайвоне дигар бимирад ё даррандагон аз он хӯрда бошанд, ғайри он ки забҳаш кунед. Ва низ ҳар чӣ бар пои бутон забҳ шавад ва он чӣ ба воситаи тирҳои қимор қисмат кунед, ки ин кор худ нофармонист. Имрӯз кофирон аз бозгашти шумо аз дини хеш ноумед шудаанд. Аз онҳо матарсед, аз Ман битарсед. Имрӯз дини шуморо ба камол расонидам ва неъмати худ бар шумо тамом кардам ва исломро дини шумо баргузидам. Пас ҳар кӣ дар гуруснагӣ бечора монад, бе он ки қасди гуноҳ дошта бошад, бидонад, ки Худа омурзанда ва меҳрубон аст!'),
(673, 5, 4, 'Аз ту мепурсанд, ки чӣ чизҳое бар онҳо ҳалол шудааст. Бигӯ: «Чизҳои покиза бар шумо ҳалол шуда ва низ хӯрдани сайди он ҳайвон, ки ба он сайд кардан омӯхтаед, чун паррандагони шикорӣ ва сагони шикорӣ, ҳар гоҳ онҳоро ба он сон, ки Худоятон омӯхтааст, таълим дода бошед. Аз он сайд, ки бароятон мегиранд ва нигоҳ медоранд, бихӯред ва номи Худоро бар он бихонед ва аз Худо битарсед, ки Ӯ зудҳисобкунанда аст!'),
(674, 5, 5, 'Имрӯз чизҳои покиза бар шумо ҳалол шудааст. Таъоми аҳли китоб бар шумо ҳалол аст ва таъоми шумо низ бар онҳо ҳалол аст. Ва низ занони солеҳи мӯъмина ва занони солеҳи аҳли китоб ҳар гоҳ маҳрашонро бипардозед, ба таври заношӯӣ на зинокорӣ ва дӯстгирӣ бар шумо ҳалоланд. Ва ҳар кас, ки ба ислом кофир шавад, амалаш ночиз шавад ва дар охират аз зиёнкорон хоҳад буд.'),
(675, 5, 6, 'Эй касоне, ки имон овардаед, чун ба намоз бархостед, рӯйҳову дастҳоятонро то оринч бишӯед ва сари худро масҳ кашед, поҳоятонро то қузак (буҷулак) бишӯед. Ва агар ҷунуб (олуда) будед, худро пок созед. Ва агар бемор ё дар сафар будед, ё аз ҷои қазои хоҷат омада будед, ё бо занон наздикӣ карда будед ва об наёфтед, бо хоки пок таяммум кунед ва сурату дастҳоятонро бо он маҳе кунед. Худо намехоҳад шумо дар ранҷ афтед, балки мехоҳад, ки шуморо покиза созад ва неъматашро бар шумо комил кунад, бошад, ки шукр кунед!'),
(676, 5, 7, 'Неъматеро, ки Худо ба шумо додааст ва паймонеро, ки ба шумо бастааст, ба он ҳангом, ки гуфтед, шунидем ва фармонбардорӣ кардем, ёд оваред. Ва аз Худо битарсед, ки Худо ба он чӣ дар дилҳо мегузарад, огоҳ аст!'),
(677, 5, 8, 'Эй касоне, ки имон овардаед, барои Худо, ҳақ гуфтанро бар пой доред ва ба адл гувоҳӣ диҳед. Бадбинӣ бо гурӯҳе дигар водоратон накунад, ки адолат накунед. Адолат варзед, ки ба тақво наздиктар аст ва аз Худо битарсед, ки Ӯ ба ҳар коре, ки мекунед, огоҳ аст!'),
(678, 5, 9, 'Худо ба касоне, ки имон овардаанд ва корҳои некӯ кардаанд, ваъдаи омурзиш ва музде бузург додааст.'),
(679, 5, 10, 'Ва онон, ки кофир шудаанд ва оёти моро дурӯғ баровардаанд, аҳли ҷаҳаннаманд.'),
(680, 5, 11, 'Эй касоке, ки имон овардаед, аз неъмате, ки Худо ба шумо арзонӣ доштааст, ёд кунед: он гоҳ ки гурӯҳе қасди он карданд, то бар шумо даст ёзанд, ва Худо дасти ононро аз шумо кӯтоҳ кард. Аз Худо битарсед. Ва мӯъминон бар Худо таваккал мекунанд.'),
(681, 5, 12, 'Худованд аз банӣ-Исроил паймон гирифт ва аз миёни онон дувоздаҳ нақиб ҷудо кардем. Ва Худо гуфт: «Агар намоз бихонед ва закот бидиҳед ва ба паёмбарони ман имон биёваред ва ёриашон кунед ва ба Худо қарзи нек бидиҳед, ман бо шумоям. Бадиҳоятонро мезудоям ва шуморо ба биҳиштҳое дохил мекунам, ки дар он наҳрҳо равон бошад. Ва ҳар кас аз шумо, ки аз он пас кофир шавад, роҳи ростро гум кардааст».'),
(682, 5, 13, 'Ва аҳли китоб, ҷуз андаке аз онҳо, чун паймонашонро шикастанд, лаънаташон кардем ва дилҳояшонро сахт гардонидем. Калимотро аз маънии худ тағйир месозанд ва аз он панд, ки ба онҳо дода шуда буд, насиби хеш фаромӯш кардаанд ва ҳамеша аз корҳои хоинонаашон огоҳ мешавӣ. Афвашон кун ва аз гуноҳашон даргузар, ки Худо некӯкоронро дӯст медорад.'),
(683, 5, 14, 'Ва аз касоне, ки гуфтанд, ки мо насронӣ ҳастем, паймон гирифтем. Пас қисмате аз пандҳоеро, ки ба онҳо дода будем, фаромӯш карданд ва Мо низ миёни онҳо то рӯзи қиёмат кинаву душманӣ афкандем. Ба зуди Худо ононро аз корҳое, ки мекунанд, огоҳ хоҳад сохт!'),
(684, 5, 15, 'Эй аҳли китоб, паёмбари Мо назди шумо омад, то бисёре аз китоби Худоро, ки пинҳон медоштед, бароятон баён кунад ва аз бисёре даргузарад ва аз ҷониби Худо нуре ва китобе зоҳиру ошкор бар шумо нозил шудааст.'),
(685, 5, 16, 'То Худо бо он ҳар касро, ки дар паи хушнудии ӯст, ба роҳҳои саломат ҳидоят кунад ва ба фармони худ аз торикӣ ба рӯшноияшон бубарад ва ононро ба роҳи рост ҳидоят кунад.'),
(686, 5, 17, 'Онҳо, ки гуфтаид, ки Худо ҳамон Масеҳ —писари Марям аст, кофир шуданд. Бигӯй: «Чӣ касе метавонад азоби Худоро дафъ кунад, агар ирода кунад, Масеҳ — писари Марям ва модараш ва ҳамаи аҳли заминро ба ҳалокат расонад?» Аз они Худост фармонравоии осмонҳову замин ва ҳар чӣ мобайни онҳост. Он чӣ мехоҳад, меофаринад ва ба ҳар чиз тавоност!'),
(687, 5, 18, 'Яҳудиён ва масеҳиён гуфтанд, ки мо фарзандону дӯстони Худо ҳастем. Бигӯй: «Пас чаро шуморо ба ҷазои гуноҳонатон азоб мекунад? Балки шумо инсонҳое аз ҷумлаи офаридагон ҳастед». Ҳар касро, ки бихоҳад, мебахшад ва ҳар касро, ки бихоҳад, азоб мекунад ва аз они Худост фармонравоии осмонҳову замин ва он чӣ дар миёни онҳост ва бозгаштн ҳама ба назди Ӯст.'),
(688, 5, 19, 'Эй аҳли китоб, фиристодаи Мо дар замоне, ки паёмбароне набуданд, фирпстода шуд, то ҳақро бар шумо ошкор кунад ва нагӯед, ки муждадиҳанда ва бимдиҳандае бар мо фиристода нашудааст. Инак, он муждадиҳанда ва бимдиҳанда омадааст ва Худо бар ҳар чиз тавоност!'),
(689, 5, 20, 'Ва Мӯсо ба қавми худ гуфт: «Эй қавми ман, неъматеро, ки Худо бар шумо арзонӣ доштааст, ёд кунед, ки аз миёни шумо паёмбароне ихтиёр кард ва шуморо соҳибони ихтиёри хеш гардонид ва ба шумо чизҳое дод, ки ба ҳеҷ як аз мардуми ҷаҳон ато накардааст.'),
(690, 5, 21, 'Эй қавми ман, ба замини муқаддасе, ки Худо бароятон муқаррар кардааст, дохил шавед ва бозпас магардед, ки зиёндида бозмегардед».'),
(691, 5, 22, 'Гуфтанд; «Эй Мӯсо, дар он ҷо мардуме пурзӯранд ва мо ба он сарзамин дарнаёем, то он гоҳ, ки он пурзӯрон берун шаванд. Агар онҳо аз он сарзамин берун шаванд, ба он дохил шавем».'),
(692, 5, 23, 'Ду мард аз онон, ки парҳезгорӣ пеша доштанд ва Худо неъматашон ато карда буд, гуфтанд: «Аз ин дарвоза бар онҳо дохил шавед. Ва чун ба шаҳр даромадед, шумо пирӯз хоҳед шуд, Ва бар Худо таваккал кунед, агар аз мӯъминон ҳастед!»'),
(693, 5, 24, 'Гуфтанд; «Эй Мӯсо, то вақте ки пурзӯрҳо дар он ҷоянд, ҳаргиз бад-он шаҳр дохил нахоҳем шуд. Мо ин ҷо менишинем, ту ва Парвардигорат бираведу ҷиҳод кунед».'),
(694, 5, 25, 'Гуфт: «Эй Парвардигори ман, ман танҳо молики нафси худ ва бародарам ҳастам. Миёни ман ва ин мардуми нофармон ҷудоӣ бияндоз».'),
(695, 5, 26, 'Худо гуфт: «Вуруд ба он сарзамин ба муддати чиҳил сол барояшон ҳаром шуд ва дар он биёбон саргардон хоҳанд монд. Пас барои ин нофармонон андӯҳгин мабош!»'),
(696, 5, 27, 'Ва достони ростини ду писари Одамро барояшон бихон, он гоҳ ки қурбоние карданд. Аз якешон пазируфта шуд ва аз дигаре пазируфта нашуд. Гуфт: «Туро мекушам». Гуфт; «Худо қурбонии парҳезгоронро мепазирад.'),
(697, 5, 28, 'Агар ту бар ман даст кушоӣ, ки маро бикушӣ, ман бар ту даст накушоям, ки туро бикушам. Ман аз Худо, ки Парвардигори ҷахониён аст, метарсам.'),
(698, 5, 29, 'Мехоҳам, ки ҳам гуноҳи маро ба гардан гирӣ ва ҳам гуноҳи худро, то аз дӯзахиён гардӣ, ки ин аст ҷазои ситамкорон».'),
(699, 5, 30, 'Нафсаш ӯро ба куштани бародар тарғиб кард ва ӯро кушт ва аз зиёнкорон гардид.'),
(700, 5, 31, 'Худо зоғеро фиристод то заминро бикобад ва ба ӯ биёмузад, ки чӣ гуна ҷасади бародари худ пинҳон созад. Гуфт: «Вой бар ман, натавонам монанди ин зоғ бошам ва ҷасади бародарамро дафн кунам». Ва дар зумраи пушаймонон даромад.'),
(701, 5, 32, 'Аз ин рӯ бар банӣ-Исроил муқаррар доштем, ки ҳар кас каси дигарро на ба қасоси катли касе ё содир кардани фасоде бар рӯи замин бикушад, чунон аст, ки ҳамаи мардумро кушта бошад. Ва ҳар кас, ки ба ӯ ҳаёт бахшад, чун касест, ки ҳамаи мардумро ҳаёт бахшида бошад. Ва албатта паёмбарони Мо ҳамроҳ бо далелҳои равшан бар онҳо фиристода шуданд. Боз ҳам бисёре аз онҳо ҳамчунон бар рӯи замин аз ҳадди хеш таҷовуз мекарданд.'),
(702, 5, 33, 'Ҷазои касоне, ки бо Худо ва паёмбаронаш ҷанг мекунанд ва дар замин ба фасод мекӯшанд, он аст, ки кушта шаванд ё бар дор гарданд ё дастҳову поҳояшон яке аз чапу яке аз рост бурида шавад ё аз сарзамини худ бадарға шаванд. Инҳо расвоияшон дар ин ҷаҳон аст ва дар охират низ ба азобе бузург гирифтор оянд.'),
(703, 5, 34, 'Ғайри касоне, ки пеш аз он ки шумо бар онҳо даст ёбед, тавба кунанд. Пас бидонед, ки Худо омурзанда ва меҳрубон аст!'),
(704, 5, 35, 'Эй касоне, ки имон овардаед, аз Худо битарсед ва ба Ӯ қарибӣ ҷӯед ва дар роҳаш чиҳод кунед. Шояд, ки наҷот ёбед!'),
(705, 5, 36, 'Онҳое, ки кофир шудаанд, агар ҳамаи он чӣ дар рӯи замин аст ва ҳамонанди он аз они онҳо бошад ва бихоҳанд худро бо он аз азоби рӯзи қиёмат бозхаранд, аз онҳо пазируфта нашавад ва ба азоби дардовар гирифтор шаванд!'),
(706, 5, 37, 'Мехоҳанд, ки аз оташ берун оянд, дар ҳоле ки берун нахоҳанд шуд ва азобашон поянда аст.'),
(707, 5, 38, 'Дасти марди дузд ва зани дуздро ба ҷазои коре, ки кардаанд, бибуред. Ин уқубатест аз ҷониби Худо, ки Ӯ пирӯзманду ҳаким аст!'),
(708, 5, 39, 'Ҳар кас пас аз кирдори нописандаш тавба кунад ва ба салоҳ ояд, Худо тавбаи ӯро мепазирад, ки Ӯ омурзанда ва меҳрубон аст!'),
(709, 5, 40, 'Оё надонистаӣ, ки фармонравоии осмонҳову замин аз они Худост, ҳар киро бихоҳад, азоб мекунад ка ҳар киро бихоҳад, мебахшад ва бар ҳар коре тавоност?'),
(710, 5, 41, 'Эй паёмбар, ғамгин накунад туро кирдори онон, ки ба куфр мешитобанд. Чӣ онҳое, ки ба забон гуфтанд, ки имон овардем ва ба дил имон наёварданд ва чӣ он яҳудон, ки гӯш меандозанд, то дурӯғ банданд ва барои гурӯҳе дигар, ки худ назди ту намеоянд, суханчинӣ мекуканд ва сухани Худоро дигаргун месозанд ва мегӯянд: «Агар шуморо инчунин гуфт, бипазиред вагарна аз вай дурӣ бикунед!» Ва ҳар касро, ки Худо азоб кардан хоҳад, ту аз қаҳри Худо раҳоӣ нахоҳӣ дод. Инҳо касоне ҳастанд, ки Худо нахостааст, ки дилҳояшонро пок гардонад. Ононро дар дунё хорӣ ва дар охират азобе бузург аст!'),
(711, 5, 42, 'Шунавандагони дурӯғанд, хӯрандагони ҳароманд. Пас агар назди ту омаданд, миёнашон ҳукм кун ё аз онҳо рӯйгардон шав ва агар рӯйгардон шавӣ, ҳеҷ ба ту зиёне нарасонанд. Ва агар миёнашон ҳукм кунӣ, ба адолат ҳукм кун, ки Худо адолатпешагонро дӯст дорад.'),
(712, 5, 43, 'Чӣ гуна туро ҳоким қарор медиҳанд, дар ҳоле ки Таврот, ки дарбаргирандаи ҳукми Худост, дар назди онҳост? Сипас аз ҳукми ту рӯйгардон мешаванд ва онҳо имон наёвардаанд.'),
(713, 5, 44, 'Мо Тавротро, ки дар он ҳидоят ва рӯшноист, нозил кардем. Паёмбароне, ки таслими фармон буданд, мувофиқи он барон яҳуд ҳукм карданд ва низ худошиносону донишмандон, ки ба ҳифзи китоби Худо маъмур буданд ва бар он гувоҳӣ доданд, пас аз мардум натарсед, аз Ман битарсед ва оёти маро ба баҳои андак мафурӯшед. Ва ҳар кӣ мувофиқи оёте, ки Худо нозил кардааст, ҳукм накунад, кофир аст.'),
(714, 5, 45, 'Ва дар Таврот бар онон муқаррар доштем, ки шахс дар баробари шахс ва чашм дар баробари чашм ва бинӣ дар баробари бинӣ ва гӯш дар баробари гӯш ва дандон дар баробари дандон ва ҳар захмеро қасосест. Ва ҳар кӣ аз қасос даргузарад, гуноҳашро кафорае (товоне) хоҳад буд. Ва ҳар кӣ ба он чӣ Худо нозил кардааст, ҳукм накунад, аз ситамкорон аст.'),
(715, 5, 46, 'Ва аз паи онҳо Исо — писари Марямро фиристодем, ки тасдиқкунандаи Тавроте буд, ки пеш аз ӯ фиристода будем ва Инҷилро, ки тасдиқкунандаи Таврот пеш аз ӯ буд, ба ӯ додем, ки дар он ҳидоят ва рӯшноие бувад ва барои парҳезгорон ҳидоят ва панде.'),
(716, 5, 47, 'Ва бояд, ки аҳли Инҷил мувофиқи он чӣ Худо дар он китоб нозил кардааст, ҳукм кунанд. Зеро ҳар кас ба он чӣ Худо нозил кардааст, ҳукм накунад, аз нофармонон аст.'),
(717, 5, 48, 'Ва ин китобро ба ростӣ бар ту нозил кард ем; тасдиқкунанда ва ҳоким бар китобҳоест, ки пеш аз он будаанд. Пас мувофиқи он чӣ Худо нозил кардааст, дар миёнашон ҳукм кун ва аз паи хоҳишҳояшон марав, то он чиро аз ҳақ бар ту нозил шудааст, тарк кунӣ. Барон ҳар гурӯҳе аз шумо шариъат ва равише ниҳодем. Ва агар Худо мехост, ҳамаи шуморо як уммат месохт. Вале хост дар он чӣ бар шумо арзонӣ доштааст, биёзмоядатон. Пас дар некиҳо бар якдигар пешӣ гиред. Ҳамагӣ бозгаштатон ба Худост, то аз он чӣ дар он ихтилоф мекардед, огоҳатон созад.'),
(718, 5, 49, 'Миёнашон мувофиқи он чӣ Худо нозил кардааст, ҳукм кун ва аз хостҳошон пайравӣ макун ва аз онҳо бипарҳез, ки мабодо бифиребандат, то аз баъзе аз чизҳое, ки Худо бар ту нозил кардааст, рӯй гардонӣ. Ва агар рӯйгардон шуданд бидон, ки Худо мехоҳад онҳоро ба ҷазои баъзе гуноҳонашон уқубат кунад ва албатта бисёре аз мардум нофармонанд.'),
(719, 5, 50, 'Оё ҳукми ҷоҳилиятро меҷӯянд? Барои он мардуме, ки аҳли яқин ҳастанд, чӣ ҳукме аз ҳукми Худо беҳтар аст?'),
(720, 5, 51, 'Эй касоне, ки имон овардаед, яҳуд ва насороро ба дӯстӣ нагиред. Онон худ дӯстони якдигаранд. Ҳар кас аз шумо, ки онҳоро дӯст гирад, аз ҷумлаи онҳост. Ва Худо ситамкоронро ҳидоят намекунад!'),
(721, 5, 52, 'Онҳоеро, ки дар дил маразе доранд, мебинӣ, ки ба сӯҳбаташон мешитобанд, мегӯянд: «Метарсем, ки ба мо осебе расад». Аммо шояд, ки Худо пирӯзие падид орад ё коре кунад, он гоҳ аз он чӣ дар дил ниҳон дошта буданд, пушаймон шаванд.'),
(722, 5, 53, 'Онҳо, ки имон овардаанд, мегӯянд: «Оё инҳо ҳамон касонанд, ки ба Худо савгандҳои сахт мехӯрданд, ки бо шумо хоҳанд буд?». Амалҳояшон ботил гардид ва худ дар ҷумлаи зиёнкорон даромаданд.'),
(723, 5, 54, 'Эй касоне, ки имон овардаед, ҳар кӣ аз шумо аз динаш бозгардад, чӣ бок; ба зудӣ Худо мардумеро биёварад, ки дӯсташон бидорад ва дӯсташ бидоранд. Дар баробари мӯъминон хоксоранд ва дар баробари кофирон саркаш; дар роҳи Худо ҷиҳод мекунанд ва аз маломати ҳеҷ маломатгаре намеҳаросанд. Ин фазли Худост, ки ба ҳар кас, ки хоҳад медиҳад. ва Худованд бахшояндаву доност!'),
(724, 5, 55, 'Фақат ва фақат дӯсти шумо Худост ва расули Ӯ ва мӯъминоне, ки намоз мехонанд ва ҳамчунон, ки дар рукӯъанд, садақа мекунанд,'),
(725, 5, 56, 'Ва ҳар кӣ Худо ва паёмбари Ӯ ва мӯъминонро дӯсти худ бигирад, бидонад, ки пирӯзмандон гурӯҳи Худованданд.'),
(726, 5, 57, 'Эй касоне, ки имон овардаед, аҳли китобро, ки дини шуморо ба масхара ва бозӣ мегиранд ва низ кофиронро ба дӯстӣ нагиред. Ва агар имон овардаед, аз Худо битарсед!'),
(727, 5, 58, 'Ва чун бонги намоз кунед, онро ба масхараву бозича гиранд, зеро мардуме ҳастанд, ки намеандешанд.'),
(728, 5, 59, 'Бигӯ: «Эй аҳли китоб, оё моро сарзаниш мекунед? Ба он сабаб, ки мо ба Худо ва он чӣ бар мо нозил шуда ва он чӣ пеш аз ин нозил шудааст, имон овардаем ва шумо бештарин нофармон ҳастед?»'),
(729, 5, 60, 'Бигӯ: «Оё шуморо аз касоне, ки дар назди Худо ҷазое бадтар аз ин доранд, хабар бидиҳам: Касоне, ки Худояшон лаънат карда ва бар онҳо хашм гирифта ва баъзеро бӯзинаву хук гардонидааст ва худ бут парастидаанд? Инҳоро бадтарин ҷойгоҳ аст ва аз роҳи рост гумгаштатаранд.'),
(730, 5, 61, 'Ва чун назди шумо омаданд, гуфтанд, ки имон овардем ва ҳол он ки бо куфр дохил шуданд ва бо куфр хориҷ гаштанд ва Худо ба он чӣ пинҳон медоранд, огоҳтар аст!'),
(731, 5, 62, 'Мебинӣ бисёре аз онҳоро, ки ба гуноҳу таҷовуз ва ҳаромхорагӣ мешитобанд. Чӣ бад корҳое мекарданд!'),
(732, 5, 63, 'З Барои чӣ худопарастону донишмандон ононро аз гуфтори бад ва ҳаромхорагӣ бознамедоранд. Чӣ бад аст корҳое, ки мекарданд!'),
(733, 5, 64, 'Яҳуд гуфтанд, ки дасти Худо баста аст. Дастҳои худашон баста бод? Ва ба он сухан, ки гуфтанд, малъун гаштанд. Дастҳои Худо кушода аст. Ба ҳар навъе, кн бихоҳад, рӯзӣ медиҳад. Ва он чӣ бар ту аз ҷониби Парвардигорат нозил шудааст, ба туғён ва куфри бештарашон хоҳад афзуд. Мо то рӯзи қиёмат миёнашон душманнву кина андохтаем. Ҳар гоҳ ки оташи ҷангро афрӯхтанд, Худо хомӯшаш сохт. Ва онон дар рӯи замин ба фасод мекӯшанд ва Худо фасодкоронро дӯст надорад.'),
(734, 5, 65, 'Агар аҳли китоб имон биёваранд ва парҳезгорӣ кунанд, гуноҳонашон хоҳем нобуд кард ва онҳоро ба биҳиштҳои пурнеъмат дохил хоҳем кард.'),
(735, 5, 66, 'Ва агар Таврот ва Инҷил ва он чиро, ки аз ҷониби Худо бар онҳо нозил шуда, барпой доранд, аз болои сару зери пояшон рӯзӣ бихӯранд. Баъзе аз онҳо мардуме миёнарав ҳастанд ва бисёрашон бадкирдоранд.'),
(736, 5, 67, 'Эй паёмбар, он чиро, ки аз Парвардигорат бар ту нозил шудааст, ба мардум бирасон. Агар чунин накунӣ, амри фиристодаи Ӯро адо накардаӣ. Худо туро аз мардум ҳифз мекунад, ки Худо мардуми кофирро ҳидоят намекунад.'),
(737, 5, 68, 'Бигӯ: «Эй аҳли китоб, шумо ҳеҷ нестед, то он гоҳ, ки Таврот ва Инҷил ва он чиро аз ҷониби Парвардигоратон бар шумо нозил шудааст, барпой доред». Он чӣ аз ҷониби Парвардигорат бар ту нозил шудааст, бар туғён ва куфри бештаринашон бияфзояд. Пас бар ин мардуми кофир ғамгин мабош.'),
(738, 5, 69, 'Албатта аз миёни онон, ки имон овардаанд ва яҳуд ва собион ва насоро, — ҳар кӣ ба Худо ва рӯзи қиёмат имон дошта бошад ва кори шоиста кунад, — биме бар ӯ нест ва ғамгин намешавад.'),
(739, 5, 70, 'Мо аз банӣ-Исроил паймон гирифтем ва паёмбароне барояшон фиристодем. Ҳар гоҳ ки паёмбаре чизе мегуфт, ки бо хоҳиши дилашон мувофиқ набуд, гурӯҳеро дурӯғ мебароварданд ва гурӯҳеро мекуштанд.'),
(740, 5, 71, 'Ва пиндоштанд, ки уқубате нахоҳад буд. Пас кӯру кар шуданд. Он гоҳ Худо тавбаашон қабул кард. Боз бисёре аз онҳо кӯру кар шуданд. Ҳар чӣ мекунанд, Худо мебинад.'),
(741, 5, 72, 'Бе шубҳа онҳое, ки гуфтанд, ки Худо ҳамон Масеҳ-Писари Марям аст, кофир шуданд. Масеҳ гуфт: «Эй банӣ-Исроил, Оллоҳ --- Парвардигори ман ва Парвардигори худро бипарастед». Зеро ҳар кас, ки барои Худованд шарике қарор диҳад, Худо биҳиштро бар ӯ ҳаром куна два ҷойгоҳи ӯ оташ аст ва ситамкорон ёваре нест.'),
(742, 5, 73, 'Онон, ки гуфтанд: «Оллоҳ саввумини се аст», кофир шуданд. Дар ҳоле ки ҳеҷ Худое ҷуз Оллоҳ нест. Агар аз он чӣ мегӯянд бознаистанд, ба кофиронашон азобе дардовар хоҳад расид.'),
(743, 5, 74, 'Оё ба даргоҳи Худо тавба намекунанд ва аз Ӯ бахшиш намехоҳанд? Худо бахшоянда ва меҳрубон аст!'),
(744, 5, 75, 'Масеҳ- писари Марям фақат паёмбаре буд, ки паёмбароне пеш аз ӯ будаанд ва модараш зане ростгӯй буд, ки ҳар ду ғизо мехӯрданд. Бингар, ки чӣ гуна оётро барояшон баён мекунем. Сипас бингар, ки чӣ гуна аз ҳақ рӯй мегардонанд.'),
(745, 5, 76, 'Бигӯ: «Оё ғайри Оллоҳ худои дигареро, ки барои шумо ҳеҷ суду зиёне надорад, мепарастед ва ҳол он, ки Оллоҳ шунаво ва доност?».'),
(746, 5, 77, 'Бигӯ: «Эй аҳли китоб, ба ноҳақ дар дини хеш ғулувв макунед ва ба хоҳишҳои он мардуме, ки аз пеш гумроҳ шуда буданд ва бисёреро гумроҳ карданд ва худ аз роҳи рост каҷравӣ кардаанд, пайравӣ макунед».'),
(747, 5, 78, 'Аз банӣ-Исроил онон, ки кофир шуданд, ба забони Довуд Ва Исо бинни Марям лаънат шуданд ва ин лаънат ҷазои исён ва таҷовузашон буд.'),
(748, 5, 79, 'Аз кори зиште, ки мекарданд, якдигарро манъ намекарданд ва албатта бадкорӣ мекарданд.'),
(749, 5, 80, 'Бисёре аз онҳоро мебинӣ, ки бо кофирон дӯстӣ меварзанд. Ба даст он чӣ пешопеш барои худ фиристоданд. Хашми Худо бар онҳост ва дар азоби ҷовидонаанд.'),
(750, 5, 81, 'Агар ба Худо ва паёмбар ва он чӣ бар ӯ нозил шуда, имон оварда буданд, кофиронро ба дӯсти намегирифтанд, вале бештарашон фосиқонанд.'),
(751, 5, 82, 'Душмантарин мардум нисбат ба касоне, ки имон овардаанд, яҳуд ва мушриконро меёби ва меҳрубонтарин касон нисбат ба онон, ки имон овардаанд, касокеро меёбӣ, ки мегӯянд: «Мо насронӣ ҳастем». Зеро баъзе аз онҳо донишмандону обидон ҳастанд ва онҳо сарварӣ намеҷӯянд.'),
(752, 5, 83, 'Чун он чиро, ки бар паёмбар нозил шуда, бишнаванд, ва ҳақикатро дарёбанд, чашмонашон пур аз ашк мешавад. Мегӯянд: «Эй Парвардигори мо, имон овардем, моро низ дар зумраи шаҳодатдиҳандагон бинавис.'),
(753, 5, 84, 'Чаро ба Худо ва ин дини ҳақ ки бар мо нозил шудааст, имон наёварем ва тамаъ накунем, дар ин, ки Парвардигори мо моро дар шумори солеҳон оварад?»'),
(754, 5, 85, 'Ба подоши ин сухан, ки гуфтанд, Худо ононро, ба биҳиштҳое, ки дар он наҳрҳо равон аст, подош дод. Дар он ҷовидонанд ва ин аст подоши некӯкорон.'),
(755, 5, 86, 'Ва онон, ки кофирӣ карданд ва оёти моро дурӯғ бароварданд, аҳли ҷаҳаннаманд.'),
(756, 5, 87, 'Эй касоне, ки имон овардаед, чизҳои покизаеро, ки Худо бар шумо ҳалол кардааст, ҳаром макунед ва аз ҳад дарнагузаред, ки Худо аз ҳад гузарандагонро дӯст надорад.'),
(757, 5, 88, 'Аз чизҳои ҳалолу покизае, ки Худо ба шумо рӯзӣ додааст, бихӯред ва аз Худое, ки ба Ӯ имон овардаед, битарсед.'),
(758, 5, 89, 'Худованд шуморо ба сабаби савгандҳои беҳудаатон бозхост нахоҳад кард. Вале ба сабаби шикастани савгандҳое, ки ба қасд мехӯред, бозхост мекунад ва ҷаримаи он таъоми даҳ мискин аст аз ғизои миёкае, ки ба хонаводаи хеш мехӯронед ё пӯпиши онҳо, ё озод кардани як банда ва ҳар кӣ наёбад, се рӯз рӯза доштан. Ин кафораи қасам аст, ҳар гоҳ, ки қасам хӯрдед. Ба қасамҳои худ вафо кунед. Худо оёти худро барои шумо инчунин баён мекунад, бошад, ки шукргузор бошед.'),
(759, 5, 90, 'Эй касоне, ки имон овардаед, шароб ва қимор а бутҳо ва гаравбандӣ бо тирҳо палидӣ ва кори шайтон аст, аз он парҳез кунед то наҷот ёбед!'),
(760, 5, 91, 'Шайтон мехоҳад бо шаробу қимор миёни шумо кинаву душманӣ афканад ва шуморо аз ёди Худо ва намоз боздорад, оё бас мекунед?'),
(761, 5, 92, 'Худову паёмбарашро итоъат кунед ва ҳазар кунед. Агар рӯйгардон шавед, бидонед, ки вазифаи паёмбари Мо расонидани паёми равшани Худованд аст!'),
(762, 5, 93, 'Бар онон, ки имон овардаанд ва корҳои шоиста кардаанд, дар он чӣ хӯрдаанд, гуноҳе нест, ҳар гоҳ парҳезгорӣ кунанд ва имон биёваранд ва ба корҳои шоиста пардозанд, боз ҳам парҳезгорӣ куканд ва имон биёваранд, боз ҳам парҳезгорӣ кунанд ва некӣ, ки Худо некӯкоронро дӯст дорад.'),
(763, 5, 94, 'Эй касоне, ки имон овардаед, Худо шуморо ба сайде, ки ба даст мегиред ё ба найза шикор мекунед, меозмояд, то бидонад чӣ касе дар ниҳон аз Ӯ метарсад. Ва ҳар кӣ аз ин пас аз ҳад таҷовуз кунад, ӯрост азобе дардовар.'),
(764, 5, 95, 'Эй касоне, ки имон овардаед, ҳар гоҳ, ки дар эҳром бошед, шикорро макушед. Ҳар кӣ сайдро қасдан бикушад, ҷазои ӯ қурбонӣ кардани ҳайвонест монанди он чӣ куштааст, ба шарти он ки ду одил аз шумо гувоҳӣ диҳанд ва қурбониро ба Каъба расонад ё ба ҷарима дарвешонро таъом диҳад ё баробари он рӯза бигирад, то уқубати кори худ бичашад. Аз он чӣ дар гузашта кардаед, Худо афв кардааст, вале ҳар кӣ ба он боз гардад, Худо аз ӯ интиқом мегирад, ки Худо пирӯзманду интиқомгиранда аст!'),
(765, 5, 96, 'Шикори дарёӣ ва хӯрдани он ба ҷиҳати баҳраманд шуданатон аз он бар шумо ва мусофирон ҳалол шуда аст. Ва шикори саҳроӣ, то ҳангоме ки дар эҳром ҳастед, бар шумо ҳадом шуда. Аз Худованде, ки ба назди Ӯ гирд оварда мешавед, битарсед!'),
(766, 5, 97, 'Худо Каъба, байтулҳаромро бо моҳи ҳаром ва қурбонии беқилода ва қурбонии боқилода қивоми кори мардум гардонид, то бидонед, ки Худо ҳар чӣро ки дар осмонҳову замин аст, медонад ва Ӯ бар ҳар чизе огоҳ аст.'),
(767, 5, 98, 'Бидонед, ки уқубати Худо сахт аст ва ҳам Ӯ бахшояндаву меҳрубон аст!'),
(768, 5, 99, 'Бар паёмбар ғайри расонидани паем вазифае нест. Ва он чиро, ки ошкор месозед ё пинҳон медоред, Худо медонад!'),
(769, 5, 100, 'Бигӯ: «Нопоку пок баробар нестанд, ҳарчанд фаровонии нопок туро ба ҳайрат афканад». Пас, эй хирадмандон, аз Худой битарсед, бошад, ки наҷот ёбед!'),
(770, 5, 101, 'Эй касоне, ки имон овардаед, аз чизҳое, ки чун барои шумо ошкор шаванд, андӯҳгинатон мекунаид, напурсед. Ва агар суол аз онҳоро вогузоред, то ба ҳангоми нузули Қуръон бароятон ошкор хоҳад шуд. Худо аз онҳо афв кардааст, ки омурзандаву бурдбор аст.'),
(771, 5, 102, 'Мардуме, ки пеш аз шумо буданд, аз он чизҳо суол карданд ва ба он сабаб кофир шуданд.'),
(772, 5, 103, 'Худованд дар бораи баҳира ва соиба ва васила ва ҳомӣ ҳукме накарда аст, вале кофирон бар Худо дурӯғ мебанданд ва бештаринашон бехираданд.'),
(773, 5, 104, 'Ва чун ба онҳо гӯянд, ки ба он чӣ Худо нозил кардааст ва ба паёмбар рӯй оваред, гӯянд: «Он дине, ки падарони худро ба он мӯътақид ёфтаем, моро бас аст». Ҳатто агар падаронашон ҳеҷ намедонистаанд ва роҳи ҳидоят наёфта будаанд?'),
(774, 5, 105, 'Эй касоне, ки имон овардаед, ба худ пардозед. Агар шумо ҳидоят ёфтаед, онон, ки гумроҳ мондаанд, ба шумо зиёне нарасонанд. Бозгашти ҳамаи шумо назди Худост, то шуморо ба он корҳо, ки мекардаед, огоҳ гардонад.'),
(775, 5, 106, 'Эй касоне, ки имон овардаед, чун маргатон фаро расад, ба ҳангоми васият ду одилро аз миёни худатон ба шоҳидӣ гиред ё аз ғайри худатон, ҳар гоҳ, ки дар сафар будед ва маргатон фаро расид. Агар аз он ду дар шак будед, нигоҳашон доред то баъд аз намоз, он гоҳ ба Худо савганд хӯранд, ки ин шаҳодатро ба ҳеҷ қимате дигаргун накунем, ҳарчанд ба нафъи хешовандамон бошад ва онро рӯпуш накунем, агар ҷуз ин бошад, аз гунаҳгоронем.'),
(776, 5, 107, 'Ва ҳар гоҳ маълум шавад, ки он ду шоҳид муртакиби гуноҳи хиёнат шудаанд, ду шоҳидӣ дигар, ки авлотар аз он ду бошанд, ҷои онҳоро бигиранд. Он ду ба Худо қасам хӯранд, ки шоҳидии мо аз шоҳидии он ду дурусттар аст ва мо аз ҳақ таҷовуз накунем, ҳар гоҳ чунин кунем, аз ситамкорон бошем.'),
(777, 5, 108, 'Ин ҳукм наздиктар ба он аст, ки ҳам шоҳидиро бар ваҷҳи худ адо кунанд ё пас аз савганд хӯрдан аз радди савгандҳояшон битарсанд. Аз Худо битарсед. Ва гӯш доред. Худо мардуми нофармонро ҳидоят намекунад!'),
(778, 5, 109, 'Рӯзе, ки Худо паёмбаронро гирд орад ва бипурсад, ки даъвати шуморо чӣ гуна посӯх доданд? Гӯянд: «Моро ҳеҷ донише нест, ки доно ба ғайб Ту ҳастӣ».'),
(779, 5, 110, 'Худо ба Исо бинни Марям гуфт: «Неъматеро, ки ба ту ва модарат ато намудаам, ёд кун. Он замон, ки ба рӯҳулқудус ёриат кардам, то ту чӣ дар гаҳвора ва чӣ дар бузургсолӣ сухан гӯӣ ва ба ту китобу ҳикмат ва Тавроту Инҷил омӯхтам. Ва он гоҳ ки ба амри Ман аз гил чизе чун парранда сохтӣ ва дар он дамидӣ ва ба амри Ман паррандае шуд ва кӯри модарзоду песӣ гирифтаро ба фармони Ман шифо додӣ ва мурдагонро ба фармони Ман зинда берун овардӣ ва чун бо ин далоили равшан назди банӣ-Исроил омадӣ, Ман ононро аз осеб расондан ба ту боздоштам ва аз миёнашон касоне, ки кофир буданд, гуфтанд, ки ин ҷуз ҷодуи ошкор нест!»'),
(780, 5, 111, 'Ва ба ҳавориён ваҳй кардам: «Ба Ман ва ба паёмбари Ман имон биёваред». Гуфтанд: «Имон овардем, гувоҳ бош, ки мо таслим ҳастем!»'),
(781, 5, 112, 'Ва ҳавориён пурсиданд: «Эй Исо бинни Марям, оё Парварднгори ту метавонад, ки барои мо аз осмон моидае фиристад?» Гуфт; Агар имон овардаед, аз Худо битарсед».'),
(782, 5, 113, 'Гуфтанд: «Мехоҳем, ки аз он дастархон бихӯрем, то дилҳоямон ором гирад ва бидонем, ки ту ба мо рост гуфтаӣ ва бар он шоҳидӣ диҳем».'),
(783, 5, 114, 'Исо бинни Марям гуфт: «Бор Худое, эй Парвардигори мо, барои мо дастархоне аз оомон бифирист, то моро ва ононро, ки баъд аз мо меоянд, иде ва нишоне аз ту бошад ва моро рӯзӣ деҳ ки Ту беҳтарини рӯзидиҳандагон ҳастӣ».'),
(784, 5, 115, 'Худо гуфт: «Ман он дастархонро барои шумо мефиристам, вале ҳар кӣ аз шумо аз он пас кофир шавад, чунон азобаш мекунам, ки ҳеҷ як аз мардуми ҷаҳонро ончунон азоб накарда бошам».'),
(785, 5, 116, 'Ва он гоҳ ки Худо ба Исо бинни Марям гуфт: «Оё ту ба мардум гуфтӣ, ки марову модарамро ғайри Оллоҳ ба худоӣ гиред?» Гуфт: «Ба покӣ ёд мекунам Туро. Насазад маро, ки чизе гӯям, ки шоистаи он набошам. Агар ман чунин гуфта будам, Ту худ медоннстӣ, зеро ба он чӣ дар замири ман мегузарад, доноӣ ва ман аз он чӣ дар зоти Туст, бехабарам. Зеро Ту донотарини касон ба ғайб ҳастӣ!'),
(786, 5, 117, 'Ман ба онон ҷуз он чӣ Ту фармонам дода будӣ, нагуфтам. Гуфтам, ки Оллоҳ — Парвардигори маро ва Парвардигори худро бипарастед. Ва ман то дар миёнашон будам, нигаҳбони ақидаашон будам ва чун маро миронидӣ, Ту худ нигаҳбони акидаташон гаштӣ. Ва Ту бар ҳар чизе огоҳӣ.'),
(787, 5, 118, 'Агар ононро азоб кунӣ, бандагони Ту ҳастанд ва агар ононро биёмурзӣ, Ту пирӯзманду ҳакимӣ!»'),
(788, 5, 119, 'Худо гуфт: «Ин рӯзест, ки ростгуёнро ростии гуфторашон нафъ диҳад. Аз они онҳост биҳиштҳое, ки дар он наҳрҳо ҷорист. Ҳамеша дар он ҷовидон хоҳанд буд». Худо аз онҳо хушнуд аст ва онон низ аз Худо хушнуданд. Ва ин комёбии бузургест!'),
(789, 5, 120, 'Аз они Худ ост фармонравоии осмонҳову замин ва он чӣ миёни онҳост. Ва Ӯ ба ҳар коре тавоност!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Ситоиш аз они Худовандест, ки осмонҳову заминро биёфарид ва торикиҳову рӯшноиро падидор кард, бо ин ҳама кофирон бо Парвардигори хеш дигареро баробар медоранд.'),
(791, 6, 2, 'Ӯст, ки шуморо аз гил биёфарид ва умре муқаррар кард: муддате дар назди Ӯ муайян. Бо ин ҳама шубҳа мекунед.'),
(792, 6, 3, 'Дар осмонҳову замин Ӯст, ки Худованд аст. Ниҳону ошкоратонро медонад ва аз кирдоратон огоҳ аст.'),
(793, 6, 4, 'Ва ҳеҷ ояе аз оёти Парвардигорашон барояшон нозил нашуд, ҷуз он ки аз он рӯйгардон шуданд!'),
(794, 6, 5, 'Чун сухани ҳақ бар онҳо арза шуд, дурӯғ бароварданд. Пас ба зудӣ хабари чизҳое, ки масхараашон мекарданд, ба онҳо хоҳад расид.'),
(795, 6, 6, 'Оё надидаанд, ки пеш аз онҳо чӣ мардумеро ҳалок кардаем? Мардуме, ки дар, замин қудраташон дода будем, ончунон қудрате, ки ба шумо надодаем. Ва барояшон аз осмон боронҳои пайдарпай фиристодем ва рӯдҳо аз зери пояшон равон сохтем. Он гоҳ ба ҷазои гуноҳонашон ҳалокашон кардем ва пас аз онҳо мардуме дигар падид овардем.'),
(796, 6, 7, 'Ҳатто агар китобе навишта бар рӯи коғаз бар ту нозил мекардем ва онро бо дасти хеш ламс мекарданд, боз ҳам он кофирон мегуфтанд, ки ин ҷуз ҷодуи ошкоро нест.'),
(797, 6, 8, 'Ва гуфтанд: «Чаро фариштае бар ӯ нозил нашудааст?» Агар фариштае мефиристодем, кор ба поён мерасид ва ба онҳо ҳеҷ мӯҳлате дода намешуд.'),
(798, 6, 9, 'Ва агар он паёмбарро аз миёни фариштагон меинтихобидем, боз ҳам ӯро ба сурати марде мефиристодсм ва ни халту (омезишу) иштибоҳ, ки пайдо карданд, нигоҳ медоштем.'),
(799, 6, 10, 'Паёмбаронеро ҳам, ки пеш аз ту буданд, масхара мекарданд. Пас масхаракунандагонро азобе, ки барои масхара карданашон буд, фурӯ шрифт.'),
(800, 6, 11, 'Бигӯ: «Дар рӯи замин бигардед ва бингаред, ки поёни кори дурӯғбарорандагон чӣ гуна будааст».'),
(801, 6, 12, 'Бигӯ: «Аз они кист он чӣ дар осмонҳову замин аст?» Бигӯ: «Аз они Худост! Бахшоишро бар худ муқаррар дошта, ҳамаи шуморо дар рӯзи қиёмат, ки дар он шубҳае нест, гирд меоварад. Онон, ки ба зиёни хеш кор кардаанд, имон намеоваранд.'),
(802, 6, 13, 'Аз они Ӯст ҳар чӣ дар шабу рӯз ҷой дорад ва Ӯст шунавову доно!»'),
(803, 6, 14, 'Бигӯ; «Оё дигаре ғайри Худо ба дӯстӣ гирам, ки офаринандаи осмонҳову замин аст ва мехӯронад ва ба таъом мӯтоҷ нест?» Бигӯ; Албатта ман фармон шудаам, ки нахустин касе бошам, ки таслими амри Худо шуда бошад. Пас аз мушрикон мабош!»'),
(804, 6, 15, 'Бигӯ: «Аз азоби он рӯзи бузург метарсам, агар аз Парвардигорам фармон на барам!»'),
(805, 6, 16, 'Дар он рӯз азобро аз ҳар кӣ бигардонанд, мавриди раҳмати Худо воқеъ шудааст ва ин комёбии ошкорест!'),
(806, 6, 17, 'Агар Худо ба ту зараре бирасонад, ҳеҷ кас ҷуз Ӯ дафъаш натавонад кард ва агар ба ту хайре бирасонад, бар ҳар коре тавоност!'),
(807, 6, 18, 'Ва Ӯст ғолибе болотар аз ҳамаи бандагони хеш ва донову огоҳ аст!'),
(808, 6, 19, 'Бигӯ: Шоҳиди чӣ касе аз ҳар шоҳидӣ бузургтар аст?» Бигӯ: «Худо миёни ману шумо гувоҳ аст ва ин Қуръон бар ман ваҳй шудааст, то шуморо ва ҳар касро, ки ба Ӯ бирасад, бим диҳад. Оё шоҳидӣ медиҳед, ки: бо Оллоҳ худоёни дигаре ҳам ҳастанд?» Бигӯ: «Ман шоҳидӣ намедиҳам». Бигӯ: «Ҷуз ин нест, ки Ӯ худоест яктову аз он чӣ бо Ӯ шарик месозед, безорам!»'),
(809, 6, 20, 'Аҳли китоб ӯро чунон мешиносанд, ки фарзандони худро, инҳо, ки ба худ зиён мерасонанд, имон намеоваранд.'),
(810, 6, 21, 'Чӣ касест ситамкортар аз он, ки ба Худо дурӯғ мебандад ё оёти Ӯро дурӯғ мебарорад? Албатта ситамкоронро наҷотёбӣ нест!'),
(811, 6, 22, 'Рӯзе ҳамаро гирд оварем, сипас ба онҳо, ки ширк овардаанд, бигӯем: «Он касон, ки мепиндоштед, ки шарикони Худоянд, акнун куҷоянд?»'),
(812, 6, 23, 'Узре, ки меоваранд, ғайри ин нест, ки мегӯянд: «Савганд ба Худо, Парвардигори мо, ки мо мушрик набудаем!»'),
(813, 6, 24, 'Бингар, ки чӣ гуна бар худ дурӯғ бастанд ва он дурӯғҳо, ки сохта буданд, ночиз гардид.'),
(814, 6, 25, 'Баъзе аз онҳо ба сухани ту гӯш медиҳанд, вале Мо бар дилҳояшон пардаҳо афкандаем, то онро дарнаёбанд ва гушҳояшонро вазнин кардаем. Ва ҳар мӯъҷизаеро, ки бингаранд, ба он имон намеоваранд. Ва чун назди ту оянд, бо ту ба мунозара сар кунанд. Кофирон мегӯянд, ки инҳо чизе ҷуз афсонаҳои пешиниён нест.'),
(815, 6, 26, 'Инҳо мардумро аз паёмбар бозмедоранд ва худ аз ӯ канора меҷӯянд ва ҳол он, ки намедонанд, ки танҳо хештанро ба ҳалокат мерасонанд.'),
(816, 6, 27, 'Агар онҳоро дар он рӯз, ки баробари оташ нигоҳашон доштаанд, бингарӣ, мегӯянд: «Эй кош, моро бозгардонанд, то оёти Парвардигорамонро дурӯғ нагӯем ва аз мӯъминон бошем!»'),
(817, 6, 28, 'На, он чиро, ки аз ин пеш пӯшида медоштанд, акнун бар онҳо ошкор шуда, агар онҳоро ба дунё бозгардонанд, боз ҳам ба ҳамон корҳо, ки манъашон карда буданд, бозмегарданд, инҳо дурӯғгӯёнанд.'),
(818, 6, 29, 'Ва гуфтанд: «Ғайри ин зиндагии дунявии мо ҳеҷ нест ва мо дигар бор зинда нахоҳем шуд».'),
(819, 6, 30, 'Ва агар бубинӣ он ҳангомро, ки дар баробари Парвардигорашон истодаанд. Худо мегӯяд: «Оё ин ба ҳақ набувад?» Гӯянд: «Оре, савганд ба Парвардигорамон!» Гӯяд,: «Ба ҷазои он, ки кофир будаед, азоби Худоро бичашед!»'),
(820, 6, 31, 'Зиён карданд онҳое, ки дидор бо Худоро дурӯғ пиндоштанд. Ва чун қиёмат ба ногаҳон фаро расад, гӯянд; «Эй ҳасрато, бар мо ба хотири кӯтоҳие, ки кардем». Инҳо бори гуноҳашонро бар пушт мекашанд. Хон, чӣ бад бореро бар дӯш мекашанд!'),
(821, 6, 32, 'Ва зиндагии дунё чизе ҷуз бозичаву лаҳв нест ва парҳезгоронро сарои охират беҳтар аст. Оё ба ақл намеёбед?'),
(822, 6, 33, 'Медонем, ки суханашон туро андӯҳгин месозад, вале инон танҳо тӯро дурӯғ мебароранд, балки ин ситамкорон сухани Худоро инкор мекунанд!'),
(823, 6, 34, 'Паёмбаронеро ҳам, ки пеш аз ту буданд, дурӯғгуфтанд, вале онҳо бар он дурӯғу озор сабр карданд, то ёрии мо фаро расидашон. Ва суханони Худоро тағйирдиҳандае нест. Ва албатта баъзе аз ахбори паёмбарон бар ту нозил шудааст,.'),
(824, 6, 35, 'Агар рӯй гардондани онҳо бар ту гарон аст, хоҳӣ нақбе дар замин биҷӯй ё нардбоне бар осмон бинеҳ то мӯъҷизае бар онҳо биёварӣ. Агар Худо бихоҳад, ҳамаро ба роҳи рост барад. Пас аз нодонон мабош.'),
(825, 6, 36, 'Албатта танҳо онҳое, ки мешунаванд, мепазиранд. Ва мурдагонро Худо зинда мекунад ва сипас ҳама ба назди Ӯ бозгардонда мешаванд.'),
(826, 6, 37, 'Ва гуфтанд: «Чаро мӯъҷизае аз Парвардигораш бар ӯ нозил нашуда?» Бигӯ: «Худо қодир аст, ки мӯъҷизае фурӯ фиристад. Вале бештаринашон намедонанд!»'),
(827, 6, 38, 'Ҳеҷ ҷунбандае дар рӯи замин нест ва ҳеҷ паррандае бо болҳои худ дар ҳаво намепарад, магар он ки чун шумо умматҳое ҳастанд. Мо дар ин китоб ҳеҷ чизеро тарк накардаем. Ва сипас ҳамаро дар назди Парвардигорашон гирд меоваранд.'),
(828, 6, 39, 'Онҳое, ки оёти моро дурӯғ гуфтаанд, каронанду гунгонанд ва дар торикиҳоянд. Худо ҳар киро хоҳад, гумроҳ кунад ва ҳар киро хоҳад, ба роҳи рост андозад.'),
(829, 6, 40, 'Бигӯ; «Хабар диҳед, ки агар бар шумо азоби Худо фурӯд ояд ё қиёмат даррасад, агар рост мегӯед, боз ҳам ғайри Худоро мехонед?»'),
(830, 6, 41, 'На, танҳо Ӯро мехонед ва агар бихоҳад он ранҷеро, ки Худоро ба хотири он мехонед, аз миён мобарад ва шумо шарикеро, ки барои Ӯ сохтаед, ао ёд мебаред.'),
(831, 6, 42, 'Албатта бар умматҳое, ки пеш аз ту буданд, паёмбароне фиристодем ва ононро ба сахтиҳову офатҳо дучор кардем, то магар зорӣ кунанд!'),
(832, 6, 43, 'Пас чаро ҳангоме, ки азоби мо ба онҳо расид, зорӣ накарданд? Зеро дилҳояшонро қасоват (сахтӣ) фаро гирифта ва шайтон амалҳояшонро дар назарашон ороста буд.'),
(833, 6, 44, 'Чун ҳамаи пандҳоеро, ки ба онҳо дода шуда буд, фаромӯш карданд, ҳамаи дарҳоро ба рӯяшон кушодем, то аз он чӣ ёфта буданд, шодмон гаштанд, пас ба ногоҳ фурӯ гирифтемашон ва ҳамагон ноумед гардидаид.'),
(834, 6, 45, 'Пас шукр Парварднгори ҷаҳониёнро, ки решаи ситамкорон барканда шуд!'),
(835, 6, 46, 'Бигӯ: «Оё медонед, ки агар Оллоҳ гӯшу чашмони шуморо бозситонад ва бар дилҳоятон мӯҳр ниҳад, чӣ худое ғайри Оллоҳ онҳоро ба шумо бозмегардонад? Бингар, ки оёти Худоро чӣ гуна ба шеваҳои гуногун баён мекунем. Боз ҳам рӯй барметобанд».'),
(836, 6, 47, 'Бигӯ; «Хабар диҳед, агар азоби Худо ба ногоҳ ё ошкоро бар шумо фурӯд ояд, оё ғайри ситамкорон ҳалок мешаванд?»'),
(837, 6, 48, 'Мо паёмбаронро ҷуз барои мужда додан ё бим кардан намефиристем. Пас ҳар кас, ки имон овард ва кори шоистае кард, бимнок ва ғамгин намешавад!'),
(838, 6, 49, 'Ва ба касоне, ки оёти моро дурӯғ гуфтанд, ба ҷазои нофармонияшон азоб хоҳад расид!'),
(839, 6, 50, 'Бигӯ; «Ба шумо намегӯям, ки хазинаҳои Худо назди ман аст. Ва илми ғайб ҳам намедонам. Ва намегӯям, ки фариштае ҳастам. Танҳо ба чизе пайравӣ мекунам, ки бар ман ваҳй шудааст». Бигӯ: «Оё нобинову бино яксонанд? Чаро намеандешед?»'),
(840, 6, 51, 'Огоҳ соз ба ин китоб касонеро, ки аз гирд омадан бар остони Парвардигорашон ҳаросноканд, ки онҳоро ғайри Ӯ ҳеҷ ёвареву шафеъе нест. Бошад, ки парҳезгорӣ кунанд.'),
(841, 6, 52, 'Касонеро, ки ҳар бомдод ва шабонгоҳ Парвардигори хешро мехонанд ва хостори хушнудии Ӯ ҳастанд, аз назди худ дур макун. На чизе аз ҳисоби онҳо бар ӯҳдаи туст ва на чизе аз ҳисоби ту бар ӯҳдаи онҳо. Агар онҳоро дур кунӣ, дар гурӯҳи ситамкорон дароӣ.'),
(842, 6, 53, 'Ҳамчунин баъзеро ба баъзе озмудем, то бигӯянд: «Оё аз миёни мо инҳо буданд, кн Худо ба онҳо неъмат дод?» Оё Худо ба шукргузорон донотар нест?'),
(843, 6, 54, 'Чун имоновардагон ба оёти Мо назди ту омаданд, бигӯ: «Салом бар шумо! Худо бар хеш муқаррар карда, ки шуморо раҳмат кунад. Зеро ҳар кас аз шумо, ки аз рӯи нодонӣ коре бад кунад, он гоҳ тавба кунад ва некӯтар шавад, бидонад, ки Худо бахшандаву меҳрубон аст».'),
(844, 6, 55, 'Ва ин чунин оётро батафсил баён мекунем, то роҳу расми гунаҳгорон ошкор гардад!'),
(845, 6, 56, 'Бигӯ: «Маро манъ кардаакд, ки он худоёнро, ки ғайри Оллоҳ мехонед, парастиш кунам». Бигӯ; «Ба ҳавасҳои шумо пайравӣ намекунам, то мабодо гумроҳ шавам ва аз ҳидоятёфтагон набошам».'),
(846, 6, 57, 'Бигӯ: «Ман аз Парвардигорам далеле равшан дорам ва шумо он далелро дурӯғ мехонед. Он кӣ ба ин шитоб металабед, ба дасти ман нест. Ҳукм танҳо ҳукми Худост. Хақиқатро баён мекунад ва Ӯ беҳтарини доварон (ҳукмкунандагон) аст».'),
(847, 6, 58, 'Бигӯ: «Агар он чиро, ки бад-ин шитоб металабед, ба дасти ман буд, миёни ману шумо кор ба поён мерасид, зеро Худо ба ситамкорон донотар аст».'),
(848, 6, 59, 'Калидҳои ғайб назди Ӯст. Ғайри Ӯ касеро аз ғайб огоҳӣ кест. Ҳар чиро, ки дар хушкиву дарёст, медонад. Ҳеҷ барге аз дарахте намеафтад, магар он ки аз он огоҳ аст, Ва ҳеҷ донае дар торикиҳои замин ва ҳеҷ тареву хушке нест, ҷуз он ки дар китоби мубин Лавҳул маҳфуз омада аст.'),
(849, 6, 60, 'Ва Ӯст, ки шуморо шабҳангом мемиронад ва ҳар чӣ дар рӯз кардаед, медонад, он гоҳ бомдодон шуморо зинда месозад, то он ҳангом, ки муддати муайяни умратон ба поён расад. Сипас бозгаштатон ба назди Ӯст ва шуморо аз он чӣ кардаед, огоҳ мекунад.'),
(850, 6, 61, 'Ӯст ғолиб дар болои бандагонаш. Нигаҳбононе бар шумо мефиристад, то чун яке аз шуморо марг фаро расад, фиристодагони Мо бе ҳеҷ кӯтоҳиву гузаште ҷони ӯ бигиранд.'),
(851, 6, 62, 'Сипас ба назди Худо — мавлои ҳақиқии хеш бозгардонида шаванд. Бидон, ки ҳукм ҳукми Ӯст ва Ӯ босуръаттаринн ҳисобгарон аст.'),
(852, 6, 63, 'Бигӯ: «Чӣ касе шуморо аз ваҳшатҳои хушкиву даре мерахонад? Ӯро ба зорӣ ва дар ниҳон мехонед, ки агар аз ин маҳлака моро бираҳонад, мо низ аз шукргузорон хоҳем буд».'),
(853, 6, 64, 'Бигӯ: «Худост, ки шуморо аз он маҳлака ва аз ҳар андӯҳе мераҳонад, боз ҳам ба Ӯ ширк меоваред».'),
(854, 6, 65, 'Бигӯ; «Ӯ қодир ба он аст, ки аз болои саратон ё аз зери поҳоятон азобе бар шумо бифиристад ё шуморо гурӯҳ-гурӯҳ ба ҳам биомезад ва хашму қини гурӯҳеро ба гурӯҳи дигар бичашонад». Бингар, ки оётро чӣ гуна гуногун баён мекунем. Бошад ки ба фаҳм дарёбанд!'),
(855, 6, 66, 'Қавми ту Қуръонро дурӯғ мешумурданд, дар ҳоле ки суханест барҳақ. Бигӯ: «Ман корсози шумо нестам».'),
(856, 6, 67, 'Ба зудӣ хоҳед донист, ки барои ҳар хабаре замоне муайян аст?'),
(857, 6, 68, 'Ва чун бубинӣ, ки дар оёти Мо аз рӯи масхара гуфтугӯ мекунанд, аз онҳо рӯйгардон шав, то ба сухане ғайри он сар кунанд. Ва агар шайтон туро ба фаромӯшӣ афканад, чун ба ёдат омад, бо он мардуми ситамкора манишин.'),
(858, 6, 69, 'Касоне, ки парҳезгорӣ пеша кардаанд, ба гуноҳи кофирон бозхост нахоҳанд шуд, вале бояд ононро панд диҳанд. Шояд, ки парҳезгор шаванд!'),
(859, 6, 70, 'Ва тарк кун он касонеро, ки дини худ бозичаву лаҳв гирифтаанд ва зиндагонии дунё фиребашон дод. Ва ба Қуръон пандашон деҳ мабодо бар ҷазои амалҳои хеш гирифтор ояид. Ғайри Худо додрасу шафеъе надоранд. Ва агар барои раҳоии хеш ҳар гуна фидя диҳанд, пазируфта нахоҳад шуд. Инҳо ба уқубати аъмоли худ гирифтор ва ба ҷазои он ки кофир шудаанд, барояшон шаробе аз оби ҷӯшон ва азобе дардовар муҳайё шудааст.'),
(860, 6, 71, 'Бигӯ: «Оё ғайри Оллоҳ касеро бихонем, ки на моро нафъ медиҳад ва на зиён мерасонад? Ва оё пас аз он ки Худо моро ҳидоят кардааст, монанди он кас, ки шайтон гумроҳаш сохта ва ҳайрон бар рӯи замин раҳояш карда, аз дин бозгардем? Ӯро ёронест, ки ба ҳидоят нидояш медиҳанд, ки назди мо бозгард». Бигӯ: «Ҳидояте, ки аз сӯи Худо бошад, ҳидояти воқеъист. Ва ба мо фармон расида, ки дар баробари Парвардигори ҷаҳониён таслем шавем».'),
(861, 6, 72, 'Ва намоз бихонед ва аз Ӯ битарсед. Ӯст, он ки ҳамагон наздаш ҷамъ мешавед!'),
(862, 6, 73, 'Ва Ӯст, он ки осмонҳову заминро ба ҳақ биёфарид. Ва рӯзе, ки бигӯяд;. «Мавҷуд шав», пас мавҷуд мешавад. Гуфтори Ӯ хақ аст. Ва дар он рӯз, ки дар сур дамида шавад, фармонравоӣ аз они Ӯст. Донои ниҳону ошкор аст ва Ӯ ҳакиму огоҳ аст!'),
(863, 6, 74, 'Ва Иброҳим падараш Озарро гуфт: «Оё бутонро ба худоӣ мегирӣ? Ту ва қавматро ба ошкоро дар гумроҳӣ мебинам».'),
(864, 6, 75, 'Ба ин тарз ба Иброҳим корхонаи осмонҳову заминро нишон додем, то аз аҳли яқин гардад.'),
(865, 6, 76, 'Чун шаб ӯро фаро гирифт, ситорае дид. Гуфт: «Ин аст Парвардигори ман!» Чун ғоиб шуд, гуфт; «Фӯрушавандагонро дӯст надорам».'),
(866, 6, 77, 'Он гоҳ моҳро дид, ки тулӯъ мекунад. Гуфт: «Ин аст Парвардигори ман?» Чун ғоиб шуд, гуфт; «Агар Парвардигори ман маро роҳ нанамояд, аз гумроҳон хоҳам буд».'),
(867, 6, 78, 'Ва чун хуршедро дид, ки тулӯъ мекунад, гуфт: «Ин аст Парвардигори ман, ин бузургтар аст!» Ва чун ғоиб шуд, гуфт: «Эй қавми ман, ман аз он чӣ шарики Худояш медонед, безорам.'),
(868, 6, 79, 'Ман аз рӯи ихлос рӯй ба сӯи касе овардам, ки осмонҳову заминро офарида аст ва ман аз мушрикон нестам».'),
(869, 6, 80, 'Ва қавмаш бо ӯ ба баҳс бархостанд. Гуфт; «Оё дар бораи Оллоҳ бо ман баҳс мекунед ва ҳол он ки Ӯ маро ҳидоят кардааст? Ман аз он чизе, ки шарики Ӯ меҳисобед, наметарсам. Магар он ки Парвардигори ман чизеро бихоҳад. Илми Парвардигори ман ҳама чизро дар бар гирифтааст. Оё панд намегиред?'),
(870, 6, 81, 'Чаро аз он чизе, ки шарики Худояш сохтаед, битарсам, дар ҳоле ки шумо чизҳоеро, ки ҳеҷ далеле дар бораи онҳо нозил накардааст, мепарастед ва биме ба дил роҳ намедиҳед? Агар медонед, бигӯед, ки кадом як аз ин ду гурӯҳ ба эминӣ сазовортаранд?»'),
(871, 6, 82, 'Касоне, ки имон овардаанд, ва имони худро ба ширк намеолоянд эминӣ аз онҳо аст ва онҳо ҳидоятёфтагонанд.'),
(872, 6, 83, 'Ин бурҳони мо буд, ки онро ба Иброҳим ато намудем, дар баробари қавмаш. Ҳар киро бихоҳем, ба дараҷоте боло мебарем. Албатта Парвардигори ту ҳакиму доност!'),
(873, 6, 84, 'Ва ба ӯ Исхоқ ва Яъқубро бахшидем ва ҳамагиро ҳидоят кардем. Ва Нӯҳро пеш аз ин ҳидоят карда будем ва аз фарзандони Иброҳим Довуду Сулаймон ва Айюбу Юсуф ва Мӯсову Ҳорунро ҳидоят кардем. Ва некӯкоронро ин гуна подош диҳем!'),
(874, 6, 85, 'Ва Закариёву Яҳё ва Исову Илёс, ки ҳама аз солеҳон буданд.'),
(875, 6, 86, 'Ва Исмоилу Алясаъ ва Юнусу Лут, ки ҳамаро бар ҷаҳониён бартарӣ додем.'),
(876, 6, 87, 'Ва аз падаронашону фарзандонашон ва бародаронашон баъзеро ҳидоят кардем ва онҳоро баргузидем ва ба роҳи рост роҳ намудем.'),
(877, 6, 88, 'Ин аст ҳидояти Худо. Ҳар киро аз бандагонаш хоҳад, ба он ҳидоят мекунад ва агар ширк оварда буданд, амалҳое, ки анҷом дода буданд, нобуд мегардид.'),
(878, 6, 89, 'Инҳо касоне ҳастанд, ки ба онҳо китобу фармон ва нубувват додаем. Агар ин қавм ба он имон наёваранд, қавми дигареро бар он вакил кардаем, ки инкораш намекунанд.'),
(879, 6, 90, 'Инҳо касоне ҳастанд, ки Худо ҳидояташон кардааст пас ба равиши онҳо пайравӣ кун. Бигӯ; «Дар баробари он ҳеҷ музде аз шумо наметалабам, ин китоб ҷуз панде барои мардуми ҷаҳон пест».'),
(880, 6, 91, 'Вақте ки мегӯянд, ки Худо бар ҳеҷ инсоне чизе нозил накардааст, Худоро ончунон, ки лоиқи Ӯст, нашинохтаанд, бигӯ: «Китоберо, ки Мӯсо барои рӯшноӣ ва ҳидояти мардум овард, чӣ касе бар ӯ нозил карда буд? Онро бар коғазҳо навиштед, порае аз онро ошкор месозед, вале бештаринро пинҳон медоред. Ба шумо чизҳо омӯхтанд, ки аз ин пеш на шумо медонистед ва на падаронатон медонистанд. Бигӯ: «Он Оллоҳ аст!» Он гоҳ раҳояшон соз, то ҳамчунон ба ботили худ дилхуш бошанд.'),
(881, 6, 92, 'Ин аст китобе муборак, ки нозил кардаем, тасдиқкунандаи чизест, ки пеш аз он нозил шудааст. То бо он мардуми уммулқуро ва мардуми атрофашро бим диҳӣ. Касоне, ки ба рӯзи қиёмат имон доранд, ба он низ имон доранд. Инҳо ҳифзкунандаи намозҳои хешанд.'),
(882, 6, 93, 'Кист ситамкортар аз он кас, ки ба Худо дурӯғ баст ё гуфт, ки ба ман ваҳй шуда ва ҳол он ки ба ӯ ҳеҷ чиз ваҳй нашуда буд ва он кас, ки гуфт: «Ман низ монанди оёте, ки Худо нозил кардаст, нозил хоҳам кард?» Агар бубинӣ, он гоҳ, ки ин ситамкорон дар сакароти марг гирифторанд ва малоика бар онҳо даст кушодаанд, ки ҷони хеш берун кунед, имрӯз шуморо ба азобе хоркунанда азоб мекунанд ва ин ба ҷазои он аст, ки дар бораи Худо ба ноҳақ сухан мегуфтед ва аз оёти Ӯ сарпечӣ мекардед.'),
(883, 6, 94, 'Албатта танҳо-танҳо, он тавр ки дар оғоз "шуморо биёфаридем, назди мо омадаед, дар ҳоле ки ҳар чиро, ки арзонатом дошта будем, пушти сар ниҳодаед ва ҳеҷ як аз шафеъонатонро, ки мепиндоштед, бо шумо шариканд, ҳамроҳатон намебинем. Аз ҳам бурида шудаед ва пиндори худро гумгашта ёфтаед.'),
(884, 6, 95, 'Худост, ки донаву хастаро мешикофад ва зиндаро аз мурда берун меоварад ва мурдаро аз зинда берун меоварад. Ин аст Худои якто. Пас чӣ гуна аз ҳақ гумроҳатон мекунанд?'),
(885, 6, 96, 'Шукуфандаи субҳгоҳон аст ва шабро барои оромиш қарор дод ва хуршеду моҳро барои ҳисоб кардани вақт. Ин аст тақдири Худои пирӯзманди доно!'),
(886, 6, 97, 'Ӯст Худое, ки ситорагонро бароятон офарид, то ба онҳо дар торикиҳои хушкиву дарё роҳи худро биёбед. Оётро барои онон, ки медонанд, батафсил баён кардаем!'),
(887, 6, 98, 'Ва Ӯст Худованде, ки шуморо аз як тан биёфарид. Сипас шуморо қароргоҳест ва вадиъат (амонат) ҷоест. Оётро барои онон, ки мефаҳманд, батафсил баён кардаем!'),
(888, 6, 99, 'Ӯст Худое, ки аз осмон борон фиристод ва бо он борон ҳар гуна гиёҳеро руёнидем ва аз он гиёҳ танае сабз ва аз он донаҳое бар якдигар чида ва низ аз навдаҳои нахл хӯшаҳое сар бароварда падид овардем ва низ бӯстонҳое аз токҳо ва зайтуну анор монанду номонаяд! Ба меваҳояш, он гоҳ ки падид меоянд ва он гоҳ ки мерасанд, бингаред, ки дар онҳо ибратҳост барои онон, ки имон меоваранд?'),
(889, 6, 100, 'Барои Худо шариконе аз ҷин қарор доданд ва ҳол он ки ҷинро Худо офаридааст. Ва бе ҳеҷ донише ба дурӯғ духтароневу писароне барои Ӯ тасаввур карданд. Ӯ пок аст ва болотар аст аз он чӣ васфаш мекунанд!'),
(890, 6, 101, 'Падидоварандаи осмонҳову замин аст. Чӣ гуна Ӯро фарзанде бошад ва ҳол он ки Ӯро ҳамсаре нест. Ҳар чизеро Ӯ офаридааст ва ба ҳар чизе доност!'),
(891, 6, 102, 'Ин аст Худои якто, ки Парвардигори шумост. Худое ҷуз Ӯ нест. Офаринандаи ҳар чизест. Пас Ӯро бипарастед, ки нигоҳбони ҳар чизест!'),
(892, 6, 103, 'Чашмҳо Ӯро намебинанд ва Ӯ бинандагонро мебинад. Дақиқу огоҳ аст!'),
(893, 6, 104, 'Аз сӯи Парвардигоратон барои шумо нишонаҳои равшан омад. Ҳар кӣ аз рӯи ибрат менигарад, ба нафъи ӯст ва ҳар кӣ чашми ибрат барҳам пӯшад, ба зиёни ӯст. Ва ман нигоҳдорандаи шумо нестам!'),
(894, 6, 105, 'Оёти Худоро инчунин гуногун баён мекунем, то мабод, ки бигӯянд, ки аз касе дарс гирифтаӣ ва мо ин оётро барои аҳли дониш баён мекунем.'),
(895, 6, 106, 'Ба ҳар чӣ аз ҷониби Парвардигорат бар ту ваҳй шудааст, пайравӣ кун. Ҳеҷ худое ҷуз Ӯ нест. Ва аз мушрикон рӯй гардон!'),
(896, 6, 107, 'Агар Худо мехост онон ширк намеоварданд ва мо туро нигаҳбонашон насохтаем ва ту корсозашон нестӣ!'),
(897, 6, 108, 'Чизҳоеро, ки онон ба ҷои Оллоҳ мехонанд, дашном мадиҳед, ки онон низ бе ҳеҷ донише аз ҳад гузашта ба Оллоҳ дашном диҳанд. Инчунин амали ҳар қавмеро дар чашмашон оростаем. Пас бозгашти ҳамагон ба Парвардигорашон аст ва Ӯ ҳамаро аз корҳое, ки кардаанд, огоҳ месозад.'),
(898, 6, 109, 'То он ҷо, ки тавонистанд, ба сахттарин қасамҳо ба Худо савганд ёд карданд, ки агар мӯъҷизае бар онҳо нозил шавад, ба он имон оварданд. Бигӯ: «Ҳамаи мӯъҷизаҳо назди Худост ва шумо аз куҷо медонед, ки агар мӯъҷизае нозил шавад, имон намеоваранд».'),
(899, 6, 110, 'Ва ҳамчунон, ки дар оғоз ба он имон наёварданд, ин бор низ дар дилҳову дидагонашонро гумроҳ мекунем ва ононро саргардон дар гумроҳиашон раҳо месозем.'),
(900, 6, 111, 'Ва агар мо фариштагонро бар онҳо нозил карда будем ва мурдагон бо онҳо сухан мегуфтанд ва ҳар чизеро даста-даста казди онон гирд меовардем, боз ҳам имон намеоварданд, магар ин ки Худо бихоҳад. Валек бештарашон ҷоҳиланд!'),
(901, 6, 112, 'Ва ҳамчунин барои ҳар паёмбаре душманоне аз шаётини инсу ҷин қарор додем. Барои фиреби якдигар суханони ороста ваҳй мекунанд. Агар Парвардигорат мехост, чунин намекарданд. Пас бо тӯҳмате, ки мезананд, раҳояшон соз,'),
(902, 6, 113, 'то онон, ки ба қиёмат имон надоранд, гӯши дилро ба он супоранд ва нисандашон афтад ва ҳар чӣ анҷом медиҳанд, анҷом диҳанд.'),
(903, 6, 114, 'Оё қозии дигаре ҷуз Худо талаб кунам ва ҳол ок ки Ӯст, ки ин китоби равшанро бар шумо нозил кардааст? Ва аҳли китоб медонанд, ки ба ҳақ аз ҷониби Парвардигорат нозил шудааст. Пас аз так оварандагон мабош!'),
(904, 6, 115, 'Ва каломи Парвардигори ту дар ростиву адолат ба ҳадди камол аст. Ҳеҷ кас дигаргункунандаи сухани Ӯ нест ва Ӯст шунавову доно!'),
(905, 6, 116, 'Агар аз бисёриҳое, ки дар ин сарзаминанд, пайравӣ кунӣ, туро аз роҳи Худо гумроҳ созанд. Зеро фақат аз паи гумон мераванд ва фақат ба дурӯғ сухан мегӯянд.'),
(906, 6, 117, 'Парвардигори ту ба касоне, ки аз роҳи Вай дур мегарданд, огоҳтар аст ва ҳидоятёфтагонро беҳтар мешиносад!'),
(907, 6, 118, 'Агар ба оёти Худо имон доред, аз забҳе, ки номи Худо бар он ёд шудааст, бихӯред.'),
(908, 6, 119, 'Чаро аз он чӣ номи Худо бар он ёд шудааст, намехӯред ва Худо чизҳоеро, ки бар шумо ҳаром шудааст, батафсил баён кардааст, ғайри он гоҳ ки ночор гардед? Бисёре бе ҳеҷ донише, дигаронро гумроҳи гумонҳои худ кунанд. Албатта Парвардигори ту ба таҷовузкорон аз ҳад донотар аст!'),
(909, 6, 120, 'Ва гуноҳро, чӣ ошкор бошад ва чӣ пинҳон, тарк гӯед. Онон, ки гуноҳ мекунанд, ба ҷазои амалҳои худ хоҳанд расид!'),
(910, 6, 121, 'Аз забҳе, ки номи Худо бар он ёд нашудааст, нахӯред, ки худ нофармонист. Ва шаётин ба дӯстони худ ваҳй мекунанд, ки бо шумо муҷодала кунанд; агар аз онҳо пайравӣ кунед, аз мушриконед,'),
(911, 6, 122, 'Оё он касе, ки мурда буд ва Мо зиндааш сохтем ва нуре дар роҳаш доштем, то ба он дар миёни мардум роҳи худро биёбад, монанди касест, ки ба торикӣ гирифтор аст ва роҳи берун шуданро намедонад? Амалҳои кофирон дар назарашон инчунин ороста гардидааст!'),
(912, 6, 123, 'Ва ба ин тавр дар ҳар деҳае гунаҳкоронашонро бузургонашон қарор додем, то дар он ҷо макр кунанд, вале намедонанд, ки ҷуз бо худ макр намеварзанд.'),
(913, 6, 124, 'Чун ояе бар онҳо нозил шуд, гуфтанд, ки мо имон намеоварем, то он гоҳ, ки ҳар чӣ ба паёмбарони Худо дода шуда, ба мо низ дода шавад. Бигӯ «Худо донотар аст, ки рисолати худро дар куҷо қарор диҳад. Ба зудӣ ба гунаҳгорон ба ҷазои макре, ки мекарданд, аз ҷониби Худо хориву азобе сахт хоҳад расид!»'),
(914, 6, 125, 'Ҳар, касро Худо хоҳад, ки ҳидоят кунад, дилашро барои ислом мекушояд ва ҳар касро, ки хоҳад гумроҳ кунад, қалбашро чунон фурӯ мебандад, ки гӯё мехоҳад, ки ба осмон боло равад. Ба ин тавр Худо ба онҳое, ки имон намеоваранд, палидӣ мениҳад!'),
(915, 6, 126, 'Ин аст роҳи рости Парвардигорат. Оётро барои мардуме, ки панд мегиранд, батафсил баён кардаем.'),
(916, 6, 127, 'Барои онҳо дар назди Парпардигорашон хонаи оромиш аст. Ва ба савоби кирҳое, ки мекунанд, Худо дӯстдори онҳост.'),
(917, 6, 128, 'Ва рӯзе, ки ҳамагонро гирд оварад ва гӯяд: «Эй гурӯҳи ҷинниён, шумо бисёре аз одамиёнро пайрави худ сохтед». Ёронашон аз миёни одамиён гӯянд: «Эй Парвардигори мо, мо аз якдигар баҳраманд мешудем ва ба поёни замоне, ки барои зистани мо қарор дода будӣ, расидем». Гӯяд: «Ҷойгоҳи шумо оташ аст, ҷовидона дар он ҷо хоҳед буд, магар он чӣ Худо бихоҳад». Албатта Парвардигори ту ҳакиму доност!'),
(918, 6, 129, 'Ва ба ин тарз ситамкоронро ба ҷазои корҳое, ки мекарданд, ба якдигар вомегузорем.'),
(919, 6, 130, 'Эй гурӯҳи ҷинниёну одамиён, оё бар шумо паёмбароне аз худатон фиристода нашуда, то оёти Маро бароятон бихонанд ва шуморо аз дидори чунин рӯзе битарсонанд? Гӯянд: «Мо ба зиёни худ гувоҳӣ медиҳем». Зиндагии дунёӣ ононро бифирефт ва ба зиёни худ гувоҳӣ доданд, ки аз кофирон буданд.'),
(920, 6, 131, 'Ва ин ба он сабаб аст, ки Парвардигори ту мардуми ҳеҷ деҳаеро ки бехабар буданд, аз рӯи ситам ҳалок намекард.'),
(921, 6, 132, 'Барои ҳар як баробари корҳое, ки анҷом додаанд, дараҷотест, ки Парвардигори ту аз он чӣ мекунанд, ғофил нест.'),
(922, 6, 133, 'Ва Парвардигори ту бениёзу меҳрубон аст. Ва ҳамчунон ки шуморо аз насли мардумони дигар биёфарида аст, агар бихоҳад, шуморо мебарад ва пас аз шумо ҳар чиро, ки бихоҳад, ҷонишинатон месозад.'),
(923, 6, 134, 'Ҳарчи ба шумо ваъда додаанд, хоҳад омад ва шуморо роҳи гурезе нест.'),
(924, 6, 135, 'Бигӯ: «Эй қавми ман, ҳар чӣ аз дастатон бармеояд, бикунед, ки ман низ мекунам; ба зудӣ хоҳед донист, ки поёни ин зиндагӣ ба нафъи кӣ хоҳад буд, Албатта ситамкорон растагор намешаванд!»'),
(925, 6, 136, 'Барои Худо аз киштаҳову чорпоёне, ки офаридааст, насибе муъайян карданд ва ба хаёли худ гуфтанд, ки ин аз они Худост ва ин аз они бутони мост. Пас он чӣ аз они бутонашон буд, ба Худо намерасид ва он чӣ аз они Худо буд, ба бутонашон мерасид. Ба тарзе бад доварӣ мекарданд.'),
(926, 6, 137, 'Ҳамчунин коҳинон куштани фарзандро дар назари бисёре аз мушрикон биёростанд, то ҳалокашон кунанд ва дар бораи динашон ба шакку иштибохашон андозанд. Агар Худо мехост, чунин намекарданд. Пас бо дурӯғе, ки мебофанд, таркашои соз!'),
(927, 6, 138, 'Аз рӯи пиндор гуфтанд: «Инҳо чорпоёну киштзорони мамнӯъ аст. Ҳеҷ кас ҷуз он, ки мо бихоҳем, набояд аз онҳо бихӯрад». Ва инҳо чорпоёнест, ки савор шуданашон ҳаром аст ва инҳо чорпоёнест, ки номи Худоро бар онҳо ёд накунанд, ба Худо дурӯғ мебанданд ва ба зудӣ ба ҷазои дурӯғе, ки мебастаанд, ҷазояшонро хоҳад дод!'),
(928, 6, 139, 'Ва гуфтанд: «Он чӣ дар шиками ин чорпоён аст, барои мардони мо ҳалол ва барои занонамон ҳаром аст». Ва агар мурдор бошад, зану мард дар он шариканд. Худо ба сабаби ин гуфтор ҷазояшон хоҳад кард. Албатта Ӯ ҳакиму доност!'),
(929, 6, 140, 'Зиён карданд касоне, ки ба беақлӣ бе ҳеҷ ҳуҷҷате фарзандони худро куштанд ва ба Худо дурӯғ бастанд ва он чиро ба онҳо рӯзӣ дода буд, ҳаром карданд. Инон гумроҳ шудаанд ва роҳи ҳидоятро наёфтаанд!'),
(930, 6, 141, 'Ва Ӯст, ки боғҳое офарид мӯҳтоҷ ба поя ва низ худ танадор ва дарахти хурмову киштзор бо таъмҳои гуногун ва зайтуну анор, монанд, дар (мазаашон) номонанд. Чун самара оварданд, аз онҳо бихӯред ва дар рӯзи дарав ҳаққи онро низ бипардозед ва исроф макунед, ки Худо исрофкоронро дӯст надорад!'),
(931, 6, 142, 'Ва низ чорпоёне, ки бор мебаранд ё (барои куштан) хобонида шаванд. Аз он чӣ Худо ба шумо рӯзӣ додааст, бихӯред ва ба шайтон пайравӣ макунед, ки ӯ душмани ошкори шумост!'),
(932, 6, 143, 'Ҳашт ҷуфт аз гӯсфанд — нару мода ва аз буз—нару мода. Бигӯ: «Оё он ду нарро ҳаром кардааст ё он ду модаро ё он чиро, ки дар шиками модагон аст? Агар рост мегӯед, аз рӯи илм ба ман хабар диҳед».'),
(933, 6, 144, 'Ва аз шутур нару мода ва аз гов нару мода, бигӯ: «Оё он ду нарро ҳаром кардааст ё он ду модаро ё он чиро, ки дар шиками модагон аст? Оё он ҳангом, ки Худо чунин фармон медод, шумо он ҷо будед?» Пас чӣ касс ситамкортар аз он касест, ки ба Худо дурӯғ мебофад, то аз рӯи бехабарӣ мардумро гумроҳ кунад? Албатта Худо ситамкоронро ҳидоят намекунад.'),
(934, 6, 145, 'Бигӯ: «Дар миёни он чӣ бар ман ваҳй шудааст, чизеро, ки хӯрдани он ҳаром бошад, намеёбам ҷуз мурдор ё хуни рехта, ё гӯшти хук, ки палид аст ё ҳайвоне, ки дар куштанаш аз ҷиҳати нофармонӣ кардан, бе гуфтани номи Оллоҳ забҳаш кунанд». Агар касе ночор ба хӯрдан гардад, ҳар гоҳ бемайлй ҷӯяд ва аз хад нагузаронад; бидонад, ки Худо бахшанда ва меҳрубон аст!'),
(935, 6, 146, 'Ва бар яҳуд ҳаром кардем ҳар ҳайвони соҳибнохунеро ва аз гову гӯсфанд чарбуи он дуро ҷуз он чӣ бар пушти онҳост ё бар чарби рудаи онҳост ё ба устухонашон часпида бошад. Ба сабаби ситамкорияшон инчунин ҷазояшон додем ва Мо ростгӯёнем!'),
(936, 6, 147, 'Агар туро дурӯғ бароранд, бигӯ; «Парвардигори шумо соҳиби раҳмате васеъ аст ва хашму азобаш аз гунаҳгорон боздошта нахоҳад шуд».'),
(937, 6, 148, 'Мушрикон хоҳанд гуфт: «Агар Худо мехост, мо ва падаронамон мушрик намешудем ва чизеро ҳаром намекардем». Ҳамчунин касоне, ки пеш аз онҳо буданд, паёмбаронро дурӯғ бароварданд ва хашму азоби Моро чашиданд. Бигӯ: «Агар шуморо донише ҳаст, онро барои мо ошкор созед. Вале шумо танҳо аз гумони хеш пайравӣ мекунед ва дурӯғгуёне беш нестед!»'),
(938, 6, 149, 'Бигӯ: «Хоси Худост далели маҳкаму расо, агар мехост ҳамаи шуморо ҳидоят мекард».'),
(939, 6, 150, 'Бигӯ: «Гувоҳонатонро, ки гувоҳӣ медиҳанд, ки Худо ин ё онро ҳаром кардааст, биёваред!» Пас агар гувоҳӣ доданд, ту бо опон гувоҳӣ надеҳ ва аз хостаҳои онҳое, ки оёти Моро дурӯғ гуфтанд ва ба охират имон надоранд ва касонеро бо Парвардигорашон баробар медоранд, пайравӣ макун!'),
(940, 6, 151, 'Бигӯ: «Биёед, то он чиро, ки Парвардигоратон бар шумо ҳаром кардааст, бароятон бихонам. Ин, ки ба Худо ширк наёваред! Ва ба падару модар некӣ кунед! Ва аз бими дарвешӣ фарзандони худро накушед! Мо ба шумо ва онҳо рӯзӣ медиҳем. Ва ба корҳои зишт чӣ пинҳону чӣ ошкоро наздик нашавед! Ва касеро, ки Худо куштанашро ҳаром кардааст, ғайри ба хақ макушед! Инхост он чӣ Худо шуморо ба он супориш мекунад, бошад, ки ба ақл дарёбед!'),
(941, 6, 152, 'Ба моли ятим наздик машавед, магар ба некӯтарин ваҷҳе, ки ба салоҳи ӯ бошад то ба синни булуғ расад. Ва паймонаву вазнро аз рӯи адл комил кунед! Мо ба касе ҷуз ба андозаи тавонаш таклиф намекунем. Ва ҳар гоҳ сухан гӯед, одилона гӯед, ҳарчанд ба зиёни хешовандон бошад. Ва ба аҳди Худо вафо кунед! Инҳост он чӣ Худо шуморо ба он супориш мекунад, шояд, ки панд гиред!'),
(942, 6, 153, 'Ва ин аст роҳи рости Ман. Аз он пайравӣ кунед ва ба роҳҳои гуногун маравед, ки шуморо аз роҳи Худо пароканда месозад. Инҳост он чӣ Худо шуморо ба он супориш мекунад, шояд парҳезгор шавед!»'),
(943, 6, 154, 'Сипас ба Мӯсо китоб додем, то бар касе, ки некӯкор будааст, неъматро пурра кунем ва барои баёни ҳар чизе ва низ барои роҳнамоиву раҳмат. Шояд, ки ба дидори Парвардигорашон имон биёваранд!'),
(944, 6, 155, 'Ин китобест муборак. Онро нозил кардаем. Пас ба он пайравӣ кунед ва парҳезгор бошед, шояд ки мавриди раҳмат қарор гиред!'),
(945, 6, 156, 'То нагӯед, ки танҳо бар ду гурӯҳе, ки пеш аз мо буданд, китоб нозил шуда ва мо аз омӯхтани онҳо ғофил будаем.'),
(946, 6, 157, 'Ё нагӯед, ки агар бар мо низ китоб нозил мешуд, беҳтар аз онон ба роҳи ҳидоят мерафтем. Бар шумо низ аз ҷониби Парвардигоратон далели равшан ва ҳидояту раҳмат фаро расид. Пас чӣ касе ситамкортар аз он кас аст, ки оёти Худоро дурӯғ пиндошт ва аз онҳо рӯйгардон шуд? Ба зудӣ касонеро, ки аз оёти Мо рӯйгардон шудаанд, ба сабаби ин рӯйгардониашон ба азобе бад ҷазо хоҳем дод!'),
(947, 6, 158, 'Оё интизори он доранд, ки фариштагон наздашон биёянд? Ё Парвардигорат? "Ё нишонае аз нишонаҳои Худо бар онҳо зоҳир шавад. Рӯзе, ки баъзе нишонаҳои Худо ошкор шавад, имони касе, ки пеш аз он имон наёварда ё ба ҳангоми имон кори неке анҷом надодааст, барои ӯ суде нахоҳад дошт. Бигӯ: «Чашм ба роҳ бошед, мо низ чашм бароҳем».'),
(948, 6, 159, 'Туро бо онҳо, ки дини хеш фирқа-фирқа карданд ва даста-даста шуданд, коре нест. Кори онҳо бо Худост. Ва Худо ононро ба корҳое, ки мекарданд, огоҳ месозад.'),
(949, 6, 160, 'Ҳар кас кори неке анҷом диҳад, даҳ баробар ба ӯ подош диҳанд ва ҳар кӣ кори баде анҷом диҳад танҳо монанди он ҷазо бинад, то ситаме бар онҳо нарафта бошад.'),
(950, 6, 161, 'Бигӯ: «Парвардигори ман маро ба роҳи рост ҳидоят кардааст, ба дине ҳамеша устувор, дини ҳанифи Иброҳим. Ва ӯ аз мушрикон набуд».'),
(951, 6, 162, 'Бигӯ: «Намози ману қурбонии ман ва зиндагии ману марги ман барои Худо — он Парвардигори ҷаҳониён аст.'),
(952, 6, 163, 'Ӯро шарике нест. Ба ман чунин амр шудаст ва ман аз нахустин мусалмононам».'),
(953, 6, 164, 'Бигӯ: «Оё ҷуз Оллоҳ Парвардигоре ҷӯям?» Ӯ Парвардигори ҳар чизест ва ҳар кас танҳо ҷазои кори худро мебинад. Ва касе бори гуноҳӣ дигареро бар душ намекашад. Сипас бозгашти ҳамаи шумо ба назди Парвардигоратон аст ва Ӯ шуморо ба он чизҳое, ки дар он ихтилоф меварзидед огоҳ мекунад.'),
(954, 6, 165, 'Ӯст Худое, ки шуморо халифагони замин кард ва баъзеро бар баъзи дигар ба дараҷаҳо бартарӣ дод, то шуморо дар чизе, ки атоятон кардааст, биёзмояд. Албатта Парвардигорат зуд ҷазо медиҳад ва Ӯ бахшояндаву меҳрубон аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Алиф, лом, мим, сод.'),
(956, 7, 2, 'Китобест, ки бар ту нозил шуда, дар дили ту аз он шубҳае набошад, то ба он бим диҳӣ (тарсонӣ) ва мӯъминонро панде бошад.'),
(957, 7, 3, 'Ба он чӣ аз ҷониби Парвардигоратон бароятон нозил шудааст, пайравӣ кунед ва ғайри Ӯ аз худоёни дигар пайравӣ макунед! Шумо чӣ андак пандмегиред!'),
(958, 7, 4, 'Чӣ бисёр деҳаҳое, ки мардумашро ба ҳалокат расонидем ва азоби мо шабҳангом ё он гоҳ ки ба хоби нимрӯзӣ фурӯ рафта буданд, ба онон даррасид.'),
(959, 7, 5, 'Чун азоби Мо ба онон даррасид, суханашон фақат ин буд, ки гуфтанд; «Мо ситамкор будем!».'),
(960, 7, 6, 'Албатта, аз мардуме, ки барояшон паёмбароне фиристода шуда ва низ аз паёмбароне, ки фиристода шудаанд, суол хоҳем кард.'),
(961, 7, 7, 'Ва аз ҳар чӣ кардаанд, бо огоҳии тамом барояшон ҳикоят хоҳем кард, зеро Мо ҳаргиз ғоиб набудаем!'),
(962, 7, 8, 'Дар он рӯз вазн кардан ба ҳақ хоҳад буд. Онҳо, ки тарозуяшон сангин (вазнин) аст, наҷот ёфтаанд.'),
(963, 7, 9, 'Онон, ки тарозуи аъмолашон сабук гаштааст, касоне ҳастанд, ки ба оёти Мо имон наёварда буданд ва аз ин рӯ ба худ зиён расонидаанд.'),
(964, 7, 10, 'Дар замнн ҷойгоҳатон додем ва роҳҳои маъишататонро дар он қарор додем. Ва чӣ андак шукр мегузоред!'),
(965, 7, 11, 'Ва шуморо биёфаридем ва сурат бахшидем, он гоҳ ба фариштагон гуфтем: «Одамро саҷда кунед», ҳама ҷуз Иблис саҷда карданд ва Иблис дар шумори саҷдакунандагон набуд.'),
(966, 7, 12, 'Худо гуфт: «Вақте туро ба саҷда фармон додам, чӣ чиз туро аз он боздошт?» Гуфт: «Мап аз ӯ беҳтарам, маро аз оташ офарндаӣ ва ӯро аз гил».'),
(967, 7, 13, 'Гуфт: «Аз ин мақом поён рав! Туро чӣ расад, ки дар он гарданкашӣ кунӣ? Берун рав, ки ту аз хоршудагонӣ?»'),
(968, 7, 14, 'Гуфт: «Маро то рӯзи қиёмат, ки зинда мешаванд, мӯҳлат деҳ».'),
(969, 7, 15, 'Гуфт: «Ту аз мӯҳлатёфтагонӣ».'),
(970, 7, 16, 'Гуфт; «Ҳоло ки маро ноумед сохтаӣ, ман ҳам онҳоро аз роҳи рости Ту мунҳариф (каҷ) мекунам.'),
(971, 7, 17, 'Он гоҳ аз пешу аз пас ва аз чапу рост бар онҳо метозам. Ва бештаринашонро шукргузор нахоҳӣ ёфт».'),
(972, 7, 18, 'Гуфт: «Аз ин ҷо берун шав, манфури ронда! Аз касоне, ки пайравии ту мекунанд ва аз ҳамаи шумо ҷаҳаннамро хоҳам пур кард!'),
(973, 7, 19, 'Эй одам, туву ҳамсарат дар биҳишт макон гиред. Аз ҳар ҷо, ки хоҳед, бихӯред, вале ба ин дарахт наздик машавед, ки дар қатори ба хеш ситамкунандагон хоҳед шуд!»'),
(974, 7, 20, 'Пас шайтон он дуро васваса кард, то шармгоҳашонро, ки аз онҳо пӯшида буд, дар назарашон ошкор кунад. Ва гуфт: «Парвардигоратон шуморо аз ин дарахт манъ кард, то мабод аз фариштагон ё ҷобидонон шавед».'),
(975, 7, 21, 'Ва барояшон савганд хӯрд, ки некхоҳи шумоям.'),
(976, 7, 22, 'Ва он дуро фиребнд ва ба пастӣ андохт. Чун аз он дарахт хӯрданд, шармгоҳҳояшон ошкор шуд ва ба пӯшидани худ бо баргҳои биҳишт сар карданд. Парпардигорашон нидо кард: «Оё шуморо аз он дарахт манъ накарда будам ва нагуфта будам, ки шайтон ба ошкоро душмани шумост?»'),
(977, 7, 23, 'Гуфтанд: «Эй Парвардигори мо, ба худ ситам кардем ва агар моро набахшӣ ва бар мо раҳмат накунӣ, аз зиёндидагон хоҳем буд».'),
(978, 7, 24, 'Гуфт: «Поён равед, баъзе душмани баъзе дигар ва то рӯзи қиёмат замин қароргоҳ ва ҷои манфиати шумо хоҳад буд».'),
(979, 7, 25, 'Гуфт: «Дар он ҷо зиндагӣ хоҳед кард ва дар он ҷо хоҳед мурд ва аз он берун оварда шавед».'),
(980, 7, 26, 'Эй фарзандони Одам, барои шумо ҷомае фиристодем то шармгоҳатонро бипушад ва низ ҷомаи зинат. Ва ҷомаи парҳезгорӣ аз хар ҷомае беҳтар аст. Ва ин яке аз оёти Худост, бошад, ки панд гиранд.'),
(981, 7, 27, 'Эй фарзандони Одам, шайтон шуморо нафиребад, ҳамчунон, ки падару модаратонро аз биҳишт берун ронд, либос аз танашон канд, то шармгоҳашонро ба онҳо бинамоёнад. Ӯ ва қабилааш аз ҷое, ки онҳоро намебинед, шуморо мебинанд. Мо шайтонҳоро дӯстони касоне қарор додем, ки имон намеоваранд.'),
(982, 7, 28, 'Чун кори зиште кунанд, гӯянд: «Падарони худро низ чунин ёфтаем ва Худо моро ба он фармон додааст». Бигӯ: «Худо ба зишткорӣ фармон намедиҳад. Чаро дар бораи Худо чизҳое мегӯед, ки намедонед?»'),
(983, 7, 29, 'Бигӯ: «Парвардигори ман ба адл фармон додааст ва ба ҳангоми ҳар намоз рӯй ба ҷониби Ӯ доред, ва Ӯро бо имони холис бихонед. Ва ҳамчунон, ки шуморо офаридааст, бозмегардед.'),
(984, 7, 30, 'Гурӯҳеро ҳидоят карда ва гурӯҳе гумроҳиро лоиқанд. Инҳо шайтонҳоро ба ҷои Худо ба дӯстӣ гирифтанд ва мепиндоранд, ки ҳидоят ёфтаанд».'),
(985, 7, 31, 'Эй фарзандони Одам, ба ҳангоми ҳар ибодат либоси худ бипӯшед. Ва низ бихӯреду биёшомед, вале исроф макунед, ки Худо исрофкоронро дӯст намедорад.'),
(986, 7, 32, 'Бигӯ: «Чӣ касе либосҳоеро, ки Худо барои бандагонаш баровардааст ва хурданиҳои хуштаъмро ҳаром кардааст?». Бигӯ: «Ин чизҳо дар ин дунё барои касонест, ки имон овардаанд ва дар рӯки қиёмат низ хоси онҳо бошад». Оёти Худоро барои доноён инчунин батафсил баён мекунем.'),
(987, 7, 33, 'Бигӯ: «Парвардигори ман, зишткориҳоро чӣ ошкор бошанд ва чӣ пинҳон ва низ гуноҳону афзунӣ ҷустан ба ноҳақро ҳаром кардааст ва низ ҳаром аст, чизеро шарики Худо созед, ки ҳеҷ далеле бар вуҷуди он нозил нашудааст ё дар бораи Худо чизҳое бигӯед, ки намедонед.'),
(988, 7, 34, 'Ҳар умматеро муддати умрест. Чун аҷалашон фаро расад, як соат пешу пас нашаванд».'),
(989, 7, 35, 'Эй фарзандони Одам, ҳар гоҳ паёмбароне аз худи шумо биёянд ва оёти Маро бар шумо бихонанд, касоне, ки парҳезгорӣ кунанд ва ба салоҳ оянд, биме бар онҳо нест ва худ ғамгин намешаванд!'),
(990, 7, 36, 'Ва онҳо, ки оёти Моро дурӯғ баровардаанд ва аз он сар баргофтаанд, аҳли ҷаҳанкаманд ва ҷовидона дар он хоҳанд буд!'),
(991, 7, 37, 'Кист ситамкортар аз он, ки ба Худо дурӯғ бандад ё оёти Ӯро дурӯғ барорад? Насибе, ки барояшон муқаррар шуда, ба онҳо хоҳад расид. Он гоҳ ки фиристодагони мо биёянд, то ҷонашонро бигиранд, мепурсанд: «0н чизҳое, ки ба ҷои Худо парастиш мекардед. акиуи куҷоянд?. Гӯянд; «Нест шуданд ва аз дасти мо рафтанд!» Ва дар ин ҳол ба зиёни худ шоҳидӣ диҳанд, ки кофир будаанд.'),
(992, 7, 38, 'Гӯяд: «Ба миёни умматҳое, ки пеш аз шумо будаанд, аз ҷинну инс дар оташ дохил шавед. Ҳар уммате, ки ба оташ дохил шавад, уммати ҳамдини худро лаънат кунад. То чун ҳамагӣ дар он ҷо гирд оянд, гурӯҳҳое, ки пайрав будаанд, дар бораи гурӯҳҳое, ки пешво будаанд, гӯянд: «Парвардигоро, инҳо моро гумроҳ карданд, дучандон дар оташ азобашон кун». Гӯяд: «Азоби ҳама дучандон аст, вале шумо намедонед!»'),
(993, 7, 39, 'Пешвоён ба пайравон гӯянд: «Шуморо бар мо ҳеҷ бартарӣ нест, инак ба ҷазои корҳое, ки карда будед, азобро бичашед!»'),
(994, 7, 40, 'Дарҳои осмон бар рӯи касоне, ки оёти Моро дурӯғ гуфтанд ва аз онҳо сар тофтаанд, кушода намешаванд ва ба биҳишт намедароянд, то он гоҳ, ки шутур аз сӯрохи сузан бигзарад. Ва осиёнро инчунин ҷазо медиҳем!'),
(995, 7, 41, 'Бистаре аз оташи ҷаҳаннам дар зер ва пӯшише аз оташи ҷаҳаннам бар рӯй доранд. Ва ситамкоронро инчунин ҷазо медиҳем!'),
(996, 7, 42, 'Ба ҳеҷ кас ғайри андозаи тавонаш таклиф намекунем. Онон, ки имон овардаанд, ва корҳои некӯ кардаанд, аҳли биҳиштанд ва дар он ҷо ҷовидонанд.'),
(997, 7, 43, 'Ва ҳар гуна кинаеро аз дилашон бармеканем. Наҳрҳо дар зери пояшон ҷорист. Гӯянд: «Шукр Худоро, ки моро ба ин роҳ раҳбарӣ карда ва агар моро раҳбарӣ накарда буд, роҳи худ намеёфтем. Расулони Парвардигори мо ба ҳақ омаданд. Ва он гоҳ онҳоро овоз диҳанд, ки ба ивази корҳое, ки мекардед, ин биҳиштро ба шумо додаанд!»'),
(998, 7, 44, 'Биҳиштиён дузахиёнро овоз диҳанд, ки мо ба ҳақиқат ёфтем он чиро, ки Парвардигорамон ваъда дода буд, оё шумо низ ба ҳақиқат ёфтаед он чиро, ки Парвардигоратон ваъда дода буд? Гӯянд: «Оре». Он гоҳ овоздиҳандае дар он миён овоз диҳад, ки лаънати Худо бар кофирон бод!'),
(999, 7, 45, 'Онон, ки аз роҳи Худо рӯй бармегардонанд ва онро каҷравӣ мепиндоранд ва ба қиёмат имон надоранд!'),
(1000, 7, 46, 'Ва миёнашон ҳиҷобест ва бар аъроф мардоне ҳастанд, ки ҳамаро ба нишонияшон мешиносанд ва аҳли биҳиштро овоз медиҳанд, ки салом бар шумо бод. Инҳо ҳарчанд тамаъя биҳишт доранд, вале ҳанӯз ба он дохил нашудаанд.'),
(1001, 7, 47, 'Чун чашм ба ҷониби дӯзахиён гардонанд, гӯянд: «Эй Парвардигори мо, моро дар шумори ситамкорон қарор мадеҳ!»'),
(1002, 7, 48, 'Сокинони аъроф мардонеро, ки аз нишонияшон мешиносанд, овоз диҳанд ва гӯянд; «Он молҳо, ки ҷамъ оварда будед ва он ҳама саркашӣ, ки доштед, шуморо фоидае набахшид!'),
(1003, 7, 49, 'Оё инхо6 хамом касонанд, ки шумо7 савганд хурда будед, ки ра^мати Худованд насибашон намешавад? Дохил дар бих, ишт шавед8, на биме бар шумост ва на гамгин мешавед!»'),
(1004, 7, 50, 'Дузахиён бихиштиёнро овоз диҳанд, ки андаке об ё аз чизҳое, ки Худо ба шумо арзонӣ кардааст, бар мо фурӯ резед! Гӯянд: «Худо онҳоро бар кофирон ҳаром кардааст!'),
(1005, 7, 51, 'Имрӯз онҳоро, ки дини хеш лаҳву бозича пиндоштанд ва зиндагии дунё фиребашон дода буд, фаромӯш мекунем, ҳамчунон, ки онҳо низ расидан ба мн рӯзро аз ёд бурда буданд ва оёти Моро дурӯғ мебароварданд.'),
(1006, 7, 52, 'Барои ҳидояти мӯъминон ва бахшоиш ба онҳо китобе овардем, ки дар он ҳар чизро аз рӯи дониш батафсил баён кардаем.'),
(1007, 7, 53, 'Оё ғайри натиҷаи амалҳоро интизоранд? Рӯзе, ки натиҷаи аъмол падидор шавад, касоне, ки он рӯзро аз ёд бурда буданд, мегӯянд: «Паёмбарони Парвардигори мо ба хақ омаданд. Оё шафеъоне ҳастанд, ки моро шафоъат кунанд? Ё он ки касе ҳаст, ки моро бозгардовад то корҳои дигаре ҷуз он корҳо, ки мекардем, анҷом диҳем?» Ба худ зиён расонданд ва он бутонро, ки ба дурӯғ сохта буданд, инак аз даст додаанд.'),
(1008, 7, 54, 'Парвардигори шумо Оллоҳ аст, ки осмонҳову заминро дар шаш рӯз офарид. Пас бар арш пардохт. Шабро дар рӯз мепушонад ва рӯз шитобон онро металабад. Ва офтобу моҳ ва ситорагон мусаххари фармони Ӯ ҳастанд. Огоҳ бошед, ки Ӯрост офаринишу фармонравоӣ. Худо, он Парвардигори ҷаҳониён, бағоят бузург аст!'),
(1009, 7, 55, 'Парвардигоратонрр бо тазаррӯъ (зорӣ) ва дар ниҳон бихонед, зеро Ӯ аз ҳадгузарандагони саркашро дӯст надорад!'),
(1010, 7, 56, 'Дар замин он гоҳ, ки ба салоҳ омадааст, фасод макунед! Ва Худоро аз рӯи тарсу умед бихонед! Ва раҳмати Худо ба некӯкорон наздик аст.'),
(1011, 7, 57, 'Ва Ӯст, ки пешопеши раҳмати худ бодҳоро хушхабар мефирстад. Чун бодҳо абрҳои гаронборро бардоранд, Мо онро ба сарзаминҳои мурда равон созем ва аз он борон мефиристем ва ба борон ҳар гуна меваҳоеро мерӯёнем. Мурдагонро низ инчунин зинда мегардонем, шояд панд гиред!'),
(1012, 7, 58, 'Ва сарзамини хуб — гиёҳи он ба фармони Парвардигораш мерӯяд ва замини бад — ҷуз андак гиёҳе аз он падид намеояд. Барои мардуме, ки шукр мегӯянд, сети Худоро инчунин гуногун баён мекунем?'),
(1013, 7, 59, 'Нӯҳро бар қавмаш ба рисолат фиристодем. Гуфт: «Эй қавми ман, Оллоҳро бипарастед, шуморо Худое ҷуз Ӯ нест, ман аз азоби рӯзе бузург бар шумо метарсам».'),
(1014, 7, 60, 'Бузургони қавмаш гуфтанд: «Ту] ро ба ошкоро дар гумроҳӣ мебинем».'),
(1015, 7, 61, 'Гуфт: «Эй қавми ман, гумроҳиро дар ман роҳе нест, ман паёмбаре аз ҷониби Парвардигори ҷаҳониёнам.'),
(1016, 7, 62, 'Паёмҳои Парвардигорамро ба шумо мерасонам ва шуморо панд медиҳам ва аз Худо он медонам, ки шумо намедонед.'),
(1017, 7, 63, 'Оё аз ин ки бар марде аз худатон аз ҷониби Парвардигоратон ваҳй нозил шудааст, то шуморо битарсонад ва парҳезгори кунед ва коре кунад, ки мавриди раҳмат қарор гиред, тааҷҷуб кардед?»'),
(1018, 7, 64, 'Пас дурӯғ бароварданд ва Мо ӯву касонеро, ки бо ӯ дар киштӣ буданд, раҳонидем ва ононро, ки оёти Моро дурӯғ мепиндоштанд, ғарқ сохтем, ки мардуме кӯрдил буданд!'),
(1019, 7, 65, 'Ва бар қавми Од бародарашон Ҳудро фиристодем. Гуфт: «Эй қавми ман, Оллоҳро биперастед, ки шуморо ғайри Ӯ худое нест ка чаро намепарҳезед?»'),
(1020, 7, 66, 'Бузургони қавми ӯ, ки кофир шуда буданд, гуфтанд: «Мебинем, ки ба бехирадӣ гирифтор шудаӣ ва пиндорем, ки аз дурӯғгуён бошӣ».'),
(1021, 7, 67, 'Гуфт: «Эй қавми ман, дар ман нишонае аз бехирадӣ нест, ман паёмбари Парвардигори ҷаҳониёнам.'),
(1022, 7, 68, 'Паёмҳои Парвардигорамро ба шумо мерасонам ва шуморо пандгӯе боваринокам.'),
(1023, 7, 69, 'Оё аз ин, ки бар марде аз худатон аз ҷониби Пар-вардигоратон ваҳй нозил шудааст, то шуморо битарсонад, таъаҷҷуб мекунед? Ба ёд оред он замонро, ки шуморо ҷонишини қавми Нӯҳ сохт ва ба ҷисм фузунӣ дод. Пас неъматҳои Худоро ба ёд оваред, бошад, ки наҷот ёбед!»'),
(1024, 7, 70, 'Гуфтанд: «Оё назди мо омадаӣ, то танҳо Оллоҳро бипарастем ва он чиро, ки падаронамон мепарастиданд, раҳо кунем? Агар рост мегӯӣ, он чиро, ки ба мо ваъда медиҳӣ, биёвар!»'),
(1025, 7, 71, 'Гуфт: «Азобу хашми Парвардигоратон ҳатман бар шумо воқеъ хоҳад шуд. Оё дар бораи ин бутҳое; ки худ ва падаронатон ба ин номҳо номидаед ва Худо ҳеҷ далеле бар онҳо нозил насохтааст, бо ман ҳусумат мекунед? Ба интизор бимонед, ман ҳам бо шумо ба интизор мемонам».'),
(1026, 7, 72, 'Ҳуд ва ҳамроҳонашро ба фазлу раҳмати хеш раҳонидем ва касонеро, ки оёти Моро дурӯғ бароварданд ва имон наёварда буданд, аз реша баркандем.'),
(1027, 7, 73, 'Бар қавми Самуд бародарашон Солеҳро фиристодем. Гуфт: «Эй қавми ман, Оллоҳро бипарастед, шуморо ҳеҷ худое ҷуз Ӯ нест. Аз ҷониби Худо барои шумо нишонае ошкор омад. Ин модашутури Худо бароятон нишонаест. Ӯро бигузоред то дар замини Худо бичарад ва ҳеҷ осебе ба ӯ нарасонед, ки азобе дардовар шуморо фаро хоҳад гирифт.'),
(1028, 7, 74, 'Ба ёд оред он замонро, ки шуморо ҷонишинони қавми Од кард ва дар ин замин ҷой дод, то бар рӯи хокаш қасрҳо бино кунед ва дар кӯҳистонҳояш хонаҳое биканед, Неъматҳои Худоро ёд кунед ва дар замин табаҳкориву фасод макунед!»'),
(1029, 7, 75, 'Бузургони қавмаш, ки гарданкашӣ мекарданд, ба забуншудагони қавм, ки имон оварда буданд, гуфтанд: «Оё медонед, ки Солеҳ аз ҷониби Парвардигораш омада аст?» Гуфтанд: «Мо ба дине, ки ба он амр шуда, имон дорем!»'),
(1030, 7, 76, 'Гарданкашон гуфтанд: «Мо ба касе, ки шумо имон овардаед, имон намеоварем».'),
(1031, 7, 77, 'Пас модашутурро қатл карданд ва аз фармони Парвардигорашон сар боззаданд ва гуфтанд: «Эй Солеҳ, агар паёмбар ҳастӣ, он чиро ба мо ваъда медиҳӣ, биёвар!»'),
(1032, 7, 78, 'Пас зилзилае сахт ононро фурӯ гирифт ва дар хонаҳои худ бар ҷой мурданд.'),
(1033, 7, 79, 'Солеҳ аз онон рӯй баргардонид ва гуфт: «Эй қавми ман, рисолати Парвардигорамро ба шумо расонидам ва шуморо панд додам, вале шумо некхоҳонро дӯст надоред!»'),
(1034, 7, 80, 'Ва Лутро фиристодем. Он гоҳ ба қавми худ гуфт: «Чаро коре зишт мекунед, ки ҳеҷ кас аз мардуми Ҷаҳон пеш аз шумо накардааст?'),
(1035, 7, 81, 'Шумо ба ҷои занон бо мардон шаҳват меронед. Шумо мардуме таҷовузкор ҳастед».'),
(1036, 7, 82, 'Ҷавоби қавми ӯ ҷуз ин набуд, ки гуфтанд: «Онҳоро аз деҳаи худ биронед, ки онон мардуме ҳастанд, ки аз кори мо безорӣ меҷӯянд!'),
(1037, 7, 83, 'Лут ва хонадонашро наҷот додем. Ғайри занаш, ки бо дигарон дар шаҳр монд.'),
(1038, 7, 84, 'Бар онҳо бороне боридем, бингар, ки оқибати муҷримон чӣ гуна буд!'),
(1039, 7, 85, 'Ва бар мардуми Мадян бародарашон Шуъайбро фирестодем. Гуфт: «Эй қавми ман, Оллоҳро бипарастед, шуморо худое ҷуз Ӯ нест. Аз ҷониби Парвардигоратон нишонае равшан омадааст. Паймонаву тарозуро расо адо кунед ва ба мардум кам мафурӯшед ва аз он пас, ки замин ба салоҳ омадааст, дар он фасод макунед, ки агар имон овардаед, ин бароятон беҳтар аст!'),
(1040, 7, 86, 'Ва бар сари роҳҳо манишинед, то мӯъминони ба Худоро битарсонед ва аз роҳи Худо боздоред ва ба каҷравӣ водоред. Ва ба ёд оред он гоҳ ки андак будед, Худо бар шумори шумо афзуд. Ва бингаред, ки оқибати фасодкорон чӣ гуна будааст!'),
(1041, 7, 87, 'Агар гурӯҳе аз шумо ба он чӣ ман аз ҷониби Худо ба он мабъус шудаам, имон овардаанд ва гурӯҳе ҳанӯз имон наовардаанд, сабр кунед, то Худо миёни мо ҳукм куиад, ки Ӯ беҳтарини доварон аст!»'),
(1042, 7, 88, 'Бузургони қавмаш, ки саркашӣ пеша карда буданд, гуфтанд: «Эй Шуъайб, туву касонеро, ки ба ту имон овардаанд, аз деҳаи худ меронем, агар ба дини мо барнагардед». Гуфт: «Ва ҳарчанд аз он бадбинӣ дошта бошем?».'),
(1043, 7, 89, 'Пас аз он ки Худо моро аз дини шумо раҳонидааст, агар ба он бозгардем, бар Худо дурӯғ баста бошем ва мо дигар бор ба он дин бознамегардем, магар он ки Худо, он Парвардигори мо хоста бошад. Зеро илми Парвардигори мо бар ҳама чиз иҳота дорад. Мо бар Худо таваккал мекунем. Эй Парвардигори мо, миёни мову қавми мо ба ҳақ роҳе бикшо, ки Ту беҳтарин роҳкушоён ҳастӣ!»'),
(1044, 7, 90, 'Бузургони қавмаш, ки кофир буданд, гуфтанд: «Агар ба Шуъайб пайравӣ кунед, сахт зиён кардаед».'),
(1045, 7, 91, 'Пас зилзилае сахт ононро фурӯ гирифт ва дар хонаҳои худ бар ҷой мурданд.'),
(1046, 7, 92, 'Онон, ки Шуъайбро ба дурӯғгӯӣ нисбат доданд, гӯӣ, ки ҳаргиз дар он диёр набудаанд. Окон, ки Шуъайбро ба дурӯғгӯӣ нисбат доданд, худ зиён карданд.'),
(1047, 7, 93, 'Пас аз онҳо руйгардон шуд ва гуфт: «Эй қавми ман, албатта паёмҳои Парвардигорамро ба шумо расонидам ва пандатон додам. Чӣ гуна бар мардуме кофир андӯҳгин шавам?»'),
(1048, 7, 94, 'Ва мо ҳеҷ паёмбареро ба ҳеҷ деҳае нафиристодем, магар он ки сокинонашро ба сахтиву беморӣ гирифтор кардем, бошад, ки зорӣ кунанд.'),
(1049, 7, 95, 'Он гоҳ ба ҷои балову меҳнат ба хушиву хубӣ супурдем, то шумораашон афзун шуд ва гуфтанд: Падарони мо ҳам бо сахтиву беҳрузӣ буданд». Ногаҳон ононро бехабар фурӯ гирифтем.'),
(1050, 7, 96, 'Агар мардуми деҳаҳо имон оварда ва парҳезгорӣ пеша карда будакд, баракоти осмону заминро ба рӯяшон мекушудем. Вале паёмбаронро ба дурӯғгӯӣ нисбат доданд. Мо низ ба ҷазои кирдорашон муохазашон (уқубат) кардем.'),
(1051, 7, 97, 'Оё мардуми деҳаҳо бепарвоанд, аз ин ки азоби мо шабҳангом, ки ба хоб рафтаанд, бар сари онҳо биёяд?'),
(1052, 7, 98, 'Ва оё мардуми деҳаҳо бепарвоанд, аз ин ки азоби мо ба ҳангоми чошт, ки ба бозӣ машғуланд, бар сари онҳо биёяд.'),
(1053, 7, 99, 'Оё пиндоштанд, ки аз макри Худо дар амонанд? Аз макри Худо ҷуз зиёнкорон эмин нанишинанд.'),
(1054, 7, 100, 'Оё барои онон, ки заминро аз пешиниён ба ирс бурдаанд, равшан нашудааст, ки агар бихоҳем, онҳоро низ ба ҷазои гуноҳонашон ба уқубат мерасонем ва бар дилҳояшон мӯҳр мениҳем, то шунидаи натавонанд?'),
(1055, 7, 101, 'Инхо деҳаҳоест, ки мо ахборашонро бар ту ҳиконт кунем. Паёмбаронашон бо далелҳои равшан омаданд. Ва ба он чизҳо, ки аз он пеш дурӯғ хонда буданд, имон наёварданд. Ва Худо бар дилҳои кофирон инчунин мӯҳр мениҳад!'),
(1056, 7, 102, 'Дар бештаринашон вафое ба аҳд наёфтем ва бештаринро ҷуз нофармонон надидем.'),
(1057, 7, 103, 'Баъд аз онҳо Мӯсоро бо оётамон бар Фиръавну қавмаш фиристодем. Ба оётамон кофир шуданд. Инак бингар, ки оқибати муфсидон чӣ гуна будааст!'),
(1058, 7, 104, 'Мӯсо гуфт: «Эй Фиръавн, ман паёмбаре аз ҷониби Парвардигори оламиёнам.'),
(1059, 7, 105, 'Шоиста аст, ки дар бораи Худо ҷуз ба ростӣ сухан нагӯям. Ман ҳамроҳ бо мӯъҷизае аз ҷониби Парвардигоратон омадаам. Банӣ-Исроилро бо ман бифирист».'),
(1060, 7, 106, 'Гуфт: «Агар рост мегӯӣ ва мӯъҷизае ба ҳамроҳ дорӣ, онро биёвар».'),
(1061, 7, 107, 'Асояшро андохт, аждаҳое ростин шуд.'),
(1062, 7, 108, 'Ва дасташро берун овард, дар назари онон, ки медиданд сафеду дурахшон буд.'),
(1063, 7, 109, 'Бузургони қавми Фиръавн гуфтанд: «Ин ҷодугарест доно,'),
(1064, 7, 110, 'мехоҳад шуморо аз сарзаминатон берун кунад, чӣ мефармоед?»'),
(1065, 7, 111, 'Гуфтанд: «Ӯ ва бародарашро нигаҳ дор ва касонеро ба шаҳрҳо бифирист,'),
(1066, 7, 112, 'то ҳамаи ҷодугарони доноро назди ту биёваранд».'),
(1067, 7, 113, 'Ҷодугарон назди Фиръавн омаданду гуфтанд: «Агар ғалаба ёбем, моро мукофоте ҳает?»'),
(1068, 7, 114, 'Гуфт: «Оре, ва шумо аз наздиконам хоҳед буд».'),
(1069, 7, 115, 'Гуфтанд: «Эй Мӯсо, оё нахуст ту мепартоӣ ё мо партоем?»'),
(1070, 7, 116, 'Гуфт; «Шумо бипартоед». Чун афканданд, дидагони мардумро ҷоду карданд ва онҳоро тарсониданд ва ҷодуе азим оварданд.'),
(1071, 7, 117, 'Ва ба Мӯсо ваҳй кардем, ки асои худро бияфкан. Ба ногоҳ диданд, ки ҳамаи ҷодуҳояшонро фурӯ мебарад.'),
(1072, 7, 118, 'Пас ҳақ ба субут расид ва корҳои онон ботил шуд.'),
(1073, 7, 119, 'Дар ҳамон ҷо мағлуб шуданд ва хору забун бозгаштанд.'),
(1074, 7, 120, 'Ҷодугарои ба саҷда афтоданд.'),
(1075, 7, 121, 'Гуфтанд: «Ба Парвардигори ҷаҳониён имон овардем,'),
(1076, 7, 122, 'Парвардигори Мӯсо ва Ҳорун».'),
(1077, 7, 123, 'Фиръавн гуфт: «Оё пеш аз он ки ман ба шумо рухсат диҳам, ба Ӯ имон овардед? Ин ҳиллаест, ки дар бораи ин шаҳр андешидаед, то мардумашро берун кунед. Ба зудӣ хоҳед донист!'),
(1078, 7, 124, 'Дастҳову пойҳоятонро ба хилофи якдигар хоҳам бурид ва ҳамагиятонро бар дор хоҳам кард!»'),
(1079, 7, 125, 'Гуфтанд: «Мо ба назди Парвардигорамон бозмегардем!'),
(1080, 7, 126, 'Хашм бар мо мегирӣ, ба; рои оя ки чун нишонаҳои Парвардигорамон бар мо ошкор шуд, ба онҳо имон овардем. Эй Парвардигори мо, бар мо сабр бибор ва моро мусалмон бимирон!'),
(1081, 7, 127, 'Меҳтарони қавми Фиръавн гуфтанд: Оё Мӯсо ва қавмашро мегузорӣ, то дар замин фасод кунанд ва туву худоёнатро тарк гӯянд?» Гуфт: «Писаронашонро хоҳам кушт ва занонашонро зинда хоҳам гузошт. Мо болотар аз онҳоем ва бар онҳо ғалаба меёбем!»'),
(1082, 7, 128, 'Мӯсо ба қавмаш гуфт: «Аз Худо мадад ҷӯед ва сабр пеша созед, ки ин замин аз они Худост ва ба ҳар кас аз бандагонаш, ки бихоҳад, онро ба мерос медиҳад. Ва оқибати нек аз они парҳезгорон аст!»'),
(1083, 7, 129, 'Гуфтанд: «Пеш аз он ки ту биёӣ, дар ранҷ будем ва пас аз он ки омадӣ, боз дар ранҷем». Гуфт: «Умед аст, ки Парвардигоратон душманатонро ҳалок кунад ва шуморо дар рӯи замин ҷонишини ӯ гардонад. Он гоҳ бингарад, ки чӣ мекунед!»'),
(1084, 7, 130, 'Қавми Фиръавнро ба қаҳту нуқсони ҳосил мубтало кардем, шояд панд гиранд!'),
(1085, 7, 131, 'Чум некие насибашон мешуд, мегуфтанд: «Ҳаққи мост». Ва чун бадие ба онҳо мерасид, мегуфтанд: «Аз шумии Мӯсо ва пайравони ӯст.» Огоҳ бошед, он неку бад, ки ба онҳо расад, аз Худост, вале бештаринашон намедонанд!'),
(1086, 7, 132, 'Ва гуфтанд: «Ҳар гуна мӯъҷиза барои мо биёварӣ, ки моро ба он ҷоду кунӣ, ба ту имон нахоҳем овард!'),
(1087, 7, 133, 'Мо низ ба онҳо нишонаҳое ошкор ва гуногун чун тӯфону малах ва шапушу қурбоққа ва хун фиристодем. Боз саркашӣ карданд, ки мардуме гунаҳкор буданд.'),
(1088, 7, 134, 'Ва чун азоб бар онҳо фурӯд омад, гуфтанд: «Эй Мӯсо, ба он аҳде, ки Худоро бо ту ҳаст, ӯро бихон, ки агар ин азоб аз мо дур кунӣ, ба ту имон меоварем ва банӣ-Исроилро бо ту мефиристем».'),
(1089, 7, 135, 'Чун то он замон, ки қарор ниҳода буданд, азобро аз онҳо дур кардем, паймони худро шикастанд.'),
(1090, 7, 136, 'Пас аз онҳо интиком гирифтем ва дар дарё ғарқашон кардем. Зеро оёти Моро дурӯғ донистанд ва аз онҳо ғафлат карданд!.'),
(1091, 7, 137, 'Ва ба он мардуме, ки ба нотавонӣ афтода буданд, шарқу ғарби он сарзаминро, ки баракат дода будем, ба мерос додем ва ваъдаи некӯе, ки Парвардигори ту ба банӣ—Исроил дода буд, ба он сабаб, ки сабр намуда буданд, ба камол расид ва ҳар чиро Фиръавну қавмаш месохтанд ва қасрҳоеро, ки бармеафроштанд, вайрон кардем!'),
(1092, 7, 138, 'Ва банӣ-Исроилро аз дарё гузаронидем. Бар қавме гузаштанд, ки ба парастиши бутҳои худ дил баста буданд. Гуфтанд: «Эй Мӯсо, ҳамон тавр, ки онҳоро худоёнест, барон мо ҳам худое бисоз». Гуфт: «Шумо мардуме бехирад ҳастед.'),
(1093, 7, 139, 'Он чӣ инҳо дар онанд, нобудшаванда ва коре, ки мекунанд, ботил аст».'),
(1094, 7, 140, 'Гуфт: «Оё ҷуз Оллоҳ бароятон худое биҷӯям ва ҳол он ки Ӯст, ки шуморо бар ҷаҳониён бартарӣ бахшид?»'),
(1095, 7, 141, 'Ва шуморо аз оли Фиръавн раҳонидем. Ба азобҳои сахтарин меозурданд, писаронатонро мекуштанд ва занонатонро зинда мегузоштанд ва дар ин аз ҷониби Парвардигоратон озмоише бузург буд.'),
(1096, 7, 142, 'Сӣ шаб бо Мӯсо ваъда ниҳодем ва даҳ шаби дигар бар он афзудем, то ваъдаи Парвардигораш чиҳил шаби комил шуд. Ва Мӯсо ба бародараш Ҳорун гуфт: «Бар қавми ман ҷонишини ман бош ва роҳи салоҳ пеш гир ва ба тариқи фасодкорон марав».'),
(1097, 7, 143, 'Чун Мӯсо ба миъодгоҳи (ваъдагоҳи) мо омад ва Парвардигораш бо ӯ сухан гуфт, гуфт: Эй Парвардигори ман, бинамой, то дар ту назар кунам». Гуфт: Ҳаргиз Маро нахоҳӣ дид. Ба он кӯҳ бингар. Агар бар ҷои худ қарор ёфт, ту низ Маро хоҳӣ дид». Чун Парвардигорзш бао кӯҳ таҷаллӣ кард, кӯҳро ба замин яксон кард ва Мӯсо беҳуш бияфтод. Чун ба хуш омад, гуфт: «Ту покӣ, ба Ту бозгаштам ва ман гахустини мӯъминонам».'),
(1098, 7, 144, 'Гуфт: «Эй Мӯсо, Ман туро ба паёмҳоям ва сухан гуфтанам аз миёни мардум интихобидам, пас он чиро ба ту додаам, фаро гир ва аз сипосгузорон бош!»'),
(1099, 7, 145, 'Барои ӯ дар он алвоҳ ҳар гуна панд ва тафсили ҳар чизро навиштем. Пас гуфтем: «Онро ба нерӯмандӣ бигир ва қавматро бифармоӣ, то ба беҳтарини он амал кунанд, ба зудӣ ҷойгоҳи нофармононро ба шумо нишон диҳам.'),
(1100, 7, 146, 'Он касонеро, ки ба ноҳақ дар замин саркашӣ мекунанд, ба зудӣ аз оёти хеш рӯйгардон созам, чунон ки ҳар оятеро, ки бубинанд, имон наёваранд ва агар тариқи ҳидоят бубинанд, ба он нараванд ва агар тариқи гумроҳӣ бубинанд, бо он бираванд. Зеро онҳо оётро дурӯғ бароварданд ва аз он ғафлат карданд.'),
(1101, 7, 147, 'Ва корҳои онон, ки оёти Мо ва дидори қисматро дурӯғ донистанд, ночиз шуд. Оё ҷуз дар муқобили кирдорашон ҷазо хоҳанд дид?'),
(1102, 7, 148, 'Қавми Мӯсо баъд аз ӯ аз зеварҳояшон шакли гӯсолае сохтанд, ки овоз мекард. Оё намебинанд, ки он гӯсола бо онҳо сухан намегӯяд ва онҳоро ба хеҷ роҳе ҳидоят намекунад?» Онро ба худоӣ гирифтанд ва бар худ ситам карданд.'),
(1103, 7, 149, 'Ва чун аз он кор пушаймон шуданд ва диданд, ки дар гумроҳӣ афтодаанд, гуфтанд: «Агар Парвардигорамон ба мо раҳм накунад ва моро наёмурзад, дар зумраи зиёнкардагон хоҳем буд».'),
(1104, 7, 150, 'Чун Мӯсо хашмгину андӯҳнок назди қавми худ бозгашт, гуфт: «Дар ғайбати ман чӣ бад ҷонишиноне будед. Чаро бар фармони Парвардигори худ шитоб кардед? Ва алвоҳро бар замин афканд ва мӯи бародарашро гирифту ба сӯи худ кашид. Ҳорун гуфт: «Эй писари модарам, ин қавм маро забун ёфтанд ва наздик буд, ки маро бикушанд, душманонро хурсанд макун ва дар шумори ситамкорон маёвар!»'),
(1105, 7, 151, 'Гуфт: «Эй Парвардигори ман, маро ва бародарамро бибахшо ва моро дар раҳмати худ дохил кун, ки Ту меҳрубонтарини меҳрубононӣ!'),
(1106, 7, 152, 'Онон, ки гӯсоларо интихобиданд, ба зудӣ ба ғазаби Парвардигорашон гирифтор хоҳанд шуд ва дар зиндагонии инҷаҳонӣ ба хори хоҳанд афтод. Дурӯғсозонро инчунин ҷазо мадиҳем!'),
(1107, 7, 153, 'Онон, ки содиркунандаи корҳои бад шуданд, он гоҳ тавба карданд ва имон оварданд, бидонанд, ки Парвардигори ту пас аз тавба бахшояндаву меҳрубон аст!'),
(1108, 7, 154, 'Чун хашми Мӯсо фурӯ нишаст, тахтаҳоро баргирифт. Ва дар навиштаҳои он барои онҳое, ки аз Парвардигорашон метарсанд, ҳидоят ва раҳмат буд.'),
(1109, 7, 155, 'Мӯсо барои ваъдагоҳи Мо аз миёни қавмаш ҳафтод мардро бигирифт. Чун зилзила онҳоро фурӯ гирифт, гуфт: «Эй Парвардигори ман, агар мехостӣ онҳоро ва маро пеш аз ин ҳалок мекардӣ. Оё ба хотири корҳое, ки бехирадони мо анҷом додаанд, моро ба ҳалокат мерасонӣ? Ва ин ҷуз имтиҳони Ту нест. Ҳар касро бихоҳӣ, ба он гумроҳ мекунӣ ва ҳар касро бихоҳӣ, ҳидоят. Ту ёвари мо ҳастӣ, моро биёмурз ва бар мо бибахшой, ки Ту беҳтарини бахшояндагонӣ!'),
(1110, 7, 156, 'Барои мо дар дунё ва охират некӣ бинавис! Мо ба сӯи ту бозгаштаем». Гуфт: «Азоби худро ба ҳар кас, ки бихоҳам, мерасонам ва раҳмати ман ҳама чизро дар бар мегирад, Онро барои касоне, ки парҳезгорӣ мекунанд ва закот медиҳанд ва ба оёти Мо имон меоваранд, муқаррар хоҳам дошт,'),
(1111, 7, 157, 'онон, ки аз ин расул, ин паёмбари уммӣ, ки номашро дар Тавроту Инҷили худ навишта меёбанд, пайравӣ мекунанд — он кӣ ба некӣ фармонашон медиҳад ва аз ношоист бозашон медорад ва чизҳои покизаро бар онҳо ҳалол мекунад ва чизҳои нопокро ҳаром ва бори гаронашонро аз дӯшашон бармедорад ва банду занҷирашонро мекушояд. Пас касоне, ки ба ӯ имон оварданд ва ҳурматашро нигоҳ доштанд ва ёриаш карданд ва аз он китоб, ки ба ӯ нозил кардаем, пайравӣ карданд, наҷот ёфтагонанд!»'),
(1112, 7, 158, 'Бигӯ: «Эй мардум, ман фиристодаи Худо бар ҳамаи шумо ҳастам. Он Худое, ки фармонравоии осмонҳову замин аз они Ӯст ва ҳеҷ худое ғайри Ӯ нест. Зинда мекунад ва мемиронад. Пас ба Худо ва расули Ӯ — он паёмбари уммӣ, ки ба Худову калимоти Ӯ имон дорад, имон биёваред ва аз Ӯ пайравӣ кунед, бошад, ки ҳидоят шавед!»'),
(1113, 7, 159, 'Гурӯҳе аз қавми Мӯсо ҳастанд, ки мардумро ба хақ роҳ менамоянд ва ба адолат рафтор мекунанд.'),
(1114, 7, 160, 'Бакӣ-Исроилро ба дувоздаҳ сибт таисим кардем. Ва чун қавми Мӯсо аз ӯ об хостанд, ба ӯ ваҳй кардем, ки асоятро бар санг бизан. Аз он санг дувоздаҳ чашма равон шуд. Ва ҳар гурӯҳ ҷои оби хешро бишинохт. Ва абрро соябонашон сохтем ва барояшон «манна ва салво» нозил кардем. Бихӯред аз ин чизҳои покиза, ки бар шумо рӯзӣ додаем! Ва онон ба мо ситам накарданд, балки ба худашон ситам мекарданд.'),
(1115, 7, 161, 'Ва ба онон гуфта шуд, ки дар ин деҳа сукунат кунед ва ҳар ҷо ҳар чӣ хоҳед, бихӯред ва бигӯед, ки гуноҳони моро бирез ва саҷдакунон аз дарвоза дохил шавед, то гуноҳонатонро биёмурзем. Ба мукофоти некӯкорон хоҳем афзуд.'),
(1116, 7, 162, 'Аз миёни онон он гурӯҳ, ки бар худ ситам карда буданд, суханеро, ки ба онҳо гуфта шуда буд, дигар карданд. Пас ба ҷазои ситаме, ки мекарданд, барояшон аз осмон азоб фиристодем.'),
(1117, 7, 163, 'Дар бораи он деҳаи наздик ба даре аз онҳо Бипурс. Он гоҳ ки дар рӯзи шанбе суннат мешикастанд. Зеро дар рӯзе, ки шанбе мекарданд, моҳиён ошкор бар рӯи об меомаданд ва рӯзе, ки шанбе намекарданд, намеомаданд. Инҳоро, ки мардуме нофармон буданд, инчунин меозмудем!'),
(1118, 7, 164, 'Ва он гоҳ ки гурӯҳе аз онҳо гуфтанд; «Чаро қавмеро панд медиҳед, ки Худо ҳалокашон хоҳад кард ва ба азобе дарднок мубтало хоҳад сохт?» Гуфтанд: «То моро назди Парвардигоратон узре бошад. Ва шояд, ки парҳезгор шаванд».'),
(1119, 7, 165, 'Чун пандеро, ки ба онҳо дода шуда буд, фаромӯш карданд, ононро, ки аз бадӣ парҳез мекарданд, наҷот додем ва гунаҳкоронро ба сабаби гуноҳашон ба азобе сахт фурӯ гирифтем»'),
(1120, 7, 166, 'Ва чун аз тарки чизе, ки аз он манъашон: карда буданд, сарпечӣ карданд, гуфтем: «Бӯзинагоне хоршуда шавед!»'),
(1121, 7, 167, 'Ва Парвардигори ту эълом кард, ки касеро бар онон бифиристад, ки то ба рӯзи қиёмат ба азобе ногувор азобашон гардонад. Албатта Парвардигори ту зуд уқубат мекунад ва низ омурзандаву меҳрубон аст!'),
(1122, 7, 168, 'Ононро гурӯҳ-гурӯҳ дар замин тақсим кардем, баъзе некӯкор ва баъзе ғайри он-Ва ба некиҳову бадиҳо озмудем, шояд бозгарданд.'),
(1123, 7, 169, 'Баъд аз инҳо гурӯҳе ба ҷояшон нишастанд ва вориси он китоб шуданд, ки ба матоъи дунявӣ дил бастанд ва гуфтанд, ки ба зудӣ омурзида мешавем. Ва агар монанди он боз ҳам матоъо биёбанд, баргиранд. Оё аз онҳо паймон нагирифтаанд, ки дар бораи Худо ҷуз ба ростӣ сухан нагӯянд, ҳол он ки он чӣ дар-он китоб омада буд, хонда буданд? Сарое охират барои касоне, ки мепарҳезанд, беҳтар аст. Оё ақл намекунед?'),
(1124, 7, 170, 'Онҳое, ки ба китоби Худо пойбандӣ меҷӯянд ва намоз мегузоранд, бидонанд, ки подоши некӯкоронро бекор намесозем.'),
(1125, 7, 171, 'Ва кӯҳро бар болои сарашон чун соябоне нигоҳ доштем ва мепиндоштанд, ки акнун бар сарашон хоҳад афтод. Китоберо, ки ба шумо додаем, бо нерӯмандӣ бигиред ва ҳар чиро, ки дар он омадааст, ба ёд доред, шояд, ки парҳезгор шавед!'),
(1126, 7, 172, 'Ва Парвардигори ту аз пушти банӣ-Одам фарзандонашро берун овард. Ва ононро бар худашон гувоҳ гирифт ва пурсид: «Оё Ман Парвардигоратон нестам?» Гуфтанд: «Оре, гувоҳӣ медиҳем». То дар рӯзи қиёмат нагӯед, ки мо аз он бехабар будем.'),
(1127, 7, 173, 'Ё нагӯед, ки падарони мо пеш аз ин мушрик буданд ва мо насле будем баъд аз онҳо ва оё ба сабаби коре, ки гумроҳон карда буданд, моро ба ҳалокат мерасонӣ?'),
(1128, 7, 174, 'Оятҳоро инчунин ба равшанӣ баён мекунем, шояд бозгарданд!'),
(1129, 7, 175, 'Хабари он мардро бар онҳо бихон, ки оёти хешро ба ӯ ато карда будем ва ӯ аз он илм маҳрум гашт ва шайтон дар паяш афтод ва дар зумраи гумроҳон даромад.'),
(1130, 7, 176, 'Агар хоста будем, ба сабаби он илм, ки ба ӯ дода будем, мартабаи баландаш мебахшидем, вале ӯ дар замин бимонд ва аз паи ҳавои хеш рафт. Сифати ӯ чун сифати он саг аст, ки агар ба ӯ ҳамла кунӣ, забон аз даҳон берун орад ва агар раҳояш кунӣ, боз ҳам забон аз даҳон берун орад. Мисоли он, он, ки оётро дурӯғ шумориданд, низ чунин аст. Қиссаро бигӯй, шояд ба андеша фурӯ раванд!'),
(1131, 7, 177, 'Бад аст мисоли мардуме, ки оёти Моро дурӯғ шумориданд ва ба худ ситам раво медоштанд!'),
(1132, 7, 178, 'Ҳар касро, ки Худо роҳ намояд, роҳи хеш биёбад. Ва ононро, ки гумроҳ созад, зиёнкор шаванд.'),
(1133, 7, 179, 'Барои ҷаҳаннам бисёре аз ҷинну инсро биёфаридем. Онҳоро дилҳоест, ки ба он намефаҳманд ва чашмҳоест, ки ба он намебинанд ва гушҳоест, ки ба он намешунаванд. Инҳо ҳамонанди чорпоёнанд, ҳатто гумроҳтар аз онҳоянд. Инҳо худ ғофилонанд.'),
(1134, 7, 180, 'Аз они Худованд аст некӯтарин номҳо. Ба он номҳояш бихонед. Онҳоро, ки ба номҳои Худо каҷравӣ меварзанд, бигузоред. Инҳо ба ҷазои аъмоли худ хоҳанд расид.'),
(1135, 7, 181, 'Аз офаридагони мо гурӯҳе ҳастанд, ки ба ҳақ роҳ менамоянд ва ба адолат рафтор мекунанд.'),
(1136, 7, 182, 'Ва ононро, ки оёти моро дурӯғ шумориданд, аз роҳе, ки худ намедонанд, ба тадриҷ хорашон месозем.'),
(1137, 7, 183, 'Ва ба онҳо мӯҳлат диҳам, ки тадбири Ман устувор аст.'),
(1138, 7, 184, 'Оё фикр накардаанд, ки дар ёрашон нишоне аз девонагӣ нест ва Ӯ ба ошкоро тарсонанда аст?'),
(1139, 7, 185, 'Оё дар мулки осмонҳову замин ва чизҳое, ки Худо офаридааст, намеандешанд? Ва шояд, ки маргашон наздик бошад. Ва баъд аз Қуръон кадом суханро бовар доранд?'),
(1140, 7, 186, 'Ҳар касро, ки Худо гумроҳ кунад, ҳеҷ роҳнамое барояш нест. Ва ононро вомегузорад, то ҳамчунон дар саркашии хеш саргардон бимонанд.'),
(1141, 7, 187, 'Дар бораи қиёмат аз ту мепурсанд, ки чӣ вақт фаро мерасад. Бигӯ: «Илми он назди Парвардигори ман аст. Танҳо Ӯст, ки чун замонаш фаро расад, ошкораш месозад. Фаро расидани он бар осмониёну заминиён пӯшида аст, ба ногаҳон бар шумо ояд», Чунон аз ту мепурсанд, ки гӯӣ ту аз он огоҳӣ. Бигӯ: «Илми он назди Худост, вале бештари мардум намедонанд».'),
(1142, 7, 188, 'Бигӯ; «Ман молики нафъу зиёни худ нестам. Ғайри он чӣ Худо бихоҳад. Ва агар илми ғайб медонистам, ба хайри худ хеле меафзудам ва ҳеҷ шарре ба ман намерасид. Ман касе ҷуз бимдиҳанда ва муждадиҳандае барои мӯъминон нестам».'),
(1143, 7, 189, 'Ӯст, ки ҳамаи шуморо аз як тан биефарид. Ва аз он як тан занашро низ биёфарид то ба ӯ оромиш ёбад. Чун бо ӯ якҷоя шуд, ба боре еабук ҳомила шуд ва муддате бо он гузаронд. Ва чун бор сангин гардид, он ду Оллоҳ Парвардигори худро бихонданд, ки агар моро фарзанде солеҳ диҳӣ, аз свпосгузорон хоҳем буд!'),
(1144, 7, 190, 'Чун Худо он дуро фарзанде солеҳ дод, барои Ӯ дар он чӣ ба онҳо ато карда буд, шариконе қоил шуданд, ҳол он ки Худо аз ҳар чӣ бо Ӯ шарик месозанд, болотар аст.'),
(1145, 7, 191, 'Оё шарики Худо месозанд чизҳоеро, ки наметавонанд ҳеҷ чиз биёфаринанд ва худ махлуқ ҳастанд?'),
(1146, 7, 192, 'На метавонанд ёрияшон кунанд ва на метавонанд ба ёрии худ бархезанд.'),
(1147, 7, 193, 'Агар онҳоро ба роҳи ҳидоят бихонӣ, ба шумо пайравӣ нахоҳанд кард. Бароятон яксон аст, чӣ даъваташон кунед ва чӣ хомуш бошед.'),
(1148, 7, 194, 'Онҳое, ки ҷуз Оллоҳ ба худоӣ мехонед, бандагоне чун шумоянд. Агар рост мегӯед, онҳоро бихонед, бояд шуморо иҷобат кунанд!'),
(1149, 7, 195, 'Оё онҳоро поҳое ҳаст, ки бо он роҳ бираванд ё онҳоро дастҳое ҳаст, ки бо он ҳамла кунанд ё чашмҳое ҳаст, ки бо он бубинанд ё гушҳое ҳаст, ки бо он бишнаванд? Бигӯ: «Шариконатро бихонед ва бар зидди ман тадбир кунед ва маро мӯҳлат надиҳед.'),
(1150, 7, 196, 'Ёвари ман Оллоҳ аст, ки ин китобро нозил карда ва Ӯ дӯсти шоистагон аст.'),
(1151, 7, 197, 'Ононро, ки ба ҷои Оллоҳ ба худоӣ мехонед, на шуморо метавонанд ёрӣ кунанд ва на худро.'),
(1152, 7, 198, 'Ва агар онҳоро ба роҳи ҳидоят бихонӣ, намешунаванд ва мебинӣ, ки ба ту менигаранд, вале гӯӣ, ки намебинанд.'),
(1153, 7, 199, 'Афвро пеша кун ва ба некӣ фармон деҳ ва аз ҷоҳилон рӯй гардон!'),
(1154, 7, 200, 'Ва агар аз ҷониби шайтон дар ту васвасае падид омад, ба Худо паноҳ бибар, зеро Ӯ шунаво ва доност!'),
(1155, 7, 201, 'Касоне, ки парҳезгорӣ мекунанд, чун аз шайтон васвасае ба онҳо бирасад, Худоро ёд мекунанд ва ногаҳон бино шаванд.'),
(1156, 7, 202, 'Ва бародаронашон онҳоро ба гумроҳӣ мекашанд ва аз амали хеш бознамеистанд.'),
(1157, 7, 203, 'Чун ояе барояшон наоварӣ гуянд: «Чаро аз худ чизе намегӯӣ?» Бигӯ: «Ман пайрави чизе ҳастам, ки аз Парвардигорам ба ман ваҳй мешавад. Ва ин ҳуҷҷатҳоест аз ҷониби Парвардигоратон ва раҳнамуду раҳмат аст барои мардуме, ки имон меоваранд.'),
(1158, 7, 204, 'Чун Қуръон хонда шавад, ба он гӯш фаро диҳед ва хомӯш бошед, шояд шомили раҳмати Худо шавед!'),
(1159, 7, 205, 'Парвардигоратро дар дили худ ба зориву тарс бе он, ки садои худ баланд кунӣ, ҳар субҳу шом ёд кун ва аз ғофилон мабош!'),
(1160, 7, 206, 'Албатта онон, ки дар назди Парвардигори ту ҳастанд, аз парастиши Ӯ сар наметобанд ва тасбеҳаш мегӯянд ва барояш саҷда мекунанд! Сачдаи тиловат.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Туро аз ғаноими (ғаниматҳои) ҷангӣ мепурсанд, бигӯ: «Ғаноими ҷангӣ азони Худову паёмбар аст. Пас агар аз мӯъминон ҳастед, аз Худой битарсед ва бо якдигар ба оштӣ зист кунед ва аз Худову паёмбараш фармон баред!»'),
(1162, 8, 2, 'Мӯъминон касоне ҳастанд, ки чун номи Худо бурда шавад, хавф дилҳошонро фаро гирад ва чун оёти Худо бар онон хонда шавад, имонашон афзун гардад ва бар Парвардигорашон таваккал мекунанд,'),
(1163, 8, 3, 'ҳамон касон, ки намоз мегузоранд ва аз он чӣ рӯзиашон додаем, садақа мекунанд.'),
(1164, 8, 4, 'Инҳо мӯьминони ҳақиқи ҳастанд. Дар назди Парвардигорашон соҳиби дараҷотанд ва мағрифату ризқе некӯ.'),
(1165, 8, 5, 'Ончунон буд, ки Парвардигорат туро аз хонаат ба ҳақ берун овард, ҳол он ки гурӯҳе аз мӯъминон нохушнуд буданд.'),
(1166, 8, 6, 'Бо он ки ҳақиқат бар онҳо ошкор шуда дар бораи он бо ту муҷодала мекунанд, чунон қадам бармедоранд, ки гуё мебинанд, ки онҳоро ба сӯӣ марг мебаранд.'),
(1167, 8, 7, 'Ва ба ёд ор он гоҳро, ки Худо ба шумо ваъда дод, ки яке аз он ду гурӯҳ ба дасти шумо афтад ва дӯст доштед, ки он гурӯҳ ки холӣ аз қудрат аст, ба дас ти шумо афтад, ҳол он ки Худо мехост ба суханони уд ҳақро бар ҷои худ нишонад ва решаи кофиронро қатъ кунад,'),
(1168, 8, 8, 'ҳақро собит ва ноҳақро ночиз гардонад, ҳарчанд гуноҳкорон нохушнуд бошанд.'),
(1169, 8, 9, 'Ва он гоҳ ки аз Парвардигоратон ёрӣ хостед ва Худо қабул кард, ки ман бо ҳазор фаришта, ки аз паи якдигар меоянд, ёриятон мекунам!'),
(1170, 8, 10, 'Ва он корро Худо ҷуз барои шодмонии шумо накард ва то дилҳоятон ба он ором гирад. Ва ёрӣ танҳо аз сӯӣ Худост, ки Ӯ пирӯзманду ҳаким аст!'),
(1171, 8, 11, 'Ва ба ёд ор он гоҳро, ки Худо чунон эминиатон дода буд, ки хобе сабук шуморо фурӯ гирифт ва аз осмон бароятон бороне борид, то шустушӯятон диҳад на васвасаи шайтонро аз шумо дур кунад ва дилҳоятонро қавӣ гардонад ва қадамҳоятонро устувор созад.'),
(1172, 8, 12, 'Ва он гоҳро, ки Парвардигорат ба фариштагон ваҳй кард: «Ман бо шумоям Шумо мӯъминонро ба пойдорӣ водоред. Ман дар дилҳои кофирон бим хоҳам андохт. Бар гарданҳояшон бизанед ва ангуштонашонро қатъ кунед!»'),
(1173, 8, 13, 'Зеро бо Худову паёмбараш ба мухолифат бархостанд. Ва ҳар ки бо Худову паёмбараш мухолифат варзад, бидонад, ки уқубати Худо сахт аст!'),
(1174, 8, 14, 'Уқубатро бичашед ва азоби оташ аз они кофирон аст!'),
(1175, 8, 15, 'Эй касоне, ки имон овардаед, чун кофиронро ҳамлавар дидед, ба гурез пушт накунед;'),
(1176, 8, 16, 'ҷуз онҳо, ки барои ҳамлае бозмегарданд ё онҳо, ки ба ёрии гурӯҳе дигар мераванд, ҳар кас, ки пушт ба душман кунад, мавриди хашми Худо қарор ме-гирад ва ҷойгоҳи ӯ ҷаҳаннам аст ва ҷаҳаннам бад ҷойгоҳест!'),
(1177, 8, 17, 'Шумо ононро намекуштед. Худо буд, ки онҳоро мекушт. Ва он гоҳ, ки тир меандохтӣ, ту тир намеандохтӣ, Худо буд, ки тир меандохт, то ба мӯъминон неъмате неку арзонӣ дорад, Албатта\' Худо шунавову доност!'),
(1178, 8, 18, 'Инчунин буд! Ва Худо сусткунандаи ҳилаи кофирон аст.'),
(1179, 8, 19, 'Агар пирӯзӣ хоҳед, пирӯзиятон фаро расид ва агар аз куфр бозистед, бароятон беҳтар аст ва агар бозгардед, бозмегардем ва сипоҳи шумо ҳарчанд фаровон бошад, бароятон нафъе нахоҳад дошт, ки Ху до бо мӯъминон аст.'),
(1180, 8, 20, 'Эй касоне, ки имон овардаед ба Худову паёмбараш итоъат кунед ва дар ҳоле, ки сухани Ӯро мешунавед, аз Ӯ рӯй бармагардонед.'),
(1181, 8, 21, 'Ва аз онон мабошед, ки гуфтанд, ки шунидем, дар ҳоле, ки намешуниданд.'),
(1182, 8, 22, 'Бадтарини ҷонварон дар назди Худо ин карону гунгон ҳастанд, ки дарнамеёбанд.'),
(1183, 8, 23, 'Ва агар Худо хайре дар онон меёфт, шунавояшон месохт ва агар ҳам ононро шунаво сохта буд, боз ҳам бармегаштанд ва рӯйгардон мешуданд.'),
(1184, 8, 24, 'Эй касоне, ки имон овардаед, чун Худову паёмбараш шуморо ба чизе даъват кунанд, ки зиндагиятон мебахшад, даъваташонро қабул кунед ва бидонед, ки Худо миёни одамӣ ва қалбаш ҳоил аст ва ҳама ба пешгоҳи Ӯ гирд оварда шавед!'),
(1185, 8, 25, 'Ва битарсед аз фитнае, ки танҳо ситамкоронатонро дар бар нахоҳад гирифт ва бидонед, ки Худо ба сахтӣ уқубат мекунад!'),
(1186, 8, 26, 'Ва ба ёд оваред он ҳангомро, ки андак будед ва д а р шумори хоршудагони ин сарзамин бими он доштед, ки мардум шуморо аз миён бардоранд ва Ху до паноҳатон дод ва ёрӣ кард ва пирӯз гардонид ва аз чизҳои покиза рӯзи дод, ки шояд шукр гӯед!'),
(1187, 8, 27, 'Эй касоне, ки имон овардаед, медонед, ки набояд ба Худову паёмбар хиёнат кунед ва дар амонат набояд хиёнат кунед.'),
(1188, 8, 28, 'Бидонед, ки молҳоятон ва фарзандонатон василаи озмоиши шумоянд ва музди бузург дар назди Худост.'),
(1189, 8, 29, 'Эй касоне, ки имон овардаед, агар аз Худо би тарсед, шуморо биноии шинохти ҳақ аз ботил диҳад ва гуноҳонатонро нест кунад ва шуморо биёмурзад, ки соҳиби фазлу караме бузург аст!'),
(1190, 8, 30, 'Ва он ҳангомро ба ёд овар, ки кофирон дар бораи ту макре карданд, то дар бандат афкананд ё бикушандат ё аз шаҳр берун созанд. Онон макр кар данд ва Худо низ макр кард ва Худо беҳтарини макркунандагон аст.'),
(1191, 8, 31, 'Чун оёти Мо бар онҳо хонда шуд, гуфтанд: «Шунидем. Ва агар бихоҳед монанди он мегӯем, ки ин чизе ғайри афсонаҳои пешиниён нест!»'),
(1192, 8, 32, 'Ва он ҳангомро, ки гуфтанд: «Бор Худое, агар ин ки аз ҷониби ту омада ҳақ аст, бар мо аз осмон бороне аз санг бибор ё азоби дардоваре бар мо бифирист».'),
(1193, 8, 33, 'З То он гоҳ, ки ту дар миёнашон ҳастӣ. Худо азобашон накунад ва то он гоҳ, ки аз Худо омурзиш металабанд, низ Худо азобашон нахоҳад кард.'),
(1194, 8, 34, 'Чаро Худо азобашон накунад, ҳол он ки мардумро аз Масҷидулҳаром бозмедоранд ва соҳибони он нестанд? Соҳибони он танҳо парҳезгоронанд, ва ле бештаринашон намедонанд!'),
(1195, 8, 35, 'Ва дуъояшон дар назди хонаи Каъба ҷуз хуштак кашидан ва даст задан ҳеҷ набуд. Пас ба ҷазои ин коратон азобро бичашед!'),
(1196, 8, 36, 'Кофирон амволашонро харҷ мекунанд, то мардумро аз роҳи Худо боздоранд. Амволашонро харҷ хоҳанд кард ва ҳасрат хоҳанд бурд, сипас мағлуб мешаванд. Ва кофиронро дар ҷаҳаннам гирд меоваранд!'),
(1197, 8, 37, 'То Худо нопокро аз пок ҷудо кунад ва нопоконро барҳам занад. Он гоҳ ҳамаро гирд кунад ва ба ҷаҳаннам партояд. Инҳо зиёнкоронанд!'),
(1198, 8, 38, 'Бо кофирон бигӯй, ки агар даст бардоранд, гуноҳони гузаштаи онҳо бахшида шавад ва агар бозгарданд, донанд, ки бо пешиниён чӣ рафторе шудааст.'),
(1199, 8, 39, 'Бо онон набард кунед, то дигар фитнае набошад ва дини ҳама дини Худо гардад. Пас агар бозистоданд, Худо кирдорашонро мебинад.'),
(1200, 8, 40, 'Ва агар рӯй гардонанд, бидонед. ки Худо мавлои шумост, Ӯ мавло ва ёридиҳандаи некӯст!'),
(1201, 8, 41, 'Ва агар ба Худо ва он чӣ бар бандаи худ дар рӯзи фурқон, ки ду гурӯҳ ба ҳам расиданд, нозил кардаем, имон овардаед, бидонед, ки ҳар гоҳ чизе ба ғанимат гирифтед, хумси (панҷяки) он аз они Худову паёмбар ва хешовандону ятимон ва мискинону дар роҳ мондагон аст. Ва Худо ба ҳар чизе тавоност!'),
(1202, 8, 42, 'Шумо дар каронаи наздиктари биёбон будед ва онҳо дар каронаи дуртар буданд ва он қофила дар маконе пасттар аз шумо буд. Агар шумо бо якдигар замони ҷангро таъйин мекардед, боз ҳам аз он хилоф меварзидед, то коре, ки Худо муқаррар кардааст, воқеъ шавад, то ҳар кӣ ҳалок мешавад, ба далеле ҳалок шавад ва ҳар кӣ зинда мемонад, ба далеле зинда монад. Албатта Худо шунавову доност!'),
(1203, 8, 43, 'Дар хоб Худо шуморашонро ба ту андак нишон дод. Агар шумори онҳоро бисёр нишон дода буд, аз тарс нотавон мешудед ва дар тасмим ба ҷанг ба низоъ бармехостед, вале Худованд шуморо аз душманон дар амон дошт, ки Ӯ ба он чӣ дар дилҳост, огоҳ аст!'),
(1204, 8, 44, 'Ва он гоҳ, ки чун ба ҳам расидед, ононро дар чашми шумо андак намуд ва шуморо низ дар чашми онон андак, то он коре, ки муқаррар дошта буд, воқеъ гардад. Ва ҳамаи корҳо ба Худо бармегардад!'),
(1205, 8, 45, 'Эй касоне, ки имон овардаед, агар ба гурӯҳе аз душман бархӯрдед, пойдорӣ кунед ва Худоро фаровон ёд кунед, бошад, ки ғолиб шавед.'),
(1206, 8, 46, 'Ба Худову паёмбараш итоъат кунед ва бо якдигар ба низоъ бармахезед, ки нотавон шавед ва қуввати шумо биравад. Сабр пеша кунед, ки Худо ҳамроҳи собирон аст!'),
(1207, 8, 47, 'Монанди он касон мабошед, ки сармасти ғурур ва барон худнамоӣ аз диёри худ берун омаданд ва дигаронро аз роҳи Худо боздоштанд. Ва Худо ба ҳар коре, ки мекунанд, (бо илми худ) иҳота дорад.'),
(1208, 8, 48, 'Шайтон кирдорашонро дар пазарашон биёрост ва гуфт; «Имрӯз аз мардум касе бар шумо ғолиб намешавад ва ман паноҳи шумоям». Вале чун ду гурӯҳ рӯбарӯ шуданд, ӯ бозгашту гуфт: «Ман аз шу мо безорам, ки чизҳое мебинам, ки шумо намебинед, ман аз Худо метарсам, ки Ӯ ба сахтӣ уқубат мекунад!»'),
(1209, 8, 49, 'Мунофиқон ва он касоне, ки дар дил беморие доранд, гуфтанд: «Инҳоро динашон фирефта аст». Ва ҳар кас, ки ба Худо таваккул кунад, ӯро пирӯзманд ва ҳаким хоҳад ёфт!'),
(1210, 8, 50, 'Агар бубинӣ он замонро, ки фариштагон ҷони кофиронро меситонанд ва ба рӯйҳову пушташон мезананд ва мегӯянд: «Азоби сӯзонро бичашед!»'),
(1211, 8, 51, 'Ин ба ҷазои корҳое буд, ки пеш аз ин карда будед ва Худо ба бандагонаш ситам кунанда нест,'),
(1212, 8, 52, 'Монанди амали хонадони Фиръавн ва касоне, ки пеш аз онҳо буданд» Онҳо ба оёти Худо кофир шуданд. Пас Худо онҳоро ба ҷазои гуноҳонашон азоб кард, ки Худо нерӯманд ва сахтуқубат аст!'),
(1213, 8, 53, 'Зеро Худо неъматеро, ки ба қавме арзонӣ дошта аст, дигаргун насозад, то он қавм худ дигаргун шаванд. Ва Худо шунаво ва доност!'),
(1214, 8, 54, 'Монанди амали хонадони Фиръавн ва касоне, ки пеш аз онҳо буданд. Онҳо оёти Парвардигорашонро дурӯғ донистанд ва Мо ба ҷазои гуноҳонашон ҳалокашон кардем ва хонадони Фиръавнро ғарқ сохтем, зеро ҳама ситамкор буданд.'),
(1215, 8, 55, 'Албатта бадтарин ҷунбандагон дар назди Худо онҳоянд, ки кофир шудаанд ва имон намеоваранд.'),
(1216, 8, 56, 'Касоне, ки ту аз онон паймон гирифтӣ, сипас паёпай паймони хешро мешикананд ва ҳеҷ наметарсанд.'),
(1217, 8, 57, 'Пас агар онҳоро дар ҷанг биёбӣ, парокандаашон соз, то пайравонашон низ пароканда шаванд, шояд ки ибрат гиранд.'),
(1218, 8, 58, 'Агар медонӣ, ки гурӯҳе дар паймон хиёнат мекунанд, ба онон эълом кун, ки монанди худашон амал хоҳӣ кард. Зеро Худо хоинонро дӯст надорад!'),
(1219, 8, 59, 'Онон, ки кофир шудаанд, напиндоранд, ки растаанд, зеро Худоро нотавон намесозанд.'),
(1220, 8, 60, 'Ва дар баробари онҳо то метавонед нерӯ ва аспони саворӣ омода кунед, то душманони Худо ва душманони худ ва ғайри онҳоро, ки шумо намешиносед ва Худо мешиносад битарсонед. Ва он чиро, ки дар роҳи Худо харҷ мекунед, ба тамомӣ ба шумо бозгардонда шавад ва ба шумо ситам нашавад.'),
(1221, 8, 61, 'Ва агар ба сулҳ майл кунанд, ту низ ба сулҳ майл кун. Ва бар Худо таваккал кун, ки Ӯст шунаво ва доно!'),
(1222, 8, 62, 'Ва агар хостанд, ки туро бифиребанд, Худо ба рои ту кофист. Ӯст, ки туро ба ёрии худ ва ёрии мӯъминон қувват додааст.'),
(1223, 8, 63, 'Дилҳояшонро ба якдигар меҳрубон сохт. Агар ту ҳамаи он чиро, ки дар рӯи замин аст, харҷ мекарди, дилҳои онҳоро ба якдигар меҳрубон намесохтӣ, Вале Худо дилҳояшонро ба якдигар меҳрубон сохт, ки Ӯ пирузманду ҳаким аст!'),
(1224, 8, 64, 'Эй паёмбар, Худо ва мӯъминоне, ки аз ту пайравӣ мекунанд, туро басанд.'),
(1225, 8, 65, 'Эй паёмбар, мӯъминонро ба ҷанг барангез. Агар аз шумо бист тан бошанд ва дар ҷанг пойдорӣ кунанд, бар дусад тан ғалаба хоҳанд ёфт. Ва агар сад тан бошанд, ба ҳазор тан аз кофирон пирӯз мешаванд. Зеро онон мардуме маҳрум аз фаҳманд.'),
(1226, 8, 66, 'Акнун Худо коратонро сабук кард ва аз нотавониятон огоҳ шуд. Агар аз шумо сад тан бошанд ва дар ҷанг сабр кунанд. бар дусад тан пирӯз мешаванд. Ва агар аз шумо ҳазор тан бошанд, ба ёрии Худо бар ду ҳазор тан пирӯз мешаванд. Ва Худо бо онҳост, ки сабр мекунанд!'),
(1227, 8, 67, 'Барои ҳеҷ паёмбаре насазад* ки асирон дошта бошад, то ки дар рӯи замин куштори бисёр кунад. Шумо матоъи инҷаҳониро мехоҳед ва Худо охиратро мехоҳад. Ва Ӯ пирӯзманду ҳаким аст!'),
(1228, 8, 68, 'Агар пеш аз ин аз ҷониби Худо ҳукме нашуда буд, ба сабаби он чӣ гирифта будед, азобе бузург ба шумо мерасид.'),
(1229, 8, 69, 'Аз он чӣ ба ғанимат гирифтаед, ки ҳалол асту покиза, бихӯрел. Ва аз Худо битарсед. Албатта Худо бахшандаву меҳрубон аст!'),
(1230, 8, 70, 'Эй паёмбар, ба асироне, ки дар дасти шумо ҳастанд, бигӯ: «Агар Худо дар дилҳоятон нишони имон бубинад, беҳтар аз он чӣ аз шумо гирифта шуда, ба шумо хоҳад дод на шуморо мебахшад ва Худо бахшандаву меҳрубон аст!»'),
(1231, 8, 71, 'Агар қасди хиёнат ба туро дошта бошанд, пеш аа ин ба Худо хиёнат намудаанд ва Худо туро бар онҳо нусрат додааст, ки донову ҳаким аст!'),
(1232, 8, 72, 'Онон, ки имон овардаанд ва муҳоҷират кардаанд ва бо молу ҷони хеш дар роҳи Худо ҷиҳод кардаанд ва онон, ки ба муҳоҷирон ҷой дода ва ёриашон кардаанд, ёру дӯсти якдигаранд. Ва онон, ки имон овардаанд, ва муҳоҷират накардаанд, ёру дӯсти шумо нестанд, то он гоҳ ки муҳоҷират кунанд. Вале агар шуморо ба ёрӣ талабиданд, бояд ба ёриашон бархезед. ғайри он, ки бар зидди он гурӯҳе бошад, ки миёни шумо ва онҳо паймоне баста шуда бошад. Ва Худо ба корҳое, ки мекунед, биност!'),
(1233, 8, 73, 'Кофирон низ ёру дӯсти якдигаранд. Агар риояи он накунед, фитнаву фасоде бузург дар он сарзамин падид хоҳад омад.'),
(1234, 8, 74, 'Онон, ки имон овардаанд, ва муҳочират карда анд ва дар роҳи Худо ҷиҳод кардаанд ва онон, ки ҷояшон додаанд ва ёриашон кардаанд, ба ҳақиқат мӯъминонанд, бахшоиш ва рӯзии некӯ аз они онҳост.'),
(1235, 8, 75, 'Ва касоне, ки баъдан имон овардаанд ва муҳоҷират кардаанд ва ҳамроҳи шумо ҷиҳод кардаанд, аз шумо ҳастанд. Ба ҳукми китоби Худо хешовандон ба якдигар сазовортаранд. Ва Худо бар ҳар чизе доност!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Худову паёмбараш безоранд аз мушриконе, ки бо онҳо паймон бастаед.'),
(1237, 9, 2, 'Пас чаҳор моҳ ба шумо мӯҳлат дода шуд, ки дар ин сарзамин сайр кунед ва бидонед, ки аз Худо натавонед гурехт ва Ӯст, ки кофиронро расво месозад!'),
(1238, 9, 3, 'Дар рӯзи ҳаҷҷи бузург аз ҷониби Худову паёмбараш ба мардум эълом мешавад, ки Худову паёмбараш аз мушрикон безоранд. Пас агар тавба кунед, бароятон беҳтар аст. Вале агар сарпечӣ кунед, бидонед, ки аз Худо натавонед гурехт. Ва кофиронро ба азобе дардовар хабар деҳ.'),
(1239, 9, 4, 'Ғайри он гурӯҳ аз мушрикон, ки бо онҳо паймон бастаед ва дар паймони худ камбудие наёвардаанд ва бо хеҷ кас бар зидди шумо ҳамдаст нашудаанд. Бо инҳо ба паймони хеш то поёни муддаташ вафо кунед, зеро Худо парҳезгоронро дӯст дорад!'),
(1240, 9, 5, 'Ва чун моҳҳои ҳаром ба поён расид, ҳар ҷо, ки мушриконро ёфтед, бикушед ва бигиреду ба ҳабс афканед ва дар ҳама ҷо ба каминашон нишинед. Аммо агар тавба карданд ва намоз хонданд ва закот доданд, аз онҳо даст бардоред, зеро Худо бахшандаву меҳрубон аст!'),
(1241, 9, 6, 'Ва ҳар гоҳ яке аз мушрикон ба ту паноҳ овард, паноҳаш деҳ то каломи Худоро бишнавад, сипас ба макони амнаш бирасон, зеро инҳо мардуме нодонанд.'),
(1242, 9, 7, 'Чӣ гуна мушриконро бо Худо ва паёмбари Ӯ паймоне бошад? Ғайри онҳое, ки назди Масҷидулҳаром бо онҳо паймон бастед. Агар бар сари паймонашон истоданд, бар сари паймонатон биистед. Худо парҳезгоронро дӯст дорад!'),
(1243, 9, 8, 'Чӣ гуна паймоне бошад, ки агар бар шумо пирӯз шаванд, ба ҳеҷ аҳду савганд ва хешовандӣ вафо накунанд? Бо забоп хушнудатон месозанд ва дар дил сар мепечанд ва бештарин фосиқонанд.'),
(1244, 9, 9, 'Оёти Худоро ба баҳои андак фурӯхтанд ва мардумро аз роҳи Худо боздоштанд ва бадкорӣ карданд.'),
(1245, 9, 10, 'Аҳду савганд ва хешовандии ҳеҷ мӯъминеро риоат намекунанд ва мардуме таҷовузкоранд.'),
(1246, 9, 11, 'Пас агар тавба карданд ва намоз ба ҷой оварданд ва закот доданд, бародарони динии шумо ҳастанд. Мо оёти Худоро барои мардуме доно тафсил мекунем.'),
(1247, 9, 12, 'Агар пас аз бастани паймон савганди худ шикастанд ва дар дини шумо таън заданд, бо пешвоёни куфр қитол кунед, ки онҳоро расми савганд нигоҳ доштан нест, бошад, ки аз кирдори худ бозистанд.'),
(1248, 9, 13, 'Оё бо мардуме, ки еавганди худро шикастанд ва оҳанги бадарғаи паёмбар карданд ва онҳо бар зидди шумо душманӣ оғоз карданд, намеҷангед? Оё аз онҳо метарсед ва ҳол он ки агар имон оварда бошед, сазовортар аст, ки аз Худо битарседу бас.'),
(1249, 9, 14, 'Бо онҳо биҷангед. Худо ба дасти шумо азобашон мекунад ва хорашон месозад ва шуморо пирӯзӣ медиҳад ва дилҳои мӯъминонро хунук мегардонад,'),
(1250, 9, 15, 'ва кина аз дилҳояшон дур кунад ва Худо тавбаи ҳар киро, ки бихоҳад, мепазирад. Ва Худост донову ҳаким!'),
(1251, 9, 16, 'Оё пиндоштаед, ки шуморо ба ҳоли худ вомегузоранд, бе он ки Худо касоне аз шуморо, ки ҷиҳод мекунанд ва ҷуз Худову паёмбараш ва мӯъминонро ба ҳамрозӣ намегиранд, маълум дорад? Ва Худо ба ҳар коре, ки мекунед, огоҳ аст!'),
(1252, 9, 17, 'Мушриконро лоиқ нест, ки дар ҳоле, ки ба куфри худ иқрор мекунанд, масҷидҳои Худоро иморат кунанд. Аъмоли онҳо ночиз шуда ва дар оташ ҷовидонанд!'),
(1253, 9, 18, 'Масҷидҳои Худоро касоне иморат мекунанд, ки ба Худо ва рӯзи қиёмат имон овардаанд ва намоз мегузоранд ва закот медиҳанд ва аз ғайри Худо наметарсанд. Умед аст, ки инҳо аз ҳидоятёфтагон бошанд!'),
(1254, 9, 19, 'Оё об додан ба ҳоҷиён ва иморати Масҷидулҳаромро бо кардаи касе, ки ба Худо ва рӯзи қиёмат имон оварда ва дар роҳи Худо ҷиҳод карда, баробар медонед? Назди Худо баробар нестанд ва Худо ситамкоронро ҳидоят намекунад!'),
(1255, 9, 20, 'Онон, ки имон оварданд ва муҳоҷират карданд ва дар роҳи Худо ба молу ҷони хеш ҷиҳод карданд, дар назди Худо дараҷаҳое азимтар доранд ва комёфтагонанд.'),
(1256, 9, 21, 'Парвардигорашон ба раҳмату хушнудии худ ва ба биҳиште, ки дар он неъматҳои ҷовид бошад, хушхабарашон медиҳад.'),
(1257, 9, 22, 'Дар он биҳишт ҷовидон бимонанд. Зеро аҷру музди бузург дар назди Худованд аст!'),
(1258, 9, 23, 'Эй касоне, ки имон овардаед, агар падарону бародаронатон дӯст доранд, ки куфрро ба ҷои имон биинтихобанд, онҳоро ба дӯстӣ магиред ва ҳар кас аз шумо дӯсташон бидорад, аз ситамкорон хоҳад буд.'),
(1259, 9, 24, 'Бигӯ: «Агар падаронатону фарзандонатон ва бародаронатону занонатон ва хешовандонатону амволе, ки ҷамъ кардед ва тиҷорате, ки аз қасоди он бим доред ва хонаҳое, ки бад-он дилхуш ҳастед, барои шумо аз Худову паёмбараш ва ҷиҳод кардан дар роҳи Ӯ дӯст доштанитар аст, мунтазир бошед, то Худо фармони худ биёварад». Ва Худо нофармононро ҳидоят нахоҳад кард!'),
(1260, 9, 25, 'Худо шуморо дар бисёре аз ҷойҳо ёрӣ кард. Ва низ дар рӯзи Ҳунайн он гоҳ, ки бисёрии лашкаратон шуморо ба тааҷҷуб оварда буд, вале барои шумо суде надошт ва замин бо ҳамаи васеъияш бар шумо танг шуд ва бозгаштед ва ба душман пушт кардед.'),
(1261, 9, 26, 'Он гоҳ Худо оромиши худро бар паёмбараш ва бар мӯъминон нозил кард ва лашкариёне, ки онҳоро намедидед, поён фиристод ва кофиронро азоб кард ва ин аст ҷазои кофирон.'),
(1262, 9, 27, 'Аз он пас Худо тавбаи ҳар касро, ки бихоҳад, мепазирад, ки омурзанда ра меҳрубон аст!'),
(1263, 9, 28, 'Эй касоне, ки имон овардаед, мушрикон палиданд ва аз соли баъд набояд ба Масҷидулҳаром наздик шаванд. Ва агар аз бенавоӣ метарсед. Худо агар бихоҳад, ба фазли хеш тавонгаратон хоҳад кард. Зеро Худо донову ҳаким аст!'),
(1264, 9, 29, 'Бо касоне аз аҳли китоб, ки ба Худо ва рӯзи қиёмат имон намеоваранд ва чизҳоеро, ки Худову паёмбараш ҳаром кардаст, бар худ ҳаром намекунанд ва дини ҳақро намепазиранд, ҷанг кунед, то он гоҳ ки ба дасти худ дар айни хорӣ ҷизя (хироҷ) бидиҳанд.'),
(1265, 9, 30, 'Яҳуд гуфтанд, ки Узайр писари Худост ва насоро гуфтанд, ки Исо писари Худост. Ин сухан, ки мегӯянд, монанди гуфтори касонест, ки пеш аз ин кофир буданд. Худо бикушадашон. Чӣ гуна аз ҳақ гумроҳашон мекунанд?'),
(1266, 9, 31, 'Олимону обидони хеш ва Масеҳ — писари Марямро ба ҷои Оллоҳ ба худоӣ гирифтанд ва ҳол он, ки маъмур буданд, ки танҳо як худоро бипарастанд, ки ҳеҷ худое ҷуз Ӯ нест. Пок аст аз он чӣ шарикаш месозанд!'),
(1267, 9, 32, 'Мехоҳанд нури Худоро бо даҳони худ хомӯш кунанд ва Худо ҷуз ба камол расонидани нури худро намехоҳад. Ҳарчанд кофиронро хуш наёяд.'),
(1268, 9, 33, 'Ӯ касест, ки паёмбари худро барои ҳидояти мардум фиристод бо дине дурусту барҳақ то ӯро бар ҳамаи динҳо пирӯз гардонад, ҳарчанд мушриконро хуш наёяд.'),
(1269, 9, 34, 'Эй касоне, ки имон овардаед, бисёре аз олимону обидон амволи мардумро ба ноҳақ мехӯранд ва дигаронро аз роҳи Худо бозмедоранд. Ва касонеро, ки зару сим ҷамъ мекунанд ва дар роҳи Худо харҷаш намекунанд, ба азобе дардовар хабар деҳ,'),
(1270, 9, 35, 'рӯзе, ки дар оташи ҷаҳаннам гудохта шаванд ва пешониву паҳлӯ ва пушташонро бо он доғ кунанд. Ин аст он чизе, ки барои худ ҷамъ карда будед. Ҳоло таъми захираи хешро бичашед!'),
(1271, 9, 36, 'Шумори моҳҳо дар назди Худо, дар китоби Худо аз он рӯз, ки осмонҳову заминро биёфарида, дувоздаҳ аст. Чаҳор моҳ моҳҳои ҳароманд. Ин аст дини дуруст. Дар он моҳҳо бар хештан ситам макунед. Ва ҳамчунон, ки мушрикон ҳамагӣ ба ҷанги шумо бархостанд, ҳамагӣ ба ҷанги онҳо бархезед. Ва бидонед, ки Худо бо парҳезгорон аст!'),
(1272, 9, 37, 'Ба дер партофтани моҳҳои ҳаром афзунӣ дар куфр аст ва сабаби гумроҳии кофирон. Онҳо як сол он моҳро ҳалол мешумурданд ва як сол ҳаром, то бо он шумор, ки Худо ҳаром кардааст, мувофиқ ёбанд. Пас он чиро, ки Худо ҳаром карда, ҳалол мешуморанд. Кирдори нописандашон дар назарашон ороста гардида ва Худо кофиронро ҳидоят намекунад!'),
(1273, 9, 38, 'Эй касоне, ки имон овардаед, барои чӣ чун ба шумо гӯянд, ки барои ҷанг дар роҳи Худо ҷамъ шавед, гӯё ба замин мечаспед? Оё ба ҷои зиндагии ухравӣ ба зиндагии дунё розӣ шудаед? Матоъи ин дунё дар баробари матоъи охират ғайри андаке ҳеҷ нест.'),
(1274, 9, 39, 'Агар ба ҷанг ҷамъ нашавед. Худо шуморо ба шиканҷае дарднок азоб мекунад ва қавми дигареро ба ҷои шумо интихоб кунад ва ба Худо низ зиёне намерасонед, ки Худо бар ҳар коре тавоност!'),
(1275, 9, 40, 'Агар шумо ёриаш накунед, он гоҳ, ки кофирон берунаш карданд, Худо ёриаш кард. Яке аз он ду ба ҳангоме, ки дар ғор буданд, ба рафиқаш мегуфт: «Андӯҳгин мабош, Худо бо мост!» Худо ба дилаш оромиш бахшид ва бо лашкарҳое, ки шумо онро намедидед, қувваташ дод. Ва каломи кофиронро паст гардонид, зеро каломи Худо болост ва Худо пирӯзманду ҳаким аст!'),
(1276, 9, 41, 'Ба ҷанг биравед, хоҳ бар шумо осон бошад, хоҳ душвор ва бо молу ҷони хеш дар роҳи Худо ҷиҳод кунед. Агар бидонед, хайри шумо дар ин аст!'),
(1277, 9, 42, 'Агар матоъе даст ёфтанӣ бувад ё сафаре бувад на чандон дароз, аз паи ту меомаданд, вале акнун, ки роҳ дароз шудааст, паймуданашро душвор меҳисобанд. Ва ба Худо қасам мехӯранд, ки агар метавонистем, ҳамроҳи шумо берун меомадем. Инҳо тан ба ҳалокат месупоранд. Худо медонад, ки дурӯғ мегӯянд.'),
(1278, 9, 43, 'Худоят афв кунад, Чаро ба онон рухсати мондан додӣ? Мебоист онҳо, ки рост мегуфтанд, ошкор шаванд ва ту дурӯғгӯёнро ҳам бишносӣ.'),
(1279, 9, 44, 'Онон, ки ба Худо ва рӯзи қиёмат имон доранд, ҳаргиз барон ҷиҳод кардан бо молу ҷонашон аз ту рухсат намехоҳанд ва Худо парҳезгоронро мешиносад!'),
(1280, 9, 45, 'Касоне, ки ба Худо ва рӯзи қиёмат имон надоранд ва дилҳояшонро шак фаро гирифтааст ва ин шак онҳоро ба ҳайронӣ афканда ас, т, аз ту рухсат мехоҳанд,'),
(1281, 9, 46, 'Агар мехостанд, ки ба ҷанг берун шаванд, барои худ асбоби сафар омода карда буданд, вале Худо сафарбар шудани ононро нохуш дошт ва аз ҷангашон боздошт ва ба онҳо гуфта шуд, ки ҳамроҳи касоне, ки бояд дар хона бинишинанд, дар хона бинишинед.'),
(1282, 9, 47, 'Агар бо шумо ба ҷанг берун омада буданд, чизе ҷуз изтироб ба шумо намеафзуданд ва то фитнаангезӣ кунанд ва барои душманон ба ҷосусӣ пардозанд, дар миёни шумо рахна мекарданд ва Худо ба ситамкорон огоҳ аст!'),
(1283, 9, 48, 'Пеш аз ин ҳам фитнагарӣ мекарданд ва корҳоро барои ту вожгуна ҷилва медоданд то он гоҳ, ки ҳақ ғолиб омад ва фармони Худованд ошкор шуд, дар ҳоле, ки аз он нописандӣ доштанд.'),
(1284, 9, 49, 'Баъзе аз онон мегӯянд: «Маро рухсат деҳ ва ба гуноҳ маяндоз». Огоҳ бош, ки инҳо худ дар гуноҳ афтодаанд ва ҷаҳаннам бар кофирон иҳота дорад,'),
(1285, 9, 50, 'Агар хайре ба ту расад, андӯҳгинашон созад ва агар ба ту мусибате расад, мегӯянд: «Мо чораи кори худ пеш аз ин андешидаем». Он гоҳ шодмон бозмегарданд.'),
(1286, 9, 51, 'Бигӯ: «Мусибате ҷуз он чӣ Худо барои мо муқаррар кардааст, ба мо нахоҳад расид. Ӯ мавлои мост ва мӯъминон бар Худо таваккал кунанд».'),
(1287, 9, 52, 'Бигӯ: «Оё ҷуз яке аз он ду некӣ интизори чизи дигареро барои мо доред? Вале мо мунтазирем, ки азоби Худо ё аз ҷониби Ӯ ё ба дасти мо ба шумо бирасад. Пас шумо мунтазир бошед, мо низ бо шумо мунтазир мемонем».'),
(1288, 9, 53, 'Бигӯ: «Худо аз шумо намепазирад, чӣ аз рӯи ризо харҷ кунед, чи аз рӯи норизоӣ, зеро мардуме нофармон ҳастед».'),
(1289, 9, 54, 'Ҳеҷ чиз монеъи қабули харҷҳояшон нашуда, ғаири он ки ба Худову паёмбараш имон наёваранд ва бо бемайлӣ ба намоз ҳозир мешаванд ва ба норизоӣ нафақа мекунанд.'),
(1290, 9, 55, 'Дороиву фарзандонашон туро ба ҳайрат наандозад. Барои онанд, ки Худо мехоҳад ба онҳо дар ин дунё азобашон кунад ва дар ҳоле, ки ҳанӯз кофиранд, ҷонашон барояд.'),
(1291, 9, 56, 'Ба Худо савганд мехӯранд, ки аз шумоянд ва ҳол он ки аз шумо нестанд, инҳо аз шумо бимноканд.'),
(1292, 9, 57, 'Агар паноҳгоҳ ё ғор ё ниҳонгоҳе меёфтанд, шитобон ба он ҷо рӯй меоварданд.'),
(1293, 9, 58, 'Баъзе аз онҳо туро дар тақсими садақот ба зулм муттаҳам мекунанд, агар ба онҳо ато кунанд, хушнуд мешаванд ва агар ато накунанд, хашм мегиранд.'),
(1294, 9, 59, 'Чӣ мешавад, агар ба он чӣ Худову паёмбараш ба онон ато мекунад, хушнуд бошанд ва бигӯянд: «Худо моро бас аст ва Худову паёмбараш моро аз фазли худ бенасиб нахоҳанд гузошт ва мо ба Худо рағбат мекунем?»'),
(1295, 9, 60, 'Садақот барои фақирон асту мискинон ва коргузорони ҷамъоварии он. Ва низ барои ба даст овардани дили мухолифон ва озод кардани бандагону қарздорон ва харҷ дар роҳи Худо ва мусофирони мӯҳтоҷ ва он фаризаест аз ҷониби Худо. Ва Худо донову ҳаким аст!'),
(1296, 9, 61, 'Баъзе аз онҳо паёмбарро меозоранду мегӯянд, ки ӯ ба сухани ҳар кас гӯш медиҳад. Бигӯ: «Ӯ барои шумо шунавандаи сухани хайр аст. Ба Худо имон дорад ва мӯъминонро бовар дорад ва раҳматест барои онҳое, ки имон овардаанд. Ва онон, ки расули Худоро биёзоранд, ба шиканҷае дардовар гирифтор хоҳанд шуд.'),
(1297, 9, 62, 'Дар баробари шумо ба Худо савганд мехӯранд, то хушнудатон созанд ва ҳол он ки агар имон овардаанд, шоистатар аст, ки Худову расулашро хушнуд созанд.'),
(1298, 9, 63, 'Оё намедонанд, ки ҳар кас, ки ба Худову паёмбараш зиддият мекунад, ҷовидона дар оташи ҷаҳаннам хоҳад буд ва ин расвоии бузургест?'),
(1299, 9, 64, 'Мунофиқон метарсанд, ки мабод аз осмон дар бораи онҳо сурае нозил шавад ва аз он чӣ дар дил пинҳон кардаанд бохабарашон созад. Бигӯ: «Масхара кунед, ки Худо он чиро, ки аз он метарсед, ошкор хоҳад сохт».'),
(1300, 9, 65, 'Агар аз онҳо бипурсӣ, ки чӣ мекардед? Мегӯянд: «Мо бо ҳам харф мезадем ва бозӣ мекардем». Бигӯ: «Оё Худо ва оёти Ӯ ва паёмбарашро масхара мекардед?»'),
(1301, 9, 66, 'Узр наёваред. Пас аз имон кофир шудаед. Агар аз гуноҳи гурӯҳе аз шумо бигузарем, гурӯҳи дигарро, ки гунаҳкор шудаанд, азоб хоҳем кард.'),
(1302, 9, 67, 'Мардони мунофиқ ва занони мунофиқ ҳама ҳамонанди якдигаранд. Ба корҳои зишт фармон медиҳанд ва аз корҳои нек манъ мекунанд ва мушти худро аз харҷи дар рохи Худо мебанданд. Худоро фаромӯш кардаанд. Худо низ эшонро фаромӯш кардааст, зеро мунофиқон нофармононанд.'),
(1303, 9, 68, 'Худо ба мардони мунофиқу занони мунофиқ ва кофирон ваъдаи оташи ҷаҳаннам додааст. Дар он ҷовидонаанд. Ҳамин барояшон бас аст. Лаънати Худо бар онҳо бод ва ба азобе пойдор гирифтор хоҳанд шуд.'),
(1304, 9, 69, 'Ҳамонанди касоне, ки пеш аз шумо буданд, бо нерӯи бештар ва дороиву фарзандонн бештар, Онҳо аз насиби хеш баҳраманд шуданд. Шумо низ аз насиби хеш баҳраманд шудаед, ҳамчунон, ки касоне, ки пеш аз шумо будаанд, аз насиби хеш баҳраманд шуда буданд. Шумо суханони ботил гуфтед, ҳамчунон, ки онҳо сухани ботил мегуфтанд. Аъмоли онон дар дунёву охират ночиз гардид ва зиёнманд шуданд.'),
(1305, 9, 70, 'Оё хабари касоне, ки пеш аз онҳо будаанд, чун қавми Нӯҳ ва Оду Самуд ва қавми Иброҳиму асҳоби Мадяну Муътафикаро нашунидаанд, ки паёмбаронашон бо нишонаҳои ошкор бар онҳо фиристода шуданд? Худо ба онҳо ситам намекард, онон худ бар худ ситам мекарданд!'),
(1306, 9, 71, 'Мардони мӯъмин ва занони мӯъмин дӯстони якдигаранд. Ба некӣ фармон медиҳанд ва аз ношоист бозмедоранд ва намоз мегузоранд ва закот медиҳанд ва аз Худову паёмбараш фармонбардорӣ мекунанд. Худо инҳоро раҳмат хоҳад кард, Худо пирӯзманду ҳаким аст!'),
(1307, 9, 72, 'Худо ба мардони мӯъмин ва занони мӯъмин биҳиштҳоеро ваъда додааст, ки ҷӯйҳо дар он ҷорист ва биҳиштиён ҳамеша дар он ҷоянд ва низ хонаҳое некӯ дар биҳишти ҷовид. Вале хушнудии Худо аз ҳама бузургтар аст, ки пирӯзии бузург хушнудии Худованд аст!'),
(1308, 9, 73, 'Эй паёмбар, бо кофирону мунофиқон биҷанг ва бо онон ба шиддат рафтор кун, ҷойгоҳашон ҷаҳаннам аст, ки бад саранҷомест!'),
(1309, 9, 74, 'Ба Худо савганд мехӯранд, ки нагуфтаанд, вале калимаи куфрро бар забон рондаанд. Ва пас аз он ки ислом оварда буданд, кофир шудаанд. Ва қасдкорӣ карданд, аммо ба он ноил нашуданд. Айбҷӯиашон аз он рӯст, ки Худову паёмбараш аз ғаниматҳо босарваташон карданд. Пас, агар тавба кунанд, хайрашон дар он аст ва агар рӯйгардон шаванд. Худо ба азоби дардноке дар дунёву охират азобашон хоҳад кард ва онҳоро дар рӯи замин на дӯстдоре хоҳад буд ва на мададкоре!'),
(1310, 9, 75, 'Баъзе аз онҳо бо Худо паймон бастанд, ки агар аз фазли худ моле насибамон кунад, закот медиҳем ва дар зумраи солеҳон медароем.'),
(1311, 9, 76, 'Чун Худо аз фазли худ моле насибашон кард, бахилӣ карданд ва ба рӯйгардонӣ бозгаштанд.'),
(1312, 9, 77, 'Ва аз он пас то рӯзе, ки бо ӯ мулоқот кунанд, бо он ваъдахилофӣ бо Худо ва он дурӯғҳо, ки мегуфтанд, дилҳояшонро ҷои нифоқ сохт.'),
(1313, 9, 78, 'Оё намедонанд, ки Худо рози дили онҳоро медонад ва аз пич-пич карданашон огоҳ аст? Зеро Ӯст, ки донои ниҳонҳост.'),
(1314, 9, 79, 'Худованд ба масхара мегирад касонеро, ки бар мӯъминоне, ки ба рағбат садақа медиҳанд ва беш аз тавоноии хеш чизе намеёбанд, айб мегиранд ва масхараашон мекунанд. Ва онҳоро азобе дардовар аст.'),
(1315, 9, 80, 'Мехоҳӣ барояшон бахшиш бихоҳ мехоҳӣ бахшиш нахоҳ. Агар ҳафтод бор ҳам барояшон бахшиш бихоҳӣ. Худояшон нахоҳад бахшид, зеро ба Худову паёмбараш имон надоранд ва Худо мардуми нофармонро ҳидоят намекунад!'),
(1316, 9, 81, 'Онон, ки дар хона нишастаанд ва аз ҳамроҳӣ бо расули Худо ақибнишинӣ карданд, хушҳоланд. Ҷиҳод бо молу ҷони хешро дар роҳи Худо нохуш шумурданду гуфтанд: «Дар ҳавои гарм ба ҷанг наравед!» Агар мефаҳманд, бигӯ: «Гармии оташи ҷаҳаннам бештар аст!»'),
(1317, 9, 82, 'Ба ҷазои аъмоле, ки анҷом додаанд, бояд, ки андак биханданд ва фаровон бигирянд!'),
(1318, 9, 83, 'Агар Худо туро аз ҷанг бозгардонид ва бо гурӯҳе аз онҳо дидор кардӣ ва аз ту хостанд, ки барои ҷанги дигар берун оянд, бигӯ: «Шумо ҳаргиз бо ман ба ҷанг берун нахоҳед шуд ва ҳамроҳи ман бо ҳеҷ душмане набард нахоҳед кард, зеро шумо аз нахуст ба нишастан дар хона хушнуд будаед. Пас акнун ҳам бо онон, ки аз фармон хилофкорӣ кардаанд, дар хона бимонед!»'),
(1319, 9, 84, 'Чун бимиранд, бар ҳеҷ як аз онон намоз макун ва бар қабрашон маист. Инҳо ба Худо ва расулаш кофир шудаанд ва нофармон мурдаанд!'),
(1320, 9, 85, 'Амволу авлодашон ба ҳайр\'атат наяфканад. Худо мехоҳад ба сабаби онҳо дар дунё азобашон кунад ва дар айни куфр ҷон бисупоранд.'),
(1321, 9, 86, 'Чун сурае нозил шуд, ки ба Худо имон биёваред ва бо паёмбараш ба ҷанг биравед, тавонгаронашон аз ту рухсат хостанду гуфтанд: «Моро бигзор то бо онҳое, ки бояд дар хона нишинанд, дар хона бинишинем!»'),
(1322, 9, 87, 'Ба он розӣ шудаанд, ки ҳамроҳи хонанишинон бошанд, бар дилҳояшон мӯҳр ниҳода шуда ва намефаҳманд.'),
(1323, 9, 88, 'Вале паёмбар ва касоне, ки бо ӯ имон овардаанд, бо молу ҷони худ дар роҳи Худо ҷиҳод карданд. Некиҳо аз они онҳост ва онҳоянд, наҷотёфтагон!'),
(1324, 9, 89, 'Худо барояшон биҳиштҳое, ки ҷӯйҳо дар он равон аст ва дар он ҷо ҷовидонанд, омода кардааст. Ин аст наҷоти бузург!'),
(1325, 9, 90, 'Гурӯҳе аз арабҳои бодиянишин омаданд ва узр оварданд, то онҳоро рухсат диҳанд, ки ба ҷанг нараванд ва онҳое, ки ба Худову паёмбараш дурӯғ гуфта буданд, дар хона нишастанд. Ба зудӣ ба кофиронашон азобе дардовар хоҳад расид!'),
(1326, 9, 91, 'Бар нотавонону беморон ва онон, ки маводи ҷангии хеш намеёбанд, ҳар гоҳ дар амал барои Худову паёмбараш ихлос кунанд, гуноҳе нест, агар ба ҷанг наёянд, ки бар некӯкорон ҳеҷ гуна итобе нест ва Худо бахшояндаву меҳрубон аст!'),
(1327, 9, 92, 'Ва низ бар онон, ки назди ту омаданд, то маркаб барои набардашон диҳӣ ва ту гуфтӣ, ки маркабе надорам ва онҳо барои харҷ кардан ҳеҷ наёфтанд ва ашкрезону ғамгин бозгаштанд, гуноҳе нест.'),
(1328, 9, 93, 'Хашму азоби Худо бар касонест, ки дар айни тавонгарӣ аз ту рухсат мехоҳанд ва ба он хушнуданд, ки бо хонанишинон дар хона бимонанд. Худо дилҳояшонро мӯҳр барниҳода аст, ки намедонанд.'),
(1329, 9, 94, 'Чун шумо ба наздашон бозгардед, меоянду узр меоваранд. Бигӯ: «Узр маёваред, гуфторатонро бовар надорем, ки Худо моро аз ахбори шумо огоҳ кардааст. Ба зудӣ Худову паёмбараш ба аъмоли шумо хоҳанд расид. Он гоҳ шуморо назди он Худованде, ки донои ниҳону ошкор аст, мебаранд, то аз натиҷаи аъмолатон огоҳатон созад!»'),
(1330, 9, 95, 'Чун ба наздашон бозгардед, бароятон савганд Мехӯранд, то аз хатояшон даргузарсд. Аз онҳо рӯй гардонед, ки мардуме палиданд ва ба хотири аъмолашон ҷой дар ҷаҳаннам доранд!'),
(1331, 9, 96, 'Бароятон савганд мехӯранд, то аз онон хушнуд шавед. Агар шумо ҳам хушнуд шавед, Худо аз ин мардуми нофармон хушнуд нахоҳад шуд!'),
(1332, 9, 97, 'Арабҳои бодиянишин кофиртару мунофиқтар аз дигаронанд ва ба бехабарӣ аз ҳукмҳое, ки Худо бар паёмбараш нозил кардааст, сазовортаранд. Ва Худо донову ҳаким аст!'),
(1333, 9, 98, 'Баъзе аз арабҳои бодиянишин он чиро, ки харҷ мекунанд, чун зиёне мепиндоранд ва мунтазиранд, то ба шумо мусибате бирасад. Мусибати бад бар худашон бод ва Худо шунавову доност!'),
(1334, 9, 99, 'Ва баъзе аз арабҳои бодиянишин ба Худову рӯзи қиёмат имон доранд ва он чиро, ки харҷ мекунанд, барои наздик шудан ба Худо ва ба хотири дуъои паёмбар харҷ мекуканд. Бидонед, ки ҳамин сабаби наздикияшон ба Худо хоҳад шуд ва Худо ба раҳмати хеш дохилашон хоҳад кард, зеро омурзандаву меҳрубон аст!'),
(1335, 9, 100, 'Аз он гурӯҳи нахустин аз муҳоҷирон ва аҳли Мадина, ки пешкадам шуданд ва онон, ки ба некӣ аз паяшон рафтанд, Худо хушнуд аст ва онҳо низ аз Худо хушнуданд. Барояшон биҳиштҳое, ки дар онҳо нахрҳо ҷорист ва ҳамеша дар он ҷо хоҳанд буд, омода кардааст. Ин аст комёбии бузург!'),
(1336, 9, 101, 'Гурӯҳе аз арабҳои бодиянишин, ки гирди шуморо гирифтаанд, мунофиқанд ва гурӯҳе аз мардуми Мадина низ дар нифоқ исрор меварзанд. Ту онҳоро намешиносӣ, мо мешиносемашон ва ду бор азобашон хоҳем кард ва ба азоби бузург гирифтор мешаванд.'),
(1337, 9, 102, 'Ва гурӯҳе дигар ба гуноҳи худ эътироф карданд, ки аъмоли некӯро бо корҳои зишт омехтаанд. Шояд Худо тавбаашонро бипазирад, зеро Худо омурзандаву меҳрубон аст!'),
(1338, 9, 103, 'Аз дороиҳояшон садақа биситон, то ононро поку муназзаҳ созӣ ва барояшон дуъо кун, зеро дуъои ту мояи оромиши онҳост ва Худо шунавову доност!'),
(1339, 9, 104, 'Оё ҳанӯз надонистаанд, ки Худост, ки тавбаи бандагонашро мепазирад ва садақотро меситонад ва Худост, ки тавбапазиру меҳрубон аст?'),
(1340, 9, 105, 'Бигӯ: «Амал кунед, Худову паёмбараш аз мӯъминон аъмоли шуморо хоҳанд дид ва шумо ба назди донои ниҳону ошкоро бозгардонида мешавед ва Ӯ аз амалҳоятон огоҳатон хоҳад кард».'),
(1341, 9, 106, 'Ва гурӯҳе дигар ба машийяти (хости) Худованд вогузошта шудаанд, ки ё азобашон мекунад ё тавбаашонро мепазирад. Ва Худо донову ҳаким аст!'),
(1342, 9, 107, 'Худо шоҳидӣ медиҳад: «Онҳое, ки масҷиде месозанд, то ба мӯъминон зиён расонанд ва миёнашон куфру ҷудои андозанд ва то барои касоне, ки мехоҳанд бо Худову паёмбараш ҷанг кунанд, камингоҳе бошад, он гоҳ савганд мехӯранд, ки моро қасде ҷуз некӯкорӣ набуда аст, дурӯғ мегӯянд».'),
(1343, 9, 108, 'Ҳаргиз дар он масҷид намоз магузор. Масҷиде, ки аз рӯзи нахуст бар парҳезгорӣ бунёд шуда, шоистатар аст, ки дар он ҷо намоз кунӣ. Дар он ҷо мардоне ҳастанд, ки дӯст доранд покиза бошанд, зеро Худо покизагонро дӯст дорад.'),
(1344, 9, 109, 'Оё касе, ки бинои масҷидро бар тарс аз Худо ва хушнудии Ӯ ниҳода, беҳтар аст ё он касе, ки бинои масҷидро бар канораи селгоҳе, ки об зери онро шуста бошад, ниҳодааст, то бо ӯ дар оташи ҷаҳаннам сарнагун гардад? Ва Худо мардуми ситамгарро ҳидоят намекунад.'),
(1345, 9, 110, 'Он бино, ки баровардаанд, ҳамеша мояи ташвиш дар дилашон хоҳад буд, то он ҳангом, ки дилашон пора-пора гардад. Ва Худо донову ҳаким аст!'),
(1346, 9, 111, 'Худо аз мӯъминон ҷонҳову молҳояшонро харид, то биҳишт аз онон бошад. Дар роҳи Худо ҷанг мекунанд, чӣ бикушанд ё кушта шаванд, ваъдае, ки Худо дар Тавроту Инҷил ва Қуръон дода аст, ба ҳақ бар ӯҳдаи Ӯст. Ва чӣ касе беҳтар аз Худо ба аҳди худ вафо хоҳад кард? Ба ин хариду фурӯхт, ки кардаед, шод бошед, ки комёбии бузургест!'),
(1347, 9, 112, 'Тавбакунандагонанд, парастандагонанд, шукргузоронанд, рӯзадоронанд, руқуъкунандагонанд, саҷдакунандагонанд, амркунандагон ба маъруф ва манъкунандагон аз мункаранд, ва ҳофизони ҳудуди Худоянд! Ва мӯъминонро хушхабар деҳ!'),
(1348, 9, 113, 'Набояд паёмбар ва касоне, ки имон овардаанд, барои мушрикон, ҳарчанд аз хешовандон бошанд, пас аз он ки донистанд, ки ба ҷаҳаннам мераванд, талаби бахшоиш кунанд.'),
(1349, 9, 114, 'Бахшоиш хостани Иброҳим барои падараш набуд, ғайри хотири ваъдае, ки ба ӯ дода буд. Ва чун барои ӯ ошкор шуд, ки падараш душмани Худост, аз ӯ безорӣ ҷуст. Зеро Иброҳим бисёр худойтарсу бурдбор буд!'),
(1350, 9, 115, 'Худо қавмеро, ки ҳидоят кардааст, гумроҳ намекунад, то барояшон равшан кунад, ки аз чӣ чиз бояд бипарҳезанд, ки Худо бар ҳар чизе доност!'),
(1351, 9, 116, 'Фармонравоии осмонҳову замин аз они Худост! Зинда мекунаду мемиронад ва шуморо ҷуз Худо дӯстдору ёваре нест.'),
(1352, 9, 117, 'Худо тавбаи паёмбару муҳоҷирин ва ансорро, ки дар он соати усрат ҳамроҳи Ӯ буданд, аз он пас, ки наздик буд, ки гурӯҳеро дил аз ҷанг бигардад, пазируфт. Тавбаашон пазируфт, зеро ба онҳо рауфу (мушфиқу) меҳрубон аст!'),
(1353, 9, 118, 'Ва низ Худо пазируфт тавбаи он се танро, ки аз ҷанг хонанишинӣ карда буданд, то он гоҳ, ки замин бо ҳамаи кушодагияш бар онҳо танг шуд ва ҷон дар танашон намеғунҷид ва худ донистанд, ки ҷуз Худованд ҳеҷ паноҳгоҳе, ки ба он рӯй оваранд, надоранд. Пас Худованд тавбаи онон пазируфт, то ба Ӯ бозоянд, ки тавбапазиру меҳрубон аст!'),
(1354, 9, 119, 'Эй касоне, ки имон овардаед, аз Худо битарсед ва бо ростгӯён бошед!'),
(1355, 9, 120, 'Аҳли Мадина ва арабҳои бодиянишини атрофи онро нарасад, ки аз ҳамроҳӣ бо паёмбари Худо кафокашӣ намоянд ва набояд, ки аз Ӯ ба худ (яъне аз Ӯ гузашта ба ҳифзи ҷони худ бошанд) пардозанд. Зеро дар роҳи Худо ҳеҷ ташнагӣ ба онҳо чира нашавад ё ба ранҷ наафтанд, ё ба гуруснагӣ дучор нагарданд ё қадаме, ки кофиронро хашмгин созад, барнадоранд, ё ба душман дастбурде назананд, магар он ки амали солеҳе барояшон навишта шавад, ки Худо подоши некӯкоронро нобуд намесозад!'),
(1356, 9, 121, 'Ва ҳеҷ моле — чӣ андаку чӣ бисёр — харҷ накунанд ва аз ҳеҷ водие нагзаранд, магар он ки ба ҳисобашон навишта шавад, то Худо подошашон диҳад, чун подоши некӯтарин коре, ки мекардаанд.'),
(1357, 9, 122, 'Ва натавонанд мӯъминон, ки ҳамагӣ ба сафар раванд. Чаро аз ҳар гурӯҳе дастае ба сафар нараванд, то дониши дини хешро биёмузанд ва чун бозгаштанд, мардуми худро хушдор диҳанд, бошад, ки аз зишткорӣ ҳазар кунанд?'),
(1358, 9, 123, 'Эй касоне, ки имон овардаед, бо кофироне, ки наздики шумоянд, ҷанг кунед, то дар шумо шиддату дуруштӣ ёбанд ва бидонед, ки Худо бо парҳезгорон аст!'),
(1359, 9, 124, 'Ва чун сурае нозил шавад, баъзе мепурсанд: «Ин сура ба имони кадом як аз шумо дарафзуд?» Онон, ки имон овардаанд, ба имонашон афзуда шавад ва худ шодмонӣ мекунанд.'),
(1360, 9, 125, 'Аммо онон, ки дар дилҳояшон маразест, ҷуз инкоре бар инкорашон наяфзуд ва ҳамчунон кофир бимурданд.'),
(1361, 9, 126, 'Оё намебинанд, ки дар ҳар сол як ё ду бор мавриди озмоиш воқеъ мешаванд? Вале на тавба мекунанд ва на панд мегиранд.'),
(1362, 9, 127, 'Ва чун сурае нозил шавад, баъзе ба баъзе дигар нигоҳ мекунанд: «Оё касе шуморо мебинад?» Ва бозмегарданд. Худо дилҳояшонро аз имон мунсариф сохта (баргардонда), зеро мардуме нофаҳманд!'),
(1363, 9, 128, 'Албатта паёмбаре аз худи шумо бар шумо фиристода шуд, ҳар он чӣ шуморо ранҷ медиҳад, бар ӯ гарон меояд. Сахт ба шумо дилбаста аст ва бо мӯъминон мушфиқу меҳрубон аст.'),
(1364, 9, 129, 'Агар бозгарданд, бигӯ: «Худо барои ман кофист, худое ҷуз Ӯ нест, бар Ӯ таваккал кардам ва Ӯст Парвардигори арши бузург!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Алиф, лом, ро. Ин аст оятҳоикитоби бо ҳикмат.'),
(1366, 10, 2, 'Оё мардум дар тааҷҷубанд, аз ин ки бар марде аз худашон ваҳй кардем, ки мардумро битарсон ва мӯъминонро хушхабар деҳ, ки дар назди Парвардигорашон пойгоҳе баланд доранд? Кофирон гуфтанд, ки ин мард ошкоро ҷодугарест!'),
(1367, 10, 3, 'Парвардигори шумо Оллоҳ аст, ки осмонҳову заминро дар шаш рӯз биёфарид; сипас ба арш пардохт: корҳоро тадбир кард. Ҷуз ба рухсати Ӯ шафоъаткунандае набошад. Ин аст Оллоҳ — Парвардигори шумо. Ӯро бипарастед! Чаро панд намегиред?'),
(1368, 10, 4, 'Бозгашти ҳамаи шумо ба Ӯст. Ин барҳақ ваъдаи Худост. Ӯ махлуқотро биёфаринад. Он гоҳ ононро ба сӯи худ бозгардонад, то касонеро, ки имон оварда ва кори шоиста кардаанд, аз рӯи адл мукофот диҳад. Ва кофиронро ба ҷазои куфрашон шаробест аз оби ҷушону азобест дардовар.'),
(1369, 10, 5, 'Ӯст он, ки хуршедро равшанӣ бахшиду моҳро мунаввар сохт ва барояш манозиле муъайян кард, то аз шумори солҳо ва ҳисоб огоҳ шавед. Худо ҳамаи инҳоро ҷуз ба ҳақ наёфарид ва оётро барои мардуме, ки медонанд, батафсил баён мекунад.'),
(1370, 10, 6, 'Дар омадушуди шабу рӯз ва он чӣ Худо дар осмонҳову замин офаридааст, барои парҳезгорон ибратҳост!'),
(1371, 10, 7, 'Касоне, ки ба дидори Мо умед надоранд ва ба зиндагии дунявӣ хушнуд шуда ва ба он оромиш ёфтаанд ва онон, ки аз оёти Мо бехабаранд,'),
(1372, 10, 8, 'ба ҷазои корҳое, ки мекардаанд, ҷойгоҳашон ҷаҳаннам аст.'),
(1373, 10, 9, 'Ононро, ки имон овардаанд ва корҳои шоиста кардаанд, Парвардигорашон ба сабаби имонашон ба биҳиштҳое пурнеъмат, ки наҳрҳои об дар зери пояшон ҷорист, ҳидоят мекунад.'),
(1374, 10, 10, 'Дар он ҷо дуъояшон ин аст: «Бор Худое, Ту поКӣ». Ва ба ҳангоми дуруд салом мегӯянд. Ва поёни дуъояшон ин аст: «Шукр Худоро, он Парвардигори ҷаҳониён».'),
(1375, 10, 11, 'Агар бо ҳамон шитоб, ки мардум барои худ хайр металабанд, Худо барояшон шарр металабид, маргашон фаро расида буд. Пас ононро, ки ба дидори Мо умед надоранд, вомегузорем, то дар гумроҳии худ саргардон бимонанд.'),
(1376, 10, 12, 'Чун ба одамӣ зараре расад, чӣ бар паҳлу хуфта бошад ва чӣ нишаста ё истода, Моро ба дуъо мехонад. Ва чун он зарарро аз ӯ дур созем, чунон мегузарад, ки гӯӣ Моро барои дафъи он зараре, ки ба ӯ расида буд, ҳаргиз нахондааст. Амалҳои исрофкорон инчунин дар назарашон ороста шудааст.'),
(1377, 10, 13, 'Ва Мо мардумеро, ки пеш аз шумо буданд, чун ситам карданд ва ба паёмбаронашон, ки бо далоили равшан сӯи онҳо омада буданд, имон намеоварданд, ҳалок кардем. Мардуми гунаҳкорро инчунин ҷазо медиҳем.'),
(1378, 10, 14, 'Он гоҳ шуморо дар рӯи замин ҷонишини онҳо гардонидем, то бингарем, ки чӣ гуна амал мекунед.'),
(1379, 10, 15, 'Чун оёти равшани мо бар онон тиловат шуд, онҳо, ки ба дидори Мо умед надоранд, гуфтанд: «Қуръоне ғайри ин Қуръон биёвар ё дигаргунаш кун!» Бигӯ: «Маро нарасад, ки онро аз сӯи худ дигаргун кунам. Ҳар чӣ ба ман ваҳй мешавад, пайрави ҳамон ҳастам. Метарсам, ки агар ба Парвардигорам нофармон шавам, ба азоби он рӯзи бузург гирифтор оям».'),
(1380, 10, 16, 'Бигӯ: «агар Худо мехост, ман онро бар шумо тиловат намекардам ва шуморо аз он огоҳ намесохтам. Ва пеш аз ин дар миёни шумо умре зистаам. Чаро ба ақл дарнамеёбед?»'),
(1381, 10, 17, 'Кист ситамкортар аз он, ки бар Худо дурӯғ мебандад, ё оёти Ӯро дурӯғ мебарорад? Албатта кофирон наҷот намеёбанд!.'),
(1382, 10, 18, 'Ғайри Худо чизҳоеро мепарастанд, ки на судашон мерасонад на зиён. Ва мегӯянд: «Инҳо шафеъони мо дар назди Худоянд». Бигӯ: «Оё ба Худо аз чизе хабар медиҳед, ки дар замину осмон аз он хабаре надорад? Худо пок аст ва аз он чӣ бо Ӯ шарик месозед, болотар аст!»'),
(1383, 10, 19, 'Мардум ҷуз як уммат набуданд, миёнашон ихтилоф афтод. Ва агар на он сухане буд, ки пеш аз ин аз Парвардигорат содир гашта буд, дар он чӣ ихтилоф мекунанд, ҳукм шуда буд.'),
(1384, 10, 20, 'Ва мегӯянд: «Чаро аз ҷониби Парвардигораш мӯъҷизае бар ӯ нозил намешавад?» Бигӯ: «Илми ғайб аз они Худост. Интизор бикашед! Ман низ бо шумо мунтазир мемонам!»'),
(1385, 10, 21, 'Ҳар гоҳ пас аз зараре, ки ба мардум расидааст, раҳмате ба онҳо бичашонем, бинӣ, ки дар оёти Мо бадандешӣ мекунанд, бигӯ: «Макри Худо зудтар аст». Расулони Мо низ бадандешиҳои шуморо менависанд.'),
(1386, 10, 22, 'Ӯст, ки шуморо дар хушкӣ ва дар дарё сайр медиҳад. То он гоҳ, ки дар киштиҳо ҳастанд ва боди мувофиқ ба ҳаракаташон меоварад, шодмонанд. Чун тӯфон фаро расад ва мавҷ аз ҳар сӯ бар онҳо резад, чунон кн пиндоранд, ки дар муҳосираи мавҷ қарор гирифтаанд, Худоро аз рӯи ихлоси ақида бихонанд, ки агар моро аз ин хатар бираҳонӣ, аз шукргузорон хоҳем буд.'),
(1387, 10, 23, 'Чун Худо онҳоро бираҳонад, бинӣ, ки дар замин ба ноҳақ саркашӣ кунанд. Эй мардум, ин саркашӣ ба зиёни худатон аст. Манфиатест дар ин зиндагии дунявӣ. Он гоҳ ҳама ба назди Мо бозмегардед то аз корҳое, ки мекардаед, огоҳатон созем.'),
(1388, 10, 24, 'Мисоли ин зиндагии дунё чун боронест, ки аз осмон нозил кунем, то ба он ҳар гуна растаниҳо аз замин бирӯяд, чӣ онҳо, ки одамиён мехӯранд ва чӣ онҳо, ки чорпоён мечаранд. Чун замин зинати худ баргирифт ва ороста шуд ва мардумаш пиндоштанд, ки худ қодир бар он ҳама будаанд, фармони Мо шабҳангом ё ба рӯз даррасад ва чунон аз бехаш барканем, ки гӯӣ дирӯз дар он макон ҳеҷ чиз набудааст, Оётро барои мардуме, ки меандешанд, инчунин тафсил медиҳем.'),
(1389, 10, 25, 'Худованд ба сарои саломат даъват мекунад ва ҳар киро бихоҳад ба роҳи рост ҳидоят мекунад!'),
(1390, 10, 26, 'Музди онон, ки некӣ мекунанд, некист ва чизе афзун бар он. На сияхрӯй шаванду на хор. Инҳо аҳли биҳиштанд ва дар он ҷовидонанд.'),
(1391, 10, 27, 'Ва барои онон, ки муртакиби бадиҳо шаванд, музди ҳар бадӣ монанди он аст ва хори бар онҳо ғолиб мешавад. Касе онҳоро аз хашми Худо нигаҳ намедорад, чунон шаванд, ки гӯё сураташон дар порае аз шаби торик пушида шуда аст. Инҳо аҳли ҷаҳанаманд ва ҷовидона дар он ҳастанд.'),
(1392, 10, 28, 'Ва рӯзе ҳамаи онҳоро дар маҳшар гирд оварем. Сипас мушриконро гӯем: «Шумо ва бутҳоятон дар макони худ қарор гиред!» Сипас онҳоро аз якдигар ҷудо мекунем ва бутҳояшон мегӯянд: «Шумо ҳаргиз моро намепарастидаед».'),
(1393, 10, 29, 'Худо ба гувоҳӣ миёни мову шумо кофист, ки мо аз парастиши шумо ҳаргиз огоҳ набудаем.'),
(1394, 10, 30, 'Дар он ҷо ҳар кас ҳарчӣ кардааст, ҷазоашро хоҳад дид ва ҳамаро ба сӯи Худо — мавлои ҳақиқиашон бозмегардонанд ва ҳамаи он бутон, ки ба ботил мепарастиданд, нобуд мешаванд.'),
(1395, 10, 31, 'Бигӯ: «Чӣ касе аз осмону замин ба шумо рӯзӣ медиҳад? Кист, ки шунавоиву биноӣ мебахшад? Зиндаро аз мурда падид меорад ва мурдаро аз зинда ва корхоро ба сомон меоварад?» Хоҳанд гуфт: «Оллоҳ». Бигӯ: «Оё намотарсед?».'),
(1396, 10, 32, 'Он Оллоҳ — Парвардигори хақиқии шумост, баъд аз ҳақиқат ҷуз гумроҳӣ чист? Пас ба куҷо рӯй меоваред?'),
(1397, 10, 33, 'Пас сухани Парвардигори ту дар бораи исёнгарон, ки гуфт: «Инҳо имон намеоваранд», ба ҳақиқат пайваст!'),
(1398, 10, 34, 'Бигӯ: «Оё аз ин бутони шумо касе ҳаст, ки чизеро биёфаринад ва боз зинда кунад?» Бигӯ: «Худои якто махлуқотро меофаринад ва боз зинда мекунад. Пас чӣ гуна аз ҳақ бероҳатон мекунанд?»'),
(1399, 10, 35, 'Бигӯ: «Оё аз ин бутони шумо касе ҳаст, ки ба ҳақ роҳ намояд?» Бигӯ: «Худо ба ҳақ роҳ менамояд. Оё он, ки ба ҳақ роҳ менамояд ба пайравӣ сазовортар аст а он ки ба ҳақ роҳ наменамояд ва худ низ мӯҳтоҷи ҳидоят аст? Шуморо чӣ мешавад? Чӣ гуна ҳукм мекунед?»'),
(1400, 10, 36, 'Бештарашон фақат пайрави гумонанд ва гумон наметавонад ҷои ҳақро бигирад. Албатта Худо ба коре, ки мекунанд, огоҳ аст!'),
(1401, 10, 37, 'Нашояд, ки ин Қуръонро ҷуз ба Худо нисбат кунанд балки тасдиқи чизест, ки пеш аз он омадааст ва баёни он китоб аст. Шакке нест, ки аз ҷониби Парвардигори чаҳониён аст.'),
(1402, 10, 38, 'Мегӯянд, ки онро ба дурӯғ ба Худо нисбат мекунад. Бигӯ: «Агар рост мегӯед, ҷуз Худо ҳар киро, ки метавонед, ба ёрӣ бихонед ва сурае монанди он биёваред».'),
(1403, 10, 39, 'Чизеро дурӯғ шумурданд, ки ба илми он иҳота наёфта буданд ва ҳанӯз азобаш ба онҳо наомадааст. Касоне, ки пеш аз онон буданд, низ паёмбаронро чунин ба дурӯғ баровардаанд. Пас бингар, ки оқибати кори ситамкорон чӣ гуна будааст.'),
(1404, 10, 40, 'Баъзе аз онҳо ба он имон меоваранд ва баъзе имон намеоваранд. Ва IIарвардигори ту ба фасодкорон огоҳтар аст!'),
(1405, 10, 41, 'Агар туро ба дурӯғ нисбат кардан, бигӯ: «Амали ман аз они ман аст ва амали шумо аз они шумо. Шумо аз кори ман безоред ва ман аз кори шумо безорам».'),
(1406, 10, 42, 'Баъзе аз онҳо ба ту гӯш медиҳанд. Оё агар дарнаёбанд, ту метавонӣ каронро шунаво созӣ?'),
(1407, 10, 43, 'Баъзе аз онҳо ба ту менигаранд. Оё агар набинанд, ту метавонӣ кӯрони нобиноро роҳ бинамоӣ?'),
(1408, 10, 44, 'Худованд ба мардум ҳеҷ ситам намекунад, вале мардум худ ба худ ситам мекунанд!'),
(1409, 10, 45, 'Ва рӯзе, ки Худованд ононро дар маҳшар гирд оварад, чунон пиндорӣ, ки танҳо соате аз рӯз дар дунё истодаанд, якдигарро бишиносанд. Онҳо, ки дидор бо Худоро дурӯғ, меҳисобиданд, зяёндидаанд ва ҳидоятнаёфтаанд!'),
(1410, 10, 46, 'Ё баъзе аз чизҳоеро, ки ба онҳо ваъда додаем, ба ту менамоёнем ё туро мемиронем. Бозгашти ҳамаашон ба назди мост ва Худо ба ҳар коре, ки мекунанд, шоҳид аст.'),
(1411, 10, 47, 'Ҳар умматеро паёмбаре буд ва чун паёмбарашон биёмад, корҳо миёнашон ба адолат ҳал шуданд ва мавриди ситам воқеъ нашуданд.'),
(1412, 10, 48, 'Мегӯянд: «Агар рост мегӯед, пас ин ваъда чӣ вақт хоҳад буд?»'),
(1413, 10, 49, 'Бигӯ: «Ман дар бораи худ, ҷуз он чӣ Худо бихоҳад, молики ҳеҷ суду зиёне нестам. Марги ҳар умматеро замоне муайян аст. Чун замонашон фаро расад, на як соат дер кунанд ва на як соат пеш раванд».'),
(1414, 10, 50, 'Бигӯ: «Чӣ мекунед, агар азоби ӯ ба ногоҳ шабҳангом ё ба рӯз фаро расад? Гуноҳкорон чӣ чизро инчунин ба шитоб металабанд?'),
(1415, 10, 51, 'Оё чун азоб ба вуқӯъ пайвандад, ба он имон меоваред? Акнун ин ҳамон чизест, ки фаро расиданашро ба шитоб мехостед.'),
(1416, 10, 52, 'Он гоҳ ба ситамкорон гӯянд: «Азоби ҷовидро бичашед! Оё на чунин аст, ки дар баробари амалҳоятон ҷазо мебинед?»'),
(1417, 10, 53, 'Аз ту хабар мепурсанд, ки оё ҳақ аст? Бигӯ: «Оре, савганд ба Парвардигорам, ки ҳақ аст ва шуморо қувваи фирор аз он нест!»'),
(1418, 10, 54, 'Агар ҳар гунаҳкоре соҳиби ҳамаи рӯи замин бошад, хоҳад, ки худро ба он азоб бозхарад ва чун азобро бингаранд, пушаймонии худ дар дил ниҳон доранд. Миёнашон ба адолат ҳукм шавад ва мавриди ситам воқеъ нагарданд.'),
(1419, 10, 55, 'Бидонед, ки ҳар чӣ дар осмонҳову замин аст, аз они Худост! Ва огоҳ бошед, ки ваъдаи Худо ҳақ аст, вале бештарашон намедонанд!'),
(1420, 10, 56, 'Ӯст, ки зинда мекунаду мемиронад ва ҳама ба сӯи Ӯ бозмегарданд.'),
(1421, 10, 57, 'Эй мардум, барои шумо аз ҷониби Парвардигоратон панде омад ва шифое барои он беморие, ки дар дил доред ва роҳнамоиву раҳмате барои мӯъминон.'),
(1422, 10, 58, 'Бигӯ: «Ба фазли Худову раҳмати Ӯ». Шодмон шаванд, зеро ин ду аз ҳар чӣ ҷамъ мекунанд, беҳтар аст.'),
(1423, 10, 59, 'Бигӯ: «Оё ба ризқе, ки Худо бароятон нозил кардааст, нигаристаед? Баъзеро ҳаром шумурдед ва баъзеро ҳалол». Бигӯ: «Худо ба шумо иҷоза додааст ё ба Ӯ дурӯғ мебандед?»'),
(1424, 10, 60, 'Онон, ки ба Худо дурӯғ мебанданд, дар бораи рӯзи қиёмат чӣ гумон кардаанд? Худо фазли худро ба мардумон арзонӣ медорад, вале бештаринашон шукр намегуянд!'),
(1425, 10, 61, 'Дар ҳар коре, ки бошӣ ва ҳар чӣ аз Қуръон бихонӣ ва даст ба ҳар амале, ки бизанед, ҳангоме ки ба он мепардозед, мо нозир бар шумо ҳастем. Бар Парвардигори ту ҳатто ба миқдори заррае дар замину осмонҳо пушида нест. Ва ҳар чӣ хурдтар аз он ё бузургтар аз он бошад, дар китоби мубин навишта шудааст.'),
(1426, 10, 62, 'Огоҳ бошед, ки бар дустони Худо биме нест ва ғамгин намешаванд.'),
(1427, 10, 63, 'Касонеро, ки имон оварданд ва парҳезгорӣ мекарданд,'),
(1428, 10, 64, 'хушхабар аст онҳоро дар дунёву охират. Сухани Худо дигаргун намешавад. Ин аст комёбии бузург!'),
(1429, 10, 65, 'Сухани онон туро ғамгин насозад. Иззат ба тамомӣ аз они Худованд аст. Ӯст, ки шунавову доност!'),
(1430, 10, 66, 'Огоҳ бошед, ки аз они Худост ҳар чӣ дар осмонҳову замин аст ва онон, ки ғайри Худоро шарикон мехонанд, аз гумони худ пайравӣ мекунанд ва дурӯғ мегӯянд.'),
(1431, 10, 67, 'Ӯст, ки шабро бароятон падид овард, то дар он биёромед ва рӯзро падид овард, то дар он бубинед. Дар инҳо барои мардуме, ки мешунаванд, ибратҳост!'),
(1432, 10, 68, 'Гуфтанд: «Худо фарзанде интихобидаст». Пок аст! Бениёз аст! Аз они Ӯст ҳар чӣ дар осмонҳову замин аст! Шуморо бар он сухан далеле нест. Чаро дар бораи Худо чизҳое мегӯед, ки ба он огоҳ нестед?'),
(1433, 10, 69, 'Бигӯ: «Онҳо, ки ба Худо дурӯғ мебанданд, наҷот намеёбанд!».'),
(1434, 10, 70, 'Бархӯрдорӣ аз дунё, он гоҳ бозгашташон ба сӯи Мо. Сипас ба ҷазои куфр варзиданашон азобе сахташон мечашонем.'),
(1435, 10, 71, 'Барояшон достони Нӯҳро бихон, он гоҳ, ки ба қавми худ гуфт: «Эй қавми ман, агар истодани ман дар миёни шумо ва ёдоварии оёти Худо бар шумо гарон меояд, ман бар Худо таваккал мекунам, корубори худ ва бутонатонро гирд оваред, чунон ки ҳеҷ чиз аз коре, ки мекунед, бар шумо пӯшида набошад ва ба душмании ман қадам ба пеш ниҳед ва маро мӯҳлат надиҳед!'),
(1436, 10, 72, 'Ва агар пушт кардед, ман аз шумо ҳеҷ музде нахостаам, ки музди ман бо Худост ва ман амр шудаам, ки аз таслимшудагон бошам!»'),
(1437, 10, 73, 'Дурӯғаш бароварданд ва Мо ӯву ҳамроҳонашро дар киштӣ бираҳонидем ва ононро ҷонишини пешиниён сохтем ва касонеро, ки оёти Моро дурӯғ мешумурданд, ғарқ кардем. Пас бингар, ки оқибати бимдодашудагон чӣ гуна буд!'),
(1438, 10, 74, 'Он гоҳ пас аз ӯ паёмбароне бар қавмашон фиристодем ва онон далелҳое равшан оварданд, вале мардум ба он чӣ пеш аз он дурӯғ бароварда буданд, имон оваранда набуданд. Бар дилҳои таҷовузкорон инчунин мӯҳр мениҳем!'),
(1439, 10, 75, 'Ва пас аз онҳо Мӯсову Ҳорунро ҳамроҳи оётамон бар Фиръавну бузургони қавмаш фиристодем. Ва онон саркашӣ карданд ва мардуме табаҳкор буданд.'),
(1440, 10, 76, 'Ва чун ҳақ аз назди Мо ба сӯи онҳо омад, гуфтанд, ки ин ҷодуе ошкор аст.'),
(1441, 10, 77, 'Мӯсо гуфт: «Оё сухани ҳақро, ки инак бар шумо нозил шудааст, ҷоду мехонед ва ҳол он ки ҷодугарон наҷот намеёбанд!»'),
(1442, 10, 78, 'Гуфтанд: «Оё омадаӣ, то моро аз он дин, ки падаронамонро бар он ёфтаем, дур созӣ, то дар замин сарварӣ ёбед? Вале мо ба шумо имон намеоварем!»'),
(1443, 10, 79, 'Фиръавн гуфт: «Ҳамаи ҷодугарони доноро назди ман биёваред!»'),
(1444, 10, 80, 'Чун ҷодугарон омаданд, Мӯсо гуфт: «Бияфканед, ҳар чиро, ки бояд бияфканед!»'),
(1445, 10, 81, 'Чун бияфканданд, Мӯсо гуфт: «Он чӣ шумо овардаед, ҷодуст ва Худо онро ботил хоҳад сохт, зеро Худо кори фасодкоронро ба салоҳ намеоварад!'),
(1446, 10, 82, 'Ва Худованд бо суханони худ ҳақро ба исбот мерасонад, агарчи гунаҳгоронро нохуш ояд».'),
(1447, 10, 83, 'Аз бими озори Фиръавн ва бузургони қавми ӯ, ҷуз гурӯҳе аз онҳо ба Мӯсо имон наёварданд. Ва Фиръавн дар замин бартарӣ меҷӯст ва ӯ аз таҷовузкорон буд.'),
(1448, 10, 84, 'Мӯсо гуфт: «Эй қавми ман, агар ба Худо имон овардаед ва таслими фармон ҳастед, ба Ӯ таваккал кунед!»'),
(1449, 10, 85, 'Гуфтанд; Бар Худо таваккал кардем, эй Парвардигори мо, моро мағлуби ин мардуми ситамкор макун,'),
(1450, 10, 86, 'ва ба раҳмати худ моро аз ин кофирон раҳоӣ бахш!»'),
(1451, 10, 87, 'Ба Мӯсо ва бародараш ваҳй кардем, ки барои қавми худ дар Миср хонаҳое муҳайё кунед ва хонаҳои худро ибодатгоҳ созед ва намоз гузоред ва мӯъминонро хушхабар деҳ.'),
(1452, 10, 88, 'Мӯсо гуфт: «Эй Парвардигори мо, ба Фиръавну бузургони қавмаш дар ин ҷаҳон зинату амвол додаӣ, эй Парвардигори мо, то дигаронро аз тариқи Ту гумроҳ кунанд. Эй Парвардигори мо, молҳояшонро нобуд соз ва дилашонро сахт кун! Имон намеоваранд, то он гоҳ, ки азоби дардоварро бингаранд».'),
(1453, 10, 89, 'Гуфт: «Хости шумо иҷобат шуд, собитқадам бошед ва ба тариқаи нодонон пайравӣ макунед».'),
(1454, 10, 90, 'Мо банӣ—Исроилро аз дарё гузаронидем. Фиръавну лашкариёнаш ба қасди ситаму таҷовуз ба таъқибашон пардохтанд. Чун Фиръавн ғарк мешуд, гуфт: «Имон овардам, ки ҳеҷ худованде ҷуз он ки банӣ—Исроил ба он имон овардаавд, нест ва ман аз таслимшудагонам».'),
(1455, 10, 91, 'Оё акнун? Ва ту пеш аз ин исён мекардӣ ва аз фасодкорон буди.'),
(1456, 10, 92, 'Имрӯз ҷисми туро ба хушкӣ мепартоем, то барон онон, ки пас аз ту мемонанд, ибрате бошӣ ва ҳол он ки бисёре аз мардум аз оёти Мо ғофиланд!'),
(1457, 10, 93, 'Албатта банӣ-Исроилро дар маконе некӯ ҷой додем ва аз покиҳо рӯзияшон додем ва то он ҳангом, ки соҳиби дониш нашуда буданд, зиддияте надоштанд. Парвардигори ту дар рӯзи қиёмат дар он чӣ зиддият мекарданд, миёнашон ҳукм хоҳад кард!'),
(1458, 10, 94, 'Агар дар он чӣ бар ту нозил кардаем, дар шубҳа ҳастӣ, аз онҳое, ки китоби осмонии пеш аз туро мехонанд, бипурс. Албатта ҳақ аст он чӣ аз ҷониби Парвардигорат бар ту нозил шудааст. Пас набояд дар шумори шубҳакукандагон бошӣ!'),
(1459, 10, 95, 'Ва аз онон, ки оёти Худоро дурӯғ мебароранд, мабош, ки дар зумраи зиёнкунандагон бошӣ.'),
(1460, 10, 96, 'Касоне, ки сухани Парвардигори ту дар бораи онон воҷиб шудааст, имон намеоваранд,'),
(1461, 10, 97, 'ҳарчанд ҳар гуна мӯъҷизае бар онон ошкор шавад, то он гоҳ, ки азоби дардоварро бингаранд.'),
(1462, 10, 98, 'Чаро мардуми ҳеҷ деҳае, ба ҳангоме ки имонашом нафъашон мсдод, имон наёварданд. магар қавми Юнус, ки чун имон оварданд, азоби хорӣ дар дунёро аз онон бардоштем ва то ҳангоме ки аҷалашон фаро расид, аз зиндагӣ бархурдорашон кардем.'),
(1463, 10, 99, 'Агар Парвардигори ту бихоҳад, ҳамаи касоне, ки дар рӯи заминанд, имон меоваранд. Оё ту мардумро маҷбур мекунӣ, ки имон биёваранд?'),
(1464, 10, 100, 'Бе хости Худо ҳеҷ касро нарасад, ки имон биёварад. Ва палидиро бар касоне, ки хиради худ ба кор намебаранд, муқаррар мекунад.'),
(1465, 10, 101, 'Бигӯ: «Бингаред, ки чӣ чизҳое дар осмонҳову замин аст!» Ва ин оёту ҳушдорҳо қавмеро, ки имон намеоваранд, нафъ намекунад!'),
(1466, 10, 102, 'Интизори рӯзеро мекашанд монанди рӯзҳое, ки пешиниёнашон мунтазираш буданд? Бигӯ; «Интизор бикашед, ки ман низ бо шумо мунтазирам!»'),
(1467, 10, 103, 'Он гоҳ паёмбаронамон ва касонеро, ки имон овардаанд, мераҳонем. Зеро бар Мо фариза аст, ки мӯъминонро бираҳонем.'),
(1468, 10, 104, 'Бигӯ: «Эй мардум, агар шумо дар дини ман шак доред, ман чизҳоеро, ки ба ҷои Оллоҳ мепарастед, намепарастам. Ман Оллоҳро мепарастам, ки шуморо мемиронад ва амр шудаам, ки аз мӯъминон бошам!»'),
(1469, 10, 105, 'Ва ба дини пок рӯй овар ва аз мушрикон мабош!'),
(1470, 10, 106, 'Ба ҷои Оллоҳ худоёнеро, ки на ба ту суд мерасонанд ва на зиён, махон. Агар чунин кунӣ, аз ситамкорон хоҳӣ буд.'),
(1471, 10, 107, 'Ва агар Худо ба ту зиёне бирасонад, ҷуз Ӯ касе дафъи он натавонад кард Ва агар барои ту хайре бихоҳад, ҳеҷ кас фазли Ӯро бознатавонад дошт. Фазли худро ба ҳар кас аз бандагонаш, ки бихоҳад, мерасонад ва Ӯ омурзандаву меҳрубон аст!'),
(1472, 10, 108, 'Бигӯ: «Эй мардум, паёми ростин аз ҷониби Парвардигоратон бароятон, фаро расид. Пас ҳар кас, ки ба роҳи рост ҳидоят ёбад, ҳидоят ба суди ӯст. Ва ҳар ки гумроҳ гардад, ба зиёни худ ба гумроҳӣ афтодааст. Ва ман ӯҳдадори шумо нестам».'),
(1473, 10, 109, 'Чизеро, ки ба ту ваҳй мешавад, пайравӣ кун ва сабр кун то Худо доварӣ кунад, ки Ӯ беҳтарини доварон аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Алиф, лом, ро. Китобест бо оёте устувору равшан аз ҷониби ҳакиме огоҳ,'),
(1475, 11, 2, 'ки ғайри Худои якторо напарастед, ман аз ҷониби Ӯ тарсонандаи шумо ва низ муждадиҳандае барои шумо ҳастам!'),
(1476, 11, 3, 'Ва низ аз Парвардигоратон бахшоиш бихоҳед ва ба даргоҳаш тавба кунед, то шуморо аз ризқе некӯ, то он гоҳ, ки муқаррар аст, бархурдорӣ диҳад. Ва ҳар шоистаи неъматро неъмат диҳад. Ва агар рӯйгардон шавед, бар шумо аз азоби рӯзи бузург бимнокам.'),
(1477, 11, 4, 'Бозгаштатон ба Худост ва Ӯ ба ҳар коре тавоност!'),
(1478, 11, 5, 'Огоҳ бош, ки инҳо сурат бармегардонанд, то рози дили худ пинҳон доранд, ҳол он ки ба он ҳангом, ки ҷомаҳои худ дар сар мекашанд. Худо ошкору ниҳонашонро медонад, зеро Ӯ ба рози дилҳо огоҳ аст.'),
(1479, 11, 6, 'Ҳар ҷунбандае дар рӯи замин мавҷуд аст, рӯзии ӯ бар ӯҳдаи Худост ва ҷои зисту маргашро медонад зеро ҳама дар китоби мубин омадааст.'),
(1480, 11, 7, 'Ӯст, ки осмонҳову заминро дар шаш рӯз офарид ва арши Ӯ бар рӯи об буд. То биёзмояд кадом як аз шумо ба амал некӯтар аст. Ва агар бигӯӣ, ки баъд аз марг зинда мешавед, кофирон гӯянд, ки ин ҷуз ҷодуии ошкор нест!'),
(1481, 11, 8, 'Ва агар чанд гоҳе азобашонро ба дер бияфканем, мепурсанд: «Чӣ чиз монеъи он шудааст?» Огоҳ бошед, чун азобашон фаро расад, онро бознагардонанд ва он чӣ масхарааш мекарданд, ононро дар бар хоҳад гирифт.'),
(1482, 11, 9, 'Агар ба инсон раҳмате бичашонем, он гоҳ аз ӯ бозаш гирем, ноумед мешавад ва ношукрӣ мекунад.'),
(1483, 11, 10, 'Ва агар пас аз сахтиву ранҷ неъмату осоише ба ӯ бичашонем, мегӯяд: «Ногувориҳо аз ман дур шудааст». Ва дар ин ҳол шодмон аст ва фахр мекунад,'),
(1484, 11, 11, 'ғайри касоне, ки сабр варзиданд ва корҳои некӯ карданд, ки омурзиш ва музди бузург аз они онҳост!'),
(1485, 11, 12, 'Шояд, ки баъзе аз чизҳоеро, ки бар ту ваҳй кардаем, тарк кунӣ ва ба он дилтанг бошӣ, ки мегӯянд: «Чаро ганҷе бар ӯ афканда намешавад? Ва чаро фариштае ҳамроҳи ӯ намеояд?» Ҷуз ин нест, ки ту бимдиҳандае беш нести ва Худост, ки корсози ҳар чизест.'),
(1486, 11, 13, 'Ё он ки мегӯянд, ки аз худ бармебофад ва ба дурӯғ ба Худо нисбаташ мекунад. Бигӯ: «Агар рост мегӯед, ҷуз Худо ҳар киро, ки тавонед, ба ёрӣ биталабед ва даҳ сура монанди он ба ҳам барбофта, биёваред.'),
(1487, 11, 14, 'Пас агар шуморо иҷобат накарданд, бидонед, ки Қуръон ба илми Худо нозил шуда ва низ ҳеҷ худое ҷуз Ӯ нест. Оё таслим мешавед?»'),
(1488, 11, 15, 'Онон, ки зиндагиву зинати ин дунёро бихоҳанд, пас ҳамаи музди кирдорашонро дар ин ҷаҳон медиҳем ва дар он нуқсоне намебинанд.'),
(1489, 11, 16, 'Инҳо касоне ҳастанд, ки дар охират ҷуз оташ насибе надоранд ва ҳар чӣ кардаанд, ночиз шавад ва ҳар чӣ ба ҷой оваранд, ботил аст!'),
(1490, 11, 17, 'Оё он кас, ки аз ҷониби Парвардигори худ далеле равшан дорад ва забонаш ба он гӯёст ва пеш аз ин китоби Мӯсо, ки худ пешвову раҳмате будааст, ба он гувоҳӣ дода, бо он кас, ки далеле надорад, баробар аст? Онҳо ба он китоби равшан имон меоваранд. Ва ҳар гурӯҳи дигаре, ки ба Ӯ кофир шавад, ҷойгоҳаш дар оташ аст. Дар он шак макун, ки ҳақ аст ва аз ҷониби Парвардигорат омадааст. Вале бештари мардум имон намеоваранд!'),
(1491, 11, 18, 'Чӣ кас ситамкортар аз он касест, ки ба Худо дурӯғ мебандад? Инҳоро ба Парвардигорашон арза хоҳанд дошт ва шоҳидон гувоҳӣ хоҳанд дод, ки инҳоанд, ки бар Парвардигорашон дурӯғ мебастаанд. Лаънати Худо бар ситамкорон бод;'),
(1492, 11, 19, 'онон, ки мардумро аз роҳи Худо бозмедоранд ва роҳи Худоро каҷиашро мехоҳанд ва ба охират имон надоранд,'),
(1493, 11, 20, 'Инҳо наметавонанд дар рӯи замин аз Худо бигурезанд ва ғайри Ӯ ҳеҷ ёваре надоранд, азобашон музоъаф (дучандон) мешавад. На тавони шунидан доштаанд ва на тавони дидан.'),
(1494, 11, 21, 'Инҳо ба хештан зиён расониданд ва он чиро, ки ба дурӯғ Худо мехонданд, аз даст додаанд.'),
(1495, 11, 22, 'Ба ночор дар охират зиёнкортаранд,'),
(1496, 11, 23, 'Касоне, ки имон оварданд ва корҳои шоиста кардаанд ва дар баробари Парвардигорашон сар хам намудаанд, аҳли биҳиштанд ва дар он ҷовидонанд.'),
(1497, 11, 24, 'Мисоли ин ду гурӯҳ мисли кӯру кар ва бинову шунавост. Оё ин ду ба мисол бо ҳам баробаранд? Чаро панд намегиред?'),
(1498, 11, 25, 'Ва Нӯҳро бар мардумаш ба паёмбарӣ фиристодем. Гуфт: «Ман барои шумо бимдиҳандае ошкорам,'),
(1499, 11, 26, 'ки ҷуз Худоӣ якторо напарастед! Зеро аз азоби рӯзи сахти қиёмат бар шумо бимнокам!»'),
(1500, 11, 27, 'Бузургони қавмаш, ки кофир буданд, гуфтанд: -Мо туро ҷуз инсоне монанди худ намебинем. Ва намебинем, ки ҷуз одамони паст ба ту пайравӣ кунанд. Ва намебинем, ки шуморо бар мо беҳтарие бошад, балки пиндорем, ки дурӯғ мегӯед».'),
(1501, 11, 28, 'Гуфт: «Эй қавми ман, чӣ мегӯед, агар аз Парвардигорам ҳуҷҷате ба ҳамроҳ дошта бошам ва Ӯ маро раҳмати хеш арзонӣ карда бошад ва шумо аз дидани он нотавон бошед, оё дар ҳоле, ки худ намехоҳед, шуморо ба зӯрӣ ба қабули Ӯ водорем?'),
(1502, 11, 29, 'Эй қавми ман, дар баробари таблиғи рисолати худ моле аз шумо наметалабам, Музди ман танҳо бо Худост. Онҳоеро, ки имон овардаанд, аз худ намеронам, онон бо Парвардигори худ дидор хоҳанд кард. Вале мебинам, ки шумо мардуме нодон ҳастед.'),
(1503, 11, 30, 'Эй қавми ман, агар онҳоро аз худ биронам, чӣ касе дар муқобили Худо маро ёрӣ хоҳад кард? Оё ҳақиқатро дарнамеёбед?'),
(1504, 11, 31, 'Ба шумо намегӯям, ки хазоини Худо дар назди ман аст, Ва илми ғайб ҳам намедонам. Ва намегӯям, ки фаришта ҳастам. Ва намегӯям, ки Худо ба онон, ки шумо ба ҳақорат дар онҳо менигаред, хайри худро ато накунад. Худо ба он чӣ дар дилҳои онҳост, огоҳтар аст. Агар чунин кунам, аз ситамкорон хоҳам буд».'),
(1505, 11, 32, 'Гуфтанд: «Эй Нӯҳ бо мо ҷидол кардӣ ва бисёр ҳам ҷидол кардӣ. Агар рост мегӯӣ, ҳар ваъдае, ки ба мо додаӣ, биёвар!»'),
(1506, 11, 33, 'Гуфт: «Худост, ки агар бихоҳад, он ваъдаро ошкор мекунад ва шумо аз он натавонед гурехт.'),
(1507, 11, 34, 'Ва агар Худо хоста бошад, ки гумроҳатон созад, агар ман бихоҳам шуморо панд диҳам, пандам суд нахоҳад кард. Ӯст Парвардигори шумо ва ҳама ба назди Ӯ бозмегардед».'),
(1508, 11, 35, 'Ё мегӯянд, ки онро ба Худо дурӯғ бастааст. Бигӯ: «Агар онро ба Худо дурӯғ баста бошам, гуноҳаш бар ман аст ва ман аз гуноҳе, ки мокунед, мубарро (пок) ҳастам.'),
(1509, 11, 36, 'Ва ба Нӯҳ ваҳй расид, ки аз қавми ту ҷуз он гурӯҳ, ки имон овардаанд, дигар имон нахоҳанд овард. Аз кирдори онон андӯҳгин мабош.'),
(1510, 11, 37, 'Киштиро зери назар ва илҳоми Мо бисоз ва дар бораи ин ситамкорон бо Ман сухан магӯй, ки ҳама ғарқ шаванд.'),
(1511, 11, 38, 'Нӯҳ киштӣ месохт ва ҳар бор, ки бузургони қавмаш бар ӯ мегузаштанд, масхарааш мекарданд. Мегуфт: «Агар шумо моро масхара мекунед, ба зуди мо ҳам монанди шумо масхараатон хоҳем кард.'),
(1512, 11, 39, 'Ба зуди хоҳед донист, ки азоб бар ки расад ва хораш созад ва азоби ҷовид бар кӣ фарояд!»'),
(1513, 11, 40, 'Чуи фармони Мо фаро омад ва танур ҷӯшид, гуфтем: «Аз ҳар нару мода дуто ва низ хонадони худро дар киштӣ биншон, — ғайри он касе, ки ҳукм дар борааш аз пеш содир шуда бошад — ва низ онҳоеро, ки ба ту имон овардаанд. Ва ҷуз андаке ба ӯ имон наёварда буданд».'),
(1514, 11, 41, 'Гуфт: «Бар он савор шавед, ки ба номи Худо ба рох афтад ва ба номи Худо биистад. Зеро Парвардигори ман бахшояндаву меҳрубон аст!'),
(1515, 11, 42, 'Киштӣ ононро дар миёни мавчҳое чун кӯҳ мебурд. Нӯҳ писарашро, ки дар гушае истода буд, нидо дод: «Эй писар, бо мо савор шав ва бо кофирон мабош!»'),
(1516, 11, 43, 'Гуфт: «Ман бар сари кӯҳе, ки маро аз об нигаҳ дорад, ҷо хоҳам гирифт». Гуфт: «Имрӯз ҳеҷ нигаҳдорандае аз фармони Худо нест, ғайри касеро, ки бар ӯ раҳм оварад». Ногаҳон мавҷ миёни он ду парда гашт ва ӯ аз ғарқ шудагон буд.'),
(1517, 11, 44, 'Ва гуфта шуд: «Эй замин, оби худ фурӯ бар ва эй осмон, бозист». Об фурӯ шуд ва кор ба поён омад ва киштӣ бар кӯҳи Ҷудӣ қарор гирифт ва нидо омад, ки лаънат бод бар мардуми ситамкора!'),
(1518, 11, 45, 'Нӯҳ Парвардигорашро нидо дод: «Эй Парвардигори ман, писарам аз хонадони ман буд ва ваъдаи Ту ҳақ аст ва нерӯмандтарини ҳукмкунандагон Ту Ҳастӣ!»'),
(1519, 11, 46, 'Гуфт: «Эй Нӯҳ, ӯ аз хонадони ту нест, ӯ амалест носолеҳ. Аз сари ноогоҳӣ аз Ман чизе махоҳ. Барҳазар медорам туро, ки аз мардуми нодон бошӣ».'),
(1520, 11, 47, 'Гуфт: «Эй Парвардигори ман, паноҳ мебарам ба Ту, агар аз сари ноогоҳӣ чизе бихоҳам ва агар маро набахшоӣ ва ба ман раҳмат наёварӣ, аз зиёнкардагон хоҳам буд».'),
(1521, 11, 48, 'Гуфта шуд: «Эй Нӯҳ ба саломат ва баракоте, ки бар туву онҳо, ки ҳамроҳи туанд, арзонӣ доштаем, фуруд ой. Ва умматҳое ҳастанд, ки онҳоро бархурдор месозем, он гоҳ дастхуши азоби дардовари Мо мешаванд».'),
(1522, 11, 49, 'Инҳо аз хабарҳои ғайб аст, ки бар ту ваҳй мекунем. Пеш аз ин на ту онҳоро медонистӣ ва на қавми ту. Пас сабр кун, зеро оқибати нек аз они парҳезгорон аст!'),
(1523, 11, 50, 'Ва бар қавми Од бародарашон Ҳудро фиристодем. Гуфт: «Эй қавми ман, Худои якторо бипарастед, шуморо ҳеҷ худое ҷуз Ӯ нест ва шумо дурӯғсозоне беш нестед.'),
(1524, 11, 51, 'Эй қавми ман, дар баробари паёми худ аз шумо музде наметалабам. Музди ман танхо бо он касест, ки маро офаридааст. Чаро аз рӯи хирад намеандешед?'),
(1525, 11, 52, 'Ва эй қавми ман, аз Парвардигоратон бахшоиш бихоҳед, он гоҳ бар остони Ӯ тавба кунед то боронро пай дар пай бар шумо фурӯ резад ва бар нерӯятон бияфзояд! Ва чун гунаҳкорон руй барматобед!»'),
(1526, 11, 53, 'Гуфтанд: «Эй худ, ту барои мо далели равшане наёвардаӣ ва мо ба гуфтори ту худоёни худро тарк намекунем ва ба ту имон намеоварем.'),
(1527, 11, 54, 'Ғайри ин нагӯем, ки баъзе аз худоёни мо ба ту озоре расондаанд». Гуфт: «Худоро гувоҳ мегирам ва шумо низ гувоҳ бошед, ки ман аз он чӣ ғайри Худои якто ба ширк мепарастед, безорам.'),
(1528, 11, 55, 'Ҳамагӣ ба ҳилагарӣ бар зидди ман бархезед ва маро мӯҳлат надиҳед.'),
(1529, 11, 56, 'Ман бар Худои якто, ки Парвардигори ман ва Парвардигори шумост, таваккал кардам. Ҳеҷ ҷунбандае, нест магар он ки зимоми ихтиёрашро Ӯ гирифтааст. Албатта Парвардигори ман бар сироти мустақим аст!'),
(1530, 11, 57, 'Агар, ҳам рӯйгардон шавед, ман рисолати худро ба шумо расонидам ва Парвардигори ман мардуми дигареро ҷонишини шумо хоҳад сохт ва ҳеҷ ба ӯ зиёне намерасонед. Зеро Парзардигори ман нигаҳбони ҳамаи чизҳост!»'),
(1531, 11, 58, 'Чун фармони Мо даррасид, ба бахшоиши хеш Ҳуд ва касонеро, ки ба ӯ имон оварда буданд, наҷот додем ва онҳоро аз азоби сахт раҳонидем.'),
(1532, 11, 59, 'Инҳо қавми Од буданд, ки оёти Парвардигорашонро инкор карданд ва паёмбаронашро нофармонӣ карданд ва ба фармони ҳар ҷаббори саркаш гардан ниҳоданд.'),
(1533, 11, 60, 'Ва дар ин дунёву рӯзи қиёмат гирифтори лаънат шуданд. Огоҳ бошед, ки қавми Од ба Парвардигорашон кофир шуданд, ки лаънат бод бар Од, қавми Ҳуд!'),
(1534, 11, 61, 'Ва бар қавми Самуд бародарашон Солеҳро фиристодем. Гуфт: «Эй қавми ман. Худой якторо бипарастед! Шуморо ҷуз Ӯ худое нест. Ӯст, ки шуморо аз замин падид овардааст ва хост, ки ободонаш доред. Пас бахшоиш хоҳед ва ба даргоҳаш тавба кунед. Албатта Парвардигори ман наздик аст ва дуъоҳоро иҷобат мекунад!»'),
(1535, 11, 62, 'Гуфтанд: «Эй Солеҳ пеш аз ин ба ту умед медоштем. Оё моро аз парастиши он чӣ падаронамон мепарастиданд, бозмедорӣ? Мо аз он чӣ моро ба он даъват мекунӣ, дар шаккем».'),
(1536, 11, 63, 'Гуфт: «Эй қавми ман, чӣ гӯед агар аз Парвардигорам ҳуҷҷате ба ҳамроҳ дошта бошам ва Ӯ маро раҳмати худ арзонӣ карда бошад, чӣ касе маро ёрӣ мекунад, агар аз фармонаш сарпечӣ кунам? Агар аз шумо фармон барам, ҷуз ба зиёни ман нахоҳед афзуд.'),
(1537, 11, 64, 'Эй қавми ман, ин модашутури Худованд аст ва нишонаест барои шумо. Бигзоредаш то дар замини Худо бичарад ва ба бадӣ маёзоредаш, ки ба зуди азоб шуморо фурӯ гирад».'),
(1538, 11, 65, 'Пас модашутурро қатл карданд. Гуфт: «Се рӯз дар хонаҳои худ аз зиндагӣ насибадор шавед ва ин ваъдаест холӣ аз дурӯғ».'),
(1539, 11, 66, 'Чун амри Мо фаро расид Солеҳро бо касоне, ки ба ӯ имон оварда буданд, ба раҳмати худ аз хории он рӯз наҷот бахшидем. Зеро Парвардигори ту тавонову пирӯзманд аст!'),
(1540, 11, 67, 'Ва ситамкоронро сайҳае (наърае) фурӯ гирифт ва дар хонаҳои худ бар ҷой мурданд.'),
(1541, 11, 68, 'Чунон ки гӯё ҳаргиз дар он диёр набудаанд. Огоҳ бошед, ки қавми Самуд ба Парвардигорашон кофир шуданд, ҳамоно, бар қавми Самуд лаънат бод!'),
(1542, 11, 69, 'Ба таҳқиқ расулони Мо барои Иброҳим мужда озарданд. Гуфтанд: «Салом!» Гуфт: «Салом!» Ва лаҳзае баъд гӯсолае бирён хозир овард.'),
(1543, 11, 70, 'Ва чун дид, ки ба он даст дароз намекунанд, ононро нохуш дошт ва дар дил аз онҳо бимнок шуд, гуфтанд: «Матарс, мо бар қавми Лут фиристода шудаем».'),
(1544, 11, 71, 'Занаш, ки истода буд, хандид. Ӯро ба Исҳоқ хушхабар додем ва пас аз Исҳоқ ба Яъқуб.'),
(1545, 11, 72, 'Зан гуфт: «Вой бар ман, оё дар ин пиронсолӣ мезоям ва ин шавҳари ман низ пир аст? Ин чизи аҷибест!»'),
(1546, 11, 73, 'Гуфтанд: «Оё аз фармони Худо таъаҷҷуб мекунӣ? Раҳмату баракати Худо бар шумо — аҳли ин хона арзонӣ бод. Ӯ сутуданӣ ва бузургвор аст!'),
(1547, 11, 74, 'Чун ваҳдат аз Иброҳим бирафт ва ӯро хушхабар омад, бо Мо дар бораи қавми Лут ба муҷодала бархост.'),
(1548, 11, 75, 'Иброҳим бурдбор аст ва раҳмдил аст ва фармонбардор аст!'),
(1549, 11, 76, 'Эй Иброҳим, аз ин сухан рӯй гардон! Фармони Парвардигорат омадааст ва бар онҳо азобе, ки ҳеҷ баргаште надорад, фурӯд хоҳад омад.'),
(1550, 11, 77, 'Чун расулони Мо назди Лут омаданд, Лут андӯҳгину дилтанг шуд ва гуфт: «Имрӯз рӯзи сахтест».'),
(1551, 11, 78, 'Ва қавмаш шитобон назди ӯ омаданд ва онон пеш аз ин муртакиби корҳои зишт мешуданд. Лут гуфт: «Эй қавми ман, инҳо духтарони ман ҳастанд. Барои Шумо покизатаранд. Аз Худо битарсед ва моро дар баробари меҳмононам ҳиҷил макунед. Оё марди хирадманде дар миёни шумо нест?»'),
(1552, 11, 79, 'Гуфтаь: д: «Ту худ медонӣ, ки моро ба духтарони ту ниёзе нест ва низ медонӣ, ки чӣ мехоҳем».'),
(1553, 11, 80, 'Лут гуфт: «Кош дар баробари шумо қудрате медоштам ва метавонистам ба такягоҳе устувор паноҳ бибарам!»'),
(1554, 11, 81, 'Гуфтанд: «Эй Лут, мо расулони Парвардигори ту ҳастем! Инҳо ҳаргиз ба ту даст нахоҳанд ёфт. Чун посе аз шаб бигзарад, хонадони худро берун бибар. Ва ҳеҷ як аз шумо рӯй барнагардонад, ҷуз занат, ки ба ӯ низ он чӣ ба онҳо расад, хоҳад расид. Ваъдаи онҳо субҳгоҳ аст. Оё субҳ наздик нест?»'),
(1555, 11, 82, 'Чун фармони Мо фаро расид, он ҷоро зеру забар кардем ва бар он шаҳр бороне аз сангҳое аз сиҷҷил" пай дар пай боронидем,'),
(1556, 11, 83, 'ки бар онҳо нишони Парвардигорат буд ва чунин азобе аз ситамкорон дур нест.'),
(1557, 11, 84, 'Ва бар Мадян бародарашон Шуъайбро фиристодем. Гуфт: «Эй қавми ман, Худои якторо бипарастед, шуморо ҳеҷ худое ҷуз Ӯ нест! Ва дар паймонаву тарозу нуқсон макунед! Инак шуморо дар неъмат мебинам. Ва аз рӯзе, ки азобаш шуморо фурӯ гирад, бимнокам.'),
(1558, 11, 85, 'Эй қавми ман, паймонаву тарозуро аз рӯи адл комил адо кунед ва ба мардум чизҳояшонро кам мадиҳед ва чун табаҳкорон дар замни фасод накунед.'),
(1559, 11, 86, 'Агар имон овардаед, он чӣ Худо боки мегузорад, бароятон беҳтар аст. Ва ман нигаҳбони шумо нестам».'),
(1560, 11, 87, 'Гуфтанд: «Эй Шуъайб, оё намозат ба ту фармон медиҳад, ки мо он чиро падаронамон мепарастиданд, тарк гӯем ё дар молҳои худ ончунон, ки худ мехоҳем, истифода накунем? Ба ростӣ ту марде бурдбору хирадманд ҳастӣ».'),
(1561, 11, 88, 'Гуфт: «Эй қавми ман, чӣ мегӯед, агар бо ман аз ҷониби Парвардигорам ҳуҷҷате бошад ва Ӯ маро ризқе некӯ ато карда бошад? Агар шуморо манъ мекунам, барои он нест, ки худ суде бибарам. То он ҷо, ки битавонам, қасде ҷуз ба салоҳ оварданатон надорам. Тавфиқи ман танҳо бо Худост. Ба Ӯ таваккал кардаам ва ба даргоҳи Ӯ рӯй меоварам».'),
(1562, 11, 89, 'Эй қавми ман, зиддият бо ман шуморо ба коре вонадорад то он чӣ бар қавми Нӯҳ ё қавми Ҳуд ё қавми Солеҳ ё дар ҳамин наздикӣ ба қавми Лут расид, ба шумо низ бирасад.'),
(1563, 11, 90, 'Аз Парвардигоратон омурзиш бихоҳед. Ба даргоҳаш тавба кунед, ки Парвардигори ман меҳрубону дӯстдоранда аст!»'),
(1564, 11, 91, 'Гуфтанд: «Эй Шуъайб, бисёре аз чизҳоеро, ки мегӯӣ, намефаҳмем, туро дар миёни худ нотавон мебинем, агар ба хотири қабилаат набуд, сангсорат мекардем ва ту бар мо пирӯзӣ наёбӣ».'),
(1565, 11, 92, 'Гуфт: «Эй қавми ман, оё қабилаи ман дар назди шумо аз Худо пирӯзмандтар аст? Оё Худоро паси пушти худ афкапдед? Ва ҳол он ки Парвардигори ман бар ҳар коре, ки мекунед, огоҳ аст!'),
(1566, 11, 93, 'Эй қавми ман, шумо ҳамчунон, ки ҳастед, ба кори худ машғул бошед ва ман ҳам ба кори худ машғул мешавам. Ба зуди хоҳед донист, ки он азоби хоркунанда бар чӣ касе фурӯд меояд ва чӣ касе дурӯғгӯст. Мунтазир бимонед, ман низ бо шумо мунтазир мемонам!»'),
(1567, 11, 94, 'Чун амри Мо фаро расид, Шуъайб ва касонеро, ки ба ӯ имон оварда буданд, ба раҳмати худ раҳонидем. Ва ситамкоронро фарёди сахте фурӯ гирифт ва дар хонаҳои хеш бар ҷой мурданд,'),
(1568, 11, 95, 'чунон ки гӯё ҳаргиз дар он диёр набудаанд. Огоҳ бош, лаънат бар мардуми Мадян бод, ҳамчунон ки лаънат бар қавми Самуд!'),
(1569, 11, 96, 'Ва Мо Мӯсоро ҳамроҳ бо оёту ҳуҷҷати ошкори худ фиристодем,'),
(1570, 11, 97, 'ба назди Фиръавн ва бузургони қавмаш. Аммо онон пайрави фармони Фиръавн шуданд. Ва фармони Фиръавн ба роҳи савоб роҳ наменамуд.'),
(1571, 11, 98, 'Дар рӯзи қиёмат пешопеши қавми худ биёяд ва ҳамаро ба оташ дароварад, ки дохилшудагонро бад ҷойгоҳест!'),
(1572, 11, 99, 'Лаънати инҷаҳонӣ ва лаънати рӯзи қиёматро аз пай доранд ва чӣ бад атое ба онон дода шудаст!'),
(1573, 11, 100, 'Инҳо ахбори деҳаҳоест, ки барои ту ҳикоят мекунем; деҳаҳое, ки баъзе ҳанӯз барпоянд ва баъзе вайрон.'),
(1574, 11, 101, 'Мо ба онҳо ситам накардем, балки худ ба худ ситам мекарданд. Ва чун амри Парвардигори ту фаро расид, худоёне, ки ба ҷои Худои якто мепарастиданд, ҳеҷ даркорашон наёмаданд ва ҷуз зиёнкорӣ чизе бар онон наяфзуданд.'),
(1575, 11, 102, 'Инчунин буд ба азоб гирифтани Парвардигори ту, вақте ки бихоҳад деҳае ситамкорро ба азоб кашад. Ба азоб гирифтани Ӯ азобе сахт дардовар аст!'),
(1576, 11, 103, 'Дар инҳо барои касоне, ки аз азоби охират бимноканд, ибратест, дар он рӯз, ки мардум гирд оварда шаванд ва он рӯз, ки мардумро дар он ҳозир оваранд!'),
(1577, 11, 104, 'Ва ҷуз то андак муддате ба дераш намегузорем.'),
(1578, 11, 105, 'Рӯзе, ки чун биёяд, ҳеҷ кас ҷуз ба фармони Ӯ сухан нагӯяд ва мардумон баъзе бадбахт бошанд ва баъзе некбахт.'),
(1579, 11, 106, 'Аммо бадбахтон дар оташанд ва мардумонро дар он ҷо нолае зору фарёде сахт бувад.'),
(1580, 11, 107, 'Ва то осмонҳову замин боқӣ ҳастанд, дар он ҷо ҷовидона бимонанд, магар он чи Парвардигорат бихоҳад, зеро Парвардигори ту ҳар чӣ хоҳад, ҳамон кунад!'),
(1581, 11, 108, 'Аммо некбахтон то осмонҳову замин боқӣ ҳастанд, дар биҳишт ҷовидон бимонанд; ғайри он чӣ Парвардигорат бихоҳад. Атои Ӯ ҳеҷ қатъ намешавад.'),
(1582, 11, 109, 'Аз он чӣ инҳо мепарастанд, ба шакку шубҳа мабош. Ҷуз ба он гуна, ки падаронашон пеш аз ин мепарастиданд, намепарастанд. Мо насиби ононро бе ҳеҷ камукост адо хоҳем кард!'),
(1583, 11, 110, 'Ба Мӯсо китоб додем. Дар он китоб ихтилоф шуд. Агар на ҳукме буд, ки пеш аз ҷониби Парвардигорат содир шуда буд, миёнашон доварӣ шуда буд, ки онҳо дар он китоб сахт дар шубҳаанд.'),
(1584, 11, 111, 'Ва Парвардигори ту ҷазои аъмоли ҳамаро ба тамомӣ хоҳад дод ва Худо ба корҳое, ки мекунанд, огоҳ аст!'),
(1585, 11, 112, 'Ҳамроҳ бо онон, ки бо ту рӯ ба Худо кардаанд, ҳамчунон, ки амр шудаӣ, собитқадам бош. Ва аз ҳад нагузаред, ки Ӯ ба ҳар коре, ки мекунед, биност!'),
(1586, 11, 113, 'Ба ситамкорон майл накунед, ки оташ бисӯзонадатон. Шуморо ғайри Худо ҳеҷ дӯсте нест ва касе ёриатон накунад!'),
(1587, 11, 114, 'Намоз бигузор дар оғозу анҷоми рӯз ва соъате аз шаб. Зеро некиҳо бадиҳоро аз миён мебаранд. Ин пандест барои пандгирандагон.'),
(1588, 11, 115, 'Сабр кун, зеро Худованд музди некӯкоронро табоҳ намесозад!'),
(1589, 11, 116, 'Чаро дар миёни мардумоне, ки пеш аз шумо буданд, ғайри андаке, ки аз он миён наҷоташон додем, хирадмандоне набуданд, то мардумонро аз фасод кардан дар замин боздоранд? Золимон аз паи осудагиву лаззати дунё рафтанд ва гунаҳкор буданд.'),
(1590, 11, 117, 'Парвардигори ту ҳеҷ деҳаеро, ки мардумаш некӯкор бошанд, ба ситам ҳалок нахоҳад сохт.'),
(1591, 11, 118, 'Ва агар Парпардигори ту хоста буд, ҳамаи мардумро як уммат карда буд, вале хамеша гуногун хоҳанд буд,'),
(1592, 11, 119, 'магар онҳое, ки Парвардигорат бар онҳо раҳмат оварда ва онҳоро барои ҳамин биёфаридааст. Ва сухани Парвардигори ту бар ин муқаррар шуда, ки ҷаҳаннамро аз ҳамаи ҷинну инс пур мекунам.'),
(1593, 11, 120, 'Ҳар хабаре аз ахбори паёмбаронро бароят ҳикоят мекунем, то туро қавидил гардонем. Ва дар ин китоб бар ту сухани ҳақ ва барои мӯъминон панду насиҳат нозил шудааст.'),
(1594, 11, 121, 'Ба касоне, ки имон намеоваранд, бигӯ: «Шумо ба чӣ тарз, ки хоҳед, амал кунед, мо низ амал мекунем.'),
(1595, 11, 122, 'Шумо интизор бикашед, мо низ мунтазир мемонем!»'),
(1596, 11, 123, 'Ва аз они Худост ниҳони осмонҳову замин ва ба Ӯ бозгардонда мешавад ҳамаи корҳо. Ӯро бипараст ва бар Ӯ таваккал кун, ки Парвардирорат аз он чӣ ба ҷой меоваред, ғофил нест!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Алиф, лом, ро. Инҳост оёти китоби равшангар.'),
(1598, 12, 2, 'Мо Қуръоне арабӣ нозилаш кардаем, бошад, ки шумо дарёбед!'),
(1599, 12, 3, 'Бо ин Қуръон, ки ба ту ваҳй кардаем, беҳтарин достонро бароят ҳикоят мекунем, ки ту аз ин пеш аз бехабарон будаӣ.'),
(1600, 12, 4, 'Он гоҳ, ки Юсуф ба падари худ гуфт: «Эй падар, ман дар хоб ёздаҳ ситора ва хуршед ва моҳ дидам, дидам, ки саҷдаам мекунанд».'),
(1601, 12, 5, 'Гуфт: «Эй писари хурдакам, хобатро барои бародаронат ҳикоят макун, ки туро ҳилае меандешанд, Зеро шайтон одамиёнро душмане ошкор аст!'),
(1602, 12, 6, 'Ва инчунин Парвардигорат туро бармегузинад ва таъбири хоб меомӯзад ва ҳамчунон, ки неъмати худро пеш аз ин бар падарони ту Иброҳиму Исҳоқ комил карда буд, бар туву хонадони Яъқуб ҳам комил мекунад, ки Парвардигорат донову ҳаким аст!»'),
(1603, 12, 7, 'Дар достони Юсуф ва бародаронаш барои онон, ки аз он пурсидаанд, ибратҳост.'),
(1604, 12, 8, 'Он гоҳ, ки гуфтанд: «Юсуф ва бародараш назди падарамон маҳбубтар аз мо ҳастанд, ҳол он ки мо худ гурӯҳе нерӯмандем. Падарамон дар гумроҳии ошкорест».'),
(1605, 12, 9, '«Юсуфро бикушед ё дар сарзамине дигараш биандозед, то падар хоси шумо гардад ва аз он пас мардуме шоиста гардед».'),
(1606, 12, 10, 'Яке аз онҳо гуфт: «Агар мехоҳед коре кунед, Юсуфро накушед; дар қаъри торики чоҳаш бипартоед, то корвоне ӯро бигирад».'),
(1607, 12, 11, 'Гуфтанд: Эй падар, чӣ шуд, ки моро бар Юсуф боварӣ намекунӣ, ҳол он ки мо хайрхоҳи ӯ ҳастем?'),
(1608, 12, 12, 'Фардо ӯро бо мо бифирист, то бигардаду бози кунад ва мо нигаҳдораш ҳастем».'),
(1609, 12, 13, 'Гуфт: «Агар ӯро бибаред, ғамгин мешавам ва метарсам, ки аз ӯ ғофил шавед ва гург ӯро бихӯрад».'),
(1610, 12, 14, 'Гуфтанд: «Бо ин гурӯҳи нерӯманд, ки мо ҳастем, агар гург ӯро бихӯрад, аз зиёнкорон хоҳем буд».'),
(1611, 12, 15, 'Чун ӯро бурданд ва қасд карданд, ки дар қаъри торики чоҳаш бияфкананд, ба ӯ ваҳй кардем, ки онҳоро аз ин корашон огоҳ хоҳӣ сохт ва худ надонанд.'),
(1612, 12, 16, 'Шабҳангом гирён назди падарашон боз омаданд.'),
(1613, 12, 17, 'Гуфтанд: «Эй падар, мо ба асп тохтан рафта будем ва Юсуфро назди матоъи худ гузошта будем, гург ӯро хӯрд. Ва ҳарчанд ҳам, ки рост бигӯем, ту сухани моро бовар надорӣ».'),
(1614, 12, 18, 'Ҷомаашро, ки ба хуни дурӯғин оғушта буд, оварданд, гуфт: «Нафси шумо кореро дар назаратон биёроста аст. Акнун барои ман сабри ҷамил беҳтар аст ва Худост, ки дар ин бора аз ӯ ёрӣ бояд хост».'),
(1615, 12, 19, 'Корвоне омад. Обоварашонро фиристоданд. Сатил фурӯ кард. Гуфт: «Муждагонӣ, ин писарест». Ӯро чун матоъе пинҳон сохтанд ва Худо ба коре, ки мекарданд, огоҳ буд.'),
(1616, 12, 20, 'Ӯро ба баҳои андак, ба чанд дирҳам фурӯхтанд, ки ҳеҷ рағбате ба ӯ надоштанд.'),
(1617, 12, 21, 'Касе аз мардуми Миср, ки ӯро харида буд, ба занаш гуфт: «То дар ин ҷост, гиромияш бидор, шояд ба мо суде бирасонад, ё ӯро ба фарзандӣ бипазирем». Ва инчунин Юсуфро дар замин ҷой додем, то ба ӯ таъбири хоб омӯзем. Ва Худо бар кори хеш ғолиб аст. Вале бештари мардум намедоканд!'),
(1618, 12, 22, 'Ва чун болиғ шуд, ҳикмату донишаш додем ва некӯкоронро инчунин мукофот медиҳем.'),
(1619, 12, 23, 'Ва он зан, ки Юсуф дар хонааш буд, дар паи комҷӯӣ аз Юсуф мебуд. Ва дарҳоро басту гуфт: «Бишитоб». Гуфт: «Паноҳ мебарам ба Худо, Ӯ парваронандаи ман аст ва маро обрӯи некӯ дода ва ситамкорон наҷотёфта намешаванд».'),
(1620, 12, 24, 'Он зан майли ӯ кард. Ва агар бурҳони Парвардигорашро надида буд, ӯ низ майли он зан мекард. Чунин кардем, то бадиву зишткориро аз вай бозгардонем. Зеро ӯ аз бакдагони покдили Мо буд!'),
(1621, 12, 25, 'Ҳар ду ба ҷониби дар давиданд ва зан ҷомаи ӯро аз пас бидарид. Ва шӯи он занро наздики дар диданд. Зан гуфт: «Ҷазои касе, ки бо зани ту қасди баде дошта бошад, чист, ҷуз ин ки ба зиндон афтад ё ба азобе дардовар гирифтор ояд?»'),
(1622, 12, 26, 'Юсуф гуфт: «Ӯ дар паи комҷӯӣ аз ман буд ва маро ба худ хонд». Ва яке аз касони зан гувоҳӣ дод, ки агар ҷомааш аз пеш даридааст, зан рост мегӯяд ва ӯ дурӯғгӯст.'),
(1623, 12, 27, 'Ва агар ҷомааш аз пас даридааст, зан дурӯғ мегӯяд ва ӯ ростгӯст.'),
(1624, 12, 28, 'Чун дид ҷомааш аз пас даридааст, гуфт: «Ин аз макри шумо занон аст, ки макри шумо занон макре бузург аст!'),
(1625, 12, 29, 'Эй Юсуф, забони худ нигоҳ дор ва эй зан, аз гуноҳи худ бахшоиш бихоҳ, ки ту аз хатокоронӣ».'),
(1626, 12, 30, 'Закони шаҳр гуфтанд: «Зани Азиз дар паи комҷӯӣ аз ғуломи худ шудааст ва шефтаи ӯ гашта аст. Мо вайро дар гумроҳии ошкоро мебипем».'),
(1627, 12, 31, 'Чун афсунашонро шунид, наздашон кас фиристод ва маҷлисе тартиб дод ва ба ҳар як корде дод ва гуфт: «Берун ой, то туро бингаранд». Чун ӯро диданд, бузургаш шумурданд ва дасти хеш бибуриданд ва гуфтанд: «Маъозаллоҳ ин одами нест, ин фариштае бузургвор аст!»'),
(1628, 12, 32, 'Гуфт: «Ин ҳамон аст, ки маро дар боби ӯ маломат мекардед. Ман дар паи комҷӯӣ аз ӯ будам ва ӯ худро нигоҳ дошт. Агар он чӣ фармонаш медиҳам, накунад, ба зиндон хоҳад афтод ва хор хоҳад шуд».'),
(1629, 12, 33, 'Гуфт: «Эй Парвардигори ман, барои ман зиндон дӯст доштанитар аст аз он чӣ маро ба он мехонанд ва агар макри ин занонро аз ман нагардонӣ, ба онҳо майл мекунам ва дар шумори нодонон дармеоям».'),
(1630, 12, 34, 'Парвардигораш дуъояшро иҷобат кард ва макри занон аз ӯ дур кард. Зеро Худо шунавову доност!'),
(1631, 12, 35, 'Пас бо он нишонаҳо, ки дида буданд, тасмим карданд, ки чанде ба зиндонаш бияфкананд.'),
(1632, 12, 36, 'Ду ҷавон низ бо ӯ ба зиндон афтоданд. Яке аз он ду гуфт: «Дар хоб худро дидам, ки ангур мефишорам». Дигаре гуфт: «Худро дидам, ки нон бар сар ниҳода мебарам ва паррандагон аз он мехӯранд. Моро аз таъбири он огоҳ кун, ки аз некӯкоронат мебинем».'),
(1633, 12, 37, 'Гуфт: «Таъоми рӯзонаи шумо ҳанӯз наомада бошад, ки пеш аз он шуморо аз таъбири он хобҳо, чунон ки Парвардигорам ба ман омӯхтааст, хабар медиҳам. Ман дини мардумеро, ки ба Худои якто имон надоранд ва ба рӯзи қиёмат кофиранд, тарк кардаам.'),
(1634, 12, 38, 'Ман пайрави дини падаронам Иброҳиму Исҳоқу Яъқуб ҳастам ва моро нашояд, ки ҳеҷ чизро шарики Худо қарор диҳем. Ин фазилатест, ки Худо бар мову бар мардуми дигар арзонӣ доштааст, вале бештари мардум ношукранд.'),
(1635, 12, 39, 'Эй ду зиндонӣ, оё худоёни пароканда беҳтар аст ё Оллоҳ — он Худованди яктои ғолиб бар ҳамагон?'),
(1636, 12, 40, 'Намепарастед ғайри Худои якто магар бутонеро, ки худ ва падаронатон онҳоро ба номҳое хондаед ва Худо ҳуҷҷате бар исботи онҳо нозил накардааст. Ҳукм ҷуз ҳукми Худо нест. Фармон додааст, ки ҷуз ӯро напарастед. Ин аст дини росту устувор, вале бештари мардум намедонанд.'),
(1637, 12, 41, 'Эй ду зиндонӣ, аммо яке аз шумо барои хоҷаи худ шароб резад, аммо дигареро бар дор кунанд ва паррандагон сари ӯ бихӯранд. Коре, ки дар бораи он назар мехостед, ба поён омадааст»,'),
(1638, 12, 42, 'Ба яке аз он ду, ки медоннст, раҳо мешавад, гуфт: «Маро назди хоҷаи худ ёд кун!» Аммо шайтон аз хотираш дур кард, ки пеши мавлояш аз ӯ ёд кунад ва чанд сол дар зиндон бимонд.'),
(1639, 12, 43, 'Подшоҳ гуфт: «Дар хоб ҳафт гови фарбеҳро дидам, ки онҳоро ҳафт гови лоғар мехӯранд ва ҳафт хушаи сабз дидам ва ҳафт хушаи хушк. Эй хосагони ман, хоби маро таъбир кунед, агар таъбири хоб медонед».'),
(1640, 12, 44, 'Гуфтанд: «Инҳо хобҳои ошуфтааст ва моро ба таъбири ин хобҳо огоҳӣ нест».'),
(1641, 12, 45, 'Яке аз он ду, ки раҳо шуда буд ва пас аз муддате ба ёдаш омада буд, гуфт: «Ман шуморо аз таъбири он огоҳ мекунам. Маро назди ӯ бифиристед».'),
(1642, 12, 46, '«Эй Юсуф, эй марди ростгӯй, барои мо таъбир кун, ки ҳафт гови фарбеҳро ҳафт гови лоғар мехӯранд ва ҳафт хушаи сабзу ҳафт хушаи хушк. Бошад, ки ман назди мардум бозгардам ва онон огоҳ гарданд».'),
(1643, 12, 47, 'Гуфт: «Ҳафт сол пай дар пай бикоред ва ҳар чӣ медаравед, ҷуз андаке, ки мехӯред, бо хуша анбор кунед.'),
(1644, 12, 48, 'Аз он пас ҳафт сол кахтӣ меояд ва дар он ҳафт сол он чӣ барояшон ҷамъ кардаанд, бихӯранд ғайри андаке, ки нигоҳ медоред.'),
(1645, 12, 49, 'Пас аз он соле ояд, ки мардумонро борон диҳанд ва дар он сол афшурданиҳоро мефушуранд».'),
(1646, 12, 50, 'Подшоҳ гуфт: «Назди манаш биёваред». Чун фиристода назди ӯ омад, Юсуф гуфт: «Назди хоҷаат бозгард ва бипурс: Ҳикояти он занон, ки дастҳои худро буриданд, чӣ буд? Ки Парвардигори ман ба макрашон огоҳ аст!»'),
(1647, 12, 51, 'Гуфт: «Эй занон, он гоҳ ки хостори тани Юсуф мебудед, ҳикояти шумо чӣ буд?» Гуфтанд: «Паноҳ бар Худо! Ӯро ҳеҷ гунаҳкор намедонем». Зани Азиз гуфт: «Акнун ҳақ ошкор шуд. Ман дар паи комҷӯӣ аз ӯ мебудам ва ӯ дар зумраи ростгӯён аст!»'),
(1648, 12, 52, '«Чунин шуд то бидонад, ки ман дар ғайбаташ ба ӯ хиёнат накардаам ва Худо ҳилаи хоинонро ба мақсад намерасоздад.'),
(1649, 12, 53, 'Ман худамро бегуноҳ намедонам, зеро нафс одамиро ба бадӣ фармон медиҳад. Магар Парвардигори ман бибахшояд, зеро Парвардигори ман омурзандаву меҳрубон аст!»'),
(1650, 12, 54, 'Подшоҳ гуфт: «Ӯро назди ман биёваред то ҳамнишини хоси худ гар донам». Ва чун бо ӯ сухан гуфт, гуфт; «Ту аз имрӯз назди мо соҳибмартаба ва боваринок ҳастӣ».'),
(1651, 12, 55, 'Гуфт: «Маро бар хазинаҳои ин сарзамин муқаррар кун, ки ман нигаҳбоне доноям».'),
(1652, 12, 56, 'Инчунин Юсуфро дар он сарзамин обрӯ додем. Ҳар ҷо, ки мехост, ҷой мегирифт. Раҳмати худро ба ҳар кас, ки бихоҳем, арзонӣ медорем ва музди некӯкоронро барбод намекунем.'),
(1653, 12, 57, 'Ва албатта музди онҷаҳонӣ барои касоне, ки имоп овардаанд ва парҳезгорӣ мекунанд, беҳтар аст.'),
(1654, 12, 58, 'Бародарони Юсуф омаданд ва бар ӯ дохил шуданд, онҳоро шинохт ва онҳо нашинохтандаш.'),
(1655, 12, 59, 'Чун борҳояшонро муҳайё сохт, гуфт: «Бародари падариятонро низ назди ман биёваред, оё намебинед, ки паймонаро комил адо мекунам ва беҳтарин мизбонам?'),
(1656, 12, 60, 'Агар ӯро назди ман наёваред, паймонае назди ман нахоҳед дошт ва ба ман наздик машавед».'),
(1657, 12, 61, 'Гуфтанд: «Мо ӯро ба исрор аз падар хоҳем хост ва ин корро хоҳем кард».'),
(1658, 12, 62, 'Ва ба мардони худ гуфт: «Сармояашонро дар борҳояшон биниҳед, бошад, ки чун назди касонашон бозгарданд ва онро биёбанд, боз оянд».'),
(1659, 12, 63, 'Чун назди падар бозгаштанд, гуфтанд: «Эй падар, паймона аз мо манъ намуданд. Бародарамонро бо мо бифирист, то паймона бозгирем. Мо нигаҳдори ӯ ҳастем».'),
(1660, 12, 64, 'Гуфт: «Оё ӯро ба шумо бисупорам, ҳамчунон ки бародарашро пеш аз ин ба шумо супурдам?» Худо беҳтарин нигаҳдор аст ва Ӯст меҳрубонтарини меҳрубонон!'),
(1661, 12, 65, 'Чун бори худро кушуданд, диданд, ки сармояашонро пас додаанд. Гуфтанд: «Эй падар, дар талаби чӣ ҳастем? Ин сармояи мост, ки ба мо пас додаанд. Барои касони худ ғалла биёварем ва бародарамонро ҳифз кунем ва бори шутуре афзун гирем, ки он чӣ дорем, андак аст».'),
(1662, 12, 66, 'Гуфт: «Ҳаргиз ӯро бо шумо намефиристам, то бо ман ба номи Худо паймоне бибандед, ки назди манаш бозмеоваред. Магар он ки ҳама гирифтор шавед». Чун бо ӯ аҳд карданд, гуфт: «Худо бар он чӣ мегӯем, гувоҳ аст!»'),
(1663, 12, 67, 'Гуфт: «Эй писарони ман, аз як дарвоза дохил нашавед; аз дарвозаҳои гуногун дохил шавед. Ва ман қазои Худоро аз сари шумо дафъ натавонам кард ва ҳеҷ фармоне ғайри фармони Худо нест. Бар Ӯ таваккал кардам ва таваккалкунандагон бар Ӯ таваккул кунанд».'),
(1664, 12, 68, 'Чун аз ҷое, ки падар фармон дода буд, дохил шуданд, ин кор дар баробари иродаи Худо судашон набахшид. Танҳо ҳоҷате дар замири Яъқуб буд, ки онро ошкор сохт, зеро ӯро илме буд, ки Худ ба ӯ омӯхта будем, вале бештари мардум намедонанд.'),
(1665, 12, 69, 'Чун бар Юсуф дохил шуданд, бародарашро назди худ ҷой дод. Гуфт: «Ман бародари ту ҳастам. Аз коре, ки инҳо кардаанд, андӯҳгин мабош».'),
(1666, 12, 70, 'Чун борҳояшонро муҳайё кард, ҷомро дар бори бародар ниҳод. Он гоҳ мунодӣ (садодиҳанда) садо дод: «Эй корвониён, шумо дуздонед».'),
(1667, 12, 71, 'Корвониён назди онҳо бозгаштанд ва гуфтанд: «Чӣ гум кардаед?»'),
(1668, 12, 72, 'Гуфтанд: «Ҷоми подшоҳро. Ва ҳар ки биёварадаш, ӯро бори шутурест ва ман кафилӣ мекунам».'),
(1669, 12, 73, 'Гуфтанд: «Худоро, шумо худ медонед, ки мо фасод кардан ба ин сарзамин наёмадаем ва дузд набудаем».'),
(1670, 12, 74, 'Гуфтанд: «Агар дурӯғ гуфта бошед, ҷазои дузд чист?»'),
(1671, 12, 75, 'Гуфтанд: «Ҷазояш ҳамон касест, ки дар бори ӯ ёфта шавад. Пас ӯ худ ҷазои амали худ аст ва мо гунаҳкоронро чунин ҷазо диҳем».'),
(1672, 12, 76, 'Пеш аз бори бародар бар бори онҳо кофтанро сар кард, он гоҳ аз бори бародараш берун овард. Ҳилае инчунин ба Юсуф омӯхтем. Дар дини он подшоҳ гирифтани бародар хаққи ӯ набуд, чизе буд, ки Худо мехост. Ҳар касро, ки бихоҳем, ба дараҷоте боло мебарем ва болои ҳар доное донотарест.'),
(1673, 12, 77, 'Гуфтанд: «Агар ӯ дуздӣ карда, бародараш низ пеш аз ин дуздӣ карда буд». Юсуф ҷавоби он сухан дар дил пинҳон дошт ва ҳеҷ изхор накард ва гуфт: «Шумо дар вазъе бадтар ҳастед ва Худо ба бӯҳтоне, ки мезанед, огоҳтар аст!»'),
(1674, 12, 78, 'Гуфтанд: «Эй азиз, ӯро падарест солхӯрда. Яке аз моро ба ҷои ӯ бигир, ки аз некӯкоронат мебинем».'),
(1675, 12, 79, 'Гуфт: «Худо накунад, ки ҷуз он касро, ки бори худ назди ӯ ёфтаем, бигирем. Агар чунин кунем, аз ситамкорон хоҳем буд».'),
(1676, 12, 80, 'Чун аз ӯ ноумед шуданд, барои машварат ба каноре рафтанд ва бузургтаринашон гуфт: «Оё намедонед, ки падаратон аз шумо ба номи Худо паймон гирифта ва пеш аз ин низ дар ҳаққи Юсуф кӯтоҳӣ кардаед? Ман аз ин сарзамин берун намеоям, то падар маро рухсат диҳад ё Худо дар бораи ман довари кунад, ки Ӯ беҳтарини доварон аст.'),
(1677, 12, 81, 'Назди падар бозгардед ва бигӯед: «Эй падар, писарат дуздӣ кард ва мо ҷуз ба он чӣ медонистем, шаҳодат надодем ва аз ғайб низ огоҳ нестем.'),
(1678, 12, 82, 'Аз шаҳре, ки дар он будаем ва аз корвоне, ки ҳамроҳаш омадаем бипурс, ки мо рост мегӯем».'),
(1679, 12, 83, 'Гуфт: «На, нафси шумо кореро дар назаратон биёрост ва маро сабри ҷамил беҳтар аст. Шояд Худо ҳамаро ба ман бозгардонад, ки Ӯ донову ҳаким аст!»'),
(1680, 12, 84, 'Рӯи худ аз онҳо бигардониду гуфт: «Эй андӯҳо бар Юсуф». Ва чашмонаш аз ғам сапедӣ гирифт ва ҳамчунон андӯҳи худ фурӯ мехӯрд.'),
(1681, 12, 85, 'Гуфтанд: «Ба Худо савганд пайваста Юсуфро ёд мекунӣ, то бемор гардӣ ё бимирӣ».'),
(1682, 12, 86, 'Гуфт; «Ҷуз ин нест, ки шарҳи андӯҳи худ танҳо бо Худо мегӯям. Зеро он чӣ ман аз Худо медонам, шумо намедонед!'),
(1683, 12, 87, 'Эй писарони ман, бираведу Юсуф ва бародарашро биҷӯед ва аз раҳмати Худо ноумед машавед. Зеро танҳо кофирон аз раҳмати Худо маъюс мешаванд».'),
(1684, 12, 88, 'Чун бар Юсуф дохил шуданд, гуфтанд: «Эй азиз, мову касонамон ба гуруснагӣ афтодаем ва бо сармояи андак омадаем, паймонаи моро тамом адо кун ва бар мо садақа бидеҳ зеро Худо садақадиҳандагонро дӯст дорад!»'),
(1685, 12, 89, 'Гуфт: «Медонед, ки аз рӯи нодонӣ бо Юсуф ва бародараш чӣ кардед?»'),
(1686, 12, 90, 'Гуфтанд: «Оё ба ҳақиқат ту Юсуфӣ?» Гуфт: «Ман Юсуфам ва ин бародари ман аст ва Худо ба мо неъмат дод. Зеро ҳар кас, ки парҳезгорӣ кунад ва сабр намояд, Худо муздашро нобуд намесозад».'),
(1687, 12, 91, 'Гуфтанд: «Ба Худо савганд, ки худо туро бар мо фазилат дод ва мо хатокор будем».'),
(1688, 12, 92, 'Гуфт: «Имрӯз шуморо сарзаниш набояд кард; Худо шуморо мебахшояд, ки Ӯ меҳрубонтарини меҳрубонон аст!'),
(1689, 12, 93, 'Ин ҷомаи маро бибаред ва бар рӯи падарам андозед то бино гардад. Ва ҳамаи касони худро назди ман биёваред».'),
(1690, 12, 94, 'Чун корвон ба роҳ афтод, падарашон гуфт: «Агар маро девона нахонед, бӯи Юсуф ҳис мекунам».'),
(1691, 12, 95, 'Гуфтанд: «Ба Худо савганд, ки ту дар ҳамон гумроҳии деринан худ ҳастӣ!»'),
(1692, 12, 96, 'Чун муждадиҳанда омад ва ҷома бар рӯи ӯ андохт, бино гашт. Гуфт: «Оё нагуфтаматон, ки он чӣ ман аз Худо медонам, шумо намедонед?»'),
(1693, 12, 97, 'Гуфтанд: «Эй падар, барои гуноҳони мо омурзиш бихоҳ, ки мо хатокор будаем».'),
(1694, 12, 98, 'Гуфт; «Аз Парвардигорам барои шумо бахшоиш хоҳам хост, Ӯ бахшояндаву меҳрубон аст!»'),
(1695, 12, 99, 'Чун бар Юсуф дохил шуданд, падару модарро ба оғӯш кашиду гуфт: «Ба Миср дароед, ки агар Худо бихоҳад, дар амон хоҳед буд!»'),
(1696, 12, 100, 'Падару модарро бар тахт баровард ва ҳама дар баробари ӯ ба саҷда даромаданд. Гуфт: «Эй падар, ин аст таъбири он хоби ман, ки инак Парвардигорам онро рост баровард. Ва чӣ қадар ба ман некӣ кардааст, он гоҳ, ки маро аз зиндон бираҳонид ва пас аз он, ки шайтон миёни ману бародаронам фасод карда буд, шуморо аз бодия ба ин ҷо овард. Парвардигори ман ба ҳар чӣ ирода кунад, дақиқ аст, ки Ӯ донову ҳаким аст!'),
(1697, 12, 101, 'Эй Парвардигори ман, маро фармонравоӣ додӣ ва маро илми таъбири хоб омӯхтӣ. Эй офаринандаи осмонҳову замин, ту дар дунёву охират корсози мани. Маро мусалмон бимирон ва қарини шоистагон соз!»'),
(1698, 12, 102, 'Инҳо хабарҳои ғайб аст, ки ба ту ваҳй мекунем. Ва он ҳангом, ки бо якдигар гирд омада буданд ва машварат мекарданд ва хила месохтанд, ту назди онҳо набудӣ!'),
(1699, 12, 103, 'Ҳарчанд ту ба имонашон ҳарис бошӣ, бештари мардум имон намеоваранд!'),
(1700, 12, 104, 'Ва ту дар муқобили паёмбарият аз онҳо музде наметалабӣ ва ин китоб ҷуз панде барои мардуми ҷаҳон нест!'),
(1701, 12, 105, 'Чӣ бисёр нишонаҳое дар осмонҳову замин аст, ки бар он мегузаранд ва аз он рӯй барметобанд!'),
(1702, 12, 106, 'Ва бештарашон ба Худо имон наёваранд, балки ҳамчунон мушриканд!'),
(1703, 12, 107, 'Оё пиндоранд, ки эминӣ ёфтаанд аз ин, ки уқубате умумӣ аз азоби Худо онҳоро фурӯ гирад ё қиёмат ба ногоҳ фаро расад, бе он ки хабардор шаванд?'),
(1704, 12, 108, 'Бигӯ: «Ин роҳи ман аст. Ману пайравонам ҳамагонро бо далелу ҳуҷҷат ба сӯи Худо мехонем. Пок аст Худо ва ман аз мушрикон нестам!»'),
(1705, 12, 109, 'Ва Мо пеш аз ту ба пайғамбарӣ нафиристодем магар мардонеро аз мардуми деҳаҳо, ки ба онҳоваҳй мекардем. Оё дар рӯи замин намегарданд, то бингаранд, ки поёни кори пешиниёнашон чӣ будааст? Ва олами охират парҳезгоронро беҳтар аст, чаро намеандешед?'),
(1706, 12, 110, 'Чун паёмбарон навмед шуданд ва чунон донистанд, ки онҳоро дурӯғ мебароранд, ёриашон кардем ва ҳар киро, ки хостем, наҷот додем ва азоби Мо аз мардуми гунаҳкор бозгардонида нашавад!'),
(1707, 12, 111, 'Дар достонҳояшон хирадмандонро ибратест. Ин достоне сохта нест, балки тасдиқи сухани пешиннён ва тафсили ҳар чизест барои онҳо, ки имон овардаанд, ҳидоят асту раҳмат.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Алиф, лом, мим, ро. Инҳо оёти ин китоб аст ва он чӣ аз Парвардигорат бар ту нозил шудааст, ҳақ аст, вале бештари мардум имон намеоваранд,'),
(1709, 13, 2, 'Оллоҳ — ҳамон Худовандест, ки осмонҳоро бе ҳеҷ сутуне, ки онро бубинед, барафрошт. Сипас ба арш пардохт ва офтобу моҳро, ки ҳар як то замоне муайян дар сайранд, ром кард. Корҳоро мегардонад ва оётро баён мекунад, бошад, ки ба дидори Парвардигоратон яқин кунед!'),
(1710, 13, 3, 'Ӯст, ки заминро бигустурд ва дар он кӯҳхову рудҳо қарор дод ва аз ҳар мева ҷуфт-ҷуфт падид овард ва шабро дар рӯз мепӯшонад. Дар инҳо ибратҳост барои мардуме, ки меандешанд!'),
(1711, 13, 4, 'Ва бар рӯи замин қитъаҳоест дар канори якдигар ва боғҳои ангуру кнштзорҳо ва нахлҳое, ки дутана аз як реша руста аст ё яктана аз як реша ва ҳама ба як об сероб мешаванд ва дар самара баъзеро дар баъзе дигар бартарӣ ниҳодаем. Албатта дар инҳо барои хирадмандон ибратҳост!'),
(1712, 13, 5, 'Агар ту дар ҳайрат меоӣ, тааҷҷуб дар сухани онҳо аст, ки оё он ҳангом, ки хок шавем, аз нав моро биёфаринад? Инҳо ба Парвардигорашон имон надоранд, бар гарданҳошон тавқҳо бошад ва аҳли ҷаҳаннаманд ва дар он ҷовидонаанд.'),
(1713, 13, 6, 'Ба шитоб аз ту пеш аз хайру бахшоиш азоб металабанд. Пеш аз онҳо азобҳое будааст ва Парвардигори ту мардумро, бо он ки ситам кардаанд, мебахшад ва низ Парвардигори ту ба сахтӣ уқубат мекунад.'),
(1714, 13, 7, 'Кофирон мегӯянд: «Чаро аз ҷониби Парвардигораш муъҷизае бар ӯ нозил намешавад?» Ҷуз ин нест, ки ту бимдиҳандае ҳастӣ ва ҳар қавмеро роҳнамоест!'),
(1715, 13, 8, 'Худо медонад, ки ҳар зане чӣ дар раҳм дорад ва он чӣ раҳмҳо коҳад ва он чӣ раҳмҳо афзояд. Ва ҳар чизро дар назди Ӯ миқдоре муъайян аст.'),
(1716, 13, 9, 'Донои ниҳону ошкор — он Худом бузурги баландмартаба!'),
(1717, 13, 10, 'Барои Ӯ яксон аст аз шумо ҳар кӣ сухан ба пинҳон гӯяд ё ба ошкоро. Ва он ки дар пардаи шаб пинҳон мешавад ва он ки дар рӯз ба ошкоро меравад.'),
(1718, 13, 11, 'Одамиро фариштагонест, ки паёпай ба амри Худо аз рӯбарӯву пушти сараш меоянд ва нигаҳбонияш мекунанд. Худо чизеро, ки аз они мардумест, дигаргун накунад, то он мардум худ дигаргун шаванд. Чун Худо барои мардуме бадӣ хоҳад, ҳеҷ чиз монеъи Ӯ натавонад шуд ва онҳоро ғайри Худо ҳеҷ корсозе нест!'),
(1719, 13, 12, 'Ӯст, ки барқро гоҳ барои тарсонидан ва гоҳ барои умед бахшидан ба шумо менамоёнад ва абрҳои гаронборро падид меоварад.'),
(1720, 13, 13, 'Раъд ба ситоиши Ӯ ва фариштагон аз бими Ӯ тасбеҳ мекунанд. Ва оташакҳоро мефиристад ва ҳар киро бихоҳад, ба он осеб мерасонад. Боз ҳам дар бораи Худо баҳсу ҷидол мекунанд. Ва Ӯ ба сахтӣ уқубат мекунад!'),
(1721, 13, 14, 'Хоси Ӯст дуову илтиҷои ростин. Онҳо, ки ҷуз Ӯро илтиҷо мекунанд, ҳеҷ посухашон намегӯянд. Монанди касе, ки ду даст ба сӯи об барад то об ба даҳон расонад ва натавонад, ки об ба даҳон расонад. Ва дуъои кофирон ҷуз ба гумроҳӣ нест!'),
(1722, 13, 15, 'Хоҳу нохоҳ барои Худованд саҷда мекунанд ҳамаи онҳо, ки дар осмонҳову заминанд ва сояҳояшон низ бомдодону шомгоҳон ба саҷда меоянд. (Саҷда).'),
(1723, 13, 16, 'Бигӯ: «Кист Парвардигори осмонҳову замин?» Бигӯ: «Оллоҳ». Бигӯ; «Оё ғайри Ӯ худоёне гирифтаед, ки қодир ба суду зиёни худ нестанд? Бигӯ: «Оё нобинову бино баробаранд? Ё торикиву равшанӣ яксонанд? Ё шариконе, ки барои Худо қоил шудаанд, чизҳое офаридаанд, монанди он чӣ Худо офаридааст ва онон дар бораи офариниш ба иштибоҳ афтодаанд?» Бигӯ: «Оллоҳ офаринандаи ҳар чизест ва Ӯ ягонаву қаҳҳор аст!»'),
(1724, 13, 17, 'Аз осмон об фиристод ва ҳар як ҷӯйе ба андозаи худ ҷорӣ шуд ва оби равон каф (к) бар сар овард. Ва аз он чӣ бар оташ мегудозанд, то зевару матоъе созанд, низ кафке бар сар ояд. Худо барои ҳаққу ботил чунин масал занад, Аммо кафк ба каноре афтад ва нобуд шавад ва он чӣ барои мардум судманд аст, дар замин пойдор бимонад. Худо инчунин мисол меорад.'),
(1725, 13, 18, 'Барои онон, ки даъвати парвардигорашонро қабул карданд, мукофоти некӯест. Ва касоне, ки даъвати Ӯро қабул накарданд, агар ҳар он чиро, ки бар рӯи замин аст ва монанди онро дошта бошанд, онро фидо диҳанд, онон ба сахтӣ бозхост хоҳанд шуд ва маконашон ҷаҳаннам аст ва бад ҷойгоҳест!'),
(1726, 13, 19, 'Оё касе, ки медонад он чӣ аз ҷониби Парвардигорат бар ту нозил шуда ҳақ аст монанди кассет, ки нобиност? Танҳо хирадмандон панд мегиранд'),
(1727, 13, 20, 'касоне, ки ба аҳди Худо вафо мекунанд ва худ паймон намешикананд;'),
(1728, 13, 21, 'онон, ки он чиро Худо ба пайвастани он фармон дода, пайванд мекунанд ва аз Парвардигорашон метарсанд ва аз сахтии ҳисоби Худованд бимноканд;'),
(1729, 13, 22, 'онон, ки ба талаби савоби Парвардигори худ сабр пеша карданд ва намоз гузориданд ва дар ниҳону ошкор аз он чӣ ба онҳо рӯзӣ додаем, хайр карданд ва бадиро бо некӣ бармегардонанд. Оқибати неки охират аз онҳост.'),
(1730, 13, 23, 'Биҳиштҳои ҷовидон онҳо ва ҳар ки некӯкор будааст аз падарону ҳамсарон ва фарзандонашон, бадон дохил шаванд ва фариштагон аз ҳар дар ба наздашон оянд.'),
(1731, 13, 24, 'Салом бар шумо ба хотири он ҳама сабре, ки кардаед. Сарои охират чӣ сарое некӯст!'),
(1732, 13, 25, 'Ва онҳо, ки паймони Худоро пас аз бастанаш мешикананд ва он чиро, ки Худо ба пайвастани он фармон дода, қатъ мекунанд ва дар замин фасод мекунанд, лаънат бар онҳост ва бадиҳои он ҷаҳон насибашон.'),
(1733, 13, 26, 'Худо ҳар киро хоҳад, рӯзии бисёр диҳад ё рӯзии андак. Ва мардум ба зиндагии дунё хушнуданд, ҳол он ки зиндагии дунё дар баробари зиндагии охират ҷуз андак матоъе нест.'),
(1734, 13, 27, 'Кофирон мегӯянд: «Чаро аз ҷониби Парвардигораш мӯъҷизае бар ӯ нозил намешавад?» Бигӯ: «Худо ҳар киро бихоҳад, гумроҳ мекунад. Ва ҳар киро ба даргоҳи Ӯ рӯй кунад, ҳидоят мекунад».'),
(1735, 13, 28, 'Онҳое, ки имон овардаанд ва дилҳояшон ба ёди Худо оромиш меёбад, огоҳ бошед, ки дилҳо ба ёди Худо оромиш меёбад!'),
(1736, 13, 29, 'Зиндагии хуш ва бозгаштангоҳи некӯ аз они касонест, ки имон овардаанд ва корҳои шоиста кардаанд.'),
(1737, 13, 30, 'Ҳамчунин туро ба миёни уммате, ки пеш аз онхо умматҳои дигар будаанд, ба пайғамбарӣ фиристодаем, то он чиро, ки бар ту ваҳй кардаем, барояшон тиловат кунӣ ва онҳо ба Раҳмон кофир мешаванд. Бигӯ: «Ӯ Парвардигори ман аст. Худое ҷуз Ӯ нест. Бар Ӯ таваккал кардаам ва тавбаи ман ба даргоҳи Ӯст!»'),
(1738, 13, 31, 'Агар Қуръоне тавонист буд, ки куҳҳо ба он ба ҷунбиш оянд ё замин пора-пора шавад, ё мурдагонро ба сухан орад, ҷуз ин Қуръон намебуд, ки ҳамаи корҳо аз они Худост. Оё мӯъминон ҳанӯз надонистаанд, ки агар Худо мехост, ҳамаи мардумро ҳидоят мекард? Ва кофиронро пайваста ба сабаби амалҳояшон ҳодисае расад ё он ҳодиса дар наздикии хонаҳояшон фурӯд ояд, то он гоҳ, ки ваъдаи Худо ояд, зеро Худо хилофи ваъда намекунад!'),
(1739, 13, 32, 'Паёмбароне ҳам, ки пеш аз ту буданд, ба масхара гирифта шуданд. Ман ба кофирон мӯҳлат додам, он гоҳ ҳамаро фурӯ гирифтам ва он уқубати ман чӣ сахт буд!'),
(1740, 13, 33, 'Оё он ки бар ҳамаи мардум ва амалҳояшон нозир аст, монанди бутон аст? Барои Худо шариконе қоил шуданд. Бигӯ: «Онҳоро ном бибаред. Шояд мехоҳед Ӯро аз чизе дар рӯи замин огоҳ кунед, ки намедонад, ё суханони беҳуда мегӯянд?» Дар назари кофирон макрашон ороста омад ва аз роҳи рост бероҳ шуданд. Ва ҳар киро Худо гумроҳ кунад, ҳеҷ роҳнамое надорад!'),
(1741, 13, 34, 'Дар ин зиндагии дунё ба азобе гирифтор оянд ва азоби охират сахттар аст ва онҳоро ҳеҷ нигаҳдорандае аз азоби Худо нест!'),
(1742, 13, 35, 'Сифати биҳиште, ки ба парҳезгорон ваъда шуда: об аз зери дарахтонаш равон аст ва меваҳову сояаш ҳамешагист. Ин саранҷоми касонест, ки парҳезгоранд ва саранҷоми кофирон оташ аст.'),
(1743, 13, 36, 'Аҳли китоб аз он чӣ бар ту нозил шуда, шодмонанд. Ва аз он ҷамоъат касоне ҳастанд, ки баъзе аз онро инкор мекунанд. Бигӯ: «Ман амр шудаам, ки Худои якторо бипарастам ва ба Ӯ ширк наёварам. Ба сӯи Ӯ даъват мекунам ва бозгашти ман ба сӯи Ӯст!'),
(1744, 13, 37, 'Ҳамчунин Қуръонро ба забони арабӣ нозил кардем. Агар пас аз донише, ки ба ту расида, аз паи хоҳишҳои онҳо биравӣ, дар баробари азоби Худо корсозу нигаҳдорандае нахоҳӣ дошт.'),
(1745, 13, 38, 'Ба таҳқиқ пеш аз ту паёмбароне фиристодаем ва барояшон ҳамсарону фарзандон қарор додаем. Ва ҳеҷ паёмбареро ҳаққи он набуд, ки ҷуз ба фармони Худо ояте биёварад ва ҳар амреро замоне навишташуда аст.'),
(1746, 13, 39, 'Худо ҳар чиро бихоҳад, маҳв ё исбот мекунад ва уммулкитоб назди Ӯст.'),
(1747, 13, 40, 'Агар баъзе аз он чиро ки ба онҳо ваъда кардаем, ба ту нишон диҳем, ё туро пеш аз вақт бимиронем, дар ҳар ҳол он чӣ бар ӯҳдаи туст, таблиғ аст ва он чӣ бар ӯҳдаи мост, ҳисоб кардан!'),
(1748, 13, 41, 'Оё ҳанӯз надонистаанд, ки мо аз атрофи ин сарзамин кам мекунем ва Худо ҳукм мекунад ва ҳеҷ чиз ҳукми Ӯро рад намекунад ва Ӯ зуд ба ҳисоби ҳама мерасад?'),
(1749, 13, 42, 'Касоне, ки пеш аз инҳо буданд, макрҳо карданд, вале ҳамаи макрҳо назди Худованд аст. Медонад, ки ҳар касе чӣ мекунад. Ва кофирон ба зудӣ хоҳанд донист, ки некӯии охират аз они кист!'),
(1750, 13, 43, 'Кофирон мегӯянд, ки ту паёмбар нести. Бигӯ: «Худо ва ҳар кас, ки аз китоб огоҳӣ дошта бошад, ба шоҳидӣ миёни ману шумо кофист!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Алиф, лом, ро. Китобест, ки бар ту нозил кардем, то мардумро ба фармони Парвардигорашон аз торикӣ ба рӯшноӣ бибарӣ ва ба роҳи Худои пирӯзманди сутуданӣ (ҳамаи забонҳо шукри Ӯкунанд) роҳ намоӣ.'),
(1752, 14, 2, 'Худое, ки ҳар чӣ дар осмонҳову замин аст, аз они Ӯст! Пас вой бар кофирон аз азобе сахт.'),
(1753, 14, 3, 'Онҳое, ки зиндагии дунёро аз охират дӯсттар доранд ва дигаронро аз роҳи Худо бозмедоранд ва онро мунҳариф (каҷрав нишон доданашро) мехоҳанд, сахт дар гумроҳӣ ҳастанд.'),
(1754, 14, 4, 'Ҳар паёмбареро фақат ба забони мардумаш фиристодем, то битавонад паёми Худоро барояшон баён кунад. Пас Худо ҳар киро бихоҳад, гумроҳ мекунад ва ҳар киро бихоҳад, ҳидоят мекунад ва Ӯст пирӯзманду ҳаким!'),
(1755, 14, 5, 'Мӯсоро бо оёти худ фиристодем, ки қавми худро аз торикӣ ба рӯшанӣ берун ор ва рӯзҳои Худоро ба ёдашон биёвар. Ва дар ин барои ҳар сабркунандаи шукргузор ибратҳоест!'),
(1756, 14, 6, 'Ва Мӯсо ба қавми худ гуфт: «Неъматеро, ки Худо бар шумо арзонӣ доштааст, ба ёд оваред, он гоҳ ки шуморо аз фиръавниён бираҳонид. Ба сахтӣ озоратон мекарданд ва писаронатонро мекуштанд ва занонатонро зинда мегузоштанд ва дар ин аз ҷониби Парвардигоратон барои шумо озмоише бузург буд».'),
(1757, 14, 7, 'Ва Парвардигоратон эълом кард, ки агар Маро шукр гӯед, бар неъмати шумо меафзоям ва агар ношукрӣ кунед, бидонед, ки азоби Ман сахт аст!'),
(1758, 14, 8, 'Ва Мӯсо гуфт: «Агар шумо ва ҳамаи мардуми рӯи замин кофир шавед, Худо бениёз ва сазовори ситоиш аст!»'),
(1759, 14, 9, 'Оё хабари касоне, ки пеш аз шумо будаанд, чун қавми Нӯҳ ва Од ва Самуд ва низ касоне, ки баъд аз онҳо будаанд, ба шумо нарасидааст? Ғайри Худо касеро аз онон огоҳӣ нест. Паёмбаронашон ҳамроҳ бо далоили равшан омаданд ва онҳо даст бар даҳон бурданду гуфтанд: Мо ба он чӣ ба он амр шудаед, имон намеоварем ва дар чизе, ки моро ба он мехонед, сахт дар шубҳа ҳастем».'),
(1760, 14, 10, 'Паёмбаронашон гуфтанд: «Оё дар Худо — он офаринандаи осмонҳову замин — шакке ҳаст? Шуморо даъват мекунад, то гуноҳонатонро бибахшад ва то муддате муайян шуморо зинда гузорад». Гуфтанд: «Шумо мардумоне монанди мо ҳастед. Мехоҳед моро аз он чӣ падаронамон мепарастиданд, боздоред. Барои мо далеле равшан биёваред!»'),
(1761, 14, 11, 'Паёмбаронашон гуфтанд: мо мардумоне монанди шумо ҳастем, вале Худо ба ҳар як бандагонаш, ки бихоҳад, миннат ниҳад. Моро нашояд, ки барои шумо далеле, ҷуз ба фармони Худо, биёварем ва мӯъминон бар Худо таваккал кунанд.'),
(1762, 14, 12, 'Чаро бар Худо таваккал накунем ва ҳол он ки Ӯ роҳи моро ба мо бинамуд? Ва мо бар озоре, ки ба мо мерасонед, сабр хоҳем кард ва таваккалкунандагон бар Худо таваккал кунанд».'),
(1763, 14, 13, 'Кофирон ба Паёмбаронашон гуфтанд: «Ё шуморо аз сарзамини худ меронем ё ба дини мо бозгардед». Пас Парвардигорашон ба паёмбарон ваҳй кард, ки ситамкоронро ҳалок хоҳем кард.'),
(1764, 14, 14, 'Ва шуморо пас аз онҳо дар он сарзамин ҷой хоҳем дод, Ин аз они касест, ки аз истодан дар пешгоҳи Ман ва аз ваъдаи азоби Ман битарсад!'),
(1765, 14, 15, 'Пирӯзӣ хостанд ва ҳар ҷаббори саркаше ноумед шуд.'),
(1766, 14, 16, 'Пушти сараш ҷаҳаннам аст, то дар он ҷо аз оби чирку хунаш бихӯронанд.'),
(1767, 14, 17, 'Ҷуръа-ҷуръа онро менӯшад ва ба осонӣ фурӯ набарад ва марг аз ҳар сӯ бар ӯ метозад, аммо намемирад, ва азобе сахт дар интизори ӯст.'),
(1768, 14, 18, 'Мисоли аъмоли касоне, ки ба Худо кофир шудаанд, чун хокистарест, ки дар рӯзе тӯфонӣ боде сахт бар он бивазад. Тавони нигоҳ доштани он чиро, ки ба даст оварданд, надоранд. Ин аст гӯмроҳии беохир!'),
(1769, 14, 19, 'Оё надидаӣ, ки Худо осмонҳову заминро ба ҳақ офаридааст? Агар бихоҳад, шуморо аз байн мебарад ва махлуқе нав меоварад.'),
(1770, 14, 20, 'Ва ин бар Худо душвор нест.'),
(1771, 14, 21, 'Ҳама дар пешгоҳи Худо ҳозир оянд. Заъифон ба Онҳое, ки такаббурӣ мекарданд, гӯянд: «Мо пайрави шумо будем. Оё акнун метавонед моро ба кор оед ва андаке аз азоби Худоро аз мо дафъ кунед?» Гӯянд: «Агар Худо моро ҳидоят карда буд, мо низ шуморо ҳидоят мекардем. Ҳоло моро роҳи халосӣ нест. Барои мо яксон аст, чӣ бетобӣ кунем, чӣ сабр варзем».'),
(1772, 14, 22, 'Чун кор ба поён ояд, шайтон гӯяд: «Худо ба шумо ваъда дод ва ваъдаи Ӯ дуруст буд ва ман низ ба шумо ваъда додам, вале ваъдаи худ хилоф кардам. Ва бароятон ҳеҷ далелу бурҳоне наёвардам, фақат ки даъвататон кардам, шумо низ даъвати ман иҷобат кардед, пас маро маломат макунед, худро маломат кунед. На ман фарёдраси шумоям, на шумо фарёдраси ман. Аз ин ки маро пеш аз ин шарики Худо карда будед, безорам». Зеро барои ситамкорон азобест дардовар!'),
(1773, 14, 23, 'Касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, ба биҳиштҳое, ки наҳрҳо дар он ҷорист, дароваранд, ба фармони Парвардигорашон дар он ҷо ҷовидона бимонанд ва ба салом якдигарро таҳийят (салом) гӯянд.'),
(1774, 14, 24, 'Оё надидаӣ, ки Худо чӣ гуна мисол овард? Сухани пок чун дарахте пок аст, ки решааш дар замин устувор ва шохаҳояш дар осмон аст.'),
(1775, 14, 25, 'Ба фармони Худо ҳар замон меваи худро медиҳад. Худо барои мардум мисолҳо меоварад, бошад, ки панд гиранд!'),
(1776, 14, 26, 'Ва мисоли сухани нопок чун дарахте нопок аст, ки реша дар замин надорад ва барпо натавонад монд.'),
(1777, 14, 27, 'Худо мӯъминонро ба сабаби эътиқоди устуворашон дар дунёву охират пойдор медорад. Ва золимонро гумроҳ месозад ва ҳар чӣ хоҳад, ҳамон мекунад.'),
(1778, 14, 28, 'Оё надидаӣ он касонро, ки неъмати Худоро ба куфр иваз сохтанд ва мардуми худро ба диёри ҳалок бурданд?'),
(1779, 14, 29, 'Ба ҷаҳаннам, он қароргоҳи бад, дохил мешаванд!'),
(1780, 14, 30, 'Барои Худо шариконе қарор доданд, то мардумро аз роҳи Ӯ гумроҳ созанд. Бигӯ: «Акнун баҳраманд гардед, ки бозгаштатон ба оташ аст».'),
(1781, 14, 31, 'Ба бандагони Ман, ки имон овардаанд, бигӯ то намоз бигузоранд ва аз он чӣ рӯзиашон додаем, ниҳону ошкоро садақа кунанд, пеш аз он, ки рӯзе фаро расад, ки дар он на хариду фурӯхте бошад ва на ҳеҷ дӯстие ба кор ояд.'),
(1782, 14, 32, 'Худост, ки осмонҳову заминро офарид ва аз осмон борон нозил кард ва ба он борон барои рӯзии шумо меваҳо руёнид ва киштиҳоро роми шумо кард то ба фармони Ӯ дар даре равон шаванд ва рӯдҳоро итоаткунандаи шумо сохт.'),
(1783, 14, 33, 'Ва офтобу моҳро, ки ҳамеша дар ҳаракатанд, роми шумо кард ва шабу рӯзро фармонбари шумо гардонид.'),
(1784, 14, 34, 'Ва ҳар чӣ аз Ӯ хостед, ба шумо арзонӣ доштааст ва агар хоҳед, ки неъматҳояшро шумор кунед, натавонед, дар ҳақиқат одами ситамкор ва кофири неъмат аст!'),
(1785, 14, 35, 'Ва Иброҳим гуфт: «Эй Парвардигори ман, ин сарзаминро тинҷ гардон ва марову фарзандонамро аз парастиши бутон дур бидор.'),
(1786, 14, 36, 'Эй Парвардигори ман, инҳо бисёре аз мардумро гумроҳ кардаанд, пас ҳар кас, ки аз ман пайравӣ кунад, аз ман аст ва ҳар кас фармони ман набарад, ту омурзандаву меҳрубонӣ!'),
(1787, 14, 37, 'Эй Парвардигори мо, баъзе аз фарзандонамро ба водии бе ҳеҷ киштае, наздики хонаи гиромии ту ҷой додам, эй Парвардигори мо, то намоз бигузоранд. Дилҳои мардумон чунон кун, ки майли онҳо кунанд ва аз ҳар мевае рӯзиашон деҳ, шояд, ки сипос гузоранд!'),
(1788, 14, 38, 'Эй Парвардигори мо, ҳар чиро пинҳон медорем, ё ошкор месозем, Ту ба он огоҳӣ. Ва бар Худо ҳеҷ чиз дар замину осмон пӯшида нест!'),
(1789, 14, 39, 'Сипос Худоеро, ки дар ин пирӣ Исмоилу Исҳоқро ба ман ато кард. Парвардигори ман дуъоҳоро мешунавад!'),
(1790, 14, 40, 'Эй Парвардигори ман, маро ва фарзандони маро барпойдорандагони намоз гардон. Эй Парвардигори мо, дуъои маро бипазир.'),
(1791, 14, 41, 'Эй Парвардигори мо, маро ва падару модарам ва ҳамаи мӯъминонро дар рӯзи шумор бибахшоӣ!»'),
(1792, 14, 42, 'Ва напиндор, ки Худо аз кирдори ситамкорон ғофил аст. Азобашонро то он рӯз, ки чашмҳо дар он хира мемонад, ба дер мепартояд!'),
(1793, 14, 43, 'Мешитобанд, сарҳоро боло гирифтаанд, чашм бар ҳам намезананд ва дилҳояшон холи аз хирад аст.'),
(1794, 14, 44, 'Мардумро аз он рӯз, ки азоб фаро мерасад, битарсон. Ситамкорон мегӯянд: «Бор Худоё, моро андаке мӯҳлат дех, то даъвати Туро иҷобат кунем ва ба паёмбаронн Ту пайравӣ кунем». Оё шумо пеш аз ин савганд намехӯрдед, ки ҳаргиз завол намеёбед? (намемиред).'),
(1795, 14, 45, 'Дар хонаҳои касоне, ки худ бар худ ситам мекарданд, ҷой гирифтед ва донистед бо онон чӣ гуна рафтор кардем ва бароятон мисолҳо овардем.'),
(1796, 14, 46, 'Онон найрангҳои худ намуданд ва Худо аз найрангҳояшон огоҳ буд. Ҳарчанд, ки аз найрангҳояшор кӯҳ аз пой меафтод.'),
(1797, 14, 47, 'Мапиндор, ки Худо ваъдаеро, ки ба паёмбаронаш додааст, хилоф мекунад. Худованд пирӯзманд ва интиқомгиранда аст.'),
(1798, 14, 48, 'Он рӯз, ки замин ба замине ғайри ин замин мубаддал шавад ва осмонҳо ба осмоне дигар ва ҳама дар пешгоҳи Худои воҳиди қаҳҳор ҳозир оянд!'),
(1799, 14, 49, 'Гунаҳкоронро дар он рӯз ба ғулҳое, ки дастро ба гардан банданд, баста бубинӣ.'),
(1800, 14, 50, 'Ҷомаҳошон аз қатрон аст ва оташ рӯйҳояшонро пушидааст.'),
(1801, 14, 51, 'То Худо ҳар касро баробари амалаш ҷазо диҳад, албатта Худо зудҳисобкунанда аст!'),
(1802, 14, 52, 'Ин паёмест барои мардум то ба он бимнок шаванд ва бидонанд, ки Ӯст Худои якто ва то хирадмандон панд гиранд!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Алиф, лом, ро. Ин аст-оёти китоб ва Қуръони равшангар.'),
(1804, 15, 2, 'Бисёр вақт кофирон орзу кунанд, ки эй кош мусалмон мебуданд,'),
(1805, 15, 3, 'Бигузорашон, то бихӯранд ва баҳравар шаванд ва орзу ба худ машғулашон дорад, ба зуди хоҳанд донист.'),
(1806, 15, 4, 'Ҳар деҳаеро ҳалок кардем, албатта замоне маълум дошт.'),
(1807, 15, 5, 'Ҳеҷ уммате аз аҷали худ на пеш меафтад ва на пас мемонад.'),
(1808, 15, 6, 'Ва гуфтанд: «Эй марде, ки Қуръон ба ту нозил шуда, ҳаққо, ки ту девонаӣ!'),
(1809, 15, 7, 'Агар рост мегӯӣ, чаро фариштагонро барои мо намеоварӣ?»'),
(1810, 15, 8, 'Мо фариштагонро ғайри ба ҳақ нозил намекунем ва дар он ҳангом дигар мӯҳлаташон надиҳанд.'),
(1811, 15, 9, 'Мо Қуръонро худ нозил кардаем ва худ нигаҳбонаш ҳастем.'),
(1812, 15, 10, 'Ва Мо расулони худро пеш аз ту ба миёни қавмҳои пешин фиристодаем.'),
(1813, 15, 11, 'Ҳар паёмбаре бар онҳо фиристода шуд, албатта масхарааш карданд.'),
(1814, 15, 12, 'Роҳашро дар дили гунаҳгорон инчунин мекушоем!'),
(1815, 15, 13, 'Ба он имон намеоваранд ва суннати пешиниён чунин будааст.'),
(1816, 15, 14, 'Агар барояшон аз осмон даре бикушоем, ки аз он боло раванд,'),
(1817, 15, 15, 'гӯянд: «Чашмони моро ҷоду кардаанд, балки мо мардуме ҷодузада ҳастем!»'),
(1818, 15, 16, 'Ва албатта дар осмон бурҷҳое офаридем ва барон бинандагонашон биёростем.'),
(1819, 15, 17, 'Ва аз ҳар шайтони раҷиме ҳифзашон кардем.'),
(1820, 15, 18, 'Ғайри он ки дуздона гӯш медод ва шарорае равшан таъқибаш кард.'),
(1821, 15, 19, 'Ва заминро густурдем ва дар он кӯҳҳое азим афкандем. Ва аз ҳар чиз ба шевае санҷида дар он рӯёнидем.'),
(1822, 15, 20, 'Ва маъишати шумо ва касонеро, ки шумо рӯзидеҳашон нестед, дар он ҷо қарор додем.'),
(1823, 15, 21, 'Ҳар чӣ ҳаст, хазинаҳои он назди Мост ва Мо ҷуз ба андозае муъайян онро намефиристем.'),
(1824, 15, 22, 'Ва бодҳои обистанкунандаро фиристодем ва аз осмон обе нозил кардем ва шуморо ба он сероб сохтем ва шуморо нарасад, ки хазинадори он, бошед.'),
(1825, 15, 23, 'Албатта Мо ҳастем, ки зинда мекунем ва мемиронем ва баъд аз ҳама боқӣ мемонем.'),
(1826, 15, 24, 'Ва медонем чӣ касоне аз шумо аз ин пеш рафтаанд ва чӣ касоне боқӣ мондаанд.'),
(1827, 15, 25, 'Ва Парвардигори ту ҳамаро зинда карда ҷамъ мегардонад, зеро Ӯст, ки ҳакиму доност!'),
(1828, 15, 26, 'Мо одамиро аз гили хушк, аз лаҷани (лойи) бӯйнок офаридем.'),
(1829, 15, 27, 'Ва ҷинро пеш аз он аз оташи сӯзандаи бедуд офарида будем.'),
(1830, 15, 28, 'Ва Парвардигорат ба фариштагон гуфт: «Мехоҳам башаре аз гили хушк, аз лои бӯйнок биёфаринам.'),
(1831, 15, 29, 'Чун офаринишамро ба поён бурдам ва аз рӯҳи Худ дар он дамидам, дар баробари ӯ ба саҷда бияфтед».'),
(1832, 15, 30, 'Фариштагон ҳамагӣ саҷда карданд,'),
(1833, 15, 31, 'ғайри Иблис, ки сар боззад, ки бо саҷдакунандагон бошад.'),
(1834, 15, 32, 'Гуфт: «Эй Иблис, чаро ту аз саҷдакунандагон Набудӣ?»'),
(1835, 15, 33, 'Гуфт: «Ман барои башаре, ки аз гили хушк, аз лойи бӯйнок офаридаӣ, саҷда намекунам».'),
(1836, 15, 34, 'Гуфт: «Аз он ҷо берун шав, ки рондашуда ҳастӣ.'),
(1837, 15, 35, 'То рӯзи қиёмат бар ту лаънат аст!»'),
(1838, 15, 36, 'Гуфт: «Эй Парвардигори ман, маро то рӯзе, ки дубора зинда мешаванд, мӯҳлат деҳ».'),
(1839, 15, 37, 'Гуфт: «Ту дар шумори мӯҳлатёфтагонӣ,'),
(1840, 15, 38, 'то он рӯзе, ки вақташ маълум аст».'),
(1841, 15, 39, 'Гуфт: «Эй Парвардигори ман, чун маро ноумед кардӣ, дар рӯи замин бадиҳоро дар назарашон биёроям ва ҳамагонро гумроҳ кунам,'),
(1842, 15, 40, 'ғайри онҳо, ки бандагони боихлоси Ту бошанд».'),
(1843, 15, 41, 'Гуфт: «Роҳи ихлос роҳи ростест, ки ба Ман мерасад.'),
(1844, 15, 42, 'Туро бар бандагони Ман ғолибияте нест, магар бар он гумроҳоне, ки туро пайравӣ кунанд.'),
(1845, 15, 43, 'Ва ҷаҳаннам ваъдагоҳи ҳама аст.'),
(1846, 15, 44, 'Ҳафт дар дорад ва барои хар дар гурӯҳе аз онон муъайян шудаанд.'),
(1847, 15, 45, 'Парҳезгорон дар биҳиштҳо, канори чашмасоронанд.'),
(1848, 15, 46, 'Ба саломату эминӣ дохил шавед».'),
(1849, 15, 47, 'Ҳар кинаеро аз дилашон баркандаем, ҳама бародаранд, бар тахтҳо рӯбарӯи ҳаманд.'),
(1850, 15, 48, 'Ҳеҷ ранҷе ба онҳо намерасад ва аз он ҷо берунашон наронанд.'),
(1851, 15, 49, 'Ба бандагонам хабар деҳ, ки ман бахшояндаву меҳрубонам.'),
(1852, 15, 50, 'Ва азоби Ман азобе дардовар аст!'),
(1853, 15, 51, 'Ва аз меҳмонони Иброҳим хабардорашон кун».'),
(1854, 15, 52, 'Он гоҳ, ки бар ӯ дохил шуданд ва гуфтанд: «Салом!» Иброҳим гуфт: «Мо аз шумо метарсем!»'),
(1855, 15, 53, 'Гуфтанд: «Матарс, мо туро ба писаре доно мужда (хушхабар) медиҳем».'),
(1856, 15, 54, 'Гуфт: «Оё маро мужда медиҳед, бо он ки пир шудаам? Ба чӣ чиз муждаам медиҳед?»'),
(1857, 15, 55, 'Гуфтанд: «Ба ҳақ хушхабарат додем, аз ноумедон мабош!»'),
(1858, 15, 56, 'Гуфт: «Ҷуз гумроҳон чӣ касе аз раҳмати Парвардигораш ноумед мешавад?»'),
(1859, 15, 57, 'Гуфт: «Эй расулон, кори шумо чист?»'),
(1860, 15, 58, 'Гуфтанд: «Мо ба сари мардуме гунаҳкор фиристода шудаем,'),
(1861, 15, 59, 'ғайри хонадони Лут, ки ҳамаро наҷот медиҳем,'),
(1862, 15, 60, 'ҷуз занашро, ки муқаррар кардаем, ки аз боқимондагон бошад».'),
(1863, 15, 61, 'Чун расулон назди хонадони Лут омаданд,'),
(1864, 15, 62, 'Лут гуфт: «Шумо бегонаед».'),
(1865, 15, 63, 'Гуфтанд: «На, чизеро, ки дар он шак мекарданд, овардаем.'),
(1866, 15, 64, 'Мо туро хабари рост овардаем ва мо ростгӯёнем.'),
(1867, 15, 65, 'Чун посе аз шаб бигзарад, хонадони худро берун бубар ва худ аз паи онҳо рав ва набояд ҳеҷ як аз шумо ба ақиб бингарад. Ва ҳар ҷо, ки фармонатон додаанд, биравед».'),
(1868, 15, 66, 'Ва барои ӯ ҳодисаро ҳикоят кардем, ки чун субҳ фаро расад, решаи онҳо барканда шавад.'),
(1869, 15, 67, 'Аҳли шаҳр шодикунон омаданд.'),
(1870, 15, 68, 'Гуфт: «Инҳо меҳмонони мананд, маро расво макунед.'),
(1871, 15, 69, 'Аз Худованд битарсед ва маро шармсор масозед».'),
(1872, 15, 70, 'Гуфтанд: «Магар туро аз мардум манъ накарда будем?»'),
(1873, 15, 71, 'Гуфт: «Агар қасде доред, инак духтарони ман ҳастанд».'),
(1874, 15, 72, 'Ба ҷони ту савганд, ки онҳо дар мастии худ саргашта буданд.'),
(1875, 15, 73, 'Чун субҳ бидамид, ононро фарёд фурӯ гирифт.'),
(1876, 15, 74, 'Шаҳрро зеру забар кардем ва бороне аз сиҷҷил бар онон боронидем.'),
(1877, 15, 75, 'Дар ин ибратҳост барои ибратгирандагон.'),
(1878, 15, 76, 'Ва он шаҳр акнун дар сари роҳи корвониён аст.'),
(1879, 15, 77, 'Ва мӯъминонро дар он ибратест.'),
(1880, 15, 78, 'Ва мардуми Айка низ ситамкор буданд.'),
(1881, 15, 79, 'Аз онҳо интиқом гирифтем ва шаҳрҳои он ду қавм ошкоро бар сари роҳанд.'),
(1882, 15, 80, 'Мардуми Ҳиҷр низ паёмбаронро ба дурӯғ нисбат доданд.'),
(1883, 15, 81, 'Оёти Худро бар онон расонидем, вале аз он рӯй мегардонданд.'),
(1884, 15, 82, 'Хонаҳои худро, то тинҷ бошанд, дар куҳҳо меканданд.'),
(1885, 15, 83, 'Субҳгоҳон ононро фарёд фурӯ гирифт.'),
(1886, 15, 84, 'Кирдорашон аз онҳо дафъи бало накард.'),
(1887, 15, 85, 'Осмонҳову замин ва он чиро, ки миёни онҳост, ба ғайри ҳақ наёфаридаем. Ва бешак қиёмат фаро мерасад. Пас гузашт кун, гузаште некӯ.'),
(1888, 15, 86, 'Албатта Парвардигори ту офаринандае доност!'),
(1889, 15, 87, 'Мо сабъаммасонӣ ва Қуръони бузургро ба ту додем.'),
(1890, 15, 88, 'Агар баъзе аз мардону занонашонро ба чизе баҳравар сохтаем, ту ба он нигоҳ макун. Ва ғами онҳоро махӯр. Ва дар баробари мӯъминон фурӯтан (хоксор) бош.'),
(1891, 15, 89, 'Ва бигӯ: «Ман бимдиҳандае равшангарам».'),
(1892, 15, 90, 'Ҳамонанди азобе, ки бар тақсимкунандагон нозил кардем:'),
(1893, 15, 91, 'онол, ки Қуръонрю ба қисмҳо тақсим карда буданд.'),
(1894, 15, 92, 'Ба Парвардигорат савганд, ки ҳамаро бозхост кунем,'),
(1895, 15, 93, 'ба хотири корҳое, ки мекардаанд!'),
(1896, 15, 94, 'Ба ҳар чӣ амр шудаӣ, сареҳу баланд бигӯ ва аз мушрикон рӯйгардон бош!'),
(1897, 15, 95, 'Мо масхаракунандагонро аз ту бозмедорем,'),
(1898, 15, 96, 'онҳо, ки бо Оллоҳ худое дигар қоил мешаванд. Пас ба зудӣ хоҳанд донист.'),
(1899, 15, 97, 'Ва медонем, ки ту аз гуфторашон дилтанг мешавӣ.'),
(1900, 15, 98, 'Ба ситоиши Парвардигорат тасбеҳ кун ва аз саҷдакунандагон бош!'),
(1901, 15, 99, 'Ва Парвардигоратро бипараст, то лаҳзаи маргат фаро расад!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Фармони Худованд даррасид, ба шитобаш наталабед. Ӯ пок аст ва аз ҳар чӣ шарики Ӯ месозанд, бартар аст.'),
(1903, 16, 2, 'Фариштагонро ҳамроҳи ваҳй, ки фармони Ӯст, бар ҳар як аз бандагонаш, ки бихоҳад, поён мефиристад, то бим диҳанд, ки ғайри Ман худое нест, пас аз Ман битарсед.'),
(1904, 16, 3, 'Осмонҳову заминро ба ҳақ биёфарид, аз ҳар чӣ бо Ӯ шарик месозанд, болотар аст.'),
(1905, 16, 4, 'Одамиро аз нутфа биёфарид. Ва акнун ӯст, ки ба ошкоро душманӣ мекунад.'),
(1906, 16, 5, 'Чаҳорпоёнро бароятон биёфарид, шуморо аз онҳо ҳарорату дигар нафъҳост. Ва аз онҳо мехӯред.'),
(1907, 16, 6, 'Ва чун шабҳангом бозмегардонед ва бомдодон берун мефиристед, нишони зинату обрӯи шумоянд;'),
(1908, 16, 7, 'Борҳоятонро ба шаҳрҳое, ки бе ранҷи тан ба онҳо натавонед расид, ҳамл мекунанд, зеро Парвардигоратон мушфиқу меҳрубон аст!'),
(1909, 16, 8, 'Ва аспону уштурону харонро барои он офаридааст, ки саворашон шавед ва низ зинати шумо бошанд. Ва Худо чизҳое офарида, ки шумо намедонед.'),
(1910, 16, 9, 'Бар Худост, ки роҳи ростро бинамояд. Аз миёни роҳҳо низ роҳест каҷ. Агар Худо мехост, ҳамаи шуморо ҳидоят мекард.'),
(1911, 16, 10, 'Ӯст, ки аз осмон бароятон борон нозил кард. Аз он менӯшед ва ба он гиёҳ мерӯяд ва чорпоёнро мечаронед.'),
(1912, 16, 11, 'Ва бо он бароятон киштзор ва зайтуну наҳлҳо ва токистонҳо ва ҳар навъ мева бирӯёнад. Дар ин ибратест барои мардуме, ки меандешанд.'),
(1913, 16, 12, 'Ва мусаххари (тобеъи) шумо кард шабу рӯзро ва хуршеду моҳро ва ситорагон ҳама фармонбардори амри Ӯ ҳастанд. Дар ин барои онҳо, ки ба ақл дармеёбанд, ибратҳост.'),
(1914, 16, 13, 'Дар замин чизҳое бо рангҳои гуногун офарид, дар ин ибратест барои мардуме, ки панд мегиранд!'),
(1915, 16, 14, 'Ӯст, ки дарёро ром кард то аз он гӯшти тоза бихӯред ва зеварҳое берун оред ва худро ба он биёроед ва киштиҳоро бинӣ, ки дарёро мешикофанд ва пеш мераванд то аз фазли Худо рӯзӣ биталабед, шояд, ки шукр гӯед!'),
(1916, 16, 15, 'Ва бар замин кӯҳҳои бузург афканд, то шуморо наларзонад. Ва ҷӯйҳову роҳҳо падид овард. Шояд ҳидоят шавед!'),
(1917, 16, 16, 'Ва нишонаҳо ниҳод ва ба ситорагон роҳ меёбанд.'),
(1918, 16, 17, 'Оё он ки меофаринад, монанди касест, ки намеофаринад? Чаро дарнамеёбед?'),
(1919, 16, 18, 'Агар бихоҳед неъматҳои Худоро шумор кунед, шумор кардан натавонед. Худо бахшояндаву меҳрубон аст!'),
(1920, 16, 19, 'Он чиро, ки пинҳон мекунед ё ошкор месозед, Худо ба он огоҳ аст.'),
(1921, 16, 20, 'Онҳоеро, ки ба ҷои Оллоҳ ба худоӣ мехонанд, наметавонанд чизе биёфаринанд ва худ махлуқанд.'),
(1922, 16, 21, 'Мурдагонанд, на зиндагон ва надонанд, ки чӣ вақт онҳоро дубора зинда мекунанд.'),
(1923, 16, 22, 'Худои шумо Худоест якто. Ва онон, ки ба охират имон надоранд, дилҳояшон инкор кунад ва худ кибр кунанд.'),
(1924, 16, 23, 'Ба ростӣ, ки Худо медонад, ки чӣ дар дил пинҳон медоранд ва чӣ чизро ошкор месозанд ва Ӯ мутакаббиронро дӯст надорад!'),
(1925, 16, 24, '? 4 Чун ба онҳо гуфта шавад: «Парвардигоратон чӣ чиз нозил кардааст?» Гӯянд: «Афсонаҳои гузаштагон».'),
(1926, 16, 25, 'То дар рӯзи қиёмат ҳамаи бори гуноҳи хеш ва бори гуноҳи касонеро, ки ба нодонӣ гумроҳашон карда буданд, бардоранд. Огоҳ бош, ки бори баде бармедоранд!'),
(1927, 16, 26, 'Пешиниёнашон ҳилла сохтанд. Фармони Худо даррасид ва он биноро аз поя вайрон сохт ва сақф бар сарашон фуруд омад ва аз сӯе, ки нафаҳмиданд, азоб ононро фурӯ гирифт.'),
(1928, 16, 27, 'Он гоҳ дар рӯзи қиёмат расвояшон созад ва гӯяд: «Бутоне, ки шарики Ман мехондед ва бар сари онҳо бо якдигар ихтилоф мекардед, акнун куҷоянд?» Донишмандон гӯянд: «Имрӯз насиби кофирон хориву азоб аст!'),
(1929, 16, 28, 'Касоне ҳастанд, ки бар худ ситам раво доштаанд, чун фариштагон ҷонашонро биситонанд, сари таслим фуруд оранд ва гӯянд: «Мо ҳеҷ кори баде намекардем». Оре, Худо аз корҳое, ки мекардед, огоҳ аст.'),
(1930, 16, 29, 'Аз дарҳои ҷаҳаннам дохил шавед ва то абад дар он ҷо бимонед. Бад ҷойгоҳест ҷойгоҳи гарданкашон!'),
(1931, 16, 30, 'Аз парҳезгорон пурсанд: «Парвардигори шумо чӣ чиз нозил кардааст?» Гӯянд: «Беҳтаринро». Ба онон, ки дар ин дунё некӣ кунанд, некӣ музд диҳанд ва диёри охират некӯтар аз он аст ва ҷойгоҳи парҳезгорон чӣ ҷойгоҳи хубест!'),
(1932, 16, 31, 'Ба биҳиштҳои ҷовидон дохил мешаванд. Дар он ҷӯйҳо равон аст. Ҳар чӣ бихоҳанд, барояшон муҳайёст. Худо парҳезгоронро инчунин мукофот медиҳад,'),
(1933, 16, 32, 'онҳое, ки чун фариштагонашон поксират бимиронанд, мегӯянд: «Салом бар шумо! Ба мукофоти корҳое, ки мекардаед, ба биҳишт дароед».'),
(1934, 16, 33, 'Оё чашм ба роҳанд, ки фариштагон наздашон биёянд, ё фармони Парвардигорат фаро расад? Гурӯҳе низ, ки пеш аз онҳо буданд, чунин мекарданд ва Худо ба онҳо ситам накард, балки онҳо худ ба худ ситам мекарданд.'),
(1935, 16, 34, 'Ба ҷазои кирдори бадашон расиданд ва ҳамон чизҳое, ки ба масхара мегирифтанд, бар сарашон омад.'),
(1936, 16, 35, 'Мушрикон гуфтанд: «Агар Худо мехост, мову падаронамон ҳеҷ чиз ҷуз Ӯро намепарастидем ва он чиро ҳаром кардаем, ҳаром намекардем». Мардуме ҳам, ки пеш аз онҳо буданд, чунин мегуфтанд. Оё паёмбаронро ҷуз таблиғи ошкоро вазифаи дигарест?'),
(1937, 16, 36, 'Ба миёни ҳар миллате паёмбаре фиристодем, ки Худоро бипарастед ва аз бут дурӣ ҷӯед. Баъзеро Худо ҳидоят кард ва бар баъзе гумроҳӣ муқаррар гашт. Пас дар замин бигардед ва бингаред, ки оқибати кори касоне, ки паёмбаронро ба дурӯғ нисбат медоданд, чӣ гуна будааст.'),
(1938, 16, 37, 'Ва агар ту ба ҳидояти онҳо ҳарис бошӣ, Худо онро, ки гумроҳ кардааст, ҳидоят намекунад ва ин гумроҳонро ҳеҷ ёридиҳандае нест.'),
(1939, 16, 38, 'То он ҷо, ки метавонистанд, ба Худо қасам хӯрданд, ки Худо касонеро, ки мемиранд, ба қиёмат зинда намекунад. Оре, ин ваъдаест, ки анҷом додани он бар ӯҳдаи Ӯст, вале бештари мардум намедонанд.'),
(1940, 16, 39, 'То он чиро ки дар он ихтилоф мекарданд, барояшон ошкор кунад ва кофирон бидонанд, ки дурӯғ мегуфтаанд.'),
(1941, 16, 40, 'Фармони Мо ба ҳар чизе, ки иродаашро бикунем, ин аст, ки мегӯем: «Мавҷуд шав!» Ва мавҷуд мешавад.'),
(1942, 16, 41, 'Ба онон, ки мавриди ситам воқеъ шуданд ва дар роҳи Худо муҳоҷират карданд, дар ин ҷаҳон ҷойгоҳе некӯ медиҳем. Ва агар бидонанд, музди охират бузургтар аст,'),
(1943, 16, 42, 'онон, ки сабр пеша карданд ва бар Парвардигорашон таваккал мекунанд.'),
(1944, 16, 43, 'Агар худ намедонед, аз аҳли китоб бипурсед, ки Мо пеш аз ту ба пайғамбарӣ нафиристодем, ғайри мардоне, ки ба онҳо ваҳй мефиристодем,'),
(1945, 16, 44, 'ҳамроҳ бо далоили равшан ва китобҳо ва бар ту низ Қуръонро нозил кардем, то он чиро барси мардум нозил шудааст, барояшон баён кунӣ ва бошад, ки бияндешанд.'),
(1946, 16, 45, 'Оё онон, ки содиркунандан бадиҳо мешаванд, магар эминанд аз ин, ки замин ба фармони Худо онҳоро фурӯ барад ё азоб аз ҷое, ки намедонанд, бар сарашон фуруд ояд?'),
(1947, 16, 46, 'Ё ба ҳангоми омадурафт фурӯ гирадашон, чунон ки натавонанд бигурезанд?'),
(1948, 16, 47, 'Ё аз онон ягон-ягон кам кунад? Албатта Парвардигоратон мушфиқу меҳрубон аст!'),
(1949, 16, 48, 'Оё ба чизҳое, ки Худо офаридааст, наменигаранд, ки барои саҷда ба даргоҳи Ӯ сояҳояшон аз росту чап ҳаракат доранд ва дар баробари Ӯ хошеъанд (тобеъанд)?'),
(1950, 16, 49, 'Ҳар чӣ дар осмонҳову замин аст аз ҷунбандагону фариштагон Худоро саҷда мекунанд ва такаббур намеварзанд.'),
(1951, 16, 50, 'Аз Парвардигорашон, ки болои онҳост, метарсанд ва ба ҳар чӣ амр шудаанд, ҳамон мекунанд.'),
(1952, 16, 51, 'Худо гуфт: «Ду худоро мапарастед ва фақат Ӯст Худои якто. Пас аз Ман битарсед?»'),
(1953, 16, 52, 'Ҳарчи дар осмонҳову замин аст, аз они Ӯст ва парастиш хоси Ӯст. Оё аз ғайри Худо метарсед?'),
(1954, 16, 53, 'Ҳар неъмате, ки шуморост, аз ҷониби Худованд аст ва чун меҳнате ба шумо расад, ба пешгоҳи Ӯ зорӣ мекунед.'),
(1955, 16, 54, 'Ва боз чун он меҳнатро ба поён орад, гурӯҳе аз шумо ба Парвардигорашон мушрик мешаванд,'),
(1956, 16, 55, 'то неъматро, ки ба онҳо додаем, ношукрӣ кунанд, Акнун мутаматтеъ (баҳраманд) шавед, ба зудӣ хоҳед донист!'),
(1957, 16, 56, 'Он гоҳ аз он чӣ рӯзиашон додаем, насибе барои бутоне, ки ҳеҷ намедонанд, муъайян мекунанд. Ба Худо савганд ба сабаби дурӯғе, ки мебандед, бозхост мешавед!'),
(1958, 16, 57, 'Барои Худо духтарон коил мешаванд — Ӯ пок аст — ва барои худ ҳар чӣ дӯст доранд.'),
(1959, 16, 58, 'Ва чун ба якешон муждаи духтар диҳанд, сияҳрӯй шавад ва хашмгин гардад.'),
(1960, 16, 59, 'Аз шарми ин мужда аз мардум пинҳон мешавад. Оё бо хори нигоҳаш дорад ё дар хок ниҳонаш кунад? Огоҳ бошед, ки бад доварӣ мекунанд.'),
(1961, 16, 60, 'Сифати бад аз они касонест, ки ба қиёмат имон намеоваранд ва сифати бартар аз они Худованд аст. Ва Ӯст пирӯзманду ҳаким!'),
(1962, 16, 61, 'Агар Худованд бихоҳад, ки мардумро ба гуноҳашон ҳалок кунад, бар рӯи замин ҳеҷ ҷунбандае боқӣ нагузорад, вале азобашонро то муддате муъайян ба дер меафканад. Ва чун аҷалашон фаро расад, як соъат пасу пеш нашаванд.'),
(1963, 16, 62, 'Он чиро намеписанданд, ба Худо нисбат кунанд ва забонашон ба дурӯғ гӯяд, ки хубиҳо аз они онҳост, на, бешак насиби онҳо оташ аст ва онҳоро ба шитоб ба сӯи он баранд!'),
(1964, 16, 63, 'Ба Худо савганд, ки барои мардуме ҳам, ки пеш аз ту будаанд, паёмбароне фиристодаем. Вале шайтон амалҳояшонро дар чашмашон биёрост. Ва дар он рӯз шайтон дӯсташон хоҳад буд ва онҳоро азобе дардовар аст!'),
(1965, 16, 64, 'Мо ин китобро бар ту нозил кардаем, барои он ки ҳар чиро дар он ихтилоф мекунанд, барояшон баён кунӣ. Ва низ роҳнамову раҳмате барои мӯъминон бошад.'),
(1966, 16, 65, 'Ва Худо аз осмон борон фиристод ва замини мурдаро бо он зинда кард. Барои мардуме, ки гӯши шунаво доранд, дар ин ибратест!'),
(1967, 16, 66, 'Барои шумо дар чорпоён пандест. Аз шири холис, ки аз шикамашон, аз миёни саргину хун берун меояд, ба шумо менӯшонем. Шире, ки ба коми нӯшандагонаш гуворост.'),
(1968, 16, 67, 'Ва аз меваҳои нахлҳову токҳо шаробе мастиовар ва ризқе некӯ ба даст меоваред ва хирадмандонро дар ин ибратест.'),
(1969, 16, 68, 'Парвардигори ту ба занбӯри асал илҳом дод, ки аз кӯҳҳову дарахтон ва дар биноҳое, ки месозанд, хонаҳое интихоб кун.'),
(1970, 16, 69, 'Он гоҳ аз ҳар самарае бихӯр ва фармонбардор ба роҳи Парвардигорат бирав. Аз шиками Ӯ шаробе рангоранг берун меояд, ки шифои мардум дар он аст. Ва соҳибони андешаро дар ин ибратест.'),
(1971, 16, 70, 'Худо шуморо биёфарид, он гоҳ мемиронад ва аз миёни шумо касеро ба пирӣ мерасонад, то ҳар чиро, ки омӯхтааст, аз ёд бибарад, зеро Худо донову тавоност!'),
(1972, 16, 71, 'Худо рӯзии баъзе аз шуморо аз баъзе дигар афзун кардаст. Пас онон, ки фузунӣ ёфтаанд, аз рӯзии худ ба ғуломони худ намедиҳанд, то ҳама дар рӯзӣ яксон шаванд. Оё неъмати Худоро инкор мекунед?'),
(1973, 16, 72, 'Худо барои шумо аз миёни худатон ҳамсароне қарор дод ва аз ҳамсаронатон фарзандон ва фарзандзодагон падид овард ва аз чизҳои хушу пок рӯзиятон дод. Оё ҳануз ба ботил имон меоваранд ва неъмати Худоро ношукрӣ мекунанд?'),
(1974, 16, 73, 'Ғайри Худо чизҳоеро мепарастанд, ки нотавонанд ва ёрои он надоранд, ки аз осмонҳову замин рӯзияшон диҳанд.'),
(1975, 16, 74, 'Барои Худо мисол наоред; Худо медонад ва шумо намедонед.'),
(1976, 16, 75, 'Худо ғуломи зархаридеро мисол меорад, ки ҳеҷ қудрате надорад ва касеро, ки аз ҷониби худ ризқи некӯяш додаем ва дар ниҳону ошкоро нафақа мекунад. Оё ин ду баробаранд? Шукр хоси Худост, вале бештарашон нодонанд.'),
(1977, 16, 76, 'Ва Худо мисоли ду мардро баён мекунад, ки яке лол асту тавони ҳеҷ чиз надорад ва бори дӯши хоҷаи худ аст, ҳар ҷо, ки ӯро бифиристад, ҳеҷ фоидае ҳосил намекунад. Оё ин мард бо он кас, ки мардумро ба адл фармон медиҳад ва худ бар роҳи рост меравад, баробар аст?'),
(1978, 16, 77, 'Аз они Худост ниҳони осмонҳову замин. Ва фаро расидани қиёмат танҳо як чашм бар ҳам задан ё наздиктар аз он аст. Зеро Худо бар ҳар коре тавоност!'),
(1979, 16, 78, 'Худо шуморо аз батни модаронатон берун овард ва ҳеҷ намедонистед. Ва бароятон чашму гӯшу дил биёфарид. Шояд шукр гӯед!'),
(1980, 16, 79, 'Оё он паррандагони ромшуда дар ҷавви (фазои) осмонро намебинанд? Ҳеҷ кас ҷуз Худо онҳоро дар ҳаво нигоҳ натавонад дошт. Ва дар ин барои мардуме, ки имон меоваранд, ибратҳост.'),
(1981, 16, 80, 'Худо хонаҳоятонро ҷои оромишатон қарор дод ва аз пӯсти чаҳорпоён бароятон хаймаҳо сохт, то ба ҳангоми сафар ва ба ҳангоми муқимӣ аз бардоштани онҳо дар ранҷ наафтед. Ва аз пашму курку мӯяшон то рӯзи қиёмат бароятон асоси хона ва асбоби зиндагӣ сохт.'),
(1982, 16, 81, 'Худо барои шумо аз чизҳое, ки офаридааст, сояҳо падид овард. Ва дар кӯҳҳо бароятон ғорҳо сохт. Ва ҷомаҳое, ки шуморо аз гармо ҳифз мекунад ва ҷомаҳое, ки дар ҷанг нигаҳдори шумост. Худо неъматҳои худро инчунин бар шумо комил мекунад. Шояд, ки таслими фармони Ӯ шавед.'),
(1983, 16, 82, 'Агар рӯйгардон шуданд, ҷуз ин нест, ки вазифаи ту таблиғе ошкорост.'),
(1984, 16, 83, 'Неъматҳои Худоро мешиносанд, без ҳам мункири он мешаванд, зеро бештарашон кофиронанд.'),
(1985, 16, 84, 'Рӯзе, ки аз ҳар уммате гувоҳе бигирем ва ба кофирон рухсати сухан гуфтан дода нашавад ва узрашон пазируфта нашавад.'),
(1986, 16, 85, 'Ва ситамкорон азобро бингаранд, азобе, ки ҳеҷ сабукӣ наёбад ва ҳеҷ мӯҳлаташон надиҳанд.'),
(1987, 16, 86, 'Ва чун мушрикон ононро, ки шарики Худо месохтанд, бубинанд, мегӯянд: «Эй Парвардигори мо, инҳо шариконе ҳастанд, ки мо ба ҷои Ту онҳоро мехондем». Онон посух мегӯянд, ки шумо дурӯғ мегӯед!'),
(1988, 16, 87, 'Ва дар ин рӯз ба бандагӣ худро таслими Худо кунанд ва он дурӯғҳо, ки мебофтаанд, нобуд шавад.'),
(1989, 16, 88, 'Онон, ки кофир шуданд ва дигаронро аз роҳи Худо боздоштанд, ба ҷазои фасоде, ки мекарданд, азобе бар азобашон хоҳем афзуд.'),
(1990, 16, 89, 'Ва рӯзе бошад, ки аз ҳар уммате шоҳиде аз худашон бар худашон бифиристем ва туро биёварем, то бар онон шаҳодат диҳӣ. Ва мо Қуръонро, ки баёнкунандаи ҳар чизест ва ҳидояту раҳмат ва хушхабар барои мусалмонон аст, бар ту нозил кардаем.'),
(1991, 16, 90, 'Худо ба адлу эҳсон ва бахшиш ба хешовандон фармон медиҳад. Ва аз фаҳшову зишткорӣ ва ситам наҳй мекунад. Шуморо панд медиҳад, бошад, ки пазирои панд шавед!'),
(1992, 16, 91, 'Чун бо Худо паймон бастед, ба он вафо кунед ва чун савганд хӯрдед, онро машканед, ки Худоро зомини худ кардаед ва Ӯ медонад, ки чӣ мекунед!'),
(1993, 16, 92, 'Ва монандн он зан, ки риштаеро, ки маҳкам тофта буд, аз ҳам кушуд ва қитъа-қитъа кард, мабошед, то савгандҳои худро василаи фиреби якдигар созед, ба ин баҳона, ки гурӯҳе бештар аз гурӯҳи дигар аст. Худо шуморо ба он меозмояд ва дар рӯзи қиёмат чизеро, ки дар он ихтилоф мекардед, бароятон баён мекунад.'),
(1994, 16, 93, 'Агар Худо мехост, ҳамаи шуморо як уммат карда буд, вале ҳар киро бихоҳад, гумроҳ месозад ва ҳар киро бихоҳад, ҳидоят мекунад ва аз ҳар коре, ки мекунед, бозхост мешавед.'),
(1995, 16, 94, 'Савгандҳои худро василаи фиреби якдигар масозед, то қадамеро, ки устувор сохтаед, билағзад ва ба сабаби рӯйгоронӣ аз роҳи Худо гирифтори бадӣ шавед ва ба азобе бузург гирифтор оед.'),
(1996, 16, 95, 'Паймони Худоро ба баҳои андак мафурӯшед, зеро агар бидонед, он чӣ дар назди Худост, бароятон беҳтар аст!'),
(1997, 16, 96, 'Он чӣ назди шумост, фано мешавад ва он чӣ назди Худост, боқӣ мемонад. Ва ононро, ки сабр варзиданд, савобе беҳтар аз кирдорашон хоҳем дод.'),
(1998, 16, 97, 'Ҳар зану марде, ки коре некӯ анҷом диҳад, агар имон оварда бошад, зиндагии хушу покизае ба ӯ хоҳем дод ва савобе беҳтар аз кирдорашон ато хоҳем кард.'),
(1999, 16, 98, 'Ва чун Қуръон бихонӣ, аз шайтони раҷим ба Худо паноҳ бубар.'),
(2000, 16, 99, 'Шайтонро бар касоне, ки имон овардаанд ва бар Худо таваккал мекунанд, ғолибияте нест.'),
(2001, 16, 100, 'Ғолибияти ӯ танҳо бар касонеет, ки дӯсташ медоранд ва ба Худо ширк меоваранд.'),
(2002, 16, 101, 'Чун ояеро ҷонишини ояи дигар кунем, Худо беҳтар медонад, ки чӣ чиз нозил кунад. Гуфтанд, ки ту дурӯғ мебофӣ, на, бештаринашон нодонанд!'),
(2003, 16, 102, 'Бигӯ: «Онро руҳулқудус (Ҷабраил (а)) ба ҳақ аз ҷониби Парвардигорат нозил кардааст, то мӯъминонро устуворӣ ато намояд ва мусалмононро ҳидояту хушхабар бошад».'),
(2004, 16, 103, 'Медонем, ки мегӯянд: «Ин Қуръонро башаре ба ӯ меомузад». Забони касе, ки ба ӯ нисбат мекунанд, аҷамист, ҳол он ки ин забони арабии равшанест.'),
(2005, 16, 104, 'Худо касонеро, ки ба оёташ имон намеоваранд, ҳидоят намекунад ва барояшон азобе дардовар муҳайёст.'),
(2006, 16, 105, 'Касоне дурӯғ мебофанд, ки ба оёти Худо имон надоранд. Инҳ худ дурӯғгӯянд.'),
(2007, 16, 106, 'Касе, ки пас аз имон ба Худо кофир мешавад, на он ки ӯро ба зур водоштаанд, то изҳори куфр кунад ва ҳол он, кн дилаш ба имони худ оромгирандаву осуда аст, бар онон, ки дари дилро ба рӯи куфр мекушоянд, мавриди хашми Худоянд ва азобе бузург барояшон муҳайёст.'),
(2008, 16, 107, 'Ва ин ба он сабаб аст, ки инҳо зиндагии дунёро бештар аз зиндагии охират дӯст доранд ва Худо мардуми кофирро ҳидоят намекунад.'),
(2009, 16, 108, 'Худо бар дилу гӯшҳо ва чашмонашон мӯҳр ниҳодааст ва худ бехабаранд.'),
(2010, 16, 109, 'Ба ночор дар охират ҳам аз зиёндидагон бошанд,'),
(2011, 16, 110, 'Сипас Парвардигори ту барои касоне, ки пас аз он ранҷҳо, ки диданд, ҳиҷрат карданд ва ба ҷиҳод рафтанд ва пой фушурданд, бахшояндаву меҳрубон аст.'),
(2012, 16, 111, 'Рӯзи қиёмат ҳар кас ба дифоъ аз худ ба муҷодала мепардозад ва ҷазои ҳар кас, чунон кӣ хаққи ӯст, дода мешавад ва ба касе ситам раво надоранд.'),
(2013, 16, 112, 'Худо деҳаеро мисол меорад, ки тинҷу ором буд, рӯзии мардумаш ба фаровонӣ аз ҳар ҷо мерасид, аммо ношукрии неъмати Худо карданд ва Худо ба ҷазои амалҳояшон ба гуруснагиву ваҳшат мубталояшон сохт.'),
(2014, 16, 113, 'Паёмбаре аз худашон ба наздашон омад, дурӯғ бароварданд ва азоб он ситамкоронро фурӯ гирифт.'),
(2015, 16, 114, 'Аз ин чизҳои ҳалолу покиза, ки Худо ба шумо рӯзӣ додааст, бихӯред ва агар Худоро мепарастед, шукри неъматашро ба ҷой оваред!'),
(2016, 16, 115, 'Худо ҳаром кардааст бар шумо мурдору хун ва гӯшти хук ва ҳар чиро ҷуз ба номи Худо забҳ карда бошанд. Аммо касе, ки ночор шавад, ҳар гоҳ бемайлӣ ҷӯяд ва аз ҳад нагузаронад, Худо бахшояндаву меҳрубон аст.'),
(2017, 16, 116, 'То бар Худо дурӯғ бофед, барои ҳар дурӯғ, ки бар забонатон меояд, нагӯед, ки ин ҳалол асту ин ҳаром. Касоне, ки ба Худованд дурӯғ мебанданд, наҷот намеёбанд!'),
(2018, 16, 117, 'Андак фоидае мебаранд ва дар иваз ба азобе дардовар гирифтор мешаванд.'),
(2019, 16, 118, 'Ва бар яҳудиён он чиро, ки пеш аз ин барои ту ҳикоят кардем, ҳаром карда будем. Мо ба онҳо ситам накардаем, онҳо худ ба худ ситам кардаанд.'),
(2020, 16, 119, 'Парвардигори ту барои касоне, ки аз рӯи нодонӣ кунандаи коре зишт шаванд, сипас тавба кунанд ва ба салоҳ оянд, бахшояндаву меҳрубон аст!'),
(2021, 16, 120, 'Иброҳим бузургвор марде буд. Ба фармони Худо бар пой истод ва соҳиби дини холис буд. Ва аз мушрикон набуд,'),
(2022, 16, 121, 'ва шукургузори неъматҳои Ӯ буд, Худовандаш интихобаш кард ва ба роҳи рост ҳидоят кард.'),
(2023, 16, 122, 'Дар дунё ба ӯ некӣ ато кардем ва дар охират аз солеҳон аст.'),
(2024, 16, 123, 'Ба ту низ ваҳй кардем, ки аз дини поки Иброҳим пайравӣ кун, ки ӯ аз мушрикон набуд.'),
(2025, 16, 124, 'Шанбе гирифтанро (бузург доштани шанберо) барои т; асоне, ки дар он ихтилоф карда буданд, фарз кардаанд ва Парвардигори ту дар рӯзи қиёмат дар бораи он чӣ ихтилоф мекарданд, ҳукм хоҳад кард!'),
(2026, 16, 125, 'Мардумро бо ҳикмату панди некӯ ба роҳи Парвардигорат бихон ва бо беҳтарин тарз бо онон муҷодала кун. Зеро Парвардигори ту ба касоне, ки аз роҳи Ӯ каҷрав шудаанд, огоҳтар аст ва ҳидоятёфтагонро беҳтар мешиносад!'),
(2027, 16, 126, 'Агар уқубат мекунед, чунон уқубат кунед, ки шуморо уқубат кардаанд. Ва агар сабр кунед, собиронро сабр некӯтар аст.'),
(2028, 16, 127, 'Сабр кун, ки сабри ту бе тавфиқи Худо нест ва барояшон ғамгин мабош ва аз макрашон дилтангӣ накун,'),
(2029, 16, 128, 'зеро Худо бо касонест, ки мепарҳезанд ва некӣ мекунанд!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Пок аст он Худое, ки бандаи худро шабе аз масҷидулҳаром ба масҷидулақсо, ки гирдогирдашро баракат додаем, сайр дод, то баъзе аз оёти худро ба ӯ нишон диҳем, албатта! Ӯ шунавову биност!'),
(2031, 17, 2, 'Ва он китобро ба Мӯсо додем ва, онро роҳнамои банӣ-Исроил гардонидем, ки ҷуз Ман корсозе интихоб накунед.'),
(2032, 17, 3, 'Эй фарзандони мардуме, ки бо Нӯҳ дар киштияшон нишондем, бидонед, ки ӯ бандае сипосгузор буд.'),
(2033, 17, 4, 'Ва банӣ-Исроилро дар он китоб хабар додем, ки ду бор дар замин фасод хоҳед кард ва низ саркашӣ хоҳед кард, саркашӣ кардане бузург.'),
(2034, 17, 5, 'Чун аз он ду бор ваъдаи нахустин даррасид, гурӯҳе аз бандагони худро, ки ҷанговароне зӯрманд буданд, ба сари шумо фиристодем. Онҳо ҳатто дар даруни хонаҳо ҳам куштор карданд ва ид ваъда ба анҷом расид.'),
(2035, 17, 6, 'Бори дигар шуморо бар онҳо ғалаба додем ва ба молу фарзанд мадад кардем ва бар шуморатон афзудем.'),
(2036, 17, 7, 'Агар некӣ кунед, ба худ мекунед ва агар бадӣ кунед, ба худ мекунед. Ба чун ваъдаи дуввум фаро расид, касоне бар саратон фиристодем то шуморо ғамгин созанд ва чун бори аввал, ки ба масҷид даромада буданд, ба масҷид дароянд ва ба ҳар чӣ даст ёбанд, нобуд созанд.'),
(2037, 17, 8, 'Шояд Парвардигоратон бар шумо раҳмат оварад. Ва агар бозгардед, бозмегардем ва ҷаҳаннамро зиндони кофирон сохтаем.'),
(2038, 17, 9, 'Ин Қуръон ба дурусттарин динҳо роҳ менамояд ва мӯъминонеро, ки корҳои шоиста ба ҷой меоваранд, хушхабар медиҳад, ки аз музде некӯ баҳраманд хоҳанд шуд.'),
(2039, 17, 10, 'Ва барои касоне, ки ба охират имон надоранд, азобе дардовар муҳайё кардаем.'),
(2040, 17, 11, 'Ва одамӣ ба дуъо бадиеро металабад, чунон, ки гӯӣ ба дуъо хайреро меҷӯяд. Ва одамӣ то буда, шитобзада будааст.'),
(2041, 17, 12, 'Шабу рӯзро ду оят аз оёти Худо қарор додем. Ояти шабро торик гардонидем ва ояти рӯзро равшан, то ба талаби ризқе, ки Парвардигоратон муқаррар дошта аст, бархезед ва шумори солҳову ҳисобро бидонед ва мо ҳар чизро ба тафсил баён кардаем.'),
(2042, 17, 13, 'Кирдори некӯ бади ҳар инсонеро чун тавқе ба гарданаш овехтаем. Ва дар рӯзи қиёмат барои ӯ номае кушода берун оварем, то дар он бингарад.'),
(2043, 17, 14, '«Бихон номаатро Имрӯз ту худ барои ҳисоб кардани аз худ басандаӣ!»'),
(2044, 17, 15, 'Ҳар кӣ ҳидоят шавад, ба суди худ ҳидоят шуда ва ҳар кӣ гумроҳ гардад, ба зиёни худ гумроҳ шудааст ва ҳеҷ кас бори дигареро барнадорад ва Мо ҳеҷ қавмеро азоб намекунем то он гоҳ, ки паёмбаре бифиристем.'),
(2045, 17, 16, 'Чун, бихоҳем деҳаеро ҳалок кунем, дорандаҳои неъматашро бияфзоем, то дар он ҷо табаҳкорӣ кунанд, он гоҳ азоб бар онҳо воҷиб гардад ва онро дар ҳам фурӯ кӯбем.'),
(2046, 17, 17, 'Баъд аз Нӯҳ чӣ бисёр мардумеро ҳалок кардаем. Ва Парвардигори ту огоҳӣ ёфтан ва дидани гуноҳони бандагонашро кофист.'),
(2047, 17, 18, 'Ҳар кас, ки хорони ин ҷаҳон бошад, ҳар чӣ бихоҳем, зудаш арзонӣ дорем, он гоҳ ҷаҳаннамро ҷойгоҳи ӯ созем то баддидашудаву рондашуда ба он дарафтад.'),
(2048, 17, 19, 'Ва ҳар кӣ хоҳони охират бошад ва дар талаби он саъй кунад ва мӯъмин бошад, ҷазои саъйаш дода хоҳад шуд.'),
(2049, 17, 20, 'Ҳамаро – чӣ он гурӯҳро ва чӣ ин гурӯҳро аз атои Парвардигорат пай дар пай хоҳем дод, зеро атои Парвардигоратро аз касе бознадоранд.'),
(2050, 17, 21, 'Бингар, ки чӣ гуна баъзеяшонро бар баъзе дигар бартарӣ ниҳодаем ва дар охират дараҷоту бартариҳо бартару болотар аст.'),
(2051, 17, 22, 'Бо Худои якто худои дигаре ба худоӣ магир, ки баддидашудаву хор хоҳӣ монд.'),
(2052, 17, 23, 'Парвардигорат муқаррар дошт, ки ҷуз Ӯро напарастед ва ба падару модар некӣ кунед. Ҳар гоҳ то ту зинда ҳастӣ, ҳар ду ё яке аз он ду солхӯрда шаванд, ононро маёзор ва ба дуруштӣ хитоб макун ва бо онон ба икром сухан бигӯй.'),
(2053, 17, 24, 'Дар баробарашон аз рӯи меҳрубони сари хоксорӣ хам бикун ва бигӯ: «Эй Парвардигори ман, ҳамчунон ки маро дар хурдӣ парвариш доданд, бар онҳо раҳмат овар».'),
(2054, 17, 25, 'Парвардигоратон аз ҳар каси дигар ба он чӣ дар дилҳоятон мегузарад, донотар аст ва агар аз солеҳон бошед, Ӯ тавбакунандагонро меомурзад.'),
(2055, 17, 26, 'Ҳаққи хешовандону мискин ва дар роҳ мондаро адо кун ва ҳеҷ исрофкорӣ макун.'),
(2056, 17, 27, 'Исрофкорон бо шайтонҳо бародаранд ва шайтон нисбат ба Парвардигораш ношукр буд,'),
(2057, 17, 28, 'Ва агар ба интизори кушоише, ки аз ҷониби Парвардигорат умед медорӣ, ва аз онҳо рӯйгардонӣ мекунӣ, пас бо онҳо ба нармӣ сухан бигӯй.'),
(2058, 17, 29, 'На дасти худ аз рӯи хиссат (хасисӣ) ба гардан бубанд ва на ба саховат якбора бикушой, ки дар ҳар ду ҳол маломатзадаву ҳасратхӯрда бинишинӣ.'),
(2059, 17, 30, 'Парвардигори ту дар ризқи ҳар кас, ки бихоҳад, кушоиш медиҳад ё танг мегирад, зеро Ӯ ба бандагонаш огоҳу биност!'),
(2060, 17, 31, 'Фарзандони худро аз бими дарвешӣ макушед. Мо ҳам шуморо рӯзӣ медиҳем ва ҳам онҳоро Куштанатон хатои бузургест.'),
(2061, 17, 32, 'Ва ба зино наздик машавед. Зино коре зишт ва роҳе нописанд аст!'),
(2062, 17, 33, 'Касеро, ки Худо куштанашро ҳаром кардааст, накушед, ғайри ба ҳақ. Ва ҳар кас, ки ба ситам кушта шавад, ба талабкунандаи хуни ӯ қудрате додаем, вале дар интиқом аз ҳад нагузарад, ки Ӯ пирӯзманд аст.'),
(2063, 17, 34, 'Ҷуз ба тарзе некӯтар ба моли ятим наздик нашавед, то он гоҳ, ки ба ҳадди булуғаш бирасад. Ва ба аҳди худ вафо кунед, ки бозхост хоҳед шуд.'),
(2064, 17, 35, 'Чун чизеро чен кунед, паймонаро комил гардонед ва бо тарозуи дуруст вазн кунед, ки ин беҳтар ва саранҷомаш некӯтар аст.'),
(2065, 17, 36, 'Аз паи он чӣ надонӣ, ки чист, марав, зеро гӯшу чашму дил — ҳамаро ба он бозхост кунанд.'),
(2066, 17, 37, 'Ба худписандӣ бар замин роҳ марав, ки заминро нахоҳӣ шикофт ва ба баландии кӯҳҳо нахоҳӣ расид.'),
(2067, 17, 38, 'Ҳамаи- ин корҳо нописанд аст ва Парвардигори ту онҳоро нохуш дорад.'),
(2068, 17, 39, 'Ин ҳикматест, ки Парвардигори ту ба ту ваҳй кардааст. Бо Худои якто худои дигаре напиндор, ки маломатшудаву рондашуда ба ҷаҳаннамат андозанд.'),
(2069, 17, 40, 'Оё Парвадигоратон барои шумо писарон интихобид ва худ фаршитагонро ба духтарӣ ихтиёр кард? Ҳаққо, ки суханест бузург, ки бар забои меоваред.'),
(2070, 17, 41, 'Мо суханони гуногун дар ин Қуръон овардем, бошад, ки панд гиранд, вале ҷуз ба рамиданашон наяфзояд.'),
(2071, 17, 42, 'Бигӯ: «Ҳамчунон, ки мегӯянд, агар бо Ӯ худоёни Дигаре ҳам буданд, пас ба сӯи соҳиби арш роҳе ҷуста буданд».'),
(2072, 17, 43, 'Ӯ пок аст ва аз он чӣ дар борааш мегӯянд, бартару болотар аст.'),
(2073, 17, 44, 'Ҳафт осмону замин ва ҳар чӣ дар онҳост, тасбеҳаш мекунанд ва ҳеҷ мавҷуде нест, ҷуз он ки Ӯро ба покӣ меситояд, вале шумо зикри тасбеҳашонро намефаҳмед. Ӯ бурдбор ва бахшоянда аст.'),
(2074, 17, 45, 'Чун ту Қуръон бихонӣ, мисни туву онон, ки ба қиёмат имон намеоваранд, пардае ғафс қарор медиҳем.'),
(2075, 17, 46, 'Ва бар дилҳошон парда афканем, то онро дарнаёбанд ва гӯшҳошон сангин кунем. Ва чун Парвардигоратро дар Қуръон ба яктоӣ ёд кунӣ, бозмегарданду мераманд.'),
(2076, 17, 47, 'Мо беҳтар медонем, ки чун ба ту гӯш медиҳанд, чаро гӯш медиҳанд ё вақте ки бо ҳам наҷво мекунанд, чӣ мегӯянд. Кофирон мегӯянд: «Шумо дар паи марди ҷодушудае ба роҳ афтодаед».'),
(2077, 17, 48, 'Бингар, ки чӣ гуна, барои ту, масалҳо задаанд. Гумроҳ шудаанд ва роҳ ба ҷое намебаранд.'),
(2078, 17, 49, 'Гуфтанд: «Оё агар аз мо устухоне бимонаду хоке, боз ҳам бо офаринише нав аз қабр бармехезем?»'),
(2079, 17, 50, 'Бигӯ: «Санг бошед ё охан,'),
(2080, 17, 51, 'ё махлуқе, ки дар хотиратон бузург менамояд». Хоҳанд гуфт: «Чӣ касе моро бозмегардонад?» Бигӯ: «Он кас, ки бори нахуст шуморо офарид». Он гоҳ дар баробари ту сар меҷунбонанд ва мегӯянд: «Чӣ вақт?» Бигӯ: «Мумкин аст, ки дар ҳамин наздикӣ».'),
(2081, 17, 52, 'Рӯзе, ки шуморо даъват мекунад ва шумо ситоишгӯён посух медиҳед ва мепиндоред, ки андаке орамидаед.'),
(2082, 17, 53, 'Ва ба бандагони ман бигӯ, ки бо якдигар ба беҳтарин тарз сухан бигӯянд, ки шайтон дар миёни онҳо ба фитнагарист, зеро шайтон одамиро душмане ошкор аст!'),
(2083, 17, 54, 'Парвардигоратон ба шумо огоҳтар аст. Агар бихоҳад, бар шумо раҳмат меоварад ва агар бихоҳад, азобатон мекунад ва мо туро ба коргузорияшон нафиристодаем.'),
(2084, 17, 55, 'Ва Парвардигори ту ба он чӣ дар осмонҳову замин аст, огоҳтар аст. Баъзе аз паёмбаронро бар баъзе дигар беҳтарӣ додаем ва ба Довуд Забурро додем.'),
(2085, 17, 56, 'Бигӯ: «Онҳоеро, ки ҷуз Ӯ худо мепиндоред, бихонед. Наметавонанд балоро аз шумо дур созанд ё онро насиби дигарон кунанд!»'),
(2086, 17, 57, 'Онҳое, ки кофирон ба худоӣ мехонанд, дар талаби онанд, ки худ ба даргоҳи Парвардигорашон василае биёбанд ва наздиктарин шаванд ва ба раҳмати Ӯ умед мебанданд ва аз азоби Ӯ метарсанд, зеро азоби Парвардигорат тарснок аст!'),
(2087, 17, 58, 'Ҳеҷ деҳае набошад, ки пеш аз фаро расидаии рӯзи қиёмат ҳалокаш накунем ё ба азобе сахт гирифтораш насозем. Ва ин дар он китоб навишта шудааст.'),
(2088, 17, 59, 'Моро аз фиристодани мӯъҷизот бознадошт. Фақат ин ки пешиниён дурӯғаш бароварданд. Ба қавми Самуд ба унвони мӯъҷизае равшангар модашутурро додем. Бар он ситам карданд ва Мо ин мӯъҷизаҳоро фақат барои тарсондан мефиристем.'),
(2089, 17, 60, 'Ва он гоҳ ки туро гуфтем: «Парвардигорат бар ҳамаи мардум иҳота дорад». Ва он чӣ дар хоб ба ту нишон додем ва достони дарахти малъун, ки дар Қуръон омадааст, чизе ҷуз озмоиши мардум набуд. Мо мардумро метарсонем, вале танҳо ба куфру саркашияшон афзуда мешавад.'),
(2090, 17, 61, 'Ва ба фариштагон гуфтем: «Одамро саҷда кунед». Ҳамагон ғайри Иблис саҷда карданд. Гуфт: «Оё барои касе, ки аз гил офаридаӣ, саҷда кунам?»'),
(2091, 17, 62, 'Ва гуфт: «Бо ман бигӯй, чаро инро бар ман бартарӣ ниҳодаӣ? Агар маро то рӯзи қиёмат мӯҳлат диҳӣ, фарзандонн ӯро ғайри андаке реша канам».'),
(2092, 17, 63, 'Гуфт: «Бирав, ҷазои ту ва ҳар кас, ки пайрави ту гардада, ҷаҳаннам аст, ки ҷазое тамом аст!»'),
(2093, 17, 64, 'Бо фарёди худ ҳар киро тавонӣ, аз ҷой, бархезон ва ба ёрии саворону пиёдагонат бар онон битоз ва дар молу фарзанд бо онон шарики кун ва ба онхо ваъда бидеҳ. Ва ҳол он ки шайтон ҷуз ба фиребе ваъдаашон надиҳад!'),
(2094, 17, 65, 'Туро бар бандагоди Ман ҳеҷ ғолибияте набошад ва Парвардигори ту барои нигаҳбониашон кофист!»'),
(2095, 17, 66, 'Парвардигори шумост, ки киштиро дар дарё меронад то аз фазли Ӯ рӯзии худ ба даст оред. Зеро бо шумо меҳрубон аст.'),
(2096, 17, 67, 'Чун дар дарё шуморо озоре расад, ҳамаи онҳое, ки мепарастед, аз назаратон нест шаванд, ғайри Ӯ. Ва чун шуморо ба соҳили наҷот бирасонад, аз Ӯ рӯйгардон шавед, зеро одамӣ ношукр аст!'),
(2097, 17, 68, 'Оё бепарвоед аз ин, ки ногаҳон дар соҳили дарё шуморо дар замин фурӯ барад, ё тундбоде регбор бар шумо фнристад ва барои худ ҳеҷ нигаҳбоне наёбед?'),
(2098, 17, 69, 'Ё бепарвоанд аз ин ки, бори дигар шуморо ба дарё бозгардонад ва тӯфоне сахту киштишикан бифиристад ва ба хотири кофирие, ки намудаед ғарқатон созад ва касе, ки моро аз корамон бозхост кунад, наёбед?'),
(2099, 17, 70, 'Мо фарзандони Одамро каромат бахшидем ва бар дарёву хушкӣ савор кардем ва аз чизҳои хушу покиза рӯзӣ додаем ва бар бисёре аз махлуқоти худ беҳтариашон додем.'),
(2100, 17, 71, 'Рӯзе, ки ҳар гурӯҳ аз мардумро бо пешвоёнашон бихонем, номаи ҳар кӣ ба дасти росташ дода шавад чун бихонад, бинад, ки ба андозаи риштаи борике, ки даруни донаи хурмост, ба ӯ ситам нашудааст.'),
(2101, 17, 72, 'Ва ҳар кӣ дар ин дунё нобино бошад, дар охират низ нобино ва гумроҳтар аст.'),
(2102, 17, 73, 'Наздик буд туро аз он чӣ бар ту ваҳй карда будем, бероҳ созанд, то чизи дигаре ғайри онро ба дурӯғ ба мо нисбат кунӣ, он гоҳ бо ту дӯстӣ кунанд.'),
(2103, 17, 74, 'Ва агар ба ту пойдорӣ намедодем, наздик буд, ки андаке ба онон майл кунӣ.'),
(2104, 17, 75, 'Он гоҳ туро дучандон дар дунё ва дучандон дар охират азоб мекардем ва барои худ дар баробари Мо ёридиҳандае намеёфтӣ.'),
(2105, 17, 76, 'Ва наздик буд, ки туро аз ин сарзамин бархезонанд, то аз он ҷо овораат созанд. Ва худ пас аз ту андак замоне мемонданду бас.'),
(2106, 17, 77, 'Суннати Худо дар бораи паёмбароне, ки пеш аз ту фиристодаем, чунин аст ва суннати моро тағйирпазир наёбӣ.'),
(2107, 17, 78, 'Аз ҳангоми заволи хуршед то он гоҳ ки шаб фаро мерасад, намозро барпой дор ва низ намози субҳгоҳро. Ва намози субҳгоҳро ҳамагон ҳозир шаванд.'),
(2108, 17, 79, 'Порае аз шабро ба намоз хондан зинда бидор. Ин нофила (ибодат) хоси ту аст. Бошад, ки Парвардигорат туро ба мақоме писандида бирасонад.'),
(2109, 17, 80, 'Бигӯ: «Эй Парвардигори ман, маро ба ростиву некӯӣ дохил кун ва ба ростиву некӯӣ берун бар ва маро аз ҷониби худ пирӯзиву ёрӣ ато кун!'),
(2110, 17, 81, 'Бигӯ: «Ҳак омад ва ботил нобуд шуд. Ҳаққо, ки ботил нобуд шуданӣ буд».'),
(2111, 17, 82, 'Ва ин Қуръонро, ки барои мӯъминон шифову раҳмат аст, нозил мекунем, вале кофиронро ҷуз зиён наяфзояд.'),
(2112, 17, 83, 'Чун ба инсон неъмат додем, рӯй гардонд ва худро ба як сӯ кашид ва чун зараре ба ӯ расид, ноумед гардид.'),
(2113, 17, 84, 'Бигӯ: «Ҳар кас ба тарикаи худ амал мекунад ва Парвардигори ту беҳтар медонад, ки кадом як ба ҳидоят наздиктар аст».'),
(2114, 17, 85, 'Туро аз рӯҳ мепурсанд. Бигӯ: «Рӯҳ ҷузъе аз фармони Парвардигори ман аст ва шуморо ҷуз андак донише надодаанд».'),
(2115, 17, 86, 'Агар бихоҳем ҳамаи он чиро, ки бар ту ваҳй кардаем, бозмеситонем ва ту барои худ дар баробари Мо мададкоре намеёбӣ.'),
(2116, 17, 87, 'Магар Парвардигорат раҳмат кард, ки инъоми Ӯ дар бораи ту бисёр аст.'),
(2117, 17, 88, 'Бигӯ; «Агар ҷину инс гирд оянд, то монанди ин Қуръон биёваранд, наметавонанд монанди онро биёваранд, ҳарчанд, ки якдигарро ёрӣ диҳанд».'),
(2118, 17, 89, 'Мо дар ин Куръон барои мардум ҳар мисолсро гуногун баён кардем, вале бештари мардум ҷуз ношукрӣ ҳеҷ коре накарданд.'),
(2119, 17, 90, 'Гуфтанд: «Ба ту имон намеоварем, то барои мо аз замин чашмае равон созӣ,'),
(2120, 17, 91, 'ё туро бӯстоне бошад аз дарахтони хурмову ангур, ки дар мобайнаш нахрҳо ҷорӣ гардонӣ,'),
(2121, 17, 92, 'ё чунон ки гуфтаӣ, осмонро пора-пора бар сари мо афканӣ ё Худову фариштагонро пеши мо ҳозир оварӣ,'),
(2122, 17, 93, 'ё туро хонае аз тилло бошад, ё ба осмон боло раВӣ. Ва мо ба осмон рафтанатро бовар накунем, то барои мо китобе, ки аз он бихонем, биёварӣ». Бигӯ: «Парвардигори ман пок аст. Оё на ин аст, ки ман инсоне ҳастам, ки ба пайғамбарӣ омадаам?».'),
(2123, 17, 94, 'Ҳеҷ чиз мардумро аз имон овардан он гоҳ ки ҳидояташон мекарданд, бознадошт, ғайри инки мегуфтанд: «Оё Худо инсонеро ба рисолат (пайғамбарӣ) мефиристад?»'),
(2124, 17, 95, 'Бигӯ: «Агар дар рӯи замин фариштагон ба оромӣ роҳ мерафтанд, барояшон ба паёмбарӣ фариштае аз осмон нозил мекардем».'),
(2125, 17, 96, 'Бигӯ; «Худо миёни ману шумо шоҳидиро кофист, зеро Ӯ ба бандагонаш огоҳу биност!»'),
(2126, 17, 97, 'Ва ҳар киро Худо ҳидоят кунад, хидоятшуда аст. Ва ҳар киро гумроҳ созад, ғайри Худо барои Ӯ сарпарасте наёбӣ ва дар рӯзи қиёмат, дар ҳоле ки чеҳраҳояшон рӯ ба замин аст, кӯру гунгу кар зинда ва ҷамъашон мекунем ва ҷаҳаннам ҷойгохи онҳост, ки ҳар чӣ шӯълаи он фурӯ нишинад, бештараш меафрӯзем.'),
(2127, 17, 98, 'Ин ҷазои онҳост, зеро ба оёти Мо имон наёварданд ва гуфтанд: «Оё чун мо устухон шудему хокӣ, моро ба сурати тозае зинда мекунанд?'),
(2128, 17, 99, 'Оё намедонанд, ки Худое, ки осмонҳову заминро офаридааст, қодир аст, ки монанди онҳоро биёфаринад ва барояшон муддати умре ниҳода, ки дар он шаке нест? Аммо золимон фақат инкор кунанд.'),
(2129, 17, 100, 'Бигӯ: «Агар шумо молики ҳамаи хазоини Парвардигори ман мебудед, аз бими дарвешӣ бахилӣ пеша мекардед, зеро одамӣ бахил аст!'),
(2130, 17, 101, 'Аз банӣ-Исроил бипурс, ки чун Мӯсо назди онон омад, ба ӯ нӯҳ нишонаи ошкоро дода будем. Ва Фиръавн ба ӯ гуфт: «Эй Мӯсо пиндорам, ки туро ҷоду карда бошанд».'),
(2131, 17, 102, 'Гуфт: «Ба ҳақиқат медонӣ, ин нишонаҳои ошкорро ҷуз Парвардигори осмонҳову замин нафиристода аст. Ва ман, эй Фиръавн, туро ба яқин ҳалокшуда мебинам».'),
(2132, 17, 103, 'Фиръавн қасди он дошт, кн онҳоро аз он сарзамин барканад, вале Мо ӯ ва ҳамаи ҳамроҳонашро ғарқ сохтем.'),
(2133, 17, 104, 'Ва пас аз ӯ ба банӣ-Исроил гуфтем: «Дар он сарзамин сокин шавед ва чун ваъдаи охират расад, ҳамаи шуморо бо ҳам зиндаву ҷамъ мекунем»,'),
(2134, 17, 105, 'Ин Қуръонро ба ҳақ нозил кардаем ва ба ҳак нозил шудааст ва туро нафиристодем, магар он ки хушхабар диҳӣ ва битарсони.'),
(2135, 17, 106, 'Ва Қуръонро ба тафориқ (ҷудо-ҷудо) нозил кардем, то ту онро оҳиста-оҳиста ва андак-андак бар мардум бихонӣ ва нозилаш кардем, нозил кардане ба камол.'),
(2136, 17, 107, 'Бигӯ; «Хоҳ ба он имон биёваред ё имон наёваред, онон, ки аз ин пеш дониш омӯхтаанд, чун Қуръон барояшон тиловат шавад, саҷдакунон бар рӯй дармеафтанд (Саҷда)'),
(2137, 17, 108, 'ва мегӯянд: «Пок аст Парвардигори мо. Ваъдаи Парвардигори мо анҷом ёфтанист».'),
(2138, 17, 109, 'Ва ба рӯ дармеафтаид ва мегирянд ва бар хушӯъашон (шикастагиашон) афзуда мешавад.'),
(2139, 17, 110, 'Бигӯ: «Чӣ Оллоҳро бихонед, чӣ Раҳмонро бихонед, ҳар кадомро, ки бихонед, номҳои некӯ аз они Ӯст». Садоятро ба намоз баланд макун ва низ садоятро ба он паст макун ва миёни ин ду роҳе интихоб кун.'),
(2140, 17, 111, 'Бигӯ; «Шукр Худоро, ки фарзанде надорад ва Ӯро шарике дар мулк нест ва ба нотавонӣ наяфтад, то ба ёрӣ мӯҳтоҷ шавад. Пас Ӯро такбир гӯй, такбире шоиста!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Сипос Худовандеро, ки бар бандаи худ ин китобро нозил кард ва ҳеҷ каҷиву инҳироф дар он наниҳод!'),
(2142, 18, 2, 'Китобе холӣ аз каҷи. То мардумро аз хашми шадиди худ битарсонад ва мӯъминонро, ки корҳои шоиста мекунанд, хабари хуш диҳад, ки мукофоте некӯ доранд,'),
(2143, 18, 3, 'дар ҳоле ки то абад дар он ҷовидонаанд.'),
(2144, 18, 4, 'Ва касонеро, ки гуфтанд: «Худо фарзанде ихтиёр кард», битарсонад.'),
(2145, 18, 5, 'Не. худ ба он донише доранд ва на падаронашон доштаанд. Чӣ бузург аст сухане, ки аз даҳонашон берун меояд ва дурӯғ мегӯянд!'),
(2146, 18, 6, 'Шояд агар ба ин сухан имон наёваранд, худатро ба хотирашон аз андӯҳ ҳалок созӣ.'),
(2147, 18, 7, 'Мо ҳар чӣ дар рӯи замин аст, зиннати он қарор додем то имтиҳонашон кунем, ки қадомашон ба амал беҳтаранд.'),
(2148, 18, 8, 'Ва низ мо растем, ки рӯи заминро чун биёбоне хушк хоҳем кард.'),
(2149, 18, 9, 'Оё пиндоштаӣ, ки асҳоби каҳф ва рақим аз нишонаҳои аҷибу ғариби Мо будаанд?'),
(2150, 18, 10, 'Он гоҳ ки он ҷавонмардон ба ғорҳо паноҳ бурданд ва гуфтанд: «Эй Парвардигори мо, моро аз сӯи худ раҳмат ато кун ва кори моро ба роҳи рост андоз».'),
(2151, 18, 11, 'Соле чанд дар он ғор ба хобашон кардем.'),
(2152, 18, 12, 'Сипас бедорашон кардем, то бидонем кадом як аз он ду гурӯҳ ҳисоботи муддати орамиданашонро доштаанд.'),
(2153, 18, 13, 'Мо хабарашонро ба ростӣ барои ту ҳикоят мекунем: онҳо ҷавонмардоне буданд, ки ба Парвардигорашон имон оварда буданд ва Мо низ бар ҳидояташон афзудем.'),
(2154, 18, 14, 'Бар дилҳояшон нерӯ бахшидем. Он гоҳ, ки бархостанд ва гуфтанд: «Парвардигори мо Парвардигори осмонҳову замин аст. Ҷуз Ӯ касеро худо нахонем, ки ҳар гоҳ чунин кунем, сухане сахт куфромез гуфта бошем.'),
(2155, 18, 15, 'Инҳо, ки қавми мо ҳастанд, ба ғайри Ӯ худоёне ихтиёр кардаанд. Чаро барои исботи худоии онҳо далели равшане намеоваранд? Кист ситамкортар аз касе, ки ба Худо дурӯғ мебандад?'),
(2156, 18, 16, 'Агар аз қавми худ канора ҷустаед ва ҷуз Худои якто худои дигареро намепарастед, ба ғор паноҳ баред ва Худо раҳмати худ бар шумо арзонӣ дорад ва неъмататонро дар он муҳайё дорад.'),
(2157, 18, 17, 'Ва хуршедро мебинӣ, ки чун бармеояд, аз ғорашон ба ҷониби рост майл мекунад ва чук ғуруб кунад, онҳоро вогузорад ва ба чап гардад. Ва онон дар саҳнаи ғоранд. Ва ин аз оёти Худост. Ҳар киро Худо ҳидоят кунад, ҳидоят ёфтааст ва ҳар киро гумроҳ созад, ҳаргиз корсозе роҳнамо барои ӯ нахоҳӣ ёфт.'),
(2158, 18, 18, 'Мепиндоштӣ, ки бедоранд, ҳол он ки дар хоб буданд ва Мо ононро ба дасти росту дасти чап мегардонидем ва сагашон бар даргоҳи ғор ду дасти худ дароз карда буд. Агар ба сарашон мерафтӣ, гурезон бозмегаштӣ ва аз онҳо сахт метарсидӣ.'),
(2159, 18, 19, 'Ҳамчунин бедорашон кардем, то бо якдигар гуфтушунид кунанд. Яке аз онҳо пурсид: «Чанд вақт аст, ки орамидаед?» Гуфтанд: «Як рӯз ё порае аз рӯзро орамидаем». Гуфтанд: «Парвардигоратон беҳтар донад, ки чанд вақт орамидаед. Якеро аз худ бо ин пулатон ба шаҳр бифиристед, то бингаред, ки ғизои покиза кадом аст ва бароятон аз он рӯзиятонро биёварад. Ва бояд, ки ба меҳрубонӣ рафтор кунад, то касе ба шумо огоҳӣ наёбад.'),
(2160, 18, 20, 'Зеро агар шуморо биёбанд, сангсор хоҳанд кард ё ба дини худ дароваранд. Ва агар чунин шавад, то абад наҷот нахоҳед ёфт».'),
(2161, 18, 21, 'Ба ин тарз мардумро ба ҳолашон огоҳ кардем то бидонанд, ки ваъдаи Худо рост аст ва дар қиёмат шубҳае нест. Он гоҳ дар бораи онҳо бо якдигар ба гуфтугӯ пардохтанд ва гуфтанд: «Бар рӯи онҳо биное бисозед, Парвардигорашон ба корашон огоҳтар аст». Ва онон, ки бар холашон огоҳтар шуда буданд, гуфтанд: «На, ин ҷоро масҷиде мекунем».'),
(2162, 18, 22, 'Хоҳанд гуфт: «Се тан буданд ва чаҳорумашон сагашон буд» ва мегӯянд: «Панҷ тан буданд ва шашуминашон сагашон буд». Тир ба торикӣ мезананд ва мегӯянд: «Ҳафт тан буданд ва ҳаштуминашон сагашон буд». Бигӯ: «Парвардигори ман ба адади онҳо донотар аст ва шумори онҳоро ҷуз андак касон намедонанд». Ва ту дар бораи онҳо ҷуз ба зоҳир муҷодала накун ва аз кас напурс.'),
(2163, 18, 23, 'Ҳаргиз магӯй: «Фардо чунин мекунам»,'),
(2164, 18, 24, 'магар Худованд бихоҳад. Ва чун фаромуш кунӣ, Парвардигоратро ба ёд ор ва бигӯ: «Шояд Парвардигори ман маро аз наздиктарин роҳ ҳидоят кунад».'),
(2165, 18, 25, 'Ва онон дар ғори худ сесад сол орамиданд ва нуҳ сол ба он афзудаанд.'),
(2166, 18, 26, 'Бигӯ: «Худованд донотар аст, ки чанд сол орамиданд. Ғайби осмонҳову замин аз они Ӯст. Чӣ биносту чӣ шунавост! Ҷуз Ӯ дӯсте надоранд ва касро дар фармони худ шарик насозад».'),
(2167, 18, 27, 'Аз китоби Парвардигорат ҳар чӣ бар ту ваҳй шудааст, тиловат кун. Сухани Ӯро тағйирдиҳандае нест, Ва ту ғайри Ӯ паноҳгоҳе намеёбӣ.'),
(2168, 18, 28, 'Ва ҳамроҳ бо касоне, ки ҳар субҳу шом Парвардигорашонро мехонанд ва хушнудии Ӯро меҷӯянд, худро ба сабр водор. Ва набояд чашмони ту барои ёфтани зебу зиннатҳои ин зиндагии дунявӣ аз инон як сӯ гардад. Ва аз он, ки дилашро аз зикри худ бехабар сохтаем ва аз паи ҳавои нафси худ меравад ва дар корҳояш исроф меварзад, панравӣ накун.'),
(2169, 18, 29, 'Бигӯ: «Ин сухани ҳақ аз ҷониби Парвардигори шумост. Ҳар кӣ бихоҳад, имон биёварад ва ҳар кӣ бихоҳад, кофир шавад». Мо барои кофирон, оташе, ки лаҳиби (шӯълаи) он ҳамаро дар бар мегирад, омода кардаем ва чун ба истиғоса (додхоҳӣ) об хоҳанд, аз обе чун миси гудохта, ки аз ҳарораташ чеҳраҳо кабоб мешавад, бихӯронандашон, чӣ оби баде ва чӣ оромгоҳе бад!'),
(2170, 18, 30, 'Касоне, ки имон овардаанд ва корҳое шоиста кардаанд, бидонанд, ки Мо мукофоти некӯкоронро барбод намекунем.'),
(2171, 18, 31, 'Барояшон биҳиштҳое ҷовид аст. Аз зери поҳошон наҳрҳо ҷорист. Биҳиштиёнро ба дастбандҳое зар меороянд ва ҷомаҳое сабз аз шоҳии нозуку дебои дурушт мепӯшанд ва дар он ҷо бар тахтҳо такя мезананд. Чӣ мукофоти некӯе ва чӣ оромгоҳи некӯе!'),
(2172, 18, 32, 'Барояшон достони ду мардро баён кун, ки ба яке ду токистон дода будем ва бар гирди онҳо дарахтони хурмо ва дар миёнашон киштзор карда будем.'),
(2173, 18, 33, 'Он ду токистон меваи худро медоданд бе ҳеҷ каму кост. Ва наҳре байни он ду ҷорӣ гардонидем.'),
(2174, 18, 34, 'Ҳосил аз они ӯ буд. Ба дӯсташ, ки бо ӯ гуфтугӯ мекард, гуфт; «Ман ба мол аз ту бештар ва ба нафар пирӯзтарам».'),
(2175, 18, 35, 'Ва он мард ситам бар хештан карда ба боғи худ даромад ва гуфт: «Напиндорам, ки ин бор ҳаргиз аз миён биравад.'),
(2176, 18, 36, 'Ва он мард ситам бар худаш карда ба боғи худ ҳам маро назди Худо баранд, ҷойгоҳе беҳтар аз ин боғ хоҳам ёфт».'),
(2177, 18, 37, 'Дӯсташ, ки бо ӯ гуфтугӯ мекард, гуфт: «Оё бар он кас, ки туро аз хок ва сипас аз нутфа биёфарид ва марде ростқомат кард, кофир шудаӣ?'),
(2178, 18, 38, 'Вале Ӯ Худои якто, Парвардигори ман аст ва ман ҳеҷ касро шарики Парвардигорам намесозам.'),
(2179, 18, 39, 'Чаро он гоҳ, ки ба боғи худ даромадӣ, нагуфтӣ: «Ҳар чӣ Худованд хоҳад ва ҳеҷ қуввае ғайри қувваи Худо нест?» Агар мебинӣ, ки дороиву фарзанди ман камтар аз ту аст,'),
(2180, 18, 40, 'шояд Парвардигори ман маро чизе беҳтар аз боғи ту диҳад. Шояд бар он боғ соъиқае (офате) бифиристад ва онро ба замине софу лағзанда бадал созад.'),
(2181, 18, 41, 'Ё оби он бар замин фурӯ равад ва ҳаргиз ба ёфтани он қудрат наёбӣ.'),
(2182, 18, 42, 'Ба самарааш офат расид ва бомдодон дасти пушаймонӣ бар даст месоид, ки чӣ ҳарче карда буд ва акнун ҳамаи биноҳояш фурӯ рехта аст. Ва мегӯяд;. «Эй кош, касеро шарики Парвардигорам насохта будам!»'),
(2183, 18, 43, 'Ғайри Худо гурӯҳе, ки ба ёриаш бархезанд, набуд ва худи ӯ қудрат надошт.'),
(2184, 18, 44, 'Он ҷо ёрӣ кардан Хулон ҳақро сазовор. Мукофоти Ӯ беҳтар ва саранҷомаш некӯтар аст.'),
(2185, 18, 45, 'Барояшон зиндагии дунёро мисол ор» ки чун боронест, ки аз осмон биборад ва бо он гиёҳони гуногун ба фаровонӣ бирӯяд. Ногоҳ хушк шавад ва бод ба ҳар сӯ парокандааш созад ва Худо бар ҳар коре тавоност!'),
(2186, 18, 46, 'Дороиву фарзанд зебу зиннатҳои ин зиндагонии дунёст ва кирдорҳои нек, ки ҷовидон бар ҷой мемонанд, назди Парвардигорат беҳтар ва умед бастан ба онҳо некӯтар аст.'),
(2187, 18, 47, 'Рӯзе, ки кӯҳҳоро ба роҳ меандозем ва заминро бинӣ, ки ҳар чӣ андарун дорад, берун афкандааст ва ҳамаро барои ҳисоб гирд меоварем ва як тан аз онҳоро ҳам раҳо намекунем.'),
(2188, 18, 48, 'Ҳама дар як саф ба Парвардигорат арза мешаванд. Акнун назди Мо омадед, ҳамчунон ки нахустин бор шуморо биёфаридем. Оё мепиндоштед, ки ҳаргиз бароятон ваъдагоҳе қарор нахоҳем дод?'),
(2189, 18, 49, 'Дафтари аъмол кушода шавад. Гунаҳкоронро бинӣ, ки аз он чӣ дар он омадааст, бимноканд ва мегӯянд: «Вой бар мо, ин чӣ дафтарест, ки ҳеҷ гуноҳи хурду бузургеро ҳисоб ношуда нагузоштааст». Он гоҳ амалҳои худро дар муқобили худ биёбанд ва Парвардигори ту ба касе ситам намекунад,'),
(2190, 18, 50, 'Ва он гоҳ, ки ба фариштагон гуфтем, ки Одамро саҷда кунед, ҳама ғайри Иблис, ки аз ҷин буд ва аз фармони Парвардигораш сар битофт, саҷда карданд. Оё шайтон ва фарзандокашро ба ҷои ман ба дӯстӣ мегиред, ҳол он, ки душмани шумоянд? Золимон бад чизеро ба ҷои Худо интихобиданд.'),
(2191, 18, 51, 'Он гоҳ ки оомонҳову замин ва худашонро меофаридам, аз онҳо ёрӣ нахостам. Зеро Ман гумроҳкунандагонро ба ёрӣ намегирам.'),
(2192, 18, 52, 'Ва рӯзе, ки мегӯяд: «Онҳоеро, ки мепиндоштед шарикони Мананд, ҷеғ занед». Ҷеғ заданд ва шарикон ҷавоб надиҳанд. Он гоҳ ҳалокатгоҳро миёнашон қарор диҳем.'),
(2193, 18, 53, 'З Чун гунаҳкорон оташро бубинанд, бидонанд, ки дар он хоҳанд афтод ва роҳи раҳоӣ аз он нест.'),
(2194, 18, 54, 'Дар ин Қуръон барои мардум ҳар гуна достоне баён кардаем, вале одами беш аз ҳама ба ҷадал (хусумат) бармехезад.'),
(2195, 18, 55, 'Мардумонро чун (аз Худо) ҳидоят омад, ҳеҷ чиз аз имон овардану бахшоиш хостан бознадошт, ғайри он, ки мебоист ба суннати пешиниён гирифтори азоб шаванд ё он ки азоби рӯёрӯю ошкоро бар онон фаро расад.'),
(2196, 18, 56, 'Ва Мо паёмбаронро нафиристодем, ҷуз он ки ба мардум мужда диҳанд ё бим. Ва кофирон мехоҳанд бо ҷидоли ботил ҳақро аз миён бибаранд, ва оёту хушдорҳои Маро ба масхара мегиранд.'),
(2197, 18, 57, 'Кист ситамкортар аз он, ки оёти Парвардигорашро барояш бихонанд ва ӯ рӯй гардонад ва корҳое, ки аз пеш карда, фаромӯш кунад? Бар дили онҳо парда афкандем, то оётро дарнаёбанд ва гӯшҳояшонро кар сохтем, ки агар ба роҳи ҳидояташон даъват кунӣ, ҳаргиз роҳ наёбанд.'),
(2198, 18, 58, 'Парвардигорат бахшояндаву меҳрубон аст. Агар мехост, ба сабаби кирдорашон дар ин ҷаҳон бозхосташон кунад, бо шитоб ба азобашон месупурд. Аммо азобро замоне муъайян аст, ки чун фаро расад, паноҳгоҳе наёбанд.'),
(2199, 18, 59, 'Ва он деҳаҳоро, чун кофир шуданд, ба ҳалокат расонидем ва барои ҳалокаташон ваъдае ниҳодем.'),
(2200, 18, 60, 'Ва Мӯсо ба шогирди худ гуфт: «Ман ҳамчунон хоҳам рафт то он ҷо, ки ду дарё ба ҳам расидаанд. Ё мерасам ё умрам ба сар меояд».'),
(2201, 18, 61, 'Чун он ду ба он ҷо, ки ду дарё ба ҳам расида буданд, расиданд, моҳияшонро фаромӯш карданд ва моҳӣ роҳи дарё гирифт ва дар об шуд.'),
(2202, 18, 62, 'Чун аз он ҷо гузаштанд, ба шогирди худ гуфт: «Хӯроки чоштамонро биёвар, ки дар ин сафарамон ранҷи фаровон дидаем».'),
(2203, 18, 63, 'Гуфт: «Оё ба ёд дорӣ он гоҳро, ки дар канори санг макон гирифта будем? Ман моҳиро фаромӯш кардаам. Ва ин шайтон буд, ки сабаб шуд фаромӯ шаш кунам ва моҳӣ ба тариқи тааҷҷубовар ба дарё рафт».'),
(2204, 18, 64, 'Гуфт: «Он ҷо ҳамон ҷоест, ки дар талабаш будаем». Ва ба пайи қадамҳои худ ҷустуҷӯкунон бозгаштанд.'),
(2205, 18, 65, 'Дар он ҷо бандае аз бандагони Моро, ки раҳмати худ ба ӯ арзонӣ дошта будем ва худ ба ӯ дониш омӯхта будем, биёфтанд.'),
(2206, 18, 66, 'Мӯсо гуфташ; «Оё бо ту биёям то аз он ҳидояте ки ба ту омӯхтаанд, ба ман биёмӯзӣ?»'),
(2207, 18, 67, 'Гуфт: «Туро сабри ҳамроҳӣ бо ман нест.'),
(2208, 18, 68, 'Ва чи гуна дар баробари чизе, ки ба он огоҳӣ Наёфтаӣ, сабр хоҳӣ кард?»'),
(2209, 18, 69, 'Гуфт: «Агар Худо бихоҳад, маро собир хоҳӣ ёфт, он чунон ки дар ҳеҷ коре туро нофармонӣ накунам».'),
(2210, 18, 70, 'Гуфт: «Агар аз паи ман меой, набояд, ки аз ман чизе бипурсӣ, то ман худ туро аз он огоҳ кунам».'),
(2211, 18, 71, 'Пас ба роҳ афтоданд, то ба киштӣ савор шуданд. Киштиро сӯрох кард. Гуфт: «Киштиро сӯрох мекунӣ, то мардумашро ғарқ созӣ? Коре, ки мекунӣ, коре сахт бузург ва зишт аст!»'),
(2212, 18, 72, 'Гуфт: «Нагуфтам. ки туро сабри ҳамроҳӣ бо ман нест?»'),
(2213, 18, 73, 'Гуфт: «Агар фаромӯш кардаам, маро бозхост макун ва ба ин андоза бар ман сахт магир».'),
(2214, 18, 74, 'Ва рафтанд, то ба писаре расиданд, ӯро кушт. Мӯсо гуфт: «Оё ҷони покеро, бе он ки қатле карда бошад, мекушӣ? Содиркунандаи коре зишт гардидӣ».'),
(2215, 18, 75, 'Гуфт: «Нагуфтам, ки туро сабри ҳамроҳӣ бо ман нест?»'),
(2216, 18, 76, 'Гуфт: «Агар аз ин пас аз ту чизе бипурсам, бо ман ҳамроҳӣ макун, ки аз ҷониби ман маъзур бошӣ».'),
(2217, 18, 77, 'Пас бирафтанд, то ба деҳе расиданд. Аз мардуми он деҳ таъоме хостанд. Аз мизбониашон сар бартофтанд. Он ҷо деворе диданд, ки наздик буд фурӯ резад. Деворро рост кард. Мӯсо гуфт: «Кош дар баробари ин кор музде мехостӣ».'),
(2218, 18, 78, 'Гуфт: «Акнун замени ҷудоӣ миёни ману туст ва туро аз сирри он корҳо, ки таҳаммулашонро надоштӣ, огоҳ мекунам.'),
(2219, 18, 79, 'Аммо он киштӣ аз они бенавоёне буд, ки дар даре кор мекарданд. Хостам маъюбаш кунам, зеро дар он сӯтарашон подшоҳе буд, ки киштиҳоро ба зурӣ мегирифт.'),
(2220, 18, 80, 'Аммо он писар падару модараш мӯъмин буданд. Тарсидем, ки он дуро ба исёну куфр андозад.'),
(2221, 18, 81, 'Хостем то дар ивази ӯ Парвардигорашон чизе насибашон созад ба покӣ беҳтар аз ӯ ва ба меҳрубонӣ наздиктар аз ӯ.'),
(2222, 18, 82, 'Аммо девор аз они ду писари ятим аз мардуми ин шаҳр буд, дар зераш ганҷе буд аз они писарон. Падарашон марде солеҳ буд, Парвардигори ту мехост он ду ба ҳадди булуғ расанд ва ганҷи худро берун оранд. Ва ман ин корро ба майли худ накардам. Раҳмати Парвардигорат буд. Ин аст сирри он сухан, ки гуфтам: «Туро сабри он нест».'),
(2223, 18, 83, 'Ва аз ту дар бораи Зулқарнайн мепурсанд. Бигӯ: «Бар эй шумо аз ӯ чизе мехонам»,'),
(2224, 18, 84, 'Мо ӯро дар замин қудрат додем ва роҳи расидан ба ҳар чизеро ба ӯ нишон додем.'),
(2225, 18, 85, 'Ӯ низ, ба он роҳ раҳсипор шуд.'),
(2226, 18, 86, 'То ба ғурубгоҳи хуршед расид. Дид, ки дар чашмае гилолуду сиёҳ ғуруб мекунад ва дар он ҷо мардуме ёфт. Гуфтем: «Эй Зулқарнайн, мехоҳӣ уқубаташон кун ва мехоҳӣ бо онҳо ба некӣ рафтор кун».'),
(2227, 18, 87, 'Гуфт: «Аммо ҳар кас, ки ситам кунад, мо уқубаташ хоҳем кард. Он гоҳ ӯро назди Парвардигораш мебаранд, то ӯ низ ба сахтӣ азобаш кунад.'),
(2228, 18, 88, 'Ва аммо ҳар кас, ки имон оварад ва коруҳои шоиста кунад, аҷре некӯ дорад. Ва дар бораи ӯ фармонҳои осон хоҳем ронд».'),
(2229, 18, 89, 'Боз ҳам роҳро пеш гирифт.'),
(2230, 18, 90, 'То ба макони баромадани офтоб расид. Дид бар қавме тулӯъ мекунад, ки ғайр аз партави он барояшон ҳеҷ пӯшише қарор надодаем.'),
(2231, 18, 91, 'Чунин буд. Ва мо бар аҳволи ӯ хабар дорем.'),
(2232, 18, 92, 'Боз ҳам роҳро пеш гирифт.'),
(2233, 18, 93, 'То ба миёни ду кӯҳ расид. Дар паси он ду кӯҳ мардумеро дид, ки гӯӣ ҳеҷ суханеро намефаҳманд.'),
(2234, 18, 94, 'Гуфтанд: «Эй Зулқарнайн, Яъҷуҷу Маъҷуҷ дар замин фасод мекунанд. Мехоҳи хироҷе барои ту муқаррар кунем, то ту миёни мову онҳо деворе бароварӣ?»'),
(2235, 18, 95, 'Гуфт: «Он чӣ Парвардигори ман маро бад-он тавоноӣ додааст, беҳтар аст. Маро ба нерӯи хеш мадад кунед, то миёни шумову онҳо деворе бароварам.'),
(2236, 18, 96, 'Барои ман тиккаҳои оҳан биёваред». Чун миёни он ду куҳ ҷамъкарда шуд, гуфт: «Бидамед!» То он оҳанро бигудохт. Ва гуфт: «Миси гудохта биёваред, то бар он резам».'),
(2237, 18, 97, 'На тавонистанд аз он боло раванд ва на дар он сӯрох кунанд.'),
(2238, 18, 98, 'Гуфт: «Ин раҳмате буд аз ҷониби Парвардигори ман ва чун ваъдаи Парвардигори ман даррасад, онро зеру забар кунад ва ваъдаи Парвардигори ман рост аст».'),
(2239, 18, 99, 'Ва дар он рӯз вогузорем, то мардум чун мавҷ равон гарданд ва чун дар сур дамида шавад, ҳамаро як ҷо гирд оварем.'),
(2240, 18, 100, 'Ва дар он рӯз ҷаҳаннамро ба кофирон, чунон ки бояд, нишон хоҳем дод.'),
(2241, 18, 101, 'Он касон, ки дидагонашон аз ёди ман дар парда буда ва тавони шунидак надоштаанд.'),
(2242, 18, 102, 'Оё кофирон пиндоранд, ки ба ҷои Ман бандагони Маро ба худоӣ гиранд? Мо ҷаҳаннамро омода сохтаем то манзилгоҳи кофирон бошад.'),
(2243, 18, 103, 'Бигӯ: «Оё шуморо огоҳ кунем, ки кирдори чӣ касоне беш аз ҳама зиёнкунандатар буд?'),
(2244, 18, 104, 'Онҳое, ки кӯшиданашон дар зиндагии дунё табоҳ шуд ва мепиндоштанд коре некӯ мекунанд.'),
(2245, 18, 105, 'Онон ба оёти Парвардигорашон ва ба мулоқот бо Ӯ имон наёварданд, пас амалҳояшон ночиз шуд ва Мо дар рӯзи қиёмат барояшон вазне қоил нестем.'),
(2246, 18, 106, 'Ҳамчунин ҷазои онон ба сабаби куфрашон ва низ ба он сабаб, ки оёту паёмбарони Маро масхара мекарданд, ҷаҳаннам аст.'),
(2247, 18, 107, 'Касоне, ки имон оварданд ва корҳое шоиста карданд, меҳмонсарояшон боғҳои фирдавс аст.'),
(2248, 18, 108, 'Дар он ҷо ҷовидонанд ва ҳаргиз ҳаваси кӯчидан накунанд.'),
(2249, 18, 109, 'Бигӯ: «Агар даре барои навиштани калимоти Парвардигори ман ранг шавад, дарё ба поён мерасад ва калимоти Парвардигори ман ба поён намерасад, ҳарчанд дарёи дигаре ба мадади он биёварем».'),
(2250, 18, 110, 'Бигӯ; Ман инсоне ҳастам монанди шумо. Ба ман ваҳй мешавад. Албатта Худои шумо худоест якто. Ҳар кас дидори Парвардигори худро умед мебандад, бояд кирдори шоиста дошта бошад ва дар парастиши Парвардигораш ҳеҷ касро шарик насозад».');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Коф, ҳо, ё, айн, сод.'),
(2252, 19, 2, 'Баёни бахшоиши Парвардигорат бар бандаи Худ Закариёст.'),
(2253, 19, 3, 'Он гоҳ ки Парвардигорашро пинҳонӣ садо дод.'),
(2254, 19, 4, 'Гуфт: «Эй Парвардигори ман, устухони ман суст гашта ва сарам аз пирӣ сафед шудааст ва ҳаргиз дар дуъо ба даргоҳи ту, эй Парвардигори ман, бебаҳра набудаам.'),
(2255, 19, 5, 'Ман пас аз марги худ аз хешовандонам бимнокам ва занам нозоянда аст. Маро аз ҷониби Худ фарзанде ато кун,'),
(2256, 19, 6, 'ки меросбари ман ва меросбари хонадони Яъқуб бошад ва ӯро, эй Парвардигори ман, шоиставу писандида гардон».'),
(2257, 19, 7, '«Эй Закариё, Мо туро ба писаре ба номи Яҳё мужда медиҳем. Аз ин пеш касеро ҳамноми ӯ насохтаем».'),
(2258, 19, 8, 'Гуфт: «Эй Парвардигори ман, маро аз куҷо писаре бошад, ҳол он ки занам нозоянда аст ва ман худ дар пирӣ ба фартутӣ расидаам».'),
(2259, 19, 9, 'Гуфт: «Парвардигорат инчунин гуфтааст: «Ин барои Ман осон аст ва туро, ки пеш аз ин чизе набудаӣ, биёфаридаам».'),
(2260, 19, 10, 'Гуфт: «Эй Парвардигори ман, ба ман нишоне бинамой». Гуфт: «Нишони ту ин аст, ки се шабу се рӯз бо мардум су хан нагӯӣ бе ҳеҷ осебе».'),
(2261, 19, 11, 'Аз меҳроб ба миёни қавми худ берун омад ва ба онон эълом кард, ки субҳу шом тасбеҳ гӯед.'),
(2262, 19, 12, '«Эй Яҳё, китобро ба қувват бигир». Ва дар кудакӣ ба ӯ доноӣ ато кардем.'),
(2263, 19, 13, 'Ва ба ӯ шафқат кардем ва покизааш сохтем ва ӯ парҳезгор буд.'),
(2264, 19, 14, 'Ба падару модар некӣ мекард ва ҷаббору гарданкаш набуд.'),
(2265, 19, 15, 'Салом бар ӯ, рӯзе, ки зода шуд ва рӯзе, ки мемирад ва рӯзе, ки дигар бор зинда хезонида мешавад!'),
(2266, 19, 16, 'Дар ин китоб Марямро ёд кун, он гоҳ, ки аз хонадони худ ба маконе рӯ ба сӯи баромадани офтоб яқсу шуд.'),
(2267, 19, 17, 'Миёни худ ва онон пардае кашид ва Мо Рӯҳи Худро наздаш фиристодем ва чун инсоне тамом бар ӯ намудор шуд.'),
(2268, 19, 18, 'Марям гуфт: «Аз ту ба Худои раҳмон паноҳ мебарам, ки парҳезгор бошӣ».'),
(2269, 19, 19, 'Гуфт: «Ман фиристодаи Парвардигори ту ҳастам, то туро писаре покиза бибахшам».'),
(2270, 19, 20, 'Гуфт: «Аз куҷо маро фарзанде бошад, ҳол он ки ҳеҷ башаре ба ман даст назадааст ва ман бадкора ҳам набудаам».'),
(2271, 19, 21, 'Гуфт: «Парвардигори ту инчунин гуфтааст: «Ин барон ман осон аст. Мо он писарро барои мардум оятеву бахшоише кунем ва ин корест ҳатмӣ ва поёнёфта».'),
(2272, 19, 22, 'Пас ба ӯ ҳомиладор шуд ва ӯро бо худ ба маконе дурафтода бурд.'),
(2273, 19, 23, 'Дарди зоидан ӯро ба сӯи танаи дарахти хурмое кашонид. Гуфт: «Эй кош, пеш аз ин мурда будам ва аз ёдҳо фаромӯш шуда будам».'),
(2274, 19, 24, 'Кудак аз зери ӯ нидо дод: «Ғамгин мабош, Парвардигорат аз зери пои ту ҷӯи обе равон сохт».'),
(2275, 19, 25, 'Дарахтро биҷунбон то хурмои тозачида бароят фурӯ резад.'),
(2276, 19, 26, 'Пас, эй зан, бихӯру биёшом ва шодмон бот ва агар аз одамиён касеро дидӣ, бигӯй: «Барои Худои раҳмон рӯза назр кардаам ва имрӯз бо ҳеҷ башаре сухан намегӯям».'),
(2277, 19, 27, 'Кудакро бардошт ва назди қавми худ овард. Гуфтанд: «Эй Марям, коре қабеҳ кардаӣ.'),
(2278, 19, 28, 'Эй хоҳари Ҳорун, на падарат марди баде буд ва на модарат зане бадкора».'),
(2279, 19, 29, 'Ба фарзанд ишора кард. Гуфтанд: «Чӣ гуна бо кудаке, ки дар гаҳвора аст, сухан бигӯем».'),
(2280, 19, 30, 'Кудак гуфт: «Ман бандаи Худоям, ба ман китоб дода ва маро паёмбар гардонидааст.'),
(2281, 19, 31, 'Ва ҳар ҷо, ки бошам, каро баракат дода ва то зиндаам, ба намозу закот васият кардааст.'),
(2282, 19, 32, 'Ва низ некӣ кардан ба модарам. Ва маро гарданкашу бадбахт насохтааст.'),
(2283, 19, 33, 'Салом бар ман, рӯзе, ки зода шудам ва рӯзе, ки мемирам ва рӯзе, ки дигар бор зинда карда мешавам!»'),
(2284, 19, 34, 'Ин аст Исо бинни Марям, ба сухани ҳақ ҳамон ки дар бораи ӯ дар шакку шубҳа буданд.'),
(2285, 19, 35, 'Насазад Худовандро, ки фарзанде бигирад. Пок аст. Чун продай коре кунад, мегӯяд: «Мавҷуд шав!» Пас мавҷуд мешавад.'),
(2286, 19, 36, 'Ва Оллоҳ Парвардигори ману Парвардигори шумост, пас Ӯро бипарастед, ки роҳи рост ки аст!'),
(2287, 19, 37, 'Гурӯҳҳо бо ҳам ихтилоф карданд, пас вой бар кофирон ба ҳангоми ҳузур дар он рӯзи бузург.'),
(2288, 19, 38, 'Чӣ хуб мешунаванд ва чӣ хуб мебинанд, он рӯз, ки назди Мо меоянд, вале ситамкорон имрӯз дар гумроҳии ошкоре ҳастанд.'),
(2289, 19, 39, 'Онҳоро аз рӯзи пушаймонӣ, ки кор ба поён омада ва онон ҳамчунон дар ҳоли ғафлату беимонӣ ҳастанд, битарсон.'),
(2290, 19, 40, 'Албатта Мо замин ва ҳар киро бар рӯи он аст, ба мерос мебарем ва ҳама ба назди Мо бозгардонда мешаванд.'),
(2291, 19, 41, 'Ва дар ин китоб Иброҳимро ёд кун, ки ӯ паёмбаре ростгӯ буд.'),
(2292, 19, 42, 'Он гоҳ, ки падарашро гуфт: «Эй падар, чаро чизеро, ки на мешунавад ва на мебинад ва на ҳеҷ ҳоҷати туро мебарорад, мепарастӣ?'),
(2293, 19, 43, 'Эй падар, маро донише додаанд, ки ба ту надодаанд. Пас ба ман пайравӣ кун, то туро ба роҳи рост ҳидоят кунам.'),
(2294, 19, 44, 'Эй падар, шайтонро напараст, зеро шайтон Худои раҳмонро нофармон буд.'),
(2295, 19, 45, 'Эй падар, метарсам азобе аз ҷониби Худои раҳмон ба ту расад ва ту дӯстдори шайтон бошӣ!»'),
(2296, 19, 46, 'Гуфт: «Эй Иброҳим, оё аз худоёни ман безор ҳастӣ? Агар бознаистӣ, сангсорат мекунам. Акнун замоне дароз аз ман дур бош!»'),
(2297, 19, 47, 'Гуфт: «Туро саломат бод! Аз Парвардигорам бароят бахшоиш хоҳам хост. Зеро Ӯ бар ман меҳрубон аст».'),
(2298, 19, 48, 'Аз шумо ва аз он чизҳое, ки ба ҷои Худои якто ибодат мекунед, канора мегирам ва Парвардигори худро мехонам, бошад, ки чун Парвардигорамро бихонам, маҳрум нашавам».'),
(2299, 19, 49, 'Чун аз онҳо ва он чӣ ба ҷои Худои якто мепарастиданд, канора гирифт, Исҳоқ ва Яъқубро ба ӯ бахшидем ва ҳамаро паёмбарӣ додем,'),
(2300, 19, 50, 'ва раҳмати Худро ба онҳо арзонӣ доштем ва сухани некӯ ва овозаи баланд додем.'),
(2301, 19, 51, 'Ва дар ин китоб Мӯсоре ёд кун. Ӯ бандае мухлис ва фиристодае паёмбар буд.'),
(2302, 19, 52, 'Ва ӯро аз ҷониби рости кӯҳи Тӯр нидо додем ва наздикаш сохтем, то бо ӯ роз гӯем.'),
(2303, 19, 53, 'Ва аз раҳмати худ бародараш Ҳоруни паёмбарро ба ӯ бахшидем.'),
(2304, 19, 54, 'Ва дар ин китоб Исмоъилро ёд кун. Ӯ дурустқавл ва фиристодае паёмбар буд.'),
(2305, 19, 55, 'Ва хонадони худро ба намозу закот амр мекард ва назди Парвардигораш шоиставу писандида буд.'),
(2306, 19, 56, 'Ва дар ин китоб Идрисро ёд кун. Ӯ ростгуфторе паёмбар буд.'),
(2307, 19, 57, 'Ӯро ба маконе баланл бардоштем.'),
(2308, 19, 58, 'Инҳо гурӯҳе аз паёмбарон буданд, ки Худо ба онон инъом карда буд, аз фарзандони Одам ва фарзандони онон, ки бо Нӯҳ дар киштӣ нишондем ва фарзандони Иброҳиму Исроил ва онҳо, ки ҳидояташон кардем ва интихоб кардем. Ва чун оёти Худои раҳмон бар онон тиловат мешуд, гирён ба саҷда меафтоданд. (Саҷда).'),
(2309, 19, 59, 'Сипас касоне ҷонишини инҳо шуданд, ки намозро зоеъ карданд ва пайрави шаҳватҳо гардиданд ва ба зудӣ ба Ғай хоҳанд афтод,'),
(2310, 19, 60, 'ғайри он кас, ки тавба кард ва имон овард ва корҳое шоиста кард. Инҳо ба биҳишт дохил мешаванд ва ҳеҷ мавриди ситам қарор намегиранд.'),
(2311, 19, 61, 'Биҳиштҳои ҷовид, ки Худои раҳмон дар ғайб ба бандагони худ ваъда кардааст ва ваъдаи ӯ фаро хоҳад расид.'),
(2312, 19, 62, 'Дар он ҷо сухани беҳуда намешунаванд. Гуфторашон салом аст. Ва рӯзияшон ҳар субҳу шом омода аст.'),
(2313, 19, 63, 'Ин ҳамон биҳиштест, ки ба бандагонамон, ки парҳезгорӣ кардаанд, ато мекунем.'),
(2314, 19, 64, 'Ва мо ҷуз ба фармони Парвардигори ту фуруд намеоем. Он чӣ дар пеши рӯи мо ва пушти сари мо ва миёни ин ду қарор дорад, аз они Ӯст. Ва Парвардигори ту фаромӯшкор нест.'),
(2315, 19, 65, 'Паргардигори осмонҳову замин ва он чӣ миёни он дӯст. Ӯро бипараст ва дар парастиш босабр бош. Оё ҳамноме барои Ӯ мешиносӣ?'),
(2316, 19, 66, 'Одами мегӯяд: «Оё замоне, ки бимирам, зинда аз гӯр берун оварда хоҳам шуд?»'),
(2317, 19, 67, 'Ва одами ба ёд надорад, ки ӯро пеш аз ин офаридаем ва ӯ худ чизе набуд?'),
(2318, 19, 68, 'Ба Парвардигорат савганд, онҳоро бо шайтонҳо гирд меоварем, он гоҳ ҳамаро дар атрофи ҷаҳаннам ба зону менишонем.'),
(2319, 19, 69, 'Он гоҳ аз ҳар гурӯҳ касонеро, ки дар баробари Худои раҳмон бештар саркашӣ кардаанд, ҷудо мекунем.'),
(2320, 19, 70, 'Ва Мо онҳоеро, ки сазовортар ба дохил шудан дар оташ ва сӯхтан дар он бошанд, беҳтар мешиносем.'),
(2321, 19, 71, 'Ва ҳеҷ як аз шумо нест, ки вориди ҷаҳаннам нашавад ва ин ҳукмест ҳатмӣ аз ҷониби Парвардигори ту,'),
(2322, 19, 72, 'Он гоҳ парҳезгоронро наҷот медиҳем ва ситамкоронро ҳамчунон ба зону нишаста дар он ҷо мегузорем.'),
(2323, 19, 73, 'Чун оёти равшангари моро бар онон бихонанд, кофирон ба мӯъминон мегӯянд: «Кадом як аз ду фирқаро ҷойгоҳи беҳтар ва маҳфили некӯтар аст?»'),
(2324, 19, 74, 'Пеш аз онҳо чӣ бисёр мардумеро ба ҳалокат расонидаем, ки аз ҷиҳати хонаву ҷой ва сурату намуд беҳтар аз онҳо буданд.'),
(2325, 19, 75, 'Бигӯ: «Ҳар кас, ки дар гумроҳӣ бошад. Худои раҳмон ӯро ба фузунӣ мадад мерасонад», то он гоҳ он чиро ба ӯ ваъда дода шудааст, бингарад: ё азоб ва ё қиёмат. Он гоҳ хоҳанд донист, ки чӣ касеро ҷойгоҳ бадтару лашкар нотавонтар аст.'),
(2326, 19, 76, 'Ва Худо бар роҳнамоии онон, ки ҳидоят ёфтаанд, хоҳад афзуд ва назди Парвардигори ту мукофот ва натиҷаи кирдорҳои шоистае, ки боқӣ монданианд, беҳтар аст.'),
(2327, 19, 77, 'Оё он касеро, ки ба оёти Мо кофир буд, дидӣ, ки мегуфт: «Албатта, ба ман молу фарзанд дода хоҳад шуд!»'),
(2328, 19, 78, 'Оё аз ғайб огоҳӣ доптт, ё аз Худои раҳмон паймон гирифта буд?'),
(2329, 19, 79, 'На чунин аст. Он чӣ мегӯяд, хоҳем навишт ва албатта, бар азобаш хоҳем афзуд.'),
(2330, 19, 80, 'Он чиро, ки мегӯяд аз ӯ мегирем, то танҳо назди мо биёяд.'),
(2331, 19, 81, 'Ва ба ғайр аз Оллоҳ худоёне ихтиёр карданд, то сабаби изааташон гарданд.'),
(2332, 19, 82, 'На чунин аст. Ба зуди ибодаташонро инкор кунанд ва ба мухолифаташон (бар зидди онҳо) бархезанд.'),
(2333, 19, 83, 'Оё надидаӣ, ки Мо шаётинро ба сӯи кофирон фиристодем то онҳоро ташвиқ кунанд (ба гуноҳу куфр).'),
(2334, 19, 84, 'Пас ту бар онон шитоб макун, ки Мо рӯзҳоятонро ба диққат мешуморем.'),
(2335, 19, 85, 'Рӯзе, ки парҳезгоронро назди Худои раҳмон савора гирд оварем,'),
(2336, 19, 86, 'ва гунаҳкоронро ташнаком ба ҷаҳаннам биронем.'),
(2337, 19, 87, 'Аз шафоъат бенасибанд, фақат он кас, ки бо Худои раҳмон паймоне баста бошад. (шафоъат ёбад).'),
(2338, 19, 88, 'Гуфтанд: «Худой раҳмон соҳиби фарзандест».'),
(2339, 19, 89, 'Дар ҳақиқат сухане зишт овардаед.'),
(2340, 19, 90, 'Наздик аст, ки аз ин сухан осмонҳо бишикофанд ва замин шикофта шавад ва кӯҳҳо фурӯ афтанд ва дарҳам резанд,'),
(2341, 19, 91, 'зеро барои Худои раҳмон фарзанде қоил шудаанд.'),
(2342, 19, 92, 'Худои раҳмонро сазовор нест, ки соҳиби фарзанде бошад.'),
(2343, 19, 93, 'Ҳеҷ чиз дар осмонҳову замин нест, ки ба бандагӣ сӯи Худои раҳмон наояд.'),
(2344, 19, 94, 'Ӯ ҳамаро ҳисоб карда ва як-як шумурда аст.'),
(2345, 19, 95, 'Ва ҳамагиашон дар рӯзи қиёмат танҳо назди Ӯ меоянд.'),
(2346, 19, 96, 'Худой раҳмон касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, маҳбуби ҳама гардонад.'),
(2347, 19, 97, 'Ин Қуръонро бар забони ту осон кардем, то парҳезгоронро мужда диҳӣ ва саркашонро битарсонӣ.'),
(2348, 19, 98, 'Ва чӣ бисёр мардумеро пеш аз онҳо ҳалок кардаем. Оё ҳеҷ як аз онҳоро меёбӣ ё ҳатто андак овозе аз онҳо мешунавӣ?');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'То, ҳо.'),
(2350, 20, 2, 'Қуръонро бар ту нозил накардаем, ки дар ранҷ Афтӣ.'),
(2351, 20, 3, 'Танҳо ҳущдорест барои он кӣ метарсад.'),
(2352, 20, 4, 'Аз ҷониби касе, ки замину осмонҳои баландро офаридааст, нозил шуда.'),
(2353, 20, 5, 'Худои раҳмон бар арш истилову иҳота дорад.'),
(2354, 20, 6, 'Аз они Ӯст он чӣ дар осмонҳову замин ва миёни онҳост ва он чӣ дар зери замин аст.'),
(2355, 20, 7, 'Ва агар сухани баланд гӯӣ, Ӯ ба рози ниҳон ва ниҳонтар огоҳ аст.'),
(2356, 20, 8, 'Оллоҳ — он ки ҳеҷ худое ғайри Ӯ нест, номҳои хуб аз они Ӯст.'),
(2357, 20, 9, 'Оё хабари Мӯсо ба ту расидааст?'),
(2358, 20, 10, 'Он гоҳ, ки оташе дид ва ба хонаводаи худ гуфт: «Ин ҷо бошед, ки ман аз дур оташе мебинам, шояд бароятон шӯълаеро биёварам ё дар рӯшноии он роҳе биёбам».'),
(2359, 20, 11, 'Чун назди оташ омад, нидо дода шуд: «Эй Мӯсо!'),
(2360, 20, 12, 'Ман Парвардигори ту ҳастам. Пойафзоратро берун кун, ки инак дар водии муқаддаси Туво ҳастй.'),
(2361, 20, 13, 'Ва ман туро интихоб кардам (ба пайғамбарӣ). Пас ба он чӣ ваҳй мешавад, гӯш дор.'),
(2362, 20, 14, 'Худои якто Ман ҳастам. Ҳеҷ Худое ғайри Ман нест. Пас Маро бипараст ва то Маро ёд кунӣ, намоз бигзор.'),
(2363, 20, 15, 'Қиёмат омаданӣ аст. Мехоҳам замони онро пинҳон дорам, то ҳар кас дар муқобили коре, ки кардааст, ҷазо бубинад.'),
(2364, 20, 16, 'Он кас, ки ба он имон надорад ва пайрави ҳавои худ аст, туро аз он (имони ба қиёмат) рӯйгардон накунад, то ба ҳалокат афтӣ.'),
(2365, 20, 17, 'Эй Мӯсо, он чист ба дасти ростат?'),
(2366, 20, 18, 'Гуфт: «Ин асои ман аст. Бар он такя мекунам ва барои гӯсфандонам бо он барг мерезам. Ва маро бо он корҳои дигар аст».'),
(2367, 20, 19, 'Гуфт: «Эй Мӯсо, онро бияфкан».'),
(2368, 20, 20, 'Бияфкандаш. Ба ногаҳон море шуд, ки медавид.'),
(2369, 20, 21, 'Гуфт: «Бигираш ва матарс. Бори дигар онро ба сурати нахустинаш бозмегардонем.'),
(2370, 20, 22, 'Дасти хеш дар бағал кун, бе ҳеҷ айбе сафед берун ояд. Ин ҳам мӯъҷизаест дигар,'),
(2371, 20, 23, 'то оятҳои бузургтари худро ба ту нишон диҳем.'),
(2372, 20, 24, 'Назди Фиръавн бирав, ки саркашӣ мекунад».'),
(2373, 20, 25, 'Гуфт: «Эй Парвардигори ман, синаи маро барои ман кушода гардон.'),
(2374, 20, 26, 'Ва кори маро осон соз.'),
(2375, 20, 27, 'Ва гиреҳ аз забони ман бикушой,'),
(2376, 20, 28, 'то гуфтори маро бифаҳманд.'),
(2377, 20, 29, 'Ва ёваре аз хонадони ман барои ман қарор деҳ'),
(2378, 20, 30, 'бародарам Ҳорунро.'),
(2379, 20, 31, 'Пушти маро ба ӯ маҳкам кун.'),
(2380, 20, 32, 'Ва дар кори ман шарикаш гардон,'),
(2381, 20, 33, 'то Туро фаровон ситоиш кунем.'),
(2382, 20, 34, 'Ва Туро фаровон ёд кунем,'),
(2383, 20, 35, 'ки Ту бар ҳоли мо огоҳ будаӣ».'),
(2384, 20, 36, 'Гуфт: «Эй Мӯсо, ҳар чӣ хостӣ, ба ту дода шуд.'),
(2385, 20, 37, 'Ва Мо бори дигар ба ту неъмати фаровон додаем.'),
(2386, 20, 38, 'Он гоҳ, ки бар модарат он чӣ ваҳй карданӣ буд, ваҳй кардем,'),
(2387, 20, 39, 'ки ӯро дар сандуқе андоз, сандуқро ба дарё парто, то дарё ба соҳилаш андозад ва яке аз душманони Ман ва душманони ӯ сандуқро бигирад. Муҳаббати худ ба ту арзонӣ доштам, то зери назари Ман парвариш ёбӣ.'),
(2388, 20, 40, 'Он гоҳ, ки хоҳарат мерафт ва мегуфт: «Мехоҳед шуморо ба касе, ки нигаҳдорияш кунад, роҳ бинамоям?» Мо туро назди модарат бозгардонидем, то чашмонаш равшан гардад ва ғам нахӯрад. Ва ту якеро бикуштӣ ва Мо аз ғам озодат кардем ва борҳо туро биёзмудем. Ва соле чанд миёни мардуми Мадйан зистӣ. Ва акнун, эй Мӯсо, дар ин ҳангом, ки тақдир карда будем, омадаӣ.'),
(2389, 20, 41, 'Туро хоси Худ кардам.'),
(2390, 20, 42, 'Ту ва бародарат оёти Маро бибаред ва дар рисолати Ман сустӣ макунед!'),
(2391, 20, 43, 'Ба сӯи Фиръавн биравед, ки ӯ аз ҳад гузаштааст.'),
(2392, 20, 44, 'Бо ӯ ба нармӣ сухан гӯед, шояд панд гирад ё битарсад».'),
(2393, 20, 45, 'Гуфтанд: «Эй Парвардигори мо, бим дорем, ки бар мо зулму ситам кунад ё саркашӣ аз ҳад бигузаронад».'),
(2394, 20, 46, 'Гуфт: «Матарсед. Ман бо шумо ҳастам. Мешунаваму мебинам.'),
(2395, 20, 47, 'Пас назди ӯ раведу гӯед: «Мо расулони Парвардигори туем. Банӣ-Исроилро бо мо бифирист ва озорашон мадеҳ. Мо нишонае аз Парвардигорат бароят овардаем. Ва салом бар он кас, ки аз паи ҳидоят қадам ниҳад.'),
(2396, 20, 48, 'Албатта ба мо ваҳй шудааст, ки азоб барои он касест, ки ростро дурӯғ шуморад ва аз он рӯй гардонад»,'),
(2397, 20, 49, 'Гуфт: «Эй Мӯсо, Парвардигори шумо кист?»'),
(2398, 20, 50, 'Гуфт: «Парвардигори мо ҳамон касест, ки офариниши (сурату шакли) ҳар чизеро ба Ӯ дода, сипас ҳидояташ кардаст».'),
(2399, 20, 51, 'Гуфт: «Ҳоли қавмҳое, ки аз ин пеш мезистанд, чист?»'),
(2400, 20, 52, 'Гуфт: «Илми он дар китобест назди Парвардигори ман, Парвардигори ман на хато мекунад ва на фаромӯш»,'),
(2401, 20, 53, 'Касест, ки заминро оромгоҳи шумо сохт ва бароятон дар он роҳҳое падид овард ва аз осмон борон фиристод, то бо он анвоъе гуногун аз наботот бирӯёнем.'),
(2402, 20, 54, 'Бихӯред ва чорпоёнатонро бичаронед. Дар ин барои хирадмандон ибратҳост!'),
(2403, 20, 55, 'Шуморо аз замин офаридем ва ба он бозмегардонем ва бори дигар аз он берун меоварем.'),
(2404, 20, 56, 'Албатта ҳамаи мӯъҷизаҳои Худро ба ӯ нишон додем, вале дурӯғ баровард ва сар боззад.'),
(2405, 20, 57, 'Гуфт: «Эй Мӯсо, оё назди мо омадаӣ, то моро ба ҷудоӣ аз сарзаминамон берун кунӣ?'),
(2406, 20, 58, 'Мо низ дар баробари ту ҷодуе чун ҷодуи ту меоварем. Байни мову худ ваъдагоҳе дар замини ҳамвор бигузор, ки на мо ваъдаро хилоф кунем ва на ту».'),
(2407, 20, 59, 'Гуфт; «Ваъдагоҳи шумо рӯзи зинат аст ва ҳамаи мардум бад-он ҳангом, ки офтоб баланд гардад гирд оянд.'),
(2408, 20, 60, 'Фиръавн бозгашт ва ёрони ҳилагари худ ҷамъ кард ва бозомад.'),
(2409, 20, 61, 'Мӯсо гуфташон: «Вой бар шумо, бар Худо дурӯғ мабандед, ки ҳамаи шуморо ба азобе ҳалок кунад ва ҳар ки дурӯғ бандад, ноумед шавад».'),
(2410, 20, 62, 'Пас ба якдигар дар кори худ ба машварат пардохтанд. Ва пинҳон розҳо гуфтанд:'),
(2411, 20, 63, 'Гуфтанд: «Ин ду ҷодугароне ҳастанд, ки мехоҳанд ба ҷодуи худ шуморо аз сарзаминатон биронанд ва дини беҳтарини шуморо маҳв кунанд.'),
(2412, 20, 64, 'Шумо тадбирҳои худ ҷамъ кунед ва дар як саф биёед. Касе, ки имрӯз пирӯз шавад наҷот ёбад».'),
(2413, 20, 65, 'Гуфтанд: «Эй Мӯсо, оё ту мепартоӣ ё мо нахуст бипартоем?».'),
(2414, 20, 66, 'Гуфт: «Шумо бипартоед». Ногаҳон аз ҷодуе, ки карданд, чунон дар назараш омад, ки он бандҳо ва асоҳо ба ҳар сӯ медаванд.'),
(2415, 20, 67, 'Мӯсо худашро тарсон ёфт.'),
(2416, 20, 68, 'Гуфтем; «Матарс, ки ту болотар ҳастӣ.'),
(2417, 20, 69, 'Он чӣ дар дасти рост дорӣ, бияфкан, то ҳар чиро, ки сохтаанд, фурӯ барад. Онон ҳилаи ҷодувон сохтаанд ва ҷодугар ҳеҷ гоҳ пирӯз намешавад».'),
(2418, 20, 70, 'Соҳирон ба саҷда водор шуданд. Гуфтанд: «Ба Парвардигори Ҳоруну Мӯсо имон овардем».'),
(2419, 20, 71, 'Фиръавн гуфт: «Оё пеш аз он, ки шуморо рухсат диҳам, ба Ӯ имон овардед? Ӯ бузурги шумост, ки ба шумо ҷодугарӣ омӯхтааст. Дастҳову поҳотонро аз чапу рост мебурам ва бар танаи дарахти хурмо ба доратон меовезам то бидонед, ки азоби кадом як аз мо сахттару пояндатар аст».'),
(2420, 20, 72, 'Гуфтанд: «Савганд ба он ки моро офаридааст, ки туро бар он нишонаҳои равшан, ки дидаем, тарҷеҳ намедиҳем (беҳтар намешуморем), ба ҳар чӣ хоҳӣ, ҳукм кун, ки ривоҷи ҳукми ту дар зиндагии инҷаҳонист.'),
(2421, 20, 73, 'Инак ба Парвардигори худ имон овардем то аз хатоҳои мо ва он ҷодугарӣ, ки моро ба он водоштӣ, даргузарад, ки Худо беҳтару пойдортар аст».'),
(2422, 20, 74, 'Албатта ҳар кас, ки гунаҳкор назди Парвардигораш биёяд, ҷаҳаннам ҷойгоҳи ӯст, ки дар он ҷо на мемирад ва на зиндагӣ мекунад.'),
(2423, 20, 75, 'Ва онон, ки бо имон назди ӯ оянд ва корҳои шоиста кунанд, соҳиби дараҷоте баланд бошанд.'),
(2424, 20, 76, 'Биҳиштҳои ҷовидон, ки дар он наҳрҳо равон аст ва абадӣ дар он ҷо бошанд. Ва ин аст музди покон.'),
(2425, 20, 77, 'Ба Мӯсо ваҳй кардем, ки бандагони Моро шабҳангом берун бибар ва барояшон (бо мӯъҷиза) дар дарё гузаргоҳе хушк биҷӯй ва матарс, ки бар ту даст ёбанд ва бим ба дил роҳ мадеҳ!'),
(2426, 20, 78, 'Фирьавн, бо лашкарҳояш аз паяшон равон шуд. Ва дарё чунон, ки бояд, ононро дар худ фурӯ пушид.'),
(2427, 20, 79, 'Фиръавн қавмашро гумрох кард, на роҳнамоӣ.'),
(2428, 20, 80, '«Эй банӣ-Исроил, шуморо аз душманатон раҳонидем ва бо шумо дар ҷониби рости кӯҳи Тур ваъда ниҳодем ва бароятон манна (ширинӣ) ва салво (бедона) нозил кардем.'),
(2429, 20, 81, 'Аз чизҳои покиза, ки шуморо рӯзӣ додаем, бихӯред ва аз ҳад мегузаронедаш, то мабод хашми Ман ба шумо расад, ки ҳар кас, ки хашми Ман ба ӯ бирасад, дар оташ афтад.'),
(2430, 20, 82, 'Ҳар кас, ки тавба кунад ва имон оварад ва кори шоиста кунад ва ба роҳи ҳидоят бияфтад, ӯро мебахшоем.'),
(2431, 20, 83, 'Эй Мӯсо, чӣ чиз туро водошт, то бар қавмат пешӣ гирӣ?» (яъне аз қавмат пештар биёӣ).'),
(2432, 20, 84, 'Гуфт: «Онҳо ҳамонҳоянд, ки аз паи ман равонанд. Эй Парвардигори ман, ман ба сӯи Ту шитофтам, то хушнуд гардӣ».'),
(2433, 20, 85, 'Гуфт: «Мо қавми туро пас аз ту озмоиш кардем ва Сомирӣ гумроҳашон сохт».'),
(2434, 20, 86, 'Мӯсо хашмгин ва пурандӯҳ назди қавмаш бозгашту гуфт: «Эй қавми ман, оё Парвардигоратон шуморо ваъдаҳои некӯ надода буд? Оё дер кардани ман дароз кашид ё хостед, ки хашми Парвардигоратон ба шумо фуруд ояд, ки ваъдаи маро хилоф кардед?»'),
(2435, 20, 87, 'Гуфтанд; «Мо ба ихтиёри худ ваъдаи ту хилоф накардем. Аз зару зевари он қавм бардоштем. Онҳоро дар оташ андохтем. Ва Сомирӣ низ андохт.'),
(2436, 20, 88, 'Ва барояшон тандиси (пайкараи) гӯсолае, ки наъраи говонро дошт, бисохт ва гуфтанд: «Ин худоӣ шумо ва худои Мӯсост. Ва Мӯсо фаромуш карда аст».'),
(2437, 20, 89, 'Оё намедонанд, ки ҳеҷ посухе ба суханашон намедиҳад ва ҳеҷ нафъу зиёне барояшон надорад?'),
(2438, 20, 90, 'Ҳорун низ пеш аз ин ба онҳо гуфта буд: «Эй қавми ман, шуморо ба ин гӯсола озмудаанд. Парвардигори шумо Худои раҳмон аст. Аз паи ман биёед ва фармонбардори ман бошед!»'),
(2439, 20, 91, 'Гуфтанд; «Мо ҳамеша ба парастиши ӯ менишинем, то Мӯсо ба назди мо бозгардад».'),
(2440, 20, 92, 'Гуфт: «Эй Ҳорун, ҳангоме ки дидӣ гумроҳ мешаванд,'),
(2441, 20, 93, 'чаро аз паи ман наомадӣ? Оё ту низ аз фармони ман сарпечӣ карда будӣ?»'),
(2442, 20, 94, 'Гуфт: «Эй писари модарам, чанг бар ришу сари ман маяндоз. Ман тарсидам, ки бигӯӣ: «Ту миёни банӣ-Исроил ҷудоӣ афкандӣ ва гуфтори маро риоят накардӣ».'),
(2443, 20, 95, 'Гуфт: «Ва ту, эй Сомирӣ, ин чӣ коре буд, ки кардӣ?»'),
(2444, 20, 96, 'Гуфт: «Ман чизе дидам, ки онҳо намедиданд. Муште аз хоке, ки нақши пои он расул (Ҷибриил) бар он буд, баргирифтам ва дар он қолаб афкандам ва нафси ман ин корро дар чашми ман биёрост».'),
(2445, 20, 97, 'Гуфт: «Бирав, дар зиндагии ин дунё чунон шавӣ, ки пайваста бигӯй: «Ба ман наздик машав». Ва низ туро ваъдаест, ки! аз он раҳо нашавӣ ва инак ба худоят, ки пайваста ибодаташ мекардӣ, бингар, ки месӯзонемаш ва ба дарёаш меафшонем.'),
(2446, 20, 98, 'Ҷуз ин нест, ки худои шумо Оллоҳ аст, ки ҳеҷ худое ғайри Ӯ нест ва илмаш ҳама чизро дар бар гирифтааст»,'),
(2447, 20, 99, 'Инчунин хабарҳои гузаштаро барои ту ҳикоят мекунем. Ва ба ту аз ҷониби Худ Қуръонро ато кардем.'),
(2448, 20, 100, 'Ҳар кас, ки аз он рӯй гардонад, рӯзи қиёмат бори гуноҳ бар дӯш мекашад.'),
(2449, 20, 101, 'Дар он кор хамеша бимонанд. Ва бори, рӯзи қиёмат барояшон бори бадест!'),
(2450, 20, 102, 'Рӯзе, ки дар сур дамида шавад ва гунаҳкоронро дар он рӯз кабудчашм гирд меоварем.'),
(2451, 20, 103, 'Оҳиста бо ҳам сухан мегӯянд, ки даҳ рӯз беш дар дунё наистодаед.'),
(2452, 20, 104, 'Мо бар гуфтори онҳо огоҳтарем, он гоҳ ки некӯравиштарини онҳо мегӯяд: «Ҷуз як рӯз наистодаед».'),
(2453, 20, 105, 'Туро аз кӯҳҳо мепурсанд. Бигӯ: «Парвардигори ман ҳамаро пароканда месозад.'),
(2454, 20, 106, 'ва онҳоро ба замине ҳамвор бадал мекунад;'),
(2455, 20, 107, 'дар он ҳеҷ каҷиву пастиву баландӣ намебинӣ».'),
(2456, 20, 108, 'Дар он рӯз аз паи он доъӣ (Исрофил), ки ҳеҷ каҷиро дар ӯ роҳ нест, равон гарданд. Ва садоҳо дар баробари Худои раҳмон паст мешаванд ва аз ҳеҷ кас ғайри садои пое нахоҳӣ шунид.'),
(2457, 20, 109, 'Дар он рӯз шафоъат нафъ надиҳад, фақат онро, ки Худои раҳмон иҷозат диҳад ва суханашро биписандад, нафъ диҳад.'),
(2458, 20, 110, 'Ҳар чиро дар пеши рӯи онҳост ва ҳар чиро дар пушти сарашон аст медонад ва илми онон Ӯро дар бар нагирад.'),
(2459, 20, 111, 'Ва чеҳраҳо дар баробари Худои зиндаи поянда хозеъ (мутеъ) мешавад ва ҳар кӣ бори куфр бар дӯш мекашад, ноумед мегардад.'),
(2460, 20, 112, 'Ва ҳар кас, ки корҳои шоиста кунад ва мӯъмин бошад, набояд аз ҳеҷ ситаме ва шикасте битарсад.'),
(2461, 20, 113, 'Инчунин онро Қуръоне арабӣ нозил кардем ва дар он гуногун хушдор додем, шояд битарсанд ё панде тоза гиранд.'),
(2462, 20, 114, 'Пас бартар аст Худои якто- он подшоҳи ростин! Ва пеш аз он ки ваҳй ба поён расад, дар хондани Қуръон шитоб макун. Ва бигӯ: «Эй Парвардигори ман, ба илми ман бияфзой».'),
(2463, 20, 115, 'Ва мо пеш аз ин бо Одам паймон бастем, вале фаромӯш кард ва босабраш наёфтем.'),
(2464, 20, 116, 'Ва он гоҳ, ки ба фариштагон гуфтем: «Одамро саҷда кунед». Ҳама ғайри Иблис, ки сарпечӣ кард, саҷда карданд.'),
(2465, 20, 117, 'Гуфтем: «Эй Одам, ин душмани ту ва ҳамсари туст, шуморо аз биҳишт берун накунад, ки бадбахт шавӣ,'),
(2466, 20, 118, 'ки ту дар биҳишт на гурусна мешавӣ ва на бараҳна мемонӣ'),
(2467, 20, 119, 'ва на ташна мешавӣ ва на дучори тобиши офтоб».'),
(2468, 20, 120, 'Шайтон васвасааш карду гуфт: «Эй Одам, оё туро ба дарахти ҷовидонӣ ва мулки дурнашаванда роҳ бинамоям?»'),
(2469, 20, 121, 'Аз он дарахт хӯрданд ва шармгоҳашон дар назарашон падидор шуд. Ва ҳамчунон барги дарахтони биҳишт бар онҳо мечаспониданд. Одам ба Парвардигори худ оси шуд ва роҳ гум кард.'),
(2470, 20, 122, 'Сипас Парвардигораш ӯро ихтиёр кард ва тавбаашро бипазируфт ва ҳидояташ кард.'),
(2471, 20, 123, 'Гуфт: «Ҳамагӣ аз он ҷо поин равед, душманони якдигар! Агар аз ҷониби Ман шуморо роҳнамоӣ омад, ҳар кас аз он роҳнамоии Ман пайравӣ кунад, на гумроҳ мешавад ва на тирабахт.'),
(2472, 20, 124, 'Ва ҳар кас, ки аз ёди Ман рӯй гардонад, зиндагиаш танг шавад ва дар рӯзи қиёмат нобино зиндааш созем».'),
(2473, 20, 125, 'Гӯяд: «Эй Парвардигори ман, чаро маро нобино зинда кардӣ ва ҳол он ки ман бино будам?»'),
(2474, 20, 126, 'Гуяд: «Ҳамчунон ки ту оёти Моро фаромӯш мекардӣ, имрӯз худ фаромӯш гаштаӣ».'),
(2475, 20, 127, 'Инчунин исрофкорон ва касонеро, ки ба оётӣ Парвардигорашон имон намеоваранд, ҷазо медиҳем. Албатта азоби охират сахттару пойдортар аст!'),
(2476, 20, 128, 'Оё он ҳама мардуме, ки пеш аз ин дар масокини инҳо роҳ мерафтанд ва Мо ҳамаро ҳалок кардем, сабаби ҳидояти инҳо нашудаанд? Инҳо нишонаҳоест барои хирадмандон!'),
(2477, 20, 129, 'Агар на сухане буд, ки Парвардигорат пеш аз ин гуфта ва замонро муъайян карда буд, азобашон дар ин ҷаҳон ҳатмӣ мебуд.'),
(2478, 20, 130, 'Барои он чӣ мегӯянд, бо сабр бош ва Парвардигоратро пеш аз тулӯъи хуршед ва пеш аз ғуруби он ба покӣ ва ҳамд ёд кун. Ва дар соъати шаб ва аввалу охири рӯз тасбеҳ гӯй. Шояд хушнуд гардӣ.'),
(2479, 20, 131, 'Агар занону мардоне аз онҳоро аз як зиндагии хуш баҳраманд сохтаем, ту ба онҳо манигар. Ин барои он аст, ки имтиҳонашон кунем. Ризқи Парвардигорат беҳтару пойдортар аст!'),
(2480, 20, 132, 'Касони худро ба намоз фармон деҳ ва худ дар он кор босабр бош. Аз ту рӯзӣ намехоҳем. Мо ба ту рӯзӣ медиҳем. Ва оқибати нек аз они парҳезгорон аст.'),
(2481, 20, 133, 'Ва гуфтанд: «Чаро муъҷизае аз Парвардигораш барои мо намеоварад?» Оё далелҳои равшане, ки дар сахифаҳои пешин омада, ба онҳо нарасидааст?'),
(2482, 20, 134, 'Агар пеш аз омадани Паёмбаре азобашон мекардем, мегуфтанд: «Эй Парвардигори мо, чаро расуле бар мо нафиристодӣ то пеш аз он ки ба хориву расвоӣ афтем, аз оёти ту пайравӣ кунем?»'),
(2483, 20, 135, 'Бигӯ: «Ҳама мунтазиранд, шумо низ мунтазир бимонед. Ба зудӣ хоҳед донист, онҳо, ки ба роҳи рост мераванд ва онҳо, ки ҳидоят ёфтаанд, чӣ касоне ҳастанд».');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'Рӯзи ҳисоби мардум наздик шуд ва онон ҳамчунон ба ғафлат рӯйгардонанд.'),
(2485, 21, 2, 'Аз сӯи Парвардигорашон барояшон ҳар панде тозае омад, онро шуниданд ва саргарми бозича буданд.'),
(2486, 21, 3, 'Дилҳояшон ғофил шудааст. Ва он ситамгарон сар дар гӯши якдигар ниҳоданду пинҳонӣ гуфтанд: «Оё ин мард як инсоне монанди шумо нест? Оё бо он ки ба чашмн худ мебинед, ҳамчунон аз паи ҷоду меравед?»'),
(2487, 21, 4, 'Гуфт: «Парвардигори ман аз ҳар сухане дар осмону замин огоҳ аст ва Ӯ шунавову доност».'),
(2488, 21, 5, 'Гуфтанд: «На Хобҳои парешон аст ё дурӯғест, ки мебофад ё шоирест. Пас барои мо аз он гуна, ки ба паёмбарони пешин дода шуда буд, мӯъҷизае биёварад».'),
(2489, 21, 6, 'Пеш аз онҳо мардуми деҳаеро, ки ҳалок кардем, имон наёварда буданд. Оё инҳо имон меоваранд?'),
(2490, 21, 7, 'Мо пеш аз ту ба пайрамбарӣ фақат мардонеро, ки ба онҳо ваҳй мекардем фиристодем ва агар худ намедонед, аз аҳли китоб бипурсед.'),
(2491, 21, 8, 'Онҳоро ҷасадҳое накардем, ки ба таъом мӯҳтоҷ набошанд ва умри ҷовидон ҳам надоштанд.'),
(2492, 21, 9, 'Сипас ҳар ваъдае, ки ба онҳо додем, баровардем ва онҳову ҳар касро, ки хостем, раҳонидем ва аз ҳад гузарандагонро ҳалок кардем.'),
(2493, 21, 10, 'Китобе бар шумо нозил кардаем, ки дар он шарафи бузурги шумост. Оё ба ақл дарнамеёбед?'),
(2494, 21, 11, 'Ва чӣ бисёр ситамкор мардумеро, ки ҳалок кардем ва ба ҷояшон мардуме дигар биёфаридем.'),
(2495, 21, 12, 'Чун азоби моро ҳис мекарданд, ба ногоҳ аз он ҷо мегурехтанд.'),
(2496, 21, 13, 'Мегузеред! Бо нозу ишрат ба хонаҳои худ боз гардед то пурсида шавед!'),
(2497, 21, 14, 'Гуфтанд; «Вой бар мо, мо ситамкор будаем!»'),
(2498, 21, 15, 'Ва ҳамеша суханашон ин буд, то ҳамаро чун кишти даравида ва оташи хомӯшгашта гардонидем.'),
(2499, 21, 16, 'Мо ин осмону замин ва он чиро миёни он дӯст, ба бозича наофаридаем.'),
(2500, 21, 17, 'Агар хостори бозичае мебудем, Худ онро меофаридем, агар хоста будем.'),
(2501, 21, 18, 'Балки ҳақро бар сари ботил мезанем, то онро дар ҳам кӯбад ва ботил нобудшаванда аст. Ва вой бар шумо аз он чӣ ба Худо сифат медиҳед.'),
(2502, 21, 19, 'Аз они Ӯст ҳар кӣ дар осмонҳову замин аст. Ва онон, ки дар назди Ӯ ҳастанд, аз ибодаташ ба такаббур сар наметобанд ва хаста намешаванд,'),
(2503, 21, 20, 'Шабу рӯз, бе он ки сустие дар онҳо падид ояд, тасбеҳ мегӯянд.'),
(2504, 21, 21, 'Оё дар замин худоёне интихобидаанд, ки мурдагонро аз гӯр бар мехезонанд?'),
(2505, 21, 22, 'Агар дар замину осмон худоёне ғайри Оллоҳ мебуд, ҳар ду табоҳ мешуданд. Пас Оллоҳ — Парвардигори арш аз ҳар чӣ ба васфаш мегӯянд, пок аст.'),
(2506, 21, 23, 'Ӯ дар баробари ҳеҷ як аз корҳое, ки мекунад, пурсида намешавад, вале мардум пурсида мешаванд.'),
(2507, 21, 24, 'Оё ба ҷуз Ӯ худоёнро интихобидаанд? Бигӯ: «Ҳуҷҷати худ биёваред. Дар ин китоб сухани касонест, ки бо ман ҳастанд ва сухане касоне, ки пеш аз ман будаанд». На, бештаринашон чизе аз ҳақ намедонанд ва аз он рӯй мегардонанд.'),
(2508, 21, 25, 'Мо пеш аз ту ҳар паёмбаре ки фиристодем, фақат ба ӯ ваҳй кардем, ки ғайри Ман худое нест. Пас Маро бипарастед.'),
(2509, 21, 26, 'Ва гуфтанд, ки Худои раҳмон соҳиби фарзанд аст. Пок аст Ӯ. Балки онон бандагоне гироми ҳастанд,'),
(2510, 21, 27, 'дар сухан бар Ӯ пешӣ намегиранд ва ба фармони Ӯ кор мекунанд.'),
(2511, 21, 28, 'Медонад ҳар чӣ дар баробари онҳост ва ҳар чӣ пушти сари онҳост. Ва онон ҷуз касонерю, ки Худо аз онҳо хушнуд аст, шафоъат намекунанд ва аз бими Ӯ ларзонанд.'),
(2512, 21, 29, 'Ҳар кас аз онҳо, ки бигӯяд: «Ман ғайри Ӯ худоям», ҷазои чунин касеро ҷаҳаннам медиҳад ва золимонро чунин ҷазо медиҳем.'),
(2513, 21, 30, 'Оё кофирон намедонанд, ки осмонҳову замин ба ҳам якҷоя часпида буданд, Мо онҳоро ҷудо кардем ва ҳар чизи зиндаеро аз об пайдо кардем? Чаро имон намеоваранд?'),
(2514, 21, 31, 'Ва бар замин кӯҳҳо биёфаридем то наларзонадашон. Ва дар он роҳҳои васеъ сохтем, бошад, ки роҳи худ биёбанд.'),
(2515, 21, 32, 'Ва осмонро сақфе нигоҳдошташуда кардем ва боз ҳам аз ибратҳои он рӯ мегардонанд.'),
(2516, 21, 33, 'Ӯст касе, ки шабу рӯз ва офтобу моҳро, ки ҳар як дар фалаке шиноваранд, биёфарид.'),
(2517, 21, 34, 'Мо пеш аз ту ҳеҷ инсонеро ҷовидон насохтаем. Чӣ сон агар ту низ бимирӣ, онҳо ҷовидон бимонанд?'),
(2518, 21, 35, 'Ҳар кас таъми маргро мечашад. Ва шуморо ба хайру шар меозмоем. Ва ҳама ба назди Мо бозмегардед.'),
(2519, 21, 36, 'Кофирон чун туро бубинанд, бешак масхараат хоҳанд кард, ки оё ин ҳамон мардест, ки аз худоёнатон ба бадӣ ёд мекунад? Ва инҳо худ ёди Худои раҳмонро мункиранд.'),
(2520, 21, 37, 'Одамӣ шитобкор офарида шуда. Оёти худро ба шумо нишон хоҳам дод, шитоб макунед.'),
(2521, 21, 38, 'Мегӯянд: «Агар рост мегӯед, пас он ваъда куҷост?»'),
(2522, 21, 39, 'Кофирон он гоҳ, ки натавонанд оташро аз чеҳраву пушти худ боздоранд ва касе ба ёриашон барнахезад, хоҳанд донист.'),
(2523, 21, 40, 'Аммо қиёмат ба ногаҳон биояд ва ҳайронашон созад ва аз ӯҳдаи бозгардонидани он барнаёянд ва мӯҳлаташон надиҳанд.'),
(2524, 21, 41, 'Паёмбаронеро, ки пеш аз ту буданд, низ ба масхара гирифта буданд, вале азобе, ки масхарааш мекарданд, бар сари масхаракунандагон фуруд омад.'),
(2525, 21, 42, 'Бигӯ: «Кист он ки шуморо шабу рӯз аз қаҳри Худои раҳмон ҳифз мекунад?» Бо ин ҳама аз ёд кардани Парвардигорашон рӯйгардонӣ мекунанд.'),
(2526, 21, 43, 'Ё он ки худоёне доранд, ки ба ҷои мо ононро аз ҳодисаҳо нигоҳ медоранд? На! Он худоён ёрии худро натавонанд ва на дар баробари мо паноҳида шаванд.'),
(2527, 21, 44, 'Балки мо онҳо ва падаронашонро баҳраманд кардем, то умрашон ба дароз кашид. Оё намебинанд, ки қасди ин сарзамин мекунем ва аз атрофи он кам мекунем? Оё боз ҳам онҳо пирӯзанд?'),
(2528, 21, 45, 'Бигӯ: «Ман шуморо ба ваҳй бим медиҳам», вале каронро чун бим диҳанд, намешунаванд.'),
(2529, 21, 46, 'Ва агар андаке аз азоби Парвардигорат ба онҳо бирасад, хоҳанд гуфт: «Вой бар мо, ки ситамкор будаем!»'),
(2530, 21, 47, 'Рӯзи қиёмат тарозуҳои адлро омода мекунем ва ба ҳеҷ кас ситам намешавад. Агар амале ба сангинии як хардал ҳам бошад, ба ҳисобаш меоварем, ки мо ҳисоб карданро басандаем!'),
(2531, 21, 48, 'Ба Мӯсо ва Ҳорун китобе додем, ки ҳаққу ботилро аз якдигар ҷудо мекунад ва равшаниву панд аст барои парҳезгорон.'),
(2532, 21, 49, 'Онон, ки аз Худои худ дар ниҳон метарсанд ва аз рӯзи қиёмат ҳаросноканд.'),
(2533, 21, 50, 'Ин муборак Қуръонест, ки нозил кардаем, Оё мункири он ҳастед?'),
(2534, 21, 51, 'Пеш аз ин ба Иброҳим қувваи шинохташро ато кардем ва ба ӯ огоҳ будем.'),
(2535, 21, 52, 'Он гоҳ, ки ба падараш ва қавмаш гуфт: «Ин суратҳо, ки ба парастиши онҳо дил ниҳодаед, чистанд?»'),
(2536, 21, 53, 'Гуфтанд: «Падаронамонро дидем, ки онҳоро мепарастиданд».'),
(2537, 21, 54, 'Гуфт: «Албатта шумо ва падаронатон дар гумроҳии ошкоро будаед».'),
(2538, 21, 55, 'Гуфтанд: «Барои мо сухани ҳаққе овардаӣ ё моро ба бозӣ гирифтаӣ?»'),
(2539, 21, 56, 'Гуфт: «На, Парвардигори шумо Парвардигори осмонҳову замин аст. Он ки онҳоро биофаридааст. Ва ман бар ин сухан, ки мегӯям, гувоҳӣ медиҳам.'),
(2540, 21, 57, 'Ва ба Худо савганд, ки чун шумо биравед, чораи ин бутонатонро хоҳам кард».'),
(2541, 21, 58, 'Онҳоро пора-пора кард. Лекин бузургтаринашонро, пора накард, шояд бар он рӯй оранд.'),
(2542, 21, 59, 'Гуфтанд: «Чӣ кас ба худоёни мо чунин кардааст? Албатта ӯ аз ситамкорон аст!»'),
(2543, 21, 60, 'Гуфтанд: «Шунидаем, ки ҷавоне ба номи Иброҳим аз онҳо сухан мегуфтааст».'),
(2544, 21, 61, 'Гуфтанд: «Ӯро ба назди мардум биёваред то шоҳидӣ диҳанд».'),
(2545, 21, 62, 'Гуфтанд: «Эй Иброҳим ту бо худоёни мо чунин кардаӣ?»'),
(2546, 21, 63, 'Гуфт: «Балки бузургтаринашон чунин кардааст. Агар сухан мегӯянд, аз онҳо бипурсед».'),
(2547, 21, 64, 'Бо худашон гуфтугӯ карданд ва гуфтанд: «Шумо худ ситамкор ҳастед».'),
(2548, 21, 65, 'Он гоҳ ба ҳайрат сар фурӯ доштанд ва гуфтанд: «Ту худ медонӣ, ки инҳо сухан намегӯянд».'),
(2549, 21, 66, 'Гуфт: «Оё ғайри Оллоҳ чизеро мепарастед, ки на шуморо фоида мерасонад, на зиён.'),
(2550, 21, 67, 'Безорам аз шумо ва аз он чизҳое, ки ғайри Оллоҳ мепарастед. Оё ба ақл дарнамеёбед?'),
(2551, 21, 68, 'Гуфтанд; «Агар мехоҳед коре бикунед, (Иброҳимро) бисӯзонед ва худоёни худро ёрӣ дихед».'),
(2552, 21, 69, 'Гуфтем: «Эй оташ, бар Иброҳим хунук ва саломат бош!»'),
(2553, 21, 70, 'Мехостанд барои Иброҳим макре бияндешанд, вале мо зиёнкорашон гардонидем.'),
(2554, 21, 71, 'Ӯ ва Лутро наҷот додем ва ба сарзамине, ки онро баракати ҷаҳониён қарор додаем, бурдем.'),
(2555, 21, 72, 'Ва ба ӯ Исҳоқ ва фарзандзодае чун Яъқубро бахшидем. Ва ҳамаро аз шоистагон гардонидем.'),
(2556, 21, 73, 'Ва ҳамаро пешвоёне сохтем, ки ба амри Мо ҳидоят мекарданд. Ва анҷом додани корҳои нек ва барпой доштани намоз ва додани закотро ба онҳо ваҳй кардем ва ҳама парастандаи Мо буданд.'),
(2557, 21, 74, 'Ва ба Лут ҳукму илм додем ва аз он деҳа, ки мардумаш корҳои палид мекарданд, наҷоташ бахшидем. Онҳо, ки мардуме баду фосиқ буданд!'),
(2558, 21, 75, 'Ӯро дар раҳмати худ дохил кардем, ки ӯ аз шоистагон буд.'),
(2559, 21, 76, 'Ва Нӯҳро ёд кун, ки пеш аз он Моро нидо дод ва Мо ба ӯ посух додем ва ӯву хонадонашро аз меҳнате бузург раҳонидем.'),
(2560, 21, 77, 'Ва ӯро бар мардуме, ки оёти Моро дурӯғ мебароварданд, пирӯзӣ додем. Онон бад мардуме буданд ва Мо ҳамаро ғарқ сохтем,'),
(2561, 21, 78, 'Ва Довуду Сулаймонро ёд кун, он гоҳ ки дар бораи киштзоре доварӣ карданд, ки гӯсфандони он қавм шабона дар он ҷо мечариданд. Ва Мо шоҳиди доварии онҳо будем.'),
(2562, 21, 79, 'Ва ин шеваи довариро ба Сулаймон омӯхтем ва ҳамаро ҳукму илм додем ва кӯҳҳоро мутеъи Довуд гардонидем, ки онҳо ва паррандагон бо ӯ тасбеҳ мегуфтанд ва ин ҳама Мо кардем.'),
(2563, 21, 80, 'Ва ба Ӯ омӯхтем, то бароятон зиреҳ бисозад. То шуморо ба ҳангоми ҷангиданатон ҳифз кунад. Оё сипосгузорӣ мекунед?'),
(2564, 21, 81, 'Ва тундбодро фармонбардори Сулаймон кардем, ки ба амри ӯ дар он сарзамин, ки баракаташ дода будем, ҳаракат мекард. Ва Мо бар ҳар чизе доноем.'),
(2565, 21, 82, 'Ва гурӯҳе аз девон барояш дар дарё шиноварӣ мекарданд ва ғайри он ба корҳои дигар машғул буданд ва Мо ҳофизи онҳо будем.'),
(2566, 21, 83, 'Ва Айюбро ёд кун, он гоҳ, ки Парвардигорашро нидо дод: «Ба ман беморӣ ва ранҷ расидааст ва Ту меҳрубонтарини меҳрубононӣ».'),
(2567, 21, 84, 'Дуъояшро иҷобат кардем. Ва озор аз ӯ дур кардем ва хонадонашро ва монанди онҳоро бо онҳо ба ӯ бозгардонидем. Ва ин раҳмате буд аз ҷониби Мо то худопарастон ҳамеша ба ёд дошта бошанд.'),
(2568, 21, 85, 'Ва Исмоил ва Идрис ва Зулкифлро ёд кун, ки ҳама аз собирон буданд.'),
(2569, 21, 86, 'Ононро сазовори бахшоиши худ гардонидем ва ҳама аз шоистагон буданд.'),
(2570, 21, 87, 'Ва Зуннунро он гоҳ, ки хашмнок бирафт ва пиндошт, ки ҳаргиз бар ӯ танг намегирем. Ва дар торикӣ нидо дод: «Х, еч худое чуз Ту нест. Ту пок хастй ва ман аз ситамкорон хастам».'),
(2571, 21, 88, 'Дуъояшро мустачоб кардем ва уро аз андух рахонидем ва муъминонро инчунин мерахонем.'),
(2572, 21, 89, 'Ва Закариёро ёд кун, он гох ки Парвардигорашро нидо дод: «Эй Парвардигори ман, маро танҳо вомагузор. Ва Ту беҳтарини ворисонӣ!»'),
(2573, 21, 90, 'Дуъояшро мустаҷоб кардем ва ба у Яҳёро бахшидем ва занашро барояш шоиста гардонидем. Инҳо дар корҳои нек шитоб мекарданд ва ба биму умед Моро мехонданд ва дар баробари Мо фурӯтан (сархам) буданд.'),
(2574, 21, 91, 'Ва он занро ёд кун, ки шармгоҳи худро нигоҳ дошт ва Мо аз рӯҳи Худо дар ӯ дамидем ва ӯву фарзандашро барои ҷаҳониён ибрате гардонидем.'),
(2575, 21, 92, 'Ин шариъати шумост, шариъате ягона. Ва Ман Парвардигори шумоям, Маро бипарастед!'),
(2576, 21, 93, 'Ва дар дини худ фирқа-фирқа шуданд. Ҳама ба назди Мо бозмегарданд!'),
(2577, 21, 94, 'Ҳар кас, ки коре шоиста кунад ва имон оварда бошад, кушиши ӯро ношукрӣ нест ва Мо амалҳояшонро менависем.'),
(2578, 21, 95, 'Ва деҳаеро, ки ба ҳалокат расонидаем, муҳол аст, ки бозгаште дошта бошанд.'),
(2579, 21, 96, 'То он гоҳ, ки Яъҷуҷу Маъҷуҷ кушода шаванд ва онон аз баландиҳо ба шитоб фароянд.'),
(2580, 21, 97, 'Ва он ваъдаи ростин наздик гардад ва чашмони кофирон ҳамчунон хира монад: «Вой бар мо, мо аз ин ҳол ғофил будем. Балки ситамкор будем!»'),
(2581, 21, 98, 'Шумо ва он чизҳое, ки ғайри Оллоҳ мепарастидед, ҳезумҳои дӯзах ҳастед. Шумо ба дӯзах хоҳед рафт!'),
(2582, 21, 99, 'Агар инҳо худоён мебуданд, ба ҷаҳаннам намерафтанд ва ҳол он ки ҳама дар ҷаҳаннам ҷовидонанд.'),
(2583, 21, 100, 'Онон дар ҷаҳаннам фарёд мекашанд ва дар он ҷо ҳеҷ намешунаванд.'),
(2584, 21, 101, 'Касоне, ки пеш аз ин муқаррар кардаем, ки ба онҳо некӯӣ кунем, аз дӯзах барканоранд.'),
(2585, 21, 102, 'Инҳо садои дӯзахро намешунаванд ва дар биҳишт, ки ҳар чӣ хоҳанд, муҳайёст, ҷовидонаанд.'),
(2586, 21, 103, 'Он ваҳшати бузург ғамгинашон накунад ва фариштагон ба дидорашон оянд, ки ин ҳамон рӯзест, ки ба шумо ваъда дода буданд.'),
(2587, 21, 104, 'Рӯзе, ки осмонро чун туморе навишта дар ҳам печонем. Ва чунон ки нахустин бор биёфаридем, офариниш аз сар гирем. Ин ваъдаест, ки бароварданаш бар ӯхдаи Мост ва Мо чунон хоҳем кард.'),
(2588, 21, 105, 'Ва Мо дар Забур, пас аз Таврот, навиштаем, ки ин заминро бандагони солеҳи Ман ба мерос хоҳанд бурд.'),
(2589, 21, 106, 'Ва дар ин китоб таблиғест барои мардуми худопараст!'),
(2590, 21, 107, 'Ва фиристодем туро фақат барои он, ки мехостем ба мардуми ҷаҳон раҳмате ато кунем.'),
(2591, 21, 108, 'Бигӯ: «Ба ман ваҳй шуда, ки Худой шумо Худоест якто, оё ба Ӯ гардан мениҳед?»'),
(2592, 21, 109, 'Пас агар рӯйгардон шуданд, бигӯ: «Шуморо хабар додам, то ҳама дар огоҳӣ яксон бошед. Ва ман намедонам, он чӣ шуморо ваъда кардаанд, наздик аст ё дур.'),
(2593, 21, 110, 'Ӯст, ки медонад ҳар суханеро, ки ба овози баланд гӯед ё дар дил пинҳон доред.'),
(2594, 21, 111, 'Ва намедонам шояд ин (дер кардани азоб) озмоише барои шумо ва баҳрамандӣ то ба ҳангоми марг бошад».'),
(2595, 21, 112, 'Гуфт: «Эй Парвардигори ман, ба ҳақ довари кун. Ва Парвардигори мо бахшоянда аст ва бо вуҷуди он чӣ мегӯед, ҳама аз Ӯ ёрӣ меҷӯянд».');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Эй мардум, аз Парвардигоратон битарсед, ки зилзилаи қиёмат ҳодисаи бузургест!'),
(2597, 22, 2, 'Он рӯз, ки бубинедаш, ҳар ширдиҳанде ширхораашро аз ёд бибарад ва ҳар ҳомилае бори худро, бар замин гузорад ва мардумро чун мастон бинӣ, ҳол он ки маст нестанд, балки азоби Худо сахт аст.'),
(2598, 22, 3, 'Баъзе аз мардум бе ҳеҷ донише дар бораи Худо баҳс мекунанд ва ба дар шайтони саркаше пайравӣ мекунанд.'),
(2599, 22, 4, 'Бар шайтон чунин муқаррар шуда, ки ҳар касро, ки дӯсташ бидорад, гумроҳ кунад ва ба азоби оташи сӯзонаш кашонад.'),
(2600, 22, 5, 'Эй мардум, агар аз рӯзи қиёмат дар шубҳа ҳастед, Мо шуморо аз хок ва сипас аз нутфа, он гоҳ аз лахтаи хуне ва сипас аз порагӯште гоҳ тамом офарида, гоҳ нотамом биёфаридаем, то қудрати худро бароятон ошкор кунем. Ва то замоне муъайян ҳар чиро хоҳем, дар раҳмхҳо нигаҳ медорем. Он гоҳ шуморо, ки кӯдаке ҳастед, берун меоварем, то ба ҳадди зӯрмандии худ расед. Баъзе аз шумо мемирандва баъзе ба солхӯрдагӣ бурда мешаванд, то он гоҳ ки ҳар чӣ омӯхтаанд, фаромӯш кунанд. Ва ту заминро хушкида мебинӣ. Чун борон бар он бифирис- тем, дар ҷунбиш ояд ва нашъу намо кунад ва аз ҳар гуна гиёҳи боравнақ бирӯёнад.'),
(2601, 22, 6, 'Ва инҳо далел бар он аст, ки Худо ҳақ аст, мурдагонро зинда месозад ва бар ҳар коре тавоност.'),
(2602, 22, 7, 'Ва низ қиёмат хоҳад омад, шаке дар он нест. Ва Худо ҳамаи касонеро, ки дар гӯрҳо ҳастанд, зинда мекунад.'),
(2603, 22, 8, 'Аз мардум касест, ки бе ҳеҷ донише ва ҳеҷ роҳнамоӣ ва ҳеҷ китоби равшанибахше дар бораи Худо баҳс мекунад.'),
(2604, 22, 9, 'Гарданро ба такаббур ба як сӯ мепечонад, то мардумро аз роҳи Худо гумроҳ созад. Насиби ӯ дар дунё хорист ва дар рӯзи қиёмат азоби оташро ба ӯ мечашонем.'),
(2605, 22, 10, 'Ин ҷазои корҳоест, ки пеш аз ин кардаӣ ва Худо ба бандагонаш ситам намекунад.'),
(2606, 22, 11, 'Ва аз миёни мардум касест, ки Худоро бо шубҳа мепарастад. Агар хайре ба ӯ расад, дилаш ба он ором гирад ва агар озмоише пеш ояд, рӯй бартобад. Дар дунёву охират зиён бинад ва он зиёне ошкор аст.'),
(2607, 22, 12, 'Ғайри Худо касеро мехонад, ки на зиёне ба ӯ мерасонад ва на нафъе ва ин гумроҳиест беохир.'),
(2608, 22, 13, 'Касеро мехонад, ки зиёнаш наздиктар аз нафъи ӯст. Чӣ бад дӯстдорест ва чӣ бад ҳамсӯҳбат.'),
(2609, 22, 14, 'Худо касонеро, ки имон оварда ва корҳои шоиста кардаанд, ба биҳиштҳое, ки дар он наҳрҳо ҷорист, дохил месозад ва Худо ҳар чӣ хоҳад, ҳамон мекунад.'),
(2610, 22, 15, 'Касе, ки мепиндорад, ки Худо дар дунёву охират ёриаш нахоҳад кард, худашро бо ресмоне ба сақф биёвезад то худро хафа кунад ва бингарад, ки оё ин ҳила хашмашро аз миён хоҳад бурд.'),
(2611, 22, 16, 'Ба ин тарз Қуръонро нозил кардем бо оёте равшангар. Ва Худо ҳар касро, ки бихоҳад, ҳидоят мекунад.'),
(2612, 22, 17, 'Худо миёни онон, ки имон овардаанд ва онон, ки дини яҳуд ё собион ё насоро ё маҷус ихтиёр кардаанд ва онон, ки мушрик шудаанд, дар рӯзи қиёмат ҳукм мекунад. Зеро Ӯ бар дар коре шоҳид аст!'),
(2613, 22, 18, 'Оё надидаӣ, ки ҳар кас дар осмонҳову ҳар кас, ки дар замин аст ва офтобу моҳ ва ситорагону кӯҳҳо ва дарахтону ҷунбандагон ва бисёре аз мардум Худоро саҷда мекунанд? Ва бар бисёре азоб муқаррар шуда ва ҳар киро Худо хор созад, ҳеҷ кас гиромиаш намедорад. Зеро Худо ҳар чӣ бихоҳад, ҳамон мекунад. (Сачда).'),
(2614, 22, 19, 'Ин ду гурӯҳ дар бораи Парвардигорашон ба хусумат бархостаанд. Барои онон, ки кофиранд, ҷомаҳое аз оташ буридаанд ва аз боло бар сарашон оби ҷӯшон мерезанд.'),
(2615, 22, 20, 'Бо он оби ҷӯшон ҳар чӣ дар даруни шикам доранд ва низ пӯстҳояшон гудохта мешавад.'),
(2616, 22, 21, 'Ва низ барои онҳост гурзҳое оҳанин.'),
(2617, 22, 22, 'Ҳар гоҳ, ки хоҳанд, аз он азоб, аз он андӯҳ берун оянд, бори дигар ононро ба он бозгардонанд, ки бичашед азоби оташи сӯзандаро.'),
(2618, 22, 23, 'Худо касонеро, ки имон оварда ва корҳои шоиста кардаанд, ба биҳиштҳое, ки дар он наҳрҳо ҷорист, дохил месозад. Дар он ҷо бо дастбандҳое аз тиллову марворид ороста шаванд ва либосашон аз ҳарир аст.'),
(2619, 22, 24, 'Онҳо ба он сухани пок ва ба роҳи Худованди сутуданӣ роҳнамоӣ шудаанд.'),
(2620, 22, 25, 'Онҳое, ки кофир шуданд ва мардумро аз роҳи Худо ва масҷидулҳаром, ки барои мардуми чӣ муқиму чӣ ғариб яксон аст, бозмедоранд ва низ ҳар киро дар он ҷо қасди каҷравӣ ё ситамкорӣ дошта бошад, азоби дардовар мечашонем.'),
(2621, 22, 26, 'Ва макони хонаро барои Иброҳим ошкор кардем ва гуфтем: «Ҳеҷ чизро шарики Ман масоз ва хонаи Маро барои тавофкунандагон ва ба намоз истодагон ва рокеъону соҷидон покиза бидор».'),
(2622, 22, 27, 'Ва мардумро ба ҳаҷ даъват кун то пиёда ё савор бар шутурони лоғар аз роҳҳои дур назди ту биёянд.'),
(2623, 22, 28, 'То нафъҳоеро, ки аз они онҳост, бубинанд ва номи Худоро дар рӯзҳое муъайян ба ҳангоми забҳи чорпоёне, ки Худо ризқи онҳо сохта, ёд кунанд. Пас аз онҳо бихӯреду бенавоёнӣ фақирро низ таъом диҳед.'),
(2624, 22, 29, 'Сипас чирк аз худ дур кунанд ва назрҳои худро адо кунанд ва бар он хонаи кӯҳансол тавоф кунанд.'),
(2625, 22, 30, 'Ҳамчунин ҳар кас даст аз ҳаром бидорад, дар назди Парвардигораш барояш беҳтар аст. Ва чорпоён бар шумо ҳалоланд, ғайри онҳое, ки бароятон хонда шуда. Пас аз бутҳои палиду сухани ботилу дурӯғ парҳез кунед.'),
(2626, 22, 31, 'Рӯйовардагон ба Худо бошед ва ширкнаёвардагон ба Ӯ; ва ҳар кас, ки ба Худо ширк оварад, чун он касест, ки аз осмон фурӯ афтад ва мурғ ӯро бирабояд ё бодаш ба маконе дур андозад.'),
(2627, 22, 32, 'Оре, касоне, ки шиъорҳои Худоро бузург мешуморанд, корашон нишони парҳезгории дилҳояшон бошад.'),
(2628, 22, 33, 'Аз он шутурони қурбонӣ то замоне муъайян бароятон судҳост, сипас ҷои қурбониашон дар он хокаи кӯҳансол аст.'),
(2629, 22, 34, 'Барои ҳар уммате расми қурбонӣ кардане ниҳодем, то ба он сабаб, ки Худо аз чорпоён рӯзиашон додааст, номи Ӯро бар забон ронанд. Пас Худои шумо Худои яктост, дар баробари Ӯ таслим шавед. Ва ибодаткунандагонро хушхабар деҳ'),
(2630, 22, 35, 'онон, ки чун номи Худо бурда шавад, дар дил битарсанд ва ба он ҳангом, ки ба онҳо мусибате расад, собиранд ва намозгузоронанд ва аз он чӣ рӯзиашон додаем, садақа мекунанд.'),
(2631, 22, 36, 'Шутурони қурбониро барои шумо аз шиорҳои Худо қарор додем. Шуморо дар он хайрест. Ва ҳамчунон ки бар пой истодаанд, номи Худоро бар онҳо бихонед ва чун паҳлӯяшон бар замин расид, аз онҳо бихӯред ва фақиронро қонеъу гадоёнро таъом диҳед. Инҳоро барои шумо ром кардем. Бошад, ки шукргузорӣ кунед!'),
(2632, 22, 37, 'Гӯшту хуни ин шутурон ба Худо намерасад. Он чӣ ба Ӯ мерасад, парҳезгории шумост. Ҳамчунин онҳоро роми шумо сохт то Худоро ба шукронаи он, ки ҳидоятатон кардааст, ба бузургӣ ёд кунед. Ва некӯкоронро хушхабар деҳ!'),
(2633, 22, 38, 'Худо аз касоне, ки имон овардаанд, дифоъ мекунад ва Худо хиёнаткорони носипосро дӯст надорад!'),
(2634, 22, 39, 'Ба касоне, ки ба ҷанг ба сарашон тохт овардаанд ва мавриди ситам қарор гирифтаанд, рухсат дода шуд ва Худо бар пирӯз гардониданашон тавоност.'),
(2635, 22, 40, 'Онҳое, ки ба ноҳақ аз диёрашон ронда шудаанд, фақат ин буд, ки мегуфтанд; «Парвардигори мо Худои яктост?» Ва агар Худо баъзеро ба василаи баъзе дигар дафъ накарда буд, дайрҳову калисоҳо ва куништҳову масҷидҳое, ки номи Худо ба фаровонӣ дар он бурда мешавад, вайрон мегардид. Ва Худо ҳар касро, ки ёриаш кунад, ёрӣ мекунад ва Худо тавонову пирӯзманд аст!'),
(2636, 22, 41, 'Ҳамон касон, ки агар дар замин қудраташон диҳем, намоз мегузоранд ва закот медиҳанд ва амр ба маъруфу наҳй аз мункар мекунанд. Ва саранҷоми ҳамаи корҳо бо Худост!'),
(2637, 22, 42, 'Агар инҳо туро дурӯғ баровардаанд, пеш аз онҳо қавми Нӯҳ ва Од ва Самуд низ дурӯғ баровардаанд,'),
(2638, 22, 43, 'ва низ қавми Иброҳим ва қавми Лут,'),
(2639, 22, 44, 'ва мардуми Мадян. Ва Мӯсо низ такзиб гардвд. Ман ба кофирон мӯҳлат додам, он гоҳ онҳоро фурӯ гирифтам. Ва уқубати Ман чӣ гуна буд?'),
(2640, 22, 45, 'Чӣ бисёр деҳаҳои золимро ҳалок кардем ва сақфҳояшон фурӯ рехт ва чӣ бисёр чоҳҳо, ки бекора монд ва қасрҳои баланди гаҷкоришуда бесоҳиб монд.'),
(2641, 22, 46, 'Оё дар замин сайр намекунанд, то соҳиби дилҳое гарданд, ки ба он ақл кунанд ва гӯшҳое, ки бо он бишнаванд? Зеро чашмҳо нестанд, ки кӯр мешаванд, балки дилҳое, ки дар синаҳо ҷой доранд, кӯр шаванд.'),
(2642, 22, 47, 'Аз ту ба шитоб азоб металабанд ва Худо ҳаргиз ваъдаи худро хилоф намекунад. Ва як рӯз аз рӯзҳои Парвардигори ту баробар бо ҳазор сол аст, аз он солҳое, ки мешумуред.'),
(2643, 22, 48, 'Чӣ бисёр деҳаҳое, ки золим буданд ва Ман мӯҳлаташон додам, он гоҳ онҳоро фурӯ гирифтам. Ва бозгашти ҳама назди Ман аст!'),
(2644, 22, 49, 'Бигӯ: «Эй мардум, ман барои шумо бимдиҳандае ошкорам».'),
(2645, 22, 50, 'Пас ононро, ки имон овардаанд ва корҳои шоиста кардаанд, бахшоишу ризқе бузург аст.'),
(2646, 22, 51, 'Ва онон, ки дар радди оёти Мо мекӯшанд ва мехоҳанд Моро очиз кунанд, аҳли ҷаҳаннаманд.'),
(2647, 22, 52, 'Мо пеш аз ту ҳар як расул ё набиеро фиристодабошем, чун ба хондани оёт машғул шуд, шайтон дар сухани ӯ чизе афканд. Ва Худо он чиро, ки шайтон афканда буд насх (рад) кард, сипас оёти худро устоворӣ бахшид ва Худо донову ҳаким аст!'),
(2648, 22, 53, 'То ки он чӣ шайтон дар сухани ӯ афканда, барои касоне, ки дар қалбҳояшон беморист ва низ сахтдилон озмоише бошад ва ситамкорон дар душмание бузурганд!'),
(2649, 22, 54, 'Ва то донишёфтагон бидонанд, ки Қуръон ба ростӣ аз ҷониби Парвардигори туст ва ба он имон биёваранд ва дилҳояшон ба он ором гирад. Ва Худо касонеро, ки имон овардаанд, ба роҳи рост ҳидоят мекунад.'),
(2650, 22, 55, 'Ҳамеша кофирон дар он шак мекунанд, то он гоҳ, ки қиёмат ногаҳон бар сарашон фаро расад ё ба азоби рӯзи бешодмонӣ гирифтор оянд.'),
(2651, 22, 56, 'Фармонравоӣ дар он рӯз аз они Худост. Миёнашон ҳукм мекунад. Пас касоне, ки имон овардаанд ва корҳои шоиста кардаанд, дар биҳиштҳои пурнеъматанд.'),
(2652, 22, 57, 'Ва касоне, ки кофир шудаанд ва оёти Моро дурӯғ шумурдаанд, барояшон азобест хоркунанда!'),
(2653, 22, 58, 'Ва касонеро, ки дар роҳи Худо муҳоҷират карданд, сипас кушта шуданд ё худ мурданд, Худо ба ризқе некӯ рӯзӣ медиҳад. Зеро Худо беҳтарини рӯзидиҳандагон аст.'),
(2654, 22, 59, 'Ононро ба ҷое дароварад, ки аз он хушнуд бошанд. Ва албатта Худо донову собирает!'),
(2655, 22, 60, 'Ҳар кас уқубат кунад, ҳамчунон ки ӯро уқубат кардаанд, он гоҳ бар ӯ дубора ситам кунанд, Худо ёриаш хоҳад кард. Зеро Худо авфкунанда ва омурзанда аст.'),
(2656, 22, 61, 'Ин ба он сабаб аст, ки Худо аз шаб кам мекунад ва ба рӯз меафзояд ва аз рӯз кам мекунад ва ба шаб меафзояд. Ва Худо шунавову биност.'),
(2657, 22, 62, 'Ин ба он сабаб аст, ки Худо ҳақ аст ва он чӣ ба ҷои Ӯ ба худоӣ мехонанд, ботил аст ва Ӯ баландмартабаву бузург аст.'),
(2658, 22, 63, 'Оё надидаӣ, ки Худо аз осмон борон фиристод ва замин пурсабза гардид? Албатта Худо борикбину огоҳ аст!'),
(2659, 22, 64, 'Аз они Ӯст он чӣ дар осмонҳо ва он чӣ дар замин аст. Ӯст бениёз (бе эҳтиёҷ) ва сазовори ситоиш!'),
(2660, 22, 65, 'Оё надидаӣ, ки Худо ҳар чиро дар рӯи замин аст, роми шумо кардааст ва киштиҳоро, ки дар дарё ба фармони Ӯ мераванд? Ва осмонро нигоҳ дошта, ки ҷуз ба фармони Ӯ бар замин наафтад. Зеро Худоро бар мардум раъфату (шафқату) меҳрубонист.'),
(2661, 22, 66, 'Ӯст он кӣ шуморо зиндагӣ бахшид ва сипас бимиронд ва боз зинда месозад. Ва одамӣ ношукр аст.'),
(2662, 22, 67, 'Барои ҳар уммате дине ниҳодем, то бар он дин бошад. Пас дар ин кор бо ту муҷодала накунанд ва мардумро ба Парвардигори худ даъват кун, зеро ту ба ростӣ бар роҳи рост қадам мениҳӣ.'),
(2663, 22, 68, 'Ва агар бо ту муҷодала карданд, бигӯ: «Худо бар ҳар коре, ки мекунед, огоҳтар аст.'),
(2664, 22, 69, 'Дар он чӣ мавриди ихтилофи шумост, Худо дар рӯзи қиёмат ҳукм хоҳад кард».'),
(2665, 22, 70, 'Оё надонистаӣ, ки Худо ҳар чиро, ки дар осмонҳову замин аст, медонад? Ва ин дар китобе навиштааст ва корест бар Худо осон.'),
(2666, 22, 71, 'Ва ғайри Оллоҳ чизеро мепарастанд, ки хеҷ далеле бар вуҷудаш нозил нашудааст ва ҳеҷ донише бад-он надоранд. Ва ситамкорон ёваре надоранд.'),
(2667, 22, 72, 'Чун оёти равшангари Мо бар онон хонда шавад, нишони инкорро бар чеҳраи кофирон мешиносӣ чунон ки наздик бошад бар онон, ки оётро мехонанд, ҳамлавар шаванд. Бигӯ: «Оё шуморо ба чизе бадтар аз ин огоҳ кунам? Оташ. Худо онро бар касоне, ки имон наовардаанд, ваъда додааст ва оташ бад саранҷомест!»'),
(2668, 22, 73, 'Эй мардум, мисоле ова; рда шуд. Бад-он гӯш диҳед. Касоне, ки онҳоро ба ҷои Оллоҳ ба худоӣ мехонед, агар ҳама гирд оянд, магасеро нахоҳанд офарид ва агар магасе чизе аз онҳо бирабояд, бозситонданашро натавонанд. Толибу матлуб ҳар ду нотавонанд!'),
(2669, 22, 74, 'Худоро, чунон ки лоиқи Ӯст, нашинохтанд. Ҳар оина Худо нерӯманду пирӯз аст!'),
(2670, 22, 75, 'Худо аз миёни фариштагону мардумон расулоне ихтиёр мекунад. Албатта Худо шунавову биност!'),
(2671, 22, 76, 'Он чиро, ки дар пеши рӯяшон аст ва он чиро, ки пӯшти сарашон аст, медонад. Ва ҳамаи корҳо бад-Ӯ бозмегардад.'),
(2672, 22, 77, 'Эй касоне, ки имон овардаед, руқӯъ кунед ва саҷда кунед ва Парвардигоратонро бипарастед ва корҳои нек ба ҷой оваред, бошад, ки наҷот ёбед.'),
(2673, 22, 78, 'Дар роҳи Худованд, чунон ки бояд ҷиҳод кунед. Ӯ шуморо баргузид. Ва бароятон дар дин ҳеҷ тангное падид наёвард. Дини падаратон Иброҳим аст. Ӯ пеш аз ин ва дар ин шуморо мусалмон номид. То паёмбар бар шумо гувоҳ бошад ва шумо бар дигар мардум гувоҳ бошед. Пас намоз бигузоред ва закот бидиҳед ва ба Худо ибодат кунед! Ӯст мавлои шумо. Чӣ мавлое неку ва чӣ ёваре некӯ!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Дар ҳақиқат наҷот ёфтанд мӯъминон:'),
(2675, 23, 2, 'онон, ки дар намозашон тарсу умед мекунанд'),
(2676, 23, 3, 'ва онон, ки аз беҳуда рӯй мегардонанд'),
(2677, 23, 4, 'ва онон, ки закотро медиҳанд'),
(2678, 23, 5, 'ва онон, ки шармгоҳи худро нигоҳ медоранд'),
(2679, 23, 6, 'ғайри ҳамсарон ё канизони худ, ки дар наздикӣ бо онон мавриди маломат қарор намегиранд'),
(2680, 23, 7, 'ва касоне, ки ғайр аз ин ду биҷӯянд, аз ҳадди худ таҷовуз кардаанд.'),
(2681, 23, 8, 'ва онон, ки амонатҳо ва паймонҳои худро риоя ме- кунанд'),
(2682, 23, 9, 'ва онон, ки бар намозҳои худ давомдиҳандаанд,'),
(2683, 23, 10, 'инҳо меросбарон ҳастанд,'),
(2684, 23, 11, 'ки фирдавсро, ки ҳамеша дар он ҷовидонанд, ба мерос мебаранд.'),
(2685, 23, 12, 'Албатта Мо инсонро аз гили холис офаридем,'),
(2686, 23, 13, 'сипае ӯро нутфае дар ҷойгоҳе устувор қарор до- дем,'),
(2687, 23, 14, 'он гоҳ аз он нутфа лахтахуне офаридем ва аз он лахтахун порагӯште ва аз он порагӯшт устухонҳо офаридем ва устухонҳоро бо гӯшт пӯшонидем; бори дигар ӯро офаринише дигар додем. Сазовори таъ- зим аст Худованд, он беҳтарини офаринандагон!'),
(2688, 23, 15, 'Ва баъд аз ин ҳамаи шумо хоҳед мурд.'),
(2689, 23, 16, 'Боз дар рӯзи қиёмат зинда мегардед.'),
(2690, 23, 17, 'Ва бар болои саратон ҳафт осмон биёфаридем, ҳол он ки аз ин офариниш ғофил набудаем.'),
(2691, 23, 18, 'Ва аз осмон ба миқдори эҳтиёҷ об фиристодем ва онро дар замин ҷой додем ва Мо бар нобуд карда- наш тавоно ҳастем.'),
(2692, 23, 19, 'Бо он об бароятон бӯстонҳое аз хурмову ангур пайдо кардем. Шуморо дар он борҳо меваҳои бисё- рест, ки аз онҳо мехӯред.'),
(2693, 23, 20, 'Ва дарахтест, ки дар Тӯри Сино мерӯяд. Равған медиҳад ва он равған барои хӯрандагон нонхӯри- шест.'),
(2694, 23, 21, 'Шуморо дар чорпоён ибратест. Аз шире, ки дар шикамашон ҳаст, ба шумо мепушонем ва аз онҳо фоида- ҳои бисёр мебаред ва аз онҳо мехӯред.'),
(2695, 23, 22, 'Ва бар онҳо ва бар киштиҳо савор мешавед.'),
(2696, 23, 23, 'Қасам, ки Нӯҳро бар қавмаш фиристодем. Гуфт: «Эй қавми ман, Худои якторо бипарастед. Шуморо Худованде ғайри Ӯ нест. Оё наметарсед?»'),
(2697, 23, 24, 'Бузургони қавмаш, ки кофир буданд, гуфтанд: «Ин мард инсонест монанди шумо, мехоҳад аз шумо болотар шавад. Агар Худо мехост, фарпштаго- неро мефиристод. Мо ҳаргиз чунин чизе дар рӯзго- ри ниёгонамон нашунидаем.'),
(2698, 23, 25, 'Ӯ марди девонае беш нест. Якчанд муддат бар ӯ сабр кунед». (то маргаш фаро расад).'),
(2699, 23, 26, 'Гуфт: «Эй Парвардигори ман, акнун, ки маро дурӯғ мебароранд, ёриам кун».'),
(2700, 23, 27, 'Ба ӯ ваҳй кардем, ки киштиро дар ҳузури Мо ва ба илҳоми Мо бисоз. Ва чун фармони Мо дарра- сид ва об аз танӯр берун зад, аз ҳар ҷинсе дуто ва низ касони худро ба он бибар. Ғайри он кас, ки пеш аз ин дар бораи ӯ сухан рафтааст. Ва дар бораи си- тамкорон бо Ман сухан магӯй, ки онҳо ҳама ғарқ- шудагонанд.'),
(2701, 23, 28, 'Чун худ ва ҳамроҳонат ба киштӣ нишастед, би- гӯ: «Шукр Худойро, ки моро аз мардуми золим наҷот бахшид».'),
(2702, 23, 29, 'Ва бигӯ: «Эй Парвардигори ман, маро фуруд овар дар ҷойгоҳе муборак, ки ту беҳтарини фароран- дагонӣ».'),
(2703, 23, 30, 'Дар ин достон ибратҳову пандҳост ва мо танҳо озмояндагоне будем.'),
(2704, 23, 31, 'Ва баъд аз онҳо мардуме дигар биёфаридем.'),
(2705, 23, 32, 'Ва аз худашон ба миёнашон паёмбаре фиристо- дем, ки Худои якторо бипарастед, шуморо худое ҷуз Ӯ нест. Оё наметарсед?'),
(2706, 23, 33, 'Гурӯҳе аз бузургони қавмаш, ки кофир буданд ва дидори охиратро дурӯғ мешумурданд ва дар ин дунёяшон айшу неъмат дода будем, гуфтанд: «Ин мард инсонест монанди шумо, аз он чӣ мехӯред, мехӯрад ва аз он чӣ меошомед. меошомад.'),
(2707, 23, 34, 'Ва агар ба инсоне монанди худ итоъат кунед, зиён кардаед.'),
(2708, 23, 35, 'Оё ба шумо ваъда медиҳад, ки чун мурдед ва хоку устухон шудед, шуморо аз гӯр берун меова- ранд?'),
(2709, 23, 36, 'Ин ваъдае, ки ба шумо дода шуда, дур аст, дур!'),
(2710, 23, 37, 'Ҷуз ҳамин зиндагонии дунявии мо ҳеҷ нест, ба дунё меоему мемирем ва дигар бор зинда намеша- вем.'),
(2711, 23, 38, 'Ин мардест, ки ба худои якто дурӯғ мебофад ва мо ба ӯ имон намеоварем».'),
(2712, 23, 39, 'Гуфт: «Эй Парвардигори ман, акнун ки маро ду- рӯғ мебароранд, ёриам кун!»'),
(2713, 23, 40, 'Гуфт: «Ба ҳамин зуди аз карда пушаймон меша- ванд».'),
(2714, 23, 41, 'Пас ба ҳақ садое сахт ононро фурӯ гирифт. Мо- нанди гиёҳ хушкашон кардем. Эй, насиби мардуми ситамкора дурӣ аз раҳмати Худо бод!'),
(2715, 23, 42, 'Ва баъд аз онҳо мардуме дигар биёфаридем.'),
(2716, 23, 43, 'Ҳеҷ миллате аз аҷали худ на пеш меафтад ва на дертар мекунад,'),
(2717, 23, 44, 'Сипас пай дар пай паёмбарони худро фиристо- дем. Ҳар бор, ки паёмбаре бар миллате омад, дурӯғ бароварданд ва Мо низ онҳоро аз паси якдигар ба ҳалокат расонидем ва ононро достонҳо кардем. Эй, насиби мардуме, ки имон намеоваравдд, дурӣ аз раҳмати Худо бод!'),
(2718, 23, 45, 'Он гоҳ Мӯсо ва бародараш Ҳорунро бо оёти Худ ва далелҳои равшан фиристодем;'),
(2719, 23, 46, 'назди Фиръавн ва бузургони қавмаш. Пас гар- данкашӣ кардаид, кн мардуме бартариҷӯй буданд'),
(2720, 23, 47, 'ва гуфтанд: «Оё ба ду инсон, ки монанди мо ҳастанд ва қавмашон бандагони мо буданд, имон биёварем?»'),
(2721, 23, 48, 'Онҳоро дурӯғ бароварданд ва худро ба ҳалокат афканданд.'),
(2722, 23, 49, 'Албатта Мо ба Мӯсо китоб додем, бошад, ки ҳи- доят ёбанд.'),
(2723, 23, 50, 'Ва писари Марям ва модарашро мӯъҷизае сохтем ва он дуро бар баланд ҷое, ки қароргоҳе ва оби равон дошт ҷой додем.'),
(2724, 23, 51, 'Эй паёмбарон, аз чизҳои покизаву хуш бихӯред ва корҳои шоиста кунед, ки Ман ба корҳое, ки ме- кунед, огоҳам!'),
(2725, 23, 52, 'Албатта ин дини шумо динест ягона ва Ман Парвардигори шумоям, аз Ман битарсед.'),
(2726, 23, 53, 'Пас дини худро фирқа-фирқа карданд ва ҳар фирқае ба равише, ки интихоб карда буд, дилхуш буд.'),
(2727, 23, 54, 'Ононро то чанде дар гумроҳияшон бигузор.'),
(2728, 23, 55, 'Оё мепиндоранд, ки он молу фарзанд, ки ба онҳо додем,'),
(2729, 23, 56, 'барои он аст, ки мекӯшем хайре ба онҳо бирасо- нем? На, онон дарнамеёбанд!'),
(2730, 23, 57, 'Онҳое, ки аз хавфи Парвардигорашон ларзонанд'),
(2731, 23, 58, 'ва онҳое, ки ба оёти Парвардигорашон имон ме- оваранд'),
(2732, 23, 59, 'ва онҳое, ки ба Парвардигорашон ширк намеова- ранд'),
(2733, 23, 60, 'ва онҳое, ки ҳамаи он чиро бояд адо кунанд, адо мекунанд ва боз ҳам дилҳояшон тарсон аст, ки бояд назди Парвардигорашон бозгарданд,'),
(2734, 23, 61, 'инҳо ҳастанд, ки ба корҳои нек мешитобанд ва дар он бар якдигар пешӣ меҷӯянд.'),
(2735, 23, 62, 'Бар ҳеҷ кас ҷуз ба миқдори тавонаш таклиф на- мекунем. Ва назди Мо китобест, ки ба ҳақ сухан мегӯяд. Ва бар онҳо ситаме намеравад.'),
(2736, 23, 63, 'На! Дилҳояшон аз ин сухан дар пардаи ғафлат аст. Ва онҳоро корҳоест ҷуз ин корҳо, ки мекунанд.'),
(2737, 23, 64, 'То он гоҳ ки сарватмандонашонро ба азоб ги- рифтор кунем ва нола сар диҳанд.'),
(2738, 23, 65, 'Имрӯз нола сар мадиҳед, ки шуморо аз азоби Мо кас наҷот надиҳад!.'),
(2739, 23, 66, 'Оёти Ман! бароятюн хонда, мешуд ва шумо наме- пазируфтед ва пас-пас мерафтед.'),
(2740, 23, 67, 'Бар он такаббур мекардед ва шабҳангом дар аф- сонасароӣ носазо мегуфтед.'),
(2741, 23, 68, 'Оё дар ин сухан намеандешанд ё барои онҳо чи- зе нозил шуда, ки барои ниёгонашон нозил нашуда буд?'),
(2742, 23, 69, 'Ё он ки паёмбарашонро нашинохтаанд, ки инко- раш мекунанд?'),
(2743, 23, 70, 'Ё мегӯянд, ки девона аст? На, паёмбарашон ба дини ҳақ бар онҳо фиристода шуд, вале бештара- шон аз ҳақ бадбинӣ доранд.'),
(2744, 23, 71, 'Агар ҳақ аз паи ҳавасҳояшон мерафт, осмонҳо- ву замин ва ҳар кӣ дар онҳост, ҳалок мешуд. Вале Мо пан; даш, он фиристодем ва онҳо аз пандашон рӯйгардон шуданд.'),
(2745, 23, 72, 'Ё ту аз онҳо музде металабӣ? Музди Парварди- горат беҳтар аст, ки Ӯ беҳтарини рӯзидиҳандагон аст!'),
(2746, 23, 73, 'Албатта ту ба роҳи росташон даъват мекунӣ.'),
(2747, 23, 74, 'Ва касоне, ки ба охират имон надоранд, аз роҳи рост каҷраванд!'),
(2748, 23, 75, 'Агар бар онҳо раҳмат меоварем ва озореро, ки ба он гирифторанд, аз онҳо дур месохтем, боз ҳам хамчунон бо сарсахтӣ дар таҷовузи худ саргашта ме- монданд.'),
(2749, 23, 76, 'Ба азоб гирифторашон кардем на сар хам кар- данд ва на илтиҷо.'),
(2750, 23, 77, 'То он гоҳ ки даре аз азоби сахт ба рӯяшон ку- шодем, чунон ки аз ҳама ҷо ноумед гаштанд.'),
(2751, 23, 78, 'Ва ӯст он Худое, ки бароятон гӯшу чашму дил биёфарид. Чӣ андак шукр мегузоред!'),
(2752, 23, 79, 'Ӯст он Худое, ки шуморо дар рӯи замин халқ кард ва ҳама назди Ӯ ҷамъ оварда мешавед.'),
(2753, 23, 80, 'Ва Ӯст он Худое, ки зинда мекунад ва мемиро- над ва омадушуди шабу рӯз аз Ӯст, Чаро ақл намекунед?'),
(2754, 23, 81, 'На, онҳо низ ҳамон суханон гуфтанд, ки пеши- ниён мегуфтанд.'),
(2755, 23, 82, 'Гуфтанд: «Оё агар мо бимирем ва хоку устухон шавем, боз ҳам зинда мешавем?»'),
(2756, 23, 83, 'Ба мо ва падаронамон низ пеш аз ин чунин ваъ- даҳое дода шуда буд. Инҳо чизе ҷуз афсонаҳои пе- шиниён нест!»'),
(2757, 23, 84, 'Бигӯ: «-Агар медонед, ин замин ва ҳар кӣ дар он аст, аз они кист?»'),
(2758, 23, 85, 'Хоҳанд гуфт: «Аз они Худо». Бигӯ: «Оё панд на- мегиред?»'),
(2759, 23, 86, 'Бигӯ: «Кист Парвардигори ҳафт осмон ва Пар- вардигори арши бузург?».'),
(2760, 23, 87, 'Хоҳанд гуфт: «Худо». Бигӯ: «Оё наметарсед?»'),
(2761, 23, 88, 'Бигӯ: «Агар медонед, подшоҳии ҳама чизҳо ба дасти кист? Кист он, ки ба ҳама кас паноҳ диҳад ва касеро аз ӯ паноҳ нест?»'),
(2762, 23, 89, 'Хоҳанд гуфт: «Худо». Бигӯ: «Пас чаро фиреб ме- хӯред?»'),
(2763, 23, 90, 'На! Мо бар онҳо сухани рост фиристодем ва онҳо дурӯғ мегӯянд!'),
(2764, 23, 91, 'Худо ҳеҷ фарзанде надорад ва ҳеҷ худое бо Ӯ нест. Агар чунин мебуд ҳар худое бо офаридагони худ ба як сӯ мекашид ва бар якдигар болотаршавиро меҷустанд. Худо аз он гуна, ки Ӯро васф мекунанд, пок аст.'),
(2765, 23, 92, 'Донои пинҳону ошкоро, аз ҳар чӣ шарики Ӯ ме- созанд, болотар аст.'),
(2766, 23, 93, 'Бигӯ: «Эй Парвардигори, ман, кош чизеро, ки ба онон ваъда дода шуда, ба ман нишон медодӣ.'),
(2767, 23, 94, 'Пас, эй Парвардигори ман, маро дар зумраи си- тамкорон қарор мадеҳ».'),
(2768, 23, 95, 'Ва Мо агар бихоҳем, ки он чиро ба онон ваъда додаем, туро нишон диҳем, метавонем.'),
(2769, 23, 96, 'Сухани бади онҳоро бар ҳар чӣ некӯтар меояд, ҷавоб деҳ. Мо ба сухани онҳо огоҳтар ҳастем.'),
(2770, 23, 97, 'Бигӯ: «Эй Парвардигори ман, аз васвасаҳои шай- тон ба ту паноҳ меоварам.'),
(2771, 23, 98, 'Ва ба Ту паноҳ меоварам, эй Парвардигори ман, агар назди ман ҳозир оянд!»'),
(2772, 23, 99, 'Чун якеашонро марг фаро расад, гӯяд: «Эй Пар- вардигори ман, маро бозгардон. (ба ҳаёт).'),
(2773, 23, 100, 'Шояд корҳои шоистаеро, ки тарк карда будам, ба ҷой оварам». Ҳаргиз! Ин суханест, ки ӯ мегӯяд ва пушти сарашон то рӯзи қиёмат монеъест, ки бозгашт натавонанд.'),
(2774, 23, 101, 'Чун дар сур дамида шавад, ҳеҷ хешованде миё- нашон намонад ва ҳеҷ аз холи якдигар напурсанд.'),
(2775, 23, 102, 'Онон, ки тарозуяшон сангин бошад, худ наҷотёф- тагонанд.'),
(2776, 23, 103, 'Ва онон, ки тарозуяшон сабук бошад, ба худ зиён расокидаанд ва дар ҷаҳаннам ҷовид монда- гонанд.'),
(2777, 23, 104, 'Оташ чеҳраҳояшонро месӯзонад ва дар дӯзах туршрӯянд.'),
(2778, 23, 105, 'Оё оятҳои Ман бароятон хонда намешуд ва онҳо- ро дурӯғ меҳисобидед?'),
(2779, 23, 106, 'Гӯянд: «Эй Парвардигори мо, шӯрбахтиямон бар мо ғалаба кард ва мо мардуме гумроҳ будем.'),
(2780, 23, 107, 'Эй Парвардигори мо, моро аз ин оташ берун овар. Агар дигар бор чунон кардем, аз золимон бошем».'),
(2781, 23, 108, 'Гӯяд: «Дар оташ гум шавед ва бо Ман сухан нагӯед».'),
(2782, 23, 109, 'Оре, гурӯҳе аз бандагони Ман мегуфтанд: «Эй Парвардигори мо, имон овардем, моро бибахшо ва бар мо раҳмат овар, ки Ту беҳтарини раҳмоваран- дагонӣ».'),
(2783, 23, 110, 'Ва шумо масхараашон мекардед то ёди Маро аз хотиратон рабуданд. Ва шумо ҳамчунон ба онҳо механдидед.'),
(2784, 23, 111, 'Имрӯз онҳоро ба хотири сабре, ки мекарданд, музд медиҳам. Онҳо ба муроди худ расидаанд,'),
(2785, 23, 112, 'Гӯяд: «Ба шумори солҳо чӣ муддат дар замин зистаед?»'),
(2786, 23, 113, 'Гӯянд: «Як рӯз ё қиёмате аз як рӯз. Аз онҳо, ки мешумурданд, бипурс».'),
(2787, 23, 114, 'Гӯяд: «Агар огоҳӣ доштед, медонистед, ки ҷуз андаке назистаед.'),
(2788, 23, 115, 'Оё пин д оре д, ки шуморо беҳуда офаридаем ва шумо ба назди Мо бозгардонида намешавед?»'),
(2789, 23, 116, 'Пас пок аст Худои якто — он фармонравои ростин. Ҳеҷ худое ҷуз Ӯ нест! Парвардигори арши бузургвор аст.'),
(2790, 23, 117, 'Он кас, ки ба ҷои Худои якто худои дигареро ме- хонад, ва ба ҳаққонияташ ҳеҷ бурҳоне надорад, ин аст, ки ҳисобаш назди Парвардигораш хо- ҳад буд. Ва кофирон наҷот намеёбанд.'),
(2791, 23, 118, 'Ва бигӯ: «Эй Парвардигори ман, бибахшову раҳ- мат кун ва Ту беҳтарини раҳматкунандагон ҳас- тӣ!»');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Сураест, ки онро нозил кардаем ва воҷибаш сохтаем ва дар он оёте равшан фиристодаем, бошад, ки панд гиред.'),
(2793, 24, 2, 'Зану марди зинокорро ҳар якро сад зарба бизанед. Ва агар ба Худову рӯзи қиёмат имон доред, мабод, ки дар ҳукми Худо нисбат ба он ду раҳмдил гардед. Ва бояд, ки ба ҳангоми шиканҷа карданашон гурӯҳе аз мӯъминон ҳозир бошанд.'),
(2794, 24, 3, 'Марди зинокор ғайри зани зинокор ё мушрикро намегирад ва зани зинокорро ғайри марди зинокор ё мушрик намегирад. Ва ин бар мӯъминон ҳаром шудаст.'),
(2795, 24, 4, 'Касонеро, ки занони покро ба зино мутаҳҳам мекунанд ва чаҳор шоҳид намеоваранд, ҳаштод зарба бизанед ва аз он пас ҳаргиз шоҳидиашонро напазиред, ки мардуме фосиқанд.'),
(2796, 24, 5, 'Ғайри касоне, ки баъд, аз он тавба кунанд ва ба ислоҳ оянд. Зеро Худо бахшояндаву меҳрубон аст!'),
(2797, 24, 6, 'Ва касоне, ки занони худро ба зино мутаҳҳам мекунанд ва шоҳиде ҷуз худ надоранд, ҳар як аз онҳоро чаҳор бор шоҳидӣ аст ба номи Худо, ки аз ростгӯён аст.'),
(2798, 24, 7, 'Ва бори панҷум бигӯяд, ки лаънати Худо бар ӯ бод, агар аз дурӯғгуён бошад.'),
(2799, 24, 8, 'Ва агар он зан чаҳор бор ба Худо савганд хӯрад, ки он мард дурӯғ мегӯяд, ҷазо аз ӯ бардошта мешавад.'),
(2800, 24, 9, 'Ва бори панҷум бигӯяд, ки хашми Худо бар ӯ бод, агар мард аз ростгӯён бошад.'),
(2801, 24, 10, 'Ҳалок мешудед агар фазлу раҳмате, ки Худо бар шумо додааст, намебуд? Ва агар Худо тавбапазиру ҳаким намебуд?'),
(2802, 24, 11, 'Касоне, ки он дурӯғи бузургро сохтаанд, гурӯҳе аз шумоянд. Мапиндоред, ки шуморо дар он шарре бувад. На, хайри шумо дар он бувад. Ҳар марде аз онҳо ба он андоза гуноҳ, ки кардааст, ба ҷазо расад ва аз миёни онҳо, он ки бештарини ин бӯҳтонро ба ӯҳда дорад ба азобе бузург гирифтор меояд.'),
(2803, 24, 12, 'Чаро ҳангоме ки он бӯҳтонро шунидед, мардону занони мӯъмин ба худ гумони нек набурданд ва нагуфтанд, ки ин тӯҳмате ошкор аст?'),
(2804, 24, 13, 'Чаро чаҳор шоҳид бар иддаъои худ наёварданд? Пас агар шоҳидоне наёвардаанд, дар назди Худо дар зумраи дурӯғгӯёнанд.'),
(2805, 24, 14, 'Агар фазлу раҳмати Худо дар дунёву охират намебуд, ба сазои он суханон, ки мегуфтед, шуморо азобе бузург дармерасид.'),
(2806, 24, 15, 'Он гоҳ, ки он суханро аз даҳони якдигар мегирифтед ва чизе бар забон мерондед, ки дар бораи он ҳеҷ, намедонистед ва мепиндоштед, ки коре хурд аст ва ҳол он, ки дар назди Худо коре бузург буд.'),
(2807, 24, 16, 'Чаро он гоҳ ки сухан шунидед, нагуфтед: «Моро нашояд, ки онро бозгӯем, Парвардигоро, Ту покӣ, ин тӯҳмате бузург аст?»'),
(2808, 24, 17, 'Худо шуморо панд медиҳад, ки агар аз мӯьминон ҳастед, бори дигар гирди чунон коре магардед.'),
(2809, 24, 18, 'Худо оётро барон шумо баён мекунад ва Худо донову ҳаким аст!'),
(2810, 24, 19, 'Барои касоне, ки дӯст доранд, дар бораи мӯъминон тӯҳмати зино паҳн шавад, дар дунёву охират азобе дардовар муҳайёст. Худо медонад, ва шумо намедонед!'),
(2811, 24, 20, 'Ҳалок мешудед, агар фазлу раҳмате, ки Худо ба шумо додааст, намебуд. Ва агар Худо меҳрубону бахшоянда намебуд?'),
(2812, 24, 21, 'Эй касоне, ки имон овардаед, пой ба ҷои пои шайтон магузоред. Ва ҳар кӣ пой ба ҷои пои шайтон гузорад, бидонад, ки ӯ ба фаҳшову мункар (корҳои бад) фармон медиҳад. Ва агар фазлу раҳмате ки Худо бар шумо арзонӣ доштааст, намебуд, ҳеҷ як аз шумо ҳаргиз рӯи покӣ намедид. Вале Худо ҳар касро, ки бихоҳад, покиза месозад. Ва Худо шунавову доност!'),
(2813, 24, 22, 'Тавонгарон ва онон, ки кушоише дар кори онҳост, набояд савганд бихӯранд, ки ба хешовандону мискинон ва муҳоҷирон дар роҳи Худо чизе надиҳанд. Бояд бибахшанду бибахшоянд. Оё намехоҳед, ки Худо шуморо бибахшояд? Ва Худост бахшояндаи меҳрубон.'),
(2814, 24, 23, 'Касоне, ки бар занони покдоман ва бехабар аз зино ва мӯъмин тӯҳмати зино мезананд, дар дунёву охират лаънат шудаанд ва барояшон азобест бузург'),
(2815, 24, 24, 'рӯзе, ки забонашон ва дастҳояшону пойҳояшон бар зиддашон бар корҳое, ки мекардаанд, шоҳидӣ диҳанд.'),
(2816, 24, 25, 'Он рӯз, ки Худо ҷазояшонро ба тамомӣ бидиҳад ва бидонанд, ки Худо ҳақиқати ошкор аст.'),
(2817, 24, 26, 'Занони нопок барои мардони нопок ва мардони нопок барои занони нопок ва занони пок барои мардони пок ва мардони пок барои занони пок. Онҳо аз он чӣ дар бораашон мегӯянд, поканд. Бахшоишу ризқи некӯ барои онҳост!'),
(2818, 24, 27, 'Эй касоне, ки имон овардаед, ба хонае ғайр аз хонаи худ бе он, ки иҷозат талабида ва бар сокинонаш салом карда бошед, надароед. Ин барои шумо беҳтар аст, бошад, ки панд гиред!'),
(2819, 24, 28, 'Ва агар дар хона касеро наёфтед, дохил нашавед то шуморо рухсат диҳанд. Ва агар гӯянд: «Бозгардед», бозгардед, ин бароятон покизатар аст. Ва Худо ба ҳар коре, ки мекунед, огоҳ аст.'),
(2820, 24, 29, 'Бар шумо гуноҳе нест, агар ба хонаҳои ғайримаскун, ки матоъе дар он доред, дохил шавед. Ҳар чиро ошкор созед, ё пинҳон доред. Худо ба он огоҳ аст!'),
(2821, 24, 30, 'Ба мардони мӯъмин бигӯ, ки чашмони худ бипушанд ва шармгоҳи худ иигаҳ доранд. Ин барояшон покизатар аст. Зеро Худо ба корҳое, ки мекунанд, огоҳ аст!'),
(2822, 24, 31, 'Ва ба занони мӯъмин бигӯ, ки чашмони худ бипушанд ва шармгоҳи худ нигоҳ доранд ва зинатҳои худро ҷуз он миқдор, ки пайдост, ошкор накунанд ва миқнаъаҳои (рӯймолҳои) худро то гиребон бипӯшанд ва зинатҳои худро ошкор накунанд, ғайри ба шавҳари худ ё падари худ ё падари шавҳари худ ё писари худ ё писари шавҳари худ ё бародари худ ё писари бародари худ ё писари хоҳари худ ё занони ҳамдини худ, ё бандагони худ ё мардони хидматгузори худ, ки рағбат ба зан надоранд, ё кӯдаконе, ки аз шармгоҳи занон бехабаранд. Ва низ чунон пой бар замин назананд то он зинат, ки пииҳон кардаанд, дониста шавад. Эй мӯъминон, ҳамагон ба даргоҳи Худо тавба кунед, шояд наҷот ёбед.'),
(2823, 24, 32, 'Безанонатонро ва ғулому канизони худро, ки шоиста бошанд, ҳамсар диҳед. Агар бенаво бошанд, Худо ба карами худ тавонгарашон хоҳад сохт, ки Худо кушоишдиҳанда ва доност!'),
(2824, 24, 33, 'Онон, ки тавоноии заношӯӣ надоранд, бояд покДоманӣ пеша кунанд, то Худо аз карами худ тавонгарашон гардонад. Ва аз бандагонатон онон, ки хоҳони бозхаридани худанд, агар дар онҳо хайре ёфтед, бозхариданашонро бипазиред. Ва аз он мол, ки Худо ба шумо арзонӣ доштааст, ба онон бидиҳед. Ва канизони худро агар хоҳанд, ки парҳезгор бошанд, ба хотири сарвати дунявӣ ба зино маҷбур накунед. Ҳар кас, ки ононро ба зино водорад, Худо барои он канизон, ки ба маҷбурӣ ба он кор водор гаштаанд, бахшояндаву меҳрубон аст!'),
(2825, 24, 34, 'Ба таҳқиқ барои шумо оёте равшан ва достонҳое аз касоне, ки пеш аз шумо будаанд, ва низ пандҳое барои парҳезгорон нозил кардем.'),
(2826, 24, 35, 'Худо нури осмонҳову замин аст. Мисоли нури Ӯ чун чароғдонест, ки дар он чароғе бошад, он чароғ даруни шишае ва он шиша чун ситорае дурахшанда. Аз равғани дарахти пурбаракати зайтун, ки на шарқӣ асту на ғарбӣ афрӯхта бошад. Равғанаш равшанӣ бахшад, ҳарчанд оташ ба он нарасида бошад. Нур бар болои нур. Худо ҳар касро, ки бихоҳад, ба он нур роҳ менамояд ва барои мардум мисолҳо меоварад. Зеро ба ҳар чизе огоҳ аст!'),
(2827, 24, 36, 'Он нур дар хонаҳоест, ки Худо рухсат дод баланд бардоранд ва номаш дар он ҷо ёд шавад ва Ӯро ҳар бомдоду шабонгоҳ тасбеҳ гӯянд.'),
(2828, 24, 37, 'Мардоне, ки ҳеҷ тиҷорат ва хариду фурӯхте аз ёди Худо ва намоз гузоридану закот додай бозашон надорад, аз рӯзе, ки дилҳову дидагон дигаргун мешаванд, метарсанд.'),
(2829, 24, 38, 'То Худо ба некӯтар аз он чӣ кардаанд, ҷазояшон диҳад ва аз фазли худ бар он биафзояд ва Худо ҳар киро хоҳад, беҳисоб рӯзӣ диҳад.'),
(2830, 24, 39, 'Амалҳои кофирон чун саробест (мавҷи ғармӣ) дар биёбоне. Ташна обаш пиндорад ва чун ба он наздик шавад, ҳеҷ наёбад ва Худоро назди худ ёбад, пас ҷазои ӯро ба тамом бидиҳад. Ва Худо зуд ба ҳисобҳо мерасад!'),
(2831, 24, 40, 'Ё монанди торикиҳоест дар дарёе чуқур, ки мавҷаш пӯшонад ва бар болои он мавҷе дигар ва бар болояш абрест тира, торикиҳое бар болои якдигар, он тавр, ки агар дасти худ бе? рун орад онро натавонад дид. Ва он, ки Худо роҳашро ба ҳеҷ нуре равшан накарда бошад, ҳеҷ нуре бар роҳи худ наёбад!'),
(2832, 24, 41, 'Оё надидаӣ, ки ҳар чӣ дар осмонҳову замин аст ва низ мурғоне, ки дар парвозанд, тасбеҳгӯи Худо ҳастанд? Ҳама намозу тасбеҳи Ӯро медонанд. Ва Худо ба ҳар коре, ки мокунанд, оғоҳ аст!'),
(2833, 24, 42, 'Аз они Худост фармонравоии осмонҳову замин ва бозгашти ҳамагон назди Ӯст.'),
(2834, 24, 43, 'Оё надидаӣ, ки Худо абрҳоеро ба оҳистагӣ меронад, он гоҳ, онҳоро ба ҳам мепайвандад ва абре бузург падид меоварад ва боронро бинӣ, ки дар он ҷост, тагарг (жола) мефиристад ва ҳар киро хоҳад, бо он мезанад ва аз ҳар кӣ мехоҳад, бозаш медорад. Рӯшноии барқаш наздик бошад, ки дидагонро кӯр созад.'),
(2835, 24, 44, 'Худо шабу рӯзро мегардонад. Хирадмандонро дар ин ибратест.'),
(2836, 24, 45, 'Ва Худо ҳар ҷунбандаеро аз об биёфарид Баъзе аз онҳо бар шикам мераванд ва баъзе бар ду по мераванд ва баъзе бар чаҳор по мераванд. Худо ҳар чӣ бихоҳад, меофаринад. Зеро Худо бар ҳар коре тавоност!'),
(2837, 24, 46, 'Ба таздик оёте равшангар нозил кардем ва Худо ҳар касро, ки бихоҳад, ба роҳи рост ҳидоят мекунад.'),
(2838, 24, 47, 'Ва мегӯянд: «Ба Худову паёмбараш имон овардаем ва итоъат мекунем». Пае аз он гурӯҳе аз онҳо бозмегарданд. Ва инҳо имон наёвардаанд,'),
(2839, 24, 48, 'Чун онҳоро ба Худову паёмбараш даъват кунанд, то миёнашон ҳукм кунад, бинӣ, ки гурӯҳе рӯйгардонӣ мекунанд.'),
(2840, 24, 49, 'Ва агар ҳақ бо онҳо бошад, меоянд ва ба ҳукм гардан мениҳанд.'),
(2841, 24, 50, 'Оё дар дилҳояшон маразест ё дар шак ҳастанд ё бими он доранд, ки Худову паёмбараш бар онҳо зулм кунанд? На, онон худ золимонанд!'),
(2842, 24, 51, 'Чун мӯъминонро ба Худову паёмбараш даъват кунанд, то миёнашон ҳукм кунад, суханашон ғайри ин нест, ки мегӯянд: «Шунидем ва итоъат кардем», инҳо наҷотёфтагонанд.'),
(2843, 24, 52, 'Ва касоне, ки ба Худову паёмбараш фармонбардоранд ва аз Худо метарсанд ва парҳезгорӣ мекунанд, наҷотёфтагонанд.'),
(2844, 24, 53, 'Ба Худо қасам хӯрданд, қасамҳои сахт, ки агар ба онҳо фармон диҳӣ, аз диёри худ берун раванд. Бигӯ: «Қасам нахӯред, тоъате сазовор ба ҷо оред. Худо ба корҳое, ки мекунед, огоҳ аст!»'),
(2845, 24, 54, 'Бигӯ: «Ба Худо итоъат кунед ва ба паёмбар итоъат кунед. Пас агар рӯйгардон шавед, бар паёмбар аст он чӣ ба ӯ таклиф кардаанд ва бар шумост он чӣ бар шумо таклиф кардаанд. Ва агар ба ӯ итоъат кунед, ҳидоят ёбед. Ва бар ӯҳдаи паёмбар ғайри таблиғи ошкор ҳеҷ нест».'),
(2846, 24, 55, 'Худо ба касоне аз шумо, ки имон овардаанд ва корҳое шоиста кардаанд, ваъда дод, ки дар рӯи замин ҷонишини дигаронашон кунад, ҳамчунон, ки мардумеро, ки пеш аз онҳо буданд, ҷонишини дигарон кард, Ва динашонро, ки худ барояшон писандида аст, устувор созад. Ва ваҳшаташонро ба тинҷи иваз кунад. Маро мепарастанд ва ҳеҷ чизеро бо Ман шарик намекунанд. Ва онҳо, ки аз ин пас ношукрӣ кунанд, нофармонанд!'),
(2847, 24, 56, 'Ва намоз бигузоред ра закот бидиҳед ва ба паёмбар итоъат кунед, бошад, ки бар шумо раҳмат оваранд!'),
(2848, 24, 57, 'Мапиндор, ки кофирон метавонанд дар ин сарзамин ба ҷое бигурезанд. Ҷойгоҳашон ҷаҳаннам аст ва чӣ бад саранҷомест!'),
(2849, 24, 58, 'Эй касоне, ки имон овардаед, бояд ғуломони шумо ва онҳо, ки ҳанӯз ба ҳадди булуғ нарасидаанд, дар се ҳангом аз шумо барои дохил шудан ба хона рухсат талабанд: пеш аз намози субҳ ва ҳангоми зуҳр, ки либос аз тан берун мекунед ва баъд аз намози хуфтан. Ин се вақт вақти хилвати шумост. Дар ғайри он се ҳангом шумо ва онҳо гуноҳе накардаед, агар бар якдигар бигзаред. Худо оётро инчунин барои шумо баён мекунад. Ва Худо донову ҳаким аст!'),
(2850, 24, 59, 'Ва чун атфоли шумо ба ҳадди булуғ расиданд, бояд монанди касоне, ки зикрашон гузашт, рухсат талабанд. Худо оётро инчунин барои шумо баён мекунад. Ва Худо донову ҳаким аст!'),
(2851, 24, 60, 'Пиразанон, ки дигар умеди шӯй карданашон нест, бе он, ки зинатҳои худро ошкор кунанд, агар чодари худ биниҳанд, гуноҳе накардаанд. Ва худдорӣ кардан барояшон беҳтар аст ва Худо шунавову доност!'),
(2852, 24, 61, 'Бар нобино ҳараҷе (гуноҳе) нест ва бар ланг гуноҳе нест ва бар бемор гуноҳе нест. Ва бар шумо гуноҳе нест, агар аз хонаи худ ё хонаи падаратон ё хонаи модаратон ё хонаи бародаратон ё хонаи хоҳаратон ё хонаи амакатон ё хонаи аммаатон ё хонаи доиятон (тағоятон) ё хонаи холаатон ё хонае, ки калидҳои он назди шумост, ё хонаи дӯстатон чизе бихӯред. Ва гуноҳе бар шумо нест, агар бо ҳам бихӯред ё ҷудо-ҷудо. Ва чун ба хонае дохил шавед, бар якдигар салом кунед. ин саломест муборак ва покиза аз ҷониби Худо. Худо оётро барои шумо инчунин баён мекунад, шояд, ки ақл кунед!'),
(2853, 24, 62, 'Мӯъминон касоне ҳастанд, ки ба Худову паёмбараш имон овардаанд ва чун бо паёмбар дар коре якҷоя бошанд, то аз ӯ рухсат наталабидаанд, набояд бираванд. Онҳо, ки аз ту рухсат металабанд, касоне ҳастанд, ки ба Худову паёмбараш имон овардаанд. Пас ҳангоме ки аз ту барои баъзе аз корҳояшон рухсат хостанд, бар ҳар як аз онон, ки хоҳӣ, рухсат бидеҳ ва барояшон аз Худо бахшоиш бихоҳ ки Худо бахшояндаву меҳрубон аст!'),
(2854, 24, 63, 'Ончунон ки якдигарро садо мекунед, паёмбарро садо накунед. Худо медонад чӣ касоне аз шумо дар паноҳи дигаре худро пинҳон месозанд ва оҳистаоҳиста берун мераванд. Онон, ки аз фармони Ӯ сарпечӣ мекунанд, бояд битарсанд, ки мабодо ба онҳо фитнае ё азобе дардовар бирасад!'),
(2855, 24, 64, 'Аз они Худост ҳар чӣ дар осмонҳову замин аст. Медонад, ки шумо бар чӣ коред. Ва он рӯз, ки ба Ӯ бозгардонда шаванд, ононро аз корҳое, ки кардаанд, огоҳ созад. Ва Худо бар ҳар чизе доност!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Бузург асту бузургвор он кас, ки ин фурқонро бар бандаи Худ нозил кард, то ҷаҳониёнро бимдиҳандае бошад.'),
(2857, 25, 2, 'Он кас, ки аз они Ӯст фармонравоии осмонҳову замин ва фарзанде нагирифтааст ва Ӯро шарике дар фармонравоӣ нест ва ҳар чизро биёфаридааст ва онро ба миқдор офаридааст.'),
(2858, 25, 3, 'Ғайри Ӯ худоёне дигар гирифтанд, ки ҳеҷ чиз намеофаринанд, ва худ махлуқанд. На молики зиёни худ ҳастанд ва на нафъи худ ва молики маргу зиндагӣ ва қиёмат нестанд.'),
(2859, 25, 4, 'Ва кофирон гуфтанд, ки ин ҷуз дурӯғе, ки худ бофтааст ва гурӯҳе дигар ӯро бар он ёрӣ додаанд, ҳеҷ нест. Ҳаққо, он чӣ мегӯянд, ситаму ботил аст.'),
(2860, 25, 5, 'Ба гуфтанд: «Ин афсонаҳои пешиниён аст, ки ҳар субҳу шом бар ӯ хонда мешавад ва ӯ менависадаш».'),
(2861, 25, 6, 'Бигӯ: «Ин китобро касе нозил кардааст, ки ниҳони осмонҳову заминро медонад ва бахшояндаву меҳрубон аст!»'),
(2862, 25, 7, 'Гуфтанд: «Чаро ин паёмбар, ғизо мехӯрад ва дар бозорҳо роҳ меравад? Чаро фариштае бар ӯ фурӯд намеояд то бо ӯ бимдиҳанда бошад?'),
(2863, 25, 8, 'Чаро аз осмон ганҷе барояш афканда нашавад? Чаро ӯро боғе нест, ки аз он бихӯрад?» Ва ситамкорон гуфтанд: «Шумо ба марди ҷодушудае пайравӣ мекунед!»'),
(2864, 25, 9, 'Бингар, ки чӣ гуна бароят достонҳо меоваранд. Гумроҳ шудаанд ва тавони роҳ ёфтанашон нест!'),
(2865, 25, 10, 'Бузургу бузургвор аст он кас, ки агар хоҳад, беҳтар аз он ба ту медиҳад — боғҳое, ки дар онҳо наҳрҳо ҷорӣ бошад ва бароят қасрҳо биофаринад.'),
(2866, 25, 11, 'Балки инҳо қиёматро дурӯғ ҳисобанд. Ва Мо барои каноне, ки қиёматро дурӯғ ҳисобанд, оташи сӯзон омода кардаем,'),
(2867, 25, 12, 'ки чун аз роҳи дур бубинадашон, ҷӯшишу хурӯшашро бишнаванд.'),
(2868, 25, 13, 'Ва чун дастҳо бар гардан баста дар тангие аз он афтанд, ба дуъо марги худ мехоҳанд.'),
(2869, 25, 14, 'Имрӯз на як бор марги худ хоҳед, марги худ фаровон хоҳед!'),
(2870, 25, 15, 'Бигӯ: «Оё ин беҳтар аст ё он биҳишти ҷовидон, ки ба парҳезгорон ваъда шудааст, ки мукофоту саранҷоми онон хоҳад буд?»'),
(2871, 25, 16, 'То абад ҳар чӣ бихоҳанд, дар он ҷо ҳаст. Ваъдаест, ки анҷом додани он аз Парвардигорат хоста шудааст.'),
(2872, 25, 17, 'Рӯзе, ки ононро бо чизҳое, ки ғайри Худои якто мепарастиданд, ба машҳар ҷамъ овард ва сипас пурсад: «Оё шумо ин бандагони Маро гумроҳ мекардед, ё онҳо худ роҳро гум карда буданд?»'),
(2873, 25, 18, 'Гӯянд: «Покӣ Ту! Моро сазовор набудааст, ки ҷуз ту касеро ба ёрӣ гирем. Ту худ онҳо ва падаронашонро бархурдор сохтӣ, чунон ки ёди Туро фаромӯш карданд ва мардуме шуданд ба ҳалокат афтода.'),
(2874, 25, 19, 'Инҳо он чиро мегуфтед, дурӯғ хонданд ва акнун натавонед азоберо аз худ дур созед ё худро ёрӣ диҳед. Ва ҳар кас аз шумо, ки ситам кунад, азобе бузургаш мечашонем.'),
(2875, 25, 20, 'Пеш аз ту паёмбароне фиристодаем, ки таъом мехӯрданд ва дар бозорҳо роҳ мерафтанд. Ва шуморо василаи озмоиши якдигар қарор додем. Оё сабр тавонед кард? Ва Парвардигори ту биност.'),
(2876, 25, 21, 'Касоне, ки ба дидори Мо умед надоранд, гуфтанд: «Чаро фариштагон бар мо намефароянд? Ё чаро Парвардигори худро намебинем?» Ба ростӣ, ки худро бузург шумурданд ва саркашӣ карданд, саркашие бузург.'),
(2877, 25, 22, 'Рӯзе, ки фариштагонро бубинанд, дар он рӯз гунаҳкоронро ҳеҷ хушхабаре надиҳанд ва ба инҳо гӯянд: «Хушхабар бар шумо ҳаром аст!»'),
(2878, 25, 23, 'З Ва ба аъмоле, ки кардаанд, қасд кунем ва ҳамаро чун зарроти хок барбод диҳем.'),
(2879, 25, 24, 'Аҳли биҳишт дар ин рӯз дар беҳтарин ҷойгоҳ ва беҳтарин макон барои оромиш ҳастанд,'),
(2880, 25, 25, 'Рӯзе, ки осмон бо абрҳо мешикофад ва фариштагон бар замин поён фиристода шаванд,'),
(2881, 25, 26, 'фармонравоӣ дар он рӯз, ба ростӣ, аз они Худои раҳмон аст. Ва барои кофирон рӯзе душвор хоҳад буд.'),
(2882, 25, 27, 'Рӯзе, ки кофир дастони худро ба дандон газад ва гӯяд: «Эй кош, роҳеро, ки расул дар пеш гирифта буд дар роҳ гирифта будам.'),
(2883, 25, 28, 'Вой бар ман, кош фалонро дӯст намегирифтам;'),
(2884, 25, 29, 'бо он ки Қуръон барои ман нозил шуда буд, маро аз пайравиаш бозмедошт. Ва ин шайтон ҳамеша одамиро хору танҳо мегузорад.'),
(2885, 25, 30, 'Паёмбар гуфт: «Эй Парвардигори ман қавми ман тарки Қуръон гуфтанд!»'),
(2886, 25, 31, 'Инчунин ҳар паёмбареро аз миёни кофирон душмане падид овардем. Ва Парвардигори ту барои роҳнамоиву ёрии ту кофист!'),
(2887, 25, 32, 'Кофирон гуфтанд: «Чаро ин Қуръон ба якбора бар ӯ нозил намешавад?» Барои он аст, ки дили туро ба он устуворӣ диҳем ва онро ба оҳистагиву тартиб хонем.'),
(2888, 25, 33, 'Ҳар мисоле барои ту биёваранд, албатта ҷавобашро ба ростӣ ва некӯтарин баён биёварем.'),
(2889, 25, 34, 'Касонеро, ки аз рӯй мекашанду дар ҷаҳаннам ҷамъ меоваранд, ба ҷойгоҳ бадтару ба роҳ гумгаштатаранд.'),
(2890, 25, 35, 'Ба Мӯсо китоб додем ва бародараш Ҳорунро мададгораш сохтем.'),
(2891, 25, 36, 'Ва гуфтем: «Назди мардуме, ки оёти Моро дурӯғ баровардаанд, биравед». Ва он қавмро ба сахтӣ ҳалок кардем.'),
(2892, 25, 37, 'Қавми Нӯҳро, чун паёмбаронро дурӯғ бароварданд, ғарк кардем ва онҳоро барои мардум ибрате сохтем. Ва барои ситамкорон азобе дардовар омода кардаем'),
(2893, 25, 38, 'ва Одро ва Самудро ва асҳоби Рассро ва наслҳои бисёреро, ки миёни онҳо буданд, ҳалок кардем.'),
(2894, 25, 39, 'Ва барои ҳама мисолҳое овардем ва ҳамаро несту нобуд сохтем.'),
(2895, 25, 40, 'Ва бар деҳае, ки бар он борони азоб борида будем, гузар мекарданд. Оё онро намедиданд? Оре, ба қиёмат умед надоштанд.'),
(2896, 25, 41, 'Чун туро диданд, масхараат карданд, ки оё ин аст он паёмбаре, ки Худо бар мо фиристода аст?'),
(2897, 25, 42, 'Агар бар худоёнамон устувор намеистодем, наздиқ буд, ки моро аз парастишашон гумроҳ кунад. Чун азобро бубинанд, хоҳанд донист чӣ касе гумроҳтар будааст.'),
(2898, 25, 43, 'Оё он касро, ки ҳавои нафсро ба худоӣ гирифта буд, дидӣ? Оё ту зомини ӯ ҳастӣ?'),
(2899, 25, 44, 'Ё гумон кардаӣ, ки бештаринашон мешунаванду мефаҳманд? Инҳо чун чорпоёне ҳастанд, балки аз чорпоён ҳам гумроҳтаранд.'),
(2900, 25, 45, 'Надидаӣ, ки Парвардигори ту чӣ гуна сояро мекашад? Агар мехост, дар як ҷо сокинаш мегардонд. Он гоҳ офтобро бар ӯ далел гардонидем.'),
(2901, 25, 46, 'Сипас баргиркфтемаш, гирифтане андак-андак.'),
(2902, 25, 47, 'Ӯст, ки шабро пӯшиши шумо кард ва хобро оромишатон ва рӯзро замони бархостанатон.'),
(2903, 25, 48, 'Ӯст, ки пешопеши раҳмати худ бодҳоро ба мужда додан фиристод. Ва аз осмон обе пок нозил кардем,'),
(2904, 25, 49, 'то сарзамини мурдаро ба он ҳаёт бахшем ва чорпоён ва мардуми бисёреро, ки офаридаем, аз он об диҳем.'),
(2905, 25, 50, 'Ва то ба тафаккур пардозанд, боронро миёнашон тақсим кардем. Вале бештари мардум носипосӣ карданд!'),
(2906, 25, 51, 'Агар мехостем, ба ҳар деҳае бимдиҳандае мефиристодем.'),
(2907, 25, 52, 'Ба кофирон итоъат макун ва ба ҳукми Худо бо онҳо чиҳод кун, чиҳоде бузург!'),
(2908, 25, 53, 'Ӯст, ки ду дарё ба ҳам биёмехт — яке ширину гуворо ва дигаре шӯробе сахт газанда. Ва миёни он ду мрнеъае ва садде устувор қарор дод.'),
(2909, 25, 54, 'Ӯст, ки одамиро аз об биёфарид ва ӯро насабу пайванд сохт ва Парвардигори ту ба ин корҳо тавоност!'),
(2910, 25, 55, 'Ғайри Худои якто чизҳоеро мепарастанд, ки ба онҳо на нафъ медиҳад ва на зиён. Ва кофир ҳамеша ёрирасони мухолифони Парвардигори худ. аст.'),
(2911, 25, 56, 'Мо туро фиристодем, барои он ки хушхабар диҳӣ ва битарсонӣ.'),
(2912, 25, 57, 'Бигӯ: «Ман аз шумо ҳеҷ музде наметалабам лекин ҳарки хоҳад, ба суй Парвардигораш роҳе биёбад».'),
(2913, 25, 58, 'Ва бар Он зиндае, ки намемирад, таваккал кун ва ба ситоиши Ӯ тасбеҳ гӯй ва Ӯ худ барои огоҳӣ аз гуноҳони бандагонаш кофист,'),
(2914, 25, 59, 'он, ки осмонҳову замин ва ҳар чиро дар миёни онҳост, дар шаш рӯз биёфарид, он гоҳ ба арш пардохт. Ӯст Худои раҳмон ва дар бораи Ӯ аз касе бипурс, ки огоҳ бошад.'),
(2915, 25, 60, 'Ва чун ба онон гуфта шуд, ки Раҳмонро саҷда кунед, гуфтанд: «Раҳмон кист? Оё бар ҳар кас, ки ту фармон медиҳӣ, саҷда кунем?» Ва бар нафраташон афзуда шуд. (Саҷда).'),
(2916, 25, 61, 'Бузургу бузургвор аст он касе, ки дар осмон бурҷҳо биёфарид ва дар онҳо чароғеву моҳе тобон падид овард.'),
(2917, 25, 62, 'Ва Ӯст, ки шабу рӯзро барои касоне, ки мехоҳанд ибрат гиранд, ё шукргузорӣ кунанд, аз паи ҳам қарор дод.'),
(2918, 25, 63, 'Бандагони Худои раҳмон касоне ҳастанд, ки дар рӯи замин ба фурӯтанӣ роҳ мераванд. Ва чун ҷоҳилон ононро хитоб кунанд, ба мулоимӣ сухан гӯянд.'),
(2919, 25, 64, 'Ва онон, ки шабро дар саҷда ё дар қиём барои Парвардигорашон рӯз мекунанд.'),
(2920, 25, 65, 'Ва онон, ки мегӯянд: «Эй Парвардигори мо, азоби ҷаҳаннамро аз мо бигардон, зеро азоби ҷаҳаннам азобест доим!'),
(2921, 25, 66, '"Ҷаҳаннам бад кароргоҳу бад маконест».'),
(2922, 25, 67, 'Ва онон, ки чун нафақа мекунанд, исроф намекунанд ва хасисӣ намекунанд, балки миёни ин ду роҳи миёнаро мегиранд.'),
(2923, 25, 68, 'Ва онон, ки бо Худои якто худои дигаре намегиранд ва касеро, ки Худо куштанашро ҳаром карда, ба ғайри ҳақ намекушанд ва зино намекунанд. Ва ҳар кӣ ин корҳо кунад, уқубати гуноҳи худро мебинад.'),
(2924, 25, 69, 'Азобаш дар рӯзи қиёмат музоъаф (дучандон) мешавад ва то абад ба хори дар он азоб хоҳад буд,'),
(2925, 25, 70, 'ғайри он касон, ки тавба кунанд ва имон оваранд ва корҳои шоиста кунанд. Худо гуноҳонашонро ба некиҳо мубаддал мекунад ва Худо бахшояндаву меҳрубон аст!'),
(2926, 25, 71, 'Ва ҳар кӣ тавба кунад ва кори шоиста кунад, ба шоистагӣ назди Худо бозгардад.'),
(2927, 25, 72, 'Ва онон, ки ба дурӯғ шоҳидӣ намедиҳанд ва чун бар нописандӣ бигзаранд, ба шитоб аз он дурӣ меҷӯянд.'),
(2928, 25, 73, 'Ва онон, ки чун ба оёти Парвардигорашон пандашон диҳанд, дар баробари он чун карону кӯрон набошанд.'),
(2929, 25, 74, 'Ва онон, ки мегӯянд: «Эй Парвардигори мо, аз ҳамсарону фарзандонамон дилҳои моро шод дор ва моро пешвон! парҳезгорон гардон!»'),
(2930, 25, 75, 'Инҳо ҳамон касонанд, ки ба хотири сабре, ки таҳаммул кардаанд, ғурфаҳои биҳиштро музд ёбанд ва дар он ҷо ба дуруду саломашон бинавозанд.'),
(2931, 25, 76, 'Ҷовидона дар он ҷо бошанд. Чӣ некӯ қароргоҳу маконест!'),
(2932, 25, 77, 'Бигӯ: «Агар дуъо (ву илтиҷо)-и шумо намебуд, Парвардигори ман ба шумо қадрдоние намекард. Дар ҳақиқат шумо дурӯғ кардаед ва ҷазоатон ҳамроҳатон хоҳад буд».');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'То, син, мим.'),
(2934, 26, 2, 'Инҳост оёти ин китоби равшан(-у фаҳмо)'),
(2935, 26, 3, 'Шояд аз ин, ки имон намеоваранд, худро ҳалок созӣ.'),
(2936, 26, 4, 'Агар бихоҳем, аз осмон барояшон мӯъҷизае нозил мекунем, ки дар баробари он ба фармонбарӣ сар фуруд оваранд.'),
(2937, 26, 5, 'Барояшон ҳар сухани тозае аз Худои раҳмон нозил шавад, албатта аз он рӯгардонӣ мекунанд.'),
(2938, 26, 6, 'Пас дурӯғ бароварданд. Ба зуди хабари он чизҳое, ки масхарааш мекарданд, ба онҳо бирасад.'),
(2939, 26, 7, 'Оё ба замин нанигаристаанд, ки чӣ қадар аз ҳар гуна гиёҳони некӯ дар он рӯёнидаем.'),
(2940, 26, 8, 'Дар ин ибратест, вале бештарашон мӯъмин набудаанд.'),
(2941, 26, 9, 'Ба ростӣ Парвардигори ту пирӯзманду меҳрубон аст!'),
(2942, 26, 10, 'Ва Парвардигорат Мӯсоро нидо до, д, ки: эй Мӯсо, ба сӯи он мардуми ситамгор бирав:'),
(2943, 26, 11, 'қавми Фиръавн. Оё намехоҳанд парҳезгор шаванд?'),
(2944, 26, 12, 'Гуфт: «Эй Парвардигори ман, метарсам, ки дурӯғгӯям хонанд.'),
(2945, 26, 13, 'Ва дили ман танг гардад ва забонам кушода нашавад. Ҳорунро пайғамбарӣ деҳ!'),
(2946, 26, 14, 'Ва бар ман ба гуноҳе даъвое доранд, метарсам, ки маро бикушанд».'),
(2947, 26, 15, 'Гуфт: «Ҳаргиз, оёти Маро ҳар ду назди онҳо бибаред, Мо низ бо шумо ҳастем ва гӯшкунандаем.'),
(2948, 26, 16, 'Пас назди Фиръавн равед ва бигӯед: «Мо расули Парвардигори ҷаҳониён ҳастем,'),
(2949, 26, 17, 'ки банӣ-Исроилро бо мо бифиристӣ».'),
(2950, 26, 18, 'Гуфт: «Оё ба ҳангоми кӯдакӣ назди худ парваришат надодем ва ту чанд сол аз умратро дар миёни мо нагузаронидӣ?'),
(2951, 26, 19, 'Ва он корро, ки аз ту оар зад, накардаӣ? Пас ту кофири неъматӣ».'),
(2952, 26, 20, 'Гуфт: «Он вақт, ки чунон кардам, аз хатокорон будам.'),
(2953, 26, 21, 'Ва чун аз шумо тарсидам, гурехтам. Вале Парвардигори мам ба ман паёмбарӣ дод ва маро дар шумори паёмбарон овард.'),
(2954, 26, 22, 'Ва миннати ин неъматро бар ман мекунӣ, ки банӣ-Исроилро ғулом сохтаӣ?»'),
(2955, 26, 23, 'Фиръавн гуфт: «Парвардигори ҷаҳониён чист?»'),
(2956, 26, 24, 'Гуфт: «Агар ба яқин мепазиред, Парвардигори осмонҳову замин ва ҳар чӣ миёни онҳост»,'),
(2957, 26, 25, 'Ба онон, ки дар атрофаш буданд, гуфт: «Оё нашунидед?»'),
(2958, 26, 26, 'Гуфт; «Парвардпгори шумо ва Парвардигори падарони аввалини шумо».'),
(2959, 26, 27, 'Фиръазн гуфт: «Ин паёмбаре, ки бар шумо фиристода шуда, девона аст».'),
(2960, 26, 28, 'Гуфт: «Ӯст Парвардигори машрику мағриб ва ҳар чӣ миёни он дӯст, агар ақл кунед».'),
(2961, 26, 29, 'Фиръавн гуфт: «Агар ба ҷои ман каси дигареро ба худои гири, ба зиндонат меафканам».'),
(2962, 26, 30, 'Гуфт: «Ҳатто агар мӯъҷизае равшан барои ту оварда бошам?»'),
(2963, 26, 31, 'Гуфт; Агар рост мегӯӣ, онро биёвар».'),
(2964, 26, 32, 'Асояшро партофт. Ба ошкоро аждаҳое шуд.'),
(2965, 26, 33, 'Ва дасташро берун овард. Дар назари бинандагон сафед менамуд.'),
(2966, 26, 34, 'Ба бузургони қавмаш, ки канораш буданд, гуфт: «Ин мард ҷодугаре доност.'),
(2967, 26, 35, 'Мехоҳад ба ҷодуи. худ шуморо аз сарзаминатон берун кунад. Чӣ раъй медиҳед?»'),
(2968, 26, 36, 'Гуфтанд: «Аз ӯ ва ба бародараш мӯҳлат бихоҳ ва касон ба шаҳрҳо бифирист,'),
(2969, 26, 37, 'то ҳар ҷодугари доноеро, ки ҳаёт, назди ту биёранд».'),
(2970, 26, 38, 'Ҷодугаронро дар рӯзе муъайян ба ваъдагоҳ оварданд.'),
(2971, 26, 39, 'Ва мардумро гуфтанд: «Оё шумо низ ҷамъ меоед?'),
(2972, 26, 40, 'То агар ҷодугарон пирӯз омаданд, ҳама ба онҳо пайравӣ кунем!»'),
(2973, 26, 41, 'Чун ҷодугарон омаданд, ба Фиръавн гуфтанд: «Оё агар мо пирӯз шавем, моро музде хоҳад буд?»'),
(2974, 26, 42, 'Гуфт: «Оре, ҳама аз наздиконам хоҳед буд».'),
(2975, 26, 43, 'Мӯсо ба онҳо гуфт: «Ҳар чӣ мехоҳед, партофтан, бипартоед!»'),
(2976, 26, 44, 'Онон ресмонҳову чубҳои худ бияфканданд ва гуфтанд: «Ба иззати Фиръавн, ки мо пирӯз шудем!»'),
(2977, 26, 45, 'Ва Мӯсо асояшро партофт. Ногоҳ ҳамаи он дурӯғҳоеро, ки сохта буданд, фурӯ бурд.'),
(2978, 26, 46, 'Ҷодугарон ба саҷда афтоданд.'),
(2979, 26, 47, 'Гуфтанд: «Ба Парвардигори ҷаҳониён имон овардем,'),
(2980, 26, 48, 'Парвардигори Мӯсо ва Ҳорун!»'),
(2981, 26, 49, 'Гуфт: «Оё пеш аз он, ки шуморо рухсат диҳам, имон овардед? Албатта он мард бузурги шумост, ки шуморо ҷоду омӯхтааст. Хоҳед дид. Акнун дастҳову поҳоятонро аз чапу рост хоҳам бурид ва ҳамаатонро бар дор хоҳам кард!»'),
(2982, 26, 50, 'Гуфтанд: «Боке нест, мо назди Парвардигорамон бозмегардем.'),
(2983, 26, 51, 'Мо умед медорем, ки Парвардигорамон хатоҳои моро бубахшад, ки мо нахустин касоне ҳастем, ки имон овардем».'),
(2984, 26, 52, 'Ва ба Мӯсо ваҳй кардем, ки шабҳангом бандагони Маро берун бибар, ки аз паи шумо биёянд.'),
(2985, 26, 53, 'Ва Фиръавн ҷамъоварандагони сипоҳро ба шаҳрҳо фиристод.'),
(2986, 26, 54, 'ки инҳо гурӯҳе андаканд'),
(2987, 26, 55, 'ва моро ба хашм овардаанд'),
(2988, 26, 56, 'ва мо ҳамагӣ омодаи ҷангем.'),
(2989, 26, 57, 'Пас онҳоро аз борҳову чашмасорҳо берун кардем'),
(2990, 26, 58, 'ва аз ганҷҳову хонаҳои некӯ,'),
(2991, 26, 59, 'ба ин тарз ҳамаро ба банӣ-Исроил вогузоштем'),
(2992, 26, 60, 'Фиръавниён ба ҳангоми баромадани офтоб аз паи онҳо рафтанд.'),
(2993, 26, 61, 'Чун он ду гурӯҳ якдигарро диданд, ёрони Мӯсо гуфтанд: «Гирифтор омадем!»'),
(2994, 26, 62, 'Гуфт: «Ҳаргиз, Парвардигори ман бо ман аст ва маро роҳ хоҳад нишон дод».'),
(2995, 26, 63, 'Пас ба Мӯсо ваҳй кардем, ки асоятро ба дарё бизан. Дарё бишикофт ва ҳар пора чун кӯҳе азим гашт.'),
(2996, 26, 64, 'Ва он гурӯҳи дигарро низ ба дарё расонидем.'),
(2997, 26, 65, 'Мӯсо ва ҳамаи ҳамроҳонашро наҷот додем.'),
(2998, 26, 66, 'Ва он дигаронро ғарк сохтем.'),
(2999, 26, 67, 'Албатта дар ин ибратест ва бештаринашон имон наоварданд!'),
(3000, 26, 68, 'Албатта Парвардигори ту пирӯзманду бахшанда аст!'),
(3001, 26, 69, 'Ва достони Иброҳимро барояшон тиловат кун!'),
(3002, 26, 70, 'Он гоҳ ки ба падару қавми худ гуфт: «Чӣ мепарастед?»'),
(3003, 26, 71, 'Гуфтачкд: «Бутонеро мепарастем ва муқими остонашон ҳастем».'),
(3004, 26, 72, 'Гуфт: «Оё вақте онҳоро мехонед, садоятонро мешунаванд?'),
(3005, 26, 73, 'Ё барои шумо фоидаву зиёне доранд?»'),
(3006, 26, 74, 'Гуфтанд: «На, падаронамонро дидаем, ки чунин мекардаанд».'),
(3007, 26, 75, 'Гуфт: Оё медонед, ки чӣ мепарастидаед'),
(3008, 26, 76, 'шумо ва падаронатон?'),
(3009, 26, 77, 'Онҳо душманони мананд, вале Парвардигори ҷаҳониён дӯсти ман аст.'),
(3010, 26, 78, 'Он, ки маро биёфарида, сипас роҳнамоиям мекунад'),
(3011, 26, 79, 'ва он, ки ба ман таъом медиҳад ва маро об медиҳад.'),
(3012, 26, 80, 'ва чун бемор шавам, шифоям мебахшад'),
(3013, 26, 81, 'ва он, ки маро мемиронад ва сипас зинда мекунад'),
(3014, 26, 82, 'ва он, ки умед медорам, ки дар рӯзи қиёмат хатоямро бибахшояд.'),
(3015, 26, 83, 'Эй Парвардигори ман, маро ҳикмат бахш ва маро ба шоистагон бипайванд.'),
(3016, 26, 84, 'Ва зикри неки маро дар даҳони ояндагон андоз'),
(3017, 26, 85, 'ва маро аз ворисони биҳишти пурнеъмат қарор деҳ'),
(3018, 26, 86, 'ва падарамро биёмурз, ки аз гумроҳон аст'),
(3019, 26, 87, 'ва дар рӯзи қиёмат маро расво масоз,'),
(3020, 26, 88, 'рӯзе, ки на мол нафъ медиҳад ва на фарзандон,'),
(3021, 26, 89, 'ғайри он кас, ки бо қалбе покиза аз ширк ба назди Худо биёяд!»'),
(3022, 26, 90, 'Ва биҳиштро барои парҳезгорон наздик оранд.'),
(3023, 26, 91, 'Ва ҷаҳаннамро дар назари кофирон ошкор кунанд.'),
(3024, 26, 92, 'Ба онҳо гӯянд: Чизҳое, ки ба ҷои Худои якто мепарастидед, куҷоянд?'),
(3025, 26, 93, 'Оё ёриатон мекунанд, ё худ ёрӣ меҷӯянд?»'),
(3026, 26, 94, 'Онҳо ва кофиронро сармагун дар ҷаҳаннам андозанд'),
(3027, 26, 95, 'ва ҳамаи лашкари Иблисро.'),
(3028, 26, 96, 'Ва дар ҳоле, ки дар ҷаҳаннам бо якдигар ҳусумат мекунанду, мегӯянд:'),
(3029, 26, 97, '«Ба Худо савганд, ки мо дар гумроҳии ошкор будем,'),
(3030, 26, 98, 'он гоҳ ки шуморо бо Парвардигори ҷаҳониён баробар медонистем.'),
(3031, 26, 99, 'Ва моро бадкорон гумроҳ сохтанд'),
(3032, 26, 100, 'ва акнун моро шафеъоне (халоскунандаҳое) нест'),
(3033, 26, 101, 'ва моро дӯсти меҳрубоне нест.'),
(3034, 26, 102, 'Кош бори дигар ба дунё бозгардем, то аз мӯъминон гардем».'),
(3035, 26, 103, 'Дар ин ибратест ва бештаринашон имон наёварданд.'),
(3036, 26, 104, 'Албатта Парвардигори ту ғолибу меҳрубон аст!'),
(3037, 26, 105, 'Қавми Нӯҳ паёмбаронро дурӯғ барозарданд.'),
(3038, 26, 106, 'Он гоҳ ки бародарашон Нӯҳ ба онҳо гуфт: «Оё наметарсед?'),
(3039, 26, 107, 'Ман барои шумо паёмбаре ростгӯ ҳастам.'),
(3040, 26, 108, 'Аз Худо битарсед ва ба ман итоъат кунед.'),
(3041, 26, 109, 'Ман аз шумо дар баробари ҳидояти худ музде наметалабам. Музди ман танҳо бар ӯҳдаи Парвардигори ҷаҳониён аст.'),
(3042, 26, 110, 'Пас аз Худо битарсед ва ба ман итоъат кунед!»'),
(3043, 26, 111, 'Гуфтанд: «Оё ба ту имон биёварем ва ҳол он ки камбағалон пайрави ту ҳастанд?»'),
(3044, 26, 112, 'Гуфт: «Дониши ман ба корҳое, ки мекунанд, намерасад.'),
(3045, 26, 113, 'Агар мефаҳмед, ҳисобаш танҳо бо Парвардигори ман аст.'),
(3046, 26, 114, 'Ва ман мӯъминонро дур намекунам.'),
(3047, 26, 115, 'Бимдиҳандае ошкор ҳастам».'),
(3048, 26, 116, 'Гуфтанд: «Эй Нӯҳ агар бас накунӣ, сангсор мешавӣ».'),
(3049, 26, 117, 'Гуфт: «Эй Парвардигори ман, қавми ман маро дурӯғ мебароранд.'),
(3050, 26, 118, 'Миёни ману онҳо роҳе баркушой ва марову мӯъминони ҳамроҳи маро наҷот бидеҳ».'),
(3051, 26, 119, 'Ӯ ва ҳамроҳонашро дар он киштии пур наҷот додем.'),
(3052, 26, 120, 'Ва боқиро ғарк, кардем.'),
(3053, 26, 121, 'Албатта дар ин ибратест ва бештаринашон имон наёварданд.'),
(3054, 26, 122, 'Албатта Парвардигори ту ғолибу меҳрубон аст!'),
(3055, 26, 123, 'Қавми Од паёмбаронро такзиб карданд (дурӯғ бароварданд).'),
(3056, 26, 124, 'Он гоҳ, ки бародарашон Ҳуд гуфт: «Оё наметарсед?'),
(3057, 26, 125, 'Ман барои шумо паёмбаре ростгӯ ҳастам.'),
(3058, 26, 126, 'Аз Худо битарсед ва ба ман итоъат кунед.'),
(3059, 26, 127, 'Ман аз шумо дар баробари ҳидояти худ музде наметалабам. Музди ман танҳо бар ӯҳдаи Парвардигори ҷаҳониён аст.'),
(3060, 26, 128, 'Оё бар сари ҳар баландӣ ба беҳудагӣ биное баланд месозед?'),
(3061, 26, 129, 'Ва бад-ин пиндор, ки ҳамеша зиндаед, қасрхое бино мекунед?'),
(3062, 26, 130, 'Ва ҳар гоҳ интиқом гиред, чун бераҳмон интиқом мегиред?'),
(3063, 26, 131, 'Пас аз Худо битарсед ва аз ман итоъат кунед.'),
(3064, 26, 132, 'Ва битарсед аз он Худое, ки он чиро, ки медонед, ба шумо ато кардааст.'),
(3065, 26, 133, 'Ва ба шумо чорпоёну фарзандон ато кардааст.'),
(3066, 26, 134, 'ва боғҳову чашмасорон.'),
(3067, 26, 135, 'Ман аз азоби рӯзе бузург бар шумо бимнокам».'),
(3068, 26, 136, 'Гуфтанд: «Барюй мо яксон аст, ки моро панд диҳӣ ё панд надиҳӣ.'),
(3069, 26, 137, 'Инҳо ҷуз ҳамон дурӯғу найранги пешиниён нест.'),
(3070, 26, 138, 'Ва мо азоб нахоҳем шуд».'),
(3071, 26, 139, 'Пас дурӯғаш бароварданд ва Мо ононро ҳалок кардем. Албатта дар ин ибратест ва бештаринашон имон наёварданд!'),
(3072, 26, 140, 'Албатта Парвардигори ту ғолибу меҳрубон аст!'),
(3073, 26, 141, 'Қавми Самуд паёмбаронро дурӯғ бароварданд,'),
(3074, 26, 142, 'Он гоҳ ки бародарашон Солеҳ гуфт: «Оё наметарсед?'),
(3075, 26, 143, 'Ман барои шумо паёмбаре ростгӯ ҳастам.'),
(3076, 26, 144, 'Аз Худо битарсед ва ба ман итоъат кунед!'),
(3077, 26, 145, 'Ман аз шумо дар баробари ҳидояти худ музде наметалабам. Музди ман танҳо бар ӯҳдаи Парвардигори ҷаҳониён аст!'),
(3078, 26, 146, 'Оё пиндоред, ки шуморо дар ин неъматҳо эмин раҳо мекунанд?'),
(3079, 26, 147, 'Дар боғҳову чашмасорҳо?'),
(3080, 26, 148, 'Ва киштзорҳову наҳлҳо (хурмозорҳо) бо он шукуфаҳои нарму латиф?'),
(3081, 26, 149, 'Ва шодмон дар кӯҳҳо хонаҳое метарошед?'),
(3082, 26, 150, 'Пас аз Худо битарсед ва ба ман итоъат кунед!'),
(3083, 26, 151, 'Ва фармони ин исрофкоронро қабул накунед.'),
(3084, 26, 152, 'Инҳо, ки дар замин фасод мекунанд ва ислоҳ намекунанд».'),
(3085, 26, 153, 'Гуфтанд: «Ғайри ин нест, ки туро ҷоду кардаанд.'),
(3086, 26, 154, 'Ту низ башаре монанди мо ҳастӣ. Агар рост мегӯӣ, нишонае биёвар».'),
(3087, 26, 155, 'Гуфт: «Ин модашутури ман аст. Як рӯз об хӯрдан ҳаққи ӯ бошад ва як рӯз ҳаққи шумо.'),
(3088, 26, 156, 'Ба он осебе нарасонед, ки азоби рӯзе бузург шуморо фурӯ мегирад».'),
(3089, 26, 157, 'Онро куштанд ва пушаймон шуданд.'),
(3090, 26, 158, 'Пас азоб онҳоро фурӯ гирифт., Албатта дар ин ибратест ва бештаринашон имон наёварданд.'),
(3091, 26, 159, 'Албатта Парвардигори ту ғолибу меҳрубон аст!'),
(3092, 26, 160, 'Қавми Лут паёмбаронро дурӯғ бароварданд.'),
(3093, 26, 161, 'Он гоҳ, ки бародарашон Лут гуфт: «Оё наметарсед?'),
(3094, 26, 162, 'Мам барои шумо паёмбаре ростгӯ ҳастам.'),
(3095, 26, 163, 'Аз Худо битарсед ва ба ман итоъат кунед!'),
(3096, 26, 164, 'Ман аз шумо дар баробари ҳидояти худ музде наметалабам. Музди ман танҳо бар ӯхдаи Парвардигори ҷаҳониён аст!'),
(3097, 26, 165, 'Чаро аз миёни мардуми ҷаҳон бо мардон меомезед?'),
(3098, 26, 166, 'Ва ҳамсаронеро, ки Парвардигоратон бароятон офаридааст, тарк мегӯед? На, шумо мардуме таҷовузкор ҳастед!»'),
(3099, 26, 167, 'Гуфтанд: «Эй Лут, агар бас накунӣ, аз шаҳр берунат мекунем».'),
(3100, 26, 168, 'Гуфт: «Ман бо кори шумо душманам.'),
(3101, 26, 169, 'Эй Парвардигори ман, маро ва касонамро аз оқибати амале, ки мекунанд, наҷот бахш!»'),
(3102, 26, 170, 'Ӯ ва ҳамаи касонашро наҷот додем,'),
(3103, 26, 171, 'ғайри пиразане, ки хост бимонад.'),
(3104, 26, 172, 'Сипас дигаронро ҳалок кардем,'),
(3105, 26, 173, 'бар онҳо бороне боронидем ва чӣ бад буд борони таҳдидшудагон!'),
(3106, 26, 174, 'Албатта дар ин ибратест ва бештаринашон имон наёварданд.'),
(3107, 26, 175, 'Албатта Парвардигори ту ғолибу меҳрубон аст!'),
(3108, 26, 176, 'Мардуми Айка паёмбаронро дурӯғ бароварданд.'),
(3109, 26, 177, 'Он гоҳ ки Шуъайб ба онҳо гуфт: «Оё аз Худо наметарсед?'),
(3110, 26, 178, 'Ман барои шумо паёмбаре ростгӯ ҳастам.'),
(3111, 26, 179, 'Аз Худо битарсед ва ба ман итоъат кунед!'),
(3112, 26, 180, 'Ман аз шумо дар баробари ҳидояти худ музде наметалабам. Музди ман танҳо бар ӯҳдаи Парвардигори ҷаҳониён аст.'),
(3113, 26, 181, 'Паймонаро (зарфи ченкуниро) тамом бипардозед ва камфурӯшӣ макунед.'),
(3114, 26, 182, 'Ва бо тарозуи дуруст вазн кунед.'),
(3115, 26, 183, 'Ба мардум кам мадиҳед ва бебокона дар замин фасод макунед.'),
(3116, 26, 184, 'Аз он кас, ки шумо ва офаридагони пеш аз шуморо офаридааст, битарсед».'),
(3117, 26, 185, 'Гуфтанд: «Ғайри ин нест, ки туро ҷоду кардаанд.'),
(3118, 26, 186, 'Ту низ инсоне монанди мо ҳастӣ ва мепиндорем, ки дурӯғ мегӯӣ.'),
(3119, 26, 187, 'Агар рост мегӯӣ, порае аз осмонро бар сари мо андоз».'),
(3120, 26, 188, 'Гуфт: «Парвардигори ман ба коро, ки мекунед, донотар аст».'),
(3121, 26, 189, 'Пас дурӯғаш бароварданд ва дар он рӯзи абрӣ азоб ононро фурӯ гирифг. Ва он азоби рӯзе бузург буд!'),
(3122, 26, 190, 'Албатта дар ин ибратест ва бештаринашон имон наёварданд.'),
(3123, 26, 191, 'Албатта Парвардигори ту ғолибу меҳрубон аст.'),
(3124, 26, 192, 'Ва ин китоб нозилшуда аз ҷониби Парвардигори ҷаҳониён аст.'),
(3125, 26, 193, 'Онро рӯҳуламин нозил кардааст'),
(3126, 26, 194, 'бар дили ту, то аз бимдиҳандагон бошӣ,'),
(3127, 26, 195, 'ба забони арабии равшан.'),
(3128, 26, 196, 'Ва он дар навиштаҳои пешиниён низ ҳаст.'),
(3129, 26, 197, 'Оё ин нишона бар онҳо басанда нест, ки уламои банӣ-Исроил аз он огоҳанд?'),
(3130, 26, 198, 'Агар онро бар яке аз аҷамон (ғайри арабҳо) нозил карда будем'),
(3131, 26, 199, 'ва барояшон онро мехонд, ба он имон намеоварданд,'),
(3132, 26, 200, 'Ба ин тарз Қуръонро дар дилҳои гунаҳкорон роҳ додем.'),
(3133, 26, 201, 'Ба он имон намеоваранд то азоби дардоварро бубинанд.'),
(3134, 26, 202, 'Ва он азоб ногаҳон ва бехабар бар онон фурӯд меояд.'),
(3135, 26, 203, 'Мегӯянд: «Оё моро мӯҳлате хоҳанд дод?»'),
(3136, 26, 204, 'Оё ба азоби мо мешитобанд?'),
(3137, 26, 205, 'Дидӣ, ки ҳарчанд солҳо баҳрамандашон сохтем,'),
(3138, 26, 206, 'боз ҳам азобе, ки ба онҳо ваъда шуда буд, бар сарашон омад.'),
(3139, 26, 207, 'Он баҳрамандиҳо ба ҳолашон нафъ накард?'),
(3140, 26, 208, 'Мо ҳеҷ деҳаеро ҳалок накардем, ҷуз он, ки барояшон бимдиҳандагоне буданд,'),
(3141, 26, 209, 'то пандашон диҳанд. Зеро мо ситамкор нестем.'),
(3142, 26, 210, 'Ва ин Қуръонро шайтонҳо нозил накардаанд.'),
(3143, 26, 211, 'Онон на лоиқи ин коранд ва на тавони он доранд.'),
(3144, 26, 212, 'Шайтонҳоро аз шунидани ваҳй дур доштаанд.'),
(3145, 26, 213, 'Пас бо Худои якто худои дигареро махон, то мабод дар шумори азобшавандагон дароӣ.'),
(3146, 26, 214, 'Хешовандони наздикатро битарсон.'),
(3147, 26, 215, 'Дар баробари ҳар як аз мӯъминон, ки ба ту пайравӣ мекунад, хоксору нарм бош!'),
(3148, 26, 216, 'Ва агар бар ту нофармони карданд, бигӯ: «Ман аз корҳои шумо безорам!»'),
(3149, 26, 217, 'Ва бар Худои пирӯзманду меҳрубон таваккал кун.'),
(3150, 26, 218, 'Он, ки туро мебинад, он гоҳ, ки бармехезӣ'),
(3151, 26, 219, 'ва намоз хонданатро бо дигар намозгузорон мебинад.'),
(3152, 26, 220, 'Албатта Ӯст шунавои доно!'),
(3153, 26, 221, 'Оё шуморо огоҳ кунам, ки шайтонҳо ба назди киҳо меоянд?'),
(3154, 26, 222, 'Бар ҳар дурӯғгӯи гунаҳкор меоянд.'),
(3155, 26, 223, 'Гӯш медиҳанд ва бештаринашон дурӯғгӯёнанд.'),
(3156, 26, 224, 'Ва гумроҳон аз паи шоирон мераванд.'),
(3157, 26, 225, 'Оё надидаӣ, ки шоирон дар ҳар водие саргаштаанд?'),
(3158, 26, 226, 'Ва чизҳо мегӯянд, ки худ амал намекунанд?'),
(3159, 26, 227, 'Ғайри онҳо, ки имон оварданд ва корҳои шоиста карданд ва Худоро фаровон ёд карданд ва чун мавриди ситам воқеъ шуданд, интиқом гирифтанд. Ва ситамкорон ба зуди хоҳанд донист, ки ба чӣ маконе бозмегарданд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'То, син. Ин аст оёти Қуръон ва китоби равшангар.'),
(3161, 27, 2, 'Раҳнамун ва хушхабарест барои мӯъминон:'),
(3162, 27, 3, 'онон, ки намоз мегузоранд ва закот медиҳанд ва ба рӯзи қиёмат яқин доранд.'),
(3163, 27, 4, 'Амалҳои онҳоеро, ки ба охират имон надоранд, дар дар назарашон биёростем. Аз ин рӯй саргашта мондаанд.'),
(3164, 27, 5, 'Онҳо ҳамон касонанд, ки азоби сахт аз они онҳост ва дар охират зиёнкортаранд.'),
(3165, 27, 6, 'Ту касе ҳастӣ, ки Қуръон аз ҷониби Худои ҳакиму доно ба ту нозил мешавад.'),
(3166, 27, 7, 'Мӯсо ба хонаводаи худ гуфт: «Ман аз дур оташе дидам, ба зудӣ, ки аз он бароятон хабаре биёварам ё пораи оташе. Шояд гарм шавед».'),
(3167, 27, 8, 'Чун ба оташ расид, садояш доданд, ки баракат дода шуда он, ки даруни оташ аст ва он, ки дар канори он аст. Ва пок аст Худои якто, он Парвардигори ҷаҳониён!'),
(3168, 27, 9, '«Эй Мӯсо, Ман Худои ғолибу ҳаким ҳастам.'),
(3169, 27, 10, 'Асоятро бияфкан!»; Чун дидаш, ки монанди море меҷунбад, гурезон бозгашт ва ба ақиб нанигарист. «Эй Мӯсо, матарс. Паёмбарон набояд, ки дар назди ман битарсанд,'),
(3170, 27, 11, 'ғайри касе, ки гуноҳе карда бошад ва пас аз бадкорӣ некӯкор шавад. Зеро Ман бахшояндаву меҳрубонам!'),
(3171, 27, 12, 'Дастатро дар гиребон бибар, то бе ҳеҷ осебе сафед берун ояд. Бо нӯҳ нишона назди Фиръавн ва қавмаш бирав, ки мардуме фосиқанд».'),
(3172, 27, 13, 'Чун нишонаҳои равшани Моро диданд, гуфтанд: «Ин ҷодуе ошкор аст!»'),
(3173, 27, 14, 'Бо он, ки дар дил ба он яқин оварда буданд, вале аз рӯи ситаму бартариҷӯӣ инкораш карданд. Пас бингар, ки оқибати табаҳкорон чӣ гуна бувад!'),
(3174, 27, 15, 'Мо ба Довуду Сулаймон дониш додем. Гуфтанд: «Шукр Худоро аст, ки моро бар бисёре аз бандагони мӯъмини худ бартарӣ дод!»'),
(3175, 27, 16, 'Ва Сулаймон вориси Довуд шуд ва гуфт: «Эй мардум, ба мо забони мурғон омӯхтанд ва аз ҳар неъмате ато карданд. Ва ин иноятест ошкор!»'),
(3176, 27, 17, 'Сипоҳиёни Сулаймон аз ҷинну одами ва парранда гирд омаданд ва онҳо ба саф мерафтанд.'),
(3177, 27, 18, 'То ба водии мӯрчагон расиданд. Мӯрчае гуфт: «Эй мӯрчагон, ба лонаҳои худ дароед, то Сулаймону лашкариёнаш шуморо бехабар помол накунанд».'),
(3178, 27, 19, 'Сулаймон аз сухани ӯ лабханд заду гуфт: «Эй Парвардигори ман, маро тавфиқ деҳ то шукри неъмати туро, ки бар ман ва падару модари ман арзонӣ доштаӣ, ба ҷой оварам ва корҳои шоистае кунам, ки ту хушнуд шавӣ ва маро ба раҳмати худ дар шумори бандагони шоистаат даровар!»'),
(3179, 27, 20, 'Дар миёни мурғон ҷустуҷӯ кард ва гуфт: «Чаро ҳудҳудро намебинам. Оё аз ғоибшудагон аст?'),
(3180, 27, 21, 'Ба сахттарин тарзе азобаш мекунам ё сарашро мебурам, агар барои ман далеле равшан наорад».'),
(3181, 27, 22, 'Дер карданаш ба дароз накашид. Биёмаду гуфт «Ба чизе даст ёфтаам, ки ту даст наёфта будӣ ва аз Сабо бароят хабаре дуруст овардаам.'),
(3182, 27, 23, 'Занеро ёфтам, ки бар онҳо подшоҳӣ мекунад. Аз ҳар неъмате бархурдор аст ва тахте бузург дорад.'),
(3183, 27, 24, 'Дидам, ки худ ва мардумаш ба ҷои Худои якто офтобро саҷда мекунанд. Ва шайтон амалҳояшонро дар назарашон биёростааст ва аз роҳи Худо гумроҳашон кардааст, чунон ки рӯи ҳидоят нахоҳанд дид.'),
(3184, 27, 25, 'Чаро Худоеро, ки ниҳони осмонҳову заминро ошкор мекунад ва ҳар чиро пинҳон медоред ё ошкор месозед, медонад, саҷда накунанд?'),
(3185, 27, 26, 'Худои якто, ки ҳеҷ худое ғайри Ӯ нест. Парвардигори арши азим». (Саҷда).'),
(3186, 27, 27, 'Гуфт: «Акнун бингарем, ки рост гуфтаӣ ё дар шумори дурӯғгӯёнӣ.'),
(3187, 27, 28, 'Ин номаи маро бибар ва бар онҳо афкан, сипас ба як сӯ шав ва бингар, ки чӣ ҷавоб медиҳанд».'),
(3188, 27, 29, 'Зан гуфт: «Эй бузургон, номае гиромӣ ба сӯи ман партофта шуд.'),
(3189, 27, 30, 'Нома аз Сулаймон аст ва ин аст: «Ба номи Худои бахшояндаи меҳрубон».'),
(3190, 27, 31, '«Бар ман бартарӣ маҷӯед ва ба таслим назди ман биёед».'),
(3191, 27, 32, 'Зан гуфт: «Эй бузургон, дар кори ман раъй бидиҳед, ки то шумо ҳозир набошед, ман ҳеҷ кореро ҳал натавонам кард».'),
(3192, 27, 33, 'Гуфтанд: Мо қудратмандон ва соҳибони ҷанги сахт ҳастем. Корҳо ба дасти туст. Бингар, ки чӣ фармон медиҳӣ».'),
(3193, 27, 34, 'Зан гуфт: «Подшоҳон чун ба деҳае дароянд, вайронаш мекунанд ва азизонашро хор месозанд. Оре, чунин кунанд.'),
(3194, 27, 35, 'Ман ҳадяе наздашон мефиристам ва менигарам, ки қосидон чӣ ҷавоб меоваранд».'),
(3195, 27, 36, 'Чун қосид назди Сулаймон омад, Сулаймон гуфт: «Оё мехоҳед ба мол маро ёрӣ кунед? Он чӣ Худо ба ман дода, аз он чӣ ба шумо дода беҳтар аст. На, шумо ба ҳадяи худ шодмон ҳастед.'),
(3196, 27, 37, 'Акнун ба наздашон бозгард. Лашкаре бар сарашон мекашем, ки ҳаргиз тоқати онро надошта бошанд. Ва ба хориву зори аз он ҷо берунашон мекунем».'),
(3197, 27, 38, 'Гуфт: «Эй бузургон, кадом як аз шумо тахти ӯро, пеш аз он, ки ба таслим назди ман ояд, бароям меоваред?»'),
(3198, 27, 39, 'Ифрите аз миёни ҷинҳо гуфт: «Ман қабл аз он, ки аз ҷоят бархезӣ, онро назди ту ҳозир мекунам, ки ман бар ин кор ҳам тавонояму ҳам боваринок».'),
(3199, 27, 40, 'Ва он кас, ки аз илми китоб баҳрае дошт, гуфт: «Ман, пеш аз он, ки чашм бар ҳам занӣ, онро назди ту меоварам». Чун онро назди худ дид, гуфт: «Ин бахшиши Парвардигори ман аст, то маро биёзмояд, ки шукргузорам ё кофири неъмат. Пас ҳар кӣ шукр гӯяд, барои худ гуфтааст ва ҳар кӣ ношукрӣ кунад, Парвардигори ман бениёзу карим аст».'),
(3200, 27, 41, 'Гуфт: «Тахташро дигаргун кунед, бубинем онро бозмешиносад ё аз онҳост, ки бознатавонанд шинохт».'),
(3201, 27, 42, 'Чун омад, гуфтандаш: «Оё тахти ту чунин буд?» Гуфт: «Гӯё ҳамон аст. Ва мо пеш аз ин огоҳ шуда будем ва таслим будаем».'),
(3202, 27, 43, 'Ва ӯро аз ҳар чӣ ба ҷои Худои якто мепарастид, боздошт. Зеро ӯ дар зумраи кофирон буд.'),
(3203, 27, 44, 'Гуфтандаш: «Ба саҳни қаср дарой!» Чун бидидаш, пиндошт, ҳавзи пур аз об аст. Доман аз соқҳояш баргирифт. Сулаймон гуфт: «Ин саҳнест соф аз шиша». Гуфт: «Эй Парвардигори ман, ман бар худ ситам кардаам ва инак бо Сулаймон дар баробари Парвардигори ҷаҳониён таслим шудам».'),
(3204, 27, 45, 'Ва ба қавми Самуд бародарашон Солеҳро фиристодем, ки: Худои якторо бипарастед. Ногаҳон ду гурӯҳ шуданд ва бо якдигар ба хусумат бархостанд.'),
(3205, 27, 46, 'Гуфт: «Эй қавми ман, чаро пеш аз некӣ бар бадӣ мешитобед? Чаро аз Худо бахшоиш намехоҳед? Шояд бар шумо раҳмат оварад».'),
(3206, 27, 47, 'Гуфтанд: «Мо туро ва ёронатро ба фоли бад гирифтаем». Гуфт: «Фоли бадатон назди Худост. Инак мардуме фиребхӯрда ҳастед!»'),
(3207, 27, 48, 'Дар шаҳр нӯҳ марди носолеҳ буданд, ки дар он сарзамин фасод мекарданд, на ислоҳ.'),
(3208, 27, 49, 'Гуфтанд; «Ба Худо савганд хӯред, ки ӯ ва касонаш ро шабона мекушем. Ва чун касе ба талаби хунаш бархезад, бигӯем: «Мо ба ҳангомн ҳалокати касони ӯ он ҷо набудаем ва мо ростгуфторем».'),
(3209, 27, 50, 'Ва ғофил буданд, ки агар онҳо ҳилае андешидаанд, Мо низ ҳилае андешидаем.'),
(3210, 27, 51, 'Бингар, ки оқибати макрашон чӣ шуд: Мо онҳо ва қавмашонро ба тамомӣ ҳалок кардем.'),
(3211, 27, 52, 'Он хонаҳои онҳост, ки ба ҷазои зулме, ки мекарданд, холӣ афтодааст. Дар он доноёнро ибратест.'),
(3212, 27, 53, 'Онҳоеро, ки имон оварда буданд ва парҳезгор буданд, наҷот додем.'),
(3213, 27, 54, 'Ва Лутро ба ёд ор. Он гоҳ ки ба қавми худ гуфт: «Корҳои зишт мекунед, дар ҳоле, ки худ ба зиштии он огоҳед?'),
(3214, 27, 55, 'Чаро аз рӯи шаҳват ба ҷои занон ба мардон шаҳват меронед? Шумо мардуме нодон ҳастед!»'),
(3215, 27, 56, 'Ҷавоби қавми ӯ ин буд, ки гуфтанд: «Хонадони Лутро аз деҳи худ берун кунед. Онон даъвои покӣ мекунанд» (Пораи 20).'),
(3216, 27, 57, 'Ӯ ва касонаш, ғайри занашро, наҷот додем. Чунон хостем, ки он зан аз бозмондагон бошад.'),
(3217, 27, 58, 'Бороне бар онҳо боронидем ва борони таҳдидшудагон чӣ бад боронест!'),
(3218, 27, 59, 'Бигӯ; «Шукр Оллоҳро ва салом бар бандагони интихобкардаи Ӯ. Оё Оллоҳ беҳтар аст ё он чизҳо, ки шарики Ӯ қарор медиҳанд?'),
(3219, 27, 60, 'Ё он касе, ки осмонҳову заминро офарид ва аз осмон бароятон об фиристод ва бо он бӯстонҳое хушманзар рӯёнидем, ки шуморо тавони рӯёнидани дарахте аз он нест. Оё бо вуҷуди Оллоҳ худои дигаре ҳаст? На, онҳо мардуме ҳастанд аз ҳақ қаҷрав!'),
(3220, 27, 61, 'Ё он ки заминро оромгоҳ сохт ва дар он рӯдҳо пайдо кард, кӯҳҳо ва миёни ду даре монеъе қарор дод, Оё бо вуҷуди Оллоҳ худои дигаре ҳаст? На, бештаринашон намедонанд!'),
(3221, 27, 62, 'Ё он ки дармондаро чун бихонадаш, посух медиҳад ва ранҷ аз ӯ дур мекунад ва шуморо дар замин ҷонишини пешиниён месозад. Оё бо вуҷуди Оллоҳ худои дигаре ҳаст? Чӣ андак панд мегиред!'),
(3222, 27, 63, 'Ё он ки шуморо дар торикиҳои биёбону дарё роҳ менамояд ва он кӣ пеш аз борони раҳматаш бодҳоро ба мужда мефиристад. Оё бо вуҷуди Оллоҳ худои дигаре ҳаст? Оллоҳ болотар аст аз ҳар чӣ шарики Ӯ месозанд!'),
(3223, 27, 64, 'Ё он ки махлуқотро меофаринад, сипас онҳоро бозмегардонад ва он, ки аз осмону замин ба шумо рӯзӣ медиҳад. Оё бо вуҷуди Оллоҳ худои дигаре ҳаст? Бигӯ; Агар рост мегӯед: далели худ биёваред!»'),
(3224, 27, 65, 'Бигӯ: «Ҳеҷ кас дар осмонҳову замин ғайбро намедонад, магар Оллоҳ. Ва низ намедонанд чӣ вақт зинда мешаванд!»'),
(3225, 27, 66, 'Дар боби охират ба яқин расиданд? На! Ҳамчунон дар шак ҳастанд, балки мардуме нобиноянд.'),
(3226, 27, 67, 'Кофирон гуфтанд: «Чӣ гуна агар мову падаронамон хок шудем, моро аз қабр берун меоваранд?'),
(3227, 27, 68, 'Ба мо ва падаронамон ҳам пеш аз ин чунин ваъдае дода буданд. Инҳо чизе ғайри афсонаҳои пешиниён нест!»'),
(3228, 27, 69, 'Бигӯ: «Дар замин сайр кунед ва бингаред, ки поёни кори гунаҳкорон чӣ гуна будааст!»'),
(3229, 27, 70, 'Барояшон андӯҳгин мабош ва аз ҳилае, ки меандешанд, тангдил машав.'),
(3230, 27, 71, 'Мегӯянд: «Агар рост мегӯед, он ваъдаи қиёмат чӣ вақт хоҳад буд?»'),
(3231, 27, 72, 'Бигӯ: «Шояд баъзе аз он чӣ ба шитоб металабед, наздики шумо бошад».'),
(3232, 27, 73, 'Ва Парвардигори ту фазли худро бар мардум ато мекунад, вале бештаринашон шукр намегӯянд!'),
(3233, 27, 74, 'Ва Парвардигори ту медонад, ки чӣ чизро дар дил ниҳон кардаанд ва чӣ чизро ошкор месозанд!'),
(3234, 27, 75, 'Ва дар осмонҳову замин ҳеҷ чизи махфӣ нест, ки он дар китоби мубин наомада бошад.'),
(3235, 27, 76, 'Ин Қуръон бисёре аз чизҳоеро, ки банӣ-Исроил дар он ихтилоф доранд, барояшон ҳикоят мекунад.'),
(3236, 27, 77, 'Ва барои мӯъмимон ҳидояту раҳмат аст.'),
(3237, 27, 78, 'Албатта Парвардигори ту ба раъйи худ миёнашон ҳукм хоҳад кард, ки Ӯ ғолибу доност!'),
(3238, 27, 79, 'Пас ба Худо таваккал кун, ки ту ҳамроҳ бо ҳақиқате равшан ҳастӣ!'),
(3239, 27, 80, 'Ту наметавонӣ мурдагокро шунаво созӣ ва овози худро ба гӯши кароне, ки аз ту рӯй мегардонанд. Бирасонӣ.'),
(3240, 27, 81, 'Ту наметавонӣ кӯронро аз гумроҳияшон роҳ намоӣ. Овози худро танҳо ба гӯши касоне тавонӣ расонд, ки ба оёти Мо имон овардаанд ва мусалмон ҳастанд.'),
(3241, 27, 82, 'Чун фармони қиёмат муқаррар гардад, барояшон ҷунбандае аз замин берун меоварем, ки бо онон сухан бигӯяд, ки ин мардум ба оёти Мо яқин намеоварданд.'),
(3242, 27, 83, 'Рӯзе, ки аз ҳар миллате гурӯҳеро аз онон, ки оёти Моро дурӯғ мебароварданд, ҷамъ меоварем ва ононро ба саф биронанд,'),
(3243, 27, 84, 'то чун ба маҳшар бирасанд, гӯяд: «Оё шумо оёти Маро дурӯғ мебаровардед ва ҳол он, ки дониши шумо ба он иҳота наёфта буд? Он чӣ коре буд, ки мекардед?»'),
(3244, 27, 85, 'Ба ҷазои ситаме, ки мекарданд, ҳукми азоб бар онҳо муқаррар шавад, бе он, ки ҳеҷ сухане бигӯянд.'),
(3245, 27, 86, 'Оё намебинанд, ки шабро падид овардем, то дар он биёроманд ва рӯзро равшанӣ бахшидем. Албатта дар ин барои мӯъминон ибратҳост.'),
(3246, 27, 87, 'Рӯзе, ки дар сур дамида шавад ва ҳамаи касоне, ки дар осмонҳову замин ҳастанд, ғайри он кас ки Худо бихоҳад, битарсанд ва ҳама хору залил ба сӯи Ӯ равон шаванд.'),
(3247, 27, 88, 'Ва кӯҳҳоро бинӣ, пиндорӣ, ки беҷонанд, ҳол он, ки ба суръати абр мераванд. Кори Худованд, аст, ки ҳар чизеро ба камол халқ кардааст. Албатта Ӯ ба ҳар чӣ мекунед, огоҳ аст.'),
(3248, 27, 89, 'Ҳар кас, ки кори неке кунад, беҳтар аз онро мукофот гирад ва некӯкорон аз ваҳшати он рӯз дар амон бошанд.'),
(3249, 27, 90, 'Ва ононро, ки корҳои бад мекунанд, сарнагун дар оташ андозанд, оё ҷуз мувофиқи корҳое, ки кардаед, ҷазо ёбед?'),
(3250, 27, 91, 'Ба ман фармон шуда, ки Парвардигори ин шаҳрро бипарастам. Шаҳре, ки Худованде, ки ҳама чиз аз они Ӯст, ҳурматаш ниҳода ва амр шудаам, ки аз мусалмонон бошам.'),
(3251, 27, 92, 'Ва Қуръонро тиловат кунам. Пас ҳар ки ҳидоят ёфт, ба нафъи худ ҳидоят ёфтааст ва ҳар кӣ гумроҳ шуд, бигӯ: «Ман бимдиҳандае ҳастам».'),
(3252, 27, 93, 'Ва бигӯ: «Шукр Худорост. Оёти худро ба шумо нишон медиҳад, то онҳоро бишиносед. Ва Парвардигори ту аз ҳеҷ коре, ки мекунед, бехабар нест».');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'То, син, мим.'),
(3254, 28, 2, 'Ин аст оятҳои ин китоби равшангар.'),
(3255, 28, 3, 'Барои онҳо, ки имон меоваранд, достони ростини Мӯсо ва Фиръавнро бар ту мехонем.'),
(3256, 28, 4, 'Фиръавн дар он сарзамин бартарӣ ҷуст ва мардумашро фирқа-фирқа сохт. Фирқаеро хор медошт ва писаронашонро мекушт ва занонашонро зинда мегузошт, ки ӯ аз фасодкорон буд.'),
(3257, 28, 5, 'Ва Мо бар он ҳастем, ки бар мустазъафони (заъифони) рӯи замин неъмат диҳем ва ононро пешвоён созем ва ворисон гардонем.'),
(3258, 28, 6, 'Ва онҳоро дар он сарзамин обрӯву қудрат бахшидем ва ба Фиръавну Ҳомон ва лашкариёнашон чизеро, ки аз он метарсиданд, нишон диҳем.'),
(3259, 28, 7, 'Ва ба модари Мӯсо ваҳй кардем, ки шираш бидеҳ ва агар бар ӯ бимнок шудӣ ӯро ба дарё бияндоз ва матарс ва ғамгин машав, ӯро ба ту бозмегардонем ва дар шумори паёмбаронаш меоварем.'),
(3260, 28, 8, 'Хонадони Фиръавн ӯро ёфтанд, то душманонашон ва сабаби андӯҳашон гардад, Фиръавн ва Ҳомон ва лашкариёнашон хато мекарданд.'),
(3261, 28, 9, 'Зани Фиръавн гуфт: «Ин сабаби шодмонии ману туст. Ӯро накушед, шояд ба мо нафъе бирасонад, ё ӯро ба фарзандӣ гирем». Ва намедонистанд, ки чӣ мекунанд.'),
(3262, 28, 10, 'Дили модари Мӯсо танг шуд. Ва агар дилашро қавӣ накарда будем, то аз мӯъминон бошад, наздик буд, ки он сирро фош созад.'),
(3263, 28, 11, 'Ва ба хоҳари ӯ гуфт: «Аз паи ӯ бирав». Ва зан бе он ки онҳо фаҳманд, аз дур дар ӯ менигарист.'),
(3264, 28, 12, 'Пистони ҳамаи доягонро аз пеш бар ӯ ҳаром карда будем. Он зан гуфт: «Оё мехоҳед шуморо ба хонаводае роҳнамоӣ кунам, ки ӯро бароятон нигоҳ доранд ва некхоҳаш бошанд?»'),
(3265, 28, 13, 'Пас ӯро назди модараш баргардонидем, то чашмони он зан равшан гардад ва ғамгин набошад ва бидонад, ки ваъдаи Худо ҳақ аст, вале бештаринашон намедонанд!'),
(3266, 28, 14, 'Чун ба ҳадди булуғ расид ва боқувват шуд, ӯро ҳикмату дониш додем ва некӯкоронро чунии мукофот медиҳем.'),
(3267, 28, 15, 'Бехабар аз мардуми шаҳр ба шаҳр дохил шуд. Ду танро дид, ки бо ҳам ҷанг мекунанд. Ин як аз пайравонаш буд ва он як аз душманонаш. Он кӣ аз пайравонаш буд, бар зидди он дигар, ки аз душманонаш буд, аз ӯ ёрӣ хост. Мӯсо муште бар ӯ зад ва ӯро кушт. Гуфт: «Ин кори шайтон буд. Ӯ ба ошкоро душмане гумроҳкунанда аст».'),
(3268, 28, 16, 'Гуфт: «Эй Парвардигори ман, ман ба худ ситам кардам. Маро бибахшой». Ва Худояш бибахшид. Зеро бахшояндаву меҳрубон аст!'),
(3269, 28, 17, 'Гуфт: «Эй Парвардигори ман, ба шукри неъмате, ки бар ман ато кардӣ, ҳаргиз пуштибони гунаҳкорон нахоҳам шуд!»'),
(3270, 28, 18, 'Дигар рӯз дар шаҳр тарсон ва чашм бар роҳи ҳодиса мегардид. Марде, ки дирӯз аз ӯ мадад хоста буд, боз ҳам аз ӯ мадад хост. Мӯсо ба ӯ гуфт: «Ту ба ошкоро гумроҳ ҳастӣ!»'),
(3271, 28, 19, 'Чун хост мардеро, ки душмани ҳар дуи онҳо буд, бизанад, гуфт: «Эй Мӯсо, оё мехоҳӣ ҳамчунон ки дирӯз якеро куштӣ, маро низ бикушӣ? Ту мехоҳи, ки дар ин сарзамин золиме боши ва намехоҳи, ки аз муслиҳон бошӣ».'),
(3272, 28, 20, 'Марде аз дурдасти шаҳр давон омаду гуфт: «Эй Мӯсо, сардорони шаҳр дар бораи ту машварат мекунанд то бикушандат. Берун бирав. Ман хайрхоҳи ту ҳастам!»'),
(3273, 28, 21, 'Тарсону нигарон аз шаҳр берун шуд. Гуфт: «Эй Парвардигори ман, маро аз ситамкорон наҷот деҳ!»'),
(3274, 28, 22, 'Чун ба ҷониби Мадян равон шуд, гуфт: «Шояд Парвардигори ман маро ба роҳи рост раҳбарӣ кунад».'),
(3275, 28, 23, 'Чун ба оби Мадян расид, гурӯҳе аз мардумро дид, ки чорпоёни худро об медиҳанд ва пушти сарашон ду занро дид, ки гӯсфандони худро бозмеронанд, Гуфт: «Шумо чӣ мекунед?» Гуфтанд: «Мо об намедиҳем, то он гоҳ, ки чӯпонон бозгарданд, ки падари мо пире бузургвор аст».'),
(3276, 28, 24, 'Гӯсфандонро об дод. Сипас ба соя бозгашту гуфт: «Эй Парвардигори ман, ман ба он неъмате, ки бароям мефиристӣ, мӯҳтоҷам».'),
(3277, 28, 25, 'Яке аз он ду зан, ки ба озарм (шарм) роҳ мерафт, назди ӯ омад ва гуфт: «Падарам туро мехонад, то музди об доданатро бидиҳад». Чун назди ӯ омад ва саргузашти худ бигуфт, гуфт: «Матарс, ки ту аз мардуми золим наҷот ёфтаӣ».'),
(3278, 28, 26, 'Яке аз он ду гуфт: «Эй падар, ӯро мардикор гир, ки агар чунин кунӣ, беҳтарин марди нерӯманди боваринокест, ки мардикор кардаӣ».'),
(3279, 28, 27, 'Гуфт: «Мехоҳам яке аз ин ду духтарамро зани ту кунам, ба шарти он, ки ҳашт сол мардикори ман бошӣ. Ва агар даҳ солро пурра кунӣ, худ хидматест ва ман намехоҳам, ки туро ба машаққат афканам. Иншооллоҳ, маро аз солеҳон хоҳй ёфт».'),
(3280, 28, 28, 'Гуфт: «Ин аст паймон миёни ману ту. Ҳар як аз ду муддатро, ки тамом кунам, бар ман ситаме, нахоҳад рафт ва Худо бар он чӣ мегӯем, вакил аст».'),
(3281, 28, 29, 'Чун Мӯсо муддатро тамом кард ва бо занаш равон шуд, аз сӯи Тур оташе дид. Ба касони худ гуфт: «Ин ҷо истед, Оташе дидам. Шояд аз он хабаре ё пораи оташе биёварам, то гарм шавед».'),
(3282, 28, 30, 'Чун назди оташ омад, аз канораи рости водӣ дар он сарзамини муборак аз он дарахт садо дода шуд, ки эй Мӯсо, ман Худои якто, Парвардигори ҷаҳониёнам.'),
(3283, 28, 31, 'Асоятро бипарто! Чун дидаш, ки монанди море меҷунбад, гурезон бозгашт ва ба ақиб нанигарист. «Эй Мӯсо, пеш ою матарс. Ту дар амон ҳастӣ.'),
(3284, 28, 32, 'Дасти худ ба гиребон бибар то берун ояд сафед бе ҳеҷ осебе. Ва то аз ваҳшат биёромӣ, дасти худ дар бағал кун. Ин ду аз ҷониби Парвардигорат ҳуҷҷатҳои ту барои Фиръавн ва атрофиёни ӯст, ки онон мардуме нофармонанд».'),
(3285, 28, 33, 'Гуфт: «Эй Парвардигори ман, яке аз онҳоро куштаам ва метарсам маро бикушанд.'),
(3286, 28, 34, 'Ва бародарам Ҳорун ба забон аз ман фасеҳтар аст. Ӯро ба мадади ман бифирист, то маро тасдиқ кунад, ки бими он дорам, ки дурӯғгӯям шуморанд».'),
(3287, 28, 35, 'Гуфт: «Туро бо бародарат қавидаст хоҳем кард ва бароятон ҳуҷҷате қарор медиҳем. Ба сабаби нишонаҳое, ки шуморо додаем, ба шумо даст нахоҳанд ёфт. Шумо ва пайравонатон ғолиб хоҳед буд».'),
(3288, 28, 36, 'Чун Мӯсо оёти равшани Моро наздашон овард, гуфтанд; «Ин ҷодуест сохта, мо аз ниёгонамон чунин чизҳое нашунидаем».'),
(3289, 28, 37, 'Ва Мӯсо гуфт: «Парвардигори ман ба он касе, ки аз ҷониби Ӯ ба ҳидоят омадааст ва оқибати писандида дар он дунё аз они ӯст, огоҳтар аст. Зеро ситамкорон растагор (наҷотёфта) намешаванд!»'),
(3290, 28, 38, 'Фиръавн гуфт: «Эй ҷамоат, ман барои шумо худое ғайри худ намешиносам. Эй Ҳомон, барои ман оҷур (хишт) бипаз ва тораме (гунбаде) баланд бисоз, шояд худои Мӯсоро бубинам, ки дурӯғгӯяш пиндорам».'),
(3291, 28, 39, 'Ӯ ва лашкарҳояш ба ноҳақ дар замин саркашӣ карданд ва пиндоштанд, ки ба назди мо бозгардонида намешаванд.'),
(3292, 28, 40, 'Пас ӯ ва лашкарҳояшро гирифтем ва ба дарё партофтем. Бингар, ки оқибати кори ситамкорон чӣ гуна буд.'),
(3293, 28, 41, 'Ва ононро аз он гуна пешвоёне сохтем, ки мардумро ба оташ даъват мекунанд ва дар рӯзи қиёмат касе ёриашон накунад.'),
(3294, 28, 42, 'Дар ин дунё аз паяшон лаънат равона сохтем ва дар рӯзи қиёмат аз зиштрӯёнанд.'),
(3295, 28, 43, 'Пас аз он, ки мардуми рӯзгори пешинро ҳалок сохтем, ба Мӯсо китоб додем, то мардумро ибрату ҳидоят ва раҳмат бошад. Шояд панд гиранд.'),
(3296, 28, 44, 'Он гоҳ ки ба Мӯсо фармони амри пайғамбариро ваҳй кардем, ту на дар ҷониби ғарбии Тур будӣ ва на аз ҳозирон.'),
(3297, 28, 45, 'Вале Мо аз он пас наслҳоеро биёфаридем, ки умрашон ба дароз кашид. Ва ту дар миёни мардуми Мадян муқим набудӣ, ки оёти Моро бар онҳо бихонӣ. Вале Мо будем, ки паёмбароне мефиристодем.'),
(3298, 28, 46, 'Ту дар канори Тур набудӣ, он гоҳ ки Мӯсоро нидо кардем. Вале ин раҳматест аз ҷониби Парвардигорат то мардумеро, ки пеш аз ту бимдиҳандае надоштанд, бим диҳй. Бошад, ки пандпазир шаванд.'),
(3299, 28, 47, 'То чун ба сабаби амалҳое, ки кардаанд, мусибате ба онҳо расад, нагӯянд: «Эй Парвардигори мо, чаро паёмбаре бар мо нафиристодӣ, то аз оёти Ту пайравӣ кунем ва аз мӯъминон бошем?»'),
(3300, 28, 48, 'Чун паёмбари ростгӯй аз ҷониби Мо бар онон фиристода шуд, гуфтанд: «Чаро он чӣ ба Мӯсо дода шуда, ба ӯ дода нашуда?» Оё инҳо пеш аз ин ба он чӣ ба Мӯсо дода шуда буд, кофир нашуда буданд? Ва гуфтанд, ки ин ҳар ду, ду ҷодуст монанди ҳам ва мо ба ҳеҷ як имон намеоварем.'),
(3301, 28, 49, 'Бигӯ: «Агар рост мегӯед, аз ҷониби Худованд китобе биёваред, ки аз ин ду беҳтар роҳ бинамояд, то ман ҳам аз он пайравӣ кунам!»'),
(3302, 28, 50, 'Пас агар иҷобатат накарданд, бидон, ки аз паи ҳавои нафси худ мераванд ва кист гумроҳтар аз он кас, ки бе он, ки роҳнамоӣ аз Худо хоҳад, аз паи ҳавои нафси худ равад? Зеро Худо мардуми золимро ҳидоят намекунад.'),
(3303, 28, 51, 'Барояшон сухан дар сухан пайвастем, бошад, ки пандпазир гарданд.'),
(3304, 28, 52, 'Касоне, ки пеш аз ин китоб китобашон дода будем, ба он имон меоваранд.'),
(3305, 28, 53, 'Ва чун бар онон тиловат шуд, гуфтанд: «Ба он имон овардем, Ҳаққест аз ҷониби Парвардигори мо ва мо пеш аз он таслим будаем».'),
(3306, 28, 54, 'Инон ба сабаби сабре, ки кардаанд, ду бор подош дода шаванд. Инон бадиро ба некӣ дур мекунанд ва низ аз он чӣ рӯзияшон додаем, садақа мекунанд.'),
(3307, 28, 55, 'Ва чун сухани беҳудае бишнаванд, аз он рӯйгардонӣ кунанд ва гӯянд: «Кирдорҳои мо аз они мо ва кирдорҳои шумо аз они шумо. Ба саломат бимонед. Мо хостори ҷоҳилон нестем!»'),
(3308, 28, 56, 'Ту ҳар касро, ки бихоҳӣ, ҳидоят намекунӣ. Худост, ки ҳар киро бихоҳад, ҳидоят мекунад ва Ӯ ҳидоятёфтагонро беҳтар мешиносад.'),
(3309, 28, 57, 'Гуфтанд: «Агар аз дини ту пайравӣ кунем, моро аз сарзаминамон бармекананд». Оё онҳоро дар ҳараме амн ҷой на додаем, ки ҳама гуна меваҳо дар он фароҳам мешавад ва ин ризқест аз ҷониби Мо? Вале бештаринашон намедонанд.'),
(3310, 28, 58, 'Чӣ басо мардуми деҳаеро ҳалок сохтем, ки аз зиндагии худ дучори сармастӣ шуда буданд ва ин хонаҳои онҳост, ки баъд аз онҳо андак замоне кас дар онҳо сукно (маскан) гирифт ва Мо вориси онҳо будем.'),
(3311, 28, 59, 'Парвардигори ту мардуми деҳаҳоро ҳалок накард, то он гоҳ, ки аз худашон паёмбаре бар онҳо фиристод ва ӯ оёти Моро бар онҳо бихонд. Ва мо деҳаҳоро нобуд кардаем, ба сабаби он, ки мардумаш ситамкор будаанд.'),
(3312, 28, 60, 'Чизҳое, ки ба шумо дода шудааст, матоъест ва ороиши ин зиндагии дунявист. Ҳол он ки он чӣ назди Худост, беҳтару монданитар аст. Чаро ақлатонро кор намефармоед?'),
(3313, 28, 61, 'Оё он кас, ки ӯро ваъдаҳои нек додаем ва он ваъдаҳоро хоҳад дид, монанди касест, ки ӯро аз матоъи инҷаҳонӣ баҳраманд кардаем ва дар рӯзи қиёмат ҳам аз ҳозир карда шудагон аст?'),
(3314, 28, 62, 'Ва рӯзе, ки ононро нидо медиҳад ва мегӯяд: «Шариконеро, ки барои Ман мепиндоштаед, куҷоянд?»'),
(3315, 28, 63, 'Онон, ки ҳукм дар бораашон таҳқиқ шуда мегӯянд: «Эй Парвардигори мо, инҳо касоне, ҳастанд, ки мо гумроҳашон кардем. Аз он рӯ гумроҳашон кардем, ки худ гумроҳ будем. Аз онҳо безорӣ меҷӯем ва ба ту мегаравем. Инҳо моро намепарастидаанд».'),
(3316, 28, 64, 'Ва гуфта шавад: «Акнун бутонатонро фарёд кунед». Фарёд мекунанд, вале ба онҳо ҷавоб намедиҳанд. Ва чун, азобро бубинанд, орзу кунанд, ки кош аз ҳидоятёфтагон мебуданд.'),
(3317, 28, 65, 'Ва рӯзе, ки Худо нидояшон кунад ва гӯяд: «Паёмбаронро чӣ гуна қабул кардед?»'),
(3318, 28, 66, 'Дар ин рӯз хабарҳо бар онон пӯшида шавад ва аз якдигар низ натавонанд пурсид.'),
(3319, 28, 67, 'Аммо он кас, ки тавба карда ва имон оварда ва амали солеҳ ба ҷой оварда, шояд, ки аз наҷотёфтагон бошад.'),
(3320, 28, 68, 'Парвардигори ту ҳар чиро, ки бихоҳад, меофаринад ва меихтиёрад. Вале онҳоро тавони ихтиёр нест. Пок аст Худо ва аз ҳар чӣ барояш шарик месозанд ва бартар аст.'),
(3321, 28, 69, 'Ва Парвардигори ту ҳар чиро, ки дар дил пинҳон доштаанд, ё ошкор кардаанд, медонад.'),
(3322, 28, 70, 'Ӯст Худои якто. Ҳеҷ худое ғайри Ӯ нест. Сано (шукр) хоси Ӯст, чӣ дар ин ҷаҳон ва чӣ дар ҷаҳони дигар. Ва фармон фармони Ӯст. Ва ҳамагон ба Ӯ бозгардонда мешавед.'),
(3323, 28, 71, 'Бигӯ: «Чӣ тасаввур мекунед, агар Оллоҳ шабатонро то рӯзи қиёмат тӯлонӣ созад? Ғайри Ӯ кадом худост, ки шуморо равшанӣ медиҳад? Магар намешунавед?»'),
(3324, 28, 72, 'Бигӯ: «Чӣ тасаввур мекунед, агар Оллоҳ рӯзатонро то рӯзи қиёмат тулонӣ созад? Ғайри Ӯ кадом худост, ки шуморо шаб медиҳад, ки дар он биёсоед? Магар намебинед?'),
(3325, 28, 73, 'Ва аз раҳмати Ӯ он кӣ барои шумо шабу рӯзро падид овард, то дар он як биёсоед ва дар ин як ба талаби рӯзи бархезед, бошад, ки шукр гӯед».'),
(3326, 28, 74, 'Ва рӯзе, ки Худо нидояшон кунад ва гӯяд: «Шариконе, ки барои Ман тасаввур мекардед, куҷоянд?'),
(3327, 28, 75, 'Ва аз ҳар миллате шоҳиде биёварем ва гӯем: «Далели худ биёваред. Он гоҳ бидонанд, ки ҳақ аз они Оллоҳ аст ва он бутон, ки ба дурӯғ худо мехонданд нобуд шаванд».'),
(3328, 28, 76, 'Қорун аз қавми Мӯсо буд, ки бар онҳо афзунӣ ҷуст. Ва ба ӯ чунон ганҷҳое додем, ки бардоштани калидҳояш бар гурӯҳе аз мардуми нерӯманд душвор менамуд. Он гоҳ, ки қавмаш ба ӯ гуфтанд: «Сармаст мабош, зеро Худо сармастонро дӯст надорад.'),
(3329, 28, 77, 'Дар он чи Худоят ато кардааст, ҷаҳони охиратро биҷӯй ва насиби худро аз дунё фаромӯш макун. Ва ҳамчунон ки Худо ба ту некӣ карда, некӣ кун ва дар замин аз паи фасод марав, ки Худо фасодкунандагонро дуст надорад!»'),
(3330, 28, 78, 'Гуфт: «Он чӣ ба ман дода шуда, ба сабаби дониши ман будааст». Оё надонистааст, ки Худо пеш аз ӯ наслҳоеро ҳалок карда, ки қувваташон аз ӯ афзунтар ва шуморашон бештар будааст? Ва ин гунаҳкоронро аз гуноҳашон намепурсанд.'),
(3331, 28, 79, 'Ороста ба зеварҳои худ ба миёни мардумаш омад. Онон, ки хостори зиндагии дунявӣ буданд, гуфтанд: «Эй кош, он чӣ ба Қорун дода шуда, моро низ мебуд, ки ӯ соҳиби насиби бузург аст».'),
(3332, 28, 80, 'Аммо донишёфтагон гуфтанд: «Вой бар шумо. Барои онҳо, ки имон меоваранд ва корҳои шоиста мекунанд, савоби Худо беҳтар аст. Ва ба ин савоб собирон расанд».'),
(3333, 28, 81, 'Пас ӯ ва хонаашро дар замин фурӯ бурдем ва дар баробари Худо ҳеҷ гурӯҳе надошт, ки ёриаш кунад ва худ ёрӣ кардани худро наметавонист.'),
(3334, 28, 82, 'Рӯзи дигар он касон, ки дирӯз орзӯ мекарданд, ки ба ҷои ӯ мебуданд, мегуфтанд: «Аҷабо, ки Худо рӯзии ҳар касро, ки хоҳад фаровон кунад ё танг созад. Агар Худо ба мо низ неъмати фаровон дода буд, моро низ дар замин фурӯ мебурд. Намебинӣ, ки кофирон наҷот намеёбанд?»'),
(3335, 28, 83, 'Ин ҷаҳони охиратро аз они касоне сохтаем, ки дар ин ҷаҳон на хоҳони такаббурӣ ҳастанд ва на хоҳони фасод. Ва саранҷоми нек аз они парҳезгорон аст.'),
(3336, 28, 84, 'Ҳар кас, ки кори неке ба ҷой орад, беҳтар аз он музд ёбад ва ҳар ки гуноҳе кунад, пас онҳо, ки гуноҳ кардаанд, ба андозаи амалашон ҷазо дода шаванд.'),
(3337, 28, 85, 'Он кас, ки Қуръонро бар ту фарз кардааст, туро ба ваъдагоҳат бозмегардонад. Бигӯ: «Парвардигори ман беҳтар медонад, ки чӣ касе бар роҳи рост аст ва чӣ касе дар гумроҳии ошкор аст».'),
(3338, 28, 86, 'Агар раҳмати Парвардигорат набуд, умеди онро надоштӣ, ки ин китоб бар ту дода шавад. Пас набояд пуштибони кофирон бошӣ!'),
(3339, 28, 87, 'Пас аз он, ки оятҳои Худо бар ту нозил шуд, туро аз он бероҳ накунанд. Мардумро ба сӯи Парвардигорат бихон ва аз мушрикон мабош.'),
(3340, 28, 88, 'Бо Худои якто худои дигареро махон. Ҳеҷ худое ғайрн Ӯ нест. Ҳар чизе нобуд шуданӣ аст ғайри зоти Ӯ. Фармон, фармони Ӯст ва ҳама ба назди ӯ бозгардонида шавед!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Алиф, лом, мим.'),
(3342, 29, 2, 'Оё мардум мепиндоранд, ки «Имон овардаем». мегӯянду, бе имтиҳону санҷиш гузошта мешаванд?'),
(3343, 29, 3, 'Албатта мардумеро, ки пеш аз онҳо буданд, озмудем, то Худо касонеро, ки рост гуфтаанд, маълум дорад ва дурӯғгӯёнро ҷудо кунад.'),
(3344, 29, 4, 'Оё онҳое, ки содиркунандаи гуноҳ мешаванд, пиндоштаанд, ки аз Мо мегурезанд? Чӣ бад ҳукм мекунанд!'),
(3345, 29, 5, 'Ҳар кас, ки ба дидори Худо ӯ мед медорад, бидонад, ки ваъдаи Худо омаданист ва Ӯ шунавову доност!'),
(3346, 29, 6, 'Ҳар кӣ чиҳод кунад, ба нафъи худ кардааст, зеро Худо аз ҳамаи ҷаҳониён беэҳтиёҷ аст.'),
(3347, 29, 7, 'Гуноҳони ононро, ки имон оварданд ва корҳои шоиста карданд, нест мекунем ва беҳтар аз он чӣ амал кардаанд, мукофоташон медиҳем.'),
(3348, 29, 8, 'Ба одами супориш кардем, ки ба падару модари худ некӣ кунад. Ва агар он ду бикӯшанд, ки ту чизеро, ки ба он огоҳ нести, шарики Ман қарор диҳӣ, итоъаташон макун. Бозгашти ҳамаи шумо ба сӯи Ман аст ва шуморо ба корҳое, ки мекардаед, огоҳ мекунам!'),
(3349, 29, 9, 'Касонеро, ки имон оварданд ва амалҳои шоиста карданд, дар гурӯҳи солеҳон даровардем.'),
(3350, 29, 10, 'Баъзе аз мардум мегӯянд: «Ба Худо имон овардаем. Ва чун дар роҳи Худо озоре бубинанд, он озорро чун азоби Худо ба шумор меоваранд. Ва чун аз сӯи Парвардигори ту мададе расад, мегӯянд: «Мо низ бо шумо будем». Оё Худо ба он чӣ дар дилҳои мардуми ҷаҳон мегузарад, огоҳтар нест?'),
(3351, 29, 11, 'Албатта, Худо медонад, ки мӯъминон чӣ касонанд ва мунофиқон чӣ касон.'),
(3352, 29, 12, 'Кофирон ба мӯъминон гуфтанд: «Ба роҳи мо биёед, бори гуноҳатон бар гардани мо. Ҳол он ки инҳо бори гуноҳи касеро бар гардан нахоҳанд гирифт ва дурӯғ мегӯянд.'),
(3353, 29, 13, 'Балки бори гуноҳи худ ва борҳое бо бори худ бар гардан хоҳанд гузошт ва дар рӯзи қиёмат ба сабаби дурӯғҳое, ки бар ҳам мебофтаанд, бозхост хоҳанд шуд.'),
(3354, 29, 14, 'Мо Нӯҳро бар мардумаш ба паёмбарӣ фиристодем. Ӯ ҳазор солу панҷоҳ сол кам дар миёни онон бизист. Ва чун мардуме золим буданд, тӯфонашон фурӯ гирифт.'),
(3355, 29, 15, 'Ӯ ва касонеро, ки дар киштӣ буданд, наҷот додем ва он тӯфонро нишони ибрате барои ҷаҳониён гардонидем.'),
(3356, 29, 16, 'Ва низ Иброҳимро ба паёмбарӣ фиристодем, он гоҳ ки ба мардумаш гуфт: «Худои якторо бипарастед ва аз Ӯ битарсед. Ин бароятон беҳтар аст, агар мардуме доно бошед.'),
(3357, 29, 17, 'Шумо ғайри Худои якто бутонеро мепарастед ва дурӯғҳои бузург мепардозед. Онҳое, ки ғайри Худои якто мепарастед, наметавонанд шуморо рузӣ диҳанд. Аз Худои якто рӯзӣ биҷӯед ва Ӯро ибодат кунед ва шукр гӯед, зеро ба сӯи Ӯ бозгардонида мешавед».'),
(3358, 29, 18, 'Агар шумо такзиб мекунед (дурӯғ мебароред), мардумоне ҳам, ки пеш аз шумо буданд, такзиб мекарданд. Ва бар ӯҳдаи паёмбар ғайри таблиғе равшангар чизи дигаре нест.'),
(3359, 29, 19, 'Оё намебинанд, ки Худо чӣ гуна махлуқро меофаринад ва пас аз нестӣ бозаш мегардонад? Ин кор бар Худо осон аст.'),
(3360, 29, 20, 'Бигӯ: «Дар замин сайр кунед ва бингаред, ки чӣ гуна Худо мавҷудотро офарида. Сипас офариниши охиринро падид меоварад. Зеро Худованд бар ҳар чизе тавоност!'),
(3361, 29, 21, 'Ҳар киро бихоҳад, азоб мекунад ва бар ҳар кӣ хоҳад, раҳмат меовард ва ҳам ба назди Ӯ бозгардонида мешавед.'),
(3362, 29, 22, 'Шумо наметавонед аз Худо бигурезед, на дар замин ва на дар осмон ва шуморо ғайри Ӯ ҳеҷ корсозу ёваре нест!»'),
(3363, 29, 23, 'Ва онон, ки ба оёти Худо ва дидори Ӯ имон надоранд, аз раҳмати ман ноумеданд ва онҳоро азобе дардовар аст.'),
(3364, 29, 24, 'Ҷавоби қавми ӯ ин буд, ки гуфтанд: «Ӯро бикушад ё бисӯзонед». Ва Худояш аз оташ наҷот дод. Ва дар ин барои мӯъминон ибратҳост.'),
(3365, 29, 25, 'Гуфт: «Шумо бутонеро ба ҷои Худои якто ба худоӣ гирифтаед то дар ин зиндагонии дунё миёнатон дӯстӣ бошад, вале дар рӯзи қиёмат мункири якдигар мешавед ва якдигарро лаънат мекунед ва ҷоятон дар оташ аст ва ҳеҷ ёваре надоред.'),
(3366, 29, 26, 'Лут ба Ӯ имон овард ва гуфт: «Ман ба сӯи Парвардигорам муҳоҷират (тарки ватан) мекунам, зеро Ӯ пирӯзманду ҳаким аст!»'),
(3367, 29, 27, 'Ва Исҳоқу Яъқубро ба ӯ бахшидем ва дар фарзандони ӯ паёмбариву китоб ниҳодем. Ва муздашонро дар дунё додем ва ӯ дар охират аз солеҳон аст.'),
(3368, 29, 28, 'Ва Лутро паёмбарӣ додем. Ба қавми худ гуфт: «Шумо коре зиштро пеша кардаед, ки ҳеҷ як аз мардуми ҷаҳон пеш аз шумо чунин намекардааст.'),
(3369, 29, 29, 'Оё бо мардон меомезед ва роҳзанӣ мекунед ва дар махфили худ корҳои нописанд мекунед?» Ҷавоби қавмаш ин буд, ки гуфтанд: «Агар рост мегӯӣ, азоби Худоро бар сари мо биёвар!»'),
(3370, 29, 30, 'Гуфт: «Эй Парвардигори ман, маро бар мардуми табахкор (расодкор) нусрат деҳ!»'),
(3371, 29, 31, 'Чун фиристодагони Мо Иброҳимро мужда оварданд, гуфтанд: «Мо мардуми ин деҳаро ҳалок хоҳем кард, ки мардуме золим ҳастанд».'),
(3372, 29, 32, 'Гуфт: «Лут дар он ҷост». Гуфтанд: «Мо беҳтар медонем, чӣ касе дар он ҷост. Ӯ ва хонадонашро, ғайри занашро, ки дар ҳамон ҷо хоҳад монд, наҷот медиҳем».'),
(3373, 29, 33, 'Чун фиристодагони Мо назди Лут омаданд, ғамгин шуд ва дар ғами корашон бимонд. Гуфтанд: «Матарс ва ғамгии мабош, туву хонадонатро, ғайри занатро, ки дар ҳамон ҷо хоҳад монд, наҷот медиҳем.'),
(3374, 29, 34, 'Мо ба мардуми ин деҳа ба сабаби фисқе, ки мекунанд, аз осмон азоб нозил мекунем».'),
(3375, 29, 35, 'Албатта аз он деҳа барои хирадмандон ибрате равшан бар ҷой гузоштем.'),
(3376, 29, 36, 'Ва бародарашон Шуъайбро ба Мадян фиристодем. Гуфт: «Эй қавми ман, Худоро бипарастед ва ба рӯзи қиёмат умедвор бошед ва дар замин ба табаҳкорӣ макӯшед.'),
(3377, 29, 37, 'Пас такзибаш карданд (дурӯғаш бароварданд) да зилзила ононро фурӯ гирифт ва дар хонаҳои худ бар ҷой мурданд.'),
(3378, 29, 38, 'Ва Оду Самудро ҳалок кардем. Ва ин аз масканҳояшон бароятон ошкор аст. Шайтон корҳояшонро дар назарашон биёрост ва ононро аз роҳ боздошт. Ва ҳол он ки мардуме соҳиби ақлу хуш буданд.'),
(3379, 29, 39, 'Ва низ Қоруну Фиръавн ва Ҳомонро, ки Мӯсо бо далелҳои равшани худ назди онҳо омад. Онҳо дар замин саркашӣ мекарданд, вале натавонистанд аз Худо бигурезанд.'),
(3380, 29, 40, 'Ҳамаро ба гуноҳашон фурӯ гирифтем: бар баъзе бодҳои регбор фиристодем, баъзеро фарёди сахт фурӯ гирифт, баъзеро дар замин фурӯ бурдем, баъзеро ғарқ сохтем. Ва Худо ба онҳо ситам намекард, онҳо худ ба худашон ситам карда буданд.'),
(3381, 29, 41, 'Мисоли онон, ки ғайри Худоро авлиё гирифтанд, мисли анкабут (тортанак) аст, ки хонае бисохт. Ва кош медонистанд, албатта сусттарин хонаҳо хонаи анкабут аст.'),
(3382, 29, 42, 'Худой якто медрнад, ки чӣ чизҳоро ба ҷои Ӯ ба худои мехонанд ва Ӯст ғолибу ҳаким!'),
(3383, 29, 43, 'Ин мисолҳоро барои мардум меорем ва онро фақат доноён дармеёбанд.'),
(3384, 29, 44, 'Худо осмонҳову заминро ба ростӣ биёфарид ва дар ин ибратест барои мӯъминон!'),
(3385, 29, 45, 'Ҳар чиро аз ин китоб бар ту ваҳй шудааст, тиловат кун. Ва намоз бигзор, ки намоз одамиро аз фаҳшову мункар бозмедорад ва зикри Худо бузургтар аст ва Худо ба корҳое, ки мекунед, огоҳ аст!'),
(3386, 29, 46, 'Бо аҳли китоб ба некӯтарин тарзе баҳс кунед. Магар бо онҳо, ки ситам пеша карданд. Ва бигӯед: «Ба он чӣ бар мо нозил шуда ва он чӣ бар шумо нозил шудааст, имон овардем. Ва Худои мову Худои шумо якест ва мо дар баробари Ӯ гардан ниҳодаем».'),
(3387, 29, 47, 'Ҳамчунон ин китобро бар ту нозил кардем ва аҳли китоб ба он имон меоваранд ва аз инҳо низ касоне ба он имон меоваранд ва ғайри кофирон касе оёти Моро инкор намекунад.'),
(3388, 29, 48, 'Ту пеш аз Қуръон ҳеҷ китоберо намехондӣ ва ба дасти худ китобе наменавиштӣ. Агар чунон буд, аҳли ботил ба шак афтода буданд.'),
(3389, 29, 49, 'Балки Қуръон оётест равшан, ки дар синаи аҳли дониш ҷой дорад. Ва оёти Моро ситамкорон инкор мекунанд.'),
(3390, 29, 50, 'Ва гуфтанд: «Чаро аз ҷониби Парвардигораш оёте бар ӯ нозил намешавад?» Бигӯ: «Оёт дар назди Худост ва ман бимдиҳандае ошкор ҳастам».'),
(3391, 29, 51, 'Оё онҳоро кифоя нест, ки бар ту китоб фиристодаем ва бар онҳо хонда мешавад. Дар ин китоб барои мӯъминон раҳмату панд аст!'),
(3392, 29, 52, 'Бигӯ: «Худо ба гувоҳӣ миёни ману шумо кофист. Он чиро, ки дар осмонҳову замин аст, медонад. Касоне, ки ба ботил гаравиданд ва ба Худо кофир шуданд, зиёнкоронанд».'),
(3393, 29, 53, 'Аз ту ба шитоб азобро металабанд. Агар онро замоне муъайян набуд, бар онҳо меомад. Ва ногаҳон ва бе он, ки хабар шаванд, бар онҳо фуруд хоҳад омад.'),
(3394, 29, 54, 'Аз ту ба шитоб азобро металабанд, ҳол он ки ҷаҳаннам бар кофирон иҳота дорад.'),
(3395, 29, 55, 'Рӯзе, ки азоб аз болои сару зери пой онҳоро дар бар гирад ва Худо бигӯяд: «Ба хотири амалҳое, ки мекардед, инак азобро бичашед!»'),
(3396, 29, 56, 'Эй бандагони ман, ки ба ман имон овардаед, замини ман васеъ аст, пас танҳо Маро бипарастед.'),
(3397, 29, 57, 'Ҳар касе чашандаи таъми марг аст. Ва ба сӯи Мо бозгардонида шавед.'),
(3398, 29, 58, 'Касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, дар ғурфаҳои биҳишт ҷой диҳем. Аз зери он ҷӯйҳои об равон аст. Дар он ҷо ҷовидона бимонанд. Амалкунандагонро чӣ некӯ муздест,'),
(3399, 29, 59, 'онҳо, ки сабр варзиданд ва бар Парвардигорашон таваккал мекунанд.'),
(3400, 29, 60, 'Чӣ бисёр ҷунбандагоне, ки тавони таҳсили рӯзии худ надоранд ва Худо онҳоро ва шуморо рӯзӣ медиҳад. Ва Ӯ шунавову доност!'),
(3401, 29, 61, 'Агар аз онҳо бипурсӣ: «Чӣ касе осмонҳову заминро офарида ва офтобу моҳро ром кардааст?» Хоҳанд гуфт: «Худои якто». Пас аз чӣ сабаб ақида дигаргун мекунанд?'),
(3402, 29, 62, 'Худо рӯзии ҳар як аз бандагонашро, ки бихоҳад, фаровон мекунад ё бар ӯ танг мегирад. Зеро Худо ба ҳама чиз доност!'),
(3403, 29, 63, 'Агар аз онҳо бипурсӣ: «Чӣ касе аз осмон борон фиристод ва замин мурдаро ба он зинда кард?» Хоҳанд гуфт: «Худо якто». Бигӯ; «Шукр Худорост!» Вале бештаринашон дарнамеёбанд!'),
(3404, 29, 64, 'Зиндагонии ин дунё лаҳву лаъаб (бозӣ) аст. Агар бидонанд, ҷахони охират ҷаҳони зиндагонист.'),
(3405, 29, 65, 'Чун ба киштӣ нишастанд, Худоро бо ихлос илтиҷову ибодат кунанд ва чун наҷоташон дод ва ба хушкӣ овард, ширк оваранд,'),
(3406, 29, 66, 'то неъматеро, ки ба онон ато карда будем, куфрон кунанд ва бархурдор шаванд. Ба зуди хоҳанд донист.'),
(3407, 29, 67, 'Оё надонистаанд, ки ҳарамро ҷои амни мардум қарор додем, ҳол он ки мардум дар атрофашон ба асорат гирифта мешаванд? Оё ба ботил имон меоваранд ва неъмати Худоро куфрон мекунанд?'),
(3408, 29, 68, 'Пас кист ситамкортар аз он, ки бар Худо дурӯғ мебандад ё ба ҳангоме ки ҳақ ба сӯи ӯ ояд, такзибаш мекунад (дурӯғ мешуморад)? Оё дар ҷаҳаннам ҷойгоҳе барои кофирон нест?'),
(3409, 29, 69, 'Касонеро, ки дар роҳи Мо ҷиҳод кунанд, ба роҳҳои худ ҳидояташон мекунем ва Худо бо некӯкорон аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Алиф, лом, мим.'),
(3411, 30, 2, 'Румиён мағлуб шуданд'),
(3412, 30, 3, 'дар наздики ин сарзамин ва пас аз мағлуб шудан бори дигар ғолиб хоҳанд шуд'),
(3413, 30, 4, 'дар муддати чанд сол. Фармон, фармони Худост, чӣ пеш аз пирӯзӣ ва чӣ баъд аз он. Ва дар он рӯз мӯъминон шодмон мешаванд'),
(3414, 30, 5, 'ба ёрии Худо. Худо ҳар касро, ки бихоҳад, ёрӣ мекунад, зеро ғолибу меҳрубон аст!'),
(3415, 30, 6, 'Худо ваъда додааст, ба ваъдаи худ хилоф накунад, вале бештари мардум намедонанд.'),
(3416, 30, 7, 'Онон ба зоҳири зиндагии дунё огоҳанд ва аз охират бехабаранд.'),
(3417, 30, 8, 'Оё бо худ намеандешанд, ки Худо осмонҳову заминро ва ҳар чӣ дар миёни онҳост, ба ҳақ ва то муддате маҳдуд, офаридааст? Ва бисёре аз мардум ба дидори Парвардигорашон имон надоранд.'),
(3418, 30, 9, 'Оё дар замин намегарданд, то бингаранд, ки чӣ гуна будааст оқибати касоне, ки пеш аз онҳо зистаанд? Касоне, ки тавонашон бештар будааст ва заминро ба шудгор задан зеру рӯ карда ва бештар аз инҳо ободаш сохта буданд ва паёмбароне бо мӯъҷизаҳо бар онҳо фиристода шуда буд. Худо ба онҳо зулм намекард, онон худ ба худашон зулм мекарданд.'),
(3419, 30, 10, 'Сипас оқибати он касон, ки корҳои бад карданд, ногувортар буд. Зеро инҳо оёти Худоро дурӯғ бароварданд ва онҳоро ба масхара гирифтанд.'),
(3420, 30, 11, 'Худо мавҷудотро меофаринад, он гоҳ дигар бора бозмегардонад, он гоҳ ҳама ба сӯи Ӯ бозгардонда мешавед.'),
(3421, 30, 12, 'Рӯзе, ки қиёмат барпо шавад, гуноҳкорон ҳайратзада бимонанд.'),
(3422, 30, 13, 'Онҳоро аз миёни бутонашон шафеъоне (наҷотдиҳандае) нест. Ва дигар ба бутони худ бовар надоранд.'),
(3423, 30, 14, 'Ва чун қиёмат барпо шавад, дар он рӯз аз якдигар чудо шаванд (мӯъминон аз кофирон).'),
(3424, 30, 15, 'Аммо онҳо, ки имон овардаанд ва корҳои шоиста кардаанд, дар ҷаннат ба шодмонӣ бархӯрдоранд.'),
(3425, 30, 16, 'Ва аммо онҳое, ки кофир шудаанд ва оёти Моро дурӯғ мебароранд ва дидори охиратро дурӯғ мепиндоранд, ҳамаро дар азоб ҳозир оранд.'),
(3426, 30, 17, 'Худоро биситоед, ба он ҳангом ки ба шаб дармеоед ва ба он ҳангом ки ба субҳ дармеоед.'),
(3427, 30, 18, 'Шукр Ӯрост дар осмонҳову замин, ба ҳангоми шаб ва ба ҳангоме ки ба нимрӯз мерасед.'),
(3428, 30, 19, 'Зиндаро аз мурда берун орад ва мурдаро аз зинда. Ва заминро пас аз мурданаш зинда месозад ва шумо низ инчунин аз гӯрҳо берун мешавед.'),
(3429, 30, 20, 'Аз нишонаҳои қудрати Ӯст, ки шуморо аз хок биёфарид то инсон шудед ва ба ҳар сӯ пароканда гаштед.'),
(3430, 30, 21, 'Ва аз нишонаҳои қудрати Ӯст, ки бароятон аз ҷинси худатон ҳамсароне офарид, то ба онҳо оромиш ёбед ва миёни шумо дӯстиву меҳрубонӣ ниҳод. Дар ин ибратҳоест барои мардуме, ки тафаккур мекунанд,'),
(3431, 30, 22, 'Ва аз нишонаҳои қудрати Ӯст офариниши осмонҳову замин ва гуногунии забонҳову рангҳоятон. Дар ин ибратҳоест барои доноён.'),
(3432, 30, 23, 'Ва аз нишонаҳои қудрати Ӯ хобидани шумост дар шаб ва рӯз ба талаби рӯзӣ бархостани шумост. Дар ин ибратҳоест барои мардуме, ки мешунаванд.'),
(3433, 30, 24, 'Ва аз нишонаҳои қудрати Ӯст, ки барқро ба қасди биму умед ба шумо менамоёнад. Ва аз осмон борон мефиристад то замини мурдаро ба он зинда созад. Дар ин ибратҳоест барои касоне, ки ба ақл дармеёбанд.'),
(3434, 30, 25, 'Ва аз нишонаҳои қудрати Ӯст, ки осмону замин ба фармони Ӯ барпой истодаанд. Сипас вақте, ки шуморо аз замин фаро мехонад, шумо аз замин берун меоед.'),
(3435, 30, 26, 'Аз они Ӯст ҳар чӣ дар осмонҳову замин аст ва ҳама фармонбардори Ӯ ҳастанд.'),
(3436, 30, 27, 'Ӯст, ки мавҷудотро меофаринад, сипас онҳоро бозмегардонад. Ва ин кор бар Ӯ осонтар аст. Ӯрост сифати бартарӣ дар осмонҳову замин ва Ӯст ғолибу ҳаким!'),
(3437, 30, 28, 'Барои шумо аз худатон мисоле меоварад: магар бандагонатон дар он чӣ ба шумо рӯзӣ додаем, бо шумо шарик ҳастанд, то дар мол бо шумо баробар бошанд ва ҳамчунон ки шумо озодон аз якдигар метарсед, аз онҳо ҳам бимнок бошед? Оётро барои мардуме, кн ақл мекунанд, ин тавр тафсил медиҳем.'),
(3438, 30, 29, 'Оре, ситамкорон бе ҳеҷ донише аз ҳавои нафси худ пайравӣ кардаанд. Онро, ки Худо гумроҳ кардааст, чӣ касе ҳидоят мекунад? Онҳоро ҳеҷ ёваре нест.'),
(3439, 30, 30, 'Ба яктопарастӣ рӯй ба дин овар. Фитратест, ки Худо ҳамаро ба он фитрат биёфаридааст ва дар офариниши Худо тағйире нест. Дини поку пойдор ин аст. Вале бештари мардум намедонанд!'),
(3440, 30, 31, 'Ба Ӯ бозгардед ва аз Ӯ битарсед ва намоз бигузоред ва аз мушрикон набошед.'),
(3441, 30, 32, 'Аз онон мабошед, ки дини худро пора-пора карданд ва фирқа-фирқа шуданд ва ҳар фирқае ба ҳар чӣ дошт, дилхуш буд.'),
(3442, 30, 33, 'Чун бар одамиён зиёне расад, Парвардигорашонро бихонанд ва ба даргоҳи Ӯ тавба кунанд ва чун раҳмати худ ба онҳо бичашонад, гурӯҳеро бинӣ, ки ба Парвардигорашон ширк меоваранд.'),
(3443, 30, 34, 'Пас неъматеро, ки ба онҳо додаем, ношукрӣ кунанд, Акнун мутаматтеъ (баҳраманд) шавед, ба зуди, ки хоҳед донист!'),
(3444, 30, 35, 'Оё барояшон далеле нозил кардем, то аз чизе, ки бо Худо шарик месозанд, сухан гуфта бошад?'),
(3445, 30, 36, 'Чун ба мардум раҳмате бичашонем, шодмон мешаванд ва чун ба сабаби корҳое, ки кардаанд, ранҷе ба онҳо расад, ногаҳон ноумед мегарданд!'),
(3446, 30, 37, 'Оё намебинанд, ки Худо рӯзии ҳар касро, ки бихоҳад, фаровон мекунад ё ӯро тангрӯзӣ месозад? Дар ин ибратҳоест барои мардуме, ки имон меоваранд!'),
(3447, 30, 38, 'Ҳаққи хешовандону мискин ва дар роҳ мондаро адо кун. Ин беҳтар аст барои касоне, ки хушнудии Худоро меҷӯянд ва онҳо наҷотёфтагонанд.'),
(3448, 30, 39, 'Моле, ки ба рибо медиҳед, то дар амволи мардум афзун шавад, назди Худо ҳеҷ афзун намешавад ва моле, ки барои хушнудии Худо аз бобати закот медиҳед, зиёд шавад, касоне ки чунин кунанд, мукофоти музоъаф (дучанд) доранд.'),
(3449, 30, 40, 'Худост, он ки шуморо биёфарид, сипас рӯзӣ дод, сипас мемиронад, сипас зинда мекунад. Оё касоне, ки шарики Худо месозед, ҳеҷ аз ин корҳо тавонанд? Пок аст Ӯ ва аз ҳар чӣ барояш шарик меоваранд, бартар (болотар) аст.'),
(3450, 30, 41, 'Ба сабаби амалҳои мардум фасод дар хушкиву дарё ошкор шуд, то ба онон ҷазои баъзе аз корҳояшонро бичашонад; шояд, ки бозгарданд.'),
(3451, 30, 42, 'Бигӯ: «Дар замин бигардед ва бингаред, ки оқибати пешиниён ки бештаринашон аз мушрикон буданд, чӣ гуна будааст».'),
(3452, 30, 43, 'Ба дини поки пойдор рӯй биёвар, пеш аз он ки рӯзе фаро расад, ки аз амри Худо бозгаштанаш набошад. Дар он рӯз мардум ду гурӯҳ шаванд:'),
(3453, 30, 44, 'касоне, ки кофир бошанд, куфрашон ба зиёнашон бошад ва онҳо, ки коре шоиста карда бошанд, барои худ подоше (мукофоти) некӯ омода кардаанд,'),
(3454, 30, 45, 'то касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, аз фазли худ музд диҳад. Зеро Худо кофиронро дӯст надорад!'),
(3455, 30, 46, 'Ва аз нишонаҳои қудрати Ӯ он, ки бодҳои муждадиҳандаро мефиристад, то раҳмати Худро ба шумо бичашонад ва то киштиҳо ба фармони Ӯ равон бошанд ва аз фазли Ӯ рӯзӣ биҷӯед. Шояд, ки шукр гӯед!'),
(3456, 30, 47, 'Ва пеш аз ту паёмбаронеро бар қавмашон фиристодем. Онон бо далелҳои равшани худ наздашон омаданд. Ва Мо аз касоне, ки гуноҳ карданд, интиқом гирифтем ва ёрӣ додани мӯъминон бар ӯҳдаи Мо буд.'),
(3457, 30, 48, 'Худост, ки бодҳоро мефиристад, то абрҳоро барангезад ва чунон ки хоҳад, бар осмон паҳн кунад ва онро пора-пора кунад ва бинӣ, ки борон аз даруни абрҳо берун меояд. Ва чун боронро ба ҳар кӣ хоҳад аз бандагонаш бирасонад, шодмон шаванд,'),
(3458, 30, 49, 'агарчи пеш аз он ки борон бар онҳо биборад, ноумед будаанд.'),
(3459, 30, 50, 'Пас ба осори раҳмати Худо бингар, ки чӣ гуна заминро пас аз мурданаш зинда мекунад. Чунин Худое зиндакунандаи мурдагон аст ва бар ҳар коре тавоност.'),
(3460, 30, 51, 'Ва агар боде бифиристем, ки киштаҳоро зард бубинанд аз он пас ҳама кофир шаванд.'),
(3461, 30, 52, 'Ту наметавонӣ мурдагонро шунаво созӣ ва агар инҳо низ аз ту бозгарданд, овози худро ба – гӯши он карон нарасонӣ.'),
(3462, 30, 53, 'Ту ҳидояткунандаи нобиноён аз гумроҳияшон нестӣ. Ту садоятро танҳо ба гӯши касоне мерасонӣ, ки ба оёти Мо имон овардаанд ва таслим шудаанд.'),
(3463, 30, 54, 'Худост, ки шуморо нотавон биёфарид ва пас аз нотавонӣ боқувват сохт, он гоҳ пас аз боқувватӣ нотавониву пирӣ овард. Ҳар чӣ бихоҳад, меофаринад ва Ӯ донову тавоност!'),
(3464, 30, 55, 'Рӯзе, ки қиёмат барпо шавад, кофирон савганд хӯранд, ки зиёда аз соаъте дар гӯр наоромидаанд. Оре, инчунин аз ҳақ каҷрав мешуданд.'),
(3465, 30, 56, 'Онон, ки донишу имон дода шудаанд, гӯянд: Шумо мувофиқи китоби Худо то рӯзи қиёмат дар гӯр орамидаед ва ин рӯзи қиёмат аст ва шумо намедонистаед».'),
(3466, 30, 57, 'Дар ин рӯз узрхоҳии ситамкорон ба ҳолашон нафъ надиҳад ва аз онҳо нахоҳанд, ки тавба кунанд,'),
(3467, 30, 58, 'Мо дар ин Қуръон барои мардум ҳар гуна масал овардаем. Ва чун ояе барояшон биёварӣ, кофирон хоҳанд гуфт: «Шумо беҳудагӯёне беш нестед!»'),
(3468, 30, 59, 'Инчунин Худо бар дилҳои касоне, ки аз доноӣ бебаҳраанд, мӯҳр мениҳад!'),
(3469, 30, 60, 'Пас сабр кун, ки ваъдаи Худо ҳақ аст. Мабод; онон, ки ба марҳалаи яқин нарасидаанд, туро бесуботу безобита гардонанд!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Алиф, лом, мим.'),
(3471, 31, 2, 'Инҳо ояҳои китоби ҳикматомез аст.'),
(3472, 31, 3, 'Некӯкоронро ҳидояту раҳматест:'),
(3473, 31, 4, 'онон, ки намоз мегузоранду закот медиҳанд ва ба охират яқин доранд.'),
(3474, 31, 5, 'Инҳо аз ҷониби Парвардигорашон ҳидоят ёфтаанд ва инҳо наҷотёфтагонанд.'),
(3475, 31, 6, 'Баъзе аз мардум харидори суханони беҳудаанд, то ба нодонӣ мардумро аз роҳи Худо гумроҳ кунанд ва Қуръонро ба масхара мегиранд. Насиби инҳо азобост хоркунанда.'),
(3476, 31, 7, 'Ва чун оёти Мо бар онҳо хонда шавад, бо худписандӣ рӯй баргардонанд, чунон ки гӯӣ нашунидаанд. Ё монанди касе, ки гӯшҳояш сангин (вазнин) шуда бошад. Ӯро ба азобе дардовар мужда деҳ!'),
(3477, 31, 8, 'Насиби онон, ки имон овардаанд ва корҳои шоиста кардаанд, боғҳои пурнеъмати биҳишт аст.'),
(3478, 31, 9, 'Дар он ҷо ҷовидонанд, ваъдаи Худо рост аст. Ӯ ғолибу ҳаким аст!'),
(3479, 31, 10, 'Осмонҳоро бе ҳеҷ сутуне, ки бубинед, биёфарид ва бар рӯи замин кӯҳҳоро бияфканд, то наларзонадатон ва аз ҳар гуна ҷунбандае дар он бипароканд. Ва аз осмон об фиристодем ва дар замин ҳар гуна гиёҳи некӯе рӯёнидем.'),
(3480, 31, 11, 'Ин офариниши Худост. Пас ба Ман нишон диҳед онҳо, ки ғайри Ӯ ҳастанд, чӣ офаридаанд. Балки золимони дар гумроҳии ошкоре ҳастанд.'),
(3481, 31, 12, 'Албатта ба Луқмон ҳикмат додему гуфтетм: «Худоро шукр гӯй, зеро ҳар кӣ шукр гӯяд, ба нафъи худ шукр гуфта ва ҳар кӣ ношукрӣ кунад, Худо бениёзу (беэҳтиёҷ) ситоиш карданист!»'),
(3482, 31, 13, 'Ва Луқмон ба писараш гуфт ва ӯро панд медод, ки эй писараки ман, ба Худо ширк маёвар, зеро ширк зулмест бузург.'),
(3483, 31, 14, 'Одамиро дар бораи падару модараш супориш кардем. Модараш ба ӯ томила шуд ва ҳар рӯз нотавонтар мешуд ва пас аз ду сол аз шираш бозгирифт. Ва супориш кардем, ки Марову падару модаратро шукр гӯй, ки бозгашти ту назди ман аст.'),
(3484, 31, 15, 'Агар он ду ба кӯшиш аз ту бихоҳанд, то чизеро, ки намедонӣ чист, бо Ман шарик гардонӣ, итоъаташон накун. Дар дунё бо онҳо ба тарифе писандида зиндагӣ кун ва худ роҳи касонеро, ки ба даргоҳи Ман бозмегарданд, дар пеш гир. Бозгашти ҳамаи шумо ба сӯи Ман аст ва Ман аз корҳое, ки мекардаед, огоҳатон мекунам.'),
(3485, 31, 16, 'Эй писараки ман! «Агар амал ба қадри як донаи хардал дар даруни харсанге ё дар осмонҳо ё дар замин, бошад, Худо ба ҳисобаш меоварад, ки Ӯ заррабину огоҳ аст!'),
(3486, 31, 17, 'Эй писараки ман! Намоз бигузор ва амр ба маъруфу наҳй аз мункар кун ва бар ҳар чӣ бар ту расад, сабр кун, ки ин аз корҳоест, ки набояд дар онҳо бепарвоӣ кард.'),
(3487, 31, 18, 'Ба такаббур аз мардум рӯй магардон ва ба худписандӣ бар замин роҳ марав, зеро Худо ҳеҷ такаббури фахрфурӯшеро дӯст надорад.'),
(3488, 31, 19, 'Дар рафторат роҳи миёнаро баргузин ва овозатро фуруд ор, яъне баланди беҳад накун, зеро нохуштарини овозҳо овози харон аст.'),
(3489, 31, 20, 'Оё надидаед, ки Худо ҳар чиро, ки дар осмонҳову замин аст, роми шумо кардааст ва неъматҳои худро чӣ ошкору чӣ пинҳон ба тамомӣ бар шумо ато кардааст? Ва баъзе аз мардум бе ҳеҷ донише ё роҳнамоеву китоби равшане дар бораи Худо ҷидол (баҳс) мекунанд.'),
(3490, 31, 21, 'Ва чун ба онҳо бигӯянд: «Ба он чӣ Худо нозил кардааст, пайравӣ кунед», гӯянд: «На! Мо аз дине, ки падарони худро бар он ёфтаем, пайравӣ мекунем». Агарчи шайтон ба азоби оташашон даъват карда бошад.'),
(3491, 31, 22, 'Ҳар кӣ рӯи худ ба Худо кунад ва некӯкор бошад, албатта ба дастгираи устуворе ҷанг задааст ва поёни ҳамаи корҳо ба сӯи Худост!'),
(3492, 31, 23, 'Он кас, ки кофир шудааст, куфраш туро ғамгин насозад. Бозгашташон назди Мост. Пас ба корҳое, ки кардаанд, огоҳашон мекунем. Зеро Худо ба он чӣ дар дилҳо мегузарад, огоҳ аст!'),
(3493, 31, 24, 'Андаке комёбашон месозем, сипас ба бечорагӣ ба азоби сахташон мекашонем.'),
(3494, 31, 25, 'Агар аз онҳо бипурсӣ: «Чӣ касе осмонҳову заминро офаридааст?» Хоҳанд гуфт: «Худо». Бигӯ: «Шукр Худоро!» Балки бештарашон нодонанд!'),
(3495, 31, 26, 'Аз они Худост он чӣ дар осмонҳову замин аст ва Худо бениёзу сутуданист! (шукр гуфтанист)'),
(3496, 31, 27, 'Ва агар ҳамаи дарахтони рӯи замин қалам шаванд ва даре мураккаб (ранг) ва ҳафт дарёи дигар ба мададаш биёяд, суханони Худо поён намеёбад. Ва Худо пирӯзманду ҳаким аст!'),
(3497, 31, 28, 'Офариниши ҳамаи шумо ва боз зинда карданатон танҳо монанди зинда кардани як тан аст Албатта Худо шунавову биност!'),
(3498, 31, 29, 'Оё надидаӣ, ки Худо аз шаб кам мекунаду ба рӯз меафзояд ва аз рӯз кам мекунаду ба шаб меафзояд ва офтобу моҳро роми худ сохт, ки ҳар як то замоне муъайян дар ҳаракат аст? Ва Худо ба корҳое, ки мекунед, огоҳ аст!'),
(3499, 31, 30, 'Ин ба он сабаб аст, ки Худои якто ҳақ аст ва ҳар чӣ ба ғайри Ӯ мехонанд, ботил аст ва Худост баландмартабаву бузургвор.'),
(3500, 31, 31, 'Оё надидаӣ, ки киштӣ ба неъмати Худо дар дарё равон мешавад, то Худо баъзе аз оёти қудрати худро ба шумо бинамоёнад? Дар ин барои мардуми сабркунандаи шукргузор ибратҳост!'),
(3501, 31, 32, 'Ва чун мавҷҳои азим монанди абрҳо онҳоро фурӯ пӯшад, Худоро аз рӯи ихлос дар дил бихонанд. Ва чун наҷоташон диҳад ва ба хушкӣ барад, баъзе ба аҳди худ вафо кунанд. Ва ҷуз ғаддорони (бевафоёни) кофири неъмат оёти Моро инкор накунанд.'),
(3502, 31, 33, 'Эй мардум, аз Парвардигоратон битарсед ва аз рӯзе, ки ҳеҷ падаре ҷазои фарзандро ба ӯҳда нагирад ва ҳеҷ фарзанде ҷазои падарро ӯҳдадор нашавад, битарсед. Ваъдаи Худо ҳақ аст. Зиндагии дунё фиребатон надиҳад ва низ шайтони фиребкор ба карами Худо фиребатон насозад.'),
(3503, 31, 34, 'Худост, ки медонад, ки қиёмат чӣ вақт меояд. Ӯст, ки борон меборонад ва аз он чӣ дар шикамҳои занҳои ҳомила аст, огоҳ аст. Ва ҳеҷ кас намедонад, ки фардо чӣ чиз ба даст хоҳад овард ва касе намедонад, ки дар кадом замин хоҳад мурд. Худо донову огоҳ аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Алиф, Лом, Мим.'),
(3505, 32, 2, 'Нозил шудани ин китоб, ки дар он ҳеҷ шакке нест, аз ҷониби Парвардигори ҷаҳониён аст.'),
(3506, 32, 3, 'Оё мегӯянд: «(Қуръонро) худаш бофтааст?» На, суханест барҳақ аз ҷониби Парвардигорат, то мардумеро, ки пеш аз ту бимдиҳандае надоштаанд, тарсонӣ. Шояд ба роҳи ҳидоят раванд.'),
(3507, 32, 4, 'Худост, ки осмонҳову заминро ва он чӣ миёни онҳост, дар шаш рӯз биёфарид ва он гоҳ ба арш пардохт. Шуморо ҷуз Ӯ корсозу шафеъе нест. Оё панд намегиред?'),
(3508, 32, 5, 'Корро аз осмон то замин сомон медиҳад. Сипас дар рӯзе, ки, миқдори он ҳазор сол аст, чунон ки мешуморед, ба сӯи Ӯ боло меравед.'),
(3509, 32, 6, 'Ӯст, ки донои ниҳону ошкор аст. Пирӯзманду меҳрубон аст.'),
(3510, 32, 7, 'Он кӣ ҳар чиро офарид, ба некӯтарин ваҷҳ офарид ва хилқати офаридани инсонро аз гил оғоз кард.'),
(3511, 32, 8, 'Сипас насли ӯро аз усораи (шираи) обе беқадр падид овард.'),
(3512, 32, 9, 'Он гоҳ, аъзои ӯ рост кард ва аз рӯҳи худ дар он бидамид. Ва бароятон гӯшу чашмҳо ва дилҳо офарид. Чӣ андак шукр мегӯед!'),
(3513, 32, 10, 'Ва гуфтанд: «Оё вақте ки дар замин нопадид шавем, офариниши тозае хоҳем ёфт?» Оре, онҳо ба дидор бо Парвардигорашон имон надоранд!'),
(3514, 32, 11, 'Бигӯ: «Фариштаи марг, ки муваккал (гумошта) бар шумост, шуморо мемиронад. Сипас ба сӯи Парвардигоратон бозгардонида мешавед».'),
(3515, 32, 12, 'Туро ибрат аст, он гоҳ ки гунаҳкоронро дар назди Парвардигорашон сарафканда бинӣ. Гӯянд: «Эй Парвардигори мо, дидем ва шунидем. Акнун моро бозгардон то коре шоиста кунем, ки инак ба яқин расидаем».'),
(3516, 32, 13, 'Агар мехостем, ҳидояти ҳар касро ба ӯ ато мекардем, вале ваъдаи Ман, ки ҷаҳаннамро аз ҳаман ҷинниёну одамиён пур мекунам, ҳақ аст.'),
(3517, 32, 14, 'Ба ҷазои он, ки дидори чунин рӯзеро фаромӯш карда будед, акнун бичашед. Мо низ шуморо аз ёд бурдаем. Ба сазои корҳое, ки мекардаед, азоби ҷовидро бичашед!'),
(3518, 32, 15, 'Танҳо касоне ба оёти Мо имон овардаанд, ки чун оёти Моро бишнаванд, ба саҷда бияфтанд ва Парвардигорашонро ба покӣ биситоянд ва саркашӣ накунанд (Сачда).'),
(3519, 32, 16, 'Аз бистари хоб паҳлӯ тиҳӣ (бедорхобӣ) мекунанд, Парвардигорашонро бо биму умед илтиҷо мекунанд ва аз он чӣ ба онҳо додаем, садақа мекунанд.'),
(3520, 32, 17, 'Ва ҳеҷ кас аз он мукофоте аз хушиву хурсандӣ хабар надорад, ки ба музди корҳои кардааш, барояш пинҳон карда шудааст.'),
(3521, 32, 18, 'Оё он кас, ки имон оварда, мопанди кассет, ки фисқ меварзад? На, баробар нестанд!'),
(3522, 32, 19, 'Аммо онон, ки имон овардаанд ва корҳои шоиста кардаанд, ба ҷазои неки амалҳое, ки макардаанд, манзилгоҳе дар боғҳои биҳишт хоҳанд ёфт.'),
(3523, 32, 20, 'Ва аммо фосиқон, манзилгоҳашон оташ аст. Ҳар гоҳ ки бихоҳанд аз он берун оянд, бори дигар онҳоро ба даруни оташ бозгардонанд ва бигӯяндашон: «Бичашед азоби оташеро, ки дурӯғаш мепиндоштед!»'),
(3524, 32, 21, 'Ва азоби дунёро пеш аз он азоби бузургтар ба онҳо бичашонем, шояд, ки бозгарданд!'),
(3525, 32, 22, 'Кист ситамкортар аз он касе, ки ӯро ба оёти Парвардигораш панд диҳанд, вале рӯй гардонад? Мо аз гунаҳкорон интиқом мегирем!'),
(3526, 32, 23, 'Ҳақиқатан ба Мӯсо китоб додем. Аз дидори ӯ дар шубҳа мабош. Ва онро роҳнамои банӣ-Исроил қарор додем.'),
(3527, 32, 24, 'Аз миёни он қавм пешвоёне падид овардем, ки чун сабр пеша карданд ва ба оёти Мо яқин доштанд, ба фармоии Мо мардумро ҳидоят мекарданд.'),
(3528, 32, 25, 'Албатта Парвардигори ту дар рӯзи қиёмат дар он чӣ ихтилоф мекарданд, миёнашон довари хоҳад кард.'),
(3529, 32, 26, 'Оё он ҳама мардумеро, ки пеш аз онҳо ҳалок кардем ва акнун инҳо дар хонаҳояшон роҳ мераванд, сабаби ҳидояташон нашуд? Дар ин ибратҳост, чаро намешунаванд?'),
(3530, 32, 27, 'Оё намебинанд, ки обро ба замини хушку бегиёҳ равона месозем, то киштзорҳо бирӯёнем ва чорпоён ва худашон аз он бихӯранд? Чаро намебинанд?'),
(3531, 32, 28, 'Мегӯянд: «Агар рост мегӯед, пирӯзӣ чӣ вақт хоҳад буд?»'),
(3532, 32, 29, 'Бигӯ: «Дар рӯзи пирӯзӣ, имон овардани кофирон фоидаашон надиҳад ва мӯҳлаташон надиҳад».'),
(3533, 32, 30, 'Пас аз онҳо рӯй гардон ва мунтазир бош, ки онҳо низ дар интизоранд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Эй паёмбар, аз Худо битарс ва ба кофирону мунофиқон итоъат макун. Зеро Худо донову ҳаким аст!'),
(3535, 33, 2, 'Ба ҳар чӣ аз Парвардигорат ба ту ваҳй мешавад, итоъат кун. Зеро Худо ба он чӣ мекунед, огоҳ аст.'),
(3536, 33, 3, 'Ва бар Худо таваккал кун, зеро Худо корсозиро басанда аст.'),
(3537, 33, 4, 'Худо дар даруни ҳеҷ мярде ду қалб наниҳодааст. Ва занонатонро, ки модари худ мехонед, модаратон қарор надод ва фарзандхондагонатонро фарзандонатон насохт. Инҳо чизҳоест, ки ба забои мегӯед ва сухани ҳақ аз они Худост ва Ӯст, ки роҳ менамояд.'),
(3538, 33, 5, 'Писархондагонро ба номи падарашон бихонед, ки дар назди Худо мувофиқи инсоф аст. Агар падарашонро намешиносед, бародарони динӣ ва маволии (дӯстони) шумо бошанд. Агар пеш аз ин хатое кардаед, боке нест, магар он, ки ба қасди дил кунед. Ва Худо бахщояндаву меҳрубон аст!'),
(3539, 33, 6, 'Паёмбар ба мӯъминон аз худашон сазовортар аст ва занонаш модарони мӯъминон ҳастанд ва дар китоби Худо хешовандони насабӣ аз мӯъминону муҳоҷирон ба якдигар сазовортаранд, ғайри он, ки бихоҳед ба яке аз дӯстони худ некӣ кунед. Ва ин ҳукм дар китоби Худо мактуб аст.'),
(3540, 33, 7, 'Ва он ҳангом, ки аз паёмбарон паймон гирифтем ва аз туву аз Нӯҳ ва Иброҳим ва Мӯсо ва Исо бинни Марям ва аз ҳамаи онҳо паймоне сахт гирифтем,'),
(3541, 33, 8, 'то ростгӯёнро аз сидқашон бипурсад ва барои кофирон азобе дардовар муҳайё кардааст.'),
(3542, 33, 9, 'Эй касоне, ки имон овардаед, аз неъмате, ки Худо ба шумо додааст, ёд кунед, ба он ҳангом, ки лашкарҳо бар сари шумо ҳуҷум карданд ва Мо бодро ва лашкарҳоеро, ки намедидед, бар сарашон фиристодем ва Худо ба он чӣ мекардед, бино буд!'),
(3543, 33, 10, 'Он гоҳ ки аз самти боло ва аз самти поин бар шумо тохтанд, чашмҳо хира шуд ва дилҳо ба гулӯ расида буд ва ба Худо гумонҳои гуногун мебурдед.'),
(3544, 33, 11, 'Дар он ҷо мӯъминон имтиҳон карда шуданд ва сахт такон хӯрданд!'),
(3545, 33, 12, 'Зеро мунофиқон ва онҳое, ки дар дилҳояшон беморист мегуфтанд: «Худо ва паёмбараш ғайри фиреб ба мо ваъдае надодаанд!»'),
(3546, 33, 13, 'Ва гурӯҳе аз онҳо гуфтанд: «Эй мардуми Ясриб, ин ҷо ҷои монданатон нест. Бозгардед». Ва гурӯҳе аз онҳо аз паёмбар рухсат талабиданд. Мегуфтанд: «Хонаҳои моро ҳифзкунандае нест». Хонаҳояшон беҳифзкунанда набуд, мехостанд бигурезанд.'),
(3547, 33, 14, 'Ва агар аз атроф хонаҳояшонро муҳосира кунанд ва аз онҳо бихоханд, ки кофир шаванд, кофир хоҳанд шуд ва андаке ҳам мӯҳлат раво нахоҳанд гузаронд.'),
(3548, 33, 15, 'Инҳо пеш аз ин бо Худо паймон баста буданд, ки дар ҷанг ба душман пушт накунанд. Худо аз паймони худ бозхост хоҳад кард.'),
(3549, 33, 16, 'Бигӯ: «Агар аз марг ё кушта шудан бигурегед, ҳаргиз гурехтан нафъатон надиҳад. Ва он гоҳ аз зиндагӣ ғайри андаке баҳраманд нахоҳед шуд».'),
(3550, 33, 17, 'Бигӯ: «Агар Худо бароятон қасди баде дошта бошад ё бихоҳад ба шумо раҳмате арзонӣ дорад, кист, ки шуморо аз иродаи Ӯ нигаҳ дорад?» Онҳо ғайри Худо барои худ дӯсту мададкоре нахоҳанд ёфт.'),
(3551, 33, 18, 'Худо медонад чӣ касоне аз шумо мардумро аз ҷанг бозмедоранд. \'Ва низ мешиносад касоноро, ки ба бародарони худ мегӯянд: «Ба назди мо биёед». Ва ғайри андаке ба ҷанг намеоянд.'),
(3552, 33, 19, 'Ҳар чизеро аз шумо дареғ медоранд ва ҷуи тарсу хафв ояд, бинӣ, ки ба ту менигаранд ва чашмонашон (аз тарси и марг) давр мезанад мисли касе, ки аз марг беҳуш туда бошад, Ва чун ваҳшат аз миён биревад, аз ҳирси ғаниматҳо бо забони тези худ биранҷонадатон. Инҳо имон наёвардаанд. Ва Худо амалҳояшонро нобуд кардааст ва ин кор бар Худо осон будааст.'),
(3553, 33, 20, 'Мепиндоранд, ки лашкари кофирон нарафтааст. Ва агар он лашкарҳо бозмеомаданд, орзу мекарданд, ки кош дар миёни аъроби бодиянишин мебуданд ва ҳамеша аз ахбори шумо мепурсиданд. Агар ҳам дар миёни шумо мебуданд, ғайри андаке ҷанг намекарданд.'),
(3554, 33, 21, 'Барои шумо агар ба Худо ва рӯзи қиёмат умед медоред ва Худоро фаровон ёд мекунед, дар симои расулуллоҳ муқтадои (пешвои) писандидаест, мебинед.'),
(3555, 33, 22, 'Ва чун мӯъминон он гурӯҳҳоро диданд, гуфтанд: Ин ҳамон чизест, ки Худо ва паёмбараш ба мо ваъда додаанд ва Худову паёмбараш рост гуфтаанд». Ва имон ва таслимашон зоид шуд.'),
(3556, 33, 23, 'Аз мӯъминон мардоне ҳастанд, ки ба паймоне, ки ба Худо баста буданд, вафо карданд. Баъзе бар сари паймони худ ҷон бохтанд ва баъзе чашм ба роҳанд ва ҳеҷ паймони худ дигаргун накардаанд.'),
(3557, 33, 24, 'То Худо ростгӯёнро ба сабаби ростии гуфторашон музд диҳад ва мунофиқонро агар хоҳад азоб кунад ё тавбаи онҳоро бипазирад, ки Худо бахшояндаву меҳрубон аст!'),
(3558, 33, 25, 'Худо кофирони хашмгирифтаро бозпас гардонид. Инҳо ба ҳеҷ ғанимате даст наёфтанд. Ва дар майдони ҷанг мӯъминонро Худо кифоя аст. Зеро Худо пуртавону ғолиб аст!'),
(3559, 33, 26, 'Аз аҳли китоб он гурӯҳро, ки ба ёриашон бархоста буданд, аз қалъаҳояшон фуруд овард ва дар дилҳояшон бим афканд. Гурӯҳеро куштед ва гурӯҳеро ба асорат гирифтед.'),
(3560, 33, 27, 'Худо замину хонаҳо ва молҳояшон ва заминҳоеро, ки бар онҳо пой наниҳодаед, ба шумо мерос гузошт. Ва Худо бар ҳар коре тавоност!'),
(3561, 33, 28, 'Эй паёмбар, ба занонат бигӯ: «Агар хоҳони зиндагии дунё ва зинатҳои он ҳастед, биёед, то шуморо баҳраманд созам ва ба тарзи некӯ раҳоятон кунам,'),
(3562, 33, 29, 'ва агар хоҳони Худову паёмбари Ӯ ва сарои охират ҳастед, Худо ба некӯкоронатон подоше (мукофоте) бузург хоҳад дод.'),
(3563, 33, 30, 'Эй занони паёмбар, ҳар кас аз шумо кори зишти сазовори уқубат, кунад. Худо азоби ӯро ду баробар мекунад. Ва ин бар Худо осон аст.'),
(3564, 33, 31, 'Ва ҳар кас аз шумо, ки ба фармонбардории Худо ва паёмбараш давом диҳад ва коре шоиста кунад, ду бор ба ӯ мукофот диҳем. Ва барои ӯ ризқе нек омода кардаем.'),
(3565, 33, 32, 'Эй занони паёмбаар, шумо монанди дигар занон. нестед, агар аз Худо\' битарсед. Пас ба нармӣ сухан; нагӯед, то он марде, ки дар қалби ӯ маразе ҳаст, ба тамаъ афтад, Ва сухани писандида бигӯед.'),
(3566, 33, 33, 'Ва дар хонаҳои худ бимонед. Ва чунон ки дар замони пешини ҷоҳилият мекарданд, зинатҳои худро ошкор накунед. Ва намоз бигузоред ва закот бидиҳед ва ба Худову паёмбараш итоъат кунед. Эй аҳли байт. Худо мехоҳад, палидиро аз шумо дур кунад ва шуморо пок дорад.'),
(3567, 33, 34, 'Он чиро дар хонаҳоятон аз оёти Худо ва ҳикмат тиловат мешавад,. ёд кунед, ки Худо борикбину огоҳ аст!»'),
(3568, 33, 35, 'Худо барои, мардони мусалмон ва занони мусалмон ва мардони мӯъмину занони мӯъмин ва мардони аҳли тоъату занони аҳли тоъат ва мардони ростгӯву занони ростгӯй ва мардони босабру занони босабр ва мардони худотарсу занони худотарс ва мардони садақадиҳандаву занони садақадиҳанда ва мардони рӯзадору занони рӯзадор ва мардоне, ки шармгоҳи худ ҳифз мекунанд ва заноне, ки шармгоҳи худ ҳифз мекунанд ва мардоне, ки Худоро фаровон. ёд мекунанд ва заноне ки Худоро фаровон ёд мекунанд, бахшоиш ва музде бузург омода кардааст!'),
(3569, 33, 36, 'Ҳеҷ марди мӯъмину зани мӯъминро нарасад ки чун Худову паёмбараш дар коре ҳукме карданд, онҳоро дар он корашон ихтиёр бошад. Ҳар ки ба Худову паёмбараш нофармонӣ кунад, сахт дар гумроҳӣ афтодааст.'),
(3570, 33, 37, 'Ва ту ба он мард, ки Худо. неъматаш дода буд ва ту низ неъматаш дода будӣ, гуфтӣ: «Занатро барои худ нигаҳ дор ва аз Худой битарс». Дар ҳоле ки дар дили худ он чиро Худо ошкор сохт, махфӣ дошта буди ва аз мардум метарсидӣ, ҳол он ки Худо аз ҳар каси дигар сазоворгар буд, ки аз ӯ битарсӣ. Пас чун Зайд аз ӯ ҳоҷати худ барорад, ба ҳамсарии туаш даровардем, то мӯъминонро дар заношӯӣ бо занони фарзандхондагони худ, агар ҳоҷати худ аз ӯ бароварда бошанд, манъе набошад. Ва ҳукми Худованд шуданист!'),
(3571, 33, 38, 'Бар паёмбар дар анҷом додани он чӣ Худо ба ӯ муқаррар кардааст, ҳараҷе (гуноҳе) нест, ҳамчунон ки Худо барои паёмбарони пешин низ чунин суннате ниҳода буд ва фармони Худо фармонест бе ҳеҷ зиёдаву нуқсон.'),
(3572, 33, 39, 'Касоне, ки паёмҳои Худоро мерасонанд ва аз Ӯ метарсанд ва аз ҳеҷ кас ғайри Ӯ наметарсанд, Худо барои ҳисоб кардани амалҳояшон кофист!'),
(3573, 33, 40, 'Муҳаммад падари ҳеҷ як аз мардони шумо нест. Ӯ расули Худо ва ҳотами (охирини) паёмбарон аст. Ва Худо ба ҳар чизе доност!'),
(3574, 33, 41, 'Эй касоне, ки имон овардаед, Худоро фаровон ёд кунед!'),
(3575, 33, 42, 'Ва ҳар бомдоду шабонгоҳ тасбеҳаш гӯед!'),
(3576, 33, 43, 'Ӯст, ки Худ ва фариштагонаш бар шумо дурӯғ мефиристанд, то шуморо аз торикӣ ба равшанӣ барад, зеро Худованд ба мӯъминон меҳрубон аст!'),
(3577, 33, 44, 'Рӯзе, ки бо Ӯ дидор кунанд, дурудашон ин аст: «Салом!» Ва Худо барояшон мукофоте некӯ омода кардааст.'),
(3578, 33, 45, 'Эй паёмбар, Мо туро фиристодем то шоҳиду муждадиҳанда ва бимдиҳанда бошӣ!'),
(3579, 33, 46, 'Ва мардумро ба фармони Худо ба сӯи Ӯ даъват кунӣ ва чароғе тобнок бошӣ!'),
(3580, 33, 47, 'Ва мӯъминонро хушхабар деҳ, ки аз сӯи Худо барояшон фазилате бузург омадааст!'),
(3581, 33, 48, 'Ба кофирону мунофиқон итоъат макун ва озорашонро тарк кун ва бар Худо таваккал кун, ки Худо корсозиро кифоя аст.'),
(3582, 33, 49, 'Эй касоне, ки имон овардаед, чун занони мӯъминро никоҳ кардед, ва пеш аз он ки бо онҳо наздикӣ кунед, талоқашон гуфтед, шуморо бар онҳо иддае нест, ки бипоянд. Пас ононро ҳадя диҳед ва ба некӯтарин тарз раҳояшон кунед.'),
(3583, 33, 50, 'Эй паёмбар, Мо занонеро, ки маҳрашонро додаӣ ва ононро, ки ба унвони ғаниматҳои ҷангӣ, ки Худо ба ту ато намудааст, молик шудаӣ ва духтари амуҳо ва духтари аммаҳо ва духтари доиҳо (тағоҳо) ва духтари холаҳои туро, ки бо ту муҳоҷират кардаанд, бар ту ҳалол кардем ва низ зани мӯъминеро, ки худро ба паёмбар бахшида бошад ҳар гоҳ паёмбар бихоҳад уро ба занӣ гирад. Ин ҳукми махсуси туст на дигар мӯьминон. Мо медонем дар борам занонашон ва канизонашон чӣ ҳукме кардаем, то барои ту мушкилие пеш наёяд. Ва Худо бахшояндаву меҳрубон аст!'),
(3584, 33, 51, 'Аз занони худ ҳар киро хоҳӣ ба навбат муаххар (ақиб) дор ва ҳар киро хоҳӣ, бо худ нигаҳ дор. Ва агар аз онҳо, ки дур доштаӣ, якеро биталабӣ, бар ту гуноҳе нест. Дар ин интихобу ихтиёр бояд, ки шодмон бошанд ва ғамгин нашавагд ва аз он чӣ ҳамагияшонро ато менамоӣ, бояд, ки хушнуд гарданд ва Худо медонад, ки дар дилҳои шумо чист. Ва Худост, ки донову бурдбор аст!'),
(3585, 33, 52, 'Баъд аз ин занон ҳеҷ зане бар ту ҳалол нест ва низ зане ба ҷои ояҳо ихтиёр кардан, ҳарчанд туро аз зебоии ӯ хуш ояд, ғайри он чӣ ба ғанимат ба дасти ту афтад. Ва Худо назораткунандаи ҳар чизест!'),
(3586, 33, 53, 'Эй касоне, ки имон овардаед, ба хонаҳои паёмбар дохил нашавед, магар шуморо ба хӯрдани таъоме фаро хонанд, бе он ки мунтазир биншинед, то таъом ҳозир шавад. Агар шуморо даъват кунанд, дохил шавед, ва чун таъом хӯрдед, пароканда гардед. На он ки барои саргармӣ сухан оғоз кунед. Албатта ин корҳо паёмбарро озор медиҳад ва ӯ аз шумо шарм медорад, Вале Худо аз гуфтани ҳақ шарм намедорад. Ва агар аз занони паёмбар чизе хостед, аз пушти парда бихоҳед. Ин кор ҳам барои дилҳои шумо ва ҳам барои дилҳои онҳо покдорандатар аст. Шуморо набояд, ки паёмбари Худоро биёзоред ва на он ки занҳояшро баъд аз вай ҳаргиз ба занӣ гиред. Ин корҳо дар назди Худо гуноҳе бузург аст.'),
(3587, 33, 54, 'Агар чизеро ошкор кунед ё махфӣ доред, дар ҳар ҳол Худо ба ҳар чизе огоҳ аст.'),
(3588, 33, 55, 'Занонро гуноҳе нест, агар дар назди падару писар ва бародару бародарзода ва хоҳарзодаву занони ҳамдин ва ё канизони худ беҳиҷоб бошанд. Ва бояд аз Худо битарсед, ки Худо бар ҳар чизе нозир аст.'),
(3589, 33, 56, 'Худо ва фариштагонаш бар паёмбар салавот мефиристанд. Эй касоне, ки имон овардаед, бар ӯ салавот фиристед ва салом кунед, саломе некӯ.'),
(3590, 33, 57, 'Ҳар оина (албатта) касонеро, ки Худо ва паёмбарашро озор медиҳанд, Худо дар дунё ва охират лаънат карда ва барояшон азобе хоркунанда муҳайё кардааст.'),
(3591, 33, 58, 'Ва касоне, ки мардони мӯъмину занони мӯъминро бе ҳеҷ гунохе, ки карда бошанд, меозоранд, тӯҳмату гуноҳи ошкореро бар душ мекашанд.'),
(3592, 33, 59, 'Эй паёмбар, ба занону духтарони худ ва занони мӯъминон бигӯ, ки чодари худро бар худ пушанд. Ин муносибтар аст, то шинохта шаванд ва мавриди озор воқеъ нагарданд. Ва Худо бахшояндаву меҳрубон аст!'),
(3593, 33, 60, 'Агар мунофиқон ва касоне, ки дар дилҳояшон маразест ва онҳо, ки дар Мадина шойеъа (овоза) мепарокананд, аз кори худ бознаистанд, туро бар онҳо ғолиб мегардонем, то аз он пас ҷуз андаке бо ту дар шаҳр ҳамсоя набошанд.'),
(3594, 33, 61, 'Инҳо лаънат шудагоизпд. Ҳар ҷо ёфта шаванд, бояд дастгир гарданд ва ба сахтӣ кушта шаванд.'),
(3595, 33, 62, 'Ин суннати Худовандист, ки дар миёни пешиниён низ буд ва дар суннатн Худо тағйире нахоҳӣ ёфт!'),
(3596, 33, 63, 'Мардум туро аз қиёмат мепурсанд, бигӯ: «Илми он назди Худост». Ва ту чӣ медонӣ, шояд қиёмат наздик бошад.'),
(3597, 33, 64, 'Худо кофиронро лаънат карда ва барояшон оташе сӯзон муҳайё кардааст,'),
(3598, 33, 65, 'ки дар он ҷовидонаанд ва ҳеҷ дӯсту ёваре нахоҳанд ёфт.'),
(3599, 33, 66, 'Рӯзе, ки суратҳояшонро (рӯйҳояшонро) дар оташ бигардонанд, мегӯянд: «Эй кош, Худоро итоъат карда будем ва расулро итоъат карда будем!»'),
(3600, 33, 67, 'Ва гуфтанд: «Эй Парвардигори мо, ба сарварону бузургони худ итоъат кардем ва онон моро гумроҳ карданд.'),
(3601, 33, 68, 'Эй Парвардигори мо, азобашонро дучандон кун ва ба лаънати бузурге гирифторашон соз!»'),
(3602, 33, 69, 'Эй касоне, ки имон овардаед, мабошед монанди он касон, ки Мӯсоро озурда карданд ва Худояш аз он айбе, ки гуфта буданд, покаш сохт ва назди Худо обрӯманд буд.'),
(3603, 33, 70, 'Эй касоне, ки имон овардаед, аз Худо битарсед ва сухани дуруст бигӯед.'),
(3604, 33, 71, 'Худо корҳои шуморо ба салоҳ оварад ва гуноҳонатонро бибахшояд. Ва ҳар кӣ ба Худову паёмбараш итоъат кунад, ба комёбии (зафари) бузурге даст ёфтааст.'),
(3605, 33, 72, 'Мо ин амонатро бар осмонҳову замин ва кӯҳҳо арза доштем, аз таҳаммули он рӯй гардонданд ва аз он тарсиданд. Инсон он амонатро бар дӯш гирифт, кк ӯ ситамкору нодон буд,'),
(3606, 33, 73, 'то Худо мардони мунофиқу занони мунофиқ ва мардони мушрику занони мушрикро азоб кунад ва тавбаи мардони мӯъмину занони мӯъминро бипазирад, ки Худо бахшояндаву меҳрубон аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Ситоиш аз они Худоест, ки ҳар чӣ дар осмонҳову ҳар чӣ дар замин аст. аз они Ӯст. Ва дар он ҷаҳон низ ситоиш аз они Ӯст ва Ӯ ҳакиму огоҳ аст!'),
(3608, 34, 2, 'Ҳар чиро, ки дар замин фурӯ шавад ва ҳар чиро, ки аз замин берун ояд ва ҳар чиро, ки аз осмон фуруд ояд ва ҳар чиро, ки бар осмон боло равад, медонад. Ва Ӯ меҳрубону бахшоянда аст!'),
(3609, 34, 3, 'Кофирон гуфтанд,: «Моро қиёмат нахоҳад омад!» Бигӯ: «Оре, ба Парвардигорам — он донои ғайб савганд, ки шуморо хоҳад омад. Ба қадри заррае ё кучактар аз он ва ё бузургтар аз он дар осмонҳову замин аз Худо пинҳон нест ва ҳама дар китоби мубин омадааст,'),
(3610, 34, 4, 'то касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, музд диҳад. Барои онҳост бахшоиш ва ризқи некӯ.'),
(3611, 34, 5, 'Ва онон, ки ба оёти Мо дарафтоданд, то Моро ба нотавонӣ оранд, барояшон аэобест сахт дардовар.'),
(3612, 34, 6, 'Онон, ки аз дониш баҳрамандӣ ёфтаанд, медонанд, ки он чӣ аз ҷониби Парвардигорат бар ту нозил шуда аст, ҳақ аст ва ба роҳи Худои ғолибу сутуданӣ роҳ менамояд.'),
(3613, 34, 7, 'Кофирон гуфтанд: «Оё мехоҳед ба марде далолататон кунем, ки шуморо мегӯяд: «Он гоҳ ки порапора шавед ва рез-рез, аз лав офарида хоҳед шуд?'),
(3614, 34, 8, 'Оё бар Худо дурӯғ мебандад, ё девона аст?» На, онон, ки ба охират имон надоранд, дар азобанд ва сахт аз роҳ дуранд.'),
(3615, 34, 9, 'Оё ба паши рӯй ё пушти сари худ аз осмонҳову замин наменигаранд? Агар бихоҳем, онҳоро дар замин фурӯ мебарем, ё қитъае аз осмонро бар сарашон меафканем. Ва дар ин барои ҳар бандае, ки ба Худо бозмегардад, ибратест.'),
(3616, 34, 10, 'Довудро аз сӯи худ фазилате додем, ки эй кӯҳҳо ва эй паррандагон, бо ӯ ҳамовоз шавед. Ва оҳанро барояш нарм кардем,'),
(3617, 34, 11, 'ки зиреҳҳои баланд бисоз ва дар бофтани зиреҳ андозаҳоро нигаҳ дор. Ва корҳои шоиста кунед, ки Ман ба корҳоятон биноам!'),
(3618, 34, 12, 'Ва бодро роми Сулаймон кардем. Бомдодон якмоҳа роҳ мерафт ва шабонгоҳ якмоҳа роҳ. Ба чашмаи мисро барояш ҷорӣ сохтем ва гурӯҳе аз девҳо ба фармони Парвардигораш барояш кор мекарданд ва ҳар кӣ аз онон сар аз фармони Мо мепечид, ба ӯ азоби оташи сӯзонро мечашонидем.'),
(3619, 34, 13, 'Барои вай ҳар чӣ мехост, аз биноҳои баланд ва суратҳо ва косаҳое чун ҳавз ва дегҳои маҳками барҷой, месохтанд. Эй хонадони Довуд, барои шукр гузорӣ коре кунед ва андаке аз бандагони Ман шукргузоранд.'),
(3620, 34, 14, 'Чун ҳукми маргро бар ӯ рондем, ҳашарае аз ҳашароти замин мардумро бар маргаш огоҳ кард, асояшро (як сол) хоид. Чун фурӯ афтод, дегҳо дарёфтанд, ки агар илми ғайб медонистанд, дар он азоби хоркунанда намемонданд.'),
(3621, 34, 15, 'Мардуми Саборо дар масканҳояшон ибрате буд: ду бӯстон доштанд, яке аз ҷониби рост ва яке аз ҷониби чап. Аз он чӣ Парвардигоратон ба шумо рӯзӣ додааст, бихӯред ва шукри Ӯ ба ҷой оваред. Шаҳре хушу покиза ва Парвардигоре бахшоянда!'),
(3622, 34, 16, 'Рӯй гардонданд. Мо низ сели вайронгарро бар онҳо фиристодем ва ду бӯстонашонро ба ду бӯстон бадал кардем бо мевае талх ва шӯрагаз ва андаке сидр (номи дарахт).'),
(3623, 34, 17, 'Онҳоро, ки носипос буданд, инчунин ҷазо додем, Оё Мо ғайри носипосонро ҷазо мекунем?'),
(3624, 34, 18, 'Миёни онон ва қарияҳое (деҳаҳое), ки баракат дода будем, деҳаҳое ободон ва бар сари роҳ падид овардем. Ва манзилҳои баробар мӯъайян кардем. «Дар он роҳҳо боамну осоиштагӣ шабҳову рӯзҳо сафар кунед!»'),
(3625, 34, 19, 'Бар хештан ситам карданд ва гуфтанд: «Эй Парвардигори мо, манзилгоҳҳои моро аз ҳам, дур гардон!» Мо низ афсонаи рӯзгорашон гардонидем ва сахт парокандаашон сохтем ва дар ин ибратҳост барои сабркунандагони шукргузор!'),
(3626, 34, 20, 'Шайтон гумони худро дар бораи онҳо дуруст ёфт. Ва ғайри гурӯҳе аз мӯъминон дигарон аз ӯ пайравӣ карданд.'),
(3627, 34, 21, 'Ва шайтонро бар онон ғолибияте набуд, магар он ки мехостем маълум дорем, ки чӣ касоне ба қиёмат имон доранд ва чӣ касоне аз он дар шак ҳастанд. Ва Парвардигори ту нигаҳбони ҳар чизест!'),
(3628, 34, 22, 'Бигӯ: «Бихонед касонеро, ки ҷуз Худои якто худо мепиндоред!» Молики заррае дар осмонҳову замин нестанд ва дар офариниши он ду ширкате надоштаанд ва Худоро аз миёни онҳо ёригаре набудааст.'),
(3629, 34, 23, 'Шафоъат назди Худо нафъ накунад, магар дар бораи касе, ки Ӯ худ иҷозат диҳад, Ва чун бим аз дилҳояшон биравад, гӯянд: «Парвардигоратон чӣ гуфт?» Гӯянд: «Сухани ҳақ гуфт. Ва Ӯ баландмартабаву бузург аст!»'),
(3630, 34, 24, ': Бигӯ: «Аз осмонҳову замин чӣ касе, ба шумо рӯзӣ медиҳад?» Бигӯ: «Худои якто». Инак мову шумо ё дар тариқи ҳидоят ҳастем ё дар гумроҳии ошкор». (Саҷда).'),
(3631, 34, 25, 'Бигӯ: Агар мо гуноҳе кунем, шуморо пурсиш намекунанд ва агар шумо гуноҳе кардед моро пурсиш нахоҳанд кард».'),
(3632, 34, 26, 'Бигӯ: «Парвардигори мо мову шуморо гирд меоварад, сипас миёни мо ба ҳақ доварӣ мекунад. Зеро Ӯст ҳукмкунандаву доно!»'),
(3633, 34, 27, 'Бигӯ: «Онҳоеро, ки шарики Худо пиндоштед, ба ман нишон бидиҳед! Ҳаргиз, Ӯст Худои ғолибу ҳаким!»'),
(3634, 34, 28, 'Туро ба паёмбарӣ фиристодем, бар ҳамаи мардум; муждадиҳандаву бимдиҳанда. Вале бештари мардум намедонанд.'),
(3635, 34, 29, 'Ва мегӯянд: «Агар рост мегӯед, ин ваъда (қиёмат) чӣ вақт фаро расад?»'),
(3636, 34, 30, 'Бигӯ: «Он рӯз, ки меъоде (ваъдагоҳи) шумост, на соъате таъхир кунед ва на соъате пеш афтед».'),
(3637, 34, 31, 'Ва кофирон гуфтанд: «Мо на ба ин Қуръон имон меоварем ва на ба китобҳои пеш аз он!» Агар бубинӣ он рӯз, ки ситамкоронро ба пешгоҳи Парвардигорашон нигоҳ доранд, ҳар кас гуноҳи худ ба гардани дигаре андозад. Забуншудагон пайравон ба қудратмандон (сардорон) гӯянд: «Агар шумо набудед, мо имон оварда будем».'),
(3638, 34, 32, 'Қудратмандон ба забуншудагон гӯянд: «Оё аз он пас, ки шуморо ба роҳи ҳидоят даъват карданд, мо шуморо боздоштем? На, шумо худ гунаҳкор будед!»'),
(3639, 34, 33, 'Забуншудагон ба қудратмандон гӯянд: «На, шумо шабу рӯз ҳила мекардед, он гоҳ ки моро фармон медодед, ки ба Худои якто кофир шавем ва барои Ӯ шариконе қарор диҳем!» Ва чун азобро бубинанд, дар дил пушаймонӣ кунанд. Ва Мо гулҳоро бар гардани кофирон бигузорем. Оё на чунин аст, ки дар баробари амалҳояшон ҷазо дода мешаванд?'),
(3640, 34, 34, 'Мо кадом бимдиҳандае ба деҳае фиристодем, тавонгарони бо айшу ишраташ гуфтанд: «Мо ба он чӣ шуморо ба он фиристодаанд, имон намеоварем».'),
(3641, 34, 35, 'Ва гуфтанд: «Молҳову авлоди мо аз ҳама бештар аст ва касе моро азоб накунад».'),
(3642, 34, 36, 'Бигӯ: «Парвардигори ман аст, ки рӯзии ҳар касро, ки бихоҳад, фаровон мекунад ва бар ҳар кӣ бихоҳад, танг мегирад. Вале бештари мардум намедонанд».'),
(3643, 34, 37, 'Молҳову авлодатон чизе нест, ки шуморо ба Мо наздик созад. Ғайри онҳое, ки имон овардаанд ва корҳои шоиста кардаанд, ки музди инҳо ба сабаби амалҳояшон ду баробар аст ва эмин дар ғурфаҳои биҳишт ҳастанд.'),
(3644, 34, 38, 'Онҳое, ки ба оёти Мо метозанд ва мепиндоранд, ки аз Мо мегурезанд, тӯъмаи азобанд.'),
(3645, 34, 39, 'Бигӯ: «Парвардигори ман аст, ки рӯзии ҳар кӣ аз бандагонашро, ки бихоҳад, фаровон мекунад ё ӯро ба тангӣ меафканад». Ва агар чизе садақа кунед, ивазашро хоҳад дод ва Ӯ беҳтарини рӯзидиҳандагон аст!'),
(3646, 34, 40, 'Рӯзе, ки ҳамаро ҷамъ оварад, он гоҳ малоикаро гӯяд: «Оё инҳо буданд, ки шуморо мепарастиданд?»'),
(3647, 34, 41, 'Мегӯянд; «Ту покӣ. Туӣ валии мо, на онҳо. Инҳо ҷинҳоро мепарастиданд ва бештарин ба онҳо имон доштанд».'),
(3648, 34, 42, 'Он рӯз ба якдигар ҳеҷ суду зиёне натавонед расонд. Ва ба ситамкорон гӯем: «Бичашед азоби оташро, ки дурӯғаш мепиндоштед!»'),
(3649, 34, 43, 'Чун оёти равшани Мо бар онон хонда шуд, гуфтанд: «Ин мардест, ки мехоҳад шуморо аз он чӣ падаронатон мепарастиданд, боздорад». Ва гуфтанд: «Ин ғайри дурӯғе ба ҳам бофта чизи дигаре нест!» Ва касоне, ки ба Худо кофир шуда буданд, чун сухани ҳақ бар онҳо нозил шуд, гуфтанд: «Ин чизе ғайри ҷодуе ошкор нест!»'),
(3650, 34, 44, 'Пеш аз ин китобе, ки онро бихонанд, ба онҳо надодаем ва пеш аз ту бимдиҳандае бар онҳо нафиристодаем.'),
(3651, 34, 45, 'Ва касоне, ки пеш аз онҳо буданд, паёмбаронро такзиб (дурӯғ) кардаанд ва инҳо худ ба даҳяки он чӣ ба онҳо дода будем, нарасидаанд ва бо ин ҳол паёмбарони Маро дурӯғ бароварданд. Пас уқубати Ман чӣ сахт буд!'),
(3652, 34, 46, 'Бигӯ: «Шуморо ба як чиз панд медиҳам: ду-ду ва як-як барои (ибодати) Худо бархезед. Сипас бияндешед, то бидонед, ки дар ёри шумо девонагие нест. Ӯст, ки шуморо аз омадани азобе сахт метарсонад!»'),
(3653, 34, 47, 'Бигӯ: «Ҳар музде, ки аз шумо талабидаам, аз они худатон бод. Музди ман танҳо бар ӯҳдаи Худост. Ӯст, ки бар ҳар коре нозир аст!»'),
(3654, 34, 48, 'Бигӯ: «Парвардигори ман илҳомбахши сухани ҳақ аст. Донои ғайбҳост!»'),
(3655, 34, 49, 'Бигӯ: «Ҳақ (Қуръон) омад ва ботил зоҳир намешавад ва бознамегардад!»'),
(3656, 34, 50, 'Бигӯ: «Агар ман гумроҳ шавам, зиёнаш бар ман аст ва агар ба роҳи ҳидоят равам, ба он сабаб аст, ки Парвардигори ман ба ман ваҳй мекунад. Ӯ шунавову наздик аст!»'),
(3657, 34, 51, 'Агар бубинӣ, он гоҳ ки сахт битарсанд ва халосиашон набошад ва аз маконе наздик гирифторашон созанд,'),
(3658, 34, 52, 'Гӯянд: «Инак ба расул имон овардем». Аммо аз он ҷои дур чӣ сон ба он даст ёбанд?'),
(3659, 34, 53, 'Пеш аз ин ба Ӯ кофир шуда буданд ва ба гумони худ ба Ӯ тӯҳмат мезаданд.'),
(3660, 34, 54, 'Миёни онҳо ва он орзу, ки доранд, ҷудоӣ афтод. Ҳамчунон ки бо дигарон, ки чунин меандешиданд ва сахт дар шубҳа буданд, низ чунин шуд!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Ситоиш аз они Худованд аст, офаринандаи осмонҳову замин, он ки фариштагонро расулон гардонид. Фариштагоне, ки болҳое доранд, ду-ду ва сесе ва чаҳор-чаҳор. Дар офариниш ҳар чӣ бихоҳад, меафзояд, зеро Худо бар ҳар коре тавоност!'),
(3662, 35, 2, 'Раҳмате, ки Худо бар мардум бикушояд, касе натавонад, ки бозаш дорад. Ва чун чизеро дареғ дорад, кас натавонад, ғайри Ӯ, ки онро фиристад. Ва Ӯст ғолибу ҳаким!'),
(3663, 35, 3, 'Эй мардум, неъматеро, ки Худо бар шумо ато кардааст, ёд кунед. Оё ҷуз Худо офаринандаи дигаре ҳаёт, ки шуморо аз осмону замин рӯзӣ диҳад? Худое ғайри Ӯ нест, пас чӣ гуна аз ҳақ бероҳатон мекунанд?'),
(3664, 35, 4, 'Агар туро такзиб (дурӯғ) кардаанд, паёмбаронеро ҳам, ки пеш аз ту будаанд, такзиб кардаанд ва ҳамаи корҳо ба Худо бозгардонда шавад.'),
(3665, 35, 5, 'Эй мардум ваъдаи Худо ҳақ аст. Зиндагии дунё шуморо нафиребад ва он шайтони фиребанда ба Худо мағруратон нагардонад.'),
(3666, 35, 6, 'Шайтон душмани шумост. Ӯро душман гиред. Ӯ фармонбарони худро даъват кунад то ҳама аз дузахиён бошанд.'),
(3667, 35, 7, 'Ва кофиронро азобест сахт ва барои касоне, ки имон овардаанд ва кордон шоиста кардаанд, бахшоиш ва савоби бузургест.'),
(3668, 35, 8, 'Оё он ки кирдори бадаш дар назараш ороста шуда, чунон ки некӯяш пиндошт, монанди касест, ки чунин нест? Пас Худо ҳар киро хоҳад, гумроҳ мекунад ва ҳар киро хоҳад, ҳидоят мекунад. Набояд, ки ҷони ту ба хотири онҳо дучори андӯҳ, шавад. Зеро Худо ба корҳое, ки мекунанд, огоҳ аст.'),
(3669, 35, 9, 'Худост, ки бодҳоро фиристод то абрҳоро биронанд. Ва Мо онҳоро ба сарзаминҳои мурда меронем ва замини мурдаро ба он зинда мекунем. Зинда гаштан дар рӯзи қиёмат низ чунин аст.'),
(3670, 35, 10, 'Ҳар кӣ хоҳони иззат аст, бидонад, ки иззат ҳамагӣ аз они Худост. Сухани хушу пок ба сӯи Ӯ боло меравад ва кирдори нек аст, ки онро боло мебарад. Ва барои онон, ки аз рӯи макр фасодкорӣ мекунанд, азобест сахт ва макрашон низ аз миён биравад.'),
(3671, 35, 11, 'Худо шуморо аз хок ва сипас аз нутфа биёфарид. Он гоҳ ҷуфтҳои якдигаратон қарор дод. Ҳеҷ зане ҳомиладор намешавад ва намезояд магар ба илми Ӯ. Ва умри ҳар солхурдае ба дарозо кашад ва аз умри касе кам гардад, албатта ҳама дар китобе навишта шудааст. Ва ин корҳо бар Худо осон аст!'),
(3672, 35, 12, 'Ин ду дарё яксон нестанд: яке обаш ширину гуворост ва яке шӯру талх. Аз ҳар ду гушти тоза мехӯред ва аз онҳо чизҳое барои ороиши тани худ берун мекашед. Ва мебинӣ киштиҳоро, ки обро мешикофанд, то шумо аз фазли Худо рӯзӣ талабед ва шояд, ки шукргузор бошед!'),
(3673, 35, 13, 'Аз шаб кам мекунаду ба рӯз меафзояд ва аз рӯз кам мекунаду ба шаб меафзояд ва офтобу моҳро ром кард. Ҳар як то замоне муайян дар ҳаракатанд, Ин аст Худо — Парвардигори шумо. Фармонравоӣ аз они Ӯст. Чизҳоеро, ки ғайри Ӯ ба худоӣ мехонед, молики пӯсти миёни донаи хурмое ҳам нестанд.'),
(3674, 35, 14, 'Ва агар онҳоро бихонед, садоятонро намешунаванд ва агар бишнаванд, посухатон нагӯянд. Ва дар рӯзи қиёмат ширк оварданатонро инкор кунанд ва кас ҳамонанди Худои огоҳ хабардорат насозад!'),
(3675, 35, 15, 'Эй мардум, ҳамаи шумо ба Худо мӯҳтоҷед. Ӯст беэҳтиёҷу лоиқи ситоиш.'),
(3676, 35, 16, 'Агар бихоҳад, шуморо аз миён мебарад ва мардуме нав меоварад.'),
(3677, 35, 17, 'Ва ин кор бар Худо душвор нест.'),
(3678, 35, 18, 'Ҳеҷ кас бори гуноҳи дигареро бар дӯш накашад. Ва агар гаронборе касеро ба бардоштани бори худ фарёд кунад, ҳарчанд хешованди ӯ бошад, аз бардоштани он рӯй гардонад. Ту фақат касонеро метарсонӣ, ки аз Парвардигорашон нодида бимноканд ва намоз мегузоранд. Ва ҳар кӣ пок шавад, барои худ пок шуда. Ва бозгашти ҳама ба сӯӣ Худост!'),
(3679, 35, 19, 'Нобинову бино баробар нестанд'),
(3680, 35, 20, 'ва торикиву равшанӣ'),
(3681, 35, 21, 'ва на сояву ҳарорати офтоб.'),
(3682, 35, 22, 'Ва зиндагону мурдагон баробар нестанд. Худо ҳар киро хоҳад, мешунавонад. Ва ту наметавонӣ сухани худро ба мурдагоне, ки дар гӯр хуфтаанд, бишнавонӣ.'),
(3683, 35, 23, 'Ту ҷуз бимдиҳандае нести.'),
(3684, 35, 24, 'Мо туро ба ҳақ ба пайғамбарӣ фиристодем, то мужда диҳӣ ва бим диҳӣ ва ҳеҷ миллате нест, ки ба миёнашон бимдиҳандае наомада бошад!'),
(3685, 35, 25, 'Агар туро дурӯғ бароранд, касоне ҳам, ки пеш аз инҳо будаанд, паёмбаронашонро, ки бо мӯъҷизот ва бо навиштаҳо ва китоби равшаноибахш ба миёнашон омада буданд, дурӯғ баровардаанд.'),
(3686, 35, 26, 'Сипас онҳоеро, ки кофир шуданд, ба ҳалокат гирифтам. Уқубати Ман чӣ гуна буд?'),
(3687, 35, 27, 'Оё надидаӣ, ки Худо аз осмон борон фиристод ва бо он меваҳои гуногун рӯёнидем? Ва аз кӯҳҳо роҳҳо падид овардем: сафед ва сурх ва рангоранг ва бағоят сиёҳ.'),
(3688, 35, 28, 'Ҳамчунин аз мардум ва ҷунбандагон ва чаҳорпоёни гуногун. Албатта аз миёни бандаони Худо танҳо донишмандон аз Ӯ метарсанд. Ва Худо ғолибу бахшоянда аст!'),
(3689, 35, 29, 'Онон, ки китоби Худоро мехонанд ва намоз мегузоранд ва аз он чӣ ба онҳо додаем, пинҳониву ошкоро садақа мекунанд, умедвор ба тиҷорате ҳастанд, ки ҳаргиз зиён намекунад.'),
(3690, 35, 30, 'Зеро Худо мукофоташонро ба тамомӣ медиҳад ва аз фазли худ чизе бар он меафзояд. Зеро бахшояндаву шукрпазир аст!'),
(3691, 35, 31, 'Ҳар чӣ аз он китоб ба ту ваҳй кардаем, ҳак, аст, ки китобҳои пеш аз худро тасдиқ мекунад ва Худо бар бандагонаш огоҳу биност!'),
(3692, 35, 32, 'Сипас китобро ба касоне аз бандагонамон, ки баргузида будем, ба мерос додем. Баъзе бар худ ситам карданд ва баъзе роҳи миёнаро баргузиданд ва баъзе ба фармони Худо дар корҳои нек пешӣ гирифтанд. Ва ин аст бахшоише бузург!'),
(3693, 35, 33, 'Ба биҳиштҳое, ки ҷойгоҳи ҷовидонаи онҳост, дохил мешаванд. Дар он ҷо ба дастбандаҳои зару марворидашон меороянд ва дар он ҷо ҷомаҳошон аз ҳарир (шоҳӣ) аст.'),
(3694, 35, 34, 'Ва мегӯянд: «Шукр Худоро, ки андӯҳ аз мо дур кард, зеро Парварднгори мо бахшояндаву шукрпазир аст!'),
(3695, 35, 35, 'Он Худое, ки моро аз фазли худ ба ин ҷаҳони ҷовидон даровард, ки дар он ҷо на ранҷе ба мо мерасад ва на хастагӣ».'),
(3696, 35, 36, 'Ва кофиронро оташи ҷаҳаннам аст, на бимиронандашон ва на аз азобашон кам гардад. Кофиронро чунин ҷазо медиҳем!'),
(3697, 35, 37, 'Ва аз даруни оташ фарёд зананд: «Эй Парвардигори мо, моро берун ор, то корҳое шоиста кунем, ғайр аз он чӣ мекардем». Оё он қадар шуморо умр надода будем, ки пандгирандагон панд гиранд? Ва шуморо бимдиҳанда омад. Пас бичашед, ки гуноҳкоронро ёридиҳандае нест!'),
(3698, 35, 38, 'Худо донои ниҳони осмонҳову замин аст ва Ӯ ба он чӣ дар дилҳост, огоҳ аст!'),
(3699, 35, 39, 'Ӯст, ки шуморо дар рӯи замин ҷонишини пешиниён кард, пас ҳар кас, ки кофирӣ кунад, куфраш бар зиёни ӯст ва куфри кофирон ҷуз бар душмании Парвардигорашон бо онҳо наяфзояд. Низ куфри кофирон фақат ба зиёнашон афзояд.'),
(3700, 35, 40, 'Бигӯ: «Он шариконеро, ки ба ҷои Худои якто мехондед, дидед? Ба ман бинамоед, ки аз ин замин чӣ чизро офаридаанд? Ё дар офариниши осмон чӣ шарикие доштаанд? Оё бар онҳо китобе фиристодаем, ки онро ҳуҷҷати худ созанд? На, ситамкорон ба якдигар ғайри фиреб ваъдае намедиҳанд».'),
(3701, 35, 41, 'Худо осмонҳову заминро нигаҳ медорад, то аз ҷой беҷо нашаванд ва агар аз ҷой беҷо шаванд, ҳеҷ як аз шумо, ғайри Ӯ, наметавонад онҳоро нигаҳ дорад. Албатта Худо бурдбору бахшоянда аст!'),
(3702, 35, 42, 'Ба Худо қасамҳои сахт хӯрданд, ки агар бимдиҳандае биёяд, беҳтар аз ҳар уммати дигар ҳидоят ёбанд. Вале чун бимдиҳандае омад, бадбинияшон афзуд;'),
(3703, 35, 43, 'ба саркашӣ дар замин ва найрангҳои бад. Ва ин найрангҳои бад фақат найрангбозонро дар бар гирад. Оё ҷуз суннате, ки бар гузаштагон рафтааст, мунтазири чизи дигаре ҳастанд? Дар суннати Худо ҳеҷ табдиле намеёбӣ ва дар суннати Худо ҳеҷ тағйире намеёбӣ.'),
(3704, 35, 44, 'Оё дар замин намегарданд, то бубинанд, ки оқибати мардуме, ки пеш аз онҳо будаанд ва нерӯе бештар доштаанд, ба куҷо кашид? Ҳеҷ чиз дар осмонҳову замин нест, ки Худоро нотавон созад. Зеро Ӯ донову тавоност!'),
(3705, 35, 45, 'Ва агар Худо бихоҳад, мардумро ба сабаби корҳое, ки кардаанд, бозхост кунад, бар рӯи замин ҳеҷ ҷунбандае боқӣ нагузорад, вале онҳоро то замоне муъайян мӯҳлат медиҳад ва чун муддаташон тамом щуд, ба амалҳои бандагони худ огоҳ аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Ё, син.'),
(3707, 36, 2, 'Савганд ба Қуръони ҳикматомез,'),
(3708, 36, 3, 'ки ту аз паёмбарон ҳастӣ,'),
(3709, 36, 4, 'бар роҳе рост.'),
(3710, 36, 5, 'Қуръон аз ҷониби он ғолиби меҳрубон нозил шуда,'),
(3711, 36, 6, 'то мардумеро бим диҳӣ, ки падаронашон бим дода нашуданд ва дар бехабарӣ буданд.'),
(3712, 36, 7, 'Азоби Худо барои бештаринашон воҷиб шудааст ва имон намеоваранд.'),
(3713, 36, 8, 'Ва мо бар гарданҳояшон то манаҳҳо тавқҳо (занҷирҳо) ниҳодем, чунон ки сарҳояшон ба болост ва поин оварда натавонанд,'),
(3714, 36, 9, 'Дар баробарашон деворе кашидем ва дар пушти сарашон деворе. Ва бар чашмонашон низ пардае афкандем, то натавонанд дид.'),
(3715, 36, 10, 'Фарқашон накунад, чӣ онҳоро битарсонӣ ва чӣ натарсонӣ, имон намеоваранд.'),
(3716, 36, 11, 'Танҳо ту касеро метарсонӣ, ки ба Қуръон пайравӣ кунад ва аз Худои раҳмон дар ниҳон битарсад. Чунин касро ба бахшоиш ва музди некӯ хушхабар бидеҳ!'),
(3717, 36, 12, 'Мо мурдагонро зинда мекунем. Ва ҳар кореро, ки пеш аз ин кардаанд ва ҳар асареро, ки падид овардаанд, менависем ва ҳар чизро дар китоби мубин шумор кардаем.'),
(3718, 36, 13, 'Достони мардуми он деҳаро бар онҳо биёвар, он гоҳ ки расулон ба он ҷо омаданд.'),
(3719, 36, 14, 'Нахуст ду танро ба наздашон фиристодем ва дурӯғ бароварданд, пас бо саввуми қувваташон додем ва гуфтанд: «Мо ба сӯи шумо фиристода шудаем».'),
(3720, 36, 15, 'Гуфтанд: «Шумо инсонҳое монанди мо ҳастед ва Худои раҳмон ҳеҷ чиз нафиристодааст ва шумо дурӯғ мегӯед».'),
(3721, 36, 16, 'Гуфтанд: «Парвардигори мо медонад, ки мо ба сӯи шумо фиристода шудаем.'),
(3722, 36, 17, 'Ва бар ӯҳдаи мо ғайри паём расонидани ошкоро ҳеҷ чизи дигар нест».'),
(3723, 36, 18, 'Гуфтанд: Мо шуморо ба фоли бад гирифтаем. Агар бас накунед, сангсоратон хоҳем кард ва шуморо аз мо шиканҷае сахт хоҳад расид».'),
(3724, 36, 19, 'Гуфтанд: «Фоли бади шумо бо худи шумост. Оё агар пандатон диҳанд чунин мегӯед? На, мардуме аз ҳад гузашта ҳастед!»'),
(3725, 36, 20, 'Марде аз дурдасти шаҳр давон-давон омаду гуфт: «Эй қавми ман, ба ин расулон пайравӣ кунед!'),
(3726, 36, 21, 'Ба касоне, ки аз шумо ҳеҷ музде наметалабанд ва худ мардуме ҳидоятёфтаанд, пайравӣ кунед!'),
(3727, 36, 22, 'Чаро Худоеро, ки маро офаридааст ва ба назди Ӯ бозгардонда мешавед, напарастам?'),
(3728, 36, 23, 'Оё ғайри Ӯ худоёяеро ихтиёр кунам, ки агар Худои раҳмон бихоҳад ба ман зиёне бирасонад, шафоъаташон маро ҳеҷ суд накунад ва маро халосӣ набахшанд?'),
(3729, 36, 24, 'Ва дар ин ҳолат ман дар гумроҳии ошкор бошам.'),
(3730, 36, 25, 'Ман ба Парвардигоратон имон овардам. Сухани маро бишнавед».'),
(3731, 36, 26, 'Гуфта шуд: «Ба биҳишт дарой!» Гуфт: «Эй кош, қавми ман медонистанд,'),
(3732, 36, 27, 'ки Парвардигори ман маро бибахшоид ва дар гурӯҳи гиромишудагон даровард».'),
(3733, 36, 28, 'Ва аз он пас бар сари қавми ӯ ҳеҷ лашкаре аз осмон фурӯ нафиристодем. Ва Мо фурӯфиристанда набудем.'),
(3734, 36, 29, 'Як бонги сахте буд, ки ногоҳ ҳама бар ҷой сард шуданд.'),
(3735, 36, 30, 'Эй дареғ бар ин бандагон! Ҳеҷ паёмбаре бар онҳо фиристода нашуд, ки масхарааш накарда бошанд.'),
(3736, 36, 31, 'Оё надидаанд, ки чӣ мардумеро пеш аз онҳо ҳалок кардаем, ки дигар ба наздашон бознамегарданд.'),
(3737, 36, 32, 'Ва касе немопад, ки назди Мо ҳозираш наоранд.'),
(3738, 36, 33, 'Нишонаи ибратест бар онҳо замини мурда, ки зиндааш сохтем ва аз он донае, ки аз он мехӯранд, берун овардем.'),
(3739, 36, 34, 'Ва дар он боғҳое аз дарахтони хурмову токҳо падид овардем ва чашмаҳои равон сохтем,'),
(3740, 36, 35, 'то аз меваҳои он ва дастранҷи худ бихӯранд. Чаро шукр намегӯянд?'),
(3741, 36, 36, 'Пок аст он Худое, ки ҳамаи ҷуфтҳоро биёфарид, чӣ аз он чӣ замин мерӯёнад ва чӣ аз худашон ва чӣ он чизҳое, ки намешиносанд.'),
(3742, 36, 37, 'Шаб низ барояшон ибрате дигар аст, ки рӯзро аз он бармекашем ва ҳама дар торикӣ фурӯ мераванд.'),
(3743, 36, 38, 'Ва офтоб ба сӯи қароргоҳи худ равон аст. Ин фармони Худои пирӯзманду доност.'),
(3744, 36, 39, 'Ва барои моҳ манзилҳое андоза кардем, то монанди шохаи хушки хурмо борик шавад.'),
(3745, 36, 40, 'Офтобро сазовортар нест, ки ба моҳ расад ва шабро сазовор нест, ки бар рӯз пешӣ гирад ва ҳама дар фалаке шиноваранд.'),
(3746, 36, 41, 'Ибрате дигар барои онҳо, ки падаронашонро дар он киштии пуркардашуда савор кардем.'),
(3747, 36, 42, 'Ва барояшон монанди киштӣ чизе офаридем, ки бар он савор шаванд.'),
(3748, 36, 43, 'Ва агар бихоҳем, ҳамаро ғарк, месозем ва онҳоро ҳеҷ фарёдрасе набошад ва халосӣ наёбанд,'),
(3749, 36, 44, 'ғайри раҳмати Мо ва бархурдорӣ то ҳангоми марг.'),
(3750, 36, 45, 'Ва он гоҳ ки ба онҳо гуфта шавад, ки аз он чӣ дар пеши рӯй доред ё пушти сар мегузоред, битарсед, шояд бар шумо раҳмат оранд, рӯй бигардонанд.'),
(3751, 36, 46, 'Ва кадом ояте аз оёти Парвардигорашон бар онҳо нозил шавад, албатта аз он рӯй гардонанд.'),
(3752, 36, 47, 'Ва чун гуфта шавад, ки аз он чӣ Худо рӯзиятон кардааст, садақа кунед, кофирон ба мӯъминон гуянд: «Оё касонеро таъом диҳем, ки агар Худо мехост, худ онҳоро таъом медод? Шумо дар гумроҳии ошкор ҳастед».'),
(3753, 36, 48, 'Ва мегӯянд: «Агар рост мегӯед, ин ваъда кай хоҳад буд?»'),
(3754, 36, 49, 'Инҳо интизори як бонги сахтро мебаранд, то ба он ҳангом ки саргарми хусумат ҳастанд, фурӯ гирадашон,'),
(3755, 36, 50, 'ончунон ки тавони васияте надошта бошанд ва натавонанд назди касони худ бозгарданд.'),
(3756, 36, 51, 'Ва дар сур дамида шавад ва онон аз қабрҳо берун оянд ва шитобон ба сӯи Парвардигорашон раванд.'),
(3757, 36, 52, 'Мегӯянд: «Вой бар мо, чӣ касе моро аз хобгоҳҳоямон бархезонд? Ин ҳамон ваъдаи Худои раҳмон аст ва паёмбарон рост гуфта буданд!»'),
(3758, 36, 53, 'Фақат як бонги сахт хоҳад буд, ки ҳама назди Мо ҳозир меоянд.'),
(3759, 36, 54, 'Он рӯз ба кас ситам намешавад. Ва ҷуз монанди коре, кн кардаед, мукофот намебинед.'),
(3760, 36, 55, 'Биҳиштиён он рӯз ба шодмонӣ машғул бошанд.'),
(3761, 36, 56, 'Онҳо ва ҳамсаронашон дар сояҳо бар тахтҳо такя задаанд.'),
(3762, 36, 57, 'Дар он ҷо ҳар мева ва ҳар чизи дигар, ки бихоҳанд, фароҳам аст.'),
(3763, 36, 58, 'Ва саломе, ки сухани Парвардигори меҳрубон аст.'),
(3764, 36, 59, 'Эй гуноҳкорон, имрӯз ҷудо шавед (аз мӯъминон)!'),
(3765, 36, 60, 'Эй фарзандони Одам, оё бо шумо паймон набастам, ки шайтонро напарастед, зеро душмани ошкори шумост?'),
(3766, 36, 61, 'Ва Маро бипарастед, ки роҳи рост ин аст.'),
(3767, 36, 62, 'Бисёре аз шуморо гумроҳ кард. Магар ба ақл дарнамеёфтед?'),
(3768, 36, 63, 'Ин аст он ҷаҳаннаме, ки ба шумо ваъда дода шуда буд.'),
(3769, 36, 64, 'Ба ҷазои куфратон инак дар он дохил шавед!»'),
(3770, 36, 65, 'Имрӯз бар даҳонҳояшон мӯҳр мениҳем. Ва дастҳояшон бо Мо сухан хоҳанд гуфт ва поҳояшон шоҳидӣ хоҳанд дод, ки чӣ мекардаанд.'),
(3771, 36, 66, 'Агар бихоҳем чашмонашонро маҳв мекунем. Пас шитобон оҳанги роҳ кунанд. Аммо куҷоро тавонанд дид?'),
(3772, 36, 67, 'Ва агар бихоҳем,, онҳоро бар ҷояшон масх (тағйир) кунем, ки на тавони он дошта бошанд, ки ба пеш қадам бардоранд ва на бозпас гарданд.'),
(3773, 36, 68, 'Ҳар киро умри дароз диҳем, дар офариниш дигаргунаш кунем. Чаро ақл намекунанд?'),
(3774, 36, 69, 'Ба ӯ шеър наёмӯхтаем ва шеър лоиқи ӯ нест. Он чӣ ба ӯ омухтаем, панду Қуръоне равшангар аст,'),
(3775, 36, 70, 'то мӯъминонро бим диҳад ва сухани ҳақ бар азоби кофирон воҷиб шавад.'),
(3776, 36, 71, 'Оё надидаанд, ки ба яди (дасти) қудрати худ бар онҳо чорпоёнро офаридем ва акнун моликашон ҳастанд?'),
(3777, 36, 72, 'Ва онҳоро ромашон кардем. Бар баъзе савор мешаванд ва аз гӯшти баъзе мехӯранд.'),
(3778, 36, 73, 'Ва онҳоро дар онҷо фоидаҳост ва ошомиданиҳо. Чаро шукр намегӯянд?'),
(3779, 36, 74, 'Ва ба ҷои Оллоҳ худоёне ихтиёр карданд, ба он умед, ки ёрияшон кунанд.'),
(3780, 36, 75, 'Онҳоро қудрати он нест, ки ба ёриашон бархезанд, вале инҳо (кофирон) монанди лашкаре ба хидмати онҳо омодаанд.'),
(3781, 36, 76, 'Суханашон туро андӯҳгин насозад. Мо ҳар чиро пинҳон медоранд, ё ошкор месозанд, медонем.'),
(3782, 36, 77, 'Оё одами, ки акнун хасме ошкор аст, намедонад, ки ӯро аз нутфае офаридаем?'),
(3783, 36, 78, 'Дар ҳоле, ки офариниши худро аз ёд бурдааст, барои мо мисол меорад, ки чӣ касе ин устухонҳои пусидаро зинда мекунад?'),
(3784, 36, 79, 'Бигӯ: «Касе онҳоро зинда мекунад, ки аввалин бор офаридааст ва Ӯ ба ҳар офаринише доност.'),
(3785, 36, 80, 'Он Худое, ки аз дарахти сабз бароятон оташ падид овард ва шумо аз он оташ меафрӯзед».'),
(3786, 36, 81, 'Оё касе, ки осмонҳову заминро офаридааст, наметавонад монандашонро биёфаринад? Оре, метавонад, ки Ӯ офаринандае доност!'),
(3787, 36, 82, 'Чун бихоҳад чизеро биёфаринад, фармонаш ин аст, ки мегӯяд: «Мавҷуд шав!» Пас мавҷуд мешавад.'),
(3788, 36, 83, 'Пок аст он Худое, ки малакути (қудрати) ҳар чизе ба дасти Ӯст ва ҳама ба сӯи Ӯ бозгардонда мешавед!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Савганд ба он фариштагон, ки барои Худо саф бастаанд,'),
(3790, 37, 2, 'савганд ба он фариштагон, ки абрҳоро меронанд,'),
(3791, 37, 3, 'савганд ба он фариштагон, ки зикри Худоро мехонанд,'),
(3792, 37, 4, 'ки Худои шумо Худои яктост.'),
(3793, 37, 5, 'Парвардигори осмонҳову замин аст ва ҳар чӣ дар миёни онҳост. Ва Парвардигори машрикҳост!'),
(3794, 37, 6, 'Мо осмони дунёро ба зинати ситорагон биёростем.'),
(3795, 37, 7, 'Ва аз ҳар шайтони нофармон нигаҳ доштем.'),
(3796, 37, 8, 'То сухани сокинони олами болоро нашнаванд ва аз ҳар сӯй ронда шаванд.'),
(3797, 37, 9, 'То дур гарданд ва барои онҳост азобе доим.'),
(3798, 37, 10, 'Ғайри он шайтон, ки ногаҳон чизе бирабояд ва ногаҳон шӯълаи сӯзандае дунболаш кунад.'),
(3799, 37, 11, 'Пас, аз кофирон бипурс, ки оё офариниши онҳо душвортар аст ё он чӣ Мо офаридаем. Мо онҳоро аз гиле часпанда офаридаем.'),
(3800, 37, 12, 'Ту дар тааҷҷуб шудӣ ва онҳо масхара мекунанд.'),
(3801, 37, 13, 'Ва чун ба онҳо панд дода шавад, панд, намепазиранд.'),
(3802, 37, 14, 'Ва чун ояе бубинанд, аз якдигар мехоҳанд, то онро ба масхара гиранд.'),
(3803, 37, 15, 'Ва мегӯянд: «Ин чизе ғайри ҷодуи ошкор нест,'),
(3804, 37, 16, 'оё вақте ки мо мурдем ва хоку устухон шудем, моро зинда мекунанд?'),
(3805, 37, 17, 'ё падарони моро?»'),
(3806, 37, 18, 'Бигӯ: «Бале, ва шумо хору бечора мешавед!»'),
(3807, 37, 19, 'Ва онон худ хоҳанд дид, ки ба як фармон ҳамаро зинда мекунанд.'),
(3808, 37, 20, 'Мегӯянд: «Вой бар мо, ин ҳамон рӯзи ҷазост!»'),
(3809, 37, 21, 'Ин ҳамон рӯзи довари аст, ки дурӯғаш мепиндоштед.'),
(3810, 37, 22, 'Ононро, ки зулм мекарданд ва ҳамроҳонашонро ва онҳоеро, ки ба ҷои Худои якто мепарастиданд, ҷамъ оваред.'),
(3811, 37, 23, 'Ҳамаро ба роҳи ҷаҳаннам роҳ нишон диҳед.'),
(3812, 37, 24, 'Ва нигаҳ доредашон, бояд бозхост шаванд.'),
(3813, 37, 25, '«Чаро якдигарро ёрӣ намекунед?»'),
(3814, 37, 26, 'На, имрӯз ҳама таслимшавандагонанд.'),
(3815, 37, 27, 'Ва он гоҳ рӯй ба якдигар кунанд ва бипурсанд.'),
(3816, 37, 28, 'Гӯянд: «Шумо будед, ки аз дари некхоҳӣ бар мо медаромадед».'),
(3817, 37, 29, 'Гӯянд: «На, шумо имон наёварда будед.'),
(3818, 37, 30, 'Моро бар шумо ҳеҷ ғолибияте набуд. Шумо худ мардумоне нофармон будед.'),
(3819, 37, 31, 'Гуфтаи Парвардигорамон бар мо воҷид шуд. Акнун бояд бичашем.'),
(3820, 37, 32, 'Мо шуморо гумроҳ кардем ва худ низ гумроҳ будем».'),
(3821, 37, 33, 'Дар он рӯз ҳама дар азоб шариканд.'),
(3822, 37, 34, 'Мо бо гунаҳкорон чунин мекунем.'),
(3823, 37, 35, 'Чун ба онон гуфта мешуд, ки ғайри Худои якто худое нест, такаббур мекарданд'),
(3824, 37, 36, 'ва мегуфтанд: «Оё ба хотири шоири девонае худоёнамонро тарк гӯем?»'),
(3825, 37, 37, 'На, он паёмбар ҳақро овард ва паёмбарони пешинро тасдиқ кард.'),
(3826, 37, 38, 'Шумо азоби дардоварро хоҳед чашид!'),
(3827, 37, 39, 'Дар баробари амалҳоятон ҷазо хоҳед дид.'),
(3828, 37, 40, 'Ғайри бандагони мухлиси Худо,'),
(3829, 37, 41, 'ки онҳоро ризқе муъайян аст.'),
(3830, 37, 42, 'аз меваҳо, ва гиромӣ доштагонанд'),
(3831, 37, 43, 'дар биҳиштҳои пурнеъмат,'),
(3832, 37, 44, 'бар тахтҳое, ки рӯбарӯи ҳаманд.'),
(3833, 37, 45, 'Ва ҷоме аз чашмаи хушгувор миёнашон ба гардиш дарояд,'),
(3834, 37, 46, 'сафед асту нӯшандагонашро лаззатбахш,'),
(3835, 37, 47, 'на дарди сар орад ва на нӯшанда маст шавад.'),
(3836, 37, 48, 'Заноне фарохчашм, ки танҳо ба шавҳарони худ назар доранд, ҳамдами онҳоянд.'),
(3837, 37, 49, 'Ҳамонанди тухми мурғҳое дур аз дастрас.'),
(3838, 37, 50, 'Рӯ ба якдигар кунанд ва гуфтугӯ кунанд.'),
(3839, 37, 51, 'Яке аз онҳо гӯяд: «Маро ҳамнишине буд,'),
(3840, 37, 52, 'ки мегуфт: «Оё ту аз онҳо ҳастӣ, ки тасдиқ мекунанд?'),
(3841, 37, 53, 'Оё он гоҳ, ки мурдему хоку устухон шудем, ҷазоямон медиҳанд?»'),
(3842, 37, 54, 'Гӯянд: «Метавонед аз боло бинигаред?»'),
(3843, 37, 55, 'Аз боло бинигарад ва ӯро дар миёни оташи дӯзах бинад.'),
(3844, 37, 56, 'Гуядаш: «Ба Худо савганд, наздик буд, ки маро ба ҳалокат афканӣ.'),
(3845, 37, 57, 'Ва агар мавҳибати (бахшиши) Парвардигорам набуд, ман низ аз ҳозиршудагони азоб мебудам.'),
(3846, 37, 58, 'Оё моро марге нест,'),
(3847, 37, 59, 'ҷуз он марги нахустин? Ва дигар азобамон намекунанд?»'),
(3848, 37, 60, 'Ҳар оина (албатта) ин аст комёбии бузург!'),
(3849, 37, 61, 'Амалкунандагон бояд барои чунин мукофоте амал кунанд.'),
(3850, 37, 62, 'Оё хӯришро ин беҳтар ё дарахти заққум?'),
(3851, 37, 63, 'Мо он дарахтро барои ибтилои (гирифтории) ситамкорон падид овардаем.'),
(3852, 37, 64, 'Дарахтест, ки аз қаъри ҷаҳаннам мерӯяд,'),
(3853, 37, 65, 'Мевааш монанди сари шайтонҳо аст,'),
(3854, 37, 66, 'дӯзахиён аз он мехӯранд ва шикам пур месозанд'),
(3855, 37, 67, 'ва бар сари он омехтае аз оби сӯзон менушанд.'),
(3856, 37, 68, 'Сипас бозгашташон ба ҳамон ҷаҳаннам аст.'),
(3857, 37, 69, 'Онҳо падарони худро пеш аз худ гумроҳ ёфтанд'),
(3858, 37, 70, 'ва аз паи онҳо шитобон мераванд.'),
(3859, 37, 71, 'Ва пеш аз онҳо бештар пешиниён гумроҳ шуданд.'),
(3860, 37, 72, 'Ва Мо бимдиҳандагоне ба миёнашон фиристодем.'),
(3861, 37, 73, 'Пас бингар, ки саранҷоми он бимёфтагон чӣ гуна буд.'),
(3862, 37, 74, 'Ғайри бандагони мухлиси Худо!'),
(3863, 37, 75, 'Нӯҳ Моро нидо кард ва Мо чӣ нек посухдиҳандае будем!'),
(3864, 37, 76, 'Ӯ ва касонашро аз андӯҳи бузург наҷот додем.'),
(3865, 37, 77, 'Ва фарзандонашро боқӣ гузоштем.'),
(3866, 37, 78, 'Ва номи неки ӯро дар наслҳои баъд боқӣ ниҳодем.'),
(3867, 37, 79, 'Салом бар Нӯҳ бод дар саросари ҷаҳон!'),
(3868, 37, 80, 'Мо некӯкоронро чунин мукофот медиҳем.'),
(3869, 37, 81, 'Ӯ аз бандагони мӯъмини Мо буд.'),
(3870, 37, 82, 'Сипас дигаронро ғарқ сохтем.'),
(3871, 37, 83, 'Иброҳим аз пайравони ӯ буд.'),
(3872, 37, 84, 'Он гоҳ ки бо диле холи аз шубҳа рӯй ба Парвардигораш овард.'),
(3873, 37, 85, 'Ба падараш ва қавмаш гуфт: «Чӣ мепарастед?'),
(3874, 37, 86, 'Оё ба ҷои Худои якто худоёни дурӯғинро мехоҳед?'),
(3875, 37, 87, 'Ба Парвардигори ҷаҳониён чӣ гумон доред?»'),
(3876, 37, 88, 'Нигоҳе ба ситорагон кард'),
(3877, 37, 89, 'ва гуфт: «Ман беморам!»'),
(3878, 37, 90, 'Аз ӯ рӯйгардон шуданд ва бозгаштанд.'),
(3879, 37, 91, 'Пинҳонӣ назди худоёнашон омад ва гуфт: «Чизе намехӯред?'),
(3880, 37, 92, 'Чаро сухан намегӯед?»'),
(3881, 37, 93, 'Ва дар ниҳон дасте бақувват бар онҳо зад.'),
(3882, 37, 94, 'Қавмаш шитобон наздаш омаданд.'),
(3883, 37, 95, 'Гуфт: «Оё чизҳоеро, ки худ метарошед, мепарастед?'),
(3884, 37, 96, 'Худои яктост, ки шумо ва ҳар чӣ месозед, офаридааст».'),
(3885, 37, 97, 'Гуфтанд: «Барояш биное оозед ва дар оташаш андозед».'),
(3886, 37, 98, 'Хостанд то бадандешӣ кунанд, Мо низ онҳоро зердаст (хору мағлуб) гардонидем.'),
(3887, 37, 99, 'Гуфт: «Ман ба сӯи Парвардигорам меравам. Ӯ маро роҳнамоӣ хоҳад кард.'),
(3888, 37, 100, 'Эй Парвардигори ман, маро фарзанде солеҳ ато кун!»'),
(3889, 37, 101, 'Пас ӯро ба писаре бурдбор хушхабар додем.'),
(3890, 37, 102, 'Чун бо падар ба ҷое расид, ки бояд ба кор сар кунанд, гуфт: «Эй писаракам, дар хоб дидаам, ки туро забҳ мекунам. Бингар, ки чӣ меандешӣ». Гуфт: «Эй падар, ба ҳар чӣ фармон шудаӣ, амал кун, ки агар Худо бихоҳад, маро аз собирон (сабркунандагон) хоҳӣ ёфт».'),
(3891, 37, 103, 'Чун ҳар ду таслим шуданд ва ӯро ба пешонӣ хобонид,'),
(3892, 37, 104, 'Мо нидояш додем: «Эй Иброҳим,'),
(3893, 37, 105, 'хобатро ба ҳақиқат паивастӣ», Ва Мо некӯкоронро чунин мукофот медиҳем.'),
(3894, 37, 106, 'Ин озмоише ошкор буд.'),
(3895, 37, 107, 'Ва ӯро ба забҳе бузург бозхаридем'),
(3896, 37, 108, 'Ва номи неки ӯро дар наслҳои баъд боқӣ гузоштем.'),
(3897, 37, 109, 'Салом бар Иброҳим!'),
(3898, 37, 110, 'Мо некӯкоронро инчунин мукофот медиҳем!'),
(3899, 37, 111, 'Ӯ аз бандагони мӯъмини Мо буд.'),
(3900, 37, 112, 'Ӯро ба Исҳок — паёмбаре шоиста мужда додем.'),
(3901, 37, 113, 'Ӯ на Исҳоқро баракат додем. Ва аз фарзандонашон баъзе некӯкор ҳастанд ва баъзе ба ошкоро бар худ ситамкор.'),
(3902, 37, 114, 'Мо ба Мӯсо ва Ҳорун неъмат додем.'),
(3903, 37, 115, 'Он ду ва мардумашонро аз ранҷе бузург наҷот бахшидем.'),
(3904, 37, 116, 'Ононро ёрӣ додем, то ғолиб шуданд.'),
(3905, 37, 117, 'Ва китобе равшангарашон додем.'),
(3906, 37, 118, 'Ва ба роҳи рост ҳидояташон кардем.'),
(3907, 37, 119, 'Ва номи некашонро дар наслҳои баъд боқӣ гузоштем.'),
(3908, 37, 120, 'Салом бар Мӯсо ва Ҳорун!'),
(3909, 37, 121, 'Мо некӯкоронро инчунин мукофот медиҳем!'),
(3910, 37, 122, 'Он ду аз бандагони мӯъмини Мо буданд.'),
(3911, 37, 123, 'Ва Илёс аз паёмбарон буд.'),
(3912, 37, 124, 'Ба мардуми худ гуфт: «Оё аз Худо наметарсед?'),
(3913, 37, 125, 'Оё Баълро ба худои мехонед ва Он беҳтарини офаринандагонро тарк мекунед?'),
(3914, 37, 126, 'Парварднгори шумо ва Парвардигори падаронатон Худои яктост».'),
(3915, 37, 127, 'Пас дурӯғаш бароварданд ва онон аз ҳозиркардагонанд, (дар азоб)'),
(3916, 37, 128, 'Ғайри бандагони мухлиси Худо!'),
(3917, 37, 129, 'Ва номи неки ӯро дар наслҳои баъд боқӣ гузоштем.'),
(3918, 37, 130, 'Салом бар хонадони Илёс!'),
(3919, 37, 131, 'Мо некӯкоронро инчунин подош (мукофот) медиҳем!'),
(3920, 37, 132, 'Ӯ аз бандагони мӯъмини Мо буд.'),
(3921, 37, 133, 'Ва Лут аз паёмбарон буд.'),
(3922, 37, 134, 'Ӯ ва ҳамаи касонашро наҷот додем.'),
(3923, 37, 135, 'Ғайри пиразане, ки бо мардуми шаҳр бимонд.'),
(3924, 37, 136, 'Сипас дигаронро ҳалок кардем.'),
(3925, 37, 137, 'Шумо бар (маскани) онҳо мегузаред бомдодон'),
(3926, 37, 138, 'ва шомгоҳон. Оё ақл намекунед?'),
(3927, 37, 139, 'Ва Юнус аз паёмбарон буд.'),
(3928, 37, 140, 'Чун ба он киштии пур аз мардум гурехт,'),
(3929, 37, 141, 'қуръа заданд ва ӯ дар қуръа мағлуб шуд.'),
(3930, 37, 142, 'Моҳӣ фурӯ бурдаш ва ӯ лоиқи сарзаниш буд.'),
(3931, 37, 143, 'Пас агар аз тасбеҳ гуён намебуд,'),
(3932, 37, 144, 'то рӯзи қиёмат дар шиками моҳӣ мемонд.'),
(3933, 37, 145, 'Пас ӯро, ки бемор буд, ба хушкӣ афкандем.'),
(3934, 37, 146, 'Ва бар болои сараш бӯтаи кадуе рӯёнидем.'),
(3935, 37, 147, 'Ва ӯро ба пайғамбарӣ бар сад ҳазор кас ва бештар фиристодем.'),
(3936, 37, 148, 'Онҳо имон оварданд ва то зинда буданд, бархурдорияшон додем.'),
(3937, 37, 149, 'Аз онҳо бипурс; «Оё духтарон аз они Парвардигори ту бошанд ва писарон аз они онҳо?»'),
(3938, 37, 150, 'Оё вақте ки Мо малоикаро зан меофаридем, онҳо медиданд?'),
(3939, 37, 151, 'Огоҳ бош, ки аз дурӯғгӯияшон аст, ки мегӯянд:'),
(3940, 37, 152, '«Худо соҳиби фарзанд аст». Дурӯғ мегӯянд.'),
(3941, 37, 153, 'Оё Худо духтаронро бар писарон бартарӣ дод?'),
(3942, 37, 154, 'Шуморо чӣ мешавад? Чӣ гуна ҳукм мекунед?'),
(3943, 37, 155, 'Оё намеандешед?'),
(3944, 37, 156, 'Ё бар даъвои худ далели равшане доред?'),
(3945, 37, 157, 'Агар рост мегӯед, китобатонро биёваред.'),
(3946, 37, 158, 'Ва миёни Худову ҷинниён хешовандӣ қоил шуданд ва ҷинниён медонанд, ки ҳозиркардашудагонанд. (дар азоб).'),
(3947, 37, 159, 'Аз он сифат, ки меоварданд, Худо пок аст,'),
(3948, 37, 160, 'ғайри бандагони мухлиси Худо!'),
(3949, 37, 161, 'Шумо ва бутоне, ки мепарастед,'),
(3950, 37, 162, 'натавонистед гумроҳ кунед,'),
(3951, 37, 163, 'магар он касро, ки роҳии ҷаҳаннам бошад.'),
(3952, 37, 164, '«Ва ҳар кас аз мо ҷое муъайян дорад.'),
(3953, 37, 165, 'Албатта мо сафзадагонем.'),
(3954, 37, 166, 'Ва албатта мо тасбеҳгӯяндагонем».'),
(3955, 37, 167, 'Агарчи мегуфтанд:'),
(3956, 37, 168, '«Агар аз пешиниён назди мо китобе монда буд,'),
(3957, 37, 169, 'мо аз бандагони мухлиси Худо мебудем».'),
(3958, 37, 170, 'Онҳо ба он китоб кофир шуданд ва ба зуди, ки хоҳанд фаҳмид.'),
(3959, 37, 171, 'Мо дар бораи бандагонамон, ки ба пайғамбарӣ мефиристем, аз пеш тасмим гирифтаем,'),
(3960, 37, 172, 'ки албатта онон ёрӣ мешаванд.'),
(3961, 37, 173, 'Ва лашкари Мо худ ғолибанд.'),
(3962, 37, 174, 'Пас то муддате аз онҳо рӯй бигардон.'),
(3963, 37, 175, 'Оқибаташонро бубин, онҳо низ хоҳанд дид.'),
(3964, 37, 176, 'Оё азоби Моро ба шитоб металабанд?'),
(3965, 37, 177, 'Чун азоб ба майдонашон фарояд, ин бимдодашудагон чӣ бомдоди баде хоҳанд дошт.'),
(3966, 37, 178, 'Аз онҳо то чанде рӯй бигардон.'),
(3967, 37, 179, 'Пас оқибаташонро бубин, онҳо низ хоҳанд дид.'),
(3968, 37, 180, 'Муназзаҳ (покаст) аст Парвардигори ту — он Парвардигори пирӯзманд — аз ҳар чӣ ба васфи Ӯ мегӯянд!'),
(3969, 37, 181, 'Ва салом бар паёмбарон!'),
(3970, 37, 182, 'Ва ҳамд аз они Парвардигори ҷаҳониён аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Сод. Савганд ба Қуръони шарифи соҳиби панд,'),
(3972, 38, 2, 'ки кофирон ҳамчунон дар саркашиву зиддият'),
(3973, 38, 3, 'Чӣ бисёр мардумеро, ки пеш аз онҳо ба ҳалокат расонидем. Онон фарёд мебароварданд, вале гурезгоҳе набуд.'),
(3974, 38, 4, 'Дар тааҷҷуб шуданд аз ин ки бимдиҳандае аз миёни худашон бархост. Ва кофирон гуфтанд: «Ин ҷодугаре дурӯғгӯст.'),
(3975, 38, 5, 'Оё ҳамаи худоёнро як худо гардонида аст? Ва ин чизе аҷиб аст!»'),
(3976, 38, 6, 'Бузургонашон ба роҳ афтоданду гуфтанд: «Биравед ва бар парастиши худоёни худ пойдорӣ кунед, ки ин аст чизе, ки аз шумо хоста шуда.'),
(3977, 38, 7, 'Мо дар ин охирин дин чунин сухане нашунидаем ва ин ғайри дурӯғ ҳеҷ нест.'),
(3978, 38, 8, 'Оё аз миёки ҳамаи мо ваҳй бар ӯ нозил шудааст?» Балки онҳо аз ваҳйи Ман дар шубҳаанд ва ҳанӯз азоби Маро начашидаанд.'),
(3979, 38, 9, 'ё он ки хазинаҳои раҳмати Парвардигори ғолиби бахшандаат дар назди онҳост?'),
(3980, 38, 10, 'Ё фармонравоии осмонҳову замин ва ҳар чӣ миёни он дуст, аз они онҳост? Пас бо нардбонҳое худро ба осмон кашанд.'),
(3981, 38, 11, 'Он ҷо лашкарест ночиз аз чанд гурӯҳи шикастхӯрдаи (бар зидди пайрамбарон) ҷамъшуда.'),
(3982, 38, 12, 'Пеш аз онҳо қавми Нӯҳ ва Од ва Фиръавн, ки мардумро ба чаҳормех мекашид, паёмбаронро дурӯғ мебароварданд.'),
(3983, 38, 13, 'Ва низ қавми Самуд ва қавми Лут ва мардуми Айка аз он ҷамоъатҳо буданд.'),
(3984, 38, 14, 'Аз инҳо касе набуд, ки паёмбаронро дурӯғ набаровард ва уқубати Ман воҷиб омад.'),
(3985, 38, 15, 'Инҳо низ бонге сахтро интизори доранд, чунон ки онро дигар таваққуфе (бозистодане) набошад.'),
(3986, 38, 16, 'Ва гуфтанд: «Эй Парвардигори мо, номаи аъмоли моро пеш аз фаро расидани рӯзи ҳисоб ба дасти мо бидеҳ!»'),
(3987, 38, 17, 'Бар ҳарчӣ мегӯянд, сабр кун. Ва аз бандаи Мо Довуди қудратманд, ки ҳамеша ба даргоҳи Мо тавба мекард, ёд овар!'),
(3988, 38, 18, 'Мо кӯҳҳоро ром кардем ва кӯҳҳо ҳар шомгоҳу бомдодон бо ӯ тасбеҳ мекарданд.'),
(3989, 38, 19, 'Ва паррандагон бар ӯ ҷамъ меомаданд. Ҳама фармонбари ӯ буданд.'),
(3990, 38, 20, 'Фармонравоияшро устуворӣ бахшидем ва ӯро ҳикмату фасоҳат дар сухан ато кардем.'),
(3991, 38, 21, 'Оё хабари он хусуматкунандагонро шунидаӣ, он гоҳ ки аз девори қаср ба масҷид даромаданд?'),
(3992, 38, 22, 'Бар Довуд дохил шуданд. Довуд аз онҳо тарсид. Гуфтанд: «Матарс, мо ду даъвогар ҳастем, ки яке бар дигаре ситам кардааст. Миёни мо ба ҳақ довари кун ва пой аз адолат берун манеҳ ва моро ба рохи рост ҳидоят кун.'),
(3993, 38, 23, 'Ин бародари ман аст. Ӯро наваду нӯҳ меш (гӯсфанд) аст ва маро як меш. Мегӯяд: «Онро ҳам ба ман бидеҳ» ва дар даъво бар ман ғалаба ёфтааст».'),
(3994, 38, 24, 'Довуд гуфт: «Ӯ, ки меши туро аз ту мехоҳад то ба мешҳои худ бияфзояд, ба ту ситам мекунад. Ва бисёре аз шарикон ғайри касоне, ки имон овардаанд ва корҳои шоиста кардаанд — ва инҳо низ андак ҳастанд — бар якдигар ситам мекунанд». Ва Довуд донист, ки ӯро озмудем. Пас аз Парвардигораш бахшоиш хост ва ба руқӯъ дарафтоду тавба кард. (Саҷда).'),
(3995, 38, 25, 'Мо ин хатояшро бахшидем. Ӯро ба даргоҳи Мо тақарруб (наздикӣ) аст ва бозгаште некӯ.'),
(3996, 38, 26, 'Эй Довуд, Мо туро халифаи рӯи замин гардонидем. Дар миёни мардум ба ҳақ довари кун ва аз паи ҳавои нафс марав, ки туро аз роҳи Худо бероҳ созад. Онон, ки аз роҳи Худо каҷрав шаванд, ба он сабаб, ки рӯзи ҳисобро аз ёд бурдаанд, ба азобе сахт гирифтор мешаванд.'),
(3997, 38, 27, 'Мо ин осмону замин ва он чиро, ки миёни онҳост, ба ботил наёфаридаем. Ин гумони касонест, ки кофир шуданд. Пас вой бар кофирон аз оташ.'),
(3998, 38, 28, 'Оё касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, монанди фасокунандагон дар замин қарор хоҳем дод? Ё парҳезгоронро чун гуноҳкорон?'),
(3999, 38, 29, 'Китобе муборак аст, ки онро бар ту нозил кардаем, то дар оятҳояш бияндешанд ва хирадмандон аз он панд гиранд.'),
(4000, 38, 30, 'Сулаймонро ба Довуд ато кардем. Чӣ бандае некӯе буд ва рӯй ба Худо дошт.'),
(4001, 38, 31, 'Он гоҳ ки ба ҳангоми аср аспони тезравро, ки истода буданд, ба ӯ нишон доданд,'),
(4002, 38, 32, 'гуфт: «Ман дӯстии ин аспонро бар ёди Парвардигорам бигзидам, то офтоб дар пардаи ғуруб пушида шуд (яъне намози аср қазо шуд),'),
(4003, 38, 33, 'он аспонро назди ман бозгардонед». Пас ба буридани соқҳо ва гарданашон оғоз кард.'),
(4004, 38, 34, 'Мо Сулаймонро озмудем ва бар тахти ӯ ҷасадеро афкандем ва ӯ рӯй ба Худо овард.'),
(4005, 38, 35, 'Гуфт: «Эй Парвардигори ман, маро бибахш ва маро мулке ато кун, ки пас аз ман касе сазовори он набошад, ки ту бахшояндаӣ».'),
(4006, 38, 36, 'Пас бодро роми ӯ кардем, ки ба нармӣ ҳар ҷо, ки мехост, ба фармони ӯ мерафт.'),
(4007, 38, 37, 'Ва девонро, ки ҳам бинокор буданду ҳам ғаввос (шиновар).'),
(4008, 38, 38, 'Ва гурӯҳе дигарро, ки ҳама баста дар занҷири ӯ буданд.'),
(4009, 38, 39, 'Ин атои беҳисоби Мост, хоҳӣ онро бидеҳ ва хоҳӣ нигаҳ дор.'),
(4010, 38, 40, 'Ӯрост назди Мо тақаррубу (наздик шудану) бозгаште некӯ.'),
(4011, 38, 41, 'Ва аз бандаи Мо Айюб ёд кун, он гоҳ ки Парвардигорашро нидо кард, ки маро шайтон ба ранҷу азоб афканда аст.'),
(4012, 38, 42, '«Поятро бар замин бикӯб: ин обест барои шустушӯй ва сард барои ошомидан».'),
(4013, 38, 43, 'Ва ба ӯ хонаводааш ва ҳамчанди он аз дигар ёронро ато кардем ва ин худ раҳмате аз Мо буд ва барои хирадмандон андарзе (панде).'),
(4014, 38, 44, '«Дастае аз чубҳои борик ба даст гир ва бо он (занатро) бизан ва савганди худро машикан». Ӯро бандае собир ёфтем, Ӯ, ки ҳамеша рӯй ба даргоҳи Мо дошт, чӣ некӯ бандае буд!'),
(4015, 38, 45, 'Бандагони Мо Иброҳиму Исҳоқ ва Яъқуб он мардони қудратманду бо илмро ёд кун!'),
(4016, 38, 46, 'Ононро хислати покдилӣ бахшидем, то ёди қиёмат кунанд.'),
(4017, 38, 47, 'Онҳо дар назди Мо интихобшудагону неконанд.'),
(4018, 38, 48, 'Ва Исмоил ва Алясаъ ва Зулкифлро ёд кун, ки ҳама аз неконанд.'),
(4019, 38, 49, 'Ин Қуръон пандест ва парҳезгоронро бозгаште некӯст;'),
(4020, 38, 50, 'бихиштҳои ҷовидон, ки дари он ба рӯяшон кушода аст,'),
(4021, 38, 51, 'Дар он ҷо такя задаанд ва ҳар гуна меваву нӯшиданӣ, ки бихоҳанд, металабанд.'),
(4022, 38, 52, 'Заноне ҳамсол аз он гуна, ки ғайри шӯи худ ба касе назар надоранд, назди онҳоянд.'),
(4023, 38, 53, 'Ин аст он чизҳое, ки барои рӯзи ҳисоб ба шумо ваъда додаанд.'),
(4024, 38, 54, 'Ин ризқи Мост, ки поён наёфтанист,'),
(4025, 38, 55, 'чунин аст. Ва саркашонро бадтарин бозгаштхост.'),
(4026, 38, 56, 'Ба ҷаҳаннам мераванд, ки бад ҷойгоҳест.'),
(4027, 38, 57, 'Бояд оби ҷӯшону чирк ва хунобаи дӯзахиёнро бинӯшанд.'),
(4028, 38, 58, 'Ва азобҳои дигар аз ҳар навъ.'),
(4029, 38, 59, 'Ин гурӯҳ бо шумо (эй гумроҳкунандаҳо) ба оташ медароянд. Хушомадашон мабод, ки ба оташ меафтанд.'),
(4030, 38, 60, 'Гӯянд: «На, хушомад шуморо набод. Шумо ин азобро пешопеш барои мо фиристода будед, чӣ бад ҷойгоҳест!»'),
(4031, 38, 61, 'Гӯянд: «Эй Парвардигори мо, ҳар кас, ки ин азобро пешопеш барои мо омода кардааст, азобашро дар оташ дучандон зиёда кун!»'),
(4032, 38, 62, 'Ва гӯянд: «Чаро мардонеро, ки аз ашрор (бадкорон). мешумурдем, акнун намебинем?'),
(4033, 38, 63, 'Ононро ба масхара мегирифтем. Оё аз назарҳо дур мондаанд?»'),
(4034, 38, 64, 'Ин баҳсу ҷидоли аҳли ҷаҳаннам бо якдигар чизест, ки ба ҳақиқат воқеъ шавад.'),
(4035, 38, 65, 'Бигӯ: «Фақат ҳамин аст, ки ман бимдиҳандае ҳастам ва ҳеҷ худое ғайри Худои яктои каҳҳор нест.'),
(4036, 38, 66, 'Он пирӯзманду бахшоянда, Парвардигори осмонҳову замин ва ҳар чӣ миёни онҳост».'),
(4037, 38, 67, 'Бигӯ: «Ин хабаре бузург аст,'),
(4038, 38, 68, 'ки шумо аз он рӯй мегардонед,'),
(4039, 38, 69, 'Ҳангоме ки бо якдигар ҷидол (баҳс) мекарданд, ман хабаре аз сокинони олами боло надоштам.'),
(4040, 38, 70, 'Танҳо аз он рӯй ба ман ваҳй мешавад, ки бимдиҳандае равшангар ҳастам».'),
(4041, 38, 71, 'Парвардигорат ба фариштагон гуфт: «Ман башареро аз гил меофаринам.'),
(4042, 38, 72, 'Чун тамомаш кардам ва дар он аз рӯҳи худ дамидам, ҳама саҷдааш кунед!»'),
(4043, 38, 73, 'Ҳама фариштагон саҷда карданд,'),
(4044, 38, 74, 'Ғайри Иблис, ки саркашӣ карду аз кофирон шуд.'),
(4045, 38, 75, 'Гуфт: «Эй Иблис, чӣ чиз туро аз саҷда кардан дар баробари он чӣ Ман бо ду дасти худ офаридаам, манъ кард? Оё бузургӣ намудӣ ё мақоме арҷманд доштӣ?»'),
(4046, 38, 76, 'Гуфт: «Ман аз ӯ беҳтарам. Маро аз оташ офаридаи ва ӯро аз гил».'),
(4047, 38, 77, 'Гуфт: «Аз ин ҷо берун шав, ки ту рондашудаӣ.'),
(4048, 38, 78, 'Ва то рӯзи қиёмат лаънати Ман бар туст».'),
(4049, 38, 79, 'Гуфт: «Эй Парвардигори ман, маро то рӯзе, ки аз нав зинда шаванд, мӯҳлат деҳ»'),
(4050, 38, 80, 'Гуфт: «Ту аз мӯҳлатёфтагонӣ,'),
(4051, 38, 81, 'то он рӯзи муъайяни маълум».'),
(4052, 38, 82, 'Гуфт: «Ба иззати Ту савганд, ки ҳамагонро гумроҳ кунам,'),
(4053, 38, 83, 'ғайри онҳо, ки аз бандагони мухлиси Ту бошанд».'),
(4054, 38, 84, 'Гуфт: «Ҳақ аст ва он чӣ мегӯям, рост аст,'),
(4055, 38, 85, 'ки ҷаҳаннамро аз туву аз ҳамаи пайравонат пур кунам».'),
(4056, 38, 86, 'Бигӯ: «Ман аз шумо музде наметалабам ва нестам аз онон, ки ба дурӯғ чизе бар худ мебофанд».'),
(4057, 38, 87, 'Ва ии ғайри панде барои мардуми ҷаҳон нест.'),
(4058, 38, 88, 'Ва ту баъд аз ин аз хабари он огох хоҳӣ шуд!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Нозил шудани ин китоб аз ҷониби Худои пирӯзманди ҳаким аст.'),
(4060, 39, 2, 'Мо ин китобро ба ростӣ бар ту нозил кардем (фиристодем). Пас Худоро бипараст ва дини худ барои Ӯ холис гардон!'),
(4061, 39, 3, 'Огоҳ бош, ки дини холис аз они Худост ва онон, ки ғайри Ӯ дигареро ба худои гирифтанд, гуфтанд: «Инонро аз он рӯ мепарастем, то василаи наздикии мо ба Худои якто шаванд. Ва Худо дар он чӣ ихтилоф мекунанд, миёнашон ҳукм хоҳад кард. Худо онро, ки дурӯғгӯву носипос бошад, ҳидоят намекунад.'),
(4062, 39, 4, 'Агар Худо мехост, ки барои худ фарзанде баргирад, аз миёни махлуқоти худ ҳар чиро, ки мехост, интихоб мекард. Пок аст. Ӯст Худои яктои қаҳҳор!'),
(4063, 39, 5, 'Осмонҳову заминро ба ҳақ биёфарид. Шабро бар рӯз дохил мекунад ва рӯзро бар шаб. Ва офтобу моҳро ром гардонид ва ҳар як то замоне муъайян дар ҳаракатанд. Огоҳ бош, ки Ӯст пирӯзманду омурзанда!'),
(4064, 39, 6, 'Шуморо аз як тан биёфарид. Ва аз он як тан занашро офарид. Ва бароятон аз чорпоён ҳашт чуфт биёфарид. Шуморо дар шиками модаратон дар чанд марҳала дар даруни торикиҳои сегона офариниш бахшид. Ин аст Худои якто, Парвардигори шумо. Фармонравоӣ аз они Ӯст. Худое ҷуз Ӯ нест. Пас чӣ гуна рӯйгардонатон месозанд?'),
(4065, 39, 7, 'Агар носипосӣ кунед, Худо аз шумо беҳоҷат аст ва носипосиро (ношукриро) барои бандагонаш намеписандад. Меписандад, ки сипосгузор бошед. Ва ҳеҷ кас бори гуноҳи дигареро бар душ намекашад. Бозгашти ҳамаи шумо назди Парвардигоратон аст. Ӯ шуморо аз корҳое, ки мекардаед, огоҳ мекунад, Ӯ ба он чӣ дар дилҳо мегузарад, огоҳ аст.'),
(4066, 39, 8, 'Чун ба одамӣ дарду ранҷе бирасад, ба Парвардигораш рӯй меоварад ва Ӯро мехонад. Он гоҳ чун ба ӯ неъмате бахшад, ҳамаи он дуъоҳоро, ки пеш аз ин карда буд, аз ёд мебарад ва барои Худо ҳамтоёне қарор медиҳад, то мардумро аз тариқи Ӯ гумроҳ кунанд. Бигӯ: «Андаке аз куфрат баҳраманд шав, ки ту аз дӯзахиён хоҳӣ буд!»'),
(4067, 39, 9, 'Оё он кас, ки дар ҳамаи соъати шаб ба ибодат пардохта ё дар суҷуд аст ё дар қиём ва аз охират бимнок аст ва ба раҳмати Парвардигораш умедвор аст, бо он ки чунин нест, яксон аст? Бигӯ: «Оё онҳое, ки медонанд бо онҳое, ки намедонанд, баробаранд?» Танҳо хирадмандон панд мегиранд.'),
(4068, 39, 10, 'Бигӯ: «Эй бандагони Ман, ки имон овардаед, аз Парвардигоратон битарсед. Барои онон, ки дар ҳаёти инҷаҳонӣ некӣ кардаанд, подоши (ҷазои) нек аст. Ва замини Худо паҳновар аст. Музди собирон беҳисобу комил адо мешавад!»'),
(4069, 39, 11, 'Бигӯ: «Ман амр шудаам, ки Худоро бипарастам ва барои Ӯ дар дин ихлос кунам.'),
(4070, 39, 12, 'Ва маро фармудаанд, ки нахустини мусалмонон бошам».'),
(4071, 39, 13, 'Бигӯ: «Агар Парвардигорамро нофармонӣ кунам, аз азоби он рӯзи бузург метарсам».'),
(4072, 39, 14, 'Бигӯ: «Худоро мепарастам ва барои Ӯ дар дини худ ихлос меварзам.'),
(4073, 39, 15, 'Бипарастед ҳар чизи дигареро ҷуз Ӯ». Бигӯ: «Зиёнкунандагон касоне ҳастанд, ки дар рӯзи қиёмат худ ва хонадонашонро аз даст бидиҳанд. Бахуш бошед, ки ин зиёне ошкор аст.'),
(4074, 39, 16, 'Болои сарашон табақоти оташ аст ва дар зери пояшон табақоти оташ». Ин чизест, ки Худо бандагони худро бо он метарсонад. Пас, эй бандагони Ман, аз Ман битарсед!'),
(4075, 39, 17, 'Ва касонеро, ки аз парастиши бутон парҳез кардаанд ва ба Худо рӯй овардаан. д, башорат (хабари хуш) аст. Пас бандагони Маро башорат деҳ:'),
(4076, 39, 18, 'он касоне, ки ба сухан гӯш медиҳанд ва аз беҳтарини он пайравӣ мекунанд, онҳоянд касоне, ки Худо ҳидояташон карда ва онҳо хирадмандонанд.'),
(4077, 39, 19, 'Оё касеро, ки ҳукми азоб бар ӯ бароварда шуда, ту метавонӣ ӯро, ки дар оташ аст, бираҳонӣ?'),
(4078, 39, 20, 'Аммо барои онон, ки аз Худо метарсанд, ғурфаҳоест (иморатҳоест) бар болои ҳам сохта, ки аз зерашон ҷӯйборон равон аст. Ин ваъдаи Худост ва Худо ваъдаи худ хилоф нахоҳад кард!'),
(4079, 39, 21, 'Оё надидаӣ, ки Худо аз осмон борон фиристод ва онро чун чашмасорҳое дар замин равон гардонид, он гоҳ ба он киштаҳои рангоранг бирӯёнид, сипас ҳама хушк мешаванд ва мебинӣ, ки зард шудаанд, он гоҳ хурдашон месозад? Албатта хирадмандонро дар он пандест!'),
(4080, 39, 22, 'Оё касе, ки Худо дилашро бар рӯи ислом кушод ва ӯ дар партави нури Парвардигораш ҷой дорад, ҳамонанди касест, ки имон надорад? Пас вой бар сахтдилоне, ки ёди Худо дар дилҳояшон роҳ надорад, ки дар гумроҳии ошкор ҳастанд.'),
(4081, 39, 23, 'Худо беҳтарин суханро нозил кардааст. Китобе муташобеҳ ва дуто-дуто, ки аз тиловати он касонеро, ки аз Парвардигорашон метарсанд, аз тарс тан биларзад. Сипас тану ҷонашон ба ёди Худо биёромад. Ин роҳи Худост, ки ҳар киро бихоҳад, ба он роҳнамоӣ мекунад ва ҳар киро Худо гумроҳ кунад, ӯро ҳеҷ роҳнамое нахоҳад буд!'),
(4082, 39, 24, 'Оё он кас, ки дар рӯзи қиёмат бо чеҳраи худ азоби сахти қиёматро аз худ бозмедорад, монанди касест ки биҳиштист? Ба ситамкорон гуфта мешавад: «Ба кайфари (ҷазои) корҳое, ки мекардаед, азобро бичасшед!»'),
(4083, 39, 25, 'Пешиниёнашон дурӯғ бароварданд ва азоб аз ҷое, ки намедонистанд, бар сарашон расид.'),
(4084, 39, 26, 'Худо дар ин ҷаҳон расвояшон сохт, вале агар бидонанд, азоби охират бузургтар аст.'),
(4085, 39, 27, 'Мо дар ни Қуръон барои мардум ҳар гуна мисоле овардем, шояд панд гиранд.'),
(4086, 39, 28, 'Қуръоне ба забони арабӣ бе ҳеҷ (нуқсону) каҷӣ. Шояд парҳезгорӣ кунанд,'),
(4087, 39, 29, 'Худо масале мезанад: «Мардеро, ки чанд тан дар ӯ шариканд ва бар сари ӯ ихтилоф доранд. Ва марде, ки танҳо аз они яке бошад. Оё ин ду бо ҳам баробаранд? Шукру сипос Худоро. На, бештарашон намедонанд.'),
(4088, 39, 30, 'Ту мемирӣ ва онҳо низ мемиранд.'),
(4089, 39, 31, 'Сипас ҳама дар рӯзи қиёмат назди Парвардигоратон бо якдигар ҳусумат хоҳед кард.'),
(4090, 39, 32, 'Пас кист ситамкортар аз он, ки бар Худо дурӯғ мебандад ва сухани ростеро, ки бар ӯ омадааст, дурӯғ мебарорад. Оё кофиронро дар ҷаҳаннам ҷойгоҳе нест?'),
(4091, 39, 33, 'Ва касе, ки сухани рост овард ва тасдиқаш кард, онон парҳезгоронанд.'),
(4092, 39, 34, 'Барояшон ҳар чӣ бихоҳанд, дар назди Парвардигорашон муҳайёст. Ин аст подоши (мукофоти) некӯкорон,'),
(4093, 39, 35, 'то Худо бадтарин амалҳоеро, ки кардаанд, аз онон нобуд кунад ва ба беҳтар аз он чӣ мекардаанд, мукофоташон диҳад.'),
(4094, 39, 36, 'Туро ба касоне, ки ғайри Худо ҳастанд, метарсонанд. Оё Худо барои нигаҳдории бандааш кофӣ нест? Ва ҳар касро, ки Худо гумроҳ созад, ҳеҷ роҳнамое нахоҳад буд.'),
(4095, 39, 37, 'Ҳар касро, ки Худо роҳнамоӣ кунад, гумроҳкунандае нест. Оё Худо ғолибу интиқомгиранда нест?'),
(4096, 39, 38, 'Агар аз онҳо бипурсӣ: «Чй касе осмонҳову заминро офаридааст?» Хоҳанд гуфт: «Худои якто». Бигӯ: «Пас инҳоеро, ки ғайри Ӯ мепарастед, чӣ гуна мебинед? Агар Худои якто бихоҳад ба ман ранҷе бирасонад, оё инҳо метавонанд он ранҷро дафъ кунанд? Ё агар бихоҳад ба ман раҳмате ато кунад, метавонанд он раҳматро аз ман боздоранд?» Бигӯ; «Худо барои ман бас аст. Таваккалкунандагон ба Ӯ таваккал мекунанд».'),
(4097, 39, 39, 'Бигӯ: «Эй қавми ман, мувофиқи имкони худ амал кунед. Ман низ амал мекунам. Ва ба зуди хоҳед донист, ки'),
(4098, 39, 40, 'чӣ касе ба азобе, ки ӯро хор месозад, гирифтор мешавад ё азоби ҷовид бар сари ӯ фуруд меояд».'),
(4099, 39, 41, 'Мо ин китобро ба ҳақ барои ҳидояти мардум бар ту нозил кардем. Пас ҳар кас, ки ҳидоят ёфт, ба нафъи худи ӯст ва ҳар кӣ гумроҳ шуд, ба зиёни худ ба гумроҳӣ афтода ва ту вакили онҳо нестӣ.'),
(4100, 39, 42, 'Худо ҷонҳоро ба ҳангоми мурданашон мегирад ва низ ҷони касонеро, ки дар хоби худ намурдаанд. Ҷонҳоеро, ки ҳукми марг бар онҳо ронда шуда, нигаҳ медорад ва дигаронро то замоне, ки муъайян аст, бозмефиристад. Дар ин ибратҳост барои онҳое, ки меандешанд.'),
(4101, 39, 43, 'Оё ғайри Худо шафеъоне ихтиёр карданд? Бигӯ: «Хатто агар он шафеъон қудрат ба коре надошта бошанд ва чизеро дарнаёбанд?»'),
(4102, 39, 44, 'Бигӯ: «Шафоъат саросар аз они Худост. Аз они Ӯст фармонравоии осмонҳову замин. Сипас ҳама ба назди Ӯ бозгардонда мешавед».'),
(4103, 39, 45, 'Чун Худоро ба яктоӣ ёд кунанд, дилҳои он касон, ки ба қиёмат имон наёвардаанд, нафрат гирад ва чун номи дигаре ғайри Ӯ бурда шавад, шодмон шаванд.'),
(4104, 39, 46, 'Бигӯ: «Бор Худое, Туӣ офаринандаи осмонҳову замин, донои ниҳону ошкор, Ту миёни бандагонат дар ҳар чӣ дар он ихтилоф (зиддият) мекардаанд, довари хоҳӣ кард».'),
(4105, 39, 47, 'Агар ҳар чӣ дар рӯи замин аст ва ҳамонанди он аз они ситамкорон мебуд, дар рӯзи қиёмат худро бо он аз азоби дарднок бозмехариданд. Ва аз Худо барояшон чизҳое ошкор мешуд, ки ҳаргиз ҳисобашро намекарданд.'),
(4106, 39, 48, 'Ҷазои бади амалҳое, ки мекарданд, барояшон ошкор шуд ва он чӣ масхарааш мекарданд, гирдорирдашонро бигирифт.'),
(4107, 39, 49, 'Чун одамиро ранҷе расад, Моро мехонад ва чун аз ҷониби худ неъмате арзонияш дорем, гӯяд: «Ба сабаби доноиям ин неъматро ба ман додаанд». Ин озмоише бошад, вале бештаринашон намедонанд.'),
(4108, 39, 50, 'Ин сухане буд, ки пешиниёнашон ҳам мегуфтанд, вале ҳар чӣ ҷамъ оварда буданд, ба ҳолашон нафъ накард.'),
(4109, 39, 51, 'Уқубати амалҳояшон домангирашон шуд. Ва аз ин миён ба онҳо, ки роҳи куфр пеш гирифтаанд, уқубати амалҳояшон хоҳад расид ва наметавонанд аз Мо бигурезанд.'),
(4110, 39, 52, 'Оё ҳанӯз надонистаанд, ки Худост, ки рӯзии ҳар касро, ки бихоҳад афзун месозад ё тангрӯзиаш мекунад? Ва дар ин худ ибратҳост барои мардуме ки имон меоваранд.'),
(4111, 39, 53, 'Бигӯ: «Эй бандагони Ман, ки бар зиёни худ исроф кардаед, аз раҳмати Худо ноумед нашавед. Зеро Худо ҳамаи гуноҳонро мебахшояд. Ӯст бахшояндаву меҳрубон!'),
(4112, 39, 54, 'Пеш аз он ки азоб фаро расад ва касе ба ёриатон барнахезад, ба Парвардигоратон рӯй оред ва ба Ӯ таслим шавед.'),
(4113, 39, 55, 'Ва пеш аз он ки баногоҳ ва бехабар азоб бар шумо фуруд ояд, ба беҳтарин чизе, ки аз ҷониби Парвардигоратон нозил шудааст, пайравӣ кунед,'),
(4114, 39, 56, 'то касе нагӯяд: «Эй ҳасрато бар ман, ки дар кори Худо кутоҳӣ кардам ва аз масхаракунандагон будам».'),
(4115, 39, 57, 'Ё бигӯяд: «Агар Худо маро ҳидоят мекард, ман аз парҳезгорон мебудам».'),
(4116, 39, 58, 'Ё чун азобро бубинад, бигӯяд: «Агар бори дигар ба дунё бозмегардидам, аз некӯкорон мешудам!»'),
(4117, 39, 59, 'Оре, оёти ман барои ту нозил шуд ва ту гарданкашӣ кардӣ ва аз кофирон буди.'),
(4118, 39, 60, 'Дар рӯзи қиёмат касонеро, ки ба Худо дурӯғ бастаанд, мебинӣ, рӯяшон сиёҳ шудааст. Оё мутакаббиронро дар ҷаҳаннам ҷойгоҳе нест?'),
(4119, 39, 61, 'Худо парҳезгоронро ба сабаби роҳи наҷотёбӣ, ки дар пеш гирифта буданд, мераҳонад. Ба онҳо ҳеҷ бадӣ нарасад ва аз андӯҳ дур бошанд.'),
(4120, 39, 62, 'Худост, ки офаридгори ҳар чизест ва Ӯ нигаҳбони ҳар чизе.'),
(4121, 39, 63, 'Калидҳои осмонҳову замин назди Ӯст ва онҳо, ки ба оёти Худо кофир шудаанд, зиёндидагон ҳастанд.'),
(4122, 39, 64, 'Бигӯ: «Эй нодонон, оё маро фармон медиҳед, ки ғайр аз Худоро бипарастам?»'),
(4123, 39, 65, 'Ба ту ва паёмбарони пеш аз ту ваҳй шудааст, ки агар ширк биёваред, амалҳоятон ночиз гардад ва худ аз зиёнкунандагон хоҳед буд.'),
(4124, 39, 66, 'Балки Худоро бипараст ва шукргузори Ӯ бош!'),
(4125, 39, 67, 'Худоро нашинохтанд, ончунон ки лоиқи шинохти Ӯст. Ва дар рӯзи қиёмат замин якҷо дар қабзаи Ӯст ва осмонҳо дар ҳам печида дар яди қудрати Ӯ. Пок аст ва бартар аз ҳар чӣ шарики Ӯ мепиндоранд.'),
(4126, 39, 68, 'Ва дар сӯр дамида шавад. Пас ҳар кӣ дар осмонҳо ва ҳар кӣ дар замин аст, — ғайри онҳо, ки Ӯ бихоҳад, — беҳуш мешаванд. Ва бори дигар дар он дамида шавад, ногаҳон аз ҷой бармехезанд ва менигаранд.'),
(4127, 39, 69, 'Ва замин ба нури Парвардигораш равшан шавад ва номаҳои аъмолро биниҳанд ва паёмбарону гувоҳонро биёваранд ва дар миёни мардум ба ҳақ довари шавад ва бар касе ситаме наравад.'),
(4128, 39, 70, 'Ҷазои ҳар кас баробари кирдораш ба тамомӣ адо шавад, дар ҳоле ки Худо ба корҳое, ки мекардаанд, огоҳтар аст.'),
(4129, 39, 71, 'Ва кофиронро гурӯҳ-гурӯҳ ба ҷаҳаннам биронанд. Чун ба ҷаҳаннам расанд, дарҳояш кушода шавад ва нигаҳбонони оташ гӯянд: «Оё паёмбароне аз худи шумо бар шумо фиристода нашудаанд, то оёти Парвардигоратонро бароятон бихонанд ва шуморо аз дидор бо чунин рӯзе битарсонанд?» Мегӯянд: «Бале». Вале бар кофирон азоб ҳатмӣ шуда буд.'),
(4130, 39, 72, 'Гуфта шавад: «Аз дарҳои ҷаҳаннам дохил шавед, ҳамеша дар он ҷо хоҳед буд». Ҷойгоҳи саркашон чӣ бад ҷойгоҳест!'),
(4131, 39, 73, 'Ва ононро, ки аз Парвардигорашон тарсидаанд, гурӯҳ-гурӯҳ ба биҳишт мебаранд. Чун ба биҳишт бирасанд, дарҳояш кушода шавад. Ва дарбонҳои бихишт гӯяндашон: «Салом бар шумо, биҳиштатон хуш бод, ба дарун биёед, ҳамеша дар ин ҷо хоҳед буд!»'),
(4132, 39, 74, 'Мегӯянд: «Шукр Худоеро, ки ҳар ваъда, ки ба мо дод, рост буд. Он заминро ба мерос ба мо дод. Ва акнун дар ҳар ҷои биҳишт, ки бихоҳем, макон мегирем». Амалкунандагонро чӣ музди некӯест!'),
(4133, 39, 75, 'Ва фариштагонро мебинӣ, ки гирди арши Худо ҳалқа задаанд ва ба ситоиши Парвардигорашон тасбеҳ мегӯянд. Миёни онҳо низ ба ҳақ довари гардад ва гуфта шавад, ки ситоиш аз они Худоест, ки Парвардигори ҷаҳониён аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Ҳо, мим.'),
(4135, 40, 2, 'Нозил шудани ин китоб аз ҷониби Худои ғолиби доност,'),
(4136, 40, 3, 'бахшояндаи гуноҳ, пазирандаи тавба, сахтуқубат, соҳиби ниъам (неъматҳо). Ҳеҷ худое ғайри Ӯ нест. Бозгашти ҳама ба сӯи Ӯст.'),
(4137, 40, 4, 'Фақат кофирон дар оёти Худо ҷидол мекунанд. Пас омаду рафташон дар шаҳрҳо туро нафиребад.'),
(4138, 40, 5, 'Пеш аз онҳо қавми Нӯҳ ва гурӯҳҳое, ки баъд аз эшон буданд, паёмбарашонро такзиб карданд. Ва ҳар уммате оҳанги он кард, ки паёмбарашро дастгир кунад ва ба ботил ба хусумат бархост, то ҳақро аз миён бардорад. Аммо Ман онҳоро ба уқубат фурӯ гирифтам ва уқубати Ман чӣ сахт буд!'),
(4139, 40, 6, 'Ба ин тарз ҳукми Парвардигори ту бар кофирон воҷиб шуд, ки аҳли ҷаҳаннаманд!'),
(4140, 40, 7, 'Онон, ки аршро мебардоранд ва онон, ки бар гирди он ҳастанд, ба ситоиши Парвардигорашон тасбеҳ, мегӯянд ва ба Ӯ имон доранд ва аз Ӯ барои мӯъминон бахшоиш мехоҳанд: «Эй Парварднгори мо, раҳмату илми Ту ҳама чизро фаро гирифтааст. Пас ононро, ки тавба кардаанд ва ба роҳи ту омадаанд, бибахш ва аз азоби ҷаҳаннам нигаҳ дор!'),
(4141, 40, 8, 'Ва эй Парвардигори мо, ононро ва ҳар кӣ солеҳ бошад, аз падарону ҳамсарон ва фарзандонашон ба биҳиштҳои ҷовидонӣ, ки ба онҳо ваъда додаӣ, дохил кун, ки Ту ғолибу ҳакими!'),
(4142, 40, 9, 'Ононро аз азобҳо ҳифз куд, ки ҳар киро дар он рӯз аз азобҳо ҳифз кунӣ, бар ӯ раҳмат овардаӣ ва он комёбии бузургест!»'),
(4143, 40, 10, 'Кофиронро нидо медиҳанд, ки душмании Худо бо шумо аз душмании шумо бо худатон, он гоҳ ки ба имонатон даъват мекарданд ва роҳи куфр пеш мегирифтед, бузургтар аст.'),
(4144, 40, 11, 'Мегӯянд: «Эй Парвардигори мо, моро ду бор мирондӣ ва, ду бор зинда сохтӣ ва мо ба гуноҳонамон иқрор кардаем. Оё берун шуданро роҳе ҳаст?»'),
(4145, 40, 12, 'Ин азоб ба он сабаб аст, ки чун Худоро ба яктоӣ мехонданд, шумо инкор мекардед ва агар барои Ӯ шарике қарор медоданд, шумо ба он шарик имон меовардед. Пас фармон фармони Худои баландмартабаи бузург аст.'),
(4146, 40, 13, 'Ӯст, он ки оёти Худро ба шумо нишон дод ва бароятон аз осмон рӯзӣ фиристод. Танҳо касе панд мегирад, ки рӯй ба Худо оварад.'),
(4147, 40, 14, 'Пас Худоро бихонед, дар ҳоле ки танҳо барои Ӯ дар дин ихлос мекунед, агарчи кофиронро нохуш ояд.'),
(4148, 40, 15, 'Баландкунандаи мартабаҳо, соҳиби арш, ки бар ҳар як аз бандагонаш, ки бихоҳад, ба фармони Худ ваҳй мефиристад, то мардумро аз рӯзи қиёмат битарсонад.'),
(4149, 40, 16, 'Он рӯз, ки ҳамагон ошкор шаванд, ҳеҷ чиз аз онҳо бар Худо пушида намонад. Дар он рӯз фармонравоӣ аз они кист? Аз они Худои яктои қаҳҳор!'),
(4150, 40, 17, 'Он рӯз ҳар касро монанди амалаш ҷазо медиҳанд ва ба кас ситаме намеравад. Ва Худо зуд ҳисобкунанда аст!.'),
(4151, 40, 18, 'Аз рӯзи қиёмат битарсонашон. Он гоҳ ки дилҳо лабрез аз андӯҳ наздики ҳанҷарҳо (гулӯҳо) расад. Ситамкоронро дар он рӯз на хешованде бошад ва на шафеъе, (пуштибон) ки суханашро бишнаванд.'),
(4152, 40, 19, 'Назарҳои дуздидаро ва ҳар чӣ дилҳо ниҳон доштаанд, медонад.'),
(4153, 40, 20, 'Худо ба ҳақ доварӣ мекунад. Вале касоне, ки ғайри Ӯ ба худоӣ мехонанд, ҳеҷ довари натавонанд, зеро Худо мешунавад ва мебинад!'),
(4154, 40, 21, 'Оё дар замин сайр намекунанд, то бингаранд, ки оқибати пешиниёнашон чӣ гуна будааст? Тавоноии онҳо ва ёдгориҳое, ки дар рӯи замин падид оварда буданд, аз инҳо беш буд. Ва Худо онҳоро ба ҷазои куфрашон фурӯ гирифт ва аз қаҳри Худовандашон нигаҳдорандае набуд.'),
(4155, 40, 22, 'Ин ба он сабаб буд, ки паёмбаронашон бо далелҳои равшан наздашон омаданд, вале инкор карданд ва Худо ҳам ононро фурӯ гирифт. Ва Худо нерӯманд аст ва ба сахтӣ уқубат мекунад!'),
(4156, 40, 23, 'Мо Мӯсоро бо оёти Худ ва ҳуҷҷате ошкор фиристодем'),
(4157, 40, 24, 'ба сӯи Фиръавну Ҳомон ва Қорун. Ва гуфтанд, ки ӯ ҷодугаре дурӯғгӯст!'),
(4158, 40, 25, 'Чун дини ҳақро аз ҷониби Мо бар онҳо арза дошт, гуфтанд: «Писарони касонеро, ки ба Ӯ имон овардаанд, бикушед ва занонашонро зинда бигузоред!» Ва ҳиласозии кофирон дар тариқи барбодӣ бошад.'),
(4159, 40, 26, 'Фиръавн гуфт: «Бигузоред, Мӯсоро бикушам ва ӯ Худои худро ба ёрӣ талабад, Метарсам динатонро дигаргун кунад ё дар ин сарзамин фасоде бархезонад,'),
(4160, 40, 27, 'Мӯсо гуфт: «Ман ба Парвардигори худ ва Парвардигори шумо аз ҳар худписанде, ки ба рӯзи хисоб бовар надорад, паноҳ мебарам!»'),
(4161, 40, 28, 'Ва марде мӯъмин аз хонадони Фиръавн, ки имонашро пинҳон дошта буд, гуфт: «Оё мардеро, ки мегӯяд, ки Парвардигори ман Худои яктост ва бо далелҳои равшан аз ҷониби Парвардигоратон омадааст, мекушед? Агар дурӯғ мегӯяд, гуноҳи дурӯғаш бар гардани худи ӯст ва агар рост мегӯяд, баъзе аз ваъдаҳое, ки додааст, ба шумо хоҳад расид, Албатта Худо ҳеҷ исрофкори дурӯғгӯеро ҳидоят намекунад!'),
(4162, 40, 29, 'Эй қавми ман, имрӯз фармонравоӣ аз они шумост. Бар ин сарзамин ғалаба доред. Вале агар азоби Худо бар сари мо ояд, чӣ касе ёриямон хоҳад кард?» Фиръавн гуфт: «Шуморо ғайри он чи худ маслиҳат додаам, роҳе нишон надиҳам ва ба ғайри роҳи савоб роҳнамоӣ накунам».'),
(4163, 40, 30, 'Он ки имон оварда буд, гуфт: «Эй қавми ман, бар шумо аз он чӣ бар сари он қавмҳои дигар омадааст, бимнокам (метарсам).'),
(4164, 40, 31, 'Ҳамонанди қавми Нӯҳ ва Од ва Самуд ва касоне, ки аз он пас омаданд. Ҳол он ки Худо барои бандагонаш хостори зулм нест.'),
(4165, 40, 32, 'Эй қавми ман, аз он рӯз, ки якдигарро ба фарёд бихонед, бар шумо бимнокам.'),
(4166, 40, 33, 'Он рӯз, ки ҳамагӣ пушт карда бозмегардед ва ҳеҷ кас шуморо аз азоби Худо нигоҳ намедорад. Ва ҳар кас, ки Худо гумроҳаш кунад, ҳеҷ роҳнамое надорад!'),
(4167, 40, 34, 'Юсуф пеш аз ин бо далоили равшан бар шумо мабъус (фиристода) шуд ва шумо аз он чӣ оварда буд, ҳамчунон дар шак мебудед. Чун Юсуф бимурд, гуфтед: «Худо пас аз ӯ дигар паёмбаре нахоҳад фиристод!» Худо таҷовузкори шаковарандаро ин гуна гумроҳ месозад!'),
(4168, 40, 35, 'Касоне, ки бе ҳеҷ ҳуҷате, ки дар даст дошта бошанд, дар оёти Худо ҷидол мекунанд. Кори онон назди Худову мӯъминон сахт нописанд аст. Худо бар дили ҳар мутакаббири (худро аз дигарон болотар донандаи) ҷабборе инчунин мудр мениҳад!\''),
(4169, 40, 36, 'Фиръавн гуфт: «Эй Ҳомон, барои ман қалъаи баланде бисоз, шояд ба он дарҳо даст ёбам:'),
(4170, 40, 37, 'дарҳои осмонҳо. Ва Худои Мӯсоро бубинам, зеро пиндорам, ки дурӯғ мегӯяд». Ба ин сон кирдори зишти Фиръавн дар назараш ороста шуд ва ӯ аз роҳ бозмонд ва чораҷӯии ӯ ғайри зиён ҳеҷ набуд.'),
(4171, 40, 38, 'Он марде, ки имон оварда буд, гуфт: «Эй қавми ман, аз паи ман биёед, то ба роҳи савоб ҳидоятатон кунам.'),
(4172, 40, 39, 'Эй қавми ман, ин зиндагии дунё беарзишу таъайюшест (хушгузарониест). Ва охират сарои бақост (боқӣ).'),
(4173, 40, 40, 'Ҳар кас кори баде бикунад, фақат монанди амалаш ҷазо ёбад. Ва ҳар кас аз марду зан, ки мӯъмин бошад ва амали солеҳе ба ҷой орад, ба биҳишт дохил шавад ва беҳисоб рӯзиаш диҳанд.'),
(4174, 40, 41, 'Эй қавми ман, чист, ки ман шуморо ба наҷот мехонам ва шумо маро ба оташ даъват мекунед?'),
(4175, 40, 42, 'Маро даъват мекунед, ки ба Худо кофир шавам ва чизеро, ки намешиносам, шарики Ӯ қарор диҳам, ҳол он ки ман шуморо ба Худои ғолиби бахшоянда даъват мекунам.'),
(4176, 40, 43, 'Бешак он чӣ шумо маро ба он даъват мекунед, тавонои онаш нест, ки дар дунёву охират касеро ба сӯи худ хонад, ҳол он ки бозгашти мо ба сӯи Худои яктост ва таҷовузкорон дар ҷаҳаннам бошанд.'),
(4177, 40, 44, 'Ба зуди он чиро, ки акнун мегӯям, ба ёд хоҳед овард. Ва ман кори худ ба Худо ҳавола кардам, зеро Ӯ бандагонашро мебинад!»'),
(4178, 40, 45, 'Худо ӯро аз осеби макре, ки барояш андешида буданд, нигоҳ дошт ва он азоби ногувор хонадонӣ Фиръавнро дар миён гирифт.'),
(4179, 40, 46, 'Оташ. Ҳар субҳу шом бар он арза шаванд ва рӯзе, ки қиёмат барпо шавад, нидо диҳанд ки хонадонӣ Фиръавнро ба сахттарин азобҳо дароваред!»'),
(4180, 40, 47, 'Ва он гоҳ дар даруни оташ бо якдигар ба муҷодала бархезанд ва нотавонон ба гарданкашон гӯянд, ки мо пайравони шумо будем, оё метавонед андаке аз ин оташе, ки насиби мо шудааст, кам кунед?'),
(4181, 40, 48, 'Онон, ки саркашӣ карда буданд, мегӯянд: «Ҳамаи мо дар оташем. Ва Худост, ки миёни бандагонаш довари кардааст!»'),
(4182, 40, 49, 'Ва онон, ки дар оташанд, ба нигаҳбонони ҷаҳаннам мегӯянд: «Аз Парвардигоратон бихоҳед, то як рӯз аз азоби мо кам кунад».'),
(4183, 40, 50, 'Мегӯянд: «Оё паёмбаронатон бо далелҳои равшан назди шумо наёмада буданд?» Мегӯянд: «Бале». Мегӯянд: «Пас дуъо кунед». Ва дуъои кофирон, ғайри он ки барбод равад, чизе дигар нахоҳад буд.'),
(4184, 40, 51, 'Мо паёмбаронамон ва мӯъминонро дар зиндагии дунё ва дар рӯзи қиёмат, ки шоҳидон барои шоҳидӣ рост меистанд, ёрӣ мекунем.'),
(4185, 40, 52, 'дар рӯзе, ки ситамкоронро пузиш (узр) хостан нафъ надиҳад ва насиби онҳо лаънат аст ва он сарои (макони) бад!'),
(4186, 40, 53, 'Мо ба Мӯсо мартабаи ҳидоят додем ва он китобро (тавротро) ба банӣ-Исроил мерос додем.'),
(4187, 40, 54, 'Китобе, ки худ ҳидоят аст ва барои хирадмандон андарз (панд).'),
(4188, 40, 55, 'Сабр кун, ки ваъдаи Худо ҳақ аст. Барои гуноҳат бахшоиш бихоҳ ва ҳар шомгоҳу бомдод ба ситоиши Парвардигорат тасбеҳ бигӯй.'),
(4189, 40, 56, 'Онон, ки бе ҳеҷ ҳуҷҷате, ки аз осмон омада бошад, дар бораи оёти Худо муҷодала мекунанд, дар дилашон ғайри ҳаваси бузургӣ нест, вале ба он нахоҳанд расид. Пас ба Худо паноҳ бибар, ки Ӯ шунавову биност!'),
(4190, 40, 57, 'Офариниши осмону замин аз офариниши мардум бузургтар аст, вале бештари мардум намедонанд!'),
(4191, 40, 58, 'Нобинову бино баробар нестанд. Низ онҳое, ки имон овардаанд ва корҳои шоиста кардаанд, бо зишткорон яксон набошанд. Чӣ андак панд мегиред!'),
(4192, 40, 59, 'Қиёмат меояд ва дар он шӯбҳае нест, вале бештари мардум имон намеоваранд.'),
(4193, 40, 60, 'Парвардигоратон гуфт: «Дуову илтиҷо кунед Маро, то шуморо иҷобат кунам. Онҳое, ки аз парастиши Ман саркашӣ мекунанд, ба зуди дар айни хори ба ҷаҳаннам дароянд!»'),
(4194, 40, 61, 'Худост, он ки шабро бароятон офарид, то дар он биёромед ва рӯзро равшаноӣ бахшид. Худо фазли худ ба мардум арзонӣ медорад. Вале бештари мардум шукркунанда нестанд.'),
(4195, 40, 62, 'Ин аст Худои якто — Парвардигори шумо, офаридгорн ҳар чизе. Худое ғайри Ӯ нест. Пас чӣ гуна аз ҳақ бероҳатон мекунанд?'),
(4196, 40, 63, 'Ҳамчунин инҳо, ки оёти Худоро инкор мекунанд, аз имон рӯй бармегардонанд.'),
(4197, 40, 64, 'Худост, ки заминро қароргоҳи шумо сохт ва осмонро чун биное бардошт. Ва шуморо сурат бахшид ва суратҳоятонро некӯ сохт. Ва аз чизҳои покизаву хуш рӯзиятон дод. Ин аст Худои якто — Парвардигори шумо. Бартару бузургвор аст Худо — он Парвардигори ҷаҳониён!'),
(4198, 40, 65, 'Ӯ зинда аст, Худое ғайри Ӯ нест. Ӯро ибодат кунед, дар ҳоле, ки дини Ӯро ба ихлос пазируфта бошед, ки ситоиш (шукру зикри нек) аз они Худоест, ки Парвардигори ҷаҳониён аст!'),
(4199, 40, 66, 'Бигӯ: «Маро манъ кардаанд, ки чизҳоеро, ки ба ҷои Худо мепарастед, парастиш кунам, дар ҳоле, ки аз ҷониби Парвардигорам барои ман далоиле равшан омадааст. Ва ба ман фармон додаанд, ки дар баробари Парвардигори ҷаҳониён таслим бошам».'),
(4200, 40, 67, 'Ӯст он Худое, ки шуморо аз хок, сипас аз нутфа, сипас аз лахтаи хуне биёфаридааст. Он гоҳ шуморо, дар сурати кӯдаке баровард, то ба синни ҷавонӣ бирасед ва пир шавед. Баъзе аз шумо пеш аз пирӣ бимиред ва баъзе ба он замони муъайян мерасед ва шояд ба ақл дарёбед.'),
(4201, 40, 68, 'Ӯст, ки зинда мекунад ва мемиронад. Ва чун иродаи чизе кунад, мегӯядаш: «Мавҷуд шав!» Пас мавҷуд мешавад.'),
(4202, 40, 69, 'Оё надидаӣ онҳое, ки дар оёти Худо кашмакаш мекунанд, чӣ гуна каҷрав мешаванд?'),
(4203, 40, 70, 'Касоне, ки ин китобро дурӯғ мешуморанд ва он чиро, ки ба василаи паёмбарон фиристодаем, такзиб мекунанд (дурӯғ мебароранд), ба зуди хоҳанд донист,'),
(4204, 40, 71, 'он гоҳ ки тавқҳоро ба гарданашон андозанд ва бо занчирҳо бикашандашон'),
(4205, 40, 72, 'дар оби ҷӯшон, сипас дар оташ афрӯхта шаванд.'),
(4206, 40, 73, 'Он гоҳ ба онҳо гуфта шавад: «Он шарикон, ки барои Худо мегирифтед, куҷо ҳастанд?»'),
(4207, 40, 74, 'Мегӯянд: «Онҳоро аз даст додаем, балки пеш аз ин чизеро ба худоӣ намехондаем». Худо кофиронро ба ин тарз гумроҳ мекунад.'),
(4208, 40, 75, 'Ин ба он сабаб аст, ки ба ноҳақ дар замин шодмонӣ мекардед ва нозу карашма мекардед.'),
(4209, 40, 76, 'Аз дарҳои ҷаҳаннам дохил шавед. Ҳамеша дар он ҷо бимонед. Ва ҷойгоҳи саркашон чӣ бад чойгоҳест!'),
(4210, 40, 77, 'Пас сабр кун, албатта, ваъдаи Худо рост аст. Е баъзе аз он чизҳоро, ки ба онҳо ваъда додаем, ба ту нишон медиҳем ё туро бимиронем ва онҳо ба назди Мо бозгардонида шаванд.'),
(4211, 40, 78, 'Пеш аз ту паёмбароне фиристодаем. Достони баъзеро бароят гуфтаем ва достони баъзеро нагуфтаем. Ва ҳеҷ паёмбареро нарасад, ки ояте биёварад, магар ба фармони Худо. Ва чун фармони Худо даррасад, ба ҳақ довари гардад ва онон, ки бар ботил будаанд, он ҷо, зиён хоҳанд дид.'),
(4212, 40, 79, 'Худост, ки чорпоёнро бароятон офарид, то бар баъзе савор шавед ва аз баъзе бихӯред.'),
(4213, 40, 80, 'Ва шуморо дар он манфиатҳост ва метавонед бо онҳо ба ҳоҷате, ки дар назар доред, бирасед. Ва бар онҳо ва бар киштиҳо савор шавед.'),
(4214, 40, 81, 'Оёти худро бар шумо менамоёнад. Пас кадом як аз оёти Худоро инкор мекунед?'),
(4215, 40, 82, 'Оё дар замин сайр накардаанд, то бингаранд, ки оқибати касоне, ки пеш аз онҳо мезистаанд, чӣ гуна будааст? Мардуме, ки қувваташон бештар ва осорашон дар рӯи замин фаровонтар буд. Пас он чизҳое, ки ба даст меоварданд, фоида набахшид.'),
(4216, 40, 83, 'Чун паёмбаронашон бо далелҳои равшан ба сӯяшон омаданд, ба дониши худ дилхуш буданд, то он чизе, ки масхарааш мекарданд, онҳоро дар миён гирифт.'),
(4217, 40, 84, 'Ва чун азоби Моро диданд, гуфтанд: «Ба Худои якто имон овардем ва ба он чизҳое, ки шарики Худо қарор дода будем кофир шудем».'),
(4218, 40, 85, 'Аммо ба он ҳангом, ки азоби Моро диданд, дигар имонашон барояшон суде набахшид, Ин суннати Худост дар рафтор бо бандагонаш. Ва кофирон дар он рӯз зиён карданд!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Ҳо, мим.'),
(4220, 41, 2, 'Китобест, ки аз ҷониби Он бахшояндаи меҳрубон нозил шудааст.'),
(4221, 41, 3, 'Китобест, ки ояҳояш ба тафсилу фаҳмо баён шуда, Қуръонест ба забони арабӣ барои мардуме, ки медонанд.'),
(4222, 41, 4, 'Ҳам муждадиҳанда аст ва ҳам бимдиҳанда. Бештарашон аз он эъроз кардаанд (рӯй гардондаанд) ва сухан намешунаванд.'),
(4223, 41, 5, 'Гуфтанд: «Дилҳои мо аз он чӣ моро ба он даъват мекунӣ, дар парда аст ва гӯшҳоямон сангин аст ва миёни мову ту ҳиҷобест. Ту ба кори худ пардоз ва мо низ ба кори худ мепардозем».'),
(4224, 41, 6, 'Бигӯ: «Ман инсоне ҳастам ҳамонанди шумо. Ба ман ваҳй шуда, ки худоятон Худоест якто. Пас ба ӯ рӯй оваред ва аз Ӯ бахшоиш бихоҳед. Ва вой бар мушрикон:'),
(4225, 41, 7, 'онҳое, ки закот намедиҳанд ва ба охират имон надоранд.'),
(4226, 41, 8, 'Ононро, ки имон овардаанд ва корҳои шоиста мекунанд, подошест (мукофотест) тамомношуданӣ».'),
(4227, 41, 9, 'Бигӯ: «Оё ба касе, ки заминро дар ду рӯз офаридааст, кофир мешавед ва барои Ӯ шарикҳо қарор медиҳед? Ӯст Парвардигори ҷаҳониён!'),
(4228, 41, 10, 'Бар рӯи замин кӯҳҳо падид овард ва онро пурбаракат сохт ва ризқи ҳамаро муъайян кард дар чаҳор рӯз яксон барои ҳамаи пурсандагон.'),
(4229, 41, 11, 'Сипас ба осмон пардохт ва он дуде буд. Пас ба осмону замин гуфт: «Хоҳ ё нохоҳ биёед». Гуфтанд: «Фармонбардор омадем».'),
(4230, 41, 12, 'Он гоҳ ҳафт осмонро дар ду рӯз офарид. Ва дар ҳар осмоне корашро ба он ваҳй кард. Ва осмони дунёро ба чароғҳое биёростем ва ҳифзаш кардем. Ин аст тадбири Он ғолиби доно».'),
(4231, 41, 13, 'Пас агар рӯй гардонанд, бигӯ: «Шуморо аз раъду барқе ҳамонанди раъду барқе, ки бар Оду Самуд фуруд омад, метарсонам.'),
(4232, 41, 14, 'Он гоҳ ки расулони пеш ва баъди онҳо навдашон омаданд ва гуфтанд, ки ғайри Худои якторо мапарастед, гуфтанд: «Агар Парвардигори мо мехост, фариштагонро аз осмон мефаровард. Мо ба он чӣ шумо бо он фиристода шудаед, имон намеоварем».'),
(4233, 41, 15, 'Аммо қавми Од ба ноҳақ дар, рӯи замин гарданкашӣ карданд ва гуфтанд: «Чӣ касе аз мо боқувват тар аст?» Оё намедиданд, ки Худое, ки онҳоро офаридааст, аз онҳо бо қувваттар аст, ки оёти Моро инкор мекарданд?'),
(4234, 41, 16, 'Мо низ боде сахту ғуррон дар рӯзҳое шум бар сарашон фиристодем, то дар дунё азоби хориро ба онҳо бичашонем. Ва азоби охират хоркунандатар аст ва касе ба ёриашон барнахезад!'),
(4235, 41, 17, 'Аммо қавми Самуд, ҳидояташон кардем. Ва онҳо куриро аз ҳидоят бештар дӯст медоштанд, то он ки ба хотири амалҳое, ки мекарданд, соъиқаи азоби хоркунанда онҳоро фурӯ гирифт.'),
(4236, 41, 18, 'Ва Мо касонеро, ки имон оварда буданд ва парҳезгор буданд, наҷот додем.'),
(4237, 41, 19, 'Ва рӯзе, ки душманони Худоро гирд оваранд ва ба саф биронандашон,'),
(4238, 41, 20, 'чун ба канори оташ оянд, гӯшу чашмҳо ва пӯстҳошон ба амалҳое, ки кардаанд, бар зиддашон шоҳидӣ диҳанд.'),
(4239, 41, 21, 'Ба пӯстҳои худ гӯянд: «Чаро бар зидди мо шоҳидӣ додед?» Гӯянд: «Он Худое, ки ҳар чизеро ба сухан меоварад ва шуморо нахустин бор биёфарид ва ба Ӯ бозгашт меёбед, моро ба сухан овардааст».'),
(4240, 41, 22, 'Аз ин ки гӯшу чашмҳо ва пӯстҳоятон ба зиёни шумо шоҳидӣ диҳанд, чизе ниҳон намедоштед, балки мепиндоштед, ки Худо бар бисёре аз корҳое, ки мекунед, огоҳ нест.'),
(4241, 41, 23, 'Ва ин буд гумоне, ки ба Парвардигоратон доштед. Ҳалокатон кард ва дар шумори зиёнкардагон даромадед.'),
(4242, 41, 24, 'Пас агар сабр кунанд, ҷойгоҳашон дар оташ аст ва агар ҳам толиби афв шаванд, касе онҳоро афв накунад.'),
(4243, 41, 25, 'Ба барояшон ҳамдамоне муқаррар кардем ва онон ҳолу ояндаро дар назарашон биёростанд. Ва бар онҳо низ монанди пешиниёнашон аз ҷинну инс азоб муқаррар шуд. Зеро зиёнкор буданд.'),
(4244, 41, 26, 'Кофирон гуфтанд: «Ба ин Қуръон гӯш мадиҳед ва сухани беҳуда ба он биёмезед, шояд пирӯз гардед!»'),
(4245, 41, 27, 'Кофиронро азобе сахт мечашонем ва бадтар аз он чӣ мекардаанд, подош медиҳем.'),
(4246, 41, 28, 'Ин аст кайфари (ҷазои) душманони Худо оташ. Дар даруни он хонае ҳамешагӣ доранд. Ин ҷазои онҳост, зеро оёти Моро инкор мекарданд.'),
(4247, 41, 29, 'Кофирон гӯянд: «Эй Парвардигори мо, он ду танро аз ҷинну инс, ки моро гумроҳ карданд, ба мо бинамоён, то пой бар сари онҳо ниҳем, то аз мо пасттар (поёнтар) раванд!»'),
(4248, 41, 30, 'Бар онон, ки гуфтанд: «Парвардигори мю Оллоҳ аст» ва пойдорӣ кардаанд, фариштагон фуруд меоянд, ки метарсед ва ғамгин набошед, шуморо ба биҳиште, ки ба шумо ваъда дода шуда, хушхабар аст.'),
(4249, 41, 31, 'Мо дар дунё дӯстони шумо будем ва низ дар охират дӯстдори шумоем. Дар биҳишт ҳар чӣ дилатон бихоҳад ва ҳар чӣ талаб кунед, бароятон фароҳам аст.'),
(4250, 41, 32, 'Меҳмоннавозист аз ҷониби Худои бахшояндаи меҳрубон.'),
(4251, 41, 33, 'Чӣ касеро сухан некӯтар аз сухани он, ки ба сӯи Худо даъват мекунад ва корҳои шоиста мекунад ва мегӯяд, албатта, ки ман аз мусалмононам?'),
(4252, 41, 34, 'Хубиву бадӣ баробар нестанд. Ҳамеша ба некӯтарин тарзе ҷавоб деҳ, то касе, ки миёни туву ӯ душманӣ аст, чун дӯсти меҳрубони ту гардад.'),
(4253, 41, 35, 'Бархурдор нашаванд аз ин ғайри касоне, ки босабр бошанд ва касоне, ки аз имон баҳрае бузург дошта бошанд.'),
(4254, 41, 36, 'Ва агар аз ҷониби шайтон гирифтори васвасае гардӣ, ба Худо паноҳ бибар, ки Ӯ шунавову доност!'),
(4255, 41, 37, 'Ва аз нишонаҳои қудрати Ӯ шабу рӯз ва офтобу моҳ аст. Ба офтобу моҳ саҷда макунед. Ба Худои якто, ки онҳоро офаридааст, саҷда кунед, агар Ӯро мепарастед!'),
(4256, 41, 38, 'Агар онон такаббур меварзанд, онҳо, ки дар назди Парвардигори ту ҳастанд, бе он ки малул шаданд шабу рӯз тасбеҳи Ӯ мегӯянд (Саҷда)'),
(4257, 41, 39, 'Ва аз оёти қудрати Ӯ он, ки ту заминро хушк мебинӣ. Чун об бар он бифиристем, ба ҷунбиш ояд ва гиёҳ бирӯёнад. Он кас, ки онро зинда мекунад, зиндакунандаи мурдагон аст, ки Ӯ бар ҳар чизе тавоност!'),
(4258, 41, 40, 'Касоне, ки дар оёти Мо роҳи ботил пеш мегиранд, бар Мо пӯшида нестанд. Оё он ки ба оташ афканда мешавад, беҳтар аст ё он ки рӯзи қиёмат бе ҳеҷ ваҳшате меояд? Ҳарчи мехоҳед бикунед, Ӯ ба корҳоятон биност!'),
(4259, 41, 41, 'Ба ҷазои худ расанд онон, ки ба Қуръон, ки барои ҳидояташон омадааст, имон намеоваранд, ҳол он ки китобе арҷманд аст.'),
(4260, 41, 42, 'На аз пеши рӯй ботил ба ӯ роҳ ёбад ва на аз пас. Нозилшуда аз ҷониби Худованде ҳакиму сутуданист. (лоиқи шукру сипос аст).'),
(4261, 41, 43, 'Ҳар чӣ дар бораи ту мегӯянд, дар бораи паёмбарони пеш аз ту низ гуфтаанд. Албатта Парвардигори ту ҳам бахшоянда аст ва ҳам соҳиби уқубатест дардовар!'),
(4262, 41, 44, 'Агар Қуръонро ба забони Аҷам мефиристодем, мегуфтанд: «Чаро оёташ ба равшанӣ баён нашудааст? Китобе ба забони Аҷам ва паёмбаре араб?» Бигӯ: «Ин китоб барои онҳо, ки имон оварданд, ҳидоят ва шифост ва онҳо, ки имон наоварданд, гӯшҳояшон сангин аст ва чашмонашон кӯр аст, чунонанд, ки гӯи онҳоро аз ҷое дур садо мекунанд.'),
(4263, 41, 45, 'Мӯсоро китоб додем, аммо дар он ихтилоф карданд. Ва агар он сухане, ки Парвардигорат аз пеш гуфта буд, намебуд миёнашон кор ба поён меомад. Ва, албатта, ҳанӯз ба сахтӣ дар шубҳаанд.'),
(4264, 41, 46, 'Ҳар кас, ки коре шоиста кунад, ба фоидаи худи ӯст ва ҳар кӣ бад кунад, ба зиёни ӯст. Ва Парвардигори ту ба бандагон зулм раво намедорад.'),
(4265, 41, 47, 'Илм ба рӯзи қиёмат назди Ӯст. Ва ҳар мевае аз ғилофи худ берун меояд ва ҳар модае ҳомиладор мешаваду мезояд, албатта ки Ӯ ба он огоҳ аст. Ва он рӯз, ки онҳоро нидо диҳад, ки шарикони Ман куҷоянд? Гӯянд: Огоҳат кардем, ки касе аз мо ба ширк гувоҳӣ намедиҳад!'),
(4266, 41, 48, 'Ва он чиро пеш аз ин ба, худоӣ мехонданд, аз даст доданд ва донистанд, ки роҳи гурезе надоранд.'),
(4267, 41, 49, 'Одами ҳар чӣ талаби хайр кунад, хаста намешавад, аммо чун бадӣ ба ӯ бирасад, бадандешу ноумед мегардад.'),
(4268, 41, 50, 'Агар пас аз ранҷе, ки ба ӯ расида, раҳмате ба ӯ бичашонем, мегӯяд: «Ин ҳаққи ман аст ва напиндорам, ки қиёмате барпо шавад. Ва агар ҳам маро назди Парвардигорам баргардонанд, албатта, ки назди Ӯ ҳолате хуштар бошад». Пас кофиронро ба амалҳое, ки кардаанд, огоҳ мекунем ва ба онҳо азобе сахт мечашонем.'),
(4269, 41, 51, 'Чун ба одами неъмате ато кунем, рӯйгардон мешавад ва ба такаббур гардан боло мекунад. Ва агар ба ӯ бадие бирасад, бисёр фарёду фиғон мекунад.'),
(4270, 41, 52, 'Бигӯ: «Чӣ мебинед?» Агар ин китоб аз ҷониби Худост ва шумо ба он имон намеоваред, гумроҳтар аз касе, ки ҳамчунон роҳи мухолифат (зиддият) дар пеш гирифтааст, кист?'),
(4271, 41, 53, 'Ба зудӣ, оёти қудрати Худро дар атрофи олам ва дар вуҷуди худашон ба онҳо нишон хоҳем дод, то барояшон ошкор шавад, ки, Ӯ ҳақ аст. Оё ин ки Парвардигори ту дар ҳама ҷо ҳозир аст, кофӣ нест?'),
(4272, 41, 54, 'Баҳуш (огоҳ) бош, ки онҳо аз дидори Парвардигорашон дар шубҳаанд. Баҳуш бош, ки Ӯ бар ҳар чизе иҳота дорад.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Ҳо, мим.'),
(4274, 42, 2, 'Айн, син, қоф.'),
(4275, 42, 3, 'Худованди ғолибу ҳаким ба ту ва касоне, ки пеш аз ту буданд, инчунин ваҳй мефиристад:'),
(4276, 42, 4, 'аз они Ӯст, ҳар чӣ дар осмонҳову замин аст. Ва Ӯст он баландмартабаи бузургвор!'),
(4277, 42, 5, 'Наздик аст, ки осмонҳо ба болои якдигар дар ҳам шикананд ва фариштагон ба ситоиши Парвардигорашон тасбеҳ мегӯянд ва барои сокинони замин бахшоиш металабанд. Огоҳ бошед, ки Худо бахшояндаву меҳрубон аст!'),
(4278, 42, 6, 'Худо муроқиби (нозири) аъмоли касонест, ки ғайри Ӯро ба дӯстӣ гирифтанд. Ва ту вакили онҳо нестӣ.'),
(4279, 42, 7, 'Ва низ ин Қуръонро ба забони арабӣ бар ту ваҳй кардем то уммулқуро ва сокинони атрофашро бим диҳй. Ҳамчунин ононро аз рӯзи қиёмат, ки дар он шакке нест, битарсонӣ, ки гурӯҳе дар биҳиштанд ва гурӯҳе дар оташи сӯзон.'),
(4280, 42, 8, 'Агар Худо мехост, ҳамаро як уммат карда буд. Вале Ӯ ҳар киро ки бихоҳад, ба раҳмати худ дароварад ва ситамкоронро ҳеҷ дӯсту ёваре нест!'),
(4281, 42, 9, 'Оё ғайри Худоро ба дӯстӣ гирифтанд? Дӯсти ҳақиқӣ Худост. Ва Ӯст, ки мурдагонро зинда мекунад ва Ӯст, ки бар ҳар коре тавоност!'),
(4282, 42, 10, 'Дар ҳар чӣ ихтилоф мекунед, ҳукмаш бо Худост. Ин Худои якто Парвардигори ман аст. Бар Ӯ таваккал кардам ва ба Ӯ рӯй меоварам.'),
(4283, 42, 11, 'Офаридгори осмонҳову замин аст. Барои шумо, ҳам аз шумо ҳамсароне биёфарид. Ва низ барои чорпоён ҷуфтҳое падид овард. Во офариниши ҳамсарон бар шуморатон меафзояд. Ҳеҷ чиз монанди Ӯ нест ва Ӯст, ки шунавову биност!'),
(4284, 42, 12, 'Калидҳои осмонҳову замин аз они Ӯст. Дар рӯзии ҳар кӣ бихоҳад, кушоиш медиҳад ё танг мегирад ва Ӯ ба ҳар чизе доност!'),
(4285, 42, 13, 'Барои шумо дину оине муқаррар кард аз ҳамон гуна, ки ба Нӯҳ тавсия карда буд ва аз он чӣ бар ту ваҳй кардаем ва ба Иброҳиму Мӯсо ва Исо тавсия кардаем, ки динро барпой нигаҳ доред ва дар он фирқа-фирқа машавед. Таҳаммули он чӣ ба он даъват мекунед, бар мушрикон душвор аст. Худо ҳар киро хоҳад, барои пайғамбарии Худ интихоб мекунад ва ҳар киро ба ӯ бозгардад, ба Худ роҳ менамояд.'),
(4286, 42, 14, 'Аз рӯи ҳасаду адоват фирқа-фирқа шуданд, аз он пас, ки ба дониш даст ёфтанд. Ва агар Парвардигори ту аз пеш муқаррар накарда буд, ки онҳоро то замоне муъайян мӯҳлат аст, бар онҳо ҳукми азоб мерафт. Ва касоне, ки баъд аз онҳо вориси китоби Худо шудаанд, дар бораи он сахт ба шубҳа афтодаанд.'),
(4287, 42, 15, 'Барои он даъват кун ва чунон ки фармон ёфтаӣ, пойдорӣ кун ва аз паи хоҳишҳояшон марав ва бигӯ: «Ба китобе, ки Худо нозил кардааст, имон дорам ва ба ман фармон додаанд, ки дар миёни шумо ба адолат рафтор кунам. Худои якто Парвардигори мо ва Парвардигори шумост. Аъмоли мо аз они мо ва аъмоли шумо аз они шумо. Миёну мову шумо ҳеҷ муҳоҷҷае (душмание) нест. Худо моро дар як ҷо ҷамъ меоварад ва бозгашт ба сӯи Ӯст!»'),
(4288, 42, 16, 'Ва касоне, ки дар бораи Худо ҷидол мекунанд, пас аз он ки даъвати Ӯро иҷобат кардаанд, назди Парвардигорашон ҳуҷҷаташон ночиз аст. Бар онҳост хашми Худо ва бар онҳост азобе сахт!'),
(4289, 42, 17, 'Худост, ки ин китоби бархақ ва тарозуро нозил кардааст. Ва ту чӣ медонӣ? Шояд қиёмат наздик бошад.'),
(4290, 42, 18, 'Онон, ки бовараш надоранд, (омадани) онро ба шитоб металабанд ва онон, ки имон овардаанд, аз он бимноканд ва медонанд, ки ҳақ аст. Огоҳ бош, касоне, ки дар бораи қиёмат ҷидол мекунанд, сахт дар гумроҳӣ ҳастанд.'),
(4291, 42, 19, 'Худо бо бандагонаш меҳрубон аст. Ҳар киро бихоҳад, рӯзӣ медиҳад. Ва Ӯ тавонову ғолиб аст!'),
(4292, 42, 20, 'Ҳар кас кишти охиратро бихоҳад, ба киштааш меафзоем ва ҳар кас кишти дунёро бихоҳад, ба ӯ ато мекунем, вале дигар дар кишти охираташ насибе нест.'),
(4293, 42, 21, 'Оё мушриконро бутонест, ки дине барояшон овардаанд, ки Худо рухсати онро надодааст? Ва агар қасди таъхири (ба дер партофтани) азобашон набуд, корашон ба поён омада буд ва ситамкоронро азобест дардовар!'),
(4294, 42, 22, 'Ситамкоронро бинӣ, ки аз ҳосили корҳояшон бимноканд. Ва албатта, ба ҷазои худ хоҳанд расид. Вале онҳо, ки имон овардаанд ва корҳои шоиста кардаанд, дар боғҳои биҳиштанд. Ҳар чӣ бихоҳанд, назди Парвардигорашон ҳаст. Ва ин фазлу бахшоисти бузургест!'),
(4295, 42, 23, 'Ин аст он чизе, ки Худо он гурӯҳ аз бандагокашро, ки имон овардаанд ва корҳои шоиста кардаанд, ба он мужда медиҳад. Бигӯ: «Бар он таблиғи Қуръон музде аз шумо ҷуз дӯст доштани хешовандон намехоҳам». Ва ҳар кӣ кори неке кунад, ба некӯияш меафзоем, зеро Худо бахшояндаву шукрпазир аст!'),
(4296, 42, 24, 'Ё мегӯянд, ки бар Худо дурӯғ мебандад. Агар Худо бихоҳад, бар дили ту мӯҳр мениҳад. Ва Худо ба калигмоти худ ботилро маҳв мекунад ва ҳақро собит мегардонад. Ӯ бар ҳар чӣ дар дилҳо мегузарад, доност!'),
(4297, 42, 25, 'Ва Ӯст, ки тавбаи бандагонашро мепазирад ва аз гуноҳон афвашон мекунад ва ҳар чӣ мекунед, медонад,'),
(4298, 42, 26, 'Ва дуьои касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, иҷобат мекунад ва аз фазли худ ононро бисёр медиҳад. Ва кофиронро азобе сахт аст!'),
(4299, 42, 27, 'Агар Худо рӯзии бандагонашро бисёр кунад, дар замин фасод мекунанд, вале ба миқдоре, ки бихоҳад, рӯзӣ мефиристад. Зеро бар бандагони худ огоҳу биност!'),
(4300, 42, 28, 'Ва Ӯст он Худое, ки баъд аз ноумедияшон борон мефиристад ва раҳмати Худро ба ҳама ҷо пароканда мекунад ва Ӯст корсозу соҳиби ҳамду сано.'),
(4301, 42, 29, 'Ва аз нишонаҳои қудрати Ӯ офариниши осмонҳову замин ва парокандани ҷунбандагон дар он дӯст. Ва ҳар гоҳ бихоҳад, бар ҷамъ оварданашон тавоност!'),
(4302, 42, 30, 'Агар шуморо мусибате расад, ба хотири корҳоест, ки кардаед. Ва Худо бисёре аз гуноҳонро афв мекунад.'),
(4303, 42, 31, 'Шумо натавонед дар рӯи замин аз Ӯ бигурезед ва шуморо ғайри Ӯ корсозу ёваре нест!'),
(4304, 42, 32, 'Аз нишонаҳои қудрати Ӯ киштиҳоест чун кӯҳ ки дар дарёҳо равонанд.'),
(4305, 42, 33, 'Агар бихоҳад, бодро нигаҳ медорад, то киштиҳо бар рӯи дарё аз рафтан бозмонанд. Албатта дар ин барои сабркунандагони шукргузор ибратҳоест.'),
(4306, 42, 34, 'Ё онҳоро ба хотири амалҳояшон ғарк мекунад ва бисёреро низ мебахшояд.'),
(4307, 42, 35, 'Ва то бидонанд, касоне, ки дар оёти Мо ҷидол мекунанд. Ононро ҳеҷ гурезгоҳе нест.'),
(4308, 42, 36, 'Он чӣ шуморо додаанд, баҳраи ин зиндагии дунявист. Ва он чӣ дар назди Худост, барои онҳо, ки имон овардаанд ва ба Парвардигорашон таваккал мекунанд, беҳтару пояндатар аст.'),
(4309, 42, 37, 'Ва он касоне, ки аз гуноҳони бузург ва зиштиҳо парҳез мекунанд ва чун дар хашм шаванд, хатоҳоро мебахшоянд;'),
(4310, 42, 38, 'ва онон, ки даъвати Парвардигорашонро қабул мекунанд ва намоз мегузоранд ва корашон бар пояи машварат бо якдигар аст ва аз он чӣ ба онҳо рӯзӣ додаем, садақа мекунанд;'),
(4311, 42, 39, 'ва онон, ки чун ситаме ба онҳо расад, интиқом мегиранд.'),
(4312, 42, 40, 'Ҷазои ҳар бадӣ бадиест монанди он. Пас касе, ки афв кунад ва оштӣ кунад, муздаш бо Худост, зеро Ӯ ситамкоронро дӯст надорад.'),
(4313, 42, 41, 'Бар касоне, ки пас аз зулме, ки бар онҳо рафта бошад, интиқом мегиранд, маломате нест.'),
(4314, 42, 42, 'Маломат бар касонест, ки ба мардум зулм мекунанд ва ба ноҳақ дар рӯи замин саркашӣ мекунанд. Барои онҳост азобе дардовар!'),
(4315, 42, 43, 'Ва он ки сабр кунад ва аз хато даргузарад, ин аз корҳои писандида аст.'),
(4316, 42, 44, 'Ҳар касро, ки Худо гумроҳ кунад, аз он пас ҳеҷ дӯсте нахоҳад дошт. Ва золимонро мебинӣ, ки чун азобро бингаранд, мегӯянд: «Оё моро роҳи бозгаште ҳаст?»'),
(4317, 42, 45, 'Онҳоро мебинӣ, ки ба ҷаҳаннам мебаранд. Тарсону хор аз гӯшаи чашм нигоҳе дуздида мекунанд. Касоне, ки имон оварда буданд, мегӯянд: «Инҳо худ ва касонашонро дар рӯзи қиёмат барбод доданд». Огоҳ бош, ки ситамкорон дар азоби доим хоҳанд буд!'),
(4318, 42, 46, 'Ғайри Худо ёру мададгоре надоранд ва ҳар касро, ки Худо гумроҳ кунад, ҳеҷ роҳе барояш нест!'),
(4319, 42, 47, 'Пеш аз он ки рӯзе биёяд, ки аз ҷониби Худо бозгаште надорад, ба Парвардигоратон ҷавоби қабул диҳед. Дар он рӯз на паноҳе хоҳед дошт ва на касе аз шумо муҳофизат тавонад кард!'),
(4320, 42, 48, 'Агар рӯй гардонанд, туро нафиристодаем, ки нигаҳбонашон бошӣ. Бар ту ғайри расонидани пайғом ҳеҷ нест. Ва Мо чун ба инсон аз раҳмати Худ бичашонем, шодмон мегардад ва агар ба хотири корҳое, ки кардааст, норавоӣ ба ӯ расад, носипосӣ мекунад.'),
(4321, 42, 49, 'Аз они Худост фармонравоии осмонҳову замин. Ҳар чӣ бихоҳад, меофаринад. Ба ҳар кӣ бихоҳад, духтар мебахшад ва ба ҳар кӣ бихоҳад, писар мебахшад.'),
(4322, 42, 50, 'Ва ё ҳам писар диҳад ва ҳам духтар. Ва ҳар касро бихоҳад, ақим (нозо) мегардонад, зеро Ӯ донову тавоност!'),
(4323, 42, 51, 'Ҳеҷ башареро нарасад, ки Худо ҷуз ба ваҳй ё аз он сӯи парда бо ӯ сухан гӯяд. Ё фариштае мефиристад то ба фармони Ӯ ҳар чӣ бихоҳад, ба ӯ ваҳй кунад. Ӯ баландпояву ҳаким аст!'),
(4324, 42, 52, 'Ҳамчунин каломи Худро ба фармони Худ ба ту ваҳй кардем. Ту намедонистӣ китобу имон чист. Вале Мо онро нуре сохтем то ҳар як аз бандагонамонро, ки бихоҳем, ба он ҳидоят кунем ва ту ба роҳи рост роҳ нишон медиҳӣ,'),
(4325, 42, 53, 'роҳи он Худое, ки аз они Ӯст ҳар чӣ дар осмонҳову замин аст. Огоҳ бошед, ки ҳамаи корҳо ба Худо бозмегардад!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Ҳо, мим.'),
(4327, 43, 2, 'Савганд ба ин китоби равшангар!'),
(4328, 43, 3, 'Мо ин китобро Қуръоне арабӣ қарор додем, бошад, ки ба ақл дарёбед.'),
(4329, 43, 4, 'Ва он дар уммулкитоб аст дар назди Мо: китобе арҷманду ҳикматомез.'),
(4330, 43, 5, 'Оё ба он сабаб, ки мардуме аз ҳад гузашта ҳастед, аз шумо рӯй гардонем ва Қуръонро аз шумо дареғ дорем?'),
(4331, 43, 6, 'Ба миёни пешиниён паёмбарони бисёре фиристодем.'),
(4332, 43, 7, 'Ва ҳеҷ паёмбаре бар онҳо фиристода намешуд, ки масхарааш накарда бошанд.'),
(4333, 43, 8, 'Онҳоро, ки аз онҳо боқувваттар буданд, ҳалок кардем. Ва достони пешиниён гузашт.'),
(4334, 43, 9, 'Агар аз онҳо бипурсӣ: «Чӣ касе осмонҳову заминро офаридааст?» Мегӯянд: «Онҳоро он ғолиби доно офаридааст!»'),
(4335, 43, 10, 'Он ки заминро бисоти шумо сохт ва бароятон роҳҳо дар он падид овард, бошад, ки ҳидоят шавед.'),
(4336, 43, 11, 'Ва он ки аз осмон об фиристод ба миқдор. Ва ба он сарзамини мурдаро зинда кардем. Шумо низ инчунин аз гӯрҳо берун оварда шавед'),
(4337, 43, 12, 'ва он ки ҳамаи ҷуфтҳоро биёфарид. Ва бароятон аз киштиҳо ва чорпоён маркабҳо сохт, ки савор шавед.'),
(4338, 43, 13, 'Ва чун бар пушти онҳо қарор гирифтед, неъмати Парвардигоратонро ёд кунед ва бигӯед: «Пок аст он кас, ки инҳоро роми мо кард, вагарна моро тавони он набуд.'),
(4339, 43, 14, 'Ва мо ба сӯи Парвардигорамон бозмегардем».'),
(4340, 43, 15, 'Ва барон Ӯ аз миёни бандагонаш фарзанде қоил шуданд. Одамӣ ба ошкоро носипос (ношукр) аст!'),
(4341, 43, 16, 'Оё Ӯ аз миёни махлуқонаш барои Худ духтарон баргирифта ва писаронро хоси шумо кардааст?'),
(4342, 43, 17, 'Ва чун ба яке аз онҳо муждаи таваллуди ҳамон чизеро диҳанд, ки ба Худои раҳмон нисбат дода, рӯяш сиёҳ гардад ва пур аз хашм шавад.'),
(4343, 43, 18, 'Оё он ки ба ороиш парвариш ёфта (яъне духтар) ва дар ҳангоми ҷидол ошкор намегардад, аз они Худост?'),
(4344, 43, 19, 'Ва фариштагонро, ки бандагони Худоянд, зан пиндоштанд. Оё ба ҳангоми офаринишашон он ҷо ҳозир будаанд? Ба зудӣ ин шоҳидиашонро менависанд ва аз онҳо бозхост мешавад.'),
(4345, 43, 20, 'Гуфтанд: «Агар Худои раҳмон мехост, мо фариштагонро намепарастидем». Он чӣ мегӯянд, аз рӯи нодонист ва ғайри ба дурӯғ сухан намегӯянд.'),
(4346, 43, 21, 'Оё пеш аз Қуръон китобе барояшон нозил кардаем, ки акнун ба он ҷанг мезананд? (пайравӣ мекунанд).'),
(4347, 43, 22, 'На, мегӯянд: «Падаронамонро бар дине ёфтем ва мо аз паи онҳо меравем».'),
(4348, 43, 23, 'Ва он тавр пеш аз ту ба кадом деҳае бимдиҳандае фиристодем, мутанаъимонаш (соҳибнеъматонаш) гуфтанд: «Падаронамонро бар дине ёфтем ва мо ба амалҳои онҳо иқтидо (панравӣ) мекунем».'),
(4349, 43, 24, 'Гуфт: «Ҳатто агар барои шумо чизе биёварам, ки аз он чӣ падаронатонро бар он ёфта будед, ҳидояткунандатар бошад?» Гуфтанд: «Мо ба дине, ки шуморо ба он фиристодаанд, беимонем».'),
(4350, 43, 25, 'Пас аз онҳо интиқом гирифтем. Ва бингар, ки оқибати дурӯғбарорандагон чӣ гуна будааст?'),
(4351, 43, 26, 'Ва Иброҳим ба падару қавмаш гуфт: «Ман аз он чӣ шумо мепарастед, безорам,'),
(4352, 43, 27, 'ғайри он ки маро офарида, ки Ӯ роҳнамоиям хоҳад кард».'),
(4353, 43, 28, 'Ва ин суханро дар фарзандони худ сухане поянда кард, бошад, ки ба Худо бозгарданд.'),
(4354, 43, 29, 'Ва Ман инҳову падаронашонро аз зиндагӣ баҳраманд кардам, то он гоҳ ки ҳақ ва паёмбаре равшангар ба сӯяшон омад.'),
(4355, 43, 30, 'Чун ҳақ бар онҳо ошкор шуд, гуфтанд: «Ин ҷодуст ва мо ба он имон намеоварем».'),
(4356, 43, 31, 'Гуфтанд: «Чаро ин Қуръон бар марде аз бузургмардони он ду деҳа нозил нашудааст?»'),
(4357, 43, 32, 'Оё онон раҳмати Парвардигоратро тақсим мекунанд? Хол он ки мо рузии онҳоро дар ин зиндагии дунё миёнашон тақсим мекунем. Ва баъзеро ба мартаба болотар аз баъзе дигар қарор додаем, то баъзе баъзи дигарро ба хидмат гиранд. Ва раҳмати Парвардигорат аз он чӣ онҳо ҷамъ меоваранд, беҳтар аст.'),
(4358, 43, 33, 'Ва агар на он буд, ки ҳамаи мардум як уммат намешуданд, сақфҳои хонаҳои касонеро, ки Худои раҳмонро бовар надоранд, аз нуқра мекардем ва бар онҳо нардбонҳое, мениҳодем, то бар он боло раванд.'),
(4359, 43, 34, 'Ва барои хонаҳояшон низ дарҳое аз нуқра мекардем ва тахтҳое, ки бар онҳо такя зананд'),
(4360, 43, 35, 'ва аз ҳар гуна зинат. Ва ҳамаи инҳо баҳрамандии ночизи дунявист, ҳол он ки охират дар назди Парвардигори ту барои парҳезгорон аст.'),
(4361, 43, 36, 'Ҳар кас, ки аз ёди Худои раҳмон рӯй гардонад, шайтоне бар ӯ обаста мекунем,, ки ҳамеоша ҳамроҳаш бошад.'),
(4362, 43, 37, 'Ва он шайтонҳо ононро аз роҳи Худо бозмегардонанд, вале пиндоранд, ки ҳидоятёфтагонанд.'),
(4363, 43, 38, 'То он гоҳ ки назди Мо ояд, мегӯяд: «Эй кош, дурии ману ту (мисли) дурии машриқу мағриб буд. Ва ту чӣ ҳамроҳи баде буди».'),
(4364, 43, 39, 'Чун ситам кардед, он рӯз пушаймонӣ фоида накунад ва ҳар ду дар азоб шарик бошед.'),
(4365, 43, 40, 'Оё ту мехоҳӣ ба карон сухан бишунавонӣ ё курон ва онҳоеро, ки дар гумроҳии ошкор ҳастанд, роҳ нишон диҳӣ?'),
(4366, 43, 41, 'Ва агар туро бибарем, аз онҳо интиқом мегирем.'),
(4367, 43, 42, 'Ё он азоберо, ки ба онҳо ваъда додаем, ба ту нишон медиҳем, ки Мо бар онҳо тавоно ҳастем.'),
(4368, 43, 43, 'Пас дар он чӣ ба ту ваҳй шудааст, ҷанг бизан, (пайравӣ кун), ки ту бар роҳи рост ҳастӣ.'),
(4369, 43, 44, 'Ва Қуръон сабаби баландовоза гаштани ту ва қавми туст ва ба зудӣ бозхост шавед.'),
(4370, 43, 45, 'Аз паёмбарони Мо, ки пеш аз ту фиристодаем, бипурс: «Оё ғайри Худои раҳмон дигареро барои парастиши онҳо қарор дода будем?»'),
(4371, 43, 46, 'Ва албатта Мӯсоро ҳамроҳ бо оёти Худ бар Фиръавн ва раисони қавмаш фиристодем ва гуфт: «Ман фиристодаи Парвардигори ҷаҳониёнам!»'),
(4372, 43, 47, 'Чун оёти Моро бар онон арза дошт, ба ногоҳ ҳама аз он ба ханда афтоданд.'),
(4373, 43, 48, 'Ва ҳар мӯъҷизае, ки ба онҳо нишон додем, аз мӯъҷизаи дигар азимтар буд. Он гоҳ ҳамаро ба азоб гирифтор кардем, бошад, ки бозгарданд.'),
(4374, 43, 49, 'Гуфтанд: «Эй ҷодугар, Парвардигоратро бо он номҳое, ки бо ту ваҳй кардааст, баром мо бихон, ки мо ҳидоятшудагонем».'),
(4375, 43, 50, 'Чун азобро аз онҳо бардоштем, паймони худро шикастанд.'),
(4376, 43, 51, 'Фиръавн дар миёни мардумаш нидо дод, ки эй қавми ман, оё подшоҳии Мисру ин ҷӯйборон, ки аз зери пои ман ҷорӣ ҳастанд, аз они ман нестанд? Оё намебинед?'),
(4377, 43, 52, 'Оё ман беҳтарам ё ин марди хори залил, ки дуруст сухан гуфтан натавонад?'),
(4378, 43, 53, 'Чаро дастҳояшро ба дастбандҳои тилло наёростаанд? Ва чаро гурӯҳе аз фариштагон ҳамроҳаш наёмадаанд?'),
(4379, 43, 54, 'Пас Фиръавн қавми худро гумроҳ сохт, то ба ӯ итоъат карданд, ки мардуме табаҳ кор буданд!'),
(4380, 43, 55, 'Чун Моро ба хашм оварданд, аз онҳо интиқом гирифтем ва ҳамагонро ғарқ сохтем.'),
(4381, 43, 56, 'Ононро дар шумори гузаштагон ва достон барои ояндагон кардем.'),
(4382, 43, 57, 'Ва чун достони писари Марям оварда шуд, қавми ту ба шодмонӣ фарёд заданд'),
(4383, 43, 58, 'ва гуфтан, д: «Оё худоёни мо беҳтаранд ё Ӯ?» Ва ин суханро фақат барои ҷидол бо ту гуфтаанд, ки мардуме хусумат кунандаанд,'),
(4384, 43, 59, 'Ӯ фақат бандае буд, ки Мо неъматаш ато кардем ва мояи ибрати банӣ- Исроилаш гардонидем.'),
(4385, 43, 60, 'Агар мехостем, ба ҷои шумо фариштагоне падид меовардем, то дар рӯи замин ҷонишини шумо гарданд.'),
(4386, 43, 61, 'Ва бешубҳа ӯ (Исо) нишонаи фаро расидани қиёмат аст. Дар ин шак макунед ва ба Ман пайравӣ кунед! Ин аст роҳи рост.'),
(4387, 43, 62, 'Шайтон шуморо аз роҳ бознагардонад, зеро ӯ душмани ошкори шумост.'),
(4388, 43, 63, 'Ва чун Исо бо далелҳои равшани худ омад, гуфт: «Бароятон ҳикмат овардаам ва омадаам, то чизҳоеро, ки дар он ихтилоф мекунед, баён кунам. Пас аз Худо битарсед ва ба ман итоъат кунед.'),
(4389, 43, 64, 'Худои якто Парвардигори ману Парвардигори шумост. Ӯро бипарастед. Роҳи рост ин аст».'),
(4390, 43, 65, 'Гурӯҳҳо бо ҳам ихтилоф карданд. Пас вой бар ситамкорон аз азоби дардовари қиёмат!'),
(4391, 43, 66, 'Оё чашм ба роҳи чизе ҷуз қиёматанд, ки ногоҳ ва бехабар бар онҳо биёяд?.'),
(4392, 43, 67, 'Дар он рӯз дӯстон, — ғайр аз парҳезгорон, — душмани якдигаранд.'),
(4393, 43, 68, 'Эй бандагони Ман, дар он рӯз биме бар шумо нест ва шумо ғамгин намешавед.'),
(4394, 43, 69, 'Он касон, ки ба оёти Мо имон овардаанд ва таслими амри Мо шудаанд,'),
(4395, 43, 70, 'шумо ва ҷуфтҳоятон бо шодкомӣ ба биҳишт дохил шавед.'),
(4396, 43, 71, 'Қадаҳҳои заррин ва кӯзаҳоро дар миёнашон ба гардиш меоваранд. Дар он ҷост ҳар чи нафс орзу кунад ва дида аз он лаззат бибарад. Ва дар он ҷо ҷовидона хоҳед буд!'),
(4397, 43, 72, 'Ин биҳиштест, ки ба ивази корҳое, ки кардаед, ба меросаш мебаред.'),
(4398, 43, 73, 'Дар он ҷо бароятон меваҳои бисёр ҳаст, ки аз онҳо мехӯред.'),
(4399, 43, 74, 'Гунаҳкорон дар азоби ҷаҳаннам ҷовидонанд!'),
(4400, 43, 75, 'Азобашон кам намешавад ва онҳо аз ноумедӣ хомуш бошанд.'),
(4401, 43, 76, 'Мо ба онҳо зулме накардаем, онҳо худ ба худашон ситам кардаанд.'),
(4402, 43, 77, 'Фарёд бароваранд, ки эй молики дӯзах кош Парвардигори ту моро бимиронад. Мегӯяд: «На, шумо дар ин ҷо монданӣ ҳастед».'),
(4403, 43, 78, 'Мо шуморо бо ҳақ ошно кардем, вале бештаратон ҳақро бад дидед.'),
(4404, 43, 79, 'Оё онҳо дар эътиқоди худ пой фишурдаанд? Мо ҳам пой фишурдаем.'),
(4405, 43, 80, 'Оё мепиндоранд, Мо розу наҷво (суханони пинҳониашонро) намешунавем? Оре, расулони Мо назди онҳо ҳастанд ва менависанд.'),
(4406, 43, 81, 'Бигӯ: «Агар Худои раҳмонро фарзанде мебуд, ман аз нахустин парастандагон мебудам.'),
(4407, 43, 82, 'Пок аст Парвардигори осмонҳову замин ва Парвардигори арш аз он нисбатҳо, ки ба Ӯ медиҳенд.'),
(4408, 43, 83, 'Пас бигузорашон, то ба ҳамон суханони ботил машғул бошанд ва ба бозича саргарм, то он рӯзе, ки онҳоро ваъда додаанд, бирасад.'),
(4409, 43, 84, 'Ӯст, ки ҳам дар осмон Худост ва ҳам дар замин Худост ва ҳакиму доност!'),
(4410, 43, 85, 'Бартару бузургвортар аст он кас, ки фармонравоии осмонҳову замин ва ҳар чӣ дар миёни онҳост, аз они Ӯст ва илми фаро расидани қиёмат хоси Ӯст ва ба Ӯ бозгардонида мешавед.'),
(4411, 43, 86, 'Касоне ғайри Ӯро, ки ба худоӣ мехонанд, қодир ба шафоъати касе нестанд. Ғайри касоне, ки аз рӯи илм ба ҳақ шоҳидӣ дода бошанд.'),
(4412, 43, 87, 'Агар аз онҳо бипурсӣ, ки чӣ касе онҳоро офаридааст, мегӯянд: «Оллоҳ». Пас чаро аз ҳақ рӯй мегардонанд?'),
(4413, 43, 88, 'Гуфтори пайёмбар ин буд: «Эй Парвардигори ман, инҳо мардуме ҳастанд, ки имон намеоваранд!'),
(4414, 43, 89, 'Пас, аз онҳо даргузар ва бигӯ: «Саломат бошед!» Оре, ба зуди хоҳанд донист.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Ҳо, мим.'),
(4416, 44, 2, 'Савганд ба ин китоби равшангар!'),
(4417, 44, 3, 'Мо онро дар шаби мубораке нозил кардем. Мо бимдиҳанда будаем.'),
(4418, 44, 4, 'Дар он шаб ҳар фармоне мувофиқи ҳикмат содир мешавад,'),
(4419, 44, 5, 'фармоне аз ҷониби Мо. Ва фиристандаи он будаем.'),
(4420, 44, 6, 'Раҳматест аз ҷониби Парвардигорат ва албатта Ӯст шунавову доно.'),
(4421, 44, 7, 'Парвардигори осмонҳову замин ва он чӣ миёни ин ду аст, агар шумо яқинкунандаед.'),
(4422, 44, 8, 'Нест Худое ғайри Ӯ. Зннда мекунаду мемиронад. Парвардигори шумову Парвардигори падарони пешини шумост.'),
(4423, 44, 9, 'На, онҳо ҳамчунон ба шакки худ дилхушанд!'),
(4424, 44, 10, 'Пас мунтазири рӯзе бош, ки осмон ба ошкоро дуд биёварад.'),
(4425, 44, 11, 'Ва он дуд ҳамаи мардумро дар худ фурӯ гирад. Ва ин азобест дардовар.'),
(4426, 44, 12, '«Эй Парвардигори мо, ин азобро аз мо дур гардон, ки мо имон овардаем».'),
(4427, 44, 13, 'Куҷо панд мегиранд? Ҳол он ки чун паёмбари равшангар бар онҳо фиристода шуд,'),
(4428, 44, 14, 'аз ӯ рӯйгардон шуданд ва гуфтанд: «Девонаест таълимёфта!»'),
(4429, 44, 15, 'Азобро андаке бармедорем ва шумо боз ба дини худ бозмегардед.'),
(4430, 44, 16, 'Рӯзе онҳоро ба савлате (ҳамлае) сахт фурӯ гирем, ки Мо интиқомгирандаем!'),
(4431, 44, 17, 'Пеш аз онҳо қавми Фиръавнро озмудем ва паёмбаре бузургвор наздашон омад,'),
(4432, 44, 18, '«ки бандагони Худоро ба ман таслим кунед, ки ман паёмбаре аминам.'),
(4433, 44, 19, 'Ва низ бар Худо саркашӣ макунед, ки ман бо ҳуҷҷате равшан назди шумо омадаам.'),
(4434, 44, 20, 'Ва агар бихоҳед бар ман санг бизанед, ман ба Парвардигори худ ва Парвардигори шумо паноҳ мебарам,'),
(4435, 44, 21, 'ва агар ба ман имон намеоварад, аз ман канор гиред».'),
(4436, 44, 22, 'Пас Парвардигорашро дуо кард, ки инҳо мардуме муҷриманд (гунаҳгоранд).'),
(4437, 44, 23, 'Бандагони Маро шабҳангом равона кун, то аз паи шумо биёянд.'),
(4438, 44, 24, 'Дарёро ором пушти сар гузор, ки он лашкар ғарқшудагонанд.'),
(4439, 44, 25, 'Баъд аз худ чӣ боғҳову чашмасорҳо бар ҷой гузоштанд'),
(4440, 44, 26, 'ва киштзорҳову хонаҳои некӯ'),
(4441, 44, 27, 'ва неъмате, ки дар он ғарқи шодмонӣ буданд.'),
(4442, 44, 28, 'Ҳамчунин шуд ва Мо он неъматҳоро ба мардуме дигар мерос гузоштем.'),
(4443, 44, 29, 'На осмон бар онҳо гирист ва на замин ва на ба онҳо мӯҳлат дода шуд.'),
(4444, 44, 30, 'Ва мо банӣ-Исроилро аз он азоби хоркунанда халос кардем.'),
(4445, 44, 31, 'аз азоби Фиръавни саркаши аз ҳад гузаранда'),
(4446, 44, 32, 'ва аз рӯи илм бар аҳли ҷаҳонашон баргузидем.'),
(4447, 44, 33, 'Ва оёте ба онҳо ато кардем, ки дар он имтиҳоне ошкор буд.'),
(4448, 44, 34, 'Албатта инҳо мегӯянд:'),
(4449, 44, 35, '«Поёни кор ҷуз ҳамин марги нахустин нест ва мо дигар бор зинда намешавем.'),
(4450, 44, 36, 'Ва агар рост мегӯед, падарони моро ба ҷаҳон бозоваред».'),
(4451, 44, 37, 'Оё инҳо беҳтаранд ё қавми Туббаъ ва касоне, ки пеш аз он қавм буданд? Ҳамаро ҳалок кардем, ки муҷримон (гунаҳгорон) буданд.'),
(4452, 44, 38, 'Мо ин осмонҳову замин ва он чиро миёни онҳост, ба бозича наёфаридаем.'),
(4453, 44, 39, 'Онҳоро ба ҳақ офаридаем, вале бештарин намедонанд.'),
(4454, 44, 40, 'Ваъдагоҳи ҳама дар рӯзи доварӣ қиёмат аст.'),
(4455, 44, 41, 'Рӯзе, ки ҳеҷ дӯсте аз дӯсти худ чизеро дафъ накунад ва аз сӯи касе ёрӣ нашавад,'),
(4456, 44, 42, 'ғайри касе, ки Худо бар ӯ бубахшояд, зеро Ӯст ғолибу меҳрубон.'),
(4457, 44, 43, 'Ҳар оина (Албатта) дарахти зуккум'),
(4458, 44, 44, 'таъоми гуноҳкорон аст.'),
(4459, 44, 45, 'Монанди миси гудохта дар шикамҳо меҷушад.'),
(4460, 44, 46, 'Монанди ҷӯшидани оби ҷӯшон.'),
(4461, 44, 47, 'Бигиредаш ва ба сахтӣ ба миёни ҷаҳаннамаш бикашед'),
(4462, 44, 48, 'ва бар сараш оби ҷӯшон бирезед, то шиканҷа шавад.'),
(4463, 44, 49, 'Бичаш, ки ту ғолибу бузуррворӣ!'),
(4464, 44, 50, 'Ин ҳамон чизест, ки дар он шак мекардед.'),
(4465, 44, 51, 'Парҳезкорон дар ҷои амне ҳастанд,'),
(4466, 44, 52, 'дар боғҳову чашмасорҳо'),
(4467, 44, 53, 'либосҳое аз сундусу истабрақ мепушанд ва рӯбарӯи ҳам мешинанд.'),
(4468, 44, 54, 'Ҳамчунин ҳурулъайнро (ҳурони фарохчашм) ба ҳамсарияшон дароварем.'),
(4469, 44, 55, 'Дар осоиштагӣ ҳар меваеро, ки бихоҳанд, металабанд.'),
(4470, 44, 56, 'Дар он ҷо таъми маргро намечашанд, ғайри ҳамон марги аввалин. Ва онҳоро Худо аз азоби ҷаҳаннам нигоҳ доштааст.'),
(4471, 44, 57, 'Ин бахшишест аз ҷониби Парвардигорат ва ин пирӯзии бузургест!'),
(4472, 44, 58, 'Мо адои сухани худ бар забони ту осон кардем, бошад, ки панд гиранд!'),
(4473, 44, 59, 'Ту мунтазир бош, ки онҳо низ мунтазиранд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Ҳо, мим.'),
(4475, 45, 2, 'Нозил шудани ин китоб аз ҷониби Худои ғолибу ҳаким аст.'),
(4476, 45, 3, 'Албатта дар осмонҳову замин нишонаҳои ибратест барои мӯъминон!'),
(4477, 45, 4, 'Ва дар офариниши шумо ва пароканда шудани ҷунбандагон ибратҳост барои аҳли яқин. (имон)'),
(4478, 45, 5, 'Омадушуди шабу рӯз ва ризқе, ки Худо аз осмон мефиристад ва замини мурдаро ба он зинда мекунад ва низ дар вазиши бодҳо ибратҳост барои оқилон.'),
(4479, 45, 6, 'Инҳо оёти Худост, ки ба ростӣ бар ту тиловат мекунем. Ғайри Худову оёташ ба кадом сухан имон меоваранд?'),
(4480, 45, 7, 'Вой бар ҳар дурӯғбофи гунаҳкоре!'),
(4481, 45, 8, 'Оёти Худоро, ки бар ӯ хонда мешавад, мешунавад. Он гоҳ ба гарданкашӣ сахт меистад, чунон ки гӯӣ ҳеҷ нашунидааст. Пас ба азобе дардовараш башорат (хушхабар) деҳ.'),
(4482, 45, 9, 'Чун чизе аз оёти Моро фаро гирад, ба масхарааш мегирад, инҳо сазовори азобе хоркунандаанд.'),
(4483, 45, 10, 'Пеши рӯяшон ҷаҳаннам аст. Ва моле, ки ба даст овардаанд ва касоне, ки ғайри Худои якто ба худоӣ гирифтаанд, ба ҳолашон фоида накунад. Онҳорост азобе бузург!'),
(4484, 45, 11, 'Ин раҳнамоиест. Ва онон, ки ба оёти Парвардигорашон имон намеоваранд, барояшон азобест ва сахттарин азобҳои дардовар.'),
(4485, 45, 12, 'Худост, ки дарёро роми шумо кард, то дар он ба фармони Ӯ киштиҳо равон бошанд ва талаби маъишат кунед, бошад, ки шукргузор бошед!'),
(4486, 45, 13, 'Роми шумо сохт он чӣ дар осмонҳост ва он чӣ дар замин аст. Ҳама аз они Ӯст. Дар ин барои фикркунандагон ибратҳоест.'),
(4487, 45, 14, 'Ба касоне, ки имон овардаанд, бигӯ: «Аз хатои касоне, ки ба рӯзҳои Худо бовар надоранд, даргузаред, то худ он мардумро ба ҷазои амалҳое, ки муртакиб шудаанд (кардаанд), ҷазо диҳад.'),
(4488, 45, 15, 'Ҳар кас кори шоистае кунад, ба нафъи худи ӯст ва ҳар кас муртакиби кори баде шавад, ба зиёни ӯст. Сипас ҳама ба сӯи Парвардигоратон бозгардонда мешавед.'),
(4489, 45, 16, 'Мо ба банӣ-Исроил китоб ва илми довариву паёмбарӣ ато кардем ва аз чизҳои покизаву хуш рузияшон кардем. Ва бар ҷаҳониён бартарияшон (беҳтариашон) додем.'),
(4490, 45, 17, 'Ва онҳоро дар бораи он амр далоиле равшан додем. Ва дар он аз рӯи ҳасаду кина ихтилоф карданд, он гоҳ, ки дониш ёфтанд. Худо дар рӯзи қиёмат дар он чӣ ихтилоф (зиддият) мекарданд, довари хоҳад кард.'),
(4491, 45, 18, 'Пас туро ба роҳи дин андохтем. Бо он роҳ бирав ва аз паи хоҳиши нодонон марав.'),
(4492, 45, 19, 'Инҳо туро ҳеҷ аз азоби Худо дафъ намекунанд ва золимон дӯстдорони якдигаранд ва Худо дӯстдори парҳезгорон аст.'),
(4493, 45, 20, 'Ин Қуръон далелҳои равшани мардум ва ҳидояту раҳматест барои аҳли яқин.'),
(4494, 45, 21, 'Оё окон, ки кунандаи бадиҳо мешаванд, мепиндоранд, ки дар шумори касоне, ки имон овардаанд ва корҳои шоиста кардаанд, қарорашон медиҳем? Ва оё зиндагиву маргашон яксон аст? Чӣ бад доварӣ мекунанд.'),
(4495, 45, 22, 'Ва Худо осмонҳову заминро ба ҳақ биёфарид, то ҳар касеро баробари коре, ки кардааст, ҷазо диҳад ва ба онҳо ситам нашавад.'),
(4496, 45, 23, 'Оё он касро, ки ҳавасашро чун худои худ гирифт ва Худо аз рӯи илм гумроҳаш кард ва бар гӯшу дилаш мӯҳр ниҳод ва бар дидагонаш парда афканд, дидаӣ? Агар Худо ҳидоят накунад, чӣ касе ӯро ҳидоят хоҳад кард? Чаро панд намегиред?'),
(4497, 45, 24, 'Ва гуфтанд: «Ҷуз зиндагии дунявии мо ҳеҷ нест. Мемирему зинда мешавем ва моро ғайри даҳр ҳалок накунад». Ононро ба он донише нест ва фақат дар пиндоре ҳастанд.'),
(4498, 45, 25, 'Ва чун оёти равшани Мо бар онҳо тиловат шавад, ҳуҷҷаташон ин аст, ки мегӯянд: «Агар рост мегӯед, падарони моро зинда кунед».'),
(4499, 45, 26, 'Бигӯ: «Худост, ки шуморо зинда мекунад, пас мемиранад ва сипас ҳамаро дар рӯзи қиёмат, — ки шаке дар он нест, — ҷамъ меоварад. Вале бештари мардум намедонанд!»'),
(4500, 45, 27, 'Аз они Худованд аст фармонравоии осмону замин. Ва он рӯз, ки қиёмат барпо шавад, аҳли ботил зиён хоҳанд кард.'),
(4501, 45, 28, 'Ҳар умматеро бубинӣ, ки ба зону даромадааст ва ҳар умматеро барои гирифтани номаи аъмолаш бихонанд. Дар чунин рӯзе дар баробари амалҳое, ки кардаед, ҷазо мебинед.'),
(4502, 45, 29, 'Ин навиштаи Мост, ки ба ҳақ сухан мегӯяд, зеро Мо корҳоеро, ки мекардаед, менавиштаем.'),
(4503, 45, 30, 'Аммо касонеро, ки имон овардаанд ва корҳои шоиста мекунанд. Парвардигорашон дохил дар раҳмати Худ мекунад ва ин зафари ошкор аст.'),
(4504, 45, 31, 'Аммо, эй онон, ки имон наёвардаед, магар оёти Мо бар шумо хонда намешуд ва шумо такаббур менамудед ва мардуме гуноҳкор будед?'),
(4505, 45, 32, 'Чун гуфта мешуд, ки ваъдаи Худо ҳақ аст ва дар қиёмат шакке нест, мегуфтед: «Мо намедонем қиёмат чист? Фақат андак гумоне мебарем ва ба яқин (имони қавӣ) нарасидаем»,'),
(4506, 45, 33, 'Амалҳои зишташон дар баробарашон ошкор шуд ва он чизе, ки масхарааш мекарданд гирд бар гирдашонро бигирифт.'),
(4507, 45, 34, 'Ба онҳо гуфта шавад: «Имрӯз фаромушатон мекунем, ҳамчунон, ки шумо дидори чунин рӯзатонро фаромӯш карда будед. Ҷойгоҳатон дар оташ аст ва шуморо ҳеҷ ёваре нест.'),
(4508, 45, 35, 'Ва, ин ба ҷазои он аст, ки оёти Худоро ба масхара мегирифтед ва зиндагии дунявӣ шуморо бифирефт». Пас имрӯз аз ин оташ касе берунашон набарад ва касе узрашонро қабул накунад,'),
(4509, 45, 36, 'Ситоиш аз они Худост — Парвардигори осмонҳову Парвардигори замин ва Парвардигори ҷаҳониён.'),
(4510, 45, 37, 'Бузургворӣ дар осмонҳову замин хоси Ӯст ва Ӯст ғолибу ҳаким!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Ҳо, мим.'),
(4512, 46, 2, 'Нозил шудани ин китоб аз ҷониби Худои ғолибу ҳаким аст.'),
(4513, 46, 3, 'Мо осмонҳову замин ва он чиро, ки дар миёни он дӯст, ба ҳак, ва то муддате муъайян офаридаем. Ва кофирон аз он чӣ онҳоро метарсонанд, рӯй мегардонанд.'),
(4514, 46, 4, 'Бигӯ: «Аз онҳое, ки ғайри Оллоҳ ба худоӣ мехонед, хабар диҳед. Ба ман нишон диҳед, ки аз ин замин чӣ чизро офаридаанд? Ё дар хилқати (офаридани) осмонҳо ширкатдоштаанд? Агар рост мегӯед, барои ман китобе, ки пеш аз ин Қуръон омада бошад ё агар донише аз пешиниён мондааст, биёваред!'),
(4515, 46, 5, 'Ва кист гумроҳтар аз он, ки ба ҷои Оллоҳ чизеро ба худои мехонад, ки то рӯзи қиёмат ба ӯ ҷавоб намедиҳад, ва бутон аз дуъои бутпарастон бехабаранд.'),
(4516, 46, 6, 'Ва чун дар қиёмат мардумро ҷамъ оранд, бутон бо парастандагони худ душман бошанд ва аз ибодаташон мункиранд.'),
(4517, 46, 7, 'Чун оёти Мо ба равшанӣ ба онон хонда шавад, кофирон ҳақиқатеро, ки барояшон нозил шудааст, гӯянд: «Ҷодуии ошкор аст».'),
(4518, 46, 8, 'Ё мегӯянд: «Ин китоб дурӯғест, ки худ бофтааст!» Бигӯ: «Агар ман онро чун дурӯғе ба ҳам бофта бошам, шумо наметавонед хашми Худоро аз ман боздоред. Худо аз он таънаҳо, ки ба он мезанед, огоҳтар аст. Ва шоҳидии Ӯ миёни ману шумо кофист. Ва Ӯст бахшояндаву меҳрубон!»'),
(4519, 46, 9, 'Бигӯ: «Ман аз миёни дигар паёмбарон навомада (аввалин) нестам ва намедонам, ки бар ман ё бар шумо чӣ хоҳад шуд. Ман ба чизе ғайри он чӣ ба ман ваҳй мешавад, пайравӣ намекунам ва ман фақат бимдиҳандае ошкор ҳастам».'),
(4520, 46, 10, 'Бигӯ: «Чӣ мекунед, агар Қуръон аз ҷониби Худо бошад ва шумо ба он имон наёваред? Яке аз банӣ-Исроил ба он шоҳидӣ дод ва имон овард. Вале шумо гарданкашӣ мекунед. Худо мардуми ситамкорро ҳидоят намекунад!»'),
(4521, 46, 11, 'Кофирон мӯъминонро гуфтанд: «Агар дар он хайре мебуд, инҳо аз мо пештар қабул намекарданд». Ва чун ба он роҳ наёфтанд, хоҳанд гуфт, ки ин дурӯғе дерина аст.'),
(4522, 46, 12, 'Ва пеш аз он китоби Мӯсо роҳнамову раҳмат буд. Ва ин китобест тасдиқкунандаи он ба забони арабӣ то ситамкоронро биме ва некӯкоронро хушхабаре бошад.'),
(4523, 46, 13, 'Албатта онон, ки гуфтанд, ки Парвардигори мо Оллоҳ аст, ва сипас пойдорӣ карданд, биме бар онҳо нест ва андӯҳгин намешаванд.'),
(4524, 46, 14, 'Инҳо ба мукофоти амалҳояшон аҳли биҳиштанд ва дар он ҷо ҷовидонаанд.'),
(4525, 46, 15, 'Одамиро ба некӣ кардан бо падару модари худ супориш кардем. Модараш бори ӯро ба душворӣ бардошт ва ба душворӣ бар замин ниҳод. Ва муддати ҳамл то аз шир бозгирифтанаш сӣ моҳ аст то чун ба синни ҷавонӣ расад ва ба чиҳилсолагӣ дарояд, гӯяд: «Эй Парвардигори ман, ба ман биёмӯз то шукри неъмате, ки бар ман ва бар падару модарам ато кардаӣ, ба он ҷой орам. Коре шоиста бикунам, ки Ту аз он хушнуд шавӣ ва фарзандони маро солеҳ кун. Ман ба Ту бозгаштам ва аз таслимшудагонам».'),
(4526, 46, 16, 'Инҳо касоне ҳастанд, ки корҳои некашонро мепазирем ва аз гуноҳашон дармегузарем. Дар зумраи аҳли биҳиштанд. Ҳар ваъдае, ки ба онҳо дода шуда, рост аст.'),
(4527, 46, 17, 'Ва он ки ба падару модараш гуфт: «Уф бар шумо, оё ба ман ваъда медиҳед, ки аз гӯрам бархезонанд ва ҳол он ки мардуме пеш аз ман будаанд, ки барнахостаанд? Ва он ду ба даргоҳи Худо истиғоса (додхоҳӣ) мекунанд ва, гӯянд: «Вой бар ту, имон биёвар, ки ваъдаи Худо ҳақ аст!» Мегӯяд: «Инҳо чизе ҷуз ҳамон афсонаи пешиниён нест!»'),
(4528, 46, 18, 'Дар бораи инҳо ҳамон сухан, ки дар бораи умматҳои пешин аз ҷинну инс гуфта шуда буд, ба ҳақиқат мепайвандад. Инҳо зиёнкунандагонанд.'),
(4529, 46, 19, 'Ва ҳар якро нисбат ба коре, ки кардааст, дараҷаҳост, то Худо ҷазои корҳояшонро ба тамомӣ бидиҳад ва ба он он ситам намешавад.'),
(4530, 46, 20, 'Ва рӯзе, ки кофиронро бар оташ арза кунанд: дар зиндагии дунявӣ аз чизҳои покиза ва хуш баҳраманд шудед, имрӯз ба азоби хори ҷазоятон медиҳанд. Ва ин ба он сабаб аст, ки дар замин бе ҳеҷ ҳаққе такаббурӣ мекардед ва бадкорӣ пеш гирифта будед,'),
(4531, 46, 21, 'Бародари қавми Одро ба ёд биёвар, ки чун қавми худро дар Аҳқоф бим дод — ва пеш аз ӯ паёмбароне буданд ва рафтанду пас аз ӯ паёмбароне омаданд, — ки ғайри Худои якторо напарастед, ки ман аз азоби рӯзе бузург бар шумо метарсам.'),
(4532, 46, 22, 'Гуфтанд: «Оё омадаӣ, то моро аз худоёнамон рӯйгардон созӣ? Агар рост мегӯӣ, ҳар чӣ ба мо ваъда додаӣ, биёвар».'),
(4533, 46, 23, 'Гуфт: «Инро Худо медонад ва ман он чиро ба он фиристода шудаам, ба шумо мерасонам. Вале мебинам, ки мардуме нодон ҳастед».'),
(4534, 46, 24, 'Чун абре диданд, ки аз ҷониби рӯдхонаҳошон меояд, гуфтанд: «Ин абре боронзост». На ин ҳамон чизест, ки онро ба шитоб металабидед. Бод аст ва дар он бод азобе дардовар,'),
(4535, 46, 25, 'ба фармони Парвардигораш ҳама чизро ҳалок мекунад. Чунон шуданд, ки акнун фақат хонаҳояшонро бинӣ. Ва Мо муҷримонро (гунаҳгоронро) ин тавр ҷазо медиҳем.'),
(4536, 46, 26, 'Ва онҳо чунон тавону қудрат дода будем, ки ба шумо надидаем. Барояшон гӯшу чашму дил қарор додем. Вале гӯшу чашму дилашон ба ҳолашон ҳеҷ фоида накард, зеро оёти Худоро инкор мекарданд, то он азобе, ки ба масхарааш мегирифтанд, онҳоро фурӯ шрифт.'),
(4537, 46, 27, 'Мо ҳамаи қарияҳоеро (деҳаҳоеро), ки атрофи шумо будаанд, ҳалок кардаем ва оётро гуногун баён кардем, шояд, ки бозгарданд.'),
(4538, 46, 28, 'Пас чаро он худоёне, ки ғайри Оллоҳ барои наздикӣ ба худоӣ гирифта буданд, ёрияшон накарданд, балки аз назарашон гум шуданд? Ия аст дурӯғу бофтаҳояшон!'),
(4539, 46, 29, 'Ва гурӯҳе аз ҷинро назди ту равона кардем, то Қуръонро бишнаванд. Чун ба ҳазраташ (наздаш) расиданд, гуфтанд: «Гӯш андозед! Чун ба поён омад, монанди бимдиҳандагоне назди қавми худ бозгаштанд,'),
(4540, 46, 30, 'Гуфтанд: «Эй қавми мо, мо китобе шунидем, ки баъд аз Мӯсо нозил шуда, китобҳои пешинро тасдиқ мекунад ва ба ҳақ ва роҳи рост роҳ менамояд,'),
(4541, 46, 31, 'Эй қавми мо, ин даъваткунанда ба Худоро қабул кунед ва ба ӯ имон биёваред, то Худо гуноҳонатонро бибахшояд ва шуморо аз азобе дардовар дар амон дорад.'),
(4542, 46, 32, 'Ва ҳар касе, ки ба ин даъваткунанда ҷавоб нагӯяд, наметавонад, дар рӯи замин аз Худо бигурезад ва ӯро ғайри Худо ҳеҷ ёваре нест ва дар гумроҳии ошкорест!»'),
(4543, 46, 33, 'Оё намебинанд, ки Худо яктост, он ки осмонҳову заминро биёфарид ва дар офаридани онҳо дарнамонд. Ӯ метавонад мурдагонро зинда кунад. Бале, Ӯ бар ҳар коре тавоност!'),
(4544, 46, 34, 'Рӯзе, ки кофирон ба оташ арза шаванд... Оё ин ҳақиқат нест? Гӯянд: «Бале, ба Парвардигорамон савганд!» Гӯяд: «Ба хотири он ки кофир будед, инак азобро бичашед!»'),
(4545, 46, 35, 'Пас пойдорӣ кун, ҳамчунон ки паёмбарони улулазм (боазм) пойдорӣ карда буданд, Ва дар уқубаташон шитоб макун. Он рӯз, ки он ваъдаеро, ки ба онҳо дода шуда, бингаранд, пиндоранд, ки ҷуз ба қадри соъате аз рӯз дар гӯр намонда буданд. Ин расонидани пайғом аст. Оё ҷуз нофармонон ба ҳалокат мерасанд?');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Худо амалҳои касонеро, ки кофир шуданд ва мардумро аз роҳи Худо боздоштанд, ботил сохтааст.'),
(4547, 47, 2, 'Худо гуноҳи касонеро, ки имон овардаанд ва корҳои шоиста кардаанд ва ба он чӣ бар Муҳаммад нозил шуда, — ки он ҳақ аст ва аз ҷониби Парвардигорашон, — имон овардаанд, аз онҳо дур кард ва корҳояшонро ба салоҳ овард.'),
(4548, 47, 3, 'Ва ин ба он сабаб аст, ки кофирон ба ботил пайравӣ карданд ва онон, ки имон оварданд, ба ҳаққе, ки аз ҷониби Парвардигорашон омада буд, пайравӣ кардаанд. Худо барои мардум инчунин мисол меорад.'),
(4549, 47, 4, 'Чун бо кофирон рӯбарӯ шудед, гарданашонро бизанед. Ва чун онҳоро сахт фурӯ кӯфтед, асирашон кунед ва сахт бибандед. Он гоҳ ё ба миннат озод кумед ё ба фидя (ба ивази пул). То он гоҳ, ки ҷанг ба поён ояд. Ва ин аст ҳукми Худо. Ва агар Худо мехост, аз онон интиқом мегирифт, вале хост то шуморо ба якдигар биёзмояд. Ва онон, ки дар роҳи Худо кушта шудаанд, амалҳояшонро ботил намекунад.'),
(4550, 47, 5, 'Ба зуди, ки ҳидояташон кунад ва корҳояшонро ба салоҳ оварад.'),
(4551, 47, 6, 'Ва ба биҳиште, ки барояшон васф кардааст, дохилашон созад.'),
(4552, 47, 7, 'Эй касоне, ки имон овардаед, агар Худоро ёрӣ кунед, шуморо ёрӣ хоҳад кард ва пойдорӣ хоҳад бахшид.'),
(4553, 47, 8, 'Ҳалокату шӯрбахтӣ бод бар кофирон. Худо амалҳояшонро ботил кардааст.'),
(4554, 47, 9, 'Зеро онон чизеро, ки Худо нозил кардааст, нохуш доранд. Худо низ амалҳояшонро нобуд кард.'),
(4555, 47, 10, 'Оё дар замин сайр накардаанд, то бингаранд, ки оқибати касоне, ки пеш аз онҳо будаанд, чӣ гуна будааст? Худо ҳалокашон кард ва кофирон низ оқибате ончунон хоҳанд дошт.'),
(4556, 47, 11, 'Ин ба он сабаб аст, ки Худо ёвари \'касонест, ки имон овардаанд. Ва кофиронро ҳеҷ ёваре нест.'),
(4557, 47, 12, 'Худо касонеро, ки имон овардаанд ва корҳои шоиста мекунанд, ба биҳиштҳое, ки наҳрҳо аз он порист, дохил хоҳад кард. Ва кофирон аз ин ҷаҳон баҳраманд мешаванд ва чун чорпоён мехӯранд ва ҷойгоҳашон оташ аст.'),
(4558, 47, 13, 'Чӣ қарияҳое (деҳаҳое), ки мардумаш аз мардуми қарияи ту, ки аз он берунат карданд, хеле пурқувваттар буданд, ки ҳалокашон кардем ва ҳеҷ ёрикунандае надоштанд.'),
(4559, 47, 14, 'Оё касе, ки аз ҷониби Парвардигораш далели равшане дорад, монанди касонест, ки кирдори бадашон дар назарашон ороста шуда ва аз паи ҳавоҳои худ мераванд?'),
(4560, 47, 15, 'Васфи биҳиште, ки ба парҳезгорон ваъда дода шуда, ин аст, ки дар он наҳрҳоест аз обҳое тағйирнопазир ва наҳрҳое аз шире, ки таъмаш дигаргун намешавад ва наҳрҳое аз шароб, ки ошомандагон аз он лаззат мебаранд ва наҳрҳое аз асали мусаффо. Ва дар он ҷо ҳар гуна мева, ки бихоҳанд, ҳаст ва низ бахшоиши Парвардигорашон. Оё биҳиштиён монанди касоне ҳастанд, ки дар оташи ҷовидонаанд ва ононро аз обе ҷӯшон меошомананд, чунон ки рудаҳояшон тика-тика мешавад?'),
(4561, 47, 16, 'Баъзе ба ту гӯш медиҳанд, то он гоҳ ки аз назди ту берун раванд, аз донишмандон мепурсанд: «Ин чӣ суханоне буд, ки мегуфт?» Худо бар дилҳояшон мӯҳр ниҳодааст ва аз паи ҳавоҳои худ рафтаанд.'),
(4562, 47, 17, 'Онон, ки ҳидоят ёфтаанд, Худо ба ҳидояташон меафзояд ва парҳезгорияшон ато мекунад.'),
(4563, 47, 18, 'Оё танҳо мунтазири онанд, ки ба ногоҳ қиёмат фаро расад? Ҳар оина (Албатта) нишонаҳои қиёмат ошкор шудааст. Ва чун фаро расад, панд гирифтанашонро чӣ фоида?'),
(4564, 47, 19, 'Пас бидон, ки ҳеҷ худое ҷуз Оллоҳ нест. Аз гуноҳи худ ва аз гуноҳи мардону занони мӯъмин бахшоиш бихоҳ. Худо медонад, ки рӯз ба куҷо меравед ва шаб ба куҷо меоромед,'),
(4565, 47, 20, 'Касоне, ки имон овардаанд, мегӯянд: «Чаро аз ҷониби Худо сурае нозил намешавад?» Чун сурае аз муҳқамот нозил шавад, ки дар оя сухан аз ҷанг рафта бошад, ононро, ки дар дилашон маразе ҳаст, бинӣ, ки чун касе, ки беҳушии марг бар ӯ ғолиб шуда, ба ту менигаранд. Пас барояшоя шоистатар'),
(4566, 47, 21, 'фармонбардорист ва сухани некӯ гуфтан. Ва чун тасмим ба ҷанг гирифта шуд, агар бо Худо садоқат кунанд, барояшон беҳтар аст.'),
(4567, 47, 22, 'Оё агар ба ҳукумат расидед, мехоҳед дар замин фасод кунед ва пайванди хешовандиятонро бибуред?'),
(4568, 47, 23, 'Инҳоанд, ки Худо лаънаташон кардааст ва гӯшҳояшонро кару чашмонашонро кӯр сохтааст.'),
(4569, 47, 24, 'Оё дар Қуръон намеандешанд ё бар дилҳояшон қуфлҳост?'),
(4570, 47, 25, 'Шайтон амалҳои касонеро, ки баъд аз ошкор шудани роҳи ҳидоят муртад шуданд (аз дин гаштанд) ва бозгаштанд, дар назарашон биёрост ва орзуяшон дароз кард.'),
(4571, 47, 26, 'Ва ин ба он сабаб аст, ки ба он гурӯҳ, ки оёти Худоро нохуш медоштанд, мегуфтанд: «Мо дар баъзе аз корҳо фармонбардори шумо ҳастем. Ва Худо аз розашон огоҳ аст.'),
(4572, 47, 27, 'Чӣ гунаанд, он гоҳ ки фариштагон онҳоро мемиронанд ва бар рӯ ва пуштҳояшон мезананд?'),
(4573, 47, 28, 'Ин ба ҷазои он аст, ки ба он чӣ Худоро ба хашм меоварад, пайравӣ мекардаанд ва ба он чӣ хушнудаш месохта, бадбинӣ доштаанд. Худо низ амалҳояшонро нобуд кард.'),
(4574, 47, 29, 'Оё онон, ки дар дилашон маразест, мепиндоранд, ки Худо кинаеро, ки дар дил пинҳон доранд, ошкор нахоҳад кард?'),
(4575, 47, 30, 'Агар бихоҳем, онҳоро ба ту менамоёнем ва ту онҳоро ба симояшон \'ё аз шеваи суханашон хоҳӣ шинохт ва Худо аз амалҳоятон огоҳ аст.'),
(4576, 47, 31, 'Ва шуморо меозмоем, то муҷоҳидон ва собиронатонро маълум дорем ва хабарҳоятонро ошкор кунем.'),
(4577, 47, 32, 'Касоне, ки имон наёварданд ва аз роҳи Худо рӯйгардон шуданд ва бо он ки роҳи ҳидоят барояшон ошкор шуда буд, бо паёмбар мухолифат намуданд, ҳеҷ зиёне ба Худо нахоҳанд расонид ва Худо амалҳояшонро нобуд хоҳад кард.'),
(4578, 47, 33, 'Эй касоне, ки имон овардаед, ба Худо итоъат кунед ва ба паёмбар итоъат кунед ва аъмоли худро ботил масозед.'),
(4579, 47, 34, 'Худо касонеро, ки кофир шуданд ва мардумро аз роҳи Худо боздоштанд ва дар куфр мурданд, ҳаргиз нахоҳад бахшонд.'),
(4580, 47, 35, 'Сустӣ наварзед, то даъват ба мусолиҳа кунед. Шумо бартар ҳастед ва Худо бо шумост ва аз подошҳоятон (мукофотҳоятон) нахоҳад кам кард,'),
(4581, 47, 36, 'Зиндагии инҷаҳонӣ фақат бозичаву беҳудагист. Ва агар имон биёваред ва парҳезгорӣ кунед, Худо мукофотҳоятонро хоҳад дод. Ва аз шумо молҳоятонро наметалабад.'),
(4582, 47, 37, 'Агар аз шумо моле талабад ва бардавом ҳам талабад, он гоҳ бахилӣ мекунед ва кинаҳои пинҳонатонро ошкор месозад.'),
(4583, 47, 38, 'Огоҳ бошед ки шуморо даъват мекунанд то дар роҳи Худо садақа кунед. Баъзе аз шумо бахили мекунанд, ва ҳар кас ки бахилӣ кунад, дар ҳаққи худ бахилӣ кардааст. Зеро Худо беҳоҷат аст ва шумо ҳоҷатмандонед. Ва агар рӯй бартобед, ба ҷои шумо мардуме дигар орад, ки ҳаргиз мисли шумо набошанд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Мо барои ту пирӯзии намоёнеро ҳукм кардаем.'),
(4585, 48, 2, 'То Худо гуноҳи туро, он чӣ пеш аз ин буда ва он чӣ пас аз ин бошад, барои ту бибахшояд ва неъмати худро бар ту комил кунад ва туро ба роҳи рост роҳ намояд.'),
(4586, 48, 3, 'Ва Худо ёрият кунад, ёрӣ кардане пирӯзмандона.'),
(4587, 48, 4, 'Ӯст, ки бар дилҳои мӯъминон оромиш фиристод, то бар имонашон пайваста бияфзояд. Ва аз они Худост лашкарҳои осмонҳову замин ва Худо донову ҳаким аст!'),
(4588, 48, 5, 'Ва то мардону занони мӯъминро ба биҳиштҳое дохил кунад, ки дар он наҳрҳо ҷорист ва дар он ҷо ҷовидонанд ва гуноҳонашонро нест кунад. Ва ин дар назди Худо комёбии бузургест!'),
(4589, 48, 6, 'Ва мардону занони мунофиқ ва мардону занони мушрикро, ки бар Худо бадгумонанд, \'азоб кунад. Бар онҳо бод мусибати бад ва Худо бар онҳо хашм гирифт ва лаънаташон кард ва ҷаҳаннамро барояшон омода кардааст ва ҷаҳаннам ҷойгоҳи бадест!'),
(4590, 48, 7, 'Аз они Худост лашкарҳои осмонҳову замин ва Худо ғолибу ҳаким аст!'),
(4591, 48, 8, 'Туро гувоҳидиҳандаву муждадиҳанда ва бимдиҳанда фиристодаем.'),
(4592, 48, 9, 'То ба Худову паёмбараш имон биёваред ва ёрияш кунеду бузургаш доред ва Худоро субҳгоҳу шомгоҳ тасбеҳ гӯед.'),
(4593, 48, 10, 'Онон, ки бо ту байъат (аҳд) мекунанд, ҷуз ин нест, ки бо Худо байъат мекунанд. Дасти Худо болои дастҳояшон аст. Ва ҳар кӣ байъатро бишканад, ба зиёни худ шикастааст. Ва ҳар кӣ ба он байъат, ки бо Худо бастааст, вафо кунад, ӯро музде некӯ диҳад.'),
(4594, 48, 11, 'Аз аъроби бодиянишин онон, ки аз ҷанг қафокашӣ карданд, ба ту хоҳанд гуфт: «Дороиву касони мо моро аз ҷанг боздоштанд, пас барои мо бахшоиш бихоҳ». Ба забон чизе мегӯяд, ки дар дилашон нест. Бигӯ: «Агар Худо бароятон зиёне бихоҳад ё суде бихоҳад, чӣ касе метавонад дар баробари Худо онро дигаргун кунад?» Балки Ӯст, ки ба корҳоятон огоҳ аст!'),
(4595, 48, 12, 'Ё мепиндоштед, ки паёмбар ва мӯъминон ҳаргиз назди касонашен боз нахоҳанд гашт ва дил ба ин хуш карда будед. Пиндори баде доштаед ва мардуме сазовори ҳалокат будаед.'),
(4596, 48, 13, 'Ва ҳар кас ба Худову паёмбараш имон наёвардааст, бидонад, ки барои кофирон оташе сӯзон омода кардаем.'),
(4597, 48, 14, 'Аз они Худост фармонравоии осмонҳову замин. Ҳар киро бихоҳад, мебахшад ва ҳар киро бихоҳад, азоб мекунад ва Худо бахшояндаву меҳрубон аст!'),
(4598, 48, 15, 'Чун барои гирифтани ғаниматҳо ба роҳ бияфтед, онон, ки аз ҷанг тахаллуф (қафокашӣ) намудаанд, хоҳанд гуфт: «Бигзоред, то мо ҳам аз паи шумо биёем! Мехоҳанд сухани Худоро дигаргун кунанд. Бигӯ: \'«Шумо ҳаргиз аз паи мо нахоҳед омад. Худо аз пеш чунин гуфтааст». Сипас хоҳанд гуфт: «Балки бар мо ҳасад мебаред?» На, инҳо ҷуз андаке намефаҳманд.'),
(4599, 48, 16, 'Ба аъроби бодиянишин, ки аз ҷанг тахаллуф намудаанд, бигӯ: «Ба зудӣ барои ҷанг ба мардуме сахт нерӯманд даъват карда мешавед, ки бо онҳо биҷангед ё мусалмон шаванд. Агар итоъат кунед, Худоятон музде некӯ хоҳад дод ва агар, ҳамчунон ки пеш аз ин сар бартофтаед, сар бартобед, шуморо ба азобе дардовар азоб мекунад».'),
(4600, 48, 17, 'Бар кӯр ҳараҷе (гуноҳе) нест ва бар ланг хараҷе нест ва бар бемор хараҷе нест. Ва ҳар кӣ ба Худову паёмбараш итоъат кунад, ӯро ба биҳиштҳое дохил мекунад, ки дар он наҳрҳо равон аст. Ва ҳар кӣ сар бартобад, ба азобе дардовараш азоб мекунад.'),
(4601, 48, 18, 'Худо аз мӯъминон, он ҳангом ки дар зери дарахт бо ту байъат карданд, хушнуд гашт ва донист, ки дар дилашон чӣ мегузарад. Пас оромиш бар онҳо нозил кард ва ба фатҳе (ғалабае) наздик мукофоташон дод.'),
(4602, 48, 19, 'ва ба ғаниматҳои бисёр, ки ба даст меоваранд. Ва Худо ғолибу ҳаким аст!'),
(4603, 48, 20, 'Худо ба шумо ваъдаи ғаниматҳои бисёр додааст, ки ба даст меоваред ва ин ғаниматро зудтар ато кард ва шуморо аз осеби мардумон амон бахшид, то барои мӯъминон ибрате бошад ва ба роҳи рост ҳидояташон кунад.'),
(4604, 48, 21, 'Ва ғаниматҳои дигаре, ки ҳанӯз ба онҳо даст наёфтаед. Ҳар оина (Албатта) Худо ба он (бо илмаш) иҳота дорад ва Ӯ ба ҳар коре тавоност.'),
(4605, 48, 22, 'Ва агар кофирон бо шумо ба ҷанг бархезанд, пушт гардонда бигрезанд ва дигар ҳеҷ дӯсту ёригаре намеёбанд.'),
(4606, 48, 23, 'Ин суннати Худост, ки аз пеш чунин будааст ва ту дар суннати Худо дигаргунӣ \'нахоҳӣ ёфт!'),
(4607, 48, 24, 'Ӯст, ки чун дар батни (мобайни) Макка бар онҳо ғолибиятатон дод, дасти онҳоро аз шумо ва дасти шуморо аз онҳо боздошт. Ва Худо ба корҳое, ки мекардед, огоҳу бино буд!'),
(4608, 48, 25, 'Инҳо ҳамонҳоянд, ки кофир шуданд ва шуморо аз масҷидулҳаром боздоштанд ва нагузоштанд, ки қурбонӣ ба қурбонгоҳаш бирасад. Агар мардони мусалмону занони мусалмоне, ки онҳоро намешиносед, дар миёни онҳо набуданд ва бими он набуд, ки онҳоро зери пой кунед ва нодониста гуноҳ содир кунед, Худо дасти шуморо аз онҳо бознамедошт. Ва Худо ҳар киро бихоҳад, шомили раҳмати Худ гардонад. Агар аз якдигар ҷудо мебуданд, кофиронашонро ба азобе дардовар азоб мекардем.'),
(4609, 48, 26, 'Он гоҳ ки кофирон тасмим гирифтанд, дар дили худ таъассубро, таъассуби ҷоҳилӣ, Худо низ оромиши Худро бар дили паёмбараш ва мӯъминон фурӯд овард ва ба тақво лозимашон кард, ки инҳо ба тақво сазовортару шоистатар буданд ва Худо бар ҳар чизе доност!'),
(4610, 48, 27, 'Худо хоби паёмбарашро рост баровард, ки гуфта буд: «Агар Худо бихоҳад, эмин гурӯҳе сартарошида ва гурӯҳе мӯй кӯтоҳ карда бе ҳеҷ тарс ба масҷидулҳаром дохил мешавед. Ӯ чизҳое медонист, ки шумо намедонистед. Ва ҷуз он дар ҳамин наздикӣ фатҳе насиби шумо карда буд.'),
(4611, 48, 28, 'Ӯст, ки паёмбарашро ба ҳидоят ва дини ҳақ фиристод, то он динро бар ҳамаи динҳо ғолиб гардонад. Ва Худо барои шоҳидӣ дидан кофист!'),
(4612, 48, 29, 'Муҳаммад — паёмбари Худо ва касоне, ки бо ӯ ҳастанд, бар кофирон сахтгиранд ва бо якдигар меҳрубон. Ононро бинӣ, ки руқӯъ мекунанд, ба саҷда меоянд ва талабкунандаи фазлу хушнудии Худо ҳастанд. Нишонашон асари саҷдаест, ки бар чеҳраи онҳост. Ин аст васфашон дар Таврот ва дар Инҷил, мисли зироате ҳастанд, ки гиёҳ барорад ва он ҷавона маҳкам шавад ва бар поҳои худ биистад ва деҳқононро ба ҳайрат водорад, то он ҷо ки кофиронро ба хашм оварад. Худо аз миёни онҳо касонеро, ки имон овардаанд, ва корҳои шоиста кардаанд, бахшоиш ва мукофоте бузург ваъда додааст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Эй касоне, ки имон овардаед, бар Худову паёмбари Ӯ пешдастӣ макунед ва аз Худо битарсед, зеро Худо шунавову доност!'),
(4614, 49, 2, 'Эй касоне, ки имон овардаед, садои худро аз садои паёмбар баландтар накунед ва ҳамчунон, ки бо якдигар баланд сухан мегӯед, бо ӯ ба овози баланд сухан нагӯед, ки амалҳоятон ночиз шавад ва шумо огоҳ нашавед.'),
(4615, 49, 3, 'Касоне, ки дар назди паёмбари Худо садояшонро поин меоваранд, ҳамонҳоянд, ки Худо дилҳояшонро ба такво озмуда аст. Онҳоро бахшоишу музди бисёр аст.'),
(4616, 49, 4, 'Онҳое, ки аз он сӯи ҳуҷраҳо туро нидо медиҳанд, бештар бехирадонанд.'),
(4617, 49, 5, 'Агар сабр, мекарданд, то ту худ берун меомадӣ ва назди онҳо мерафтӣ, барояшон беҳтар мебуд ва Худо бахшояндаи меҳрубон аст!'),
(4618, 49, 6, 'Эй касоне, ки имон овардаед, агар фосиқе бароятон хабаре овард, таҳқиқ кунед, мабод аз рӯи нодонӣ ба мардуме осеб (ранҷ) бирасонед, он гоҳ, аз коре, ки кардаед, пушаймон шавед.'),
(4619, 49, 7, 'Ва бидонед, ки паёмбари Худо дар миёни шумост. Агар дар бисёре аз корҳо аз шумо фармон бибарад, ба ранҷ меафтед. Вале Худо имонро маҳбуби шумо сохт ва онро дар дилатон биорост ва куфру фисқу нофармониро дар назаратон бад гардонид. Инҳо худ роҳёфтагонанд.'),
(4620, 49, 8, 'Бахшишу неъматест аз ҷониби Худо ва Худо донову ҳаким аст!'),
(4621, 49, 9, 'Ва агар ду гурӯҳ аз мӯъминон бо якдигар ба чанг бархостанд, миёнашон оштӣ андозед. Ва агар як гурӯҳ бар дигаре даст дарозӣ кард, бо он ки даст дарозӣ кардааст, биҷангед, то ба фармони Худо бозгардад. Пас агар бозгашт, миёнашон сулҳе одилона барқарор кунед ва адолат кунед, ки Худо одилонро дӯст дорад,'),
(4622, 49, 10, 'Ҳар оина (Албатта) мӯъминон бародаронанд. Миёни бародаронатон оштӣ андозед ва аз Худо битарсед, шояд, ки бар шумо раҳмат орад.'),
(4623, 49, 11, 'Эй касоне, ки имон овардаед, мабод, ки гурӯҳе аз мардон гурӯҳи дигарро масхара кунад, шояд он масхарашудагон беҳтар аз онҳо бошанд. Ва мабод, ки гурӯҳе аз занон гурӯҳи дигарро масхара кунад, шояд он масхарашудагон беҳтар аз онҳо бошанд. Ва аз ҳам айбчӯй макунед ва якдигарро ба лақабҳои зишт махонед. Бад аст унвони фисқ пас аз имон овардан. Ва касоне, ки тавба намекунанд, худ ситамкоронанд.'),
(4624, 49, 12, 'Эй касоне, ки имон овардаед, аз бисёре аз гумонҳо бипарҳезед. Зеро баъзе аз гумонҳо дар ҳадди гуноҳ аст. Ва дар корҳои пинҳонии якдигар ҷосусӣ макунед. Ва аз якдигар ғайбат макунед. Оё ҳеҷ як аз шумо дӯст дорад, ки гӯшти бародари мурдаи худро бихӯрад? Пас онро нохуш хоҳед дошт. Ва аз Худо битарсед, зеро Худо тавбапазиру меҳрубон аст!'),
(4625, 49, 13, 'Эй мардум, мо шуморо аз як марду як зан биёфаридем. Ва шуморо ҷамоъатҳо ва қабилаҳо кардем, то якдигарро бишиносед. Албатта гиромитарини шумо назди Худо парҳезгортарини шумост. Худо донову кордон аст!'),
(4626, 49, 14, 'Аъроби бодиянишин гуфтанд: «Имон овардем!» Бигӯ: «Имон наёва. рдаед. Бигӯед, ки таслим шудаем ва ҳанӯз имон дар дилҳоятон дохил нашудааст. Ва агар Худову паёмбарашро итоъат кунед, аз савоби амалҳои шумо кам карда намешавад, зеро Худо бахшояндаву меҳрубон аст».'),
(4627, 49, 15, 'Мӯьминон касоне ҳастанд, ки ба Худову паёмбари Ӯ имон овардаанд ва дигар шак накардаанд ва бо молу ҷони худ дар роҳи Худо ҷиҳод кардаанд. Инҳо ростгӯёнанд.'),
(4628, 49, 16, 'Бигӯ: «Оё мехоҳед Худоро аз диндории худ огоҳ кунед? Ҳол он ки Худо аз ҳар чӣ дар осмонҳову ҳар чӣ дар замин аст, огоҳ аст ва Ӯ ба ҳар чизе олим аст!»'),
(4629, 49, 17, 'Аз ин ки ислом овардаанд, бар ту миннат мекунанд. Бигӯ: «Ба хотири исломатон бар ман миннат мекунед, балки Худо ба он сабаб, ки шуморо ба имон роҳ намудааст, бар шумо миннат мениҳад, агар рост мегӯед!»'),
(4630, 49, 18, 'Албатта Худо ниҳони осмонҳову заминро медонад ва ба ҳар коре, ки мекунед, биност!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Қоф. Қасам ба ин Қуръони арҷманд!'),
(4632, 50, 2, 'Ки дар тааҷҷуб шуданд, аз ин ки аз миёни худашон бимдиҳандае сӯяшон омад, ва кофирон гуфтанд: «Ин чизе аҷиб аст.'),
(4633, 50, 3, 'Оё замоне, ки мурдему хок шудем, дигар бор зинда мешавем? Ин бозгаште мухол (номумкин) аст!»'),
(4634, 50, 4, 'Мо медонем, ки хок чӣ гуна аз (ҷасадҳои) онҳо кам мекунад. Ва китобе, ки ҳама чиз дар он нигаҳдорӣ шуда, назди Мост.'),
(4635, 50, 5, 'Оре, онҳо сухани ростеро, ки бар онҳо омада буд, дурӯғ шумурданд. Пас дар коре шӯрида афтоданд.'),
(4636, 50, 6, 'Оё ба ин осмон бар болои сарашон назар намекунашд, ки чӣ гуна онро бино кардаем ва оростаем ва ҳеҷ шикофе дар он нест?'),
(4637, 50, 7, 'Ва заминро густурдем ва дар он кӯҳҳои баланд афкандем ва аз ҳар гуна набототи хушманзар дар он рӯёнидем.'),
(4638, 50, 8, 'Бинише ва пандест барои ҳар бандае, ки ба Худо бозгардад.'),
(4639, 50, 9, 'Ва аз осмои обе пурбаракат фиристодем ва бо он боғҳову донаҳои даравшуданӣ рӯёнидем'),
(4640, 50, 10, 'ва нахлҳои баланд бо хушаҳои барҳам чида,'),
(4641, 50, 11, 'то ризқи баидагон бошад ва бо он об сарзамини мурдаро зинда кардем. Баромадан аз гӯр низ чунин аст.'),
(4642, 50, 12, 'Пеш аз онҳо қавми Нӯҳ ва асҳоби Рас ва Самуд такзиб карда (дурӯғ бароварда) буданд'),
(4643, 50, 13, 'ва қавми Оду Фиръавн ва қавми Лут'),
(4644, 50, 14, 'ва асҳоби Айка ва қавми Туббаъ ҳама паёмбаронро дурӯғ шумурданд ва сазовори ваъдаи азоби Ман шуданд.'),
(4645, 50, 15, 'Оё аз офариниши нахустин оҷиз шуда будем? На онҳо дар офариниши тоза дар шакканд.'),
(4646, 50, 16, 'Мо одамиро офаридаем ва аз васвасаҳои нафси ӯ огоҳ ҳастем, зеро аз раги гарданаш ба ӯ наздиктарем.'),
(4647, 50, 17, 'Ҳангоме ки он ду фариштаи фарогиранда дар ҷониби росту ҷониби чапи ӯ нишастаанд, ҳар чизро фаро мегиранд.'),
(4648, 50, 18, 'Ҳар каломе ки мегӯяд, албатта дар канори ӯ нозиру нависандае ҳозир аст.'),
(4649, 50, 19, 'Ба ростӣ, ки беҳушии марг фаро мерасад. Ин аст он чӣ аз он мегурехтӣ.'),
(4650, 50, 20, 'Ва дар сур дамида мешавад, Ин аст, рӯзи ваъдаҳои азоб!'),
(4651, 50, 21, 'Ҳар касе меояд ва бо ӯ касест, ки меронадаш ва касест, ки барояш шоҳидӣ медиҳад.'),
(4652, 50, 22, 'Ту аз ин ғофил будӣ. Мо парда аз баробарат бардоштем ва имрӯз чашмонат тезбин шудааст.'),
(4653, 50, 23, 'Фариштаи (муваккали) ӯ гӯяд: «Ин аст он чӣ ман омода кардаам».'),
(4654, 50, 24, 'Ҳар ношукри саркашро ба ҷаҳаннам бияндозед:'),
(4655, 50, 25, 'он ки аз бахшиш бахилӣ кард ва таҷовузкор буд ва шак меовард,'),
(4656, 50, 26, 'он ки бо Худои як то худои дигаре қарор дод. Пас ба азоби сахташ бипартоед!'),
(4657, 50, 27, 'Ҳамнишини ӯ гӯяд: «Эй Парвардигори мо, ман ӯро ба саркашӣ водор накардам, балки ӯ худ, сахт дар гумроҳӣ буд».'),
(4658, 50, 28, 'Мегӯяд: «Назди Ман муҷодала накунед. Ман пеш аз ин бо шумо сухан аз азоб гуфта будам.'),
(4659, 50, 29, 'Ваъдаи Ман дигаргун намешавад ва Ман ба бандагон зулм намекунам».'),
(4660, 50, 30, 'Рӯзе, ки ҷаҳаннамро мегӯем: «Оё пур шудаӣ?» Мегӯяд: «Оё ҳеҷ зиёдатӣ ҳаст?»'),
(4661, 50, 31, 'Ва биҳиштро барои парҳезгорон наздик биёваранд, то аз он дур набошанд.'),
(4662, 50, 32, 'Ин ҳамон чизест, ки ба ҳар тавбакунащдаи парҳезгоре ваъдааш дода буданд.'),
(4663, 50, 33, 'Онҳоеро, ки дар ниҳон аз Худои раҳмон метарсанд ва бо диле тавбакор омадаанд.'),
(4664, 50, 34, 'Гӯянд: «Дар амон аз ҳар азобе ба биҳишт дохил шавед. Ин рӯз рӯзи ҷовидонист».'),
(4665, 50, 35, 'Дар он ҷо ҳар чӣ бихоҳанд, ҳаст ва бештар аз он назди Мо ҳаст.'),
(4666, 50, 36, 'Пеш аз онҳо чӣ бисёр мардумеро, ки боқувваттар буданд ва дар шаҳрҳо сайру ҷустуҷӯ мекарданд, ба ҳалокат овардаем. Оё роҳи гурезе ҳаст?'),
(4667, 50, 37, 'Дар ин сухан барои соҳибдилон ё онон, ки бо ҳузур гӯш меандозанд, андарзест (пандест).'),
(4668, 50, 38, 'Мо осмонҳову замин, ва он чиро миёни онҳост, дар шаш рӯз офаридем ва ҳеҷ хастагӣ ба Мо нарасид.'),
(4669, 50, 39, 'Дар баробари он чӣ мегӯянд, пойдорӣ кун ва дар ситоиши Парвардигорат пеш аз баромадани офтоб ва пеш аз фаромадани он тасбеҳ гӯй.'),
(4670, 50, 40, 'Ва низ дар порае аз шаб ва баъд аз ҳар саҷда ӯро тасбеҳ гӯй.'),
(4671, 50, 41, 'Ва он гоҳ ки мунодӣ (нидокунанда, Исрофил) аз маконе наздик нидо медиҳад, гӯш фаро дор.'),
(4672, 50, 42, 'Рӯзе, ки он овози сахтро ба ҳақ мешунаванд, он рӯз рӯзи берун шудан аз гӯр аст.'),
(4673, 50, 43, 'Мо зинда мекунем ва мемиронем ва бозгашт назди Мост.'),
(4674, 50, 44, 'Рӯзе, ки замин бишкофад ва онҳо ба шитоб берун оянд ва ин ҷамъоварӣ барои Мо осон аст.'),
(4675, 50, 45, 'Мо ба он чӣ мегӯянд, донотарем ва ту ба онҳо зӯр намегӯӣ. Пас ҳар киро аз ваъдаи азоби Ман метарсад, ба Қуръон панд деҳ!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Савганд ба бодҳое, ки хок пароканда мекунанд,'),
(4677, 51, 2, 'савганд, ба абрҳои гаронбор,'),
(4678, 51, 3, 'савганд ба киштиҳое, ки ба осонӣ равонанд'),
(4679, 51, 4, 'ва савганд ба фариштагоне, ки тақсимкунандаи корҳоянд,'),
(4680, 51, 5, 'ки он чӣ шуморо ваъда медиҳанд, рост аст'),
(4681, 51, 6, 'ва рӯзи ҷазо омаданист.'),
(4682, 51, 7, 'Ва савганд ба осмон, ки ба ситорагон ороста аст,'),
(4683, 51, 8, 'ки шумо (дар хусуси пайғамбар) сухани гуногун мегӯед!'),
(4684, 51, 9, 'Аз ҳақ бозгардонида шавад, он ки бозгардониданашро хостаанд.'),
(4685, 51, 10, 'Марг бод бар он дурӯғгӯён,'),
(4686, 51, 11, 'онон ки ба ғафлат дар ҷаҳл фурӯ рафтаанд.'),
(4687, 51, 12, 'Мепурсанд: «Рӯзи ҷазо кай хоҳад буд?»'),
(4688, 51, 13, 'Рӯзест, ки бар оташ азобашон мекунанд.'),
(4689, 51, 14, '«Азоби худро бичашед! Ин аст он чизе, ки ба шитоб металабидед!»'),
(4690, 51, 15, 'Парҳезгорон дар боғҳову канори чашмасорон бошанд,'),
(4691, 51, 16, 'Он чиро Худо ба онҳо додааст, гирифтаанд. Зеро пеш аз он некӯкор буданд,'),
(4692, 51, 17, 'андаке аз шабро мехобиданд'),
(4693, 51, 18, 'ва ба ҳангоми саҳар истиғфор (тавба) мекарданд'),
(4694, 51, 19, 'ва дар молҳояшон барои пурсандаву маҳрум хаққе буд.'),
(4695, 51, 20, 'Ва дар замин барои ахли яқин ибратҳоест'),
(4696, 51, 21, 'ва низ дар вуҷуди худатон. Оё намебинед?'),
(4697, 51, 22, 'Ва ризқи шумо ва ҳар чӣ ба шумо ваъда шуда, дар осмон аст.'),
(4698, 51, 23, 'Пас савганд ба Парвардигори осмонаҳову замин, ки ин сухан ончунон ки сухан мегӯянд, ҳақ ва рост аст.'),
(4699, 51, 24, 'Оё достони меҳмонони гиромии Иброҳим ба ту расидааст?'),
(4700, 51, 25, 'Он гоҳ ки назди ӯ омаданду гуфтанд: «Салом!» Гуфт: «Салом! Шумо мардуми ношинохтаед!»'),
(4701, 51, 26, 'Дар ниҳон ва шитобон назди касони худ рафт ва гӯсолаи фарбеҳе овард.'),
(4702, 51, 27, 'Таъомро ба наздашон гузошту гуфт: «Чаро намехӯред?»'),
(4703, 51, 28, 'Ва аз онҳо бимнок шуд. Гуфтанд: «Матарс!» Ва ӯро ба фарзанде доно хушхабар доданд.'),
(4704, 51, 29, 'Ва занаш фарёдзанон омад ва бар рӯй заду гуфт: Ман пиразане нозоям».'),
(4705, 51, 30, 'Гуфтанд: «Парвардигори ту инчунин гуфтааст. Ва ӯ ҳакиму доност!»'),
(4706, 51, 31, 'Гуфт: «Эй расулон, ба чӣ кор омадаед?»'),
(4707, 51, 32, 'Гуфтанд: «Моро бар мардуме табаҳкор фиристодаанд,'),
(4708, 51, 33, 'то тиккаҳои сангҳои гили бар сарашон биборем,'),
(4709, 51, 34, 'ки бар он сангҳо аз ҷониби Парвардигорат барои аз ҳад, гузаштаҳо нишон гузоштаанд».'),
(4710, 51, 35, 'Пас ҳамаи касонеро, ки имон оварда буданд, берун бурдем.'),
(4711, 51, 36, 'Ва дар он шаҳр ғайри як хона ва фармонбардорон наёфтем.'),
(4712, 51, 37, 'Ва дар он сарзамин барои касоне, ки аз азоби дардовар метарсанд, нишоне боқӣ гузоштем.'),
(4713, 51, 38, 'Ва ибратест дар Мӯсо, он гоҳ ки ӯро бо ҳуҷҷате ошкор назди Фиръавн фиристодем.'),
(4714, 51, 39, 'Ва Ӯ бо ҳамаи қуввааш рӯй гардонду гуфт: «Ҷодугарест ё девонае!»'),
(4715, 51, 40, 'Ӯ ва лашкарҳояшро гирифтем ва ба дарё партофтем. Ва ӯ муставҷиби (сазовори) маломат буд.'),
(4716, 51, 41, 'Ва низ ибратест дар қавми Од, он гоҳ, ки боди ақимро бар онҳо фиристодем.'),
(4717, 51, 42, 'Бар ҳар чизе вазид, фақат устухоне пӯсидааш бар ҷой гузошт.'),
(4718, 51, 43, 'Ва низ ибратест дар қавми Самуд, он гоҳ ки ба онҳо гуфта шуд: «То замоне чанд бархурдор шавед».'),
(4719, 51, 44, 'Онон аз фармони Парвардигорашон сар боззаданд ва ҳамчунон ки менигаристанд, раъди тунде фурӯ гирифташон.'),
(4720, 51, 45, 'Истодан натавонистанд ва тавони интиқом надоштанд.'),
(4721, 51, 46, 'Ва аз пеш қавми Нӯҳро ҳалок кардем, ки қавме нофармон буданд.'),
(4722, 51, 47, 'Ва осмонро ба қувват барафроштем ва ҳаққо, ки Мо тавоноем.'),
(4723, 51, 48, 'Ва заминро густурдем ва чӣ некӯ густарандагонем!'),
(4724, 51, 49, 'Ва аз ҳар чиз ҷуфте биёфаридаем, шояд, ки ибрат гиред.'),
(4725, 51, 50, 'Пас ба сӯи Худованд бигрезед. Ман шуморо аз ҷониби Ӯ бимдиҳандае ошкорам.'),
(4726, 51, 51, 'Ва бо Худои якто худои дигароро мапарастед. Ман шуморо аз ҷониби Ӯ бимдиҳандае ошкорам.'),
(4727, 51, 52, 'Ҳамчунин бар онҳое, ки аз ин пеш буданд, ҳар паёмбаре, ки фиристода шуд, гуфтанд: «Ҷодугарест ё девонаест!»'),
(4728, 51, 53, 'Оё ба ин кор якдигарро васият карда буданд? На, худ мардуме тоғӣ (саркаш) буданд.'),
(4729, 51, 54, 'Пас аз онҳо рӯйгардон шав. Кас туро маломат нахоҳад кард.'),
(4730, 51, 55, 'Андарз (панд) бидеҳ, ки андарз мӯъминонро манфиат дорад.'),
(4731, 51, 56, 'Ҷинну инсро фақат ва фақат барои парастиши Худ офаридаам.'),
(4732, 51, 57, 'Аз онҳо ризқе намехоҳам ва намехоҳам, ки маро итъом кунанд (хӯронанд).'),
(4733, 51, 58, 'Худост рӯзидиҳанда. Ва Ӯст соҳиби қуввате сахт устувор!'),
(4734, 51, 59, 'Ин ситамкоронро аз азоб баҳраест монанди баҳрае, ки ёронашон доштанд. Пас ба шитоб чизе (азоб) аз Ман нахоҳанд!'),
(4735, 51, 60, 'Вой бар кофирон, аз он рӯз, ки онҳоро ваъда додаанд!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Қасам ба кӯҳи Тур'),
(4737, 52, 2, 'ва қасам ба китоби навишташуда,'),
(4738, 52, 3, 'дар сафҳае кушода (Қуръон)'),
(4739, 52, 4, 'ва қасам ба байтулмаъмур'),
(4740, 52, 5, 'ва қасам ба ин, сақфи барафрошта (осмон)'),
(4741, 52, 6, 'ва қасам ба дарёи моломол, (пур)'),
(4742, 52, 7, 'ки азоби Парвардигорат воқеъ шуданист.'),
(4743, 52, 8, 'Ва онро дафъкунандае нест.'),
(4744, 52, 9, 'Рӯзе, ки осмон сахт бичархад'),
(4745, 52, 10, 'ва кӯҳҳо ба шитоб равон шаванд,'),
(4746, 52, 11, 'пас дар он рӯз вой бар дурӯғбарорандагон,'),
(4747, 52, 12, 'онон, ки саргарми кордон ботили худанд!'),
(4748, 52, 13, 'Рӯзе, ки онҳоро ба қаҳр ба ҷониби ҷаҳаннам кашанд.'),
(4749, 52, 14, 'Ин аст он оташе, ки дурӯғаш мешумурдед.'),
(4750, 52, 15, 'Оё ин ҷодуст ё шумо намебинед?'),
(4751, 52, 16, 'Ба оташ дароед. Хоҳ бар он сабр кунед ё сабр накунад, фарқе накунад. Шуморо дар баробари корҳое, ки мекардаед, ҷазо медиҳанд.'),
(4752, 52, 17, 'Парҳезгорон дар биҳиштҳо ва неъматанд.'),
(4753, 52, 18, 'Аз он чӣ Парвардигорашон ба онҳо додааст, шодмонанд ва Худо онҳоро аз азоби ҷаҳаннам нигаҳ доштааст.'),
(4754, 52, 19, 'Ба музди корҳое, ки кардаед, бихӯреду биёшомед. Гувороятон бод.'),
(4755, 52, 20, 'Бар он тахтҳои канори ҳам чида такя мезананд. Ҳуриёнро ҳамсарашон мегардонем.'),
(4756, 52, 21, 'Касоне, ки худ имон оварданд ва фарзандонашон дар имон пайравияшон карданд. Фарзандонашонро бо онҳо якҷоя мекунем ва аз музди амалашон ҳеҷ кам намекунем, ки дар касе дар гарави кори худ аст.'),
(4757, 52, 22, 'Ва пайваста аз ҳар гуна, ки бихоҳанд, меваву гӯшт атояшон мекунем.'),
(4758, 52, 23, 'Дар он ҷо ҷоми май диҳанд ва ҷоми май ситонанд, ки дар он на сухани беҳуда бошад ва на гуноҳ кардан.'),
(4759, 52, 24, 'Ғуломонашон чун марвориди пинҳон дар садаф ба гирдашон мечарханд.'),
(4760, 52, 25, 'Пурсишкунон рӯй ба якдигар мекунанд.'),
(4761, 52, 26, 'Мегӯянд: «Пеш аз ин дар миёни касонамон танҳо мо аз Худо метарсидем.'),
(4762, 52, 27, 'Пас Худо бар мо миннат ниҳод ва моро аз азоби самум нигаҳ дошт.'),
(4763, 52, 28, 'Мо пеш аз ин Ӯро (бо дуъову илтиҷо) мехондем, ки Ӯ эҳсонкунандаву меҳрубон аст!»'),
(4764, 52, 29, 'Пандашон деҳ, ки ту ба баракати неъмати Парвардигорат на коҳин (ҷодугар) ҳастиву на маҷнун.'),
(4765, 52, 30, 'Ё мегӯянд: «Шоиресту мо барои вай мунтазири ҳаводиси рӯзгорем». (ки шояд бимирад).'),
(4766, 52, 31, 'Бигӯ: «Шумо мунтазир бимонед, ки ман низ бо шумо интизор мекашам».'),
(4767, 52, 32, 'Оё ононро рӯъёҳояшон (хобҳояшон) ба ин пиндорҳо кашонида ё худ, мардуме саркаш ҳастанд.'),
(4768, 52, 33, 'Ё мегӯянд: «Қуръонро худ сохтааст!» На, онҳо имон намеоваранд!'),
(4769, 52, 34, 'Агар рост мегӯянд, сухане монанди он биёваранд.'),
(4770, 52, 35, 'Оё бе ҳеҷ холиқе офарида шуданд, ё худ холиқи худанд?'),
(4771, 52, 36, 'Ё осмонҳову заминро ҳал кардаанд? На, ба яқин нарасидаанд.'),
(4772, 52, 37, 'Ё хазинаҳои Парвардигорат назди онҳост? Ё бар чизе тасаллут ёфтаанд? (ҳукумат ёфтаанд).'),
(4773, 52, 38, 'Ё нардбоне доранд, ки аз он боло мераванд ва гӯш меандозанд? Пас он касе, ки гӯш андохта бошад, ҳуҷҷате ошкор биёварад.'),
(4774, 52, 39, 'Ё худовандро духтарон асту шуморо писарон.'),
(4775, 52, 40, 'Ё аз онҳо музде металабӣ ва пардохти музд бар онҳо душвор аст?'),
(4776, 52, 41, 'Ё илми ғайб медонанду менависанд?'),
(4777, 52, 42, 'Ё мехоҳанд ҳилае кунанд? Аммо кофирон худ ба ҳила гирифторанд.'),
(4778, 52, 43, 'ё онҳоро худоест ғайри Худои якто? Пок аст Худои якто аз ҳар чӣ шарикаш месозанд.'),
(4779, 52, 44, 'Агар бингаранд, ки қитъае аз осмон фурӯ меафтад, мегӯянд: «Абрест мутароким (қабат-қабат ҷамъшуда)».'),
(4780, 52, 45, 'Пас онҳоро вогузор, то рӯзеро, ки дар оя ба ҳалокат мерасанд, бингаранд,'),
(4781, 52, 46, 'рӯзе, ки макрашон ҳеҷ ба ҳолашон фоидае накунад ва кас ба ёрияшон барнахезад.'),
(4782, 52, 47, 'Барои касоне, ки зулм мекунанд, боз ҳам азобест ғайри ин азоб, вале бештаринашон намедонанд.'),
(4783, 52, 48, 'Дар баробари фармони Парвардигорат босабр бош, ки ту таҳти назари Моӣ! Ва ҳангоме ки бархостӣ, ба ситоиши Парвардигорат тасбеҳ гӯй!'),
(4784, 52, 49, 'Ва тасбҳ гӯй дар қисме аз шаб ва ба ҳангоми нопадид шудани ситорагон,');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Қасам ба он ситора, чун пинҳон шуд,'),
(4786, 53, 2, 'ки ёри шумо на гумроҳ шуда ва на ба роҳи каҷ рафтааст.'),
(4787, 53, 3, 'Ва сухан аз рӯи ҳаво (ҳавас) намегӯяд.'),
(4788, 53, 4, 'Ин сухан фақат он чизест, ки ба ӯ ваҳй мешавад.'),
(4789, 53, 5, 'Ӯро он фариштаи пурқувват таълим додааст,'),
(4790, 53, 6, 'соҳибнерӯе, ки рост истод'),
(4791, 53, 7, 'ва Ӯ ба канораи балаыди осмон буд,'),
(4792, 53, 8, 'сипас наздик шуд ва бисёр наздик шуд'),
(4793, 53, 9, 'то ба қадри ду камон ё наздиктар.'),
(4794, 53, 10, 'Ва Худо ба бандаи худ ҳар чӣ бояд, ваҳй кунад, ваҳй кард.'),
(4795, 53, 11, 'Дил он чиро, ки, дид, дурӯғ нашумурд.'),
(4796, 53, 12, 'Оё дар он чӣ мебинад, бо Ӯ ҷидол (баҳс) мекунед?'),
(4797, 53, 13, 'Ӯро дигар бор ҳам бидид'),
(4798, 53, 14, 'назди сидратулмунтаҳо,'),
(4799, 53, 15, 'ки оромгоҳи биҳишт назди он дарахт аст.'),
(4800, 53, 16, 'Вақте ки сидраро чизе (нурҳои файзи илоҳӣ) дар худ мепӯшид,'),
(4801, 53, 17, 'чашм хато накард ва аз ҳад дарнагузашт.'),
(4802, 53, 18, 'Албатта баъзе аз оёти бузурги Парвардигорашро бидид.'),
(4803, 53, 19, 'Оё Лот ва Уззоро дидаед?'),
(4804, 53, 20, 'Ва Манот он бути саввуми дигарро?'),
(4805, 53, 21, 'Оё шуморо писар бошад ва Ӯро духтар?'),
(4806, 53, 22, 'Ин тақсимест ғайри одилона.'),
(4807, 53, 23, 'Инҳо чизе нестанд ғайри номҳое, ки худ ва падаронатон ба онҳо додаед. Ва Худованд ҳеҷ далеле бар онҳо нафиристодааст. Танҳо аз паи гумону ҳавои нафси худ мераванд ва хол он ки аз ҷониби Худо роҳнамоияшон кардаанд.'),
(4808, 53, 24, 'Оё ҳарчи одами орзу кунад, барояш ҳосил аст?'),
(4809, 53, 25, 'Ҳам охират ва ҳам дунё аз они Худост.'),
(4810, 53, 26, 'Ва чӣ бисёр фариштагон дар осмонанд, ки шафоъаташон ҳеҷ нафъ надиҳад, лекин аз он пас, ки Худо барои ҳар кӣ хоҳад, рухсат диҳад ва хушнуд бошад. (нафъ кунад).'),
(4811, 53, 27, 'Онон, ки ба охират имон надоранд, фариштагонро ба номҳои занон меноманд.'),
(4812, 53, 28, 'Инҳоро ба он чӣ мегӯянд, ҳеҷ донише нест. Танҳо аз гумони худ пайравӣ мекунанд ва гумон барои шинохти ҳақиқат кофӣ нест!'),
(4813, 53, 29, 'Пас ту низ аз касе, ки аз сухани Мо рӯйгардон мешавад ва ғайри зиндагии дунявиро намеҷӯяд, рӯй гардон.'),
(4814, 53, 30, 'Ниҳояти донишашон ҳамин аст. Парвардигори ту ба он касе ки аз роҳи Ӯ гумроҳ мешавад ё ба роҳи ҳидоят меафтад, донотар аст.'),
(4815, 53, 31, 'Аз они Худост ҳар чӣ дар осмонҳову ҳар чӣ дар замин аст, то бадкоронро дар баробари корҳояшон ҷазо диҳад ва некӯкоронро ба кирдори некашон мукофот диҳад.'),
(4816, 53, 32, 'Касоне, ки аз гуноҳони бузургу зиштиҳо парҳез мекунанд, — ғайри он ки гуноҳе кӯчак аз онҳо сар занад, — бидонанд, ки бахшоиши Парвардигори ту васеъ аст ва Ӯ ба шумо он гоҳ ки аз замин биёфаридатон ва он гоҳ ки дар шиками модаратон пинҳон будед, огоҳтар аст. Худатонро бегуноҳ надонед. Ӯст, ки парҳезгорро беҳтар мешиносад!'),
(4817, 53, 33, 'Оё онро, ки аз ту рӯйгардои шуд, дидӣ?'),
(4818, 53, 34, 'Андак мебахшид ва дар садақа бахилӣ мекард.'),
(4819, 53, 35, 'Оё илми ғайб дораду мебинад?'),
(4820, 53, 36, 'Ё аз он чӣ дар саҳифаҳои Мӯсо омада, бехабар мондааст?'),
(4821, 53, 37, 'Ё аз Иброҳим, ки ҳаққи пайғамбариро адо кард?'),
(4822, 53, 38, 'Ки ҳеҷ кас бори гуноҳи, дигареро барнадорад?'),
(4823, 53, 39, 'Ва ин ки: барои мардум музде ғайри он чӣ худ кардаанд, нест?'),
(4824, 53, 40, 'Ва ба зудӣ, ки кушиши ӯ дар назар ояд (рӯзи қиёмат).'),
(4825, 53, 41, 'Сипас ба ӯ ҷазое комил диҳанд.'),
(4826, 53, 42, 'Ва поёни роҳи ҳама Парвардигори туст.'),
(4827, 53, 43, 'Ва Ӯст, ки механдонаду мегирёнад.'),
(4828, 53, 44, 'Ва Ӯст, ки мемиронаду зинда мекунад.'),
(4829, 53, 45, 'Ва Ӯст, ки ҷуфтҳои нару модаро офаридааст,'),
(4830, 53, 46, 'аз дутфа, он гоҳ, ки дар раҳм рехта мешавад.'),
(4831, 53, 47, 'Ва бар Худованд аст, ки онро бори дигар зинда кунад.'),
(4832, 53, 48, 'Ва Ӯст, ки тавонгар кунад ва рӯзӣ диҳад,'),
(4833, 53, 49, 'Ва Ӯст Парвардигори ситораи Шиъро.'),
(4834, 53, 50, 'Ва Ӯст, ки Од — он қавми пешинро ҳалок кард,'),
(4835, 53, 51, 'ва аз Самуд ҳеҷ боқӣ нагузошт.'),
(4836, 53, 52, 'Ва пеш аз онҳо қавми Нӯҳро, ки ситамкортару саркаштар буданд'),
(4837, 53, 53, 'ва низ Мӯътафикаро нобуд кард.'),
(4838, 53, 54, 'Ва фурӯ пӯшид он қавмро, ҳар чӣ бояд фурӯ пӯшад (сангборон кард).'),
(4839, 53, 55, 'Пас ба кадом як аз неъматҳои Парвардигорат шак мекунӣ?'),
(4840, 53, 56, 'Ин бимдиҳандаест монанди бимдиҳандагони пешин.'),
(4841, 53, 57, 'Қиёмат фаро расид.'),
(4842, 53, 58, 'Кас ҷуз Худованд ошкораш накунад.'),
(4843, 53, 59, 'Оё аз ин сухан дар ҳайрат афтодаед?'),
(4844, 53, 60, 'Ва механдеду намегирйед?'),
(4845, 53, 61, 'Ва шумо ғофил шудаед.'),
(4846, 53, 62, 'Пас Худоро саҷда кунед ва Ӯро бипарастед!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Қиёмат наздик шуд ва моҳ дупора гардид.'),
(4848, 54, 2, 'Ва агар мӯъҷизае бубинанд, рӯй бигардоданд ва гӯянд: «"Ҷодуе бузург аст!»'),
(4849, 54, 3, 'Ва дурӯғ мепиндоранд ва аз паи ҳавоҳои худ мераванд. Ва ҳар коре дар вақти худ қарор гирифтааст.'),
(4850, 54, 4, 'Ва барояшон хабарҳое омадааст, ки дар онҳо пандаст.'),
(4851, 54, 5, 'Ҳикматест тамом. Вале бимдиҳандагон судашон надиҳанд.'),
(4852, 54, 6, 'Пас дар он рӯз, ки оя даъваткунандга ононро ба чизе нохуш даъват кунад, аз онҳо рӯйгардон шав.'),
(4853, 54, 7, 'Нишони зиллат (хори) дар чашмонашон ошкор аст. Чун малахҳое пароканда аз қабрҳо берун меоянд.'),
(4854, 54, 8, 'Сарҳоро боло гирифта ба сӯи он даъваткунанда мешитобад. Кофирон мегӯянд: «Ин рӯзи душворест!»'),
(4855, 54, 9, 'Пеш аз инҳо қавми Нӯҳ такзиб карда (дурӯғ бароварда) буданд. Бандаи Моро такзиб карданду гуфтанд: «Девона аст! «Ва ба дашномаш ронданд.'),
(4856, 54, 10, 'Ва Парвардигорашро дуъо кард: «Ман мағлуб шудаам, интиқом бигир!»'),
(4857, 54, 11, 'Ва мо низ дарҳои осмонро ба рӯи обе, ки ба шиддат мерехт, кушодем.'),
(4858, 54, 12, 'Ва аз замин чашмаҳо шикофтем то об ба он миқдор, ки андоза шуда буд, гирд омад.'),
(4859, 54, 13, 'Ва ӯро бар он киштӣ, ки тахтаҳову мехҳо дошт, савор кардем.'),
(4860, 54, 14, 'Зери назари Мо равон шуд. Ин буд ҷазои касоне, ки бовар надошт.'),
(4861, 54, 15, 'Ва албатта он киштиро нишонае сохтем. Оё ҳеҷ пандгирандае ҳаст?'),
(4862, 54, 16, 'Пас азобу бим доданҳои Ман чӣ гуна буд?'),
(4863, 54, 17, 'Ва ин Қуръонро осон кардем, то аз он панд гиранд. Оё пандгирандае ҳаст?'),
(4864, 54, 18, 'Қавми Од такзиб карданд. Пас азобу бим доданҳои Ман чӣ гуна буд?'),
(4865, 54, 19, 'Мо бар онҳо дар рӯзе наҳсу тӯлонӣ боде сахт фиристодем,'),
(4866, 54, 20, 'ки мардумонро аз замин ҳамонанди решаҳои аз ҷой кандаи нахл бармеканд.'),
(4867, 54, 21, 'Азобу бим доданҳои Ман чӣ гуна буд?'),
(4868, 54, 22, 'Ва ин Қуръонро осон кардем, то аз он панд гиранд. Оё пандгирандае ҳаст?'),
(4869, 54, 23, 'Қавми Самуд бимдиҳандагонро такзиб карданд.'),
(4870, 54, 24, 'Гуфтанд: «агар аз инсоне монанди худ пайравӣ кунем, гумроҳу девона бошем!'),
(4871, 54, 25, 'Оё аз миёни ҳамаи, мо каломи Худо ба ӯ нозил шудааст? На, ӯ дурӯғгӯе худхоҳ аст».'),
(4872, 54, 26, 'Фардо хоҳанд донист, ки дурӯғгӯи худхоҳ кист!'),
(4873, 54, 27, 'Мо он модашутурро барои озмоишашон мефиристем. Пас мунтазирашон бош ва сабр кун!'),
(4874, 54, 28, 'Ва ба онҳо бигӯй, ки об миёнашон тақсим шуда. Навбати ҳар кӣ бошад, ӯ ба сари об меравад.'),
(4875, 54, 29, 'Ёрашонро нидо доданд ва ӯ шамшер баргирифту онро бикушт.'),
(4876, 54, 30, 'Азобу бим доданҳои Ман чӣ гуна буд?'),
(4877, 54, 31, 'Мо бар онҳо як овози даҳшатнок фиристодем. Пас ҳамонанди он алафҳои хушки огили гӯсфанд шуданд.'),
(4878, 54, 32, 'Ва ин Қуръонро осон кардем, то аз он панд гиранд. Оё пандгирандае ҳаст?'),
(4879, 54, 33, 'Қавми Лут бимдиҳандагонро такзиб карданд.'),
(4880, 54, 34, 'Мо бар онҳо боде регбор фиристодем, ғайри хонадони Лут, ки онҳоро саҳаргоҳ раҳонидем.'),
(4881, 54, 35, 'Неъмате буд аз ҷониби Мо ва ононро, ки сипос гӯянд, чунин музд диҳем.'),
(4882, 54, 36, 'Аз интиқоми сахти Мо тарсонидашон, вале бо бимдиҳандагон ба ҷидол (хусумат) бархостанд.'),
(4883, 54, 37, 'Аз меҳмони ӯ коре зишт хостанд. Мо низ чашмонашонро кур гардонидем. Пас бичашед азобу бим доданҳои Маро!'),
(4884, 54, 38, 'Албатта бомдодон азобе пойдор ба сарвақташон омад.'),
(4885, 54, 39, 'Пас азоби Ман ва бим доданҳои Маро бичашед!'),
(4886, 54, 40, 'Ва ин Қуръонро осон кардем, то аз он панд гиранд. Оё пандгирандае ҳаст?'),
(4887, 54, 41, 'Бимдиҳандагон назди хонадони Фиръавн омаданд.'),
(4888, 54, 42, 'Ҳамаи оёти Моро такзиб карданд. Мо низ онҳоро фурӯ гирифтем, чун фурӯ гирифтани ғолибе пурқудрат.'),
(4889, 54, 43, 'Оё кофирони шумо аз онҳо нерӯмандтаранд ё дар китобҳо омадааст, ки дар амон ҳастед?'),
(4890, 54, 44, 'Ё мегӯянд, ки мо ҳамаги ба интиқом бармехезем?'),
(4891, 54, 45, 'Ба зудӣ он ҷамъ мунҳазим шавад (шикаст хӯрад) ва пушт карда бозгарданд.'),
(4892, 54, 46, 'Балки ваъдагоҳи онҳо қиёмат аст ва қиёмат сахттару талхтар аст.'),
(4893, 54, 47, 'Гунаҳкорон дар гумроҳиву ҷаҳолатанд.'),
(4894, 54, 48, 'Рӯзе, ки онҳоро ба рӯяшон дар ҷаҳаннам кашанд, ки бичашед азоби сақарро (дӯзахро)!'),
(4895, 54, 49, 'Мо ҳар чизро ба андоза офаридаем!'),
(4896, 54, 50, 'Фармони Мо танҳо як фармон аст, он ҳам чашм бар ҳам заданест.'),
(4897, 54, 51, 'Касонеро, ки монанди шумо буданд, ҳалок кардем. Оё пандгирандае ҳаст?'),
(4898, 54, 52, 'Ҳар коре, ки кардаанд, дар дафтарҳост.'),
(4899, 54, 53, 'Ҳар кори бузургу хурде навишта шудааст.'),
(4900, 54, 54, 'Парҳезгорон дар боғҳову канори ҷӯйборонанд,'),
(4901, 54, 55, 'дар ҷойгоҳе писандида назди фармонравои тавоно!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Худои раҳмон'),
(4903, 55, 2, 'Қуръонро таълим дод,'),
(4904, 55, 3, 'инсонро биёфарид,'),
(4905, 55, 4, 'ба ӯ гуфтан омӯхт.'),
(4906, 55, 5, 'Офтобу моҳ ба ҳисобе муқаррар дар ҳаракатанд.'),
(4907, 55, 6, 'Ва гиёҳу дарахт саҷдааш мекунанд.'),
(4908, 55, 7, 'Осмонро барафрохт ва тарозуро барниҳод,'),
(4909, 55, 8, 'то дар тарозу таҷовуз накунед.'),
(4910, 55, 9, 'Вазн карданро ба адолат риъоят кунед ва камфурӯшӣ макунед!'),
(4911, 55, 10, 'Заминро барои мардум қарор дод.'),
(4912, 55, 11, 'Дар он меваҳост ва дарахтони хурмо бо хӯшаҳое, ки дар ғилофанд.'),
(4913, 55, 12, 'Ва донаҳое, ки ҳамроҳ бо команд ва низ гиёҳони хушбӯ.'),
(4914, 55, 13, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4915, 55, 14, 'Одамиро аз гили хушкшудае чун сафол биёфарид,'),
(4916, 55, 15, 'Ва ҷинро аз шӯълае бедуд.'),
(4917, 55, 16, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4918, 55, 17, 'Парвардигори ду машриқ ва Парвардигори ду мағриб.'),
(4919, 55, 18, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4920, 55, 19, 'Ду дарёро пеш ронд, то ба ҳам расиданд,'),
(4921, 55, 20, 'миёнашон пардаест то ба ҳам омехта нашаванд.'),
(4922, 55, 21, 'Пас кадом як аз неъматҳои Парвардигорагонро дурӯғ мешуморед?'),
(4923, 55, 22, 'Аз он ду марвориду марҷон берун меояд.'),
(4924, 55, 23, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4925, 55, 24, 'Ва Ӯрост киштиҳое монанди кӯҳ, ки дар дарё мераванд.'),
(4926, 55, 25, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4927, 55, 26, 'Ҳар чӣ бар рӯи замин аст, фонӣ (тамом) шуданист.'),
(4928, 55, 27, 'Ва зоти Парвардигори соҳиби бузургӣ ва икроми туст, ки боқӣ мемонад.'),
(4929, 55, 28, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4930, 55, 29, 'Ҳар кас, ки дар осмонҳову замин аст, пурсандаи даргоҳи Ӯст ва Ӯ ҳар рӯз дар корест.'),
(4931, 55, 30, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4932, 55, 31, 'Эй ҷинниёну одамиён, ба ҳисоби шумо хоҳем расид!'),
(4933, 55, 32, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4934, 55, 33, 'Эй гурӯҳи ҷинниёну одамиён, агар метавонед, ки аз канораҳои осмонҳову замин берун равед, берун равед! Вале берун натавонед рафт, магар бо доштани қудрате.'),
(4935, 55, 34, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4936, 55, 35, 'Бар шумо шӯълае аз оташ фиристода шавад ё дуде низ пас бо ӯ муқобала натавонед кард.'),
(4937, 55, 36, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4938, 55, 37, 'Он гоҳ, ки осмон шикофта шавад, ранге сурх чун ранги чарм хоҳад дошт.'),
(4939, 55, 38, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4940, 55, 39, 'Пас дар он рӯз аз гуноҳи ҳеҷ ҷинневу одамие напурсанд.'),
(4941, 55, 40, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4942, 55, 41, 'Кофиронро ба нишони қиёфаашон мешиносанд ва аз мӯи ҷилави сар ва пойҳояшон мегиранд.'),
(4943, 55, 42, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4944, 55, 43, 'Ин ҳамон ҷаҳаннамест, ки гунаҳкорон дурӯғаш мепиндоштанд.'),
(4945, 55, 44, 'Ва акнун дар миёни он ва оби ҷӯшон, мегарданд.'),
(4946, 55, 45, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4947, 55, 46, 'Ҳ, ар касро, ки аз истодан ба пешгоҳи Парвардигораш тарсида бошад, ду биҳишт аст.'),
(4948, 55, 47, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4949, 55, 48, 'Он ду пур аз дарахтонанд.'),
(4950, 55, 49, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4951, 55, 50, 'Дар он ду, ду чашма ҷорист.'),
(4952, 55, 51, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4953, 55, 52, 'Дар он ҷо аз ҳар гуна мевае ду қисм аст.'),
(4954, 55, 53, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4955, 55, 54, 'Бар бистарҳое, ки астарашон аз истабрақ аст, такя задаанд ва меваҳои он ду биҳишт дар наздикашон бошад.'),
(4956, 55, 55, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4957, 55, 56, 'Дар он ҷост заноне, ки ҷуз ба шавҳари худ нанигаранд ва пеш аз биҳиштиён ҳеҷ одамиву ҷинне ба ояҳо даст назадааст.'),
(4958, 55, 57, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4959, 55, 58, 'Гӯй, ки он ҳурон монанди ёқуту марҷонанд.'),
(4960, 55, 59, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморад?'),
(4961, 55, 60, 'Мукофоти некӣ некисту бас.'),
(4962, 55, 61, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4963, 55, 62, 'Ва ғайри онҳо ду биҳишти дигар аст.'),
(4964, 55, 63, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4965, 55, 64, 'Аз шиддати сабзӣ моил ба сиёҳианд.'),
(4966, 55, 65, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед,?'),
(4967, 55, 66, 'Дар он ду, ду чашмаи ҷӯшанда ҳаст.'),
(4968, 55, 67, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4969, 55, 68, 'Дар он ду мева ҳаст ва дарахти хурмо ҳасту анор ҳаст.'),
(4970, 55, 69, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4971, 55, 70, 'Дар он ҷо занонест нексирату зеборӯй.'),
(4972, 55, 71, 'Пас кадом як аз неъматҳои Парвардигаратонро дурӯғ мешуморед?'),
(4973, 55, 72, 'Ҳуроне нигаҳдошташуда дар хаймаҳо.'),
(4974, 55, 73, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4975, 55, 74, 'Пеш аз биҳиштиён ҳеҷ одамеву ҷинне ба онҳо даст назадааст.'),
(4976, 55, 75, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4977, 55, 76, 'Бар болишҳои сабзу фаршҳои, некӯ такя мезананд.'),
(4978, 55, 77, 'Пас кадом як аз неъматҳои Парвардигоратонро дурӯғ мешуморед?'),
(4979, 55, 78, 'Бузург аст номи Парвардигори ту — он соҳиби бузургиву икром!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Чун қиёмат воқеъ шавад,'),
(4981, 56, 2, 'ки дар воқеъ шуданаш ҳеҷ дурӯғ нест,'),
(4982, 56, 3, 'гурӯҳеро пасткунанда аст ва гурӯрҳеро баландкунанда.'),
(4983, 56, 4, 'Он гоҳ, ки замин ба сахтӣ биларзад'),
(4984, 56, 5, 'ва кӯҳҳо ба тамом реза-реза шаванд'),
(4985, 56, 6, 'ва чун ғуборе пароканда гарданд,'),
(4986, 56, 7, 'шумо се гурӯҳ бошед:'),
(4987, 56, 8, 'яке аҳли саъодат. Аҳли саъодат чӣ ҳол доранд?'),
(4988, 56, 9, 'Дигар аҳли шақоват (бадбахт). Аҳли шақоват чӣ ҳол доранд?'),
(4989, 56, 10, 'Севумини онҳо, ки пешқадаму пешдаст буданд ва инак пеш раводанд (дар ҷаннат).'),
(4990, 56, 11, 'Инҳо муқаррабонанд (наздиккардашудагонанд)'),
(4991, 56, 12, 'дар биҳиштҳои пурнеъмат.'),
(4992, 56, 13, 'Гурӯҳе аз пешиниён'),
(4993, 56, 14, 'ва андаке аз онҳо, ки аз пас омадаанд'),
(4994, 56, 15, 'бар тахтҳои мурассаъ (зарбофт)'),
(4995, 56, 16, 'рӯбарӯи ҳам бар онҳо такя задаанд.'),
(4996, 56, 17, 'Писароне ҳамеша ҷавон гирдашон мечарханд'),
(4997, 56, 18, 'бо қадаҳҳову кӯзаҳо ва ҷомҳое аз шаробе, ки дар ҷӯйҳо ҷорист,'),
(4998, 56, 19, 'аз нушиданаш на сардард гиранд ва на беҳуш шаванд.'),
(4999, 56, 20, 'Ва меваҳое, ки худ интихоб мекунанд.'),
(5000, 56, 21, 'Ва гӯшти парранда, аз ҳар чӣ бихоҳанд,'),
(5001, 56, 22, 'Ва ҳурони дуруштчашм'),
(5002, 56, 23, 'ҳамонанди марворидҳое дар садаф.'),
(5003, 56, 24, 'Ҳама ба мукофоти корҳое, ки мекардаанд.'),
(5004, 56, 25, 'Дар он ҷо на сухани беҳуда шунаванд ва на гуноҳолуд'),
(5005, 56, 26, 'ҷуз як сухан: «Салом, салом!»'),
(5006, 56, 27, 'Аммо аҳли саъодат, аҳли саъодат чӣ ҳол доранд?'),
(5007, 56, 28, 'Дар зери дарахти сидри бехор'),
(5008, 56, 29, 'ва дарахти музе (банан), ки мевааш бар якдигар чида шуда'),
(5009, 56, 30, 'ва сояе доим'),
(5010, 56, 31, 'ва обе ҷорӣ'),
(5011, 56, 32, 'ва мевае бисёр,'),
(5012, 56, 33, 'ки бепоён ва касро аз он бознадоранд.'),
(5013, 56, 34, 'Ва фаршҳои баланд'),
(5014, 56, 35, 'Он занонро Мо биёфаридем, офариданӣ'),
(5015, 56, 36, 'ва душизагон сохтем.'),
(5016, 56, 37, 'Маъшуқи ҳамсарони худанд,'),
(5017, 56, 38, 'барои аҳли саъодат,'),
(5018, 56, 39, 'гурӯҳе аз пешиниён'),
(5019, 56, 40, 'ва гурӯҳе, ки аз пас омадаанд.'),
(5020, 56, 41, 'Аммо аҳли шақоват, аҳли шақоват (бадбахт) чӣ ҳол доранд?'),
(5021, 56, 42, 'Дар боди гарм ва оби ҷӯшонанд'),
(5022, 56, 43, 'дар сояе аз дуди сиёҳ'),
(5023, 56, 44, 'на сарду на хуш.'),
(5024, 56, 45, '"Инҳо пеш аз ин дар нозу неъмат буданд.'),
(5025, 56, 46, 'ва бар гуноҳони бузург исрор (давом) мекарданд'),
(5026, 56, 47, 'ва мегуфтанд: «Оё замоне, ки мо мурдему хоку устухон шудем, боз ҳам моро зинда мекунанд'),
(5027, 56, 48, 'ё ниёгони моро?»'),
(5028, 56, 49, 'Бигӯ: «Ҳамаро, онон, ки аз ин пеш буданд ва онҳо, ки аз паяшон омада буданд,'),
(5029, 56, 50, 'ҳама дар ваъдагоҳи он рӯзи муъайян хоҳанд буд.'),
(5030, 56, 51, 'Он гоҳ шумо, эй гумроҳони дурӯғбароранда,'),
(5031, 56, 52, 'аз дарахтони заққум хоҳед хурд'),
(5032, 56, 53, 'ва шикамҳои худро аз он пур хоҳед кард'),
(5033, 56, 54, 'ва бар болои он оби ҷӯшон хоҳед нушид,'),
(5034, 56, 55, 'чунон менушед, ки шутури ташн, а об менӯшад.'),
(5035, 56, 56, 'Ин аст меҳмондориашон дар рӯзи ҷазо.'),
(5036, 56, 57, 'Мо шуморо офаридаем, пас чаро тасдиқ намекунед?'),
(5037, 56, 58, 'Оё он маниро, ки мерезед, дидаед?'),
(5038, 56, 59, 'Оё шумо ӯро меофаринед ё Мо офаринандаем?'),
(5039, 56, 60, 'Мо маргро бар шумо, муайян сохтем ва нотавон аз он нестом, ки'),
(5040, 56, 61, 'ба ҷои шумо қавме монанди шумо биёварем ва шуморо ба сурате, ки аз он бехабаред, аз нав биёфаринем.'),
(5041, 56, 62, 'Шумо ай офариниши нахуст огоҳед, чаро ба ёдаш наёваред?'),
(5042, 56, 63, 'Оё чизеро, ки мекоред, дидаед?'),
(5043, 56, 64, 'Оё шумо мерӯёнедаш ё Мо рӯёнандаем?'),
(5044, 56, 65, 'Агар мехостем, хошокаш месохтем, то дар ҳайрат бимонед.'),
(5045, 56, 66, 'Гӯянд: «Мо зиён кардагонем,'),
(5046, 56, 67, 'мо бенасиб мондагонем».'),
(5047, 56, 68, 'Оё оберо, ки менӯшед, дидаед?'),
(5048, 56, 69, 'Оё шумо онро аз абр фурӯд меоред ё Мо фурӯд орандагонем?.'),
(5049, 56, 70, 'Агар мехостем онро талх мегардонидем. Пас чаро шукр намегӯед?'),
(5050, 56, 71, 'Оё он оташро, ки меафрузед дидаед?'),
(5051, 56, 72, 'Оё дарахташро шумо офаридаед ё Мо офаринандаем?'),
(5052, 56, 73, 'Мо онро хушдоре (аз дӯзах) ва барои мусофирони роҳнавард матоъе сохтем.'),
(5053, 56, 74, 'Ба номи Парвардигори бузурги худ тасбеҳ гӯй!'),
(5054, 56, 75, 'Пас савганд ба ғурубгоҳи ситорагон!'),
(5055, 56, 76, 'Ва ин савгандест, — агар бидонед, — бузург.'),
(5056, 56, 77, 'Албатта ин Қуръонест гиромиқадр'),
(5057, 56, 78, 'дар китобе макнун,'),
(5058, 56, 79, 'ки ҷуз покон даст бар он назананд.'),
(5059, 56, 80, 'Нозилшуда аз ҷониби Парвардигори ҷаҳониён аст.'),
(5060, 56, 81, 'Оё ин суханро дурӯғ меҳисобед?'),
(5061, 56, 82, 'Ва насиби худро дар дурӯғ ҳисобидани он қарор медиҳед?'),
(5062, 56, 83, 'Пас чаро он гоҳ ки ҷон ба гулӯ расад'),
(5063, 56, 84, 'ва шумо дар ин ҳангом менигаред?'),
(5064, 56, 85, 'Мо аз шумо ба ӯ наздиктарем, вале шумо намебинед.'),
(5065, 56, 86, 'Агар қиёматро бовар надоред,'),
(5066, 56, 87, 'агар рост мегӯед, бозаш гардонед! (рӯҳро бар ҷасад)'),
(5067, 56, 88, 'Аммо агар аз муқаррабон (наздикшудагон) бошад.'),
(5068, 56, 89, 'барои ӯст осоишу рӯзӣ ва биҳишти пурнеъмат.'),
(5069, 56, 90, 'Ва аммо агар аз аҳли саъодат бошад,'),
(5070, 56, 91, 'пас туро аз аҳли саъодат салом аст.'),
(5071, 56, 92, 'Ва аммо агар, аз такзибкунандагони (дурӯғ барорандагони) гумроҳ бошад,'),
(5072, 56, 93, 'ба оби ҷӯшон меҳмонаш кунанд'),
(5073, 56, 94, 'ва ба дӯзахаш дароваранд.'),
(5074, 56, 95, 'Ин сухан сухане росту яқин аст!'),
(5075, 56, 96, 'Пас ба номи Парвардигори бузургат тасбеҳ гӯй!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Худовандро тасбеҳ мегӯянд, ҳар чӣ дар осмонҳову замин аст ва Ӯ ғолибу ҳаким аст!'),
(5077, 57, 2, 'Фарманравоии осмонҳову замин аз они Ӯст. Зинда мекунад ва мемиронад ва бар ҳар чиз тавоност.'),
(5078, 57, 3, 'Ӯст аввалу охир ва зоҳиру ботия ва Ӯ ба ҳар чизе доност!'),
(5079, 57, 4, 'Ӯст, ки осмонҳову заминро дар шаш рӯз офарид. Сипас ба арш пардохт. Ҳар чиро дар замин фурӯ равад ва ҳар чиро аз замин борун ояд ва ҳар чиро аз осмон фуруд ояд ва ҳар чиро ба осмон боло равад, медонад. Ва ҳар ҷо, ки бошед, ҳамроҳи шумост ва ба ҳар коре, ки мекунед, биност!'),
(5080, 57, 5, 'Фармонравоии оомонҳову замин аз они Ӯст ва ҳамаи корҳо ба Худо бозмегардад.'),
(5081, 57, 6, 'Аз шаб мекоҳад (кӯтоҳ кунад) ва ба рӯз меафзояд ва аз рӯз мехоҳад ва ба шаб меафзояд. Ва ба ҳар чӣ дар дилҳо мегузарад, огоҳ аст.'),
(5082, 57, 7, 'Ба Худову паёмбараш имон биёваред ва аз он мол, ки ба варосат (мерос) ба шумо расоядааст, инфоқ (садақа) кунед. Аз миёни шумо ҳар киро имон оварда ва инфоқ карда, музде фаровонаш диҳанд.'),
(5083, 57, 8, 'Чист шуморо, ки ба Худо имон намеоваред ва ҳол он ки паёмбар шуморо даъват мекунад, ки ба Парвардигоратон имон биёваред ва агар бовар доред, аз шумо паймон гирифтааст?'),
(5084, 57, 9, 'Ӯст он Худое, ки бар бандаи худ оёти, равшанро нозил мекунад, то шуморо аз торикӣ ба рӯшноӣ оварад. Зеро Худо ба шумо мушфиқу меҳрубон аст.'),
(5085, 57, 10, 'Ва чаро дар роҳи Худо инфоқ намекунед ва ҳол он ки, аз они Худост мероси осмонҳову замин? Аз миёни шумо инҳо, ки пеш аз фатҳ инфоқ карда ва ба ҷанг рафта бо онон, ки баъд аз фатҳ инфоқ кардаанд ва ба ҷанг рафтаанд, баробар нестанд, Дараҷати онон болотар аст. Ва Худо ба ҳама ваъдаи нек медиҳад. Ва ба ҳар коре, ки мекунед, огоҳ аст.'),
(5086, 57, 11, 'Кист, ки Худоро қарзулҳасана (дар роҳи Худо садақа) диҳад, то барои ӯ дучандонаш кунад ва ӯро мукофоте некӯ бошад?'),
(5087, 57, 12, 'Рӯзе, ки мардони мӯъмину занонимӯъминро бубинӣ, ки нурашон пешопеш ва дар самти росташон меравад. Дар он рӯз башорататон (хушхабаратон) ба биҳиштҳоест, ки дар он, наҳрҳо равон аст ва дар он ҷовид хоҳед монд ва ин комёбии бузургест.'),
(5088, 57, 13, 'Рӯзе, ки мардони мунофиқу занони мунофиқ ба касоне, ки имон овардаанд, мегӯянд: «Ба мо нигоҳ кунед, то аз нуратон фурӯғе гирем!» Гӯянд: «Ба дунё бозгардед ва аз он ҷо нур биталабед!» Миёнашон деворе бароваранд, ки бар он девор даре бошад, даруни он раҳмат бошаду беруни он азоб.'),
(5089, 57, 14, 'Ва онҳоро нидо диҳанд, ки оё мо бо, шумо ҳамроҳ набудем? Мегӯянд: «Бале, аммо шумо худатонро дар бало афкандед ва ба интизор нишастед ва дар шак будед ва орзуҳо шуморо бифирефт, то он гоҳ ки фармони Худо даррасид ва шайтон ба Худо фиребатон кард.'),
(5090, 57, 15, 'Ва имрӯз на аз шумо фидяе (ҷаримае) пазиранд ва на аз кофирон. Ҷойгоҳатон оташ аст. Оташ сазовори шумост ва бад саранҷомест!»'),
(5091, 57, 16, 'Оё мӯъминонро вақти он нарасидааст, ки дилҳояшон дар баробарӣ ёди Худо ва сухани ҳақ, ки нозил шудааст, хошеъ (моил, мутеъ, ҳозир) шавад? Монанди он мардуме набошанд, ки пеш аз ин китобашон додем ва чун муддате баромад, дилҳояшон сахт шуд ва бисёре нофармон шуданд.'),
(5092, 57, 17, 'Бидонед, ки Худо заминро пас аз мурданаш зинда мекунад. Мо оётро бароятон ба равшанӣ баён кардем, бошад, ки ба ақл дарёбед!'),
(5093, 57, 18, 'Худо ба мардони садақадиҳанда ва занони садақадиҳандае, ки ба Худо қарзулҳасана медиҳанд, дучандон музд медиҳад ва низ онҳоро аҷре некӯст.'),
(5094, 57, 19, 'Касоне, ки ба Худову паёмбараш имон овардаанд, ростгӯёну росткоронанд. Ва шаҳидон дар назди Парвардигорашон ҳастанд. Соҳиби музду нури худанд. Ва онҳо, ки кофиранд ва оёти Моро дурӯғ мебароранд, дар ҷаҳаннаманд.'),
(5095, 57, 20, 'Бидонед, ки зиндагии инҷаҳонӣ бозича аст ва беҳудагиву ороиш ва фахр кардану афзунҷӯӣ дар молҳову авлод монанди бороне бавақт аст, ки рӯиданиҳояш кофиронро ба шигифт афканад (хурсанд, кунад). Сипас пажмурда мешавад ва бинӣ, ки зард гаштааст ва хошок шудааст. Ва дар охират насиби гурӯҳе азоби сахт аст ва нисбати гурӯҳе бахшоиши Худо ва хушнудии Ӯ. Ва зиндагии дунё фақат матоъе фиребанда аст.'),
(5096, 57, 21, 'Барои расидан ба бахшоиши Парвардигоратон ва биҳиште, ки паҳнои он монанди паҳнои осмонҳову замин аст, бар якдигар пешӣ гиред (мусобиқа). Ин биҳишт барои касоне, ки ба Худову паёмбаронаш имон овардаанд, муҳайё шудааст. Ин бахшоишест аз ҷониби Худо, ки ба ҳар кӣ мехоҳад, онро ато мекунад, ва Худо соҳиби бахшоише бузург аст.'),
(5097, 57, 22, 'Ҳар мусибате ба мол ё ба ҷонатон расад албатта пеш аз он ки биёфаринемаш, дар китобе навишта шудааст. Ва ин бар Худо осон аст!'),
(5098, 57, 23, 'То бар он чӣ аз дастатон меравад, андӯҳгин набошед ва ба он чӣ ба дастатон меояд, шодмонӣ накунед. Ва Худо ҳеҷ мутакаббири худситояндаро дӯст надорад.'),
(5099, 57, 24, 'онон, ки бахилӣ мекунанд ва мардумро ба бухл вомедоранд. Ва ҳар кӣ рӯйгардон мешавад, бидонад, ки Худо беҳоҷату сутуданист. (васф карданист).'),
(5100, 57, 25, 'Мо паёмбаронамонро бо далелҳои равшан фиристодем ва бо онҳо китобу тарозуро низ возил кардем, то мардум, ба адолат амал кунанд. Ва оҳанро, ки дар он нерӯе сахт ва фоидаҳое барои мардум аст, фурӯд овардем то Худо бидонад чӣ касе ба нодида Ӯ ва паёмбаронашро ёрӣ мекунад. Зеро Худо тавонову ғолиб аст!'),
(5101, 57, 26, 'Мо Нӯҳу Иброҳимро ба пайғамбарӣ фиристодем ва дар миёни фарзандонашон нубувват ва китоб ниҳодем. Баъзеяшон ҳидоятёфтагон, буданд, вале бештаринашон нофармонон.'),
(5102, 57, 27, 'Сипас паёмбаронамонро пас аз онҳо фиристодем. Ва Исо бинни Марямро аз паи онҳо равона кардем ва ба ӯ Инҷилро додем ва дар дили пайравонаш раъфату (шафқату) раҳмат ниҳодем. Ва руҳбонияте (узлат; гӯшанншинӣ), ки ба бидъат овардаанд, Мо бар онҳо муқаррар накардаем, аммодар он хушнудии Худо меҷӯстанд, вале ҳаққи, онро ба ҷо нагузориданд. Мо аз он миён ба касоне, ки имон оварда буданд, мукофот додем, вале бештаринашон нофармон буданд.'),
(5103, 57, 28, 'Эй касоне, ки имон овардаед, аз Худо битарсед ва ба паёмбараш имон биёваред, то шуморо аз раҳмати худ ду бор музд бидиҳад. Ва шуморо нуре ато кунад, ки дар рӯшноии он роҳ биҷӯед ва шуморо бибахшояд, ки Худо бахшояндаи меҳрубон аст,'),
(5104, 57, 29, 'то аҳли китоб бидонанд, ки ба бахшоиши Худованд ҳеҷ дастрасӣ надоранд ва ин бахшоиш ба дасти Ӯст, ки ба ҳар кӣ хоҳад ато мекунад ва Худоро бахшоише азим аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Худо сухани занеро, ки дар бораи шавҳараш бо ту ба муҷодала омадааст ва ба Худо шиква мекунад, шунид. Ва Худо гуфтугӯи шуморо мешунавад, зеро шунавову биност!'),
(5106, 58, 2, 'Аз миёни шумо касоне, ки занонашонро зиҳр (монанд кардани яке аз узвҳои зан бо узви модар, ки навъе аз қасам аст) мекунанд, бидонанд, ки занонашон модаронашон нестанд, модаронашон фақат заноне ҳастанд, ки онҳоро зоидаанд ва сухане нописанду дурӯғ аст, ки мегӯянд. Ва Худо аз гуноҳ он даргузаранда аст ва бахшоянда аст!'),
(5107, 58, 3, 'Онҳое, ки заношашонро зиҳор мекунанд, он гоҳ аз он чӣ гуфтаанд, пушаймон мещаванд, пеш аз он ки бо якдигар тамос ёбанд, бояд ғуломе озод кунанд. Ин пандест, ки ба шумо медиҳанд ва Худо ба корҳое, ки мекунед, огоҳ аст!'),
(5108, 58, 4, 'Аммо касоне, ки ғуломе наёбанд, пеш аз он ки бо якдигар тамос ёбанд, бояд ду моҳ пай дар пай рӯза бидоранд. Ва он кӣ натавонад, бояд шаст мискинро таъом диҳад. Ва ин ба он сабаб аст, ки ба Худову паёмбараш имон биёваред. Инҳо, ҳудуди Худост. Ва барои кофирон азобест дардовар!'),
(5109, 58, 5, 'Касоне, ки бо Худову паёмбараш мухолифат (зиддият, муқобала) мекунанд, залилу хор мешаванд, ҳамчунон ки пешиниёнашон хор шудаанд. Мо, оёте равшан нозил кардем ва кофиронро азобест хоркунанда!'),
(5110, 58, 6, 'Рӯзе, ки Худо ҳамагонро зинда мекунанд, ононро аз коре, ки кардаанд, огоҳ месозад. Худо корҳои онҳоро шумора кардааст, ҳарчанд худ аз ёд бурдаанд ва Худо шоҳиду нозир бар ҳар чизест!'),
(5111, 58, 7, 'Оё надонистаӣ, ки Худо ҳар чиро дар осмонҳову замин аст, медонад? Се кас бо ҳам наҷво (роз, сухани махфӣ) кунанд, Худованд чаҳорумини онҳост ва панч кас бошанд, Худованд шашумини онҳост. Ва камтар аз ин, — ҳар ҷо ки бошанд, — ва бештар аз ин. Худо бо онҳост. Сипас ҳамаро дар рӯзи қиёмат ба корҳое, ки кардаанд, огоҳ мекунад. Зеро Худо бар ҳама чиз огоҳ аст!'),
(5112, 58, 8, 'Оё онҳоро, ки аз наҷво манъ туда буданд, надидӣ, ки кореро, ки аз он манъ шуда буданд, аз сар гирифтанд ва боз ҳам барои гуноҳу душманӣ ва нофармонӣ аз паёмбар бо ҳам наҷво мекунанд? Ва чун назди ту меоянд, ба гунае туро салом мегӯянд, ки Худо туро ба он гуна салом нагуфтааст ва дар дил мегӯянд: «Чаро Худо моро ба он чӣ мегӯем, азоб намекунад?» Ҷаҳаннам барояшон кофист. Ва он дохил мешаванд ва ин бад бозгаштест!'),
(5113, 58, 9, 'Эй касоне, ки имон овардаед, агар бо якдигар наҷво (роз, сухани махфӣ) мекунед, дар боби гуноҳу душманӣ ва нофармонӣ аз паёмбар наҷво макунед, балки дар боби некиву парҳезгорӣ наҷво кунед. Аз он Худое, ки ҳамагон назди Ӯ ҷамъ меоед, битарсед,'),
(5114, 58, 10, 'Ҳар оина (албатта) наҷво кардан кори шайтон аст, ки мехоҳад мӯъминонро ғамгин кунад ва ҳол он ки ҳеҷ зиёне ҷуз ба фармони худованд ба онҳо намерасонад, Ва мӯъминон бояд, ки бар Худо таваккал кунанд!'),
(5115, 58, 11, 'Эй касоне, ки имон овардаед, чун шуморо гӯянд, дар маҷолис ҷой боз кунед, ҷой боз кунед, то Худо дар кори шумо кушоиш диҳад, Ва чун гӯянд, ки бархезед, бархезед. Худо онҳоеро, ки имон овардаанд ва касонеро, ки дониш ёфтаанд, ба дараҷаҳо баланд бардорад, ва Худо ба корҳое, ки мекунед, огоҳ аст!'),
(5116, 58, 12, 'Эй касоне, ки имон овардаед, чун хоҳед, ки бо паёмбар наҷво кунед, пеш аз наҷво карданатон садақа бидиҳед. Ин барои шумо беҳтару покизатар аст. Ва агар барои садақа чизе наёфтед, Худо бахшояндаву меҳрубон аст?'),
(5117, 58, 13, 'Оё тарсидед пеш аз наҷво кардан садақа бидиҳед? Ҳол, ки садақа надодаед ва Худо ҳам тавбаи шуморо пазируфтааст, пас намоз бигузоред ва закот бидиҳед ва ба Худову паёмбараш итоъат кунед, ки Худо ба корҳое, ки мекунед, огоҳ аст!'),
(5118, 58, 14, 'Оё надидаӣ он касонро, ки бо мардуме, ки Худо бар онҳо хашм гирифта буд, дӯстӣ карданд? Инҳо на аз шумоянд ва на аз онҳо. Ва худ медонанд, ки бардурӯғ савганд мехӯранд.'),
(5119, 58, 15, 'Худо барояшон азобе сахт омода кардааст. Зеро корҳое. ки мекунанд, нописанд аст!'),
(5120, 58, 16, 'Аз савгандҳояшон сипаре сохтаанд. Ва аз роҳи Худо рӯйгардон шуданд. Пас барои онҳост азобе хоркунанда!'),
(5121, 58, 17, 'Молҳову авлодашон барояшон дар баробари хашми Худо нафъ накунад. Аҳли ҷаҳаннаманд ва дар он ҷовидонанд,'),
(5122, 58, 18, 'Рӯзе, ки Худо ҳамаи онҳоро зинда мекунад, ҳамчунон ки барои шумо қасам мехӯрданд, барои Ӯ ҳам қасам хоҳанд хурд. Ва мепиндоранд, ки фоидае хоҳанд бурд. Огоҳ бошед, ки дурӯғгӯёнанд.'),
(5123, 58, 19, 'Шайтон бар онҳо чира (ғолиб) шудааст ва номи Худоро аз ёдашон бурдааст. Онҳо ҳизби шайтонанд. Огоҳ бош, ки ҳизби шайтон зиёнкунандагонанд!'),
(5124, 58, 20, 'Касоне, ки бо Худову паёмбараш мухолифат мекунанд, дар зумраи хоршудагонанд.'),
(5125, 58, 21, 'Худо муқаррар дошта, ки албатта, Ману паёмбаронам ғолиб мешавем! Зеро Худо тавонову ғолиб аст!'),
(5126, 58, 22, 'Намеёбӣ мардумеро, ки ба Худо ва рӯзи қиёмат имон оварда бошанд, вале бо касоне, ки бо Худову паёмбараш мухолифат меварзанд, дӯстӣ кунанд, ҳарчанд, он мухолифон падарон ё фарзандон ё бародарон ва ё қабилаи онҳо бошанд. Худо бар дилашон имонро навишта ва ба рӯҳе аз худ ёрияшон кардааст ва онҳоро ба биҳиштҳое, ки дар он наҳрҳо ҷорист, дароварад. Дар он ҷо ҷовидона бошанд. Худо аз онҳо хушнуд аст ва онон низ аз Худо хушнуданд. Инҳо ҳизби Худоянд, огоҳ бош, ки ҳизби Худо растагоронанд (наҷотёфтагон)!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Худовандро тасбеҳ гӯянд ҳар чӣ дар осмонҳову ҳар чӣ дар замин аст. Ва Ӯст ғолибу ҳаким!'),
(5128, 59, 2, 'Ӯст он Худое, ки нахустин бор касоне аз аҳли китобро, ки кофирон буданд, аз хонаҳояшон берун ронд ва шумо намепиндоштед, ки берун раванд. Онҳо низ мепиндоштанд қалъаҳояшонро тавони он хаст, ки дар баробари Худо нигаҳдораон бошад, Худо аз сӯе, ки гумонашро намекарданд, бар онҳо азоб овард ва дар дилашон тарс андохт, чунон ки хонаҳои худро ба дасти худ ва ба дасти мӯъминон хароб мекарданд. Пас, эй соҳибақлҳо ибрат бигиред!'),
(5129, 59, 3, 'Агар Худр тарки диёрро бар онҳо муқаррар накарда буд, дар дунё ба азоб гирифторашон мекард ва дар охираташон азоби оташ аст.'),
(5130, 59, 4, 'Ва ин ба ҷазои он буд, ки бо Худову паёмбараш мухолифат карданд ва ҳар кӣ бо Худо мухолифат мекунад, бидонад, ки Худо ба сахтӣ уқубат мекунад!'),
(5131, 59, 5, 'Ҳар дарахти хурмоерр, ки буридед, ё онро бар решааш боқӣ гузоридед,, ба фармони Худо бувад, то нофармонон хор гарданд.'),
(5132, 59, 6, 'Ва он чӣ Худо аз дороияшон ба паёмбари худ ғанимат дод, шумо бо асп ё шутуре бар он натохта будед, балки Худо паёмбаронашро бар ҳар кӣ бихоҳад, ғолиб, месозад ва Худо бар ҳар чизе қодир, аст!'),
(5133, 59, 7, 'Он ғанимате, ки Худо аз мардуми деҳаҳо насиби паёмбараш кардааст, аз они Худост ва паёмбар ва хешовандону ятимон ва мискинону мусофирони дар роҳ монда, то миёни тавонгаронатон даст ба даст нашавад. Ҳар чӣ паёмбар ба шумо дод, биситонед ва аз ҳар чӣ шуморо манъ кард, парҳез кунед. Ва аз Худо битарсед, ки Худо сахтуқубат аст!'),
(5134, 59, 8, 'Низ ғаноим аз они муҳоҷирони (тарки Ватан кардагон) фақирест, ки аз сарзаминашон ронда шудаанд ва онҳо дар талаби фазлу хушнудии Худоянд ва Худову паёмбарашро ёрӣ мекунанд, инҳо ростгӯёнанд'),
(5135, 59, 9, 'Ва касоне, ки пеш аз омадани муҳоҷирон, дар диёри худ будаанд ва имон овардаанд, онҳоеро, ки ба сӯяшон мухоҷират кардаанд, дӯст медорад. Ва аз он чӣ муҳоҷиронро дода мешавад, дар дил эҳсоси ҳасад намекунанд ва дигаронро бар худ ихтиёр мекунанд, ҳарчанд худ мӯҳтоҷ бошанд. Ва онон, ки аз бахилии худ дар амон монда бошанд, наҷотёфтагонанд!'),
(5136, 59, 10, 'Касоне, ки аз паси онҳо омадаанд, мегӯянд: «Эй Парвардигори мо, мову бародарони моро, ки пеш аз мо имон овардаанд, бибахшо ва кинаи касонеро, ки имон овардаанд, дар дили мо ҷой мадеҳ. Эй Парвардигори мо, ту мушфиқу меҳрубон ҳастӣ!»'),
(5137, 59, 11, 'Оё мунофиқонро надидаӣ, ки ба ёрони худ аз аҳли китоб, ки кофир буданд, мегуфтанд: «Агар шуморо бадарға кунанд, мо низ бо шумо берун меоем ва ба зиёни шумо ба ҳеҷ кас итоъат нахоҳем кард ва агар бо шумо ҷангиданд, ёриатон мекунем». Ва ҳол он ки Худо медонад, ки дурӯғ мегӯянд.'),
(5138, 59, 12, 'Агар онҳоро бадарға кунанд, бо онҳо берун нашааванд. Ва агар ба ҷангашон биёянд, ёриаатон намекунанд ва агар ҳам ба, ёриашон бархезанд, ба душман пушт мекунанд. Пас рӯи ёрӣ набинанд.'),
(5139, 59, 13, 'Тарси аз шумо дар дили онҳо бештар аз бими Худост. Зеро мардуме ҳастанд, ки ба фаҳм дарнамеёбанд.'),
(5140, 59, 14, 'Онон ҳамагӣ бо шумо ҷанг намекунанд, магар дар деҳаҳое, ки қалъа дошта бошад ё аз паси девор. Онҳо сахт бо якдигар душмананд, ту якдил мепиндорияшон ва ҳол он ки дилҳошон аз ҳам ҷудост, ки мардуме бехираданд.'),
(5141, 59, 15, 'Монанди онҳое ҳастанд, ки чанде пеш вуболи гуноҳи худро чашиданд. Ва ба азобе дардовар низ гирифтор хоҳанд шуд.'),
(5142, 59, 16, 'Монанди шайтон, ки ба одамӣ гуфт: «Кофир шав!» Чун кофир шуд, гуфт: «Ман аз ту безорам. Ман аз Худо — он ПарварДигори ҷаҳониён метарсам!»'),
(5143, 59, 17, 'Саранҷомашон он шуд, ки ҳар ду ба оташ афтанд ва ҷовидона дар он бошанд. Ин аст ҷазои ситамкорон!'),
(5144, 59, 18, 'Эй касоне, ки имон овардаед, аз Худо битарсед. Ва ҳар кас бояд бингарад, ки барои фардояш чӣ фиристодааст. Аз Худо битарсед, ки Худо ба корҳое, ки мекунед, огоҳ аст!'),
(5145, 59, 19, 'Аз он касон мабошед, ки Худоро фаромӯш кардард ва Худо низ чунон кард, то худро фаромӯш кунанд. Онҳо нофармонанд (фосиқонанд).'),
(5146, 59, 20, 'Аҳли дӯзах ва аҳли биҳишт бо ҳам баробар нестанд. Аҳли биҳишт худ зафарёфтагонанд.'),
(5147, 59, 21, 'Агар ин Қуръонро бар кӯҳ нозил мекардем, аз хавфи Худо онро тарсида ва шикофхӯрда медидӣ. Ва ин мисолҳоест, ки барои мардум меоварем, шояд ба фикр фурӯ раванд.'),
(5148, 59, 22, 'Ӯст Худои ягона ки ҳеҷ худое ҷуз Ӯ нест. Донои ниҳону ошкор ва бахшояндаву меҳрубон аст!'),
(5149, 59, 23, 'Ӯст Худои ягона, ки ҳеҷ худои дигаре ҷуз Ӯ нест, фармонравост, пок аст, дар саломат аз ҳар айб аст, эминибахш аст, нигаҳбон аст, ғолиб аст, боҷабарут (азамат) аст ва бузургвор аст. Ва аз ҳар ки барои Ӯ шарик карор медиҳанд, пок аст!'),
(5150, 59, 24, 'Ӯст Худое, ки офаридгор аст, эҷодкунанда ва суратбахш аст, номҳои некӯ аз они Ӯст. Ҳарчи дар осмонҳо ва замин аст, тасбеҳгӯи Ӯ ҳастанд ва Ӯ ғолибу ҳаким аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Эй касоне, ки имон овардаед, душмани Ману душмани худро ба дӯстӣ ихтиёр накунед. Шумо бо онон пайғоми дӯстӣ меафканед ва ҳол он ки онҳо ба сухани ҳаққе, ки бар шумо омададст, имон надоранд. Ва ба он сабаб, ки ба Худо — Парвардигори худ имон оварда будед, паёмбар ва шуморо берун ронданд. Агар барои ҷиҳод дар роҳи Ман ва талаби ризои Ман берун омадаед, дар ниҳон бо онҳо дӯстӣ накунед ва Ман ба ҳар чӣ пинҳон медоред ё ошкор месозед, огоҳтарам. Ва ҳар кӣ чунин кунад, аз роҳи рост гумроҳ гаштааст.'),
(5152, 60, 2, 'Агар бар шумо даст ёбанд, душманӣ мекунанд ва ба озоратон дасту зйбон мекушоянд ва дӯст доранд ки шумо низ кофир гардед.'),
(5153, 60, 3, 'Дар рӯзи қиёмат на хешовандон бароятои нафъ кунанд, на фарзандонатон. Худо миёнатон ҷудои меандозад ва амалҳоятонро мебинад.'),
(5154, 60, 4, 'Иброҳим ва касоне, ки бо вай буданд, он гоҳ ки ба қавми худ гуфтанд, ки мо аз шумо ва аз он чӣ ҷуз Худои якто мепарастед, безорем ва шуморо кофир мешуморем ва миёни мову шумо ҳамеша душманиву бадбинӣ хоҳад буд, то вақте ки ба Худои якто имон биёваред, бароятон некӯ муқтадое (пешвое) буданд. Магар он гоҳ ки Иброҳим падарашро гуфт, ки барои ту бахшоиш металабам, зеро наметавонам азоби Худоро аз ту дафъ кунам. Эй Парвардигори мо, бар Ту таваккал кардем ва ба Ту рӯй овардем ва ба сӯи туст бозгашт!'),
(5155, 60, 5, 'Эй Парвардигори мо, моро чунон макун, ки кофирон хор дорандамон. Ва моро бибахшо, ки Ту ғолибу ҳакимӣ!'),
(5156, 60, 6, 'Онҳо некӯ муқтадое (пешвое) ҳастанд барои шумо — барои касоне, ки ба Худову рӯзи қиёмат умед доранд. Ва ҳар кӣ рӯйгардон шавад. Худо беэҳтиёҷу соҳиби шукр аст!'),
(5157, 60, 7, 'Шояд Худо миёни шумову касоне, ки бо онҳо душманӣ мекунед, дӯстӣ падид оварад. Худо қодир аст ва Худо бахшояндаву меҳрубон аст.'),
(5158, 60, 8, 'Худо шуморо аз некӣ кардан ва адолат намудан бо онон, ки бо шумо дар дин наҷангидаанд ва аз сарзаминатон берун нарондаанд, бознамедорад. Худо касонеро, ки ба адолат рафтор. мекунанд, дӯст дорад.'),
(5159, 60, 9, 'Фақат Худо аз дӯстӣ кардан бо касоне, ки бо шумо дар дин ҷангидаанд ва аз сарзамини худ берунатон рондаанд ё дар берун ронданатон ҳамдастӣ кардаанд, шуморо манъ мекунад. Ва ҳар кӣ бо онҳо дӯстӣ кунад, аз ситамкорон хоҳад буд.'),
(5160, 60, 10, 'Эй касоне, ки имон овардаед, чун занони мӯъмине, ки муҳоҷират (тарки Ватан) кардаанд, ба наздатон оянд, онҳоро бисанҷед. Худо ба имонашон донотар аст. Пас агар донистед, ки имон овардаанд, назди кофирон бозашон нагардонед. Зеро инҳо бар мардони кофир ҳалол нестанд ва мардони кофир низ бар онҳо ҳалол нестанд. Ва ҳар чӣ он кофирон барои ин гуна занон нафақа кардаанд, бипардозед. Ва агар онҳоро никоҳ кунед ва маҳрашонро бидиҳед, гуноҳе накардаед. Ва занони кофири худро нигоҳ надоред. Ва ҳар чӣ нафақа кардаед, аз мардони кофир бихоҳед ва онҳо низ ҳар чӣ хазина (нафақа) кардаанд, аз шумо бихоҳанд. Ин ҳукми Худост. Худо миёни шумо ҳукм мекунад, ва Ӯ донову ҳаким аст!'),
(5161, 60, 11, 'Ва агар яке аз занони шумо ба кофирон пайваст ва он гоҳ ба интиқом аз кофирон бархостед, ба касоне, ки занонашон рафтаанд, ба миқдори нафақае, ки кардаанд, бипардозед ва аз Худое, ки ба Ӯ имон овардаед, битарсед!'),
(5162, 60, 12, 'Эй паёмбар, агар занони мӯъмин назди ту омаданд, то баъат (аҳду паймон) кунанд, ба ин шарт, ки ҳеҷ касро бо Худо шарик накунанд ва дуздӣ накунанд ва зино накунанд ва фарзандони худро накушанд ва фарзандеро, ки аз они шавҳарашон нест, ба дурӯғ ба ӯ нисбат надиҳанд ва дар корҳои нек нофармонии ту накунанд, бо онҳо байъат кун ва барояшон аз Худо бахшоиш бихоҳ, ки Худо бахшояндаву меҳрубон аст!'),
(5163, 60, 13, 'Эй каооне, ки имон овардаед, бо мардуме, ки Худо бар онҳо хашм гирифтааст, дӯстӣ макунед. Инҳо аз охират ноумеданд, ҳамчунон ки он кофироне, ки инак дар гӯранд, аз охират ноумеданд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Худоро тасбеҳ гӯянд ҳар чӣ дар осмошҳову дар замин аст ва Ӯст пирӯзманду ҳаким!'),
(5165, 61, 2, 'Эй касоне, ки имон овардаед, чаро суханоне мегӯед, ки иҷро намекунед?'),
(5166, 61, 3, 'Худованд сахт ба хашм меояд, ки чизе бигӯеду ба ҷой наёваред.'),
(5167, 61, 4, 'Худо дӯст дорад касонеро, ки дар роҳи Ӯ саф кашида монанди деворе, ки хиштҳояшонро ба ҳам пайванд дода бошанд, меҷанганд.'),
(5168, 61, 5, 'Ва Мӯсо ба қавми худ гуфт: «Эй қавми ман, чаро маро меозоред, ҳол он ки медонед, ки ман паёмбари Худо бар шумо ҳастам?» Чун аз ҳақ рӯйгардон шуданд, Худо низ дилҳошонро аз ҳақ бигардонид. Ва Худо мардуми нофармонро ҳидоят намекунад.'),
(5169, 61, 6, 'Ва Исо бинни Марям гуфт: «Эй банӣ-Исроил, ман паёмбари Худо бар шумо ҳастам. Тавротеро, ки пеш аз ман будааст, тасдиқ мекунам ва ба паёмбаре, ки баъд аз ман меояд ва номаш Аҳмад аст, хушхабаратон медиҳам». Чун он паёмбар бо мӯъҷизаҳои равшан омад, гуфтанд: Ин ҷодуест ошкор!»'),
(5170, 61, 7, 'Кист золимтар аз он кас, ки ба Худо дурӯғ мебандад, дар ҳоле ки ӯро ба ислом даъйат мекунанд? Ва Худо ситамкоронро ҳидоят намекунад.'),
(5171, 61, 8, 'Мехоҳанд нури Худоро ба даҳонҳояшон хомӯш кунанд, вале Худо комилкунандаи нури худ аст, агарчи кофиронро нохуш ояд.'),
(5172, 61, 9, 'Ӯст он Худое, ки паёмбари Худро ҳамроҳ бо ҳидоят ва дини ростин бифиристод, то ӯро бар ҳамаи динҳо ғолиб гардонад, дарчанд мушриконро нохуш ояд.'),
(5173, 61, 10, 'Эй касоне, ки имон овардаед, оё шуморо ба тиҷорате, ки аз азоби дардовар раҳоиятон диҳад, роҳ нишон диҳам?'),
(5174, 61, 11, 'Ба Худову паёмбараш имон биёваред ва дар роҳи Худо бо молу ҷони худ ҷиҳод кунед. Ва ин барои шумо беҳтар аст, агар доно бошед.'),
(5175, 61, 12, 'Гуноҳони шуморо мебахшояд ва ба биҳиштҳое, ки дарунаш наҳрҳо ҷорист ва низ хонаҳое хушу покиза дар биҳиштҳои ҷовид дохил мекунад ва ин пирӯзии бузургест.'),
(5176, 61, 13, 'Ва неъмате дигар, ки дӯсташ доред: нусратест (ёриест) аз ҷониби Худованд ва пирӯзии наздик мӯъминонро хабари хуш деҳ.'),
(5177, 61, 14, 'Эй касоне, ки имон овардаед, ёварони Худо бошед, ҳамчунон ки Исо бинни Марям ба ҳавориён (ёварон) гуфт: «Чӣ касоне ёварони ман дар роҳи Худоянд?» Ҳавориён гуфтанд: «Мо ёварони Худо ҳастем». Пас гурӯҳе аз банӣ-Исроил имон оварданд ва гурӯҳе кофир шуданд. Мо касонеро, ки имон оварда буданд, бар зидди душманонашон мадад кардем, то ғолиб шуданд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Худоро тасбеҳ гуянд ҳар чӣ дар осмонҳову ҳар чӣ дар замин аст, он фармонравои пок аз айбро, он ғолиби ҳакимро!'),
(5179, 62, 2, 'Ӯст Худое, ки ба миёни мардуме бесавод паёмбаре аз худашон фиристод, то оёташро бар онҳо бихонад ва онҳоро покиза созад ва китобу ҳикматашон биёмузад. Агарчи пеш аз он дар гумроҳии ошкор буданд,'),
(5180, 62, 3, 'Ва бар гурӯҳе дигар аз онҳо, ки ҳанӯз ба онҳо напайвастаанд. Ва Ӯст Ғолибу ҳаким!'),
(5181, 62, 4, 'Ин бахшоиши Худост, ки ба ҳар кӣ хоҳад, медиҳад ва Худоро бахшоише бузург аст.'),
(5182, 62, 5, 'Мисоли касоне, ки Таврот ба онҳо дода шуда ва ба он амал намекунанд, мисли он хар аст, ки китобҳоеро мебардорад. Бад мисолест мисолм мардуме, ки оёти Худоро дурӯғ мешумурдаанд. Ва Худо золимонро ҳидоят намекунад.'),
(5183, 62, 6, 'Бигӯ: «Эй қавми яҳуд, ҳар гоҳ мепипдоред, ки шумо дӯстони Худо ҳастед, на мардуми дигар, пас орзуи марг кунед, агар рост мегӯед!»'),
(5184, 62, 7, 'Ва онон ба сабаби амалҳое, ки пеш аз ин кардаанд, ҳаргиз орзуи марг нахоҳанд кард. Ба Худо ба ситамкорон доност!'),
(5185, 62, 8, 'Бигӯ: «Он марге, ки аз он мегурезед, шуморо дархоҳад ёфт ва сипас назди он донои ниҳону ошкоро баргардонда мешавед, то ба корҳое, ки кардаед, огоҳатон созад».'),
(5186, 62, 9, 'Эй касоне, ки имон овардаед, чун азони намози рӯзи ҷумъа дода шавад, ба намоз бишитобед ва доду гирифтро тарк кунед,. Агар доно бошед, ин кор бароятон беҳтар аст.'),
(5187, 62, 10, 'Ва чун намоз поён ёфт, дар замин пароканда шавед ва ризқи Худоро талаб кунед ва бисёр ёдаш кунед, бошад, ки наҷот ёбед!'),
(5188, 62, 11, 'Ва чун тиҷорате ё бозичае бинанд, пароканда мешаванд ва ба ҷониби он мераванд ва туро ҳанчунон истода раҳо мекунанд. Бигӯ: «Он чӣ дар назди Худост, аз бозичаву тиҷорат беҳтар аст». Ва Худо беҳтарини рӯзидиҳандагон аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Чун мунофиқон назди ту оянд, гӯянд: «Шаҳодат (гувоҳӣ) медиҳем, ки ту паёмбари Худо ҳастӣ». Худо медонад, ки ту паёмбараш ҳастӣ ва Худо шоҳидӣ медиҳад, ки мунофиқон дурӯғгӯянд.'),
(5190, 63, 2, 'Аз савгандҳои худ сипаре сохтанд ва аз роҳи Худо боздоштанд ва ба ҳақиқат он чӣ мекунанд, нописанд аст.'),
(5191, 63, 3, 'Ин ба он сабаб аст, ки имон оварданд, сипас кофир шуданд. Худо низ бар дилҳошон мӯҳр ниҳод ва онон дарнамеёбанд.'),
(5192, 63, 4, 'Чун онҳоро бубинӣ, туро аз зоҳирашон хуш меояд ва чун сухан бигӯянд, ба суханашон гӯш медиҳӣ. Гӯё ки чӯбҳое ҳастанд ба девор такя дода. Ҳар овозеро ба зиёни худ мепиндоранд. Онҳо душманонанд. Аз онҳо ҳазар кун. Худояшон бикушад. Ба куҷо каҷрав мешаванд?'),
(5193, 63, 5, 'Чун ба онҳо гуфта шавад, ки биёед, то паёмбари Худо бароятон бахшоиш бихоҳад, сар мепечанд. Мебинӣ, ки рӯйгардониву такаббурӣ мекунанд.'),
(5194, 63, 6, 'Тафовуте накунад, чӣ барояшон бахшоиш бихоҳӣ, чӣ бахшоиш нахоҳӣ. Худояшон нахоҳад бахшид. Ва Худо мардуми нофармонро ҳидоят намекунад!'),
(5195, 63, 7, 'Инҳо ҳамонҳоянд, ки мегӯянд: «Бар онҳо, ки гирди паёмбари Худоянд, чизе надиҳед, то аз гирдаш пароканда шаванд». Ва ҳол, он ки хазинаҳои осмонҳову замин аз они Худост, вале мунофиқон намефаҳманд!'),
(5196, 63, 8, 'Мегӯянд: «Чун ба Мадина, бозгардем, соҳибони иззат хору залилҳоро аз он ҷо берун хоҳанд кард». Иззат аз они Худову паёмбараш ва мӯъминон аст. Вале мунофиқон намедонанд.'),
(5197, 63, 9, 'Эй касоне, ки имон овардаед, молҳову авлодатон шуморо аз зикри Худо, ба худ машғул надорад, ки ҳар кӣ чунин кунад, зиёнкор аст.'),
(5198, 63, 10, 'Аз он чӣ рӯзиятон додаем, дар роҳи Худо садақа кунед, пеш аз он ки яке\' аз шуморо марг фаро расад ва бигӯяд; «Эй Парвардигори ман, чаро марги маро андаке ба дертар наяндохтӣ, то садақа диҳам ва аз шоистагон бошам?»'),
(5199, 63, 11, 'Чун касе аҷалаш фаро расад, Худо марги ӯро ба таъхир (дертар) намегузорад, Ва Худо ба корҳое, ки мекунед, огоҳ аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Худоро тасбеҳ мегӯянд ҳар чӣ дар осмонҳову ҳар чӣ дар замин аст. Фармонравоӣ аз они Ӯст ва шукр сазовори Ӯст ва Ӯ бар ҳар чизе тавоност!'),
(5201, 64, 2, 'Ӯст, ки шуморо биёфарид. Баъзе аз шумо кофир ва баъзе мӯъминанд. Ва корҳое, ки мекунед, мебинад!'),
(5202, 64, 3, 'Осмонҳову заминро ба ҳақ биёфарид ва шуморо сурат баст ва некӯ сурат баст. Ва бозгашти ҳама ба сӯи Ӯст!'),
(5203, 64, 4, 'Ҳар чиро дар осмонҳову замин аст, медонад ва ба он чӣ пинҳон медоред, ё ошкор месозед, огоҳ аст. Ва Худо ба ҳар чӣ дар дилҳо мегузарад, доно аст!'),
(5204, 64, 5, 'Оё шуморо хабари касоне, ки аз ин пеш кофир шуда буданд, нарасидааст? Уқубати амалашонро чашиданд ва онҳорост азобе дардовар.'),
(5205, 64, 6, 'Ин ба ҷазои он буд, ки паёмбаронашон бо далелҳои равшан бар онон фиристода шуданд, Ва онҳо гуфтанд: «Оё одамиён моро ҳидоят мекунанд?» Пас кофир шуданд ва рӯйгардон гаштанд ва Худо беҳоҷат асту тавонгар ва сазовори ситоиш аст.'),
(5206, 64, 7, 'Кофирон пиндоштанд, ки онҳоро зинда намекунанд. Бигӯ: «Оре, ба Парвардигорам савганд, ки шуморо зинда мекунанд, то ба корҳое, ки кардаед, огоҳ, созанд. Ва ин бар Худо осон аст».'),
(5207, 64, 8, 'Пас ба Худову паёмбараш ва ин нур, ки нозил кардаем, имон биёваред. Ва Худо ба корҳое, ки мекунед, огоҳ аст!'),
(5208, 64, 9, 'Рӯзе, ки ҳамаи шуморо дар маҳшар ҷамъ оварад, он рӯз рӯзи зиён аст. Ва ҳар ки ба Худо имон биёварад ва кори шоиста кунад, гуноҳонашро мерезад ва ба биҳиштҳое, ки дар он наҳрҳо ҷорист, дохил кунад. Он ҷо ҷовидона хоҳанд буд. Ин комёбии бузургест!'),
(5209, 64, 10, 'Ва онон, ки кофир шуданд ва оёти Моро дурӯғ бароварданд, аҳли оташанд. Дар он ҷовидонаанд ва ҷаҳаннам бад бозгаштест!'),
(5210, 64, 11, 'Ҳеҷ мусибате бе фармони Худо ба касе намерасад, Ва ҳар кӣ ба Худо имон биёварад, Худо қалбашро ҳидоят мекунад,. Ва Худо ба ҳар чизе доност!'),
(5211, 64, 12, 'Худоро итоъат кунед ва паёмбарро итоъат кунед. Пас агар шумо рӯй гардонед, бар фиристодаи Мо вазифае ғайри расонидани паёми ошкор нест.'),
(5212, 64, 13, 'Худо яктост, ки ҳеҷ худое ғайри Ӯ нест. Мӯъминон, албатта, бар Худои якто таваккал кунанд.'),
(5213, 64, 14, 'Эй касоне, ки имон овардаед, баъзе аз занону фарзандонатон душмани шумо ҳастанд. Аз онҳо ҳазар кунед, Ва агар афв кунед ва чашм бипӯшеду гуноҳашон пӯшида доред, Худо бахшояндаву меҳрубон аст!'),
(5214, 64, 15, 'Ҷуз ин нест, ки амволу авлоди шумо озмоише ҳастанд. Ва ҳол он ки музди фаровон назди Худост.'),
(5215, 64, 16, 'То тавонед, аз Худо битарсед ва гӯш андозед ва итоъат кунед ва ба нафъи худ аз молатон хайр кунед. Ва онон, ки аз ҳирси нафси худ дар амон мондаанд, наҷот ёфтагонанд.'),
(5216, 64, 17, 'Агар ба Худо қарзулҳасана садақа дар роҳи Худо диҳед, бароятон ду \'баробараш хоҳад кард. Ва шуморо мебахшояд, ки Ӯ шукркунандаву бурдбор аст.'),
(5217, 64, 18, 'Донои ниҳону ошкор аст, ғолибу ҳаким аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Эй паёмбар, агар занонро талоқ медиҳед, ба вақти идда талоқашон диҳед. Ва шумори иддаро нигаҳ доред. Ва аз Худои якто—Парвардигорагош битарсед. Ва ононро аз хонаҳояшон берун накунед. Ва аз хона берун нараванд, магар он ки ба ошкоро коре зишт кунанд. Инҳо аҳкоми Худованд аст ва ҳар кӣ аз он таҷовуз кунад, ба худ ситам кардааст. Ту чӣ донӣ, шояд Худо аз ин пас амре тоза падид оварад.'),
(5219, 65, 2, 'Ва чун ба поёни муддат расиданд ё ба ваҷҳе некӯ нигоҳашон доред ё ба ваҷҳе некӯ аз онҳо ҷудо шавед ва ду тан одил аз худатонро ба гувоҳӣ гиред. Ва барои Худо гувоҳиро ба ростӣ адо кунед. Ҳар киро ба Худову рӯзи қиёмат имон дорад, инчунин панд медиҳанд. Ва ҳар кӣ аз Худо битарсад, барои ӯ роҳе барои берун шудаи қарор хоҳад дод'),
(5220, 65, 3, 'ва аз ҷое, ки гумонашро надорад, рӯзиаш медиҳад. Ва ҳар кӣ бар Худо таваккал кунад, Худо ӯро кофист. Худо кори худро ба иҷро мерасонад ва ҳар чизро андозае қарор додааст.'),
(5221, 65, 4, 'Агар дар шубҳа ҳастед, аз миёни занонатон аз онҳое, ки аз ҳайз маъюс шудаанд ва онҳое, ки ҳанӯз ҳайз нашудаанд, иддаашон се моҳ аст. Ва иддаи занони ҳомила ҳамон вазъи ҳамл (таваллуд) аст. Ва ҳар кӣ аз Худо битарсад, Худо корашро осон хоҳад кард.'),
(5222, 65, 5, 'Ин фармони Худост, ки бар шумо нозил кардааст. Ва ҳар кӣ аз Худо битарсад, гуноҳонашро аз ӯ дур мекунад ва ӯро музде бузург медиҳад.'),
(5223, 65, 6, 'Ҳар ҷо, ки худ сукунат кунед, онҳоро низ ба қадри тавонии худ маскан диҳед. Ва то бар онҳо танг гиред, маёзоредашон. Ва агар ҳомила буданд, нафақаашонро \'бидиҳед, то вазъи ҳамд кунанд. Ва агар фарзанди шуморо шир медиҳанд, муздашонро бидиҳед ва ба ваҷҳе некӯ бо якдигар маслиҳат кунед. Ва агар мувофиқат накардед, аз зане дигар бихоҳед, ки кӯдакро шир диҳад.'),
(5224, 65, 7, 'Ҳар молдоре аз моли худ нафақа дихаҳд ва касе, ки тангдаст бошад, аз ҳар чӣ Худо ба ӯ додааст, нафақа диҳад. Худо ҳеҷ касро ғайри он андоза, ки ба ӯ додааст, вазифадор намесозад ва ба зуди Худо пас аз сахтӣ осонӣ пеш орад.'),
(5225, 65, 8, 'Чӣ басо мардуми деҳае, ки аз фармони Парвардигорашон ва паёмбаронаш бар боззаданд. Он гоҳ Мо сахт аз онҳо ҳисоб гирифтем ва ба азобе сангин азобашон кардем.'),
(5226, 65, 9, 'Пас уқубати амали худро чашиданд. Оқибати корашон зиёнкорӣ буд.'),
(5227, 65, 10, 'Худованд барояшон азобе сахт омода кард. Пас, эй хирадмандоне, ки имон овардаед, аз Худо битарсед. Худо бар шумо Қуръонро нозил кардааст.'),
(5228, 65, 11, 'Ва паёмбаре, ки оёти равшани Худоро бароятон мехонад, то касонеро, ки имон овардаанд ва корҳои шоиста кардаанд, аз торикӣ ба рушноӣ оварад, Ва ҳар киро ба Худо имон оварад ва кори шоиста кунад, ба биҳиштҳое дароварад, ки дар он наҳрҳо ҷорист, ҳамеша дар он ҷо бимонанд ва Худо рӯзияшонро бепоён гардонад.'),
(5229, 65, 12, 'Худост, он ки ҳафт осмону монанди онҳо замин биёфарид. Фармони Ӯ миёни осмонҳову замин ҷорист, то бидонед, ки Худо ба ҳар чиз қодир аст ва ба илм бар ҳама чиз иҳота дорад.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Эй паёмбар, чаро чизеро, ки Худо бар ту ҳалол кардааст, ба хотири хушнуд сохтани занонат бар худ ҳаром мекунӣ? Ва Худо бахшояндаву меҳрубон аст!'),
(5231, 66, 2, 'Худо барои шумо кушодани савганд ҳоятонро муқаррар дошта аст. Худост ёвари шумо ва Ӯст донову ҳаким!'),
(5232, 66, 3, 'Он гоҳ ки паёмбар бо яке аз занони худ розе дар миён ниҳод, чун он зан он роз бо дигаре бозгуфт. Худо паёмбарро аз он огоҳ сохт ва ӯ баъзе аз он розро бар он зан ошкор, кард ва аз ифшон баъзеи дигар рӯй тофт. Чун ӯро аз он хабар дод, гуфт: «Чӣ касе туро аз ин моҷаро огоҳ кардааст?» Гуфт: «Он Худои донои огоҳ ба ман хабар додааст».'),
(5233, 66, 4, 'Агар шумо ду зан тавба кунед, беҳтар аст, зеро дилҳоятон аз ҳақ бозгаштааст. Ва агар барои озораш ҳамдаст шавед, Худо ёвари ӯст ва низ Ҷабраил ва мӯъминони шоиставу фаришгагон аз он пас ёвари ӯ хоҳанд буд,'),
(5234, 66, 5, 'Шояд агар шуморо талоқ гӯяд, Парвардигораш ба ҷои шумо заноне, беҳтар аз шумояш бидиҳад. Заноне мусалмон, мӯъмин, фармонбардор, тавбакунанда, ибодаткунанда рӯзагиранда, хоҳ шавҳар карда, хоҳ бокира.'),
(5235, 66, 6, 'Эй касоне, ки имон овардаед, худ ва хонаводаи худро аз оташе, ки ҳезуми он мардум ва сангҳо ҳастанд, нигаҳ доред. Фариштагоне дуруштгуфтору сахтгир бар он оташ муваккаланд (вазифадор). Ҳар чӣ Худо бигӯяд, нофармонӣ намекунанд ва ҳамон мекунанд, ки ба он амр шудаанд.'),
(5236, 66, 7, 'Эй кофирон, дар он рӯз узр махоҳед. Фақат баробари коре, ки кардаед, ҷазо дода шавед.'),
(5237, 66, 8, 'Эй касоне, ки имон овардаед, ба даргоҳи Худо тавба кунед, тавбае аз рӯи ихлос. Бошад, ки Парвардигоратон гуноҳонатонро маҳв (дур) кунад ва шуморо ба биҳиштҳое дохил кунад, ки дар он наҳрҳо ҷорист. Дар он рӯз Худо паёмбар ва касонеро, ки бо ӯ имон овардаанд, хору зор накунад ва нурашон пешопеш ва самти росташон дар ҳаракат бошад. Мегӯянд: «Эй Парвардигори мо, нури моро барои мо ба камол расон ва моро бибахшой, ки Ту бар ҳар коре тавоно ҳастӣ!»'),
(5238, 66, 9, 'Эй паёмбар, ба кофирону мунофиқон: чиҳод кун ва бар онҳо сахт бигир. Ҷойгоҳашон ҷаҳаннаад аст, ки бад ҷои бозгашт аст!'),
(5239, 66, 10, 'Худо барои кофирон мисоли зани Нӯҳу зани Лутро меоварад, ки ҳар ду дар никоҳи ду тан аз бандагони солеҳи Мо буданд ва ба он ду хиёнат карданд, Ва онҳо натавонистанд аз занони худ дафъи азоб кунанд ва гуфта шуд: «Бо дигарон ба оташ дароед!»'),
(5240, 66, 11, 'Ва Худо барои касоне, ки имон овардаанд, зани Фиръавнро мисол меорад, он гоҳ ки гуфт: «Эй Парвардигори ман, барои ман дар биҳишт назди Худ хонае бино кун ва маро аз Фиръавн ва амалаш наҷот деҳ ва маро аз мардуми золим бираҳон!»'),
(5241, 66, 12, 'Ва Марям духтари Имронро, ки шармгоҳи худро аз зино нигаҳ дошт ва Мо аз рӯҳи Худ дар он дамидем. Ва ӯ калимоти Парвардигори худ ва китобҳояшро тасдиқ кард ва ӯ аз фармонбардорон буд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Бузургвору бобаракат Худованде, ки фармонравой ба дасти Ӯст ва Ӯ бар ҳар чизе тавоност,'),
(5243, 67, 2, 'он ки маргу зиндагиро биёфарид, то биёзмоядатон, ки кадом як аз шумо ба амал некӯтар аст ва Ӯст ғолибу бахшоянда!'),
(5244, 67, 3, 'Он ки ҳафт осмони табақа-табақаро биёфарид. Дар офариниши Худои раҳмон ҳеҷ халалу беназмӣ намебинӣ. Пес бори дигар назар кун, оё дар осмон шикофе мебинӣ?'),
(5245, 67, 4, 'Бори дигар низ чашм боз кун ва бингар. Нигоҳи ту хаставу дармонда ба назди ту бозхоҳад гашт.'),
(5246, 67, 5, 'Мо осмони дунёро ба чароғҳое биёростем ва он чароғҳоро василаи сангсор кардани шаётин гардонидем ва барояшон азоби оташи сӯзон омода кардаем.'),
(5247, 67, 6, 'Ва барои касоне, ки ба Парвардигорашон кофир шудаанд, азоби ҷаҳаннам бошад ва ҷаҳаннам бад ҷои бозгаштест!'),
(5248, 67, 7, 'Чун дар ҷаҳаннам афканда шаванд, ба ҷӯш ояд ва бонги зишташро бишнаванд.'),
(5249, 67, 8, 'Наздик аст, ки аз хашм пора-пора шавад. Ва чун гурӯҳеро дар он (афкананд, нигаҳбононаш гӯяндашон:'),
(5250, 67, 9, '«Оё шуморо бимдиҳандае наёмад?» Гӯянд: «Чаро, бимдиҳанда омад, вале ӯро дурӯғ баровардем ва гуфтем; «Худо ҳеҷ чиз нозил накардааст, шумо дар гумроҳии бузурге ҳастед!»'),
(5251, 67, 10, 'Ва мегӯянд: «Агар мо мешунидем ё ақл мекардем, аҳли ин оташи сӯзанда намебудем».'),
(5252, 67, 11, 'Пас ба гуноҳи худ эътироф мекунанд. Эй лаънат бод бар онҳо, ки аҳли оташи сӯзандаанд!'),
(5253, 67, 12, 'Барои касоне, ки нодида аз Парвардигорашон метарсанд бахшоишу музди фаровон аст.'),
(5254, 67, 13, 'Чӣ пинҳон сухан гӯед ва чӣ ошкоро, Ӯ ба ҳар чӣ дар дилҳо мегузарад, доност.'),
(5255, 67, 14, 'Оё он кӣ офарида, намедонад? Ҳол он ки Ӯ борикбину огоҳ аст.'),
(5256, 67, 15, 'Ӯст, ки заминро роми шумо гардонид Пас бар рӯи он сайр кунед, ва аз ризқи Худо бихӯред. Чун аз қабр берун оед, ба сӯи Ӯ меравед.'),
(5257, 67, 16, 'Оё аз он кӣ дар осмон аст, эмин нишастаед, ки ногоҳ заминро ба ларзиш дароварад ва замин шуморо дар худ фурӯ барад?'),
(5258, 67, 17, 'Ё аз он кӣ дар осмон аст, эмин нишастаед, ки ногоҳ боде ҳамроҳ бо сангрез бар сари шумо фиристад? Ба зуди хоҳед донист, ки бим додани Ман чӣ гуна аст.'),
(5259, 67, 18, 'Албатта касоне, ки пеш аз онҳо буданд, паёмбаронро дурӯғ бароварданд. Пас азоби Ман чӣ гуна буд!'),
(5260, 67, 19, 'Оё паррандагонеро, ки бол кушуда ва бол кашида бар болои сарашон дар парвозанд, надидаанд? Онҳоро ҷуз Худои раҳмон касе дар ҳаво нигоҳ натавонад дошт. Ӯст, ки ба ҳама чиз биност!'),
(5261, 67, 20, 'Оё онон, ки ёрони шумоянд, тавонанд дар баробари Худо ёриатон кунанд? Кофирон дар фиребе беш нестанд.'),
(5262, 67, 21, 'Оё кист он кӣ ба шумо рӯзӣ диҳад, агар Ӯ рӯзии худ боздорад? На! дар саркашиву дури аз ҳақ пойдор меистанд.'),
(5263, 67, 22, 'Оё он касе, ки нагунсор (хамшуда) бар рӯй афтода роҳ меравад, ҳидоятёфтатар аст ё он ки бар пой истода ва ба роҳи рост меравад?'),
(5264, 67, 23, 'Бигӯ: «Ӯст, ки шуморо офаридааст ва гӯшу чашму дил додааст. Чӣ андак шукр мегузоред!»'),
(5265, 67, 24, 'Бигӯ: «Ӯст, ки шуморо дар замин офарид ва дар қиёмат назди Ӯ ҷамъ оварда мешавед».'),
(5266, 67, 25, 'Мегӯянд: «Агар рост мегӯед, ин ваъда чӣ вақт фаро мерасад?»'),
(5267, 67, 26, 'Бигӯ: «Илми он назди Худост ва ман бимдиҳандае ошкорам».'),
(5268, 67, 27, 'Кофирон чун аз наздикаш бингаранд, чеҳраашон гирифта шавад, Ва ба онҳо гуфта мешавад: «Ин аст он чӣ ботилаш мехондед!'),
(5269, 67, 28, 'Бигӯ: «Хабар диҳед, ки агар (фаразан) Худо марову ҳамроҳонамро ҳалок кунад ё бар мо раҳмат оварад, чӣ касе кофиронро аз азоби дардовар мераҳонад?»'),
(5270, 67, 29, 'Бигӯ: «Ӯ Худои раҳмон аст. Ба Ӯ имон овардем ва бар Ӯ таваккал кардем. Ва ба зуди, хоҳед донист, чӣ касе дар гумроҳии ошкор аст!»'),
(5271, 67, 30, 'Бигӯ: «Хабар диҳед, агар обатон дар замин фурӯ равад, чӣ касе шуморо оби равон хоҳад дод?»');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Нун, савганд ба қалам ва он чӣ менависанд,'),
(5273, 68, 2, 'ки ту ба фазли Парвардигорат девона нестӣ'),
(5274, 68, 3, 'ва турост мукофоте бепоён'),
(5275, 68, 4, 'ва турост хулқе азим.'),
(5276, 68, 5, 'Ба зудӣ ту бубинӣ ва онҳо низ бубинанд,'),
(5277, 68, 6, 'ки девонагӣ дар кадом як аз шумост.'),
(5278, 68, 7, 'Албатта Парвардигори ту беҳтар медонад чӣ касе аз тариқи Ӯ гумроҳ гашта, ва Ӯ ба роҳёфтагон донотар аст.'),
(5279, 68, 8, 'Пас ба такзибкунандагон (дурӯғ шуморандагон) итоъат макун.'),
(5280, 68, 9, 'Дӯст доранд, ки нармӣ кунӣ, то нармӣ кунанд,.'),
(5281, 68, 10, 'Ба ҳар фурӯмояе (пасте), ки бисёр: савганд мехӯрад пайравӣ макун:'),
(5282, 68, 11, 'айбҷӯе, ки барои суханчинӣ ин ҷову он ҷо меравад,'),
(5283, 68, 12, 'боздоранда аз хайр, аз ҳад гузаранда, гуноҳкор,'),
(5284, 68, 13, 'дағалмарде, ношинохтанасаб,'),
(5285, 68, 14, 'ба он ҷиҳат, ки соҳиби молу фарзанд аст.'),
(5286, 68, 15, 'Чун оёти Мо бар ӯ хонда шавад, гӯяд: «Афсонаҳои пешиниён аст».'),
(5287, 68, 16, 'Ба зудӣ бар биниаш доғ гузорем.'),
(5288, 68, 17, 'Мо онҳоро озмудем, чунон ки соҳибони он бустонҳоро озмудем. Он гоҳ., ки қасам хӯрданд, ки фардо бомдод меваҳоро хоҳанд чид.'),
(5289, 68, 18, 'Ва иншооллоҳ нагуфтанд.'),
(5290, 68, 19, 'Пас шабҳангом, ки ба хоб буданд, офате аз осмон омад,'),
(5291, 68, 20, 'ва бӯстонҳо сиёҳ шуд.'),
(5292, 68, 21, 'Ва саҳаргоҳон якдигарро нидо доданд:'),
(5293, 68, 22, '«Агар мехоҳед, мева бичинед, бомдодон ба киштзори худ биравед!»'),
(5294, 68, 23, 'Ба роҳ афтоданд ва оҳиста мегуфтанд;'),
(5295, 68, 24, 'ки имрӯз набояд бенавое ба бӯстони шумо дарояд.'),
(5296, 68, 25, 'Субҳгоҳон ба ин ният, ки метавонанд бенаворо манъ кунанд, берун шуданд.'),
(5297, 68, 26, 'Чун бӯстонҳои худро диданд, гуфтанд: «Роҳро гум кардаем.'),
(5298, 68, 27, 'На, мо аз ҳосил маҳрум шудаем».'),
(5299, 68, 28, 'Некмардашон гуфт: «Шуморо нагуфгам, чаро Худоро тасбеҳ намегӯед?»'),
(5300, 68, 29, 'Гуфтанд,: «Пок аст Парвардигори мо, мо ситамкор будем».'),
(5301, 68, 30, 'Пас забон ба маломати якдигар кушуданд.'),
(5302, 68, 31, 'Гуфтанд: «Вой бар мо, мо мардуме саркаш будаем,'),
(5303, 68, 32, 'бошад, ки Парвардигори \'мо дар иваз чизе беҳтар аз он моро диҳад, Мо ба Парвардигори худ рӯй овардаем!»'),
(5304, 68, 33, 'Инчунин аст азоб! Ва агар бидонанд, азоби охират бузургтар аст!'),
(5305, 68, 34, 'Барои парҳезгорон дар назди Парвардигорашон бӯстонҳоест пурнеъмат.'),
(5306, 68, 35, 'Оё бо таслимшудагон чун ба гунаҳкорон рафтор мекунем?'),
(5307, 68, 36, 'Шуморо чӣ мешавад? Чӣ тавр доварӣ мекунед?'),
(5308, 68, 37, 'Оё шуморо китобест, ки аз он мехонед?'),
(5309, 68, 38, 'Ва ҳар чӣ ихтиёр кунед, дар он ҳаст?'),
(5310, 68, 39, 'Ё бо Мо паймоне, ки то рӯзи қиёмат кашад, бастаед, ки ба ҳар чӣ ҳукм кунед, ҳаққи шумо бошад?'),
(5311, 68, 40, 'Аз онҳо бипурс, кадом якашон зомини чунин паймонест?'),
(5312, 68, 41, 'Ё онҳоро шариконест? Агар рост мегӯянд, шарикони худро биёваранд.'),
(5313, 68, 42, 'Рӯзе, ки он воқеъаи азим рӯй диҳад ва онҳоро ба саҷда даъваткунанд, вале натавонанд.'),
(5314, 68, 43, 'Ваҳшат дар чашмонашон пайдост, хори бар онҳо ғолиб шудааст, пеш аз ин низ онҳоро дар айни тандуруст ба саҷда даъват карда буданд.'),
(5315, 68, 44, 'Пас Маро ба онҳо, ки ин суханро дурӯғ мебароранд вогузор, то андак-андак,: чунон ки дарнаёбанд, ба азоб гиремашон'),
(5316, 68, 45, 'ва ба онҳо мӯҳлат диҳам. Албатта макри Ман макре устувор аст.'),
(5317, 68, 46, 'Ё аз онон музде талабидаӣ ва акнун аз адои он дар ранҷанд?'),
(5318, 68, 47, 'Ё илми ғайб медонанд ва онҳо, менависанд?'),
(5319, 68, 48, 'Дар баробари фармони Парвардигорат собир бош ва чун соҳиби моҳӣ мабош, ки бо диле пурандӯҳ нидо дардод.'),
(5320, 68, 49, 'Агар неъмати Парвардигораш набуд, дар айни бадҳолӣ ба саҳрое беобу гиёҳ меафтод.'),
(5321, 68, 50, 'Пас Парвардигораш ӯро ихтиёр кард ва дар зумраи солеҳонаш даровард.'),
(5322, 68, 51, 'Ва кофирон чун Қуръонро шуниданд, наздик буд, ки туро, бо чашмони худ билағжонанд ва мегӯянд, ки ӯ девона аст'),
(5323, 68, 52, 'ва ҳол он ки Қуръон барои ҷаҳониён фақат панде аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Он рӯзи барҳақ!'),
(5325, 69, 2, 'Чист он рӯзи барҳақ?'),
(5326, 69, 3, 'Ва чӣ донӣ, ки он рӯзи барҳақ чист?'),
(5327, 69, 4, 'Қавми Самуду Од рӯзи қиёматро дурӯғ бароварданд.'),
(5328, 69, 5, 'Аммо қавми Самуд ба он бонги сахт ҳалок шуданд.'),
(5329, 69, 6, 'Ва аммо қавми Од бо вазиши боли сар-сяр (сахт) ба ҳалокат расиданд.'),
(5330, 69, 7, 'Он азобро ҳафт шабу ҳашт рӯз пай дар пай бар онон фиристод. Он қавмро чун танаҳои пӯсидаи хурмо медидӣ, ки афтодаанду мурдаанд,.'),
(5331, 69, 8, 'Оё касеро мебинӣ, ки аз онҳо бар ҷой монда бошад?'),
(5332, 69, 9, 'Ва Фиръявну мардуме, ки пеш аз ӯ буданд ва низ мардуми Мӯътафиқа гунаҳкор омаданд,'),
(5333, 69, 10, 'Фиристодаи Парвардигорашонро нофармонӣ карданд ва Худо низ ононро ба сахтӣ фуру гирифт,'),
(5334, 69, 11, 'Чун об туғён кард, шуморо ба киштӣ савор кардем,'),
(5335, 69, 12, 'то онро мояи пандатон гардонем ва гӯшнигаҳдорандаи панд онро фаро гирад.'),
(5336, 69, 13, 'Чун як бор дар сур дамида шавая'),
(5337, 69, 14, 'ва замину кӯҳҳоро баргиранд ва якбора дар ҳам кӯбанд,'),
(5338, 69, 15, 'он рӯз он ҳодиса ба вуқӯъ пайваста бошад'),
(5339, 69, 16, 'ва осмон, ки дар он рӯз суст шудааст, бишикофад.'),
(5340, 69, 17, 'Ва фариштагон дар атрофи осмон бошанд, Ва дар он рӯз ҳашт тан аз онҳо арши Парвардигоратро бар болои сарашон мебардоранд.'),
(5341, 69, 18, 'Он рӯз шуморо пеш оваранд ва ҳеҷ чизи шумо ниҳон намонад.'),
(5342, 69, 19, 'Аммо ҳар кас, ки номаи аъмолашро ба дасти росташ диҳанд, мегӯяд: «Ном\'аи маро бигиреду бихонед.'),
(5343, 69, 20, 'Ман яқин доштам, ки ҳисоби худро хоҳам дид».'),
(5344, 69, 21, 'Пас ӯ дар як зиндагии писандидаву хуш хоҳад буд'),
(5345, 69, 22, 'дар биҳиште олӣ,'),
(5346, 69, 23, 'ки меваҳояш наздикшаванда бошад.'),
(5347, 69, 24, 'Бихӯреду биёшомед, гуворо бод шуморо. Инҳо мукофоти амалҳоест, ки дар айёми гузашта ба ҷой меовардаед!»'),
(5348, 69, 25, 'Аммо он кас, ки номаи аъмолашро ба дасти чапаш диҳанд, мегӯяд: «Эй кош,, номаи маро ба дасти ман намедоданд'),
(5349, 69, 26, 'ва надониста будам, ки ҳисоби ман чист!'),
(5350, 69, 27, 'Эй кош, ҳамон марг мебуду бас!'),
(5351, 69, 28, 'Дороии ман маро фоида набахшид,'),
(5352, 69, 29, 'қудрати ман аз дасти ман бирафт!»'),
(5353, 69, 30, '«Бигиредаш, занҷираш кунед!'),
(5354, 69, 31, 'Ва ба ҷаҳаннамаш дараред!'),
(5355, 69, 32, 'Ва ӯро бо занҷире ба дарозии ҳафтод зироъ (аз оринҷ то ангушт) бикашед!'),
(5356, 69, 33, 'Зеро ба Худои бузург имон надошт'),
(5357, 69, 34, 'ва ба таъом додан ба мискинон касеро тарғиб намекард.'),
(5358, 69, 35, 'Дар он рӯз дар он ҷо ҳеҷ дӯсте нахоҳад дошт.'),
(5359, 69, 36, 'Таъомаш чизе ғайри чирку хуни аҳли дӯзах нест,'),
(5360, 69, 37, 'Танҳо хатокорон аз он таъом мехӯранд,'),
(5361, 69, 38, 'Пас савганд ба он чи мебинед'),
(5362, 69, 39, 'ва он чӣ намебинед,'),
(5363, 69, 40, 'ки ин сухани фиристодае бузургвор аст,'),
(5364, 69, 41, 'на сухани шоире. Чӣ андак имон меоварад!'),
(5365, 69, 42, 'Ва низ сухани коҳине (фолбин, ҷодугар) нест. Чӣ андак панд мегиред!'),
(5366, 69, 43, 'Аз ҷониби Парвардигри ҷаҳониён нозил шуда аст.'),
(5367, 69, 44, 'Агар паёмбар баъзе суханонро бофта бар мо мебаст, («аз ҷониби худост» — мегуфт).'),
(5368, 69, 45, 'бо қудрат ӯро фурӯ мегирифтем,'),
(5369, 69, 46, 'сипас раги дилашро пора, мекардем'),
(5370, 69, 47, 'ва ҳеҷ як аз шуморо тавони он набувад, ки монеъ шавад.'),
(5371, 69, 48, 'Ва Қуръон барои парҳезгорон пандест.'),
(5372, 69, 49, 'Мо медонем, ки аз миёни шумо касоне ҳастанд, ки такзиб мекунанд, (дурӯғ мешумуранд).'),
(5373, 69, 50, 'Ва Қуръон кофиронро мояи пушаймонӣ аст.'),
(5374, 69, 51, 'Ва он сухани ҳақ ва яқин аст.'),
(5375, 69, 52, 'Пас ба номи Парвардигори бузургат тасбеҳ (бигӯй.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Пурсандае аз азобе пурсид, ки'),
(5377, 70, 2, 'бар кофирон фуруд хоҳад омад ва кас онро дафъ натавонад кард'),
(5378, 70, 3, 'аз ҷониби Худо, ки соҳиби осмонҳост.'),
(5379, 70, 4, 'Дар рӯзе, ки миқдораш панҷоҳ ҳазор сол аст, фариштагон ва рӯҳ ба он ҷо боло раванд.'),
(5380, 70, 5, 'Пас сабр кун, сабре некӯ.'),
(5381, 70, 6, 'Онҳо он рӯзро дур мебинанд'),
(5382, 70, 7, 'ва мо наздикаш мебинем'),
(5383, 70, 8, 'рӯзе, ки осмон чун филиз гудохта гардад'),
(5384, 70, 9, 'ва кӯҳҳо чун пашм (дар сабукӣ)'),
(5385, 70, 10, 'ва ҳеҷ хешованде аз ҳоли хешованди худ напурсад,'),
(5386, 70, 11, 'якдигарро баробари чашмашон оранд. Гуноҳкор дӯст дорад, ки худашро бозхарад ба фарзандонаш'),
(5387, 70, 12, 'ва занаш ва бародараш'),
(5388, 70, 13, 'ва аширааш (хешонаш), ки ӯро макон додааст'),
(5389, 70, 14, 'ва ҳамаи онҳо, ки дар рӯи заминанд ва наҷот ёбад.'),
(5390, 70, 15, 'Ҳаргиз на, ки он шӯълаи оташ аст,'),
(5391, 70, 16, 'пӯсти сарро меканад,'),
(5392, 70, 17, 'ҳар киро, ки ба ҳақ пушт кард ва аз фармон сар печид, ба тарафи худ мехонад'),
(5393, 70, 18, 'ва онро, ки ҷамъ меоварду ҷой мекард.'),
(5394, 70, 19, 'Албатта одамиро ҳарису бесабр офаридаанд.'),
(5395, 70, 20, 'Чун бадие ба ӯ расад, беқарорӣ кунад.'),
(5396, 70, 21, 'Ва чун моле ба дасташ афтад бахилӣ мекунад,'),
(5397, 70, 22, 'магар намозгузорандагон:'),
(5398, 70, 23, 'онон, ки ба намоз мудовамат (давом) мекунанд'),
(5399, 70, 24, 'ва онон, ки дар молҳояшон ҳаққест муъайян'),
(5400, 70, 25, 'барои гадову маҳрум'),
(5401, 70, 26, 'ва касоне, ки рӯзи қиёматро тасдиқ мекунанд'),
(5402, 70, 27, 'ва касоне, ки аз азбоби Парвардигорашон тарсонанд,'),
(5403, 70, 28, 'ки аз азоби Парвардигорашон дар амон натавонанд буд'),
(5404, 70, 29, 'ва касоне, ки шармгоҳи худ нигаҳ медоранд,'),
(5405, 70, 30, 'магар барои ҳамсаронашон ё канизонашон, ки дар ин ҳол, маломате бар онҳо нест.'),
(5406, 70, 31, 'Ва онон, ки ғайри инро биталабанд, аз ҳад гузаштагон ҳастанд.'),
(5407, 70, 32, 'Ва касоне, ки амонатҳову аҳдхои худро риъоят мекунанд'),
(5408, 70, 33, 'ва касоне, ки шоҳидии худ ба ҳақ адо мекунанд'),
(5409, 70, 34, 'ва касоне, ки бар намозҳошон мувозибат (давом) доранд,'),
(5410, 70, 35, 'инҳо дар биҳишт гиромн ҳастанд.'),
(5411, 70, 36, 'Пас чист, ки кофирон ба сӯи ту мешитобанд?'),
(5412, 70, 37, 'Даста-даста аз ҷониби чапу, аз ҷониби рост.'),
(5413, 70, 38, 'Оё ҳар як аз онҳо тамаъ мекунад, ки ба биҳишти пурнеъмат дохил шавад?'),
(5414, 70, 39, 'Ҳаргиз на, онҳо худ медонанд,, ки аз чӣ чиз онҳоро офаридаем.'),
(5415, 70, 40, 'Пас савганд ба Парвардигори машриқҳову мағрибҳо, ки Мо тавоноем,'),
(5416, 70, 41, 'ки ба ҷои онҳо мардуме беҳтар биёварем ва дар ин кор нотавон нестем.'),
(5417, 70, 42, 'Пас бигузорашон то ба беҳудагӣ машғул шаванду ба бозича, то ба он рӯзе, ки ваъдаашон додаем, бирасанд,'),
(5418, 70, 43, 'рӯзе, ки шитобон аз қабрҳо бадар оянд, чунон ки гӯӣ назди бутон мешитобанд'),
(5419, 70, 44, 'ваҳшат бар чашмонашон ғолиб шуда ва ба хори афтодаанд: ин аст ҳамон рӯзе, ки онҳоро ваъда дода буданд!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Мо Нӯҳро ба сӯи қавмаш фиристодем, ки қавми худро пеш аз он ки азобе дардовар бар сарашон фуруд ояд, бим деҳ.'),
(5421, 71, 2, 'Гуфт: «Эй қавми ман, ман бимдиҳандае равшангарам,'),
(5422, 71, 3, 'Худоро бипарастед ва аз Ӯ битарсед ва ба ман итоъат кунед,'),
(5423, 71, 4, 'то гуноҳонатонро бибахшояд ва шуморо то муддате муъайян мӯҳлат диҳад, зеро он муддати худо вақте ояд, — агар бидонед, — дигар ба дер наяфтад».'),
(5424, 71, 5, 'Гуфт: «Эй Парвардигори ман, ман қавми худро шабу рӯз даъват кардам'),
(5425, 71, 6, 'ва датъвати ман ҷуз ба фирорашон наяфзуд.'),
(5426, 71, 7, 'Ва ман, ҳар бор, ки даъваташон кардам, то Ту онҳоро бибахшоӣ, ангуштҳо дар гӯшҳои худ карданд ва ҷома дар сар кашиданд ва пой фишурданд ва ҳар чӣ бештар саркашӣ карданд.'),
(5427, 71, 8, 'Пас онҳоро ба овози баланд даъват кардам.'),
(5428, 71, 9, 'Боз ба ошкор гуфтам ва дар ниҳон гуфтам.'),
(5429, 71, 10, 'Сипас гуфтам: «Аз Парвардигоратон бахшоиш бихоҳед, ки Ӯ бахшоянда аст,'),
(5430, 71, 11, 'то аз осмон бароятон пай дар пай борон фиристад'),
(5431, 71, 12, 'ва шуморо ба\'молҳову фарзандон мадад кунад ва бароятон бӯстонҳову наҳрҳо биёфаринад.'),
(5432, 71, 13, 'Шуморо чӣ мешавад, ки аз шукӯҳи Худованд наметарсед,'),
(5433, 71, 14, 'ҳол он ки шуморо ба гунаҳои мухталиф биёфарид.'),
(5434, 71, 15, 'Оё намебинед, ки чӣ гуна Худо ҳафт осмони табақа-табақаро биёфарид?'),
(5435, 71, 16, 'Ва моҳро равшании онҳо ва хуршедро чароғашон гардонид.'),
(5436, 71, 17, 'Ва Худо шуморо чун наботе аз замин бирӯёнид,'),
(5437, 71, 18, 'Боз шуморо ба он бозмегардонад ва боз аз он берун меоварад,'),
(5438, 71, 19, 'Ва Худо заминро чун фарше бароятон бигустурд,'),
(5439, 71, 20, 'то бар роҳҳои паҳновари он биравед».'),
(5440, 71, 21, 'Нӯҳ гуфт: «Эй Парвардигори ман, онҳо фармони ман набурданд ва ба касе пайравӣ карданд, ки молу фарзандаш ҷуз ба зиёнаш наяфзуд.'),
(5441, 71, 22, 'Макр карданд — макре бузург.'),
(5442, 71, 23, 'Ва гуфтанд: «Худоёнатонро тарк накунед:, Вадд ва Сувоъ ва Яғус ва Яъуқ ва Насрро* тарк накунед!»'),
(5443, 71, 24, 'Бисёреро гумроҳ карданд ва ту ҷуз бар гумроҳии ситамкорон наяфзой!»'),
(5444, 71, 25, 'Ба ҷазои гуноҳонашон ғарк шуданд ва ба оташ рафтанд ва ҷуз Худо барои худ ёваре наёфтанд.'),
(5445, 71, 26, 'Ва Нӯҳ гуфт: «Эй Парвардигори ман, бар рӯи замин ҳеҷ як аз кофиронро магузор,'),
(5446, 71, 27, 'ки агар бигузорияшон, бандагонро гупроҳ мекунанд ва ҷуз фарзандоне фоҷиру кофир наёваранд.'),
(5447, 71, 28, 'Эй Парвардигори ман, марову падару модарамро ва ҳар киро бо имон ба хонаи ман ворид шавад ва низ мардони мӯъмину занони мӯъминро бибахшо ва ситамкоронро ҷуз ба ҳалокаташон маяфзой!»');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Бигӯ: «Ба ман ваҳй шудааст, ки гурӯҳе аз ҷин гӯш андохтанду гуфтанд,- ки мо Қуръоне аҷиб шунидем.'),
(5449, 72, 2, 'ба роҳи рост ҳидоят мекунад. Пас мо ба он имон овардем ва ҳаргиз касеро шарики Парвардигорамон намесозем.'),
(5450, 72, 3, 'Азамати Парвардигори мо олист. На ҳамсаре гирад, ва на фарзанде дорад.'),
(5451, 72, 4, 'Ва бехиради мо дар бораи Худо суханоне ба ноҳақ мегуфт.'),
(5452, 72, 5, 'Ва мо мепиндоштем, ки одамиву ҷин дар бораи Худо дурӯғ намегӯянд.'),
(5453, 72, 6, 'Ва низ мардоне буданд аз одамиён, ки ба мардоне аз ҷин паноҳ мебурданд ва бар саркашиашон меафзуданд.'),
(5454, 72, 7, 'Ҳамчунон ки шумо мепиндоштед, онҳо ҳам мепиндоштанд, ки Худо ҳеҷ касро дубора зинда намесозад.'),
(5455, 72, 8, 'Мо ба осмон расидем ва онро пур аз нигаҳбонони қудратманду шиҳобҳо (чароғ, оташак) ёфтем.'),
(5456, 72, 9, 'Мо дар он ҷойҳо, ки метавон гӯш андохт, менишастем. Аммо ҳар кӣ акнун (ба) гӯш нишинад, шиҳоберо (оташпораеро) дар камини худ ёбад.'),
(5457, 72, 10, 'Аз ин рӯй намедонем, ки оё барои касоне, ки дар рӯи замин ҳастанд, бадӣ тақдир мешавад ё Парвардигорашон мехоҳад ба роҳи росташон бибарад.'),
(5458, 72, 11, 'Баъзе аз мо солеҳанд ва баъзе ҷуз он ва мо фирқаҳое гуногунем.'),
(5459, 72, 12, 'Мо медонем, ки Худоро дар замин нотавон намекунем ва бо гурехтан\' низ нотавонаш насозем.'),
(5460, 72, 13, 'Ва чун сухани ҳидоят шунидем, ба он имон овардем. Ва ҳар кӣ ба Парвардигораш имон оварад, на аз нуқсон метарсад ва на аз зулм.'),
(5461, 72, 14, 'Ва баъзе аз мо мусалмонанд ва баъзе аз ҳак, дур. Ва онон, ки ислом овардаанд, дар ҷустуҷӯи роҳи рост будаанд.'),
(5462, 72, 15, 'Аммо онон, ки аз ҳақ дуранд, ҳезуми ҷаҳаннам хоҳанд буд».'),
(5463, 72, 16, 'Ва агар бар тариқаи рост пойдорӣ кунанд аз обе фаровон ба онҳо менушонем,'),
(5464, 72, 17, 'то онҳоро биёзмоем. Ва ҳар кӣ аз зикри Парвардигораш рӯй гардонад, ӯро ба азобе душвор меандозад,'),
(5465, 72, 18, 'Ва масҷидҳо аз ояи Худост. Ва бо вуҷуди Худои якто касе дигарро ба худоӣ махонед.'),
(5466, 72, 19, 'Чун бандаи Худо барои парастиши Ӯ бар пой истод, гирли ӯро гирифтанд.'),
(5467, 72, 20, 'Бигӯ: «Танҳо Парвардигорамро ибодат мекунам ва ҳеҷ касро шарики Ӯ намесозам».'),
(5468, 72, 21, 'Бигӯ: «Ман наметавонам ба шумо зиёне бирасонам ё шуморо ба салоҳ оварам».'),
(5469, 72, 22, 'Бигӯ: «Ҳеҷ кас маро аз уқубати Худо паноҳ надиҳад ва ман ғайри Ӯ паноҳгоҳе намеёбам,'),
(5470, 72, 23, 'он чӣ метавонам фақат расондани паёми Ӯ ва анҷом додани рисолатҳои Ӯ аст. Ва ҳар кӣ Худову паёмбарашро нофармонӣ кунад, насиби ӯ оташи ҷаҳаннам аст, ки ҳамеша дар он хоҳад буд,'),
(5471, 72, 24, 'то он гоҳ ки он чиро ба онҳо ваъда дода буданд, бингаранд. Пас хоҳанд донист, чӣ касеро ёрони нотавонтар ва шумори камтар будааст».'),
(5472, 72, 25, 'Бигӯ: «Ман намедонам, ки оё он ваъдае, ки ба шумо додаанд, наздик аст ё Парвардигори ман барои он замоне нисҳодааст?'),
(5473, 72, 26, 'Ӯ донои ғайб аст ва ғайби худро бар ҳеҷ кас ошкор намесозад,'),
(5474, 72, 27, 'магар бар он паёмбаре, ки аз ӯ хушнуд бошад, зеро ки барои нигаҳбонӣ аз ӯ пеши рӯй ва пушти сараш нигаҳбоне қарор медиҳад,'),
(5475, 72, 28, 'то бидонад, ки онҳо паёмҳои Парвардигорашонро расонидаанд ва Худо ба он чӣ дар назди онҳост, иҳота дорад (бо илми худ) ва ҳама чизро ба адад шумор кардааст».');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Эй ҷома бар худ печида,'),
(5477, 73, 2, 'Шабро зинда бидор, магар андакеро,'),
(5478, 73, 3, 'нимае аз онро ё андаке аз нима кам кун,'),
(5479, 73, 4, 'ё андаке бар нима бияфзой ва Қуръонро оҳиставу равшан бихон.'),
(5480, 73, 5, 'Мо ба ту сухане душворро илқо (андохтан, омӯхтан) хоҳем кард.'),
(5481, 73, 6, 'Албатта шабҳангом аз бистар бархостан мувофиқати забону дилро афзояндатар аст ва баёни суханро устувор дорандатар'),
(5482, 73, 7, 'ки корҳои ту дар рӯз бисёр аст.'),
(5483, 73, 8, 'Ва номи Парвардигоратро ёд кун ва аз ҳама бибур ва ба Ӯ бипайванд:'),
(5484, 73, 9, 'Парвардигори машриқу мағриб. Ҳеҷ худое ғайри Ӯ нест. Ӯро корсози худ интихоб кун!'),
(5485, 73, 10, 'Ва бар он чӣ мегӯянд, сабр кун ва ба ваҷҳе писандида аз онҳо дурӣ ҷӯй.'),
(5486, 73, 11, 'Дурӯғбарорандагони соҳибнеъматро ба Ман бигузор. Ва андаке мӯҳлаташон деҳ.'),
(5487, 73, 12, 'Назди Мост бандҳои вазнин ва дӯзах.'),
(5488, 73, 13, 'ва таъоме гулӯгиру азобе дардовар,'),
(5489, 73, 14, 'рӯзе, ки замину кӯҳҳо ба ларза оянд ва кӯҳҳо теппае аз реги равон гарданд!'),
(5490, 73, 15, 'Мо бар шумо паёмбаре фиристодем, ки бар амалҳоятон гувоҳ аст, ҳамон тавр, ки ба Фиръавн ҳам паёмбаре фиристодем.'),
(5491, 73, 16, 'Ва Фиръавн ба паёмбар фармон набурд. Мо низ ӯро ба сахтӣ фурӯ гирифтем.'),
(5492, 73, 17, 'Агар кофир бошед, дар рӯзе, ки кӯдаконро пир мегардонад, чӣ гуна дар амон монед?'),
(5493, 73, 18, 'Дар он рӯз осмон бишикофад ва ваъдаи Худо ба вуқӯъ пайвандад.'),
(5494, 73, 19, 'Ин пандест. Пас ҳар кӣ бихоҳад, роҳе ба сӯи Парвардигораш оғоз кунад.'),
(5495, 73, 20, 'Парвардигори ту медонад, ки ту ва гурӯҳе аз онон, ки бо ту ҳастанд, наздик ба ду сеяки шабу нимшаб ва сеяки шабро ба намоз меистед. Ва Худост, ки андозаи шабу рӯзро муъайян мекунад. Ва медонад, ки шумо ҳаргиз ҳисоби онро натавонед дошт. Пас тавбаи шуморо қабул кард. Ва ҳар чӣ муяссар шавад, аз Қуръон бихонед. Медонад чӣ касоне аз шумо бемор хоҳанд шуд ва гурӯҳе дигар ба талаби рӯзии Худо ба сафар мераванд ва гурӯҳи дигар дар роҳи Худо ба ҷанг мераванд. Пас ҳар чӣ муяссар шавад, аз он бихонед. Намоз бигузореду закот бидиҳед ва ба Худо қарзи некӯ диҳед. Ва ҳар хайреро, ки барои худ пешопеш бифиристед, онро назди Худо хоҳед ёфт. Ва он мукофоти беҳтар аст ва музде бузургтар аст. Ва аз Худо бахшоиш бихоҳед, зеро Худо бахшояндаву меҳрубон аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Эй ҷома дар сар кашида,'),
(5497, 74, 2, 'бархезу бим деҳ!'),
(5498, 74, 3, 'Ва Парвардигоратро такбир гӯй!'),
(5499, 74, 4, 'Ва ҷомаатро покиза дор!'),
(5500, 74, 5, 'Ва аз палидӣ дурӣ ҷӯй!'),
(5501, 74, 6, 'Ва чизе мадеҳ ки беш аз он умед дошта бошӣ.'),
(5502, 74, 7, 'Барои Парвардигорат сабркунанда бош!'),
(5503, 74, 8, 'Ва он гоҳ ки дар сур дамида шавад,'),
(5504, 74, 9, 'он рӯз рӯзе сахт хоҳад буд,'),
(5505, 74, 10, 'Ва барои кофирон душвор.'),
(5506, 74, 11, 'Маро бо он ки танҳояш офаридаам, вогузор.'),
(5507, 74, 12, 'Ӯро моле бисёр додам.'),
(5508, 74, 13, 'Ва писароне ҳама дар назди ӯ ҳозир'),
(5509, 74, 14, 'ва корҳои ӯро ба некӯтар тарзе вусъат додам.'),
(5510, 74, 15, 'Он гоҳ тамаъ мебандад, ки зиёдат кунам.'),
(5511, 74, 16, 'Не, не! Ӯ дар баробари оёти Мо ситеза ҷӯст.'),
(5512, 74, 17, 'Ӯро ба машаққате меандозам.'),
(5513, 74, 18, 'Ӯ андешид ва тарҳе афканд (нақшае кашид).'),
(5514, 74, 19, 'Марг бар ӯ бод, чӣ гуна тарҳе афканд?'),
(5515, 74, 20, 'Боз ҳам марг бар ӯ бод, чӣ гуна тарҳе афканд?'),
(5516, 74, 21, 'Он гоҳ нигарист.'),
(5517, 74, 22, 'Сипас рӯй турш кард ва пешонӣ дар ҳам кашид.'),
(5518, 74, 23, 'Сипас рӯй гардониду гарданкашӣ кард,'),
(5519, 74, 24, 'гуфт: «Ин ғайри ҷодуе, ки дигаронаш омӯхтанд, ҳеҷ нест.'),
(5520, 74, 25, 'Ин ғайри сухани одамӣ ҳеҷ нест».'),
(5521, 74, 26, 'Ба зудӣ ӯро ба сақар бияфканем.'),
(5522, 74, 27, 'Чӣ чиз огоҳат сохт, ки сақар чист?'),
(5523, 74, 28, 'На ҳеҷ боқӣ мегузорад ва на чизеро тарк кунад.'),
(5524, 74, 29, 'Сӯзонандаи пӯст аст.'),
(5525, 74, 30, 'Нуздаҳ фаришта бар он вазифадоранд.'),
(5526, 74, 31, 'Вазифадорони дузахро фақат аз фариштагон қарор додем. Ва шумори онҳо фақат барои имтиҳони кофирон аст. То аҳли китоб яқин кунанд ва бар имони мӯъминон бияфзояд ва аҳли китобу мӯъминон шак накунанд. Ва то онон, ки дар дилҳояшон маразест, нагӯянд: «Худо аз ин мисол чӣ мехостааст?» Худо инчунин ҳар касро, ки бихоҳад, гумроҳ мекунад ва ҳар касро, ки бихоҳад, роҳ менамояд. Ва шумори лашкари Парвардигоратро ғайри Ӯ надонад. Ва ин сухан фақат панде аз барои мардум аст.'),
(5527, 74, 32, 'Оре, савганд ба моҳ!'),
(5528, 74, 33, 'Ва савганд ба шаб, чун рӯй ба рафтан орад'),
(5529, 74, 34, 'ва савганд ба субҳ чун парда барафканад,'),
(5530, 74, 35, 'ки ин яке аз ҳодисаҳои бузург аст.'),
(5531, 74, 36, 'Тарсонандаи одамиён аст.'),
(5532, 74, 37, 'Барои ҳар кас аз шумо, ки хоҳад, пеш ояд (бо амали нек) ё аз пай равад (бо куфру нофармонӣ).'),
(5533, 74, 38, 'Ҳар кас гаравгони корест, ки кардааст,'),
(5534, 74, 39, 'ғайри аҳли саъодат,'),
(5535, 74, 40, 'ки дар биҳиштҳо нишастаанд ва мепурсанд'),
(5536, 74, 41, 'аз гуноҳкорон,'),
(5537, 74, 42, 'ки чӣ чиз шуморо ба ҷаҳаннам кашонид?,'),
(5538, 74, 43, 'Мегӯянд: «Мо аз намозгузорон набудем'),
(5539, 74, 44, 'ва ба дарвешон таъом намедодем'),
(5540, 74, 45, 'ва бо онон, ки сухани ботил мегуфтанд, ҳамовоз мешудем'),
(5541, 74, 46, 'ва рӯзи қиёматро дурӯғ мешуморидем,'),
(5542, 74, 47, 'то марги мо фаро расид».'),
(5543, 74, 48, 'Пас шафоъати шафоъаткунандагон фоидаашон набахшад.'),
(5544, 74, 49, 'Чӣ шудааст, ки аз ин панд рӯй мегардонанд?'),
(5545, 74, 50, 'Монанди харони рамида,'),
(5546, 74, 51, 'ки аз шер мегурезанд,'),
(5547, 74, 52, 'балки ҳар як аз онҳо мехоҳад, ки номаҳое ку шода ба ӯ дода шавад.'),
(5548, 74, 53, 'Оре, ки аз охират наметарсанд.'),
(5549, 74, 54, 'Оре, ки ин Қуръон пандест,'),
(5550, 74, 55, 'ҳар ки хоҳад онро бихонад.'),
(5551, 74, 56, 'Ва панд намегиранд, бе он ки Худо хоҳад. Ӯ шоёни он аст, ки аз Ӯ битарсанд. Ва ӯ шоёни бахшоидан аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Қасам мехӯрам ба рӯзи қиёмат'),
(5553, 75, 2, 'ва қасам мехӯрам ба нафси маломатгар!'),
(5554, 75, 3, 'Оё одамӣ мепиндорад, ки Мо устухонҳояшро гирд нахоҳем овард?'),
(5555, 75, 4, 'Оре, мо қодир ҳастем, ки сарангуштҳояшро баробар кунем.'),
(5556, 75, 5, 'Балки одамӣ мехоҳад, ки дар оянда низ ба корҳои ношоиста пардозад.'),
(5557, 75, 6, 'Мепурсад: «Рӯзи қиёмат чӣ вақт хоҳад буд?»'),
(5558, 75, 7, 'Рӯзе, ки чашмҳо хира шавад'),
(5559, 75, 8, 'ва моҳ тира шавад'),
(5560, 75, 9, 'ва офтобу моҳ дар як ҷой ҷамъ оянд.'),
(5561, 75, 10, 'Инсон дар он рӯз мегӯяд: «Роҳи гурез куҷост?»'),
(5562, 75, 11, 'Ҳаргиз паноҳгоҳе нест!'),
(5563, 75, 12, 'Қароргоҳи ҳама дар ин рӯз назди Парвардигори туст!'),
(5564, 75, 13, 'Дар ин рӯз одамиро аз ҳар чӣ пешопеш фиристода ва баъд аз худ гузоштааст, хабар медиҳанд.'),
(5565, 75, 14, 'Балки одамӣ худро нек мешиносад,'),
(5566, 75, 15, 'ҳарчанд ба забон узрҳо оварад.'),
(5567, 75, 16, 'Ба шитоб забон ба хондани Қуръон наҷунбон,'),
(5568, 75, 17, 'ки ҷамъ овардану хонданаш бар ӯҳдаи Мост,'),
(5569, 75, 18, 'Чун хондемаш, ту он хонданро пайравӣ кун,'),
(5570, 75, 19, 'Сипас баёни он бар ӯҳдаи Мост.'),
(5571, 75, 20, 'Оре, шумо ин ҷаҳони зудгузарро дӯст медоред'),
(5572, 75, 21, 'ва охиратро тарк мекунед.'),
(5573, 75, 22, 'Дар он рӯз рӯйҳое ҳаст зебову дурахшон,'),
(5574, 75, 23, 'ки сӯи Парвардигорашон назар мекунанд'),
(5575, 75, 24, 'ва рӯйҳое ҳаст гирифтаву турш,'),
(5576, 75, 25, 'ки медонад он азоби камаршикан бар ӯ фуруд ояд.'),
(5577, 75, 26, 'Оре, чун ҷон ба гулӯ расад'),
(5578, 75, 27, 'ва гуфта шавад, ки чӣ касест, ки афсун бихонад?'),
(5579, 75, 28, 'Ва яқин кунад, ки замони ҷудоӣ фаро расида'),
(5580, 75, 29, 'ва соқҳои по дар ҳам печида шаванд,'),
(5581, 75, 30, 'он рӯз рӯзи ронданаш ба сӯи Парвардигори туст.'),
(5582, 75, 31, 'На тасдиқ кардааст ва на намоз гузоридааст.'),
(5583, 75, 32, 'Аммо дурӯғ бароварда ва рӯй гардондааст.'),
(5584, 75, 33, 'Он гоҳ хиромон назди касонаш рафтааст.'),
(5585, 75, 34, 'Вой бар ту, пас вой бар ту!'),
(5586, 75, 35, 'Боз ҳам вой бар ту, пас вой бар ту!'),
(5587, 75, 36, 'Оё инсон мепиндорад, ки ӯро ба ҳоли худ, вогузоштаанд?'),
(5588, 75, 37, 'Оё ӯ нутфае аз манӣ, ки дар раҳме рехта шуда, набудааст?'),
(5589, 75, 38, 'Сипас лахтае хун? Он гоҳ ба андоми дурусташ биёфарид.'),
(5590, 75, 39, 'Ва онҳоро ду синф кард: нару мода.'),
(5591, 75, 40, 'Оё Худованд қодир нест, ки мурдагонро зинда созад?');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Дар ҳақиқат бар инсон муддате аз замон гузашт ва ӯ чизе зикршуда набуд.'),
(5593, 76, 2, 'Мо одамиро аз нутфае омехта биёфаридем, то ӯро имтиҳон кунем. Ва шунавову бинояш сохтаем.'),
(5594, 76, 3, 'Роҳро ба ӯ нишон додаем. Ё шукргузор бошад ё ношукр.'),
(5595, 76, 4, 'Мо барои кофирон занҷирҳову ғулҳо ва оташи афрӯхта омода кардаем.'),
(5596, 76, 5, 'Некон аз ҷомҳое менӯшанд, ки омехта ба кофур аст,'),
(5597, 76, 6, 'чашмае, ки бандагони Худо аз он менушанд ва онро ба ҳар ҷое, ки мехоҳанд, равон месозанд.'),
(5598, 76, 7, 'Ба назр вафо мекунанд ва аз рӯзе, ки шарри он ҳама ҷоро гирифтааст, метарсанд.'),
(5599, 76, 8, 'Ва таъомро дар ҳоле, худ дӯсташ доранд, ба мискину ятим ва асир мехӯронанд,'),
(5600, 76, 9, 'шуморо фақат барои Худо таъом медиҳем ва аз шумо на музде мехоҳем на шукре.'),
(5601, 76, 10, 'Мо аз Парвардигори худ метарсем, дар рӯзе, ки он рӯз рӯйҳо турш мешавад ва тарснок аст.'),
(5602, 76, 11, 'Худо онҳоро аз бадии он рӯз нигаҳ дошт ва ононро таровату шодмонӣ бахшид,'),
(5603, 76, 12, 'Ба мукофоти сабре, ки кардаанд, биҳишту ҳарир дод.'),
(5604, 76, 13, 'Дар он ҷо бар тахтҳо такя задаанд на ҳеҷ офтобе мебинанд ва на хунукии сахте.'),
(5605, 76, 14, 'Сояҳои дарахтонаш бар сарашон афтода ва меваҳояш ба фармонашон бошад.'),
(5606, 76, 15, 'Косаҳои нуқрагину кӯзаҳои шароб миёнашон ба гардиш дармеояд.'),
(5607, 76, 16, 'Кӯзаҳое аз нуқра, ки онҳоро ба андоза пур кардаанд.'),
(5608, 76, 17, 'Дар он ҷо ҷоме бинӯшонандашон, ки омехта бо занҷабил бошад,'),
(5609, 76, 18, 'аз чашмае, ки онро салсабил мегӯянд.'),
(5610, 76, 19, 'Ва писароне ба гирдашон мечарханд, ки чун онҳоро бубинӣ, пиндорӣ марвориди парокандаанд.'),
(5611, 76, 20, 'Чун бингарӣ, ҳар чи бингари неъмат фаровон аст ва фармонравоии бузург.'),
(5612, 76, 21, 'Бар танашон ҷомаҳоест аз сундуси сабз ва истабрақ. Ва ба дастбандҳое аз нуқра зинат шудаанд ва онҳоро Парвардигорашон шароби пок бинӯшонад.'),
(5613, 76, 22, 'Ин мукофоти шумост ва аз кӯшишатон шукргузорӣ шудааст.'),
(5614, 76, 23, 'Мо Қуръонро бар ту нозил кардем, нозил кардане некӯ!'),
(5615, 76, 24, 'Дар баробари фармони Парвардигорат собир бош ва аз ҳеҷ гуноҳкор ё носипосе итоъат макун!'),
(5616, 76, 25, 'Ва бомдодну шамгоҳон номи Парвардигоратро бар забон биёвар.'),
(5617, 76, 26, 'Дар қисме аз шаб саҷдааш кун ва дар ҳамаи дарозии шаб тасбеҳаш гӯй!'),
(5618, 76, 27, 'Инҳо ин дунёи зудгузарро дӯст доранд ва он рӯзи душворро паси пушт мепартоянд.'),
(5619, 76, 28, 'Мо одамиёнро офаридем ва аъзову пайвандҳояшонро нерӯманд гардонидем ва агар хоҳем, ба ҷои онҳо қавме монандашон биёварем.'),
(5620, 76, 29, 'Ин пандест. Пас ҳар кӣ хоҳад роҳе ба сӯи Парвардигори худ интихоб кунад.'),
(5621, 76, 30, 'Ва шумо ҷуз он намехоҳед, ки Худо хоста бошад зеро Худо донову ҳаким аст!'),
(5622, 76, 31, 'Ҳар касро, ки бихоҳад, шомили раҳмати худ созад ва барои ситамкорон азобе дардовар омода кардааст.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Савганд ба бодҳое, ки аз паи ҳам меоянд'),
(5624, 77, 2, 'ва савганд ба бодҳои сахти вазанда'),
(5625, 77, 3, 'ва савганд ба бодҳои бороновар'),
(5626, 77, 4, 'ва савганд ба онҳо, ки ҳақро аз ботил ҷудо мекунанд'),
(5627, 77, 5, 'ва савганд ба онҳо, ки ваҳйи Худоро мефароранд,'),
(5628, 77, 6, 'дафъ кардани узрро ё бим карданро,'),
(5629, 77, 7, 'ки он чӣ ба шумо ваъда дода шавад, воқеъ хоҳад шуд!'),
(5630, 77, 8, 'Пас он гоҳ ки ситорагон хомуш шаванд'),
(5631, 77, 9, 'ва он гоҳ ки осмон бишикофад,'),
(5632, 77, 10, 'ва он гоҳ ки кӯҳҳо пора-пора гарданд'),
(5633, 77, 11, 'ва он гоҳ ки паёмбаронро вақте муъайян шавад.'),
(5634, 77, 12, 'Барои чӣ рӯзе муъайян шавад?'),
(5635, 77, 13, 'Барои рӯзи фасл.'),
(5636, 77, 14, 'Ва чӣ донӣ, ки рӯзи фасл чист?'),
(5637, 77, 15, 'Дар он рӯз вой бар такзибкунандагон (дурӯғбарорандагон)!'),
(5638, 77, 16, 'Оё аввалиёнро ҳалок накардаем'),
(5639, 77, 17, 'ва охириёнро низ аз паи онҳо намебарем?'),
(5640, 77, 18, 'Бо гунаҳкорон чунин рафтор мекунем.'),
(5641, 77, 19, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5642, 77, 20, 'Оё шуморо аз обе пасту бемиқдор наёфаридаем?'),
(5643, 77, 21, 'Ва онро дар ҷойгоҳе устувор қарор надодем,'),
(5644, 77, 22, 'то замоне муъайян?'),
(5645, 77, 23, 'Пас Мо тавоно будем ва нек тавоно будем!'),
(5646, 77, 24, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5647, 77, 25, 'Оё заминро ҷойгоҳе насохтем'),
(5648, 77, 26, 'барои зиндагону мурдагон?'),
(5649, 77, 27, 'Дар он кӯҳҳои баланд падид овардем ва аз обе гуворо серобатон сохтем.'),
(5650, 77, 28, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5651, 77, 29, 'Ба сӯи он чӣ дурӯғаш мешумурдед, ба роҳ дароед.'),
(5652, 77, 30, 'Ба сӯи он дуди сешуъба (се қисм) ба роҳ афтед,'),
(5653, 77, 31, 'ки на аз осебатон нигаҳ дорад ва на дафъи од шӯъла кунад,'),
(5654, 77, 32, 'Ва ҷаҳаннам шарораҳое мепартояд ҳар як ба баландии қасре азим,'),
(5655, 77, 33, 'гӯӣ, ки уштуроне зард бошанд.'),
(5656, 77, 34, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5657, 77, 35, 'Ин рӯзест, ки кас сухан нагӯяд.'),
(5658, 77, 36, 'Онҳоро рухсат надиҳанд, то узр хоҳанд.'),
(5659, 77, 37, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5660, 77, 38, 'Ин рӯз рӯзи фасл аст, ки шумо ва пешиниёнатонро гирд меоварем.'),
(5661, 77, 39, 'Пас агар ҳилае доред, ба кор баред.'),
(5662, 77, 40, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5663, 77, 41, 'Парҳезгорон дар сояҳову канори чашмасоронанд,'),
(5664, 77, 42, 'бо ҳар мевае, ки орзу кунанд,'),
(5665, 77, 43, 'Ба мукофоти корҳое, ки мекардаед, бихӯреду биёшомед, шуморо гуворо бод,'),
(5666, 77, 44, 'Мо некӯкоронро инчунин мукофот медиҳем!'),
(5667, 77, 45, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5668, 77, 46, 'Бихӯреду андак баҳрае баргиред (дар дунё), ки шумо гунаҳкоронед,'),
(5669, 77, 47, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5670, 77, 48, 'Ва чун гӯяндашон, ки руқӯъ кунед, руқӯъ намекунанд (намоз намехонанд).'),
(5671, 77, 49, 'Дар он рӯз вой бар дурӯғбарорандагон!'),
(5672, 77, 50, 'Пас аз Қуръон ба кадом сухан имон меоваранд?');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Аз чӣ чиз (ҳамдигарро) мепурсанд?'),
(5674, 78, 2, 'Аз он хабари бузург, (қиёмат).'),
(5675, 78, 3, 'ки дар он ихтилоф мекунанд.'),
(5676, 78, 4, 'Оре, ба зудӣ хоҳад донист.'),
(5677, 78, 5, 'Боз ҳам, оре, ба зудӣ хоҳанд донист!'),
(5678, 78, 6, 'Оё Мо заминрро бистаре насохтем?'),
(5679, 78, 7, 'Ва кӯҳҳоро мехҳое?'),
(5680, 78, 8, 'Ва шуморо ҷуфт-ҷуфт офаридем.'),
(5681, 78, 9, 'Ва хобатонро оссишатон гардонидем.'),
(5682, 78, 10, 'Ва шабро пӯшишатон қарор додем.'),
(5683, 78, 11, 'Ва рӯзро вақти талаби маъишат. (касб).'),
(5684, 78, 12, 'Ва бар болои саратон ҳафт осмони устувор бино кардем.'),
(5685, 78, 13, 'Ва чароғе равшан офаридем. (офтоб).'),
(5686, 78, 14, 'Аз абрҳои боронзой обе фаровон нозил кардем,'),
(5687, 78, 15, 'то бо он донаву набот бирӯёнем'),
(5688, 78, 16, 'ва бӯстонҳои анбӯҳ (сердарахт).'),
(5689, 78, 17, 'Албатта рӯзи доварӣ рӯзест муъайян.'),
(5690, 78, 18, 'Рӯзе, ки дар сур дамида шавад, ва шумо гурӯҳгурӯҳ биёед.'),
(5691, 78, 19, 'Осмон шикофта шавад ва ҳар шикоф даре бошад.'),
(5692, 78, 20, 'Ва куҳҳо равон шаванд ва сароб гарданд.'),
(5693, 78, 21, 'Ҷаҳаннам дар интизор бошад.'),
(5694, 78, 22, 'Саркашонро бозгаштангоҳест.'),
(5695, 78, 23, 'Замоне дароз дар он ҷо бошанд.'),
(5696, 78, 24, 'На хунукӣ чашанду на об,'),
(5697, 78, 25, 'фақат оби ҷӯшону хуну чирк. (нӯшанд).'),
(5698, 78, 26, 'Ин ҷазоест мувофиқи кирдор.'),
(5699, 78, 27, 'Зеро онон ба рӯзи ҳисоб умед надоштанд.'),
(5700, 78, 28, 'Ва оёти Моро ба сахтӣ дурӯғ мебароварданд.'),
(5701, 78, 29, 'Ва Мо ҳама чизро дар, китобе шумора кардаем.'),
(5702, 78, 30, 'Пас бичашед, ки фақат бар азоби шумо хоҳем афзуд!'),
(5703, 78, 31, 'Парҳезгоронро ҷоест дар амон аз ҳар осеб,'),
(5704, 78, 32, 'бӯстонҳову токистонҳо'),
(5705, 78, 33, 'ва духтароне ҳамсол бо пистонҳои баромада'),
(5706, 78, 34, 'ва ҷомҳои пур.'),
(5707, 78, 35, 'На сухани беҳуда шунаванд ва на дурӯғ'),
(5708, 78, 36, 'ва ин мукофотест кофӣ аз ҷониби Парвардигорат—'),
(5709, 78, 37, 'Парвардигори осмонҳову замин ва он чӣ миёни онҳост. Он Худои раҳмон, ки касро ба Ӯ ёрои хитоб набошад,'),
(5710, 78, 38, 'рӯзе, ки рӯҳ ва фариштагон ба саф меистанд ва кас сухан намегӯяд, ғайри он ки Худои раҳмон ба ӯ рухсат диҳад, ва ӯ сухан ба савоб гӯяд.'),
(5711, 78, 39, 'Он рӯз рӯзест омаданӣ. Пас ҳар кӣ хоҳад ба сӯи Парвардигораш бозгардад.'),
(5712, 78, 40, 'Мо шуморо аз азобе наздик метарсонем: рӯзе, ки одамӣ ҳар чиро пешопеш фиристодааст, менигарад ва кофир мегӯяд: «Эй кош, ман хок мебудам!»');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Савганд ба фариштагоне, ки ҷонҳоро (ҷонҳои кофиронро) ба сахтӣ мегиранд'),
(5714, 79, 2, 'ва савганд ба фариштагоне, ки чонҳоро (ҷонҳои муъминонро) ба осонӣ мегиранд'),
(5715, 79, 3, 'ва савганд ба фариштагоне, ки шиноваранд'),
(5716, 79, 4, 'ва савганд, ба фариштагоне, ки бар якдигар пешӣ мегиранд (дар боло бурдани ҷонҳои мӯъминон)'),
(5717, 79, 5, 'ва савганд ба онҳо, ки тадбири корҳо мекунанд,'),
(5718, 79, 6, 'ки он рӯз, ки нахустин нафхаи қиёмат (дамидани дар сур) заминро биларзонад'),
(5719, 79, 7, 'ва нафхаи дуввум аз паси он биёяд,'),
(5720, 79, 8, 'дар он рӯз дилҳое дар ҳарос бошанд'),
(5721, 79, 9, 'ва нишони хушӯъ (хорӣ) дар дидагон намоён!'),
(5722, 79, 10, 'Мегӯянд: «Оё мо ба ҳолати нахустин бозмегардем,'),
(5723, 79, 11, 'он гоҳ ки устухонҳое пӯсида будем?»'),
(5724, 79, 12, 'Гӯянд: «Ин бозгашти мо бозгаштест зиёновар!'),
(5725, 79, 13, 'Фақат ин аст, ки танҳо як бонг бармеояд.'),
(5726, 79, 14, 'ва онҳо худро дар он саҳро хоҳанд ёфт.'),
(5727, 79, 15, 'Оё достони Мӯсо ба ту расидааст?'),
(5728, 79, 16, 'Он гоҳ ки Парвардигораш ӯро дар водии муқаддаси Туво нидо дод:'),
(5729, 79, 17, '«Ба сӯи Фиръавн бирав, ки аз ҳад гузаштааст.'),
(5730, 79, 18, 'Бигӯ: «Оё туро рағбате ҳаст, ки покиза шавӣ,'),
(5731, 79, 19, 'ва ман туро ба Парвардигорат роҳ бинамоям ва ту битарсӣ (аз Ӯ)?»'),
(5732, 79, 20, 'Пас он ояти бузургро ба ӯ нишон дод.'),
(5733, 79, 21, 'Ва ӯ дурӯғ бароварду нофармонӣ кард.'),
(5734, 79, 22, 'Сипас пушт гардонду мешитофт.'),
(5735, 79, 23, 'Пас ҳамаро ҷамъ кард ва нидо дод.'),
(5736, 79, 24, 'ва гуфт: «Ман Парвардигори бартари шумо ҳастам!»'),
(5737, 79, 25, 'Худояш ба аозби охирату дунё гирифтор кард.'),
(5738, 79, 26, 'Дар ин ибратест барои, касе, ки метарсад!'),
(5739, 79, 27, 'Оё шумо ба офариниш сахттаред, ё ин осмоне, ки Ӯ бино кардааст?'),
(5740, 79, 28, 'Сақфашро барафрошт ва дуруст кард,'),
(5741, 79, 29, 'шабашро торик кард ва рӯшноияшро ошкор сохт'),
(5742, 79, 30, 'ва пас аз он заминро бигустурд (паҳн кард)'),
(5743, 79, 31, 'ва аз он об берун овард ва чарогоҳҳо падид овард'),
(5744, 79, 32, 'ва кӯҳҳоро устувор гардонид'),
(5745, 79, 33, 'барои баҳрагирии шумо ва чорпоёнатон.'),
(5746, 79, 34, 'Чун он ҳодисаи бузург даррасад,'),
(5747, 79, 35, 'рӯзе, ки одамӣ ҳамаи амалҳои худро ба ёд оварад'),
(5748, 79, 36, 'ва ҷаҳаннамро ба ҳар кӣ мебинад нишон диҳанд,'),
(5749, 79, 37, 'Пас ҳар кй аз ҳад гузашта бошад (дар куфру исён)'),
(5750, 79, 38, 'ва зиндагии инҷаҳонро ихтиёр карда бошад,'),
(5751, 79, 39, 'ҷаҳаннам ҷойгоҳи ӯст.'),
(5752, 79, 40, 'Аммо ҳар кас, ки аз истодан дар баробари Парвардигораш тарсида ва нафсро аз ҳаво (ва ҳавас) боздошта бошад,'),
(5753, 79, 41, 'биҳишт ҷойгоҳи ӯст.'),
(5754, 79, 42, 'Туро аз қиёмат мепурсанд, ки чӣ вақт воқеъ шавад.'),
(5755, 79, 43, 'Аз ёд кардани (илми) он туро чӣ кор аст?'),
(5756, 79, 44, 'Интиҳои илми он назди Парвардигори туст.'),
(5757, 79, 45, 'Фақат ва фақат ту касонеро, ки аз он рӯз битарсанд, бим медиҳӣ.'),
(5758, 79, 46, 'Чун онро бубинанд, пиндоранд дар ин ҷаҳон фақат як шомгоҳ ё чоштгоҳ истодаанд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Рӯйро турш кард ва сар баргардонид,'),
(5760, 80, 2, 'чун он нобино ба наздаш омад,'),
(5761, 80, 3, 'Ва ту чӣ донӣ, шояд, ки ӯ покиза шавад,'),
(5762, 80, 4, 'ё панд гирад ва панди ту фоидааш кунад.'),
(5763, 80, 5, 'Аммо он ки ӯ тавонгар аст,'),
(5764, 80, 6, 'ту рӯи худ ба ӯ мекунӣ'),
(5765, 80, 7, 'ва агар ҳам пок нагардад, чизе бар зиёни ту нест.'),
(5766, 80, 8, 'Ва аммо он, ки шитобон ба назди ту меояд'),
(5767, 80, 9, 'ва метарсад, (аз худо).'),
(5768, 80, 10, 'ту аз ӯ ба дигаре рӯй, меорӣ.'),
(5769, 80, 11, 'Оре, ин Қуръон пандест,'),
(5770, 80, 12, 'пас ҳар кӣ хоҳад, аз он панд гирад.'),
(5771, 80, 13, 'Дар саҳифаҳое гиромӣ.'),
(5772, 80, 14, 'баландқадру покиза,'),
(5773, 80, 15, 'ба дасти котибоне,'),
(5774, 80, 16, 'бузургвору некӯкор.'),
(5775, 80, 17, 'Марг бар одамӣ бод, ки чӣ носипос (ношукр) аст!'),
(5776, 80, 18, 'Ӯро аз чӣ офаридааст?'),
(5777, 80, 19, 'Аз нутфае офарид ва ба андоза муайян кард,'),
(5778, 80, 20, 'сипас роҳашро осон сохт.'),
(5779, 80, 21, 'Он гоҳ бимирондаш ва дар гӯр кард.'),
(5780, 80, 22, 'Ва он гоҳ, ки хоҳад, зиндааш созад.'),
(5781, 80, 23, 'На ки ҳанӯз он чиро ба ӯ фармон дода буд, ба ҷой наёвардааст.'),
(5782, 80, 24, 'Пас одамӣ ба таъоми худ бингарад'),
(5783, 80, 25, 'Мо боронро фурӯ боридем, бориданӣ'),
(5784, 80, 26, 'ва заминро шикофтем, шикофтанӣ'),
(5785, 80, 27, 'ва дар он донаҳо рӯёнидем'),
(5786, 80, 28, 'ва току сабзавот'),
(5787, 80, 29, 'ва зайтуну нахл (дарахти хурмо)'),
(5788, 80, 30, 'ва боғҳои пурдарахт'),
(5789, 80, 31, 'ва меваву алаф,'),
(5790, 80, 32, 'то шумову чорпоёнатон баҳра баред,'),
(5791, 80, 33, 'Чун бонги қиёмат барояд,'),
(5792, 80, 34, 'рӯзе, ки одамӣ аз бародараш мегурезад'),
(5793, 80, 35, 'ва аз модарашу падараш'),
(5794, 80, 36, 'ва аз занашу фарзандонаш.'),
(5795, 80, 37, 'Ҳар касро дар рӯз корест, ки ба худ машғулаш дорад.'),
(5796, 80, 38, 'Чеҳраҳое дар он рӯз дурахшонанд,'),
(5797, 80, 39, 'хандонанду шодонанд.'),
(5798, 80, 40, 'Ва чеҳраҳое дар он рӯз ғуборолуданд,'),
(5799, 80, 41, 'дар сиёҳӣ фурӯ рафтаанд.'),
(5800, 80, 42, 'Инҳо кофирону фоҷиронанд (бадкорон).');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Чун хуршед бефурӯғ (хира) шавад'),
(5802, 81, 2, 'ва чун ситорагон фурӯ резанд'),
(5803, 81, 3, 'ва чун кӯҳҳо аз ҷои худ беҷо бишаванд'),
(5804, 81, 4, 'ва чун шутурони ҳомила ба ҳоли худ гузошта шаванд'),
(5805, 81, 5, 'ва чун ҷонварони ваҳшӣ ҷамъ оварда шаванд,'),
(5806, 81, 6, 'ва чун дарёҳо оташ гиранд'),
(5807, 81, 7, 'ва чун рӯҳҳо бо танҳо якҷоя гардганд!'),
(5808, 81, 8, 'ва чун аз духтари зинда ба гӯр шуда пурсида шавад, ки'),
(5809, 81, 9, 'ба чӣ гуноҳе кушта шудааст'),
(5810, 81, 10, 'ва чун саҳифаҳо боз шаванд,'),
(5811, 81, 11, 'ва чун осмон аз ҷои худ канда шавад\''),
(5812, 81, 12, 'ва чун ҷаҳаннам афрӯхта гардад'),
(5813, 81, 13, 'ва чун биҳиштро пеш оваранд,'),
(5814, 81, 14, 'ҳар кас бидонад, ки чӣ ҳозир овардааст.'),
(5815, 81, 15, 'Савганд ба ситорагони бозгарданда,'),
(5816, 81, 16, 'сайркунандаи ғойбшаванда'),
(5817, 81, 17, 'ва савганд ба шаб, чун торик шавад'),
(5818, 81, 18, 'ва савганд ба субҳ чун бидамад,'),
(5819, 81, 19, 'ки ин сухани расуле бузургвор (Ҷибрил (а)) аст,'),
(5820, 81, 20, 'нерӯманд, дар назди Худованди арш соҳибобрӯ'),
(5821, 81, 21, 'он ҷо (дар назди Худо) фармонравои боваринокаст'),
(5822, 81, 22, 'Ва ҳамсӯҳбати шумо девона нест.'),
(5823, 81, 23, 'Албатта Ӯро (Ҷибрилро) дар уфуқи равшан дидааст'),
(5824, 81, 24, 'Ва он чиро аз ғайб ба ӯ гӯянд, дареғ намедорад.'),
(5825, 81, 25, 'Ва он сухан сухани шайтони раҷим нест.'),
(5826, 81, 26, 'Пас ба куҷо меравед?'),
(5827, 81, 27, 'Ин китоб пандест барои ҷаҳониён,'),
(5828, 81, 28, 'барои ҳар кас аз шумо, ки бихоҳад ба роҳи рост афтад.'),
(5829, 81, 29, 'Ва шумо намехоҳед, ғайри он чиро, ки Парвардигори ҷаҳониён хоста бошад.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Он гоҳ, ки осмон бишикофад'),
(5831, 82, 2, 'ва он гоҳ ки ситоратон пароканда шаванд'),
(5832, 82, 3, 'ва он гоҳ, ки дарёҳо ба ҳам бипайванданд'),
(5833, 82, 4, 'ва он гоҳ, ки қабрҳо зеру забар (шикофта) гардаид,'),
(5834, 82, 5, 'ҳар кас медонад, чӣ чиз пешопеш фиристода ва чӣ чиз бар ҷой гузоштааст.'),
(5835, 82, 6, 'Эй одамӣ, чӣ чиз туро ба Парвардигори каримат мағрур кардааст? (фирефтааст).'),
(5836, 82, 7, 'Он кӣ туро биёфарид, ва аъзоят дуруст кард ва туро мӯътадил қомат кард.'),
(5837, 82, 8, 'Ва ба ҳар сурате, ки хост, аъзои туро ба ҳам андохт (таркиб дод).'),
(5838, 82, 9, 'Не, не шумо рӯзи ҷазоро дурӯғ меҳисобед.'),
(5839, 82, 10, 'Ҳол он ки бар шумо муҳофизоне (фариштагоне) вобаста шудаанд:'),
(5840, 82, 11, 'котибоне бузургвор'),
(5841, 82, 12, 'медонанд, ки шумо чӣ мекунед.'),
(5842, 82, 13, 'Албатта некӯкорон дар неъматанд'),
(5843, 82, 14, 'ва гуноҳкорон дар ҷаҳаннам!'),
(5844, 82, 15, 'Дар рӯзи шумор (қиёмат) ба ҷаҳаннам дароянд'),
(5845, 82, 16, 'ва аз он ғоиб нашаванд'),
(5846, 82, 17, 'Ту чӣ донӣ, ки рӯзи шумор чист?'),
(5847, 82, 18, 'Боз ҳам ту чӣ донӣ, ки рӯзи шумор чист?'),
(5848, 82, 19, 'Рӯзест, ки касе барои каси дигар ҳеҷ коре натаванад кард ва дар он рӯз фармон фармони Худо бошад.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Вой бар камфурӯшон,'),
(5850, 83, 2, 'онон, ки чун аз мардум кайл (паймона) меситонанд, онро пур мекунанд'),
(5851, 83, 3, 'ва чун барои мардум мепаймоянд ё бармекашанд, аз он кам мекунанд.'),
(5852, 83, 4, 'Оё инҳо намедонанд, ки зинда мешаванд'),
(5853, 83, 5, 'дар он рӯзи бузург?'),
(5854, 83, 6, 'Рӯзе, ки мардум ба пешгоҳи Парвардигори ҷаҳониён меистанд.'),
(5855, 83, 7, 'Ҳаққо, ки албатта аъмоли бадкорон навишташуда дар сиҷҷин аст.'),
(5856, 83, 8, 'Ту чӣ медонӣ, ки сиҷҷин чист?'),
(5857, 83, 9, 'Китобест навишта!'),
(5858, 83, 10, 'Дар он рӯз вой бар такзибкунандагон (дурӯғбарорандагон),'),
(5859, 83, 11, 'онон, ки рӯзи ҷазоро дурӯғ меҳисобанд!'),
(5860, 83, 12, 'Ва он рӯзро фақат аз ҳад гузарандаи гуноҳкор дурӯғ меҳисобад.'),
(5861, 83, 13, 'Чун оёти Мо бар ӯ хонда шуд, гуфт: «Афсонаҳои пешиниён аст!»'),
(5862, 83, 14, 'Ҳаққо, ки корҳое, ки карда буданд, бар дилҳошон (ғолиб шуда) занг бастааст.'),
(5863, 83, 15, 'Ҳаққо, ки дар он рӯз аз (дидори) Парвардигорашон маҳҷуб (маҳрум) бошанд.'),
(5864, 83, 16, 'Пас онҳо ба ҷаҳаннам дароянд.'),
(5865, 83, 17, 'Ва онҳоро гӯянд: «Ин аст он чӣ дурӯғаш мешумурдед!»'),
(5866, 83, 18, 'Ҳаққо, ки амалҳои некон дар иллийин мактуб аст.'),
(5867, 83, 19, 'Ва ту чӣ медонӣ, ки иллийин чист?'),
(5868, 83, 20, 'Китобест навишта!'),
(5869, 83, 21, 'Ки муқаррабони (наздикшудагони) Худо дар он менигаранд.'),
(5870, 83, 22, 'Албатта некон дар неъматанд,'),
(5871, 83, 23, 'бар тахтҳо нишаста ва назора мекунанд.'),
(5872, 83, 24, 'Бар чеҳраҳошон таровати неъматро бишиносӣ.'),
(5873, 83, 25, 'Аз шаробе холис, ки бар сари он мӯҳр ниҳодаанд, сероб мешаванд.'),
(5874, 83, 26, 'Мӯҳри он аз мушк аст ва пешдастӣ кунандагон дар он бар якдигар пешдастӣ кунанд,'),
(5875, 83, 27, 'Омезаи он тасним аст, (яъне ботасним омехта)'),
(5876, 83, 28, 'чашмаест, ки муқаррабони (наздикони даргоҳи) Худо аз он меошоманд.'),
(5877, 83, 29, 'Гуноҳкорон ба мӯъминон механдиданд'),
(5878, 83, 30, 'Ва чун бар онҳо мегузаштанд, ба чашму абрӯ ишора мекарданд (бо нияти масхара)'),
(5879, 83, 31, 'ва чун назди касонашон бозмегаштанд, шодмона бозмегаштанд.'),
(5880, 83, 32, 'Ва чун ононро медиданд, мегуфтанд, ки инҳо гумроҳанд.'),
(5881, 83, 33, 'Ва ҳол он ки ононро ба нигаҳбониву муҳофизати амалҳои мӯъминон нафиристода буданд.'),
(5882, 83, 34, 'Ва имрӯз мӯъминон ба кофирон механданд,'),
(5883, 83, 35, 'бар тахтҳо такя задаанд ва менигаранд.'),
(5884, 83, 36, 'Оё кофирон баробари амалҳояшон ҷазо ёфтаанд?');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Чун осмон шикофта шавад'),
(5886, 84, 2, 'ва ба фармони Парвардигораш гӯш диҳад ва ҳақ бувад, ки чунин кунад'),
(5887, 84, 3, 'ва чун замин мунбасит (кашида ва паҳд) шавад'),
(5888, 84, 4, 'ва ҳар чиро ки дар дарун дорад, берун партояд ва холӣ гардад'),
(5889, 84, 5, 'ва ба фармони Парвардигораш гӯш диҳад ва ҳақ бувад, ки чунин кунад.'),
(5890, 84, 6, 'Эй инсон, ту дар роҳи Парвардигорат ранҷи фаровон мекашӣ, пас мукофоти онро хоҳӣ дид.'),
(5891, 84, 7, 'Ҳар кас, ки номаашро ба дасти росташ диҳанд,'),
(5892, 84, 8, 'ба зудӣ осон аз ӯ ҳисоб кунанд'),
(5893, 84, 9, 'ва шодмон назди касонаш бозгардад.'),
(5894, 84, 10, 'Аммо ҳар кас, ки номааш аз пушти сар дода шавад,'),
(5895, 84, 11, 'ба зудӣ, ки бигӯяд: «Вой бар ман, ки ҳалок шудам».'),
(5896, 84, 12, 'Ва ба оташи афрӯхта дарояд.'),
(5897, 84, 13, 'Ӯ дар дунё назди касонаш шодмон зиста буд.'),
(5898, 84, 14, 'Ва мепиндошт, ки ҳаргиз бознахоҳад гашт.'),
(5899, 84, 15, 'Оре, Парвардигораш ӯро медидааст.'),
(5900, 84, 16, 'Пас ба шафақ савганд мехӯрам'),
(5901, 84, 17, 'ва савганд ба шабу ҳар чиро фурӯ пӯшад'),
(5902, 84, 18, 'ва савганд ба моҳ, чун пурра шавад,'),
(5903, 84, 19, 'ки ба ҳоле баъд аз ҳоли дигар хоҳед расид.'),
(5904, 84, 20, 'Чӣ мешавад онҳоро, ки имон намеоваранд?'),
(5905, 84, 21, 'Ва чун Қуръон бар онҳо хонда шавад, саҷда намекунанд? (Саҷда).'),
(5906, 84, 22, 'Балки кофирон такзиб мекунанд (дурӯғ мебароранд).'),
(5907, 84, 23, 'Ва Худо ба он чӣ дар дил доранд, огоҳ аст.'),
(5908, 84, 24, 'Пас ононро ба азобе дардовар хушхабар деҳ'),
(5909, 84, 25, 'ғайри онон, ки имон овардаанд ва корҳои шоиста кардаанд, ки мукофоташонро поёне нест.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Қасам ба осмон, ки дорандаи бурҷҳост'),
(5911, 85, 2, 'ва қасам ба рӯзи мавъуд'),
(5912, 85, 3, 'ва қасам ба шоҳидидиҳанда ва он чӣ ба он шоҳидӣ диҳанд,'),
(5913, 85, 4, 'ки соҳибони уҳдуд ба ҳалокат расиданд.'),
(5914, 85, 5, 'Оташе афрӯхта аз ҳезумҳо'),
(5915, 85, 6, 'он гоҳ ки бар канори он оташ нишаста буданд'),
(5916, 85, 7, 'ва бар он чӣ бар сари мӯъминон меоваранд, шоҳид буданд.'),
(5917, 85, 8, 'Ва ҳеҷ айбе дар онҳо наёфтанд, ҷуз он ки ба Худои ғолиби лоиқи ситоиш имон оварда буданд,'),
(5918, 85, 9, 'он Худое, ки фармонравоии осмонҳову замин аз они Ӯст ва бар ҳар чизе нозир аст.'),
(5919, 85, 10, 'Албатта онон, ки мардону занони мӯъминро шиканҷа карданд ва тавба накарданд, азоби ҷаҳаннам ва азоби оташ барои онҳост.'),
(5920, 85, 11, 'Барои касоне, ки имон овардаанд ва корҳои шоиста кардаанд, биҳиштҳоест, ки дар он наҳрҳо ҷорист ва он комёбии бузургест!'),
(5921, 85, 12, 'Ба азоб гирифтани Парвардигори ту сахт аст.'),
(5922, 85, 13, 'Ӯст, ки нахуст меофаринад ва пас аз марг зинда месозад.'),
(5923, 85, 14, 'Ӯ бахшояндаву дӯстдоранда аст.'),
(5924, 85, 15, 'Ӯст соҳиби арши бузург.'),
(5925, 85, 16, 'Ҳар чиро ирода кунад, ба анҷом мерасонад.'),
(5926, 85, 17, 'Оё достони лашкарҳоро шунидаӣ?'),
(5927, 85, 18, 'Лашкарҳои Фиръавну қавми Самуд,'),
(5928, 85, 19, 'Оре, онон, ки роҳи куфр пеш гирифтаанд, ҳамчунон дар дурӯғбарорӣ ҳастанд;'),
(5929, 85, 20, 'ва Худо аз ҳама сӯ бар онҳо иҳота дорад.'),
(5930, 85, 21, 'Бале, ин Қуръони маҷид аст'),
(5931, 85, 22, 'дар лавҳи маҳфуз!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Савганд ба осмону ба он чӣ дар шаб ояд!'),
(5933, 86, 2, 'Ва ту чӣ донӣ, ки он чӣ дар шаб ояд, чист?'),
(5934, 86, 3, 'Ситораест дурахшанда.'),
(5935, 86, 4, 'Ҳеҷ кас нест, кӣ бар ӯ нигаҳбоне набошад.'),
(5936, 86, 5, 'Пас одамӣ бингарад, ки аз чӣ чиз офариша шудааст.'),
(5937, 86, 6, 'Аз обе ҷаҳанда офарида шудааст,'),
(5938, 86, 7, 'ки аз миёни пушту сина берун меояд.'),
(5939, 86, 8, 'Худо ба бозгардонидани ӯ тавоност,'),
(5940, 86, 9, 'рӯзе, ки розҳо (сирҳо) ошкор мешаванд.'),
(5941, 86, 10, 'Ӯро на қувватест, ва на ёваре.'),
(5942, 86, 11, 'Қасам ба осмони бозборанда!'),
(5943, 86, 12, 'Қасам ба замини руёни шикофхӯрда, (барои гиёҳ)'),
(5944, 86, 13, 'ки ин китоб ҳақро аз ботил ҷудо кунад,'),
(5945, 86, 14, 'ва сухани ҳазл нест!'),
(5946, 86, 15, 'Онон ҳилае меандешанд'),
(5947, 86, 16, 'ва Ман ҳам ҳилае меандешам.'),
(5948, 86, 17, 'Пас кофиронро мӯҳлат деҳ андак мӯҳлаташон деҳ!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Номи Парвардигори бузурги худро ба покӣ ёд кун,'),
(5950, 87, 2, 'он ки офарид ва дурустандом кард,'),
(5951, 87, 3, 'Ва он ки андоза муъайян кард. Сипас роҳ намуд.'),
(5952, 87, 4, 'Ва он ки чарогоҳҳоро рӯёнид,'),
(5953, 87, 5, 'сипас хушку сиёҳ гардонид.'),
(5954, 87, 6, 'Ба зудӣ барои ту бихонем, мабод, ки фаромӯш кунӣ.'),
(5955, 87, 7, 'ғайри он чи Худо бихоҳад. Ӯст, ки ошкорову ниҳонро медонад.'),
(5956, 87, 8, 'Ва туро ба дини осон тавфиқ диҳем.'),
(5957, 87, 9, 'Агар панд, доданат суд кунад, панд деҳ.'),
(5958, 87, 10, 'Он ки метарсад, панд мегирад.'),
(5959, 87, 11, 'Ва бадбахт аз он дурӣ мегирад,'),
(5960, 87, 12, 'он ки ба оташи бузурги ҷаҳаннам дарафтад'),
(5961, 87, 13, 'ва дар он ҷо на бимирад ва на зинда бошад.'),
(5962, 87, 14, 'Албатта покон наҷот ёфтанд,'),
(5963, 87, 15, 'онон, ки номи Парвардигори худро бар забон оварданд ва намоз гузориданд.'),
(5964, 87, 16, 'Оре, шумо зиндагии ин ҷаҳонро ихтиёр мекунед,'),
(5965, 87, 17, 'ҳол он ки охират беҳтару пояндатар аст.'),
(5966, 87, 18, 'Ин сухан дар саҳифаҳои нахустин аст,'),
(5967, 87, 19, 'саҳифаҳои Иброҳиму Мӯсо!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Оё достони ғошия ба ту расидааст?'),
(5969, 88, 2, 'Дар он рӯз ваҳшат дар чеҳраҳо падидор аст,'),
(5970, 88, 3, 'талошкардаву ранҷдида'),
(5971, 88, 4, 'дар оташи сӯзон дарафтанд,'),
(5972, 88, 5, 'аз он чашмаи бисёр гарм обашон диҳанд,'),
(5973, 88, 6, 'таъоме ғайри хор надоранд,'),
(5974, 88, 7, 'ки на фарбеҳ мекунад ва на дафъи гуруснагӣ.'),
(5975, 88, 8, 'Ва дар он рӯз чеҳраҳое тоза бошанд,'),
(5976, 88, 9, 'аз кори худ хушнуд,'),
(5977, 88, 10, 'дар биҳиште олимақом,'),
(5978, 88, 11, 'ки дар он сухани беҳуда нашнавӣ'),
(5979, 88, 12, 'ва дар он чашмасорҳо равон бошад'),
(5980, 88, 13, 'ва тахтҳое баланд зада'),
(5981, 88, 14, 'ва кӯзаҳое ниҳода'),
(5982, 88, 15, 'ва болишҳое дар канори ҳам чида'),
(5983, 88, 16, 'ва фаршҳое паҳн карда.'),
(5984, 88, 17, 'Оё ба шутур наменигаранд, ки чӣ гуна офарида шуда?'),
(5985, 88, 18, 'Ва ба осмон, ки чӣ гуна бардошташуда?'),
(5986, 88, 19, 'Ва ба кӯҳҳо, ки чӣ гуна баланд кашидашуда?'),
(5987, 88, 20, 'Ва ба замин, ки чӣ сон густурда шуда?'),
(5988, 88, 21, 'Пас панд деҳ ки ту панддиҳандае ҳастӣ.'),
(5989, 88, 22, 'Ту бар онон фармонраво нестӣ.'),
(5990, 88, 23, 'Магар он кас кӣ рӯйгардон шуд ва кофир шуд.'),
(5991, 88, 24, 'Пас Худояш ба азоби бузургтар азоб мекунад.'),
(5992, 88, 25, 'Албатта бозгашташон ба сӯи Мост.'),
(5993, 88, 26, 'Сипас ҳисобашон бо Мост.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Савганд ба сапедаи субҳ'),
(5995, 89, 2, 'ва савганд ба шабҳои даҳгона (даҳ шаби Зулҳиҷҷа)'),
(5996, 89, 3, 'ва савганд ба ҷуфту тоқ'),
(5997, 89, 4, 'ва савганд ба шаб, чун рӯй ба рафтан ниҳад.'),
(5998, 89, 5, 'Оё дар ин зикркардашудаҳо хирадмандро савгандҳои кофист?'),
(5999, 89, 6, 'Оё надидаӣ, ки Парвардигори ту бо қавми Од чӣ кард?'),
(6000, 89, 7, 'Бо Ирам, ки сутунҳо дошт?'),
(6001, 89, 8, 'Ва монанди он дар ҳеҷ шаҳре офарида нашуда буд.'),
(6002, 89, 9, 'Ва қавми Самуд, ки дар он водӣ сангро мебуриданд.'),
(6003, 89, 10, 'Ва қавми Фиръавн, он дорандаи мехҳо,'),
(6004, 89, 11, 'онҳо дар шаҳрҳо аз ҳад таҷовуз карданд'),
(6005, 89, 12, 'ва дар онҳо бисёр фасод карданд.'),
(6006, 89, 13, 'Ва Парвардигори ту тозиёнаи азобро бар сарашон фуруд овард.'),
(6007, 89, 14, 'Зеро Парвардигорат дар камингоҳ аст.'),
(6008, 89, 15, 'Аммо одамӣ, чута Парвардигораш биёзмояд ва гиромиаш дорад ва неъматаш диҳад, мегӯяд: «Парвардигори ман маро гиромӣ дошт».'),
(6009, 89, 16, 'Ва чун биёзмоядаш ва ризқ бар ӯ танг гирад, мегӯяд: «Парвардигори ман маро хор сохт».'),
(6010, 89, 17, 'На чунон аст. Шумо ятимро гиромӣ намедоред'),
(6011, 89, 18, 'ва якдигарро ба таъом додани мискиш тарғиб намекунед'),
(6012, 89, 19, 'ва меросро ҳарисона мехӯред'),
(6013, 89, 20, 'ва молро фаровон дӯст доред.'),
(6014, 89, 21, 'Оре, чун замин шикаста шавад ва шикаста шавад'),
(6015, 89, 22, 'ва амри Парвардигори ту фаро расад ва фариштагон саф дар саф шаванд'),
(6016, 89, 23, 'ва дар он рӯз ҷаҳаннамро ҳозир оранд, одамӣ панд гирад. Ва чӣ ҷои панд гирифтан бошад?'),
(6017, 89, 24, 'Мегӯяд: «Эй кош ки барои ҳаёти ҷовидонаи худ пешопеш чизе мефиристодам».'),
(6018, 89, 25, 'Дар он рӯз касе чун азоби ӯ азоб накунад'),
(6019, 89, 26, 'ва монанди занҷири ӯ ба занҷир накашад.'),
(6020, 89, 27, 'Эй рӯҳи оромишёфта,'),
(6021, 89, 28, 'хушнуду писандида ба сӯи Парвардигорат бозгард'),
(6022, 89, 29, 'ва дар зумраи бандагони Ман дохил шав'),
(6023, 89, 30, 'ва ба биҳишти Ман дарой!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Қасам ба ин шаҳр! (Макка)'),
(6025, 90, 2, 'Ва ту дар ин шаҳр ҳалол хоҳӣ шуд.'),
(6026, 90, 3, 'Ва қасам ба падару фарзандони ӯ,'),
(6027, 90, 4, 'ки одамиро, дар ранҷу меҳнат биёфаридаем.'),
(6028, 90, 5, 'Оё мепиндорад, ки кас бар ӯ қодир нагардад?'),
(6029, 90, 6, 'Мегӯяд: «Моле фаровонро харҷ кардам!»'),
(6030, 90, 7, 'Оё мепиндорад, ки касе ӯро надидааст?'),
(6031, 90, 8, 'Оё барои ӯ ду чашм наёфаридаем?'),
(6032, 90, 9, 'Ва як забону ду лаб?'),
(6033, 90, 10, 'Ва ду роҳ ба ӯ далолат накардем?'),
(6034, 90, 11, 'Ва ӯ дар он гузаргоҳи сахт қадам наниҳод.'),
(6035, 90, 12, 'Ва ту чӣ донӣ, ки гузаргоҳи сахт чист?'),
(6036, 90, 13, 'Озод кардани банда аст,'),
(6037, 90, 14, 'ё таъом додан дар рӯзи қаҳтӣ,'),
(6038, 90, 15, 'хоса ба ятиме, ки хешованд бошад,'),
(6039, 90, 16, 'ё ба мискине хокнишин (мӯҳтоҷ).'),
(6040, 90, 17, 'Баъд, аз он аз касоне бошад, ки имон овардаанд ва якдигарро ба сабр васият кардаанду ба шафқат.'),
(6041, 90, 18, 'Инҳо аҳли саъодатанд.'),
(6042, 90, 19, 'Ва касоне, ки ба оёти Мо кофиранд, аҳли шақоватанд (бадбахтиянд).'),
(6043, 90, 20, 'Насиби онҳост оташе, ки аз ҳар сӯ сараш пӯшидааст.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Савганд ба офтоб ва равшаниаш ба ҳангоми чошт'),
(6045, 91, 2, 'ва савганд ба моҳ чун аз паи он барояд'),
(6046, 91, 3, 'ва савганд, ба рӯз, чун дунёро равшан кунад'),
(6047, 91, 4, 'ва савганд ба шаб, чун рӯзро бипушад'),
(6048, 91, 5, 'ва савганд ба осмону он ки онро бино карда'),
(6049, 91, 6, 'ва савганд ба замину он ки онро бигустурда'),
(6050, 91, 7, 'ва савганд, ба нафсу он ки некӯяш биёфарида,'),
(6051, 91, 8, 'сипас бадиҳову парҳезгориҳояшро ба ӯ илҳом карда,'),
(6052, 91, 9, 'ки ҳар ки дар покии нафс кӯшид, наҷот ёфт'),
(6053, 91, 10, 'ва ҳар кӣ дар палидиаш кӯшид, зиён кард.'),
(6054, 91, 11, 'Қавми Самуд аз рӯи саркашӣ дурӯғ бароварданд.'),
(6055, 91, 12, 'Он гоҳ ки бадбахттаринашон бархост.'),
(6056, 91, 13, 'Паёмбари Худо ба онҳо гуфт, ки модашутури Худрро бо навбати оби ӯро бигузоред!'),
(6057, 91, 14, 'Ӯро дурӯғ бароварданд ва шутурро куштанд. Пас Парвардигорашои ба сабаби гуноҳашод бар сарашон азоб овард ва бо хок яксон сохт.'),
(6058, 91, 15, 'Ва Ӯ аз оқибати он наметарсад.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Савганд ба шаб, он гоҳ ки чаҳонро бо торикиаш бипӯшад'),
(6060, 92, 2, 'ва савганд ба рӯз, он гоҳ ки ошкор шавад'),
(6061, 92, 3, 'ва савганд, ба он ки нару модаро биёфарид,'),
(6062, 92, 4, 'ки ҳосили кӯшишҳои шумо гуногун аст.'),
(6063, 92, 5, 'Аммо касе, ки бахшоишу парҳезгорӣ кард'),
(6064, 92, 6, 'ва он беҳтаринро тасдиқ кард (калимаи тайибаро),'),
(6065, 92, 7, 'пас барои биҳишт омодааш мекунем.'),
(6066, 92, 8, 'Аммо он кас кӣ бахиливу беэҳтиёҷӣ кард.'),
(6067, 92, 9, 'ва он беҳтаринро такзиб кард (дурӯғ баровард),'),
(6068, 92, 10, 'ӯро барои дӯзах омода месозем.'),
(6069, 92, 11, 'Ва чун ҳалокаш даррасад, дороияш ба ҳолаш фоида набахшад.'),
(6070, 92, 12, 'Ва он чӣ бар Мост, роҳнамоист.'),
(6071, 92, 13, 'Ва аз они Мост он ҷаҳону ин чаҳон.'),
(6072, 92, 14, 'Пас шуморо аз оташе, ки забона мезанад, метарсонам,'),
(6073, 92, 15, 'ҷуз аҳли бадбахтӣ ба он дарнаяфтад,'),
(6074, 92, 16, 'он ки дурӯғ баровард ва рӯйгардон шуд.'),
(6075, 92, 17, 'Ва парҳезгортарин мардумро аз он дур доранд,'),
(6076, 92, 18, 'он кӣ моли худ мебахшояд ва покӣ меҷӯяд.'),
(6077, 92, 19, 'Ва ҳеҷ касро бар (гардани) ӯ ҳаққе набошад, ки акнун мукофот хоҳад,'),
(6078, 92, 20, 'Ғайри ҷустани хушнудии Парвардигори баландмартабаи худ.'),
(6079, 92, 21, 'Ва ба зудӣ хушнуд шавад.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Савганд ба вақти чошт,'),
(6081, 93, 2, 'ва савганд ба шаб, чун бипӯшад (бо торикии худ атрофро),'),
(6082, 93, 3, 'ки Парвардигорат туро тарк накарда ва душман ҳам нагирифтааст.'),
(6083, 93, 4, 'Албатта охират барои ту беҳтар аз дунёст.'),
(6084, 93, 5, 'Ба зуди Парвардигорат туро ато хоҳад дод, то хушнуд шавӣ.'),
(6085, 93, 6, 'Оё туро ятим наёфт ва паноҳат надод?'),
(6086, 93, 7, 'Ва туро гумгашта ёфт ва ҳидоятат кард?'),
(6087, 93, 8, 'Ва туро дарвеш ёфт ва тавонгарат гардонид?'),
(6088, 93, 9, 'Пас ятимро маёзор'),
(6089, 93, 10, 'ва гадоро марон'),
(6090, 93, 11, 'ва аз неъмати Парвардигорат сухан бигӯй.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Оё синаатро бароят накушодем?'),
(6092, 94, 2, 'Ва бори гаронатро аз пуштат барнадоштем?'),
(6093, 94, 3, 'Боре, ки бар пушти ту сангинӣ мекард?'),
(6094, 94, 4, 'Оё туро баландовоза насохтем?'),
(6095, 94, 5, 'Пас аз паи душворӣ осонист!'),
(6096, 94, 6, 'Албатта аз паи душворӣ осонист.'),
(6097, 94, 7, 'Чун аз кор фориғ шавӣ, ба ибодат кӯш'),
(6098, 94, 8, 'ва ба Парвардигорат рағбат намо!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Савганд ба анҷиру зайтун'),
(6100, 95, 2, 'савганд ба Тури муборак,'),
(6101, 95, 3, 'савганд ба ин шаҳри эмин,'),
(6102, 95, 4, 'ки мо одамиро дар некӯтарин сурате биёфаридем.'),
(6103, 95, 5, 'Он гоҳ ӯро фурӯтар аз ҳамаи фурӯтарон гардонидем, (пасттар аз ҳамаи пастҳо)'),
(6104, 95, 6, 'ғайри онон, ки имон овардаанд ва корҳои шоиста кардаанд, ки музде бепоён доранд.'),
(6105, 95, 7, 'Пас чист, ки бо ин ҳол туро ба дурӯғ шуморидани қиёмат вомедорад?'),
(6106, 95, 8, 'Оё Худо довартарини доварон нест?');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Бихон ба номи Парвардигорат, ки биёфарид,'),
(6108, 96, 2, 'одамиро аз лахтаи хуне биёфарид.'),
(6109, 96, 3, 'Бихон! Ва Парвардигори ту арҷмандтарин аст.'),
(6110, 96, 4, 'Худое, ки ба воситаи қалам омӯзиш дод,'),
(6111, 96, 5, 'ба одами он чиро, ки намедонист, биёмухт.'),
(6112, 96, 6, 'Ҳаққо, ки одами аз ҳад мегузарад,'),
(6113, 96, 7, 'ҳар гоҳ ки хештанро тавонгар бинад.'),
(6114, 96, 8, 'Албатта бозгашт ба сӯи Парвардигори туст.'),
(6115, 96, 9, 'Оё дидӣ он касро, ки, манъ мекунад,'),
(6116, 96, 10, 'бандаеро, ки намоз мехонад?'),
(6117, 96, 11, 'Канӣ бигӯ, агар он мард бар тариқи ҳидоят бошад?'),
(6118, 96, 12, 'Ё ба парҳезгорӣ фармон диҳад?'),
(6119, 96, 13, 'Канӣ бигӯ, агар дурӯғ барорад ва рӯйгардон шавад?'),
(6120, 96, 14, 'Оё надонистааст, ки Худо мебинад?'),
(6121, 96, 15, 'Ҳаққо, ки агар бознаистад, аз мӯи пеши сараш мегирему мекашем,'),
(6122, 96, 16, 'мӯи пеши сари дурӯғгӯи хатокорро.'),
(6123, 96, 17, 'Пас ҳамдамони худро даъват кунад.'),
(6124, 96, 18, 'Мо низ коргузоронидӯзахро даъват мекунем!'),
(6125, 96, 19, 'На, ҳаргиз, аз ӯ пайравӣ макун ва саҷда куну ба Худо наздик шав! (Саҷда).');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Мо дар шаби қадраш нозил кардем! (Қуръонро)'),
(6127, 97, 2, 'Ва ту чӣ донӣ, ки шаби қадр чист?'),
(6128, 97, 3, 'Шаби қадр беҳтар аз ҳазор моҳ аст.'),
(6129, 97, 4, 'Дар он шаб фариштагону рӯҳ ба фармони Парвардигорашон барои анҷом додани корҳо мефароянд.'),
(6130, 97, 5, 'Он шаб то тулӯъи бомдод ҳама салому эминӣ аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Кофирони аҳли китоб ва мушрикон даст барнадоранд (аз куфри худ), то барояшон хуҷҷате равшан биёяд —'),
(6132, 98, 2, 'паёмбаре аз ҷониби Худо, ки сахифаҳои покро мехонад,'),
(6133, 98, 3, 'дар онҳо навиштаҳоест росту дуруст.'),
(6134, 98, 4, 'Ва аҳли китоб пароканда шуданд, аз он пас, ки барояшон бурҳоне равшан омад.'),
(6135, 98, 5, 'Ва ононро фақат ин фармон доданд, ки Худоро бипарастанд, дар ҳоле ки дар дини Ӯ ихлос меварзанд. Ва намоз гузоранду закот диҳанд. Ин аст дини дурусту рост!'),
(6136, 98, 6, 'Аз аҳли китоб онҳо, ки кофиранд ва, низ мушрикон дар оташи ҷаҳаннаманд ва дар он ҳамеша хоҳанд буд. Инҳо бадтарини офаридагонанд.'),
(6137, 98, 7, 'Касоне, ки имон овардаанд ва корҳои шоиста мекунанд, беҳтарини офаридагонанд.'),
(6138, 98, 8, 'Муздашон дар назди Парвардигорашон биҳиштҳоест ҷовид, ки дар он наҳрҳо ҷорист. То абад дар он ҷоянд. Худо аз онҳо розӣ аст ва онҳо аз Худо розианд. Ва ин хоси касест, ки аз Парвардигораш битарсад.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Он гоҳ ки замин ларзонда шавад ба сахттарин ларзаҳояш'),
(6140, 99, 2, 'ва замин борҳои сангинашро берун резад'),
(6141, 99, 3, 'ва одамӣ бигӯяд, ки заминро чӣ расидааст?'),
(6142, 99, 4, 'Дар ин рӯз замин хабарҳои худро ҳикоят меку над,'),
(6143, 99, 5, 'аз он чӣ Парвардигорат ба ӯ ваҳй кардааст.'),
(6144, 99, 6, 'Дар он рӯз мардум пароканда аз қабрҳо берун меоянд, то амалҳояшонро ба онҳо нишон диҳанд.'),
(6145, 99, 7, 'Пас ҳар кас ба вазни заррае некӣ карда бошад, онро мебинад'),
(6146, 99, 8, 'ва ҳар кас ба вазни заррае бадӣ карда бошад, онро мебинад,');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Савганд ба аспони тездав,'),
(6148, 100, 2, 'савганд ба аспоне, ки ба сум аз санг оташ меҷаҳонанд'),
(6149, 100, 3, 'ва савганд ба аспоне, ки бомдодон ҳуҷум оваранд'),
(6150, 100, 4, 'ва дар он ҷо ғубор бархезонанд'),
(6151, 100, 5, 'ва дар он ҷо дар миён дароянд,'),
(6152, 100, 6, 'ки одамӣ Парвардигори худро шукр намегӯяд'),
(6153, 100, 7, 'ва ӯ худ бар ин гувоҳ аст.'),
(6154, 100, 8, 'Ӯ молро сахт дӯст дорад.'),
(6155, 100, 9, 'Оё намедонад, ки чун он чӣ дар гӯрҳост, зинда гардад,'),
(6156, 100, 10, 'ва он чӣ дар дилҳо ниҳон аст, ошкор шавад,'),
(6157, 100, 11, 'Парвардигорашон дар он рӯз аз ҳолашон огоҳ аст.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Қориъаҳ.'),
(6159, 101, 2, 'Чист қориъаҳ?'),
(6160, 101, 3, 'ва ту чӣ донӣ, ки қориъаҳ чист?'),
(6161, 101, 4, 'Рӯзест, ки мардум чун парвонагон пароканда бошанд'),
(6162, 101, 5, 'ва кӯҳҳо чун пашми задашуда.'),
(6163, 101, 6, 'Аммо ҳар кӣ паллаи некиҳои тарозуяш вазнин бошад,'),
(6164, 101, 7, 'Пас ӯ дар айши писандида бошад.'),
(6165, 101, 8, 'ва аммо ҳар кӣ сабук шуд паллаи некиҳои тарозуяш'),
(6166, 101, 9, 'ҷойгоҳаш дар ҳовияҳ аст.'),
(6167, 101, 10, 'Ва ту чӣ донӣ, ки ҳовияҳ чист?'),
(6168, 101, 11, 'Оташест дар ниҳояти гармӣ.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Ба ғафлат кашид шуморо фахр кардан ба бисёрии молу фарзанд,'),
(6170, 102, 2, 'то ба гӯрҳо расидед.'),
(6171, 102, 3, 'Ҳаққо, ки ба зудӣ хоҳед донист.'),
(6172, 102, 4, 'Боз ҳам ҳаққо, ки ба зудӣ хоҳед донист.'),
(6173, 102, 5, 'Ҳаққо агар аз рӯи яқин бидонед,'),
(6174, 102, 6, 'албатта, ҷаҳаннамро хоҳед дид.'),
(6175, 102, 7, 'Сипас ба чашми яқинаш хоҳед дид,'),
(6176, 102, 8, 'сипас дар он рӯз шуморо аз неъматҳои дунявӣ бозхост мекунанд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Савганд ба ин замон,'),
(6178, 103, 2, 'ки одамӣ дар зиён аст,'),
(6179, 103, 3, 'ғайри онҳо, ки имон оварданд ва корҳои шоиста карданд ва якдигарро ба ҳақ супориш карданд ва якдигарро ба сабр супориш карданд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Вой бар ҳар ғайбаткунандаи айбҷӯе,'),
(6181, 104, 2, 'он ки моле ҷамъ кард ва ҳисоби он нигаҳ дошт.'),
(6182, 104, 3, 'Мепиндорад, ки дороияш ҷовидонаш гардонад.'),
(6183, 104, 4, 'На чунин аст! ки Ӯро дар ҳутамаҳ андозанд.'),
(6184, 104, 5, 'Ва ту чӣ донӣ, ки ҳутамаҳ чист?'),
(6185, 104, 6, 'Оташи афрӯхтаи Худост,'),
(6186, 104, 7, 'ки бар дилҳо ғалаба меёбад.'),
(6187, 104, 8, 'Он оташ аз ҳар сӯ дар миёнашон гирифтааст,'),
(6188, 104, 9, 'дар сутунҳои дарози оташӣ дароварда шудааст.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Оё надидаӣ, ки Парвардигорат бо асҳоби фил чӣ кард?'),
(6190, 105, 2, 'Оё макрашонро ботил насохт?'),
(6191, 105, 3, 'Ва бар сари онҳо паррандагоне села - села фиристод,'),
(6192, 105, 4, 'то онҳоро бо сиҷҷил сангборон карданд.'),
(6193, 105, 5, 'Ва ононро чун коҳи пасмонда дар охур сохт.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Барои шукри улфату амни Қурайш;'),
(6195, 106, 2, 'улфату амнашон дар сафари зимистониву тобистонӣ»'),
(6196, 106, 3, 'Пас бояд Парвардигори ин хонаро (Каъбаро) бипарастанд:'),
(6197, 106, 4, 'он ки ба ҳангоми гуруснагӣ таъомашон дод ва аз хавф дар амонашон дошт.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Оё онро, ки рӯзи ҷазоро дурӯғ мешумурад, дидӣ?'),
(6199, 107, 2, 'Ӯ ҳамон касест, ки ятимро ба иҳонат (хорӣ) меронад'),
(6200, 107, 3, 'ва мардумро ба таъом додан ба бенаво тарғиб намекунад.'),
(6201, 107, 4, 'Пас вой бар он намозгузороне,'),
(6202, 107, 5, 'ки дар намози худ саҳл ангоранд (дар ғафлатанд),'),
(6203, 107, 6, 'онон, ки риё мекунанд'),
(6204, 107, 7, 'ва аз додани закот саркашӣ мекунанд.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Мо кавсарро ба ту ато кардем.'),
(6206, 108, 2, 'Пас барои Парвардигорат намоз бихон ва қурбонӣ кун,'),
(6207, 108, 3, 'ки бадхоҳи ту худ абтар аст.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'Бигӯ: «Эй кофирон!'),
(6209, 109, 2, 'Ман чизеро, ки шумо мепарастед, намепарастам'),
(6210, 109, 3, 'ва шумо низ чизеро, ки ман мепарастам, намепарастед'),
(6211, 109, 4, 'ва ман парастандаи чизе, ки шумо мепарастед, нестам'),
(6212, 109, 5, 'ва шумо парастандаи чизе, ки ман мепарастам, нестед,'),
(6213, 109, 6, 'Шуморо дини худ ва маро дини худ!»');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Чун ёрии Худову пирӯзӣ ояд'),
(6215, 110, 2, 'ва мардумро бубинӣ, ки гурӯҳ-гурӯҳ ба дини Худо медароянд,'),
(6216, 110, 3, 'пас ба ситоиши Парвардигорат тасбеҳ гӯй ва аз Ӯ бахшоиш бихоҳ, ки Ӯ тавбапазир аст!');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Дастҳои Абулаҳаб бурида бод ва ҳалок бар ӯ бод.'),
(6218, 111, 2, 'Моли ӯ ва он чӣ ба даст оварда буд, ба ҳолаш суд накард.'),
(6219, 111, 3, 'Ба зудӣ ба оташе шӯълавар дарояд'),
(6220, 111, 4, 'ва занаш ҳезумкаш аст'),
(6221, 111, 5, 'ва бар гардан ресмоне аз лифи (пӯсти) хурмо дорад.');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'Бигӯ: «Ӯст Худои якто,'),
(6223, 112, 2, 'Худое, ки дар ҳоҷат ба Ӯ рӯ кунанд,'),
(6224, 112, 3, 'на зоида аст ва на зоида шуда'),
(6225, 112, 4, 'ва на ҳеҷ кас ҳамтои Ӯст!»');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'Бигӯ: «Ба Парвардигори субҳгоҳ паноҳ мебарам,'),
(6227, 113, 2, 'аз шарри он чӣ биёфаридааст'),
(6228, 113, 3, 'ва аз шарри шаб чун дарояд'),
(6229, 113, 4, 'ва аз шарри ҷодугароне, ки дар гиреҳҳо афсун медаманд'),
(6230, 113, 5, 'ва аз шарри ҳасуд чун ҳасад мекунад!»');
INSERT INTO `tg_ayati` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'Бигӯ: «Ба Парвардигори мардум паноҳ мебарам,'),
(6232, 114, 2, 'фармонравои мардум,'),
(6233, 114, 3, 'Худои мардум,'),
(6234, 114, 4, 'аз шарру васвасагари пинҳоншаванда,'),
(6235, 114, 5, 'он кӣ дар дилҳои мардум васваса мекунад,'),
(6236, 114, 6, 'хоҳ аз ҷинниён бошад ё аз одамиён!»');
