INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Rahman ve Rahim Allah\'ın adıyla'),
(2, 1, 2, 'Alemlerin Rabbi (sahibi, yetiştiricisi) Allah\'a hamdolsun.'),
(3, 1, 3, '(O) Rahman\'dır, Rahim\'dir.'),
(4, 1, 4, 'Din (ceza ve mükafat) gününün sahibidir.'),
(5, 1, 5, '(Ya Rabbi) Ancak sana kulluk eder, ancak Senden yardım isteriz!'),
(6, 1, 6, 'Bizi doğru yola ilet:'),
(7, 1, 7, '-ni\'met verdiğin kimselerin yoluna. Kendilerine gazabedilmiş olanların ve sapmışların yoluna değil. (ya Rabbi)!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Elif lam mim.'),
(9, 2, 2, 'İşte o Kitap; kendisinde hiç şüphe yoktur; müttakiler için yol göstericidir.'),
(10, 2, 3, 'Onlar ki gaybde(gizlide, içtenlikle) inanıp namazlarını kılarlar ve kendilerine verdiğimiz rızıktan (Allah rızası için) harcarlar.'),
(11, 2, 4, 'Sana indirilene ve senden önce indirilene inanırlar; ahirete de kesinlikle iman ederler.'),
(12, 2, 5, 'İşte onlar, Rablerinden bir hidayet üzeredirler ve umduklarına erenler, işte onlardır!'),
(13, 2, 6, 'İnkar edenlere gelince, onları uyarsan da, uyarmasan da, onlar için birdir; inanmazlar.'),
(14, 2, 7, 'Allah, onların kalblerini ve kulaklarını mühürlemiştir, gözlerine de perde inmiştir. Onlar için büyük bir azab vardır.'),
(15, 2, 8, 'İnsanlardan öyleleri de vardır ki, inanmadıkları halde "Allah\'a ve ahiret gününe inandık" derler.'),
(16, 2, 9, 'Allah\'ı ve mü\'minleri aldatmağa çalışırlar, halbuki yalnız kendilerini aldatırlar da farkında olmazlar.'),
(17, 2, 10, 'Onların kablerinde hastalık vardır. Allah da hastalıklarını artırmıştır. Yalan söylemelerinden ötürü onlara acı bir azab vardır.'),
(18, 2, 11, 'Onlara: "Yeryüzünde bozgunculuk yapmayın," dendiği zaman: "Biz sadece düzelticileriz," derler.'),
(19, 2, 12, 'İyi bilin ki, onlar bozgunculardır; fakat anlamazlar.'),
(20, 2, 13, 'Onlara: "İnsanların inandıkları gibi siz de inanın" dense, "O beyinsizlerin inandığı gibi inanır mıyız?" derler. İyi bilin ki, asıl beyinsizler kendileridir; fakat bilmezler.'),
(21, 2, 14, 'İnanmış olanlara rastladıkları zaman; "İnandık," derler. Fakat şeytanlarıyla yalnız kaldıkları zaman; "Biz sizinle beraberiz, biz sadece (onlarla) alay ediyoruz," derler.'),
(22, 2, 15, 'Allah da kendileriyle alay eder ve onları bırakır; taşkınları içinde bocalayıp dururlar.'),
(23, 2, 16, 'İşte onlar o kimselerdir ki, hidayet karşılığında sapıklığı satın aldılar da ticaretleri kar etmedi, doğru yolu da bulamadılar.'),
(24, 2, 17, 'Onların durumu, tıpkı şuna benzer ki, (aydınlanmak için) bir ateş yakmak istedi. (Ateş) çevresini aydınlatır aydınlatmaz, Allah onların nurunu giderdi ve onları karanlıklar içinde bıraktı, artık görmezler.'),
(25, 2, 18, '(Onlar) sağırdırlar, dilsizdirler, kördürler. Onlar (Hakk\'a) dönmezler.'),
(26, 2, 19, 'Ya da (onlar), gökten boşanan, içinde karanlıklar, gök gürlemesi ve şimşek (ler) bulunan bir yağmur(a tutulmuş) gibi(dirler). Yıldırım seslerinden ölüm korkusuyla parmaklarını kulaklarına tıkarlar; oysa Allah, inkarcıları tamamen kuşatmıştır.'),
(27, 2, 20, 'Neredeyse gözlerini kapıverecek olan şimşek önlerini aydınlattı mı o(nun ışığı)nda yürürler, üzerlerine karanlık çökünce dikilip kalırlar. Allah dileseydi elbette işitmelerini ve görmelerini de götürürdü. Şüphesiz Allah\'ın her şeyi yapmağa gücü yeter.'),
(28, 2, 21, 'Ey insanlar, sizi ve sizden öncekileri yaratan Rabbinize kulluk edin ki, (azaptan) korunasınız.'),
(29, 2, 22, 'O (Rabb) ki yeri, sizin için döşek, göğü de bina yaptı. Gökten su indirdi, onunla size rızık olarak çeşitli ürünler çıkardı. Öyleyse siz de, bile bile Allah\'a eşler koşmayın.'),
(30, 2, 23, 'Eğer kulumuz (Muhammed)e indirdiğimizden şüphe içinde iseniz, haydi onun gibi bir sure getirin. Allah\'tan başka bütün şahid (yardımcı)larınızı da çağırın; eğer doğru iseniz (bunu yapın).'),
(31, 2, 24, 'Yok eğer yapmadınızsa, ki asla yapamayacaksınız, o halde yakıtı insanlar ve taşlar olan, inkarcılar için hazırlanmış ateşten sakının.'),
(32, 2, 25, 'İnanıp yararlı işler yapanlara, altlarından ırmaklar akan cennetlerin kendilerine aidolduğunu müjdele! Onlardaki herhangi bir meyveden rızıklandırıldıkça: "Bu, daha önce de rızıklandığımız şeydir, (dünyada iken de bu rızıktan yemiştik)" derler. (Cennetteki bu rızık), onlara, o(dedikleri)ne benzer verilmiştir. Onlar için orada tertemiz eşler de vardır ve onlar orada ebedi kalacaklardır.'),
(33, 2, 26, 'Allah, bir sivrisineği hatta onun da üstünde olan(ondan daha zayıf bir varlığ)ı misal vermekten utanmaz. İnananlar onun, Rablerinden (gelen) bir gerçek olduğunu bilirler. İnkar edenler ise: "Allah, bu misalle ne demek istedi?" derler. (Allah), onunla birçoğunu saptırır ve yine onunla birçoğunu yola getirir. Onunla sadece fasıkları saptırır.'),
(34, 2, 27, 'Onlar ki, söz verip bağlandıktan sonra Allah\'a verdikleri sözü bozarlar, Allah\'ın, birleştirmesini emrettiği şeyi (iman ve akrabalık bağlarını) keserler ve yeryüzünde bozgunculuk yaparlar; işte ziyana uğrayanlar onlardır.'),
(35, 2, 28, 'Allah\'a nasıl nankörlük edersiniz ki, siz ölüler idiniz, O sizi diriltti; yine öldürecek, yine diriltecek; sonra O\'na döndürüleceksiniz.'),
(36, 2, 29, 'O ki, yeryüzünde ne varsa hepsini sizin için yarattı; sonra göğe yöneldi, onları yedi gök olarak düzenledi. O, herşeyi bilir.'),
(37, 2, 30, 'Bir zamanlar Rabbin meleklere: "Ben yeryüzünde bir halife yapacağım," demişti. (Melekler): "Orada bozgunculuk yapan, kan döken birisini mi halife yapacaksın? Oysa biz seni överek tesbih ediyor ve seni takdis ediyoruz?" dediler. (Rabbin): Ben sizin bilmediklerinizi bilirim," dedi.'),
(38, 2, 31, 'Adem\'e isimlerin tümünü öğretti, sonra onları meleklere sunup: "Haydi, doğru iseniz onların isimlerini bana söyleyin," dedi.'),
(39, 2, 32, 'Dediler ki: "Sen yücesin (ya Rab); bizim senin bize öğrettiğinden başka bilgimiz yoktur. Şüphesiz sen bilensin, hakimsin (her şeyin içyüzünü bilen, her şeyi yerli yerince yapansın.)'),
(40, 2, 33, '(Allah) dedi ki: "Ey Adem, bunlara onların isimlerini haber ver." (Adem), bunlara onların isimlerini haber verince (Allah): "Ben size, ben göklerin ve yerin gayblarını bilirim, sizin açıkladığınızı ve içinizde gizlemekte olduğunuz şeyleri bilirim, dememiş miydim? dedi.'),
(41, 2, 34, 'Meleklere: "Adem\'e secde edin" demiştik, hemen secde ettiler: Yalnız İblis diretti, böbürlendi, nankörlerden oldu.'),
(42, 2, 35, 'Dedik ki: "Ey Adem, sen ve eşin cennette oturun, ondan dilediğiniz yerde bol bol yeyin, ama şu ağaca yaklaşmayın, yoksa zalimlerden olursunuz!"'),
(43, 2, 36, 'Derken şeytan onlar(ın ayağın)ı oradan kaydırdı, içinde bulundukları (ni\'met yurdu)ndan çıkardı. (Biz de) dedik ki: "Birbirinize düşman olarak inin. Sizin, yeryüzünde kalıp bir süre yaşamanız lazımdır."'),
(44, 2, 37, 'Adem, Rabbinden birtakım kelimeler aldı (onlarla amel edip Rabbine yalvardı, O da) bunun üzerine onun tevbesini kabul etti. Şüphesiz O, tevbeyi çok kabul eden(kulunun günahından geçen)dir, çok esirgeyendir.'),
(45, 2, 38, 'Hepiniz oradan inin, dedik, "Yalnız (iyi bilin ki) size benden bir hidayet geldiği zaman, kimler benim hidayetime uyarsa artık onlara bir korku yoktur ve onlar üzülmeyeceklerdir.'),
(46, 2, 39, 'İnkar edip ayetlerimizi yalanlayanlar ise ateş halkıdır, onlar orada ebedi kalacaklardır.'),
(47, 2, 40, 'Ey İsrail oğulları, size verdiğim ni\'metleri hatırlayın, bana verdiğiniz sözü tutun ki, ben de size verdiğim sözü tutayım ve sadece benden korkun!'),
(48, 2, 41, 'Sizin yanınızda bulunanı doğrulayıcı olarak indirmiş bulunduğum (Kur\'an)a inanın ve onu ilk inkar eden, siz olmayın; benim ayetlerimi birkaç paraya satmayın ve benden sakının.'),
(49, 2, 42, 'Bile bile gerçeği batılla bulayıp hakkı gizlemeyin.'),
(50, 2, 43, 'Namazı kılın, zekatı verin, rüku edenlerle (Allah\'ın huzurunda eğilenlerle) beraber eğilin.'),
(51, 2, 44, 'Siz Kitabı okuduğunuz halde, insanlara iyiliği emredip kendinizi unutuyor musunuz? Aklınızı kullanmıyor musunuz?'),
(52, 2, 45, 'Sabırla, namazla Allah\'tan yardım dileyin, şüphesiz bu, (Allah\'a) saygı gösterenlerden başkasına ağır gelir.'),
(53, 2, 46, 'O(saygılı insa)nlar, Rablerine kavuşacaklarını (gözetir) ve gerçekten O\'na döneceklerini bilirler.'),
(54, 2, 47, 'Ey İsrail oğulları, size verdiğim ni\'meti ve sizi alemlere üstün kıldığımı hatırlayın.'),
(55, 2, 48, 'Ve öyle bir günden sakının ki, o gün hiç kimse, kimsenin cezasını çekmez (borcunu ödemez); kimseden şefaat (aracılık, iltimas) da kabul edilmez; kimseden fidye de alınmaz ve onlara hiçbir yardım yapılmaz.'),
(56, 2, 49, 'Sizi Fir\'avn ailesinden de kurtarmıştık. Hani (onlar), size azabın en kötüsünü reva görüyor, oğullarınızı boğazlayıp, kadınlarınızı sağ bırakıyorlardı ve bunda sizin için Rabbinizden büyük bir imtihan vardı.'),
(57, 2, 50, 'Sizin için denizi yarmıştık, sizi kurtarmış ve Fir\'avn ailesini boğmuştuk; siz de bunu görüyordunuz.'),
(58, 2, 51, 'Musa ile kırk gece için sözleşmiştik, sonra siz onun ardından buzağıyı (tanrı) edinmiştiniz, (kendinize böylece) zulmediyordunuz.'),
(59, 2, 52, 'Bundan sonra da yine belki şükredersiniz diye sizi affetmiştik.'),
(60, 2, 53, 'Yola gelesiniz diye Musa\'ya Kitap ve furkan (gerçekle batılı birbirinden ayıran ölçü) vermiştik.'),
(61, 2, 54, 'Musa kavmine demişti ki: "Ey kavmim, sizler, buzağıyı (tanrı) edinmekle kendinize zulmettiniz; gelin Yaratıcınıza tevbe edin de nefislerinizi öldürün. Bu, Yaratıcınız katında sizin için daha iyidir. (Bu suretle O), sizin tevbenizi kabul buyurmuş olur. Çünkü O, öyle bağışlayıcı, öyle merhametlidir.'),
(62, 2, 55, 'Bir zaman da: "Ey Musa, biz Allah\'ı açıkça görmedikçe sana inanmayız," demiştiniz de derhal sizi yıldırım gürültüsü yakalamıştı; siz de bunu görüyordunuz.'),
(63, 2, 56, 'Sonra belki şükredersiniz diye sizi ölümünüzün ardından tekrar diriltmiştik.'),
(64, 2, 57, 'bulutu üstünüze gölgelik çektik, size kudret helvası ve bıldırcın indirdik: "Size verdiğimiz güzel rızıklardan yeyin," (dedik). Ama onlar bize değil, kendi kendilerine zulmediyorlardı.'),
(65, 2, 58, 'Demiştik ki: "Şu kente girin, oradan dilediğiniz yerde bol bol yeyin; secde ederek kapıdan girin ve "hitta (ya Rabbi, bizi affet)" deyin ki, biz de sizin hatalarınızı bağışlayalım, güzel davrananlara daha fazlasını da veririz.'),
(66, 2, 59, 'Derken o zalimler, onu, kendilerine söylenenden başka bir sözle değiştirdiler. Biz de yaptıkları kötülüklerden dolayı o zulmedenlerin üzerine gökten bir azab indirdik.'),
(67, 2, 60, 'Bir zaman da Musa, kavmi için su istemişti; "Asanla taşa vur," demiştik. Bunun üzerine taştan on iki göze fışkırmıştı. Her bölük, kendi içecekleri pınarı bilmişti: "Allah\'ın rızkından yeyin, için ve yeryüzünde bozgunculuk yaparak (başkalarına) saldırmayın." (demiştik.)'),
(68, 2, 61, 'Hani siz demiştiniz ki: "Ey Musa, biz bir yemeğe dayanamayız, bizim için Rabbine du\'a et de bize yerin bitirdiği sebzesinden, acurundan, sarımsağından, mercimeğinden, soğanından çıkarsın." (Musa): "İyi olanı, daha aşağı olanla mı değiştirmek istiyorsunuz? Bir şehre inin, orada size istediğiniz var," demişti. Üzerlerine alçaklık ve yoksulluk damgası vuruldu; Allah\'ın gazabına uğradılar. Öyle oldu, çünkü onlar, Allah\'ın ayetlerini inkar ediyorlar ve haksız yere peygamberleri öldürüyorlardı. İsyanadaldıkları, sınırı aştıkları için bunu hak ettiler.'),
(69, 2, 62, 'Şüphesiz inananlar; yahudiler, hıristiyanlar ve sabiiler(den) Allah\'a ve ahiret gününe inanan ve iyi iş(ler) yapanlara, Rableri katında mükafat vardır; onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(70, 2, 63, 'Bir zaman da sizin sözünüzü almış, üzerinize dağı kaldırmıştık: "Size verdiğimizi kuvvetle tutun, içinde olanı hatırlayın ki (azabımızdan) korunasınız," (demiştik).'),
(71, 2, 64, 'Ardından yine dönmüştünüz; eğer Allah\'ın size iyiliği ve merhameti olmasaydı, elbette ziyana uğrayanlardan olurdunuz.'),
(72, 2, 65, 'İçinizden, Cumartesi günü (avlanma yasağı)nı çiğneyenleri elbette bilmişsinizdir; işte onlara: "Aşağılık maymunlar olun!" dedik.'),
(73, 2, 66, 'Ve bunu, önündekilere ve ardından geleceklere ibret bir ceza, (Allah\'ın azabından) korunanlara da bir öğüt yaptık.'),
(74, 2, 67, 'Musa, kavmine: "Allah size bir inek kesmenizi emrediyor." demişti. "Bizimle alay mı ediyorsun?" dediler. "cahillerden olmaktan Allah\'a sığınırım!" dedi.'),
(75, 2, 68, 'Bizim için Rabbine du\'a et, onun ne olduğunu bize açıklasın. dediler. Dedi ki: "O diyor ki: O (inek) ne yaşlı, ne körpe, ikisinin ortasında (bir inek)tir! Haydi, size emredileni yapın."'),
(76, 2, 69, 'Dediler ki: "Bizim için Rabbine du\'a et, renginin nasıl olduğunu açıklasın." Dedi: "O diyor ki: "Rengi parlak, sarı bir inektir, bakanlara sevinç verir."'),
(77, 2, 70, 'Bizim için Rabbine du\'a et, onun nasıl bir şey olduğunu bize açıklasın. Zira o inek bize (başka ineklere) benzer geldi. Ama Allah dilerse mutlaka (emredileni yapmağa) yol buluruz. dediler.'),
(78, 2, 71, 'Dedi: "O şöyle diyor: O, henüz boyundurluk altına alınmamış bir inektir. Yeri sürmez, ekin sulamaz. Salma, (çifte koşulmamış) hiç alacası yok." "İşte şimdi gerçeği getirdin" deyip ineği boğazladılar; az daha yapmayacaklardı.'),
(79, 2, 72, 'Hani siz bir adam öldürmüştünüz de onun (katili) hakkında birbirinizle atışmıştınız; oysa Allah, gizlediğinizi ortaya çıkaracaktı.'),
(80, 2, 73, 'Onun için "(ineğin) bir parçasıyla o (öldürülene) vurun." demiştik. İşte Allah böylece ölüleri diriltir, size ayetlerini gösterir ki düşünesiniz.'),
(81, 2, 74, 'Sonra bunun ardından yine kalbleriniz katılaştı; şimdi onlar, taş gibi, hatta daha da katıdır. Çünkü öyle taş var ki, içinden ırmaklar fışkırır; öylesi var ki, çatlar da bağrından su kaynar, öylesi de var ki, Allah korkusundan aşağı düşer. Allah, yaptıklarınızı bilmez değildir.'),
(82, 2, 75, 'Şimdi (ey mü\'minler) siz, bunların size inanmalarını mı umuyorsunuz? Oysa bunlardan bir grup vardı ki, Allah\'ın sözünü işitirlerdi de düşünüp akıl erdirdikten sonra, bile bile onu değiştirirlerdi.'),
(83, 2, 76, 'İnananlara rastladıkları zaman: "İnandık" derler; birbirleriyle yalnız kaldıkları zaman: "Allah\'ın size açtığını onlara söylüyorsunuz ki, onu Rabbiniz katında sizin aleyhinizde delil olarak mı kullansınlar? Aklınızı kullanmıyor musunuz?" derler.'),
(84, 2, 77, 'Bilmiyorlar mı ki, Allah onların gizlediklerini ve açığa vurduklarını biliyor?'),
(85, 2, 78, 'Onların içinde bir de ümmiler var ki, Kitabı bilmezler, bütün bildikleri birtakım kuruntular(yahut kulaktan dolma şeyler)dir; onlar sadece zannediyorlar.'),
(86, 2, 79, 'Vay haline o kimselerin ki, Kitabı elleriyle yazıp, az bir paraya satmak için, "Bu Allah katındandır," derler! Ellerinin yazdığından ötürü vay haline onların! Kazandıklarından ötürü vay haline onların!'),
(87, 2, 80, 'Bir de dediler ki: "Sayılı birkaç gün dışında bize ateş dokunmayacaktır." De ki: "Allah\'tan (bu hususta) bir söz mü aldınız. şayet öyle ise Allah verdiği sözden dönmez-yoksa Allah hakkında bilmediğiniz bir şey mi söylüyorsunuz?'),
(88, 2, 81, 'Evet kim bir günah kazanır da suçu kendisini kuşatmış olursa işte onlar, ateş halkıdır, orada sürekli kalacaklardır.'),
(89, 2, 82, 'İnanıp yararlı işler yapanlara gelince, onlar da cennet halkıdır, orada sürekli kalacaklardır.'),
(90, 2, 83, 'Biz İsrail oğullarından şöyle söz almıştık: "Allah\'tan başkasına kulluk etmeyeceksiniz, anaya-babaya, yakınlara, yetimlere, yoksullara iyilik edeceksiniz. İnsanlara güzel söz söyleyin, namazı kılın, zekatı verin!" Sonra siz, pek azınız hariç, döndünüz; hala da yüz çevirip duruyorsunuz.'),
(91, 2, 84, 'Birbirinizin kanını dökmeyeceksiniz, birbirinizi yurtlarınızdan çıkarmayacaksınız? diye sizden kesin söz almıştık; göre göre bunu kabul etmiştiniz.'),
(92, 2, 85, 'Ama siz yine birbirinizi öldürüyorsunuz, sizden bir grubu yurtlarından çıkarıyorsunuz; onlara karşı günah ve düşmanlık yapmakta birleşiyorsunuz, onları çıkarmak size yasaklanmış iken (çıkarıyorsunuz, sonra da) esir olarak geldiklerinde fidyelerini veriyor (kurtarıyor)sunuz. Yoksa siz Kitabın bir kısmına inanıp bir kısmını inkar mı ediyorsunuz? Sizden bunu yapanın cezası, dünya hayatında rezil olmaktan başka nedir? Kıyamet gününde de (onlar) azabın en şiddetlisine itilirler. Allah yaptıklarınızı bilmez değildir.'),
(93, 2, 86, 'İşte onlar, ahireti verip dünya hayatını satın alan kimselerdir. Onlardan azab hiç hafifletilmez ve onlara hiç yardım edilmez.'),
(94, 2, 87, 'Andolsun, Musa\'ya Kitabı verdik, arkasından peygamberler gönderdik. Meryem oğlu Îsa\'ya da açık deliller verdik ve onu Ruh\'ül-Kudüs (Cebrail) ile destekledik. Ne zaman ki, bir peygamber, size canınızın istemediği bir şey getirdiyse büyüklük taslamadınız mı? Kimini yalanladınız, kimini de öldürüyordunuz?'),
(95, 2, 88, 'Kalblerimiz, perdelidir, dediler. Hayır, ama inkarlarından dolayı Allah onları la\'netlemiştir, artık çok az inanırlar.'),
(96, 2, 89, 'Ne zaman ki, onlara Allah katından, yanlarında bulunan (Tevrat)ı doğrulayıcı bir Kitap (Kur\'an) geldi, daha önce inkar edenlere karşı yardım isteyip dururlarken o bildikleri (Kur\'an) kendilerine gelince onu inkar ettiler; artık Allah\'ın la\'neti, inkarcıların üzerine olsun!'),
(97, 2, 90, 'Allah\'ın, kullarından dilediğine lutfuyla (vahiy) indirmesini çekemeyerek, Allah\'ın indirdiğini inkar etmek için kendilerini ne alçak şeye sattılar da gazab üstüne gazaba uğradılar. İnkar edenler için alçaltıcı bir azab vardır.'),
(98, 2, 91, 'Onlara: "Allah\'ın indirdiğine inanın!" denilse, "Bize indirilene inanırız." derler, ötesini kabul etmezler. Halbuki o, kendi yanlarında bulunanı doğrulayıcı bir gerçektir. De ki: "Gerçekten inanıyor idiyseniz neden daha önce Allah\'ın peygamberlerini öldürüyordunuz?"'),
(99, 2, 92, 'Andolsun Musa, size açık deliller getirmişti, sonra onun ardından tuttunuz buzağıya taptınız; siz öyle zalimlersiniz işte!'),
(100, 2, 93, 'Bir zaman üzerinize Tur(dağın)ı kaldırıp sizden kesin söz almıştık: "Size verdiğimiz şeyi kuvvetle tutun, dinleyin!" (demiştik). "Dinledik ve isyan ettik." dediler. İnkarlarıyla kalblerine buzağı sevgisi içirildi. De ki: "Eğer inanan kimseler iseniz, imanınız size ne kötü şey emrediyor."'),
(101, 2, 94, 'De ki: "Eğer (dilediğiniz gibi) gerçekten Allah katında ahiret yurdu kimsenin değil, yalnız sizin ise, sözünüzde doğru iseniz, haydi ölümü temenni edin!"'),
(102, 2, 95, 'Fakat ellerinin yapıp öne sürdüğü işlerden dolayı ölümü asla istemezler, Allah zalimleri bilir.'),
(103, 2, 96, 'Onları, insanların hayata en düşkünü, ortak koşanlardan daha tutkunu bulursun; her biri, bin yıl yaşatılmasını ister. Oysa yaşatılması, onu azabdan uzaklaştıracak değildir. Allah ne yaptıklarını görüyor.'),
(104, 2, 97, 'De ki: "Allah\'ın izniyle Kur\'an\'ı kendinden öncekini doğrulayıcı ve inananlara yol gösterici ve müjdeci olarak senin kalbine indirdiği için, kim Cebrail\'e düşman olursa,'),
(105, 2, 98, '(Evet) kim Allah\'a, meleklerine, elçilere, Cebrail\'e ve Mikail\'e düşman olursa bilsin ki, Allah da inkar edenlerin düşmanıdır.'),
(106, 2, 99, 'Andolsun, sana apaçık ayetler indirdik, onları yoldan çıkmışlardan başkası inkar etmez.'),
(107, 2, 100, 'Ne zaman bir ahit (andlaşma) yaptılarsa, onlardan bir grup o ahdi bozup atmadı mı? Zaten çokları inanmazlar.'),
(108, 2, 101, 'Allah tarafından kendilerine, yanlarında bulunanı doğrulayıcı bir elçi gelince, Kitap verilmiş olanlardan bir grup, Allah\'ın Kitabını sanki bilmiyorlarmış gibi, sırtlarının arkasına attılar.'),
(109, 2, 102, 'Süleyman\'ın hükümdarlığı hakkında onlar, şeytanların uydurdukları sözlere uydular (Süleyman\'ın, büyü yaparak saltanatını kazandığını söyleyen şeytan ruhlu insanlara uyup, Süleyman\'ın büyücü olduğuna inandılar). Oysa Süleyman (büyü yaparak) küfre gitmemişti. Fakat o şeytanlar küfre gittiler: İnsanlara büyü ve Babil\'de Harut ve Marut adlı melekler(den ilham alan iki kişiy)e indirileni öğretiyorlar. Halbuki onlar: "Biz bir fitneyiz (sizin için bir sınavız), sakın, küfre gitme(yin)!" demedikçe kimseye bir şey öğretmiyorlardı. Fakat bunlar, onlardan, erkekle karısının arasını açacak şeyler öğreniyorlardı. Ama, onlar, Allah\'ın izni olmadan onunla hiç kimseye zarar veremezler. Onlar, kendilerine yarar vereni değil, zarar vereni öğreniyorlardı. Andolsun, onu sat(ıp onunla çıkar sağlay)anın, ahirette bir nasibi olmadığını gayet iyi biliyorlardı. Vicdanlarını sattıkları şey ne kötüdür, keşke (bunu) bilselerdi!'),
(110, 2, 103, 'Eğer onlar inanıp (Allah\'ın azabından) korunmuş olsalardı, elbette Allah katından (verilecek) sevap, (kendileri için) daha hayırlı olurdu. Keşke bilselerdi!'),
(111, 2, 104, 'Ey inananlar, "Ra\'ina (bizi gözet, yahut: kaba söz)" demeyin, "unzurna (bize bak)" deyin ve dinleyin. Kafirler için acı bir azab vardır.'),
(112, 2, 105, 'Nankör olan bazı Kitap ehli kimseler de, müşrikler de size Rabbinizden bir hayır indirilmesini istemezler. Oysa Allah, rahmetini dilediğine tahsis eder, Allah, büyük lutuf sahibidir.'),
(113, 2, 106, 'Biz bir ayeti siler veya unutturursakondan daha iyisini, ya da benzerini getiririz. Allah\'ın her şeye gücü yeter olduğunu bilmedin mi?'),
(114, 2, 107, 'Bilmedin mi ki, göklerin ve yerin mülkü (hükümranlığı, yönetimi, mülkiyeti) yalnız Allah\'ındır. Sizin için Allah\'tan başka ne bir koruyucu, ne de bir yardımcı yoktur.'),
(115, 2, 108, 'Yoksa siz de Elçinizden, daha önce Musa\'dan istendiği gibi bir takım isteklerde bulunmak mı istiyorsunuz? Kim imanı küfürle değiştirirse, şüphesiz (o), dümdüz yolu sapıtmıştır.'),
(116, 2, 109, 'Kitap sahiplerinden çoğu, gerçek kendilerine besbelli olduktan sonra, sırf içlerindeki kıskançlıktan ötürü sizi imanınızdan sonra küfre döndürmek isterler. Allah emrini getirinceye kadar affedin, hoş görün. Şüphesiz Allah, her şeye gücü yetendir.'),
(117, 2, 110, 'Namazı kılın, zekatı verin; kendiniz için yapıp gönderdiğiniz her hayrı, Allah\'ın yanında bulursunuz, Allah yaptıklarınızı görür.'),
(118, 2, 111, 'Yahudi yahut hıristiyan olandan başkası cennete girmeyecek, dediler. Bu, onların kuruntusudur. De ki: "Doğru iseniz, delilinizi getirin."'),
(119, 2, 112, 'Hayır, kim işini güzel yaparak özünü Allah\'a teslim ederse, onun mükafatı, Rabbinin yanındadır. Onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(120, 2, 113, 'Yahudiler: "Hıristiyanlar, bir temel üzerinde değiller," dediler. Hıristiyanlar da: "Yahudiler bir temel üzerinde değiller," dediler. Oysa hepsi de Kitabı okuyorlar. Bilmeyenler de tıpkı onların dedikleri gibi demişlerdi. Artık Allah, ayrılığa düştükleri şey hakkında, kıyamet günü aralarında hüküm verecektir.'),
(121, 2, 114, 'Allah\'ın mescidlerinde, Allah\'ın adının anılmasına engel olan ve onların harabolmasına çalışandan daha zalim kim vardır? Bunların, oralara korka korka girmeleri gerekir (başka türlü girmeğe hakları yoktur). Bunlar için dünyada rezillik, ahirette de büyük azab vardır.'),
(122, 2, 115, 'Doğu da, batı da Allah\'ındır. Nereye dönerseniz Allah\'ın yüzü (zatı) oradadır. Şüphesiz Allah\'(ın rahmeti ve ni\'meti) boldur. O (her şeyi) bilendir.'),
(123, 2, 116, 'Allah, çocuk edindi, dediler. Haşa, O, yücedir. Göklerde ve yerde olanların hepsi O\'nundur, hepsi O\'na boyun eğmiştir.'),
(124, 2, 117, '(O), göklerin ve yerin yaratıcısıdır. Bir şeyi yaratmak istedi mi, ona sadece "ol" der, o da hemen oluverir.'),
(125, 2, 118, 'Bilmeyenler dediler ki: "Allah bizimle konuşmalı, ya da bize bir ayet (mu\'cize) gelmeli değil miydi?" Onlardan öncekiler de onların dedikleri gibi demişlerdi. Kalbleri birbirine benzedi. Gerçekleri iyice bilmek isteyenlere ayetleri açıkladık.'),
(126, 2, 119, 'Doğrusu biz seni, gerçekle, müjdeleyici ve uyarıcı olarak gönderdik. Cehennem halkından sen sorumlu değilsin.'),
(127, 2, 120, 'Sen onların, kendi dinlerine uymadıkça ne yahudiler, ne de hıristiyanlar senden razı olmazlar. "Asıl doğru yol, Allah\'ın yoludur" de. Sana gelen ilimden sonra eğer onların arzularına uyarsan, andolsun ki, Allah\'tan sana ne bir dost, ne de bir yardımcı olmaz.'),
(128, 2, 121, 'Kendilerine verdiğimiz Kitabı, gereğince okuyanlar var ya, işte onlar, ona inanırlar. Onu inkar edenler ise ziyana uğrarlar.'),
(129, 2, 122, 'Ey İsrail oğulları, size verdiğim ni\'meti ve sizi alemlere üstün kılmış olduğumu hatırlayın.'),
(130, 2, 123, 'Ve şu günden sakının ki, kimse kimsenin cezasını çekmez (borcunu ödemez), kimseden fidye kabul edilmez, hiç kimseye şefaat (aracılık, iltimas) fayda vermez, bir taraftan yardım da görmezler.'),
(131, 2, 124, 'Bir zaman Rabbi İbrahim\'i birtakım kelimelerle sınamış, o da onları tamamlayınca: "Ben seni insanlara önder yapacağım" demişti. "Soyumdan da (önderler yap, ya Rabbi!)" dedi. (Rabbi): "zalimlere ahdim ermez (onlar için söz vermem!)" buyurdu.'),
(132, 2, 125, 'Biz Beyt\'i (Ka\'be\'yi) insanlara sevap kazanılacak bir toplantı ve güven yeri yaptık. Siz de İbrahim\'in makamından bir namaz yeri edinin (orada namaz kılın). İbrahim ve İsma\'il\'e: "Tavaf edenler, ibadete kapananlar, rüku ve secde edenler için Ev\'imi temizleyin!" diye emretmiştik.'),
(133, 2, 126, 'İbrahim demişti ki: "Rabbim, bu şehri güvenli bir şehir yap, halkından Allah\'a ve ahiret gününe inananları çeşitli ürünlerle besle!" (Rabbi) buyurdu: "İnkar edeni dahi az bir süre geçindirir, sonra onu cehennem azabına (girmeğe) zorlarım, ne kötü varılacak yerdir orası!"'),
(134, 2, 127, 'İbrahim, İsma\'il\'le beraber Ev\'in temellerini yükseltiyor: "Rabbi\'imiz, bizden kabul buyur, kuşkusuz sen işitensin, bilensin."'),
(135, 2, 128, 'Rabbimiz, bizi sana teslim olanlar yap, neslimizden de sana teslim olan bir ümmet çıkar; bize ibadet yerlerimizi göster, tevbemizi kabul et; zira, tevbeleri kabul eden, çok merhametli olan ancak sensin. Sen!'),
(136, 2, 129, 'Rabbimiz, onlara kendi içlerinden, senin ayetlerini kendilerine okuyacak, onlara Kitabı ve hikmeti öğretecek, onları temizleyecek bir elçi gönder. Her zaman üstün gelen, her şeyi yerli yerince yapan yalnız sensin, sen!'),
(137, 2, 130, 'Nefsini aşağılık yapan (beyinsiz)den başka, kim İbrahim dininden yüz çevirir? Andolsun ki, biz onu dünyada beğenip seçmiştik, ahirette de, o iyilerdendir.'),
(138, 2, 131, 'Rabbi ona: "İslam ol!" demişti, "Alemlerin Rabbine teslim oldum." dedi.'),
(139, 2, 132, 'İbrahim de bunu kendi oğullarına vasiyyet etti, Ya\'kub da: "Oğullarım, Allah, sizin için o dini seçti, bundan dolayı sadece müslümanlar olarak ölünüz." (dedi).'),
(140, 2, 133, 'Yoksa siz, Ya\'kub\'a ölüm (hali) geldiği zaman orada mı idiniz? O zaman (Ya\'kub), oğullarına: "Benden sonra neye kulluk edeceksiniz?" demişti. "Senin tanrın ve ataların İbrahim, İsma\'il ve İshak\'ın tanrısı olan tek Tanrı\'ya kulluk edeceğiz, biz O\'na teslim olanlarız." dediler.'),
(141, 2, 134, 'Onlar bir ümmetti, gelip geçti. Onların kazandıkları kendilerine, sizin kazandıklarınız size aittir. Siz onların yaptıklarından sorulmazsınız.'),
(142, 2, 135, 'Yahudi veya hıristiyan olun ki, doğru yolu bulasınız. dediler. De ki: "Hayır, biz dosdoğru İbrahim dinine (uyarız). O, (Allah\'a) ortak koşanlardan değildi."'),
(143, 2, 136, 'Allah\'a, bize indirilene, İbrahim\'e, İsma\'il\'e, İshak\'a, Ya\'kub\'a ve sıbt(torun kabile)lere indirilene, Musa ve Îsa\'ya verilene ve (diğer) peygamberlere Rabbleri tarafından verilene inandık, onlar arasında bir ayırım yapmayız, biz Allah\'a teslim olanlarız. deyin.'),
(144, 2, 137, 'Eğer onlar da sizin inandığınız gibi inanırlarsa doğru yolu bulmuş olurlar; ama dönerlerse mutlaka anlaşmazlık içine düşerler. Onlara karşı Allah sana yeter. O, işitendir, bilendir.'),
(145, 2, 138, 'Allah\'ın boyası (ile boyan). Allah\'ın boyasından daha güzel boyası olan kimdir? Biz ancak O\'na kulluk ederiz.'),
(146, 2, 139, 'Söyle (onlara): "Allah, bizim de Rabbimiz, sizin de Rabbiniz iken, O\'nun hakkında bizimle tartışıyor musunuz? Bizim yaptıklarımız bize, sizin yaptıklarınız size aittir. Biz O\'na gönülden bağlananlarız."'),
(147, 2, 140, 'Yoksa siz, İbrahim, İsma\'il, İshak, Ya\'kub ve sıbt(torun kabile)lerin, yahudi, yahut hıristiyan olduklarını mı söylüyorsunuz? De ki: "Siz mi daha iyi bilirsiniz, yoksa Allah mı?" Allah tarafından bildiği bir (gerçeğin) tanıklığını gizleyenden daha zalim kim olabilir? Allah yaptıklarınızdan gafil değildir.'),
(148, 2, 141, 'Onlar bir ümmetti, gelip geçti. Onların kazandıkları kendilerine, sizin kazandıklarınız size aittir. Siz onların yaptıklarından sorulmazsınız.'),
(149, 2, 142, 'İnsanlardan bazı beyinsizler: "Onları, üzerinde bulundukları kıbleden çeviren nedir?" diyecekler. De ki: "Doğu da batı da Allah\'ındır. O, dilediğini doğru yola iletir."'),
(150, 2, 143, 'Böylece sizi orta bir ümmet yaptık ki, insanlara şahid olasınız. Elçi de size şahid olsun. Biz, Elçi\'ye uyanı, ökçesi üzerinde geriye dönenden ayıralım diye, eskiden yöneldiğin Ka\'be\'yi kıble yaptık. Bu, Allah\'ın yol gösterdiği kimselerden başkasına elbette ağır gelir. Allah sizin imanınızı zayi edecek değildir. Şüphesiz Allah, insanlara şefkatli, merhametlidir.'),
(151, 2, 144, '(Ey Muhammed), biz senin yüzünün göğe doğru çevrilip durduğunu (gökten haber beklediğini) görüyoruz. (Merak etme) elbette seni, hoşlanacağın bir kıbleye döndüreceğiz. (Bundan böyle) yüzünü Mescid-i Haram tarafına çevir. Nerede olursanız, yüzlerinizio yöne çevirin. Kitap verilenler, bunun Rableri tarafından bir gerçek olduğunu bilirler. Allah onların yaptıklarından habersiz değildir.'),
(152, 2, 145, 'Sen Kitap verilenlere her türlü ayeti getirsen yine onlar senin kıblene uymazlar; sen de onların kıblesine uyacak değilsin. Onlar birbirlerinin kıblesine de uymazlar. Sana gelen ilimden sonra onların keyiflerine uyarsan, o takdirde sen, mutlaka zalimlerden olursun.'),
(153, 2, 146, 'Kendilerine Kitap verdiklerimiz, onu, oğullarını tanıdıkları gibi tanırlar, ama yine de onlardan bir grup, bile bile gerçeği gizlerler.'),
(154, 2, 147, 'Gerçek, Rabbinden gelendir, artık kuşkulananlardan olma.'),
(155, 2, 148, 'Her ümmetin yöneldiği bir yönü vardır. O halde hayır işlerine koşun; nerede olsanız, Allah sizi bir araya getirir, kuşkusuz Allah, her şeyi yapabilir.'),
(156, 2, 149, 'Nereden (yola) çıkarsan, yüzünü Mescid-i Haram\'a doğru çevir. Bu elbette Rabbinden gelen gerçektir. Allah, yaptıklarınızdan habersiz değildir.'),
(157, 2, 150, 'Nereden (yola) çıkarsan yüzünü Mescid-i Haram\'a doğru çevir, nerede olursanız, yüzünüzü o yana çevirin ki, haksızlardan başka hiç kimsenin, aleyhinizde bir delili olmasın. Onlardan da çekinmeyin, benden çekinin ve (o yana dönün ki) size olan ni\'metimi tamamlayayım, böylece yolu bulmuş olasınız.'),
(158, 2, 151, 'Nitekim kendi içinizden, size ayetlerimizi okuyan, sizi temizleyen, size Kitabı, hikmeti ve bilmediklerinizi öğreten bir Elçi gönderdik.'),
(159, 2, 152, 'Öyle ise beni anın ki, ben de sizi anayım; bana şükredin, nankörlük etmeyin.'),
(160, 2, 153, 'Ey inananlar, sabır ve namazla (Allah\'tan) yardım isteyin, muhakkak ki Allah, sabredenlerle beraberdir.'),
(161, 2, 154, 'Allah yolunda öldürülenlere, "ölüler" demeyin; hayır, onlar diridirler, ama siz farkında olmazsınız.'),
(162, 2, 155, 'Andolsun, sizi korku, açlık, mallar(ınız)dan canlar(ınız)dan ve ürünler(iniz)den eksiltmek gibi şeylerle deneriz; sabredenleri müjdele.'),
(163, 2, 156, 'Ki onlara bir bela eriştiği zaman: "Biz Allah içiniz ve biz O\'na döneceğiz," derler.'),
(164, 2, 157, 'İşte Rablerinden bağışlamalar ve rahmet hep onlaradır ve doğru yolu bulanlar da onlardır.'),
(165, 2, 158, 'Safa ile Merve Allah\'ın nişanlarındandır. Kim Ev\'i hacceder, ya da ömre yaparsa onları tavaf etmesinde kendisine bir günah yoktur. Kim kendiliğinden bir iyilik yaparsa bilsin ki, Allah karşılığını verir, (yaptığını) bilir.'),
(166, 2, 159, 'İndirdiğimiz açık delilleri ve hidayeti biz Kitapta insanlara açıkça belirttikten sonra gizleyenler (var ya), işte onlara hem Allah la\'net eder, hem bütün la\'net edebilenler la\'net eder.'),
(167, 2, 160, 'Ancak tevbe edip uslananlar ve (gerçeği) açıklayanlar başka. Onları bağışlarım. Çünkü ben tevbeyi çok kabul edenim, çok esirgeyenim.'),
(168, 2, 161, 'Ama ayetlerimizi inkar etmiş ve kafir olarak ölmüş olanlar, işte Allah\'ın, meleklerin ve tüm insanların la\'neti onların üstünedir.'),
(169, 2, 162, 'Ebedi la\'net içinde kalırlar. Ne kendilerinden azab hafifletilir, ne de onlara fırsat verilir.'),
(170, 2, 163, 'Tanrınız bir tek Tanrı\'dır, O\'ndan başka tanrı yoktur, O Rahman\'dır, Rahim\'dir.'),
(171, 2, 164, 'Şüphesiz göklerin ve yerin yaratılışında, gece ve gündüzün değişmesinde, insanların faydasına olan şeyleri denizde taşıyıp giden gemilerde, Allah\'ın gökten su indirip onunla ölmüş olan yeri dirilterek üzerine her çeşit canlıyı yaymasında, rüzgarlarıve yer ile gök arasında emre hazır bekleyen bulutları evirip çevirmesinde elbette düşünen bir topluluk için (Allah\'ın varlığına ve birliğine) deliller vardır.'),
(172, 2, 165, 'İnsanlardan kimi, Allah\'tan başka eşler tutar, Allah\'ı sever gibi onları severler. İnananlar ise en çok Allah\'ı severler. Zulmedenler, azabı gördükleri zaman bütün kuvvetin Allah\'a aidolduğunu ve Allah\'ın azabının çetin olduğunu anlayacaklarını keşkebilselerdi!'),
(173, 2, 166, 'İşte uyulanlar, uyanlardan uzak durdular; azabı gördüler, aralarındaki bağlar kesildi.'),
(174, 2, 167, 'Uyanlar, şöyle dediler; "Ah keşke bir daha dünyaya gitmemiz mümkün olsaydı da şimdi onların bizden uzak durdukları gibi biz de onlardan uzak dursaydık!" Böylece Allah, onlara işledikleri bütün fiilleri hasretler (pişmanlık kaynağı olarak) gösterir. Ve onlar, ateşten çıkamazlar.'),
(175, 2, 168, 'Ey insanlar, yeryüzünde bulunan helal ve temiz şeylerden yeyin, şeytanın adımlarını izlemeyin; çünkü o, sizin apaçık düşmanınızdır.'),
(176, 2, 169, 'O size daima kötülük ve çirkin iş (yapmanızı), Allah hakkında bilmediğiniz şeyler söylemenizi emreder.'),
(177, 2, 170, 'Onlara: "Allah\'ın indirdiğine uyun!" dense, "Hayır, biz atalarımızı üzerinde bulduğumuz(yol)a uyarız!" derler. Peki ama, ataları bir şey düşünmeyen, doğru yolu bulamayan kimseler olsalar da mı (atalarının yoluna uyacaklar)?'),
(178, 2, 171, 'O inkar edenler(i Hakk\'a çağıran)ın durumu, tıpkı bağırıp çağırmadan başka bir şey işitmeyen (işittiği sesin manasını anlamayan hayvanlar)a haykıran kimsenin durumu gibidir. (Onlar), sağır, dilsiz ve kördürler, onun için düşünmezler.'),
(179, 2, 172, 'Ey inananlar, size verdiğimiz rızıkların iyilerinden yeyin, Allah\'a tapıyorsanız, O\'na şükredin.'),
(180, 2, 173, 'Allah size leş, kan, domuz eti ve Allah\'tan başkası adına kesileni haram kıldı. Ama kim mecbur kalırsa, (başkasına) saldırmadan ve sınırı aşmadan (bunlardan) yemesinde bir günah yoktur. Muhakkak ki Allah çok bağışlayandır, çok esirgeyendir.'),
(181, 2, 174, 'Allah\'ın indirdiği Kitaptan bir şey gizleyip, onu birkaç paraya satanlar var ya, işte onlar karınlarına ateşten başka bir şey koymuyorlar. Kıyamet günü Allah ne onlara konuşacak ve ne de onları temizleyecektir. Onlar için acı bir azab vardır.'),
(182, 2, 175, 'Onlar hidayet karşılığında sapıklık, mağfiret karşılığında azab satın almışlardır. Onlar ateşe, karşı ne kadar da dayanıklıdırlar(!)'),
(183, 2, 176, '(Onlara) böyle(azab edilecek)dir. Çünkü Allah, Kitabı gerçekle indirmiştir. Kitapta ayrılığa düşenler, elbette derin bir anlaşmazlık içindedirler.'),
(184, 2, 177, 'Yüzlerinizi doğu ve batı tarafına çevirmeniz iyilik değildir. Asıl iyilik, o(kimsenin iyiliği)dir ki, Allah\'a, ahiret gününe, meleklere, Kitaba ve peygamberlere inandı; sevdiği malını yakınlara, yetimlere, yoksullara, yolda kalmışlara, dilencilere ve boyunduruk altında bulunan(köle ve esir)lere verdi; namazı kıldı, zekatı verdi. Andlaşma yaptıkları zaman andlaşmalarını yerine getirenler; sıkıntı, hastalık ve savaş zamanlarında sabredenler, işte doğru olanlar onlardır, (Allah\'ın azabından) korunanlar da onlardır.'),
(185, 2, 178, 'Ey inananlar, öldürmelerde kısas size farz kılındı. (Katilin de öldürülmesi gerekir). Hüre hür, köleye köle, kadına kadın. Kardeşi tarafından kısmen affedilen kimse, örfe uyup o(affeden kardeşi)ne güzelce (diyeti) ödemelidir! Bu, Rabbiniz tarafından bir hafifletme ve acımadır. Kim bundan sonra da saldırıya kalkarsa artık onun için acı bir azab vardır.'),
(186, 2, 179, 'Ey akıl sahipleri, kısasta sizin için hayat vardır, böylece korunursunuz.'),
(187, 2, 180, 'Birinize ölüm geldiği zaman, eğer bir hayır (mal) bırakacaksa, anaya, babaya, yakınlara uygun bir biçimde vasiyyet etmek, korunanlar üzerine bir borçtur.'),
(188, 2, 181, 'Kim işittikten sonra vasiyyeti değiştirirse, günahı, onu değiştirenlerin boynunadır. Şüphesiz Allah işitendir, bilendir.'),
(189, 2, 182, 'Kim de vasiyyet edenin bir hata veya günah işlemesinden korkar da (tarafların) aralarını düzeltirse, ona günah yoktur, Allah bağışlayandır, esirgeyendir.'),
(190, 2, 183, 'Ey inananlar, sizden öncekilere yazıldığı gibi (günahlardan) korunmanız için sizin üzerinize de oruç yazıldı;'),
(191, 2, 184, 'Sayılı günler olarak. Sizden kim hasta veya seferde olursa tutamadığı günler sayısınca başka günlerde (tutar). Oruca (güç) dayananların fidye vermesi, bir yoksulu doyurması lazımdır. Bununla beraber gönül isteğiyle kim bir iyilik yapar(oruç tutar)sa o, kendisi için iyidir. Bilirseniz oruç tutmanız, sizin için daha hayırlıdır.'),
(192, 2, 185, 'Ramazan ayı, insanlara yol gösteren, hidayeti, doğruyu ve yanlışı ayırdedip açıklayan Kur\'an\'ın indirildiği aydır. İçinizden kim o aya yetişir(ayı görür)se oruç tutsun. Kim hasta olur, yahut seferde bulunursa tutamadığı günler sayısınca başka günlerde oruç tutsun. Allah sizin için kolaylık ister, güçlük istemez. Sayıyı tamamlamanızı, size doğru yolu gösterdiğinden dolayı Allah\'ı tekbir etmenizi ister. Şükredesiniz diye (size bu kolaylığı gösterir).'),
(193, 2, 186, 'Kullarım, sana benden sorar(lar)sa (söyle): Ben (onlara) yakınım. du\'a eden, bana du\'a ettiği zaman onun du\'asına karşılık veririm. O halde onlar da bana karşılık versin(benim çağrıma uysun)lar, bana inansınlar ki, doğru yolu bulmuş olalar.'),
(194, 2, 187, 'Oruç gecesi, kadınlarınıza yaklaşmak, size helal kılındı. Onlar sizin elbisenizdir, siz de onların elbisesisiniz. Allah, sizin kendinize yazık etmekte olduğunuzu bildi de tevbenizi kabul edip sizi affetti. Artık şimdi onlara yaklaşın ve Allah\'ın sizin için yaz(ıp takdir etmiş ol)duğunu arayın; şafağın beyaz ipliği siyah iplikten ayırdelinceye kadar yeyin, için; sonra ta gece oluncaya dek orucu tamamlayın; mescidlerde ibadete çekilmiş iken kadınlara yaklaşmayın. Bunlar, Allah\'ın (yasak) sınırlarıdır, bunlara yaklaşmayın. Allah, insanlara ayetlerini böyle açıklar ki korunup sakınsınlar.'),
(195, 2, 188, 'Mallarınızı, aranızda batıl (sebepler) ile yemeyin; bile bile insanların mallarından bir kısmını günah bir biçimde yemeniz için onları hakimler(in önün)e atmayın (hakimlere götürmeyin veya onlara rüşvet vermeyin).'),
(196, 2, 189, 'Sana doğan aylardan soruyorlar. De ki: "Onlar, insanlar ve hac için vakit ölçüleridir." Evlere arkalarından girmek iyilik değildir. İyilik, Allah\'tan korkanın iyiliğidir. Evlere kapılarından girin ve Allah\'tan korkun ki, başarıya eresiniz, umduğunuzu bulasınız.'),
(197, 2, 190, 'Sizinle savaşanlarla Allah yolunda savaşın; fakat haksız yere saldırmayın, çünkü Allah, saldırganları sevmez.'),
(198, 2, 191, 'Onları nerede yakalarsanız öldürün, onların sizi çıkardıkları yer(Mekke)den size de onları çıkarın! Fitne (baskı yapmak), adam öldürmekten daha kötüdür. Mescid-i Haram\'da onlarla savaşmayın ki, onlar da sizinle orada savaşmasınlar. Fakat onlar sizinle savaşırlarsa, hemen onları öldürün; kafirlerin cezası böyledir.'),
(199, 2, 192, 'Eğer onlar (saldırılarına) son verirlerse, Allah bağışlayandır, esirgeyendir.'),
(200, 2, 193, 'Onlarla savaşın ki, fitne (baskı) ortadan kalksın, din yalnız Allah\'ın dini olsun. (Yalnız O\'na tapılsın) Eğer (saldırılarına) son verirlerse artık zalimlerden başkasına düşmanlık olmaz.'),
(201, 2, 194, 'Haram ayı, haram aya karşılıktır. Hürmetler, karşılıklıdır. Kim size saldırırsa, onun size saldırdığı kadar siz de ona saldırın; Allah\'tan korkun, bilin ki Allah (günahlardan) korunanlarla beraberdir.'),
(202, 2, 195, '(Mallarınızı) Allah yolunda harcayın, kendi ellerinizle kendinizi tehlikeye atmayın, iyilik edin, doğrusu Allah iyilik edenleri sever.'),
(203, 2, 196, 'Allah için haccı ve ömreyi tamamlayın. Eğer (engellenmiş olursanız kolayınıza gelen kurbanı (gönderin); kurban yerine varıncaya kadar başlarınızı tıraş etmeyin. İçinizden hasta olan, ya da başından bir rahatsızlığı bulunan (bundan ötürü tıraş olmak zorunda kalan) kimse, oruçtan, sadakadan veya kurbandan (biriyle) fidye (versin). Güvene kavuştuğunuzda, hac (zamanın)a kadar ömre ile faydalanmak isteyen kimse, kolayına gelen kurbanı keser. (Kurban) Bulamayan kimse üç gün hacda, yedi gün de döndüğünüz zaman (olmak üzere) tam on gün oruç tutar. Bu, ailesi Mescid-i Haram (civarın)da oturmayanlar içindir. Allah\'tan korkun ve Allah\'ın cezasının çetin olduğunu bilin.'),
(204, 2, 197, 'Hac, bilinen aylardadır. Kim o aylarda (ihrama girerek) haccı (kendisine) gerekli kılarsa bilsin ki, hacda kadına yaklaşmak, günaha sapmak, kavga etmek yoktur. Allah, yaptığınız her iyiliği bilir. Yanınıza azık alın (da açlıktan korunun), azığın en iyisi korunmadır. Ey akıl sahipleri benden korunun!'),
(205, 2, 198, 'Rabbinizin lutuf ve keremini aramanızda sizin için bir günah yoktur. Arafat(taki duruş)tan ayrılıp (Müzdelife\'ye) akın edince Meş\'ar-i haram\'da Allah\'ı anın, O\'nun size gösterdiği biçimde O\'nu anın. O\'nun yol göstermesinden önce siz, sapıklardan idiniz.'),
(206, 2, 199, 'Sonra insanların akın akın döndüğü yerden siz de akın edin ve Allah\'tan mağfiret dileyin, şüphesiz Allah bağışlayandır, esirgeyendir.'),
(207, 2, 200, 'Hac ibadetlerinizi bitirince atalarınızı andığınız gibi hatta, daha kuvvetli bir anışla Allah\'ı anın. İnsanlardan kimi "Rabbimiz bize dünyada ver!" der; onun ahirette bir payı yoktur.'),
(208, 2, 201, 'Onlardan kimi de: "Rabbimiz, bize dünyada da güzellik ver, ahirette de güzellik ver, bizi ateş azabından koru!" der.'),
(209, 2, 202, 'İşte onlara, kazandıklarından bir pay vardır. Allah, hesabı çabuk görendir.'),
(210, 2, 203, 'Sayılı günlerde Allah\'ı anın (tekbir alın). Kim hemen iki gün içinde (Mina\'dan Mekke\'ye) dönerse ona günah yoktur. Kim geri kalırsa korunduğu takdirde ona da günah yoktur. Allah\'tan korkun ve O\'nun huzuruna toplanacağınızı bilin.'),
(211, 2, 204, 'İnsanlardan öylesi var ki, dünya hayatına dair sözü, senin hoşuna gider. Kalbinde olan (samimi düşüncelerini söylediğin)e Allah\'ı şahid tutar. Oysa o, hasımların en yamanıdır.'),
(212, 2, 205, 'Dönüp gitti mi (veya iş başına geçti mi) yeryüzünde bozgunculuk yapmağa, ekin ve nesli yok etmeğe çalışır; Allah da bozgunculuğu sevmez.'),
(213, 2, 206, 'Ona: "Allah\'tan kork!" dense gururu, kendisini günaha sürükler. Artık ona cehennem yetişir; ne kötü bir yataktır o!..'),
(214, 2, 207, 'İnsanlardan öylesi var ki, kendisini Allah\'ın rızasın(ı kazanmay)a satar. Allah da kullar(ın)a çok şefkatlidir.'),
(215, 2, 208, 'Ey inananlar, hepiniz birlikte islama (veya barışa) girin, şeytanın adımlarını izlemeyin, çünkü o size apaçık düşmandır.'),
(216, 2, 209, 'Size açık açık deliller geldikten sonra yine (hak yoldan) kayarsanız, bilin ki Allah daima üstündür, hüküm ve hikmet sahibidir.'),
(217, 2, 210, 'Onlar, ille buluttan gölgeler içinde Allah\'ın ve meleklerin gelmesini ve işin bitirilmesini mi bekliyorlar? Halbuki bütün işler tekrar Allah\'a döndürülüp götürülecektir.'),
(218, 2, 211, 'İsrail oğullarına sor; onlara nice açık ayetler verdik. Kim, Allah\'ın kendisine gelen ni\'metini değiştirirse bilsin ki, Allah\'ın cezası çetindir.'),
(219, 2, 212, 'İnkar edenlere dünya hayatı süslü gösterildi; (onlar) inananlarla alay ederler. Oysa korunanlar, kıyamet gününde onlardan üstündürler. Allah, dilediğine hesapsız rızık verir.'),
(220, 2, 213, 'İnsanlar bir tek ümmet idi. Sonra Allah, peygamberleri, müjdeciler ve uyarıcılar olarak gönderdi; onlarla beraber, anlaşmazlığa düştükleri konularda insanlar arasında hükmetmek üzere, içinde gerçekleri taşıyan Kitabı indirdi. Kendilerine Kitap verilmiş olanlar, kendilerine açık deliller geldikten sonra, sırf aralarındaki kıskançlıktan ötürü o(Kitap hakkı)nda anlaşmazlığa düştü(ler). Bunun üzerine Allah, kendi izniyle inananları, onların üzerinde ayrılığa düştükleri gerçeğe iletti. Allah, dilediğini doğru yola iletir.'),
(221, 2, 214, 'Yoksa siz, sizden önce geçenlerin durumu başınıza gelmeden cennete gireceğinizi mi sandınız? Onlara öyle yoksulluk ve sıkıntı dokunmuştu, öyle sarsılmışlardı ki, nihayet peygamber ve onunla birlikte inananlar: "Allah\'ın yardımı ne zaman?" diyecek olmuşlardı. İyi bilin ki, Allah\'ın yardımı yakındır.'),
(222, 2, 215, 'Sana (Allah yolunda) ne harcayacaklarını soruyorlar. De ki: "Verdiğiniz hayır (mal), ana-baba, yakınlar, öksüzler, yoksullar ve yolda kalmış(lar) içindir. Yaptığınız her hayrı muhakkak Allah bilir.'),
(223, 2, 216, 'Hoşunuza gitmese de size savaş yazıldı (farz kılındı). Bazen hoşlanmadığınız bir şey, hakkınızda iyi olabilir ve hoşlandığınız bir şey de hakkınızda kötü olabilir. Allah bilir, siz bilmezsiniz.'),
(224, 2, 217, 'Sana haram ayında savaşmaktan soruyorlar. De ki: "Onda savaş, büyük bir günahtır. Fakat Allah yoluna engel olmak, Allah\'a ve Mescid-i Haram\'a karşı nankörlük etmek, halkını ondan (Mekke\'den) sürüp çıkarmak, Allah yanında daha büyük bir günahtır. Fitne (baskı yapmak, adam) öldürmekten daha büyük(bir günah)tır". Onlar yapabilseler sizi dininizden döndürünceye kadar sizinle savaşmaya devam ederler. Sizden kim dininden döner ve kafir olarak ölürse, işte onların bütün yaptıkları dünyada da, ahirette de boşa çıkmıştır ve onlar, ateş halkıdır, orada sürekli kalacaklardır.'),
(225, 2, 218, 'Onlar ki inandılar, göç ettiler, Allah yolunda savaştılar; işte onlar, Allah\'ın rahmetini umarlar. Allah, çok bağışlayan, çok merhamet edendir.'),
(226, 2, 219, 'Sana şaraptan ve kumardan soruyorlar. De ki; "O ikisinde büyük günah ve insanlara bazı yararlar vardır. Fakat onların günahı yararından büyüktür." Ve sana Allah yolunda ne vereceklerini soruyorlar. De ki; "Af (yani ihtiyaçlarınızdan fazlasını veya helal ve güzel olan şeyleri verin!)" Allah size ayetleri böyle açıklıyor ki düşünesiniz:'),
(227, 2, 220, 'Dünya ve ahiret hakkında(ki işleri düşünesiniz). Ve sana öksüzlerden soruyarlar. De ki: "Onları(n durumlarını) düzeltmek hayırlıdır. Eğer onlara karışır(onlarla bir arada yaşar)sanız (onlar) sizin kardeşlerinizdir. Allah, bozanı düzeltenden ayırır. Allah dileseydi sizi zora sokardı. Şüphesiz Allah daima üstündür, hüküm ve hikmet sahibidir.'),
(228, 2, 221, 'Allah\'a ortak koşan kadınlarla, onlar inanıncaya kadar, evlenmeyin. (Allah\'a ortak koşan hür kadın), hoşunuza gitse dahi, inanan bir cariye, ortak koşan (hür) kadından iyidir. Ortak koşan erkekler de inanıncaya kadar, onları (kadınlarınızla) evlendirmeyin. (Allah\'a ortak koşan hür erkek) hoşunuza gitse dahi, inanan bir köle, ortak koşan (hür) adamdan iyidir. (Zira) onlar ateşe çağırıyorlar. Allah ise izniyle cennete ve mağfirete çağırıyor. İnsanlara ayetlerini açıklıyor ki öğüt alsınlar.'),
(229, 2, 222, 'Sana adet görmeden soruyorlar. De ki: "O eziyettir." Adet halinde kadınlardan çekilin, temizleninceye kadar onlara yaklaşmayın. Temizlendikleri zaman Allah\'ın emrettiği yerden onlara varın. Allah tevbe edenleri sever, temizlenenleri sever.'),
(230, 2, 223, 'Kadınlarınız sizin tarlanızdır. Tarlanıza dilediğiniz biçimde varın. Kendiniz için ileriye hazırlık yapın ve mutlaka Allah\'a kavuşacağınızı bilin. İnananları müjdele.'),
(231, 2, 224, 'Allah\'ı, yemin(ettiğiniz iş)lerinize (yani) iyilik etmenize, (kötülüklerden) korunmanıza ve insanların arasını düzeltmenize engel yapmayın. Allah, işitendir, bilendir.'),
(232, 2, 225, 'Allah sizi, yaptığınız kasıtsız yeminlerinizden sorumlu tutmaz; fakat kalblerinizin kazandığı (bile bile yaptığınız) yeminlerden sorumlu tutar. Allah bağışlayandır, halimdir.'),
(233, 2, 226, 'Kadınlarına yaklaşmamağa yemin edenler için ancak dört ay bekleme (hakkı) vardır. Eğer (o süre) içinde dönerlerse Allah bağışlayan, merhamet edendir.'),
(234, 2, 227, 'Eğer boşamağa kesin karar verirlerse, şüphesiz Allah işitendir, bilendir.'),
(235, 2, 228, 'Boşanmış kadınlar, üç kur\'(üç adet veya üç temizlik süresi bekleyip) kendilerini gözetlerler (hamile olup olmadıklarına bakarlar). Eğer Allah\'a ve ahiret gününe inanıyorlarsa, Allah\'ın kendi rahimlerinde yarattığını gizlemeleri (karınlardında çocuk bulunduğunu saklamaları) kendilerine helal olmaz. Kocaları da bu arada barışmak isterlerse, onları geri almağa daha çok hak sahibidirler. Erkeklerin kadınlar üzerinde bulunan hakları gibi, kadınların da erkekler üzerinde hakları vardır. Erkeklerin, kadınlar üzerinde(ki hakları), bir derece fazladır. Allah azizdir, hakimdir.'),
(236, 2, 229, 'Boşama iki defadır. (Bundan sonra kadını) ya iyilikle tutmak, ya da güzelce salıvermek (lazım)dır. Onlara verdiklerinizden bir şey geri almanız, size helal değildir. Şayet erkek ve kadın, Allah\'ın sınırlarında duramayacaklarından korkarlarsa başka. Eğer erkek ve kadının, Allah\'ın sınırlarında duramayacaklarından korkarsanız, o zaman kadının (ayrılmak için) verdiği fidye(hakkından vazgeçmesin)de ikisine de bir günah yoktur. İşte bunlar Allah\'ın sınırlarıdır, sakın bunları aşmayın. Kim(ler) Allah\'ın sınırlarını aşarsa işte onlar zalimlerdir.'),
(237, 2, 230, 'Erkek yine boşarsa, artık bundan sonra kadın, başka bir kocaya varmadan kendisine helal olmaz. O (vardığı adam) da bunu boşarsa, Allah\'ın sınırları içinde duracaklarına inandıkları takdirde (eski karı kocanın) tekrar birbirlerine dönmelerinde kendilerine bir günah yoktur. İşte bunlar Allah\'ın sınırlarıdır. (Allah) bunları, bilen bir toplum için açıklamaktadır.'),
(238, 2, 231, 'Kadınları boşadığınız zaman, bekleme sürelerini bitirdiler mi, ya onları iyilikle tutun, ya da iyilikle bırakın; haklarına tecavüz edip zarar vermek için onları (yanınızda) tutmayın. Kim bunu yaparsa kendine yazık etmiş olur. Allah\'ın ayetlerini eğlence yerine koymayın; Allah\'ın size olan ni\'metini ve size öğüt vermek için Kitap ve Hikmet\'ten size indirdiklerini düşünün, Allah\'tan korkun ve bilin ki, Allah her şeyi bilir.'),
(239, 2, 232, 'Kadınları boşadığınız zaman bekleme sürelerini bitirdiler mi, kendi aralarında güzelce anlaştıkları takdirde, (eski) kocalarıyle evlenmelerine engel olmayın. Bu, içinizden Allah\'a ve ahiret gününe inanan kimseye verilen öğüttür. Bu, sizin için daha iyi ve daha temizdir. Allah bilir, siz bilmezsiniz.'),
(240, 2, 233, 'Anneler, çocuklarını -emzirmeyi tamamlamak isteyen kimse için- tam iki yıl emzirirler. Onların uygun biçimde yiyeceğini ve giyeceğini sağlamak, çocuğun babasına aittir. Herkes ancak gücü ölçüsünde bir şeyle yükümlü tutulur. Ne anne çocuğu yüzünden, ne de çocuğun aidolduğu baba, çocuğu yüzünden zarara sokulmasın. mirasçının da aynı şeyi yapması gerekir. Eğer (ana-baba), anlaşıp danışarak (çocuğu) sütten kesmek isterlerse, kendilerine günah yoktur. Çocuklarınızı (sütannesi tutup) emzirtmek isterseniz, verdiğiniz(ücret)i güzelce verdikten sonra yine üzerinize bir günah yoktur. Allah\'tan korkun ve bilin ki, Allah, yaptığınız her şeyi görmektedir.'),
(241, 2, 234, 'İçinizden ölenlerin, geriye bıraktıkları eşleri, dört ay on gün (bekleyip) kendilerini gözetlerler. Sürelerini bitirince artık kendileri için uygun olanı yapmalarında size bir günah yoktur. Allah yaptıklarınızı haber alır.'),
(242, 2, 235, 'Böyle (iddetini bekleyen) kadınlara evlenme isteğinizi üstü kapalı biçimde bildirmenizden, yahut içinizde tutmanızdan dolayı size bir günah yoktur. (Çünkü) Allah, sizin onları anacağınızı bilmektedir. Sakın (kapalı evlenme teklifi sırasında), iyi sözsöylemeniz dışında, onlarla bir gizli(buluşma)ya sözleşmeyin ve farz olan bekleme süresi dolmadan nikah bağını bağlamağa kalkmayın ve bilin ki, Allah içinizden geçeni bilir. O\'ndan sakının ve yine bilin ki, Allah bağışlayandır, halimdir (ceza vermekte aceleci değildir).'),
(243, 2, 236, 'Henüz dokunmadan, ya da mehir kesmeden kadınları boşarsınız size bir günah yoktur. Ancak onları faydalandırın (bir miktar bir şey verin). Eli geniş olan, kendi gücü nisbetinde, eli dar olan da kendi kaderince güzel bir şekilde faydalandırmalı(herkesgücü ölçüsünde bir şey vermeli)dir. Bu, iyilik edenlerin üzerine bir borçtur.'),
(244, 2, 237, 'Bir mehir kestiğiniz takdirde, henüz dokunmadan onları boşamışsanız, kestiğinizin yarısını (verin). Ancak kadınlar vazgeçer, yahut nikah bağı elinde bulunan (erkek) vazgeçerse başka. (Erkekler,) Sizin affetmeniz (müsamaha gösterip mehrin tümünü vermeniz) takvaya daha yakındır. Aranızda birbirinize iyilik etmeyi unutmayın. Şüphesiz Allah, yaptıklarınızı görür.'),
(245, 2, 238, 'Namazları ve orta namazı koruyun, gönülden bağlılık ve saygı ile Allah\'ın huzuruna durun.'),
(246, 2, 239, 'Eğer (bir tehlikeden) korkarsanız, yaya, yahut binmiş olarak kılın; güvene kavuştuğunuz zaman, bilmediğiniz şeyleri size öğrettiği şekilde Allah\'ı anın.'),
(247, 2, 240, 'İçinizden ölüp geriye eşler bırakan(erkek)ler eşlerinin, (evlerinden) çıkarılmadan bir yıla kadar geçimlerinin sağlanmasını vasiyyet etsinler. Şayet kendileri çıkarlarsa, onların, kendileri hakkında uygun olanı yapmalarında sizin için bir günah yoktur. Allah daima üstündür, hüküm ve hikmet sahibidir.'),
(248, 2, 241, 'Boşanmış kadınların uygun olan geçimlerini sağlamak, (Allah\'ın azabından) korunanlar üzerine bir borçtur.'),
(249, 2, 242, 'Düşünesiniz diye Allah size ayetlerini böyle açıklamaktadır.'),
(250, 2, 243, 'Şu, binlerce kişi iken ölüm korkusuyla yurtlarından çıkanları görmedin mi? Allah onlara, "Ölün!" demişti de sonra kendilerini diriltmişti. Şüphesiz Allah, insanlara karşı ikram sahibidir. Ama insanların çoğu şükretmezler.'),
(251, 2, 244, 'Allah yolunda savaşın ve bilin ki Allah, işitendir, bilendir.'),
(252, 2, 245, 'Kimdir o adam ki, Allah\'a güzel bir borç versin de, Allah da ona kat kat fazlasıyla (verdiğini) ödesin! Allah (rızkı) kısar da, açar da. Hep O\'na döndürüleceksiniz.'),
(253, 2, 246, 'Musa\'dan sonra İsrail oğullarının ileri gelenlerini görmedin mi? Peygamberlerine: "Bize bir hükümdar gönder, (onun önderliğinde) Allah yolunda savaşalım." demişlerdi. "Ya size savaş yazılınca savaşmazsanız?" dedi. Dediler: "Bizler neden Allah yolunda savaşmayalım ki; oysa biz yurtlarımızdan ve oğullarımızın arasından çıkarılıp sürüldük?" Fakat kendilerine savaş yazılınca, içlerinden pek azı hariç, yüz çevirdiler. Allah zalimleri bilir.'),
(254, 2, 247, 'Peygamberleri onlara dedi ki: "Allah Talut\'u size hükümdar gönderdi." Dediler ki: "O bizim üzerimize nasıl hükümdar olabilir? Biz hükümdarlığa ondan daha layıkız, ona bol mal da verilmemiştir." Dedi: "Allah onu sizin üzerinize (hükümdar) seçti, onun bilgisini ve gücünü artırdı." Allah mülkünü dilediğine verir. Allah(ın lutfu) geniştir, (O, herşeyi) bilendir.'),
(255, 2, 248, 'Ve peygamberleri onlara dedi ki; "Onun hükümdarlığının alameti, içinde Rabbinizden bir huzur ve Musa ailesinin, Harun ailesinin geriye bıraktığından bir kalıntı bulunan, meleklerin taşıdığı (Allah\'ın Ahid sandığı) Tabut\'un size gelmesidir. Eğer inanıyorsanız bunda sizin için (Talut\'un hükümdarlığına) kesin bir alamet vardır."'),
(256, 2, 249, 'Talut, askerleri(ni) yürütüp (ordugahtan) çıkarınca dedi ki: "Allah sizi bir ırmakla deneyecektir. Kim ondan içerse benden değildir. Ondan (kana kana) tadmayıp sadece eliyle bir avuç alan bendendir." İçlerinden pek azı hariç, hepsi ondan içtiler. Nihayet Talut ve kendisiyle beraber inananlar, ırmağı geçince: "Bugün Calut\'a ve askerlerine karşı bizim gücümüz yok." dediler. Allah\'a kavuşacaklarına kanaat getirenler ise: "Nice az bir topluluk var ki, Allah\'ın izniyle çok topluluğa galib gelmiştir. Allah, sabredenlerle beraberdir." dediler.'),
(257, 2, 250, '(Talut\'un askerleri) Calut ve askerlerinin karşısına çıktıklarında şöyle dediler: "Rabbimiz, üzerimize sabır dök! ayaklarımızı sağlam tut ve o kafir millete karşı bize yardım et!"'),
(258, 2, 251, 'Derken, Allah\'ın izniyle onları bozdular, Davud Calut\'u öldürdü; Allah ona (Davud\'a) hükümdarlık ve hikmet verdi ve ona dilediğini öğretti. Eğer Allah, insanların bir kısmıyle diğerlerini savmasaydı, dünya bozulurdu. Fakat Allah, bütün alemlere karşı lutuf sahibidir.'),
(259, 2, 252, 'Bunlar, Allah\'ın ayetleridir; bunları sana gerçek ile okuyoruz (bunlarla sana gerçekleri açıklıyoruz). Elbette sen gönderilen elçilerdensin.'),
(260, 2, 253, 'İşte o elçilerden kimini kiminden üstün kıldık. Allah onlardan kimine konuştu, kimini de derecelerle yükseltti. Meryem oğlu Îsa\'ya da açık deliller verdik ve onu Ruh\'ül-Kudüs ile destekledik. Allah dileseydi onların arkasından gelen milletler, kendilerine açık deliller gelmiş olduktan sonra birbirlerini öldürmezlerdi. Fakat anlaşmazlığa düştüler, onlardan kimi inandı, kimi de inkar etti. Allah dileseydi, birbirlerini öldürmezlerdi. Ama Allah dilediğini yapar.'),
(261, 2, 254, 'Ey inananlar, ne alışverişin, ne dostluğun ve ne de şefaatin olmadığı gün gelmezden önce, size verdiğimiz rızıktan (Allah için) harcayın. Kafirler, zalimlerin ta kendileridir.'),
(262, 2, 255, 'Allah, ki O\'ndan başka tanrı yoktur, daima diri ve yaratıklarını koruyup yöneticidir. Kendisini ne bir uyuklama, ne de uyku tutmaz. Göklerde ve yerde olanların hepsi O\'nundur. O\'nun izni olmadan kendisinin katında kim şefaat edebilir? Onların önlerinde ve arkalarında olanı bilir. O\'nun ilminden, ancak kendisinin dilediği kadarından başka bir şey kavrayamazlar. O\'nun Kürsüsü, gökleri ve yeri kaplamıştır (O yüce padişah, göklere, yere, bütün kainata hükmetmektedir). Onları koru(yup gözet)mek, kendisine ağır gelmez. O yücedir, büyüktür.'),
(263, 2, 256, 'Dinde zorlama yoktur. Doğruluk, sapıklıktan seçilip belli olmuştur. Kim tağut (şeytan)ı inkar edip Allah\'a inanırsa, muhakkak ki o, kopmayan, sağlam bir kulpa yapışmıştır. Allah işitendir, bilendir.'),
(264, 2, 257, 'Allah, inananların dostudur. Onları karanlıklardan aydınlığa çıkarır. kafirlerin dostları da tağuttur. (O da) onları aydınlıktan karanlıklara çıkarır. Onlar ateş halkıdır, orada ebedi kalacaklardır.'),
(265, 2, 258, 'Allah, kendisine hükümdarlık verdi diye (şımararak) Rabbi hakkında İbrahim\'le tartışanı görmedin mi? İbrahim: "Benim Rabbim O\'dur ki yaşatır, öldürür" demişti. "Ben de yaşatır, öldürürüm" dedi. İbrahim: "Allah, güneşi doğudan getirir, sen de onu batıdan getir!" deyince inkar eden o adam şaşırıp kaldı. Allah, zalim toplumu doğru yola iletmez.'),
(266, 2, 259, 'Yahut şu kimse gibisini (görmedin mi) ki, duvarları, çatıları üstüne yığılmış (alt üst olmuş) ıssız bir kasabaya uğramıştı; "Allah, bunu böyle öldükten sonra nasıl diriltecek?" demişti. Allah da kendisini yüz sene öldürüp sonra diriltti. "Ne kadar kaldın?" dedi. "Bir gün, ya da bir günün birazı kadar kaldım" dedi. (Allah) "Hayır, dedi, yüz yıl kaldın. Yiyecek ve içeceğine bak, bozulmamış. Eşeğine bak, seni insanlar için bir ibret kılalım diye (böyle yaptık). Kemiklere bak, nasıl onları birbiri üstüne koyuyor, sonra onlara et giydiriyoruz!" Bu işler ona açıkça belli olunca: "Allah\'ın herşeye kadir olduğunu biliyorum." dedi.'),
(267, 2, 260, 'İbrahim de bir zaman: "Rabbim, ölüleri nasıl dirilttiğini bana göster!" demişti. (Allah); "İnanmadın mı?" dedi, (İbrahim): "Hayır (inandım), fakat kalbim kuvvet bulsun diye (görmek istiyorum) dedi. "O halde kuşlardan dördünü tut, onları kendine çek (kendine alıştır), sonra her dağın başına onlardan bir parça koy. Sonra onları kendine çağır; koşarak sana gelecekler. Bil ki, Allah daima üstün, hüküm ve hikmet sahibidir" dedi.'),
(268, 2, 261, 'Mallarını Allah yolunda harcayanların durumu, her başağında yüz dane olmak üzere yedi başak veren bir danenin durumu gibidir. Allah dilediğine kat kat verir. Allah(ın lutfu) geniştir, (O) bilendir.'),
(269, 2, 262, 'Mallarını Allah yolunda verip de sonra verdiklerinin ardından başa kakmayan ve eziyet etmeyenlerin, Rableri katında ödülleri vardır. Onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(270, 2, 263, 'Güzel bir söz (söylemek) ve affetmek, peşinden eziyet gelen sadakadan iyidir. Allah, zengindir, halimdir.'),
(271, 2, 264, 'Ey inananlar, insanlara gösteriş için malını verip Allah\'a ve ahiret gününe inanmayan adam gibi, başa kakmak ve eziyet etmekle sadakalarınızı boşa çıkarmayın. Öylesinin durumu, üzerinde biraz toprak bulunan şu kayaya benzer ki, bir sağnak indi de (üstündeki toprağı silip süpürerek) onu sert bir taş halinde bıraktı. (Böyleleri), kazandıklarından bir şey elde edemezler. Allah, kafir toplumu doğru yola iletmez.'),
(272, 2, 265, 'Allah\'ın rızasını kazanmak ve ruhlarındaki(ima)nı kökleştirmek için mallarını harcayanların durumu da tepe üzerinde bulunan bir bahçeye benzer ki, bol yağmur değince ürününü iki kat verdi. Yağmur değmeseydi bile çisinti olurdu. Allah yaptıklarınızı görmektedir.'),
(273, 2, 266, 'Biriniz ister mi ki, kendisinin altından ırmaklar akan, içinde her çeşit meyvası bulunan, hurmalardan ve üzümlerden oluşmuş bir bahçesi olsun; kendisinin üstüne tam ihtiyarlığın çöktüğü, aciz çocuklarının da bulunduğu bir sırada birden ateşli bir kasırga gelsin de bahçeyi yakıp kül etsin? Allah, düşünesiniz diye size ayetleri böyle açıklıyor.'),
(274, 2, 267, 'Ey inananlar, kazandıklarınızın ve yerden sizin için çıkardığımız ni\'metlerin iyilerinden (Allah için) verin, kendiniz (utandığınızdan ve iğrendiğinizden dolayı) göz yummadan alamayacağınız kötü şeyleri sadaka vermeye kalkmayın. Bilin ki Allah zengindir, övülmüştür.'),
(275, 2, 268, 'Şeytan sizi fakirlikle korkutur, (fakir düşeceğinizi söyleyerek sadaka vermekten geri kalmanızı ister) ve size çirkin şeyleri yapmayı emreder. Allah ise size kendi tarafından bağışlama ve lutuf va\'adediyor. Şüphesiz Allah(ın lutfu) geniştir, (O) bilendir.'),
(276, 2, 269, 'Hikmeti dilediğine verir. Hikmet verilen kimseye çok hayır verilmiştir. Bunu ancak sağduyu sahipleri düşünüp anlar(lar).'),
(277, 2, 270, '(Allah için) yaptığınız her harcamayı yahut adadığınız her adağı Allah bilir. Zalimlerin yardımcısı yoktur.'),
(278, 2, 271, 'Sadakaları açıktan verirseniz ne güzel! Eğer onları gizleyerek fakirlere verirseniz bu, sizin için daha iyidir ve sizin günahlarınızdan bir kısmını kapatır. Allah yaptıklarınızı duyar.'),
(279, 2, 272, '(Ey Muhammed) Onları yola iletmek sana düşmez, dilediğini doğru yola ileten Allah\'tır. Verdiğiniz her hayır, kendiniz içindir. Çünkü yalnız Allah\'ın rızasını kazanmak için veriyorsunuz. Verdiğiniz her hayır, size tastamam verilir ve hiç hakkınız yenmez.'),
(280, 2, 273, '(Sadakalar) şu fakirlere mahsustur ki, Allah yolunda kapanıp kalmışlardır. Yeryüzünde gezip dolaşamazlar. Bilmeyen, utangaçlıklarından dolayı onları zengin sanır. Onları simalarından (yüzlerinden) tanırsın. Yüzsüzlük edip insanlardan istemezler. Yaptığınız her hayrı Allah bilir.'),
(281, 2, 274, 'Mallarını gece gündüz, gizli ve açık Allah yolunda verenlerin ödülü Rableri yanındadır. Onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(282, 2, 275, 'Riba yiyenler, ancak şeytanın dokunup çarptığı kimsenin kalktığı gibi kalkarlar. Bu, onların: "Alışveriş de riba gibidir." demelerinden ötürüdür. Oysa Allah, alış-verişi helal, ribayı haram kılmıştır. Kime Rabbi\'nden bir öğüt gelir de (o öğüte uyarakribadan) vazgeçerse, geçmişte olan kendisinindir ve işi de Allah\'a kalmıştır. (Allah onu affeder). Kim tekrar (ribaya) dönerse onlar ateş halkıdır, orada ebedi kalacaklardır.'),
(283, 2, 276, 'Allah, ribayı mahveder, sadakaları artırır. Allah, hiçbir günahkar nankörü sevmez.'),
(284, 2, 277, 'Onlar ki, inandılar, güzel işler yaptılar, namazı kıldılar, zekatı verdiler; işte onların ödüller, Rableri yanındadır. Onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(285, 2, 278, 'Ey inananlar, Allah\'tan korkun, eğer inanıyorsanız ribadan (henüz alınmayıp) geri kalan kısmı bırakın (almayın).'),
(286, 2, 279, 'Eğer böyle yapmazsanız, Allah ve Elçisiyle savaşa girdiğinizi bilin. Tevbe ederseniz, ana malınız sizindir. Ne haksızlık edersiniz, ne de haksızlığa uğratılırsınız.'),
(287, 2, 280, 'Eğer (borçlu) darlık içinde ise, bir kolaylığa çıkıncaya kadar beklemek (lazımdır). Eğer bilirseniz (verdiğiniz borcu, eli darda olan borçluya) sadaka olarak bağışlamanız sizin için daha hayırlıdır.'),
(288, 2, 281, 'Şu günden sakının ki, o gün Allah\'a döndürüleceksiniz, sonra herkese kazandığı tastamam verilecek ve onlara haksızlık edilmeyecektir.'),
(289, 2, 282, 'Ey inananlar, belirli bir süreye kadar birbirinize borç verdiğiniz zaman onu yazın. Aranızda bir yazıcı, adaletle yazsın. Yazıcı, Allah\'ın kendisine öğrettiği şekilde yazmaktan kaçınmasın, yazsın; borçlu olan da yazdırsın, Rabbi olan Allah\'tan korksun, borcundan hiçbir şeyi eksik etmesin. Eğer borçlu olan kimse aklı ermez, yahut zayıf, ya da kendisi yazdıramayacak durumda ise velisi onu adaletle yazdırsın. Erkeklerinizden iki kişiyi de şahid tutun. Eğer iki erkek yoksa razı olduğunuz şahidlerden bir erkek, iki kadın (şahidlik etsin). Ta ki kadınlardan biri şaşırırsa diğeri ona hatırlatsın. Şahidler çağrıldıkları zaman (gelmekten) kaçınmasınlar. Az olsun, çok olsun, onu süresine kadar yazmaktan üşenmeyin. Bu, Allah katında daha adaletli, şahidlik için daha sağlam, kuşkulanmamanız için daha elverişlidir. Yalnız aranızda hemen alıp vereceğiniz peşin ticaret olursa onu yazmamanızdan ötürü üzerinize bir günah yoktur. Alışveriş yaptığınız zaman da şahid tutun. Yazana da, şahide de asla zarar verilmesin. Eğer (bir zarar) yaparsanız, bu kendinize kötülük olur. Allah\'tan korkun, Allah size öğretiyor. Allah herşeyi bilir.'),
(290, 2, 283, 'Ve eğer seferde olur da yazacak birini bulamazsanız, alınan rehinler (yeter). Birbirinize güvenirseniz, kendisine güvenilen kimse emanetini ödesin, Rabbi olan Allah\'tan korksun. Şahidilği gizlemeyin, onu gizleyenin kalbi günahkardır. Allah, yaptıklarınızı bilir.'),
(291, 2, 284, 'Göklerdekilerin ve yerdekilerin hepsi Allah\'ındır. İçlerinizdekini açıklasanız da gizleseniz de Allah sizi onunla hesaba çeker; dilediğini bağışlar, dilediğine azabeder. Allah, herşeye kadirdir.'),
(292, 2, 285, 'Elçi, Rabbinden, kendisine indirilene inandı, mü\'minler de. Hepsi Allah\'a, meleklerine, Kitaplarına ve peygamberlerine inandı. "O\'nun elçilerinden hiçbirini diğerinden ayırdetmeyiz" (dediler). Ve dediler ki: "İşittik, ita\'at ettik! Rabbimiz, (bizi) bağışlamanı dileriz. Dönüş(ümüz) sanadır!"'),
(293, 2, 286, 'Allah, kimseye gücünün üstünde bir şey teklif etmez. Herkesin kazandığı iyilik kendi yararına, kötülük de kendi zararınadır. "Rabbimiz, unutur, ya da yanılırsak bizi sorumlu tutma! Rabbimiz, bize, bizden öncekilere yüklediğin gibi ağır yük yükleme! Rabbimiz, bize gücümüzün yetmediği şeyleri yükleme! Bizi affet, bizi bağışla, bize acı! Sen bizim mevlamız(sahibimiz, efendimiz)sin! kafirler toplumuna karşı bize yardım eyle!"');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Elif lam mim.'),
(295, 3, 2, 'Allah ki, O\'ndan başka tanrı yoktur, daima diri ve (yaratıklarını) koruyup yöneticidir.'),
(296, 3, 3, 'Sana Kitabı gerçek ile ve kendinden öncekini doğrulayıcı olarak indirdi, Tevrat ve İncil\'i de indirmişti.'),
(297, 3, 4, 'Daha önce, insanlara yol gösterici olarak, Furkan(doğruyu ve eğriyi birbirinden ayırdeden Kitap)ı da indirdi, Muhakkak ki Allah\'ın ayetlerini tanımayanlar için çetin bir azab vardır. Allah daima üstündür ve öc alandır.'),
(298, 3, 5, 'Ne yerde, ne de gökte hiçbir şey Allah\'a gizli kalmaz.'),
(299, 3, 6, 'Rahimlerde sizi dilediği gibi şekillendiren O\'dur. O\'ndan başka tanrı yoktur. O azizdir, hüküm ve hikmet sahibidir.'),
(300, 3, 7, 'Kitabı sana O indirdi. Onun bazı ayetleri muhkemdir (ki) onlar Kitabın anasıdır. Diğerleri de müteşabih(birbirine benzeyen, sonucu tam bilinmeyen)dir. Kalblerinde eğrilik olanlar, fitne çıkarmak, uyardığı sonuca uğra(yıp belalarını bul)mak için onun müteşabih ayetlerinin ardına düşerler. Oysa onun te\'vili(uyardığı sonucun ne zaman gerçekleşeceği)ni Allah\'tan başka kimse bilmez. İlimde ileri gidenler: "Ona inandık, hepsi Rabbimiz katındandır" derler. Sağduyu sahiplerinden başkası düşünüp öğüt almaz.'),
(301, 3, 8, '(Onlar derler ki): "Rabbimiz, bizi doğru yola ilettikten sonra kalblerimizi eğriltme, bize katından bir rahmet ver, kuşkusuz sen çok bağış yapansın."'),
(302, 3, 9, 'Rabbimiz, sen mutlaka insanları, asla şüphe olmayan bir günde toplayacaksın. Allah sözünden dönmez.'),
(303, 3, 10, 'İnkar edenler var ya, ne malları, ne de çocukları onlara, Allah\'a karşı hiçbir yarar sağlamaz. Onlar ateşin yakıtıdırlar;'),
(304, 3, 11, 'Fir\'avn ailesinin ve onlardan öncekilerin durumu gibi. Onlar da ayetlerimizi yalanladılar. Allah da onları günahlarıyla yakaladı. Allah\'ın cezası çetindir.'),
(305, 3, 12, 'İnkar edenlere söyle: "Yenileceksiniz ve cehenneme sürüleceksiniz. Orası ne kötü bir döşektir!"'),
(306, 3, 13, 'Karşılaşan şu iki toplulukta sizin için bir ibret vardır: Bir topluluk Allah yolunda çarpışıyordu, öteki de nankördü, onları, gözleriyle kendilerinin iki katı görüyorlardı. Allah dilediğini yardımıyle destekler. Elbette (bunda) gözleri olanlar için bir ibret vardı.'),
(307, 3, 14, 'Kadınlardan, oğullardan, kantarlarca yığılmış altın ve gümüşten, salma atlardan, davarlardan ve ekinlerden gelen zevklere aşırı düşkünlük, insanlara süslü (cazip) gösterildi. Bunlar, sadece dünya hayatının geçimidir. Asıl varılacak güzel yer, Allah\'ın yanındadır.'),
(308, 3, 15, 'De ki: "Bunlardan daha iyisini size söyleyeyim mi? Korunanlar için Rableri katında altlarından ırmaklar akan, içinde sürekli kalacakları cennetler, tertemiz eşler ve Allah\'ın rızası vardır." Allah, kulları görür:'),
(309, 3, 16, 'Rabbimiz, biz inandık, bizim günahlarımızı bağışla, bizi ateş azabından koru! diyenleri,'),
(310, 3, 17, 'Sabredenleri, doğru olanları, huzurunda gönülden boyun büküp divan duranları, Allah için (mal) harcayanları ve seherlerde istiğfar edenleri (Allah\'tan bağışlanmalarını dileyenleri Allah) görmektedir.'),
(311, 3, 18, 'Allah, kendisinden başka tanrı olmadığına şahiddir. Melekler ve ilim sahipleri de adaletle şahiddir (ki O\'ndan başka tanrı yoktur. O), azizdir, hakimdir.'),
(312, 3, 19, 'Allah katında din, İslamdır. Kitap verilmiş olanlar, kendilerine ilim geldikten sonra sırf aralarındaki aşırılık yüzünden ayrılığa düştüler. Kim Allah\'ın ayetlerini inkar ederse, bilsin ki Allah, hesabı çabuk görendir.'),
(313, 3, 20, 'Seninle tartışmaya girişirlerse, de ki: "Ben de özümü Allah\'a teslim ettim bana uyanlar da." Kendilerine Kitap verilenlere ve ümmilere de ki: "Siz de İslam oldunuz mu?" Eğer İslam olurlarsa doğru yolu bulmuşlardır. Yok eğer dönerlerse, sana düşen, sadece duyurmaktır. Allah kulları(nın yaptıklarını) görmektedir.'),
(314, 3, 21, 'Allah\'ın ayetlerini inkar edenler, haksız yere peygamberleri öldürenler, insanlar arasında adaleti emredenleri öldürenler (var ya), onlara, acı bir azabı müjdele!'),
(315, 3, 22, 'Onların yaptıkları, dünyada da, ahirette de boşa çıkmıştır ve onların hiçbir yardımcıları da yoktur.'),
(316, 3, 23, 'Baksana Kitaptan kendilerine bir pay verilmiş olanlar, aralarında hüküm versin diye Allah\'ın Kitabına çağırılıyorlar da sonra onlardan bir topluluk yüz çevirerek dönüyorlar.'),
(317, 3, 24, 'Bu hareketleri, onların: "Bize, ateş sayılı birkaç günden başka dokunmayacak." demelerinden ileri gelmektedir. Uydurdukları şeyler, onları dinlerinde yanıltmıştır...'),
(318, 3, 25, 'Peki, ya kendilerini, hiç şüphe olmayan bir gün için topladığımız ve herkesin kazandığı, kendisine tastamam verilip hiç kimseye haksızlık edilmediği zaman (durumları) nasıl (olacak)?'),
(319, 3, 26, 'De ki: "Allah\'ım, (ey) mülkün sahibi, sen dilediğine mülkü verirsin, dilediğinden mülkü alırsın; dilediğini yükseltirsin, dilediğini alçaltırsın. Hayır (mal), senin elindedir. Sen her şeye kadirsin!"'),
(320, 3, 27, 'Geceyi gündüze sokarsın, gündüzü geceye sokarsın; ölüden diri çıkarırsın, diriden ölü çıkarırsın, dilediğini hesapsız rızıklandırırsın!'),
(321, 3, 28, 'Mü\'minler, inananları bırakıp, kafirleri dost edinmesin. Kim böyle yaparsa Allah ile bir dostluğu kalmaz. Ancak onlardan (gelebilecek tehlikeden) korunmanız başka. (Şerlerinden korunmak için dost gözükebilirsiniz). Allah sizi kendisin(in emirlerine karşı gelmek)den sakındırır. (Sakın hükümlerine aykırı davranarak, düşmanlarını dost tutarak O\'nun gazabına uğramayın. Çünkü) dönüş Allah\'adır.'),
(322, 3, 29, 'De ki: "Göğüslerinizde olanı gizleseniz de, açığa vursanız da Allah onu bilir; göklerde ve yerde olanları da bilir. Allah her şeye kadirdir.'),
(323, 3, 30, 'O gün her nefis, yaptığı her hayrı hazır bulacaktır; işlediği her kötülüğü de. O kötülükle kendisi arasında uzak bir mesafe bulunmasını ister. Allah sizi kendisin(in emirlerine karşı gelmek)den sakındırıyor. Allah, kulllarına şefkatlidir.'),
(324, 3, 31, 'De ki: "Eğer Allah\'ı seviyorsanız bana uyun ki Allah da sizi sevsin ve günahlarınızı bağışlasın. Allah bağışlayandır, esirgeyendir."'),
(325, 3, 32, 'De ki: "Allah\'a ve Elçiye ita\'at edin!" Eğer dönerlerse muhakkak ki Allah, kafirleri sevmez.'),
(326, 3, 33, 'Allah Adem\'i, Nuh\'u, İbrahim ailesini ve İmran ailesini seçip alemlere üstün kıldı.'),
(327, 3, 34, '(Bunlar) Birbirinden türeyen nesil(ler)dir. Allah işitendir, bilendir.'),
(328, 3, 35, 'İmran\'ın karısı demişti ki: "Rabbim, karnımda olanı tam hür olarak sana adadım, benden kabul buyur; şüphesiz sen işitensin, bilensin."'),
(329, 3, 36, 'Onu doğurunca Allah onun ne doğurduğunu bilirken yine şöyle söyledi: "Rabbim, onu kız doğurdum, erkek, kız gibi değildir. Ona Meryem adını verdim. Onu ve soyunu kovulmuş şeytanın şerrinden sana ısmarlıyorum."'),
(330, 3, 37, 'Rabbi onu güzel bir şekilde kabul buyurdu; onu güzel bir bitki gibi yetiştirdi ve Zekeriyya da onun bakımını üstlendi. Zekeriyya, onun yanına, mihraba her girdiğinde yanında bir rızık bulurdu. "Ey Meryem, bu sana nereden?" derdi. (O da) "Bu, Allah katından" derdi. "Allah, dilediğine hesapsız rızık verir."'),
(331, 3, 38, 'Orada Zekeriyya, Rabbine du\'a etmiş: "Rabbim, demişti, bana katından temiz bir nesil ver. Sen du\'ayı işitensin!"'),
(332, 3, 39, 'Zekeriyya, mabedde durmuş namaz kılarken, melekler ona: "Allah sana, Allah\'tan bir kelimeyi doğrulayıcı, efendi, nefsine hakim ve iyilerden bir peygamber olacak Yahya\'yı müjdeler," diye ünlediler.'),
(333, 3, 40, 'Dedi ki: "Rabbim, bana ihtiyarlık gelip çatmış, karım da kısırken benim nasıl oğlum olur?" (Allah): "Öyle (ama) Allah, dilediğini yapar." dedi.'),
(334, 3, 41, 'Rabbim, o halde bana (oğlum olacağına dair) bir alamet ver! dedi. (Allah) buyurdu ki: "Senin alametin üç gün insanlarla işaretten başka türlü konuşamamandır; Rabbini çok an, akşam sabah (O\'nu) tesbih et!"'),
(335, 3, 42, 'Melekler demişti ki: "Ey Meryem, Allah seni seçti, temizledi ve seni dünyaların kadınlarına üstün kıldı."'),
(336, 3, 43, 'Ey Meryem, Rabbine divan dur, secde et ve (O\'nun huzurunda) eğilenlerle beraber eğil!'),
(337, 3, 44, '(Ey Muhammed) Bunlar sana vahyettiğimiz, görünmez alemin haberlerindendir. Meryem\'e hangisi kefil olacak diye (kur\'a) oklarını atarlarken sen onların yanında değildin; birbirleriyle çekiştikleri zaman da sen yanlarında değildin.'),
(338, 3, 45, 'Melekler demişti ki: "Ey Meryem, Allah seni, kendisinden bir kelime ile müjdeliyor: Adı Meryem oğlu Îsa Mesih\'dir; dünyada da, ahirette de yüzde (şerefli) ve (Allah\'a) yakın olanlardandır."'),
(339, 3, 46, 'Beşikte ve yetişkinlikte insanlara konuşacak ve iyilerden olacaktır.'),
(340, 3, 47, 'Dedi ki: "Rabbim, bana bir beşer dokunmamışken benim nasıl çocuğum olur?" "Allah, böylece dilediğini yaratır, dedi, bir şey(in olmasını) istedi mi ona \'ol\' der, o da oluverir."'),
(341, 3, 48, 'Ona Kitabı, Hikmeti, Tevrat\'ı ve İncil\'i öğretecek.'),
(342, 3, 49, 'Onu İsrail oğullarına (şöyle diyen) bir elçi yapacak: Ben size Rabbinizden bir mu\'cize getirdim: Ben çamurdan kuş şeklinde bir şey yaratır, ona üflerim, Allah\'ın izniyle hemen kuş oluverir; körü ve alacalıyı iyileştiririm; Allah\'ın izniyle ölüleri diriltirim; evlerinizde ne yeyip, ne biriktirdiğinizi size haber veririm. Eğer inanıcı iseniz elbette bunda sizin için bir ibret vardır."'),
(343, 3, 50, '(Ben), Benden önce gelen Tevrat\'ı doğrulayıcı olarak ve size haram kılınan bazı şeyleri size helal yapayım diye gönderildim. Size Rabbinizden bir mu\'cize getirdim, Allah\'tan korkun, bana ita\'at edin!'),
(344, 3, 51, 'Allah benim de Rabbim, sizin de Rabbinizdir; O\'na kulluk edin, doğru yol budur.'),
(345, 3, 52, 'Îsa onlardan inkarı sezince: "Allah yolunda kimler bana yardımcı olacak?" dedi. Havariler: "Biz, Allah(yolun)un yardımcılarıyız; Allah\'a inandık, şahid ol, biz müslümanlarız." dediler.'),
(346, 3, 53, 'Rabbimiz, senin indirdiğine inandık, elçiye uyduk; bizi şahidlerle beraber yaz!'),
(347, 3, 54, 'Tuzak kurdular, Allah da onların tuzaklarına karşılık verdi; çünkü Allah, en iyi tuzak kurandır.'),
(348, 3, 55, 'Allah demişti ki: "Ey Îsa, ben senin canını alacağım, seni bana yükselteceğim, seni inkar edenlerden temizleyeceğim ve sana uyanları kıyamet gününe kadar inkar edenlerin üstünde tutacağım. Sonra dönüşünüz bana olacaktır. Ayrılığa düştüğünüz şeyler hakkında aranızda ben hükmedeceğim."'),
(349, 3, 56, 'İnkar edenlere gelince, onlara dünyada da, ahirette de şiddetle azabedeceğim, onların yardımcıları da olmayacaktır.'),
(350, 3, 57, 'İnanıp iyi şeyler yapanlara da (Allah) mükafatlarını tam olarak verecektir. Allah zalimleri sevmez.'),
(351, 3, 58, 'İşte bu sana okuduğumuz, o ayetlerden ve o hikmetli Zikir(Kitap)dandır.'),
(352, 3, 59, 'Allah\'a göre Îsa\'nın durumu, Adem\'in durumu gibidir: Onu, topraktan yarattı, sonra ona "Ol!" dedi, artık olur...'),
(353, 3, 60, '(Bu,) Rabbinden gelen gerçektir. Öyle ise kuşkulananlardan olma.'),
(354, 3, 61, 'Kim sana gelen ilimden sonra seninle tartışmaya kalkarsa, de ki: "Gelin oğullarımızı ve oğullarınızı, kadınlarımızı ve kadınlarınızı, kendimizi ve kendinizi çağıralım, sonra gönülden la\'netle du\'a edelim de, Allah\'ın la\'netini yalancıların üstüne atalım!"'),
(355, 3, 62, 'İşte (Îsa hakkındaki) gerçek kıssa (öykü) budur. Allah\'tan başka tanrı yoktur. Allah, elbette aziz (kesin galib), hüküm ve hikmet sahibidir.'),
(356, 3, 63, 'Eğer dönerlerse, muhakkak ki Allah, bozguncuları bilir.'),
(357, 3, 64, 'De ki: "Ey Kitap ehli, bizim ve sizin aranızda eşit olan bir kelimeye gelin: "Yalnız Allah\'a tapalım. O\'na hiçbirşeyi ortak koşmayalım; birbirimizi Allah\'tan başka tanrılar edinmeyelim." Eğer yüz çevirirlerse; "Şahid olun, biz müslümanlarız!" deyin.'),
(358, 3, 65, 'Ey Kitap ehli, neden İbrahim hakkında tartışıyorsunuz? Oysa Tevrat da, İncil de ondan sonra indirilmiştir. Düşünmüyor musunuz?'),
(359, 3, 66, 'Haydi siz, biraz bilginiz olan şey hakkında tartıştınız; ama hiç bilginiz olmayan şey hakkında neden tartışıyorsunuz? Allah bilir, siz bilmezsiniz.'),
(360, 3, 67, 'İbrahim ne yahudi, ne de hıristiyandı; dosdoğru bir müslümandı. Müşriklerden de değildi.'),
(361, 3, 68, 'Doğrusu, insanların İbrahim\'e en yakın olanı, ona uyanlar, bu peygamber ve mü\'minlerdir. Allah da mü\'minlerin dostudur.'),
(362, 3, 69, 'Kitap ehlinden bir grup istedi ki sizi saptırsınlar. Oysa sadece kendilerini saptırıyorlar; fakat farkında değiller.'),
(363, 3, 70, 'Ey Kitap ehli, (gerçeği) gördüğünüz halde, niçin Allah\'ın ayetlerini inkar ediyorsunuz?'),
(364, 3, 71, 'Ey Kitap ehli, niçin hakkı batıla karıştırıyor ve bile bile gerçeği gizliyorsunuz?'),
(365, 3, 72, 'Kitap ehlinden bir grup dedi ki: "İnananlara indirilmiş olana, günün önünde inanın, sonunda inkar edin; belki (size bakarak onlar da) dönerler;"'),
(366, 3, 73, 'Sizin dininize uyandan başkasına güvenmeyin! (dediler.) De ki: "Hidayet Allah\'ın hidayetidir. Birine, size verilenin benzerinin verilmesinden veya Rabbinizin huzurunda aleyhinize deliller getireceklerinden ötürü mü (böyle söylüyorsunuz)?, De ki: "Lutuf Allah\'ın elindedir, onu dilediğine verir, Allah(ın lutfu) geniştir, (O her şeyi) bilendir.'),
(367, 3, 74, 'Rahmetini dilediğine has kılar. Allah, büyük lutuf ve ikram sahibidir.'),
(368, 3, 75, 'Kitap ehlinden öylesi vardır ki, ona yüklerle emanet bıraksan, onu sana öder. Onlardan öylesi de vardır ki, ona bir dinar versen, devamlı olarak başına dikilmeden onu sana ödemez. Onlar "Ümmilere karşı bize bir sorumluluk yoktur." dedikleri için böyle yapıyorlar ve Allah\'a karşı bile bile yalan söylüyorlar.'),
(369, 3, 76, 'Hayır, kim sözünü yerine getirir ve (günahtan) korunursa, şüphesiz Allah da korunanları sever.'),
(370, 3, 77, 'Fakat Allah\'a verdikleri sözü ve yeminlerini az bir paraya satanlar var ya, işte onların ahirette bir payı yoktur; Allah kıyamet günü onlara konuşmayacak, onlara bakmayacak ve onları yüceltmeyecektir. Onlar için acı bir azab vardır.'),
(371, 3, 78, 'Onlardan bir grup var ki, Kitapta olmayan bir şeyi, siz Kitaptan sanasınız diye dillerini Kitapla eğip büker(sözlerini, Kitabın sözü imiş gibi göstermek için kelimeleri dillerinde bükerek okur, onları, Kitabın sözlerine benzetmeğe çalışır)lar ve: "O, Allah katındandır." derler. Oysa o, Allah katından değildir. Bile bile Allah\'a karşı yalan söylerler.'),
(372, 3, 79, 'Hiçbir insana yakışmaz ki, Allah ona Kitap, hüküm (hikmet) ve peygamberlik versin de, sonra (o kalksın) insanlara: "Allah\'ı bırakıp bana kullar olun", desin; fakat: "Öğrettiğiniz Kitap ve okuduğunuz şeyler gereğince Rabba halis kullar olun!" der.'),
(373, 3, 80, 'Ve size: "Melekleri ve peygamberleri tanrılar edinin!" diye de emretmez. Siz müslüman olduktan sonra, size inkarı emreder mi?'),
(374, 3, 81, 'Allah, peygamberlerden şöyle söz almıştı: "Bakın, size Kitap ve hikmet verdim; imdi yanınızda bulunan(Kitap)ı doğrulayıcı bir peygamber geldiğinde, ona mutlaka inanacak ve ona mutlaka yardım edeceksiniz! Bunu kabul ettiniz mi? Ve bu hususta ağır ahdimi üzerinize aldınız mı?" demişti. "Kabul ettik!" dediler. "O halde tanık olun, ben de sizinle beraber tanık olanlardanım." dedi.'),
(375, 3, 82, 'Artık kim bundan sonra dönerse, işte onlar fasıklardır.'),
(376, 3, 83, 'Allah\'ın dininden başkasını mı arıyorlar? Oysa göklerde ve yerde olanların hepsi, ister istemez, O\'na teslim olmuştur ve O\'na döndürüleceklerdir.'),
(377, 3, 84, 'De ki: "Allah\'a, bize indirilene, İbrahim\'e, İsma\'il\'e, İshak\'a, Ya\'kub\'a ve sıbtlara (Ya\'kub oğullarından türeyen kabilelere) indirilene; Musa\'ya, Îsa\'ya ve peygamberlere Rableri tarafından verilene inandık; onlar arasında bir ayırım yapmayız, biz O\'na teslim olanlarız."'),
(378, 3, 85, 'Kim İslam\'dan başka bir din ararsa, bilsin ki, (o din) ondan kabul edilmeyecek ve o, ahirette kaybedenlerden olacaktır.'),
(379, 3, 86, 'İman ettikten, Resul\'ün hak olduğunu gördükten ve kendilerine açık deliller geldikten sonra, inkar eden bir topluma Allah nasıl yol gösterir? Allah, zalim toplumu doğru yola iletmez.'),
(380, 3, 87, 'İşte onların cezası: Allah\'ın, meleklerin ve bütün insanların la\'neti onların üzerinedir!'),
(381, 3, 88, 'O(la\'net)in içinde ebedi kalacaklardır. Onlardan azab hafifletilmeyecek ve onlara asla fırsat verilmeyecektir.'),
(382, 3, 89, 'Ancak ondan sonra, tevbe edip uslananlar başka. Çünkü Allah, çok bağışlayan, çok esirgeyendir.'),
(383, 3, 90, 'Onlar ki, inandıktan sonra inkar ettiler, sonra inkarları arttı; onların tevbeleri kabul edilmeyecektir. Onlar sapıkların ta kendileridir.'),
(384, 3, 91, 'İnkar edip kafir olarak ölenler, dünya dolusu altın fidye vermiş olsa dahi hiçbirinden kabul edilmeyecektir. Onlar için acı bir azab vardır ve onların hiçbir yardımcıları yoktur!'),
(385, 3, 92, 'Sevdiğiniz şeylerden (Allah için) harcamadıkça asla iyiliğe eremezsiniz. Ne harcarsanız Allah onu bilir.'),
(386, 3, 93, 'Tevrat indirilmeden önce, İsrail\'in kendisine haram kıldığı şeyler dışında, İsrail oğullarına bütün yiyecekler helaldi. De ki: "Doğru iseniz, Tevrat\'ı getirip okuyun."'),
(387, 3, 94, 'Artık bundan sonra da kim Allah\'a yalan uydurursa, işte onlar zalimlerdir.'),
(388, 3, 95, 'De ki: "Allah doğru söyledi, öyle ise dosdoğru, Allah\'ı birleyici olarak İbrahim dinine uyun. O, ortak koşanlardan değildi."'),
(389, 3, 96, 'Doğrusu insanlara (ma\'bed olarak) ilk kurulan ev, Mekke\'de olandır. Alemlere uğur, bereket ve hidayet kaynağı olarak kurulmuştur.'),
(390, 3, 97, 'Onda açık açık deliller, İbrahim\'in Makamı vardır. Ona giren, güvene erer. Yoluna gücü yeten herkesin, o Ev\'e gi(dip haccet)mesi, insanlar üzerinde Allah\'ın bir hakkıdır. Kim nankörlük ederse şüphesiz Allah, bütün alemlerden zengindir.'),
(391, 3, 98, 'De ki: "Ey Kitap ehli, Allah yaptıklarınıza tanık iken neden Allah\'ın ayetlerini inkar ediyorsunuz?"'),
(392, 3, 99, 'De ki: "Ey Kitap ehli, gerçeğe tanık olduğunuz halde, niçin Allah\'ın yolunu eğri göstermeğe yeltenerek, inanmak isteyenleri Allah yolundan çevirmeğe çalışıyorsunuz? Allah yaptıklarınızdan habersiz değildir."'),
(393, 3, 100, 'Ey inananlar, Kitap verilenlerden herhangi bir gruba uyarsanız imanınızdan sonra, (onlar) sizi döndürüp kafir yaparlar.'),
(394, 3, 101, 'Size Allah\'ın ayetleri okunmakta ve O\'nun Elçisi de aranızda iken nasıl inkar edersiniz? Kim Allah\'a sarılırsa muhakkak ki o, doğru yola iletilmiştir.'),
(395, 3, 102, 'Ey inananlar, Allah\'tan, O\'na yaraşır biçimde korkun ve ancak müslümanlar olarak ölün.'),
(396, 3, 103, 'Ve topluca Allah\'ın ipine yapışın, ayrılmayın; Allah\'ın size olan ni\'metini hatırlayın: Hani siz birbirinize düşman idiniz, (Allah) kalblerinizi uzlaştırdı. O\'un ni\'metiyle kardeşler haline geldiniz. Siz ateşten bir çukurun kenarında bulunuyordunuz, (Allah) sizi ondan kurtardı. Allah size ayetlerini böyle açıklıyor ki, yola gelesiniz.'),
(397, 3, 104, 'İçinizden hayra çağıran, iyiliği emredip kötülükten men\'eden bir topluluk olsun; işte onlar kurtuluşa erenlerdir.'),
(398, 3, 105, 'Kendilerine açık deliller geldikten sonra bölünüp ihtilaf edenler gibi olmayın. İşte onlar (evet) onlar için büyük bir azab vardır.'),
(399, 3, 106, 'O gün bazı yüzler ağarır, bazı yüzler kararır. Yüzleri kararanlara: "İnanmanızdan sonra inkar ettiniz ha? Öyle ise inkar etmenize karşılık azabı tadın!" (denilir).'),
(400, 3, 107, 'Yüzleri ağaranlar ise Allah\'ın rahmeti içindedirler, orada sürekli kalacaklardır.'),
(401, 3, 108, 'İşte onlar Allah\'ın ayetleridir. Onları sana gerçek ile okuyoruz. Allah, alemlere zulmetmek istemez.'),
(402, 3, 109, 'Göklerde ve yerde olanların hepsi Allah\'ındır. Bütün işler Allah\'a döndürülür.'),
(403, 3, 110, 'Siz, insanlar için çıkarılmış en hayırlı bir ümmet oldunuz. İyiliği emreder, kötülükten men\'edersiniz ve Allah\'a inanırsınız. Eğer Kitap ehli, inanmış olsaydı, elbette kendileri için iyi olurdu. Onlardan inananlar da var, ama çokları yoldan çıkmışlardır.'),
(404, 3, 111, 'Size eziyetten başka bir zarar veremezler. Sizinle savaşsalar bile, size arkalarını dönüp kaçarlar, sonra onlara yardım da edilmez.'),
(405, 3, 112, 'Nerede olsalar, onlara alçaklık (damgası) vurulmuştur (ezilmeğe mahkumdurlar). Meğer ki Allah\'ın ahdine ve (inanan) insanların ahdine sığınmış olsunlar. Allah\'ın gazabına uğradılar ve üzerlerine miskinlik damgası vuruldu (yoksulluk içinde ezildiler). Böyle oldu, çünkü onlar Allah\'ın ayetlerini inkar ediyorlar, haksız yere peygamberleri öldürüyorlardı ve çünkü isyan etmişlerdi, haddi aşıyorlardı.'),
(406, 3, 113, 'Ama hepsi bir değildir. Kitap ehli içinde, gece saatlerinde ayakta durup Allah\'ın ayetlerini okuyarak secdeye kapanan bir topluluk da vardır.'),
(407, 3, 114, 'Onlar, Allah\'a ve ahiret gününe inanırlar, iyiliği emreder, kötülükten men\'ederler; hayır işlerine koşarlar. İşte onlar iyilerdendir.'),
(408, 3, 115, 'Yapacakları hiçbir iyilik inkar edilmeyecektir. Şüphesiz Allah, (günahlardan) korunanları bilmektedir.'),
(409, 3, 116, 'Nankörlere gelince, ne malları, ne de evladları onlara, Allah\'a karşı hiçbir yarar sağlamayacaktır. Onlar ateş halkıdır; onlar orada sürekli kalacaklardır.'),
(410, 3, 117, 'Onların bu dünya hayatında harcadıkları malların durumu, nefislerine zulmeden bir topluluğun ekinine vurup onu mahveden dondurucu bir rüzgar(ın tahribatın)a benzer. Allah onlara zulmetmedi; fakat onlar, kendi kendilerine zulmediyorlardı.'),
(411, 3, 118, 'Ey inananlar, kendinizden başkasını kendinize dost edinmeyin; onlar sizi bozmaktan geri durmazlar. Size sıkıntı verecek şeyleri isterler. Onların ağızlarından öfke taşmaktadır. Göğüslerinde gizledikleri (kin) ise daha büyüktür. Düşünürseniz, size ayetleri açıkladık.'),
(412, 3, 119, 'İşte, siz öyle kimselersiniz ki, onları seversiniz, halbuki onlar sizi sevmezler. Kitabın hepsine inanırsınız. Onlar sizinle karşılaştıkları zaman "İnandık" derler. Ama kendi başlarına kaldıklarında, size karşı öfkeden parmak uçlarını ısırırlar. De ki: "Öfkenizden ölün! Şüphesiz Allah, göğüslerin özünü bilir."'),
(413, 3, 120, 'Size bir iyilik dokunsa (Bu,) Onları tasalandırır; size bir kötülük dokunsa, ona sevinirler. Eğer sabreder, korunursanız, onların tuzağı size hiçbir zarar vermez. Şüphesiz Allah, onların yaptıklarını kuşatmıştır.'),
(414, 3, 121, 'Hani sen, erkenden ailenden ayrılmıştın, (Uhud\'da) mü\'minleri savaş üslerine yerleştiriyordun. Allah da işitendi, bilendi.'),
(415, 3, 122, 'Sizden iki takım, korkup bozulmaya yüz tutmuştu. Halbuki Allah, kendilerinin dostu idi. İnananlar, Allah\'a dayansınlar.'),
(416, 3, 123, '(Allah mü\'minlere yardım eder). Nitekim Allah, zayıf durumda bulunduğunuz Bedir\'de de size yardım etmişti. O halde Allah\'tan korkun ki, şükredesiniz.'),
(417, 3, 124, 'O zaman sen mü\'minlere: "Rabbinizin, size, indirilmiş üç bin melek ile yardım etmesi, size yetmez mi?" diyordun.'),
(418, 3, 125, 'Evet, sabreder, korunursanız; onlar hemen şu dakikada üzerinize gelseler, Rabbiniz size nişanlı beşbin melekle yardım eder.'),
(419, 3, 126, 'Allah bu(yardım va\'di)ni sırf size müjde olsun ve kalbleriniz bununla güven bulsun diye yaptı. Yardım, yalnız, daima galib, hüküm ve hikmet sahibi Allah katındandır.'),
(420, 3, 127, 'İnkar edenlerden bir kısmını kessin ve perişan etsin de umutsuz olarak dönüp gitsinler diye (size yardım eder).'),
(421, 3, 128, 'O konuda senin yapacağın bir şey yoktur. Allah, ya tevbelerini kabul edip onları affeder, ya da zalim olduklarından dolayı onlara azab eder.'),
(422, 3, 129, 'Göklerde ve yerde olanların hepsi Allah\'ındır. (O), dilediğini bağışlar, dilediğine azabeder, Allah, çok bağışlayan, çok esirgeyendir.'),
(423, 3, 130, 'Ey inananlar, kat kat riba yemeyin, Allah\'tan korkun ki, kurtuluşa eresiniz.'),
(424, 3, 131, 'Kafirler için hazırlanmış ateşten sakının!'),
(425, 3, 132, 'Allah\'a ve Elçiye ita\'at edin ki, size merhamet edilsin.'),
(426, 3, 133, 'Rabbinizden bir bağışlanmaya ve genişliği göklerle yer arası kadar olan, korunanlar için hazırlanmış cennete koşun!'),
(427, 3, 134, 'O(koruna)nlar bollukta ve darlıkta Allah için harcarlar, öfke(lerin)i yutkunurlar, insanları affederler. Allah da güzel davrananları sever.'),
(428, 3, 135, 'Ve onlar bir kötülük yaptıkları, ya da nefislerine zulmettikleri zaman, Allah\'ı hatırlayarak hemen günahlarının bağışlanmasını dilerler; günahları da Allah\'tan başka kim bağışlayabilir? Ve onlar, hatalarında bile bile, ısrar etmezler.'),
(429, 3, 136, 'İşte onların mükafatı Rableri tarafından bağışlanma ve altlarından ırmaklar akan, içinde sürekli kalacakları cennetlerdir. Çalışanların ücreti ne güzeldir!'),
(430, 3, 137, 'Sizden önce de yasalar uygulanmıştır. Yeryüzünde dolaşın da yalanlayıcıların sonunun nasıl olduğunu görün.'),
(431, 3, 138, 'Bu, insanlara bir açıklama, korunanlara yol gösterme ve öğüttür.'),
(432, 3, 139, 'Gevşemeyin, üzülmeyin, eğer inanıyorsanız, mutlaka siz üstün geleceksiniz.'),
(433, 3, 140, 'Eğer size bir yara dokunduysa, o topluluğa da benzeri bir yara dokunmuştu. O günler... onları biz insanlar arasında çevirip dururuz (kah bir kavme, kah ötekine galibiyet veririz; bazen bir topluma iyi veya kötü günler gösteririz, bazan ötekine). Allah inananları ortaya çıkarmak, sizden şehidler edinmek için (zamanı kah lehinize, kah aleyhinize çevirmektedir). Allah, zalimleri sevmez.'),
(434, 3, 141, 'Ve inananları iyice özleştirmek, kafirleri de mahvetmek için (günleri insanlar arasında böyle çevirmektedir).'),
(435, 3, 142, 'Yoksa siz, Allah, içinizden cihad edenleri (sınayıp) bilmeden, sabredenleri (sınayıp) bilmeden cennete gireceğinizi mi sandınız?'),
(436, 3, 143, 'Andolsun ki, siz ölümle karşılaşmadan önce onu arzuluyordunuz. İşte onu gördünüz, ama bakıp duruyorsunuz.'),
(437, 3, 144, 'Muhammed, sadece bir elçidir. Ondan önce de elçiler gelip geçmiştir. Şimdi o ölür veya öldürülürse siz ökçelerinizin üzerinde geriye mi döneceksiniz? Kim ökçesi üzerinde geriye dönerse, Allah\'a hiçbir ziyan veremez. Allah, şükredenleri mükafatlandıracaktır.'),
(438, 3, 145, 'Allah\'ın izni olmadan hiçbir kişi ölmez. (Ölüm) Belirli bir süreye göre yazılmıştır. Kim dünya sevabını (menfaatini) isterse, kendisine ondan veririz; kim ahiret sevabını isterse, kendisine ondan veririz, şükredenleri mükafatlandıracağız.'),
(439, 3, 146, 'Nice peygamber var ki, kendileriyle beraber birçok erenler çarpıştılar; Allah yolunda başlarında gelenlerden yılmadılar, zayıflık göstermediler, boyun eğmediler. Allah sabredenleri sever.'),
(440, 3, 147, 'Sadece şöyle diyorlardı: "Rabbimiz, bizim günahlarımızı ve işimizde taşkınlığımızı bağışla, ayaklarımızı (yolunda) sağlam tut, kafir topluma karşı bize yardım eyle!"'),
(441, 3, 148, 'Allah da onlara hem dünya karşılığını, hem ahiret karşılığının en güzelini verdi. Çünkü Allah, güzel davrananları sever.'),
(442, 3, 149, 'Ey inananlar, eğer inkar edenlere ita\'at ederseniz, sizi arkanıza (küfre) çevirirler, o zaman büsbütün kaybedersiniz.'),
(443, 3, 150, 'Hayır, Mevlanız Allah\'a (ita\'at edin), yardımcıların en iyisi O\'dur.'),
(444, 3, 151, 'Allah\'ın, kendilerine hiçbir güç (vermediği, haklarında hiçbir delil) indirmediği şeyleri, Allah\'a ortak koştuklarından dolayı inkar edenlerin kalblerine korku salacağız; gidecekleri yer de cehennemdir! Zalimlerin varacağı yer, ne kötüdür!'),
(445, 3, 152, 'Kendi izniyle onları öldürdüğünüz sürece Allah, size (yardım) va\'dini doğruladı: Nihayet siz korktunuz, Allah size sevdiğiniz(galibiyet)i gösterdikten sonra (verilen) emir hakkında (birbirinizle) çekişip isyan ettiniz: Kiminiz dünyayı istiyordu, kiminiz ahireti istiyordu. Sonra Allah sizi denemek için onlardan geri çevirdi (yenilgiye uğrattı. Buna rağmen) sizi bağışladı. Allah mü\'minlere karşı çok lutufkardır.'),
(446, 3, 153, 'Elçi, aranızdan sizi çağırırken siz, boyuna uzaklaşıyor, hiç kimseye dönüp bakmıyordunuz. Bundan dolayı Allah, size gam üstüne gam verdi ki ne elinizden gidene, ne de başınıza gelene üzülmeyesiniz. Allah, yaptıklarınızı duymaktadır.'),
(447, 3, 154, 'Sonra o üzüntünün ardından (Allah) size bir güven, bir kısmınızı bürüyen bir uyku indirdi; bir kısmınız da kendi canlarının kaygısına düşmüştü. Allah\'a karşı cahiliyye zannı gibi haksız bir zanda bulunuyorlar: "Bu işten bize bir şey var mı?" diyorlardı. De ki: "Bütün iş, Allah\'a aittir." Onlar sana açıklayamadıklarını içlerinde gizliyorlar. Diyorlar ki: "Bu işten bize bir fayda olsaydı, burada öldürülmezdik." De ki: "Evlerinizde dahi olsaydınız, yine üzerine öldürülme(si) yazılmış olanlar, mutlaka (vurulup) yatacakları yeri boylardı. Allah göğüslerinizdekini denemek, kalblerinizdekini açığa çıkarmak için (bunları başınıza getirdi)". Allah göğüslerin özünü bilir.'),
(448, 3, 155, 'İki topluluğun karşılaştığı gün, içinizden yüz çevirip gidenleri, yaptıkları bazı işlerden dolayı şeytan, (yoldan) kaydırmak istemişti. Ama yine de Allah, onları affetti. Şüphesiz Allah, çok bağışlayandır, halimdir.'),
(449, 3, 156, 'Ey inananlar, siz inkar edenler ve yeryüzünde sefere, ya da savaşa çıkan gazi kardeşleri için: "Eğer bizim yanımızda olsalardı ölmezlerdi ve vurulmazlardı." diyenler gibi olmayın. Allah, onların bu düşünce ve sözlerini, kalblerinde dert yapar. Yaşatan da, öldüren de Allahtır. Allah, yaptıklarınızı görmektedir.'),
(450, 3, 157, 'Eğer Allah yolunda öldürülür, ya da ölürseniz, Allah\'ın bağışlaması ve rahmeti, onların topladıkları(dünya malı)ndan daha hayırlıdır.'),
(451, 3, 158, 'Ölür veya öldürülürseniz, elbette Allah\'a götürüleceksiniz!'),
(452, 3, 159, 'Allah\'ın rahmeti sebebiyledir ki, sen onlara yumuşak davrandın. Eğer kaba, katı yürekli olsaydın, çevrenden dağılır, giderlerdi. Öyleyse onlar(ın kusurların)dan geç, onlar için mağfiret dile. İşini onlara danış, karar verince de Allah\'a dayan; çünkü Allah kendine dayanıp güvenenleri sever.'),
(453, 3, 160, 'Eğer Allah size yardım ederse, artık sizi yenecek yoktur. Ve eğer sizi yüz üstü bırakırsa, O\'ndan sonra size kim yardım edebilir? Mü\'minler, Allah\'a dayansınlar.'),
(454, 3, 161, 'Bir peygamberin aşırması, hiyanet etmesi, olur şey değildir. Kim (emanete hıyanet eder), aşırırsa kıyamet günü aşırdığını boynuna yüklenip getirir. Sonra herkese kazandığı tastamam verilir, hiçbir haksızlığa uğratılmazlar.'),
(455, 3, 162, 'Hiç Allah\'ın rızasına uyan kimse; Allah\'ın hışmına uğrayan, yeri de cehennem olan adam gibi olur mu? Ne kötü sonuçtur orası!'),
(456, 3, 163, 'O(insa)nlar, Allah katında derece derecedirler. Allah, onların yaptıklarını görmektedir.'),
(457, 3, 164, 'Andolsun ki, Allah, mü\'minlere büyük lutufta bulundu: Zira daha önce açık bir sapıklık içinde bulunuyorlarken onlara, kendi içlerinden, kendilerine Allah\'ın ayetlerini okuyan, kendilerini yücelten ve kendilerine Kitap ve hikmeti öğreten bir elçi gönderdi.'),
(458, 3, 165, 'Başınıza bir bela gelince -siz, onun iki katını onların başlarına getirmiş olduğunuz halde yine- Bu nereden başımıza geldi?" dediniz. De ki: "O (bela), kendinizdendir." Allah, herşeye kadirdir.'),
(459, 3, 166, 'İki topluluğun karşılaştığı gün, sizin başınıza gelen, ancak Allah\'ın izniyle olmuştur ki (O), inananları bilsin (deneyip ortaya çıkarsın).'),
(460, 3, 167, 'Ve iki yüzlülük edenleri bilsin (ortaya çıkarsın). Onlara: "Gelin, Allah yolunda savaşın, ya da savunun." dendiği halde: "Eğer savaş (olacağını) bilseydik, sizinle gelirdik." dediler. Onlar, o gün, imandan çok küfre yakın idiler. Ağızlarıyla, kalblerinde olmayanı söylüyorlar. Halbuki Allah, içlerinde sakladıkları şeyi çok iyi bilmektedir.'),
(461, 3, 168, '(Savaştan geri kalıp) Oturarak, kardeşleri için "Bizim sözümüzü tutsalardı, öldürülmezlerdi." diyenlere söyle: "Eğer doğru iseniz, kendinizden ölümü savınız!"'),
(462, 3, 169, 'Allah yolunda öldürülenleri ölüler sanma; hayır, (onlar) diridirler, Rableri katında rızıklanmaktadırlar.'),
(463, 3, 170, 'Allah\'ın, keremiyle kendilerine verdiklerinden sevinçli olarak, arkalarından henüz kendilerine yetişemeyenlere de korku olmadığına, onların da üzüntüye uğramayacaklarına sevinirler.'),
(464, 3, 171, 'Allah\'ın ni\'metine, lutfuna ve Allah\'ın mü\'minlerin ecrini zayi etmeyeceğine sevinirler.'),
(465, 3, 172, 'O(mü\'mi)nler ki yaralandıkları halde yine Allah\'ın ve Elçinin çağrısına uydular; onlardan güzel davrananlar ve (günahlardan) korunanlar için pek büyük ecir vardır.'),
(466, 3, 173, 'Onlar ki, halk kendilerine: "(Düşman) İnsanlar size karşı ordu toplamışlar, onlardan korkun!" deyince, (bu söz,) onların imanını artırdı. Ve: "Allah bize yeter, O, ne güzel vekildir." dediler.'),
(467, 3, 174, 'Bundan dolayı Allah\'tan bir ni\'met ve bollukla geri döndüler, kendilerine hiçbir kötülük dokunmadı. Ve Allah\'ın rızasına uydular. Allah büyük lutuf sahibidir.'),
(468, 3, 175, 'O şeytan sizi kendi dostlarından korkutuyor, eğer inanmış iseniz, onlardan korkmayın, benden korkun!'),
(469, 3, 176, 'İnkara koşanlar seni üzmesin, onlar Allah\'a hiçbir zarar veremezler. Allah onlara ahirette hiçbir nasip koymamak istiyor. Onlar için büyük bir azab vardır.'),
(470, 3, 177, 'İman karşılığında inkarı satın alanlar, Allah\'a hiçbir zarar vermezler. Onlar için acı bir azab vardır.'),
(471, 3, 178, 'İnkar edenler sanmasınlar ki, kendilerine süre vermemiz, kendileri için hayırlıdır. Biz onlara süre veriyoruz ki günahı artırsınlar. Onlar için alçaltıcı bir azab vardır.'),
(472, 3, 179, 'Allah mü\'minleri, (şu) üzerinde bulunduğunuz halde bırakacak değildir, temizi pisten ayıracaktır. Ve Allah sizi gaybe vakıf kılacak değildir. Fakat Allah, elçilerinden dilediğini seçer (onu gaybe vakıf kılar). O halde Allah\'a ve elçilerine inanın; eğer inanır ve (günahlardan) korunursanız sizin için büyük mükafat vardır.'),
(473, 3, 180, 'Allah\'ın kereminden kendilerine verdiğine cimrilik edenler, onu kendileri için hayırlı sanmasınlar. Hayır, o, kendileri için şerlidir. Cimrilik ettikleri şeyler, kıyamet günü boyunlarına dolandırılacaktır. Göklerin ve yerin mirası Allah\'ındır (bütün mülk O\'na aittir ve O\'na kalacaktır). Allah yaptıklarınızı haber alandır.'),
(474, 3, 181, 'Allah: "Allah fakirdir, biz zenginiz." diyenlerin sözünü işitti. Onların dediklerini ve haksız yere peygamberleri öldürmelerini yazacağız ve: "Yangın azabını tadın!" diyeceğiz.'),
(475, 3, 182, 'Bu, sizin ellerinizin yapıp öne sürdürdüğünün karşılığıdır. Allah, kullara asla zulmedici değildir.'),
(476, 3, 183, 'Onlar: "Allah bize, and verdi ki, bize ateşin yiyeceği bir kurban getirmedikçe hiçbir elçiye inanmayalım." dediler. De ki: "Size benden önce açık deliller ve bu dediğinizi de getiren elçiler gelmişti. Eğer doğru idiyseniz niçin onları öldürdünüz?"'),
(477, 3, 184, 'Eğer seni yalanladılarsa, senden önce açık deliller, hikmetli sahifeler ve aydınlatıcı Kitabı getiren peygamberler de yalanlanmıştı.'),
(478, 3, 185, 'Her can ölümü tadacaktır. Kıyamet günü ecirleriniz size eksiksiz verilecektir. Kim ki hemen ateşin elinden çekilip kurtarılır da cennete sokulursa, işte o, kurtuluşa ermiştir. Dünya hayatı, aldatıcı zevkten başka bir şey değildir.'),
(479, 3, 186, 'Mallarınız ve canlarınız hususunda deneneceksiniz; sizden önce kendilerine Kitap verilenlerden ve ortak koşanlardan çok incitici (sözler) duyacaksınız. Ama sabreder, korunursanız; işte bunlar, yapmağa değer işlerdendir.'),
(480, 3, 187, 'Allah, kendilerine Kitap verilenlerden: "Onu mutlaka insanlara açıklayacaksınız, gizlemeyeceksiniz!" diye söz almıştı. Fakat onlar, verdikleri sözü sırtlarının ardına attılar ve karşılığında birkaç para aldılar. Ne kötü şey satın alıyorlar.'),
(481, 3, 188, 'O ettiklerine sevinen, yapmadıkları şeylerle övülmeyi sevenlerin, azabdan kurtulacaklarını sanma. Onlar için acı bir azab vardır.'),
(482, 3, 189, 'Göklerin ve yerin mülkü Allah\'ındır. Allah herşeye kadirdir.'),
(483, 3, 190, 'Göklerin ve yerin yaratılışında, gecenin ve gündüzün gidip gelişinde elbette sağduyu sahipleri için ibretler vardır.'),
(484, 3, 191, 'Onlar ayakta, oturarak ve yanları üzerine yatarken Allah\'ı anarlar, göklerin ve yerin yaratılışı üzerinde düşünürler: "Rabbimiz (derler), bunu boş yere yaratmadın, sen yücesin, bizi ateş azabından koru!"'),
(485, 3, 192, 'Rabbimiz, sen birini ateşe soktun mu, onu perişan etmişsindir. zalimlerin yardımcıları yoktur.'),
(486, 3, 193, 'Rabbimiz, biz, \'Rabbinize inanın\' diye imana çağıran bir davetçi işittik, hemen inandık. Rabbimiz, bizim günahlarımızı bağışla, kötülüklerimizi ört, canımızı iyilerle beraber al (bizi ma\'nada onlarla beraber eyle)!'),
(487, 3, 194, 'Rabbimiz bize, elçilerine va\'dettiğini ver, kıyamet günü bizi rezil, perişan etme. Zira sen verdiğin sözden caymazsın!'),
(488, 3, 195, 'Rableri onlara karşılık verdi: "Ben, sizden erkek kadın, hiçbir çalışanın işini zayi etmeyeceğim. Hep birbirinizdensiniz. Göç edenler, yurtlarından çıkarılanlar, yolumda işkence edilenler, vuruşanlar ve öldürülenler... Elbette onların kötülüklerini örteceğim ve onları, altlarından ırmaklar akan cennetlere sokacağım. (Yaptıklarına), Allah katından bir karşılık olarak (onlara bu ni\'metleri vereceğim). Karşılıkların en güzeli Allah katındadır."'),
(489, 3, 196, 'İnkar edenlerin, öyle şehirlerde gezip dolaşması seni aldatmasın.'),
(490, 3, 197, 'Bu, az bir geçimdir. Sonra gidecekleri yer, cehennemdir. Ne kötü bir yataktır orası!'),
(491, 3, 198, 'Fakat Rablerinden korkanlar için, altlarından ırmaklar akan cennetler var. Orada ebedi kalacaklar, Allah tarafından ağırlanacaklardır. İyiler için Allah yanında bulunan ödüller ise (dünya varlığından) daha hayırlıdır.'),
(492, 3, 199, 'Kitap ehlinden öyleleri var ki, Allah\'a inanırlar, size indirilene ve kendilerine indirilene inanırlar; Allah\'a karşı saygılıdırlar; Allah\'ın ayetlerini birkaç paraya satmazlar. Onların da Rableri katında ödülleri vardır! Şüphesiz Allah, hesabı çabuk görendir.'),
(493, 3, 200, 'Ey inananlar, sabredin, direnin. Savaşa hazırlıklı, uyanık bulunun ve Allah\'tan korkun ki, başarıya eresiniz.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Ey insanlar, sizi bir tek nefisten (nefes alan candan) yaratan ve ondan eşini yaratıp ikisinden birçok erkekler ve kadınlar üreten Rabbinizden korkun; adına birbirinizden dilekte bulunduğunuz Allah\'tan ve akrabalık(bağlarını kırmak)tan sakının. Şüphe siz Allah, sizin üzerinizde gözetleyicidir.'),
(495, 4, 2, 'Öksüzlere mallarını verin, temizi pis olanla değiştirmeyin, onların mallarını sizin mallarınıza katarak (helal, temiz malınızı kirletip) yemeyin; çünkü bu, büyük bir günahtır.'),
(496, 4, 3, 'Şayet öksüz(kızlarla evlendiğiniz takdirde on)lar hakkında adaleti yerine getiremeyeceğinizden korkarsanız, size helal olan kadınlardan ikişer, üçer, dörder alın. O(kadı)nlar arasında da adalet yapamayacağınızdan korkarsanız bir tane alın; yahut ellerinizin altında bulunan(cariye)lerle yetinin. Cevr (ve haksızlık) etmemeniz için en uygun olan budur.'),
(497, 4, 4, 'Kadınlara mehirlerini bir hak olarak (gönül hoşluğuyla) verin; eğer kendi istekleriyle o mehrin bir kısmını size bağışlarlarsa, onu da afiyetle yeyin.'),
(498, 4, 5, 'Allah\'ın, sizin için geçim kaynağı yaptığı mallarınızı aklı ermezlere vermeyin; o mallarla onları besleyin, giydirin ve onlara güzel söz seyleyin.'),
(499, 4, 6, 'Nikah çağına varıncaya kadar öksüzleri deneyin, eğer onlarda bir olgunluk görürseniz, mallarını kendilerine verin. Büyüsünler diye alıkoyup israf ile tez elden onların mallarını yemeğe kalkmayın. Zengin olan, çekinsin; yoksul olan da (malın muhafazası için gösterdiği çabaya ve ihtiyacına) uygun şekilde yesin. Onlara mallarını geri verdiğiniz zaman da yanlarında şahid bulundurun. Hesapçı olarak da Allah yeter (O, her yaptığınızı hesabetmektedir).'),
(500, 4, 7, 'Ana babanın ve akrabanın geriye bıraktıklarından erkeklere pay vardır; ana babanın ve akrabanın geriye bıraktıklarından kadınlara da pay vardır. Gerek azından gerek çoğundan (hem erkeğe, hem de kadına) bir hisse ayrılmıştır.'),
(501, 4, 8, '(Miras düşmeyen) Akrabalar, öksüzler, yoksullar da (miras) taksim(in)de hazır bulunursa bir şeyler vererek onları da ondan rızıklandırın (gönüllerini hoş edin) ve onlara güzel söz söyleyin.'),
(502, 4, 9, 'Kendileri, geriye zayıf çocuklar bıraktıkları takdirde onların durumundan endişe edecek olanlar, (öksüzlerin hakkına dokunmaktan) çekinsinler. Allah\'tan korksunlar ve doğru söz söylesinler.'),
(503, 4, 10, 'Zulüm ile öksüzlerin mallarını yiyenler, karınlarına sadece ateş koymaktadırlar ve çılgın bir ateşe gireceklerdir.'),
(504, 4, 11, 'Allah size, çocuklarınız(ın alacağı miras) hakkında, erkeğe kadının payının iki katını tavsiye eder. (Çocuklar) ikiden fazla kadın iseler, (ölenin geriye) bıraktığının üçte ikisi onlarındır. Eğer (çocuk) yalnız bir kadınsa (mirasın) yarısı onundur. Ölenin çocuğu varsa, bıraktığı mirasta ana babasından her birinin altıda bir hissesi vardır. Eğer çocuğu yok da ana babası ona varis oluyorsa, anasına üçte bir düşer. Eğer kardeşleri varsa, anasının payı altıda birdir. (Bu hükümler, ölenin) Yapacağı vasiyyetten, ya da borcundan sonradır. Babalarınız ve oğullarınızdan, hangisinin fayda bakımından size daha yakın olduğunu bilmezsiniz. Bunlar, Allah\'ın koyduğu haklardır. Şüphesiz Allah bilendir, hikmet sahibidir.'),
(505, 4, 12, 'Eğer çocukları yoksa, eşlerinizin yapacakları vasiyyetten ve borçtan sonra bıraktıkları mirasın yarısı sizindir. Çocukları varsa, bıraktıklarının dörtte biri sizindir. Sizin de çocuğunuz yoksa, yapacağınız vasiyyet ve borçtan sonra bıraktığınızın dörtte biri, onlarındır; çocuğunuz varsa bıraktığınızın sekizde biri onlarındır. Eğer miras bırakan erkek veya kadının evladı ve ana babası olmayıp bir erkek veya bir kızkardeşi varsa, her birine altıda bir düşer. Bundan fazla iseler, üçte bire ortaktırlar. (Bu taksim) Zarar verici olmayan vasiyyet ve borçtan sonra (uygulanır). Bunlar, Allah\'tan (size) vasiyyettir. Allah bilendir, halimdir.'),
(506, 4, 13, 'Bunlar Allah\'ın sınırlarıdır. Kim Allah\'a ve Elçisine ita\'at ederse Allah onu, altlarından ırmaklar akan, içinde sürekli kalacakları cennetlere sokar. İşte büyük başarı budur.'),
(507, 4, 14, 'Kim de Allah\'a ve Elçisi\'ne karşı gelir, O\'nun sınırlarını aşarsa, Allah onu, sürekli kalacağı ateşe sokar. Onun için alçaltıcı bir azab vardır.'),
(508, 4, 15, 'Kadınlarınızdan fuhuş yapanlara karşı içinizden dört şahid getirin; eğer onlar şahidlik ederlerse, o kadınları ölüm alıncaya, ya da Allah onların yararına bir yol gösterinceye kadar evlerde tutun (dışarı çıkarmayın).'),
(509, 4, 16, 'İçinizden iki kişi, fuhuş yaparsa, onlara eziyet edin; eğer tevbe eder, uslanırlarsa artık onlardan vazgeçin. Çünkü Allah, tevbeleri çok kabul edendir, çok esirgeyendir.'),
(510, 4, 17, 'Allah\'a göre, şu kimselerin tevbesi makbuldür ki, cahillikle bir kötülük yapıp hemen ardından dönerler. İşte Allah onların tevbesini kabul eder. Allah bilendir, hüküm ve hikmet sahibidir.'),
(511, 4, 18, 'Yoksa kötülükler yapıp yapıp da nihayet kendilerine ölüm gelip çatınca: "Ben şimdi tevbe ettim" diyenlere ve kafir olarak ölenlere tevbe (af) yoktur. Onlar için acı bir azab hazırlamışızdır!'),
(512, 4, 19, 'Ey inananlar, kadınları miras yoluyla zorla almanız size helal değildir. Onlara verdiklerinizin bir kısmını alıp götürmek için onları sıkıştırmayın. Şayet açık bir edepsizlik yaparlarsa başka. Onlarla iyi geçinin. Eğer onlardan hoşlanmazsanız, bilinki sizin hoşlanmadığınız bir şeye Allah çok hayır koymuş olabilir.'),
(513, 4, 20, 'Bir eşin yerine başka bir eş almak istediğiniz takdirde, onlardan birine (evvelki eşinize) kantarlarca mal vermiş olsanız dahi verdiğinizden hiçbir şeyi geri almayın. İftira ederek ve açık günaha girerek verdiğinizi alacak mısınız?'),
(514, 4, 21, 'Nasıl alırsınız ki, birbirinize geçmiş (içli dışlı olmuş) idiniz ve onlar, sizden sağlam te\'minat almışlardı.'),
(515, 4, 22, 'Geçmişte olanlar hariç, artık babalarınızın evlendiği kadınlarla evlenmeyin. Çünkü bu, edepsizliktir, (Allah\'ın) hışm(ı)dır ve iğrenç bir yoldur.'),
(516, 4, 23, 'Size (şunlarla evlenmeniz) haram kılındı: Analarınız, kızlarınız, kızkardeşleriniz, halalarınız, teyzeleriniz, kardeş kızları, kızkardeş kızları, sizi emziren analarınız, süt bacılarınız, karılarınızın anaları, birleştiğiniz karılarınızdan olup evlerinizde bulunan üvey kızlarınız -eğer onlarla henüz birleşmemişseniz, (kızlarını almaktan ötürü) üzerinize bir günah yoktur-kendi sulbünüzden gelen oğullarınızın karıları ve iki kızkardeşi bir arada almanız. Ancak geçmişte olanlar hariç. Şüphesiz Allah, çok bağışlayan, çok esirgeyendir.'),
(517, 4, 24, '(Savaşta tutsak olarak) ellerinize geçen(cariye)ler dışında, evli kadınlar(la evlenmeniz) de haramdır. (İşte bunlar) size Allah\'ın yazdığı yasaklardır. Bunlardan ötesini, iffetli yaşamak, zina etmemek şartıyle mallarınızla istemeniz (evlenmeniz), size helal kılındı. O halde onlardan yararlanmanıza karşılık, kesilen ücretlerini bir hak olarak onlara verin. Hakkın kesiminden sonra karşılıklı anlaşma(k suretiyle kesilenden az veya çok vermeniz)de üzerinize bir günah yoktur. Allah bilendir, hüküm ve hikmet sahibidir.'),
(518, 4, 25, 'İçinizden inanmış hür kadınlarla evlenmeğe gücü yetmeyen kimse, elleriniz altında bulunan inanmış genç kızlarınız(olan cariyeleriniz)den alsın. Allah sizin imanınızı daha iyi bilir. Hepiniz birbirinizdensiniz (hepiniz aynı kökten gelmekte, aynı toplumun bireylerisiniz; insanlık bakımından aranızda bir fark yoktur). Öyle ise iffetli yaşamaları, zina etmemeleri ve gizli dost da tutmamaları şartıyle, sahiplerinin izniyle onlarla evlenin, ücretlerini (mehirlerini) de güzelce verin. Evlendikten sonra bir fuhuş yaparlarsa onlara, hür kadınlara yapılan işkencenin yarısı uygulanır. Bu (cariye ile evlenme), içinizden sakıntıya düşmekten korkanlar içindir. Sabretmeniz ise sizin için daha iyidir. Allah bağışlayandır, esirgeyendir.'),
(519, 4, 26, 'Allah size (helal ve haram olanı) açıklamak ve sizi, sizden öncekilerin yasalarına iletmek ve günahlarınızı bağışlamak istiyor. Allah bilendir, hüküm ve hikmet sahibidir.'),
(520, 4, 27, 'Allah, sizin tevbenizi kabul etmek istiyor; şehvetlerine uyanlar ise sizin büyük bir sapıklığa düşmenizi istiyorlar.'),
(521, 4, 28, 'Allah sizden (ağır teklifleri) hafifletmek istiyor. Çünkü insan zayıf yaratılmıştır.'),
(522, 4, 29, 'Ey inananlar, mallarınızı aranızda batılla (doğru olmayan yollarla, haksız yere) yemeyin. Kendi rızanızla yaptığınız ticaret olursa başka. Canlarınızı da öldürmeyin. Doğrusu Allah, size karşı çok merhametlidir.'),
(523, 4, 30, 'Kim düşmanlık ve zulüm ile bunu yaparsa (bilsin ki) onu cehenneme sokacağız. Bu, Allah\'a kolaydır.'),
(524, 4, 31, 'Eğer size yasaklanan büyük günahlardan kaçınırsanız, sizin küçük günahlarınızı örteriz ve sizi güzel bir yere sokarız.'),
(525, 4, 32, 'Allah\'ın sizi birbirinizden üstün kıldığı şeylere göz dikmeyin. Erkeklere de kazandıklarından bir pay var, kadınlara da kazandıklarından bir pay var. Allah\'tan, O\'nun lutfunu isteyin. Kuşkusuz Allah, her şeyi bilendir.'),
(526, 4, 33, 'Ana babanın ve akrabanın bıraktıklarından her birine varisler kıldık. yeminlerinizin bağladığı kimselere de hisselerini verin. Allah herşeyi görmektedir.'),
(527, 4, 34, 'Allah, insanları birbirinden üstün kıldığı ve mallarından harca(yıp kadınların geçmini sağla)dıkları için erkekler, kadınlar üzerinde yöneticidirler. Bundan dolayı iyi kadınlar ita\'atkar olup, Allah\'ın kendilerini korumasına karşılık (Allah\'ın verdiği başarı ile) gizliyi korurlar (kocalarına asla ihanet etmezler). Hırçınlık, etmelerinden korktuğunuz kadınlara öğüt verin, yataklarda onlara sokulmayın, onları dövün. Eğer size ita\'at ederlerse artık onların aleyhine başka bir yol aramayın. Çünkü Allah yücedir, büyüktür.'),
(528, 4, 35, 'Eğer (karı-kocanın) aralarının açılmasından endişe duyarsanız, erkeğin ailesinden bir hakem ve kadının ailesinden bir hakem gönderin. Bunlar uzlaştırmak isterlerse, Allah onların arasını bulur. Çünkü Allah (herşeyi) bilendir, haber alandır.'),
(529, 4, 36, 'Allah\'a kulluk edin, O\'na hiçbir şeyi ortak koşmayın, ana babaya, akrabaya, öksüzlere, yoksullara, yakın komşuya, uzak komşuya, yan(ınız)daki arkadaşa, yolcuya, ellerinizin altında bulunanlara iyilik edin. Allah, kurumlu, böbürlenen insanları sevmez.'),
(530, 4, 37, 'Bunlar öyle insanlardır ki, cimrilik ederler ve insanlara da cimriliği emrederler, Allah\'ın bol hazinesinden kendilerine verdiğini gizlerler. (Biz de) o nankörlere alçaltıcı bir azab hazırlamışızdır.'),
(531, 4, 38, 'Bunlar mallarını insanlara gösteriş için verirler, Allah\'a, ve ahiret gününe inanmazlar. Kimin arkadaşı şeytan ise, o(nun) ne kötü bir arkadaş(ı var)dır!'),
(532, 4, 39, 'Onlara ne olurdu sanki Allah\'a ve ahiret gününe inansalardı ve Allah\'ın kendilerine verdiği rızıktan Allah yolunda harcasalardı! Allah onları biliyordu.'),
(533, 4, 40, 'Allah zerre kadar haksızlık etmez, zerre miktarı bir iyilik olsa onu kat kat yapar ve kendi katından da büyük bir mükafat verir.'),
(534, 4, 41, 'Her ümmetten (inanç ve davranışlarının doğru olup olmadığına tanıklık edecek) bir şahid, seni de bunlara şahid getirdiğimiz zaman (halleri) nice olur?'),
(535, 4, 42, 'Nankörlük edip, (Allah\'ın) Elçi(sin)e karşı gelenler, o gün yerin dibine geçirilmeyi isterler ve Allah\'tan hiçbir söz gizleyemezler.'),
(536, 4, 43, 'Ey inananlar, sarhoşken namaza yaklaşmayın ki ne dediğinizi bilesiniz. Yoldan geçici olmanız dışında, cünüp iken de yıkanıncaya kadar (namaza yaklaşmayın). Eğer hasta, yahut yolculukta iseniz, yahut biriniz tuvaletten gelmişse, yahut da kadınlara dokunmuşsanız (bu durumlarda) su bulamadığınız takdirde temiz toprağa teyemmüm edin: (Toprağı) yüzlerinize ve ellerinize sürün. Şüphesiz Allah, çok affeden, çok bağışlayandır.'),
(537, 4, 44, 'Şu kendilerine Kitaptan bir pay verilenleri görmedin mi? (Baksana onlar,) sapıklığı satın alıyorlar, istiyorlar ki, siz de yolu sapıtasınız.'),
(538, 4, 45, 'Allah sizin düşmanlarınızı daha iyi bilir. Dost olarak Allah yeter, yardımcı olarak da Allah yeter.'),
(539, 4, 46, 'Yahudilerden öyleleri var ki, kelimeleri yerlerinden kaydırıyorlar. Dillerini eğip bükerek ve dini taşlayarak: "İşittik ve isyan ettik", "dinle, dinlemez olası" ve: "ra\'ina" diyorlar. Eğer onlar: "İşittik ve ita\'at ettik", "Dinle ve bize bak!" deselerdi, elbette kendileri için daha iyi olurdu. Fakat Allah, inkarlarından dolayı onları la\'netlemiştir, pek az inanırlar.'),
(540, 4, 47, 'Ey Kitap verilenler, biz bazı yüzleri, silip arkalarına döndürmeden, ya da Cumartesi adamlarını la\'netlediğimiz gibi onları da la\'netlemeden önce, yanınızdakini doğrulayıcı olarak indirdiğimiz(Kur\'an)a inanın. Allah\'ın buyruğu yapılır.'),
(541, 4, 48, 'Allah, kendisine ortak koşulmasını bağışlamaz, bundan başkasını dilediğine bağışlar. Allah\'a ortak koşan da gerçekten büyük bir günah işlemiştir.'),
(542, 4, 49, 'Şu kendilerini övüp yüceltenleri görmedin mi? Hayır, ancak Allah dilediğini yüceltir, onlara kıl kadar zulmedilmez.'),
(543, 4, 50, 'Bak nasıl Allah\'a yalan uyduruyorlar. Apaçık bir günah olarak bu (onlara) yeter.'),
(544, 4, 51, 'Kendilerine Kitaptan bir pay verilenleri görmedin mi? (Baksana onlar) cibt ve tağut\'a inanıyorlar ve inkar edenler için: "Bunlar, inananlardan daha doğru yoldadır" diyorlar.'),
(545, 4, 52, 'İşte onlar, Allah\'ın la\'netlediği insanlardır. Allah, kimi la\'netlerse artık onun için hiçbir yardımcı bulamazsın.'),
(546, 4, 53, 'Yoksa onların mülkten bir payı mı var? Öyle olsaydı insanlara bir çekirdek zerresi bile vermezlerdi.'),
(547, 4, 54, 'Yoksa Allah\'ın, lutfundan insanlara verdiği (vahiyler) yüzünden onları kıskanıyorlar mı? Oysa biz İbrahim soyuna da Kitabı ve hikmeti vermiş ve onlara büyük bir mülk vermiştik.'),
(548, 4, 55, 'Onlardan kimi O(Hak Kitabı)na inandı, kimi de ondan yüz çevirdi. Öylesine de çılgın alevli cehennem yetti.'),
(549, 4, 56, 'O ayetlerimizi inkar edenleri yakında bir ateşe sokacağız, derileri piştikçe azabı tadsınlar diye onlara başka deriler vereceğiz! Şüphesiz Allah daima üstündür, hüküm ve hikmet sahibidir.'),
(550, 4, 57, 'İnanıp iyi işler yapanları da altlarından ırmaklar akan cennetlere sokacağız. Orada sürekli kalacaklardır. Orada kendilerine tertemiz eşler de vardır ve onları (hiç güneş sızmayan) eşsiz bir gölgeye sokacağız.'),
(551, 4, 58, 'Allah, size emanetleri ehline vermenizi, insanlar arasında hükmettiğiniz zaman adaletle hükmetmenizi emreder. Allah size böylece ne güzel öğüt veriyor. Doğrusu, Allah işiten, görendir.'),
(552, 4, 59, 'Ey inananlar, Allah\'a ita\'at edin, Elçiye ve sizden olan buyruk sahibine ita\'at edin. Eğer herhangi bir şeyde anlaşmazlığa düşerseniz; -Allah\'a ve ahiret gününe inanıyorsanız -onu Allah\'a ve Elçiye götürün. Bu, daha iyidir ve sonuç bakımından da daha güzeldir.'),
(553, 4, 60, 'Şunları görmedin mi, kendilerinin, sana indirilene ve senden önce indirilene inandıklarını sanıyorlar da hakem olarak tağuta (o azgın şeytana) başvurmak istiyorlar! Oysa kendilerine onu inkar etmeleri emredilmişti. Şeytan da onları iyice saptırmak istiyor.'),
(554, 4, 61, 'Kendilerine: "Allah\'ın indirdiğine ve Elçiye gelin!" denince, o ikiyüzlülerin, senden büsbütün uzaklaştıklarını görürsün.'),
(555, 4, 62, 'Ya nasıl, elleriyle yaptıkları (kötülükler) yüzünden başlarına bir felaket gelince, hemen sana gelirler: "Biz sadece iyilik etmek ve uzlaştırmak istedik." diye Allah\'a yemin ederler?'),
(556, 4, 63, 'Allah onların kalblerinde olanı bilir. Onlara aldırma, onlara öğüt ver ve onların içlerine işleyecek güzel söz seyle!'),
(557, 4, 64, 'Biz hiçbir elçiyi, Allah\'ın izniyle ita\'at edilmekten başka bir amaçla göndermedik. Eğer onlar, kendilerine zulmettikleri zaman sana gelseler, Allah\'tan, günahlarını bağışlamasını isteseler ve Elçi de onların bağışlanmasını dileseydi, elbette Allah\'ı affedici, merhametli bulurlardı.'),
(558, 4, 65, 'Hayır, Rabin hakkı için onlar aralarında çıkan çekişmeli işlerde seni hakem yapıp, sonra da senin verdiğin hükme, içlerinde bir burukluk duymadan tam anlamıyle teslim olmadıkça inanmış olmazlar.'),
(559, 4, 66, 'Eğer onlara: "Kendinizi öldürün, ya da yurtlarınızdan çıkın!" diye yazmış olsaydık, içlerinden pek azı hariç, bunu yapmazlardı. Ama kendilerine öğütleneni yapsalardı, elbette kendileri için daha iyi ve daha sağlam olurdu.'),
(560, 4, 67, 'O zaman kendilerine katımızdan büyük mükafat verirdik.'),
(561, 4, 68, 'Ve onları doğru bir yola iletirdik.'),
(562, 4, 69, 'Kim Allah\'a ve Elçi\'ye ita\'at ederse işte onlar, Allah\'ın ni\'met verdiği peygamberler, sıddiklar, şehidler ve Salihlerle beraberdir. Onlar da ne güzel arkadaştır!'),
(563, 4, 70, 'Bu ni\'met, Allah\'tandır. Bilen olarak Allah yeter.'),
(564, 4, 71, 'Ey inananlar, (uyanık bulunup) korunma(tedbirleri)nizi alın, bölük bölük, ya da hep birlikte savaşa gidin.'),
(565, 4, 72, 'İçinizden bir kısmı var ki, pek ağır davranır. Eğer size bir felaket erişirse: "Allah bana lutfetti de onlarla beraber bulunmadım." der.'),
(566, 4, 73, 'Eğer Allah\'tan size bir ni\'met erişirse, sizinle kendisi arasında hiç sevgi yokmuş gibi: "Keşke ben de onlarla beraber olsaydım da büyük bir başarı kazansaydım!" der.'),
(567, 4, 74, 'Dünya hayatını ahiret hayatı karşılığında satanlar, Allah yolunda savaşsınlar. Kim Allah yolunda savaşır da öldürülür veya galib gelirse, biz ona yakında büyük bir mükafat vereceğiz.'),
(568, 4, 75, 'Size ne oldu ki Allah yolunda ve; "Rabbimiz bizi şu, halkı zalim kentten çıkar, bize katından bir koruyucu ver, bize katından bir yardımcı ver!" diyen zayıf erkek, kadın ve çocuklar uğrunda savaşmıyorsunuz?'),
(569, 4, 76, 'İnananlar Allah yolunda savaşırlar, inkar edenler de tağut yolunda savaşırlar. O halde şeytanın dostlarıyle savaşın, çünkü şeytanın hilesi zayıftır:'),
(570, 4, 77, 'Kendilerine: "Ellerinizi (savaştan) çekin, namazı kılın, zekatı verin!" denilenleri görmedin mi? Kendilerine savaş yazılınca hemen içlerinden bir grup, insanlardan, Allah\'tan korkar gibi hatta daha fazla korkmaya başladılar: "Rabbimiz, niçin bize savaş yazdın? Bizi yakın bir süreye kadar ertelesen (savaş emrini bir süre geciktirsen) olmaz mıydı?" dediler. De ki: "dünya geçimi azdır, korunan için ahiret daha iyidir. Size kıl kadar haksızlık edilmez."'),
(571, 4, 78, 'Nerede olsanız, sağlam kaleler içinde de bulunsanız yine ölüm sizi bulur. Onlara bir iyilik erişirse: "Bu, Allah tarafındandır" derler. Onlara bir kötülük erişirse: "Bu, senin yüzündendir" derler. De ki: "Hepsi Allah tarafındandır". Bu topluma ne oluyor ki hemen hiç söz anlamıyorlar?'),
(572, 4, 79, 'Sana gelen her iyilik Allah\'tandır, sana gelen her kötülük de kendi(günahın yüzü)ndendir. Seni insanlara elçi gönderdik. (Buna) şahid olarak Allah yeter.'),
(573, 4, 80, 'Kim Elçi\'ye ita\'at ederse Allah\'a ita\'at etmiş olur. Kim de yüz çevirirse (bil ki), biz seni onların üzerine bekçi göndermedik.'),
(574, 4, 81, 'Peki, derler, ama yanından çıkınca içlerinden birtakımı senin söylemiş olduğunun tersini kurar. Allah, onların geceleyin ne düşünüp kurduklarını yazmaktadır. Sen onlara aldırma, Allah\'a dayan. (Sana) vekil olarak Allah yeter.'),
(575, 4, 82, 'Kur\'an\'ı düşünmüyorlar mı? Eğer Allah\'tan başkası tarafından (indirilmiş) olsaydı, onda birbirini tutmaz çok şey bulurlardı.'),
(576, 4, 83, 'Onlara güven veya korkuya dair bir haber gelse onu yayarlar. Halbuki onu Elçi\'ye ve aralarında buyruk sahiplerine götürselerdi, işin içyüzünü araştırıp çıkaranlar, onun ne olduğunu (haberin taşıdığı anlamı) bilirlerdi. Eğer size Allah\'ın lutfu ve rahmeti olmasaydı, birçok işinizde şeytana uyardınız.'),
(577, 4, 84, '(Ey Muhammed), Allah yolunda savaş; sen yalnız kendinden sorumlusun! İnananları da (savaşa) teşvik et. Umulur ki Allah, kafirlerin gücünü kırar. Allah\'ın baskını daha güçlü, cezası daha çetindir.'),
(578, 4, 85, 'Kim güzel bir (işe) destek olursa, onun da o işten bir payı olur. Kim kötü bir (işe) destek olursa, onun da o işten bir payı olur. Allah her şeyi gözetip karşılığını verendir.'),
(579, 4, 86, 'Bir selam ile selamlandığınız zaman siz de ondan daha güzeliyle selam verin; yahut verilen selamı aynen iade edin. Şüphesiz Allah, her şeyi hesaplayandır.'),
(580, 4, 87, 'Allah -ki O\'ndan başka tanrı yoktur- sizi, vukuunda asla şüphe olmayan kıyamet (Duruşma) gününde bir araya toplayacaktır. Allah\'tan daha doğru sözlü kim olabilir?'),
(581, 4, 88, 'Size ne oldu ki, münafıklar hakkında iki gruba ayrıldınız? Oysa yaptıkları işlerden dolayı Allah onları baş aşağı etmiştir. Allah\'ın saptırdığını doğru yola iletmek mi istiyorsunuz? Allah birini saptırırsa artık onun için bir yol bulamazsınız!'),
(582, 4, 89, 'Sizin de kendileri gibi inkar etmenizi istediler ki, onlarla bir olasınız. O halde onlar Allah yolunda göç edinceye kadar onlardan dostlar edinmeyin. Eğer yüz çevirirlerse onları yakalayın, nerede bulursanız öldürün ve onlardan ne dost, ne de yardımcı tutmayın!'),
(583, 4, 90, 'Ancak sizinle kendileri arasında andlaşma bulunan bir topluma sığınanlar, yahut ne sizinle ne de kendi toplumlarıyle savaşmak(istemedikleri)nden yürekleri sıkılarak size gelenler hariç. Allah dileseydi, onları sizin üstünüze salardı, sizinle savaşırlardı. O halde onlar, sizden uzak dururlar, sizinle savaşmazlar ve sizinle barış içinde yaşamak isterlerse, Allah size, onlara saldırmak için bir yol vermemiştir.'),
(584, 4, 91, 'Başka birtakım insanlar da bulacaksınız ki, hem sizden, hem de kendi toplumlarından emin olmak isterler. Ama ne zaman fitneye götürülseler, fitnenin içine başaşağı atılırlar. Eğer onlar sizden uzak durmazlar, sizinle barış içinde yaşamak istemezler, ellerini (saldırıdan) çekmezlerse onları yakalayın ve nerede bulursanız öldürün! İşte öylelerine karşı size açık bir yetki verdik.'),
(585, 4, 92, 'Yanlışlık dışında bir mü\'min, bir mü\'mini öldüremez: Yanlışlıkla bir mü\'mini öldüren kimsenin, mü\'min bir köle azadetmesi ve ölenin ailesine de bir diyet vermesi gerekir. Eğer (ölenin ailesi), bağışlar(diyetten vazgeçer)lerse başka. (Öldürülen) mü\'min, düşmanınız olan bir topluluktan ise mü\'min bir köle azadetmek gerekir. Ve eğer sizinle kendileri arasında andlaşma bulunan bir topluluktan ise ailesine verilecek bir diyet ve mü\'min bir köle azadetmek lazımdır. Bunları bulamayan kimsenin, Allah tarafından tevbesinin kabulü için iki ay ardı ardına oruç tutması gerekir. Allah bilendir, hüküm ve hikmet sahibidir.'),
(586, 4, 93, 'Her kim bir mü\'mini kasden öldürürse -onun cezası-, içinde sürekli kalacağı cehennemdir. Allah ona gazabetmiş, la\'net etmiş ve onun için büyük bir azab hazırlamıştır!'),
(587, 4, 94, 'Ey inananlar, Allah yolunda savaşa çıktığınız zaman iyi anlayın, dinleyin, size selam verene, dünya hayatının geçici menfaatini gözeterek: "Sen mü\'min değilsin!" demeyin. Çünkü Allah\'ın yanında çok ganimetler vardır. Önceden siz de öyle idiniz, Allah size lutfetti (imana geldiniz). O halde iyice anlayın (dinleyin, peşin hüküm vermeyin). Çünkü Allah yaptıklarınızı haber almaktadır.'),
(588, 4, 95, 'İnananlardan özürsüz olarak yerlerinde oturanlar ile, mallariyle canlariyle Allah yolunda cihadedenler bir olmaz. Allah, mallariyle canlariyle cihadedenleri, derece bakımından oturanlardan üstün kılmıştır. Gerçi Allah hepsine de güzellik va\'detmiştir ama mücahidleri oturanlardan çok daha büyük ecirle üstün kılmıştır:'),
(589, 4, 96, 'Kendi katından yüksek dereceler, bağış ve rahmet. Allah, bağışlayandır, esirgeyendir.'),
(590, 4, 97, 'Nefislerine yazık eden kimselere, canlarını alırken melekler: "Ne işte idiniz (dininiz için ne yapıyordunuz)?" dediler. (Bunlar): "Biz yer yüzünde aciz düşürülmüştük." diye cevap verdiler. Melekler dediler ki: "Allah\'ın yeri geniş değil miydi ki onda göç ed(ip gönlünüzce yaşayabileceğiniz bir yere gid)eydiniz?" İşte onların durağı cehennemdir, ne kötü bir gidiş yeridir orası!'),
(591, 4, 98, 'Yalnız hiçbir çareye gücü yetmeyen ve göç için yol bulamayan, gerçekten zayıf erkekler, kadınlar ve çocuklar hariç.'),
(592, 4, 99, 'Çünkü Allah\'ın onları affetmesi umulur. Allah, çok affeden, çok bağışlayandır.'),
(593, 4, 100, 'Allah yolunda göç eden kimse, yeryüzünde gidecek çok yer ve bolluk bulur. Kim Allah ve Elçisi için göç etmek amacıyle evinden çıkar da kendisine ölüm yetişirse, onun mükafatı Allah\'a düşer. Allah, bağışlayandır, esirgeyendir.'),
(594, 4, 101, 'Yeryüzünde sefere çıktığınız zaman inkar edenlerin size bir kötülük yapmalarından korkarsanız, namazı kısaltmanızdan ötürü size bir günah yoktur. Muhakkak ki kafirler, sizin açık düşmanınızdır.'),
(595, 4, 102, 'Sen de içlerinde bulunup onlara namazı başlattığın zaman onlardan bir bölük seninle beraber namaza dursun ve silahlarını da yanlarına alsınlar. (Namazda olanlar), secde edince arkanıza geçsinler; bu kez namaz kılmayan öteki bölük gelsin, seninle beraber namaz kılsınlar, korunma(tedbir)lerini ve silahlarını da alsınlar. İnkar edenler istediler ki siz silahlarınızdan ve eşyanızdan gaflet etseniz de birden üzerinize bir baskın yapsalar. Yağmurdan zahmet çekerseniz, ya da hasta olursanız, silahlarınızı bırakmanızda size bir günah yoktur. Ama korunma tedbirinizi alın (uyanık bulunun). Allah, kafirlere alçaltıcı bir azab hazırlamıştır.'),
(596, 4, 103, 'Namazı bitirdiğiniz zaman ayakta, oturarak ve yanlarınız üzerinde (uzanarak) Allah\'ı anın; güvene kavuştunuz mu namazı (tam) kılın. Çünkü namaz, mü\'minlere vakitli olarak farz kılınmıştır.'),
(597, 4, 104, 'O topluluğu takibetmekte gevşeklik göstermeyin. Eğer siz acı çekiyorsanuz, onlar da sizin acı çektiğiniz gibi acı çekmektedirler. Üstelik siz Allah\'tan, onların ummayacakları şeyleri, ummaktasınız. Alah bilendir, hüküm ve hikmet sahibidir.'),
(598, 4, 105, 'Biz sana Kitabı gerçek ile indirdik ki, insanlar arasında Allah\'ın sana gösterdiği biçimde hüküm veresin; hainlerin savunucusu olma!'),
(599, 4, 106, 'Allah\'tan mağfiret dile. Kuşkusuz Allah bağışlayandır, esirgeyendir.'),
(600, 4, 107, 'Kendilerine hainlik edenleri savunma; zira Allah, hainlik yapıp günah işleyen insanı sevmez!'),
(601, 4, 108, '(Günah işlerken) İnsanlardan gizleniyorlar da Allah\'tan gizlenmiyorlar. Oysa geceleyin O\'nun istemediği sözü söyle(yip plan kura)rlarken O, onlarla beraberdir. Allah, onların yaptıkları herşeyi kuşatmıştır (hiçbir şeyi O\'ndan gizleyemezler).'),
(602, 4, 109, 'Haydi siz, dünya hayatında onları savundunuz; ya kıyamet günü onları Allah\'a karşı kim savunacak, ya da kim onlara vekil olacak?'),
(603, 4, 110, 'Kim bir kötülük yapar, yahut nefsine zulmeder de sonra Allah\'tan mağfiret dilerse, Allah\'ı bağışlayıcı ve esirgeyici bulur.'),
(604, 4, 111, 'Kim bir günah işlerse onu kendi aleyhine kazanır. Allah bilendir, hüküm ve hikmet sahibidir.'),
(605, 4, 112, 'Kim bir hata, ya da günah işler de sonra onu bir suçsuzun üstüne atarsa, muhakkak ki büyük bir iftira ve açık bir günah yüklenmiş olur.'),
(606, 4, 113, 'Allah\'ın sana lutfu ve acıması olmasaydı, onlardan bir grup, seni saptırmağa yeltenmişti. Onlar sadece kendilerini saptırırlar, sana hiçbir zarar veremezler. Allah, sana Kitabı ve hikmeti indirdi ve sana bilmediğin şeyleri öğretti. Allah\'ın sana lutfu, cidden büyüktür.'),
(607, 4, 114, 'Onların aralarındaki gizli konuşmaların çoğunda hayır yoktur. Yalnız sadaka, yahut iyilik, ya da insanların arasını düzeltmeyi emreden(in konuşması) hariç. Kim Allah\'ın rızasını kazanmak amacıyle bunu yaparsa, yakında ona büyük bir mükafat vereceğiz.'),
(608, 4, 115, 'Kim de kendisine doğru yol belli olduktan sonra Elçi\'ye karşı gelir ve mü\'minlerin yolundan başka bir yola uyarsa, onu döndüğü yola yöneltiriz ve cehenneme sokarız. Ne kötü bir gidiş yeridir orası!'),
(609, 4, 116, 'Allah, kendisine ortak koşulmasını bağışlamaz, bundan başka herşeyi dilediğine bağışlar. Allah\'a ortak koşan da uzak bir sapıklığa düşmüştür.'),
(610, 4, 117, 'O(Allah\'a ortak koşa)nlar, O\'nu bırakıp birtakım dişilerden başkasına çağırmıyorlar ve onlar, (hayırsız) asi şeytandan başkasına yalvarmıyorlar.'),
(611, 4, 118, '(O şeytan) Ki Allah ona la\'net etti ve o da, "Elbette senin kullarından belirli bir pay alacağım" dedi.'),
(612, 4, 119, 'Onları mutlaka saptıracağım, mutlaka onları boş kuruntulara sokacağım ve onlara emredeceğim: hayvanların kulaklarını yaracaklar; onlara emredeceğim: Allah\'ın yaratışını değiştirecekler! kim Allah\'ın yerine şeytanı dost tutarsa, muhakkak ki açık bir ziyana uğramıştır.'),
(613, 4, 120, '(Şeytan) Onlara söz verir, umut verir, fakat şeytanın onlara sözü, aldatmadan başka bir şey değildir.'),
(614, 4, 121, 'İşte onların varacağı yer cehennemdir. Asla ondan kaçmak (imkanı) bulamazlar.'),
(615, 4, 122, 'İnanıp iyi işler yapanları da altlarından ırmaklar akan cennetlere sokacağız, orada ebedi kalacaklardır. Bu, Allah\'ın gerçek va\'didir. Allah\'tan daha doğru sözlü kim olabilir?'),
(616, 4, 123, '(İş) Ne sizin kuruntularınızla, ne Kitap ehlinin kuruntularıyla olmaz. Kötülük yapan, onunla cezalandırılır ve kendisine Allah\'tan başka ne dost, ne de yardımcı bulamaz (Allah\'ın vereceği cezayı hiç kimse ondan savamaz).'),
(617, 4, 124, 'Erkek veya kadından her kim inanarak güzel işler yaparsa, işte öyle kimseler cennete girerler ve zerre kadar haksızlığa uğratılmazlar.'),
(618, 4, 125, 'Hangi insan, din yönünden, iyilik edici olarak yüzünü Allah\'a teslim edip dosdoğru İbrahim dinine tabi olandan daha güzel olabilir? Allah, İbrahim\'i dost edinmişti.'),
(619, 4, 126, 'Göklerde ve yerde olanların hepsi Allah\'ındır. Allah(ın bilgisi), herşeyi kuşatmıştır.'),
(620, 4, 127, 'Senden, kadınlar hakkında fetva istiyorlar. De ki: "Allah, size onlar hakkında hükmünü açıklıyor: Kendilerine yazılmış olan(miras hakların)ı vermeyip kendileriyle evlenmek istediğiniz öksüz kadınlar, zavallı çocuklar ve öksüzlere karşı adaleti yerine getirmeniz hakkında Kitapta size okunan(ayet)ler (de Allah\'ın hükmünü açıklamaktadır)". Yapacağınız her hayrı muhakkak ki Allah bilir.'),
(621, 4, 128, 'Ve eğer bir kadın, kocasının huysuzluğundan, yahut kendisinden yüz çevirmesinden korkarsa, anlaşma ile aralarını düzeltmelerinde ikisine de günah yoktur. Barış daima iyidir. Zaten nefisler cimriliğe hazır duruma getirilmiştir (insanın mayasında cimrilik vardır). Eğer güzel geçinir, (kötülükten) sakınırsanız, Allah yaptıklarınızı haber alır (yaptığınız güzel işler boşa gitmez).'),
(622, 4, 129, 'Ne kadar isteseniz de kadınlar arasında (tam) adalet yapamazsınız. Öyle ise (birine) tamamen yönelip ötekini askıda (kocasızmış) gibi bırakmayın. Eğer arayı düzeltir, sakınırsanız, Allah bağışlayandır, esirgeyendir.'),
(623, 4, 130, 'Eğer (eşler) ayrılırlarsa, Allah bol ni\'metiyle onların her birini zengin eder (diğerine muhtaceylemez). Allah(ın ni\'meti) geniştir, (O) hüküm ve hikmet sahibidir.'),
(624, 4, 131, 'Göklerde ve yerde olanların hepsi Allah\'ındır. Sizden önce Kitap verilenlere de, size de "Allah\'tan korkun!" diye tavsiye ettik. Eğer inkar ederseniz, (bilin ki) göklerde ve yerde olanların hepsi Allah\'ındır. Allah, zengindir, Övgüye layıktır.'),
(625, 4, 132, 'Göklerde ve yerde olanların hepsi Allah\'ındır. Vekil olarak Allah yeter.'),
(626, 4, 133, 'Ey insanlar, (Allah) dilerse sizi götürür ve başkalarını getirir. Allah, bunu yapabilir.'),
(627, 4, 134, 'Kim dünya sevabını isterse (bilsin ki) dünya ve ahiret sevabı Allah katındadır. Allah işitendir, görendir.'),
(628, 4, 135, 'Ey inananlar, adaleti tam yerine getirerek Allah için şahidlik edenler olun, kendinizin, ana babanızın ve yakınlarınızın aleyhinde bile olsa, (şahidlik ettiğiniz kimseler) zengin veya fakir de olsalar (adaletten ayrılmayın). Çünkü Allah, ikisine de daha yakındır (onları sizden çok kayırır). Öyle ise keyfinize uyarak doğruluktan sapmayın. Eğer (şahidlik ederken dilinizi) eğip bükerseniz, ya da doğruyu söylemezseniz, muhakkak ki Allah yaptıklarınızı bilir.'),
(629, 4, 136, 'Ey inananlar, Allah\'a, Elçisine, Elçisine indirdiği Kitaba ve daha önce indirmiş bulunduğu Kitaba inanın. Kim Allah\'ı, meleklerini, Kitaplarını, elçilerini ve ahiret gününü inkar ederse o, uzak bir sapıklığa düşmüştür.'),
(630, 4, 137, 'Onlar ki inandılar, sonra inkar ettiler; daha sonra yine inandılar, yine inkar ettiler, sonra inkarları arttı; işte Allah onları ne bağışlayacak, ne de doğru yola iletecektir.'),
(631, 4, 138, 'Münafıklara, acı bir azabın kendilerinin olacağını müjdele!'),
(632, 4, 139, 'Onlar mü\'minleri bırakıp kafirleri dost tutuyorlar. Onların yanında şeref mi arıyorlar? Bütün şeref, tamamen Allaha aittir.'),
(633, 4, 140, '(Allah) Size Kitapta indirmişti ki: Allah\'ın ayetlerinin inkar edildiğini ve onlarla alay edildiğini işittiğiniz zaman, onlar (bu sözü bırakıp) başka bir söze dalıncaya kadar onlarla beraber oturmayın; yoksa siz de onlar gibi olursunuz. Şüphesiz Allah, bütün iki yüzlüleri ve kafirleri cehennemde toplayacaktır.'),
(634, 4, 141, 'Onlar sizi gözetleyip dururlar. Eğer size Allah\'tan bir fetih nasibolursa: "Biz de sizinle beraber değil miydik?" derler. Ve eğer savaşta kafirlerin bir payı olur(savaşı düşmanlarınız kazanır)sa, (bu kez onlara): "Biz size üstünlük sağlayıp, sizi mü\'minlerden korumadık mı?" derler. Artık kıyamet gününde Allah, aranızda hükmedecek ve mü\'minlere karşı kafirlere asla yol vermeyecektir.'),
(635, 4, 142, 'İki yüzlüler, Allah\'ı (guya) aldatmağa çalışırlar. Oysa, O, onları aldatır. Namaza kalktıkları zaman da üşene üşene kalkarlar, insanlara gösteriş yaparlar, Allah\'ı pek az anarlar.'),
(636, 4, 143, 'Arada yalpalayıp dururlar. Ne bunlara (bağlanırlar), ne de onlara. Allah\'ın şaşırttığı kimseye bir (çıkar) yol bulamazsın!'),
(637, 4, 144, 'Ey inananlar, mü\'minleri bırakıp kafirleri dost tutmayın! Allah\'a, aleyhinizde olacak açık bir delil vermek mi istiyorsunuz?'),
(638, 4, 145, 'Doğrusu iki yüzlüler, ateşin en aşağı tabakasındadırlar. Onlar için hiçbir yardımcı bulamazsın.'),
(639, 4, 146, 'Ancak tevbe edenler, uslananlar, Allah\'a yapışanlar ve dinlerini sırf Allah için yapanlar (yalnız O\'na tapanlar), işte onlar mü\'minlerle beraberdir; Allah da yakında mü\'minlere büyük bir mükafat verecektir.'),
(640, 4, 147, 'Siz şükreder. inanırsanız Allah size azabetmeyi ne yapacak? Allah şükrün karşılığını veren, (herşeyi) bilendir.'),
(641, 4, 148, 'Allah, kendisine haksızlık edilen dışında (hiç kimse tarafından) açıkça kötü söz söylenmesini sevmez. Doğrusu Allah, işitendir, bilendir.'),
(642, 4, 149, 'Bir iyiliği, açığa vurur veya onu gizlerseniz yahut bir kötülüğü affederseniz (bilin ki) Allah da affedicidir, güçlüdür.'),
(643, 4, 150, 'Onlar ki Allah\'ı ve elçilerini inkar ederler, Allah ile elçilerinin arasını ayırmak isterler, "Kimine inanırız, kimini inkar ederiz!" derler; bu ikisinin (inanmakla inkarın) arasında bir yol tutmak isterler.'),
(644, 4, 151, 'İşte onlar gerçek kafirlerdir. Biz de kafirlere alçaltıcı bir azab hazırlamışızdır!'),
(645, 4, 152, 'Ve onlar ki, Allah\'a ve elçilerine inandılar, onlardan hiçbiri arasında ayırım yapmadılar; işte (Allah), pek yakında onların da mükafatlarını verecektir. Allah, çok bağışlayan, çok esirgeyendir.'),
(646, 4, 153, 'Kitap ehli, senden, kendilerine gökten bir Kitap indirmeni istiyorlar. Musa\'dan bundan daha büyüğünü istemişler: "Allah\'ı bize açıkça göster!" demişlerdi. Haksızlıklarından dolayı derhal onları yıldırım gürültüsü yakalamıştı. Sonra kendilerine açık deliller gelmişken buzağıyı (tanrı) tutmuşlardı. Bundan da vazgeçtik ve Musa\'ya açık bir yetki verdik.'),
(647, 4, 154, 'Söz vermeleri için Tur\'u üzerlerine kaldırdık ve onlara: "Secde ederek kapıdan girin!" dedik. Ve onlara: "Cumartesi(yasakları)nı çiğnemeyin!" dedik. Ve onlardan sağlam bir söz aldık.'),
(648, 4, 155, 'Sözlerini bozmalarından, Allah\'ın ayetlerini inkar etmelerinden, haksız yere peygamberleri öldürmelerinden ve "Kalblerimiz kılıflı" demelerinden ötürü (başlarına belalar getirdik). Hayır, fakat inkarlarından ötürü Allah o kalblerin üzerini mühürlemiştir. Artık pek az inanırlar.'),
(649, 4, 156, 'Küfürlerinden ve Meryem\'e büyük bir iftira atmalarından;'),
(650, 4, 157, 'Biz Allah\'ın elçisi, Meryem oğlu Îsa Mesih\'i öldürdük! demelerinden ötürü (belalara uğradılar). Oysa onu öldürmediler ve asmadılar; fakat (bu iş) kendilerine, benzer gösterildi. Onun hakkında ayrılığa düşenler, ondan yana tam bir kuşku içindedirler. O hususta kesin bilgileri yoktur. Sadece zanna uyuyorlar. Onu yakinen öldürmediler (onu öldürdüklerini kesin biçimde bilemediler).'),
(651, 4, 158, 'Hayır, Allah onu kendisine yükseltti. Allah daima üstündür, hüküm ve hikmet sahibidir.'),
(652, 4, 159, 'Andolsun, Kitap ehlinden hiç kimse yoktur ki, ölümünden önce ona inanacak olmasın. kıyamet günü de O, (Îsa) onların aleyhine şahid olacaktır.'),
(653, 4, 160, 'yahudilerin yaptıları zulümlerden, çok kimseyi Allah yolundan çevirmelerinden dolayı kendilerine helal kılınmış temiz ve hoş şeyleri onlara yasakladık.'),
(654, 4, 161, 'Menedildikleri halde riba almalarından ve haksız yere insanların mallarını yemelerinden ötürü (böyle yaptık). İçlerinden inkar edenlere de acı bir azab hazırladık.'),
(655, 4, 162, 'Fakat içlerinden ilimde derinleşmiş olanlar ve mü\'minler, sana indiriline ve senden önce indirilene inanırlar. O namazı kılanlar, zekatı verenler, Allah\'a ve ahiret gününe inananlar var ya, işte onlara büyük bir mükafat vereceğiz!'),
(656, 4, 163, 'Biz, Nuh\'a ve ondan sonra gelen peygamberlere vahyettiğimiz gibi, sana da vahyettik. Nitekim İbrahim\'e, İsma\'il\'e, İshak\'a, Ya\'kub\'a, sıbtlara, Îsa\'ya, Eyyub\'a, Yunus\'a, Harun\'a, Süleyman\'a da vahyetmiş ve Davud\'a da Zebur\'u vermiştik.'),
(657, 4, 164, 'Daha önce sana anlattığımız elçilere ve sana anlatmadığımız elçilere de (vahyetmiştik). Ve Allah Musa\'ya da konuşmuştu.'),
(658, 4, 165, '(Bunları) Müjdeleyici ve uyarıcı elçiler olarak (gönderdik) ki, elçiler geldikten sonra insanların Allah\'a karşı bahaneleri kalmasın. Allah üstündür, hüküm ve hikmet sahibidir.'),
(659, 4, 166, 'Allah, sana indirdiğini kendi bilgisiyle indirmiş olduğuna şahidlik eder. Melekler de (buna) şahidlik ederler. Allah\'ın şahidliği de (bir şeyin gerçekliği için) kafidir.'),
(660, 4, 167, '(Sana gelenleri) İnkar edip Allah yolundan menedenler, hakikaten uzak bir sapıklığa düşmüşlerdir.'),
(661, 4, 168, 'O inkar edip zulmedenler var ya, Allah onları ne bağışlayacak, ne de yola iletecektir.'),
(662, 4, 169, 'Sadece cehennemin yoluna (iletecek ve) orada sürekli kalacaklardır. Bu da Allah\'a çok kolaydır.'),
(663, 4, 170, 'Ey İnsanlar, Elçi size, Rabbinizden gerçeği getirdi. Kendi yararınıza olarak (ona) inanın. Eğer inkar ederseniz, bilin ki göklerde ve yerde olanlar Allah\'ındır. Allah bilendir, hüküm ve hikmet sahibidir.'),
(664, 4, 171, 'Ey Kitap ehli, dininizde taşkınlık etmeyin ve Allah hakkında gerçek olmayan şeyleri söylemeyin! Meryem oğlu Îsa Mesih, sadece Allah\'ın elçisi, O\'nun Meryem\'e attığı kelimesi ve O\'ndan bir ruhtur. Allah\'a ve elçilerine inanın, (Allah) "Üçtür" demeyin. Kendi yararınıza olarak buna son verin. Çünkü Allah, yalnız bir tek tanrıdır. Haşa O, çocuk sahibi olmaktan yücedir. Göklerde ve yerde olanların hepsi O\'nundur. Vekil olarak Allah yeter.'),
(665, 4, 172, 'Ne Mesih, Allah\'a kul olmaktan çekinir, ne de (Allah\'a) yaklaştırılmış melekler. Kim O\'na kulluktan çekinir ve büyüklük taslarsa bilsin ki O, onların hepsini kendi huzuruna toplayacaktır.'),
(666, 4, 173, 'İnanıp iyi işler yapanların mükafatlarını eksiksiz ödeyecek ve lutfundan onlara daha fazlasını da verecektir. (Kulluktan) çekinip büyüklük taslayanlara da acı bir şekilde azabedecek ve onlar kendilerine Allah\'tan başka ne bir dost, ne de bir yardımcı bulacaklardır.'),
(667, 4, 174, 'Ey insanlar, size Rabbinizden delil geldi ve size apaçık bir nur indirdik.'),
(668, 4, 175, 'Alah\'a inanıp O\'na yapışanları (Allah), kendinden bir rahmetin ve lutfun içine sokacak ve onları kendisine varan doğru bir yola iletecektir.'),
(669, 4, 176, 'Senden fetva istiyorlar. De ki: Allah size ana-babasız ve çocuksuz kişinin mirası hakkında hükmünü şöyle açıklıyor: Ölen kişinin çocuğu yok, bir kızkardeşi varsa, bıraktığı malın yarısı o(kızkardeşi)nindir. Fakat kendisi, (ölen) kızkardeşinin çocuğu yoksa, onun mirasını (tamamen) alır. Eğer (ölenin) iki kızkardeşi varsa, bıraktığının üçte ikisi onlarındır. Ve eğer (varisler) erkek kadın birçok kardeşler olursa, erkeğe, iki kadının payı kadar (pay) verilir. Şaşırırsınız diye Allah size (hükmünü) açıklıyor. Allah, herşeyi bilir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Ey inananlar, akitleri(zi) yerine getirin. Size oku(nup açıkla)nacak olanların dışında kalan hayvanlar sizin için helal kılındı. Yalnız ihramda iken avı helal saymamak şartiyle. Allah, istediği hükmü verir.'),
(671, 5, 2, 'Ey inananlar, ne Allah\'ın işaretlerine, ne haram aya, ne kurbana, ne gerdanlık(lı kurban)lara ve ne de Rablerinin lutuf ve rızasını arzu ederek Beyt-i haram\'a doğru gelenlere saygısızlık etmeyin. İhramdan çıktığınız zaman avlanabilirsiniz. Sizi Mescid-i haram\'dan çevirdiklerinden dolayı bir topluma karşı beslediğiniz kin, sizi suç işlemeğe itmesin. İyilik ve takva üzerinde yardımlaşın, günah ve düşmanlık üzerinde yardımlaşmayın, Allah\'tan korkun. Çünkü Allah\'ın azabı çetindir.'),
(672, 5, 3, 'Leş, kan, domuz eti, Allah\'tan başkası adına boğazlanan, boğulmuş, (tahta veya taşla) vurul(arak öldürül)müş, yukarıdan düşmüş, boynuzlanmış ve canavar parçalayarak ölmüş olan havyanlar -henüz canları çıkmadan kestikleriniz hariç- dikili taşlar (putlar) adına boğazlanan hayvanlar ve fal oklariyle kısmet (şans) aramanız size haram kılındı. Bunlar fısktır (insanı yoldan çıkaran kötü şeylerdir). Bugün artık inkar edenler, sizin dininiz(i yok etmek)den umudu kesmişlerdir. Onlardan korkmayın, benden korkun! Bugün sizin için dininizi olgunlaştırdım, size ni\'metimi tamamladım ve size din olarak İslam\'a razı oldum. Kim açlıktan daralır, günaha istekle yönelmeden bunlardan yemek zorunda kalırsa ona günah yoktur. Doğrusu, Allah bağışlayandır, esirgeyendir.'),
(673, 5, 4, 'Sana, kendilerine neyin helal kılındığını soruyarlar. De ki: "Size iyi ve temiz şeyler helal kılındı. Allah\'ın size öğrettiğinden öğreterek yetiştirdiğiniz avcı hayvanların, sizin için tuttuklarını yeyin ve üzerine Allah\'ın adını anın, Allah\'tan korkun. Çünkü Allah, hesabı çabuk görendir.'),
(674, 5, 5, 'Bugün size iyi ve temiz şeyler helal kılındı. Kendilerine Kitap verilenlerin yemeği, size helal, sizin yemeğiniz de onlara helaldir. İnanan, namuslu, hür kadınlar ve sizden önce kendilerine Kitap verilenlerden namuslu hür kadınlar -zina etmeksizin, gizli dost tutmaksızın, namuslu bir biçimde (evlenmek üzere) mehirlerini verdiğiniz takdirde-size helaldir. Kim inanmayı kabul etmezse, onun ameli boşa çıkmıştır ve o, ahirette kaybedenlerdendir.'),
(675, 5, 6, 'Ey inananlar, namaza dur(mak iste)diğiniz zaman yıkayın: yüzlerinizi ve dirseklere kadar ellerizi; meshedin: başlarınızı ve topuklara kadar ayaklarınızı. Eğer cünüp iseniz tam temizlenin. Hasta, yahut yolcu iseniz, yahut biriniz tuvaletten gelmişse, ya da kadınlara dokunmuş da su bulamamışsanız temiz toprağa teyemmüm edin; ondan yüzlerinize ve ellerinize sürün. Allah size güçlük çıkarmak istemiyor, fakat sizi temizlemek ve size olan ni\'metini tamamlamak istiyor ki, şükredesiniz.'),
(676, 5, 7, 'Allah\'ın size olan ni\'metini ve O\'na verdiğiniz sözü hatırlayın: Hani "İşittik ve ita\'at ettik!" demiştiniz. Allah\'tan korkun; çünkü Allah, göğüslerin özünü bilir.'),
(677, 5, 8, 'Ey inananlar, Allah için adaletle şahidlik edenler olun. Bir topluluğa karşı duyduğunuz kin, sizi adaletten saptırmasın. Adil davranın, takvaya yakışan budur. Allah\'tan korkun, kuşkusuz Allah yaptıklarınızı haber almaktadır.'),
(678, 5, 9, 'Allah, inanıp iyi işler yapanlara va\'detmiştir: Bağışlama ve büyük mükafat onlarındır.'),
(679, 5, 10, 'İnkar edip ayetlerimizi yalanlayanlara gelince, onlar da cehennemin halkıdır.'),
(680, 5, 11, 'Ey inananlar, Allah\'ın size olan ni\'metini hatırlayın: Hani bir topluluk size ellerini uzatmağa (saldırmaya) yeltenmişti de (Allah) Onların ellerini sizden çekmişti. Alah\'tan korkun. Mü\'minler Allah\'a dayansınlar.'),
(681, 5, 12, 'Allah, İsrail oğullarından söz almıştı ve içlerinden on iki başkan göndermiştik. Allah demişti ki: "Ben sizinle beraberim, eğer namazı kılar, zekatı verirseniz; elçilerime inanır, onlara yardım eder ve Allah\'a güzel borç verirseniz, elbette sizin günahlarınızı örterim ve sizi altlarından ırmaklar akan cennetlere sokarım. Bundan sonra sizden kim nankörlük ederse, düz yoldan sapmış olur.'),
(682, 5, 13, 'Sözlerini bozdukları için onları la\'netledik ve kalblerini katılaştırdık. Kelimeleri yerlerinden kaydırıyorlar. Kendilerine öğütlenen şeyden pay almayı unuttular. İçlerinden pek azı hariç, daima onlardan hainlik görürsün. Yine de onları affet, aldırma, çünkü Allah güzel davrananları sever.'),
(683, 5, 14, 'Biz hıristiyanız diyenlerin de sözünü almıştık, ama Kendilerine öğütlenen şeyden pay almayı unuttular. Bu yüzden kıyamet gününe kadar aralarına düşmanlık ve kin saldık. Yakında Allah, onlara, ne yaptıklarını haber verecektir.'),
(684, 5, 15, 'Ey Kitap ehli, elçimiz size geldi, Kitaptan gizlediğiniz şeylerin çoğunu size açıklıyor, çoğundan da geçiyor. Gerçekten size Allah\'tan bir nur ve açık bir Kitap gelmiştir.'),
(685, 5, 16, 'Allah, onunla rızasına uyanları esenlik yollarına iletir ve onları kendi izniyle karanlıklardan aydınlığa çıkarıp dosdoğru bir yola iletir.'),
(686, 5, 17, 'Allah, Meryem oğlu Mesih\'tir. diyenler küfre gitmişlerdir. De ki: "Öyle ise Allah, Meryem oğlu Mesih\'i, annesini ve yeryüzünde olanların hepsini helak etmek istese, Allah\'a karşı kimin elinde bir şey var?" Göklerde, yerde ve ikisinin arasında bulunan herşey O\'nundur. O, dilediğini yaratır, Allah, herşeyi yapabilendir.'),
(687, 5, 18, 'Yahudiler ve hıristiyanlar; "Biz Allah\'ın oğulları ve sevgilileriyiz." dediler. De ki: "O halde niçin günahlarınızdan ötürü (Allah) size azabediyor?" Hayır, siz de O\'nun yaratıklarından birer insansınız. O dilediğini bağışlar, dilediğine azabeder. Göklerin, yerin ve ikisi arasında bulunan herşeyin mülkü Allah\'ındır. Dönüş de O\'nadır.'),
(688, 5, 19, 'Ey Kitap ehli, elçilerin arasının kesildiği sırada size Elçimiz geldi, size gerçekleri açıklıyor ki, (yarın kıyamette); "Bize bir müjdeleyici ve uyarıcı gelmedi" demeyesiniz. İşte size müjdeleyici ve uyarıcı geldi. Allah, herşeyi yapabilendir.'),
(689, 5, 20, 'Musa, kavmine demişti ki: "Ey kavmim, Allah\'ın size olan ni\'metini hatırlayın; zira (O), aranızda peygamberler var etti, sizi krallar yaptı ve size dünyalarda hiç kimseye vermediğini verdi."'),
(690, 5, 21, 'Ey kavmim, Allah\'ın size yaz(ıp nasibet)diği Kutsal Toprağa girin, arkanıza dönmeyin, yoksa kaybedersiniz!'),
(691, 5, 22, 'Dediler ki: "Ey Musa, orada zorba bir millet var. Onlar oradan çıkmadıkça biz oraya girmeyiz. Eğer çıkarlarsa, o zaman oraya gireriz."'),
(692, 5, 23, '(Allah\'tan) korkanlardan Allah\'ın ni\'met verdiği iki adam dedi ki; "Onların üzerine kapıdan girin, eğer kapıdan girerseniz, muhakkak ki siz galib gelirsiniz. Haydi eğer inanıyorsanız Allah\'a dayanın!"'),
(693, 5, 24, 'Dediler ki: "Ey Musa, onlar orada olduğu sürece biz oraya asla girmeyiz. Sen ve Rabbin, gidin, savaşın, biz burada oturuyoruz!"'),
(694, 5, 25, '(Musa), "Ya Rabbi, dedi, ben kendimden ve kardeşimden başkasına malik değilim. Bizimle, o yoldan çıkmış toplumun arasını ayır."'),
(695, 5, 26, '(Allah) buyurdu ki: "Orası onlara kırk yıl yasaklandı. O yerde şaşkın şaşkın dolaşacaklar. Sen, yoldan çıkmış o toplum için üzülme."'),
(696, 5, 27, 'Onlara iki Adem oğlunun haberini gerçek olarak oku: Hani her biri birer kurban sunmuşlardı, (kurban) birinden kabul edilmiş, ötekinden kabul edilmemişti. (Kurbanı kabul edilmeyen, kabul edilene): "Seni öldüreceğim" demişti. (O da); "Allah, sadece korunanlardan kabul eder" dedi.'),
(697, 5, 28, 'Andolsun, eğer sen beni öldürmek için bana elini uzatırsan, ben seni öldürmek için sana elimi uzatmam. Çünkü ben alemlerin Rabbinden korkarım!'),
(698, 5, 29, 'Ben isterim ki sen, benim günahımı da, senin günahını da yüklenip ateş halkından olasın! zalimlerin cezası budur.'),
(699, 5, 30, 'Nefsi, onu kardeşini öldürmeye çağırdı, (o da nefsine uyarak) onu öldürdü, ziyana uğrayanlardan oldu.'),
(700, 5, 31, 'Derken Allah, ona kardeşinin cesedini nasıl gömeceğini göstermek için yeri eşeleyen bir karga gönderdi. (Karganın yaptığını görünce); "Yazık bana, şu karga kadar olup da kardeşimin cesedini gömmekten aciz miyin (ben?)" dedi ve pişman olanlardan oldu!'),
(701, 5, 32, 'Bundan dolayı İsrail oğullarına şöyle yazdık: Kim, bir cana kıymamış, ya da yeryüzünde bozgunculuk yapmamış olan bir canı öldürürse, sanki bütün insanları öldürmüş gibidir. Kim de onu(n hayatını kurtarmak suretiyle) yaşatırsa, bütün insanları yaşatmış gibi olur. Andolsun elçilerimiz onlara açık deliller getirdiler, ama bundan sonra da onlardan çoğu, yine yeryüzünde israf etmekte(aşırı gitmekte)dirler.'),
(702, 5, 33, 'Allah ve elçisiyle savaşanların ve yeryüzünde bozgunculuk yapmağa çalışanların cezası: (ya) öldürülmeleri, ya asılmaları, ya ellerinin, ayaklarının çapraz kesilmesi veya bulundukları yerden sürülmeleridir. Bu, onların dünyada çekecekleri rezilliktir. Âhirette ise onlara büyük bir azab vardır.'),
(703, 5, 34, 'Ancak sizin onları (yenip) ele geçirmenizden önce tevbe edenler olursa bilin ki Allah, bağışlayandır, esirgeyendir (tevbe edenleri affeder).'),
(704, 5, 35, 'Ey inananlar, Allah\'tan korkun, O\'na (yaklaşmağa) yol arayın ve O\'nun yolunda cihadedin ki, kurtuluşa eresiniz.'),
(705, 5, 36, 'O inkar edenler var ya, eğer yeryüzünde olanların hepsi ve onun bir katı daha kendilerinin olsa da, kıyamet gününün azabından kurtulmak için (bunları) fidye verseler, kendilerinden kabul edilmez. Onlar için acı bir azab vardır.'),
(706, 5, 37, 'Ateşten çıkmak isterler ama oradan çıkacak değillerdir. Onlar için sürekli bir azab vardır.'),
(707, 5, 38, 'Hırsızlık eden erkek ve kadının, yaptıklarına karşılık Allah\'tan bir ceza olarak ellerini kesin! Allah daima üstündür, hüküm ve hikmet sahibidir.'),
(708, 5, 39, 'Kim yaptığı haksızlıktan sonra tevbe eder, uslanırsa, şüphesiz Allah, onun tevbesini kabul eder. Çünkü Allah bağışlayan, acıyandır.'),
(709, 5, 40, 'Göklerin ve yerin mülkünün Allah\'a aidolduğunu bilmedin mi? (O), dilediğine azabeder, dilediğini bağışlar. Allah herşeye kadirdir.'),
(710, 5, 41, 'Ey Elçi, ağızlariyle "inandık" dedikleri halde kalbleri inanmamış olanlar arasında küfürde yarış edenler seni üzmesin. yahudiler arasında da yalana kulak veren, sana gelmemiş olan bir kavme kulak verenler vardır. Onlar kelimeleri yerlerinden kaydırırlar: "Eğer size bu verilirse alın, bu verilmezse sakının!" derler. Allah birini şaşırtmak isterse, sen onun için Allah\'a karşı hiçbir şey yapamazsın. Onlar, Allah\'ın, kalblerini temizlemek istemediği kimselerdir. Onlar için dünyada rezillik var ve yine onlar için ahirette de büyük bir azab vardır.'),
(711, 5, 42, 'Yalana kulak verirler, haram yerler. Sana gelirlerse, ister aralarında hüküm ver, ister onlardan yüz çevir; eğer onlardan yüz çevirirsen, sana hiçbir zarar veremezler. Ve eğer hüküm verirsen, aralarında adaletle hüküm ver. Çünkü Allah, adalet yapanları sever.'),
(712, 5, 43, 'İçinde Allah\'ın hükmü bulunan Tevrat yanlarında dururken seni nasıl hakem yapıyorlar, ondan sonra da (verdiğin hükümden) dönüyorlar. Onlar inanıcı değillerdir.'),
(713, 5, 44, 'Gerçekten Tevrat\'ı biz indirdik, onda yol gösterme ve nur vardır. İslam olmuş peygamberler, onunla yahudilere hüküm verirlerdi, kendilerini Tanrıya vermiş zahidler ve alimler de "Allah\'ın Kitabını korumakla görevlendirildiklerinden onunla (hüküm verirlerdi) ve onu gözetip kollarlardı. (Ey hİkimler), insanlardan korkmayın, benden korkun ve benim ayetlerimi az bir paraya satmayın! Kim Allah\'ın indirdiği ile hükmetmezse işte kafirler onlardır!'),
(714, 5, 45, 'O(Hak Kitabı)nda onlara, cana can, göze göz, buruna burun, kulağa kulak, dişe diş ve yaralara karşılıklı kısas (ödeşme) yazdık. Kim bunu bağışlar(kısas hakkından vazgeçer)se o, kendisi için keffaret olur. Ve kim Allah\'ın indirdiğiyle hükmetmezse, işte zalimler onlardır.'),
(715, 5, 46, 'Onların ardından, yanlarındaki Tevrat\'ı doğrulayıcı olarak Meryem oğlu Îsa\'yı gönderdik ve ona, içinde yol gösterme ve nur bulunan, önündeki Tevrat\'ı doğrulayan, korunanlar için yol gösterici ve öğüt olan İncil\'i verdik.'),
(716, 5, 47, 'İncil sahipleri, Allah\'ın onda indirdiği ile hükmetsinler. Kim Allah\'ın indirdiğiyle hükmetmezse işte onlar, yoldan çıkmışlardır.'),
(717, 5, 48, 'Sana da kendinden önceki Kitabı doğrulayıcı ve onu kollayıp koruyucu olarak Kitabı gerçekle indirdik. Artık onların aralarında Allah\'ın indirdiğiyle hükmet ve sana gelen gerçekten ayrılıp onların keyiflerine uyma! Sizden her biriniz için bir şeri\'atve bir yol belirledik. Allah isteseydi, hepinizi bir tek ümmet yapardı, fakat size verdiğ(i ni\'met)ler(i) içinde sizi sınamak istedi. Öyleyse hayır işlerine koşun, hepinizin dönüşü Allah\'adır. O size ayrılığa düştüğünüz şeyler(in hakikatin)i haber verecektir.'),
(718, 5, 49, 'Aralarında Allah\'ın indirdiğiyle hükmet, onların keyiflerine uyma ve onların, Allah\'ın indirdiği şeylerin bir kısmından seni şaşırtmalarından sakın! Eğer dönerlerse bil ki Allah, bazı günahları yüzünden onları felakete uğratmak istiyordur. Zaten insanlardan çoğu, yoldan çıkmışlardır.'),
(719, 5, 50, 'Yoksa cahiliyye hükmünü mü arıyorlar? İyi bilen bir toplum için Allah\'tan daha güzel hüküm veren kim olabilir?'),
(720, 5, 51, 'Ey inananlar, yahudileri ve hıristiyanları veliler edinmeyin! Onlar, birbirlerinin velileridir. Sizden kim onları kendine veli yaparsa, o onlardandır. Şüphesiz Allah, zalim toplumu doğru yola iletmez.'),
(721, 5, 52, 'Kalblerinde hastalık bulunanların: "Bize bir felaket gelmesinden korkuyoruz!" diyerek onların arasına koştuklarını görürsün. Belki Allah fetih ya da kendi katından bir iş getirir de onlar, içlerinde gizlediklerine pişman olurlar.'),
(722, 5, 53, '(O zaman) inananlar, "Bunlar mı o, bütün güçleriyle sizinle beraber olduklarına yemin edenler?" derler. Bütün çabaları boşa çıkmış, kaybedenlerden olmuşlardır.'),
(723, 5, 54, 'Ey inananlar, sizden kim dininden dönerse (bilsin ki) Allah, yakında öyle bir toplum getirecek ki (O) onları sever, onlar da O\'nu severler. Mü\'minlere karşı alçak gönüllü, kafirlere karşı onurlu ve şiddetlidirler. Allah yolunda cihad ederler, hiçbir kınayıcının kınamasından korkmazlar. Bu, Allah\'ın bir lutfudur, onu dilediğine verir. Allah\'(ın lutfu) geniştir, (O), bilendir.'),
(724, 5, 55, 'Sizin veliniz, ancak Allah, Elçisi ve namazlarını kılan, zekatlarını veren, rüku\'a varan mü\'minlerdir.'),
(725, 5, 56, 'Kim Allah\'ı, Elçisini ve mü\'minleri dost tutarsa (bilsin ki) galib gelecek olanlar, yalnız Allah\'ın taraftarlarıdır.'),
(726, 5, 57, 'Ey inananlar, sizden önce Kitap verilmiş olanlardan ve kafirlerden dininizi eğlence ve oyun yerine koyanları dost tutmayın; inanıyorsanız Allah\'tan korkun.'),
(727, 5, 58, 'Namaza çağırıldığınız zaman onu eğlence ve oyun yerine koydular. Düşüncesiz bir topluluk oldukları için böyle yaptılar.'),
(728, 5, 59, 'De ki: "Ey Kitap ehli, Allah\'a, bize indirilene ve bizden önce indirilene inandığımız için mi bizden hoşlanmıyorsunuz? Oysa sizin çoğunuz yoldan çıkmıştır."'),
(729, 5, 60, 'De ki: "Allah katında yeri bundan daha kötü olanı size söyleyeyim mi? Allah kim(ler)e la\'net ve gazab etmiş, kimlerden maymunlar, domuzlar ve şeytana tapanlar yapmışsa, işte onların yeri daha kötüdür ve onlar düz yoldan daha çok sapmışlardır.'),
(730, 5, 61, '(Onlar) size geldiklerinde "inandık" derler. Oysa küfürle (yanınıza) girmişler, yine onunla (yanınızdan) çıkmışlardır. Allah onların (içlerinde) gizlediklerini daha iyi bilir.'),
(731, 5, 62, 'Onlardan çoğunun günah, düşmanlık ve haram yemede birbirleriyle yarıştıklarını görürsün. Yaptıkları şey ne kötüdür!'),
(732, 5, 63, 'Rabbanilerin ve hahamların, onları günah söz söylemekten, haram yemekten menetmeleri gerekmezmiydi? Yaptıkları şey ne kötüdür!'),
(733, 5, 64, 'Yahudiler "Allah\'ın eli bağlıdır (Allah cimridir)". dediler. Kendi elleri bağlandı ve söyledikleri sözden ötürü la\'netlendiler. Hayır, Allah\'ın iki eli de açıktır, dilediği gibi verir. Andolsun, Rabbinden sana indirilen, onların çoğunun azgınlığını ve küfrünü artıracaktır. Biz onların aralarına ta kıyamet gününe kadar düşmanlık ve kin atmışızdır. Ne zaman savaş için bir ateş yakmışlarsa Allah onu söndürmüştür. (Onlar) yeryüzünde bozgunculuğa koşarlar. Allah da bozguncuları sevmez.'),
(734, 5, 65, 'Eğer Kitap ehli inanıp (Allah\'ın azabından) korunsalardı, onların kötülüklerinden geçerdik ve onları ni\'meti bol cennetlere sokardık.'),
(735, 5, 66, 'Eğer onlar Tevrat\'ı, İncil\'i ve Rablerinden kendilerine indirileni gereğince uygulasalardı, muhakkak ki üstlerinde(ki ağaçların meyvelerinde)n ve ayaklarının altın(daki ürünler)den yerlerdi. İçlerinde tutumlu (ılımlı) bir ümmet var, ama onlardan çoğu, ne kötü işler yapıyorlar?'),
(736, 5, 67, 'Ey Elçi, Rabbinden sana indirileni duyur; eğer bunu yapmazsan, O\'nun mesajını duyurmamış olursun. Allah seni insanlardan korur. Doğrusu Allah, kafirler toplumunu yola iletmez.'),
(737, 5, 68, 'De ki: Ey Kitap ehli, siz Tevrat\'ı, İncil\'i ve Rabbi\'nizden size indirileni uygulamadıkça bir esas üzerinde değilsiniz. (Ey Muhammed), Rabbinden sana indirilen, onlardan çoğunun azgınlık ve inkarını artıracaktır. Sen o kafirler toplumu için üzülme!'),
(738, 5, 69, 'İnananlar, yahudiler, sabiiler ve hıristiyanlar(dan) Allah\'a ve ahiret gününe inanan ve iyi işler yapanlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(739, 5, 70, 'Andolsun, biz İsrail oğullarından söz almış ve onlara elçiler göndermiştik. Ne zaman bir elçi onlara canlarının istemediği bir şey getirdiyse (gelen elçilerin) bir kısmını yalanladılar, bir kısmını da öldürüyorlardı.'),
(740, 5, 71, 'Bir fitne kopmayacak sandılar, kör oldular, sağır kesildiler. Sonra Allah onların tevbesini kabul etti. Sonra yine çokları kör, sağır kesildiler. Allah yaptıklarını görüyor.'),
(741, 5, 72, 'Andolsun, "Allah, ancak Meryem oğlu Mesih\'tir." diyenler elbette kafir olmuşlardır. Halbuki Mesih demişti ki: "Ey İsrail oğulları, benim Rabbim ve sizin Rabbiniz olan Allah\'a kulluk edin. Zira kim Allah\'a ortak koşarsa muhakkak ki, Allah ona cenneti haram etmiştir ve onun varacağı yer ateştir; zalimlerin yardımcıları yoktur!"'),
(742, 5, 73, 'Allah, üçün üçüncüsüdür diyenler elbette kafir olmuşlardır. Oysa yalnız bir tek tanrı vardır, başka tanrı yoktur. Bu dediklerinden vazgeçmezlerse elbette onlardan inkar edenlere acı bir azab dokunacaktır.'),
(743, 5, 74, 'Hala Allah\'a tevbe edip O\'ndan af dilemiyorlar mı? Allah bağışlayandır, esirgeyendir.'),
(744, 5, 75, 'Meryem oğlu Mesih, bir elçiden başka bir şey değildir. Ondan önce de elçiler gelip geçmiştir. Annesi de dosdoğruydu. İkisi de yemek yerlerdi. (Yaşamak için yemeğe muhtaç olan nasıl tanrı olabilir?) Bak, onlara nasıl ayetleri açıklıyoruz, sonra bak nasıl (haktan) çevriliyorlar!?'),
(745, 5, 76, 'De ki: "Allah\'ı bırakıp size ne zarar, ne de yarar vermeğe gücü yetmeyen şeylere mi tapıyorsunuz? Oysa Allah, işiten, bilendir (O\'na tapmanız gerekmez mi?)."'),
(746, 5, 77, 'De ki: "Ey Kitap ehli, dininizde haksız yere aşırılığa dalmayın ve önceden sapmış, birçoğunu da saptırmış, düz yoldan şaşmış bir milletin keyiflerine uymayın!"'),
(747, 5, 78, 'İsrail oğullarının nankörlerine, Davud ve Meryem oğlu Îsa diliyle la\'net edilmiştir. Çünkü (onlar) isyan etmişlerdi ve saldırıyorlardı.'),
(748, 5, 79, 'Yaptıkları kötülükten vazgeçmiyorlardı. Ne kötü işler yapıyorlardı!'),
(749, 5, 80, 'Onlardan çoğunun, inkar edenlerle dostluk ettiklerini görürsün. Gerçekten nefislerinin, kendileri için yapıp gönderdiği ne kötüdür (ki o yüzden) Allah onlara gazabetmiştir ve azabda sürekli kalacaklardır.'),
(750, 5, 81, 'Eğer Allah\'a, Peygambere ve ona indirilene inansalardı, o(inkar ede)nleri veli yapmazlardı. Ama onlardan çoğu yoldan çıkmış insanlardır.'),
(751, 5, 82, 'İnsanlar içerisinde, inananlara en yaman düşman olarak yahudileri ve (Allah\'a) ortak koşanları bulursun. İnananlara sevgice en yakınları da "Biz hıristiyanlarız." diyenleri bulursun. Çünkü onların içlerinde keşişler ve rahipler vardır ve onlar büyüklük taslamazlar.'),
(752, 5, 83, 'Elçi\'ye indirilen(Kur\'an)ı dinledikleri zaman, tanıdıkları gerçekten dolayı gözlerinin yaşla dolup taştığını görürsün. Derler ki: "Rabbimiz, inandık, bizi şahidlerle beraber yaz!"'),
(753, 5, 84, 'Biz, Rabbimizin bizi iyiler arasına katmasını umarken neden Allah\'a ve bize gelen gerçeğe inanmayalım?'),
(754, 5, 85, 'Bu sözlerinden dolayı Allah onlara, altlarından ırmaklar akan, içinde ebedi kalacakları cennetler verdi. Güzel davrananların mükafatı işte budur!'),
(755, 5, 86, 'İnkar edip ayetlerimizi yalanlayanlar da cehennem halkıdır.'),
(756, 5, 87, 'Ey inananlar, Allah\'ın size helal kıldığı güzel ve temiz şeyleri haram etmeyin, sınırı aşmayın. Çünkü Allah, sınırı aşanları sevmez.'),
(757, 5, 88, 'Allah\'ın size verdiği rızıklardan helal ve temiz olarak yeyin ve inandığınız Allah\'tan korkun!'),
(758, 5, 89, 'Allah sizi, yeminlerinizdeki lağv(kasıtsız olarak yaptığınız yeminler)den ötürü sorumlu tutmaz. Fakat bilerek yaptığınız yeminlerden ötürü sizi sorumlu tutar. Bu(geleceğe bağlı yemini bozma)nın keffareti: ailenize yedirdiğinizin orta derecesinden on fakiri yedir(ip doyur)mak, yahut onları giydirmek, ya da bir boyun(köley)i hürriyete kavuşturmaktır. Bunu bulamayan kimse, üç gün oruç tutsun. İşte yemin ettiğiniz zaman, yeminleriniz(i bozman)ın keffareti budur. yeminlerinizi koruyun. Allah, ayetlerini size böyle açıklıyor ki, şükredesiniz.'),
(759, 5, 90, 'Ey inananlar, şarap, kumar, dikili taşlar, şans okları şeytan işi birer pisliktir. Bunlardan kaçının ki kurtuluşa eresiniz.'),
(760, 5, 91, 'Şeytan, şarap ve kumar ile aranıza düşmanlık ve kin sokmak, sizi Allah\'ı anmaktan ve namazdan alakoymak istiyor. Artık (bunlardan) vazgeçecek misiniz?'),
(761, 5, 92, 'Allah\'a ita\'at edin, Elçi\'ye ita\'at edin, (kötü şeylerden) sakının! Eğer (kabul etmez) dönerseniz, bilin ki elçimize düşen, açıkça duyurmaktır.'),
(762, 5, 93, 'İnanıp iyi işler yapanlara -bundan böyle (kötülüklerden) korunup inandıkları ve iyi işler yaptıkları, sonra (yasaklardan) korunup (onların yasaklığına) inandıkları ve yine korunup iyilik ettikleri takdirde (daha önce) yediklerinden ötürü bir günah yoktur. Allah güzel davrananları sever.'),
(763, 5, 94, 'Ey inananlar, Allah sizi, ellerinizin ve mızraklarınızın erişeceği bir av\'la dener ki, gizlide kendisiden kimin korktuğunu bilsin (görmeden Alah\'a inanıp O\'ndan korkanlar ortaya çıksın, bilinsin). Kim bundan sonra saldırıda bulunursa onun için acı bir azab vardır.'),
(764, 5, 95, 'Ey inananlar, ihramda iken av öldürmeyin. Sizden kim kasden onu öldürürse, öldürdüğünün dengi olan bir hayvan cezası vardır ki (bu, öldürülene denk olduğuna) içinizden iki adil kişinin karar vereceği, Ka\'be\'ye varacak bir kurban; yahut yoksullara yedirme şeklinde keffaret; ya da buna denk oruçtur. Ta ki böylece (o insan), yaptığı işin vebalini tadsın. Allah, geçmişi affetmiştir. Kim düşmanlık ederse Allah ondan öc alır. Allah, daima galiptir, öc alandır.'),
(765, 5, 96, 'Hem kendinize, hem de yolculara bir geçimlik olmak üzere deniz avı ve yiyeceği, size helal kılındı. İhramda olduğunuz sürece size kara avı yasaklandı. Huzuruna toplanacağınız Allah\'tan korkun!'),
(766, 5, 97, 'Allah Ka\'be\'yi, o saygıdeğer evi, insanlar için (hayat ve güven) durağı yaptı. O saygıdeğer ayı, kurbanı, tasmalı kurbanlıkları da (böyle yaptı) ki Allah\'ın göklerde ve yerde olanları bildiğini ve Allah\'ın herşeyi bilici olduğunu anlayasınız.'),
(767, 5, 98, 'İyi bilin ki Allah\'ın cezası çetindir ve Allah bağışlayandır, esirgeyendir.'),
(768, 5, 99, 'Elçi\'ye düşen, sadece duyurmaktır. Allah, gizlediğiniz ve açığa vurduğunuz şeyleri bilir.'),
(769, 5, 100, 'De ki:"Murdarla temiz bir olmaz. Murdarın çokluğu hoşuna gitse de. O halde ey sağduyu sahipleri, Allah\'tan korkun ki kurtuluşa eresiniz!"'),
(770, 5, 101, 'Ey inananlar, açıklandığı zaman hoşunuza gitmeyecek şeyleri sormayın. Eğer Kur\'an indirilirken onları sorarsanız, size açıklanır. Halbuki Allah onlardan geçmiştir. Allah bağışlayandır, halimdir.'),
(771, 5, 102, 'Sizden önce gelen bir toplum da onları sormuştu da sonra onları tanımaz olmuşlardı.'),
(772, 5, 103, 'Allah, bahire, saibe, vasile ve ham diye bir şey yapmamıştır. Fakat inkar edenler, Allah\'a yalan uyduruyorlar ve çokları da akıl erdiremiyorlar.'),
(773, 5, 104, 'Onlara: "Allah\'ın indirdiğine ve Elçi\'ye gelin!" dense, "Babalarımızı üzerinde bulduğumuz şey bize yeter!" derler. Babaları hiçbir şey bilmeyen, doğru yolu bulamayan kimseler olsa da mı?'),
(774, 5, 105, 'Ey inananlar, siz kendinize bakın, siz doğru yolda olduğunuz takdirde sapan kimse size zarar vermez. Hepinizin dönüşü Allah\'adır. O size ne yaptığınızı haber verecektir.'),
(775, 5, 106, 'Ey inananlar, birinize ölüm gelince vasiyyet sırasında içinizden iki adil kişi, aranızda şahidlik etsin. Ya da yeryüzünde yolculuk ederken başınıza ölüm musibeti gelmişse, sizden olmayan iki kişi (şahidlik etsin). Kuşkulanırsanız, namazdan sonra onları tutar(yemin ettirir)siniz: "Akraba da olsa yeminimizi hiçbir paraya satmayacağız, Allah\'ın (üzerimizde bir borç olarak bulunan) şahidliğini gizlemeyeceğiz, yoksa biz, elbette günahkarlardan oluruz,"diye Allah\'a yemin ederler.'),
(776, 5, 107, 'Eğer onların bir günah işledikleri (yalan söyleyip hakkı gizledikleri) anlaşılırsa; (o iki şahidin), haklarına tecavüz etmek istediği kimselerden (mirasçılardan, ölüye yakınlıklarından ve durumu daha iyi bildiklerinden dolayı şahidliğe) daha layık olan iki kişi, onların yerine geçer. Allah\'a (şöyle) yemin ederler: "Mutlaka bizim şahidliğimiz, onların şahidliğinden daha doğrudur, biz (hakka) tecavüz etmedik, yoksa biz elbette zalimlerden oluruz" (derler).'),
(777, 5, 108, 'Şahidliği gereği gibi yapmalarına, yahut yemin(etme)lerinden sonra yeminlerin(in) reddedilmesinden korkmalarına en uygun olan budur. Allah\'tan korkun ve iyi dinleyin: Allah, yoldan çıkan topluluğu doğru yola iletmez.'),
(778, 5, 109, 'Allah, Elçileri toplayacağı gün: "Size ne cevap verildi?" der. "Bizim bilgimiz yok, derler, gizlileri bilen yalnız sensin, sen!"'),
(779, 5, 110, 'Allah demişti ki: "Ey Meryem oğlu Îsa, sana ve annene olan ni\'metimi hatırla, hani seni Ruhu\'l-Kudüs ile desteklemiştim; beşikte ve yetişkin iken insanlarla konuşuyordun; sana Kitabı, hikmeti, Tevrat\'ı ve İncil\'i öğrettim. Benim iznimle çamurdan kuş şeklinde bir şey yaratıyor, içine üflüyordun, benim iznimle kuş oluyordu; anadan doğma körü ve alacalıyı benim iznimle iyileştiriyordun; benim iznimle ölüleri (diriltip kabirlerden) çıkarıyordun ve İsrail oğullarını da senden savmıştım; hani sen onlara açık deliller getirdiğin zaman, içlerinden inkar edenler: "Bu açık bir büyüden başka bir şey değil!" demişti.'),
(780, 5, 111, 'Havarilere: "Bana ve elçime inanın!" diye vahyetmiştim (kalblerine bu düşünceyi atmıştım); "İnandık, bizim müslümanlar olduğumuza şahidol!" demişlerdi.'),
(781, 5, 112, 'Havariler demişlerdi ki: "Ey Meryem oğlu Îsa, Rabbin bize gökten bir sofra indirebilir mi? (Îsa): "İnanıyorsanız Allah\'tan korkun!" dedi.'),
(782, 5, 113, 'İstiyoruz ki, ondan yiyelim, kalblerimiz iyice yatışsın, senin bize doğru söylediğini bilelim ve buna bizzat tanık olalım. dediler.'),
(783, 5, 114, 'Meryem oğlu Îsa da: "Allah\'ım, Rabbimiz, bizim üzerimize gökten bir sofra indir ki bizim için, önce ve sonra gelenlerimiz için (o gün) bir bayram olsun ve (o olay) Senden de bir mu\'cize olsun. Bizi rızıklandır, Sen rızık verenlerin en hayırlısısın!"dedi.'),
(784, 5, 115, 'Allah buyurdu ki: "Ben onu sizin üzerinize indireceğim, ama ondan sonra sizden kim inkar ederse ben ona dünyalarda hiç kimseye yapmayacağım azabı yaparım!"'),
(785, 5, 116, 'Ve yine Allah demişti ki: "Ey Meryem oğlu Îsa sen mi insanlara \'Beni ve annemi, Allah\'tan başka iki tanrı edinin\' dedin?". Haşa, dedi, Sen yücesin, benim için gerçek olmayan bir şeyi söylemek benim haddime değildir! Eğer demiş olsaydım, sen bunu bilirdin, sen benim nefsimde olanı bilirsin, ben senin nefsinde olanı bilmem, çünkü gizlileri bilen yalnız sensin, sen!"'),
(786, 5, 117, 'Ben onlara: Benim ve sizin Rabbiniz olan Allah\'a kulluk edin, diye senin bana emretmiş olduğundan başka bir şey söylemedim. Ben onların içinde olduğum sürece onları kolladım, fakat sen beni vefat ettirince onları gözetleyen (yalnız) Sen oldun. Sen herşeyi görensin.'),
(787, 5, 118, 'Eğer onlara azabedersen, onlar senin kullarındır (dilediğini yaparsın); eğer onları bağışlarsan, şüphesiz sen daima üstünsün, hüküm ve hikmet sahibisin!'),
(788, 5, 119, 'Allah buyurdu: "Bu, sadıklara, doğruluklarının fayda sağlayacağı gündür. Onlar için altlarından ırmaklar akan, içinde ebedi kalacakları cennetler vardır." Allah onlardan razı olmuştur, onlar da O\'ndan razı olmuşlardır. İşte büyük başarı budur!'),
(789, 5, 120, 'Göklerin, yerin ve bunlarda bulunan herşeyin mülkü Allah\'ındır. O, herşeye kadirdir!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Hamdolsun o Allah\'a ki, gökleri ve yeri yarattı, karanlıkları ve aydınlığı var etti. Yine de inkarcılar, Rablerine eşler tutuyorlar.'),
(791, 6, 2, 'O, sizi çamurdan yaratıp, sonra (da hayatınıza) bir süre koymuştur. (Kafirlerin cezalandırılması için) Belirli bir süre de kendi katındadır. Böyle iken, siz hala kuşkulanıyorsunuz.'),
(792, 6, 3, 'O, göklerde de, yerde de (tek) Allah\'tır. Sizin gizlinizi, açığınızı ve ne kazandığınızı bilir.'),
(793, 6, 4, 'Onlara Rablerinin ayetlerinden hiçbir ayet gelmez ki, ondan yüz çevirmesinler.'),
(794, 6, 5, 'İşte, kendilerine gelen hakkı da yalanladılar. Fakat alay ettikleri şeyin haberleri, yakında kendilerine gelecek(uyarıldıkları azab onları kuşatacak)tır.'),
(795, 6, 6, 'Görmediler mi, onlardan önce nice nesiller yok ettik; hem onlara, yeryüzünde size vermediğimiz imkanları vermiştik ve göğü de üzerlerine bol bol boşaltmıştık ve ırmakları ayaklarının altından akar kılmıştık. Fakat günahlarından ötürü onları helak ettik ve onların ardından başka bir nesil yarattık.'),
(796, 6, 7, 'Eğer sana kağıt üzerine yazılı bir Kitap indirmiş olsaydık da onu elleriyle tutsalardı, yine inkar edenler, "Bu, apaçık bir büyüden başka bir şey değildir!" derlerdi.'),
(797, 6, 8, 'O\'na bir melek indirilmeli değil miydi? dediler. Eğer bir melek indirseydik, iş bitirilmiş olurdu, artık kendilerine hiç göz açtırılmazdı.'),
(798, 6, 9, 'Eğer O(Hak Elçisi)ni melek yapsaydık, yine bir adam (şeklinde) yapardık ve onları yine düştükleri kuşkuya düşürürdük.'),
(799, 6, 10, 'Senden önce de peygamberlerle alay edilmişti. Fakat onlarla alay edenleri, alay ettikleri gerçek kuşatıverdi.'),
(800, 6, 11, 'De ki: "Yeryüzünde dolaşın da yalanlayanların sonu nasıl olmuş, görün!"'),
(801, 6, 12, 'De ki: "Göklerde ve yerde olanlar kimindir?" "Allah\'ındır" de. O, rahmet etmeyi kendi üstüne yazmış(acımayı kendisine prensip edinmiş)tir. Sizi elbette varlığında şüphe olmayan kıyamet gününde toplayacaktır. Ama kendilerini ziyana sokanlar, inanmazlar.'),
(802, 6, 13, 'Gece ve gündüzde barınan her şey O\'nundur. O, işitendir, bilendir.'),
(803, 6, 14, 'De ki: "Gökleri ve yeri yoktan var eden, besleyen, fakat kendisi beslenmeyen Allah\'tan başka dost mu tutayım?" "Bana, İslam olanların ilki olmam emrerdildi" de ve sakın ortak koşanlardan olma!'),
(804, 6, 15, 'De ki: "Eğer Rabbime isyan edersem, büyük bir günün azabından korkarım!"'),
(805, 6, 16, '(O öyle bir gündür ki) o gün kimden azab çevrilip savılırsa gerçekten (Allah) ona acımıştır. İşte apaçık başarı budur.'),
(806, 6, 17, 'Allah sana bir zarar dokundursa, onu yine kendisinden başka açacak yoktur ve eğer sana bir hayır dokundursa, kuşkusuz O, herşeyi yapabilendir.'),
(807, 6, 18, 'O, kullarının üstünde tam hakimdir (onları istediği gibi yönetir), O herşeyi yerli yerince yapan, (herşeyi) haber alandır.'),
(808, 6, 19, 'De ki: "Şahidlik bakımından hangi şey daha büyüktür?" De ki: "Benimle sizin aranızda Allah şahiddir. Bu Kur\'an bana vahyolundu ki, onunla sizi ve ulaştığı herkesi uyarayım. Siz gerçekten Allah ile beraber başka tanrılar olduğuna şahidlik ediyor musunuz?", "Ben şahidlik etmem!" de: "O, ancak tek bir Tanrıdır, ben sizin ortak koştuğunuz şeylerden uzağım" de.'),
(809, 6, 20, 'Kendilerine Kitap verdiklerimiz, oğullarını tanıdıkları gibi onu tanırlar (onun Allah tarafından vahyedildiğini bilirler), ama kendilerini ziyana sokanlar inanmazlar.'),
(810, 6, 21, 'Allah\'a yalan uyduran, ya da O\'nun ayetlerini yalanlayandan daha zalim kim olabilir? zalimler de kurtuluş yüzü görmezler.'),
(811, 6, 22, 'Hepsini topladığımız, sonra ortak koşanlara; "Hani (Allah\'a) ortak sandığınız şeyler nerede?" dediğimiz gün;'),
(812, 6, 23, 'Sonra onların: "Rabbimiz Allah\'a andolsun ki biz ortak koşanlar değildik." demelerinden başka çareleri kalmadığı (gün);'),
(813, 6, 24, 'Bak ki, nasıl kendilerine karşı yalan söylediler ve uydurdukları şeyler kendilerinden sapıp gitti.'),
(814, 6, 25, 'İçlerinden seni dinleyenler vardır; fakat biz onu anlamalarına engel olmak için kalblerinin üstüne kılıflar, kulaklarının içine de ağırlık koyduk. (Onlar) her mu\'cizeyi görseler de yine ona inanmazlar. Hatta sana geldiklerinde seninle tartışırlar; o kafirler: "Bu, eskilerin masallarından başka bir şey değildir" derler.'),
(815, 6, 26, 'Onlar hem (insanları) ondan menederler, hem de kendileri ondan uzak dururlar. Böylece yalnız kendilerini mahvediyorlar ama farkında değiller!'),
(816, 6, 27, 'Onların, ateşin başında durdurulmuş iken: "Ah ne olurdu keşke biz (dünyaya) geri döndürülseydik de Rabbimizin ayetlerini yalanlamasaydık, inananlardan olsaydık!" dediklerini bir görsen!'),
(817, 6, 28, 'Hayır, daha önce gizlemekte oldukları, onlara göründü. Geri gönderilselerdi yine men\'olundukları şeyi yapmağa dönerlerdi, çünkü onlar yalancılardır.'),
(818, 6, 29, 'Dediler ki: "dünya hayatımızdan başka bir hayat yoktur. Biz diriltilecek değiliz."'),
(819, 6, 30, 'Onları Rablerinin huzurunda durdurulmuş iken bir görsen: (Allah) "Bu gerçek değil miymiş?" dedi. Dediler ki, "Evet Rabbimiz hakkı için gerçektir!" "Öyle ise inkar ettiğinizden dolayı azabı tadın!" dedi.'),
(820, 6, 31, 'Allah\'ın huzuruna çıkmayı yalanlayanlar, gerçekten ziyana uğradı(lar). Nihayet kendilerine ansızın o sa\'at gelip çatınca, günahlarını sırtlarına yüklenmiş olarak: "Hayatta (iyi işler yapmaktan) geri kalıp günah işlememizden ötürü vah bize!" dediler. Bakın, ne kötü şeyler yüklenip taşıyorlar!'),
(821, 6, 32, 'Dünya hayatı sadece bir oyun ve eğlenceden başka bir şey değildir. Korunanlar için elbette ahiret yurdu daha iyidir. Düşünmüyor musunuz?'),
(822, 6, 33, 'Biliyoruz, onların dedikleri seni üzüyor, gerçekte onlar seni yalanlamıyorlar, fakat o zalimler bile bile Allah\'ın ayetlerini inkar ediyorlar.'),
(823, 6, 34, 'Senden önce de elçiler yalanlanmıştı. Yalanlanmalarına ve eziyet edilmelerine sabrettiler, nihayet onlara yardımımız yetişti. Allah\'ın kelimelerini değiştirebilecek kimse yoktur. Sana da elçilerin haberinden bir parça gelmiştir.'),
(824, 6, 35, 'Eğer onların yüz çevirmesi sana ağır geldiyse, haydi (yapabilirsen) yerin içine (inebileceğin) bir delik, ya da göğe (çıkaileceğin) bir merdiven ara ki onlara bir mu\'cize getiresin! Allah, dileseydi, elbette onları hidayet üzerinde toplardı, o halde cahillerden olma!'),
(825, 6, 36, 'Ancak işitenler (çağrıya) gelir, ölülere gelince Allah onları diriltir, sonra O\'na döndürülürler.'),
(826, 6, 37, 'Dediler ki: "Ona Rabbinden bir mu\'cize indirilmeli değil miydi?" De ki: "Şüphesiz Allah, bir mu\'cize indirmeğe kadirdir, fakat çokları bilmezler."'),
(827, 6, 38, 'Yeryüzünde yürüyen hiçbir hayvan ve iki kanadiyle uçan hiçbir kuş yoktur ki, (onlar da) sizin gibi birer ümmet olmasınlar. Biz Kitapta hiçbir şeyi eksik bırakmamışızdır. Sonra (onlar), Rableri(nin huzuru)na toplanacaklardır.'),
(828, 6, 39, 'Bizim ayetlerimizi yalanlayanlar, karanlıklar içinde kalmış sağır ve dilsizlerdir. Allah dilediği kimseyi şaşırtır, dilediği kimseyi de doğru yola koyar.'),
(829, 6, 40, 'De ki: "Düşündünüz mü kendinizi hiç? Size Allah\'ın azabı gelse, ya da o (Duruşma) sa\'at(i) gelse, Allah\'tan başkasına mı yalvarırsınız? Doğru (sözlü) iseniz (söyleyin).'),
(830, 6, 41, 'Hayır, yalnız O\'na yalvarırsınız; O da dilerse (kaldırmasını) istediğiniz belayı kaldırır ve o zaman ortak koştuğunuz şeyleri unutursunuz.'),
(831, 6, 42, 'Senden önce de ümmetlere elçiler gönderdik. (İnkarlarından dönüp bize) yalvarsınlar diye onları darlık ve sıkıntı ile yakalayıp cezalandırmıştık.'),
(832, 6, 43, 'Hiç olmazsa kendilerine böyle baskınımız geldiği zaman yalvarsalardı! Fakat kalbleri katılaştı ve şeytan da onlara yaptıklarını süslü gösterdi.'),
(833, 6, 44, 'Kendileri yapılan uyarıları unutunca, üzerlerine her şeyin kapılarını açıverdik; kendilerine verilenle sevince daldıkları sırada da ansızın onları yakaladık, birden bire bütün umutlarnı yitirdiler.'),
(834, 6, 45, 'Böylece haksızlık eden milletin ardı kesildi. Alemlerin Rabbi Allah\'a hamdolsun!'),
(835, 6, 46, 'De ki: "Söyleyin bana, eğer Allah işitme(duyu)nuzu ve gözlerinizi alsa, kalblerinizin üstüne de mühür vursa, Allah\'tan başka bun(lar)ı size getir(ip ver)ecek tanrı kimdir?" Bak, nasıl ayetleri döndürüp türlü türlü açıklıyoruz, sonra yine onlar yüz çeviriyorlar?'),
(836, 6, 47, 'De ki: "Düşündünüz mü kendinizi hiç? Size Allah\'ın azabı ansızın, ya da açıkça gelse, zalim toplumdan başkası mı helak edilir?"'),
(837, 6, 48, 'Biz elçileri sadece müjdeciler ve uyarıcılar olarak göndeririz. Kim inanır ve uslanırsa onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(838, 6, 49, 'Ayetlerimizi yalanlayanlara da yaptıkları fenalık yüzünden azab dokunacaktır.'),
(839, 6, 50, 'De ki: "Ben size, Allah\'ın hazineleri yanımdadır, demiyorum. Gaybı da bilmem. Size \'Ben meleğim\' de demiyorum. Ben sadece bana vahyolunana uyuyorum." De ki: "Körle, gören bir olur mu? Düşünmüyor musunuz?"'),
(840, 6, 51, 'Rablerin(in huzuru)na toplanacakların(a inanıp bu durum)dan korkanları onunla uyar ki; kendilerinin, O\'ndan başka ne dostları, ne de destekçileri yoktur. (Onları uyar), belki korunurlar.'),
(841, 6, 52, 'Sabah akşam Rablerinin rızasını isteyerek, O\'na yalvaranları kovma. Onların hesabından sana bir sorumluluk, senin hesabından da onlara bir sorumluk yok ki, onları kovup da zalimlerden olasın!'),
(842, 6, 53, 'Böylece biz onların kimini kimi ile denedik ki: "Allah, aramızdan şunlara mı lutfu layık gördü?" desinler. Allah, şükredenleri daha iyi bilmez mi?'),
(843, 6, 54, 'Ayetlerimize inananlar, sana geldikleri zaman: "Size selam olsun, de, Rabbiniz, kendi üzerine rahmeti yazmış(yaratıklarına acımayı prensip edinmiş)tir. Sizden kim, bilmeyerek bir kötülük yapar da sonra ardından tevbe eder, uslanırsa muhakkak ki O, bağışlayandır, esirgeyendir."'),
(844, 6, 55, 'Böylece ayetleri (döne, döne) açıklıyoruz ki, suçluların yolu belli olsun.'),
(845, 6, 56, 'De ki: "Ben, Allah\'tan başka yalvardıklarınıza tapmaktan men\'olundum." De ki: "Ben sizin keyiflerinize uymam, çünkü o takdirde sapıtmış ve yola gelenlerden olmamış olurum."'),
(846, 6, 57, 'De ki: "Ben, Rabbimden (gelen) açık bir delil üzerindeyim. Siz ise onu yalanladınız. Acele istediğiniz (azab) da benim yanımda değildir. Hüküm vermek, yalnız Allah\'a aittir. (O) gerçeği anlatır ve O, (davayı çözüp) ayırdedenlerin en iyisidir."'),
(847, 6, 58, 'De ki: "Eğer acele istediğiniz şey benim yanımda olsaydı, elbette benimle sizin aranızda iş, şimdi (çoktan) bitirilmişti." Allah zalimleri daha iyi bilir.'),
(848, 6, 59, 'Gayb\'ın (görünmez bilginin) anahtarları, O\'nun yanındadır, onları O\'ndan başkası bilmez. (O) karada ve denizde olan herşeyi bilir. Düşen bir yaprak, ki mutlaka onu bilir, yerin karanlıkları içinde gömülen dane, yaş ve kuru hiçbir şey yoktur ki, apaçık bir Kitapta olmasın.'),
(849, 6, 60, 'O\'dur ki, geceleyin sizi öldürür (gibi uyutur), gündüzün ne işlediğinizi bilir; sonra belirlenmiş süre geçirilip tamamlansın diye gündüzün sizi diriltir. Sonra dönüşünüz O\'nadır; sonra (O, dünyada) yaptıklarınızı size haber verecektir.'),
(850, 6, 61, 'O, kulların üstünde tek hakimdir. Size koruyucu(melek)ler gönderir, nihayet birinize ölüm gelince elçilerimiz onun canını alırlar, onlar (bu hususta) hiç geri kalmazlar.'),
(851, 6, 62, 'Sonra o(ca)nlar, gerçek Tanrıları olan Allah\'a döndü(rülüp götü)rülürler. Doğrusu hüküm, yalnız O\'nundur; O hesap görenlerin en çabuğudur.'),
(852, 6, 63, 'De ki: "Gizli ve açık olarak: \'Bizi bundan kurtarırsa elbette şükredenlerden olacağız!\' diye O\'na yalvarıp yakardığınız zaman, karanın ve denizin karanlıklarından sizi kim kurtarıyor?"'),
(853, 6, 64, 'De ki: "Ondan ve bütün sıkıntılardan sizi Allah kurtarıyor, sonra siz yine O\'na ortak koşuyorsunuz!?"'),
(854, 6, 65, 'De ki: "O, sizin üzerinize üstünüzden, yahut ayaklarınızın altından bir azab göndermeğe, ya da sizi parti parti birbirinize düşürüp kiminize kiminizin hıncını taddırmağa kadirdir." Bak, anlasınlar diye ayetleri nasıl açıklıyoruz?!'),
(855, 6, 66, 'O, gerçek iken kavmin onu yalanladı. De ki: "Ben size vekil değilim!"'),
(856, 6, 67, 'Her haberin gerçekleşeceği bir zaman vardır. Yakında bilirsiniz.'),
(857, 6, 68, 'Ayetlerimiz hakkında (münasebetsizliğe) dalanları gördüğün zaman, onlar başka bir söze geçinceye kadar onlardan yüz çevir; eğer şeytan sana (bunu) unutturursa hatırladıktan sonra (hemen kalk), o zalimler topluluğuyla beraber oturma!'),
(858, 6, 69, 'Korunanlara, o(inanmaya)nların hesabından bir sorumluluk yoktur, ama belki (inanıp) korunurlar diye bir hatırlatmak lazımdır.'),
(859, 6, 70, 'Bırak o dinlerini oyun, eğlence yerine koyan ve dünya hayatının aldattığı kimseleri de, sen o (Kur\'an) ile (şunu) hatırlat ki, bir kişi, yaptığı işin eline teslim edilmeye görsün, (yoksa) Allah\'tan başka onun ne bir dostu, ne de bir yardımcısı olmaz. (Amelinin elinden kurtulmak için) her türlü fidyeyi verse de ondan kabul edilmez. İşte onlar, kazandıklarının eline teslim edilmişlerdir. Onlar için kaynar sudan bir içki ve inkarlarından dolayı da acı bir azab vardır!'),
(860, 6, 71, 'De ki: "Allah\'tan başka, bize ne yarar, ne zarar vermeyen şeylere mi yalvaralım? Ve Allah bizi doğru yola ilettikten sonra, ökçelerimiz üzerinde (eski durumumuza) döndürülüp; şeytanların ayartarak şaşkın bir halde çölde bıraktıkları; arkadaşlarının ise "Bize gel!" diye doğru yola çağırdıkları kimse gibi (şaşkın bir duruma) mı düşelim?" De ki: "Yol gösterme, ancak Allah\'ın yol göstermesidir. Bize, alemlerin Rabbine teslim olmamız emredilmiştir."'),
(861, 6, 72, 'Namazı kılın ve O\'ndan korkun (diye emredilmiştir)! Varıp huzuruna toplanacağınız O\'dur.'),
(862, 6, 73, 'Gökleri ve yeri hak (ve hikmet) ile yaratan O\'dur. "Ol!" dediği gün, oluverir. Sözü haktır. Sur\'a üfleneceği gün de, mülk O\'nundur. Gizliyi ve açığı bilendir. O, hükümdardır, herşeyi haber alandır.'),
(863, 6, 74, 'İbrahim, babası Azer\'e demişti ki: "Sen putları tanrılar mı ediniyorsun? Doğrusu ben seni ve kavmini açık bir sapıklık içinde görüyorum."'),
(864, 6, 75, 'Böylece biz İbrahim\'e göklerin ve yerin melekutunu (büyük ve harikulade muhteşem varlıklarını) gösteriyorduk ki, kesin inananlardan olsun.'),
(865, 6, 76, 'Üzerine gece basınca (İbrahim) bir yıldız gördü; "Budur Rabbim" dedi. Yıldız batınca: "Batanları sevmem", dedi.'),
(866, 6, 77, 'Ay\'ı doğarken görünce: "Budur Rabbim" dedi. O da batınca: "Rabbim bana doğru yolu göstermeseydi, elbette sapan topluluktan olurdum." dedi.'),
(867, 6, 78, 'Güneşi doğarken görünce: "Budur Rabbim, bu daha büyük!" dedi. (O da) batınca dedi ki: "Ey kavmim, ben sizin (Allah\'a) ortak koştuğunuz şeylerden uzağım."'),
(868, 6, 79, 'Ben yüzümü tamamen, gökleri ve yeri yoktan var edene çevirdim ve artık ben O(na) ortak koşanlardan değilim!'),
(869, 6, 80, 'Kavmi onunla tartışmaya girişti. (O onlara) dedi ki: "Beni doğru yola iletmiş iken Allah hakkında benimle tartışıyor musunuz? Ben, sizin O\'na ortak koştuğunuz şeylerden korkmam. Ancak Rabbimin dilediği olur! Rabbim, bilgice herşeyi kuşatmıştr. Hala öğüt almıyor musunuz?'),
(870, 6, 81, 'Hem siz, Allah\'ın size, (tanrı oldukları) hakkında hiçbir delil indirmediği şeyleri, O\'na ortak koşmaktan korkmuyorsunuz da ben nasıl sizin (O\'na) ortak kuştuğunuz şeylerden korkarım? Şimdi biliyorsanız (söyleyin), iki topluluktan hangisi (tek Allah\'a inananlar mı, yoksa Allah\'a ortak koşanlar mı) güvende olmağa daha layıktır?'),
(871, 6, 82, 'İnananlar ve imanlarını bir haksızlıkla bulamayanlar... İşte güven onlarındır ve doğru yolu bulanlar da onlardır.'),
(872, 6, 83, 'İşte bunlar, kavmine karşı İbrahim\'e verdiğimiz hüccet(kanıt)lerimizdir. Dilediğimizi derecelerle yükseltiriz. Şüphesiz Rabbin hüküm ve hikmet sahibidir, bilendir.'),
(873, 6, 84, 'Biz ona İshak\'ı ve (İshak\'ın oğlu) Ya\'kub\'u da hediye ettik; hepsine de doğru yolu gösterdik. Nitekim daha önce Nuh\'a ve onun soyundan Davud\'a, Süleyman\'a, Eyyub\'a, Yusuf\'a, Musa\'ya ve Harun\'a da yol göstermiştik. Biz güzel davrananları böyle ödüllendiririz.'),
(874, 6, 85, 'Zekeriyya\'ya, Yahya\'ya, Îsa ve İlyas\'a da (yol göstermiştik). Hepsi iyilerden idi.'),
(875, 6, 86, 'İsma\'il\'e, el-Yesa\'a, Yunus\'a ve Lut\'a da (yol gösterdik), hepsini alemlere üstün kıldık.'),
(876, 6, 87, 'Babalarından, çocuklarından ve kardeşlerinden bazılarını da... Onları seçtik ve onları doğru yola ilettik.'),
(877, 6, 88, 'İşte bu, Allah\'ın hidayetidir, kullarından dilediğini bununla doğru yola iletir. Eğer (onlar Allah\'a) ortak koşsalardı, yaptıkları (güzel) şeyler hiç olur, giderdi.'),
(878, 6, 89, 'İşte onlar, kendilerine Kitap, hüküm ve peygamberlik verdiğimiz kimselerdir. Şimdi şunlar, (yani Kureyş kavmi), bunları inkar ederse, (bilsinler ki) biz, bunları inkar etmeyecek (koruyacak) bir toplumu, bunlara vekil bırakmışızdır.'),
(879, 6, 90, 'İşte onlar, Allah\'ın hidayet ettiği kimselerdir. Onların yoluna uy ve de ki: "Ben ona karşılık sizden bir ücret istemiyorum. O, sadece alemlere bir öğüttür."'),
(880, 6, 91, 'Allah\'ı şanına yaraşır biçimde tanıyamadılar, zira "Allah, insana bir şey indirmedi" dediler. De ki: "Öyleyse Musa\'nın, insanlara nur ve yol gösterici olarak getirdiği, ki siz onu parça parça kağıtlar haline getirip gösteriyorsunuz, çoğunu da gizliyorsunuz- ve ne sizin, ne de babalarınızın bilmediği şeylerin size öğretildiği Kitabı kim indirdi?" "Alah" de, sonra bırak onları, daldıkları bataklıkta oynayadursunlar.'),
(881, 6, 92, 'Bu da Anakent(Mekke\'y)i ve çevresindeki(kasaba)ları uyarman için sana indirdiğimiz feyz kaynağı ve kendinden önceki (Tanrı Kitabı)nı doğrulayıcı bir Kitaptır. Ahirete inananlar, buna inanırlar ve onlar, namazlarına devam ederler.'),
(882, 6, 93, 'Allah\'a karşı yalan uydurandan, ya da kendisine bir şey vahyedilmemiş iken "Bana vahyolundu" diyenden ve "Ben de Allah\'ın indirdiği gibi indireceğim!" diyenden daha zalim kim olabilir? O zalimler ölüm dalgaları içinde, melekler ellerini uzatmış: "Haydi canlarınızı çıkarın, Allah\'a gerçek olmayanı söylemenizden ve O\'nun ayetlerine karşı büyüklük taslamanızdan ötürü, bugün alçaklık azabıyla cezalandırılacaksınız!" (derken) onların halini bir görsen!'),
(883, 6, 94, 'Andolsun, sizi ilk kez yarattığımız gibi, yine tek olarak bize geldiniz ve (dünyada) sizi hayaline daldırdığımız şeyleri arkanızda bıraktınız. Hani, siz(in yaratılışınızda ve ibadetleriniz)de (bize) ortak olduklarını sandığınız şefaatçilerinizi de yanınızda görmüyoruz. Aranızdaki bağlar kesilmiş ve (şefa\'atçi) sandığınız şeyler sizden kaybolup gitmiştir!'),
(884, 6, 95, 'Daneyi ve çekirdeği yaran, şüphesiz Allah\'tır. (O), ölüden diriyi çıkarır, diriden de ölüyü çıkarır. İşte Allah budur. O halde nasıl (yalnız O\'na tapmaktan) çevriliyorsunuz?'),
(885, 6, 96, 'Karanlığı yarıp sabahı ortaya çıkaran O\'dur. Geceyi dinlenme zamanı, güneşi ve ayı (vakitlerin bilinmesi için) birer hesap (ölçüsü) yapmıştır. Bu, o üstün ve bilen(Allah)ın takdiridir.'),
(886, 6, 97, 'O\'dur ki size, karanın ve denizin karanlıklarında, yıldızlardan yararlanıp yol bulma imkanı verdi. Gerçekten biz, bilen bir toplum için ayetleri geniş geniş açıkladık.'),
(887, 6, 98, 'Odur ki sizi bir tek nefisten inşa etti. Sizin için bir kalış ve bir emanet olarak konuluş yeri ve süresi vardır. Gerçekten biz, anlayan bir toplum için ayetleri geniş geniş açıkladık.'),
(888, 6, 99, 'O\'dur ki, size gökten su indirdi. Onunla her çeşit bitkiyi çıkardık, o bitkiden bir filiz çıkardık, ondan da birbiri üzerine binmiş daneler; hurmanın tomurcuğundan sarkan salkımlar; üzüm bağları; zeytin ve nar (bahçeleri) çıkarıyoruz. (Bunların) kimi birbirine benzer, kimi benzemez. Her birinin meyvesine bakın: Meyve verirken ve olgunlaştığı zaman. Şüphesiz bu size gösterilenlerde, inananlar toplumu için elbette çok ibret vardır.'),
(889, 6, 100, '(Tuttular) cinleri Allah\'a ortak yaptılar. Halbuki onları O yaratmıştır. Bilmeden O\'na oğullar ve kızlar icadettiler. Haşa O, onların ileri sürdüğü niteliklerden münezzehtir!'),
(890, 6, 101, '(O) gökleri ve yeri yoktan var edendir. O\'nun nasıl çocuğu olabilir ki? Kendisinin bir eşi yoktur, herşeyi O yaratmıştır ve O, herşeyi bilendir.'),
(891, 6, 102, 'Rabbiniz Allah, işte budur. O\'ndan başka tanrı yoktur. (O), herşeyin yaratıcısıdır. O\'na kulluk edin, O herşeye vekildir.'),
(892, 6, 103, 'Gözler O\'nu görmez, O gözleri görür; O latif (gözle görülmez veya lutuf sahibi), herşeyi haber alandır.'),
(893, 6, 104, 'Doğrusu size Rabbinizden basiretler geldi. Artık kim (gerçeği) görürse yararı kendisine, kim de (gerçeğe karşı) kör olursa zararı kendisinedir. Ben sizin üzerinize bekçi değilim.'),
(894, 6, 105, 'İşte böylece ayetleri döne döne açıklıyoruz ki (onlar sana): "Sen ders almışsın (bunları bir yerden okumuş, öğrenmişsin)" desinler ve bilen bir toplum için de onu iyice açıklayalım.'),
(895, 6, 106, 'Rabbinden sana vahyolunana uy; O\'ndan başka tanrı yoktur. (O\'na) ortak koşanlara da aldırma!'),
(896, 6, 107, 'Allah isteseydi, ortak koşmazlardı. Biz seni onların üzerine bekçi yapmadık, sen onlara vekil de değilsin!'),
(897, 6, 108, '(Onların) Allah\'tan başka yalvardıklarına sövmeyin ki, onlar da bilmeyerek taşkınlıkla Allah\'a sövmesinler! Biz, her ümmete yaptıkları işi böyle süslü gösterdik; sonunda dönüşleri Rablerinedir. O, onlara ne yaptıklarını haber verecektir.'),
(898, 6, 109, 'Eğer kendilerine bir mu\'cize gelirse ona mutlaka inanacaklarına olanca güçleriyle Allah\'a yemin ettiler. De ki: "Mu\'cizeler ancak Allah\'ın yanındadır." Hem bilir misiniz o (mu\'cize) gelmiş olsa da onlar yine inanmazlar?'),
(899, 6, 110, 'Gönüllerini ve gözlerini ters çeviririz, ilkin ona inanmadıkları gibi (mu\'cizeyi gördükten sonra da inanmazlar) ve bırakırız onları, azgınlıkları içinde bocalayıp dururlar.'),
(900, 6, 111, 'Biz onlara melekleri indirseydik, ölüler kendilerine konuşsaydı ve her şeyi toplayıp karşılarına getirseydik, Allah dilemedikten sonra yine inanmazlardı; fakat çokları cahillik eder(kaprislerine uyar)lar.'),
(901, 6, 112, 'Böylece biz, her peygambere insan ve cin şeytanlarını düşman yaptık. (Bunlar), aldatmak için birbirlerine yaldızlı sözler fısıldarlar. Rabbin dileseydi onu yapamazlardı. Artık onları, uydurdukları şeylerle baş başa bırak.'),
(902, 6, 113, 'Ki ahirete inanmayanların kalbleri o(nların yaldızlı sözleri)ne kansın, ondan hoşlansınlar ve onlar, işledikleri suçları işlemeğe devam etsinler.'),
(903, 6, 114, 'Allah, size Kitabı açıklanmış olarak indirmiş iken O\'ndan başka bir hakem mi arayayım? Kendilerine Kitap verdiklerimiz, O(Kur\'a)nın, gerçekten Rabbin tarafından indirilmiş olduğunu bilirler, hiç kuşkulananlardan olma.'),
(904, 6, 115, 'Rabbinin sözü hem doğruluk, hem de adalet bakımından tamamlanmıştır. O\'nun sözlerini değiştirebilecek hiç kimse yoktur. O, işitendir, bilendir.'),
(905, 6, 116, 'Yeryüzünde bulunan(insan)ların çoğuna uysan, seni Allah\'ın yolundan saptırırlar. Onlar sadece zannediyorlar ve onlar sadece saçmalıyorlar.'),
(906, 6, 117, 'Rabbin, (evet) O, yolundan sapan(lar)ı çok iyi bilir ve O, yolda olan(lar)ı çok iyi bilir.'),
(907, 6, 118, 'O halde Allah\'ın ayetlerine inanıyorsanız, üzerine O\'nun adı anılan(hayvan)lardan yeyiniz.'),
(908, 6, 119, 'Üzerine Allah\'ın adı anılmış olanlardan niçin yemeyesiniz? Çaresiz yemek zorunda kaldıklarınız dışında, size haram kıldığı şeyleri (Allah) size açıklamıştır. Doğrusu birçokları, bilmeden keyiflerine uyarak halkı şaşırtıyorlar. Muhakkak ki Rabbin, (evet) O, sınırı aşanları çok iyi bilir.'),
(909, 6, 120, 'Günahın açığını da, gizlisini de bırakın! Günah kazananlar, yaptıklarının cezasını çekeceklerdir.'),
(910, 6, 121, '(Kesilirken) üzerine Allah\'ın adı anılmayan(hayvan)lardan yemeyiniz! Çünkü o(nu yemek), yoldan çıkmadır. Şeytanlar, dostlarına, sizinle mücadele etmelerini fısıldarlar. Eğer onlara uyarsanız, şüphesiz siz de ortak koşanlar(gibi olur)sunuz.'),
(911, 6, 122, 'Ölü iken kendisini dirilttiğimiz ve kendisine insanlar arasında yürüyebileceği bir ışık verdiğimiz kimse, karanlıklar içinde kalıp ondan hiç çıkamayan kimse gibi olur mu? İşte kafirlere, yaptıkları (işler), öyle süslü gösterilmiştir.'),
(912, 6, 123, 'Böylece her kentin büyüklerini, oranın suçluları yaptık ki, orada tuzak kursunlar (her kentin ileri gelenlerine, tuzak kurmaları için fırsat verdik). Onlar kendilerinden başkasına tuzak kurmuyorlar, ama farkında değiller.'),
(913, 6, 124, 'Onlara bir ayet gelince: "Allah\'ın elçilerine verilenin aynı bize de verilmedikçe kat\'iyyen inanmayız!" dediler. Allah, mesajını koyacağı yeri (elçilik görevini kime vereceğini) bilir. Suç işleyenlere Allah katında bir aşağılık ve yaptıkları hileye karşı çetin bir azab erişecektir.'),
(914, 6, 125, 'Allah kimi doğru yola iletmek isterse onun göğsünü İslam\'a açar, kimi de saptırmak isterse onun göğsünü, (o kimse) göğe çıkıyormuş gibi dar ve tıkanık yapar. Allah. inanmayanların üstüne işte böyle pislik (sıkıntı) çökertir.'),
(915, 6, 126, 'İşte Rabbinin doğru yolu budur. Biz, öğüt alanlar için ayetleri geniş geniş açıkladık.'),
(916, 6, 127, 'Rableri katında esenlik yurdu onlarındır. Yaptıkları (güzel) işlerden dolayı O, onların dostudur.'),
(917, 6, 128, 'Hepsini bir araya toplayacağı gün: "Ey cin(şeytan)lar topluluğu, (der), siz insanlarla çok uğraştınız." Onların, insan dostları derler ki: "Rabbimiz, birbirimizden yararlandık ve bize verdiğin sürenin sonuna ulaştık." (Allah da) buyurur ki "Durağınız ateştir. Allah\'ın, dile(yip affet)mesi hariç, orada ebedi kalacaksınız." Şüphesiz Rabbin hüküm ve hikmet sahibidir, bilendir.'),
(918, 6, 129, 'İşte kazandıkları(günahları)ndan ötürü zalimlerin bir kısmını diğer bir kısmının peşine böyle takarız.'),
(919, 6, 130, 'Ey cin ve insan topluluğu, içinizden, size ayetlerimi anlatan ve bugününüzle karşılaşacağınıza dair sizi uyaran elçiler gelmedi mi? "Kendi aleyhimize şahidiz." dediler. Dünya hayatı onları aldattı ve kendilerinin kafir olduklarına şahidlik ettiler.'),
(920, 6, 131, 'Bu böyledir, çünkü Rabbin, halkı habersiz iken ülkeleri zulüm ile helak edici değildir.'),
(921, 6, 132, 'Her birinin yaptıkları işlere göre dereceleri vardır. Rabbin, onların yaptıklarından habersiz değildir.'),
(922, 6, 133, 'Rabbin zengin, rahmet sahibidir. Dilerse sizi götürür, sizi nasıl başka bir topluluğun soyundan yarattı ise, sizden sonra da dilediğini (yaratıp) sizin yerinize getirir.'),
(923, 6, 134, 'Size söylenen uyarı, muhakkak gelecektir, siz onu engelleyemezsiniz.'),
(924, 6, 135, 'De ki: "Ey kavmim, gücünüz yettiğince yapacağınızı yapın, ben de yapacağımı yapıyorum. Yakında (dünya) yurdu(nu)n sonunun kime aidolacağını bileceksiniz. Zalimler, asla onmazlar!'),
(925, 6, 136, 'Allah\'ın yarattığı, ekin(ler)den ve hayvanlardan Allah\'a pay ayırdılar. Zanlarınca: "Bu Allah\'a, bu da ortaklarımıza" dediler. Ortakları için ayrılan Allah\'a ulaşmıyor, fakat Allah için ayrılan, ortaklarına ulaşıyor. Ne kötü hüküm veriyorlar!'),
(926, 6, 137, 'Yine ortakları, müşriklerden çoğuna evladlarını öldürmeyi süslü gösterdiler ki (böylece) hem onları mahvetsinler, hem de dinlerini karıştırıp bozsunlar. Allah dileseydi bunu yapamazlardı. Öyleyse onları, uydurduklarıyle baş başa bırak!'),
(927, 6, 138, 'Zanlarınca dediler ki: "Bunlar dokunulmaz hayvanlar ve ekinlerdir. Bunları bizim dilediğimizden başkası yiyemez. Bunlar da sırtı(na binilmesi) yasaklanmış hayvanlar." Bir kısım hayvanları da üzerlerine Allah\'ın adını anmaz(dan boğazlar)lar. (Bütün bunları) Allah\'a iftira ederek (ortaya çıkardılar. Allah) Onları iftiralarıyle cezalandıracaktır.'),
(928, 6, 139, 'Dediler ki: "Bu hayvanların karınlarında olanlar, yalnız erkeklerimize aittir, kadınlarımıza haramdır". Eğer (hayvanın karnındaki yavru) ölü doğarsa, o zaman hepsi onda ortaktır. Bu nitelendirmelerinden dolayı Allah onların cezasını verecektir. Çünkü O, hüküm ve hikmet sahibidir, bilendir.'),
(929, 6, 140, 'Bilgisizlik yüzünden beyinsizce, çocuklarını öldürenler ve Allah\'ın kendilerine verdiği rızkı, Allah\'a iftira ederek haram kılanlar muhakkak ki ziyana uğradılar, saptılar, yola gelici de değiller!'),
(930, 6, 141, 'Çardaklı ve çardaksız (üzüm) bahçeleri, ürünleri çeşit çeşit hurma(ları), ekin(ler)i, zeytinleri, narları -birbirine benzer, benzemez biçimde- yaratan hep O\'dur. Her biri meyva verdiği zaman meyvasından yeyin, hasat günü hakkını (sadakasını) verin; fakat israf etmeyin; çünkü O, israf edenleri sevmez!'),
(931, 6, 142, 'Hayvanlardan da (çeşit çeşit yarattı,) Kimi yük taşır, kiminin tüyünden sergi yapılır. Allah\'ın size verdiği rızıktan yeyin, şeytanın adımlarını izlemeyin (onun peşinden gitmeyin). Zira o, sizin için apaçık bir düşmandır.'),
(932, 6, 143, 'Sekiz çift (hayvan): Koyundan iki, keçiden iki. De ki: "(Allah), iki erkeği mi haram etti, iki dişiyi mi, yoksa iki dişinin Rahimlerinde bulunan(yavru)ları mı? Eğer doğru iseniz bana bilgi ile haber verin."'),
(933, 6, 144, 'Ve deveden iki, sığırdan iki. De ki: "İki erkeği mi haram etti, iki dişiyi mi, yoksa iki dişinin Rahimlerinde bulunan(yavru)ları mı? Yoksa Allah\'ın size böyle vasiyyet ettiğine şahidler mi oldunuz?" (Allah, böyle tavsiye ederken siz O\'nun yanında mıydınız?) Öyle bilmeden insanları saptırmak için Allah\'a karşı yalan uydurandan daha zalim kim olabilir? Allah o zalim topluluğu doğru yola iletmez.'),
(934, 6, 145, 'De ki: Bana vahyolunanda, (bu haram dediklerinizi) yiyen kimse için haram edilmiş bir şey bulamıyorum. Ancak leş, yahut akıtılmış kan, yahut domuz eti -ki pistir- ya da Alah\'tan başkası adına boğazlanmış bir fısk (murdar olmuş hayvan) olursa başka (bunlar haramdır). Ama kim çaresiz kalırsa, (başkasının hakkına) saldırmamak ve (zorunluluk) sınırı(nı) aşmamak üzere (bunlardan yiyebilir). Çünkü Rabbin bağışlayandır, esirgeyendir.'),
(935, 6, 146, 'yahudilere bütün tırnaklı(hayvan)ları haram ettik. Sığır ve koyunun da, yağlarını onlara haram kıldık, yalnız (hayvanların) sırtlarının, yahut bağırsaklarının taşıdığı, ya da kemiğe karışan yağlarını haram etmedik. Aşırılıkları yüzünden onları böyle cezalandırdık. Biz elbette doğru söyleyenleriz.'),
(936, 6, 147, 'Eğer seni yalanladılarsa, de ki: "Rabbiniz bol rahmet sahibidir. Fakat O\'nun azabı da suçlu toplumdan geri çevrilmez (gazabı suçluların üzerine bir indi mi, onu kimse geri çeviremez)."'),
(937, 6, 148, '(Allah\'a) Ortak koşanlar diyecekler ki: "Allah isteseydi ne biz ne de babalarımız ortak koşmazdık, hiçbir şeyi de haram yapmazdık." Onlardan önce yalanlayanlar da öyle demişlerdi de nihayet azabımızı tadmışlardı. De ki: "Yanınızda bize çıka(rıp gösterece)ğiniz bir bilgi (yazılı belge) var mı? Siz sadece zanna uyuyorsunuz ve siz sadece saçmalıyorsunuz."'),
(938, 6, 149, 'De ki: "Üstün delil, Allah\'ındır. Allah dileseydi, elbette hepinizi doğru yola iletirdi."'),
(939, 6, 150, 'De ki: "Haydi Allah\'ın bunu yasakladığına şahidlik edecek tanrılarınızı getirin." Eğer (onlar) şahidlik ederlerse sen onlarla beraber şahidlik etme; ayetlerimizi yalanlayanların ve ahirete inanmayanların keyiflerine uyma. (Nasıl uyarsın ki) onlar, Rablerine eş tutmaktadırlar.'),
(940, 6, 151, 'De ki: "Gelin, Rabbinizin size haram kıldığı şeyleri okuyayım: O\'na hiçbir şeyi ortak koşmayın, ana babaya iyilik edin, fakirlik korkusuyla çocuklarınızı öldürmeyin; sizi de onları da biz besliyoruz. Fuhuşların açığına da, kapalısına da yaklaşmayın ve haksız yere Allah\'ın yasakladığı cana kıymayın! Düşünesiniz diye Allah size bunları tavsiye etti.'),
(941, 6, 152, 'Yetimin malına yaklaşmayın: yalnız erginlik çağına erişinceye kadar (onun malına) en güzel biçimde (yaklaşabilir, onu uygun tarzda sarfedebilirsiniz); ölçü ve tartıyı tam adaletle (dengeli) yapın. Biz, kişiye gücünün yettiğinden fazlasını teklif etmeyiz. Söylediğiniz zaman da akrabanız da olsa adalet yapın ve Allah\'a verdiğiniz sözü tutun. Hatırlayıp öğüt alasınız diye (Allah) size bunları tavsiye etti.'),
(942, 6, 153, 'İşte benim doğru yolum budur, ona uyun, (başka) yollara uymayın ki, sizi O\'nun yolundan ayırmasın! Korunmanız için (Allah) size böyle tavsiye etti.'),
(943, 6, 154, 'Sonra iyilik edenlere (ni\'metimizi) tamamlamak, her şeyi açıklamak ve yola iletici ve rahmet olmak üzere Musa\'ya Kitabı verdik ki, Rablerinin huzuruna varacaklarına inansınlar.'),
(944, 6, 155, 'İşte bu (Kur\'an) da indirdiğimiz mübarek Kitaptır. O\'na uyun ve korunun ki size rahmet edilsin!'),
(945, 6, 156, '(Onu size indirdik ki) "Kitap, yalnız bizden önceki iki topluluğa (yahudilere, hıristiyanlara) indirildi, biz ise onların okumasından habersizdik (o Kitapları okuyamıyor, dillerini anlayamıyorduk)" demeyesiniz.'),
(946, 6, 157, 'Yahut: "Eğer bize Kitap indirilseydi, biz onlardan daha çok doğru yolda olurduk." demeyesiniz. İşte size de Rabbinizden açık delil, hidayet ve rahmet geldi. Allah\'ın ayetlerini yalanlayıp onlardan yüz çevirenden daha zalim kim olabilir? Ayetlerimizden yüz çevirenleri, yüz çevirmelerinden ötürü, azabın en kötüsüyle cezalandıracağız.'),
(947, 6, 158, '(İnanmak için) ille meleklerin gelmesini, yahut Rabbinin gelmesini ya da Rabbinin bazı ayetlerinin gelmesini mi bekliyorlar? Ama Rabbinin bazı ayetleri geldiği gün, daha önce inanmamış, ya da imanında bir hayır kazanmamış olan kimseye, artık inanması, fayda sağlamaz. De ki: "Bekleyin, biz de beklemekteyiz."'),
(948, 6, 159, 'Dinlerini parça parça edip, grup grup olanlar var ya, senin onlarla hiçbir ilişkin yoktur. Onların işi Allah\'a kalmıştır, sonra (Allah) onlara yaptıklarını haber verecektir.'),
(949, 6, 160, 'Kim iyilik getirirse, ona o(getirdiği)nin on katı vardır. Kim kötülük getirirse, sadece onun dengiyle cezalandırılır, onlar haksızlığa uğratılmazlar.'),
(950, 6, 161, 'De ki: "Rabbim beni doğru yola iletti. Dosdoğru dine, Allah\'ı birleyen İbrahim\'in dinine. O, ortak koşanlardan değildi."'),
(951, 6, 162, 'De ki: "Benim namazım, ibadetim, hayatım ve ölümüm hep alemlerin Rabbi Allah içindir."'),
(952, 6, 163, 'O\'nun ortağı yoktur. Bana böyle emrolundu ve ben müslümanların ilkiyim.'),
(953, 6, 164, 'De ki: "Allah, herşeyin Rabbi iken ben O\'ndan başka Rab mı arayayım? Herkesin kazandığı yalnız kendisine aittir. Kendi (günah) yükünü taşıyan hiç kimse, bir başkasının (günah) yükünü taşımaz. Sonra dönüşünüz Rabbinizedir; (O) ayrılığa düştüğünüz gerçeği size haber verecektir."'),
(954, 6, 165, 'Sizi yeryüzünün halifeleri yapan, size verdiği şeylerde, sizi denemek için, kiminizi kiminizden derecelerle üstün kılan O\'dur. Doğrusu Rabbin, cezası çabuk olandır ve O, bağışlayandır, esirgeyendir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Elif lam mim sad.'),
(956, 7, 2, '(Bu,) Sana indirilen bir Kitaptır. Onunla (insanları) uyarman ve insanlara öğüt (vermen) hususunda göğsünde bir sıkıntı olmasın (hiç kuşkulanma, tasalanma, bu tamamen Allah tarafındandır. Sen hemen insanları uyar).'),
(957, 7, 3, '(Ey insanlar), Rabbinizden size indirilene uyun ve O\'ndan başka velilere uymayın. Ne kadar da az öğüt alıyorsunuz!'),
(958, 7, 4, 'Nice kent(ler)i helak ettik; gece yatarlarken, yahut gündüz uyurlarken, azabımız onlara geliverdi.'),
(959, 7, 5, 'Azabımız onlara geldiğinde "Biz gerçekten zalimlermişiz!" demelerinden başka yalvarıları kalmadı.'),
(960, 7, 6, 'Hem kendilerine elçi gönderilmiş olanlara soracağız, hem de gönderilen elçilere soracağız.'),
(961, 7, 7, 'Ve elbette onlara, olan biten herşeyi bilgi ile anlatacağız, zira biz onlardan uzak değiliz.'),
(962, 7, 8, 'O gün tartı tam doğrudur. Kimin (sevap) tartıları ağır gelirse, işte onlar kurtulanlardır.'),
(963, 7, 9, 'Kimin (sevap) tartıları hafif gelirse, işte onlar da ayetlerimize haksızlık etmelerinden ötürü kendilerini ziyana sokanlardır.'),
(964, 7, 10, 'Doğrusu biz sizi yeryüzünde yerleştirdik, orada size geçimlikler verdik; ne kadar da az şükrediyorsunuz!'),
(965, 7, 11, 'Sizi yarattık, sonra size biçim verdik, sonra da meleklere: "Adem\'e secde edin!" dedik; hepsi secde ettiler, yalnız İblis etmedi, o secde edenlerden olmadı.'),
(966, 7, 12, '(Allah) buyurdu: "Sana emrettiğim zaman seni secde etmekten alıkoyan nedir?" (İblis): "Ben, dedi, ondan hayırlıyım. Beni ateşten yarattın, onu çamurdan yarattın."'),
(967, 7, 13, '(Allah) buyurdu: "Öyle ise oradan in, orada büyüklük taslamak senin haddin değildir. Çık, çünkü sen aşağılıklardansın!"'),
(968, 7, 14, '(İblis) dedi: "(Bari) bana (insanların) tekrar dirilecekleri güne kadar süre ver."'),
(969, 7, 15, '(Allah) buyurdu: "Haydi sen süre verilmişlerdensin."'),
(970, 7, 16, 'Öyle ise, dedi, beni azdırmana karşılık, and içerim ki, ben de onlar(ı saptırmak) için senin doğru yolunun üstüne oturacağım.'),
(971, 7, 17, 'Sonra (onların) önlerinden, arkalarından, sağlarından, sollarından onlara sokulacağım ve çoklarını şükredenlerden bulmayacaksın!'),
(972, 7, 18, '(Allah) buyurdu: "Haydi, sen, yerilmiş ve kovulmuş olarak oradan çık. Andolsun ki onlardan sana kim uyarsa (bilin ki) sizin hepinizden (derleyip) cehennemi dolduracağım (azdıran sizler de, size uyup yoldan çıkan insanlar da cehenneme gireceksiniz)!"'),
(973, 7, 19, '(Sonra Allah, Adem\'e hitabetti): "Ey Adem, sen ve eşin cennette durun, dilediğiniz yerden yeyin; fakat şu ağaca yaklaşmayın, yoksa zalimlerden olursunuz."'),
(974, 7, 20, 'Derken şeytan, onların, kendilerinden gizlenmiş olan çirkin yerlerini kendilerine göstermek için onlara fısıldadı: "Rabbiniz, başka bir sebepten dolayı değil, sırf ikiniz de birer melek, ya da ebedi kalıcılardan olursunuz diye sizi şu ağaçtan men\'etti" dedi.'),
(975, 7, 21, 'Ve onlara: "Elbette ben size öğüt verenlerdenim." diye de yemin etti.'),
(976, 7, 22, 'Böylece onları aldatarak aşağı sarkıttı, (önceki mevkilerinden indirdi). Ağac(ın meyvasın)ı tadınca çirkin yerleri kendilerine göründü ve cennet yapraklarını üst üste yamayıp üzerlerine örtmeğe başladılar. Rableri onlara ünledi: "Ben sizi o ağaçtan men\'etmedim mi ve şeytan size apaçık düşmandır, demedim mi?"'),
(977, 7, 23, 'Dediler: "Rabbimiz, biz kendimize zulmettik, eğer bizi bağışlamaz ve bize acımazsan, muhakkak ziyana uğrayanlardan oluruz!"'),
(978, 7, 24, '(Allah) buyurdu: "Birbirinize düşman olarak inin, sizin yeryüzünde bir süreye kadar kalıp geçinmeniz gerekmektedir."'),
(979, 7, 25, 'Orada yaşayacaksınız, orada öleceksiniz ve yine oradan (diriltilip) çıkarılacaksınız! dedi.'),
(980, 7, 26, 'Ey Adem oğulları, size çirkin yerlerinizi örtecek giysi, süslenecek elbise indirdik. Korunma giysisi, en iyisidir. İşte bu(nlar), Allah\'ın ayetlerindendir, belki düşünüp öğüt alırlar.'),
(981, 7, 27, 'Ey Adem oğulları, şeytan, ana babanızı, çirkin yerlerini onlara göstermek için elbiselerini soyarak cennetten çıkardığı gibi, sizi de (şaşırtıp) bir belaya düşürmesin! Çünkü o ve kabilesi, sizin onları göremeyeceğiniz yerden sizi görürler. Biz şeytanları, inanmayanların dostları yaptık.'),
(982, 7, 28, 'Onlar bir kötülük yaptıkları zaman: "Babalarımızı bu yolda bulduk, bunu bize Allah emretti." dediler. "Allah kötülüğü emretmez, de, Allah\'a karşı bilmediğiniz şeyler mi söylüyorsunuz?"'),
(983, 7, 29, 'De ki: "Rabbim adaleti emretti. Her mescidde yüzlerinizi O\'na doğrultun ve dini yalnız kendisine has kılarak O\'na yalvarın (Allah\'a hiçbir benzer, eş, ortak koşmadan, gönlünüze başka tanrılar getirmeden sırf Allah\'a yönelerek O\'na kulluk edin). İlkin sizi yarattığı gibi yine O\'na döneceksiniz."'),
(984, 7, 30, '(O) bir topluluğu doğru yola iletti, bir topluluğa da sapıklık hak oldu. Çünkü onlar, şeytanları Allah\'tan başka dostlar tuttular ve kendilerinin de doğru yolda olduklarını sanıyorlar.'),
(985, 7, 31, 'Ey Adem oğulları, her mesci(de gidişiniz)de süs(lü, güzel giysiler)inizi alın; yeyin, için, fakat israf etmeyin; çünkü O, israf edenleri sevmez.'),
(986, 7, 32, 'De ki: "Allah\'ın, kulları için çıkardığı süsü ve güzel rızıkları kim haram etti?" De ki: "O, dünya hayatında inananlarındır, kıyamet günü de yalnız onlarındır." İşte biz, bilen bir topluluk için ayetleri böyle açıklıyoruz.'),
(987, 7, 33, 'De ki: "Rabbim, fuhuşları, gerek açığını, gerek kapalısını; günahı ve haksız yere saldırmayı; hakkında hiçbir delil indirmediği bir şeyi Allah\'a ortak koşmayı ve Allah hakkında bilmediğiniz şeyler söylemenizi kesinlikle haram etmiştir."'),
(988, 7, 34, 'Her ümmetin bir süresi vardır. Süreleri gelince (onlar), ne bir an geri kalırlar, ne de öne geçerler, (tam vaktinde batıp giderler).'),
(989, 7, 35, 'Ey Adem oğulları, size kendi içinizden elçiler gelip size ayetlerimi anlattıkarı zaman korunup uslananlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(990, 7, 36, 'Ayetlerimizi yalanlayıp onları kabule tenezzül etmeyenlere gelince, onlar da ateş halkıdır; onlar orada sürekli kalacaklardır.'),
(991, 7, 37, 'Allah\'a yalan uyduran, ya da O\'nun ayetlerini yalanlayandan daha zalim kim olabilir? Onlara Kitaptan nasipleri erişir (ezelde kendileri için ne rızık takdir edilmişse onu alır ve kendilerine yazılmış süre kadar yaşarlar); nihayet (ömürleri tükendiğizaman) melek elçilerimiz gelip canlarını alırken: "Hani Alah\'tan başka yalvardıklarınız nerede?" dediklerinde: "Bizden sapıp, kayboldular" dediler ve kendi aleyhlerine, kendilerinin kafir olduklarına şahidlik ettiler.'),
(992, 7, 38, '(Allah) buyurdu: "Sizden önce geçen cin ve insan topluluklariyle beraber ateşin içine girin!" Her ümmet girdikçe yoldaşına la\'net etti. Hepsi birbiri ardından orada toplanınca sonrakiler, öncekiler için dediler ki: "Rabbimiz, bunlar bizi saptırdılar. Bunlara ateşten bir kat daha azab ver!" (Allah): "Hepsi için bir kat fazla (azab) vardır, ama siz bilmezsiniz." dedi.'),
(993, 7, 39, 'Öncekiler de sonrakilere dediler ki: "Sizin bize bir üstünlüğünüz yok. O halde siz de kazandıklarınıza karşılık azabı tadın!"'),
(994, 7, 40, 'Bizim ayetlerimizi yalanlayan ve onlara inanmağa tenezzül etmeyenler var ya, işte onlara gök kapıları açılmayacak ve deve, iğne deliğinden geçinceye kadar onlar cennete giremeyeceklerdir! İşte suçluları böyle cezalandırırız.'),
(995, 7, 41, 'Onlar için cehennemden bir döşek ve üstlerinde de (yine ateşten) örtüler vardır. İşte zalimleri böyle cezalandırırız!'),
(996, 7, 42, 'İnanıp iyi işler yapanlar, -ki hiç kimseye gücünün üstünde bir şey yüklemeyiz- İşte onlar cennet halkıdır, onlar orada ebedi kalacaklardır.'),
(997, 7, 43, 'Göğüslerinden kinden (tasadan) ne varsa hepsini çıkarıp atmışızdır. Altlarından ırmaklar akmaktadır. "lutfedip bizi buraya getiren Allah\'a hamdolsun, Allah bizi getirmeseydi, biz bunu bulamazdık! Rabbimizin elçileri, gerçeği getirmişler (söyledikleri doğruymuş)." dediler. Onlara: "İşte size cennet; yaptıklarınıza karşılık o size miras verildi" diye seslenildi.'),
(998, 7, 44, 'Cennet halkı, ateş halkına seslendi: "Rabbimizin bize va\'dettiğini biz gerçek bulduk. Siz de Rabbinizin size va\'dettiğini gerçek buldunuz mu?" (Onlar da): "Evet", dediler ve aralarından bir ünleyici: "Allah\'ın la\'neti zalimlerin üzerine olsun!" diye ünledi.'),
(999, 7, 45, 'Onlar ki Allah\'ın yolundan menedip, onu eğriltmek isterler, ahireti de inkar ederlerdi.'),
(1000, 7, 46, 'İki taraf arasında bir perde ve A\'raf üzerinde de hepsini (hem cennetlikleri hem de cehennemlikleri, yüzlerindeki) işaretleriyle tanıyan erkekler vardır. (Bunlar), henüz cennete girmemiş olan, fakat girmeyi bekleyen, cennet halkına: "selam size!" diye seslendiler.'),
(1001, 7, 47, 'Gözleri ateş halkı tarafına çevrildiği zaman da; "Rabbimiz, bizi şu -zalim toplulukla beraber bulundurma!" dediler.'),
(1002, 7, 48, 'A\'raf halkı, yüzlerindeki işaretleriyle tanıdıkları birtakım adamlara da ünleyerek dediler ki: "Ne topluluğunuz, ne de büyüklük taslamanız, size hiçbir yarar sağlamadı."'),
(1003, 7, 49, 'Allah onları hiçbir rahmete erdirmeyecek, diye yemin ettiğiniz kimseler bunlar mıydı? (Cennetliklere dönerek): "Girin cennete, artık size ne korku vardır, ne de siz üzüleceksiniz!" dediler.'),
(1004, 7, 50, 'Ateş halkı, cennet halkına: "Suyunuzdan veya Allah\'ın size verdiği rızıktan biraz da bizim üzerimize akıtın (ne olur)!" diye seslendiler. (Onlar da) dediler ki; "Allah, bu ikisini kafirlere haram etmiştir."'),
(1005, 7, 51, 'Onlar ki dinlerini bir eğlence ve oyun yerine koydular ve dünya hayatı, kendilerini aldattı. Onlar, bu günleriyle karşılaşacaklarını nasıl unuttular ve ayetlerimizi bile bile nasıl inkar ediyor idilerse, biz de bugün onları öyle unuturuz!.'),
(1006, 7, 52, 'Gerçekten onlara, bilgiye göre açıkladığımız, inanan bir toplum için yol gösterici ve rahmet olan bir Kitap getirdik.'),
(1007, 7, 53, 'İlle onun te\'vilini mi gözetiyorlar? Onun te\'vili geldiği (haber verdiği şeyler ortaya çıktığı) gün, önceden onu unutmuş olanlar derler ki: "Doğrusu Rabbimizin elçileri gerçeği getirmiş. Şimdi bizim şefa\'atçilerimiz var mı ki bize şefa\'at etsinler, yahut tekrar geri döndürül(üp dünyaya gönderil)memiz mümkün mü ki, (orada eski) yaptıklarımızdan başkasını yapalım?" Onlar, kendilerini ziyana soktular ve uydurdukları şeyler, kendilerinden saptı (kaybolup gitti).'),
(1008, 7, 54, 'Rabbiniz o Allah\'tır ki; gökleri ve yeri altı günde yarattı, sonra Arşa istiva etti (tahta kuruldu. O), geceyi, durmadan onu kovalayan gündüzün üzerine bürüyüp örter. Güneşi, ayı ve yıldızları buyruğuna boyun eğmiş vaziyette (yaratan O\'dur). İyi bilin ki, yaratma ve emir O\'nundur. Âlemlerin Rabbi Allah, ne uludur!'),
(1009, 7, 55, 'Rabbinize yalvararak ve gizlice du\'a edin, çünkü O, haddi aşanları sevmez.'),
(1010, 7, 56, 'Yeryüzü düzeltildikten sonra onda bozgunculuk yapmayın, korkarak ve umarak O\'na du\'a edin. Muhakkak ki Allah\'ın rahmeti, iyilik edenlere yakındır.'),
(1011, 7, 57, 'O ki rüzgarları rahmetinin önünde müjdeci gönderir. Nihayet onlar, ağır ağır bulutları yüklenince, onu ölü bir ülkeye yollarız; onunla su indirir ve türlü türlü meyvalar çıkarırız. İşte ölüleri de böyle çıkaracağız. Herhalde bundan ibret alırsınız.'),
(1012, 7, 58, 'Güzel olan ülkenin bitkisi, Rabbinin izniyle çıkar; kötü olandan ise yararsız bitkiden başka bir şey çıkmaz. İşte biz, şükreden bir toplum için ayetleri böyle döndürüp (tekrar tekrar) açıklarız.'),
(1013, 7, 59, 'Andolsun Nuh\'u kavmine gönderdik: "Ey kavmim, dedi, Allah\'a kulluk edin, sizin O\'ndan başka tanrınız yoktur. Doğrusu ben, size büyük bir günün azabın(ın inmesin)den korkuyorum."'),
(1014, 7, 60, 'Kavminden ileri gelenler dediler ki: "Biz seni açık bir sapıklık içinde görüyoruz!"'),
(1015, 7, 61, 'Dedi ki: "Ey kavmim, bende bir sapıklık yok, ben alemlerin Rabbi tarafından gönderilmiş bir elçiyim."'),
(1016, 7, 62, 'Size Rabbimin mesajlarını duyuruyorum, size öğüt veriyorum ve Allah tarafından, sizin bilmediğiniz şeyleri biliyorum.'),
(1017, 7, 63, 'Korunup da merhamete uğramanız için, içinizden sizi uyaracak bir adam aracılığı ile bir Zikir (sizi ikaz eden bir Kitap, size şan ve şeref verecek bir kanun) gelmesine şaştınız mı?'),
(1018, 7, 64, 'O\'nu yalanladılar, biz de O\'nunla berebar gemide bulunanları kurtardık, ayetlerimizi yalanlayanları boğduk! Çünkü onlar kör bir kavim idiler.'),
(1019, 7, 65, 'Ad(kavmin)e de kardeşleri Hud\'u (gönderdik): "Ey kavmim, Allah\'a kulluk edin, sizin O\'dan başka tanrınız yoktur. (O\'na karşı gelmekten) sakınmaz mısınız?" dedi.'),
(1020, 7, 66, 'Kavminden ileri gelen inkarcılar dediler ki: "Biz seni bir beyinsizlik içinde görüyoruz ve biz seni yalancılardan sanıyoruz!"'),
(1021, 7, 67, 'Ey kavmim, bende beyinsizlik yok, ben alemlerin Rabbi tarafından gönderilmiş bir elçiyim. dedi.'),
(1022, 7, 68, 'Size Rabbimin mesajlarını duyuruyorum ve ben sizin için güvenilir bir öğütçüyüm.'),
(1023, 7, 69, 'Sizi uyarması için içinizden bir adam aracılığı ile Rabbinizden size bir Zikir gelmesine şaştınız mı? Düşünün ki (Allah) sizi, Nuh kavminden sonra, onların yerine hakimler yaptı. Üstelik, yaratılışta, size irilik verdi (sizi daha iri yapılı yarattı). Allah\'ın ni\'metlerini hatırlayın ki başarıya eresiniz.'),
(1024, 7, 70, 'Dediler ki; "Ya, demek sen, tek Allah\'a kulluk edelim ve atalarımızın taptıklarını bırakalım diye mi bize geldin? Eğer doğrulardan isen bizi tehdidettiğin(o azab)ı bize getir!"'),
(1025, 7, 71, 'Dedi ki: "Artık size Rabbinizden bir rics (pislik) ve gazab inmiştir. Allah\'ın, kendileri için hiçbir delil indirmediği (ve hiçbir güç vermediği), sadece sizin ve atalarınızın taktığı (boş) isimler hakkında mı benimle tartışıyorsunuz? Bekleyin öyle ise, ben de sizinle beraber bekleyenlerdenim!"'),
(1026, 7, 72, 'O\'nu ve O\'nunla beraber olanları, bizden bir rahmetle kurtardık, ayetlerimizi yalanlayanların ve inanmayacak olanların ardını kestik.'),
(1027, 7, 73, 'Semud(kavmin)e de kardeşleri Salih\'i (gönderdik): "Ey kavmim dedi, Allah\'a kulluk edin, sizin O\'ndan başka tanrınız yoktur. Size Rabbinizden açık delil geldi. İşte şu, Allah\'ın devesi, size bir mu\'cizedir; bırakın onu Allah\'ın arzından yesin (içsin), sakın ona bir kötülük etmeyin, yoksa sizi acı bir azab yakalar."'),
(1028, 7, 74, 'Düşünün ki (Allah), Ad\'dan sonra sizi hükümdarlar yaptı ve yeryüzünde sizi yerleştirdi: Onun düzlüklerinde saraylar ediniyorsunuz, dağlarını yontup evler yapıyorsunuz, artık Allah\'ın ni\'metlerini hatırlayın da yeryüzünde bozgunculuk yapıp karışıklık çıkarmayın.'),
(1029, 7, 75, 'Kavminden büyüklük taslayan ileri gelenler, içlerinden zayıf görülen inananlara: "Siz, dediler, Salih\'in, gerçekten Rabbi tarafından gönderildiğini biliyor musunuz?" (Onlar da): "(Evet), doğrusu biz onunla gönderilene inananlarız!" dediler.'),
(1030, 7, 76, 'Büyüklük taslayanlar: "Biz, sizin inandığınızı inkar edenleriz!" dediler.'),
(1031, 7, 77, 'Derken dişi deveyi boğazladılar ve Rablerinin buyruğu dışına çıktılar; "Ey Salih, eğer hakikaten elçilerdensen, bizi tehdidettiğin (azab)ı bize getir!" dediler.'),
(1032, 7, 78, 'Bunun üzerine hemen onları, o sarsıntı yakaladı, yurtlarında diz üstü çökekaldılar.'),
(1033, 7, 79, '(Salih), onlardan öteye döndü de: "Ey kavmim, ben size Rabbimin mesajlarını duyurdum ve size öğüt verdim; fakat siz öğüt verenleri sevmiyorsunuz!" dedi.'),
(1034, 7, 80, 'Lut\'u da (gönderdik). Kavmine dedi ki: "Siz, sizden önce dünyalarda hiç kimsenin yapmadığı fuhşu mu yapıyorsunuz?"'),
(1035, 7, 81, 'Siz, kadınları bırakıp şehvetle erkeklere gidiyorsunuz ha! Doğrusu siz, israfçı (aşırı) bir kavimsiniz!'),
(1036, 7, 82, 'Kavminin cevabı: "Onları (şu Lut taraftarlarını) kentinizden çıkarın, çünkü onlar, fazla temizlenen insanlarmış!" demelerinden başka olmadı.'),
(1037, 7, 83, 'Biz de onu ve ailesini kurtardık, yalnız karısı geride kalanlardan oldu.'),
(1038, 7, 84, 'Ve üzerlerine bir (taş) yağmur(u) yağdırdık; bak, işte suçluların sonu nasıl oldu!'),
(1039, 7, 85, 'Medyen\'e de kardeşleri Şuayb\'i (gönderdik): "Ey kavmim, dedi, Allah\'a kulluk edin, sizin O\'ndan başka tanrınız yoktur. Size Rabbinizden açık bir delil geldi. Ölçüyü ve tartıyı tam yapın, insanların eşyalarını eksik vermeyin, düzeltildikten sonra yeryüzünde bozgunculuk yapmayın; eğer inananlar iseniz, böylesi sizin için daha iyidir!"'),
(1040, 7, 86, 'Ve her yolun başına oturup da tehdidederek inananları Allah yolundan çevirmeğe ve o(Hak yolu)nu eğriltmeğe çalışmayın; düşünün siz az idiniz, O sizi çoğalttı ve bakın, bozguncuların sonu nasıl oldu!'),
(1041, 7, 87, 'Eğer içinizden bir kısmı benimle gönderilene inanmış, bir kısmı da inanmamış ise, Allah aramızda hükmedinceye kadar sabredin; O, hükmedenlerin en iyisidir!'),
(1042, 7, 88, 'Kavminden büyüklük taslayan ileri gelenler dediler ki: "Ey Şu\'ayb, mutlaka seni ve seninle beraber inananları kentimizden çıkarırız, ya da dinimize dönersiniz!" Dedi ki: "İstemesek de mi (bizi yurdumuzdan çıkaracak veya dinimizden döndüreceksiniz)?'),
(1043, 7, 89, 'Allah, bizi sizin dininizden kurtardıktan sonra eğer tekrar ona dönersek, Allah\'ın üzerine yalan atmış oluruz. Rabbimiz Allah, dilemedikten sonra o(sizin dediğiniz di)ne dönmemiz, bizim için olur şey değildir. Rabbimiz, bilgice her şeyi kuşatmıştır. Biz Allah\'a dayanmışız. (Ey) Rabbimiz, bizimle kavmimizin arasın(daki iş)i gerçekle aç(ığa çıkar). Muhakkak ki sen (gerçekleri) aç(ığa çıkar)anlanın en iyisisin!"'),
(1044, 7, 90, 'Kavminden inkar eden ileri gelenler dediler ki: "Eğer Şu\'ayb\'e uyarsanız muhakkak siz ziyana uğrarsınız!"'),
(1045, 7, 91, 'Derken o müthiş sarsıntı onları yakalayıverdi, yurtlarında diz üstü çökekaldılar.'),
(1046, 7, 92, 'Şu\'ayb\'i yalanlayanlar, sanki yurtlarında hiç oturmamış gibi oldular. Şu\'ayb\'i yalanlayanlar... işte ziyana uğrayanlar, onlar oldular.'),
(1047, 7, 93, '(Şu\'ayb), onlardan öteye döndü de: "Ey kavmim dedi, ben size Rabbimin mesajlarını duyurdum ve size öğüt verdim, artık kafir bir kavme nasıl acırım?"'),
(1048, 7, 94, 'Biz hangi ülkeye bir peygamber gönderdiysek, onun halkını -yalvarıp yakarsınlar diye- mutlaka yoksulluk ve darlıkla sıkmışızdır.'),
(1049, 7, 95, 'Sonra kötülüğü değiştirip yerine iyilik getirdik de (insanlar) çoğaldılar ve: "Atalarımıza da darlık ve sevinç dokunmuştu (onlar da üzüntülü ve sevinçli günler geçirmişlerdi)." dediler (de olaylardan ibret alıp şükretmediler). Biz de onları, hiç farkında olmadıkları bir sırada ansızın yakaladık.'),
(1050, 7, 96, '(O) ülkelerin halkı inanıp (kötülüklerden) korunsalardı, elbette üzerlerine gökten ve yerden bolluklar açardık; fakat yalanladılar, biz de onları kazandıklarıyle yakaladık.'),
(1051, 7, 97, 'Peki (o) ülkelerin halkı, geceleyin uyurlarken azabımızın kendilerine gelmeyeceğinden emin midirler?'),
(1052, 7, 98, 'Ya da (o) ülkelerin halkı, kuşluk vakti eğlenirlerken azabımızın onlara gelmeyeceğinden emin midirler?'),
(1053, 7, 99, 'Allah\'ın tuzağından (kurtulacaklarına) emin mi oldular? Ziyana uğrayan topluluktan başkası, Allah\'ın tuzağın(a yakalanmayacağın)dan emin olamaz.'),
(1054, 7, 100, '(Geçmiştekilerin başlarına gelenler), sahiplerinden sonra şu toprağa varis olanları yola getirmedi mi (hala anlamadılar mı) ki biz dilesek, kendilerini de günahlarıyle cezalandırırız ve kalblerini mühürleriz, artık hiç işitmezler.'),
(1055, 7, 101, 'İşte o ülkeler; sana onların haberlerinden bir kısmını anlatıyoruz. Andolsun, elçileri onlara açık deliller getirmişlerdi. Fakat önceden yalanladıklarından ötürü, inanmak istemediler. İşte Allah, kafirlerin kalblerini böyle mühürler.'),
(1056, 7, 102, 'Onların çoklarını yoldan çıkmış bulduk ama, çoklarında sözde durma diye bir şey bulmadık.'),
(1057, 7, 103, 'Onlardan sonra Musa\'yı ayetlerimizle Fir\'avn\'a ve onun ileri gelen adamlarına gönderdik, ayetlerimize haksızlık ettiler; fakat bak, bozguncuların sonu nasıl oldu!'),
(1058, 7, 104, 'Musa dedi ki: "Ey Fir\'avn, ben alemlerin Rabbi tarafından gönderilmiş bir elçiyim."'),
(1059, 7, 105, 'Allah\'a karşı gerçekten başkasını söylememek, benim üzerime borçtur. Size Rabbinizden açık bir delil getirdim, artık İsrail oğullarını benimle gönder!'),
(1060, 7, 106, '(Fir\'avn) dedi. "Eğer bir ayet (mu\'cize) getirmiş isen, hakikaten doğru söylüyorsan göster onu bakalım!"'),
(1061, 7, 107, 'Bunun üzerine (Musa), asasını attı, birden o, açıkça bir ejderha (oluverdi).'),
(1062, 7, 108, 'Ve elini (böğründen) çıkardı, birden o, bakanlar için, bembeyaz parlayan bir şey oldu.'),
(1063, 7, 109, 'Fir\'avn kavminden ileri gelen bir topluluk dediler ki: "Bu, çok bilgili bir büyücüdür!"'),
(1064, 7, 110, 'Sizi yurdunuzdan çıkarmak istiyor, ne buyurursunuz?'),
(1065, 7, 111, 'Onu da kardeşini de beklet, dediler, şehirlere toplayıcılar yolla.'),
(1066, 7, 112, 'Bütün bilgili büyücüleri (toplayıp) sana getirsinler.'),
(1067, 7, 113, 'Büyücüler Fir\'avn\'a gelip: "Eğer üstün gelen biz olursak, elbet bize bir mükafat var, değil mi?" dediler.'),
(1068, 7, 114, '(Fir\'avn): "Evet, dedi, hem de siz (benim) yakınlar(ım)dan(olacak)sınız!"'),
(1069, 7, 115, 'Dediler ki "Ey Musa, sen mi (önce hünerini ortaya) atacaksın, yoksa (önce) atanlar biz mi olalım?"'),
(1070, 7, 116, 'Siz atın dedi. (Hünerlerini ortaya) atınca, insanların gözlerini büyülediler, onları ürküttüler ve büyük bir büyü (ortaya) getirdiler.'),
(1071, 7, 117, 'Biz de Musa\'ya: "Asanı at!" diye vahyettik. Bir de baktılar ki o, onların uydurduklarını yakalayıp yutuyor. (Musa\'nın ejderha olan değneği, büyücülerin büyülerini yutup yok etmişti).'),
(1072, 7, 118, 'Gerçek ortaya çıktı ve onların bütün yaptıkları batıl oldu.'),
(1073, 7, 119, 'Orada yenildiler, küçük düştüler.'),
(1074, 7, 120, 'Ve büyücüler secdeye kapandılar:'),
(1075, 7, 121, 'Alemlerin Rabbine inandık! dediler.'),
(1076, 7, 122, 'Musa ve Harun\'un Rabbine!'),
(1077, 7, 123, 'Fir\'avn: "Ben size izin vermeden ona inandınız mı?" dedi. "Bu, bir tuzaktır, şehirde bu tuzağı kurdunuz ki, halkını oradan çıkarasınız, ama yakında (başınıza gelecekleri) bileceksiniz!"'),
(1078, 7, 124, 'Elbette ellerinizi ve ayaklarınızı çaprazlama keseceğim, sonra hepinizi (hurma dallarına) asacağım!'),
(1079, 7, 125, 'Dediler ki: "Biz zaten Rabbimize döneceğiz!"'),
(1080, 7, 126, 'Rabbimizin, bize gelmiş olan ayetlerine inandığımız için bizden öc alıyorsun. (Ey) Rabbimiz, üzerimize sabır boşalt ve bizi müslümanlar olarak öldür!'),
(1081, 7, 127, 'Fir\'avn kavminden ileri gelen bir topluluk dedi ki: "Musa\'yı ve kavmini bırakıyorsun ki, seni ve tanrılarını terk edip yeryüzünde bozgunculuk mu yapsınlar?" (Fir\'avn): "Biz onların oğullarını öldüreceğiz, kadınlarını sağ bırakacağız. Biz daima onların üstünde eziciler olacağız!" dedi.'),
(1082, 7, 128, 'Musa, kavmine; "Allah\'tan yardım isteyin, sabredin!" dedi; yeryüzü Allah\'ındır, onu kullarından dilediğine verir. Sonuç, korunanlarındır!"'),
(1083, 7, 129, '(Ey Musa), sen bize gelmezden önce de, sen bize geldikten sonra da bize işkence edildi. dediler. (Musa) dedi: "Umulur ki Rabbiniz düşmanınızı yok eder ve onların yerine sizi yeryüzüne hakim kılar da nasıl hareket edeceğinize bakar."'),
(1084, 7, 130, 'Andolsun biz, Fir\'avn ailesini tuttuk, öğüt alsınlar diye yıllarca kıtlıkla ve ürünleri azaltmakla sıktık.'),
(1085, 7, 131, 'Onlara bir iyilik geldiği zaman: "Bu, bizimdir (kendi becerimizle bunu elde ettik)" derler; kendilerine bir kötülük ulaşırsa, Musa ve onunla beraber olanları uğursuz sayarlar(onların yüzünden belaya uğradıklarını sanırlar)dı. İyi bilinki, onların uğursuzluğu Allah katındadır, fakat çokları bilmezler.'),
(1086, 7, 132, 'Ve dediler ki: "bizi büyülemek için ne kadar mu\'cize getirirsen getir, biz sana inanacak değiliz!"'),
(1087, 7, 133, 'Biz de onların üzerine ayrı ayrı mu\'cizeler olarak Tufan, Çekirge, Kımıl (haşerat), Kurbağalar ve Kan gönderdik; ama yine büyüklük tasladılar ve suçlu bir topluluk oldular.'),
(1088, 7, 134, 'Üzerlerine azab çökünce: "Ey Musa, dediler, sana verdiği söz uyarınca bizim için Rabbine du\'a et; eğer bizden azabı kaldırırsan, muhakkak sana inanacağız ve mutlaka İsrail oğullarını seninle beraber göndereceğiz!"'),
(1089, 7, 135, 'Biz onlardan, geçirecekleri bir süreye kadar azabı kaldırınca, hemen yeminlerini bozmağa başladılar.'),
(1090, 7, 136, 'Biz de onlardan öc aldık, onları yemm(su)da boğduk! Çünkü onlar, ayetlerimizi yalanlamışlardı ve onları umursamaz olmuşlardı.'),
(1091, 7, 137, 'Hor görülüp ezilmekte olan milleti de içini bereketlerle donattığımız yerin, doğularına ve batılarına mirasçı kıldık. Rabbinin İsrail oğullarına verdiği güzel söz, sabretmeleri yüzünden tam yerine geldi. Fir\'avn\'ın ve kavminin yapageldiği şeyleri ve yükseltmekte oldukları sarayları (ve bahçeleri) de yıktık.'),
(1092, 7, 138, 'İsrail oğullarını denizden geçirdik, kendilerine mahsus birtakım putlara tapan bir kavme rastladılar: "Ey Musa, dediler, (bak) bunların nasıl tanrıları var, bize de öyle bir tanrı yap!" (Musa) dedi: "Siz, gerçekten cahil bir toplumsunuz."'),
(1093, 7, 139, 'Şunların içinde bulundukları (din) yıkılmıştır ve yaptıkları şeyler boşa çıkmıştır.'),
(1094, 7, 140, 'Allah, sizi alemlere üstün yapmış iken size Allah\'tan başka bir tanrı mı arayayım? dedi.'),
(1095, 7, 141, '(Ey İsrail oğulları), hatırlayın o zamanı ki biz sizi Fir\'avn ailesinden kurtarmıştık. Onlar size azabın en kötüsünü yapıyorlardı: "Oğullarınızı öldürüyor, kadınlarınızı sağ bırakıyorlardı. Bunda, size Rabbiniz tarafından büyük bir imtihan vardı.'),
(1096, 7, 142, 'Musa ile otuz gece (bana ibadet etmesi için) sözleştik ve buna on gece daha kattık. Böylece Rabbinin tayin ettiği vakit, kırk geceye tamamlandı. Musa, kardeşi Harun\'a dedi ki: "Kavmim içinde benim yerime geç, ıslah et, bozguncuların yoluna uyma."'),
(1097, 7, 143, 'Musa, tayin ettiğimiz vakitte bizimle buluşmağa gelip de Rabbi ona konuşunca: "Rabbim, bana görün, sana bakayım!" dedi. (Rabbi) buyurdu ki: "Sen beni göremezsin; fakat dağa bak, eğer o yerinde durursa, sen de beni göreceksin!" Rabbi dağa görününce onu darmadağın etti ve Musa da baygın düştü. Ayılınca: "Sen yücesin, sana tevbe ettim, ben inananların ilkiyim!" dedi.'),
(1098, 7, 144, '(Allah) buyurdu ki: "Ey Musa, Ben mesajlarımla ve konuşmamla seni insanların başına seçtim; sana verdiğimi al ve şükredenlerden ol!"'),
(1099, 7, 145, 'Öğüte ve her şeyin açıklamasına dair ne varsa hepsini Musa için levhalara yazdık: "Bunları kuvvetle tut, kavmine de emret, bunların en güzelini tutsunlar (bu en güzel buyruklar gereğince amel etsinler); size, yoldan çıkmışların yurdunu (nasıl tarumar ettiğimi) göstereceğim!"'),
(1100, 7, 146, 'Yeryüzünde haksız yere büyüklenenleri ayetlerimden uzaklaştıracağım. Onlar her ayeti görseler de yine ona inanmazlar. Doğru yolu görseler, onu yol edinmezler, ama azgınlık yolunu görseler, onu yol edinirler. Çünkü onlar, ayetlerimizi yalanladılar ve onları umursamaz oldular.'),
(1101, 7, 147, 'Ayetlerimizi ve ahirete kavuşmayı yalanlayanların eylemleri boşa çıkmıştır. Onlar, yalnız yaptıklarıyle cezalanmıyorlar mı?'),
(1102, 7, 148, 'Musa kavmi, kendisin(in, Rabbi ile mülakata gitmesin)den sonra kendilerinin zinet takımlarından yapılmış, böğürmesi olan bir buzağı heykelini (tanrı diye) benimsediler. Görmediler mi ki o, ne kendilerine söz söylüyor, ne de onlara yol gösteriyor? Onu benimsediler ve zalimler(den) oldular.'),
(1103, 7, 149, 'Ne zaman ki (pişmanlıklarından ötürü) başları elleri arasına düşürüldü ve kendilerinin gerçekten sapmış olduklarını gör(üp anla)dılar, dediler ki: "Eğer Rabbimiz bize acımaz ve bizi bağışlamazsa, elbette ziyana uğrayanlardan oluruz!"'),
(1104, 7, 150, 'Musa, kavmine kızgın ve üzgün bir halde dönünce: "Benden sonra arkamdan ne kötü işler yaptınız? Rabbinizin emrini (beklemeyip) acele mi ettiniz?" dedi, levhaları yere attı ve kardeşinin başını tutup kendine doğru çekmeye başladı. (Kardeşi): "Anamın oğlu, dedi, bu insanlar beni hırpaladılar, az daha beni öldürüyorlardı. (Ne olur) düşmanları üstüme güldürme, beni bu zalim kavimle beraber tutma!"'),
(1105, 7, 151, '(Musa): "Rabbim, dedi, beni ve kardeşimi bağışla, bizi rahmetinin içine sok, merhametlilerin en merhametlisi sensin!"'),
(1106, 7, 152, 'Buzağıyı (tanrı diye) benimseyenlere, muhakkak Rablerinden bir öfke ve dünya hayatında bir alçaklık erişecektir! İşte biz iftiracıları böyle cezalandırırız.'),
(1107, 7, 153, 'Ama kötülükler yaptıktan sonra ardından tevbe edip inananlar(a karşı), muhakkak ki Rabbin, o(tevbe ve ima)ndan sonra, elbette bağışlayandır, esirgeyendir.'),
(1108, 7, 154, 'Öfkesi dinince Musa, levhaları aldı. Onlardaki yazıda Rablerinden korkanlar için yol gösterme ve rahmet vardı.'),
(1109, 7, 155, '(Allah, Musa\'ya kırk gece ibadetten sonra buluşma va\'detmiş ve kavminden yetmiş kişiyi de seçip o huzura getirmesini emretmişti). Musa, bizimle buluşma vakti için kavminden yetmiş adam seçti (huzura getirdi. Gelenler, Musa ile Allah arasındaki o yüce konuşmayı işitmekle yetinmeyip Allah\'ı açıkça görmedikçe inanmayacaklarını söylediler. Bunun üzerine) onları sarsıntı yakalayınca (Musa) dedi ki: "Rabbim, dileseydin bunları da beni de daha önce helak ederdin. İçimizden bazı beyinsizlerin yaptıklarından ötürü bizi helak mı edeceksin? Bu (iş), senin imtihanından başka bir şey değildir. Onunla dilediğini şaşırtırsın, dilediğine yol gösterirsin. Sen bizim velimizsin, bizi bağışla, bize acı! Sen bağışlayanların en iyisisin!"'),
(1110, 7, 156, 'Bize bu dünyada da iyilik yaz, ahirette de. Biz sana yöneldik. (Alah) buyurdu ki: "Azabıma, dilediğimi uğratırım; rahmetim ise her şeyi kaplamıştır. Onu, korunanlara, zekatı verenlere ve ayetlerimize inananlara yazacağım."'),
(1111, 7, 157, 'Onlar ki yanlarındaki Tevrat ve İncil\'de yazılı buldukları o Elçi\'ye, o ümmi Peygamber\'e uyarlar. O (Peygamber) ki, kendilerine iyiliği emreder, kendilerini kötülükten meneder; onlara güzel şeyleri helal, çirkin şeyleri haram kılar, üzerlerindeki ağırlıkları, sırtlarındaki zincirleri kaldırıp atar. O\'na inanan, destekleyerek O\'na saygı gösteren, O\'na yardım eden ve O\'nunla beraber indirilen nura uyanlar, işte felaha erenler onlardır.'),
(1112, 7, 158, 'De ki: "Ey insanlar, ben sizin hepinize, göklerin ve yerin sahibi olan, kendisinden başka tanrı bulunmayan, yaşatan, öldüren Allah\'ın Elçisiyim. Gelin Allah\'a ve O\'nun ümmi peygamberi olan Elçisine inanın -ki o (peygamber) de Allah\'a ve O\'nun sözleri ne inanmaktadır,-O\'na uyun ki doğru yolu bulasınız!"'),
(1113, 7, 159, 'Musa kavmi içinde doğrulukla hakka götüren ve hak ile adalet yapan bir topluluk da vardır.'),
(1114, 7, 160, 'Biz onları (Ya\'kub\'un oniki oğlundan gelen) oniki torun kabileye ayırdık. Kavmi kendisinden su isteyince, Musa\'ya: "Asanla taşa vur!" diye vahyettik. Taştan oniki göze fışkırdı. Her kabile içeceği yeri bildi. (Ayrıca) üzerlerine bulutla gölge yaptık ve onlara kudret helvasıyle bıldırcın eti indirdik: "Size verdiğimiz güzel rızıklardan yeyin!" (dedik). Ama onlar (saptılar, haksızlık ettiler. Böylece onlar) bize zulmetmediler, fakat kendi kendilerine zulmediyorlardı.'),
(1115, 7, 161, 'Onlara: "Şu kentte oturun. Orada dilediğiniz yerden yeyin, (Allah\'a niyaz edip bizi) affet deyin ve secde ederek kapıdan girin ki hatalarınızı bağışlayalım; biz iyilik edenlere daha fazlasını da vereceğiz." denildi.'),
(1116, 7, 162, 'İçlerinden zulmedenler, (söylediğimiz) sözü, kendilerine söylenmeyen bir sözle değiştirdiler. Biz de haksızlık ettiklerinden dolayı üzerlerine gökten bir azab gönderdik.'),
(1117, 7, 163, 'Onlara, deniz kıyısında bulunan kent(halkın)ın durumunu sor. Hani onlar Cumartesine saygısızlık edip haddi aşıyorlardı. Çünkü Cumartesi (tatil) yaptıkları gün, balıkları onlara akın akın gelirdi. Cumartesi (tatil) yapmadıkları gün balıkları gelmezlerdi. Biz onları yoldan çıkmalarından ötürü böyle sınıyorduk.'),
(1118, 7, 164, 'İçlerinden bir topluluk: "Allah\'ın helak edeceği, yahut şiddetli bir şekilde azabedeceği bir kavme artık ne diye öğüt veriyorsunuz?" dedi. Dediler ki: "Rabbinize ma\'zeret (beyan edebilmek) için, bir de belki korunurlar diye (öğüt veriyoruz)."'),
(1119, 7, 165, 'Ne zaman ki onlar, kendilerine hatırlatılanı unuttular, biz de kötülükten menedenleri kurtardık; zulmedenleri de, yoldan çıkmaları yüzünden çetin bir azab ile yakaladık.'),
(1120, 7, 166, 'Kibirlerinden dolayı kendilerine yasak kılınan şeylerden vazgeçmeyince onlara: "Aşağılık maymunlar olun!" dedik.'),
(1121, 7, 167, 'Rabbin, "Elbette ta kıyamet gününe kadar onlara azabın en kötüsünü yapacak kimseler gönderecektir!" diye ilan etmişti. Doğrusu, Rabbin çabuk ceza verendir ve O, çok bağışlayan, çok esirgeyendir.'),
(1122, 7, 168, 'Onları yeryüzünde topluluklara ayırdık. Onlardan kimi iyi kişilerdi, kimi de alçak! Belki dönerler diye onları iyiliklerle de, kötülüklerle de sınadık.'),
(1123, 7, 169, 'Onların ardından, yerlerine geçip Kitaba varis olan birtakım insanlar geldi ki, onlar, şu alçak(dünyan)ın menfaatini alıyorlar: "Biz nasıl olsa bağışlanacağız!" diyorlar. Kendilerine, ona benzer bir menfaat daha gelse onu da alırlar. Peki "Allah hakkında, gerçekten başkasını, söylememeleri hususunda kendilerinden Kitap misakı alınmamış mıydı? Ve onun içindekini okuyup öğrenmediler mi? Âhiret yurdu, korunanlar için daha hayırlıdır. Düşünmüyor musunuz?'),
(1124, 7, 170, 'O(koruna)nlar ki Kitaba sımsıkı sarılırlar ve namazı kılarlar; elbette biz, iyiliğe çalışanların ecrini zayi etmeyiz.'),
(1125, 7, 171, 'Bir zaman da üzerlerine dağı, bir gölge gibi kaldırmıştık, üstlerine düşecek sanmışlardı: "Size verdiğim(Kitap)ı kuvvetle tutun ve içinde olanı hatırlay(ıp yap)ın ki (azabımızdan) korunasınız!" (demiştik).'),
(1126, 7, 172, 'Rabbin, Adem oğullarından, onların bellerinden zürriyetlerini almış ve: "Ben sizin Rabbiniz değil miyim?" diye onları kendilerine şahid tutmuştu. "Evet, (buna) şahidiz!" dediler. kıyamet günü "Biz bundan habersizdik!" demeyesiniz.'),
(1127, 7, 173, 'Yahut: "(Ne yapalım) daha önce babalarımız (Allah\'a) ortak koştu, biz de onlardan sonra gelen bir nesil old(uğumuz için öyle yapt)ık. (Gerçekleri) iptal edenlerin yaptıkları yüzünden bizi helak mı ediyorsun?" demeyesiniz diye (sizin Rabbiniz olduğum hakkında sizleri şahid tutmuştuk).'),
(1128, 7, 174, 'İşte biz, ayetleri böyle açıklıyoruz, artık herhalde döner(yola gelir)ler.'),
(1129, 7, 175, 'Onlara şu adamın haberini de oku: Kendisine ayetlerimizi verdik de onlardan sıyrıldı, çıktı, şeytan onu peşine taktı, böylece azgınlardan oldu.'),
(1130, 7, 176, 'Dileseydik elbette onu o ayetlerle yükseltirdik, fakat o, yere saplandı ve hevesinin peşine düştü. Onun durumu, tıpkı şu köpeğin durumuna benzer: Üstüne varsan da dilini sarkıtıp solur, onu bıraksan da dilini sarkıtıp solur. İşte ayetlerimizi yalanlayanların durumu budur. Bu kıssayı anlat, belki düşünür(öğüt alır)lar.'),
(1131, 7, 177, 'Ayetlerimizi yalanlayan ve kendilerine de zulmeden topluluğun durumu ne kötüdür!'),
(1132, 7, 178, 'Allah kime yol gösterirse, işte yolu bulan odur. Kimi de saptırırsa, işte ziyana uğrayanlar onlardır.'),
(1133, 7, 179, 'Andolsun, cehennem için de birçok cin ve insan yarattık ki kalbleri var, fakat onlarla anlamazlar; gözleri var, fakat onlarla görmezler; kulakları var, fakat onlarla işitmezler. İşte onlar hayvanlar gibidir, hatta daha da sapık... Ve işte gafiller onlardır!'),
(1134, 7, 180, 'En güzel isimler Allah\'ındır. O halde O\'na o (güzel isim)lerle du\'a edin ve O\'nun isimleri hakkında eğriliğe sapanları bırakın; onlar yaptıklarının cezasını çekeceklerdir.'),
(1135, 7, 181, 'Yarattıklarımız içinde, doğrulukla hakka götüren ve hak ile adalet yapan bir ümmet de vardır.'),
(1136, 7, 182, 'Ayetlerimizi yalanlayanları, hiç bilmeyecekleri yerden yavaş yavaş helake yaklaştıracağız.'),
(1137, 7, 183, 'Onlara mühlet veriyorum, çünkü benim tuzağım sağlamdır.'),
(1138, 7, 184, 'Düşünmediler mi ki arkadaşlarında hiçbir delilik yoktur, o apaçık bir uyarıcıdır?'),
(1139, 7, 185, 'Göklerin, yerin melekutuna ve Allah\'ın yarattığı şeylere ve ecellerinin yaklaşmış olabileceğine bak(ıp ibret al)madılar mı? Peki bun(a inanmadık)dan sonra hangi söze inanacaklar?'),
(1140, 7, 186, 'Allah kimi saptırırsa, artık onun için yol gösteren olmaz. Ve bırakır onları, azgınlıkları içinde bocalayıp dururlar.'),
(1141, 7, 187, 'Sana (Duruşma) sa\'at(in)den soruyorlar: Gelip çatması ne zaman diye. De ki: "Onun bilgisi, ancak Rabbimin yanındadır. Onu tam zamanında açığa çıkaracak olan, yalnız O\'dur. O, göklere de, yere de ağır gelmiştir. O size ansızın gelecektir." Sanki sen, onu biliyormuşsun gibi, sana soruyorlar. De ki: "Onun bilgisi, Allah\'ın yanındadır. Fakat insanların çoğu bilmezler."'),
(1142, 7, 188, 'De ki: "Ben kendime, Allah\'ın dilediğinden başka ne bir fayda, ne de bir zarar verme gücüne sahip değilim. Eğer gaybı bilseydim, elbete çok hayır (mal ve mülk) elde ederdim. Bana kötülük dokunmamış (beni cin çarpmamış)tır. Ben sadece inanan bir kavim için bir uyarıcı ve müjdeleyiciyim.'),
(1143, 7, 189, 'O\'dur ki sizi bir tek nefisten yarattı, gönlü ısınsın diye ondan eşini var eti; eşini sarıp örtünce (eşiyle birleşince) eşi, hafif bir yük yüklendi, onu gezdirdi. (Yükü) ağırlaşınca ikisi beraber Rableri Allah\'a du\'a ettiler: "Eğer bize iyi, güzel bir çocuk verirsen elbette şükredenlerden oluruz!" (dediler).'),
(1144, 7, 190, 'Fakat (Allah) onlara iyi, güzel bir çocuk verince, kendilerine verdiği şeyde Allah\'a ortaklar koşmağa başladılar. Allah ise onların ortak koştukları şeylerden yücedir.'),
(1145, 7, 191, 'Hiçbir şey yaratmayan, kendileri yaratılan şeyleri (Allah\'a) ortak mı koşuyorlar?'),
(1146, 7, 192, '(O putlar), ne onlara bir yardım edebilirler, ne de kendilerine yardım ederler?'),
(1147, 7, 193, 'Onları doğru yola çağırsanız size uymazlar. Ha onları çağırmışsınız, ha susmuşsunuz, sizin için birdir.'),
(1148, 7, 194, 'Allah\'tan başka yalvardıklarınız da sizler gibi kullardır, (onların tanrı olduğu hakkındaki iddianızda) doğru iseniz, çağırın onları da size cevap versinler.'),
(1149, 7, 195, 'Onların yürüyecekleri ayakları mı var, yoksa tutacakları elleri mi var, yoksa görecekleri gözleri mi var, yahut işitecekleri kulaklarımı var? De ki: "(Allah\'a) ortak(koştuk)larınızı çağırın, sonra bana tuzak kurun, haydi (elinizden geliyorsa) hiç göz açtırmayın bana!"'),
(1150, 7, 196, 'Benim velim, Kitabı indiren Allah\'tır. O, iyileri yönetir (korur).'),
(1151, 7, 197, 'O\'ndan başka yalvardıklarınız ise, ne size yardım edebilirler, ne de kendilerine yardım ederler.'),
(1152, 7, 198, 'Onları hidayete çağırırsanız, işitmezler. Onların sana baktıklarını sanırsın, oysa onlar görmezler.'),
(1153, 7, 199, 'Affı al, iyiliği emret, cahillere aldırış etme.'),
(1154, 7, 200, 'Ne zaman şeytandan bir kötü düşünce seni dürtüklerse, Allah\'a sığın; çünkü O, işitendir, bilendir.'),
(1155, 7, 201, 'Allah\'tan korkanlar, kendilerine şeytandan gelen bir vesvese dokunduğu zaman düşünür, (gerçeği) görürler.'),
(1156, 7, 202, 'Kardeşleri ise onları, azgınlığa çekerler, hiç yakalarını bırakmazlar.'),
(1157, 7, 203, 'Onlara bir ayet getirmediğin zaman: "Bunu da derleseydin ya!" derler. De ki: "Ben, ancak Rabbimden bana vahyolunana uyuyorum. Bu (Kur\'an), Rabbinizden gelen basiretler(gönül gözlerini açan nurlar, gerçeğe ileten kanıtlar)dır ve inanan bir toplum için yol gösterici ve rahmettir!"'),
(1158, 7, 204, 'Kur\'an okunduğu zaman onu dinleyin ve susun ki, size rahmet edilsin.'),
(1159, 7, 205, 'Rabbini, içinden, yalvararak ve korkarak, yüksek olmayan bir sesle sabah akşam an, gafillerden olma!'),
(1160, 7, 206, 'Rabbinin yanında olanlar, büyüklük taslayıp O\'na kulluktan geri kalmazlar, (daima) O\'nu tesbih ederler ve O\'na secde ederler.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Sana ganimetlerden sorarlar; de ki: "Ganimetler, Allah\'ın ve Elçi(si)nindir. Siz, (gerçekten) inananlar iseniz, Allah\'tan korkun, aranızı düzeltin, Allah\'a ve Elçisine ita\'at edin!"'),
(1162, 8, 2, 'Mü\'minler o kimselerdir ki, Allah anıldığı zaman yürekleri ürperir, O\'nun ayetleri kendilerine okunduğu zaman imanlarını artırır ve Rablerine tevekkül ederler.'),
(1163, 8, 3, 'Namazlarını kılarlar ve kendilerine verdiğimiz rızıktan (Allah için) harcarlar.'),
(1164, 8, 4, 'İşte gerçek mü\'minler onlardır. Onlara Rablerinin katında dereceler, bağışlanma ve tükenmez rızık var.'),
(1165, 8, 5, '(Ganimetlerin bölüştürülmesinde bazı kimselerin hoşnutsuzluk göstermesi, daha önce, Bedir Savaşı\'na çıkmanı hoş görmeyenlerin durumuna benzer). Nitekim hak uğruna (savaşa gitmek için) Rabbin seni, evinden çıkardığı zaman, mü\'minlerden birtakımı, bundan hoşlanmıyordu.'),
(1166, 8, 6, 'Hak ortaya çıkmış iken sanki gözleri göre göre ölüme sürülüyorlarmış gibi seninle tartışıyorlardı.'),
(1167, 8, 7, 'Allah size, iki topluluktan birinin sizin olduğunu va\'dediyordu; siz de kuvvetsiz olanın sizin olmasını istiyordunuz. Oysa Allah, sözleriyle hakkı gerçekleştirmek ve (kuvvetli olan takımı yok ederek) kafirlerin ardını kesmek istiyordu.'),
(1168, 8, 8, 'Ki suçlular istemese de hakkı gerçekleştirsin, batılı da ortadan kaldırsın.'),
(1169, 8, 9, 'Siz Rabbinizden yardım istiyordunuz, O da: "Ben size birbiri ardınca bin melek ile yardım edeceğim," diye duanızı kabul buyurmuştu.'),
(1170, 8, 10, 'Allah bunu ancak müjde olsun (sevinesiniz) ve kalbiniz bununla yatışsın (güvene ve huzura kavuşsun) diye yapmıştı. Yardım, yalnız Allah katındandır. Allah daima üstün, hüküm ve hikmet sahibidir.'),
(1171, 8, 11, 'O zaman sizi, Allah\'tan bir güven olmak üzere hafif bir uyku bürüyordu, üzerinize sizi temizlemek, şeytanın pisliğini (içinize attığı kötü düşünceleri) sizden gidermek, kalblerinizi (birbirine) bağlamak ve ayakları(nızı) pekiştirmek için üzerinize gökten bir su indiriyordu.'),
(1172, 8, 12, 'Rabbin meleklere vahyediyordu ki: "Ben sizinle beraberim, siz inananları pekiştirin; ben inkar edenlerin yüreklerine korku salacağım; vurun (onların) boyunların(ın) üstüne, vurun onların her parmağına!"'),
(1173, 8, 13, 'Böyle (olacak), çünkü, onlar Allah\'a ve Elçisine karşı geldiler. Kim Allah\'a ve Elçisine karşı gelirse muhakkak ki, Allah\'ın cezası çetin olur.'),
(1174, 8, 14, 'İşte siz şimdi tadın onu; (ayrıca) kafirler için ateş azabı da vardır!'),
(1175, 8, 15, 'Ey inananlar, inkar edenlerle toplu halde karşılaşırsanız, onlara arkalar(ınız)ı döndür(üp kaç)mayın.'),
(1176, 8, 16, 'Kim o gün savaşmak için bir tarafa çekilmek, ya da başka bir birliğe katılmak dışında arkasını döner(kaçar)sa o, Allah\'tan bir gazaba uğrar, onun yeri cehennemdir, o ne kötü varılacak bir yerdir!'),
(1177, 8, 17, '(O gün) Onları siz öldürmediniz, fakat onları Allah öldürdü; (ey Muhammed), attığın zaman sen atmadın, fakat Allah attı. Mü\'minleri güzel bir imtihanla sınamak için (bunu yaptı). Doğrusu Allah işitendir, bilendir.'),
(1178, 8, 18, 'İşte size böyle yaptı. Çünkü Allah, kafirlerin tuzağını zayıflatır.'),
(1179, 8, 19, 'Eğer fetih istiyorsanız işte size fetih geldi. Eğer (eski yaptıklarınızdan) vazgeçerseniz, bu sizin için iyidir. Ama yine (eski yaptıklarınıza) dönerseniz, biz de döneriz (size yardım etmekten vazgeçeriz). O zaman topluluğunuz çok da olsa, size hiçbir yarar sağlayamaz. Allah, inananlarla beraberdir.'),
(1180, 8, 20, 'Ey inananlar, Allah\'a ve Elçisine ita\'at edin, işittiğiniz halde ondan dönmeyin.'),
(1181, 8, 21, 'İşitmedikleri halde "İşittik" diyenler gibi olmayın.'),
(1182, 8, 22, 'Allah katında canlıların en kötüsü, düşünmeyen sağırlar ve dilsizlerdir.'),
(1183, 8, 23, 'Allah onlarda bir iyilik olduğunu bilseydi, elbette onlara işittirirdi, onlara işittirseydi de yine aldırmayarak dönerlerdi.'),
(1184, 8, 24, 'Ey inananlar (elçi), sizi yaşatacak şeylere çağırdığı zaman Allah\'ın ve Elçisinin çağrısına koşun ve bilin ki, Allah, kişi ile onun kalbi arasına girer ve siz, O\'nun huzuruna toplanacaksınız.'),
(1185, 8, 25, '(Öyle) Bir fitneden sakının ki, aranızdan yalnız haksızlık edenlere erişmekle kalmaz (hepinize erişir). Bilinki Allah\'ın azabı çetindir.'),
(1186, 8, 26, 'Düşünün ki bir zaman siz az idiniz, yeryüzünde hırpalanıyordunuz. İnsanların sizi kapıp götürmesinden korkuyordunuz. Allah, sizi barındırdı, sizi yardımıyle destekledi, sizi güzel şeylerle besledi ki, şükredesiniz.'),
(1187, 8, 27, 'Ey inananlar, bile bile emanetlerinize hiyanet etmek suretiyle Allah\'a ve Elçisine hiyanet etmeyin.'),
(1188, 8, 28, 'Bilin ki mallarınız ve çocuklarınız birer fitne(sınav)dır. Allah\'a gelince büyük mükafat, o\'nun yanındadır.'),
(1189, 8, 29, 'Ey inananlar, Allah\'tan korkarsanız O size iyi ile kötüyü ayırdedici bir anlayış verir, kötülüklerinizi örter ve sizi bağışlar. Allah büyük lutuf sahibidir.'),
(1190, 8, 30, 'İnkar edenler seni tutup bağlamaları, öldürmeleri, ya da sürmeleri için sana tuzak kuruyorlardı. Onlar tuzak kurarlarken Allah da tuzak kuruyordu. Allah tuzak kuranların en iyisidir. (O, kendisine karşı tuzak kuranların tuzaklarını başlarına geçirir).'),
(1191, 8, 31, 'Onlara ayetlerimiz okunduğu zaman "İşittik" dediler, "İstesek, biz de bunun gibisini söyleriz. Bu, evvelkilerin masallarından başka bir şey değildir!"'),
(1192, 8, 32, 'Ve: "Allah\'ım, eğer bu, senin yanından gelmiş gerçekse başımıza gökten taş yağdır, yahut bize acı bir azab getir!" demişlerdi.'),
(1193, 8, 33, 'Oysa sen onların içinde bulundukça Allah, onlara azab edecek değildi ve onlar istiğfar ederlerken (içlerinde istiğfar edenler var iken) de Allah, onlara azab edecek değildi.'),
(1194, 8, 34, 'Onlar, (inananları) Mescid-i haramdan geri çevirdikleri ve onun velisi, (bakıcısı, koruyucusu) olmadıkları halde neden Allah onlara azabetmesin? Onun velileri, (bakıcıları, koruyucuları) sadece (günahlardan) korunanlardır. Fakat çokları bilmezler.'),
(1195, 8, 35, 'Onların Beyt(ullah) yanındaki namazları da, ıslık çalmadan ve el çırpmadan ibarettir. "O halde inkarınızdan dolayı azabı tadın!"'),
(1196, 8, 36, 'İnkar edenler, Allah yoluna engel olmak için mallarını harcarlar ve harcayacaklar da. Sonra bu, kendilerine dert olacak, nihayet yenilecekler ve inkar edenler cehenneme sürüleceklerdir.'),
(1197, 8, 37, '(Sürüleceklerdir) Ki, Allah, murdarı temizden ayıklasın ve bütün murdarları birbiri üzerine koyup yığsın da hepsini cehenneme atsın. İşte ziyana uğrayanlar onlardır.'),
(1198, 8, 38, 'İnkar edenlere söyle: "Eğer vazgeçerlerse, geçmişteki (günahları) kendilerine bağışlanır; yok yine (eski hallerine) dönerlerse, öncekilerin (başlarına gelen Allah) kanunu geçmiştir (bunların da başına gelecektir. Onu beklesinler).'),
(1199, 8, 39, 'Fitne (baskı) kalmayıncaya ve din tamamen Allah\'ın oluncaya kadar onlarla savaşın! Eğer (baskıya) son verirlerse muhakkak ki Allah, ne yaptıklarını görmektedir.'),
(1200, 8, 40, 'Eğer dönerlerse, bilin ki Allah sizin sahibinizdir. O, ne güzel sahip, ne güzel yardımcıdır!'),
(1201, 8, 41, 'Eğer Allah\'a ve (hak ile batılın) ayrılma gününde, o iki topluluğun karşılaştığı (Bedir) gün(ün)de kulumuz(Muhammed)e indirdiğimiz(ayetler)e inanmışsanız bilin ki aldığınız ganimetlerin beşte biri, Allah\'a, Elçisine ve (Allah\'ın Elçisi ile) akrabalığı bulunan(lar)a, yetimlere, yoksullara ve yolcu(lar)a aittir. Allah her şeye kadirdir.'),
(1202, 8, 42, 'O gün siz, vadinin yakın kenarında idiniz, onlar da uzak kenarında idiler. Kervan da sizden daha aşağıda idi. Eğer sözleşmiş olsaydınız dahi, sözleştiğiniz vakitte öyle buluşamazdınız. Fakat Allah, yapılması gereken bir işi yerine getirmek için (sizi böyle buluşturdu) ki helak olan, açık delille helak olsun; yaşayan da açık delille yaşasın. Çünkü Allah, işitendir, bilendir.'),
(1203, 8, 43, 'Allah, sana onları uykunda az gösteriyordu. Eğer sana onları çok gösterseydi, çekinirdiniz ve (savaş) iş(in)de çekişirdiniz. Fakat Allah, (sizi bundan) kurtardı. Doğrusu O, göğüslerin özünü bilir.'),
(1204, 8, 44, 'Karşılaştığınız zaman onları sizin gözlerinize az gösteriyor, sizi de onların gözlerinde azaltıyordu ki yapılması gereken bir işi yerine getirsin. İşler, hep Allah\'a döndürülecektir.'),
(1205, 8, 45, 'Ey inananlar, bir toplulukla karşılaştığınız zaman sebat edin ve Allah\'ı çok anın ki, başarıya erişesiniz.'),
(1206, 8, 46, 'Allah\'a ve Elçisine ita\'at edin, birbirinizle çekişmeyin, yoksa korkuya kapılırsınız, devletiniz gider. Sabredin, çünkü Allah sabredenlerle beraberdir.'),
(1207, 8, 47, 'Yurtlarından çalım satarak, insanlara gösteriş yaparak çıkan ve Allah yolundan men\'edenler gibi olmayın. Allah, onların bütün yaptıklarını kuşatmıştır.'),
(1208, 8, 48, 'O zaman şeytan onlara yaptıkları işi süslemiş: "Bugün insanlardan sizi yenecek kimse yoktur, (korkmayın), ben sizin yanınızdayım!" demişti. Fakat iki topluluk birbirini görünce iki ökçesi üzerine (geriye) dönüp: "Ben sizden uzağım, ben sizin görmediğinizi görüyorum, ben Allah\'tan korkarım, zira Allah\'ın cezası çetindir!" demişti.'),
(1209, 8, 49, 'Münafıklar ve kalblerinde hastalık bulunanlar (sizin için): "Bunları dinleri aldatmış, (baksana başa çıkamayacakları bir kuvvetle savaşmağa kalkıyorlar)." diyorlardı. Oysa, kim Allah\'a dayanırsa şüphesiz Allah, daima galib, hüküm ve hikmet sahibidir.'),
(1210, 8, 50, 'Görseydin o inkar edenleri: Melekler, onların canlarını alırken yüzlerine ve kıçlarına vuruyorlar: "Haydi, yangın azabını tadın!" (diyorlardı).'),
(1211, 8, 51, 'İşte bu, ellerinizin yapıp öne sürdüğü işler yüzündendir. Yoksa Allah, kullara zulmedici değildir.'),
(1212, 8, 52, '(Bunlar da) tıpkı Fir\'avn ailesi ve onlardan öncekilerin gidişi gibi(davrandılar. Onlar da): Allah\'ın ayetlerini inkar etmişlerdi; Allah da onları, günahlarıyla yakalamıştı. Şüphesiz Allah güçlüdür, O\'nun cezası çetindir.'),
(1213, 8, 53, 'Bu böyledir, çünkü bir millet kendilerinde bulunan(güzel meziyet)i değiştirmedikçe Allah onlara verdiği ni\'meti değiştirmez. Allah işitendir, bilendir.'),
(1214, 8, 54, '(Evet) Fir\'avn ailesi ve onlardan öncekilerin gidişi gibi: Rablerinin ayetlerini yalanlamışlardı; biz de onları günahlarıyle mahvetmiştik ve Fir\'avn ailesini boğmuştuk. Hepsi de zulmedicilerdi.'),
(1215, 8, 55, 'Allah\'a göre canlıların en kötüsü, kafirlerdir; artık onlar inanmazlar.'),
(1216, 8, 56, 'Sen kendileriyle andlaşma yaptığın halde onlar, hiç çekinmeden, her defa andlaşmalarını bozarlar.'),
(1217, 8, 57, 'Savaşta onları yakalarsan, onlar(a vereceğin ceza) ile arkalarında bulunan kimseleri de dağıt ki ibret alsınlar.'),
(1218, 8, 58, 'Bir kavmin, (andlaşmaya) hiyanet etmesinden korkarsan, sen de (onlarla yaptığın andlaşmayı) aynı şekilde onlara at; çünkü Allah; hainleri sevmez.'),
(1219, 8, 59, 'İnkar edenler (bizim elimizden kurtulup) geçtiklerini sanmasınlar. Onlar (bizi) aciz bırakamazlar.'),
(1220, 8, 60, 'Onlara karşı gücünüz yettiği kadar kuvvet ve cihad için bağlanıp beslenen atlar hazırlayın. Bununla Allah\'ın düşmanını, sizin düşmanınızı ve onlardan başka sizin bilmediğiniz, Allah\'ın bildiği (düşman) kimseleri korkutursunuz. Allah yolunda ne harcarsanız tam olarak size ödenir, hiç haksızlığa uğratılmazsınız.'),
(1221, 8, 61, 'Eğer onlar barışa yanaşırlarsa sen de ona yanaş ve Allah\'a dayan, çünkü O, işitendir, bilendir.'),
(1222, 8, 62, 'Eğer sana hile yapmak isterlerse (korkma) Allah sana yeter. O ki, yardımıyle seni ve mü\'minleri destekledi.'),
(1223, 8, 63, 'Ve onların kalblerinin arasını uzlaştırdı. Sen yeryüzünde bulunan herşeyi verseydin, yine onların kalblerinin arasını uzlaştıramazdın; fakat Allah, onların arasını uzlaştırdı. Çünkü O, daima üstündür, hüküm ve hikmet sahibidir.'),
(1224, 8, 64, 'Ey peygamber, Allah sana ve sana tabi olan mü\'minlere yeter.'),
(1225, 8, 65, 'Ey peygamber, mü\'minleri savaşa teşvik et. Eğer sizden sabreden yirmi kişi olursa, iki yüz(kafir)i yenerler. Sizden yüz kişi olursa, kafirlerden bin kişiyi yenerler. Çünkü kafirler, anlamaz bir topluluktur.'),
(1226, 8, 66, 'Şimdi Allah sizden hafifletti, sizde zayıflık bulunduğunu bildi. Bundan böyle sizden sabreden yüz kişi olsa, iki yüz(kafir)i yenerler. Ve eğer sizden bin kişi olsa Allah\'ın izniyle iki bin(kafir)i yenerler. Allah, sabredenlerle beraberdir.'),
(1227, 8, 67, 'Yeryüzünde ağır bas(ıp küfrün belini iyice kır)ıncaya kadar hiçbir peygambere esirler sahibi olmak yakışmaz. Siz, geçici dünya malını istiyorsunuz, Allah ise (sizin için) ahireti istiyor. Allah daima üstün, hüküm ve hikmet sahibidir.'),
(1228, 8, 68, 'Eğer Allah\'tan, (yanılma ile verilen hükümlerden ötürü azabetmemek hakkında) bir yazı geçmemiş olsaydı, aldığınız fidyeden dolayı size mutlaka büyük bir azab dokunurdu.'),
(1229, 8, 69, 'Artık aldığınız ganimetten helal ve temiz olarak yeyin ve Allah\'tan korkun. Şüphesiz Allah, bağışlayandır, esirgeyendir.'),
(1230, 8, 70, 'Ey peygamber, ellerinizde bulunan esirlere söyle: "Eğer Allah, sizin kalblerinizde bir hayır olduğunu bilirse, size, sizden alınan(fidye)den daha hayırlısını verir ve sizi bağışlar. Allah bağışlayandır, esirgeyendir.'),
(1231, 8, 71, 'Eğer sana hainlik yapmak isterlerse, daha önce Allah\'a da hainlik yapmışlardı. Bu yüzden (Allah) onlara karşı (sana) imkan verdi. Allah (herşeyi) bilendir, yerli yerince yapandır.'),
(1232, 8, 72, 'Onlar ki inandılar, hicret ettiler, Allah yolunda mallariyle, canlariyle savaştılar ve onlar ki (yurtlarına göçenleri) barındırdılar ve yardım ettiler; işte onlar, birbirlerinin velisi(dostu, koruyucusu)durlar. İnanıp da hicret etmeyenlere gelince, onlar hicret edinceye kadar, onların velayetinden size bir şey yoktur (onları korumakla yükümlü değilsiniz). Fakat dinde yardım isterlerse (onlara) yardım etmeniz gerekir. Yalnız, aranızda andlaşma bulunan bir topluma karşı (yardım etmeniz) olmaz. Allah, yaptıklarınızı görmektedir.'),
(1233, 8, 73, 'İnkar edenler, birbirlerinin velisidirler. Eğer bunu yapmazsanız (mü\'minleri bırakıp kafirleri dost tutarsanız), yeryüzünde fitne ve büyük bir kargaşa olur.'),
(1234, 8, 74, 'Onlar ki, inandılar, hicret ettiler, Allah yolunda savaştılar ve onlar ki, (göçmenleri) barındırdılar ve (onlara) yardım ettiler, işte gerçek mü\'minler onlardır. Onlar için bağış ve bol rızık vardır.'),
(1235, 8, 75, 'Onlar ki sonradan inandılar, hicret ettiler, sizinle beraber savaştılar, işte onlar da sizdendir. Rahim sahipleri (kan akrabası), Allah\'ın Kitabına göre birbirlerine daha yakın dostturlar. Allah herşeyi bilir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Allah ve Elçisinden, andlaşma yaptığınız müşriklere ihtardır.'),
(1237, 9, 2, 'Dört ay daha yeryüzünde dolaşın, bilin ki siz, Allah\'ı aciz bırakamazsınız ve Allah, kafirleri rezil, perişan edecektir!'),
(1238, 9, 3, 'En büyük Hac günü, Allah ve Elçisinden insanlara duyurudur: Allah ve Elçisi puta tapanlardan uzaktır. Eğer tevbe ederseniz, bu sizin için daha iyidir. Ve eğer dönerseniz bilin ki siz Allah\'ı aciz bırakacak değilsiniz! (Ey Muhammed) kafirlere acı bir azabı müjdele.'),
(1239, 9, 4, 'Ancak andlaşma yaptığınız müşriklerden, (andlaşma şartlarından) hiçbir şeyi size eksik bırakmayan ve size karşı hiç kimseye arka çıkmayanların andlaşmalarını, kendilerine tanıdığınız süreye kadar tamamlayın. Çünkü Allah korunanları sever.'),
(1240, 9, 5, 'Haram aylar çıkınca (Allah\'a) ortak koşanları nerede bulursanız öldürün; onları yakalayın, hapsedin ve her gözetleme yerinde otur(up) onları bekleyin. Eğer tevbe ederler, namazı kılarlar, zekatı verirlerse yollarını serbest bırakın. Çünkü Allah bağışlayandır, esirgeyendir.'),
(1241, 9, 6, 'Ve eğer ortak koşanlardan biri güvence dileyip yanına gelmek isterse, onu yanına al ki, Allah\'ın sözünü işitsin; sonra onu güven içinde bulunacağı yere ulaştır. Böyle (yap), çünkü onlar, bilmez bir topluluktur.'),
(1242, 9, 7, 'Ortak koşanların, Allah\'ın yanında ve Elçisinin yanında nasıl andlaşması olabilir? Ancak Mescid-i haram\'da andlaştklarınız hariç. Onlar size dürüst davrandıkça siz de onlara dürüst davranın, çünkü Allah, korunanları sever.'),
(1243, 9, 8, 'Evet (Allah ve Elçisi yanında onların) nasıl (ahdi olabilir)? Eğer onlar size galib gelselerdi, sizin hakkınızda ne and ne de andlaşma gözetmezlerdi. Ağızlarıyla sizi razı ederler, fakat kalbleri (sizi) istemez. Çokları da yoldan çıkmışlardır.'),
(1244, 9, 9, 'Allah\'ın ayetlerini az bir paraya sattılar da O\'nun yoluna engel oldular. Onların yaptıkları, gerçekten ne kötüdür!'),
(1245, 9, 10, 'Bir mü\'mine karşı ne and, ne de andlaşma gözetmezler. İşte saldırganlar onlardır.'),
(1246, 9, 11, 'Eğer tevbe ederler, namazı kılarlar ve zekatı verirlerse, dinde sizin kardeşlerinizdirler. Biz, bilen bir kavme ayetleri böyle uzun uzun açıklıyoruz.'),
(1247, 9, 12, 'Eğer andlaşma yaptıktan sonra andlarını bozarlar ve dininize dil uzatırlarsa, o küfür önderleriyle savaşın. Çünkü onların andları yoktur; belki (böylece küfürden) vazgeçerler.'),
(1248, 9, 13, 'Andlarını bozan, Elçiyi (Mekke\'den) çıkarmağa yeltenen ve ilk önce kendileri siz(inle savaş)a başlamış olan bir kavimle savaşmayacak mısınız? Yoksa onlardan korkuyor musunuz? Eğer gerçekten inanan insanlar iseniz, kendisinden korkmanıza en layık olan Allah\'tır.'),
(1249, 9, 14, 'Onlarla savaşın ki Allah, sizin ellerinizle onlara azabetsin, onları rezil etsin, sizi onlara üstün getirsin ve inananlar toplumunun göğüslerine şifa versin;'),
(1250, 9, 15, 'Yüreklerinin öfkesini gidersin. Allah, dilediğinin tevbesini kabul eder. Allah bilendir, hüküm ve hikmet sahibidir.'),
(1251, 9, 16, 'Yoksa siz, Allah içinizden cihadeden ve Allah\'tan, Elçisinden ve mü\'minlerden başkasını kendisine sırdaş edinmeyenleri bilmeden, bırakılacağınızı mı sandınız? Allah yaptıklarınızı haber almaktadır.'),
(1252, 9, 17, '(Allah\'a) Ortak koşanlar nefislerinin küfrünü göre göre Allah\'ın mescidlerini şenlendiremezler. Onların yaptıkları işler, boşa çıkmıştır. Ve onlar, ateşte sürekli kalacaklardır.'),
(1253, 9, 18, 'Allah\'ın mescidlerini, ancak Allah\'a ve ahiret gününe inanan, namazı kılan, zekatı veren ve Allah\'tan başka kimseden korkmayanlar şenlendirirler. Onların, doğru yolu bulanlardan olacakları umulur.'),
(1254, 9, 19, '(Ey müşrikler siz), hacılara su verme ve Mescid-i haram\'ı şenlendirmeyi; Allah\'a, ahiret gününe inanan ve Allah yolunda cihadeden(in eylemiy)le bir mi tuttunuz? Bunlar, Allah katında bir olmazlar. Allah, zalimler topluluğuna yol göstermez.'),
(1255, 9, 20, 'İnanan, hicret eden ve Allah yolunda mallarıyle, canlarıyle savaşanların, Allah katında dereceleri daha büyüktür. İşte kurtuluşa erenler onlardır.'),
(1256, 9, 21, 'Rableri onlara, kendisinden bir rahmet, rıza ve içinde sürekli kalacakları ni\'meti bol cennetleri müjdeler.'),
(1257, 9, 22, 'Orada ebedi kalacaklardır. Şüphesiz büyük mükafat Allah katındandır!'),
(1258, 9, 23, 'Ey inananlar, eğer imana karşı küfrü seviyorlarsa babalarınızı ve kardeşlerinizi veliler edinmeyin. Sizden kim onları veli tanır(dost tutar)sa işte zalimler onlardır.'),
(1259, 9, 24, 'De ki: "Eğer babalarınız, oğullarınız, kardeşleriniz, eşleriniz, hısım akrabanız, kazandığınız mallar, düşmesinden korktuğunuz ticaret(iniz), hoşlandığınız konutlar, size Allah\'tan, Elçisinden ve O\'nun yolunda cihadetmekten daha sevgili ise o halde Allah emrini getirinceye kadar gözetleyin (başınıza gelecekleri göreceksiniz)! Allah, yoldan çıkmış topluluğu (doğru) yola iletmez.'),
(1260, 9, 25, 'Andolsun Allah size birçok yerlerde, Huneyn gününde de yardım etmişti. Hani (o gün) çokluğunuz sizi böbürlendirmişti. Fakat size hiçbir yarar da sağlamamıştı. Bütün genişliğine rağmen yeryüzü başınıza dar gelmişti, nihayet bozularak arkanızı dönmüş(kaçmağa başlamış)tınız.'),
(1261, 9, 26, 'Sonra Allah, Elçisinin ve mü\'minlerin üzerine sekinetini (güven veren rahmetini) indirdi, sizin görmediğiniz askerler indirdi ve kafirlere azab etti (onları bozguna uğrattı). İşte kafirlerin cezası budur!'),
(1262, 9, 27, 'Sonra Allah, bunun ardından yine dilediğinin tevbesini kabul eder. Allah bağışlayandır, esirgeyendir.'),
(1263, 9, 28, 'Ey inananlar, (Allah\'a) ortak koşanlar pisliktir, artık bu yıllarından sonra Mescid-i Haram\'a yaklaşmasınlar. Eğer (onların hacca gelmemeleri sonucu ekonominiz bozulup) yoksulluğa düşmekten korkarsanız; biliniz ki Allah dilerse yakında sizi kendi lutfundan zengin edecektir. Şüphesiz Allah, bilendir, hikmet sahibidir.'),
(1264, 9, 29, 'Kendilerine Kitap verilenlerden Allah\'a ve ahiret gününe inanmayan, Allah\'ın ve Elçisinin haram kıldığını haram saymayan ve gerçek dini din edinmeyen kimselerle, küçül(üp boyun eğ)erek elleriyle cizye verecekleri zamana kadar savaşın.'),
(1265, 9, 30, 'Yahudiler: "Uzeyr, Allah\'ın oğludur." dediler. Hıristiyanlar da: "Mesih Allah\'ın oğludur." dediler. Bu, onların ağızlariyle geveledikleri sözleridir. (Sözlerini), önceden inkar etmiş(olan müşrik)lerin sözlerine benzetiyorlar. Allah onları kahretsin, nasıl da (haktan batıla) çevriliyorlar!?'),
(1266, 9, 31, 'Hahamlarını ve rahiplerini Allah\'tan ayrı rabler edindiler, Meryem oğlu Mesih\'i de öyle. Oysa kendilerine yalnız tek Tanrı olan Allah\'a ibadet etmeleri emredilmişti. O\'ndan başka tanrıı yoktur. O, onların ortak koştukları şeylerden münezzehtir.'),
(1267, 9, 32, 'Allah\'ın nurunu ağızlariyle söndürmek istiyorlar. Halbuki, kafirler hoşlanmasa da Allah, mutlaka nurunu tamamlamak ister, (bundan başka bir şeye razı olmaz).'),
(1268, 9, 33, 'O, Elçisini hidayetle ve hak dinle gönderdi ki (Allah\'a) ortak koşanlar hoşlanmasa da o (hak di)ni, bütün din(ler)in üstüne çıkarsın.'),
(1269, 9, 34, 'Ey inananlar, hahamlardan ve rahiplerden birçoğu, insanların mallarını haksızlıkla yerler ve (insanları) Allah yolundan çevirirler. Altın ve gümüşü yığıp da onları Allah yolunda harcamayanlar var ya, işte onlara acı bir azabı müjdele!'),
(1270, 9, 35, 'O gün cehennem ateşinde bunların üzeri ısıtılı(p pullanı)r; bunlarla, onların alınları, yanları ve sırtları dağlanır: "İşte nefisleriniz için yığdıklarınız, yığdıklarınızı tadın!" (denilir).'),
(1271, 9, 36, 'Gökleri ve yeri yarattığı gündeki yazısına göre Allah\'ın katında ayların sayısı on ikidir. Bunlardan dördü haram(ay)lardır. İşte doğru din budur. O aylar içinde (konulmuş yasağı çiğneyerek) kendinize zulmetmeyin ve (Allah\'a) ortak koşanlar nasıl sizinle topyekun savaşıyorlarsa, siz de onlarla topyekun savaşın ve bilin ki Allah korunanlarla beraberdir.'),
(1272, 9, 37, '(Haram ayını) Ertelemek, küfürde daha ileri gitmektir. İnkar edenler, onunla saptırılır. O(haram ayı)nı bir yıl helal sayarlar, bir yıl haram sayarlar ki, Allah\'ın haram kıldığının sayısını denk getirip, Allah\'ın haram kıldığını helal yapsınlar. Yaptıkları işin kötülüğü, kendilerine süslü gösterildi. Allah, kafirler toplumuna yol göstermez.'),
(1273, 9, 38, 'Ey inananlar, size ne oldu ki: "Allah yolunda topluca savaşa çıkın!" dendiği zaman yere çakılıp kaldınız? ahirettense dünya hayatına mı razı oldunuz? Ama dünya hayatının geçimi, ahiretin yanında pek azdır.'),
(1274, 9, 39, 'Eğer topluca (savaşa) çıkmazsanız, (Allah) size acı (bir şekilde) azabeder ve yerinize sizden başka bir topluluk getirir, O\'na hiçbir zarar veremezsiniz, Allah herşeyi yapabilendir.'),
(1275, 9, 40, 'Eğer siz o(Hak elçisi)ne yardım etmezseniz, iyi bilin ki, Allah ona yardım etmişti: Hani yalnız iki kişiden biri olduğu halde, inkar edenler kendisini (Mekke\'den) çıkardıkları sırada ikisi mağarada iken arkadaşına "Üzülme, Allah bizimle beraberdir!"diyordu. (İşte o zaman) Allah (ona yardım etti) onun üzerine sekine(huzur ve güven duygu)sunu indirdi ve onu, sizin görmediğiniz askerlerle destekledi; inanmayanların sözünü alçattı. Yüce olan, yalnız Allah\'ın sözüdür. Allah daima üstündür, hüküm ve hikmet sahibidir.'),
(1276, 9, 41, 'Gerek hafif, gerek ağır olarak savaşa çıkın, mallarınızla ve canlarınızla Allah yolunda cihadedin. Eğer bilirseniz bu, sizin için daha hayırlıdır.'),
(1277, 9, 42, 'Yakın bir dünya menfaati ve orta bir yolculuk olsaydı (savaşa katılmayan o münafıklar), elbette sana tabi olurlardı. Fakat güç aşılacak mesafe, kendilerine uzak geldi. Bir de "Gücümüz yetseydi, sizinle beraber çıkardık!"diye Allah\'a yemin edecekler. Boşuna kendilerini mahvediyorlar. Allah, onların yalancı olduklarını biliyor.'),
(1278, 9, 43, 'Allah seni affetsin; doğru söyleyenler sana iyice belli olup, yalan söyleyenleri bilmezden önce niçin onlara izin verdin?'),
(1279, 9, 44, 'Allah\'a ve ahiret gününe inananlar; mallariyle, canlariyle, cihadetmek(ten geri kalmaları) için senden izin istemezler. Allah, korunanları bilir.'),
(1280, 9, 45, 'Ancak Allah\'a ve ahiret gününe inanmayan, kalbleri kuşkuya düşmüş ve şüpheleri içinde bocalayıp duranlar, (geri kalmak için) senden izin isterler.'),
(1281, 9, 46, 'Eğer (cihada) çıkmak isteselerdi, onun için bir hazırlık yaparladı. Fakat Allah, onların davranışlarından hoşlanmadığı için onları durdurdu: "Oturan(kadın ve çocuk)larla beraber oturun!" denildi.'),
(1282, 9, 47, 'Sizin içinizde (sefere) çıkmış olsalardı, size bozgunculuktan başka bir katkıları olmazdı. Sizi birbirinize düşürmek için hemen aranıza sokulurlardı, içinizde de onlara kulak verenler vardı. Allah zalimleri bilir.'),
(1283, 9, 48, '(Onlar) önceden de fitne çıkarmak istediler ve sana nice işleri ters çevirdiler. Nihayet hak geldi, onlar istemedikleri halde Allah\'ın emri galebe çaldı.'),
(1284, 9, 49, 'İçlerinden öylesi var ki: "Bana izin ver, beni fitneye düşürme" der. İyi bilinki, onlar zaten fitneye düşmüşlerdir. Cehennem de kafirleri kuşatacaktır.'),
(1285, 9, 50, 'Sana bir iyilik ulaşsa (bu,) onların hoşuna gitmez ve eğer sana bir kötülük ulaşsa: "Biz önceden (sefere katılmamakla) başımızın çaresine bakmışız" derler, sevinerek döner(gider)ler.'),
(1286, 9, 51, 'De ki: "Allah, bizim için ne yazmış (ne takdir etmiş) ise ancak o, bize ulaşır, bizim sahibimiz O\'dur. İnananlar Allah\'a dayansınlar."'),
(1287, 9, 52, 'De ki: "Bize yalnız iki iyilikten (ya gazilik veya şehidlikten) birini gözetmiyor musunuz? Ama biz, Allah\'ın size ya kendi tarafından veya bizim ellerimizle bir azab ulaştırmasını gözetiyoruz. Haydi gözetin, biz de sizinle beraber gözetenleriz."'),
(1288, 9, 53, 'De ki: "İster gönüllü, ister gönülsüz sadaka verin: sizden kabul edilmeyecektir. Çünkü siz yoldan çıkan bir kavimsiniz!"'),
(1289, 9, 54, 'Sadakalarının kabul edilmesine engel olan sadece şudur: Onlar Allah\'a ve elçisine karşı nankörlük ettiler; namaza da üşene üşene gelirler ve istemeye istemeye sadaka verirler.'),
(1290, 9, 55, 'Onların ne malları, ne de evladları seni imrendirmesin. Allah bunlarla onlara dünya hayatında azabetmeyi ve kafir olarak canlarının çıkmasını istiyor.'),
(1291, 9, 56, 'Sizden olduklarına Allah\'a yemin ediyorlar. Oysa onlar sizden değiller, fakat onlar korkak bir topluluktur.'),
(1292, 9, 57, 'Eğer (sizden korunmak için) sığınacak bir yer, yahut (barınacak) mağaralar, ya da sokulacak bir delik bulsalardı, hemen oraya doğru koşarlardı.'),
(1293, 9, 58, 'Onlardan kimi de sadakalar(ın bölüştürülmesi hususun)da sana dil uzatır. Eğer o sadakalardan kendilerine pay verilse hoşlanırlar, onlardan kendilerine pay verilmezse hemen kızarlar.'),
(1294, 9, 59, '(Ne olur) onlar, Allah\'ın ve Elçisinin kendilerine verdiğine razı olup: "Allah bize yeter, yakında Allah da bize bol lutfundan verecek, Elçisi de; biz sadece Allah\'a rağbet ederiz (yalnız O\'ndan umarız)." deselerdi!'),
(1295, 9, 60, 'Sadakalar, (zekatlar) Allah\'tan bir farz olarak ancak fakirlere, düşkünlere, onlar üzerinde çalışan (zekat toplayan) memurlara, kalbleri (İslam\'a) ısındırılacak olanlara, kölelik altında bulunanlara, borçlulara, Allah yoluna ve yolcuya mahsustur (toplanan zekat, ancak bu sayılanlara verilir). Allah bilendir, hüküm ve hikmet sahibidir.'),
(1296, 9, 61, 'İçlerinden bazıları da Peygamberi incitirler: "O, (her söyleneni dinleyen) bir kulaktır." derler. De ki: "(O), sizin için hayır kulağıdır. Allah\'a inanır, mü\'minlere inanır. Sizden inananlar için de (O), bir rahmettir, Allah\'ın Elçisini incitenlere acı bir azab vardır."'),
(1297, 9, 62, 'Gönlünüzü hoş etmek için size (gelip) Allah\'a yemin ederler. Halbuki inanmış olsalardı, Allah\'ı ve Resulünü hoşnud etmeleri daha uygundu.'),
(1298, 9, 63, 'Bilmediler mi ki kim Allah\'a ve Elçisine karşı koymağa kalkarsa onun için sürekli kalacağı cehennem ateşi vardır. İşte, büyük rezillik budur.'),
(1299, 9, 64, 'Münafıklar, kendileri hakkında, kalblerinde bulunanı kendilerine haber verecek bir surenin indirileceğinden çekiniyorlar. De ki; "Siz alay edin, Allah çekindiğiniz şeyi ortaya çıkaracaktır.'),
(1300, 9, 65, 'Eğer onlara sorsan: "Biz sadece lafa dalmış, şakalaşıyorduk!" derler. De ki: "Allah ile, O\'nun ayetleriyle ve O\'nun Elçisi ile mi alay ediyordunuz?"'),
(1301, 9, 66, 'Hiç özür dilemeyin, siz inandıktan sonra inkar ettiniz. Sizden bir kısmını affetsek bile suç işlediklerinden dolayı bir kısmına da azab edeceğiz.'),
(1302, 9, 67, 'Münafık erkekler ve münafık kadınlar birbirlerindendir. Kötülüğü emrederler, iyilikten meneder ve ellerini sıkı tutarlar. Allah\'ı unuttular, O da onları unuttu. Münafıklar; işte yoldan çıkanlar onlardır.'),
(1303, 9, 68, 'Allah münafık erkeklere ve münafık kadınlara ve kafirlere içinde ebedi kalacakları cehennem ateşini va\'detmiştir. O, onlara yeter. Allah, onları la\'netlemiştir. Onlar için sürekli bir azab vardır.'),
(1304, 9, 69, '(Siz de), sizden öncekiler gibi (yaptınız). Onlar kuvvetçe sizden daha yaman, mal ve evladça sizden daha çok idiler. Onlar, (dünya malından) kendi paylarına düşenle zevklerine baktılar, sizden öncekilerin, (dünyadan) kendi paylarına düşenle zevklerine baktıkları gibi, siz de kendi payınıza düşenle zevkinize baktınız ve (batıla) dalanlar gibi siz de(batıla) daldınız. Onlar, eylemleri, dünya ve ahirette boşa gitmiş kimselerdir ve ziyana uğrayanlar da onlardır.'),
(1305, 9, 70, 'Onlara kendilerinden öncekilerin, Nuh, Ad, Semud kavminin, İbrahim kavminin, Medyen halkının ve başları üstüne ters dönen şehirlerin haberi gelmedi mi? Elçileri, onlara açık deliller getirmişti (Ama inanmadılar, bundan dolayı Allah\'ın gazabına uğradılar). Allah onlara zulmediyor değildi, onlar kendi kendilerine zulmediyorlardı.'),
(1306, 9, 71, 'İnanan erkekler ve inanan kadınlar, birbirlerinin velisidirler. İyiliği emrederler, kötülükten men\'ederler, namazı kılarlar, zekatı verirler, Allah\'a ve Elçisine ita\'at ederler. İşte onlara Alah rahmet edecektir. Allah daima üstündür, hüküm ve hikmetsahibidir.'),
(1307, 9, 72, 'Allah inanan erkeklere ve inanan kadınlara, altlarından ırmaklar akan, içinde ebedi kalacakları cennetler ve Adn cennetlerinde güzel meskenler va\'detmiştir. Allah\'ın (onlardan) razı olması ise hepsinden büyüktür. İşte büyük başarı budur.'),
(1308, 9, 73, 'Ey peygamber, kafirlerle ve münafıklarla cihadet, onlara sert davran; onların varacakları yer cehennemdir. Ne kötü bir gidiş yeridir o!'),
(1309, 9, 74, '(Senin aleyhinde söyledikleri yakışıksız sözleri) söylemediklerine Allah\'a yemin ediyorlar. Halbuki o küfür sözünü söylediler, İslam olduktan sonra inkar ettiler, başaramadıkları bir şeye yeltendiler. Sırf Allah ve Elçisi, Allah\'ın lutfiyle kendilerini zengin etti diye (şimdi) öc almağa kalktılar. (Allah ve Elçisinin iyiliğine karşı böyle nankörlük ettiler.) Eğer tevbe ederlerse kendileri için daha iyi olur. Yok eğer (inkar yoluna) dönerlerse Allah onlara dünyada da, ahirette de acı bir biçimde azabedecektir. Yeryüzünde onların ne velisi, ne de yardımcısı vardır.'),
(1310, 9, 75, 'Kimileri de: "Eğer Allah, lutfundan bize verirse elbette sadaka vereceğiz ve yararlı insanlardan olacağız!" diye Allah\'a and içtiler.'),
(1311, 9, 76, 'Ne zaman ki Allah lutfundan onlara verdi, O\'n(un verdiğin)e cimrilik ettiler ve yüz çevirerek (sözlerinden) döndüler.'),
(1312, 9, 77, 'Kendisine verdikleri sözden döndüklerinden ve yalan söylediklerinden dolayı Allah, kendisiyle karşılaşacakları güne kadar onların kalblerine iki yüzlülük sokmuştur.'),
(1313, 9, 78, 'Bilmediler mi ki Allah, onların sırlarını ve gizli konuşmalarını bilir ve Allah, gizlileri bilendir?'),
(1314, 9, 79, 'Sadakalar hususunda gönülden veren mü\'minleri çekiştiren ve güçlerinin yettiğinden başkasını bulamayanlarla alay edenler yok mu, Allah onlarla alay etmiştir. Onlar için acı bir azab vardır.'),
(1315, 9, 80, 'Onlar için ister af dile, ister dileme, onlar için yetmiş defa af dilesen, yine Allah onları affetmez. Böyledir, çünkü onlar Allah\'ı ve Elçisini tanımadılar; Allah, yoldan çıkan kavmi yola iletmez.'),
(1316, 9, 81, 'Allah\'ın Elçisinin arkasından oturmakla sevindiler, mallarıyle ve canlarıyle cihadetmekten hoşlanmadılar: "Sıcakta sefere çıkmayın." dediler. De ki: "Cehennemin ateşi daha sıcaktır!" Keşke anlasalardı!'),
(1317, 9, 82, 'Artık kazandıkları işlere karşılık az gülsünler, çok ağlasınlar!'),
(1318, 9, 83, 'Eğer Allah, seni onlardan bir topluluğun yanına döndürür de (onlar savaşa) çıkmak için senden izin isterlerse "Asla benimle çıkmayacaksınız, benimle beraber düşmanla savaşmayacaksınız. Siz ilk önce oturmağa razı olmuştunuz. Öyle ise geri kalanlarla beraber oturun!"de.'),
(1319, 9, 84, 'Ve Onlardan ölen birinin üzerine asla namaz kılma, onun kabri başında durma. Çünkü onlar Allah\'ı ve Elçisini tanımadılar ve yoldan çıkmış olarak öldüler.'),
(1320, 9, 85, 'Onların malları ve evladları seni imrendirmesin; Allah onlara dünyada, bunlarla azabetmeyi ve kafir olarak canlarının çıkmasını istiyor.'),
(1321, 9, 86, 'Allah\'a inanın, Elçisiyle beraber cihadedin! diye bir sure indirildiği zaman içlerinden servet sahibi olanlar, senden izin istediler: "Bizi bırak, oturanlarla beraber oturalım." dediler.'),
(1322, 9, 87, 'Geride kalan kadınlarla beraber olmağa razı oldular, kalbleri mühürlendi, artık onlar anlamazlar.'),
(1323, 9, 88, 'Fakat Elçi ve onunla beraber inananlar, mallariyle, canlariyle cihadettiler. İşte bütün hayırlar onlarındır ve işte başarıya erenler onlardır.'),
(1324, 9, 89, 'Allah, onlar için altlarından ırmaklar akan, içlerinde ebedi kalacakları cennetler hazırlamıştır. İşte büyük başarı budur.'),
(1325, 9, 90, 'Özür bahane eden bedevi Araplar, kendilerin(in savaşa katılmamasın)a izin verilmesi için geldiler; Allah\'a ve Elçisine yalan söyleyenler oturdular. Onlardan inkar edenlere, acı bir azab erişecektir.'),
(1326, 9, 91, 'Zayıflara, hastalara, harcayacak bir şey bulamayanlara, Allah ve Elçisi için öğüt verdikleri takdirde (sefere katılmamalarından ötürü) bir günah yoktur. İyilik edenlerin aleyhine bir yol yoktur (onlar kınanmazlar). Allah, bağışlayandır, esirgeyendir.'),
(1327, 9, 92, 'Kendilerini (binek sağlayıp) bindirmen için sana geldikleri zaman, sen: "Sizi bindirecek bir şey bulamıyorum" deyince harcayacak bir şey bulamadıklarından dolayı üzüntüden gözlerinden yaş akarak dönen kimselerin aleyhine de (yol yoktur, Onlar da kınanmazlar).'),
(1328, 9, 93, 'Ancak şu kimselerin kınanmasına yol vardır ki, zengin oldukları halde (geri kalmak için) senden izin isterler. Geri kalan kadınlarla beraber olmağa razı oldular. Allah da onların kalblerini mühürledi; artık onlar bilmezler.'),
(1329, 9, 94, '(Seferden) geri dönüp onların yanına geldiğiniz zaman sizden özür dilerler. De ki: "Hiç özür dilemeyin, size inanmayız! Allah bize sizin haberlerinizden (bize karşı çevirdiğiniz entrikalardan) bazılarını bildirdi. Yaptığınızı Allah da görecek, Elçiside. Sonra görülmeyeni ve görüleni bilenin huzuruna döndürüleceksiniz, O size yaptıklarınızı haber verecek."'),
(1330, 9, 95, 'Siz yanlarına geldiğiniz zaman kendilerinden vazgeçesiniz diye Allah\'a yemin edecekler. Onlardan vazgeçin, çünkü onlar murdardır. Kazandıkları işlerin cezası olarak varacakları yer de cehennemdir.'),
(1331, 9, 96, 'Size yemin ediyorlar ki kendilerinden razı olasınız. Siz onlardan razı olsanız bile Allah, yoldan çıkan topluluktan razı olmaz.'),
(1332, 9, 97, 'Bedevi Araplar, küfür ve iki yüzlülükçe daha yaman ve Allah\'ın, Elçisine indirdiği şeylerin sınırlarını tanımamağa daha müsaittirler. Allah bilendir, hüküm ve hikmet sahibidir.'),
(1333, 9, 98, 'Bedevi Araplardan kimi var ki, verdiğini angarya sayar ve sizin başınıza belalar gelmesini gözetler. Kötü bela onların başına gelsin. Allah işitendir, bilendir.'),
(1334, 9, 99, 'Bedevi Araplardan kimi de var ki Allah\'a ve ahiret gününe inanır, verdiğini Allah\'a yakın dereceler kazanmağa ve Elçinin du\'alarını almağa vesile sayar. Gerçekten o (verdikleri) kendileri için yakın dereceler(e vesile)dir. Allah onları rahmetinin içine sokacaktır. Muhakkak ki Allah bağışlayandır, esirgeyendir.'),
(1335, 9, 100, 'Muhacirlerden ve Ensardan (İslam\'a girmekte) ilk öne geçenler ile bunlara güzelce tabi olanlar... Allah onlardan razı olmuştur, onlar da O\'ndan razı olmuşlardır. (Allah) onlara, altlarından ırmaklar akan, içinde ebedi kalacakları cennetler hazırlamıştır. İşte büyük kurtuluş budur.'),
(1336, 9, 101, 'Çevrenizdeki bedevi Araplardan ve Medine halkından iki yüzlülüğe iyice alışmış münafıklar vardır. Sen onları bilmezsin, onları biz biliriz. Onlara iki kere azabedeceğiz, sonra da onlar, büyük azaba itileceklerdir.'),
(1337, 9, 102, 'Başka bir kısmı da günahlarını itiraf ettiler, iyi işle kötü işi birbirine karıştırdılar. Belki Allah, onların tevbesini kabul eder. Çünkü Allah bağışlayandır, esirgeyendir.'),
(1338, 9, 103, 'Onların mallarından, kendilerini temizleyeceğin, yücelteceğin bir sadaka al ve onlara du\'a et; çünkü senin du\'an, onlara huzur verir. Allah işitendir, bilendir.'),
(1339, 9, 104, 'Bilmediler mi ki, kullarından tevbeyi kabul eden, sadakaları alan Allah\'tır. Ve Allah, tevbeyi çok kabul eden, çok esirgeyendir.'),
(1340, 9, 105, 'De ki: "Yapın (yapacağınızı); yaptığınız işleri Allah da görecek Elçisi de, mü\'minler de. Sonra görülmeyeni ve görüleni bilen(Allah)a döndürüleceksiniz. O size yaptıklarınızı bir bir haber verecek.'),
(1341, 9, 106, 'Başka bir takımları da var ki Allah\'ın emrine bırakılmışlardır. (Allah) ya onlara azabeder, ya da onları affeder. Allah bilendir, hüküm ve hikmet sahibidir.'),
(1342, 9, 107, '(Seferden geri kalanlar arasında) Zarar vermek, nankörlük etmek, mü\'minlerin arasını açmak ve önceden Allah ve Elçisiyle savaşmış olan(adamın gelmesin)i gözetlemek için bir mescid yapanlar da var. "İyilikten başka bir niyetimiz yoktu" diye de yemin edecekler. Oysa Allah onların yalan söylediklerine şahidtir.'),
(1343, 9, 108, 'Orada asla namaza durma, ta ilk günden takva üzere kurulan mescid, elbette içinde namaza durmana daha uygundur. Onda temizlenmeyi seven erkekler vardır. Allah da temizlenenleri sever.'),
(1344, 9, 109, 'Yapısını, Allah\'tan korku ve rıza üzerine kuran mı hayırlıdır, yoksa yapısını bir yarın kenarına kurup onunla birlikte cehennem ateşine yuvarlanan mı? Allah zalimler topluluğunu doğru yola iletmez.'),
(1345, 9, 110, 'Yaptıkları bina, kalbleri parçalanıncaya dek yüreklerinde bir kuşku olarak kalacaktır. Allah bilendir, hüküm ve hikmet sahibidir.'),
(1346, 9, 111, 'Allah, mü\'minlerden canlarını ve mallarını cennet kendilerinin olmak üzere satın almıştır. Allah yolunda savaşırlar, öldürürler ve öldürülürler. Bu, Allah\'ın, Tevrat\'ta, İncil\'de ve Kur\'an\'da üstlendiği gerçek bir sözdür! Kim Allah\'tan daha çok sözünde durabilir? O halde O\'nunla yaptığınız bu alışverişinizden ötürü sevinin. Gerçekten bu, büyük başarıdır.'),
(1347, 9, 112, 'Tevbe edenler, ibadet edenler, hamdedenler, seyahat edenler, rüku edenler, secde edenler, iyiliği emredip kötülükten men\'edenler ve Allah\'ın sınırlarını koruyanlar... İşte o mü\'minleri müjdele (ne mutlu onlara)!'),
(1348, 9, 113, 'Akraba bile olsalar, cehennem halkı oldukları belli olduktan sonra (Allah\'a) ortak koşanlar için mağfiret dilemek; ne peygamberin, ne de inananların yapacağı bir iş değildir.'),
(1349, 9, 114, 'İbrahim\'in babası için mağfiret dilemesi, sadece ona verdiği bir sözden ötürü idi. Fakat onun, bir Allah düşmanı olduğu, kendisine belli olunca ondan uzak durdu. Gerçekten İbrahim, çok içli ve yumuşak huylu idi.'),
(1350, 9, 115, 'Allah, bir kavmi doğru yola ilettikten sonra, sakınmaları gereken şeyleri kendilerine açıklamadıkça onları saptıracak değildir. Allah herşeyi bilendir.'),
(1351, 9, 116, 'Göklerin ve yerin mülkü Allah\'ındır. Yaşatan, öldüren O\'dur. Sizin Allah\'tan başka bir dost ve yardımcınız yoktur.'),
(1352, 9, 117, 'Andolsun Allah, Peygamberi ve o güçlük sa\'atinde ona uyan Muhacirleri ve Ensarı affetti. O zaman içlerinden bir kısmının kalbleri kaymağa yüz tutmuş iken yine de onların tevbesini kabul buyurdu. Çünkü O, onlara karşı çok şefkatli, çok merhametlidir.'),
(1353, 9, 118, 'Ve (seferden) geri bırakılan o üç kişinin de tevbesini kabul buyurdu. Bütün genişliğiyle beraber dünya başlarına dar gelmiş ve canları sıkıldıkça sıklmış ve Allah\'tan, yine kendisine sığınmaktan başka çare olmadığını anlamışlardı. Allah onların tevbesini kabul buyurdu ki tevbe etsinler. Çünkü Allah, tevbeyi çok kabul eden, çok esirgeyendir.'),
(1354, 9, 119, 'Ey inananlar, Allah\'tan korkun ve doğrularla beraber olun.'),
(1355, 9, 120, 'Ne Medine halkının, ne de onların çevresinde bulunan bedevi Arapların, Allah\'ın Elçisinden geri kalmaları ve onun canından önce kendi canlarının kaygısına düşmeleri, onlara yakışmaz. Böyledir, çünkü Allah yolunda uğrayacakları hiçbir susuzluk, yorgunluk, açlık; kafirleri öfkelendirecek bir yeri çiğne(yip zaptet)meleri ve düşmana karşı bir başarı kazanmaları yoktur ki mutlaka bunlarla kendilerine iyi bir amel yazılmış olmasın. Allah güzel davrananların ecrini zayi etmez.'),
(1356, 9, 121, 'Küçük, büyük bir masraf yapmaları, bir vadiyi geçmeleri, mutlaka onların lehine yazılır ki Allah onları, yaptıklarının en güzeliyle mükafatlandırsın.'),
(1357, 9, 122, 'İnsanların hepsi toptan sefere çıkacak değillerdi. Ama her kabileden bir cemaatin dini iyice öğrenmeleri ve dönüp kavimlerine geldiklerinde, sakınmaları umuduyla onları uyarmaları için sefere çıkmaları gerekmez miydi?'),
(1358, 9, 123, 'Ey inananlar, yakınınızda bulunan kafirlerle savaşın, (onlar), sizde bir katılık bulsunlar. Bilin ki Allah, korunanlarla beraberdir.'),
(1359, 9, 124, 'Ne zaman bir sure indirilse onlardan kimi: "Bu, hanginizin imanını artırdı?" der. Bu, inananların imanını artırır, onlar sevinirler.'),
(1360, 9, 125, 'Fakat yüreklerinde hastalık olanlara gelince (bu), onların pisliklerine pislik katar. Ve onlar kafir olarak ölürler.'),
(1361, 9, 126, 'Kendilerinin her yıl bir iki defa sınandıklarını görmüyorlar mı? Yine de tevbe etmiyor, öğüt almıyorlar.'),
(1362, 9, 127, 'Bir sure indirildiği zaman: "Sizi birisi görüyor mu?" diye birbirine bakar, sonra sıvışırlar. Anlamaz bir topluluk oldukları için Allah onların kalblerini çevirmiştir.'),
(1363, 9, 128, 'Andolsun, içinizden size öyle bir Elçi geldi ki sıkıntıya uğramanız ona ağır gelir; size düşkün, mü\'minlere şefkatli, merhametlidir.'),
(1364, 9, 129, 'Eğer yüz çevirirlerse de ki: "Allah bana yeter! O\'ndan başka tanrı yoktur. O\'na dayandım, O büyük Arş\'ın sahibidir!"');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Elif lam ra. İşte şunlar, o hikmetli Kitab\'ın ayetleridir.'),
(1366, 10, 2, 'İçlerinden bir adama: "İnsanları uyar ve inananlara, Rableri katında kendileri için bir doğruluk kademesi bulunduğunu müjdele!" diye vahyettiğimiz, insanlara tuhaf mı geldi? kafirler: "Bu, apaçık bir büyücüdür." dediler.'),
(1367, 10, 3, 'Rabbiniz O Allah\'tır ki, gökleri ve yeri altı günde yarattı, sonra Arş\'a istiva etti (kuruldu). Emri tebdir (buyruğunu) icra eder (yaratıklarını yönetir). O\'nun izni olmadan hiç kimse şefa\'at edemez. İşte Rabbiniz Allah budur. O\'na kulluk edin, düşünmüyor musunuz?'),
(1368, 10, 4, 'Hepinizin dönüşü, O\'nadır. Bu, Allah\'ın gerçek olarak verdiği sözdür. O, yaratmağa başlar, sonra inanıp iyi işler yapanlara adaletle karşılık vermek için yeniden yaratır. İnkar edenlere gelince, küfürlerinden dolayı onlara kaynar sudan bir içki ve acı bir azab vardır.'),
(1369, 10, 5, 'Güneşi ziya, ay\'ı nur yapan; yılların sayısını ve (vakitlerin) hesabı(nı) bilmeniz için aya (dolaşma) konaklar(ı) düzenleyen O\'dur. Allah, bunları (boş yere değil), gerçek ile (hikmeti uyarınca) yaratmıştır. Bilen bir kavim için ayetleri açıklamaktadır.'),
(1370, 10, 6, 'Gece ve gündüzün değişmesinde ve Allah\'ın göklerde ve yerde yarattığı şeylerde, korunan bir topluluk için nice ibretler vardır.'),
(1371, 10, 7, 'Bizimle buluşmayı ummayan, dünya hayatına razı olup onunla rahat edenler ve bizim ayetlerimizden gaflet edenler...'),
(1372, 10, 8, 'İşte kazandıkları işlerden ötürü onların varacakları yer, ateştir!'),
(1373, 10, 9, 'İnanıp iyi işler yapanlara gelince imanlarından dolayı Rableri, onları altlarından ırmaklar akan ni\'met cennetlerine iletir.'),
(1374, 10, 10, 'Onların orada du\'ası: "Allah\'ım Sen her türlü eksiklikten uzaksın!", birbirlerine sağlık dilekleri: "Selam", du\'alarının sonu da: "Alemlerin Rabbi Allah\'a hamdolsun!" sözleridir.'),
(1375, 10, 11, 'İnsanların, hayrı acele istemeleri gibi, Allah da onlara şerri acele verseydi, süreleri hemen bitirilmiş olurdu. Ama biz, bizimle buluşmayı ummayanları bırakırız, azgınlıkları içinde bocalar, dururlar.'),
(1376, 10, 12, 'İnsana bir darlık dokunduğu zaman, yanı üzere yatarken, yahut otururken ya da ayakta bize yalvarır; ama biz onun darlığını aç(ıp kaldır)ınca sanki kendisine dokunan bir darlıktan ötürü bize hiç yalvarmamış gibi hareket eder. İşte aşırı gidenlere, yaptıkları iş böylesine süslü gösterilmiştir.'),
(1377, 10, 13, 'Sizden önce, zulmettikleri ve peygamberleri kendilerine açık kanıtlar getirdikleri halde inanmadıkları için nice nesilleri helak etmişizdir. İşte suç işleyen kavmi böyle cezalandırırız.'),
(1378, 10, 14, 'Sonra onların ardından, bu dünyada onların yerine sizi geçirdik ki, sizin de nasıl davranacağınızı görelim.'),
(1379, 10, 15, 'Onlara açık açık ayetlerimiz okunduğu zaman, bizimle buluşmayı ummayanlar: "Bundan başka bir Kur\'an getir veya bunu değiştir." derler. De ki: "Onu kendi tarafımdan değiştiremem. Ben sadece bana vahyolunana uyarım. Şayet ben Rabbime karşı gelirsem, büyük bir günün azabından korkarım."'),
(1380, 10, 16, 'De ki: "Eğer Allah dileseydi, onu size okumazdım ve onu size hiç bildirmezdi. Ben ondan önce aranızda bir ömür boyu kalmıştım (böyle bir şey yapmamıştım), düşünmüyor musunuz?"'),
(1381, 10, 17, 'Uydurduğu yalanı Allah\'ın üzerine atan, yahut O\'nun ayetlerini yalanlayandan daha zalim kim olabilir? Şüphesiz suçlular asla onmazlar.'),
(1382, 10, 18, 'Allah\'ı bırakıp kendilerine ne zarar, ne de yarar veremeyen şeylere tapıyorlar ve: "Bunlar Allah katında bizim şefa\'atçilerimizdir!" diyorlar. De ki: "Allah\'ın, göklerde ve yerde bilmediği bir şeyi mi Allah\'a haber veriyorsunuz?" O, onların koştukları ortaklardan uzak ve yücedir.'),
(1383, 10, 19, 'İnsanlar bir tek milletten başka bir şey değildi, ama ayrılığa düştüler. Eğer Rabbinden bir söz geçmemiş olsaydı, ayrılığa düştükleri konuda hemen aralarında hüküm verilir(işleri bitirilir)di.'),
(1384, 10, 20, 'Ona Rabbinden bir mu\'cize indirilmeli değil mi? diyorlar. De ki: "Gayb Allah\'ındır (görülmeyeni bilen O\'dur). Bekleyin, ben de sizinle beraber bekleyenlerdenim."'),
(1385, 10, 21, 'Kendilerine dokunan bir darlıktan sonra insanlara bir rahmet (sağlık ve bolluk zevkini) taddırdığımız zaman bakarsın ki, yine onların, ayetlerimiz hakkında bir tuzakları vardır. De ki: "Allah daha çabuk tuzak kurar!" Elçilerimiz, sizin kurduğunuz tuzakları yazıyorlar.'),
(1386, 10, 22, 'Sizi karada ve denizde yürüten O\'dur. Gemide olduğunuz zaman(ı düşünün): Gemiler, içinde bulunanları hoş bir rüzgarla alıp götürdüğü, ve (yolcular) bununla sevindikleri sırada, birden gemiye, şiddetli bir kasırga gelip de, her yerden gelen dalgalar onları sardığı ve artık kendilerinin tamamen kuşatıldıklarını (bir daha kurtulamayacaklarını) sandıkları zaman, dini, yalnız Allah\'a halis kılarak O\'na şöyle yalvarmağa başlarlar: "Andolsun, eğer bizi bundan kurtarırsan, şükredenlerden olacağız.'),
(1387, 10, 23, 'Ama (Allah) onları kurtarınca hemen yeryüzünde haksız yere taşkınlık yaparlar. Ey insanlar, taşkınlığınız kendi aleyhinizedir. Sadece şu yakın (geçici) hayatın zevkinden ibarettir. Sonra dönüşünüz bizedir; size bütün yaptıklarınızı haber veririz.'),
(1388, 10, 24, 'Şu yakın hayat, tıpkı gökten indirdiğimiz bir suya benzer: İnsanların ve hayvanların yediği arz bitkisi o su ile karıştı: nihayet yer zinetini takınıp süslendiği ve halkı da on(un ürününü devşirmeğ)e kadir olduklarını zannettikleri sırada birden buyruğumuz ona gece veya gündüz geldi; sanki dün o hiç (bitkisiyle süslenip) şenlenmemiş gibi, onu biçilmiş yaptık (süsünü, zenginliğini biçtik, yok ettik). İşte biz, düşünen bir toplum için ayetleri böyle geniş geniş açıklarız.'),
(1389, 10, 25, 'Allah; esenlik yurduna çağırır ve dilediğini doğru bir yola iletir.'),
(1390, 10, 26, 'Güzel davrananlara daha güzel karşılık ve fazlası var. Onların yüzlerine ne bir kara bulaşır, ne de horluk. İşte onlar cennet halkıdır, orada ebedi kalacaklardır.'),
(1391, 10, 27, 'Kötü işler yapanlara da (yaptıkları) kötülüğün aynen cezası verilir. Ve onların yüzlerini bir horluk kaplar. Onları Allah\'tan kurtaracak hiç kimse yoktur. Sanki yüzleri, karanlık geceden parçalara bürünmüştür. İşte onlar da ateş halkıdır, hep orada kalacaklardır.'),
(1392, 10, 28, 'O gün onları hep bir araya toplarız, sonra ortak koşanlara; "Haydi siz ve koştuğunuz ortaklar yerlerinize!" deriz. Artık (tanrılariyle) aralarını açmışızdır (dünyadaki gibi aralarında bir bağ kalmamıştır). Koştukları ortaklar: "Siz bize tapmıyordunuz?" demektedirler.'),
(1393, 10, 29, 'Şimdi bizimle sizin aranızda Allah\'ın şahid olması yeter; doğrusu biz sizin (bize) tapmanızdan tamamen habersizdik!'),
(1394, 10, 30, 'İşte orada her can, geçmişte yaptıklarını dener (yaptıklarının yararını ve zararını görür). Gerçek sahipleri olan Allah\'a döndürülürler ve uydurdukları şeyler, kendilerinden kaybolup gider.'),
(1395, 10, 31, 'De ki: "Sizi gökten ve yerden kim rızıklandırıyor? Ya da o kulak(lar)ın ve gözlerin sahibi kimdir? Ölüden diriyi, diriden ölüyü kim çıkarıyor? Kim buyruğu(nu) yürütüyor (kainatı yönetiyor)?" "Allah." diyecekler. "O halde, korunmuyor musunuz?" de.'),
(1396, 10, 32, 'İşte sizin gerçek Rabbiniz Allah budur. Gerçekten sonra sapıklıktan başka ne var? Öyleyse nasıl (hak\'tan sapıklığa) çevriliyorsunuz?'),
(1397, 10, 33, 'Böylece Rabbinin, yoldan çıkanlar için söylediği: "Onlar inanmazlar." sözü, gerçekleşti.'),
(1398, 10, 34, 'De ki: "Sizin koştuğunuz ortaklardan ilk defa yaratacak, sonra onu çevirip yeniden yaratacak olan var mı?" De ki: "Allah ilk defa yaratır, sonra onu çevirip yeniden yaratır. Öyleyse nasıl (doğru yoldan) çevriliyorsunuz?"'),
(1399, 10, 35, 'De ki: "Sizin ortaklarınızdan hakka götürecek var mı?" De ki: "Allah, hakka götürür. Hakka götüren mi uyulmağa daha layıktır, yoksa (tutulup) yola götürülmedikçe kendisi doğru yolu bulamayan mı? O halde neyiniz var? Nasıl hükmediyorsunuz?"'),
(1400, 10, 36, 'Onların çoğu, zandan başka bir şeye uymuyorlar. Zan ise gerçekten hiçbir şey kazandırmaz. Muhakkak ki Allah, onların ne yaptıklarını bilir.'),
(1401, 10, 37, 'Bu Kur\'an, Allah\'tan başkası tarafından uydurulacak bir şey değildir. Ancak kendinden öncekinin doğrulaması ve Kitabın açıklamasıdır. Onda asla şüphe yoktur. Alemlerin Rabbi tarafından(indirilmiş)dir.'),
(1402, 10, 38, 'Yoksa "Onu uydurdu" mu diyorlar? De ki: "Eğer doğru iseniz haydi onun benzeri bir sure getirin ve Allah\'tan başka çağırabildiklerinizi de çağırın!"'),
(1403, 10, 39, 'Hayır, bilgisini kavrayamadıkları, sonucu henüz başlarına gelmemiş olan bir şeyi yalanladılar. Onlardan öncekiler de böyle yalanlamışlardı. Bak, o zalimlerin sonu nice oldu?'),
(1404, 10, 40, 'Onlardan kimi, ona inanır, kimi de inanmaz. Rabbin bozguncuları çok iyi bilir.'),
(1405, 10, 41, 'Eğer onlar seni yalanladılarsa de ki: "Benim yaptığım bana, sizin yaptığınız size. Siz benim yaptığımdan uzaksınız, ben de sizin yaptığınızdan uzağım!"'),
(1406, 10, 42, 'İçlerinden sana kulak verip dinleyenler de vardır. Fakat sağırlara sen mi duyuracaksın? Hele akıllarını da kullanmıyorlarsa!'),
(1407, 10, 43, 'İçlerinden sana bakanlar da var. Fakat körleri sen mi yola götüreceksin? Hele sezgileriyle de görmüyorlarsa?'),
(1408, 10, 44, 'Allah insanlara hiç zulmetmez, fakat insanlar kendi kendilerine zulmediyorlar.'),
(1409, 10, 45, 'Onları bir araya toplayacağı gün, sanki onlar sadece gündüzün, görüşüp, tanıştıkları bir sa\'ati kadar dünyada kalmış olurlar. Allah\'ın huzuruna çıkmayı yalanlayıp, yola gelmemiş olanlar, en büyük ziyana uğramışlardır.'),
(1410, 10, 46, 'Ya onları uyardığımız şeylerin bir kısmını sana gösteririz. Ya da (bundan önce) seni vefat ettiririz (farketmez). Nasıl olsa dönüşleri bizedir. Sonra Allah onların yaptıklarına da şahiddir.'),
(1411, 10, 47, 'Her ümmetin bir elçisi vardır. Elçileri gel(ip de bunlar onu yalanlay)ınca aralarında adaletle hükmolunur, onlara hiç haksızlık edilmez.'),
(1412, 10, 48, 'Doğru iseniz bu bizi tehdid(ettiğiniz) azab ne zaman? diyorlar.'),
(1413, 10, 49, 'De ki: "Ben kendime dahi, Allah\'ın dilediğinden başka, ne zarar, ne de yarar verme gücüne sahip değilim. Her ümmetin bir süresi vardır. Süreleri gelince ne bir an geri kalırlar, ne de ileri giderler."'),
(1414, 10, 50, 'De ki: "Bakın, eğer O\'nun azabı size geceleyin, ya da gündüzün gelirse... Suçlular bun(lar)dan hangisini acele istiyor?"'),
(1415, 10, 51, '(Azab) başınıza geldikten sonra mı ona inanacaksınız? Şimdi mi (inandınız)? Hani ya siz onu çabuk isteyip duruyordunuz (nasılmış)?'),
(1416, 10, 52, 'Sonra zulmedenlere: "Sürekli azabı tadın!" denilecek, "Yalnız kazandığınız şeylerle cezalandırılmıyor musunuz?"'),
(1417, 10, 53, 'Sahiden o gerçek midir? diye senden soruyorlar. De ki: "Evet, Rabbim hakkı için o gerçektir. Siz (onu) önleyemezsiniz!"'),
(1418, 10, 54, '(O zaman), kendisine zulmeden her kişi, yeryüzünde ne varsa hepsi kendisinin olsaydı (azabdan kurtulmak için) onu fidye verirdi. Azabı gördükleri zaman, içlerinde pişmanlık duyarlar, aralarında adaletle hükmedilir, asla haksızlığa uğratılmazlar.'),
(1419, 10, 55, 'İyi bil ki, göklerde ve yerde ne varsa hepsi Allah\'ındır. İyi bil ki Allah\'ın va\'di gerçektir, fakat çokları bilmiyorlar.'),
(1420, 10, 56, 'O, yaşatır, öldürür ve siz O\'na döndürülüp götürüleceksiniz.'),
(1421, 10, 57, 'Ey insanlar, size Rabbinizden bir öğüt, göğüslerde olan(sıkıntılar)a şifa ve inananlara bir yol gösterici ve rahmet gelmiştir.'),
(1422, 10, 58, 'De ki: "Allah\'ın lutfiyle, rahmetiyle (evet) ancak onunla ferahlansınlar. O onların toplayıp yığdıklarından hayırlıdır."'),
(1423, 10, 59, 'De ki: "Gördünüz mü, Allah\'ın size rızık olarak indirdiği şeylerin bir kısmını haram ve bir kısmını helal yaptınız." De ki: "Allah mı size böyle izin verdi, yoksa siz Allah\'a iftira mı ediyorsunuz?"'),
(1424, 10, 60, 'Allah\'a yalan uyduranların kıyamet günü hakkındaki zanları nedir? Muhakkak ki Allah, insanlara karşı lutuf sahibidir, ama çokları şükretmiyorlar.'),
(1425, 10, 61, 'Ne işte bulunsan, Kur\'an\'dan ne okusan ve siz ne iş yapsanız mutlaka biz, içine daldığınız an üzerinizde şahidiz (her yaptığınızı görürüz). Ne yerde, ne de gökte zerre ağırlığınca bir şey, Rabbin(in bilgisin)den kaçmaz. Ne bundan küçük, ne de büyük hiçbir şey yoktur ki, hepsi apaçık bir Kitapta olmasın.'),
(1426, 10, 62, 'İyi bil ki, Allah\'ın velilerine (sevdiklerine) korku yoktur ve onlar üzülmeyeceklerdir.'),
(1427, 10, 63, 'Onlar ki, inandılar ve korunurlardı.'),
(1428, 10, 64, 'Dünya hayatında da, ahirette de müjde onlara! Allah\'ın kelimeleri değişmez (O\'nun verdiği söz, mutlaka yerine getirilir). İşte bu, büyük kurtuluştur.'),
(1429, 10, 65, 'Onların sözü seni üzmesin, üstünlük tamamen Allah\'ındır. İşiten ve bilen O\'dur.'),
(1430, 10, 66, 'İyi bilki, göklerde ve yerde kim varsa hepsi Allah\'ındır. Allah\'tan başkasına yalvaranlar (gerçekte koştukları) ortaklara uymuyorlar, onlar sadece zanna uyuyorlar, (hayallerine kapılıyorlar) ve onlar sadece saçmalıyorlar.'),
(1431, 10, 67, 'Geceyi sizin istirahat etmenize elverişli, gündüzü de (geçiminizi sağlamanız için) aydınlık yapan O\'dur. Şüphesiz, bunda işiten bir toplum için ibretler vardır.'),
(1432, 10, 68, 'Allah, çocuk edindi, dediler. Haşa, Allah bundan uzaktır, O zengindir (hiçbir şeye muhtaç değildir). Göklerde ve yerde ne varsa hepsi O\'nundur. Bu hususta hiçbir deliliniz yok. Allah hakkında bilmediğiniz şeyi mi söylüyorsunuz?'),
(1433, 10, 69, 'De ki: "Allah hakkında yalan uyduranlar, iflah olmazlar!"'),
(1434, 10, 70, 'Dünyada biraz geçinir, sonra bize dönerler. Sonra da biz, inkarlarından dolayı onlara şiddetli azabı taddırırız.'),
(1435, 10, 71, 'Onlara Nuh\'un haberini oku. Kavmine: "Ey kavmim demişti, eğer benim kalkıp size Allah\'ın ayetlerini hatırlatmam, size ağır geldiyse, o halde ben Allah\'a dayandım, siz de ortaklarınızla beraber toplanıp yapacağınız işi kararlaştırın da işiniz başınıza dert olmasın. Sonra hükmünüzü bana uygulayın, bana hiç fırsat da vermeyin!"'),
(1436, 10, 72, 'Eğer yüz çevirdiyseniz (neden?), ben sizden bir ücret istemedim ki! Benim ücretim, ancak Allah\'ın üzerinedir. Bana müslümanlardan olmam emredilmiştir.'),
(1437, 10, 73, 'Yine de onu yalanladılar. Biz de onu ve gemide onunla beraber bulunanları kurtardık, onları egemen yaptık ve ayetlerimizi yalanlayanları da boğduk. Bak işte uyarıl(ıp da yola gelmey)enlerin sonu nice oldu!'),
(1438, 10, 74, 'Sonra onun ardından bir çok elçileri kavimlerine gönderdik; onlara; belgeler getirdiler. (Fakat onlar) önce yalanlamış oldukları şeye bir türlü inanmıyorlardı. İşte haddi aşanların kalblerini böyle mühürleriz.'),
(1439, 10, 75, 'Sonra onların ardından Musa ve Harun\'u ayetlerimizle birlikte Fir\'avn\'a ve adamlarına gönderdik; böbürlendiler ve suç işleyen bir topluluk oldular.'),
(1440, 10, 76, 'Onlara katımızdan gerçek gelince: "Bu, apaçık bir büyüdür." dediler.'),
(1441, 10, 77, 'Musa: "Size gelen gerçek için böyle mi diyorsunuz? Büyü müdür bu? Halbuki büyücüler, iflah olmazlar!" dedi.'),
(1442, 10, 78, 'Dediler ki: "Sen bizi, babalarımızı üzerinde bulduğumuz şeyden çeviresin de yeryüzünde büyüklük yalnız ikinize kalsın diye mi geldin? Biz size inanacak değiliz!"'),
(1443, 10, 79, 'Fir\'avn: "Bana bütün bilgili büyücüleri getirin." dedi.'),
(1444, 10, 80, 'Büyücüler gelince Musa onlara: "Atacağınızı atın (hünerinizi gösterin)." dedi.'),
(1445, 10, 81, 'Onlar (iplerini ve değneklerini atınca) Musa; "Sizin getirdiğiniz şey, büyüdür, dedi. Allah, onu mutlaka boşa çıkaracaktır. Çünkü Allah bozguncuların işini düzeltmez!"'),
(1446, 10, 82, 'Ve suçlular istemese de Allah, sözleriyle gerçeği ortaya çıkaracaktır!'),
(1447, 10, 83, 'Fir\'avn\'ın ve adamlarının, kendilerine kötülük yapmasından korktukları için kavminin içinde Musa\'ya, yalnız genç bir kuşaktan başkası inanmadı. Çünkü Fir\'avn, yeryüzünde çok ululanan ve çok aşırı gidenlerden idi.'),
(1448, 10, 84, 'Musa dedi ki: "Ey kavmim, eğer Allah\'a inandıysanız, gerçekten müslüman insanlar iseniz o\'na dayanın."'),
(1449, 10, 85, 'Dediler ki: "Allah\'a dayandık, Rabbimiz bizi o zulmeden kavme fitne yapma (bizi onların işkencesiyle deneme)!'),
(1450, 10, 86, 'Acımanla bizi o inkarcı toplumdan kurtar.'),
(1451, 10, 87, 'Musa\'ya ve kardeşine "İkiniz kavminiz için Mısır\'da evler hazırlayın (ey İsrail oğulları) evlerinizi karşı karşıya kurun, namaz kılın ve (ey Musa) mü\'minleri müjdele" diye vahyettik.'),
(1452, 10, 88, 'Musa: "Rabbimiz dedi, sen Fir\'avn\'a ve adamlarına yakın hayatta süs ve nice mallar verdin. Rabbimiz, senin yolundan saptırsınlar diye mi? Rabbimiz, onların mallarını yok et, kalblerini sık ki, acı azabı görünceye kadar inanmasınlar!"'),
(1453, 10, 89, '(Allah): "ikinizin du\'ası kabul olundu, dedi, doğru olun, bilmezlerin yoluna uymayın."'),
(1454, 10, 90, 'İsrail oğullarını denizden geçirdik, Fir\'avn ve askerleri de zulmetmek ve saldırmak için onların arkalarına düştü. Nihayet boğulma kendisini yakalayınca (Fir\'avn): "Gerçekten İsrail oğullarının inandığından başka tanrı olmadığına inandım, ben de müslümanlardanım!" dedi.'),
(1455, 10, 91, 'Şimdi mi? Oysa daha önce isyan etmiş, bozgunculardan olmuştun? (denildi).'),
(1456, 10, 92, 'Bugün senin (canından ayırdığımız) bedenini, (denizin dibinden) kurtarıp (sahilde) bir tepeye atacağız ki senden sonra gelenlere ibret olasın. Ama insanlardan çoğu bizim ayetlerimizden gafildirler.'),
(1457, 10, 93, 'Andolsun biz, İsrail oğullarını iyi bir yere yerleştirdik ve onlara güzel rızıklar verdik. Kendilerine ilim gelinceye kadar ayrılığa düşmediler (de bilgi geldikten sonra ayrılığa düştüler). Şüphesiz Rabbin, kıyamet günü, anlaşmazlığa düştükleri şey hakkında aralarında hüküm verecektir.'),
(1458, 10, 94, 'Eğer sen, sana indirdiğimizden kuşkuda isen, senden önce Kitabı okuyanlara sor. Andolsun, sana Rabbinden hak geldi, sakın kuşkulananlardan olma!'),
(1459, 10, 95, 'Ve sakın Allah\'ın ayetlerini yalanlayanlardan olma, yoksa ziyana uğrayanlardan olursun.'),
(1460, 10, 96, 'Üzerlerine Rabbinin (azab) kelimesi hak olanlar inanmazlar.'),
(1461, 10, 97, 'Onlara bütün ayetler gelmiş olsa bile, acı azabı görünceye kadar (inanmazlar).'),
(1462, 10, 98, 'Keşke bir kasaba olsaydı da inansaydı ve inanması kendisine fayda verseydi! Yalnız Yunus\'un kavmi, inanınca, dünya hayatnda onlardan rezillik azabını kaldırmış ve onları bir süre daha yaşatmıştık.'),
(1463, 10, 99, 'Rabbin isteseydi, yeryüzündekilerin hepsi mutlaka inanırdı. O halde sen mi insanları inanmaları için zorlayacaksın?'),
(1464, 10, 100, 'Allah\'ın izni olmadan hiç kimse inanmaz ve (Allah) pisliği (huzursuzluğu, azabı), akıllarını kullanmayanların üzerine kor.'),
(1465, 10, 101, 'Göklerde ve yerde olanlara bakın! de; ama o ayetler ve uyarılar, inanmayacak bir kavme yarar sağlamaz.'),
(1466, 10, 102, 'Onlar sadece kendilerinden önce gelip geçenlerin başlarına gelen günler gibisini bekliyorlar öyle mi? De ki: "O halde bekleyin, ben de sizinle beraber bekleyenlerdenim!"'),
(1467, 10, 103, 'Sonunda elçilerimizi ve inananları kurtarırız. İşte böyle, üzerimize bir borç olarak mü\'minleri kurtarırız.'),
(1468, 10, 104, 'De ki: "Ey insanlar, benim dinimden kuşkuda iseniz, ben sizin, Allah\'tan başka taptıklarınıza tapmam; fakat sizi öldürecek olan Allah\'a taparım. Bana mü\'minlerden olmam emredilmiştir."'),
(1469, 10, 105, 'Ve: "Yüzünü hanif (Allah\'ı birleyici) olarak dine çevir; sakın (Allah\'a) ortak koşanlardan olma!"'),
(1470, 10, 106, 'Allah\'tan başka; sana ne fayda, ne de zarar veremeyecek olan şeylere yalvarma! Eğer böyle yaparsan, o takdirde sen muhakkak zalimlerden olursun. (diye emredilmiştir).'),
(1471, 10, 107, 'Eğer Allah sana bir zarar dokundursa onu, yine O\'ndan başka kaldıracak yoktur ve eğer sana bir hayır dilese, O\'nun keremini de geri çevirecek yoktur. Hayrını, kullarından dilediğine verir. O, bağışlayandır, esirgeyendir.'),
(1472, 10, 108, 'De ki: "Ey insanlar, işte size Rabbinizden gerçek geldi. Artık yola gelen, kendisi için gelir; sapan da kendi zararına sapar. Ben sizin üzerinize vekil değilim!"'),
(1473, 10, 109, 'Sana vahyolunana uy ve Allah hükmünü verinceye kadar sabret. O, hüküm verenlerin en iyisidir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Elif lam ra. (Bu,) bir Kitaptır ki, hikmet sahibi, herşeyden haberi olan (Allah) tarafından ayetleri sağlamlaştırılmış ve güzelce açıklanmıştır.'),
(1475, 11, 2, 'Ta ki Allah\'tan başkasına tapmayasınız. Ben de, O\'ndan size (gönderilmiş) bir uyarıcı ve müjdeleyiciyim.'),
(1476, 11, 3, 'Ve Rabbinizden mağfiret dileyesiniz, sonra O\'na tevbe edesiniz ki, sizi belirtilmiş bir süreye kadar güzelce yaşatsın ve her lutuf sahibine lutfetsin. Ve eğer yüz çevirirseniz, ben sizin için büyük bir günün azabından korkarım.'),
(1477, 11, 4, 'Dönüşünüz Allaha\'dır. O, herşeyi yapacak güçtedir.'),
(1478, 11, 5, 'İyi bilin ki, onlar O\'ndan gizlenmek için göğüslerini bükerler. Yine iyi bilin ki onlar, örtülerine büründükleri zaman dahi (Allah onların) içlerinde gizlediklerini ve açığa vurduklarını bilir. Çünkü O, göğüslerin özünü bilendir.'),
(1479, 11, 6, 'Yeryüzünde hiçbir canlı yoktur ki, rızkı Allah\'a aidolmasın. (Allah) onun durduğu ve emanet bırakıldığı yeri bilir. Bunların hepsi apaçık bir Kitaptadır.'),
(1480, 11, 7, 'Gökleri ve yeri altı günde yaradan O\'dur. O zaman Arş\'ı su üzerinde idi. (Bu kainatı yarattı) Ki, hanginizin daha güzel iş yaptığınızı denesin. Böyle iken yine sen: "Öldükten sonra diriltileceksiniz" desen, inkar edenler, mutlaka: "Bu, apaçık bir büyüden başka bir şey değildir." derler.'),
(1481, 11, 8, 'Andolsun onlardan azabı sayılı bir ümmete (belli bir süreye) ertelesek, "Onu tut(up bize gelmesine engel ol)an nedir?" derler. İyi bilin ki, o (azab) başlarına geldiği gün, bir daha onlardan geri çevrilmez ve alay ettikleri şey, kendilerini kuşatmışolur.'),
(1482, 11, 9, 'Eğer biz insana, bizden bir rahmet taddırsak da sonra onu kendisinden çekip alsak, hemen o, umutsuzluğa düşer, nankör olur.'),
(1483, 11, 10, 'Ve eğer kendisine dokunan bir zarardan sonra ona bir ni\'met taddırsak, mutlaka: "Kötülükler benden gitti" der, sevinir, övünür.'),
(1484, 11, 11, 'Ancak sabredip iyi işler yapanlar böyle değildir. İşte onlar için mağfiret ve büyük mükafat vardır.'),
(1485, 11, 12, 'Herhalde sen: "Ona bir hazine indirilmeli veya beraberinde bir melek gelmeli değil miydi?" demelerinden ötürü, sana vahyolunanın bir kısmını bırakacaksın ve bununla göğsün sıkılacak; ama sen sadece bir uyarıcısın (böyle sözlere aldırma), her şeye vekil olan Allah\'tır.'),
(1486, 11, 13, 'Yoksa, "O\'nu uydurdu" mu diyorlar? De ki: "Öyleyse siz de onun benzeri on uydurulmuş sure getirin; eğer doğru iseniz Allah\'tan başka, çağırabildiklerinizi de (yardıma) çağırın (da bunu yapın)!"'),
(1487, 11, 14, 'Eğer size cevap veremedilerse bilin ki (o) Allah\'ın bilgisiyle indirilmiştir ve O\'ndan başka tanrı yoktur. Nasıl, artık müslüman oldunuz mu?'),
(1488, 11, 15, 'Kimler dünya hayatını ve süsünü isterse onlara oradaki amellerin(in karşılığın)ı tam veririz ve onlar orada hiçbir eksikliğe uğratılmazlar.'),
(1489, 11, 16, 'Ama onlar öyle kimselerdir ki ahirette onlar için ateşten başka bir şey yoktur ve yaptıklarının hepsi orada boşa çıkmıştır, amelleri hep batıl olmuştur!'),
(1490, 11, 17, 'Hiç böyleleri, şu kimse gibi olur mu ki, o Rabbinden bir delil üzerinde bulunur, ayrıca O\'ndan bir şahid de onu takib eder. O(Hak şahidi Kur\'a)n\'dan önce de bir önder ve rahmet olarak Musa\'nın Kitabı var. İşte onlar O(Kur\'a)n\'a inanırlar. Topluluklardan kim onu inkar ederse, onun yeri ateştir! O(Kur\'a)n\'dan hiç kuşkun olmasın. Muhakkak o, Rabbinden gelen gerçektir. Fakat insanların çoğu inanmazlar.'),
(1491, 11, 18, 'Allah\'a yalan uyduranlardan daha zalim kim olabilir? Onlar Rablerine sunulacaklar. Şahidler de: "İşte Rablerine karşı yalan söyleyenler bunlardır!" diyecekler. İyi bilin ki Allah\'ın la\'neti zalimlerin üzerinedir.'),
(1492, 11, 19, 'Onlar ki Allah\'ın yoluna engel olurlar ve onu eğriltmek isterler ve onlar, (evet) onlar, ahireti de tanımazlar.'),
(1493, 11, 20, 'Onlar dünyada Allah\'ı aciz bırakacak değillerdir. Ve onların Allah\'tan başka dostları da yoktur. Onlar için azab kat kat yapılır. Çünkü (gerçeği) işitmeğe tahammül edemezlerdi ve (onu) görmezlerdi.'),
(1494, 11, 21, 'İşte onlar canlarını ziyana sokan kimselerdir. Ve uydurdukları şeyler, kendilerinden kaybolup gitmiştir.'),
(1495, 11, 22, 'Elbette ahirette en çok ziyana uğrayanlar onlardır.'),
(1496, 11, 23, 'İnanıp iyi işler yapan ve Rablerine gönülden boyun eğenlere gelince; işte onlar da cennet halkıdır, onlar orada ebedi kalacaklardır.'),
(1497, 11, 24, 'Bu iki zümrenin durumu, kör ve sağır ile gören ve işiten gibidir. Bunlar bir olur mu hiç? Hala ibret almaz mısınız?'),
(1498, 11, 25, 'Andolsun biz Nuh\'u da kavmine gönderdik: "Ben sizin için apaçık bir uyarıcıyım."'),
(1499, 11, 26, 'Allah\'tan başkasına tapmayın. Gerçekten ben, sizin, acı bir günün azabına uğramanızdan korkuyorum. (dedi).'),
(1500, 11, 27, 'Kavminden ileri gelen inkarcı grup dedi ki: "Biz seni de bizim gibi insan görüyoruz ve sana bizim basit görüşlü ayak takımlarımızdan başkasının uyduğunu görmüyoruz. Sizin bize karşı bir üstünlüğünüzü de görmüyoruz; tersine sizi yalancı sanıyoruz!"'),
(1501, 11, 28, 'Dedi ki: "Ey kavmim, bakın, ya ben Rabbimden bir delil üzerinde isem ve (O), kendi katından bana bir rahmet vermiş de, o (rahmet) sizin gözlerinizden gizli bırakılmış ise? Şimdi siz onu istemezken, biz sizi o(Tanrı rahmeti)ne zorla mı sokacağız?"'),
(1502, 11, 29, 'Ey kavmim, buna karşı ben sizden bir mal istemiyorum, benim ücretim Allah\'a aittir. Ve (siz istemiyor, hor görüyorsunuz diye) ben, inananları (yanımdan) kovacak değilim. Çünkü onlar Rablerinin huzuruna gidecek(yaptıklarının hesabını verecek)lerdir. ( Herkes kendi amelinden sorumludur. Onları niçin kovayım?) Fakat ben sizi, cahillik eden bir kavim görüyorum.'),
(1503, 11, 30, 'Ey kavmim, ben onları kovarsam, Allah\'a karşı beni kim savunur? Düşünmüyor musunuz?'),
(1504, 11, 31, 'Ben size: "Allah\'ın hazineleri benim yanımdadır." demiyorum. Gaybı da bilmem. "Ben meleğim," de demiyorum. Sizin gözlerinizin hor gördüğü kimseler için "Allah onlara bir hayır vermeyecek" de demem. Allah, onların içlerinde olanı daha iyi bilir. Böylebir şey yaptığım takdirde ben, mutlaka zalimlerden olurum."'),
(1505, 11, 32, 'Dediler ki: "Ey Nuh, bizimle mücadele ettin. Hem bizimle mücadelede çok ileri gittin. Eğer doğrulardan isen haydi bizi tehdidettiğin şeyi bize getir!"'),
(1506, 11, 33, 'Dedi: "Onu, ancak Allah dilerse size getirir; siz engel olamazsınız!"'),
(1507, 11, 34, 'Eğer Allah, sizi azdırmak diliyorsa, ben size öğüt de etmek istesem, öğütüm size yarar sağlamaz. Rabbiniz O\'dur ve siz O\'na döndürüleceksiniz.'),
(1508, 11, 35, 'Yoksa "O(Kur\'a)n\'ı uydurdu" mu diyorlar? De ki: "Eğer O\'nu uydurmuşsam, suçum banadır. Ama ben sizin işlediğiniz suçlardan uzağım."'),
(1509, 11, 36, 'Nuh\'a vahyolundu ki: "Kavminden, inanmış olanlardan başka kimse inanmayacak, onların yaptıklarından dolayı üzülme!"'),
(1510, 11, 37, 'Gözlerimizin önünde ve vahyimiz gereğince gemiyi yap ve zulmedenler hakkında bana hitabetme (onların kurtuluşu için bana yalvarma); onlar mutlaka boğulacaklardır!'),
(1511, 11, 38, 'Nuh gemiyi yapıyor, kavminden ileri gelenler yanından geçtikçe onunla alay ediyorlardı. "Siz bizimle alay ederseniz, sizin alay ettiğiniz gibi biz de sizinle alay edeceğiz?" dedi.'),
(1512, 11, 39, 'Yakında bileceksiniz: İnsanı rezil eden azab kime geliyor, sürekli azab kimin başına konuyor?'),
(1513, 11, 40, 'Nihayet emrimiz gelip de tandır kaynayınca (iş ciddileşip sular kaynamağa başlayınca, Nuh\'a) dedik ki: "Her şeyden ikişer çifti ve aleyhlerinde hüküm verdiklerimiz haric olmak üzere aileni ve inananları gemiye yükle!" Zaten onunla beraber inanan pek azdı.'),
(1514, 11, 41, 'Haydi, gemiye binin, dedi. Onun akıp gitmesi de durması da Allah\'ın adıyledir. Rabbim, elbette bağışlayandır, esirgeyendir!'),
(1515, 11, 42, 'Gemi, onları dağlar gibi dalga(lar) arasından geçirirken Nuh, bir kenarda duran oğluna. "Yavrum, bizimle beraber bin, kafirlerle beraber olma!" diye seslendi.'),
(1516, 11, 43, '(Oğlu): "Beni sudan koruyacak bir dağa sığınacağım," dedi. (Nuh): "Bugün, Allah\'ın emrinden koruyacak hiçbir şey yoktur, ancak O\'nun acıdığı (kurtulur)." dedi. Ve aralarına dalga girdi, o da boğulanlardan oldu.'),
(1517, 11, 44, 'Ey yer, suyunu yut ve ey gök tut! denildi. Su azaldı, iş bitirildi. (Gemi) Cudi\'ye oturdu. "Haksızlık yapan kavim yok olsun!" denildi.'),
(1518, 11, 45, 'Nuh Rabbine seslendi: "Rabbim, dedi, oğlum benim ailemdendir. Senin sözün elbette haktır ve sen hakimmlerin hakimisin!"'),
(1519, 11, 46, '(Rabbi): "Ey Nuh, dedi, o senin ailenden değildir. O, yaramaz iş yaptı. Bilmediğin bir şeyi benden isteme. Sana cahillerden olmamanı öğütlerim!"'),
(1520, 11, 47, '(Nuh) dedi ki: "Rabbim, bilmediğim bir şeyi senden istemekten sana sığınırım. Eğer beni bağışlamaz, bana acımazsan ziyana uğrayanlardan olurum!"'),
(1521, 11, 48, 'Ey Nuh, denildi, sana ve seninle beraber bulunan ümmetlerden bir bölüme bizden selamet ve bolluklarla (gemiden) in. Ama öyle ümmetler de var ki, onları bir süre yaşatacağız, sonra onlara bizden acı bir azab dokunacaktır!'),
(1522, 11, 49, '(Ey Muhammed), bunlar sana vahyettiğimiz gayb haberlerindendir. Ne sen, ne de kavmin, daha önce bunları bilmiyordunuz. O halde sabret, sonuç korunanlarındır.'),
(1523, 11, 50, 'Ad(kavmin)e de kardeşleri Hud\'u (gönderdik): "Ey kavmim, dedi, Allah\'a kulluk edin, O\'ndan başka tanrınız yoktur. Siz sadece uyduruyorsunuz!"'),
(1524, 11, 51, 'Ey kavmim, ben sizden bunun için bir ücret istemiyorum. Benim ücretim, beni yaratana düşer. Aklınızı kullanmıyor musunuz?'),
(1525, 11, 52, 'Ey kavmim, Rabbinizden mağfiret dileyin, sonra O\'na tevbe edin (O\'na yönelin) ki gökten üzerinize bol bol rahmet göndersin, kuvvetinize kuvvet katsın. Suç işleyerek (Allah\'tan) yüz çevirmeyin!'),
(1526, 11, 53, 'Dediler ki: "Ey Hud, bize bir mu\'cize getirmedin. Biz senin sözünle tanrılarımızı terk edecek değiliz ve biz sana inanacak değiliz!"'),
(1527, 11, 54, '(Senin hakkında) seni tanrılarımızdan biri fena çarpmış!" demekten başka bir söz bulamıyoruz" Dedi ki: "Ben Allah\'ı şahid tutuyorum, siz de şahid olun ki, ben sizin (Allah\'a) ortak koştuğunuz şeylerden uzağım."'),
(1528, 11, 55, 'O(Allah)\'dan başka (taptığınız tanrılardan). Haydi hepiniz bana tuzak kurun, sonra bana hiç göz açtırmayın (elinizden ne gelirse yapın)!"'),
(1529, 11, 56, 'Ben, benim de Rabbim, sizin de Rabbiniz olan Allah\'a dayandım. Hiçbir canlı yoktur ki O, onun perçeminden tutmuş olmasın (onu dilediği gibi yönetmesin). Gerçekten Rabbim, doğru bir yol üzerindedir (O adildir, yanında kimse zulme uğramaz).'),
(1530, 11, 57, 'Eğer yüz çevirirseniz, artık ben size sunmakla görevlendirildiğim mesajı size duyurdum. Rabbim, sizin yerinize başka bir kavim de getirebilir. Siz O\'na hiçbir zarar veremezsiniz. Şüphesiz Rabbim, herşeyi koruy(up gözet)endir.'),
(1531, 11, 58, 'Emrimiz gelince Hud\'u ve onunla beraber inanmış olanları bizden bir rahmetle kurtardık; onları katı bir azabdan kurtardık.'),
(1532, 11, 59, 'İşte \'Ad (kavmi), Rablerinin ayetlerini inkar ettiler, peygamberlerine karşı geldiler ve her inatçı zorbanın emrine uydular.'),
(1533, 11, 60, 'Böylece hem bu dünyada, hem de kıyamet gününde peşlerine la\'net takıldı. İyi bilin, \'Ad (kavmi) Rablerini inkar ettiler; iyi bilin Hud\'un kavmi \'Ad, (Allah\'ın rahmetinden) uzak olsun (yok olup gitsin)!'),
(1534, 11, 61, 'Semud(kavmin)e de kardeşleri Salih\'i (gönderdik). Dedi ki: "Ey kavmim, Allah\'a kulluk edin, O\'ndan başka tanrınız yoktur! Sizi yerden inşa eden ve orada yaşatan O\'dur; O\'ndan mağfiret dileyin, sonra O\'na tevbe edin! Çünkü Rabbim yakındır, (du\'aları) kabul edendir."'),
(1535, 11, 62, 'Dediler ki: "Ey Salih, sen bundan önce bizim aramızda ümit beslenen kişi idin. Şimdi atalarımızın taptıklarına tapmaktan bizi men mi ediyorsun? Biz senin bizi çağırdığın şeyden şüphe içindeyiz, kuşkulanıyoruz!"'),
(1536, 11, 63, 'Ey kavmim, dedi, bakın ya ben Rabbimden bir kanıt üzerinde isem ve O, bana kendinden bir rahmet vermişse? Peki bu durumda O\'na karşı gelirsem beni Allah\'tan kim kurtarır? Sizin bana, ziyanımı artırmaktan başka bir katkınız olamaz!'),
(1537, 11, 64, 'Ey kavmim, işte şu, Allah\'ın devesi, size bir mu\'cizedir. Bırakın onu, Allah\'ın arzında yesin, ona bir kötülük dokundurmayın, yoksa sizi yakın bir azab yakalar!'),
(1538, 11, 65, 'Fakat onu kesip devirdiler. (Salih) dedi ki: "Yurdunuzda üç gün yaşayın, (sonra mahvolacaksınız); bu, yalan olmayan bir uyarıdır!"'),
(1539, 11, 66, 'Nihayet emrimiz gelince Salih\'i ve onunla beraber inanmış olanları, bizden bir rahmetle kurtardık, (onları) o günün zilletinden (kurtardık). İşte Rabbin öyle güçlü, öyle galiptir.'),
(1540, 11, 67, 'Zulmedenleri de o korkunç ses yakaladı, yurtlarında çöküp kaldılar.'),
(1541, 11, 68, 'Orada hiç şenlik kurmamış gibi oldular. İyi bilin ki Semud (kavmi), Rablerini inkar ettiler ve iyi bilin ki Semud (kavmi) def olup gittiler!'),
(1542, 11, 69, 'Elçilerimiz, İbrahim\'e müjde getirip "selam!" demişlerdi. O da "selam!" dedi; çok durmadan hemen (elçilere) kızarmış bir buzağı getirdi.'),
(1543, 11, 70, 'Ellerinin buzağıya uzanmadığını görünce durumlarını beğenmedi ve onlardan ötürü içinde bir korku duydu. "Korkma, dediler, biz Lut kavmine gönderildik."'),
(1544, 11, 71, 'Ayakta durmakta olan karısı, güldü. Biz de ona İshak\'ı müjdeledik. İshak\'ın ardından da Ya\'kub\'u.'),
(1545, 11, 72, 'Vay, dedi, ben bir kocakarı, bu kocam da bir pir iken doğuracak mıyım? Bu, cidden şaşılacak bir şey!'),
(1546, 11, 73, 'Dediler ki: "Allah\'ın işine mi şaşıyorsun? Allah\'ın rahmeti ve bereketleri sizin üzerinizdedir, ey ev halkı! O, övülmeğe layıktır, iyiliği boldur."'),
(1547, 11, 74, 'İbrahim\'den korku gidip kendisine sevinç gelince, Lut kavmi hakkında bizimle tartışmağa başladı (onlardan azabı kaldırmamızı veya hafifletmemizi rica ediyordu).'),
(1548, 11, 75, 'Çünkü İbrahim, gerçekten halimdir, içlidir, (Allah\'a) yüz tutup yalvarandır.'),
(1549, 11, 76, '(Melekler): "Ey İbrahim, dediler, bundan vazgeç (boşuna uğraşma). Zira Rabbinin emri gelmiştir. Mutlaka onlara, geri çevrilmez azab gelecektir!"'),
(1550, 11, 77, 'Elçilerimiz Lut\'a gelince onlar yüzünden kaygılandı. onlar için arşını daraldı (ne yapacağını şaşırdı): "Bu, çetin bir gündür!" dedi.'),
(1551, 11, 78, 'Daha önce de kötü işler yapmakta olan kavmi koşarak ona geldiler. (Lut): "Ey kavmim, dedi, işte kızlarım, onlar sizin için daha (güzel, daha) temiz! Allah\'tan korkun, konuklarımın içinde beni rezil etmeyin! İçinizde aklı başında bir adam yok mu sizin?"'),
(1552, 11, 79, 'Dediler ki: "Senin kızlarında bizim bir hakkımız olmadığını bilmişsindir. Ve sen bizim ne isteğimizi de pekala bilirsin!"'),
(1553, 11, 80, '(Lut): "Keşke sizi savacak gücüm olsaydı, yahut da çok sarp bir kaleye sığınabilseydim!" dedi.'),
(1554, 11, 81, '(Melekler) dediler ki: "Ey Lut, biz senin Rabbinin elçileriyiz. Onlar sana asla dokunamazlar. Gecenin bir kısmında aileni yürüt; içinizden karından başka hiç kimse geri dönüp bakmasın. Çünkü ötekilerine erişen (azab) ona da erişecektir. Başlarına gelecek azab zamanı, sabah vaktidir. Sabah da yakın değil mi?"'),
(1555, 11, 82, '(Azab) emrimiz gelince oranın üstünü altına getirdik, üzerine de taş yağdırdık: Çamurdan pişmiş, (azab için) hazırlanmış, istif edilmiş.'),
(1556, 11, 83, 'Rabbinin katında işaretlenmiş (taşlar). O, zalimlerden uzak değildir.'),
(1557, 11, 84, 'Medyen\'e de kardeşleri Şu\'ayb\'i (gönderdik): "Ey kavmim, dedi, Alah\'a kulluk edin, sizin O\'ndan başka tanrınız yoktur; ölçüyü ve tartıyı eksik yapmayın. Ben sizi bolluk içinde görüyorum ve ben sizin için kuşatıcı bir günün azabından korkuyorum!"'),
(1558, 11, 85, 'Ey kavmim, ölçüyü ve tartıyı tam dengeli yapın, insanların eşyasını eksik vermeyin ve yeryüzünde bozgunculuk yaparak kötülük etmeyin!'),
(1559, 11, 86, 'Eğer inanan insanlar iseniz, Allah\'ın bıraktığı (kar), sizin için daha hayırlıdır. Fakat ben sizin üzerinize bekçi değilim!'),
(1560, 11, 87, 'Ey Şu\'ayb, dediler, senin namazın mı sana, babalarımızın taptığı şeylerden, yahut mallarımız üzerinde dilediğimizi yapmaktan vazgeçmemizi emrediyor? Oysa sen, yumuşak huylu, akıllı(bir insan)sın!'),
(1561, 11, 88, 'Ey kavmim, dedi, bakın, ya ben Rabbimden bir kanıt üzerinde isem ve (O), bana kendinden güzel bir rızık vermişse? Ben size menettiğim şeylerde size aykırı davranmak istemiyorum. Sadece gücümün yettiği kadar düzeltmek istiyorum. Başarım ancak Allah(ınyardımı) iledir. Yalnız O\'na dayandım ve yalnız O\'na yönelirim!'),
(1562, 11, 89, 'Ey kavmim, bana karşı gelmeniz, sakın sizi Nuh kavminin, yahut Hud kavminin veyahut Salih kavminin başlarına gelenler gibi bir felakete uğratmasın! Lut kavmi henüz sizden uzak değildir.'),
(1563, 11, 90, 'Rabbinizden mağfiret dileyin, sonra O\'na tevbe edin! Doğrusu Rabbim çok esirgeyen, çok sevendir.'),
(1564, 11, 91, 'Dediler ki: "Ey Şu\'ayb, senin söylediklerinden çoğunu anlamıyoruz, biz seni içimizde zayıf görüyoruz. Kabilen olmasaydı seni mutlaka taşla(yarak öldürü)rdük! Senin bizim yanımızda hiçbir değerin yoktur!"'),
(1565, 11, 92, 'Ey kavmim, dedi, size göre kabilem Allah\'tan daha mı değerli ki O\'nu arkanıza at(ıp unut)tunuz? Şüphesiz Rabbim yaptıklarınızı kuşatıcıdır (herşeyinizi bilmektedir).'),
(1566, 11, 93, 'Ey kavmim, olduğunuz yerde (yaptığınızı) yapın, ben de yapıyorum. Yakında kime azabın gelip kendisini rezil edeceğini ve kimin yalancı olduğunu bileceksiniz. Gözetin, ben de sizinle beraber gözetmekteyim!'),
(1567, 11, 94, 'Emrimiz gelince, Şu\'ayb\'i ve onunla beraber inanmış olanları bizden bir acıma ile kurtardık; zulmedenleri de o korkunç ses yakaladı, yurtlarında çöküp kaldılar.'),
(1568, 11, 95, 'Sanki orada hiç şenlik kurmamışlardı! İyi bilin ki, Semud (kavmi) nasıl uzaklaşıp gittiyse Medyen halkı da öyle uzaklaşıp gitti.'),
(1569, 11, 96, 'Andolsun, Musa\'yı da ayetlerimizle ve açık bir delil ile gönderdik.'),
(1570, 11, 97, 'Fir\'avn\'a ve adamlarına. (Ama o insanlar), Fir\'avn\'ın buyruğuna uydular. Oysa, Fir\'avn\'ın buyruğu, doğruya iletici değildi.'),
(1571, 11, 98, '(Fir\'avn), kıyamet günü kavminin önünde gidiyor. İşte onları ateşe getirdi. O varılan yer de ne fena bir yerdir!'),
(1572, 11, 99, 'Bu dünyada da (onların) ardına la\'net takılmıştır, kıyamet gününde de (burada da la\'netle anılacaklardır, ahirette de)! Bu vergi, ne kötü bir vergidir!'),
(1573, 11, 100, '(Ey Muhammed), bu sana anlattıklarımız, o kentlerin haberlerinden(başlarına gelen olaylardan)dır. Onlardan kimi hala ayakta, kimi de biçilmiştir.'),
(1574, 11, 101, 'Biz onlara zulmetmedik. Fakat onlar, kendi kendilerine zulmediyorlardı. Rabbinin emri geldiği zaman, Allah\'tan başka yalvardıkları tanrıları, kendileriden hiçbir şeyi savamadı ve onların ziyanlarını artırmaktan başka bir işe yaramadı!'),
(1575, 11, 102, 'İşte Rabbin zulmeden kentleri yakaladığı zaman böyle yakalar. Doğrusu O\'nun yakalaması, çok acı ve çok çetindir.'),
(1576, 11, 103, 'Şüphesiz ahiret azabından korkanlar için, bunda elbette ibret vardır. O, bütün insanların toplandığı bir gündür ve o, görülecek bir gündür.'),
(1577, 11, 104, 'Biz onu, sadece sayılı bir süre için erteliyoruz.'),
(1578, 11, 105, 'O geldiği gün, hiç kimse O\'nun izni olmadan konuşamaz. O(raya toplana)nlardan kimi şaki (bahtsız), kimi sa\'id(mutlu)dur.'),
(1579, 11, 106, 'Bahtsızlar ateştedirler. Onların orada (o bunaltıcı ateş içinde) bir soluk alıp verişleri vardır ki!...'),
(1580, 11, 107, 'Gökler ve yer durdukça orada sürekli kalacaklardır. Meğer Rabbin, çıkmalarını dilemiş olsun. Çünkü Rabbin, istediğini yapandır.'),
(1581, 11, 108, 'Mutlu kılınanlar ise cennettedirler. Gökler ve yer durdukça orada sürekli kalacaklardır. Meğer Rabbin, çıkmalarını dilemiş olsun. Bu, Kesintisiz bir vergidir!.'),
(1582, 11, 109, 'Şunların taptıkları şeyler(in, yararsızlığın)dan hiç kuşkun olmasın. Onlar da önceden atalarının taptığı gibi tapıyorlar. Biz onların da paylarını eksiksiz vereceğiz!'),
(1583, 11, 110, 'Andolsun, Musa\'ya Kitabı verdik, onda da ayrılığa düşüldü. Rabbin, (süre tanıyacağına) söz vermemiş olsaydı, derhal aralarında hüküm verilmiş, (hak eden, cezasını bulmuş) olurdu. Onlar, bu(Kur\'a)n\'dan kuşkulu bir şüphe içindedirler.'),
(1584, 11, 111, 'Şüphesiz Rabbin, hepsinin işlerini(n karşılığını) tam verecektir. Çünkü Allah, yaptıklarını bilmektedir.'),
(1585, 11, 112, 'Öyleyse emrolunduğun gibi doğru ol; seninle beraber tevbe edenler de (doğru olsunlar), aşırı gitmeyiniz! Zira O, yaptıklarınızı görmektedir.'),
(1586, 11, 113, 'Sakın zulmedenlere dayanmayın, sonra size ateş dokunur. Sizin Allah\'tan başka dostlarınız yoktur. Sonra size yardım edilmez.'),
(1587, 11, 114, 'Gündüzün iki tarafında (sabah, akşam) ve geceye yakın sa\'atlerde namaz kıl; çünkü iyilikler, kötülükleri giderir. Bu, ibret alanlara bir öğüttür.'),
(1588, 11, 115, 'Sabret, çünkü Allah güzel davrananların ecrini zayi etmez.'),
(1589, 11, 116, 'Sizden önceki nesillerden akıllı kimselerin, yeryüzünde bozgunculuk yapmaktan men\'etmeleri gerekmez miydi? Fakat onlar arasından, ancak kendilerini kurtardığımız pek az kişi böyle yaptı. Zulmedenler ise kendilerine verilen refahın peşine düşüp şımardılar ve suç işleyenler olup çıktılar.'),
(1590, 11, 117, 'Halkı uslu kimseler olsaydı, Rabbin o kentleri, zulüm ile helak edecek değildi.'),
(1591, 11, 118, 'Rabbin dileseydi, insanları bir tek ümmet yapardı. Ama ihtilaf edip durmaktadırlar.'),
(1592, 11, 119, 'Yalnız Rabbinin acıdıkları (bu ihtilafın dışında kalmışlardır). Zaten (Allah) onları bunun için yaratmıştır. Rabbinin: "Andolsun, ben cehennemi hep cinlerden ve insanlardan bir kısmıyle dolduracağım!" sözü tam yerine gelmiştir.'),
(1593, 11, 120, 'Peygamberlerin haberlerinden, senin kalbini sağlamlaştıracak her şeyi sana anlatıyoruz. Bunda da sana hak ve inananlar için bir öğüt ve ibret gelmiştir.'),
(1594, 11, 121, 'İnanmayanlara de: "Olduğunuz yerde yapacağınızı yapın, biz de yapıyoruz!"'),
(1595, 11, 122, 'Bekleyin, biz de bekliyoruz!'),
(1596, 11, 123, 'Göklerin ve yerin gaybı (görünmez bilgisi), Allah\'a aittir. Bütün işler hep Allah\'a döndürülüp götürülür. O\'na kulluk et ve O\'na dayan. Rabbin sizin yaptıklarınızdan gafil değildir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Elif lam ra. Bunlar apaçık Kitabın ayetleridir.'),
(1598, 12, 2, 'Biz onu Arapça bir Kur\'an olarak indirdik ki anlayasınız.'),
(1599, 12, 3, 'Biz, bu Kur\'an\'ı vahyetmekle sana kıssaların en güzelini anlatıyoruz. Sen ondan önce (bunları) bilmeyenlerden idin.'),
(1600, 12, 4, 'Hani bir zaman Yusuf, babasına: "Babacığım demişti, ben (rü\'yada) on bir yıldız, güneşi ve ayı gördüm, bunların bana secde ettiklerini gördüm." demişti.'),
(1601, 12, 5, '(Babası Ya\'kub): "Yavrum, dedi, rü\'yanı kardeşlerine anlatma, sonra sana bir tuzak kurarlar. Çünkü şeytan, insanın apaçık düşmanıdır!'),
(1602, 12, 6, 'Böyece Rabbin seni seçecek ve sana düşlerin yorumundan bir parça öğretecek, sana ve Ya\'kub soyuna ni\'metini tamamlayacaktır; nasıl ki daha önce ataların İbrahim\'e ve İshak\'a da ni\'metini tamamlamıştı. Şüphesiz Rabbin, bilendir, hüküm ve hikmet sahibidir."'),
(1603, 12, 7, 'Andolsun, Yusuf ve kardeşlerin(in kıssaların)da, soranlar için ibretler vardır:'),
(1604, 12, 8, '(Kardeşleri) demişlerdi ki: "Yusuf ve (öz) kardeşi (Bünyamin), babamıza bizden daha sevgilidir. Oysa biz bir cemaatiz. Babamız açık bir yanlışlık içindedir!"'),
(1605, 12, 9, 'Yusuf\'u öldürün, ya da onu bir yere bırakın da babanızın yüzü yalnız size kalsın (bundan böyle babanız yalnız sizi görsün ve sevsin)! Ondan sonra da (tevbe eder), iyi bir topluluk olursunuz!'),
(1606, 12, 10, 'İçlerinden bir sözcü: "Yusuf\'u öldürmeyin, onu kuyunun dibine atın, kervanlardan biri onu (görüp) alsın; eğer yapacaksanız (böyle yapın)," dedi.'),
(1607, 12, 11, '(Bu fikirde karar kıldılar ve babalarına gelip) Dediler ki: "Ey babamız, neden Yusuf hakkında bize güvenmiyorsun, oysa biz ona öğüt verenler(onun iyiliğini isteyenler)iz?"'),
(1608, 12, 12, 'Yarın onu da bizimle beraber (kıra) gönder, gezsin, oynasın; biz onu elbette koruruz.'),
(1609, 12, 13, '(Ya\'kub) Dedi ki: "Onu götürmeniz beni üzer; korkarım ki, sizin haberiniz yokken onu kurt yer!"'),
(1610, 12, 14, 'Dediler ki: "Biz bir topluluk olduğumuz halde onu kurt yerse, o zaman biz tamamen beceriksiz kimseleriz, demektir!"'),
(1611, 12, 15, 'Nihayet onu götürüp de kuyunun dibine atmağa topluca karar verdikleri zaman biz, Yusuf\'a: "Andolsun sen onların bu işlerini, hiç farkında olmayacakları bir sırada kendilerine haber vereceksin!" diye vahyettik.'),
(1612, 12, 16, 'Akşamleyin ağlayarak babalarına geldiler.'),
(1613, 12, 17, 'Ey babamız, dediler, biz gittik, yarışıyorduk; Yusuf\'u yiyeceğimizin yanında bırakmıştık. Onu kurt yemiş! Ama biz doğru söylesek de sen bize inanmazsın!'),
(1614, 12, 18, '(Yusuf\'un) gömleğinin üstünde yalan kan getirdiler. (Ya\'kub): "Herhalde, dedi, nefisleriniz sizi aldatıp bir işe sürükledi. Artık tek çarem güzelce sabretmektir. Dediğinize (dayanmak için) ancak Allan\'tan yardım istenir!"'),
(1615, 12, 19, 'Bir kervan geldi, sucularını gönderdiler, (o da gidip kuyuya) kovasını sarkıttı: "Müjde, dedi, işte bir oğlan!" Onu ticaret için sakladılar, halbuki Allah, onların ne yaptıklarını biliyordu.'),
(1616, 12, 20, 'Nihayet (Mısır\'a varınca) onu düşük bir pahaya, birkaç paraya sattılar. Onlar, ona (Yusuf\'a) karşı isteksiz idiler.'),
(1617, 12, 21, 'Onu satın alan Mısır\'lı (hazine bakanı Kıtfir), karısı(Zeliha\'y)a: "Ona iyi bak, belki bize yararı dokunur, ya da onu evlad ediniriz!" dedi. Böylece biz Yusuf\'a o yerde güzel bir imkan verdik ki ona düşlerin yorumunu öğretelim. Allah, buyruğunu yerine getirendir, ama insanların çoğu bilmezler.'),
(1618, 12, 22, '(Yusuf), kuvvetli çağına erişince ona hüküm ve ilim verdik. İşte biz, güzel hareket edenleri böyle mükafatlandırırız.'),
(1619, 12, 23, 'Yusuf\'un, evinde kaldığı kadın, onun nefsinden murad almak istedi ve kapıları kilitleyip: "Haydi gelsene!" dedi (Yusuf): "Allah\'a sığınırım dedi, efendim bana güzel baktı. zalimler iflah olmazlar!"'),
(1620, 12, 24, 'Andolsun, kadın onu arzu etmişti, eğer Rabbinin doğruyu gösteren delilini görmeseydi o da onu arzu etmişti. Böylece biz kötülüğü ve fuhşu ondan çevirmek istedik; çünkü o, ihlasa erdirilmiş (temiz) kullarımızdandır.'),
(1621, 12, 25, 'Kapıya doğru koşuştular. Kadın, Yusuf\'un gömleğini arkadan yırttı. Kapının yanında kadının bey\'ine rastladılar. Kadın: "Senin ailene kötülük yapmak isteyenin cezası nedir? Zindana kapatılmak veya acı bir biçimde işkence edilmek değil midir?" dedi.'),
(1622, 12, 26, '(Yusuf): "O benden murad almak istedi!" dedi. Kadının ailesinden bir şahid de şöyle şahidlik etti: "Eğer Yusuf\'un gömleği önden yırtılmışsa kadın doğrudur, o yalancılardandır."'),
(1623, 12, 27, 'Ve eğer onun gömleği arkadan yırtılmışsa kadın yalancıdır, o doğrulardandır!'),
(1624, 12, 28, '(Kadının kocası, Yusuf\'un) gömleğinin arkadan yırtılmış olduğunu görünce (kadına): "Bu, sizin düzeninizdendir, dedi, gerçekten sizin düzeniniz büyüktür!"'),
(1625, 12, 29, 'Yusuf, sen bundan vazgeç (bunu kimseye söyleme), (ey kadın), sen de günahının bağışlanmasını dile! Çünkü sen, günahkarlardan oldun!'),
(1626, 12, 30, 'Şehirde birtakım kadınlar: Vezir\'in karısı, uşağının nefsinden murad almak istemiş! Sevda, onun bağrını yakmış! Biz onu açık bir sapıklık içinde görüyoruz!" dediler.'),
(1627, 12, 31, '(Kadın), onların (dedi-kodu yaparak kendisini dile düşürme) düzenlerini işitince, onlara (adam) gönderdi (yemeğe davet etti). Onlar için dayanacak yastıklar hazırladı ve her birine de birer bıçak verdi. (Yusuf\'a): "Çık karşılarına!" dedi. Kadınlar, (önlerine konan meyveleri soyup yemekle meşgul iken) Yusuf\'u görünce onu (gözlerinde) büyüttüler, (ona hayranlıklarından ötürü) ellerini kestiler ve: "Allah için, haşa bu, insan değildir; bu ancak güzel bir melektir!" dediler.'),
(1628, 12, 32, '(Kadın) Dedi ki: "İşte siz, beni bunun için kınamıştınız! Andolsun ben kendisinden murad almak istedim de o, iffetinden ötürü reddetti. Ama kendisine emrettiğimi yapmazsa, elbette zindana atılacak ve alçalanlardan olacaktır!"'),
(1629, 12, 33, '(Yusuf): "Rabbim dedi, bana göre zindan, bunların beni çağırdığı şeyden iyidir. Eğer onların düzenini benden savmazsan onlara kayarım ve cahillerden olurum!"'),
(1630, 12, 34, 'Rabbi onun du\'asını kabul buyurdu da onların düzenini ondan savdı. Şüphesiz O, işitendir, bilendir.'),
(1631, 12, 35, 'Sonra (aziz Kıtfir ve adamları, Yusuf\'un masumluğu hakkındaki) bu delilleri gördükleri halde yine onu bir süre zindana atmaları kendilerine uygun geldi.'),
(1632, 12, 36, 'Onunla beraber iki genç daha zindana girdi. Onlardan biri dedi ki: "Ben düşümde şarap sıktığımı görüyorum." Öteki de: "Ben de, görüyorum ki başımın üstünde ekmek taşıyorum, kuşlar ondan yiyor. Bunun yorumunu bize haber ver, zira biz seni güzel davranan(iyi rü\'ya yoran)lardan görüyoruz." dedi.'),
(1633, 12, 37, '(Yusuf) şöyle dedi: "Size rızık olarak verilen yemek henüz size gelmezden önce bunun yorumunu size haber vermiş olurum. Bu (yorum) Rabbimin bana öğrettiği şeylerdendir (bu bilgileri Rabbim bana lutfetti). Ben, Allah\'a inanmayan, ahireti de inkar eden bir kavmin dinini terk ettim:'),
(1634, 12, 38, 'Atalarım İbrahim, İshak ve Ya\'kub\'un dinine uydum. Bizim, herhangi bir şeyi Allah\'a ortak koşmağa hakkımız yoktur. Bu (tevhid), bize ve bütün insanlara Allah\'ın bir lutfudur, ama insanların çoğu şükretmezler.'),
(1635, 12, 39, 'Ey benim zindan arkadaşlarım, çeşitli tanrılar mı iyi, yoksa herşeyi (hükmü altında tutan) kahredici tek Allah mı? 	O my two fellow-prisoners! Are divers lords better, or Allah the One, the Almighty?'),
(1636, 12, 40, 'Siz, o\'nu bırakıp ancak sizin ve atalarınızın taktığı birtakım (boş) isimlere tapıyorsunuz. Allah onlar(ın gerçekliği) hakkında hiçbir delil indirmemiş(onlara hiçbir güç vermemiş)tir. Hüküm, yalnız Allah\'ındır. O, yalnız kendisine tapmanızı buyurmuştur. İşte doğru din budur. Ama insanların çoğu bilmezler."'),
(1637, 12, 41, 'Ey zindan arkadaşlarım, (rü\'yanıza gelince) biriniz (eskisi gibi) yine efendisine şarap sunacak, diğeri ise asılacak, kuşlar onun başından yiyecek. Sorduğunuz iş (bu şekilde) kesinleşmiştir.'),
(1638, 12, 42, 'O iki kişiden kurtulacağını sandığı kimseye: "Beni efendin(kralın)ın yanında an (benim suçsuz olduğumu krala hatırlat)" dedi. Fakat şeytan o adama, (Yusuf\'un durumunu) efendisine söylemeyi unutturdu, (bundan ötürü Yusuf), birkaç yıl zindanda kaldı.'),
(1639, 12, 43, '(Bir gün) Kral dedi ki: "Ben, düşümde yedi semiz inek görüyorum, bunları yedi zayıf inek yiyor. Ve yedi yeşil, yedi de kuru başak (görüyorum). Ey efendiler, eğer siz rü\'ya ta\'bir ediyorsanız bu rü\'yamın ta\'birini bana anlatın."'),
(1640, 12, 44, '(Yorumcular) dediler ki: "Bu, karışık düşlerden ibarettir. Biz, karışık düşlerin yorumunu bilmeyiz."'),
(1641, 12, 45, '(Zindandaki) İki kişiden kurtulan (adam), uzun bir süre sonra (bu olay üzerine Yusuf\'u) hatırladı da dedi ki: "Ben size onun yorumunu haber veririm, hemen beni (zindana) gönderin."'),
(1642, 12, 46, '(Zindana, Yusuf\'un yanına geldi, dedi ki): "Yusuf, ey çok doğru söyleyen, bize şu rü\'yayı çöz: Yedi semiz ineği, yedi zayıf (inek) yiyor ve yedi yeşil, yedi de kuru başak (neyi gösterir)? Umarım ki senin yorumunla insanlara dönerim, onlar da bilirler."'),
(1643, 12, 47, '(Yusuf) Dedi ki: "Siz, adetiniz üzere yedi yıl (ürün) ekersiniz. Biçtiğinizi başağında bırakırsınız, ancak yiyeceğiniz az bir mikdar(ı alırsınız, gerisini depolarsınız)."'),
(1644, 12, 48, 'Sonra onun ardından yedi kurak (yıl) gelir ki (tohumluk olarak) sakladığınız az miktar dışında, o yıllar için önceden biriktirdiklerinizi yeyip bitirir.'),
(1645, 12, 49, 'Sonra onun ardından bir yıl gelir ki, o yılda insanlara bol yağmur verilir ve insanlar o yıl (bol bol meyva) sıkarlar (hayvan sağarlar).'),
(1646, 12, 50, '(Elçi bu yorumu getirince) Kral: "Onu bana getirin." dedi. Elçi, Yusuf\'un yanına gelince (Yusuf): "Efendine dön de ona sor, ellerini kesen o kadınların maksadı neydi? (Bunu ortaya çıkarsın). Şüphesiz Rabbim, onların tuzaklarını biliyor", dedi.'),
(1647, 12, 51, '(Kral, kadınlara): "Yusuf\'un nefsinden murad almak istediğiniz zaman durumunuz neydi?" dedi. Dediler ki: "Haşa, Allah için (doğru söylemek lazım), biz onda hiçbir kötülük görmedik!" Aziz\'in karısı da: "İşte şimdi hak yerini buldu, ben onun nefsinden murad almak istemiştim. O tamamen doğrulardandır!" dedi.'),
(1648, 12, 52, '(Gerçeği söyledim ki Yusuf) Benim, arkadan kendisine hainlik etmediğimi ve Allah\'ın, hainlerin tuzağını başarıya ulaştırmayacağını bilsin."'),
(1649, 12, 53, 'Ben nefsimi temize çıkarmam. Çünkü nefis, daima kötülüğü emredicidir. Meğer Rabbimin esirgediği bir nefis ola. Rabbim bağışlayandır, esirgeyendir.'),
(1650, 12, 54, 'Kral: "Onu bana getirin, dedi, onu kendime özel (dost) yapayım!" Kendisiyle konuş(up ondaki olgunluğu gör)ünce (Yusuf\'a): "Sen, dedi, artık bugün yanımızda mevki sahibi, güvenilir(bir kimse)sin.'),
(1651, 12, 55, '(Yusuf, krala): "Beni ülkenin hazineleri üstüne bakan yap. Çünkü ben (onları) iyi korur, (yönetmesini) iyi bilirim." dedi.'),
(1652, 12, 56, 'Böylece biz Yusuf\'a o ülke\'de iktidar verdik. Orada dilediği yerde konaklardı. Biz, dilediğimiz kimseye rahmetimizi ulaştırırız, güzel davrananların ecrini zayi etmeyiz.'),
(1653, 12, 57, 'İnananlar ve (kötülüklerden) korunanlar için elbette ahiret ödülü, daha hayırlıdır.'),
(1654, 12, 58, 'Yusuf\'un kardeşleri geldiler, onun yanına girdiler, o onları tanıdı; fakat onlar onu tanımıyorlardı.'),
(1655, 12, 59, '(Yusuf) Onların (zahire) yüklerini hazırlatınca dedi ki: "Sizin baba bir kardeşinizi de bana getirin, görüyorsunuz ya ben, ölçüyü tam yapıyorum ve ben konukseverlerin en iyisiyim!"'),
(1656, 12, 60, 'Eğer onu bana getirmezseniz artık benim yanımda size ölçü(lüp verilecek bir şey) yok. (Bir daha) bana yaklaşmayın!'),
(1657, 12, 61, 'Dediler ki: "Onu babasından isteyip getirmeğe çalışacağız, (bunu) mutlaka yapacağız"'),
(1658, 12, 62, '(Yusuf) Uşaklarına: "Onların sermayelerini yüklerinin içine koyun, belki ailelerine döndükleri zaman bunun farkına varırlar da yine gelirler" dedi.'),
(1659, 12, 63, 'Babalarına döndüklerinde dediler ki: "Ey babamız, bizden ölçü men\'edildi, kardeşimizi bizimle beraber gönder de (ihtiyacımız olanı) ölç(üp al)alım. Biz onu mutlaka koruruz."'),
(1660, 12, 64, '(Yakup) dedi ki: "Daha önce kardeşi için size güvendiğim gibi onun için de size güveneyim, öyle mi? En iyi koruyan Allah\'tır ve O, merhametlilerin merhametlisidir!"'),
(1661, 12, 65, 'Zahire yüklerini açtıklarında sermayelerinin kendilerine geri verilmiş olduğunu gördüler. Dediler ki: "Ey babamız, daha ne istiyoruz? İşte sermayemiz de bize geri verilmiş! Yine ailemize yiyecek getiririz. Kardeşimizi koruruz, bir deve yükü de fazla (azık) alırız. (Çünkü) Bu, az bir ölçüdür (bize yetmez)."'),
(1662, 12, 66, '(Ya\'kub): "Hepiniz kuşatılıp engellenmedikçe siz, onu bana getireceğinize dair Allah adına bana sağlam söz vermeden onu asla sizinle göndermem!" dedi. Ne zaman ki, sözlerini verdiler, (Ya\'kub): "Söylediğimize Allah, vekildir!" dedi.'),
(1663, 12, 67, 'Ve dedi ki: "Oğullarım, (Mısır\'a) bir kapıdan girmeyin, ayrı ayrı kapılardan girin. Ben, Allah\'tan gelecek hiçbir şeyi sizden savamam. Hüküm, yalnız Allah\'ındır. (O size ne takdir etmişse muhakkak olacaktır.) Ben O\'na tevekkül ettim, tevekkül edenlerde O\'na tevekkül etsinler!"'),
(1664, 12, 68, 'Babalarının emrettiği yerden (Mısır\'a) girdiler; (gerçi) bu, Allah\'tan gelecek hiçbir şeyi onlardan savamazdı. Ama sadece Ya\'kub, içindeki bir dileği söylemişti. O, kendisine öğrettiğimizden ötürü bilgi sahibi idi (bundan dolayı \'Allah\'ın takdirinden hiçbir şeyi sizden savamam\' demişti). Fakat insanların çoğu bilmezler.'),
(1665, 12, 69, '(Kardeşleri), Yusuf\'un yanına girince, (Yusuf, öz) kardeşi(Bünyami)n\'i yanına aldı ve: "Ben senin kardeşinim, onların (bizim hakkımızda) yaptıklarına üzülme!" dedi.'),
(1666, 12, 70, 'Onların yüklerini hazırlatırken su tasını (öz) kardeşinin yükünün içine koydu. (Kervan hareket ettikten) sonra bir ünleyici şöyle seslendi: "Ey kervan, siz hırsızlarsınız!"'),
(1667, 12, 71, 'Bunlara döndüler: "Ne kaybettiniz, (ne arıyorsunuz)? dediler.'),
(1668, 12, 72, 'Dediler ki: "Kralın su tasını kaybettik (onu arıyoruz). Onu getirene bir deve yükü (mükafat) var. Ben buna kefilim"'),
(1669, 12, 73, '(Yusuf\'un kardeşleri): "Allah, Allah! dediler, herhalde siz de bilmişsinizdir ki biz bu yere bozgunculuk yapmak için gelmedik. Ve biz hırsız değiliz!"'),
(1670, 12, 74, '(Yusuf\'un adamları): "Peki, dediler, ya yalancı çıkarsanız o(hırsızlık ede)nin cezası nedir?"'),
(1671, 12, 75, 'Cezası, (tas) kimin yükünde bulunursa işte o, onun karşılığıdır. (Hırsızlığına karşılık kendisine el konur). Biz haksızları böyle cezalandırırız! dediler.'),
(1672, 12, 76, 'Bunun üzerine (Yusuf), kardeşinin yükünden önce ötekilerin yüklerini aramağa başladı; sonra tası kardeşinin yükünden çıkardı. İşte Yusuf\'a böyle bir çare öğrettik. Yoksa kralın dini(kanunu)na göre (Yusuf) kardeşini alamazdı. Meğer Allah dilemiş olsun. (Biz) dilediğimizi derecelerle yükseltiriz. Her bilgi sahibinin üstünde daha bir bilen vardır.'),
(1673, 12, 77, '(Yusuf\'un kardeşleri) Dediler ki: "(Bu) çaldıysa bundan önce kardeşi de çalmıştı." Yusuf bunu içinde sakladı, onlara açmadı. (İçinden): "Siz fena bir durumdasınız, Allah, sizin anlattığınızın içyüzünü çok iyi biliyor!" dedi.'),
(1674, 12, 78, 'Dediler ki: "Ey vezir, onun büyük bir ihtiyar babası var! (Onun alıkonduğuna çok üzülür.) Onun yerine (bizden) birimizi al; doğrusu, biz seni iyilik edenlerden görüyoruz."'),
(1675, 12, 79, 'Eşyamızı yanında bulduğumuz kimseden başkasını almaktan Allah\'a sığınırız, yoksa biz zulmedenler oluruz! dedi.'),
(1676, 12, 80, 'Ondan umudu kesince aralarında konuşmak üzere (bir kenara) çekildiler. Büyükleri dedi ki: "Babanızın sizden Allah adına kesin söz aldığını; daha önce de Yusuf hakkında işlediğiniz kusuru bilmiyor musunuz? Babam bana izin verinceye, yahut Allah benim için hükmedinceye kadar bu yerden ayrılmayacağım. O, hükmedenlerin en iyisidir."'),
(1677, 12, 81, 'Babanıza dönün, deyin ki: Ey babamız, oğlun hırsızlık etti! Biz ancak bildiğimize şahidlik ettik (tasın, onun yükünden çıktığını gördük, ötesini bilmiyoruz), Biz gizliyi bilenler değiliz.'),
(1678, 12, 82, '(İnanmazsan) İçinde bulunduğumuz kente ve beraber geldiğimiz kervana sor. Biz doğru söylüyoruz!"'),
(1679, 12, 83, '(Dönüp babalarına geldiler ve kardeşlerinin sözünü söylediler. Ya\'kub): "Herhalde, dedi, nefisleriniz size bir işi süs(leyerek sizi ona sürük)ledi. Artık (bana) güzelce sabretmek gerek. Belki de Allah, onların hepsini bana getirir. Çünkü O, bilendir, herşeyi hikmetle (yerli yerince) yapandır.'),
(1680, 12, 84, 'Ve yüzünü onlardan öteye çevirdi de: "Ey Yusuf üzerindeki tasam (gel, gel, tam senin gelme zamanındır)!" dedi ve tasadan gözleri ağardı. (Acısını) yutkunuyor(açığa vurmamağa çalışıyor)du.'),
(1681, 12, 85, 'Dediler ki: "Vallahi sen, Yusuf\'u ana ana hasta olacaksın, yahut öleceksin!"'),
(1682, 12, 86, 'Ben üzüntü ve tasamı yalnız Allah\'a arz ederim ve Allah tarafından, sizin bilmediğiniz şeyleri bilirim. dedi.'),
(1683, 12, 87, 'Ey oğullarım, gidin, Yusuf\'u ve kardeşini araştırın, Allah\'ın rahmetinden umut kesmeyin; zira kafir kavimden başkası Allah\'ın rahmetinden umut kesmez!'),
(1684, 12, 88, '(Ya\'kub\'un oğulları, tekrar Mısır\'a) Yusuf\'un yanına döndüklerinde dediler ki: "Ey vezir, bize ve çocuklarımıza darlık dokundu, değersiz de bir sermaye ile geldik, ama sen bizim için tam ölçü ver, bize tasadduk eyle; çünkü Allah, tasadduk edenleri mükafatlandırır."'),
(1685, 12, 89, '(Yusuf) Dedi: "Sizler cahil iken Yusuf\'a ve kardeşine neler yaptığınızı bildiniz mi?"'),
(1686, 12, 90, 'A, yoksa sen, Yusuf musun? dediler. "Ben Yusuf\'um, bu da kardeşimdir, dedi. Allah bize lutfetti (bizi korudu, yüceltti), doğrusu kim (Allah\'tan) korkar ve sabrederse, Allah iyilik edenlerin ecrini zayi etmez."'),
(1687, 12, 91, 'Vallahi dediler, Allah seni bizden üstün kıldı. Doğrusu biz suç işlemiştik!'),
(1688, 12, 92, 'Bugün sizi kınama yok, Allah sizi bağışlar; O merhametlilerin merhametlisidir! dedi.'),
(1689, 12, 93, 'Şimdi benim şu gömleğimi götürün, babamın yüzüne koyun da gözü açılsın. Ve bütün ailenizle birlikte bana gelin.'),
(1690, 12, 94, 'Kervan (Mısır\'dan) ayrıl(ıp yola koyul)unca, babaları, (yanında bulunanlara): "Eğer bana bunak demezseniz, ben Yusuf\'un kokusunu alıyorum." dedi.'),
(1691, 12, 95, 'Vallahi sen hala eski şaşkınlığın içindesin! dediler.'),
(1692, 12, 96, 'Müjdeci gelip de (Yusuf\'un gömleği)ni (Ya\'kub\'un) yüzüne koyunca, derhal (gözü açıldı), görür oldu: "Size demedim mi ben, Allah\'tan sizin bilmediğiniz şeyleri bilirim?" dedi.'),
(1693, 12, 97, '(Oğulları): "Ey babamız, bizim günahlarımızın bağışlanmasını dile. Gerçekten biz günah işledik." dediler.'),
(1694, 12, 98, 'Sizin için Rabbimden mağfiret dileyeceğim, dedi, şüphesiz O, bağışlayandır, esirgeyendir.'),
(1695, 12, 99, '(Hep beraber Mısır\'a hareket ettiler). Nihayet Yusuf\'un yanına vardıklarında (Yusuf) ana-babasını kendine çekip kucakladı ve: "Allah\'ın dileğiyle güven içinde Mısır\'a girin!" dedi.'),
(1696, 12, 100, 'Ana-babasını tahtın üstüne çıkardı ve hepsi onun için secdeye kapandılar (önünde saygı ile eğildiler. Yusuf): "Babacığım, dedi, işte bu, önceden (gördüğüm) rü\'yanın yorumudur. Rabbim onu gerçek yaptı, bana iyilik etti; zira şeytan, benimle kardeşlerim arasına fitne soktuktan sonra O, beni zindandan çıkardı, sizi de çölden getirdi. Gerçekten Rabbim dilediği şeyi çok ince düzenler. O, (her tedbiri) bilen, her şeyi yerli yerince yapandır."'),
(1697, 12, 101, 'Rabbim, bana bir parça mülk verdin ve bana düşlerin yorumunu öğrettin. Ey göklerin ve yerin yaratıcısı! dünyada da, ahirette de benim yarim sensin! Beni müslüman olarak öldür ve beni iyilere kat!'),
(1698, 12, 102, '(Ey Muhammed) bu (anlatılanlar), sana vahyettiğimiz gayb haberlerindendir. Onlar kararlarnı verip tuzak kurarlarken sen yanlarında değildin.'),
(1699, 12, 103, 'Ama sen, ne kadar istesen de, yine insanların çoğu inanacak değillerdir.'),
(1700, 12, 104, 'Sen bu(okudukları)na karşılık onlardan bir ücret istemiyorsun. O, sadece bütün alemler için bir öğüttür.'),
(1701, 12, 105, 'Göklerde ve yerde nice ayet(ler) var ki onların yanından yüzlerini çevirerek geçerler.'),
(1702, 12, 106, 'Onların çoğu, Allah\'a ortak koşmadan inanmazlar.'),
(1703, 12, 107, 'Onlar, Alah\'ın azabından, sargın bir belanın, kendilerine gelmeyeceğinden veya hiç farkında değillerken ansızın O (Duruşma) sa\'atin(in) kendilerine gelmeyeceğinden emin midirler?'),
(1704, 12, 108, 'De ki: "İşte benim yolum budur: Allah\'a basiretle da\'vet ederim. Ben ve bana uyanlar... Allah\'ın şanı yücedir, ben ortak koşanlardan değilim."'),
(1705, 12, 109, 'Senden önce de kentler halkından, yalnız kendilerine vahyettiğimiz erkeklerden başka, (elçi) göndermedik. Yeryüzünde hiç gezmediler mi ki kendilerinden öncekilerin sonunun nasıl olduğunu görsünler? Korunanlar için ahiret yurdu daha iyidir. Aklınızı kullanmıyor musunuz?'),
(1706, 12, 110, '(Bir süre serbest bırakılmalarına aldanmasınlar. Kendilerinden önce gelenlere de öyle fırsat verilmişti. Fakat) Ne zaman ki, elçiler umutlarını kestiler ve kendilerinin yalana çıkarıldıklarını (kafirlere karşı kendilerine yapılacağı va\'dedilen yardımın yapılmayacağını) sandılar, işte o zaman onlara yardımımız geldi ve dilediğimiz kimseler kurtarıldı. Azabımız suçlular topluluğundan asla geri çevrilmez.'),
(1707, 12, 111, 'Elbette onların hikayelerinde akıl sahipleri için ibret vardır. Bu (Kur\'an), uydurulacak bir söz değildir; ancak kendinden önceki(Hak Kitabı)nın doğrulanması, her şeyin açıklaması; inananlar için bir kılavuz ve rahmettir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Elif lam mim ra. Şunlar Kitabın ayetleridir; Rabbinden, sana indirilen haktır, fakat insanların çoğu inanmazlar.'),
(1709, 13, 2, 'Allah odur ki gökleri, görebileceğiniz bir direk olmadan yükseltti, sonra Arş üzerine istiva etti (mülkünün tahtına oturdu), güneşi ve ay\'ı iradesine boyun eğdirdi. Her biri, belli bir süre için akıp gitmektedir. (Yaratma) işi(ni) düzenler, ayetleri açıklar ki, Rabbinizle karşılaşacağınıza kesin olarak inanasınız.'),
(1710, 13, 3, 'O\'dur ki arzı uzattı, orada sabit dağlar ve ırmaklar var etti, orada her meyvadan iki çift (erkek-dişi) yarattı. Geceyi gündüzün üzerine örter. Şüphesiz bunda düşünen bir toplum için ayetler vardır.'),
(1711, 13, 4, 'Arzda birbirine komşu kıt\'alar, üzüm, bağları, ekinler, çatallı ve çatalsız hurmalıklar vardır; bunların hepsi bir su ile sulanır ama ürünlerini birbirinden üstün yaparız. Şüphesiz bunda, aklını kullanan bir toplum için ayetler vardır.'),
(1712, 13, 5, 'Eğer şaşacaksan, onların şu sözlerine şaşmak lazım: "Biz toprak olduğumuz zaman mı, biz mi yeniden yaratılacağız?" İşte onlar, Rablerine karşı nankörlük edenlerdir. Ve Onlar, boyunlarında halkalar bulunan kimselerdir, onlar ateş halkıdır. Onlar orada sürekli kalacaklardır.'),
(1713, 13, 6, 'Senden, iyilikten önce kötülüğü acele istiyorlar. Oysa onlardan önce benzerleri(ne nice cezalar) gelip geçti. (Niçin onlardan ibret almazlar?) Şüphesiz Rabbin, o(insa)nların zulümlerine karşı mağfiret sahibidir, fakat Rabbinin azabı da pek çetindir.'),
(1714, 13, 7, 'İnkar edenler diyorlar ki: "Ona Rabbinden bir ayet indirmeli değil miydi?" Sen, ancak bir uyarıcısın, her toplumun bir yol göstericisi vardır.'),
(1715, 13, 8, 'Allah, her dişinin neyi yüklendiğini ve Rahimlerin neyi eksiltip artırdığını bilir. Onun yanında her şey, bir miktar iledir.'),
(1716, 13, 9, '(O), gizliyi ve aşikareyi bilendir, büyüktür, yücedir.'),
(1717, 13, 10, 'Aranızdan sözü gizleyen de, onu açık söyleyen de, geceleyin gizlenen de, gündüzün görünen de (O\'nca) birdir.'),
(1718, 13, 11, 'O(insa)nın önünden ve arkasından izleyen(melek)ler vardır, onu Allah\'ın emrinden korurlar. Bir milet kendi durumlarını değiştirmedikçe Allah onların durumlarını değiştirmez. Allah da bir kavme kötülük istedi mi artık onu geri çevirecek yoktur. Zaten onların, O\'ndan başka koruyucuları da yoktur.'),
(1719, 13, 12, 'O\'dur ki size, korku ve umud içinde şimşeği gösterir. (Yağmurla yüklü) ağır, ağır bulutlar yapar.'),
(1720, 13, 13, 'Gök gürültüsü, övgüsüyle, melekler de korkusundan O\'nu tesbih ederler. Yıldırımlar gönderir de onlarla dilediğini çarpar. Allah\'ın tuzağı (cezası) pek çetin olduğu halde, onlar hala O\'nun hakkında tartışmaktadırlar.'),
(1721, 13, 14, 'Gerçek du\'a, ancak O\'na yapılır. O\'ndan başka du\'a ettikleri ise, kendilerinin hiçbir isteklerini karşılayamazlar. (Onların durumu) tıpkı ağzına gelsin diye suya avuçlarını uzatan kimse gibidir. Oysa (uzanıp suyu avuçlamadıkça su) on(un ağzın)a gelmez. İşte kafirlerin du\'ası, öyle boşa gider.'),
(1722, 13, 15, 'Göklerde ve yerde olanların hepsi, ister istemez Allh\'a secde ederler. Gölgeleri de sabah akşam (uzanıp kısalarak O\'na secde etmektedirler).'),
(1723, 13, 16, 'De ki: "Göklerin ve yerin Rabbi kimdir?" De ki: "Allah!", "O halde, de, O\'ndan başka kendilerine dahi bir fayda ve zarar veremeyen veliler mi edindiniz?" De ki: "Körle gören, yahut karanlıklarla aydınlık bir olur mu?" Yoksa Allah\'a, O\'nun yarattığı gibi yaratan ortaklar mı buldular da, (ikisinin) yaratma(sı) onlara, benzer mi göründü? De ki: "Her şeyin yaratıcısı Allah\'tır. O, tektir, kahreden(herşeye üstün gelen)dir."'),
(1724, 13, 17, 'Gökten bir su indirdi de dereler kendi ölçüsünce (o su ile) çağlayıp aktı. Sel üste çıkan köpüğü taşıdı. Süs, yahut eşya yapmak için ateşte yak(ıp erit)tikleri madenlerden de bunun gibi bir köpük (posa) vardır. Allah, hak ve batılı böyle benzetme ile anlatır. Köpük yok olup gider. İnsanlara yararlı olan ise yeryüzünde kalır. İşte Alllah, böyle güzel meseller verir.'),
(1725, 13, 18, 'Rablerinin buyruğuna uyanlara, en güzel karşılık vardır. Ona uymayanlar ise, yeryüzünde bulunan her şey ve bunun bir misli daha kendilerinin olsa, (Allah\'ın azabından kurtulmak için) onu fidye verirlerdi. Onların hesabı çok kötüdür. Varacakları yer de cehennemdir, ne kötü bir yataktır o!'),
(1726, 13, 19, 'Rabbinden sana indirilenin hak olduğunu bilen kimse, (bunu kabul etmeyen) kör gibi olur mu? Ancak sağduyu sahipleri öğüt alır.'),
(1727, 13, 20, 'Onlar, Allah\'ın ahdini yerine getirirler ve andlaşmayı bozmazlar.'),
(1728, 13, 21, 'Ve onlar Allah\'ın bitiştirilmesini istediği şeyi bitiştirirler. Rablerine karşı saygılı olur ve en kötü hesaptan korkarlar.'),
(1729, 13, 22, 'Ve onlar Rablerinin yüzünü (rızasını) arzu ederek (nefsin gücüne giden şeylere) sabrederler; namazı kılarlar, kendilerine verdiğimiz rızıktan gizli ve açık olarak (hayır yoluna) harcarlar ve kötülüğü iyilikle savarlar. İşte şu yurdun sonucu onlarındır:'),
(1730, 13, 23, '(Onlar) Adn cennetlerine girerler. Babalarından, eşlerinden ve çocuklarından iyi olanlar da kendileriyle beraber olur. Melekler de her kapıdan yanlarına varırlar:'),
(1731, 13, 24, 'Sabretmenize karşılık selam size, yurdun sonu ne güzel! (derler).'),
(1732, 13, 25, 'Ama Allah\'a verdikleri sözü iyice pekiştirdikten sonra bozanlar ve Allah\'ın bitiştirilmesini istediği şeyi kesenler ve yeryüzünde bozgunculuk yapanlar... İşte la\'net onlara, yurdun kötü sonucu da onlaradır.!'),
(1733, 13, 26, 'Allah, dilediğine rızkı açar da, kısar da. Dünya hayatıyle sevindiler. Oysa ahiretin yanında dünya hayatı, bir geçimden ibarettir.'),
(1734, 13, 27, 'İnkar edenler: "Ona Rabbinden bir ayet indirilmeli değil miydi?" diyorlar. De ki: "Allah, dilediğini (bu tür sözlerle) saptırır. Yöneleni de kendisine iletir."'),
(1735, 13, 28, 'Onlar inanan ve Allah\'ı anmakla gönülleri huzur bulan kimselerdir. İyi bilin ki gönüller, ancak Allah\'ı anmakla huzur bulur.'),
(1736, 13, 29, 'İşte mutluluk ve güzel gelecek, o inanıp güzel işler yapanlarındır.'),
(1737, 13, 30, 'Seni de böylece, kendilerinden önce nice milletler geçmiş bulunan bir millete gönderdik ki, sana vahyettiğimizi onlara okuyasın. Oysa onlar Rahman\'a nankörlük ederler. De ki: "O (Rahman), benim Rabbimdir. O\'ndan başka tanrı yoktur. O\'na dayandım, tevbem yalnız O\'nadır."'),
(1738, 13, 31, 'Eğer kendisiyle dağların yürütüldüğü, yahut arzın parçalandığı, yahut ölülerin konuşturulduğu bir Kur\'an olsaydı!.. Hayır, bütün işler Allah\'a aittir. İnananlar hala anlamadılar mı ki, Allah dileseydi, bütün insanları yola iletirdi? Yaptıkları işler yüzünden inkar edenlerin başlarına ani bela(lar) gelmeğe devam edecek, yahut yurtlarının yakınına konacak (yahut sen onların yurtlarının yakınına konacaksın), Allah\'ın va\'di gelinceye kadar bu böyle sürüp gidecektir. Allah sözünden caymaz.'),
(1739, 13, 32, 'Senden önceki peygamberlerle alay edildi de inkar edenlere bir süre meydan verdim, sonra onları yakaladım. Cezam nasılmış, (gördüler)!'),
(1740, 13, 33, 'Her nefsin yaptığı işin başında duran, (hiçbir şeyden haberi olmayanla bir olur) mu? Onlar Allah\'a ortaklar koştular. De ki: "Onları isimlendirin (nitelendirin bakalım tapılmağa değer bir yanları var mı?) Yoksa siz Allah\'ın, yeryüzünde bilmediği bir şeyi mi Kendisine haber veriyorsunuz? Yoksa boş söz mü söylüyorsunuz? Hayır, inkar edenlere tuzakları süslü gösterildi. (Hak) yoldan çıkarıldılar. Allah kimi şaşırtırsa artık ona yol gösteren olmaz!'),
(1741, 13, 34, 'Dünya hayatında onlar için azab vardır, ahiret azabı ise daha zordur. Onları Allah(ın azabın)dan koruyacak kimse de yoktur.'),
(1742, 13, 35, '(Allah\'ın emirlerine karşı gelmekten) Korunanlara va\'dedilen cennetin durumu şöyledir: Altından ırmaklar akar; yemişi de süreklidir, gölgesi de. İşte korunanların sonu budur. İnkar edenlerin sonu da ateştir.'),
(1743, 13, 36, 'Kendilerine Kitap verdiğimiz kimseler, sana indirilene sevinirler. Fakat kabilelerden onun bir kısmını inkar edenler vardır. De ki: "Bana, yalnız Allah\'a kulluk etmem ve O\'na hiçbir şeyi ortak koşmamam emredildi. Ben (insanları) O\'na da\'vet ederim, dönüşüm de O\'nadır."'),
(1744, 13, 37, 'Ve işte biz onu, Arapça bir hüküm (hikmet gereğince hükmeden bir Kitap) olarak indirdik. Eğer sana gelen bu ilimden sonra onların keyiflerine uyarsan, artık seni Allah\'tan kurtaracak ne bir veli ne de koruyucu olmaz.'),
(1745, 13, 38, 'Andolsun, biz senden önce de elçiler gönderdik, onlara da eşler ve çocuklar verdik. Allah\'ın izni olmadan hiçbir elçi, bir ayet (mu\'cize) getiremezdi. Her sürenin bir yazısı vardır (herşeyin zamanı yazılıp tesbit edilmiştir).'),
(1746, 13, 39, 'Allah, dilediğini siler, (dilediğini) bırakır. Ana Kitap O\'nun yanındadır.'),
(1747, 13, 40, 'Ya onları uyardığımız şeylerin bir kısmını sana gösteririz, ya da (bundan önce) senin canını alırız (fark etmez). Sana düşen, sadece duyurmaktır. Hesap görmek bize düşer.'),
(1748, 13, 41, 'Bizim o toprağa gelip nasıl onu, uçlarından eksilttiğimizi görmediler mi? Hüküm veren Allah\'tır. O\'nun hükmünün ardına düşüp onu iptal edecek yoktur. O, hesabı çabuk görendir.'),
(1749, 13, 42, 'Onlardan öncekiler de tuzak kurmuştu. Fakat bütün tuzaklar, (tedbirler) Allah\'ındır. (Allah\'ın tedbiri, onların tuzaklarını bozar. O), her canın ne kazandığını (ne yaptığını) bilir. Kafirler de, bu yurdun sonunun kimin olacağını bileceklerdir!'),
(1750, 13, 43, 'İnkar edenler: "Sen gönderilmiş bir elçi değilsin!" diyorlar. De ki: "Benimle sizin aranızda Allah\'ın ve yanında Kitap bilgisi bulunanların şahid olması yeter.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Elif lam ra. (Bu,) Rablerinin izniyle insanları karanlıklardan aydınlığa çıkarıp o güçlü ve övgüye layık olan(Allah)ın yoluna iletmen için sana indirdiğimiz Kitaptır.'),
(1752, 14, 2, 'O Allah ki, göklerde ve yerde olanların hepsi O\'nundur. Çetin azabdan dolayı vay şu kafirlerin haline!'),
(1753, 14, 3, 'Ki onlar, dünya hayatını ahirete tercih ederler, Allah\'ın yoluna engel olur ve onun eğrilmesini isterler. İşte onlar, derin bir sapıklık içindedirler.'),
(1754, 14, 4, 'Biz, her elçiyi kendi kavminin diliyle gönderdik ki olara açıklasın. Allah dilediğini şaşırtır, dilediğini yola iletir. O, azizdir, hüküm ve hikmet sahibidir.'),
(1755, 14, 5, 'Andolsun biz, Musa\'yı da "Kavmini karanlıklardan aydınlığa çıkar, onlara Allah\'ın günlerini (geçmiş milletlerin başlarına gelen olayları) hatırlat!" diye ayetlerimizle birlikte göndermiştik. Şüphesiz bunda sabreden, şükreden herkes için ayetler (ibret verici işaretler) vardır.'),
(1756, 14, 6, 'Musa, kavmine demişti ki: "Allah\'ın üzerinizdeki ni\'metini hatırlayın, O sizi Fir\'avn soyundan kurtardı. Onlar sizi işkencenin en kötüsüne koşuyorlar, oğullarınızı kesiyorlar, kadınlarınızı sağ bırakıyorlardı. Bunda Rabbinizden size büyük bir imtihan vardı.'),
(1757, 14, 7, 'Ve Rabbiniz size şöyle bildirmişti: "Andolsun şükrederseniz elbette size daha fazla veririm ve eğer nankörlük ederseniz azabım pek çetindir."'),
(1758, 14, 8, 'Ve Musa dedi ki: "Siz ve yeryüzünde bulunanlar hep nankörlük etseniz, iyi bilin ki Allah zengindir, övülmüştür (sizin şükrünüze muhtaç değildir)".'),
(1759, 14, 9, 'Sizden öncekilerin: Nuh, \'Ad ve Semud kavimlerinin ve onlardan sonra gelenlerin -ki onları(n sayısını) Allah\'tan başka kimse bilmez- haberi size gelmedi mi? Elçileri onlara kanıtlar getirdi de onlar, ellerini ağızlarına koydu (öfkelerinden parmaklarını ısırdı)lar (yahut: peygamberlerin ağızlarını tuttular): "Biz sizinle gönderilen mesajı tanımadık ve biz sizin bizi çağırdığınız şeye karşı derin bir kuşku içindeyiz!" dediler.'),
(1760, 14, 10, 'Elçileri: "Gökleri ve yeri yaratan Allah hakkında şüphe (edilir) mi? (O), sizin günahlarınızdan bir kısmını bağışlamak ve sizi belirtilmiş bir süreye kadar ertelemek için sizi davet ediyor" dediler. Onlar: "Siz de bizim gibi bir insandan başka bir şey değilsiniz. Bizi, atalarımızın taptığından çevirmek istiyorsunuz. O halde bize açık bir delil getirin!" dediler.'),
(1761, 14, 11, 'Elçileri onlara dediler ki: "Evet biz de sizin gibi insandan başka bir şey değiliz. Fakat Allah, kullarından dilediğine lutfeder. Allah\'ın izni olmadan biz size delil getiremeyiz. İnananlar, Allah\'a dayansınlar."'),
(1762, 14, 12, 'Bize yollarımızı göstermişken neden biz Allah\'a dayanmayalım? Sizin bize yaptığınız eziyetlere katlanacağız. Tevekkül edenler, Allah\'a dayansınlar.'),
(1763, 14, 13, 'İnkar edenler, elçilerine dediler ki: "Ya sizi mutlaka yurdumuzdan çıkarırız, ya da bizim dinimize dönersiniz!" Rableri de onlara şöyle vahyetti, "zalimleri mutlaka helak edeceğiz!"'),
(1764, 14, 14, 'Ve onların ardından sizi o yere yerleştireceğiz. Bu, makamımdan korkan ve tehdidimden korkan için(verdiğim söz)dür.'),
(1765, 14, 15, '(Elçiler, düşmanlarına karşı Allah\'tan) fetih istediler ve her inatçı zorba perişan oldu.'),
(1766, 14, 16, 'Ardından da kendisine irin (gibi) bir suyun içirileceği cehennem vardır.'),
(1767, 14, 17, 'O suyu yutmağa çalışır, fakat boğazından geçiremez ve her yandan ona ölüm geldiği halde yine ölemez. Bunun ardından da kaba bir azab!'),
(1768, 14, 18, 'Rablerine karşı nankörlük edenlerin iyi işleri, tıpkı fırtınalı bir günde rüzgarın savurduğu küle benzer. Kazandıklarından hiçbir şeyi ele geçiremezler. İşte derin sapıklık budur!'),
(1769, 14, 19, 'Allah\'ın, gökleri ve yeri hak(ve hikmet) ile yarattığını görmedin mi? Dilerse sizi götürür ve yepyeni bir halk getirir.'),
(1770, 14, 20, 'Bu, Allah\'a güç değildir.'),
(1771, 14, 21, 'Hepsi Allah\'ın huzurunda göründüler. Zayıflar, büyüklük taslayan(önder)ler(in)e: "Biz size tabi idik. Şimdi siz, bizden Allah\'ın azabından (en ufak) bir şey savabilir misiniz?" dediler. (Büyüklük taslayanlar kendilerini ma\'zur göstermek için: "Ne yapalım?") dediler: "Allah bize yol gösterseydi, biz de size yol gösterirdik. Artık biz sızlansak da, sabretsek de birdir; kaçıp sığınacak bir yerimiz yoktur!"'),
(1772, 14, 22, 'İş bitirildikten sonra şeytan (onlara) şöyle dedi: "Allah size gerçek va\'detti, ben de size va\'dettim ama ben sözümden caydım! Benim sizi zorlayacak bir gücüm yoktu. Sadece sizi (küfür ve isyana) davet ettim. Siz de benim da\'vetime koştunuz. O halde beni kınamayın, kendi kendinizi kınayın! Ne ben sizi kurtarabilirim, ne de siz beni kurtarabilirsiniz! Ben, önceden beni (Allah\'a) ortak koşmanızı da tanımamıştım zaten. Doğrusu zalimler için acı bir azab vardır!"'),
(1773, 14, 23, 'İnanıp iyi işyer yapanlar da Rablerinin izniyle sürekli kalacakları, altlarından ırmaklar akan cennetlere sokuldular. Onların orada dirlik temennileri "selam"dır.'),
(1774, 14, 24, 'Görmedin mi Allah nasıl bir benzetme yaptı: Güzel söz, kökü (yerde) sabit, dalları gökte olan güzel bir ağaç gibidir.'),
(1775, 14, 25, '(O ağaç), Rabbinin izniyle her zaman yemişini verir. Allah, öğüt almaları için insanlara böyle benzetmeler yapar.'),
(1776, 14, 26, 'Kötü sözün durumu da gövdesi yerin üstünden koparılmış, sabit olmayan kötü bir ağaca benzer.'),
(1777, 14, 27, 'Allah, inananları, dünya hayatında da, ahirette de sağlam sözle tesbit eder. Allah, zalimleri de şaşırtır ve Allah, dilediğini yapar.'),
(1778, 14, 28, 'Baksana şunlara, Allah\'ın ni\'metini nankörlüğe çevirdiler (O\'nun verdiği ni\'mete şükredecekleri yerde nankörlük edip inkara saptılar), kavimlerini de helak yurduna kondurdular.'),
(1779, 14, 29, 'Yaslanacakları cehenneme (götürdüler). Ne kötü bir duraktır o!'),
(1780, 14, 30, 'Allah\'ın yolundan saptırmak için O\'na eşler koştular. De ki: "(Şimdilik) eğlenin, gideceğiniz yer ateştir!"'),
(1781, 14, 31, 'İnanan kullarıma söyle: Namazı kılsınlar, ne alışverişin, ne de dostluğun olmadığı bir gün gelmeden önce, kendilerine verdiğimiz rızıktan gizli ve açık sarfetsinler.'),
(1782, 14, 32, 'Allah O\'dur ki, gökleri ve yeri yarattı, gökten su indirdi ve onunla size rızık olarak çeşitli meyvalar çıkardı. Buyruğuyla denizde akıp gitmesi için gemileri emrinize verdi, ırmakları emrinize verdi.'),
(1783, 14, 33, 'Sürekli olarak (seyir ve aydınlatma) görevlerini yapan güneşi ve ay\'ı emrinize verdi, geceyi ve gündüzü de emrinize verdi.'),
(1784, 14, 34, 'Ve kendisinden istediğiniz herşeyden size bir parça verdi. Eğer Allah\'ın ni\'metini saymak isteseniz sayamazsınız! (Doğrusu) insan çok haksızlık edendir, çok nankördür!'),
(1785, 14, 35, 'Bir zaman İbrahim, şöyle demişti: "Rabbim, bu şehri güvenli kıl, beni ve oğullarımı putlara tapmaktan uzak tut!"'),
(1786, 14, 36, 'Rabbim, onlar insanlardan birçoğunu şaşırttılar. Artık bundan böyle kim bana uyarsa o bendendir, kim bana karşı gelirse (o da senin merhametine kalmıştır), şüphesiz sen bağışlayan, esirgeyensin.'),
(1787, 14, 37, 'Rabbimiz, ben çocuklarımdan bazısını, senin Haram Evinin yanında, ekinsiz bir vadiye yerleştirdim. Rabbimiz, namazı kılsınlar diye (böyle yaptım). Artık sen de insanlardan birtakım gönüllüleri, onları sever yap ve onları çeşitli meyvalarla besle ki şükretsinler.'),
(1788, 14, 38, 'Rabbimiz, sen bizim gizlediğimizi ve açığa vurduğumuzu hep bilirsin. Ne yerde, ne de gökte hiçbir şey Allah\'a gizli kalmaz.'),
(1789, 14, 39, 'İhtiyarlık çağımda bana İsma\'il ve İshak\'ı lutfeden Allah\'a hamdolsun. Şüphesiz Rabbim du\'ayı işiten(kabul buyuran)dır.'),
(1790, 14, 40, 'Rabbim, beni ve zürriyetimden bir kısmını namazı kılan yap; Rabbimiz, du\'amı kabul buyur!'),
(1791, 14, 41, 'Rabbimiz, hesabın görüleceği gün, beni, anamı-babamı ve mü\'minleri bağışla!'),
(1792, 14, 42, 'Zalimlerin yaptığından Allah\'ı gafil sanma, O, sadece onları, gözlerin dehşetten donup kalacağı bir güne ertelemektedir.'),
(1793, 14, 43, '(O gün) başlarını dikerek koşarlar, bakışları kendilerine dönmez, (öyle donup kalmıştır sanki). Yüreklerinin içi de bomboş havadır. (Şaşkınlıktan, kafalarında düşünce adına bir şey kalmamıştır).'),
(1794, 14, 44, 'İnsanları, kendilerine azabın geleceği şu güne karşı uyar ki, zalimler: "Rabbimiz, derler, bizi yakın bir süreye kadar ertele de senin çağrına gelelim, elçilere uyalım! "Peki, önceden sizin için hiç zeval olmadığına (sürekli yaşayacağınıza) yemin etmemiş miydiniz?"'),
(1795, 14, 45, '(Sizden önce \'Ad ve Semud gibi) kendilerine yazık eden milletlerin yerlerinde oturmuştunuz, onlara nasıl yaptığımız, size belli olmuştu ve size benzetmeler de yapıp anlatmıştık (değil mi?)"'),
(1796, 14, 46, 'Onlar tuzaklarını kurdular. Oysa tuzakları dağları yerinden kaldıracak (cinsten) olsa bile onların tuzakları, Allah\'ın yanındaydı (Allah onların tuzaklarını bozar, cezalarını verirdi).'),
(1797, 14, 47, 'Sakın, Allah\'ı, elçilerine verdiği sözden cayar, sanma! Çünkü Allah daima üstündür, öc alandır!'),
(1798, 14, 48, 'O gün yer başka yere, gökler de (başka göklere) değiştirilir. (Bütün) insanlar tek ve kahredici Allah\'ın huzurunda görünürler.'),
(1799, 14, 49, 'Ve o gün suçluları, birbirine (veya elleri ayaklarına) yaklaştırılarak zincirlere vurulmuş görürsün!'),
(1800, 14, 50, 'Gömlekleri katrandandır, yüzlerini de ateş kaplamaktadır.'),
(1801, 14, 51, 'Allah, her canı kazandığiyle cezalandırmak için (böyle yapar). Şüphesiz Allah, hesabı çabuk görendir.'),
(1802, 14, 52, 'Bu (Kur\'an), insanlara bir tebliğdir. (İnsanlar), bununla uyarılsınlar; O\'nun yalnız Tek tanrı olduğunu bilsinler ve sağduyu salipleri öğüt alsınlar diye (gönderilmiştir).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Elif lam ra. Şunlar Kitabın ve apaçık Kur\'an\'ın ayetleridir.'),
(1804, 15, 2, 'Bir zaman gelir ki nankörlük edenler, "Keşke müslüman olsaydılar" diye arzu ederler.'),
(1805, 15, 3, 'Bırak onlar yesinler, eğlensinler; arzu onları oyalasın. Yakında (yaptıklarının kötü sonucunu) bileceklerdir.'),
(1806, 15, 4, 'Biz hiçbir kenti yok etmedik ki, onun mutlaka bilinen bir yazısı olmasın (helak ettiğimiz her ülkenin yazılmış, tesbit edilmiş bir süresi vardır. O süre dolunca onları yok etmişizdir).'),
(1807, 15, 5, 'Hiçbir millet ne süresini geçebilir, ne de (ondan) geri kalır (her kavim mutlaka, kendileri için belirtilmiş sürede helak olur).'),
(1808, 15, 6, 'Dediler ki: "Ey kendisine Zikir (Kitap) indirilmiş olan, sen mutlaka cinlenmişsin!"'),
(1809, 15, 7, 'Eğer doğrulardansan, bize melekleri getirsene!'),
(1810, 15, 8, 'Biz, melekleri ancak hak ile (hikmet gereğince) indiririz, o zaman da kendilerine asla göz açtırılmaz, (derhal işleri bitirilir, mahvolup giderler).'),
(1811, 15, 9, 'O Zikri (Kitap)ı biz indirdik biz; ve O\'nun koruyucusu da elbette biziz!'),
(1812, 15, 10, 'Andolsun, senden önceki milletlerin kolları içine de elçiler gönderdik.'),
(1813, 15, 11, 'Onlara hiçbir elçi gelmezdi ki, onunla alay etmesinler.'),
(1814, 15, 12, 'İşte biz o(Tanrı Zikri)ni suçluların kalblerine böyle sokarız.'),
(1815, 15, 13, 'Kendilerinden öncekilerin sünneti (inkarcıların mahvedileceği yasası) geçtiği halde yine de ona inanmazlar.'),
(1816, 15, 14, 'Onlara gökten bir kapı açsak da oraya çıkacak olsalardı:'),
(1817, 15, 15, 'Herhalde gözlerimiz döndürüldü, biz büyülenmiş bir topluluğuz, derlerdi.'),
(1818, 15, 16, 'Andolsun biz, gökte burçlar yaptık. Ve onu bakanlar için süsledik.'),
(1819, 15, 17, 'Ve onu, her recim (taşlanmış, kovulmuş uydurma sözler atan) şeytandan koruduk.'),
(1820, 15, 18, 'Ancak kulak hırsızlığı eden olursa, onu da parlak bir ışın kovalar.'),
(1821, 15, 19, 'Arzı da yaydık, oraya sağlam dağlar attık ve orada ölçülü mütenasib şeyler bitirdik.'),
(1822, 15, 20, 'Orada sizin için ve (beslediğinizi sandığınız, fakat aslında) sizin beslemediğiniz kimseler için geçimlikler var ettik.'),
(1823, 15, 21, 'Hiçbir şey yoktur ki onun hazineleri, bizim yanımızda olmasın, ama biz onu, bilinen bir miktar ile indiririz.'),
(1824, 15, 22, 'Rüzgarları, aşılayıcı olarak gönderdik de gökten su indirdik, böylece sizi suladık. Onu depolayan siz değilsiniz.'),
(1825, 15, 23, 'Biziz, elbette biz ki, yaşatır, öldürürüz; gerçek varis olan da biziz (her faninin mülkü bize geçer. Ölmeyen, daima kalan yalnız biziz).'),
(1826, 15, 24, 'Andolsun, sizden önce geçenleri de bildik, sonra gelenleri de bildik.'),
(1827, 15, 25, 'Gerçekten onları toplayacak olan, Rabbindir. O hükümdardır bilendir.'),
(1828, 15, 26, 'Andolsun biz insanı pişmemiş çamurdan, değişmiş cıvık balçıktan yarattık.'),
(1829, 15, 27, 'Cinne gelince onu da (insandan) daha önce, (vücudun gözeneklerine) nüfuz eden kavurucu ateşten yarattık.'),
(1830, 15, 28, 'Bir zaman Rabbin meleklere demişti ki: "Ben kupkuru çamurdan, değişken balçıktan bir insan yaratacağım!"'),
(1831, 15, 29, 'Onu düzenle(yip insan şekline koydu)ğum ve ona ruhumdan üflediğim zaman hemen ona secdeye kapanın!'),
(1832, 15, 30, 'Meleklerin hepsi topluca secde ettiler.'),
(1833, 15, 31, 'Yalnız İblis, secde edenlerle beraber olmayı kabul etmedi.'),
(1834, 15, 32, '(Allah): "Ey İblis, nen var ki, sen secde edenlerle beraber olmadın?" dedi.'),
(1835, 15, 33, '(İblis): "Ben bir çamurdan, değişken bir balçıktan yarattığın insana secde edemem!" dedi.'),
(1836, 15, 34, '(Allah): "Öyleyse çık oradan (meleklerin içinden çık), dedi, çünkü sen kovuldun!"'),
(1837, 15, 35, 'Ta ceza gününe kadar üzerine la\'net edilecektir!'),
(1838, 15, 36, '(İblis): "Rabbim, dedi bari tekrar dirilecekleri güne kadar beni(m canımı almayı) ertele!"'),
(1839, 15, 37, '(Allah): "Haydi, dedi, sen ertelenmişlerdensin!"'),
(1840, 15, 38, 'O bilinen vaktin gününe kadar!'),
(1841, 15, 39, '(İblis): "Rabbim, dedi, beni azdırmandan ötürü andolsun ki, ben de yer yüzünde onlara (günahları) süsleyeceğim ve onların hepsini azdıracağım.'),
(1842, 15, 40, 'Ancak içlerinden kendilerine ihlas verilen kulların hariç. (Benim azdırmam, onları etkilemez.)'),
(1843, 15, 41, '(Allah) buyurdu ki: "İşte bana varan doğru yol budur."'),
(1844, 15, 42, 'Benim halis kullarıma karşı senin bir gücün yoktur. Ancak sana uyan azgınlar(ı azdırabilirsin).'),
(1845, 15, 43, 'Cehennem o (şeytana uya)nların hepsinin buluşma yeridir.'),
(1846, 15, 44, 'Onun yedi kapısı vardır. Her kapıya, onlardan bir bölüm ayrılmıştır.'),
(1847, 15, 45, '(Şeytana uymaktan, küfür ve isyandan) korunanlar ise cennetlerde, pınar başlarındadırlar.'),
(1848, 15, 46, '(Onlara): "Oraya esenlikle, güven içinde girin!" (denilir).'),
(1849, 15, 47, 'Onların göğüslerindeki kini çıkarıp atmışızdır; (hepsi) kardeşler olarak divanlar üzerinde karşı karşıya oturur (sohbet eder)ler.'),
(1850, 15, 48, 'Orada onlara hiçbir yorgunluk dokunmaz ve onlar oradan çıkarılacak da değillerdir.'),
(1851, 15, 49, '(Ey Muhammed), kullarıma haber ver: İşte ben öyle bağışlayan, öyle esirgeyenim.'),
(1852, 15, 50, 'Fakat benim azabım da çok acı bir azabdır.'),
(1853, 15, 51, 'Onlara İbrahim\'in konuklarından haber ver;'),
(1854, 15, 52, 'Onun yanına girmişler: "Selam" demişlerdi. O da: "Biz sizden korkuyoruz." dedi.'),
(1855, 15, 53, 'Korkma dediler, biz sana bilgin bir çocuk(un olacağını) müjdeleriz!'),
(1856, 15, 54, 'Bana ihtiyarlık dokunduktan sonra mı beni müjdelediniz? Ne tuhaf bir şey ile müjdeliyorsunuz beni? dedi.'),
(1857, 15, 55, 'Sana gerçeği müjdeledik, umut kesenlerden olma! dediler. 	They said: We bring thee good tidings in truth. So be not thou of the despairing.'),
(1858, 15, 56, 'Sapıklardan başka kim Rabbinin rahmetinden umut keser? dedi.'),
(1859, 15, 57, '(İbrahim gelenlerin Hak elçileri melekler olduklarını anlayınca): "Ey elçiler, dedi, işiniz nedir?"'),
(1860, 15, 58, 'Biz suç işleyen bir kavme gönderildik, dediler.'),
(1861, 15, 59, 'Yalnız Lut ailesi suçlu değildir. Biz onların hepsini kurtaracağız."'),
(1862, 15, 60, 'Ancak karısı hariç. Onun da (suçlularla beraber) kalanlardan olmasını uygun gördük.'),
(1863, 15, 61, 'Elçiler Lut ailesine geldiklerinde:'),
(1864, 15, 62, '(Lut): "Siz hiç tanınmamış kimselersiniz!" dedi.'),
(1865, 15, 63, 'Dediler ki: "Doğrusu, biz onların, hakkında şüphe ettikleri((tanrı azabı)nı sana getirdik,"'),
(1866, 15, 64, 'Sana gerçeği getirdik, biz elbette doğru söyleyenleriz!'),
(1867, 15, 65, 'Hemen gecenin bir parçasında aileni yürüt, sen de arkalarından git, içinizden hiç kimse ardına dönüp bakmasın. Emredildiğiniz yere gidin!'),
(1868, 15, 66, 'Ona: "Şunlar sabaha girerlerken arkaları kesilecektir!" buyruğunu bildirdik.'),
(1869, 15, 67, '(Lut kavminin oturduğu Sodom) Kent(inin) halkı, (Lut\'un genç konuklarını duyup) sevinerek geldiler.'),
(1870, 15, 68, '(Lut onlara): "Bunlar benim konuğumdur, dedi, beni mahcubetmeyin!"'),
(1871, 15, 69, '(Ne olur), Allah\'tan korkun, beni rezil etmeyin!'),
(1872, 15, 70, 'Seni alemlerden (başkalarının işine karışmaktan) menetmemiş miydik? dediler.'),
(1873, 15, 71, 'Eğer yapacaksanız, işte kızlarım. dedi.'),
(1874, 15, 72, 'Senin ömrüne andolsun ki, onlar, sarhoşlukları içinde bocalıyorlardı.'),
(1875, 15, 73, 'Güneşin doğma zamanına girerlerken korkunç ses onları yakaladı.'),
(1876, 15, 74, 'O kentin üstünü altına getirdik ve üzerlerine de çamurdan pişmiş taşlar yağdırdık.'),
(1877, 15, 75, 'Şüphesiz bunda işaretten anlayanlara (nice) ibretler vardır.'),
(1878, 15, 76, 'Ve o (kent, herkesin gelip geçtiği) bir yol üzerinde durmaktadır.'),
(1879, 15, 77, 'Elbette bunda inananlar için bir ibret vardır.'),
(1880, 15, 78, 'Gerçekten Eyke halkı da zalim kimselerdi.'),
(1881, 15, 79, 'Onlardan da öcümüzü aldık, her ikisi de (Sodom da, Eyke de) hala (yol üzerinde, gözler) ön(ün) de apaçık durmaktadır.'),
(1882, 15, 80, 'Andolsun Hicr halkı (Semud kavmi) de peygamberleri yalanladılar.'),
(1883, 15, 81, 'Onlara ayetlerimizi verdik, ama onlardan yüz çeviriyorlardı.'),
(1884, 15, 82, 'Dağlardan güvenli evler yontuyorlardı.'),
(1885, 15, 83, 'Sabaha girerlerken onları da (o) korkunç ses yakaladı.'),
(1886, 15, 84, 'Kazandıkları, kendilerinden hiçbir şeyi savamadı.'),
(1887, 15, 85, 'Biz gökleri yeri ve bunlar arasında bulunanları hak ile yarattık; (gerçeğin ortaya çıkacağı) o sa\'at, mutlaka gelecektir! Şimdi sen güzel bir hoşgörü ile hareket et.'),
(1888, 15, 86, 'Yaratan, bilen ancak Rabbindir..'),
(1889, 15, 87, 'Andolsun sana ikililerden yedi ve bu büyük Kur\'an\'ı verdik.'),
(1890, 15, 88, 'Onlardan bazı çiftlere (sınıflara) verdiğimiz dünyalığa gözlerini dikme ve (sana inanmadıkları için) onlara üzülme. Mü\'minlere kanadını indir, (onlara karşı mütevazi, şefkatli davran).'),
(1891, 15, 89, 'Ve: "Ben, ancak ben, apaçık bir uyarıcıyım!" de.'),
(1892, 15, 90, '(Siz bilirsiniz, inanmazsanız Allah\'ın azabı başınıza inecektir.) Tıpkı o bölücülere (veya and içenlere) indirdiğimiz gibi (sizin başınıza da azab indiririz)!'),
(1893, 15, 91, 'Onlar ki Kur\'an\'ı bölük bölük ettiler.'),
(1894, 15, 92, 'Senin Rabbin hakkı için biz onların hepsine mutlaka soracağız:'),
(1895, 15, 93, 'Yaptıkları şeylerden.'),
(1896, 15, 94, 'Sen emrolunduğun şeyi açıkça söyle ve ortak koşanlara aldırma.'),
(1897, 15, 95, 'O alay edenlere karşı biz sana yeteriz.'),
(1898, 15, 96, 'O, Allah ile beraber başka tanrı tutanlar, yakında (yaptıklarının sonucunu) bileceklerdir!'),
(1899, 15, 97, 'Andolsun onların söylediklerine senin göğsünün daraldığını (canının sıkıldığını) biliyoruz.'),
(1900, 15, 98, 'Sen Rabbini hamd ile tesbih et (O\'nu övecek sözlerle an, subhanallahi velhamdulillah de) ve secde edenlerden ol.'),
(1901, 15, 99, 'Ve Rabbine kulluk et ki sana yakin gelsin (kesin bilgiye eresin)!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Allah\'ın emri geldi, artık onu acele istemeyin. Allah, onların ortak koştukları şeylerden uzaktır, yücedir.'),
(1903, 16, 2, 'Melekleri, kullarından dilediğine, emrinden olan ruh (vahy) ile indirir: "(İnsanları) Benden başka tanrı yoktur, benden korkun! diye uyarın!" (der).'),
(1904, 16, 3, '(Allah), gökleri ve yeri hak ile (hikmeti uyarınca) yarattı. (O), onların ortak koştuklarından yücedir.'),
(1905, 16, 4, 'İnsanı nutfe(sperm)den yarattı, birden o (insan) konuşkan bir karşı koyucu olup çıktı.'),
(1906, 16, 5, 'Hayvanları da yarattı. Onlarda sizin için ısınma(nızı sağlayan şeyler) ve daha birçok yararlar vardır. Ve onlardan kimini de yersiniz.'),
(1907, 16, 6, 'Ve akşamleyin mer\'adan getirdiğiniz, sabahleyin mer\'aya götürdüğünüz zaman onlarda sizin için bir güzellik de vardır. (Onların gidiş gelişleri size ayrı bir güzellik ve zevk verir.)'),
(1908, 16, 7, 'Ağırlıklarınızı öyle (uzak) şehirlere taşırlar ki, (onlar olmasa) canlar(ınız), büyük zahmetler çekmeden oraya varamazdınız. Doğrusu Rabbiniz, çok şefkatli, çok acıyandır.'),
(1909, 16, 8, 'Binmeniz ve süs için atları, katırları ve merkepleri (yarattı) ve daha sizin bilmediğiniz nice şeyler yaratmaktadır.'),
(1910, 16, 9, 'Kısa ve doğru yolu Allah gösterir. Ama o yoldan sapan da var. Allah dileseydi, hepinizi doğru yola iletirdi.'),
(1911, 16, 10, 'O\'dur ki, sizin için gökten bir su indirdi. İçeceğ(iniz) ondandır ve hayvanları otlattığınız ağaç(lar, bitkiler) ondan(sulanıp filizlenmekte)dir.'),
(1912, 16, 11, 'Onunla size ekin, zeytin, hurma, üzümler ve her çeşit meyvalardan bitirmektedir. Şüphesiz bunda düşünen bir toplum için ibret vardır.'),
(1913, 16, 12, 'Geceyi, gündüzü, güneşi ve ay\'ı sizin hizmetinize verdi. Yıldızlar da O\'nun emriyle (size) boyun eğdirilmiştir. Şüphesiz bunda aklını kullanan bir toplum için ibretler vardır.'),
(1914, 16, 13, 'Yeryüzünde yarattığı çeşitli renklerdeki(hayvanları, bitki)leri de (sizin hizmetinize verdi). Şüphesiz bunda öğüt alan bir toplum için ibret vardır.'),
(1915, 16, 14, 'O, denizi de (hizmetinize) verdi ki ondan taptaze et yiyesiniz ve ondan kuşanacağınız süsler çıkarasınız. Görüyorsun ki gemiler, denizi yara yara akıp gitmektedir. Allah\'ın lutfunu aramanız ve O\'na şükretmeniz için.'),
(1916, 16, 15, 'Sizi sarsar diye arza ağır baskılar attı, ırmaklar ve yollar yaptı ki doğru yolu bulasınız (amaçlarınıza eresiniz).'),
(1917, 16, 16, '(Yol bulmak için yararlanılacak) işaretler de (yarattı). Onlar yıldız(lar)la da yol bulurlar.'),
(1918, 16, 17, 'Yaratan, yaratmayan gibi midir? Hiç düşünmüyor musunuz?'),
(1919, 16, 18, 'Eğer Allah\'ın ni\'metini saysanız, sayamazsınız. Doğrusu Allah çok bağışlayan, çok esirgeyendir.'),
(1920, 16, 19, 'Allah, gizlediğiniz ve açığa vurduğunuz her şeyi bilir.'),
(1921, 16, 20, 'Allah\'tan başka yalvardıkları (tanrılar), hiçbir şey yaratamazlar, zaten kendileri yaratılmaktadırlar.'),
(1922, 16, 21, 'Onlar ölüdürler, diri değildirler. Ne zaman dirileceklerini de bilmezler.'),
(1923, 16, 22, 'Tanrınız bir tek Tanrıdır. Ama ahirete inanmayanların kalbleri inkarcıdır, onlar büyüklük taslarlar.'),
(1924, 16, 23, 'Gerçekten Allah, onların gizlediklerini de bilir, açığa vurduklarını da. O, büyüklük taslayanları sevmez.'),
(1925, 16, 24, 'Onlara: "Rabbiniz ne indirdi?" dendiği zaman, "Evvelkilerin masalları!" derler.'),
(1926, 16, 25, 'Ki kıyamet günü hem kendi veballerini tam olarak yüklensinler, hem de bilgisizce saptırdıkları kimselerin veballerinden bir kısmını. Bak, ne kötü şey yükleniyorlar!'),
(1927, 16, 26, 'Onlardan öncekiler de tuzak kurmuşlardı da Allah, binalarına temellerinden gelmiş, üstlerindeki tavan, başlarına çökmüştü! Ve azab onlara ummadıkları yerden gelmişti.'),
(1928, 16, 27, 'Sonra kıyamet günü de, onları rezil eder ve "Hani haklarında (mü\'minlere) düşmanlık ettiğiniz ortaklarım nerede?" der. Kendilerine ilim verilmiş olanlar: "Bugün rezillik ve kötülük kafirleredir!" derler.'),
(1929, 16, 28, 'Nefislerine zulmederlerken meleklerin, canlarını aldığı kimseler; "Biz hiçbir kötülük yapmıyorduk!" diye teslim olurlar. "Hayır, Allah sizin yaptıklarınızı biliyor."'),
(1930, 16, 29, 'Onun için, içinde sürekli kalmak üzere cehennemin kapılarına girin. Kibirlenenlerin yeri ne kötüdür!'),
(1931, 16, 30, '(Allah\'ın azabından) korunanlara da: "Rabbiniz ne indirdi?" dendi. "Hayır (indirdi)." dediler. Bu dünyada güzel iş yapanlara güzellik vardır, (onlar için) ahiret yurdu ise daha hayırlıdır. Korunanların yurdu ne güzeldir.'),
(1932, 16, 31, 'Altlarından ırmaklar akan adn cennetlerine girerler. Orada onlar için diledikleri her şey vardır. İşte Allah, korunanları böyle mükafatlandırır.'),
(1933, 16, 32, 'Melekler, iyi insanlar olarak canlarını aldığı kimselere de: "Selam size, yaptıklarınıza karşılık cennete girin!" derler.'),
(1934, 16, 33, '(İnkar edenler) İlle kendilerine meleklerin gelmesini, yahut Rabbinin (azab) emrinin gelmesini mi bekliyorlar? Onlardan öncekiler de öyle yapmıştı. Allah onlara zulmetmedi, fakat onlar kendi kendilerine zulmediyorlardı.'),
(1935, 16, 34, 'Nihayet yaptıklarının kötülükleri onlara ulaştı ve alay ettikleri şey onları kuşattı.'),
(1936, 16, 35, '(Allah\'a) ortak koşanlar, "Allah dileseydi ne biz, ne de atalarımız O\'ndan başka bir şeye tapmazdık ve O\'nsuz hiçbir şeyi haram kılmazdık!" dediler. Onlardan öncekiler de böyle yapmıştı. Elçilere düşen, yalnız açıkça tebliğ etmek değil midir?'),
(1937, 16, 36, 'Andolsun biz, her millet içinde: "Allah\'a kulluk edin, şeytan(a tapmak)dan kaçının" diye bir elçi gönderdik. Onlardan kimine Allah hidayet etti, onlardan kimine de sapıklık gerekli oldu. İşte yeryüzünde gezin de bakın, yalanlayanların sonu nasıl olmuş!'),
(1938, 16, 37, '(Ey Muhammed) Sen onların yola gelmelerini ne kadar istesen de Allah şaşırttığını yola getirmez ve onların yardımcıları da olmaz!'),
(1939, 16, 38, '(Onlar), yeminlerinin bütün şiddetiyle: "Allah ölen kimseyi diriltmez!" diye Allah\'a yemin ettiler. Hayır diriltecektir, bu, O\'nun gerçek olarak verdiği sözdür. Ama insanların çoğu bilmezler.'),
(1940, 16, 39, '(Diriltecektir ki) Hakkında ihtilaf ettikleri gerçeği onlara açıklasın ve inkar edenler de yalancı olduklarını bilsinler.'),
(1941, 16, 40, 'Biz bir şeyi(n olmasını) istediğimiz zaman, söyleyeceğimiz söz, sadece ona "ol" dememizdir, derhal oluverir.'),
(1942, 16, 41, 'Kendilerine zulmedildikten sonra Allah uğrunda göç edenleri, dünyada güzelce yerleştireceğiz, (onlara vereceğimiz) ahiret mükafatı ise daha büyüktür. Keşke bilseler!'),
(1943, 16, 42, 'Onlar ki sabrettiler ve Rablerine dayanmaktadırlar.'),
(1944, 16, 43, 'Biz senden önce de, kendilerine vahyettiğimiz erkeklerden başkasını elçi göndermedik. Sorun, Zikir ehline; eğer bilmiyorsanız:'),
(1945, 16, 44, 'Açık kanıtları ve Kitapları. Sana da o Zikr\'i indirdik ki, kendilerine indirileni insanlara açıklayasın, ta ki düşünüp öğüt alsınlar.'),
(1946, 16, 45, 'Kötülükler yapmayı kuranlar, Allah\'ın, kendilerini yer(in dibin)e geçirmeyeceğinden, yahut hiç ummadıkları bir yerden kendilerine azabın gelmeyeceğinden emin midirler?'),
(1947, 16, 46, 'Yahut dönüp dolaşırlarken oun, kendilerini yakalamayacağından (emin midirler)? Kendileri buna engel olacak değillerdir!'),
(1948, 16, 47, 'Yahut (azabın) kendilerini bir korku üzerinde yakalamayacağından (emin midirler)? Doğrusu Rabbiniz, çok şefkatli, çok acıyandır!'),
(1949, 16, 48, 'Allah\'ın yarattığı şeylerin gölgelerinin dahi nasıl sağdan, soldan sürünüp Allah\'a secde ederek döndüğünü görmediler mi? (Her şeyin gölgesi yerde uzanıp kısalarak hep Allah\'a secde etmektedir).'),
(1950, 16, 49, 'Göklerde ve yerde bulunan canlıların, meleklerin hepsi Allah\'a secde ederler, onlar asla büyük taslamazlar.'),
(1951, 16, 50, 'Üstlerindeki Rablerinden korkarlar ve emredildikleri şeyi yaparlar.'),
(1952, 16, 51, 'Allah: "İki tanrı tutmayın. O, ancak tek Tanrıdır. Yalnız benden korkun!" dedi.'),
(1953, 16, 52, 'Göklerde ve yerde ne varsa hepsi O\'nundur. Kulluğun da yalnız O\'na yapılması lazımdır. Siz, Allah\'tan başkasından mı korkuyorsunuz?'),
(1954, 16, 53, 'Size ulaşan her ni\'met Allah\'tandır. Sonra size bir sıkıntı dokunduğu zaman da yalnız O\'na yalvarırsınız.'),
(1955, 16, 54, 'Sonra, sizden o sıkıntıyı kaldırdığı zaman içinizden bir grup, hemen Rablerine ortak koşarlar.'),
(1956, 16, 55, 'Ki kendilerine verdiğimiz (ni\'metlere ve yaptığımız iyiliğ)e karşı nankörlük etsinler. Öyleyse eğlenin, yakında bileceksiniz!'),
(1957, 16, 56, 'Kendilerine verdiğimiz rızıktan, (hiçbir şey) bilmeyen(tanrı)lar(ın)a pay ayırıyorlar. Allah\'a andolsun ki siz, bu uydurduğunuz şeylerden mutlaka sorulacaksınız.'),
(1958, 16, 57, 'O şanı yüce Allah\'a kızları veriyorlar (melekleri Allah\'ın kızları sanıyorlar) da kendilerine hoşlandıkları(erkek çocukları)nı (alıyorlar). (Kız çocuklarından arlanıyorlar. Peki ama neden arlandıkları şeyleri Allah\'a layık görüyorlar?)'),
(1959, 16, 58, 'Onlardan birine dişi (çocuğu olduğu) müjdelendiği zaman içi öfkeyle dolarak yüzü kapkara kesilir.'),
(1960, 16, 59, 'Kendisine verilen müjdenin kötülüğünden dolayı kavminden gizlenir. (Şimdi ne yapsın) onu, hakaretle tutsun mu yoksa onu toprağa mı gömsün! Bak, ne kötü hüküm veriyorlar!'),
(1961, 16, 60, 'Ahirete inanmayanların durumu, kötüdür. En yüce durum, Allah\'ındır. O, üstün hüküm ve hikmet sahibidir.'),
(1962, 16, 61, 'Eğer Allah, insanları, yaptıkları (her) haksızlıkla cezalandırsaydı, yeryüzünde tek canlı bırakmazdı. Fakat onları takdir edilen bir süreye kadar erteler. Süreleri geldiği zaman da bir sa\'at dahi ne geri kalırlar, ne de ileri geçerler (derhal mahvolup giderler).'),
(1963, 16, 62, 'Kendilerinin hoşlanmadıkları şeyi Allah\'a veriyorlar, üstelik de dilleri, "En güzel sonuç, kendilerinin olacak" diye yalan söylüyor. Onlara mutlaka ateş vardır ve onlar ona sürüleceklerdir!'),
(1964, 16, 63, 'Allah\'a andolsun ki senden önceki milletlere de elçi gönderdik; şeytan onlara yaptıkları işleri süsledi. (Bu yüzden peygamberleri yalanladılar). O, bugün de onların dostudur. Onlar için acı bir azab vardır.'),
(1965, 16, 64, 'Biz sana Kitabı indirdik ki, hakkında ayrılığa düştükleri şeyi onlara açıklayasın ve (o Kitap), inanan bir kavim için yol gösterici ve rahmet olsun.'),
(1966, 16, 65, 'Allah, gökten bir su indirdi, onunla yeri ölümünden sonra diriltti, şüphesiz bunda işiten bir millet için ibret vardır.'),
(1967, 16, 66, 'Hayvanlarda da sizin için ibret vardır. Size onların karınlarından, fışkı ile kan arasıdan (çıkardığımız) halis, içenlere (içimi) kolay süt içiriyoruz.'),
(1968, 16, 67, 'Hurma ağaçlarının meyvalarından ve üzümlerden de sarhoşluk ve güzel rızık elde edersiniz. Şüphesiz bunda aklını kullanan bir toplum için ibret vardır.'),
(1969, 16, 68, 'Rabbin, bal arısına şöyle vahyetti: "Dağlardan, ağaçlardan ve kurdukları çardaklardan evler edin!"'),
(1970, 16, 69, 'Sonra her çeşit meyvalardan ye de Rabbinin yollarında boyun eğerek yürü! Onun karınlarından, renkleri çeşit çeşit bir içecek çıkar ki onda insanlara şifa vardır. Şüphesiz bunda düşünen bir millet için ibret vardır.'),
(1971, 16, 70, 'Allah sizi yarattı, sonra öldürür; içinizden kimi de ömrün en reziline (bebeklik çağı gibi güçsüz ihtiyarlık çağına) itilir ki, biraz bilgiden sonra hiçbir şeyi bilmez olsun! Doğrusu Allah bilendir. (O, her şeye) kadirdir.'),
(1972, 16, 71, 'Allah, rızıkta kiminizi kiminizden üstün kıldı. (Rızıkça) üstün kılınanlar, ellerinin altında bulunanlara kendi rızıklarını verip de hepsi rızıkta eşit olmuyorlar. Allah\'ın ni\'metini mi inkar ediyorlar?'),
(1973, 16, 72, 'Allah size kendi nefislerinizden eşler yarattı ve eşlerinizden de size oğullar ve torunlar yarattı ve sizi güzel rızıklarla besledi. Böyle iken onlar, batıla inanıp da Allah\'ın ni\'metine nankörlük mü ediyorlar?'),
(1974, 16, 73, 'Allah\'tan başka, göklerden ve yerden kendileri için hiçbir rızık veremeyecek ve bunu asla yapamayacak olan şeylere mi tapıyorlar?'),
(1975, 16, 74, 'Allah\'a meseller vermeğe (benzetmeler yapmağa) kalkmayın! Çünkü Allah (benzetme yapmayı) bilir, siz ise bilmezsiniz!'),
(1976, 16, 75, 'Allah, hiçbir şeye gücü yetmeyen ve başkasının malı olan bir köle ile; kendisine güzel rızık verdiğimiz, o rızıktan gizli ve açık harcayan kimseyi misal olarak anlattı. Hiç bunlar bir olurlar mı? Hamd Allah\'a mahsustur, fakat çokları bilmezler.'),
(1977, 16, 76, 'Ve Allah şu iki adamı da misal olarak anlattı: Birisi dilsizdir, hiçbir şey yapamaz, efendisinin üzerine bir yüktür. (Efendisi) onu nereye gönderse bir hayır getirmez (bir iş beceremez). Şimdi bu (adam), doğru yolda giderek adaleti emreden kimse gibi olur mu?'),
(1978, 16, 77, 'Göklerin ve yerin gaybı Allah\'a aittir. O sa\'atin işi, bir göz açıp yumma gibi, yahut daha yakın(kısa)dır. Şüphesiz Allah, her şeye gücü yetendir.'),
(1979, 16, 78, 'Allah sizi, (hiçbir şey bilmediğiniz durumda) annelerinizin karınlarından çıkardı, size işitme (duyusu), gözler ve gönüller verdi ki şükredesiniz.'),
(1980, 16, 79, 'Göğün boşluğunda, O\'nun emrine boyun eğdirilmiş olan kuşlara bakmadılar mı? Onları Allah\'tan başka tutan yoktur. Şüphesiz bunda inanan bir kavim için ayetler (Allah\'ın büyüklüğüne işaretler) vardır.'),
(1981, 16, 80, 'Allah size, evlerinizi oturma yeri yaptı ve size hayvan derilerinden, göç gününüzde (yolculukta) ve ikamet gününüzde (oturma zamanlarınızda) kolayca kullanacağınız hafif evler (çadırlar, portatif evler) ve yünlerinden, yapağılarından ve kıllarından bir süreye kadar (kullanacağınız) giyilecek, döşenecek eşya ve geçimlik (ticaret malı) yaptı.'),
(1982, 16, 81, 'Allah, yarattıklarından sizin için gölgeler yaptı ve sizin için dağlarda oturulacak barınaklar (mağaralar) var etti ve sizi sıcaktan koruyan elbiseler ve savaşta sizi koruyan elbiseler (zırhlar) var eyledi. Allah size ni\'metini böyle tamamlıyor ki siz müslüman olup esenliğe eresiniz.'),
(1983, 16, 82, 'Eğer yine yüz çevirirlerse, artık senin üzerine düşen sadece açık bir şekilde duyurmaktır.'),
(1984, 16, 83, 'Allah\'ın ni\'metini bilirler (bu ni\'metleri Allah\'ın yarattığını kabul ederler), sonra da (bunları kendilerine verenden başkasına taparak) bu ni\'metleri inkar ederler, çokları da (nankördürler).'),
(1985, 16, 84, 'Her ümmetten bir şahid getirdiğimiz gün, artık ne nankörler(in konuşmaların)a izin verilir, ne de onların özür dilemeleri istenir.'),
(1986, 16, 85, 'Zulmedenler azabı gördükleri zaman artık azab onlardan ne hafifletilir, ne de onlara fırsat verilir.'),
(1987, 16, 86, 'Ortak koşanlar, ortak koştukları şeyleri gördükleri zaman: "Rabbimiz, işte senden başka yalvar(ıp tap)dığımız ortaklarımız!" derler. (Onlar da bunlara): "Siz tamamen yalancılarsınız!" diye söz atarlar.'),
(1988, 16, 87, 'O gün (ortak koşanlar) Allah\'a teslim olmuşlar ve uydurup durdukları şeyler kendilerinden sapıp gitmiştir.'),
(1989, 16, 88, 'Nankörlük edip Allah\'ın yoluna engel olan kimselerin, -bozgunculuklarından dolayı- azaplarının üstüne azab katmışızdır!'),
(1990, 16, 89, 'Her ümmet içinde, kendi aralarından, aleyhlerine bir şahid getireceğimiz gün, seni de bunların aleyhine şahid getirmiş olacağız. Sana bu Kitabı, her şeyi açıklayan ve müslümanlara yol gösterici, rahmet ve müjde olarak indirdik.'),
(1991, 16, 90, 'Allah adaleti, ihsanı, akrabaya vermeyi emreder, fahşa(edepsizlikten)dan, münker(fenalık)den ve bağy(azgınlık)den meneder. Öğüt almanız için size böyle öğüt verir.'),
(1992, 16, 91, 'Andlaşma yaptığınız zaman Allah\'ın ahdini tam yerine getirin (verdiğiniz sözü tutun), pekiştirdikten sonra yeminleri bozmayın. Çünkü Allah\'ı üzerinize kefil (şahid) yaptınız. Allah yaptıklarınızı bilir.'),
(1993, 16, 92, 'Bir topluluk, diğer bir topluluktan (sayıca ve malca) daha çok olduğu için, yeminlerinizi aranızda bozucu bir vasıta yaparak, ipliğini kuvvetli büktükten sonra çözen kadın gibi olmayın! Çünkü Allah, sizi bununla dener. Hakkında ayrılığa düştüğünüz şeyleri kıyamet günü size açıklayacaktır.'),
(1994, 16, 93, 'Allah dileseydi, hepinizi, bir tek ümmet yapardı, fakat (O), dilediğini şaşırtır, dilediğini doğru yola iletir. Ve siz, mutlaka yaptığınız şeylerden sorulacaksınız.'),
(1995, 16, 94, 'Yeminlerinizi aranızı bozan bir şey yapmayın, sonra sağlam basmış olan ayak, kayar ve Allah\'ın yoluna engel olduğunuzdan dolayı kötülüğü(n cezasını) tadarsınız ve büyük bir azaba uğrarsınız.'),
(1996, 16, 95, 'Allah\'a verdiğiniz sözü (peygambere yaptığınız bey\'atı) az bir paraya satmayın. Zira bilirseniz Allah\'ın yanında olan, sizin için daha hayırlıdır.'),
(1997, 16, 96, 'Sizin yanınızda bulunan (dünya malı) tükenir. Allah\'ın yanında bulunan ise kalıcıdır. Biz sabredenlerin karşılığını yaptıklarının en güzeliyle vereceğiz.'),
(1998, 16, 97, 'Erkek ve kadından her kim inanmış olarak iyi bir iş yaparsa, onu (dünyada) hoş bir hayatla yaşatırız, onların ücretini yaptıklarının en güzeliyle veririz.'),
(1999, 16, 98, 'Kur\'an, oku(mak iste)diğin zaman kovulmuş şeytandan Allah\'a sığın.'),
(2000, 16, 99, 'Çünkü inananlara ve Rablerine dayananlara o(şeyta)nın bir gücü yoktur.'),
(2001, 16, 100, 'Onun gücü, sadece kendisini dost tutanlara ve Allah\'a ortak koşanlaradır (o, sadece onları etkileyebilir).'),
(2002, 16, 101, 'Biz bir ayetin yerine başka bir ayet getirdiğimiz zaman, ĞAllah ne indirdiğini bilirkenĞ "Sen (Allah\'a) iftira ediyorsun (bu sözleri kendin uydurup Allah\'ın üstüne atıyorsun)" derler. Hayır, onların çokları bilmiyorlar.'),
(2003, 16, 102, 'De ki: "İnananları sağlamlaştırmak ve müslümanlara yol gösterici ve müjde olmak üzere onu, Ruhu\'l-Kudüs (Cebrail) Rabbinden gerçek (bilgi) olarak indirdi."'),
(2004, 16, 103, 'Biz onların, "Ona bir insan öğretiyor!" dediklerini biliyoruz. Hak\'tan saparak kendisine yöneldikleri adamın dili a\'cemi (yabancıdır, açık değildir), bu ise apaçık Arapça bir dildir.'),
(2005, 16, 104, 'Allah\'ın ayetlerine inanmayanları Allah doğru yola iletmez, onlar için acı bir azab vardır.'),
(2006, 16, 105, 'Yalanı ancak Allah\'ın ayetlerine inanmayanlar uydurur; yalancılar, işte onlardır.'),
(2007, 16, 106, 'İnandıktan sonra Allah\'a nankörlük eden, Ğkalbi imanla yatışmış olduğu halde (inkara) zorlanan değilĞ, fakat küfre göğüs açan, (küfürle sevinç duyan) kimselere Allah\'tan bir gazab iner ve onlar için büyük bir azab vardır.'),
(2008, 16, 107, 'Bu, onların dünya hayatını ahirete tercih etmelerinden ve Allah\'ın, inkar eden kavmi doğru yola iletmeyeceğinden ötürü böyledir.'),
(2009, 16, 108, 'Onlar, Allah\'ın kalblerini kulaklarını ve gözlerini mühürlediği kimselerdir. İşte gafiller onlardır.'),
(2010, 16, 109, 'Elbette onlar, ahirette ziyana uğrayacaklardır.'),
(2011, 16, 110, 'Sonra Rabbin, şunların şu işkenceye uğratıldıktan sonra göç eden, sonra savaşan ve sabredenlerin yanındadır. Elbette (bütün) bun(lar)dan sonra Rabbin bağışlayan, esirgeyendir.'),
(2012, 16, 111, 'O gün herkes gelir, kendi canını kurtarmak için uğraşır ve herkese yaptığının tam karşılığı verilir, onlara asla haksızlık edilmez.'),
(2013, 16, 112, 'Allah şöyle bir kenti misal olarak anlattı: Güven, huzur içinde idi; her yerden rızkı bol bol kendisine geliyordu. Fakat Allah\'ın ni\'metlerine nankörlük etti, bunun üzerine (halkının) yaptıklarından ötürü Allah ona açlık ve korku elbisesi taddırdı.'),
(2014, 16, 113, 'Andolsun, onlara, kendilerinden bir elçi geldi, onu yalanladılar. Bunun üzerine onlar zulümlerine devam ederken azab onları yakalayıverdi.'),
(2015, 16, 114, 'Allah\'ın size verdiği rızıktan helal, hoş olarak yeyin de Allah\'ın ni\'metine şükredin; eğer O\'na kulluk ediyorsanız.'),
(2016, 16, 115, 'Allah size ölüyü, kanı, domuz etini ve Allah\'tan başkasının adı anılarak kesilen(hayvanlar)ı haram kıldı. Kim mecbur kalırsa (başkasının hakkına) saldırmadan, sınırı da aşmadan (bunlardan) yiyebilir. Şüphesiz Allah, bağışlayan, esirgeyendir.'),
(2017, 16, 116, 'Dillerinizin yalan yere nitelendirmesinden ötürü "Şu helaldir, şu haramdır," demeyin, sonra Allah\'a karşı yalan uydurmuş olursunuz. Allah\'a karşı yalan uyduranlar ise iflah olmazlar.'),
(2018, 16, 117, 'Azıcık yaşama(nın ardından), onlara acı bir azab gelecektir.'),
(2019, 16, 118, 'Yahudi olanlara da, bundan önce sana anlattıklarımızı haram kılmıştık. Biz onlara zulmetmedik, fakat onlar kendi kendilerine zulmediyorlardı!'),
(2020, 16, 119, 'Sonra Rabbin şunlardan yanadır ki, cehaletle kötülük işlediler, sonra onun ardından tevbe ettiler, uslandılar. Bütün bunlardan sonra Rabbin, elbette bağışlayandır, esirgeyendir.'),
(2021, 16, 120, 'İbrahim Allah\'ı birleyerek O\'na ita\'at eden bir ümmet (her iyiliği kendinde toplayan bir önder) idi, ortak koşanlardan değildi.'),
(2022, 16, 121, 'O\'nun ni\'metlerine şükredici idi. (Allah) onu seçmiş ve doğru yola iletmişti.'),
(2023, 16, 122, 'Ona dünyada iyilik vermiştik. O, ahirette de iyilerdendir.'),
(2024, 16, 123, 'Sonra sana: "Allah\'ı birleyerek İbrahim\'in yoluna uy; o, ortak koşanlardan değildi" diye vahyettik.'),
(2025, 16, 124, 'Cumartesi (gününü ta\'til ve ibadet günü yapmak), onda ayrılığa düşen(yahudi)lere (farz) kılındı. Rabbin, elbette ayrılığa düştükleri şey hakkında kıyamet günü aralarında hükmünü verecektir.'),
(2026, 16, 125, 'Hikmetle ve güzel öğütle Rabbinin yoluna çağır ve onlarla en güzel biçimde mücadele et. Kuşkusuz Rabbin, işte yolundan sapanları en iyi bilen O\'dur ve O, yola gelenleri de en iyi bilendir.'),
(2027, 16, 126, 'Eğer azab edecekseniz, size yapılan azab kadar azab edin. Ama sabdederseniz, andolsun ki o, sabredenler için daha iyidir.'),
(2028, 16, 127, 'Sabret, sabrın ancak Allah(ın yardımı) iledir. Onlara üzülme, kurdukları tuzaklardan da sıkıntıya düşme.'),
(2029, 16, 128, 'Çünkü Allah, korunanlarla ve iyilik edenlerle beraberdir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Eksiklikten uzaktır O (Allah) ki gecenin bir vaktinde kulunu, ayetlerimizden bir bölümünü, kendisine göstermemiz için, Mescid-i Haram\'dan, çevresini bereketli kıldığımız Mescid-i Aksa\'ya yürüttü. Gerçekten O, işitendir, görendir.'),
(2031, 17, 2, 'Biz Musa\'ya Kitabı verdik ve onu İsrail oğullarına "Benden başka bir vekil tutmayın!" diye bir kılavuz yaptık.'),
(2032, 17, 3, 'Ey Nuh ile beraber (gemide) taşıdıklarımızın çocukları, doğrusu o (Nuh), çok şükreden bir kuldu. (Siz de atanız gibi olun.)'),
(2033, 17, 4, 'Kitapta İsrail oğullarına şu hükmü verdik: "Siz o ülkede iki kez bozgunculuk yapacaksınız ve çok böbürleneceksiniz (zorbalık edeceksiniz)!'),
(2034, 17, 5, 'Birincisinin zamanı gelince üzerinize çok güçlü kullarımızı gönderdik, evlerin aralarına girip (sizi) araştırdılar. Bu, yapılması gereken bir va\'d idi.'),
(2035, 17, 6, 'Sonra tekrar size, onları yenme imkanı verdik ve sizi mallarla, oğullarla destekledik ve savaşçılarınızı çoğalttık.'),
(2036, 17, 7, 'İyilik ederseniz, kendinize iyilik etmiş olursunuz. Kötülük ederseniz, o da kendi aleyhinizedir. Son taşkınlığınızın zamanı gelince (yine öyle kullar göndeririz) ki, yüzlerinizi kötü duruma soksunlar (üzüntüden suratlarınızın asılmasına sebeb olsunlar) ve ilk kez girdikleri gibi yine Mescid\'e (Kudüs\'e) girsinler ve ele geçirdiklerini mahvetsinler.'),
(2037, 17, 8, '(Bundan sonra) Belki Rabbiniz size acır, ama siz (bozgunculuk yapmaya) dönerseniz, biz de (sizi cezalandırmağa) döneriz. Cehennemi, kafirler için kuşatıcı (bir zindan) yapmışızdır!'),
(2038, 17, 9, 'Gerçekten bu Kur\'an da en doğru yola iletir ve iyi işler yapan mü\'minlere, kendileri için büyük bir ecir olduğunu müjdeler.'),
(2039, 17, 10, 'Ahirete inanmayanlara da acı bir azab hazırlamışızdır.'),
(2040, 17, 11, 'İnsan, hayra du\'a eder gibi, şerre du\'a etmekte(hayrı ister gibi şerri istemekte)dir. İnsan pek acelecidir.'),
(2041, 17, 12, 'Biz gece ve gündüzü, (kudretimizi gösteren) iki ayet yaptık. Gece ayetini sildik, gündüz ayetini aydınlatıcı yaptık ki hem Rabbinizin lutfunu arayasınız ve hem de yılların sayısını ve hesabı bilesiniz. Biz her şeyi açık açık anlattık.'),
(2042, 17, 13, 'Her insanın tair(kuş)ini boynuna bağladık, kıyamet günü onun için, açılmış olarak bulacağı bir Kitap çıkarırız:'),
(2043, 17, 14, 'Kitabını oku, bugün nefsin sana hesapçı olarak yeter! (deriz).'),
(2044, 17, 15, 'Kim yola gelirse kendisi için yola gelmiş olur, kim de saparsa kendi aleyhine sapar. Hiçbir günahkar, başkasının günah yükünü taşımaz. Biz elçi göndermedikçe azab edecek değiliz.'),
(2045, 17, 16, 'Biz bir kenti helak etmek istediğimiz zaman onun varlıklılarına emrederiz, orada kötü işler yaparlar, böylece o ülkeye (azab) karar(ı) gerekli olur, biz de orayı darmadağın ederiz.'),
(2046, 17, 17, 'Nitekim Nuh\'dan sonra nice kuşakları helak ettik. Kullarının günahlarını haber alıcı, görücü olarak Rabbin yeter.'),
(2047, 17, 18, 'Kim bu aceleci(dünya)yı isterse, orada ona, (evet) istediğimiz kimseye hemen çabucak dilediğimiz kadar veririz; ama sonra yerini cehennem yaparız! Kınanmış ve kovulmuş olarak oraya girer.'),
(2048, 17, 19, 'Kim de ahireti ister ve inanarak ona yaraşır biçimde çalışırsa, öylelerinin çalışmalarının karşılığı verilir.'),
(2049, 17, 20, 'Hepsine onlara da, onlara da (dünyayı isteyenlere de, ahireti isteyenlere de, mü\'minlere de, kafirlere de) Rabbinin vergisiden uzatırız. Rabbinin vergisi kesilmez.'),
(2050, 17, 21, 'Bak, (rızık bakımından) nasıl onların kimini kiminden üstün yaptık. Elbette ahiret, dereceler bakımından da daha büyük, üstünlük bakımından da daha büyüktür.'),
(2051, 17, 22, 'Allah ile beraber başka bir tanrı edinme, sonra kınanmış ve yalnız başına bırakılmış olarak oturup kalırsın!'),
(2052, 17, 23, 'Rabbin, yalnız kendisine tapmanızı ve anaya babaya, iyilik etmenizi emretti. İkisinden birisi, yahut her ikisi, senin yanında ihtiyarlık çağına ulaşır(ihtiyarlık zamanlarında senin yanında kalırlar)sa sakın onlara "Öf!" deme, onları azarlama! Onlara güzel söz söyle.'),
(2053, 17, 24, 'Onlara acımadan dolayı, küçülme kanadını indir, (onlara karşı alçak gönüllü ol) ve: "Ey (her varlığı terbiye edip yetiştiren) Rabbim! Bunlar, beni küçükken nasıl (acıyıp) yetiştirdilerse sen de bunlara (öyle) acı!" de.'),
(2054, 17, 25, 'Rabbiniz içlerinizdekini daha iyi bilir. Eğer siz iyi kişiler olursanız şüphesiz O, tevbe edenleri bağışlayandır.'),
(2055, 17, 26, 'Akrabaya, yoksula ve yolcuya hakkını ver, fakat saçıp savurma.'),
(2056, 17, 27, 'Çünkü savurganlar, şeytanların kardeşleri olmuşlardır. Şeytan ise Rabbine karşı çok nankördür!'),
(2057, 17, 28, 'Eğer (elin dar olduğu için) Rabbinden umduğun bir rahmeti bekleyerek onlardan yüz çevirecek, (onlara birşey vermeyecek) olursan, bari onlara yumuşak söz söyle.'),
(2058, 17, 29, 'El(ler)ini boynuna bağlanmış yapma, tamamen de açma, sonra kınanır, hasret içinde kalırsın.'),
(2059, 17, 30, 'Rabbin dilediğine rızkı açar (bol bol verir, dilediğine) kısar. Çünkü O, kulları(nın hali)ni bilir, görür.'),
(2060, 17, 31, 'Fakirlik korkusuyla çocuklarınızı öldürmeyin. Onları da sizi de biz besliyoruz. Onları öldürmek, büyük günahtır.'),
(2061, 17, 32, 'Zinaya yaklaşmayın, çünkü o, açık bir kötülüktür, çok kötü bir yoldur!'),
(2062, 17, 33, 'Allah\'ın haram kıldığı canı haksız yere öldürmeyin. Kim haksızlıkla öldürülürse, onun velisi(olan mirasçısı)na yetki vermişizdir (öldürülenin hakkını arar. Fakat o da) öldürmede aşırı gitmesin. Çünkü kendisine yardım edilmiş(yetki verilmiş)tir.'),
(2063, 17, 34, 'Yetimin malına yaklaşmayın, ancak erginlik çağına erişinceye kadar en güzel bir tarzda (onun malını kullanıp geliştirebilirsiniz). Ahdi de yerine getirin, çünkü ahd\'den sorulacaktır.'),
(2064, 17, 35, 'Ölçtüğünüz zaman ölçüyü tam yapın, doğru terazi ile tartın. Bu daha iyidir, sonuç bakımından da daha güzeldir.'),
(2065, 17, 36, 'Bilmediğin bir şeyin ardına düşme, çünkü kulak, göz ve gönül, bunların hepsi o(yaptığı)ndan sorumludur.'),
(2066, 17, 37, 'Yeryüzünde kabara kabara yürüme. Çünkü sen yeri yırtamazsın, boyca da dağlara erişemezsin!'),
(2067, 17, 38, 'Bunlar("Allah ile beraber başka tanrı edinme!" ayetinden itibaren sayılan fiiler)in hepsi, kötü olan, Rabbinin katında hoş görülmeyen şeylerdir.'),
(2068, 17, 39, 'Şunlar, Rabbinin, Hikmet\'ten sana vahyettiği(emirleri)ndendir. Allah ile berebar başka tanrı edinme, sonra kınanmış, (Allah\'ın rahmetinden) uzaklaştırılmış olarak cehenneme atılırsın.'),
(2069, 17, 40, 'Rabbiniz, oğulları size seçti de kendisine meleklerden kadınlar mı edindi? Gerçekten siz büyük (çok tehlikeli) bir söz söylüyorsunuz!'),
(2070, 17, 41, 'Biz Kur\'an\'da sözü türlü biçimlerde anlattık ki, düşünüp anlasınlar. Fakat bu, onların sadece kaçışlarını artırıyor.'),
(2071, 17, 42, 'De ki: "Eğer dedikleri gibi O\'nunla beraber (başka) tanrılar olsaydı o zaman onlar da Arşın sahibine gitmenin yolunu ararlardı.'),
(2072, 17, 43, 'Haşa, O, onların dediklerinden çok yücedir, uludur.'),
(2073, 17, 44, 'Yedi gök, arz ve bunların içinde bulunanlar, O\'nu tesbih ederler. O\'nu övgü ile tesbih etmeyen hiçbir şey yoktur, ama siz onların tesbihlerini anlamazsınız. O, halimdir, çok bağışlayandır.'),
(2074, 17, 45, 'Kur\'an okuduğun zaman seninle, ahirete inanmayanların arasına gizli bir perde çekeriz.'),
(2075, 17, 46, 'Kablerine -onu anlamalarına engel olacak- kabuklar, kulaklarına da bir ağırlık koyarız. Kur\'an\'da yalnız Rabbini andığın zaman (tek Tanrı inancından hoşlanmadıkları için) arkalarına dönüp kaçarlar.'),
(2076, 17, 47, 'Biz onların, seni dinlerken ne sebeple dinlediklerini, kendi aralarında gizli konuşurlarken de o zalimlerin: "Siz büyülenmiş bir adamdan başkasına uymuyorsunuz!" dediklerini gayet iyi biliyoruz.'),
(2077, 17, 48, 'Bak, nasıl misaller verdiler (seni şa\'ire, büyücüye, kahine ve mecnuna benzettiler) de şaştılar. Artık bir daha yolu bulamazlar.'),
(2078, 17, 49, 'Dediler ki: "Biz kemikler haline geldikten, ufalanıp toprak olduktan sonra mı sahiden biz mi yeni bir yaratılışla diriltileceğiz?"'),
(2079, 17, 50, 'De ki: "İster taş olun, ister demir,"'),
(2080, 17, 51, 'İster gönlünüzde büyüyen, (aklınıza tuhaf gelen) herhangi bir yaratık, (ne olursanız olun, Allah sizi mutlaka diriltecektir). Bizi kim tekrar (hayata) döndürebilir?" diyecekler. "Sizi ilk defa yaratan (döndürür)" de. Sana alaylı alaylı başlarını sallayacaklar ve: "Ne zaman o?" diyecekler. "Pek yakın olabilir" de.'),
(2081, 17, 52, 'Sizi çağıracağı gün O\'na hamdederek çağrısına uyarsınız (dirilip kalkarsınız) ve (dünyada) pek az kaldığınızı sanırsınız.'),
(2082, 17, 53, 'Kullarıma söyle: En güzel sözü söylesinler (puta tapanlara sert davranmasınlar). Çünkü şeytan aralarına girer (onları tartışmaya ve kavgaya dürtükler). Doğrusu şeytan, insanın apaçık düşmanıdır.'),
(2083, 17, 54, 'Rabbiniz sizi daha iyi bilir. Dilerse size acır, dilerse size azabeder. Biz seni, onların üzerine vekil göndermedik.'),
(2084, 17, 55, 'Rabbin, göklerde ve yerde olan kimseleri daha iyi bilir (O, peygamber olmağa kimi layık görürse onu seçer). Andolsun ki biz, peygamberlerin kimini kimine üstün kıldık, Davud\'a da Zebur\'u verdik.'),
(2085, 17, 56, 'De ki: "O\'ndan başka (tanrı olduğunu) sandığınız şeylere yalvarın; onlar ne sizden sıkıntıyı kaldırabilirler, ne de (onu) başka bir yana çevirebilirler.'),
(2086, 17, 57, 'O yalvardıkları da, onların (Allah\'a) en yakın olan(lar)ı da Rablerine yaklaşmak için vesile ararlar; O\'nun merhametini umarlar, azabından korkarlar. Çünkü Rabbinin azabı, cidden korkunçtur.'),
(2087, 17, 58, 'Hiçbir kent yoktur ki biz, kıyamet gününden öne onu yok edecek, yahut ona şiddetli bir şekilde azabedecek olmayalım. Bu, Kitapta yazılmıştır.'),
(2088, 17, 59, 'Bizi ayetler (mu\'cizeler) göndermekten alıkoyan şey, evvelkilerin, (onları) yalanlamış olmasıdır. Semud(kavmin)e açık bir mu\'cize olarak dişi deveyi verdik, o zulmetmelerine sebeb oldu (deveyi boğazlayarak kedilerine yazık etmiş oldular). Biz mu\'cizeleri, yalnız korkutmak için göndeririz.'),
(2089, 17, 60, 'Bir zaman sana: "Rabbin insanları kuşatmıştır, (suçluları cezalandırmak üzeredir)" demiştik. Sana gösterdiğimiz rü\'yayı ve Kur\'an\'da la\'netlenmiş ağacı, insanları(n imanını) sınama (aracı) yaptık. Biz onları (çeşitli biçimlerde) korkutuyoruz. Fakat korkutmamız onların azgınlıklarını daha da artırmaktan başka bir katkı yapmıyor'),
(2090, 17, 61, 'Bir zaman meleklere: "Adem\'e secde edin!" demiştik. Secde ettiler, yalnız İblis etmedi: "Ben çamur olarak yarattığın kimseye secde eder miyim? dedi.'),
(2091, 17, 62, 'Şu benden üstün yaptığını gördün mü (nesi var onun ki onu benden üstün kıldın)? Andolsun, eğer beni kıyamet gününe kadar ertelersen, onun zürriyetini, pek azı hariç kökünden koparıp sürükleyeceğim! dedi.'),
(2092, 17, 63, '(Allah) "(defol) git, dedi, onlardan kim sana uyarsa cezanız cehennemdir, mükemmel bir ceza (size)!"'),
(2093, 17, 64, 'Onlardan gücünün yettiğini sesinle yerinden oynat; atlıların ve yayalarınla onların üzerine yaygarayı bas; mallarda ve evladlarda onlara ortak ol; onlara (çeşitli) va\'dler yap (va\'dlerinle onları oyala); şeytan, onlara aldatmadan başka bir şey va\'detmez-.'),
(2094, 17, 65, 'Benim (gerçek) kullarım(a gelince) senin onlar(ı kandırmağ)a gücün yetmez! vekil olarak Rabbin yeter.'),
(2095, 17, 66, '(Ey insanlar), Rabbiniz O\'dur ki lutfundan (payınızı) aramanız için size gemileri denizde yürütür. Doğrsu O, size çok acır.'),
(2096, 17, 67, 'Denizde size bir sıkıntı (boğulma korkusu) dokunduğu zaman O\'ndan başka bütün yalvardıklarınız kaybolur (artık o zaman, Allah\'tan başka kimseden yardım istemezsiniz. Çünkü O\'ndan başka sizi kurtaracak kimse yoktur). Fakat (O) sizi kurtarıp karaya çıkarınca yine (Allah\'ı bir tanımaktan) yüz çevirirsiniz. Gerçekten insan nankördür.'),
(2097, 17, 68, '(Allah\'ın) Karayı ters çevirip sizi batırmayacağından, yahut üzerinize taşlar savuran bir kasırga göndermeyeceğinden emin misiniz? (Ki bunlar olduktan) Sonra kendinize bir koruyucu bulamazsınız!'),
(2098, 17, 69, 'Yoksa O\'nun sizi bir kez daha denize gönderip, üstünüze, kırıp geçiren bir fırtına salarak inkar ettiğinizden dolayı sizi boğmayacağından emin misiniz? O zaman bize karşı sizi izleyip koruyacak birini bulamazsınız!'),
(2099, 17, 70, 'Andolsun biz, Adem oğullarına çok ikram ettik: onları karada ve denizde (hayvanlar ve taşıtlar üzerinde) taşıdık. Onları güzel rızıklarla besledik ve onları yarattıklarımızın bir çoğundan üstün kıldık.'),
(2100, 17, 71, 'Her milleti, imamıyla (eylemlerini saptayan defteriyle veya izlediği önderiyle) çağırdığımız gün, kimlerin Kitabı sağından verilirse işte onlar, Kitaplarını okurlar ve en ufak bir haksızlığa uğratılmazlar.'),
(2101, 17, 72, 'Şu dünyada kör olan kimse, ahirette de kördür (dünyada doğru yolu göremeyen, ahirette de kurtuluş yolunu göremeyecektir, hatta onun) yolu daha da sapıktır.'),
(2102, 17, 73, 'Az daha onlar, baskı ile seni, sana vahyettiğimizden ayırarak ondan başkasını üstümüze atman için kandıracaklardı. İşte o zaman seni dost edinirlerdi.'),
(2103, 17, 74, 'Eğer biz seni sağlamlaştırmamış olsaydık, onlara bir parça yanaşacaktın.'),
(2104, 17, 75, 'O takdirde sana hayatın da, ölümün de kat kat(azab)ını taddırırdık. Sonra bize karşı bir yardımcı da bulamazdın.'),
(2105, 17, 76, 'Neredeyse seni yurdundan çıkarmak için tedirgin edeceklerdi. O takdirde kendileri de senin ardından pek az kalabilirler.'),
(2106, 17, 77, 'Senden önce gönderdiğimiz elçilerimizin de yasası (budur). Bizim yasamızda bir değişiklik bulamazsın.'),
(2107, 17, 78, 'Güneşin sarkmasından (aşağı kaymasından) gecenin kararmasına (yatsı vaktine) kadar namaz kıl ve sabahın Kur\'an\'ın(ı, uzunca Kur\'an okunan sabah namazını) da (unutma). Çünkü sabah Kur\'an (okuması) görülecek şeydir.'),
(2108, 17, 79, 'Ayrıca sana özgü olarak gecenin bir kısmında da Kur\'an oku(yup namaz kıl)mak üzere uyan! Rabbinin seni güzel bir makama ulaştırması umulur.'),
(2109, 17, 80, 'De ki: "Rabbim, beni doğruluk girdirişiyle girdir ve beni doğruluk çıkarışiyle çıkar. Bana katından yardımcı bir güç ver."'),
(2110, 17, 81, 'De ki: "Hak geldi, batıl gitti; zaten batıl yok olmağa mahkumdur."'),
(2111, 17, 82, 'Biz Kur\'an\'dan mü\'minlere şifa ve rahmet olan şeyler indiriyoruz. Ama bu, zalimlerin ziyanını artırmaktan başka bir katkıda bulunmaz.'),
(2112, 17, 83, 'İnsana ni\'met verdiğimiz zaman, yüz çevirip yan çizer. Ona bir zarar dokununca da umutsuzluğa düşer.'),
(2113, 17, 84, 'De ki: "Herkes kendi karakterine göre hareket eder. Rabbiniz kimin en doğru yolda olduğunu daha iyi bilir."'),
(2114, 17, 85, 'Sana ruhtan sorarlar. De ki: "Ruh, Rabbimin emrindendir. Size ilimden pek az bir şey verilmiştir."'),
(2115, 17, 86, 'Andolsun, biz dilesek, sana vahyettiğimiz(ayetler)i tamamen gideririz; sonra onun (geri alınması) için bize karşı sana bir yardımcı bulamazsın.'),
(2116, 17, 87, 'Ancak Rabbin sana acıyarak ayetlerini geri almamaktadır. Çünkü O\'nun sana olan lutfu cidden büyüktür.'),
(2117, 17, 88, 'De ki: "Andolsun eğer insan(lar) ve cin(ler) bu Kur\'an\'ın bir benzerini getirmek üzere toplansalar ve birbirlerine arka ol(up yardım et)seler yine onun benzerini getiremezler.'),
(2118, 17, 89, 'Andolsun biz bu Kur\'an\'da insanlara her çeşit misali türlü biçimlerde anlattık, ama insanlardan çoğu inkarda direttiler.'),
(2119, 17, 90, 'Dediler ki: "Yerden bize bir göze fışkırtmadıkça sana inanmayız!"'),
(2120, 17, 91, 'Yahut senin hurmalardan ve üzümlerden oluşan bir bahçen olmalı, aralarından ırmaklar fışkırtmalısın!'),
(2121, 17, 92, 'Yahut zannettiğin gibi üzerimize gökten parçalar düşürmelisin, yahut Allah\'ı ve melekleri karşımıza getirmelisin (onlar senin doğru söylediğine şahidlik etmelidirler)!'),
(2122, 17, 93, 'Yahut altundan bir evin olmalı, ya da göğe çıkmalısın. Ama, sen üzerimize, okuyacağımız bir Kitap indirmedikçe senin sadece göğe çıkmana da inanmayız! De ki: "Rabbimin şanı yücedir. (Böyle şeyleri yapmak benim işim değildir). Ben, sadece elçi ol(arak gönderil)en bir insan değil miyim?"'),
(2123, 17, 94, 'Zaten kendilerine hidayet geldiği zaman insanları doğru yola gelmekten alıkoyan şey, hep: "Allah, bir insanı elçi mi gönderdi?" demeleridir.'),
(2124, 17, 95, 'De ki: "Eğer yer yüzünde uslu uslu yürüyen melekler olsaydı, elbette onlara gökten bir meleği elçi gönderirdik."'),
(2125, 17, 96, 'De ki: "Benimle sizin aranızda şahid olarak Allah yeter. O, kulları(nın halleri)ni haber alır, görür."'),
(2126, 17, 97, 'Allah kime hidayet ederse, işte doğru yolu bulan odur. Kimi de sapıklıkta bırakırsa artık onlar için O\'ndan başka veliler bulamazsın. Kıyamet günü onları, yüzü koyun, kör, dilsiz ve sağır bir halde süreriz. Varacakları yer cehennemdir. Ateş her dindikçe, onlara çılgın alevi artırırız.'),
(2127, 17, 98, 'İşte cezaları budur. Çünkü onlar, ayetlerimizi inkar ettiler ve: "Biz kemikler ve ufalanmış toprak haline geldikten sonra mı, biz mi yeni bir yaratılışla diriltileceğiz?" dediler.'),
(2128, 17, 99, 'Görmediler mi ki, gökleri ve yeri yaratan Allah, kendilerinin benzerini yaratmağa da kadirdir? Kendileri için, bir süre koymuştur, onda hiç şüphe yoktur. Ama zalimler inkardan başka bir şey yapmazlar.'),
(2129, 17, 100, 'De ki: "Eğer Rabbimin rahmet hazinelerine siz sahip olsaydınız, harcamaktan korkarak tutardınız. Gerçekten insan çok cimridir!'),
(2130, 17, 101, 'Andolsun biz Musa\'ya açık açık dokuz mu\'cize vermiştik. İşte İsrail oğullarına sor: Musa onlara gelmiş; Fir\'avn ona: "Ey Musa, ben seni büyülenmiş sanıyorum" demişti.'),
(2131, 17, 102, 'Musa dedi ki: "Bunları, ancak göklerin ve yerin Rabbinin, (benim doğruluğumu belgeleyen) kanıtlar olarak indirdiğini pekala bildin. Ey Fir\'avn, ben de seni mahvolmuş görüyorum.'),
(2132, 17, 103, 'Fir\'avn onları o ülkeden sürüp çıkarmak istedi, biz de onu, yanındakilerle birlikte toptan boğduk.'),
(2133, 17, 104, 'Onun ardından İsrail oğullarına: "O ülkede oturun, ahiret zamanı gelince hepinizi toplayıp bir araya getireceğiz," dedik.'),
(2134, 17, 105, 'Biz o(Kur\'a)nı hak olarak indirdik ve o, hak ile inmiştir. Seni de ancak bir müjdeleyici ve uyarıcı olarak gönderdik.'),
(2135, 17, 106, 'Onu, insanlara ağır ağır okuman için, okuma parçalarına ayırdık ve onu azar azar indirdik.'),
(2136, 17, 107, 'De ki: "Siz ister ona inanın, ister inanmayın, O, daha önce kendilerine bilgi verilenlere okunduğu zaman onlar, derhal çeneleri üstüne secdeye kapanırlar."'),
(2137, 17, 108, 'Rabbimizin şanı yücedir, gerçekten Rabbimizin sözü mutlaka yerine getirilir! derler.'),
(2138, 17, 109, 'Ağlayarak çeneleri üstüne kapanırlar ve Kur\'an onların derin saygısını artırır.'),
(2139, 17, 110, 'De ki: "İster Allah diye çağırın, ister Rahman diye çağırın. Hangisiyle çağırsanız en güzel isimler O\'nundur. Namazında pek bağırma, pek de sesini gizleme, bu ikisinin arasında bir yol tut.'),
(2140, 17, 111, 'Çocuk edinmeyen, mülkte ortağı olmayan, acze düşüp de yardımcıya ihtiyacı bulunmayan Allah\'a hamdolsun! de ve O\'nu gereği gibi tekbir et (saygı ve tekbir ile an).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Allah\'a hamdolsun ki, kuluna Kitabı indirdi ve ona hiçbir eğrilik koymadı.'),
(2142, 18, 2, 'Onu dosdoğru (bir Kitap) olarak indirdi ki katından gelecek şiddetli azaba karşı (insanları) uyarsın ve iyi işler yapan mü\'minlere de kendileri için güzel mükafat bulunduğunu müjdelesin.'),
(2143, 18, 3, 'Onlar sürekli olarak o mükafat içinde bulunacaklardır.'),
(2144, 18, 4, 'Ve: "Allah çocuk edindi" diyenleri de uyarsın.'),
(2145, 18, 5, 'Bu hususta ne kendilerinin, ne de atalarının hiçbir bilgisi yoktur. Ağızlarından ne büyük (küstahça) söz çıkıyor! Onlar, yalandan başka bir şey söylemiyorlar.'),
(2146, 18, 6, 'Herhalde sen, onlar bu söze inanmıyorlar diye, peşlerinde üzüntüden kendini helak edeceksin!'),
(2147, 18, 7, 'Biz yeryüzündeki şeyleri, kendisine süs olsun diye yarattık ki onların, hangisinin daha güzel iş yaptığını deneyelim.'),
(2148, 18, 8, 'Biz elbette (bir gün) yerin üzerindekileri kupkuru bir toprak yaparız.'),
(2149, 18, 9, 'Yoksa sen, sadece Kehf ve Rakim sahiplerinin bizim şaşılacak ayetlerimizden olduklarını mı sandın? (onlardan başka çok daha acaip ayetlerimiz vardır. Arzı yeşertip sonra kurutmamız da şaşılacak ayetlerimizden değil midir?)'),
(2150, 18, 10, 'O gençler mağaraya sığındılar: Rabbimiz, bize katından bir rahmet ver ve bize şu işimizden bir çıkış yolu hazırla!" dediler.'),
(2151, 18, 11, 'Bunun üzerine mağarada nice yıllar onların kulaklarına ağırlık vurduk (onları derin bir uykuya daldırdık)'),
(2152, 18, 12, 'Sonra onları uyandırdık ki, (onların uyuma müddetleri hakkında ihtilaf eden) iki zümreden hangisinin, (onların) kaldıkları süreyi daha iyi hesabedeceğini bilelim.'),
(2153, 18, 13, 'Biz sana onların haberlerini gerçek olarak anlatıyoruz: Onlar Rablerine inanmış gençlerdi. Biz de onların hidayetlerini artırmıştık.'),
(2154, 18, 14, 'Kalblerinin üstüne metanet bağlamıştık. Kalktılar, dediler ki: "Rabbimiz göklerin ve yerin Rabbidir. Biz O\'ndan başkasına Tanrı demeyiz. Yoksa saçma söylemiş oluruz."'),
(2155, 18, 15, 'Şunlar, şu kavmimiz O\'ndan başka tanrılar edindiler. Onların tanrı olduğuna açık bir delil getirmeleri gerekmez miydi? Allah\'a karşı yalan uydurandan daha zalim kim olabilir?'),
(2156, 18, 16, '(İçlerinden biri şöyle dedi): "Madem ki siz onlardan ve Allah\'tan başka taptıkları şeylerden ayrıldınız, o halde mağaraya sığının ki, Rabbiniz size rahmetinden bir parça yaysın (rızkınızı açıp bollaştırsın) ve (şu) işinizden size yararlı bir şey hazırlasın."'),
(2157, 18, 17, 'Güneşi görürsün, doğduğu zaman mağaralarından sağa doğru eğiliyor, battığı zaman da sola doğru onları makaslayıp geçiyor (hiçbir halde onların üzerine düşüp kendilerini rahatsız etmiyor) ve onlar, mağaranın geniş bir dehlizi içindedirler. Bu (durum), Allah\'ın ayetlerindendir. Allah kimi doğru yola iletirse o, yolu bulmuştur; kimi de sapıklıkta bırakırsa, artık onun için yol gösteren bir dost bulamazsın.'),
(2158, 18, 18, 'Uykuda oldukları halde sen onları uyanıklar sanırsın onları (uykuda) sağa sola çeviririz. Köpekleri de girişte iki kolunu (ön ayaklarını) uzatmış vaziyettedir. Onların durumunu görseydin, mutlaka onlardan dönüp kaçardın. Ve onlardan içine korku dolardı.'),
(2159, 18, 19, 'Yine böyle onları dirilttik ki, kendi aralarında (birbirlerine) sorsunlar: İçlerinden biri: "Ne kadar kaldınız?" dedi. "Bir gün, ya da günün bir parçası (kadar kaldık)." dediler. (Fakat işin içyüzünü iyice bilmediklerinden herşeyi en iyi bilenin Allah olduğunu ifade ettiler): "Ne kadar kaldığınızı Rabbiniz daha iyi bilir, dediler, birinizi şu gümüş (para) ile şehre gönderin, baksın, hangi yiyecek daha temiz (ve nefis) ise ondan size bir azık getirsin; fakat çok dikkatli davransın, sakın sizi birisine sezdirmesin."'),
(2160, 18, 20, 'Çünkü onlar sizi ellerine geçirirlerse taşlayarak öldürürler, yahut kendi dinlerine döndürürler ki, o takdirde asla iflah olamazsınız.'),
(2161, 18, 21, '(Nasıl onları uyutup sonra uyandırdıksa yine) böylece onları (bazı insanlara) buldurduk ki, Allah\'ın (öldükten sonra diriltme) va\'dinin gerçek olduğunu ve (Duruşma) saatin(in geleceğin)de asla şüphe olmadığını bilsinler. (Bulanlar), o sırada kendi aralarında onların durumlarını tartışıyorlardı: "Onların üstüne bir bina yapın!" dediler. Rableri onları daha iyi bilir. Onların işine gaalip gelen(yetkili)ler: "Mutlaka onların üstüne bir mescid yapacağız" dediler.'),
(2162, 18, 22, 'Görülmeyene taş atar gibi: "Onlar üçtür, dördüncüleri köpekleridir" diyecekler; "Beştir, altıncıları köpekleridir" diyecekler. "(Hayır,) Yedidir, sekizincileri köpekleridir.\' diyecekler. De ki: "Onların sayısını Rabbim daha iyi bilir. Onları bilen azdır. Onun için onlar hakkında, sathi tartışma dışında, derin münakaşaya girme ve onlar hakkında bunlardan hiçbirine bir şey sorma.'),
(2163, 18, 23, 'Hiçbir şey için "Bunu yarın yapacağım" deme.'),
(2164, 18, 24, 'Ancak "Allah dilerse (yapacağım)" (de). Unuttuğun zaman Rabbini an ve "Rabbimin beni bundan daha doğru bir bilgiye ulaştırcağını umarım" de.'),
(2165, 18, 25, 'Mağaralarında üçyüz yıl kaldılar. Dokuz (yıl) da ilave ettiler.'),
(2166, 18, 26, 'De ki: "Onların ne kadar kaldıklarını Allah daha iyi bilir. Göklerin ve yerin gaybı O\'nundur. O ne güzel görendir, ne güzel işitendir! Onların, O\'ndan başka bir yardımcısı yoktur. Ve O, kendi hükmüne kimseyi ortak etmez.'),
(2167, 18, 27, 'Rabbinin Kitabı\'ndan sana vahyedileni oku; O\'nun sözlerini değiştirecek yoktur. O\'ndan başka sığınılacak bir kimse de bulamazsın.'),
(2168, 18, 28, 'Nefsini, sabah akşam, rızasını isteyerek Rablerine yalvaranlarla beraber tut (onlarla beraber bulunmağa candan sabret). Gözlerin, dünya hayatının süsünü isteyerek onlardan başka yana sapmasın. Kalbini bizi anmaktan alıkoyduğumuz keyfine uyan ve işi, hep aşırılık olan kişiye itaat etme.'),
(2169, 18, 29, 'De ki: "Bu gerçek, Rabbinizdendir. Artık dileyen inansın, dileyen inkar etsin." Çünkü biz zalimlere öyle bir ateş hazırladık ki, çadırı onları kuşatmıştır. Eğer (susuzluktan) feryad edip yardım isteseler erimiş maden gibi yüzleri haşlayan bir su ile kendilerine yardım edilir! O ne kötü bir içecektir ve ne kötü bir dayanacak(koltuk)dur!'),
(2170, 18, 30, 'Onlar ki inandılar ve iyi işler yaptılar; elbette biz işi güzel yapanın ecrini zayi etmeyiz.'),
(2171, 18, 31, 'Onlar öyle kimselerdir ki kendileri için Adn cennetleri vardır. Altlarından ırmaklar akar. Orada altın bileziklerle bezenirler; ince ipekten yeşil giysiler giyerek koltuklar üzerine yaslanırlar. Ne güzel sevap ve ne güzel dayanacak (koltuk)!'),
(2172, 18, 32, 'Onlara şu iki adamı misal olarak anlat: İkisinden birine iki üzüm bağı vermiş, onların etrafını hurmalarla çevirmiş, ortalarında da ekin bitirmiştik.'),
(2173, 18, 33, 'Her iki bağ da yemişini vermiş, ondan hiçbir şey eksik etmemişti. Aralarından bir de ırmak akıtmıştık.'),
(2174, 18, 34, 'O(adam)ın (başka) ürünü de vardı. Arkadaşiyle konuşurken ona; "Ben malca senden zenginim, adamca da senden güçlüyüm." dedi.'),
(2175, 18, 35, '(Böylece) kendisine yazık ederek bağına girdi: "Bunun yok olacağını hiç sanmam" dedi.'),
(2176, 18, 36, 'Kıyametin kopacağını da sanmıyorum. Şayet Rabbime döndürülsem bile (orada) bundan daha güzel bir sonuç (daha güzel bir yer) bulurum.'),
(2177, 18, 37, 'Kendisiyle konuşan arkadaşı ona dedi ki: "Seni topraktan, sonra nutfe (sperm)den yaratan, sonra da seni bir adam biçimine koyan Rabbine nankörlük mü ettin?"'),
(2178, 18, 38, 'Fakat O Allah benim Rabbimdir, ben Rabbime hiç kimseyi ortak koşmam!'),
(2179, 18, 39, 'Bağına girdiğin zaman: Maşaallah (Allah dilemiş de olmuş), kuvvet yalnız Allah iledir! demen gerekmez miydi? Gerçi sen beni malca ve evlatça senden az görüyorsun ama'),
(2180, 18, 40, 'Rabbim bana, senin bağından daha iyisini verebilir. Ve o(senin bağı)nın üzerine de gökten bir hesap görme afeti gönderir de bağın kupkuru bir toprak kesilir.'),
(2181, 18, 41, 'Yahut suyu dibe çekilir de bir daha su arayamazsın.'),
(2182, 18, 42, 'Derken (o inkarcı kişinin) ürünü yok edildi, çardakları üzerine yıkılmış durumda olan(bağ)ın karşısında ona harcadıklarına acıyarak ellerini uğuşturmağa başladı: "Ah nolaydı, ben Rabbime kimseyi ortak koşmamış olaydım!" diyordu.'),
(2183, 18, 43, 'Allah\'tan başka, kendisine yardım eden bir topluluğu da olmadı, kendi kendisini de kurtaramadı.'),
(2184, 18, 44, 'İşte o durumda velilik (koruyuculuk) yalnız hak olan Allah\'a mahsustur. O\'nun vereceği sevap da daha hayırlıdır, sonuç da daha hayırlıdır.'),
(2185, 18, 45, 'Onlara dünya hayatının, tıpkı şöyle olduğunu anlat: Gökten bir su indirdik, Yerin bitkisi onunla karıştı ve (sonunda bitkiler), rüzgarların savurduğu çöp kırıntıları haline geliverdi. Allah, her şeye kadirdir.'),
(2186, 18, 46, 'Mal ve oğullar dünya hayatının süsüdür. Kalıcı olan güzel işler ise Rabbinin katında sevapça da daha hayırlıdır, umutça da daha hayırlıdır.'),
(2187, 18, 47, '(Yalnız kalıcı eylemlerin yarar sağlayacağı) O gün dağları yürütürüz; yeri al açık (çırılçıplak) görürsün (dağlar savrulup dümdüz olmuş, engebeler kalkmıştır) onları (hep bir yere) toplamışız, hiçbirini bırakmamışızdır.'),
(2188, 18, 48, 'Ve hepsi sıra sıra senin Rabbine sunulmuşlardır: "Andolsun, sizi ilk defa yarattığımız gibi (çırılçıplak, yalnız, malsız, mülksüz) bize geldiniz! Oysa siz, size (yaptıklarınızdan hesap sorulacak) bir zaman tayin etmeyeceğimizi sanmıştınız!"'),
(2189, 18, 49, 'Kitap (ortaya) konulmuştur. Suçluların onun içindekilerden korkarak: "Vah bize, bu Kitaba da ne oluyor, ne küçük ne de büyük hiçbir şey bırakmıyor, her (yaptığımız) şeyi sayıp döküyor!" dediklerini görürsün. Yaptıklarını hazır bulmuşlardır. Rabbin kimseye zulmetmez.'),
(2190, 18, 50, 'Meleklere: "Adem\'e secde edin!" demiştik; secde ettiler, yalnız İblis etmedi. O cinlerdendi, Rabbinin buyruğu dışına çıktı. Şimdi siz, benden ayrı olarak onu ve onun neslini dostlar mı ediniyorsunuz? Oysa onlar, sizin düşmanınızdır. Zalimler için ne kötü bir değiştirmedir (bu. Dost olan Allah\'ı bırakıp düşman olan şeytanı ve zürriyetini dost tutmak)!'),
(2191, 18, 51, 'Ben onları ne göklerin, yerin, yaratılmasında ve ne de kendilerinin yaratılmasında hazır bulundurdum; yoldan şaşırtanları (kendime) yardımcı tutmuş da değilim.'),
(2192, 18, 52, 'O gün (Allah, kafirlere) der ki: "Benim ortaklarım zannettiğiniz şeyleri çağırın (da sizi azabımdan kurtarsınlar)! İşte çağırdılar ama (çağırdıkları), kendilerine cevap vermediler. Ve biz onların aralarına tehlikeli bir uçurum koyduk.'),
(2193, 18, 53, 'Suçlular ateşi gördüler, artık içine düşeceklerini iyice anladılar, fakat ondan kaçacak bir yer bulamadılar.'),
(2194, 18, 54, 'Andolsun biz bu Kur\'an\'da insanlara her çeşit misali türlü biçimlerde anlattık. Ama insan, tartışmaya her şeyden daha çok düşkündür.'),
(2195, 18, 55, 'Kendilerine hidayet geldiği zaman insanları inanmaktan ve Rablerine istiğfar etmekten alıkoyan şey, ancak evvelkilerin yasasının kendilerine de gelmesi(ni) yahut azabın açıkça karşılarına gelmesi(ni beklemeleri)dir.'),
(2196, 18, 56, 'Biz elçileri sadece müjdeleyiciler ve uyarıcılar olarak göndeririz. İnkar edenler, hakkı batılla gidermek için mücadele ediyorlar. (Onlar), ayetlerimle ve uyarıldıkları şeylerle alay ettiler.'),
(2197, 18, 57, 'Kendisine Rabbinin ayetleri hatırlatıldığı halde onlardan yüz çeviren ve ellerinin (yapıp) öne sürdüğü(günahlarını, isyanları)nı unutandan daha zalim kim olabilir? Biz onların kalbleri üzerine, onu anlamalarına engel olan örtüler, kulaklarının içine de ağırlık koymuşuz. Onları doğru yola çağırsan da bu halde asla doğru yola gelmezler (çünkü gerçeğe basiretlerini kapamışlardır).'),
(2198, 18, 58, 'Ama çok bağışlayan, esirgeyen Rabbin eğer onları, yaptıklariyle hemen cezalandıracak olsaydı, onların azabını çabuklaştırırdı. Fakat onlar için va\'dedilen bir zaman vardır ki, ondan (kaçıp) sığınacak bir yer bulamayacaklardır.'),
(2199, 18, 59, 'İşte şu kentler de zulmetmeğe başlayınca onları helak ettik. Onları helak etmek için de bir süre belirlemiştik.'),
(2200, 18, 60, 'Musa uşağına demişti ki: "Durmayıp ya iki denizin birleştiği yere varacağım veya uzun bir zaman yürüyeceğim."'),
(2201, 18, 61, 'İkisi (yürüdüler), iki denizin birleştiği yere varınca, balıklarını unuttular, (balık) sıyrılıp denizde yolunu tuttu.'),
(2202, 18, 62, 'Orayı geçip gittiklerinde (Musa) uşağına: "Kahvaltımızı bize getir (de yiyelim), andolsun ki, bu yolculuğumuzdan (epey) yorgunluk çektik." dedi.'),
(2203, 18, 63, '(Uşağı): "Gördün mü, dedi, kayaya sığındığımız vakit balığı unuttum. Onu söylememi, bana ancak şeytan unutturdu. (Balık), şaşılacak biçimde denizin içinde yolunu tuttu!'),
(2204, 18, 64, '(Musa): "İşte aradığımız o idi." dedi. Tekrar izlerini ta\'kibederek geriye döndüler, (kayaya vardılar).'),
(2205, 18, 65, '(Orada) Kullarımızdan bir kul buldular ki, biz ona katımızdan bir rahmet vermiştik ve ona katımızdan bir ilim öğretmiştik.'),
(2206, 18, 66, 'Musa ona: "Sana öğretilenden, bana da bir bilgi öğretmen için sana tabi olabilir miyim?" dedi.'),
(2207, 18, 67, '(O da): "Sen benimle beraber bulunmağa dayanamazsın" dedi.'),
(2208, 18, 68, 'Sana bildirilmeyen bir şeye nasıl dayanabilirsin?'),
(2209, 18, 69, '(Musa): "İnşaallah, dedi, beni sabredici bulursun, senin emrine karşı gelmem."'),
(2210, 18, 70, '(O kul): "O halde, dedi, eğer bana tabi olursan ben sana anlatıncaya kadar (yaptığım) hiçbir şey hakkında bana soru sorma."'),
(2211, 18, 71, 'Bunun üzerine yürüdüler. Nihayet gemiye bindikleri zaman gemiyi deliverdi. (Musa): "Halkını boğmak için mi gemiyi deldin? Gerçekten sen çok tehlikeli bir iş yaptın!" dedi.'),
(2212, 18, 72, '(O kul): "Sen benimle beraber bulunmağa dayanamazsın demedim mi?" dedi.'),
(2213, 18, 73, '(Musa): "Unuttuğum şeyden ötürü beni kınama ve bana bu işimden dolayı bir güçlük çıkarma." dedi.'),
(2214, 18, 74, 'Yine yürüdüler. Nihayet bir oğlana rastladılar. (O kul) hemen onu öldürdü. (Musa): "Bir can karşılığı olmadan temiz bir cana kıydın ha? Doğrusu sen, çirkin bir iş yaptın!" dedi.'),
(2215, 18, 75, '(O kul): "Ben sana, sen benimle beraber bulunmağa dayanamazsın, dememiş miydim? dedi.'),
(2216, 18, 76, '(Musa) dedi ki: "Eğer bundan sonra (bir daha) sana bir şey sorarsam, artık bana arkadaş olma. (O zaman) benim tarafımdan sana özür ulaşmıştır (artık benden ayrılmakta mazur sayılırsın).'),
(2217, 18, 77, 'Yine yürüdüler. Nihayet bir kent halkına varıp onlardan yemek istediler (kent halkı) onları konuklamaktan kaçındılar. Derken orada yıkılmağa yüz tutan bir duvar buldular; hemen onu doğrulttu. (Musa): "İsteseydin buna karşılık bir ücret alırdın," dedi.'),
(2218, 18, 78, 'İşte, dedi bu, benimle senin aramızın ayrılmasıdır. Şimdi sana sabredemeğin şeylerin içyüzünü haber vereceğim.'),
(2219, 18, 79, 'O (yaraladığım) gemi, denizde çalışan yoksullarındı. Onu kusurlu yapmak istedim, çünkü onların ilerisinde her (sağlam) gemiyi zorla alan bir kral vardı.'),
(2220, 18, 80, 'Oğlana gelince: Onun anası babası mü\'min insanlardı. Bunun, onlara azgınlık ve küfür sarmasından korktuk.'),
(2221, 18, 81, 'İstedik ki Rableri onun yerine onlara ondan daha temiz, daha merhametli (ana babasına iyilik eden) birini versin.'),
(2222, 18, 82, 'Duvar ise şehirde iki yetim çocuğun idi. Altında onlara ait bir hazine vardı. Babaları da iyi bir kimse idi. Rabbin istedi ki onlar (büyüyüp) güçlü çağlarına ersinler ve Rabbinden bir rahmet olarak hazinelerini çıkarsınlar. Bunları, ben kendiliğimden yapmadım. İşte senin sabredemediğin şeylerin içyüzü budur.'),
(2223, 18, 83, '(Ey Muhammed), sana Zu\'l-Karneyn\'den soruyorlar. De ki: "Size ondan bir anı okuyacağım."'),
(2224, 18, 84, 'Biz onu yeryüzünde güçlü kıldık ve ona herşeyden bir sebep (istediği herşeye ulaşmanın yolunu, aracını) verdik.'),
(2225, 18, 85, 'O da (kendisini batı ülkelerine ulaştıracak) bir yol tuttu.'),
(2226, 18, 86, 'Nihayet güneşin battığı yere ulaşınca onu, kara balçıklı bir gözede batar buldu. Onun yanında da bir kavim buldu. Dedik ki: "Ey Zu\'l-Karneyn, (onlara) ya azab edersin veya kendilerine güzel davranırsın (onları güzellikle yola getirirsin. Nasıl istersen öyle yaparsın)."'),
(2227, 18, 87, 'Dedi: "Kim haksızlık ederse, ona azab edeceğiz, sonra o, Rabbine döndürülecektir. O da ona görülmemiş bir azab edecektir."'),
(2228, 18, 88, 'Fakat inanıp iyi iş yapan kimseye de en güzel mükafat vardır. Ona buyruğumuzdan kolay olanı söyleyeceğiz (onu zor işlere koşmayacağız).'),
(2229, 18, 89, 'Sonra yine bir yol tuttu.'),
(2230, 18, 90, 'Nihayet güneşin doğduğu yere ulaşınca onu, güneşe karşı kendilerine siper yapmadığımız bir kavim üzerine doğar buldu.'),
(2231, 18, 91, 'İşte (Zu\'l-Karneyn) böyle (yüksek bir mevkie ve hükümranlığa sahip) idi. Onun yanında (daha) nice bilgi ve yetki bulunduğunu biliyorduk.'),
(2232, 18, 92, 'Sonra yine bir yol tuttu.'),
(2233, 18, 93, 'Nihayet iki sed arasına ulaşınca onların önünde hemen hiç söz anlamayan bir kavim buldu.'),
(2234, 18, 94, 'Dediler ki: "Ey Zu\'l-Karneyn, Ye\'cuc ve Me\'cuc, bu yerde bozgunculuk yapıyorlar. Bizimle onların arasına bir sed yapman için sana bir vergi verelim mi?"'),
(2235, 18, 95, 'Dedi ki: "Rabbimin, beni içinde bulundurduğu imkanlar, (sizin vereceğinizden) daha hayırlıdır. Siz bana (insan) güc(üy)le yardım edin de sizinle onlar arasına sağlam bir engel yapayım."'),
(2236, 18, 96, 'Bana demir kütleleri getirin. (Zu\'l-Karneyn) iki dağın arasını (demir kütleleriyle doldurtup dağlarla) aynı seviyeye getirince: "Üfleyin!" dedi. Nihayet o(demir kütleleri)ni bir ateş haline sokunca "Getirin bana, üzerine erimiş katran dökeyim," dedi.'),
(2237, 18, 97, 'Artık (Ye\'cuc Me\'cuc) onu ne aşabildiler, ne de delebildiler.'),
(2238, 18, 98, '(Zu\'l-Karneyn) dedi: "Bu, Rabbimin bir rahmetidir. Rabbimin va\'di gel(ip Ye\'cuc ve Me\'cuc\'un çıkması, yahut kıyametin kopması gerek)diği zaman onu yerle bir eder; şüphesiz Rabbimin va\'di gerçektir."'),
(2239, 18, 99, 'Biz o gün (Ye\'cuc ve Me\'cuc\'u) bırakmışızdır: Birbiri içinde dalgalanır(lar). Sur\'a da üflenmiştir ve onları hep bir araya toplamışızdır.'),
(2240, 18, 100, 'O gün cehennemi kafirlere açıkça göstereceğiz.'),
(2241, 18, 101, 'Onlar ki beni anmağa karşı gözleri perde içinde idi ve (Kur\'an\'ı) dinlemeğe tahammül edemezlerdi.'),
(2242, 18, 102, 'O nankörler benden ayrı olarak kullarımı kendilerine veliler yapacaklarını mı sandılar? Biz kafirlere cehennemi konak olarak hazırladık.'),
(2243, 18, 103, 'De ki: "Size işleri bakımından en çok ziyana uğrayacak olanları söyleyeyim mi?"'),
(2244, 18, 104, 'Dünya hayatında bütün çabaları boşa gitmiş olan ve kendileri de iyi iş yaptıklarını sanan kimseleri?'),
(2245, 18, 105, 'İşte onlar, Rablerinin ayetlerini ve O\'na kavuşmayı inkar eden, bu yüzden eylemleri boşa çıkan kimselerdir. (Yaptıkları işler tamamen boşa çıktığından) kıyamet günü onlar için bir terazi kurmayız (veya onlara hiçbir değer vermeyiz).'),
(2246, 18, 106, 'İnkar ettikleri, ayetlerimi ve elçilerimi eğlence yerine koydukları için onların cezası cehennemdir.'),
(2247, 18, 107, 'İnanıp iyi işler yapanlara gelince, onların konağı da Firdevs cennetleridir.'),
(2248, 18, 108, 'Orada sürekli kalacaklardır. Oradan hiç ayrılmak istemezler.'),
(2249, 18, 109, 'De ki: "Rabbimin sözleri(ni yazmak) için deniz mürekkep olsa, Rabbimin sözleri tükenmeden önce deniz tükenir." Yardım için bir o kadarını daha getirsek (yine yetmez).'),
(2250, 18, 110, 'De ki: "Ben de sizin gibi bir insanım; Tanrınızın bir tek Tanrı olduğu bana vahyolunuyor. Kim Rabbine kavuşmayı arzu ediyorsa iyi iş yapsın ve Rabbine (yaptığı) ibadete hiç kimseyi ortak etmesin.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Kaf ha ya \'ayn sad.'),
(2252, 19, 2, 'Bu, Rabbinin, kulu Zekeriyya\'ya rahmetini anıştır.'),
(2253, 19, 3, 'O, Rabbine gizli bir seslenişle yalvarmıştı:'),
(2254, 19, 4, 'Rabbim, demişti, ben, bende kemik gevşedi; baş, ihtiyarlk aleviyle tutuştu. Rabbim, sana du\'a ile hiçbir zaman bahtsız olmadım (her du\'a ettikçe kabul buyurdun, beni istediğimden mahrum etmedin).'),
(2255, 19, 5, 'Doğrusu ben arkamdan, yerime geçecek yakınlar(ımın iyi hareket etmeycekler)inden korktum; karım da kısır. (Ne olur) katından bana yerime geçecek bir veli lutfet.'),
(2256, 19, 6, 'Ki, (o), bana ve Ya\'kub oğullarına mirasçı olsun. Rabbim, onu beğendiğin bir insan yap.'),
(2257, 19, 7, '(Allah buyurdu): Ey Zekeriyya, biz sana bir oğul müjdeleriz, adı Yahya\'dır. Daha önce ona hiç kimseyi adaş yapmadık (ondan önce kimseye bu adı vermedik.)"'),
(2258, 19, 8, '(Zekeriyya): "Rabbim, dedi benim nasıl oğlum olur? Karım da kısırdır. Ben ise ihtiyarlığın son sınırına vardım."'),
(2259, 19, 9, 'Dedi: "Öyledir, ama Rabbin: \'O bana kolaydır, daha önce sen de hiçbir şey değilken seni de yaratmıştım\' dedi."'),
(2260, 19, 10, 'Rabbim, dedi, (öyle ise) bana bir işaret ver. "Senin işaretin, sapasağlam olduğun halde tam üç gece (ve gündüz) insanlarla konuşamamandır." dedi.'),
(2261, 19, 11, '(Zekeriyya), ma\'bedden kavminin karşısına çıkıp onlara: "Sabah akşam (Rabbinizi) tesbih edin!" diye işaret etti.'),
(2262, 19, 12, 'Ey Yahya, Kitabı kuvvetle tut (Onun emirlerini uygula). (dedik) ve ona çocuk iken hikmet verdik.'),
(2263, 19, 13, 'Katımızdan bir rahmet (bir acıma duygusu) ve temizlik de (verdik; o günahlardan) korunan oldu.'),
(2264, 19, 14, 'Ana babasına iyilik ediciydi, baş kaldıran bir zorba değildi.'),
(2265, 19, 15, 'Doğduğu gün, öleceği gün ve diri olarak kaldırılacağı gün ona selam olsun!'),
(2266, 19, 16, 'Kitapta Meryem\'i de an. Bir zaman o ailesinden ayrılıp doğu yönünde bir yere çekilmişti.'),
(2267, 19, 17, 'Onlarla kendisi arasına bir perde çekmişti. Biz de ruhumuzu (Cebrail\'i) ona gönderdik. (O) ona düzgün bir insan şeklinde göründü.'),
(2268, 19, 18, '(Meryem) dedi ki: "Ben senden, çok esirgeyen(Allah)\'a sığınırım. Eğer (Allah\'tan) korkuyorsan (bana dokunma)."'),
(2269, 19, 19, '(Ruh): "Ben, dedi, sadece Rabbinin elçisiyim. Sana tertemiz bir erkek çocuğu hediye edeyim diye (geldim)."'),
(2270, 19, 20, 'Benim nasıl oğlum olur, dedi, bana bir insan dokunmadı ve ben bir kahpe de değilim.'),
(2271, 19, 21, '(Ruh): "Öyledir, dedi, Rabbin: \'O bana kolaydır. Onu insanlara bir mu\'cize ve bizden bir rahmet kılmak için (bunu yapacağız)\' dedi" ve iş olup bitti.'),
(2272, 19, 22, '(Meryem), ona gebe kaldı. Onunla uzak bir yere çekildi.'),
(2273, 19, 23, 'Doğum sancısı onu, bir hurma dalı(nın altı)na getirdi. "Keşke dedi, bundan önce ölseydim, unutulup gitseydim!"'),
(2274, 19, 24, 'Altından (Ruh) ona şöyle seslendi: "Üzülme Rabbin alt tarafında bir su arkı var etti."'),
(2275, 19, 25, 'Hurma dalını sana doğru silkele, üzerine olmuş, taze hurma dökülsün.'),
(2276, 19, 26, 'Ye, iç, gözün aydın olsun! Eğer insanlardan birini görürsen: Ben Rahman için (susma) oruc(u) adadım, bugün hiçbir insanla konuşmayacağım" de."'),
(2277, 19, 27, '(Meryem) onu taşıyarak kavmine getirdi: "Ey Meryem, dediler, sen tuhaf bir iş yaptın."'),
(2278, 19, 28, 'Ey Harun\'un kızkardeşi, baban kötü bir adam değildi, annen de fahişe değildi (sen ne yaptın böyle)?'),
(2279, 19, 29, '(Meryem), çocuğu gösterdi. Dediler ki: "Beşikteki çocukla nasıl konuşuruz?"'),
(2280, 19, 30, '(Çocuk): "Ben Allah\'ın kuluyum, dedi, (O) bana Kitabı verdi, beni peygamber yaptı."'),
(2281, 19, 31, 'Beni bulunduğum her yerde yararlı kıldı. Sağ olduğum sürece bana namaz kılmayı, zekat vermeyi emretti!'),
(2282, 19, 32, '(Beni) anneme iyilik eder (kıldı), beni baş kaldıran bir zorba yapmadı.'),
(2283, 19, 33, 'Doğduğum gün de, öleceğim gün de ve diri olarak kaldırılacağım gün de bana esenlik verilmiştir. 	Peace on me the day I was born, and the day I die, and the day I shall be raised alive!'),
(2284, 19, 34, 'İşte Meryem oğlu Îsa. Şüphe edip ayrılığa düştükleri şey, "gerçek söz"e göre budur.'),
(2285, 19, 35, 'Çocuk edinmek, Allah\'a yakışmaz. O\'nun şanı yücedir. Bir işi yapmak istedi mi ona sadece "ol" der, (o da) olur.'),
(2286, 19, 36, 'Şüphesiz, Allah benim de Rabbim, sizin de Rabbinizdir, O\'na kulluk edin. İşte doğru yol budur.'),
(2287, 19, 37, 'Kendi aralarından hizipler, ayrılığa düştüler. Artık büyük bir günü görmekten ötürü vay kafirlerin haline!'),
(2288, 19, 38, 'Bize geldikleri gün ne güzel işitir, ne güzel görürler. Ama o zalimler, bugün apaçık sapıklık içindedirler!'),
(2289, 19, 39, 'Onları şu hasret gününe karşı uyar ki, o zaman kendileri gaflet içinde inanmamakta ısrar ederlerken iş bitirilmiş olur (yaptıklarına pişman olup hasret çeker dururlar, ama iş işten geçmiştir artık).'),
(2290, 19, 40, 'Dünyaya ve üzerinde bulunanlara biz varis oluruz biz, ve bize döndürülürler.'),
(2291, 19, 41, 'Kitapta İbrahim\'i de an; gerçekten o, çok doğru bir peygamberdi.'),
(2292, 19, 42, 'Babasına demişti ki: "Babacığım, işitmeyen görmeyen ve sana hiçbir yararı olmayan şeylere niçin tapıyorsun?"'),
(2293, 19, 43, 'Babacığım, bana sana, gelmeyen bir bilgi geldi; bana uy, seni düzgün bir yola ileteyim.'),
(2294, 19, 44, 'Babacığım, şeytana tapma, çünkü şeytan, Rahman\'a isyan etmiştir.'),
(2295, 19, 45, 'Babacığım, ben sana Rahman\'dan bir azabın dokunmasından korkuyorum. O zaman, şeytanın dostu olursun.'),
(2296, 19, 46, '(Babası): "Ey İbrahim, dedi, sen benim tanrılarımdan yüz mü çeviriyorsun? Eğer (onlara dil uzatmaktan) vazgeçmezsen, andolsun seni taşlarım. Uzun süre benden ayrıl, git!"'),
(2297, 19, 47, '(İbrahim): "Selam sana, (esenlik içinde kal), dedi, senin için Rabbimden mağfiret dileyeceğim. Çünkü O, bana çok lutufkardır."'),
(2298, 19, 48, 'Sizden de, Allah\'tan başka yalvardıklarınızdan da ayrılıyor ve yalnız Rabbime yalvarıyorum. Umarım ki Rabbime yalvarmakla bahtsız olmam (istediklerimden mahrum bırakılmam).'),
(2299, 19, 49, 'İşte onlardan ve onların Allah\'tan başka taptıklarından ayrılınca biz ona İshak\'ı ve (İshak\'ın oğlu) Ya\'kub\'u armağan ettik ve hepsini de peygamber yaptık.'),
(2300, 19, 50, 'Onlara rahmetimizden (mal ve çocuk) lutfettik ve onlar için yüce bir doğruluk dili verdik.'),
(2301, 19, 51, 'Kitapta Musa\'yı da an, çünkü o, içi temiz (bir insan)dı ve elçi bir peygamberdi.'),
(2302, 19, 52, 'Ona Tur\'un sağ tarafından seslendik ve onu, özel konuşmak için (kendimize) yaklaştırdık.'),
(2303, 19, 53, 'Ona, acıdığımızdan dolayı kardeşi Harun\'u da peygamber olarak armağan ettik.'),
(2304, 19, 54, 'Kitapta İsma\'il\'i de an. Çünkü o sözünde duran, elçi bir peygamberdi.'),
(2305, 19, 55, 'Halkına namaz kılmayı, zekat vermeyi emrederdi. Rabbi yanında beğenilmişti.'),
(2306, 19, 56, 'Kitapta İdris\'i de an: Çünkü o, çok doğru bir peygamberdi.'),
(2307, 19, 57, 'Onu yüce bir yere yükseltmiştik.'),
(2308, 19, 58, 'İşte bunlar; Allah\'ın ni\'met verdiği peygamberlerden, Adem, neslinden, Nuh ile beraber gemide taşıdıklarımızın neslinden, İbrahim ve İsrail (Ya\'kub) neslinden, yol gösterdiğimiz ve seçtiğimiz kimselerdendir. Onlara Rahman\'ın ayetleri okunduğu zaman ağlayarak secdeye kapanırlardı.'),
(2309, 19, 59, 'Onlardan sonra yerlerine öyle bir nesil geldi ki, namazı zayi ettiler, şehvetlerine uydular. Onlar kötülük bulacaklardır.'),
(2310, 19, 60, 'Ancak tevbe eden, inanan ve iyi işler yapanlar, cennete girecekler ve hiç haksızlığa uğratılmayacaklardır.'),
(2311, 19, 61, 'Rahman\'ın kullarına gıyaben va\'dettiği Adn cennetleri(ne gireceklerdir). Şüphesiz O\'nun va\'di yerine gelecektir.'),
(2312, 19, 62, 'Orada boş söz değil, yalnız selam işitirler. Orada sabah akşam rızıkları da hazırdır.'),
(2313, 19, 63, 'İşte kullarımızdan, korunanlara vereceğimiz cennet budur.'),
(2314, 19, 64, 'Biz ancak Rabbinin emriyle ineriz. Önümüzde, arkamızda ve bunlar arasında olan herşey O\'na aittir. Rabbin, asla unutkan değildir.'),
(2315, 19, 65, '(O), göklerin, yerin ve bunlar arasında bulunan şeylerin Rabbidir. O\'na kulluk et ve O\'na kullukta sabret. Hiç O\'nun adıyla anılan birini biliyor musun?'),
(2316, 19, 66, 'İnsan: "Ben öldükten sonra mı diri olarak çıkarılacağım?" diyor.'),
(2317, 19, 67, 'İnsan önceden hiçbir şey değilken kendisini nasıl yarattığımızı düşünmüyor mu?'),
(2318, 19, 68, 'Rabbine andolsun ki, onları ve şeytanları mutlaka toplayacağız, sonra onları diz çökmüş vaziyette cehennemin çevresinde bulunduracağız.'),
(2319, 19, 69, 'Sonra her milletten Rahman\'a en çok karşı geleni ayıracağız.'),
(2320, 19, 70, 'Sonra, elbette biz, kimlerin oraya girmeğe uygun olduğunu daha iyi biliriz.'),
(2321, 19, 71, 'İçinizden oraya gitmeyecek hiç kimse yoktur. Bu, Rabbinin üzerine aldığı kesin borçtur.'),
(2322, 19, 72, 'Sonra korunanları kurtarırız ve zalimleri öyle diz üstü çökmüş olarak bırakırız.'),
(2323, 19, 73, 'Onlara açık açık ayetlerimiz okunduğu zaman, inkar edenler, inananlar için "İki topluluktan hangisinin makamı daha hayırlı, meclisi (mevkii) daha güzeldir?" derler.'),
(2324, 19, 74, 'Onlardan önce nice nesiller helak ettik ki onlar eşyaca ve gösterişce daha güzeldi.'),
(2325, 19, 75, 'De ki: "Kim sapıklık içinde ise Rahman ona süre versin (ne çıkar). Nihayet va\'dedildiklerini Ğazabı veya (Duruşma) sa\'ati(ni)Ğ gördükleri zaman, kimin yerce daha kötü ve adamca daha zayıf olduğunu bileceklerdir.'),
(2326, 19, 76, 'Allah, yola gelenlerin hidayetini artırır. Kalıcı olan yararlı işler, Rabbinin yanında hem mükafat bakımından daha iyidir, hem varılacak yer bakımından daha iyidir!'),
(2327, 19, 77, 'Ayetlerimizi inkar edip: "Bana mal ve evlad verilecek" diyen adamı gördün mü?'),
(2328, 19, 78, 'Gaybe mi çık(ıp bak)tı, yoksa Rahman\'ın huzurunda bir söz mü aldı (Allah ile bir andlaşma mı yaptı)?'),
(2329, 19, 79, 'Hayır (yanılıyor), biz onun dediğini yazacağız ve onun için azabı uzattıkça uzatacağız.'),
(2330, 19, 80, 'O dediği(malı ve evladı)na biz varis olacağız (nesi varsa hepsi bize kalacak) ve o, bize tek başına gelecek (yanında ne malı, ne de evladı olmayacak).'),
(2331, 19, 81, 'Kendilerine destek olsunlar diye Allah\'tan başka tanrılar edindiler.'),
(2332, 19, 82, 'Hayır, (yarın o taptıkları tanrılar), bunların tapmalarını inkar edecekler ve bunlara zıd olacaklardır.'),
(2333, 19, 83, 'Görmedin mi biz kafirlere şeytanları gönderdik, onları oynatıp duruyorlar.'),
(2334, 19, 84, 'Onlar hakkında acele etme, biz onlar(ın günlerini ve nefeslerini doldurmaları) için saydıkça sayıyoruz.'),
(2335, 19, 85, 'Korunanları, binek üzerinde ikram ile Rahman\'a götürdüğümüz gün,'),
(2336, 19, 86, 'Suçluları da yaya ve susuz olarak cehenneme sürdüğümüz (gün),'),
(2337, 19, 87, 'Yalnız Rahman\'ın huzurunda söz almış olanlardan başkaları şefa\'at edemezler.'),
(2338, 19, 88, 'Rahman çocuk edindi dediler.'),
(2339, 19, 89, 'Andolsun ki, "Siz pek kötü bir cür\'ette bulundunuz!"'),
(2340, 19, 90, 'Neredeyse o(sözün dehşeti)nden gökler çatlayacak, yer yarılacak ve dağlar yıkılıp dağılacaktır!.'),
(2341, 19, 91, 'Rahman için çocuk iddia ettiklerinden ötürü.'),
(2342, 19, 92, 'Çocuk edinmek Rahman\'a yakışmaz.'),
(2343, 19, 93, 'Göklerde ve yerde bulunan herkes Rahman\'a kul olarak gelecektir.'),
(2344, 19, 94, 'O, onların hepsini kuşatmış ve onları bir bir saymıştır.'),
(2345, 19, 95, 'Onların hepsi, kıyamet günü O\'na tek başına gelecektir.'),
(2346, 19, 96, 'İnanıp faydalı işler yapanlar için Rahman, (gönüllerde) bir sevgi yaratacak(onları herkese sevdirecek)tir.'),
(2347, 19, 97, 'Biz o(Kur\'a)n\'ı senin diline kolaylaştırdık ki, onunla korunanları müjdeleyesin ve inatçı bir kavmi onunla uyarasın.'),
(2348, 19, 98, 'Biz onlardan önce nice nesilleri helak ettik. Şimdi onlardan hiçbirini duyuyor musun, yahut onların gizli bir sesini işitiyor musun?');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Ta, Ha.'),
(2350, 20, 2, 'Biz bu Kur\'an\'ı sana güçlük çekesin diye indirmedik.'),
(2351, 20, 3, 'Ancak (Allah\'tan) korkanlara bir öğüt (olarak indirdik).'),
(2352, 20, 4, '(O) yeri ve yüce gökleri yaratan tarafından azar azar indirilmiştir.'),
(2353, 20, 5, 'Rahman Arş\'a istiva etmiş(kurulmuş)tur.'),
(2354, 20, 6, 'Göklerde, yerde, ikisinin arasında ve toprağın altında bulunanlar hep O\'nundur (ne kadar kapalı olursa olsun, O\'ndan hiçbir şey gizli kalmaz).'),
(2355, 20, 7, 'Sözü açık söylesen de (gizli söylesen de) muhakkak O, gizliyi de ondan daha gizlisini de bilir.'),
(2356, 20, 8, 'Allah ki, O\'ndan başka tanrı yoktur. En güzel isimler O\'nundur.'),
(2357, 20, 9, 'Musa\'nın haberi sana geldi mi?'),
(2358, 20, 10, 'Hani (o) bir ateş görmüştü de ailesine: "Siz durun ben bir ateş gördüm, belki ondan size bir kor getiririm, yahut ateşin yanında bir yol gösteren bulurum" demişti.'),
(2359, 20, 11, '(Musa), o(ateşin yanı)na gelince kendisine "Ey Musa!" diye seslenildi.'),
(2360, 20, 12, 'Ben, (evet) ben senin Rabbinim! Pabuçlarını çıkar. Çünkü sen, kutsal vadide, Tuva\'dasın.'),
(2361, 20, 13, 'Ben seni seçtim, şimdi vahyolunanı dinle.'),
(2362, 20, 14, 'Muhakkak ben, (evet) ben Allah\'ım, benden başka tanrı yoktur. (Yalnız) bana kulluk et ve beni anmak için namaz kıl.'),
(2363, 20, 15, '(Kıyamet) Sa\'at(i) mutlaka gelecektir. Herkesin, peşinde koştuğu işlerle cezalanması için, neredeyse onu gizleyeceğim.'),
(2364, 20, 16, 'Ona inanmayıp keyfine uyan kimse, seni on(a inanmak)dan alıkoymasın, sonra helak olursun!'),
(2365, 20, 17, 'Sağ elindeki nedir ey Musa?'),
(2366, 20, 18, '(Musa) dedi: "O, asa\'mdır. Ona dayanıyorum ve onunla davarıma yaprak silkeliyorum ve onda benim daha birçok ihtiyaçlarım var (onunla birçok ihtiyacımı gideririm)."'),
(2367, 20, 19, '(Allah) buyurdu; "(Yere) at onu ey Musa!"'),
(2368, 20, 20, '(Musa) attı, bir de ne görsün o, koşan kocaman bir yılan!'),
(2369, 20, 21, '(Allah): "Al onu, dedi, korkma biz onu yine ilk durumuna sokacağız."'),
(2370, 20, 22, 'Elini böğrüne sok; bir hastalık olmadan, ayrı bir mu\'cize olarak bembeyaz bir durumda çıksın.'),
(2371, 20, 23, 'Ki sana en büyük mu\'cizelerimizden bazılarını göstermiş olalım'),
(2372, 20, 24, 'İmdi sen Fir\'avn\'e git: çünkü o azdı.'),
(2373, 20, 25, '(Musa) dedi ki: "Rabbim, benim göğsümü aç (risalet görevini yüklenebilmesi için yüreğimi genişlet)"'),
(2374, 20, 26, 'Bana işimi kolaylaştır.'),
(2375, 20, 27, 'Dilimin düğümünü çöz.'),
(2376, 20, 28, 'Ki sözümü anlasınlar'),
(2377, 20, 29, 'Bana ailemden bir vezir ver:'),
(2378, 20, 30, 'Kardeşim Harun\'u.'),
(2379, 20, 31, 'Onunla arkamı kuvvetlendir.'),
(2380, 20, 32, 'Onu da işime ortak yap,'),
(2381, 20, 33, 'Ki seni çok tesbih edelim,'),
(2382, 20, 34, 'Ve seni çok analım,'),
(2383, 20, 35, 'Şüphesiz sen, bizi görmektesin.'),
(2384, 20, 36, '(Allah) buyurdu: "Ey Musa, istediğin sana verildi."'),
(2385, 20, 37, 'Zaten biz sana bir kez daha lutufta bulunmuştuk.'),
(2386, 20, 38, '(Sen doğduğun zaman,) Annene vahyedileni vahyetmiştik:'),
(2387, 20, 39, 'Onu sandığa koy, suya at; su onu sahile bıraksın; onu benim de düşmanım onun da düşmanı olan biri alacaktır. "Gözümün önünde yetiştirilmen için senin üzerine benden bir sevgi koydum (görenler senin üzerine koyduğum bu sevgiden ötürü sana meftun oldular)."'),
(2388, 20, 40, 'Kızkardeşin ona bakacak birini size göstereyim mi? diyordu. Böylece seni annene geri verdik ki gözü aydın olsun, üzülmesin. Sen bir de adam öldürmüştün. O zaman da seni tasadan kurtarmış ve seni iyice denemiştik. Medyen halkı arasında yıllarca kaldın. Sonra belirlediğimiz bir vakitte bize geldin ey Musa!'),
(2389, 20, 41, 'Seni kendim için yetiştirdim.'),
(2390, 20, 42, 'Sen ve kardeşin, ayetlerimi götürün, beni anmakta gevşeklik etmeyin.'),
(2391, 20, 43, 'Fir\'avn\'e gidin, çünkü o azdı.'),
(2392, 20, 44, 'Ona yumuşak söz söyleyin, belki öğüt alır veya korkar.'),
(2393, 20, 45, 'Dediler ki: "Rabbimiz, onun bize taşkınlık etmesinden, yahut iyice azmasından korkuyoruz."'),
(2394, 20, 46, 'Korkmayın, dedi, ben sizinle beraberim, işitir ve görürüm.'),
(2395, 20, 47, 'Haydi, varın ona, deyin ki: Biz senin Rabbinin elçileriyiz; İsrail oğullarını bizimle gönder, onlara azab etme. Biz Rabbinden sana bir ayet getirdik. Esenlik, hidayete uyanlaradır.'),
(2396, 20, 48, 'Bize, yalanlayıp yüz çevirenin, azaba uğrayacağı vahyolundu.'),
(2397, 20, 49, '(Fir\'avn): "Rabbiniz kimdir ey Musa?" dedi.'),
(2398, 20, 50, '(Musa): "Rabbimiz, her şeye yaratılışını (varlığını ve biçimini) verip sonra onu doğru yola ileten (yaratılış gayesine uygun yola yönelten)dir." dedi.'),
(2399, 20, 51, '(Fir\'avn): "Peki ya ilk nesillerin hali ne olacak?" dedi.'),
(2400, 20, 52, 'Dedi ki: "Onların bilgisi Rabbimin yanında bir Kitaptadır. Rabbim şaşmaz ve unutmaz."'),
(2401, 20, 53, 'O ki, yeri size beşik yaptı ve onda sizin için yollar açtı, gökten bir su indirdi. Onunla her çeşit bitkiden çiftler çıkardık.'),
(2402, 20, 54, 'Yeyin, hayvanlarınızı otlatın. Şüphesiz bunda, akıl sahipleri için ibretler vardır.'),
(2403, 20, 55, 'Sizi topraktan yarattık, yine oraya döndürürüz ve sizi bir kez daha ondan çıkarırız.'),
(2404, 20, 56, 'Andolsun biz o(Fir\'av)n\'a ayetlerimizin hepsini gösterdik, yine de yalanladı ve dayattı.'),
(2405, 20, 57, 'Ve: "Sen bizi büyünle yurdumuzdan çıkarasın diye mi geldin ey Musa?" dedi.'),
(2406, 20, 58, 'Biz de mutlaka sana o(se)nin (büyün) gibi bir büyü getireceğiz. Sen şimdi seninle bizim aramızda bir buluşma zamanı ve yeri tayin et; ne senin, ne de bizim caymayacağımız uygun bir yer olsun.'),
(2407, 20, 59, '(Musa): "Buluşma zamanınız, Süs (bayram) günü ve insanaların toplanacağı kuşluk vakti olsun" dedi.'),
(2408, 20, 60, 'Fir\'avn, dönüp gitti, hilesini (büyücüleri ve onların aletlerini) topladı, sonra (belirtilen yere) geldi.'),
(2409, 20, 61, 'Musa onlara: "Yazık size, dedi, Allah\'a yalan uydurmayın, sonra (O), bir azab ile kökünüzü keser, doğrusu iftira eden perişan olmuştur!"'),
(2410, 20, 62, '(Fir\'avn\'ın topladığı büyücüler), işlerini kendi aralarında tartıştılar ve gizli konuştular.'),
(2411, 20, 63, 'Dediler ki: "Bunlar iki büyücü, başka bir şey değil. Büyüleriyle sizi yurdunuzdan çıkarmak ve sizin örnek yolunuzu, (üstün dininizi) gidermek istiyorlar."'),
(2412, 20, 64, 'Onun için siz hilenizi toplayın, sonra sıra halinde gelin. Bugün üstün gelen başarmıştır.'),
(2413, 20, 65, '(Büyücüler önce Musa\'nın işe başlamasını istediler) Dediler ki: "Ey Musa, ya sen at, yahut önce atan biz olalım."'),
(2414, 20, 66, '(Musa): "Hayır siz atın!" dedi. (Attılar. Musa) bir de ne görsün: Büyülerinden ötürü onların ipleri ve sopaları gerçekten koşuyor gibi görünüyor.'),
(2415, 20, 67, 'Bu yüzden Musa, içinde bir korku duydu.'),
(2416, 20, 68, '(Biz kendisine): "Korkma, dedik, üstün gelecek sensin, sen!"'),
(2417, 20, 69, 'Sağ elindekini at! Onların yaptıklarını yutsun. Çünkü onların yaptıkları, bir büyücünün hilesidir. Büyücü de nereye varsa iflah olmaz!'),
(2418, 20, 70, 'Bunun üzerine büyücüler secdeye kapandılar: "Harun\'un ve Musa\'nın Rabbine inandık!" dediler.'),
(2419, 20, 71, '(Fir\'avn): "Ben size izin vermeden ona inandınız ha? O, size büyü öğreten büyüğünüzdür. Öyleyse ben de sizin ellerinizi ve ayaklarınızı çapraz keseceğim ve sizi hurma dallarına asacağım, hangimizin azabı daha çetin ve sürekli imiş bileceksiniz!" dedi.'),
(2420, 20, 72, 'Dediler ki: "Biz, seni, bize gelen açık delillere ve bizi yaratana tercih edemeyiz. Yapacağını yap, sen ancak bu dünya hayatında istediğini yapabilirsin."'),
(2421, 20, 73, 'Biz Rabbimize inandık ki (O) bizim günahlarımızı ve senin bizi yapmaya zorladığın büyüyü bağışlasın. (Elbette) Allah daha hayırlı ve (O\'nun mükafatı ve cezası) daha süreklidir.'),
(2422, 20, 74, 'Kim Rabbine suçlu olarak gelirse onun için cehennem vardır; orada ne ölür ne de yaşar.'),
(2423, 20, 75, 'Kim de iyi işler yapmış bir mü\'min olarak O\'na gelirse, işte onlar için de yüksek dereceler vardır:'),
(2424, 20, 76, 'Altlarından ırmaklar akan Adn cennetleri. Orada sürekli olarak kalırlar. İşte arınanların mükafatı budur!'),
(2425, 20, 77, 'Andolsun biz Musa\'ya: "Kullarımı geceleyin (Mısır\'dan çıkarıp) yürüt; (asanla suya) vur, denizde onlar için kuru bir yol (aç). (Fir\'avn\'ın sana) yetişme(sin)den korkma, (boğulmaktan) endişe etme." diye vahyetmiştik.'),
(2426, 20, 78, 'Fir\'avn, askerleriyle onların ardına düştü, denizden onları örten örttü (deniz onları örtüp boğdu).'),
(2427, 20, 79, 'Fir\'avn toplumunu saptırdı, doğru yola iletmedi.'),
(2428, 20, 80, 'Ey İsrail oğulları, biz sizi düşmanınızdan kurtardık ve Tur\'un sağ yanında, (Musa ile konuşmayı) size va\'dettik; üzerinize kudret helvasıyle bıldırcın indirdik.'),
(2429, 20, 81, 'Size verdiğimiz rızkın temizlerinden yeyin, ama bu hususta taşkınlık etmeyin; sonra gazabım üzerinize iner, kimin üstüne gazabım inerse o, düşmüş(mahvolmuş)tur.'),
(2430, 20, 82, 'Ve Ben, tevbe eden, inanan ve yararlı iş yapan, sonra da yola gelen kimseye karşı çok bağışlayıcıyımdır.'),
(2431, 20, 83, 'Seni kavminden çabucak ayrıl(ıp gel)meğe sevk eden nedir? (Niçin onları hemen bırakıp geldin) ey Musa? (dedik).'),
(2432, 20, 84, 'Dedi: "Onlar benim arkamdan geliyorlar, ya Rabbi razı olman için sana çabuk geldim."'),
(2433, 20, 85, '(Allah): "Ama biz senden sonra kavmini sınadık. Samiri onları saptırdı" dedi.'),
(2434, 20, 86, 'Bunun üzerine Musa, çok kızgın ve üzüntülü bir halde kavmine döndü: "Ey Kavmim, dedi, Rabbiniz size güzel bir va\'idde bulunmamış mıydı? Süre mi size uzun geldi (zamanla verdiğiniz sözü unuttunuz mu)? Yoksa Rabbinizden bir gazabın üstünüze inmesini mi istediniz ki, bana verdiğiniz sözden caydınız (beni izlemediniz)?"'),
(2435, 20, 87, 'Dediler ki: "Kendi malımızla senin sözünden çıkmadık", fakat o milletin (yani Mısırlıların) süs(eşyas)ından bize yükler yükletilmişti. Onları (ateşe) attık. Aynı şekilde Samiri de attı."'),
(2436, 20, 88, 'Onlara, böğürmesi olan bir buzağı heykeli ortaya çıkardı. Dediler ki, "Bu sizin de tanrınız, Musa\'nın da tanrısıdır, fakat o unuttu".'),
(2437, 20, 89, 'Onlar görmüyorlar mı ki o (buzağı) kendilerine bir söz söyleyemez; bir zarar, ve yarar veremez?'),
(2438, 20, 90, 'Önceden Harun, kendilerine: "Ey kavmim, andolsun siz bununla sınandınız. Rabbiniz, o çok esirgeyendir. Bana uyun, buyruğuma ita\'at edin!" demişti.'),
(2439, 20, 91, 'Dediler: "Musa bize dönünceye kadar buna tapmaktan vazgeçmeyeceğiz!"'),
(2440, 20, 92, '(Musa) "Ey Harun, oların saptıklarını gördüğün zaman sana ne engel oldu (da önlemedin)? dedi.'),
(2441, 20, 93, 'Neden bana uymadın, buyruğuma karşı mı geldin? (Ve kardeşinin sakalından tutup çekmeğe başladı.)'),
(2442, 20, 94, '(Harun, kardeşini yumuşatabilmek için): "Ey anamın oğlu, dedi, sakalımı, başımı tutma. Ben senin \'İsrail oğulları arasında ayrılık çıkardın, sözümü tutmadın\' diyeceğinden korktum (da onun için idare yoluna gittim)."'),
(2443, 20, 95, '(Musa, Samiri\'ye döndü): "Ey Samiri, ya senin amacın nedir?" dedi.'),
(2444, 20, 96, '(Samiri): "Ben dedi, onların görmediklerini gördüm. Elçinin eserinden bir avuç aldım da attım; nefsim bana böyle (yapmayı) hoş gösterdi."'),
(2445, 20, 97, '(Musa): "Git, dedi. Artık hayat boyunca sen: \'Bana dokunmayın!\' diyeceksin (toplumdan refüze edilip yalnız başına kalacaksın), sana va\'dedilen bir ceza var ki ondan asla şaşırılmayacaksın (mutlaka o cezanı tam zamanında bulacaksın). Şimdi durup taptığın tanrına bak. Biz onu yakacağız, sonra onu ufalayıp denize savuracağız."'),
(2446, 20, 98, 'Tanrınız ancak kendisinden başka tanrı olmayan Allah\'tır. O\'nun bilgisi her şeyi kuşatmıştır.'),
(2447, 20, 99, 'Böylece sana geçmişlerin haberlerinden bir miktar anlatıyoruz. Gerçekten sana katımızdan bir Zikir (geçmiş olaylardan bir anı) verdik.'),
(2448, 20, 100, 'Kim ondan yüz çevirirse o, kıyamet günü (ağır) bir günah yüklenecekdir.'),
(2449, 20, 101, 'Sürekli olarak o yükün altında kalacaklardır. Kıyamet gününde bu, onlar için ne kötü bir yüktür!'),
(2450, 20, 102, 'O gün Sur\'a üflenir ve o gün suçluları, gömgök (kör bir durumda) süreriz.'),
(2451, 20, 103, 'Kendi aralarında gizli gizli, "(dünyada) On günden fazla kalmadınız" derler.'),
(2452, 20, 104, 'Onların dedikleri(kalış süresi)ni biz daha iyi biliriz. En akıllıları ise: "Siz yalnız bir gün kaldınız," der.'),
(2453, 20, 105, 'Sana dağlardan soruyorlar. De ki: "Rabbim onları ufalayıp savuracak!'),
(2454, 20, 106, 'Yerlerini boş, dümdüz bırakacaktır.'),
(2455, 20, 107, 'Orada ne bir eğrilik, ne de bir tümsek görmeyeceksin.'),
(2456, 20, 108, 'O gün hiç pürüzü olmayan çağrıcıya uyarlar; (ondan sapma imkanı yoktur). Rahman\'ın huzurunda sesler kısılır, fısıltıdan başka bir şey işitemezsin.'),
(2457, 20, 109, 'O gün Rahman\'ın izin verip sözünden hoşlandığı kimseden başkasının şefa\'ati fayda vermez.'),
(2458, 20, 110, 'O, onların önlerindekini ve arkalarındakini (geçmişlerini ve geleceklerini) bilir; onlar ise bilgice O\'nu kavrayamazlar.'),
(2459, 20, 111, 'Bütün yüzler, o diri ve yöneticiye boyun eğmiştir. Zulüm yüklenen perişan olmuştur.'),
(2460, 20, 112, 'Kim inanarak iyi olan işlerden yaparsa artık o, ne zulümden, ne de hakkının çiğnenmesinden korkar.'),
(2461, 20, 113, 'Biz sana onu böyle Arapça bir Kur\'an olarak indirdik ve onda tehditleri türlü biçimlere çevirip açıkladık ki korunsunlar. Yahut (Kur\'an,) onlara bir hatırlama yaptırsın.'),
(2462, 20, 114, 'Gerçek hükümdar olan Allah, yücedir. Sana vahyedilmesi henüz tamamlanmadan Kur\'an\'ı acele okumağa kalkma; "Rabbim, ilmimi artır!" de.'),
(2463, 20, 115, 'Andolsun biz, önceden Adem\'e (o ağaçtan yememesini) emretmiştik, unuttu. Biz onda bir azim (ve sebat) bulmadık.'),
(2464, 20, 116, 'Meleklere: "Adem\'e secede edin," demiştik, secde ettiler, yalnız İblis diretti.'),
(2465, 20, 117, 'Dedik ki: "Ey Adem, bu, senin ve eşinin düşmanıdır. Sakın, sizi cennetten çıkarmasın, sonra yorulursun."'),
(2466, 20, 118, 'Şimdi burada acıkmayacaksın, çıplak kalmayacaksını.'),
(2467, 20, 119, 'Ve sen susamayacaksın, kuşluk vakti güneşi(nin ısısı)ndan etkilenmeyeceksin.'),
(2468, 20, 120, 'Nihayet şeytan ona fısıldayıp: "Ey Adem, sana ebedilik ağacını ve yok olmayacak bir hükümranlığı göstereyim mi? dedi.'),
(2469, 20, 121, 'O ağaçtan yediler. Böylece kendilerine kötü yerleri göründü (üreme organları ortaya çıktı). Üstlerini cennet yaprağıyle örtmeğe başladılar. Adem Rabbinin buyruğuna karşı geldi de şaşırdı.'),
(2470, 20, 122, 'Sonra Rabbi onu seçti, tevbesini kabul etti, doğru yola iletti.'),
(2471, 20, 123, 'Dedi ki: "Hepiniz oradan inin, birbirinize düşmansınız. İmdi benden size bir hidayet geldiği zaman kim benim hidayetime uyarsa o, sapmaz ve sıkıntıya düşmez."'),
(2472, 20, 124, 'Ama kim beni anmaktan yüz çevirirse, onun için de dar bir geçim var. Kıyamet günü onu kör olarak (yüce Divana) süreriz.'),
(2473, 20, 125, 'Rabbim der, niçin beni kör sürdün, oysa ben görür idim?'),
(2474, 20, 126, '(Allah) buyurur ki: "Nasıl sana ayetlerimiz geldiği zaman, sen onları unuttuysan, bugün de sen öyle unutulursun!"'),
(2475, 20, 127, 'İşte israf eden ve Rabbinin ayetlerine inanmayanları böyle cezalandırırız. Elbette ahiretin azabı daha çetin ve daha süreklidir.'),
(2476, 20, 128, '(Bugün) meskenlerinde dolaştıkları, kendilerinden önce yaşamış nice nesilleri yok edişimiz onları hala yola getirmedi mi? Elbette bunda akıl sahipleri için ibretler vardır.'),
(2477, 20, 129, 'Eğer Rabbin tarafından söylenmiş bir söz ve belirtilmiş bir süre olmasaydı. (bunların da hemen helak edilmeleri) gerekli olurdu.'),
(2478, 20, 130, 'Onların dediklerine sabret, güneşin doğmasından ve batmasından önce Rabbini överek tesbih et; gece sa\'atlerinden bir kısmında ve gündüzün taraflarında da tesbih et ki memnun olasın!'),
(2479, 20, 131, 'Onlardan bazı zümrelere kendilerini denemek için verdiğimiz dünya hayatının süsüne gözlerini dikme. Rabbinin rızkı daha hayırlı ve daha süreklidir.'),
(2480, 20, 132, 'Ailene namazı emret, kendin de namaz kılmaya dayan. Biz senden rızık istemiyoruz. Seni biz besliyoruz. Sonuç takva(sahipleri)nindir.'),
(2481, 20, 133, 'Dediler ki: "Rabbinden bize bir ayet (mu\'cize) getirmeli değil mi?" Onlara, önceki Kitap\'larda bulunan kanıt gelmedi mi?'),
(2482, 20, 134, 'Şayet onları, ondan önce bir azab ile helak etseydik: "Rabbimiz, bize bir elçi gönderseydin de böyle alçak ve rezil olmadan önce senin ayetlerine uysaydık!" derlerdi.'),
(2483, 20, 135, 'De ki: "Herkes gözetlemektedir. Gözetleyin, düzgün yolun sahipleri kimdir, doğru yolda olan kimdir, bileceksiniz!"');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'İnsanların hesapları yaklaştı, fakat onlar hala gaflet içinde yüz çevirmektedirler.'),
(2485, 21, 2, 'Kendilerine Rablerinden gelen her yeni ikazı mutlaka eğlenerek dinlerler.'),
(2486, 21, 3, 'Kalbleri eğlencededir. O zulmedenler, aralarında şu konuşmayı gizlediler: "Bu (Muhammed) de sizin gibi bir insan değil mi? Şimdi siz, göre göre büyüye mi kapılacaksınız?"'),
(2487, 21, 4, 'Dedi ki: "Rabbim gökte ve yerde konuşulan her sözü bilir, (O\'ndan gizli kalan hiçbir şey yoktur). O, işitendir, bilendir."'),
(2488, 21, 5, 'Hayır, dediler, (bu) karmakarışık hayallerdir; hayır onu uydurmuş; hayır o şa\'irdir. (Eğer gerçekten peygamberse) öncekilerin, (mu\'cizelerle) gönderildikleri gibi o da bize bir mu\'cize getirsin.'),
(2489, 21, 6, 'Bunlardan önce helak ettiğimiz hiçbir kent (halkı) inanmamıştı, şimdi bunlar mı inanacaklar?'),
(2490, 21, 7, 'Biz, senden önce yalnız kendilerine vahyedilen erkeklerden başkasını elçi göndermedik. Eğer bilmiyorsanız Zikir ehline (Kitap sahiplerine) sorun.'),
(2491, 21, 8, 'Biz onları yemek yemeyen ceset(ler) yapmadık. (Onlar), ölümsüz de değillerdi.'),
(2492, 21, 9, 'Sonra onlara verdiğimiz sözü yerine getirdik, onları ve dilediklerimizi kurtardık, aşırı gidenleri helak ettik.'),
(2493, 21, 10, 'Andolsun, size, içinde Zikr\'iniz bulunan bir Kitap indirdik. Aklınızı kullanmıyor musunuz?'),
(2494, 21, 11, '(Halkı) zulmeden nice şehri kırıp geçirdik ve onlardan sonra başka bir topluluk getirdik.'),
(2495, 21, 12, 'Azabımızı hissettikleri zaman onlar, derhal oradan (kaçmak için hayvanlarını) mahmuzluyorlardı.'),
(2496, 21, 13, '(Boşuna) Kaçmayın, (bol bol verilip) içinde şımartıldığınız(ni\'metler)e ve yurtlarınıza dönün, çünkü sorguya çekileceksiniz!'),
(2497, 21, 14, 'Eyvah bize, dediler, gerçekten biz zalimlermişiz!'),
(2498, 21, 15, 'Bu mırıldanmaları sürüp giderken biz onları, biçilmiş (ekin gibi) yaptık, sönüp gittiler.'),
(2499, 21, 16, 'Biz göğü, yeri ve bunlar arasında bulunanları, eğlence için yaratmadık.'),
(2500, 21, 17, 'Eğer bir eğlence edinmek isteseydik, kendi katımızdan edinirdik. Yapacak olsaydık, böyle yapardık.'),
(2501, 21, 18, 'Hayır, biz hakkı batılın üstüne atarız da o onun beynini parçalar, derhal (batılın) canı çıkar. Allah\'a yakıştırdığınız niteliklerden ötürü de vay siz(in haliniz)e!'),
(2502, 21, 19, 'Göklerde ve yerde kim varsa hep O\'nundur. O\'nun yanında bulunanlar, O\'na kulluk etmekten büyüklenmez ve yorulmazlar.'),
(2503, 21, 20, 'Gece gündüz tesbih ederler, hiç ara vermezler.'),
(2504, 21, 21, 'Yoksa (o müşrikler), yerden birtakım tanrılar edindiler de (ölüleri) onlar mı diriltecek?'),
(2505, 21, 22, 'Eğer yerde, gökte Allah\'tan başka tanrılar olsaydı, ikisi de (yer de, gök de) bozulup gitmişti. Arş\'ın sahibi Allah, onların nitelendirmelerinden yüce(münezzeh)dir.'),
(2506, 21, 23, 'O, yaptığından sorulmaz, ama onlar, sorulurlar.'),
(2507, 21, 24, 'Yoksa O\'ndan başka tanrılar mı edindiler? De ki: "(Bu hususta kesin) delilinizi getirin. İşte benimle beraber olanların da öğütü ve benden öncekilerin de öğütü budur." Ama çokları hakkı bilmezler, bundan dolayı onlar, (haktan) yüz çevirirler.'),
(2508, 21, 25, 'Senden önce hiçbir peygamber göndermedik ki ona: "Benden başka tanrı yoktur, bana kulluk edin!" diye vahyetmiş olmayalım.'),
(2509, 21, 26, 'Rahman çocuk edindi. dediler. O, yücedir. Hayır (Rahman\'ın çocukları sanılan melekler, O\'nun) değerli kullar(ı)dır.'),
(2510, 21, 27, 'O\'ndan önce söz söylemezler ve onlar, O\'nun buyruğunu yaparlar.'),
(2511, 21, 28, '(Allah) Onların önlerinde ve arkalarında olanı bilir. (Allah\'ın) razı olduğundan başkasına şefa\'at edemezler ve onlar, O\'nun korkusundan titrerler.'),
(2512, 21, 29, 'Onlardan her kim: "Ben O\'ndan başka bir tanrıyım!" derse onu cehennemle cezalandırırız. Biz zalimleri böyle cezalandırırız.'),
(2513, 21, 30, 'O nankörler görmediler mi ki göklerle yer bitişik idi, biz onları ayırdık ve her canlı şeyi sudan yarattık? Hala inanmıyorlar mı?'),
(2514, 21, 31, 'Yer, onları sarsar diye, onun üstünde yüksek dağlar yarattık. Ve istedikleri yere gidebilmeleri için orada geniş yollar açtık.'),
(2515, 21, 32, 'Göğü, korunmuş bir tavan yaptık; onlarsa hala göğün, (Allah\'ın) ayetlerinden yüz çevirmektedirler.'),
(2516, 21, 33, 'Geceyi, gündüzü, güneşi, ayı yaratan O\'dur. (Bunların) her biri bir yörüngede yüzmektedir.'),
(2517, 21, 34, 'Senden önce hiçbir insana ebedi yaşama vermedik. Şimdi sen ölürsen onlar ebedi mi kalacaklar?'),
(2518, 21, 35, 'Her nefis, ölümü tadacaktır. Biz sizi sınamak için şerre de hayra da müptela kılıyoruz. Ve (sonunda) bize döndürüleceksiniz.'),
(2519, 21, 36, 'Kafirler seni gördükleri zaman: "Sizin tanrılarınızı diline dolayan bu mu?" diye seninle alay ederler. Oysa kendileri Rahman\'ın Zikri(uyarısı)nı kabul etmiyorlar.'),
(2520, 21, 37, '(İnsanın tabiatinde acelecilik vardır. Öye acelecidir ki, sanki) İnsan aceleden yaratılmıştır. (Durun,) Size ayetlerimi göstereceğim, benden acele istemeyin.'),
(2521, 21, 38, 'Doğru söyleyenler iseniz bu (bizi) tehdid(ettiğiniz azab) ne zaman? diyorlar.'),
(2522, 21, 39, 'İnkar edenler, ne yüzlerinden, ne de sırtlarından ateşi savamayacakları ve yardım da olunmayacakları zamanı bir bilselerdi (onu böyle acele istemezlerdi)!'),
(2523, 21, 40, 'Doğrusu o, onlara ansızın gelecek, onları şaşırtacak, ne onu reddedebilecekler, ne de kendilerine süre verilecek.'),
(2524, 21, 41, 'Andolsun, senden önceki peygamberlerle de alay edildi, ama onlarla alay edenleri, o alay ettikleri şey kuşatıverdi.'),
(2525, 21, 42, 'De ki: "Gece gündüz, sizi Rahman\'dan kim koruyacak?" Hayır, onlar, Rablerinin Zikr\'inden yüz çeviriyorlar.'),
(2526, 21, 43, 'Yoksa onları, bize karşı koruyacak tanrıları mı var? Onlar, ne kendilerine yardım edebilirler, ne de bizim tarafımızdan onlara sahip çıkılır.'),
(2527, 21, 44, 'Biz onları ve atalarını yaşattık, nihayet kendilerine ömür uzun geldi, (ebedi yaşayacaklarını sandılar). Bizim, yere gelip, onu uçlarından eksilttiğimizi görmüyorlar mı? Üstün gelen onlar mı (yoksa biz miyiz)?'),
(2528, 21, 45, 'De ki: "Ben ancak sizi vahiyle uyarıyorum. Ama sağır(lar) uyarıldıkları zaman çağırıyı işitmez(ler)."'),
(2529, 21, 46, 'Andolsun, onlara Rabbinin azabından bir esinti dokunsa, "Eyvah bize, biz gerçekten zalimlermişiz," derler.'),
(2530, 21, 47, 'Kıyamet günü için adalet terazileri kurarız. Hiç kimseye bir haksızlık edilmez (insanın yaptığı iş), bir hardal danesi ağırlığınca da olsa onu getiririz. Hesab gören olarak biz yeteriz.'),
(2531, 21, 48, 'Andolsun biz, Musa\'ya ve Harun\'a hak ve batılı ayırdeden ve korunanlar için bir ışık ve öğüt olan Kitabı verdik.'),
(2532, 21, 49, 'Korunanlar görmeden Rablerinden korkarlar ve (Duruşma) sa\'at(in)den de titrerler.'),
(2533, 21, 50, 'Bu (Kur\'an) da ona (yani Muhammed\'e) indirdiğimiz mübarek (çok faydalı) bir öğüttür. Şimdi siz onu inkar mı ediyorsunuz? (Ne kadar gafilsiniz siz)!'),
(2534, 21, 51, 'Andolsun biz, daha önceden İbrahim\'e de doğru yolu bulma yeteneğini vermiştik. Zaten biz onu(n olgun insan olduğunu) biliyorduk.'),
(2535, 21, 52, 'Babasına ve kavmine demişti ki: "Sizin şu karşısında durup taptığınız heykeller nedir?"'),
(2536, 21, 53, 'Babalarımızı onlara tapar bulduk (da onun için biz de onlara tapıyoruz.) dediler.'),
(2537, 21, 54, 'Doğrusu siz de, babalarınız da açık bir sapıklık içine düşmüşsünüz! dedi.'),
(2538, 21, 55, 'Dediler ki: "Sen bize gerçeği mi getirdin, yoksa şaka yapanlardan mısın?"'),
(2539, 21, 56, 'Hayır, dedi, Rabbiniz, göklerin ve yerin Rabbidir ki, onları yaratmıştır. Ben de buna şahidlik edenlerdenim.'),
(2540, 21, 57, 'Allah\'a and olsun ki siz dönüp gittikten sonra putlarınıza bir tuzak kuracağım!'),
(2541, 21, 58, 'Nihayet (İbrahim) onları parça parça etti, yalnız onların büyüğünü bıraktı; belki ona müracaat ederler diye(!)'),
(2542, 21, 59, '(Döndükleri zaman): "Bunu tanrılarımıza kim yaptı? Muhakkak o zalimlerden biridir." dediler.'),
(2543, 21, 60, 'Onları diline dolayan bir genç işittik, kendisine İbrahim deniliyormuş, dediler.'),
(2544, 21, 61, 'Onu insanların gözü önüne getirin de (nasıl cezalandırılacağına) tanık olsunlar dediler.'),
(2545, 21, 62, '(İbrahim\'i getirdiler), dediler ki: "İbrahim, tanrılarımıza sen mi bunu yaptın?"'),
(2546, 21, 63, 'Hayır dedi, (büyük putu göstererek) işte şu büyükleri yapmış; onlara sorun, eğer konuşurlarsa (!)'),
(2547, 21, 64, 'Kendi vicdanlarına başvurup (içlerinden): "Hakikaten sizler haksızsınız!" dediler.'),
(2548, 21, 65, 'Sonra yine eski kafalarına döndürüldüler: "Sen de bilirsin ki bunlar konuşmazlar," dediler.'),
(2549, 21, 66, 'Peki, dedi, siz Allah\'ı bırakıp da size hiçbir fayda ve zarar vermeyen şeylere mi tapıyorsunuz?'),
(2550, 21, 67, 'Yuh size ve Allah\'tan başka taptıklarınıza. Aklınızı kullanmıyor musunuz siz?'),
(2551, 21, 68, 'Dediler: "Onu yakın, tanrılarınıza yardım edin, eğer bir iş yapacaksanız."'),
(2552, 21, 69, 'Biz de: "Ey ateş, İbrahim\'e serin ve esenlik ol!" dedik.'),
(2553, 21, 70, 'Ona bir tuzak kurmak istediler. Biz de, asıl kendilerini hüsrana uğrattık.'),
(2554, 21, 71, 'Onu ve Lut\'u kurtarıp, alemlere bereketli kıldığımız bir yere getirdik.'),
(2555, 21, 72, 'Ona İshak\'ı hediye ettik, üstelik (torunu) Ya\'kub\'u da (verdik). Hepsini de iyi insanlar yaptık.'),
(2556, 21, 73, 'Onları, emrimizle doğru yolu gösteren önderler yaptık ve onlara hayırlı işler yapmayı, namaz kılmayı ve zekat vermeyi vahyettik. Onlar bize kulluk eden(insan)lardı.'),
(2557, 21, 74, 'Lut\'a da hüküm (hükümranlık, peygamberlik, hikmet) ve ilim verdik ve onu çirkin işler yapan bir kentten kurtardık. Gerçekten onlar yoldan çıkan kötü bir kavim idiler.'),
(2558, 21, 75, 'Ve onu rahmetimizin içine soktuk. Çünkü o, Salihlerden idi.'),
(2559, 21, 76, 'Nuh\'u da (an), o da bunlardan önce bize yalvarmıştı. Biz de onun du\'asını kabul edip kendisini ve ailesini büyük sıkıntıdan kurtarmıştık.'),
(2560, 21, 77, 'Ve ayetlerimizi yalanlayan kavimden onun öcünü almıştık. Onlar, kötü bir kavim olmuşlardı, biz de onların hepsini boğmuştuk.'),
(2561, 21, 78, 'Davud ile Süleyman\'ı da (an); hani onlar, toplumun davarının yayıldığı bir ekin hakkında hükmediyorlardı, biz de onların hükümlerine tanık idik.'),
(2562, 21, 79, 'O hükmü Süleyman\'a bellettik. Onların hepsine de hükümdarlık ve bilgi verdik. Davud\'a dağları ve kuşları boyun eğdirdik, onunla beraber tesbih ediyorlardı. Biz (bunları) yaparız.'),
(2563, 21, 80, 'Ona, sizi, savaşın şiddetinden korumak için zırh yapmayı öğretmiştik. Ama siz şükrediyor musunuz ki?'),
(2564, 21, 81, 'Süleyman\'a da fırtınayı (boyun eğdirmiştik). Onun emriyle, içinde bereketler yarattığımız yere akıp giderdi. Biz her şeyi biliriz.'),
(2565, 21, 82, 'Kendisi için denize dalan ve bundan başka işler yapan bazı şeytanları da emrine vermiştik. Biz onları onun emrinde tutuyorduk.'),
(2566, 21, 83, 'Eyyub\'u da an. O, Rabbine: "Bu dert bana dokundu, sen merhametlilerin en merhametlisisin!" diye du\'a etmişti.'),
(2567, 21, 84, 'Biz de onun du\'asını kabul etmiş, kendisine bulaşan derdi kaldırmıştık; ona tarafımızdan bir rahmet ve ibadet edenler için bir öğüt olarak ailesini ve onlarla beraber bir katını daha vermiştik.'),
(2568, 21, 85, 'İsma\'il\'i, İdris\'i, Zu\'l-Kifl\'i de an; hepsi de sabredenlerdendi.'),
(2569, 21, 86, 'Onları rahmetimize soktuk, çünkü onlar Salihlerdendi.'),
(2570, 21, 87, 'Zünnun\'u (balık karnına girmiş olan Yunus ibn Matta\'yı) da an; zira (o, kavmine) kızarak gitmişti, bizim kendisine güç yetiremeyeceğimizi, (kavminin arasından çıkmakla kendisini kurtaracağını) sanmıştı. Nihayet karanlıklar içinde (kalıp): "Senden başka tanrı yoktur. Senin şanın yücedir, ben zalimlerden oldum!" diye yalvardı.'),
(2571, 21, 88, 'Biz de onun du\'asını kabul ettik ve onu tasadan kurtardık. İşte biz, inananları böyle kurtarırız.'),
(2572, 21, 89, 'Zekeriyya\'yı da (an). Rabbine: "Rabbim, beni tek bırakma! Sen, varislerin en iyisisin (her şeyim sana kalacaktır)" diye du\'a etmişti.'),
(2573, 21, 90, 'Onun du\'asını da kabul buyurduk ve ona Yahya\'yı armağan ettik. Eşini de kendisi için ıslah ettik (çocuk doğurmağa elverişli bir hale getirdik). Gerçekten onlar hayır işlere koşarlar, umarak ve korkarak bize du\'a ederlerdi ve bize derin saygı gösterirlerdi.'),
(2574, 21, 91, 'O ırzını korumuş olan(Meryem)i de an; ona ruhumuzdan bir çocuk üflemiş, kendisini ve oğlunu alemlere bir ibret yapmıştık.'),
(2575, 21, 92, 'İşte bu sizin ümmetiniz (olan tevhid ve İslam milleti), bir tek ümmettir. Rabbiniz de benim. Yalnız bana kulluk edin.'),
(2576, 21, 93, 'İşlerini aralarında parçaladılar (Tanrıdan gelen dini parça parça ettiler, ayrılığa düştüler); hepsi (sonunda) bize döneceklerdir.'),
(2577, 21, 94, 'İmdi kim inanmış olarak iyi işlerden yaparsa onun çalışmasına nankörlük edilmez, biz (onun çalışmasını) yazanlarız.'),
(2578, 21, 95, 'Helak ettiğimiz bir ülkeye artık (yaşamak) haramdır: Onlar bir daha geri dönemezler.'),
(2579, 21, 96, 'Nihayet Ye\'cuc ve Me\'cuc\'un önü açıldığı ve onlar her tepeden akın etmeye başladıkları zaman,'),
(2580, 21, 97, 'Gerçek va\'d (yani kıyamet) yaklaşmış olur. İnkar edenlerin gözleri birden donup kalır. "Vah bize, biz bundan gaflet içinde idik (bunun doğru olacağını hiç düşünmüyorduk). Meğer biz zulmediyormuşuz!" (diye mırıldandılar).'),
(2581, 21, 98, 'Siz ve Allah\'tan başka taptıklarınız cehennemin odunusunuz. Siz, oraya gireceksiniz.'),
(2582, 21, 99, 'Eğer onlar tanrı olsalardı oraya girmezlerdi. Oysa hepsi orada sürekli kalacaklardır.'),
(2583, 21, 100, 'Onlar için bir inleme ve soluma vardır! Ve onlar orada (azabın dehşeti içinde hiçbir şey) işitmezler.'),
(2584, 21, 101, 'Ama bizden kendilerine (ezelde) güzellik geçmiş (mutluluk takdir edilmiş) olanlar, işte onlar, ondan (cehennemden) uzaklaştırılmışlardır.'),
(2585, 21, 102, 'Onun uğultusunu duymazlar. Ve canlarının çektiği (ni\'metler) içinde ebedi kalırlar.'),
(2586, 21, 103, 'O en büyük korku, onları asla tasalandırmaz. Melekler onları şöyle karşılar: "İşte bu, size va\'dedilen gününüzdür!"'),
(2587, 21, 104, 'O gün göğü yazı tomarlarını dürer gibi toplarız. İlk yaratmaya başladığımız gibi onu iade ederiz. Üzerimize sözdür; biz bunu mutlaka yapacağız.'),
(2588, 21, 105, 'Andolsun Tevrat\'tan sonra Zebur\'da da: "Arza mutlaka iyi kullarım varis olacak (bu yer onların eline geçecek)" diye yazmıştık.'),
(2589, 21, 106, 'Şüphesiz bunda kulluk eden kimseler için yeterli bir öğüt vardır.'),
(2590, 21, 107, '(Ey Muhammed) Biz seni ancak alemlere rahmet için gönderdik.'),
(2591, 21, 108, 'De ki: "Bana, Tanrınız, ancak bir tek Tanrıdır; diye vahyolunur. O\'na teslim ol(up putperestliği bırak)cak mısınız?'),
(2592, 21, 109, 'Eğer yüz çevirirlerse de ki: "Ben sizin hepinize eşit biçimde açıkladım. Artık tehdidedildiğiniz şeyin yakın mı, yoksa uzak mı olduğunu bilmem."'),
(2593, 21, 110, 'Şüphesiz O, sözün açığını da bilir, gizlediklerinzi de bilir.'),
(2594, 21, 111, 'Bilmem belki de o (azabın ertelenmesi) sizi denemek ve bir süreye kadar yaşatmak içindir'),
(2595, 21, 112, '(Allah\'ın Resulü) Dedi: "Rabbim (aramızda) hak ile hükmet, Rabbimiz çok merhamet edendir. Sizin nitelendirdiğinize (iftiralarınıza) karşı O\'nun yardımına sığınılır (O, bizi her tehlikeden korur)!"');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Ey insanlar, Rabbinizden korkun, çünkü (Duruşma) sa\'ati(ni)n depremi, cidden korkunç bir şeydir.'),
(2597, 22, 2, 'Onu gördüğünüz gün, her emziren, emzirdiğinden geçer; her gebe yükünü bırakır; insanları sarhoş görürsün, oysa sarhoş değillerdir ama Allah\'ın azabı şiddetlidir (bu dehşetli azab, onların akıllarını başlarından almıştır.)'),
(2598, 22, 3, 'İnsanlardan kimi, Allah hakkında bilmeden tartışır ve her kaba (şarlatan) şeytana uyar.'),
(2599, 22, 4, 'O(Şeyta)nın hakkında: "Kim bunu takibederse muhakkak bu, onu saşırtır ve onu alevli ateş azabına götürür" diye yazılmıştır.'),
(2600, 22, 5, 'Ey insanlar eğer öldükten sonra dirilmekten kuşkuda iseniz (bilin ki) biz sizi (önce) topraktan, sonra nutfe(sperm)den, sonra alaka(embriyo)dan, sonra biçimlenen ve biçimlenmeyen bir çiğnem et parçasından yarattık ki, size (kudretimizi) açıkça gösterelim. Dilediğimizi belirtilmiş bir süreye kadar rahimlerde tutarız, sonra sizi bir bebek olarak çıkarırız. Sonra güç(ve kabiliyetler)inize ermeniz için (sizi büyütürüz). İçinizden kimi (henüz çocukken) öldürülür, kimi de ömrün en kötü çağına(ihtiyarlığa) itilir ki, bilirken bir şey bilmez hale gelsin (çocukluğundaki gibi bedence ve akılca güçsüz bir duruma düşsün). Yeri de kurumuş, ölmüş görürsün. Fakat biz onun üzerine suyu indirdiğimiz zaman titreşir, kabarır ve her güzel çifti bitirir.'),
(2601, 22, 6, 'Bu böyledir. Çünkü Allah, tek gerçektir. (Her şey O\'nunla varlık kazanır) ve O, ölüleri diriltir ve O, her şeyi yapabilir.'),
(2602, 22, 7, 'Ve (çünkü) o (duruşma) sa\'at(i) mutlaka gelecektir, onda şüphe yoktur. Ve Allah, kabirlerde olanları diriltecektir.'),
(2603, 22, 8, 'İnsanlardan kimi bilmeden, ne bir yol göstereni, ne de aydınlatıcı bir Kitabı olmadan, Allah hakkında tartışır.'),
(2604, 22, 9, 'Allah\'ın yolundan şaşırtmak için boynunu öteye döndürerek (kabara kabara tartışmasını sürdürür), dünyada onun için bir kepazelik vardır. Kıyamet günü de ona yangın azabını taddıracağız:'),
(2605, 22, 10, '(Ey insan), "İşte bu, senin ellerinin yapıp öne sürdüğü işler yüzündendir. Allah kullara zulmedici değildir!"'),
(2606, 22, 11, 'İnsanlardan kimi de Allah\'a bir kenardan, ibadet eder. Eğer kendisine bir hayır gelirse onunla huzura kavuşur (sevinir) ve eğer başına bir kötülük gelirse yüz üstü döner (dini kötüleyerek ondan vazgeçer). O, dünyayı da, ahireti de kaybetmiştir. İşte apaçık ziyan budur.'),
(2607, 22, 12, 'Allah\'tan ayrı olarak kendisine ne zarar, ne de yarar veremeyen şeylere yalvarır. İşte (doğru yoldan) uzak(lara) sapma budur!'),
(2608, 22, 13, 'Zararı, faydasından daha yakın olana yalvarır. (O), Ne kötü bir yardımcı ve ne kötü bir arkadaştır!'),
(2609, 22, 14, 'Allah, inanan ve iyi işler yapanları, altlarından ırmaklar akan cennetlere sokacaktır. Şüphesiz Allah istediğini yapar.'),
(2610, 22, 15, 'Kim Allah\'ın, dünyada ve ahirette kendisine yardım etmeyeceğini sanıyorsa öfkesini gidermek için göğe bir sebep(ip)le uzansın, sonra (ayaklarını yerden) kessin de baksın, bu çaresi, öfkelendiği şeyi giderebilecek mi?'),
(2611, 22, 16, 'Ve işte biz Kur\'an\'ı böyle açık açık ayetler olarak indirdik. Şüphesiz Allah, dilediğini doğru yola iletir.'),
(2612, 22, 17, 'İnananlar, yahudiler, sabiiler, hırıstiyanlar, mecusiler ve (Allah\'a) ortak koşanlar... Allah, kıyamet günü bunlar arasında hüküm verecek(haklıyı haksızı ortaya çıkaracak)tır. Şüphesiz Allah, her şeye şahidtir!'),
(2613, 22, 18, 'Görmedin mi (baksana), göklerde, yerde bulunan kimseler, güneş, ay, yıldızlar, dağlar, ağaçlar, hayvanlar ve insanlardan birçoğu hep Allah\'a secde ediyorlar! Ama birçoğuna da azab hak olmuştur. Allah kimi aşağılatırsa artık ona değer veren olmaz. Allah, dilediğini yapar.'),
(2614, 22, 19, 'İşte şunlar, Rableri hakkında çekişen iki hasım taraf: Nankörler için ateşten giysi biçildi, başlarının üstünden de kaynar su dökülüyor!'),
(2615, 22, 20, 'Onunla karınlarının içindekiler ve derileri eritiliyor.'),
(2616, 22, 21, '(Ayrıca) onlar için demir kamçılar da var.'),
(2617, 22, 22, 'Oradan, (o) gamdan her çıkmak istediklerinde (demir kamçılarla vurularak) oraya geri çevrilirler ve: "Yangın azabını tadın!" (denilir).'),
(2618, 22, 23, 'Allah, inanan ve iyi işler yapanları da altlarından ırmaklar akan cennetlere sokar. Orada altun bilezikler ve inci(ler) takınırlar. Orada giysileri de ipektir.'),
(2619, 22, 24, 'Sözün güzeline ve çok övülen(Allah)ın yoluna iletilmişlerdir.'),
(2620, 22, 25, 'Nankörlük edenler, Allah\'ın yolundan ve gerek yerli, gerek dışarıdan gelen bütün insanlar için ibadet yeri yaptığımız Mescid-i Haram\'dan (insanları) geri çevirenler (bilsinler ki), kim orada (böyle) zulüm ile haktan sapmak isterse ona acı bir azab taddırırız.'),
(2621, 22, 26, 'Bir zamanlar İbrahim\'i Beyt(Ka\'be\'n)in yerine kondurmuş(ve ona şöyle emretmiş)tik: "Bana hiçbir şeyi ortak koşma ve tavaf edenler, ayakta duranlar, rüku\' ve secde edenler için Evimi temizle."'),
(2622, 22, 27, 'İnsanlar içinde haccı ilan et; yaya olarak veya uzak yollardan gelen yorgun develer üzerinde sana gelsinler.'),
(2623, 22, 28, 'Ki kendileri için birtakım faydalara tanık olsunlar ve (Allah\'ın) kendilerine rızık olarak verdiği hayvanlar üzerine belli günlerde (onları kurban ederken) Allah\'ın adını ansınlar. Onlardan yeyin, sıkıntı içinde bulunan fakire de yedirin.'),
(2624, 22, 29, 'Sonra (bıyıklarını, tırnaklarını kesmek, sair temizlik gereklerini yapmak suretiyle) Kirlerini gidersinler, adaklarını yerine getirsinler ve Eski Ev(Ka\'be\'y)i tavaf etsinler.'),
(2625, 22, 30, 'İşte öyle. Kim Allah\'ın yasaklarına saygı gösterirse, o (hareketi), Rabbinin yanında kendisi için iyidir. Size (ayetlerle) oku(nup açıkla)nanlar dışındaki hayvanlar sizin için helal kılınmıştır. Artık o pis putlardan ve yalan sözden kaçının.'),
(2626, 22, 31, 'Allah\'a ortak koşmadan, halis olarak O\'nu birleyenler olun. Kim Allah\'a ortak koşarsa, o, sanki gökten düşmüş de kendisini kuş kapıyor veya rüzgar onu, uzak bir yere sürüklüyor gibidir.'),
(2627, 22, 32, 'İşte böyle. Kim Allah\'ın nişanlarına (hac ibadetlerine ve kurbanlara) saygı gösterirse, bu, kalblerin takvasındandır (kalblerinde Allah korkusu olanlar, O\'nun dininin işaretlerine saygı gösterirler).'),
(2628, 22, 33, 'O(hayva)nlarda belli bir süreye kadar sizin için menfaatler vardır. Sonra onların varacakları yer, Eski Ev(Ka\'be)dir. (Orada kurban edilirler).'),
(2629, 22, 34, 'Biz, her ümmet için bir kurban ibadeti koyduk ki Allah\'ın kendilerine rızık olarak verdiği hayvanların üzerine O\'nun adını ansınlar. Tanrınız bir tek Tanrıdır, yalnız O\'na teslim olun. (Ey Muhammed) o alçak gönüllü, saygılı, samimi insanları müjdele;'),
(2630, 22, 35, 'Onlar ki Allah anıldığı zaman kalbleri titrer. Başlarına gelene sabrederler, namazı kılarlar ve kendilerine verdiğimiz rızıktan (Allah yoluna) harcarlar.'),
(2631, 22, 36, 'Biz o kurbanlık develeri de size Allah\'ın (dininin) işaretlerinden yaptık. Onlarda sizin için hayır vardır. Onlar ön ayaklarını sıra halinde yere basmış durumda iken üzerlerine Allah\'ın adını anın (da boğazlayın) yanları yere düş(üp canları çık)ınca da onlardan yeyin, kanaat eden(fakir)e de; isteyen(fakir)e de yedirin. Allah o(kocaman hayva)nları, size boyun eğdirdi ki şükredesiniz.'),
(2632, 22, 37, 'Onların etleri ve kanları Allah\'a ulaşmaz. Fakat sizin takvanız O\'na ulaşır. Allah onları size böyle boyun eğdirdi ki, sizi doğru yola ilettiği için O\'nun büyüklüğünü anasınız. (Ey Muhammed), güzel davrananları müjdele.'),
(2633, 22, 38, 'Allah inananları savunur. Allah hiçbir hain, nankörü sevmez.'),
(2634, 22, 39, 'Kendileriyle savaşılan(mü\'min)lere (karşı koyma) izn(i) verildi. Çünkü onlara zulmedilmiştir ve şüphesiz Allah, onlara yardım etmeğe kadirdir.'),
(2635, 22, 40, 'Onlar, sırf "Rabbimiz Allah\'tır" dedikleri için haksız yere yurtlarından çıkarıldılar. Eğer Allah\'ın bazı insanları diğer bazılarıyle savunması olmasaydı, içlerinde Allah\'ın ismi çok anılan manastırlar, kiliseler, havralar ve mescidler yıkılırdı. Allah, kendi(dini)ne yardım edene elbette yardım eder. Kuşkusuz Allah, kuvvetlidir, galibdir.'),
(2636, 22, 41, 'O(Allah\'ın dinine yardım ede)nleri yer yüzünde iktidara getirdiğimiz takdirde (zorbaların yoluna sapmazlar, bil\'akis) namazı kılarlar, zekatı verirler, iyiliği emrederler, kötülükten vazgeçirmeğe çalışırlar. Bütün işlerin sonu Allah\'a aittir (her şey sonunda O\'na varacaktır).'),
(2637, 22, 42, '(Ey Muhammed), eğer (bunlar) seni yalanlıyorlarsa (bil ki) bunlardan önce Nuh, \'Ad ve Semud kavmi de yalanlamıştı.'),
(2638, 22, 43, 'İbrahim kavmi ve Lut kavmi de (yalanlamıştı).'),
(2639, 22, 44, 'Medyen halkı da (yalanlamıştı); Musa da yalanlanmıştı. Ben de kafirlere bir süre vermiş, sonra onları yakalamıştım. (Bak), benim (onları) inkarım (görülmemiş biçimde cezalandırmam) nasıl oldu!'),
(2640, 22, 45, '(Halkı) zulmederken helak ettiğimiz nice kent vardırki duvarları (yıkılan) tavanlarının üstüne çökmüştür. Nice kullanılmaz olmuş kuyu ve nice (ıssız kalmış) sağlam köşk vardır!'),
(2641, 22, 46, 'Hiç yer yüzünde gezmediler mi ki (kendilerinden önce mahvolanların yerlerini görsünler de) düşünecekleri kalbleri, işitecekleri kulakları olsun (akıllları başlarına gelsin, hak sözünü işitsinler). Zira gözler kör olmaz (çünkü gözlerin körlüğü, geçici bir görme yetersizliğidir); fakat (asıl) göğüslerdeki kalbler kör olur.'),
(2642, 22, 47, 'Senden azabı çabuk istiyorlar. Allah sözünden caymaz. (Ama herşeyin bir zamanı vardır. O, acele etmez. Zira) Rabbinin yanında bir gün, sizin saydıklarınızdan bin yıl gibidir.'),
(2643, 22, 48, 'Nice kent var ki zulmederken ona biraz süre vermişim, sonra onu yakalamışımdır. Sonunda dönüş ancak banadır.'),
(2644, 22, 49, 'De ki: "Ey insanlar, ben sizin için ancak apaçık bir uyarıcıyım."'),
(2645, 22, 50, 'İnanıp iyi işler yapanlar için mağfiret ve bol rızık vardır.'),
(2646, 22, 51, 'Ayetlerimizi etkisiz bırakmak için çalışanlara gelince, onlar da cehennemin adamlarıdır.'),
(2647, 22, 52, 'Senden önce hiçbir resul ve nebi göndermemiştik ki o, temenni ettiği zaman, şeytan onun temennisine (bir düşünce) atmış olmasın. Fakat Allah, şeytanın attığını siler, sonra kendi ayetlerini sağlamlaştırır. Allah, \'alim(bilen)dir, hakim (sağlamlaştıran)dır.'),
(2648, 22, 53, '(Allah, böyle yapar ki) Şeytanın attığını, kalblerinde hastalık olanlar ve kalbleri katılaşanlar için bir imtihan yapsın; zalimler uzak bir ayrılık içindedirler.'),
(2649, 22, 54, 'Ve kendilerine ilim verilmiş olanlar da o(Kur\'a)nın, Rabbinden (gelen) gerçek olduğunu bilsinler de ona inansınlar; böylece kalbleri ona saygı duysun. Şüphesiz Allah, inananları mutlaka doğru yola iletir.'),
(2650, 22, 55, 'İnkar edenler ise ansızın o sa\'at (kıyamet veya ölüm) kendilerine gelinceye yahut o kısır (hayırsız) günün azabı kendilerine gelinceye kadar o(Kur\'a)ndan yana, kuşku içinde olacaklardır.'),
(2651, 22, 56, 'O gün mülk Allah\'ındır. (O) onların aralarında hükmeder. İnananlar ve iyi işler yapanlar ni\'met cennetlerindedirler.'),
(2652, 22, 57, 'İnkar edip ayetlerimizi yalanlayanlara da alçaltan bir azab vardır.'),
(2653, 22, 58, 'Allah yolunda göç edip sonra öldürülen veya ölenlere gelince, Allah onları en güzel bir rızıkla besleyecektir. Doğrusu Allah, rızık verenlerin en iyisidir.'),
(2654, 22, 59, 'Onları razı olacakları bir yere sokacaktır. Doğrusu Allah, bilendir, halimdir.'),
(2655, 22, 60, 'İşte böyle. Kim kendisine yapılan cezanın dengiyle ceza verir de sonra kendisine tekrar saldırılırsa elbette, Allah ona yardım eder. Şüphesiz Allah, affeden, bağışlayındır.'),
(2656, 22, 61, 'İşte böyle. (Allah), geceyi gündüzün içine sokar, gündüzü gecenin içine sokar. Doğrusu Allah, işiten ve görendir.'),
(2657, 22, 62, 'İşte böyle. Çünkü Allah, Hak\'tır, O\'ndan başka yalvardıkları ise batıldır (aslı olmayan yalan şeylerdir). İşte çok yüce, çok büyük olan, Allah\'tır.'),
(2658, 22, 63, 'Görmedin mi Allah gökten bir su indirdi de yer yeşeriyor. Doğrusu Allah latiftir (bilgisi veya lutfu en ince ve nazik şeylere kadar varır), habirdir (her türlü tedbiri bilir, her şeyi haber alır).'),
(2659, 22, 64, 'Göklerde ve yerde ne varsa hep O\'nundur. Allah; işte zengin O\'dur, övülmeğe layık O\'dur.'),
(2660, 22, 65, 'Görmedin mi Allah, yerdekileri ve emriyle, (koyduğu kanunla) denizde akıp giden gemileri sizin buyruğunuza verdi. Yerin üstüne düşmesin diye göğü tutuyor. (Gök) ancak O\'nun izniyle düşer. Çünkü Allah, insanlara çok şefkatli, çok merhametlidir.'),
(2661, 22, 66, 'O\'dur ki sizi diriltti, sonra sizi öldürür, sonra yine sizi diriltir. Gerçekten insan çok nankördür.'),
(2662, 22, 67, 'Biz her ümmete, uydukları bir mensek (ibadet yöntemi) yaptık. Bu işte seninle asla çekişmesinler. Sen Rabbine çağır, kuşkusuz sen doğru, bir yol üzerindesin.'),
(2663, 22, 68, 'Eğer seninle mücadele ederlerse: "Allah yaptıklarınızı daha iyi bilir" de.'),
(2664, 22, 69, 'Allah kıyamet günü, ayrılığa düştüğünüz hususlarda aranızda hükmedecek(haklıyı, haksızı ayıracak)tır.'),
(2665, 22, 70, 'Bilmez misin ki Allah gökte ve yerde ne varsa hepsini bilir. Bunların hepsi, bir Kitaptadır (katında yazılıdır). Bu, Allah\'a kolaydır.'),
(2666, 22, 71, 'Allah\'tan ayrı olarak öyle şeylere tapıyorlar ki (Allah), onlara hiçbir kudret indirmemiştir. Kendilerinin de onların tanrı olabileceği hakkında bir bilgileri yoktur. O zalimlerin yardımcısı yoktur.'),
(2667, 22, 72, 'Kendilerine apaçık ayetlerimiz okunduğu zaman kafirlerin yüzlerinde hoşnutsuzluk belirdiğini anlarsın. Neredeyse kendilerine ayetlerimizi okuyanların üzerine saldıracaklar. De ki: "Size bundan (bu öfkeli durumunuzdan) daha kötü bir şey haber vereyim mi? Varacağınız ateş! Allah onu kafirlere va\'detmiştir. Ne kötü sonuçtur (o)!'),
(2668, 22, 73, 'Ey insanlar, size bir temsil verildi, onu dinleyin: O Allah\'tan başka yalvardıklarınız (var ya), onların hepsi bir araya toplansalar, bir sinek dahi yaratamazlar. Sinek onlardan bir şey kapsa, bunu ondan kurtaramazlar. İsteyen de aciz, istenen de.'),
(2669, 22, 74, 'Allah\'ı layikıyle takdir edemediler (O\'nu gereği gibi bilemediler). Allah kuvvetlidir, üstündür.'),
(2670, 22, 75, 'Allah meleklerden de, insanlardan da elçiler seçer. Allah, işitendir, görendir.'),
(2671, 22, 76, 'Onların önlerinde ve arkalarında olan(bütün olayları, yaptıkları bütün işler)i bilir. Bütün işler Allah\'a döndürülür.'),
(2672, 22, 77, 'Ey inananlar, rüku\' edin, secde edin, Rabbinize ibadet edin, hayır işleyin ki umduğunuza eresiniz.'),
(2673, 22, 78, 'Allah uğrunda, O\'na yaraşır biçimde cihad edin. O, sizi seçti ve dinde size bir güçlük yüklemedi; babanız İbrahim\'in dini(ne uyun). O (Allah) bu (Kur\'a)ndan önce(ki Kitaplarda) da, bu(Kur\'a)nda da size "müslümanlar" adını verdi ki, Elçi size şahid olsun, siz de insanlara şahid olasınız. Haydi namazı kılın, zekatı verin ve Allah\'a sarılın; sahibiniz O\'dur. Ne güzel sahip ve ne güzel yardımcıdır (O)!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Felaha ulaştı o mü\'minler.'),
(2675, 23, 2, 'Ki onlar, namazlarında saygılıdırlar.'),
(2676, 23, 3, 'Onlar boş şeylerden yüz çevirirler.'),
(2677, 23, 4, 'Onlar zekatı verirler.'),
(2678, 23, 5, 'Ve onlar ırzlarını korurlar.'),
(2679, 23, 6, 'Ancak eşleri, yahut ellerinin sahipolduğu (cariyeler) hariç. (Bunlarla ilişkilerinden dolayı da) onlar kınanmazlar.'),
(2680, 23, 7, 'Ama bunun ötesine gitmek isteyen olursa, işte onlar haddi aşanlardır.'),
(2681, 23, 8, 'Ve o(mü\'min)ler emanetlerine ve ahidlerine özen gösterirler.'),
(2682, 23, 9, 'Onlar namazlarını (vakitlerinde kılarak) korurlar.'),
(2683, 23, 10, 'İşte varis olacaklar onlardır.'),
(2684, 23, 11, 'Onlar (en yüksek cennet olan) Firdevs\'e varis olacaklar, orada ebedi kalacaklardır.'),
(2685, 23, 12, 'Andolsun biz insanı çamurdan bir süzmeden yarattık.'),
(2686, 23, 13, 'Sonra onu bir nutfe (sperm) olarak sağlam bir karar yerine koyduk.'),
(2687, 23, 14, 'Sonra nutfeyi alaka(embriyo)ya çevirdik, alaka(embriyo)yı bir çiğnemlik ete çevirdik, bir çiğnemlik eti kemiklere çevirdik, kemiklere et giydirdik; sonra onu bambaşka bir yaratık yaptık. Yaratanların en güzeli Allah, ne yücedir!'),
(2688, 23, 15, 'Sonra siz, bunun ardından öleceksiniz.'),
(2689, 23, 16, 'Sonra, siz kıyamet günü muhakkak diriltileceksiniz.'),
(2690, 23, 17, 'Üstünüzde de yedi tabaka (yedi gök) yarattık. Biz yaratmadan gafil değiliz.'),
(2691, 23, 18, 'Gökten belli ölçü ve miktarda su indirip onu yerde durdurduk. Biz onu (indirmeğe kadir olduğumuz gibi) gidermeğe de kadiriz.'),
(2692, 23, 19, 'Onunla size, içlerinde sizin için birçok meyvalar bulunan hurma ve üzüm bahçeleri yetiştirdik, onlardan yiyorsunuz.'),
(2693, 23, 20, 'Yine onunla Tur-i Sina\'dan çıkan, (meyvası) yağlı olarak biten, yiyenlerin (yağına ekmeklerini) batıracakları bir (zeytin) ağac(ı) yetiştirdik.'),
(2694, 23, 21, 'Hayvanlarda da sizin için ibret vardır: Karınlarının içindekinden size içiriyoruz. Onlarda sizin için daha birçok faydalar var, aynı zamanda onlardan yersiniz.'),
(2695, 23, 22, 'O(hayva)nların üzerinde ve gemiler üzerinde taşınırsınız.'),
(2696, 23, 23, 'Andolsun biz, Nuh\'u kavmine gönderdik: "Ey kavmim, dedi, Allah\'a kulluk edin, sizin O\'ndan başka tanrınız yoktur, korunmaz mısınız?"'),
(2697, 23, 24, 'Kavminin içinden ileri gelen inkarcı bir grup (şöyle) dedi: "Bu da sizin gibi bir insandan başka bir şey değildir. Size üstün gelmek istiyor. Eğer Allah (elçi göndermek) dileseydi, melekleri indirirdi. Biz ilk babalarımızdan böyle bir şey işitmedik."'),
(2698, 23, 25, 'O, kendisinde delilik bulunan bir adamdır, başka bir şey değildir. Hele bir süreye kadar onu gözetleyin. 	He is only a man in whom is a madness, so watch him for a while.'),
(2699, 23, 26, '(Nuh): "Rabbim, beni yalanlamaları karşısında bana yardım et (bana verdiğin sözü yerine getir)!" dedi.'),
(2700, 23, 27, 'Biz de ona vahyettik ki: "Gözlerimizin önünde ve vahyimiz(öğretimimiz)le o gemiyi yap. Bizim buyruğumuz gelip de tandır kaynayınca her cinsten iki çift ve aileni de alıp ona sok. Yalnız onlar içinde alehylerine söz geçmiş (azabımıza uğrama hükmü giymiş) olanları bırak. O zulmedenler hakkında bana yalvarma; onlar, mutlaka boğulacaklardır!'),
(2701, 23, 28, 'Sen ve yanında bulunanlar gemiye yerleştiğiniz zaman: "Bizi o zalim kavimden kurtaran Allah\'a hamdolsun." de.'),
(2702, 23, 29, 'Ve de ki: "Rabbim, beni mübarek bir inişle indir; sen konuklayanların en hayırlısısın."'),
(2703, 23, 30, 'Gerçi biz, (onları) sınıyorduk ama, bu olayda (sizler için de) nice ibretler vardır.'),
(2704, 23, 31, 'Sonra onların ardından başka bir nesil yetiştirdik.'),
(2705, 23, 32, 'Onlara da kendi içlerinden: "Allah\'a kulluk edin, sizin O\'ndan başka Tanrınız yoktur, (Allah\'ın azabından) korunmaz mısınız?" diyen bir elçi gönderdik.'),
(2706, 23, 33, 'Kavminden, kendilerine dünya hayatında bol ni\'met verdiğimiz o inkar eden ve ahiret buluşmasını (hesap ve cezasını) yalanlayan eşraf takımı dedi ki: "Bu da sizin gibi bir insandan başka bir şey değildir. Sizin yediğinizden yiyor, içtiğinizden içiyor."'),
(2707, 23, 34, 'Eğer sizin gibi bir insana ita\'at ederseniz o takdirde siz, mutlaka ziyana uğrayanlarsınız demektir.'),
(2708, 23, 35, 'O size, siz öldüğünüz, toprak ve kemik haline geldiğiniz zaman yeniden hayata çıkarılacağınızı mı va\'dediyor?'),
(2709, 23, 36, 'Heyhat, o size va\'dedilen şey ne kadar uzak!'),
(2710, 23, 37, 'Ne ise hep bu dünya hayatımızdır; ölürüz ve yaşarız, biz öldükten sonra diriltilecek değiliz.'),
(2711, 23, 38, 'O, Allah\'a yalan uydurandan başka bir adam değildir. Biz ona inanıcı(insan)lar değiliz.'),
(2712, 23, 39, '(O peygamber): "Rabbim, dedi, beni yalanlamaları karşısında bana yardım et."'),
(2713, 23, 40, '(Allah): "Az sonra onlar pişman olacaklar!" dedi.'),
(2714, 23, 41, 'Derken o korkunç ses, onları gerçekten yakaladı da onları sel süprüntüsü haline getirdik. Uzak olsun o zalim kavim!.'),
(2715, 23, 42, 'Sonra onların ardından başka nesiller yetiştirdik.'),
(2716, 23, 43, 'Hiçbir ümmet, ne süresinden ileri geçebilir, ne de geri kalabilir.'),
(2717, 23, 44, 'Sonra biz, elçilerimizi ardı ardına gönderdik. Hangi ümmete elçisi geldiyse onlar onu yalanladılar, biz de onları birbiri ardınca devirdik ve hepsini birer efsane yaptık. İnanmayan toplum uzak olsun.'),
(2718, 23, 45, 'Sonra Musa\'yı ve kardeşi Harun\'u ayetlerimizle ve apaçık bir delille gönderdik;'),
(2719, 23, 46, 'Fir\'avn\'e ve ileri gelen adamlarına. Onlar büyüklük tasladılar ve böbürlenen bir topluluk oldular.'),
(2720, 23, 47, 'Şu iki adamın kavmi bize kölelik ederken, şimdi biz kalkıp bizim gibi iki insana mı inanacağız? dediler.'),
(2721, 23, 48, 'Onları yalanladılar ve helak edilenlerden oldular.'),
(2722, 23, 49, '(Sonra Musa, İsrail oğullarını Mısır\'dan çıkardı. İsrail oğulları) Doğru yolu bulsunlar diye biz, Musa\'ya Kitabı (Tevrat\'ı) verdik.'),
(2723, 23, 50, 'Meryem oğlunu ve annesini bir mu\'cize kıldık ve onları oturmaya uygun, çeşmeli bir tepeye yerleştirdik.'),
(2724, 23, 51, 'Ey elçiler, güzel şeylerden yeyin ve yararlı iş yapın. Çünkü ben yaptıklarınızı bilmekteyim.'),
(2725, 23, 52, 'Ve işte sizin bu ümmetiniz bir tek ümmettir, ben de sizin Rabbinizim, benden korkun. (dedik).'),
(2726, 23, 53, 'Fakat işlerini aralarında parçalayıp, çeşitli Kitaplara ayırdılar. Her parti, kendi yanında bulunanla sevinmektedir.'),
(2727, 23, 54, 'Bir süreye kadar onları, (daldıkları) gafletleri içinde bırak.'),
(2728, 23, 55, 'Onlar sanıyorlar mı ki kendilerine verdiğimiz mal ve oğullar ile,'),
(2729, 23, 56, 'Onların iyiliklerine koşuyoruz? Hayır, (bu verdiğimiz dünya ni\'metleri, onlar için bir imtihandır, fakat onlar) farkında değiller.'),
(2730, 23, 57, 'Onlar ki Rablerine saygıdan titrerler.'),
(2731, 23, 58, 'Ve onlar ki Rablerinin ayetlerine inanırlar.'),
(2732, 23, 59, 'Ve onlar ki Rablerine ortak koşmazlar.'),
(2733, 23, 60, 'Verdiklerini, Rablerinin huzuruna dönecekleri düşüncesiyle kalbleri korkudan ürpererek verirler.'),
(2734, 23, 61, 'İşte onlar, hayır işlerine koşarlar ve onlar hayır için önde giderler.'),
(2735, 23, 62, 'Biz, hiç kimseye gücünün üstünde bir şey teklif etmeyiz. Katımızda gerçeği söyleyen bir Kitap vardır. (Herkesin eylemleri onda tesbit edilmiştir), onlara asla haksızlık edilmez.'),
(2736, 23, 63, 'Fakat onların kalbleri, bundan gaflet içindedir. Onların bundan başka (birtakım pis) işleri daha var ki, onlar hep o işler için çalışırlar.'),
(2737, 23, 64, 'Nihayet varlıklılarını azab ile yakaladığımız zaman, hemen feryada başlarlar.'),
(2738, 23, 65, 'Bugün artık feryadetmeyin, bize karşı size yardım olunmaz (kimse sizi bizim azabımızdan kurtaramaz). 	Supplicate not this day! Assuredly ye will not be helped by Us.'),
(2739, 23, 66, 'Ayetlerim size okunuyordu da siz arkanıza dönüyordunuz. 	My revelations were recited unto you, but ye used to turn back on your heels,'),
(2740, 23, 67, 'Ayetlerime karşı kibirlenerek geceleyin (Ka\'be\'nin çevresinde toplanıp) saçmalıyordunuz.'),
(2741, 23, 68, 'Onlar o sözü (Kur\'an\'ı) iyice düşünmediler mi, yoksa onlara, ilk atalarına gelmeyen bir şey (bir elçi ve Kitap) geldi diye mi (böyle davranıyorlar)?'),
(2742, 23, 69, 'Yoksa elçilerini tanımadıkları (onun doğruluğunu, dürüstlüğünü bilmedikleri) için mi onu inkar ediyorlar?'),
(2743, 23, 70, 'Yoksa "Onda bir delilik var" mı diyorlar? Hayır, o kendilerine hakkı getirdi, fakat çokları haktan hoşlanmıyorlar.'),
(2744, 23, 71, 'Eğer hak, onların keyiflerine uysaydı, gökler, yer ve bunların içinde bulunan kimseler bozulur, giderdi. Biz onlara Zikir\'lerini getirdik fakat onlar, Zikirlerinden yüz çeviriyorlar.'),
(2745, 23, 72, 'Yoksa sen onlardan bir vergi mi istiyorsun (da onun için mi hakkı kabul etmiyorlar)? Rabbinin vergisi daha hayırlıdır. O, rızık verenlerin en iyisidir.'),
(2746, 23, 73, 'Sen onları doğru bir yola çağırıyorsun.'),
(2747, 23, 74, 'Ama ahirete inanmayanlar yoldan sapıyorlar.'),
(2748, 23, 75, 'Biz onlara acıyıp da başlarındaki sıkıntıyı açsaydık, yine azgınlıklarında bocalamağa devam ederlerdi.'),
(2749, 23, 76, 'Andolsun biz onları azab ile yakaladık, ama yine Rabblerine boyun eğmediler, O\'na yalvarmıyorlar.'),
(2750, 23, 77, 'Nihayet üzerlerine şiddetli bir azab kapısı açtığımız zaman, derhal O\'nun içinde şaşkın ve umutsuz kalırlar.'),
(2751, 23, 78, 'O\'dur ki, sizin için o kulağı, o gözleri ve gönülleri inşa etti. Ne kadar az şükrediyorsunuz!'),
(2752, 23, 79, 'O\'dur ki, sizi yeryüzünde yaratıp yaydı ve O\'na götürüleceksiniz.'),
(2753, 23, 80, 'O\'dur ki yaşatıyor, öldürüyor. Gecenin ve gündüzün değişmesi O\'nun(eseri)dir. Aklınızı kullanmıyor musunuz?'),
(2754, 23, 81, 'Hayır, onlar da evvelkilerin dedikleri gibi dediler:'),
(2755, 23, 82, 'Öldüğümüz, toprak ve kemik haline geldiğimiz zaman mı, biz mi diriltileceğiz? dediler.'),
(2756, 23, 83, 'Andolsun bu tehdid bize de bizden önce atalarımıza da yapıldı. Bu, evvelkilerin masallarından başka bir şey değildir.'),
(2757, 23, 84, 'De ki: "Biliyorsanız dünya ve içinde bulunanlar kimindir?"'),
(2758, 23, 85, 'Allah\'ındır diyecekler. "O halde düşün(üp, ilk kez yaratanın, ikinci defa yine yaratılabileceğini anla)mıyor musunuz?" de.'),
(2759, 23, 86, 'Yedi göğün Rabbi ve büyük Arş\'ın Rabbi kimdir? de.'),
(2760, 23, 87, 'Bunlar Allah\'ındır diyecekler. "O halde korunmuyor musunuz?" de.'),
(2761, 23, 88, 'Biliyorsanız (söyleyin) her şeyin melekutu (mülkü ve yönetimi) elinde olan, koruyup kollayan, fakat kendisi korunup kollan(maya muhtaç ol)mayan kimdir? de.'),
(2762, 23, 89, '(Her şeyin yönetimi) Allah\'a aittir diyecekler. "O halde nasıl büyüleniyorsunuz?" de.'),
(2763, 23, 90, 'Doğrusu biz, onlara hakkı getirdik, (bizim söylediklerimiz gerçektir), onlarsa yalancıdırlar.'),
(2764, 23, 91, 'Allah çocuk edinmemiştir. O\'nunla beraber hiçbir tanrı yoktur. Öyle olsaydı her tanrı, kendi yarattığını götürürdü ve onlardan biri diğerine üstün gelmeğe çalışırdı. Allah, onların tanımlamalarından uzaktır.'),
(2765, 23, 92, '(O), görünmeyeni ve görüneni bilir; onların ortak koştukları şeylerden yücedir.'),
(2766, 23, 93, 'De ki: "Rabbim, eğer onların tehdidedildikleri şeyi mutlaka bana göstereceksen (ben sağ iken onları cezalandıracaksan),"'),
(2767, 23, 94, 'Rabbim, beni şu zalim kavmin içinde bırakma!'),
(2768, 23, 95, 'Biz, onları tehdidettiğimiz şeyi sana göstermeğe elbette kadiriz (onları cezalandıracağız ve sen bunu göreceksin).'),
(2769, 23, 96, 'Kötülüğü en güzel şeyle sav. Biz onların (seni) nasıl vasıflandıracaklarını biliyoruz.'),
(2770, 23, 97, 'Ve de ki: "Rabbim, şeytanların dürtüklemelerinden sana sığınırım."'),
(2771, 23, 98, 'Ve onların yanıma uğramalarından sana sığınırım Rabbim.'),
(2772, 23, 99, 'Nihayet onlardan birine ölüm geldiği zaman: "Rabbim, der, beni geri döndürünüz!"'),
(2773, 23, 100, 'Ki terk ettiğim dünyada yararlı bir iş yapayım. Hayır, bu onun söylediği bir sözdür. Önlerinde ta diriltilecekleri güne kadar bir berzah vardır.'),
(2774, 23, 101, 'Sur\'a üflendiği zaman, artık o gün aralarında soylar yoktur ve (insanlar, birbirlerine soylarını) sormazlar.'),
(2775, 23, 102, 'Kimlerin (eylemlerinin) tartıları ağır gelirse, işte onlar kurtuluşa erenlerdir.'),
(2776, 23, 103, 'Kimlerin tartıları hafif gelirse, işte onlar da kendilerini ziyana sokanlar, cehennemde sürekli kalanlardır.'),
(2777, 23, 104, '(Orada onların) yüzlerini ateş yalar. Öyle ki (ateşin) içinde (dehşetten dudakları gerilir de) dişleri açıkta kalır.'),
(2778, 23, 105, 'Ayetlerim size okunurdu da siz onları yalanlardınız değil mi?'),
(2779, 23, 106, 'Rabbimiz, dediler, bahtsızlığımız bizi yendi. Biz sapık bir topluluk olduk.'),
(2780, 23, 107, 'Rabbimiz, bizi bundan çıkar. Eğer bir daha (yaptığımız kötü işlere) dönersek artık biz gerçekten zalimleriz.'),
(2781, 23, 108, 'Buyurdu ki: "Sinin orada, bana bir şey söylemeyin!"'),
(2782, 23, 109, 'Zira kullarımdan bir zümre: \'Rabbimiz inandık, bizi bağışla, bize acı, sen acıyanların en hayırlısısın\' dedikleri için'),
(2783, 23, 110, 'Siz onlarla alay ettiniz, (sürekli onlarla uğraştığınız için onlar) size beni anmayı unutturdular. Siz daima onlara gülüyordunuz.'),
(2784, 23, 111, 'Bugün ben, onlara sabretmelerinin karşılığını verdim; onlar (evet) işte kurtulup murada erenler onlardır.'),
(2785, 23, 112, 'Ve buyurdu: "Yer yüzünde yıllar sayısınca ne kadar kaldınız?"'),
(2786, 23, 113, '(Herhalde) Bir gün, yahut günün bir kısmı kadar kaldık; sayanlara sor, dediler.'),
(2787, 23, 114, 'Buyurdu ki: "Sadece az bir zaman kaldınız, keşke bilseydiniz!"'),
(2788, 23, 115, 'Bizim sizi boş yere, bir oyun ve eğlence olarak yarattığımızı ve sizin bize döndürülüp getirilmeyeceğinizi mi sandınız?'),
(2789, 23, 116, 'Hak padişah olan Allah, pek yücedir. O\'ndan başka tanrı yoktur. O, Kerim Arş\'ın sahibidir.'),
(2790, 23, 117, 'Kim Allah ile beraber, varlığını kanıtlayacak hiçbir delil bulunmayan bir tanrıya taparsa, onun hesabı, Rabbinin yanındadır (onu Allah cezalandırır) çünkü kafirler iflah olmazlar.'),
(2791, 23, 118, 'De ki: "Rabbim, bağışla, acı, sen acıyanların en hayırlısısın."');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Bu indirdiğimiz ve uygulanmasını farz kıldığımız bir suredir. Düşünüp öğüt almanız için onda açık açık ayetler indirdik.'),
(2793, 24, 2, 'Zina eden kadın ve zina eden erkeğin her birine yüz değnek vurun; Allah\'a ve ahiret gününe inananlar iseniz Allah\'ın cezasını uygulamada sizi, onlara karşı acıma duygusu tut(up engelle)mesin. Mü\'minlerden bir grup da onlara yapılan azaba şahid olsun.'),
(2794, 24, 3, 'Zina eden erkek, zina eden veya ortak koşan kadından başkasıyla evlenmez; zina eden kadın da zina eden veya ortak koşan erkekten başkasıyla evlenmez. Böyleleriyle evlenmek mü\'minlere haram kılınmıştır.'),
(2795, 24, 4, 'Namuslu kadınları zina ile suçlayıp da sonra (bu suçlamalarını ispat için) dört şahid getirmeyenlere seksen değnek vurun ve artık onların şahidliğini asla kabul etmeyin. Onlar yoldan çıkmış kimselerdir.'),
(2796, 24, 5, 'Ancak bundan sonra tevbe edip uslananlar hariç. Çünkü Allah çok bağışlayan, çok esirgeyendir.'),
(2797, 24, 6, 'Eşlerini zina ile suçlayıp kendilerinden başka şahidleri bulunmayan kimselere gelince: Onlardan her birinin şahidliği, kendisinin mutlaka doğru söyleyenlerden olduğuna, dört defa Allah\'ı şahid tutmasıdır.'),
(2798, 24, 7, 'Beşinci defa da: Eğer yalan söyleyenlerden ise Allah\'ın la\'netinin kendi üzerine olmasını diler.'),
(2799, 24, 8, 'Kadının da dört defa sözüne Allah\'ı şahid tutup kocasının, mutlaka yalan söyleyenlerden olduğuna şahidlik etmesi, kendisinden azabı kaldırır.'),
(2800, 24, 9, 'Beşinci defa da: Eğer kocası doğrulardan ise Allah\'ın gazabının kendi üzerine olmasını diler.'),
(2801, 24, 10, 'Ya Allah\'ın size lutfu ve rahmeti olmasaydı ve Allah, tevbeleri çok kabul eden ve hikmet sahibi olmasaydı (ne yapardınız)?'),
(2802, 24, 11, 'O yalan haberi getir(ip ortaya at)anlar, içinizden bir topluluktur. Siz, onu sizin için şer sanmayın. Tersine o, sizin için hayırdır. Onlardan her kişi işlediği günah\'ın cezasını görecektir. Onlardan o(yala)nın en büyüğünü idare edene de büyük bir azab vardır.'),
(2803, 24, 12, 'Onu işittiğiniz zaman inanan erkek ve kadınların, kendiliklerinden güzel zanda bulunup: "Bu, apaçık bir iftiradır" demeleri gerekmez miydi?'),
(2804, 24, 13, 'Ona dört şahid getirmeleri gerekmez miydi? Madem ki şahidleri getirmediler, o halde onlar Allah yanında yalancıların ta kendileridir.'),
(2805, 24, 14, 'Eğer size dünyada ve ahirette Allah\'ın lutfu ve rahmeti olmasaydı, içine daldığınız yaygarada size mutlaka büyük bir azab dokunurdu.'),
(2806, 24, 15, 'Çünkü siz, onu dillerinizle alıveriyorsunuz ve hakkında hiç bilginiz olmayan bir şeyi, (düşünüp taşınmadan, hemen) ağızlarınızla söylüyorsunuz ve onu önemsiz bir iş sanıyorsunuz. Oysa o, Allah yanında büyük(bir günah)tır.'),
(2807, 24, 16, 'Onu işittiğiniz zaman, "Bunu konuşmamız bize yakışmaz, haşa, bu, büyük bir iftiradır." demeniz gerekmez miydi?'),
(2808, 24, 17, 'Allah size öğüt veriyor ki eğer inananlar iseniz böyle bir şeye bir daha asla dönmeyesiniz.'),
(2809, 24, 18, 'Allah size ayetleri(ni) açıklıyor. Allah bilendir, hüküm ve hikmet sahibidir.'),
(2810, 24, 19, 'İnananlar içinde edepsizliğin yayılmasını isteyenler için dünyada da, ahirette de acı bir azab vardır. Allah bilir, siz bilmezsiniz.'),
(2811, 24, 20, 'Eğer size Allah\'ın lutfu ve rahmeti olmasaydı ve Allah çok şefkatli ve merhametli olmasaydı (bu iftiranızdan dolayı büyük bir azaba uğrardınız)!'),
(2812, 24, 21, 'Ey inananlar, şeytanın adımlarını izlemeyin. Kim şeytanın adımlarını izlerse o, ona edepsizliği ve kötülüğü emreder. Eğer size, lutfu ve rahmeti olmasaydı Allah, hiçbirinizi asla temizlemezdi. Fakat Allah dilediğini arındırır. Allah işitendir, bilendir.'),
(2813, 24, 22, 'Sizden fazilet ve servet sahibi kimseler, yakınlığı bulunanlara, yoksullara, Allah yolunda göç edenlere bir şey vermemeğe yemin etmesinler, affetsinler, hoşgörsünler. Allah\'ın sizi bağışlamasını sevmez misiniz? Allah bağışlayandır, esirgeyendir.'),
(2814, 24, 23, 'O namuslu, bir şeyden habersiz, inanmış kadınlara zina iftira edenler, dünya\'da da ahirette de la\'netlenmişlerdir. Onlar için büyük bir azab vardır.'),
(2815, 24, 24, 'O gün, dilleri, elleri ve ayakları yaptıklarına şahidlik edecektir.'),
(2816, 24, 25, 'O gün Allah, onlara hak ettikleri cezalarını tam verir ve onlar da bilirler ki Allah, apaçık Hak\'tır.'),
(2817, 24, 26, 'Kötü kadınlar, kötü erkeklere; kötü erkekler kötü kadınlara; iyi kadınlar iyi erkeklere; iyi erkekler de iyi kadınlara mahsustur. Bunlar onların söyledikleri(çirkin şeyler)den uzaktırlar. Bunlara, (Allah tarafından) bağışlama ve cömertçe bir rızık vardır.'),
(2818, 24, 27, 'Ey inananlar, kendi evlerinizden başka evlere, izin alıp halkına selam vermeden girmeyin. Herhalde bunun, sizin için daha iyi olduğunu düşünüp anlarsınız.'),
(2819, 24, 28, 'Eğer orada kimseyi bulamazsanız, size izin verilinceye kadar oraya girmeyin. Ve eğer size: "Dönün" denirse dönün. Bu, sizin için daha temizdir. Allah yaptıklarınızı bilendir.'),
(2820, 24, 29, 'Oturulmayan ve içinde eşyanız bulunan evlere (izinsiz) girmenizden dolayı size bir günah yoktur. Allah, açığa vurduğunuzu da, gizlediğinizi de bilir.'),
(2821, 24, 30, 'İnanan erkeklere söyle: "Bazı bakışlarını kıssınlar, ırzlarını korusunlar. Bu, onlar için daha temizdir. Şüphesiz Allah, onların her yaptıklarını haber almaktadır.'),
(2822, 24, 31, 'İnanan kadınlara da söyle: "Bazı bakışlarını kıssınlar, ırzlarını korusunlar. Süslerini göstermesinler. Ancak kendiliğinden görünenler hariç. Baş örtülerini (göğüs) yırtmaçlarının üstüne koysunlar. Süslerini kimseye göstermesinler. Yalnız kocalarına, yahut babalarına, yahut kocalarının babalarına, yahut oğullarına, yahut kocalarının oğullarına, yahut kardeşlerine, yahut kardeşlerinin oğullarına, yahut kızkardeşlerinin oğullarına, yahut kadınlarına, yahut ellerinin altında bulunan(köle)lerine, yahut kadına ihtiyacı bulunmayan erkek tabi\'lerine, yahut henüz kadınların mahrem yerlerini anlamayan çocuklara gösterebilir. Gizledikleri süslerin bilinmesi için ayaklarını vurmasınlar. Ey mü\'minler, topluca Allah\'a tevbe edin ki felaha eresiniz.'),
(2823, 24, 32, 'İçinizden bekarları ve köle ve cariyelerinizden iyileri evlendirin. Eğer yoksul iseler, Allah, lutfiyle onları zengin eder. Allah(ın mülkü) geniştir, O, (her şeyi) bilendir.'),
(2824, 24, 33, 'Evlenme (imkanı) bulamayanlar, Allah kendilerini lutfundan zengin edinceye kadar iffetlerini korusunlar. Ellerinizin altında bulunan (köle ve cariye)lerden, mükatebe (akdi) yapmak isteyenlerle Ğeğer kendilerinde hayır görürsenizĞ mükatebe yapın ve Allah\'ın size verdiği malından onlara da verin. Dünya hayatının geçici menfaatini elde etmek için, namuslu kalmak isteyen cariyelerinizi fuhşa zorlamayın. Kim onları (fuhşa) zorlarsa, şüphesiz Allah (fuhşa) zorlanmalarından sonra (o kadınlara karşı) bağışlayıcı, esirgeyicidir.'),
(2825, 24, 34, 'Andolsun ki size, açıklayıcı ayetler ve sizden önce gelip geçenlerden bir temsil ve korunanlar için bir öğüt indirdik.'),
(2826, 24, 35, 'Allah, göklerin ve yerin nurudur. O\'nun nuru, içinde lamba bulunan bir kandile benzer. Lamba cam içerisindedir. Cam, sanki inciden bir yıldız. Ne doğuya ve ne batıya mensub olmayan mübarek bir zeytin ağacı(nın yağı)ndan yakılır. (Öyle mübarek bir ağaç) Ki, neredeyse ateş değmese de yağı ışık verir. Işığı parıl, parıldır. Allah, dilediği kimseyi nuruna iletir. Allah insanlara misaller verir. Allah her şeyi bilir.'),
(2827, 24, 36, '(Bu kandil) Allah\'ın yükseltilmesine ve içlerinde adının anılmasına izin verdiği evlerdedir. Onların içinde sabah akşam O\'nu tesbih eder(şanının yüceliğini anar)lar:'),
(2828, 24, 37, 'Kendilerini ne ticaretin, ne de alışverişin Allah\'ı anmaktan, namaz kılmaktan, zekat vermekten alıkoymadığı erkekler. (Onlar), yüreklerin ve gözlerin (dehşetten) ters döneceği günden korkarlar.'),
(2829, 24, 38, 'Ki Allah onlara yaptıklarının en güzel karşılığını versin ve lutfundan onlara daha fazlasını da ihsan etsin. Allah dilediğini hesapsız rızıklandırır.'),
(2830, 24, 39, 'İnkar edenler(e gelince): Onların işleri, düz arazideki serap gibidir. Susayan onu su sanır, fakat yanına gelince hiçbir şey olmadığını anlar ve yanında Allah\'ı bulur; Allah onun hesabını tam görür, O, hesabı çabuk görendir.'),
(2831, 24, 40, 'Yahut (Onların işleri) engin bir denizdeki karanlıklar gibidir: (Bir deniz) Ki üstünü bir dalga, örtüyor, onun üstünden bir dalga onun üstünden de bir bulut (örtmektedir). Birbiri üstüne yığılmış karanlıklar. (İçinde bulunan kimse) Elini çıkarsa neredeyse onu dahi göremez. Allah bir kimseye nur vermemişse artık onun nuru olmaz.'),
(2832, 24, 41, 'Görmedin mi göklerde ve yerde olan kimseler, kanatlarını çırparak uçan kuşlar Allah\'ı tesbih ederler? Her biri kendi du\'asını ve tesbihini bilmiştir. Allah da onların ne yaptıklarını bilmektedir.'),
(2833, 24, 42, 'Göklerin ve yerin mülkü Allah\'ındır. Dönüş de Allah\'adır.'),
(2834, 24, 43, 'Görmedin mi Allah bulutları sürer, sonra onları birbirine geçirir, sonra onları birbiri üstüne yığar (sıkıştırır), arasından yağmurun çıktığını görürsün. Gökteki dağlar(gibi büyük bulut parçaların)dan bir dolu indirir de onunla dilediğini vurur, dilediğinden de onu öteye çevirir. Şimşeğinin parıltısı neredeyse gözleri alır.'),
(2835, 24, 44, 'Allah gece ile gündüzü çevirir. Kuşkusuz gözleri olanlar için bunda bir ibret vardır.'),
(2836, 24, 45, 'Allah her canlıyı sudan yarattı; onlardan kimi karnı üzerinde (sürünerek) yürür, kimi iki ayak üstünde yürür, kimi de dört (ayak) üstünde yürür. Allah dilediğini yaratır. Çünkü Allah her şeye kadirdir.'),
(2837, 24, 46, 'Andolsun biz, (gerçekleri) açıklayan ayetler indirdik. Allah dilediğini, doğru yola iletir.'),
(2838, 24, 47, 'Allah\'a ve Elçiye inandık ve ita\'at ettik, diyorlar. Sonra onladan bir grup, bunun ardından dönüyor. Bunlar inanmış değillerdir.'),
(2839, 24, 48, 'Elçinin, aralarında hükmetmesi için Allah\'a ve Elçisine çağırıldıkları zaman hemen onlardan bir grup yüz çevirirler.'),
(2840, 24, 49, 'Eğer hüküm kendi lehlerine olursa ita\'at ederek, gelirler.'),
(2841, 24, 50, 'Kalblerinde bir hastalık mı var, yoksa şühpe mi ettiler? Yoksa Allah\'ın ve Elçisinin kendilerine haksızlık yapacağından mı korkuyorlar? Hayır, onlar zalimlerdir.'),
(2842, 24, 51, 'Elçinin, aralarında hükmetmesi için Allah\'a ve Elçisine çağırıldıkları zaman inananların sözü ancak: "İşittik ve ita\'at ettik" demeleridir. İşte umduklarına erenler bunlardır, bunlar.'),
(2843, 24, 52, 'Kim(ler) Allah\'a ve Resulüne ita\'at eder, Allah\'tan korkar, O\'(nun azabı)ndan korunursa işte kurtuluşa erenler onlardır.'),
(2844, 24, 53, 'Yeminlerinin var gücüyle Allah\'a yemin ettiler: Eğer sen onlara emredersen (savaşa) çıkacaklar diye. De ki: "Yemin etmeyin. (Sizden istenen, yalan yere yemin etmek değil), güzel ita\'at etmektir. Şüphesiz Allah, yaptıklarınızı haber almaktadır".'),
(2845, 24, 54, 'De ki: "Allah\'a ita\'at edin, Elçiye ita\'at edin." Eğer dönerseniz, ona gereken, kendisine yükletilen(duyurma görevini yapmak), size gereken de size yükletilen(ita\'at görevini yapmak)dır. Eğer ona ita\'at ederseniz, doğru yolu bulursunuz. Elçiye düşen, sadece açık bir şekilde duyurmaktır.'),
(2846, 24, 55, 'Allah sizden, inanıp iyi işler yapanlara va\'detmiştir: Onlardan öncekileri nasıl hükümran kıldıysa, onları da yer yüzünde hükümran kılacak ve kendileri için seçip beğendiği dinlerini kendilerine sağlamlaştıracak ve korkularının ardından kendilerini (tam) bir güvene erdirecektir. Bana kulluk edecekler ve bana hiçbir şeyi ortak koşmayacaklar. Ama kim(ler) bundan sonra da nankörlük ederse işte onlar, yoldan çıkanlardır.'),
(2847, 24, 56, 'Namazı kılın, zekatı verin, Elçiye ita\'at edin ki size acınsın.'),
(2848, 24, 57, 'Nankörlerin, yer yüzünde (Allah\'ı) aciz bırakacaklarını, (Allah\'ın azabına engel olacaklarını) sanma. Onların varacağı yer ateştir. Ne kötü bir gidiş yeridir o!'),
(2849, 24, 58, 'Ey inananlar, ellerinizin altında bulunan (köle ve hizmetçi)ler ve henüz erginliğe ermemiş çocuklarınız üç vakitte (odalarınıza girebilmek için) izin istesinler: Sabah namazından önce, öğle vakti elbisenizi çıkar(ıp yat)acağınız zaman ve yatsı namazından sonra. Bunlar sizin üstünüzün açılabileceği üç vakittir. Bunların dışında (hizmetçilerin ve çocukların, izin almadan içeri girmelerinden dolayı) ne size, ne de onlara bir günah yoktur. (Onlar sizin) yanınızda dolaşırlar, birbirinizin yanına girip çıkarsınız. Allah ayetleri size böyle açıklar. Allah bilendir, hüküm ve hikmet sahibidir.'),
(2850, 24, 59, 'Çocuklarınız erginlik çağına erdikleri zaman kendilerinden öncekilerin izin istedikleri gibi izin istesinler. İşte Allah size ayetlerini böyle açıklıyor. Allah bilendir, hüküm ve hikmet sahibidir.'),
(2851, 24, 60, 'Evlenme arzusu kalmamış, oturan (ihtiyar) kadınların, kasden süs göstermeğe çalışmadan, dış örtülerini bırakmalarında kendileri için bir günah yoktur. Ama sakınmaları, kendileri için daha hayırlıdır. Allah işitendir, bilendir.'),
(2852, 24, 61, 'Köre güçlük yoktur, topala güçlük yoktur, hastaya güçlük yoktur. Size de kendi evlerinizden, yahut babalarınızın evlerinden, yahut annelerinizin evlerinden, yahut kardeşlerinizin evlerinden, yahut kızkardeşlerinizin evlerinden, yahut amcalarınızın evlerinden, yahut halalarınızın evlerinden, yahut dayılarınızın evlerinden, yahut teyzelerinizin evlerinden, yahut anahtarları ellerinizde bulunan evlerden, yahut arkadaşınızın evlerinden yemenizde bir güçlük yoktur. Toplu olarak yahut ayrı ayrı yemenizde de üzerinize bir günah yoktur. Evlere girdiğiniz zaman Allah tarafından kutlu, güzel bir yaşama dileği olarak kendinize (kendinizden olan ev halkına) selam verin. İşte Allah, ayetleri size böyle açıklıyor ki düşünüp anlayasınız.'),
(2853, 24, 62, 'Mü\'minler o kimselerdir ki Allah\'a ve Elçisine (gönülden) inanmışlardır. Toplumsal bir iş için Allah\'ın Elçisi ile beraber bulundukları zaman ondan izin almadan gitmezler. (Ey Muhammed), Senden izin alanlar, işte Allah\'a ve Elçisine inananlar onlardır. Bazı işleri için senden izin istedikleri zaman onlardan dilediğine izin ver ve onlar için Allah\'tan mağfiret dile. Şüphesiz Allah çok bağışlayan, çok esirgeyendir.'),
(2854, 24, 63, 'Elçinin çağırmasını, aranızda herhangi birinizin diğerini çağırmasıyla bir tutmayın. Allah içinizden, birbirinin arkasına gizlenerek sıvışıp gidenleri bilir. Elçinin emrine aykırı davrananlar, kendilerine bir belanın çarpmasından, yahut onlara acı bir azabın uğramasından sakınsınlar.'),
(2855, 24, 64, 'İyi bilinki göklerde ve yerde olanlar hep Allah\'ındır. O, sizin ne iş üzerinde bulunduğunuzu, (ne yaptığınızı, içinizde nasıl bir niyet taşıdığınızı) bilir. O\'na döndürülüp götürüldükleri gün, ne yaptıklarını onlara haber verir. Allah, her şeyi bilendir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Alemlere uyarıcı olması için kuluna Furkanı (hakkı batıldan ayırma ölçüsünü) indiren (Allah) pek kutludur!'),
(2857, 25, 2, 'Göklerin ve yerin mülkü O\'nundur, O, bir çocuk edinmemiştir, mülkünde ortağı yoktur. Her şeyi yaratmış, ona ölçü, biçim ve düzen vermiştir.'),
(2858, 25, 3, 'O\'ndan ayrı olarak, hiçbir şey yaratmayan, kendileri yaratılan ve kendilerine dahi ne zarar ne de yarar veremeyen; öldüremeyen, yaşatamayan, (ölüleri diriltip) kaldıramayan birtakım tanrılar edindiler.'),
(2859, 25, 4, 'İnkar edenler: "Bu, yalandan başka bir şey değildir. (Muhammed) onu uydurdu, başka bir topluluk da kendisine yardım etti." dediler ve kesin bir haksızlığa ve iftiraya vardılar.'),
(2860, 25, 5, 'Dediler: "Evvelkilerin masalları, onları yazmış, sabah akşam onlar kendisine yazdırılıyor."'),
(2861, 25, 6, 'De ki: "Onu, göklerdeki ve yerdeki gizleri bilen indirdi. O, çok bağışlayan, çok esirgeyendir.'),
(2862, 25, 7, 'Dediler: "Bu elçiye ne oluyor ki yemek yiyor, çarşılarda geziyor? Ona kendisiyle beraber uyarıcı olacak bir melek indirilmeli değil mi?"'),
(2863, 25, 8, 'Yahut üstüne bir hazine atılmalı, yahut kendisinin ürününden yiyeceği bir bahçesi olmalı değil mi? Ve zalimler: "Siz başka değil, sadece büyülenmiş bir adama uyuyorsunuz" dediler.'),
(2864, 25, 9, 'Bak, senin için nasıl benzetmeler yaptılar da saptılar. Artık bir daha yolu bulamazlar.'),
(2865, 25, 10, 'Yücedir O ki dilerse sana bundan daha hayırlısını, altlarından ırmaklar akan bahçeler verir ve senin için saraylar yapar.'),
(2866, 25, 11, 'Onlar (senin hakkında o yakışıksız sözleri söylemekle kalmadılar) bilakis, (Duruşma) sa\'ati(ni) de yalanladılar. Biz (Duruşma) sa\'ati(ni) yalanlayanlara alevli bir ateş hazırlamışızdır.'),
(2867, 25, 12, '(Bu ateş) onları uzak bir yerden görünce onlar bunun öfkesini ve homurtusunu işitirler.'),
(2868, 25, 13, '(Elleri boyunlarına zincirlerle) Bağlı olarak onun dar bir yerine atıldıkları zaman orada ölümü çağırırlar (yetiş ey ölüm, nerdesin, gel bizi bu azabdan kurtar! derler).'),
(2869, 25, 14, 'Bugün bir ölüm çağırmayın, birçok ölüm çağırın.'),
(2870, 25, 15, 'De ki: "Bu mu iyi, yoksa korunanlara va\'dedilen ebedi cennet mi? O da onların mükafat ve sonucudur!"'),
(2871, 25, 16, 'Orada istediklerini bulurlar ve sürekli kalırlar. Bu, Rabbinin, istenen, arzu edilen bir va\'didir.'),
(2872, 25, 17, '(Rabbin), onları ve Allah\'tan başka taptıklarını biraraya toplayacağı gün, (tapılanlara) der ki: "Bu kullarımı siz mi saptırdınız, yoksa kendileri mi yolu sapıttılar?"'),
(2873, 25, 18, 'Derler ki: "Senin şanın yücedir, senden başka veliler edinmek bize yaraşmaz. Fakat sen onları ve atalarını ni\'met verip yaşattın, (bolluk içinde dünyaya daldılar da seni) anmayı unuttular ve helaki hak eden bir topluluk oldular.'),
(2874, 25, 19, '(Bu kez hitap, bunlara tanrı diye tapanlara yönelir.) İşte (tanrı) dedikleriniz de sizi yalanladılar. Artık ne (azabı geri) çevirmeğe gücünüz yeter, ne de (kendinize) bir yardım bulabilirsiniz! Sizden kim zulmederse ona büyük bir azab taddırırız.'),
(2875, 25, 20, 'Senden önce gönderdiğimiz bütün elçiler de yemek yerler, çarşılarda gezerlerdi. Biz sizi birbiriniz için bir sınav yaptık. (Sizin bir kısmınızı, diğer bir kısmınızla denemekteyiz ki bakalım) sabrediyor musunuz? Rabbin, (herşeyi) görendir.'),
(2876, 25, 21, 'Bizimle karşılaşmayı ummayanlar: "Bize melekler indirilmeliydi, yahut Rabbimizi görmeliydik değil mi?" dedi(ler). Andolsun ki onlar kendi içlerinde büyüklük tasladılar ve büyük bir azgınlıkla haddi aştılar.'),
(2877, 25, 22, 'Melekleri gördükleri gün, işte o gün suçlulara müjde yoktur ve onlar; (Size sevinmek) yasaktır, yasak!" derler.'),
(2878, 25, 23, 'Yaptıkları her işin önüne geçmişiz de onu (etrafa) saçılmış toz zerreleri haline getirmişizdir.'),
(2879, 25, 24, 'O gün cennet halkının kalacakları yer daha iyi, dinlenip safa sürecekleri yer daha güzeldir.'),
(2880, 25, 25, 'Göğün bulutları parçalayıp meleklerin bölük bölük indirildiği gün;'),
(2881, 25, 26, 'İşte o gün, gerçek mülk, Rahmanın\'dır, (bütün hükümranlık yalnız O\'na aittir) ve o (gün), kafirler için çetin bir gündür.'),
(2882, 25, 27, 'O gün zalim ellerini ısırıp: "Nolaydı, keşke ben elçiyle beraber bir yol edineydim!" der.'),
(2883, 25, 28, 'Vah bana, ne olurdu, ben falanı dost tutmasaydım!'),
(2884, 25, 29, 'O beni, bana gelen Zikirden saptırdı. Zaten şeytan, insanı yapayalnız ve yardımcısız bırakır."'),
(2885, 25, 30, 'Elçi de: "Ya Rabbi, kavmim, bu Kur\'an\'ı terk edilmiş bıraktılar demiştir.'),
(2886, 25, 31, 'Biz böylece her elçiye suçlulardan bir düşman var ettik. Yol gösterici ve yardımcı olarak Rabbin yeter.'),
(2887, 25, 32, 'İnkar edenler: "Kur\'an, ona bir defada indirilmeli değil miydi?" dediler. Biz onunla senin kalbini sağlamlaştırmak için onu böyle (parça parça indirdik) ve onu ağır ağır okuduk.'),
(2888, 25, 33, 'Onların sana getirdiği her misale (her batıl soruya) karşı mutlaka biz sana, (o batılı yok edecek) gerçeği ve en güzel açıklamayı getiririz.'),
(2889, 25, 34, 'O yüzükoyun cehenneme toplanacak olanlar, işte onlar, yerce çok kötü ve yolca çok sapıktır.'),
(2890, 25, 35, 'Andolsun biz Musa\'ya Kitabı verdik ve kardeşi Harun\'u kendisinin yanında vezir yaptık.'),
(2891, 25, 36, 'Ayetlerimizi yalanlayan kavme gidin, dedik. (Onlara gittiler. Onlar, kendilerine gelen bu elçilerimizi kabul etmeyince biz) de onları yıkıp yok ettik.'),
(2892, 25, 37, 'Nuh kavmi de peygamberleri yalanladıkları vakit- onları da boğduk ve onları insanlara bir ibret yaptık. Zalimlere acı bir azab hazırladık.'),
(2893, 25, 38, 'Ad\'ı, Semud\'u, Res halkını ve bu arada daha birçok nesilleri (inkarları yüzünden helak ettik).'),
(2894, 25, 39, 'Hepsine de (uyarmak için) misaller (geçmişlerden hikayeler) anlattık. (Öğüt almayıp küfürlerinde ısrar edince biz de) hepsini helak ettik.'),
(2895, 25, 40, '(Şu Kureyş müşrikleri) bela yağmuruna tutulan, (üstüne taş yağdırılan) kente vardılar. Onun durumunu görmüyorlar mıydı (ki ibret alsınlar)? Hayır, onlar (öldükten sonra) tekrar dirilip kalkmayı ummuyorlar.'),
(2896, 25, 41, 'Seni gördükleri zaman, mutlaka seni eğlence konusu yapıyorlar; "Allah bunu mu elçi göndermiş?"'),
(2897, 25, 42, 'Eğer biz tanrılarımıza tapmakta ısrar etmeseydik, nerdeyse bizi tanrılarımızdan saptıracaktı. (diyorlar). Azabı gördükleri zaman kimin yolunun sapık olduğunu bileceklerdir.'),
(2898, 25, 43, 'Arzusunu tanrı edinen kimseyi gördün mü? Onun üstüne sen mi bekçi olacaksın?'),
(2899, 25, 44, 'Yoksa sen onların çoğunun işittiklerini, düşündüklerini mi sanıyorsun? Hayır, onlar hayvanlar gibidir, hatta onlar, yolca (hayvanlardan) daha sapıktır.'),
(2900, 25, 45, 'Rabbini görmedin mi gölgeyi nasıl uzattı? Dileseydi, onu durgun yapardı. Sonra nasıl güneşi ona delil kıldık (gölgenin görünmesini, ışığa bağlı kıldık)?'),
(2901, 25, 46, 'Sonra (güneş yükseldikçe) gölgeyi yavaş yavaş çekip aldık.'),
(2902, 25, 47, 'O, geceyi sizin için elbise, uykuyu dinlenme, gündüzü de kalkıp çalışma zamanı yaptı.'),
(2903, 25, 48, 'Ve O, rahmetinin önünde rüzgarları müjdeci gönderdi. Ve gökten tertemiz bir su indirdik.'),
(2904, 25, 49, 'Ki onunla ölü bir ülkeyi diriltelim ve onunla yarattığımız hayvanlardan ve insanlardan birçoğunu sulayalım.'),
(2905, 25, 50, 'Andolsun biz, bu sözü onların aralarında çevirip çevirip anlattık ki öğüt alsınlar. Ama insanların çoğu, nankörlükte direnmektedir.'),
(2906, 25, 51, 'Eğer biz dileseydik, her kente bir uyarıcı gönderirdik.'),
(2907, 25, 52, 'Kafirlere boyun eğme ve bu Kur\'an ile onlara karşı büyük cihad et.'),
(2908, 25, 53, 'O, iki denizi birbirine salmıştır. Bu tatlı, susuzluğu giderici; bu tuzlu ve acıdır. Ve ikisinin arasına birbirine kavuşmalarına engel olan bir perde koymuştur (hiç birbirine kavuşmazlar).'),
(2909, 25, 54, 'Ve O, sudan bir insan yarattı da onu nesep ve sıhr kıldı. Rabbin, her şeye gücü yetendir.'),
(2910, 25, 55, 'Allah\'tan başka kendilerine ne yarar, ne de zarar veremeyecek şeylere tapıyorlar. Kafir, Rabbine karşı şeylere yardımcıdır.'),
(2911, 25, 56, 'Biz seni ancak müjdeleyici ve uyarıcı olarak gönderdik.'),
(2912, 25, 57, 'Buna karşı sizden bir ücret istemiyorum; ancak Rabbine varan yola girmek isteyene yol gösteriyorum de.'),
(2913, 25, 58, 'Ve ölmeyen(diriy)e tevekkül et ve O\'nu överek tesbih et. Kullarının günahlarını, O\'nun bilmesi yeter.'),
(2914, 25, 59, 'O, gökleri, yeri ve ikisinin arasında bulunanları altı günde yarattı, sonra Arş\'a kuruldu (böylece mülkünü yönetmektedir. O) Rahman\'dır. Bunu bir bilene sor.'),
(2915, 25, 60, 'Onlara: "Rahman\'a secde edin!" dendiği zaman: "Rahman nedir? Senin bize emrettiğine secde eder miyiz hiç?" derler. Ve (bu söz), onların nefretini artırır.'),
(2916, 25, 61, 'Yücedir O ki gökte burçlar yaptı ve orada bir kandil ve aydınlatıcı bir ay var etti.'),
(2917, 25, 62, 'Ve O, öğüt almak veya şükretmek isteyenler için gece ile gündüzü, birbirini izler yaptı.'),
(2918, 25, 63, 'Rahman\'ın kulları öyle kimselerdir ki, yeryüzünde mütevazi olarak yürürler, cahiller kendilerine laf atarsa "Selam" derler.'),
(2919, 25, 64, 'Gecelerini Rablerine secde ederek, Onun divanında durarak geçirirler:'),
(2920, 25, 65, 'Rabbimiz, cehennemin azabını bizden uzaklaştır, doğrusu onun azabı sargındır derler.'),
(2921, 25, 66, 'Orası ne kötü bir karargah ve ne kötü bir makamdır!'),
(2922, 25, 67, 'Ve harcadıkları zaman, ne israf ederler ne de cimrilik ederler; harcamaları, bu ikisinin arasında dengeli olur.'),
(2923, 25, 68, 'Ve onlar Allah ile beraber başka tanrıya yalvarmazlar. Allah\'ın haram ettiği canı haksız yere öldürmezler ve zina etmezler. Kim bunları yaparsa cezasını bulur.'),
(2924, 25, 69, 'Kıyamet günü onun için azab kat kat yapılır ve o azab\'ın içinde hor ve hakir olarak kalır.'),
(2925, 25, 70, 'Ancak tevbe edip inanan ve faydalı bir iş yapanlar, işte Allah onların kötülüklerini iyiliklere değiştirecektir. Allah çok bağışlayandır, çok esirgeyendir.'),
(2926, 25, 71, 'Kim tevbe eder ve faydalı iş yaparsa o, makbul bir kimse olarak Allah\'a döner.'),
(2927, 25, 72, 'Onlar yalan ve boş sözün yanında bulunmazlar, boş söze rastladıklarında vekar ile (oradan) geçip giderler.'),
(2928, 25, 73, 'Ve kendilerine Rablerinin ayetleri hatırlatıldığı zaman onlara karşı sağır ve kör davranmazlar.'),
(2929, 25, 74, 'Ve: "Rabbimiz, bize gözler sevinci (gönüller açan) eşler ve çocuklar lutfeyle ve bizi korunanlara önder yap!" derler.'),
(2930, 25, 75, 'İşte onlar, sabretmelerine karşılık saraylarda ödüllendirelecekler ve orada bir sağlık dileği ve selam ile karşılanacaklardır.'),
(2931, 25, 76, 'Orada ebedi kalacaklardır. Ne güzel karargah ve ne güzel makamdır orası!'),
(2932, 25, 77, 'De ki: "Du\'anız (ibadetiniz) olmadıktan sonra Rabbim sizi ne yapsın? (Size haber verdiklerimi) yalanladınız. Bu yüzden cezalandırılmanız gerekecektir."');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Ta sin mim.'),
(2934, 26, 2, 'Şunlar o apaçık Kitabın ayetleridir.'),
(2935, 26, 3, 'Herhalde sen, inanmıyorlar diye neredeyse kendini helak edeceksin!'),
(2936, 26, 4, 'Dilesek onların üzerine gökten bir mu\'cize indiririz de boyunları ona eğilir (inanırlar).'),
(2937, 26, 5, 'Rahman\'dan onlara hiçbir yeni Zikir (uyarı) gelmez ki, mutlaka ondan yüz çevirici olmasınlar.'),
(2938, 26, 6, 'Yalanladılar ama, alay edip durdukları şeyin haberleri, yakında kendilerine gelecektir.'),
(2939, 26, 7, 'Yere bakmadılar mı orada her çeşit güzel çifti bitirmişiz?'),
(2940, 26, 8, 'Şüphesiz bunda bir ibret vardır, ama yine çokları inanıcı değillerdir.'),
(2941, 26, 9, 'Şüphesiz Rabbin, işte üstün O\'dur merhamet eden O\'dur.'),
(2942, 26, 10, 'Rabbin Musa\'ya seslendi: "O zalim kavme git!"'),
(2943, 26, 11, 'Fir\'avn\'ın kavmine. Onlar (kötülüklerden) korunmayacaklar mı?'),
(2944, 26, 12, '(Musa): "Rabbim, dedi, ben, onların beni yalanlayacaklarından korkuyorum."'),
(2945, 26, 13, 'Göğsüm daralıyor, dilim açılmıyor (tutukluk yapıyor), onun için Harun\'a da elçilik ver."'),
(2946, 26, 14, 'Hem benim üzerimde onlara karşı işlediğim bir günah da var (onlardan bir adam öldürmüştüm); onların beni öldürmelerinden korkuyorum.'),
(2947, 26, 15, '(Allah): "Hayır, dedi, ikiniz de ayetlerimizle gidin, biz sizinle beraberiz, (aranızda geçecekleri) dinliyoruz."'),
(2948, 26, 16, 'Fir\'avn\'e giderek deyin ki: Biz alemlerin Rabbinin elçisiyiz."'),
(2949, 26, 17, 'İsrail oğullarını bizimle beraber gönder.'),
(2950, 26, 18, '(Gittiler, Allah\'ın emrini duyurdular. Fir\'avn) Dedi ki: "Biz seni, içimizden bir çocuk olarak yetiştirmedik mi? Ömründe nice yıllar aramızda kalmadın mı?"'),
(2951, 26, 19, 'Ve sonunda o yaptığını da yaptın, sen nankörlerden birisin.'),
(2952, 26, 20, '(Musa): "Onu yaptığım zaman sapıklardan idim" dedi.'),
(2953, 26, 21, 'Sizden korkunca aranızdan kaçtım, sonra Rabbim bana hükümdarlık verdi ve beni elçilerden yaptı'),
(2954, 26, 22, 'O başıma kaktığın ni\'met de İsrail oğullarını köle yapman(yüzünden)dir. (Onları köle diye kullanıp erkek çocuklarını kesmeseydin, senin eline düşmezdim)'),
(2955, 26, 23, 'Fir\'avn dedi ki: "(Ey Musa) alemlerin Rabbi nedir?"'),
(2956, 26, 24, '(Musa): "Göklerin, yerin ve ikisi arasında bulunan her şeyin Rabbidir. Eğer gerçekten inanan kimseler iseniz (bunu anlarsınız)," dedi.'),
(2957, 26, 25, '(Fir\'avn): Çevresinde bulunanlara: "İşitiyor musunuz?" dedi.'),
(2958, 26, 26, '(Musa): "O sizin de Rabbiniz, önceki atalarınızın da Rabbidir" dedi.'),
(2959, 26, 27, '(Fir\'avn): "Size gönderilen bu elçiniz mutlaka delidir" dedi.'),
(2960, 26, 28, '(Musa): "Eğer düşünürseniz O, doğunun batının ve bunlar arasında bulunanların da Rabbidir" dedi.'),
(2961, 26, 29, '(Fir\'avn ey Musa): "Andolsun ki benden başka tanrı edinirsen, seni mutlaka zindana atılanlardan yapacağım" dedi.'),
(2962, 26, 30, '(Musa, peki): "Sana (doğruluğumu) kanıtlayan apaçık bir şey getirmiş olsam da mı?" dedi.'),
(2963, 26, 31, '(Fir\'avn): "Eğer doğrulardansan onu getir (bakalım)," dedi.'),
(2964, 26, 32, '(Musa), asasını attı, bir de (baktılar ki) o apaçık bir ejderha!'),
(2965, 26, 33, 'Elini (koltuğunun altından) çıkardı; o da, bakanlara parıl parıl parlayan bir şey oluverdi.'),
(2966, 26, 34, '(Fir\'avn), çevresindeki ileri gelenlere: "Bu dedi, bilgin bir büyücüdür."'),
(2967, 26, 35, 'Büyüsüyle sizi toprağınızdan çıkarmak istiyor. Ne buyurursunuz?'),
(2968, 26, 36, 'Dediler ki: "Onu ve kardeşini eğle, kentlere toplayıcılar gönder."'),
(2969, 26, 37, 'Bütün bilgin büyücüleri sana getirsinler.'),
(2970, 26, 38, 'Derken büyücüler belli bir günün belirlenen vaktinde bir araya getirildi.'),
(2971, 26, 39, 'Halka da: "Siz de toplanır mısınız?" denildi.'),
(2972, 26, 40, 'Umarız ki büyücüler üstün gelirse biz de onlara uyarız.'),
(2973, 26, 41, 'Büyücüler gelince Fir\'avn\'e: "Eğer üstün gelenler biz olursak, bize mutlaka bir ücret var değil mi?" dediler.'),
(2974, 26, 42, 'Evet dedi, hem o takdirde siz (bana) yakınlardan olacaksınız.'),
(2975, 26, 43, 'Musa onlara: "Atacağınızı atın!" dedi.'),
(2976, 26, 44, 'İplerini ve değneklerini attılar ve "Fir\'avn\'ın şerefine biz, elbette biz galib geleceğiz" dediler.'),
(2977, 26, 45, 'Musa da asasını attı. Birden o, onların uydurduklarını yutmağa başladı.'),
(2978, 26, 46, 'Derhal büyücüler secdeye kapandılar:'),
(2979, 26, 47, 'Dediler: "Alemlerin Rabbine inandık."'),
(2980, 26, 48, 'Musa\'nın ve Harun\'un Rabbine.'),
(2981, 26, 49, '(Fir\'avn) dedi: "Ben size izin vermeden mi ona inandınız? O, size büyü öğreten büyüğünüzdür. Öyleyse (size ne yapacağımı) yakında bileceksiniz: Ellerinizi ve ayaklarınızı çapraz olarak keseceğim ve hepinizi asacağım!"'),
(2982, 26, 50, 'Zararı yok, dediler, (nasıl olsa) biz Rabbimize döneceğiz.'),
(2983, 26, 51, 'Biz ilk inananlar olduğumuz için Rabbimizin, hatalarımızı bağışlayacağını umarız.'),
(2984, 26, 52, 'Musa\'ya: "Kullarımı geceleyin (Mısır\'dan çıkar), yürüt; siz takibedileceksiniz." diye vahyettik.'),
(2985, 26, 53, 'Fir\'avn, (İsrail oğullarının gittiğini duyunca) kentlere (asker) toplayıcılar gönderdi.'),
(2986, 26, 54, 'Şunlar, (şu İsrail oğulları), az bir topluluktur dedi.'),
(2987, 26, 55, 'Bizi kızdırmaktadırlar.'),
(2988, 26, 56, 'Biz, ihtiyatlı, koca bir cemaatiz.'),
(2989, 26, 57, 'Böylece biz onları çıkardık: bahçeler(in)den, çeşmeler(in)den.'),
(2990, 26, 58, 'Hazineler(in)den ve o güzel yer(lerin)den.'),
(2991, 26, 59, 'Böylece bunları İsrail oğullarına miras yaptık.'),
(2992, 26, 60, '(Fir\'avn ve adamları), güneş doğarken onların ardına düştüler.'),
(2993, 26, 61, 'İki topluluk (yaklaşıp) birbirini görünce Musa\'nın adamları: "İşte yakalandık!" dediler.'),
(2994, 26, 62, '(Musa): "Hayır, dedi, Rabbim benimle beraberdir. Bana yol gösterecektir."'),
(2995, 26, 63, 'Musa\'ya: "Değneğinle denize vur!" diye vahyettik. (Vurunca deniz) yarıldı, (on iki yol açıldı). Her bölüm, kocaman bir dağ gibi oldu.'),
(2996, 26, 64, 'Ötekileri de buraya yaklaştırdık (Musa ve adamlarının ardından, düşmanları da bu denizde açılan yollara girdiler).'),
(2997, 26, 65, 'Musa\'yı ve beraberinde olanları tamamen kurtardık.'),
(2998, 26, 66, 'Sonra ötekilerini boğduk (Musa ve adamları karaya çıkınca deniz kapandı, Fir\'avn ve adamları boğuldu).'),
(2999, 26, 67, 'Muhakkak ki bunda bir ibret vardır, ama çokları inanmazlar.'),
(3000, 26, 68, 'Şüphesiz Rabbin, işte üstün O\'dur, merhamet eden O\'dur.'),
(3001, 26, 69, 'Onlara İbrahim\'in haberini de oku:'),
(3002, 26, 70, 'Babasına ve kavmine: "Neye tapıyorsunuz?" demişti.'),
(3003, 26, 71, 'Putlara tapıyoruz, onların önünde ibadete duruyoruz. dediler.'),
(3004, 26, 72, 'Peki, dedi, siz du\'a ettiğiniz zaman onlar sizi işitiyorlar mı?'),
(3005, 26, 73, 'Yahut size fayda veya zarar verebiliyorlar mı?'),
(3006, 26, 74, 'Hayır, ama babalarımızın böyle yaptıklarını gördük, (onun için biz de böyle yapıyoruz). dediler.'),
(3007, 26, 75, 'İşte gördünüz mü neye tapıyorsunuz? dedi.'),
(3008, 26, 76, 'Siz ve eski atalarınız?'),
(3009, 26, 77, 'Onlar benim düşmanımdır. Yalnız alemlerin Rabbi (benim dostumdur).'),
(3010, 26, 78, 'Beni yaratan ve bana yol gösteren O\'dur.'),
(3011, 26, 79, 'Bana yediren ve içiren O\'dur.'),
(3012, 26, 80, 'Hastalandığım zaman bana şifa veren O\'dur.'),
(3013, 26, 81, 'Beni öldürecek, sonra diriltecek O\'dur.'),
(3014, 26, 82, 'Ceza günü hatamı bağışlayacağını umduğum da O\'dur.'),
(3015, 26, 83, 'Rabbim, bana hüküm (hükümdarlık, bilgi) ver ve beni Salihler arasına kat.'),
(3016, 26, 84, 'Sonra gelenler arasında bana, bir doğruluk dili nasib eyle (sonraki nesiller arasında hayır ile anılmamı sağla)!'),
(3017, 26, 85, 'Beni ni\'met(i bol olan) cennetinin varislerinden kıl.'),
(3018, 26, 86, 'Babamı da bağışla. Çünkü o, sapıklardandır. 	And forgive my father. Lo! he is of those who err.'),
(3019, 26, 87, '(Kulların) diriltilecekleri gün, beni utandırma.'),
(3020, 26, 88, 'O gün ki, ne mal, ne de oğullar yarar vermez.'),
(3021, 26, 89, 'Ancak Allah\'a sağlam ve temiz kalb getiren (yarar görür).'),
(3022, 26, 90, '(O gün) cennet, korunanlara yaklaştırılır.'),
(3023, 26, 91, 'Cehennem de azgınların karşısına çıkarılır.'),
(3024, 26, 92, 'Onlara "Hani taptıklarınız nerede?" denilir.'),
(3025, 26, 93, 'O Allah\'tan başka (taptıklarınız) size yardım ediyorlar mı, yahut kendilerine yardımları dokunuyor mu?'),
(3026, 26, 94, 'Onlar ve azgınlar, tepe taklak oraya atılırlar.'),
(3027, 26, 95, 'İblis\'in bütün askerleri de.'),
(3028, 26, 96, 'Onlar orada (putlarıyle) çekişerek derler ki:'),
(3029, 26, 97, 'Vallahi biz apaçık bir sapıklık içinde imişiz!'),
(3030, 26, 98, 'Çünkü sizi alemlerin Rabbine eşit tutuyorduk.'),
(3031, 26, 99, 'Ama bizi saptıran o suçlulardır.'),
(3032, 26, 100, 'Şimdi artık bizim ne şefa\'atçilerimiz var,'),
(3033, 26, 101, 'Ne de sıcak bir dostumuz.'),
(3034, 26, 102, 'Ah keşke bir dönüşümüz daha olsa da inananlardan olsak!'),
(3035, 26, 103, 'Muhakkak ki bunda bir ibret vardır, ama yine çokları inanmazlar."'),
(3036, 26, 104, 'Şüphesiz Rabbin, işte üstün O\'dur, merhamet eden O\'dur.'),
(3037, 26, 105, 'Nuh kavmi de gönderilen elçileri yalanladı.'),
(3038, 26, 106, 'Kardeşleri Nuh onlara: "Korunmaz mısınız?" demişti.'),
(3039, 26, 107, 'Ben size gönderilmiş güvenilir bir elçiyim.'),
(3040, 26, 108, 'Allah\'tan korkun ve bana ita\'at edin.'),
(3041, 26, 109, 'Ben sizden, buna karşı bir ücret istemiyorum. Benim ücretim, yalnız alemlerin Rabbine aittir.'),
(3042, 26, 110, 'Öyle ise Allah\'tan korkun ve bana ita\'at edin.'),
(3043, 26, 111, 'Dediler ki: "Sana bayağı kimseler uymuşken biz sana inanır mıyız?"'),
(3044, 26, 112, 'Dedi ki: "Ben onların yaptıklarını(n iç yüzünü) bilmem (ben ancak görünüşe göre hüküm veririm)."'),
(3045, 26, 113, 'Anlayışınız olsa, onların hesabının Rabbime aidolduğunu bilirsiniz.'),
(3046, 26, 114, 'Ben inananları kovacak değilim.'),
(3047, 26, 115, 'Ben sadece apaçık bir uyarıcıyım.'),
(3048, 26, 116, 'Dediler: "Ey Nuh, (bu dediğinden) vazgeçmezsen mutlaka taşlananlardan olacaksın."'),
(3049, 26, 117, '(Nuh): "Rabbim, dedi, kavmim beni yalanladı."'),
(3050, 26, 118, 'Benimle onların arasını aç (aramızda hükmet), beni ve benimle beraber bulunan mü\'minleri kurtar!'),
(3051, 26, 119, 'Biz de onu ve onunla beraber bulunanları, dolu gemi içinde kurtardık.'),
(3052, 26, 120, 'Sonra bunun ardından, geride kalanları boğduk.'),
(3053, 26, 121, 'Muhakkak ki bunda bir ibret vardır, ama yine çokları inanmazlar.'),
(3054, 26, 122, 'Şüphesiz Rabbin, işte üstün O\'dur, merhamet eden O\'dur.'),
(3055, 26, 123, 'Ad (kavmi) de, gönderilen elçileri yalanladı.'),
(3056, 26, 124, 'Kardeşleri Hud onlara: "Korunmaz mısınız?" demişti.'),
(3057, 26, 125, 'Ben size gönderilmiş güvenilir bir elçiyim.'),
(3058, 26, 126, 'Allah\'tan korkun ve bana ita\'at edin.'),
(3059, 26, 127, 'Ben sizden buna karşı bir ücret istemiyorum. Benim ücretim yalnız alemlerin Rabbine aittir.'),
(3060, 26, 128, 'Siz her yol üzerine, (gelip geçenleri yanıltmak için) bir işaret yapıp da boş şeyle mi uğraşıyorsunuz?'),
(3061, 26, 129, 'Belki ebedi yaşarsınız diye köşkler (ve müstahkem kaleler) ediniyorsunuz?'),
(3062, 26, 130, '(Bir kavmi) yakaladığınız zaman da zorbalar gibi yakalıyorsunuz.'),
(3063, 26, 131, 'Allah\'tan korkun ve bana ita\'at edin.'),
(3064, 26, 132, 'Size bildiğiniz ni\'metleri bol bol veren(Allah)dan korkun. 	Keep your duty toward Him Who hath aided you with (the good things) that ye know,'),
(3065, 26, 133, 'O size verdi: davarlar, oğullar,'),
(3066, 26, 134, 'Bahçeler, çeşmeler.'),
(3067, 26, 135, 'Doğrusu ben size büyük bir günün azabı(nın çarpması)ndan korkuyorum.'),
(3068, 26, 136, 'Dediler ki: "Öğüt versen de, öğüt verenlerden olmasan da bizce birdir."'),
(3069, 26, 137, 'Bu (davranışımız), sadece evvelkilerin ahlakı(ve geleneği)dir.'),
(3070, 26, 138, 'Biz azaba uğratılacak değiliz.'),
(3071, 26, 139, '(Böylece) onu yalanladılar. Biz de onları helak ettik. Muhakkak ki bunda bir ibret vardır, ama yine çokları inanmazlar.'),
(3072, 26, 140, 'Şüphesiz Rabbin, işte üstün O\'dur, merhamet eden O\'dur.'),
(3073, 26, 141, 'Semud (kavmi) de gönderilen elçileri yalanladı:'),
(3074, 26, 142, 'Kardeşleri Salih, onlara demişti ki: "Korunmaz mısınız?"'),
(3075, 26, 143, 'Ben sizin için güvenilir bir elçiyim.'),
(3076, 26, 144, 'Allah\'tan korkun ve bana ita\'at edin.'),
(3077, 26, 145, 'Ben sizden buna karşı bir ücret istemiyorum. Benim ücretim yalnız alemlerin Rabbine aittir.'),
(3078, 26, 146, 'Siz burada güven içinde bırakılacağınızı mı sanıyorsunuz?'),
(3079, 26, 147, 'Böyle bahçelerde, çeşme başlarında?'),
(3080, 26, 148, 'Ekinler ve yumuşak tomurcuklu güzel hurmalıklar arasında?'),
(3081, 26, 149, 'Dağlardan ustalıkla evler yontuyorsunuz.'),
(3082, 26, 150, 'Allah\'tan korkun ve bana ita\'at edin.'),
(3083, 26, 151, 'O aşırıların emrine uymayın.'),
(3084, 26, 152, 'Yeryüzünde bozgunculuk yapan, ıslah etmeyen o kimseler(in sözüyle hareket etmeyin).'),
(3085, 26, 153, 'Dediler: Sen, iyice büyülenmişlerdensin."'),
(3086, 26, 154, 'Sen de bizim gibi bir insansın. Eğer doğrulardansan bize bir mu\'cize getir.'),
(3087, 26, 155, 'Dedi: "İşte bu dişi deve(mu\'cize)dir. (Bir gün) onun su içme hakkı var, belli bir günün su içme hakkı da sizin."'),
(3088, 26, 156, 'Sakın, ona bir kötülük dokundurmayın, sonra büyük bir günün azabı sizi yakalar.'),
(3089, 26, 157, 'Nihayet onu kestiler, ama pişman oldular.'),
(3090, 26, 158, 'Ve azab onları yakaladı. Muhakkak ki bunda bir ibret vardır, ama yine çokları inanmazlar.'),
(3091, 26, 159, 'Şüphesiz Rabbin, işte üstün O\'dur, merhamet eden O\'dur.'),
(3092, 26, 160, 'Lut (kavmi) de gönderilen elçileri yalanladı.'),
(3093, 26, 161, 'Kardeşleri Lut, onlara "Korunmaz mısınız?" demişti.'),
(3094, 26, 162, 'Ben sizin için güvenilir bir elçiyim.'),
(3095, 26, 163, 'Allah\'tan korkun ve bana ita\'at edin.'),
(3096, 26, 164, 'Ben sizden buna karşı bir ücret istemiyorum. Benim ücretim yalnız alemlerin Rabbine aittir. 	And I ask of you no wage therefore; my wage is the concern only of the Lord of the Worlds.'),
(3097, 26, 165, 'Alemlerin içinde erkeklere mi gidiyorsunuz?'),
(3098, 26, 166, 'Ve Rabbinizin sizin için yarattığı eşlerinizi bırakıyorsunuz? Siz sınırı aşan bir kavimsiniz.'),
(3099, 26, 167, 'Dediler: "Ey Lut, andolsun, eğer (bundan) vazgeçmezsen, mutlaka sürülenlerden olacaksın.'),
(3100, 26, 168, '(Lut) dedi: "Ben sizin bu işinize, (kadınları bırakıp erkeklere gidişinize) kızanlardanım."'),
(3101, 26, 169, 'Rabbim, beni ve ailemi bunların yaptıklarından kurtar!'),
(3102, 26, 170, 'Biz de onu ve ailesini tamamen kurtardık.'),
(3103, 26, 171, 'Yalnız geride kalanlar arasında bulunan bir koca karıyı (kurtarmadık).'),
(3104, 26, 172, 'Sonra ötekilerini hep yıktık, helak ettik.'),
(3105, 26, 173, 'Ve üzerlerine bir yağmur yağdırdık, uyarıl(ıp da yola gelmey)enlerin yağmuru hakikaten çok kötü oldu!'),
(3106, 26, 174, 'Muhakkak ki bunda bir ibret vardır, ama yine çokları inanmazlar.'),
(3107, 26, 175, 'Şüphesiz Rabbin, işte üstün O\'dur, merhamet eden O\'dur.'),
(3108, 26, 176, 'Eyke halkı da gönderilen elçileri yalanladı.'),
(3109, 26, 177, 'Şu\'ayb, onlara demişti ki: "Korunmaz mısınız?"'),
(3110, 26, 178, 'Ben sizin için güvenilir bir elçiyim.'),
(3111, 26, 179, 'Allah\'tan korkun ve bana ita\'at edin.'),
(3112, 26, 180, 'Ben sizden, buna karşı bir ücret istemiyorum. Benim ücretim yalnız alemlerin Rabbine aittir.'),
(3113, 26, 181, 'Ölçüyü tam yapın, eksiltenlerden olmayın.'),
(3114, 26, 182, 'Doğru terazi ile tartın.'),
(3115, 26, 183, 'İnsanların haklarını kısmayın. Yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın.'),
(3116, 26, 184, 'Sizi ve önceki nesilleri yaratandan korkun. 	And keep your duty unto Him Who created you and the generations of the men of old.'),
(3117, 26, 185, 'Dediler: "Sen iyice büyülenmişlerdensin."'),
(3118, 26, 186, 'Sen de bizim gibi bir insansın, biz seni mutlaka yalancılardan sanıyoruz.'),
(3119, 26, 187, 'Eğer doğrulardansan o halde üzerimize gökten parçalar düşür.'),
(3120, 26, 188, 'Rabbim yaptığınızı daha iyi bilir dedi.'),
(3121, 26, 189, 'Onu yalanladılar, nihayet o gölge gününün azabı, kendilerini yakaladı. Gerçekten o, büyük bir günün azabı idi.'),
(3122, 26, 190, 'Muhakkak ki bunda bir ibret vardır ama yine çokları inanmazlar.'),
(3123, 26, 191, 'Şüphesiz Rabbin, işte üstün O\'dur, merhamet eden O\'dur.'),
(3124, 26, 192, 'Muhakkak ki o (Kur\'an), alemlerin Rabbinin indirmesidir.'),
(3125, 26, 193, 'Onu, er-Ruhu\'l-Emin (güvenilir ruh, Cebrail) indirdi:'),
(3126, 26, 194, 'Senin kalbine; uyarıcılardan olman için,'),
(3127, 26, 195, 'Apaçık Arapça bir dille.'),
(3128, 26, 196, 'O(nun içeriği), evvelkilerin Kitaplarında da vardır.'),
(3129, 26, 197, 'İsrail oğulları bilginlerinin onu bilmesi de onlar için (Kur\'an\'ın Güvenilir Ruh tarafından vahyedildiğine) yeterli bir delil değil mi?'),
(3130, 26, 198, 'Biz onu yabancılardan birine indirseydik de,'),
(3131, 26, 199, 'Onu onlara okusaydı, ona inanmazlardı:'),
(3132, 26, 200, 'Biz onu, suçluların kalblerine öyle soktuk.'),
(3133, 26, 201, 'Acı azabı görünceye kadar da ona inanmazlar.'),
(3134, 26, 202, 'Azab onlara öyle ansızın gelir ki, onlar hiç farkında olmazlar.'),
(3135, 26, 203, '(Birden onu karşılarında bulunca) Acaba bize süre verilir mi?" derler.'),
(3136, 26, 204, 'Hala bizim azabımızı mı acele istiyorlar (doğru söyleyenlerden isen bizi tehdidettiğin azabı getir mi diyorlar)?'),
(3137, 26, 205, 'Baksana, biz onları yıllarca yaşatsak,'),
(3138, 26, 206, 'Sonra tehdidedildikleri (azab) kendilerine gelse,'),
(3139, 26, 207, 'O yaşatıldıkları (zevk-u sefa sürdükleri) şeyler, kendilerine ne yarar sağlardı?'),
(3140, 26, 208, 'Biz, hiçbir kenti helak etmedik ki onun uyarıcıları olmasın (helak etmeden önce mutlaka uyarıcı gönderdik).'),
(3141, 26, 209, '(Uyarıcılar) uyarırlardı. Biz zulmediciler değildik.'),
(3142, 26, 210, 'O(Kur\'a)n\'ı şeytanlar (cinler) indirmedi.'),
(3143, 26, 211, 'Bu, onlara yaraşmaz ve zaten yapamazlar da.'),
(3144, 26, 212, 'Çünkü onlar, (meleklerin sözlerini) işitmekten uzaklaştırılmışlardır.'),
(3145, 26, 213, 'Allah ile beraber başka bir tanrı çağırma, sonra azabedilenlerden olursun.'),
(3146, 26, 214, 'En yakın akrabanı uyar.'),
(3147, 26, 215, 'Ve sana uyan mü\'minlere kanadını indir (onlara karşı mütevazi ve şefkatli davran).'),
(3148, 26, 216, 'Şayet sana (uymaz) karşı gelirlerse: "Ben sizin yaptıklarınızdan uzağım," de.'),
(3149, 26, 217, 'Galib ve esirgeyen(Allah)a tevekkül et.'),
(3150, 26, 218, 'O, seni görür: Namaza durduğun zaman,'),
(3151, 26, 219, 'Ve secde edenler arasında eğilip doğrulurken.'),
(3152, 26, 220, 'Çünkü O, işitendir, bilendir.'),
(3153, 26, 221, 'Şeytanların kime ineceğini size haber vereyim mi?'),
(3154, 26, 222, 'Onlar, her günahkar yalancıya inerler.'),
(3155, 26, 223, 'O yalancılar, (şeytanlara) kulak verirler, çokları da yalan söylerler.'),
(3156, 26, 224, 'Şa\'irlere gelince onlara da azgınlar uyar.'),
(3157, 26, 225, 'Baksana onlar, her vadide şaşkın şaşkın dolaşırlar?'),
(3158, 26, 226, 'Ve onlar yapmayacakları şeyleri söylerler.'),
(3159, 26, 227, 'Ancak inananlar, iyi işler yapanlar, Allah\'ı çok ananlar ve kendilerine zulmedildikten sonra (rakiplerine) üstün gelmeğe çalışanlar böyle değildir. Zulmedenler, yakında nasıl bir devrime uğrayıp devrileceklerini bileceklerdir!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Ta sin. Şunlar Kur\'an\'ın ve apaçık bir Kitabın ayetleridir.'),
(3161, 27, 2, 'İnananlara yol gösterici ve müjdedir.'),
(3162, 27, 3, 'Onlar ki namazı kılarlar, zekatı verirler ve ahirete de kesin olarak inanırlar.'),
(3163, 27, 4, 'Ahirete inanmayanların işlerini kendilerine süslemişizdir, onlar körü körüne bocalarlar.'),
(3164, 27, 5, 'Onlar, öyle kimselerdir ki, en kötü azab kendilerinindir. Ve onlar ahirette de en çok ziyana uğrayanlardır.'),
(3165, 27, 6, '(Ey Muhammed) Sana bu Kur\'an, hüküm ve hikmet sahibi, (herşeyi) bilen (Allah) katından verilmektedir.'),
(3166, 27, 7, 'Musa, ailesine: "Ben bir ateş gördüm (gidip) size ondan bir haber getireyim, yahut size bir ateş koru getireyim de ısınasınız." demişti.'),
(3167, 27, 8, 'Oraya gelince (kendisine) seslenildi: "Ateşin içinde bulunan da, çevresinde olan da mübarek kılındı. Alemlerin Rabbi Allah, eksikliklerden münezzehtir."'),
(3168, 27, 9, 'Ey Musa, gerçek şu ki ben, güçlü, hüküm ve hikmet sahibi olan Allah\'ım!'),
(3169, 27, 10, 'Asanı at! (Musa attığı) asasının küçük bir yılan gibi titreştiğini görünce (korkudan) arkaya dön(üp kaç)dı, geri dön(üp bak)madı (bile). "Ey Musa korkma, çünkü ben (evet), benim huzurumda elçiler korkmaz(lar)."'),
(3170, 27, 11, 'Ancak zulmeden, sonra yaptığı kötülüğün yerine iyilik yapan olursa ona karşı da ben bağışlayıcı, esirgeyiciyim.'),
(3171, 27, 12, 'Elini koynuna sok, kusursuz olarak bembeyaz (parıl parıl) çıksın. (Bu da) Fir\'avn\'a ve onun kavmine (göstereceğin) dokuz mu\'cize içindedir. Çünkü onlar yoldan çıkan bir kavimdir.'),
(3172, 27, 13, 'Onlara açıkça görünen ayetlerimiz gelince: "Bu, apaçık bir büyüdür" dediler.'),
(3173, 27, 14, 'Vicdanları, onlar(ın doğruluğun)a kanaat getirdiği halde, sırf haksızlık ve böbürlenme yüzünden onları inkar ettiler. Bak işte o bozguncuların sonu nasıl oldu.'),
(3174, 27, 15, 'Andolsun biz, Davud\'a ve Süleyman\'a bir ilim verdik de onlar: "Bizi inanan kullarından birçoğuna üstün kılan Allah\'a hamdolsun." dediler.'),
(3175, 27, 16, 'Süleyman, Davud\'a mirasçı oldu ve dedi ki: "Ey insanlar, bize kuşların dili öğretildi. Ve bize her şeyden (bolca) bir pay verildi. İşte bu, açık bir lutuftur."'),
(3176, 27, 17, 'Süleyman\'a cinlerden insanlardan ve kuşlardan orduları toplandı, hepsi bir arada düzenli olarak sevk ediliyordu.'),
(3177, 27, 18, 'Karınca vadisine geldikleri zaman bir karınca: "Ey karıncalar dedi, yuvalarınıza girin ki Süleyman ve orduları farkında olmayarak sizi ezmesinler."'),
(3178, 27, 19, '(Süleyman) Onun sözüne gülümseyerek dedi: "Rabbim, bana ve anama, babama lutfettiğin ni\'mete şükretmemi, senin beğeneceğin faydalı bir iş yapmamı gönlüme ilham eyle ve rahmetinle beni iyi kullarının arasına sok."'),
(3179, 27, 20, 'Kuşları teftiş etti, (içlerinde hüdhüdü bulamadı), dedi ki: "Neden hüdhüdü göremiyorum, yoksa kayıplardan mı oldu?"'),
(3180, 27, 21, 'Ona çetin bir azabedeceğim, ya da onu keseceğim. Yahut da bana (mazeretini belirten) açık bir delil getirecek.'),
(3181, 27, 22, 'Çok geçmeden (hüdhüd) geldi: "Ben, dedi, senin görmediğin bir şey gördüm ve Seba\'dan sana gerçek bir haber getirdim.'),
(3182, 27, 23, 'Ben onlara hükümdarlık eden bir kadın buldum, kendisine her şey verilmiş ve büyük bir tahtı var.'),
(3183, 27, 24, 'Onun ve kavminin, Allah\'ı bırakıp güneşe secde ettiklerini gördüm. Şeytan onlara işlerini süsleyip onları doğru yoldan çevirmiş, bu yüzden yola gelmiyorlar.'),
(3184, 27, 25, 'Göklerde ve yerde gizleneni açığa çıkaran ve gizlediklerini ve açığa vurduklarını bilen Allah\'a secde etmeleri gerekmez mi?'),
(3185, 27, 26, 'Allah ki, O\'ndan başka Tanrı yoktur, büyük Arş\'ın sahibidir.'),
(3186, 27, 27, '(Süleyman): "Bakalım, dedi, doğru mu söyledin, yoksa yalancılardan mısın?"'),
(3187, 27, 28, 'Bu mektubumu götür, onlara at, sonra onlardan biraz öteye çekil de bak, neye başvuruyorlar (ne yapacaklar).'),
(3188, 27, 29, '(Hüdhüd\'ün mektubu götürüp kendisine attığı Seba melikesi Belkis) Danışmanlarına dedi ki: "Ey ileri gelenler, bana çok önemli bir mektup bırakıldı."'),
(3189, 27, 30, 'O Süleyman\'dandır ve Rahman ve Rahim Allah\'ın adiyle (başlamakta)dır. 	Lo! it is from Solomon, and lo! it is: In the name of Allah the Beneficent, the Merciful;'),
(3190, 27, 31, 'Bana karşı büyüklük taslamayın ve bana teslim olarak gelin (diye yazıyor).'),
(3191, 27, 32, 'Ey ileri gelenler, dedi, bu işimde bana bir fikir verin; ben, siz olmadıkça hiçbir işi kesip atmam.'),
(3192, 27, 33, 'Dediler ki: "Biz güçlüyüz, yaman savaşçılarız ama emir senindir. Bak, ne buyurursan öyle yaparız"'),
(3193, 27, 34, 'Dedi: "Hükümdarlar bir ülkeye girdiler mi, orayı bozarlar, halkının şereflilerini alçaltırlar, (evet) böyle yaparlar."'),
(3194, 27, 35, 'Ben onlara bir hediye göndereyim de bakayım elçiler ne ile dönecekler.'),
(3195, 27, 36, '(Elçi, hediyelerle) Süleyman\'a gelince (Süleyman) dedi ki: "Siz bana mal ile yardım mı etmek istiyorsunuz? Allah\'ın bana verdiği, size verdiğinden daha iyidir. Hediyenizle ancak siz sevinirsiniz.'),
(3196, 27, 37, 'Sen, onlara dön (söyle): onlara, kendilerinin asla karşı koyamayacakları ordularla gelirim ve onları hor ve hakir bir durumda oradan sürüp çıkarırım."'),
(3197, 27, 38, '(Elçi gittikten sonra Süleyman, danışmanlarını topladı): "Ey ileri gelenler, dedi, onların bana teslim olarak gelmelerinden önce hanginiz onun tahtını bana getirebilir?"'),
(3198, 27, 39, 'Cinlerden bir ifrit (kötü bir cin): "Sen makamından kalkmadan önce ben onu sana getiririm, dedi, bunu yapmağa gücüm yeter ve bana güvenilir."'),
(3199, 27, 40, 'Yanında Kitaptan bir ilim bulunan kimse de: "Sen gözünü açıp yummadan ben onu sana getirebilirim." dedi. (Süleyman) tahtı yanına yerleşmiş görünce dedi ki: "Bu, Rabbimin lutfundandır. (Kendisine) şükür mü edeceğim, yoksa nankörlük mü edeceğim diye beni sınamak istiyor. Şükreden kendisi için şükretmiş olur; nankörlük edene gelince, Rabbim zengindir (onun şükrüne muhtaç değildir), kerimdir (çok ikram sahibidir, yücedir)."'),
(3200, 27, 41, '(Ve) dedi ki: "Onun tahtını tanınmaz hale getirin, bakalım tanıyabilecek mi, yoksa tanımayacak mı?"'),
(3201, 27, 42, '(Kraliçe) Gelince (ona): "Senin tahtın da böyle mi?" dendi, "Tıpkı o, dedi, zaten bize daha önce bilgi verilmişti. (Allah\'ın kudretini ve senin peygamber olduğunu anlamış) ve biz müslüman olmuştuk."'),
(3202, 27, 43, 'Onu, Allah\'tan başka taptığı şeyler, (bu zamana dek tevhid dinine girmekten) alıkoymuştu. Çünkü kendisi, inkar eden bir kavimden idi.'),
(3203, 27, 44, 'Ona: "Köşke gir!" dendi. Köşkü görünce zemini su sandı ve bacaklarını sıvadı. (Süleyman) "O, cilalı, şeffaf sırçadandır" dedi. (Kraliçe): "Rabbim, ben kendime zulmetmişim. (Artık) Süleyman\'la beraber alemlerin Rabbi Allah\'a teslim oldum," dedi.'),
(3204, 27, 45, 'Andolsun biz, Semud(kavmin)e de kardeşleri Salih\'i: "Allah\'a kulluk edin!" demesi için gönderdik. Baktı ki onlar, birbiriyle çekişen iki bölük olmuşlar.'),
(3205, 27, 46, 'Dedi: "Ey kavmim, iyilikten önce neden kötülüğe eviyorsunuz? Esirgenmeniz için Allah\'tan mağfiret dilemeniz gerekmez mi?"'),
(3206, 27, 47, 'Dediler: "Senin ve seninle beraber bulunanların yüzünden uğursuzluğa uğradık." Dedi: "Uğursuzluğunuz(un sebebi), Allah\'ın yanındadır (herşey O\'nun takdiriyle olur). Doğrusu siz (bu olaylarla) sınanan bir toplumsunuz."'),
(3207, 27, 48, 'Şehirde dokuz kişi vardı ki yeryüzünde bozgunculuk yaparlar, düzeltmezlerdi.'),
(3208, 27, 49, 'Allah\'a and içerek birbirlerini: "Biz, gece ona ve ailesine baskın yap(ıp onları öldür)elim sonra velisine: \'Ailesinin öldürülüşünde bulunmadığımızı, bizim doğru olduğumuzu\' söyleyelim" dediler.'),
(3209, 27, 50, 'Böyle bir tuzak kurdular, biz de onlar hiç farkında olmadan onlara bir tuzak kurduk.'),
(3210, 27, 51, 'Bak, işte tuzaklarının sonucu nasıl oldu, (nasıl) biz onları ve kavimlerini toptan yıktık, yok ettik.'),
(3211, 27, 52, 'İşte şunlar, zulümleri yüzünden çökmüş, ıssız kalmış evleridir. Şüphesiz bunda bilen bir kavim için ibret vardır.'),
(3212, 27, 53, 'İnananları ve korunanları kurtardık.'),
(3213, 27, 54, 'Lut\'u da (gönderdik), kavmine dedi ki: "Siz göre göre o aşırı kötülüğü yapıyorsunuz ha?!"'),
(3214, 27, 55, 'Siz, kadınları bırakıp şehvetle erkeklere mi yaklaşıyorsunuz? Siz gerçekten cahil bir toplumsunuz.'),
(3215, 27, 56, 'Kavminin cevabı sadece şöyle demek oldu: "Lut ailesini kentinizden çıkarın, çünkü onlar temiz kalmak isteyen kimselermiş(!)"'),
(3216, 27, 57, 'Biz de onu ve ailesini kurtardık, yalnız karısının (azabda) kalanlardan olmasını takdir ettik.'),
(3217, 27, 58, 'Üzerlerine (pişmiş çamurdan bir taş) yağmur(u) indirdik. Uyarıl(ıp da aldırmay)anların yağmuru gerçekten ne kötü oldu!'),
(3218, 27, 59, 'De ki: "Hamd olsun Allah\'a, selam O\'nun seçtiği kullarına. Allah mı hayırlı yoksa ortak koştukları şeyler mi?"'),
(3219, 27, 60, 'Yahut gökleri ve yeri kim yarattı? Size gökten su indirdi de onunla sizin bir ağacını dahi bitiremeyeceğiniz gönül açan bahçeler bitirdik. Allah ile beraber başka bir tanrı mı var? Hayır, onlar (haktan) sapan bir kavimdir.'),
(3220, 27, 61, 'Yahut şu dünyayı durulacak yer yapan, arasından ırmaklar çıkaran, üstünde sağlam dağlar yaratan ve iki deniz arasına bir perde koyan kimdir? Allah ile beraber başka bir tanrı mı var? Hayır çokları bilmiyorlar.'),
(3221, 27, 62, 'Yahut du\'a ettiği zaman darda kalmışa kim yetişiyor da kötülüğü (onun üzerinden) kaldırıyor ve sizi (eskilerin yerine) yeryüzünün sahipleri yapıyor? Allah ile beraber başka bir tanrı mı var? Ne de az düşünüyorsunuz?'),
(3222, 27, 63, 'Yahut karanın ve denizin karanlıkları içinde size yol gösteren kim ve rahmetinin önünde rüzgarları müjdeci gönderen kim? Allah ile beraber başka bir tanrı mı var? Haşa, Allah ortak koştukları şeylerden yücedir, münezzehtir (O, eksikliklerden uzaktır).'),
(3223, 27, 64, 'Yahut yaratmağa kim başlıyor, sonra onu (kim) iade ediyor (ölüp ortadan kalkan şeyleri yeniden yaratıyor)? Sizi gökten ve yerden kim rızıklandırıyor? Allah ile beraber başka bir tanrı mı var? De ki: "Eğer doğru iseniz delilinizi getirin."'),
(3224, 27, 65, 'De ki: "Göklerde ve yerde Allah\'tan başka kimse gaybı bilmez. Ne zaman dirileceklerini de bilmezler.'),
(3225, 27, 66, 'Doğrusu onların ahiret hakkındaki bilgileri, ardarda gelip bir araya toplandı. Fakat onlar (hala) ondan bir kuşku içindedirler. Daha doğrusu, onlar ondan yana kördürler'),
(3226, 27, 67, 'İnkar edenler dediler ki: "Biz de babalarımız da toprak olduktan sonra mı, biz mi (diriltilip) çıkarılacağız?"'),
(3227, 27, 68, 'Bu tehdid, bize de; önceden atalarımıza da yapıldı. Bu, öncekilerin masallarından başka bir şey değildir.'),
(3228, 27, 69, 'De ki: "Yeryüzünde yürüyün de suçluların sonunun nasıl olduğunu görün."'),
(3229, 27, 70, '(Ey Muhammed) onlar(ın sözlerin)e üzülme, tuzak kurmalarından da sıkılma.'),
(3230, 27, 71, 'Doğru iseniz bu tehdid(ettiğiniz azab) ne zaman (gelecek)? diyorlar.'),
(3231, 27, 72, 'De ki: "Belki de acele ettiğiniz(azab)ın bir kısmı ardınıza takılmıştır, bile."'),
(3232, 27, 73, 'Şüphesiz Rabbin, insanlara karşı lutuf sahibidir, fakat çokları şükretmezler.'),
(3233, 27, 74, 'Ve Rabbin elbette onların göğüslerinin gizlediğini de, açığa vurduklarını da bilir.'),
(3234, 27, 75, 'Gökte ve yerde gizli hiçbir şey yoktur ki apaçık bir Kitapta olmasın.'),
(3235, 27, 76, 'Bu Kur\'an, İsrail oğullarına, kendilerinin ayrılığa düştükleri şeylerin birçoğunu anlatmaktadır.'),
(3236, 27, 77, 'Ve elbette o, mü\'minlere bir yol gösterici ve rahmettir.'),
(3237, 27, 78, 'Şüphesiz, Rabbin onlar arasında hükmünü verecektir. O, azizdir, hakkiyle bilendir.'),
(3238, 27, 79, 'Allah\'a tevekkül et, çünkü sen apaçık gerçek üzerindesin.'),
(3239, 27, 80, 'Sen ölülere duyuramazsın, arkalarını dönmüş kaçmakta olan sağırlara da çağrıyı işittiremezsin.'),
(3240, 27, 81, 'Ve sen kör(ler)i içine düştükleri sapıklıklardan çıkarıp yola getiremezsin. Sen, ancak ayetlerimize inananlara duyurabilirsin ve onlar derhal müslüman olurlar.'),
(3241, 27, 82, 'O söz, başlarına geldiği zaman, onlara yerden bir Dabbe (canlı) çıkarırız; o, onlara insanların, ayetlerimize inanmadıklarını söyler.'),
(3242, 27, 83, 'O gün her ümmet içinde ayetlerimizi yalanlayanlardan bir cemaat toplarız. Onlar hep bir araya getirilip tutuklanarak (ilahi huzura) sevk edilirler.'),
(3243, 27, 84, '(Divanına) Geldiklerinde (Allah onlara) der: "Ayetlerimi anlamadığınız halde yalanladınız mı? Yoksa ne yaptınız?"'),
(3244, 27, 85, 'Zulmetmeleri yüzünden o (azab) karar(ı) başlarına gelmiştir, artık konuşmazlar.'),
(3245, 27, 86, 'Görmediler mi, biz geceyi, içinde istirahat etmeleri için yarattık, gündüzü de aydınlık yaptık. Şüphesiz bunda inanan bir kavim için ayetler vardır.'),
(3246, 27, 87, 'Sur\'a üfleneceği gün, Allah\'ın diledikleri dışında, göklerde ve yerde bulunan kimselerin hepsi, korku içinde kalır (bayılır). Hepsi boyun bükerek O\'na gelirler.'),
(3247, 27, 88, 'Görüp de donuk sandığın dağlar, bulutun yürümesi gibi yürümektedir. (Bu,) Her şeyi gayet iyi yapan Allah\'ın yapısıdır. Doğrusu O, yaptıklarınızı haber almaktadır.'),
(3248, 27, 89, 'Kim iyilik getirirse ona, ondan daha hayırlısı vardır. Ve onlar o gün korkudan uzak, güven içindedirler.'),
(3249, 27, 90, 'Ve kim kötülük getirirse onların da yüzleri cehenneme yıkılır: "Yaptıklarınızdan başka bir şeyle mi cezalandırılıyorsunuz?" (denilir).'),
(3250, 27, 91, '(De ki): "Ben sadece bu kentin Rabbine kulluk etmekle emrolundum. O, burayı saygıdeğer kıldı ve her şey O\'nundur. Ve bana müslümanlardan olmam emredildi."'),
(3251, 27, 92, 'Ve Kur\'an okumam (emredildi). "İmdi kim yola gelirse kendi yararına yola gelmiş olur ve kim saparsa, de ki: "Ben ancak uyarıcılardanım."'),
(3252, 27, 93, 'Ve de ki: "Allah\'a hamdolsun, O size ayetlerini gösterecek, siz de onları tanıyacaksınız." Rabbin, yaptıklarınızdan gafil değildir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Ta sin mim.'),
(3254, 28, 2, 'Şunlar, o apaçık Kitabın ayetleridir.'),
(3255, 28, 3, 'İnanan bir toplum için Musa ile Fir\'avn\'ın haberinden bir parçayı, gerçek olarak sana okuyacağız:'),
(3256, 28, 4, 'Fir\'avn, orada ululandı (zorbalığa kalktı), halkını çeşitli gruplara böldü. Onlardan bir zümreyi (İsrail oğullarını) eziyor, oğullarını kesiyor, kadınlarını sağ bırakıyordu. Çünkü o, bozgunculardan idi.'),
(3257, 28, 5, 'Biz de istiyorduk ki o yerde ezilenlere lutfedelim, onları önderler yapalım, onları (ötekilerin mülküne) mirasçı kılalım.'),
(3258, 28, 6, 'Ve onları o yerde iktidara getirelim de Fir\'avn\'a, Haman\'a ve askerlerine, onlardan (ezdikleri zümreden) korktukları şeyi gösterelim.'),
(3259, 28, 7, 'Musa\'nın annesine, "O(çocuğu)nu emzir, başına bir şey gelmesinden korkuyorsan (bir sandık içinde) onu suya bırak, korkma, üzülme biz onu tekrar sana geri vereceğiz ve onu elçilerden yapacağız." diye vahyettik.'),
(3260, 28, 8, 'Nihayet onu Fir\'avn ailesi aldı ki, kendilerine bir düşman ve başlarına derd olsun. Gerçekten Fir\'avn, Haman ve askerleri yanılıyorlardı.'),
(3261, 28, 9, 'Fir\'avn\'ın karısı (çocuğu sandıktan çıkarınca): "Bana da, sana da göz bebeği (olacak, çok sevimli bir çocuk). Onu öldürmeyin, belki bize yararı dokunur, ya da onu evlad ediniriz." dedi. (Onu almakla hata ettiklerini) anlamıyorlardı.'),
(3262, 28, 10, 'Musa\'nın annesinin gönlü bomboş sabahladı (meraktan çıldıracak oldu). Eğer biz, (va\'dimize) inananlardan olması için onun kalbini iyice pekiştirmemiş olsaydık, neredeyse işi açığa vuracaktı.'),
(3263, 28, 11, '(Musa\'nın) kızkardeşine "Onun izini takip et," dedi. O da onlar farkına varmadan onu uzaktan gözetledi.'),
(3264, 28, 12, 'Biz daha önce ona, süt verenler(in sütünü emmey)i haram etmiştik. (Hiçbir kadının sütünü emmiyordu. Fir\'avn ve ailesi, çocuğun emeceği bir dadı bulma telaşı içinde idiler. Kızkardeşi uzaktan durumu görünce sokuldu): "Sizin için onun bakımını üstlenecek ve ona öğüt ver(ip onu güzelce eğit)ecek bir aileyi göstereyim mi?" dedi.'),
(3265, 28, 13, 'Böylece biz onu, annesine geri verdik ki gözü aydın olsun, üzülmesin ve Allah\'ın va\'dinin gerçek olduğunu bilsin. Fakat çokları bilmezler.'),
(3266, 28, 14, '(Musa), güçlü çağına erip, olgunlaşınca biz ona hüküm ve ilim verdik. İşte güzel davrananları böyle mükafatlandırırız.'),
(3267, 28, 15, 'Halkının (kendisinden) habersiz olduğu bir sırada şehre girdi, orada biri kendi taraftarlarından, öbürü de düşmanlarından olan iki adamın dövüştüklerini gördü. Kendi taraftarlarından olan, düşmanlarından olana karşı Musa\'dan yardım istedi. Musa da ötekine bir yumruk indirip onun işini bitirdi. (Sonra): "Bu dedi, şeytanın işindendir. O, gerçekten apaçık, şaşırtıcı bir düşmandır."'),
(3268, 28, 16, 'Rabbim, ben nefsime zulmettim, beni bağışla! dedi. (Allah) onu bağışladı. Çünkü O, çok bağışlayan, çok esirgeyendir.'),
(3269, 28, 17, 'Rabbim, dedi, bana lutfettiğin ni\'metler hakkı için artık bir daha suçlulara arka olmayacağım.'),
(3270, 28, 18, 'Şehirde korku içinde (sonucu) gözetleyerek sabahladı. Bir de baktı ki dün kendisinden yardım isteyen (İsrail oğlu), yine kendisine feryaded(ip yardım ist)iyor. Musa, ona: "Belli ki sen bir azgınsın!" dedi.'),
(3271, 28, 19, 'Nihayet (Musa) ikisinin de (kendisinin ve yardım isteyenin) düşmanı olan adamı yakalamak isteyince o dedi ki: "Ey Musa, dün bir canı öldürdüğün gibi şimdi de beni mi öldürmek istiyorsun? Sen yeryüzünde bir zorba olmak istiyorsun, arabuluculardan olmak istemiyorsun."'),
(3272, 28, 20, 'Şehrin öbür ucundan bir adam koşarak geldi. "Ey Musa, dedi, ileri gelenler seni öldürmek için aralarında konuşuyorlar. Sen çık (git), ben sana öğüt verenlerdenim."'),
(3273, 28, 21, '(Musa, etrafı) kollayarak, korka, korka oradan çıktı: "Rabbim, beni şu zalim kavimden kurtar!" dedi.'),
(3274, 28, 22, 'Medyen\'e doğru yönelince: "Umarım ki Rabbim beni doğru yola iletir" dedi.'),
(3275, 28, 23, 'Medyen suyuna varınca o(su)nun başında birçok insanların, (hayvanlarını) suladıklarını gördü. Onların gerisinde de, (diğerlerinin hayvanlarına karışmasın diye hayvanlarını) sudan meneden iki kız buldu. (Musa, onlara): "İşiniz nedir, (niçin hayvanları suya bırakmıyorsunuz)?" dedi. Dediler ki: "Çobanlar sulayıp çekilmeden biz (onların içine sokulup hayvanlarımızı) sulamayız. Babamız da büyük bir ihtiyardır (O gelemez)."'),
(3276, 28, 24, 'Hemen (Musa) onlarınkini de suladı, sonra gölgeye çekildi: "Rabbim, dedi, doğrusu bana indireceğin bir hayra muhtacım, (azıcık azık indir de şu karnımı doyur)."'),
(3277, 28, 25, 'Derken o iki kızdan biri utana utana yürüyerek ona geldi: "Babam seni çağırıyor, bizim için (hayvanları) sulamanın ücretini verecek," dedi. (Musa), o(kızların babaları)na gelip (başından geçen) hikayeyi anlatınca o: "Korkma, o zalim kavimden kurtuldun" dedi.'),
(3278, 28, 26, 'O(kız)lardan biri: "Babacağım, dedi, bunu (çoban) tut işte, çünkü ücretle tuttuklarının en hayırlısı, güçlü, güvenilirdir."'),
(3279, 28, 27, 'O zat, (Musa\'ya) dedi ki: "(Bak), bana sekiz yıl hizmet etmen şartıyle şu iki kızımdan birini sana nikahlamak istiyorum. Eğer (bu süreyi) on(yıl)a tamamlarsan artık o, senin tarafından (bir iyilik)dir. Ben sana zahmet vermek istemem. İnşallah beni iyilerden bulacaksın."'),
(3280, 28, 28, '(Musa) dedi: "Bu, seninle benim aramızda(bir sözleşme)dir. Demek hangi süreyi yerine getirsem, bana düşmanlık yok. Allah dediğimize vekildir."'),
(3281, 28, 29, 'Musa, süreyi bitirip ailesiyle yola çıkınca Tur\'un (sağ) yanında bir ateş gördü. Ailesine dedi ki: "Siz durun, ben bir ateş gördüm, belki ondan size bir haber getiririm, yahut bir ateş koru (getiririm) de ısınırsınız."'),
(3282, 28, 30, 'Oraya gelince o mübarek yerdeki vadinin sağ kıyısındaki ağaçtan kendisine şöyle seslenildi: "Ey Musa, alemlerin Rabbi Allah benim, ben!"'),
(3283, 28, 31, 'Asanı at! (Musa attığı kocaman) asa\'sının küçük bir yılan gibi titreş(ip hareket et)tiğini görünce (korkudan) öyle dönüp kaçtı (ki) arkasına bile bakmadı: "Ey Musa, dön, korkma, sen güvende olanlardansın."'),
(3284, 28, 32, 'Elini koynuna sok, kusursuz olarak bembeyaz çıksın. Ve kanadını korkudan kendine çek. İşte bunlar, Fir\'avn\'a ve onun adamlarına (göstermek için) Rabbinden sana verilen iki delildir. Çünkü onlar yoldan çıkan bir kavim olmuşlardır.'),
(3285, 28, 33, 'Rabbim, dedi, ben onlardan bir kişi öldürmüştüm, beni öldüreceklerinden korkuyorum.'),
(3286, 28, 34, 'Kardeşim Harun, o, dil bakımından benden daha güzel konuşur. Onu da benimle beraber, beni doğrulayan bir yardımcı olarak gönder. Zira ben, beni yalanlayacaklarından korkuyorum.'),
(3287, 28, 35, '(Allah) dedi: "Senin pazunu kardeşinle kuvvetlendireceğiz ve size öyle bir yetki vereceğiz ki, ayetlerimiz sayesinde onlar size asla erişemeycekler. İkiniz ve size uyanlar üstün geleceksiniz!"'),
(3288, 28, 36, 'Musa, onlara açık açık ayetlerimizle gelince: "Bu uydurulmuş bir büyüden başka bir şey değildir. İlk atalarımız arasında böyle bir şey (olduğunu) işitmedik." dediler.'),
(3289, 28, 37, 'Musa, "Rabbim, kimin kendisinin yanından hidayet getirdiğini ve bu (dünya) evin(in) sonun(da güzel sonuc)un kime aidolacağını daha iyi biliyor. Muhakkak ki zalimler iflah olmaz" dedi.'),
(3290, 28, 38, 'Fir\'avn dedi ki: "Ey ileri gelenler, ben sizin için benden başka bir tanrı bilmiyorum, ey Haman, haydi benim için çamurun üzerinde ateş yak(arak tuğla imal et de) bana bir kule yap, belki Musa\'nın tanrısına çıkarım, çünkü ben onu (Musa\'yı) yalancılardan sanıyorum."'),
(3291, 28, 39, 'O (Fir\'avn) ve askerleri yeryüzünde haksız yere büyüklük tasladılar ve kendilerinin bize döndürülmeyeceklerini sandılar.'),
(3292, 28, 40, 'Biz de onu ve askerlerini tuttuk, suya attık; bak, o zalimlerin sonu nasıl oldu!'),
(3293, 28, 41, 'Biz onları ateşe çağıran önderler yaptık. Kıyamet günü asla yardım olunmazlar.'),
(3294, 28, 42, 'Bu dünya hayatında biz onların ardına bir la\'net taktık (daima la\'netle anılacaklardır). Kıyamet günü ise onlar çirkinleştirilenlerdendir.'),
(3295, 28, 43, 'Andolsun biz, ilk nesilleri (Nuh, Hud, Salih ve Lut kavimlerini) helak ettikten sonra Musa\'ya, insanların gönül gözlerini aydınlatacak nur ve onlara yol gösterici olarak Kitabı verdik, belki düşünür, öğüt alırlar diye.'),
(3296, 28, 44, 'Musa\'ya o işi yaptığımız (yani kendisine bildirmek istediğimiz işi ona vahyettiğimiz) vakit sen (Mukaddes Vadinin) batı tarafında değildin, (o hadiseyi) görenlerden de değildin.'),
(3297, 28, 45, 'Fakat biz (Musa\'dan sonra) birçok nesiller yarattık da onların üzerinden uzun zamanlar geçti. Sen Medyen halkı arasında oturmuş değildin ki (orada olanları görüp öğrenesin de) ayetlerimizi bunlara okuyasın. (Bu, bir yerden görme, öğrenme ile değildir, fakat) Biz seni elçi olarak gönderdik (ve bu olayları sana vahyettik).'),
(3298, 28, 46, '(Musa\'ya) ünlediğimiz zaman sen Tur\'un yanında değildin. Fakat Rabbinden bir rahmet olarak (orada geçenleri sana bildirdik) ki senden önce kendilerine bir uyarıcı gelmemiş olan toplumu uyarasın; belki düşünüp öğüt alırlar.'),
(3299, 28, 47, 'Kendi elleriyle yaptıkları (günahları) yüzünden başlarına bir felaket geldiği zaman: "Ey Rabbimiz, bize bir elçi gönderseydin de ayetlerine uyup mü\'minlerden olsaydık." diyecek olmasalardı (seni göndermezdik. Bu bahanelerine fırsat vermemek için senigönderdik).'),
(3300, 28, 48, 'Fakat onlara katımızdan hak gelince: "Musa\'ya verilenin eşi, buna da verilmeli değil miydi?" dediler. Daha önce Musa\'ya verileni de inkar etmemişler miydi? "Birbirine destek olan iki büyü", dediler. "Biz hepsini inkar ederiz", dediler.'),
(3301, 28, 49, 'De ki: "Eğer doğru iseniz, Allah katından bu ikisinden (yani Musa\'ya ve bana inen Kitaplardan) daha doğru bir Kitap getirin de ben ona uyayım."'),
(3302, 28, 50, 'Eğer sana cevap veremezlerse bil ki onlar, keyiflerine uyuyorlar. Allah\'tan bir yol gösterici olmadan, yalnız kendi keyfine uyandan daha sapık kim olabilir? Muhakkak ki Allah, zalim kavmi doğru yola iletmez.'),
(3303, 28, 51, 'Andolsun biz, düşünüp öğüt alsınlar diye onlar için sözü(müzü) birbirine bitiştirdik (ardı ardına gerçeği kanıtlayan ayetler gönderdik).'),
(3304, 28, 52, 'Bundan önce kendilerine Kitap verdiklerimiz, bu(Kur\'a)n\'a inanırlar.'),
(3305, 28, 53, 'Onlara (Kur\'an) okunduğu zaman: "Ona inandık, o, Rabbimizden gelen gerçektir... Zaten biz ondan önce de müslümanlar idik." derler.'),
(3306, 28, 54, 'İşte onlara, sabretmelerinden ötürü mükafatları iki kez verilir; onlar kötülüğü iyilikle savarlar ve kendilerine verdiğimiz rızıktan (hayır yoluna) harcarlar.'),
(3307, 28, 55, 'Boş söz işittikleri zaman ondan yüz çevirirler ve: "Bizim işlerimiz bize, sizin işleriniz size. Size selam olsun (haydi hoşça kalın), biz cahiller(le sohbet etmey)i istemeyiz" derler.'),
(3308, 28, 56, '(Ey Muhammed), sen, sevdiğini doğru yola iletemezsin, fakat Allah, dilediğini doğru yola iletir. O, yola gelecek olanları daha iyi bilir.'),
(3309, 28, 57, 'Dediler ki: "Biz seninle beraber doğru yola gelirsek yurdumuzdan atılırız." Biz onlara kendi katımızdan bir rızık olarak, her şeyin ürünlerinin toplanıp getirildiği, güvenli, dokunulmaz bir mekan vermedikmi? Fakat çokları bilmezler.'),
(3310, 28, 58, 'Biz refah içinde şımarmış nice kenti helak ettik. İşte şunlar, onların meskenleri, onlardan sonra oralarda pek az oturuldu. Onlara hep biz varis olduk (hepsi bize kaldı).'),
(3311, 28, 59, 'Rabbin, Anakent(olan Mekke)de onlara ayetlerimizi okuyan bir elçi göndermedikçe ülkeleri helak edici değildir. Ve biz, halkı zalim olmadan ülkeleri helak ediciler değiliz.'),
(3312, 28, 60, 'Size verilen her şey, dünya hayatının geçimi ve süsüdür. Allah\'ın yanında olan ise daha hayırlı ve daha kalıcıdır. Aklınızı kullanmıyor musunuz?'),
(3313, 28, 61, 'İmdi kendisine güzel bir söz verdiğimiz ve muhakkak o(söz verile)ne kavuşacak olan kimse; sırf kendisine dünya hayatının geçici zevkini yaşattığımız ve sonra kıyamet günü (yakalanıp) getirileceklerden olan insan gibi midir?'),
(3314, 28, 62, 'O gün (Allah) onlara seslenerek: "Benim ortaklarım (olduklarını) sandığınız şeyler nerede?" der.'),
(3315, 28, 63, '(Azab) söz(ü) üzerlerine hak olanlar: "Rabbimiz, azdırdıklarımız şunlar. Kendimiz azdığımız gibi onları da azdırdık. (Biz azdık, onlar da bize uydular. Onların yaptıklarından) uzak olduğumuzu, (bu hususta bizim suçumuz olmadığını) sana arz ederiz. Zaten onlar bize tapmıyorlardı (kendi arzularına tapıyorlardı)." derler.'),
(3316, 28, 64, '(Allah tarafından) onlara: "(Bana), koştuğunuz ortakları çağırın!" denir. Onları çağırırlar. Fakat (çağırılanlar), bunların çağrısına cevap vermezler ve (bunlar), karşılarında azabı görürler (sanki çağırdıkları şey, azabın kendisi olmuştur). Ne olurdu (sanki dünyada) yola gelselerdi!'),
(3317, 28, 65, '(Allah) Onlara seslenerek: "Elçilere ne cevap verdiniz?" dediği gün,'),
(3318, 28, 66, 'O gün haberler, onlara kör olmuştur (yani sözler sanki kör olmuştur, hiçbir söz gelip onların ağızlarını bulamaz, yanıt verecek bir tek kelime bulamazlar) onlar, birbirlerine de soramazlar.'),
(3319, 28, 67, 'Ama kim tevbe eder, inanır ve iyi iş yaparsa, o kurtuluşa erenlerden olabilir.'),
(3320, 28, 68, 'Rabbin, dilediğini yaratır ve seçer. Seçim, onlara ait değildir. Allah, onların ortak koştukları şeylerden uzaktır, yücedir.'),
(3321, 28, 69, 'Rabbin, onların göğüslerinin neyi gizleyip neyi açığa vurduğunu bilir.'),
(3322, 28, 70, 'O, kendisinden başka tanrı olmayan Allah\'tır. İlkte de, sonda da (dünyada da, ahirette de) hamd O\'na mahsustur. Hüküm de O\'nundur ve O\'na döndürüleceksiniz.'),
(3323, 28, 71, 'De ki: "Baksanıza, eğer Allah, üzerinize geceyi kıyamet gününe kadar sürekli kılsa Allah\'tan başka size ışık getirecek tanrı kimdir? (Söyleyin), işitmiyor musunuz?"'),
(3324, 28, 72, 'De ki: "Baksanıza, eğer Allah, üzerinize gündüzü, kıyamet gününe kadar sürekli kılsa, Allah\'tan başka, size dinleneceğiniz geceyi getirecek tanrı kimdir? Görmüyor musunuz?"'),
(3325, 28, 73, 'Rahmetinden dolayı sizin için geceyi ve gündüzü var etti ki, geceleyin dinlenesiniz ve (gündüzün) Allah\'ın lutfunu arayasınız ve (Allah\'ın ni\'metine) şükredesiniz.'),
(3326, 28, 74, 'O gün onlara seslenerek: "Ortaklarım sandığınız şeyler nerede?" der.'),
(3327, 28, 75, 'Her ümmetten bir şahid çıkarırız: "Delilinizi getirin!" deriz. Gerçeğin Allah\'a aidolduğunu bilirler ve uydurdukları şeyler kendilerinden sapıp gider.'),
(3328, 28, 76, 'Karun, Musa\'nın kavminden idi. Onlara karşı azgınlık etti. Biz kendisine öyle hazineler vermiştik ki onun (hazinelerinin) anahtarlarını (taşımak), güçlü bir topluluğa ağır geliyordu. Kavmi ona demişti ki: "Şımarma, Allah, şımarıkları sevmez."'),
(3329, 28, 77, 'Allah\'ın sana verdiği (bu servet) içinde ahiret yurdunu ara, dünyadan da nasibini unutma, Allah sana nasıl iyilik ettiyse sen de öyle iyilik et, yeryüzünde bozgunculuk (etmeyi) isteme, çünkü Allah bozguncuları sevmez.'),
(3330, 28, 78, 'Bu (servet) bende bulunan bir bilgi sayesinde bana verildi dedi. Bilmedi mi ki Allah, kendisinden önceki kuşaklar arasıda kendisinden daha güçlü ve daha çok cemaati bulunan nice kimseleri helak etmiştir? Suçlulara günahlarından sorulmaz.'),
(3331, 28, 79, '(Karun) süsü, (debdebesi) içinde kavminin karşısına çıktı. dünya hayatını isteyenler: "Keşke Karun\'a verilenin bir benzeri de bize verilseydi, dediler, gerçekten onun büyük şansı var!"'),
(3332, 28, 80, 'Kendilerine bilgi verilmiş olanlar ise: "Yazık size, dediler, inanan ve iyi iş yapan kimse için Allah\'ın sevabı daha hayırlıdır. Buna ancak sabredenler kavuşturulur."'),
(3333, 28, 81, 'Nihayet onu da, evini barkını da yere batırdık. Allah\'a karşı ona yardım edecek bir topluluğu olmadı. Kendi kendini (savunup) kurtaranlardan da değildi.'),
(3334, 28, 82, 'Dün onun yerinde olmayı isteyenler: "Vay, demek Allah kullarından dilediğine rızkı açar ve kısar. Allah bize lutfetmiş olmasaydı, bizi de yere batırırdı. Demek gerçekten kafirler iflah olmaz!" demeğe başladılar.'),
(3335, 28, 83, 'İşte ahiret yurdu: Onu yeryüzünde böbürlenmek ve bozgunculuk yapmak istemeyenlere veririz. (Güzel) sonuç, (günahlardan) sakınanlarındır.'),
(3336, 28, 84, 'Kim bir iyilik getirirse ona ondan daha güzeli vardır. Kim kötülük getirirse, kötülükleri yapanlar, ancak yaptıkları (kötülük) kadar cezalanırlar.'),
(3337, 28, 85, 'Kur\'an\'ı sana (indiren ve) gerekli kılan (Allah), elbette seni varılacak yere döndürecektir. De ki: "Rabbim kimin hidayet getirdiğini ve kimin apaçık bir sapıklık içinde bulunduğunu bilir."'),
(3338, 28, 86, 'Sen, o Kitabın, senin kalbine bırakılacağını ummazdın. Ancak Rabbinden bir rahmet olarak (Kitap senin kalbine bırakıldı). O halde kafirlere arka olma.'),
(3339, 28, 87, 'Ve Allah\'ın, ayetleri sana indirildikten sonra sakın seni onlardan alıkoymasınlar. Rabbine da\'vet et, ortak koşanlardan olma.'),
(3340, 28, 88, 'Allah ile beraber başka bir tanrıya yalvarma. O\'ndan başka tanrı yoktur. O\'nun yüzü(zatı)ndan başka herşey helak olacaktır. Hüküm O\'nundur ve O\'na döndürüleceksiniz.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Elif lam mim.'),
(3342, 29, 2, 'İnsanlar yalnız "inandık" demekle, hiç sınanmadan bırakılacaklarını mı sandılar?'),
(3343, 29, 3, 'Andolsun biz, onlardan öncekilerini sınadık. Elbette Allah (sınayıp) doğruları bilecek, yalancıları bilecektir.'),
(3344, 29, 4, 'Yoksa kötülükleri yapanlar, bizi geçeceklerini (bizim, kendilerine yetişip onları cezalandıramayacağımızı) mı sandılar? Ne kötü hüküm veriyorlar.'),
(3345, 29, 5, 'Kim Allah ile buluşmayı umarsa; Allah\'ın (buluşma) süresi gelmektedir. O, işitendir, bilendir.'),
(3346, 29, 6, 'Kim cihad ederse ancak kendi yararına cihad eder. Allah, alemlerden zengindir. (Kimsenin cihadına muhtaç değildir. İnsanların cihad ve ibadetleri kendi menfaatleri içindir).'),
(3347, 29, 7, 'İnanıp iyi işler yapanların, mutlaka kötülüklerini örteceğiz ve onları, yaptıklarının en güzeliyle mükafatlandıracağız.'),
(3348, 29, 8, 'Biz insana ana babasına iyilik etmeyi tavsiye ettik. Eğer onlar seni, (gerçekliği) hakkında hiçbir bilgin olmayan bir şeyi bana ortak koşman için zorlarlarsa (bu hususta) onlara ita\'at etme. Dönüşünüz banadır. O zaman size yaptıklarınızı haber veririm.'),
(3349, 29, 9, 'İnanıp iyi işler yapanları, salihler arasına sokarız.'),
(3350, 29, 10, 'İnsanlardan kimi vardır ki "Allah\'a inandık." der, fakat Allah uğrunda kendisine eziyet edilince insanların işkencesini, Allah\'ın azabı gibi sayar. Ama Rabbinden (sana) bir yardım gelse, andolsun: "Biz de sizinle beraberdik," derler. Allah, alemlerin göğüslerinde bulunan (düşünceler)i daha iyi bilmez mi?'),
(3351, 29, 11, 'Allah, elbette inananları da bilir, ve elbette iki yüzlüleri de bilir.'),
(3352, 29, 12, 'İnkar edenler, inananlara: "Siz bizim yolumuza uyun. Sizin hatalarınızı biz taşırız" dediler. Oysa kendileri, onların hatalarından hiçbir şey taşıyacak değillerdir. Onlar tamamen yalancıdırlar.'),
(3353, 29, 13, 'Onlar, hem kendi yüklerini, hem de kendi yükleriyle beraber başka yükleri (başkalarını kandırıp saptırmalarının vebalini) taşıyacaklar ve elbette uydurdukları şeylerden kıyamet gününde sorguya çekileceklerdir.'),
(3354, 29, 14, 'Andolsun biz, Nuh\'u kavmine gönderdik, onların arasında bin seneden elli yıl eksik kaldı (öğüt verdi, dinlemediler), sonunda haksızlık etmekte olan insanları Tufan yakaladı.'),
(3355, 29, 15, 'Onu ve gemi halkını kurtardık ve o gemiyi alemlere bir ibret yaptık.'),
(3356, 29, 16, 'İbrahim\'i de (gönderdik). Kavmine dedi ki: "Allah\'a kulluk edin, O\'ndan korkun, bilirseniz bu, sizin için daha hayırlıdır."'),
(3357, 29, 17, 'Siz Allah\'tan başka bir takım putlara tapıyorsunuz, yalan şeyler uyduruyorsunuz. Sizin Allah\'tan başka taptıklarınız, size rızık vermezler. Siz rızkı Allah\'ın yanında arayın, O\'na tapın ve O\'na şükredin. O\'na döndürüleceksiniz.'),
(3358, 29, 18, 'Eğer yalanlarsanız, sizden önceki ümmetler de yalanlamışlardı. Elçiye düşen, yalnız açıkça duyurmaktıır.'),
(3359, 29, 19, 'Görmediler mi Allah nasıl yaratmayı başlatıyor, sonra onu iade ediyor (dönüp yeniden yaratıyor). Bu, Allah\'a göre kolaydır.'),
(3360, 29, 20, 'De ki: "Yeryüzünde gezin, bakın yaratmağa nasıl başladı, sonra Allah, son yaratmayı da yapacaktır. Çünkü Allah, her şeyi yapabilendir.'),
(3361, 29, 21, 'Dilediğine azabeder, dilediğine acır ve hepiniz O\'na çevrilirsiniz.'),
(3362, 29, 22, 'Siz, ne yerde, ne de gökte Allah\'(ın yapacağı iş)i engelleyemezsiniz. Sizin Allah\'tan başka ne bir koruyucunuz, ne de bir yardımcınız vardır.'),
(3363, 29, 23, 'Allah\'ın ayetlerini ve O\'nunla buluşmayı inkar edenler, işte onlar benim rahmetimden ümidi kesmişlerdir ve onlar için acı bir azab vardır.'),
(3364, 29, 24, 'Kavminin (İbrahim\'e) cevabı, sadece: "Onu öldürün, yahut yakın!" demeleri oldu. Allah onu ateşten kurtardı. Şüphesiz bunda inanan bir toplum için ibretler vardır.'),
(3365, 29, 25, '(İbrahim kavmine) dedi ki: "Siz dünya hayatında birbirinizi sevmek için Allah\'ı bırakıp birtakım putlar edindiniz. (O putlara tapmanız, dünyada aranızda bir sevgi meydana gelmesine sebep olsa bile) daha sonra kıyamet gününde birbirinizi inkar eder ve birbirinizi la\'netlersiniz. Varacağınız yer de ateştir ve hiçbir yardımcınız da yoktur.'),
(3366, 29, 26, 'Bunun üzerine Lut ona inandı ve (İbrahim, kavmine) dedi ki: "Rabbim(e ibadet edeceğim yer)e göç edeceğim. Kuşkusuz O, galibdir, hüküm ve hikmet sahibidir."'),
(3367, 29, 27, 'Biz ona İshak\'ı ve (torunu) Ya\'kub\'u armağan ettik. Onun nesli içine peygamberlik ve Kitap koyduk. Ona dünyada (yaptığı güzel işin) karşılığını verdik. Şüphesiz o, ahirette de iyilerdendir.'),
(3368, 29, 28, 'Lut\'u da (gönderdik). Kavmine dedi ki: "Siz, sizden önce alemlerden hiç kimsenin yapmadığı bir fuhşa gidiyorsunuz."'),
(3369, 29, 29, 'Siz (kadınları bırakıp) erkeklere gidiyorsunuz, yol kesiyorsunuz ve toplantılarınızda edepsizce şeyler yapıyorsunuz ha?.. Kavmi\'nin cevabı, sadece: "Eğer doğrulardan isen, haydi Allah\'ın azabını getir!" demeleri oldu.'),
(3370, 29, 30, '(Lut): "Rabbim, şu bozguncu kavme karşı bana yardım et," dedi.'),
(3371, 29, 31, 'Elçilerimiz İbrahim\'e (oğlu olacağına dair) müjdeyi getirdikleri zaman dediler ki: "Biz şu (Sodom) kenti(ni)n halkını helak edeceğiz. Çünkü oranın halkı zalim oldular."'),
(3372, 29, 32, '(İbrahim) dedi: "Ama orada Lut var?" dediler: "Biz orada kimin bulunduğunu daha iyi biliriz. Onu ve ailesini kurtaracağız. Yalnız karısı (azabda) kalacaklardandır."'),
(3373, 29, 33, 'Elçilerimiz Lut\'a gelince (Lut) onlar yüzünden fenalaştı ve onlar hakkında arşını daraldı. (Melekler): "Korkma üzülme, dediler, biz seni ve aileni kurtaracağız, yalnız karın, kalacaklardan olmuştur."'),
(3374, 29, 34, 'Biz yoldan çıkan şu ülke halkının üstüne gökten bir azab indireceğiz.'),
(3375, 29, 35, 'Andolsun biz, aklını kullanan bir toplum için ondan, (harab ettiğimiz o ülkeden) açık bir işaret bırakmışızdır.'),
(3376, 29, 36, 'Medyen\'e de kardeşleri Şuayb\'i (gönderdik): "Ey kavmim, dedi, Allah\'a kuluk edin, ahiret gününü umun, yeryüzünde karışıklık çıkarıp bozgunculuk yapmayın!"'),
(3377, 29, 37, 'Onu yalanladılar, bu yüzden onları (o müthiş) deprem yakaladı, yurtlarında diz üstü çöküp kaldılar.'),
(3378, 29, 38, 'Ad ve Semud\'u da (helak ettik). Bu, oturdukları yerlerden size belli olmaktadır. Şeytan onlara yaptıkları işleri süsleyip onları yoldan çıkardı. Oysa bakıp ibret alabilirlerdi (ama almadılar).'),
(3379, 29, 39, 'Kaarun\'u, Fir\'avn\'ı, Haman\'ı da (helak ettik). Andolsun, Musa onlara açık kanıtlar getirdi, fakat onlar o yerde büyüklük tasla(yıp ayetlerimizi kabule tenezzül etme)diler. Ama geçip gidecek, (elimizden kurtulacak) değillerdi.'),
(3380, 29, 40, 'Nitekim hepsini günahiyle yakaladık. Onlardan kiminin üstüne taş yağdıran bir fırtına gönderdik, kimini korkunç ses yakaladı, kimini yere batırdık, kimini de boğduk. Allah onlara zulmedecek değildi; fakat onlar, kendi kendilerine zulmediyorlardı.'),
(3381, 29, 41, 'Allah\'tan başka veliler edinenler, bir ev edinen örümceğe benzerler. Evlerin en gevşeği örümcek evidir, keşke bilselerdi.'),
(3382, 29, 42, 'Allah, onların kendisinden başka ne gibi şeylere yalvardıklarını bilir, O üstündür, hüküm ve hikmet sahibidir.'),
(3383, 29, 43, 'Biz bu misalleri insanlara anlatıyoruz ama onları, bilenlerden başkası düşünüp anlamaz.'),
(3384, 29, 44, 'Allah, gökleri ve yeri hak ile yarattı. Şüphesiz bunda inananlar için bir ibret vardır.'),
(3385, 29, 45, 'Kitaptan sana vahyedileni oku ve namazı da kıl. Çünkü namaz kötü ve iğrenç şeylerden men\'eder. Elbette Allah\'ı anmak, en büyük(ibadet)tir. Allah, ne yaptığınızı bilir.'),
(3386, 29, 46, 'Kitap ehliyle, Ğhaksızlık edenleri dışındaĞ en güzel tarzda tartışın ve deyin ki: "Bize indirilene de size indirilene de inandık. Tanrımız ve tanrınız birdir, biz de O\'na teslim olanlarız.'),
(3387, 29, 47, 'İşte böylece Kitabı sana da indirdik. Kendilerine Kitabı verdiklerimiz, ona inanırlar: Şunlardan (şu Araplardan) da ona inananlar vardır. Ayetlerimizi, kafirlerden başkası inkar etmez.'),
(3388, 29, 48, '(Ey Muhammed) Sen bundan önce bir Kitap okumuyordun, elinle de onu yazmıyorsun. Öyle olsaydı o zaman (Allah\'ın sözlerini boşa çıkarmaya çalışan) iptalciler, kuşkulanırlardı.'),
(3389, 29, 49, 'Hayır, o (sana vahyedilenler) kendilerine bilgi verilmiş olanların göğüslerinde bulunan açık açık ayetlerdir. Bizim ayetlerimizi, zalimlerden başkası inkar etmez.'),
(3390, 29, 50, 'Dediler ki: "Ona Rabbinden ayetler indirilmeli değil miydi?" De ki: "Ayetler (mu\'cizeler) Allah\'ın yanındadır. Ben ancak apaçık bir uyarıcıyım."'),
(3391, 29, 51, 'Kendilerine okunan Kitabı sana indirmemiz, onlara yetmedi mi? Şüphesiz inanan bir toplum için bunda bir rahmet ve öğüt vardır.'),
(3392, 29, 52, 'De ki: "Benimle sizin aranızda şahid olarak Allah yeter. O, göklerde ve yerde olanları bilir. Batıla inanıp Allah\'a karşı nankörlük edenler, işte ziyana uğrayacaklar onlardır."'),
(3393, 29, 53, 'Senden azabı çabuk istiyorlar. Eğer (azab için) belirtilmiş bir süre olmasaydı, onlara hemen azab gelirdi. Fakat hiç farkına varmadıkları bir sırada o, kendilerine ansızın gelecektir.'),
(3394, 29, 54, 'Cehennem o nankörleri kuşatmış iken, onlar hala senden azabı çabuk istiyorlar.'),
(3395, 29, 55, 'O gün azab, onları üstlerinden, ayaklarının altından örter ve (Allah onlara): "Yaptığınız işleri tadın!" der.'),
(3396, 29, 56, 'Ey inanan kullarım, benim arzım geniştir, bana kulluk edin.'),
(3397, 29, 57, 'Her can, ölümü tadacaktır. Sonra bize döndürüleceksiniz.'),
(3398, 29, 58, 'İnanıp iyi işler yapanları, cennette, altlarından ırmaklar akan yüksek odalara yerleştiririz. Orada ebedi kalırlar. Çalışanların ücreti ne güzeldir!'),
(3399, 29, 59, 'Onlar ki sabrettiler ve Rabblerine dayanmaktadırlar'),
(3400, 29, 60, 'Nice canlı var ki rızkını taşıyamaz, onları da sizi de Allah besler. O, işitendir, bilendir.'),
(3401, 29, 61, 'Andolsun, onlara: "Gökleri ve yeri kim yarattı, güneşi ve ayı kim (sizin yararınıza) boyun eğdirdi?" desen; "Allah", derler. O halde nasıl Allah\'ın (birliğinden) döndürülüyorsunuz?'),
(3402, 29, 62, 'Allah kullarından dilediğine rızkı açar da, kısar da. Şüphesiz Allah, her şeyi bilendir.'),
(3403, 29, 63, 'Onlara: "Kim gökten suyu indirip de ölmüş olan yeri onunla diriltti?" diye sorsan; "Allah", derler. De ki: "Hamd (övgü), Allah\'a layıktır." Doğrusu çokları düşünmezler.'),
(3404, 29, 64, 'Bu dünya hayatı eğlence ve oyundan başka bir şey değildir. Ahiret yurdu, işte asıl hayat odur (asıl yaşanacak yer orasıdır), keşke bilselerdi!'),
(3405, 29, 65, 'Gemiye bindikleri zaman, dini yalnız Allah\'a halis kılarak O\'na yalvarırlar. Fakat (Allah) onları salimen karaya çıkarınca hemen (O\'na) ortak koşarlar.'),
(3406, 29, 66, 'Ki kendilerine verdiğimiz(ni\'metler)e nankörlük etsinler ve (şu geçici hayatta) zevk içinde yaşasınlar. Ama yakında (gerçeği) bileceklerdir.'),
(3407, 29, 67, 'Görmediler mi çevrelerinde insanlar kaçırılırken biz (kendi şehirleri Mekke\'yi), güvenli, dokunulmaz bir bölge yaptık? Hala batıla inanıp Allah\'ın ni\'metine nankörlük mü ediyorlar?'),
(3408, 29, 68, 'Uydurduğu yalanı Allah\'ın üzerine atan veya kendisine gelen gerçeği yalanlayandan daha zalim kimdir? Kafirlerin durağı cehennemde değil midir?'),
(3409, 29, 69, 'Ama biz(im uğrumuz)da cihad edenleri biz, elbette yollarımıza iletiriz. Muhakkak ki Allah, iyilik edenlerle beraberdir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Elif lam mim.'),
(3411, 30, 2, 'Rum(lar), yenildi:'),
(3412, 30, 3, '(Bölgeye) En yakın bir yerde. Onlar (bu) yenilgilerinden sonra yeneceklerdir;'),
(3413, 30, 4, 'Birkaç (-9) yıl içinde. (Onların) Bu(yenilgileri)nden önce de, sonra da emir Allah\'ındır (ferman O\'nundur). O gün mü\'minler sevinir(ler):'),
(3414, 30, 5, 'Allah\'ın yardımıyle. (Allah) Dilediğine yardım eder. O, galiptir, esirgeyendir.'),
(3415, 30, 6, '(Bu,) Allah\'ın va\'didir. Allah va\'dinden caymaz; fakat insanların çoğu bilmezler.'),
(3416, 30, 7, 'Onlar, sadece şu yakın hayatın dış yüzünü bilirler; ahiretten ise onlar tamamen gafildirler.'),
(3417, 30, 8, 'Kendi içlerinde hiç düşünmediler mi ki Allah, göklerde, yerde ve bu ikisi arasında bulunan her şeyi ancak hak olarak ve belirtilmiş bir süre ile yaratmıştır? İnsanlardan çoğu, Rabblerine kavuşmayı inkar etmektedirler.'),
(3418, 30, 9, 'Yeryüzünde gezmediler mi ki, kendilerinden öncekilerin sonunun nasıl olduğuna baksınlar. Onlar, kendilerinden daha güçlü idiler; (sular, madenler çıkarmak, ekin ekmek, ağaç dikmek için) toprağı (kazmış) alt-üst etmişler ve onu, bunların imar ettiklerinden daha çok imar etmişlerdi. Onlara da elçileri, deliller getirmişti. Allah onlara zulmedecek değildi. Fakat onlar, kendi kendilerine zulmediyorlardı.'),
(3419, 30, 10, 'Sonra kötülük edenlerin sonu çok kötü oldu. Çünkü Allah\'ın ayetlerini yalanladılar. Ve onlarla alay ediyorlardı.'),
(3420, 30, 11, 'Allah, yaratmağa başlar, sonra onu çevirip yeniden yapar; sonra O\'na döndürülürsünüz.'),
(3421, 30, 12, '(Duruşma) Sa\'at(i) başladığı gün, suçlular (umutsuzluk içinde) susarlar.'),
(3422, 30, 13, '(Allah\'a) ortak(koştukları put)larından da kendilerine hiçbir şefa\'atçi çıkmaz. O zaman ortaklarını inkar ederler.'),
(3423, 30, 14, 'O sa\'at başladığı gün, o gün (inananlar ve inanmayanlar) ayrılırlar:'),
(3424, 30, 15, 'İnanıp iyi işler yapanlar, onlar (çiçekli, ırmaklı) bir bahçe içinde neş\'elendirilirler.'),
(3425, 30, 16, 'Fakat inkar edip ayetlerimizi ve ahiret buluşmasını yalanlayanlara gelince, onlar da (tutuklanıp) azabın içine getirilirler.'),
(3426, 30, 17, 'Öyle ise akşama girdiğiniz zaman da, sabaha erdiğiniz zaman da tesbih Allah\'ındır (O\'nun şanının yüceliği anılır).'),
(3427, 30, 18, 'Göklerde ve yerde, günün sonunda da, öğleye erdiğiniz zaman da hamd, O\'na mahsustur.'),
(3428, 30, 19, '(Allah), ölüden diri çıkarır, diriden ölü çıkarır; yeri ölümünden sonra diriltir. İşte siz de (bedensel hayata) öyle çıkarılacaksınız.'),
(3429, 30, 20, 'O\'nun ayetlerinden (gücünün işaretlerinden) biri sizi topraktan yaratmasıdır. Sonra siz, (yeryüzüne) yayılan insan(lar) oluverdiniz.'),
(3430, 30, 21, 'O\'nun ayetlerinden biri de, size nefislerinizden, sakinleşeceğiniz eşler yaratması ve aranıza sevgi ve acıma koymasıdır. Şüphesiz bunda, düşünen bir toplum için ibretler vardır.'),
(3431, 30, 22, 'O\'nun ayetlerinden biri de göklerin ve yerin yaratılması, dillerinizin ve renklerinizin değişik olmasıdır. Şüphesiz bunda, bilenler için ibretler vardır.'),
(3432, 30, 23, 'O\'nun ayetlerinden biri de, geceleyin ve gündüzün uyumanız ve O\'nun lutfundan (nasibinizi) aramanızdır. Şüphesiz bunda, işiten bir toplum için ibretler vardır.'),
(3433, 30, 24, 'O\'nun ayetlerinden biri de, size, korku ve umut vermek için şimşeği göstermesi, gökten bir su indirip onunla ölümünden sonra yeri diriltmesidir. Şüphesiz bunda, aklını kullanan bir toplum için ibretler vardır.'),
(3434, 30, 25, 'O\'nun ayetlerinden biri de göğün ve yerin, kendisinin buyruğuyla durmasıdır. Sonra sizi yerden bir tek da\'vetle çağırdığı zaman bir de bakarsınız ki çıkıyorsunuz.'),
(3435, 30, 26, 'Göklerde ve yerde bulunan kimseler hep O\'nundur, hepsi O\'na ita\'at etmektedirler.'),
(3436, 30, 27, 'Yaratmağa başlayan O\'dur. Sonra onu çevirip yeniden yapar. Bu, O\'na daha kolaydır. Göklerde ve yerde en yüce durum O\'nundur (En büyük kudret ve şeref misali O\'dur). O, üstündür, hüküm ve hikmet sahibidir.'),
(3437, 30, 28, 'Size kendinizden bir misal verdi: (Bakın) size verdiğimiz rızıklarda; sizin ellerinizin altında bulunan(köleler, hizmetçi)lerden sizinle eşit derecede (yönetim hakkına sahip) olan, birbiriniz(in hakkına dokunmak)dan çekindiğiniz gibi onlar(ın hakkına dokunmak)dan da çekindiğiniz ortaklar var mı (ki tutup kendi mülkümüzde, saltanatımızda bize ortaklar atfediyorsunuz, kendi kullarımızı, yaratıklarımızı bize eş koşuyorsunuz)? İşte biz, aklını kullanan bir toplum için ayetleri böyle açıklıyoruz.'),
(3438, 30, 29, 'Hayır, zulmedenler, bilgisizce keyiflerine uydular. Allah\'ın şaşırttığını (Allah\'ın gönderdiği hidayete uymadığı için düştüğü sapıklığında terk ettiği kimseyi) kim yola getirebilir? Onların hiçbir yardımcıları yoktur.'),
(3439, 30, 30, 'Sen yüzünü, Allah\'ı birleyici olarak doğruca dine çevir: Allah\'ın yaratma yasasına (uygun olan dine dön) ki, insanları ona göre yaratmıştır. Allah\'ın yaratması değiştirilemez. İşte doğru din odur. Fakat insanların çoğu bilmezler.'),
(3440, 30, 31, 'Yalnız O\'na yönelin ve O\'ndan korkun; namazı kılın ve (Allah\'a) ortak koşanlardan olmayın.'),
(3441, 30, 32, '(O ortak koşanlardan olmayın ki onlar) Dinlerini parçaladılar ve bölük bölük oldular. Her parti kendi yanındakiyle sevin(ip övün)mektedir.'),
(3442, 30, 33, 'İnsanlara bir zarar dokundu mu, Rablerine yönelerek O\'na yalvarırlar. Sonra (Rableri), onlara kendinden bir rahmet taddırınca, hemen onlardan bir grup, Rablerine ortak koşarlar.'),
(3443, 30, 34, '(Böyle yaparlar) Ki kendilerine verdiğimiz(ni\'met)e karşı nankörlük etsinler. Şimdi zevk içinde yaşayın bakalım, yakında (sonunuzun ne olduğunu) bileceksiniz.'),
(3444, 30, 35, 'Yoksa onlara bir delil indirmişiz de o mu (Allah\'a) ortak koşmalarını söylüyor?'),
(3445, 30, 36, 'Biz insanlara bir rahmet taddırdığımız zaman onunla sevinirler. Elleriyle yapıp öne sürdükleri(işleri)nden dolayı onlara bir kötülük erişince de, derhal umutsuzluğa düşerler.'),
(3446, 30, 37, 'Görmediler mi, Allah dilediğine rızkı genişletiyor da, daraltıyor da. Şüphesiz inanan bir toplum için bunda ibretler vardır.'),
(3447, 30, 38, 'Akrabaya, yoksula, yolcuya (zekat ve sadakadan) hakkını ver. Allah\'ın yüzünü (rızasını) isteyenler için bu, daha hayırlıdır ve onlar başarıya erenlerdir.'),
(3448, 30, 39, 'İnsanların malları içinde, artması için verdiğiniz riba, Allah katında artmaz. Ama Allah\'ın yüzünü (O\'nun rızasını) isteyerek verdiğiniz zekat(a gelince); işte (onu verenler sevaplarını ve mallarını) kat kat artıranlardır.'),
(3449, 30, 40, 'Allah sizi yarattı, sonra besledi sonra öldürüyor, sonra diriltiyor. Ortaklarınız içinde bunlardan birini yapan var mı? O, onların ortak koştukları şeylerden uzak ve yücedir.'),
(3450, 30, 41, 'İnsanların elleriyle kazandıkları (günahları) yüzünden, karada ve denizde fesat çıktı. Belki dönerler diye, (Allah) onlara, yaptıklarının bir kısmını taddırıyor.'),
(3451, 30, 42, 'De ki: "Yeryüzünde gezin, öncekilerin sonunun nasıl olduğuna bakın." Onların da çoğu ortak koşanlardan idi.'),
(3452, 30, 43, 'Allah\'tan, geri çevirilmesi mümkün olmayan gün gelmezden önce, yüzünü dosdoğru dine yönelt. O gün (insanlar) bölük bölük ayrılırlar (bir bölük cennete, öbür bölük ateşe gider).'),
(3453, 30, 44, 'Kim inkar ederse, inkarı kendi aleyhinedir. İyi bir iş yapanlar da (cennette) kendileri için yer hazırlamaktadırlar.'),
(3454, 30, 45, 'Ta ki Allah, inanıp iyi işler yapanları lutfundan mükafatlandırsın. Doğrusu O, kafirleri sevmez.'),
(3455, 30, 46, 'O\'nun ayetlerinden biri de (şudur): Rüzgarları (yağmurun) müjdeleri olarak gönderir ki size rahmetinden biraz taddırsın, gemiler buyruğuyla yürüsün ve siz O\'nun lutfundan arayasınız da (verdiği ni\'metlere) şükredesiniz.'),
(3456, 30, 47, 'Andolsun ki, biz senden önce de elçileri kavimlerine gönderdik; onlara deliller getirdiler ve biz, (onları dinlemeyip) suç işleyenlerden öc aldık. (Elbette alırız, çünkü) mü\'minlere yardım etmek, üzerimize borç idi.'),
(3457, 30, 48, 'Allah, rüzgarları gönderir, bulutu kaldırır; sonra onu gökte dilediği gibi yayar ve parça parça eder; arasından yağmurun çıktığını görürsün. Derken, onu kullarından dilediğine uğratınca hemen sevinirler.'),
(3458, 30, 49, 'Halbuki onlar, yağmurun kendilerine indirilmesinden önce umutsuz idiler.'),
(3459, 30, 50, 'Allah\'ın rahmetinin eserlerine bak ki, nasıl yeri ölümünden sonra diriltiyor?! Şüphe yok ki, O, ölüleri de diriltecektir, O her şeye kadirdir.'),
(3460, 30, 51, 'Andolsun bir rüzgar, göndersek de o(eki)ni sararmış görseler, ondan sonra nankörlük etmeğe başlarlar.'),
(3461, 30, 52, 'Sen de ölülere söz dinletemezsin; arkalarını dönüp giden sağırlara çağrıyı işittiremezsin.'),
(3462, 30, 53, 'Ve sen, körleri de sapıklıklarından çıkarıp yola getiremezsin. Sen ancak, ayetlerimize inananlara işittirirsin de onlar müslüman olurlar.'),
(3463, 30, 54, 'Allah\'tır ki sizi zayıflıktan yarattı. Sonra zayıflığın ardından (size) bir kuvvet verdi. Sonra kuvvetin ardından da zayıflık ve ihtiyarlık verdi. Allah, dilediğini yaratır, O, bilendir, gücü yetendir.'),
(3464, 30, 55, '(Duruşma) Sa\'at(i) başladığı gün, suçlular, (dünyada veya Berzahta) bir sa\'atten fazla kalmadıklarına yemin ederler. İşte onlar, (dünyada da haktan) böyle çevriliyorlardı.'),
(3465, 30, 56, 'Kendilerine bilgi ve iman verilenler dediler ki: "Andolsun siz, Allah\'ın yazgısınca ta yeniden dirilme gününe kadar kaldınız. İşte bu da dirilme günüdür. Fakat siz bilmiyordunuz.'),
(3466, 30, 57, 'Artık zulmetmiş olanlara o gün, ne mazeretleri fayda verir ve ne de onlardan rıza talebetmeleri istenir.'),
(3467, 30, 58, 'Andolsun biz bu Kur\'an\'da insanlara her çeşit misali getirip anlattık. Onlara bir ayet getirdiğin zaman inkar edenler: "Siz (geleneklerimizi) iptal edenlerden başka bir şey değilsiniz." derler.'),
(3468, 30, 59, 'İşte Allah, bilmeyenlerin kalblerini böyle mühürler.'),
(3469, 30, 60, 'Sabret, Allah\'ın va\'di haktır (o mutlaka yerine gelecektir). İnanmayanlar seni telaşa düşürmesin.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Elif lam mim'),
(3471, 31, 2, 'Şunlar, hikmetli Kitabın ayetleridir.'),
(3472, 31, 3, 'Güzel davrananlara yol gösterici ve rahmet olarak (indirilmiştir).'),
(3473, 31, 4, 'Onlar ki namazı kılarlar, zekatı verirler ve onlar ahirete de kesin olarak inanırlar.'),
(3474, 31, 5, 'İşte onlar, Rableri tarafından (gösterilen) doğru bir yol üzerindedirler ve onlar, umduklarına ereceklerdir.'),
(3475, 31, 6, 'İnsanlardan kimi var ki; bilgisizce (insanları) Allah\'ın yolundan saptırmak ve onunla alay etmek için boş hadisi (eğlence sözünü) satın alır. İşte onlara küçük düşürücü bir azab vardır.'),
(3476, 31, 7, 'Ona ayetlerimiz okunduğu zaman sanki onları hiç işitmemiş, sanki kulaklarında ağırlık varmış gibi büyüklük taslayarak döner. Ona acı bir azabı müjdele.'),
(3477, 31, 8, 'İnanan ve iyi işler yapanlara ni\'meti bol cennetler vardır.'),
(3478, 31, 9, 'Orada ebedi kalacaklardır. (Bu,) Allah\'ın gerçek va\'didir. O üstündür, hüküm ve hikmet sahibidir.'),
(3479, 31, 10, '(Allah), gökleri görebildiğiniz bir direk olmadan yarattı, sizi sarsar diye yere de sağlam ve yüksek dağlar attı ve orada her çeşit canlıyı yaydı. Gökten bir su indirdik de orada her güzel çifti bitirdik.'),
(3480, 31, 11, 'İşte bunlar, Allah\'ın yarattıklarıdır. Gösterin bana, O\'ndan başka(tanrı dedik)leri(niz) ne yarattı? Doğrusu o zalimler, açık bir sapıklık içindedirler.'),
(3481, 31, 12, 'Andolsun biz Lokman\'a, "Allah\'a şükret!" diye hikmet verdik, kim şükrederse kendisi için şükreder; kim nankörlük ederse Allah zengindir, (onun şükrüne muhtaç değildir), övülmüştür (hamde layıktır).'),
(3482, 31, 13, 'Lokman oğluna öğüt vererek demişti ki: "Yavrum, Allah\'a ortak koşma, çünkü ortak koşmak, büyük bir zulümdür."'),
(3483, 31, 14, 'Biz insana, ana babasını tavsiye ettik. Anası onu zayıflık üstüne zayıflık çekerek (karnında) taşımıştır. (Ona gebe kaldığından itibaren ta doğuruncaya kadar günden güne güçsüzleşmiş, ağırlaşmıştır). Onun sütten kesilmesi de iki yıl içinde olmuştur. (Bunların hepsi, güç şeylerdir. Onun için biz insana): "Bana ve anana-babana şükret, dönüş banadır."'),
(3484, 31, 15, 'Eğer onlar seni, hakkında bilgin olmayan bir şeyi bana ortak koşman için zorlarlarsa, onlara ita\'at etme. Onlarla dünya(işlerin)de iyi geçin ve bana yönelen kimsenin yoluna uy. Sonra dönüşünüz banadır; (o zaman ben) size yaptıklarınızı haber vereceğim (diye öğüt verdik).'),
(3485, 31, 16, '(Lokman öğütlerine devam ederek dedi ki): "Yavrum, (yaptığın iyilik veya kötülük), hardal danesi ağırlığınca bir şey de olsa, bir kayanın içinde, göklerde veya yerde bulunsa Allah mutlaka onu getirir. Çünkü Allah latiftir (O\'nun bilgisi her gizli veince şeye ulaşır. O, her şeyi) haber alır."'),
(3486, 31, 17, 'Yavrum namazı kıl, iyiliği emret, kötülükten vazgeçir ve başına gelene sabret. Çünkü bunlar yapılması gereken işlerdendir.'),
(3487, 31, 18, 'İnsanlara yanağını bükme (kibirlenerek boynunu bir yana büküp yüzünü insanlardan öte çevirme) ve yeryüzünde böbürlenerek yürüme. Zira Allah, kendini beğenip övünen kimseyi sevmez.'),
(3488, 31, 19, 'Yürüyüşünde tutumlu ol, (orta yürü, ne çabuk ne de çok yavaş git, ölçülü hareket et), sesini de kıs. Çünkü seslerin en çirkini eşeklerin sesidir.'),
(3489, 31, 20, 'Görmediniz mi Allah, göklerde ve yerde bulunan şeyleri size boyun eğdirdi ve size zahir ve batın (dış ve iç; görülen, görülmeyen; bildiğiniz ve bilmediğiniz) ni\'metlerini bol bol verdi? Yine de insanlardan kimi var ki ne bilgisi, ne yol göstereni ve ne de aydınlatıcı bir Kitabı olmadan Allah hakkında tartışır (durur).'),
(3490, 31, 21, 'Onlara: "Allah\'ın indirdiğine uyun!" dense: "Hayır, biz babalarımızı üzerinde bulduğumuz şeye uyarız (onların yolundan gideriz)" derler. Şeytan onları alevli ateşin azabına çağırmış olsa da mı (babalarının izinde gidecekler)?'),
(3491, 31, 22, 'Kim güzel davranarak özünü Allah\'a teslim ederse o, en sağlam kulpa yapışmıştır. İşlerin sonu Allah\'a döner.'),
(3492, 31, 23, 'Kim de inkar ederse, onun inkarı seni üzmesin. Sonunda onların dönüşleri bizedir. O zaman yaptıklarını kendilerine haber veririz. Şüphesiz Allah göğüslerin özünü (kalblerden ne düşünceler geçtiğini) bilir.'),
(3493, 31, 24, 'Onları biraz yaşatırız, sonra kaba bir azaba süreriz.'),
(3494, 31, 25, 'Andolsun onlara: "Gökleri ve yeri kim yarattı? diye sorsan, mutlaka: "Allah" derler. "Hamd Allah\'a layıktır" de. Hayır, çokları bilmezler.'),
(3495, 31, 26, 'Göklerde ve yerde ne varsa hepsi Allah\'ındır. Allah, işte ğani (zengin) O, övülen O\'dur.'),
(3496, 31, 27, 'Yeryüzünde bulunan ağaçlar kalem olsa, deniz(ler) de (mürekkep olsa), arkasından yedi deniz (daha gelip) ona yardım etse de (Allah\'ın kelimeleri yazılsa), yine (bunlar tükenir), Allah\'ın kelimeleri tükenmez. Allah öyle üstündür, öyle hikmet sahibidir.'),
(3497, 31, 28, 'Sizin yaratılmanız ve diriltilmeniz, bir tek kişi(nin yaratılıp diriltilmesi) gibidir. Şüphesiz Allah, işitendir, görendir.'),
(3498, 31, 29, 'Görmedin mi Allah, geceyi gündüzün içine sokuyor; gündüzü gecenin içine sokuyor. Güneşi ve ayı, emrine boyun eğdirmiştir. Her biri belli bir süreye kadar akıp gider. Ve Allah yaptıklarınızı haber almaktadır.'),
(3499, 31, 30, 'Böyledir, çünkü Allah haktır, O\'ndan başka yalvardıkları batıldır. Gerçekten ulu ve büyük olan, yalnız Allah\'tır.'),
(3500, 31, 31, '(Allah) size, bir kısım ayetlerini göstersin diye, Allah\'ın ni\'metiyle gemilerin denizde gittiğini görmedin mi? Şüphesiz bunda sabreden, şükreden herkes için ibretler vardır.'),
(3501, 31, 32, '(Denizde) onları, gölgeler gibi dalga(lar) sardığı zaman, dini yalnız kendisine has kılarak Allah\'a yalvarırlar. Fakat O, onları kurtarıp karaya çıkarınca içlerinden bir kısmı iktisad eder (Allah\'a yönelmeyi kısar, gevşetir); zaten bizim ayetlerimizi (öyle) nankör gaddarlardan başkası inkar etmez.'),
(3502, 31, 33, 'Ey insanlar, Rabbinizden korkun ve babanın, çocuğunun cezasını çekmeyecei, çocuğun da babasının cezasını çekmeyeceği (hiç kimse, kimsenin borcunu ödemeyeceği) günden çekinin. Allah\'ın va\'di gerçektir. Dünya hayatı sizi aldatmasın. O aldatıcı (şeytan), sizi Allah hakkında (O\'nun) yumuşak davranmasına, mühlet vermesine güvendirerek) aldatmasın.'),
(3503, 31, 34, 'Allah, (işte kıyamet) sa\'atin(in ne zaman geleceği) hakkındaki bilgi, O\'nun yanındadır. Yağmuru O yağdırır, rahimlerde olanı bilir. Hiç kimse yarın ne kazanacağını bilmez ve hiç kimse hangi yerde öleceğini bilmez. (Her şeyi) bilen, (her şeyden) haberi olan yalnız Allah\'tır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Elif lam mim.'),
(3505, 32, 2, 'Şüphe yok ki Kitabın indirilişi, alemlerin Rabbi tarafındandır.'),
(3506, 32, 3, 'Yoksa "Onu uydurdu" mu diyorlar? Hayır, o senden önce kendilerine hiçbir uyarıcı gelmemiş olan bir kavmi, doğru yola gelirler umuduyla uyarman için Rabbin tarafından (sana indirilen) gerçektir.'),
(3507, 32, 4, 'O (Allah) ki gökleri, yeri ve bunlar arasında bulunanları altı günde yarattı; sonra Arş\'a istiva etti. Sizin, O\'ndan başka bir dostunuz, şefa\'atçiniz yoktur. Düşünüp öğüt almıyor musunuz?'),
(3508, 32, 5, '(Allah) Emri gökten yere tedbir eder (buyruğunu indirir). Sonra emir, sizin hesabınızca bin yıl süren bir gün içinde O\'na çıkar.'),
(3509, 32, 6, 'İşte görünmeyeni de, görüneni de bilen, güçlü ve esirgeyici olan O\'dur.'),
(3510, 32, 7, 'O\'dur ki, yarattığı herşeyi güzel yaptı ve insanı yaratmağa çamurdan başladı.'),
(3511, 32, 8, 'Sonra onun neslini bir özden, hakir bir su(yun özü)nden yaptı.'),
(3512, 32, 9, 'Sonra ona biçim verdi, ona kendi ruhundan üfledi. Ve sizin için kulak(lar), gözler ve gönüller yarattı. Ne kadar az şükrediyorsunuz!'),
(3513, 32, 10, 'Biz toprakta kaybolduktan sonra, yeni bir yaratılış içinde mi olacağız? dediler. Doğrusu onlar, Rablerine kavuşmayı inkar edenlerdir.'),
(3514, 32, 11, 'De ki: "Üzerinize vekil edilen ölüm meleği, canınızı alır, sonra Rabbinize döndürülürsünüz."'),
(3515, 32, 12, 'Rablerinin huzurunda (utançtan) başlarını öne eğmiş; "Rabbimiz, gördük, işittik, bizi geri döndür, iyi iş yapalım; artık kesin olarak inandık!" demekte olan suçluları bir görsen!'),
(3516, 32, 13, 'Dileseydik, herkese hidayetini verirdik, (herkesi doğru yola iletirdik). Fakat benden "Mutlaka cehennemi, cinlerden ve insanlardan bir kısmiyle tamamen dolduracağım!" kararı çıkmıştır.'),
(3517, 32, 14, 'Bu gününüzle karşılaşmayı unutmanızın cezasını tadın! (Şimdi) Biz de sizi unuttuk. Yaptıklarınızdan ötürü ebedi azabı tadın!'),
(3518, 32, 15, 'Bizim ayetlerimize o kimseler inanırlar ki onlar, kendilerine öğüt verildiği zaman derhal secdeye kapanırlar; Rablerini överek tesbih ederler, büyüklük taslamazlar.'),
(3519, 32, 16, 'Yanları yataklardan uzaklaşır, (gece teheccüd namazı kılmak için yanlarını yataklardan ayırılıp kalkarlar), korkarak ve umarak Rablerine du\'a ederler ve kendilerine verdiğimiz rızıktan hayır için harcarlar.'),
(3520, 32, 17, 'Yaptıklarına karşılık olarak onlar için ne gözler aydınlatıcı(ni\'metleri)in saklandığını hiç kimse bilmez!'),
(3521, 32, 18, 'Hiç inanan kimse, (yoldan çıkan) fasık gibi olur mu? Elbette bunlar bir olmazlar.'),
(3522, 32, 19, 'İnanan ve iyi işler yapanlara gelince, onlar, yaptıklarına karşılık, durulmağa değer cennetlerde ağırlanırlar.'),
(3523, 32, 20, 'Yoldan çıkanların barınacakları yer de ateştir. Ne zaman oradan çıkmak isteseler, yine oraya geri çevrilirler ve onlara: "Yalanlamakta olduğunuz ateş azabını tadın" denilir.'),
(3524, 32, 21, 'Belki dön(üp yola gel)irler diye, mutlaka onlara o büyük azabdan ayrı olarak, daha yakın azabı da taddıracağız.'),
(3525, 32, 22, 'Kendisine Rabbinin ayetleriyle öğüt verildikten sonra onlardan yüz çevirenlerden daha zalim kim olabilir? Muhakkak ki biz, suçlulardan öç alıcıyız.'),
(3526, 32, 23, 'Andolsun biz Musa\'ya Kitabı verdik. Sakın onun (Musa\'ya) ulaşmasından kuşkuya düşme. Onu İsrail oğullarına yol gösterici yaptık.'),
(3527, 32, 24, 'Sabrettikleri ve ayetlerimize kesinlikle inandıkları zaman, onların içinden, buyruğumuzla doğru yola ileten önderler yetiştirmiştik.'),
(3528, 32, 25, 'Şüphesiz Rabbin, kıyamet günü, ayrılığa düştükleri konularda onların aralarında hükmedecektir.'),
(3529, 32, 26, 'Bugün yurtlarında dolaştıkları nice kuşakları daha önce helak etmiş olmamız, hala onları yola getirmedi mi? Şüphesiz bunda ibretler vardır. (Öğüt alma kulağıyle) İşitmiyorlar mı?'),
(3530, 32, 27, 'Görmüyorlar mı biz nasıl suyu, kuru, otsuz yere sürüyoruz da onunla ekin bitiriyoruz; ondan hayvanları da, kendileri de yiyor? Görmüyorlar mı?'),
(3531, 32, 28, 'Doğru iseniz bu fetih ne zaman? diyorlar.'),
(3532, 32, 29, 'De ki: "Fetih günü (gelince, şimdi) inkar edenlere (o zaman) inanmaları fayda vermez ve kendilerine mühlet de verilmez.'),
(3533, 32, 30, 'Sen onlardan yüz çevir ve bekle, zaten onlar da beklemektedirler.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Ey peygamber, Allah\'tan kork; kafirlere ve münafıklara ita\'at etme. Şüphesiz Allah bilendir, hüküm ve hikmet sahibidir.'),
(3535, 33, 2, 'Rabbinden sana vahyedilene uy; muhakkak ki Allah yaptıklarınızı haber almaktadır.'),
(3536, 33, 3, 'Allah\'a dayan; vekil olarak Allah yeter.'),
(3537, 33, 4, 'Allah, bir adamın (göğüs) boşluğunda iki kalb yaratmadı ve zıhar yaptığınız (sen bana, annemin sırtı gibisin dediğiniz) eşlerinizi, sizin anneleriniz yapmadı; evlatlıklarınızı da sizin öz oğullarınız kılmadı. Bunlar sizin ağızlarınıza gelen sözlerinizdir. Allah gerçeği söyler ve O, doğru yola iletir.'),
(3538, 33, 5, 'Onları babalarının adına bağlayarak çağırın; bu, Allah yanında daha adaletlidir. Eğer babalarını bilmiyorsanız, onlar sizin din kardeşleriniz ve dostlarınızdır. Yanılarak yaptığınızda size bir günah yok, fakat kalblerinizin bile bile yaptığında günah vardır. Allah çok bağışlayan, çok esirgeyendir.'),
(3539, 33, 6, 'Peygamber, mü\'minlere canlarından ileridir. Onun eşleri de onların anneleridir. Rahim sahipleri (anne tarafından akrabalar) da Allah\'ın Kitabında birbirlerine öteki mü\'minlerden ve Muhacirlerden daha yakındırlar. Ancak dostlarınıza bir iyilik yapmanız (bir vasiyyet etmeniz) hariç (yaptığınız o vasiyyet yerine getirilir). Bunlar Kitapta yazılmıştır.'),
(3540, 33, 7, 'Biz peygamberlerden, (verdiğimiz elçilik görevini yapmak ve hak dine da\'vet etmek hususunda) kuvvetle ahidlerini almıştık, senden, Nuh\'dan, İbrahim\'den, Musa\'dan ve Meryem oğlu Îsa\'dan, (evet) onlardan sapasağlam söz almıştık.'),
(3541, 33, 8, '(Böyle yaptık) Ki (Allah), o doğrulara doğruluklarından sorsun. Kafirlere de acı bir azab hazırlamıştır.'),
(3542, 33, 9, 'Ey inananlar, Allah\'ın size olan ni\'metini hatırlayın, hani bir zaman size ordular gelmişti de biz onların üzerine bir rüzgar ve sizin görmediğiniz ordular göndermiştik. Allah, yaptıklarınızı görmekteydi.'),
(3543, 33, 10, 'Hani onlar üstünüzden ve alt tarafınızdan gelmişlerdi, gözler (şaşkınlıktan ötürü) kaymış, yürekler (korkudan) hançerelere dayanmıştı. Allah hakında türlü zanlarda bulunuyordunuz.'),
(3544, 33, 11, 'İşte orada mü\'minler denenmiş, şiddetli bir sarsıntı ile sarsılmışlardı.'),
(3545, 33, 12, 'Münafıklar ve kalblerinde hastalık bulunan kimseler: "Allah ve Resulü bize sadece boş vaadlerde bulundu." diyordu.'),
(3546, 33, 13, 'Onlardan bir grup da demişti ki: "Ey Yesrib (Medine) halkı, artık size duracak yer yok, (haydi durmayın, evlerinize) dönün (Yahut: Artık bu dinde durmanız doğru değil, dönün)". Onlardan bir topluluk da. "Evlerimiz (sağlam değil), açıktır" diyerek peygamberden izin istiyordu. Oysa onlar(ın evleri) açık değildi. Sadece kaçmak istiyorlardı.'),
(3547, 33, 14, 'Eğer (Medine\'nin) her yanından onların üzerine giril(ip saldırıl)saydı da kendilerinden (halka) baskı ve işkence yapmaları istenseydi bunu yaparlardı; bunu yapmakta fazla gecikmezlerdi.'),
(3548, 33, 15, 'Oysa arkalarına dön(üp kaç)mayacaklarına dair Allah\'a söz vermişlerdi. Allah\'a verilen sözden sorumlu idiler.'),
(3549, 33, 16, 'De ki: "Eğer ölümden veya öldürülmekten kaçıyorsanız, kaçmak size fayda vermez. Kaçsanız bile pek az bir zaman yaşatılırsınız (sonunda yine ölürsünüz)."'),
(3550, 33, 17, 'De ki: "Allah size kötülük istese veya size rahmet dilese, sizi O\'ndan kim korur? (Allah\'ın azabından sizi kim kurtarır, O\'nun rahmetine kim engel olur?) Kendilerine Allah\'tan başka ne bir dost ne de bir yardımcı bulurlar.'),
(3551, 33, 18, 'Allah içinizden (savaştan) alıkoyanları ve kardeşlerine: "Bize gelin!" diyenleri biliyor. Onlar savaşa pek az gelirler.'),
(3552, 33, 19, '(Geldikleri zaman da) Size karşı cimriler olarak (gelirler). Ama korkulu bir durum olunca onların, üstüne ölüm baygınlığı çökmüş insan gibi, gözleri dönerek sana baktıklarını görürsün. Korku gid(ip de sıra ganimetleri paylaşmağa gel)ince mala düşkünlük göstererek sizi sivri dillerle incitirler. Onlar, (içtenlikle) inanmamışlar, bu yüzden Allah onların işlerini boşa çıkarmıştır. Bu, Allah\'a göre kolaydır.'),
(3553, 33, 20, '(Korkaklıklarından ötürü düşman) Orduların(ın Medine\'den) gitmediklerini sanıyorlardı. Eğer o ordular (ikinci kez) gelseler, bunlar çölde bedevi Araplar arasında bulunmayı, sizin haberlerinizi (başınıza gelecek olayları) oradan sorup öğrenmeyi arzu ederlerdi. İçinizde bulunsalardı dahi pek az dövüşürlerdi.'),
(3554, 33, 21, 'Andolsun Allah\'ın Elçisinde sizin için Allah\'a ve ahiret gününe kavuşmaya inanan ve Allah\'ı çok anan kimseler için, (uyulacak) en güzel bir örnek vardır.'),
(3555, 33, 22, 'Mü\'minler (düşman) orduları(nı) gördükleri zaman (korkmadılar): "Bu Allah\'ın ve Resulünün, bize va\'dettiği(zafer)dir. Allah ve Resulü doğru söylemiştir." dediler. Ve bu, onların sadece imanlarını ve teslimiyetlerini artırdı.'),
(3556, 33, 23, 'Mü\'minlerden öyle erkekler var ki, Allah\'a verdikleri sözde durdular. Onlardan kimi adağını yerine getirdi, (şehid oluncaya kadar çarpışacaklarını adamışlardı, çarpıştılar ve şehid düştüler), kimi de (şehidlik) beklemektedir; sözlerini asla değiştirmemişlerdir.'),
(3557, 33, 24, 'Ki Allah doğruları, doğruluklarıyle mükafatladırsın, iki yüzlülere de dilerse azabetsin; yahut tevbelerini kabul buyursun. Şüphesiz Allah çok bağışlayandır, çok esirgeyendir.'),
(3558, 33, 25, 'Allah, inkar edenleri öfkeleriyle geri çevirdi; hiçbir hayra eremediler. Allah savaşta (rüzgar ve meleklerin yardımıyle) mü\'minlere yetti. Allah güçlüdür, üstündür.'),
(3559, 33, 26, 'Kitap ehlinden onlara yardım eden(Kurayza yahudi)lerini de kalelerinden indirdi ve kalblerine korku düşürdü. (Onlardan) Bir kısmını öldürüyordunuz, bir kısmını da esir alıyordunuz.'),
(3560, 33, 27, 'Onların topraklarını, evlerini, mallarını ve henüz ayak basmadığınız bir toprağı size miras verdi. Allah, her şeye kadirdir.'),
(3561, 33, 28, 'Ey peygamber! Eşlerine söyle: "Eğer siz, dünya hayatını ve süsünü istiyorsanız, gelin size müt\'a (boşanma bedeli) vereyim ve sizi güzellikle salayım."'),
(3562, 33, 29, 'Eğer siz, Allah\'ı, Eçisini ve ahiret yurdunu istiyorsanız, (biliniz ki) Allah, sizden güzel hareket edenlere büyük bir mükafat hazırlamıştır.'),
(3563, 33, 30, 'Ey peygamber kadınları! Sizden kim açık bir fuhuş (edepsizlik) yaparsa onun için azab iki kat yapılır. Bu, Allah\'a göre kolaydır.'),
(3564, 33, 31, 'Fakat sizden kim Allah\'a ve Resulüne ita\'ate devam eder ve yararlı iş yaparsa ona da mükafatını iki kez veririz ve onun için bol bir rızık hazırlamışızdır.'),
(3565, 33, 32, 'Ey peygamber kadınları, siz, kadınlardan herhangi biri gibi değilsiniz. Eğer (Allah\'ın buyruğuna karşı gelmekten) korunuyorsanız, sözü yumuşak (kıvrak) bir eda ile söylemeyin ki, kalbinde hastalık bulunan kimse tamah etmesin; güzel, (kuşkudan uzak bir biçimde) söz söyleyin.'),
(3566, 33, 33, 'Evlerinizde oturun, ilk cahiliye(çağı kadınları)nın açılıp kırıtması gibi açılıp kırıtmayın. Namazı kılın, zekatı verin, Allah\'a ve Resulüne ita\'at edin. Ey Ehl-i Beyt (ey peygamberin ev halkı), Allah sizden, kiri gidermek ve sizi tertemiz yapmak istiyor.'),
(3567, 33, 34, 'Evlerinizde okunan Allah ayetlerini ve hikmeti hatırlayın. Şüphesiz Allah latiftir, haber alandır.'),
(3568, 33, 35, 'Müslüman erkekler ve müslüman kadınlar, mü\'min erkekler ve mü\'min kadınlar, ta\'ate devam eden erkekler ve ta\'ate devam eden kadınlar, doğru erkekler ve doğru kadınlar; sabreden erkekler ve sabreden kadınlar, (Allah\'a) saygılı erkekler ve saygılı kadınlar, sadaka veren erkekler ve sadaka veren kadınlar, oruç tutan erkekler ve oruç tutan kadınlar, ırzlarını koruyan erkekler ve (ırzlarını) koruyan kadınlar, Allah\'ı çok zikreden erkekler ve zikreden kadınlar; (İşte) Allah bunlar için bağış ve büyük bir mükafat hazırlamıştır.'),
(3569, 33, 36, 'Allah ve Resulü, bir işte hüküm verdiği zaman, artık inanmış bir erkek ve kadının, o işi kendi isteklerine göre seçme hakkı yoktur. Kim Allah\'a ve Resulüne karşı gelirse, apaçık bir sapıklığa düşmüş olur.'),
(3570, 33, 37, 'Allah\'ın ni\'met verdiği; senin de kendisine ni\'met ver(ip hürriyete kavuştur)duğun kimseye: "Eşini yanında tut, Allah\'tan kork" diyordun, fakat Allah\'ın açığa vuracağı şeyi içinde gizliyordun, insanlardan çekiniyordun; oysa asıl çekinmene layık olan, Allah idi. Zeyd, o kadından ilişiğini kesince biz onu sana nikahladık ki (bundan böyle) evlatlıkları, kadınlarıyle ilişkilerini kestikleri zaman o kadınlarla evlenmek hususunda mü\'minlere bir güçlük olmasın. Allah\'ın buyruğu (her zaman) yerine getirilmiştir.'),
(3571, 33, 38, 'Allah\'n kendisine takdir ettiği bir şeyi yerine getirmekte, Peygambere herhangi bir güçlük yoktur. Sizden önce geçenler arasında da Allah\'ın yasası böyle idi. Allah\'ın emri, olup bitmiş bir şeydir.'),
(3572, 33, 39, '(O peygamberler), Allah\'ın mesajlarını duyururlar, Allah\'tan korkarlar ve O\'ndan başka kimseden korkmazlardı. Hesap görücü olarak Allah yeter.'),
(3573, 33, 40, 'Muhammed, sizin erkeklerinizden birinin babası değil, falat Allah\'ın Elçisi ve peygamberlerin hatemidir. Allah her şeyi bilendir.'),
(3574, 33, 41, 'Ey inananlar, Allah\'ı çok anın.'),
(3575, 33, 42, 'Ve O\'nu sabah akşam tesbih edin.'),
(3576, 33, 43, 'O(Allah)dır ki, sizi karanlıklardan aydınlığa çıkarmak için üzerinize rahmet eder, melekleri de (size acıyıp mağfiret dilerler. Allah) inananlara karşı çok esirgeyendir.'),
(3577, 33, 44, 'Kendisine kavuştukları gün, selam ile karşılanırlar. (Allah) Onlara güzel bir mükafat hazırlamıştır.'),
(3578, 33, 45, 'Ey peygamber, biz seni şahid, müjdeci ve uyarıcı olarak gönderdik.'),
(3579, 33, 46, 'Ve izniyle, Allah\'a da\'vetçi ve aydınlatıcı bir lamba olarak (gönderdik).'),
(3580, 33, 47, 'Mü\'minlere, Allah\'tan büyük bir lutuf bulunduğunu müjdele!'),
(3581, 33, 48, 'Kafirlere ve münafıklara ita\'at etme. Onların eziyetlerine aldırma, Allah\'a dayan; vekil (koruyucu) olarak Allah yeter.'),
(3582, 33, 49, 'Ey inananlar, inanan kadınları nikahlayıp da henüz onlara dokunmadan boşarsanız, onların üzerinde sayacağınız bir iddet hakkınız yoktur. Hemen müt\'alarını verin (biraz geçimlik verip memnun edin) ve onları güzellike serbest bırakın.'),
(3583, 33, 50, 'Ey peygamber, biz, ücretlerini (mehirlerini) verdiğin eşlerini; Allah\'ın sana ganimet olarak verdiğ(i savaş esir)lerinden elinin altında bulunan(cariye)leri; amcanın, halalarının, dayının ve teyzelerinin seninle beraber göç eden kızlarını sana helal kıldık. Bir de kendisini (mehirsiz olarak) peygambere hibe eden ve peygamberin de kendisini almak dilediği inanmış kadını, diğer mü\'minlere değil, sırf sana mahsus olmak üzere (helal kıldık). Biz, eşleri ve ellerinin altında bulunan(cariye)leri hakkında mü\'minlere yapmalarını gerekli kıldığımız şeyi bil(dir)dik. (onların bu hususta ne yapması lazım geldiğini de daha önce açıkladık) ki, sana bir zorluk olmasın, (sen bir sıkıntıya, güç bir duruma düşmeyesin). Allah çok bağışlayan, çok esirgeyendir.'),
(3584, 33, 51, 'Onlardan dilediğini geri bırakır, dilediğini yanına alırsın. (Geçici olarak) Ayrıldıklarından (tekrar birleşmeyi) arzu ettiğine (dönmekte) senin üzerine bir günah yoktur. Onların gözlerinin aydınlanıp tasalanmamalarına ve hepsinin, senin verdiklerine razı olmalarına en elverişli olan budur. Allah sizin kalblerinizde olanı bilir. Allah bilendir, halimdir (birden öfkeye kapılıp ceza vermez).'),
(3585, 33, 52, 'Bundan sonra artık sana (başka) kadınlar(la evlenmek), güzellikleri çok hoşuna giden kadınlar olsa da, bunları başka eşlerle değiştirmek helal değildir. Yalnız elinin altında bulunan(cariye)ler bunun dışındadır. Allah, her şeyi gözetleyicidir.'),
(3586, 33, 53, 'Ey inananlar, (rastgele) Peygamber\'in evlerine girmeyin. Ancak yemek için size izin verilir de girerseniz (erkenden gelip) yemeğin pişmesini beklemeyin. Çağrıldığınız zaman girin; yemeği yeyince dağılın, söze dalmayın. Çünkü bu (davranışınız) Peygamberi incitiyor, fakat o, (size bunu söylemekten) utanıyordu. Ama Allah, gerçek(i söylemek)ten utanmaz. Onlardan (yani peygamberin hanımlarından) bir şey istediğiniz zaman perde arkasından isteyin. Bu, hem sizin kalbleriniz, hem de onların kalbleri için daha temizdir. Sizin, Allah\'ın Elçisini incitmeniz ve kendisinden sonra onun eşlerini nikahlamanız asla olamaz. Çünkü bu, Allah katında büyük(bir günah)tır.'),
(3587, 33, 54, 'Bir şeyi açığa vursanız da, yahut onu gizleseniz de (fark etmez), Allah her şeyi gayet iyi bilmektedir.'),
(3588, 33, 55, 'Onlara (yani Peygamberin hanımlarına) ne babaları, ne oğulları, ne kardeşleri, ne kardeşlerinin oğulları, ne kızkardeşlerinin oğulları, ne kadınları ve ne de ellerinin altında bulunan(köle)leri hakkında bir günah yoktur, (bunlara karşı örtünmeleri gerekmez. Ey Peygamberin hanımları) Allah\'tan korkun; şüphesiz Allah, her şeyi görmektedir.'),
(3589, 33, 56, 'Allah\'ı ve melekleri, Peygambere salat etmekte(onun şerefini gözetmeğe, şanını yüceltmeğe özen göstermekte)dir. Ey inananlar, siz de ona salat edin, (onun şanını yüceltmeğe özen gösterin); içtenlikle selam edin (ona esenlik dileyin).'),
(3590, 33, 57, 'Allah\'ı ve Elçisini incitenler var ya, işte Allah onlara dünyada ve ahirette la\'net etmiş ve onlar için alçaltıcı bir azab hazırlamıştır.'),
(3591, 33, 58, 'Mü\'min erkekleri ve mü\'min kadınları yapmadıkları bir şeyle (suçlayıp) incitenler bir iftira ve açık bir günah yüklenmişlerdir.'),
(3592, 33, 59, 'Ey Peygamber, eşlerine, kızlarına ve inananların kadınlarına söyle: (Bir ihtiyaç için dışarı çıktıkları zaman) örtülerini üstlerine salsınlar; onların tanınıp incitilmemesi için en elverişli olan budur. Allah çok bağışlayan, çok esirgeyendir.'),
(3593, 33, 60, 'Andolsun, iki yüzlüler, kalblerinde hastalık bulunanlar, şehirde kötü haberler yayanlar (bu yaptıklarından) vazgeçmezlerse seni onların üstüne süreriz (onlarla savaşmanı ve onları şehirden sürüp çıkarmanı sana emrederiz); sonra orada, senin yanında ancak az bir zaman kalabilirler.'),
(3594, 33, 61, 'La\'netlenirler; nerede rastlansalar yakalanıp; öldürülürler.'),
(3595, 33, 62, 'Allah\'ın önceden geçen(millet)ler arasında (uygulanan) yasası budur. (Peygamberlere karşı iki yüzlülük edenler öldürülürler). Allah\'ın yasasını değiştirme(ğe imkan) bulamazsın.'),
(3596, 33, 63, 'İnsanlar sana O sa\'atten soruyorlar. De ki: "Onun bilgisi Allah\'ın yanındadır." Ne bilirsin belki o sa\'at yakın olur?'),
(3597, 33, 64, 'Allah kafirlere la\'net etmiş ve onlar için çılgın bir ateş hazırlamıştır.'),
(3598, 33, 65, 'Orada ebedi olarak kalacaklar; (kendilerini koruyacak) bir dost ve yardımcı bulamayacaklardır.'),
(3599, 33, 66, 'Yüzleri ateşin içinde çevrildiği gün: "Eyvah bize! Keşke Allah\'a ita\'at etseydik, Elçiye ita\'at etseydik!" derler.'),
(3600, 33, 67, 'Ve dediler ki: "Rabbimiz, biz beylerimize ve büyüklerimize uyduk da bizi yoldan saptırdılar."'),
(3601, 33, 68, 'Rabbimiz, onlara iki kat azab ver ve onlara büyük bir la\'net eyle!'),
(3602, 33, 69, 'Ey inananlar, şu kimseler gibi olmayın ki, Musa\'ya eziyet ettiler de Allah onu onların dediklerinden beraat ettirdi; o, Allah yanında vecih (gözde, itibarlı bir kul) idi.'),
(3603, 33, 70, 'Ey inananlar, Allah\'tan korkun ve doğru söz söyleyin.'),
(3604, 33, 71, 'Ki (Allah) işlerinizi düzeltsin ve günahlarınızı bağışlasın. Kim Allah\'a ve Resulüne ita\'at ederse, büyük bir başarıya ermiş olur.'),
(3605, 33, 72, 'Biz emaneti, göklere, yere ve dağlara sunduk; onu yüklenmekten kaçındılar, on(un sorumluluğun)dan korktular; onu insan yüklendi; (fakat onun ağır sorumluluğunu tam kavrayamadı) doğrusu o, çok zalim, çok cahildir.'),
(3606, 33, 73, '(Allah bu emaneti insana vermiştir) Ki iki yüzlü erkeklere ve iki yüzlü kadınlara, ortak koşan erkeklere ve ortak koşan kadınlara azab etsin; inanan erkekleri ve inanan kadınları da bağışlasın. Allah çok bağışlayan, çok esirgeyendir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Hamd, göklerde ve yerde bulunanların hepsi kendisinin olan Allah\'a mahsustur. Ahirette de hamd O\'na mahsustur. O, hüküm ve hikmet sahibidir; (her şeyi) haber alandır.'),
(3608, 34, 2, 'Yerin içine gireni ve ondan çıkanı, gökten ineni ve oraya çıkanı bilir. O, çok esirgeyen, çok bağışlayandır.'),
(3609, 34, 3, 'İnkar edenler: "O Sa\'at bize gelmez," dediler. De ki: "Hayır, gaybı bilen Rabbim hakkı için o, mutlaka size gelecektir. Göklerde ve yerde zerre ağırlığınca bir şey, O\'ndan gizli kalmaz. Ne bundan küçük, ne de bundan büyük hiçbir şey yoktur ki apaçık bir Kitapta bulunmasın.'),
(3610, 34, 4, '(Her şeyi apaçık bir Kitapta tesbit etmiştir) Ki, inanıp iyi işler yapanları mükafatlandırsın. Onlar için mağfiret ve güzel rızık vardır.'),
(3611, 34, 5, 'Ayetlerimiz hakkında (bizi) aciz bırakmağa çalışanlara gelince: onlar içinde pislikten acı bir azab vardır.'),
(3612, 34, 6, 'Kendilerine bilgi verilenler, Rabbinden sana indirilenin, gerçek olduğunu, mutlak galib ve hamde layık olan(Allah)ın yoluna ilettiğini görürler.'),
(3613, 34, 7, 'İnkar edenler, dediler ki: "Siz tamamen dağılıp parçalandıktan sonra, mutlaka yeni bir yaratılış içinde olacağınızı size haber veren bir adam gösterelim mi size?"'),
(3614, 34, 8, 'Allah\'a yalan mı uydurdu, yoksa kendisinde delilik mi var? Hayır, ahirete inanmayanlar, azab ve uzak bir sapıklık içindedirler.'),
(3615, 34, 9, 'Onlar gökten ve yerden önlerinde ve arkalarında bulunanı, (kendilerini her yandan kuşatan göğü ve yeri) görmüyorlar mı? Dilesek onları yere batırırız, ya da üzerlerine gökten parçalar düşürürüz. Şüphesiz bunda (Rabbine) yönelen her kul için bir ibret vardır.'),
(3616, 34, 10, 'Andolsun, Davud\'a tarafımızdan bir üstünlük verdik: "Ey dağlar, onunla beraber tesbih edin. Ve ey kuşlar (siz de onun tesbihine katılın)!" (dedik) ve ona demiri yumuşattık:'),
(3617, 34, 11, 'Geniş zırhlar yap, dokumasını ölçülü yap ve (hepiniz) iyi işler yapın. Çünkü ben yaptıklarınızı görmekteyim. diye (vahyettik).'),
(3618, 34, 12, 'Süleyman\'a da, sabah gidişi bir ay(lık mesafe), akşam dönüşü bir ay(lık mesafe) olan rüzgarı boyun eğdirdik ve onun için katran (petrol) kaynağını da akıttık. Rabbinin izniyle cinlerin bir kısmı, onun önünde çalışırdı. Onlardan kim buyruğumuzdan sapsa, ona alevli azabı taddırırdık.'),
(3619, 34, 13, 'Ona dilediği gibi kaleler, heykeller, havuzlar kadar (geniş) leğenler, sabit kazanlar yaparlardı. "Ey Davud ailesi, şükredin!" kullarımdan şükreden azdır.'),
(3620, 34, 14, '(Süleyman\'ın) Ölümüne hükmettiğimiz zaman, onun öldüğünü, ancak değneğini yiyen bir ağaç kurdu gösterdi. (Kurdun yemesiyle değnek çürüyüp de ona dayalı duran Süleyman) Yıkılınca (onun öldüğü anlaşıldı ve) anlaşıldı ki eğer cinler gaybı bilselerdi, o küçük düşürücü azab içinde kalmazlardı.'),
(3621, 34, 15, 'Andolsun (Kahtan oğlu, Ya\'rub oğlu...) Sebe (oğulların)ın oturdukları yerlerde de bir ibret vardır: (O meskenler) Sağdan, soldan iki bahçe (ile çevrili idi. Onlara): "Rabbinizin rızkından yeyin de O\'na şükredin! Hoş (bir) ülke, çok bağışlayan Rab!" (denilmişti).'),
(3622, 34, 16, 'Ama (şükürden) yüz çevirdiler; bu yüzden üzerlerine Arim selini gönderdik; onların iki bahçesini buruk yemişli, acı meyvalı ve içinde biraz da sedir ağacı bulunan iki bahçeye çevirdik.'),
(3623, 34, 17, 'Nankörlük ettiklerinden ötürü onları böyle cezalandırdık; biz nankörden başkasını cezalandırır mıyız?'),
(3624, 34, 18, 'Onlarla, içinde bereketler yarattığımız kentler arasında, açıkça görünen kentler var ettik ve bunlar arasında yürümeyi takdir ettik: "Oralarda geceleri ve gündüzleri güven içinde yürüyün" (dedik).'),
(3625, 34, 19, 'Rabbimiz, seferlerimizin arasını uzaklaştır (şehirlerimiz birbirine çok yakın, bunlarını arasını uzat da daha uzun mesafelere gidelim) dediler ve kendilerine zulmettiler. Biz de onları efsanelere çevirdik, onları darmadağın ettik. Şüphesiz bunda, sabreden, şükreden herkes için ibretler vardır.'),
(3626, 34, 20, 'Andolsun İblis, onlar hakkındaki zannını doğru çıkardı, inanan bir bölükten başka (hepsi) ona uydular.'),
(3627, 34, 21, 'Onun onlar üzerinde zorlayıcı bir gücü yoktu. Ancak ahirete inananı, ondan kuşkulanandan (ayırd edip) bilelim diye (ona bu fırsatı verdik). Rabbin her şeyi korumaktadır.'),
(3628, 34, 22, 'De ki: "Allah\'tan başka (tanrı) sandığınız şeyleri çağırın, onlar ne göklerde ve ne de yerde zerre ağırlığınca bir şeye sahip değillerdir. Bu ikisi(nin yaratılmasında ve mülkü)nde bir ortaklıkları yoktur. Ve Allah\'ın onlardan bir yardımcısı da yoktur."'),
(3629, 34, 23, 'O\'nun huzurunda, O\'nun izin verdiği kimselerden başkasının şefa\'ati fayda vermez. Nihayet onların yüreklerinden korku giderilince (birbirlerine): "Rabbiniz ne buyurdu?" derler. "Hakkı (buyurdu)" derler. O, yücedir, büyüktür.'),
(3630, 34, 24, 'De ki: "Size göklerden ve yerden kim rızık veriyor?" De ki: "Allah, O halde ya biz veya siz, (ikimizden biri), doğru yol üzerinde veya açık bir sapıklık içindeyiz."'),
(3631, 34, 25, 'De ki: "Bizim işlediğimiz suçtan siz sorulacak değilsiniz; biz de sizin işlediğinizden sorumlu değiliz."'),
(3632, 34, 26, 'De ki: "Rabbimiz (kıyamet günü), hepimizi bir araya toplayacak, sonra aramızdaki sorunu çözecektir. O sorunları en güzel çözümleyendir, bilendir."'),
(3633, 34, 27, 'De ki: "O\'na kattığınız ortakları bana gösterin (bakayım, onlar tanrı olabilirler mi?). Hayır (böyle şey olamaz.) Doğrusu O, galib, hüküm ve hikmet sahibi olan Allah\'tır."'),
(3634, 34, 28, 'Biz seni ancak bütün insanlara müjdeleyici ve uyarıcı olarak gönderdik; fakat insanların çoğu bilmezler.'),
(3635, 34, 29, 'Diyorlar ki: "Doğru iseniz bu tehdid(ettiğiniz azap) ne zaman (olacak)?"'),
(3636, 34, 30, 'De ki: "Sizin için belirtilmiş bir gün vardır. Ondan ne bir sa\'at geri kalırsınız, ne de ileri geçebilirsiniz."'),
(3637, 34, 31, 'İnkar edenler dediler ki: "Biz ne bu Kur\'an\'a, ne de bundan öncekilere inanırız." Sen o zalimleri, Rablerinin huzurunda tutuklanmış, birbirlerine söz atarlarken bir görsen: Zayıf düşürülenler, büyüklük taslayanlara: "Siz olmasaydınız, elbette biz inanan insanlar olurduk." diyorlar.'),
(3638, 34, 32, 'Büyüklük taslayanlar da zayıf düşürülenlere dediler ki: "Size hidayet geldiği zaman sizi ondan biz mi engelledik? Hayır, zaten siz kendiniz suç işliyordunuz."'),
(3639, 34, 33, 'Zayıf düşürülenler büyüklük taslayanlara: "Hayır, gece gündüz dolap (kurar, kötülük aşılardınız) Allah\'a nankörlük etmemizi, O\'na eşler koşmamızı bize emrederdiniz." dediler. Ve azabı gördüklerinde, içlerinde pişmanlıklarını gizlediler. Biz de o nankörlerin boyunlarına demir halkalar geçirdik. Yalnız yaptıklarıyle cezalanmıyorlar mı?'),
(3640, 34, 34, 'Biz hangi ülkeye bir uyarıcı gönderdiysek mutlaka oranın varlıkla şımarmış kimseleri: "Biz, sizin gönderildiğiniz şeyi inkar ediyoruz" dediler.'),
(3641, 34, 35, 'Ve dediler ki: "Biz malca ve evladça daha çoğuz, biz azaba uğratılacak değiliz."'),
(3642, 34, 36, 'De ki: "Rabbim dilediğine rızkı yayar ve (dilediğine) kısar; fakat insanların çoğu bilmezler, (sanırlar ki mal ve evlad çokluğu şeref ve büyüklük sebebidir.)"'),
(3643, 34, 37, 'Ne mallarınız, ne de evladlarınız size katımızda bir yakınlık sağlar. Ancak inanıp faydalı iş yapanlar başka. Onlara yaptıklarının kat kat fazlası mükafat vardır ve onlar saraylarda güven (ve huzur) içindedirler.'),
(3644, 34, 38, 'Ayetlerimizi etkisiz kılmağa çalışanlara gelince, onlar da azabın içine getirileceklerdir.'),
(3645, 34, 39, 'De ki: "Rabbim kullarından dilediğine rızkı yayar ve ona (tekrar rızkı) kısar. Siz Allah için ne verseniz, Allah onun yerine başkasını verir. O, rızık verenlerin en hayırlısıdır."'),
(3646, 34, 40, 'O gün, onların hepsini bir araya toplar, sonra meleklere: "Bunlar size mi tapıyorlardı?" der.'),
(3647, 34, 41, '(Melekler) derler ki: "Sen yücesin, bizim velimiz (koruyucumuz) onlar değil, sensin. Hayır, onlar cinlere tapıyorlardı. Çokları onlara inanıyorlardı."'),
(3648, 34, 42, 'O gün birinizin, diğerine ne bir fayda, ne de zarar vermeğe gücü yeter. Biz zulmedenlere: "Yalanlamakta olduğunuz ateş azabını tadın!" deriz.'),
(3649, 34, 43, 'Onlara açık açık ayetlerimiz okunduğu zaman dediler ki: "Bu, sadece sizi babalarınızın taptığı(tanrılar)dan çevirmek isteyen bir adamdan başka bir şey değildir." Ve o nankörler dediler ki: "Bu, uydurulmuş bir yalandan başka bir şey değildir." Ve kendilerine gelen hakkı inkar edenler: "Bu, apaçak bir büyüdür, başka bir şey değildir" dediler.'),
(3650, 34, 44, 'Halbuki biz onlara okuyacakları bir Kitap vermemiştik ve senden önce onlara bir uyarıcı göndermemiştik.'),
(3651, 34, 45, 'Bunlardan önceki(millet)ler de yalanlanmışlardı. Bunlar, onlara verdiklerimizin onda birine bile erişmemişlerdir. (Onlara o kadar ni\'met verdiğim halde yine) elçilerimi yalanladılar. Ama benim de (onları) inkarım nasıl oldu, (onları nasıl mahvettim)!?'),
(3652, 34, 46, 'De ki: "Size bir şeyi öğütleyeyim: \'Allah için, ikişer ikişer ve teker teker durup düşününüz! Arkadaşınızda delilikten eser yoktur. O, çetin bir azabın arefesinde sizin için bir uyarıcıdır."'),
(3653, 34, 47, 'De ki: "Ben sizden bir ücret istemişsem, o sizin olsun. Benim ücretim yalnız Allah\'a aittir. O her şeye şahiddir\'."'),
(3654, 34, 48, 'De ki: "Rabbim gerçeği, (dilediği kulunun kalbine) atar. (O) gaybleri bilendir."'),
(3655, 34, 49, 'De ki: "Hak geldi, artık batıl ne bir şey ortaya çıkarabilir, ne de geri getirebilir. (O tamamen yok olup gitmiştir)."'),
(3656, 34, 50, 'De ki: "Eğer saparsam, kendi zararıma sapmış olurum. Eğer yolu bulursam, bu da Rabbimin bana vahyettiği (Kur\'an) sayesindedir. Şüphesiz O, işitendir, yakındır."'),
(3657, 34, 51, 'Telaşa düştükleri zaman (onları) bir görsen: Hiçbiri kurtulamaz, yakın yerden yakalanmışlardır.'),
(3658, 34, 52, 'Ona inandık demektedirler, ama uzak yerden (ta dünyadan imanı) nasıl alabilsinler?'),
(3659, 34, 53, 'Oysa daha önce onu inkar etmişlerdi. Uzak yerden görülmeyene taş atıyorlardı.'),
(3660, 34, 54, 'Tıpkı bundan önce benzerlerine yapıldığı gibi, kendileriyle arzu ettikleri şey arasına perde çekildi. Doğrusu, onlar katmerli bir kuşku içindedirler.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Gökleri ve yeri yoktan var eden; melekleri, ikişer, üçer, dörder kanatlı elçiler yapan Allah\'a hamd olsun. O, yaratmada (dilediği kadar) artırır: (Kimine daha fazla kanat verir, kiminin bünyesini daha sağlam, daha büyük, kimini daha güzel yapar). Şüphesiz Allah, her şeyi yapabilendir.'),
(3662, 35, 2, 'Allah, insanlara bir rahmet açtı mı onu tutan olamaz, O\'nun tuttuğunu da O\'ndan sonra salacak yoktur. O, üstündür, hüküm ve hikmet sahibidir.'),
(3663, 35, 3, 'Ey insanlar, Allah\'ın size olan ni\'metini hatırlayın: Allah\'tan başka size gökten ve yerden rızık verecek bir yaratıcı var mı? O\'ndan başka tanrı yoktur. Nasıl oluyor da gerçekten çevriliyorsunuz?'),
(3664, 35, 4, 'Eğer seni yalanlıyorlarsa, senden önceki elçiler de yalanlanmıştır. Bütün işler Allah\'a döndürülecek(ve Allah, herkesi yaptığıyle cezalandıracak)tır.'),
(3665, 35, 5, 'Ey insanlar, Allah\'ın va\'di gerçektir; sakın dünya hayatı sizi aldatmasın, o aldatıcı, sizi Allah(ın affına güvendirmek sureti) ile aldatmasın.'),
(3666, 35, 6, 'Şeytan, sizin düşmanınızdır, siz de onu düşman tutun. O, partisini alevli ateşin halkından olmağa çağırır.'),
(3667, 35, 7, 'İnkar edenler için çetin bir azab var; inanıp iyi işler yapanlara da mağfiret ve büyük bir mükafat vardır.'),
(3668, 35, 8, 'Kötü işi, kendisine süslendirilip de onu güzel gören kimse (vehmine aldanmayarak kötü amelini güzel görmeyen, aklıyle gerçeği gören kimse gibi olur) mu? Allah dilediğini sapıklık içinde bırakır, dilediğini yola iletir. Bundan dolayı canın, onlar için hasretlere (üzüntülere) gitmesin, Allah onların ne yaptıklarını biliyor.'),
(3669, 35, 9, 'Allah\'tır ki, gönderdiği rüzgarlar bir bulut kaldırır, onu ölü bir ülkeye süreriz, ölmüş olan yeri onunla diriltiriz. İşte diriltme de böyledir.'),
(3670, 35, 10, 'Kim şeref istiyorsa (bilsin ki) şeref tamamen Allah\'ındır, (onu başkasından değil, Allah\'tan istesin). Güzel söz O\'na çıkar, iyi amel onu yükseltir. Kötü şeyleri kuranlara gelince, onlar için çetin bir azab vardır. Ve onların tuzağı bozulacaktır.'),
(3671, 35, 11, 'Allah sizi önce topraktan, sonra nutfe(sperm)den yarattı, sonra sizi çift, çift yaptı. Bir dişinin gebe kalması ve doğurması hep O\'nun bilgisiyledir. Bir canlıya ömür verilmesi de, onun ömründen azaltılması da mutlaka bir Kitapta(yazılı)dır. Şüphesiz bu, Allah\'a göre kolaydır.'),
(3672, 35, 12, 'İki deniz bir olmaz: Şu tatlıdır, susuzluğu keser, içimi (boğazdan) kayar; şu da tuzlu, acıdır. Hepsinden de taze et yersiniz ve takındığınız (inci, sedef gibi) süs (eşyası) çıkarırsınız. (Allah\'ın) Lutfundan payınızı arayıp şükretmeniz için gemilerin, denizi yarıp gittiğini görürsün.'),
(3673, 35, 13, '(Allah) Geceyi gündüzün içine sokar, gündüzü de gecenin içine sokar; güneşi ve ayı buyruğu altına almıştır. Her biri belirtilmiş bir süreye kadar akıp gider. İşte Rabbiniz Allah budur, mülk O\'nundur. O\'ndan başka yalvardığınız şeyler ise bir çekirdek zarına bile sahip değillerdir.'),
(3674, 35, 14, 'Onları çağırsanız sizin çağırmanızı işitmezler. İşitseler bile size cevap veremezler. Kıyamet günü de, sizin (onları Allah\'a) ortak koşmanızı tanımazlar. Hiç kimse sana, herşeyi bilen (Allah) gibi gerçekleri haber veremez.'),
(3675, 35, 15, 'Ey insanlar, siz Allah\'a muhtaçsınız, Allah ise, işte zengin ve hamde layık olan O\'dur.'),
(3676, 35, 16, 'Dilese sizi götürür ve yeni bir halk getirir.'),
(3677, 35, 17, 'Bu, Allah\'a zor değildir.'),
(3678, 35, 18, 'Hiçbir günahkar başkasının günahını çekmez. Eğer yükü ağır gelen kimse onu taşımak için (başkalarını çağırsa) onun yükünden hiçbir şey, taşınmaz; akrabası dahi olsa (kimse onun yükünü taşımaz). Sen ancak görmeden Rablerinden korkanları ve namazı kılanları uyarırsın. Ma\'nen arınıp yücelen, kendi yararına arınmış olur. Dönüş Allah\'adır, (Allah, herkese yaptığının karşılğını verir).'),
(3679, 35, 19, 'Körle, gören bir olmaz.'),
(3680, 35, 20, 'Karanlıklarla, aydınlık da bir olmaz.'),
(3681, 35, 21, 'Gölge ile sıcaklık da bir olmaz.'),
(3682, 35, 22, 'Dirilerle, ölüler de bir olmaz. Allah dilediğine işittirir; yoksa sen kabirlerde bulunanlara işittirecek değilsin.'),
(3683, 35, 23, 'Sen sadece bir uyarıcısın.'),
(3684, 35, 24, 'Biz seni gerçek ile birlikte müjdeleyici ve uyarıcı olarak gönderdik. Her millet içinde mutlaka bir uyarıcı (peygamber gelip) geçmiştir.'),
(3685, 35, 25, 'Eğer (bunlar) seni yalanlıyorlarsa (üzülme çünkü) bunlardan öncekiler de yalanlamışlardı. Elçileri onlara açık kanıtlar, sahifeler ve aydınlatıcı Kitap getirmişlerdi (yine de onları yalanladılar).'),
(3686, 35, 26, 'Sonra ben de o inkar edenleri yakaladım. Benim (onları) inkarım (cezalandırmam) nasıl oldu?'),
(3687, 35, 27, 'Görmedin mi Allah gökten su indirdi de, onunla renkleri çeşit çeşit meyvalar çıkardık. Dağlardan (geçen) beyaz, kırmızı, değişik renklerde ve simsiyah yollar (yarattık).'),
(3688, 35, 28, 'İnsanlardan, hayvanlardan ve davarlardan da yine böyle türlü renkte olanlar var. Kulları içinden ancak bilginler, Allah\'tan (gereğince) korkar. Şüphesiz Allah daima üstündür, çok bağışlayandır.'),
(3689, 35, 29, 'Allah\'ın Kitabını okuyanlar, namazı kılanlar ve kendilerine verdiğimiz rızıktan hayır için gizli ve açık harcayanlar, asla batmayacak bir ticaret umarlar.'),
(3690, 35, 30, 'Ki (Allah), onlara ücretlerini tam ödesin ve lutfundan onlara fazlasını da versin. Çünkü O, çok bağışlayan, çok karşılık verendir.'),
(3691, 35, 31, 'Kitaptan sana vahyettiğimiz, kendinden öncekini doğrulayan gerçektir. Allah kulların(ın her halini) haber alandır, görendir.'),
(3692, 35, 32, 'Sonra Kitabı kullarımız arasından seçtiklerimize miras verdik. Onlardan kimi nefsine zulmedendir, kimi orta gidendir, kimi de Allah\'ın izniyle hayırlarda öne geçendir. İşte büyük lutuf budur.'),
(3693, 35, 33, 'Adn cennetleri... Oraya girerler; orada altın bilezikler ve inci(ler) takınırlar. Orada giysileri de ipektir.'),
(3694, 35, 34, 'Dediler ki: "Bizden tasayı gideren Allah\'a hamdolsun, doğrusu Rabbimiz çok bağışlayan, çok karşılık verendir."'),
(3695, 35, 35, 'O (Rab) ki lutfuyla bizi durulacak yurda kondurdu. Orada bize ne bir yorgunluk dokunur ve ne de orada bize bir usanç dokunur.'),
(3696, 35, 36, 'Nankörlere de cehennem ateşi vardır. (Orada) Onlara ne (ölümle) hükmedilir ki, ölsünler ve ne de onlardan cehennem azabı biraz hafifletilir. İşte biz her nankörü böyle cezalandırırız.'),
(3697, 35, 37, 'Onlar orada: "Rabbimiz, bizi çıkar, (önce) yaptığımızdan başkasını yapalım?" diye feryadederler. "Sizi, öğüt alacak olanın, öğüt alacağı kadar bir süre yaşatmadık mı? Size uyarıcı da geldi (fakat inanmadınız). Öyle ise (azabı) tadın artık. Zalimlerin yardımcısı yoktur."'),
(3698, 35, 38, 'Allah göklerin ve yerin gaybını bilendir. O, göğüslerin özünü bilir.'),
(3699, 35, 39, 'Sizi yeryüzünde halifeler (yöneticiler) yapan O\'dur. Artık kim nankörlük ederse nankörlüğü kendi zararınadır. Kafirlerin küfrü, Rableri yanında (kendilerine) gazabdan başka bir şey artırmaz; kafirlerin küfrü, (kendilerine) ziyandan başka bir şey artırmaz.'),
(3700, 35, 40, 'De ki: "Siz, Allah\'tan başka yalvardığınız şu tanrılarınızı gördünüz mü? Bana gösterin (bakayım), onlar yerden hangi şeyi yarattılar?" Yoksa onların, gökler(in yaratılmasın)da (Allah\'a) ortaklıkları mı var? Yoksa biz onlara (taptıkları putları bize ortak koşmalarını söyleyen) bir Kitap vermişiz de onlar o Kitaptan bir delil üzerinde mi bulunuyorlar? Hayır, o zalimler birbirlerine, aldatmadan başka bir şey va\'detmiyorlar.'),
(3701, 35, 41, 'Allah yıkılmamaları için gökleri ve yeri tutmaktadır. Andolsun, gökler ve yer yıkılsa, onları, Kendisinden başka hiç kimse tutamaz. Şüphesiz O, halimdir, çok bağışlayandır.'),
(3702, 35, 42, 'Andolsun eğer kendilerine bir uyarıcı (peygamber) gelirse, her milletten daha çok doğru yolda olacaklar diye, yeminlerinin bütün gücüyle Allah\'a yemin ettiler. Fakat kendilerine uyarıcı gelince, onlara Hak\'tan uzaklaşmaktan başka bir katkı sağlamadı.'),
(3703, 35, 43, 'Yeryüzünde büyüklük taslama(larını) ve kötü tuzak(lar) kurma(larını artırdı.) Kötü tuzak, ancak sahibine dolanır. Onlar öncekilerin yasasından başkasını mı bekliyorlar? Allah\'ın yasasında bir değişme bulamazsın; Allah\'ın yasasında bir sapma bulamazsın.'),
(3704, 35, 44, '(Bunlar,) Yeryüzünde hiç gez(ip dolaş)madılar mı ki kendilerinden öncekilerin sonunun nasıl olduğunu görsünler? Onlar, (bunlardan) daha güçlü idiler. Ne göklerde ne de yerde Allah\'ı engelleyecek bir şey var. O, bilendir, güçlüdür.'),
(3705, 35, 45, 'Eğer Allah, insanları yaptıkları işler yüzünden (hemen) cezalandıracak olsaydı, yeryüzünde hiçbir canlı bırakmazdı. Fakat Allah, onları belirtilmiş bir süreye kadar erteliyor. Süreleri geldiği zaman, kuşkusuz Allah kullarını görmektedir (onları yaptıkları işlere göre cezalandıracaktır).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Yasin'),
(3707, 36, 2, 'Hikmetli Kur\'an\'a andolsun.'),
(3708, 36, 3, 'Kuşkusuz sen gönderilmiş elçilerdensin.'),
(3709, 36, 4, 'Dosdoğru bir yol üzerinde,'),
(3710, 36, 5, 'Yani üstün ve çok esirgeyen Allah\'ın indirdiği (Kur\'an yolu) üzerindesin.'),
(3711, 36, 6, 'Babaları uyarılmamış, bu yüzden kendileri de gaflet içinde kalmış bir toplumu uyarman için (seni gönderdik).'),
(3712, 36, 7, 'Andolsun onların çoğuna o söz (cinlerden ve insanlardan bir kısmını cehenneme dolduracağım, sözü) hak oldu; artık onlar inanmazlar.'),
(3713, 36, 8, 'Biz onların boyunlarına halkalar geçirdik. Çenelere kadar dayanan o halkalar yüzünden kafaları kalkıktır.'),
(3714, 36, 9, 'Önlerinden bir sed ve arkalarından bir sed çektik de onları kapattık; artık görmezler.'),
(3715, 36, 10, 'Onları uyarsan da uyarmasan da onlar için birdir, inanmazlar.'),
(3716, 36, 11, 'Sen ancak Zikre uyan ve görmeden Rahman\'dan korkan kimseyi uyarabilirsin. İşte öylesini bir mağfiret ve güzel bir mükafatla müjdele.'),
(3717, 36, 12, 'Biziz, biz ki, ölüleri diriltiriz ve öne sürdükleri işleri ve bıraktıkları eserleri yazarız. Zaten biz, her şeyi apaçık bir kütüğe ayrıntılı olarak kaydetmişizdir.'),
(3718, 36, 13, 'Onlara elçilerin geldiği şu kent halkını misal olarak anlat:'),
(3719, 36, 14, 'Biz onlara iki elçi gönderdik, onları yalanladılar, biz de (elçileri) üçüncü biriyle destekledik. Dediler ki: "Biz size gönderilen elçileriz."'),
(3720, 36, 15, '(Kentliler) Dediler ki: "Siz de bizim gibi insandan başka bir şey değilsiniz. Rahman bir şey indirmemiştir. Siz sadece yalan söylüyorsunuz."'),
(3721, 36, 16, '(Elçiler) Dediler ki: "Rabbimiz bilir ki biz size gönderilmiş elçileriz."'),
(3722, 36, 17, 'Bizim üzerimize düşen, yalnız açıkça duyurmaktır.'),
(3723, 36, 18, '(Kentliler) Dediler ki: "Doğrusu biz sizin yüzünüzden uğursuzluğa uğradık. Eğer bu işten vazgeçmezseniz sizi mutlaka taşlarız ve bizden size acı bir azab dokunur."'),
(3724, 36, 19, '(Elçiler) Dediler ki: "Uğursuzluğunuz sizin kendinizdedir. Size öğüt verildiği için mi (uğursuzluğa uğruyorsunuz)? Hayır siz aşırı giden bir kavimsiniz."'),
(3725, 36, 20, 'Kentin en uzak yerinden bir adam koşarak geldi: "Ey kavmim, elçilere uyun." dedi.'),
(3726, 36, 21, 'Sizden bir ücret istemeyenlere uyun, onlar doğru yoldadırlar.'),
(3727, 36, 22, 'Ben niçin beni yaratana kulluk etmeyeyim? Siz de hep O\'na döndürüleceksiniz.'),
(3728, 36, 23, 'O\'ndan başka tanrılar edinir miyim hiç? Eğer O çok esirgeyen, bana bir zarar vermek dilese, onların şefa\'ati bana hiçbir yarar sağlamaz ve onlar beni kurtaramazlar."'),
(3729, 36, 24, 'O takdirde ben, apaçık bir sapıklık içinde olurum.'),
(3730, 36, 25, 'Ben sizin Rabbinize inandım, (gelin) beni dinleyin.'),
(3731, 36, 26, 'Ona: "Cennete gir" denilince: "Keşke, dedi, kavmim bilseydi.'),
(3732, 36, 27, 'Rabbimin beni bağışladığını ve beni ağırlananlardan kıldığını!"'),
(3733, 36, 28, 'Ondan sonra biz, kavminin üzerine gökten bir ordu indirmedik, indirici de değildik, (buna gerek yoktu).'),
(3734, 36, 29, 'Sadece korkunç bir gürültü oldu, hemen sönüverdiler.'),
(3735, 36, 30, 'Yazık şu kullara! Kendilerine gelen her elçi ile mutlaka alay ederlerdi.'),
(3736, 36, 31, 'Görmediler mi kendilerinden önce nice nesilleri yok ettik; onlar bir daha kendilerine dönüp gelmezler?'),
(3737, 36, 32, 'Ancak hepsi toplandığı zaman huzurumuza getirileceklerdir.'),
(3738, 36, 33, 'Ölü toprak, onlar için bir ayettir, (ölüleri nasıl dirilteceğimize işarettir): Biz onu dirilttik, ondan dane çıkardık da ondan yiyorlar.'),
(3739, 36, 34, 'Orada hurma ve üzüm bahçeleri yarattık; orada çeşmeler akıttık.'),
(3740, 36, 35, 'Ki o(suyun, yahut bahçe)nin ürününden ve ellerinin emeğinden yesinler. Hala şükretmiyorlar mı?'),
(3741, 36, 36, 'Ne yücedir O (Allah) ki toprağın bitirdiklerinden, kendilerinden ve daha bilmedikleri nice şeylerden olan bütün çiftleri yaratmıştır.'),
(3742, 36, 37, 'Gece de onlar için bir ayettir. Gündüzü ondan soyup, alırız, birden onlar karanlıkta kalıverirler.'),
(3743, 36, 38, 'Güneş de kendi müstekarrı (istikrarı veya istikrar bulacağı yer) için akıp gider. Bu, üstün ve bilen(Allah)ın takdiridir.'),
(3744, 36, 39, 'Aya da konaklar tayin ettik. Nihayet o, eski urcun(hurma salkımının sapın)a benzer bir hale geldi.'),
(3745, 36, 40, 'Ne güneş aya erişebilir, ne de gece, gündüzün önüne geçebilir. Hepsi bir felekte (yörüngede) yüzmektedirler.'),
(3746, 36, 41, 'Onlar için bir ayet de, onların çoçuklarını dolu gemide taşımamız,'),
(3747, 36, 42, 'Ve kendilerine onun gibi binecekleri nice şeyler yaratmamızdır.'),
(3748, 36, 43, 'Dilesek onları (suda) boğarız, ne kendilerine imdad (eden) olur, ne de kurtarılırlar.'),
(3749, 36, 44, 'Ancak bizden bir rahmet ve bir süreye kadar yaşatma vardır (acıyarak onları bir süre yaşatırız).'),
(3750, 36, 45, 'Onlara: "Önünüzdeki ve arkanızdaki (yani sizden önce geçen ve ileride sizi bekleyen) olaylardan sakının ki, esirgenesiniz," dendiği zaman (aldırmazlar).'),
(3751, 36, 46, 'Zaten, onlara Rabblerinin ayetlerinden hiçbir ayet gelmez ki ondan yüz çevirmiş olmasınlar.'),
(3752, 36, 47, 'Onlara: "Allah\'ın size verdiği rızıktan (Allah için) verin!" dendiği zaman, nankörler, inananlara: "Allah\'ın dilediği takdirde yedireceği bir kimseye biz mi yedirelim? Doğrusu siz, apaçık bir sapıklık içindesiniz." derler.'),
(3753, 36, 48, 'Ve: "Eğer doğru söylüyorsanız bu tehdid (ettiğiniz azab) ne zaman (gelecek)?" diyorlar.'),
(3754, 36, 49, 'Onların işi sadece korkunç bir sese bakar. Çekişip dururlarken ansızın o, kendilerini yakalar.'),
(3755, 36, 50, 'Artık ne bir tavsiye yapabilirler, ne de ailelerine dönebilirler.'),
(3756, 36, 51, 'Sur\'a üflendi. İşte onlar kabirlerden Rablerine koşuyorlar.'),
(3757, 36, 52, 'Dediler: "Vah bize, bizi yattığımız yerden kim kaldırdı? İşte Rahman\'ın va\'dettiği şey budur. Demek peygamberler doğru söylemiş!"'),
(3758, 36, 53, 'Sadece bir tek gürültü olur, hemen onların hepsi huzurumuza getirilirler.'),
(3759, 36, 54, 'O gün, hiç kimseye bir haksızlık yapılmaz ve siz ancak yaptığınızın cezasını çekersiniz.'),
(3760, 36, 55, 'O gün cennet halkı, bir iş içinde eğlenirler.'),
(3761, 36, 56, 'Kendileri ve eşleri, gölgelerde, koltuklara yaslanmışlardır.'),
(3762, 36, 57, 'Orada onlar için meyvalar ve istedikleri her şey vardır.'),
(3763, 36, 58, 'Çok esirgeyen Rabden (onlara) sözle selam (vardır).'),
(3764, 36, 59, 'Ey suçlular, bugün şöyle ayrılın!'),
(3765, 36, 60, 'Ey Adem oğulları, ben size and vermedim mi: Şeytana tapmayın o sizin apaçık düşmanınızdır. 	Did I not charge you, O ye sons of Adam, that ye worship not the devil - Lo! he is your open foe!'),
(3766, 36, 61, 'Bana tapın doğru yol budur diye?"'),
(3767, 36, 62, 'O, sizden birçok kuşağı saptırmıştı. Düşünmüyor muydunuz?'),
(3768, 36, 63, 'İşte size söylenen cehennem!'),
(3769, 36, 64, 'İnkarınızdan dolayı bugün oraya girin!'),
(3770, 36, 65, 'O gün ağızlarını mühürleriz, elleri bize söyler, ayakları yaptıklarına şahidlik eder.'),
(3771, 36, 66, 'Dilesek gözlerini silerdik de yola dökülürlerdi, ama nasıl görecekler?'),
(3772, 36, 67, 'Dilesek kılıklarını değiştirip onları oldukları yerde dondururduk, ne ileri gidebilir, ne geri dönebilirlerdi.'),
(3773, 36, 68, 'Kime uzun ömür versek, onun yaratılışını baş aşağı çevirir(gücünü azaltır)ız, (sonunda zayıflar, ihtiyarlar). Akıllarını kullanmıyorlar mı?'),
(3774, 36, 69, 'Biz ona (Muhammed\'e) şiir öğretmedik, (şiir) ona yakışmaz da. O(na vahyedilen) sadece bir öğüt ve apaçık bir Kur\'an\'dır.'),
(3775, 36, 70, '(Bu Kur\'an Muhammed\'e vahyedilmiştir) Ki, diri olanları uyarsın ve inkar edenlere de (azab) söz(ü) hak olsun.'),
(3776, 36, 71, 'Görmediler mi ellerimizin yaptıklarından kendilerine nice hayvanlar yarattık da kendileri onlara malik olmaktadırlar?'),
(3777, 36, 72, 'Onları kendilerine boyun eğdirdik, onlardan bazıları binekleridir, ve onlardan bazılarını da yerler.'),
(3778, 36, 73, 'Kendileri için onlarda daha birçok yararlar ve içecekler var. Hala şükretmiyorlar mı?'),
(3779, 36, 74, 'Belki kendilerine yardım edilir diye Allah\'tan başka tanrılar edindiler.'),
(3780, 36, 75, '(O tanrılar) Kendilerine yardım edemezler. Tersine kendileri onlar için hazırlanmış askerlerdir (Onları korumaktadırlar).'),
(3781, 36, 76, 'Onların sözü seni üzmesin. Biz onların gizlediklerini de açığa vurduklarını da biliyoruz.'),
(3782, 36, 77, 'İnsan, bizim kendisini nasıl bir nutfe(sperm)den yarattığımızı görmedi mi ki, şimdi apaçık bir hasım kesildi?'),
(3783, 36, 78, 'Kendi yaratılışını unutarak bize bir mesel verdi: "Şu çürümüş kemikleri kim diriltecek?" dedi.'),
(3784, 36, 79, 'De ki: "Onları ilk defa yaratan diriltecek. O, her yaratmayı bilir."'),
(3785, 36, 80, 'O size yeşil ağaçtan ateş yaptı da siz ondan yakıyorsunuz.'),
(3786, 36, 81, 'Gökleri ve yeri yaratan, onların benzerlerini yaratamaz mı? Elbette yaratır. O, çok bilen yaratıcıdır.'),
(3787, 36, 82, 'O\'nun işi, bir şeyi(n olmasını) istedi mi ona, sadece "ol!" demektir, hemen oluverir.'),
(3788, 36, 83, 'Yücedir O ki, her şeyin hükümranlığı O\'nun elindedir ve siz O\'na döndürüleceksiniz.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Andolsun o sıra sıra dizilenlere,'),
(3790, 37, 2, 'Bağırıp sürenlere,'),
(3791, 37, 3, 'Zikir okuyanlara,'),
(3792, 37, 4, 'Ki Tanrınız, birdir.'),
(3793, 37, 5, 'Göklerin, yerin ve bunlar arasında bulunanların Rabbi, doğuların da Rabbidir.'),
(3794, 37, 6, 'Biz en yakın göğü bir zinetle, yıldızlarla süsledik.'),
(3795, 37, 7, 'Ve (onu) ita\'at dışına çıkan her türlü şeytandan koruduk.'),
(3796, 37, 8, 'O (şeyta)nlar mele-i A\'layı (yüce melekler topluluğunu) dinleyemezler; her yandan kendilerine (ışınlar) atılır.'),
(3797, 37, 9, 'Kovulurlar. Onlar için sürekli bir azab vardır.'),
(3798, 37, 10, 'Yalnız (yüce topluluktan) bir söz kapan olursa, onu da delici bir şihab (ışın)izler.'),
(3799, 37, 11, 'Şimdi onlara sor: Yaratılış bakımından kendileri mi daha çetin, yoksa bizim yarattıklarımız mı? Biz kendilerini yapışkan bir çamurdan yarattık.'),
(3800, 37, 12, 'Hayır sen (bu muhteşem kudrete) hayran kaldın; onlarsa (seninle) alay ediyorlar.'),
(3801, 37, 13, 'Kendilerine öğüt verilse öğüt almıyorlar.'),
(3802, 37, 14, 'Bir mu\'cize görseler, alay ediyorlar.'),
(3803, 37, 15, 'Bu apaçık bir büyüden başka bir şey değildir. diyorlar.'),
(3804, 37, 16, 'Yani biz öldüğümüz, toprak ve kemik olduğumuz zaman mı, biz mi diriltilecek mişiz?'),
(3805, 37, 17, 'Evvelki atalarımız da mı?'),
(3806, 37, 18, 'De ki: "Evet siz aşağılanarak (diriltileceksiniz)!"'),
(3807, 37, 19, 'O (iş) sadece korkunç bir sesten ibarettir: Hemen onlar (diriltilmiş olarak) bakıyorlardır.'),
(3808, 37, 20, 'Vah bize, bu ceza günüdür! dediler.'),
(3809, 37, 21, 'Bu, yalanlamakta olduğunuz hüküm günüdür!'),
(3810, 37, 22, '(Yüce Allah meleklerine emreder): "Toplayın o zalimleri, onların eşlerini ve taptıklarını."'),
(3811, 37, 23, 'Allah\'tan başka. Onları cehennemin yoluna götürün!'),
(3812, 37, 24, 'Durdurun onları, çünkü onlar sorguya çekileceklerdir.'),
(3813, 37, 25, 'Size ne oldu ki birbirinize yardım etmiyorsunuz?'),
(3814, 37, 26, '(Başları öne eğik, utançtan yüzleri kızarmış. Cevap verecek durumda değillerdir). Hayır, onlar o gün teslim olmuşlardır.'),
(3815, 37, 27, 'Birbirlerine döndüler, soruyorlar.'),
(3816, 37, 28, '(Uyanlar, uydukları adamlara) Dediler ki: "Siz bize sağdan gelir(güvendiğimiz yandan bize sokulup vesvese verir)diniz."'),
(3817, 37, 29, '(Ötekiler de): "Hayır, dediler, zaten siz kendiniz inanan insanlar değildiniz."'),
(3818, 37, 30, 'Bizim sizi zorlayacak bir gücümüz yoktu. Siz kendiniz azgın bir toplum idiniz.'),
(3819, 37, 31, 'Artık Rabbimizin sözü bize hak oldu. Biz (hak ettiğimiz cezayı mutlaka) tadacağız!'),
(3820, 37, 32, 'Sizi azdırdık, çünkü biz kendimiz azmıştık(siz de bize uyunca azmış oldunuz).'),
(3821, 37, 33, 'O gün onlar azab (çekme)de ortaktırlar.'),
(3822, 37, 34, 'İşte biz, suçlulara böyle yaparız.'),
(3823, 37, 35, 'Çünkü onlara: "Allah\'tan başka tanrı yoktur!" dendiği zaman büyüklük taslarlardı.'),
(3824, 37, 36, 'Cinlenmiş bir şair için biz tanrılarımızı mı terk edeceğiz? derlerdi.'),
(3825, 37, 37, 'Hayır, o (ne şairdi, ne mecnun. O) gerçeği getirmiş ve elçileri de doğrulamıştı.'),
(3826, 37, 38, 'Siz acı azabı tadacaksınız!'),
(3827, 37, 39, 'Sadece yaptığınız (işler)le cezalanıyorsunuz!'),
(3828, 37, 40, 'Ancak Allah\'ın halis kulları bu cezanın dışındadır.'),
(3829, 37, 41, 'Onlar için bilinen bir rızık vardır.'),
(3830, 37, 42, '(Türlü türlü) Meyvalar. Ve onlar ağırlanırlar.'),
(3831, 37, 43, 'Ni\'met cennetlerinde.'),
(3832, 37, 44, 'Tahtlar üzerinde, karşılıklı otururlar.'),
(3833, 37, 45, 'Önlerinde akan kaynaktan (doldurulmuş) kadehler dolaştırılır.'),
(3834, 37, 46, 'Berrak, içenlere lezzet veren bir içki.'),
(3835, 37, 47, 'Onda ne sersemletme var, ne onunla sarhoş olurlar.'),
(3836, 37, 48, 'Yanlarında da, yalnız kendilerine göz dikmiş iri gözlü eşler vardır.'),
(3837, 37, 49, 'Saklı yumurta gibi bembeyaz eşler.'),
(3838, 37, 50, 'Bunlar birbirine dönmüş soruyorlar:'),
(3839, 37, 51, 'Onlardan bir sözcü: "Benim, dedi, bir arkadaşım vardı."'),
(3840, 37, 52, 'Derdi ki: \'Sen doğrulayanlardan mısın?'),
(3841, 37, 53, 'Biz ölüp toprak ve kemik olduğumuz zaman mı, biz mi (diriltilip yaptığımız işlere göre) cezalanacağız?\' "'),
(3842, 37, 54, '(Sonra yanındakilere): "Bakar mısınız?" dedi.'),
(3843, 37, 55, 'Baktı onu cehennemin ortasında gördü.'),
(3844, 37, 56, 'Tallahi, dedi, sen az daha beni de alçaltacaktın.'),
(3845, 37, 57, 'Rabbimin ni\'meti olmasaydı, şimdi ben de (oraya) getirilenlerden olurdum.'),
(3846, 37, 58, 'Biz bir daha ölmeyecek miyiz der.'),
(3847, 37, 59, 'Yalnız ilk ölümümüz, başka ölüm yok ve biz azaba da uğratılmayacağız ha?!'),
(3848, 37, 60, 'Gerçekten büyük başarı ve mutluluk budur!'),
(3849, 37, 61, 'Çalışanlar bunun için çalışsınlar.'),
(3850, 37, 62, '(Nasıl) Ağırlanmak için bu mu hayırlı, yoksa zakkum ağacı mı?'),
(3851, 37, 63, 'Biz onu zalimler için bir fitne (sınav) yaptık.'),
(3852, 37, 64, 'O, cehennemin dibinde çıkan bir ağaçtır.'),
(3853, 37, 65, 'Tomurcukları, şeytanların başları gibidir.'),
(3854, 37, 66, 'Onlar ondan yiyecekler ve karınlarını onunla dolduracaklardır.'),
(3855, 37, 67, 'Sonra onların, bunun üzerine kaynar su karıştırılmış bir içkileri vardır.'),
(3856, 37, 68, 'Sonra dönecekleri yer, elbette cehennemdir.'),
(3857, 37, 69, 'Çünkü onlar babalarını sapık kimseler buldular.'),
(3858, 37, 70, 'Kendileri de onların izlerinde koşturuyorlar.'),
(3859, 37, 71, 'Andolsun, onlardan önce, evvelkilerin çoğu da sapmıştı.'),
(3860, 37, 72, 'Biz onların içine de uyarıcılar göndermiştik.'),
(3861, 37, 73, 'Bak, o uyarılanların sonu nice oldu.'),
(3862, 37, 74, 'Ancak Allah\'ın halis kulları o azabın dışında kaldılar.'),
(3863, 37, 75, 'Andolsun Nuh bize yalvarmıştı da ne güzel kabul buyurmuştuk!'),
(3864, 37, 76, 'Onu ve ailesini büyük sıkıntıdan kurtarmıştık.'),
(3865, 37, 77, 'Yalnız onun zürriyetini kalıcılar yaptık (onlardan başka hepsini helak ettik).'),
(3866, 37, 78, 'Sonra gelenler arasında ona (iyi bir ün) bıraktık:'),
(3867, 37, 79, 'Alemler içinde Nuh\'a selam olsun (bütün insanlar onu esenlikle anarlar).'),
(3868, 37, 80, 'İşte biz güzel davrananları böyle mükafatlandırırız.'),
(3869, 37, 81, 'Çünkü o bizim, inanan kullarımızdandı.'),
(3870, 37, 82, 'Sonra ötekilerini suda boğduk.'),
(3871, 37, 83, 'İbrahim de onun kolundan idi.'),
(3872, 37, 84, 'Zira Rabbine tertemiz bir kalb getirmişti.'),
(3873, 37, 85, 'Babasına ve kavmine: "Neye tapıyorsunuz?" demişti.'),
(3874, 37, 86, 'Allah\'tan başka uydurma tanrılar mı istiyorsunuz?'),
(3875, 37, 87, 'Alemlerin Rabbi hakkında zannınız nedir (ki O\'na böyle ortaklar koştunuz)?'),
(3876, 37, 88, 'Yıldızlara bir göz attı:'),
(3877, 37, 89, 'Ben hastayım, dedi.'),
(3878, 37, 90, 'Bunun üzerine arkalarını dönüp ondan kaçtılar.'),
(3879, 37, 91, 'O da gizlice onların tanrılarına sokuldu: "Yemez misini?" dedi.'),
(3880, 37, 92, 'Neyiniz var ki konuşmuyorsunuz?'),
(3881, 37, 93, 'Ve gizlice üzerlerine yürüyüp sağ eliyle onlara kuvvetli bir darbe indirdi.'),
(3882, 37, 94, '(Puta, tapanlar, döndüklerinde putlarını kırılmış görünce) Hemen koşarak ona gittiler.'),
(3883, 37, 95, '(Elinizle) Yonttuğunuz şeylere mi tapıyorsunuz? dedi.'),
(3884, 37, 96, 'Oysa sizi de, yaptığınız(bu şeyler)i de Allah yaratmıştır.'),
(3885, 37, 97, 'Onun için bir bina yapın da onu (o binada) ateşe atın dediler.'),
(3886, 37, 98, 'Ona bir tuzak kurmak istediler, biz de (onların tuzaklarını boşa çıkardık), onları alçak düşürdük.'),
(3887, 37, 99, '(İbrahim) Dedi ki: "Ben Rabbime gideceğim, O, beni doğru yola iletecek."'),
(3888, 37, 100, 'Rabbim, bana iyilerden (bir çocuk) lutfet!'),
(3889, 37, 101, 'Ona halim bir erkek çocuk müjdeledik.'),
(3890, 37, 102, '(Çocuk) Onun yanında koşma çağına erişince (İbrahim ona): "Yavrum, dedi, ben uykuda görüyorum ki ben seni kesiyorum; (düşün) bak, ne dersin?" (Çocuk): "Babacığım, sana emredileni yap, inşallah beni sabredenlerden bulacaksın." dedi.'),
(3891, 37, 103, 'İkisi de böylece (Allah\'ın emrine) teslim olup (İbrahim, kurban etmek için) çocuğu alnı üzerine yıkınca,'),
(3892, 37, 104, 'Biz ona: "İbrahim!" diye ünledik.'),
(3893, 37, 105, 'Sen rüyayı doğruladın, işte biz, güzel davrananları böyle mükafatlandırırız!'),
(3894, 37, 106, 'Gerçekten bu, apaçık bir sınav idi.'),
(3895, 37, 107, 'Ve fidye olarak ona büyük bir kurbanlık verdik.'),
(3896, 37, 108, 'Sonra gelenler arasında ona (iyi bir ün) bıraktık.'),
(3897, 37, 109, '(İleride gelecek nesiller): "İbrahim\'e selam olsun!" (diyeceklerdi.)'),
(3898, 37, 110, 'İşte biz güzel davrananları böyle mükafatlandırırız.'),
(3899, 37, 111, 'Çünkü o bizim mü\'min kullarımızdandı.'),
(3900, 37, 112, 'Biz ona İshak\'ı, iyilerden bir peygamber olarak müjdeledik.'),
(3901, 37, 113, 'Kendisine de, İshak\'a da bereketler verdik. Onların neslinden (gelenler arasında) iyi hareket eden de var, açıkça kendisine zulmeden de.'),
(3902, 37, 114, 'Andolsun Musa\'ya ve Harun\'a da lutuflarda bulunduk.'),
(3903, 37, 115, 'Onları ve kavimlerini büyük sıkıntıdan kurtardık.'),
(3904, 37, 116, 'Onlara yardım ettik de üstün gelenler kendileri oldular.'),
(3905, 37, 117, 'Onlara açık ifadeli Kitabı verdik.'),
(3906, 37, 118, 'Ve onları doğru yola ilettik.'),
(3907, 37, 119, 'Ve sonra gelenler arasında onlara (iyi bir ün) bıraktık.'),
(3908, 37, 120, '(Hep): "Musa\'ya ve Harun\'a selam olsun!" (diyeceklerdi).'),
(3909, 37, 121, 'İşte biz güzel davrananları böyle mükafatlandırırız.'),
(3910, 37, 122, 'Çünkü ikisi de bizim inanan kullarımızdandı.'),
(3911, 37, 123, 'İlyas da elçilerdendi.'),
(3912, 37, 124, 'Kavmine demişti ki: "(Allah\'ın azabından) Korunmaz mısınız?"'),
(3913, 37, 125, 'Ba\'l\'e yalvarıyorsunuz da, bırakıyor musunuz, yaratıcıların en güzelini?"'),
(3914, 37, 126, 'Sizin Rabbiniz ve önceki atalarınızın Rabbi olan Allah\'ı?'),
(3915, 37, 127, 'Onu yalanladılar, bundan dolayı onlar (azaba) getirileceklerdir.'),
(3916, 37, 128, 'Yalnız Allah\'ın halis kulları azab dışındadırlar.'),
(3917, 37, 129, 'Biz, sonra gelenler arasında ona (İlyas\'a da iyi bir ün) bıraktık:'),
(3918, 37, 130, 'İlyas\'a selam olsun.'),
(3919, 37, 131, 'İşte biz güzel davrananları böyle mükafatlandırırız.'),
(3920, 37, 132, 'Çünkü o bizim mü\'min kullarımızdandı.'),
(3921, 37, 133, 'Lut da gönderilen elçilerdendi.'),
(3922, 37, 134, 'Onu ve ailesini kurtardık.'),
(3923, 37, 135, 'Yalnız (azabda) kalacaklar arasında bulunan acuze bir kadın hariç.'),
(3924, 37, 136, 'Sonra ötekileri kırdık (geçirdik).'),
(3925, 37, 137, 'Siz onların yanlarından geçip gidiyorsunuz; sabahleyin,'),
(3926, 37, 138, 'Ve geceleyin. Düşünmüyor musunuz?'),
(3927, 37, 139, 'Yunus da gönderilen elçilerdendi.'),
(3928, 37, 140, 'Dolu gemiye kaçmıştı.'),
(3929, 37, 141, '(Yükü fazla oluğundan gemi taşıyamamış, yolculardan birini denize atmak gerekmişti. Birini atmak üzere gemidekilerle) Kur\'a çekti. (Yunus) Yenilenlerden oldu. (Kur\'a kendisine isabet etti).'),
(3930, 37, 142, '(Yunus, Rabbinden izinsiz olarak kavminden ayrıldığı için) Kendi kendisini kınarken (denize attılar) balık onu yuttu.'),
(3931, 37, 143, 'Eğer tesbih edenlerden olmasaydı,'),
(3932, 37, 144, '(İnsanların) Yeniden diriltilecekleri güne kadar balığın karnında kalırdı.'),
(3933, 37, 145, '(Ama balığın karnında bizi andı, tesbih etti, biz de) Onu hasta bir halde ağaçsız, çıplak bir yere attık.'),
(3934, 37, 146, 'Ve üzerine (gölge yapması için) Bir asma kabak ağacı bitirdik.'),
(3935, 37, 147, 'Ve onu yüz bin insana, ya da daha fazla olanlara elçi gönderdik.'),
(3936, 37, 148, 'İnandılar, biz de onları bir süreye kadar geçindirdik.'),
(3937, 37, 149, 'Şimdi onlara sor: Rabbine kızlar, onlara da oğlanlar mı?'),
(3938, 37, 150, 'Yoksa biz melekleri, onların gözleri önünde dişi mi yarattık (ki meleklerin dişi olduğunu söylüyorlar)?'),
(3939, 37, 151, 'İyi bilin, onlar iftiraları yüzünden diyorlar ki:'),
(3940, 37, 152, 'Allah doğurdu. Onlar elbette yalancıdırlar.'),
(3941, 37, 153, '(Allah) Kızları seçip oğlanlara tercih mi etmiş?'),
(3942, 37, 154, 'Size ne oldu, nasıl hüküm veriyorsunuz?'),
(3943, 37, 155, 'Hiç mi düşünmüyorsunuz?'),
(3944, 37, 156, 'Yoksa sizin, (meleklerin, Allah\'ın kızları oldukları hakkında) açık bir deliliniz mi var?'),
(3945, 37, 157, 'Eğer doğru iseniz Kitabınızı getirin.'),
(3946, 37, 158, 'Allah ile cinler arasında bir nesep, (bir soy bağlantısı) uydurdular. Oysa cinler de kendilerinin (yüce divana) getirileceklerini bilmişlerdir.'),
(3947, 37, 159, 'Haşa Allah, onların taktıkları sıfatlardan (münezzehtir), yücedir.'),
(3948, 37, 160, 'Fakat Allah\'ın temiz kulları hariç (onlar azaba sokulmayacaklardır).'),
(3949, 37, 161, '(Ey inkarcılar) Ne siz, ne de taptıklarınız,'),
(3950, 37, 162, 'Kandırıp Allah\'ın yolundan çıkaramazsınız;'),
(3951, 37, 163, 'Cehenneme girecek olandan başkasını.'),
(3952, 37, 164, 'Bizden herkesin belli bir makamı vardır.'),
(3953, 37, 165, 'Biziz, o saf saf dizilenler, biz.'),
(3954, 37, 166, 'Biziz, o tesbih edenler, biz.'),
(3955, 37, 167, 'Gerçi o(ortakkoşa)nlar şöyle diyorlardı:'),
(3956, 37, 168, 'Eğer yanımızda öncekiler(e gelen Kitap\'lar)dan bir uyarı olsaydı.'),
(3957, 37, 169, 'Elbette biz, Allah\'ın halis kulları olurduk!'),
(3958, 37, 170, 'Ama o uyarıyı inkar ettiler, yakında (inkar etmelerinin sonunun nasıl olacağını) bileceklerdir.'),
(3959, 37, 171, 'Gönderilen elçi kullarımıza şu sözümüz geçmişti:'),
(3960, 37, 172, 'Mutlaka zafere ulaştırılanlar kendileri olacaktır.'),
(3961, 37, 173, 'Ve galip gelenler, mutlaka bizim ordumuz olacaktır!'),
(3962, 37, 174, 'Bir süreye kadar onlardan dön (onların sözlerine aldırış etme).'),
(3963, 37, 175, 'Onları gözetle. Yakında (başlarına neler geleceğini) göreceklerdir.'),
(3964, 37, 176, 'Bizim azabımızı mı acele istiyorlar?'),
(3965, 37, 177, 'Fakat o azab yurtlarına indiği zaman uyarılmış olanların sabahı ne kötü olur!'),
(3966, 37, 178, 'Bir süreye kadar onları kendi hallerine bırak.'),
(3967, 37, 179, 'Ve (bekle de) gör, onlar da göreceklerdir.'),
(3968, 37, 180, 'Kudret ve şeref sahibi Rabbin, onların nitelendirmelerinden yücedir.'),
(3969, 37, 181, 'Selam, gönderilen elçilere,'),
(3970, 37, 182, 'Hamd, alemlerin Rabbi Allah\'a!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Sad, (uyarıcı) ve şanlı Kur\'an\'a andolsun ki,'),
(3972, 38, 2, 'İnkar edenler bir gurur ve ayrılık içindedirler.'),
(3973, 38, 3, 'Onlardan önce nice nesilleri helak ettik de feryad ettiler; fakat artık kurtuluş zamanı geçmişti.'),
(3974, 38, 4, 'Onlara kendilerinden bir uyarıcı (peygamber) gelmesine hayret ettiler de o kafirler dediler ki: "Bu yalancı bir sihirbazdır."'),
(3975, 38, 5, 'Tanrıları bir tek tanrı mı yaptı? Bu, cidden tuhaf bir şeydir.'),
(3976, 38, 6, 'Onlardan bir grup fırladı: "Yürüyün tanrılarınıza bağlı kalın. Çünkü bu, arzu edilen bir şeydir."'),
(3977, 38, 7, 'Biz bu(nun söylediği)ni (babalarımızın bağlı olduğu) öteki dinde işitmedik. Bu uydurmadan başka bir şey değildir.!'),
(3978, 38, 8, 'O Zikr (uyarı, başka kimse kalmadı da) aramızdan ona mı indirildi? Doğrusu, onlar benim Zikr\'imden yana şüphe içindedirler. Hayır, onlar henüz azabımı tadmadılar!..'),
(3979, 38, 9, 'Yoksa daima üstün olan, çok lutufta bulunan Rabbinin rahmet hazineleri onların yanında mı?'),
(3980, 38, 10, 'Yoksa göklerin, yerin ve ikisi arasında bulunanların mülkü onların mı? Öyleyse sebepler (vasıtalar) içinde yükselsinler (vasıtalara binip göklere çıksınlar da oradan alemi yönetsinler, vahyi de kendi isteklerine göre indirsinler).'),
(3981, 38, 11, '(Onlar) Şurada bozguna uğratılacak derme çatma bir ordudur.'),
(3982, 38, 12, 'Onlardan önce de Nuh kavmi, Ad (kavmi) ve kazıklar sahibi (temelleri kazık gibi yere çakılmış, yüksek pramitler yaptıran) Fir\'avn da yalanlamıştı.'),
(3983, 38, 13, 'Semud (kavmi), Lut kavmi ve Eyke halkı da (böyle yapmıştı). İşte onlar da (peygamberlere karşı birleşik) kabilelerdi.'),
(3984, 38, 14, 'Hepsi de elçileri yalanladılar, benim cezamı hak ettiler.'),
(3985, 38, 15, 'Bunlar(ın işi) de sadece geri dönmesi olmayan bir na\'raya bakıyor.'),
(3986, 38, 16, '(Alay ederek) Dediler ki: "Rabbimiz, bizim (azab) payımızı hesap gününden önce, hemen ver."'),
(3987, 38, 17, 'Onların dediklerine sabret de güçlü kulumuz Davud\'u an; çünkü o (bize) çok başvururdu.'),
(3988, 38, 18, 'Biz dağları onunla beraber (tesbih etmeleri için) boyun eğdirmiştik; akşam sabah onunla tesbih ederler (onun yaptığı tesbihle çınlarlar)dı.'),
(3989, 38, 19, 'Toplanıp gelen kuşları da (ona ram etmiştik). Hepsi onun nağmesine katılır (beraber tesbih ederler)di.'),
(3990, 38, 20, 'Onun mülkünü güçlendirmiştik, kendisine hikmet (peygamberlik, yüksek bilgi, hakkı batıldan ayırma, davaları çözme) ve açık, güzel konuşma (yeteneği) vermiştik.'),
(3991, 38, 21, 'Sana davacıların haberi geldi mi? Hani odasının duvarına tırmanmışlardı,'),
(3992, 38, 22, 'Davud\'un yanına girmişlerdi de (Davud) onlardan korkmuştu: "Korkma, dediler, biz iki davacıyız. Birimiz, ötekinin hakkına saldırdı. Şimdi sen aramızda hak ile hükmet, (adaletten ayrılıp bize) zulmetme. Bizi yolun ortasına (adalete) götür."'),
(3993, 38, 23, 'Bu kardeşimin doksan dokuz koyunu var. Benimse bir tek koyunum var. Böyle iken Onu da bana ver" dedi ve konuşmada bana ağır bastı (onunla baş edemedim.)"'),
(3994, 38, 24, '(Davud) dedi ki: "And olsun (o) senin, koyununu kendi koyunlarına katmayı istemekle sana zulmetmiştir. Zaten (mallarını birbirine) karıştıran(ortak)ların çoğu birbirine zulmederler. Yalnız inanıp iyi işler yapanlar bunun dışındadır ki, onlar da ne kadar azdır!" Davud, (bu hükümle) kendisini denediğimizi (kendisine bir bela vereceğimizi) sandı da Rabbinden mağfiret diledi, eğilerek secdeye kapandı ve tevbe edip (bize) döndü.'),
(3995, 38, 25, 'Biz de ondan bunu affettik. Yanımızda onun bir yakınlığı ve güzel bir geleceği vardır.'),
(3996, 38, 26, 'Ey Davud, biz seni yeryüzünde (senden öncekilerin yerine) hükümdar yaptık. İnsanlar arasında adaletle hükmet; keyf(in)e uyma, sonra seni Allah\'ın yolundan saptırır. Allah\'ın yolundan sapanlar ise, hesap gününü unuttuklarından dolayı, çetin azaba uğrayacaklardır.'),
(3997, 38, 27, 'Göğü, yeri ve ikisi arasındakileri boş yere yaratmadık (bunlar bir tesadüf eseri değildir) bu, inkar edenlerin zannıdır, (onlar kainatın boş bir tesadüf eseri olduğunu söylerler). Ateşten vay hallerine o nankörlerin!'),
(3998, 38, 28, 'Yoksa biz, inanıp iyi işler yapanları, yeryüzünde bozgunculuk yapanlar gibi mi tutacağız? Yoksa korunanları yoldan çıkanlar gibi mi tutacağız?'),
(3999, 38, 29, 'Sana (bu) mübarek Kitabı indirdik ki ayetlerini düşünsünler ve sağduyu sahipleri öğüt alsınlar.'),
(4000, 38, 30, 'Biz Davud\'a Süleyman\'ı armağan ettik, (Süleyman) ne güzel kuldu! Hep Allah\'a başvururdu.'),
(4001, 38, 31, 'Akşam üstü kendisine safin (görkemli) hızlı koşan (saf kan Arap) atları gösterilmişti.'),
(4002, 38, 32, 'Ben, dedi, mal sevgisini, Rabbimi anmaktan (ötürü) tercih ettim. Nihayet bu atlar perde ile gizlendi (koşup dağın arkasına düşmekle gözden kayboldu).'),
(4003, 38, 33, 'Onları bana getirin (dedi), bacaklarını ve boyunlarını okşamağa başladı.'),
(4004, 38, 34, 'Andolsun Süleyman\'ı denedik: Tahtının üstüne bir ceset bıraktık, sonra (bize) yöneldi.'),
(4005, 38, 35, 'Rabbim, dedi, beni affet, bana, benden sonra hiç kimseye nasib olmayan bir mülk (hükümdarlık) ver. Çünkü Sensin o çok lutfeden, Sen!"'),
(4006, 38, 36, 'Biz, rüzgarı ona boyun eğdirdik. Onun buyruğuyla, onun istediği yere tatlı tatlı eserdi.'),
(4007, 38, 37, 'Ve şeytanları; her bina ustasını ve dalgıcı,'),
(4008, 38, 38, 'Ve zincirlerle birbirine bağlanmış başka (şeytan)ları.'),
(4009, 38, 39, 'Bu bizim ihsanımızdır. Artık dilediğine ver veya verme, hesapsızdır. (dedik).'),
(4010, 38, 40, 'Onun için, bizim yanımızda bir yakınlık ve güzel bir gelecek de vardır.'),
(4011, 38, 41, 'Kulumuz Eyyub\'u da an: (O) Rabbine "Şeytan, bana bir yorgunluk ve azab dokundurdu" diye seslenmişti.'),
(4012, 38, 42, 'Ayağını (yere) vur, işte yıkanacak ve içilecek serin (bir su), (dedik). 	(And it was said unto him): Strike the ground with thy foot. This (spring) is a cool bath and a refreshing drink.'),
(4013, 38, 43, 'Ona bizden bir rahmet ve sağduyu sahiplerine bir ibret olarak ailesini ve onlarla beraber bir eşini daha armağan ettik.'),
(4014, 38, 44, '(Dedik ki): "Eline bir demet sap al, onunla vur da yeminini bozma." Gerçekten biz onu sabreden (bir kul) bulmuştuk. Ne güzel kuldu, o daima (bize) başvururdu.'),
(4015, 38, 45, 'Kuvvetli ve basiretli kullarımız İbrahim\'i, İshak\'ı ve Ya\'kub\'u da an.'),
(4016, 38, 46, 'Biz onları ahiret yurdunu düşünme özelliğiyle temizleyip, kendimize halis (kul) yaptık.'),
(4017, 38, 47, 'Onlar bizim yanımızda seçkinlerden, hayırlılardandır.'),
(4018, 38, 48, 'İsma\'il\'i, Elyesa\'ı, Zülkifil\'i de an. Hepsi de iyilerdendir.'),
(4019, 38, 49, 'Bu, bir hatırlamadır. Korunanlar için güzel bir gelecek vardır:'),
(4020, 38, 50, 'Kapıları kendilerine açılmış Adn cennetleri.'),
(4021, 38, 51, 'Orada (koltuklara) yaslanarak bir çok meyva ve içki isterler.'),
(4022, 38, 52, 'Yanlarında da bakışlarını yalnız (kocalarına) diken (kendileriyle) yaşıt dilberler vardır.'),
(4023, 38, 53, 'İşte, hesap günü için size söz verilen budur!'),
(4024, 38, 54, 'Doğrusu bizim bu rızkımızın bitip tükenmesi yoktur!'),
(4025, 38, 55, 'Bu böyledir; fakat azgınlara da en kötü bir gelecek vardır:'),
(4026, 38, 56, 'Cehennem! Oraya girerler. Ne kötü bir döşektir o!'),
(4027, 38, 57, 'İşte onu tadsınlar: Kaynar ve kokuşmuş sudur!'),
(4028, 38, 58, 'Ve daha başka çeşit çeşit (azab) vardır.'),
(4029, 38, 59, 'İşte şunlar da sizinle beraber (cehenneme) girecek olanlardır: "Onlara merhaba yok, (yerleri geniş olmasın, rahat yüzü görmesinler)! Onlar ateşe gireceklerdir."'),
(4030, 38, 60, '(Uyanlar, uyulanlara) Dediler ki: "Hayır, asıl size merhaba yok, (asıl siz rahat yüzü görmeyin), siz bunu bizim önümüze getirdiniz. Ne kötü durak (bu)!"'),
(4031, 38, 61, '(Ve hepsi birbiri aleyhine du\'a ederek): "Rabbimiz, bunu bizim önümüze kim getirdiyse onun ateşteki azabını bir kat daha artır!" dediler.'),
(4032, 38, 62, 'Bize ne oldu ki, (dünyada) kötülerden saydığımız adamları (burada) görmüyoruz? dediler.'),
(4033, 38, 63, 'Hani onlarla alay ederdik. Yoksa gözler(imiz) mi onlardan kaydı, (onları gözden mi kaçırdık)?'),
(4034, 38, 64, 'Bu, mutlaka gerçektir, ateş halkının tartışmasıdır (bunun olacağından asla şüphe yoktur).'),
(4035, 38, 65, 'De ki: "Ben ancak bir uyarıcıyım. Tek ve (her şeyi) kahreden Allah\'tan başka tanrı yoktur."'),
(4036, 38, 66, 'O, göklerin, yerin ve ikisi arasındakilerin Rabbidir, daima üstündür, çok bağışlayandır.'),
(4037, 38, 67, 'De ki: "O, büyük bir haberdir."'),
(4038, 38, 68, '(Ama gafletinizden dolayı) Siz ondan yüz çeviriyorsunuz.'),
(4039, 38, 69, 'Yüce topluluk tartışırlarken (aralarında) neler geçtiği hakkında bir bilgim yoktu.'),
(4040, 38, 70, 'Ben ancak apaçık bir uyarıcı olduğum için (bu bilgi) bana vahyediliyor.'),
(4041, 38, 71, 'Rabbin meleklere demişti ki: "Ben çamurdan bir insan yaratacağım."'),
(4042, 38, 72, 'Onu biçimlendirip ona ruhumdan üflediğim zaman derhal ona secdeye kapanın!'),
(4043, 38, 73, 'Meleklerin hepsi tüm olarak secde ettiler.'),
(4044, 38, 74, 'Yalnız İblis etmedi, büyüklük tasladı ve kafirlerden oldu.'),
(4045, 38, 75, '(Rabbin ona) Dedi ki: "Ey İblis, iki elimle yarattığıma secde etmekten seni alıkoyan nedir? Büyüklük mü tasladın, yoksa yücelerden mi oldun?"'),
(4046, 38, 76, 'Dedi: "Ben ondan iyiyim. Beni ateşten, onu çamurdan yarattın."'),
(4047, 38, 77, 'Buyurdu ki: "Haydi çık oradan, sen kovuldun!"'),
(4048, 38, 78, 'Ta ceza gününe kadar lanetim üzerinedir!'),
(4049, 38, 79, 'Rabbim, dedi, öyleyse yeniden dirilecekleri güne kadar bana süre ver.'),
(4050, 38, 80, 'Buyurdu: "Haydi sen süre verilenlerdensin."'),
(4051, 38, 81, 'O belli vaktin gününe kadar. 	Until the day of the time appointed.'),
(4052, 38, 82, '(İblis) Dedi: "Senin izzet ve şerefine and olsun ki, onların tümünü azdıracağım."'),
(4053, 38, 83, 'Yalnız onlardan ihlaslı kulların(a dokunmayacağım).'),
(4054, 38, 84, 'Buyurdu ki: "Gerçektir (sen benim halis kullarımı kandıramazsın), ve ben gerçek olarak diyorum ki:'),
(4055, 38, 85, 'Senden ve onlar içinde sana uyan kimselerden (gelenler ile) cehennemi dolduracağım!'),
(4056, 38, 86, 'De ki: "Ben sizden buna karşı bir ücret istemiyorum. Ve ben yapmacık yapanlardan, (uydurma şeylerle peygamberlik taslayanlardan) değilim."'),
(4057, 38, 87, 'O (Kur\'an), ancak bütün alemlere öğüttür.'),
(4058, 38, 88, 'Bir süre sonra "Onun haberi(nin doğruluğu)nu gayet iyi bileceksiniz!"');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Kitabını indirilmesi, aziz hüküm ve hikmet sahibi Allah tarafındandır.'),
(4060, 39, 2, 'Biz bu Kitabı sana hak ile indirdik; sen dini yalnız Allah\'a halis kılarak O\'na kulluk et.'),
(4061, 39, 3, 'İyi bil ki, halis din yalnız Allah\'ındır. O\'ndan başka veliler edinerek: "Biz bunlara, sırf bizi Allah\'a yaklaştırmaları için tapıyoruz," diyenler(e gelince): Şüphesiz ki Allah, onlar arasında, ayrılığa düştükleri konuda hükmünü verecektir. Allah, yalancı, nankör insanı doğru yola iletmez.'),
(4062, 39, 4, 'Eğer Allah çocuk edinmek isteseydi, yarattıklarından dilediğini seçerdi. O (bundan münezzehtir) yücedir. O tek ve kahredici Allah\'tır.'),
(4063, 39, 5, 'Gökleri ve yeri hak ile yarattı. Geceyi gündüzün üzerine dolar, gündüzü de gecenin üzerine dolar. Güneşi ve ayı buyruğu altına almıştır. Her biri belli bir süreye kadar akıp gitmektedir. İyi bil ki O, aziz ve çok bağışlayandır.'),
(4064, 39, 6, 'Sizi bir tek candan yarattı, sonra ondan eşini meydana getirdi ve sizin için davarlardan sekiz çift indirdi: (Deve, öküz, koyun, keçi.) Sizi annelerinizin karınlarında üç karanlık içinde yaratmadan yaratmaya (aşamadan aşamaya) geçirerek yaratmaktadır. İşte Rabbiniz Allah budur. Mülk O\'nundur. O\'ndan başka tanrı yoktur. Nasıl (O\'na kulluktan şirke) çevriliyorsunuz?'),
(4065, 39, 7, 'Eğer nankörlük ederseniz, şüphesiz Allah, siz(in imanınız)a muhtaç değildir. Fakat kulları için küfre razı olmaz. Ve eğer şükrederseniz sizin için ona razı olur. Hiçbir günahkar, diğerinin günahını çekmez. Sonra dönüşünüz Rabbinizedir, (O), size yaptıklarınızı haber verir. Çünkü O, göğüslerin özünü bilir.'),
(4066, 39, 8, 'İnsana bir zarar dokundu mu, hemen içtenlikle Rabbine yönelerek O\'na du\'a eder. Sonra (Rabbi) ona kendisinden bir ni\'met verdi mi; önceden O\'na yalvarmakta olduğunu unutur da, O\'nun yolundan saptırmak için Allah\'a eşler koşmağa başlar. De ki: "Küfrünle azıcık yaşa, sen ateş halkındansın!"'),
(4067, 39, 9, 'Yoksa o, gece sa\'atlerinde secde ederek, ayakta durarak ibadet eden, ahiretten korkan ve Rabbinin rahmetini uman gibi midir? De ki: "Bilenlerle bilmeyenler bir olur mu?" Doğrusu ancak sağduyu sahipleri öğüt alır.'),
(4068, 39, 10, '(Tarafımdan) De ki: "Ey inanan kullarım, Rabbinizden korkun. Bu dünya hayatında güzel davrananlara güzellik vardır. Allah\'ın yeri geniştir. Ancak sabredenlere, ödülleri hesapsız ödenecektir.'),
(4069, 39, 11, 'De ki: "Bana dini yalnız Allah\'a halis kılarak, O\'na kulluk etmem emredildi."'),
(4070, 39, 12, 'Ve bana müslümanların ilki olmam emredildi.'),
(4071, 39, 13, 'De ki: "Ben, Rabbime isyan edersem, büyük bir günün azabından korkarım."'),
(4072, 39, 14, 'De ki: "Ben, dinimi yalnız Allah\'a halis kılarak O\'na kulluk ediyorum."'),
(4073, 39, 15, 'Siz de O\'ndan başka dilediğinize kulluk edin. De ki: "Ziyana uğrayanlar kıyamet günü hem kendilerini, hem de ailelerini ziyan edenlerdir. Dikkat edin, işte bu, apaçık bir ziyandır!"'),
(4074, 39, 16, 'Onların üstlerinden ateşten gölgeler, altlarından da (ateşten) gölgeler var. İşte Allah kullarını bu durumdan korkutur. Ey kullarım, benden korkun!'),
(4075, 39, 17, 'Tağut\'a kulluk etmekten kaçınan ve Allah\'a yönelenlere müjde var. Müjdele kullarımı:'),
(4076, 39, 18, 'Onlar ki, sözü dinlerler ve onun en güzeline uyarlar. İşte onlar Allah\'ın kendilerini doğru yola ilettiği kimselerdir ve onlar sağduyu sahipleridir.'),
(4077, 39, 19, 'Üzerine azab kararı hak olanı mı, sen ateşte bulunanı mı kurtaracaksın?'),
(4078, 39, 20, 'Fakat Rablerinden korkanlar için üstüste yapılmış odalar var. Odaların altından da ırmaklar akmaktadır. Bu, Allah\'ın va\'didir. Allah va\'dinden caymaz.'),
(4079, 39, 21, 'Görmedin mi Allah gökten bir su indirdi, onu yerin içindeki kaynaklara geçirdi, Sonra onunla çeşitli renklerde ekin çıkarıyor. Sonra (ekin) kurur, onu sararmış görürsün. Sonra Allah onu bir çöp yapar. Şüphesiz bunda sağduyu sahipleri için bir ibret vardır.'),
(4080, 39, 22, 'Allah\'ın, göğsünü İslam\'a açtığı kimse, Rabbinden bir nur üzerinde değil midir? Allah\'ı anmağa karşı yürekleri katılaşmış olanlara yazıklar olsun. Onlar apaçık bir sapıklık içindedirler.'),
(4081, 39, 23, 'Allah, sözün en güzelini, (Kur\'an\'ın ayetlerini güzellikte) birbirine benzer, ikişerli bir Kitap halinde indirdi. Rablerinden korkanların, ondan derileri ürperir, sonra derileri ve kalbleri Allah\'ın zikrine yumuşar. İşte bu (Kitap) Allah\'ın rehberidir. Dilediğini bununla doğru yola iletir. Ama Allah kimi sapıklığında bırakırsa artık ona yol gösteren olmaz.'),
(4082, 39, 24, 'Kıyamet günü, (elleri bağlı olduğu için) yüzüyle o en kötü azabdan korunmağa çalışan (ile güven içinde bulunan bir olur) mu? (O gün) Zalimlere: "Kazandığınız(ın tadın)ı tadın!" denmiştir.'),
(4083, 39, 25, 'Onlardan öncekiler de yalanladılar, bundan dolayı hiç farkına varmadıkları bir yönden onlara azab geldi.'),
(4084, 39, 26, 'Allah, dünya hayatında onlara rezillik taddırdı. Ahiret azabı ise daha büyüktür, keşke bilselerdi!'),
(4085, 39, 27, 'Andolsun biz, bu Kur\'an\'da insanlara, öğüt almaları için her temsili anlattık.'),
(4086, 39, 28, 'Korunanlar için bunu, pürüzsüz Arapça bir Kur\'an olarak (indirdik).'),
(4087, 39, 29, 'Allah, (ortak koşanla tek Allah\'a inananın durumunu anlatmak için) şöyle bir misal verdi: Birbiriyle çekişen ortaklara bağlı olan bir adam (yani köle) ile yalnız bir kişiye bağlı olan bir adam. Şimdi bu ikisinin durumu bir olur mu? Hamd yalnız Allah\'a mahsustur, fakat çokları (yalnız Allah\'a tapılacağını) bilmiyorlar.'),
(4088, 39, 30, 'Sen de öleceksin, onlar da ölecekler.'),
(4089, 39, 31, 'Sonra siz, kıyamet günü, Rabbinizin divanında davalaşacaksınız.'),
(4090, 39, 32, 'Allah hakkında yalan uydurandan ve kendisine gelen doğruyu yalanlayandan daha zalim kim olabilir? Cehennemde kafirler için bir yer yok mudur?'),
(4091, 39, 33, 'Doğruyu getirene ve onu doğrulayanlara gelince: İşte korunanlar onlardır.'),
(4092, 39, 34, 'Rablerinin yanında onlara, diledikleri her şey var. İşte güzel davrananların mükafatı budur.'),
(4093, 39, 35, '(Böyle olur) Ki Allah onların yaptıklarının en kötülerini onlardan örtsün ve onları, yaptıklarının en güzeliyle mükafatlandırsın.'),
(4094, 39, 36, 'Allah kuluna kafi değil mi? Seni O\'ndan başkalarıyle korkutuyorlar. Allah kimi şaşırtırsa artık onu yola getiren olmaz.'),
(4095, 39, 37, 'Allah kime de yol gösterirse; artık onu şaşırtan olmaz. Allah galib ve öc alan değil midir?'),
(4096, 39, 38, 'Andolsun onlara: "Gökleri ve yeri kim yarattı?" diye sorsan, elbette "Allah" derler. De ki: "O halde Allah\'tan başka yalvardıklarınızı gördünüz mü, şimdi Allah, bana bir zarar vermek istese, onlar O\'nun vereceği zararı kaldırabilirler mi? Yahut (Allah) bana bir rahmet (fayda) vermek dilese onlar O\'nun rahmetini durdurabilirler mi?" De ki: "Allah bana yeter. Tevekkül edenler O\'na dayanırlar."'),
(4097, 39, 39, 'De ki: "Ey kavmim, durumunuza göre bildiğinizi yapın, ben de (bildiğimi) yapıyorum; yakında bileceksiniz;"'),
(4098, 39, 40, 'Kendisini rezil edecek azab kime geliyor ve sürekli azab kimin üzerine konuyor?'),
(4099, 39, 41, 'Biz Kitabı, insanlar için, sana hak ile indirdik. Artık kim doğru yola gelirse kendi yararınadır, kim de saparsa kendi zararına sapmış olur. Sen onların üzerinde vekil değilsin.'),
(4100, 39, 42, 'Allah, ölmekte olan canları alır, ölmeyenleri de uykularında (bedenlerinden alıp kendilerinden geçirir); sonra ölümüne hükmettiğini yanında tutar, ötekilerini de belli bir süreye kadar salıverir. Şüphesiz bunda düşünen bir toplum için ibretler vardır.'),
(4101, 39, 43, 'Yoksa Allah\'tan başka şefa\'atçiler mi edindiler? De ki: "Onlar, hiçbir şeye malik olmayan, düşünmeyen şeyler olsalar da mı (onları şefa\'atçi edineceksiniz?)"'),
(4102, 39, 44, 'De ki: "Şefa\'at tamamen Allah\'ındır (yardım ve destek yalnız O\'ndandır). Göklerin ve yerin mülkü O\'nundur. Sonra O\'na döndürüleceksiniz."'),
(4103, 39, 45, 'Allah, tek olarak anıldığı zaman; ahirete inanmayanların kalbleri ürker. Ama O\'ndan başka(tanrı)ları da anıldığı zaman, hemen sevinirler.'),
(4104, 39, 46, 'De ki: "Allah\'ım, ey gökleri ve yeri yoktan var eden, görülmeyeni ve görüleni bilen! Ancak Sen, ayrılığa düştükleri şeylerde kullarının arasında hükmedersin."'),
(4105, 39, 47, 'Eğer yeryüzünde bulunanların tümü, ve onun bir misli daha zulmedenlerin olsaydı, kıyamet günü o kötü azabdan (kurtulmak için) onu mutlaka fidye verirlerdi. (Çünkü) hiç hesabetmedikleri şeyler, Allah\'tan karşılarına çıkmıştır.'),
(4106, 39, 48, 'Yaptıkları işlerin kötülükleri kendilerine görünmüş ve alay edegeldikleri şey onları kuşatmıştır.'),
(4107, 39, 49, 'İnsana bir zarar dokunduğu zaman bize du\'a eder. Sonra, ona bizden bir ni\'met verdiğimiz vakit; "Bu, (benim) bilgi(m) sayesinde bana verildi" der. Hayır, o bir imtihandır, fakat çokları bilmiyorlar.'),
(4108, 39, 50, 'Onlardan öncekiler de bunu demişlerdi. Ama kazandıkları şeyler, kendilerine hiçbir yarar sağlamadı.'),
(4109, 39, 51, 'Kazandıklarının kötülükleri, sonunda başlarına geldi. Bunlardan zulmedenlere de yaptıklarının kötülükleri erişecektir. Onlar, buna engel olacak değillerdir.'),
(4110, 39, 52, 'Bilmediler mi ki Allah dilediğine rızkı açar ve kısar. Şüphesiz bunda inanan bir toplum için ibretler vardır.'),
(4111, 39, 53, '(Tarafımdan onlara) De ki: "Ey nefislerine karşı aşırı giden kullarım, Allah\'ın rahmetinden umut kesmeyin. Allah bütün günahları bağışlar. Çünkü O, çok bağışlayan, çok esirgeyendir."'),
(4112, 39, 54, 'Size azab gelip çatmadan Rabbinize dönün, O\'na teslim olun. Sonra size yardım edilmez.'),
(4113, 39, 55, 'Ansızın ve hiç farkına varmadığınız bir sırada, size azab gelmezden önce Rabbinizden size indirilenin en güzeline uyun.'),
(4114, 39, 56, '(O gün günahkar) nefsin şöyle demesinden sakının: "Allah\'ın yanında (O\'na kullukta) kusur edişimden dolayı vah (bana). Gerçekten ben alay edenlerdendim."'),
(4115, 39, 57, 'Yahut şöyle demesinden: "Allah bana hidayet etseydi, elbet ben de (Allah\'ın azabından) korunanlardan olurdum."'),
(4116, 39, 58, 'Yahut azabı gördüğü zaman: "Keşke benim için bir kez daha (dünyaya dönüş) olsaydı da güzel hareket edenlerden olsaydım!" demesinden.'),
(4117, 39, 59, '(Allah şöyle buyurur): "Hayır, sana ayetlerim geldi de sen onları yalanladın, büyüklük tasladın ve nankörlerden oldun!"'),
(4118, 39, 60, 'Allah\'a yalan uyduranların kıyamet günü yüzlerinin kapkara kesildiğini görürsün. Kibirlenenler için cehennemde bir yer yok mudur?'),
(4119, 39, 61, 'Allah, korunanları başarılarıyle kurtarır; onlara kötülük dokunmaz ve onlar üzülmezler.'),
(4120, 39, 62, 'Allah her şeyin yaratıcısıdır, O, her şeyin yöneticisidir.'),
(4121, 39, 63, 'Göklerin ve yerin anahtarları O\'nundur. Allah\'ın ayetlerini inkar edenler, işte ziyana uğrayacaklar onlardır.'),
(4122, 39, 64, 'De ki: "Allah\'tan başkasına kulluk etmemi mi bana emrediyorsunuz ey cahiller?"'),
(4123, 39, 65, 'Sana ve senden öncekilere şöyle vahyedildi: "Andolsun, eğer (Allah\'a) ortak koşarsan amelin boşa çıkar ve kaybedenlerden olursun!"'),
(4124, 39, 66, 'Hayır, yalnız Allah\'a kulluk et ve şükredenlerden ol.'),
(4125, 39, 67, 'Allah\'ı gereği gibi bilemediler. Halbuki kıyamet günü yer, tamamen O\'nun avucu içindedir, gökler de sağ elinde dürülmüştür. O, onların ortak koştuklarından uzak ve yücedir.'),
(4126, 39, 68, 'Sur\'a üflenmiş, göklerde ve yerde olanlar (korkudan) bayılmışlar, ancak Allah\'ın dilediği sarsılmamıştır. Sonra ona bir daha üflenmiştir, birden onlar kalkmış, bakıyorlardır.'),
(4127, 39, 69, 'Yer, Rabbinin nuru ile parlamış, Kitap (ortaya) konmuş, peygamberler ve şahidler getirilmiş ve aralarında adaletle hükmedilmiştir. Onlara asla haksızlık edilmez'),
(4128, 39, 70, 'Herkese yaptığının karşılığı tam verilmiştir. O, onların ne yaptıklarını en iyi bilendir.'),
(4129, 39, 71, 'Nankörler, bölük bölük cehenneme sürülmüşlerdir. Oraya geldikleri zaman, kapıları açılan cehennemin bekçileri onlara şöyle demiştir: "Kendi aranızdan, Rabbinizin ayetlerini size okuyan ve sizi bu gününüzle karşılaşacağınız hakkında uyaran elçiler gelmedi mi?" "Evet geldi, demişlerdir; ama kafirlere azab sözü hak olmuştur (kafirler azab hükmünü giymeyi hak etmişlerdir)."'),
(4130, 39, 72, 'O halde içinde ebedi kalmak üzere cehennemin kapılarından girin. Kibirlenenlerin yeri ne kötüymüş! denilmiştir.'),
(4131, 39, 73, 'Rablerinin (azabından) korunanlar da bölük bölük cennete sevk edilmişlerdir. Kapıları daha önce açılmış bulunan cennete vardıklarında onun bekçileri onlara: "Selam size, (ne) hoşsunuz, ebedi kalmak üzere buraya girin!" demişlerdir.'),
(4132, 39, 74, '(Cennettekiler de:) "Bize verdiği sözü yerine getiren ve bizi dilediğimiz yerinde oturacağımız bu cennet yurduna varis kılan Allah\'a hamdolsun. (Allah için) çalışanların ücreti ne güzeldir!" demişlerdir.'),
(4133, 39, 75, 'Meleklerin de Arşın çevresinde dönerek Rablerini övgü ile andıklarını görürsün. İnsanlar arasında hak ile hükmedilmiş ve: "Hamd alemlerin Rabbine mahsustur" denilmiştir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Ha mim.'),
(4135, 40, 2, 'Bu Kitabın indirilişi, aziz (daima galib) ve alim (herşeyi en iyi bilen) Allah tarafındandır;'),
(4136, 40, 3, 'Günahı bağışlayan, tevbeyi kabul eden, azabı çetin olan, lutuf sahibi (Allah tarafından). O\'ndan başka tanrı yoktur, dönüş O\'nadır.'),
(4137, 40, 4, 'İnkar edenlerden başkası, Allah\'ın ayetleri hakkında mücadele etmez. Onların (öyle) şehirlede dolaşmaları, seni aldatmasın.'),
(4138, 40, 5, 'Onlardan önce Nuh kavmi ve onlardan sonra gelen kollar da yalanladı. Her millet, elçisini yakalamağa yeltendi; hakkı gidermek için boş şeyler ileri sürerek tartıştılar. Bu yüzden onları yakaladım. (Bak işte) Azabım nasıl oldu?!'),
(4139, 40, 6, 'Böylece Rabbinin inkar edenler hakkındaki "Onlar ateş halkıdır" sözü yerini bulmuş oldu.'),
(4140, 40, 7, 'Arş\'ı taşıyanlar ve onun çevresinde bulunanlar, Rablerini överek tesbih ederler. O\'na inanırlar ve mü\'minler için (şöyle) mağfiret dilerler: "Rabbimiz, Sen rahmet ve bilgi bakımından her şeyi kapladın. Tevbe edip senin yoluna uyanları bağışla, onları cehennem azabından koru!"'),
(4141, 40, 8, 'Rabbimiz, onları ve babalarından, eşlerinden, çocuklarından iyi olan kimseleri onlara söz verdiğin Adn cennetlerine sok. Şüphesiz, üstün olan, hüküm ve hikmet sahibi olan sensin sen!'),
(4142, 40, 9, 'Onları kötülüklerden koru. O gün Sen, kimi kötülüklerden korursan ona acımışsındır. İşte o büyük başarı budur!'),
(4143, 40, 10, 'İnkar edenlere de bağırılır: "Allah\'ın (size) kızması, sizin kendi kendinize kızmanızdan daha büyüktür. Zira siz imana çağrılırdınız da inkar ederdiniz!"'),
(4144, 40, 11, 'Dediler ki: "Rabbimiz, bizi iki kez öldürdün ve iki kez dirilttin. Günahlarımızı itiraf ettik. Şimdi (şu ateşten) çıkmak için (bize) bir yol var mı (acaba)?"'),
(4145, 40, 12, '(Şöyle cevap verilir): Bu(duruma düşmeniz)in sebebi şudur: Tek Allah\'a çağrıldığınız zaman inkar ederdiniz. O\'na ortak koşulunca inanırdınız. Artık hüküm yüce ve büyük Allah\'a aittir.'),
(4146, 40, 13, 'O\'dur ki, size ayetlerini gösteriyor ve sizin için gökten rızık indiriyor, (rızkın sebebi olan yağmur, güneş ve hava veriyor). Ancak (O\'na) yönelen öğüt alır.'),
(4147, 40, 14, 'Kafirlerin hoşuna gitmesede siz, dini yalnız Allah\'a halis kılarak O\'na çağırın.'),
(4148, 40, 15, '(O,) Dereceleri yükselten; Arş\'ın sahibi (Allah), buluşma gününe karşı uyarmak için, emrinden olan ruhu, kullarından dilediğine indirir.'),
(4149, 40, 16, 'O gün onlar, ortaya çıkarlar. Onlardan hiçbir şey Allah\'a gizli kalmaz. (Ve sorulur onlara): "Bugün mülk kimindir? O tek ve kahhar olan Allah\'ın!"'),
(4150, 40, 17, 'Bugün her can, kazandığıyle cezalanır. Bugün zulüm yoktur. Allah, hesabı çabuk görendir.'),
(4151, 40, 18, 'Onları yaklaşan güne karşı uyar. Zira (o gün) yürekler, (korkudan adeta yerinden sökülüp) gırtlaklara dayanmıştır; (kederlerini) yutkunur dururlar. Zalimlerin ne bir dostu, ne de sözü tutulur bir aracıları yoktur.'),
(4152, 40, 19, '(Allah) gözlerin hain(bakışlar)ını ve göğüslerin gizlediği düşünceleri bilir.'),
(4153, 40, 20, 'Allah, hak ile hükmeder. O\'ndan başka yalvardıkları (tanrılar) ise hiçbir hüküm veremezler. Çünkü işiten, gören yalnız Allah\'tır.'),
(4154, 40, 21, '(Onlar) Yeryüzünde gezip dolaşmadılar mı ki, kendilerinden önce gelenlerin sonunun nasıl olduğunu görsünler. Onlar kuvvet ve yeryüzündeki eserleri bakımından kendilerinden daha üstün idiler. Fakat Allah, onları günahları yüzünden yakaladı. Onları Allah\'a karşı koruyan olmadı.'),
(4155, 40, 22, 'Çünkü onlar (öyle kimselerdi ki) elçileri onlara açık kanıtlar getirirdi ama kabul etmezlerdi. Bu yüzden Allah onları yakaladı. Zira O güçlüdür, cezası çetin olandır.'),
(4156, 40, 23, 'Andolsun biz Musa\'yı ayetlerimizle ve apaçık bir yetki ile gönderdik:'),
(4157, 40, 24, 'Fir\'avn\'e, Haman\'a ve Karun\'a. "(Bu,) Yalancı bir büyücüdür." dediler.'),
(4158, 40, 25, '(Musa,) Onlara katımızdan hakkı getirince: "Onunla beraber inananların oğullarını öldürün, kadınlarını sağ bırakın!" dediler. Fakat kafirlerin tuzağı hep boşa çıkar.'),
(4159, 40, 26, 'Fir\'avn dedi: "Bırakın Musa\'yı öldüreyim de, Rabbine yalvarsın (bakalım O, Musa\'yı kurtaracak mı?) Çünkü ben onun, dininizi değiştireceğinden yahut yeryüzünde fesad çıkaracağından korkuyorum"'),
(4160, 40, 27, 'Musa dedi: "Ben, hesap gününe inanmayan her kibirliden, benim de Rabbim sizin de Rabbiniz(olan Allah)a sığındım."'),
(4161, 40, 28, 'Fir\'avn ailesinden imanını gizleyen mü\'min bir adam (şöyle) dedi: "Rabbim Allah\'tır dediği için bir adamı öldürüyor musunuz? Oysa o size Rabbinizden kanıtlar getirmiştir. Eğer yalancı ise yalanı kendi zararınadır. Ve eğer doğru söylüyorsa, size va\'dettiklerinin bir kısmı başınıza gelir. Şüphesiz Allah aşırı giden, yalancı kimseyi doğru yola iletmez."'),
(4162, 40, 29, 'Ey kavmim, bugün mülk sizindir; buraya siz hakimsiniz. Eğer bize (Allah\'ın hışmı) gelirse kim bizi ondan kurtarır? Fir\'avn dedi: "Ben size yalnız (doğru) gördüğümü gösteriyorum ve ben sizi ancak doğru yola götürüyorum."'),
(4163, 40, 30, 'İnanan adam dedi ki: "Ey kavmim, ben üzerinize önceki toplulukların günü gibi bir günün gelmesinden korkuyorum.:'),
(4164, 40, 31, 'Nuh kavminin, \'Ad ve Semud\'un ve onlardan sonrakilerin durumu gibi (bir durumla karşılaşmanızdan kaygı duyuyorum). Allah kullara zulmetmek istemez, (günahsız kimselere ceza vermez).'),
(4165, 40, 32, 'Ey kavmim, sizin için o (Yüce Divana) çağırma (yahut feryadetme) gününden korkuyorum.'),
(4166, 40, 33, 'O gün arkanızı dönüp kaç(mak ist)ersiniz ama sizi Allah(ın azabın)dan kurtaracak kimse yoktur. Allah kimi şaşırtırsa artık ona yol gösteren olmaz.'),
(4167, 40, 34, 'Daha önce Yusuf da size açık kanıtlar getirmişti. Onun getirdiklerinden de kuşkulanıp duruyordunuz. Nihayet o ölünce: \'Allah ondan sonra elçi göndermez,\' dediniz. İşte Allah, aşırı giden, şüpheci kimseleri böyle saptırır."'),
(4168, 40, 35, 'Onlar ki kendilerine gelmiş bir delil olmadan Allah\'ın ayetleri hakkında tartışırlar. (Bu hareketleri) Gerek Allah yanında, gerek inananlar yanında (onlara karşı) ne büyük bir kızgınlık (doğurur)! İşte Allah, her kibirli zorbanın kalbini böyle mühürler."'),
(4169, 40, 36, 'Fir\'avn dedi: "Ey Haman, bana yüksek bir kule yap da o sebeplere (yollara) erişeyim:"'),
(4170, 40, 37, '(Yani) Göklerin yollarına (erişeyim) de çıkıp Musa\'nın tanrısına bakayım. Çünkü ben Musa\'yı, yalancı sanıyorum. Böylece yaptığı kötü iş, Fir\'avn\'a süslü gösterildi ve (o), yoldan çıkarıldı. Fir\'avn\'ın tuzağı, tamamen boşa çıktı.'),
(4171, 40, 38, 'İnanan (adam) dedi ki: "Ey kavmim, bana uyun, sizi doğru yola götüreyim."'),
(4172, 40, 39, 'Ey kavmim, bu dünya hayatı (kısa) bir geçinmedir. Ahiret ise ebedi olarak durulacak yerdir.'),
(4173, 40, 40, 'Kim bir kötülük yaparsa sadece onun (yaptığı kötülük) kadar cezalanır, ama erkek ve kadından her kim inanarak faydalı bir iş yaparsa onlar cennete girerler ve orada kendilerine hesapsız rızık verilir.'),
(4174, 40, 41, 'Ey kavmim, neden ben sizi kurtuluşa çağırdığım halde siz beni ateşe çağırıyorsunuz?'),
(4175, 40, 42, 'Siz beni, Allah\'a nankörlük etmeğe ve bilmediğim şeyleri O\'na ortak koşmağa çağırıyorsunuz; bense sizi O aziz ve çok bağışlayana çağırıyorum.'),
(4176, 40, 43, 'Sizin beni çağırdığınız şeye kesinlikle ne dünyada, ne de ahirette du\'a edilemez (onlar kendilerine yapılan du\'ayı duymazlar ve ona cevap veremezler). Bizim dönüşümüz Allah\'adır. Aşırı gidenler, işte onlar ateş halkıdır.'),
(4177, 40, 44, 'Benim size söylediklerimi yakında hatırlayacaksınız. Ben işimi Allah\'a bırakıyorum. Şüphesiz Allah, kulları görür.'),
(4178, 40, 45, 'Allah onu, onların kurdukları tuzakların kötülüklerinden korudu ve Fir\'avn ailesini, azabın en kötüsü kuşattı:'),
(4179, 40, 46, 'Ateş! Sabah akşam ona sunulurlar. Kıyamet koptuğu gün de: "Fir\'avn ailesini azabın en çetinine sokun!" (denilir).'),
(4180, 40, 47, 'Ateşin içinde birbirleriyle tartışırlarken, zayıf olanlar, büyüklük taslayanlara dediler ki: "Biz size uymuştuk. Şimdi siz, şu ateşin ufak bir parçasını bizden savabilir misiniz?"'),
(4181, 40, 48, 'Büyüklük taslayanlar da dediler ki: "Hepimiz de onun içindeyiz. Allah kullar arasında (böyle) hüküm verdi!"'),
(4182, 40, 49, 'Ateştekiler, cehennemin bekçilerine dediler ki: "Rabbinize du\'a edin de hiç değilse bir gün, bizden azabı biraz hafifletsin!"'),
(4183, 40, 50, '(Bekçiler) Dediler: "Elçileriniz size açık kanıtlar getirmezler miydi?" "Evet (getirirlerdi) dediler. (Bekçiler:) "Öyle ise yalvar(ıp dur)un. Nankörlerin yalvarması hep çıkmazdadır." dediler.'),
(4184, 40, 51, 'Elbette biz elçilerimize ve inananlara hem dünya hayatında hem, şahidlerin (şahidliğe) duracakları günde yardım ederiz.'),
(4185, 40, 52, 'O gün zalimlere, ma\'zeretleri fayda vermez. Onlar için la\'net ve yurt(lar)ın en kötüsü vardır.'),
(4186, 40, 53, 'Andolsun biz Musa\'ya hidayet verdik ve İsrail oğullarına o Kitabı miras kıldık.'),
(4187, 40, 54, '(O,) sağduyu sahiplerine bir yol gösterici ve öğüttür.'),
(4188, 40, 55, 'Sabret, Allah\'ın va\'di mutlaka gerçektir. Günahına da istiğfar et ve akşam sabah Rabbini övgü ile an.'),
(4189, 40, 56, 'Kendilerine gelmiş hiçbir delil olmadan Allah\'ın ayetleri hakkında tartışanlar var ya, onların göğüslerinde, (hiçbir zaman) erişemeyecekleri bir büyüklük taslamaktan başka bir şey yoktur. Sen Allah\'a sığın, çünkü işiten, gören O\'dur.'),
(4190, 40, 57, 'Gökleri ve yeri yaratmak, insanları yaratmaktan çok daha zordur. Fakat insanların çoğu bilmezler.'),
(4191, 40, 58, 'Körle gören bir olmaz. İnanan ve iyi işler yapanlarla kötülük yapan bir olmaz. Ne kadar az düşünüyorsunuz!'),
(4192, 40, 59, '(Duruşma) sa\'at(i) mutlaka gelecektir. Bunda asla şüphe yoktur. Fakat insanların çoğu inanmazlar.'),
(4193, 40, 60, 'Rabbiniz buyurdu ki: "Bana du\'a edin, du\'anızı kabul edeyim. Bana kulluk etmeğe tenezzül etmeyenler, aşağılık olarak cehenneme gireceklerdir."'),
(4194, 40, 61, 'Allah O\'dur ki size geceyi, içinde istirahat etmeniz için (serin ve karanlık); gündüzü de (işinizi) görmeniz için aydınlık yaptı. Şüphesiz Allah, insanlara lutufkardır; fakat insanların çoğu şükretmezler.'),
(4195, 40, 62, 'İşte herşeyin yaratıcısı olan Rabbiniz Allah budur. O\'ndan başka tanrı yoktur. Nasıl da (O\'nu birlemekten) çevriliyorsunuz?'),
(4196, 40, 63, 'İşte Allah\'ın ayetlerini kasden inkar edenler de (haktan) böyle çevriliyorlardı.'),
(4197, 40, 64, 'Allah O\'dur ki arzı size durulacak yer, göğü de bina yaptı; sizi şekillendirdi, şekillerinizi de güzel yaptı. Ve sizi güzel rızıklarla besledi. İşte Rabbiniz Allah budur. Bütün alemleri yaratan Allah, ne yücedir!'),
(4198, 40, 65, 'O diridir, O\'ndan başka tanrı yoktur. Dini yalnız kendisine halis kılarak O\'na yalvarın. Hamd, alemlerin Rabbine mahsustur.'),
(4199, 40, 66, 'De ki: "Ben, Rabbimden bana açık deliller gelince, sizin Allah\'tan başka yalvardıklarınıza tapmaktan men\'olundum ve alemlerin Rabbine teslim olmakla emrolundum."'),
(4200, 40, 67, 'O\'dur ki (önce) sizi topraktan, sonra nutfe(sperm)den, sonra alaka(embriyo)dan yarattı. Sonra sizi çocuk olarak (annelerinizin karnından) çıkarıyor. Sonra güçlü çağınıza eresiniz, sonra da ihtiyarlar olasınız diye sizi yaşatıyor. İçinizden kimi de daha önce öldürülüyor. Belli süreye erişmeniz ve aklınızı kullan(ıp Allah\'ın bundaki hikmetlerini anla)manız için (böyle yapıyor).'),
(4201, 40, 68, 'Yaşatan ve öldüren O\'dur. Bir işin olmasını istedi mi, ona sadece "ol!" der, o da olur. *'),
(4202, 40, 69, 'Baksana, Allah\'ın ayetleri hakkında tartışanlar, nasıl (Hak\'tan) çevriliyorlar?'),
(4203, 40, 70, 'O, Kitabı ve elçilerimizi gönderdiğimiz mesajı yalanlayanlar, yakında bileceklerdir!'),
(4204, 40, 71, 'Boyunlarında demir halkalar ve zincirler olduğu halde sürüklenceklerdir:'),
(4205, 40, 72, 'Kaynar su içinde. Sonra da ateşte yakılacaklardır.'),
(4206, 40, 73, 'Sonra onlara denilecektir: "Ortak koştuklarınız nerede?'),
(4207, 40, 74, 'Allah\'tan başka (taptıklarınız)?" Diyecekler ki: "Bizden (uzaklaşıp) kayboldular; hayır, meğer biz önceden hiçbir şeye tapmıyormuşuz, (taptıklarımız hiçbir şey değilmiş)!" İşte Allah, kafirleri böyle şaşırtır.'),
(4208, 40, 75, 'Bu durum, sizin yeryüzünde haksız olarak şımarmanızdan ve bölürlenmenizden ötürüdür.'),
(4209, 40, 76, 'Cehennemin kapılarından girin, orada ebedi kalacaksınız. Kibirlenenlerin yeri ne kötüdür!'),
(4210, 40, 77, 'Sabret, Allah\'ın sözü gerçektir. Onları tehdidettiğimiz şeylerin bir kısmını ya sana gösteririz, yahut seni daha önce vefat ettiririz. (Sonunda) onlar bize döndürülecklerdir.'),
(4211, 40, 78, 'Andolsun biz, senden önce de elçiler gönderdik. Onlardan kimini sana anlattık, kimini de anlatmadık. Hiçbir elçi, Allah\'ın izni olmadan bir mu\'cize getiremez. Allah\'ın emri geldiği zaman hak yerine getirilir ve işte o zaman (Allah\'ın ayetlerini) boşa çıkarmağa uğraşanlar, hüsrana uğrarlar. *'),
(4212, 40, 79, 'Allah\'tır ki kimine binmeniz, kiminden yemeniz için size hayvanları yarattı.'),
(4213, 40, 80, 'Onlarda sizin için (sütleri, derileri, tüyleri gibi daha birçok) faydalar var. Onların üstünde gönüllerinizdeki arzuya erersiniz; onların ve gemilerin üstünde taşınırsınız.'),
(4214, 40, 81, '(Allah) Size ayetlerini gösteriyor. Allah\'ın ayetlerinden hangisini inkar ediyorsunuz?'),
(4215, 40, 82, 'Yeryüzünde gezip dolaşmadılar mı ki kendilerinden öncekilerin sonunun nasıl olduğunu görsünler? Onlar, bunlardan daha çok, daha kuvvetli ve yeryüzündeki eserleri bakımından daha sağlam idiler. Ama kazandıkları, kendilerine hiçbir yarar sağlamadı.'),
(4216, 40, 83, 'Elçileri onlara açık kanıtlar getirince, yanlarında bulunan bilgi ile sevin(ip övün)düler (peygamberlerin getirdikleri bilgiye değer vermediler, onlarla alay ettiler). Sonunda alay edegeldikleri şey, kendilerini kuşatıverdi.'),
(4217, 40, 84, 'Ne zaman ki hışmımızı gördüler: "Tek Allah\'a inandık ve O\'na ortak koştuğumuz şeyleri inkar ettik." dediler.'),
(4218, 40, 85, 'Fakat hışmımızı gördükleri zaman inanmaları, kendilerine bir fayda sağlamadı. Allah\'ın kulları hakkında eskiden beri yürürlükte olan yasası budur. İşte o zaman kafirler ziyana uğramışlardır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Ha mim.'),
(4220, 41, 2, '(Bu,) Rahman, Rahim\'den indirilmiştir.'),
(4221, 41, 3, 'Bilen bir toplum için ayetleri açıklanmış, Arapça okunan bir Kitaptır.'),
(4222, 41, 4, 'Müjdeleyici ve uyarıcı olarak (gönderilmiştir). Fakat çokları yüz çevirmiştir; onlar işitmezler.'),
(4223, 41, 5, 'Dediler ki: "Bizi çağırdığın şeye karşı kalblerimiz kılıflar içinde, kulaklarımızda bir ağırlık ve seninle bizim aramızda bir perde var. Sen (istediğini) yap, biz de (istediğimizi) yapıyoruz."'),
(4224, 41, 6, 'De ki: "Ben de ancak sizin gibi bir insanım. Bana, tanrınızın bir tek Tanrı olduğu vahyediliyor. O\'na doğrulun (O\'na yönelerek işlerinizi düzeltin), O\'ndan mağfiret dileyin. (O\'na) Ortak koşanların vay haline!"'),
(4225, 41, 7, 'Onlar ki zekat vermezler ve onlar ahireti de inkar ederler.'),
(4226, 41, 8, 'İnanıp iyi işler yapanlara gelince; onlar için kesintisiz bir mükafat vardır.'),
(4227, 41, 9, 'De ki: "Siz mi arzı iki günde Yaratan\'a nankörlük ediyor ve O\'na eşler koşuyorsunuz? O, alemlerin Rabbidir."'),
(4228, 41, 10, 'Arza, üstünden ağır baskılar (sağlam dağlar) yaptı. Onda bereketler yarattı ve onda arayıp soranlar için gıdalarını (bitkilerini ve ağaçlarını) tam dört günde takdir etti (düzene koydu).'),
(4229, 41, 11, 'Sonra duman (gaz) halinde bulunan göğe yöneldi, ona ve arza: "İsteyerek veya istemeyerek (buyruğuma) gelin" dedi. "İsteyerek (buyruğuna) geldik." dediler.'),
(4230, 41, 12, 'Böylece onları, iki günde yedi gök yaptı ve her göğe emrini (kanunlarını) vahyetti. Biz, en yakın göğü lambalarla ve koruma ile (koruyucu güçlerle) donattık. İşte bu, o güçlü, bilen (Allah)ın takdiridir.'),
(4231, 41, 13, 'Eğer yüz çevirirlerse, de ki: "Ben sizi \'Ad ve Semud\'un başına düşen yıldırım gibi bir yıldırıma karşı uyardım."'),
(4232, 41, 14, 'Onlara: "Yalnız Allah\'a kulluk edin!" diye önlerinden ve arkalarından elçiler gelmişti. "Rabbimiz dileseydi, melekler indirirdi. Biz sizinle gönderilen mesajı tanımıyoruz." dediler.'),
(4233, 41, 15, 'Ad (kavmi), yeryüzünde haksız olarak büyüklük tasladılar ve: "Bizden daha kuvvetli kim var?" dediler. Onları yaratan Allah\'ın kendilerinden daha güçlü olduğunu görmediler mi? Bizim ayetlerimizi de inkar ediyorlardı.'),
(4234, 41, 16, 'Biz de onlara dünya hayatında rezillik azabını taddırmak için o uğursuz günlerde, üzerlerine dondurucu bir rüzgar gönderdik. Ahiret azabı ise daha da kepaze edicidir. Onlara hiç yardım edilmeyecektir.'),
(4235, 41, 17, 'Semud(kavmin)e gelince onlara yol gösterdik; fakat onlar, körlüğü doğru yolu bulmağa yeğlediler. Böylece yaptıkları yüzünden alçaltıcı azab yıldırımı onları yakaladı.'),
(4236, 41, 18, 'İnananları ve korunanları kurtardık.'),
(4237, 41, 19, 'Allah\'ın düşmanları ateşe sürüldükleri gün toplanıp bir araya getirilirler.'),
(4238, 41, 20, 'Nihayet oraya vardıklarında kulakları, gözleri ve derileri, yaptıkları işler hakkında aleyhlerine şahidlik ettiler.'),
(4239, 41, 21, 'Derilerine: "Niçin aleyhimize şahidlik ettiniz?" dediler. (Derileri): "Her şeyi konuşturan Allah bizi konuşturdu. İlk defa sizi O yaratmıştı, işte O\'na döndürülüyorsunuz." dediler.'),
(4240, 41, 22, 'Siz (günah işlerken) kulaklarınızın, gözlerinizin ve derilerinizin, aleyhinize şahidlik etmesinden gizlenmiyordunuz, yaptıklarınızın çoğunu Allah\'ın bilmeyeceğini sanıyordunuz.'),
(4241, 41, 23, 'İşte Rabbinize karşı beslediğiniz bu zannınız, sizi helak etti, ziyana uğrayanlardan oldunuz!'),
(4242, 41, 24, 'Şimdi eğer dayanabilirlerse, ateştir onların yeri. Ve eğer özür dileyip Rablerini razı etmek isterlerse özürleri kabul edilmeyecektir (çünkü özür dileme vakti geçmiştir artık).'),
(4243, 41, 25, 'Biz onlara birtakım (kötü) arkadaşlar sardırdık. Onların önlerinde ve arkalarında bulunan herşeyi onlara süslü gösterdiler (yaptıkları işlerin güzel olduğunu söylediler). Kendilerinden önce gelip geçmiş olan cin ve insan topluluklarına (uygulanan) söz, kendilerine de gerekli oldu (bunlar da azabı hak ettiler), çünkü hep ziyanda idiler.'),
(4244, 41, 26, 'İnkar edenler dediler ki: "Bu Kur\'an\'ı dinlemeyin, o(okunduğu)nda gürültü edin, (böylece onun anlaşılmasına engel olun), belki ona galib gelirsiniz (başka türlü onunla başa çıkmanıza imkan yoktur)."'),
(4245, 41, 27, 'İnkar edenlere şiddetli bir azab taddıracağız ve onları, yaptıklarının en kötüsüyle cezalandıracağız.'),
(4246, 41, 28, 'O Allah düşmanlarının cezası: Ateştir. Ayetlerimizi inkar etmelerinin cezası olarak onlara, orada sürekli kalma yurdu vardır.'),
(4247, 41, 29, '(Ateşe giren) Kafirler dediler ki: "Rabbimiz, bizi saptıran cin ve insanları bize göster, onları ayaklarımızın altına alalım da alçaklardan olsunlar!"'),
(4248, 41, 30, 'Rabbimiz Allah\'tır deyip, sonra doğru olanların üzerine melekler iner: "Korkmayın, üzülmeyin, size söz verilen cennetle sevinin! (derler)."'),
(4249, 41, 31, 'Biz dünya hayatında da, ahirette de sizin dostlarınızız. Orada size canlarınızın çektiği her şey var. Orada size istediğiniz her şey var.'),
(4250, 41, 32, '(Bütün bunlar) Çok bağışlayan, çok esirgeyen(Allah)ın ağırlaması olarak (size lutfedilir).'),
(4251, 41, 33, '(İnsanları) Allah\'a çağıran, iyi iş yapan ve "Ben müslümanlardanım" diyenden daha güzel sözlü kim olabilir?'),
(4252, 41, 34, 'İyilikle kötülük bir olmaz. (Sen kötülüğü) En güzel olan şeyle sav. O zaman bir de bakarsın ki, seninle arasında düşmanlık bulunan kimse, sanki sıcak bir dosttur.'),
(4253, 41, 35, 'Bu(kötülüğü iyilikle savma olgunluğu)na ancak sabredenler kavuturulur. Buna ancak büyük şansı olan kavuşturulur.'),
(4254, 41, 36, 'Eğer şeytandan kötü bir düşünce, seni dürtecek olursa hemen Allah\'a sığın. Çünkü O, işitendir, bilendir.'),
(4255, 41, 37, 'Gece, gündüz, güneş ve ay O\'nun ayetlerindendir. Eğer Allah\'a tapıyorsanız, güneşe ve aya secde etmeyin; onları yaratan Allah\'a secde edin.'),
(4256, 41, 38, 'Eğer büyüklük taslarlarsa (bilsinler ki) Rabbinin yanında bulunan(melek)ler, gece gündüz O\'nu tesbih ederler ve onlar hiç usanmazlar.'),
(4257, 41, 39, 'O\'nun ayetlerinden biri de (şudur): Sen, toprağı, boynu bükük görürsün. Onun üzerine suyu döktüğümüz zaman titreşir ve kabarır. Onu dirilten, elbette ölüleri de diriltir. O, her şeye kadirdir.'),
(4258, 41, 40, 'Ayetlerimiz hususunda doğruluktan sapanlar bize gizli kalmazlar. Şimdi, ateşin içine atılan mı daha iyidir, yoksa kıyamet günü güvenle gelen mi? Dilediğinizi yapın, O, yaptıklarınızı görmektedir.'),
(4259, 41, 41, 'Onlar, kendilerine gelen Kur\'an\'ı inkar ettiler. Halbuki o, öyle eşsiz bir Kitaptır,'),
(4260, 41, 42, 'Ki ne önünden, ne de arkasından onu boşa çıkaracak bir söz gelmez. (O) Hüküm ve hikmet sahibi, çok övülen(Allah)dan indirilmiştir.'),
(4261, 41, 43, 'Sana söylenen, senden önceki elçilere söylenmiş olandan başka bir şey değildir. Kuşkusuz Rabbin, hem bağışlama sahibi, hem de acı azab sahibidir.'),
(4262, 41, 44, 'Eğer biz onu, yabancı (dilde) bir Kur\'an yapsaydık derlerdi ki: "Ayetleri (anlayacağımız) bir dille açıklanmalı değil miydi? Araba yabancı söz mü (geliyor)?" De ki: "O, inananlar için bir yol gösterici ve (gönüllere) şifadır. İnanmayanlara gelince, onların kulaklarında bir ağırlık vardır ve o, onlara bir körlüktür. (Sanki) Onlar, uzak bir yerden çağırılıyorlar (da duymuyorlar).'),
(4263, 41, 45, 'Andolsun biz Musa\'ya Kitabı vermiştik, onda da ayrılığa düşülmüştü. Eğer Rabbinden bir söz geçmiş olmasaydı, aralarında derhal hüküm verilir(işleri bitirilir)di. Onlar ondan işkilli bir kuşku içindedirler.'),
(4264, 41, 46, 'Kim iyi iş yaparsa yararı kendisinedir ve kim kötülük yaparsa zararı kendisinedir. Rabbin kullara zulmedici değildir.'),
(4265, 41, 47, '(Duruşma) Sa\'ati(ni) bilmek, Allah\'a havale edilir. O\'nun bilgisi olmadan ne meyvalar kabuklarından çıkar, ne bir dişi gebe kalır ve ne de doğurur. (Alah) Onlara: "Ortaklarım nerede?" diye ünlediği gün: "Sana arz ederiz ki bizden hiçbir gören yok." dermişlerdir.'),
(4266, 41, 48, 'Önceden yalvarıp durdukları şeyler, onlardan sapıp gitmiş ve onlar, kendileri için kaçacak bir yer olmadığını anlamışlardır.'),
(4267, 41, 49, 'İnsan hayır istemekten usanmaz (daima malının artamasını diler). Ama kendisine bir şer dokundu mu hemen üzülür, ümitsiz olur.'),
(4268, 41, 50, 'Eğer kendisine dokunan bir zarardan sonra biz ona bir rahmet taddırırsak: "Bu benim hakkımdır; kıyametin kopacağını sanmıyorum; (kıyamet kopsa da) Rabbime götürülmüş olsam bile muhakkak O\'nun yanında benim için daha güzel şeyler vardır" der. Biz, o nankörlere, yaptıklarını mutlaka haber vereceğiz ve mutlaka onlara kaba azabdan taddıracağız.'),
(4269, 41, 51, 'İnsana bir ni\'met verdik mi yüz çevirir; yan çizer. Ona bir şer dokundu mu yalvarıp durur.'),
(4270, 41, 52, 'De ki: "Gördünüz mü, ya o (Kur\'an) Allah tarafından ise ve siz de onu inkar etmişseniz, o zaman uzak bir ayrılığa düşenden daha sapık kim olabilir?"'),
(4271, 41, 53, 'Biz onlara, ufuklarda ve kendi canlarında ayetlerimizi göstereceğiz ki o(Kur\'a)n\'ın gerçek olduğu, onlara iyice belli olsun. Rabbinin her şeye tanık olması yetmez mi?'),
(4272, 41, 54, 'İyi bil ki onlar, Rablerine kavuşmaktan kuşku içindedirler. İyi bil ki O, her şeyi kuşatmıştır (her şey Allah\'ın bilgisi içindedir).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Ha mim'),
(4274, 42, 2, 'Ayın sin kaf.'),
(4275, 42, 3, 'O aziz ve hakim olan Allah, sana ve senden öncekilere böyle vahyeder:'),
(4276, 42, 4, 'Göklerde ve yerde bulunan herşey O\'nundur. O, yücedir, uludur.'),
(4277, 42, 5, 'Neredeyse gökler üstlerinden çatlayacaklar. Melekler Rablerini hamd ile tesbih ederler; yerdekiler için de mağfiret dilerler. İyi bil ki Allah, işte çok bağışlayan, çok esirgeyen O\'dur.'),
(4278, 42, 6, 'O\'ndan başka veliler edinenleri Allah kollamaktadır. Sen onların üzerinde vekil değilsin.'),
(4279, 42, 7, 'Biz sana böyle Arapça bir Kur\'an vahyettik ki Anakent(Mekke\'y)i ve çevresinde bulunanları ikaz edip; (vukuunda) asla kuşku bulunmayan toplanma gününe karşı uyarasın. (O gün), bir bölük cennette, bir bölük ateştedir.'),
(4280, 42, 8, 'Allah dileseydi, onları bir tek millet yapardı. Fakat O, dilediğini rahmetine sokar. Zalimlere gelince: Onların ne velisi ne de yardımcısı vardır.'),
(4281, 42, 9, 'Yoksa Allah\'tan başka veliler mi edindiler? Veli yalnız Allah\'tır. Ölüleri O diriltir. O her şeye kadirdir.'),
(4282, 42, 10, 'Ayrılığa düştüğünüz herhangibir şeyde hüküm vermek, Allah\'a aittir. İşte Rabbim Allah budur. O\'na dayandım, O\'na yöneldim.'),
(4283, 42, 11, '(O) Gökleri ve yeri yoktan var edendir. Size kendinizden çiftler, hayvanlardan da çiftler yaratmıştır. Bu(düzen içi)nde sizi üretiyor. O\'na benzer hiçbir şey yoktur. O işitendir, görendir.'),
(4284, 42, 12, 'Göklerin ve yerin anahtarları O\'nundur. Dilediğine rızkı açar ve kısar. O, her şeyi bilendir.'),
(4285, 42, 13, 'O size, dinden Nuh\'a tavsiye ettiğini, sana vahyettiğimizi, İbrahim\'e, Musa\'ya ve Îsa\'ya tavsiye ettiğimizi şeri\'at (hukuk düzeni) yaptı. Şöyle ki: Dini doğru tutun ve onda ayrılığa düşmeyin. Fakat kendilerini çağırdığın (bu) esas, Allah\'a ortak koşanlara ağır geldi. Allah dilediğini kendisine seçer ve iyi niyyetle yöneleni kendisine iletir.'),
(4286, 42, 14, 'Onlar, kendilerine ilim geldikten sonra sadece aralarındaki çekememezlik yüzünden ayrılığa düştüler. Eğer Rabbinden belli bir süreye kadar yaşatma sözü geçmiş olmasaydı, aralarında hüküm verilir(işleri bitirilir)di. Onlardan sonra Kitaba varis kılınanlar ondan, kuşku veren bir şüphe içindedirler.'),
(4287, 42, 15, 'Bundan dolayı sen (Hakka) çağır ve emrolunduğun gibi doğru ol; onların keyiflerine uyma ve de ki: "Ben Allah\'ın indirdiği her Kitaba inandım ve aranızda adalet yapmakla emrolundum. Allah bizim de Rabbimiz, sizin de Rabbinizdir. Bizim eylemlerimiz bize, sizin eylemleriniz size aittir. Bizimle sizin aranızda bir tartışma nedeni yoktur. Allah aramızı bulur, (yahut: Allah bizi bir araya toplar), dönüş O\'nadır.'),
(4288, 42, 16, '(Kamu tarafından) Kabul edildikten sonra, hala Allah(ın dini hakkın)da tartışanların delilleri, Rableri yanında batıldır. Üzerlerine bir gazab ve onlara şiddetli bir azab vardır.'),
(4289, 42, 17, 'Allah\'tır ki gerçeği içeren Kitabı ve (adalet) ölçü(sün)ü indirdi. Ne bilirsin, belki o sa\'at yakındır?'),
(4290, 42, 18, 'Ona inanmayanlar, onun çabuk gelmesini isterler. İnananlar ise ondan korkarlar ve onun gerçek olduğunu bilirler. İyi bil ki, o sa\'at hakkında tartışanlar, uzak bir sapıklık içine düşmüşlerdir.'),
(4291, 42, 19, 'Allah kullarına lutufkardır, dilediğini rızıklandırır. O kuvvetlidir, galiptir.'),
(4292, 42, 20, 'Kim ahiret ekinini istiyorsa onun ekinini artırırız; kim dünya ekinini istiyorsa ona da dünyadan bir şey veririz. Fakat onun ahirette bir nasibi olmaz.'),
(4293, 42, 21, 'Yoksa onların, kendilerine, Allah\'ın izin vermediği dini koyan ortaklar mı var? Eğer (bir süre fırsat verilmesi hakkında) karar olmasaydı derhal aralarında hüküm verilir(işleri bitirilir)di. Kuşkusuz zalimler için acı bir azab vardır.'),
(4294, 42, 22, 'Yaptıkları işler başlarına inerken zalimlerin, korkudan titrediklerini görürsün. Fakat inanıp iyi işler yapanlar cennet bahçelerindedirler. Rablerinin yanında onlara diledikleri her şey vardır. İşte büyük lutuf budur.'),
(4295, 42, 23, 'Allah\'ın, inanan ve iyi işler yapan kullarını müjdelediği (büyük lutuf). De ki: "Ben bunu karşılık sizden bir ücret istemiyorum. Ancak (Allah\'a) yaklaşmayı arzu ediyorum." Kim bir iyilik yaparsa onun iyiliğini artırırız. Şüphesiz Allah bağışlayan, (iyiliğe) karşılık verendir.'),
(4296, 42, 24, 'Yoksa: "Allah\'a yalan uydurdu" mu diyorlar? Öyle bir durumda Allah, dilese senin kalbine mühür basar; batılı mahveder, hakkı sözleriyle yerleştirir. Şüphesiz O, göğüslerin özünü bilir.'),
(4297, 42, 25, 'O\'dur ki kullarından tevbeyi kabul eder, kötülüklerden geçer ve yaptıklarınızı bilir.'),
(4298, 42, 26, 'İnanan ve iyi işler yapanların dileklerini kabul eder; lutuf ve kereminden onlara, daha fazlasını da verir. Kafirlere gelince: onlara da çetin bir azab vardır.'),
(4299, 42, 27, 'Allah kullarına rızkı bollaştırsaydı, yeryüzünde azarlardı. Fakat (O rızkı) dilediği ölçüde indiriyor. Çünkü O, kullarını(n her halini) haber alandır, görendir.'),
(4300, 42, 28, 'O\'dur ki (kulları) umutlarını kestikten sonra yağmuru indirir, rahmetini yayar. O velidir, övülmüştür.'),
(4301, 42, 29, 'Gökleri, yeri ve bunların içine yaydığı canlıları yaratması da O\'nun ayetlerinden(birliğinin ve kudretinin işaretlerinden)dir. O, dilediği zaman onları toplamağa da kadirdir.'),
(4302, 42, 30, 'Başınıza gelen herhangi bir musibet kendi ellerinizin yaptığı (işler) yüzündendir. (Allah, hatalarınızın) Birçoğunu da affeder.'),
(4303, 42, 31, 'Siz, yer yüzünde O(nun cezası)na engel olamazsınız. Sizin Allah\'tan başka ne bir veliniz, ne de bir yardımcınız vardır.'),
(4304, 42, 32, 'Denizde dağlar gibi akıp giden(gemi)ler O\'nun ayetlerindendir.'),
(4305, 42, 33, 'Dilerse rüzgarı durdurur, (gemiler denizin) sırtında durakalır. Kuşkusuz bunda sabreden, şükreden herkes için ibretler vardır.'),
(4306, 42, 34, 'Yahut yaptıkları (işler) yüzünden gemileri(n içindekileri) helak eder. Birçoğunu da affeder (kurtarır).'),
(4307, 42, 35, 'Ki ayetlerimiz hakkında tartışanlar, kendileri için kaçacak bir yer olmadığını bilsinler.'),
(4308, 42, 36, 'Size verilen şeyler, dünya hayatının geçimidir. İnanıp Rablerine dayananlar için Allah\'ın yanında bulunan ödül ise daha hayırlı ve daha kalıcıdır.'),
(4309, 42, 37, 'Onlar büyük günahlardan ve çirkin işlerden kaçınırlar; kızdıkları zaman da onlar, affederler.'),
(4310, 42, 38, 'Rablerinin çağrısına gelirler, namazı kılarlar. İşleri, aralarında danışma iledir. Kendilerine verdiğimiz rızıktan hayır için harcarlar.'),
(4311, 42, 39, 'Bir zulüm ve saldırıya uğradıkları zaman kendilerini savunurlar.'),
(4312, 42, 40, 'Kötülüğün cezası, yine onun gibi bir kütülüktür. Kim affeder, barışırsa onun mükafatı Allah\'a aittir. Doğrusu O, zalimleri sevmez.'),
(4313, 42, 41, 'Kim zulme uğradıktan sonra kendini savunursa öylelerinin aleyhine bir yol yoktur (onlar kınanmaz ve cezalandırılmazlar).'),
(4314, 42, 42, 'Ancak şunlar aleyhine yol vardır ki, insanlara zulmederler ve yeryüzünde haksız yere saldırırlar. İşte böylelerine acı bir azab vardır.'),
(4315, 42, 43, 'Fakat kim sabreder, affederse, şüphesiz bu, çok önemli işlerdendir!'),
(4316, 42, 44, 'Allah kimi sapıklıkta bırakırsa artık onun, Allah\'tan sonra bir velisi yoktur. Zalimlerin, azabı gördükleri zaman: "Geri dönecek bir yol var mı?" dediklerini görürsün.'),
(4317, 42, 45, 'Yine onları görürsün: Aşağılıktan başlarını öne eğmiş vaziyette ateşe sunulurlarken göz ucuyla gizli gizli bakarlar. İnananlar da: "İşte asıl ziyana uğrayanlar, kıyamet günü hem kendilerini, hem ailelerini ziyan edenlerdir. Bakın, gerçekten zalimler sürekli bir azab içindedirler" demişlerdir.'),
(4318, 42, 46, 'Onların, Allah\'tan başka kendilerine yardım edecek velileri yoktur. Allah kimi sapıklıkta bırakırsa artık onun için bir (kurtuluş) yol(u) yoktur.'),
(4319, 42, 47, 'Allah\'tan, geri çevrilmesi mümkün olmayan bir gün gelmezden önce, Rabbiniz(in çağrısı)na uyun. Çünkü o gün ne sığınacak bir yeriniz var; ne de (yaptıklarınızı) inkara çare.'),
(4320, 42, 48, 'Eğer yüz çevirirlerse (üzülme); biz seni onların üzerine bekçi göndermedik. Sana düşen, yalnız duyurmaktır. Biz insana, bizden bir rahmet taddırdığımız zaman ona sevinir. Ama ellerinin (yapıp) öne sürdüğü işlerden dolayı başlarına bir kötülük gelirse, insan hemen nankör olur.'),
(4321, 42, 49, 'Göklerin ve yerin mülkü Allah\'ındır. (O) Dilediğini yaratır. Dilediğine dişiler bahşeder, dilediğine de erkekler bahşeder.'),
(4322, 42, 50, 'Yahut onları çift yapar: Hem dişi, hem erkek (verir). Dilediğini de kısır yapar. O (herşeyi) bilendir, (herşeye) gücü yetendir.'),
(4323, 42, 51, 'Allah bir insanla (karşılıklı) konuşmaz. Ancak vahiyle (kulunun kalbine dilediği düşünceyi doğurarak), yahut perde arkasından konuşur; yahut izniyle dilediğini vahyedecek bir elçi gönderir. O, yücedir, hüküm ve hikmet sahibidir.'),
(4324, 42, 52, 'İşte sana da böyle emrimizden bir ruh (gönüllere can veren bir söz) vahyettik. Sen Kitap nedir, iman nedir bilmezdin. Fakat biz onu, kullarımızdan dilediğimizi, doğru yola ilettiğimiz bir nur yaptık. Şüphesiz sen, doğru yola götürüyorsun:'),
(4325, 42, 53, 'Göklerde ve yerde bulunan herşeyin sahibi Allah\'ın yoluna. İyi bilin ki bütün işler sonunda Allah\'a varır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Ha mim.'),
(4327, 43, 2, 'Apaçık Kitaba andolsun ki'),
(4328, 43, 3, 'Biz, düşünüp anlamanız için onu Arapça bir Kur\'an yaptık.'),
(4329, 43, 4, 'O, katımızda bulunan ana Kitaptadır. Yücedir, hikmetlidir.'),
(4330, 43, 5, 'Siz, aşırı giden bir kavim oldunuz diye, sizi uyarmaktan vaz mı geçelim?'),
(4331, 43, 6, 'Biz önce gelenlere nice peygamber gönderdik.'),
(4332, 43, 7, 'Onlara hiçbir peygamber gelmezdi ki mutlaka onunla alay etmesinler.'),
(4333, 43, 8, 'Biz de bunlardan daha güçlü olan(o kavimler)i helak ettik. Öncekilerin örneği geçti.'),
(4334, 43, 9, 'Andolsun onlara: "Gökleri ve yeri kim yarattı?" diye sorsan elbette diyecekler ki: "Onları, çok üstün, çok bilen (Allah) yarattı."'),
(4335, 43, 10, 'O yeri sizin için beşik kıldı ve varacağınız yere gitmeniz için yeryüzünde size yollar yaptı.'),
(4336, 43, 11, 'Gökten bir ölçü ile su indirdi de, onunla ölü bir ülkeyi canlandırdık. İşte siz de öyle (canlandırılıp) çıkarılacaksınız.'),
(4337, 43, 12, 'O bütün çiftleri yarattı ve size bineceğiniz gemiler ve hayvanlar var etti,'),
(4338, 43, 13, 'Ki onların sırtlarına binesiniz, sonra onlara bindiğiniz zaman Rabbinizin ni\'metini anasınız ve (şöyle) diyesiniz: "Bunu bizim hizmetimize veren (Allah)ın şanı yücedir, yoksa biz bunu (hizmetimize) yanaştıramazdık."'),
(4339, 43, 14, 'Biz elbette Rabbimize döneceğiz.'),
(4340, 43, 15, 'Tuttular, O\'na kullarından bir parça tasarladılar. Gerçekten insan apaçık bir nankördür.'),
(4341, 43, 16, 'Yoksa (Allah), yarattıklarından kızları kendisine aldı da oğullar için sizi mi yeğledi?'),
(4342, 43, 17, 'Onlardan birine Rahman\'a benzer olarak anlattığı (kız çocuğu) müjdelense yüzü kapkara kesilir, öfkesinden yutkunup durur.'),
(4343, 43, 18, 'Süs içinde yetiştirilip, mücadelede açık olmayanı (tartışmayı ve kavgayı beceremeyeni) mi (Allah\'ın çocuğu yaptılar)?'),
(4344, 43, 19, 'Rahman\'ın kulları olan melekleri dişi saydılar. Onların yaratılışlarına mı şahid oldular ki (böyle hüküm veriyorlar)? Şahidlikleri yazılacak ve (bundan) sorulacaklardır.'),
(4345, 43, 20, 'Ve dediler ki: "Rahman dileseydi, biz onlara tapmazdık." Onların bu hususta bir bilgileri yoktur. Onlar sadece saçmalıyorlar.'),
(4346, 43, 21, 'Yoksa bundan önce onlara bir Kitap vermişiz de ona mı sarılıyorlar?'),
(4347, 43, 22, 'Hayır, (ne bilgileri var, ne de Kitapları). Sadece: "Biz babalarımızı bir din üzerinde bulduk, biz de onların izlerinde gidiyoruz" dediler.'),
(4348, 43, 23, 'İşte böyle, senden önce de hangi kente uyarıcı gönderdiysek mutlaka oranın varlıklıları: "Biz babalarımızı bir din üzerinde bulduk, biz de onların izlerine uyarız." dediler.'),
(4349, 43, 24, 'Ben size, babalarınızı, üzerinde bulduğunuz(din)den daha doğrusunu getirmiş olsam da (yine babalarınızın yolunu)mu (tutacaksınız)? dedi. "Doğrusu biz sizinle gönderilen mesajı tanımıyoruz." dediler.'),
(4350, 43, 25, 'Biz de onlardan öc aldık. Bak, yalanlayanların sonu nasıl oldu?'),
(4351, 43, 26, 'Bir zaman İbrahim babasına ve kavmine demişti ki: "Ben sizin taptıklarınızdan uzağım."'),
(4352, 43, 27, 'Ben yalnız beni yaratana (taparım). Çünkü O, bana doğru yolu gösterecektir.'),
(4353, 43, 28, 'Ve bu sözü ardında kalıcı bir söz yaptı ki (insanlar Allah\'a kulluğa) dönsünler.'),
(4354, 43, 29, 'Doğrusu bunları da, babalarını da kendilerine gerçek söz ve (onu) açıklayan elçi gelinceye dek yaşattım.'),
(4355, 43, 30, 'Fakat kendilerine gerçek gelince: "Bu, büyüdür, biz onu tanımayız" dediler.'),
(4356, 43, 31, 'Ve dediler ki: "Bu Kur\'an iki kentten, büyük bir adama indirilmeli değil miydi?"'),
(4357, 43, 32, 'Rabbinin rahmetini onlar mı bölüştürüyorlar? Dünya hayatında onların geçimliklerini aralarında biz taksim ettik ve onlardan kimini ötekine derecelerle üstün kıldık ki biri, diğerine iş gördürebilsin. Rabbinin rahmeti, onların toplayıp yığdıklarındandaha hayırlıdır.'),
(4358, 43, 33, 'İnsanlar (küfürde birleşen) bir tek ümmet olacak olmasaydı. Rahman\'ı inkar edenlerin evlerine gümüşten tavanlar ve üzerine binip çıkacakları merdivenler yapardık.'),
(4359, 43, 34, 'Ve evlerine kapılar ve üzerine yaslanacakları koltuklar, divanlar.'),
(4360, 43, 35, 'Ve (nice) süs(ler verirdik). Bütün bunlar, sadece dünya hayatının geçiminden ibarettir. Rabbinin katında ahiret ise, (günahlardan) korunanlar içindir.'),
(4361, 43, 36, 'Kim Rahman\'ın zikrine karşı kör olursa ona bir şeytanı sardırırız; artık o, onun (yanından ayrılmaz, ona sürekli olarak kötülükleri telkin eden) arkadaşı olur.'),
(4362, 43, 37, 'O(şeyta)nlar onları yoldan çıkardıkları halde bunlar doğru yolda olduklarını sanırlar.'),
(4363, 43, 38, 'Nihayet (Zikr\'imize karşı körlük edip yoldan çıkan o adam) bize geldiği zaman (kötü arkadaşına) der ki: "Keşke benimle senin aranda iki doğu (doğu ile batı) arası kadar uzaklık olsaydı (seni hiç görmeseydim); meğer ne kötü arkadaş(mışsın sen)!"'),
(4364, 43, 39, '(Böyle söylemeniz) Bugün size bir yarar sağlamaz; çünkü zulmettiniz. Siz, azab (çekme)de ortaksınız.'),
(4365, 43, 40, '(Ey Muhammed), sen mi sağıra işittireceksin, yahut körü ve apaçık sapıklıkta olanı yola ileteceksin?'),
(4366, 43, 41, 'Ya biz seni alıp götürdükten sonra onlardan öc alırız.'),
(4367, 43, 42, 'Yahut onları uyardığımız şeyi sana gösteririz (senin gözlerinin önünde onları azaba uğratırız); bizim onlara gücümüz yeter.'),
(4368, 43, 43, 'Sen, sana vahyedilene sımsıkı sarıl, çünkü sen doğru yoldasın.'),
(4369, 43, 44, 'O (Kur\'an) sana ve kavmine bir Zikir(uyarı, şan ve şeref)dir ve yakında (ona uyup uymadığınızdan) sorulacaksınız.'),
(4370, 43, 45, 'Senden önce gönderdiğimiz elçilerimizden sor: Rahman\'dan başka tapılacak tanrılar yapmış mıyız?'),
(4371, 43, 46, 'Andolsun biz Musa\'yı da ayetlerimizle Fir\'avn\'a ve ileri gelen adamlarına gönderdik: "Ben alemlerin Rabbinin elçisiyim" dedi.'),
(4372, 43, 47, 'Onlara ayetlerimizi getirince onlar o ayetlerle alay edip gülmeğe başladılar.'),
(4373, 43, 48, 'Onlara gösterdiğimiz her mu\'cize, mutlaka kızkardeşinden (ötekinden) büyüktü. Belki dönerler diye onları (kıtlık, tufan, çekirge gibi türlü) azab(lar) ile cezalandırdık.'),
(4374, 43, 49, 'Bunun üzerine dediler ki: "Ey büyücü, bizim için Rabbine du\'a et, sana verdiği söz hakkı için (bizi bağışlasın) artık biz yola geleceğiz!"'),
(4375, 43, 50, 'Fakat biz onlardan azabı kaldırınca sözlerinden dönmeğe başladılar.'),
(4376, 43, 51, 'Fir\'avn kavminin içinde bağırıp dedi: "Ey kavmim, Mısır mülkü ve şu altımdan akıp giden ırmaklar benim değil mi? Görmüyor musunuz?"'),
(4377, 43, 52, 'Yahut ben, şu aşağılık, nerdeyse söz anlatamayacak durumda olan adamdan daha iyi değil miyim?'),
(4378, 43, 53, '(Eğer o, doğru söylüyorsa) Üzerine altın bilezikler atılmalı, yahut yanında (kendisine yardım eden, onu doğrulayan) melekler de gelmeli değil miydi?"'),
(4379, 43, 54, 'Kavmini küçümsedi, onlar da ona boyun eğdiler. Çünkü onlar yoldan çıkmış bir kavim idiler.'),
(4380, 43, 55, 'Onlar bizi kızdırınca biz de onlardan öc aldık, hepsini boğduk.'),
(4381, 43, 56, 'Onları sonradan gelen(inkarcı)ların geçmiş ataları ve örneği yaptık(bunlar da onların izinden gittiler).'),
(4382, 43, 57, 'Meryem oğlu, bir misal olarak anlatılınca hemen kavmin, ondan ötürü yaygarayı bastılar:'),
(4383, 43, 58, 'Bizim tanrılarımız mı hayırlı, yoksa o mu? dediler. Bunu sadece tartışma için sana misal verdiler. Doğrusu onlar, kavgacı bir toplumdur.'),
(4384, 43, 59, 'O, sadece kendisine ni\'met verdiğimiz ve İsrail oğullarına örnek kıldığımız bir kuldur.'),
(4385, 43, 60, 'Eğer dileseydik, sizden şu dünyada yerinize geçen melekler yapardık.'),
(4386, 43, 61, 'O, kıyametin kopacağını gösterir bir ilimdir. O sa\'atin geleceğinden hiç şüphe etmeyin, bana uyun, doğru yol budur.'),
(4387, 43, 62, 'Şeytan sizi (bundan) alıkoymasın. Çünkü o, sizin için açık bir düşmandır.'),
(4388, 43, 63, 'Îsa açık kanıtlar getirince dedi ki: "Ben size hikmet getirdim ve ayrılığa düştünüğünüz şeylerden bir kısmını size açıklamak için (geldim), Allah\'tan korkun ve bana ita\'at edin."'),
(4389, 43, 64, 'Allah, işte benim de Rabbim, sizin de Rabbiniz O\'dur. O\'na tapın, doğru yol budur. 	Lo! Allah, He is my Lord and your Lord. So worship Him. This is a right path.'),
(4390, 43, 65, 'Aralarından çıkan partiler, birbirleriyle ihtilafa düşmüşlerdir. Acı bir günün azabından vay o zulmedenlerin haline!'),
(4391, 43, 66, 'Onlar ille o sa\'atin, kendilerinin hiç farkında olmadıkları bir sırada, ansızın başlarına gelmesini mi bekliyorlar?'),
(4392, 43, 67, 'O gün, korunanlar dışında, dostlar birbirine düşmandır. (Onlara alemlerin Rabbi şöyle hitabeder):'),
(4393, 43, 68, 'Ey kullarım, bugün size korku yoktur ve siz üzülmeyeceksiniz.'),
(4394, 43, 69, 'Onlar, ayetlerimize inanmış ve müslüman olmuş (kullarım) idiler.'),
(4395, 43, 70, 'Haydi, siz cennete girin. Siz ve eşleriniz ağırlanıp sevindirileceksiniz!'),
(4396, 43, 71, 'Onların önünde altın tepsiler ve kadehler dolaştırılır. Orada canların çektiği, gözlerin hoşlandığı her şey var! Ve siz orada ebedi kalacaksınız.'),
(4397, 43, 72, 'İşte yaptıklarınıza karşılık size miras verilen cennet budur.'),
(4398, 43, 73, 'Orada sizin için çok meyva var. Onlardan yersiniz.'),
(4399, 43, 74, 'Suçlular, cehennem azabında sürekli kalacaklardır.'),
(4400, 43, 75, '(Azab) Kendilerinden hiç hafifletilmeyecektir. Onlar azab içinde umutsuzdurlar!'),
(4401, 43, 76, 'Biz onlara zulmetmedik; fakat onlar kendileri zalim idiler.'),
(4402, 43, 77, '(Cehennemin muhafızına): "Ey Malik, Rabbin bizim işimizi bitirsin, (bizi yok etsin, böyle yaşamaktansa ölmek daha iyidir)!" diye seslendiler. (Malik) "Siz kalacaksınız (hiçbir suretle buradan kurtuluş yok)." dedi.'),
(4403, 43, 78, 'Andolsun biz size hakkı getirdik; fakat sizin çoğunuz haktan hoşlanmıyorsunuz.'),
(4404, 43, 79, 'Yoksa (hakka engel olma hususunda) bir iş mi kararlaştırdılar? Biz de (onları cezalandırmağa ve hakkı yerleştirmeğe) kararlıyız.'),
(4405, 43, 80, 'Yoksa biz, onların sırlarını ve gizli konuşmalarını işitmez miyiz sanıyorlor? Hayır, işitiriz ve yanlarında bulunan elçilerimiz de (her yaptıklarını) yazarlar.'),
(4406, 43, 81, 'De ki: "Eğer Rahman\'ın çocuğu olsaydı (O\'na) tapanların ilki ben olurdum.'),
(4407, 43, 82, 'Göklerin ve yerin Rabbi, Arş\'ın Rabbi onların nitelendirmelerinden yücedir, münezzehtir.'),
(4408, 43, 83, 'Bırak onları, kendilerine söylenen günlerine kavuşuncaya kadar dalsın, oynasınlar.'),
(4409, 43, 84, 'O\'dur ki gökte de Tanrı\'dır, yerde de Tanrı\'dır. O, hakimdir, bilendir.'),
(4410, 43, 85, 'Göklerin, yerin ve ikisi arasında bulunan her şeyin mülkü kendisine aidolan (Allah) ne yücedir! (Kıyametin kopacağı) Sa\'ati bilmek de O\'nun yanındadır ve siz O\'na döndürülüp götürüleceksiniz.'),
(4411, 43, 86, 'O\'ndan başka (tanrı diye) yalvardıkları şeyler şefa\'at (yetkisin)e sahip değillerdir. Ancak bilerek hakka şahidlik edenler (bildiklerini doğru anlatanlar) bunun dışındadır.'),
(4412, 43, 87, 'Andolsun onlara, "Kendilerini kim yarattı?" diye sorsan, elbette: "Allah," derler. O halde nasıl (haktan) çevriliyorlar?'),
(4413, 43, 88, 'Ve Elçinin: "Ya Rab, bunlar inanmayan bir kavimdir," demesini de (Allah biliyor).'),
(4414, 43, 89, 'Şimdi sen onlardan geç ve: "Size esenlik (dilerim)" de. Yakında bileceklerdir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Ha mim.'),
(4416, 44, 2, 'Apaçık Kitaba andolsun ki,'),
(4417, 44, 3, 'Biz onu mübarek bir gecede indirdik. Çünkü biz, uyarıcıyız.'),
(4418, 44, 4, 'Her hikmetli emir, o gecede ayırdedilir;'),
(4419, 44, 5, 'Katımızdan (verilen her) emir. Çünkü biz elçi göndericiyiz.'),
(4420, 44, 6, 'Senin Rabbinin acıması gereği olarak (gönderdiğimiz elçilere o gece emirlerimizi açıklar, vahiylerimizi bildiririz). Doğrusu O, işitendir, bilendir.'),
(4421, 44, 7, 'Eğer kesin olarak inanıyorsanız (bilin ki Allah), göklerin, yerin ve ikisi arasında bulunanların Rabbidir.'),
(4422, 44, 8, 'O\'ndan başka tanrı yoktur, yaşatır, öldürür. Sizin de Rabbiniz, önceki atalarınızın da Rabbidir.'),
(4423, 44, 9, 'Ama onlar, şüphe içinde oynuyorlar.'),
(4424, 44, 10, 'Göğün, açık bir duman getireceği günü gözetle.'),
(4425, 44, 11, '(Duman) İnsanları sarar. Bu, acı bir azabdır.'),
(4426, 44, 12, 'Rabbimiz, bizden azabı kaldır, çünkü biz artık inanıyoruz derler.'),
(4427, 44, 13, 'Artık onlar nasıl düşünüp öğüt alacaklar (öğüt alma zamanı geçti)? Oysa kendilerine apaçık bir elçi gelmişti.'),
(4428, 44, 14, 'Ondan yüz çevirdiler: "Bu, öğretilmiştir, cinlenmiştir" dediler.'),
(4429, 44, 15, 'Biz sizden azabı birazcık kaldırırız ama siz yine (inkarınıza) dönersiniz.'),
(4430, 44, 16, 'O gün büyük vuruşla vururuz; zira biz öc alıcıyız!'),
(4431, 44, 17, 'Andolsun, onlardan önce Fir\'avn toplumunu da (imkanlar vererek) sınadık. Onlara değerli bir elçi geldi, (şöyle diyerek):'),
(4432, 44, 18, 'Allah\'ın kullarını bana teslim edin; çünkü ben sizin için güvenilir bir elçiyim.'),
(4433, 44, 19, 'Allah\'a karşı ululanmayın. Ben size apaçık bir delil getiriyorum.'),
(4434, 44, 20, 'Ben, beni taşla(yıp öldür)menizden, benim de Rabbim, sizin de Rabbiniz olan(Allah)a sığındım.'),
(4435, 44, 21, 'Eğer bana inanmadınızsa bari ben(im yolum)dan çekilin.'),
(4436, 44, 22, 'Sonra (Musa): "Bunlar, suç işleyen bir toplumdur!" diye Rabbine du\'a etti.'),
(4437, 44, 23, '(Allah): "O halde kullarımı geceleyin yürüt. Çünkü takibedileceksiniz" (dedi).'),
(4438, 44, 24, 'Denizi (yarıp toplumunu geçirdikten sonra olduğu gibi) açık bırak. Çünkü onlar boğulacak bir ordudur.'),
(4439, 44, 25, 'Onlar geride nice şeyler bıraktılar: Bahçeler, çeşmeler.'),
(4440, 44, 26, 'Ekinler, güzel makamlar!'),
(4441, 44, 27, 'Ve zevkü sefa sürdükleri nice ni\'metler!'),
(4442, 44, 28, 'İşte böyle oldu ve biz onları başka bir topluma miras verdik.'),
(4443, 44, 29, 'Onlara gök ve yer ağlamadı. Ve kendilerine fırsat da verilmedi.'),
(4444, 44, 30, 'Andolsun biz, İsrail oğullarını o küçültücü azabdan kurtardık:'),
(4445, 44, 31, 'Fir\'avn\'dan. Çünkü o, (insanları ezip) ululanan, sınırı aşanlardan biri idi.'),
(4446, 44, 32, 'Andolsun biz, onları bir bilgiye göre alemlere üstün kıldık.'),
(4447, 44, 33, 'Onlara, içinde açık bir sınav bulunan ayetler verdik.'),
(4448, 44, 34, 'Şunlar (Kureyş kafirleri) de diyorlar ki:'),
(4449, 44, 35, 'İlk ölümümüzden sonra bir şey yoktur. Biz diriltilecek değiliz.'),
(4450, 44, 36, 'Doğru söylüyorsanız, babalarımızı getirin.'),
(4451, 44, 37, 'Onlar mı hayırlı, yoksa Tubba\' kavmi ve onlardan önce gelen(kavim)ler mi? Suç işledikleri için biz onların hepsini helak ettik.'),
(4452, 44, 38, 'Biz gökleri, yeri ve bunlar arasında bulunanları eğlenmek için yaratmadık!'),
(4453, 44, 39, 'Onları sadece gerçek bir sebeple, (hikmetli bir gaye ile) yarattık. Fakat onların çoğu bilmiyorlar.'),
(4454, 44, 40, '(Hakkın batıldan ayrılacağı) Hüküm günü, hepsinin varacağı gündür.'),
(4455, 44, 41, 'O gün dost, dostundan bir şey savamaz. Ve onlara yardım da edilmez.'),
(4456, 44, 42, 'Ancak Allah\'ın acıdığı kimseler (kurtulur). Şüphesiz O, üstündür esirgeyendir.'),
(4457, 44, 43, 'Zakkum ağacı,'),
(4458, 44, 44, 'Günahkarların yemeğidir.'),
(4459, 44, 45, 'Pota gibi karınlarda kaynar.'),
(4460, 44, 46, 'Sıcak suyun kaynaması gibi.'),
(4461, 44, 47, '(Allah, zebanilere emreder): "Tutun onu, cehennemin ortasına sürükleyin."'),
(4462, 44, 48, 'Sonra başının üstüne kaynar su azabından dökün!'),
(4463, 44, 49, 'Tad, zira sen kendince üstündün, şerefliydin.'),
(4464, 44, 50, 'İşte o kuşkulanıp durduğunuz şey budur!"'),
(4465, 44, 51, 'Korunanlar ise güvenli bir makamdadır.'),
(4466, 44, 52, 'Bahçelerde ve çeşme başlarında.'),
(4467, 44, 53, 'İnce ipekten ve parlak atlastan giysiler giyerek karşılıklı otururlar.'),
(4468, 44, 54, 'Ayrıca onları, iri gözlü hurilerle de evlendirmişizdir.'),
(4469, 44, 55, 'Orada, güven içinde, her meyveyi isterler.'),
(4470, 44, 56, 'Orada ilk ölümden başka ölüm tadmazlar (sürekli yaşarlar). Ve (Allah) onları cehennem azabından korumuştur.'),
(4471, 44, 57, 'Rabbinden bir lutuf olarak (bu ni\'metler kendilerine verilmiştir). İşte, o büyük başarı budur.'),
(4472, 44, 58, 'Biz o(Kur\'a)nı senin diline kolaylaştırdık ki, düşünüp öğüt alsınlar.'),
(4473, 44, 59, 'Biraz bekle, onlar da beklemektedirler (yakında başlarına neler geleceğini göreceklerdir).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Ha mim.'),
(4475, 45, 2, 'Kitabın indirilmesi, o üstün, hüküm ve hikmet sahibi Allah tarafındandır.'),
(4476, 45, 3, 'Şüphesiz göklerde ve yerde, inananlar için ibretler vardır.'),
(4477, 45, 4, 'Sizin yaratılışınızda ve (yeryüzünde) yaymakta olduğu canlılarda, kesin olarak inananlar için ibretler vardır.'),
(4478, 45, 5, 'Gecenin ve gündüzün değişmesinde, Allah\'ın gökten rızık (sebebi) indirip onunla ölümünden sonra yeri diriltmesinde, rüzgarları estirmesinde düşünen bir toplum için ibretler vardır.'),
(4479, 45, 6, 'İşte şunlar, Allah\'ın ayetleridir, onları sana gerçek ile okuyoruz. Allah\'tan ve O\'nun ayetlerinden sonra hangi hadise (söze) inanacaklar?'),
(4480, 45, 7, 'Her yalancı, günah yüklü kimseye yuh olsun!'),
(4481, 45, 8, 'O, Allah\'ın ayetlerinin kendisine okunduğunu işitir de sonra büyüklük taslayarak sanki hiç onları işitmemiş gibi (küfründe) direnir. Onu, acı bir azab ile müjdele.'),
(4482, 45, 9, 'O, bizim ayetlerimizden bir şey öğrendiği zaman onunla alay eder. İşte öyleleri için alçaltıcı bir azab vardır.'),
(4483, 45, 10, 'Ötelerinden de cehennem (onları beklemektedir). Ne kazandıkları ve ne de Allah\'tan başka edindikleri veliler kendilerine bir yarar sağlayabilir. Onlar için büyük bir azab vardır.'),
(4484, 45, 11, 'İşte yol gösterici, bu(Kur\'an)dır. Rablerinin ayetlerini tanımayanlar için çok çetin bir azab vardır!'),
(4485, 45, 12, 'Allah\'tır ki denizi size boyun eğdirdi, ta ki gemiler buyruğuyla denizin içinde akıp gitsin de, siz bu sayede O\'nun lutfundan payınızı arayasınız ve şükredesiniz.'),
(4486, 45, 13, 'Göklerde ve yerde bulunan şeyleri kendisinden (bir lutuf olarak) size boyun eğdirdi. Elbette bunda, düşünen bir toplum için ibretler vardır.'),
(4487, 45, 14, 'İnananlara söyle: Allah\'ın (ceza) günlerinin geleceğini ummayanları affetsinler ki (Allah), bir toplumu, yaptıklarıyle cezalandırsın.'),
(4488, 45, 15, 'Kim iyi bir iş yaparsa yararı kendisinedir, kim de kötülük yaparsa zararı kendisinedir. Sonunda Rabbinize döndürüleceksiniz.'),
(4489, 45, 16, 'Andolsun biz, İsrail oğullarına Kitap, hüküm (hikmet, hükümranlık) ve peygamberlik verdik, onları güzel rızıklarla besledik ve onları alemlere üstün kıldık.'),
(4490, 45, 17, 'Ve onlara bu (din) iş(in)de açık deliller verdik. Onlar kendilerine bilgi geldikten sonra sadece aralarındaki çekememezlik yüzünden ayrılığa düştüler. Şüphesiz, Rabbin kıyamet günü, ayrılığa düştükleri şeylerde onlar arasında hüküm verecektir.'),
(4491, 45, 18, 'Sonra seni de buyruk(umuz)dan bir şeriate (bir hukuk düzenine) koyduk. Sen ona uy, bilmeyenlerin keyiflerine uyma.'),
(4492, 45, 19, 'Çünkü onlar, Allah\'tan (gelecek) hiçbir şeyi senden savamazlar. Zalimler birbirlerinin velisidirler. Allah ise korunanların velisidir.'),
(4493, 45, 20, 'Bu (Kur\'an), insanlara kanıtlar(sunmakta)dır; kesin olarak inananlara yol gösterici ve rahmettir.'),
(4494, 45, 21, 'Yoksa kötülükleri işleyen kimseler, kendilerini, inanıp iyi ameller işleyen kimseler gibi yapacağımızı mı sandılar? Yaşamaları ve ölümleri onlarla bir olacak öyle mi? Ne kötü hüküm veriyorlar!'),
(4495, 45, 22, 'Allah, gökleri ve yeri gerçek olarak yaratmıştır ki her can, kazandığıyle cezalandırılsın, kimseye haksızlık edilmez.'),
(4496, 45, 23, 'Keyfini tanrı edinen ve Allah\'ın bir bilgiye göre saptırdığı, kulağını ve kalbini mühürlediği, gözünün üstüne de perde çektiği kimseyi gördün mü? Şimdi ona Allah\'tan sonra kim doğru yolu gösterecek? Düşünmüyor musunuz?'),
(4497, 45, 24, 'Dediler ki: "Ne varsa dünya hayatımızdır, başka bir şey yoktur. Ölürüz, yaşarız. Bizi zamandan başkası helak etmiyor." Fakat onların bu hususta hiçbir bilgileri yoktur. Onlar sadece zannediyorlar.'),
(4498, 45, 25, 'Onlara açık açık ayetlerimiz okunduğu zaman: "Doğru iseniz, babalarımızı getirin" demelerinden başka bir delilleri olmamıştır.'),
(4499, 45, 26, 'De ki: "Allah sizi yaşatıyor, sonra sizi öldürüyor. Sonra sizi, toplayıp duruşma gününe getirecektir. Bunda asla şüphe yoktur, ama insanların çoğu bilmezler."'),
(4500, 45, 27, 'Göklerin ve yerin mülkü Allah\'ındır. O sa\'at başladığı gün, işte o gün (Allah\'ın ayetlerini etkisiz bırakmağa çalışan) iptalciler hüsrana uğrayacaktır.'),
(4501, 45, 28, '(O gün) Her ümmeti (Allah\'ın huzurunda) toplanmış görürsün. Her ümmet, kendi Kitabına (yaptığı işlerin tutanağı olan amel defterine) çağırılır: "Bugün yaptıklarınızla cezalandırılacaksınız!"'),
(4502, 45, 29, 'İşte Kitabımız, aleyhinize gerçeği söylüyor. Çünkü biz, yaptıklarınızı yazıyorduk.'),
(4503, 45, 30, 'İnanıp iyi işler yapanlar; Rableri onları rahmetine sokar. İşte apaçık başarı budur.'),
(4504, 45, 31, 'Ama inkar edenlere gelince (onlara da şöyle denir): "Ayetlerim size okunurdu, fakat siz büyüklük tasladınız ve suçlu bir toplum oldunuz değil mi?"'),
(4505, 45, 32, 'Allah\'ın va\'di gerçektir, (Duruşma) sa\'at(inin geleceğin)de şüphe yoktur dendiği zaman: "Sa\'at nedir, bilmiyoruz, (onu) sadece (bir kuruntu) sanıyoruz biz ona inanmıyoruz" demiştiniz ha?!"'),
(4506, 45, 33, 'Yaptıklarının kötülükleri onlara göründü ve alay edip durdukları şey onları kuşattı.'),
(4507, 45, 34, 'Ve (kendilerine şöyle) denildi: "Siz, bu gününüzle karşılaşmayı nasıl unuttunuzsa biz de bugün sizi unuttuk. Yeriniz ateştir. Hiçbir yardımcınız da yoktur!"'),
(4508, 45, 35, 'Böyledir, çünkü siz Allah\'ın ayetlerini eğlence yaptınız; dünya hayatı sizi aldattı. Artık bugün onlar ne ateşten çıkarılırlar ve ne de kendilerinden Allah\'ı memnun etmeğe çalışmaları istenir.'),
(4509, 45, 36, 'Hamd, göklerin Rabbi, yerin Rabbi ve bütün alemlerin Rabbi olan Allah\'a mahsustur.'),
(4510, 45, 37, 'Göklerde ve yerde ululuk, yalnız O\'na aittir. O, azizdir hakimdir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Ha mim.'),
(4512, 46, 2, 'Kitabın indirilişi aziz, hakim olan Allah tarafındandır.'),
(4513, 46, 3, 'Biz gökleri, yeri ve ikisi arasında bulunanları (boş yere değil), ancak gerçek ile ve belli bir süreye göre yarattık. İnkar edenler, uyarıldıkları şeyden yüz çevirmektedirler.'),
(4514, 46, 4, 'De ki: "Allah\'tan başka yalvardıklarınızı gördünüz mü? Bana gösterin, onlar yerden neyi yarattılar? Yoksa gökler(in yaratılışın)da onların bir ortaklığı mı var? Eğer doğru iseniz bundan önce (inmiş olan) bir Kitap, yahut bir bilgi kalıntısı getirin."'),
(4515, 46, 5, 'Allah\'ı bırakıp da kıyamet gününe kadar kendisine cevap veremeyecek şeylere yalvarandan daha sapık kim olabilir? Oysa onlar, bunların yalvardıklarından habersizdirler.'),
(4516, 46, 6, 'İnsanlar (Yüce Divana) toplandıkları gün, (taptıkları tanrılar) onlara düşman olurlar ve onların, kendilerine tapmalarını tanımazlar.'),
(4517, 46, 7, 'Onlara açık açık ayetlerimiz okunduğu zaman kendilerine gelen hakkı inkar edenler: "Bu, apaçık bir büyüdür" dediler.'),
(4518, 46, 8, 'Yoksa "Onu uydurdu" mu diyorlar? De ki: "Eğer ben onu uydurmuşsam, Allah\'tan gelecek cezaya karşı sizin bana hiçbir yararınız olmaz. O, sizin yaptığınız taşkınlığı daha iyi bilir. Benimle sizin aranızda O\'nun şahid olması yeter. O, bağışlayan, esirgeyendir.'),
(4519, 46, 9, 'De ki: "Ben türedi bir elçi değilim. Bana ve size ne yapılacağını da bilmem. Ben sadece bana vahyedilene uyuyorum ve ben apaçık bir uyarıcıdan başka bir şey değilim."'),
(4520, 46, 10, 'De ki: "Hiç düşündünüz mü: Eğer bu (Kur\'an) Allah katından olduğu halde siz onu tanımamışsanız; İsrail oğullarından bir şahid de bunun benzerini (Tevrat\'ta) görüp inandığı halde siz (inanmağa) tenezzül etmemişseniz (durumunuz nice olur)? Allah, zalim bir toplumu doğru yola iletmez.'),
(4521, 46, 11, 'İnkar edenler, inananlar için "(Muhammed\'in getirdiği) iyi bir şey olsaydı (şu zavallı kişiler) ona inanmada bizi geçemezlerdi, (biz onlardan önce inanırdık) dediler. Onlar, onun gösterdiği doğru yola eremediklerinden: "Bu eski bir yalandır," diyeceklerdir.'),
(4522, 46, 12, 'Ondan önce de önder ve rahmet olarak Musa\'nın Kitabı vardır. Bu da (şirk ile) kendilerine yazık edenleri uyarmak, güzel davrananları müjdelemek için Arap diliyle indirilmiş (kendinden önceki Kitabı) doğrulayan Kitaptır.'),
(4523, 46, 13, 'Rabbimiz Allah\'tır deyip sonra doğru olanlar, onlara korku yoktur ve onlar üzülmeyeceklerdir.'),
(4524, 46, 14, 'Onlar cennet halkıdır, yaptıklarına karşılık orada ebedi kalacaklardır.'),
(4525, 46, 15, 'Biz insana, ana babasına iyilik etmesini tavsiye ettik. Anası onu zahmetle taşıdı ve zahmetle doğurdu. (Ana karnında) Taşınması ile sütten kesilmesi otuz ay sürdü. Nihayet (insan) güçlü çağına erip kırk yaşına varınca: "Ya Rabbi dedi, beni, bana ve anama, babama verdiğin ni\'mete şükretmeğe, razı olacağın yararlı işler yapmağa sevk eyle. Benim için zürriyetim içinde de salahı devam ettir (benden gelecek olanları da iyi insanlar yap). Ben sana yüz tuttum ve ben (sana) teslim olanlardanım.'),
(4526, 46, 16, 'Onlar öyle kişilerdir ki, yaptıklarının en iyisini onlardan kabul ederiz ve onların kötülüklerinden geçeriz, cennet halkı arasındadırlar. Bu, (dünyada) kendilerine söylenen doğru söz(ün gerçekleşmesi)dir.'),
(4527, 46, 17, 'Fakat o kimse ki anasına, babasına: "Öf size, benden önce nice nesiller gelip geçmiş, (kimse geri gelmemiş) iken siz benim (diriltilip) çıkarılacağımı mı bana va\'dediyor(beni bununla mı tehdidediyor)sunuz?" dedi. Onlarsa Allah\'a sığınarak: "Yazık sana, (etme, gel) inan; Allah\'ın sözü gerçektir" derken o: "Bu, eskilerin masallarından başka bir şey değildir." der.'),
(4528, 46, 18, 'İşte onlar da kendilerine (azab) söz(ü) gerekli olmuş kimselerdir. Kendilerinden önce geçen cin ve insan toplulukları arasında (azabın içinde) bulunacaklardır. Gerçekten onlar, ziyana uğrayanlardır.'),
(4529, 46, 19, 'Her birinin (inananların ve inanmayanların) yaptıkları işlerden dereceleri vardır. Allah, onlara yaptıklarının karşılığını tam verir; kendilerine hiç haksızlık edilmez.'),
(4530, 46, 20, 'İnkar edenler ateşe sunulacakları gün, (kendilerine denir ki): "Dünya hayatınızda bütün güzel şeylerinizi zayi ettiniz; (orada) bunlarla sefa sürüp bunları tükettiniz (burası için hiçbir şey bırakmadınız). Yeryüzünde haksız yere büyüklük taslamanızdan ve yoldan çıkmanızdan ötürü bugün, alçaltıcı bir azab ile cezalandırılacaksınız."'),
(4531, 46, 21, 'Ad\'ın kardeşini (Hud\'u) an: AhÆaf\'taki kavmini uyarmıştı. Onun önünden ve ardından nice uyarıcılar da gelip geçti (demişti ki): "Allah\'tan başkasına kulluk etmeyin; ben sizin, büyük bir günün azabına uğramanızdan korkuyorum."'),
(4532, 46, 22, 'Dediler: "Sen bizi tanrılarımızdan çevirmek için mi geldin? Doğrulardan isen bizi tehdidettiğin şeyi bize getir."'),
(4533, 46, 23, 'Dedi: "(Azabın ne zaman geleceğine dair) Bilgi, ancak Allah katındadır. Ben, tebliğ görevlendirildiğim mesajı size duyuruyorum; fakat sizi cahillik eden bir kavim görüyorum."'),
(4534, 46, 24, 'Nihayet azabın (ufukta) geniş bir bulut halinde vadilerine doğru geldiğini görünce: "Bu, bize yağmur yağdıracak bir buluttur" dediler. Hayır, o sizin acele gelmesini istediğiniz şey, içinde acı azab bulunan bir rüzgardır.'),
(4535, 46, 25, 'Rabbinin emriyle her şeyi yıkar, mahveder. Derken onlar o hale geldiler ki konutlarından başka bir şey görülmez oldu. İşte biz, suç işleyen toplumu böyle cezalandırırız.'),
(4536, 46, 26, 'Onlara size vermediğimiz servet ve kuvveti vermiştik, onlara kulaklar, gözler ve gönüller yaratmıştık. Fakat ne kulakları, ne gözleri ne de gönülleri kendilerine bir yarar sağladı. Zira (düşünüp ibret almıyorlar, tersine) bile bile Allah\'ın ayetlerini inkar ediyorlardı. Ve alay edip durdukları şey, kendilerini kuşatıverdi.'),
(4537, 46, 27, 'Andolsun, biz çevrenizdeki kentleri de yok ettik ve belki (küfürlerinden) dönerler diye ayetleri tekrar tekrar açıkladık.'),
(4538, 46, 28, 'Allah\'tan başka, kendilerine (Allah yanında) yakınlık sağlamak için tanrı edindikleri şeyler, kendilerine yardım etselerdi ya! Hayır, (tanrıları), onlardan kaybolup gittiler. İşte onların yalanları ve uydurmaları budur.'),
(4539, 46, 29, 'Bir zaman, cinlerden bir topluluğu Kur\'an dinlemek üzere sana yöneltmiştik. Ona geldiklerinde (birbirlerine): "Susun, (dinleyin)" dediler. (Okuma) Bitirilince de uyarıcılar olarak kavimlerine döndüler:'),
(4540, 46, 30, 'Ey kavmimiz, dediler, biz Musa\'dan sonra indirilen, kendinden öncekini doğrulayan, gerçeğe ve doğru yola götüren bir Kitap dinledik.'),
(4541, 46, 31, 'Ey kavmimiz, Allah\'ın da\'vetçisine uyun ve O\'na inanın ki (Allah) günahlarınızdan bir kısmını bağışlasın ve sizi, acı azabdan korusun.'),
(4542, 46, 32, 'Kim Allah\'ın da\'vetçisine uymazsa, yeryüzünde (başına inecek belaya) engel olamaz. Kendisinin O\'ndan başka velileri de olmaz. Onlar, apaçık bir sapıklık içindedirler.'),
(4543, 46, 33, 'Gökleri ve yeri yaratan, bunları yaratmakla yorulmayan Allah\'ın, ölüleri diriltmeğe de kadir olduğunu görmediler mi? Evet O, her şeye kadirdir.'),
(4544, 46, 34, 'İnkar edenler ateşe sunulacakları gün (Allah onlara): "(Nasıl), Bu gerçek değil miymiş?" (der); "Evet Rabbimiz hakkı için (gerçekmiş)" derler. "Öyleyse inkar etmenizden dolayı azabı tadın" der.'),
(4545, 46, 35, 'O halde sen de, azim (ve irade) sahibi elçilerin sabrettikleri gibi sabret. O (nankör)ler için acele etme. Onlar, tehdidedildikleri azabı gördükleri gün, sanki gündüzün sadece bir sa\'ati kadar yaşamış gibi olurlar. (Bu) Bir duyurudur. Yoldan çıkmış topluluktan başkası mı helak edilecektir?');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Allah, inkar edip kendisinin yoluna engel olanların işlerini boşa çıkarmıştır.'),
(4547, 47, 2, 'İnanıp iyi işler yapanların, Rableri tarafından Muhammed\'e indirilen gerçeğe inananların da günahlarını örtmüş ve hallerini düzeltmiştir.'),
(4548, 47, 3, 'Bu, böyledir: Çünkü inkar edenler batıla uymuşlar; inananlar ise Rablerinden gelen hakka uymuşlardır. İşte Allah, onların durumlarını, insanlara böyle anlatır.'),
(4549, 47, 4, '(Savaşta) İnkar edenlerle karşılaştığınız zaman boyunlarını vurun. Nihayet onları iyice vurup sindirince bağı sıkıca bağlayın (onları esir alın). Ondan sonra artık ya lutfen bırakır veya karşılığında fidye alırsınız. Harb, ağırlıklarını bırakıncaya (savaş sona erinceye) kadar (böyle yaparsınız). Allah dileseydi (kendisi) onlardan öc alırdı, fakat sizi birbirinizle denemek için (size savaşı emrediyor). Allah, kendi yolunda öldürülenlerin yaptıkları işleri zayi etmeyecektir.'),
(4550, 47, 5, 'Onları doğru yola iletecek ve durumlarını düzeltecektir.'),
(4551, 47, 6, 'Onları, kendilerine tanımladığı cennete sokacaktır.'),
(4552, 47, 7, 'Ey inananlar, eğer siz Allah(ın dinin)e yardım ederseniz (Allah da) size yardım eder; ayaklarınızı (hakkı koruma yolunda) sağlam tutar.'),
(4553, 47, 8, 'İnkar edenler(e gelince): Yıkım onlara! (Allah) Onların işlerini boşa çıkarmıştır.'),
(4554, 47, 9, 'Böyledir, çünkü onlar, Allah\'ın indirdiğinden hoşlanmamışlar, Allah da onların amellerini heder etmiştir.'),
(4555, 47, 10, '(Onlar) Yeryüzünde gezip dolaşmadılar mı ki kendilerinden öncekilerin sonunun nasıl olduğunu görsünler? Allah onları(n evlerini, barklarını) yıkıp başlarına geçirmiştir. Bu kafirlere de, onun benzeri sonuçlar vardır.'),
(4556, 47, 11, 'Bu böyledir, çünkü Allah inananların koruyucusudur. Kafirlerin ise koruyucuları yoktur. *'),
(4557, 47, 12, 'Allah, inanıp iyi işler yapanları, altlarından ırmaklar akan cennetlere sokar. İnkar edenler ise dünyada biraz yaşarlar, hayvanların yediği gibi yerler, (sonunda) yerleri ateştir.'),
(4558, 47, 13, 'Seni (içinden) çıkarmış olan kentten daha kuvvetli nice kent var ki biz onları yok ettik de onlara yardım eden çıkmadı.'),
(4559, 47, 14, 'Rabbinden bir delil üzerinde bulunan insan, kötü işi kendilerine süslendirilen ve keyiflerine uyan kimseler gibi olur mu? (Olmaz elbet).'),
(4560, 47, 15, '(Şirkten, günahlardan) korunanlara söz verilen cennetin durumu şudur: İçinde bozulmayan su ırmakları, tadı değişmeyen süt ırmakları, içenlere lezzet veren şarap ırmakları ve süzme bal ırmakları vardır ve onlar için orada her çeşit meyva, Rablerinden de bağışlama vardır. (Şimdi bu ni\'metler içinde yaşayanlar) ateşte ebedi kalan ve barsaklarını parça parça kesen sıcak suyun içirildiği kimseler gibi olur mu?'),
(4561, 47, 16, 'Onlardan kimi de gelip seni dinler. Fakat senin yanından çıktıkları zaman kendilerine bilgi verilmiş olanlara: "Demin ne söyledi?" derler. Onlar Allah\'ın kalblerini mühürlediği, keyiflerinin ardına düşmüş kimselerdir.'),
(4562, 47, 17, 'Hidayet bulanlara gelince, Allah onların hidayetlerini artırmış ve onlara korunmalarını (kendilerini kötü sonuçtan koruma çareleri) vermiştir.'),
(4563, 47, 18, '(İnanmayanlar) İlle (helak edilecekleri) sa\'atin ansızın kendilerine gelmesini mi bekliyorlar? İşte onun belirtileri geldi. O uyarıldıkları sa\'at kendilerine geldikten sonra artık öğüt almaları nereden mümkün olsun?'),
(4564, 47, 19, 'Allah\'tan başka tanrı olmadığını bil ve kendi günahın, inanan erkeklerin ve inanan kadınların günahı için (Allah\'tan) mağfiret dile. Allah, dönüp dolaşacağınız yeri ve varıp duracağınız yeri bilir.'),
(4565, 47, 20, 'İnananlar: " (Savaş hakkında) Bir sure indirilmeli değil miydi?" derler. Fakat hükmü açık bir sure indirilip de onda savaştan söz edilince, kalblerinde hastalık bulunanların sana ölümden bayılıp düşen kimsenin bakışı gibi baktıklarını görürsün. Onlara ölüm gerektir.'),
(4566, 47, 21, 'Onlara düşen, ita\'at etmek ve güzel söz söylemektir. İş ciddiye bindiği zaman Allah\'a verdikleri söze sadık kalsalardı, elbette kendileri için daha iyi olurdu.'),
(4567, 47, 22, 'Demek işbaşına gelecek olursanız, yeryüzünde bozgunculuk yapacak, rahimleri (akrabalık bağlarını) koparacaksınız öyle mi?"'),
(4568, 47, 23, 'Onlar, Allah\'ın la\'netleyip sağır yaptığı ve gözlerini kör ettiği kimselerdir.'),
(4569, 47, 24, 'Kur\'an\'ı(n anlamını) düşünmüyorlar mı? Yoksa kalbler(inin) üzerinde kilitleri mi var (ki hiçbir hakikat, gönüllerine girmiyor)?'),
(4570, 47, 25, 'Kendilerine doğru yol belli olduktan sonra arkalarına (yine eski küfürlerine) dönenlere, şeytan hatalarını süslemiş ve (günah işlemelerini) kolaylaştırmış ve onları uzun emellere, umutlara düşürmüştür.'),
(4571, 47, 26, 'Bu böyledir. Çünkü onlar, Allah\'ın indirdiğinden hoşlanmayanlara: "Bazı hususlarda size ita\'at edeceğiz" dediler. Oysa Allah, onların gizlediklerini biliyor.'),
(4572, 47, 27, 'Ya melekler, canlarını alırrken yüzlerine ve kıçlarına vurarak dövünecekleri zaman durumları nice olur?'),
(4573, 47, 28, 'Bu böyledir. Çünkü onlar, Allah\'ı kızdıran şeylerin ardınca gittiler. O\'nu razı edecek şeylerden hoşlanmadılar. Allah da onların amellerini boşa çıkardı.'),
(4574, 47, 29, 'Yoksa kalblerinde hastalık bulunanlar, Allah\'ın, kendilerinin kinlerini ortaya çıkarmayacak mı sandılar?'),
(4575, 47, 30, 'Biz dileseydik onları sana gösterirdik, sen onları simalarından tanırdın ve onları sözlerinin üslubundan tanırdın. Allah yaptığınız işleri bilir.'),
(4576, 47, 31, 'Andolsun biz sizi deneyeceğiz ki içinizden cihadedenleri (güçlüklere) sabredenleri bilelim ve söylediğiniz sözlerin (doğru olup olmadığını) sınayalım.'),
(4577, 47, 32, 'Nankörlük edip Allah yoluna engel olanlar ve kendilerine doğru yol belli olduktan sonra Elçiyi incitenler Allah\'a hiçbir zarar veremezler. Allah onların işlerini boşa çıkaracaktır.'),
(4578, 47, 33, 'Ey inananlar, Allah\'a ita\'at edin, Elçi\'ye ita\'at edin, işlerinizi boşa çıkarmayın.'),
(4579, 47, 34, 'Nankörlük edip Allah yoluna engel olan, sonra kafir olarak ölenleri Allah affetmeyecektir.'),
(4580, 47, 35, 'Siz galip durumda iken gevşeyip barış istemeyin. Allah sizinle beraberdir, O sizin amellerinizi zayi etmeyecektir.'),
(4581, 47, 36, 'Dünya hayatı, bir oyun ve eğlenceden ibarettir. Eğer inanır, (günahlardan) korunursanız (Allah) size mükafatlarınızı verir ve sizden (bütün) mallarınızı istemez.'),
(4582, 47, 37, 'Eğer onları isteseydi de sizi sıkıştırsaydı, cimrilik ederdiniz ve kinlerinizi ortaya çıkarırdı (Allah\'ın Elçisine kin beslemeğe başlardınız).'),
(4583, 47, 38, 'İşte sizler, Allah yolunda harcamağa çağrılıyorsunuz; ama içinizden kimisi cimrilik ediyor. Cimrilik eden, kendi nefsine karşı cimrilik etmiş olur. Allah zengindir, sizler fakirsiniz. Eğer yüz çevirecek olursanız, Allah, yerinize başka bir toplum getirir de onlar sizin gibi olmazlar.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Biz sana apaçık bir fetih verdik.'),
(4585, 48, 2, 'Ki Allah, senin geçmiş ve gelecek günahını bağışlasın (bütün tasalarını gidersin) ve sana olan ni\'metini tamamlasın ve seni doğru bir yola iletsin.'),
(4586, 48, 3, 'Ve Allah sana şanlı bir zafer versin.'),
(4587, 48, 4, 'O, imanlarına iman katsınlar diye mü\'minlerin kalblerine huzur indirdi. Göklerin ve yerin askerleri Allah\'ındır. Allah bilendir, hüküm ve hikmet sahibidir.'),
(4588, 48, 5, '(Allah işini böyle hikmetle çevirir, mü\'minlerin gönüllerine huzur verir, onlara görünmez askerleriyle yardım eder) Ki inanan erkekleri ve inanan kadınları, altlarından ırmaklar akan, içinde ebedi kalacakları cennetlere soksun, onların kötülüklerini de örtsün. Gerçekten bu, Allah katında büyük bir başarıdır.'),
(4589, 48, 6, 'Allah hakkında kötü zanda bulunan münafık erkeklere ve münafık kadınlara; (Allah\'a) ortak koşan erkeklere ve ortak koşan kadınlara da azabetsin. (Onların, müslümanlar için istedikleri) Kötü olaylar, kendi başlarına gelsin. Allah, onlara gazabetmiş, onları la\'netlemiş ve onlara cehennemi hazırlamıştır. Orası da ne kötü bir yerdir!'),
(4590, 48, 7, 'Göklerin ve yerin askerleri Allah\'ındır. Allah azizdir, hakimdir.'),
(4591, 48, 8, 'Biz seni, şahid, müjdeleyici ve uyarıcı olarak gönderdik.'),
(4592, 48, 9, 'Ki Allah\'a ve Resulüne inanasınız, O\'nu(n dinini) destekleyesiniz. Ona saygı gösteresiniz ve sabah akşam O\'nu tesbih ed(ip şanını yücelt)esiniz...'),
(4593, 48, 10, 'Sana bi\'at edenler (İslam uğrunda ölünceye kadar savaşmak üzere sana söz verenler), gerçekte Allah\'a bi\'at etmektedirler. Allah\'ın eli, onların ellerinin üzerindedir. Kim ahdini bozarsa, kendi aleyhine bozmuş olur. Ve kim Allah\'a verdiği sözü tutarsa Allah ona büyük bir mükafat verecektir.'),
(4594, 48, 11, 'Göçebe Araplardan geri bırakılanlar, sana diyecekler ki: "Mallarımız ve çocuklarımız bizi, (seninle beraber gelmekten) alıkoydu. Bizim için mağfiret dile." Onlar, dilleriyle kalblerinde olmayan bir şeyi söylüyorlar. De ki: "Allah size bir zarar vermek istemiş, yahut size bir yarar vermek istemiş olsa Allah\'ın, sizin için dilediğine kim engel olabilir? Hayır, Allah yaptıklarınızı haber almaktadır."'),
(4595, 48, 12, 'Herhalde siz sandınız ki Elçi ve mü\'minler, bir daha ailelerine dönmeyecekler. Bu (düşünce) gönüllerinizde süslendirildi, (size güzel gösterildi,) kötü zanda bulundunuz ve helaki hak etmiş bir topluluk oldunuz.'),
(4596, 48, 13, 'Kim Allah\'a ve Elçisine inanmazsa bilsin ki, biz, kafirler için alevli bir ateş hazırlamışızdır.'),
(4597, 48, 14, 'Göklerin ve yerin mülkü Allah\'ındır. O, dilediğini bağışlar, dilediğine azab eder. Allah bağışlayandır, esirgeyendir.'),
(4598, 48, 15, 'O geri bırakılanlar, ganimetleri almak için gittiğiniz zaman: "Bizi bırakın, sizinle beraber gelelim," diyecekler. Onlar, Allah\'ın sözünü değiştirmek istiyorlar. De ki: "Siz, bizimle gelemezsiniz. Allah, önceden böyle buyurdu." Onlar: "Bizi çekemiyorsunuz" diyecekler. Hayır, onlar, pek az anlarlar.'),
(4599, 48, 16, 'O geride kalan göçebe Araplara de ki: "Siz yakında çok kuvvetli bir kavme karşı savaşmağa da\'vet edileceksiniz, onlarla (ya) dövüşürsünüz, yahut (onlar) müslüman olurlar. Eğer ita\'at ederseniz, Allah size güzel bir mükafat verir; (yok) eğer önceden döndüğünüz gibi yine dönerseniz, size acı bir şeklide azabeder.'),
(4600, 48, 17, 'Köre güçlük yoktur, topala güçlük yoktur, hastaya güçlük yoktur. (Bunlar savaşa katılmak zorunda değillerdir.) Kim Allah\'a ve Elçisine ita\'at ederse (Allah) onu, altından ırmaklar akan cennetlere sokar. Kim de yüz çevirirse onu da acı bir azaba uğratır.'),
(4601, 48, 18, 'Allah şu mü\'minlerden razı olmuştur ki onlar, ağacın altında sana bi\'at ediyorlardı, Allah onların gönüllerinden geçeni bildiği için onların üzerine huzur ve güven indirdi ve onlara yakın bir fetih verdi.'),
(4602, 48, 19, 'Yine onlara alacakları birçok ganimetler bahşeyledi. Allah üstündür, hüküm ve hikmet sahibidir.'),
(4603, 48, 20, 'Allah size elde edeceğiniz birçok ganimetler va\'detti. Şimdilik size bu(Hudeybiye Barışı)nı verdi. İnsanların ellerini sizden çekti ki bu, inananlara bir ibret olsun ve (Allah) sizi dosdoğru yola iletsin.'),
(4604, 48, 21, '(Size) Başka (ganimetler) de söz vermiştir ki henüz onları ele geçiremediniz, fakat Allah onları kuşatmış(sizin için ayırmış)tır. Allah her şeye yapabilir.'),
(4605, 48, 22, 'Eğer kafirler sizinle savaşsalardı, arkalarına dön(üp kaç)arlardı, sonra ne bir koruyucu, ne de bir yardımcı bulabilirlerdi.'),
(4606, 48, 23, 'Bu, Allah\'ın ötedenberi süregelen yasasadır: Allah\'ın yasasında bir değişme bulamazsın.'),
(4607, 48, 24, 'Mekke\'nin göbeğinde, sizi onlara galip getirdikten sonra onların ellerini sizden, sizin ellerinizi onlardan çeken O\'dur. Allah, yaptıklarınızı görmektedir.'),
(4608, 48, 25, 'Onlar öyle kimselerdir ki inkar ettiler, sizin Mescid-i Haram\'ı ziyaret etmenize ve bekletilen kurbanların yerlerine varmasına engel oldular. Eğer orada, kendilerini bilmediğiniz için tepeleyeceğiniz ve bilmeyerek tepelemenizden ötürü, kınanacağınızinanmış erkekler ve inanmış kadınlar olmasaydı (Allah sizin savaşmanıza engel olmazdı. Böyle yaptı) ki Allah, dilediğini rahmetine soksun. Şayet (inananlar ve inanmayanlar) birbirinden ayrılmış olsalardı elbette onlardan inkar edenleri, acı bir azaba çarptırırdık.'),
(4609, 48, 26, 'O zaman inkar edenler, kalblerine öfke ve gayreti, o cahiliyye (çağının) öfke ve gayretini koymuşlardı, Allah da Elçisine ve mü\'minlere huzur ve güvenini indirdi; onları takva kelimesine (sebata ve ahde vefaya) bağladı. Zeten onlar, buna layık ve ehil idiler. Allah, her şeyi bilendir.'),
(4610, 48, 27, 'Andolsun, Allah, Elçisinin rüyasını doğru çıkardı. Allah dilerse, başlarınızı (kökten) traş ederek ve(ya) saçlarınızı kısaltarak, korkmadan, güven içinde Mescid-i Haram\'a gireceksiniz. Allah sizin bilmediğinizi bildi, bundan önce size yakın bir fetih verdi.'),
(4611, 48, 28, 'O, Elçisini hidayet ve hak dinle gönderdi ki, o(hak di)ni, bütün dinlere üstün kılsın. Şahid olarak Alah yeter.'),
(4612, 48, 29, 'Muhammed Allah\'ın elçisidir. Onun yanında bulunanlar, kafirlere karşı katı, birbirlerine karşı merhametlidirler. Onların, rüku\' ve secde ederek Allah\'ın lutuf ve rızasını aradıklarını görürsün. Yüzlerinde secde izinden nişanları vardır. Onların Tevrat\'taki vasıfları ve İncildeki vasıfları da şöyle bir ekin gibidir ki, filizini çıkardı, onu güçlendirdi, kalınlaştı, derken gövdesinin üstüne dikildi, ekincilerin hoşuna gider, onlara karşı kafirleri de öfkelendirir bir duruma geldi. Allah onlardan inanıp iyi işler yapanlara mağfiret ve büyük mükafat va\'detmiştir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Ey inananlar, Allah\'ın ve Elçisinin önüne geçmeyin. Allah\'tan korkun. Şüphesiz Allah, işitendir, bilendir.'),
(4614, 49, 2, 'Ey inananlar, seslerinizi, Peygamberin sesinin üstüne çıkarmayın, birbirinizle yüksek sesle konuştuğunuz gibi, onunla da öyle yüksek sesle konuşmayın; yoksa siz farkında olmadan amelleriniz boşa gider.'),
(4615, 49, 3, 'Allah\'ın Elçisinin huzurunda seslerini kısanlar, öyle kimselerdir ki Allah, onların kalblerini, takva için imtihan etmiş(onların takvaya ehil olduklarını anlamış)tır. Onlar için mağfiret ve büyük bir mükafat vardır.'),
(4616, 49, 4, 'Odaların arkasından sana bağıranların çoğu, düşüncesiz kimselerdir.'),
(4617, 49, 5, 'Onlar, sen kendilerinin yanına çıkıncaya kadar bekleselerdi, elbette kendileri için daha iyi olurdu. Allah bağışlayandır, esirgeyendir.'),
(4618, 49, 6, 'Ey inananlar, size fasık (yoldan çıkmış) bir adam bir haber getirirse onun doğruluğunu araştırın. Yoksa bilmeyerek bir topluluğa karşı kötülük edersiniz de sonra yaptığınıza pişman olursunuz.'),
(4619, 49, 7, 'Bilin ki, Allah\'ın Elçisi içinizdedir. Şayet o, birçok işte size uysaydı, sıkıntıya düşerdiniz. Fakat Allah size imanı sevdirdi ve onu sizin kalblerinizde süsledi ve size küfrü, fıskı ve isyanı çirkin gösterdi. İşte doğru yolda olanlar bunlardır.'),
(4620, 49, 8, '(Bu) Allah\'ın bir lutuf ve ni\'metidir. Allah bilendir, hakimdir.'),
(4621, 49, 9, 'Eğer inananlardan iki grup vuruşurlarsa onların arasını düzeltin; şayet biri ötekine saldırırsa Allah\'ın buyruğuna dönünceye kadar saldıran tarafla vuruşun. (Allah\'ın buyruğuna) Dönerse artık adaletle onların arasını düzeltin ve daima adil olun. Çünkü Allah, adalet yapanları sever.'),
(4622, 49, 10, 'Muhakkak mü\'minler kardeştirler. Kardeşlerinizin arasını düzeltin ve Allah\'tan korkun ki size rahmet edilsin.'),
(4623, 49, 11, 'Ey inananlar, bir topluluk, başka bir toplulukla alay etmesin. Belki (alay ettikleri kimseler), kendilerinden iyidirler. Kadınlar da başka kadınlarla alay etmesinler. Belki onlar, kendilerinden iyidirler. Birbirinizde kusur aramayın; birbirinizi kötü lakaplarla çağırmayın. İnandıktan sonra fısk adı, ne kötü bir şeydir! Kim tevbe etmezse, işte onlar, zalimdirler.'),
(4624, 49, 12, 'Ey inananlar, zandan çok sakının. Zira zannın bir kısmı günahtır. Birbirinizin gizli şeylerini araştırmayın; biriniz diğerinizi arkasından çekiştirmesin. Biriniz, ölmüş kardeşinin etini yemeği sever mi? İşte bundan iğrendiniz. O halde Allah\'tan korkun, şüphesiz Allah, tevbeyi çok kabul eden, çok esirgeyendir.'),
(4625, 49, 13, 'Ey insanlar, biz sizi bir erkek ve bir kadından yarattık ve birbirinizi tanımanız için sizi milletlere ve kabilelere ayırdık. Allah yanında en üstün olanınız, (günahlardan) en çok korunanınızdır. Allah bilendir, haber alandır.'),
(4626, 49, 14, 'Göçebe Araplar: "İnandık" dediler. De ki: "İnanmadınız, fakat \'İslam olduk\' deyin. Henüz iman kalblerinize girmedi. Eğer Allah\'a ve Elçisine ita\'at ederseniz (Allah), yaptığınız güzel işlerden hiçbirinin sevabını size eksik vermez. Allah çok bağışlayan, çok esirgeyendir."'),
(4627, 49, 15, 'Mü\'minler onlardır ki Allah\'a ve Elçisine inandılar, sonra şüphe etmediler; Allah yolunda mallarıyle, canlarıyle cihadettiler. İşte (iman sözlerinde) doğru olanlar onlardır.'),
(4628, 49, 16, 'De ki: "Siz mi Allah\'a din(darlığ)ınızı öğreteceksiniz? Allah, göklerde ve yerde olanları bilir. Allah, her şeyi bilendir."'),
(4629, 49, 17, 'İslam olmalarını senin başına kakıyorlar. De ki: "Müslüman olmanızı benim başıma kakmayın. Tersine, eğer gerçekten inanmışsanız, sizi imana ilettiği için Allah, sizin başınıza kaksa yeridir."'),
(4630, 49, 18, 'Şüphesiz Allah, göklerin ve yerin gizlisini bilir. Allah yaptıklarınızı görmektedir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Kaf. Zikir\'li (uyarıcı, şerefli) Kur\'an\'a andolsun,'),
(4632, 50, 2, 'İçlerinden bir uyarıcı gelmesine şaştılar da, o kafirler: "Bu tuhaf bir şeydir" dediler.'),
(4633, 50, 3, 'Biz öldüğümüz ve toprak olduğumuz zaman mı (tekrar bedene döneceğiz)? Bu, uzak bir dönüştür.'),
(4634, 50, 4, 'Biz yerin, onlar(ın cesetlerin)den ne eksilttiğini bilmişizdir. Yanımızda (her şeyi) zapteden bir Kitap vardır.'),
(4635, 50, 5, 'Doğrusu onlar, hak kendilerine gelince onu yalanladılar. Şimdi onlar çalkantılı bir durumun içindedirler.'),
(4636, 50, 6, 'Üstlerindeki göğe bakmadılar mı, onu nasıl yaptık, süsledik, hiçbir çatlağı yoktur?'),
(4637, 50, 7, 'Arzı nasıl yaydık, ona sağlam dağlar attık, onda her güzel çifti bitirdik!'),
(4638, 50, 8, '(Bütün bunları) Allah\'a yönelen her kulun, gönül gözünü açmak için ve (ona) ibret vermek için (yaptık).'),
(4639, 50, 9, 'Gökten bereketli bir su indirdik, onunla bahçeler ve biçilecek dane(li ekin)ler bitirdik.'),
(4640, 50, 10, 'Birbirine girmiş kat kat tomurcukları olan yüksek hurma ağaçları yetiştirdik;'),
(4641, 50, 11, 'Kullara rızık olması için. Ve o su ile, ölü bir ülkeye can verdik. İşte çıkış da öyledir.'),
(4642, 50, 12, 'Onlardan önce Nuh kavmi, Resliler ve Semud (kavmi) de yalanlamıştı.'),
(4643, 50, 13, 'Ad, Fir\'avn ve Lut\'un kardeşleri (durumundaki kavmi),'),
(4644, 50, 14, 'Eyke halkı ve Tubba\' kavmi. Bunların hepsi elçileri yalanlayıp, uyardığım(azab)ı hak ettiler.'),
(4645, 50, 15, 'İlk yaratmadan aciz mi kaldık ki (yeniden yaratamayalım)? Doğrusu onlar yeni bir yaratmadan kuşku içindedirler:'),
(4646, 50, 16, 'Andolsun insanı biz yarattık ve nefsinin ona ne fısıldadığını biliriz, çünkü biz ona şah damarından daha yakınız.'),
(4647, 50, 17, 'Onun sağında ve solunda oturan iki alıcı (melek, onun sözlerini ve işlerini) kaydetmektedir.'),
(4648, 50, 18, '(İnsan,) Hiçbir söz söylemez ki yanında kendisini gözetleyen, dediklerini zapteden (bir melek) hazır bulunmasın.'),
(4649, 50, 19, 'Ölüm sarhoşluğu gerçekten geldi. İşte (ey insan) bu, senin öteden beri kaçtığın şeydir.'),
(4650, 50, 20, 'Sur\'a üflendi. İşte bu, kendisine karşı uyarılan gündür.'),
(4651, 50, 21, 'Her can, yanında bir sürücü ve şahidle geldi.'),
(4652, 50, 22, '(Allah ona): "Andolsun, sen bundan gaflet içinde idin. Biz sen(in gözün)den perdeni açtık; bugün artık gözün keskindir" (dedi).'),
(4653, 50, 23, 'Yanındaki arkadaşı: "İşte yanımdaki hazır" dedi.'),
(4654, 50, 24, '(Allah sürücü ve şahide buyurdu ki): "Haydi ikiniz, atın cehenneme her inatçı nankörü!"'),
(4655, 50, 25, 'Hayra engel olan, saldırgan, şüpheciyi.'),
(4656, 50, 26, 'O ki Allah ile beraber başka tanrılar edindi, bundan dolayı onu çetin bir azaba atın.'),
(4657, 50, 27, 'Yanındaki arkadaşı dedi ki: "Rabbimiz, ben onu azdırmadım, zaten o kendisi derin bir sapıklık içinde idi."'),
(4658, 50, 28, '(Allah) Buyurdu ki: "Huzurumda çekişmeyin. Ben size daha önce uyarı göndermiştim."'),
(4659, 50, 29, 'Benim huzurumda söz değiştirilmez ve ben kullara zulmedici değilim.'),
(4660, 50, 30, 'O gün cehenneme: "Doldun mu?" deriz. "Daha yok mu" der.'),
(4661, 50, 31, 'Cennet de korunanlara yaklaştırılmıştır, uzak değildir.'),
(4662, 50, 32, 'İşte size va\'dedilen budur. Daima Allah\'a yüz tutan (O\'nun buyruklarını) koruyan, 	(And it is said): That is that which ye were promised. (It is) for every penitent and heedful one,'),
(4663, 50, 33, 'Görmeden Rahman\'a saygı gösteren ve (Hakka) dönük bir yürek getiren herkesin (mükafatı budur)!"'),
(4664, 50, 34, 'Ona selam (esenlik) ile girin. Bu, süreklilik günüdür!'),
(4665, 50, 35, 'Orada onlara istedikleri herşey vardır. Katımızda daha fazlası da vardır.'),
(4666, 50, 36, 'Bunlardan önce nice kuşakları helak etmiştik ki onların tutuşu, bunlardan daha kuvvetli idi, yakalaması daha güçlü idi. Ülkelerde gezip dolaşmışlardı, ama bir kurtuluş buldular mı?'),
(4667, 50, 37, 'Muhakkak ki bunda, kalbi olan, yahut şahid olarak (zihnini toplayarak dikkatle) kulak veren kimse için bir öğüt vardır.'),
(4668, 50, 38, 'Andolsun, biz gökleri, yeri ve ikisi arasında bulunanları altı günde yarattık, bize hiçbir yorgunluk dokunmadı.'),
(4669, 50, 39, 'Onların dediklerine sabret ve Rabbini övgü ile an: güneş doğmadan önce, batmadan önce,'),
(4670, 50, 40, 'Gecenin bir kısmında ve secde arkalarında O\'nu tesbih et.'),
(4671, 50, 41, 'Dinle, o gün o ünleyici, yakın bir yerden çağırır.'),
(4672, 50, 42, 'O gün o çağrıyı gerçek olarak duyarlar. İşte bu, (dirilip) çıkış günüdür.'),
(4673, 50, 43, 'Yaşatan ve öldüren ancak biziz, biz. Dönüş de bizedir.'),
(4674, 50, 44, 'O gün yer onlar(ın üstün)den yarıl(ıp açıl)ır, (çağırana doğru) sür\'atle koşarlar. İşte bu, toplamadır; bize göre kolaydır.'),
(4675, 50, 45, 'Biz onların ne dediklerini biliyoruz. Sen onların üstünde bir zorlayıcı değilsin, sadece tehdidimden korkanlara Kur\'an ile öğüt ver.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Savurup kaldıranlara,'),
(4677, 51, 2, '(Yağmur) Yüklü (bulut)lara,'),
(4678, 51, 3, 'Kolayca akıp gidenlere,'),
(4679, 51, 4, 'İş(ler)i taksim edenlere (rızıkları, yağmurları dağıtan güçlere) andolsun ki,'),
(4680, 51, 5, 'Size va\'dedilen, mutlaka doğrudur.'),
(4681, 51, 6, 'Ceza muhakkak olacaktır.'),
(4682, 51, 7, '(Çeşitli) yolları (yörüngeleri) bulunan göğe andolsun ki,'),
(4683, 51, 8, 'Siz, çeşitli söz(ler) içindesiniz.'),
(4684, 51, 9, 'Çevrilen, ondan çevriliyor.'),
(4685, 51, 10, 'O (çeşitli sözleri) atan yalancılar kahrolsun!'),
(4686, 51, 11, 'Onlar aptallık içinde yanılıp durmaktadırlar.'),
(4687, 51, 12, 'Ceza günü ne zaman? diye sorarlar.'),
(4688, 51, 13, 'O gün onlar ateş üzerinde yakılacaklardır.'),
(4689, 51, 14, '(Kendilerine): "Fitnenizi (fesadınızın cezasını) tadın! Acele isteyip durduğunuz şey budur işte!" (denilecek).'),
(4690, 51, 15, 'Korunanlar, cennetlerde, çeşme başlarındadırlar;'),
(4691, 51, 16, 'Rablerinin, kendilerine verdiğini alırlar. Çünkü onlar bundan önce güzel davranırlardı:'),
(4692, 51, 17, 'Geceleri pek az uyurlardı,'),
(4693, 51, 18, 'Seherlerde onlar istiğfar ederlerdi,'),
(4694, 51, 19, 'Mallarında dilenci ve yoksul için hak vardı.'),
(4695, 51, 20, 'Kesin inanacaklar için yerde nice ibretler vardır.'),
(4696, 51, 21, 'Kendi canlarınızda da öyle. Görmüyor musunuz?'),
(4697, 51, 22, 'Gökte rızkınız da var, uyarıldığınız (azab)da var!'),
(4698, 51, 23, 'Göğün ve yerin Rabbine andolsun ki o, sizin konuştuğunuz gibi gerçektir.'),
(4699, 51, 24, 'İbrahim\'in ağırlanan konuklarının haberi sana geldi mi?'),
(4700, 51, 25, 'Bir zaman onun yanına girmişler: "Selam" demişlerdi. "Selam, dedi, (siz) tanınmamış bir topluluk(sunuz)."'),
(4701, 51, 26, '(Konuklarına yemek hazırlamak için) gizlice ailesinin yanına gitti, semiz bir buzağı getirdi.'),
(4702, 51, 27, 'Onu, önlerine yaklaştırdı, "Yemez misiniz?" dedi.'),
(4703, 51, 28, '(Yemediklerini görünce) Onlardan içine bir korku düşürdü. "Korkma" dediler ve ona bilgin bir oğlan çocuğu müjdelediler.'),
(4704, 51, 29, 'Karısı (Sare) çığlık içinde geldi (hayretten elini) yüzüne vurarak: "(Ben) Kısır bir koca karı(yım, benden nasıl çocuk olur)?" dedi.'),
(4705, 51, 30, 'Dediler ki: "Rabbin böyle dedi. O, hüküm ve hikmet sahibidir, bilendir."'),
(4706, 51, 31, '(İbrahim): "O halde göreviniz nedir ey elçiler?" dedi.'),
(4707, 51, 32, 'Dediler: "Biz suçlu bir kavme gönderildik."'),
(4708, 51, 33, 'Ki onların üzerine çamurdan taş(lar) salalım.'),
(4709, 51, 34, 'Rabbinin katında, haddi aşanlar için işaretlenmiş (taşlar).'),
(4710, 51, 35, 'Orada bulunan mü\'minleri çıkardık.'),
(4711, 51, 36, 'Zaten orada bir ev(halkın)dan başka müslüman da bulmadık.'),
(4712, 51, 37, 'Acı azabdan korkanlar için orada bir ibret bıraktık.'),
(4713, 51, 38, 'Musa\'da da (ibret alınacak şeyler vardır). Onu açık bir delil ile Fir\'avn\'e göndermiştik.'),
(4714, 51, 39, '(Fir\'avn ona) Yanını çevirdi ve: "Bu, ya büyücü veya cinlidir" dedi.'),
(4715, 51, 40, 'Biz de onu ve askerlerini yakaladık, onları denize attık. (O boğulurken pişmanlıkla) Kendi kendini kınıyordu.'),
(4716, 51, 41, 'Ad(kavmin)de de (ibret alınacak şeyler vardır). Onlara, köklerini kesen bir rüzgar gönderdik.'),
(4717, 51, 42, 'Üzerinden geçtiği hiçbir şeyi bırakmıyor, onu kül gibi ediyordu.'),
(4718, 51, 43, 'Semud(kavmin)de de (ibret alınacak şeyler vardır). Onlara: "Bir süreye kadar sefa sürün" denmişti.'),
(4719, 51, 44, 'Rablerinin buyruğuna başkaldırdılar, bu yüzden onlar bakıp dururlarken, onları yıldırım yakaladı.'),
(4720, 51, 45, '(Yurtlarında çöküverdiler) Ne kalkabildiler, ne de (bu duruma) engel olabildiler.'),
(4721, 51, 46, 'Daha önce de Nuh kavmini helak etmiştik. Çünkü onlar da yoldan çıkmış bir toplum idiler.'),
(4722, 51, 47, 'Göğü sağlam yaptık, biz genişleticiyiz (kudretimiz geniştir, göğü öyle genişleten biziz).'),
(4723, 51, 48, 'Yeri biz döşedik, (biz) ne güzel döşeyiciyiz.'),
(4724, 51, 49, 'Her şeyden iki çift (erkek-dişi) yarattık ki düşünüp öğüt alasınız.'),
(4725, 51, 50, 'O halde Allah\'a kaçın, ben size O\'nun tarafından görevlendirilmiş apaçık bir uyarıcıyım.'),
(4726, 51, 51, 'Allah ile beraber başka tanrılar uydurmayın. Ben size O\'nun tarafından görevlendirilmiş apaçık bir uyarıcıyım.'),
(4727, 51, 52, 'İşte böyle, onlardan önce de ne kadar elçi geldiyse mutlaka: "Büyücü veya cinlenmiş" dediler.'),
(4728, 51, 53, 'Bunu birbirlerine tavsiye mi ettiler (ki hep aynı şeyi söylüyorlar)? Doğrusu, onlar azgın bir topluluktur.'),
(4729, 51, 54, 'Onlardan yüz çevir, sen kınanacak değilsin.'),
(4730, 51, 55, 'Ama yine de hatırlat, çünkü hatırlatmak inananlara yararlıdır.'),
(4731, 51, 56, 'Ben cinleri ve insanları, ancak bana kulluk etsinler diye yarattım.'),
(4732, 51, 57, 'Ben onlardan rızık istemiyorum, beni beslemelerini de istemiyorum.'),
(4733, 51, 58, 'Şüphesiz rızık veren, sağlam kuvvet sahibi olan ancak Allah\'tır.'),
(4734, 51, 59, 'Muhakkak ki, bu zulmedenlerin de (geçmiş) arkadaşlarının payı gibi bir azab payı vardır, (ötekilerin başına gelen azab gibi bir azab bunların da başına gelecektir), acele etmesinler.'),
(4735, 51, 60, 'Uyarıldıkları günlerinden dolayı vay o kafirlerin haline!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Andolsun Tur\'a (Musa\'nın vahiy aldığı Sina Dağı\'na).'),
(4737, 52, 2, 'Satır satır yazılmış Kitaba;'),
(4738, 52, 3, 'Yayılmış ince deri üzerine,'),
(4739, 52, 4, 'Ma\'mur (bakımlı, şen) Ev (Ka\'be\'y)e,'),
(4740, 52, 5, 'Yükseltilmiş tavana (göğe),'),
(4741, 52, 6, 'Kaynatılmış denize (bunlara andolsun ki),'),
(4742, 52, 7, 'Rabbinin azabı mutlaka vukubulacaktır;'),
(4743, 52, 8, 'Ona engel olacak bir şey yoktur.'),
(4744, 52, 9, 'O gün gök, bir çalkalanış çalkanır,'),
(4745, 52, 10, 'Dağlar bir yürüyüş yürür ki!..'),
(4746, 52, 11, 'Yalanlayanların vay haline o gün!'),
(4747, 52, 12, 'O daldıkları batıl içinde oynayıp duranlar,'),
(4748, 52, 13, 'O gün (şöyle denilerek) cehennem ateşine kakılırlar:'),
(4749, 52, 14, 'İşte yalanlayıp durduğunuz ateş budur!'),
(4750, 52, 15, '(Nasıl) Şimdi bu, büyümüymüş, yoksa siz mi görmüyor muşsunuz?'),
(4751, 52, 16, 'Girin ona, ister dayanın, ister dayanmayın, sizin için birdir. Ancak yaptıklarınıza göre cezalandırılacaksınız.'),
(4752, 52, 17, 'Korunanlar da cennetlerde, ni\'met içindedirler.'),
(4753, 52, 18, 'Rablerinin kendilerine verdikleriyle sefa sürerler. Rableri onları, cehennem azabından korumuştur.'),
(4754, 52, 19, 'Yaptıklarınıza karşılık afiyetle yeyin, için;'),
(4755, 52, 20, 'Sıra sıra dizilmiş koltuklara yaslanarak. Onları, iri gözlü hurilerle evlendirmişizdir.'),
(4756, 52, 21, 'Kendileri inanmış, zürriyetleri de imanda kendilerine uymuş olan kimselerin zürriyetlerini de kendilerine katmışızdır; kendi ameller(inin sevab)ından da hiçbir şey eksiltmemişizdir. Herkes kendi kazandığına bağlıdır.'),
(4757, 52, 22, 'Ve onlara canlarının istediği meyvadan ve etten bol bol vermişizdir.'),
(4758, 52, 23, 'Orada bir kadeh kapışırlar ki içinde ne saçmalama var, ne de günaha sokma.'),
(4759, 52, 24, 'Çevrelerinde de kendilerine mahsus, sedef içinde saklı inci gibi civanlar dolaşır (hizmet eder).'),
(4760, 52, 25, 'Birbirlerine dönmüş soruyorlar:'),
(4761, 52, 26, 'Daha önce biz ailemiz içinde (iken sonumuzdan) korkardık. dediler.'),
(4762, 52, 27, 'Allah bize lutfetti de bizi o delikçiklere işleyen azabdan korudu.'),
(4763, 52, 28, 'Biz bundan önce yalnız O\'na yalvarır(bizi korumasını O\'ndan niyaz eder)dik. Çünkü iyilik eden, esirgeyen O\'dur, O.'),
(4764, 52, 29, '(Ey Muhammed), Sen hatırlat, öğüt ver. Rabbinin ni\'meti sayesinde sen ne kahinsin, ne de mecnun.'),
(4765, 52, 30, 'Yoksa onlar (senin hakkında): "Bir şa\'irdir, zamanın felaketlerine çarpılmasını gözetliyoruz" mu diyorlar?'),
(4766, 52, 31, 'De ki: "Gözetleyin, ben de sizinle beraber gözetleyenlerdenim. (Bakalım hangimiz felaketlere çarpılacağız?)"'),
(4767, 52, 32, 'Akılları mı bunu kendilerine emrediyor, yoksa onlar azgın bir topluluk mudur?'),
(4768, 52, 33, 'Yoksa "Onu uydurdu" mu diyorlar? Hayır, onlar inanmıyorlar.'),
(4769, 52, 34, 'Doğru iseler haydi onun gibi bir söz getirsinler.'),
(4770, 52, 35, 'Yoksa kendileri, hiçbir şey olmadan (raslantı sonucu olarak) mı yaratıldılar? Yoksa yaratanlar kendileri midir?'),
(4771, 52, 36, 'Yoksa gökleri ve yeri mi yarattılar? Hayır, onlar düşünüp de inanmazlar.'),
(4772, 52, 37, 'Yoksa Rabbinin hazineleri onların yanında mıdır? Yahut hakim olan (her şeyi istedikleri gibi yöneten) kendileri midir?'),
(4773, 52, 38, 'Yoksa onların, (göğe çıkıp meleklerin sözlerini ve onlara vahyedileni) dinleyecekleri bir merdivenleri mi var? Öyleyse dinleyenleri, (meleklerin sözlerini dinlediklerine) açık bir delil getirsin.'),
(4774, 52, 39, 'Yoksa kızlar O\'na, oğullar size mi?'),
(4775, 52, 40, 'Yoksa sen onlardan (vahiyleri duyurmana karşı) bir ücret istiyorsun da onlar, ağır bir borç yükü altında mı kalmışlardır?'),
(4776, 52, 41, 'Yoksa gayb (görülmeyen bilgi) kendilerinin yanındadır da kendileri mi (oradan istediklerini) yazıyorlar?'),
(4777, 52, 42, 'Yoksa bir tuzak mı kurmak istiyorlar? Asıl tuzağa düşecek olanlar, o inkar edenlerin kendileridir.'),
(4778, 52, 43, 'Yoksa onların Allah\'tan başka bir tanrısı mı var? Allah\'ın şanı onların ortak koştuklarından yücedir.'),
(4779, 52, 44, 'Gökten bir parçanın düştüğünü görseler, (yine inatlarından): "Üst üste yığılmış bulutlardır" derler.'),
(4780, 52, 45, 'Korkudan bayılacakları günlerine kavuşuncaya kadar bırak onları.'),
(4781, 52, 46, 'O gün, tuzakları kendilerine hiçbir yarar sağlamaz ve onlara yardım da edilmez.'),
(4782, 52, 47, 'Zulmedenlere, bundan başka bir azab da vardır. Fakat çokları bilmezler.'),
(4783, 52, 48, 'Rabbinin hükmüne sabret, çünkü sen, gözlerimizin önündesin (korumamız altındasın), Kalktığın zaman Rabbini övgü ile an.'),
(4784, 52, 49, 'Gecenin bir kısmında ve yıldızların ardından da O\'nu tesbih et.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Aşağı kayan yıldıza andolsun ki:'),
(4786, 53, 2, 'Arkadaşınız sapmadı, azmadı.'),
(4787, 53, 3, 'O heva\'dan konuşmaz.'),
(4788, 53, 4, 'O(nun okuduğu Kur\'an) kendisine vahyedilen vahiyden başka bir şey değildir.'),
(4789, 53, 5, 'Onu, mühtiş kuvvetleri olan biri öğretti;'),
(4790, 53, 6, 'Üstün akıl sahibi (melek). Doğruldu;'),
(4791, 53, 7, 'Kendisi yüksek ufukta iken.'),
(4792, 53, 8, 'Sonra yaklaştı, (yere doğru) sarktı.'),
(4793, 53, 9, '(Muhammed ile arasındaki mesafe) İki yay uzunluğu kadar, yahut daha az kaldı.'),
(4794, 53, 10, 'Kuluna, vahyettiğini vahyetti.'),
(4795, 53, 11, 'Gönül gördüğünde yanılmadı (yalan söylemedi, gerçeği gördü).'),
(4796, 53, 12, 'Onun gördüğünden kuşku mu duyuyorsunuz?'),
(4797, 53, 13, 'Andolsun, onu bir inişinde daha görmüştü;'),
(4798, 53, 14, 'Sidretü\'l-Münteha(uzak ağaç)ın yanında,'),
(4799, 53, 15, 'Ki onun yanında oturulacak bahçe vardır.'),
(4800, 53, 16, 'Sidre\'yi kaplayan kaplıyordu.'),
(4801, 53, 17, '(Muhammed\'in) Göz(ü) şaşmadı ve azmadı.'),
(4802, 53, 18, 'Andolsun, Rabbinin büyük ayetlerinden bazılarını gördü.'),
(4803, 53, 19, 'Gördünüz mü o Lat ve \'Uzza\'yı?'),
(4804, 53, 20, 'Ve üçüncü(leri olan) öteki (put) Menat\'ı?'),
(4805, 53, 21, 'Demek erkek size, kadın Allah\'a mı?'),
(4806, 53, 22, 'O halde bu insafsızca bir taksim!'),
(4807, 53, 23, 'Onlar, sizin ve babalarınızın, (tanrı) diye isimlendirdiğiniz (boş, kavramsız) isimlerden başka bir şey değildir. Allah, onlara hiçbir güç (tanrı oldukları hakkında hiçbir delil) indirmemiştir. O(putlara tapa)nlar zanna ve nefislerin hevesine uyuyorlar. Oysa kendilerine, Rableri tarafından yol gösterici gelmiştir.'),
(4808, 53, 24, 'Yoksa insan, her arzu ettiğine sahip mi olacaktır?'),
(4809, 53, 25, 'Son da ilk de (ahiret de, dünya da) Allah\'ındır.'),
(4810, 53, 26, 'Göklerde nice melek var ki onların şefa\'ati hiçbir işe yaramaz. Meğer Allah\'ın dilediği ve razı olduğu kimseye izin verdikten sonra olsun (ancak o zaman şefa\'atin faydası olur).'),
(4811, 53, 27, 'Ahirete inanmayanlar, meleklere dişilerin adlarını takıyorlar.'),
(4812, 53, 28, 'Onların bu hususta bir bilgileri yoktur. Sadece zanna uyuyorlar. Zan ise haktan hiçbir gerçek kazandırmaz. (Zan ile gerçeğe ulaşılmaz.)'),
(4813, 53, 29, 'Bizi anmaktan yüz çeviren ve dünya hayatından başka bir şey istemeyen kimseden yüz çevir.'),
(4814, 53, 30, 'İşte onların erişebilecekleri bilgi (sınırı) budur. (Bundan ötesine akılları ermez). Şüphesiz Rabbin, yolundan sapanı da iyi bilir ve O, yola geleni de iyi bilir.'),
(4815, 53, 31, 'Göklerde ve yerde bulunan herşey Allah\'ındır. (Bunları yaratmıştır) Ki kötülük edenleri, yaptıklarıyle cezalandırsın, güzel davrananları da güzellikle mükafatlandırsın.'),
(4816, 53, 32, 'Onlar, günahın büyüklerinden ve çirkin işlerden kaçınırlar, yalnız bazı küçük hatalar işleyebilirler. Şüphesiz Rabbinin affı geniştir (O kendisine yönelen kulunu affeder). O sizi daha iyi bilir: Gerek sizi topraktan inşa ettiği, gerek annelerinizin karınlarında bulunduğunuz zaman biçim verdiği sırada (sizin her halinizi bilmiştir), artık kendinizi övüp yüceltmeyin, çünkü O, korunanı daha iyi bilir.'),
(4817, 53, 33, 'Gördün mü şu adamı ki arkasını döndü?'),
(4818, 53, 34, 'Azıcık verdi, gerisini elinde sıkı sıkı tuttu?'),
(4819, 53, 35, 'Gayb\'ın bilgisi kendi yanında da o mu (alemin esrarını) görüyor?'),
(4820, 53, 36, 'Yoksa kendisine haber mi verilmedi: Musa\'nın sahifelerinde bulunan,'),
(4821, 53, 37, 'Ve çok vefalı İbrahim\'in (sahifelerinde bulunan şu gerçekler):'),
(4822, 53, 38, 'Ki hiçbir günahkar, başkasının günah yükünü yüklenmez.'),
(4823, 53, 39, 'İnsana çalışmasından başka bir şey yoktur.'),
(4824, 53, 40, 'Ve çalışması da yakında görülecektir.'),
(4825, 53, 41, 'Sonra ona tastamam karşılığı verilecektir.'),
(4826, 53, 42, 'Ve sonunda senin Rabbine varılacaktır.'),
(4827, 53, 43, 'Güldüren de O\'dur, ağlatan da O\'dur.'),
(4828, 53, 44, 'Öldüren de O\'dur, yaşatan da O\'dur.'),
(4829, 53, 45, 'O yarattı iki çifti: erkeği ve dişiyi,'),
(4830, 53, 46, 'Atıldığı zaman nutfe(sperm)den.'),
(4831, 53, 47, 'Şüphesiz tekrar yaratmak da O\'nun işidir.'),
(4832, 53, 48, 'Zengin eden O\'dur, bol verip memnun eden O.'),
(4833, 53, 49, '(Taptıkları) Şi\'ra(yıldızı)nın Rabbi O\'dur.'),
(4834, 53, 50, 'O helak etti, önce gelen \'Ad\'ı,'),
(4835, 53, 51, 'Semud\'u, komadı (onları).'),
(4836, 53, 52, 'Önceden de Nuh kavmini (helak etmişti). Çünkü onlar daha zalim ve azgın idiler.'),
(4837, 53, 53, 'Altı üstüne getirilen kentleri (Lut kavminin oturduğu bölgeleri) devirip yıktı.'),
(4838, 53, 54, 'Onların üstüne neler çöktü, neler!'),
(4839, 53, 55, 'O halde Rabbinin hangi ni\'metinden kuşku duyuyorsun?'),
(4840, 53, 56, 'Bu (Kur\'an veya peygamber) de ilk uyarıcılar gibi bir uyarıcıdır.'),
(4841, 53, 57, 'O yaklaşıcı, yaklaştı.'),
(4842, 53, 58, 'Onu Allah\'tan başka açacak (geldiği zaman kaldıracak, vaktini erteleyecek veya onun ne zaman geleceğini belirleyecek) kimse yoktur.'),
(4843, 53, 59, 'Şimdi siz bu söze mi hayret ediyorsunuz?'),
(4844, 53, 60, 'Ve gülüyorsunuz da ağlamıyorsunuz?'),
(4845, 53, 61, 'Ve siz baş kaldırıyorsunuz?'),
(4846, 53, 62, 'Haydi Allah\'a secde edin ve kulluk edin!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'O sa\'at yaklaştı, ay yarıldı.'),
(4848, 54, 2, 'Bir mu\'cize görecek olsalar yüz çevirirler ve "Süregelen bir büyüdür" derler.'),
(4849, 54, 3, 'Yalanladılar, nefislerinin heveslerine uydular. Halbuki her iş, yerini bulacaktır (Allah\'ın kararına kimse engel olamaz).'),
(4850, 54, 4, 'Andolsun, onlara, (batılda kalmalarını) önleyecek (ibret verici olayları anlatan) haberler geldi.'),
(4851, 54, 5, 'Bunlar üstün hikmettir! Ama uyarılar fayda vermiyor.'),
(4852, 54, 6, 'Öyleyse sen de onlardan yüz çevir; o çağırıcının görülmemiş, tanınmamış bir şeye çağıracağı gün,'),
(4853, 54, 7, 'Gözleri düşkün düşkün (zillet ve dehşet içinde) kabirlerden çıkarlar; tıpkı yayılan çekirgeler gibidirler.'),
(4854, 54, 8, 'Boyunlarını, çağırana doğru uzatmış koşarlarken, kafirler: "Bu çetin bir gündür!" derler.'),
(4855, 54, 9, 'Onlardan önce Nuh\'un kavmi de yalanlamıştı. Kulumuzu yalanladılar ve: "Cinlenmiştir" dediler. Ve o(na çeşitli eziyetler yapılarak tebliğden) menedildi.'),
(4856, 54, 10, 'Bunun üzerine Rabbine: "Ben yenik düştüm, yardım et!" diye yalvardı.'),
(4857, 54, 11, 'Biz de boşalan bir su ile göğün kapılarını açtık.'),
(4858, 54, 12, 'Yeri kaynaklar halinde fışkırttık, (göğün ve yerin) su(ları) takdir edilmiş bir işin olması için birleşti.'),
(4859, 54, 13, 'Nuh\'u da tahtalar ve çiviler(le yapılmış gemi) üzerinde taşıdık.'),
(4860, 54, 14, '(Kendisine karşı) Nankörlük edilen(kulumuz)a (bizden) bir mükafat olmak üzere (gemi), gözlerimizin önünde akıp gidiyordu.'),
(4861, 54, 15, 'Bunu bir ibret olarak bıraktık, ibret alan yok mudur?'),
(4862, 54, 16, 'Benim azabım ve uyarılarım nasılmış (görsünler diye).'),
(4863, 54, 17, 'Andolsun biz, Kur\'an\'ı öğüt almak için kolaylaştırdık. Öğüt alan yok mudur?'),
(4864, 54, 18, 'Ad da yalanladı, ama azabım ve uyarılarım nasıl oldu?'),
(4865, 54, 19, 'Biz onların üstüne uğursuz mu uğursuz bir günde uğultulu bir kasırga saldık.'),
(4866, 54, 20, 'İnsanları sanki köklerinden sökülmüş hurma kütükleri imişler gibi koparıp deviriyordu.'),
(4867, 54, 21, 'Benim azabım ve uyarılarım nasıl oldu?'),
(4868, 54, 22, 'Andolsun biz Kur\'an\'ı öğüt almak için kolaylaştırdık. Öğüt alan yok mudur?'),
(4869, 54, 23, 'Semud da uyarıları yalandı:'),
(4870, 54, 24, 'Bizden bir insana mı uyacağız? O takdirde biz apaçık bir sapıklık ve çılgınlık içine düşmüş oluruz dediler.'),
(4871, 54, 25, 'Zikir, aramızdan ona mı bırakıldı? Hayır o, yalancı küstahın biridir!'),
(4872, 54, 26, '(Salih\'e dedik ki): Yarın onlar, yalancı, küstahın kim olduğunu bilecekler.'),
(4873, 54, 27, 'Biz onlara, kendilerini sınamak için dişi deveyi göndereceğiz. Hele sen onları gözetle, sabret.'),
(4874, 54, 28, 'Onlara, suyun aralarında paylaştırılacağını, (bir gün devenin, bir gün de kendilerinin su içme nöbeti olacağını) haber ver; içme sırası kiminse o gelip suyunu alsın.'),
(4875, 54, 29, 'Bir arkadaşlarını çağırdılar, o da bıçağı çekip (deveyi) kesti.'),
(4876, 54, 30, 'Ama azabım ve uyarılarım nasıl oldu?'),
(4877, 54, 31, 'Biz onların üzerine tek sayha (korkunç bir ses) gönderdik; ağılcının topladığı kuru ot gibi kırılıp döküldüler.'),
(4878, 54, 32, 'Andolsun Biz Kur\'an\'ı öğüt almak için kolaylaştırdık. Öğüt alan yok mudur?'),
(4879, 54, 33, 'Lut\'un kavmi de uyarıları yalanladı.'),
(4880, 54, 34, 'Biz de üstlerine (taşlar savuran) bir fırtına gönderdik, yalnız Lut ailesini seher vakti kurtardık;'),
(4881, 54, 35, 'Katımızdan bir ni\'met olarak. Biz şükredeni böyle mükafatlandırırız.'),
(4882, 54, 36, 'Lut, onları bizim yakalamamıza karşı uyarmıştı, fakat uyarılara karşı kuşku duydular.'),
(4883, 54, 37, 'Onun (güzel delikanlılar şeklinde görünen melek) konuklarından murad almağa kalkıştılar. Biz de gözlerini siliverdik: "Haydi azabımı ve uyarılarımı tadın!"'),
(4884, 54, 38, 'Sabah erken, onları kararlı bir azab yakaladı.'),
(4885, 54, 39, 'Azabımı ve uyarılarımı(n akıbetini) tadın!'),
(4886, 54, 40, 'Andolsun biz Kur\'an\'ı öğüt almak için kolaylaştırdık. Öğüt alan yok mudur?'),
(4887, 54, 41, 'Fir\'avn\'ın kavmine de uyarılar gelmiştir.'),
(4888, 54, 42, 'Bütün ayetlerimizi yalanladılar. Biz de onları, galib ve güçlü(padişah)ın yakalaması gibi yakaladık.'),
(4889, 54, 43, 'Şimdi sizin kafirleriniz, ötekilerinizden hayırlı mı? Yoksa Kitaplarda sizin için bir beraet (inkarınızdan dolayı size sorumsuzluk) mu var?'),
(4890, 54, 44, 'Yoksa "Biz muzaffer (yenilmez) bir topluluğuz" mu diyorlar?'),
(4891, 54, 45, 'O topluluk bozulacak ve geriye dönüp kaçacaklardır.'),
(4892, 54, 46, 'Hayır, buluşma zamanları o (uyarıldıkları) sa\'attir. O sa\'at cidden çok feci ve acıdır;'),
(4893, 54, 47, 'Suçlular bir sapıklık ve çılgınlık içindedir.'),
(4894, 54, 48, 'O gün yüzükoyun ateşe sürüklenecekler: "Cehennemin dokunuşunu tadın!" diye.'),
(4895, 54, 49, 'Biz her şeyi bir kadere (bir düzene, ölçüye, plana) göre yarattık.'),
(4896, 54, 50, 'Bizim buyruğumuz yalnız bir tektir, göz açıp yumma gibidir.'),
(4897, 54, 51, 'Andolsun biz sizin benzerlerinizi hep helak ettik. Öğüt alan yok mudur?'),
(4898, 54, 52, 'İşledikleri her şey, Kitaplarda mevcuttur.'),
(4899, 54, 53, 'Küçük, büyük hepsi satır satır yazılmıştır.'),
(4900, 54, 54, 'Korunanlar cennetlerde ırmaklar(ın kenarın)dadırlar.'),
(4901, 54, 55, 'Güçlü padişahın huzurunda doğruluk koltuklarında(memnunluk içinde)dirler.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Çok merhametli (Allah),'),
(4903, 55, 2, 'Kur\'an\'ı öğretti.'),
(4904, 55, 3, 'İnsanı yarattı.'),
(4905, 55, 4, 'Ona beyanı (konuşup, düşüncelerini açıklamayı) öğretti.'),
(4906, 55, 5, 'Güneş de, Ay da bir hesap ile(cereyan etmekte)dir.'),
(4907, 55, 6, 'Necm (bitkiler, yıldızlar) ve ağaçlar (Allah\'a) secde etmektedirler.'),
(4908, 55, 7, 'Göğü yükseltti ve mizanı koydu.'),
(4909, 55, 8, 'Tartıda taşkınlık edip dengeyi bozmayın.'),
(4910, 55, 9, 'Tartıyı adaletle yapın, terazide eksiklik yapmayın.'),
(4911, 55, 10, '(Allah) Yeri halk için koydu.'),
(4912, 55, 11, 'Onda meyva(lar) ve salkımlı hurmalar var.'),
(4913, 55, 12, 'Saplı ve yapraklı dane(ler) ve hoş kokulu bitkiler var.'),
(4914, 55, 13, '(Ey cinler ve insanlar topluluğu) Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4915, 55, 14, 'İnsanı kiremit gibi pişmiş çamurdan yarattı.'),
(4916, 55, 15, 'Cin\'i de halis ateşten yarattı.'),
(4917, 55, 16, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4918, 55, 17, 'İki doğunun ve iki batının Rabbidir.'),
(4919, 55, 18, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4920, 55, 19, 'İki denizi salıverdi, birbirine kavuşuyorlar,'),
(4921, 55, 20, 'Aralarında bir engel vardır, birbirine geçip karışmıyorlar.'),
(4922, 55, 21, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4923, 55, 22, 'İkisinden de inci ve mercan çıkar.'),
(4924, 55, 23, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4925, 55, 24, 'Denizde koca dağlar gibi akıp giden kocaman gemiler de O\'nundur.'),
(4926, 55, 25, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4927, 55, 26, '(Yer) Üzerinde bulunan her şey yok olacaktır.'),
(4928, 55, 27, 'Yalnız Rabbinin celal ve ikram sahibi yüzü baki kalacaktır.'),
(4929, 55, 28, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4930, 55, 29, 'Göklerde ve yerde bulunanlar (her şeyi) O\'ndan isterler. O, her gün (her an) yeni bir iştedir.'),
(4931, 55, 30, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4932, 55, 31, 'Ey iki sekal, sizin için de boş vaktimiz olacak (sizin de hesabınızı göreceğiz).'),
(4933, 55, 32, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4934, 55, 33, 'Ey cinler ve insanlar topluluğu, göklerin ve yerin bucaklarından geçip gitmeğe gücünüz yeterse geçin gidin. Ancak kudretle geçebilirsiniz.'),
(4935, 55, 34, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4936, 55, 35, 'İkinizin de üzerine, ateşten yalın alev ve kıpkızıl bir duman (yahut erimiş bakır) gönderilir, başaramazsınız.'),
(4937, 55, 36, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4938, 55, 37, 'Gök yarılıp da erimiş yağ gibi kıpkırmızı bir gül olduğu zaman...'),
(4939, 55, 38, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4940, 55, 39, 'O gün ne insana, ne de cin\'e günahından sorulur.'),
(4941, 55, 40, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4942, 55, 41, 'Suçlular, simalarından tanınır, alınlar(ın)dan ve ayaklar(ın)dan tutulur.'),
(4943, 55, 42, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4944, 55, 43, 'İşte bu, suçluların yalanladığı cehennemdir!'),
(4945, 55, 44, 'Onunla kaynar su arasında dolaşırlar.'),
(4946, 55, 45, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4947, 55, 46, 'Rabbinin divanında dur(up hesap ver)mekten korkan kimseye iki cennet var.'),
(4948, 55, 47, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4949, 55, 48, 'İkisinin de çeşitli ağaçları, meyvaları var.'),
(4950, 55, 49, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4951, 55, 50, 'İkisinde de akıp giden iki kaynak var.'),
(4952, 55, 51, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4953, 55, 52, 'İkisinde de her meyvadan iki çift var.'),
(4954, 55, 53, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4955, 55, 54, '(Orada) Astarları kalın atlastan yataklara yaslanırlar. İki cennetin de devşirmesi yakındır.'),
(4956, 55, 55, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4957, 55, 56, 'Onlarda bakışları kısa (yalnız kocalarına bakan) öyle dilberler de var ki, bunlardan önce onları ne insan, ne de cin kanatmamıştır.'),
(4958, 55, 57, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4959, 55, 58, 'Sanki onlar yakut ve mercandırlar.'),
(4960, 55, 59, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4961, 55, 60, 'İyiliğin karşılığı, yalnız iyilik değil midir?'),
(4962, 55, 61, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4963, 55, 62, 'Bu ikisinin ötesinde iki cennet daha var.'),
(4964, 55, 63, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4965, 55, 64, 'Yemyeşildirler.'),
(4966, 55, 65, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4967, 55, 66, 'İkisinde de fışkıran iki kaynak var.'),
(4968, 55, 67, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4969, 55, 68, 'İkisinde de meyva, hurma ve nar var.'),
(4970, 55, 69, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4971, 55, 70, 'Onlarda da iyi huylu, güzel kadınlar var.'),
(4972, 55, 71, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4973, 55, 72, 'Çadırlara kapanmış huriler.'),
(4974, 55, 73, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4975, 55, 74, 'Bunlardan önce onları ne insan, ne de cin kanatmamıştır.'),
(4976, 55, 75, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4977, 55, 76, '(Cennettekiler) Yeşil yastıklara ve harikulade güzel işlemeli döşeklere yaslanırlar.'),
(4978, 55, 77, 'Şimdi Rabbinizin hangi ni\'metlerini yalanlıyorsunuz?'),
(4979, 55, 78, 'Büyüklük ve ikram sahibi Rabbinin adı ne yücedir!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Olacak vak\'a olduğu (kıyamet koptuğu) zaman,'),
(4981, 56, 2, 'Onun oluşunu yalanlayacak yoktur.'),
(4982, 56, 3, 'O alçaltıcı, yükselticidir (yerleri alt üst eder),'),
(4983, 56, 4, 'Yer şiddetliesarsıldığı,'),
(4984, 56, 5, 'Dağlar serpildikçe serpildiği,'),
(4985, 56, 6, 'Dağılan toz duman haline geldiği'),
(4986, 56, 7, 'Ve sizler üç sınıf olduğunuz zaman;'),
(4987, 56, 8, 'Sağın adamları (amel defterleri sağ tarafından verilenler), ne uğurlulardır onlar!'),
(4988, 56, 9, 'Solun adamları (amel defterleri sol tarafından verilenler), ne uğursuzlardır onlar!'),
(4989, 56, 10, 'Ve o sabıklar, sabıklar!'),
(4990, 56, 11, 'İşte, onlardır (Allah\'a) yaklaştırılanlar,'),
(4991, 56, 12, 'Ni\'met cennetlerinde.'),
(4992, 56, 13, 'Çoğu öncekilerden,'),
(4993, 56, 14, 'Birazı da sonrakilerden (olan bu insanlar),'),
(4994, 56, 15, 'Altın ve cevahirle işlenmiş tahtlar üzerindedirler.'),
(4995, 56, 16, 'Onların üzerinde karşılıklı yaslanırlar.'),
(4996, 56, 17, 'Çevrelerinde, ebedi yaşamağa erdirilmiş gençler dolaşır;'),
(4997, 56, 18, 'Akıp giden şarap kaynağından doldurulmuş testiler, ibrikler ve kadehlerle.'),
(4998, 56, 19, '(Bir şarap ki) Ondan ne başları ağrıtılır, ne de akılları giderilir.'),
(4999, 56, 20, 'Beğendikleri meyva(lar),'),
(5000, 56, 21, 'Canlarının çektiği kuş et(ler)i,'),
(5001, 56, 22, 'İri gözlü huriler,'),
(5002, 56, 23, 'Saklı inciler gibi;'),
(5003, 56, 24, 'Yaptıklarına karşılık olarak.'),
(5004, 56, 25, 'Orada ne boş bir söz ve ne de günaha sokan bir laf işitirler.'),
(5005, 56, 26, 'Duydukları söz, yalnız "Selam, selam" dır.'),
(5006, 56, 27, 'Sağın adamları, nedir o sağın adamları!'),
(5007, 56, 28, '(Onlar) Dikensiz kirazlar,'),
(5008, 56, 29, '(Kökünden tepesine kadar) meyva dizili muzlar,'),
(5009, 56, 30, 'Uzamış gölge(ler),'),
(5010, 56, 31, 'Fışkıran sular,'),
(5011, 56, 32, 'Pek çok mevya arasında;'),
(5012, 56, 33, 'Tükenmeyen ve yasaklanmayan!'),
(5013, 56, 34, 'Ve yükseltilmiş döşekler üstündedirler.'),
(5014, 56, 35, 'Biz (oradaki) kadınları da yeniden bir güzel inşa\' etmişiz,'),
(5015, 56, 36, 'Onları bakireler yapmışızdır.'),
(5016, 56, 37, 'Hep yaşıt sevgililer;'),
(5017, 56, 38, 'Sağın adamları için.'),
(5018, 56, 39, '(Bu sağcıların) Bir bölümü öncekilerdendir,'),
(5019, 56, 40, 'Bir bölümü de sonrakilerdendir.'),
(5020, 56, 41, 'Solun adamları (amel defterleri, sol tarafından verilenler), nedir o solcular!'),
(5021, 56, 42, '(Onlar) Delikçiklere işleyen bir ateş ve kaynar su içinde,'),
(5022, 56, 43, 'Kara dumandan bir gölge altında,'),
(5023, 56, 44, 'Ki ne serindir, ne faydalı.'),
(5024, 56, 45, 'Çünkü onlar bundan önce varlık içinde şımartılmışlardı.'),
(5025, 56, 46, 'Büyük günahı işlemekte ısrar ediyorlardı.'),
(5026, 56, 47, 'Ve diyorlardı ki: "Biz öldükten, toprak ve kemik yığını olduktan sonra, biz mi bir daha diriltileceğiz?"'),
(5027, 56, 48, 'Önceki atalarımız da mı?'),
(5028, 56, 49, 'De ki: "Öncekiler de sonrakiler de."'),
(5029, 56, 50, 'Belli bir günün buluşma vakti için mutlaka toplanacaklardır.'),
(5030, 56, 51, 'Sonra siz de, ey sapık yalanlayıcılar (o zaman toplanacaksınız).'),
(5031, 56, 52, '(Suçlular) Mutlaka bir Zakkum ağacından yiyecekler,'),
(5032, 56, 53, 'Onunla karınları(nı) dolduracaklar,'),
(5033, 56, 54, 'Üzerine de kaynar su içeceklerdir.'),
(5034, 56, 55, 'Susuzluk hastalığına tutulmuş develerin içişi gibi içeceklerdir!'),
(5035, 56, 56, 'İşte ceza gününde onların ağırlanışı böyledir.'),
(5036, 56, 57, 'Biz sizi yarattık; doğrulamanız gerekmez mi?'),
(5037, 56, 58, 'Akıttığınız meniyi gördünüz mü?'),
(5038, 56, 59, 'Siz mi onu yaratıyorsunuz, yoksa yaratıcılar biz miyiz?'),
(5039, 56, 60, 'Aranızda ölümü takdir eden biziz ve bizim önümüze geçilmiş değildir (kimse ölüme engel olamaz).'),
(5040, 56, 61, '(Size böyle ölümü takdir ettik) Ki sizin yerinize benzerlerinizi getirelim ve sizi, bilmediğiniz bir biçimde yeniden inşa\' edelim.'),
(5041, 56, 62, 'Andolsun, ilk yaratmayı bildiniz, (bunu) düşünüp ibret almanız gerekmez mi?'),
(5042, 56, 63, 'Ektiğinizi gördünüz mü?'),
(5043, 56, 64, 'Siz mi onu bitiyorsunuz, yoksa bitirenler biz miyiz?'),
(5044, 56, 65, 'Dileseydik, onu kuru bir çöp yapardık, sızlanıp dururdunuz:'),
(5045, 56, 66, 'Biz borçlandık, (yaptığmız masraflar boşa gitti)!'),
(5046, 56, 67, 'Doğrusu, biz yoksun bırakıldık! (derdiniz).'),
(5047, 56, 68, 'İçtiğiniz suya baktınız mı?'),
(5048, 56, 69, 'Siz mi onu buluttan indirdiniz, yoksa indirenler biz miyiz?'),
(5049, 56, 70, 'Dileseydik onu tuzlu yapardık. Şüketmeniz gerekmez mi?'),
(5050, 56, 71, '(İki dalı birbirine sürterek) Çıkardığınız ateşi gördünüz mü?'),
(5051, 56, 72, 'Onun ağacını siz mi yarattınız, yoksa yaratanlar biz miyiz?'),
(5052, 56, 73, 'Biz onu bir ibret ve çölden gelip geçenlere bir fayda yaptık.'),
(5053, 56, 74, 'Öyleyse büyük Rabbinin adını yücelt.'),
(5054, 56, 75, 'Yoo, yıldızların yerlerine yemin ederim,'),
(5055, 56, 76, 'Bilirseniz, bu büyük bir yemindir.'),
(5056, 56, 77, 'O, elbette değerli bir Kur\'an\'dır,'),
(5057, 56, 78, 'Saklı bir Kitaptadır.'),
(5058, 56, 79, 'Ki ona temizlerden başkası dokunmaz.'),
(5059, 56, 80, '(O), Alemlerin Rabbinden indirilmiştir.'),
(5060, 56, 81, 'Şimdi siz, bu sözü mü küçümsüyorsunuz?'),
(5061, 56, 82, '(Kur\'an\'dan istifade edeceğiniz yerde) Rızkınızı, yalanlamanızdan ibaret mi kılıyorsunuz (sizin ondan elde ettiğiniz nasib, sadece onu yalanlamanız mıdır)?'),
(5062, 56, 83, 'Ya can boğaza dayandığı zaman?'),
(5063, 56, 84, 'Ki siz de o zaman (can çekişen kimseye) bakıp durursunuz.'),
(5064, 56, 85, 'Biz ona sizden daha yakınız, fakat siz görmezsiniz.'),
(5065, 56, 86, 'Eğer (öldükten sonra) cezalandırılmayacaksanız'),
(5066, 56, 87, '(Bu sözünüzde doğru iseniz) o(çıkmakta olan ca)nı geri döndürsenize!'),
(5067, 56, 88, '(O can, Allah\'a) Yaklaştırılanlardan ise,'),
(5068, 56, 89, 'O\'na rahatlık, güzel rızık ve ni\'met cenneti var.'),
(5069, 56, 90, 'Eğer sağcılardan (amel defteri sağ tarafından verilenlerden) ise,'),
(5070, 56, 91, '(Ey sağcı) Sana sağcılardan selam var!'),
(5071, 56, 92, 'Ama yalanlayıcı sapıklardan ise;'),
(5072, 56, 93, 'Kaynar sudan bir ziyafet,'),
(5073, 56, 94, 'Ve cehenneme atılma var.'),
(5074, 56, 95, 'Kesin gerçek budur işte.'),
(5075, 56, 96, 'Öyleyse büyük Rabbinin adını tesbih et (O\'nu, kendisine layık olmayan sıfatlardan tenzih eyle).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Göklerde ve yerde bulunan her şey Allah\'ı tesbih etmiştir. O, azizdir hakimdir.'),
(5077, 57, 2, 'Göklerin ve yerin mülkü O\'nundur. Yaşatır, öldürür, O her şeyi yapabilir.'),
(5078, 57, 3, 'O, ilktir (kendisinden önce hiçbir varlık yoktur,) sondur (kendisinden sonra hiçbir varlık yoktur. Her şey yok olurken O kalacaktır,) zahirdir (delilleriyle varlığı gün gibi açıktır,) batındır (zatının hakikati gizlidir, akıllar O\'nun özünü idrak edemez,) O, her şeyi bilendir.'),
(5079, 57, 4, 'O\'dur ki gökleri ve yeri altı günde yarattı, sonra Arş\'a oturdu. Yere gireni, ondan çıkanı, gökten ineni, ona çıkanı bilir. Nerede olsanız, O sizinle beraberdir, Allah yaptıklarınızı görmektedir.'),
(5080, 57, 5, 'Göklerin ve yerin mülkü O\'nundur. Bütün işler Allah\'a döndürülecektir.'),
(5081, 57, 6, 'Geceyi gündüzün içine sokar, gündüzü gecenin içine sokar. O, göğüslerin özünü bilir.'),
(5082, 57, 7, 'Allah\'a ve Elçisine inanın ve (O\'nun) sizi hakim kıldığı, sizin yönetiminize verdiği şeylerden (Allah için) harcayın. Sizden, inanan ve (hak rızasına) harcayanlar için büyük mükafat vardır.'),
(5083, 57, 8, 'Elçi sizi Rabbinize inanmağa (güvenmeğe) çağırdığı ve (bu konuda) sizden sağlam söz almış olduğu halde inananlar iseniz neden Allah\'a güvenmiyorsunuz?'),
(5084, 57, 9, 'Sizi karanlıklardan aydınlığa çıkarmak için kuluna açık açık ayetler indiren O\'dur. Şüphesiz Allah, size karşı çok şefkatli, çok merhametlidir.'),
(5085, 57, 10, 'Neden siz Allah yolunda harcamayasınız ki? Göklerin ve yerin mirası zaten Allah\'ındır. Elbette içinizden (Mekke\'nin) feth(in)den önce (Hak yolunda) harcayan ve savaşan(lar, ötekilerle) bir olmaz. Onların derecesi, sonradan infak eden ve savaşanlardan daha büyüktür. Bununla beraber Allah hepsine de (gerek fetihten önce, gerek fetihten sonra infak eden ve savaşan müslümanlara) en güzel sonucu va\'detmiştir. Allah, yaptıklarınızı haber almaktadır.'),
(5086, 57, 11, 'Kimdir o, Allah\'a güzel bir borç verecek olan ki, Allah da onun verdiğini kat kat artırsın ve onun için değerli bir mükafat da versin?'),
(5087, 57, 12, 'O gün inanan erkekleri ve inanan kadınları; ışıkları, önlerinde ve sağlarında koşar durumda görürsün. (Kendilerine): "Bugün müjdeniz, altlarından ırmaklar akan, içinde ebedi kalacağınız cennetlerdir." (denilir). İşte büyük başarı budur!'),
(5088, 57, 13, 'O gün münafık erkekler ve münafık kadınlar (cennete gitmekte olan) mü\'minlere derler ki: "Bize bakın da sizin nurunuzdan yararlanalım." Onlara: "Arkanıza dönün de nur arayın!" denilir. Aralarına kapılı bir sur çekilir ki, onun içinde rahmet vardır, dış yönünde de azab.'),
(5089, 57, 14, '(Münafıklar) onlara seslenirler: "Biz de sizinle beraber değil miydik?" (Mü\'minler) derler ki: "Evet ama, siz kendi canlarınıza kötülük ettiniz, beklediniz (hemen tevbe etmediniz) kuşkulandınız, kuruntular sizi aldattı. Allah\'ın emri (ölüm) gelinceye kadar (böyle hareket ettiniz,) o çok aldatıcı (şeytan,) sizi Allah(ın affı) ile aldattı."'),
(5090, 57, 15, 'Bugün artık ne sizden, ne de inkar edenlerden fidye alınmaz, varacağınız yer ateştir. Sizin layığınız odur. Ne kötü gidilecek yerdir orası!'),
(5091, 57, 16, 'İnananlar için hala vakit gelmedi mi ki kalbleri Allah\'ın Zikrine ve inen hakka saygı duysun ve bundan önce kendilerine Kitap verilmiş, sonra üzerlerinden uzun zaman geçmekle kalbleri katılaşmış, çoğu da yoldan çıkmış kimseler gibi olmasınlar?'),
(5092, 57, 17, 'Biliniz ki Allah yeri, ölümünden sonra diriltir. Belki aklınızı kullanırsınız diye size ayetleri açıkladık.'),
(5093, 57, 18, 'Sadaka veren erkekler ve sadaka veren kadınlar ve Allah\'a güzel borç verenler, işte onlara, (verdikleri), kat kat yapılır ve onlar için değerli bir mükafat da vardır.'),
(5094, 57, 19, 'Allah\'a ve elçilerine inananlar (yok mu) işte Rableri yanında, sıddikler (çok doğru olanlar) ve şehidler onlardır. Onların mükafatları ve nurları vardır. İnkar edenler ve ayetlerimizi yalanlayanlar(a gelince), onlar da cehennem halkıdır.'),
(5095, 57, 20, 'Bilin ki dünya hayatı bir oyun, eğlence, süs, kendi aranızda övünme mal ve evlad çoğaltma yarışıdır. Tıpkı bir yağmura benzer ki; bitirdiği ot, ekincilerin hoşuna gider, sonra kurur, onu sapsarı görürsün, sonra çerçöp olur. Ahirette ise çetin bir azab; Allah\'tan mağfiret ve rıza vardır. Dünya hayatı aldatıcı bir zevkten başka bir şey değildir.'),
(5096, 57, 21, '(O halde siz), Rabbinizden bir mağfirete ve genişliği, gökle yerin genişliği gibi olup Allah\'a ve elçilerine inananlar için hazırlanmış bulunan bir cennete koşun. İşte bu, Allah\'ın dilediğine vereceği lutfudur. Allah, büyük lutuf sahibidir.'),
(5097, 57, 22, 'Ne yerde ne de kendi canlarınızda meydana gelen hiçbir musibet (afet, hastalık) yoktur ki biz onu yaratmadan önce, bir Kitapta (yazılmış ezeli bilgimizde tesbit edilmiş) olmasın. Doğrusu bu, Allah\'a kolaydır.'),
(5098, 57, 23, '(Başınıza gelecek olayları, önceden bir Kitaba yazdık) Ki elinizden çıkana üzülmeyesiniz ve (Allah\'ın) size verdiğiyle sevinip şımarmayasınız. Çünkü Allah, kendini beğenip övünen kimseleri sevmez.'),
(5099, 57, 24, 'Onlar cimrilik edip insanlara da cimriliği emrederler. Kim (Allah yolunda harcamaktan) yüz çevirirse (bilsin ki) Allah, zengindir, övgüye layıktır.'),
(5100, 57, 25, 'Andolsun biz elçilerimizi açık kanıtlarla gönderdik ve onlarla beraber Kitabı ve (adalet) ölçü(sün)ü indirdik ki insanlar adaleti yerine getirsinler. Ve kendisinde büyük bir kuvvet ve insanlara birçok yararlar bulunan demiri indirdik ki Allah, kimin (ondan yararlanarak) gaybda (görmediği halde) kendisine ve elçilerine yardım edeceğini bilsin, (ortaya çıkarsın). Şüphesiz Allah kuvvetlidir, daima üstündür.'),
(5101, 57, 26, 'Andolsun, Nuh\'u ve İbrahim\'i elçi gönderdik, peygamberliği ve Kitabı bunların zürriyetleri arasına koyduk. Onlardan doğru yolda olanlar da vardır, ama onlardan çoğu yoldan çıkmıştır.'),
(5102, 57, 27, 'Sonra bunların peşinden ardarda elçilerimizi gönderdik. Meryem oğlu Îsa\'yı da onların ardına kattık; ona İncil\'i verdik ve ona uyanların kalblerine şefkat ve merhamet koyduk. İcadettikleri ruhbanlığı, biz onlara yazmamıştık, yalnız Allah\'ın rızasını kazanmak için kendiliklerinden uyguladılar ama ona gereği gibi uymadılar. Biz de onlardan iman edenlere mükafatlarını verdik. Fakat onlardan birçoğu da yoldan çıkmıştır.'),
(5103, 57, 28, 'Ey inananlar, Allah\'tan korkun, O\'nun Elçisine inanın ki size rahmetinden iki pay versin, sizin için ışığında yürüyeceğiniz bir nur yaratsın ve sizi bağışlasın. Allah çok bağışlayan, çok esirgeyendir.'),
(5104, 57, 29, 'Böylece Kitap ehli, kendilerinin, Allah\'ın lutfundan hiçbir şeye malik olmadıklarını, bütün lutfun, Allah\'ın elinde olduğunu, onu dilediğine vereceğini bilmezlik etmesinler. Allah, büyük lutuf sahibidir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Allah, kocası hakkında seninle tartışan ve Allah\'a şikayette bulunan kadının sözünü işitti. Allah, ikinizin birbirinizle konuşmanızı işitir. Çünkü Allah işitendir, görendir.'),
(5106, 58, 2, 'Sizden kadınlara zıhar edenler (sen bana, anamın sırtı gibisin diyenler), bilmelidirler ki o kadınlar, onların anaları değillerdir. Onların anaları, ancak kendilerini doğuran kadınlardır. Onlar, çirkin ve yalan olan bir söz söylüyorlar. Bununla beraber Allah, affedicidir bağışlayıcıdır.'),
(5107, 58, 3, 'Kadınlarına zıhar edip sonra söylediklerine dönenler, karılarıyle temaslarından önce bir köleyi hürriyete kavuşturmalıdırlar. Size öğütlenen budur. Allah, yaptıklarınızı haber almaktadır.'),
(5108, 58, 4, 'Buna imkan bulamayan, temaslarından önce aralıksız olarak iki ay oruç tutmalıdır. Buna da gücü yetmeyen, altmış fakiri doyurmalıdır. Allah\'a ve Elçisine inanmanız (onların sözlerini doğrulamanız) için bu hükümler konmuştur. Bunlar, Allah\'ın sınırlarıdır (bu sınırları tanımayan) kafirler için acı bir azab vardır.'),
(5109, 58, 5, 'Allah\'a ve Elçisine karşı gelen (onların koyduğu sınırlardan başka sınırlar koymağa kalkan)lar kendilerinden öncekilerin tepelendikleri gibi tepeleneceklerdir! Biz açık açık ayetler indirdik. kafirler için küçük düşürücü bir azab vardır.'),
(5110, 58, 6, 'Allah onların hepsini tekrar dirilteceği gün ne yaptıklarını kendilerine haber verecektir! Allah on(ların yaptıkları işler)i hep saymış (zaptetmiş)tir. Onlar ise onu unutmuşlardır. Allah her şeye şahiddir.'),
(5111, 58, 7, 'Göklerde ve yerde olanları, Allah\'ın bildiğini görmedin mi? Üç kişi gizli konuşsa mutlaka dördüncüleri O\'dur. Beş kişi gizli konuşsa mutlaka altıncıları O\'dur. Bundan az da, bundan çok da olsalar, nerede bulunsalar mutlaka O, onlarla beraberdir. Sonra kıyamet günü, onlara yaptıklarını haber verir. Çünkü Allah, her şeyi bilendir.'),
(5112, 58, 8, 'Görmedin mi şu adamları ki gizli gizli konuşmaktan menedildikleri halde yine o menedildikleri işe dönüyorlar; günah, düşmanlık, Elçiye isyan hususunda gizli gizli konuşuyorlar. Sana geldikleri zaman seni, Allah\'ın selamlamadığı bir tarzda selamlıyorlar ve kendi içlerinde de: "Bu dediğimizden ötürü Allah bize azab etse ya" diyorlar. Cehennem onlara yeter. Oraya gireceklerdir, ne kötü gidilecek yerdir orası!'),
(5113, 58, 9, 'Ey inananlar, aranızda gizli konuştuğunuz zaman günah, düşmanlık ve Elçiye karşı gelme üzerinde konuşmayın; iyilik ve takva üzerinde konuşun ve huzuruna toplanacağınız Allah\'tan korkun.'),
(5114, 58, 10, 'Gizli konuşma( fiskos) şeytandandır. (Şeytan insanları bu yola iletir ki) inananlar üzülsünler. Oysa o, Allah\'ın izni olmadıkça mü\'minlere hiçbir zarar veremez. Mü\'minler Allah\'a dayansınlar.'),
(5115, 58, 11, 'Ey inananlar, size: "Meclislerde yer açın" dendiği zaman yer açın ki Allah da size genişlik versin. Size: "Kalkın" dendiği zaman da, kalkın ki Allah sizden inananları ve kendilerine ilim verilenleri derecelerle yükseltsin. Allah yaptıklarınızı haberalmaktadır.'),
(5116, 58, 12, 'Ey inananlar, siz Elçi ile gizli konuşacağınız zaman bu gizli konuşmanızdan önce bir sadaka verin. Bu sizin için daha hayırlı ve daha temizdir. Şayet (sadaka verecek bir şey) bulamazsınız, Allah bağışlayandır, esirgeyendir.'),
(5117, 58, 13, 'Gizli konuşmanızdan önce sadaka vermenizden korktunuz mu? Çünkü yapmadınız. Allah da sizi (bundan) affetti. Artık namazı kılın, zekatı verin, Allah\'a ve Elçisine ita\'at edin. Allah yaptıklarınızı bilmektedir.'),
(5118, 58, 14, 'Allah\'ın kendilerine gazabettiği bir topluluğu dost edinenleri görmedin mi? Onlar ne sizdendirler, ne de onlardan. Bilerek yalan yere yemin ediyorlar.'),
(5119, 58, 15, 'Allah onlar için çetin bir azab hazırlamıştır. Onlar ne kötü işler yapıyorlar.'),
(5120, 58, 16, 'Yeminlerini kalkan yapıp Allah\'ın yoluna engel oldular. Onlar için küçük düşürücü bir azab vardır.'),
(5121, 58, 17, 'Onların ne malları, ne de çocukları kendilerini Allah\'a karşı koruyabilir. Onlar ateş halkıdır. Orada sürekli kalacaklardır.'),
(5122, 58, 18, 'Allah onların hepsini tekrar dirilttiği gün, dünyada size yemin ettikleri gibi O\'na da yemin edecekler ve kendilerinin bir şey üzerinde bulunduklarını, (doğru yolda olduklarını) sanacaklardır. İyi bilin ki onlar yalancılardır.'),
(5123, 58, 19, 'Şeytan onları kuşatmış (ruhlarına hakim olmuş) onlara Allah\'ı anmayı unutturmuştur. Onlar şeytanın hizbi(partisi)dir. Muhakkak ki şeytanın hizbi kaybedecektir.'),
(5124, 58, 20, 'Allah\'a ve Elçisine düşman olanlar, onlar en alçaklar arasındadırlar.'),
(5125, 58, 21, 'Allah: "Elbette ben ve elçilerim galib geleceğiz" diye yazmıştır. Şüphesiz Allah güçlüdür, galiptir.'),
(5126, 58, 22, 'Allah\'a ve ahiret gününe inanan bir milletin babaları, oğulları, kardeşleri, yahut akrabaları da olsa Allah\'a ve Elçisine düşman olanlarla dostluk ettiğini görmezsin. Allah onların kalblerine iman yazmış ve onları kendinden bir ruh ile (kalb nuru veya Kur\'an ile) desteklemiştir. Onları altlarından ırmaklar akan cennetlere sokacak, orada ebedi kalacaklardır. Allah onlardan razı olmuş, onlar da O\'ndan razı olmuşlardır. İşte onlar Allah\'ın hizbi(partisi)dir. Muhakkak ki başarıya ulaşacak olanlar, Allah\'ı hizbidir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Göklerde ve yerde bulunan herşey Allah\'ı tesbih etmiş(O\'nun şanının eksikliklerden uzak, zatının yüce olduğunu anmışlar)dır. O, üstündür, hüküm ve hikmet sahibidir.'),
(5128, 59, 2, 'Kitap sahiplerinden inkar edenleri, hemen ilk haşirde (müslümanların, kaleleri önünde toplanmalarında) yurtlarından O çıkardı. Siz onların çıkacaklarını sanmamıştınız. Onlar da kalelerinin, kendilerini Allah\'tan koruyacağını sanmışlardı. Allah onlara ummadıkları yerden geldi, yüreklerine korku saldı; öyle ki evlerini kendi elleriyle ve mü\'minlerin elleriyle harap ediyorlardı. Ey akıl sahipleri ibret alın.'),
(5129, 59, 3, 'Eğer Allah onlara sürgünü yazmamış olsaydı, mutlaka onlara dünyada azabederdi. Ahirette de onlar için ateş azabı vardır.'),
(5130, 59, 4, 'Bunun sebebi şudur: Onlar Allah\'a ve Elçisine karşı geldiler; kim Allah\'a karşı gelirse (bilsin ki) Allah\'ın azabı çetindir.'),
(5131, 59, 5, 'Herhangibir hurma ağacını kesmeniz, yahut onu kökleri üzerinde bırakmanız hep Allah\'ın izniyle ve (O\'nun) yoldan çıkanları cezalandırması için olmuştur.'),
(5132, 59, 6, 'Allah\'ın, onlardan Elçisine verdiği ganimetlere gelince, siz (onu elde etmek için) onun üzerine ne at ne de deve sürdünüz. Fakat Allah, elçilerini, dilediği kimselerin üzerine salar (onlara üstün getirir). Allah her şeyi yapabilir.'),
(5133, 59, 7, '(Onların durumu) Tıpkı şeytanın durumuna benzer ki insana "İnkar et" dedi. (İnsan) inkar edince de: "Ben seden uzağım, ben alemlerin Rabbi Allah\'tan korkarım!" dedi.'),
(5134, 59, 8, 'Allah\'ın, o kent halkından, Elçisine verdiği ganimetler, Allah\'a, Elçiye, (ona) akraba olanlara, yetimlere, yoksullara (yolda kalan) yolcuya aittir. Ta ki (o mallar), içinizden yalnız zenginler arasında dolaşan bir şey olmasın. Elçi size ne verdiyse onu alın, size neyi yasakladıysa ondan sakının ve Allah\'tan korkun. Çünkü Allah\'ın azabı şiddetlidir.'),
(5135, 59, 9, '(O mallar) Şu göçmen fakirlere aittir ki (onlar) yurtlarından ve mallarından (sürülüp) çıkarılmışlardır; Allah\'ın lutuf ve rızasını ararlar; Allah\'a ve Elçisine yardım ederler. İşte doğru olanlar onlardır.'),
(5136, 59, 10, 'Ve onlardan önce o yurda (Medine\'ye) yerleşen, imana sarılanlar kendilerine göç edip gelenleri severler ve onlara verilen(ganimet)lerden ötürü göğüslerinde bir ihtiyaç (eğilimi) duymazlar. Kendilerinin ihtiyaçları olsa dahi, (göç eden yoksul kardeşlerini) öz canlarına tercih ederler. Kim nefsinin cimriliğinden korunursa, işte onlar başarıya erenlerdir.'),
(5137, 59, 11, 'Onlardan sonra gelenler derler ki: "Rabbimiz, bizi ve bizden önce inanmış olan kardeşlerimizi bağışla, kalblerimizde inananlara karşı bir kin bırakma! Rabbimiz, Sen çok şefkatli çok merhametlisin!"'),
(5138, 59, 12, 'İki yüzlülük edenleri görmedin mi? Kitap ehlinden inkar eden kardeşlerine: "Eğer siz (yurdunuzdan) çıkarılırsanız, mutlaka biz de sizinle beraber çıkarız, sizin aleyhinize hiç kimseye ita\'at etmeyiz. Şayet sizinle savaşılırsa mutlaka size yardım ederiz." derler. Allah, onların yalancı olduklarına şahidlik eder.'),
(5139, 59, 13, 'Andolsun eğer onlar, çıkarılsalar, (bunlar) onlarla beraber çıkmazlar; eğer onlarla savaşılsa onlara yardım etmezler; yardım etseler bile arkalar(ın)a dönüp kaçarlar, sonra kendilerine de yardım edilmez.'),
(5140, 59, 14, 'Onların kalblerinde sizin korkunuz, Allah\'ınkinden fazladır. (Allah\'tan çok sizden korkarlar). Böyledir, çünkü onlar anlamaz bir topluluktur.'),
(5141, 59, 15, 'Onlar toplu olarak sizinle savaşamazlar, ancak müstahkem kaleler içinde, yahut duvarların ardından (savaşırlar). Kendi aralarında şiddetli ayrılık vardır. Sen onları toplu sanırsın, ama kalbleri dağınıktır. Öyledir, çünkü onlar düşünmez bir topluluktur.'),
(5142, 59, 16, '(Onların durumu), kendilerinden az önce, yaptıklarının vebalini tadmış olan, ahirette de kendileri için acı bir azab bulunan kimselerin durumu gibidir.'),
(5143, 59, 17, 'Nihayet ikisinin de sonu, ebedi olarak ateşte kalmaları oldu. Zalimlerin cezası budur.'),
(5144, 59, 18, 'Ey inananlar, Allah\'tan korkun ve kişi yarın için ne (yapıp) gönderdiğine baksın. Allah\'tan korkun; çünkü Allah, yaptıklarınızı bilmektedir.'),
(5145, 59, 19, 'Şu, Allah\'ı unuttuklarından dolayı (Allah\'ın da) onlara kendi canlarını unutturduğu kimseler gibi olmayın. Onlar, yoldan çıkan insanlardır.'),
(5146, 59, 20, 'Ateş halkıyle cennet halkı bir olmaz. Kurtulanlar, ancak cennet halkıdır.'),
(5147, 59, 21, 'Biz bu Kur\'an\'ı bir dağa indirseydik, Allah korkusundan onu, baş eğmiş, çatlamış, yarılmış görürdün. Bu misalleri, düşünmeleri için insanlara anlatıyoruz.'),
(5148, 59, 22, 'O, öyle Allah\'tır ki O\'ndan başka tanrı yoktur. Görülmeyeni ve görüleni bilir. O çok esirgeyen, çok acıyandır.'),
(5149, 59, 23, 'O, öyle Allah\'tır ki O\'ndan başka tanrı yoktur. Padişahtır, mukaddestir, selam (esenlik veren) mü\'min (güvenlik veren), müheymin (kollayıp koruyan), aziz (üstün, galib), cebbar (istediğini zorla yaptıran), mütekebbir(çok ulu)dur! Allah (puta tapanların) ortak koşmalarından yücedir.'),
(5150, 59, 24, 'O, yaratan, var eden, (varlığa getirdiklerine) biçim veren Allah\'tır. En güzel isimler O\'nundur. Göklerde ve yerde bulunanların hepsi O\'nun ululuğunu anarlar. O, aziz (mutlak galip), hakim(hükümdar, herşeyi hikmetle yapan)dır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Ey inananlar! Benim de düşmanım, sizin de düşmanınız olan kimseleri dost edinmeyin. Onlar size gelen gerçeği inkar ettikleri, Rabbiniz Allah\'a inandığınızdan dolayı Elçiyi ve sizi (yurdunuzdan) çıkardıkları halde siz onlara sevgi iletiyorsunuz. Benim yolumda cihadetmek ve benim rızamı kazanmak için (yurdunuzdan) çıktığınız halde içinizde onlara sevgi (mi) gizilyorsunuz? Oysa ben sizin gizlediğiniz ve açığa vurduğunuz her şeyi bilirim. Sizden kim bunu yaparsa doğru yoldan sapmış olur.'),
(5152, 60, 2, 'Onlar sizi ele geçirseler, size düşman olurlar, size ellerini, dillerini kötülükle uzatırlar ve inkar etmenizi isterler.'),
(5153, 60, 3, 'Kıyamet günü akrabanız ve çocuklarınız size fayda vermez. (Allah) Aranızı ayırır. Allah yaptıklarınızı görmektedir.'),
(5154, 60, 4, 'İbrahim\'de ve onunla beraber bulunanlarda sizin için güzel bir örnek vardır; onlar kavimlerine "Biz sizden ve sizin Allah\'tan başka taptıklarınızdan uzağız. Sizi(n taptıklarınızı) tanımıyoruz. Siz, bir tek Allah\'a inanıncaya kadar sizinle bizim aramızda sürekli bir düşmanlık ve nefret belirmiştir" demişlerdi. Yalnız İbrahim\'in babasına: "Senin için mağfiret dileyeceğim, fakat Allah\'tan gelecek bir şeyi senden savamam" demesi hariç. "Rabbimiz, sana dayandık, sana yöneldik. Dönüş(ümüz) sanadır!"'),
(5155, 60, 5, 'Rabbimiz, bizi inkar edenler için bir sınav yapma (bizi onların baskı ve işkencesi altına düşürme), bizi bağışla. Rabbimiz, yegane galib, hüküm ve hikmet sahibi, ancak Sensin, Sen!'),
(5156, 60, 6, 'Andolsun, onlarda sizin için, Allah\'ı ve "Son Günü" arzu edenler için güzel bir örnek vardır. Kim yüz çevirirse (bilsin ki) Allah işte zengin, övgüye layık olan O\'dur.'),
(5157, 60, 7, 'Belki de Allah sizinle, onlardan düşman olduklarınız arasına bir sevgi koyar. Allah kadirdir. Allah çok bağışlayan, çok esirgeyendir.'),
(5158, 60, 8, 'Allah sizi, din hakkında sizinle savaşmayan ve sizi yurtlarınızdan çıkarmayan kimselere iyilik etmekten, onlara adaletli davranmaktan men\'etmez. Çünkü Allah, adalet yapanları sever.'),
(5159, 60, 9, 'Allah sizi, ancak sizinle din hakkında savaşan, sizi yurtlarınızdan çıkaran ve çıkarılmanıza yardım eden kimselerle dost olmaktan men\'eder. Kim onlarla dost olursa, işte zalimler onlardır.'),
(5160, 60, 10, 'Ey inananlar, mü\'min kadınlar göç ederek size geldiği zaman, onları imtihan edin. Allah onların imanlarını daha iyi bilir. Eğer onların (gerçekten) inanmış olduklarını anlarsanız, onları kafirlere geri döndürmeyin. Ne bu(kadı)nlar onlara helaldir; ne de onlar bunlara helal olurlar. Onların (bu kadınlara) harcadıkları(mehirleri)ni onlara verin. Ücretlerini kendilerine verdiğiniz takdirde bu(kadı)nlarla evlenmenizde sizin için bir günah yoktur. Kafir kadınların ismetlerini (nikah bağlarını) tutmayın (onları salıverin ve kafirlere katılan kadınlara) harcadığınız(mehri)i isteyin. Onlar da (size katılan kadınlarına) harcadıklarını istesinler. Bu size Allah\'ın hükmüdür. Aranızda (böyle) hükmediyor. Allah bilendir, hüküm ve hikmet sahibidir.'),
(5161, 60, 11, 'Eğer eşleriniz(e sarfettiğiniz mehirler)den herhangibir şey kafirlere gider de, sonra (onlardan da size kaçan kadınlar çıkar ve bu kez mehir ödeme) sıra(sı) size gelirse eşleri giden (mü\'minlere) harcadıklarının mislini verin. İnandığınız Allah\'a karşı gelmekten sakının.'),
(5162, 60, 12, 'Ey peygamber, inanmış kadınlar sana gelip Allah\'a hiçbir şeyi ortak koşmamaları, hırsızlık etmemeleri, zina etmemeleri, çocuklarını öldürmemeleri, elleriyle ayakları arasında bir iftira uydurup getirmemeleri, iyi bir işte sana karşı gelmemeleri hususunda sana bi\'at ederlerse onların bi\'atlerini ve onlar için Allah\'tan mağfiret dile. Şüphesiz Allah, çok bağışlayan, çok esirgeyendir.'),
(5163, 60, 13, 'Ey inananlar, Allah\'ın kendilerine gazabettiği; kafirlerin mezarlık halkından umudu kestiği gibi ahiretten umudu kesmiş olan bir topluluk ile dostluk etmeyin!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Göklerde ve yerde bulunanların hepsi Allah\'ı tesbih etmiştir. O üstündür, hüküm ve hikmet sahibidir.'),
(5165, 61, 2, 'Ey inananlar niçin yapmayacağınız şeyi söylüyorsunuz?'),
(5166, 61, 3, 'Yapmayacağınız şeyi söylemek, Allah katında en sevilmeyen bir şeydir.'),
(5167, 61, 4, 'Allah, kendi yolunda kenetlenmiş binalar gibi saf bağlayarak çarpışanları sever.'),
(5168, 61, 5, 'Bir zaman Musa, kavmine: "Ey kavmim, benim, Allah\'ın size gönderdiği elçisi olduğumu bildiğiniz halde niçin beni incitiyorsunuz?" demişti. Onlar eğrilince Allah da kalblerini eğriltti. Allah, yoldan çıkanları doğru yola iletmez.'),
(5169, 61, 6, 'Meryem oğlu Îsa da: "Ey İsrail oğulları, ben size Allah\'ın elçisiyim, benden önce gelen Tevrat\'ı doğrulayıcı ve benden sonra gelecek, Ahmed adında bir elçiyi müjdeleyici olarak gönderildim" demişti. Fakat (Îsa\'nın müjdelediği elçi) onlara apaçık deliller getirince: "Bu, apaçık bir büyüdür" dediler.'),
(5170, 61, 7, 'İslama çağırıldığı halde, Allah\'ın üstüne yalan atandan daha zalim kim olabilir? Allah zalimler topluluğunu doğru yola iletmez.'),
(5171, 61, 8, 'Ağızlarıyle Allah\'ın nurunu söndürmek istiyorlar. Oysa kafirler hoşlanmasa da Allah, nurunu tamamlayacaktır.'),
(5172, 61, 9, 'O, Elçisini, hidayet ve hak din ile gönderdi ki müşrikler hoşlanmasa da onu, bütün dinlere üstün getirsin.'),
(5173, 61, 10, 'Ey inananlar, size, sizi acı azabdan kurtaracak bir ticaret göstereyim mi?'),
(5174, 61, 11, 'Allah\'a ve Elçisine inanırsınız, Allah yolunda mallarınızla ve canlarınızla cihadedersiniz. İşte bilirseniz, sizin için en iyisi budur.'),
(5175, 61, 12, '(Böyle yapınız ki Allah) sizin günahlarınızı bağışlasın ve sizi altlarından ırmaklar akan cennetlere ve durulmağa değer bahçeler içinde güzel konutlara koysun. İşte büyük başarı budur.'),
(5176, 61, 13, 'Seveceğiniz bir şey daha var: Allah\'tan bir zafer ve yakın bir fetih... Mü\'minleri müjdele.'),
(5177, 61, 14, 'Ey inananlar, Allah\'ın yardımcıları olun. Nitekim Meryem oğlu Îsa da havarilere: "Allah yolunda benim yardımcıları kimdir?" demişti. Havariler: "Allah(yolun)un yardımcların biziz" dediler. İsrail oğullarından bir zümre inandı, bir zümre inkar etti. Biz de inananları, düşmanlarına karşı destekledik, onlar üstün geldiler.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Göklerde ve yerde bulunanların hepsi padişah, mukaddes, aziz, hakim olan Allah\'ı tesbih etmektedir.'),
(5179, 62, 2, 'O\'dur ki ümmiler içinde, kendilerinden olan ve onlara Allah\'ın ayetlerini okuyan, onları yücelten, onlara Kitabı ve hikmeti öğreten bir elçi gönderdi. Oysa onlar, önceden, açık bir sapıklık içinde idiler.'),
(5180, 62, 3, '(O Elçiyi) yine onlardan olup henüz kendilerine katılmamış bulunan başka kimselere de (gönderdi). O azizdir, hakimdir.'),
(5181, 62, 4, 'Bu, Allah\'ın, dilediğine vereceği lutfudur. Allah, büyük lutuf sahibidir.'),
(5182, 62, 5, 'Kendilerine Tevrat yükletilip de sonra onu taşımayan(onun buyruklarını tutmayan)ların durumu, Kitaplar taşıyan eşeğin durumu gibidir. Allah\'ın ayetlerini yalanlayanların durumu ne kötüdür. Allah zalimler topluluğunu doğru yola iletmez.'),
(5183, 62, 6, 'De ki: "Ey yahudi olanlar, eğer insanlar arasında yalnız sizin, Allah\'ın dostları olduğunuzu sanıyorsanız, (bu inancınızda) samimi iseniz ölümü temenni edin.'),
(5184, 62, 7, 'Ama onlar, ellerinin (yapıp) öne sürdüğü (işler) yüzünden asla ölümü temenni etmezler. Allah zalimleri bilir.'),
(5185, 62, 8, 'De ki: "Sizin, kendisinden kaçtığınız ölüm, sizi mutlaka bulacaktır. Sonra görünmeyeni ve görüneni Bilen\'e döndürüleceksiniz, O size yaptıklarınızı haber verecektir.'),
(5186, 62, 9, 'Ey inananlar, Cuma günü namaz için çağrıldığı(nız) zaman, Allah\'ı anmağa koşun, alışverişi bırakın. Eğer bilirseniz bu, sizin için daha hayırlıdır.'),
(5187, 62, 10, 'Namaz kılındıktan sonra yeryüzüne dağılın ve Allah\'ın lutfundan (nasibinizi) arayın. Allah\'ı çok anın ki başarıya eresiniz.'),
(5188, 62, 11, 'Bir ticaret veya eğlence gördükleri zaman hep dağılıp ona giderler ve seni ayakta bırakırlar. De ki: "Allah\'ın yanında bulunan, eğlenceden de ticaretten de hayırlıdır. Allah, rızık verenlerin en hayırlısıdır."');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Münafıklar sana geldikleri zaman: "Senin muhakkak Allah\'ın elçisi olduğuna tanıklık ederiz" derler. Senin muhakkak kendisinin elçisi olduğunu Allah bilir ve Allah münafıkların yalancı olduklarına tanıklık eder.'),
(5190, 63, 2, 'Yeminlerini kalkan yapıp Allah\'ın yoluna engel oldular. Onların yaptıkları ne kötüdür!'),
(5191, 63, 3, '(Bu davranışlarının) Sebebi şudur: İnandılar, sonra inkar ettiler, bu yüzden kalblerinin üzeri mühürlendi, artık onlar anlamazlar.'),
(5192, 63, 4, 'Onları gördüğün zaman cisimleri hoşuna gider (çünkü gösterişli adamlardır,) konuşsalar sözlerini dinlersin, onlar dayatılmış odunlar gibidirler. Her bağırtıyı kendi aleyhlerinde sanırlar. Onlar düşmandır, onlardan sakın. Allah onları kahretsin, nasıl da (haktan) döndürülüyorlar?'),
(5193, 63, 5, 'Onlara: "Gelin, Allah\'ın Elçisi sizin için mağfiret dilesin" dendiği zaman başlarını çevirirler ve onların, büyüklük taslayarak yüz çevirdiklerini görürsün.'),
(5194, 63, 6, 'Onlar için mağfiret dilesen de, mağfiret dilemesen de onlar için birdir. Allah onları bağışlamayacaktır. Çünkü Allah, yoldan çıkan topluluğu yola iletmez.'),
(5195, 63, 7, 'Onlar öyle kimselerdir ki: "Allah\'ın Elçisinin yanında bulunanları beslemeyin ki dağılıp gitsinler" diyorlar. Oysa göklerin ve yerin hazineleri Allah\'ındır, fakat münafıklar anlamazlar.'),
(5196, 63, 8, 'Diyorlar ki: "Andolsun, eğer Medine\'ye dönersek üstün olan, alçak olanı oradan mutlaka çıkaracaktır." Üstünlük, ancak Allah\'a, Elçisine ve mü\'minlere mahsustur. Fakat münafıklar bilmezler.'),
(5197, 63, 9, 'Ey inananlar, mallarınız ve çocuklarınız sizi Allah\'ı anmaktan alıkoymasın. Kim bunu yaparsa işte onlar ziyana uğrayanlardır.'),
(5198, 63, 10, 'Biriniz kendisine ölüm gelip de: "Rabbim beni yakın bir süreye kadar erteleseydin de sadaka verip iyilerden olsaydım!" demeden önce, size verdiğimiz rızıktan sadaka verin.'),
(5199, 63, 11, 'Allah, süresi dolan hiçbir canı ertelemez. Allah, yaptıklarınızı haber alandır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Göklerde ve yerde bulunanların hepsi Allah\'ın şanının yüceliğini anmaktadır. Mülk O\'nundur, hamd O\'nundur. O, herşeye kadirdir.'),
(5201, 64, 2, 'Sizi yaratan O\'dur. Kiminiz kafirdir, kiminiz mü\'min. Allah yaptıklarınızı görmektedir.'),
(5202, 64, 3, 'Gökleri ve yeri hak (hikmet) ile yarattı, sizi biçimlendirdi, biçimlerinizi güzel yaptı. Dönüş O\'nadır.'),
(5203, 64, 4, 'Göklerde ve yerde bulunanları bilir, gizlediğiniz ve açığa vurduğunuz şeyleri de bilir. Allah, göğüslerin özünü bilendir.'),
(5204, 64, 5, 'Daha önce inkar etmiş olanların haberi size gelmedi mi? (Onlar), işlerinin vebalini taddılar ve onlar için acı bir azab da vardır.'),
(5205, 64, 6, 'Çünkü onlara elçileri, açık deliller getirirlerdi, fakat onlar, "Bir insan mı bize yol gösterecek" deyip inkar ettiler ve yüz çevirdiler. Allah da (hiçbir şeye) muhtaç olmadığını gösterdi. Allah zengindir, övülmüştür.'),
(5206, 64, 7, 'İnkar edenler, kesinlikle diriltilmeyeceklerini sandılar. De ki: "Hayır, Rabbim hakkı için mutlaka diriltileceksiniz, sonra yaptıklarınız size haber verilecektir. Bu, Allah\'a göre kolaydır.'),
(5207, 64, 8, 'Artık Allah\'a, Elçisine ve indirdiğimiz ışığa inanın. Allah, yaptıklarınızı haber almaktadır.'),
(5208, 64, 9, 'Toplantı günü için sizi topladığı gün, işte o aldanma günüdür. Kim Allah\'a inanır ve yararlı iş yaparsa (Allah) onun kötülüklerini örter ve onu, altlarından ırmaklar akan cennetlere sokar, orada ebedi kalırlar. İşte büyük başarı budur.'),
(5209, 64, 10, 'Nankörlük eden ve ayetlerimizi yalanlayanlar ise ateş halkıdır. Orada sürekli kalacaklardır. Ne kötü gidilecek yerdir orası!'),
(5210, 64, 11, 'Başa gelen her musibet Allah\'ın izniyledir. Kim Allah\'a inanırsa (Allah) onun kalbini doğru(düşünce)ye iletir. Allah, herşeyi bilendir.'),
(5211, 64, 12, 'Allah\'a ita\'at edin, Elçiye ita\'at edin. Eğer dönerseniz (bilin ki) Elçimize düşen, açıkça duyurmaktır.'),
(5212, 64, 13, 'Allah ki O\'ndan başka tanrı yoktur. Mü\'minler Allah\'a dayansınlar.'),
(5213, 64, 14, 'Ey inananlar, eşlerinizden ve çocuklarınızdan bazıları size düşmandır. Onlardan sakının. Ama affeder, hoşgörür, bağışlarsanız muhakkak ki Allah da bağışlayandır, esirgeyendir (O da sizi bağışlar).'),
(5214, 64, 15, 'Mallarınız ve evladlarınız bir fitne(sınav)dir, (Allah, onlarla sizi imtihan etmektedir). Allah ise, işte büyük ödül O\'nun yanındadır.'),
(5215, 64, 16, 'Öyle ise gücünüz yettiği kadar Allah\'tan korkun, (O\'nun öğütlerini) dinleyin, (O\'na) ita\'at edin ve kendi iyiliğinize olarak (mallarınızı Allah uğrunda) harcayın. Kim nefsinin cimriliğinden korunursa işte onlar, başarıya erenlerdir.'),
(5216, 64, 17, 'Eğer Allah\'a güzel borç verirseniz, Allah onu sizin için kat kat yapar ve sizi bağışlar. Allah karşılık verendir, halimdir (hoşgörülüdür).'),
(5217, 64, 18, 'Görünmeyeni ve görüneni bilendir. (O\'na hiçbir şey gizli kalmaz, O,) Azizdir, hakimdir (üstündür, hüküm ve hikmet sahibidir).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Ey peygamber, kadınları boşa(mak iste)diğiniz zaman onları iddetleri içinde (adetten temiz oldukları sırada) boşayın ve iddeti sayın (üç defa adet görüp temizlenmelerini hesabedin). Rabbiniz Allah\'tan korkun (bekleme süresi içinde) onları evlerinden çıkarmayın. Kendileri de çıkmasınlar. Ancak apaçık bir edepsizlik yapmaları durumu, bu hükmün dışındadır (o zaman evden çıkarabilirsiniz). Bunlar Allah\'ın sınırlarıdır. Kim Allah\'ın sınırlarını geçerse, kendisine yazık etmiş olur. Bilmezsin belki Allah, bundan sonra (iddet süresi içinde) yeni bir iş ortaya çıkarır (gönülleri uzlaştırıp birleşme ortamı yaratır).'),
(5219, 65, 2, 'Sürelerinin sonuna vardıklarında ya onları güzelce (yanınızda) tutun, yahut, güzellikle onlardan ayrılın. (Eşinizi yanınızda tutmak veya ondan ayrılmak için) içinizden adaletli iki kişiyi de şahid tutun. Şahidliği Allah için yapın. İşte içinizden Allah\'a ve Son Güne inanan kimseye öğütlenen budur. Kim Allah(ın yasakların)dan sakınırsa (Allah) ona bir çıkış (yolu) yaratır.'),
(5220, 65, 3, 'Ve onu ummadığı yerden rızıklandırır. Kim Allah\'a dayanırsa O, ona yeter. Allah, buyruğunu yerine getirendir. Allah herşey için bir ölçü koymuştur.'),
(5221, 65, 4, '(Yaşlılıklarından ötürü) Adetten kesilen kadınlarınızın (bekleme süresinden) şüphe ederseniz, (bilin ki) onların bekleme süresi üç aydır. Henüz adet görmeyenler de böyledir. Gebe olanların bekleme süresi, yüklerini bırakmalarına kadardır. Kim Allah\'tan korkarsa (Allah) ona işinde bir kolaylık yaratır.'),
(5222, 65, 5, 'Bu, Allah\'ın size indirdiği buyruğudur. Kim Allah\'tan korkarsa (Allah) onun kötülüklerini örter ve onun mükafatını büyütür.'),
(5223, 65, 6, '(Boşadığınız) O kadınları, gücünüz ölçüsünde oturduğunuz yerin bir bölümünde oturtun ve onları sıkıştır(ıp evden çıkmağa zorla)mak için kendilerine zarar vermeğe kalkışmayın. Şayet gebe iseler, yüklerini bırakıncaya kadar onların geçimini sağlayın. Sonra sizin için (çocuğunuzu) emzirirlerse onlara ücretlerini verin ve aranızda güzellikle konuşup anlaşın. Eğer (anlaşmakta) güçlük çekerseniz (o zaman) çocuğu, başka bir kadın emzirecektir.'),
(5224, 65, 7, 'Eli geniş olan, genişliğine göre nafaka versin. Rızkı kısılmış bulunan da Allah\'ın kendisine verdiğinden versin. Allah, bir kişiye ne vermişse ancak onu yükler, (kimseye gücünün üstünde bir şey yüklemez). Allah, bir güçlükten sonra bir kolaylık yaratacaktır.'),
(5225, 65, 8, 'Nice kent var ki Rabbinin ve elçilerinin buyruğuna baş kaldırdı, biz de onu çetin bir hesaba çektik ve ona görülmemiş biçimde azabettik.'),
(5226, 65, 9, 'İşinin vebalini taddı. İşinin sonucu da tüm bir ziyan idi.'),
(5227, 65, 10, 'Allah o(insa)nlara şiddetli bir azab da hazırlamıştır. Ey inanmış olan, sağduyu sahipleri, Allah\'tan korkun, Allah size bir uyarı indirdi.'),
(5228, 65, 11, 'Yani size Allah\'ın açık açık ayetlerini okuyan bir elçi (gönderdi) ki, inanıp yararlı işler yapanları, karanlıklardan aydınlığa çıkarsın. Kim Allah\'a inanır ve yararlı iş yaparsa (Allah) onu, altlarından ırmaklar akan, içinde ebedi kalacakları cennetlere sokar. Allah ona gerçekten güzel rızık vermiştir.'),
(5229, 65, 12, 'Allah O\'dur ki yedi göğü ve yerden de onların benzerini yarattı. (Allah\'ın) Buyruğu, bunlar arasında iner ki Allah\'ın herşeye kadir olduğunu ve Allah\'ın bilgice her şeyi kuşatmış bulunduğunu bilesiniz.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Ey peygamber niçin, Allah\'ın sana helal kıldığı şeyi, eşlerinin, hatırı için haram kılıyorsun? Allah bağışlayadır, esirgeyendir.'),
(5231, 66, 2, 'Allah size, yeminlerinizi (keffaretle) çözmeyi meşru\' kılmıştır. Allah sizin sahibinizdir. O bilendir, hüküm ve hikmet sahibidir.'),
(5232, 66, 3, 'Peygamber, eşlerinden birine gizli bir söz söylemişti. Fakat eşi, o sözü (başkasına) haber verip, Allah da peygamberi, eşinin bu davranışına muttali kılınca (Peygamber, eşine) o(söylediği)nin bir kısmını bildirmiş (şunları şunları filana söyledin demiş), bir kısmından da vazgeçmişti. (Peygamber) Bunu eşine haber verince eşi: "Bunu sana kim söyledi?" dedi (Peygamber): "(Herşeyi) Bilen, haber alan (Allah) bana söyledi" dedi.'),
(5233, 66, 4, 'Eğer ikiniz, kalblerinizin sapmış olmasından dolayı Allah\'a tevbe ederseniz (ne a\'la). Ve eğer peygambere karşı birbirinize arka olursanız (bilin ki) onun koruyucusu ve yardımcısı Allah, Cibril ve mü\'minlerin iyileridir. Ayrıca melekler de ona arkadır.'),
(5234, 66, 5, 'O sizi boşarsa belki de Rabbi ona, sizden daha hayırlı, kendisini Allah\'a teslim eden, inanan, gönülden ita\'at eden, tevbe eden, ibadet eden seyahat eden dul ve bakire eşler verir.'),
(5235, 66, 6, 'Ey inananlar, kendinizi ve ailenizi bir ateşten koruyun ki onun yakıtı insanlar ve taşlardır. Onun başında gayet katı, şiddetli, Allah\'ın kendilerine buyurduğuna karşı gelmeyen ve emredildikleri şeyi yapan melekler vardır.'),
(5236, 66, 7, '(Oraya girenlere derler ki:) Ey nankörlük edenler, bugün özür dilemeyin. Çünkü siz, ancak yaptığınız şeylerle cezalandırılıyorsunuz.!"'),
(5237, 66, 8, 'Ey inananlar, Allah\'a yürekten tevbe edin. Umulur ki Rabbiniz sizin kötülüklerinizi örter; Allah\'ın, peygamberi ve onunla beraber inanmış olanları utandırmayacağı günde, sizi altlarından ırmaklar akan cennetlere sokar. (O gün) onların nuru, önleridenve sağ yanlarından koşar. Derler ki: "Rabbimiz, nurumuzu tamamla, bizi bağışla. Doğrusu, senin herşeye gücün yeter!"'),
(5238, 66, 9, 'Ey Peygamber! kafirlerle ve iki yüzlülerle uğraş, onlara karşı katı davran. Onların varacağı yer cehennemdir. Ne kötü varılacak yerdir o!'),
(5239, 66, 10, 'Allah inkar edenler hakkında Nuh\'un karısı ile Lut\'un karısını misal verdi. Bu ikisi, kullarımızdan iki Salih kulun (nikahı) altında idiler, onlara hiyanet ettiler. Kocaları Allah\'tan (gelen) hiçbir şeyi onlardan savamadı. (Onlara): "Haydi, girenlerle beraber siz de ateşe girin" denildi.'),
(5240, 66, 11, 'Allah inananlar hakkında da Fir\'avn\'ın karısını misal verdi. O şöyle demişti: "Rabbim, bana katında, cennetin içinde bir ev yap, beni Fir\'avn\'dan ve onun (kötü) işinden kurtar. Ve beni şu zalimler topluluğundan kurtar!"'),
(5241, 66, 12, '(Yine Allah) İmran\'ın kızı Meryem\'i de (misal verdi). O ırzını korumuştu, biz de on(un rahmin)e ruhumuzdan üflemiştik. O, Rabbinin kelimelerini ve Kitaplarını doğrulamış ve gönülden ita\'at edenlerden olmuştu.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Mülk (mutlak hükümranlık ve yönetim), elinde bulunan yüce Allah, kutludur. O\'nun herşeye gücü yeter.'),
(5243, 67, 2, 'O, hanginizin daha güzel iş yapacağınızı denemek için ölümü ve hayatı yarattı. O, üstündür, bağışlayandır.'),
(5244, 67, 3, 'O, yedi göğü, birbiri üzerinde tabaka, tabaka yarattı, Rahman\'ın yaratmasında bir aykırılık, uygunsuzluk görmezsin. Gözü(nü) döndür de bak, bir bozukluk görüyor musun?'),
(5245, 67, 4, 'Sonra gözü(nü) iki kez daha döndür (bak). Göz (aradığı bozukluğu bulmaktan) umudu keserek hor ve bitkin bir halde sana döner.'),
(5246, 67, 5, 'Andolsun biz, en yakın göğü lambalarla donattık ve onları, şeytanlar için taşlamalar yaptık. Ve o(şeyta)nlara da çılgın ateş azabını hazırladık.'),
(5247, 67, 6, 'Rablerine nankörlük edenler için cehennem azabı vardır. Ne kötü gidilecek sonuçtur o!'),
(5248, 67, 7, 'Oraya atıldıkları zaman onun öfkeli homurtusunu işitirler, kaynıyor:'),
(5249, 67, 8, 'Neredeyse öfkeden çatlayacak. Her topluluk onun içine atıldıkça onun bekçileri, onlara: "Size bir uyarıcı gelmedi mi?" diye sordu(lar).'),
(5250, 67, 9, 'Dediler: "Evet, bize uyarıcı geldi ama biz yalanladık ve: \'Allah hiçbirşey indirmedi, siz ancak büyük bir sapıklık içindesiniz\' dedik."'),
(5251, 67, 10, 'Ve dediler ki: "Eğer söz dinleseydik, yahut düşünseydik, şu çılgın ateşin halkı arasında bulunmazdık!"'),
(5252, 67, 11, 'Günahlarını itiraf ettiler. O çılgın ateş halkına (Allah\'ın acımasından) uzak olup ezilmek yaraşır!'),
(5253, 67, 12, 'Fakat gizlide Rablerine saygılı olanlara gelince, onlar için bağış(lama) ve büyük mükafat vardır.'),
(5254, 67, 13, 'Sözünüzü ister gizleyin, ister onu açığa vurun (farketmez) çünkü O, göğüslerin özünü bilir.'),
(5255, 67, 14, 'Yaratan bilmez mi? O latiftir (bilgisi herşeyin içine geçen, herşeyi) haber alandır.'),
(5256, 67, 15, 'O size yeri boyun eğer yaptı. Haydi onun omuzlarında yürüyün ve Allah\'ın rızkından yeyin. (Sonunda) Dönüş O\'nadır (size verdiği ni\'metlere karşı şükredip etmediğinizi sizden soracak, sizi hesaba çekecektir).'),
(5257, 67, 16, 'Gökte olanın, sizi yere batırmayacağından emin misiniz? O zaman yer, birden sallanmağa başlar (ve siz yerin dibine geçersiniz).'),
(5258, 67, 17, 'Yoksa siz, gökte olanın, üzerine taş yağdıran (bir fırtına) göndermeyeceğinden emin misiniz? (O zaman) tehdidimin nasıl olduğunu bileceksiniz.'),
(5259, 67, 18, 'Andolsun, onlardan öncekiler de yalanladılar. Ama benim (onların yaptıklarını) inkarım nasıl oldu?'),
(5260, 67, 19, 'Üstlerinde (kanatlarını) açıp yumarak uçan kuşları görmüyorlar mı? Onları (havada) Rahman\'dan başkası tutmuyor. Doğrusu O, herşeyi görmektedir.'),
(5261, 67, 20, 'Yahut Rahman\'dan başka size yardım ed(ip sizi O\'nun azabından kurtar)acak askeriniz kimdir? Kafirler derin bir gaflet ve aldanma içindedirler.'),
(5262, 67, 21, 'Yahut Allah, rızkını tutacak olursa size rızık verecek kimdir? Doğrusu onlar, azgınlık ve nefret içinde direnmektedirler.'),
(5263, 67, 22, 'Şimdi, yüzüstü kapanarak yürüyen mi doğru gider, yoksa yolda düzgün yürüyen mi?'),
(5264, 67, 23, 'De ki: "Sizi yaratan, size işitme (duyusu), gözler ve gönüller veren O\'dur. Ne kadar az şükrediyorsunuz?'),
(5265, 67, 24, 'De ki: "Sizi yerde üreten O\'dur ve toplanıp O\'na götürüleceksiniz."'),
(5266, 67, 25, 'Doğru (söylüyor) iseniz bu tehdid(ettiğiniz azab) ne zaman gelecek? diyorlar.'),
(5267, 67, 26, 'De ki: (Ona ait) Bilgi, Allah\'ın yanındadır. Ben ancak apaçık bir uyarıcıyım."'),
(5268, 67, 27, 'Onu yakın görünce inkar edenlerin yüzleri kötüleşti. Ve: "İşte çağırıp durduğunuz şey budur!" dendi.'),
(5269, 67, 28, 'De ki: "Baksanıza, eğer Allah beni ve benimle beraber olanları öldürse de yahut bize acısa da (fark etmez,) kafirleri acı azabdan kim kurtarabilir?"'),
(5270, 67, 29, 'De ki: "O, çok merhametlidir. O\'na inanmış, O\'na dayanmışızdır. Yakında kimin apaçık bir sapıklık içinde olduğunu bileceksiniz."'),
(5271, 67, 30, 'De ki: "Baksanıza, eğer suyunuz çekilse, size kim bir akar su getirebilir?"');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nun. Kaleme ve (kalemle) yazdıklarına andolsun.'),
(5273, 68, 2, 'Sen, Rabbinin ni\'metiyle cinlenmiş (deli) değilsin.'),
(5274, 68, 3, 'Senin için kesintisiz bir mükafat vardır.'),
(5275, 68, 4, 'Ve sen, büyük bir ahlak üzerindesin.'),
(5276, 68, 5, '(Sen de) Göreceksin, onlar da görecekler;'),
(5277, 68, 6, 'Hanginizin fitnelenmiş (cin çarpmış delirmiş) olduğunu.'),
(5278, 68, 7, 'Şüphesiz Rabbin, kim(ler)in kendi yolundan saptığını ve kimlerin yolda olduğunu en iyi bilen O\'dur.'),
(5279, 68, 8, 'Öyleyse yalanlayanlara ita\'at etme.'),
(5280, 68, 9, 'İstediler ki, sen yağcılık yapasın da onlar da yağcılık yapsınlar (sana yumuşak davransınlar).'),
(5281, 68, 10, 'Şunların hiçbirine ita\'at etme: Yemin edip duran aşağılık,'),
(5282, 68, 11, 'Kötüleyip duran, söz götürüp getiren,'),
(5283, 68, 12, 'Hayra engel olan, saldırgan, günahkar,'),
(5284, 68, 13, 'Kaba, sonra da kötülükle damgalı,'),
(5285, 68, 14, 'Mal ve oğullar sahibi olmuş diye (yolunu şaşırmış).'),
(5286, 68, 15, 'Kendisine ayetlerimiz okunduğu zaman: "Eskilerin masalları" der.'),
(5287, 68, 16, 'Biz onu burnunun üzerine damga vurup işaretleyeceğiz.'),
(5288, 68, 17, 'Biz bunlara da bela verdik, şu bahçe sahiplerine bela verdiğimiz gibi: Hani onlar, sabah olunca bahçeyi mutlaka devşireceklerine yemin etmişlerdi.'),
(5289, 68, 18, 'İstisna da etmiyorlar(Allah dilerse biçeriz demiyorlar)dı.'),
(5290, 68, 19, 'Fakat onlar uyurlarken hemen (gönderilen) dolaşıcı bir bela, onu sardı da,'),
(5291, 68, 20, 'Bahçe simsiyah kesiliverdi.'),
(5292, 68, 21, 'Sabahleyin birbirlerine seslendiler:'),
(5293, 68, 22, 'Haydi devşirecekseniz erkenden ekininize gidin diye.'),
(5294, 68, 23, 'Derken yürüdüler; fısıldaşıyorlardı:'),
(5295, 68, 24, 'Sakın, bugün hiçbir yoksul bahçeye girip yanınıza sokulmasın diye.'),
(5296, 68, 25, 'Devşirebileceklerini umarak erkenden gittiler.'),
(5297, 68, 26, 'Fakat bahçeyi görünce: "Herhalde biz yolu şaşırdık." dediler.'),
(5298, 68, 27, 'Hayır, doğrusu biz mahrum bırakıldık!'),
(5299, 68, 28, 'Orta(yolda giden iyi)leri: "Ben size demedim mi? Rabbinizi tesbih etmeniz gerekmez miydi?" dedi.'),
(5300, 68, 29, 'Rabbimizi tesbih ederiz, doğrusu biz zulmedenlermişiz! dediler.'),
(5301, 68, 30, 'Dönüp birbirlerini kınamağa başladılar:'),
(5302, 68, 31, 'Yazık bize, dediler, biz azgınlarmışız!'),
(5303, 68, 32, 'Belki Rabbimiz, bize onun yerine ondan daha iyisini verir. Biz Rabbimize yönelir, O\'ndan umarız. 	It may be that our Lord will give us better than this in place thereof. Lo! we beseech our Lord.'),
(5304, 68, 33, 'İşte azab böyledir. Ahiret azabı ise daha büyüktür, keşke bilselerdi.'),
(5305, 68, 34, 'Korunanlar için de Rableri katında ni\'met bahçeleri vardır.'),
(5306, 68, 35, 'Biz müslümanları suçlular gibi yapar mıyız hiç?'),
(5307, 68, 36, 'Neyiniz var, nasıl hüküm veriyorsunuz?'),
(5308, 68, 37, 'Yoksa sizin bir Kitabınız var da onda mı (bu hükümleri) okuyorsunuz?'),
(5309, 68, 38, 'Onda istediğiniz her şeyi buluyorsunuz?'),
(5310, 68, 39, 'Yoksa sizin istediğiniz hükmü verebileceğinize dair, kıyamete kadar sürecek andlarınız mı var üzerimizde?'),
(5311, 68, 40, 'Sor onlara: Onların hangisi buna kefil olacak?'),
(5312, 68, 41, 'Yoksa kendilerinin ortakları mı var? Doğru iseler ortaklarını çağırsınlar.'),
(5313, 68, 42, 'Bacaktan açılacağı (paçanın sıvanacağı, işlerin güçleşeceği) ve secdeye da\'vet edilecekleri gün (secde) edemezler.'),
(5314, 68, 43, 'Gözleri düşük olarak yüzlerini bir zillet kaplar. Onlar sağlam iken de secdeye da\'vet edilirler(fakat secde etmezler)di.'),
(5315, 68, 44, 'Bu sözü yalanlayanı bana bırak; onları bilmedikleri yerden derece derece (azaba) yaklaştıracağız.'),
(5316, 68, 45, 'Onlara mühlet veriyorum. Doğrusu benim tuzağım sağlamdır (onu kimse bozamaz).'),
(5317, 68, 46, 'Yoksa sen onlardan bir ücret istiyorsun da onlar ağır borç altında mı kalıyorlar?'),
(5318, 68, 47, 'Yoksa gayb (görünmez bilgi hazinesi), kendi yanlarında da onlar mı (istedikleri gibi) yazıyorlar?'),
(5319, 68, 48, 'Sen Rabbinin hükmüne sabret, balık sahibi (Yunus) gibi olma. Hani o, sıkıntıdan yutkunarak (Allah\'a) seslenmişti.'),
(5320, 68, 49, 'Eğer Rabbinden ona bir ni\'met yetişmeseydi, yerilerek çıplak bir yere atılırdı.'),
(5321, 68, 50, 'Fakat Rabbi onun du\'asını kabul etti de onu Salih(iyi insan)lardan yaptı.'),
(5322, 68, 51, 'O inkar edenler Zikr(Kur\'an)\'ı işittikleri zaman, neredeyse seni gözleriyle devireceklerdi. "O mecnundur" diyorlardı.'),
(5323, 68, 52, 'Halbuki o, alemler için uyarıdan başka bir şey değildir!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Gerçekleşen,'),
(5325, 69, 2, 'Nedir o gerçekleşen?'),
(5326, 69, 3, 'Gerçekleşenin ne olduğunu nerden bileceksin?'),
(5327, 69, 4, 'Semud ve \'Ad (kavimleri), başa çarpan olayı yalanladılar.'),
(5328, 69, 5, 'Bu yüzden Semud (kavmi) azgın bir vak\'a ile helak edildiler.'),
(5329, 69, 6, 'Ad (kavmi) ise uğultulu, azgın bir kasırga ile helak edildiler.'),
(5330, 69, 7, '(Allah) Onu, yedi gece, sekiz gün ardı ardına onların üzerine saldı. O kavmi orada, içi boş hurma kütükleri gibi serilmiş görürsün.'),
(5331, 69, 8, 'Onlardan hiç geri kalan görüyor musun?'),
(5332, 69, 9, 'Fir\'avn ve ondan öncekiler ve altüst olmuş kentler(in halkı olan Lut kavmi) de hatalı iş yaptılar.'),
(5333, 69, 10, 'Rablerinin elçisine karşı geldiler. O da onları şiddeti gittikçe artan bir yakalayışla yakaladı.'),
(5334, 69, 11, 'Su(lar) kabarınca biz sizi, akıp giden(gemi)de taşıdık.'),
(5335, 69, 12, 'Ki onu size bir ibret yapalım ve belleyen kulak(lar) onu bellesin.'),
(5336, 69, 13, 'Sur\'a bir tek üfleme üflendiği,'),
(5337, 69, 14, 'Arz ve dağlar yerlerinden kaldırılıp şiddetle birbirine çarpılarak darmadağın olduğu zaman,'),
(5338, 69, 15, 'İşte o gün, olan olmuştur.'),
(5339, 69, 16, 'Gök yarılmıştır; o gün o, zayıf, sarkıktır.'),
(5340, 69, 17, 'Melekler de onun kenarlarındadır. O gün Rabbinin tahtını, üstlerinde sekiz (melek) taşır.'),
(5341, 69, 18, 'O gün (Allah\'a) arz olunursunuz. Sizden hiçbir giz, (Allah\'a) gizli kalmaz.'),
(5342, 69, 19, 'Kitabı sağından verilen: "Alın Kitabımı okuyun" der.'),
(5343, 69, 20, 'Ben hesabımla karşılaşacağımı sezmiştim zaten.'),
(5344, 69, 21, 'Artık o, memmun eden bir yaşam içindedir.'),
(5345, 69, 22, 'Yüksek bir bahçede.'),
(5346, 69, 23, 'Ki devşirmesi kolay (meyvaları yakın. Oturan, elini uzatıp alabilir).'),
(5347, 69, 24, 'Geçmiş günlerde yaptığınız işlerden ötürü afiyetle yeyin, için!'),
(5348, 69, 25, 'Kitabı sol tarafından verilen ise der ki: "Keşke bana Kitabım verilmeseydi!"'),
(5349, 69, 26, 'Şu hesabımı hiç bilmemiş olsaydım!'),
(5350, 69, 27, 'Keşke (ölüm) işimi bitirmiş olsaydı!'),
(5351, 69, 28, 'Malım bana hiçbir yarar sağlamadı.'),
(5352, 69, 29, 'Gücüm (saltanatım) benden yok olup gitti'),
(5353, 69, 30, '(Allah, cehennemin muhafızlarına buyurur:) "Tutun onu, bağlayın onu."'),
(5354, 69, 31, 'Sonra cehenneme sallayın onu!'),
(5355, 69, 32, 'Sonra uzunluğu yetmiş arşın olan zincire vurun onu!'),
(5356, 69, 33, 'Çünkü o büyük Allah\'a inanmıyordu.'),
(5357, 69, 34, 'Yoksulu doyurmaya ön ayak olmuyurdu!'),
(5358, 69, 35, 'Bugün burada onun için candan bir dost yoktur.'),
(5359, 69, 36, 'İrinden başka yiyecek de yoktur.'),
(5360, 69, 37, 'Onu, (bile bile) hata işleyenlerden başkası yemez.'),
(5361, 69, 38, 'Yoo, yemin ederim; gördüklerinize,'),
(5362, 69, 39, 'Ve görmediklerinize,'),
(5363, 69, 40, 'Ki, o (Kur\'an) elbette değerli bir elçinin sözüdür.'),
(5364, 69, 41, 'O, bir şa\'irin sözü değildir. Ne de az inanıyorsunuz!'),
(5365, 69, 42, 'Bir kahinin sözü de değildir. Ne de az düşünüyorsunuz!'),
(5366, 69, 43, 'Alemlerin Rabbi tarafından indirilmiştir.'),
(5367, 69, 44, 'Eğer o, (Muhammed), bazı laflar uydurup bize iftira etseydi,'),
(5368, 69, 45, 'Elbette onun sağ(elini veya kuvvet)ini alırdık.'),
(5369, 69, 46, 'Sonra onun can damarını keserdik.'),
(5370, 69, 47, 'Sizden hiç kimse buna engel olamazdı.'),
(5371, 69, 48, 'O (Kur\'an), korunanlar için bir öğüttür.'),
(5372, 69, 49, 'Biz, içinizde yalanlayanlar bulunduğunu elbette biliyoruz.'),
(5373, 69, 50, 'Doğrusu o, kafirler için hasrettir.'),
(5374, 69, 51, 'O, kesin gerçektir.'),
(5375, 69, 52, 'Öyleyse ulu Rabbinin adını tesbih et (O\'nun eksikliklerinden uzak, yücelerden yüce olduğunu an).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Bir soran, inecek azabı sordu:'),
(5377, 70, 2, 'Kafirler için, ki onu savacak yoktur,'),
(5378, 70, 3, 'Yükselme derecelerinin sahibi Allah\'tan.'),
(5379, 70, 4, 'Melekler ve Ruh, miktarı elli bin yıl süren bir gün içinde O\'na çıkar.'),
(5380, 70, 5, 'Şimdi sen güzelce sabret.'),
(5381, 70, 6, 'Onlar onu uzak görüyor(lar).'),
(5382, 70, 7, 'Biz ise onu yakın görüyoruz.'),
(5383, 70, 8, 'O gün gök, erimiş maden gibi olur.'),
(5384, 70, 9, 'Dağlar, renkli yün gibi olur.'),
(5385, 70, 10, 'Dost dostun halini sormaz.'),
(5386, 70, 11, 'Birbirlerine gösterilirler (fakat herkes kendi derdine düştüğünden, başkasıyle ilgilenemez). Suçlu ister ki o günün azabından (kurtulmak için) fidye versin: Oğullarını,'),
(5387, 70, 12, 'Eşini ve kardeşini,'),
(5388, 70, 13, 'Kendisini barındıran, içinde yetiştiği tüm ailesini,'),
(5389, 70, 14, 'Ve yeryüzünde bulunanların hepsini (versin) de tek kendisini kurtarsın.'),
(5390, 70, 15, 'Hayır! O (ateş), alevlenen bir ateştir.'),
(5391, 70, 16, 'Derileri kavurur, soyar.'),
(5392, 70, 17, '(Kendine) Çağırır; sırtını dönüp gideni,'),
(5393, 70, 18, '(Mal) Toplayıp kasada yığanı!'),
(5394, 70, 19, 'Doğrusu insan hırslı (ve huysuz) yaratılmıştır.'),
(5395, 70, 20, 'Kendisine kötülük dokundu mu sızlanır,'),
(5396, 70, 21, 'Kendisine hayır dokundu mu yardım etmez (sıkı sıkı tutar).'),
(5397, 70, 22, 'Ancak namaz kılanlar bunun dışındadır.'),
(5398, 70, 23, 'Onlar ki: Namazlarını sürekli kılarlar (aksatmazlar).'),
(5399, 70, 24, 'Onların mallarında belli bir hisse vardır:'),
(5400, 70, 25, 'Saile ve mahruma (isteyene ve utancından dolayı istemeyip mahrum kalana).'),
(5401, 70, 26, 'Ceza gününü tasdik ederler,'),
(5402, 70, 27, 'Rablerinin azabından korkarlar.'),
(5403, 70, 28, 'Çünkü Rablerinin azabına güven olmaz.'),
(5404, 70, 29, 'Irzlarını korurlar.'),
(5405, 70, 30, 'Yalnız eşlerine, ya da ellerinin altında bulunan(cariyelerin)e karşı (korumazlar. Bundan ötürü de) onlar kınanmazlar.'),
(5406, 70, 31, 'Ama kim bundan ötesini ararsa, onlar (sınırı) aşanlardır.'),
(5407, 70, 32, 'Emanetlerini ve ahidlerini gözetirler.'),
(5408, 70, 33, 'Şahidliklerini yaparlar.'),
(5409, 70, 34, 'Namazlarını korurlar.'),
(5410, 70, 35, 'İşte onlar cennetlerde ağırlanırlar.'),
(5411, 70, 36, 'Nankörlere ne oluyur ki sana doğru koşuyorlar?'),
(5412, 70, 37, 'Sağdan, soldan, ayrı ayrı gruplar halinde (gelip etrafını sarıyorlar)?'),
(5413, 70, 38, 'Onlardan her biri, ni\'met cennetine sokulacağını mı umuyor?'),
(5414, 70, 39, 'Hayır! Öyle şey yok! Biz onları bildikleri şeyden yarattık.'),
(5415, 70, 40, 'Yoo, doğuların ve batıların Rabbine yemin ederim ki bizim gücümüz yeter:'),
(5416, 70, 41, 'Onları, kendilerinden daha hayırlı olanlarla değiştirmeğe. Bizim önümüze geçilmez (bize engel olunamaz).'),
(5417, 70, 42, 'Bırak onları kendilerine va\'dedilen günlerine kavuşuncaya kadar dalsın, oynasınlar.'),
(5418, 70, 43, 'O gün kabirlerden hızlı hızlı çıkarlar. Onlar dikilen(putlara yahut hedef)lere doğru koşar gibi (koşarlar).'),
(5419, 70, 44, 'Gözleri düşük, yüzlerini alçaklık bürümüş bir durumda. İşte onlara va\'dedilen gün, bugündür.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Biz Nuh\'u kavmine gönderdik: "Onlara acı bir azab gelmezden önce kavmini uyar," diye.'),
(5421, 71, 2, 'Ey kavmim, dedi, ben sizin için açık bir uyarıcıyım.'),
(5422, 71, 3, 'Allah\'a kulluk edin, O\'ndan korkun, bana da ita\'at edin.'),
(5423, 71, 4, 'Ki (Allah) günahlarınızdan bir kısmını bağışlasın ve sizi belli bir süreye kadar ertelesin. Zira Allah\'ın süresi geldiği zaman ertelenmez. Bilir(kişiler) olsaydınız (bunu anlardınız).'),
(5424, 71, 5, '(Nuh:) "Rabbim, dedi, ben kavmimi gece gündüz da\'vet ettim."'),
(5425, 71, 6, 'Benim da\'vetim, onlara kaçışlarını artırmaktan başka bir katkıda bulunmadı.'),
(5426, 71, 7, 'Günahlarını bağışlaman için onları (sana) ne kadar da\'vet ettimse parmaklarını kulaklarına tıkadılar, örtülerini başlarına çektiler, direttiler, çok böbürlendiler.'),
(5427, 71, 8, 'Sonra ben onları açıkça da\'vet ettim.'),
(5428, 71, 9, 'Sonra onlara açıktan söyledim, gizli gizli söyledim: 	And lo! I have made public proclamation unto them, and I have appealed to them in private.'),
(5429, 71, 10, '\'Rabbinizden mağfiret dileyin, çünkü O çok bağışlayandır\' dedim."'),
(5430, 71, 11, '\'(O\'ndan mağfiret dileyin) Ki üzerinize gökten bol yağmur göndersin\''),
(5431, 71, 12, '\'Ve size mallarla, oğullarla yardım etsin, size bahçeler versin, ırmaklar versin\''),
(5432, 71, 13, 'Size ne oluyor ki, Allah için saygı ummuyorsunuz?\''),
(5433, 71, 14, '\'Oysa O, sizi aşama, aşama yarattı.\''),
(5434, 71, 15, 'Görmediniz mi Allah nasıl yedi göğü birbiri üstünde tabaka tabaka yarattı?\''),
(5435, 71, 16, '\'Ve Ayı bunların içinde nur yaptı. Güneşi de bir lamba yaptı.\''),
(5436, 71, 17, 'Allah sizi yerden bir bitki olarak bitirdi.\''),
(5437, 71, 18, '\'Sonra yine oraya geri çevirecek ve tekrar çıkaracaktır.\''),
(5438, 71, 19, 'Allah, yeri sizin için bir sergi yaptı.\''),
(5439, 71, 20, '\'Ki onda açılan geniş geniş yollarda gidesiniz\'."'),
(5440, 71, 21, '(Bu öğütlerin hiçbirinin fayda vermediğini gören) Nuh, (Rabbine dönerek): "Rabbim, dedi, onlar bana karşı geldiler de malı ve çocuğu kendisinin ziyanını artırmaktan başka işe yaramayan (şımarık, gururlu) bir adama uydular."'),
(5441, 71, 22, 'Büyük büyük tuzaklar kurdular.'),
(5442, 71, 23, 'Dediler ki: "Tanrılarınızı bırakmayın: Vedd\'i, Suva\'ı, Yeğus\'u, Ye\'uk\'u ve Nesr\'i bırakmayın!"'),
(5443, 71, 24, '(Böylece) Onlar, çok kimseyi yoldan çıkardılar. Sen de o zalimlere şaşkınlıktan başka bir şey artırma.'),
(5444, 71, 25, 'Hatalarından dolayı boğuldular, ateşe sokuldular, kendilerine Allah\'tan başka yardımcılar da bulamadılar.'),
(5445, 71, 26, 'Nuh dedi ki: "Rabbim, yeryüzünde kafirlerden tek kişi bırakma."'),
(5446, 71, 27, 'Çünkü sen onları bırakırsan, kullarını şaşırtırlar ve sadece ahlaksız, nankör (insanlar) doğururlar.'),
(5447, 71, 28, 'Rabbim beni, babamı-anamı, inanarak evime gireni, inanan erkek ve kadınları bağışla; zalimlerin de sadece helakini artır (onların köklerini kurut)."');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'De ki: Cinlerden bir topluluğun Kur\'an dinleyip şöyle dedikleri bana vahyolundu: "Biz harikulade güzel bir Kur\'an dinledik.'),
(5449, 72, 2, 'Doğru yola iletiyor, ona inandık. Artık Rabbimize hiç kimseyi ortak koşmayacağız.'),
(5450, 72, 3, 'Doğrusu Rabbimizin şanı yücedir. O, eş ve çocuk edinmemiştir.'),
(5451, 72, 4, 'Meğer bizim beyinsiz (İblis veya cinlerin kafirleri) Allah hakkında saçma şeyler söylüyormuş.'),
(5452, 72, 5, 'Biz insanların ve cinlerin, Allah\'a karşı yalan söylemeyeceklerini sanmıştık (onun için o beyinsizin sözüne uymuştuk),'),
(5453, 72, 6, 'Doğrusu insanlardan bazı erkekler, cinlerden bazı erkeklere sığınırlardı da onların şımarıklığını artırırlardı.'),
(5454, 72, 7, 'Onlar da sizin sandığınız gibi Allah\'ın hiç kimseyi diriltmeyeceğini sanmışlardı.'),
(5455, 72, 8, 'Biz göğe dokunduk, onu kuvvetli bekçilerle ve ışınlarla doldurulmuş bulduk.'),
(5456, 72, 9, 'Ve biz onun dinlemeğe mahsus olan oturma yerlerinde oturur(gayb haberlerini dinlemeğe çalışır)dık. Artık şimdi kim dinlemek istese, kendisini gözetleyen bir ışın bulur.'),
(5457, 72, 10, 'Bilmiyoruz bununla yeryüzündekilere kötülük mü (yapılmak) istendi, yoksa Rabbleri onları doğruya mı iletmek diledi.'),
(5458, 72, 11, 'Bize gelince, bizden iyiler de var ve bizden başka türlü olan da var. Biz çeşitli yollara ayrıldık.'),
(5459, 72, 12, 'Biz yeryüzünde Allah\'ı aciz bırakamayacağımızı (yerden) kaçmakla da O\'nu aciz bırak(ıp O\'ndan kurtul)amayacağımızı anladık.'),
(5460, 72, 13, 'Biz, yol gösteren (Kur\'an)ı işitince ona inandık. Kim Rabbine inanırsa (ne hakkının) eksik verilmesinden, ne de kendisine kötülük edilmesinden korkar.'),
(5461, 72, 14, 'Ve biz, bizden müslümanlar da var ve bizden doğru yoldan sapanlar da var. Kimler müslüman olursa işte onlar doğru yolu aramışlardır.'),
(5462, 72, 15, 'Hak yoldan sapanlar ise cehenneme odun olmuşlardır."'),
(5463, 72, 16, 'Şayet yolda doğru gitselerdi onlara bol su verirdik (rızıklarını bollaştırırdık).'),
(5464, 72, 17, 'Ki onları, onunla sınayalım. Kim Rabbini anmaktan yüz çevirirse (Rabbi) onu, alt eden bir azaba sokar.'),
(5465, 72, 18, 'Mescidler, Allah\'a mahsustur. Allah ile beraber hiç kimseye yalvarmayın.'),
(5466, 72, 19, 'Allah\'ın kulu kalkıp O\'na yalvarınca (hayretten, hepsi) onun üzerine üşüşüp nerdeyse keçe gibi birbirlerine geçeceklerdi.'),
(5467, 72, 20, 'De ki: "Ben ancak Rabbime yalvarırım ve hiç kimseyi O\'na ortak koşmam."'),
(5468, 72, 21, 'De ki: "Ben size ne zarar, ne de akıl verme gücüne sahip değilim."'),
(5469, 72, 22, 'De ki: "Beni Allah\'(ın azabın)dan hiç kimse kurtaramaz ve ondan başka sığınacak kimse bulamam."'),
(5470, 72, 23, 'Benim yapabileceğim sadece Allah\'tan (bana vahyedilenleri) size duyurmak ve O\'nun elçilik görevlerini yerine getirmektir. Artık kim Allah\'a ve Elçisine baş kaldırırsa, ona içinde sürekli kalacağı cehennem ateşi vardır.'),
(5471, 72, 24, 'Kendilerine va\'dedilen şeyi (ya azabı veya kıyamet sa\'atini) gördükleri zaman, kimin yardımcı bakımından daha zayıf ve sayıca daha az olduğunu bileceklerdir.'),
(5472, 72, 25, 'De ki: "Size söylenen şey yakın mıdır, yoksa Rabbim onun için uzun bir süre mi koyacaktır, bilmem."'),
(5473, 72, 26, 'Gaybı bilen O\'dur. Gizli bilgisini kimseye göstermez.'),
(5474, 72, 27, 'Ancak razı olduğu elçiye gösterir. Çünkü O, elçisinin önüne ve arkasına gözetleyiciler (koruyucular) koyar.'),
(5475, 72, 28, '(Böyle yapar) Ki onların, Rablerinin kendilerine verdiği mesajları duyurduklarını bilsin. Allah, onlarda bulunan herşeyi (bilgisiyle) kuşatmıştır ve herşeyi bir bir saymış(hesabetmiş)tir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Ey örtüsüne bürünen,'),
(5477, 73, 2, 'Geceleyin kalk (namaz kıl); yalnız gecenin birazında (uyu).'),
(5478, 73, 3, 'Gecenin yarısında (kalk) yahut bundan biraz eksilt.'),
(5479, 73, 4, 'Veya bunu artır ve ağır ağır Kur\'an oku.'),
(5480, 73, 5, 'Doğrusu biz, senin üzerine ağır bir söz bırakacağız.'),
(5481, 73, 6, 'Gerçekten gece kalk(ıp ibadet et)mek daha oturaklı ve (geceleyin) söz (du\'a) daha etkilidir.'),
(5482, 73, 7, 'Çünkü gündüz, senin uzun süre uğraşacağın şeyler vardır.'),
(5483, 73, 8, 'Rabbinin adını an ve bütün gönlünle O\'na yönel.'),
(5484, 73, 9, '(O) Doğunun ve batının Rabbidir. O\'ndan başka tanrı yoktur. Yalnız O\'nu vekil tut.'),
(5485, 73, 10, 'Onların dediklerine sabret ve güzelce onlardan ayrıl.'),
(5486, 73, 11, 'Beni ve o ni\'met sahibi yalanlayıcıları yalnız bırak ve onlara biraz mühlet ver.'),
(5487, 73, 12, 'Doğrusu, bizim yanımızda bukağılar ve cehennem var.'),
(5488, 73, 13, '(Dikenli) Boğazı tırmalayan bir yiyecek ve acı veren bir azab var.'),
(5489, 73, 14, 'O gün yer ve dağlar sarsılır ve dağlar, dağılan kum yığınları olur.'),
(5490, 73, 15, '(Ey insanlar,) Doğrusu biz size, aleyhinize tanıklık edecek bir elçi gönderdik; nasıl ki Fir\'avn\'a da bir elçi göndermiştik.'),
(5491, 73, 16, 'Fir\'avn, elçiye karşı geldi. Biz de onu ağır bir yakalayışla yakaladık.'),
(5492, 73, 17, 'Peki inkar ederseniz, çocukları ihtiyarlatan o günden kendinizi nasıl kurtaracaksınız?'),
(5493, 73, 18, 'Gök (bile) onun dehşetinden yarılır. Allah\'ın va\'di mutlaka yapılmıştır.'),
(5494, 73, 19, 'Bu bir öğüttür. Dileyen, Rabbine varan bir yol tutar.'),
(5495, 73, 20, 'Rabbin senin gecenin üçte ikisinden daha azında, yarısında ve üçte birinde kalktığını; Seninle beraber bulunanlardan bir topluluğun da böyle yaptığını biliyor. Geceyi ve gündüzü takdir eden Allah, sizin onu sayamayacağınızı (zamanı hesabedip gecenin belli sa\'atlerinde kalkamayacağınızı) bildiği için sizi affetti. Artık (belli bir sa\'at gözetmeden) Kur\'an\'dan kolayınıza geleni okuyun (ne miktar kolayınıza gelirse o kadar gece namazı kılın, kendinizi zorlamayın.) Allah, içinizden hastalar, yeryüzünde gezip Allah\'ın lutfunu arayan başka kimseler ve Allah yolunda savaşan daha başka insanlar bulunacağını bilmiştir. Onun için Kur\'an\'dan kolayınıza geldiği kadar okuyun. Namazı kılın, zekatı verin ve Allah\'a güzel bir borç verin. Kendiniz için verdiğiniz hayırları, Allah katında verdiğinizden daha hayırlı ve mükafatça daha büyük bulacaksınız. Allah\'tan mağfiret dileyin. Şüphesiz Allah, çok bağışlayan, çok esirgeyendir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Ey örtüsüne bürünen,'),
(5497, 74, 2, 'Kalk, uyar.'),
(5498, 74, 3, 'Rabbini tekbir et (O\'nun büyüklüğünü an),'),
(5499, 74, 4, 'Elbiseni temizle,'),
(5500, 74, 5, 'Pislikten kaçın.'),
(5501, 74, 6, 'Verdiğini çok bularak başa kakma.'),
(5502, 74, 7, 'Rabbin için sabret.'),
(5503, 74, 8, 'Sur\'a üflendiği zaman'),
(5504, 74, 9, 'İşte o gün, çetin bir gündür!'),
(5505, 74, 10, 'Kafirler için kolay değildir.'),
(5506, 74, 11, 'Benimle şu adamı yalnız bırak ki ben onu tek olarak yarattım.'),
(5507, 74, 12, 'Ona uzun boylu mal verdim.'),
(5508, 74, 13, 'Göz önünde oğullar (verdim).'),
(5509, 74, 14, 'Kendisine bir döşeyiş döşedim.'),
(5510, 74, 15, 'Hala daha da artırmama göz dikiyor.'),
(5511, 74, 16, 'Hayır, çünkü o bizim ayetlerimize karşı bir inatçı kesildi.'),
(5512, 74, 17, 'Onu dimdik bir yokuşa sardıracağım.'),
(5513, 74, 18, 'Zira o düşündü, ölçtü, biçti.'),
(5514, 74, 19, 'Kahrolası nasıl da ölçtü, biçti.'),
(5515, 74, 20, 'Yine kahrolası nasıl ölçtü, biçti.'),
(5516, 74, 21, 'Sonra baktı,'),
(5517, 74, 22, 'Sonra surat astı, kaşlarını çattı,'),
(5518, 74, 23, 'Sonra arkasını döndü, böbürlendi:'),
(5519, 74, 24, 'Bu dedi, rivayet edilip öğretilen bir büyüden başka bir şey değildir.'),
(5520, 74, 25, 'Bu, sadece, bir insan sözüdür.'),
(5521, 74, 26, 'Onu Sekar\'a sokacağım.'),
(5522, 74, 27, 'Sekar\'ın ne olduğunu sen nereden bileceksin?'),
(5523, 74, 28, '(Geride bir şey) Komaz, bırakmaz (her şeyi yakıp yok eder).'),
(5524, 74, 29, 'Durmadan deriler kavurur.'),
(5525, 74, 30, 'Üzerinde ondokuz (muhafız) vardır.'),
(5526, 74, 31, 'Biz cehennemin muhafızlarını hep melekler yaptık. Onların sayısını da inkar edenler için bir sınav yaptık ki, kendilerine Kitap verilmiş olanlar iyice inansın, inananların da imanı artsın. Kitap verilmiş olanlar ve inananlar kuşkulanmasınlar. Kalblerinde hastalık bulunanlar ve kafirler de: "Allah bu misalle ne demek istedi?" desinler. Böylece Allah, dilediğini şaşırtır, dilediğni doğru yola iletir. Rabbinin ordularını ancak kendisi bilir. Bu, insanlara bir uyarıdır.'),
(5527, 74, 32, 'Hayır, andolsun Aya,'),
(5528, 74, 33, 'Dönüp gitmekte olan geceye,'),
(5529, 74, 34, 'Ağaran sabaha,'),
(5530, 74, 35, 'Ki o (Sekar), büyük(bela)lardan biridir.'),
(5531, 74, 36, 'İnsanlar için uyarıcıdır;'),
(5532, 74, 37, 'Sizden (iman yolunda) ileri gitmek veya geri kalmak dileyen kimseler için (uyarıcıdır).'),
(5533, 74, 38, 'Her can, kazandığıyle (Allah katında) rehin alınmıştır.'),
(5534, 74, 39, 'Yalnız sağın adamları (Kitapları sağdan verilenler) hariç.'),
(5535, 74, 40, 'Onlar cennetler içinde soruyorlar;'),
(5536, 74, 41, 'Suçluların durumunu:'),
(5537, 74, 42, 'Sizi şu yakıcı ateşe ne sürükledi?'),
(5538, 74, 43, '(Onlar da) Dediler ki: "Biz namaz kılanlardan olmadık."'),
(5539, 74, 44, 'Yoksula da yedirmezdik.'),
(5540, 74, 45, 'Boş şeylere dalanlarla birlikte dalardık."'),
(5541, 74, 46, 'Ceza gününü yalanlardık.'),
(5542, 74, 47, 'İşte böyle iken ölüm bize gelip çattı.'),
(5543, 74, 48, 'Artık onlara şefa\'atçilerin şefa\'ati fayda vermez.'),
(5544, 74, 49, 'Böyle iken onlara ne oluyur ki öğütten yüz çeviriyorlar?'),
(5545, 74, 50, 'Yaban eşekleri gibi;'),
(5546, 74, 51, 'Aslandan ürkmüş.'),
(5547, 74, 52, 'Hayır, onlardan her kişi kendisine açılan sahifeler verilmesini istiyor.'),
(5548, 74, 53, 'Yok yok onlar ahiretten korkmuyorlar.'),
(5549, 74, 54, 'Hayır (iyi bilsinler ki) o (Kur\'an) bir ikazdır.'),
(5550, 74, 55, 'Dileyen onu düşünür, öğüt alır.'),
(5551, 74, 56, 'Allah dilemedikçe onlar öğüt almazlar. Takva ve mağfiret ehli O\'dur (kendisinden korunmağa, cezasından kaçınmağa layık olan ve günahları bağışlayan yalnız O\'dur).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Yoo, kıyamet gününe and içerim,'),
(5553, 75, 2, 'Yoo, daima, kendini kınayan nefse and içerim.'),
(5554, 75, 3, 'İnsan kendisinin kemiklerini bir araya toplamayacağımızı mı sanıyor?'),
(5555, 75, 4, 'Evet, toplarız, onun parmak uçlarnı düzenlemeğe gücümüz yeter.'),
(5556, 75, 5, 'Fakat insan, devamlı suç işleyerek ilerisini berbadetmek ister.'),
(5557, 75, 6, 'Kıyamet günü nerede? diye sorup durur.'),
(5558, 75, 7, 'Ama göz (güneş gibi ortaya çıkan gerçeğin karşısında) kamaştığı,'),
(5559, 75, 8, 'Ay tutulduğu,'),
(5560, 75, 9, 'Güneş ve Ay bir araya toplandığı zaman!'),
(5561, 75, 10, '(Evet) O gün insan: "Kaçacak yer neresi?" der.'),
(5562, 75, 11, 'Hayır, sığınacak yer yoktur.'),
(5563, 75, 12, 'O gün varıp durulacak yer, ancak Rabbinin huzurudur (ey insan).'),
(5564, 75, 13, '(O zaman) İnsanın yapıp öne sürdüğü, (yapmayıp) geri bıraktığı herşey kendisine haber verilir.'),
(5565, 75, 14, 'Doğrusu insan kendi nefsini görür,'),
(5566, 75, 15, 'Birtakım özürler ortaya atsa da.'),
(5567, 75, 16, '(Ey Muhammed,) Onu hemen okumak için diline depretme.'),
(5568, 75, 17, 'Onu (senin kalbinde) toplamak ve (sana) okumak bize düşer.'),
(5569, 75, 18, 'O halde sana Kur\'an\'ı okuduğumuz zaman onun okunuşunu izle.'),
(5570, 75, 19, 'Sonra onu açıklamak da bize düşer.'),
(5571, 75, 20, 'Hayır, siz çabuk(geçen şu dünyay)ı seviyorsunuz da,'),
(5572, 75, 21, 'Ahireti bırakıyorsunuz.'),
(5573, 75, 22, 'Yüzler var ki o gün ışıl ışıl parlar,'),
(5574, 75, 23, 'Rabbine bakar.'),
(5575, 75, 24, 'Yüzler de var ki o gün asıktır.'),
(5576, 75, 25, 'Kendisine bel kemiklerini kıran(bela)nın yapılacağını anlar.'),
(5577, 75, 26, 'Hayır, ne zaman ki can, köprücük kemiklerine dayanır,'),
(5578, 75, 27, 'Ve (başında bulunanlar tarafından): "Kim afsun yapar acaba? denir,'),
(5579, 75, 28, 'Ve kendisi artık bunun, ayrılık zamanı olduğunu anlar,'),
(5580, 75, 29, 'Ve bacak bacağa dolaşır.'),
(5581, 75, 30, 'İşte o gün, sevk Rabbinedir (can, Allah\'ın huzuruna sevk edilir).'),
(5582, 75, 31, 'Ne sadaka verdi, ne de namaz kıldı.'),
(5583, 75, 32, 'Fakat yalanladı, döndü.'),
(5584, 75, 33, 'Sonra çalım satarak ailesine gitti.'),
(5585, 75, 34, 'Yazık sana yazık!'),
(5586, 75, 35, 'Yine yazık sana yazık!'),
(5587, 75, 36, 'İnsan, başı boş bırakılacağını mı sanır?'),
(5588, 75, 37, 'Kendisi dökülen meniden bir nutfe (sperm) değil miydi?'),
(5589, 75, 38, 'Sonra alaka (rahme asılan embriyo) oldu da (Rabbi onu) yarattı, düzenledi.'),
(5590, 75, 39, 'O(meni)den iki çifti: Erkeği ve dişiyi var etti.'),
(5591, 75, 40, 'Şimdi bun(ları yapan Allah)ın ölüleri diriltmeğe gücü yetmez mi?');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'İnsanın üzerinden, henüz kendisinin anılan bir şey olmadığı uzun bir süre geçmedi mi?'),
(5593, 76, 2, 'Doğrusu biz insanı, imtihan etmek için karışık bir nutfeden yarattık da onu işitici, görücü yaptık.'),
(5594, 76, 3, 'Biz ona yolu gösterdik: Ya şükredici veya nankör olur.'),
(5595, 76, 4, 'Biz, kafirler için zincirler, demir halkalar ve alevli bir ateş hazırlamışızdır.'),
(5596, 76, 5, 'İyiler de, karışımı kafur olan bir kadehten içerler.'),
(5597, 76, 6, 'Bir kaynak ki Allah\'ın kulları ondan içerler, (istedikleri yere de) fışkırtarak akıtırlar.'),
(5598, 76, 7, 'Adaklarını yerine getirirler ve şerri salgın olan bir günden korkarlar.'),
(5599, 76, 8, 'Yosula, yetime ve esire sevdikleri yemeği yedirirler:'),
(5600, 76, 9, 'Biz size sırf Allah rızası için yediriyoruz, sizden bir karşılık ve teşekkür beklemiyoruz.'),
(5601, 76, 10, 'Çünkü biz suratsız, çok katı bir gün(ün azabın)dan ötürü Rabbimizden korkarız. (derler).'),
(5602, 76, 11, 'Allah da onları, o günün şerrinden korumuş, onlar(ın yüzlerin)e parlaklık ve (gönüllerine) sevinç vermiştir.'),
(5603, 76, 12, 'Sabrettiklerinden dolayı onları cennet ve ipekle ödüllendirmiştir!'),
(5604, 76, 13, 'Orada divanlar üzerinde yastıklara dayanırlar. Orada ne (yakıcı) güneş görürler, ne de dondurucu soğuk.'),
(5605, 76, 14, 'Cennetin gölgeleri, üzerlerine yaklaşmış, meyvaları da aşağı eğdirildikçe eğdirilmiştir.'),
(5606, 76, 15, 'Yanlarında gümüş kablar, billur kupalar dolaştırılır.'),
(5607, 76, 16, 'Öyle gümüş kadehler ki onları istedikleri ölçüde takdir etmişlerdir (istedikleri kadar içki alırlar).'),
(5608, 76, 17, 'Onlara orada, karışımı zencefil olan kadehten içirilir.'),
(5609, 76, 18, 'Bir çeşme ki adına Selsebil denir.'),
(5610, 76, 19, 'Çevrelerinde de (öyle) ölümsüz gençler dolaşır ki, onları görsen, kendilerini saçılmış inci sanırsın.'),
(5611, 76, 20, 'Orada nereye baksan, bir ni\'met ve büyük bir mülk görürsün.'),
(5612, 76, 21, '(Cennet ehlinin) Üstlerinde yeşil ipekten ince ve kalın giysiler var. Gümüş bilezikler takınmışlardır. Rableri onlara tertemiz bir içki içirmiş(ve şöyle demiş)tir:'),
(5613, 76, 22, 'Bu, sizin ödülünüzdür. Çalışmanızın karşılığı verilmiştir!'),
(5614, 76, 23, 'Muhakkak Biziz, Biz ki sana Kur\'an\'ı parça parça indirdik.'),
(5615, 76, 24, 'O halde Rabbinin hükmüne sabret ve onlardan hiçbir günahkara, yahut nanköre ita\'at etme.'),
(5616, 76, 25, 'Sabah akşam Rabbinin adını an.'),
(5617, 76, 26, 'Gecenin bir bölümünde O\'na secde et ve geceleyin uzun zaman O\'nu tesbih eyle (şanının yüceliğini an)!'),
(5618, 76, 27, 'Bunlar, şu çabuk(geçen dünyay)ı seviyorlar da ötelerindeki ağır bir günü bırakıyorlar.'),
(5619, 76, 28, 'Biz onları yarattık, yapılarını sıkıca bağladık. Dilediğimiz zaman onları benzerleriyle değiştiririz.'),
(5620, 76, 29, 'Bu bir öğüttür. Dileyen, Rabbine varan yolu tutar.'),
(5621, 76, 30, 'Allah dilemedikçe siz dileyemezsiniz. Şüphesiz Allah bilendir, hüküm ve hikmet sahibidir.'),
(5622, 76, 31, 'Dilediğini rahmetine sokar. Zalimlere gelince, onlar için acı bir azab hazırlamıştır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Andolsun; birbiri ardınca gönderilenlere,'),
(5624, 77, 2, 'Esip savuranlara,'),
(5625, 77, 3, 'Yaydıkça yayanlara,'),
(5626, 77, 4, 'Ayırdıkça ayıranlara,'),
(5627, 77, 5, 'Öğüt bırakanlara:'),
(5628, 77, 6, 'Özür yahut uyarmak için.'),
(5629, 77, 7, '(Bunlara andolsun) Ki size va\'dedilen, mutlaka olacaktır.'),
(5630, 77, 8, 'Yıldızlar(ın ışığı) silindiği zaman,'),
(5631, 77, 9, 'Gök yarıldığı zaman,'),
(5632, 77, 10, 'Dağlar ufalanıp savrulduğu zaman,'),
(5633, 77, 11, 'Elçilere vakit belirlendiği zaman:'),
(5634, 77, 12, 'Ertelenmiş oldukları gün için,'),
(5635, 77, 13, 'Yani hüküm günü için.'),
(5636, 77, 14, 'Hüküm gününün ne olduğunu sen nereden bileceksin?'),
(5637, 77, 15, 'Yalanlayanların vay haline o gün!'),
(5638, 77, 16, 'Öncekileri helak etmedik mi?'),
(5639, 77, 17, 'Sonra geridekileri de onların ardına takarız.'),
(5640, 77, 18, 'Suçlulara böyle yaparız.'),
(5641, 77, 19, '(Hakkı) yalanlayanların vay haline o gün!'),
(5642, 77, 20, 'Sizi adi bir sudan yaratmadık mı?'),
(5643, 77, 21, 'Onu sağlam bir karar yerine koyduk.'),
(5644, 77, 22, 'Belli bir süreye kadar.'),
(5645, 77, 23, 'Biçimlendirdik. Ne güzel biçim vereniz Biz.'),
(5646, 77, 24, 'Yalanlayanların vay haline o gün!'),
(5647, 77, 25, 'Arz\'ı toplanma yeri yapmadık mı?;'),
(5648, 77, 26, 'Diriler ve ölüler için.'),
(5649, 77, 27, 'Orada yüksek yüksek dağlar meydana getirmedik mi? Ve size tatlı su(lar) içirmedik mi?'),
(5650, 77, 28, 'Yalanlayanların vay haline o gün!'),
(5651, 77, 29, 'Haydi yalanladığınız(azab)a gidin!'),
(5652, 77, 30, 'Üç dallı bir gölgeye gidin."'),
(5653, 77, 31, 'Ki ne gölgelendirir, ne de alevden korur.'),
(5654, 77, 32, 'O, kütük gibi kıvılcım(lar) saçar.'),
(5655, 77, 33, '(Saçtığı) kıvılcım, sanki sarı bir halattır.'),
(5656, 77, 34, 'Yalanlayanların vay haline o gün!'),
(5657, 77, 35, 'Bu, konuşamayacakları gündür.'),
(5658, 77, 36, 'Kendilerine izin de verilmez ki özür dilesinler.'),
(5659, 77, 37, 'Yalanlayanların vay haline o gün!'),
(5660, 77, 38, 'İşte bu, hüküm günüdür. Sizi ve öncekileri bir araya topladık.'),
(5661, 77, 39, 'Eğer (kurtulmak için yapacağınız) bir hileniz varsa bana hile yapın (da beni atlatın).'),
(5662, 77, 40, 'Yalanlayanların vay haline o gün! *'),
(5663, 77, 41, 'Korunanlar ise gölgeler altında, çeşme başındadırlar.'),
(5664, 77, 42, 'Gönüllerinin çektiği meyvalar içindedirler.'),
(5665, 77, 43, 'Yaptıklarınıza karşılık afiyetle yeyin, için!'),
(5666, 77, 44, 'Biz, güzel davrananları böyle mükafatlandırırız.'),
(5667, 77, 45, 'Yalanlayanların vay haline o gün!'),
(5668, 77, 46, 'Yeyin, azıcık sefa sürün, siz suçlularsınız!'),
(5669, 77, 47, 'Yalanlayanların vay haline o gün!'),
(5670, 77, 48, 'Onlara: "Rüku\' edin" dendiği zaman rüku\' etmezler.'),
(5671, 77, 49, 'Yalanlayanların vay haline o gün!'),
(5672, 77, 50, 'Onlar bun(a inanmadık)dan sonra hangi hadise (söze) inanacaklar?');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Birbirlerine hangi şeyden soruyorlar?'),
(5674, 78, 2, 'O büyük haberden mi?'),
(5675, 78, 3, 'Ki onlar onda ayrılığa düşmektedirler.'),
(5676, 78, 4, 'Hayır (dedikleri gibi değil), yakında bilecekler.'),
(5677, 78, 5, 'Sonra hayır (dedikleri gibi değil), yakında bilecekler.'),
(5678, 78, 6, 'Yapmadık mı biz, Arzı bir beşik,'),
(5679, 78, 7, 'Dağları birer kazık?'),
(5680, 78, 8, 'Ve sizi çift çift yarattık.'),
(5681, 78, 9, 'Uykunuzu dinlenme yaptık.'),
(5682, 78, 10, 'Geceyi (sizi sarıp örten) bir giysi yaptık.'),
(5683, 78, 11, 'Gündüzü de geçim zamanı yaptık.'),
(5684, 78, 12, 'Üstünüzde yedi sağlam (gök) bina ettik.'),
(5685, 78, 13, 'Ve (orada) parıl parıl parlayan bir lamba yarattık.'),
(5686, 78, 14, 'Sıkışan(bulut)lardan şarıl şarıl su indirdik,'),
(5687, 78, 15, 'Ki onunla çıkaralım: Dane(ler), bitki(ler),'),
(5688, 78, 16, 'Ve (ağaçları) birbirine sarmaş dolaş bahçeler.'),
(5689, 78, 17, 'Muhakkak ki (haklının, haksızın ayırdedileceği) hüküm günü, belirlenmiş bir vakittir.'),
(5690, 78, 18, 'O gün Sur\'a üflenir, bölük bölük gelirsiniz.'),
(5691, 78, 19, 'Gök açılmış, kapı kapı olmuştur.'),
(5692, 78, 20, 'Dağlar yürütülmüş, bir serab olmuştur.'),
(5693, 78, 21, 'Cehennem de gözetleme yeri olmuş(suçluları gözetleyip durmakta)dır.'),
(5694, 78, 22, 'Azgınların varacağı yerdir.'),
(5695, 78, 23, 'Orada çağlar boyu kalacalardır.'),
(5696, 78, 24, 'Orada ne bir serinlik, ne de içilecek bir şey tadarlar,'),
(5697, 78, 25, 'Yalnız kaynar su ve irin (içerler);'),
(5698, 78, 26, 'Yaptıklarına uygun bir ceza olarak.'),
(5699, 78, 27, 'Çünkü onlar bir hesap (görüleceğini) ummuyorlardı.'),
(5700, 78, 28, 'Ayetlerimizi de tamamen yalanlamışlardı.'),
(5701, 78, 29, 'Biz de her şeyi sayıp yazmıştık.'),
(5702, 78, 30, 'Şimdi tadın (yaptıklarınızın tadını), artık size azabdan başka bir şey artırmayacağız! 	So taste (of that which ye have earned). No increase do We give you save of torment.'),
(5703, 78, 31, 'Korunanlar için de başarı ödülü vardır.'),
(5704, 78, 32, 'Bahçeler, bağlar,'),
(5705, 78, 33, 'Göğüsleri tomurcuklanmış yaşıt kızlar.'),
(5706, 78, 34, 'Ve dolu kadeh(ler).'),
(5707, 78, 35, 'Orada ne boş söz ne de yalan işitirler;'),
(5708, 78, 36, 'Rabbinden bir karşılık, yeterli bir bağış olarak.'),
(5709, 78, 37, 'Göklerin, yerin ve ikisi arasında bulunanların Rabbi, çok merhametli (Rab). O\'nun (izni olmadan) huzurunda konuşamazlar.'),
(5710, 78, 38, 'O gün Ruh ve melekler, sıra sıra dururlar. Ancak Rahman\'ın izin verdiği konuşabilir, o da doğruyu söyler.'),
(5711, 78, 39, 'İşte bu, hak günüdür. Artık dileyen, Rabbine varan bir yol tutar.'),
(5712, 78, 40, 'Biz sizi yakın bir azab ile uyardık. O gün kişi, ellerinin (yapıp) öne sürdüğü işlere bakar ve kafir: "Keşke ben, toprak olsaydım!" der.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Andolsun söküp çıkaranlara,'),
(5714, 79, 2, 'Hemen çekip alanlara,'),
(5715, 79, 3, 'Yüzüp gidenlere,'),
(5716, 79, 4, 'Yarışıp, geçenlere,'),
(5717, 79, 5, 'Derken işi düzenleyenlere!'),
(5718, 79, 6, 'O gün o gürültü sarsar.'),
(5719, 79, 7, 'Ardından başka bir gürültü gelir.'),
(5720, 79, 8, 'O gün bazı yürekler çarpar.'),
(5721, 79, 9, 'Gözleri (korkudan) aşağı kayar.'),
(5722, 79, 10, 'Diyorlar ki: "Biz yine eski halimize döndürülecek miyiz?"'),
(5723, 79, 11, 'Biz çürümüş kemikler olduktan sonra ha?'),
(5724, 79, 12, 'Öyle ise bu, ziyanlı bir dönüştür! dediler.'),
(5725, 79, 13, 'O (olay zor değil) bir tek haykırış(a bakmakta)dır.'),
(5726, 79, 14, 'Hemen onlar uyanıklık alanındadırlar.'),
(5727, 79, 15, 'Musa\'nın haberi sana geldi mi?'),
(5728, 79, 16, 'Hani Rabbi ona Kutsal Vadi\'de, "Tuva"\'da ünlemişti:'),
(5729, 79, 17, 'Fir\'avn\'a git, çünkü o azdı.'),
(5730, 79, 18, 'De ki: Arınmağa gönlün var mı?'),
(5731, 79, 19, 'Seni Rabbin(in yolun)a ileteyim de O\'ndan korkasın.'),
(5732, 79, 20, 'Ona büyük mu\'cizeyi gösterdi.'),
(5733, 79, 21, 'Fakat o yalanladı, karşı geldi.'),
(5734, 79, 22, 'Sonra sırtını döndü; (Musa\'nın getirdiklerini iptal etmek için) çalışmağa koyuldu.'),
(5735, 79, 23, '(Adamlarını) Topladı, (onlara) bağırdı:'),
(5736, 79, 24, 'Ben sizin en yüce Rabbinizim! dedi.'),
(5737, 79, 25, 'Allah da onu, sonun ve ilkin (ahiretin ve dünyanın) azabıyle cezalandırdı.'),
(5738, 79, 26, 'Şüphesiz bunda (Allah\'tan) korkacak kimse için ibret vardır.'),
(5739, 79, 27, 'Yaratılışça siz mi daha çetinsiniz, yoksa gök mü? (Allah) onu yaptı.'),
(5740, 79, 28, 'Kalınlığını (tavanını) yükseltti, onu düzenledi.'),
(5741, 79, 29, 'Gecesini örtüp kararttı, kuşluğunu (güneşinin ışığını) açığa çıkardı.'),
(5742, 79, 30, 'Bundan sonra da yeri yayıp yuvarlattı.'),
(5743, 79, 31, 'Ondan suyunu ve otlağını çıkardı,'),
(5744, 79, 32, 'Dağları oturttu,'),
(5745, 79, 33, 'Sizin ve hayvanlarınızın geçimi için.'),
(5746, 79, 34, 'Herşeyi bastıran o büyük felaket geldiği zaman,'),
(5747, 79, 35, 'O gün insan, neyin peşinde koşmuş olduğunu hatırlar.'),
(5748, 79, 36, 'Gören kimseler için cehennem ortaya çıkarılmıştır.'),
(5749, 79, 37, 'Artık kim azmışsa,'),
(5750, 79, 38, 'Ve şu yakın hayatı yeğlemişse,'),
(5751, 79, 39, 'Onun barınağı cehennemdir.'),
(5752, 79, 40, 'Ama kim Rabbinin divanında dur(up hesap ver)mekten korkmuş ve nefsi(ni) kötü heves(ler) den men\'etmişse'),
(5753, 79, 41, 'Onun barınağı da cennettir.'),
(5754, 79, 42, 'Sana sa\'atden soruyorlar: Demir atması (gelip çatması) ne zaman diye.'),
(5755, 79, 43, 'Sen nerede, onun vaktini söylemek nerede?!'),
(5756, 79, 44, 'Onun bilgisi Rabbine aittir.'),
(5757, 79, 45, 'Sen ancak, ondan korkacak olanları uyarıcısın.'),
(5758, 79, 46, 'Onlar onu gördükleri zaman sanki (dünyada) bir akşam veya onun kuşluk vaktinden fazla kalmamış gibi olurlar.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Surat astı ve döndü;'),
(5760, 80, 2, 'Kör geldi diye.'),
(5761, 80, 3, 'Ne bilirsin belki o arınacak?'),
(5762, 80, 4, 'Yahut öğüt dinleyecek de öğüt, kendisine yarayacak.'),
(5763, 80, 5, 'Kendisini zengin görüp tenezzül etmeyene gelince;'),
(5764, 80, 6, 'Sen ona yöneliyorsun.'),
(5765, 80, 7, 'Onun arınmamasından sana ne?'),
(5766, 80, 8, 'Fakat koşarak sana gelen,'),
(5767, 80, 9, 'Saygılı olarak gelmişken,'),
(5768, 80, 10, 'Sen onunla ilgilenmiyorsun.'),
(5769, 80, 11, 'Hayır (olmaz böyle şey); o (ayetler), bir hatırlatmadır.'),
(5770, 80, 12, 'Dileyen onu düşünüp öğüt alır.'),
(5771, 80, 13, '(O öğüt) Sahifeler içindedir: Değer verilen,'),
(5772, 80, 14, 'Saygı ile yükseltilen, tertemiz (sayfalar)'),
(5773, 80, 15, 'Yazıcıların ellerinde:'),
(5774, 80, 16, 'Değerli, iyi (yazıcıların).'),
(5775, 80, 17, 'Kahrolası insan, ne kadar da nankördür!'),
(5776, 80, 18, '(Allah) Onu hangi şeyden yarattı?'),
(5777, 80, 19, 'Nutfe (sperm)den. Onu yarattı, ona biçim verdi.'),
(5778, 80, 20, 'Sonra ona yolu kolaylaştırdı.'),
(5779, 80, 21, 'Sonra onu öldürdü, kabre koydurdu.'),
(5780, 80, 22, 'Sonra dilediği zaman onu diriltip kaldırdı.'),
(5781, 80, 23, 'Hayır, insan, O\'nun kendisine emrettiğini yapmadı.'),
(5782, 80, 24, 'İnsan şu yiyeceğine baksın.'),
(5783, 80, 25, 'Biz suyu iyice döktük.'),
(5784, 80, 26, 'Sonra toprağı güzelce yardık da,'),
(5785, 80, 27, 'Orada bitirdik: Dane,'),
(5786, 80, 28, 'Üzüm, yonca,'),
(5787, 80, 29, 'Zeytin, hurma,'),
(5788, 80, 30, 'İri ve gür bahçeler,'),
(5789, 80, 31, 'Meyva ve çayır;'),
(5790, 80, 32, 'Sizin ve hayvanlarınızın geçimi için.'),
(5791, 80, 33, 'Çarpınca kulakları sağır eden o gürültü geldiği zaman,'),
(5792, 80, 34, 'İşte o gün kişi kaçar: kardeşinden,'),
(5793, 80, 35, 'Anasından, babasından,'),
(5794, 80, 36, 'Eşinden ve oğullarından.'),
(5795, 80, 37, 'O gün, onlardan her kişinin, kendisine yeter derecede işi vardır.'),
(5796, 80, 38, 'Yüzler var ki o gün parıl parıl,'),
(5797, 80, 39, 'Güleç, sevinçli.'),
(5798, 80, 40, 'Yüzler de var ki o gün tozlanmış.'),
(5799, 80, 41, 'Onları karanlık bürümüş (öylesine üzgün, öylesine dertli).'),
(5800, 80, 42, 'İşte onlar kafirler, Hak\'tan sapanlardır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Güneş büzüldüğü zaman,'),
(5802, 81, 2, 'Yıldızlar kararıp döküldüğü zaman,'),
(5803, 81, 3, 'Dağlar yürütüldüğü zaman,'),
(5804, 81, 4, 'On aylık gebe develer başı boş bırakıldığı zaman,'),
(5805, 81, 5, 'Vahşi hayvanlar bir araya toplandığı zaman,'),
(5806, 81, 6, 'Denizler kaynatıldığı zaman,'),
(5807, 81, 7, 'Nefisler çiftleştirildiği zaman.'),
(5808, 81, 8, 'Ve sorulduğu zaman o diri diri toprağa gömülen kıza:'),
(5809, 81, 9, 'Hangi günah(ı) yüzünden öldürüldü? diye.'),
(5810, 81, 10, '(Amel) defterler(i) açılıp yayıldığı zaman,'),
(5811, 81, 11, 'Gök sıyrılıp açıldığı zaman,'),
(5812, 81, 12, 'Cehennem alevlendirildiği zaman,'),
(5813, 81, 13, 'Cennet yaklaştırıldığı zaman,'),
(5814, 81, 14, 'Her can, ne yapıp getirdiğini bilir.'),
(5815, 81, 15, 'Yoo, yemin ederim o geri kalıp gizlenenlere;'),
(5816, 81, 16, 'Akıp gidenlere, dönüp saklananlara,'),
(5817, 81, 17, 'Sırtını dönen geceye,'),
(5818, 81, 18, 'Soluk almağa başlayan sabaha,'),
(5819, 81, 19, '(Andolsun bunlara) Ki o, değerli bir elçinin (Cebrail\'in) sözüdür.'),
(5820, 81, 20, '(O elçi,) Güçlüdür, Arşın sahibi (Allah) katında yücedir.'),
(5821, 81, 21, 'Orada (kendisine) ita\'at edilen, güvenilendir.'),
(5822, 81, 22, 'Arkadaşınız cinli değildir.'),
(5823, 81, 23, 'Andolsun (Muhammed) onu apaçık ufukta görmüştür.'),
(5824, 81, 24, 'O, gayb hakkında (verdiği haberlerden dolayı) suçlanamaz.'),
(5825, 81, 25, 'O (Kur\'an) kovulmuş şeytanın sözü değildir.'),
(5826, 81, 26, 'O halde nereye gidiyorsunuz?'),
(5827, 81, 27, 'O, alemlere öğüttür.'),
(5828, 81, 28, 'Aranızdan doğru hareket etmek isteyen için;'),
(5829, 81, 29, 'Alemlerin Rabbi Allah dilemedikçe siz dileyemezsiniz.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Gök yarıldığı zaman,'),
(5831, 82, 2, 'Yıldızlar saçıldığı zaman,'),
(5832, 82, 3, 'Denizler fışkırtıldığı zaman,'),
(5833, 82, 4, 'Kabirlerin içi dışına getirildiği zaman,'),
(5834, 82, 5, 'Her can, ne (yapıp) öne sürdüğünü ve ne (yapmayıp) geride bıraktığını bilir.'),
(5835, 82, 6, 'Ey insan, seni engin kerem sahibi Rabbine karşı ne aldatıp isyana sürükledi?'),
(5836, 82, 7, 'O (Rab) ki seni yarattı, seni düzenledi, sana ölçülü bir biçim verdi.'),
(5837, 82, 8, 'Seni(n organlarını) dilediği şekilde birbirine ekledi.'),
(5838, 82, 9, 'Hayır, (bu gururunuzun sebebi şudur) siz ceza (görme)yi yalanlıyorsunuz.'),
(5839, 82, 10, 'Oysa üzerinizde koruyucu(yaptıklarınızı zabtedici melek)ler vardır;'),
(5840, 82, 11, 'Değerli yazıcılar,'),
(5841, 82, 12, 'Yaptığınız herşeyi bilirler.'),
(5842, 82, 13, 'İyiler ni\'met içindedirler.'),
(5843, 82, 14, 'Kötüler de yakıcı ateş içindedirler.'),
(5844, 82, 15, 'Ceza günü oraya girerler.'),
(5845, 82, 16, 'Onlar ondan (hiçbir yere kaçıp) kaybolacak değillerdir.'),
(5846, 82, 17, 'Ceza gününün ne olduğunu sen nereden bileceksin?'),
(5847, 82, 18, 'Ve yine ceza gününün ne olduğunu sen nereden bileceksin?'),
(5848, 82, 19, 'O, kimsenin kimseye yardım edemeyeceği bir gündür! O gün buyruk, yalnız Allah\'ındır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Ölçü ve tartıda hile yapanların vay haline!'),
(5850, 83, 2, 'Onlar insanlardan bir şey ölçüp aldıkları zaman ölçüyü tam yaparlar.'),
(5851, 83, 3, 'Kendileri onlara bir şey ölçtükleri veya tarttıkları zaman (ölçü ve tartıyı) eksik yaparlar.'),
(5852, 83, 4, 'Onlar, tekrar diriltileceklerini sanmıyorlar mı?'),
(5853, 83, 5, 'Büyük bir gün için,'),
(5854, 83, 6, 'Ki o gün insanlar, alemlerin Rabbinin divanında dururlar.'),
(5855, 83, 7, 'Hayır, (ölçü ve tartıda hile yapılamaz), doğrusu sapanların yazıcısı Siccin(aşağı zindan)dadır.'),
(5856, 83, 8, 'Siccin\'in ne olduğunu sen nereden bileceksin?'),
(5857, 83, 9, 'Yazılmış bir Kitaptır.'),
(5858, 83, 10, 'Yalanlayanların vay haline o gün!'),
(5859, 83, 11, 'Onlar ceza gününü yalanlamaktadırlar.'),
(5860, 83, 12, 'Onu, saldırgan, günahkardan başkası yalanlamaz.'),
(5861, 83, 13, 'Ona ayetlerimiz okunduğu zaman: "Eskilerin masalları" der.'),
(5862, 83, 14, 'Hayır, doğrusu, onların işleyip kazandıkları şeyler, kalblerinin üzerine pas olmuştur.'),
(5863, 83, 15, 'Hayır, doğrusu onlar, o gün Rablerinden perdelenmişlerdir.'),
(5864, 83, 16, 'Sonra onlar, elbette cehenneme gireceklerdir.'),
(5865, 83, 17, 'Sonra da: "İşte yalanlamakta olduğunuz şey budur!" denilecektir.'),
(5866, 83, 18, 'Hayır, iyilerin yazısı İlliyyin(yüceler)dedir.'),
(5867, 83, 19, 'İlliyyin(yüceler)in ne olduğunu sen nereden bileceksin?'),
(5868, 83, 20, 'Yazılmış bir Kitaptır.'),
(5869, 83, 21, '(Allah\'a) Yaklaştırılmış olanlar, ona tanık olurlar.'),
(5870, 83, 22, 'İyiler elbette ni\'met içindedirler.'),
(5871, 83, 23, 'Divanlar üzerinde oturup bakarlar.'),
(5872, 83, 24, 'Yüzlerinde ni\'metin sevinç ve parıltısını sezersin.'),
(5873, 83, 25, 'Onlara, mühürlü, halis bir şaraptan içirilir,'),
(5874, 83, 26, 'Ki sonu misktir (içildikten sonra misk gibi kokar). İşte yarışanlar, bunun için yarışsınlar.'),
(5875, 83, 27, 'Karışımı tesnimdendir.'),
(5876, 83, 28, 'Bir çeşme ki (Allah\'a) yaklaştırılanlar ondan içerler.'),
(5877, 83, 29, 'Suç işleyenler, inananların üstüne gülerlerdi.'),
(5878, 83, 30, 'Onların yanından geçtikleri zaman birbirlerine kaş göz eder(ek onları küçümser)lerdi.'),
(5879, 83, 31, 'Ailelerine döndükleri zaman da (yaptıklarıyle övünüp) eğlenmeye başlarlardı.'),
(5880, 83, 32, 'İnananları gördüklerinde: "Şunlar sapık insanlar" derlerdi.'),
(5881, 83, 33, 'Oysa kendileri, onların üzerine bekçi gönderilmemişlerdi.'),
(5882, 83, 34, 'İşte bugün de inananlar kafirlerin üstüne gülerler.'),
(5883, 83, 35, 'Divanlar üzerinde (oturup) bakarlar:'),
(5884, 83, 36, 'Kafirler, yaptıklarıyle cezalandılar mı? diye.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Gök yarıldığı,'),
(5886, 84, 2, 'Kendisine yaraştığı üzere Rabbini(n buyruğunu) dinlediği zaman!'),
(5887, 84, 3, 'Yer uzatıl(ıp dümdüz yapıl)dığı,'),
(5888, 84, 4, 'İçindekileri dışarı atıp boşaldığı,'),
(5889, 84, 5, 'Ve kendisine yaraştığı üzere Rabbini(n buyruğunu) dinlediği zaman!'),
(5890, 84, 6, 'Ey insan, sen, Rabbine varan yolda çabalayıp durmaktasın, nihayet O\'na varacaksın.'),
(5891, 84, 7, '(O zaman) Kimin Kitabı sağından verilirse:'),
(5892, 84, 8, 'O, kolay bir hesaba çekilecek,'),
(5893, 84, 9, 'Ve sevinçli olarak ailesine dönecektir.'),
(5894, 84, 10, 'Kimin Kitabı arka tarafından verilirse.'),
(5895, 84, 11, 'O, ölümü çağıracak,'),
(5896, 84, 12, 'Ve alevli ateşe girecektir.'),
(5897, 84, 13, 'Çünkü o, (dünyada) ailesi arasında (şımarık ve) sevinçli idi.'),
(5898, 84, 14, 'O, hiç (Rabbine) dönmeyeceğini sanmıştı.'),
(5899, 84, 15, 'Hayır, Rabbi O\'nu görmekte idi.'),
(5900, 84, 16, 'Yoo, and içerim; akşamın alaca karanlığına,'),
(5901, 84, 17, 'Geceye ve (gecenin bağrında) topladığı şeylere,'),
(5902, 84, 18, 'Değirmileşen aya,'),
(5903, 84, 19, 'Ki, siz, mutlaka tabakadan tabakaya bineceksiniz!'),
(5904, 84, 20, 'Onların nesi var ki inanmıyorlar?'),
(5905, 84, 21, 'Kendilerine Kur\'an okunduğu zaman secde etmiyorlar?'),
(5906, 84, 22, 'Tersine o nankörler yalanlıyorlar.'),
(5907, 84, 23, 'Allah onların, içlerinde gizledikleri (düşünceleri) biliyor.'),
(5908, 84, 24, 'Onlara acı bir azabı müjdele.'),
(5909, 84, 25, 'Ancak inanıp yararlı işler yapan kimseler için kesintisiz bir mükafat vardır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Burçlar sahibi göğe andolsun,'),
(5911, 85, 2, 'Va\'dedilen güne andolsun,'),
(5912, 85, 3, '(O gün) Şahide ve şahidlik edilene andolsun,'),
(5913, 85, 4, 'Ki kahroldu o hendeğin adamları'),
(5914, 85, 5, 'O yakıt doldurulup tutuşturulmuş ateş (hendeğinin adamları)!'),
(5915, 85, 6, 'Onlar, o(ateş hendeği)nin başında oturmuşlardı.'),
(5916, 85, 7, 'Ve onlar, mü\'minlere yaptıklarını seyrediyorlardı.'),
(5917, 85, 8, 'Mü\'minler sırf aziz, övgüye layık Allah\'a inandıkları için o (zalim)ler onlardan öc aldılar.'),
(5918, 85, 9, 'O (Allah) ki göklerin ve yerin hükümranlığı O\'nundur. Allah, her şeye tanıktır.'),
(5919, 85, 10, 'İnanmış erkek ve kadınlara işkence edip sonra (yaptıklarına) tevbe etmeyenler (yok mu), onlar için cehennem azabı vardır ve onlar için yangın azabı vardır.'),
(5920, 85, 11, 'İnanan ve iyi işler yapan kimseler için de altlarından ırmaklar akan cennetler vardır. İşte büyük başarı budur.'),
(5921, 85, 12, 'Şüphesiz Rabbinin tutuşu şiddetlidir.'),
(5922, 85, 13, 'İlkin var eden, sonra geri çevirip yeniden yaratan O\'dur.'),
(5923, 85, 14, 'O bağışlayandır, sevendir.'),
(5924, 85, 15, 'Arş\'ın sahibidir, yücedir.'),
(5925, 85, 16, 'İstediğini yapandır.'),
(5926, 85, 17, 'O orduların haberi sana geldi mi?'),
(5927, 85, 18, '(Yani) Fir\'avn ve Semud(kavimlerin)in?'),
(5928, 85, 19, 'Doğrusu, nankörler bir yalanlama içindedirler.'),
(5929, 85, 20, 'Allah ise onları arkalarından kuşatmıştır.'),
(5930, 85, 21, 'Hayır, (Kur\'an, onların dedikleri gibi bir söz değil), o şerefli bir Kur\'an\'dır.'),
(5931, 85, 22, 'Korunan bir levhada(yazılı)dır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Göğe ve tarık\'a andolsun.'),
(5933, 86, 2, 'Tarık\'ın ne olduğunu sen nereden bileceksin?'),
(5934, 86, 3, 'Parlayan yıldızdır.'),
(5935, 86, 4, 'Hiçbir can yoktur ki başında bir koruyucu (bekçi) olmasın.'),
(5936, 86, 5, 'İnsan neden yaratıldığına bir baksın:'),
(5937, 86, 6, 'Atılan bir sudan yaratıldı.'),
(5938, 86, 7, 'Bel ile kaburga kemikleri arasından çıkan (bir sudan).'),
(5939, 86, 8, 'O (Allah), onu tekrar döndür(üp yarat)mağa kadirdir.'),
(5940, 86, 9, 'Gizlilerin (ortaya dökülüp) yoklanacağı gün,'),
(5941, 86, 10, 'İnsanın ne bir gücü, ne de bir yardımcısı vardır.'),
(5942, 86, 11, 'Dönüşlü göğe andolsun,'),
(5943, 86, 12, '(Bitkilerin çıkması için) Çatlayan yere andolsun ki,'),
(5944, 86, 13, 'O (Kur\'an), elbette (hak ile batılı) ayırdedici bir sözdür.'),
(5945, 86, 14, 'O, şaka değildir.'),
(5946, 86, 15, 'Onlar (onu iptal etmek için) bir tuzak kuruyorlar.'),
(5947, 86, 16, 'Ben de (onları yakalamak için) bir tuzak kuruyorum.'),
(5948, 86, 17, 'Hele sen o kafirlere mühlet ver, biraz bırak onları (bildiklerine gitsinler).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Rabbinin yüce adını tesbih et (O\'nun eksikliklerden uzak olduğunu an).'),
(5950, 87, 2, 'O ki (her şeyi) yarattı, düzenledi.'),
(5951, 87, 3, 'Ve O ki herşeyin miktarını, biçimini belirleyip hedefini gösterdi.'),
(5952, 87, 4, 'Ve O ki otlağı çıkardı,'),
(5953, 87, 5, 'Sonra da onu kupkuru, siyah bir çöpe çevirdi.'),
(5954, 87, 6, 'Sana (Kur\'an\'ı), okutacağız, unutmayacaksın.'),
(5955, 87, 7, 'Yalnız Allah\'ın dilediğini unutursun. O, açığı da bilir, gizli olanı da.'),
(5956, 87, 8, 'Seni en kolay yola muvaffak edeceğiz.'),
(5957, 87, 9, 'O halde eğer hatırlatmak yarar verirse hatırlat, öğüt ver.'),
(5958, 87, 10, '(Allah\'a) Saygılı olan hatırlar (öğüt alır).'),
(5959, 87, 11, 'Bahtsız olan da ondan kaçınır.'),
(5960, 87, 12, 'O da en büyük ateşe girer.'),
(5961, 87, 13, 'Sonra orada ne ölür, ne de yaşar.'),
(5962, 87, 14, 'Doğrusu, mutluluğa ermiştir zekat veren;'),
(5963, 87, 15, 'Rabbinin adını anıp namaz kılan.'),
(5964, 87, 16, 'Ama siz, şu yakın hayatı yeğliyorsunuz.'),
(5965, 87, 17, 'Oysa ahiret daha iyi ve daha kalıcıdır.'),
(5966, 87, 18, 'Bu (hükümler), elbette ilk sahifelerde de vardı:'),
(5967, 87, 19, 'İbrahim\'in ve Musa\'nn sayfalarında.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, '(Şiddet ve dehşetiyle her şeyi) Sarıp kaplayacak olan(o felaket)in haberi sana geldi mi?'),
(5969, 88, 2, 'Yüzler var ki o gün öne düşüktür,'),
(5970, 88, 3, 'Çalışır, yorulur.'),
(5971, 88, 4, 'Kızgın ateşe girerler.'),
(5972, 88, 5, 'Kendilerine kaynamış bir gözeden (su) içirilir.'),
(5973, 88, 6, 'Onlar için kuru dikenden başka yiyecek de yoktur.'),
(5974, 88, 7, 'O da ne semirtir, ne de açlığı giderir.'),
(5975, 88, 8, 'Yüzler de var ki o gün ni\'met içinde mutlu,'),
(5976, 88, 9, 'İşinden memnun,'),
(5977, 88, 10, 'Yüksek bir bahçededir.'),
(5978, 88, 11, 'Orada boş söz işitmezler.'),
(5979, 88, 12, 'Orada akan bir kaynak vardır.'),
(5980, 88, 13, 'Orada yükseltilmiş tahtlar,'),
(5981, 88, 14, 'Konulmuş kadehler,'),
(5982, 88, 15, 'Dizilmiş yastıklar,'),
(5983, 88, 16, 'Serilmiş halılar vardır.'),
(5984, 88, 17, 'Bakmıyorlar mı develere, nasıl yaratılmış?'),
(5985, 88, 18, 'Göğe, nasıl yükseltilmiş?'),
(5986, 88, 19, 'Dağlara, nasıl dikilmiş?'),
(5987, 88, 20, 'Yere, nasıl yayılıp döşenmiş?'),
(5988, 88, 21, 'Öğüt ver, çünkü sen ancak öğüt verensin.'),
(5989, 88, 22, 'Onların üzerinde zorlayıcı değilsin.'),
(5990, 88, 23, 'Ancak kim yüz çevirir ve inanmazsa,'),
(5991, 88, 24, 'Allah ona en büyük azabı eder,'),
(5992, 88, 25, 'Dönüşleri Bizedir.'),
(5993, 88, 26, 'Sonra onların hesabını görmek Bize düşer.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Andolsun fecre (tan yeri ağarmasına),'),
(5995, 89, 2, 'On geceye,'),
(5996, 89, 3, 'Çift\'e ve tek\'e,'),
(5997, 89, 4, 'Gitmekte olan geceye.'),
(5998, 89, 5, 'Bu( anıla)n (şeyler)de akıl sahibi için bir yemin var, değil mi? (İşte bunlara andolsun ki kafirler mutlaka azaba uğrayacaklardır!)'),
(5999, 89, 6, 'Görmedin mi Rabbin ne yaptı \'Ad(kavmin)e?'),
(6000, 89, 7, 'Sütunlu İrem\'e?'),
(6001, 89, 8, 'Ki ülkeler arasında onun eşi yaratılmamıştı.'),
(6002, 89, 9, 'Vadi(\'l-Kura)da kayaları oya(rak evler yapa)n Semud(kavmin)e?'),
(6003, 89, 10, 'Ve kazıklar sahibi Fir\'avn\'a?'),
(6004, 89, 11, 'Bunlar ülkelerde azmışlardı.'),
(6005, 89, 12, 'Oralarda çok kötülük etmişlerdi.'),
(6006, 89, 13, 'Bu yüzden Rabbin onların üzerine azab kırbacını çarptı.'),
(6007, 89, 14, 'Elbette Rabbin gözetleme yerindedir (her an kullarının fiillerini gözetlemektedir).'),
(6008, 89, 15, 'Fakat insan öyledir; Rabbi ne zaman kendisini sınayıp ona ikramda bulunur, ona ni\'met verirse: "Rabbim bana ikram etti" der.'),
(6009, 89, 16, 'Ama Rabbi onu sınayıp rızkını daraltırsa: "Rabbim beni alçalttı (perişan etti)" der.'),
(6010, 89, 17, 'Hayır, doğrusu siz (Allah\'tan ikram bekliyorsunuz ama kendiniz) yetime ikram etmiyorsunuz.'),
(6011, 89, 18, 'Yoksula yedirmeğe teşvik etmiyorsunuz.'),
(6012, 89, 19, 'Mirası hırsla yutuyorsunuz.'),
(6013, 89, 20, 'Malı pek çok seviyorsunuz.'),
(6014, 89, 21, 'Hayır, yer birbiri ardınca sarsılıp dümdüz edildiği zaman,'),
(6015, 89, 22, 'Melekler sıra sıra dizili durumda Rabbin geldiği zaman.'),
(6016, 89, 23, 'Ve cehennem de getirildiği zaman. İşte o gün insan anlar, ama artık anlamanın kendisine ne yararı var?'),
(6017, 89, 24, '(O zaman insan): "Ah, keşke ben bu hayatım için (iyi işler yapıp) gönderseydim!" der.'),
(6018, 89, 25, 'O gün O\'nun yapacağı azabı kimse yapamaz.'),
(6019, 89, 26, 'Ve O\'nun vuracağı bağı kimse vuramaz!'),
(6020, 89, 27, 'Ey huzura eren nefis!'),
(6021, 89, 28, 'Razı edici ve razı edilmiş olarak Rabbine dön!'),
(6022, 89, 29, '(İyi) Kullarım arasına gir!'),
(6023, 89, 30, 'Cennetime gir!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Yoo, and içerim bu kente,'),
(6025, 90, 2, 'Ki sen bu şehire girmekte(burada yaşamakta)sın.'),
(6026, 90, 3, 'Ve (and içerim) doğurucuya ve doğurduğuna ki,'),
(6027, 90, 4, 'Biz insanı zorluk arasında yarattık.'),
(6028, 90, 5, 'İnsan, hiç kimsenin kendisine güç yetiremeyeceğini mi sanıyor?'),
(6029, 90, 6, '(Gösteriş ve övünme için) "Ben birçok mal telef ettim" diyor.'),
(6030, 90, 7, 'Kimsenin kendisini görmediğini mi sanıyor?'),
(6031, 90, 8, 'Biz ona vermedik mi: İki göz'),
(6032, 90, 9, 'Bir dil, iki dudak?'),
(6033, 90, 10, 'Ona iki tepeyi (anasının iki memesini emmenin veya hayır ve şerrin yolunu) gösterdik.'),
(6034, 90, 11, 'Fakat o, sarp yokuşa atılamadı.'),
(6035, 90, 12, 'Sarp yokuşun ne olduğunu sen nereden bileceksin?'),
(6036, 90, 13, 'Bir boynu (kölelik zincirinden) çözmek,'),
(6037, 90, 14, 'Yahut açlık gününde doyurmaktır:'),
(6038, 90, 15, 'Akraba olan yetimi,'),
(6039, 90, 16, 'Yahut hiçbir şeyi olmayan yoksulu.'),
(6040, 90, 17, 'Sonra inanıp birbirlerine sabır tavsiye eden ve merhamet tavsiye edenlerden olmak.'),
(6041, 90, 18, 'İşte onlar sağın adamlarıdır (Kitabı sağından verilen uğurlu kişilerdir).'),
(6042, 90, 19, 'Ayetlerimizi tanımayanlar ise solun adamlarıdır (Kitabı solundan verilen uğursuz kişilerdir).'),
(6043, 90, 20, 'Onlara (kapıları) üzerlerine kilitlenecek bir ateş vardır!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Güneşe ve onun aydın sabahına andolsun,'),
(6045, 91, 2, 'Onu izleyen aya andolsun,'),
(6046, 91, 3, 'Güneşi ortaya çıkaran gündüze andolsun.'),
(6047, 91, 4, 'Onu örten geceye andolsun.'),
(6048, 91, 5, 'Göğe ve onu yapana andolsun.'),
(6049, 91, 6, 'Yere ve onu yuvarlayıp döşeyene andolsun.'),
(6050, 91, 7, 'Nefse ve onu biçimlendirene,'),
(6051, 91, 8, 'Ona bozukluğunu ve korunmasını (isyanını ve ita\'atini) ilham edene andolsun ki:'),
(6052, 91, 9, '(Allah\'tan başkasına tapmayarak) Nefsini yücelten kazanmış,'),
(6053, 91, 10, '(Yaratıklara taparak) Onu alçaltan da ziyana uğramıştır.'),
(6054, 91, 11, 'Semud (kavmi), azgınlığı yüzünden (Hakk\'ı) yalanladı.'),
(6055, 91, 12, 'En haydutları ayaklandığı zaman,'),
(6056, 91, 13, 'Allah\'ın elçisi onlara: "Allah\'ın devesine ve onun su içme hakkına dokunmayın!" demişti.'),
(6057, 91, 14, 'Onu yalanladılar, deveyi kestiler. Rableri de, günahları yüzünden azabı başlarına geçirip, orayı dümdüz etti.'),
(6058, 91, 15, '(Rab) Bu işin sonundan korkmaz.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Örttüğü zaman geceye andolsun,'),
(6060, 92, 2, 'Göründüğü zaman gündüze andolsun,'),
(6061, 92, 3, 'Erkeği ve dişiyi yaratana andolsun ki,'),
(6062, 92, 4, 'Sizin işiniz çeşit çeşittir:'),
(6063, 92, 5, 'Kim (hayır için) verir, korunursa,'),
(6064, 92, 6, 'Ve en güzel(söz)ü doğrularsa,'),
(6065, 92, 7, 'Ona en kolay(yolda gitmey)i kolaylaştırırız.'),
(6066, 92, 8, 'Kim de cimrilik eder, kendini zengin (ve kendine yeterli) görürse,'),
(6067, 92, 9, 'Ve en güzel(söz)ü de yalanlarsa,'),
(6068, 92, 10, 'Ona da en güç(yolda gitmey)i kolaylaştırırız.'),
(6069, 92, 11, 'Çukura düştüğü zaman malı ona hiçbir fayda sağlamaz.'),
(6070, 92, 12, 'Doğru yola iletmek bize aittir.'),
(6071, 92, 13, 'Son da ilk de (ahiret de dünya da) bizimdir.'),
(6072, 92, 14, 'Ben sizi alev saçan bir ateşe karşı uyardım.'),
(6073, 92, 15, 'Ona ancak haydut olan girer.'),
(6074, 92, 16, 'O ki, yalanlandı ve sırtını döndü.'),
(6075, 92, 17, 'En çok korunan da ondan uzak tutulur.'),
(6076, 92, 18, 'O ki malını hayra vererek arınır, yücelir.'),
(6077, 92, 19, 'Ve onun yanında, hiç kimsenin karşılık verilecek bir ni\'meti yoktur (o, verdiğini kendisine yapılan bir iyiliğin karşılığı olarak değil),'),
(6078, 92, 20, 'Yalnız yüce Rabbinin rızası için verir.'),
(6079, 92, 21, 'Yakında kendisi de (Allah\'ın verceği ni\'metle) razı olacaktır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Kuşluk vaktine andolsun,'),
(6081, 93, 2, 'Sakinleşen geceye andolsun ki,'),
(6082, 93, 3, 'Rabbin, seni bırakmadı ve sana darılmadı.'),
(6083, 93, 4, 'Senin sonun, ilkinden iyi olacaktır.'),
(6084, 93, 5, 'Rabbin, sana verecek ve sen razı olacaksın.'),
(6085, 93, 6, 'O, seni yetim bulup barındırmadı mı?'),
(6086, 93, 7, 'Seni şaşırmış bulup yola iletmedi mi?'),
(6087, 93, 8, 'Seni fakir bulup zengin etmedi mi?'),
(6088, 93, 9, 'Öyleyse sakın öksüzü ezme,'),
(6089, 93, 10, 'Dilenciyi azarlama.'),
(6090, 93, 11, 'Ve Rabbinin ni\'metini anlat.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Biz senin (bunalan) göğsünü açmadık mı (ondaki bunalımları, sıkıntıları giderip, onu ilim, hikmet ve huzur ile genişletmedik mi)?'),
(6092, 94, 2, 'Ve atmadık mı senin üzerinden yükünü?'),
(6093, 94, 3, 'Ki (o, ağırlığından) sırtını çatırdatmıştı!'),
(6094, 94, 4, 'Senin şanını yükseltmedik mi?'),
(6095, 94, 5, 'Muhakkak her güçlükle beraber bir kolaylık vardır.'),
(6096, 94, 6, 'Evet, her güçlükle beraber bir kolaylık vardır.'),
(6097, 94, 7, 'O halde (işlerinden) boşaldığın zaman (ibadete) dur.'),
(6098, 94, 8, 'Rabb\'ine niyaz et, yalvar.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Tin\'e ve Zeytun\'a andolsun.'),
(6100, 95, 2, 'Sina Dağına andolsun.'),
(6101, 95, 3, 'Ve bu güvenli Şehre andolsun ki,'),
(6102, 95, 4, 'Biz insanı en güzel biçimde yarattık.'),
(6103, 95, 5, 'Sonra onu aşağıların aşağısına çevirdik.'),
(6104, 95, 6, 'Yalnız inanıp iyi işler yapanlar hariç. Onlar için kesintisiz bir mükafat vardır.'),
(6105, 95, 7, 'Böyle iken sana ahiret cezasını yalanlatan nedir?'),
(6106, 95, 8, 'Allah, hüküm verenlerin en iyisi değil midir?');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Yaratan Rabbinin adıyle oku.'),
(6108, 96, 2, 'O, insanı alaktan (embriyodan) yarattı.'),
(6109, 96, 3, 'Oku, Rabbin en büyük kerem sahibidir.'),
(6110, 96, 4, 'O ki kalemle (yazmayı) öğretti.'),
(6111, 96, 5, 'İnsana bilmediğini öğretti.'),
(6112, 96, 6, 'Hayır, (Rabbinin bu kadar iyiliğine rağmen yine) insan azar;'),
(6113, 96, 7, 'Kendini zengin (kendine yeterli) gördüğü için,'),
(6114, 96, 8, 'Ama dönüş Rabbinedir (O\'nun huzurunda bu azgınlığının hesabını verecektir).'),
(6115, 96, 9, 'Gördün mü şu men\'edeni?'),
(6116, 96, 10, 'Namaz kılarken bir kulu (namazdan)?'),
(6117, 96, 11, 'Gördün mü, ya o (kul) doğru yolda olur,'),
(6118, 96, 12, 'Yahut kötülüklerden korunmayı emrederse?'),
(6119, 96, 13, 'Gördün mü, ya bu (adam, hakkı) yalanlar yüz çevirirse? (O zaman bu yaptığı kendisi için iyi mi olur?)'),
(6120, 96, 14, 'Allah\'ın (daima kendisini) gördüğünü bilmedi mi (o)?'),
(6121, 96, 15, 'Hayır, (olmaz böyle şey), eğer bundan vazgeçmezse (onu) perçem(in)den yakalar(ateşe sürükler)iz,'),
(6122, 96, 16, 'O yalancı, günahkar perçem(den)!'),
(6123, 96, 17, 'O zaman (o gitsin) de meclisini (adamlarını) çağırsın.'),
(6124, 96, 18, 'Biz de zebanileri çağıracağız.'),
(6125, 96, 19, 'Hayır, ona boyun eğme; (Rabbine) secde et ve yaklaş!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Biz o(Kur\'a)n\'ı Kadir gecesinde indirdik.'),
(6127, 97, 2, 'Kadir gecesinin ne olduğunu sen nereden bileceksin?'),
(6128, 97, 3, 'Kadir gecesi bin aydan hayırlıdır.'),
(6129, 97, 4, 'Melekler ve Ruh, o gece Rab\'lerinin izniyle her iş için iner de iner.'),
(6130, 97, 5, 'Esenliktir o, ta tan yeri ağarıncaya kadar!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Kitap ehlinden ve müşriklerden (hakk\'ı) tanımayanlar, kendilerine açık kanıt gelinceye dek (halleri üzere) bırakılacak değillerdi (mutlaka kendilerine açıklama gelecekti).'),
(6132, 98, 2, '(Yani) Allah tarafından gönderilen ve tertemiz sahifeler okuyan bir elçi.'),
(6133, 98, 3, 'O sahifelerde doğru, değerli Kitaplar vardır.'),
(6134, 98, 4, 'Kitap verilmiş olanlar, ancak kendilerine açık kanıt geldikten sonra ayrılığa düştüler.'),
(6135, 98, 5, 'Oysa kendilerine, dini yalnız Allah\'a halis kılıp O\'nu birleyerek Allah\'a kulluk etmeleri, namazı kılmaları, zekatı vermeleri emredilmişti. İşte doğru din oydu.'),
(6136, 98, 6, 'Kitap ehlinden ve (Allah\'a) ortak koşanlardan olan nankörler, sürekli olarak cehennem ateşindedirler. Onlar, halkın en şerlisidir.'),
(6137, 98, 7, 'İnanıp iyi işler yapanlar da halkın en hayırlısıdır.'),
(6138, 98, 8, 'Rableri katında onların mükafatı altlarından ırmaklar akan, içinde ebedi kalacakları, Adn cennetleridir. Allah onlardan razı olmuş, onlar da O\'ndan razı olmuşlardır. Bu, Rabbine saygı gösterene mahsustur.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Yer o yaman sarsıntı ile sarsıldığı,'),
(6140, 99, 2, 'Yer (bağrındaki) ağırlıklarını çıkardığı,'),
(6141, 99, 3, 'Ve insan: "Ona ne oluyor?" dediği zaman!'),
(6142, 99, 4, 'İşte o gün (yer), haberlerini söyler.'),
(6143, 99, 5, 'Çünkü Rabbin ona vahyetmiştir (onun için başından geçenleri anlatır).'),
(6144, 99, 6, 'O gün insanlar, ayrı ayrı gruplar halinde (Yüce Divana) çıkarlar ki, yaptıkları işler kendilerine gösterilsin.'),
(6145, 99, 7, 'Artık kim zerre ağırlığınca hayır yapmışsa onu görür.'),
(6146, 99, 8, 'Ve kim zerre ağırlığınca şer yapmışsa onu görür.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Andolsun nefesleriyle (güp güp) ses çıkararak koşan(at)lara,'),
(6148, 100, 2, '(Tırnaklariyle yerden) Ateş çıkaranlara,'),
(6149, 100, 3, 'Sabahleyin akın edenlere,'),
(6150, 100, 4, '(Koşarak) Toz koparanlara,'),
(6151, 100, 5, 'Derken bir topluluğun ortasına dalanlara.'),
(6152, 100, 6, '(Bunlara andolsun) Ki insan, Rabbine karşı çok nankördür.'),
(6153, 100, 7, 'Ve o da buna şahiddir.'),
(6154, 100, 8, 'Doğrusu o, malı çok sever.'),
(6155, 100, 9, 'Bilmez mi o, kabirlerde olanlar dışarı atıldığı,'),
(6156, 100, 10, 'Göğüslerde bulunanlar devşirildiği zaman,'),
(6157, 100, 11, 'O gün Rabbleri onların her halini haber almış(gizli ve açık bütün yaptıklarını bilmiş)tir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Çarpan olay!'),
(6159, 101, 2, 'Nedir o çarpan olay?'),
(6160, 101, 3, 'O çarpan olayın ne olduğunu sen nereden bileceksin?'),
(6161, 101, 4, 'O gün insanlar, yayılmış pervaneler gibi olur(lar).'),
(6162, 101, 5, 'Dağlar atılmış renkli yün gibi olur.'),
(6163, 101, 6, 'Kimin tartıları ağır gelirse,'),
(6164, 101, 7, 'O, memmun edici bir hayat içindedir.'),
(6165, 101, 8, 'Kimin tartıları hafif gelirse,'),
(6166, 101, 9, 'Onun anası (bağrına atılacağı) haviye(uçurum)dur.'),
(6167, 101, 10, 'Onun ne olduğunu sen nereden bileceksin?'),
(6168, 101, 11, 'Kızgın bir ateştir!');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Çokluk yarışı, sizi oyaladı,'),
(6170, 102, 2, 'Nihayet kabirleri ziyaret ettiniz (kabre girinceye kadar mal artırmağa çalıştınız).'),
(6171, 102, 3, 'Hayır (olmaz bu), yakında bileceksiniz (hatanızı)!'),
(6172, 102, 4, 'Yine hayır, yakında bileceksiniz (hatanızı)!'),
(6173, 102, 5, 'Hayır, (gerçeği) kesin bilgi ile bilseydiniz;'),
(6174, 102, 6, 'Mutlaka cehennemi görür (oun varlığını gözle görmüş gibi kabul eder)diniz.'),
(6175, 102, 7, 'Sonra onu kesin olarak gözle göreceksiniz.'),
(6176, 102, 8, 'Sonra o gün, (size verilen) ni\'metten sorulacaksınız.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Asra andolsun ki,'),
(6178, 103, 2, 'İnsan ziyandadır.'),
(6179, 103, 3, 'Ancak inanıp iyi işler yapanlar, birbirlerine hakkı tavsiye edenler ve birbirlerine sabrı tavsiye edenler ziyanda değillerdir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, '(İnsanları) Diliyle çekiştiren, kaş ve gözüyle işaretler yapıp alay eden her fesad kişinin vay haline!'),
(6181, 104, 2, 'O ki mal yığdı, onu saydı durdu.'),
(6182, 104, 3, 'Malının, kendisini ebedi yaşatacağını sanıyor.'),
(6183, 104, 4, 'Hayır, andolsun ki o, Hutame\'ye atılacaktır.'),
(6184, 104, 5, 'Hutame\'nin ne olduğunu sen nereden bileceksin?'),
(6185, 104, 6, 'Allah\'ın tutuşturulmuş ateşidir.'),
(6186, 104, 7, '(Bir ateş) Ki gönüllere işler.'),
(6187, 104, 8, 'O, onların üzerine kapatılıp kilitlenecektir.'),
(6188, 104, 9, '(Kendileri,) Uzatılmış direkler arasında (bağlı) olarak (kalacaklardır).');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Rabbinin, Fil sahiplerine ne yaptığını görmedin m?'),
(6190, 105, 2, 'Onların tuzaklarını boşa çıkarmadı mı?'),
(6191, 105, 3, 'Üzerlerine sürü sürü kuşlar gönderdi,'),
(6192, 105, 4, 'Onlara çamurdan sertleşmiş taşlar atan (kuşlar).'),
(6193, 105, 5, 'Nihayet onları, kurt yeniği ekin yaprağı gibi yaptı.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Kureyşi alıştırdığı için,'),
(6195, 106, 2, 'Onları kış ve yaz yolculuğuna alıştırdığı için,'),
(6196, 106, 3, 'Bu Ev(Ka\'be\'n)in Rabbine kulluk etsinler.'),
(6197, 106, 4, 'O ki onları yedirip açlıktan kurtardı ve onları korkudan güvene kavuşturdu.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Din(ahiret cezasın)ı yalanlayan(adam)ı gördün mü?'),
(6199, 107, 2, 'İşte o, öksüzü iter, kakar;'),
(6200, 107, 3, 'Yoksulu doyurmağa önayak olmaz.'),
(6201, 107, 4, 'Şu namaz kılanların vay haline,'),
(6202, 107, 5, 'Ki, onlar namazlarından gaflet ederler (kıldıkları namazın değerini bilmez, ona önem vermezler).'),
(6203, 107, 6, 'Onlar gösteriş (için ibadet) yaparlar.'),
(6204, 107, 7, 'En ufak bir yardımı esirgerler.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Biz sana Kevser\'i (bol ni\'met, ilim ve büyük şeref) verdik.'),
(6206, 108, 2, 'Öyleyse Rabbin için namaz kıl ve nahret (kurban kes veya ellerini boğazına kadar kaldırıp tekbir al).'),
(6207, 108, 3, 'Asıl sonu kesik olan, sana buğzedendir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'De ki: Ey nankörler,'),
(6209, 109, 2, 'Ben sizin yaptığınız ibadeti yapmam;'),
(6210, 109, 3, 'Siz de benim yaptığım ibadeti yapmazsınız.'),
(6211, 109, 4, 'Ben asla sizin yapmakta olduğunuz ibadeti yapıcı değilim.'),
(6212, 109, 5, 'Siz de benim yapmakta olduğum ibadeti yapıcı değilsiniz.'),
(6213, 109, 6, 'Sizin dininiz size, benim dinim banadır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Allah\'ın yardımı ve fetih geldiği,'),
(6215, 110, 2, 'Ve insanların dalga dalga Allah\'ın dinine girdiklerini gördüğün zaman,'),
(6216, 110, 3, 'Rabbini överek tesbih et, O\'ndan mağfiret dile. Çünkü O, tevbeyi kabul edendir.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Ebuleheb\'in iki eli kurusun (yok olsun o); zaten yok oldu ya.'),
(6218, 111, 2, 'Ne malı, ne de kazandığı onu (Allah\'ın kahrından) kurtaramadı.'),
(6219, 111, 3, 'Alevli bir ateşe girecektir (o).'),
(6220, 111, 4, 'Karısı da, odun hamalı olarak.'),
(6221, 111, 5, 'Boynunda hurma lifinden bir ip olacaktır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'De ki: O Allah birdir.'),
(6223, 112, 2, 'Allah Samed\'dir.'),
(6224, 112, 3, 'Kendisi doğurmamıştır ve doğurulmamıştır.'),
(6225, 112, 4, 'Hiçbir şey O\'nun dengi olmamıştır.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'De ki: Sığınırım ben, karanlığı yarıp sabahı ortaya çıkaran Rabbe;'),
(6227, 113, 2, 'Yarattığı şeylerin şerrinden,'),
(6228, 113, 3, 'Karanlığı çöktüğü zaman gecenin şerrinden,'),
(6229, 113, 4, 'Düğümlere üfleyip tüküren büyücü kadınların şerrinden,'),
(6230, 113, 5, 'Ve hased ettiği zaman hasedcinin şerrinden.');
INSERT INTO `tr_ates` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'De ki: "Sığınırım ben, insanların Rabbine.'),
(6232, 114, 2, 'İnsanların padişahına,'),
(6233, 114, 3, 'İnsanların Tanrısına:'),
(6234, 114, 4, 'O sinsi vesvesecinin şerrinden.'),
(6235, 114, 5, 'O ki insanların göğüslerine (kötü düşünceler) fısıldar.'),
(6236, 114, 6, 'Gerek cinlerden, gerek insanlardan (olan bütün vesvesecilerin şerrinden Allah\'a sığınırım).');
