INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Rahman Rahim olan Allah\'ın adıyla'),
(2, 1, 2, 'Hamd* Alemlerin Rabbinedir.'),
(3, 1, 3, 'Rahman ve Rahimdir.'),
(4, 1, 4, 'Din gününün malikidir.'),
(5, 1, 5, 'Biz yalnızca Sana ibadet eder ve yalnızca Senden yardım dileriz.'),
(6, 1, 6, 'Bizi doğru yola ilet;'),
(7, 1, 7, 'Kendilerine nimet verdiklerinin yoluna gazaba uğrayanların ve sapmışlarınkine değil.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'Elif, Lam, Mim,'),
(9, 2, 2, 'Bu, kendisinde şüphe olmayan, muttakiler için yol gösterici olan bir Kitap\'tır.'),
(10, 2, 3, 'Onlar, gaybe inanırlar, namazı dosdoğru kılarlar ve kendilerine rızık olarak verdiklerimizden infak ederler.'),
(11, 2, 4, 'Ve onlar, sana indirilene, senden önce indirilenlere iman ederler ve ahirete de kesin bir bilgiyle inanırlar.'),
(12, 2, 5, 'İşte bunlar, Rablerinden olan bir hidayet üzeredirler ve kurtuluşa erenler bunlardır.'),
(13, 2, 6, 'Şüphesiz, inkar edenleri uyarsan da, uyarmasan da, onlar için fark etmez; inanmazlar.'),
(14, 2, 7, 'Allah, onların kalplerini ve kulaklarını mühürlemiştir; gözlerinin üzerinde perdeler vardır. Ve büyük azap onlaradır.'),
(15, 2, 8, 'İnsanlardan öyleleri vardır ki: "Biz Allah\'a ve ahiret gününe iman ettik" derler; oysa inanmış değillerdir.'),
(16, 2, 9, '(Sözde) Allah\'ı ve iman edenleri aldatırlar. Oysa onlar, yalnızca kendilerini aldatıyorlar ve şuurunda değiller.'),
(17, 2, 10, 'Kalplerinde hastalık vardır. Allah da hastalıklarını arttırmıştır. Yalan söylemekte olduklarından dolayı, onlar için acı bir azap vardır.'),
(18, 2, 11, 'Kendilerine: "Yeryüzünde fesat çıkarmayın" denildiğinde: "Biz sadece ıslah edicileriz" derler.'),
(19, 2, 12, 'Bilin ki; gerçekten, asıl fesatçılar bunlardır, ama şuurunda değildirler.'),
(20, 2, 13, 'Ve (yine) kendilerine: "İnsanların iman ettiği gibi siz de iman edin" denildiğinde: "Düşük akıllıların iman ettiği gibi mi iman edelim?" derler. Bilin ki, gerçekten asıl düşük-akıllılar kendileridir; ama bilmezler.'),
(21, 2, 14, 'İman edenlerle karşılaştıkları zaman: "İman ettik" derler. Şeytanlarıyla başbaşa kaldıklarında ise, derler ki: “Şüphesiz, sizinle beraberiz. Biz (onlarla) yalnızca alay ediyoruz."'),
(22, 2, 15, '(Asıl) Allah onlarla alay eder ve taşkınlıkları içinde şaşkınca dolaşmalarına (belli bir) süre tanır.'),
(23, 2, 16, 'İşte bunlar, hidayete karşılık sapıklığı satın almışlardır; fakat bu alış-verişleri bir yarar sağlamamış; hidayeti de bulmamışlardır.'),
(24, 2, 17, 'Bunların örneği, ateş yakan adamın örneğine benzer; (ki onun ateşi) çevresini aydınlattığı zaman, Allah onların aydınlığını giderir ve göremez bir şekilde karanlıklar içinde bırakıverir.'),
(25, 2, 18, 'Sağırdırlar, dilsizdirler, kördürler. Bundan dolayı dönmezler.'),
(26, 2, 19, 'Ya da (bunlar) karanlıklar, gök gürültüsü ve şimşek(ler)le yüklü, \'gökten şiddetli bir yağmur fırtınasına tutulmuş gibidirler ki, yıldırımların saldığı dehşetle\'; ölüm korkusundan parmaklarıyla kulaklarını tıkarlar. Oysa Allah kafirleri çepeçevre kuşatıcıdır.'),
(27, 2, 20, 'Çakan şimşek neredeyse gözlerini kapıverecek; önlerini her aydınlattığında (biraz) yürürler, üzerlerine karanlık basıverince de kalakalırlar. Allah dileseydi, işitmelerini de görmelerini de gideriverirdi. Şüphesiz Allah, herşeye güç yetirendir.'),
(28, 2, 21, 'Ey insanlar, sizi ve sizden öncekileri yaratan Rabbinize kulluk edin ki sakınasınız.'),
(29, 2, 22, 'O, sizin için yeryüzünü bir döşek, gökyüzünü bir bina kıldı. Ve gökten yağmur indirerek bununla sizin için (çeşitli) ürünlerden rızık çıkardı. Öyleyse (bütün bunları) bile bile Allah\'a eşler koşmayın.'),
(30, 2, 23, 'Eğer kulumuza indirdiğimiz (Kur\'an)’dan şüphedeyseniz, bu durumda, siz de bunun benzeri bir sûre getirin. Ve eğer doğru sözlüyseniz, Allah\'tan başka şahitlerinizi (kendilerine güvendiğiniz yardımcılarınızı) çağırın.'),
(31, 2, 24, 'Ama yapamazsanız -ki kesin olarak yapamayacaksınız- bu durumda kafirler için hazırlanmış ve yakıtı insanlar ile taşlar olan ateşten sakının.'),
(32, 2, 25, '(Ey Muhammed) iman edip salih amellerde bulunanları müjdele. Gerçekten onlar için altlarından ırmaklar akan cennetler vardır. Kendilerine rızık olarak bu ürünlerden her yedirildiğinde: "Bu daha önce de rızıklandığımızdır" derler. Bu, onlara, (dünyadakine) benzer olarak sunulmuştur. Orada, onlar için tertemiz eşler vardır ve onlar orada süresiz kalacaklardır.'),
(33, 2, 26, 'Şüphesiz Allah, bir sivrisineği de, ondan üstün olanı da, (herhangi bir şeyi) örnek vermekten çekinmez. Böylece iman edenler, kuşkusuz bunun Rablerinden gelen bir gerçek olduğunu bilirler; inkar edenler ise, "Allah, bu örnekle neyi amaçlamış?" derler. (Oysa Allah,) Bununla birçoğunu saptırır, birçoğunu da hidayete erdirir. Ancak O, fasıklardan başkasını saptırmaz.'),
(34, 2, 27, 'Ki (bunlar) Allah\'ın ahdini, onu kesin olarak onayladıktan sonra bozarlar, Allah\'ın kendisiyle birleştirilmesini emrettiği şeyi keserler ve yeryüzünde bozgunculuk çıkarırlar. Kayba uğrayanlar, işte bunlardır.'),
(35, 2, 28, 'Nasıl oluyor da Allah\'ı inkar ediyorsunuz? Oysa ölü iken sizi O diriltti; sonra sizi yine öldürecek, yine diriltecektir ve sonra O\'na döndürüleceksiniz.'),
(36, 2, 29, 'Sizin için yerde olanların tümünü yaratan O\'dur. Sonra göğe yönelip (istiva edip) de onları yedi gök olarak düzenleyen O\'dur. Ve O, herşeyi bilendir.'),
(37, 2, 30, 'Hani Rabbin meleklere: "Muhakkak Ben, yeryüzünde bir halife var edeceğim" demişti. Onlar da: "Biz Seni şükrünle yüceltir ve (sürekli) takdis ederken, orada bozgunculuk çıkaracak ve kanlar akıtacak birini mi var edeceksin?" dediler. (Allah:) "Şüphesiz sizin bilmediğinizi Ben bilirim" dedi.'),
(38, 2, 31, 'Ve Adem\'e isimlerin hepsini öğretti. Sonra onları meleklere yöneltip: "Eğer doğru sözlüyseniz, bunları Bana isimleriyle haber verin" dedi.'),
(39, 2, 32, 'Dediler ki: "Sen Yücesin, bize öğrettiğinden başka bizim hiçbir bilgimiz yok. Gerçekten Sen, herşeyi bilen, hüküm ve hikmet sahibi olansın."'),
(40, 2, 33, '(Allah:) "Ey Adem, bunları onlara isimleriyle haber ver" dedi. O, bunları onlara isimleriyle haber verince de dedi ki: "Size demedim mi, göklerin ve yerin gaybını gerçekten Ben bilirim, gizli tuttuklarınızı ve açığa vurduklarınızı da Ben bilirim."'),
(41, 2, 34, 'Ve meleklere: "Adem’e secde edin" dedik. İblis hariç (hepsi) secde ettiler. O ise, diretti ve kibirlendi, (böylece) kafirlerden oldu.'),
(42, 2, 35, 'Ve dedik ki: "Ey Adem, sen ve eşin cennette yerleş. İkiniz de ondan, neresinden dilerseniz, bol bol yiyin; ama şu ağaca yaklaşmayın, yoksa zalimlerden olursunuz."'),
(43, 2, 36, 'Fakat şeytan, oradan ikisinin ayağını kaydırdı ve böylece onları içinde bulundukları (durum)dan çıkardı. Biz de: "Kiminiz kiminize düşman olarak inin, sizin için yeryüzünde belli bir vakte kadar bir yerleşim ve meta vardır" dedik.'),
(44, 2, 37, 'Derken Adem, Rabbinden (birtakım) kelimeler aldı. Bunun üzerine (Allah da) tevbesini kabul etti. Şüphesiz O, tevbeleri kabul edendir, esirgeyendir.'),
(45, 2, 38, 'Dedik ki: "Oradan tümünüz inin. Bundan sonra size Benden bir hidayet geldiğinde, kim Benim hidayetime uyarsa, onlara korku yoktur ve onlar mahzun olmayacaklardır."'),
(46, 2, 39, '"İnkar edip de ayetlerimizi yalanlayanlar ise; onlar, ateşin halkıdırlar ve orada süresiz kalacaklardır."'),
(47, 2, 40, 'Ey İsrailoğulları, size bağışladığım nimetimi hatırlayın ve ahdime bağlı kalın, ki Ben de ahdinize bağlı kalayım. Ve yalnızca Benden korkun.'),
(48, 2, 41, 'Yanınızda olan (Tevrat)ı, doğrulayıcı olarak indirdiğime (Kur\'an\'a) iman edin; onu inkar edenlerin ilki siz olmayın ve ayetlerimizi az bir değer karşılığında değişmeyin. Ve yalnızca Benden korkun.'),
(49, 2, 42, 'Hakkı batıl ile örtmeyin ve hakkı gizlemeyin. (Kaldı ki) siz (gerçeği) biliyorsunuz.'),
(50, 2, 43, 'Namazı dosdoğru kılın, zekatı verin ve rüku edenlerle birlikte siz de rüku edin.'),
(51, 2, 44, 'Siz, insanlara iyiliği emrederken, kendinizi unutuyor musunuz? Oysa siz kitabı okuyorsunuz. Yine de akıllanmayacak mısınız?'),
(52, 2, 45, 'Sabır ve namazla yardım dileyin. Bu, şüphesiz, huşû duyanların dışındakiler için ağır (bir yük)dır.'),
(53, 2, 46, 'Onlar, (mü\'minler ise), şüphesiz, Rableriyle karşılaşacaklarını ve (yine) şüphesiz, O\'na döneceklerini bilirler.'),
(54, 2, 47, 'Ey İsrailoğulları, size bağışladığım nimetimi ve sizi (bir dönem) alemlere üstün kıldığımı hatırlayın.'),
(55, 2, 48, 'Ve hiç kimsenin, hiç kimse adına bir şey ödemeyeceği, hiç kimsenin şefaatinin kabul edilmeyeceği, hiç kimseden bir fidye alınmayacağı ve yardım görülmeyeceği bir günden sakının.'),
(56, 2, 49, 'Sizi, dayanılmaz işkencelere uğrattıklarında, Firavun ailesinin elinden kurtardığımızı hatırlayın. Onlar, kadınlarınızı diri bırakıp, erkek çocuklarınızı boğazlıyorlardı. Bunda sizin için Rabbinizden büyük bir imtihan vardı.'),
(57, 2, 50, 'Ve sizin için denizi ikiye yarıp sizi kurtardığımızı ve Firavun\'un adamlarını -gözlerinizin önünde- boğduğumuzu hatırlayın.'),
(58, 2, 51, 'Hani Musa ile kırk gece için sözleşmiştik. Ama sonra siz, onun arkasından buzağıyı (tanrı) edinmiş ve (böylece) zalimler olmuştunuz.'),
(59, 2, 52, 'Bundan sonra, (artık) şükredesiniz diye sizi bağışladık.'),
(60, 2, 53, 'Ve hidayete eresiniz diye Musa\'ya kitabı ve Furkan’ı verdik.'),
(61, 2, 54, 'Hani Musa, kavmine: "Ey kavmim, gerçekten siz, buzağıyı (tanrı) edinmekle kendinize zulmettiniz. Hemen, kusursuzca Yaratan(gerçek İlah)ınıza tevbe edip nefislerinizi öldürün: bu, Yaratıcınız Katında sizin için daha hayırlıdır" demişti. Bunun üzerine (Allah) tevbelerinizi kabul etti. Şüphesiz O tevbeleri kabul edendir, esirgeyendir.'),
(62, 2, 55, 'Ve demiştiniz ki: "Ey Musa, biz Allah\'ı apaçık görünceye kadar sana inanmayız." Bunun üzerine yıldırım sizi (kendinizden) almıştı. Ve siz bakıp duruyordunuz.'),
(63, 2, 56, 'Sonra şükredesiniz diye, sizi ölümünüzden sonra dirilttik.'),
(64, 2, 57, 'Bulutları üzerinize gölge kıldık ve size kudret helvası ve bıldırcın indirdik. Size rızık olarak verdiklerimizin temizinden yiyin (dedik). Onlar Bize zulmetmediler, ancak kendi nefislerine zulmettiler.'),
(65, 2, 58, 'Ve hatırlayın, demiştik ki: "Şu şehre girin ve orada istediğiniz yerde bol bol yiyin, yalnızca secde ederek kapısından girerken \'dileğimiz bağışlanmadır\' deyin; (Biz de) hatalarınızı bağışlayalım; iyilik yapanların (ecirlerini) arttıracağız."'),
(66, 2, 59, 'Ama zulmedenler, kendilerine söylenen sözü bir başkasıyla değiştirdiler. Biz de o zalimlerin yaptıkları bozgunculuğa karşılık, üzerlerine gökten iğrenç bir azap indirdik.'),
(67, 2, 60, '(Yine) Hatırlayın; Musa kavmi için su aramıştı, o zaman Biz ona: "Asanı taşa vur" demiştik de ondan on iki pınar fışkırmıştı, böylece herkes içeceği yeri bilmişti. Allah\'ın verdiği rızıktan yiyin, için ve yeryüzünde bozgunculuk (fesad) yaparak karışıklık çıkarmayın.'),
(68, 2, 61, 'Siz (ise şöyle) demiştiniz: "Ey Musa, biz bir çeşit yemeğe katlanmayacağız, Rabbine yalvar da, bize yerin bitirdiklerinden bakla, acur, sarmısak, mercimek ve soğan çıkarsın." (O zaman Musa:) "Hayırlı olanı, şu değersiz şeyle mi değiştirmek istiyorsunuz? (Öyleyse) Mısır\'a inin, çünkü (orada) kendiniz için istediğiniz vardır" demişti. Onların üzerine horluk ve yoksulluk (damgası) vuruldu ve Allah\'tan bir gazaba uğradılar. Bu, kuşkusuz, Allah\'ın ayetlerini tanımazlıkları ve peygamberleri haksız yere öldürmelerindendi. (Yine) bu, isyan etmelerinden ve sınırı çiğnemelerindendi.'),
(69, 2, 62, 'Şüphesiz, iman edenler(le) Yahudiler, Hıristiyanlar ve Sabiiler(den kim) Allah\'a ve ahiret gününe iman eder ve salih amellerde bulunursa, artık onların Allah Katında ecirleri vardır. Onlara korku yoktur ve onlar mahzun olmayacaklardır.'),
(70, 2, 63, 'Sizden misak almış ve Tur’u üstünüze yükseltmiştik (ve demiştik ki:) "Size verdiğimize sımsıkı yapışın ve onda olanı (hükümleri sürekli) hatırlayın, ki sakınasınız."'),
(71, 2, 64, 'Siz ise, bundan sonra da yüz çevirdiniz. Eğer Allah\'ın üzerinizdeki fazlı (lütuf ve ihsanı) ve rahmeti olmasaydı, siz gerçekten hüsrana uğrayanlardan olurdunuz.'),
(72, 2, 65, 'Andolsun, sizden cumartesi (günü) yasağı çiğneyenleri elbette biliyorsunuz. İşte Biz, onlara: "Aşağılık maymunlar olun" dedik.'),
(73, 2, 66, 'Bunu, hem çağdaşlarına, hem sonra gelecek olanlara \'ibret verici bir ceza\', takva sahipleri için de bir öğüt kıldık.'),
(74, 2, 67, 'Hani Musa kavmine: "Allah, muhakkak sizin bir sığır kesmenizi emrediyor" demişti. "Bizi alaya mı alıyorsun?" dediler. (Musa) "Cahillerden olmaktan Allah\'a sığınırım" dedi.'),
(75, 2, 68, '"Rabbine adımıza yalvar da, bize niteliklerini açıklasın" dediler. (Musa, Rabbine yalvardıktan sonra) "Şüphesiz Allah diyor ki: O ne pek geçkin, ne de pek genç, ikisi arası dinç(likte bir sığır olmalı)dır. Artık emrolunduğunuz şeyi yerine getirin" dedi.'),
(76, 2, 69, '(Bu sefer) dediler ki: "Rabbine adımıza yalvar da, bize rengini bildirsin." O: "(Rabbim) diyor ki: O, bakanların içini ferahlatan sarı bir inektir" dedi.'),
(77, 2, 70, '(Onlar yine:) "Rabbine adımıza yalvar da, bize onun niteliklerini açıklasın. Çünkü bize göre sığırlar birbirine benzer. İnşaAllah (Allah dilerse) biz doğruyu buluruz" dediler.'),
(78, 2, 71, '(Bunun üzerine Musa, “Rabbim) diyor ki: O, yeri sürmek ve ekini sulamak için boyunduruğa alınmayan, salma ve alacası olmayan bir inektir" dedi. (O zaman): "Şimdi gerçeği getirdin” dediler. Böylece ineği kestiler; ama neredeyse (bunu) yapmayacaklardı.'),
(79, 2, 72, 'Hani siz bir kişiyi öldürmüştünüz ve bu konuda birbirinize düşmüştünüz. Oysa Allah, gizlediklerinizi açığa çıkaracaktı.'),
(80, 2, 73, 'Bunun için de: "Ona (cesede, kestiğiniz ineğin) bir parçasıyla vurun" demiştik. Böylece, Allah ölüleri diriltir ve size ayetlerini gösterir; ki akıllanasınız.'),
(81, 2, 74, 'Bundan sonra kalpleriniz yine katılaştı; taş gibi, hatta daha katı. Çünkü taşlardan öyleleri vardır ki, onlardan ırmaklar fışkırır, öyleleri vardır ki yarılır, ondan sular çıkar, öyleleri vardır ki Allah korkusuyla yuvarlanır. Allah yaptıklarınızdan gafil (habersiz) değildir.'),
(82, 2, 75, 'Siz (Müslümanlar,) onların size inanacaklarını umuyor musunuz? Oysa onlardan bir bölümü, Allah\'ın sözünü işitiyor, (iyice algılayıp) akıl erdirdikten sonra, bile bile değiştiriyorlardı.'),
(83, 2, 76, 'İman edenlerle karşılaştıklarında "İman ettik" derler; kendi başlarına kaldıkları zaman ise, derler ki: "Allah\'ın size açtık (açıkladık)larını, Rabbiniz Katında size karşı bir belge olsun diye mi onlarla konuşuyorsunuz? Hala akıllanmayacak mısınız?"'),
(84, 2, 77, '(Peki) Onlar, Allah\'ın gizli tuttuklarını da, açığa vurduklarını da bildiğini bilmiyorlar mı?'),
(85, 2, 78, 'Onlardan bir kısmı ümmidir. Kitabı bilmezler; (bildikleri) bir sürü asılsız şeylerden başkası değildir ve yalnızca zannederler.'),
(86, 2, 79, 'Artık vay hallerine; Kitabı kendi elleriyle yazıp, sonra az bir değer karşılığında satmak için "Bu Allah Katındandır" diyenlere. Artık vay, elleriyle yazdıklarından dolayı onlara; vay kazanmakta olduklarına.'),
(87, 2, 80, 'Dediler ki: "Sayılı günlerin dışında, ateş asla bize değmeyecektir." De ki: "Allah Katından bir ahid mi aldınız? -ki Allah asla ahdinden dönmez- Yoksa Allah\'a karşı bilmediğiniz bir şeyi mi söylüyorsunuz?"'),
(88, 2, 81, 'Hayır; kim bir kötülük işler de günahı kendisini kuşatırsa, (artık) onlar, ateşin halkıdırlar, orada süresiz kalacaklardır.'),
(89, 2, 82, 'İman edip salih amellerde bulunanlar ise cennet halkıdırlar, orada süresiz kalacaklardır.'),
(90, 2, 83, 'Hani İsrailoğulları’ndan, "Allah\'tan başkasına kulluk etmeyin, anneye-babaya, yakınlara, yetimlere ve yoksullara iyilikle davranın, insanlara güzel söz söyleyin, namazı dosdoğru kılın ve zekatı verin" diye misak almıştık. Sonra siz, pek azınız hariç, döndünüz ve (hala) yüz çeviriyorsunuz.'),
(91, 2, 84, 'Hani sizden "Birbirinizin kanını dökmeyin, birbirinizi yurtlarınızdan çıkarmayın" diye misak almıştık. Sonra sizler bunu onaylamıştınız, hala (buna) şahitlik ediyorsunuz.'),
(92, 2, 85, 'Sonra (yine) siz, birbirinizi öldürüyor, bir bölümünüzü yurtlarından sürüp-çıkarıyor ve günah ve düşmanlıkla aleyhlerinde ittifaklar kuruyor ve size esir olarak geldiklerinde onlarla fidyeleşiyordunuz. Oysa onları çıkarmanız, size haram kılınmıştı. Yoksa siz, kitabın bir bölümüne inanıp da bir bölümünü inkar mı ediyorsunuz? Artık sizden böyle yapanların dünya hayatındaki cezası aşağılık olmaktan başka değildir; kıyamet gününde de azabın en şiddetli olanına uğratılacaklardır. Allah, yaptıklarınızdan gafil değildir.'),
(93, 2, 86, 'İşte bunlar, ahireti verip dünya hayatını satın alanlardır; bundan dolayı azapları hafifletilmez ve kendilerine yardım edilmez.'),
(94, 2, 87, 'Andolsun, Biz Musa\'ya kitabı verdik ve ardından peş peşe elçiler gönderdik. Meryem oğlu İsa\'ya da apaçık belgeler verdik ve onu Ruhu\'l-Kudüs\'le teyid ettik. Demek, size ne zaman bir elçi nefsinizin hoşlanmayacağı bir şeyle gelse, büyüklük taslayarak bir kısmınız onu yalanlayacak, bir kısmınız da onu öldürecek misiniz?'),
(95, 2, 88, 'Dediler ki: "Bizim kalplerimiz örtülüdür." Hayır; Allah, inkarlarından dolayı onları lanetlemiştir. Bundan dolayı pek azı iman eder.'),
(96, 2, 89, 'Allah Katından yanlarında olan (Tevrat)ı doğrulayan bir kitap geldiği zaman, -ki bundan önce inkar edenlere karşı fetih istiyorlardı- işte bilip-tanıdıkları gelince, onu inkar ettiler. Artık Allah\'ın laneti kafirlerin üzerinedir.'),
(97, 2, 90, 'Allah\'ın kullarından, dilediğine Kendi fazlından (peygamberliği) indirmesini \'kıskanarak ve hakka baş kaldırarak\' Allah\'ın indirdiklerini tanımamakla, nefislerini ne kötü şeye karşılık sattılar. Böylelikle gazab üstüne gazaba uğradılar. Kafirler için alçaltıcı bir azap vardır.'),
(98, 2, 91, 'Onlara: "Allah\'ın indirdiklerine iman edin" denildiğinde: "Biz, bize indirilene iman ederiz" derler ve ondan sonra olan (Kur\'an)ı inkar ederler. Oysa o (Kur\'an), yanlarındakini (kitabı) doğrulayan bir gerçektir. (Onlara) De ki: "Eğer inanıyor idiyseniz, daha önce ne diye Allah\'ın peygamberlerini öldürüyordunuz?"'),
(99, 2, 92, 'Andolsun, Musa size apaçık belgelerle geldi. Sonra siz onun arkasından buzağıyı (tanrı) edindiniz. İşte siz (böyle) zalimlersiniz.'),
(100, 2, 93, 'Hani sizden misak almış ve Tur\'u üstünüze yükseltmiştik (ve): "Size verdiğimize (kitaba) sımsıkı sarılın ve dinleyin" (demiştik). Demişlerdi ki: "Dinledik ve baş kaldırdık." İnkarları yüzünden buzağı (tutkusu) kalplerine sindirilmişti. De ki: "İnanıyorsanız, inancınız size ne kötü şey emrediyor?"'),
(101, 2, 94, 'De ki: "Eğer Allah Katında ahiret yurdu, başka insanların değil de, yalnızca sizin ise, (ve) doğru sözlüyseniz, öyleyse hemen ölümü dileyin."'),
(102, 2, 95, 'Oysa onlar, önceden ellerinin takdim ettiklerinden dolayı onu (ölümü) hiçbir zaman kesin olarak dilemeyeceklerdir. Allah, zalimleri bilendir.'),
(103, 2, 96, 'Andolsun, onları hayata karşı (diğer) insanlardan ve şirk koşanlardan (bile) daha ihtiraslı bulursun. (Onlardan) Her biri, bin yıl yaşatılsın ister; oysa bunca yaşaması onu azaptan kurtarmaz. Allah, onların yapmakta olduklarını görendir.'),
(104, 2, 97, 'De ki: "Cibril\'e kim düşman ise, (bilsin ki) gerçekten onu (Kitabı), Allah\'ın izniyle kendinden öncekileri doğrulayıcı ve mü\'minler için hidayet ve müjde verici olarak senin kalbine indiren O’dur.'),
(105, 2, 98, 'Her kim Allah\'a, meleklerine, elçilerine, Cibril\'e ve Mikail\'e düşman ise, artık şüphesiz Allah da kafirlerin düşmanıdır."'),
(106, 2, 99, 'Andolsun Biz sana apaçık ayetler indirdik. Bunları fasıklardan başkası inkar etmez.'),
(107, 2, 100, 'Ne zaman bir ahidde bulundularsa, içlerinden bir bölümü onu bozmadı mı? Hayır, onların çoğu iman etmezler.'),
(108, 2, 101, 'Ne zaman onlara Allah Katından yanlarındakini doğrulayan bir elçi gelse, kitap verilenlerden birtakımı, sanki bilmiyorlarmış gibi Allah\'ın Kitabı’nı arkalarına attılar.'),
(109, 2, 102, 'Ve onlar, Süleyman\'ın mülkü (nübüvveti) hakkında şeytanların anlattıklarına uydular. Süleyman inkar etmedi; ancak şeytanlar inkar etti. Onlar, insanlara sihri ve Babil\'deki iki meleğe Harut\'a ve Marut\'a indirileni öğretiyorlardı. Oysa o ikisi: "Biz, yalnızca bir fitneyiz, sakın inkar etme" demedikçe hiç kimseye (bir şey) öğretmezlerdi. Fakat onlardan erkekle karısının arasını açan şeyi öğreniyorlardı. Oysa onunla Allah\'ın izni olmadıkça hiç kimseye zarar veremezlerdi. Buna rağmen kendilerine zarar verecek ve yarar sağlamayacak şeyi öğreniyorlardı. Andolsun onlar, bunu satın alanın, ahiretten hiçbir payı olmadığını bildiler; kendi nefislerini karşılığında sattıkları şey ne kötü; bir bilselerdi.'),
(110, 2, 103, 'Eğer gerçekten iman edip sakınsalardı, Allah Katındaki sevab(ları) gerçekten daha hayırlı olurdu; bir bilselerdi.'),
(111, 2, 104, 'Ey iman edenler, "Raina-Bizi güt, bize bak" demeyin. "Unzurna-Bizi gözet" deyin ve dinleyin. Kafirler için acı bir azap vardır.'),
(112, 2, 105, 'Kitap Ehlinden olan kafirler ve müşrikler, Rabbinizden üzerinize bir hayrın indirilmesini arzu etmezler. Allah ise, dilediğine rahmetini tahsis eder. Allah büyük fazl sahibidir.'),
(113, 2, 106, 'Biz, daha hayırlısını veya bir benzerini getirinceye (kadar) hiçbir ayeti neshetmez (hükmünü yürürlükten kaldırmaz) veya unutturmayız. Bilmez misin ki Allah, gerçekten herşeye güç yetirendir.'),
(114, 2, 107, '(Yine) Bilmez misin ki, gerçekten göklerin ve yerin mülkü Allah\'ındır. Sizin Allah\'tan başka veliniz ve yardımcınız yoktur.'),
(115, 2, 108, 'Yoksa daha önce Musa\'nın sorguya çekildiği gibi, siz de Resulünüzü sorguya mı çekmek istiyorsunuz? Kim imanı inkar ile değişirse, artık o, dümdüz yoldan sapmış olur.'),
(116, 2, 109, 'Kitap Ehlinden çoğu, kendilerine gerçek (hak) apaçık belli olduktan sonra, nefislerini (kuşatan) kıskançlıktan dolayı, imanınızdan sonra sizi inkara döndürmek arzusunu duydular. Fakat, Allah\'ın emri gelinceye kadar onları bırakın ve (onlara ne sözle, ne de eylemle) ilişmeyin. Hiç şüphesiz Allah, herşeye güç yetirendir.'),
(117, 2, 110, 'Namazı dosdoğru kılın, zekatı verin; önceden kendiniz için hayır olarak neyi takdim ederseniz, onu Allah Katında bulacaksınız. Şüphesiz Allah, yaptıklarınızı görendir.'),
(118, 2, 111, 'Dediler ki: "Yahudi veya Hıristiyan olmayan hiç kimse kesin olarak cennete giremez." Bu, onların kendi kuruntularıdır. De ki: "Eğer doğru sözlüyseniz, kesin-kanıtınızı (burhan) getirin."'),
(119, 2, 112, 'Hayır, kim (güzel davranış ve) iyilikte bulunarak kendisini Allah\'a teslim ederse, artık onun Rabbi Katında ecri vardır. Onlar için korku yoktur ve onlar mahzun olmayacaklardır.'),
(120, 2, 113, 'Yahudiler dediler ki: "Hıristiyanlar bir şey (herhangi bir temel) üzere değillerdir"; Hıristiyanlar da: "Yahudiler bir şey üzere değillerdir" dediler. Oysa onlar, Kitabı okuyorlar. Bilmeyenler (bilgisizler) de, onların söylediklerinin benzerini söylemişlerdi. Artık Allah, kıyamet günü anlaşmazlığa düştükleri şeyde aralarında hüküm verecektir.'),
(121, 2, 114, 'Allah\'ın mescidlerinde O\'nun isminin anılmasını engelleyen ve bunların yıkılmasına çaba harcayandan daha zalim kim olabilir? Onların (durumu) içlerine korkarak girmekten başkası değildir. Onlar için dünyada bir aşağılanma, ahirette büyük bir azap vardır.'),
(122, 2, 115, 'Doğu da Allah\'ındır, batı da. Her nereye dönerseniz Allah\'ın yüzü (kıblesi) orasıdır. Şüphesiz ki Allah, kuşatandır, bilendir.'),
(123, 2, 116, 'Dediler ki: "Allah oğul edindi." O, (bu yakıştırmadan) Yücedir. Hayır, göklerde ve yerde her ne varsa O\'nundur, tümü O\'na gönülden boyun eğmişlerdir.'),
(124, 2, 117, 'Gökleri ve yeri (bir örnek edinmeksizin) yaratandır. O, bir işin olmasına karar verirse, ona yalnızca "OL" der, o da hemen oluverir.'),
(125, 2, 118, 'Bilgisizler, dediler ki: "Allah bizimle konuşmalı veya bize de bir ayet gelmeli değil miydi?" Onlardan öncekiler de onların bu söylediklerinin benzerini söylemişlerdi. Kalpleri birbirine benzedi. Biz, kesin bilgiyle inanan bir topluluğa ayetleri apaçık gösterdik.'),
(126, 2, 119, 'Şüphesiz Biz seni bir müjdeci ve bir uyarıcı olarak, hak (Kur\'an) ile gönderdik. Sen cehennemin halkından sorumlu tutulmayacaksın.'),
(127, 2, 120, 'Sen onların dinlerine uymadıkça, Yahudi ve Hıristiyanlar senden kesinlikle hoşnut olacak değillerdir. De ki: "Şüphesiz doğru yol, Allah\'ın (gösterdiği) yoludur." Eğer sana gelen bunca ilimden sonra onların heva (arzu ve tutku)larına uyacak olursan, senin için Allah\'tan ne bir dost vardır, ne de bir yardımcı.'),
(128, 2, 121, 'Kendilerine verdiğimiz Kitabı gereği gibi okuyanlar, işte ona iman edenler bunlardır. Kim de onu inkar ederse, artık onlar hüsrana uğrayanların ta kendileridir.'),
(129, 2, 122, 'Ey İsrailoğulları, size bağışladığım nimetimi ve sizi (bir dönem) alemlere muhakkak üstün kıldığımı hatırlayın.'),
(130, 2, 123, 'Ve hiç kimsenin hiç kimse adına bir şey ödeyemeyeceği, hiç kimseden fidye alınmayacağı ve hiç kimsenin şefaatinin kabul edilmeyeceği ve yardım görülmeyeceği bir günden sakının.'),
(131, 2, 124, 'Hani Rabbi, İbrahim\'i birtakım kelimelerle denemişti. O da (istenenleri) tam olarak yerine getirmişti. (O zaman Allah İbrahim\'e): "Seni şüphesiz insanlara imam kılacağım" dedi. (İbrahim) "Ya soyumdan olanlar?" deyince (Allah:) "Zalimler Benim ahdime erişemez" dedi.'),
(132, 2, 125, 'Hani Evi (Ka\'be’yi) insanlar için bir toplanma ve güvenlik yeri kılmıştık. "İbrahim\'in makamını namaz yeri edinin", İbrahim ve İsmail\'e de, "Evimi, tavaf edenler, itikafa çekilenler ve rüku ve secde edenler için temizleyin" diye ahid verdik.'),
(133, 2, 126, 'Hani İbrahim: "Rabbim, bu şehri bir güvenlik yeri kıl ve halkından Allah\'a ve ahiret gününe inananları ürünlerle rızıklandır" demişti de (Allah: “Sadece inananları değil) inkar edeni de az bir süre yararlandırır, sonra onu ateşin azabına uğratırım; ne kötü bir dönüştür o" demişti.'),
(134, 2, 127, 'İbrahim, İsmail\'le birlikte Evin (Ka\'be\'nin) sütunlarını yükselttiğinde (ikisi şöyle dua etmişti): "Rabbimiz bizden (bunu) kabul et. Şüphesiz, Sen işiten ve bilensin";'),
(135, 2, 128, '"Rabbimiz, ikimizi Sana teslim olmuş (Müslümanlar) kıl ve soyumuzdan Sana teslim olmuş (Müslüman) bir ümmet (ver). Bize ibadet yöntemlerini (yer veya ilkelerini) göster ve tevbemizi kabul et. Şüphesiz, Sen tevbeleri kabul eden ve esirgeyensin."'),
(136, 2, 129, '"Rabbimiz, içlerinden onlara bir elçi gönder, onlara ayetlerini okusun, Kitabı ve hikmeti öğretsin ve onları arındırsın. Şüphesiz, Sen güçlü ve üstün olansın, hüküm ve hikmet sahibisin."'),
(137, 2, 130, 'Kendi nefsini aşağılık kılandan başka, İbrahim\'in dininden kim yüz çevirir? Andolsun, Biz onu dünyada seçtik, gerçekten ahirette de o salihlerdendir.'),
(138, 2, 131, 'Rabbi ona: "Teslim ol" dediğinde (O:) "Alemlerin Rabbine teslim oldum" demişti.'),
(139, 2, 132, 'Bunu İbrahim, oğullarına vasiyet etti, Yakup da: "Oğullarım, şüphesiz Allah sizlere bu dini seçti, siz de ancak Müslüman olarak can verin" (diye benzer bir vasiyette bulundu.)'),
(140, 2, 133, 'Yoksa siz, Yakub\'un ölüm anında, orada şahidler miydiniz? O, oğullarına: "Benden sonra kime ibadet edeceksiniz?" dediğinde, onlar: "Senin İlahına ve ataların İbrahim, İsmail ve İshak\'ın İlahı olan tek bir İlaha ibadet edeceğiz; bizler O\'na teslim olduk" demişlerdi.'),
(141, 2, 134, 'Onlar bir ümmetti; gelip geçti. Onların kazandıkları kendilerinin, sizin kazandıklarınız sizindir. Siz, onların yaptıklarından sorumlu değilsiniz.'),
(142, 2, 135, 'Dediler ki: "Yahudi veya Hıristiyan olun ki hidayete eresiniz." De ki: "Hayır, (doğru yol) Hanif (muvahhid) olan İbrahim\'in dini(dir); O müşriklerden değildi."'),
(143, 2, 136, 'Deyin ki: "Biz Allah\'a; bize indirilene, İbrahim, İsmail, İshak, Yakub ve torunlarına indirilene, Musa ve İsa\'ya verilen ile peygamberlere Rabbinden verilene iman ettik. Onlardan hiçbirini diğerinden ayırt etmeyiz ve biz O\'na teslim olmuşlarız."'),
(144, 2, 137, 'Şayet onlar da, sizin inandığınız gibi inanırlarsa, kuşkusuz doğru yolu bulmuş olurlar; yok eğer yüz çevirirlerse, onlar elbette bir (çelişki ve) aykırılık içindedirler. Sana onlara karşı Allah yeter. O, işitendir, bilendir.'),
(145, 2, 138, 'Allah\'ın boyası... Allah(ın boyasın)dan daha güzel boyası olan kimdir? Biz (yalnızca) O\'na kulluk edenleriz.'),
(146, 2, 139, 'De ki: "O bizim de Rabbimiz, sizin de Rabbiniz iken, bizimle Allah hakkında (sözde kanıtlarla) tartışmalara mı giriyorsunuz? Bizim amellerimiz bizim, sizin de amelleriniz sizindir. Biz, O\'na gönülden bağlanmış (muhlis) olanlarız."'),
(147, 2, 140, 'Yoksa siz, gerçekten İbrahim\'in, İsmail\'in, İshak\'ın, Yakub\'un ve torunlarının Yahudi veya Hıristiyan olduklarını mı söylüyorsunuz? De ki: "Siz mi daha iyi biliyorsunuz, yoksa Allah mı? Allah\'tan kendisinde olan bir şehadeti gizleyenden daha zalim olan kimdir? Allah, yaptıklarınızdan gafil değildir."'),
(148, 2, 141, 'Onlar, bir ümmetti, gelip geçti; onların kazandıkları kendilerinin, sizin kazandıklarınız sizindir. Siz, onların yaptıklarından sorumlu değilsiniz.'),
(149, 2, 142, 'Birtakım beyinsiz insanlar: "Onları daha önceki kıblelerinden çeviren nedir?" diyecekler. De ki: "Doğu da Allah\'ındır, batı da. O dilediğini doğru yola yöneltir."'),
(150, 2, 143, 'Böylece Biz sizi, insanlara şahid (ve örnek) olmanız için orta bir ümmet kıldık; Peygamber de üzerinizde bir şahid olsun. Senin üzerinde bulunduğun (yönü, Ka\'be\'yi) kıble yapmamız, elçiye uyanları, topukları üzerinde gerisin geri dönenlerden ayırt etmek içindir. Doğrusu (bu,) Allah\'ın hidayete ilettiklerinin dışında kalanlar için büyük (bir yük)tür. Allah, imanınızı boşa çıkaracak değildir. Şüphesiz, Allah, insanlara şefkat edendir, esirgeyendir.'),
(151, 2, 144, 'Biz, senin yüzünü çok defa göğe doğru çevirip-durduğunu görüyoruz. Şimdi elbette seni hoşnut olacağın kıbleye çevireceğiz. Artık yüzünü Mescid-i Haram yönüne çevir. Her nerede bulunursanız, yüzünüzü onun yönüne çevirin. Şüphesiz, kendilerine kitap verilenler, tartışmasız bunun Rablerinden bir gerçek (hak) olduğunu elbette bilirler. Allah, yaptıklarınızdan gafil değildir.'),
(152, 2, 145, 'Andolsun, kendilerine kitap verilenlere her ayeti (delili) getirsen, yine onlar senin kıblene uymaz; sen de onların kıblelerine uyacak değilsin. Onlardan bir kısmı, bir kısmının kıblesine (bile) uymaz. Andolsun, eğer sana gelen bunca ilimden sonra onların heva (istek ve tutku)larına uyacak olursan, o zaman gerçekten zalimlerden olursun.'),
(153, 2, 146, 'Kendilerine kitap verdiklerimiz, onu (peygamberi), çocuklarını tanır gibi tanırlar. Buna rağmen içlerinden bir bölümü, bildikleri halde gerçeği gizlerler.'),
(154, 2, 147, 'Gerçek (hak) Rabbinden (gelen)dir. Şu halde sakın kuşkuya kapılanlardan olma.'),
(155, 2, 148, 'Herkesin (her toplumun) yüzünü çevirdiği bir yön vardır. Öyleyse hayırlarda yarışınız. Her nerede olursanız, Allah sizleri biraraya getirecektir. Şüphesiz Allah, herşeye güç yetirendir.'),
(156, 2, 149, 'Her nereden çıkarsan, yüzünü Mescid-i Haram yönüne çevir. Şüphesiz bu, Rabbinden olan bir haktır. Allah, yaptıklarınızdan gafil değildir.'),
(157, 2, 150, 'Her nereden çıkarsan, yüzünü Mescid-i Haram yönüne çevir. (Siz de) Her nerede olursanız yüzünüzü onun yönüne çevirin. Öyle ki, onlardan zulmedenlerin dışında insanların, size karşı bir delilleri olmasın. Onlardan korkmayın, Benden korkun, üzerinizdeki nimetimi tamamlayayım. Umulur ki hidayete erersiniz.'),
(158, 2, 151, 'Öyle ki size, kendinizden, size ayetlerimizi okuyacak, sizi arındıracak, size kitap ve hikmeti öğretecek ve bilmediklerinizi bildirecek bir elçi gönderdik.'),
(159, 2, 152, 'Öyleyse (yalnızca) Beni anın, Ben de sizi anayım; ve (yalnızca) Bana şükredin ve (sakın) nankörlük etmeyin.'),
(160, 2, 153, 'Ey iman edenler, sabırla ve namazla yardım dileyin. Gerçekten Allah, sabredenlerle beraberdir.'),
(161, 2, 154, 'Ve sakın Allah yolunda öldürülenlere "ölüler" demeyin; hayır onlar diridirler. Fakat siz bunun şuurunda değilsiniz.'),
(162, 2, 155, 'Andolsun, Biz sizi biraz korku, açlık ve bir parça mallardan, canlardan ve ürünlerden eksiltmekle imtihan edeceğiz. Sabır gösterenleri müjdele.'),
(163, 2, 156, 'Onlara bir musibet isabet ettiğinde, derler ki: "Biz Allah\'a ait (kullar)ız ve şüphesiz O\'na dönücüleriz."'),
(164, 2, 157, 'Rablerinden bağışlanma (salat) ve rahmet bunların üzerinedir ve hidayete erenler de bunlardır.'),
(165, 2, 158, 'Şüphesiz, \'Safa\' ile \'Merve\' Allah\'ın işaretlerindendir. Böylece kim Evi (Ka\'be\'yi) hacceder veya umre yaparsa, artık bu ikisini tavaf etmesinde kendisi için bir sakınca yoktur. Kim de gönülden bir hayır yaparsa (karşılığını alır). Şüphesiz Allah, şükrün karşılığını verendir, bilendir.'),
(166, 2, 159, 'Gerçekten, apaçık belgelerden indirdiklerimizi ve insanlar için kitapta açıkladığımız hidayeti gizlemekte olanlar; işte onlara, hem Allah lanet eder, hem de (bütün) lanet ediciler.'),
(167, 2, 160, 'Ancak tevbe edenler, (kendilerini ve başkalarını) düzeltenler ve (indirileni) açıklayanlar(a gelince); artık onların tevbelerini kabul ederim. Ben, tevbeleri kabul edenim, esirgeyenim.'),
(168, 2, 161, 'Şüphesiz, inkar edip kafir olarak ölenler, Allah\'ın, meleklerin ve bütün insanların laneti bunların üzerinedir.'),
(169, 2, 162, 'Onda (lanette) süresiz kalacaklardır, onlardan azap hafifletilmez ve onlar gözetilmezler.'),
(170, 2, 163, 'Sizin İlahınız tek bir İlah\'tır; O\'ndan başka İlah yoktur; O, Rahman’dır, Rahim’dir (bağışlayan ve esirgeyendir).'),
(171, 2, 164, 'Şüphesiz, göklerin ve yerin yaratılmasında, gece ile gündüzün art arda gelişinde, insanlara yararlı şeyler ile denizde yüzen gemilerde, Allah\'ın yağdırdığı ve kendisiyle yeryüzünü ölümünden sonra dirilttiği suda, her canlıyı orada üretip-yaymasında, rüzgarları estirmesinde, gökle yer arasında boyun eğdirilmiş bulutları evirip çevirmesinde düşünen bir topluluk için gerçekten ayetler vardır.'),
(172, 2, 165, 'İnsanlar içinde, Allah\'tan başkasını \'eş ve ortak\' tutanlar vardır ki, onlar (bunları), Allah\'ı sever gibi severler. İman edenlerin ise Allah\'a olan sevgileri daha güçlüdür. O zulmedenler, azaba uğrayacakları zaman, muhakkak bütün kuvvetin tümüyle Allah\'ın olduğunu ve Allah\'ın vereceği azabın gerçekten şiddetli olduğunu bir bilselerdi.'),
(173, 2, 166, 'Öyle ki (o gün) kendilerine tabi olunanlar, kendilerine tabi olanlardan uzaklaşıp-kaçmışlardır. (Artık) Onlar azabı görmüşlerdir ve aralarındaki bütün bağlar (ve ilişkiler) de parçalanıp-kopmuştur.'),
(174, 2, 167, '(O zaman, yönetilip) Uyanlar derler ki: "Eğer bize bir kere (daha dünyaya dönme) fırsatı verilse(ydi) muhakkak (şimdi) onların bizden uzaklaştıkları gibi, biz de onlardan uzaklaşır (onları yüzüstü bırakır)dık." Böylece Allah, onlara bütün yaptıklarını onulmaz hasretlerle gösterecektir. Ve onlar ateşten çıkacak değildirler.'),
(175, 2, 168, 'Ey insanlar, yeryüzünde olan şeyleri helal ve temiz olarak yiyin ve şeytanın adımlarını izlemeyin. Gerçekte o, sizin için apaçık bir düşmandır.'),
(176, 2, 169, 'O, size yalnızca, kötülüğü, çirkin-hayasızlığı ve Allah\'a karşı bilmediğiniz şeyleri söylemenizi emreder.'),
(177, 2, 170, 'Ne zaman onlara: "Allah\'ın indirdiklerine uyun" denilse, onlar: "Hayır, biz, atalarımızı üzerinde bulduğumuz şeye (geleneğe) uyarız" derler. (Peki) Ya atalarının aklı bir şeye ermez ve doğru yolu da bulamamış idiyseler?'),
(178, 2, 171, 'İnkar edenlerin örneği bağırıp çağırmadan başka bir şey işitmeyip (duyduğu veya bağırdığı şeyin anlamını bilmeyen ve sürekli) haykıran (bir hayvan)ın örneği gibidir. Onlar, sağırdırlar, dilsizdirler, kördürler; bundan dolayı akıl erdiremezler.'),
(179, 2, 172, 'Ey iman edenler size rızık olarak verdiklerimizin temiz olanlarından yiyin ve yalnızca O\'na kulluk ediyorsanız, (yine yalnızca) Allah\'a şükredin.'),
(180, 2, 173, 'O, size ölüyü (leşi)- kanı, domuz etini ve Allah\'tan başkası adına kesilmiş olan (hayvan)ı kesin olarak haram kıldı. Fakat kim kaçınılmaz olarak muhtaç kalırsa, taşkınlık yapmamak ve haddi aşmamak şartıyla (ölmeyecek oranda yiyebilir), ona bir günah yoktur. Gerçekten Allah, bağışlayandır, esirgeyendir.'),
(181, 2, 174, 'Allah\'ın indirdiği Kitap\'tan bir şeyi göz ardı edip saklayanlar ve onunla değeri az (bir şeyi) satın alanlar; onların yedikleri, karınlarında ateşten başkası değildir. Allah kıyamet günü onlarla konuşmaz ve onları arındırmaz. Ve onlar için acı bir azap vardır.'),
(182, 2, 175, 'Onlar, hidayete karşılık sapıklığı, bağışlanmaya karşılık azabı satın almışlardır. Ateşe karşı ne kadar dayanıklıdırlar!'),
(183, 2, 176, 'Bu, Allah\'ın Kitabı şüphesiz hak olarak indirmesindendir. Kitap konusunda anlaşmazlığa düşenler ise uzak bir ayrılık içindedirler.'),
(184, 2, 177, 'Yüzlerinizi doğuya ve batıya çevirmeniz iyilik değildir. Ama iyilik, Allah\'a, ahiret gününe, meleklere, Kitaba ve peygamberlere iman eden; mala olan sevgisine rağmen, onu yakınlara, yetimlere, yoksullara, yolda kalmışa, isteyip-dilenene ve kölelere (özgürlükleri için) veren; namazı dosdoğru kılan, zekatı veren ve ahidleştiklerinde ahidlerine vefa gösterenler ile zorda, hastalıkta ve savaşın kızıştığı zamanlarda sabredenler(in tutum ve davranışlarıdır). İşte bunlar, doğru olanlardır ve müttaki olanlar da bunlardır.'),
(185, 2, 178, 'Ey iman edenler, öldürülenler hakkında size kısas yazıldı (farz kılındı). Özgüre karşı özgür, köleye karşı köle ve dişiye karşı dişi. Fakat kimin (hangi katilin) lehine, onun (maktulün) kardeşi (varisi veya velisi) tarafından bağışlanırsa, artık (yapılması gereken) örfe uymak (ve) ona (maktulün varis veya velisine) güzellikle (diyet) ödemektir. Bu, Rabbinizden bir hafifletme ve bir rahmettir. Artık kim bundan sonra tecavüzde bulunursa, onun için elem verici bir azap vardır.'),
(186, 2, 179, 'Ey temiz akıl sahipleri, kısasta sizin için hayat vardır. Umulur ki sakınırsınız.'),
(187, 2, 180, 'Sizden birinize ölüm gelip çattığı zaman, eğer geride bir hayır bırakmışsa, anaya, babaya ve yakın akrabaya bilinen (uygun, meşru) bir tarzda vasiyette bulunması -Allah\'a karşı gelmekten sakınanlara bir hak olarak- size yazıldı (farz kılındı).'),
(188, 2, 181, 'Bundan böyle kim onu (vasiyeti) işittikten sonra değiştirirse, günahı elbette onu değiştirenlerin üzerinedir. Şüphesiz Allah, işitendir, bilendir.'),
(189, 2, 182, 'Bunun yanında, kim, vasiyet edenin haksızlığa eğilim göstereceğinden ya da günaha gireceğinden korkup da ikisinin (tarafların) arasını bulup-düzeltirse, artık ona günah yoktur. Gerçekten Allah, bağışlayandır, esirgeyendir.'),
(190, 2, 183, 'Ey iman edenler, sizden öncekilere yazıldığı gibi, oruç, size de yazıldı (farz kılındı). Umulur ki sakınırsınız.'),
(191, 2, 184, '(Oruç) Sayılı günlerdir. Artık sizden kim hasta ya da yolculukta olursa tutamadığı günler sayısınca başka günlerde (tutsun). Zor dayanabilenlerin üzerinde bir yoksulu doyuracak kadar fidye (vardır). Kim gönülden bir hayır yaparsa bu da kendisi için hayırlıdır. Oruç tutmanız, -eğer bilirseniz- sizin için daha hayırlıdır.'),
(192, 2, 185, 'Ramazan ayı... İnsanlar için hidayet olan ve doğru yolu ve (hak ile batılı birbirinden) ayıran apaçık belgeleri (kapsayan) Kur\'an onda indirilmiştir. Öyleyse sizden kim bu aya şahid olursa artık onu tutsun. Kim hasta ya da yolculukta olursa, tutmadığı günler sayısınca diğer günlerde (tutsun). Allah, size kolaylık diler, zorluk dilemez. (Bu kolaylık) sayıyı tamamlamanız ve sizi doğru yola (hidayete) ulaştırmasına karşılık Allah\'ı büyük tanımanız içindir. Umulur ki şükredersiniz.'),
(193, 2, 186, 'Kullarım Beni sana soracak olursa, muhakkak ki Ben (onlara) pek yakınım. Bana dua ettiği zaman dua edenin duasına cevap veririm. Öyleyse, onlar da Benim çağrıma cevap versinler ve Bana iman etsinler. Umulur ki irşad (doğru yolu bulmuş) olurlar.'),
(194, 2, 187, 'Oruç gecesinde kadınlarınıza yaklaşmak size helal kılındı. Onlar, sizin örtüleriniz, siz de onlara örtüsünüz. Allah, gerçekten sizin, nefislerinize ihanet etmekte olduğunuzu bildi, tevbenizi kabul etti ve sizi bağışladı. Artık onlara yaklaşın ve Allah\'ın sizin için yazdıklarını dileyin. Fecir vakti, sizce beyaz iplik siyah iplikten ayırt edilinceye kadar yiyin, için, sonra geceye kadar orucu tamamlayın. Mescidlerde itikafta olduğunuz zamanlarda onlara (kadınlarınıza) yaklaşmayın. Bunlar, Allah\'ın sınırlarıdır, (sakın) onlara yanaşmayın. İşte Allah, insanlara ayetlerini böylece açıklar; umulur ki sakınırlar.'),
(195, 2, 188, 'Birbirinizin mallarını haksızlıkla yemeyin ve bile bile günahla insanların mallarından bir bölümünü yemeniz için onları hakimlere aktarmayın.'),
(196, 2, 189, 'Sana, hilalleri (doğuş halindeki ayları) sorarlar. De ki: "O, insanlar ve hacc için belirlenmiş vakitlerdir. İyilik (birr), evlere arkalarından gelmeniz değildir, ama iyilik sakınan(ın tutumudur). Evlere kapılarından girin. Allah\'tan sakının, umulur ki kurtuluşa erersiniz.'),
(197, 2, 190, 'Sizinle savaşanlara karşı Allah yolunda savaşın, (ancak) aşırı gitmeyin. Elbette Allah aşırı gidenleri sevmez.'),
(198, 2, 191, 'Onları, bulduğunuz yerde öldürün ve sizi çıkardıkları yerden siz de onları çıkarın. Fitne, öldürmekten beterdir. Onlar, size karşı savaşıncaya kadar siz, Mescid-i Haram yanında onlarla savaşmayın. Sizinle savaşırlarsa siz de onlarla savaşın. Kafirlerin cezası işte böyledir.'),
(199, 2, 192, 'Onlar, (savaşa) son verirlerse (siz de son verin); şüphesiz Allah, bağışlayandır esirgeyendir.'),
(200, 2, 193, '(Yeryüzünde) Fitne kalmayıncaya kadar onlarla savaşın. Eğer vazgeçerlerse, artık zulüm yapanlardan başkasına karşı düşmanlık yoktur.'),
(201, 2, 194, 'Haram ay, haram aya karşılıktır; hürmetler (de) karşılıklıdır. Öyleyse kim size saldırırsa, onun saldırdığı gibi siz de ona saldırın. Allah\'tan korkup-sakının ve bilin ki Allah, muhakkak ki korkup-sakınanlarla beraberdir.'),
(202, 2, 195, 'Allah yolunda infak edin ve kendinizi kendi ellerinizle tehlikeye atmayın. İyilik edin. Şüphesiz Allah, iyilik edenleri sever.'),
(203, 2, 196, 'Haccı ve umreyi Allah için tamamlayın. Eğer (düşman, hastalık ve buna benzer nedenlerle) kuşatılırsanız, artık size kolay gelen kurban(ı gönderin). Kurban yerine varıncaya kadar başlarınızı traş etmeyin. Kim sizden hasta ise veya başından şikayeti varsa, onun ya oruç ya sadaka veya kurban olarak fidye (vermesi gerekir). Güvenliğe kavuşursanız, hacca kadar umre ile yararlanmak isteyene, kolayına gelen bir kurban(ı kesmek gerekir). Bulamayana da, haccda üç gün, döndüğünüzde yedi (gün) olmak üzere, bunlar, tamı tamına on (gün) oruç vardır. Bu, ailesi Mescid-i Haram\'da olmayanlar içindir. Allah\'tan korkun ve bilin ki Allah, muhakkak cezası pek çetin olandır.'),
(204, 2, 197, 'Hacc, bilinen aylardır. Böylelikle kim onlarda haccı farz eder (yerine getirir)se, (bilsin ki) haccda kadına yaklaşmak, fısk yapmak ve kavgaya girişmek yoktur. Siz, hayır adına ne yaparsanız, Allah, onu bilir. Azık edinin, şüphesiz azığın en hayırlısı takvadır. Ey temiz akıl sahipleri, Benden korkup-sakının.'),
(205, 2, 198, 'Rabbinizden bir fazl istemenizde sizce sakınca yoktur. Arafat\'tan hep birlikte indiğinizde Allah\'ı Meş\'ar-ı Haram\'da anın. O, sizi nasıl doğru yola yöneltip-ilettiyse, siz de O\'nu anın. Gerçek şu ki, siz bundan evvel sapmışlardandınız.'),
(206, 2, 199, 'Sonra insanların (topluca) akın ettiği yerden siz de akın edin ve Allah\'tan bağışlanma dileyin. Şüphesiz Allah bağışlayandır, esirgeyendir.'),
(207, 2, 200, '(Hacc) ibadetlerinizi bitirdiğinizde, artık (cahiliye döneminde) atalarınızı andığınız gibi, hatta ondan da kuvvetli bir anma ile Allah\'ı anın. İnsanlardan öylesi vardır ki: "Rabbimiz, bize dünyada ver" der; onun ahirette nasibi yoktur.'),
(208, 2, 201, 'Onlardan öylesi de vardır ki: "Rabbimiz, bize dünyada da iyilik ver, ahirette de iyilik (ver) ve bizi ateşin azabından koru" der.'),
(209, 2, 202, 'İşte bunların kazandıklarına karşılık nasibleri vardır. Allah, hesabı pek seri görendir.'),
(210, 2, 203, 'Sayılı günlerde Allah\'ı anın. İki günde (Mina\'dan dönmek için) elini çabuk tutana günah yoktur, geri kalana da günah yoktur. (Bu) sakınan için(dir). Allah\'tan korkup-sakının ve gerçekten bilin ki, siz O\'na döndürülüp-toplanacaksınız.'),
(211, 2, 204, 'İnsanlardan öylesi vardır ki, dünya hayatına ilişkin sözleri senin hoşuna gider ve kalbindekine rağmen Allah\'ı şahid getirir; oysa o azılı bir düşmandır.'),
(212, 2, 205, 'O, iş başına geçti mi (ya da sırtını çevirip gitti mi) yeryüzünde bozgunculuk çıkarmaya, ekini ve nesli helak etmeye çaba harcar. Allah ise, bozgunculuğu sevmez.'),
(213, 2, 206, 'Ona: "Allah\'tan kork" denildiğinde, büyüklük gururu onu günaha sürükler, kuşatır. Böylesine cehennem yeter; ne kötü bir yataktır o.'),
(214, 2, 207, 'İnsanlardan öylesi vardır ki, Allah\'ın rızasını ara(yıp kazan)mak amacıyla nefsini satın alır. Allah, kullarına karşı şefkatli olandır.'),
(215, 2, 208, 'Ey iman edenler, hepiniz topluca "barış ve güvenliğe (Silm\'e, İslam\'a) girin ve şeytanın adımlarını izlemeyin. Çünkü o, size apaçık bir düşmandır.'),
(216, 2, 209, 'Size, apaçık belgeler (ayetler) geldikten sonra yine ayağınız kayarsa, bilin ki Allah, gerçekten üstün ve güçlüdür, hüküm ve hikmet sahibidir.'),
(217, 2, 210, 'Onlar, bulut gölgeleri içinde Allah\'ın (azabının) meleklerle onlara gelmesini ve (azap) emrinin gerçekleşmesini mi gözlüyorlar? Oysa bütün işler Allah\'a döner.'),
(218, 2, 211, 'İsrailoğulları’na sor, onlara nice açık ayet(ler) verdik. Kendisine geldikten sonra kim Allah\'ın nimetini değiştirirse, (bilsin ki) şüphesiz Allah, cezası pek şiddetli olandır.'),
(219, 2, 212, 'İnkar edenlere dünya hayatı çekici kılındı (süslendi). Onlar, iman edenlerden kimileriyle alay ederler. Oysa korkup sakınanlar, kıyamet günü onların üstündedir. Allah, dilediğine hesapsız rızık verir.'),
(220, 2, 213, 'İnsanlar tek bir ümmetti. Allah, müjdeciler ve uyarıcılar olarak peygamberler gönderdi ve beraberlerinde, insanların anlaşmazlığa düştükleri şeyler konusunda, aralarında hüküm vermek üzere hak kitaplar indirdi. Oysa kendilerine apaçık ayetler geldikten sonra, birbirlerine karşı olan \'azgınlık ve kıskançlıkları’ yüzünden anlaşmazlığa düşenler, o, (kitap) verilenlerden başkası değildir. Böylece Allah, iman edenleri, hakkında ayrılığa düştükleri gerçeğe Kendi izniyle eriştirdi. Allah, kimi dilerse onu doğruya yöneltir.'),
(221, 2, 214, 'Yoksa sizden önce gelip-geçenlerin hali başınıza gelmeden cennete gireceğinizi mi sandınız? Onlara öyle bir yoksulluk, öyle dayanılmaz bir zorluk çattı ve öylesine sarsıldılar ki, sonunda elçi, beraberindeki mü\'minlerle; "Allah\'ın yardımı ne zaman?" diyordu. Dikkat edin. Şüphesiz Allah\'ın yardımı pek yakındır.'),
(222, 2, 215, 'Sana neyi infak edeceklerini sorarlar. De ki: "Hayır olarak infak edeceğiniz şey, anne-babaya, yakınlara, yetimlere, yoksullara ve yolda kalmışadır. Hayır olarak her ne yaparsanız, Allah onu şüphesiz bilir."'),
(223, 2, 216, 'Savaş, hoşunuza gitmediği halde üzerinize yazıldı (farz kılındı). Olur ki hoşunuza gitmeyen bir şey, sizin için hayırlıdır ve olur ki, sevdiğiniz şey de sizin için bir şerdir. Allah bilir de siz bilmezsiniz.'),
(224, 2, 217, 'Sana haram olan ayı, onda savaşmayı sorarlar. De ki: "Onda savaşmak büyük (bir günahtır). Ancak Allah Katında, Allah\'ın yolundan alıkoymak, onu inkar etmek, Mescid-i Haram\'a engel olmak ve halkını oradan çıkarmak daha büyük (bir günahtır). Fitne, katilden beterdir. Eğer güç yetirirlerse, sizi dininizden geri çevirinceye kadar sizinle savaşmayı sürdürürler; sizden kim dininden geri döner ve kafir olarak ölürse, artık onların bütün işledikleri (amelleri) dünyada da, ahirette de boşa çıkmıştır ve onlar ateşin halkıdır, onda süresiz kalacaklardır.'),
(225, 2, 218, 'Şüphesiz iman edenler, hicret edenler ve Allah yolunda cihad edenler; işte onlar, Allah\'ın rahmetini umabilirler. Allah bağışlayandır, esirgeyendir.'),
(226, 2, 219, 'Sana içkiyi ve kumarı sorarlar. De ki: "Onlarda hem büyük günah, hem insanlar için (bazı) yararlar vardır. Ama günahları yararlarından daha büyüktür." Ve sana neyi infak edeceklerini sorarlar. De ki: "İhtiyaçtan artakalanı." Böylece Allah, size ayetlerini açıklar; umulur ki düşünürsünüz;'),
(227, 2, 220, 'Hem dünya (konusun)da, hem ahiret (konusunda). Ve sana yetimleri sorarlar. De ki: "Onları ıslah etmek (yararlı kılmak) hayırlıdır. Eğer onları aranıza katarsanız, artık onlar sizin kardeşlerinizdir. Allah bozgun (fesad) çıkaranı ıslah ediciden bilir (ayırt eder). Eğer Allah dileseydi size güçlük çıkarırdı. Şüphesiz Allah güçlü ve üstün olandır, hüküm ve hikmet sahibidir."'),
(228, 2, 221, 'Müşrik kadınları, iman edinceye kadar nikahlamayın; iman eden bir cariye, -hoşunuza gitse de- müşrik bir kadından daha hayırlıdır. Müşrik erkekleri de iman edinceye kadar nikahlamayın; iman eden bir köle, -hoşunuza gitse de- müşrik bir erkekten daha hayırlıdır. Onlar, ateşe çağırırlar, Allah ise Kendi izniyle cennete ve mağfirete çağırır. O, insanlara ayetlerini açıklar. Umulur ki öğüt alıp-düşünürler.'),
(229, 2, 222, 'Sana \'kadınların aybaşı halini\' sorarlar. De ki: "O, bir rahatsızlık (eza)dır. Aybaşı halinde kadınlardan ayrılın ve temizlenmelerine kadar onlara (cinsel anlamda) yaklaşmayın. Temizlendiklerinde, Allah\'ın size emrettiği yerden onlara gidin. Şüphesiz Allah, tevbe edenleri sever, temizlenenleri de sever."'),
(230, 2, 223, 'Kadınlarınız sizin tarlanızdır; tarlanıza dilediğiniz gibi varın. Kendiniz için (geleceğe hazırlık olarak güzel davranışlar) takdim edin. Allah\'tan korkup-sakının ve bilin ki elbette O\'na kavuşucusunuz. İman edenlere müjde ver.'),
(231, 2, 224, 'Bir de yeminlerinizi bahane ederek; iyilik yapmanız, sakınmanız ve insanların arasını düzeltmenize Allah\'ı engel kılmayın. Allah işitendir, bilendir.'),
(232, 2, 225, 'Allah sizi, yeminlerinizdeki \'rastgele söylemelerinizden, boş, amaçsız sözler\'den dolayı sorumlu tutmaz; fakat kalplerinizin kazandıklarından dolayı sorumlu tutar. Allah bağışlayandır, yumuşak davranandır.'),
(233, 2, 226, 'Kadınlarından uzaklaşmaya yemin edenler için dört ay bekleme süresi vardır. Eğer (bu süre içinde eşlerine) dönerlerse, şüphesiz Allah, bağışlayandır, esirgeyendir.'),
(234, 2, 227, '(Yok) Eğer boşamada kararlı davranırsa (boşanırlar). Şüphesiz Allah, işitendir, bilendir.'),
(235, 2, 228, 'Boşanmış kadınlar kendi kendilerine üç \'ay hali ve temizlenme süresi\' beklerler. Eğer Allah\'a ve ahiret gününe inanıyorlarsa Allah\'ın rahimlerinde yarattığını saklamaları onlara helal olmaz. Kocaları, bu süre içinde barışmak isterlerse, onları geri almada (başkalarından) daha çok hak sahibidirler. Onların lehine de, aleyhlerindeki maruf hakka denk bir hak vardır. Yalnız erkekler için onlar üzerinde bir derece var. Allah Azizdir. Hakimdir.'),
(236, 2, 229, 'Boşanma iki defadır. (Sonra) Ya iyilikle tutmak veya güzellikle bırakmak (gerekir). Onlara (kadınlara) verdiğiniz bir şeyi geri almanız size helal değildir; ancak ikisinin Allah\'ın sınırlarını ayakta tutamayacaklarından korkmuş olmaları (durumu başka). Eğer ikisinin Allah\'ın sınırlarını ayakta tutamayacaklarından korkarsanız, bu durumda (kadının) fidye vermesinde ikisi için de günah yoktur. İşte bunlar, Allah\'ın sınırlarıdır; onlara tecavüz etmeyin. Kim Allah\'ın sınırlarına tecavüz ederse, onlar zalimlerin ta kendileridir.'),
(237, 2, 230, 'Yine onu (kadını üçüncü defa) boşarsa, (kadın) onun dışında bir başka kocayla nikahlanmadıkça ona helal olmaz. Eğer (bu koca da) onu boşarsa, onlar (ilk koca ile karısı) Allah\'ın sınırlarını ayakta tutacaklarını sanıyorlarsa, tekrar birbirlerine dönmelerinde ikisi için günah yoktur. İşte bunlar, Allah\'ın sınırlarıdır; bilen bir topluluk için bunları (böyle) açıklar.'),
(238, 2, 231, 'Kadınları boşadığınızda, bekleme sürelerini tamamlamışlarsa, onları ya güzellikle tutun ya da güzellikle bırakın. Fakat haklarını ihlal edip zarar vermek için onları (yanınızda) tutmayın. Kim böyle yaparsa artık o, kendi nefsine zulmetmiş olur. Allah\'ın ayetlerini oyun (konusu) edinmeyin ve Allah\'ın size verdiği nimeti ve size öğüt olarak indirdiği Kitabı ve hikmeti anın. Allah\'tan korkup-sakının ve bilin ki, Allah herşeyi bilendir.'),
(239, 2, 232, 'Kadınları boşadığınızda, bekleme sürelerini de tamamlamışlarsa -birbirleriyle maruf (bilinen meşru biçimde) anlaştıkları takdirde- onlara, kendilerini kocalarına nikahlamalarına engel çıkarmayın. İşte, içinizde Allah\'a ve ahiret gününe iman edenlere bununla (böyle) öğüt verilir. Bu, sizin için daha hayırlı ve daha temizdir. Allah, bilir de siz bilmezsiniz.'),
(240, 2, 233, 'Emzirmeyi tamamlamak isteyenler için anneler çocuklarını iki tam yıl emzirirler. Onların (annelerin) yiyeceği, giyeceği bilinen (örf)e uygun olarak, çocuk kendisinin olana (babaya) aittir. Kimseye güç yetireceğinin dışında (yük ve sorumluluk) teklif edilmez. Anne, çocuğu, çocuk kendisinin olan baba da çocuğu dolayısıyla zarara uğratılmasın; mirasçı üzerinde(ki sorumluluk ve görev) de bunun gibidir. Eğer (anne ve baba) aralarında rıza ile ve danışarak (çocuğu iki yıl tamamlanmadan) sütten ayırmayı isterlerse, ikisi için de bir güçlük yoktur. Ve eğer çocuklarınızı (bir süt anneye) emzirtmek isterseniz, vereceğinizi örfe uygun olarak ödedikten sonra size bir sorumluluk yoktur. Allah\'tan korkup-sakının ve bilin ki, Allah yaptıklarınızı görendir.'),
(241, 2, 234, 'İçinizden ölenlerin (geride) bıraktığı eşler, kendi kendilerine dört ay on (gün) beklerler. Bu bekleme süresi dolduğunda, artık onların kendi haklarında maruf (meşru) bir şekilde yaptıklarından dolayı size sorumluluk yoktur. Allah, işlediklerinizden haberi olandır.'),
(242, 2, 235, '(İddeti bekleyen) Kadınları nikahlamak istediğinizi (onlara) sezdirmenizde ya da böyle bir isteği gönlünüzde saklamanızda sizin için bir sakınca yoktur. Gerçekte Allah, sizin onları (kalbinizden geçirip) anacağınızı bilir. Sakın bilinen (meşru) sözler dışında onlarla gizlice vaadleşmeyin; bekleme süresi tamamlanıncaya kadar nikah bağını bağlamaya kesin karar vermeyin. Ve bilin ki, elbette Allah kalbinizden geçeni bilmektedir. Artık ondan kaçının. Ve bilin ki, şüphesiz Allah bağışlayandır, (kullara) yumuşak davranandır.'),
(243, 2, 236, 'Kendilerine el sürmediğiniz, mehirlerini tespit etmediğiniz kadınları boşamanızda sizin için bir sakınca yoktur. Onları yararlandırın, zengin olan kendi gücü, darda olan da kendi gücü oranında, maruf (meşru ve örfe uygun) bir şekilde yararlandırsın. (Bu,) iyilik edenler üzerinde bir haktır.'),
(244, 2, 237, 'Eğer onlara mehir tespit eder de, el sürmeden boşarsanız, bu durumda -kendileri veya nikah bağı elinde olanın bağışlaması hariç- tespit ettiğiniz (mehr)in yarısı onlarındır. Sizin (tümünü veya fazlasını) bağışlamanız takvaya daha yakındır. Aranızdaki üstünlüğü (derece farkını) unutmayın. Şüphesiz Allah, yapmakta olduklarınızı görendir.'),
(245, 2, 238, 'Namazları ve orta namazını (üstlerine düşerek, titizlik göstererek) koruyun ve Allah\'a gönülden boyun eğiciler olarak (namaza) durun.'),
(246, 2, 239, 'Eğer korkarsanız, yaya veya binekte iken kılın. Güvenliğe girdiğinizde ise, yine Allah\'ı, bilmediğiniz şeyleri size öğrettiği gibi zikredin.'),
(247, 2, 240, 'İçinizde ölüp de (geride) eşler bırakanlar, (evlerinden) çıkarılmaksızın, bir yıla kadar yararlanmaları için eşlerine vasiyet (bıraksınlar). Ama onlar, (kendiliklerinden) çıkarlarsa, artık onların maruf (meşru) olarak kendileri için yaptıklarından dolayı size sorumluluk yoktur. Allah güçlü ve üstün olandır. Hüküm ve hikmet sahibidir.'),
(248, 2, 241, '(Kocası tarafından) Boşanan (kadın)ların maruf (meşru) bir tarzda yararlanma (ve geçim pay)ları vardır. Bu, sakınanlar üzerinde bir hak (borç) tır.'),
(249, 2, 242, 'İşte Allah, size ayetlerini böyle açıklar; ki akıl erdiresiniz.'),
(250, 2, 243, 'Binlerce kişinin ölüm korkusuyla yurtlarından çıktıklarını görmedin mi? Allah onlara: "Ölün" dedi, sonra da onları diriltti. Şüphesiz Allah, insanlara karşı fazl sahibidir. Ancak, insanların çoğunluğu şükretmez.'),
(251, 2, 244, 'Allah yolunda savaşın ve bilin ki, şüphesiz Allah işitendir, bilendir.'),
(252, 2, 245, 'Allah\'a karşılığını çok artırma ile kat kat artıracağı güzel bir borcu verecek olan kimdir? Allah, daraltır ve genişletir ve siz O\'na döndürüleceksiniz.'),
(253, 2, 246, 'Musa\'dan sonra İsrailoğulları’nın önde gelenlerini görmedin mi? Hani, peygamberlerinden birine: "Bize bir melik gönder de Allah yolunda savaşalım" demişlerdi, O: "Ya üzerinize savaş yazıldığı halde savaşmayacak olursanız?" demişti. "Bize ne oluyor ki Allah yolunda savaşmayalım? Ki biz yurdumuzdan çıkarıldık ve çocuklarımızdan (uzaklaştırıldık.)" demişlerdi. Ama onlara savaş yazıldığı (öngörüldüğü) zaman, az bir kısmı hariç yüz çevirdiler. Allah zalimleri bilir.'),
(254, 2, 247, 'Onlara peygamberleri dedi ki: "Allah size Talut\'u (melik olarak) gönderdi." Onlar: "Biz hükümdarlığa, ona göre daha çok hak sahibiyken ve ona bir mal (servet) bolluğu verilmemişken, nasıl bizi (yönetmek üzere) hükümdarlık (mülk) onun olabilir?" dediler. O (şöyle) demişti: "Doğrusu Allah size onu seçti ve onun bilgi ve bedenî gücünü arttırdı. Allah, kime dilerse mülkünü verir; Allah (rahmeti ve gücü) geniş olandır, bilendir."'),
(255, 2, 248, 'Peygamberleri, onlara (şöyle) dedi: "Onun hükümdarlığının belgesi, size Tabut\'un gelmesi (olacaktır ki) onda Rabbinizden \'bir güven duygusu ve huzur\' ile Musa ailesinden ve Harun ailesinden arta kalanlar var; onu melekler taşır. Eğer inanmışlarsanız, bunda şüphesiz sizin için bir delil vardır."'),
(256, 2, 249, 'Talut, orduyla birlikte ayrıldığında dedi ki: "Doğrusu Allah sizi bir ırmakla imtihan edecektir. Kim bundan içerse, artık o benden değildir ve kim de -eliyle bir avuç alanlar hariç- onu tadmazsa bendendir. Küçük bir kısmı hariç (hepsi sudan) içti. O, kendisiyle beraber iman edenlerle (ırmağı) geçince onlar (geride kalanlar): "Bugün bizim Calut\'a ve ordusuna karşı (koyacak) gücümüz yok" dediler. (O zaman) Muhakkak Allah\'a kavuşacaklarını umanlar (şöyle) dediler: "Nice küçük topluluk, daha çok olan bir topluluğa Allah\'ın izniyle galib gelmiştir; Allah sabredenlerle beraberdir."'),
(257, 2, 250, 'Onlar, Calut ve ordusuna karşı meydana (savaşa) çıktıklarında, dediler ki: "Rabbimiz, üzerimize sabır yağdır, adımlarımızı sabit kıl (kaydırma) ve kafirler topluluğuna karşı bize yardım et."'),
(258, 2, 251, 'Böylece onları, Allah\'ın izniyle yenilgiye uğrattılar. Davud Calut\'u öldürdü. Allah da ona mülk ve hikmet verdi; ona dilediğinden öğretti. Eğer Allah\'ın, insanların bir kısmı ile bir kısmını def\'i (engellemesi) olmasaydı, yeryüzü mutlaka fesada uğrardı. Ancak Allah, alemlere karşı büyük fazl (ve ihsan) sahibidir.'),
(259, 2, 252, 'İşte bunlar, Allah\'ın ayetleridir; onları sana bir hak olarak okuyoruz. Sen de gönderilen elçilerdensin.'),
(260, 2, 253, 'İşte bu elçiler; bir kısmını bir kısmına üstün kıldık. Onlardan, Allah\'ın kendileriyle konuştuğu ve derecelerle yükselttiği vardır. Meryem oğlu İsa\'ya apaçık belgeler verdik ve O’nu Ruhu\'l-Kudüs\'le destekledik. Şayet Allah dileseydi, kendilerine apaçık belgeler geldikten sonra, onların peşinden gelen (ümmet)ler, birbirlerini öldürmezdi. Ancak ihtilafa düştüler; onlardan kimi inandı, kimi inkar etti. Allah dileseydi birbirlerini öldürmezlerdi. Ama Allah dilediğini yapandır.'),
(261, 2, 254, 'Ey iman edenler, hiçbir alış-verişin, hiçbir dostluğun ve hiçbir şefaatin olmadığı gün gelmezden evvel, size rızık olarak verdiklerimizden infak edin. Kafirler... Onlar zulmedenlerdir.'),
(262, 2, 255, 'Allah... O\'ndan başka İlah yoktur. Diridir, Kaimdir. O\'nu uyuklama ve uyku tutmaz. Göklerde ve yerde ne varsa hepsi O\'nundur. İzni olmaksızın O\'nun Katında şefaatte bulunacak kimdir? O, önlerindekini ve arkalarındakini bilir. (Onlar ise) Dilediği kadarının dışında, O\'nun ilminden hiçbir şeyi kavrayıp-kuşatamazlar. O\'nun kürsüsü, bütün gökleri ve yeri kaplayıp-kuşatmıştır. Onların korunması O\'na güç gelmez. O, pek Yücedir, pek büyüktür.'),
(263, 2, 256, 'Dinde zorlama (ve baskı) yoktur. Şüphesiz, doğruluk (rüşd) sapıklıktan apaçık ayrılmıştır. Artık kim tağutu tanımayıp Allah\'a inanırsa, o, sapasağlam bir kulba yapışmıştır; bunun kopması yoktur. Allah, işitendir, bilendir.'),
(264, 2, 257, 'Allah, iman edenlerin Velisi (dostu ve destekçisi)dir. Onları karanlıklardan nura çıkarır; inkar edenlerin velileri ise tağut\'tur. Onları nurdan karanlıklara çıkarırlar. İşte onlar, ateşin halkıdırlar, onda süresiz kalacaklardır.'),
(265, 2, 258, 'Allah, kendisine mülk verdi, diye Rabbi konusunda İbrahim\'le tartışmaya gireni görmedin mi? Hani İbrahim: "Benim Rabbim diriltir ve öldürür" demişti; o da: "Ben de öldürür ve diriltirim" demişti. (O zaman) İbrahim: "Şüphe yok, Allah Güneş\'i doğudan getirir, (hadi) sen de onu batıdan getir" deyince, o inkarcı böylece afallayıp kalmıştı. Allah, zalimler topluluğunu hidayete erdirmez.'),
(266, 2, 259, 'Ya da altı üstüne gelmiş, ıssız duran bir şehre uğrayan gibisini (görmedin mi?) Demişti ki: "Allah, burasını ölümünden sonra nasıl diriltecekmiş?" Bunun üzerine Allah, onu yüz yıl ölü bıraktı, sonra onu diriltti. (Ve ona) Dedi ki: "Ne kadar kaldın?" O: "Bir gün veya bir günden az kaldım" dedi. (Allah ona:) "Hayır, yüz yıl kaldın, böyleyken yiyeceğine ve içeceğine bak, henüz bozulmamış; eşeğine de bir bak; (bunu yapmamız) seni insanlara ibret-belgesi kılmamız içindir. Kemiklere de bir bak nasıl biraraya getiriyoruz, sonra da onlara et giydiriyoruz?" dedi. O, kendisine (bunlar) apaçık belli olduktan sonra dedi ki: "(Artık şimdi) Biliyorum ki gerçekten Allah, herşeye güç yetirendir."'),
(267, 2, 260, 'Hani İbrahim: "Rabbim, bana ölüleri nasıl dirilttiğini göster" demişti. (Allah ona:) "İnanmıyor musun?" deyince, "Hayır (inandım), ancak kalbimin tatmin olması için" dedi. "Öyleyse, dört kuş tut. Onları kendine alıştır, sonra onları (parçalayıp) her bir parçasını bir dağın üzerine bırak, sonra da onları çağır. Sana koşarak gelirler. Bil ki, şüphesiz Allah, üstün ve güçlü olandır, hüküm ve hikmet sahibidir."'),
(268, 2, 261, 'Mallarını Allah yolunda infak edenlerin örneği yedi başak bitiren, her bir başakta yüz tane bulunan bir tek tanenin örneği gibidir. Allah, dilediğine kat kat arttırır. Allah (ihsanı) bol olandır, bilendir.'),
(269, 2, 262, 'Mallarını Allah yolunda infak edenler, sonra infak ettikleri şeyin peşinden başa kakmayan ve eziyet vermeyenlerin ecirleri Rableri Katındadır, onlara korku yoktur ve onlar mahzun olmayacaklardır.'),
(270, 2, 263, 'Güzel bir söz ve bağışlama, peşinden eziyet gelen bir sadakadan daha hayırlıdır. Allah hiçbir şeye ihtiyacı olmayandır, yumuşak davranandır.'),
(271, 2, 264, 'Ey iman edenler, Allah\'a ve ahiret gününe inanmayıp, insanlara karşı gösteriş olsun diye malını infak eden gibi minnet ve eziyet ederek sadakalarınızı geçersiz kılmayın. Böylesinin durumu, üzerinde toprak bulunan bir kayanın durumuna benzer; üzerine sağnak bir yağmur düştü mü, onu çırılçıplak bırakıverir. Onlar kazandıklarından hiçbir şeye güç yetiremez(elde edemez)ler. Allah, kafirler topluluğuna hidayet vermez.'),
(272, 2, 265, 'Yalnızca Allah\'ın rızasını istemek ve kendilerinde olanı kökleştirip-güçlendirmek için mallarını infak edenlerin örneği, yüksekçe bir tepede bulunan, sağnak yağmur aldığında ürünlerini iki kat veren bir bahçenin örneğine benzer ki, ona sağnak yağmur isabet etmese de bir çisintisi (vardır). Allah, yaptıklarınızı görendir.'),
(273, 2, 266, 'Hangi biriniz ister ki, altından ırmaklar akan hurmalardan, üzümlerden bir bahçesi olsun, içinde kendisinin olan bütün ürünler de bulunsun; fakat kendisine ihtiyarlık gelip çatsın, (üstelik) zayıf ve küçük çocukları olsun (böyle bir durumda iken) ona (bahçesine) ateşli bir kasırga isabet etsin de yanıversin. İşte Allah size ayetleri böyle açıklar, ki düşünesiniz.'),
(274, 2, 267, 'Ey iman edenler, kazandıklarınızın iyi olanından ve sizin için yerden bitirdiklerimizden infak edin. Kendinizin göz yummadan alamayacağınız bayağı şeyleri vermeye kalkışmayın ve bilin ki, şüphesiz Allah, hiçbir şeye ihtiyacı olmayandır, övülmeye layık olandır.'),
(275, 2, 268, 'Şeytan, sizi fakirlikle korkutuyor ve size çirkin -hayasızlığı emrediyor. Allah ise, size Kendisi\'nden bağışlama ve bol ihsan (fazl) vadediyor. Allah (rahmetiyle) geniş olandır, bilendir.'),
(276, 2, 269, 'Kime dilerse hikmeti ona verir; şüphesiz kendisine hikmet verilene büyük bir hayır da verilmiştir. Temiz akıl sahiplerinden başkası öğüt alıp-düşünmez.'),
(277, 2, 270, 'Her neyi nafaka olarak infak eder ve adak olarak neyi adarsanız, muhakkak Allah onu bilir. Zulmedenlerin yardımcıları yoktur.'),
(278, 2, 271, 'Sadakaları açıkta verirseniz ne iyi; fakat gizleyip fakirlere verirseniz bu, sizin için daha hayırlıdır. O, günahlarınızdan bir kısmını bağışlar. Allah, yaptıklarınızdan haberi olandır.'),
(279, 2, 272, 'Onların hidayete ermesi, senin üzerinde (bir yükümlülük) değildir. Ancak Allah, dilediğini hidayete erdirir. Hayır olarak her ne infak ederseniz, kendiniz içindir. Zaten siz, ancak Allah\'ın hoşnutluğunu istemekten başka (bir amaçla) infak etmezsiniz. Hayırdan her ne infak ederseniz -haksızlığa (zulme) uğratılmaksızın- size eksiksizce ödenecektir.'),
(280, 2, 273, '(Sadakalar) Kendilerini Allah yolunda adayan fakirler içindir ki, onlar, yeryüzünde dolaşmaya güç yetiremezler. İffetlerinden dolayı bilmeyen onları zengin sanır. (Ama) Sen onları yüzlerinden tanırsın. Yüzsüzlük ederek insanlardan istemezler. Hayırdan her ne infak ederseniz, şüphesiz Allah onu bilir.'),
(281, 2, 274, 'Onlar ki, mallarını gece, gündüz; gizli ve açık infak ederler. Artık bunların ecirleri Rableri Katındadır, onlara korku yoktur ve onlar mahzun olmayacaklardır.'),
(282, 2, 275, 'Faiz (riba) yiyenler, ancak şeytan çarpmış olanın kalkışı gibi, çarpılmış olmaktan başka (bir tarzda) kalkmazlar. Bu, onların: "Alım-satım da ancak faiz gibidir" demelerinden dolayıdır. Oysa Allah, alış-verişi helal, faizi haram kılmıştır. Kime Rabbinden bir öğüt gelir de (faize) bir son verirse, artık geçmişi kendisine, işi de Allah\'a aittir. Kim (faize) geri dönerse, artık onlar ateşin halkıdır, orada sürekli kalacaklardır.'),
(283, 2, 276, 'Allah, faizi yok eder de, sadakaları artırır. Allah, günahkar kafirlerin hiçbirini sevmez.'),
(284, 2, 277, 'İman edip güzel amellerde bulunanlar, namazı dosdoğru kılanlar ve zekatı verenler; şüphesiz onların ecirleri Rablerinin Katındadır. Onlara korku yoktur ve onlar mahzun olmayacaklardır.'),
(285, 2, 278, 'Ey iman edenler, Allah\'tan sakının ve eğer inanmışsanız, faizden artakalanı bırakın.'),
(286, 2, 279, 'Şayet böyle yapmazsanız, Allah\'a ve Resulüne karşı savaş açtığınızı bilin. Eğer tevbe ederseniz, artık sermayeleriniz sizindir. (Böylece) Ne zulmetmiş olursunuz, ne zulme uğratılmış olursunuz.'),
(287, 2, 280, 'Eğer (borçlu) zorluk içindeyse, ona elverişli bir zamana kadar süre (verin). (Borcu) Sadaka olarak bağışlamanız ise, sizin için daha hayırlıdır; eğer bilirseniz.'),
(288, 2, 281, 'Allah\'a döneceğiniz günden sakının. Sonra herkese kazandığı eksiksizce ödenecek ve onlara haksızlık yapılmayacaktır.'),
(289, 2, 282, 'Ey iman edenler, belirli bir süre için borçlandığınız zaman onu yazınız. Aranızdan bir katip doğru olarak yazsın, katip Allah\'ın kendisine öğrettiği gibi yazmaktan kaçınmasın, yazsın. Üzerinde hak olan (borçlu) da yazdırsın ve Rabbi olan Allah\'tan sakınsın, ondan hiçbir şeyi eksiltmesin. Eğer üzerinde hak olan (borçlu), düşük akıllı ya da za\'f sahibi veya kendisi yazmaya güç yetiremeyecekse, velisi dosdoğru yazdırsın. Erkeklerinizden de iki şahid tutun; eğer iki erkek yoksa, şahidlerden rıza göstereceğiniz bir erkek ve biri şaşırdığında öbürü ona hatırlatacak iki kadın (da olur). Şahidler çağırıldıkları zaman kaçınmasınlar. Onu (borcu) az olsun, çok olsun, süresiyle birlikte yazmaya üşenmeyin. Bu, Allah Katında en adil, şahitlik için en sağlam, şüphelenmemeniz için de en yakın olandır. Ancak aranızda devredip durduğunuz ve peşin olarak yaptığınız ticaret başka, bunu yazmamanızda sizin için bir sakınca yoktur. Alış-veriş ettiğinizde de şahid tutun. Yazana da, şahide de zarar verilmesin. (Aksini) Yaparsanız, o, kendiniz için fısk (zulüm ve günah)tır. Allah\'tan sakının. Allah size öğretiyor. Allah herşeyi bilendir.'),
(290, 2, 283, 'Eğer yolculukta iseniz ve katip bulamazsanız, bu durumda alınan rehin (yeter). Şu durumda eğer birbirinize güveniyorsanız, kendisine güven duyulan, Rabbi olan Allah\'tan sakınsın da emanetini ödesin. Şahidliği gizlemeyin. Kim onu gizlerse, artık şüphesiz, onun kalbi günahkardır. Allah, yaptıklarınızı bilendir.'),
(291, 2, 284, 'Göklerde ve yerde ne varsa Allah\'ındır. İçinizdekini açığa vursanız da, gizleseniz de, Allah sizi onunla sorguya çeker. Sonra dilediğini bağışlar, dilediğini azaplandırır. Allah, herşeye güç yetirendir.'),
(292, 2, 285, 'Elçi, kendisine Rabbinden indirilene iman etti, mü\'minler de. Tümü, Allah\'a, meleklerine, kitaplarına ve elçilerine inandı. "O\'nun elçileri arasında hiçbirini (diğerinden) ayırt etmeyiz. İşittik ve itaat ettik. Rabbimiz bağışlamanı (dileriz). Varış ancak Sanadır" dediler.'),
(293, 2, 286, 'Allah, hiç kimseye güç yetireceğinden başkasını yüklemez. (Kişinin nefsinin) Kazandığı lehine, kazandırdıkları aleyhinedir. "Rabbimiz, unuttuklarımızdan veya yanıldıklarımızdan dolayı bizi sorumlu tutma. Rabbimiz, bize, bizden öncekilere yüklediğin gibi ağır yük yükleme. Rabbimiz, kendisine güç yetiremeyeceğimiz şeyi bize taşıtma. Bizi affet. Bizi bağışla. Bizi esirge, Sen bizim Mevlamızsın. Kafirler topluluğuna karşı bize yardım et."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Elif, Lam, Mim.'),
(295, 3, 2, 'Allah... O\'ndan başka İlah yoktur. Diridir, Kaimdir.'),
(296, 3, 3, 'O, sana Kitab’ı hak ve kendinden öncekileri doğrulayıcı olarak indirdi. O, Tevrat\'ı ve İncil\'i de indirmişti.'),
(297, 3, 4, 'Bundan (Kur’an’dan) önce (onlar) insanlar için bir hidayet idiler. Doğruyu yanlıştan ayıran (Furkan)ı da indirdi. Gerçek şu ki, Allah\'ın ayetlerini inkar edenler için şiddetli bir azap vardır. Allah güçlüdür, intikam alıcıdır.'),
(298, 3, 5, 'Şüphesiz, yerde ve gökte Allah\'a hiçbir şey gizli kalmaz.'),
(299, 3, 6, 'Döl yataklarında size dilediği gibi suret veren O\'dur. O\'ndan başka İlah yoktur; üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(300, 3, 7, 'Sana Kitab’ı indiren O\'dur. Ondan, Kitab’ın anası (temeli) olan bir kısım ayetler muhkem\'dir; diğerleri ise müteşabihtir. Kalplerinde bir kayma olanlar, fitne çıkarmak ve olmadık yorumlarını yapmak için ondan müteşabih olanına uyarlar. Oysa onun tevilini Allah\'tan başkası bilmez. İlimde derinleşenler ise: "Biz ona inandık, tümü Rabbimiz\'in Katındandır" derler. Temiz akıl sahiplerinden başkası öğüt alıp-düşünmez.'),
(301, 3, 8, '"Rabbimiz, bizi hidayete erdirdikten sonra kalplerimizi kaydırma ve Katından bize bir rahmet bağışla. Şüphesiz, bağışı en çok olan Sensin Sen."'),
(302, 3, 9, '"Rabbimiz, kendisinde şüphe olmayan bir günde insanları gerçekten Sen toplayacaksın. Doğrusu Allah, va\'dinden cayıp-dönmez."'),
(303, 3, 10, 'Şüphesiz inkar edenler, onların malları da, çocukları da kendilerine Allah\'tan (gelecek azaba karşı) hiçbir şey kazandırmaz. Ve onlar ateşin yakıtıdırlar.'),
(304, 3, 11, 'Tıpkı Firavun ailesi ve onlardan öncekilerin gidiş tarzı gibi. Ayetlerimizi yalanladılar, böylece Allah günahları nedeniyle onları yakalayıverdi. Allah, (cezayla) sonuçlandırması pek şiddetli olandır.'),
(305, 3, 12, 'İnkar edenlere de ki: "Yakında yenilgiye uğratılacaksınız ve toplanıp cehenneme sürüleceksiniz." Ne kötü yataktır o.'),
(306, 3, 13, 'Karşı karşıya gelen iki toplulukta, sizin için andolsun bir ayet (ibret) vardır. Bir topluluk, Allah yolunda çarpışıyordu, diğeri ise kafirdi ki göz görmesiyle karşılarındakini kendilerinin iki katı görüyorlardı. İşte Allah, dilediğini yardımıyla destekler. Şüphesiz bunda, basiret sahipleri için gerçekten bir ibret vardır.'),
(307, 3, 14, 'Kadınlara, oğullara, kantar kantar yığılmış altın ve gümüşe, salma güzel atlara, hayvanlara ve ekinlere duyulan tutkulu şehvet insanlara \'süslü ve çekici\' kılındı. Bunlar, dünya hayatının metaıdır. Asıl varılacak güzel yer Allah Katında olandır.'),
(308, 3, 15, 'De ki: "Size bundan daha hayırlısını bildireyim mi? Korkup sakınanlar için Rablerinin Katında, içinde temelli kalacakları, altından ırmaklar akan cennetler, tertemiz eşler ve Allah\'ın rızası vardır. Allah, kulları hakkıyla görendir."'),
(309, 3, 16, 'Onlar: "Rabbimiz şüphesiz biz iman ettik, artık bizim günahlarımızı bağışla ve bizi ateşin azabından koru" diyenler;'),
(310, 3, 17, 'Sabredenler, doğru olanlar, gönülden boyun eğenler, infak edenler ve \'seher vakitlerinde\' bağışlanma dileyenlerdir.'),
(311, 3, 18, 'Allah, gerçekten Kendisi\'nden başka İlah olmadığına şahitlik etti; melekler ve ilim sahipleri de O\'ndan başka İlah olmadığına adaletle şahitlik ettiler. Aziz ve Hakim olan O\'ndan başka İlah yoktur.'),
(312, 3, 19, 'Hiç şüphesiz din, Allah Katında İslam\'dır. Kitap verilenler, ancak kendilerine ilim geldikten sonra, aralarındaki "kıskançlık ve hakka başkaldırma" (bağy) yüzünden ayrılığa düştüler. Kim Allah\'ın ayetlerini inkar ederse, (bilsin ki) gerçekten Allah, hesabı pek çabuk görendir.'),
(313, 3, 20, 'Eğer seninle çekişip-tartışırlarsa, de ki: "Ben, bana uyanlarla birlikte, kendimi Allah\'a teslim ettim." Ve kitap verilenlerle ümmilere de ki: "Siz de teslim oldunuz mu?" Eğer teslim oldularsa, gerçekten hidayete ermişlerdir. Fakat yüz çevirdilerse, artık sana düşen yalnızca tebliğ(etmek)dir. Allah, kulları hakkıyla görendir.'),
(314, 3, 21, 'Allah\'ın ayetlerini inkar edenler, peygamberleri haksız yere öldürenler ve insanlardan adaleti emredenleri öldürenler; işte onlara acıklı bir azabı müjdele.'),
(315, 3, 22, 'Onlar, yaptıkları dünyada ve ahirette boşa gitmiş olanlardır. Ve onların yardımcıları yoktur.'),
(316, 3, 23, 'Kendilerine kitaptan bir pay verilenleri görmedin mi? Aralarında Allah\'ın kitabı hükmetsin diye çağrılıyorlar da, onlardan bir bölümü yüz çeviriyor. Onlar, işte böyle arka dönenlerdir.'),
(317, 3, 24, 'Bu, onların: "Ateş bize sayılı günler dışında kesinlikle dokunmayacak" demelerindendir. Onların bu iftiraları, dinleri konusunda kendilerini yanılgıya düşürmüştür.'),
(318, 3, 25, 'Artık onları, kendisinde şüphe olmayan bir gün topladığımızda ve her bir nefse -haksızlığa uğratılmaksızın- kazandığı tam olarak ödendiğinde nasıl olacak?'),
(319, 3, 26, 'De ki: "Ey mülkün sahibi Allah\'ım, dilediğine mülkü verirsin ve dilediğinden mülkü çekip-alırsın, dilediğini aziz kılar, dilediğini alçaltırsın; hayır Senin elindedir. Gerçekten Sen, herşeye güç yetirensin."'),
(320, 3, 27, '"Geceyi gündüze bağlayıp-katarsın, gündüzü de geceye bağlayıp-katarsın; diriyi ölüden çıkarırsın, ölüyü de diriden çıkarırsın. Sen, dilediğine hesapsız rızık verirsin."'),
(321, 3, 28, 'Mü\'minler, mü\'minleri bırakıp da kafirleri veliler edinmesinler. Kim böyle yaparsa, Allah\'tan hiçbir şey (yardım) yoktur. Ancak onlardan korunma gayesiyle sakınma(nız) başka. Allah, sizi Kendisi\'nden sakındırır. Varış Allah\'adır.'),
(322, 3, 29, 'De ki: "Sinelerinizde olanı -gizleseniz de, açığa vursanız da- Allah bilir. Ve göklerde olanı da, yerde olanı da bilir. Allah, herşeye güç yetirendir."'),
(323, 3, 30, 'Her bir nefsin hayırdan yaptıklarını hazır bulduğu ve her ne kötülük işlediyse onunla kendisi arasında uzak bir mesafe olmasını istediği o günü (düşünün). Allah, sizi Kendisi\'nden sakındırır. Allah, kullarına karşı şefkatli olandır.'),
(324, 3, 31, 'De ki: "Eğer siz Allah\'ı seviyorsanız bana uyun; Allah da sizi sevsin ve günahlarınızı bağışlasın. Allah bağışlayandır, esirgeyendir."'),
(325, 3, 32, 'De ki: "Allah\'a ve elçisine itaat edin." Eğer yüz çevirirlerse şüphesiz Allah, kafirleri sevmez.'),
(326, 3, 33, 'Gerçek şu ki, Allah, Adem\'i, Nuh\'u, İbrahim ailesini ve İmran ailesini alemler üzerine seçti;'),
(327, 3, 34, 'Onlar birbirlerinden (türeme tek) bir zürriyettir. Allah işitendir, bilendir.'),
(328, 3, 35, 'Hani İmran\'ın karısı: "Rabbim, karnımda olanı, \'her türlü bağımlılıktan özgürlüğe kavuşturulmuş olarak\' Sana adadım, benden kabul et. Şüphesiz işiten bilen Sensin Sen" demişti.'),
(329, 3, 36, 'Fakat onu doğurduğunda -Allah onun ne doğurduğunu daha iyi bilirken- dedi ki: "Rabbim, doğrusu bir kız (çocuğu) doğurdum. Erkek ise, kız gibi değildir. Ona Meryem adını koydum. Ben onu ve soyunu o taşa tutulmuş (kovulmuş) şeytandan Sana sığındırırım."'),
(330, 3, 37, 'Bunun üzerine Rabbi onu güzel bir kabulle kabul etti ve onu güzel bir bitki gibi yetiştirdi. Zekeriya\'yı ondan sorumlu kıldı. Zekeriya her ne zaman mihraba girdiyse, yanında bir yiyecek buldu: "Meryem, bu sana nereden geldi?" deyince, "Bu, Allah Katındandır. Şüphesiz Allah, dilediğine hesapsız rızık verendir" dedi.'),
(331, 3, 38, 'Orada Zekeriya Rabbine dua etti: "Rabbim, bana Katından tertemiz bir soy armağan et. Doğrusu Sen, duaları işitensin" dedi.'),
(332, 3, 39, 'O mihrapta namaz kılarken, melekler ona seslendi: "Allah, sana Yahya\'yı müjdeler. O, Allah\'tan olan bir kelimeyi (İsa\'yı) doğrulayan, efendi, iffetli ve salihlerden bir peygamberdir."'),
(333, 3, 40, 'Dedi ki: "Rabbim, bana gerçekten ihtiyarlık ulaşmışken ve karım da kısırken nasıl bir oğlum olabilir?" "Böyledir" dedi, "Allah dilediğini yapar."'),
(334, 3, 41, '(Zekeriya) "Rabbim, bana bir alamet (ayet) ver." dedi. "Sana alamet, işaretleşme dışında, insanlarla üç gün konuşmamandır. Rabbini çokça zikret ve akşam sabah O’nu tesbih et." dedi.'),
(335, 3, 42, 'Hani melekler: "Meryem, şüphesiz Allah seni seçti, seni arındırdı ve alemlerin kadınlarına üstün kıldı," demişti.'),
(336, 3, 43, '"Meryem, Rabbine gönülden itaatte bulun, secde et ve rüku edenlerle birlikte rüku et."'),
(337, 3, 44, 'Bunlar, gayb haberlerindendir; bunları sana vahyediyoruz. Onlardan hangisi Meryem\'i sorumluluğuna alacak diye kalemleriyle kur\'a atarlarken sen yanlarında değildin; çekişirlerken de yanlarında değildin.'),
(338, 3, 45, 'Hani melekler, dediler ki: "Meryem, doğrusu Allah Kendinden bir kelimeyi sana müjdelemektedir. Onun adı Meryem oğlu İsa Mesih\'tir. O, dünyada ve ahirette \'seçkin, onurlu, saygındır\' ve (Allah\'a) yakın kılınanlardandır."'),
(339, 3, 46, '"Beşikte de, yetişkinliğinde de insanlarla konuşacaktır. Ve O salihlerdendir."'),
(340, 3, 47, '"Rabbim, bana bir beşer dokunmamışken, nasıl bir çocuğum olabilir?" dedi. (Fakat) Allah neyi dilerse yaratır. Bir işin olmasına karar verirse, yalnızca ona "ol" der, o da hemen oluverir."'),
(341, 3, 48, '"Ona Kitab’ı, hikmeti, Tevrat’ı ve İncil’i öğretecek."'),
(342, 3, 49, 'İsrailoğulları’na elçi kılacak. (O, İsrailoğulları’na şöyle diyecek:) "Gerçek şu, ben size Rabbinizden bir ayetle geldim. Ben size çamurdan kuş biçiminde bir şey oluşturur, içine üfürürüm, o da hemencecik Allah\'ın izniyle kuş oluverir. Ve Allah\'ın izniyle doğuştan kör olanı, alaca hastalığına tutulanı iyileştirir ve ölüyü diriltirim. Yediklerinizi ve biriktirdiklerinizi size haber veririm. Şüphesiz, eğer inanmışsanız bunda sizin için kesin bir ayet vardır."'),
(343, 3, 50, '"Benden önceki Tevrat\'ı doğrulamak ve size haram kılınan bazı şeyleri helal kılmak üzere size Rabbinizden bir ayetle geldim. Artık Allah\'tan korkup bana itaat edin."'),
(344, 3, 51, '"Gerçekten Allah, benim de Rabbim, sizin de Rabbinizdir. Öyleyse O\'na ibadet edin. Dosdoğru olan yol işte budur."'),
(345, 3, 52, 'Nitekim İsa, onlarda inkarı sezince, dedi ki: "Allah için bana yardım edecekler kimdir?" Havariler: "Allah\'ın yardımcıları biziz; biz Allah\'a inandık, bizim gerçekten Müslümanlar olduğumuza şahid ol" dediler.'),
(346, 3, 53, '"Rabbimiz, biz indirdiğine inandık ve elçiye uyduk. Böylece bizi şahidlerle beraber yaz."'),
(347, 3, 54, 'Onlar (inanmayanlar) bir düzen kurdular. Allah da (buna karşılık) bir düzen kurdu. Allah, düzen kurucuların en hayırlısıdır.'),
(348, 3, 55, 'Hani Allah, İsa\'ya demişti ki: "Ey İsa, doğrusu senin hayatına Ben son vereceğim, seni Kendime yükselteceğim, seni inkar edenlerden temizleyeceğim ve sana uyanları kıyamete kadar inkara sapanların üstüne geçireceğim. Sonra dönüşünüz yalnızca Banadır, hakkında anlaşmazlığa düştüğünüz şeyde aranızda Ben hükmedeceğim."'),
(349, 3, 56, '"İnkar edenleri ise, dünyada ve ahirette şiddetli bir azapla azaplandıracağım. Onların hiç yardımcıları yoktur."'),
(350, 3, 57, '"İman edip salih amellerde bulunanların ecirleri eksiksiz ödenecektir. Allah, zalim olanları sevmez."'),
(351, 3, 58, 'Bunları Biz sana ayetlerden ve hikmetli zikirden (Kur\'an\'dan) okuyoruz.'),
(352, 3, 59, 'Şüphesiz, Allah Katında İsa\'nın durumu, Adem\'in durumu gibidir. Onu topraktan yarattı, sonra ona "ol" demesiyle o da hemen oluverdi.'),
(353, 3, 60, 'Gerçek, Rabbinden (gelen)dir. Öyleyse kuşkuya kapılanlardan olma.'),
(354, 3, 61, 'Artık sana gelen bunca ilimden sonra, onun hakkında seninle \'çekişip-tartışmalara girişirlerse\' de ki: "Gelin, oğullarımızı ve oğullarınızı, kadınlarımızı ve kadınlarınızı, kendimizi ve kendinizi çağıralım; sonra karşılıklı lanetleşelim de Allah\'ın lanetini yalan söyleyenlerin üstüne kılalım."'),
(355, 3, 62, 'Şüphesiz bu, gerçek bir olayın haberidir. Allah\'tan başka İlah yoktur. Ve şüphesiz Allah, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(356, 3, 63, 'Eğer yüz çevirirlerse elbette Allah, fesat çıkaranları bilir.'),
(357, 3, 64, 'De ki: "Ey Kitap Ehli, bizimle sizin aranızda müşterek (olan) bir kelimeye (tevhide) gelin. Allah\'tan başkasına kulluk etmeyelim, O\'na hiçbir şeyi ortak koşmayalım ve Allah\'ı bırakıp bir kısmımız (diğer) bir kısmımızı Rabler edinmeyelim." Eğer yine yüz çevirirlerse, deyin ki: "Şahid olun, biz gerçekten Müslümanlarız."'),
(358, 3, 65, '"Ey Kitap Ehli, İbrahim konusunda ne diye çekişip tartışıyorsunuz? Tevrat da, İncil de ancak ondan sonra indirilmiştir. Yine de akıl erdirmeyecek misiniz?"'),
(359, 3, 66, 'İşte sizler böylesiniz; (diyelim ki) hakkında bilginiz olan şeyde tartıştınız, ama hiç bilginiz olmayan bir konuda ne diye tartışıp-duruyorsunuz? Oysa Allah bilir, sizler bilmezsiniz.'),
(360, 3, 67, 'İbrahim, ne Yahudi idi, ne de Hıristiyandı: ancak, O hanif (muvahhid) bir Müslümandı, müşriklerden de değildi.'),
(361, 3, 68, 'Doğrusu, insanların İbrahim\'e en yakın olanı, ona uyanlar ve bu peygamber ile iman edenlerdir. Allah, mü\'minlerin velisidir.'),
(362, 3, 69, 'Kitap Ehlinden bir grup, sizi şaşırtıp saptırmayı arzuladı; fakat onlar ancak kendi nefislerini şaşırtıp-saptırırlar da şuuruna varmazlar.'),
(363, 3, 70, 'Ey Kitap Ehli, siz şahid olup dururken, ne diye Allah\'ın ayetlerini inkar ediyorsunuz?'),
(364, 3, 71, 'Ey Kitap Ehli, neden hakkı batıl ile örtüyor ve bildiğiniz halde hakkı gizliyorsunuz?'),
(365, 3, 72, 'Kitap Ehlinden bir bölümü, dedi ki: "İman edenlere inene gündüzün başlangıcında inanın, bitiminde ise inkar edin. Belki onlar da dönerler."'),
(366, 3, 73, '"Ve sizin dininize uyanlardan başkasına inanıp güvenmeyin." De ki: "Şüphesiz doğru yol Allah\'ın dosdoğru yoludur. Size verilenin bir benzeri birine (İslam peygamberine) veriliyor ya da Rabbinizin Katında onlar (Müslümanlar) size karşı deliller getiriyorlar, diye mi (bu telaşınız?) De ki: "Şüphesiz \'lütuf ve ihsan (fazl)\' Allah\'ın elindedir, onu dilediğine verir. Allah (rahmeti) geniş olandır, bilendir."'),
(367, 3, 74, 'O, kime dilerse rahmetini tahsis eder, Allah büyük \'lütuf ve ihsan (fazl)\' sahibidir.'),
(368, 3, 75, 'Kitap Ehlinden öylesi vardır ki, bir kantar emanet bıraksan onu sana geri verir; öylesi de vardır ki, ona bir dinar emanet bıraksan, sen, onun tepesine dikilip durmadıkça onu sana ödemez. Bu onların "ümmiler (zayıf ve bilgisizler veya Ehl-i Kitap olmayanlar) konusunda üzerinizde bir yol (sorumluluk) yoktur" demiş olmalarındandır. Oysa kendileri (gerçeği) bildikleri halde Allah\'a karşı yalan söylemektedirler.'),
(369, 3, 76, 'Hayır; kim ahdine vefa eder ve sakınırsa şüphesiz Allah da sakınanları sever.'),
(370, 3, 77, 'Allah\'ın ahdini ve yeminlerini az bir değere karşılık satanlar... İşte onlar; onlar için ahirette hiçbir pay yoktur, kıyamet gününde Allah onlarla konuşmaz, onları gözetmez ve onları arındırmaz. Ve onlar için acı bir azap vardır.'),
(371, 3, 78, 'Onlardan öyleleri vardır ki, dillerini kitaba doğru eğip bükerler, siz onu (bu okur göründüklerini) kitaptan sanasınız diye. Oysa o kitaptan değildir. "Bu Allah Katındandır" derler. Oysa o, Allah Katından değildir. Kendileri de bildikleri halde Allah\'a karşı (böyle) yalan söylerler.'),
(372, 3, 79, 'Beşerden hiç kimsenin, Allah kendisine kitabı, hükmü ve peygamberliği verdikten, sonra insanlara: "Allah\'ı bırakıp bana kulluk edin" deme (hakkı ve yetki)si yoktur. Fakat o, "Öğrettiğiniz ve ders verdiğiniz kitaba göre Rabbaniler olunuz” (deme görevindedir.)'),
(373, 3, 80, 'O, melekleri ve peygamberleri Rabler edinmenizi emretmez. Siz, Müslüman olduktan sonra, size küfrü mü emredecek?'),
(374, 3, 81, 'Hani Allah peygamberlerden \'kesin bir söz (misak)\' almıştı: "Andolsun size kitap ve hikmetten verip sonra size beraberinizdekini doğrulayan bir elçi geldiğinde, ona kesin olarak iman edecek ve ona yardımda bulunacaksınız." Demişti ki: "Bunu ikrar ettiniz ve bu ağır yükümü aldınız mı?" Onlar: "İkrar ettik" demişlerdi de "Öyleyse şahid olun, Ben de sizinle birlikte şahid olanlardanım" demişti.'),
(375, 3, 82, 'Artık kim bundan sonra yüz çevirirse, onlar fasık olanlardır.'),
(376, 3, 83, 'Peki onlar, Allah\'ın dininden başka bir din mi arıyorlar? Oysa göklerde ve yerde her ne varsa -istese de, istemese de- O\'na teslim olmuştur ve O\'na döndürülmektedirler.'),
(377, 3, 84, 'De ki: "Biz Allah\'a, bize indirilene, İbrahim, İsmail, İshak, Yakup ve torunlarına indirilene, Musa\'ya, İsa\'ya ve peygamberlere Rablerinden verilenlere iman ettik. Onlardan hiçbiri arasında ayrılık gözetmeyiz. Ve biz O\'na teslim olmuşlarız."'),
(378, 3, 85, 'Kim İslam\'dan başka bir din ararsa asla ondan kabul edilmez. O, ahirette de kayba uğrayanlardandır.'),
(379, 3, 86, 'Kendilerine apaçık belgeler geldiği ve elçinin hak olduğuna şahid oldukları halde, imanlarından sonra küfre sapan bir kavmi Allah nasıl hidayete erdirir? Allah, zulmeden bir kavmi hidayete erdirmez.'),
(380, 3, 87, 'İşte bunların cezası, Allah\'ın meleklerin ve bütün insanların lanetlerinin üzerine olmasıdır.'),
(381, 3, 88, 'İçinde temelli kalıcıdırlar. Onların azabı hafifletilmez ve onlar gözetilmezler.'),
(382, 3, 89, 'Ancak bundan sonra tevbe edenler, \'salih olarak davrananlar\' başka. Çünkü Allah, gerçekten bağışlayandır, esirgeyendir.'),
(383, 3, 90, 'Doğrusu, imanlarından sonra inkar edenler, sonra inkarlarını arttıranlar; bunların tevbeleri kesinlikle kabul edilmez. İşte bunlar, sapıkların ta kendileridir.'),
(384, 3, 91, 'Şüphesiz küfredip kafir olarak ölenler, bunların hiçbirisinden, yeryüzü dolusu altını olsa -bunu fidye olarak verse de- kesin olarak kabul edilmez. Onlar için acı bir azap vardır ve onların yardımcıları yoktur.'),
(385, 3, 92, 'Sevdiğiniz şeylerden infak edinceye kadar asla iyiliğe eremezsiniz. Her ne infak ederseniz, şüphesiz Allah onu bilir.'),
(386, 3, 93, 'Tevrat indirilmeden evvel, İsrail\'in kendine haram kıldıklarından başka, İsrailoğulları’na bütün yiyecekler helal idi. De ki: "Şu halde eğer doğruysanız, Tevrat\'ı getirin de onu okuyun".'),
(387, 3, 94, 'Artık bundan sonra kim Allah\'a karşı yalan uydurup iftira düzerse, işte onlar, zalim olanlardır.'),
(388, 3, 95, 'De ki: "Allah doğru söyledi. Öyleyse Allah\'ı bir tanıyan (Hanif)ler olarak İbrahim\'in dinine uyun. O, müşriklerden değildi."'),
(389, 3, 96, 'Gerçek şu ki, insanlar için ilk kurulan Ev, Bekke (Mekke) de, o, kutlu ve bütün insanlar (alemler) için hidayet olan (Ka\'be)dir.'),
(390, 3, 97, 'Orada apaçık ayetler (ve) İbrahim\'in makamı vardır. Kim oraya girerse o güvenliktedir. Ona bir yol bulup güç yetirenlerin Ev\'i haccetmesi Allah\'ın insanlar üzerindeki hakkıdır. Kim de inkar ederse, şüphesiz, Allah alemlere karşı muhtaç olmayandır.'),
(391, 3, 98, 'De ki: "Ey Kitap Ehli, Allah yaptıklarınıza şahid iken, ne diye Allah\'ın ayetlerini inkar ediyorsunuz?"'),
(392, 3, 99, 'De ki: "Ey Kitap Ehli, sizler şahidler olduğunuz halde, ne diye iman edenleri Allah yolundan -onda bir çarpıklık bulmaya yeltenerek- çevirmeye çalışıyorsunuz? Allah, yaptıklarınızdan gafil değildir."'),
(393, 3, 100, 'Ey iman edenler, eğer kendilerine kitap verilenlerden herhangi bir gruba boyun eğecek olursanız, sizi imanınızdan sonra tekrar küfre döndürürler.'),
(394, 3, 101, 'Allah\'ın ayetleri size okunuyorken ve O\'nun elçisi içinizdeyken nasıl oluyor da inkar ediyorsunuz? Kim Allah\'a sımsıkı tutunursa, artık elbette o, dosdoğru olan bir yola iletilmiştir.'),
(395, 3, 102, 'Ey iman edenler, Allah\'tan nasıl korkup-sakınmak gerekiyorsa öylece korkup-sakının ve siz, ancak Müslüman olmaktan başka (bir din ve tutum üzerinde) ölmeyin.'),
(396, 3, 103, 'Allah\'ın ipine hepiniz sımsıkı sarılın. Dağılıp ayrılmayın. Ve Allah\'ın sizin üzerinizdeki nimetini hatırlayın. Hani siz düşmanlar idiniz. O, kalplerinizin arasını uzlaştırıp-ısındırdı ve siz O\'nun nimetiyle kardeşler olarak sabahladınız. Yine siz, tam ateş çukurunun kıyısındayken, oradan sizi kurtardı. Umulur ki hidayete erersiniz diye, Allah, size ayetlerini böyle açıklar.'),
(397, 3, 104, 'Sizden; hayra çağıran, iyiliği (marufu) emreden ve kötülükten (münkerden) sakındıran bir topluluk bulunsun. Kurtuluşa erenler işte bunlardır.'),
(398, 3, 105, 'Kendilerine apaçık belgeler geldikten sonra, parçalanıp ayrılan ve anlaşmazlığa düşenler gibi olmayın. İşte onlar için büyük bir azap vardır.'),
(399, 3, 106, 'Bazı yüzlerin ağaracağı, bazı yüzlerin de kararacağı gün... Yüzleri kapkara-kesilecek olanlara: "İmanınızdan sonra inkar ettiniz, öyle mi? Öyleyse inkar etmenize karşılık olarak azabı tadın” (denilir).'),
(400, 3, 107, 'Yüzleri ağaranlar ise, artık onlar Allah\'ın rahmeti içindedirler, içinde de temelli kalacaklardır.'),
(401, 3, 108, 'Bunlar sana hak olarak okumakta olduğumuz Allah\'ın ayetleridir. Allah, alemlere zulüm isteyen değildir.'),
(402, 3, 109, 'Göklerde ve yerde olanlar Allah\'ındır ve (bütün) işler Allah\'a döndürülür.'),
(403, 3, 110, 'Siz, insanlar için çıkarılmış hayırlı bir ümmetsiniz; maruf (iyi ve İslam\'a uygun) olanı emreder, münker olandan sakındırır ve Allah\'a iman edersiniz. Kitap Ehli de inanmış olsaydı, elbette kendileri için hayırlı olurdu. İçlerinden iman edenler vardır, fakat çoğunluğu fıska sapanlardır.'),
(404, 3, 111, 'Onlar size ezadan başka kesinlikle bir zarar veremezler. Eğer sizinle savaşırlarsa size arkalarını dönüp kaçarlar. Sonra kendilerine yardım da edilmez.'),
(405, 3, 112, 'Her nerede bulunurlarsa bulunsunlar -Allah\'ın ipine ve insanların ipine (ahdine) sığınanlar başka- onlara zillet (zorluk damgası) vurulmuştur. Onlar, Allah\'tan bir gazaba uğradılar da üzerlerine aşağılanma (damgası) vuruldu. Bu, Allah\'ın ayetlerini inkar etmeleri ve peygamberleri haksız yere öldürmeleri nedeniyledir. (Yine) Bu, isyan etmeleri ve haddi aşmaları dolayısıyladır.'),
(406, 3, 113, 'Onların hepsi bir değildir. Kitap Ehli\'nden bir topluluk vardır ki, gece vaktinde ayakta durup Allah\'ın ayetlerini okuyarak secdeye kapanırlar.'),
(407, 3, 114, 'Bunlar, Allah\'a ve ahiret gününe iman eder, maruf olanı emreder, münker olandan sakındırır ve hayırlarda yarışırlar. İşte bunlar salih olanlardandır.'),
(408, 3, 115, 'Onlar hayırdan her ne yaparlarsa, elbette ondan yoksun bırakılmazlar. Allah, muttakileri bilendir.'),
(409, 3, 116, 'Gerçekten inkar edenlerin ise, ne malları, ne çocukları, onlara Allah\'tan yana bir şey sağlayamaz. İşte onlar, ateşin halkıdırlar, onda temelli olarak kalacaklardır.'),
(410, 3, 117, 'Onların bu dünya hayatındaki harcamaları kendi nefislerine zulmetmiş olan bir kavmin ekinine isabet eden kavurucu soğukluktaki bir rüzgara benzer ki onu (ekini) helak etmiştir. Allah, onlara zulmetmedi, fakat onlar kendi nefislerine zulmetmektedirler.'),
(411, 3, 118, 'Ey iman edenler, sizden olmayanları sırdaş edinmeyin. Onlar size kötülük ve zarar vermeye çalışıyor, size zorlu bir sıkıntı verecek şeyden hoşlanırlar. Buğz (ve düşmanlıkları) ağızlarından dışa vurmuştur, sinelerinin gizli tuttukları ise, daha büyüktür. Size ayetlerimizi açıkladık; belki akıl erdirirsiniz.'),
(412, 3, 119, 'Sizler, işte böylesiniz; onları seversiniz, oysa onlar sizi sevmezler. Siz kitabın tümüne inanırsınız, onlar sizinle karşılaştıklarında "inandık" derler, kendi başlarına kaldıklarında ise, size olan kin ve öfkelerinden dolayı parmak uçlarını ısırırlar. De ki: "Kin ve öfkenizle ölün." Şüphesiz Allah, sinelerin özünde saklı duranı bilendir.'),
(413, 3, 120, 'Size bir iyilik dokununca tasalanırlar, size bir kötülük isabet ettiğindeyse buna sevinirler. Eğer siz sabreder ve sakınırsanız, onların \'hileli düzenleri\' size hiçbir zarar veremez. Şüphesiz, Allah, yapmakta olduklarını kuşatandır.'),
(414, 3, 121, 'Hani sen, mü\'minleri savaşmak için elverişli yerlere yerleştirmek için evinden erkenden ayrılmıştın. Allah işitendir, bilendir.'),
(415, 3, 122, 'O zaman sizden iki grup, neredeyse \'çözülüp geri çekilmek\' istemişti. Oysa Allah onların (Velisi) yardımcısıydı. Artık mü\'minler, yalnızca Allah\'a tevekkül etmelidir.'),
(416, 3, 123, 'Andolsun, siz güçsüz iken Allah size Bedir\'de yardımıyla zafer verdi. Şu halde Allah\'tan sakının, O\'na şükredebilesiniz.'),
(417, 3, 124, 'Sen mü\'minlere: "Rabbinizin size meleklerden indirilmiş üç bin kişiyle yardım-iletmesi size yetmez mi?" diyordun.'),
(418, 3, 125, 'Evet, eğer sabrederseniz, sakınırsanız ve onlar da aniden üstünüze çullanıverirlerse, Rabbiniz size meleklerden nişanlı beş bin kişiyle yardım ulaştıracaktır.'),
(419, 3, 126, 'Allah bunu (yardımı) size ancak bir müjde olsun ve kalpleriniz bununla tatmin bulsun diye yaptı. \'Yardım ve zafer’ (nusret) ancak üstün ve güçlü, hüküm ve hikmet sahibi olan Allah\'ın Katındandır.'),
(420, 3, 127, '(Ki bununla) İnkar edenlerin önde gelenlerinden bir kısmını kessin (helak etsin) ya da \'umutları suya düşmüşler olarak onları\' tepesi aşağı getirsin de geri dönüp gitsinler.\''),
(421, 3, 128, '(Allah\'ın) Onların tevbelerini kabul etmesi veya zalim olduklarından dolayı azaplandırması işinden sana bir şey (sorumluluk ve görev) yoktur.'),
(422, 3, 129, 'Göklerde ve yerde olanların tümü Allah\'ındır. Kimi dilerse bağışlar, kimi dilerse azaplandırır. Allah bağışlayandır, esirgeyendir.'),
(423, 3, 130, 'Ey iman edenler, faizi kat kat artırılmış olarak yemeyin. Ve Allah\'tan sakının, umulur ki kurtulursunuz.'),
(424, 3, 131, 'Ve kafirler için hazırlanmış olan ateşten sakının.'),
(425, 3, 132, 'Allah\'a ve elçisine itaat edin, ki merhamet olunasınız.'),
(426, 3, 133, 'Rabbinizden olan mağfiret ve eni göklerle yer kadar olan cennete (kavuşmak için) yarışın; o, muttakiler için hazırlanmıştır.'),
(427, 3, 134, 'Onlar, bollukta da, darlıkta da infak edenler, öfkelerini yenenler ve insanlar(daki hakların)dan bağışlama ile (vaz)geçenlerdir. Allah, iyilik yapanları sever.'),
(428, 3, 135, 'Ve \'çirkin bir hayasızlık\' işledikleri ya da nefislerine zulmettikleri zaman, Allah\'ı hatırlayıp hemen günahlarından dolayı bağışlanma isteyenlerdir. Allah\'tan başka günahları bağışlayan kimdir? Bir de onlar yaptıkları (kötü şeylerde) bile bile ısrar etmeyenlerdir.'),
(429, 3, 136, 'İşte bunların karşılığı, Rablerinden bağışlanma ve içinde ebedi kalacakları, altından ırmaklar akan cennetlerdir. (Böyle) Yapıp-edenlere ne güzel bir karşılık (ecir var).'),
(430, 3, 137, 'Gerçek şu ki, sizden önce nice sünnetler gelip-geçmiştir. Bundan dolayı yeryüzünde gezip-dolaşın da yalanlayanların uğradıkları sonuç nasıl oldu bir görün.'),
(431, 3, 138, 'Bu (Kur\'an) insanlar için bir beyan sakınanlar için de bir hidayet ve öğüttür.'),
(432, 3, 139, 'Gevşemeyin, üzülmeyin; eğer (gerçekten) iman etmişseniz en üstün olan sizlersiniz.'),
(433, 3, 140, 'Eğer bir yara aldıysanız, o kavme de benzeri bir yara değmiştir. İşte o günleri Biz onları insanlar arasında devrettirip dururuz. Bu, Allah\'ın iman edenleri belirtip-ayırması ve sizden şahidler (veya şehidler) edinmesi içindir. Allah, zulmedenleri sevmez;'),
(434, 3, 141, '(Yine bu) Allah\'ın, iman edenleri arındırması ve inkar edenleri yok etmesi içindir.'),
(435, 3, 142, 'Yoksa siz, Allah, içinizden cihad edenleri belirtip-ayırt etmeden ve sabredenleri de belirtip-ayırt etmeden cennete gireceğinizi mi sandınız?'),
(436, 3, 143, 'Andolsun, siz onunla karşılaşmadan önce ölümü temenni ediyordunuz. İşte onu gördünüz, ama bakıp duruyorsunuz.'),
(437, 3, 144, 'Muhammed, yalnızca bir elçidir. Ondan önce nice elçiler gelip-geçmiştir. Şimdi O ölürse ya da öldürülürse, siz topuklarınız üzerinde gerisin geriye mi döneceksiniz? İki topuğu üzerinde gerisin geri dönen kimse, Allah\'a kesinlikle zarar veremez. Allah, şükredenleri pek yakında ödüllendirecektir.'),
(438, 3, 145, 'Allah\'ın izni olmaksızın hiçbir nefis için ölmek yoktur. O, süresi belirtilmiş bir yazıdır. Kim dünyanın yararını (sevabını) isterse ona ondan veririz, kim ahiret sevabını isterse ona da ondan veririz. Biz şükredenleri pek yakında ödüllendireceğiz.'),
(439, 3, 146, 'Nice peygamberle birlikte birçok Rabbani (bilgin)ler savaşa girdiler de, Allah yolunda kendilerine isabet eden (güçlük ve mihnet)den dolayı ne gevşeklik gösterdiler, ne boyun eğdiler. Allah, sabredenleri sever.'),
(440, 3, 147, 'Onların söyledikleri: "Rabbimiz, günahlarımızı ve işimizdeki aşırılıklarımızı bağışla, ayaklarımızı (bastıkları yerde) sağlamlaştır ve bize kafirler topluluğuna karşı yardım et" demelerinden başka bir şey değildi.'),
(441, 3, 148, 'Böylece Allah, dünya ve ahiret sevabının güzelliğini onlara verdi. Allah iyilikte bulunanları sever.'),
(442, 3, 149, 'Ey iman edenler, eğer inkar edenlere itaat ederseniz, sizi topuklarınız üzerinde gerisin-geri çevirirler, böylece büyük hüsrana uğrayanlara dönersiniz.'),
(443, 3, 150, 'Hayır, sizin Mevlanız Allah\'tır. O, yardım edenlerin en hayırlısıdır.'),
(444, 3, 151, 'Kendisi hakkında hiçbir delil indirmediği şeyi Allah\'a ortak koştuklarından dolayı küfredenlerin kalplerine korku salacağız. Onların barınma yerleri ateştir. Zalimlerin konaklama yeri ne kötüdür.'),
(445, 3, 152, 'Andolsun, Allah size verdiği sözünde sadık kaldı; siz O\'nun izniyle onları kırıp-geçiriyordunuz. Öyle ki sevdiğiniz (zafer)i size gösterdikten sonra, siz yılgınlık gösterdiniz, isyan ettiniz ve emir hakkında çekiştiniz. Sizden kiminiz dünyayı, kiminiz ahireti istiyordu. Sonra (Allah) denemek için sizi ondan çevirdi. Ama (yine de) sizi bağışladı. Allah mü\'minlere karşı fazl (ve ihsan) sahibi olandır.'),
(446, 3, 153, 'Siz o zaman durmaksızın uzaklaşıyor, kimseye dönüp bakmıyordunuz. Elçi de sürekli sizi arkadan çağırıyordu. (Allah) Elinizden kaçırdıklarınıza ve size isabet edene üzülmemeniz için sizi kederden kedere uğrattı. Allah, yaptıklarınızdan haberi olandır.'),
(447, 3, 154, 'Sonra kederin ardından üzerinize bir güvenlik (duygusu) indirdi, bir uyuklama ki, içinizden bir grubu sarıveriyordu. Bir grup da, canları derdine düşmüştü; Allah\'a karşı haksız yere cahiliye zannıyla zanlara kapılarak: "Bu işten bize ne var ki?" diyorlardı. De ki: "Şüphesiz işin tümü Allah\'ındır." Onlar, sana açıklamadıkları şeyi içlerinde gizli tutuyorlar, "Bu işten bize bir şey olsaydı, biz burada öldürülmezdik" diyorlar. De ki: "Evlerinizde olsaydınız da üzerlerine öldürülmesi yazılmış olanlar, yine devrilecekleri yerlere gidecekti. (Bunu) Allah, sinelerinizdekini denemek ve kalplerinizde olanı arındırmak için (yaptı). Allah, sinelerin özünde saklı duranı bilendir.'),
(448, 3, 155, 'İki topluluğun karşı karşıya geldikleri gün, sizden geri dönenleri, kazandıkları bazı şeyler dolayısıyla şeytan onların ayağını kaydırmak istemişti. Ama andolsun ki, Allah onları affetti. Şüphesiz Allah, bağışlayandır, yumuşak olandır.'),
(449, 3, 156, 'Ey iman edenler, inkar edenler ile yeryüzünde gezip dolaşırken veya savaşta bulundukları sırada (ölen) kardeşleri için: "Yanımızda olsalardı, ölmezlerdi, öldürülmezlerdi" diyenler gibi olmayın. Allah, bunu onların kalplerinde onulmaz bir hasret olarak kıldı. Dirilten ve öldüren Allah\'tır. Allah, yaptıklarınızı görendir.'),
(450, 3, 157, 'Andolsun, eğer Allah yolunda öldürülür ya da ölürseniz, Allah\'tan olan bir bağışlanma ve rahmet, onların bütün toplamakta olduklarından daha hayırlıdır.'),
(451, 3, 158, 'Andolsun, ölseniz de, öldürülseniz de şüphesiz Allah\'a (varıp) toplanacaksınız.'),
(452, 3, 159, 'Allah\'tan bir rahmet dolayısıyla, onlara yumuşak davrandın. Eğer kaba, katı yürekli olsaydın onlar çevrenden dağılır giderlerdi. Öyleyse onları bağışla, onlar için bağışlanma dile ve iş konusunda onlarla müşavere et. Eğer azmedersen artık Allah\'a tevekkül et. Şüphesiz Allah, tevekkül edenleri sever.'),
(453, 3, 160, 'Eğer Allah size yardım ederse, artık sizi yenilgiye uğratacak yoktur ve eğer sizi \'yapayalnız ve yardımsız\' bırakacak olursa, O’ndan sonra size yardım edecek kimdir? Öyleyse mü\'minler, yalnızca Allah\'a tevekkül etsinler.'),
(454, 3, 161, 'Hiçbir peygambere, emanete ihanet yaraşmaz. Kim ihanet ederse, kıyamet günü ihanet ettiğiyle gelir. Sonra her nefis ne kazandıysa, (ona) eksiksiz olarak ödenir. Onlar haksızlığa uğratılmazlar.'),
(455, 3, 162, 'Allah\'ın rızasına uyan kişi, Allah\'tan bir gazaba uğrayan ve barınma yeri cehennem olan kişi gibi midir? Ne kötü barınaktır o.'),
(456, 3, 163, 'Allah Katında onlar derece derecedir. Allah yaptıklarını görendir.'),
(457, 3, 164, 'Andolsun ki Allah, mü\'minlere, içlerinde kendilerinden onlara bir peygamber göndermekle lütufta bulunmuştur. (Ki O) Onlara ayetlerini okuyor, onları arındırıyor ve onlara kitabı ve hikmeti öğretiyor. Ondan önce ise onlar apaçık bir sapıklık içindeydiler.'),
(458, 3, 165, 'İki misline uğrattığınız bir musibet size isabet edince mi: "Bu nereden" dediniz? De ki: "O, sizin kendinizdendir." Şüphesiz Allah, herşeye güç yetirendir.'),
(459, 3, 166, 'İki topluluğun karşı karşıya geldiği gün, size isabet eden ancak Allah\'ın izniyle idi. (Bu, Allah\'ın) mü\'minleri ayırt etmesi;'),
(460, 3, 167, 'Münafıklık yapanları da belirtmesi içindi. Onlara: "Gelin, Allah\'ın yolunda savaşın ya da savunma yapın" denildiğinde, "Biz savaşmayı bilseydik elbette sizi izlerdik" dediler. O gün onlar, imandan çok küfre daha yakındılar. Kalplerinde olmayanı ağızlarıyla söylüyorlardı. Allah, onların gizli tuttuklarını daha iyi bilir.'),
(461, 3, 168, 'Onlar, kendileri oturup kardeşleri için: "Eğer bize itaat etselerdi, öldürülmezlerdi" diyenlerdir. De ki: "Eğer doğru sözlüler iseniz, ölümü kendinizden savın öyleyse."'),
(462, 3, 169, 'Allah yolunda öldürülenleri sakın \'ölüler\' saymayın. Hayır, onlar, Rableri Katında diridirler, rızıklanmaktadırlar.'),
(463, 3, 170, 'Allah\'ın Kendi fazlından onlara verdikleriyle sevinç içindedirler. Onlara arkalarından henüz ulaşmayanlara müjdelemeyi isterler ki, onlara hiçbir korku yoktur, mahzun da olacak değillerdir.'),
(464, 3, 171, 'Onlar, Allah\'tan bir nimeti, bir fazlı (bolluğu) ve gerçekten Allah\'ın mü\'minlerin ecrini boşa çıkarmadığını müjdelemektedirler.'),
(465, 3, 172, 'Kendilerine yara isabet ettikten sonra, Allah ve elçisinin çağrısına icabet edenler, içlerinden iyilik yapanlar ve sakınanlar için büyük bir ecir vardır.'),
(466, 3, 173, 'Onlar, kendilerine insanlar: "Size karşı insanlar topla(n)dılar, artık onlardan korkun" dedikleri halde imanları artanlar ve: "Allah bize yeter, O ne güzel vekildir" diyenlerdir.'),
(467, 3, 174, 'Bundan dolayı, kendilerine hiçbir kötülük dokunmadan bir bolluk (fazl) ve Allah\'tan bir nimetle geri döndüler. Onlar, Allah\'ın rızasına uydular. Allah, büyük fazl (ve ihsan) sahibidir.'),
(468, 3, 175, 'İşte bu şeytan, ancak kendi dostlarını korkutur. Siz onlardan korkmayın, eğer mü\'minlerseniz, Benden korkun.'),
(469, 3, 176, 'Küfürde \'büyük çaba harcayanlar\' seni üzmesin. Çünkü onlar, Allah\'a hiçbir şeyle zarar veremezler. Allah, onları ahirette pay sahibi kılmamayı ister. Onlar için büyük bir azap vardır.'),
(470, 3, 177, 'Onlar, imana karşılık küfrü satın alanlardır. Onlar, Allah\'a hiçbir şeyle zarar veremezler. Onlar için acıklı bir azap vardır.'),
(471, 3, 178, 'O küfre sapanlar, kendilerine tanıdığımız süreyi sakın kendileri için hayırlı sanmasınlar, Biz onlara, ancak günahları daha da artsın, diye süre vermekteyiz. Onlar için aşağılatıcı bir azap vardır.'),
(472, 3, 179, 'Allah, murdar olanı, temiz olandan ayırt edinceye kadar mü\'minleri, sizin kendisi üzerinde bulunduğunuz durumda bırakacak değildir. Allah sizi gayb üzerine muttali kılacak değildir. Ama Allah, elçilerinden dilediğini seçer. Öyleyse siz de Allah\'a ve elçisine iman edin. Eğer iman eder ve sakınırsanız, sizin için büyük bir ecir vardır.'),
(473, 3, 180, 'Allah\'ın, bol ihsanından kendilerine verdiği şeylerde cimrilik edenler, bunun kendileri için hayırlı olduğunu sanmasınlar. Hayır; bu, onlar için şerdir; kıyamet günü, cimrilik ettikleriyle tasmalandırılacaklardır. Göklerin ve yerin mirası Allah\'ındır. Allah yaptıklarınızdan haberi olandır.'),
(474, 3, 181, 'Andolsun; "Gerçek, Allah fakirdir, biz ise zenginiz" diyenlerin sözlerini Allah işitmiştir. Onların bu sözlerini ve peygamberleri haksız yere öldürmelerini yazacağız ve: "Yakıcı olan azabı tadın" diyeceğiz.'),
(475, 3, 182, 'Bu, ellerinizin önden sunduklarıdır. Allah, gerçekten kullara zulmedici değildir.'),
(476, 3, 183, '"Allah bize ateşin yiyeceği bir kurban getirmedikçe hiçbir elçiye inanmamamız konusunda and verdi," diyenlere de ki: "Şüphesiz, benden önce nice elçiler, apaçık belgeler ve söylediklerinizle geldi; eğer, siz doğru idiyseniz, o halde onları ne diye öldürdünüz?"'),
(477, 3, 184, 'Eğer seni yalanlarlarsa, senden önce apaçık belgeler, Zeburlar ve aydınlık kitapla gelen elçileri de yalanlamışlardır.'),
(478, 3, 185, 'Her nefis ölümü tadıcıdır. Kıyamet günü elbette ecirleriniz eksiksizce ödenecektir. Kim ateşten uzaklaştırılır ve cennete sokulursa, artık o gerçekten kurtuluşa ermiştir. Dünya hayatı, aldatıcı metadan başka bir şey değildir.'),
(479, 3, 186, 'Andolsun, mallarınızla ve canlarınızla imtihan edileceksiniz ve sizden önce kendilerine kitap verilenlerden ve şirk koşmakta olanlardan elbette çok eziyet verici (sözler) işiteceksiniz. Eğer sabreder ve sakınırsanız (bu) emirlere olan azimdendir.'),
(480, 3, 187, 'Hani kitap verilenlerden: "Onu mutlaka insanlara açıklayacaksınız, onu gizlemeyeceksiniz" diye kesin söz almıştı. Fakat onlar, bunu arkalarına attılar ve ona karşılık az bir değeri satın aldılar. O aldıkları şey ne kötüdür.'),
(481, 3, 188, 'Getirdikleriyle sevinen ve yapmadıkları şeyler nedeniyle övülmekten hoşlananları (kazançlı) sayma; onları azaptan kurtulmuş olarak sayma. Onlar için acı bir azap vardır.'),
(482, 3, 189, 'Göklerin ve yerin mülkü Allah\'ındır. Allah, herşeye güç yetirendir.'),
(483, 3, 190, 'Şüphesiz göklerin ve yerin yaratılışında, gece ile gündüzün art arda gelişinde temiz akıl sahipleri için gerçekten ayetler vardır.'),
(484, 3, 191, 'Onlar, ayakta iken, otururken, yan yatarken Allah\'ı zikrederler ve göklerin ve yerin yaratılışı konusunda düşünürler. (Ve derler ki:) "Rabbimiz, Sen bunu boşuna yaratmadın. Sen pek Yücesin, bizi ateşin azabından koru."'),
(485, 3, 192, '"Rabbimiz, şüphesiz Sen kimi ateşe sokarsan, artık onu \'hor ve aşağılık\' kılmışsındır; zulmedenlerin yardımcıları yoktur."'),
(486, 3, 193, '"Rabbimiz, biz: "Rabbinize iman edin" diye imana çağrıda bulunan bir çağırıcıyı işittik, hemen iman ettik. Rabbimiz, bizim günahlarımızı bağışla, kötülüklerimizi ört ve bizi de iyilik yapanlarla birlikte öldür."'),
(487, 3, 194, '"Rabbimiz, elçilerine va\'dettiklerini bize ver, kıyamet gününde de bizi \'hor ve aşağılık\' kılma. Şüphesiz Sen, va\'dine muhalefet etmeyensin."'),
(488, 3, 195, 'Nitekim Rableri onlara (dualarını kabul ederek) cevab verdi: "Şüphesiz Ben, erkek olsun, kadın olsun, sizden bir işte bulunanın işini boşa çıkarmam. Sizin kiminiz kiminizdendir. İşte, hicret edenlerin, yurtlarından sürülüp-çıkarılanların ve yolumda işkence görenlerin, çarpışıp öldürülenlerin, mutlaka kötülüklerini örteceğim ve onları, altlarından ırmaklar akan cennetlere sokacağım. (Bu,) Allah Katından bir karşılık (sevap)tır. (O) Allah, karşılığın (sevabın) en güzeli O\'nun Katındadır."'),
(489, 3, 196, 'İnkar edenlerin ülke ülke dönüp-dolaşmaları seni aldatmasın.'),
(490, 3, 197, '(Bu) Az bir yarar(lanma)dır. Sonra bunların barınma yerleri cehennemdir. Ne kötü bir yataktır o!'),
(491, 3, 198, 'Ama Rablerinden korkup-sakınanlar; onlar için Allah Katında -bir şölen olarak- altlarından ırmaklar akan -içinde ebedi kalacakları- cennetler vardır. İyilik yapanlar için, Allah\'ın Katında olanlar daha hayırlıdır.'),
(492, 3, 199, 'Şüphesiz, Kitap Ehlinden, Allah\'a; size indirilene ve kendilerine indirilene -Allah\'a derin saygı gösterenler olarak- inananlar vardır. Onlar Allah\'ın ayetlerine karşılık olarak az bir değeri satın almazlar. İşte bunların Rableri Katında ecirleri vardır. Şüphesiz Allah, hesabı çok çabuk görendir.'),
(493, 3, 200, 'Ey iman edenler, sabredin ve sabırda yarışın, (sınırlarda) nöbetleşin. Allah\'tan korkun. Umulur ki kurtulursunuz.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Ey insanlar sizi tek bir nefisten yaratan, ondan eşini yaratan ve her ikisinden birçok erkek ve kadın türetip-yayan Rabbinizden korkup-sakının. Ve (yine) kendisiyle, birbirinizle dilekleştiğiniz Allah\'tan ve akrabalık (bağlarını koparmak)tan sakının. Şüphesiz Allah, sizin üzerinizde gözeticidir.'),
(495, 4, 2, 'Yetimlere mallarını verin ve murdar olanla temiz olanı değiştirmeyin. Onların mallarını mallarınıza katarak yemeyin. Çünkü bu, büyük bir suçtur.'),
(496, 4, 3, 'Eğer yetim (kız)lar konusunda adaleti yerine getiremeyeceğinizden korkarsanız, bu durumda, (onlarla değil) size helal olan (başka) kadınlardan ikişer, üçer, dörder olmak üzere nikahlayın. Şayet adaleti sağlayamayacağınızdan korkarsanız, o zaman bir (eş) ya da sağ ellerinizin malik olduğu (cariye) ile (yetinin). Bu, sapmamanıza daha yakındır.'),
(497, 4, 4, 'Kadınlara mehirlerini gönülden isteyerek (ve bir hak olarak) verin, fakat onlar, gönül hoşluğuyla size ondan bir şeyi bağışlarlarsa, onu da afiyetle, iç huzuruyla yiyin.'),
(498, 4, 5, 'Allah\'ın sizin için (kendileriyle hayatınızı) kaim (geçiminizi sağlamaya destekleyici bir araç) kıldığı mallarınızı düşük akıllılara vermeyin; bunlarla onları rızıklandırıp giydirin ve onlara güzel (maruf) söz söyleyin.'),
(499, 4, 6, 'Yetimleri, nikaha erişecekleri çağa kadar deneyin; şayet kendilerinde bir (rüşd) olgunlaşma gördünüz mü, hemen onlara mallarını verin. Büyüyecekler diye israf ile çarçabuk yemeyin. Zengin olan iffetli olmaya çalışsın, yoksul olan da artık maruf (ihtiyaca ve örfe uygun) bir şekilde yesin. Mallarını kendilerine verdiğiniz zaman, onlara karşı şahid bulundurun. Hesap görücü olarak Allah yeter.'),
(500, 4, 7, 'Anne ve baba ile akrabaların bıraktıklarından erkekler için bir pay vardır; anne ve baba ile akrabanın bıraktıklarından kadınlar için de bir pay vardır. Bunun azından ve çoğundan farz kılınmış bir pay vardır.'),
(501, 4, 8, '(Mirası) Bölüşme sırasında yakınlar, yetimler ve yoksullar da hazır olursa, onları ondan rızıklandırın ve onlara güzel (maruf) söz söyleyin.'),
(502, 4, 9, 'Arkalarında bıraktıkları zayıf çocuklardan dolayı korku duyanların, (vasiyetleri altında olanlar için de) içleri ürpertiyle titresin. Allah\'tan korksunlar ve onlara doğru söz söylesinler.'),
(503, 4, 10, 'Gerçekten, yetimlerin mallarını zulmederek yiyenler, karınlarına ancak ateş doldurmuş olurlar. Onlar, çılgın bir ateşe gireceklerdir.'),
(504, 4, 11, 'Çocuklarınız konusunda Allah, erkeğe iki dişinin hissesi kadar tavsiye eder. Eğer onlar ikiden çok kadın ise (ölünün) geride bıraktığının üçte ikisi onlarındır. Kadın (veya kız) bir tek ise, bu durumda yarısı onundur. (Ölenin) Bir çocuğu varsa, geriye bıraktığından anne ve babadan her biri için altıda bir, çocuğu olmayıp da anne ve baba ona mirasçı ise, bu durumda annesi için üçte bir vardır. Onun kardeşleri varsa o zaman annesi için altıda bir\'dir. (Ancak bu hükümler, ölenin) Ettiği vasiyet veya (varsa) borcun düşülmesinden sonradır. Babalarınız, oğullarınız, siz onların hangilerinin yarar bakımından size daha yakın olduğunu bilmezsiniz. (Bunlar) Allah\'tan bir farzdır. Şüphesiz Allah, bilendir, hüküm ve hikmet sahibi olandır.'),
(505, 4, 12, 'Eşlerinizin, eğer çocukları yoksa, geride bıraktıklarının yarısı sizindir. Şayet çocukları varsa, -onunla yapacakları vasiyetten ya da (ayıracakları) borçtan sonra- bu durumda bıraktıklarının dörtte biri sizindir. Sizin çocuğunuz yoksa, geriye bıraktıklarınızdan dörtte biri onların (kadınlarınızın)dır. Eğer sizin çocuğunuz varsa geriye bıraktıklarınızdan sekizde biri onların (kadınlarınızın)dır. (Yine bu hükümler,) Edeceğiniz vasiyet veya (varsa) borcun düşülmesinden sonradır. Mirası aranan erkek ya da kadın, çocuğu ve babası olmayan bir kimse olup erkek veya kız kardeşi bulunursa onlardan her biri için altıda bir vardır. Eğer bundan fazla iseler, bu durumda -kendisiyle yapılan vasiyette ya da (varsa) borçtan sonra- üçte bir\'de -zarara uğratılmaksızın onlara ortaktırlar. (Bu size) Allah\'tan bir vasiyettir, Allah, bilendir, (kullara) yumuşak olandır.'),
(506, 4, 13, 'Bunlar, Allah\'ın sınırlarıdır. Kim Allah\'a ve elçisine itaat ederse, onu altından ırmaklar akan, içinde ebedi kalacakları cennetlere sokar. İşte büyük kurtuluş ve mutluluk budur.'),
(507, 4, 14, 'Kim Allah\'a ve elçisine isyan eder ve onun sınırlarını aşarsa, onu da içinde ebedi kalacağı ateşe sokar. Onun için alçaltıcı bir azap vardır.'),
(508, 4, 15, 'Kadınlarınızdan fuhuş yapanların aleyhinde olmak üzere içinizden dört şahid tutun. Eğer şehadet ederlerse, onları, ölüm alıp götürünceye veya Allah onlara bir yol kılıncaya kadar evlerde alıkoyun.'),
(509, 4, 16, 'Sizlerden fuhuş yapanların, her ikisine eziyet edin. Eğer tevbe ederler de ıslah olurlarsa artık onlardan vazgeçin. Şüphesiz Allah, tevbeleri kabul edendir, esirgeyendir.'),
(510, 4, 17, 'Allah\'ın (kabulünü) üzerine aldığı tevbe, ancak cehalet nedeniyle kötülük yapanların, sonra hemencecik tevbe edenlerin(kidir). İşte Allah, böylelerinin tevbelerini kabul eder. Allah, bilendir, hüküm ve hikmet sahibi olandır.'),
(511, 4, 18, 'Tevbe; ne, kötülükleri yapıp-edip de onlardan birine ölüm çatınca: "Ben şimdi gerçekten tevbe ettim" diyenler, ne de kafir olarak ölenler için değil. Böyleleri için acı bir azap hazırlamışızdır.'),
(512, 4, 19, 'Ey iman edenler, kadınlara zorla mirasçı olmaya kalkışmanız helal değildir. Apaçık olan \'çirkin bir hayasızlık\' yapmadıkları sürece, onlara verdiklerinizin bir kısmını gidermeniz (kendinize almanız) için onlara baskı yapmanız da (helal değildir.) Onlarla güzellikle geçinin. Şayet onlardan hoşlanmadınızsa, belki, bir şey hoşunuza gitmez, ama Allah onda çok hayır kılar.'),
(513, 4, 20, 'Bir eşi bırakıp yerine bir başka eşi almak isterseniz, onlardan birine (öncekine) yüklerle (mal ve para) vermişseniz bile ondan hiçbir şey almayın. Ona iftira ederek ve apaçık bir günaha girerek verdiğinizi alacak mısınız?'),
(514, 4, 21, 'Onu nasıl alırsınız ki, birbirinize katılmış (birleşerek içli-dışlı olmuş)tınız. Onlar sizden kesin bir güvence (kuvvetli bir ahid) de almışlardı.'),
(515, 4, 22, 'Kadınlardan babalarınızın nikahladıklarını nikahlamayın. Ancak (cahiliyede) geçen geçmiştir. Çünkü bu, \'çirkin bir hayasızlık\' ve \'öfke duyulan bir iğrençliktir.\' Ne kötü bir yoldu o!..'),
(516, 4, 23, 'Sizlere anneleriniz, kızlarınız, kız kardeşleriniz, halalarınız, teyzeleriniz, erkek kardeşlerin kızları, kız kardeşlerin kızları, sizi emziren (süt) anneleriniz, süt kız kardeşleriniz, kadınlarınızın anneleri ve kendileriyle (gerdeğe) girdiğiniz kadınlarınızdan olup koruyuculuğunuz altında bulunan üvey kızlarınız -onlarla gerdeğe girmemişseniz, size bir sakınca yoktur-, sizin sülbünüzden olan oğullarınızın eşleri ve iki kız kardeşi biraraya getirdiğiniz (evlilik) haram kılındı. Ancak (cahiliyede) geçen geçmiştir. Şüphesiz, Allah, bağışlayandır, esirgeyendir.'),
(517, 4, 24, 'Sağ ellerinizin malik olduğu (cariyeler) dışındaki kadınlardan \'evli ve özgür\' olanlarla da (evlenmeniz haramdır.) Bunlar, Allah\'ın üzerinize yazdığıdır. Bunların dışında kalanı iffetlerini koruyup fuhuşta bulunmamak üzere mallarınızla (mehir vererek) evlenecek kadın aramanız size helal kılındı. Öyleyse onlardan hangi şeyle (veya ne kadar) yararlandıysanız, onlara ücret (mehir)lerini tespit edildiği miktarıyla ödeyin. Miktarın tespitinden sonra, karşılıklı hoşnut olduğunuz bir şey konusunda üstünüze bir sorumluluk yoktur. Şüphesiz Allah, bilendir, hüküm ve hikmet sahibi olandır.'),
(518, 4, 25, 'İçinizden özgür mü\'min kadınları nikahlamaya güç yetiremeyenler, o zaman sağ ellerinizin malik olduğu inanmış cariyelerinizden (alsın.) Allah sizin imanınızı en iyi bilendir. Öyleyse onları, fuhuşta bulunmayan, iffetli ve gizlice dostlar edinmemişler olarak velilerinin izniyle nikahlayın. Onlara ücretlerini (mehirlerini) maruf (güzel ve örfe uygun) bir şekilde verin. Evlendikten sonra, fuhuş yapacak olurlarsa, özgür kadınlar üzerindeki cezanın yarısı(nı uygulayın.) Bu, sizden günaha sapmaktan endişe edip korkanlar içindir. Sabrederseniz sizin için daha hayırlıdır. Allah, bağışlayandır, esirgeyendir.'),
(519, 4, 26, 'Allah, size açıklayarak anlatmak, sizi sizden öncekilerin sünnetine iletmek ve tevbelerinizi kabul etmek ister. Allah bilendir, hüküm ve hikmet sahibidir.'),
(520, 4, 27, 'Allah, tevbelerinizi kabul etmek ister; şehvetleri ardınca gidenler ise, sizin büyük bir sapma ile sapmanızı isterler.'),
(521, 4, 28, 'Allah (ağır yükleri) sizden hafifletmek ister: (Çünkü) insan zayıf olarak yaratılmıştır.'),
(522, 4, 29, 'Ey iman edenler, mallarınızı, sizden karşılıklı anlaşmadan (doğan) bir ticaretten başka haksız \'nedenler ve yollarla’ (batılca) yemeyin. Ve kendi nefislerinizi öldürmeyin. Şüphesiz, Allah, sizi çok esirgeyendir.'),
(523, 4, 30, 'Kim haddi aşarak ve zulmederek böyle yaparsa, Biz onu ateşe göndeririz. Bu Allah için pek kolaydır.'),
(524, 4, 31, 'Size yasaklanan büyük günahlardan kaçınırsanız, sizin kusurlarınızı örteriz ve sizi \'onurlu-üstün\' bir makama sokarız.'),
(525, 4, 32, 'Allah\'ın kendisiyle kiminizi kiminize göre üstün kıldığı şeyi (malı) temenni etmeyin. Erkeklere kazandıklarından pay (olduğu gibi), kadınlara da kazandıklarından pay vardır. Allah\'tan onun fazlını (ihsanını) isteyin. Gerçekten, Allah herşeyi bilendir.'),
(526, 4, 33, 'Anne-babanın ve yakınların geride bıraktıklarından ve her birine mirasçılar kıldık. Yeminlerinizin (akid ile) bağladığı kimselere de kendi paylarını verin. Şüphesiz, Allah, herşeye şahid olandır.'),
(527, 4, 34, 'Allah\'ın, bazısını bazısına üstün kılması ve onların kendi mallarından harcaması nedeniyle erkekler, kadınlar üzerinde \'sorumlu gözeticidir.\' Saliha kadınlar, gönülden (Allah’a), itaat edenler, Allah nasıl koruduysa görünmeyeni koruyanlardır. Nüşuzundan korktuğunuz kadınlara (önce) öğüt verin, (sonra onları) yataklarda yalnız bırakın, (bu da yetmezse hafifçe) vurun. Size itaat ederlerse aleyhlerinde bir yol aramayın. Doğrusu Allah Yücedir, büyüktür.'),
(528, 4, 35, '(Kadın ile kocanın) Aralarının açılmasından korkarsanız, bu durumda erkeğin ailesinden bir hakem, kadının da ailesinden bir hakem gönderin. Bunlar, (arayı) düzeltmek isterlerse, Allah da aralarında başarı sağlar. Şüphesiz, Allah, bilendir, haberdar olandır.'),
(529, 4, 36, 'Allah\'a ibadet edin ve O\'na hiçbir şeyi ortak koşmayın. Anne-babaya, yakın akrabaya, yetimlere, yoksullara, yakın komşuya, uzak komşuya, yanınızdaki arkadaşa, yolda kalmışa ve sağ ellerinizin malik olduklarına güzellikle davranın. Çünkü, Allah, her büyüklük taslayıp böbürleneni sevmez.'),
(530, 4, 37, 'Onlar, cimrilikte bulunurlar, insanlara da cimriliği emreder (önerir)ler. Allah\'ın fazlından kendilerine verdiğini gizli tutarlar. Biz o kafirlere aşağılatıcı bir azap hazırlamışızdır.'),
(531, 4, 38, 'Ve onlar, mallarını insanlara gösteriş olsun diye infak ederler, Allah\'a ve ahiret gününe de inanmazlar. Şeytan, kime arkadaş olursa, artık ne kötü bir arkadaştır o.'),
(532, 4, 39, 'Allah\'a ve ahiret gününe inanarak Allah\'ın kendilerine verdiği rızıktan infak etselerdi, aleyhlerine mi olurdu? Allah, onları iyi bilendir.'),
(533, 4, 40, 'Gerçek şu ki, Allah zerre ağırlığı kadar haksızlık yapmaz. (Bu ağırlıkta) Bir iyilik olursa, onu kat kat kılar ve Kendi yanından pek büyük bir ecir verir.'),
(534, 4, 41, 'Her ümmetten bir şahid getirdiğimiz ve onların üzerine seni şahit olarak getirdiğimiz zaman nasıl olacak?'),
(535, 4, 42, 'O gün, küfre sapıp da elçiye isyan edenler, yerle bir olmayı \'severek-isteyecekler.\' Oysa Allah\'tan hiçbir sözü gizleyemezler.'),
(536, 4, 43, 'Ey iman edenler, sarhoş iken, ne dediğinizi bilinceye ve cünüp iken de -yolculukta olmanız hariç- gusül edinceye kadar namaza yaklaşmayın. Eğer hasta veya yolculukta iseniz ya da biriniz ayak yolundan (hacet yerinden) gelmişseniz yahut kadınlara dokunmuş da su bulamamışsanız, bu durumda, temiz bir toprakla teyemmüm edin, (hafifçe) yüzlerinize ve ellerinize sürün. Şüphesiz, Allah, bağışlayandır, esirgeyendir.'),
(537, 4, 44, 'Kendilerine kitaptan bir pay verilenlerin sapıklığı satın aldıklarını ve sizin de yolu sapıtmanızı istediklerini görmedin mi?'),
(538, 4, 45, 'Allah, sizin düşmanlarınızı daha iyi bilendir; bir veli (en güvenilir bir dost) olarak Allah yeter, bir yardımcı olarak da Allah yeter.'),
(539, 4, 46, 'Kimi Yahudiler, kelimeleri \'konuldukları yerlerden\' saptırırlar ve dillerini eğip bükerek ve dine bir kin ve hınç besleyerek: "Dinledik ve karşı geldik. İşit, -işitmez olası- ve \'Raina\' bizi güt, bize bak" derler. Eğer onlar: "İşittik ve itaat ettik, sen de işit ve \'Bizi gözet\' deselerdi, elbette kendileri için daha hayırlı ve daha doğru olurdu. Fakat Allah, onları küfürleri dolayısıyla lanetlemiştir. Böylece onlar, az bir bölümü dışında, inanmazlar.'),
(540, 4, 47, 'Ey kendilerine kitap verilenler birtakım yüzleri silip de arkalarına çevirmeden ya da cumartesi adamlarını (o gün yasağı çiğneyenleri) lanetlediğimiz gibi onları da lanetlemeden evvel, yanınızdakini (Tevrat ve İncil\'i) doğrulayıcı olarak indirdiğimize (Kur\'an\'a) iman edin. Allah\'ın emri yapılagelmiştir.'),
(541, 4, 48, 'Gerçekten, Allah, Kendisi\'ne şirk koşulmasını bağışlamaz. Bunun dışında kalanı ise, dilediğini bağışlar. Kim Allah\'a şirk koşarsa, doğrusu büyük bir günahla iftira etmiş olur.'),
(542, 4, 49, 'Kendilerini (övgüyle) temize çıkaranları görmedin mi? Hayır; Allah, dilediğini temizleyip yüceltir. Onlar, \'bir hurma çekirdeğindeki iplikçik kadar\' bile haksızlığa uğratılmazlar.'),
(543, 4, 50, 'Allah\'a karşı nasıl yalan uyduruyorlar, bir bak. Bu, apaçık bir günah olarak yeter.'),
(544, 4, 51, 'Kendilerine kitaptan bir pay verilenleri görmedin mi? Onlar, tağuta ve cibt\'e inanıyorlar ve diğer inkar edenler için: "Bunlar, iman edenlerden daha doğru bir yoldadır" diyorlar.'),
(545, 4, 52, 'İşte bunlar Allah\'ın kendilerini lanetlediğidir. Allah\'ın kendisini lanetlediğine hiçbir yardımcı bulamazsın.'),
(546, 4, 53, 'Yoksa onların mülk\'ten bir payları mı var? Eğer öyle olsaydı, insanlara \'çekirdeğin sırtındaki küçücük bir tomurcuğu\' bile vermezlerdi.'),
(547, 4, 54, 'Yoksa onlar, Allah\'ın Kendi fazlından insanlara verdiklerini mi kıskanıyorlar? Doğrusu Biz, İbrahim ailesine kitabı ve hikmeti verdik; onlara büyük bir mülk de verdik.'),
(548, 4, 55, 'Böylece, onlardan kimi ona inandı, kimi ona sırt çevirdi. Çılgın ateş olarak cehennem yeter.'),
(549, 4, 56, 'Ayetlerimize karşı inkara sapanları şüphesiz ateşe sokacağız. Derileri yanıp döküldükçe, azabı tatmaları için onları başka derilerle değiştireceğiz. Gerçekten, Allah, güçlü ve üstün olandır, hüküm ve hikmet sahibidir.'),
(550, 4, 57, 'İman edip salih amellerde bulunanları, altından ırmaklar akan, içinde ebedi kalacakları cennetlere sokacağız. Onda onlar için tertemiz kılınmış eşler vardır. Ve onları, \'ne sıcak-ne soğuk, tam kararında gölgeliğe\' sokacağız.'),
(551, 4, 58, 'Şüphesiz Allah, size emanetleri ehline (sahiplerine) teslim etmenizi ve insanlar arasında hükmettiğinizde adaletle hükmetmenizi emrediyor. Bununla Allah, size ne güzel öğüt veriyor!.. Doğrusu Allah, işitendir, görendir.'),
(552, 4, 59, 'Ey iman edenler, Allah\'a itaat edin; elçiye itaat edin ve sizden olan emir sahiplerine de. Eğer bir şeyde anlaşmazlığa düşerseniz, artık onu Allah\'a ve elçisine döndürün. Şayet Allah\'a ve ahiret gününe iman ediyorsanız. Bu, hayırlı ve sonuç bakımından daha güzeldir.'),
(553, 4, 60, 'Sana indirilene ve senden önce indirilene gerçekten inandıklarını öne sürenleri görmedin mi? Bunlar, tağutun önünde muhakeme olmayı istemektedirler; oysa onlar onu reddetmekle emrolunmuşlardır. Şeytan da onları uzak bir sapıklıkla sapıtmak ister.'),
(554, 4, 61, 'Onlara: "Allah\'ın indirdiğine ve elçiye gelin" denildiğinde, o münafıkların senden kaçabildiklerince kaçtıklarını görürsün.'),
(555, 4, 62, 'Öyleyse, nasıl olur da, kendi ellerinin sundukları sonucu, onlara bir musibet isabet eder, sonra sana gelerek: "Kuşkusuz, biz iyilikten ve uzlaştırmaktan başka bir şey istemedik" diye Allah\'a yemin ederler?'),
(556, 4, 63, 'İşte bunların, Allah kalplerinde olanı bilmektedir. O halde sen, onlardan yüz çevir, onlara öğüt ver ve onlara nefislerine ilişkin açık ve etkileyici söz söyle.'),
(557, 4, 64, 'Biz elçilerden hiç kimseyi ancak Allah\'ın izniyle kendisine itaat edilmesinden başka bir şeyle göndermedik. Onlar kendi nefislerine zulmettiklerinde şayet sana gelip Allah\'tan bağışlama dileselerdi ve elçi de onlar için bağışlama dileseydi, elbette Allah\'ı tevbeleri kabul eden, esirgeyen olarak bulurlardı.'),
(558, 4, 65, 'Hayır öyle değil; Rabbine andolsun, aralarında çekiştikleri şeylerde seni hakem kılıp sonra senin verdiğin hükme, içlerinde hiçbir sıkıntı duymaksızın, tam bir teslimiyetle teslim olmadıkça, iman etmiş olmazlar.'),
(559, 4, 66, 'Eğer gerçekten Biz, onlara: "Kendinizi öldürün ya da yurtlarınızdan çıkın" diye yazmış olsaydık, onlardan az bir bölümü dışında, bunu yapmazlardı. Onlar, kendilerine verilen öğüdü yerine getirselerdi, bu şüphesiz onlar için hayırlı ve daha sağlam olurdu.'),
(560, 4, 67, 'Biz de onlara, o zaman yanımızdan büyük bir ecir verirdik.'),
(561, 4, 68, 'Ve onları mutlaka dosdoğru yola yöneltip-iletirdik.'),
(562, 4, 69, 'Kim Allah\'a ve Resul\'e itaat ederse, işte onlar Allah\'ın kendilerine nimet verdiği peygamberler, doğrular (ve doğrulayanlar), şehidler ve salihlerle beraberdir. Ne iyi arkadaştır onlar?'),
(563, 4, 70, 'Bu fazl (bol ihsan), Allah\'tandır. Bilen olarak Allah yeter.'),
(564, 4, 71, 'Ey iman edenler, (düşmanlarınıza karşı) tedbirinizi alın da savaşa bölük bölük çıkın ya da topluca çıkın.'),
(565, 4, 72, 'Şüphesiz içinizden ağır davrananlar vardır. Şayet, size bir musibet isabet edecek olsa: "Doğrusu Allah, bana nimet verdi, çünkü onlarla birlikte olmadım" der.'),
(566, 4, 73, 'Eğer size Allah\'tan bir fazl (zafer) isabet ederse, o zaman da, sanki onunla aranızda hiçbir yakınlık yokmuş gibi kuşkusuz şöyle der; "Keşke onlarla birlikte olsaydım, böylece ben de büyük \'kurtuluş ve mutluluğa\' erseydim."'),
(567, 4, 74, 'Öyleyse, dünya hayatına karşılık ahireti satın alanlar, Allah yolunda savaşsınlar; kim Allah yolunda savaşırken, öldürülür ya da galip gelirse ona büyük bir ecir vereceğiz.'),
(568, 4, 75, 'Size ne oluyor ki, Allah yolunda ve: "Rabbimiz, bizi halkı zalim olan bu ülkeden çıkar, bize Katından bir veli (koruyucu sahib) gönder, bize Katından bir yardım eden yolla" diyen erkekler, kadınlar ve çocuklardan zayıf bırakılmışlar adına savaşmıyorsunuz?'),
(569, 4, 76, 'İman edenler Allah yolunda savaşırlar; inkar edenler ise tağut yolunda savaşırlar öyleyse şeytanın dostlarıyla savaşın. Hiç şüphesiz, şeytanın hileli-düzeni pek zayıftır.'),
(570, 4, 77, 'Kendilerine; "Elinizi (savaştan) çekin, namazı kılın, zekatı verin" denenleri görmedin mi? Oysa savaş üzerlerine yazıldığında, onlardan bir grup, insanlardan Allah\'tan korkar gibi- hatta daha da şiddetli bir korkuyla- korkuya kapılıyorlar ve: "Rabbimiz, ne diye savaşı üzerimize yazdın, bizi yakın bir zamana ertelemeli değil miydin?" dediler. De ki: "Dünyanın metaı azdır, ahiret, ise muttakiler için daha hayırlıdır ve siz \'bir hurma çekirdeğindeki ip-ince bir iplik kadar\' bile haksızlığa uğratılmayacaksınız."'),
(571, 4, 78, 'Her nerede olursanız, ölüm sizi bulur; yüksekçe yerlerde tahkim edilmiş şatolarda olsanız bile. Onlara bir iyilik dokunsa: "Bu, Allah\'tandır" derler; onlara bir kötülük dokunsa: "Bu sendendir" derler. De ki: "Tümü Allah\'tandır." Fakat, ne oluyor ki bu topluluğa, hiçbir sözü anlamaya çalışmıyorlar?'),
(572, 4, 79, 'Sana iyilikten her ne gelirse Allah\'tandır, kötülükten de sana ne gelirse o da kendindendir. Biz seni insanlara bir elçi olarak gönderdik; şahid olarak Allah yeter.'),
(573, 4, 80, 'Kim Resûl’e itaat ederse, gerçekte Allah\'a itaat etmiş olur. Kim de yüz çevirirse, Biz seni onların üzerine koruyucu göndermedik.'),
(574, 4, 81, '"Tamam-kabul" derler. Ama yanından çıktıkları zaman, onlardan bir grup, karanlıklarda senin söylediğinin tersini kurarlar. Allah, karanlıklarda kurduklarını yazıyor. Sen de onlardan yüz çevir ve Allah\'a tevekkül et. Vekil olarak Allah yeter.'),
(575, 4, 82, 'Onlar hala Kur\'an\'ı iyice düşünmüyorlar mı? Eğer o, Allah\'tan başkasının Katından olsaydı, kuşkusuz içinde birçok aykırılıklar (çelişkiler, ihtilaflar) bulacaklardı.'),
(576, 4, 83, 'Kendilerine güven veya korku haberi geldiğinde, onu yaygınlaştırıverirler. Oysa bunu peygambere ve kendilerinden olan emir sahiplerine götürmüş olsalardı, onlardan \'sonuç-çıkarabilenler,\' onu bilirlerdi. Allah\'ın üzerinizdeki fazlı ve rahmeti olmasaydı, azınız hariç herhalde şeytana uymuştunuz.'),
(577, 4, 84, 'Artık sen Allah yolunda savaş, kendinden başkasıyla yükümlü tutulmayacaksın. Mü\'minleri hazırlayıp-teşvik et. Umulur ki Allah, küfredenlerin ağır-baskılarını geri püskürtür. Allah, \'kahredici baskısıyla\' daha zorlu, acı sonuçlandırmasıyla da daha zorludur.'),
(578, 4, 85, 'Kim, güzel bir aracılıkla aracılıkta (şefaatte) bulunursa, ondan kendisine bir hisse vardır; kim kötü bir aracılıkla aracılıkta bulunursa, ondan da kendisine bir pay vardır. Allah herşeyin üzerinde koruyucudur.'),
(579, 4, 86, 'Bir selamla selamlandığınızda, siz ondan daha güzeliyle selam verin ya da aynıyla karşılık verin. Şüphesiz, Allah herşeyin hesabını tam olarak yapandır.'),
(580, 4, 87, 'Allah; O\'ndan başka İlah yoktur. Kendisinde hiçbir şüphe olmayan kıyamet gününde sizleri muhakkak toplayacaktır. Allah\'tan daha doğru sözlü kimdir?'),
(581, 4, 88, 'Şu halde münafıklar konusunda ikiye bölünmeniz ne diye? Oysa Allah, onları kazandıkları dolayısıyla tepe taklak etmiştir. Allah\'ın saptırdığını hidayete erdirmek mi istiyorsunuz? Allah kimi saptırırsa, artık sen ona kesin olarak bir yol bulamazsın.'),
(582, 4, 89, 'Onlar, kendilerinin inkara sapmaları gibi sizin de inkara sapmanızı istediler. Böylelikle bir olacaktınız. Öyleyse Allah yolunda hicret edinceye kadar onlardan veliler (dostlar) edinmeyin. Şayet yine yüz çevirirlerse, artık onları tutun ve her nerede ele geçirirseniz öldürün. Onlardan ne bir veli (dost) edinin, ne de bir yardımcı.'),
(583, 4, 90, 'Ancak sizinle aralarında andlaşma bulunan bir kavme sığınanlar ya da hem sizinle, hem kendi kavimleriyle savaşmak (istemeyip bun)dan göğüslerini sıkıntı basıp size gelenler (dokunulmazdır.) Allah dileseydi, onları üstünüze saldırtır, böylece sizinle çarpışırlardı. Eğer sizden uzak durur (geri çekilir), sizinle savaşmaz ve barış (şartların)ı size bırakırlarsa, artık Allah, sizin için onların aleyhinde bir yol kılmamıştır.'),
(584, 4, 91, 'Diğerlerini de sizden ve kendi kavimlerinden güvende olmayı istiyor bulacaksınız. (Ama) Fitneye her geri çağrılışlarında içine başaşağı (balıklama) dalarlar. Şayet sizden uzak durmaz, barış (şartların)ı size bırakmaz ve ellerini çekmezlerse, artık onları her nerede bulursanız tutun ve onları öldürün. İşte size, onların aleyhinde apaçık olan \'destekleyici bir delil\' kıldık.'),
(585, 4, 92, 'Bir mü\'mine, -hata sonucu olması dışında- bir başka mü\'mini öldürmesi yakışmaz. Kim bir mü’mini \'hata sonucu\' öldürürse, mü\'min bir köleyi özgürlüğüne kavuşturması ve ailesine teslim edilecek bir diyeti vermesi gerekir. Onların (bunu) sadaka olarak bağışlamaları başka. Eğer o, mü\'min olduğu halde size düşman olan bir topluluktan ise, bu durumda mü\'min bir köleyi özgürlüğe kavuşturması gerekir. Şayet kendileriyle aranızda andlaşma olan bir topluluktan ise, bu durumda ailesine bir diyet ödemek ve bir mü\'min köleyi özgürlüğe kavuşturmak gerekir. (Diyet ve köle özgürlüğü için gereken imkanı) Bulamayan ise, kesintisiz olarak iki ay oruç tutmalıdır. Bu, Allah\'tan bir tevbedir. Allah bilendir, hüküm ve hikmet sahibidir.'),
(586, 4, 93, 'Kim bir mü\'mini kasıtlı olarak (taammüden) öldürürse cezası, içinde ebedi kalmak üzere cehennemdir. Allah ona gazaplanmış, onu lanetlemiş ve ona büyük bir azap hazırlamıştır.'),
(587, 4, 94, 'Ey iman edenler, Allah yolunda adım attığınız (savaşa çıktığınız) zaman gerekli araştırmayı yapın ve size (İslam geleneğine göre) selam verene, dünya hayatının geçiciliğine istekli çıkarak: "Sen mü\'min değilsin" demeyin. Asıl çok ganimet, Allah Katındadır, bundan önce siz de böyle idiniz; Allah size lütufta bulundu. Öyleyse iyice açıklık kazandırın. Şüphesiz Allah, yaptıklarınızdan haberi olandır.'),
(588, 4, 95, 'Mü\'minlerden, özür olmaksızın oturanlar ile, Allah yolunda mallarıyla ve canlarıyla cihad edenler eşit değildir. Allah, mallarıyla ve canlarıyla cihad edenleri oturanlara göre derece olarak üstün kılmıştır. Tümüne güzelliği (cenneti) va\'detmiştir; ancak Allah, cihad edenleri oturanlara göre büyük bir ecirle üstün kılmıştır.'),
(589, 4, 96, '(Onlara) Kendinden dereceler, bağışlanma ve rahmet (vermiştir.) Allah, bağışlayandır, esirgeyendir.'),
(590, 4, 97, 'Melekler kendi nefislerine zulmedenlerin hayatına son verecekleri zaman derler ki: "Nerede idiniz?" Onlar: "Biz, yeryüzünde zayıf bırakılmışlar (müstaz\'aflar) idik." derler. (Melekler de:) "Hicret etmeniz için Allah\'ın arzı geniş değil miydi?" derler. İşte onların barınma yeri cehennemdir. Ne kötü yataktır o?'),
(591, 4, 98, 'Ancak erkeklerden, kadınlardan ve çocuklardan müstaz\'aflar olup hiçbir çareye güç yetiremeyenler ve bir yol (çıkış) bulamayanlar başka.'),
(592, 4, 99, 'Umulur ki Allah bunları affeder. Allah affedicidir, bağışlayıcıdır.'),
(593, 4, 100, 'Allah yolunda hicret eden, yeryüzünde barınacak çok yer de bulur, genişlik (ve bolluk) da. Allah\'a ve Resûlü’ne hicret etmek üzere evinden çıkan, sonra kendisine ölüm gelen kişinin ecri şüphesiz Allah\'a düşmüştür. Allah, bağışlayıcıdır, esirgeyicidir.'),
(594, 4, 101, 'Yeryüzünde adım attığınızda (yolculuğa ya da savaşa çıktığınızda), kafirlerin size bir kötülük yapmalarından korkarsanız, namazı kısaltmanızda sizin için bir sakınca yoktur. Şüphesiz kafirler, sizin apaçık düşmanlarınızdır.'),
(595, 4, 102, 'İçlerinde olup onlara namazı kıldırdığında, onlardan bir grup, seninle birlikte dursun ve silahlarını (yanlarına) alsın; böylece onlar secde ettiklerinde, arkalarınızda olsunlar. Namazlarını kılmayan diğer grup gelip seninle namaz kılsınlar, onlar da \'korunma araçlarını\' ve silahlarını alsınlar. Küfredenler, size apansız bir baskın yapabilmek için, sizin silahlarınızdan ve emtianız (erzak ve mühimmatınız)dan ayrılmış olmanızı isterler. Yağmur dolayısıyla bir güçlüğünüz varsa veya hastaysanız, silahlarınızı bırakmanızda size bir sorumluluk yoktur. Korunma tedbirlerinizi alın. Şüphesiz, Allah kafirler için aşağılatıcı bir azap hazırlamıştır.'),
(596, 4, 103, 'Namazı bitirdiğinizde, Allah\'ı ayaktayken, otururken ve yan yatarken zikredin. Artık \'güvenliğe kavuşursanız\' namazı dosdoğru kılın. Çünkü namaz, mü\'minler üzerinde vakitleri belirlenmiş bir farzdır.'),
(597, 4, 104, '(Düşmanınız olan) Topluluğu aramakta gevşeklik göstermeyin. Siz acı çekiyorsanız, şüphesiz onlar da, sizin acı çektiğiniz gibi acı çekiyorlar. Oysa siz, onların umud etmediklerini Allah\'tan umuyorsunuz. Allah, bilendir, hüküm ve hikmet sahibidir.'),
(598, 4, 105, 'Şüphesiz, Allah\'ın sana gösterdiği gibi insanlar arasında hükmetmen için Biz sana kitabı hak olarak indirdik. (Sakın) Hainlerin savunucusu olma.'),
(599, 4, 106, 'Ve Allah\'tan bağışlanma dile. Gerçekten Allah, bağışlayandır, esirgeyendir.'),
(600, 4, 107, 'Kendi nefislerine ihanet edenlerden yana mücadeleye girişme. Hiç şüphesiz Allah, ihanette ilerlemiş günahkarı sevmez.'),
(601, 4, 108, 'Onlar, insanlardan gizlerler de Allah\'tan gizlemezler. Oysa O, kendileri, sözden (plan olarak) hoşnut olmayacağı şeyi \'geceleri düzenleyip kurarlarken,\' onlarla beraberdir. Allah, yaptıklarını kuşatandır.'),
(602, 4, 109, 'İşte siz böylesiniz; dünya hayatında onlardan yana mücadele ettiniz. Peki kıyamet günü onlardan yana Allah\'a mücadele edecek kimdir? Ya da onlara vekil olacak kimdir?'),
(603, 4, 110, 'Kim kötülük işler veya nefsine zulmedip sonra Allah\'tan bağışlanma dilerse Allah\'ı bağışlayıcı ve merhamet edici olarak bulur.'),
(604, 4, 111, 'Kim bir günah kazanırsa, o ancak kendi nefsi aleyhinde onu kazanmıştır. Allah, bilendir, hüküm ve hikmet sahibidir.'),
(605, 4, 112, 'Kim bir hata veya günah kazanır da sonra bunu bir suçsuza yüklerse, gerçekten o, böyle bir yalan (bühtan)ı ve apaçık bir günahı yüklenmiştir.'),
(606, 4, 113, 'Eğer Allah\'ın fazlı ve rahmeti senin üzerinde olmasaydı, onlardan bir grup, seni de saptırmak için tasarı kurmuştu. Oysa onlar, ancak kendi nefislerini saptırırlar ve sana hiçbir şeyle zarar veremezler. Allah, sana kitabı ve hikmeti indirdi ve sana bilmediklerini öğretti. Allah\'ın üzerinizdeki fazlı çok büyüktür.'),
(607, 4, 114, 'Onların \'gizlice söyleşmelerinin\' çoğunda hayır yok. Ancak bir sadaka vermeyi veya iyilikte bulunmayı ya da insanların arasını düzeltmeyi emredenlerinki başka. Kim Allah\'ın rızasını isteyerek böyle yaparsa, artık ona büyük bir ecir vereceğiz.'),
(608, 4, 115, 'Kim kendisine \'dosdoğru yol\' apaçık belli olduktan sonra, elçiye muhalefet ederse ve mü\'minlerin yolundan başka bir yola uyarsa, onu döndüğü şeyde bırakırız ve cehenneme sokarız. Ne kötü bir yataktır o!..'),
(609, 4, 116, 'Hiç şüphesiz, Allah, Kendisi\'ne şirk koşanları bağışlamaz. Bunun dışında kalanlar ise, (onlardan) dilediğini bağışlar. Kim Allah\'a şirk koşarsa elbette o uzak bir sapıklıkla sapmıştır.'),
(610, 4, 117, 'Onlar, O\'nu bırakıp da (birtakım) dişilere taparlar. Onlar o her türlü hayırla ilişkisi kesilmiş şeytandan başkasına tapmazlar.'),
(611, 4, 118, 'Allah, onu lanetlemiştir. O da (şöyle) dedi: "Andolsun, kullarından \'miktarları tespit edilmiş bir grubu\' (kendime uşak) edineceğim.'),
(612, 4, 119, 'Onları -ne olursa olsun- şaşırtıp-saptıracağım, en olmadık kuruntulara düşüreceğim ve onlara kesin olarak davarların kulaklarını kesmelerini emredeceğim ve Allah\'ın yarattıklarını değiştirmelerini emredeceğim." Kim Allah\'ı bırakıp da şeytanı dost (veli) edinirse, kuşkusuz o, apaçık bir hüsrana uğramıştır.'),
(613, 4, 120, '(Şeytan) Onlara vaadler ediyor, onları en olmadık kuruntulara düşürüyor. Oysa şeytan, onlara bir aldanıştan başka bir şey va\'detmez.'),
(614, 4, 121, 'Onların barınma yerleri cehennemdir, ondan kaçacak bir yer bulamayacaklardır.'),
(615, 4, 122, 'İman edip salih amellerde bulunanlar, Biz onları altından ırmaklar akan, içinde ebedi kalacakları cennetlere sokacağız. Bu, Allah\'ın gerçek olan va\'didir. Allah\'tan daha doğru sözlü kim vardır?'),
(616, 4, 123, 'Ne sizin kuruntularınızla, ne de Kitap Ehlinin kuruntularıyla değil. Kim kötülük yaparsa, onunla ceza görür; o, Allah\'tan başka bir veli (dost) ve bir yardımcı bulamaz.'),
(617, 4, 124, 'Erkek olsun, kadın olsun inanmış olarak kim salih bir amelde bulunursa, onlar cennete girecek ve onlar, bir \'çekirdeğin sırtındaki tomurcuk kadar\' bile haksızlığa uğramayacaklardır.'),
(618, 4, 125, 'İyilik yaparak kendini Allah\'a teslim eden ve hanif (tevhidi) olan İbrahim\'in dinine uyandan daha güzel din\'li kimdir? Allah, İbrahim\'i dost edinmiştir.'),
(619, 4, 126, 'Göklerde ve yerde ne varsa tümü Allah\'ındır. Allah, herşeyi kuşatandır.'),
(620, 4, 127, 'Kadınlar konusunda senden fetva isterler. De ki: "Onlara ilişkin fetvayı size Allah veriyor. (Bu fetva,) Kendilerine yazılan (hakları veya miras)ı vermediğiniz ve kendilerini nikahlamayı istediğiniz yetim kadınlar ve zayıf çocuklar (hakkında) ile yetimlere karşı adaleti ayakta tutmanız konusunda size kitapta okunmakta olanlardır. Hayır adına her ne yaparsanız, şüphesiz Allah onu bilir.'),
(621, 4, 128, 'Eğer bir kadın, kocasının nüşuzundan veya ondan yüz çevirip uzaklaşmasından korkarsa, barış ile aralarını bulup düzeltmekte ikisi için sakınca yoktur. Barış daha hayırlıdır. Nefisler ise \'kıskançlığa ve bencil tutkulara\' hazır (elverişli) kılınmıştır. Eğer iyilik yapar ve sakınırsanız, şüphesiz, Allah, yaptıklarınızdan haberi olandır.'),
(622, 4, 129, 'Kadınlar arasında adaleti sağlamaya -ne kadar özen gösterseniz de- güç yetiremezsiniz. Öyleyse, büsbütün (birine) eğilim (sevgi ve ilgi) gösterip de öbürünü askıdaymış gibi bırakmayın. Eğer arayı düzeltir ve sakınırsanız, şüphesiz, Allah, bağışlayandır, esirgeyendir.'),
(623, 4, 130, 'Eğer ikisi ayrılacak olurlarsa, Allah her birine \'genişlik (rızık ve ihsan) kaynaklarından\' kazandırır (ihtiyaçlardan korur.) Allah, (rahmetiyle) geniş olandır, hüküm ve hikmet sahibidir.'),
(624, 4, 131, 'Göklerde ve yerde ne varsa Allah\'ındır. Andolsun, Biz sizden önce kitap verilenlere ve sizlere: "Allah\'tan korkup-sakının" diye tavsiye ettik. Eğer inkara saparsanız, şüphesiz, göklerde ve yerde ne varsa Allah\'ındır. Allah, hiçbir şeye ihtiyacı olmayan, hamde layık olandır.'),
(625, 4, 132, 'Göklerde ve yerde ne varsa Allah\'ındır. Vekil olarak Allah yeter.'),
(626, 4, 133, 'Eğer dilerse, ey insanlar, sizi giderir (yok eder) ve başkalarını getirir. Allah, buna güç yetirendir.'),
(627, 4, 134, 'Kim dünya sevab(yarar)ını isterse, dünyanın da, ahiretin de sevabı Allah Katındadır. Allah işitendir, görendir.'),
(628, 4, 135, 'Ey iman edenler, kendiniz, anne-babanız ve yakınlarınız aleyhine bile olsa, Allah için şahidler olarak adaleti ayakta tutun. (Onlar) ister zengin olsun, ister fakir olsun; çünkü Allah onlara daha yakındır. Öyleyse adaletten dönüp heva (tutkuları)nıza uymayın. Eğer dilinizi eğip büker (sözü geveler) ya da yüz çevirirseniz, şüphesiz Allah, yaptıklarınızdan haberi olandır.'),
(629, 4, 136, 'Ey iman edenler, Allah\'a, elçisine, elçisine indirdiği kitaba ve bundan önce indirdiği kitaba iman edin. Kim Allah\'ı, meleklerini, kitaplarını, elçilerini ve ahiret gününü inkar ederse, şüphesiz uzak bir sapıklıkla sapıtmıştır.'),
(630, 4, 137, 'Gerçek şu, iman edip sonra inkara sapanlar, sonra yine iman edip sonra inkara sapanlar sonra da inkarları artanlar… Allah onları bağışlayacak değildir, onları doğru yola da iletecek değildir.'),
(631, 4, 138, 'Münafıklara müjde ver: Onlar için gerçekten acıklı bir azap vardır.'),
(632, 4, 139, 'Onlar, mü\'minleri bırakıp kafirleri dostlar (veliler) edinirler. \'Kuvvet ve onuru (izzeti)\' onların yanında mı arıyorlar? Şüphesiz, \'bütün kuvvet ve onur,\' Allah\'ındır.'),
(633, 4, 140, 'O, size Kitap’ta: "Allah\'ın ayetlerinin inkar edildiğini ve onlarla alay edildiğini işittiğinizde, onlar bir başka söze dalıp geçinceye kadar, onlarla oturmayın, yoksa siz de onlar gibi olursunuz" diye indirdi. Doğrusu Allah, münafıkların ve kafirlerin tümünü cehennemde toplayacak olandır.'),
(634, 4, 141, 'Onlar sizi gözetleyip-duruyorlar. Size Allah\'tan bir fetih (zafer ve ganimet) gelirse: "Sizinle birlikte değil miydik?" derler. Ama kafirlere bir pay düşerse: "Size üstünlük sağlamadık mı, mü\'minlerden size (gelecek tehlikeleri) önlemedik mi?" derler. Allah, kıyamet günü aranızda hükmedecektir. Allah, kafirlere mü\'minlerin aleyhinde kesinlikle yol vermez.'),
(635, 4, 142, 'Gerçek şu ki, münafıklar (sözde), Allah\'ı aldatmaktadırlar. Oysa O, onları aldatandır. Namaza kalktıkları zaman, isteksizce kalkarlar. İnsanlara gösteriş yaparlar ve Allah\'ı ancak çok az anarlar.'),
(636, 4, 143, 'Arada bocalayıp dururlar. Ne onlarla, ne bunlarla. Allah kimi saptırırsa, artık sen ona yol bulamazsın.'),
(637, 4, 144, 'Ey iman edenler, mü\'minleri bırakıp kafirleri veliler (dostlar) edinmeyin. Kendi aleyhinizde Allah\'a apaçık olan kesin bir delil vermek ister misiniz?'),
(638, 4, 145, 'Gerçekten münafıklar, ateşin en alçak tabakasındadırlar. Onlara bir yardımcı bulamazsın.'),
(639, 4, 146, 'Ancak tevbe edenler, ıslah edenler, Allah\'a sımsıkı sarılanlar ve dinlerini katıksız olarak Allah için (halis) kılanlar başka; işte onlar mü\'minlerle beraberdirler. Allah mü\'minlere büyük bir ecir verecektir.'),
(640, 4, 147, 'Eğer şükreder ve iman ederseniz, Allah azabınızla ne yapsın? Allah şükrün karşılığını verendir, bilendir.'),
(641, 4, 148, 'Allah, zulme uğrayanlar dışında, kötü sözün açıkça söylenmesini sevmez. Allah işitendir, bilendir.'),
(642, 4, 149, 'Bir hayrı açıklar ya da gizli tutarsanız veya bir kötülüğü bağışlarsanız, şüphesiz Allah, affedicidir, güç yetirendir.'),
(643, 4, 150, 'Allah\'ı ve elçilerini (tanımayıp) inkar eden, Allah ile elçilerinin arasını ayırmak isteyen, "Bazısına inanırız, bazısını tanımayız" diyen ve bu ikisi arasında bir yol tutturmak isteyenler.'),
(644, 4, 151, 'İşte bunlar, gerçekten kafir olanlardır. Kafirlere aşağılatıcı bir azap hazırlamışızdır.'),
(645, 4, 152, 'Allah\'a ve Resûlü’ne inananlar ve onlardan hiçbiri arasında ayrım yapmayanlar, işte onlara ecirleri verilecektir. Allah, bağışlayandır, esirgeyendir.'),
(646, 4, 153, 'Kitap Ehli, senden kendilerine gökten bir kitap indirmeni istiyor. Musa\'dan bundan daha büyüğünü istemişlerdi. Demişlerdi ki: "Bize Allah\'ı açıkça göster." Böylece zulümlerinden dolayı onlara yıldırım çarpmıştı. Ardından kendilerine apaçık belgeler geldikten sonra, buzağıyı (ilah) edinmişlerdi. Yine bundan dolayı onları affettik ve Musa\'ya apaçık olan ispatlayıcı bir delil verdik.'),
(647, 4, 154, 'Kesin söz vermeleri dolayısıyla Tur\'u üstlerine yükselttik ve onlara: "Bu kapıdan secde ederek girin" dedik ve onlara: "Cumartesinde haddi aşmayın" da dedik. Ve onlardan kesin bir söz aldık.'),
(648, 4, 155, 'Onların kendi sözlerini bozmaları, Allah\'ın ayetlerine karşı inkara sapmaları, peygamberleri haksız yere öldürmeleri ve: "Kalplerimiz örtülüdür" demeleri nedeniyle (onları lanetledik.) Hayır; Allah, inkarları dolayısıyla ona (kalplerine) damga vurmuştur. Onların azı dışında, inanmazlar.'),
(649, 4, 156, '(Bir de) İnkara sapmaları ve Meryem\'in aleyhinde büyük bühtanlar söylemeleri,'),
(650, 4, 157, 'Ve: "Biz, Allah\'ın Resulü Meryem oğlu Mesih İsa\'yı gerçekten öldürdük" demeleri nedeniyle de (onlara böyle bir ceza verdik.) Oysa onu öldürmediler ve onu asmadılar. Ama onlara (onun) benzeri gösterildi. Gerçekten onun hakkında anlaşmazlığa düşenler, kesin bir şüphe içindedirler. Onların bir zanna uymaktan başka buna ilişkin hiçbir bilgileri yoktur. Onu kesin olarak öldürmediler.'),
(651, 4, 158, 'Hayır; Allah onu Kendine yükseltti. Allah üstün ve güçlüdür, hüküm ve hikmet sahibidir.'),
(652, 4, 159, 'Andolsun, Kitap Ehlinden, ölmeden önce ona inanmayacak kimse yoktur. Kıyamet günü, o da onların aleyhine şahid olacaktır.'),
(653, 4, 160, 'Yahudilerin yaptıkları zulüm ve birçok kişiyi Allah\'ın yolundan alıkoymaları nedeniyle (önceleri) kendilerine helal kılınmış güzel şeyleri onlara haram kıldık.'),
(654, 4, 161, 'Ondan nehyedildikleri halde faiz almaları ve insanların mallarını haksız yere yemeleri nedeniyle (öyle yaptık.) Onlardan kafir olanlara pek acıklı bir azap hazırlamışızdır.'),
(655, 4, 162, 'Ancak onlardan ilimde derinleşenler ile mü\'minler, sana indirilene ve senden önce indirilene inanırlar. Namazı dosdoğru kılanlar, zekatı verenler, Allah\'a ve ahiret gününe inananlar; işte bunlar, Biz bunlara büyük bir ecir vereceğiz.'),
(656, 4, 163, 'Nuh\'a ve ondan sonraki peygamberlere vahyettiğimiz gibi, sana da vahyettik. İbrahim\'e, İsmail\'e, İshak\'a, Yakub\'a, torunlarına, İsa\'ya, Eyyub\'a, Yunus\'a, Harun\'a ve Süleyman\'a da vahyettik. Davud\'a da Zebur verdik.'),
(657, 4, 164, 'Ve gerçekten sana daha önceden hikayelerini anlattığımız elçilere, anlatmadığımız elçilere (vahyettik). Allah, Musa ile de konuştu.'),
(658, 4, 165, 'Elçiler; müjdeciler ve uyarıcılar olarak (gönderildi). Öyle ki elçilerden sonra insanların Allah\'a karşı (savunacak) delilleri olmasın. Allah, üstün ve güçlü olandır, hikmet ve hüküm sahibidir.'),
(659, 4, 166, 'Fakat Allah, sana indirdiğiyle şahidlik eder ki, O, bunu kendi ilmiyle indirmiştir. Melekler de şahittirler. Şahid olarak Allah yeter.'),
(660, 4, 167, 'Şüphesiz, inkar edenler ve Allah yolundan alıkoyanlar gerçekten uzak bir sapıklıkla sapmışlardır.'),
(661, 4, 168, 'Gerçek şu ki, inkar edenler ve zulmedenler, Allah onları bağışlayacak değildir, onları bir yola da iletecek değildir.'),
(662, 4, 169, 'Ancak, onda ebedi kalmaları için cehennem yoluna (iletecektir.) Bu da Allah\'a pek kolaydır.'),
(663, 4, 170, 'Ey insanlar, şüphesiz elçi size Rabbinizden hakla geldi. Öyleyse iman edin, sizin için hayırlıdır. Eğer inkara saparsanız, şüphesiz göklerde olanların ve yerde olanların tümü Allah\'ındır. Allah bilendir, hüküm ve hikmet sahibidir.'),
(664, 4, 171, 'Ey Kitap Ehli, dininiz konusunda taşkınlık etmeyin, Allah\'a karşı gerçek olandan başkasını söylemeyin. Meryem oğlu Mesih İsa, ancak Allah\'ın elçisi ve kelimesidir. Onu (‘OL’ kelimesini) Meryem\'e yöneltmiştir ve O\'ndan bir ruhtur. Öyleyse Allah\'a ve elçisine inanınız; "üçtür" demeyiniz. (Bundan) kaçının, sizin için hayırlıdır. Allah, ancak bir tek İlah\'tır. O, çocuk sahibi olmaktan Yücedir. Göklerde ve yerde her ne varsa O\'nundur. Vekil olarak Allah yeter.'),
(665, 4, 172, 'Mesih ve yakınlaştırılmış (yüksek derece sahibi) melekler, Allah\'a kul olmaktan kesinlikle çekimser kalmazlar. Kim O\'na ibadet etmeye \'karşı çekimser\' davranırsa ve büyüklenme gösterirse (bilmeli ki,) onların tümünü huzurunda toplayacaktır.'),
(666, 4, 173, 'Ama iman edenler ve salih amellerde bulunanlar, onlara ecirlerini eksiksiz ödeyecek ve onlara Kendi fazlından ekleyecektir de. Çekimser davrananlar ve büyüklenenler, onları acıklı bir azapla azaplandıracaktır ve kendileri için Allah\'tan başka bir (vekil) koruyucu dost ve yardımcı bulamayacaklardır.'),
(667, 4, 174, 'Ey insanlar Rabbinizden size \'kesin bir kanıt (burhan)\' geldi ve size apaçık bir nur (Kur\'an) indirdik.'),
(668, 4, 175, 'İşte Allah\'a iman edenler ve O\'na sarılanlar, onları Kendisi\'nden olan bir rahmetin ve bir fazlın içine yerleştirecektir ve onları Kendisi\'ne varan dosdoğru bir yola yöneltip-iletecektir.'),
(669, 4, 176, 'Senden fetva isterler. De ki: "Allah, \'çocuksuz ve babasız olanın (kelale’nin)\' mirasına ilişkin hükmü açıklar. Ölen kişinin çocuğu yok da kız kardeşi varsa, geride bıraktıklarının yarısı kız kardeşinindir. Ama (ölen) kız kardeşinin çocuğu yoksa, kendisi (erkek kardeşi) ona mirasçı olur. Eğer kız kardeşi iki ise, geride bıraktıklarının üçte ikisi onlarındır. Ama (mirasçılar) erkekler ve kız kardeşler ise, bu durumda erkek için dişinin iki payı vardır. Allah, -şaşırıp sapmayasınız diye- açıklar. Allah, herşeyi bilendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Ey iman edenler, akitleri yerine getirin. İhramlı iken avlanmayı helal saymaksızın ve size okunacaklar dışta tutulmak üzere, hayvanlar size helal kılındı. Şüphesiz Allah, dilediği hükmü verir.'),
(671, 5, 2, 'Ey iman edenler, Allah\'ın şiarlarına, haram olan ay\'a, kurbanlık hayvanlara, (onlardaki) gerdanlıklara ve Rablerinden bir fazl ve hoşnutluk isteyerek Beyt-i Haram\'a gelenlere sakın saygısızlık etmeyin. İhramdan çıktınız mı artık avlanabilirsiniz. Sizi Mescid-i Haram\'dan alıkoyduklarından dolayı bir topluluğa olan kininiz, sakın sizi haddi aşmaya sürüklemesin. İyilik ve takva konusunda yardımlaşın, günah ve haddi aşmada yardımlaşmayın ve Allah\'tan korkup-sakının. Gerçekten Allah (ceza ile) sonuçlandırması pek şiddetli olandır.'),
(672, 5, 3, 'Ölü eti, kan, domuz eti, Allah\'tan başkası adına kesilen, boğulmuş, vurulmuş, yüksek bir yerden düşmüş, boynuzlanmış yırtıcı hayvan tarafından yenmiş, -(henüz canlıyken yetişip) kestikleriniz hariç,- dikili taşlar üzerine boğazlanan (hayvanlar) ve fal oklarıyla kısmet aramanız size haram kılındı. Bunlar fısktır (günahla yoldan sapmadır.) Bugün inkara sapanlar, sizin dininizden (dininizi yıkmaktan) umut kesmişlerdir. Bugün size dininizi kemale erdirdim, üzerinizdeki nimetimi tamamladım ve size din olarak İslam\'ı seçip-beğendim. Kim \'şiddetli bir açlıkta kaçınılmaz bir ihtiyaçla karşı karşıya kalırsa\' -günaha eğilim göstermeksizin- (bu haram saydıklarımızdan yetecek kadar yiyebilir.) Çünkü Allah bağışlayandır, esirgeyendir.'),
(673, 5, 4, 'Sana, kendilerine neyin helal kılındığını sorarlar. De ki: "Bütün temiz şeyler size helal kılındı." Allah\'ın size öğrettiği gibi öğretip yetiştirdiğiniz avcı hayvanların yakalayıverdiklerinden de -üzerine Allah\'ın adını anarak- yiyin. Allah\'tan korkup-sakının. Şüphesiz Allah, hesabı çabuk görendir.'),
(674, 5, 5, 'Bugün size temiz olan şeyler helal kılındı. (Kendilerine) Kitap verilenlerin yemeği size helal, sizin de yemeğiniz onlara helaldir. Mü\'minlerden özgür ve iffetli kadınlar ile sizden önce (kendilerine) kitap verilenlerden özgür ve iffetli kadınlar da, namuslu, fuhuşta bulunmayan ve gizlice dostlar edinmemişler olarak -onlara ücretlerini (mehirlerini) ödediğiniz takdirde- size (helal kılındı.) Kim imanı tanımayıp küfre saparsa, elbette onun yaptığı boşa çıkmıştır. O ahirette hüsrana uğrayanlardandır.'),
(675, 5, 6, 'Ey iman edenler, namaza kalktığınız zaman yüzlerinizi ve dirseklere kadar ellerinizi yıkayın, başlarınızı meshedin ve her iki topuğa kadar ayaklarınızı da (yıkayın.) Eğer cünüpseniz temizlenin (gusül edin); eğer hasta veya yolculukta iseniz ya da biriniz ayak yolundan (hacet yerinden) gelmişse yahut kadınlara dokunmuşsanız da su bulamamışsanız, bu durumda, temiz bir toprakla teyemmüm edin (hafifçe) yüzlerinize ve ellerinize ondan sürün. Allah size güçlük çıkarmak istemez, ama sizi temizlemek ve üzerinizdeki nimeti tamamlamak ister. Umulur ki şükredersiniz.'),
(676, 5, 7, 'Allah\'ın üzerinizdeki nimetini ve: "İşittik ve itaat ettik" dediğinizde sizi, kendisiyle bağladığı sözünü (misakını) anın. Allah\'tan korkup-sakının. Şüphesiz Allah, sinelerin özünde olanı bilendir.'),
(677, 5, 8, 'Ey iman edenler, adil şahidler olarak, Allah için, hakkı ayakta tutun. Bir topluluğa olan kininiz, sizi adaletten alıkoymasın. Adalet yapın. O, takvaya daha yakındır. Allah\'tan korkup-sakının. Şüphesiz Allah, yapmakta olduklarınızdan haberi olandır.'),
(678, 5, 9, 'Allah, iman edenlere ve salih amellerde bulunanlara va\'detmiştir, onlar için bir bağışlanma ve büyük bir ecir vardır.'),
(679, 5, 10, 'İnkar edenler ve ayetlerimizi yalanlayanlar ise, onlar da, alevli ateşin halkıdırlar.'),
(680, 5, 11, 'Ey iman edenler, Allah\'ın üzerinizdeki nimetini hatırlayın; hani bir topluluk, size ellerini uzatmaya yeltenmişti de, (Allah,) onların ellerini sizlerden geri püskürtmüştü. Allah\'tan korkup-sakının. Mü\'minler yalnızca Allah\'a tevekkül etmelidirler.'),
(681, 5, 12, 'Andolsun, Allah İsrailoğulları\'ndan kesin söz (misak) almıştı. Onlardan on iki güvenilir- gözetleyici göndermiştik. Ve Allah onlara: "Gerçekten Ben sizinle birlikteyim. Eğer namazı kılar, zekatı verir, elçilerime inanır, onları savunup-desteklerseniz ve Allah\'a güzel bir borç verirseniz, şüphesiz sizin kötülüklerinizi örter ve sizi gerçekten, altından ırmaklar akan cennetlere sokarım. Bundan sonra sizden kim inkar ederse, cidden dümdüz bir yoldan sapmıştır."'),
(682, 5, 13, 'Sözleşmelerini bozmaları nedeniyle, onları lanetledik ve kalplerini kaskatı kıldık. Onlar, kelimeleri konuldukları yerlerden saptırırlar. (Sık sık) Kendilerine hatırlatılan şeyden (yararlanıp) pay almayı unuttular. İçlerinden birazı dışında, onlardan sürekli ihanet görür durursun. Yine de onları affet, aldırış etme. Şüphesiz Allah, iyilik yapanları sever.'),
(683, 5, 14, 'Ve: "Biz Hıristiyanlarız" diyenlerden kesin söz (misak) almıştık. Sonunda onlar kendilerine hatırlatılan şeyden (yararlanıp) pay almayı unuttular. Böylece Biz de, kıyamete kadar aralarında kin ve düşmanlık saldık. Allah, yapageldikleri şeyi onlara haber verecektir.'),
(684, 5, 15, 'Ey Kitap Ehli, kitaptan gizlemekte olduklarınızın çoğunu size açıklayan ve birçoğundan geçiveren elçimiz geldi. Size Allah\'tan bir nur ve apaçık bir kitap geldi.'),
(685, 5, 16, 'Allah, rızasına uyanları bununla kurtuluş yollarına ulaştırır ve onları Kendi izniyle karanlıklardan nura çıkarır. Onları dosdoğru yola yöneltip-iletir.'),
(686, 5, 17, 'Andolsun, "Şüphesiz, Allah Meryem oğlu Mesih\'tir." diyenler küfre düşmüştür. De ki: "O, eğer Meryem oğlu Mesih\'i, onun annesini ve yeryüzündekilerin tümünü helak (yok) etmek isterse, Allah\'tan (bunu önlemeye) kim bir şeye malik olabilir? Göklerin, yerin ve bunlar arasındakilerin tümünün mülkü Allah\'ındır; dilediğini yaratır. Allah herşeye güç yetirendir.'),
(687, 5, 18, 'Yahudi ve Hıristiyanlar: "Biz Allah\'ın çocuklarıyız ve sevdikleriyiz" dedi. De ki: "Peki, ne diye sizi günahlarınızdan dolayı azaplandırıyor? Hayır, siz O\'nun yarattığından birer beşersiniz. O, dilediğini bağışlar, dilediğini azaplandırır. Göklerin, yerin ve bunların arasındakilerin tümünün mülkü Allah\'ındır. Son varış O\'nadır."'),
(688, 5, 19, 'Ey Kitap Ehli, elçilerin arası kesildiği dönemde: "Bize müjdeci de, bir uyarıcı da gelmedi" demenize (fırsat kalmasın) diye size apaçık anlatan elçimiz geldi. Böylece müjdeci de, uyarıcı da gelmiştir artık. Allah herşeye güç yetirendir.'),
(689, 5, 20, 'Hani, Musa kavmine (şöyle) demişti: "Ey kavmim, Allah\'ın üzerinizdeki nimetini anın; içinizden peygamberler çıkardı, sizden yöneticiler kıldı ve alemlerden hiç kimseye vermediğini size verdi."'),
(690, 5, 21, '"Ey kavmim, Allah\'ın sizin için yazdığı (girmenizi emrettiği) kutsal yere girin ve gerisin geri arkanıza dönmeyin; yoksa kayba uğrayanlar olarak çevrilirsiniz."'),
(691, 5, 22, 'Dediler ki: "Ey Musa, orda zorba bir kavim vardır, onlar çıkmadıkları sürece biz oraya kesinlikle girmeyiz. Şayet oradan çıkarlarsa, biz de muhakkak gireriz.”'),
(692, 5, 23, 'Korkanlar arasında olup da Allah\'ın kendilerine nimet verdiği iki kişi: "Onların üzerine kapıdan girin. Girerseniz, şüphesiz sizler galibsiniz. Eğer mü\'minlerdenseniz, yalnızca Allah\'a tevekkül edin." dedi.'),
(693, 5, 24, 'Dediler ki: "Ey Musa biz, onlar durduğu sürece hiçbir zaman oraya girmeyeceğiz. Sen ve Rabbin git, ikiniz savaşın. Biz burada duracağız."'),
(694, 5, 25, '(Musa:) "Rabbim, gerçekten kendimden ve kardeşimden başkasına malik olamıyorum. Öyleyse bizimle fasıklar topluluğunun arasını Sen ayır" dedi.'),
(695, 5, 26, '(Allah) Dedi: "Artık orası kendilerine kırk yıl haram kılınmıştır. Onlar yeryüzünde \'şaşkınca dönüp duracaklar.\' Sen de o fasıklar topluluğuna üzülme."'),
(696, 5, 27, 'Onlara Adem\'in iki oğlunun gerçek olan haberini oku: Onlar (Allah\'a) yaklaştıracak birer kurban sunmuşlardı. Onlardan birininki kabul edilmiş, diğerininki kabul edilmemişti. (Kurbanı kabul edilmeyen) Demişti ki: "Seni mutlaka öldüreceğim." (Öbürü de:) "Allah, ancak korkup-sakınanlardan kabul eder."'),
(697, 5, 28, '"Eğer beni öldürmek için elini bana uzatacak olursan, ben seni öldürmek için elimi sana uzatacak değilim. Çünkü ben, alemlerin Rabbi olan Allah\'tan korkarım."'),
(698, 5, 29, '"Şüphesiz kendi günahını ve benim günahımı yüklenmeni ve böylelikle ateşin halkından olmanı isterim. Zulmedenlerin cezası budur."'),
(699, 5, 30, 'Sonunda nefsi ona kardeşini öldürmeyi (tahrik edip zevkli göstererek) kolaylaştırdı; böylece onu öldürdü, bu yüzden hüsrana uğrayanlardan oldu.'),
(700, 5, 31, 'Derken, Allah, ona, yeri eşeleyerek kardeşinin cesedini nasıl gömeceğini gösteren bir karga gönderdi. "Bana yazıklar olsun" dedi. "Şu karga kadar olup da kardeşimin cesedini gömmekten aciz miyim?" Artık o, pişman olmuştu.'),
(701, 5, 32, 'Bu nedenle, İsrailoğulları’na şunu yazdık: Kim bir nefsi, bir başka nefse ya da yeryüzündeki bir fesada karşılık olmaksızın (haksız yere) öldürürse, sanki bütün insanları öldürmüş gibi olur. Kim de onu (öldürülmesine engel olarak) diriltirse, bütün insanları diriltmiş gibi olur. Andolsun, elçilerimiz onlara apaçık belgelerle gelmişlerdir. Sonra bunun ardından onlardan birçoğu yeryüzünde ölçüyü taşıranlardır.'),
(702, 5, 33, 'Allah\'a ve Resûlü’ne karşı savaş açanların ve yeryüzünde bozgunculuğa çalışanların cezası, ancak öldürülmeleri, asılmaları ya da elleriyle ayaklarının çaprazca kesilmesi veya (bulundukları) yerden sürülmeleridir. Bu, dünyadaki aşağılanmalarıdır, ahirette onlar için büyük bir azap vardır.'),
(703, 5, 34, 'Ancak, sizin onlara güç yetirmenizden önce tevbe edenler başka. Bilin ki, şüphesiz Allah bağışlayandır, esirgeyendir.'),
(704, 5, 35, 'Ey iman edenler, Allah\'tan korkup-sakının ve (sizi) O\'na (yaklaştıracak) vesile arayın; O\'nun yolunda cihad edin, umulur ki kurtuluşa erersiniz.'),
(705, 5, 36, 'Gerçek şu ki, inkar edenler, yeryüzünde olanların tümü ve bununla birlikte bir katı daha onların olsa, bununla da kıyamet gününün azabından (kurtulmak için) fidye vermeye kalkışsalar, yine onlardan kabul edilmez. Onlar için acı bir azap vardır.'),
(706, 5, 37, '(Orda) Ateşten çıkmak isterler, ama ondan çıkacak değiller. Onlar için sürekli bir azap vardır.'),
(707, 5, 38, 'Hırsız erkek ve hırsız kadının, (çalıp) kazandıklarına bir karşılık, Allah\'tan, \'tekrarı önleyen kesin bir ceza\' olmak üzere ellerini kesin. Allah üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(708, 5, 39, 'Ancak kim işlediği zulümden sonra tevbe eder ve (davranışlarını) düzeltirse, şüphesiz Allah onun tevbesini kabul eder. Muhakkak Allah, bağışlayandır, esirgeyendir.'),
(709, 5, 40, 'Göklerin ve yerin mülkünün Allah\'a ait olduğunu bilmiyor musun? O, kimi dilerse azaplandırır, kimi dilerse bağışlar. Allah, herşeye güç yetirendir.'),
(710, 5, 41, 'Ey peygamber, kalpleri inanmadığı halde ağızlarıyla "İnandık" diyenlerle Yahudilerden küfür içinde çaba harcayanlar seni üzmesin. Onlar, yalana kulak tutanlar, sana gelmeyen diğer topluluk adına kulak tutanlar (haber toplayanlar)dır. Onlar, kelimeleri yerlerine konulduktan sonra saptırırlar, "Size bu verilirse onu alın, o verilmezse ondan kaçının" derler. Allah, kimin fitne(ye düşme)sini isterse, artık onun için sen Allah\'tan hiçbir şeye malik olamazsın. İşte onlar, Allah\'ın kalplerini arıtmak istemedikleridir. Dünyada onlar için bir aşağılanma, ahirette onlar için büyük bir azap vardır.'),
(711, 5, 42, 'Onlar, yalana kulak tutanlardır, haram yiyicilerdir. Sana gelirlerse aralarında hükmet veya onlardan yüz çevir. Eğer onlardan yüz çevirecek olursan, sana hiçbir şeyle kesin olarak zarar veremezler. Aralarında hükmedersen adaletle hükmet. Şüphesiz, Allah, adaletle hüküm yürütenleri sever.'),
(712, 5, 43, 'Allah\'ın hükmünün bulunduğu Tevrat yanlarında olduğu halde, seni nasıl hakem kılıyorlar ve sonra bunun peşinden yüz çeviriyorlar? İşte onlar, inanmış değildir.'),
(713, 5, 44, 'Gerçek şu ki, Biz Tevrat’ı, içinde bir hidayet ve nur olarak indirdik. Teslim olmuş peygamberler, Yahudilere onunla hükmederlerdi. Bilgin-yöneticiler (Rabbaniyun) ve yüksek bilginler de (Ahbar), Allah\'ın Kitabı’nı korumakla görevli kılındıklarından ve onun üzerine şahidler olduklarından (onunla hükmederlerdi.) Öyleyse insanlardan korkmayın, Benden korkun ve ayetlerimi az bir değere karşılık satmayın. Kim Allah\'ın indirdiğiyle hükmetmezse, işte onlar, kafir olanlardır.'),
(714, 5, 45, 'Biz onda, onların üzerine yazdık: Cana can, göze göz, buruna burun, kulağa kulak, dişe diş ve (bütün) yaralara (karşılık da) kısas vardır. Ama kim bunu sadaka olarak bağışlarsa o kendisi için bir kefarettir. Kim Allah\'ın indirdiğiyle hükmetmezse, işte onlar, zalim olanlardır.'),
(715, 5, 46, 'Onların (peygamberleri) ardından yanlarındaki Tevrat\'ı doğrulayıcı olarak Meryem oğlu İsa\'yı gönderdik ve ona içinde hidayet ve nur bulunan, önündeki Tevrat\'ı doğrulayan ve muttakiler için yol gösterici ve öğüt olan İncil\'i verdik.'),
(716, 5, 47, 'İncil sahipleri Allah\'ın onda indirdikleriyle hükmetsinler. Kim Allah\'ın indirdiğiyle hükmetmezse, işte onlar, fasık olanlardır.'),
(717, 5, 48, 'Sana da (Ey Muhammed,) önündeki kitap(lar)ı doğrulayıcı ve ona \'bir şahid-gözetleyici\' olarak Kitab\'ı (Kur\'an\'ı) indirdik. Öyleyse aralarında Allah\'ın indirdiğiyle hükmet ve sana gelen haktan sapıp onların heva (istek ve tutku)larına uyma. Sizden her biriniz için bir şeriat ve bir yol-yöntem kıldık. Eğer Allah dileseydi, sizi bir tek ümmet kılardı; ancak (bu,) verdikleriyle sizi denemesi içindir. Artık hayırlarda yarışınız. Tümünüzün dönüşü Allah\'adır. Hakkında anlaşmazlığa düştüğünüz şeyleri size haber verecektir.'),
(718, 5, 49, 'Aralarında Allah\'ın indirdiğiyle hükmet ve onların hevalarına uyma. Allah\'ın sana indirdiklerinin bir kısmından seni şaşırtmamaları için onlardan sakın. Şayet yüz çevirirlerse, bil ki, Allah bir kısım günahları nedeniyle onlara bir musibeti tattırmak istemektedir. Şüphesiz, insanların çoğu fasıklardır.'),
(719, 5, 50, 'Onlar hala cahiliye hükmünü mü arıyorlar? Kesin bilgiyle inanan bir topluluk için hükmü, Allah\'tan daha güzel olan kimdir?'),
(720, 5, 51, 'Ey iman edenler, Yahudi ve Hıristiyanları dostlar (veliler) edinmeyin; onlar birbirlerinin dostudurlar. Sizden onları kim dost edinirse, kuşkusuz onlardandır. Şüphesiz Allah, zalimler topluluğuna hidayet vermez.'),
(721, 5, 52, 'İşte kalplerinde hastalık olanları: "Zamanın, felaketleriyle aleyhimize dönüp bize çarpmasından korkuyoruz" diyerek aralarında çabalar yürüttüklerini görürsün. Umulur ki Allah, bir fetih veya Katından bir emir getirecek de, onlar, nefislerinde gizli tuttuklarından dolayı pişman olacaklardır.'),
(722, 5, 53, 'İman edenler: "Olanca yeminleriyle elbette sizlerle birlik olduklarına ilişkin Allah\'a yemin edenler bunlar mıdır? Onların bütün yapıp-ettikleri boşa çıkmıştır, böylece hüsrana uğrayanlar olmuşlardır" derler.'),
(723, 5, 54, 'Ey iman edenler, içinizden kim dininden geri döner (irtidat eder)se, Allah (yerine) Kendisi\'nin onları sevdiği, onların da Kendisi\'ni sevdiği mü\'minlere karşı alçak gönüllü, kafirlere karşı ise \'güçlü ve onurlu,\' Allah yolunda çaba harcayan ve kınayıcının kınamasından korkmayan bir topluluk getirir. Bu, Allah\'ın bir fazlıdır, onu dilediğine verir. Allah (rahmetiyle) geniş olandır, bilendir.'),
(724, 5, 55, 'Sizin dostunuz (veliniz), ancak Allah, O\'nun elçisi, rüku ediciler olarak namaz kılan ve zekatı veren mü\'minlerdir.'),
(725, 5, 56, 'Kim Allah\'ı, Resûlü’nü ve iman edenleri dost (veli) edinirse, hiç şüphe yok, galip gelecek olanlar, Allah\'ın taraftarlarıdır.'),
(726, 5, 57, 'Ey iman edenler, sizden önce kendilerine kitap verilenlerden dininizi, alay ve oyun (konusu) edinenleri ve kafirleri dostlar (veliler) edinmeyin. Ve eğer inanıyorsanız, Allah\'tan korkup-sakının.'),
(727, 5, 58, 'Onlar, siz birbirinizi namaza çağırdığınızda onu alay ve oyun (konusu) edinirler. Bu, gerçekten onların akıl erdirmeyen bir topluluk olmalarındandır.'),
(728, 5, 59, 'De ki: "Ey Kitap Ehli, yalnızca Allah\'a, bize indirilene ve önceden indirilene inanmamız ve sizin çoğunuzun fasıklar olmanız nedeniyle mi bizden hoşlanmıyorsunuz?"'),
(729, 5, 60, 'De ki: "Allah Katında, \'kesinleşmiş bir ceza olarak\' bundan daha kötüsünü haber vereyim mi? Allah\'ın kendisine lanet ettiği, ona karşı gazablandığı ve onlardan maymunlar ve domuzlar kıldığı ile tağuta tapanlar; işte bunlar, yerleri daha kötü ve dümdüz yoldan daha çok sapmışlardır."'),
(730, 5, 61, 'Size geldiklerinde: "İnandık" derler. Oysa onlar inkarla girmişlerdir ve yine onunla çıkmışlardır. Allah, gizli tutmakta olduklarını daha iyi bilir.'),
(731, 5, 62, 'Onlardan çoğunun günahta, düşmanlıkta ve haram yiyicilikte çabalarına hız kattıklarını görürsün. Yapmakta oldukları ne kötüdür'),
(732, 5, 63, 'Bilgin-yöneticileri (Rabbaniyyun) ve yüksek bilginleri (Ahbar), onları, günah söylemelerinden ve haram yiyiciliklerinden sakındırmalı değil miydi? Yapmakta oldukları ne kötüdür.'),
(733, 5, 64, 'Yahudiler: "Allah\'ın eli sıkıdır" dediler. Onların elleri bağlandı ve söylediklerinden dolayı lanetlendiler. Hayır; O\'nun iki eli açıktır, nasıl dilerse infak eder. Andolsun, Rabbinden sana indirilen, onlardan çoğunun taşkınlıklarını ve inkarlarını artıracaktır. Biz de onların arasına kıyamet gününe kadar sürecek düşmanlık ve kin salıverdik. Onlar ne zaman savaş amacıyla bir ateş alevlendirdilerse Allah onu söndürmüştür. Yeryüzünde bozgunculuğa çalışırlar. Allah ise bozguncuları sevmez.'),
(734, 5, 65, 'Eğer, Kitap Ehli iman edip sakınsalardı, elbette onların kötülüklerini örter ve onları \'nimetlerle donatılmış\' cennetlere sokardık.'),
(735, 5, 66, 'Ve eğer onlar Tevrat\'ı, İncil\'i ve kendilerine Rablerinden indirileni (Kur\'an\'ı) ayakta tutsalardı, elbette üstlerinden ve ayaklarının altından (sayısız nimeti) yiyeceklerdi. İçlerinde aşırı olmayan (mutedil) bir ümmet vardır. Onlardan çoğunun yaptıkları ise ne kötüdür!'),
(736, 5, 67, 'Ey peygamber, Rabbinden sana indirileni tebliğ et. Eğer (bu görevini) yapmayacak olursan, O\'nun elçiliğini tebliğ etmemiş olursun. Allah seni insanlardan koruyacaktır. Şüphesiz, Allah, kafir olan bir topluluğu hidayete erdirmez.'),
(737, 5, 68, 'De ki: "Ey Kitap Ehli, Tevrat\'ı, İncil\'i ve size Rabbinizden indirileni ayakta tutmadıkça hiçbir şey üzerinde değilsiniz." Andolsun, Rabbinden sana indirilen, onlardan çoğunun tuğyanlarını ve inkarlarını artıracaktır. Sen de kafirler topluluğuna karşı üzüntüye kapılma.'),
(738, 5, 69, 'Gerçek şu ki, iman edenlerle Yahudiler, Sabiîler ve Hıristiyanlardan Allah\'a, ahiret gününe inanan ve salih amellerde bulunanlar; onlar için korku yoktur, onlar mahzun da olmayacaklardır.'),
(739, 5, 70, 'Andolsun, Biz İsrailoğulları’ndan kesin söz almış (misak) ve onlara elçiler göndermiştik. Onlara ne zaman nefislerinin hoşuna gitmeyen bir şeyle bir elçi geldiyse, bir bölümünü yalanladılar, bir bölümünü de öldürdüler.'),
(740, 5, 71, 'Bir fitne olmayacak sandılar, körleştiler, sağırlaştılar. Sonra Allah, tevbelerini kabul etti, (yine) onlardan çoğunluğu körleştiler, sağırlaştılar. Allah yapmakta olduklarını görendir.'),
(741, 5, 72, 'Andolsun, "Şüphesiz Allah, Meryem oğlu Mesih\'tir" diyenler küfre düşmüştür. Oysa Mesih\'in dediği (şudur:) "Ey İsrailoğulları, benim de Rabbim, sizin de Rabbiniz olan Allah\'a ibadet edin. Çünkü O, Kendisi\'ne ortak koşana şüphesiz cenneti haram kılmıştır, onun barınma yeri ateştir. Zulmedenlere yardımcı yoktur."'),
(742, 5, 73, 'Andolsun, "Allah üçün üçüncüsüdür" diyenler küfre düşmüştür. Oysa tek bir İlah\'tan başka İlah yoktur. Eğer söylemekte olduklarından vazgeçmezlerse, onlardan inkar edenlere mutlaka (acı) bir azap dokunacaktır.'),
(743, 5, 74, 'Yine de Allah\'a tevbe edip bağışlanma istemeyecekler mi? Oysa Allah bağışlayandır, esirgeyendir.'),
(744, 5, 75, 'Meryem oğlu Mesih, yalnızca bir elçidir. Ondan önce de elçiler gelip geçti. Onun annesi dosdoğrudur, ikisi de yemek yerlerdi. Bir bak, onlara ayetleri nasıl açıklıyoruz? (Yine) bir bak, onlar ise nasıl da çevriliyorlar?'),
(745, 5, 76, 'De ki: "Size yarara da, zarara da güç yetirmeyen Allah\'tan başka şeylere mi tapıyorsunuz? Oysa Allah, işitendir, bilendir."'),
(746, 5, 77, 'De ki: "Ey Kitap Ehli, haksız yere dininiz konusunda aşırı gitmeyin ve daha önce sapmış, birçoğunu saptırmış ve dümdüz yoldan kaymış bir topluluğun heva (istek ve tutku)larına uymayın."'),
(747, 5, 78, 'İsrailoğulları’ndan inkar edenlere, Davud ve Meryem oğlu İsa diliyle lanet edilmiştir. Bu, isyan etmeleri ve haddi aşmaları nedeniyledir.'),
(748, 5, 79, 'Yapmakta oldukları münker(çirkin iş)lerden birbirlerini sakındırmıyorlardı. Yapmakta oldukları şey ne kötü idi!'),
(749, 5, 80, 'Onlardan çoğunun inkara sapanlarla dostluklar kurduklarını görürsün. Kendileri için nefislerinin takdim ettiği şey ne kötüdür. Allah onlara gazablandı ve onlar azapta ebedi kalacaklardır.'),
(750, 5, 81, 'Eğer Allah\'a, peygambere ve ona indirilene iman etselerdi, onları dostlar edinmezlerdi. Fakat onlardan çoğu fasık olanlardır.'),
(751, 5, 82, 'Andolsun, insanlar içinde, mü\'minlere en şiddetli düşman olarak Yahudileri ve müşrikleri bulursun. Onlardan, iman edenlere sevgi bakımından en yakın olarak da: "Hıristiyanlarız" diyenleri bulursun. Bu, onlardan (birtakım) papaz ve rahiplerin olması ve onların gerçekte büyüklük taslamamaları nedeniyledir.'),
(752, 5, 83, 'Elçiye indirileni dinlediklerinde hakkı tanıdıklarından dolayı gözlerinin yaşlarla dolup taştığını görürsün. Derler ki: "Rabbimiz inandık; öyleyse bizi şahidlerle birlikte yaz."'),
(753, 5, 84, '"Hem Rabbimiz\'in bizi salihler topluluğuna katmasını umarken ne diye Allah\'a ve bize Hak’tan gelene inanmayalım?"'),
(754, 5, 85, 'Böylelikle Allah, dediklerine karşılık olarak içinde ebedi kalacakları, altından ırmaklar akan cennetler verdi. Bu, iyilik yapanların karşılığıdır.'),
(755, 5, 86, 'İnkar edenler ve ayetlerimizi yalanlayanlar; işte onlar, çılgın ateşin arkadaşlarıdırlar.'),
(756, 5, 87, 'Ey iman edenler, Allah\'ın sizin için helal kıldığı güzel şeyleri haram kılmayın ve haddi aşmayın. Şüphesiz Allah, haddi aşanları sevmez.'),
(757, 5, 88, 'Allah\'ın size rızık olarak verdiklerinden helal ve temiz olarak yiyin. Kendisi\'ne inanmakta olduğunuz Allah\'tan korkup-sakının.'),
(758, 5, 89, 'Allah sizi, yeminlerinizdeki ‘rastgele söylemelerinizden, boş sözlerden’ dolayı sorumlu tutmaz, ancak yeminlerinizle bağladığınız sözlerden dolayı sizi sorumlu tutar. Onun (yeminin) kefareti, ailenizdekilere yedirdiklerinizin ortalamasından on yoksulu doyurmak ya da onları giydirmek veya bir köleyi özgürlüğüne kavuşturmaktır. (Bunlara imkan) Bulamayan (için) üç gün oruç (vardır.) Bu, yemin ettiğinizde (bozduğunuz) yeminlerinizin kefaretidir. Yeminlerinizi koruyunuz. Allah, size ayetlerini böyle açıklar, umulur ki şükredersiniz.'),
(759, 5, 90, 'Ey iman edenler, içki, kumar, dikili taşlar ve fal okları ancak şeytanın işlerinden olan pisliklerdir. Öyleyse bun(lar)dan kaçının; umulur ki kurtuluşa erersiniz.'),
(760, 5, 91, 'Gerçekten şeytan, içki ve kumarla aranıza düşmanlık ve kin düşürmek, sizi, Allah\'ı anmaktan ve namazdan alıkoymak ister. Artık vazgeçtiniz değil mi?'),
(761, 5, 92, 'Allah\'a itaat edin, peygambere de itaat edin ve sakının. Eğer yüz çevirirseniz, bilin ki, elçimize düşen, ancak apaçık bir tebliğdir.'),
(762, 5, 93, 'İman edenler ve salih amellerde bulunanlar için korkup-sakındıkları, iman ettikleri ve salih amellerde bulundukları, sonra korkup-sakındıkları ve iman ettikleri ve sonra (yine) korkup-sakındıkları ve iyilikte bulundukları takdirde (yasaklanmadan önce) dedikleri dolayısıyla bir sorumluluk yoktur. Allah, iyilik yapanları sever.'),
(763, 5, 94, 'Ey iman edenler, Allah görünmezlikte (gaybte) Kendisi\'nden kimin korktuğunu ortaya çıkarmak için ellerinizin ve mızraklarınızın erişeceği avdan bir şeyle andolsun sizi deneyecektir. Artık kim bundan sonra haddi aşarsa, onun için acı bir azap vardır.'),
(764, 5, 95, 'Ey iman edenler, siz ihramlıyken avı öldürmeyin. Sizden kim onu kasıtlı olarak (taammüden) öldürürse, cezası, hayvandan öldürdüğünün bir benzeridir. Buna da, Kabe\'ye ulaşmış bir kurbanlık olarak içinizden adalet sahibi iki kişi hükmedecektir. Veya yoksulları doyurmak veya onun dengi oruç tutmak olan bir kefaret vardır. Böylelikle işlediğinin vebalini tatmış olsun. Allah geçmişte olanı bağışladı. Ama kim tekrarlarsa, Allah ondan öç alacaktır. Allah üstün ve güçlü olandır, öç sahibidir.'),
(765, 5, 96, 'Deniz avı ve onu yemek size ve (yeryüzünde) dolaşanlara bir yarar olarak helal kılındı. İhramlı olduğunuz sürece kara avı ise size haram kılınmıştır. O\'na (götürülüp) toplanacağınız Allah\'tan korkup-sakının.'),
(766, 5, 97, 'Allah, Beyt-i Haram (olan) Kabe\'yi insanlar için bir ayaklanma (kıyam evi) kıldı; Haram Ay\'ı, kurbanı ve boyunlardaki gerdanlıkları da. Bu, Allah\'ın göklerde ve yerde ne varsa tümünü bildiğini ve Allah\'ın gerçekten herşeyi bilen olduğunu bilmeniz içindir.'),
(767, 5, 98, 'Bilin ki, Allah gerçekten cezası pek şiddetli olandır. Ve Allah bağışlayandır, esirgeyendir.'),
(768, 5, 99, 'Elçiye tebliğden başka (yükümlülük) yoktur. Allah açığa vurduklarınızı da, gizli tuttuklarınızı da bilir.'),
(769, 5, 100, 'De ki: "Murdar ile temiz -murdarın çokluğu hoşuna gitse de- bir olmaz. Ey temiz akıl sahipleri, Allah\'tan korkup-sakının. Umulur ki kurtuluşa erersiniz.'),
(770, 5, 101, 'Ey iman edenler, size açıklandığında sizi üzecek şeyleri sormayın; Kur\'an indirildiği zaman sorarsanız, size açıklanır. Allah onu affetti. Allah bağışlayandır, (kullara) yumuşak olandır.'),
(771, 5, 102, 'Sizden önce bir topluluk onu sormuştu da sonra kafirler olmuşlardı.'),
(772, 5, 103, 'Allah Bahriye\'den, Saibe\'den, Vasiyle\'den ve Ham\'dan hiçbirini (meşru) kılmamıştır. Ancak inkar edenler, Allah\'a karşı yalan düzüp-uyduruyorlar. Onların çoğu akıl erdirmezler.'),
(773, 5, 104, 'Onlara: "Allah\'ın indirdiğine ve elçiye gelin" denildiğinde, "Atalarımızı üzerinde bulduğumuz şey bize yeter" derler. (Peki,) Ya ataları bir şey bilmiyor ve hidayete ermiyor idilerse?'),
(774, 5, 105, 'Ey iman edenler, üzerinizdeki (yükümlülük) kendi nefislerinizdir. Siz doğru yola erişirseniz, sapan size zarar veremez. Tümünüzün dönüşü Allah\'adır. O, size yaptıklarınızı haber verecektir.'),
(775, 5, 106, 'Ey iman edenler, sizden birinize ölüm gelip çattığı zaman, vasiyet hazırlanışında, aranızda içinizden adaletli iki kişiyi (şahid tutun.) Veya yolculukta olup size ölüm musibeti gelip çatarsa, sizden olmayan başka iki kişiyi (şahid tutun. İkisini) Şayet kuşkulanacak olursanız namazdan sonra alıkoyarsınız, onlar da (size): "Akraba dahi olsa onu (yeminimizi) hiçbir değere değiştirmeyeceğiz ve Allah\'ın şahidliğini gizlemeyeceğiz. Aksi takdirde biz elbette günahkarlardan oluruz" diye Allah adına yemin etsinler.'),
(776, 5, 107, 'Eğer o ikisi aleyhinde kesin olarak günahı hak ettiklerine ilişkin bilgi sahibi olunursa, bu durumda haksızlığa uğrayanlardan iki kişi -ki bunlar buna daha hak sahibidirler- öbürlerinin yerine geçerler ve: "Bizim şehadetimiz o ikisinin şehadetinden şüphesiz daha doğrudur. Biz haddi aşmadık, yoksa gerçekten zulmedenlerden oluruz" diye Allah\'a yemin ederler.'),
(777, 5, 108, 'Bu, gerektiği gibi şahidliği yapmalarına veya yeminlerinden sonra yeminlerin reddedilmesinden korkmalarına daha yakındır. Allah\'tan korkup-sakının ve dinleyin. Allah, fasıklar topluluğunu hidayete erdirmez.'),
(778, 5, 109, 'Allah, elçileri toplayacağı gün, şöyle diyecek: "Size verilen cevap nedir?" Onlar da: "Bizim bilgimiz yoktur; şüphesiz görünmeyenleri (gaybleri) bilen Sensin Sen."'),
(779, 5, 110, 'Allah şöyle diyecek: "Ey Meryem oğlu İsa, sana ve annene olan nimetimi hatırla. Ben seni Ruhu\'l-Kudüs ile destekledim, beşikte iken de, yetişkin iken de insanlarla konuşuyordun. Sana Kitab’ı, hikmeti, Tevrat\'ı ve İncil\'i öğrettim. İznimle çamurdan kuş biçiminde (bir şeyi) oluşturuyordun da (yine) iznimle ona üfürdüğünde bir kuş oluveriyordu. Doğuştan kör olanı, alacalıyı iznimle iyileştiriyordun, (yine) Benim iznimle ölüleri (hayata) çıkarıyordun. İsrailoğulları’na apaçık belgelerle geldiğinde onlardan inkara sapanlar, "Şüphesiz bu apaçık bir sihirdir" demişlerdi (de) İsrailoğulları’nı senden geri püskürtmüştüm."'),
(780, 5, 111, 'Hani Havarilere: "Bana ve elçime iman edin" diye vahy (ilham) etmiştim; onlar da: "İman ettik, gerçekten Müslümanlar olduğumuza sen de şahid ol" demişlerdi.'),
(781, 5, 112, 'Havariler: "Ey Meryem oğlu İsa, Rabbin bize gökten bir sofra indirebilir mi?" demişlerdi. O da: "Eğer inanmışlarsanız Allah\'tan korkup-sakının" demişti.'),
(782, 5, 113, '(Bu sefer Havariler:) "Ondan yemek istiyoruz, kalplerimiz tatmin olsun, senin de gerçekten bize doğru söylediğini bilelim ve buna şahidlerden olalım" demişlerdi.'),
(783, 5, 114, 'Meryem oğlu İsa: "Allah\'ım, Rabbimiz, bize gökten bir sofra indir, öncemiz ve sonramız için bir bayram ve Senden de bir belge olsun. Bizi rızıklandır, Sen rızık vericilerin en hayırlısısın" demişti.'),
(784, 5, 115, 'Allah demişti ki: "Şüphesiz Ben bunu size indireceğim. Artık sonra sizden kim inkar ederse, Ben onu gerçekten alemlerden hiç kimseyi azaplandırmayacağım bir azapla azaplandıracağım."'),
(785, 5, 116, 'Allah: "Ey Meryem oğlu İsa, insanlara, beni ve annemi Allah\'ı bırakarak iki İlah edinin, diye sen mi söyledin?" dediğinde: "Seni tenzih ederim, hakkım olmayan bir sözü söylemek bana yakışmaz. Eğer bunu söyledimse mutlaka Sen onu bilmişsindir. Sen bende olanı bilirsin, ama ben Sende olanı bilmem. Gerçekten, görünmeyenleri (gaybleri) bilen Sensin Sen."'),
(786, 5, 117, '"Ben onlara bana emrettiklerinin dışında hiçbir şeyi söylemedim. (O da şuydu:) \'Benim de Rabbim, sizin de Rabbiniz olan Allah\'a kulluk edin.\' Onların içinde kaldığım sürece, ben onların üzerinde bir şahidim. Benim (dünya) hayatıma son verdiğinde, üzerlerindeki gözetleyici Sendin. Sen herşeyin üzerine şahid olansın.”'),
(787, 5, 118, 'Eğer onları azaplandırırsan, şüphesiz onlar Senin kullarındır, eğer onları bağışlarsan, şüphesiz Aziz olan, hakim olan Sensin Sen."'),
(788, 5, 119, 'Allah dedi ki: "Bu, doğrulara, doğru söylemelerinin yarar sağladığı gündür. Onlar için, içinde ebedi kalacakları, altından ırmaklar akan cennetler vardır. Allah onlardan razı oldu, onlar da O\'ndan razı olmuşlardır. İşte büyük \'kurtuluş ve mutluluk\' budur."'),
(789, 5, 120, 'Göklerin, yerin ve içlerinde olanların tümünün mülkü Allah\'ındır. O, herşeye güç yetirendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Hamd, gökleri ve yeri yaratan, karanlıkları ve aydınlığı (nuru) kılan Allah\'adır. (Bundan) Sonra bile, inkar edenler, Rablerine (birtakım varlıkları ve güçleri) denk tutuyorlar.'),
(791, 6, 2, 'Sizi çamurdan yaratan, sonra bir ecel belirleyen O\'dur. Adı konulmuş ecel, O\'nun Katındadır. Sonra siz (yine) kuşkuya kapılıyorsunuz.'),
(792, 6, 3, 'Göklerde ve yerde Allah O\'dur. Gizlinizi ve açığınızı bilir; kazandıklarınızı da bilir.'),
(793, 6, 4, 'Onlara Rablerinin ayetlerinden bir ayet gelmeyiversin, mutlaka ondan yüz çevirirler.'),
(794, 6, 5, 'Kendilerine hak gelince, onu yalanladılar; fakat alaya aldıklarının haberleri onlara gelecektir.'),
(795, 6, 6, 'Kendilerinden önce nice nesilleri yıkıma uğrattığımızı görmüyorlar mı? Biz, sizi yerleşik kılmadığımız bir biçimde onları yeryüzünde (büyük bir güç ve servetle) yerleşik kıldık; gökten üzerlerine sağanak (bol yağmurlar) yağdırdık, nehirleri de altlarından akar yaptık. Ama günahları nedeniyle Biz onları yıkıma uğrattık ve arkalarından başka nesiller (inşa edip) var ettik.'),
(796, 6, 7, 'Biz kitabı üzerine yazılı bir kağıtta göndersek ve onlar elleriyle dokunsalar bile, inkar edenler, tartışmasız: "Bu apaçık bir büyüden başkası değildir" derler.'),
(797, 6, 8, 'Ve derler ki: "Ona bir melek indirilmeli değil miydi?" Eğer bir melek indirilseydi, elbette iş bitirilmiş olurdu da sonra kendilerine göz açtırılmazdı.'),
(798, 6, 9, 'Onu eğer bir melek kılsaydık, elbette erkek (suretinde bir melek) kılardık ve mutlaka katmakta oldukları (şüpheleri) yine katardık.'),
(799, 6, 10, 'Andolsun, senden önceki elçiler de alaya alındı da alaya aldıkları şey, onlardan maskaralık yapanları çepeçevre kuşatıverdi.'),
(800, 6, 11, 'De ki: "Yeryüzünde gezip dolaşın, sonra yalanlayanların sonu nasıl oldu, bir görün."'),
(801, 6, 12, 'De ki: "Göklerde ve yerde olanlar kimindir?" De ki: "Allah\'ındır." O, rahmeti Kendi üzerine yazdı. Sizi kendisinde şüphe olmayan kıyamet gününde elbette toplayacaktır. Nefislerini hüsrana uğratanlar, işte onlar inanmayanlardır.'),
(802, 6, 13, 'Geceleyin ve gündüzün barınan herşey O\'nundur. O, işitendir, bilendir.'),
(803, 6, 14, 'De ki: "O, gökleri ve yeri yaratırken ve O, (hep) besleyen (hiç) beslenmezken, ben Allah\'tan başkasını mı veli edineceğim?" De ki: "Bana gerçekten Müslüman olanların ilki olmam emredildi ve: Sakın müşriklerden olma." (denildi.)'),
(804, 6, 15, 'De ki: "Şüphesiz ben, Rabbime isyan edersem o büyük günün azabından korkarım."'),
(805, 6, 16, 'O gün, kim ondan (azaptan) alıkonursa, elbette, O, onu esirgemiştir. İşte apaçık olan \'kurtuluş ve mutluluk\' budur.'),
(806, 6, 17, 'Şayet Allah sana bir zarar dokunduracak olursa, O\'ndan başka bunu giderecek yoktur. Sana bir iyilik dokunduracak olursa da O, herşeye güç yetirendir.'),
(807, 6, 18, 'O, kulları üzerinde kahredici olandır. O, hüküm ve hikmet sahibi olandır, haberdar olandır.'),
(808, 6, 19, 'De ki: "Şahidlik bakımından hangi şey daha büyüktür?" De ki: "Allah benimle sizin aranızda şahiddir. Sizi -ve kime ulaşırsa- kendisiyle uyarmam için bana şu Kur\'an vahyedildi. Gerçekten Allah\'la beraber başka ilahların da bulunduğuna siz mi şahidlik ediyorsunuz?" De ki: "Ben şehadet etmem." De ki: "O, ancak bir tek olan İlah\'tır ve gerçekten ben, sizin şirk koşmakta olduklarınızdan uzağım."'),
(809, 6, 20, 'Bizim kendilerine kitap verdiklerimiz, onu, çocuklarını tanır gibi tanırlar. Kendilerini hüsrana uğratanlar; işte onlar inanmayanlardır.'),
(810, 6, 21, 'Allah\'a karşı yalan uydurup iftira düzenden veya O\'nun ayetlerini yalanlayandan daha zalim kimdir? Hiç şüphesiz o zalimler kurtuluşa eremezler.'),
(811, 6, 22, 'Onların tümünü toplayacağımız gün; sonra şirk koşanlara diyeceğiz ki: "Nerede (o bir şey) sanıp da ortak koştuklarınız?"'),
(812, 6, 23, '(Bundan) Sonra onların: "Rabbimiz olan Allah\'a andolsun ki, biz müşriklerden değildik" demelerinden başka bir fitneleri olmadı (kalmadı.)'),
(813, 6, 24, 'Bak, kendilerine karşı nasıl yalan söylediler ve düzmekte oldukları da kendilerinden kaybolup-uzaklaştı.'),
(814, 6, 25, 'Onlardan seni dinleyenler vardır; oysa Biz, onu kavrayıp anlamalarına (bir engel olarak) kalpleri üzerine kat kat örtüler ve kulaklarında bir ağırlık kıldık. Onlar, hangi \'apaçık-belgeyi\' görseler, yine ona inanmazlar. Öyle ki, o inkar etmekte olanlar, sana geldiklerinde, seninle tartışmaya girerek: "Bu, öncekilerin uydurma masallarından başka bir şey değildir" derler.'),
(815, 6, 26, 'Onlar, hem ondan alıkoyarlar, hem kendileri kaçarlar. Onlar, yalnızca kendi nefislerinden başkasını yıkıma uğratmazlar ama şuurunda değildirler.'),
(816, 6, 27, 'Ateşin üstünde durdurulduklarında onları bir görsen; derler ki: "Keşke (dünyaya bir daha) geri çevrilseydik de Rabbimiz\'in ayetlerini yalanlamasaydık ve mü\'minlerden olsaydık."'),
(817, 6, 28, 'Hayır, önceden saklı tuttukları kendilerine açıklandı. Şayet (dünyaya) geri çevrilseler bile, kendisinden sakındırıldıkları şeylere şüphesiz yine döneceklerdir. Çünkü onlar, gerçekten kafirlerdir.'),
(818, 6, 29, 'Onlar dediler ki: "Bu dünya hayatımızdan başkası yoktur. Ve bizler diriltilecek değiliz."'),
(819, 6, 30, 'Rablerinin karşısında durdurulduklarında onları bir görsen: (Allah:) "Bu, gerçek değil mi?" dedi. Onlar: "Evet, Rabbimiz hakkı için" dediler. (Allah:) "Öyleyse inkar edegeldikleriniz nedeniyle azabı tadın" dedi.'),
(820, 6, 31, 'Allah\'a kavuşmayı yalan sayanlar, doğrusu hüsrana uğramışlardır. Öyle ki, saat (kıyamet günü) apansız onlara geliverince, günahlarını sırtlarına yüklenerek: "Onda (dünyada) sorumsuzca yaptıklarımızdan dolayı yazıklar olsun bize…" derler. Dikkat edin, o işleyip-yüklendikleri ne kötüdür.'),
(821, 6, 32, 'Dünya hayatı yalnızca bir oyun ve bir oyalanmadan başkası değildir. Korkup-sakınmakta olanlar için ahiret yurdu gerçekten daha hayırlıdır. Yine de akıl erdirmeyecek misiniz?'),
(822, 6, 33, 'Kesin olarak biliyoruz ki, onların söyledikleri seni gerçekten üzüyor. Doğrusu onlar, seni yalanlamıyorlar, ancak zalimler, Allah\'ın ayetlerini inkar ediyorlar.'),
(823, 6, 34, 'Andolsun senden önce de elçiler yalanlandı; onlara, yardımımız gelinceye kadar yalanlandıkları ve eziyete uğratıldıkları şeye sabrettiler. Allah\'ın sözlerini (va\'dlerini) değiştirebilecek yoktur. Andolsun, gönderilenlerin haberlerinden bir bölümü sana da geldi.'),
(824, 6, 35, 'Eğer onların yüz çevirmeleri sana ağır geldiyse, onlara bir ayet getirmek için yerde bir tünel açmaya veya göğe bir merdiven dayamaya gücün yetiyorsa (yap). Eğer Allah dileseydi, onların tümünü hidayet üzere toplardı. Öyleyse sakın cahillerden olma.'),
(825, 6, 36, 'Ancak dinleyenler icabet eder. Ölüleri (ise,) onları da Allah diriltir. Sonra O\'na döndürülürler.'),
(826, 6, 37, '"Ona Rabbinden bir ayet indirilmeli değil miydi?" dediler. De ki: Şüphesiz Allah, ayet indirmeye güç yetirendir." Ama onların çoğu bilmezler.'),
(827, 6, 38, 'Yeryüzünde hiçbir canlı ve iki kanadıyla uçan hiçbir kuş yoktur ki, sizin gibi ümmetler olmasın. Biz kitapta hiçbir şeyi noksan bırakmadık, sonra onlar Rablerine toplanacaklardır.'),
(828, 6, 39, 'Bizim ayetlerimizi yalan sayanlar karanlıklar içinde sağırdırlar, dilsizdirler. Allah, kimi dilerse onu şaşırtıp-saptırır, kimi dilerse de onu dosdoğru yol üzerinde kılar.'),
(829, 6, 40, 'De ki: "Düşündünüz mü hiç; eğer size Allah\'ın azabı gelirse ya da saat (kıyamet) gelip çatarsa, Allah\'tan başkasını mı çağıracaksınız? Eğer doğru sözlüler iseniz (çağırın bakalım.)"'),
(830, 6, 41, 'Hayır, yalnızca O\'nu çağırırsınız, dilerse kendisini çağırdığınız şeyi açar (giderir) ve şirk koşmakta olduklarınızı unutursunuz.'),
(831, 6, 42, 'Andolsun, senden önceki ümmetlere (peygamberler) gönderdik de onları dayanılmaz zorluk (yoksulluk) ve sıkıntılarla çeviriverdik. Umulur ki yalvarırlar diye.'),
(832, 6, 43, 'Onlara, zorlu azabımız geldiği zaman yalvarmaları gerekmez miydi? Ama onların kalpleri katılaştı ve şeytan onlara yapmakta olduklarını çekici (süslü) gösterdi.'),
(833, 6, 44, 'Derken kendilerine hatırlatılanı unuttuklarında, onların üzerlerine herşeyin kapılarını açtık. Öyle ki kendilerine verilen şeylerle \'sevince kapılıp şımarınca\', onları apansız yakalayıverdik. Artık onlar umutları suya düşenler oldular.'),
(834, 6, 45, 'Böylece zulmeden topluluğun kökü kurutuldu. Hamd, alemlerin Rabbi olan Allah\'adır.'),
(835, 6, 46, 'De ki: "Düşündünüz mü hiç; eğer Allah sizin işitmenizi ve görmenizi alıverir ve kalplerinizi mühürlerse, onları size Allah\'tan başka getirebilecek ilah kimdir?" Bak, Biz nasıl ayetleri \'çeşitli biçimlerde açıklıyoruz da\' sonra onlar (yine) sırt çevirip-engelliyorlar?'),
(836, 6, 47, 'De ki: "Düşündünüz mü hiç; size Allah\'ın azabı apansız ya da açıktan geliverirse, zulme sapan kavimden başkası mı yıkıma uğrayacak?"'),
(837, 6, 48, 'Biz elçileri müjde vericiler ve uyarıp-korkutucular olmaktan başka (bir nedenle) göndermiyoruz. Şu halde kim iman ederse ve (davranışlarını) düzeltirse, artık onlar için korku yoktur, onlar mahzun da olmayacaklardır.'),
(838, 6, 49, 'Ayetlerimizi yalanlayanlara, fıska sapmalarından dolayı azap dokunacaktır.'),
(839, 6, 50, 'De ki: "Size Allah\'ın hazineleri yanımdadır demiyorum, gaybı da bilmiyorum ve ben size bir meleğim de demiyorum. Ben, bana vahyedilenden başkasına uymam." De ki: "Kör olanla, gören bir olur mu? Yine de düşünmeyecek misiniz?"'),
(840, 6, 51, 'Rablerine (götürülüp) toplanacaklarından korkanları onunla (Kur\'an\'la) uyarıp-korkut; onlar için ondan başka ne velileri vardır ne şefaatçileri. Umulur ki korkup-sakınırlar.'),
(841, 6, 52, 'Sabah akşam -O\'nun yüzünü (rızasını) dileyerek- Rablerine dua edenleri kovma. Onların hesabından senin üzerinde bir şey (yükümlülük), senin hesabından da bir şey (yükümlülük) yoktur ki onları kovman gereksin. Yoksa zalimlerden olursun.'),
(842, 6, 53, 'Böylece: "Allah içimizden bunlara mı lütufta bulundu?" demeleri için onlardan bazısını bazısıyla denedik. Allah, şükredenleri daha iyi bilen değil mi?'),
(843, 6, 54, 'Bizim ayetlerimize iman edenler sana geldiklerinde, onlara de ki: "Selam olsun size. Rabbiniz rahmeti Kendi üzerine yazdı ki, içinizden kim bir cehalet sonucu bir kötülük işler sonra tevbe eder ve (kendini) ıslah ederse şüphesiz, O, bağışlayandır, esirgeyendir."'),
(844, 6, 55, 'Suçlu-günahkarların yolu apaçık ortaya çıksın diye, ayetlerimizi işte böyle birer birer açıklıyoruz.'),
(845, 6, 56, 'De ki: "Ben, sizin Allah\'tan başka tapmakta olduklarınıza tapmaktan nehyedildim." De ki: "Ben sizin heva (istek ve tutku)larınıza uymam; yoksa bu durumda ben şaşırıp sapmış ve doğru yolu bulmamışlardan olurum."'),
(846, 6, 57, 'De ki: "Ben, gerçekten Rabbimden kesin bir belge üzerindeyim, siz ise onu yalanladınız. Sizin kendisine acele ettiğiniz (azap) yanımda değildir. Hüküm yalnızca Allah\'ındır. O, doğru haberi verir ve O, ayırt edenlerin en hayırlısıdır."'),
(847, 6, 58, 'De ki: "Kendisine acele etmekte olduğunuz şey benim yanımda olsaydı, benimle aranızda iş elbette bitirilmiş olurdu. Allah zulmedenleri en iyi bilendir.'),
(848, 6, 59, 'Gaybın anahtarları O\'nun Katındadır, O\'ndan başka hiç kimse gaybı bilmez. Karada ve denizde olanların tümünü O bilir, O, bilmeksizin bir yaprak dahi düşmez; yerin karanlıklarındaki bir tane, yaş ve kuru dışta olmamak üzere hepsi (ve herşey) apaçık bir kitaptadır.'),
(849, 6, 60, 'Sizi geceleyin öldüren (uyutan) ve gündüzün \'güç yetirip etkilemekte (yapıp kazanmakta) olduklarınızı\' bilen, sonra adı konulmuş ecel doluncaya kadar onda sizi dirilten (uyandıran) O\'dur. Sonra \'en son dönüşünüz\' O\'nadır. Sonra yapmakta olduklarınızı size O haber verecektir.'),
(850, 6, 61, 'O, kulları üzerinde kahredici (kahhar) olandır. Size koruyucular gönderiyor. Sonunda sizden birinize ölüm gelip çattığı zaman, elçilerimiz onun \'hayatına son verirler.\' Onlar (bu işte, ne eksik ne fazla) kusur etmezler.'),
(851, 6, 62, 'Sonra gerçek mevlaları olan Allah\'a döndürülürler. Haberiniz olsun; hüküm yalnızca O\'nundur. Ve O, hesap görenlerin en süratli olanıdır.'),
(852, 6, 63, 'De ki: "Sizi karanın ve denizin karanlıklarından kim kurtarmaktadır ki, siz (açıktan ve) gizliden gizliye ona yalvararak dua etmektesiniz: -Andolsun, bizi bundan kurtarırsan, gerçekten şükredenlerden oluruz."'),
(853, 6, 64, 'De ki: "Ondan ve her türlü sıkıntıdan sizi Allah kurtarmaktadır. Sonra siz yine şirk koşmaktasınız."'),
(854, 6, 65, 'De ki: "O, size üstünüzden ya da ayaklarınızın altından azap göndermeye veya sizi parça parça birbirinize kırdırıp kiminizin şiddetini kiminize taddırmaya güç yetirendir." Bak, iyice kavrayıp-anlamaları için ayetleri nasıl çeşitli biçimlerde açıklıyoruz?'),
(855, 6, 66, 'Senin kavmin, O (Kur\'an) hak iken onu yalanladı. De ki: "Ben, üzerinize bir vekil değilim."'),
(856, 6, 67, 'Her bir haber için \'kararlaştırılmış bir zaman (müstakar)\' vardır. Siz de bileceksiniz.'),
(857, 6, 68, 'Ayetlerimiz konusunda \'alaylı tartışmalara dalanlar:\' -onlar bir başka söze geçinceye kadar- onlardan yüz çevir. Şeytan sana unutturacak olursa, bu durumda hatırlamadan sonra, artık zulmeden toplulukla beraber oturma.'),
(858, 6, 69, 'Korkup-sakınanlar üzerinde onların hesabından herhangi bir şey (sorumluluk) yoktur. Ancak (bu,) bir hatırlatmadır. Umulur ki sakınırlar.'),
(859, 6, 70, 'Dinlerini bir oyun ve eğlence (konusu) edinenleri ve dünya hayatı kendilerini mağrur kılanları bırak. Onunla (Kur\'an\'la) hatırlat ki, bir nefis, kendi kazandıklarıyla helake düşmesin; (böylesinin) Allah\'tan başka ne bir velisi, ne bir şefaatçisi vardır; her türlü fidyeyi verse de kabul olunmaz. İşte onlar, kazandıkları nedeniyle helake uğrayanlardır; küfre saptıklarından dolayı onlar için çılgınca kaynar sular ve acıklı bir azap vardır.'),
(860, 6, 71, 'De ki: "Bize yararı ve zararı olmayan Allah\'tan başka şeylere mi tapalım? Allah bizi hidayete erdirdikten sonra, şeytanların ayartarak yerde şaşkınca bıraktıkları, arkadaşlarının da: "Doğru yola, bize gel" diye kendisini çağırdığı kimse gibi topuklarımız üzerinde gerisin geri mi döndürülelim?" De ki: "Hiç şüphesiz Allah\'ın yolu, asıl yoldur. Ve biz alemlerin Rabbine (kendimizi) teslim etmekle emrolunduk."'),
(861, 6, 72, 'Bir de: "Namazı kılın ve O\'ndan korkup-sakının (diye de emrolunduk.) Huzuruna (götürülüp) toplanacağınız O\'dur."'),
(862, 6, 73, 'O, gökleri ve yeri hak olarak yaratandır. O\'nun "ol" dediği gün (herşey) oluverir, O\'nun sözü haktır. Sur\'a üfürüldüğü gün, mülk O\'nundur. O, gaybı ve müşahede edilebileni bilendir. O, hüküm ve hikmet sahibi olandır, haberdar olandır.'),
(863, 6, 74, 'Hani İbrahim, babası Azer\'e (şöyle) demişti: "Sen putları ilahlar mı ediniyorsun? Doğrusu, ben seni ve kavmini apaçık bir sapıklık içinde görüyorum."'),
(864, 6, 75, 'Böylece İbrahim\'e, -kesin bilgiyle inananlardan olması için- göklerin ve yerin melekûtunu gösteriyorduk.'),
(865, 6, 76, 'Gece, üstünü örtüp bürüyünce bir yıldız görmüş ve demişti ki: "Bu benim Rabbimdir." Fakat (yıldız) kayboluverince: "Ben kaybolup-gidenleri sevmem" demişti.'),
(866, 6, 77, 'Ardından Ay\'ı, (etrafa aydınlık saçarak) doğar görünce: "Bu benim Rabbim" demiş, fakat o da kayboluverince: "Andolsun" demişti, "Eğer Rabbim beni doğru yola erdirmezse gerçekten sapmışlar topluluğundan olurum."'),
(867, 6, 78, 'Sonra Güneş’i (etrafa ışıklar saçarak) doğar görünce: "İşte bu benim Rabbim, bu en büyük" demişti. Ama o da kayboluverince, kavmine demişti ki: "Ey kavmim, doğrusu ben sizin şirk koşmakta olduklarınızdan uzağım."'),
(868, 6, 79, '"Gerçek şu ki, ben bir muvahhid olarak yüzümü gökleri ve yeri yaratana çevirdim. Ve ben müşriklerden değilim."'),
(869, 6, 80, 'Kavmi onunla çekişip-tartışmaya girdi. Dedi ki: "O beni doğru yola erdirmişken, siz benimle Allah konusunda çekişip-tartışmaya mı girişiyorsunuz? Sizin O\'na şirk koştuklarınızdan ben korkmuyorum, ancak Allah\'ın benim hakkımda bir şey dilemesi başka. Rabbim, ilim bakımından herşeyi kuşatmıştır. Yine de öğüt alıp-düşünmeyecek misiniz?"'),
(870, 6, 81, '"Hem siz, O’nun haklarında hiçbir delil indirmediği şeyleri Allah’a ortak koşmaktan korkmazken, ben nasıl sizin şirk koştuklarınızdan korkarım? Şu halde \'güvenlik içinde olmak bakımından\' iki taraftan hangisi daha hak sahibidir? Eğer bilebilirseniz."'),
(871, 6, 82, 'İman edenler ve imanlarını zulümle karıştırmayanlar, işte güvenlik onlar içindir ve onlar hidayete ermişlerdir.'),
(872, 6, 83, 'Bu, İbrahim\'e, kavmine karşı verdiğimiz delilimizdir. Biz, dilediğimizi derecelerle yükseltiriz. Şüphesiz senin Rabbin, hüküm ve hikmet sahibidir, bilendir.'),
(873, 6, 84, 'Ve ona İshak\'ı ve Yakub\'u armağan ettik, hepsini hidayete eriştirdik; bundan önce de Nuh\'u ve onun soyundan Davud\'u, Süleyman\'ı, Eyyub\'u, Yusuf\'u, Musa\'yı ve Harun\'u hidayete ulaştırdık. Biz, iyilik yapanları işte böyle ödüllendiririz.'),
(874, 6, 85, 'Zekeriya\'yı, Yahya\'yı, İsa\'yı ve İlyas\'ı da (hidayete eriştirdik.) Onların hepsi salihlerdendir.'),
(875, 6, 86, 'İsmail\'i, Elyasa\'yı, Yunus\'u ve Lut\'u da (hidayete eriştirdik). Onların hepsini alemlere üstün kıldık.'),
(876, 6, 87, 'Babalarından, soylarından ve kardeşlerinden, kimini (bunlara kattık); onları da seçtik ve dosdoğru yola yöneltip-ilettik.'),
(877, 6, 88, 'Bu, Allah\'ın hidayetidir; kullarından dilediğini bununla hidayete erdirir. Onlar da şirk koşsalardı, elbette bütün yapıp-ettikleri \'onlar adına\' boşa çıkmış olurdu.'),
(878, 6, 89, 'Bunlar, kendilerine kitap, hikmet ve peygamberlik verdiklerimizdir. Eğer bunları tanımayıp-küfre sapıyorlarsa, andolsun, Biz buna (karşı) inkara sapmayan bir topluluğu vekil kılmışızdır.'),
(879, 6, 90, 'İşte Allah\'ın hidayet verdikleri bunlardır; öyleyse sen de onların bu hidayetlerine uy. De ki: "Ben bunun için sizden bir ücret istemiyorum. O (Kur\'an), alemlere bir \'öğüt ve hatırlatmadan\' başkası değildir."'),
(880, 6, 91, 'Onlar: "Allah, beşere hiçbir şey indirmemiştir" demekle Allah\'ı, kadrinin hakkını vererek takdir edemediler. De ki: "Musa\'nın insanlara bir nur ve hidayet olarak getirdiği ve sizin de (parça parça) kağıtlar üzerinde yazılı kılıp (bir kısmını) açıkladığınız ve çoğunu göz ardı ettiğiniz kitabı kim indirdi? Sizin ve atalarınızın bilmediği şeyler size öğretilmiştir." De ki: "Allah." Sonra onları bırak, içine \'daldıkları saçma uğraşılarında\' oyalanıp-dursunlar.'),
(881, 6, 92, 'İşte bu (Kur\'an), önündekileri doğrulayıcı ve şehirler anası (Mekke) ile çevresindekileri uyarman için indirdiğimiz kutlu Kitap’tır. Ahirete iman edenler buna inanırlar. Onlar namazlarını (özenle) koruyanlardır.'),
(882, 6, 93, 'Allah\'a karşı yalan uydurup iftira düzenden veya kendisine hiçbir şey vahyolunmamışken “Bana da vahy geldi" diyen ve "Allah\'ın indirdiğinin bir benzerini de ben indireceğim" diyenden daha zalim kimdir? Sen bu zalimleri, ölümün \'şiddetli sarsıntıları\' sırasında meleklerin ellerini uzatarak onlara: "Canlarınızı (bu kıskıvrak yakalanıştan) çıkarın, bugün Allah\'a karşı haksız olanı söylediğiniz ve O\'nun ayetlerinden büyüklenerek (yüz çevirmeniz) dolayısıyla alçaltıcı bir azapla karşılık göreceksiniz" (dediklerinde) bir görsen...'),
(883, 6, 94, 'Andolsun, sizi ilk defa yarattığımız gibi (bugün de) \'teker teker, yapayalnız ve yalın (bir tarzda)\' Bize geldiniz ve size lütfettiklerimizi arkanızda bıraktınız. İçinizden, gerçekten ortaklar olduklarını sandığınız şefaatçilerinizi şimdi yanınızda görmüyoruz. Andolsun, aranızdaki (bağlar) parçalanıp-koparılmıştır ve haklarında zanlar besledikleriniz sizlerden uzaklaşmıştır.'),
(884, 6, 95, 'Taneyi ve çekirdeği yaran şüphesiz Allah\'tır. O, diriyi ölüden çıkarır, ölüyü de diriden çıkarır. İşte Allah budur. Öyleyse nasıl oluyor da çevriliyorsunuz?'),
(885, 6, 96, 'O, sabahı yarıp çıkarandır. Geceyi bir sükun (dinlenme), Güneş ve Ay\'ı bir hesap (ile) kıldı. Bu, üstün ve güçlü olan, bilen Allah\'ın takdiridir.'),
(886, 6, 97, 'O, karanın ve denizin karanlıklarından yolunuzu bulmanız için size yıldızları var edendir. Bilebilen bir topluluk için Biz ayetleri birer birer (bölüm bölüm) açıkladık.'),
(887, 6, 98, 'O, sizi tek bir nefisten yaratandır. (Sizin için) Bir karar (kalış) ve emanet (olarak konuluş) yeri vardır. Kavrayabilen bir topluluk için ayetleri birer birer açıkladık.'),
(888, 6, 99, 'O, gökten su indirendir. Bununla herşeyin bitkisini bitirdik, ondan bir yeşillik çıkardık, ondan birbiri üstüne bindirilmiş taneler türetiyoruz. Ve hurma ağacının tomurcuğundan da yere sarkmış salkımlar, -birbirine benzeyen ve benzemeyen- üzümlerden, zeytinden ve nardan bahçeler (kılıyoruz.) Meyvesine, ürün verdiğinde ve olgunluğa eriştiğinde bir bakıverin. Şüphesiz inanacak bir topluluk için bunda gerçekten ayetler vardır.'),
(889, 6, 100, 'Cinleri Allah\'a ortak koştular. Oysa onları O yaratmıştır. Bir de hiçbir bilgiye dayanmaksızın O\'na oğullar ve kızlar yakıştırıp-uydurdular. O ise nitelendiregeldikleri şeylerden Yücedir, uzaktır.'),
(890, 6, 101, 'Gökleri ve yeri bir örnek edinmeksizin yaratandır. O\'nun nasıl bir çocuğu olabilir? O\'nun bir eşi (zevcesi) yoktur. O, herşeyi yaratmıştır. O, herşeyi bilendir.'),
(891, 6, 102, 'İşte Rabbiniz olan Allah budur. O\'ndan başka İlah yoktur. Herşeyin Yaratıcısı\'dır, öyleyse O\'na kulluk edin. O, herşeyin üstünde bir vekildir.'),
(892, 6, 103, 'Gözler O\'nu idrak edemez; O ise bütün gözleri idrak eder. O, latif olandır, haberdar olandır.'),
(893, 6, 104, 'Gerçek şu ki size Rabbinizden basiretler gelmiştir. Kim basiretle-görürse kendi lehine, kim de kör olursa (görmek istemezse) kendi aleyhinedir. Ben sizin üzerinizde gözetleyici değilim.'),
(894, 6, 105, 'İşte Biz, ayetleri çeşitli biçimlerde böyle açıklıyoruz. Öyle ki sana: "Sen ders almışsın" desinler ve Biz de bilebilen bir topluluğa onu açıkça göstermiş olalım.'),
(895, 6, 106, 'Rabbinden sana vahyedilene uy. O\'ndan başka İlah yoktur. Ve müşriklerden yüz çevir.'),
(896, 6, 107, 'Eğer Allah dileseydi onlar şirk koşmazdı. Biz seni onlar üzerinde bir gözetleyici kılmadık; sen onlar üzerinde bir vekil değilsin.'),
(897, 6, 108, 'Allah\'tan başka yalvarıp-yakardıklarına (taptıklarına) sövmeyin; sonra onlar da haddi aşarak bilmeksizin Allah\'a söverler. İşte böyle, Biz her ümmete yaptıklarını süslü (çekici) gösterdik, sonra onların son varışları Rablerinedir. O, yapmakta olduklarını onlara haber verecektir.'),
(898, 6, 109, 'Olanca yeminleriyle, eğer kendilerine bir ayet gelse, kesin olarak ona inanacaklarına dair Allah\'a yemin ettiler. De ki: "Ayetler, ancak Allah Katındadır; onlara (mucizeler) gelse de kuşkusuz inanmayacaklarının şuurunda değil misiniz?'),
(899, 6, 110, 'Biz onların kalplerini ve gözlerini, ilkin inanmadıkları gibi tersine çeviririz ve onları tuğyanları içinde şaşkınca dolaşır bir durumda terk ederiz.'),
(900, 6, 111, 'Gerçek şu ki, Biz onlara melekler indirseydik, onlarla ölüler konuşsaydı ve herşeyi karşılarına toplasaydık, -Allah\'ın dilediği dışında- yine onlar inanmayacaklardı. Ancak onların çoğu cahillik ediyorlar.'),
(901, 6, 112, 'Böylece her peygambere, insan ve cin şeytanlarından bir düşman kıldık. Onlardan bazısı bazısını aldatmak için yaldızlı sözler fısıldarlar. Rabbin dileseydi bunu yapmazlardı. Öyleyse onları yalan olarak düzmekte olduklarıyla baş başa bırak.'),
(902, 6, 113, 'Bir de ahirete inanmayanların kalpleri ona meyletsin de ondan (bu yaldızlı ve içi çarpık sözlerden) hoşlansınlar ve yüklenmekte olduklarını yüklenedursunlar.'),
(903, 6, 114, 'Allah\'tan başka bir hakem mi arayayım? Oysa O, size kitabı açıklanmış olarak indirmiştir. Kendilerine kitap verdiklerimiz, bunun gerçekten Rabbinden hak olarak indirilmiş olduğunu bilmektedirler. Şu halde, sakın kuşkuya kapılanlardan olma.'),
(904, 6, 115, 'Rabbinin sözü, doğruluk bakımından da, adalet bakımından da tastamamdır. O\'nun sözlerini değiştirebilecek yoktur. O, işitendir, bilendir.'),
(905, 6, 116, 'Yeryüzünde olanların çoğunluğuna uyacak olursan, seni Allah\'ın yolundan şaşırtıp-saptırırlar. Onlar ancak zanna uyarlar ve onlar ancak \'zan ve tahminle yalan söylerler.\''),
(906, 6, 117, 'Şüphesiz Rabbin, Kendi yolundan sapanları daha iyi bilir. O, dosdoğru yolda olanları daha iyi bilendir.'),
(907, 6, 118, 'Eğer O\'nun ayetlerine inanıyorsanız, artık üzerinde yalnızca Allah\'ın ismi anılanlardan yiyin.'),
(908, 6, 119, 'Ne oluyor ki size, kaçınılmaz bir ihtiyaçla karşı karşıya kalmanız dışında, O, size haram kıldıklarını ayrı ayrı açıklamışken, üzerinde Allah\'ın ismi anılan şeyleri yemiyorsunuz? Gerçekten çoğu, bir ilim olmaksızın kendi heva (istek ve tutku)larıyla (kimilerini) saptırıyorlar. Şüphesiz, senin Rabbin haddi aşanları en iyi bilendir.'),
(909, 6, 120, 'Günahın açıkta olanını da, gizlisini de terk edin. Çünkü günahı kazananlar, yüklenegeldikleri nedeniyle karşılık göreceklerdir.'),
(910, 6, 121, 'Üzerinde Allah\'ın isminin anılmadığı şeyi yemeyin; çünkü bu fısk\'tır (yoldan çıkıştır). Gerçekten şeytanlar, sizinle mücadele etmeleri için kendi dostlarına gizli-çağrılarda bulunurlar. Onlarla itaat ederseniz şüphesiz siz de müşriklersiniz.'),
(911, 6, 122, 'Ölü iken kendisini dirilttiğimiz ve insanlar içinde yürümesi için kendisine bir nur verdiğimiz kimsenin durumu, karanlıklarda kalıp oradan bir çıkış bulamayanın durumu gibi midir? İşte, kafirlere yapmakta oldukları böyle \'süslü ve çekici\' gösterilmiştir.'),
(912, 6, 123, 'Böylece Biz, her ülkenin önde gelenlerini -orada hileli- düzenler kursunlar diye- oranın suçlu-günahkarları kıldık. Oysa onlar, hileli-düzeni ancak kendilerine kurarlar da bunun şuuruna varmazlar.'),
(913, 6, 124, 'Onlara ne zaman bir ayet gelse, derler ki: "Allah\'ın elçilerine verilenin bir benzeri bize de verilene kadar biz kesin olarak inanmayacağız." Allah, elçiliğini nereye vereceğini daha iyi bilir. Bu, suçlu-günahkarlara, kurdukları hileli-düzenleri nedeniyle şiddetli bir azap ve Allah Katında bir küçüklük isabet edecektir.'),
(914, 6, 125, 'Allah, kimi hidayete erdirmek isterse, onun göğsünü İslam\'a açar; kimi saptırmak isterse, onun göğsünü, sanki göğe yükseliyormuş gibi dar ve sıkıntılı kılar. Allah, iman etmeyenlerin üstüne işte böyle pislik çökertir.'),
(915, 6, 126, 'Bu, Rabbinin dosdoğru yoludur. Öğüt alıp düşünmesini bilen bir topluluk için ayetleri böyle birer birer açıkladık.'),
(916, 6, 127, 'Onlar için Rableri Katında barış yurdu vardır ve O, yapmakta oldukları dolayısıyla onların velisidir.'),
(917, 6, 128, 'Onların tümünü toplayacağı gün: "Ey cin topluluğu insanlardan çoğunu (ayartıp kendinize kullar) edindiniz" (diyecek). İnsanlardan onların dostları derler ki: "Rabbimiz, kimimiz kimimizden yararlandı ve bizim için tespit ettiğin süreye ulaştık." (Allah) Diyecek ki: "Allah\'ın dilediği dışta olmak üzere, ateş sizin içinde süresiz kalacağınız konaklama yerinizdir." Şüphesiz Rabbin, hüküm ve hikmet sahibi olandır, bilendir.'),
(918, 6, 129, 'Böylece Biz, kazandıkları dolayısıyla zalimlerin bir kısmını bir kısmının başına geçiririz.'),
(919, 6, 130, 'Ey cin ve insan topluluğu, içinizden size ayetlerimi aktarıp-okuyan ve bu karşı karşıya geldiğiniz gününüzle sizi uyarıp-korkutan elçiler gelmedi mi? Onlar: "Nefislerimize karşı şehadet ederiz" derler. Dünya hayatı onları aldattı ve gerçekten kafir olduklarına dair kendi nefislerine karşı şehadet ettiler.'),
(920, 6, 131, 'Bu, halkı habersizken, Rabbinin ülkeleri zulüm ve helak edici olmadığındandır.'),
(921, 6, 132, 'Yapmakta oldukları dolayısıyla her biri için dereceler vardır. Rabbin, onların yapmakta olduklarından habersiz değildir.'),
(922, 6, 133, 'Rabbin, hiçbir şeye ihtiyacı olmayan rahmet sahibidir. Dilerse sizi giderir ve dilerse, sizi bir başka kavmin soyundan (inşa edip) var ettiği gibi yerinize bir başkasını getirir.'),
(923, 6, 134, 'Hiç şüphesiz, size vadedilen mutlaka gelecektir. Ve siz aciz bırakılacak değilsiniz.'),
(924, 6, 135, 'De ki: "Ey kavmim, bütün yapabileceğinizi yapın; şüphesiz ben de yapıyorum. Bu yurdun (dünyanın) sonu, kimindir, bilip-öğreneceksiniz. Gerçekten zalimler kurtuluşa ermeyeceklerdir."'),
(925, 6, 136, 'O\'nun üretip-türettiği ekin ve hayvanlardan Allah için bir pay ayırdılar, sonra kendi zanlarınca: "Bu Allah\'ındır, bu da ortaklarımızındır" dediler. Kendi ortakları için olan (pay), Allah tarafına geçmez, ama Allah\'a ait olan kendi ortaklarının tarafına (payına) geçer. Ne kötü hüküm veriyorlar?'),
(926, 6, 137, 'Yine bunun gibi onların ortakları, müşriklerden çoğuna çocuklarını öldürmeyi süslü gösterdiler. Hem onları helake düşürmek, hem kendi aleyhlerinde dinlerini karmakarışık kılmak için. Allah dileseydi bunu yapmazlardı; sen onları ve düzmekte oldukları iftiraları bırak.'),
(927, 6, 138, 'Ve kendi zanlarınca dediler ki: "Bu hayvanlar ve ekinler dokunulmazdır. Onları bizim dilediklerimiz dışında başkası yiyemez. (Şu) Hayvanların da sırtları haram kılınmıştır." Öyle hayvanlar vardır ki, -O\'na iftira etmek suretiyle- üzerlerinde Allah\'ın ismini anmazlar. Yalan yere iftira düzmekte olduklarından dolayı O, cezalarını verecektir.'),
(928, 6, 139, 'Bir de dediler ki: "Bu hayvanların karınlarında olan, yalnızca bizim erkeklerimize aittir, eşlerimize ise haramdır. Eğer o, ölü doğarsa onlar da bunda ortaktırlar." Allah, (bu) düzmelerinin cezasını verecektir. Şüphesiz O, hüküm sahibi olandır, bilendir.'),
(929, 6, 140, 'Çocuklarını hiçbir bilgiye dayanmaksızın akılsızca öldürenler ile Allah\'a karşı yalan yere iftira düzüp Allah\'ın kendilerine rızık olarak verdiklerini haram kılanlar elbette hüsrana uğramışlardır. Onlar, gerçekten şaşırıp sapmışlardır ve doğru yolu bulamamışlardır.'),
(930, 6, 141, 'Asmalı ve asmasız bahçeleri, hurmaları ve tadları farklı ekinleri, zeytinleri ve narları -birbirine benzer ve benzeşmez- yaratan O\'dur. Ürün verdiğinde ürününden yiyin ve hasad günü hakkını verin; israf etmeyin. Çünkü O, israf edenleri sevmez.'),
(931, 6, 142, 'Hayvanlardan yük taşıyan ve (yünlerinden, tüylerinden) döşek yapılanları da (yaratan O\'dur). Allah\'ın size rızık olarak verdiklerinden yiyin ve şeytanın adımlarına uymayın. Çünkü o, sizin için apaçık bir düşmandır.'),
(932, 6, 143, 'Sekiz çift; koyundan iki, keçiden de iki. De ki: "İki erkeği mi haram kıldı? Yoksa iki dişiyi mi, ya da o iki dişinin rahimlerinin, kendisini kapsadığı (yavruları) mı? Eğer doğru sözlüler iseniz bana bir ilimle haber verin."'),
(933, 6, 144, 'Deveden iki, sığırdan da iki. De ki: "İki erkeği mi haram kıldı? Yoksa iki dişiyi mi ya da o iki dişinin rahimlerinin, kendisini kapsadığı (yavruları) mı? Yoksa Allah, bunları sizlere tavsiye ettiği zaman şahid miydiniz?" Hiçbir bilgiye dayanmaksızın insanları saptırmak için Allah\'a karşı yalan uydurup iftira düzenden daha zalim kimdir? Şüphesiz Allah, zalimler topluluğunu hidayete erdirmez.'),
(934, 6, 145, 'De ki: "Bana vahyolunanlar içinde, yiyen bir kimsenin yiyeceği (şeyler) için, ölü eti, dökülen kan, domuz eti -ki bu gerçekten murdardır- ya da Allah\'tan başkası adına kesilmiş bir fısk dışında, haram kılınmış bir şey bulmuyorum. Kim kaçınılmaz bir ihtiyaçla karşı karşıya kalırsa, -saldırmamak ve haddi aşmamak şartıyla- (bu sayılanlardan ölmeyecek kadar yiyebilir). Şüphesiz senin Rabbin bağışlayandır, esirgeyendir.'),
(935, 6, 146, 'Yahudi olanlara her tırnaklı (hayvanı) haram kıldık. Sığırlardan ve koyunlardan, sırtlarına veya bağırsaklarına yapışan veya kemiğe karışanlar dışında iç yağlarını da onlara haram kıldık. \'Azgınlık ve hakka tecavüzde bulunmaları\' nedeniyle onları böyle cezalandırdık. Biz şüphesiz doğru olanlarız.'),
(936, 6, 147, 'Şayet seni yalanlayacak olurlarsa, de ki: "Rabbiniz geniş rahmet sahibidir. O’nun şiddetli çarpması, suçlu-günahkarlar topluluğundan geri çevrilemez."'),
(937, 6, 148, 'Şirk koşanlar diyecekler ki: "Allah dileseydi ne biz şirk koşardık, ne atalarımız ve hiçbir şeyi de haram kılmazdık." Onlardan öncekiler de, Bizim zorlu-azabımızı tadıncaya kadar böyle yalanladılar. De ki: "Sizin yanınızda, bize çıkarabileceğiniz bir ilim mi var? Siz ancak zanna uymaktasınız ve siz ancak "zan ve tahminle yalan söylersiniz."'),
(938, 6, 149, 'De ki: "En \'üstün ve apaçık\' delil Allah\'ındır. Eğer O dileseydi elbette tümünüzü hidayete yöneltip-iletirdi."'),
(939, 6, 150, 'De ki: "Gerçekten Allah\'ın bunu haram kıldığına şehadet edecek şahidlerinizi getirin." Şayet onlar, şehadet edecek olurlarsa sen onlarla birlikte şehadet etme. Ayetlerimizi yalan sayanların ve ahirete inanmayanların heva (istek ve tutku)larına uyma; onlar (birtakım güçleri ve varlıkları) Rablerine denk tutmaktadırlar.'),
(940, 6, 151, 'De ki: "Gelin size Rabbinizin neleri haram kıldığını okuyayım: O\'na hiçbir şeyi ortak koşmayın, anne-babaya iyilik edin, yoksulluk-endişesiyle çocuklarınızı öldürmeyin. -Sizin de, onların da rızıklarını Biz vermekteyiz- Çirkin-kötülüklerin açığına ve gizli olanına yaklaşmayın. Hakka dayalı olma dışında, Allah\'ın (öldürülmesini) haram kıldığı kimseyi öldürmeyin. İşte bunlarla size tavsiye (emr) etti; umulur ki akıl erdirirsiniz."'),
(941, 6, 152, '"Yetimin malına, o erginlik çağına erişinceye kadar -o en güzel (şeklin) dışında- yaklaşmayın. Ölçüyü ve tartıyı doğru olarak yapın. Hiçbir nefse, gücünün kaldırabileceği dışında bir şey yüklemeyiz. Söylediğiniz zaman -yakınınız dahi olsa- adil olun. Allah\'ın ahdine vefa gösterin. İşte bunlarla size tavsiye (emr) etti; umulur ki öğüt alıp-düşünürsünüz."'),
(942, 6, 153, 'Bu Benim dosdoğru olan yolumdur. Şu halde ona uyun. Sizi O\'nun yolundan ayıracak (başka) yollara uymayın. Bununla size tavsiye etti, umulur ki korkup-sakınırsınız.'),
(943, 6, 154, 'Sonra Biz Musa\'ya, iyilik yapanların üzerinde (nimetimizi) tamamlamak, herşeyi ayrı ayrı açıklamak ve bir hidayet ve rahmet olarak kitabı verdik. Umulur ki Rablerine kavuşacaklarına inanırlar.'),
(944, 6, 155, 'Bu indirdiğimiz mübarek bir Kitap\'tır. Şu halde ona uyun ve korkup-sakının. Umulur ki esirgenirsiniz.'),
(945, 6, 156, '"Bizden önce kitap yalnız iki topluluğa indirildi, biz ise onların ders gördüklerinden habersizlerdik" dememeniz;'),
(946, 6, 157, 'Ya da: "Kitap bize de indirilseydi, elbette onlardan daha çok doğru yolda olurduk" dememeniz (için) işte size Rabbinizden apaçık bir belge, bir hidayet ve bir rahmet gelmiştir. Allah\'ın ayetlerini yalanlayandan ve (insanları) ondan alıkoyup-çevirenden daha zalim kimdir? Ayetlerimizden alıkoyup-çevirenlere, bu \'engelleme ve çevirmelerinden\' dolayı pek çetin bir azapla karşılık vereceğiz.'),
(947, 6, 158, 'Onlar, kendilerine meleklerin gelmesini mi, ya da Rabbinin gelmesini mi veya Rabbinin bazı ayetlerinin gelmesini mi bekliyorlar? Rabbinin ayetlerinden bazılarının geleceği gün, daha önce iman etmemişse veya imanıyla bir hayır kazanmamışsa hiç kimseye imanı yarar sağlamaz. De ki: "Bekleyin, Biz de şüphesiz beklemekteyiz."'),
(948, 6, 159, 'Gerçek şu ki, dinlerini parça parça edip kendileri de gruplaşanlar, sen hiçbir şeyde onlardan değilsin. Onların işi ancak Allah\'adır. Sonra O, işlemekte olduklarını kendilerine haber verecektir.'),
(949, 6, 160, 'Kim bir iyilikle gelirse, kendisine bunun on katı vardır, kim bir kötülükle gelirse, onun mislinden başkasıyla cezalandırılmaz ve onlar haksızlığa uğratılmazlar.'),
(950, 6, 161, 'De ki: "Rabbim gerçekten beni doğru yola iletti, dimdik duran bir dine, İbrahim\'in hanif (muvahhid) dinine… O, müşriklerden değildi."'),
(951, 6, 162, 'De ki: "Şüphesiz benim namazım, ibadetlerim, dirimim ve ölümüm alemlerin Rabbi olan Allah\'ındır."'),
(952, 6, 163, '"O\'nun hiçbir ortağı yoktur. Ben böyle emrolundum ve ben Müslüman olanların ilkiyim."'),
(953, 6, 164, 'De ki: "O, herşeyin Rabbi iken, ben Allah\'tan başka bir Rab mi arayayım? Hiçbir nefis, kendisinden başkasının aleyhine (günah) kazanmaz. Günahkar olan bir başkasının günah yükünü taşımaz. Sonunda dönüşünüz Rabbinizedir. O, size hakkında anlaşmazlığa düştüğünüz şeyleri haber verecektir."'),
(954, 6, 165, 'O sizi yeryüzünün halifeleri kıldı ve size verdikleriyle sizi denemek için kiminizi kiminize göre derecelerle yükseltti. Şüphesiz senin Rabbin, sonuçlandırması pek çabuk olandır ve şüphesiz O, bağışlayandır, esirgeyendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Elif, Lam, Mim, Sad.'),
(956, 7, 2, '(Bu,) Bir Kitap\'tır ki onunla uyarman için ve mü\'minlere bir öğüt olmak üzere sana indirildi. Öyleyse bundan dolayı göğsünde bir sıkıntı olmasın.'),
(957, 7, 3, 'Rabbinizden size indirilene uyun, O\'ndan başka velilere uymayın. Ne az öğüt alıyorsunuz?'),
(958, 7, 4, 'Biz nice ülkeleri yıkıma uğrattık. Geceleri uyurlarken ya da gündüzün dinlenirlerken Bizim zorlu azabımız onlara geliverdi.'),
(959, 7, 5, 'Zorlu azabımız onlara gelince yakarabildikleri: "Biz gerçekten zulme sapanlardandık" demelerinden başka olmadı.'),
(960, 7, 6, 'Andolsun, kendilerine (peygamber) gönderilenlere soracağız ve onlara gönderilenlere (peygamberlere) de elbette soracağız.'),
(961, 7, 7, 'Andolsun (yapıp-etmelerini) onlara bir ilimle mutlaka haber vereceğiz. Ve Biz gaibler (onlardan uzakta olan habersizler) de değildik.'),
(962, 7, 8, 'O gün tartı haktır. Kimin tartıları ağır basarsa, işte kurtulanlar onlardır.'),
(963, 7, 9, 'Kimin tartıları hafif kalırsa, bunlar da ayetlerimize zulmedegeldiklerinden dolayı nefislerini hüsrana uğratanlardır.'),
(964, 7, 10, 'Andolsun, sizi yeryüzünde yerleşik kıldık ve orda size geçimlikler yarattık. Ne az şükrediyorsunuz?'),
(965, 7, 11, 'Andolsun, Biz sizi yarattık, sonra size suret (biçim-şekil) verdik, sonra meleklere: "Adem\'e secde edin" dedik. Onlar da İblis\'in dışında secde ettiler; o, secde edenlerden olmadı.'),
(966, 7, 12, '(Allah) Dedi: "Sana emrettiğimde, seni secde etmekten alıkoyan neydi?" (İblis) Dedi ki: "Ben ondan hayırlıyım; beni ateşten yarattın, onu ise çamurdan yarattın."'),
(967, 7, 13, '(Allah:) "Öyleyse oradan in, orda büyüklenmen senin (hakkın) olmaz. Hemen çık. Gerçekten sen, küçük düşenlerdensin."'),
(968, 7, 14, 'O da: "(İnsanların) dirilecekleri güne kadar beni gözle(yip ertele.)" dedi.'),
(969, 7, 15, '(Allah:) "Sen gözlenip-ertelenenlerdensin" dedi.'),
(970, 7, 16, 'Dedi ki: "Madem öyle, beni azdırdığından dolayı onlar(ı insanları saptırmak) için mutlaka Senin dosdoğru yolunda (pusu kurup) oturacağım."'),
(971, 7, 17, '"Sonra muhakkak önlerinden, arkalarından, sağlarından ve sollarından sokulacağım. Onların çoğunu şükredici bulmayacaksın."'),
(972, 7, 18, '(Allah) Dedi: "Kınanıp alçaltılmış ve kovulmuş olarak oradan çık. Andolsun, onlardan kim seni izlerse, cehennemi sizlerle dolduracağım."'),
(973, 7, 19, 'Ve ey Adem, sen ve eşin cennete yerleş. İkiniz dilediğiniz yerden yiyin; ama şu ağaca yaklaşmayın. Yoksa zalimlerden olursunuz.'),
(974, 7, 20, 'Şeytan, kendilerinden \'örtülüp gizlenen çirkin yerlerini\' açığa çıkarmak için onlara vesvese verdi ve dedi ki: "Rabbinizin size bu ağacı yasaklaması, yalnızca, sizin iki melek olmamanız veya ebedi yaşayanlardan kılınmamanız içindir."'),
(975, 7, 21, 'Ve: "Gerçekten ben size öğüt verenlerdenim" diye yemin de etti.'),
(976, 7, 22, 'Böylece onları aldatarak düşürdü. Ağacı tattıkları anda ise, ayıp yerleri kendilerine beliriverdi ve üzerlerini cennet yapraklarından örtmeye başladılar. (O zaman) Rableri kendilerine seslendi: "Ben sizi bu ağaçtan menetmemiş miydim? Ve şeytanın sizin gerçekten apaçık bir düşmanınız olduğunu söylememiş miydim?"'),
(977, 7, 23, 'Dediler ki: "Rabbimiz, biz nefislerimize zulmettik, eğer bizi bağışlamazsan ve esirgemezsen, gerçekten hüsrana uğrayanlardan olacağız."'),
(978, 7, 24, '(Allah) Dedi ki: "Kiminiz kiminize düşman olarak inin. Yeryüzünde belli bir vakte kadar sizin için bir yerleşim ve meta (geçim) vardır."'),
(979, 7, 25, 'Dedi ki: "Orda yaşayacak, orda ölecek ve oradan çıkarılacaksınız."'),
(980, 7, 26, 'Ey Ademoğulları, Biz sizin çirkin yerlerinizi örtecek bir elbise ve size \'süs kazandıracak bir giyim\' indirdik (var ettik). Takva ile kuşanıp-donanmak ise, bu daha hayırlıdır. Bu, Allah\'ın ayetlerindendir. Umulur ki öğüt alıp-düşünürler.'),
(981, 7, 27, 'Ey Ademoğulları, şeytan, anne ve babanızın çirkin yerlerini kendilerine göstermek için, elbiselerini sıyırtarak, onları cennetten çıkardığı gibi sakın sizi de bir belaya uğratmasın. Çünkü o ve taraftarları, (kendilerini göremeyeceğiniz yerden) sizleri görmektedir. Biz gerçekten şeytanları, inanmayacakların dostları kıldık.'),
(982, 7, 28, 'Onlar, \'çirkin bir hayasızlık\' işlediklerinde: "Biz atalarımızı bunun üzerinde bulduk. Allah bunu bize emretti" derler. De ki: "Şüphesiz Allah, \'çirkin hayasızlıkları\' emretmez. Bilmediğiniz bir şeyi Allah\'a karşı mı söylüyorsunuz?"'),
(983, 7, 29, 'De ki: "Rabbim adaletle davranmayı emretti. Her mescid yanında (secde yerinde) yüzlerinizi (O\'na) doğrultun ve dini yalnız Kendisi\'ne has kılarak O\'na dua edin. "Başlangıçta sizi yarattığı" gibi döneceksiniz."'),
(984, 7, 30, 'Kimine hidayet verdi, kimi de sapıklığı hak etti. Çünkü bunlar, Allah\'ı bırakıp şeytanları veli edinmişlerdi. Ve gerçekten onları doğru yolda saymaktadırlar.'),
(985, 7, 31, 'Ey Ademoğulları, her mescid yanında ziynetlerinizi takının. Yiyin, için ve israf etmeyin. Çünkü O, israf edenleri sevmez.'),
(986, 7, 32, 'De ki: "Allah\'ın kulları için çıkardığı ziyneti ve temiz rızıkları kim haram kılmıştır?" De ki: "Bunlar, dünya hayatında iman edenler içindir, kıyamet günü ise yalnızca onlarındır." Bilen bir topluluk için ayetleri böyle birer birer açıklarız.'),
(987, 7, 33, 'De ki: "Rabbim yalnızca çirkin-hayasızlıkları -onlardan açıkta olanlarını ve gizli olanlarını,- günah işlemeyi, haklı nedeni olmayan \'isyan ve saldırıyı\' kendisi hakkında ispatlayıcı bir delil indirmediği şeyi Allah’a şirk koşmanızı ve Allah\'a karşı bilmediğiniz şeyleri söylemenizi haram kılmıştır."'),
(988, 7, 34, 'Her ümmet için bir ecel vardır. Onların ecelleri gelince, ne bir saat ertelenebilirler ne de öne alınabilirler (tam zamanında çökerler.)'),
(989, 7, 35, 'Ey Ademoğulları, içinizden size ayetlerimi haber veren elçiler geldiğinde, kim sakınırsa ve (davranışlarını) düzeltirse işte onlar için korku yoktur, onlar mahzun olmayacaklardır.'),
(990, 7, 36, 'Ayetlerimizi yalanlayanlar ve onlara karşı büyüklenenler, işte onlar ateşin arkadaşlarıdır; onda sonsuzca kalacaklardır.'),
(991, 7, 37, 'Öyleyse, Allah’a karşı yalan uydurup iftira düzenden veya ayetlerini yalanlayanlardan daha zalim kimdir? Kitaptan kendilerine bir pay erişecek olanlar bunlardır. Nihayet elçilerimiz, hayatlarına son vermek üzere kendilerine gittiklerinde onlara diyecekler ki: "Allah\'tan başka taptıklarınız nerede?" "Onlar bizi (yüzüstü) bırakıp-kayboldular" diyecekler. (Böylelikle) Bunlar, gerçekten kafirler olduklarına kendi aleyhlerinde şehadet ettiler.'),
(992, 7, 38, '(Allah) diyecek: "Cinlerden ve insanlardan sizden önce geçmiş ümmetlerle birlikte ateşe girin." Her bir ümmet girişinde kardeşini (kendi benzerini) lanetler. Nitekim hepsi birbiri ardınca orada toplanınca, en sonra yer alanlar, en önde gelenler için: "Rabbimiz, işte bunlar bizi saptırdı; öyleyse ateşten kat kat artırılmış bir azap ver diyecekler. (Allah da:) "Hepsi için kat kattır. Ancak siz bilmezsiniz" diyecek.'),
(993, 7, 39, '(Bu sefer) Önde gelenler, sonda yer alanlara diyecekler ki: "Sizin bize göre bir üstünlüğünüz yoktur, kazandıklarınıza karşılık olarak azabı tadın."'),
(994, 7, 40, 'Şüphesiz ayetlerimizi yalanlayanlar ve onlara karşı büyüklenenler, onlar için göğün kapıları açılmaz ve halat (ya da deve) iğnenin deliğinden geçinceye kadar cennete girmezler. Biz suçlu-günahkarları işte böyle cezalandırırız.'),
(995, 7, 41, 'Onlar için cehennemden yataklar ve üstlerine örtüler vardır. Biz zulme sapanları işte böyle cezalandırırız.'),
(996, 7, 42, 'İman edenler ve salih amellerde bulunanlar -ki Biz hiç kimseye güç yetireceğinden fazlasını yüklemeyiz- onlar da cennetin ashabı (halkı)dırlar. Onda sonsuz olarak kalacaklardır.'),
(997, 7, 43, 'Biz onların göğüslerinde kinden ne varsa çekip almışız. Altlarından ırmaklar akar. Derler ki: "Bizi buna ulaştıran Allah\'a hamd olsun. Eğer Allah bize hidayet vermeseydi biz doğruya ermeyecektik. Andolsun, Rabbimiz\'in elçileri hak ile geldiler." Onlara: "İşte bu, yaptıklarınıza karşılık olarak mirasçı kılındığınız cennettir" diye seslenilecek.'),
(998, 7, 44, 'Cennet halkı, ateş halkına (şöyle) seslenecekler: "Bize Rabbimiz\'in vadettiğini gerçek buldunuz mu?" Onlar da: "Evet" derler. Bundan sonra içlerinden seslenen biri (şöyle) seslenecektir: "Allah\'ın laneti zalimlerin üzerine olsun."'),
(999, 7, 45, '"Ki onlar Allah\'ın yolundan alıkoyanlar, onda çarpıklık arayanlar ve ahireti tanımayanlardır."'),
(1000, 7, 46, 'İki taraf arasında bir engel ve burçlar (A\'raf) üstünde hepsini yüzlerinden tanıyan adamlar vardır. Cennete gireceklere: "Selam size" derler, ki bunlar, henüz girmeyen fakat (girmeyi) \'şiddetle arzu edip umanlardır.\''),
(1001, 7, 47, 'Gözleri cehennem halkından yana çevrilince: "Rabbimiz, bizi zalimler topluluğuyla birlikte kılma" derler.'),
(1002, 7, 48, 'Burcun üstündeki adamlar, kendilerini yüzlerinden tanıdıkları (ileri gelen birtakım) adamlara seslenerek derler ki: "Ne (güç ve servet) toplamış olmanız, ne büyüklük taslamanız (istikbarınız) size bir yarar sağlamadı."'),
(1003, 7, 49, '"Kendilerine Allah\'ın bir rahmet eriştirmeyeceğine yemin ettiğiniz kimseler bunlar mıydı? (Cennettekilere de) Girin cennete. Sizin için korku yoktur ve mahzun olmayacaksınız."'),
(1004, 7, 50, 'Ateşin halkı cennet halkına seslenir: “Bize biraz sudan ya da Allah’ın size verdiği rızıktan aktarın.” Derler ki: “Doğrusu Allah, bunları inkar edenlere haram (yasak) kılmıştır.”'),
(1005, 7, 51, 'Onlar, dinlerini bir eğlence ve oyun (konusu) edinmişlerdi ve dünya hayatı onları aldatmıştı. Onlar, bu günleriyle karşılaşmayı unuttukları ve Bizim ayetlerimizi \'yok sayarak tanımadıkları\' gibi, Biz de bugün onları unutacağız.'),
(1006, 7, 52, 'Andolsun, Biz onlara bir kitap getirdik; iman edecek bir topluluğa bir hidayet ve bir rahmet olmak üzere bir bilgiye dayanarak onu çeşitli biçimlerde açıkladık.'),
(1007, 7, 53, 'Onlar, onun tevilinden başkasına bakmazlar mı? Onun tevilinin geleceği gün, daha önce onu unutanlar, diyecekler ki: "Gerçekten Rabbimiz\'in elçileri bize hakkı getirmişlerdi. Şimdi bize şefaat edecek şefaatçiler var mıdır? Veya geri çevrilsek de işlediklerimizden başkasını yapsak." Gerçek şu ki onlar, kendilerini hüsrana uğratmışlardır, uydurmakta oldukları şeyler de kendilerinden uzaklaşıp kaybolmuşlardır.'),
(1008, 7, 54, 'Gerçekten sizin Rabbiniz, altı günde gökleri ve yeri yaratan, sonra arşa istiva eden Allah\'tır. Gündüzü, durmaksızın kendisini kovalayan geceyle örten, Güneş’e, Ay’a ve yıldızlara Kendi buyruğuyla baş eğdirendir. Haberiniz olsun, yaratmak da, emir de (yalnızca) O\'nundur. Alemlerin Rabbi olan Allah ne Yücedir.'),
(1009, 7, 55, 'Rabbinize yalvara yalvara ve için için dua edin. Şüphesiz O, haddi aşanları sevmez.'),
(1010, 7, 56, 'Düzene konulması (ıslah)ından sonra yeryüzünde bozgunculuk (fesad) çıkarmayın; O\'na korkarak ve umut taşıyarak dua edin. Doğrusu Allah\'ın rahmeti iyilik yapanlara pek yakındır.'),
(1011, 7, 57, 'Rahmetinin önünde rüzgarları bir müjde olarak gönderen O\'dur. Bunlar ağırca bulutları kaldırıp yüklendiğinde, onları (kuraklıktan) ölmüş bir şehre sürükleyiveririz ve bununla oraya su indiririz de böylelikle bütün ürünlerden çıkarırız. İşte Biz, ölüleri de böyle diriltip-çıkarırız. Ki ibret alasınız.'),
(1012, 7, 58, 'Güzel şehrin bitkisi, Rabbinin izniyle çıkar; kötü olandan ise kavruktan başkası çıkmaz. İşte Biz, şükreden bir topluluk için ayetleri böyle çeşitli biçimlerde açıklıyoruz.'),
(1013, 7, 59, 'Andolsun Biz Nuh\'u kendi kavmine (toplumuna) gönderdik. Dedi ki: "Ey kavmim, Allah\'a kulluk edin, sizin O\'ndan başka İlahınız yoktur. Doğrusu ben, sizin için büyük bir günün azabından korkmaktayım."'),
(1014, 7, 60, 'Kavminin önde gelenleri: "Gerçekte biz seni açıkça bir \'şaşırmışlık ve sapmışlık\' içinde görüyoruz" dediler.'),
(1015, 7, 61, 'O: "Ey kavmim, bende bir \'şaşırmışlık ve sapmışlık\' yoktur; ama ben alemlerin Rabbinden bir elçiyim." dedi.'),
(1016, 7, 62, '"Size Rabbimin risaletini tebliğ ediyorum. (Ayrıca) Size öğüt veriyor ve sizin bilmediklerinizi ben Allah\'tan biliyorum.'),
(1017, 7, 63, '"Sakınıp rahmete kavuşmanız için, içinizden sizi uyarıp korkutacak bir adam aracılığı ile bir zikir (kitap) gelmesine mi şaştınız?"'),
(1018, 7, 64, 'Onu yalanladılar. Biz de onu ve gemide onunla birlikte olanları kurtardık, ayetlerimizi yalan sayanları suda-boğduk. Çünkü onlar kör bir kavimdi.'),
(1019, 7, 65, 'Ad (toplumuna da) kardeşleri Hud\'u (gönderdik.) (Hud, kavmine:) "Ey kavmim, Allah\'a kulluk edin, sizin O\'ndan başka İlahınız yoktur. Hala korkup-sakınmayacak mısınız?" dedi.'),
(1020, 7, 66, 'Kavminin önde gelenlerinden inkar edenler dediler ki: "Gerçekte biz seni \'aklî bir yetersizlik\' içinde görüyoruz ve doğrusu biz senin yalancılardan olduğunu sanıyoruz."'),
(1021, 7, 67, '(Hud:) "Ey kavmim" dedi. "Bende \'akıl yetersizliği\' yoktur; ama ben gerçekten alemlerin Rabbinden bir elçiyim" dedi.'),
(1022, 7, 68, '"Size Rabbimin risaletini tebliğ ediyorum. Ben sizin için güvenilir bir öğütçüyüm."'),
(1023, 7, 69, '"Sizi uyarmak için aranızdan bir adam aracılığıyla Rabbinizden size bir zikrin gelmesine mi şaşırdınız? (Allah\'ın) Nuh kavminden sonra sizi halifeler kıldığını ve sizin yaratılışta gelişiminizi arttırdığını (veya üstün kıldığını) hatırlayın. Öyleyse Allah\'ın nimetlerini hatırlayın, ki kurtuluş bulasınız."'),
(1024, 7, 70, 'Dediler ki: "Sen bize yalnızca Allah\'a kulluk etmemiz ve atalarımızın tapmakta olduklarınızı bırakmamız için mi geldin? Eğer gerçekten doğru isen, bize vadettiğin şeyi getir, bakalım."'),
(1025, 7, 71, '"Andolsun" dedi. "Rabbinizden üzerinize iğrenç bir azap ve gazab gerekli kılındı. Allah\'ın kendileri hakkında hiçbir delil indirmediği ve sizin ile babalarınızın isimlendirdiği (düzüp uydurduğu) birtakım isimler (düzme tanrılar ve kurallar) adına mı benimle mücadele ediyorsunuz? Öyleyse bekleyedurun; şüphesiz, ben de sizlerle birlikte bekleyenlerdenim."'),
(1026, 7, 72, 'Böylece onu ve onunla birlikte olanları Katımız\'dan bir rahmet ile kurtardık. Ayetlerimizi yalan sayarak inanmamış olanların kökünü kuruttuk.'),
(1027, 7, 73, 'Semud (toplumuna da) kardeşleri Salih\'i (gönderdik. Salih:) "Ey kavmim, Allah\'a kulluk edin, sizin O\'ndan başka İlahınız yoktur. Size Rabbinizden apaçık bir belge (mucize) gelmiştir: Allah\'ın bu dişi devesi size bir belgedir; onu salıverin de Allah\'ın arzında otlasın, ona bir kötülükle dokunmayın, sonra sizi acı bir azap yakalar" dedi.'),
(1028, 7, 74, '"(Allah\'ın) Ad (kavminden) sonra sizi halifeler kıldığını ve sizi yeryüzünde (güç ve servetle) yerleştirdiğini hatırlayın. Ki onun düzlüklerinde köşkler kuruyor, dağlardan evler yontuyordunuz. Şu halde Allah\'ın nimetlerini hatırlayın, yeryüzünde bozguncular olarak karışıklık çıkarmayın."'),
(1029, 7, 75, 'Kavminin önde gelenlerinden büyüklük taslayanlar (müstekbirler), içlerinden iman edip de onlarca zayıf bırakılanlara (müstaz\'aflara) dediler ki: "Salih\'in gerçekten Rabbi tarafından gönderildiğini biliyor musunuz?" Onlar: "Biz gerçekten onunla gönderilene inananlarız" dediler.'),
(1030, 7, 76, 'Büyüklük taslayanlar (müstekbirler de şöyle) dedi: "Biz de, gerçekten sizin inandığınızı tanımayanlarız."'),
(1031, 7, 77, 'Böylelikle dişi deveyi öldürdüler ve Rablerinin emrine karşı çıkıp (Salih\'e de şöyle) dediler: "Ey Salih, eğer gerçekten gönderilenlerden (bir peygamber) isen, vadettiğin şeyi getir, bakalım."'),
(1032, 7, 78, 'Bunun üzerine onları dayanılmaz bir sarsıntı tuttu da kendi yurtlarında diz üstü çöke kaldılar.'),
(1033, 7, 79, 'O da onlardan yüz çevirdi ve (şöyle) dedi: "Ey kavmim, andolsun size Rabbimin risaletini tebliğ ettim ve size öğüt verdim. Ama siz, öğüt verenleri sevmiyorsunuz."'),
(1034, 7, 80, 'Hani Lut da kavmine şöyle demişti: "Sizden önce alemlerden hiç kimsenin yapmadığı hayasız-çirkinliği mi yapıyorsunuz?'),
(1035, 7, 81, '"Gerçekten siz kadınları bırakıp şehvetle erkeklere yaklaşıyorsunuz. Doğrusu siz, ölçüyü aşan (azgın) bir kavimsiniz."'),
(1036, 7, 82, 'Kavminin cevabı: "Yurdunuzdan sürüp çıkarın bunları, çünkü bunlar çokça temizlenen insanlarmış!" demekten başka olmadı.'),
(1037, 7, 83, 'Bunun üzerine Biz, karısı dışında onu ve ailesini kurtardık; o (karısı) ise (helake uğrayanlar arasında) geride kalanlardandı.'),
(1038, 7, 84, 'Ve onların üzerine bir (azap) sağanağı yağdırdık. Suçlu-günahkarların uğradıkları sona bir bak işte.'),
(1039, 7, 85, 'Medyen (toplumuna da) kardeşleri Şuayb\'ı (gönderdik. Şuayb onlara:) Dedi ki: "Ey kavmim, Allah\'a kulluk edin, sizin O\'ndan başka İlahınız yoktur. Size Rabbinizden apaçık bir belge (mucize) gelmiştir. Ölçüyü ve tartıyı tam tutun, insanların (hakları olan mallarını) eşyasını değerinden düşürüp-eksiltmeyin ve düzene (ıslaha) konulmasından sonra yeryüzünde bozgunculuk (fesad) çıkarmayın. Bu sizin için daha hayırlıdır, eğer inanıyorsanız."'),
(1040, 7, 86, '"O\'na iman edenleri tehdit ederek, Allah\'ın yolundan alıkoymak için ve onda çarpıklık arayarak (böyle) her yolun (başını) kesip-oturmayın. Hatırlayın ki siz azınlıkta (ve güçsüz) iken O, sizi çoğalttı. Bozgunculuk çıkaranların nasıl bir sona uğradıklarına bir bakın."'),
(1041, 7, 87, '"İçinizden bir grup, kendisiyle gönderildiğim şeye inanmışken diğer bir grup inanmadığına göre, artık Allah, aramızda hüküm verenlerin en hayırlısıdır."'),
(1042, 7, 88, 'Kavminin önde gelenlerinden büyüklük taslayanlar (müstekbirler) dediler ki: "Ey Şuayb, seni ve seninle birlikte iman edenleri ya ülkemizden sürüp-çıkaracağız veya mutlaka bizim dinimize geri döneceksiniz." (Şuayb:) "Biz istemesek de mi?" dedi.'),
(1043, 7, 89, '"Allah bizi ondan kurtardıktan sonra, bizim tekrar sizin dininize dönmemiz Allah\'a karşı yalan yere iftira düzmemiz olur. Rabbimiz olan Allah\'ın dilemesi dışında, ona geri dönmemiz bizim için olacak iş değildir. Rabbimiz, ilim bakımından herşeyi kuşatmıştır. Biz Allah\'a tevekkül ettik. \'Rabbimiz, bizimle kavmimiz arasında \'Sen hak ile hüküm ver,\' Sen \'hüküm verenlerin\' en hayırlısısın."'),
(1044, 7, 90, 'Kavminin önde gelenlerinden inkar edenler, dediler ki: "Andolsun, Şuayb\'a uyacak olursanız, kuşkusuz kayba uğrayanlardan olursunuz."'),
(1045, 7, 91, 'Bunun üzerine onları dayanılmaz bir sarsıntı tuttu da, kendi yurtlarında diz üstü çökmüş olarak sabahladılar.'),
(1046, 7, 92, 'Şuayb\'ı yalanlayanlar, sanki orada \'hiç refah içinde yaşamamışlar\' gibi oldular: Şuayb\'ı yalanlayanlar, asıl büyük hüsrana uğradılar.'),
(1047, 7, 93, 'O da onlardan yüz çevirdi ve (şöyle) dedi: "Ey kavmim andolsun, size Rabbimin risaletini tebliğ ettim ve size öğüt verdim. Şimdi ben, inkara sapan bir topluluğa nasıl üzülebilirim?"'),
(1048, 7, 94, 'Biz hangi memlekete bir peygamber gönderdiysek onun halkı yalvarıp-yakarsınlar diye, mutlaka onları dayanılmaz bir zorluk (yoksulluk) ve sıkıntıyla yakalayıvermişiz.'),
(1049, 7, 95, 'Sonra kötülüğün yerini iyilikle değiştirdik, öyle ki onlar, çoğaldılar ve: "Atalarımıza da (bazen) şiddetli sıkıntılar (bazen de) refah ve genişlikler dokunmuştu" dediler. Bunun üzerine, Biz de onları kendileri hiç şuurunda değilken apansız kıskıvrak yakalayıverdik.'),
(1050, 7, 96, 'Eğer o ülkeler halkı inansalardı ve korkup-sakınsalardı, gerçekten üzerlerine hem gökten, hem yerden (sayısız) bolluklar (bereketler) açardık; ancak onlar yalanladılar, Biz de onları kazanageldikleri nedeniyle yakalayıverdik.'),
(1051, 7, 97, 'O ülkeler halkı, geceleri uyurken, onlara zorlu azabımızın gelmeyeceğinden güvende miydiler?'),
(1052, 7, 98, 'Ya da o ülkeler halkı, kuşluk vakti eğlenceye dalmışken, onlara zorlu-azabımızın gelmeyeceğinden güvende miydiler?'),
(1053, 7, 99, '(Veya) Onlar, Allah\'ın tuzağından güvende mi idiler? Allah\'ın bir tuzak kurmasından, hüsrana uğrayan bir topluluktan başkası (akılsızca) güvende olmaz.'),
(1054, 7, 100, '(Bütün bunlar,) Sakinlerinden sonra yeryüzüne mirasçı olanları doğruya erdirme(ye veya ortaya çıkarmaya yetmez) mi? Eğer Biz dilemiş olsaydık onlara günahları nedeniyle bir musibet isabet ettirirdik; ve kalplerine damgalar vururduk da onlar böylelikle işitmeyenler olurlardı.'),
(1055, 7, 101, 'İşte bu ülkeler, sana onların \'haberlerinden aktarmalar yapıyoruz.\' Gerçekten, onlara elçileri apaçık belgelerle gelmişlerdi. Ama daha önceden yalanlamaları nedeniyle iman eder olmadılar. İşte Allah, inkar edenlerin kalplerini böyle damgalar.'),
(1056, 7, 102, 'Onların çoğunda \'verdikleri söze bağlılık\' görmedik, ama onların çoğunu fasıklar (yoldan çıkanlar) olarak gördük.'),
(1057, 7, 103, 'Sonra bunların (peygamberlerin) ardından Musa\'yı ayetlerimizle Firavun\'a ve önde gelen çevresine gönderdik; onlar ona (ayetlerimize) haksızlık ettiler. İşte bozgunculuk çıkaranların nasıl bir sona uğradıklarına bir bak.'),
(1058, 7, 104, 'Musa dedi ki: "Ey Firavun, gerçekten, ben alemlerin Rabbinden (gönderilme) bir elçiyim."'),
(1059, 7, 105, '"Benim üzerimdeki yükümlülük, Allah\'a karşı ancak gerçeği söylemektir. Rabbinizden size apaçık bir belge ile geldim. Artık İsrailoğulları\'nı benimle gönder."'),
(1060, 7, 106, '(Firavun) Dedi ki: "Eğer gerçekten bir ayet getirmişsen ve doğru sözlülerden isen, bu durumda onu getir (bakalım)."'),
(1061, 7, 107, 'Böylelikle (Musa) asasını fırlatınca, anında apaçık bir ejderha oluverdi.'),
(1062, 7, 108, '(Bir de) Elini sıyırdı, o da anında bakanlara bembeyaz (görünüverdi).'),
(1063, 7, 109, 'Firavun kavminin önde gelenleri dediler ki: "Bu gerçekten bilgin bir büyücüdür";'),
(1064, 7, 110, '"Sizi topraklarınızdan sürüp-çıkarmak istiyor. Bu durumda ne buyuruyorsunuz?"'),
(1065, 7, 111, 'Dediler ki: "Onu ve kardeşini şimdilik bekletiver (vereceğin cezayı ertele), şehirlere de toplayıcılar yolla";'),
(1066, 7, 112, '"Bütün bilgin büyücüleri sana getirsinler."'),
(1067, 7, 113, 'Sihirbazlar Firavun\'a gelip dediler ki: "Eğer biz galip olursak, herhalde bize bir karşılık (armağan) var, değil mi?"'),
(1068, 7, 114, '"Evet" dedi. "(O zaman) Siz en yakın(larım) kılınanlardan olacaksınız."'),
(1069, 7, 115, 'Dediler ki: "Ey Musa (ilkin) sen mi atmak istersin, yoksa biz mi atalım?"'),
(1070, 7, 116, '(Musa:) "Siz atın" dedi. (Asalarını) atıverince, insanların gözlerini büyüleyiverdiler, onları dehşete düşürdüler ve (ortaya) büyük bir sihir getirmiş oldular.'),
(1071, 7, 117, 'Biz de Musa\'ya: "Asanı fırlatıver" diye vahyettik. (O da fırlatıverince) bir de baktılar ki, o bütün uydurduklarını derleyip-toparlayıp yutuyor.'),
(1072, 7, 118, 'Böylece hak yerini buldu, onların bütün yapmakta oldukları geçersiz kaldı.'),
(1073, 7, 119, 'Orada yenilmiş oldular ve küçük düşmüşler olarak tersyüz çevrildiler.'),
(1074, 7, 120, 'Ve sihirbazlar secdeye kapandılar.'),
(1075, 7, 121, '"Alemlerin Rabbine iman ettik" dediler.'),
(1076, 7, 122, '"Musa\'nın ve Harun\'un Rabbine…"'),
(1077, 7, 123, 'Firavun: "Ben size izin vermeden önce O\'na iman ettiniz, öyle mi? Mutlaka bu, halkı buradan sürüp-çıkarmak amacıyla şehirde planladığınız bir tuzaktır. Öyleyse siz (buna karşılık ne yapacağımı) bileceksiniz."'),
(1078, 7, 124, '"Muhakkak ellerinizi ve ayaklarınızı çaprazlama keseceğim ve hepinizi idam edeceğim."'),
(1079, 7, 125, '(Onlar da:) "Biz de şüphesiz Rabbimiz\'e döneceğiz" dediler.'),
(1080, 7, 126, '"Oysa sen, yalnızca, bize geldiğinde Rabbimiz\'in ayetlerine inanmamızdan başka bir nedenle bizden intikam almıyorsun. Rabbimiz, üstümüze sabır yağdır ve bizi Müslüman olarak öldür."'),
(1081, 7, 127, 'Firavun kavminin önde gelenleri, dediler ki: "Musa ve kavmini bu toprakta (Mısır\'da) bozgunculuk çıkarmaları, seni ve ilahlarını terk etmeleri için mi (serbest) bırakacaksın?" (Firavun) Dedi ki: "Erkek çocuklarını öldüreceğiz ve kadınlarını sağ bırakacağız. Hiç şüphesiz biz, onlara karşı kahir bir üstünlüğe sahibiz."'),
(1082, 7, 128, 'Musa kavmine: "Allah\'tan yardım dileyin ve sabredin. Gerçek şu ki, arz Allah\'ındır; ona kullarından dilediğini mirasçı kılar. En güzel sonuç muttakiler içindir" dedi.'),
(1083, 7, 129, 'Dediler ki: "Sen bize gelmeden önce de, geldikten sonra da eziyete uğratıldık." (Musa:) "Umulur ki, Rabbiniz düşmanınızı helak edecek ve sizleri yeryüzünde halifeler (egemenler) kılacak, böylece nasıl davranacağınızı gözleyecek" dedi.'),
(1084, 7, 130, 'Andolsun, Biz de Firavun aile (çevre)sini belki öğüt alıp düşünürler diye yıllar yılı kuraklığa ve ürün kıtlığına uğrattık.'),
(1085, 7, 131, 'Onlara bir iyilik geldiği zaman "Bu bizim için" dediler; onlara bir kötülük isabet ettiğinde (bunu da) Musa ve beraberindekilerin bir uğursuzluğu olarak yorumlarlardı. Haberiniz olsun, Allah Katında asıl uğursuz olanlar kendileridir; ama onların çoğu bilmezler.'),
(1086, 7, 132, 'Onlar: "Bizi büyülemek için mucize (ayet) olarak her ne getirirsen getir, yine de biz sana inanacak değiliz" dediler.'),
(1087, 7, 133, 'Bunun üzerine, ayrı ayrı mucizeler (ayetler) olarak üzerlerine tufan, çekirge, buğday güvesi, kurbağa ve kan musallat kıldık. Yine büyüklük tasladılar ve suçlu-günahkar bir kavim oldular.'),
(1088, 7, 134, 'Başlarına iğrenç bir azap çökünce, dediler ki: "Ey Musa, Rabbine -sana verdiği ahid adına- bizim için dua et. Eğer bu iğrenç azabı üzerimizden çekip-giderirsen, andolsun sana iman edeceğiz ve İsrailoğulları’nı seninle göndereceğiz.'),
(1089, 7, 135, 'Ne zaman ki, onların erişebilecekleri bir süreye kadar, o iğrenç azabı çekip-giderdik, onlar yine andlarını bozdular.'),
(1090, 7, 136, 'Biz de onlardan intikam aldık ve ayetlerimizi yalanlamaları ve onlardan habersizmişler (gibi) olmaları nedeniyle onları suda boğduk.'),
(1091, 7, 137, 'Kendisine bereketler kıldığımız yerin doğusuna da, batısına da o hor kılınıp-zayıf bırakılanları (müstaz\'afları) mirasçılar kıldık. Rabbinin İsrailoğulları’na olan o güzel sözü (vaadi), sabretmeleri dolayısıyla tamamlandı (yerine geldi). Firavun ve kavminin yapmakta oldukları ve yükselttiklerini (köşklerini, saraylarını) da yerle bir ettik.'),
(1092, 7, 138, 'İsrailoğulları’nı denizden geçirdik. Putları önünde bel büküp eğilmekte olan bir topluluğa rastladılar. Musa\'ya dediler ki: "Ey Musa, onların ilahları (var; onlarınki) gibi, sen de bize bir ilah yap." O: "Siz gerçekten cahillik etmekte olan bir kavimsiniz" dedi.'),
(1093, 7, 139, 'Onların içinde bulundukları şey (din) mahvolucudur ve yapmakta oldukları şeyler (ibadetler) de geçersizdir.'),
(1094, 7, 140, '"O sizi alemlere üstün kılmışken, ben size Allah\'tan başka bir İlah mı arayacağım?"'),
(1095, 7, 141, '"Hani size dayanılmaz işkenceler yapan, kadınlarınızı sağ bırakıp erkek çocuklarınızı öldüren Firavun ailesinden sizi kurtarmıştık. Bunda Rabbinizden sizin için büyük bir imtihan vardı."'),
(1096, 7, 142, 'Musa ile otuz gece için sözleştik ve ona bir on daha ekledik. Böylece Rabbinin belirlediği süre, kırk geceye tamamlandı. Musa, kardeşi Harun\'a "Kavmimde benim yerime geç, ıslah et ve bozguncuların yolunu tutma" dedi.'),
(1097, 7, 143, 'Musa tayin edilen sürede gelince ve Rabbi onunla konuşunca: "Rabbim, bana göster, Seni göreyim" dedi. (Allah:) "Beni asla göremezsin, ama şu dağa bak; eğer o yerinde karar kılabilirse, sen de Beni göreceksin." Rabbi dağa tecelli edince, onu paramparça etti. Musa bayılarak yere düştü. Kendine geldiğinde: "Sen ne Yücesin (Rabbim). Sana tevbe ettim ve ben iman edenlerin ilkiyim" dedi.'),
(1098, 7, 144, '(Allah:) "Ey Musa" dedi. "Sana verdiğim risaletimle ve seninle konuşmamla seni insanlar üzerinde seçkin kıldım. Sana verdiklerimi al ve şükredenlerden ol."'),
(1099, 7, 145, 'Biz ona Levhalarda herşeyden bir öğüt ve herşeyin yeterli bir açıklamasını yazdık. (Ve:) "Şimdi bunlara sıkıca sarıl ve kavmine de emret ki en güzeliyle sarılsınlar. Size fasıkların yurdunu pek yakında göstereceğim" (dedik).'),
(1100, 7, 146, 'Yeryüzünde haksız yere büyüklük taslayanları ayetlerimden engelleyeceğim. Onlar her ayeti görseler bile ona inanmazlar; dosdoğru yolu (rüşd yolunu) da görseler, yol olarak benimsemezler, azgınlık yolunu, gördüklerinde ise onu yol olarak benimserler. Bu, onların ayetlerimizi yalanlamaları ve onlardan gafil olmaları dolayısıyladır.'),
(1101, 7, 147, 'Ayetlerimizi ve ahirete kavuşmayı yalanlayanlar, onların amelleri boşa çıkmıştır. Onlar yaptıklarından başkasıyla mı cezalandırılacaklardı?'),
(1102, 7, 148, '(Tura gitmesinin) Ardından Musa\'nın kavmi süs eşyalarından böğürmesi olan bir buzağı heykelini (tapılacak ilah) edindiler. Onun kendileriyle konuşmadığını ve onları bir yola da yöneltip-iletmediğini (hidayete erdirmediğini) görmediler mi? Onu (tanrı) edindiler de, zulmedenler oldular.'),
(1103, 7, 149, 'Ne zaman ki (yaptıklarından dolayı pişmanlık duyup, başları) elleri arasına düşürüldü ve kendilerinin gerçekten şaşırıp-saptıklarını görünce: "Eğer Rabbimiz bize merhamet etmez ve bizi bağışlamazsa kesin olarak hüsrana uğrayanlardan olacağız" dediler.'),
(1104, 7, 150, 'Musa kavmine oldukça kızgın, üzgün olarak döndüğünde onlara: "Beni arkamdan, ne kötü temsil ettiniz? Rabbinizin emrini çabuklaştırdınız, öyle mi?" dedi. Levhaları bıraktı ve kardeşini başından tutup kendisine doğru çekiyordu (ki Harun ona:) "Annem oğlu, bu topluluk beni zayıflattı (hırpalayıp güçsüzleştirdi) ve neredeyse beni öldürmeye giriştiler. Bari sen düşmanları sevindirecek bir şey yapma ve beni bu zalimler topluluğuyla birlikte kılma (sayma)" dedi.'),
(1105, 7, 151, '(Musa yalvarıp) Dedi ki: "Rabbim, beni ve kardeşimi bağışla, bizi rahmetine kat. Sen merhamet edenlerin en merhametli olanısın."'),
(1106, 7, 152, 'Şüphesiz, buzağıyı (tanrı) edinenlere Rablerinden bir gazab ve dünya hayatında bir zillet yetişecektir. İşte Biz, \'yalan düzüp-uyduranları\' böyle cezalandırırız.'),
(1107, 7, 153, 'Kötülük işleyip bunun ardından tevbe edenler ve iman edenler; hiç şüphesiz Rabbin, bundan (tevbeden) sonra elbette bağışlayandır, esirgeyendir.'),
(1108, 7, 154, 'Musa kabaran öfkesi (gazabı) yatışınca Levhaları aldı. (Onlardan bir) Nüshasında "Rablerinden korkanlar için bir hidayet ve bir rahmet vardır" (yazılıydı).'),
(1109, 7, 155, 'Musa, belirlediğimiz buluşma zamanı için kavminden yetmiş adam seçip-ayırdı. Bunları da \'dayanılmaz bir sarsıntı\' tutuverince, dedi ki: "Rabbim, eğer dileseydin, onları ve beni daha önceden helak ederdin. (Şimdi) İçimizdeki beyinsizlerin yaptıklarından dolayı bizi helak edecek misin? O da Senin denemenden başkası değildir. Onunla Sen dilediğini saptırır, dilediğini hidayete erdirirsin. Bizim Velimiz Sensin. Öyleyse bizi bağışla, bizi esirge; Sen bağışlayanların en hayırlısısın."'),
(1110, 7, 156, 'Bize bu dünyada da, ahirette de iyilik yaz, şüphesiz ki biz Sana yöneldik. Dedi ki: "Azabımı dilediğime isabet ettiririm, rahmetim ise herşeyi kuşatmıştır; onu korkup-sakınanlara, zekatı verenlere ve Bizim ayetlerimize iman edenlere yazacağım."'),
(1111, 7, 157, 'Onlar ki, yanlarındaki Tevrat\'ta ve İncil\'de (geleceği) yazılı bulacakları ümmi haber getirici (Nebi) olan elçiye (Resul) uyarlar; o, onlara marufu (iyiliği) emrediyor, münkeri (kötülüğü) yasaklıyor, temiz şeyleri helal, murdar şeyleri haram kılıyor ve onların ağır yüklerini, üzerlerindeki zincirleri indiriyor. Ona inananlar, destek olup savunanlar, yardım edenler ve onunla birlikte indirilen nuru izleyenler; işte kurtuluşa erenler bunlardır.'),
(1112, 7, 158, 'De ki: "Ey insanlar, ben Allah\'ın sizin hepinize gönderdiği bir elçisi (peygamberi)yim. Ki göklerin ve yerin mülkü yalnız O\'nundur. O\'ndan başka İlah yoktur, O diriltir ve öldürür. Öyleyse Allah\'a ve ümmi peygamber olan elçisine iman edin. O da Allah\'a ve O\'nun sözlerine inanmaktadır. Ona iman edin ki hidayete ermiş olursunuz.'),
(1113, 7, 159, 'Musa\'nın kavminden hakka ileten ve onunla adalet yapan bir topluluk vardır.'),
(1114, 7, 160, 'Biz onları (İsrailoğulları’nı) ayrı ayrı oymaklar olarak on iki topluluk (ümmet) olarak ayırdık. Kavmi kendisinden su istediğinde Musa\'ya: "Asan\'la taşa vur" diye vahyettik. Ondan on iki pınar sızıp-fışkırdı; böylece her bir insan- topluluğu su içeceği yeri öğrenmiş oldu. Üzerlerine bulutla gölge çektik ve onlara kudret helvası ile bıldırcın indirdik. (Sonra da şöyle dedik:) "Size rızık olarak verdiklerimizin temiz olanlarından yiyin." Onlar Bize zulmetmedi, ancak kendi nefislerine zulmediyorlardı.'),
(1115, 7, 161, 'Onlara: "Bu şehirde oturun, ondan istediğiniz yerden yeyin, \'dileğimiz bağışlanmadır\' deyin ve kapısından secde ederek girin, (Biz de) hatalarınızı bağışlayalım. İyilik yapanların (armağanlarını) artıracağız" denildiğinde,'),
(1116, 7, 162, 'Onlardan zulmedenler, sözü kendilerine söylenenden başka bir şeyle değiştirdiler. Biz de bunun üzerine zulmetmeleri dolayısıyla gökten \'iğrenç bir azap\' indirdik.'),
(1117, 7, 163, 'Bir de onlara deniz kıyısındaki şehri(n uğradığı sonucu) sor. Hani onlar cumartesi (yasağını çiğneyerek) haddi aşmışlardı. \'Cumartesi günü iş yapma yasağına uyduklarında\', balıkları onlara açıktan akın akın geliyor, \'cumartesi günü iş yapma yasağına uymadıklarında\' ise, gelmiyorlardı. İşte Biz, fıska sapmaları dolayısıyla onları böyle imtihan ediyorduk.'),
(1118, 7, 164, 'Onlardan bir topluluk: "Allah\'ın kendilerini helak etmek veya şiddetli bir azaba uğratmak istediği bir kavme ne diye öğüt veriyorsunuz?" dediğinde "Rabbinize karşı bir özür için ve bir ihtimal sakınabilirler diye" dediler.'),
(1119, 7, 165, 'Kendilerine hatırlatılanı unuttuklarında ise, Biz de kötülükten sakındıranları kurtardık. Zulmedenleri yaptıkları fısk dolayısıyla pek zorlu bir azap ile yakaladık.'),
(1120, 7, 166, 'Onlar, kendisinden sakındırıldıkları \'şeyi yapmada ısrar edip başkaldırınca\' onlara: "Aşağılık maymunlar olunuz" dedik.'),
(1121, 7, 167, 'İşte o zaman Rabbin, onlara en kötü azabı yapacak kimse(leri) kıyamet gününe kadar üzerlerine mutlaka göndereceğini bildirdi. Şüphesiz, Rabbin (ceza ile) sonuçlandırması pek çabuk olandır ve gerçekten O, bağışlayandır, esirgeyendir.'),
(1122, 7, 168, 'Onları yeryüzünde ayrı ayrı topluluklar olarak paramparça dağıttık. Kimileri salih (davranışlarda) bulunuyor, kimileri de bunların dışında olan aşağılıklardır. Onları iyiliklerle ve kötülüklerle imtihan ettik, ki dönsünler.'),
(1123, 7, 169, 'Onların ardından yerlerine kitaba mirasçı olan birtakım \'kötü kimseler\' geçti. (Bunlar) Şu değersiz olan (dünya)ın geçici-yararını alıyor ve: "Yakında bağışlanacağız" diyorlar. Bunun benzeri bir yarar gelince onu da alıyorlar. Kendilerinden Allah\'a karşı hakkı söylemekten başka bir şeyi söylemeyeceklerine ilişkin kitap sözü alınmamış mıydı? Oysa içinde olanı okudular. (Allah\'tan) Korkanlar için ahiret yurdu daha hayırlıdır. Hala akıl erdirmeyecek misiniz?'),
(1124, 7, 170, 'Kitaba sımsıkı sarılanlar ve namazı dosdoğru kılanlar, şüphesiz Biz salih olanların ecrini kaybetmeyiz.'),
(1125, 7, 171, 'Bir zamanlar dağı, sanki bir gölgelikmiş gibi üstlerine geçirmiştik. Onlar ise neredeyse tepelerine düşecek sanmışlardı. (Onlara demiştik ki:) "Size verdiklerimize sımsıkı sarılın ve onda olanı düşünün, ki sakınasınız."'),
(1126, 7, 172, 'Hani Rabbin, Ademoğullarının sırtlarından zürriyetlerini almış ve onları kendi nefislerine karşı şahidler kılmıştı: "Ben sizin Rabbiniz değil miyim?" (demişti de) Onlar: "Evet (Rabbimiz\'sin), şahid olduk" demişlerdi. (Bu,) Kıyamet günü: "Biz bundan habersizdik" dememeniz içindir.'),
(1127, 7, 173, 'Ya da: "Bizden önce ancak atalarımız şirk koşmuştu, biz ise onlardan sonra gelme bir kuşağız; işleri batıl olanların yaptıklarından dolayı bizi helak mı edeceksin?" dememeniz için.'),
(1128, 7, 174, 'İşte Biz ayetleri böyle birer birer açıklarız, umulur ki dönerler.'),
(1129, 7, 175, 'Onlara kendisine ayetlerimizi verdiğimiz kişinin haberini anlat. O, bundan sıyrılıp-uzaklaşmış, şeytan onu peşine takmıştı. O da sonunda azgınlardan olmuştu.'),
(1130, 7, 176, 'Eğer Biz dileseydik, onu bununla yükseltirdik. Ama o yere meyletti (veya yere saplandı), hevasına uydu. Onun durumu, üstüne varsan dilini sarkıtıp soluyan, kendi başına bıraksan dilini sarkıtıp soluyan köpeğin durumu gibidir. İşte ayetlerimizi yalanlayan topluluğun durumu böyledir. Artık gerçek haberi onlara aktar. Ki düşünsünler.'),
(1131, 7, 177, 'Ayetlerimizi yalanlayanlar ve yalnızca kendi nefislerine zulmedenlerin örneği ne kötüdür.'),
(1132, 7, 178, 'Allah kime hidayet verirse o artık hidayeti bulmuştur; kimi şaşırtıp-saptırırsa artık onlar da hüsrana uğrayanlardır.'),
(1133, 7, 179, 'Andolsun, cehennem için cinlerden ve insanlardan çok sayıda kişi yarattık (hazırladık). Kalpleri vardır bununla kavrayıp-anlamazlar, gözleri vardır bununla görmezler, kulakları vardır bununla işitmezler. Bunlar hayvanlar gibidir, hatta daha aşağılıktırlar. İşte bunlar gafil olanlardır.'),
(1134, 7, 180, 'İsimlerin en güzeli Allah\'ındır. Öyleyse O\'na bunlarla dua edin. O\'nun isimlerinde \'aykırılığa (ve inkara) sapanları\' bırakın. Yapmakta oldukları dolayısıyla yakında cezalandırılacaklardır.'),
(1135, 7, 181, 'Yarattıklarımızdan, hakka yöneltip-ileten ve onunla adaleti kılan (uygulayan) bir ümmet vardır.'),
(1136, 7, 182, 'Ayetlerimizi yalanlayanları ise, onları bilmeyecekleri bir yönden derece derece (günahları yükletip azaba) yaklaştıracağız.'),
(1137, 7, 183, 'Onlara bir süre tanıyorum. Hiç şüphesiz Benim düzenim (cezalandırmam) sapasağlamdır.'),
(1138, 7, 184, 'Sahiplerinde (ya da arkadaşları olan peygamberde) delilikten hiçbir şey olmadığını düşünmüyorlar mı? O, apaçık bir uyarıcıdan başkası değildir.'),
(1139, 7, 185, 'Onlar, göklerin ve yerin \'bağımlı olduğu egemenliğe ve sünnete’ (melekût) Allah\'ın yarattığı şeylere ve ihtimal (verip) ecellerinin pek yaklaştığına bakmıyorlar mı? Bundan sonra onlar artık hangi söze inanacaklar?'),
(1140, 7, 186, 'Allah\'ın saptırdığı kimseye artık hidayet verecek yoktur. Ve onları tuğyanları içinde şaşkınca dolaşır bir durumda bırakıverir.'),
(1141, 7, 187, 'Saatin (kıyametin) ne zaman demir atacağını (gerçekleşeceğini) sorarlar. De ki: "Onun ilmi yalnızca Rabbimin Katındadır. Onun süresini O\'ndan başkası açıklayamaz. O, göklerde ve yerde ağırlaştı. O, size apansız bir gelişten başkası değildir." Sanki sen, ondan tümüyle haberdarmışsın gibi sana sorarlar. De ki: "Onun ilmi yalnızca Allah\'ın Katındadır. Ancak insanların çoğu bilmezler."'),
(1142, 7, 188, 'De ki: "Allah\'ın dilemesi dışında kendim için yarardan ve zarardan (hiçbir şeye) malik değilim. Eğer gaybı bilebilseydim muhakkak hayırdan yaptıklarımı arttırırdım ve bana bir kötülük dokunmazdı. Ben, iman eden bir topluluk için, bir uyarıcı ve bir müjde vericiden başkası değilim."'),
(1143, 7, 189, 'O, sizi tek bir nefisten yarattı ve kendisiyle durulup-yatışması için ondan eşini var etti. Onu (eşini) örtüp-bürüyünce, o da bir yük yüklendi de bununla (bir süre) gezindi. Nitekim ağırlaşınca, ikisi Rableri olan Allah\'a dua ettiler: "Eğer bize salih (bir çocuk) verirsen, andolsun şükredenlerden olacağız."'),
(1144, 7, 190, 'Ama O, onlara (Adem\'in çocukları erkek ve kadınlara) salih (bir çocuk) verince, kendilerine verdiği şey konusunda O’na ortaklar kılmaya başladılar. Allah, onların şirk koştuklarından Yücedir.'),
(1145, 7, 191, 'Kendileri yaratılıp dururken, hiçbir şeyi yaratamayan şeyleri mi ortak koşuyorlar?'),
(1146, 7, 192, 'Oysa (bu şirk koştukları güçler ve nesneler) ne onlara bir yardıma güç yetirebilir, ne kendi nefislerine yardım etmeğe.'),
(1147, 7, 193, 'Onları hidayete çağırırsanız size uymazlar. Onları çağırırsanız da, suskun dursanız da size karşı (tutumları) birdir.'),
(1148, 7, 194, 'Allah\'tan başka taptıklarınız sizler gibi kullardır. Eğer doğru iseniz, hemen onları çağırın da size icabet etsinler.'),
(1149, 7, 195, 'Onların yürüyecek ayakları var mı? Ya da tutacakları elleri mi var? Veya görecek gözleri mi var? Yoksa işitecek kulakları mı var? De ki: "Ortak koştuklarınızı çağırın, sonra bir düzen (tuzak) kurun da bana göz bile açtırmayın."'),
(1150, 7, 196, 'Hiç şüphesiz, benim velim kitabı indiren Allah\'tır ve O salihlerin koruyuculuğunu (veliliğini) yapıyor.'),
(1151, 7, 197, 'O\'ndan başka taptıklarınız ise size yardıma güç yetiremezler, kendilerine de.'),
(1152, 7, 198, 'Eğer onları doğru yola çağırırsanız işitmezler. Onları sana bakar (gibi) görürsün, oysa onlar görmezler bile.'),
(1153, 7, 199, 'Sen af (veya kolaylık) yolunu benimse, (İslam\'a) uygun olanı (örfü) emret ve cahillerden yüz çevir.'),
(1154, 7, 200, 'Eğer sana şeytandan yana bir kışkırtma (vesvese veya iğva) gelirse, hemen Allah\'a sığın. Çünkü O, işitendir, bilendir.'),
(1155, 7, 201, '(Allah\'tan) Sakınanlara şeytandan bir vesvese eriştiğinde (önce) iyice düşünürler (Allah\'ı zikredip-anarlar), sonra hemen bakarsın ki görüp bilmişlerdir.'),
(1156, 7, 202, '(Şeytan\'ın) Kardeşleri ise, onları sapıklığa sürüklerler, sonra peşlerini bırakmazlar.'),
(1157, 7, 203, 'Onlara bir ayet getirmediğin zaman: "Sen onu (inmeyen ayeti) derleyip-toplasana" derler. De ki: "Ben, yalnızca bana Rabbimden vahyolunana uyarım. Bu, Rabbinizden olan basiretlerdir; iman edecek bir topluluk için bir hidayet ve bir rahmettir."'),
(1158, 7, 204, 'Kuran okunduğu zaman, hemen onu dinleyin ve susun. Umulur ki esirgenmiş olursunuz.'),
(1159, 7, 205, 'Rabbini, sabah akşam, yüksek olmayan bir sesle, kendi kendine, ürpertiyle, yalvara yalvara ve için için zikret. Gaflete kapılanlardan olma.'),
(1160, 7, 206, 'Şüphesiz Rabbinin Katında olanlar, O\'na ibadet etmekten büyüklenmezler; O\'nu tesbih ederler ve yalnız O\'na secde ederler.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Sana savaş-ganimetlerini sorarlar. De ki: "Ganimetler Allah\'ın ve Resûlündür. Buna göre, eğer mü\'min iseniz Allah\'tan korkup-sakının, aranızı düzeltin ve Allah\'a ve Resûlü’ne itaat edin."'),
(1162, 8, 2, 'Mü\'minler ancak o kimselerdir ki, Allah anıldığı zaman yürekleri ürperir. O\'nun ayetleri okunduğunda imanlarını arttırır ve yalnızca Rablerine tevekkül ederler.'),
(1163, 8, 3, 'Onlar, namazı dosdoğru kılarlar ve kendilerine rızık olarak verdiklerimizden infak ederler.'),
(1164, 8, 4, 'İşte gerçek mü\'minler bunlardır. Rableri Katında onlar için dereceler, bağışlanma ve üstün bir rızık vardır.'),
(1165, 8, 5, 'Rabbin seni evinden hak uğrunda (savaşa) çıkardığında mü\'minlerden bir grup isteksizdi.'),
(1166, 8, 6, '(Herşey) Açıkça ortaya çıktıktan sonra bile, sanki kendileri, göz göre göre ölüme sürükleniyorlarmış gibi, seninle hak konusunda tartışıp duruyorlardı.'),
(1167, 8, 7, 'Hani Allah, iki topluluktan birinin muhakkak sizin olacağını vadetmişti; siz de güçsüz olanın sizin olmasını istiyordunuz. Oysa Allah, sözleriyle hakkın ve inkar edenlerin arkasını kesmek (kökünü kurutmak) istiyordu.'),
(1168, 8, 8, 'O, suçlu-günahkarlar istemese de, hakkı gerçekleştirmek ve batılı geçersiz kılmak için (böyle istiyordu.)'),
(1169, 8, 9, 'Siz Rabbinizden yardım taleb ediyordunuz, O da: "Şüphesiz Ben size birbiri ardınca bin melek ile yardım ediciyim" diye cevap vermişti.'),
(1170, 8, 10, 'Allah, bunu, yalnızca bir müjde ve kalplerinizin tatmin bulması için yapmıştı; (yoksa) Allah\'ın Katından başkasında nusret (zafer ve yardım) yoktur. Hiç şüphesiz Allah üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(1171, 8, 11, 'Hani Kendisi\'nden bir güvenlik olarak sizi bir uyuklama bürüyordu. Sizi kendisiyle tertemiz kılmak, sizden şeytanın pisliklerini gidermek, kalplerinizin üstünde (güven ve kararlılık duygusunu) pekiştirmek ve bununla ayaklarınızı (arz üzerinde) sağlamlaştırmak için size gökten su indiriyordu.'),
(1172, 8, 12, 'Rabbin meleklere vahyetmişti ki: "Şüphesiz Ben sizinleyim, iman edenlere sağlamlık katın, inkar edenlerin kalplerine amansız bir korku salacağım. Öyleyse (ey Müslümanlar,) vurun boyunlarının üstüne, vurun onların bütün parmaklarına."'),
(1173, 8, 13, 'Bu, elbette, onların Allah\'a ve elçisine baş kaldırmaları dolayısıyladır. Kim Allah\'a ve elçisine baş kaldırırsa, şüphesiz Allah (ceza ile) sonuçlandırması pek şiddetli olandır.'),
(1174, 8, 14, 'İşte bu sizin; tadın bunu. İnkara sapanlara bir de ateş azabı vardır.'),
(1175, 8, 15, 'Ey iman edenler, toplu olarak kafirlerle karşılaştığınız zaman, onlara arka çevirmeyin (savaştan kaçmayın).'),
(1176, 8, 16, 'Kim onlara böyle bir günde -yine savaşmak için bir yana çekilen ya da bir başka bölüğe katılmak için yer tutanın dışında- arkasını çevirirse, gerçekten o, Allah\'tan bir gazaba uğramıştır ve onun barınma yeri cehennemdir. Ne kötü bir yataktır o.'),
(1177, 8, 17, 'Onları siz öldürmediniz, ama onları Allah öldürdü; attığın zaman sen atmadın, ama Allah attı. Mü\'minleri Kendinden güzel bir imtihanla imtihan etmek için (yaptı.) Şüphesiz Allah, işitendir, bilendir.'),
(1178, 8, 18, 'İşte size böyle… Gerçekten Allah, kafirlerin hileli-düzenlerini boşa çıkarıcıdır.'),
(1179, 8, 19, 'Eğer fetih istiyor idiyseniz (ey kafirler,) işte size fetih; ama eğer (inkardan ve eski yaptıklarınızdan) vazgeçerseniz bu sizin için daha hayırlıdır. Yok, geri dönerseniz Biz de döneriz. Topluluğunuz çok da olsa, size bir şey sağlayamaz. Çünkü Allah mü\'minlerle beraberdir.'),
(1180, 8, 20, 'Ey iman edenler, Allah\'a ve Resûlü’ne itaat edin. Siz de işitiyorken, ondan yüz çevirmeyin.'),
(1181, 8, 21, 'Ve: "Biz işittik" dedikleri halde, gerçekte işitmeyenler gibi olmayın;'),
(1182, 8, 22, 'Gerçek şu ki, Allah Katında, yerde debelenenlerin en kötüsü, (bir türlü) akıl erdirmez olan sağırlar ve dilsizlerdir.'),
(1183, 8, 23, 'Eğer Allah, onlarda bir hayır görseydi muhakkak onlara işittirirdi. İşittirseydi bile, arka çevirenler olarak (yine) yüz çevirirlerdi.'),
(1184, 8, 24, 'Ey iman edenler, size hayat verecek şeylere sizi çağırdığı zaman, Allah\'a ve Resûlü’ne icabet edin. Ve bilin ki muhakkak Allah, kişi ile kalbi arasına girer ve siz gerçekten O\'na götürülüp toplanacaksınız.'),
(1185, 8, 25, 'Ve sizlerden yalnızca zulmedenlere isabet etmekle kalmayan bir fitneden korkup-sakının. Bilin ki, gerçekten Allah (ceza ile) sonuçlandırması pek şiddetli olandır.'),
(1186, 8, 26, 'Hatırlayın; hani sizler sayıca azdınız ve yeryüzünde zayıf bırakılmıştınız, insanların sizi kapıp-yakalamasından korkuyordunuz. İşte O, sizi (yerleşik kılıp) barındırandı, sizi yardımıyla destekledi ve size temiz şeylerden rızıklar verdi. Ki şükredesiniz.'),
(1187, 8, 27, 'Ey iman edenler, Allah\'a ve Resûlü’ne ihanet etmeyin, bile bile emanetlerinize de ihanet etmeyin.'),
(1188, 8, 28, 'Bilin ki, mallarınız ve çocuklarınız ancak bir fitnedir (imtihan konusudur.) Allah yanında ise büyük bir mükafat vardır.'),
(1189, 8, 29, 'Ey iman edenler, Allah\'tan korkup-sakınırsanız, size doğruyu yanlıştan ayıran bir nur ve anlayış (furkan) verir, kötülüklerinizi örter ve sizi bağışlar. Allah büyük fazl sahibidir.'),
(1190, 8, 30, 'Hani o inkar edenler, seni tutuklamak ya da öldürmek veya sürgün etmek amacıyla, tuzak kuruyorlardı. Onlar bu tuzağı tasarlıyorlarken, Allah da bir düzen (bir karşılık) kuruyordu. Allah, düzen kurucuların (tuzaklarına karşılık verenlerin) hayırlısıdır.'),
(1191, 8, 31, 'Ayetlerimiz onlara okunduğu zaman; "İşittik" dediler. "İstesek, biz de bunun bir benzerini söyleyebiliriz. Bu, eskilerin efsanelerinden başkası değildir."'),
(1192, 8, 32, 'Bir de: "Ey Allah\'ımız, eğer bu (Kur\'an) bir gerçek olarak Senin Katından ise, gökyüzünden üstümüze taş yağdır veya acı bir azap getir (bakalım)." demişlerdi.'),
(1193, 8, 33, 'Oysa sen içlerinde bulunduğun sürece, Allah onları azaplandıracak değildir. Ve onlar, bağışlanma dilemektelerken de, Allah onları azaplandıracak değildir.'),
(1194, 8, 34, 'Onlar, Mescid-i Haram\'dan (insanları) alıkoyarlarken ve onun (gerçek ve layık) koruyucuları değilken Allah, ne diye onları azaplandırmasın? Onun (asıl) koruyucularıyalnızca korkup-sakınanlardır. Ancak onların çoğu bilmezler.'),
(1195, 8, 35, 'Onların Beyt(-i Şerif) önündeki duaları, ıslık çalmaktan ve el çırpmaktan başkası değildir. Artık inkar ettikleriniz dolayısıyla tadın azabı.'),
(1196, 8, 36, 'Gerçek şu ki, inkar edenler, (insanları) Allah\'ın yolundan engellemek için mallarını harcarlar; bundan böyle de harcayacaklar. Sonra bu, onlara yürek acısı olacaktır, sonra bozguna uğratılacaklardır. İnkar edenler sonunda cehenneme sürülüp toplanacaklardır.'),
(1197, 8, 37, 'Bu, Allah\'ın murdar olanı temizden ayırt etmesi; murdarı, bir kısmını bir kısmı üzerinde kılıp tümünü biriktirerek cehenneme atması içindir. İşte bunlar hüsrana uğrayanlardır.'),
(1198, 8, 38, 'O inkar edenlere de ki: "Eğer vazgeçerlerse geçmişte (yaptıkları) şeyler bağışlanacaktır. Ama yine dönecek olurlarsa, önceki (toplumlara uygulanan) sünnet, muhakkak (onların başından da) geçmiş olacaktır.'),
(1199, 8, 39, 'Fitne kalmayıncaya ve dinin hepsi Allah\'ın oluncaya kadar onlarla savaşın. Şayet vazgeçecek olurlarsa, şüphesiz Allah, yaptıklarını görendir.'),
(1200, 8, 40, 'Geri dönerlerse, bilin ki gerçekten Allah, sizin mevlanızdır. O, ne güzel mevladır ve ne güzel yardımcıdır.'),
(1201, 8, 41, 'Bilin ki, \'ganimet olarak ele geçirdiğiniz\' şeylerin beşte biri, muhakkak Allah\'ın, Resûlün, yakınların, yetimlerin, yoksulların ve yolcunundur. Eğer Allah\'a, hak ile batılın birbirinden ayrıldığı gün, iki ordunun karşı karşıya geldiği günde (Bedir\'de) kulumuza indirdiğimize iman ediyorsanız (ganimeti böyle bölüşün). Allah, herşeye güç yetirendir.'),
(1202, 8, 42, 'Hani siz vadinin yakın kenarında, onlar uzak yamacındaydılar; kervan ise sizden daha aşağıdaydı. Eğer sözleşseydiniz, kaçınılmaz olarak sözleşme yeri (veya konusu) hakkında anlaşmazlığa düşerdiniz; ancak Allah, olacağı olan işi gerçekleştirmek için (böyle yaptı). Böylece, helak olacak kişi apaçık bir delilden sonra helak olsun, diri kalacak kişi apaçık bir delilden sonra hayatta kalsın. Şüphesiz Allah, gerçekten işitendir, bilendir.'),
(1203, 8, 43, 'Hani Allah, onları sana uykunda az gösteriyordu; eğer sana çok gösterseydi, gerçekten yılgınlığa kapılacaktınız ve iş konusunda gerçekten çekişmeye düşecektiniz. Ancak Allah esenlik (kurtuluş) bağışladı. Çünkü O, elbette sinelerin özünde saklı duranı bilendir.'),
(1204, 8, 44, 'Karşı karşıya geldiğinizde, Allah, \'olacağı olan işi gerçekleştirmek\' için, onları gözlerinizde az gösteriyor, sizi de onların gözlerinde azaltıyordu. Ve (bütün) işler Allah\'a döndürülür.'),
(1205, 8, 45, 'Ey iman edenler, bir toplulukla karşı karşıya geldiğiniz zaman, dayanıklılık gösterin ve Allah\'ı çokça zikredin. Ki kurtuluş (felah) bulasınız.'),
(1206, 8, 46, 'Allah\'a ve Resûlü’ne itaat edin ve çekişip birbirinize düşmeyin, çözülüp yılgınlaşırsınız, gücünüz gider. Sabredin. Şüphesiz Allah, sabredenlerle beraberdir.'),
(1207, 8, 47, 'Bir de yurtlarından refahtan şımarıp-azıtarak, insanlara gösteriş yaparak çıkanlar ve (halkı) Allah\'ın yolundan alıkoyanlar gibi olmayın. Allah, onların yaptıklarını çepeçevre kuşatandır.'),
(1208, 8, 48, 'O zaman şeytan onlara amellerini çekici göstermiş ve onlara: "Bugün sizi insanlardan bozguna uğratacak kimse yoktur ve ben de sizin yardımcınızım" demişti. Ne zaman ki, iki topluluk birbirini görür oldu (karşılaştı) o, iki topuğu üstünde geri döndü ve: "Şüphesiz ben sizden uzağım. Çünkü ben sizin görmediğinizi görüyorum, ben Allah\'tan da korkuyorum" dedi. Allah (ceza ile) sonuçlandırması pek şiddetli olandır.'),
(1209, 8, 49, 'Münafıklar ve kalplerinde hastalık olanlar şöyle diyorlardı: "Bunları (Müslümanları) dinleri aldattı." Oysa kim Allah\'a tevekkül ederse, şüphesiz Allah, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(1210, 8, 50, 'Melekleri, onların yüzlerine ve arkalarına vurarak: "Yakıcı azabı tadın" diye o inkar edenlerin canlarını alırken görmelisin.'),
(1211, 8, 51, 'Bu, ellerinizin önceden takdim ettiği işler yüzündendir. Yoksa şüphesiz Allah kullara zulmedici değildir.'),
(1212, 8, 52, 'Firavun ailesinin ve onlardan öncekilerin gidiş tarzı gibi Allah\'ın ayetlerini inkar ettiler de, Allah da onları günahlarından dolayı yakalayıverdi. Şüphesiz, Allah, en büyük kuvvet sahibidir, sonuçlandırması pek şiddetlidir.'),
(1213, 8, 53, 'Nedeni şu: Bir kavim (toplum), kendinde olanı değiştirinceye kadar Allah, ona nimet olarak bağışladığını değiştirici değildir. Allah şüphesiz işitendir, bilendir.'),
(1214, 8, 54, 'Firavun ailesinin ve onlardan öncekilerin gidiş tarzı gibi. Onlar, Rablerinin ayetlerini yalanladılar; Biz de günahları dolayısıyla onları helak ettik. Firavun ordusunu suda boğduk. Onların tümü zulmeden kimselerdi.'),
(1215, 8, 55, 'Allah Katında canlıların en kötüsü, şüphesiz inkar edenlerdir. Onlar artık inanmazlar.'),
(1216, 8, 56, 'Bunlar, içlerinden antlaşma yaptığın kimselerdir ki, sonra her defasında ahidlerini bozarlar. Onlar sakınmazlar.'),
(1217, 8, 57, 'Bundan dolayı, savaşta onları yakalarsan, öyle darmadağın et ki, onlarla arkalarından gelecek olanlar(ı caydır). Umulur ki ibret alırlar.'),
(1218, 8, 58, 'Eğer bir kavmin ihanet edeceğinden kesin olarak korkarsan, sen de açık ve adil bir tutumla (onlarla olan anlaşma metnini ve diplomatik ilişkiyi) at. Gerçekten Allah, ihanet edenleri sevmez.'),
(1219, 8, 59, 'İnkar edenler, kaçıp-kurtulduklarını sanmasınlar; gerçek şu ki, onlar (Bizi) aciz bırakamazlar.'),
(1220, 8, 60, 'Onlara karşı gücünüzün yettiği kadar kuvvet ve besili atlar hazırlayın. Bununla, Allah\'ın düşmanı ve sizin düşmanınızı ve bunların dışında sizin bilmeyip Allah\'ın bildiği diğer (düşmanları) korkutup-caydırasınız. Allah yolunda her ne infak ederseniz, size \'eksiksiz olarak ödenir\' ve siz haksızlığa uğratılmazsınız.'),
(1221, 8, 61, 'Eğer onlar barışa eğilim gösterirlerse, sen de ona eğilim göster ve Allah\'a tevekkül et. Çünkü O, işitendir, bilendir.'),
(1222, 8, 62, 'Onlar, seni aldatmak isterlerse, şüphesiz Allah sana yeter. O, seni yardımıyla ve mü\'minlerle destekledi.'),
(1223, 8, 63, 'Ve onların kalplerini uzlaştırdı. Sen, yeryüzündekilerin tümünü harcasaydın bile, onların kalplerini uzlaştıramazdın. Ama Allah, aralarını bulup onları uzlaştırdı. Çünkü O, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(1224, 8, 64, 'Ey Peygamber, sana ve seni izleyen mü\'minlere Allah yeter.'),
(1225, 8, 65, 'Ey Peygamber, mü\'minleri savaşa karşı hazırlayıp-teşvik et. Eğer içinizde sabreden yirmi (kişi) bulunursa, iki yüz (kişiyi) mağlub edebilirler. Ve eğer içinizden yüz (sabırlı kişi) bulunursa, kafirlerden binini yener. Çünkü onlar (gerçeği) kavramayan bir topluluktur.'),
(1226, 8, 66, 'Şimdi, Allah sizden (yükünüzü) hafifletti ve sizde bir za\'f olduğunu bildi. Sizden yüz sabırlı (kişi) bulunursa, (onların) iki yüzünü bozguna uğratır; eğer sizden bin (kişi) olursa, Allah\'ın izniyle (onların) iki binini yener. Allah, sabredenlerle beraberdir.'),
(1227, 8, 67, 'Hiçbir peygambere, yeryüzünde kesin bir zafer kazanıncaya kadar esir alması yakışmaz. Siz dünyanın geçici yararını istiyorsunuz. Oysa Allah (size) ahireti istemektedir. Allah, üstün ve güçlüdür, hüküm ve hikmet sahibidir.'),
(1228, 8, 68, 'Eğer Allah\'ın geçmişte bir yazması (söz vermesi) olmasaydı, aldıklarınıza karşılık size gerçekten büyük bir azap dokunurdu.'),
(1229, 8, 69, 'Artık ganimet olarak elde ettiklerinizden helal ve temiz olarak yiyin ve Allah\'tan korkup-sakının. Şüphesiz Allah bağışlayandır, esirgeyendir."'),
(1230, 8, 70, 'Ey peygamber, ellerinizdeki esirlere de ki: "Eğer Allah, sizin kalplerinizde bir hayır olduğunu bilirse (görürse) size sizden alınandan daha hayırlısını verir ve sizi bağışlar. Allah bağışlayandır, esirgeyendir."'),
(1231, 8, 71, 'Eğer sana ihanet etmek isterlerse, onlar daha önce Allah\'a da ihanet etmişlerdi; böylece O da, "bozguna uğramaları (için) sana imkan vermişti.\' Allah, bilendir, hüküm ve hikmet sahibidir.'),
(1232, 8, 72, 'Gerçek şu ki, iman edenler, hicret edenler ve Allah yolunda mallarıyla ve canlarıyla cihad edenler ile (hicret edenleri) barındıranlar ve yardım edenler, işte birbirlerinin velisi olanlar bunlardır. İman edip hicret etmeyenler, onlar hicret edinceye kadar, sizin onlara hiçbir şeyle velayetiniz yoktur. Ama din konusunda sizden yardım isterlerse, yardım üzerinizde bir yükümlülüktür. Ancak, sizlerle onlar arasında anlaşma bulunan bir topluluğun aleyhinde değil. Allah, yaptıklarınızı görendir.'),
(1233, 8, 73, 'İnkar edenler birbirlerinin velileridir. Eğer siz bunu yapmazsanız (birbirinize yardım etmez ve dost olmazsanız) yeryüzünde bir fitne ve büyük bir bozgunculuk (fesat) olur.'),
(1234, 8, 74, 'İman edenler, hicret edenler ve Allah yolunda cihad edenler ile (hicret edenleri) barındıranlar ve yardım edenler, işte gerçek mü\'min olanlar bunlardır. Onlar için bir bağışlanma ve üstün bir rızık vardır.'),
(1235, 8, 75, 'Bundan sonra iman edip hicret edenler ve sizinle birlikte cihad edenler, işte onlar sizdendir. Akrabalar (mirasta) Allah\'ın Kitabı’na göre, birbirlerine (mirasta) önceliklidir. Doğrusu Allah herşeyi bilendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, '(Bu,) Müşriklerden kendileriyle antlaşma imzaladıklarınıza Allah\'tan ve Resûlü’nden kesin bir uyarıdır.'),
(1237, 9, 2, 'Bundan böyle yeryüzünde (size tanınmış bir süre olarak) dört ay dolaşın. Ve bilin ki Allah\'ı aciz bırakacak değilsiniz. Gerçekten Allah, inkar edenleri hor ve aşağılık kılıcıdır.'),
(1238, 9, 3, 'Ve büyük Hacc (Hacc-ı Ekber) günü, Allah\'tan ve Resûlü’nden insanlara bir duyuru: Kesin olarak Allah, müşriklerden uzaktır, O\'nun Resûlü de… Eğer tevbe ederseniz bu sizin için daha hayırlıdır; yok eğer yüz çevirirseniz, bilin ki Allah\'ı elbette aciz bırakacak değilsiniz. İnkar edenleri acı bir azapla müjdele.'),
(1239, 9, 4, 'Ancak müşriklerden kendileriyle antlaşma imzaladıklarınızdan (antlaşmadan) bir şeyi eksiltmeyenler ve size karşı hiç kimseye yardım etmeyenler başka; artık antlaşmalarını, süresi bitene kadar tamamlayın. Şüphesiz, Allah muttaki olanları sever.'),
(1240, 9, 5, 'Haram aylar (süre tanınmış dört ay) sıyrılıp-bitince (çıkınca) müşrikleri bulduğunuz yerde öldürün, onları tutuklayın, kuşatın ve onların bütün geçit yerlerini kesip-tutun. Eğer tevbe edip namaz kılarlarsa ve zekatı verirlerse yollarını açıverin. Gerçekten Allah, bağışlayandır, esirgeyendir.'),
(1241, 9, 6, 'Eğer müşriklerden biri, senden \'eman isterse\', ona eman ver; öyle ki Allah\'ın sözünü dinlemiş olsun, sonra onu \'güvenlik içinde olacağı yere ulaştır.\' Bu, onların elbette bilmeyen bir topluluk olmaları nedeniyledir.'),
(1242, 9, 7, 'Mescid-i Haram yanında kendileriyle anlaştıklarınız dışında, müşriklerin Allah Katında ve Resûlünün katında nasıl bir ahdi olabilir? Şu halde o (anlaşmalı olanlar), size karşı (doğru) bir tutum takındıkça, siz de onlara karşı doğru bir tutum takının. Şüphesiz Allah, muttaki olanları sever.'),
(1243, 9, 8, 'Nasıl olabilir ki!.. Eğer size karşı galip gelirlerse size karşı ne \'akrabalık bağlarını\', ne de \'sözleşme hükümlerini\' gözetip-tanırlar. Sizi ağızlarıyla hoşnut kılarlar, kalpleri ise karşı koyar. Onların çoğu fasık kimselerdir.'),
(1244, 9, 9, 'Allah\'ın ayetlerine karşılık az bir değeri satın aldılar, böylece O\'nun yolunu engellediler. Onların yaptıkları gerçekten ne kötüdür.'),
(1245, 9, 10, 'Onlar (hiç) bir mü\'mine karşı ne \'akrabalık bağlarını\', ne de \'sözleşme hükümlerini\' gözetip tanırlar. İşte bunlar, haddi aşmakta olanlardır.'),
(1246, 9, 11, 'Eğer onlar tevbe edip namazı kılarlarsa ve zekatı verirlerse, artık onlar sizin dinde kardeşlerinizdir. Bilen bir topluluk için ayetleri böyle birer birer açıklarız.'),
(1247, 9, 12, 'Ve eğer antlaşmalardan sonra, yine yeminlerini bozarlarsa ve dininize hınç besleyip-saldırırlarsa, bu durumda küfrün önderleriyle çarpışın. Çünkü onlar, yeminleri olmayan kimselerdir; belki cayarlar.'),
(1248, 9, 13, 'Yeminlerini bozan, elçiyi (yurdundan) sürmeye çabalayan ve sizinle ilk defa (savaşa) başlayan bir toplulukla savaşmaz mısınız? Korkuyor musunuz onlardan? Eğer inanıyorsanız, Kendisi\'nden korkmanıza Allah daha layıktır.'),
(1249, 9, 14, 'Onlarla çarpışınız. Allah, onları sizin ellerinizle azaplandırsın, hor ve aşağılık kılsın ve onlara karşı size zafer versin, mü\'minler topluluğunun göğsünü şifaya kavuştursun.'),
(1250, 9, 15, 'Ve kalplerindeki öfkeyi gidersin. Allah dilediğinin tevbesini kabul eder. Allah bilendir, hüküm ve hikmet sahibidir.'),
(1251, 9, 16, 'Yoksa siz, içinizden cihad edenleri ve Allah\'tan ve Resûlü’nden ve mü\'minlerden başka sır-dostu edinmeyenleri Allah \'bilip (ortaya) çıkarmadan\' bırakılıvereceğinizi mi sandınız? Allah yaptıklarınızdan haberdardır.'),
(1252, 9, 17, 'Şirk koşanların, kendi inkarlarına bizzat kendileri şahidler iken, Allah\'ın mescidlerini onarmalarına (hak ve yetkileri) yoktur. İşte bunlar, yaptıkları boşa gitmiş olanlardır. Ve bunlar ateşte süresiz kalacak olanlardır.'),
(1253, 9, 18, 'Allah\'ın mescidlerini, yalnızca Allah\'a ve ahiret gününe iman eden, namazı dosdoğru kılan, zekatı veren ve Allah\'tan başkasından korkmayanlar onarabilir. İşte, hidayete erenlerden oldukları umulanlar bunlardır.'),
(1254, 9, 19, 'Hacılara su dağıtmayı ve Mescid-i Haram\'ı onarmayı, Allah\'a ve ahiret gününe iman eden ve Allah yolunda cihad edenin (yaptıkları) gibi mi saydınız? (Bunlar) Allah Katında bir olmazlar. Allah zulmeden bir topluluğa hidayet vermez.'),
(1255, 9, 20, 'İman edenler, hicret edenler ve Allah yolunda mallarıyla ve canlarıyla cihad edenlerin Allah Katında büyük dereceleri vardır. İşte \'kurtuluşa ve mutluluğa\' erenler bunlardır.'),
(1256, 9, 21, 'Rableri onlara Katından bir rahmeti, bir hoşnutluğu ve onlar için, kendisine sürekli bir nimet bulunan cennetleri müjdeler.'),
(1257, 9, 22, 'Onda ebedi kalıcıdırlar. Şüphesiz Allah, büyük mükafat Katında olandır.'),
(1258, 9, 23, 'Ey iman edenler, eğer imana karşı inkarı sevip-tercih ediyorlarsa, babalarınızı ve kardeşlerinizi veliler edinmeyin. Sizden kim onları veli edinirse, işte bunlar zulmeden kimselerdir.'),
(1259, 9, 24, 'De ki: "Eğer babalarınız, çocuklarınız, kardeşleriniz, eşleriniz, aşiretiniz, kazandığınız mallar, az kar getireceğinden korktuğunuz ticaret ve hoşunuza giden evler, sizlere Allah\'tan, O\'nun Resûlü’nden ve O\'nun yolunda cihad etmekten daha sevimli ise, artık Allah\'ın emri gelinceye kadar bekleyedurun. Allah, fasıklar topluluğuna hidayet vermez.'),
(1260, 9, 25, 'Andolsun, Allah birçok yerlerde ve Huneyn gününde size yardım etti. Hani çok sayıda oluşunuz sizi böbürlendirip-gururlandırmıştı, fakat size bir şey de sağlayamamıştı. Yer ise, bütün genişliğine rağmen size dar gelmişti, sonra arkanıza dönüp gerisin geri gitmiştiniz.'),
(1261, 9, 26, '(Bundan) Sonra Allah, elçisi ile mü\'minlerin üzerine \'güven duygusu ve huzur\' indirdi, sizin görmediğiniz orduları indirdi ve inkar edenleri azaplandırdı. Bu, inkarcıların cezasıdır.'),
(1262, 9, 27, 'Bunun ardından Allah, dilediği kimseden tevbesini kabul eder. Allah, bağışlayandır, esirgeyendir.'),
(1263, 9, 28, 'Ey iman edenler, müşrikler ancak bir pisliktirler; öyleyse bu yıllarından sonra artık Mescid-i Haram\'a yaklaşmasınlar. Eğer ihtiyaç içinde kalmaktan korkarsanız, Allah dilerse sizi Kendi fazlından zengin kılar. Şüphesiz Allah bilendir, hüküm ve hikmet sahibidir.'),
(1264, 9, 29, 'Kendilerine kitap verilenlerden, Allah\'a ve ahiret gününe inanmayan, Allah\'ın ve Resûlü’nün haram kıldığını haram tanımayan ve hak dini (İslam\'ı) din edinmeyenlerle, küçük düşürülüp cizyeyi kendi elleriyle verinceye kadar savaşın.'),
(1265, 9, 30, 'Yahudiler: "Üzeyir Allah\'ın oğludur" dediler; Hıristiyanlar da: "Mesih Allah\'ın oğludur" dediler. Bu, onların ağızlarıyla söylemeleridir; onlar, bundan önceki inkar edenlerin sözlerini taklid ediyorlar. Allah onları kahretsin; nasıl da çevriliyorlar?'),
(1266, 9, 31, 'Onlar, Allah\'ı bırakıp bilginlerini ve rahiplerini rablar (ilahlar) edindiler ve Meryem oğlu Mesih\'i de. Oysa onlar, tek olan bir İlah\'a ibadet etmekten başka bir şeyle emrolunmadılar. O\'ndan başka İlah yoktur. O, bunların şirk koştukları şeylerden Yücedir.'),
(1267, 9, 32, 'Ağızlarıyla Allah\'ın nurunu söndürmek istiyorlar. Oysa kafirler istemese de Allah, Kendi nurunu tamamlamaktan başkasını istemiyor.'),
(1268, 9, 33, 'Müşrikler istemese de, O, dini (İslam\'ı) bütün dinlere üstün kılmak için elçisini hidayetle ve hak dinle gönderen O\'dur.'),
(1269, 9, 34, 'Ey iman edenler, gerçek şu ki, (Yahudi) bilginlerinden ve (Hıristiyan) rahiplerinden çoğu, insanların mallarını haksızlıkla yerler ve Allah\'ın yolundan alıkoyarlar. Altını ve gümüşü biriktirip de Allah yolunda harcamayanlar... Onlara acı bir azabı müjdele.'),
(1270, 9, 35, 'Bunların üzerlerinin cehennem ateşinde kızdırılacağı gün, onların alınları, böğürleri ve sırtları bunlarla dağlanacak (ve:) "İşte bu, kendiniz için yığıp-sakladıklarınızdır; yığıp-sakladıklarınızı tadın" (denilecek).'),
(1271, 9, 36, 'Gerçek şu ki, Allah Katında ayların sayısı, gökleri ve yeri yarattığı günden beri Allah\'ın kitabında on ikidir. Bunlardan dördü haram aylardır. İşte dosdoğru olan hesab (din) budur. Öyleyse bunlarda kendinize zulmetmeyin ve onların sizlerle topluca savaşması gibi siz de müşriklerle topluca savaşın. Ve bilin ki Allah, takva sahipleriyle beraberdir.'),
(1272, 9, 37, '(Haram ayları) Ertelemek ancak inkarda bir artıştır. Bununla kafirler şaşırtılıp-saptırılır. Allah\'ın haram kıldığına sayı bakımından uymak için, onu bir yıl helal, bir yıl haram kılıyorlar. Böylelikle Allah\'ın haram kıldığını helal kılmış oluyorlar. Yaptıklarının kötülüğü kendilerine \'çekici ve süslü\' gösterilmiştir. Allah, inkarcı bir topluluğa hidayet vermez.'),
(1273, 9, 38, 'Ey iman edenler, ne oldu ki size, Allah yolunda savaşa kuşanın denildiği zaman, yer(iniz)de ağırlaşıp kaldınız? Ahiretten (cayıp) dünya hayatına mı razı oldunuz? Ama ahirettekine (göre), bu dünya hayatının yararı pek azdır.'),
(1274, 9, 39, 'Eğer savaşa kuşanıp-çıkmazsanız, O sizi pek acı bir azapla azaplandıracak ve yerinize bir başka topluluğu getirip değiştirecektir. Siz O\'na hiçbir şeyle zarar veremezsiniz. Allah, herşeye güç yetirendir.'),
(1275, 9, 40, 'Siz Ona (Peygambere) yardım etmezseniz, Allah Ona yardım etmiştir. Hani kafirler ikiden biri olarak Onu (Mekke\'den) çıkarmışlardı; ikisi mağarada olduklarında arkadaşına şöyle diyordu: "Hüzne kapılma, elbette Allah bizimle beraberdir." Böylece Allah Ona \'huzur ve güvenlik duygusunu\' indirmişti, Onu sizin görmediğiniz ordularla desteklemiş, inkar edenlerin de kelimesini (inkar çağrılarını) alçaltmıştı. Oysa Allah\'ın kelimesi, Yüce olandır. Allah üstün ve güçlüdür, hüküm ve hikmet sahibidir.'),
(1276, 9, 41, 'Hafif ve ağır savaşa kuşanıp çıkın ve Allah yolunda mallarınızla ve canlarınızla cihad edin. Eğer bilirseniz, bu sizin için daha hayırlıdır.'),
(1277, 9, 42, 'Eğer yakın bir yarar ve orta bir sefer olsaydı, onlar mutlaka seni izlerlerdi. Ama zorluk onlara uzak geldi. "Eğer güç yetirseydik muhakkak seninle birlikte (savaşa) çıkardık." diye sana Allah adına yemin edecekler. Kendi nefislerini helaka sürüklüyorlar. Allah onların gerçekten yalan söylediklerini biliyor.'),
(1278, 9, 43, 'Allah seni affetsin; doğru söyleyenler sana açıkça belli oluncaya ve yalancıları da öğreninceye kadar niye onlara izin verdin?'),
(1279, 9, 44, 'Allah\'a ve ahiret gününe iman edenler, mallarıyla ve canlarıyla cihad etmekten (kaçınmak için) senden izin istemezler. Allah takva sahiplerini bilendir.'),
(1280, 9, 45, 'Senden, yalnızca Allah\'a ve ahiret gününe inanmayan, kalpleri kuşkuya kapılıp, kuşkularında kararsızlığa düşenler izin ister.'),
(1281, 9, 46, 'Eğer (savaşa) çıkmak isteselerdi, herhalde ona bir hazırlık yaparlardı. Ancak Allah, (savaşa) gönderilmelerini çirkin gördü de ayaklarını doladı ve; "(Onlara) Siz de oturanlarla birlikte oturun" denildi.'),
(1282, 9, 47, 'Sizinle birlikte çıksalardı, size \'kötülük ve zarardan\' başka bir şey ilave etmez ve aranıza mutlaka fitne sokmak üzere içinizde çaba yürütürlerdi. İçinizde onlara \'haber taşıyanlar\' vardır. Allah, zulmedenleri bilir.'),
(1283, 9, 48, 'Andolsun, daha önce onlar fitne aramışlardı. Ve sana karşı birtakım işler çevirmişlerdi. Sonunda onlar, istemedikleri halde hak geldi ve Allah\'ın emri ortaya çıkıp-üstünlük sağladı.'),
(1284, 9, 49, 'Onlardan bir kısmı: "Bana izin ver ve beni fitneye katma" der. Haberin olsun, onlar fitnenin (ta) içine düşmüşlerdir. Hiç şüphesiz cehennem, o inkar edenleri mutlaka çepeçevre kuşatıcıdır.'),
(1285, 9, 50, 'Sana iyilik dokunursa, bu onları fenalaştırır, bir musibet isabet edince ise: "Biz önceden tedbirimizi almıştık" derler ve sevinç içinde dönüp giderler.'),
(1286, 9, 51, 'De ki: "Allah\'ın bizim için yazdıkları dışında, bize kesinlikle hiçbir şey isabet etmez. O bizim Mevlamızdır. Ve mü\'minler yalnızca Allah\'a tevekkül etmelidirler."'),
(1287, 9, 52, 'De ki: "Siz bizim için iki güzellikten (şehidlik veya zaferden) birinin dışında başkasını mı bekliyorsunuz? Oysa biz de, Allah\'ın ya Kendi Katından veya bizim elimizle size bir azap dokunduracağını bekliyoruz. Öyleyse siz bekleyedurun, kuşkusuz biz de sizlerle birlikte bekleyenleriz.'),
(1288, 9, 53, 'De ki: "İsteyerek veya istemeyerek infak edin; sizden kesin olarak kabul edilmeyecektir. Çünkü siz bir fasıklar topluluğu oldunuz."'),
(1289, 9, 54, 'İnfak ettiklerinin kendilerinden kabulünü engelleyen şey, Allah\'ı ve elçisini tanımamaları, namaza ancak isteksizce gelmeleri ve hoşlarına gitmiyorken infak etmeleridir.'),
(1290, 9, 55, 'Şu halde onların malları ve çocukları seni imrendirmesin; Allah bunlarla ancak onları dünya hayatında azaplandırmak ve canlarının inkar içindeyken zorlukla çıkmasını ister.'),
(1291, 9, 56, 'Gerçekten sizden olduklarına dair Allah adına yemin ederler. Oysa onlar sizden değildirler. Ancak onlar ödleri kopan bir topluluktur.'),
(1292, 9, 57, 'Eğer onlar bir sığınak ya da (kalacak) mağaralar veya girebilecekleri bir yer bulsalardı, hızla oraya yönelip koşarlardı.'),
(1293, 9, 58, 'Onlardan sadakalar konusunda seni yadırgayacaklar vardır. Ondan kendilerine verilirse hoşlanırlar, kendilerine verilmediği zaman bu sefer gazablanırlar.'),
(1294, 9, 59, 'Eğer onlar, Allah\'ın ve elçisinin verdiklerine hoşnut olsalardı ve: "Bize Allah yeter; Allah pek yakında bize fazlından verecek, O\'nun elçisi de. Biz gerçekten ancak Allah\'a rağbet edenleriz" deselerdi (ya).'),
(1295, 9, 60, 'Sadakalar, -Allah\'tan bir farz olarak- yalnızca fakirler, düşkünler, (zekat) işinde görevli olanlar, kalpleri ısındırılacaklar, köleler, borçlular, Allah yolunda (olanlar) ve yolda kalmış(lar) içindir. Allah bilendir, hüküm ve hikmet sahibidir.'),
(1296, 9, 61, 'İçlerinden Peygamberi incitenler ve: "O (her sözü dinleyen) bir kulaktır" diyenler vardır. De ki: "O sizin için bir hayır kulağıdır. Allah\'a iman eder, mü\'minlere inanıp-güvenir ve sizden iman edenler için bir rahmettir. Allah\'ın elçisine eziyet edenler... Onlar için acı bir azap vardır."'),
(1297, 9, 62, 'Sizi hoşnut kılmak için Allah\'a yemin ederler; oysa mü\'min iseler, hoşnut kılınmaya Allah ve elçisi daha layıktır.'),
(1298, 9, 63, 'Bilmiyorlar mı, kim Allah\'a ve elçisine karşı koymaya çalışırsa, gerçekten onun için, onda ebedi kalmak üzere cehennem ateşi vardır? İşte en büyük aşağılanma budur.'),
(1299, 9, 64, 'Münafıklar, kalplerinde olanı kendilerine haber verecek bir sûrenin aleyhlerinde indirilmesinden çekiniyorlar. De ki: "Alay edin. Şüphesiz, Allah kaçınmakta olduklarınızı açığa çıkarandır."'),
(1300, 9, 65, 'Onlara sorarsan, andolsun: "Biz dalmış, oyalanıyorduk" derler. De ki: "Allah ile, O\'nun ayetleriyle ve elçisiyle mi alay ediyordunuz?"'),
(1301, 9, 66, 'Özür belirtmeyiniz. Siz, imanınızdan sonra inkara saptınız. Sizden bir topluluğu bağışlasak da, bir topluluğunuzu gerçekten suçlu-günahkar olmaları nedeniyle azaplandıracağız.'),
(1302, 9, 67, 'Münafık erkekler ve münafık kadınlar, bazısı bazısındandır; kötülüğü emrederler, iyilikten alıkoyarlar, ellerini sımsıkı tutarlar. Onlar Allah\'ı unuttular; O da onları unuttu. Şüphesiz, münafıklar fıska sapanlardır.'),
(1303, 9, 68, 'Allah, erkek münafıklara da, kadın münafıklara da ve (bütün) kafirlere, içinde ebedi kalmak üzere cehennem ateşini vadetti. Bu, onlara yeter. Allah onları lanetlemiştir ve onlar için sürekli bir azap vardır.'),
(1304, 9, 69, 'Sizden önceki (münafıklar ve kafirler) gibi. Onlar sizden kuvvet bakımından daha güçlü, mal ve çocuklar bakımından daha çoktular. Onlar kendi paylarıyla yararlanmaya baktılar; siz de, sizden öncekilerin kendi paylarıyla yararlanmaya kalkışmaları gibi, kendi paylarınızla yararlanmaya baktınız ve siz de (dünyaya ve zevke) dalanlar gibi daldınız. İşte onların dünyada ahirette bütün yapıp-ettikleri (amelleri) boşa çıkmıştır ve işte onlar kayba uğrayanlardır.'),
(1305, 9, 70, 'Onlara, kendilerinden öncekilerin; Nuh, Ad, Semud kavminin, İbrahim kavminin, Medyen ahalisinin ve yerle bir olan şehirlerin haberi gelmedi mi? Onlara resulleri apaçık deliller getirmişlerdi. Demek ki Allah, onlara zulmediyor değildi, ama onlar kendi nefislerine zulmediyorlardı.'),
(1306, 9, 71, 'Mü\'min erkekler ve mü\'min kadınlar birbirlerinin velileridirler. İyiliği emreder, kötülükten sakındırırlar, namazı dosdoğru kılarlar, zekatı verirler ve Allah\'a ve Resûlü’ne itaat ederler. İşte Allah\'ın kendilerine rahmet edeceği bunlardır. Şüphesiz, Allah, üstün ve güçlüdür, hüküm ve hikmet sahibidir.'),
(1307, 9, 72, 'Allah, mü\'min erkeklere ve mü\'min kadınlara içinde ebedi kalmak üzere, altından ırmaklar akan cennetler ve Adn cennetlerinde güzel meskenler vadetmiştir. Allah\'tan olan hoşnutluk ise en büyüktür. İşte büyük kurtuluş ve mutluluk budur.'),
(1308, 9, 73, 'Ey Peygamber, kafirlerle ve münafıklarla cihad et ve onlara karşı sert ve caydırıcı davran. Onların barınma yerleri cehennemdir, ne kötü bir yataktır o!..'),
(1309, 9, 74, 'Allah\'a and içiyorlar ki (o inkar sözünü) söylemediler. Oysa andolsun, onlar inkar sözünü söylemişlerdir ve İslamlıklarından sonra inkara sapmışlardır ve erişemedikleri bir şeye yeltenmişlerdir. Oysa intikama kalkışmalarının, kendilerini Allah\'ın ve elçisinin bol ihsanından zengin kılmasından başka (bir nedeni) yoktu. Eğer tevbe ederlerse kendileri için hayırlı olur, eğer yüz çevirirlerse Allah onları dünyada da, ahirette de acı bir azapla azaplandırır. Onlar için yeryüzünde bir koruyucu-dost ve bir yardımcı yoktur.'),
(1310, 9, 75, 'Onlardan kimi de: "Andolsun, eğer bize bol ihsanından verirse gerçekten sadaka vereceğiz ve salihlerden olacağız" diye Allah\'a ahdetmiştir.'),
(1311, 9, 76, 'Onlara Kendi bol ihsanından verince ise, onunla cimrilik yaptılar ve yüz çevirdiler; onlar böyle sırt dönenlerdir.'),
(1312, 9, 77, 'Böylece O da, Allah\'a verdikleri sözü tutmamaları ve yalan söylemeleri nedeniyle, kendisiyle karşılaşacakları güne kadar, kalplerinde nifakı (sonuçta köklü bir duygu olarak) yerleşik kıldı.'),
(1313, 9, 78, 'Onlar bilmiyorlar mı ki, elbette Allah, onların gizli tuttuklarını da, fısıldaştıklarını da biliyor. Gerçekten Allah, gaybın bilgisine sahip olandır.'),
(1314, 9, 79, 'Sadakalar konusunda, mü\'minlerden ek bağışlarda bulunanlarla emeklerinden (cehdlerinden) başkasını bulamayanları yadırgayarak bunlarla alay edenler; Allah (asıl) onları alay konusu kılmıştır ve onlar için acı bir azap vardır.'),
(1315, 9, 80, 'Sen, onlar için ister bağışlanma dile, istersen dileme. Onlar için yetmiş kere bağışlanma dilesen de, Allah onları kesinlikle bağışlamaz. Bu, gerçekten onların Allah\'a ve elçisine (karşı) nankörlük etmeleri dolayısıyladır. Allah fasıklar topluluğuna hidayet vermez.'),
(1316, 9, 81, 'Allah\'ın elçisine muhalif olarak (savaştan) geri kalanlar oturup-kalmalarına sevindiler ve Allah yolunda mallarıyla ve canlarıyla cihad etmeyi çirkin görerek: "Bu sıcakta (savaşa) çıkmayın" dediler. De ki: "Cehennem ateşinin sıcaklığı daha şiddetlidir." Bir kavrayıp-anlasalardı.'),
(1317, 9, 82, 'Öyleyse kazandıklarının cezası olarak az gülsünler, çok ağlasınlar.'),
(1318, 9, 83, 'Bundan böyle, Allah seni onlardan bir topluluğun yanına döndürür de, (yine savaşa) çıkmak için senden izin isterlerse, de ki: "Kesin olarak benimle hiçbir zaman (savaşa) çıkamazsınız ve kesin olarak benimle bir düşmana karşı savaşamazsınız. Çünkü siz oturmayı ilk defa hoş gördünüz; öyleyse geride kalanlarla birlikte oturun."'),
(1319, 9, 84, 'Onlardan ölen birinin namazını hiçbir zaman kılma, mezarı başında durma. Çünkü onlar, Allah\'a ve elçisine (karşı) inkara saptılar ve fasık kimseler olarak öldüler.'),
(1320, 9, 85, 'Onların malları ve evlatları seni imrendirmesin; Allah bunlarla, ancak onları dünyada azaplandırmak ve canlarının onlar inkar içindeyken zorluk içinde çıkmasını istiyor.'),
(1321, 9, 86, '"Allah\'a iman edin, O\'nun elçisi ile cihad etmeye çıkın" diye bir sûre indirildiği zaman onlardan servet sahibi olanlar, senden izin isteyip: "Bizi bırakıver, oturanlarla birlikte olalım" dediler.'),
(1322, 9, 87, '(Savaştan) Geri kalanlarla birlikte olmayı seçtiler. Onların kalpleri mühürlenmiştir. Bundan dolayı kavrayıp-anlamazlar.'),
(1323, 9, 88, 'Ama Resul ve onunla birlikte olan mü\'minler, mallarıyla ve canlarıyla cihad ettiler; işte bütün hayırlar onlarındır ve kurtuluşa erenler onlardır.'),
(1324, 9, 89, 'Allah onlar için, süresiz kalacakları, altından ırmaklar akan cennetler hazırladı. İşte büyük \'kurtuluş ve mutluluk\' budur.'),
(1325, 9, 90, 'Bedevilerden özür belirtenler, kendilerine izin verilmesi için geldiler. Allah\'a ve elçisine yalan söyleyenler de oturup kaldı. Onlardan inkar edenlere pek acı bir azap isabet edecektir.'),
(1326, 9, 91, 'Allah\'a ve elçisine karşı \'içten bağlı kalıp hayra çağıranlar\' oldukları sürece, güçsüz-zayıflara, hastalara ve infak etmek için bir şey bulamayanlara bir sorumluluk (günah) yoktur. İyilik edenlerin aleyhinde de bir yol yoktur. Allah, bağışlayandır, esirgeyendir.'),
(1327, 9, 92, 'Bir de (savaşa katılabilecekleri bir bineğe) bindirmen için sana her gelişlerinde "Sizi bindirecek bir şey bulamıyorum" dediğin ve infak edecek bir şey bulamayıp hüzünlerinden dolayı gözlerinden yaşlar boşana boşana geri dönenler üzerinde de (sorumluluk) yoktur.'),
(1328, 9, 93, 'Yol, ancak o kimseler aleyhinedir ki, zengin oldukları halde (savaşa çıkmamak için) senden izin isterler ve bunlar geride kalanlarla birlikte olmayı seçerler. Allah, onların kalplerini mühürlemiştir. Bundan dolayı onlar, bilmezler.'),
(1329, 9, 94, 'Onlara geri döndüğünüzde size özür belirttiler. De ki: "Özür belirtmeyiniz, size kesin olarak inanmıyoruz. Allah bize, durumunuzu haber vermiştir. Yaptıklarınızı Allah görecektir, O\'nun elçisi de. Sonra gaybı da, müşahede edilebileni de bilene döndürüleceksiniz ve O, yaptıklarınızı size haber verecektir."'),
(1330, 9, 95, 'Onlara geri döndüğünüzde kendilerinden vazgeçmeniz için Allah\'a and içecekler. Artık siz onlara sırt çevirin. Onlar gerçekten pistirler. Kazanmakta olduklarının bir cezası olarak, barınma yerleri cehennemdir.'),
(1331, 9, 96, 'Kendilerinden hoşnut olmanız için size yemin ederler. Siz onlardan hoşnut olsanız bile şüphesiz Allah, fasıklar topluluğundan hoşnut olmaz.'),
(1332, 9, 97, 'Bedeviler inkar ve nifak bakımından daha şiddetlidir. Allah\'ın elçisine indirdiği sınırları bilmemeye de onlar daha \'yatkın ve elverişlidir.\' Allah bilendir, hüküm ve hikmet sahibidir.'),
(1333, 9, 98, 'Bedevilerden öyleleri vardır ki, infak ettiğini bir cereme sayar ve sizi felaketlerin sarıvermesini bekler. Kötü felaket onları sarsın. Allah işitendir, bilendir.'),
(1334, 9, 99, 'Bedevilerden öyleleri de vardır ki, onlar Allah\'a ve ahiret gününe iman eder ve infak ettiğini Allah Katında bir yakınlaşmaya ve elçinin dua ve bağışlama dileklerine (bir yol) sayar. Haberiniz olsun, bu gerçekten onlar için bir yakınlaşmadır. Allah da onları Kendi rahmetine sokacaktır. Şüphesiz Allah, bağışlayandır, esirgeyendir.'),
(1335, 9, 100, 'Öne geçen Muhacirler ve Ensar ile onlara güzellikle uyanlar; Allah onlardan hoşnut olmuştur, onlar da O\'ndan hoşnut olmuşlardır ve (Allah) onlara, içinde ebedi kalacakları, altından ırmaklar akan cennetler hazırlamıştır. İşte büyük \'kurtuluş ve mutluluk\' budur.'),
(1336, 9, 101, 'Çevrenizdeki bedevilerden münafık olanlar vardır ve Medine halkından da nifakı alışkanlığa çevirmiş olanlar vardır. Sen onları bilmezsin, Biz onları biliriz. Biz onları iki kere azaplandıracağız, sonra onlar büyük bir azaba döndürülecekler.'),
(1337, 9, 102, 'Diğerleri günahlarını itiraf ettiler, onlar salih bir ameli bir başka kötüyle karıştırmışlardır. Umulur ki Allah tevbelerini kabul eder. Hiç şüphesiz Allah, bağışlayandır, esirgeyendir.'),
(1338, 9, 103, 'Onların mallarından sadaka al, bununla onları temizlemiş, arındırmış olursun. Onlara dua et. Doğrusu, senin duan, onlar için \'bir sükûnet ve huzurdur.\' Allah işitendir, bilendir.'),
(1339, 9, 104, 'Onlar bilmiyorlar mı ki, gerçekten Allah kullarından tevbeleri kabul edecek ve sadakaları alacak olan O\'dur. Şüphesiz, tevbeleri kabul eden, esirgeyen O\'dur.'),
(1340, 9, 105, 'De ki: "Yapıp-edin. Allah sizin yapıp-ettiklerinizi (amellerinizi) görecektir. O\'nun elçisi ve mü\'minler de. Yakında gaybı ve müşahede edilebileni Bilen\'e döndürüleceksiniz ve O, size yaptıklarınızı haber verecektir."'),
(1341, 9, 106, 'Diğer bir kısmı, Allah\'ın emri için ertelenmişlerdir. O, bunları, ya azaplandıracak veya tevbelerini kabul edecektir. Allah, bilendir, hüküm ve hikmet sahibidir.'),
(1342, 9, 107, 'Zarar vermek, inkarı (pekiştirmek), mü\'minlerin arasını ayırmak ve daha önce Allah\'a ve elçisine karşı savaşanı gözlemek için mescid edinenler ve: "Biz iyilikten başka bir şey istemedik" diye yemin edenler (var ya,) Allah onların şüphesiz yalancı olduklarına şahidlik etmektedir.'),
(1343, 9, 108, 'Sen bunun (böyle bir mescidin) içinde hiçbir zaman durma. Daha ilk gününden takva temeli üzerine kurulan mescid, senin bunda (namaza ve diğer işlere) durmana daha uygundur. Onda, arınmayı içten-arzulayan adamlar vardır. Allah arınanları sever.'),
(1344, 9, 109, 'Binasının temelini, Allah korkusu ve hoşnutluğu üzerine kuran kimse mi hayırlıdır, yoksa binasının temelini göçecek bir yarın kenarına kurup onunla birlikte kendisi de cehennem ateşi içine yuvarlanan kimse mi? Allah, zulmeden bir topluluğa hidayet vermez.'),
(1345, 9, 110, 'Onların kalpleri parçalanmadıkça, kurdukları bina kalplerinde bir şüphe olarak sürüp-gidecektir. Allah bilendir, hüküm ve hikmet sahibidir.'),
(1346, 9, 111, 'Hiç şüphesiz Allah, mü\'minlerden -karşılığında onlara mutlaka cenneti vermek üzere- canlarını ve mallarını satın almıştır. Onlar Allah yolunda savaşırlar, öldürürler ve öldürülürler; (bu,) Tevrat\'ta, İncil\'de ve Kur\'an\'da O\'nun üzerine gerçek olan bir vaaddir. Allah\'tan daha çok ahdine vefa gösterecek olan kimdir? Şu halde yaptığınız bu alış-verişten dolayı sevinip-müjdeleşiniz. İşte \'büyük kurtuluş ve mutluluk\' budur.'),
(1347, 9, 112, 'Tevbe edenler, ibadet edenler, hamd edenler, (İslam uğrunda) seyahat edenler, rükû edenler, secde edenler, iyiliği emredenler, kötülükten sakındıranlar ve Allah\'ın sınırlarını koruyanlar; sen (bütün) mü\'minleri müjdele.'),
(1348, 9, 113, 'Kendilerine onların gerçekten çılgın ateşin arkadaşları oldukları açıklandıktan sonra -yakınları dahi olsa- müşrikler için bağışlanma dilemeleri peygambere ve iman edenlere yaraşmaz.'),
(1349, 9, 114, 'İbrahim\'in babası için bağışlanma dilemesi, yalnızca ona verdiği bir söz dolayısıyla idi. Kendisine, onun gerçekten Allah\'a düşman olduğu açıklanınca ondan uzaklaştı. Doğrusu İbrahim, çok duygulu, yumuşak huyluydu.'),
(1350, 9, 115, 'Bir topluluğa Allah, hidayet verdikten sonra, korkup-sakınacakları şeyleri kendilerine açıklayıncaya kadar, onları sapıklığa sürükleyecek değildir. Şüphesiz Allah, herşeyi bilendir.'),
(1351, 9, 116, 'Gerçek şu ki, göklerin ve yerin mülkü Allah\'ındır; diriltir ve öldürür. Sizin Allah\'tan başka veliniz ve yardımcınız yoktur.'),
(1352, 9, 117, 'Andolsun Allah, Peygamberin, muhacirlerin ve ensarın üzerine tevbe ihsan etti. Ki onlar -içlerinde bir bölümünün kalbi neredeyse kaymak üzereyken- ona güçlük saatinde tabi oldular. Sonra onların tevbelerini kabul etti. Çünkü O, onlara (karşı) çok şefkatlidir, çok esirgeyicidir.'),
(1353, 9, 118, '(Savaştan) Geri bırakılan üç (kişiyi) de (bağışladı). Öyle ki, bütün genişliğine rağmen yeryüzü onlara dar gelmişti, nefisleri de kendilerine dar (sıkıntılı) gelmişti ve O\'nun dışında (yine) Allah\'tan başka bir sığınacak olmadığını iyice anladılar. Sonra tevbe etsinler diye onların tevbesini kabul etti. Şüphesiz Allah, (yalnızca) O, tevbeleri kabul edendir, esirgeyendir.'),
(1354, 9, 119, 'Ey iman edenler, Allah\'tan sakının ve doğru (sadık)larla birlikte olun.'),
(1355, 9, 120, 'Medine halkına ve çevresindeki bedevilere, Allah’ın elçisinden geri kalmaları, kendi nefislerini onun nefsine tercih etmeleri yakışmaz. Bu, gerçekten onların Allah yolunda bir susuzluk, bir yorgunluk, \'dayanılmaz bir açlık\' (çekmeleri), kafirleri \'kin ve öfkeyle ayaklandıracak\' bir yere ayak basmaları ve düşmana karşı bir başarı kazanmaları karşılığında, mutlaka onlara bununla salih bir amel yazılmış olması nedeniyledir. Şüphesiz Allah, iyilik yapanların ecrini kaybetmez.'),
(1356, 9, 121, 'Küçük, büyük infak ettikleri her nafaka ve (Allah yolunda) aştıkları her vadi, mutlaka Allah\'ın yaptıklarının daha güzeliyle onlara karşılığını vermesi için, (bunlar) onlar adına yazılmıştır.'),
(1357, 9, 122, 'Mü\'minlerin tümünün öne fırlayıp çıkmaları gerekmez. Öyleyse onlardan her bir topluluktan bir grup, çıktığında (bir grup da), dinde derin bir kavrayış edinmek (tafakkuhta bulunmak) ve kavimleri kendilerine geri döndüğünde onları uyarmak için (geride kalabilir). Umulur ki onlar da kaçınıp-sakınırlar.'),
(1358, 9, 123, 'Ey iman edenler, inkar edenlerden size en yakın olanlarla savaşın; sizde \'bir güç ve caydırıcılık\' görsünler. Ve bilin ki gerçekten Allah takva sahipleriyle beraberdir.'),
(1359, 9, 124, 'Bir sûre indirildiğinde onlardan bazısı: "Bu, hanginizin imanını arttırdı?" der. Ancak iman edenlere gelince; onların imanını arttırmıştır ve onlar müjdeleşmektedirler.'),
(1360, 9, 125, 'Kalplerinde hastalık olanların ise, iğrençliklerine iğrençlik (murdarlık) ekleyip-arttırmış ve onlar kafir kimseler olarak ölmüşlerdir.'),
(1361, 9, 126, 'Görmüyorlar mı ki, gerçekten onlar her yıl, bir veya iki defa belaya çarptırılıyorlar da sonra tevbe etmiyorlar ve öğüt alıp (ders çıkarıp) düşünmüyorlar.'),
(1362, 9, 127, 'Bir sûre indirildiğinde, bazısı bazısına bakar (ve): "Sizi bir kimse görüyor mu?" (der.) Sonra sırt çevirir giderler. Gerçekten onlar, kavramayan bir topluluk olmaları dolayısıyla, Allah onların kalplerini çevirmiştir.'),
(1363, 9, 128, 'Andolsun size, içinizden sıkıntıya düşmeniz O’nun gücüne giden, size pek düşkün, mü\'minlere şefkatli ve esirgeyici olan bir elçi gelmiştir.'),
(1364, 9, 129, 'Eğer onlar yüz çevirirlerse, de ki: "Bana Allah yeter. O\'ndan başka İlah yoktur. Ben O\'na tevekkül ettim ve büyük arşın Rabbi O\'dur."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Elif, Lam, Ra. Bunlar, hikmetli Kitab’ın ayetleridir.'),
(1366, 10, 2, 'İçlerinden bir adama: "İnsanları uyar ve iman edenlere, muhakkak kendileri için Rableri Katında \'gerçek bir makam\' olduğunu müjde ver" diye vahyetmemiz, insanlara şaşırtıcı mı geldi? İnkar edenler: “Gerçekten bu, açıkça bir büyücüdür" dediler.'),
(1367, 10, 3, 'Şüphesiz sizin Rabbiniz, altı günde gökleri ve yeri yaratan, sonra arşa istiva eden, işleri evirip-çeviren Allah\'tır. O’nun izni olmadıktan sonra, hiç kimse şefaatçi olamaz. İşte Rabbiniz olan Allah budur, öyleyse O\'na kulluk edin. Yine de öğüt alıp düşünmeyecek misiniz?'),
(1368, 10, 4, 'Sizin tümünüzün dönüşü O\'nadır. Allah\'ın va\'di bir gerçektir. İman edip salih amellerde bulunanlara, adaletle karşılık vermek için yaratmayı başlatan, sonra onu iade edecek olan O\'dur. İnkar edenler ise, küfürleri dolayısıyla, onlar için kaynar sudan bir içki ve acı bir azap vardır.'),
(1369, 10, 5, 'Güneş’i bir aydınlık, Ay’ı bir nur kılan ve yılların sayısını ve hesabı bilmeniz için ona duraklar tespit eden O\'dur. Allah, bunları ancak hak ile yaratmıştır. O, bilen bir topluluk için ayetleri böyle birer birer açıklamaktadır.'),
(1370, 10, 6, 'Gerçekten, gece ile gündüzün art arda gelişinde ve Allah\'ın göklerde ve yerde yarattığı şeylerde korkup-sakınan bir topluluk için elbette ayetler vardır.'),
(1371, 10, 7, 'Bizimle karşılaşmayı ummayanlar, dünya hayatına razı olanlar ve bununla tatmin olanlar ve Bizim ayetlerimizden habersiz olanlar;'),
(1372, 10, 8, 'İşte bunların, kazandıkları dolayısıyla barınma yerleri ateştir.'),
(1373, 10, 9, 'İman edenler ve salih amellerde bulunanlar da, Rableri onları imanları dolayısıyla altından ırmaklar akan, nimetlerle donatılmış cennetlere yöneltip-iletir (hidayet eder).'),
(1374, 10, 10, 'Oradaki duaları: "Allah\'ım, Sen ne Yücesin"dir ve oradaki dirlik temennileri: "Selam"dır; dualarının sonu da: "Gerçekten, hamd alemlerin Rabbi olan Allah\'ındır."'),
(1375, 10, 11, 'Eğer Allah, onların hayra ulaşmak için çarçabuk davrandıkları gibi, insanlara şerri de çabuklaştırsaydı, mutlaka ecellerine hüküm verilirdi. İşte Bize kavuşmayı ummayanları Biz böylece taşkınlıkları içinde şaşkınca dolaşır bir durumda bırakırız.'),
(1376, 10, 12, 'İnsana bir zarar dokunduğunda, yan yatarken, otururken ya da ayaktayken Bize dua eder; zararını üstünden kaldırdığımız zaman ise, sanki kendisine dokunan zarara Bizi hiç çağırmamış gibi döner-gider. İşte, ölçüyü taşıranlara yapmakta oldukları böyle süslenmiştir.'),
(1377, 10, 13, 'Andolsun, sizden önceki nesilleri, resulleri kendilerine apaçık deliller getirdiği halde, zulmettikleri ve iman etmeyecek oldukları için yıkıma uğrattık. İşte Biz, suçlu-günahkar olan bir topluluğu böyle cezalandırırız.'),
(1378, 10, 14, 'Sonra, nasıl yapıp-davranacaksınız diye gözlemek için, onların ardından sizi yeryüzünde halifeler kıldık.'),
(1379, 10, 15, 'Onlara ayetlerimiz apaçık belgeler olarak okunduğunda, Bizimle karşılaşmayı ummayanlar, derler ki: "Bundan başka bir Kur\'an getir veya onu değiştir." De ki: "Benim onu kendi nefsimin bir öngörmesi olarak değiştirmem benim için olacak şey değildir. Ben, yalnızca bana vahyolunana uyarım. Eğer Rabbime isyan edersem, gerçekten ben, büyük günün azabından korkarım."'),
(1380, 10, 16, 'De ki: "Eğer Allah dileseydi, onu size okumazdım ve onu size bildirmezdi. Ben ondan önce sizin içinizde bir ömür sürdüm. Siz yine de akıl erdirmeyecek misiniz?"'),
(1381, 10, 17, 'Allah\'a karşı yalan uydurup iftira düzenden ve O\'nun ayetlerini yalanlayandan daha zalim kimdir? Şüphesiz O, suçlu-günahkarları kurtuluşa erdirmez.'),
(1382, 10, 18, 'Allah\'ı bırakıp kendilerine zarar vermeyecek ve yararları dokunmayacak şeylere kulluk ederler ve: "Bunlar Allah Katında bizim şefaatçilerimizdir" derler. De ki: "Siz, Allah\'a, göklerde ve yerde bilmediği bir şey mi haber veriyorsunuz? O, sizin şirk koştuklarınızdan uzak ve Yücedir."'),
(1383, 10, 19, 'İnsanlar, tek bir ümmetten başka değildi; sonra anlaşmazlığa düştüler. Eğer Rabbinden geçmiş (verilmiş) bir söz olmasaydı, anlaşmazlığa düştükleri şey konusunda mutlaka aralarında hüküm verilmiş olurdu.'),
(1384, 10, 20, 'Bir de derler ki: "Rabbinden üzerine bir ayet (mucize) indirilse ya!.." De ki: "Gayb yalnızca Allah\'ındır, siz bekleyedurun; ben de sizlerle birlikte bekleyenlerdenim."'),
(1385, 10, 21, 'İnsanlara, şiddetli bir sıkıntı dokunduktan sonra, bir rahmet dokundurduğumuz zaman, ayetlerimiz konusunda hileli bir düzen kurmak (bir entrika çevirmek) onlar için (bir alışkanlık ve kötü bir edinim)dir. De ki: "Düzen kurmada (karşılık vermede) Allah daha hızlıdır. Şüphesiz, Bizim elçilerimiz, sizin \'geliştirmekte olduğunuz düzenleri\' yazmaktadırlar."'),
(1386, 10, 22, 'Karada ve denizde sizi gezdiren O\'dur. Öyle ki siz gemide bulunduğunuz zaman, onlar da güzel bir rüzgarla onu yüzdürürlerken ve (tam) bununla sevinmektelerken, ona çılgınca bir rüzgar gelip çatar ve her yandan dalgalar onları kuşatıverir; onlar artık bu (dalgalarla) gerçekten kuşatıldıklarını sanmışlarken, dinde O\'na \'gönülden katıksız bağlılar (muhlisler)\' olarak Allah\'a dua etmeye başlarlar: "Andolsun eğer bundan bizi kurtaracak olursan, muhakkak Sana şükredenlerden olacağız."'),
(1387, 10, 23, 'Ama (Allah) onları kurtarınca, hemen haksız yere, yeryüzünde taşkınlığa koyulurlar. Ey insanlar, sizin taşkınlığınız, ancak kendi aleyhinizedir; (bu) dünya hayatının geçici metaıdır. Sonra dönüşünüz Bizedir, Biz de yaptıklarınızı size haber vereceğiz.'),
(1388, 10, 24, 'Dünya hayatının örneği, ancak gökten indirdiğimiz, onunla insanların ve hayvanların yediği yeryüzünün bitkisi karışmış olan bir su gibidir. Öyle ki yer, güzelliğini takınıp süslendiği ve ahalisi gerçekten ona güç yetirdiklerini sanmışlarken (işte tam bu sırada) gece veya gündüz ona emrimiz gelmiştir de, dün sanki hiçbir zenginliği yokmuş gibi, onu kökünden biçilip atılmış bir durumda kılmışız. Düşünen bir topluluk için Biz ayetleri böyle birer birer açıklarız.'),
(1389, 10, 25, 'Allah barış yurduna çağırır ve kimi dilerse dosdoğru yola yöneltip-iletir.'),
(1390, 10, 26, 'Güzellik yapanlara daha güzeli ve fazlası vardır. Onların yüzlerini ne bir karartı sarar, ne bir zillet, işte onlar cennetin halkıdırlar; orada süresiz kalacaklardır.'),
(1391, 10, 27, 'Kötülükler kazanmış olanlar ise; her bir kötülüğün karşılığı, kendi misliyledir. Bunları bir zillet sarıp kaplar. Onları Allah\'tan (kurtaracak) hiçbir koruyucu yok. Onların yüzleri, sanki bir karanlık gecenin parçalarına bürünmüş gibidir. İşte bunlar ateşin halkıdırlar; orada süresiz kalacaklardır.'),
(1392, 10, 28, 'O gün, onların tümünü birarada toplayacağız, sonra şirk katanlara: "Yerinizden ayrılmayınız; siz de, şirk koştuklarınız da" diyeceğiz. Artık onların arasını açmışızdır. Şirk koştukları derler ki: "Siz bize ibadet ediyor değildiniz."'),
(1393, 10, 29, '"Bizim ile sizin aranızda şahid olarak Allah yeter. Gerçekten biz, sizin ibadetinizden habersizdik."'),
(1394, 10, 30, 'İşte orada, her nefis önceden yaptıklarıyla imtihana çekilmiş olacak ve onlar asıl-gerçek mevlaları olan Allah\'a döndürülecekler. Yalan yere uydurdukları da, kendilerinden kaybolup uzaklaşacaklar.'),
(1395, 10, 31, 'De ki: "Göklerden ve yerden sizlere rızık veren kimdir? Kulaklara ve gözlere malik olan kimdir? Diriyi ölüden çıkaran ve ölüyü diriden çıkaran kimdir? Ve işleri evirip-çeviren kimdir? Onlar: "Allah" diyeceklerdir. Öyleyse de ki: "Peki siz yine de korkup-sakınmayacak mısınız?'),
(1396, 10, 32, 'İşte bu, sizin gerçek Rabbiniz olan Allah\'tır. Öyleyse haktan sonra sapıklıktan başka ne var? Peki, nasıl hala çevriliyorsunuz?'),
(1397, 10, 33, 'Böylece Rabbinin sözü o fasık kimseler üzerinde (şöyle) gerçekleşmiştir ki: "Onlar şüphesiz iman etmezler."'),
(1398, 10, 34, 'De ki: "Sizin şirk koştuklarınızdan ilk kez yaratacak, sonra onu iade edecek olan var mı?" De ki: "Allah yaratmayı (ilkin) başlatır, sonra onu iade eder. Öyleyse nasıl çevriliyorsunuz?"'),
(1399, 10, 35, 'De ki: "Sizin şirk koştuklarınızdan hakka ulaştırabilecek var mı?" De ki: "Hakka ulaştıracak Allah\'tır. Öyleyse, hakka ulaştıran mı uyulmaya daha hak sahibidir, yoksa doğru yola ulaştırılmadıkça kendisi hidayete ulaşmayan mı? Ne oluyor size? Nasıl hükmediyorsunuz?"'),
(1400, 10, 36, 'Onların çoğunluğu zandan başkasına uymaz. Gerçekten zan ise, haktan hiçbir şeyi sağlayamaz. Şüphesiz Allah, onların işlemekte olduklarını bilendir.'),
(1401, 10, 37, 'Bu Kur\'an, Allah\'tan başkası tarafından yalan olarak uydurulmuş değildir. Ancak bu, önündekileri doğrulayan ve kitabı ayrıntılı olarak açıklayandır. Bunda hiç şüphe yoktur, alemlerin Rabbindendir.'),
(1402, 10, 38, 'Yoksa: "Bunu kendisi yalan olarak uydurdu" mu diyorlar? De ki: "Bunun benzeri olan bir sûre getirin ve eğer gerçekten doğru sözlüyseniz Allah\'tan başka çağırabildiklerinizi çağırın."'),
(1403, 10, 39, 'Hayır, onlar ilmini kuşatamadıkları ve kendilerine henüz yorumu gelmemiş bir şeyi yalanladılar. Onlardan öncekiler de böyle yalanlamışlardı. Zulmedenlerin nasıl bir sonuca uğradıklarına bir bak.'),
(1404, 10, 40, 'Onlardan ona inananlar var ve ona inanmayanlar da vardır. Rabbin bozgunculuk çıkaranları daha iyi bilir.'),
(1405, 10, 41, 'Eğer seni yalanlarlarsa, onlara de ki: "Benim yaptıklarım benim, sizin yaptıklarınız sizindir. Siz benim yaptıklarımdan uzaksınız ve ben de sizin yaptıklarınızdan uzağım."'),
(1406, 10, 42, 'Onlardan seni dinleyecekler vardır. Ama hiç duymayan -sağırlara -üstelik hiç akılları ermiyorsa- sen mi duyuracaksın?'),
(1407, 10, 43, 'Ve sana bakacak olanlar vardır. Ama kör olanları -üstelik basiretleri de yoksa- sen mi doğru yola ulaştıracaksın?'),
(1408, 10, 44, 'Şüphesiz Allah, insanlara hiçbir şeyle zulmetmez. Ancak insanlar, kendi nefislerine zulmediyorlar.'),
(1409, 10, 45, 'Gündüzün bir saatinden başka sanki hiç ömür sürmemişler gibi onları birarada toplayacağı gün, onlar birbirlerini tanımış olacaklar. Allah\'a kavuşmayı yalanlayanlar gerçekten hüsrana uğramışlardır. Onlar hidayete ermiş (kimseler) değildi.'),
(1410, 10, 46, 'Onlara vaadettiğimiz (azabın) bir kısmını sana gösteririz veya senin hayatına son veririz (de görmen ahirete kalır.) Onların dönüşleri Bizedir, sonra Allah işlediklerine şahiddir.'),
(1411, 10, 47, 'Her ümmetin bir resulü vardır. Onlara resulleri geldiği zaman, aralarında adaletle hüküm verilir ve onlar zulme uğratılmazlar.'),
(1412, 10, 48, 'Derler ki: "Eğer doğru sözlüyseniz, bu belirttiğiniz süre (va\'d) ne zamanmış?"'),
(1413, 10, 49, 'De ki: "Allah\'ın dilemesi dışında, kendim için zarardan ve yarardan (hiçbir şeye) malik değilim. Her ümmetin bir eceli vardır. Onların ecelleri gelince, artık ne bir saat ertelenebilirler, ne öne alınabilirler.'),
(1414, 10, 50, 'De ki: "Düşündünüz mü hiç, eğer O\'nun azabı size gece veya gündüz geliverirse, suçlu-günahkarlar, bunu ne diye erkene almak istiyorlar?"'),
(1415, 10, 51, 'Gerçekleştikten sonra mı O\'na iman edeceksiniz? Hemen şimdi mi? Oysa siz, onun (azabın) erkence gelmesini istiyordunuz.'),
(1416, 10, 52, 'Sonra o zulmetmekte olanlara: "Sürekli azabı tadın" denilecek. Kazandıklarınız dışında, bir başka şeyle mi cezalandırılacaktınız?"'),
(1417, 10, 53, '"Bu bir gerçek mi?" diye senden haber soracaklar. De ki: "Evet, Rabbime andolsun ki, şüphesiz gerçektir ve sizler aciz bırakacak değilsiniz."'),
(1418, 10, 54, 'Zulmeden her nefis, yeryüzündekilerin tümüne sahip olsa bunu (azaba karşılık) mutlaka fidye olarak verirdi. Onlar azabı görünce pişmanlıklarını gizlerler, oysa onlar haksızlığa uğratılmadan aralarında adaletle hükmedilmiştir.'),
(1419, 10, 55, 'Haberin olsun, göktekilerin ve yerdekilerin tümü gerçekten Allah\'ındır. Haberin olsun; şüphesiz Allah\'ın va\'di haktır; ancak onların çoğu bilmezler.'),
(1420, 10, 56, 'O, diriltir ve öldürür. Ve O\'na döndürüleceksiniz.'),
(1421, 10, 57, 'Ey insanlar, Rabbinizden size bir öğüt, sinelerde olana bir şifa ve mü\'minler için bir hidayet ve rahmet geldi.'),
(1422, 10, 58, 'De ki: "Allah\'ın bol ihsanıyla (fazlıyla) ve rahmetiyle, yalnız bunlarla sevinsinler. Bu, onların toplayıp yığmakta olduklarından hayırlıdır."'),
(1423, 10, 59, 'De ki: "Allah\'ın sizin için indirdiği sizin bir kısmını haram ve helal kıldığınız rızıktan, haber var mı? Söyler misiniz?" De ki: "Allah mı size izin verdi, yoksa Allah hakkında yalan uydurup iftira mı ediyorsunuz?"'),
(1424, 10, 60, 'Allah hakkında yalan uydurup iftira edenlerin kıyamet günü zanları nedir? Şüphesiz Allah, insanlara karşı büyük ihsan (Fazl) sahibidir, ancak onların çoğu şükretmezler.'),
(1425, 10, 61, 'Senin içinde olduğun herhangi bir durum, onun hakkında Kur\'an\'dan okuduğun herhangi bir şey ve sizin işlediğiniz herhangi bir iş yoktur ki, ona (iyice) daldığınızda, Biz sizin üzerinizde şahidler durmuş olmayalım. Yerde ve gökte zerre ağırlığınca hiçbir şey Rabbinden uzakta (saklı) kalmaz. Bunun daha küçüğü de, daha büyüğü de yoktur ki, apaçık bir kitapta (kayıtlı) olmasın.'),
(1426, 10, 62, 'Haberiniz olsun; Allah\'ın velileri, onlar için korku yoktur, mahzun da olmayacaklardır.'),
(1427, 10, 63, 'Onlar iman edenler ve (Allah\'tan) sakınanlardır.'),
(1428, 10, 64, 'Müjde, dünya hayatında ve ahirette onlarındır. Allah\'ın sözleri için değişiklik yoktur. İşte büyük \'kurtuluş ve mutluluk\' budur.'),
(1429, 10, 65, 'Onların sözleri seni üzmesin. Şüphesiz \'izzet ve gücün\' tümü Allah\'ındır. O, işitendir, bilendir.'),
(1430, 10, 66, 'Haberiniz olsun; şüphesiz göklerde kim var, yerde kim var tümü Allah\'ındır. Allah\'tan başkasına tapanlar bile, şirk koştukları varlıklara ve güçlere (gerçekte) uymazlar. Onlar yalnızca bir zanna uyarlar ve onlar ancak \'zan ve tahminde bulunarak yalan söylemektedirler.\''),
(1431, 10, 67, 'O, dinlenmeniz için geceyi, gündüzü de aydınlatıcı (mubsir) olarak sizin için yaratmıştır. Şüphesiz işitebilen bir topluluk için bunda gerçekten ayetler vardır.'),
(1432, 10, 68, '"Allah çocuk edindi" dediler. O, (bundan) Yücedir; O, hiçbir şeye ihtiyacı olmayandır. Göklerde ve yerde ne varsa O\'nundur. Kendinizde buna ilişkin bir delil de yoktur. Allah\'a karşı bilmeyeceğiniz bir şeyi mi söylüyorsunuz?'),
(1433, 10, 69, 'De ki: "Allah hakkında yalan uydurup iftira edenler, kurtuluşa ermezler."'),
(1434, 10, 70, '(Onlar için) Dünyada geçici bir meta (vardır). Sonra dönüşleri Bizedir; sonra da inkara sapışları dolayısıyla onlara şiddetli azabı taddıracağız.'),
(1435, 10, 71, 'Onlara Nuh\'un haberini oku. Hani kavmine demişti ki: "Ey kavmim, benim makamım ve Allah\'ın ayetleriyle hatırlatmalarım eğer size ağır geliyorsa ben, şüphesiz Allah\'a tevekkül etmişim. Artık siz ortaklarınızla toplanıp yapacağınız işi karara bağlayın da işiniz size örtülü kalmasın (veya tasa konusu olmasın), sonra hakkımdaki hükmünüzü -bana süre tanımaksızın- verin.'),
(1436, 10, 72, 'Eğer yüz çevirecek olursanız, ben sizden bir karşılık istemedim. Benim ecrim, yalnızca Allah\'a aittir. Ve ben, Müslümanlardan olmakla emrolundum.'),
(1437, 10, 73, 'Fakat onu yalanladılar; Biz de onu ve gemide onunla birlikte olanları kurtardık ve onları halifeler kıldık. Ayetlerimizi yalanlayanları da suda boğduk. Uyarılanların nasıl bir sonuca uğratıldıklarına bir bak.'),
(1438, 10, 74, 'Sonra onun ardından kendi kavimlerine (başka) elçiler gönderdik; onlara apaçık belgeler getirmişlerdi. Ama daha önce onu yalanlamaları nedeniyle inanmadılar. İşte Biz, haddi aşanların kalplerini böyle mühürleriz.'),
(1439, 10, 75, 'Sonra bunların ardından Firavun\'a ve onun önde gelen çevresine Musa\'yı ve Harun\'u ayetlerimizle gönderdik. Fakat onlar büyüklendiler. Onlar suçlu-günahkar bir kavimdi.'),
(1440, 10, 76, 'Onlara Katımız\'dan hak geldiği zaman, dediler ki: "Bu, kuşkusuz apaçık bir büyüdür."'),
(1441, 10, 77, 'Musa: "Size hak geldiğinde (böyle) mi söylersiniz? Bu bir büyü müdür? Oysa büyücüler, kurtuluşa ermezler" dedi.'),
(1442, 10, 78, 'Onlar: "Siz ikiniz, bizi atalarımızı üzerinde bulduğumuz (yol)dan çevirmek ve yeryüzünde büyüklük sizin olsun diye mi bize geldiniz? Biz, sizin ikinize inanacak değiliz" dediler.'),
(1443, 10, 79, 'Firavun: "Bana bütün bilgin büyücüleri getirin" dedi.'),
(1444, 10, 80, 'Büyücüler geldiğinde Musa: "Atacağınız şeyleri atın” dedi.'),
(1445, 10, 81, 'Onlar atınca, Musa dedi ki: "Sizlerin (ortaya) getirdiğiniz büyüdür. Doğrusu Allah onu geçersiz kılacaktır. Şüphesiz Allah, bozgunculuk çıkaranların işini düzeltmez."'),
(1446, 10, 82, 'Allah, suçlu-günahkarlar istemese de, hakkı (hak olarak) Kendi kelimeleriyle gerçekleştirecektir.'),
(1447, 10, 83, 'Sonunda Musa\'ya kendi kavminin bir zürriyetinden (gençlerinden) başka -Firavun ve önde gelen çevresinin kendilerini belalara çarptırmaları korkusuyla- iman eden olmadı. Çünkü Firavun, gerçekten yeryüzünde büyüklenen bir zorba ve gerçekten ölçüyü taşıranlardandı.'),
(1448, 10, 84, 'Musa dedi ki: "Ey kavmim, eğer siz Allah\'a iman edip Müslüman olmuşsanız artık yalnızca O\'na tevekkül edin."'),
(1449, 10, 85, 'Dediler ki: "Biz Allah\'a tevekkül ettik; Rabbimiz, bizi zulmeden bir kavim için bir fitne (konusu) kılma."'),
(1450, 10, 86, '"Ve bizi, kafirler topluluğundan rahmetinle kurtar."'),
(1451, 10, 87, 'Musa ve kardeşine (şöyle) vahyettik: "Mısır\'da kavminiz için evler hazırlayın, evlerinizi namaz kılınan (ve kıbleye dönük) yerler yapın ve namazı dosdoğru kılın. Mü\'minleri de müjdele."'),
(1452, 10, 88, 'Musa dedi ki: "Rabbimiz, şüphesiz Sen, Firavun\'a ve önde gelen çevresine dünya hayatında bir çekicilik (güç, ihtişam) ve mallar verdin. Rabbimiz, Senin yolundan saptırmaları için (mi?) Rabbimiz, mallarını yerin dibine geçir ve onların kalplerinin üzerini şiddetle bağla; onlar acı azabı görecekleri zamana kadar iman etmeyecekler."'),
(1453, 10, 89, '(Allah) Dedi ki: "İkinizin duası kabul olundu. Öyleyse dosdoğru yolda devam edin ve bilgisizlerin yoluna uymayın."'),
(1454, 10, 90, 'Biz, İsrailoğulları\'nı denizden geçirdik; Firavun ve askerleri azgınlıkla ve düşmanlıkla peşlerine düştü. Sular onu boğacak düzeye erişince (Firavun): "İsrailoğulları\'nın kendisine inandığı (İlah\'tan) başka İlah olmadığına inandım ve ben de Müslümanlardanım" dedi.'),
(1455, 10, 91, 'Şimdi, öyle mi? Oysa sen önceleri isyan etmiştin ve bozgunculuk çıkaranlardandın.'),
(1456, 10, 92, 'Bugün ise, senden sonrakilere bir ayet (tarihi bir belge, ibret) olman için seni yalnızca bedeninle kurtaracağız (herkese cesedini göstereceğiz). Gerçekten insanlardan çoğu, Bizim ayetlerimizden habersizdirler.'),
(1457, 10, 93, 'Andolsun, Biz İsrailoğulları’nı, hoşlarına gidecek güzel bir yerde yerleştirdik ve temiz şeylerden kendilerine rızık verdik. Kendilerine ilim gelinceye kadar anlaşmazlığa düşmediler. Şüphesiz Rabbin, aralarında anlaşmazlığa düştükleri şey konusunda kıyamet günü hüküm verecektir.'),
(1458, 10, 94, 'Sana indirdiğimizden eğer kuşkudaysan, senden önce kitabı okuyanlara sor. Andolsun, Rabbinden sana gerçek gelmiştir, şu halde kuşkuya kapılanlardan olma.'),
(1459, 10, 95, 'Ve Allah\'ın ayetlerini yalanlayanlardan olma; yoksa hüsrana uğrayanlardan olursun.'),
(1460, 10, 96, 'Gerçek şu ki, Rabbinin kelimesi üzerlerinde hak olanlar, onlar inanmazlar.'),
(1461, 10, 97, 'Onlara her ayet getirilse bile. Acı azabı görünceye kadar.'),
(1462, 10, 98, 'Ama (azap geldiği sırada) iman edip imanı kendisine yarar sağlamış -Yunus kavminin dışında- bir ülke olsaydı ya! Onlar iman ettikleri zaman dünya hayatında onlardan aşağılatıcı azabı kaldırdık ve onları belli bir zamana kadar yararlandırdık.'),
(1463, 10, 99, 'Eğer Rabbin dileseydi, yeryüzündekilerin tümü, topluca iman ederdi. Öyleyse, onlar mü\'min oluncaya kadar insanları sen mi zorlayacaksın?'),
(1464, 10, 100, 'Allah\'ın izni olmaksızın, hiç kimse için iman etme (imkanı) yoktur. O, akıl erdiremeyenlerin üzerine iğrenç bir pislik kılar.'),
(1465, 10, 101, 'De ki: "Göklerde ve yerde ne var? Bir bakıverin." İman etmeyen bir topluluğa apaçık ayetler ve uyarmalar bir şey sağlamaz.'),
(1466, 10, 102, 'Kendilerinden önce gelip geçmişlerin (başlarından geçen) günlerin bir benzerinden başkasını mı bekliyorlar? De ki: "Bekleyedurun. Şüphesiz ben de sizlerle birlikte bekleyenlerdenim."'),
(1467, 10, 103, 'Sonra Biz, elçilerimizi ve iman edenleri böyle kurtarırız; mü\'minleri kurtarmamız Bizim üzerimize bir haktır.'),
(1468, 10, 104, 'De ki: "Ey insanlar, eğer benim dinimden yana bir kuşku içindeyseniz, ben, sizin Allah\'tan başka ibadet ettiklerinize ibadet etmiyorum, ancak ben, sizin hayatınıza son verecek olan Allah\'a ibadet ederim. Ben, mü\'minlerden olmakla emrolundum."'),
(1469, 10, 105, 'Ve: "Bir muvahhid (hanif) olarak yüzünü dine doğru yönelt ve sakın müşriklerden olma,"'),
(1470, 10, 106, '"Allah\'tan başka, sana yararı da, zararı da olmayan(ilahlar)a tapma. Eğer sen (bunun aksini) yapacak olursan, bu durumda gerçekten zulmedenlerden olursun" (diye emrolundum.)'),
(1471, 10, 107, 'Allah sana bir zarar dokunduracak olsa, O\'ndan başka bunu senden kaldıracak yoktur. Ve eğer sana bir hayır isterse, O\'nun bol fazlını geri çevirecek de yoktur. Kullarından dilediğine bundan isabet ettirir. O, bağışlayandır, esirgeyendir.'),
(1472, 10, 108, 'De ki: "Ey insanlar, şüphesiz size Rabbinizden hak gelmiştir. Kim hidayet bulursa, o ancak kendi nefsi için hidayet bulmuştur. Kim saparsa, o da, kendi aleyhine sapmıştır. Ben sizin üzerinizde bir vekil değilim."'),
(1473, 10, 109, 'Sana vahyolunana uy ve Allah hükmünü verinceye kadar sabret. O, hükmedenlerin en hayırlısıdır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'Elif, Lam, Ra. (Bu,) Ayetleri muhkem kılınmış, sonra hüküm ve hikmet sahibi ve herşeyden haberdar olan (Allah) tarafından birer birer (bölüm bölüm) açıklanmış bir Kitap\'tır (ki:)'),
(1475, 11, 2, 'Öyle ki, Allah\'tan başkasına ibadet etmeyin. Gerçekten Ben, sizi O’nun tarafından uyaran ve müjdeleyenim;'),
(1476, 11, 3, 'Ve Rabbinizden bağışlanma dileyin; sonra O\'na tevbe edin. O da sizi, adı konulmuş bir vakte kadar güzel bir meta (fayda) ile metalandırsın ve her ihsan sahibine kendi ihsanını versin. Eğer yüz çevirirseniz gerçekten Ben, sizin için büyük bir günün azabından korkarım.'),
(1477, 11, 4, 'Sizin dönüşünüz Allah\'adır. O, herşeye güç yetirendir.'),
(1478, 11, 5, 'Haberiniz olsun; gerçekten onlar, ondan gizlenmek için göğüslerini büker (Hak\'tan kaçınıp yan çizer)ler. (Yine) Haberiniz olsun; onlar, örtülerine büründükleri zaman, O, gizli tuttuklarını da, açığa vurduklarını da bilir. Çünkü O, sinelerin özünde saklı duranı bilendir.'),
(1479, 11, 6, 'Yeryüzünde hiçbir canlı yoktur ki, rızkı Allah\'a ait olmasın. Onun karar (yerleşik) yerini de ve geçici bulunduğu yeri de bilir. (Bunların) Tümü apaçık bir kitapta (yazılı)dır.'),
(1480, 11, 7, 'O\'nun arşı su üzerinde iken amel bakımından hanginizin daha iyi olduğunu denemek için gökleri ve yeri altı günde yaratan O\'dur. Andolsun onlara: "Gerçekten siz, ölümden sonra yine diriltileceksiniz" dersen, inkar edenler mutlaka: "Bu, açıkça bir büyüden başkası değildir" derler.'),
(1481, 11, 8, 'Andolsun, onlardan azabı sayılı bir topluluğa (veya belirli bir süreye) kadar ertelesek, mutlaka: "Onu alıkoyan nedir?" derler. Haberiniz olsun; onlara bunun geleceği gün, onlardan geri çevrilecek değildir ve alaya almakta oldukları şey de kendilerini çepeçevre kuşatacaktır.'),
(1482, 11, 9, 'Andolsun, Biz insana tarafımızdan bir rahmet tattırıp sonra bunu kendisinden çekip-alsak, kuşkusuz o, (artık) umudunu kesmiş bir nankördür.'),
(1483, 11, 10, 'Ve andolsun, kendisine dokunan bir sıkıntıdan sonra, ona bir nimet taddırsak, kuşkusuz; "Kötülükler benden gidiverdi" der. Çünkü o, şımarıktır, böbürlenendir.'),
(1484, 11, 11, 'Sabredenler ve salih amellerde bulunanlar başka. İşte, bağışlanma ve büyük ecir bunlarındır.'),
(1485, 11, 12, 'Şimdi onların: "Ona bir hazine indirilmeli veya onunla birlikte bir melek gelmeli değil miydi?" demeleri dolayısıyla göğsün daralıp sana vahyolunanlardan bir kısmını terk mi edeceksin? Sen yalnızca bir uyarıcısın. Allah herşeye vekildir.'),
(1486, 11, 13, 'Yoksa: "Onu kendisi uydurdu" mu diyorlar? De ki: "Haydi siz, yalan üzere uydurulmuş olarak onun benzeri on sûre getirin ve eğer doğru sözlüyseniz, Allah\'tan başka çağırabildiklerinizi çağırın."'),
(1487, 11, 14, 'Eğer buna rağmen size cevab vermezlerse, artık biliniz ki, o, gerçekten Allah\'ın ilmiyle indirilmiştir ve O\'ndan başka İlah yoktur. Öyleyse artık, siz Müslüman mısınız?'),
(1488, 11, 15, 'Kim dünya hayatını ve onun çekiciliğini isterse, onlara yapıp ettiklerini onda tastamam öderiz ve onlar bunda hiçbir eksikliğe uğratılmazlar.'),
(1489, 11, 16, 'İşte bunların, ahirette kendileri için ateşten başkası yoktur. Onların onda (dünyada) bütün işledikleri boşa çıkmıştır ve yapmakta oldukları şeyler de geçersiz olmuştur.'),
(1490, 11, 17, 'Rabbinden apaçık bir delil üzerinde bulunan, onu yine ondan bir şahid izleyen ve ondan önce bir önder ve rahmet olarak Musa\'nın kitabı (kendisini doğrulamakta) bulunan kimse, (artık onlar) gibi midir? İşte onlar, buna (Kur\'an\'a) inanırlar. Gruplardan biri onu inkar ederse, ateş ona vaadedilen yerdir. Öyleyse, bundan kuşkuda olma, çünkü o, Rabbinden olan bir haktır. Ancak insanların çoğunluğu inanmazlar.'),
(1491, 11, 18, 'Allah’a karşı yalan uydurup iftira düzenden daha zalim kimdir? İşte bunlar, Rablerine sunulacaklar ve şahidler: "Rablerine karşı yalan söyleyenler bunlardır" diyecekler. Haberiniz olsun; Allah\'ın laneti zalimlerin üzerinedir.'),
(1492, 11, 19, 'Bunlar Allah\'ın yolundan engelleyenler ve onda çarpıklık arayanlardır. Onlar, ahireti tanımayanlardır.'),
(1493, 11, 20, 'Bunlar, yeryüzünde (Allah\'ı) aciz bırakacak değildir ve bunların Allah\'tan başka velileri yoktur. Azap onlar için kat kat artırılır. Bunlar (hakkı) işitmeye güç yetirmezlerdi ve görmezlerdi de.'),
(1494, 11, 21, 'İşte bunlar, kendilerini hüsrana uğratanlardır ve yalan olarak uydurdukları (düzme tanrılar da) onlardan uzaklaşıp-kaybolmuşlardır.'),
(1495, 11, 22, 'Hiç şüphesiz bunlar, ahirette en çok hüsrana uğrayanlardır.'),
(1496, 11, 23, 'İman edip salih amellerde bulunanlar ve \'Rablerine kalpleri tatmin bulmuş olarak bağlananlar\', işte bunlar da cennetin halkıdırlar. Onda süresiz kalacaklardır.'),
(1497, 11, 24, 'Bu iki grubun örneği; kör ve sağır ile gören ve işiten gibidir. Örnekçe bunlar eşit olur mu? Yine de öğüt alıp-düşünmeyecek misiniz?'),
(1498, 11, 25, 'Andolsun, Biz Nuh\'u kavmine gönderdik. (Onlara:) "Ben sizin için ancak apaçık bir uyarıp-korkutucuyum."'),
(1499, 11, 26, '"Allah\'tan başkasına kulluk etmeyin. Ben size (gelecek olan) acı bir günün azabından korkarım" (dedi).'),
(1500, 11, 27, 'Kavminden, ileri gelen inkarcılar: "Biz seni yalnızca bizim gibi bir beşerden başkası görmüyoruz; sana, sığ görüşlü olan en aşağılıklarımızdan başkasının uyduğunu görmüyoruz ve sizin bize bir üstünlüğünüzü de görmüyoruz. Aksine, biz sizi yalancılar sanıyoruz" dedi.'),
(1501, 11, 28, 'Dedi ki: "Ey kavmim, görüşünüz nedir söyleyin? Eğer ben Rabbimden apaçık bir belge üzerinde isem ve Rabbim bana Kendi Katından bir rahmet vermiş de (bu,) sizin gözlerinizden saklı tutulmuşsa? Siz bunu istemiyorken biz sizi buna zorlayacak mıyız?"'),
(1502, 11, 29, '"Ey Kavmim, ben sizden buna karşılık bir mal istemiyorum. Benim ecrim, yalnızca Allah\'a aittir. Ben iman edenleri kovacak değilim. Onlar gerçekten Rablerine kavuşacaklar. Ancak ben sizi, cahillik etmekte olan bir kavim görüyorum.'),
(1503, 11, 30, '"Ey kavmim, ben onları kovarsam, Allah\'tan (gelecek azaba karşı) bana kim yardım edecek? Hiç düşünmez misiniz?"'),
(1504, 11, 31, '"Ben size Allah\'ın hazineleri yanımdadır demiyorum, gaybı da bilmiyorum. Melek olduğumu söylemiyorum ve gözlerinizin aşağılık gördüklerine, Allah kesin olarak bir hayır vermez de demiyorum. Nefislerinde olanı Allah daha iyi bilir. Bu durumda (bunun aksini yaparsam) gerçekten o zaman zalimlerdenim (demek)dir."'),
(1505, 11, 32, 'Dediler ki: "Ey Nuh, bizimle çekişip-durdun, bu çekişmede ileri de gittin. Eğer doğru söylüyorsan, bize vaadettiğini getir (görelim.)"'),
(1506, 11, 33, 'Dedi ki: "Eğer dilerse, onu size Allah getirir ve siz (O\'nu) aciz bırakacak değilsiniz."'),
(1507, 11, 34, '"Eğer Allah sizi azdırmayı dilemişse, ben size öğüt vermek istesem de, öğüdümün size yararı olmaz. O sizin Rabbinizdir ve O\'na döndürüleceksiniz."'),
(1508, 11, 35, 'Onlar: "Bunu kendisi uydurdu" mu diyorlar? De ki: "Eğer onu ben uydurduysam, günahım bana aittir. Ama ben, sizlerin suç olarak işlemekte olduklarınızdan uzağım."'),
(1509, 11, 36, 'Nuh\'a vahyedildi: "Gerçekten iman edenlerin dışında, kesin olarak kimse inanmayacak. Şu halde onların işlemekte olduklarından dolayı üzülme."'),
(1510, 11, 37, '"Bizim gözetimimiz altında ve vahyimizle gemiyi imal et. Zulmedenler konusunda Bana hitapta bulunma. Çünkü onlar suda- boğulacaklardır."'),
(1511, 11, 38, 'Gemiyi yapıyordu. Kavminin ileri gelenleri kendisine her uğradığında onunla alay ediyordu. O: "Eğer bizimle alay ederseniz, alay ettiğiniz gibi biz de sizlerle alay edeceğiz" dedi.'),
(1512, 11, 39, '"Artık, ilerde bileceksiniz. Aşağılatıcı azap kime gelecek ve sürekli azap kimin üstüne çökecek."'),
(1513, 11, 40, 'Sonunda emrimiz geldiğinde ve tandır feveran ettiği zaman, dedik ki: "Her birinden ikişer çift (hayvan) ile aleyhlerinde söz geçmiş olanlar dışında, aileni ve iman edenleri ona yükle." Zaten onunla birlikte çok azından başkası iman etmemişti.'),
(1514, 11, 41, 'Dedi ki: "Ona binin. Onun yüzmesi de, demir atması (durması) da Allah\'ın adıyladır. Şüphesiz, benim Rabbim bağışlayandır, esirgeyendir."'),
(1515, 11, 42, '(Gemi) Onlarla dağlar gibi dalga(lar) içinde yüzüyorken Nuh, bir kenara çekilmiş olan oğluna seslendi: "Ey oğlum, bizimle birlikte bin ve kafirlerle birlikte olma."'),
(1516, 11, 43, '(Oğlu) Dedi ki: "Ben bir dağa sığınacağım, o beni sudan korur." Dedi ki: "Bugün Allah\'ın emrinden, esirgeyen olan (Allah)dan başka bir koruyucu yoktur." Ve ikisinin arasına dalga girdi, böylece o da boğulanlardan oldu.'),
(1517, 11, 44, 'Denildi ki: "Ey yer, suyunu yut ve ey gök, sen de tut." Su çekildi, iş bitiriliverdi, (gemi de) Cudi (dağı) üstünde durdu ve zalimler topluluğuna da: "Uzak olsunlar" denildi.'),
(1518, 11, 45, 'Nuh, Rabbine seslendi. Dedi ki: "Rabbim, şüphesiz benim oğlum ailemdendir ve Senin va\'din de doğrusu haktır. Sen hakimlerin hakimisin."'),
(1519, 11, 46, 'Dedi ki: "Ey Nuh, kesinlikle o senin ailenden değildir. Çünkü o, salih olmayan bir iş (yapmıştır). Öyleyse hakkında bilgin olmayan şeyi Benden isteme. Gerçekten Ben, cahillerden olmayasın diye sana öğüt veriyorum."'),
(1520, 11, 47, 'Dedi ki: "Rabbim, bilgim olmayan şeyi Senden istemekten Sana sığınırım. Ve eğer beni bağışlamaz ve beni esirgemezsen, hüsrana uğrayanlardan olurum."'),
(1521, 11, 48, '"Ey Nuh" denildi. "Sana ve seninle birlikte olan ümmetler üzerine Bizden selam ve bereketlerle (gemiden) in. (Sizden türeyecek diğer kafir) Ümmetleri de yararlandıracağız, sonra onlara Bizden acı bir azap dokunacaktır."'),
(1522, 11, 49, 'Bunlar: Sana vahyettiğimiz gayb haberlerindendir. Bunları sen ve kavmin bundan önce bilmiyordun. Şu halde sabret. Şüphesiz (güzel olan) sonuç takva sahiplerinindir.'),
(1523, 11, 50, 'Ad (halkına da) kardeşleri Hud\'u (gönderdik). Dedi ki: "Ey kavmim, Allah\'a ibadet edin, sizin O\'ndan başka İlahınız yoktur. Siz yalan olarak (tanrılar) düzenlerden başkası değilsiniz.'),
(1524, 11, 51, 'Ey kavmim, ben bunun karşılığında sizden hiçbir ücret istemiyorum. Benim ücretim, beni yaratandan başkasına ait değildir. Akıl erdirmeyecek misiniz?'),
(1525, 11, 52, 'Ey kavmim, Rabbinizden bağışlanma dileyin, sonra O\'na tevbe edin. Üstünüze gökten sağanak (yağmurlar, bol nimetler) yağdırsın ve gücünüze güç katsın. Suçlu-günahkarlar olarak yüz çevirmeyin."'),
(1526, 11, 53, '"Ey Hud" dediler. "Sen bize apaçık bir belge (mucize) ile gelmiş değilsin ve biz de senin sözünle ilahlarımızı terk etmeyiz. Sana iman edecek de değiliz."'),
(1527, 11, 54, '"Biz: \'Bazı ilahlarımız seni çok kötü çarpmıştır\' (demekten) başka bir şey söylemeyiz." Dedi ki: "Allah\'ı şahid tutarım, siz de şahidler olun ki, gerçekten ben, sizin şirk koştuklarınızdan uzağım."'),
(1528, 11, 55, '"O\'nun dışındaki (tanrılardan). Artık siz bana, toplu olarak dilediğiniz tuzağı kurun, sonra bana süre tanımayın."'),
(1529, 11, 56, '"Ben gerçekten, benim de Rabbim, sizin de Rabbiniz olan Allah\'a tevekkül ettim. O\'nun, alnından yakalayıp-denetlemediği hiçbir canlı yoktur. Muhakkak benim Rabbim, dosdoğru bir yol üzerinedir (dosdoğru yolda olanı korumaktadır.)"'),
(1530, 11, 57, '"Buna rağmen yüz çevirirseniz, artık size kendisiyle gönderildiğim şeyi tebliğ ettim. Rabbim de sizden başka bir kavmi yerinize geçirir. Siz O\'na hiçbir şeyle zarar veremezsiniz. Doğrusu benim Rabbim, herşeyi gözetleyip-koruyandır."'),
(1531, 11, 58, 'Emrimiz geldiği zaman, tarafımızdan bir rahmet ile Hud\'u ve O’nunla birlikte iman edenleri kurtardık. Onları şiddetli-ağır bir azaptan kurtardık.'),
(1532, 11, 59, 'İşte Ad (halkı): Rablerinin ayetlerini tanımayıp reddettiler. O\'nun elçilerine isyan ettiler ve her inatçı zorbanın emri ardınca yürüdüler.'),
(1533, 11, 60, 'Ve bu dünyada da, kıyamet gününde de lanete tabi tutuldular. Haberiniz olsun; gerçekten Ad (halkı), Rablerine (karşı) inkar ettiler. Haberiniz olsun; Hud kavmi Ad\'a (Allah\'ın rahmetinden) uzaklık (verildi).'),
(1534, 11, 61, 'Semud (halkına da) kardeşleri Salih\'i (gönderdik). Dedi ki: "Ey kavmim, Allah\'a ibadet edin, sizin O\'ndan başka İlahınız yoktur. O sizi yerden (topraktan) yarattı ve onda ömür geçirenler kıldı. Öyleyse O\'ndan bağışlanma dileyin, sonra O\'na tevbe edin. Şüphesiz benim Rabbim, yakın olandır, (duaları) kabul edendir."'),
(1535, 11, 62, 'Dediler ki: "Ey Salih, bundan önce sen içimizde kendisinden (iyilikler ve yararlılıklar) umulan biriydin. Atalarımızın taptığı şeylere tapmaktan sen bizi engelleyecek misin? Doğrusu biz, senin bizi davet ettiğin şeyden kuşku verici bir tereddüt içindeyiz."'),
(1536, 11, 63, 'Dedi ki: "Ey kavmim, görüşünüz nedir söyler misiniz? Eğer ben Rabbimden apaçık bir belge üzerindeysem ve bana Tarafından bir rahmet vermişse, bu durumda O\'na isyan edecek olursam Allah\'a karşı bana kim yardım edecektir? Şu halde kaybımı arttırmaktan başka bana (hiçbir yarar) sağlamayacaksınız."'),
(1537, 11, 64, '"Ey kavmim, size işte bir ayet olarak Allah\'ın devesi; onu serbest bırakın, Allah\'ın arzında yesin. Ona kötülük (vermek niyeti)yle dokunmayın. Yoksa sizi yakın bir azap sarıverir."'),
(1538, 11, 65, 'Fakat onu öldürdüler. (Salih) Dedi ki: "Yurdunuzda üç gün daha yararlanın. Bu, yalanlanmayacak bir vaaddir."'),
(1539, 11, 66, 'Emrimiz geldiği zaman, tarafımızdan bir rahmetle Salih\'i ve O’nunla birlikte iman edenleri o günün aşağılatıcı azabından kurtardık. Doğrusu senin Rabbin, güçlü olandır, Aziz olandır.'),
(1540, 11, 67, 'O zulmedenleri dayanılmaz bir ses sarıverdi de kendi yurtlarında dizüstü çökmüş olarak sabahladılar.'),
(1541, 11, 68, 'Sanki orada hiç refah içinde yaşamamışlar gibi. Haberiniz olsun; Semud (halkı) gerçekten Rablerine (karşı) inkar etmişlerdi. Haberiniz olsun; Semud (halkına Allah\'ın rahmetinden) uzaklık (verildi.)'),
(1542, 11, 69, 'Andolsun, elçilerimiz İbrahim\'e müjde ile geldikleri zaman; "Selam" dediler. O da: "Selam" dedi (ve) hemen gecikmeden kızartılmış bir buzağı getirdi.'),
(1543, 11, 70, 'Ellerinin ona uzanmadığını görünce (İbrahim durumdan) hoşlanmadı ve içine bir tür korku düştü. Dediler ki: "Korkma. Biz Lut kavmine gönderildik."'),
(1544, 11, 71, 'Karısı ayaktaydı, bunun üzerine güldü. Biz ona İshak\'ı, İshak\'ın arkasından da Yakub\'u müjdeledik.'),
(1545, 11, 72, '"Vay bana" dedi (kadın). "Ben kocamış bir kadın iken ve şu kocam da bir ihtiyar iken doğuracak mıyım? Gerçekten bu, şaşırtıcı bir şey!.."'),
(1546, 11, 73, 'Dediler ki: "Allah\'ın emrine mi şaşıyorsun? Allah\'ın rahmeti ve bereketleri sizin üzerinizdedir, ey ev halkı şüphesiz O, övülmeye layık olandır, Mecid\'tir."'),
(1547, 11, 74, 'İbrahim\'den korku gittiği ve ona müjde geldiği zaman, Lut kavmi konusunda Bizimle çekişip-tartışmalara giriyor(du).'),
(1548, 11, 75, 'Doğrusu İbrahim, yumuşak huylu, duygulu ve gönülden (Allah\'a) yönelen biriydi.'),
(1549, 11, 76, '"Ey İbrahim, bundan vazgeç. Çünkü gerçek şu ki, Rabbinin emri gelmiştir ve gerçekten onlara geri çevrilmeyecek bir azap gelmiştir."'),
(1550, 11, 77, 'Elçilerimiz Lut\'a geldiği zaman, onlardan dolayı kaygılandı, göğsünü bir sıkıntı bastı ve: "Bu, zorlu bir gün" dedi.'),
(1551, 11, 78, 'Kavmi ona doğru koşarak geldi; onlar daha önceden kötülükler işlemekteydiler. "Ey kavmim" dedi. "İşte benim kızlarım, bunlar sizler için daha temizdir. Artık Allah\'tan korkun ve beni misafirim önünde küçük düşürmeyin. İçinizde hiç aklı başında olan (reşid) bir adam yok mu?"'),
(1552, 11, 79, 'Dediler ki: "Andolsun, senin kızlarında bizim haktan bir şeyimiz (ilgimiz ve arzumuz) olmadığını sen de bilmişsindir. Bizim ne istediğimizi gerçekte sen biliyorsun."'),
(1553, 11, 80, 'Dedi ki: "Size yetecek gücüm olsaydı veya sağlam bir yere sığınabilseydim."'),
(1554, 11, 81, '(Elçiler) Dediler ki: "Ey Lut, biz Rabbinin elçileriyiz. Onlar sana kesin olarak ulaşamazlar. Gecenin bir parçasında ailenle birlikte yürü (yola çık). Sakın, hiçbiriniz dönüp arkasına bakmasın; fakat senin karın başka. Çünkü onlara isabet edecek olan, ona da isabet edecektir. Onlara va\'dolunan (azap) sabah vaktidir. Sabah da yakın değil mi?"'),
(1555, 11, 82, 'Böylece emrimiz geldiği zaman, üstünü altına çevirdik ve üzerlerine balçıktan pişirilmiş, istif edilmiş taşlar yağdırdık;'),
(1556, 11, 83, 'Rabbinin Katında \'belli bir biçime sokulmuş, damgalanmış\' olarak. Bunlar zalimlerden uzak değildir.'),
(1557, 11, 84, 'Medyen (halkına da) kardeşleri Şuayb\'ı (gönderdik). Dedi ki: "Ey kavmim, Allah\'a ibadet edin, O\'ndan başka İlahınız yoktur. Ölçüyü ve tartıyı eksik tutmayın; gerçekten sizi bir \'bolluk ve refah (hayır)\' içinde görüyorum. Doğrusu sizi çepeçevre kuşatacak olan bir günün azabından korkuyorum."'),
(1558, 11, 85, '"Ey kavmim, ölçüyü ve tartıyı -adaleti gözeterek- tam tutun ve insanların eşyasını değerden düşürüp- eksiltmeyin ve yeryüzünde bozguncular olarak karışıklık çıkarmayın."'),
(1559, 11, 86, '"Eğer mü\'minseniz, Allah\'ın bıraktığı (helal işlerden olan kazanç) sizin için daha hayırlıdır. Ben, sizin üzerinizde bir gözetleyici değilim."'),
(1560, 11, 87, 'Dediler ki: "Ey Şuayb, atalarımızın taptığı şeyleri bırakmamızı ya da mallarımız konusunda dilediğimiz gibi davranmaktan vazgeçmemizi senin namazın mı emrediyor? Çünkü sen, gerçekte yumuşak huylu, aklı başında (reşid bir adam)sın."'),
(1561, 11, 88, 'Dedi ki: "Ey kavmim görüşünüz nedir söyler misiniz? Ya ben Rabbimden apaçık bir belge üzerinde isem ve O da beni Kendisi\'nden güzel bir rızık ile rızıklandırmışsa? Ben, size yasakladığım şeylere (kendim sahiplenmek suretiyle) size aykırı düşmek istemiyorum. Benim istediğim, gücüm oranında yalnızca ıslah etmektir. Benim başarım ancak Allah iledir; O\'na tevekkül ettim ve O\'na içten yönelip-dönerim."'),
(1562, 11, 89, '"Ey kavmim, bana karşı gelişiniz, sakın Nuh kavminin ya da Hud kavminin veya Salih kavminin başlarına gelenlerin bir benzerini size de isabet ettirmesin. Üstelik Lut kavmi size pek uzak değil."'),
(1563, 11, 90, '"Rabbinizden bağışlanma dileyin, sonra O\'na tevbe edin. Gerçekten benim Rabbim, esirgeyendir, sevendir."'),
(1564, 11, 91, '"Ey Şuayb" dediler. "Senin söylediklerinin çoğunu biz \'kavrayıp anlamıyoruz\'. Doğrusu biz seni içimizde zayıf biri görüyoruz. Eğer yakın-çevren olmasaydı, gerçekten seni taşa tutar-öldürürdük. Sen bize karşı güçlü ve üstün değilsin."'),
(1565, 11, 92, 'Dedi ki: "Ey kavmim, sizce benim yakın-çevrem, Allah\'tan daha mı üstündür ki, O\'nu arkanızda-unutuluvermiş (önemsiz) bir şey edindiniz. Şüphesiz benim Rabbim, yapmakta olduklarınızı sarıp-kuşatandır."'),
(1566, 11, 93, '"Ey kavmim, bütün yapabileceğinizi yapın; şüphesiz, ben de yapacağım. Kime aşağılatıcı azap gelecek ve yalancı kimdir, yakında bileceksiniz. Siz gözetleyip durun, ben de sizinle birlikte gözetleyeceğim."'),
(1567, 11, 94, 'Emrimiz geldiği zaman, tarafımızdan bir rahmetle Şuayb\'ı ve O’nunla birlikte iman edenleri kurtardık; o zulmedenleri dayanılmaz bir ses sarıverdi de kendi yurtlarında dizüstü çökmüş olarak sabahladılar.'),
(1568, 11, 95, 'Sanki orada hiç refah içinde yaşamamışlar gibi. Haberiniz olsun; Semud (halkına) nasıl bir uzaklık verildiyse Medyen (halkına da Allah\'ın rahmetinden öyle) bir uzaklık (verildi).'),
(1569, 11, 96, 'Andolsun, Musa\'yı ayetlerimizle ve apaçık olan bir delille gönderdik.'),
(1570, 11, 97, 'Firavun\'a ve onun önde gelen çevresine. Onlar Firavun\'un emrine uymuşlardı. Oysa Firavun\'un emri doğruya-götürücü (irşad edici) değildi.'),
(1571, 11, 98, 'O, kıyamet günü kavminin önderliğine geçer, böylece onları ateşe götürmüş olur. Sonunda vardıkları yer, ne kötü bir yerdir..'),
(1572, 11, 99, 'Onlar, burda da, kıyamet gününde de lanete tabi tutuldular. (Bu) Verilen bağış, ne kötü bir bağıştır.'),
(1573, 11, 100, 'Bunlar, sana doğru haber (kıssa) olarak aktardığımız (geçmişteki) nesillerin haberleridir. Onlardan kimi ayakta kalmış, (hala izleri var, kimi de) biçilmiş ekin (gibi yerlebir edilmiş, kalıntısı silinmiş) dir.'),
(1574, 11, 101, 'Biz onlara zulmetmedik, ancak onlar kendi nefislerine zulmettiler. Böylece Rabbinin emri geldiği zaman, Allah\'ı bırakıp da taptıkları ilahları, onlara hiçbir şey sağlayamadı, \'helak ve kayıplarını\' arttırmaktan başka bir işe yaramadı.'),
(1575, 11, 102, 'Onlar, zulüm işlemektelerken, ülkeleri (veya nesilleri) yakaladığı zaman... Rabbinin yakalaması işte böyledir. Gerçekten O\'nun yakalaması pek acı, pek şiddetlidir.'),
(1576, 11, 103, 'Ahiret azabından korkan için bunda kesin ayetler vardır. O, bütün insanların kendisinde toplanacağı bir gündür ve o, gözlemlenebilen bir gündür.'),
(1577, 11, 104, 'Biz onu sayılı bir sürenin (ecelin) dışında ertelemeyiz.'),
(1578, 11, 105, '(Kıyametin) Geleceği günde, O\'nun izni olmaksızın, hiç kimse söz söyleyemez. Artık onlardan kimi \'bedbaht ve mutsuz\', (kimi de) mutlu ve bahtiyardır.'),
(1579, 11, 106, 'Mutsuz olanlar ateştedirler, onlar için orada (kahırla ve acıyla) nefes alıp vermeler vardır.'),
(1580, 11, 107, 'Onlar, Rabbinin dilemesi dışında gökler ve yer sürüp gittikçe, orada süresiz kalacaklardır. Çünkü Rabbin, gerçekten dilediğini yapandır.'),
(1581, 11, 108, 'Mutlu olanlar da, artık onlar cennettedirler. Rabbinin dilemesi dışında gökler ve yer sürüp gittikçe, orada süresiz kalacaklardır. (Bu) kesintisi olmayan bir ihsandır.'),
(1582, 11, 109, 'Artık onların tapmakta oldukları şeyler konusunda, sakın kuşkuda olma. Daha önceleri, ataları nasıl tapıyor idiyseler, bunlar da ancak böyle tapıyorlar. Şüphesiz Biz, onların paylarını eksiltmeksizin onlara ödeyecek olanlarız.'),
(1583, 11, 110, 'Andolsun, Musa\'ya kitabı verdik, onda anlaşmazlığa düşüldü. Eğer Rabbinden bir söz geçmiş (verilmiş) olmasaydı, mutlaka aralarında hüküm verilmiş olacaktı. Gerçekten onlar, bundan (Kur\'an\'dan) yana kuşku verici bir tereddüt içindedirler.'),
(1584, 11, 111, 'Şüphesiz Rabbin, onlardan tümüne yapıp ettiklerini(n karşılığını) onlara tastamam ödeyecektir. Çünkü O, yapıp-ettiklerinden haberdar olandır.'),
(1585, 11, 112, 'Seninle birlikte tevbe edenlerle birlikte emrolunduğun gibi dosdoğru davran. Ve azıtmayın. Çünkü O, yaptıklarınızı görendir.'),
(1586, 11, 113, 'Zulmedenlere eğilim göstermeyin, yoksa size ateş dokunur. Sizin Allah\'tan başka velileriniz yoktur, sonra yardım göremezsiniz.'),
(1587, 11, 114, 'Gündüzün iki tarafında ve gecenin (gündüze) yakın saatlerinde namazı kıl. Şüphesiz iyilikler, kötülükleri giderir. Bu, öğüt alanlara bir öğüttür.'),
(1588, 11, 115, 'Ve sabret. Gerçekten Allah, iyilik yapanların ecrini kaybetmez.'),
(1589, 11, 116, 'Sizden önceki nesillerden onlardan kurtardığımızdan pek azı dışında yeryüzünde bozgunculuğu önleyecek fazilet sahibi kişiler bulunmalı değil miydi? Zulmedenler ise, içinde bulundukları refahın peşine düştüler. Onlar, suçlu-günahkarlardı.'),
(1590, 11, 117, 'Halkı, ıslah eden kimseler iken, senin Rabbin o ülkeleri zulüm ile helak edecek değildi.'),
(1591, 11, 118, 'Eğer Rabbin dileseydi, insanları elbette tek bir ümmet kılardı. Oysa, onlar, anlaşmazlığı sürdürmektedirler:'),
(1592, 11, 119, 'Rabbinin rahmet ettikleri dışında. Onları bunun için yarattı. Böylece Rabbinin (şu) sözü tamamlanıp gerçekleşmiştir: "Andolsun, cehennemi cinlerden ve insanlardan, (kafirlerin) tümüyle dolduracağım."'),
(1593, 11, 120, 'Sana elçilerin haberlerinden -kalbini sağlamlaştıracak- doğru haberler aktarıyoruz. Bunda sana hak ve mü\'minlere bir öğüt ve uyarı gelmiştir.'),
(1594, 11, 121, 'İman etmeyenlere de ki: "Yapabileceğinizi yapın; elbette biz de yapacağız."'),
(1595, 11, 122, 'Ve gözleyip durun; gerçekten biz de gözleyip duruyoruz."'),
(1596, 11, 123, 'Göklerin ve yerin gaybı Allah\'ındır, bütün işler O\'na döndürülür; öyleyse O\'na kulluk edin ve O\'na tevekkül edin. Senin Rabbin yaptıklarınızdan habersiz değildir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Elif, Lam, Ra. Bunlar, apaçık Kitab\'ın ayetleridir.'),
(1598, 12, 2, 'Gerçekten Biz, akıl erdirirsiniz diye, onu Arapça bir Kur\'an olarak indirdik.'),
(1599, 12, 3, 'Biz bu Kur\'an\'ı sana vahyetmemizle, en güzel kıssaları gerçek bir haber (kıssa) olarak sana aktarıyoruz, oysa sen, daha önce, bundan haberi olmayanlardandın.'),
(1600, 12, 4, 'Hani Yusuf babasına: "Babacığım, gerçekten ben (rüyamda) on bir yıldız, Güneş\'i ve Ay\'ı gördüm; bana secde etmektelerken gördüm" demişti.'),
(1601, 12, 5, '(Babası) Demişti ki: "Oğlum, rüyanı kardeşlerine anlatma, yoksa sana bir tuzak kurarlar. Çünkü şeytan, insan için apaçık bir düşmandır."'),
(1602, 12, 6, '"Böylece Rabbin seni seçkin kılacak, sözlerin yorumundan (kaynaklanan bir bilgiyi) sana öğretecek ve daha önce ataların İbrahim ve İshak\'a (nimetini) tamamladığı gibi senin ve Yakub ailesinin üzerindeki nimetini tamamlayacaktır. Elbette Rabbin, bilendir, hüküm ve hikmet sahibidir."'),
(1603, 12, 7, 'Andolsun, Yusuf ve kardeşlerinde soranlar için ayetler (ibretler) vardır.'),
(1604, 12, 8, 'Onlar şöyle demişti: "Yusuf ve kardeşi babamıza bizden daha sevgilidir; oysa ki biz, birbirini pekiştiren bir topluluğuz. Gerçekte babamız, açıkça bir şaşkınlık içindedir."'),
(1605, 12, 9, '"Öldürün Yusuf\'u veya onu bir yere atıp-bırakın ki babanızın yüzü yalnızca size (dönük) kalsın. Ondan sonra da salih bir topluluk olursunuz."'),
(1606, 12, 10, 'İçlerinden bir sözcü dedi ki: "Eğer (mutlaka bir şey) yapacaksanız, öldürmeyin Yusuf\'u, onu kuyunun derinliklerine bırakıverin de bir yolcu kafilesi alsın."'),
(1607, 12, 11, '(Bu karara vardıktan sonra) "Ey Babamız," dediler. "Sana ne oluyor, Yusuf\'a karşı bize güvenmiyorsun? Oysa gerçekte biz, onun iyiliğini isteyenleriz."'),
(1608, 12, 12, '"Sen onu yarın bizimle gönder, gönlünce gezsin, oynasın. Elbette biz onu koruyup-gözetiriz."'),
(1609, 12, 13, 'Dedi ki: "Sizin onu götürmeniz gerçekten beni üzer ve siz ondan habersiz iken onu kurdun yemesinden korkuyorum."'),
(1610, 12, 14, 'Dediler ki: "Andolsun, biz, birbirini kollayan bir topluluk iken, kurt onu yerse, bu durumda şüphesiz kayba uğrayan (aciz) kimseler oluruz."'),
(1611, 12, 15, 'Nitekim onu götürdükleri ve kuyunun derinliklerine atmaya topluca davrandıkları zaman, Biz ona (şöyle) vahyettik: "Andolsun, sen onlara kendileri, farkında değilken bu yaptıklarını haber vereceksin."'),
(1612, 12, 16, 'Akşam üstü babalarına ağlar vaziyette geldiler.'),
(1613, 12, 17, 'Dediler ki: "Ey Babamız, gerçek şu ki, biz gittik, yarışıyorduk. Yusuf\'u da yiyeceklerimizin (veya eşyamızın) yanında bırakmıştık. Fakat onu kurt yemiş. Ne var ki biz doğruyu söylesek bile sen bize inanacak değilsin."'),
(1614, 12, 18, 'Ve üzerine yalandan kan (sürülmüş) olan gömleğini getirdiler. "Hayır" dedi. Nefsiniz, sizi yanıltıp (böyle) bir işe sürüklemiş. Bundan sonra (bana düşen) güzel bir sabırdır. Sizin bu düzüp-uydurduklarınıza karşı (Kendisi\'nden) yardım istenecek olan Allah\'tır."'),
(1615, 12, 19, 'Bir yolcu-kafilesi geldi, sucularını (kuyuya su almak için) gönderdiler. O da kovasını sarkıttı. "Hey müjde... Bu bir çocuk." dedi. Ve onu (kuyudan çıkarıp) \'ticaret konusu bir mal\' olarak sakladılar. Oysa Allah, yapmakta olduklarını bilendi.'),
(1616, 12, 20, 'Onu ucuz bir fiyata, sayısı belli (birkaç) dirheme sattılar. Onu pek önemsemediler.'),
(1617, 12, 21, 'Onu satın alan bir Mısırlı (aziz,) karısına: "Onun yerini üstün tut (ona güzel bak), umulur ki bize bir yararı dokunur ya da onu evlat ediniriz" dedi. Böylelikle Biz, Yusuf\'u yeryüzünde (Mısır\'da) yerleşik kıldık. Ona sözlerin yorumundan (olan bir bilgiyi) öğrettik. Allah, emrinde galib olandır, ancak insanların çoğu bilmezler.'),
(1618, 12, 22, 'Erginlik çağına erişince, kendisine hüküm ve ilim verdik. İşte Biz, iyilik yapanları böyle ödüllendiririz.'),
(1619, 12, 23, 'Evinde kalmakta olduğu kadın, ondan murad almak istedi ve kapıları sımsıkı kapatarak: "İsteklerim senin içindir, gelsene" dedi. (Yusuf) Dedi ki: "Allah\'a sığınırım. Çünkü o benim Efendimdir, yerimi güzel tutmuştur. Gerçek şu ki, zalimler kurtuluşa ermez."'),
(1620, 12, 24, 'Andolsun kadın onu arzulamıştı, -eğer Rabbinin (zinayı yasaklayan) kesin kanıt (burhan)ını görmeseydi- o da (Yusuf da) onu arzulamıştı. Böylelikle Biz ondan kötülüğü ve fuhşu geri çevirmek için (ona delil gönderdik). Çünkü o, muhlis kullarımızdandı.'),
(1621, 12, 25, 'Kapıya doğru ikisi de koştular. Kadın onun gömleğini arkadan çekip yırttı. (Tam) Kapının yanında kadının efendisiyle karşılaştılar. Kadın dedi ki: "Ailene kötülük isteyenin, zindana atılmaktan veya acı bir azaptan başka cezası ne olabilir?"'),
(1622, 12, 26, '(Yusuf) Dedi ki: "Onun kendisi benden murad almak istedi." Kadının yakınlarından bir şahid şahitlik etti: "Eğer onun gömleği ön taraftan yırtılmışsa bu durumda kadın doğruyu söylemiştir, kendisi ise yalan söyleyenlerdendir.'),
(1623, 12, 27, 'Yok eğer onun gömleği arkadan çekilip-yırtılmışsa, bu durumda kadın yalan söylemiştir ve kendisi doğruyu söyleyenlerdendir."'),
(1624, 12, 28, 'Onun gömleğinin arkadan çekilip-yırtıldığını gördüğü zaman (kocası): "Doğrusu, bu sizin düzeninizden (biri)dir. Gerçekten sizin düzeniniz büyüktür" dedi.'),
(1625, 12, 29, '"Yusuf, sen bundan yüz çevir. Sen de (kadın) günahın dolayısıyla bağışlanma dile. Doğrusu sen günahkarlardan oldun."'),
(1626, 12, 30, 'Şehirde (birtakım) kadınlar: "Aziz (Vezir)\'in karısı kendi uşağının nefsinden murad almak istiyormuş. Öyle ki sevgi onun bağrına sinmiş. Biz doğrusu onu açıkça bir sapıklık içinde görüyoruz." dedi.'),
(1627, 12, 31, '(Kadın) Onların düzenlerini işitince, onlara (bir davetçi) yolladı, oturup dayanacakları yerler hazırladı ve her birinin eline (önlerindeki meyveleri soymaları için) bıçak verdi. (Yusuf\'a da:) "Çık, onlara (görün)" dedi. Böylece onlar onu (olağanüstü güzellikte) görünce (insanüstü bir varlıkmış gibi gözlerinde) büyüttüler, (şaşkınlıklarından) ellerini kestiler ve: "Allah\'ı tenzih ederiz; bu bir beşer değildir. Bu, ancak üstün bir melektir" dediler.'),
(1628, 12, 32, 'Kadın dedi ki: "Beni kendisiyle kınadığınız işte budur. Andolsun onun nefsinden ben murad istedim, o ise (kendini) korudu. Ve andolsun, eğer o kendisine emrettiğimi yapmayacak olursa, mutlaka zindana atılacak ve elbette küçük düşürülenlerden olacak."'),
(1629, 12, 33, '(Yusuf) Dedi ki: "Rabbim, zindan, bunların beni kendisine çağırdıkları şeyden bana daha sevimlidir. Kurdukları düzeni benden uzaklaştırmazsan, onlara (korkarım) eğilim gösterir, (böylece) cahillerden olurum."'),
(1630, 12, 34, 'Böylece Rabbi, duasını kabul etti ve onların hileli düzenlerini kendisinden uzaklaştırdı. Çünkü O, işitendir, bilendir.'),
(1631, 12, 35, 'Sonra onlarda (Yusuf\'un iffetine ilişkin) delilleri görmelerinin ardından, mutlaka onu belli bir vakte kadar zindana atmak (görüşü)ağır bastı.'),
(1632, 12, 36, 'Onunla birlikte iki genç de zindana girmişti. Biri: "Ben (rüyamda) kendimi şarap sıkıyorken gördüm." dedi. Öbürü: "Ben de kendimi başımın üstünde ekmek taşıyorken gördüm; kuş da ondan yemekteydi" dedi. "Bunun yorumundan bize haber ver. Doğrusu biz seni, iyilik yapanlardan görmekteyiz."'),
(1633, 12, 37, 'Dedi ki: "Size rızıklanacağınız bir yemek gelecek olsa, ben mutlaka size daha gelmeden önce onun ne olduğunu haber veririm. Bu, Rabbimin bana öğrettiklerindendir. Doğrusu ben, Allah\'a iman etmeyen, ahireti de tanımayanların ta kendileri olan bir topluluğun dinini terk ettim."'),
(1634, 12, 38, '"Atalarım İbrahim\'in, İshak\'ın ve Yakub\'un dinine uydum. Allah\'a hiçbir şeyle şirk koşmamız bizim için olacak şey değil. Bu, bize ve insanlara Allah\'ın lütuf ve ihsanındandır, ancak insanların çoğu şükretmezler."'),
(1635, 12, 39, '"Ey zindan arkadaşlarım, birbirinden ayrı (bir sürü) Rabler mi daha hayırlıdır, yoksa kahhar (kahredici) olan bir tek Allah mı?"'),
(1636, 12, 40, '"Sizin Allah\'tan başka taptıklarınız, Allah\'ın kendileri hakkında hiçbir delil indirmediği, sizin ve atalarınızın ad olarak adlandırdıklarınızdan başkası değildir. Hüküm, yalnızca Allah\'ındır. O, Kendisi\'nden başkasına kulluk etmemenizi emretmiştir. Dosdoğru olan din işte budur, ancak insanların çoğu bilmezler."'),
(1637, 12, 41, '"Ey zindan arkadaşlarım, ikinizden biri efendisine şarap içirecek, diğeri ise asılacak, kuş onun başından yiyecek. İşte hakkında fetva istemekte olduğunuz iş (artık) olup bitmiştir."'),
(1638, 12, 42, 'İkisinden kurtulacağını sandığı kişiye dedi ki: "Efendinin katında beni hatırla." Fakat şeytan, efendisine hatırlatmayı ona unutturdu, böylece daha nice yıllar (Yusuf) zindanda kaldı.'),
(1639, 12, 43, 'Hükümdar: "Ben (rüyamda) yedi besili inek görüyorum, onları yedi zayıf inek yiyor; bir de yedi yeşil başak ve diğerleri ise kupkuru. Ey önde gelen (kahin-bilginler,) eğer rüya yorumluyorsanız benim bu rüyamı çözüverin" dedi.'),
(1640, 12, 44, 'Dediler ki: "(Bunlar) Karmakarışık düşlerdir. Biz böyle düşlerin yorumunu bilenler değiliz."'),
(1641, 12, 45, 'O iki kişiden kurtulmuş olanı, nice zaman sonra hatırladı ve: "Ben bunun yorumunu size haber veririm, hemen beni (zindana) gönderin" dedi.'),
(1642, 12, 46, '(Zindana gidip:) "Yusuf, ey doğru (sözlü insan).. Yedi besili ineği yedi zayıf (ineğin) yediği ve yedi yeşil başakla diğerleri kuru olan (rüya) konusunda bize fetva ver. Umarım ki insanlara da (senin söylediklerinle) dönerim, belki onlar (bunun anlamını) öğrenmiş olurlar."'),
(1643, 12, 47, 'Dedi ki: "Siz yedi yıl, önceleri (ektiğiniz) gibi ekin ekin, yediğinizin az bir kısmı dışında (kalanını) biçtiklerinizi başağında bırakın."'),
(1644, 12, 48, 'Sonra bunun arkasından (kuraklığı) zorlu yedi yıl gelecektir, sakladığınız az bir miktar dışında, daha önce biriktirdiğinizi yiyip bitirecektir."'),
(1645, 12, 49, 'Sonra bunun arkasından bir yıl gelecektir ki, insanlar onda bol bol yağmura kavuşturulacak ve onda sıkıp-sağacaklar."'),
(1646, 12, 50, 'Hükümdar dedi ki: "Onu bana getirin." Ona elçi geldiğinde (Yusuf:) "Efendine (Rabbine) dön de ona sor: "Ellerini kesen o kadınların durumu neydi? Doğrusu benim Rabbim, onların hileli düzenlerini gerçekten bilendir."'),
(1647, 12, 51, '(Hükümdar topladığı o kadınlara:) "Yusuf\'un nefsinden murad almak istediğinizde sizin durumunuz neydi?" dedi. Onlar: "Allah için, haşa" dediler. "Biz ondan hiçbir kötülük görmedik." Aziz (Vezir)in de karısı dedi ki: "İşte şu anda gerçek orta yere çıktı; onun nefsinden ben murad almak istemiştim. O ise gerçekten doğruyu söyleyenlerdendir."'),
(1648, 12, 52, '(Yusuf aracıya şunu söyledi:) "Bu, (itiraf Vezirin) yokluğunda gerçekten kendisine ihanet etmediğimi ve gerçekten Allah\'ın ihanet edenlerin hileli-düzenlerini başarıya ulaştırmadığını kendisinin de bilip öğrenmesi içindi."'),
(1649, 12, 53, '"(Yine de) Ben nefsimi temize çıkaramam. Çünkü gerçekten nefis, -Rabbimin kendisini esirgediği dışında- var gücüyle kötülüğü emredendir. Şüphesiz, benim Rabbim, bağışlayandır, esirgeyendir."'),
(1650, 12, 54, 'Hükümdar dedi ki: "Onu bana getirin, onu kendime bağlı kılayım." Onunla konuştuğunda da (şöyle) dedi: "Sen bugün bizim yanımızda (artık) önemli bir yer sahibisin, güvenilir (bir danışman-yönetici)sin."'),
(1651, 12, 55, '(Yusuf) Dedi ki: "Beni (bu) yerin (ülkenin) hazineleri üzerinde (bir yönetici) kıl. Çünkü ben, (bunları iyi) bir koruyucuyum, (yönetim işlerini de) bilenim."'),
(1652, 12, 56, 'İşte böylece Biz yeryüzünde Yusuf\'a güç ve imkan (iktidar) verdik. Öyle ki, orada (Mısır\'da) dilediği yerde konakladı. Biz kime dilersek rahmetimizi nasib ederiz ve iyilik yapanların ecrini kayba uğratmayız.'),
(1653, 12, 57, 'Ahiretin karşılığı ise, iman edenler ve takvada bulunanlar için daha hayırlıdır.'),
(1654, 12, 58, '(Kuraklık başlayınca) Yusuf\'un kardeşleri gelip yanına girdiler, onu tanımadıkları halde kendisi onları hemen tanıdı.'),
(1655, 12, 59, 'Onların erzak yüklerini hazırlayınca dedi ki: "Bana babanızdan olan kardeşinizi getirin. Görmüyor musunuz, ben ölçüyü tam tutarım ve ben konukseverlerin en hayırlısıyım."'),
(1656, 12, 60, '"Eğer onu bana getirmeyecek olursanız, artık benim katımda sizin için bir ölçek (erzak) yoktur ve bana da yaklaşmayın."'),
(1657, 12, 61, 'Dediler ki: "Onu babasından istemeye çalışacağız ve herhalde biz bunu yapabileceğiz."'),
(1658, 12, 62, 'Yardımcılarına dedi ki: "Sermayelerini (erzak bedellerini) yüklerinin içine koyun. İhtimal ki ailelerine döndüklerinde bunun farkına varırlar da belki geri dönerler."'),
(1659, 12, 63, 'Böylelikle babalarına döndükleri zaman, dediler ki: "Ey babamız, ölçek bizden engellendi. Bu durumda kardeşimizi bizimle gönder de erzağı alalım. Onu mutlaka koruyacağız."'),
(1660, 12, 64, 'Dedi ki: "Daha önce kardeşi konusunda size güvendiğimden başka (bir şekilde) onun hakkında size güvenir miyim? Allah en hayırlı koruyucudur ve O, esirgeyenlerin esirgeyicisidir."'),
(1661, 12, 65, 'Erzak yüklerini açıp da sermayelerinin kendilerine geri verilmiş olduğunu gördüklerinde, dediler ki: "Ey Babamız, daha neyi arıyoruz, işte sermayemiz bize geri verilmiş; (bununla yine) ailemize erzak getiririz, kardeşimizi koruruz ve bir deve yükünü de ilave ederiz. Bu (aldığımız) az bir ölçektir."'),
(1662, 12, 66, '"Bana etrafınızın çepeçevre kuşatılması dışında, onu ne olursa olsun mutlaka bana getireceğinize dair Allah adına kesin bir söz verinceye kadar, onu sizinle asla gönderemem." dedi. Böylelikle ona kesin bir söz verince dedi ki: "Allah, söylediklerimize vekildir."'),
(1663, 12, 67, 'Ve dedi ki: "Ey çocuklarım, tek bir kapıdan girmeyin, ayrı ayrı kapılardan girin. Ben size Allah\'tan hiçbir şeyi sağlayamam (gideremem). Hüküm yalnızca Allah\'ındır. Ben O\'na tevekkül ettim. Tevekkül edenler de yalnızca O\'na tevekkül etmelidirler."'),
(1664, 12, 68, 'Babalarının kendilerine emrettiği yerden (Mısır\'a) girdiklerinde, (bu,) -Yakub\'un nefsindeki dileği açığa çıkarması dışında- onlara Allah\'tan gelecek olan hiçbir şeyi (gidermeyi) sağlamadı. Gerçekten o, kendisine öğrettiğimiz için bir ilim sahibiydi. Ancak insanların çoğu bilmezler.'),
(1665, 12, 69, 'Yusuf\'un yanına girdikleri zaman, o, kardeşini bağrına bastı; "Ben" dedi. "Senin gerçekten kardeşinim. Artık onların yaptıklarına üzülme."'),
(1666, 12, 70, 'Erzak yüklerini kendilerine hazırlayınca da, su kabını kardeşinin yükü içine bıraktı, sonra bir münadi (şöyle) seslendi: "Ey kafile, sizler gerçekten hırsızsınız."'),
(1667, 12, 71, 'Onlara doğru yönelerek: "Neyi kaybettiniz?" dediler.'),
(1668, 12, 72, 'Dediler ki: "Hükümdarın su tasını kaybettik, kim onu (bulup) getirirse, (ona armağan olarak) bir deve yükü vardır. Ben de buna kefilim."'),
(1669, 12, 73, '"Allah adına, hayret" dediler. "Siz de bilmişsiniz ki, biz (bu) yere bozgunculuk çıkarmak amacıyla gelmedik ve biz hırsız değiliz."'),
(1670, 12, 74, '"Öyleyse" dediler. "Eğer yalan söylüyorsanız (bunun) cezası nedir?"'),
(1671, 12, 75, 'Dediler ki: "Bunun cezası, (su tası) yükünde bulunanın kendisidir. İşte biz zulmedenleri böyle cezalandırırız."'),
(1672, 12, 76, 'Böylece (Yusuf) kardeşinin kabından önce onların kablarını (yoklamaya) başladı, sonra onu kardeşinin kabından çıkardı. İşte Biz Yusuf için böyle bir plan düzenledik. (Yoksa) Hükümdarın dininde (yürürlükteki kanuna göre) kardeşini (yanında) alıkoyamazdı. Ancak Allah\'ın dilemesi başka. Biz dilediğimizi derecelerle yükseltiriz. Ve her bilgi sahibinin üstünde daha iyi bir bilen vardır.'),
(1673, 12, 77, 'Dediler ki: "Şayet çalmış bulunuyorsa, bundan önce onun kardeşi de çalmıştı." Yusuf bunu kendi içinde saklı tuttu ve bunu onlara açıklamadı (ve içinden): "Siz daha kötü bir konumdasınız" dedi. "Sizin düzmekte olduklarınızı Allah daha iyi bilir."'),
(1674, 12, 78, 'Dediler ki: "Ey Vezir, gerçek şu ki, bunun yaşlı (ve) büyük bir babası var; onun yerine bizden birisini alıkoy. Doğrusu biz, seni iyilik yapanlardan görmekteyiz."'),
(1675, 12, 79, 'Dedi ki: "Eşyamızı kendisinde bulduğumuzun dışında, birisini alıkoymamızdan Allah\'a sığınırız. Yoksa bu durumda kuşkusuz biz zalim oluruz."'),
(1676, 12, 80, 'Ondan umutlarını kestikleri zaman, (durumu) kendi aralarında görüşmek üzere bir yana çekildiler. Onların büyükleri dedi ki: "Babanızın size karşı Allah adına kesin bir söz aldığını ve daha önce Yusuf konusunda yaptığımız aşırılığı (işlediğimiz suçu) bilmiyor musunuz? Artık (bundan böyle) ben, ya babam bana izin verinceye veya Allah bana ilişkin hüküm verinceye kadar (bu) yerden kesin olarak ayrılamam. O, hüküm verenlerin en hayırlısıdır."'),
(1677, 12, 81, '"Dönün babanıza ve deyin ki: \'-Ey babamız, senin oğlun gerçekten hırsızlık etti. Biz, bildiğimizden başkasına şahitlik etmedik. Biz gaybın kollayıcıları değiliz."'),
(1678, 12, 82, '"İçinde (yaşamakta) olduğumuz şehre sor, hem kendisinde geldiğimiz kervana da. Biz gerçekten doğruyu söyleyenleriz."'),
(1679, 12, 83, '(Şehre dönüp durumu babalarına aktarınca o:) "Hayır" dedi. "Nefsiniz sizi yanıltıp (böyle) bir işe sürüklemiş. Bundan sonra (bana düşen) güzel bir sabırdır. Umulur ki Allah (pek yakın bir gelecekte) onların tümünü bana getirir. Çünkü O, bilenin, hüküm ve hikmet sahibi olanın Kendisi\'dir."'),
(1680, 12, 84, 'Ve onlardan yüz(ünü) çevirdi ve: "Ey Yusuf\'a karşı (artan dayanılmaz) kahrım" dedi ve gözleri üzüntüsünden (ağardıkça) ağardı. Ki yutkundukça yutkunuyordu."'),
(1681, 12, 85, '"Allah adına, hayret" dediler. "Hala Yusuf\'u anıp durmaktasın. Sonunda (ya kahrından) hastalanacaksın ya da helake uğrayanlardan olacaksın."'),
(1682, 12, 86, 'Dedi ki: "Ben, dayanılmaz kahrımı ve üzüntümü yalnızca Allah\'a şikayet ediyorum. Ben Allah\'tan (bir bilgi olarak) sizin bilmediğinizi de biliyorum."'),
(1683, 12, 87, '"Oğullarım, gidin de Yusuf ile kardeşinden (duyarlı bir araştırmayla) bir haber getirin ve Allah\'ın rahmetinden umut kesmeyin. Çünkü kafirler topluluğundan başkası Allah\'ın rahmetinden umut kesmez."'),
(1684, 12, 88, 'Böylece onun (Yusuf\'un) huzuruna girdikleri zaman, dediler ki: "Ey Vezir, bize ve ailemize şiddetli bir darlık dokundu; önemi olmayan bir sermaye ile geldik. Bize artık (yine) ölçeği tam olarak ver ve bize ilave bir bağışta bulun. Şüphesiz Allah, tasaddukta bulunanlara karşılığını verir."'),
(1685, 12, 89, '(Yusuf) Dedi ki: "Sizler, cahiller iken Yusuf\'a ve kardeşine neler yaptığınızı biliyor musunuz?"'),
(1686, 12, 90, '"Sen gerçekten Yusuf musun, sensin öyle mi?" dediler. "Ben Yusuf\'um" dedi. "Ve bu da kardeşimdir. Doğrusu Allah bize lütufta bulundu. Gerçek şu ki, kim sakınır ve sabrederse, şüphesiz Allah, iyilikte bulunanların karşılığını boşa çıkarmaz."'),
(1687, 12, 91, 'Dediler ki: "Allah adına, hayret, Allah seni gerçekten bize karşı tercih edip-seçmiştir ve biz de gerçekten hataya düşenler idik."'),
(1688, 12, 92, 'Dedi ki: "Bugün size karşı sorgulama, kınama yoktur. Sizi Allah bağışlasın. O, merhametlilerin (en) merhametlisidir."'),
(1689, 12, 93, '"Bu gömleğimle gidin de, babamın yüzüne sürün. Gözü (yine) görür hale gelir. Bütün ailenizi de bana getirin."'),
(1690, 12, 94, 'Kafile (Mısır\'dan) ayrılmaya başladığı zaman, babaları dedi ki: "Eğer beni bunamış saymıyorsanız, inanın Yusuf\'un kokusunu (burnumda tüter) buluyorum."'),
(1691, 12, 95, '"Allah adına, hayret" dediler. "Sen hala geçmişteki yanlışlığındasın."'),
(1692, 12, 96, 'Müjdeci gelip de onu (gömleği) onun yüzüne sürdüğü zaman, gözü görür olarak (sağlığına) dönüverdi. (Yakub) Dedi ki: "Ben, size bilmediğinizi Allah\'tan gerçekten biliyorum demedim mi?"'),
(1693, 12, 97, '(Çocukları da:) "Ey babamız, bizim için günahlarımızın bağışlanmasını dile. Biz gerçekten hataya düşenler idik" dediler.'),
(1694, 12, 98, '"İlerde sizin için Rabbimden bağışlanma dilerim. Çünkü O, bağışlayandır, esirgeyendir" dedi.'),
(1695, 12, 99, 'Böylece onlar (gelip) Yusuf\'un yanına girdikleri zaman, anne ve babasını bağrına bastı ve dedi ki: "Allah\'ın dilemesiyle Mısır\'a güvenlik içinde giriniz."'),
(1696, 12, 100, 'Babasını ve annesini tahta çıkarıp oturttu; onun için secdeye kapandılar. Dedi ki: "Ey Babam, bu, daha önceki rüyamın yorumudur. Doğrusu Rabbim onu gerçek kıldı. Bana iyilik etti, çünkü beni zindandan çıkardı. Şeytan benimle kardeşlerimin arasını açtıktan sonra, (O,) çölden sizi getirdi. Şüphesiz benim Rabbim, dilediğini pek ince düzenleyip tedbir edendi. Gerçekten bilen, hüküm ve hikmet sahibi O\'dur."'),
(1697, 12, 101, '"Rabbim, Sen bana mülkten (bir pay ve onu yönetme imkanını) verdin, sözlerin yorumundan (bir bilgi) öğrettin. Göklerin ve yerin Yaratıcısı, dünyada ve ahirette benim velim Sensin. Müslüman olarak benim hayatıma son ver ve beni salihlerin arasına kat."'),
(1698, 12, 102, 'Bu, sana (ey Muhammed) vahyettiğimiz gayb haberlerindendir. Yoksa onlar, (Yusuf\'un kardeşleri) o hileli-düzeni kurarlarken, yapacakları işe topluca karar verdikleri zaman sen yanlarında değildin.'),
(1699, 12, 103, 'Sen şiddetle arzu etsen bile, insanların çoğu iman edecek değildir.'),
(1700, 12, 104, 'Oysa ki sen buna karşı onlardan bir ücret de istemiyorsun. O, alemler için yalnızca bir \'öğüt ve hatırlatmadır.\''),
(1701, 12, 105, 'Göklerde ve yerde nice ayetler vardır ki, üzerinden geçerler de, ona sırtlarını dönüp giderler.'),
(1702, 12, 106, 'Onların çoğu Allah\'a iman etmezler de ancak şirk katıp-dururlar.'),
(1703, 12, 107, 'Şimdi bunlar, kendilerine Allah\'ın azabından kapsamlı bir bürümenin gelivermesinden veya onların hiç haberleri yokken kıyametin onlara apansız gelmesinden kendilerini güvende mi buldular?'),
(1704, 12, 108, 'De ki: "Bu, benim yolumdur. Bir basiret üzere Allah\'a davet ederim; ben ve bana uyanlar da. Ve Allah\'ı tenzih ederim, ben müşriklerden değilim."'),
(1705, 12, 109, 'Biz senden önce, şehirler halkına kendilerine vahyettiğimiz kimseler dışında (başkalarını elçi olarak) göndermedik. Hiç yeryüzünde dolaşmıyorlar mı, ki kendilerinden öncekilerin nasıl bir sona uğradıklarını görmüş olsunlar? Korkup-sakınanlar için ahiret yurdu elbette daha hayırlıdır. Siz yine de akıl erdirmeyecek misiniz?'),
(1706, 12, 110, 'Öyle ki elçiler, umutlarını kesip de, artık onların gerçekten yalanladıklarını sandıkları bir sırada onlara yardımımız gelmiştir; Biz kimi dilersek o kurtulmuştur. Suçlu-günahkarlar topluluğundan zorlu azabımız kesin olarak geri çevrilmeyecektir.'),
(1707, 12, 111, 'Andolsun, onların kıssalarında temiz akıl sahipleri için ibretler vardır. (Bu Kur\'an) düzüp uydurulacak bir söz değildir, ancak kendinden öncekilerin doğrulayıcısı, herşeyin \'çeşitli biçimlerde açıklaması\' ve iman edecek bir topluluk için bir hidayet ve rahmettir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Elif, Lam, Mim, Ra. Bunlar Kitab\'ın ayetleridir. Ve sana Rabbinden indirilen haktır. Ancak insanların çoğu iman etmezler.'),
(1709, 13, 2, 'Allah O\'dur ki, gökleri dayanak olmaksızın yükseltti; onları görmektesiniz. Sonra arşa istiva etti ve güneş ile aya boyun eğdirdi, her biri adı konulmuş bir süreye kadar akıp gitmektedirler. Her işi evirip düzenler, ayetleri birer birer açıklar. Umulur ki, Rabbinize kavuşacağınıza kesin bilgiyle inanırsınız.'),
(1710, 13, 3, 'Ve O, yeri yayıp uzatan, onda sarsılmaz-dağlar ve ırmaklar kılandır. Orada ürünlerin her birinden ikişer çift yaratmıştır; geceyi gündüze bürümektedir. Şüphesiz bunlarda düşünen bir topluluk için gerçekten ayetler vardır.'),
(1711, 13, 4, 'Yeryüzünde birbirine yakın komşu kıtalar vardır; üzüm bağları, ekinler, çatallı ve çatalsız hurmalıklar da vardır ki, bunlar aynı su ile sulanır; ama ürünlerinde (ki verimde ve lezzette) bazısını bazısına üstün kılıyoruz. Şüphesiz, bunlarda aklını kullanan bir topluluk için gerçekten ayetler vardır.'),
(1712, 13, 5, 'Eğer şaşıracaksan, asıl şaşkınlık konusu onların şöyle söylemeleridir: "Biz toprak iken mi, gerçekten biz mi yeniden yaratılacağız?" İşte onlar Rablerine karşı inkara sapanlar, işte onlar boyunlarına (ateşten) halkalar geçirilenler ve işte onlar -içinde ebedi kalacakları- ateşin arkadaşları olanlardır.'),
(1713, 13, 6, 'Onlar, iyilikten önce kötülüğü çabuklaştırmak istiyorlar; oysa onlardan önce nice örnekler gelip-geçmiştir. Ve şüphesiz, senin Rabbin, zulümlerine karşılık insanlar için bağışlama sahibidir ve şüphesiz senin Rabbin, cezası çok şiddetli olandır.'),
(1714, 13, 7, 'İnkar edenler derler ki: "Ona Rabbinden bir ayet (mucize) indirilseydi ya." Sen, yalnızca bir uyarıcısın ve her topluluk için bir hidayet önderisin.'),
(1715, 13, 8, 'Allah, her dişinin neyi yüklendiğini (neye hamile kaldığını) ve döl yataklarının neyi eksiltip neyi eklediğini bilir. O\'nun Katında herşey bir miktar (ölçü) iledir.'),
(1716, 13, 9, 'O, gaybı da, müşahede edileni de bilendir. Pek büyüktür, Yücedir.'),
(1717, 13, 10, 'Sizden sözü saklı tutan da, onu açığa vuran da, geceleyin gizlenen de ve gündüzün ortaklıkta gezen de (O\'nun Katında bilme bakımından) birdir.'),
(1718, 13, 11, 'O\'nun (insanın) önünden ve arkasından izleyenleri vardır, onu Allah\'ın emriyle gözetip-korumaktadırlar. Gerçekten Allah, kendi nefis (öz)lerinde olanı değiştirip bozuncaya kadar, bir toplulukta olanı değiştirip-bozmaz. Allah bir topluluğa kötülük istedi mi, artık onu geri çevirmeye hiçbir (biçimde imkan) yoktur; onlar için O\'ndan başka bir veli yoktur.'),
(1719, 13, 12, 'O size şimşeği korku ve umut olarak gösteren, (yağmur yüklü) ağırlaşmış bulutları (inşa edip) ortaya çıkarandır.'),
(1720, 13, 13, 'Gök gürültüsü O\'nu hamd ile, melekler de O\'na olan korkularından tesbih ederler.. O, yıldırımları gönderip bununla dilediğine çarpar; onlar ise Allah hakkında çekişip-tartışırlar. O, gücü (ve cezası) pek çetin olandır.'),
(1721, 13, 14, 'Hak olan çağrı (dua, ibadet) yalnızca O\'na (olan)dır. Onların Allah\'tan başka çağırdıkları ise, onlara hiçbir şeyle cevab veremezler. (Onların durumu) yalnızca, ağzına gelsin diye, iki avucunu suya uzatan(ın boşuna beklemesi) gibidir. Oysa ona gelmez. İnkar edenlerin duası, sapıklık içinde olmaktan başkası değildir.'),
(1722, 13, 15, 'Göklerde ve yerde her ne varsa -isteyerek de olsa, istemeyerek de olsa- Allah\'a secde eder. Sabah akşam gölgeleri de (O\'na secde eder).'),
(1723, 13, 16, 'De ki: "Göklerin ve yerin Rabbi kimdir?" De ki: "Allah\'tır." De ki: "Öyleyse, O\'nu bırakıp kendilerine bile yarar da, zarar da sağlamaya güç yetiremeyen birtakım veliler mi (tanrılar) edindiniz?" De ki: "Hiç görmeyen (a\'ma) ile gören (basiret sahibi) eşit olabilir mi? Veya karanlıklarla nur eşit olabilir mi?" Yoksa Allah\'a, O\'nun yaratması gibi yaratan ortaklar buldular da, bu yaratma, kendilerince birbirine mi benzeşti? De ki: "Allah, herşeyin Yaratıcısı\'dır ve O, tektir, kahredici olandır."'),
(1724, 13, 17, '(Allah) Gökten bir su indirdi de dereler kendi miktarınca çağlayıp aktı. Sel de yüze vuran bir köpük yüklendi. Bir süs veya bir meta sağlamak için ateşte üzerine yakıp-erittikleri şeyler (madenler)de de bunun gibi bir köpük (artık) vardır. İşte Allah, hak ile batıla böyle örnekler verir. Köpüğe gelince, o atılır gider, insanlara yarar sağlayacak şey ise, yeryüzünde kalır. İşte Allah örnekleri böyle vermektedir.'),
(1725, 13, 18, 'Rablerine icabet edenlere daha güzeli vardır. O\'na icabet etmeyenler ise, yeryüzündekilerin tümü ve bununla birlikte bir katı daha onların olsa mutlaka (kurtulmak için) bunu fidye olarak verirlerdi. Sorgulamanın en kötüsü onlar içindir. Onların barınma yerleri cehennemdir, ne kötü bir yaratıktır o!..'),
(1726, 13, 19, 'Peki, sana Rabbinden indirilenin gerçekten hak olduğunu bilen kişi, o görmeyen (a\'ma) gibi midir? Ancak temiz akıl sahipleri öğüt alıp-düşünebilirler.'),
(1727, 13, 20, 'Onlar Allah\'ın ahdini yerine getirirler ve verdikleri kesin sözü (misakı) bozmazlar.'),
(1728, 13, 21, 'Ve onlar Allah\'ın ulaştırılmasını emrettiği şeyi ulaştırırlar. Rablerinden içleri saygı ile titrer, kötü hesaptan korkarlar.'),
(1729, 13, 22, 'Ve onlar-Rablerinin yüzünü (hoşnutluğunu) isteyerek sabrederler, namazı dosdoğru kılarlar, kendilerine rızık olarak verdiklerimizden gizli ve açık infak ederler ve kötülüğü iyilikle savarlar. İşte onlar, bu yurdun (dünyanın güzel) sonucu (ahiret mutluluğu) onlar içindir.'),
(1730, 13, 23, 'Onlar, Adn cennetlerine girerler. Babalarından, eşlerinden ve soylarından \'salih davranışlarda\' bulunanlar da (Adn cennetlerine girer). Melekler onlara her bir kapıdan girip (şöyle derler:)'),
(1731, 13, 24, '"Sabrettiğinize karşılık selam size. (Dünya) Yurdun(un) sonu ne güzel."'),
(1732, 13, 25, 'Allah\'a verdikleri sözü, onu kesin olarak onayladıktan sonra bozanlar, Allah\'ın ulaştırılmasını emrettiği şeyi kesip-koparanlar ve yeryüzünde bozgunculuk çıkaranlar; işte onlar, lanet onlar içindir ve yurdun kötü olanı da onlar içindir.'),
(1733, 13, 26, 'Allah dilediğine rızkı genişletir-yayar ve daraltır da. Onlar ise dünya hayatına sevindiler. Oysaki dünya hayatı, ahirette (ki sınırsız mutluluk yanında geçici) bir meta\'dan başkası değildir.'),
(1734, 13, 27, 'İnkar edenler: "Ona Rabbinden bir ayet (mucize) indirilseydi ya!" derler. De ki: "Şüphesiz Allah, dilediğini şaşırtıp-saptırır, Kendisi\'ne katıksızca yöneleni de dosdoğru yola yöneltip-iletir."'),
(1735, 13, 28, 'Bunlar, iman edenler ve kalpleri Allah\'ın zikriyle mutmain olanlardır. Haberiniz olsun; kalpler yalnızca Allah\'ın zikriyle mutmain olur.'),
(1736, 13, 29, 'İman edip salih amellerde bulunanlar, ne mutlu onlara. Varılacak yerin güzel olanı (onlarındır).'),
(1737, 13, 30, 'Böylece Biz seni, kendisinden önce nice ümmetler gelip-geçmiş olan bir ümmete (elçi olarak) gönderdik; sana vahyettiklerimizi onlara okuyasın diye. Oysa onlar Rahman\'a nankörlük ediyorlar. De ki: "O, benim Rabbimdir, O\'ndan başka İlah yoktur. Ben O\'na tevekkül ettim ve son dönüş O\'nadır."'),
(1738, 13, 31, 'Eğer kendisiyle dağların yürütüldüğü, yerin parçalandığı veya ölülerin konuşturulduğu bir Kur\'an olsaydı (yine bu Kur\'an olurdu). Hayır, emrin tümü Allah\'ındır. İman edenler hala anlamadılar mı ki, eğer Allah dilemiş olsaydı, insanların tümünü hidayete erdirmiş olurdu. İnkar edenler, Allah\'ın va’di gelinceye kadar, yaptıkları dolayısıyla ya başlarına çetin bir bela çatacak veya yurtlarının yakınına inecek. Şüphesiz Allah, verdiği sözden dönmez. (Veya miadını şaşırmaz.)'),
(1739, 13, 32, 'Andolsun, senden önceki elçilerle de alay edildi, bunun üzerine Ben de o inkara sapanlara bir süre tanıdım, sonra onları (kıskıvrak) yakalayıverdim. İşte nasıldı sonuçlandırma?'),
(1740, 13, 33, 'Her nefsin bütün kazandıkları üzerinde gözetici olana mı (baş kaldırılır?) Onlar Allah\'a ortaklar koştular. De ki: "Bunları adlandırın (bakalım). Yoksa siz yeryüzünde bilmeyeceği bir şeyi O\'na haber mi veriyorsunuz? Yoksa sözün zahirine (veya boş ve süslü olanına)mi (kanıyorsunuz)? Hayır, inkar edenlere kendi hileli-düzenleri süslü-çekici gösterilmiştir ve onlar (doğru) yoldan alıkonulmuşlardır. Allah, kimi saptırırsa, artık onun için hiçbir yol gösterici yoktur.'),
(1741, 13, 34, 'Dünya hayatında onlar için bir azap vardır, ahiretin azabı ise daha zorludur. Onları Allah\'tan (kurtaracak) hiçbir koruyucu da yoktur.'),
(1742, 13, 35, 'Takva sahiplerine vadedilen cennet; onun altından ırmaklar akar, yemişleri ve gölgelikleri süreklidir. Bu korkup-sakınanların (mutlu) sonudur, inkar edenlerin sonu ise ateştir.'),
(1743, 13, 36, 'Kendilerine kitap verdiklerimiz, sana indirilen dolayısıyla sevinirler; fakat (Müslümanların aleyhinde birleşen) gruplardan, onun bazısını inkar edenler vardır. De ki: "Ben, yalnızca Allah\'a kulluk etmek ve O\'na ortak koşmamakla emrolundum. Ben ancak O\'na davet ederim ve son dönüşüm O\'nadır."'),
(1744, 13, 37, 'İşte böylece Biz onu (Kur\'an\'ı) Arapça bir hüküm olarak indirdik. Andolsun, sana gelen bu ilimden sonra, onların heva (istek ve tutku)larına uyacak olursan, senin için Allah\'tan ne bir yardımcı, dost, ne bir koruyucu vardır.'),
(1745, 13, 38, 'Andolsun, senden önce de elçiler gönderdik, onlara eşler ve çocuklar verdik. Allah\'ın izni olmaksızın (hiç)bir elçiye herhangi bir ayeti (mucizeyi) getirmek olacak iş değildi. Her ecel (tespit edilmiş süre) için bir kitap (yazı, hüküm, son) vardır.'),
(1746, 13, 39, 'Allah, dilediğini ortadan kaldırır ve bırakır. Kitabın anası O\'nun Katındadır.'),
(1747, 13, 40, 'Onlara (azap olarak) va\'dettiklerimizden bir kısmını sana göstersek de, senin hayatına son versek de, sana düşen yalnızca tebliğdir ve hesap da Bize aittir.'),
(1748, 13, 41, 'Onlar görmüyorlar mı ki, gerçekten Biz arza geliyor ve onu çevresinden eksiltiyoruz. Allah hüküm verir. O\'nun hükmünün peşine düşecek yoktur. Ve O, hesabı pek çabuk görendir.'),
(1749, 13, 42, 'Onlardan öncekiler de hileli-düzenler kurmuşlardı; fakat düzen kuruculuğun (tedbirlerin, karşılık vermelerin) tümü Allah\'a aittir. Her bir nefsin ne kazandığını O bilir. Bu yurdun sonu kimindir, inkar edenler pek yakında bileceklerdir.'),
(1750, 13, 43, 'O inkar edenler şöyle derler: "Sen gönderilmiş (Allah\'ın bir elçisi) değilsin." De ki: "Benimle sizin aranızda şahid olarak Allah yeter ve yanlarında kitabın ilmi bulunanlar da (bu gerçeği bilir)."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Elif, Lam, Ra. Bu bir Kitap\'tır ki, Rabbinin izniyle insanları karanlıklardan nura, O güçlü ve övgüye layık olanın yoluna çıkarman için sana indirdik.'),
(1752, 14, 2, 'O Allah ki, göklerde ve yerde ne varsa O\'nundur. Şiddetli azap dolayısıyla vay inkar edenlere.'),
(1753, 14, 3, 'Onlar, dünya hayatını ahirete tercih ederler. Allah\'ın yolundan alıkoyarlar ve onu çarpıtmak isterler (veya onda çarpıklık ararlar). İşte onlar, uzak bir sapıklık içindedirler.'),
(1754, 14, 4, 'Biz hiçbir elçiyi, kendi kavminin dilinden başkasıyla göndermedik ki, onlara apaçık anlatsın. Böylece Allah, dilediğini şaşırtıp saptırır, dilediğini hidayete erdirir. O, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(1755, 14, 5, 'Andolsun Musa\'yı: "Kavmini karanlıklardan nura çıkar ve onlara Allah\'ın günlerini hatırlat" diye ayetlerimizle göndermiştik. Şüphesiz bunda çokça sabreden ve şükreden herkes için gerçekten ayetler vardır.'),
(1756, 14, 6, 'Hani Musa kavmine şöyle demişti: "Allah\'ın üzerinizdeki nimetini hatırlayın; hani O sizi Firavun ailesinden kurtarmıştı, onlar sizi en dayanılmaz işkencelere uğratıyor, kadınlarınızı sağ bırakıp erkek çocuklarınızı boğazlıyorlardı. Bunda sizin için Rabbinizden büyük bir sınav vardır."'),
(1757, 14, 7, '"Rabbiniz şöyle buyurmuştu: “Andolsun, eğer şükrederseniz gerçekten size artırırım ve andolsun, eğer nankörlük ederseniz, şüphesiz, Benim azabım pek şiddetlidir."'),
(1758, 14, 8, 'Musa demişti ki: "Eğer siz ve yeryüzündekilerin tümü inkar edecek olsanız bile şüphesiz Allah hiçbir şeye muhtaç değildir, övülmüştür."'),
(1759, 14, 9, 'Sizden öncekilerin, Nuh kavminin, Ad ve Semud ile onlardan sonra gelenlerin haberi size gelmedi mi? Ki onları, Allah\'tan başkası bilmez. Elçileri onlara apaçık delillerle gelmişlerdi de, ellerini ağızlarına götürüp (öfkelerinden ısırdılar) ve dediler ki: "Tartışmasız, biz sizin kendisiyle gönderildiğiniz şeyleri inkar ettik ve bizi kendisine çağırdığınız şeyden de gerçekten kuşku verici bir tereddüt içindeyiz."'),
(1760, 14, 10, 'Resulleri dedi ki: "Allah hakkında mı şüphe (ediyorsunuz)? O, gökleri ve yeri yaratandır; O, sizi, günahlarınızı bağışlamak için davet etmekte ve sizi adı konulmuş bir süreye kadar erteliyor." Dediler ki: "Siz, bizim benzerimiz olan birer beşerden başkası değilsiniz. Siz bizi, babalarımızın taptıklarından çevirip-engellemek istiyorsunuz, öyleyse bize apaçık bir delil getirin."'),
(1761, 14, 11, 'Resulleri onlara dediler ki: "Doğrusu biz, sizin gibi yalnızca bir beşeriz, ancak Allah kullarından dilediğine lütufta bulunur. Allah\'ın izni olmaksızın size bir delil getirmemiz bizim için olacak şey değil. Mü\'minler, ancak Allah\'a tevekkül etmelidirler."'),
(1762, 14, 12, '"Bize ne oluyor ki, Allah\'a tevekkül etmeyelim? Bize doğru olan yolları O göstermiştir. Ve elbette bize yaptığınız işkencelere karşı sabredeceğiz. Tevekkül edenler Allah\'a tevekkül etmelidirler."'),
(1763, 14, 13, 'İnkar edenler, resullerine dediler ki: "Muhakkak (ya) sizi kendi toprağımızdan süreceğiz veya dinimize geri döneceksiniz." Böylelikle Rableri kendilerine vahyetti ki: "Şüphesiz Biz, zulmedenleri helak edeceğiz.'),
(1764, 14, 14, '"Ve onlardan sonra sizi o arza mutlaka yerleştireceğiz. İşte bu, makamımdan korkana ve tehdidimden korkana ait (bir ayrıcalıktır)."'),
(1765, 14, 15, '(Peygamberler) Fetih istediler, (sonunda) her zorba inatçı bozguna uğrayıp -yok oldu- gitti.'),
(1766, 14, 16, '(Böylesinin) Önünde cehennem vardır ve (orada) irinli sudan içirilecektir.'),
(1767, 14, 17, 'Yutkunmaya çabalayacak ve boğazından geçirmeyi başaramıyacak, ona her yandan ölüm gelecek, oysa ölmeyecek de. Ardından daha katı bir azap olacak.'),
(1768, 14, 18, 'Rablerini inkar edenlerin durumu şudur: Onların yaptıkları, fırtınalı bir günde rüzgarın şiddetle savurduğu bir kül gibidir. Kazandıklarından hiçbir şeye güç yetiremezler. İşte uzak bir sapıklık (içinde olmak) budur.'),
(1769, 14, 19, 'Allah\'ın gökleri ve yeri hak ile yarattığını görmüyor musunuz? Dilerse sizi giderir-yok eder ve yeni bir halk getirir.'),
(1770, 14, 20, 'Bu, Allah\'a göre güç değildir.'),
(1771, 14, 21, 'Onların tümü-toplanıp (kıyamette) Allah\'ın huzuruna çıktılar da zayıflar (müstaz\'aflar) büyüklük taslayanlara (müstekbirlere) dedi ki: "Şüphesiz, biz size tabi idik; şimdi siz, bizden Allah\'ın azabından herhangi bir şeyi önleyebiliyor musunuz?" Dediler ki: "Eğer Allah bize doğru yolu gösterseydi biz de sizlere doğru yolu gösterirdik. Şimdi yakınsak da, sabretsek de fark etmez, bizim için kaçacak bir yer yoktur."'),
(1772, 14, 22, 'İş hükme bağlanıp-bitince, şeytan der ki: "Doğrusu, Allah, size gerçek olan va\'di va\'detti, ben de size vaadde bulundum, fakat size yalan söyledim. Benim size karşı zorlayıcı bir gücüm yoktu, yalnızca sizi çağırdım, siz de bana icabet ettiniz. Öyleyse beni kınamayın, siz kendinizi kınayın. Ben sizi kurtaracak değilim, siz de beni kurtaracak değilsiniz. Doğrusu daha önce beni ortak koşmanızı da tanımamıştım. Gerçek şu ki, zalimlere acı bir azap vardır."'),
(1773, 14, 23, 'İman edip salih amellerde bulunanlar, Rablerinin izniyle altından ırmaklar akan, içinde ebedi kalacakları cennetlere konulmuşlardır. Orada birbirlerine olan dirlik temennileri: "Selam"dır.'),
(1774, 14, 24, 'Görmedin mi ki, Allah nasıl bir örnek vermiştir: Güzel bir söz, güzel bir ağaç gibidir ki, onun kökü sabit, dalı ise göktedir.'),
(1775, 14, 25, 'Rabbinin izniyle her zaman yemişini verir. Allah insanlar için örnekler verir; umulur ki onlar öğüt alır-düşünürler.'),
(1776, 14, 26, 'Kötü (murdar) söz ise, kötü bir ağaç gibidir. Onun kökü yerin üstünden koparılmış, kararı (yerinde durma, tutunma imkanı) kalmamıştır.'),
(1777, 14, 27, 'Allah, iman edenleri, dünya hayatında ve ahirette sapasağlam sözle sebat içinde kılar. Zalimleri de şaşırtıp-saptırır; Allah dilediğini yapar.'),
(1778, 14, 28, 'Allah\'ın bu nimetini inkara değiştirenleri ve kavimlerini \'yıkım ve azap\' yurduna konduranları görmedin mi?'),
(1779, 14, 29, '(Ki bu) Cehennemdir. Ona yaslanırlar. Ne kötü bir karar (yeridir) o!..'),
(1780, 14, 30, 'O\'nun yolundan saptırmak için Allah\'a eşler koştular. De ki: "Yararlanın. Çünkü elbette sizin varışınız ateşedir."'),
(1781, 14, 31, 'İman etmiş kullarıma söyle: "Alış-verişin ve dostluğun olmadığı o gün gelmezden evvel, dosdoğru namazı kılsınlar ve kendilerine rızık olarak verdiklerimizden gizli ve açık infak etsinler."'),
(1782, 14, 32, 'Allah, gökleri ve yeri yaratan ve gökten su indirip onunla size rızık olarak türlü ürünler çıkarandır. Ve onun emriyle gemileri, denizde yüzmeleri için size, emre amade kılandır. Irmakları da sizin için emre amade kılandır.'),
(1783, 14, 33, 'Güneş\'i ve Ay\'ı hareketlerinde sürekli emrinize amade kılan, geceyi ve gündüzü de emrinize amade kılandır.'),
(1784, 14, 34, 'Size her istediğiniz şeyi verdi. Eğer Allah\'ın nimetini saymaya kalkışırsanız, onu sayıp-bitirmeye güç yetiremezsiniz. Gerçek şu ki, insan pek zalimdir, pek nankördür.'),
(1785, 14, 35, 'Hani İbrahim şöyle demişti: "Bu şehri güvenli kıl, beni ve çocuklarımı putlara kulluk etmekten uzak tut."'),
(1786, 14, 36, '"Rabbim, gerçekten onlar insanlardan birçoğunu şaşırtıp-saptırdı. Bundan böyle kim bana uyarsa, artık o bendendir, kim bana isyan ederse elbette Sen, bağışlayansın, esirgeyensin."'),
(1787, 14, 37, '"Rabbimiz, gerçekten ben, çocuklarımdan bir kısmını Beyt-i Haram yanında ekini olmayan bir vadiye yerleştirdim; Rabbimiz, dosdoğru namazı kılsınlar diye (öyle yaptım), böylelikle Sen, insanların bir kısmının kalplerini onlara ilgi duyar kıl ve onları birtakım ürünlerden rızıklandır. Umulur ki şükrederler."'),
(1788, 14, 38, '"Rabbimiz, şüphesiz Sen, bizim saklı tuttuklarımızı da, açığa vurduklarımızı da bilirsin. Yerde ve gökte hiçbir şey Allah\'a gizli kalmaz."'),
(1789, 14, 39, '"Hamd, Allah\'a aittir ki, O, bana ihtiyarlığa rağmen İsmail\'i ve İshak\'ı armağan etti. Şüphesiz Rabbim, gerçekten duayı işitendir."'),
(1790, 14, 40, '"Rabbim, beni namazı(nda) sürekli kıl, soyumdan olanları da. Rabbimiz, duamı kabul buyur."'),
(1791, 14, 41, '"Rabbimiz, hesabın yapılacağı gün, beni, anne-babamı ve mü\'minleri bağışla"'),
(1792, 14, 42, '(Ey Muhammed,) Allah\'ı sakın zulmedenlerin yapmakta olduklarından habersiz sanma, onları yalnızca gözlerin dehşetle belireceği bir güne ertelemektedir.'),
(1793, 14, 43, 'Başlarını dikerek koşarlar, gözleri kendilerine dönüp-çevrilmez. Kalpleri (sanki) bomboştur.'),
(1794, 14, 44, 'Azabın kendilerine geleceği gün (ile) insanları uyarıp-korkut ki, (o gün) zulmedenler, şöyle diyecekler: "Bizi yakın bir süreye kadar ertele ki, Senin çağrına cevap verelim ve elçilere uyalım." Oysa daha önce, kendiniz için hiç zeval yoktur diye and içenler, sizler değil miydiniz?'),
(1795, 14, 45, 'Siz, kendi nefislerine zulmedenlerin yerleştikleri yerlerde oturmuştunuz. Onlara ne yaptığımız size açıklanmıştı ve size örnekler vermiştik.'),
(1796, 14, 46, 'Gerçek şu ki, onlar hileli-düzenler kurdular. Oysa onların düzenleri, dağları yerlerinden oynatacak da olsa, Allah Katında onlara hazırlanmış düzen (kötü bir karşılık) vardır.'),
(1797, 14, 47, 'Allah\'ı, sakın elçilerine verdiği sözden dönen sanma. Gerçekten Allah Azizdir, intikam sahibidir.'),
(1798, 14, 48, 'Yerin başka bir yere, göklerin de (başka göklere) dönüştürüldüğü gün, onlar tek olan, kahhar olan Allah\'ın huzuruna çıka(rıla)caklardır.'),
(1799, 14, 49, 'O gün suçlu-günahkarların (sıkı) bukağılara vurulduklarını görürsün.'),
(1800, 14, 50, 'Giyimleri katrandandır, yüzlerini ateş bürümektedir.'),
(1801, 14, 51, '(Bu azap,) Allah\'ın her nefsi kendi kazandığıyla cezalandırması içindir. Şüphesiz Allah, hesabı pek çabuk görendir.'),
(1802, 14, 52, 'İşte bu (Kur\'an) uyarılıp korkutulsunlar, gerçekten O\'nun yalnızca bir tek İlah olduğunu bilsinler ve temiz akıl sahipleri iyice öğüt alıp düşünsünler diye bir bildirip-duyurma (bir belağ)dır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Elif, Lam, Ra. Bunlar, Kitab\'ın ve apaçık olan Kur\'an\'ın ayetleridir.'),
(1804, 15, 2, 'O inkar edenler Müslüman olmayı nice kereler dileyecekler.'),
(1805, 15, 3, 'Onları bırak; yesinler, yararlansınlar ve onları (boş) emel oyalayadursun. İlerde bileceklerdir.'),
(1806, 15, 4, 'Biz, kendisi için bilinen (takdir edilmiş) bir kitap olmaksızın hiçbir ülkeyi yıkıma uğratmadık.'),
(1807, 15, 5, 'Hiçbir ümmet, kendi ecelini ne öne alabilir, ne de onlar ertelenebilirler.'),
(1808, 15, 6, 'Onlar: "Ey kendisine Kitap indirilen (Muhammed). Gerçekten sen cinlenmiş (bir deli)sin," dediler.'),
(1809, 15, 7, '"Eğer doğruyu söylüyor isen, bizlere melekleri getirmeli değil miydin?"'),
(1810, 15, 8, 'Hak olmaksızın Biz melekleri indirmeyiz. O zaman da onlara göz açtırılmaz.'),
(1811, 15, 9, 'Hiç şüphesiz, zikri (Kur\'an\'ı) Biz indirdik Biz; onun koruyucuları da gerçekten Biziz.'),
(1812, 15, 10, 'Andolsun, senden önce geçmiş topluluklara da elçiler gönderdik.'),
(1813, 15, 11, 'Onlara herhangi bir elçi gelmeyegörsün, mutlaka onunla alay ederlerdi.'),
(1814, 15, 12, 'Böylece Biz onu (alayı), suçlu-günahkarların kalplerine sokarız.'),
(1815, 15, 13, 'Onlar ona (indirilen kitaba) inanmazlar, oysaki evvelkilerin sünneti geçmiştir.'),
(1816, 15, 14, 'Onların üzerlerine gökyüzünden bir kapı açsak, ordan yukarı yükselseler de,'),
(1817, 15, 15, 'Mutlaka: "Gözlerimiz döndürüldü, belki biz büyülenmiş bir topluluğuz" diyeceklerdir.'),
(1818, 15, 16, 'Andolsun, gökte burçlar kıldık ve onu gözleyenler için süsledik.'),
(1819, 15, 17, 'Ve onu her kovulan şeytandan koruduk.'),
(1820, 15, 18, 'Ancak kulak hırsızlığı yapan olursa, onu da parlak bir ateş izler.'),
(1821, 15, 19, 'Yere (gelince,) onu döşeyip-yaydık, onda sarsılmaz-dağlar bıraktık ve onda herşeyden ölçüsü belirlenmiş ürünler bitirdik.'),
(1822, 15, 20, 'Ve orda sizler için ve kendisine rızık vericiler olmadığınız kimseler (varlıklar ve canlılar) için geçimlikler kıldık.'),
(1823, 15, 21, 'Hiçbir şey yoktur ki, hazineleri Bizim Katımız\'da olmasın; ancak onu belirlenmiş bir miktar olarak indiririz.'),
(1824, 15, 22, 'Ve aşılayıcılar olarak rüzgarları gönderdik, böylece gökten su indirdik de sizleri suladık. Oysa siz onun hazine-koruyucuları değilsiniz.'),
(1825, 15, 23, 'Şüphesiz Biz, gerçekten Biz yaşatır ve öldürürüz ve varis olanlar Biziz.'),
(1826, 15, 24, 'Andolsun sizden öne (veya önceden) geçenleri bilmişizdir; ve (yine) andolsun, geride kalanları da bilmişizdir.'),
(1827, 15, 25, 'Ve şüphesiz senin Rabbin, O, onları haşredecektir. Gerçekten O, hüküm ve hikmet sahibidir, bilendir.'),
(1828, 15, 26, 'Andolsun, insanı kuru bir çamurdan, şekillenmiş bir balçıktan yarattık.'),
(1829, 15, 27, 'Ve Cann\'ı da daha önce \'nüfuz eden kavurucu\' ateşten yaratmıştık.'),
(1830, 15, 28, 'Hani Rabbin meleklere demişti: "Ben, kuru bir çamurdan, şekillenmiş bir balçıktan bir beşer yaratacağım."'),
(1831, 15, 29, '"Ona bir biçim verdiğimde ve ona Ruhum\'dan üfürdüğümde hemen ona secde ederek (yere) kapanın."'),
(1832, 15, 30, 'Böylece meleklerin tümü, topluca secde etti.'),
(1833, 15, 31, 'Ancak İblis, secde edenlerle birlikte olmaktan kaçınıp-dayattı.'),
(1834, 15, 32, 'Dedi ki: "Ey İblis, sana ne oluyor, secde edenlerle birlikte olmadın?"'),
(1835, 15, 33, 'Dedi ki: "Ben, kuru bir çamurdan, şekillenmiş bir balçıktan yarattığın beşere secde etmek için var değilim."'),
(1836, 15, 34, 'Dedi ki: "Öyleyse ondan (cennetten) çık, çünkü sen kovulmuş-bulunmaktasın."'),
(1837, 15, 35, '"Ve şüphesiz, din gününe kadar lanet senin üzerinedir."'),
(1838, 15, 36, 'Dedi ki: "Rabbim, öyleyse onların dirileceği güne kadar bana süre tanı."'),
(1839, 15, 37, 'Dedi ki: "Öyleyse, sen (kendisine) süre tanınanlardansın."'),
(1840, 15, 38, '"Bilinen günün vaktine kadar."'),
(1841, 15, 39, 'Dedi ki: "Rabbim, beni kışkırttığın şeye karşılık, andolsun, ben de yeryüzünde onlara, (sana başkaldırmayı ve dünya tutkularını) süsleyip-çekici göstereceğim ve onların tümünü mutlaka kışkırtıp-saptıracağım."'),
(1842, 15, 40, '"Ancak onlardan muhlis olan kulların müstesna."'),
(1843, 15, 41, '(Allah) Dedi ki: "İşte bu, Bana göre dosdoğru olan yoldur."'),
(1844, 15, 42, '"Şüphesiz, kışkırtılıp-saptırılmışlardan sana uyanlar dışında, senin Benim kullarım üzerinde zorlayıcı hiçbir gücün yoktur."'),
(1845, 15, 43, '"Ve hiç şüphe yok, onların tümünün buluşma yeri cehennemdir."'),
(1846, 15, 44, 'Onun yedi kapısı vardır; onlardan her bir kapı için bir grup ayrılmıştır.'),
(1847, 15, 45, 'Gerçekten takva sahibi olanlar, cennetlerde ve pınar başlarındadır.'),
(1848, 15, 46, 'Oraya esenlikle ve güvenlikle girin.'),
(1849, 15, 47, 'Onların göğüslerinde kinden (ne varsa tümünü) sıyırıp-çektik, kardeşler olarak tahtlar üzerinde karşı karşıyadırlar.'),
(1850, 15, 48, 'Orda onlara hiçbir yorgunluk dokunmaz ve onlar ordan çıkarılacak değildirler.'),
(1851, 15, 49, 'Haber ver kullarıma; şüphesiz Ben, Ben bağışlayanım, esirgeyenim.'),
(1852, 15, 50, 'Ve şüphesiz azabım; o acıklı bir azaptır.'),
(1853, 15, 51, 'Onlara İbrahim\'in konuklarından haber ver.'),
(1854, 15, 52, 'Yanına girdiklerinde "Selam" demişlerdi. O da: "Biz sizden korkmaktayız" demişti.'),
(1855, 15, 53, 'Dediler ki: "Korkma biz sana bilgin bir çocuk müjdelemekteyiz."'),
(1856, 15, 54, 'Dedi ki: "Bana ihtiyarlık gelip-çökmüşken mi müjdeliyorsunuz? Beni ne ile müjdelemektesiniz?"'),
(1857, 15, 55, 'Dediler ki: "Seni gerçekle müjdeledik; öyleyse umut kesenlerden olma."'),
(1858, 15, 56, 'Dedi ki: "Sapıklar dışında Rabbinin rahmetinden kim umut keser?"'),
(1859, 15, 57, 'Dedi ki: "Ey elçiler, (bunun dışında, diğer) işiniz ne?"'),
(1860, 15, 58, 'Dediler ki: "Gerçekte biz, suçlu-günahkar olan bir topluluğa gönderildik."'),
(1861, 15, 59, '"Ancak Lut ailesi hariçtir; biz onların tümünü muhakkak kurtaracağız."'),
(1862, 15, 60, '"Ama karısını (kurtaracaklarımız) dışında tuttuk, o, geride kalanlardandır."'),
(1863, 15, 61, 'Böylelikle elçiler Lut ailesine geldiklerinde,'),
(1864, 15, 62, '(Lut) Dedi ki: "Sizler gerçekten tanınmamış bir topluluksunuz."'),
(1865, 15, 63, '"Hayır" dediler. "Biz sana, onların hakkında kuşkuya kapıldıkları şeyle geldik."'),
(1866, 15, 64, '"Sana gerçeği getirdik, biz şüphesiz doğru söyleyenleriz."'),
(1867, 15, 65, '"Hemen aileni gecenin bir bölümünde yola çıkar, sen de onların ardından git ve sizden hiç kimse arkasına bakmasın; emrolunduğunuz yere gidin."'),
(1868, 15, 66, 'Ve onlara şu emri verdik: "Sabaha çıkarlarken onların arkası mutlaka kesilecektir."'),
(1869, 15, 67, 'Şehir halkı birbirlerine müjdeler vererek geldi.'),
(1870, 15, 68, '(Lut onlara) "Bunlar benim konuğumdur, beni utandırıp-dillere düşürmeyin" dedi.'),
(1871, 15, 69, '"Allah\'tan korkup-sakının ve beni küçük düşürmeyin."'),
(1872, 15, 70, 'Dediler ki: "Biz seni \'herkes(in işin)e karışmaktan\' alıkoymamış mıydık?"'),
(1873, 15, 71, 'Dedi ki: "Eğer yapmak-istiyorsanız, işte bunlar, benim kızlarım."'),
(1874, 15, 72, 'Ömrüne andolsun ki, onlar, sarhoşlukları içinde kör-sersemdiler.'),
(1875, 15, 73, 'Derken, tan yerinin ağarma vaktine girdiklerinde onları (o korkunç ve dayanılmaz) çığlık yakalayıverdi.'),
(1876, 15, 74, 'Anında (yurtlarının) üstünü altına çevirdik ve üzerlerine balçıktan pişirilmiş taş yağdırdık.'),
(1877, 15, 75, 'Elbette bunda \'derin bir kavrayışa sahip olanlar\' için gerçekten ayetler vardır.'),
(1878, 15, 76, 'O (şehir de) gerçekten bir yol üstünde (hala) durmaktadır.'),
(1879, 15, 77, 'Elbette, bunda iman edenler için gerçekten ayetler vardır.'),
(1880, 15, 78, 'Eyke halkı da gerçekten zalim-kimselerdi.'),
(1881, 15, 79, 'Bundan dolayı onlardan intikam aldık; her ikisi de açıkça (gözler) ön(ün)dedir.'),
(1882, 15, 80, 'Andolsun, Hicr halkı da gönderilen(elçi)leri yalanlamışlardı.'),
(1883, 15, 81, 'Onlara ayetlerimizi vermiştik de ondan yüz çevirmişlerdi.'),
(1884, 15, 82, 'Dağlardan güvenli evler yontuyorlardı.'),
(1885, 15, 83, 'Derken, sabah vaktine girdiklerinde, onları o dayanılmaz-çığlık yakalayıverdi.'),
(1886, 15, 84, 'Buna rağmen kazandıkları şeyler, (uğrayacakları sondan kurtulmak için) onlara yetmedi.'),
(1887, 15, 85, 'Biz, gökleri, yeri ve her ikisinin arasındakilerini hakkın dışında (herhangi bir amaçla) yaratmadık. Hiç şüphesiz o saat de yaklaşarak-gelmektedir; öyleyse (onlara karşı) güzel davranışlarla davran.'),
(1888, 15, 86, 'Çünkü Rabbin, yaratan ve bilenin ta Kendisi\'dir.'),
(1889, 15, 87, 'Andolsun, sana çiftlerden yediyi ve büyük Kur\'an\'ı verdik.'),
(1890, 15, 88, 'Sakın onlardan bazılarını yararlandırdığımız şeylere gözünü dikme, onlara karşı hüzne kapılma, mü\'minler için de (şefkat) kanatlarını ger.'),
(1891, 15, 89, 'Ve de ki: "Şüphe yok, ben apaçık bir uyarıcıyım."'),
(1892, 15, 90, 'Parça ayırıcılarına indirdiğimiz gibi,'),
(1893, 15, 91, 'Ki onlar Kur\'anı parça-parça kıldılar.'),
(1894, 15, 92, 'Rabbine andolsun, onların tümüne (bunu) soracağız.'),
(1895, 15, 93, 'Yapmakta oldukları şeyleri.'),
(1896, 15, 94, 'Öyleyse sen emrolunduğun şeyi açıkça söyle ve müşriklere aldırış etme.'),
(1897, 15, 95, 'Şüphesiz o alay edenlere (karşı) Biz sana yeteriz.'),
(1898, 15, 96, 'Ki onlar, Allah ile beraber başka İlahları (ortak) kılmaktadırlar; onlar yakında bilip-öğreneceklerdir.'),
(1899, 15, 97, 'Andolsun, onların söylemekte olduklarına karşı senin göğsünün daraldığını biliyoruz.'),
(1900, 15, 98, 'Sen Rabbini hamd ile tesbih et ve secde edenlerden ol.'),
(1901, 15, 99, 'Ve yakîn sana gelinceye kadar Rabbine ibadet et.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Allah\'ın emri geldi, artık onda acele etmeyin. O (Allah), şirk koştukları şeylerden münezzeh ve Yücedir.'),
(1903, 16, 2, 'Kullarından dilediklerine, melekleri emrinden olan ruh ile indirir: Benden başka İlah yoktur, şu halde Benden korkup-sakının, diye uyarın."'),
(1904, 16, 3, 'Gökleri ve yeri hak ile yarattı: O, şirk koştukları şeylerden Yücedir.'),
(1905, 16, 4, 'İnsanı bir damla sudan yarattı, buna rağmen o, apaçık bir düşmandır.'),
(1906, 16, 5, 'Ve hayvanları da yarattı; sizin için onlarda ısınma ve yararlar vardır ve onlardan yemektesiniz.'),
(1907, 16, 6, 'Akşamları getirir, sabahları götürürken onlarda sizin için bir güzellik vardır.'),
(1908, 16, 7, 'Kendisine ulaşmadan canlarınızın yarısının telef olacağı şehirlere onlar, ağırlıklarınızı taşımaktadırlar. Şüphesiz sizin Rabbiniz şefkatli ve merhametlidir.'),
(1909, 16, 8, 'Onlara binmeniz ve süs için atları, katırları ve merkebleri (yarattı). Ve daha sizlerin bilmediğiniz neleri yaratmaktadır?'),
(1910, 16, 9, 'Yolu doğrultmak Allah\'a aittir, kimi (yollar) ise eğridir. Eğer o dileseydi, sizin tümünüzü elbette hidayete erdirirdi.'),
(1911, 16, 10, 'Sizin için gökten su indiren O\'dur; içecek ondan, ağaç ondandır (ki) hayvanlarınızı onda otlatmaktasınız.'),
(1912, 16, 11, 'Onunla sizin için ekin, zeytin, hurmalıklar, üzümler ve meyvelerin her türlüsünden bitirir. Şüphesiz bunda, düşünebilen bir topluluk için ayetler vardır.'),
(1913, 16, 12, 'Geceyi, gündüzü, güneşi ve ayı sizin emrinize verdi; yıldızlar da O\'nun emriyle emre hazır kılınmıştır. Şüphesiz bunda, aklını kullanabilen bir topluluk için ayetler vardır.'),
(1914, 16, 13, 'Yerde sizin için üretip-türettiği çeşitli renklerdekileri de (faydanıza verdi). Şüphesiz bunda, öğüt alıp düşünen bir topluluk için ayetler vardır.'),
(1915, 16, 14, 'Denizi de sizin emrinize veren O\'dur, ondan taze et yemektesiniz ve giyiminizde ondan süs-eşyaları çıkarmaktasınız. Gemilerin onda (suları) yara yara akıp gittiğini görüyorsun. (Bütün bunlar) O\'nun fazlından aramanız ve şükretmeniz içindir.'),
(1916, 16, 15, 'Sizi sarsıntıya uğratır diye yerde sarsılmaz dağlar bıraktı, ırmaklar ve yollar da (kıldı). Umulur ki doğru yolu bulursunuz.'),
(1917, 16, 16, 'Ve (başka) işaretler de (yarattı); onlar yıldız(lar)la da doğru yolu bulabilirler.'),
(1918, 16, 17, 'Yaratan, hiç yaratmayan gibi midir? Artık öğüt alıp-düşünmez misiniz?'),
(1919, 16, 18, 'Eğer Allah\'ın nimetini saymaya kalkışacak olursanız, onu bir genelleme yaparak bile sayamazsınız. Gerçekten Allah, bağışlayandır, esirgeyendir.'),
(1920, 16, 19, 'Allah, saklı tuttuklarınızı ve açığa vurduklarınızı bilir.'),
(1921, 16, 20, 'Allah\'tan başka yakardıkları hiçbir şeyi yaratamazlar, üstelik onlar yaratılıp durmaktadırlar.'),
(1922, 16, 21, 'Ölüdürler, diri değildirler; ne zaman dirileceklerinin şuuruna varamazlar.'),
(1923, 16, 22, 'Sizin İlahınız tek bir İlah\'tır. Ahirete inanmayanların kalpleri ise inkarcıdır ve onlar müstekbir (büyüklenmekte) olanlardır.'),
(1924, 16, 23, 'Şüphesiz Allah, onların saklı tuttuklarını ve açığa vurduklarını bilir; gerçekten O, müstekbirleri sevmez.'),
(1925, 16, 24, 'Onlara "Rabbiniz ne indirdi?" dendiğinde, "Eskilerin masalları" dediler.'),
(1926, 16, 25, 'Kıyamet gününde kendi günahlarının tümünü ve bilgisizce saptırdıklarının günahlarının bir kısmını yüklenmeleri için. Bak, ne kötü yük yükleniyorlar.'),
(1927, 16, 26, 'Onlardan öncekiler, hileli-düzenler kurmuşlardı da, Allah(ın azap emri) onların kurdukları yapıların temellerine geldi, böylece üstlerindeki tavan tepelerine çöktü; azap onlara şuurunda olmadıkları yerden gelmişti.'),
(1928, 16, 27, 'Sonra (Allah) kıyamet günü onları aşağılık kılacak ve diyecek ki: "Haklarında (mü\'minlere karşı) düşman kesildiğiniz ortaklarım hani nerede?" Kendilerine ilim verilenler, dediler ki: "Bugün, gerçekten aşağılanma ve kötülük kafirlerin üstünedir."'),
(1929, 16, 28, 'Ki melekler, kendi nefislerinin zalimleri olarak onların canlarını aldıklarında, "Biz hiçbir kötülük yapmıyorduk" diye teslim olurlar. Hayır, şüphesiz Allah, sizin neler yaptığınızı bilendir.'),
(1930, 16, 29, 'Öyleyse içinde ebedi kalıcılar olarak cehennemin kapılarından girin. Büyüklük taslayanların konaklama yeri ne kötüdür.'),
(1931, 16, 30, '(Allah\'tan) Sakınanlara: "Rabbiniz ne indirdi?" dendiğinde, "Hayır" dediler. Bu dünyada güzel davranışlarda bulunanlara güzellik vardır; ahiret yurdu ise daha hayırlıdır. Takva sahiplerinin yurdu ne güzeldir.'),
(1932, 16, 31, 'Adn Cennetleri; ona girerler, onun altından ırmaklar akar, içinde onların her diledikleri şey vardır. İşte Allah, takva sahiplerini böyle ödüllendirir.'),
(1933, 16, 32, 'Ki melekler, güzellikle canlarını aldıklarında: "Selam size" derler. "Yaptıklarınıza karşılık olmak üzere cennete girin."'),
(1934, 16, 33, '(Küfre sapanlar) Kendilerine meleklerin gelmesinden veya Rabbinin emrinin gelmesinden başka bir şey mi gözlüyorlar? Onlardan öncekiler de öyle yapmıştı. Allah onlara zulmetmedi, fakat onlar kendi nefislerine zulmediyorlardı.'),
(1935, 16, 34, 'Böylece işledikleri kötülükleri kendilerine isabet etti ve alaya aldıkları şey, kendilerini sarıp-kuşatıverdi.'),
(1936, 16, 35, 'Şirk koşmakta olanlar dediler ki: "Eğer Allah dileseydi, O\'nun dışında hiçbir şeye kulluk etmezdik, biz de, atalarımız da; ve O\'nsuz hiçbir şeyi haram kılmazdık." Onlardan öncekiler de böyle yapmıştı. Şu halde elçilere düşen apaçık bir tebliğden başkası mı?'),
(1937, 16, 36, 'Andolsun, Biz her ümmete: "Allah\'a kulluk edin ve tağuttan kaçının" (diye tebliğ etmesi için) bir elçi gönderdik. Böylelikle, onlardan kimine Allah hidayet verdi, onlardan kiminin üzerine sapıklık hak oldu. Artık, yeryüzünde dolaşın da yalanlayanların uğradıkları sonucu görün.'),
(1938, 16, 37, 'Sen, onların hidayet bulmalarını ne kadar tutkuyla istesen de, Allah, şüphesiz saptırdığına hidayet vermez, onlar için yardım edecek yoktur.'),
(1939, 16, 38, 'Olanca yeminleriyle: "Öleni Allah diriltmez" diye yemin ettiler. Hayır; bu, O\'nun üzerinde hak olan bir vaidtir, ancak insanların çoğu bilmezler.'),
(1940, 16, 39, 'Hakkında ihtilafa düştükleri şeyi onlara açıklaması ve inkar edenlerin kendilerinin yalancı olduklarını bilmesi için (diriltecektir).'),
(1941, 16, 40, 'Onu istediğimizde herhangi bir şey için sözümüz, ona yalnızca "Ol" demekten ibarettir; o da hemen oluverir.'),
(1942, 16, 41, 'Zulme uğratıldıktan sonra, Allah yolunda hicret edenleri dünyada şüphesiz güzel bir biçimde yerleştireceğiz; ahiret karşılığı ise daha büyüktür. Bilmiş olsalardı.'),
(1943, 16, 42, 'Onlar sabredenler ve Rablerine tevekkül edenlerdir.'),
(1944, 16, 43, 'Biz senden evvel kendilerine vahyettiğimiz erkeklerden başka (peygamberler) göndermedik. Eğer bilmiyorsanız, zikir ehline sorun.'),
(1945, 16, 44, '(Onları) Apaçık deliller ve kitaplarla (gönderdik). Sana da zikri (Kur\'an\'ı) indirdik ki, insanlara kendileri için indirileni açıklayasın ve onlar da iyice düşünsünler, diye.'),
(1946, 16, 45, 'Artık \'kötülüğü örgütleyip düzenleyenler\', Allah\'ın, kendilerini yerin dibine geçirmeyeceğinden veya şuuruna varamayacakları yerden azabın gelmeyeceğinden emin midirler?'),
(1947, 16, 46, 'Ya da onlar, dönüp-dolaşmaktalarken, onları yakalayıvermesinden (mi emindirler?) Ki onlar (bu konuda Allah\'ı) aciz bırakacak değildirler.'),
(1948, 16, 47, 'Veya onları bir korku üzerinde yakalayıvermesinden (mi emindirler)? Öyleyse Rabbin, gerçekten şefkatli ve merhamet sahibidir.'),
(1949, 16, 48, 'Allah\'ın herhangi bir şeyden yarattığına bakmıyorlar mı? Onun gölgeleri küçülerek sağdan ve soldan Allah\'a secde eder vaziyette döner.'),
(1950, 16, 49, 'Göklerde ve yerde olan ne varsa, canlılar ve melekler Allah\'a secde ederler ve onlar büyüklük taslamazlar.'),
(1951, 16, 50, 'Üstlerinden (her an bir azap göndermeye kadir olan) Rablerinden korkarlar ve emrolundukları şeyi yaparlar.'),
(1952, 16, 51, 'Allah dedi ki: "İki İlah edinmeyin: O, ancak tek bir İlah\'tır. Öyleyse Benden, yalnızca Benden korkun."'),
(1953, 16, 52, 'Göklerde ve yerde ne varsa O\'nundur, itaat-kulluk da (din de) sürekli olarak O\'nundur. Böyleyken Allah\'tan başkasından mı korkup-sakınıyorsunuz?'),
(1954, 16, 53, 'Nimet olarak size ulaşan ne varsa, Allah\'tandır, sonra size bir zarar dokunduğunda (yine) ancak O\'na yalvarmaktasınız.'),
(1955, 16, 54, 'Sonra sizden zararı kaldırdığında, sizden bir grup (hemen) Rablerine şirk koşar;'),
(1956, 16, 55, 'Kendilerine verdiklerimize karşı nankörlük etmek için. Öyleyse yararlanın, ilerde bileceksiniz.'),
(1957, 16, 56, 'Kendilerine rızık olarak verdiklerimizden, hiçbir şey bilmeyenlere paylar ayırıyorlar. Andolsun Allah\'a karşı düzmekte olduklarınızdan dolayı mutlaka sorguya çekileceksiniz.'),
(1958, 16, 57, 'Ve Allah\'a kızlar isnad ediyorlar, (haşa) O Yücedir. Hoşlandıkları (erkek çocuklar) da kendilerinindir.'),
(1959, 16, 58, 'Onlardan birine kız (çocuk) müjdelendiği zaman içi öfkeyle-taşarak yüzü simsiyah kesilir.'),
(1960, 16, 59, 'Kendisine verilen müjdenin kötülüğünden dolayı topluluktan gizlenir; onu aşağılanarak tutacak mı, yoksa toprağa gömecek mi? Bak, verdikleri hüküm ne kötüdür?'),
(1961, 16, 60, 'Ahirete inanmayanların kötü örnekleri vardır, en Yüce örnekler ise Allah\'a aittir. O, güç sahibi olandır, hüküm ve hikmet sahibidir.'),
(1962, 16, 61, 'Eğer Allah, insanları zulümleri nedeniyle sorguya çekecek olsaydı, onun üstünde (yeryüzünde) canlılardan hiçbir şey bırakmazdı; ancak onları adı konulmuş bir süreye kadar ertelemektedir. Onların ecelleri gelince ne bir saat ertelenebilirler, ne de öne alınabilirler.'),
(1963, 16, 62, 'Onlar, Allah\'a, hoşlarına gitmeyen şeyleri uygun görürler, dilleri de yalan olarak en güzel olanın \'kendilerinin olduğunu\' düzmektedir. Hiç şüphesiz ateş onlar içindir ve hiç şüphesiz onlar, (cehennemde) öncülerdir.'),
(1964, 16, 63, 'Andolsun Allah\'a, senden önceki ümmetlere de (elçiler) gönderdik, fakat şeytan onlara yapıp ettiklerini süslü göstermiştir; bugün de onların velisi odur ve onlar için acı bir azap vardır.'),
(1965, 16, 64, 'Biz kitabı ancak, hakkında ihtilafa düştükleri şeyi onlara açıklaman ve inanan bir kavme rahmet ve hidayet olması dışında (başka bir amaçla) indirmedik.'),
(1966, 16, 65, 'Allah gökten su indirdi, ölümünden sonra yeri onunla diriltti; işitebilen bir topluluk için bunda gerçekten bir ayet vardır.'),
(1967, 16, 66, 'Sizin için hayvanlarda da elbette ibretler vardır, size onların karınlarındaki fers (yarı sindirilmiş gıdalar) ile kan arasından, içenlerin boğazından kolaylıkla kayan dupduru bir süt içirmekteyiz.'),
(1968, 16, 67, 'Hurmalıkların ve üzümlüklerin meyvelerinden kurdukları çardaklarda hem sarhoşluk verici içki, hem güzel bir rızık edinmektesiniz. Şüphesiz aklını kullanabilen bir topluluk için, gerçekten bunda bir ayet vardır.'),
(1969, 16, 68, 'Rabbin bal arısına vahyetti: Dağlarda, ağaçlarda ve onların kurdukları çardaklarda kendine evler edin.'),
(1970, 16, 69, 'Sonra meyvelerin tümünden ye, böylece Rabbinin sana kolaylaştırdığı yollarda yürü-uçuver. Onların karınlarından türlü renklerde şerbetler çıkar, onda insanlar için bir şifa vardır. Şüphesiz düşünen bir topluluk için gerçekten bunda bir ayet vardır.'),
(1971, 16, 70, 'Allah sizi yarattı, sonra sizi öldürüyor, sizden kimi de, bildikten sonra bir şey bilmesin diye, ömrün en aşağı ucuna (yaşlılığa) geri çevrilir. Şüphesiz, Allah bilendir, herşeye güç yetirendir.'),
(1972, 16, 71, 'Allah rızıkta kiminizi kiminize üstün kıldı; üstün kılınanlar, rızıklarını ellerinin altında bulunanlara onda eşit olacak şekilde çevirip-verici değildirler. Şimdi Allah\'ın nimetini inkar mı ediyorlar?'),
(1973, 16, 72, 'Allah size kendi nefislerinizden eşler yarattı ve size eşlerinizden çocuklar ve torunlar yarattı ve sizi güzel şeylerden rızıklandırdı. Şimdi onlar, batıla mı inanıyorlar ve Allah\'ın nimetini inkar mı ediyorlar?'),
(1974, 16, 73, 'Allah\'ın dışında, kendileri için göklerden ve yerden hiçbir rızka, hiçbir şeye malik olmayan ve buna güçleri yetmeyen şeylere mi tapıyorlar?'),
(1975, 16, 74, 'Artık Allah\'a benzerler aramaya kalkışmayın; çünkü Allah bilir, siz ise bilmezsiniz.'),
(1976, 16, 75, 'Allah, (Kendisi\'ne ortak koştuğunuz ilahlar konusunda) hiçbir şeye gücü yetmeyen ve başkasının mülkünde olan ile, tarafımızdan kendisine güzel bir rızık verdiğimiz, böylelikle ondan gizli ve açık infak eden kimseyi örnek olarak gösterdi; bunlar hiç eşit olur mu? Hamd Allah\'ındır; fakat onların çoğu bilmezler.'),
(1977, 16, 76, 'Allah şu örneği verdi: İki kişi; bunlardan birisi dilsiz, hiçbir şeye gücü yetmez ve herşeyiyle efendisinin üstünde (bir yük), o, onu hangi yöne gönderse bir hayır getirmez; şimdi bu, adaletle emreden ve dosdoğru yol üzerinde bulunanla eşit olabilir mi?'),
(1978, 16, 77, 'Göklerin ve yerin gaybı Allah\'a aittir. (Kıyamet) Saatin(in) emri de yalnızca (süratli) göz açıp kapama gibidir veya daha yakındır. Şüphesiz, Allah herşeye güç yetirendir.'),
(1979, 16, 78, 'Allah, sizi annelerinizin karnından hiçbir şey bilmezken çıkardı ve umulur ki şükredersiniz diye işitme, görme (duyularını) ve gönüller verdi.'),
(1980, 16, 79, 'Göğün boşluğunda boyun eğdirilmiş (musahhar kılınmış) kuşları görmüyorlar mı? Onları (böyle boşlukta) Allah\'tan başkası tutmuyor. Şüphesiz, iman eden bir topluluk için bunda ayetler vardır.'),
(1981, 16, 80, 'Allah, size evlerinizi (içinde) "güvenlik ve huzur bulacağınız yerler" kıldı; ve size hayvan derilerinden hem göç gününde, hem yerleşme gününde kolaylıkla taşıyabileceğiniz evler; yünlerinden, yapağılarından ve kıllarından bir zamana kadar giyimlikler-döşemelikler ve (ticaret için) bir meta kıldı.'),
(1982, 16, 81, 'Allah, sizin için yarattığı şeylerden gölgeler kıldı. Dağlarda da sizin için barınaklar-siperler kıldı, sizi sıcaktan koruyacak elbiseler, sizi savaşınızda (zorluklara karşı) koruyacak giyimlikler de var etti. İşte O, üzerinizdeki nimetini böyle tamamlamaktadır, umulur ki teslim olursunuz.'),
(1983, 16, 82, 'Fakat onlar yüz çevirirlerse, sana düşen yalnızca apaçık bir tebliğdir.'),
(1984, 16, 83, 'Onlar, Allah\'ın nimetini biliyorlar, sonra da inkar ediyorlar; onların çoğu inkar edenlerdir.'),
(1985, 16, 84, 'Her ümmetten bir şahid göndereceğimiz gün; (artık ondan) sonra ne inkar edenlere (özür dilemeleri için) izin verilecek, ne (Allah\'tan) hoşnutluk dilekleri kabul edilecek.'),
(1986, 16, 85, 'O zulmedenler, azabı gördüklerinde, onlara ne (azap) hafifletilecek, ne süre tanınacak.'),
(1987, 16, 86, 'O şirk koşanlar, şirk koştuklarını gördükleri zaman: "Rabbimiz, Seni bırakıp bizim taptığımız ortaklarımız bunlardır" diyecekler. (Onlar da bunlara:) "Siz gerçekten yalan söyleyenlersiniz" diye sözü (geri çevirip) fırlatacaklar.'),
(1988, 16, 87, 'O gün (artık) Allah\'a teslim olmuşlardır ve uydurdukları (yalancı ilahlar) da onlardan çekilip-uzaklaşmıştır.'),
(1989, 16, 88, 'İnkar edip de Allah\'ın yolundan alıkoyanlar; Biz, işledikleri bozgunculuğa karşılık, onlara azap üstüne azap ilave ettik.'),
(1990, 16, 89, 'Her ümmet içinde kendi nefislerinden onların üzerine bir şahid getirdiğimiz gün, seni de onlar üzerinde bir şahid olarak getireceğiz. Biz Kitab\'ı sana, herşeyin açıklayıcısı, Müslümanlara bir hidayet, bir rahmet ve bir müjde olarak indirdik.'),
(1991, 16, 90, 'Şüphesiz Allah, adaleti, ihsanı, yakınlara vermeyi emreder; çirkin utanmazlıklardan (fahşadan), kötülüklerden ve zorbalıklardan sakındırır. Size öğüt vermektedir, umulur ki öğüt alıp-düşünürsünüz.'),
(1992, 16, 91, 'Ahidleştiğiniz zaman, Allah\'ın ahdini yerine getirin, pekiştirdikten sonra yeminleri bozmayın; çünkü Allah\'ı üzerinize kefil kılmışsınızdır. Şüphesiz Allah, yaptıklarınızı bilir.'),
(1993, 16, 92, 'Bir ümmet diğer bir ümmetten (sayıca ve malca) daha gelişkindir diye, yeminlerinizi kendi aranızda bir bozuculuk unsuru yaparak, ipini kuvvetle eğirdikten sonra bozup-çözen (kadın) gibi olmayın. Şüphesiz Allah, sizi bununla imtihan etmektedir. Kıyamet günü hakkında ihtilafa düştüğünüz şeyi size muhakkak açıklayacaktır.'),
(1994, 16, 93, 'Eğer Allah dileseydi, sizi tek bir ümmet kılardı; ancak dilediğini saptırır, dilediğini hidayete erdirir. Yaptıklarınızdan muhakkak sorumlu tutulacaksınız.'),
(1995, 16, 94, 'Yeminlerinizi kendi aranızda, bir bozuculuk unsuru edinmeyin; sonra sapasağlam basan ayak kayar ve Allah\'ın yolundan alıkoyduğunuz için kötülüğü tadarsınız. (Ayrıca) Büyük azap da sizin içindir.'),
(1996, 16, 95, 'Allah\'ın ahdini ucuz bir değere karşılık satmayın. Eğer bilirseniz, Allah Katında olan sizin için daha hayırlıdır.'),
(1997, 16, 96, 'Sizin yanınızda olan tükenir, Allah\'ın Katında olan ise kalıcıdır. Sabredenlerin karşılığını yaptıklarının en güzeliyle Biz muhakkak vereceğiz.'),
(1998, 16, 97, 'Erkek olsun, kadın olsun, bir mü\'min olarak kim salih bir amelde bulunursa, hiç şüphesiz Biz onu güzel bir hayatla yaşatırız ve onların karşılığını, yaptıklarının en güzeliyle muhakkak veririz.'),
(1999, 16, 98, 'Öyleyse Kur\'an okuduğun zaman, kovulmuş şeytandan Allah\'a sığın.'),
(2000, 16, 99, 'Gerçek şu ki, iman edenler ve Rablerine tevekkül edenler üzerinde onun (şeytanın) hiçbir zorlayıcı-gücü yoktur.'),
(2001, 16, 100, 'Onun zorlayıcı-gücü ancak onu veli edinenlerle, onunla O\'na (Allah\'a) ortak koşanlar üzerindedir.'),
(2002, 16, 101, 'Biz bir ayeti, bir (başka) ayetin yeriyle değiştirdiğimiz zaman, -Allah neyi indirdiğini daha iyi bilir.- “Sen yalnızca iftira edicisin" dediler. Hayır, onların çoğu bilmezler.'),
(2003, 16, 102, 'De ki: "İman edenleri sağlamlaştırmak, Müslümanlara bir müjde ve hidayet olmak üzere, onu (Kur\'an\'ı) hak olarak Rabbinden Ruhu\'l-Kudüs indirmiştir."'),
(2004, 16, 103, 'Andolsun ki Biz, onların: "Bunu kendisine ancak bir beşer öğretmektedir" dediklerini biliyoruz. Saparak kendisine yöneldikleri (kimse)nin dili a\'cemidir, bu ise açıkça Arapça olan bir dildir.'),
(2005, 16, 104, 'Allah\'ın ayetlerine inanmayanları Allah hidayete ulaştırmaz ve onlar için acı bir azap vardır.'),
(2006, 16, 105, 'Yalanı, yalnızca Allah\'ın ayetlerine inanmayanlar uydurur. İşte yalancıların asıl kendileri onlardır.'),
(2007, 16, 106, 'Kim imanından sonra Allah\'a (karşı) inkara sapıp da, -kalbi imanla tatmin bulmuş olduğu halde baskı altında zorlanan hariç- inkara göğüs açarsa, işte onların üstünde Allah\'tan bir gazab vardır ve büyük azap onlarındır.'),
(2008, 16, 107, 'Bu, onların dünya hayatını ahirete göre daha sevimli bulmalarından ve şüphesiz Allah\'ın da inkar eden bir topluluğu hidayete erdirmemesi nedeniyledir.'),
(2009, 16, 108, 'Onlar, Allah\'ın, kalplerini, kulaklarını ve gözlerini mühürlediği kimselerdir. Gafil olanlar onların ta kendileridir.'),
(2010, 16, 109, 'Şüphesiz, onlar ahirette ziyana uğrayanlardır.'),
(2011, 16, 110, 'Sonra gerçekten Rabbin, işkenceye uğratıldıktan sonra hicret edenlerin, ardından cihad edip, sabredenlerin (destekçisidir). Şüphesiz senin Rabbin, bundan sonra da gerçekten bağışlayandır, esirgeyendir.'),
(2012, 16, 111, 'O gün, herkes kendi nefsi adına mücadele eder ve herkese yaptığının karşılığı eksiksiz ödenir. Onlar zulme uğratılmazlar.'),
(2013, 16, 112, 'Allah bir şehri örnek verdi: (Halkı) Güvenlik ve huzur içindeydi, rızkı da her yerden bol bol gelmekteydi; fakat Allah\'ın nimetlerine nankörlük etti, böylece Allah yaptıklarına karşılık olarak, ona açlık ve korku elbisesini tattırdı.'),
(2014, 16, 113, 'Andolsun, onlara kendi içlerinden bir elçi gelmişti, fakat onu yalanladılar; böylece onlar, zulümlerine devam etmektelerken azap onları yakalayıverdi.'),
(2015, 16, 114, 'Öyleyse Allah\'ın sizi rızıklandırdığı şeylerden helal (ve) temiz olanlarını yiyin; eğer O\'na kulluk ediyorsanız Allah\'ın nimetine şükredin.'),
(2016, 16, 115, 'O, size ancak ölüyü, kanı, domuz etini ve Allah\'tan başkası adına kesilmiş olan (hayvan)ı haram kıldı. Fakat kim mecbur kalırsa, saldırmamak ve sınırı aşmamak üzere (yiyebilir). Çünkü gerçekten Allah, bağışlayandır, esirgeyendir.'),
(2017, 16, 116, 'Dillerinizin yalan yere nitelendirmesi dolayısıyla şuna helal, buna haram demeyin. Çünkü Allah\'a karşı yalan uydurmuş olursunuz. Şüphesiz Allah\'a karşı yalan uyduranlar kurtuluşa ermezler.'),
(2018, 16, 117, '(Bu dünyada olup-biten) Pek az bir metadır. Onlara ise acı bir azap vardır.'),
(2019, 16, 118, 'Yahudi olanlara da, bundan önce sana aktardıklarımızı haram kıldık. Biz onlara zulmetmedik, ancak onlar kendi nefislerine zulmediyorlardı.'),
(2020, 16, 119, 'Sonra gerçekten Rabbin, cehalet sonucu kötülük işleyen, sonra bunun ardından tevbe eden ve ıslah olanlar(la beraberdir). Şüphesiz Rabbin bundan sonra bağışlayandır, esirgeyendir.'),
(2021, 16, 120, 'Gerçek şu ki, İbrahim (tek başına) bir ümmetti; Allah\'a gönülden yönelip itaat eden bir muvahhiddi ve o müşriklerden değildi.'),
(2022, 16, 121, 'O\'nun nimetlerine şükrediciydi. (Allah) Onu seçti ve doğru yola iletti.'),
(2023, 16, 122, 'Ve Biz ona dünyada bir güzellik verdik; şüphesiz o, ahirette de salih olanlardandır.'),
(2024, 16, 123, 'Sonra sana vahyettik: "Hanif (muvahhid) olan İbrahim\'in dinine uy. O, müşriklerden değildi."'),
(2025, 16, 124, 'Cumartesi, ancak onda ihtilafa düşenlere (farz) kılındı. Şüphesiz Rabbin, onların ihtilaf ettikleri şeyler hakkında kıyamet günü aralarında hükmedecektir.'),
(2026, 16, 125, 'Rabbinin yoluna hikmetle ve güzel öğütle çağır ve onlarla en güzel bir biçimde mücadele et. Şüphesiz senin Rabbin yolundan sapanı bilendir ve hidayete ereni de bilendir.'),
(2027, 16, 126, 'Eğer ceza verecekseniz, size verilen cezanın misliyle ceza verin ve eğer sabrederseniz, andolsun bu, sabredenler için daha hayırlıdır.'),
(2028, 16, 127, 'Sabret; senin sabrın ancak Allah(ın yardımı) iledir. Onlar için hüzne kapılma ve kurmakta oldukları hileli-düzenlerden dolayı sıkıntıya düşme.'),
(2029, 16, 128, 'Şüphesiz Allah korkup-sakınanlarla ve iyilik edenlerle beraberdir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Bir kısım ayetlerimizi kendisine göstermek için, kulunu bir gece Mescid-i Haram\'dan, çevresini bereketlendirdiğimiz Mescid-i Aksa\'ya götüren O (Allah) Yücedir. Gerçekten O, işitendir, görendir.'),
(2031, 17, 2, 'Musa\'ya kitap verdik ve "Benden başka vekil edinmeyin" diye onu İsrailoğulları\'na kılavuz kıldık.'),
(2032, 17, 3, '(Ey) Nuh ile birlikte taşıdıklarımızın çocukları! Şüphesiz o, şükreden bir kuldu.'),
(2033, 17, 4, 'Kitapta İsrailoğulları\'na şu hükmü verdik: "Muhakkak siz yer(yüzün) de iki defa bozgunculuk çıkaracaksınız ve muhakkak büyük bir kibirleniş-yükselişle kibirlenecek-yükseleceksiniz.'),
(2034, 17, 5, 'Nitekim o ikiden ilk-vaid geldiği zaman, oldukça zorlu olan kullarımızı üzerinize gönderdik de (sizi) evlerin aralarına kadar girip araştırdılar. Bu yerine getirilmesi gereken bir sözdü.'),
(2035, 17, 6, 'Sonra onlara karşı size tekrar \'güç ve kuvvet verdik\', size mallar ve çocuklarla yardım ettik ve topluluk olarak sizi sayıca çok kıldık.'),
(2036, 17, 7, 'Eğer iyilik ederseniz kendinize iyilik etmiş olursunuz ve eğer kötülük ederseniz o da (kendi) aleyhinizedir. Sonunda vaad geldiği zaman, (yine öyle kullar göndeririz ki) yüzlerinizi \'kötü duruma soksunlar\', birincisinde ona girdikleri gibi mescid (Kudüs)e girsinler ve ele geçirdiklerini \'darmadağın edip mahvetsinler.\''),
(2037, 17, 8, 'Umulur ki, Rabbiniz size merhamet eder, fakat siz (bozgunculuğa) dönerseniz Biz de (sizi aşağılık kılmaya ve cezalandırmaya) döneriz. Biz, cehennemi kafirler için bir kuşatma yeri kıldık.'),
(2038, 17, 9, 'Şüphesiz, bu Kur\'an, en doğru yola iletir ve salih amellerde bulunan mü\'minlere, onlar için gerçekten büyük bir ecir olduğunu müjde verir.'),
(2039, 17, 10, 'Ve şüphesiz, ahirete inanmayanlar için de acı bir azap hazırlamışızdır.'),
(2040, 17, 11, 'İnsan hayra dua ettiği gibi, şerre de dua etmektedir. İnsan, pek acelecidir.'),
(2041, 17, 12, 'Biz geceyi ve gündüzü iki ayet kıldık; gece ayetini sildik de Rabbinizden bir fazl aramanız, yılların sayısını ve hesabı öğrenmeniz için gündüzün ayetini aydınlatıcı kıldık. Biz, herşeyi yeterince açıkladık.'),
(2042, 17, 13, 'Biz, her insanın kuşunu (işlediklerini, yaptıklarını) kendi boynuna doladık, kıyamet gününde onun için açılmış olarak önüne konacak bir kitap çıkarırız.'),
(2043, 17, 14, '"Kendi kitabını oku; bugün nefsin hesap sorucu olarak sana yeter."'),
(2044, 17, 15, 'Kim hidayete ererse, kendi nefsi için hidayete erer; kim de saparsa kendi aleyhine sapar. Hiçbir günahkar, bir başkasının günah yükünü yüklenmez. Biz, bir elçi gönderinceye kadar (hiçbir topluma) azap edecek değiliz.'),
(2045, 17, 16, 'Biz, bir ülkeyi helak etmek istediğimiz zaman, onun \'varlık ve güç sahibi önde gelenlerine\' emrederiz, böylelikle onlar onda bozgunculuk çıkarırlar. Artık onun üzerine söz hak olur da, onu kökünden darmadağın ederiz.'),
(2046, 17, 17, 'Biz, Nuh\'tan sonra nice kuşakları yıkıma uğrattık. Kullarının günahlarını haber alıcı, görücü olarak Rabbin yeter,'),
(2047, 17, 18, 'Kim çarçabuk olanı (geçici dünya arzularını) isterse, orada istediğimiz kimseye dilediğimizi çabuklaştırırız, sonra ona cehennemi (yurt) kılarız; ona, kınanmış ve kovulmuş olarak gider.'),
(2048, 17, 19, 'Kim de ahireti ister ve bir mü\'min olarak ciddi bir çaba göstererek ona çalışırsa, işte böylelerinin çabası şükre şayandır.'),
(2049, 17, 20, 'Hepsine, onlara da, bunlara da Rabbinin ihsanından \'arttırarak-veririz.\' Rabbinin ihsanı kesilmiş değildir.'),
(2050, 17, 21, 'Onlardan kimini kimine nasıl üstün tuttuğumuzu gör. Muhakkak ahiret dereceler bakımından daha büyüktür, üstünlük bakımından da daha büyüktür.'),
(2051, 17, 22, 'Allah ile beraber başka ilahlar edinme, yoksa kınanmış ve kendi başına (yapayalnız ve yardımcısız) bırakılmış olursun.'),
(2052, 17, 23, 'Rabbin, O\'ndan başkasına kulluk etmemenizi ve anne-babaya iyilikle-davranmayı emretti. Şayet onlardan biri veya ikisi senin yanında yaşlılığa ulaşırsa, onlara: "Öf" bile deme ve onları azarlama; onlara güzel söz söyle.'),
(2053, 17, 24, 'Onlara acıyarak alçakgönüllülük kanadını ger ve de ki: "Rabbim, onlar beni küçükken nasıl terbiye ettilerse Sen de onları esirge."'),
(2054, 17, 25, 'Rabbiniz, sizin içinizdekini daha iyi bilir. Eğer siz salih olursanız, şüphesiz O da, (Kendisi\'ne) yönelip dönenleri bağışlayıcıdır.'),
(2055, 17, 26, 'Akrabaya hakkını ver, yoksula ve yolda kalmışa da. İsraf ederek saçıp-savurma.'),
(2056, 17, 27, 'Çünkü saçıp-savuranlar, şeytanın kardeşleri olmuşlardır; şeytan ise Rabbine karşı nankördür.'),
(2057, 17, 28, 'Eğer Rabbinden ummakta olduğun bir rahmeti beklerken (darlıkta olduğundan) onlara sırt çevirecek olursan, bu durumda onlara yumuşak söz söyle.'),
(2058, 17, 29, 'Elini boynunda bağlanmış olarak kılma, büsbütün de açık tutma. Sonra kınanır, hasret (pişmanlık) içinde kalakalırsın.'),
(2059, 17, 30, 'Şüphesiz senin Rabbin, rızkı dilediğine -genişletir- yayar ve daraltır. Gerçekten O, kullarından haberi olandır, görendir.'),
(2060, 17, 31, 'Yoksulluk endişesiyle çocuklarınızı öldürmeyin; onlara ve size Biz rızık veririz. Şüphesiz, onları öldürmek büyük bir hata (suç ve günah)dır.'),
(2061, 17, 32, 'Zinaya yaklaşmayın, gerçekten o, \'çirkin bir hayasızlık\' ve kötü bir yoldur.'),
(2062, 17, 33, 'Haklı bir neden olmaksızın Allah\'ın haram kıldığı bir kimseyi öldürmeyin. Kim mazlum olarak öldürülürse onun velisine yetki vermişizdir; o da öldürmede ölçüyü aşmasın. Çünkü o, gerçekten yardım görmüştür.'),
(2063, 17, 34, 'Erginlik çağına erişinceye kadar, -o da en güzel bir tarz olması- dışında yetimin malına yaklaşmayın. Ahde vefa gösterin. Çünkü ahid bir sorumluluktur.'),
(2064, 17, 35, 'Ölçtüğünüz zaman ölçüyü tam tutun ve dosdoğru bir tartıyla tartın; bu, daha hayırlıdır ve sonuç bakımından daha güzeldir.'),
(2065, 17, 36, 'Hakkında bilgin olmayan şeyin ardına düşme; çünkü kulak, göz ve kalp, bunların hepsi ondan sorumludur.'),
(2066, 17, 37, 'Yeryüzünde böbürlenerek yürüme; çünkü sen ne yeri yarabilirsin, ne dağlara boyca ulaşabilirsin.'),
(2067, 17, 38, 'Bütün bunlar, kötülüğü olan, Rabbinin Katında da hoş olmayanlardır.'),
(2068, 17, 39, 'Bunlar, Rabbinin sana hikmet olarak vahyettiği şeylerdir. Rabbin ile beraber başka ilahlar kılma, yoksa yerilmiş, kovulmuş olarak cehenneme bırakılırsın.'),
(2069, 17, 40, 'Rabbiniz size erkekleri seçti de meleklerden dişileri mi (Kendine) edindi? Gerçekten siz büyük bir söz söylemektesiniz.'),
(2070, 17, 41, 'Andolsun, Biz bu Kur\'an\'da çeşitli açıklamalar yaptık, öğüt alıp-düşünsünler diye. Oysa bu, onların daha uzaklaşmalarından başkasını arttırmıyor.'),
(2071, 17, 42, 'De ki: "Eğer söyledikleri gibi O\'nunla beraber ilahlar olsaydı, onlar arşın sahibine mutlaka bir yol ararlardı."'),
(2072, 17, 43, 'O, onların dediklerinden münezzeh, Yüce ve büyük bir yükseklikle yüksektir.'),
(2073, 17, 44, 'Yedi gök, yer ve bunların içindekiler O\'nu tesbih eder; O\'nu övgü ile tesbih etmeyen hiçbir şey yoktur, ancak siz onların tesbihlerini kavramıyorsunuz. Şüphesiz O, halim olandır, bağışlayandır.'),
(2074, 17, 45, 'Kur\'an okuduğun zaman seninle ahirete inanmayanlar arasında görünmez bir perde kıldık.'),
(2075, 17, 46, 'Ve onların kalpleri üzerine, onu kavrayıp anlamalarını engelleyen kabuklar, kulaklarına da bir ağırlık koyduk. Sen Kur\'an\'da sadece Rabbini "bir ve tek" (İlah olarak) andığın zaman, \'nefretle kaçar vaziyette\' gerisin geriye giderler.'),
(2076, 17, 47, 'Biz onların seni dinlediklerinde ne için dinlediklerini, gizli konuşmalarında da o zalimlerin: "Siz büyülenmiş bir adamdan başkasına uymuyorsunuz" dediklerini çok iyi biliriz.'),
(2077, 17, 48, 'Sana nasıl örnekler vererek saptıklarına bir bak, artık onların bir yola güçleri yetmemektedir.'),
(2078, 17, 49, 'Dediler ki: "Biz kemikler haline geldikten, toprak olup ufalandıktan sonra mı, gerçekten biz mi yeni bir yaratılışla diriltileceğiz?"'),
(2079, 17, 50, 'De ki: "İster taş olun, ister demir."'),
(2080, 17, 51, '"Ya da göğüslerinizde büyümekte olan (veya büyüttüğünüz) bir yaratık (olun)." Bizi kim (hayata) geri çevirebilir" diyecekler. De ki: "Sizi ilk defa yaratan." Bu durumda sana başlarını alaylıca sallayacaklar ve diyecekler ki: "Ne zamanmış o?" De ki: "Umulur ki pek yakında."'),
(2081, 17, 52, 'Sizi çağıracağı gün, O\'na övgüyle icabet edecek ve (dünyada) pek az bir süre kaldığınızı sanacaksınız.'),
(2082, 17, 53, 'Kullarıma, sözün en güzel olanını söylemelerini söyle. Çünkü şeytan aralarını açıp bozmaktadır. Şüphesiz şeytan insanın açıkça bir düşmanıdır.'),
(2083, 17, 54, 'Sizi en iyi Rabbiniz bilir; dilerse size merhamet eder, dilerse sizi azaplandırır. Biz seni onların üzerine bir vekil olarak göndermedik.'),
(2084, 17, 55, 'Rabbin, göklerde ve yerde olan herkesi en iyi bilir. Andolsun, Biz peygamberlerin bir kısmını bir kısmına üstün kıldık ve Davud\'a da Zebur verdik.'),
(2085, 17, 56, 'De ki: "O\'nun dışında (ilah olarak) öne sürdüklerinizi çağırın, onlar sizden ne zararı uzaklaştırabilirler, ne de (onu yararınıza) dönüştürebilirler.'),
(2086, 17, 57, 'Onların taptıkları da, -hangisi daha yakındır diye- Rablerine (yaklaşmak için) bir vesile arıyorlar. O\'nun rahmetini umuyorlar ve azabından korkuyorlar. Şüphesiz senin Rabbinin azabı korkunçtur.'),
(2087, 17, 58, 'Hiçbir ülke (veya şehir) olmasın ki, kıyamet gününden önce Biz onu (ya) bir yıkıma uğratacağız veya onu şiddetli bir azapla azaplandıracağız; bu (muhakkak) o kitapta yazılıdır.'),
(2088, 17, 59, 'Bizi ayet (mucize)ler göndermekten, öncekilerin onu yalanlamasından başka bir şey alıkoymadı. Semud\'a dişi deveyi görünür (bir mucize) olarak gönderdik, fakat onlar bununla (onu boğazlamakla) zulmetmiş oldular. Oysa Biz ayetleri ancak korkutmak için göndeririz.'),
(2089, 17, 60, 'Hani Biz sana: "Muhakkak Rabbin insanları çepeçevre kuşatmıştır" demiştik. Sana gösterdiğimiz o rüyayı insanları denemek için yaptık, Kur\'an\'da lanetlenmiş ağacı da. Biz onları korkutuyoruz. Fakat (bu) onlarda büyük bir azgınlıktan başka bir şey arttırmıyor.'),
(2090, 17, 61, 'Hani, meleklere: "Adem\'e secde edin" demiştik. İblis\'in dışında (hepsi) secde etmişlerdi. Demişti ki: "Bir çamur olarak yarattığın kimseye ben secde eder miyim?"'),
(2091, 17, 62, 'Demişti ki: "Şu bana karşı yücelttiğine bir bak; andolsun, eğer bana kıyamet gününe kadar süre tanırsan, onun soyunu -pek az dışında- kuşkusuz kendime bağlı kılacağım.'),
(2092, 17, 63, 'Demişti ki: "Git, onlardan kim sana uyarsa, şüphesiz sizin cezanız cehennemdir; eksiksiz bir ceza."'),
(2093, 17, 64, '"Onlardan güç yetirdiklerini sesinle sarsıntıya uğrat, atlıların ve yayalarınla onların üstüne yaygarayı kopar, mallarda ve çocuklarda onlara ortak ol ve onlara çeşitli vaadlerde bulun." Şeytan, onlara aldatmadan başka bir şey vadetmez.'),
(2094, 17, 65, '"Benim kullarım; senin onlar üzerinde hiçbir zorlayıcı gücün (hakimiyetin) yoktur." Vekil olarak Rabbin yeter.'),
(2095, 17, 66, 'Sizin Rabbiniz, fazlından aramanız için denizde gemileri sizin için yürütür. Gerçekten O, size karşı merhametli olandır.'),
(2096, 17, 67, 'Size denizde bir sıkıntı (tehlike) dokunduğu zaman, O\'nun dışında taptıklarınız kaybolur-gider; fakat karaya (çıkarıp) sizi kurtarınca (yine) sırt çevirirsiniz. İnsan pek nankördür.'),
(2097, 17, 68, 'Kara tarafında sizi yerin dibine geçirmeyeceğinden veya üzerinize taş yığınları yüklü bir kasırga göndermeyeceğinden emin misiniz? Sonra kendinize bir vekil bulamazsınız.'),
(2098, 17, 69, 'Veya sizi bir kere daha ona (denize) gönderip üzerinize kırıp geçiren bir fırtına salarak nankörlük etmeniz nedeniyle sizi batırmasına karşı emin misiniz? Sonra onun öcünü Bize karşı alacak (kimseyi de) bulamazsınız.'),
(2099, 17, 70, 'Andolsun, Biz Ademoğlunu yücelttik; onları karada ve denizde (çeşitli araçlarla) taşıdık, temiz, güzel şeylerden rızıklandırdık ve yarattıklarımızın bir çoğundan üstün kıldık.'),
(2100, 17, 71, 'Her insan-grubunu imamlarıyla çağıracağımız gün, artık kimin kitabı sağ eline verilirse, onlar kitaplarını okuyacaklar ve onlar, bir \'hurma çekirdeğindeki iplikçik kadar\' bile haksızlığa uğratılmazlar.'),
(2101, 17, 72, 'Kim bunda (dünyada) kör ise, O, ahirette de kördür ve yol bakımından daha \'şaşkın bir sapıktır.\''),
(2102, 17, 73, 'Onlar neredeyse, sana vahyettiğimizden başkasını Bize karşı düzüp uydurman için seni fitneye düşüreceklerdi; o zaman seni dost edineceklerdi.'),
(2103, 17, 74, 'Eğer Biz seni sağlamlaştırmasaydık, andolsun, onlara az bir şey (de olsa) eğilim gösterecektin.'),
(2104, 17, 75, 'Bu durumda, Biz sana, hayatın da kat kat, ölümün de kat kat (acısını) tattırırdık; sonra Bize karşı bir yardımcı bulamazdın.'),
(2105, 17, 76, 'Neredeyse seni (bu) yerden (yurdundan) çıkarmak için tedirgin edeceklerdi; bu durumda kendileri de senden sonra az bir süreden başka kalamazlar.'),
(2106, 17, 77, '(Bu,) Senden önce gönderdiğimiz resullerimizin bir sünnetidir. Sünnetimizde bir değişiklik bulamazsın.'),
(2107, 17, 78, 'Güneşin sarkmasından gecenin kararmasına kadar namazı kıl, fecir vakti (namazda okunan) Kur\'an\'ı, işte o, şahid olunandır.'),
(2108, 17, 79, 'Gecenin bir kısmında kalk, sana aid nafile olarak onunla (Kur\'an\'la) namaz kıl. Umulur ki Rabbin seni övülmüş bir makama ulaştırır.'),
(2109, 17, 80, 'Ve de ki: "Rabbim, beni (girilecek yere) doğru bir girdirişle girdir ve (çıkarılacak yerden) doğru bir çıkarışla çıkar ve Katından bana yardımcı bir kuvvet ver."'),
(2110, 17, 81, 'De ki: "Hak geldi, batıl yok oldu. Hiç şüphesiz batıl yok olucudur."'),
(2111, 17, 82, 'Kur\'an\'dan mü\'minler için şifa ve rahmet olan şeyleri indiriyoruz. Oysa o, zalimlere kayıplardan başkasını arttırmaz.'),
(2112, 17, 83, 'İnsana bir nimet verdiğimizde sırt çevirir ve yan çizer; ona bir şer dokunduğu zaman da umutsuzluğa kapılır.'),
(2113, 17, 84, 'De ki: "Herkes kendi yaratılışına (fıtrat tarzına) göre davranır. Şu halde kimin daha doğru yolda olduğunu Rabbin daha iyi bilir."'),
(2114, 17, 85, 'Sana ruhtan sorarlar; de ki: "Ruh, Rabbimin emrindendir, size ilimden yalnızca az bir şey verilmiştir."'),
(2115, 17, 86, 'Andolsun, eğer dilersek, sana vahyettiklerimizi gerçekten gideriveririz, sonra bunun için Bize karşı bir vekil bulamazsın.'),
(2116, 17, 87, '(Vahyi sende bırakan) Rabbin rahmetinden başka(sı değildir). Şüphesiz O\'nun lütfu senin üzerinde çok büyüktür.'),
(2117, 17, 88, 'De ki: "Eğer bütün ins ve cin (toplulukları), bu Kur\'an\'ın bir benzerini getirmek üzere toplansa, -onların bir kısmı bir kısmına destekçi olsa bile- onun bir benzerini getiremezler."'),
(2118, 17, 89, 'Andolsun, bu Kur\'an\'da her örnekten insanlar için çeşitli açıklamalarda bulunduk. İnsanların çoğu ise ancak inkarda ayak direttiler.'),
(2119, 17, 90, 'Dediler ki: "Bize yerden pınarlar fışkırtmadıkça sana kesinlikle inanmayız."'),
(2120, 17, 91, '"Ya da sana ait hurmalıklardan ve üzümlerden bir bahçe olup aralarından şarıl şarıl akan ırmaklar fışkırtmalısın."'),
(2121, 17, 92, '"Veya öne sürdüğün gibi, gökyüzünü üstümüze parça parça düşürmeli ya da Allah\'ı ve melekleri karşımıza (şahid olarak) getirmelisin."'),
(2122, 17, 93, '"Yahut altından bir evin olmalı veya gökyüzüne yükselmelisin. Üzerimize bizim okuyabileceğimiz bir kitap indirinceye kadar senin yükselişine de inanmayız." De ki: "Rabbimi yüceltirim; ben, elçi olan bir beşerden başkası mıyım?"'),
(2123, 17, 94, 'Kendilerine hidayet geldiği zaman, insanları inanmaktan alıkoyan şey, onların: "Allah, elçi olarak bir beşeri mi gönderdi?" demelerinden başkası değildir.'),
(2124, 17, 95, 'De ki: "Eğer yeryüzünde (insan değil de) tatmin bulmuş yürüyen melekler olsaydı, Biz de onlara gökten elçi olarak elbette melek gönderirdik."'),
(2125, 17, 96, 'De ki: "Benimle aranızda şahid olarak Allah yeter; kuşkusuz O, kullarından gerçeğiyle haberdardır, görendir."'),
(2126, 17, 97, 'Allah, kimi hidayete erdirirse, işte o, hidayet bulmuştur, kimi saptırırsa onlar için O\'nun dışında asla veliler bulamazsın. Kıyamet günü, Biz onları yüzükoyun körler, dilsizler ve sağırlar olarak haşrederiz. Onların barınma yerleri cehennemdir; ateşi sükun buldukça, çılgın alevini onlara arttırırız.'),
(2127, 17, 98, 'Bu, şüphesiz, onların ayetlerimizi inkar etmelerine ve: "Biz kemikler haline geldikten, toprak olup ufalandıktan sonra mı, gerçekten biz mi yeni bir yaratılışla diriltileceğiz?" demelerine karşılık cezalarıdır.'),
(2128, 17, 99, 'Görmüyorlar mı; gökleri ve yeri yaratan Allah, onların benzerini yaratmaya gücü yeter ve onlar için kendisinde şüphe olmayan bir süre (ecel) kılmıştır. Zulmedenler ise ancak inkarda ayak direttiler.'),
(2129, 17, 100, 'De ki: "Eğer siz Rabbimin rahmet hazinelerine malik olsaydınız, bu durumda harcama endişesiyle gerçekten (cimrilik edip elinizde) tutardınız. İnsan pek cimridir.'),
(2130, 17, 101, 'Andolsun, Biz Musa\'ya apaçık dokuz ayet (mucize) vermiştik; işte İsrailoğulları\'na sor; onlara geldiği zaman Firavun ona: "Gerçekten ben seni büyülenmiş sanıyorum" demişti.'),
(2131, 17, 102, 'O da: "Andolsun, bunları görülecek belgeler olarak göklerin ve yerin Rabbinden başkasının indirmediğini sen de bilmişsin; gerçekten ben de seni yıkılmış-harab olmuş sanıyorum" demişti.'),
(2132, 17, 103, 'Böylelikle, onları o yerden sürüp-sarsıntıya uğratmayı istedi, Biz de onu ve beraberindekileri hep birlikte boğuverdik.'),
(2133, 17, 104, 'Ve onun ardından İsrailoğulları\'na söyledik: "O toprak (yurt)ta oturun, ahiret va\'di geldiğinde hepinizi derleyip-toplayacağız."'),
(2134, 17, 105, 'Biz onu (Kur\'an\'ı) hak olarak indirdik ve o hak ile indi; seni de yalnızca bir müjde verici ve uyarıp-korkutucu olarak gönderdik.'),
(2135, 17, 106, 'Onu bir Kur\'an olarak, insanlara dura dura okuman için (bölüm bölüm) ayırdık ve onu safha safha bir indirme ile indirdik.'),
(2136, 17, 107, 'De ki: "İster ona inanın, ister inanmayın: O, daha önce kendilerine ilim verilenlere okunduğu zaman, çenelerinin üstüne kapanarak secde ederler."'),
(2137, 17, 108, 'Ve derler ki: "Rabbimiz Yücedir, Rabbimiz\'in va\'di gerçekten gerçekleşmiş bulunuyor."'),
(2138, 17, 109, 'Çeneleri üstüne kapanıp ağlıyorlar ve (Kur\'an) onların huşu (saygı dolu korku)larını arttırıyor.'),
(2139, 17, 110, 'De ki: "Allah, diye çağırın, \'Rahman\' diye çağırın, ne ile çağırırsanız; sonunda en güzel isimler O\'nundur." Namazında sesini çok yükseltme, çok da kısma, bu ikisi arasında (orta) bir yol benimse.'),
(2140, 17, 111, 'Ve de ki: "Övgü (hamd), çocuk edinmeyen, mülkte ortağı olmayan ve düşkünlükten dolayı yardımcıya da (ihtiyacı) bulunmayan Allah\'adır." Ve O\'nu tekbir edebildikçe tekbir et.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Hamd, Kitab\'ı kulu üzerine indiren ve onda hiçbir çarpıklık kılmayan Allah\'a aittir.'),
(2142, 18, 2, 'Dosdoğru (bir Kitap\'tır) ki, Kendi Katından şiddetli bir azapla uyarıp-korkutmak ve salih amellerde bulunan mü\'minlere müjde vermek için (onu indirdi); şüphesiz onlara güzel bir ecir vardır.'),
(2143, 18, 3, 'Onlar orda ebedi olarak kalıcıdırlar.'),
(2144, 18, 4, '(Bu Kur\'an) "Allah çocuk edindi" diyenleri uyarıp-korkutur.'),
(2145, 18, 5, 'Bu konuda ne kendilerinin, ne atalarının hiçbir bilgisi yoktur. Ağızlarından çıkan söz ne (kadar da) büyük. Onlar yalandan başkasını söylemiyorlar.'),
(2146, 18, 6, 'Şimdi onlar bu söze (Kur\'an\'a) inanmayacak olurlarsa Sen, onların peşi sıra esef ederek kendini kahredeceksin (öyle mi)?'),
(2147, 18, 7, 'Şüphesiz Biz, yeryüzü üzerindeki şeyleri ona bir süs kıldık; onların hangisinin daha güzel davranışta bulunduğunu deneyelim diye.'),
(2148, 18, 8, 'Biz gerçekten (yeryüzü) üzerinde olanları kupkuru-çorak bir toprak yapabiliriz.'),
(2149, 18, 9, 'Sen, yoksa Kehf ve Rakim Ehlini Bizim şaşılacak ayetlerimizden mi sandın?'),
(2150, 18, 10, 'O gençler, mağaraya sığındıkları zaman, demişlerdi ki: "Rabbimiz, Katından bize bir rahmet ver ve işimizden bize doğruyu kolaylaştır (bizi başarılı kıl).'),
(2151, 18, 11, 'Böylelikle mağarada yıllar yılı onların kulaklarına vurduk (derin bir uyku verdik).'),
(2152, 18, 12, 'Sonra iki gruptan hangisinin kaldıkları süreyi daha iyi hesap ettiğini belirtmek için onları uyandırdık.'),
(2153, 18, 13, 'Biz sana onların haberlerini bir gerçek (olay) olarak aktarıyoruz. Gerçekten onlar Rablerine iman etmiş gençlerdi ve Biz de onların hidayetlerini artırmıştık.'),
(2154, 18, 14, 'Onların kalpleri üzerinde (sabrı ve kararlılığı) rabtetmiştik; (Krala karşı) Kıyam ettiklerinde demişlerdi ki: "Bizim Rabbimiz, göklerin ve yerin Rabbidir; İlah olarak biz O\'ndan başkasına kesinlikle tapmayız, (eğer tersini) söyleyecek olursak, andolsun, gerçeğin dışına çıkarız."'),
(2155, 18, 15, '"Şunlar, bizim kavmimizdir; O\'ndan başkasını ilahlar edindiler, onlara apaçık bir delil getirmeleri gerekmez miydi? Öyleyse Allah\'a karşı yalan uydurup iftira düzenden daha zalim kimdir?"'),
(2156, 18, 16, '(İçlerinden biri demişti ki:) "Madem ki siz onlardan ve Allah\'tan başka taptıklarından kopup-ayrıldınız, o halde, (dağlara çekilip) mağaraya sığının da Rabbiniz size rahmetinden (bolca bir miktarını) yaysın ve işinizden size bir yarar kolaylaştırsın."'),
(2157, 18, 17, '(Onlara baktığında) Görürsün ki, güneş doğduğunda mağaralarına sağ yandan yönelir, battığında onları sol yandan keser-geçerdi ve onlar da onun (mağaranın) geniş boşluğundalardı. Bu, Allah\'ın ayetlerindendir. Allah, kime hidayet verirse, işte hidayet bulan odur, kimi saptırırsa onun için asla doğru-yolu gösterici bir veli bulamazsın.'),
(2158, 18, 18, 'Sen onları uyanık sanırsın, oysa onlar (derin bir uykuda) uyuşmuşlardır. Biz onları sağ yana ve sol yana çeviriyorduk. Köpekleri de iki kolunu uzatmış yatıyordu. Onları görmüş olsaydın, geri dönüp onlardan kaçardın, onlardan içini korku kaplardı.'),
(2159, 18, 19, 'Böylece, aralarında bir sorgulama yapsınlar diye onları dirilttik (uyandırdık). İçlerinden bir sözcü dedi ki: "Ne kadar kaldınız?" Dediler ki: "Bir gün veya günün bir (kaç saatlik) kısmı kadar kaldık." Dediler ki: "Ne kadar kaldığınızı Rabbiniz daha iyi bilir; şimdi birinizi bu paranızla şehre gönderin de, hangi yiyecek temizse baksın, size ondan bir rızık getirsin; ancak oldukça nazik davransın ve sakın sizi kimseye sezdirmesin."'),
(2160, 18, 20, '"Çünkü onlar üzerinize çıkıp gelirlerse, sizi taşa tutarlar veya dinlerine geri çevirirler; bu durumda ebedi olarak kurtuluş bulamazsınız."'),
(2161, 18, 21, 'Böylece, Allah\'ın va\'dinin hak olduğunu ve gerçekten kıyametin, kendisinde şüphe bulunmadığını bilmeleri için (şehir halkına ve sonraki insan kuşaklarına) onları buldurmuş olduk. (Onları görenler) Kendi aralarında durumlarını tartışıyorlardı, (bir kısmı) dedi ki: "Onların üstüne bir bina inşa edin, Rableri onları daha iyi bilir." Onların işine galip gelen (sözleri geçen)ler ise: "Üstlerine mutlaka bir mescid yapmalıyız" dediler.'),
(2162, 18, 22, '(Sonra gelen kuşaklar) Diyecekler ki: "Üç\'tüler, onların dördüncüsü köpekleridir." Ve: "Beştiler, onların altıncısı köpekleridir" diyecekler. (Bu,) Bilinmeyene (gayba) taş atmaktır. "Yedidirler, onların sekizincisi köpekleridir" diyecekler. De ki: "Rabbim, onların sayısını daha iyi bilir, onları pek az (insan) dışında kimse bilemez." Öyleyse onlar konusunda açıkta olan bir tartışmadan başka tartışma ve onlar hakkında bunlardan hiç kimseye bir şey sorma.'),
(2163, 18, 23, 'Hiçbir şey hakkında: "Ben bunu yarın mutlaka yapacağım" deme.'),
(2164, 18, 24, 'Ancak: "Allah dilerse" (inşaAllah yapacağım de). Unuttuğun zaman Rabbini zikret ve de ki: "Umulur ki, Rabbim beni bundan daha yakın bir başarıya yöneltip-iletir."'),
(2165, 18, 25, 'Onlar mağaralarında üç yüz yıl kaldılar ve dokuz (yıl) daha kattılar.'),
(2166, 18, 26, 'De ki: "Ne kadar kaldıklarını Allah daha iyi bilir. Göklerin ve yerin gaybı O\'nundur. O, ne güzel görmekte ve ne güzel işitmektedir. O\'nun dışında onların bir velisi yoktur. Kendi hükmünde hiç kimseyi ortak kılmaz."'),
(2167, 18, 27, 'Sana Rabbinin kitabından vahyedileni oku. O\'nun sözlerini değiştirici yoktur ve O\'nun dışında kesin olarak bir sığınacak (makam) bulamazsın.'),
(2168, 18, 28, 'Sen de sabah akşam O\'nun rızasını isteyerek Rablerine dua edenlerle birlikte sabret. Dünya hayatının (aldatıcı) süsünü isteyerek gözlerini onlardan kaydırma. Kalbini Bizi zikretmekten gaflete düşürdüğümüz, kendi \'istek ve tutkularına (hevasına)\' uyan ve işinde aşırılığa gidene itaat etme.'),
(2169, 18, 29, 'Ve de ki: "Hak Rabbinizdendir; artık dileyen iman etsin, dileyen inkar etsin. Şüphesiz Biz zalimlere bir ateş hazırlamışız, onun duvarları kendilerini çepeçevre kuşatmıştır. Eğer onlar yardım isterlerse, katı bir sıvı gibi yüzleri kavurup-yakan bir su ile yardım edilirler. Ne kötü bir içkidir o ve ne kötü bir destektir.'),
(2170, 18, 30, 'Şüphesiz iman edip salih amellerde bulunanlar ise; Biz gerçekten en güzel davranışta bulunanın ecrini kayba uğratmayız.'),
(2171, 18, 31, 'Onlar; altından ırmaklar akan Adn cennetleri onlarındır, orada altın bileziklerle süslenirler, hafif ipekten ve ağır işlenmiş atlastan yeşil elbiseler giyerler ve tahtlar üzerinde kurulup-dayanırlar. (Bu,) Ne güzel sevap ve ne güzel destek.'),
(2172, 18, 32, 'Onlara iki adamın örneğini ver; onlardan birine iki üzüm bağı verdik ve ikisini hurmalıklarla donattık, ikisinin arasında da ekinler bitirmiştik.'),
(2173, 18, 33, 'İki bağ da yemişlerini vermiş, ondan (verim bakımından) hiçbir şeyi noksan bırakmamış ve aralarında bir ırmak fışkırtmıştık.'),
(2174, 18, 34, '(İkisinden) Birinin başka ürün (veren yer)leri de vardı. Böylelikle onunla konuşurken arkadaşına dedi ki: “Ben, mal bakımından senden daha zenginim, insan sayısı bakımından da daha güçlüyüm.”'),
(2175, 18, 35, 'Kendi nefsinin zalimi olarak (böylece) bağına girdi (ve): "Bunun sonsuza kadar kuruyup-yok olacağını sanmıyorum" dedi.'),
(2176, 18, 36, '"Kıyamet-saatinin kopacağını da sanmıyorum. Buna rağmen Rabbime döndürülecek olursam, şüphesiz bundan daha hayırlı bir sonuç bulacağım."'),
(2177, 18, 37, 'Kendisiyle konuşmakta olan arkadaşı ona dedi ki: "Seni topraktan, sonra bir damla sudan yaratan, sonra da seni düzgün (eli ayağı tutan, gücü kuvveti yerinde) bir adam kılan (Allah)ı inkar mı ettin?"'),
(2178, 18, 38, '"Fakat, O Allah benim Rabbimdir ve ben Rabbime hiç kimseyi ortak koşmam."'),
(2179, 18, 39, '"Bağına girdiğin zaman, \'MaşaAllah, Allah\'tan başka kuvvet yoktur\' demen gerekmez miydi? Eğer beni mal ve çocuk bakımından senden daha az (güçte) görüyorsan."'),
(2180, 18, 40, '"Belki Rabbim senin bağından daha hayırlısını bana verir, (seninkinin) üstüne gökten \'yakıp-yıkan bir afet\' gönderir de kaygan bir toprak kesiliverir."'),
(2181, 18, 41, '"Veya onun suyu dibe göçüverir de böylelikle onu arayıp-bulmaya kesinlikle güç yetiremezsin."'),
(2182, 18, 42, '(Derken) Onun ürünleri (afetlerle) kuşatılıverdi. Artık o, uğrunda harcadıklarına karşı avuçlarını (esefle) oğuşturuyordu. O (bağın) çardakları yıkılmış durumdaydı, kendisi de şöyle diyordu: "Keşke Rabbime hiç kimseyi ortak koşmasaydım."'),
(2183, 18, 43, 'Allah\'ın dışında ona yardım edecek bir topluluk yoktu, kendi kendine de yardım edemedi.'),
(2184, 18, 44, 'İşte burada (bu durumda) velayet (yardımcılık, dostluk) hak olan Allah\'a aittir. O, sevap bakımından hayırlı, sonuç bakımından hayırlıdır.'),
(2185, 18, 45, 'Onlara, dünya hayatının örneğini ver; gökten indirdiğimiz suya benzer, onunla yeryüzünün bitkileri birbirine karıştı, böylece rüzgarların savurduğu çalı-çırpı oluverdi. Allah, herşeyin üzerinde güç yetirendir.'),
(2186, 18, 46, 'Mal ve çocuklar, dünya hayatının çekici-süsüdür; sürekli olan \'salih davranışlar\' ise, Rabbinin Katında sevap bakımından daha hayırlıdır, umut etmek bakımından da daha hayırlıdır.'),
(2187, 18, 47, 'Dağları yürüteceğimiz gün, yeri çırılçıplak (dümdüz olmuş) görürsün; onları birarada toplamışız da, içlerinden hiçbirini dışarda bırakmamışızdır.'),
(2188, 18, 48, 'Onlar senin Rabbine sıra sıra sunulmuşlardır. Andolsun, siz ilk defa yarattığımız gibi Bize gelmiş oldunuz. Hayır, Bizim size bir kavuşma-zamanı tespit etmediğimizi sanmıştınız değil mi?'),
(2189, 18, 49, '(Önlerine) Kitap konulmuştur; artık suçlu-günahkarların, onda olanlardan dolayı dehşetle-korkuya kapıldıklarını görürsün. Derler ki: "Eyvahlar bize, bu kitaba ne oluyor ki, küçük büyük bırakmayıp herşeyi sayıp-döküyor?" Yapıp-ettiklerini (önlerinde) hazır bulmuşlardır. Rabbin hiç kimseye zulmetmez.'),
(2190, 18, 50, 'Hani meleklere: "Adem\'e secde edin" demiştik; İblis\'in dışında (diğerleri) secde etmişlerdi. O cinlerdendi, böylelikle Rabbinin emrinden dışarı çıkmıştı. Bu durumda Beni bırakıp onu ve onun soyunu veliler mi edineceksiniz? Oysa onlar sizin düşmanlarınızdır. (Bu,) Zalimler için ne kadar kötü bir (tercih) değiştirmedir.'),
(2191, 18, 51, 'Göklerin ve yerin yaratılışında da, kendi nefislerinin yaratılışında da Ben onları şahid tutmadım. Ben, saptırıcıları yardımcı-güç de edinmedim.'),
(2192, 18, 52, '(Kafirlere) "Benim ortaklarım sandığınız şeyleri çağırın" diyeceği gün; işte onları çağırmışlardır, ama onlar, kendilerine cevap vermemişlerdir. Biz onların aralarında bir uçurum koyduk.'),
(2193, 18, 53, 'Suçlu-günahkarlar ateşi görmüşlerdir, artık içine kendilerinin gireceklerini de anlamışlardır; ancak ondan bir kaçış yolu bulamamışlardır.'),
(2194, 18, 54, 'Andolsun, bu Kur\'an\'da insanlar için Biz her örnekten çeşitli açıklamalarda bulunduk. İnsan, herşeyden çok tartışmacıdır.'),
(2195, 18, 55, 'Kendilerine hidayet geldiği zaman insanları inanmaktan ve Rablerinden bağışlanma dilemelerinden alıkoyan şey, ancak evvelkilerin sünnetinin kendilerine de gelmesi veya azabın onları karşılarcasına gelmesi(ni beklemeleri)dir.'),
(2196, 18, 56, 'Biz elçileri, müjde vericiler ve uyarıcılar olmak dışında (başka bir amaçla) göndermeyiz. İnkar edenler ise, hakkı batıl ile geçersiz kılmak için mücadele ediyorlar. Onlar Benim ayetlerimi ve uyarıldıklarını (azabı) alay konusu edindiler.'),
(2197, 18, 57, 'Kendisine Rabbinin ayetleri öğütle hatırlatıldığı zaman, sırt çeviren ve ellerinin önden gönderdikleri (amelleri)ni unutandan daha zalim kimdir? Biz gerçekten, kalpleri üzerine onu kavrayıp anlamalarını engelleyen bir perde (gerdik), kulaklarına bir ağırlık koyduk. Sen onları hidayete çağırsan bile, onlar sonsuza kadar asla hidayet bulamazlar.'),
(2198, 18, 58, 'Senin Rabbin rahmet sahibi (ve) bağışlayıcıdır. Eğer, kazandıklarından dolayı onları (azapla) yakalasaydı, şüphesiz onlara azabı (bir an önce) çabuklaştırırdı. Hayır, onlar için bir buluşma zamanı vardır, onun dışında asla başka bir sığınak bulamayacaklardır.'),
(2199, 18, 59, 'İşte ülkeler (ve onların halkları), zulmettikleri zaman onları yıkıma uğrattık; ve yıkımları için bir buluşma zamanı tespit ettik.'),
(2200, 18, 60, 'Hani Musa genç yardımcısına demişti: "İki denizin birleştiği yere ulaşıncaya kadar gideceğim ya da uzun zamanlar geçireceğim."'),
(2201, 18, 61, 'Böylece ikisi, iki (deniz)in birleştiği yere ulaşınca balıklarını unutuverdiler; (balık) denizde bir akıntıya doğru (veya bir menfez bulup) kendi yolunu tuttu.'),
(2202, 18, 62, '(Varmaları gereken yere gelip) Geçtiklerinde (Musa) genç-yardımcısına dedi ki: "Yemeğimizi getir bize, andolsun, bu yaptığımız-yolculuktan gerçekten yorulduk."'),
(2203, 18, 63, '(Genç-yardımcısı) Dedi ki: "Gördün mü, kayaya sığındığımızda, ben balığı unuttum. Onu hatırlamamı şeytandan başkası bana unutturmadı; o da şaşılacak tarzda denizde kendi yolunu tuttu."'),
(2204, 18, 64, '(Musa) Dedi ki: "Bizim de aradığımız buydu." Böylelikle ikisi izleri üzerinde geriye doğru gittiler.'),
(2205, 18, 65, 'Derken, Katımız\'dan kendisine bir rahmet verdiğimiz ve tarafımızdan kendisine bir ilim öğrettiğimiz kullarımızdan bir kulu buldular.'),
(2206, 18, 66, 'Musa ona dedi ki: "Doğru yol (rüşd) olarak sana öğretilenden bana öğretmen için sana tabi olabilir miyim?"'),
(2207, 18, 67, 'Dedi ki: "Gerçekten sen, benimle birlikte olma sabrını göstermeye güç yetiremezsin."'),
(2208, 18, 68, '(Böyleyken) "Özünü kavramaya kuşatıcı olamadığın şeye nasıl sabredebilirsin?"'),
(2209, 18, 69, '(Musa:) "İnşaAllah, beni sabreden (biri olarak) bulacaksın. Hiçbir işte sana karşı gelmeyeceğim" dedi.'),
(2210, 18, 70, 'Dedi ki: "Eğer bana uyacak olursan, hiçbir şey hakkında bana soru sorma, ben sana öğütle-anlatıp söz edinceye kadar."'),
(2211, 18, 71, 'Böylece ikisi yola koyuldu. Nitekim bir gemiye binince, o bunu (gemiyi) deliverdi. (Musa) Dedi ki: "İçindekilerini batırmak için mi onu deldin? Andolsun, sen şaşırtıcı bir iş yaptın."'),
(2212, 18, 72, 'Dedi ki: "Gerçekten benimle birlikte olma sabrını göstermeye kesinlikle güç yetiremeyeceğini ben sana söylemedim mi?"'),
(2213, 18, 73, '(Musa:) "Beni, unuttuğumdan dolayı sorgulama ve bu işimden dolayı bana zorluk çıkarma" dedi.'),
(2214, 18, 74, 'Böylece ikisi (yine) yola koyuldular. Nitekim bir çocukla karşılaştılar, o hemen tutup onu öldürüverdi. (Musa) Dedi ki: "Bir cana karşılık olmaksızın, tertemiz bir canı mı öldürdün? Andolsun, sen kötü bir iş yaptın."'),
(2215, 18, 75, 'Dedi ki: "Gerçekte benimle birlikte olma sabrını göstermeye kesinlikle güç yetiremeyeceğini ben sana söylemedim mi?"'),
(2216, 18, 76, '(Musa:) "Bundan sonra sana bir şey soracak olursam, artık benimle arkadaşlık etme. Benden yana bir özre ulaşmış olursun" dedi.'),
(2217, 18, 77, '(Yine) Böylece ikisi yola koyuldu. Nihayet bir kasabaya gelip yemek istediler, fakat (kasaba halkı) onları konuklamaktan kaçındı. Onda (kasabada) yıkılmaya yüz tutmuş bir duvar buldular, hemen onu inşa etti. (Musa) Dedi ki: "Eğer isteseydin gerçekten buna karşılık bir ücret alabilirdin."'),
(2218, 18, 78, 'Dedi ki: "İşte bu, benimle senin aranda ayrılma (zamanı)mız. Sana, üzerinde sabır göstermeye güç yetiremeyeceğin bir yorumu haber vereceğim.'),
(2219, 18, 79, '"Gemi, denizde çalışan yoksullarındı, onu kusurlu yapmak istedim, (çünkü) ilerilerinde, her gemiyi zorbalıkla ele geçiren bir kral vardı."'),
(2220, 18, 80, '"Çocuğa gelince, onun anne ve babası mü\'min kimselerdi. Bundan dolayı, onun kendilerine azgınlık ve inkar zorunu kullanmasından endişe edip-korktuk."'),
(2221, 18, 81, 'Böylece, onlara Rablerinin ondan temiz olmak bakımından daha hayırlısı, merhamet bakımından da daha yakın olanını vermesini diledik."'),
(2222, 18, 82, '"Duvar ise, şehirde iki öksüz çocuğundu, altında onlara ait bir define vardı; babaları salih biriydi. Rabbin diledi ki, onlar erginlik çağına erişsinler ve kendi definelerini çıkarsınlar; (bu,) Rabbinden bir rahmettir. Bunları ben, kendi işim (özel görüşüm) olarak yapmadım. İşte, senin sabır göstermeye güç yetiremediğin şeylerin yorumu."'),
(2223, 18, 83, 'Sana (Ey Muhammed,) Zu\'l-Karneyn hakkında sorarlar. De ki: "Size, ondan \'öğüt ve hatırlatma olarak\' (bazı bilgiler) vereceğim.'),
(2224, 18, 84, 'Gerçekten, Biz ona yeryüzünde sapasağlam bir iktidar verdik ve ona herşeyden bir yol (sebep) verdik.'),
(2225, 18, 85, 'O da, bir yol tuttu.'),
(2226, 18, 86, 'Sonunda güneşin battığı yere kadar ulaştı ve onu kara çamurlu bir gözede batmakta buldu, yanında bir kavim gördü. Dedik ki: "Ey Zu\'l-Karneyn, (istiyorsan onları) ya azaba uğratırsın veya içlerinde güzelliği (geçerli ilke) edinirsin."'),
(2227, 18, 87, 'Dedi ki: "Kim zulmederse biz onu azaplandıracağız, sonra Rabbine döndürülür, O da onu görülmemiş bir azapla azaplandırır."'),
(2228, 18, 88, 'Kim iman eder ve salih amellerde bulunursa, onun için güzel bir karşılık vardır. Ona buyruğumuzdan kolay olanını söyleyeceğiz."'),
(2229, 18, 89, 'Sonra (yine) bir yol tuttu.'),
(2230, 18, 90, 'Sonunda güneşin doğduğu yere kadar ulaştı ve onu (güneşi), kendileri için bir siper kılmadığımız bir kavim üzerine doğmakta iken buldu.'),
(2231, 18, 91, 'İşte böyle, onun yanında "özü kapsayan bilgi olduğunu" (veya yanında olup-biten herşeyi) Biz (ilmimizle) büsbütün kuşatmıştık.'),
(2232, 18, 92, 'Sonra bir yol (daha) tuttu.'),
(2233, 18, 93, 'İki seddin arasına kadar ulaştı, onların (sedlerin) önünde hemen hemen hiçbir sözü kavramayan bir kavim buldu.'),
(2234, 18, 94, 'Dediler ki: "Ey Zu\'l-Karneyn, gerçekten Ye\'cuc ve Me\'cuc, yeryüzünde bozgunculuk çıkarıyorlar, bizimle onlar arasında bir sed inşa etmen için sana vergi verelim mi?"'),
(2235, 18, 95, 'Dedi ki: "Rabbimin beni kendisinde sağlam bir iktidarla yerleşik kıldığı (güç, nimet ve imkan), daha hayırlıdır. Madem öyle, bana (insani) güçle yardım edin de, sizinle onlar arasında sapasağlam bir engel kılayım."'),
(2236, 18, 96, '"Bana demir kütleleri getirin", iki dağın arası eşit düzeye gelince, "Körükleyin" dedi. Onu ateş haline getirinceye kadar (bu işi yaptı, sonra:) dedi ki: "Bana getirin, üzerine eritilmiş bakır dökeyim."'),
(2237, 18, 97, 'Böylelikle, ne onu aşabildiler, ne onu delmeye güç yetirebildiler.'),
(2238, 18, 98, 'Dedi ki: "Bu benim Rabbimden bir rahmettir. Rabbimin va\'di geldiği zaman, O, bunu dümdüz eder; Rabbimin va\'di haktır."'),
(2239, 18, 99, 'Biz o gün, bir kısmını bir kısmı içinde dalgalanırcasına bırakıvermişiz. Sur\'a da üfürülmüştür, artık onların tümünü birarada toparlamışız.'),
(2240, 18, 100, 'Ve o gün, cehennemi, inkar edenlere tam bir sunuşla sunmuşuz.'),
(2241, 18, 101, 'Ki onlar, Beni zikretme (konusun)da gözleri bir perde içindeydi. (Kur\'an\'ı) dinlemeye katlanamazlardı.'),
(2242, 18, 102, 'İnkar edenler, Beni bırakıp kullarımı veliler edindiklerini mi sandılar? Gerçekten Biz cehennemi kafirler için bir durak olarak hazırlamışız.'),
(2243, 18, 103, 'De ki: "Davranış (ameller) bakımından en çok hüsrana uğrayacak olanları size haber vereyim mi?"'),
(2244, 18, 104, '"Onların, dünya hayatındaki bütün çabaları boşa gitmişken, kendilerini gerçekte güzel iş yapmakta sanıyorlar."'),
(2245, 18, 105, 'İşte onlar, Rablerinin ayetlerini ve O\'na kavuşmayı inkar edenlerdir. Artık onların yapıp-ettikleri boşa çıkmıştır, kıyamet gününde onlar için bir tartı tutmayacağız.'),
(2246, 18, 106, 'İşte, inkar etmeleri, ayetlerimi ve elçilerimi alay konusu edinmelerinden dolayı onların cezası cehennemdir.'),
(2247, 18, 107, 'İman edip salih amellerde bulunanlar... Firdevs cennetleri onlar için bir \'konaklama yeridir.\''),
(2248, 18, 108, 'Onda ebedi olarak kalıcıdırlar, ondan ayrılmak istemezler.'),
(2249, 18, 109, 'De ki: "Rabbimin sözleri(ni yazmak) için deniz mürekkep olsa ve yardım için bir benzerini (bir o kadarını) dahi getirsek, Rabbimin sözleri tükenmeden önce, elbette deniz tükeniverirdi.'),
(2250, 18, 110, 'De ki: "Şüphesiz ben, ancak sizin benzeriniz olan bir beşerim; yalnızca bana sizin İlahınızın tek bir İlah olduğu vahyolunuyor. Kim Rabbine kavuşmayı umuyorsa, artık salih bir amelde bulunsun ve Rabbine ibadette hiç kimseyi ortak tutmasın."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Kaf, He, Ye, Ayn, Sad.'),
(2252, 19, 2, '(Bu,) Rabbinin, kulu Zekeriya\'ya rahmetinin zikridir.'),
(2253, 19, 3, 'Hani o, Rabbine gizlice seslendiği zaman;'),
(2254, 19, 4, 'Demişti ki: "Rabbim, şüphesiz benim kemiklerim gevşedi ve baş, yaşlılık aleviyle tutuştu; ben Sana dua etmekle mutsuz olmadım."'),
(2255, 19, 5, '"Doğrusu ben, arkamdan gelecek yakınlarım adına korkuya kapıldım, benim karım da bir kısır (kadın)dır. Artık bana Kendi Katından bir yardımcı armağan et."'),
(2256, 19, 6, '"Bana mirasçı olsun. Yakup oğullarına da mirasçı olsun. Rabbim, onu (kendisinden) razı olunan(lardan) kıl."'),
(2257, 19, 7, '(Allah buyurdu:) "Ey Zekeriya, şüphesiz Biz seni, adı Yahya olan bir çocukla müjdelemekteyiz; Biz bundan önce ona hiçbir adaş kılmamışız."'),
(2258, 19, 8, 'Dedi ki: "Rabbim, karım kısır (bir kadın) iken, benim nasıl oğlum olabilir? Ben de yaşlılığın son basamağındayım."'),
(2259, 19, 9, '(Ona gelen melek:) "İşte böyle" dedi. "Rabbin dedi ki: Bu Benim için kolaydır, daha önce sen hiçbir şey değil iken, seni yaratmıştım."'),
(2260, 19, 10, 'Dedi ki: "Rabbim, bana bir alamet (ayet) ver." Dedi ki: "Senin alametin, sapasağlam iken, üç tam gece insanlarla konuşmamandır."'),
(2261, 19, 11, 'Böylelikle (Zekeriya) mescidten kavminin karşısına çıkıp onlara (şu anlamları) işaret etti: "Sabah akşam tesbih edin."'),
(2262, 19, 12, '(Çocuğun doğup büyümesinden sonra ona dedik ki:) "Ey Yahya, Kitabı kuvvetle tut." Daha çocuk iken ona hikmet verdik.'),
(2263, 19, 13, 'Katımız\'dan ona bir sevgi duyarlılığı ve temizlik (de verdik). O, çok takva sahibi biriydi.'),
(2264, 19, 14, 'Ana ve babasına itaatkardı ve isyan eden bir zorba değildi.'),
(2265, 19, 15, 'Ona selam olsun; doğduğu gün, öleceği gün ve diri olarak yeniden-kaldırılacağı gün de.'),
(2266, 19, 16, 'Kitap\'ta Meryem\'i de zikret. Hani o, ailesinden kopup doğu tarafında bir yere çekilmişti.'),
(2267, 19, 17, 'Sonra onlardan yana (kendini gizleyen) bir perde çekmişti. Böylece ona ruhumuz (Cibril\'i) göndermiştik, o da, düzgün bir beşer kılığında görünmüştü.'),
(2268, 19, 18, 'Demişti ki: "Gerçekten ben, senden Rahman (olan Allah)a sığınırım. Eğer takva sahibiysen (bana yaklaşma)."'),
(2269, 19, 19, 'Demişti ki: "Ben, yalnızca Rabbinden (gelen) bir elçiyim; sana tertemiz bir erkek çocuk armağan etmek için (buradayım)."'),
(2270, 19, 20, 'O: "Benim nasıl bir erkek çocuğum olabilir? Bana hiçbir beşer dokunmamışken ve ben azgın utanmaz (bir kadın) değilken" dedi.'),
(2271, 19, 21, '"İşte böyle" dedi. "Rabbin, dedi ki: -Bu Benim için kolaydır. Onu insanlara bir ayet ve Bizden bir rahmet kılmak için (bu çocuk olacaktır)." Ve iş de olup bitmişti.'),
(2272, 19, 22, 'Böylelikle ona gebe kaldı, sonra onunla ıssız bir yere çekildi.'),
(2273, 19, 23, 'Derken doğum sancısı onu bir hurma dalına sürükledi. Dedi ki: "Keşke bundan önce ölseydim de, hafızalardan silinip unutuluverseydim."'),
(2274, 19, 24, 'Altından (bir ses) ona seslendi: "Hüzne kapılma, Rabbin senin alt (yan)ında bir ark kılmıştır."'),
(2275, 19, 25, 'Hurma dalını kendine doğru salla, üzerine henüz oluşmuş-taze hurma dökülüversin."'),
(2276, 19, 26, 'Artık, ye, iç, gözün aydın olsun. Eğer herhangi bir beşer görecek olursan, de ki: "Ben Rahman (olan Allah)\' a oruç adadım, bugün hiç kimseyle konuşmayacağım."'),
(2277, 19, 27, 'Böylece onu taşıyarak kavmine geldi. Dediler ki: "Ey Meryem, sen gerçekten şaşırtıcı bir şey yaptın."'),
(2278, 19, 28, '"Ey Harun\'un kız kardeşi, senin baban kötü bir kişi değildi ve annen de azgın, utanmaz (bir kadın) değildi."'),
(2279, 19, 29, 'Bunun üzerine ona (çocuğa) işaret etti. Dediler ki: "Henüz beşikte olan bir çocukla biz nasıl konuşabiliriz?"'),
(2280, 19, 30, '(İsa) Dedi ki: "Şüphesiz ben Allah\'ın kuluyum. (Allah) Bana kitabı verdi ve beni peygamber kıldı."'),
(2281, 19, 31, '"Nerede olursam (olayım,) beni kutlu kıldı ve hayat sürdüğüm müddetçe, bana namazı ve zekatı vasiyet (emr) etti."'),
(2282, 19, 32, '"Anneme itati de. Ve beni mutsuz bir zorba kılmadı."'),
(2283, 19, 33, '"Selam üzerimedir; doğduğum gün, öleceğim gün ve diri olarak yeniden-kaldırılacağım gün de."'),
(2284, 19, 34, 'İşte Meryem oğlu İsa; hakkında kuşkuya düştükleri "Hak Söz".'),
(2285, 19, 35, 'Allah\'ın çocuk edinmesi olacak şey değil. O Yücedir. Bir işin olmasına karar verirse, ancak ona: "Ol" der, o da hemen oluverir.'),
(2286, 19, 36, 'Gerçek şu ki, Allah benim de Rabbim, sizin de Rabbinizdir. Öyleyse O\'na kulluk edin. Dosdoğru yol budur.'),
(2287, 19, 37, 'İçlerinden (birtakım) gruplar ayrılığa düştüler. Artık büyük bir günü görmekten dolayı, vay inkar edenlere.'),
(2288, 19, 38, 'Bize gelecekleri gün, neler işitecekler, neler görecekler. Ama bugün o zalimler apaçık bir sapıklık içindedirler.'),
(2289, 19, 39, 'İş(in) hükme bağlanıp biteceği, hasret gününe karşı onları uyar; onlar bir gaflet içindedirler ve onlar inanmıyorlar.'),
(2290, 19, 40, 'Elbette, yeryüzünde ve onun üzerindekilere Biz varis olacağız ve onlar Bize döndürülecekler.'),
(2291, 19, 41, 'Kitap\'ta İbrahim\'i de zikret. Gerçekten o, doğruyu-söyleyen bir peygamberdi.'),
(2292, 19, 42, 'Hani babasına demişti: "Babacığım, işitmeyen, görmeyen ve seni herhangi bir şeyden bağımsızlaştırmayan şeylere niye tapıyorsun?'),
(2293, 19, 43, '"Babacığım, gerçek şu ki, bana, sana gelmeyen bir ilim geldi. Artık bana tabi ol, seni düzgün bir yola ulaştırayım."'),
(2294, 19, 44, '"Babacığım, şeytana kulluk etme, kuşkusuz şeytan, Rahman (olan Allah)a başkaldırandır."'),
(2295, 19, 45, '"Babacığım, gerçekten ben, sana Rahman tarafından bir azabın dokunacağından korkuyorum, o zaman şeytanın velisi olursun."'),
(2296, 19, 46, '(Babası) Demişti ki: "İbrahim, sen benim ilahlarımdan yüz mü çeviriyorsun? Eğer (bu tutumuna) bir son vermeyecek olursan, andolsun, seni taşa tutarım; uzun bir süre benden uzaklaş, (bir yerlere) git."'),
(2297, 19, 47, '(İbrahim:) "Selam üzerine olsun, senin için Rabbimden bağışlanma dileyeceğim, çünkü, O, bana pek lütufkardır" dedi.'),
(2298, 19, 48, '"Sizden ve Allah\'tan başka taptıklarınızdan kopup-ayrılıyorum ve Rabbime dua ediyorum. Umulur ki, Rabbime dua etmekle mutsuz olmayacağım."'),
(2299, 19, 49, 'Böylelikle, onlardan ve Allah\'tan başka taptıklarından kopup-ayrılınca ona İshak\'ı ve (oğlu) Yakup\'u armağan ettik ve her birini peygamber kıldık.'),
(2300, 19, 50, 'Onlara rahmetimizden armağan(lar) bağışladık ve onlar için yüce bir doğruluk dili verdik.'),
(2301, 19, 51, 'Kitap\'ta Musa\'yı da zikret. Çünkü o, ihlasa erdirilmiş ve gönderilmiş (Resul) bir peygamberdi.'),
(2302, 19, 52, 'Ona, Tur\'un sağ yanından seslendik ve onu (kendisiyle) gizlice söyleşmek için yakınlaştırdık.'),
(2303, 19, 53, 'Ona Rahmetimiz\'den kardeşi Harun\'u da bir peygamber olarak armağan ettik.'),
(2304, 19, 54, 'Kitap\'ta İsmail\'i de zikret. Çünkü o, va\'dinde doğruydu ve gönderilmiş (Resul) bir peygamberdi.'),
(2305, 19, 55, 'Halkına, namazı ve zekatı emrediyordu ve o, Rabbi Katında kendisinden razı olunan (bir insan)dı.'),
(2306, 19, 56, 'Kitap\'ta İdris\'i de zikret. Çünkü o, doğru olan bir peygamberdi.'),
(2307, 19, 57, 'Biz onu yüce bir mekan (makam)a yükseltmiştik.'),
(2308, 19, 58, 'İşte bunlar; kendilerine Allah\'ın nimet verdiği peygamberlerdendir; Adem\'in soyundan, Nuh ile birlikte taşıdıklarımız (insan nesillerin)den, İbrahim ve İsrail (Yakup)in soyundan, doğru yola eriştirdiklerimizden ve seçtiklerimizdendirler. Onlara Rahman (olan Allah\')ın ayetleri okunduğunda, ağlayarak secdeye kapanırlar.'),
(2309, 19, 59, 'Sonra onların arkasından öyle nesiller türedi ki, namaz (kılma duyarlılığın)ı kaybettiler ve şehvetlerine kapılıp-uydular. Böylece bunlar azgınlıklarının cezasıyla karşılaşacaklardır.'),
(2310, 19, 60, 'Ancak tevbe eden, iman eden ve salih amellerde bulunanlar (onların dışındadır); işte bunlar, cennete girecekler ve hiçbir şeyle zulme uğratılmayacaklar.'),
(2311, 19, 61, 'Adn cennetleri (onlarındır) ki, Rahman (olan Allah, onu) Kendi kullarına gaybtan vadetmiştir. Şüphesiz O\'nun va\'di yerine gelecektir.'),
(2312, 19, 62, 'Onda ‘boş bir söz’ işitmezler; sadece selam (ı işitirler). Sabah akşam, onların rızıkları orda (bulunmakta)dır.'),
(2313, 19, 63, 'O cennet; Biz, kullarımızdan takva sahibi olanları (ona) varisçi kılacağız.'),
(2314, 19, 64, 'Biz (elçiler) ancak Rabbiniz emriyle ineriz. Önümüzde, ardımızda ve bunlar arasında olan herşey O\'nundur. Senin Rabbin kesinlikle unutkan değildir.'),
(2315, 19, 65, 'Göklerin, yerin ve her ikisi arasındakilerin Rabbidir; şu halde O\'na ibadet et ve O\'na ibadette kararlı ol. Hiç O\'nun adaşı olan birini biliyor musun?'),
(2316, 19, 66, 'İnsan demektedir ki: "Ben öldükten sonra mı, gerçekten diri olarak çıkarılacağım?"'),
(2317, 19, 67, 'İnsan önceden, hiçbir şey değilken, gerçekten Bizim onu yaratmış bulunduğumuzu (hiç) düşünmüyor mu?'),
(2318, 19, 68, 'Andolsun Rabbine, Biz onları da, şeytanları da mutlaka haşredeceğiz, sonra onları cehennemin çevresinde diz üstü çökmüş olarak hazır bulunduracağız.'),
(2319, 19, 69, 'Sonra, her bir gruptan Rahman (olan Allah)a karşı azgınlık göstermek bakımından en şiddetli olanını ayıracağız.'),
(2320, 19, 70, 'Sonra Biz ona (cehenneme) girmeye kimlerin en çok uygun olduğunu daha iyi biliriz.'),
(2321, 19, 71, 'Sizden ona girmeyecek hiç kimse yoktur. Bu, Rabbinin kesin olarak üzerine aldığı bir karardır.'),
(2322, 19, 72, 'Sonra, takva sahiplerini kurtarırız ve zulmedenleri diz üstü çökmüş olarak bırakıveririz.'),
(2323, 19, 73, 'Onlara apaçık ayetlerimiz okunduğunda, o inkar edenler, iman edenlere derler ki: "İki gruptan hangisi, makam bakımından daha iyi, topluluk bakımından daha güzeldir?"'),
(2324, 19, 74, 'Onlardan önce nice insan- nesillerini yıkıma uğrattık, onlar mal (giyim, kuşam ve tefriş) bakımından da, gösteriş bakımından da daha güzeldiler.'),
(2325, 19, 75, 'De ki: "Kim sapıklık içindeyse, Rahman (olan Allah), ona süre tanıdıkça tanır; kendilerine va\'dedileni -ya azabı veya kıyamet saatini- gördükleri zaman artık kimin yeri (makam, mevki) daha kötü, kimin askeri- gücü daha zayıfmış, öğreneceklerdir.'),
(2326, 19, 76, 'Allah, hidayet bulanlara hidayeti arttırır. Sürekli olan salih davranışlar, Rabbinin Katında sevap bakımından daha hayırlı, varılacak sonuç bakımından da daha hayırlıdır.'),
(2327, 19, 77, 'Ayetlerimizi inkar edip, bana: "Elbette mal ve çocuklar verilecektir" diyeni gördün mü?'),
(2328, 19, 78, 'O, gayba mı tanık oldu, yoksa Rahman (olan Allah)ın Katında(n) bir ahid mi aldı?'),
(2329, 19, 79, 'Asla; demekte olduğunu yazacağız ve onun için azapta(n) da süre tanıdıkça tanıyacağız.'),
(2330, 19, 80, 'Onun söylemekte olduğuna Biz mirasçı olacağız; o Bize, \'yapayalnız tek başına\' gelecektir.'),
(2331, 19, 81, 'Kendilerine güç (izzet) sağlasınlar diye, Allah\'tan başka ilahlar edindiler.'),
(2332, 19, 82, 'Hayır; (o yalancı ilahlar) onların tapınışlarını inkar edecekler ve onlara karşı çelişkiye düşecekler.'),
(2333, 19, 83, 'Görmedin mi, Biz gerçekten şeytanları, kafirlerin üzerine gönderdik, onları tahrik edip kışkırtıyorlar.'),
(2334, 19, 84, 'Onlara karşı acele davranma; Biz onlar için ancak saydıkça sayıyoruz.'),
(2335, 19, 85, 'Takva sahiplerini bir heyet halinde Rahman (olan Allah\'ın huzurun)a toplayacağımız gün,'),
(2336, 19, 86, 'Suçlu-günahkarları susamışlar olarak cehenneme süreceğiz.'),
(2337, 19, 87, 'Rahmanın Katında ahid almışların dışında (onlar) şefaate malik olmayacaklardır.'),
(2338, 19, 88, '"Rahman çocuk edinmiştir" dediler.'),
(2339, 19, 89, 'Andolsun, siz oldukça çirkin bir cesarette bulunup-geldiniz.'),
(2340, 19, 90, 'Neredeyse bundan dolayı, gökler paramparça olacak, yer çatlayacak ve dağlar yıkılıp göçüverecekti.'),
(2341, 19, 91, 'Rahman adına çocuk öne sürdüklerinden (ötürü bunlar olacaktı.)'),
(2342, 19, 92, 'Rahman (olan Allah)a çocuk edinmek yaraşmaz.'),
(2343, 19, 93, 'Göklerde ve yerde olan (herkesin ve herşeyin) tümü Rahman (olan Allah)a, yalnızca kul olarak gelecektir.'),
(2344, 19, 94, 'Andolsun, onların tümünü kuşatmış ve onları sayı olarak saymış bulunmaktadır.'),
(2345, 19, 95, 'Ve onların hepsi, kıyamet günü O\'na, \'yapayalnız, tek başlarına\' geleceklerdir.'),
(2346, 19, 96, 'İman edenler ve salih amellerde bulunanlar ise, Rahman (olan Allah), onlar için bir sevgi kılacaktır.'),
(2347, 19, 97, 'Biz bunu (Kur\'an\'ı) senin dilinle kolaylaştırdık, takva sahiplerine müjde vermen ve direnen bir kavmi uyarıp-korkutman için.'),
(2348, 19, 98, 'Biz, onlardan önce nice insan nesillerini yıkıma uğrattık; (şimdiyse) onlardan hiçbirini hissediyor veya onların fısıltılarını duyuyor musun?');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Ta, Ha.'),
(2350, 20, 2, 'Biz sana bu Kur\'an\'ı güçlük çekmen için indirmedik,'),
(2351, 20, 3, '\'İçi titreyerek korku duyanlara\' ancak öğütle-hatırlatma (olsun diye indirdik).'),
(2352, 20, 4, 'Yeri ve yüksek gökleri yaratan tarafından bir indirmedir.'),
(2353, 20, 5, 'Rahman (olan Allah) arşa istiva etmiştir.'),
(2354, 20, 6, 'Göklerde, yerde, bu ikisinin arasında ve nemli toprağın altında olanların tümü O\'nundur.'),
(2355, 20, 7, 'Sözü açığa vursan da, (gizlesen de birdir). Çünkü şüphesiz O, gizliyi de, gizlinin gizlisini de bilmektedir.'),
(2356, 20, 8, 'Allah; O\'ndan başka İlah yoktur. En güzel isimler O\'nundur.'),
(2357, 20, 9, 'Sana Musa\'nın haberi geldi mi?'),
(2358, 20, 10, 'Hani bir ateş görmüştü de, ailesine şöyle demişti: "Durun, bir ateş gördüm; umulur ki size ondan bir kor getiririm veya ateşin yanında bir yol-gösterici bulurum."'),
(2359, 20, 11, 'Nitekim ona gidince, kendisine seslenildi: "Ey Musa."'),
(2360, 20, 12, '"Gerçekten Ben, Ben senin Rabbinim. Ayakkabılarını çıkar; çünkü sen, kutsal vadi olan Tuva\'dasın."'),
(2361, 20, 13, '"Ben seni seçmiş bulunuyorum; bundan böyle vahyolunanı dinle."'),
(2362, 20, 14, '"Gerçekten Ben, Ben Allah\'ım, Benden başka İlah yoktur; şu halde Bana ibadet et ve Beni zikretmek için dosdoğru namaz kıl."'),
(2363, 20, 15, '"Şüphesiz, kıyamet-saati yaklaşarak gelmektedir. Herkesin harcadığı çabanın karşılığını alması için, onun (koşup haberini) neredeyse gizleyeceğim."'),
(2364, 20, 16, '"Öyleyse, ona inanmayıp kendi hevasına uyan, sakın seni ondan alıkoymasın; sonra yıkıma uğrarsın."'),
(2365, 20, 17, '"Sağ elindeki nedir ey Musa?"'),
(2366, 20, 18, 'Dedi ki: "O, benim asamdır; ona dayanmakta, onunla davarlarım için ağaçlardan yaprak düşürmekteyim, onda benim için daha başka yararlar da var."'),
(2367, 20, 19, 'Dedi ki: "Onu at, ey Musa."'),
(2368, 20, 20, 'Böylece, onu attı; (bir de ne görsün) o hemen hızla koşan (kocaman) bir yılan (oluvermiş).'),
(2369, 20, 21, 'Dedi ki: "Onu al ve korkma, Biz onu ilk durumuna çevireceğiz."'),
(2370, 20, 22, '"Elini koltuğuna sok, bir hastalık olmadan, başka bir mucize (ayet) olarak bembeyaz bir durumda çıksın."'),
(2371, 20, 23, '"Öyle ki, sana büyük mucizelerimizden (birini) göstermiş olalım."'),
(2372, 20, 24, '"Firavun\'a git, çünkü o azmış bulunuyor."'),
(2373, 20, 25, 'Dedi ki: "Rabbim, benim göğsümü aç."'),
(2374, 20, 26, '"Bana işimi kolaylaştır."'),
(2375, 20, 27, '"Dilimden düğümü çöz;"'),
(2376, 20, 28, '"Ki söyleyeceklerimi kavrasınlar."'),
(2377, 20, 29, '"Ailemden bana bir yardımcı kıl,"'),
(2378, 20, 30, '"Kardeşim Harun\'u"'),
(2379, 20, 31, '"Onunla arkamı kuvvetlendir."'),
(2380, 20, 32, '"Onu işimde ortak kıl,"'),
(2381, 20, 33, '"Böylece Seni çok tesbih edelim."'),
(2382, 20, 34, '"Ve Seni çok zikredelim."'),
(2383, 20, 35, '"Şüphesiz Sen bizi görüyorsun."'),
(2384, 20, 36, '(Allah) Dedi ki: "Ey Musa istediğin sana verilmiştir."'),
(2385, 20, 37, '"Andolsun, Biz sana bir defa daha lütufta bulunmuştuk."'),
(2386, 20, 38, '"Hani, annene vahyolunan şeyi vahyetmiştik, (şöyle ki:)"'),
(2387, 20, 39, '"Onu sandığın içine koy, suya bırak, böylece su onu sahile bıraksın; onu Benim de düşmanım, onun da düşmanı olan biri alacaktır. Gözümün önünde yetiştirilmen için, Kendim\'den sana bir sevgi yönelttim."'),
(2388, 20, 40, '"Hani kız kardeşin gezinip; "Onu(n bakımını) üstlenecek birini size haber vereyim mi?" demekteydi. Böylece, seni annene geri çevirmiş olduk ki, gözü aydın olsun ve hüzne kapılmasın. Sen bir insan öldürmüştün de, Biz seni tasadan kurtarmış ve seni \'esaslı bir denemeden geçirip-denemiştik.\' Medyen halkı arasında da yıllarca kalmıştın, sonra bir kader üzerine (buraya) geldin ey Musa."'),
(2389, 20, 41, '"Seni Kendim için seçtim."'),
(2390, 20, 42, '"Sen ve kardeşin ayetlerimle gidin ve Beni zikretmede gevşek davranmayın.'),
(2391, 20, 43, '"İkiniz Firavun\'a gidin, çünkü o, azmış bulunuyor."'),
(2392, 20, 44, '"Ona yumuşak söz söyleyin, umulur ki öğüt alıp-düşünür veya içi titrer-korkar."'),
(2393, 20, 45, 'Dediler ki: "Rabbimiz, gerçekten, onun bize karşı \'taşkın bir tutum takınmasından\' ya da \'azgın davranmasından\' korkuyoruz."'),
(2394, 20, 46, 'Dedi ki: "Korkmayın, çünkü Ben sizinle birlikteyim; işitiyorum ve görüyorum."'),
(2395, 20, 47, '"Haydi ona gidin de deyin ki: Biz senin Rabbinin elçileriyiz, İsrailoğulları\'nı bizimle birlikte gönder ve onlara (artık) azap verme. Sana Rabbinden bir ayetle geldik. Selam, hidayete tabi olanların üzerine olsun."'),
(2396, 20, 48, '"Gerçekten bize vahyolundu ki: Doğrusu azap, yalanlayan ve yüz çevirenlerin üstünedir."'),
(2397, 20, 49, '(Ona gidip aynı şeyleri tekrarladıklarında, Firavun onlara) Dedi ki: "Sizin Rabbiniz kim ey Musa?"'),
(2398, 20, 50, 'Dedi ki: "Bizim Rabbimiz, herşeye yaratılışını veren, sonra doğru yolunu gösterendir."'),
(2399, 20, 51, '(Firavun) Dedi ki: "İlk çağlardaki nesillerin durumu nedir öyleyse?"'),
(2400, 20, 52, 'Dedi ki: "Bunun bilgisi Rabbimin Katında bir kitaptadır. Benim Rabbim şaşırmaz ve unutmaz."'),
(2401, 20, 53, '"Ki (Rabbim), yeryüzünü sizin için bir beşik kıldı, onda sizin için yollar döşedi ve gökten su indirdi; böylelikle bununla her tür bitkiden çiftler çıkardık."'),
(2402, 20, 54, '"Yiyin ve hayvanlarınızı otlatın. Şüphesiz, bunda sağduyu sahipleri için elbette ayetler vardır.'),
(2403, 20, 55, 'Sizi ondan yarattık, ona geri vereceğiz ve sizi bir kere daha ondan çıkaracağız.'),
(2404, 20, 56, 'Andolsun, Biz ona ayetlerimizin tümünü gösterdik; fakat o, yalanladı ve ayak diretti.'),
(2405, 20, 57, 'Dedi ki: "Ey Musa, sen bizi sihrinle yurdumuzdan sürüp çıkarmaya mı gelmiş bulunuyorsun?"'),
(2406, 20, 58, '"Madem böyle, biz de sana buna benzer bir sihirle geleceğiz; şimdi sen, bir \'buluşma zamanı ve yeri\' tespit et, bizim de, senin de karşı olamayacağımız açık, geniş bir yer olsun" dedi.'),
(2407, 20, 59, '(Musa) Dedi ki: "Buluşma zamanımız, (ülkenin ulusal) bayram günü ve insanların toplanacağı kuşluk vakti (olsun)."'),
(2408, 20, 60, 'Böylelikle Firavun arkasını dönüp gitti, hileli düzenini (yürütecek büyücüleri) biraraya getirdi, sonra geldi.'),
(2409, 20, 61, 'Musa onlara dedi ki: "Size yazıklar olsun, Allah\'a karşı yalan düzüp uydurmayın, sonra bir azap ile kökünüzü kurutur. Yalan düzüp uyduran gerçekten yok olup gitmiştir."'),
(2410, 20, 62, 'Bunun üzerine, kendi aralarında durumlarını tartışmaya başladılar ve gizli konuşmalara geçtiler.'),
(2411, 20, 63, 'Dediler ki: "Bunlar herhalde iki sihirbazdır, sizi sihirleriyle yurdunuzdan sürüp-çıkarmak ve örnek olarak tutturduğunuz yolunuzu (dininizi) yok etmek istemektedirler."'),
(2412, 20, 64, '"Bundan ötürü, tuzaklarınızı biraraya getirin, sonra gruplar halinde gelin; bugün üstünlük sağlayan, gerçekten kurtuluşu bulmuştur."'),
(2413, 20, 65, '"Ey Musa" dediler. Ya sen (asanı) at veya önce biz atalım."'),
(2414, 20, 66, 'Dedi ki: "Hayır, siz atın." Sonra hemen (ne görsün), sihirlerinden dolayı, onların ipleri ve asaları kendisine gerçekten koşuyormuş gibi göründü.'),
(2415, 20, 67, 'Musa, bu yüzden kendi içinde bir tür korku duymaya başladı.'),
(2416, 20, 68, '"Korkma" dedik. "Muhakkak sen üstün geleceksin."'),
(2417, 20, 69, '"Sağ elindekini atıver, onların yaptıklarını yutacaktır; çünkü onların yaptıkları yalnızca bir büyücü hilesidir. Büyücü ise nereye varsa kurtulamaz."'),
(2418, 20, 70, 'Bunun üzerine büyücüler, secdeye kapandılar: "Harun\'un ve Musa\'nın Rabbine iman ettik" dediler.'),
(2419, 20, 71, '(Firavun) Dedi ki: "Ben size izin vermeden önce ona inandınız öyle mi? Şüphesiz o, size büyüyü öğreten büyüğünüzdür. O halde ben de sizin ellerinizi ve ayaklarınızı çapraz olarak keseceğim ve sizi hurma dallarında sallandıracağım. Siz de elbette, hangimizin azabı daha şiddetliymiş ve daha sürekliymiş öğrenmiş olacaksınız."'),
(2420, 20, 72, 'Dediler ki: "Bize gelen apaçık delillere ve bizi yaratana seni asla \'tercih edip-seçmeyiz." Neyde hükmünü yürütebileceksen, durmaksızın hükmünü yürüt; sen, yalnızca bu dünya hayatında hükmünü yürütebilirsin."'),
(2421, 20, 73, '"Gerçekten biz Rabbimiz\'e iman ettik; günahlarımızı ve sihir dolayısıyla bizi kendisine karşı zorlayarak-sürüklediğin (suçumuzu) bağışlasın. Allah, daha hayırlıdır ve daha süreklidir."'),
(2422, 20, 74, '"Gerçek şu ki, kim Rabbine suçlu-günahkar olarak gelirse, hiç şüphe yok, onun için cehennem vardır. Onun içinde ise, ne ölebilir, ne dirilebilir."'),
(2423, 20, 75, '"Kim O\'na iman edip salih amellerde bulunarak O\'na gelirse, işte onlar, onlar için de yüksek dereceler vardır."'),
(2424, 20, 76, '"İçlerinde ebedi kalacakları altından ırmaklar akan Adn cennetleri de (onlarındır). Ve işte bu, arınmış olanın karşılığıdır."'),
(2425, 20, 77, 'Andolsun, Biz Musa\'ya vahyetmiştik: "Kullarımı geceleyin yürüyüşe geçir, onlara denizde kuru bir yol aç, yetişilmekten korkmadan ve endişeye kapılmadan."'),
(2426, 20, 78, 'Firavun ise, ordularıyla peşlerine düştü; sulardan onları kaplayıveren kaplayıverdi.'),
(2427, 20, 79, 'Firavun, kendi kavmini şaşırtıp saptırdı ve onları doğruya yöneltmedi.'),
(2428, 20, 80, 'Ey İsrailoğulları, andolsun, sizi düşmanlarınızdan kurtardık. Tur\'un sağ yanında sizinle vaadleştik ve üzerinize kudret helvasıyla bıldırcın indirdik.'),
(2429, 20, 81, 'Size, rızık olarak verdiklerimizden temiz olanlarından yiyin, bu konuda azgınlık yapmayın, yoksa gazabım üzerinize kaçınılmaz olarak iner: Benim gazabım, kimin üzerine inerse, muhakkak o, tepetaklak düşmüştür.'),
(2430, 20, 82, 'Gerçekten Ben, tevbe eden, inanan, salih amellerde bulunup da sonra doğru yola erişen kimseyi şüphesiz bağışlayıcıyım.'),
(2431, 20, 83, '"Seni kavminden \'çarçabuk ayrılmaya iten\' nedir ey Musa?"'),
(2432, 20, 84, 'Dedi ki: "Onlar arkamda izim üzerindedirler, hoşnut kalman için, Sana gelmekte acele ettim Rabbim."'),
(2433, 20, 85, 'Dedi ki: "Biz senden sonra kavmini deneme (fitne)den geçirdik, Samiri onları şaşırtıp-saptırdı."'),
(2434, 20, 86, 'Bunun üzerine Musa, kavmine oldukça kızgın, üzgün olarak döndü. Dedi ki: "Ey kavmim, Rabbiniz size güzel bir vaadde bulunmadı mı? Size (verilen) söz (ya da süre) pek uzun mu geldi? Yoksa Rabbinizden üzerinize kaçınılmaz bir gazabın inmesini mi istediniz de bana verdiğiniz sözden caydınız?"'),
(2435, 20, 87, 'Dediler ki: "Biz sana verdiğimiz sözden kendiliğimizden dönmedik, ancak o kavmin (Mısır halkının) süs eşyalarından birtakım yükler yüklenmiştik, onları (ateşe) attık, böylece Samiri de attı."'),
(2436, 20, 88, 'Böylece onlara böğüren bir buzağı heykeli döküp çıkardı, "İşte, sizin de ilahınız, Musa\'nın ilahı budur; fakat (Musa) unuttu" dediler.'),
(2437, 20, 89, 'Onun kendilerine bir sözle cevap vermediğini ve onlara bir zarar veya fayda sağlamaya gücü olmadığını görmüyorlar mı?'),
(2438, 20, 90, 'Andolsun, Harun bundan önce onlara: "Ey kavmim, gerçekten siz bununla fitneye düşürüldünüz (denendiniz). Sizin asıl Rabbiniz Rahman (olan Allah)dır; şu halde bana uyun ve emrime itaat edin" demişti.'),
(2439, 20, 91, 'Demişlerdi ki: "Musa bize geri gelinceye kadar ona (buzağıya) karşı bel büküp önünde eğilmekten kesinlikle ayrılmayacağız."'),
(2440, 20, 92, '(Musa da gelince:) "Ey Harun" demişti. "Onların saptıklarını gördüğün zaman seni (Onlara müdahale etmekten) alıkoyan neydi?"'),
(2441, 20, 93, '"Niye bana uymadın, emrime baş mı kaldırdın?"'),
(2442, 20, 94, 'Dedi ki: "Ey annemin oğlu, sakalımı ve başımı tutup-yolma. Ben, senin: "İsrailoğulları arasında ayrılık çıkardın, sözümü önemsemedin" demenden endişe edip korktum."'),
(2443, 20, 95, '(Musa) Dedi ki: "Ya senin amacın nedir ey Samiri?"'),
(2444, 20, 96, 'Dedi ki: "Ben onların görmediklerini gördüm, böylece elçinin izinden bir avuç alıp atıverdim; böylelikle bana bunu nefsim hoşa giden (bir şey) gösterdi."'),
(2445, 20, 97, 'Dedi ki: “Haydi çekip git, artık senin hayatta (hakettiğin ceza: "Bana dokunulmasın") deyip yerinmendir." Ve şüphesiz senin için kendisinden asla kaçınamayacağın (azap dolu) bir buluşma zamanı vardır. Üstüne kapanıp bel bükerek önünde eğildiğin ilahına bir bak; biz onu mutlaka yakacağız, sonra darmadağın edip denizde savuracağız."'),
(2446, 20, 98, '"Sizin İlahınız yalnızca Allah\'tır ki, O\'nun dışında İlah yoktur. O, ilim bakımından herşeyi kuşatmıştır."'),
(2447, 20, 99, 'Sana geçmişlerin haberlerinden bir bölümünü böylece aktarıyoruz. Gerçekten, sana Katımız\'dan bir zikir verdik.'),
(2448, 20, 100, 'Kim bundan yüz çevirirse, şüphesiz kıyamet günü o, bir günah-yükü yüklenecektir.'),
(2449, 20, 101, 'O (yükün altı)nda ebedi olarak kalıcıdırlar. Bu, kıyamet günü onlar için ne kötü bir yüktür.'),
(2450, 20, 102, 'Sur\'a üfürüleceği gün, Biz suçlu-günahkarları o gün, (yüzleri kara, gözleri) gömgök (kaskatı ve kör) olarak\' toplayacağız.'),
(2451, 20, 103, '(Dünyada) Yalnızca on (gün) kaldınız" diye kendi aralarında fısıldaşacaklar.'),
(2452, 20, 104, 'Onların sözünü ettiklerini Biz daha iyi biliyoruz. Tutulan yol bakımından onların daha üst olanları ise: "Siz yalnızca bir gün kaldınız" derler.'),
(2453, 20, 105, 'Sana dağlar hakkında soruyorlar. De ki: "Benim Rabbim, onları darmadağın edip savuracak"'),
(2454, 20, 106, '"Yerlerini bomboş, çırçıplak bırakacaktır."'),
(2455, 20, 107, '"Orada ne bir eğrilik göreceksin, ne de bir tümsek."'),
(2456, 20, 108, 'O gün, kendisinden sapma imkanı olamayan çağırıcıya uyacaklar. Rahman (olan Allah)a karşı sesler kısılmıştır; artık bir hırıltıdan başka bir şey işitemezsin.'),
(2457, 20, 109, 'O gün, Rahman (olan Allah)\'ın kendisine izin verdiği ve sözünden hoşnut olduğu kimseden başkasının şefaati bir yarar sağlamaz.'),
(2458, 20, 110, 'O, önlerindekini de, arkalarındakini de bilir. Onlar ise, bilgi bakımından O\'nu kavrayıp kuşatamazlar.'),
(2459, 20, 111, '(Artık bütün) Yüzler, diri, kaim olanın önünde eğik durmuştur ve zulüm yüklenen ise yok olup gitmiştir.'),
(2460, 20, 112, 'Kim de bir mü\'min olarak, salih olan amellerde bulunursa, artık o, ne zulümden korksun, ne hakkının eksik tutulmasından.'),
(2461, 20, 113, 'Böylece Biz onu, Arapça bir Kur\'an olarak indirdik ve onda korkulacak şeyleri türlü şekillerde açıkladık; umulur ki korkup-sakınırlar ya da onlar için düşünme (yeteneğini) oluşturur.'),
(2462, 20, 114, 'Hak olan, biricik hükümdar olan Allah Yücedir. Onun vahyi sana gelip-tamamlanmadan evvel, Kur\'an\'ı (okumada) acele etme ve de ki: "Rabbim, ilmimi arttır."'),
(2463, 20, 115, 'Andolsun, Biz bundan önce Adem\'e ahid vermiştik, fakat o, unutuverdi. Biz onda bir kararlılık bulmadık.'),
(2464, 20, 116, 'Hani Biz meleklere: "Adem\'e secde edin" demiştik, İblis\'in dışında (diğerleri) secde etmişlerdi, o, ayak diremişti.'),
(2465, 20, 117, 'Bunun üzerine dedik ki: "Ey Adem, bu gerçekten sana ve eşine düşmandır; sakın sizi cennetten sürüp çıkarmasın, sonra mutsuz olursun."'),
(2466, 20, 118, 'Şüphesiz ki, senin acıkmaman ve çıplak kalmaman orda (cennette kalmana bağlı)dır."'),
(2467, 20, 119, 'Ve gerçekten sen burada susamayacaksın ve güneş altında yanmayacaksın da."'),
(2468, 20, 120, 'Sonunda şeytan ona vesvese verdi; dedi ki: "Sana sonsuzluk ağacını ve yok olmayacak bir mülkü haber vereyim mi?"'),
(2469, 20, 121, 'Böylece ikisi ondan yediler, hemen ardından ayıp yerleri kendilerine açılıverdi, üzerlerini cennet yapraklarından yamayıp-örtmeye başladılar. Adem, Rabbine karşı gelmiş oldu da şaşırıp-kaldı.'),
(2470, 20, 122, 'Sonra Rabbi onu seçti, tevbesini kabul etti ve doğru yola iletti.'),
(2471, 20, 123, 'Dedi ki: "Kiminiz kiminize düşman olarak, hepiniz ordan inin. Artık size Benden bir yol gösterici gelecektir; kim Benim hidayetime uyarsa artık o şaşırıp sapmaz ve mutsuz olmaz."'),
(2472, 20, 124, '"Kim de Benim zikrimden yüz çevirirse, artık onun için sıkıntılı bir geçim vardır ve Biz onu kıyamet günü kör olarak haşredeceğiz."'),
(2473, 20, 125, '"O da (şöyle) demiş olur: -Ben görmekte olan biriyken, beni niye kör olarak haşrettin Rabbim?"'),
(2474, 20, 126, '(Allah da) Der ki: "İşte böyle, sana ayetlerimiz gelmişti, fakat sen onları unuttun, bugün de sen işte böyle unutulmaktasın."'),
(2475, 20, 127, 'İşte Biz ölçüsüzce davrananları ve Rabbinin ayetlerine inanmayanları böyle cezalandırırız; ahiretin azabı ise gerçekten daha şiddetli ve daha süreklidir.'),
(2476, 20, 128, 'Kendilerinden önceki nesillerden nicelerini yıkıma uğratmamız, onları doğruya yöneltmedi mi? (Oysa bugün kendileri) onların kaldıkları yerlerde (tarihi kalıntıları üzerinde) gezinip duruyorlar. Şüphesiz bunda sağduyu sahipleri için ayetler vardır.'),
(2477, 20, 129, 'Eğer Rabbinden geçmiş bir söz ve adı konulmuş (belirlenmiş) bir süre (ecel) olmasaydı muhakkak (yıkım azabı) kaçınılmaz olurdu.'),
(2478, 20, 130, 'Şu halde onların söylediklerine karşı sabırlı ol, güneşin doğuşundan ve batışından önce Rabbini hamd ile tesbih et (yücelt). Gecenin bir bölümünde ve gündüzün uçlarında da tesbihte bulun ki hoşnut olabilesin.'),
(2479, 20, 131, 'Onlardan bazı gruplara, kendilerini denemek için yararlandırdığımız dünya hayatının süsüne gözünü dikme. Senin Rabbinin rızkı daha hayırlı ve daha süreklidir.'),
(2480, 20, 132, 'Ehline (ümmetine) namazı emret ve onda kararlı davran. Biz senden rızık istemiyoruz, Biz sana rızık veriyoruz. Sonuç da takvanındır.'),
(2481, 20, 133, 'Dediler ki: "Bize kendi Rabbinden bir ayet (mucize) getirmesi gerekmez miydi?" Onlara önceki kitaplarda açık belgeler gelmedi mi?'),
(2482, 20, 134, 'Eğer Biz onları bundan önceki bir azap ile yıkıma uğratmış olsaydık, şüphesiz diyeceklerdi ki: "Rabbimiz, bize bir elçi gönderseydin de, küçülmeden ve aşağılanmadan önce Senin ayetlerine tabi olsaydık."'),
(2483, 20, 135, 'De ki: "Herkes gözetlemektedir; siz de gözleyip durun. Sonunda, dümdüz (dosdoğru) yolun sahipleri kimlermiş ve doğru yola ulaşan kimlermiş, pek yakında öğreneceksiniz."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'İnsanları sorgulama (zamanı) yaklaştı, kendileri ise gaflet içinde yüz çeviriyorlar.'),
(2485, 21, 2, 'Rablerinden kendilerine yeni bir hatırlatma gelmeyiversin, bunu mutlaka oyun konusu yaparak dinliyorlar.'),
(2486, 21, 3, 'Onların kalpleri tutkuyla oyalanmadadır. Zulmedenler, gizlice fısıldaştılar: "Bu sizin benzeriniz olan bir beşer değil mi? Öyleyse, göz göre göre büyüye mi geleceksiniz?"'),
(2487, 21, 4, 'Dedi ki: "Benim Rabbim, gökte ve yerde söylenen-sözü bilir; O, işitendir, bilendir."'),
(2488, 21, 5, '"Hayır" dediler. (Bunlar) Karmakarışık düşlerdir; hayır, onu kendisi uydurmuştur; hayır o bir şairdir. Böyle değilse, öncekilere gönderildiği gibi bize de bir ayet (mucize) getirsin."'),
(2489, 21, 6, 'Kendilerinden evvel yıkıma uğrattığımız hiçbir ülke (halkı) iman etmemişti; şimdi bunlar mı iman edecek?'),
(2490, 21, 7, 'Biz senden önce de kendilerine vahyettiğimiz erkekler dışında elçi göndermedik. Eğer bilmiyorsanız, o halde zikir ehline sorun.'),
(2491, 21, 8, 'Biz onları, yemek yemez cesetler kılmadık ve onlar ölümsüz değillerdi.'),
(2492, 21, 9, 'Sonra onlara verdiğimiz söze sadık kaldık, böylece onları ve dilediklerimizi kurtardık da ölçüsüz davrananları yıkıma uğrattık.'),
(2493, 21, 10, 'Andolsun, size (bütün durumlarınızı kapsayan) zikrinizin içinde bulunduğu bir kitap indirdik. Yine de akıllanmayacak mısınız?'),
(2494, 21, 11, 'Biz, zulmeden ülkelerden nicesini kırıp geçirdik ve bunun ardından bir başka kavmi meydana getirdik.'),
(2495, 21, 12, 'Bizim zorlu-azabımızı hissettikleri zaman, oradan büyük bir hızla uzaklaşıp kaçıyorlardı.'),
(2496, 21, 13, '"Uzaklaşıp-kaçmayın, içinde şımarıp azdığınız refaha ve yurtlarınıza dönün; çünkü sorguya çekileceksiniz."'),
(2497, 21, 14, '"Yazıklar bize" dediler. "Gerçekten biz, zalimmişiz."'),
(2498, 21, 15, 'Onların bu yakınmaları, Biz onları biçilmiş ekin, sönmüş ocak durumuna getirinceye kadar son bulmadı.'),
(2499, 21, 16, 'Biz, bir \'oyun ve oyalanma konusu\' olsun diye göğü, yeri ve ikisi arasında bulunanları yaratmadık.'),
(2500, 21, 17, 'Eğer bir \'oyun ve oyalanma\' edinmek isteseydik, bunu, Kendi Katımız\'dan edinirdik. Yapacak olsaydık, böyle yapardık.'),
(2501, 21, 18, 'Hayır, Biz hakkı batılın üstüne fırlatırız, o da onun beynini darmadağın eder. Bir de bakarsın ki, o, yok olup gitmiştir. (Allah\'a karşı) Nitelendiregeldiklerinizden dolayı eyvahlar size.'),
(2502, 21, 19, 'Göklerde ve yerde kim varsa O\'nundur. O\'nun yanında olanlar, O\'na ibadet etmekte büyüklüğe kapılmazlar ve yorgunluk duymazlar.'),
(2503, 21, 20, 'Gece ve gündüz, hiç durmaksızın tesbih ederler.'),
(2504, 21, 21, 'Yoksa onlar, yerden birtakım ilahlar edindiler de, onlar mı (ölüleri) diriltecekler?'),
(2505, 21, 22, 'Eğer her ikisinde (gökte ve yerde) Allah\'ın dışında ilahlar olsaydı, elbette, ikisi de bozulup gitmişti. Arşın Rabbi olan Allah onların nitelendiregeldikleri şeylerden Yücedir.'),
(2506, 21, 23, 'O, yaptıklarından sorulmaz, oysa onlar sorguya çekilirler.'),
(2507, 21, 24, 'Yoksa O\'ndan başka ilahlar mı edindiler? De ki: "Kesin-kanıt (burhan)ınızı getirin. İşte benimle birlikte olanların zikri (kitabı) ve benden öncekilerin de zikri." Hayır, onların çoğu hakkı bilmiyorlar, bundan dolayı yüz çeviriyorlar.'),
(2508, 21, 25, 'Senden önce hiçbir elçi göndermedik ki, ona şunu vahyetmiş olmayalım: "Benden başka İlah yoktur, öyleyse Bana ibadet edin."'),
(2509, 21, 26, '"Rahman (olan Allah) çocuk edindi" dediler. O, (bu yakıştırmadan) Yücedir. Hayır, onlar (melekler) ikrama layık görülmüş kullardır.'),
(2510, 21, 27, 'Onlar sözle (bile olsa) O\'nun önüne geçmezler ve onlar O\'nun emriyle yapıp-etmektedirler.'),
(2511, 21, 28, 'O, önlerindekini ve arkalarındakini bilir; onlar şefaat etmezler (kendisinden) hoşnut olunandan başka. Ve onlar, O\'nun haşmetinden içleri titremekte olanlardır.'),
(2512, 21, 29, 'Onlardan her kim: "Gerçekten ben, O\'nun dışında bir ilahım" diyecek olsa, bu durumda Biz onu cehennemle cezalandırırız. Zalimleri Biz böyle cezalandırırız.'),
(2513, 21, 30, 'O inkar edenler görmüyorlar mı ki, (başlangıçta) göklerle yer, birbiriyle bitişik iken, Biz onları ayırdık ve her canlı şeyi sudan yarattık. Yine de onlar inanmayacaklar mı?'),
(2514, 21, 31, 'Yeryüzünde, onları sarsmasın diye, sabit dağlar yarattık ve doğru gidebilsinler diye geniş yollar açtık.'),
(2515, 21, 32, 'Gökyüzünü korunmuş bir tavan kıldık; onlar ise bunun ayetlerinden yüz çeviriyorlar.'),
(2516, 21, 33, 'Geceyi, gündüzü, güneşi ve ayı yaratan O\'dur; her biri bir yörüngede yüzüp gidiyor.'),
(2517, 21, 34, 'Senden önce hiçbir beşere ölümsüzlüğü vermedik; şimdi sen ölürsen onlar ölümsüz mü kalacaklar?'),
(2518, 21, 35, 'Her nefis ölümü tadıcıdır. Biz sizi, şerle de, hayırla da deneyerek imtihan ediyoruz ve siz Bize döndürüleceksiniz.'),
(2519, 21, 36, 'İnkar edenler seni gördüklerinde, seni yalnızca alay-konusu ediyorlar (ve:) "Sizin ilahlarınızı diline dolayan bu mu?" (derler.) Oysa Rahman (olan Allah)ın sözünü (kitabını) inkar edenler kendileridir.'),
(2520, 21, 37, 'İnsan aceleden (aceleci olarak) yaratıldı. Size ayetlerimi yakında göstereceğim. Şimdi hemen acele etmeyin.'),
(2521, 21, 38, '"Eğer doğruyu söylüyor iseniz, bu vaid (edilen günün sorgu ve azabı) ne zamandır?" derler.'),
(2522, 21, 39, 'O inkar edenler, yüzlerinden ve sırtlarından ateşi püskürtemeyecekleri ve hiç yardım alamayacakları zamanı bir bilselerdi.'),
(2523, 21, 40, 'Hayır, onlara apansız gelecek de, böylece onları şaşkına çevirecek; artık ne onu geri çevirmeye güçleri yetecek ve ne onlara süre tanınacak.'),
(2524, 21, 41, 'Andolsun, senden önceki elçilerle de alay edildi, fakat içlerinden küçük düşürenleri, o alaya aldıkları (azap) sarıp-kuşatıverdi.'),
(2525, 21, 42, 'De ki: "Gece ve gündüz sizi Rahman (olan Allah)tan kim koruyabilir?" Hayır, onlar Rablerini zikirden yüz çevirenlerdir.'),
(2526, 21, 43, 'Yoksa Bize karşı kendilerini, engelleyerek koruyabilecek ilahları mı var? Onların kendi nefislerine bile yardıma güçleri yetmez ve onlar Bizden yakınlık bulamazlar.'),
(2527, 21, 44, 'Evet, Biz onları ve atalarını yararlandırdık; öyle ki, ömür onlara (hiç bitmeyecekmiş gibi) uzun geldi. Fakat şimdi, Bizim gerçekten yere gelip onu etrafından eksiltmekte olduğumuzu görmüyorlar mı? Şu halde, üstün gelenler onlar mı?'),
(2528, 21, 45, 'De ki: "Ben sizi yalnızca vahy ile uyarıp-korkutuyorum. Ancak sağır olanlar, uyarıldıklarında çağrıyı işitmezler."'),
(2529, 21, 46, 'Andolsun, onlara Rabbinin azabından \'bir ufak esinti\' dokunacak olsa hiç tartışmasız; "Eyvahlar bize, gerçekten bizler zulme sapanlarmışız" diyecekler.'),
(2530, 21, 47, 'Biz ise, kıyamet gününe ait duyarlı teraziler koyarız da artık, hiçbir nefis hiçbir şeyle haksızlığa uğramaz. Bir hardal tanesi bile olsa ona (teraziye) getiririz. Hesap görücüler olarak Biz yeteriz.'),
(2531, 21, 48, 'Andolsun, Biz Musa\'ya ve Harun\'a, takva sahipleri için bir aydınlık ve bir öğüt (zikir) olarak, hak ile batılı birbirinden ayıran (furkan)ı verdik.'),
(2532, 21, 49, 'Onlar, Rablerine karşı gayb ile (O\'nu görmedikleri halde) bir haşyet içindedirler ve onlar, kıyamet saatinden \'içleri titremekte olanlardır.\''),
(2533, 21, 50, 'Bu, Bizim ona indirdiğimiz mübarek bir zikirdir. Şu halde onu inkar edecek olanlar siz misiniz?'),
(2534, 21, 51, 'Andolsun, bundan önce İbrahim\'e rüşdünü vermiştik ve Biz onu (doğruyu seçme yeteneğinde olduğunu) bilenlerdik.'),
(2535, 21, 52, 'Hani babasına ve kavmine demişti ki: "Sizin, karşılarında bel büküp eğilmekte olduğunuz bu temsili heykeller nedir?'),
(2536, 21, 53, '"Biz atalarımızı bunlara tapıyor bulduk" dediler.'),
(2537, 21, 54, 'Dedi ki: "Andolsun, siz ve atalarınız apaçık bir sapıklık içindesiniz."'),
(2538, 21, 55, '\'Sen bize gerçeği mi getirdin, yoksa (bizimle) oyun oynayanlardan mısın?"'),
(2539, 21, 56, '"Hayır" dedi. "Sizin Rabbiniz göklerin ve yerin Rabbidir, onları Kendisi yaratmıştır ve ben de buna şehadet edenlerdenim."'),
(2540, 21, 57, '"Andolsun Allah\'a, sizler arkanızı dönüp gittikten sonra, ben sizin putlarınıza muhakkak bir tuzak kuracağım."'),
(2541, 21, 58, 'Böylece o, yalnızca büyükleri hariç olmak üzere onları paramparça etti; belki ona başvururlar diye.'),
(2542, 21, 59, '"Bizim ilahlarımıza bunu kim yaptı? Şüphesiz o, zalimlerden biridir" dediler.'),
(2543, 21, 60, '"Kendisine İbrahim denilen bir gencin bunları diline doladığını işittik" dediler.'),
(2544, 21, 61, 'Dediler ki: "Öyleyse, onu insanların gözü önüne getirin ki ona (nasıl bir ceza vereceğimize) şahid olsunlar."'),
(2545, 21, 62, 'Dediler ki: "Ey İbrahim, bunu ilahlarımıza sen mi yaptın?"'),
(2546, 21, 63, '"Hayır" dedi. "Bu yapmıştır, bu onların büyükleridir; eğer konuşabiliyorsa, siz onlara soruverin."'),
(2547, 21, 64, 'Bunun üzerine kendi vicdanlarına başvurdular da; "Gerçek şu ki, zalim olanlar sizlersiniz (biziz)" dediler.'),
(2548, 21, 65, 'Sonra, yine tepeleri üstüne ters döndüler: "Andolsun, bunların konuşamayacaklarını sen de bilmektesin."'),
(2549, 21, 66, 'Dedi ki: "O halde, Allah\'ı bırakıp da sizlere yararı olmayan ve zararı dokunmayan şeylere mi tapıyorsunuz?"'),
(2550, 21, 67, '"Yuh size ve Allah\'tan başka taptıklarınıza. Siz yine de akıllanmayacak mısınız?"'),
(2551, 21, 68, 'Dediler ki: "Eğer (bir şey) yapacaksanız, onu yakın ve ilahlarınıza yardımda bulunun."'),
(2552, 21, 69, 'Biz de dedik ki: "Ey ateş, İbrahim\'e karşı soğuk ve esenlik ol."'),
(2553, 21, 70, 'Ona bir düzen (tuzak) kurmak istediler, fakat Biz onları daha çok hüsrana uğrayanlar kıldık.'),
(2554, 21, 71, 'Onu ve Lut\'u kurtarıp içinde, alemler (insanlık) için bereketler kıldığımız yere (ülkeye) çıkardık.'),
(2555, 21, 72, 'Ona İshak\'ı armağan ettik, üstüne de Yakub\'u; her birini salihler kıldık.'),
(2556, 21, 73, 'Ve onları, Kendi emrimizle hidayete yönelten önderler kıldık ve onlara hayrı kapsayan-fiilleri, namaz kılmayı ve zekat vermeyi vahyettik. Onlar Bize ibadet edenlerdi.'),
(2557, 21, 74, 'Lut\'a da bir hüküm ve ilim verdik ve onu çirkin işler yapmakta olan şehirden kurtardık. Şüphesiz onlar, bozulmaya uğrayan kötü bir kavimdi.'),
(2558, 21, 75, 'Onu rahmetimize soktuk, çünkü o, salihlerdendi.'),
(2559, 21, 76, 'Nuh da; daha önce çağrıda bulunduğu zaman, Biz onun çağrısına cevap verdik, onu ve ailesini büyük bir üzüntüden kurtardık.'),
(2560, 21, 77, 'Ve ayetlerimizi yalanlayan kavimden \'ona yardım edip-öcünü aldık\'. Şüphesiz onlar, kötü bir kavimdi, Biz de onların tümünü suya batırıp boğduk.'),
(2561, 21, 78, 'Davud ve Süleyman da; hani kavmin hayvanlarının içine girip yayıldığı ekin-tarlaları konusunda hüküm yürütüyorlardı. Biz onların hükmüne şahid idik.'),
(2562, 21, 79, 'Biz bunu (hükmü) Süleyman’a kavrattık, her birine hüküm ve ilim verdik. Davud ile birlikte tesbih etsinler diye, dağlara ve kuşlara boyun eğdirdik. (Bunları) Yapanlar Biz idik.'),
(2563, 21, 80, 'Ve sizin için ona, zorlu-savaşınızda sizi korusun diye, \'(madeni) giyim-sanatını\' öğrettik. Buna rağmen siz şükredenler misiniz?'),
(2564, 21, 81, 'Süleyman için de, fırtına biçiminde esen rüzgara (boyun eğdirdik) ki, kendi emriyle, içinde bereketler kıldığımız yere akıp giderdi. Biz herşeyi bilenleriz.'),
(2565, 21, 82, 'Onun için denizde dalgıçlık yapan ve bundan başka iş(ler) de gören şeytanlardan kimseleri de (emrine verdik). Biz onların koruyucuları idik.'),
(2566, 21, 83, 'Eyüp de; hani o Rabbine çağrıda bulunmuştu: "Şüphesiz bu dert (ve hastalık) beni sarıverdi. Sen merhametlilerin en merhametli olanısın."'),
(2567, 21, 84, 'Böylece onun duasına icabet ettik. Kendisinden o derdi giderdik; ona Katımız\'dan bir rahmet ve ibadet edenler için bir zikir olmak üzere ailesini ve onlarla birlikte bir katını daha verdik.'),
(2568, 21, 85, 'İsmail, İdris ve Zü\'l-Kifl, hepsi sabredenlerdendi.'),
(2569, 21, 86, 'Onları rahmetimize soktuk, şüphesiz onlar salih kimselerdi.'),
(2570, 21, 87, 'Balık sahibi (Yunus\'u da); hani o, kızmış vaziyette gitmişti ki; bundan dolayı kendisini sıkıntıya düşürmeyeceğimizi sanmıştı. (Balığın karnındaki) Karanlıklar içinde: "Senden başka İlah yoktur, Sen Yücesin, gerçekten ben zulmedenlerden oldum" diye çağrıda bulunmuştu.'),
(2571, 21, 88, 'Bunun üzerine duasına icabet ettik ve onu üzüntüden kurtardık. İşte Biz, iman edenleri böyle kurtarırız.'),
(2572, 21, 89, 'Zekeriya da; hani Rabbine çağrıda bulunmuştu: "Rabbim, beni yalnız başıma bırakma, sen mirasçıların en hayırlısısın."'),
(2573, 21, 90, 'Onun duasına icabet ettik, kendisine Yahya\'yı armağan ettik, eşini de doğurmaya elverişli kıldık. Gerçekten onlar hayırlarda yarışırlardı, umarak ve korkarak Bize dua ederlerdi. Bize derin saygı gösterirlerdi.'),
(2574, 21, 91, 'Irzını koruyan (Meryem); Biz ona Kendi ruhumuzdan üfledik, onu ve çocuğunu insanlığa bir ayet kıldık.'),
(2575, 21, 92, 'Gerçekten, sizin bu ümmetiniz tek bir ümmettir. Ben de sizin Rabbinizim, öyleyse Bana ibadet ediniz.'),
(2576, 21, 93, 'Onlar, işlerini kendi aralarında parça parça dağıttılar (dinlerinde bölünmeler yaptılar); hepsi Bize döneceklerdir.'),
(2577, 21, 94, 'Artık kim, bir mü\'min olarak salih amellerde bulunursa, onun çabası için (karşılık olarak) küfran (nankörlük) yoktur. Şüphesiz Biz, onun yazıcılarıyız.'),
(2578, 21, 95, 'Yıkıma uğrattığımız bir ülkeye (tekrar dünya hayatı) imkansız (haram)dır; hiç şüphesiz onlar, (dünyaya) bir daha geri dönmeyecekler.'),
(2579, 21, 96, 'Yecuc ve Mecuc (un sedleri) açıldığında, onlar her bir tepeden akın ederler;'),
(2580, 21, 97, 'Gerçek olan va\'d yaklaşmıştır, işte o zaman, inkar edenlerin gözleri yuvalarından fırlayacak: "Eyvahlar bize, biz bundan tam bir gaflet içindeydik, hayır, bizler zalim kimselerdik" (diyecekler).'),
(2581, 21, 98, 'Gerçekten siz de, Allah\'ın dışında taptıklarınız da cehennemin odunusunuz, siz ona varacaksınız.'),
(2582, 21, 99, 'Eğer onlar (gerçek) ilahlar olsalardı, ona girmeyeceklerdi. Oysa onların tümü içinde temelli kalıcıdırlar.'),
(2583, 21, 100, 'Orda kendileri için, \'kemikleri çatırdatan inlemeler\' vardır. Onlar orda işitmezler de.'),
(2584, 21, 101, 'Ama Bizden kendilerine güzellik geçmiş bulunanlar; işte, onlar, ondan uzaklaştırılmışlardır.'),
(2585, 21, 102, 'Onun uğultusunu bile duymazlar. Onlar nefislerinin arzuladığı (sayısız nimet) içinde ebedi kalıcıdırlar.'),
(2586, 21, 103, 'Onları, o en büyük korku hüzne kaptırmaz ve: "İşte bu sizin gününüzdür, size va\'dedilmişti" diye melekler onları karşılayacaklardır.'),
(2587, 21, 104, 'Bizim, göğü kitabın sahifelerini katlar gibi katlayacağımız gün, ilk yaratmaya başladığımız gibi, yine onu (eski durumuna) iade edeceğiz. Bu, Bizim üzerimizde bir vaiddir. Elbette, Biz yapıcılarız.'),
(2588, 21, 105, 'Andolsun, Biz zikirden sonra Zebur\'da da: "Şüphesiz Arz\'a salih kullarım varisçi olacaktır" diye yazdık.'),
(2589, 21, 106, 'Gerçek şu ki kulluk eden bir topluluk için bunda (Kur\'an\'da) \'açık bir mesaj\' (veya gerçek bir çıkış yolu) vardır.'),
(2590, 21, 107, 'Biz seni alemler için yalnızca bir rahmet olarak gönderdik.'),
(2591, 21, 108, 'De ki: "Gerçekten bana: -Sizin İlahınız yalnızca bir tek İlah\'tır" diye vahyolunuyor; artık siz Müslüman olacak mısınız?"'),
(2592, 21, 109, 'Buna rağmen yüz çevirecek olurlarsa, de ki: "Size eşitlik üzere açıklamada bulundum. Tehdit edildiğiniz (sorgu ve azap günü) yakın mı, uzak mı, bilemem."'),
(2593, 21, 110, '"Şüphesiz O, sözün açıkta söylenenini de bilmekte, saklamakta olduklarınızı da bilmektedir."'),
(2594, 21, 111, '"Bilemem; belki bu (sürenin açıklanmaması), sizin için bir (fitne) denemedir, (belki de) belli bir vakte kadar yararlanma (meta)dır."'),
(2595, 21, 112, '(Resulullah) Dedi ki: "Rabbim, hak ile hükmet. Bizim Rabbimiz, sizin her türlü nitelendirmelerinize karşı yardımına sığınılan Rahman (olan Allah)dır."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Ey insanlar, Rabbinizden korkup-sakının, çünkü kıyamet saatinin sarsıntısı büyük bir şeydir.'),
(2597, 22, 2, 'Onu gördüğünüz gün, her emzikli kendi emzirdiğini unutup geçecek ve her gebe kendi yükünü düşürecektir. İnsanları da sarhoş olmuş görürsün, oysa onlar sarhoş değillerdir. Ancak Allah\'ın azabı pek şiddetlidir.'),
(2598, 22, 3, 'İnsanlardan kimi, Allah hakkında bilgisi olmaksızın tartışır durur ve her azgın-kaypak şeytanın peşine düşer.'),
(2599, 22, 4, 'Ona yazılmıştır: "Kim onu veli edinirse, şüphesiz o (şeytan) onu şaşırtıp-saptırır ve onu çılgın ateşin azabına yöneltir."'),
(2600, 22, 5, 'Ey insanlar, eğer dirilişten yana bir kuşku içindeyseniz, gerçek şu ki, Biz sizi topraktan yarattık, sonra bir damla sudan, sonra bir alak\'tan (embriyo), sonra yaratılış biçimi belli belirsiz bir çiğnem et parçasından; size (kudretimizi) açıkça göstermek için. Dilediğimizi, adı konulmuş bir süreye kadar rahimlerde tutuyoruz. Sonra sizi bebek olarak çıkarıyoruz, sonra da erginlik çağına erişmeniz için (sizi büyütüyoruz). Sizden kiminizin hayatına son verilmekte, kiminiz de, bildikten sonra hiçbir şey bilmeme durumuna gelmesi için ömrün en aşağı ucuna (yaşlılığa) geri çevrilmektedir. Yeryüzünü kupkuru ölü gibi görürsün, fakat Biz onun üzerine suyu indirdiğimiz zaman titreşir, kabarır ve her güzel çiftten (ürünler) bitirir.'),
(2601, 22, 6, 'İşte böyle; şüphesiz Allah, hakkın Kendisi\'dir ve şüphesiz ölüleri diriltir ve gerçekten herşeye güç yetirendir.'),
(2602, 22, 7, 'Gerçek şu ki, kıyamet-saati yaklaşarak gelmektedir, onda şüphe yoktur. Gerçekten Allah kabirlerde olanları diriltecektir.'),
(2603, 22, 8, 'İnsanlardan kimi, hiçbir bilgisi, yol göstericisi ve aydınlatıcı kitabı olmaksızın Allah hakkında tartışır-durur.'),
(2604, 22, 9, 'Allah\'ın yolundan saptırmak amacıyla \'gururla salınıp-kasılarak\' (bunu yapar); dünyada onun için aşağılanma vardır, kıyamet günü de yakıcı azabı ona taddıracağız.'),
(2605, 22, 10, '(Ey insan) Bu, senin ellerinin önden takdim ettikleridir. Şüphesiz Allah, kullar için zulmedici değildir.'),
(2606, 22, 11, 'İnsanlardan kimi, Allah\'a bir ucundan ibadet eder, eğer kendisine bir hayır dokunursa, bununla tatmin bulur ve eğer kendisine bir fitne isabet edecek olursa yüzü üstü dönüverir. O, dünyayı kaybetmiştir, ahireti de. İşte bu, apaçık bir kayıptır.'),
(2607, 22, 12, 'Allah\'tan başka, kendisine ne zararı dokunan, ne yararı olan şeylere yakarır. İşte bu, en uzak bir sapıklıktır.'),
(2608, 22, 13, '(Ya da) Zararı, yararından daha yakın olana tapar; ne kötü yardımcı ve ne kötü yoldaştır.'),
(2609, 22, 14, 'Şüphesiz Allah, iman edip salih amellerde bulunanları, altından ırmaklar akan cennetlere sokar. Gerçekten Allah, her istediğini yapar.'),
(2610, 22, 15, 'Kim, Allah\'ın ona, dünyada ve ahirette kesin olarak yardım etmeyeceğini sanıyorsa, göğe bir araç uzatsın sonra kesiversin de bir bakıversin, kurduğu düzen, onun öfkesini giderebilecek mi?'),
(2611, 22, 16, 'İşte Biz onu (Kur\'an\'ı) apaçık ayetler olarak indirdik; şüphesiz Allah, dilediğini hidayete yöneltir.'),
(2612, 22, 17, 'Gerçekten iman edenler, Yahudiler, yıldıza tapanlar (Sabii) Hıristiyanlar, ateşe tapanlar (Mecusi) ve şirk koşanlar; şüphesiz Allah, kıyamet günü aralarını ayıracaktır. Doğrusu Allah, herşeyin üzerinde şahid olandır.'),
(2613, 22, 18, 'Görmedin mi ki, gerçekten, göklerde ve yerde olanlar, güneş, ay, yıldızlar, dağlar, ağaçlar, hayvanlar ve insanlardan birçoğu Allah\'a secde etmektedirler. Birçoğu üzerine azap hak olmuştur. Allah kimi aşağılık kılarsa, artık onun için bir yüceltici yoktur. Şüphesiz Allah, dilediğini yapar.'),
(2614, 22, 19, 'İşte bunlar çekişen iki gruptur, Rableri konusunda çekiştiler. İşte o inkar edenler, onlar için ateşten elbiseler biçilmiştir; başları üstünden de kaynar su dökülür.'),
(2615, 22, 20, 'Bununla karınları içinde olanlar ve derileri eritilmiş olur.'),
(2616, 22, 21, 'Onlar için demirden kamçılar vardır.'),
(2617, 22, 22, 'Ne zaman ordan, sarsıcı-üzüntüden çıkmak isterlerse, oraya geri çevrilirler ve (onlara:) "Yakıcı azabı tadın" (denir).'),
(2618, 22, 23, 'Hiç şüphesiz Allah, iman edenleri ve salih amellerde bulunanları altından ırmaklar akan cennetlere sokar, orada altından bileziklerle ve incilerle süslenirler; ordaki elbiseleri ipek(ten)tir.'),
(2619, 22, 24, 'Onlar, sözün en güzeline iletilmişlerdir ve övülen doğru yola iletilmişlerdir.'),
(2620, 22, 25, 'Gerçek şu ki, inkar edip Allah yolundan ve yerlilerle dışarıdan gelenler için eşit olarak (haram ve kıble) kıldığımız Mescid-i Haram\'dan alıkoyanlara, orada zulmederek adaletten ayrılanlara acı bir azap taddırırız.'),
(2621, 22, 26, 'Hani Biz İbrahim\'e Evin (Kabe\'nin) yerini belirtip hazırladığımız zaman (şöyle emretmiştik:) "Bana hiçbir şeyi ortak koşma, tavaf edenler, kıyam edenler, rükua ve sücuda varanlar için Evimi tertemiz tut."'),
(2622, 22, 27, '"İnsanlar içinde haccı duyur; gerek yaya, gerekse uzak yollardan (derin vadilerden) gelen yorgun düşmüş develer üstünde sana gelsinler."'),
(2623, 22, 28, 'Kendileri için birtakım yararlara şahid olsunlar ve kendilerine rızık olarak verdiği (kurbanlık) hayvanlar üzerine belli günlerde (kurban adarken) Allah\'ın adını ansınlar. Artık bunlardan yiyin ve zorluk çeken yoksulu da doyurun.'),
(2624, 22, 29, 'Sonra kirlerini gidersinler, adaklarını yerine getirsinler. Beyt-i Atik\'i tavaf etsinler.'),
(2625, 22, 30, 'İşte böyle; kim Allah\'ın haram kıldıklarını (gözetip hükümlerini) yüceltirse, Rabbinin Katında kendisi için hayırlıdır. Size (haklarında yasaklar) okunanlar dışındaki hayvanlar helal kılındı. Öyleyse iğrenç bir pislik olan putlardan kaçının, yalan söz söylemekten de kaçının.'),
(2626, 22, 31, 'Allah\'ı birleyen (Hanif)ler olarak, O\'na (hiçbir) ortak koşmaksızın. Kim Allah\'a ortak koşarsa, sanki o gökten düşmüş de onu bir kuş kapıvermiş veya rüzgar onu ıssız bir yere sürükleyip atmış gibidir.'),
(2627, 22, 32, 'İşte böyle; kim Allah\'ın şiarlarını yüceltirse, şüphesiz bu, kalplerin takvasındandır.'),
(2628, 22, 33, 'Onlarda sizin için adı konulmuş bir süreye kadar yararlar vardır. Sonra onların yerleri Beyt-i Atik\'tir.'),
(2629, 22, 34, 'Biz her ümmet için bir "Mensek" kıldık, O\'nun kendilerine rızık olarak verdiği (kurbanlık) hayvanlar üzerine Allah\'ın adını ansınlar diye. İşte sizin İlahınız bir tek İlah\'tır, artık yalnızca O\'na teslim olun. Sen alçak gönüllü olanlara müjde ver.'),
(2630, 22, 35, 'Onlar ki, Allah anıldığı zaman kalpleri ürperir; kendilerine isabet eden musibetlere sabredenler, namazı dosdoğru kılanlar ve rızık olarak verdiklerimizden infak edenlerdir.'),
(2631, 22, 36, 'İri cüsseli develeri size Allah\'ın işaretlerinden kıldık, sizler için onlarda bir hayır vardır. Öyleyse onlar bir dizi halinde (veya saf tutmuşcasına ayakta durup) boğazlanırken Allah\'ın adını anın; yanları üzerine yattıkları zaman da onlardan yiyin, kanaatkara ve isteyene yedirin. İşte böyle, onlara sizin için boyun eğdirdik, umulur ki şükredersiniz.'),
(2632, 22, 37, 'Onların etleri ve kanları kesin olarak Allah\'a ulaşmaz, ancak O\'na sizden takva ulaşır. İşte böyle, onlara sizin için boyun eğdirmiştir; O\'nun size hidayet vermesine karşılık Allah\'ı tekbir etmeniz için. Güzellikte bulunanlara müjde ver.'),
(2633, 22, 38, 'Şüphesiz Allah, (müşriklerin saldırı ve sinsi tuzaklarını) iman edenlerden uzaklaştırmaktadır. Gerçekten Allah, hain ve nankör olan kimseyi sevmez.'),
(2634, 22, 39, 'Kendilerine zulmedilmesi dolayısıyla, onlara karşı savaş açılana (mü\'minlere, savaşma) izni verildi. Şüphesiz Allah, onlara yardım etmeye güç yetirendir.'),
(2635, 22, 40, 'Onlar, yalnızca; "Rabbimiz Allah\'tır" demelerinden dolayı, haksız yere yurtlarından sürgün edilip çıkarıldılar. Eğer Allah\'ın, insanların kimini kimiyle defetmesi (yenilgiye uğratması) olmasaydı, manastırlar, kiliseler, havralar ve içinde Allah\'ın isminin çokça anıldığı mescidler, muhakkak yıkılır giderdi. Allah Kendi (dini)ne yardım edenlere kesin olarak yardım eder. Şüphesiz Allah, güçlü olandır, Aziz olandır.'),
(2636, 22, 41, 'Onlar ki, yeryüzünde kendilerini yerleştirir, iktidar sahibi kılarsak, dosdoğru namazı kılarlar, zekatı verirler, ma\'rufu emrederler, münkerden sakındırırlar. Bütün işlerin sonu Allah\'a aittir.'),
(2637, 22, 42, 'Eğer seni yalanlıyorlarsa, onlardan önce Nuh, Ad, Semud kavmi de yalanlamıştı.'),
(2638, 22, 43, 'İbrahim\'in kavmi ve Lut\'un kavmi de:'),
(2639, 22, 44, 'Medyen halkı da (peygamberlerini yalanlamıştı). Musa da yalanlanmıştı. Böylelikle Ben, o inkar edenlere bir süre tanıdım, sonra onları yakalayıverdim. Nasılmış Benim (herşeyi alt üst edip kökten değiştiren) inkılabım (veya inkarım).'),
(2640, 22, 45, '(Halkı) Zulmediyorken yıkıma uğrattığımız nice ülkeler vardır ki, şimdi onların altları üstlerine gelmiş ıpıssız durmakta, kullanılamaz durumdaki kuyuları (terk edilmiş bulunmakta), yüksek sarayları (çın çın ötmektedir).'),
(2641, 22, 46, 'Yeryüzünde gezip dolaşmıyorlar mı, böylece onların kendisiyle akledebilecek kalpleri ve işitebilecek kulakları oluversin? Çünkü doğrusu, gözler kör olmaz, ancak sinelerdeki kalpler körelir.'),
(2642, 22, 47, 'Onlar senden, azabın çarçabuk getirilmesini istiyorlar; Allah, va\'dine kesin olarak muhalefet etmez. Gerçekten, senin Rabbinin Katında bir gün, sizin saymakta olduklarınızdan bin yıl gibidir.'),
(2643, 22, 48, 'Nice ülkeler vardır ki, (halkı) zulmediyorken Ben ona bir süre tanıdım, sonra yakalayıverdim; dönüş yalnızca Banadır.'),
(2644, 22, 49, 'De ki: "Ey insanlar, gerçekten ben sizin için yalnızca bir uyarıcıyım."'),
(2645, 22, 50, 'Buna göre, iman edip salih amellerde bulunanlar, onlar için bir bağışlanma (mağfiret) ve üstün bir rızık vardır.'),
(2646, 22, 51, 'Ayetlerimiz konusunda acze düşürücü çabalar harcayanlar, alevli ateşin halkıdır.'),
(2647, 22, 52, 'Biz senden önce hiçbir Resul ve Nebi göndermiş olmayalım ki, o bir dilekte bulunduğu zaman, şeytan, onun dilediğine (bir kuşku veya sapma unsuru) katıp bırakmış olmasın. Ama Allah, şeytanın katıp-bırakmalarını giderir, sonra Kendi ayetlerini sağlamlaştırıp-pekiştirir. Allah, gerçekten bilendir, hüküm ve hikmet sahibidir.'),
(2648, 22, 53, 'Şeytanın (bu tür) katıp bırakmaları, kalplerinde hastalık olanlara ve kalpleri (her türlü) duyarlılıktan yoksun bulunanlara (Allah\'ın) bir deneme kılması içindir. Şüphesiz zalimler, (gerçeğin kendisinden) uzak bir ayrılık içindedirler.'),
(2649, 22, 54, '(Bir de) Kendilerine ilim verilenlerin, bunun (Kur\'an\'ın) hiç tartışmasız Rablerinden olan bir gerçek olduğunu bilmeleri için; böylelikle ona iman etsinler ve kalpleri ona tatmin bulmuş olarak bağlansın. Şüphesiz Allah, iman edenleri dosdoğru yola yöneltir.'),
(2650, 22, 55, 'İnkar edenler ise, kıyamet-saati onlara apansız gelinceye veya kesintiye uğramış (akim, verimsiz) bir günün azabı onlara yetişinceye kadar ondan (Kur\'an\'dan) yana şüphe içinde sür-git kalacaklardır.'),
(2651, 22, 56, 'Mülk, o gün yalnızca Allah\'ındır. O, aralarında hükmedecektir. Artık iman edip salih amellerde bulunanlar; nimetlerle donatılmış cennetler içindedirler.'),
(2652, 22, 57, 'İnkar edip ayetlerimizi yalanlayanlar; artık onlar için aşağılatıcı bir azap vardır.'),
(2653, 22, 58, 'Allah yolunda hicret edip öldürülen veya ölenlere gelince muhakkak Allah, onları güzel bir rızıkla rızıklandıracaktır. Şüphesiz Allah, rızık verenlerin en hayırlısıdır.'),
(2654, 22, 59, 'Onları, kendisinden gerçekten hoşnut kalacakları bir yere sokacaktır. Şüphesiz Allah, bilendir, halimdir.'),
(2655, 22, 60, 'İşte böyle; her kim kendisine yapılan haksızlığın benzeriyle karşılık verir, sonra aleyhine \'azgınlık ve saldırıda\' bulunulursa, Allah, mutlaka ona yardım eder. Şüphesiz Allah, affedicidir, bağışlayıcıdır.'),
(2656, 22, 61, 'İşte böyle; çünkü Allah, geceyi gündüze bağlayıp katar ve gündüzü geceye bağlayıp-katar. Şüphesiz Allah, işitendir, görendir.'),
(2657, 22, 62, 'İşte böyle; çünkü Allah, hakkın ta Kendisi\'dir. O\'nun dışında, onların taptıkları ise, şüphesiz batılın ta kendisidir. Gerçekten Allah, Yücedir, büyüktür.'),
(2658, 22, 63, 'Görmedin mi, Allah, gökten su indirdi, böylece yeryüzü yemyeşil donatıldı. Şüphesiz Allah, lütfedicidir, herşeyden haberdardır.'),
(2659, 22, 64, 'Göklerde ve yerde her ne varsa O\'nundur. Şüphesiz Allah, hiçbir şeye ihtiyacı olmayan (Gani)dır, övülmeye layık olandır.'),
(2660, 22, 65, 'Görmedin mi, Allah, yerdekileri ve denizde onun emriyle akıp giden gemileri, sizin yararınıza verdi. Ve izni olmadıkça, göğü yerin üstüne düşmekten alıkoyar. Şüphesiz Allah, insanlara karşı şefkatlidir, çok merhametlidir.'),
(2661, 22, 66, 'Sizi diri tutan, sonra öldürecek, sonra da diriltecek olan O\'dur. Gerçekten insan pek nankördür.'),
(2662, 22, 67, 'Biz her ümmete bir ibadet tarzı (Mensek) kıldık, onlar bu tarz üzere ibadet etmektedirler. Öyleyse, (din) iş(in)de seninle çekişmesinler. Sen, Rabbine çağır. Şüphesiz sen dosdoğru bir hidayet üzerindesin.'),
(2663, 22, 68, 'Eğer seninle mücadeleye girişirlerse, de ki: "Allah, yapmakta olduklarınızı daha iyi bilir."'),
(2664, 22, 69, '"Allah, kıyamet günü, kendisinde ihtilafa düştüğünüz şey hakkında aranızda hükmedecektir."'),
(2665, 22, 70, 'Allah\'ın, gökte ve yerde olanların hepsini bilmekte olduğunu bilmiyor musun? Gerçekten bunlar bir kitaptadır. Hiç şüphesiz bunlar(ı bilmek), Allah için pek kolaydır.'),
(2666, 22, 71, 'Onlar, Allah\'ı bırakıp da (Allah\'ın) kendisine bir delil indirmediği ve haklarında (hiçbir) bilgileri olmayan şeylere tapıyorlar. Zulmedenler için hiçbir yardımcı yoktur.'),
(2667, 22, 72, 'Onlara karşı apaçık olan ayetlerimiz okunduğu zaman, sen o inkar edenlerin yüzlerindeki \'red ve inkarı\' tanıyabilirsin. Neredeyse, kendilerine karşı ayetlerimizi okuyanın üzerine çullanıverecekler. De ki: "Size, bundan daha kötü olanını haber vereyim mi? Ateş... Allah, onu inkar edenlere va\'detmiş bulunmaktadır; ne kötü bir duraktır."'),
(2668, 22, 73, 'Ey insanlar, (size) bir örnek verildi; şimdi onu dinleyin. Sizin, Allah\'ın dışında tapmakta olduklarınız -hepsi bunun için biraraya gelseler dahi- gerçekten bir sinek bile yaratamazlar. Eğer sinek onlardan bir şey kapacak olsa, bunu da ondan geri alamazlar. İsteyen de güçsüz, istenen de.'),
(2669, 22, 74, 'Onlar, Allah\'ın kadrini hakkıyla takdir edemediler. Şüphesiz Allah, güç sahibidir, Azizdir.'),
(2670, 22, 75, 'Allah, meleklerden elçiler seçer ve insanlardan da. Şüphesiz Allah, işitendir, görendir.'),
(2671, 22, 76, 'O, önlerindekini ve arkalarındakini bilir. Bütün işler Allah\'a döndürülür.'),
(2672, 22, 77, 'Ey iman edenler, rüku edin, secdeye varın, Rabbinize ibadet edin ve hayır işleyin, umulur ki kurtuluş bulursunuz.'),
(2673, 22, 78, 'Allah adına gerektiği gibi mücadele edin. O, sizleri seçmiş ve din konusunda size bir güçlük yüklememiştir, atanız İbrahim\'in dini(nde olduğu gibi). O (Allah) bundan daha önce de, bunda (Kur\'an\'da) da sizi "Müslümanlar" olarak isimlendirdi; elçi sizin üzerinize şahid olsun, siz de insanlar üzerine şahidler olasınız diye. Artık dosdoğru namazı kılın, zekatı verin ve Allah\'a sarılın, sizin Mevlanız O\'dur. İşte, ne güzel mevla ve ne güzel yardımcı.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Mü\'minler gerçekten felah bulmuştur;'),
(2675, 23, 2, 'Onlar namazlarında hûşû içinde olanlardır;'),
(2676, 23, 3, 'Onlar, \'tümüyle boş\' şeylerden yüz çevirenlerdir;'),
(2677, 23, 4, 'Onlar, zekata ilişkin (söz ve görevlerini mutlaka) yerine getirenlerdir;'),
(2678, 23, 5, 'Ve onlar ırzlarını koruyanlardır;'),
(2679, 23, 6, 'Ancak eşleri ya da sağ ellerinin sahip olduklarına karşı (tutumları) hariç; bu konuda kınanmış değillerdir.'),
(2680, 23, 7, 'Fakat kim bundan ötesini ararsa, artık onlar sınırı çiğneyenlerdir.'),
(2681, 23, 8, '(Yine) Onlar, emanetlerine ve ahidlerine riayet edenlerdir.'),
(2682, 23, 9, 'Onlar, namazlarını da (titizlikle) koruyanlardır.'),
(2683, 23, 10, 'İşte (yeryüzünün hakimiyetine ve ahiretin nimetlerine) varis olacak onlardır.'),
(2684, 23, 11, 'Ki onlar Firdevs (cennetlerin)e de varis olacaklardır; içinde de ebedi olarak kalacaklardır.'),
(2685, 23, 12, 'Andolsun, Biz insanı, süzme bir çamurdan yarattık.'),
(2686, 23, 13, 'Sonra onu bir su damlası olarak, savunması sağlam bir karar yerine yerleştirdik.'),
(2687, 23, 14, 'Sonra o su damlasını bir alak (embriyo) olarak yarattık; ardından o alak\'ı (hücre topluluğu) bir çiğnem et parçası olarak yarattık; daha sonra o çiğnem et parçasını kemik olarak yarattık; böylece kemiklere de et giydirdik; sonra bir başka yaratışla onu inşa ettik. Yaratıcıların en güzeli olan Allah, ne Yücedir.'),
(2688, 23, 15, 'Sonra bunun ardından siz gerçekten ölecek olanlarsınız.'),
(2689, 23, 16, 'Sonra siz gerçekten kıyamet günü diriltileceksiniz.'),
(2690, 23, 17, 'Andolsun, Biz sizin üstünüzde yedi yol yarattık; Biz yaratmada gafiller değiliz.'),
(2691, 23, 18, 'Biz gökten belli bir miktarda su indirdik ve onu yeryüzünde yerleştirdik; şüphesiz Biz onu (kurutup) giderme gücüne de sahibiz.'),
(2692, 23, 19, 'Böylelikle, bununla size hurmalıklardan, üzümlüklerden bahçeler-bağlar geliştirdik, içlerinde çok sayıda yemişler vardır; sizler onlardan yemektesiniz.'),
(2693, 23, 20, 'Ve (daha çok) Tur-i Sina\'da çıkan bir ağaç (türü de yarattık); o yağlı ve yiyenlere bir katık olarak bitmekte (ürün vermekte)dir.'),
(2694, 23, 21, 'Gerçekten hayvanlarda da sizin için bir ders (ibret) vardır; karınlarının içinde olanlardan size içirmekteyiz ve onlarda sizin için daha birçok yararlar var. Sizler onlardan yemektesiniz.'),
(2695, 23, 22, 'Onların üzerinde ve gemilerde taşınmaktasınız.'),
(2696, 23, 23, 'Andolsun, Biz Nuh\'u kendi kavmine (elçi olarak) gönderdik. Böylece kavmine dedi ki: "Ey Kavmim, Allah\'a kulluk edin. O\'nun dışında sizin başka İlahınız yoktur, yine de sakınmayacak mısınız?"'),
(2697, 23, 24, 'Bunun üzerine, kavminden inkara sapmış önde gelenler dediler ki: "Bu, sizin benzeriniz olan bir beşerden başkası değildir. Size karşı üstünlük elde etmek istiyor. Eğer Allah (öne sürdüklerini) dilemiş olsaydı, muhakkak melekler indirirdi. Hem biz geçmiş atalarımızdan da bunu işitmiş değiliz."'),
(2698, 23, 25, '"O, kendisinde delilik bulunan bir adamdan başkası değildir, onu belli bir süre gözetleyin."'),
(2699, 23, 26, '"Rabbim" dedi (Nuh). "Beni yalanlamalarına karşılık, bana yardım et."'),
(2700, 23, 27, 'Böylelikle Biz ona: "Gözetimimiz altında ve vahyimizle gemi yap. Nitekim Bizim emrimiz gelip de tandır kızışınca, onun içine her (tür hayvandan) ikişer çift ile, içlerinden aleyhlerine söz geçmiş (azap gerekmiş) olanlar dışında olan aileni de alıp koy; zulmedenler konusunda Bana muhatap olma, çünkü onlar boğulacaklardır" diye vahyettik.'),
(2701, 23, 28, '"Böylece sen, beraberinde olanlarla gemiye bindiğinde o zaman de ki: "Bizi o zulmeden kavimden kurtaran Allah\'a hamd olsun."'),
(2702, 23, 29, 'Ve de ki: "Rabbim, beni kutlu bir konakta indir, Sen konuklayanların en hayırlısısın."'),
(2703, 23, 30, 'Hiç şüphesiz bunda ayetler vardır ve Biz gerçekten denemeden geçiririz.'),
(2704, 23, 31, 'Sonra onların ardından bir başka insan-nesli yaratıp-inşa ettik.'),
(2705, 23, 32, 'Onlara da kendi içlerinden: "Allah\'a ibadet edin. O\'nun dışında sizin başka İlahınız yoktur, yine de sakınmayacak mısınız?" (desin) diye içlerinden bir elçi gönderdik.'),
(2706, 23, 33, 'Kendi kavminden, inkar edip ahirete kavuşmayı yalanlayan ve kendilerine, dünya hayatında refah verdiğimiz önde gelenler dedi ki: "Bu, sizin benzeriniz olan bir beşerden başkası değildir, kendisi de sizin yediklerinizden yemekte ve içtiklerinizden içmektedir."'),
(2707, 23, 34, '"Eğer sizin benzeriniz olan bir beşere boyun eğecek olursanız, andolsun, siz gerçekten hüsrana uğrayanlar olursunuz."'),
(2708, 23, 35, '"O, öldüğünüz, toprak ve kemik haline geldiğiniz zaman, sizin mutlaka (yeniden diriltilip) çıkarılacağınızı mı va\'dediyor?"'),
(2709, 23, 36, '"Heyhat, size va\'dedilen şeye heyhat..."'),
(2710, 23, 37, '"O (bütün gerçek), yalnızca bizim (yaşamakta olduğumuz bu) dünya hayatımızdan ibarettir; ölürüz ve yaşarız, biz diriltilecekler değiliz."'),
(2711, 23, 38, '"O ise, yalnızca bir adam (insan)dır, Allah\'a karşı yalan uydurmaktadır, bizler de ona inanacak değiliz."'),
(2712, 23, 39, '(Peygamber) Dedi ki: "Rabbim, beni yalanlamalarına karşı bana yardım et."'),
(2713, 23, 40, '(Allah) Dedi ki: "Az bir süre (bekle), onlar gerçekten pişman olacaklar."'),
(2714, 23, 41, 'Derken, hak (ettikleri cezaya karşılık) olmak üzere, o korkunç çığlık onları yakalayıverdi. Böylece onları bir süprüntü kılıverdik. Zulmeden kavim için yıkım olsun.'),
(2715, 23, 42, 'Sonra onların ardından başka nesiller yaratıp-inşa ettik.'),
(2716, 23, 43, 'Ümmetlerden hiçbiri, kendisine tespit edilmiş eceli ne öne alabilir, ne erteleyebilir.'),
(2717, 23, 44, 'Sonra birbiri peşi sıra elçilerimizi gönderdik; her ümmete kendi elçisi geldiğinde, onu yalanladılar. Böylece Biz de onları (yıkıma uğratıp yok etmede) kimini kiminin izinde yürüttük ve onları (tarihin anlatıp aktardığı) bir olay kıldık. İman etmeyen kavim için yıkım olsun.'),
(2718, 23, 45, 'Sonra Musa ve kardeşi Harun\'u ayetlerimizle ve apaçık bir delille gönderdik.'),
(2719, 23, 46, 'Firavun\'a ve ileri gelen çevresine; fakat onlar büyüklendiler. Onlar, \'büyüklenen-zorba\' bir topluluktu.'),
(2720, 23, 47, 'Dediler ki: "Bizim benzerimiz olan iki beşere mi inanacak mışız? Kaldı ki, onların kavimleri bize kullukta (kölelikte) bulunmaktadırlar."'),
(2721, 23, 48, 'Böylece onları yalanladılar ve yıkıma uğrayanlardan oldular.'),
(2722, 23, 49, 'Andolsun, Biz Musa’ya kitabı verdik, belki onlar hidayete erer diye.'),
(2723, 23, 50, 'Biz, Meryem\'in oğlunu ve annesini bir ayet kıldık ve ikisini barınmaya elverişli ve akar suyu olan bir tepede yerleştirdik.'),
(2724, 23, 51, 'Ey elçiler, güzel ve temiz olan şeylerden yiyin ve salih amellerde bulunun; çünkü gerçekten ben yapmakta olduklarınızı biliyorum.'),
(2725, 23, 52, 'İşte sizin ümmetiniz bir tek ümmettir ve Ben de sizin Rabbinizim; öyleyse Benden korkup-sakının.'),
(2726, 23, 53, 'Ancak onlar, işlerini kendi aralarında (farklı) kitaplar halinde böldüler; her bir grup, kendi ellerinde olanla yetinip sevinmektedir.'),
(2727, 23, 54, 'Artık sen onları, belli bir süreye kadar kendi gafletleri içinde bırak.'),
(2728, 23, 55, 'Onlar sanıyorlar mı ki, kendilerine verdiğimiz mal ve çocuklarla'),
(2729, 23, 56, 'Biz onların hayırlarına koşuyoruz (veya yardım ediyoruz)? Hayır, onlar şuurunda değiller.'),
(2730, 23, 57, 'Gerçekten, Rablerine olan haşyetlerinden dolayı saygıyla korkanlar,'),
(2731, 23, 58, 'Rablerinin ayetlerine iman edenler,'),
(2732, 23, 59, 'Rablerine ortak koşmayanlar,'),
(2733, 23, 60, 'Ve gerçekten Rablerine dönecekler diye, vermekte olduklarını kalpleri ürpererek verenler;'),
(2734, 23, 61, 'İşte onlar, hayırlarda yarışmaktadırlar ve onlar bundan dolayı öne geçmektedirler.'),
(2735, 23, 62, 'Hiç kimseye güç yetireceğinden fazlasını yüklemeyiz; elimizde hakkı söylemekte olan bir kitap vardır ve onlar hiçbir haksızlığa uğratılmazlar.'),
(2736, 23, 63, 'Hayır, onların kalpleri bundan dolayı bir gaflet içindedir. Üstelik onların, bunun dışında yapmakta oldukları (birtakım şeyler) vardır; onlar bunun için çalışmaktadırlar.'),
(2737, 23, 64, 'Nihayet, onların refahtan şımaran önde gelenlerini azap ile yakalayıverdiğimiz zaman, onlar hemen feryadı basacaklar.'),
(2738, 23, 65, 'Bugün feryad etmeyin, çünkü Bizden yardım göremezsiniz.'),
(2739, 23, 66, 'Gerçekten Benim ayetlerim size okunuyordu, fakat siz topuklarınız üzerinde geri dönüyordunuz;'),
(2740, 23, 67, 'Buna (ayetlerime) karşı büyüklük taslayarak; gece vakti de hezeyanlar sergiliyordunuz.'),
(2741, 23, 68, 'Onlar, yine de o sözü (Kur\'an\'ı) gereği gibi düşünmediler mi, yoksa onlara, geçmişteki atalarına gelmeyen bir şey mi geldi?'),
(2742, 23, 69, 'Ya da kendi elçilerini tanımadılar mı ki, şimdi onu inkar ediyorlar?'),
(2743, 23, 70, 'Yahut: "Onda bir delilik var" mı diyorlar? Hayır, o, onlara hak ile gelmiş bulunmaktadır ve onların çoğu hakkı çirkin karşılıyorlar.'),
(2744, 23, 71, 'Eğer hak, onların heva (istek ve tutku)larına uyacak olsaydı hiç tartışmasız, gökler, yer ve bunların içinde olan herkes (ve herşey) bozulmaya uğrardı. Hayır, Biz onlara kendi şan ve şeref (zikir)lerini getirmiş bulunuyoruz, fakat onlar kendi zikirlerinden yüz çeviriyorlar.'),
(2745, 23, 72, 'Yoksa sen onlardan haraç mı istiyorsun? İşte Rabbinin haracı (dünya ve ahiret armağanı) daha hayırlıdır. O, rızık verenlerin en hayırlısıdır.'),
(2746, 23, 73, 'Gerçekten sen onları dosdoğru olan bir yola çağırıyorsun.'),
(2747, 23, 74, 'Ancak ahirete inanmayanlar, şüphesiz yoldan sapanlardır.'),
(2748, 23, 75, 'Eğer onlara merhamet eder ve onlara dokunan zararı gideriverirsek, taşkınlıkları içinde şaşkınca dolaşmalarını sürdürecekler.'),
(2749, 23, 76, 'Andolsun, Biz onları azapla yakalayıverdik, fakat yine de Rablerine boyun eğmediler ve yakarıp-yalvarmadılar.'),
(2750, 23, 77, 'Sonunda, üzerlerine azabı şiddetli olan bir kapı açtığımızda, onlar bunun içinde şaşkına dönüp umutlarını kaybettiler.'),
(2751, 23, 78, 'O, sizin için kulakları, gözleri ve gönülleri inşa edendir; ne az şükrediyorsunuz.'),
(2752, 23, 79, 'O, sizi yeryüzünde yaratıp-türetendir ve hepiniz yalnızca O\'na (döndürülüp) toplanacaksınız.'),
(2753, 23, 80, 'O, yaşatan ve öldürendir; gece ile gündüzün aykırılığı (veya ardarda gelişi) da O\'nun (kanunu)dur. Yine de aklınızı kullanmayacak mısınız?'),
(2754, 23, 81, 'Hayır; onlar, geçmiştekilerin söylediklerinin benzerini söylediler.'),
(2755, 23, 82, 'Dediler ki: "Öldüğümüz, bir toprak ve bir kemik olduğumuz zaman, gerçekten biz mi diriltilecek mişiz?"'),
(2756, 23, 83, '"Andolsun, bu tehdit, bize ve bizden önceki atalarımıza yapılmıştı; bu, geçmişlerin uydurma masallarından başka bir şey değildir."'),
(2757, 23, 84, 'De ki: "Eğer biliyorsanız (söyleyin:) Yeryüzü ve onun içinde olanlar kimindir?"'),
(2758, 23, 85, '"Allah\'ındır" diyecekler. De ki: "Yine de öğüt alıp-düşünmeyecek misiniz?"'),
(2759, 23, 86, 'De ki: "Yedi göğün Rabbi ve büyük Arş\'ın Rabbi kimdir?"'),
(2760, 23, 87, '"Allah\'ındır" diyecekler. De ki: "Yine de sakınmayacak mısınız?"'),
(2761, 23, 88, 'De ki: "Eğer biliyorsanız (söyleyin:) Herşeyin melekutu (mülk ve yönetimi) kimin elindedir? Ki O, koruyup kolluyorken Kendisi korunmuyor."'),
(2762, 23, 89, '"Allah\'ındır" diyecekler. De ki: "Öyleyse nasıl oluyor da böyle büyüleniyorsunuz?"'),
(2763, 23, 90, 'Hayır, Biz onlara hakkı getirdik, ancak onlar gerçekten yalancıdırlar.'),
(2764, 23, 91, 'Allah, hiçbir çocuk edinmemiştir ve O\'nunla birlikte hiçbir İlah yoktur; eğer olsaydı, her bir ilah elbette kendi yarattığını götürüverirdi ve (ilahların) bir kısmına karşı üstünlük sağlardı. Allah, onların nitelendiregeldiklerinden Yücedir.'),
(2765, 23, 92, 'Gaybı ve müşahede edilebileni bilendir; onların ortak koştuklarından Yücedir.'),
(2766, 23, 93, 'De ki: "Rabbim, eğer onlara va\'dolunan (azab)ı mutlaka bana göstereceksen,"'),
(2767, 23, 94, '"Rabbim, bu durumda beni zulmeden kavmin içinde bırakma."'),
(2768, 23, 95, 'Gerçek şu ki Biz, onları tehdit ettiğimiz şeyi şüphesiz sana gösterme gücüne sahibiz.'),
(2769, 23, 96, 'Kötülüğü en güzel olanla uzaklaştır; Biz, onların nitelendiregeldiklerini en iyi bileniz.'),
(2770, 23, 97, 'Ve de ki: "Rabbim, şeytanın kışkırtmalarından Sana sığınırım."'),
(2771, 23, 98, '"Ve onların benim yanımda bulunmalarından da Sana sığınırım Rabbim."'),
(2772, 23, 99, 'Sonunda, onlardan birine ölüm geldiği zaman, der ki: "Rabbim, beni geri çevirin."'),
(2773, 23, 100, '"Ki, geride bıraktığım (dünya)da salih amellerde bulunayım." Asla, gerçekten bu, yalnızca bir sözdür, bunu da kendisi söylemektedir. Onların önlerinde, diriltilip kaldırılacakları güne kadar bir engel (berzah) vardır.'),
(2774, 23, 101, 'Böylece Sur\'a üfürüldüğü zaman artık o gün aralarında soylar (veya soybağları) yoktur ve (üstünlük unsuru olarak soyluluğu veya birbirlerine durumlarını) soruşturmazlar da.'),
(2775, 23, 102, 'Artık kimin tartısı ağır basarsa, işte onlar, kurtuluşa erenlerin ta kendileridir.'),
(2776, 23, 103, 'Kimin tartısı hafif gelirse, işte onlar da kendi nefislerini hüsrana uğratanlar, cehennemde de ebedi olarak kalacak olanlardır.'),
(2777, 23, 104, 'Ateş, onların yüzlerini yalayarak yakar da onun içinde onlar, (etleri sıyrılmış olarak sırıtan) dişleriyle kalıverirler.'),
(2778, 23, 105, 'Ayetlerim size okunuyorken, yalanlayanlar sizler değil miydiniz?'),
(2779, 23, 106, 'Dediler ki: "Rabbimiz, mutsuzluğumuz bize karşı üstün geldi, biz sapan bir topluluk imişiz."'),
(2780, 23, 107, '"Rabbimiz, bizi (ateşin) içinden çıkar, eğer yine (inkara) dönersek, artık gerçekten zalim kimseler oluruz."'),
(2781, 23, 108, 'Der ki: "Onun içine sinin ve Benimle söyleşmeyin."'),
(2782, 23, 109, '"Çünkü gerçekten Benim kullarımdan bir grup: “Rabbimiz, iman ettik, Sen artık bizi bağışla ve bize merhamet et, Sen merhamet edenlerin en hayırlısısın, derlerdi de,"'),
(2783, 23, 110, '"Siz onları alay konusu edinmiştiniz; öyle ki, size Benim zikrimi unutturdular ve siz onlara gülüp duruyordunuz."'),
(2784, 23, 111, '"Bugün Ben, gerçekten onların sabretmelerinin karşılığını verdim. Şüphesiz onlar, \'kurtuluşa ve mutluluğa\' erenlerdir."'),
(2785, 23, 112, 'Dedi ki: "Yıl sayısı olarak yeryüzünde ne kadar kaldınız?"'),
(2786, 23, 113, 'Dediler ki: "Bir gün ya da bir günün birazı kadar kaldık, sayanlara sor."'),
(2787, 23, 114, 'Dedi ki: "Yalnızca az (bir zaman) kaldınız, gerçekten bir bilseydiniz,"'),
(2788, 23, 115, '"Bizim, sizi boş bir amaç uğruna yarattığımızı ve gerçekten Bize döndürülüp getirilmeyeceğinizi mi sanmıştınız?"'),
(2789, 23, 116, 'Hak melik olan Allah pek Yücedir, O\'ndan başka İlah yoktur; Kerim olan Arş\'ın Rabbidir.'),
(2790, 23, 117, 'Kim Allah ile beraber ona ilişkin geçerli kesin bir kanıt (burhan)ı olmaksızın başka bir İlah\'a taparsa, artık onun hesabı Rabbinin Katındadır. Şüphesiz inkar edenler kurtuluşa eremezler.'),
(2791, 23, 118, 'Ve de ki: "Rabbim, bağışla ve merhamet et, Sen merhamet edenlerin en hayırlısısın."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '(Bu,) İndirdiğimiz ve (hükümlerini) farz kıldığımız bir sûredir. İçinde, umulur ki öğüt alıp düşünürsünüz diye apaçık ayetler indirdik.'),
(2793, 24, 2, 'Zina eden kadın ve zina eden erkeğin her birine yüzer değnek (celde) vurun. Eğer Allah\'a ve ahiret gününe iman ediyorsanız, onlara Allah\'ın dini(ni uygulama) konusunda sizi bir acıma tutmasın; onlara uygulanan cezaya mü\'minlerden bir grup da şahit bulunsun.'),
(2794, 24, 3, 'Zina eden erkek, zina eden ya da müşrik olan bir kadından başkasını nikahlayamaz; zina eden kadını da zina eden ya da müşrik olan bir erkekten başkası nikahlayamaz. Bu, mü\'minlere haram kılınmıştır.'),
(2795, 24, 4, 'Korunan (iffetli) kadınlara (zina suçu) atan, sonra dört şahid getirmeyenlere de seksen değnek vurun ve onların şahidliklerini ebedi olarak kabul etmeyin. Onlar fasık olanlardır.'),
(2796, 24, 5, 'Ancak bundan sonra tevbe eden ve salihçe davrananlar hariç. Çünkü gerçekten Allah, bağışlayandır, esirgeyendir.'),
(2797, 24, 6, 'Kendi eşlerine (zina suçu) atan ve kendileri dışında şahidleri bulunmayanlar ise, onlardan da her birinin şahidliği, Allah adına dört (kere yemin) ile kendisinin hiç şüphesiz doğru söyleyenlerden olduğuna şahidlik etmektir.'),
(2798, 24, 7, 'Beşinci (yemini) ise, eğer yalan söyleyenlerdense, Allah\'ın lanetinin muhakkak kendi üzerinde olması(nı kabul etmesi)dir.'),
(2799, 24, 8, 'Onun (kadının) da dört kere Allah adına (yeminle) onun (kocasının) hiç şüphesiz yalan söyleyenlerden olduğuna şahidlik etmesi kendisinden cezayı uzaklaştırır.'),
(2800, 24, 9, 'Beşinci (yemini) ise, eğer o (kocası) doğru söylüyor ise, Allah\'ın gazabının muhakkak kendi üzerinde olması(nı kabul etmesi)dır.'),
(2801, 24, 10, 'Eğer Allah\'ın sizin üzerinizde fazlı ve rahmeti olmasaydı ve Allah gerçekten tevbeleri kabul eden hüküm ve hikmet sahibi olmasaydı (ne yapardınız)?'),
(2802, 24, 11, 'Doğrusu, uydurulmuş bir yalanla gelenler, sizin içinizden birlikte davranan bir topluluktur; siz onu kendiniz için bir şer saymayın, aksine o sizin için bir hayırdır. Onlardan her bir kişiye kazandığı günahtan (bir ceza) vardır. Onlardan (iftiranın) büyüğünü yüklenene ise büyük bir azap vardır.'),
(2803, 24, 12, 'Onu işittiğiniz zaman, erkek mü\'minler ile kadın mü’minlerin kendi nefisleri adına hayırlı bir zanda bulunup: "Bu, açıkça uydurulmuş iftira bir sözdür" demeleri gerekmez miydi?'),
(2804, 24, 13, 'Ona karşı dört şahitle gelmeleri gerekmez miydi? Şahitleri getirmediklerine göre, artık onlar Allah Katında yalancıların ta kendileridir.'),
(2805, 24, 14, 'Eğer Allah\'ın dünyada ve ahirette sizin üzerinizde fazlı ve rahmeti olmasaydı, içine daldığınız dedikodudan dolayı size büyük bir azap dokunurdu.'),
(2806, 24, 15, 'O durumda siz onu (iftirayı) dillerinizle aktardınız ve hakkında bilginiz olmayan şeyi ağızlarınızla söylediniz ve bunu kolay sandınız; oysa o Allah Katında çok büyük (bir suç)tür.'),
(2807, 24, 16, 'Onu işittiğiniz zaman: "Bu konuda söz söylemek bize yakışmaz. (Allah\'ım) Sen Yücesin; bu, büyük bir iftiradır" demeniz gerekmez miydi?'),
(2808, 24, 17, 'Eğer iman edenlerden iseniz, bunun gibisine bir daha dönmemeniz için Allah size öğüt vermektedir.'),
(2809, 24, 18, 'Allah size ayetleri açıklıyor; Allah bilendir, hüküm ve hikmet sahibidir.'),
(2810, 24, 19, 'Çirkin utanmazlıkların (fuhşun) iman edenler içinde yaygınlaşmasından hoşlananlara, dünyada ve ahirette acıklı bir azap vardır. Allah bilir, siz ise bilmiyorsunuz.'),
(2811, 24, 20, 'Eğer Allah\'ın sizin üzerinizde fazlı ve rahmeti olmasaydı ve Allah gerçekten Rauf (şefkat eden ve) Rahim olmasaydı (ne yapardınız)?'),
(2812, 24, 21, 'Ey iman edenler, şeytanın adımlarına uymayın. Kim şeytanın adımlarına uyarsa, (bilsin ki) gerçekten o (şeytan) çirkin utanmazlıkları ve kötülüğü emreder. Eğer Allah\'ın üzerinizde fazlı ve rahmeti olmasaydı, sizden hiçbiri ebedi olarak temize çıkamazdı. Ancak Allah, dilediğini temize çıkarır. Allah, işitendir, bilendir.'),
(2813, 24, 22, 'Sizden, faziletli ve varlıklı olanlar, yakınlara, yoksullara ve Allah yolunda hicret edenlere vermekte eksiltme yapmasınlar, affetsinler ve hoşgörsünler. Allah\'ın sizi bağışlamasını sevmez misiniz? Allah, bağışlayandır, esirgeyendir.'),
(2814, 24, 23, 'Namus sahibi, bir şeyden habersiz, mü\'min kadınlara (zina suçu) atanlar, dünyada ve ahirette lanetlenmişlerdir. Ve onlar için büyük bir azap vardır.'),
(2815, 24, 24, 'O gün, kendi dilleri, elleri ve ayakları aleyhlerinde yaptıklarına dair şahitlikte bulunacaklardır.'),
(2816, 24, 25, 'O gün, Allah hak ettikleri cezayı eksiksiz verecektir ve onlar da Allah\'ın hiç şüphesiz hak olduğunu bileceklerdir.'),
(2817, 24, 26, 'Kötü kadınlar, kötü erkeklere; kötü erkekler, kötü kadınlara; iyi ve temiz erkekler, iyi ve temiz kadınlara (yaraşır). Bunlar, onların demekte olduklarından uzaktırlar. Bunlar için bir bağışlanma ve kerim (üstün) bir rızık vardır.'),
(2818, 24, 27, 'Ey iman edenler, evlerinizden başka evlere, yakınlık kurup (izin almadan) ve (ev halkına) selam vermeden girmeyin. Bu sizin için daha hayırlıdır; umulur ki öğüt alıp düşünürsünüz.'),
(2819, 24, 28, 'Eğer orada kimseyi bulamazsanız, size izin verilinceye kadar artık oraya girmeyin; ve eğer "Dönün" denirse, siz de dönün, bu sizin için daha temizdir. Allah yaptıklarınızı bilendir.'),
(2820, 24, 29, 'İçinde oturulmayan ve sizin için bir meta (yarar) bulunan evlere girmenizde bir sakınca yoktur. Allah, açığa vurduklarınızı da, sakladıklarınızı da bilir.'),
(2821, 24, 30, 'Mü\'minlere söyle: "Gözlerini (harama çevirmekten) kaçındırsınlar ve ırzlarını korusunlar. Bu, onlar için daha temizdir. Gerçekten Allah, yaptıklarından haberdardır.'),
(2822, 24, 31, 'Mü\'min kadınlara da söyle: "Gözlerini (harama çevirmekten) kaçındırsınlar ve ırzlarını korusunlar; süslerini açığa vurmasınlar, ancak kendiliğinden görüneni hariç. Baş örtülerini, yakalarının üstünü (kapatacak şekilde) koysunlar. Süslerini, kendi kocalarından ya da babalarından ya da oğullarından ya da kocalarının oğullarından ya da kendi kardeşlerinden ya da kardeşlerinin oğullarından ya da kız kardeşlerinin oğullarından ya da kendi kadınlarından ya da sağ ellerinin altında bulunanlardan ya da kadına ihtiyacı olmayan (arzusuz veya iktidarsız) hizmetçilerden ya da kadınların henüz mahrem yerlerini tanımayan çocuklardan başkasına göstermesinler. Gizledikleri süsleri bilinsin diye ayaklarını yere vurmasınlar. Hep birlikte Allah\'a tevbe edin ey mü\'minler, umulur ki felah bulursunuz."'),
(2823, 24, 32, 'İçinizde evli olmayanları, kölelerinizden ve cariyelerinizden salih olanları evlendirin. Eğer fakir iseler Allah, Kendi fazlından onları zengin eder. Allah geniş (nimet sahibi)dir, bilendir.'),
(2824, 24, 33, 'Nikah (imkanı) bulamayanlar, Allah onları Kendi fazlından zenginleştirinceye kadar iffetli davransınlar. Sağ ellerinizin malik olduğu (köle ve cariyelerden) mükatebe isteyenlere -eğer onlarda bir hayır görüyorsanız- mükatebe yapın. Ve Allah\'ın size verdiği malından onlara verin. Dünya hayatının geçici metaını elde etmek için -ırzlarını korumak istiyorlarsa- cariyelerinizi fuhşa zorlamayın. Kim onları (fuhşa) zorlarsa, şüphesiz, onların (fuhşa) zorlanmalarından sonra Allah (onları) bağışlayandır, esirgeyendir.'),
(2825, 24, 34, 'Andolsun, size açıklayıcı ayetler, sizden önce gelip geçenlerden bir örnek ve takva sahipleri için bir öğüt indirdik.'),
(2826, 24, 35, 'Allah, göklerin ve yerin nurudur. O\'nun nurunun misali, içinde çerağ bulunan bir kandil gibidir; çerağ bir sırça içerisindedir; sırça, sanki incimsi bir yıldızdır ki, doğuya da, batıya da ait olmayan kutlu bir zeytin ağacından yakılır; (bu öyle bir ağaç ki) neredeyse ateş ona dokunmasa da yağı ışık verir. (Bu,) Nur üstüne nurdur. Allah, kimi dilerse onu Kendi nuruna yöneltip-iletir. Allah insanlar için örnekler verir. Allah, herşeyi bilendir.'),
(2827, 24, 36, '(Bu nur,) Allah\'ın, onların yüceltilmesine ve isminin zikredilmesine izin verdiği evlerdedir; onların içinde sabah akşam O\'nu tesbih ederler.'),
(2828, 24, 37, '(Öyle) Adamlar ki, ne ticaret, ne alış-veriş onları Allah\'ı zikretmekten, dosdoğru namazı kılmaktan ve zekatı vermekten \'tutkuya kaptırıp alıkoymaz\'; onlar, kalplerin ve gözlerin inkılaba uğrayacağı (dehşetten allak bullak olacağı) günden korkarlar.'),
(2829, 24, 38, 'Çünkü Allah, yaptıklarının en güzeliyle karşılık verecek ve onlara Kendi fazlından arttıracaktır. Allah, dilediğini hesapsız rızıklandırır.'),
(2830, 24, 39, 'İnkar edenler ise; onların amelleri dümdüz bir arazideki seraba benzer; susayan onu bir su sanır. Nihayet ona ulaştığında bir şey bulamaz ve yanında Allah\'ı bulur. (Allah da) Onun hesabını tam olarak verir. Allah, hesabı çok seri görendir.'),
(2831, 24, 40, 'Ya da (inkar edenlerin amelleri) engin bir denizdeki karanlıklara benzer; onun üstünü bir dalga kaplar, onun üstünde bir dalga, onun da üstünde bir bulut vardır. Bir kısmı bir kısmı üzerinde olan karanlıklar; elini çıkardığında onu bile neredeyse göremeyecek. Allah kime nur vermemişse, artık onun için nur yoktur.'),
(2832, 24, 41, 'Görmedin mi ki, göklerde ve yerde olanlar ve dizi dizi uçan kuşlar, gerçekten Allah\'ı tesbih etmektedir. Her biri, kendi duasını ve tesbihini şüphesiz bilmiştir. Allah, onların işlediklerini bilendir.'),
(2833, 24, 42, 'Göklerin ve yerin mülkü Allah\'ındır ve dönüş yalnızca O\'nadır.'),
(2834, 24, 43, 'Görmedin mi ki, Allah bulutları sürmekte, sonra aralarını birleştirmekte, sonra da onları üst üste yığmaktadır; böylece, yağmurun bunların arasından akıp-çıktığını görürsün. Gökten içinde dolu bulunan dağlar (gibi bulutlar) indiriverir, onu dilediğine isabet ettirir de, dilediğinden onu çevirir; şimşeğinin parıltısı neredeyse gözleri kamaştırıp götürüverecektir.'),
(2835, 24, 44, 'Allah, gece ile gündüzü evirip çevirir. Gerçekten bunda basiret sahipleri için birer ibret vardır.'),
(2836, 24, 45, 'Allah, her canlıyı sudan yarattı. İşte bunlardan kimi karnı üzerinde yürümekte, kimi iki ayağı üzerinde yürümekte, kimi de dört (ayağı) üzerinde yürümektedir. Allah, dilediğini yaratır. Hiç şüphesiz Allah, herşeye güç yetirendir.'),
(2837, 24, 46, 'Andolsun Biz, açıklayıcı ayetler indirdik. Allah, dilediğini doğru yola yöneltip-iletir.'),
(2838, 24, 47, 'Onlar derler ki: "Allah\'a ve elçisine iman ettik ve itaat ettik" sonra bunun ardından onlardan bir grup sırt çevirir. Bunlar iman etmiş değildirler.'),
(2839, 24, 48, 'Aralarında hükmetmesi için Allah\'a ve Resulüne çağrıldıkları zaman, onlardan bir grup yüz çevirir.'),
(2840, 24, 49, 'Eğer hak lehlerinde ise, ona boyun eğerek gelirler.'),
(2841, 24, 50, 'Bunların kalplerinde hastalık mı var? Yoksa kuşkuya mı kapıldılar? Yoksa Allah\'ın ve elçisinin kendilerine karşı haksızlık yapacağından mı korkuyorlar? Hayır, onlar zalim kimselerdir.'),
(2842, 24, 51, 'Aralarında hükmetmesi için, Allah\'a ve elçisine çağrıldıkları zaman mü\'min olanların sözü: "İşittik ve itaat ettik" demeleridir. İşte felaha kavuşanlar bunlardır.'),
(2843, 24, 52, 'Kim Allah\'a ve Resûlü’ne itaat ederse ve Allah\'tan korkup O\'ndan sakınırsa, işte \'kurtuluşa ve mutluluğa\' erenler bunlardır.'),
(2844, 24, 53, 'Yeminlerinin olanca gücüyle Allah\'a and içtiler; eğer sen onlara emredersen (savaşa) çıkacaklar diye. De ki: "And içmeyin, bu bilinen (örf üzere) bir itaattır. Allah, yaptıklarınızdan haberdardır."'),
(2845, 24, 54, 'De ki: "Allah\'a itaat edin, Resûl’e itaat edin. Eğer yine yüz çevirirseniz, artık Onun (peygamberin) sorumluluğu kendisine yüklenen, sizin sorumluluğunuz da size yüklenendir. Eğer Ona itaat ederseniz, hidayet bulmuş olursunuz. Elçiye düşen, apaçık bir tebliğden başkası değildir."'),
(2846, 24, 55, 'Allah, içinizden iman edenlere ve salih amellerde bulunanlara va\'detmiştir: Hiç şüphesiz onlardan öncekileri nasıl \'güç ve iktidar sahibi\' kıldıysa, onları da yeryüzünde \'güç ve iktidar sahibi\' kılacak, kendileri için seçip beğendiği dinlerini kendilerine yerleşik kılıp sağlamlaştıracak ve onları korkularından sonra güvenliğe çevirecektir. Onlar, yalnızca Bana ibadet ederler ve Bana hiçbir şeyi ortak koşmazlar. Kim bundan sonra inkar ederse, işte onlar fasıktır.'),
(2847, 24, 56, 'Dosdoğru namazı kılın, zekatı verin ve elçiye itaat edin. Umulur ki, rahmete kavuşturulmuş olursunuz.'),
(2848, 24, 57, 'İnkara sapanların, yeryüzünde (Allah\'ı) aciz bırakacaklarını sanma. Onların son barınma yerleri ateştir. Ne kötü bir dönüştür o.'),
(2849, 24, 58, 'Ey iman edenler, sağ ellerinizin malik olduğu ile sizden olup da henüz erginlik çağına ermemiş olan (çocuk)lar, (odalarınıza girmek için şu) üç vakitte izin istesinler: Sabah namazından önce, öğleyin üstünüzü çıkardığınız vakit ve yatsı namazından sonra. (Bu) Üçü sizin için mahrem (vakitleri)dir. Bunların dışında size de, onlara da bir sakınca yoktur; onlar yanınızda dolaşabilirler, birbirinizin yanında olabilirsiniz. İşte Allah, size ayetleri böyle açıklamaktadır. Allah bilendir, hüküm ve hikmet sahibidir.'),
(2850, 24, 59, 'Sizden olan çocuklar, erginlik çağına erdikleri zaman, kendilerinden öncekilerin izin istediği gibi, bundan böyle izin istesinler. İşte Allah, ayetlerini size böyle açıklar. Allah bilendir, hüküm ve hikmet sahibidir.'),
(2851, 24, 60, 'Kadınlardan evliliği ummayıp da oturmakta olanlar, süslerini açığa vurmaksızın (dış) elbiselerini çıkarmalarında kendileri için bir sakınca yoktur. Yine de iffetli davranmaları kendileri için daha hayırlıdır. Allah, işitendir, bilendir.'),
(2852, 24, 61, 'Kör olana güçlük yoktur, topal olana güçlük yoktur, hasta olana da güçlük yoktur; sizin için de, gerek kendi evlerinizden, gerekse babalarınızın evlerinden, annelerinizin evlerinden, erkek kardeşlerinizin evlerinden, kız kardeşlerinizin evlerinden, amcalarınızın evlerinden, halalarınızın evlerinden, dayılarınızın evlerinden, teyzelerinizin evlerinden, anahtarına malik olduğunuz (yerlerden) ya da dostlarınızın (evlerin)den yemenizde bir güçlük yoktur. Hep birarada veya ayrı ayrı yemenizde de bir günah yoktur. Evlere girdiğiniz vakit, Allah tarafından kutlu, güzel bir yaşama dileği olarak birbirinize selam verin. İşte Allah, size ayetleri böyle açıklar, umulur ki aklınızı kullanırsınız.'),
(2853, 24, 62, 'Mü\'minler o kimselerdir ki, Allah\'a ve Resûlü’ne iman edenler, onunla birlikte toplu(mu ilgilendiren) bir iş üzerinde iken, ondan izin alıncaya kadar bırakıp-gitmeyenlerdir. Gerçekten, senden izin alanlar, işte onlar Allah\'a ve elçisine iman edenlerdir. Böylelikle, senden kendi bazı işleri için izin istedikleri zaman, dilediklerine izin ver ve onlar için Allah\'tan bağışlanma dile. Şüphesiz Allah, bağışlayandır, esirgeyendir.'),
(2854, 24, 63, 'Elçinin çağırmasını, kendi aranızda kiminizin kimini çağırması gibi saymayın. Allah, sizden bir diğerinizi siper ederek kaçanları gerçekten bilir. Böylece onun emrine aykırı davrananlar, kendilerine bir fitnenin isabet etmesinden veya onlara acı bir azabın çarpmasından sakınsınlar.'),
(2855, 24, 64, 'Dikkatli olun; göklerde ve yerde olanların hepsi Allah\'ındır. O, üzerinde bulunduğunuz şeyi elbette bilir. Ve O\'na döndürülecekleri gün, yaptıklarını kendilerine haber verecektir. Allah, herşeyi bilendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Alemlere uyarıcı olsun diye, kuluna Furkan\'ı indiren (Allah) ne Yücedir.'),
(2857, 25, 2, 'Göklerin ve yerin mülkü O\'nundur; çocuk edinmemiştir. O\'na mülkünde ortak yoktur, herşeyi yaratmış, ona bir düzen vermiş, belli bir ölçüyle takdir etmiştir.'),
(2858, 25, 3, 'O\'nun dışında, hiçbir şeyi yaratmayan, üstelik kendileri yaratılmış olan, kendi nefislerine bile ne zarar, ne yarar sağlayamayan, öldürmeye, yaşatmaya ve yeniden diriltip-yaymaya güçleri yetmeyen birtakım ilahlar edindiler.'),
(2859, 25, 4, 'İnkar edenler dediler ki: "Bu (Kur\'an) olsa olsa ancak Onun uydurduğu bir yalandır, kendisi düzüp uydurmuş ve Ona bir başka topluluk da yardımda bulunmuştur." Böylelikle onlar, hiç şüphesiz haksızlık ve iftira ile geldiler.'),
(2860, 25, 5, 'Ve dediler ki: "Bu, geçmişlerin uydurduğu masallardır, bir başkasına yazdırmış olup kendisine sabah akşam okunmaktadır."'),
(2861, 25, 6, 'De ki: "Onu, göklerde ve yerde gizli olanı bilen (Allah) indirmiştir. Doğrusu O, çok bağışlayandır, çok esirgeyendir."'),
(2862, 25, 7, 'Dediler ki: "Bu elçiye ne oluyor ki, yemek yemekte ve pazarlarda dolaşmaktadır? Ona, kendisiyle birlikte uyarıcı olacak bir melek indirilmesi gerekmez miydi?"'),
(2863, 25, 8, '"Ya da kendisine bir hazinenin bırakılması veya (ürünlerinden) yemekte olduğu bir bahçesi olması (gerekmez miydi)?" Zulmedenler dedi ki: "Siz olsa olsa, ancak büyülenmiş bir adama uyuyorsunuz."'),
(2864, 25, 9, 'Bir bak; senin için nasıl örnekler verdiler de böylece saptılar. Artık onlar hiçbir yol bulamazlar.'),
(2865, 25, 10, 'Dilediği takdirde, sana bundan daha hayırlısı olarak altından ırmaklar akan cennetler veren ve senin için köşkler kılan (Allah) ne Yücedir.'),
(2866, 25, 11, 'Hayır, onlar kıyamet-saatini yalanladılar; Biz kıyamet saatini yalan sayanlara çılgınca yanan bir ateş hazırladık.'),
(2867, 25, 12, '(Ateş,) Onları uzak bir yerden gördüğünde, onlar bunun gazablı öfkesini ve uğultusunu işitirler.'),
(2868, 25, 13, 'Elleri boyunlarına bağlı olarak, sıkışık bir yerine atıldıkları zaman, orada yok oluşu isteyip-çağırırlar.'),
(2869, 25, 14, 'Bugün bir yok oluşu çağırmayın, birçok (kere) yok oluşu isteyip-çağırın.'),
(2870, 25, 15, 'De ki: "Bu mu daha hayırlı, yoksa takva sahiplerine va\'dedilen ebedi cennet mi? Ki onlar için bir mükafat ve son duraktır."'),
(2871, 25, 16, '"İçinde ebedi kalıcılar olarak, orada her istedikleri onlarındır; bu, Rabbinin üzerine aldığı, istenen bir vaaddir."'),
(2872, 25, 17, 'Onları ve Allah\'tan başka taptıklarını biraraya getirip toplayacağı ve: "Şu kullarımı siz mi saptırdınız, yoksa kendileri mi yoldan saptılar?” diyeceği gün;'),
(2873, 25, 18, 'Derler ki: "Sen Yücesin; Senin dışında başka veliler edinmemiz bize yakışmaz, ancak onları ve atalarını Sen meta verip yararlandırdın, öyle ki (Senin) zikri(ni) unuttular ve böylece yıkıma uğrayan bir kavim oldular."'),
(2874, 25, 19, '"İşte (ilahlarınız) sizin söylediklerinizi yalanladılar; bundan böyle (azabı) ne geri çevirmeye gücünüz yetebilir, ne de bir yardıma. Sizden kim zulmederse, ona büyük bir azap taddırırız."'),
(2875, 25, 20, 'Senden önce gönderdiklerimizden, gerçekten yemek yiyen ve pazarlarda gezen (elçi)lerden başkasını göndermiş değiliz. Biz, sizin kiminizi kimi için deneme (fitne konusu) yaptık. Sabredecek misiniz? Senin Rabbin görendir.'),
(2876, 25, 21, 'Bize kavuşmayı ummayanlar, dediler ki: "Bize meleklerin indirilmesi ya da Rabbimiz\'i görmemiz gerekmez miydi?" Andolsun, onlar kendi nefislerinde büyüklüğe kapıldılar ve büyük bir azgınlıkla baş kaldırdılar.'),
(2877, 25, 22, 'Melekleri görecekleri gün, suçlu-günahkarlara bir müjde yoktur. Ve o gün (melekler onlara) derler ki: "(Size sevinçli haber) Yasaktır, yasak."'),
(2878, 25, 23, 'Onların yaptıkları her işin önüne geçtik, böylece onu savurulmuş toz zerreleri kılıverdik.'),
(2879, 25, 24, 'O gün, cennet halkının kalacakları yer daha hayırlı, dinlenecekleri yer çok daha güzeldir.'),
(2880, 25, 25, 'Göğün bulutlarla parçalanacağı ve meleklerin bir indirilme ile indirileceği gün;'),
(2881, 25, 26, 'İşte o gün, gerçek mülk, Rahman (olan Allah)ındır. İnkar edenler için oldukça zorlu bir gündür.'),
(2882, 25, 27, 'O gün, zulmeden, ellerini (hınçla) ısırarak (şöyle) der: "Ah keşke, elçiyle birlikte bir yol edinmiş olsaydım,"'),
(2883, 25, 28, '"Vah yazıklar bana, ne olurdu da filanı dost edinmeseydim."'),
(2884, 25, 29, '"Çünkü o, gerçekten bana geldikten sonra beni zikirden (Kur\'an\'dan) saptırmış oldu. Şeytan da insanı \'yapayalnız ve yardımsız" bırakandır."'),
(2885, 25, 30, 'Ve elçi dedi ki: "Rabbim gerçekten benim kavmim, bu Kur\'an\'ı terk edilmiş (bir Kitap) olarak bıraktılar."'),
(2886, 25, 31, 'İşte böyle; Biz, her peygambere suçlu-günahkarlardan bir düşman kıldık. Yol gösterici ve yardımcı olarak Rabbin yeter.'),
(2887, 25, 32, 'İnkar edenler dediler ki: "Kur\'an Ona tek bir defada, toplu olarak indirilmeli değil miydi?" Biz onunla kalbini sağlamlaştırıp-pekiştirmek için böylece (ayet ayet indirdik) ve onu \'belli bir okuma düzeniyle (tertil üzere) düzene koyup\' okuduk.'),
(2888, 25, 33, 'Onların sana getirdikleri hiçbir örnek yoktur ki, Biz (ona karşı) sana hakkı ve en güzel açıklama tarzını getirmiş olmayalım.'),
(2889, 25, 34, 'O yüzükoyun cehenneme doğru sürülüp-toplanacak olanlar; işte onlar, yer bakımından çok kötü, yol bakımından sapmış olanlardır.'),
(2890, 25, 35, 'Andolsun, Biz Musa\'ya kitabı verdik ve onunla birlikte kardeşi Harun\'u yardımcı kıldık.'),
(2891, 25, 36, 'Böylece onlara: "Ayetlerimizi yalanlayan kavme gidin" dedik; sonunda onları (Firavun ve çevresini) kökünden darmadağın ettik.'),
(2892, 25, 37, 'Nuh\'un kavmi de, elçileri yalanlandıklarında onları suda boğduk ve insanlar için bir ayet kıldık. Biz zulmedenlere acıklı bir azap hazırladık.'),
(2893, 25, 38, 'Ad\'ı, Semud\'u, Ress halkını ve bunlar arasında birçok nesilleri (yok ettik).'),
(2894, 25, 39, 'Biz (onlardan) her birine örnekler verdik ve her birini darmadağın edip mahvettik.'),
(2895, 25, 40, 'Andolsun, onlar, üstüne felaket yağmuru yağdırılmış bulunan o ülkeye uğramışlardır; yine de onu görmüyorlar mıydı? Hayır, onlar dirilmeyi ummuyorlardı.'),
(2896, 25, 41, 'Seni gördükleri zaman, seni yalnızca alay konusu edinmektedirler: "Allah\'ın, elçi olarak gönderdiği bu mu?"'),
(2897, 25, 42, '"Eğer onlara karşı kararlılık göstermeseydik, neredeyse bizi ilahlarımızdan saptıracaktı." Azabı görecekleri zaman, kim yol bakımından daha sapıkmış, öğreneceklerdir.'),
(2898, 25, 43, 'Kendi istek ve tutkularını (hevasını) ilah edineni gördün mü? Şimdi ona karşı sen mi vekil olacaksın?'),
(2899, 25, 44, 'Yoksa sen, onların çoğunu (söz) işitir ya da aklını kullanır mı sayıyorsun? Onlar, ancak hayvanlar gibidirler; hayır, onlar yol bakımından daha şaşkın (ve aşağı) dırlar.'),
(2900, 25, 45, 'Rabbini görmedin mi, gölgeyi nasıl uzatıvermiştir? Eğer dilemiş olsaydı onu durgun kılardı. Sonra Biz Güneş\'i ona bir delil kılmışızdır.'),
(2901, 25, 46, 'Sonra da onu tutup Kendimize ağır ağır çekmişizdir.'),
(2902, 25, 47, 'O, geceyi sizin için bir elbise, uykuyu bir dinlenme ve gündüzü de yayılıp-çalışma (zamanı) kılandır.'),
(2903, 25, 48, 'Ve Kendi rahmetinin önünde rüzgarları müjdeciler olarak gönderen O\'dur. Biz, gökten tertemiz su indirdik;'),
(2904, 25, 49, 'Onunla ölü bir beldeyi (toprağı) canlandırmak ve yarattığımız hayvanlardan ve insanlardan birçoğunu onunla sulamak için.'),
(2905, 25, 50, 'Andolsun bunu, onların arasında öğüt alıp-düşünsünler diye çeşitli biçimlerde açıkladık. Ama insanların çoğu nankörlük edip ayak direttiler.'),
(2906, 25, 51, 'Eğer dilemiş olsaydık, her kasabaya bir uyarıcı gönderirdik.'),
(2907, 25, 52, 'Öyleyse kafirlere itaat etme ve onlara (Kur\'an\'la) büyük bir mücadele ver.'),
(2908, 25, 53, 'İki denizi (birbirine) salıp katan O\'dur; bu, tatlı, susuzluğu giderici, bu da tuzlu ve acıdır. İkisinin arasında (birbirlerine karışmalarını önleyen) bir engel (berzah) ve aşılmayan bir sınır koymuştur.'),
(2909, 25, 54, 'Ve insanı bir sudan yaratıp onu, neseb ve sihriyyet (sahibi) kılan O\'dur. Senin Rabbin güç yetirendir.'),
(2910, 25, 55, 'Allah\'ı bırakıp kendilerine yarar ve zarar sağlayamayacak şeylere ibadet ediyorlar. Kafir, (asıl) kendi Rabbine karşı (şeytana) arka çıkandır.'),
(2911, 25, 56, 'Biz seni yalnızca bir müjde verici ve uyarıp-korkutucu olarak gönderdik.'),
(2912, 25, 57, 'De ki: "Ben buna karşılık, Rabbine doğru bir yol tutmayı dileyen (insanlar olmanız) dışında sizden bir ücret istemiyorum."'),
(2913, 25, 58, 'Sen, asla ölmeyen ve daima diri olan (Allah)a tevekkül et ve O\'nu hamd ile tesbih et. Kullarının günahlarından O\'nun haberdar olması yeter.'),
(2914, 25, 59, 'O, gökleri ve yeri ve ikisinin arasındakileri altı günde yaratan ve sonra arşa istiva edendir. Rahman (olan Allah)dır. Bunu (bundan) haberi olana sor.'),
(2915, 25, 60, 'Onlara: "Rahman (olan Allah)a secde edin" denildiği zaman, "Rahman da neymiş? Biz senin bize emrettiğine mi secde edecek mişiz?" derler ve (bu,) onların nefretini arttırır.'),
(2916, 25, 61, 'Gökte burçlar kılan, onların içinde bir aydınlık ve nurlu bir ay vareden (Allah) ne Yücedir.'),
(2917, 25, 62, 'O, gece ile gündüzü birbiri ardınca kılandır; öğüt alıp-düşünmek isteyenler ya da şükretmek isteyenler için.'),
(2918, 25, 63, 'O Rahman (olan Allah)ın kulları, yeryüzü üzerinde alçak gönüllü olarak yürürler ve cahiller kendileriyle muhatap oldukları zaman "Selam" derler.'),
(2919, 25, 64, 'Onlar, Rablerine secde ederek ve kıyama durarak gecelerler.'),
(2920, 25, 65, 'Onlar: "Rabbimiz, cehennem azabını bizden geri çevir; gerçekten, onun azabı ödenmesi kaçınılmaz bir borç (veya sürekli bir acıdır) derler.'),
(2921, 25, 66, '"Şüphesiz o, ne kötü bir karargah ve ne kötü bir konaklama yeridir."'),
(2922, 25, 67, 'Onlar, harcadıkları zaman, ne israf ederler, ne kısarlar; (harcamaları,) ikisi arasında orta bir yoldur.'),
(2923, 25, 68, 'Ve onlar, Allah ile beraber başka bir İlah\'a tapmazlar. Allah\'ın haram kıldığı canı haksız yere öldürmezler ve zina etmezler. Kim bunları yaparsa \'ağır bir ceza ile\' karşılaşır.'),
(2924, 25, 69, 'Kıyamet günü, azap ona kat kat artırılır ve içinde aşağılanmış olarak temelli kalır.'),
(2925, 25, 70, 'Ancak tevbe eden, iman eden ve salih amellerde bulunup davranan başka; işte onların günahlarını Allah iyiliklere çevirir. Allah, çok bağışlayandır, çok esirgeyendir.'),
(2926, 25, 71, 'Kim tevbe eder ve salih amellerde bulunursa, gerçekten o, tevbesi (ve kendisi) kabul edilmiş olarak Allah\'a döner.'),
(2927, 25, 72, 'Ki onlar, yalan şahidlikte bulunmayanlar, boş ve yararsız sözle karşılaştıkları zaman onurlu olarak geçenlerdir.'),
(2928, 25, 73, 'Onlar, kendilerine Rablerinin ayetleri hatırlatıldığı zaman, onun üstünde sağır ve körler olarak kapanıp kalmayanlardır.'),
(2929, 25, 74, 'Ve onlar: "Rabbimiz, bize eşlerimizden ve soyumuzdan, gözün aydınlığı olacak (çocuklar) armağan et ve bizi takva sahiplerine önder kıl," diyenlerdir.'),
(2930, 25, 75, 'İşte onlar, sabretmelerine karşılık (cennetin en gözde yerinde) odalarla ödüllendirilirler ve orda esenlik dileği ve selamla karşılanırlar.'),
(2931, 25, 76, 'Orda ebedi olarak kalıcıdırlar; o, ne güzel bir karargah ve ne güzel bir konaklama yeridir.'),
(2932, 25, 77, 'De ki: "Sizin duanız olmasaydı Rabbim size değer verir miydi? Fakat siz gerçekten yalanladınız; artık (bunun azabı da) kaçınılmaz olacaktır."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Ta, Sin, Mim.'),
(2934, 26, 2, 'Bunlar, apaçık olan Kitab\'ın ayetleridir.'),
(2935, 26, 3, 'Onlar mü\'min olmayacaklar diye neredeyse kendini kahredeceksin (öyle mi?)'),
(2936, 26, 4, 'Dilersek, onların üzerine gökten bir ayet (mucize) indiririz de, ona boyunları eğilmiş kalıverir.'),
(2937, 26, 5, 'Onlara Rahman (olan Allah) dan yeni bir uyarı gelmeyiversin, hiç tartışmasız ondan yüz çevirirler.'),
(2938, 26, 6, 'Gerçekten yalanladılar; fakat, alay konusu yaptıkları şeyin haberi kendilerine pek yakında gelecektir.'),
(2939, 26, 7, 'Yeryüzünde bir bakmadılar mı ki, Biz onda her güzel (kerim) çiftten nice ürünler bitirdik.'),
(2940, 26, 8, 'Şüphesiz, bunda bir ayet vardır; ancak onların çoğu mü\'min değildirler.'),
(2941, 26, 9, 'Şüphesiz, senin Rabbin, gerçekten O, üstün ve güçlüdür, merhamet sahibidir.'),
(2942, 26, 10, 'Hani senin Rabbin, Musa\'ya seslenmişti: "Zulmetmekte olan kavme git;"'),
(2943, 26, 11, 'Firavun\'un kavmine, hala sakınmıyorlar mı?"'),
(2944, 26, 12, 'Dedi ki: "Rabbim, gerçekten ben, onların beni yalanlamalarından korkuyorum."'),
(2945, 26, 13, '"Göğsüm sıkışıyor, dilim dönmüyor; bundan dolayı Harun\'a da (elçilik görevini bildirmesi için Cibril\'i) gönder."'),
(2946, 26, 14, '"Üstelik, onların bana karşı (davasını savunacakları bir cinayet) suçu(m) var; bundan dolayı beni öldürmelerinden korkuyorum."'),
(2947, 26, 15, '(Allah:) "Hayır," dedi. "İkiniz de ayetlerimle gidin, şüphesiz sizinle birlikteyiz (ve) işitmekteyiz."'),
(2948, 26, 16, '"Gecikmeksizin Firavun\'a giderek deyin ki: Gerçekten biz, alemlerin Rabbinin elçisiyiz,"'),
(2949, 26, 17, '"İsrailoğulları\'nı bizimle birlikte göndermen için (sana geldik)."'),
(2950, 26, 18, '(Gittiler ve Firavun:) Dedi ki: "Biz seni içimizde daha çocukken yetiştirip büyütmedik mi? Sen ömrünün nice yıllarını aramızda geçirmedin mi?"'),
(2951, 26, 19, '"Ve sen, yapacağın işi (cinayeti) de işledin; sen nankörlerdensin."'),
(2952, 26, 20, '(Musa) Dedi ki: "Ben onu yaptığım zaman şaşkınlardandım."'),
(2953, 26, 21, '"Sizden korkunca da hemen aranızdan kaçtım; sonra Rabbim bana hüküm (ve hikmet) verdi ve beni gönderilen (elçilerden) kıldı."'),
(2954, 26, 22, '"Bana karşı lütuf-dediğin nimet de, İsrailoğulları\'nı köle kılmandan dolayıdır."'),
(2955, 26, 23, 'Firavun dedi ki: "Alemlerin Rabbi nedir?"'),
(2956, 26, 24, 'Dedi ki: "Göklerin, yerin ve bu ikisi arasında olan herşeyin Rabbidir. Eğer \'kesin bilgiyle inanıyorsanız\' (böyledir)."'),
(2957, 26, 25, 'Çevresindekilere dedi ki: "İşitiyor musunuz?"'),
(2958, 26, 26, '(Musa:) Dedi ki: "O sizin de Rabbiniz, geçmişteki atalarınızın da Rabbidir."'),
(2959, 26, 27, '(Firavun) Dedi ki: "Şüphesiz size gönderilmiş bulunan elçiniz, gerçekten bir delidir."'),
(2960, 26, 28, '"Eğer aklınızı kullanabiliyorsanız, O, doğunun da, batının da ve bunlar arasında olan herşeyin de Rabbidir" dedi (Musa).'),
(2961, 26, 29, '(Firavun) dedi ki: "Andolsun, benim dışımda bir ilah edinecek olursan, seni mutlaka hapse atacağım."'),
(2962, 26, 30, '(Musa) Dedi ki: "Sana apaçık bir şey getirmiş olsam da mı?"'),
(2963, 26, 31, '(Firavun) Dedi ki: "Eğer doğru sözlü isen, onu getir."'),
(2964, 26, 32, 'Bunun üzerine asasını bırakıverdi, bir de (ne görsünler) o, açıkça bir ejderha oluverdi.'),
(2965, 26, 33, 'Elini de çekip çıkardı, bir de (ne görsün) o, bakanlar için \'parlayıp aydınlanıvermiş\'.'),
(2966, 26, 34, '(Firavun,) Çevresindeki önde gelenlere: "Bu” dedi, "Doğrusu bilgin bir büyücüdür."'),
(2967, 26, 35, '"Büyüsüyle sizi yurdunuzdan sürüp çıkarmak istiyor; ne buyurursunuz?"'),
(2968, 26, 36, 'Dediler ki: "Bunu ve kardeşini oyala, şehirlere de toplayıcılar gönder,"'),
(2969, 26, 37, '"Bütün uzman-bilgin büyücüleri sana getirsinler."'),
(2970, 26, 38, 'Böylelikle büyücüler, bilinen bir günün belli vaktinde biraraya getirildi.'),
(2971, 26, 39, 'Ve insanlara da: "Siz de toplanıyor musunuz? dendi."'),
(2972, 26, 40, '"Umarız ki, eğer galip gelirse biz de büyücülere uyarız."'),
(2973, 26, 41, 'Büyücüler geldiklerinde, Firavun\'a: "Şayet biz galip gelirsek, bize bir ücret var gerçekten, değil mi?" dediler.'),
(2974, 26, 42, '"Evet" dedi. "Üstelik şüphesiz siz en yakın(larım) kılınanlardan olacaksınız."'),
(2975, 26, 43, 'Musa onlara dedi ki: "Atacağınızı atın."'),
(2976, 26, 44, 'Onlar da, iplerini ve asalarını atıverdiler ve: "Firavun\'un üstünlüğü adına, hiç tartışmasız, üstün olanlar gerçekten bizleriz" dediler.'),
(2977, 26, 45, 'Böylelikle Musa da asasını bırakıverdi, bir de (ne görsünler) o, uydurmakta olduklarını yutuveriyor.'),
(2978, 26, 46, 'Anında büyücüler secdeye kapandılar.'),
(2979, 26, 47, '(Ve:) "Alemlerin Rabbine iman ettik" dediler.'),
(2980, 26, 48, '"Musa\'nın ve Harun\'un Rabbine."'),
(2981, 26, 49, '(Firavun) Dedi ki: "Ona, ben size izin vermeden önce mi inandınız? Şüphesiz, o, size büyüyü öğreten büyüğünüzdür; öyleyse yakında bileceksiniz. Şüphesiz ellerinizi ve ayaklarınızı çaprazlama kestireceğim ve sizin hepinizi gerçekten asıp-sallandıracağım."'),
(2982, 26, 50, '"Hiç zararı yok" dediler. "Çünkü biz gerçekten Rabbimiz\'e dönücüleriz."'),
(2983, 26, 51, '"Doğrusu biz, iman edenlerin ilki olduğumuzdan dolayı Rabbimiz\'in bizim hatalarımızı bağışlayacağını umuyoruz."'),
(2984, 26, 52, 'Musa\'ya: "Kullarımı gece yürüyüşe geçir, çünkü izleneceksiniz" diye vahyettik.'),
(2985, 26, 53, 'Bunun üzerine Firavun şehirlere (asker) toplayıcılar gönderdi.'),
(2986, 26, 54, '"Gerçek şu ki bunlar azınlık olan bir topluluktur;"'),
(2987, 26, 55, '"Ve elbette bize karşı da büyük bir öfke beslemektedirler."'),
(2988, 26, 56, '\'Biz ise uyanık bir toplumuz" (dedi).'),
(2989, 26, 57, 'Böylelikle Biz onları (Firavun ve kavmini) bahçelerden ve pınarlardan sürüp çıkardık;'),
(2990, 26, 58, 'Hazinelerden ve soylu makam(lar)dan da.'),
(2991, 26, 59, 'İşte böyle; bunlara İsrailoğulları\'nı mirasçı kıldık.'),
(2992, 26, 60, 'Böylece (Firavun ve ordusu) güneşin doğuş vakti onları izlemeye koyuldular.'),
(2993, 26, 61, 'İki topluluk birbirini gördükleri zaman Musa\'nın adamları: "Gerçekten yakalandık" dediler.'),
(2994, 26, 62, '(Musa:) "Hayır" dedi. "Şüphesiz Rabbim, benimle beraberdir; bana yol gösterecektir."'),
(2995, 26, 63, 'Bunun üzerine Musa\'ya: "Asanla denize vur" diye vahyettik. (Vurdu ve) Deniz hemencecik yarılıverdi de her parçası kocaman bir dağ gibi oldu.'),
(2996, 26, 64, 'Ötekileri de buraya yaklaştırdık.'),
(2997, 26, 65, 'Musa\'yı ve onunla birlikte olanların hepsini kurtarmış olduk.'),
(2998, 26, 66, 'Sonra ötekileri suda boğduk.'),
(2999, 26, 67, 'Şüphesiz, bunda bir ayet vardır. Ama onların çoğu iman etmiş değildirler.'),
(3000, 26, 68, 'Ve hiç şüphesiz, senin Rabbin, güçlü ve üstün olandır, esirgeyendir.'),
(3001, 26, 69, 'Onlara İbrahim\'in haberini de aktar-oku:'),
(3002, 26, 70, 'Hani, babasına ve kavmine: "Siz neye kulluk ediyorsunuz?" demişti.'),
(3003, 26, 71, 'Demişlerdi ki: "Putlara tapıyoruz, bunun için sürekli onların önünde bel büküp eğiliyoruz."'),
(3004, 26, 72, 'Dedi ki: "Peki, dua ettiğiniz zaman onlar sizi işitiyorlar mı?"'),
(3005, 26, 73, '"Ya da size bir yararları veya zararları dokunuyor mu?"'),
(3006, 26, 74, '"Hayır" dediler. "Biz atalarımızı böyle yaparlarken bulduk."'),
(3007, 26, 75, '(İbrahim) Dedi ki: "Şimdi, neye tapmakta olduğunuzu gördünüz mü?"'),
(3008, 26, 76, '"Hem siz, hem de eski atalarınız?"'),
(3009, 26, 77, '"İşte bunlar, gerçekten benim düşmanımdır; yalnızca alemlerin Rabbi hariç"'),
(3010, 26, 78, '"Ki beni yaratan ve bana hidayet veren O\'dur;"'),
(3011, 26, 79, '"Bana yediren ve içiren O\'dur;"'),
(3012, 26, 80, '"Hastalandığım zaman bana şifa veren O\'dur;"'),
(3013, 26, 81, '"Beni öldürecek, sonra diriltecek olan da O\'dur,"'),
(3014, 26, 82, '"Din (ceza) günü hatalarımı bağışlayacağını umduğum da O\'dur;"'),
(3015, 26, 83, '"Rabbim, bana hüküm (ve hikmet) bağışla ve beni salih olanlara kat;"'),
(3016, 26, 84, '"Sonra gelecekler arasında bana bir doğruluk dili (lisan-ı sıdk) ver."'),
(3017, 26, 85, '"Beni nimetlerle-donatılmış cennetin mirasçılarından kıl,"'),
(3018, 26, 86, '"Babamı da bağışla, çünkü o şaşırıp sapanlardandır."'),
(3019, 26, 87, '"Ve beni (insanların) diriltilecekleri gün küçük düşürme,"'),
(3020, 26, 88, '\'Malın da, çocukların da bir yarar sağlayamadığı günde."'),
(3021, 26, 89, '"Ancak Allah\'a selim bir kalp ile gelenler başka."'),
(3022, 26, 90, '(O gün) Cennet takva sahiplerine yaklaştırılır.'),
(3023, 26, 91, 'Cehennem de azgınlar için sergilenir.'),
(3024, 26, 92, 'Ve onlara: "Tapmakta olduklarınız nerede?" denilir;'),
(3025, 26, 93, '"Allah\'ın dışında olan (ilah)lar; size yardımları dokunuyor mu, veya kendilerine yardımları oluyor mu?'),
(3026, 26, 94, 'Artık onlar ve azgınlar onun içine dökülüverilmiştir.'),
(3027, 26, 95, 'Ve İblis\'in bütün orduları da.'),
(3028, 26, 96, 'Orada birbirleriyle çekişip tartışarak derler ki:'),
(3029, 26, 97, '"Andolsun Allah\'a, biz gerçekten apaçık bir sapıklık içindeymişiz,"'),
(3030, 26, 98, '"Çünkü sizi (yalancı olanları) alemlerin Rabbiyle eşit tutuyorduk.'),
(3031, 26, 99, '"Bizi suçlu-günahkarlardan başka saptıran olmadı."'),
(3032, 26, 100, '"Artık bizim için ne bir şefaatçi var,"'),
(3033, 26, 101, '"Ne de candan-yakın bir dost."'),
(3034, 26, 102, '"Bizim bir kere daha (dünyaya dönüşümüz mümkün) olsaydı da iman edenlerden olabilseydik."'),
(3035, 26, 103, 'Gerçekten, bunda bir ayet vardır, ama onların çoğu iman etmiş değildirler.'),
(3036, 26, 104, 'Ve şüphesiz senin Rabbin, güçlü ve üstün olandır, esirgeyendir.'),
(3037, 26, 105, 'Nuh kavmi de gönderilen (peygamber)leri yalanladı.'),
(3038, 26, 106, 'Hani onlara kardeşleri Nuh: "Sakınmaz mısınız?" demişti.'),
(3039, 26, 107, '"Gerçek şu ki, ben size gönderilmiş güvenilir bir elçiyim."'),
(3040, 26, 108, '"Artık Allah\'tan korkup-sakının ve bana itaat edin."'),
(3041, 26, 109, '"Buna karşılık ben sizden bir ücret istemiyorum; ücretim yalnızca alemlerin Rabbine aittir."'),
(3042, 26, 110, '"Artık Allah\'tan korkup-sakının ve bana itaat edin.'),
(3043, 26, 111, 'Dediler ki: "Sana, sıradan aşağılık insanlar uymuşken inanır mıyız?"'),
(3044, 26, 112, 'Dedi ki: "Onların yapmakta oldukları hakkında benim bilgim yoktur."'),
(3045, 26, 113, '"Onların hesabı yalnızca Rabbime aittir, eğer şuurundaysanız (anlarsınız.)"'),
(3046, 26, 114, '"Ve ben mü\'min olanları kovacak değilim."'),
(3047, 26, 115, '"Ben, yalnızca apaçık bir uyarıcıyım."'),
(3048, 26, 116, 'Dediler ki: "Eğer (bu söylediklerine) bir son vermeyecek olursan, gerçekten taşa tutulup kovulacaksın."'),
(3049, 26, 117, 'Dedi ki: "Rabbim, şüphesiz kavmim beni yalanladı."'),
(3050, 26, 118, '"Bundan böyle, benimle onların arasını açık bir hükümle ayır ve beni ve benimle birlikte olan mü\'minleri kurtar."'),
(3051, 26, 119, 'Bunun üzerine, onu ve onunla birlikte olanları (insan ve hayvanlarla) yüklü gemi içinde kurtardık.'),
(3052, 26, 120, 'Sonra bunun ardından geride kalanları da suda-boğduk.'),
(3053, 26, 121, 'Gerçekten, bunda bir ayet vardır, ama onların çoğu iman etmiş değildirler.'),
(3054, 26, 122, 'Ve şüphesiz senin Rabbin, güçlü ve üstün olandır, esirgeyendir.'),
(3055, 26, 123, 'Ad (kavmi) de gönderilen (elçi)leri yalanladı.'),
(3056, 26, 124, 'Hani onlara kardeşleri Hud: "Sakınmaz mısınız?" demişti.'),
(3057, 26, 125, '"Gerçek şu ki, ben size gönderilmiş güvenilir bir elçiyim."'),
(3058, 26, 126, '"Artık Allah\'tan korkup-sakının ve bana itaat edin."'),
(3059, 26, 127, '"Buna karşılık ben sizden bir ücret istemiyorum; ücretim yalnızca alemlerin Rabbine aittir."'),
(3060, 26, 128, '"Siz, her yüksekçe yere bir anıt inşa edip (yararsız bir şeyle) oyalanıp eğleniyor musunuz?"'),
(3061, 26, 129, '"Ölümsüz kılınmak umuduyla sanat yapıları mı ediniyorsunuz?"'),
(3062, 26, 130, '"Tutup yakaladığınız zaman da zorbalar gibi mi yakalıyorsunuz?"'),
(3063, 26, 131, '"Artık Allah\'tan korkup-sakının ve bana itaat edin."'),
(3064, 26, 132, '"Bildiğiniz şeylerle size yardım edenden korkup-sakının."'),
(3065, 26, 133, '"Size hayvanlar, çocuklar (vererek) yardım etti."'),
(3066, 26, 134, '"Bahçeler ve pınarlar da."'),
(3067, 26, 135, '"Doğrusu, ben sizin için büyük bir günün azabından korkuyorum."'),
(3068, 26, 136, 'Dediler ki: "Bizim için fark etmez; öğüt versen de, öğüt verenlerden olmasan da."'),
(3069, 26, 137, '"Bu, geçmiştekilerin \'geleneksel tutumundan başkası değildir."'),
(3070, 26, 138, '"Ve biz azap görecek de değiliz."'),
(3071, 26, 139, 'Böylelikle onu yalanladılar, Biz de onları yıkıma uğrattık. Gerçekten, bunda bir ayet vardır, ama onların çoğu iman etmiş değildirler.'),
(3072, 26, 140, 'Ve şüphesiz, senin Rabbin, güçlü ve üstün olandır, esirgeyendir.'),
(3073, 26, 141, 'Semud (kavmi) de, gönderilen (elçi)leri yalanladı.'),
(3074, 26, 142, 'Hani onlara kardeşleri Salih: "Sakınmaz mısınız? demişti.'),
(3075, 26, 143, '"Gerçek şu ki, ben size gönderilmiş güvenilir bir elçiyim."'),
(3076, 26, 144, '"Artık Allah\'tan korkup-sakının ve bana itaat edin."'),
(3077, 26, 145, '"Buna karşılık ben sizden bir ücret istemiyorum;'),
(3078, 26, 146, '"Siz burada güvenlik içinde mi bırakılacaksınız?"'),
(3079, 26, 147, '"Bahçelerin, pınarların içinde,"'),
(3080, 26, 148, '"Ekinler ve yumuşak tomurcuklu göz alıcı hurmalıklar arasında?"'),
(3081, 26, 149, '"Dağlardan ustalıkla zevkli evler yontuyorsunuz."'),
(3082, 26, 150, '"Artık Allah\'tan sakının ve bana itaat edin."'),
(3083, 26, 151, '"Ve ölçüsüzce davrananların emrine itaat etmeyin."'),
(3084, 26, 152, '"Ki onlar, yeryüzünde bozgunculuk çıkarıyor ve dirlik-düzenlik kurmuyorlar (ıslah etmiyorlar)."'),
(3085, 26, 153, 'Dediler ki: "Sen ancak büyülenmişlerdensin."'),
(3086, 26, 154, '"Sen yalnızca bizim benzerimiz olan bir beşerden başkası değilsin; eğer doğru sözlü isen, bu durumda bir ayet (mucize) getir-görelim."'),
(3087, 26, 155, 'Dedi ki: "İşte, bu bir dişi devedir; su içme hakkı (bir gün) onun, belli bir günün su içme hakkı da sizindir."'),
(3088, 26, 156, '"Ona bir kötülükle dokunmayın, sonra büyük bir günün azabı sizi yakalar.'),
(3089, 26, 157, '"Sonunda onu (yine de) kestiler, ancak pişman oldular."'),
(3090, 26, 158, 'Böylece azap onları yakaladı. Gerçekten, bunda bir ayet vardır, ama onların çoğu iman etmiş değildirler.'),
(3091, 26, 159, 'Ve şüphesiz, senin Rabbin, güçlü ve üstün olandır, esirgeyendir.'),
(3092, 26, 160, 'Lut (kavmi) de, gönderilen (elçi)leri yalanladı.'),
(3093, 26, 161, 'Hani onlara kardeşleri Lut: "Sakınmaz mısınız?" demişti.'),
(3094, 26, 162, '"Gerçek şu ki, ben size gönderilmiş güvenilir bir elçiyim."'),
(3095, 26, 163, '"Artık Allah\'tan korkup-sakının ve bana itaat edin."'),
(3096, 26, 164, '"Buna karşılık ben sizden bir ücret istemiyorum; ücretim yalnızca alemlerin Rabbine aittir."'),
(3097, 26, 165, '"Siz insanlardan (cinsel arzuyla) erkeklere mi gidiyorsunuz?'),
(3098, 26, 166, '"Rabbinizin sizler için yaratmış bulunduğu eşlerinizi bırakıyorsunuz. Hayır, siz sınırı çiğneyen bir kavimsiniz."'),
(3099, 26, 167, 'Dediler ki: "Ey Lut, eğer (bu söylediklerine) bir son vermeyecek olursan, gerçekten (burdan) sürülüp çıkarılanlardan olacaksın."'),
(3100, 26, 168, 'Dedi ki: "Gerçekten ben, sizin bu yaptığınıza öfke ile karşı olanlardanım."'),
(3101, 26, 169, '"Rabbim, beni ve ailemi bunların yaptıklarından kurtar."'),
(3102, 26, 170, 'Bunun üzerine onu ve bütün ailesini kurtardık.'),
(3103, 26, 171, 'Yalnızca geri kalanlar içinde bir kocakarı hariç.'),
(3104, 26, 172, 'Sonra geride kalanları yerle bir ettik.'),
(3105, 26, 173, 'Ve üzerlerine bir yağmur yağdırdık; uyarılıp-korkutulanların yağmuru ne kötü.'),
(3106, 26, 174, 'Gerçekten, bunda bir ayet vardır, ama onların çoğu iman etmiş değildirler.'),
(3107, 26, 175, 'Ve şüphesiz, senin Rabbin, güçlü ve üstün olandır esirgeyendir.'),
(3108, 26, 176, 'Eyke halkı da, gönderilen (peygamber)leri yalanladı.'),
(3109, 26, 177, 'Hani onlara Şuayb: "Sakınmaz mısınız?" demişti.'),
(3110, 26, 178, '"Gerçek şu ki, ben size gönderilmiş güvenilir bir elçiyim."'),
(3111, 26, 179, '"Artık Allah\'tan korkup-sakının ve bana itaat edin."'),
(3112, 26, 180, '"Buna karşılık ben sizden bir ücret istemiyorum; ücretim yalnızca alemlerin Rabbine aittir."'),
(3113, 26, 181, '"Ölçüyü tam tutun ve eksiltenlerden olmayın."'),
(3114, 26, 182, '"Dosdoğru olan terazi ile tartın."'),
(3115, 26, 183, '"İnsanların eşyasını değerden düşürüp-eksiltmeyin ve yeryüzünde bozguncular olarak karışıklık çıkarmayın."'),
(3116, 26, 184, '"Sizi ve önceki yaratılmışları yaratandan sakının”.'),
(3117, 26, 185, 'Dediler ki: "Sen ancak büyülenmişlerdensin”.'),
(3118, 26, 186, '"Sen, yalnızca benzerimiz olan bir beşerden başkası değilsin ve biz senin gerçekte yalancılardan olduğunu sanıyoruz."'),
(3119, 26, 187, '"Eğer doğru sözlü isen, bu durumda gökten üstümüze bir parça düşürüver."'),
(3120, 26, 188, 'Dedi ki: "Rabbim, yaptıklarınızı daha iyi bilir.'),
(3121, 26, 189, 'Sonunda onu yalanladılar, böylece onları o gölgelik-gününün azabı yakaladı. Gerçekten o, büyük bir günün azabıydı.'),
(3122, 26, 190, 'Gerçekten, bunda bir ayet vardır, ama onların çoğu iman etmiş değildirler.'),
(3123, 26, 191, 'Ve şüphesiz, senin Rabbin, güçlü ve üstün olandır, esirgeyendir.'),
(3124, 26, 192, 'Gerçekten o (Kur\'an), alemlerin Rabbinin (bir) indirmesidir.'),
(3125, 26, 193, 'Onu Ruhu\'l-emin indirdi.'),
(3126, 26, 194, 'Uyarıcılardan olman için, senin kalbinin üzerine (indirmiştir).'),
(3127, 26, 195, 'Apaçık Arapça bir dille.'),
(3128, 26, 196, 'Ve hiç şüphesiz, o (Kur\'an), geçmişlerin kitaplarında da vardır.'),
(3129, 26, 197, 'İsrailoğulları bilginlerinin onu bilmesi onlar için bir delil (ayet) değil mi?'),
(3130, 26, 198, 'Onu Arapça bilmeyen birine indirmiş olsaydık.'),
(3131, 26, 199, 'Böylece onlara okusaydı, yine ona iman edecek değillerdi.'),
(3132, 26, 200, 'Biz onu, suçlu-günahkarların kalbine işte böyle işlettik.'),
(3133, 26, 201, 'Onlar, o pek acı azabı görünceye kadar ona inanmazlar.'),
(3134, 26, 202, 'Artık o (azap), kendileri şuurunda olmadan onlara apansız gelecektir.'),
(3135, 26, 203, 'Derler ki: "Bize bir süre tanınır mı?"'),
(3136, 26, 204, 'Onlar yine de azabımızı çabuklaştırmak mı istiyorlar?'),
(3137, 26, 205, 'Gördün mü; Biz onları yıllarca yararlandırsak,'),
(3138, 26, 206, 'Sonra kendilerine va\'dolunan (azap günü) geliverse,'),
(3139, 26, 207, 'Onların \'meta ile yararlandıkları\' şey, kendilerini (görecekleri azaptan) bağımsız kılamaz.'),
(3140, 26, 208, 'Kendisi için bir uyarıcı olmaksızın, Biz hiçbir ülkeyi yıkıma uğratmış değiliz.'),
(3141, 26, 209, '(Onlara) Hatırlatma (yapılmıştır); Biz zulmedici değiliz.'),
(3142, 26, 210, 'Onu (Kur\'an\'ı) şeytanlar indirmemiştir.'),
(3143, 26, 211, 'Bu, onlara yaraşmaz ve güç de yetiremezler.'),
(3144, 26, 212, 'Çünkü onlar, (vahyedileni) işitmekten kesin olarak uzak tutulmuşlardır.'),
(3145, 26, 213, 'Allah ile beraber başka bir İlah\'a yalvarıp-yakarma, sonra azaba uğratılanlardan olursun.'),
(3146, 26, 214, '(Öncelikle) En yakın hısımlarını (aşiretini) uyar.'),
(3147, 26, 215, 'Ve mü\'minlerden, sana tabi olanlara (koruyucu) kanatlarını ger.'),
(3148, 26, 216, 'Eğer sana isyan edecek olurlarsa, artık de ki: "Gerçekten ben, sizin yaptıklarınızdan uzağım."'),
(3149, 26, 217, 'Sen, O güçlü ve üstün, esirgeyici olan (Allah\')a tevekkül et.'),
(3150, 26, 218, 'O, kıyam ettiğin zaman seni görüyor.'),
(3151, 26, 219, 'Secde edenler arasında dönüp dolaşmanı da.'),
(3152, 26, 220, 'Hiç şüphesiz, O, işitendir, bilendir.'),
(3153, 26, 221, 'Şeytanların kimlere inmekte olduklarını size haber vereyim mi?'),
(3154, 26, 222, 'Onlar, \'gerçeği ters yüz eden,\' günaha düşkün olan her yalancıya inerler.'),
(3155, 26, 223, 'Bunlar (şeytanlara) kulak verirler ve çoğu yalan söylemektedirler.'),
(3156, 26, 224, 'Şairler ise; gerçekten onlara azgın-sapıklar uyar.'),
(3157, 26, 225, 'Görmedin mi; onlar, her bir vadide vehmedip duruyorlar,'),
(3158, 26, 226, 'Ve gerçekten onlar, yapmayacakları şeyleri söylüyorlar.'),
(3159, 26, 227, 'Ancak iman edenler, salih amellerde bulunanlar ve Allah\'ı çokça zikredenler ile zulme uğratıldıktan sonra zafer kazananlar (veya öclerini alanlar) başka. Zulmetmekte olanlar, nasıl bir inkılaba uğrayıp devrileceklerini pek yakında bileceklerdir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Ta, sin. Bunlar Kur\'an\'ın ve apaçık olan Kitab\'ın ayetleridir.'),
(3161, 27, 2, 'Mü\'minler için bir hidayet ve bir müjdedir.'),
(3162, 27, 3, 'Ki onlar, namazı dosdoğru kılarlar, zekatı verirler ve onlar, ahirete kesin bilgiyle iman ederler.'),
(3163, 27, 4, 'Ahirete inanmayanlara gelince; Biz onlara kendi yaptıklarını süslemişiz, böylece onlar, \'körlük içinde şaşkınca dolaşırlar\'.'),
(3164, 27, 5, 'İşte onlar; en kötü azap onlarındır ve ahirette de en büyük kayba uğrayanlardır.'),
(3165, 27, 6, 'Hiç şüphesiz, bu Kur\'an, sana, hüküm ve hikmet sahibi olan, (ve herşeyi gerçeğiyle) bilen (Allah\'ın) Katından ilka edilmektedir.'),
(3166, 27, 7, 'Hani Musa ailesine: "Şüphesiz ben bir ateş gördüm" demişti. "Size ondan ya bir haber veya ısınmanız için bir kor ateş getireceğim."'),
(3167, 27, 8, 'Oraya gittiğinde, kendisine seslenildi: "Ateş (yerin)de olanlar da, çevresinde bulunanlar da kutlu kılınmıştır. Alemlerin Rabbi olan Allah Yücedir.'),
(3168, 27, 9, '"Ey Musa, gerçekten Ben, güçlü ve üstün, hüküm ve hikmet sahibi olan Allah\'ım."'),
(3169, 27, 10, '"Asanı bırak;" (Bıraktı ve) onun çevik bir yılan gibi hareket etttiğini görünce, geriye doğru kaçtı ve arkasına bakmadı. “Ey Musa, korkma; şüphesiz Ben(im); Benim yanımda gönderilen (elçiler) korkmaz."'),
(3170, 27, 11, '"Ancak zulmeden başka; sonra kötülüğün ardından iyiliğe çevirirse, artık şüphesiz Ben, bağışlayanım, esirgeyenim."'),
(3171, 27, 12, '"Ve elini koynuna sok, kusursuz olarak bembeyaz çıkıversin, (bu,) Firavun ve kavmine olan dokuz ayet (mucize) içinde(n biri)dir. Gerçekten onlar, fasık olan bir kavimdir."'),
(3172, 27, 13, 'Ayetlerimiz onlara, gözler önünde sergilenmiş olarak gelince dediler ki: "Bu, apaçık olan bir büyüdür."'),
(3173, 27, 14, 'Vicdanları kabul ettiği halde, zulüm ve büyüklenme dolayısıyla bunları inkar ettiler. Artık sen, bozguncuların nasıl bir sona uğratıldıklarına bir bak.'),
(3174, 27, 15, 'Andolsun, Davud\'a ve Süleyman\'a bir ilim verdik: "Bizi inanmış kullarından birçoğuna göre üstün kılan Allah\'a hamd olsun." dediler.'),
(3175, 27, 16, 'Süleyman, Davud\'a mirasçı oldu ve dedi ki: "Ey insanlar, bize kuşların konuşma-dili öğretildi ve bize herşeyden (bol bir nimet) verildi. Gerçekten bu, apaçık bir üstünlüktür."'),
(3176, 27, 17, 'Süleyman\'a cinlerden, insanlardan ve kuşlardan orduları toplandı ve bunlar bölükler halinde dağıtıldı.'),
(3177, 27, 18, 'Nihayet karınca vadisine geldiklerinde, bir dişi karınca dedi ki: "Ey karınca topluluğu, kendi yuvalarınıza girin, Süleyman ve orduları, farkında olmaksızın sizi kırıp-geçmesin."'),
(3178, 27, 19, '(Süleyman) Bu sözü üzerine tebessüm edip güldü ve dedi ki: "Rabbim, bana, anne ve babama verdiğin nimete şükretmemi ve hoşnut olacağın salih bir amelde bulunmamı ilham et ve beni rahmetinle salih kulların arasına kat."'),
(3179, 27, 20, 'Kuşları denetledikten sonra dedi ki: "Hüdhüd\'ü neden göremiyorum, yoksa kaybolanlardan mı oldu?"'),
(3180, 27, 21, '"Onu gerçekten şiddetli bir azapla azaplandıracağım, ya da onu boğazlayacağım veya o, bana apaçık olan bir delil getirmelidir."'),
(3181, 27, 22, 'Derken uzun zaman geçmeden geldi ve dedi ki: "Senin kuşatamadığın (öğrenemediğin) şeyi, ben kuşattım ve sana Saba\'dan kesin bir haber getirdim."'),
(3182, 27, 23, '"Gerçekten ben, onlara hükmetmekte olan bir kadın buldum ki, ona herşeyden (bolca) verilmiştir ve büyük bir tahtı var."'),
(3183, 27, 24, '"Onu ve kavmini, Allah\'ı bırakıp da güneşe secde etmektelerken buldum, şeytan onlara yaptıklarını süslemiştir, böylece onları (doğru) yoldan alıkoymuştur; bundan dolayı onlar hidayet bulmuyorlar."'),
(3184, 27, 25, '"Ki onlar, göklerde ve yerde saklı olanı ortaya çıkaran ve sizin gizlediklerinizi ve açığa vurduklarınızı bilen Allah\'a secde etmesinler diye (yapmaktadırlar)."'),
(3185, 27, 26, '"O Allah, O\'ndan başka İlah yoktur, büyük Arş\'ın Rabbidir."'),
(3186, 27, 27, '(Süleyman:) "Durup bekleyeceğiz, doğruyu mu söyledin, yoksa yalancılardan mı oldun?" dedi.'),
(3187, 27, 28, '"Bu mektubumla git, onu kendilerine bırak sonra onlardan (biraz) uzaklaş, böylelikle bir bakıver, neye başvuracaklar?"'),
(3188, 27, 29, '(Hüdhüd\'ün mektubu götürüp bırakmasından sonra Saba melikesi Belkıs:) Dedi ki: "Ey önde gelenler gerçekten bana oldukça önemli bir mektup bırakıldı."'),
(3189, 27, 30, '"Gerçek şu ki, bu, Süleyman\'dandır ve \'Şüphesiz Rahman ve Rahim Olan Allah\'ın Adıyla\' (başlamakta)dır."'),
(3190, 27, 31, '(İçinde de:) "Bana karşı büyüklük göstermeyin ve bana Müslüman olarak gelin" diye (yazılmaktadır).'),
(3191, 27, 32, 'Dedi ki: "Ey önde gelenler, bu işimde bana görüş belirtin, siz (herşeye) şahidlik etmedikçe ben hiçbir işte kesin (karar veren biri) değilim."'),
(3192, 27, 33, 'Dediler ki: "Biz kuvvet sahibiyiz ve zorlu savaşçılarız. İş konusunda karar senindir, artık sen bak, neyi emredersen (biz uygularız).'),
(3193, 27, 34, 'Dedi ki: "Gerçekten hükümdarlar bir ülkeye girdikleri zaman, orasını bozguna uğratırlar ve halkından onur sahibi olanları hor ve aşağılık kılarlar; işte onlar, böyle yaparlar."'),
(3194, 27, 35, '"Ben onlara bir hediye göndereyim de, bir bakayım elçiler neyle dönerler."'),
(3195, 27, 36, '(Elçi hediyelerle) Süleyman\'a geldiği zaman: "Sizler bana mal ile yardımda mı bulunmak istiyorsunuz? Allah\'ın bana verdiği, size verdiğinden daha hayırlıdır; hayır, siz, hediyenizle sevinip öğünebilirsiniz" dedi.'),
(3196, 27, 37, '"Sen onlara dön, Biz onlara öyle ordularla geliriz ki, onların karşı koymaları mümkün değil ve Biz onları ordan horlanmış-aşağılanmış ve küçük düşürülmüşler olarak sürüp çıkarırız."'),
(3197, 27, 38, '(Elçinin gitmesinden sonra Süleyman:) "Ey önde gelenler, onlar bana teslim olmuş (Müslüman)lar olarak gelmeden önce, sizden kim onun tahtını bana getirebilir?" dedi.'),
(3198, 27, 39, 'Cinlerden ifrit: "Sen daha makamından kalkmadan, ben onu sana getirebilirim, ben gerçekten buna karşı kesin olarak güvenilir bir güce sahibim." dedi.'),
(3199, 27, 40, 'Kendi yanında kitaptan ilmi olan biri dedi ki: "Ben, (gözünü açıp kapamadan) onu sana getirebilirim." Derken (Süleyman) onu kendi yanında durur vaziyette görünce dedi ki: "Bu Rabbimin fazlındandır, O\'na şükredecek miyim, yoksa nankörlük edecek miyim diye beni denemekte olduğu için (bu olağanüstü olay gerçekleşti). Kim şükrederse, artık o kendisi için şükretmiştir, kim nankörlük ederse, gerçekten benim Rabbim Gani (hiçbir şeye ve kimseye ihtiyacı olmayan)dır, Kerim olandır.'),
(3200, 27, 41, 'Dedi ki: "Onun tahtını değişikliğe uğratın, bir bakalım doğru olanı bulabilecek mi, yoksa bulmayanlardan mı olacak?'),
(3201, 27, 42, 'Böylece (Belkıs) geldiği zaman ona: "Senin tahtın böyle mi?" denildi. Dedi ki: "Tıpkı kendisi. Bize ondan önce ilim verilmişti ve biz Müslüman olmuştuk."'),
(3202, 27, 43, 'Allah\'tan başka tapmakta olduğu şeyler onu (Müslüman olmaktan) alıkoymuştu. Gerçekte o, inkar eden bir kavimdendi.'),
(3203, 27, 44, 'Ona: "Köşke gir" denildi. Onu görünce derin bir su sandı ve (eteğini çekerek) ayaklarını açtı. (Süleyman:) Dedi ki: "Gerçekte bu, saydam camdan olma düzeltilmiş bir köşk-zemindir." Dedi ki: "Rabbim, gerçekten ben kendime zulmettim; (artık) ben Süleyman\'la birlikte alemlerin Rabbi olan Allah\'a teslim oldum."'),
(3204, 27, 45, 'Andolsun, Biz Semud (kavmine de) kardeşleri Salih\'i: "Yalnızca Allah\'a kulluk edin" diye (demek üzere) gönderdik. Bir de ne görsün, onlar birbirlerine düşman kesilmiş iki gruptur.'),
(3205, 27, 46, 'Dedi ki: "Ey kavmim, neden iyilikten önce kötülük konusunda acele davranıyorsunuz? Allah\'tan bağışlanma dilemeniz gerekmez mi? Umulur ki esirgenirsiniz."'),
(3206, 27, 47, 'Dediler ki: "Senin ve seninle birlikte olanlar yüzünden uğursuzluğa uğradık." Dedi ki: "Sizin uğursuzluğunuz (başınıza gelenler) Allah Katında (yazılı)dır. Hayır, siz denenmekte olan bir kavimsiniz."'),
(3207, 27, 48, 'Şehirde dokuzlu bir çete vardı, yeryüzünde bozgun çıkarıyorlar ve dirlik-düzenlik bırakmıyorlardı.'),
(3208, 27, 49, 'Kendi aralarında Allah adına and içerek, dediler ki: "Gece mutlaka ona ve ailesine bir baskın düzenleyelim, sonra velisine: Ailesinin yok oluşuna biz şahid olmadık ve gerçekten bizler doğruyu söyleyenleriz, diyelim."'),
(3209, 27, 50, 'Onlar hileli bir düzen kurdu. Biz de (onların hilesine karşı) onların farkında olmadığı bir düzen kurduk.'),
(3210, 27, 51, 'Artık sen, onların kurdukları hileli-düzenin uğradığı sona bir bak; Biz, onları ve kavimlerini topluca yerle bir ettik.'),
(3211, 27, 52, 'İşte, zulmetmeleri dolayısıyla enkaza dönüşmüş ıpıssız evleri. Şüphesiz bilen bir kavim için bunda bir ayet vardır.'),
(3212, 27, 53, 'İman edenleri ve sakınanları da kurtardık.'),
(3213, 27, 54, 'Lut da; hani kavmine demişti ki: "Siz, açıkça gördüğünüz halde, yine de o çirkin utanmazlığı yapacak mısınız?"'),
(3214, 27, 55, '"Siz gerçekten, kadınları bırakıp şehvetle erkeklere mi yaklaşıyorsunuz? Hayır, siz (yaptığı şeyi) bilmeyen bir kavimsiniz."'),
(3215, 27, 56, 'Kavminin cevabı: "Lut ailesini şehrinizden sürüp çıkarın. Temiz kalmak isteyen insanlarmış" demekten başka olmadı.'),
(3216, 27, 57, 'Biz de, onu ve ailesini kurtardık, yalnızca karısı hariç; onu geride (azap içinde kalanlar arasında) takdir ettik.'),
(3217, 27, 58, 'Ve üzerlerine bir yağmur yağdırdık. Uyarılanların yağmuru ne kötüdür.'),
(3218, 27, 59, 'Dedi ki: "Hamd Allah\'ındır ve selam O\'nun seçtiği kullarının üzerinedir. Allah mı daha hayırlı yoksa onların ortak koştukları mı?"'),
(3219, 27, 60, '(Onlar mı) Yoksa, gökleri ve yeri yaratan ve size gökten su indiren mi? Ki onunla (o suyla) gönül alıcı bahçeler bitirdik, sizin içinse bir ağacını bitirmek (bile) mümkün değildir. Allah ile beraber başka bir İlah mı? Hayır, onlar sapıklıkta devam eden bir kavimdir.'),
(3220, 27, 61, 'Ya da yeryüzünü bir karar yeri kılan, onun arasında ırmaklar var eden ve ona (yeryüzü için) sarsılmaz dağlar yaratan ve iki deniz arasında bir ara-engel (haciz) koyan mı? Allah ile beraber başka bir İlah mı? Hayır onların çoğu bilmiyorlar.'),
(3221, 27, 62, 'Ya da sıkıntı ve ihtiyaç içinde olana, Kendisi\'ne dua ettiği zaman icabet eden, kötülüğü açıp gideren ve sizi yeryüzünün halifeleri kılan mı? Allah ile beraber başka bir İlah mı? Ne az öğüt-alıp düşünüyorsunuz.'),
(3222, 27, 63, 'Ya da karanın ve denizin karanlıkları içinde size yol gösteren ve rahmetinin önünde rüzgarları müjde vericiler olarak gönderen mi? Allah ile beraber başka bir İlah mı? Allah, onların şirk koştuklarından Yücedir.'),
(3223, 27, 64, 'Ya da halkı sürekli yaratmakta olan, sonra onu iade edecek olan ve sizi gökten ve yerden rızıklandıran mı? Allah ile beraber başka bir İlah mı? De ki: "Eğer doğru söylüyor iseniz, kesin-kanıt (burhan)ınızı getiriniz."'),
(3224, 27, 65, 'De ki: "Göklerde ve yerde gaybı Allah\'tan başka kimse bilmez. Onlar ne zaman dirileceklerinin şuuruna varmıyorlar."'),
(3225, 27, 66, 'Hayır, onların ahiret konusundaki bilgileri \'ard arda toplanıp pekiştirildi,’ hayır, onlar bundan bir kuşku içindedirler; hayır, onlar bundan yana kördürler.'),
(3226, 27, 67, 'İnkar edenler dedi ki: "Biz ve atalarımız toprak olduktan sonra mı, gerçekten biz mi dirilip-çıkartılacakmışız?"'),
(3227, 27, 68, '"Andolsun, bu (azap ve dirilme tehdidi), bize ve daha önce atalarımıza va\'dolunmuştur. Bu, olsa olsa geçmişlerin uydurma masallarından başkası değildir."'),
(3228, 27, 69, 'De ki: "Yeryüzünde gezip dolaşın da, suçlu-günahkarların nasıl bir sona uğradıklarını görün"'),
(3229, 27, 70, 'Sen, onlara karşı hüzne kapılma ve kurdukları tuzaklardan dolayı sıkıntı içinde olma.'),
(3230, 27, 71, 'Derler ki: "Eğer doğruyu söylüyor iseniz, bu va\'dolunan (azap) ne zaman?"'),
(3231, 27, 72, 'De ki: "Belki de acele etmekte olduğunuzun (azabın) bir kısmı size yetişmiştir bile."'),
(3232, 27, 73, 'Şüphesiz, senin Rabbin, insanlara karşı büyük lütuf (fazl) sahibidir, ancak insanların çoğu şükretmiyorlar.'),
(3233, 27, 74, 'Ve şüphesiz, senin Rabbin, sinelerinin gizli tuttuklarını ve açığa vurduklarını kesin olarak bilmektedir.'),
(3234, 27, 75, 'Gökte ve yerde gizli olan hiçbir şey yoktur ki, apaçık olan bir kitapta (Levh-i Mahfuz\'da) olmasın.'),
(3235, 27, 76, 'Gerçek şu ki, bu Kur\'an, İsrailoğulları\'na hakkında ayrılığa düştükleri şeylerin bir çoğunu aktarıp anlatıyor.'),
(3236, 27, 77, 'Ve gerçekten o, mü\'minler için bir hidayet ve bir rahmettir.'),
(3237, 27, 78, 'Şüphesiz senin Rabbin, onların arasında Kendi hükmünü verecektir. O, güçlü ve üstün olandır, bilendir.'),
(3238, 27, 79, 'Sen, artık Allah\'a tevekkül et; çünkü sen apaçık olan hak üzerindesin.'),
(3239, 27, 80, 'Çünkü gerçekten sen, ölülere (söz) dinletemezsin ve arkasını dönüp kaçan sağırlara da çağrıyı işittiremezsin.'),
(3240, 27, 81, 'Ve sen körleri düştükleri sapıklıktan çekip hidayete erdirici değilsin; sen ancak, ayetlerimize iman edenlere (söz) dinletebilirsin, işte Müslüman olanlar bunlardır.'),
(3241, 27, 82, 'O söz, başlarına geldiği zaman, onlara yerden bir Dabbe çıkarırız; o da, insanların Bizim ayetlerimize kesin bir bilgiyle inanmadıklarını onlara söyler.'),
(3242, 27, 83, 'Ve her ümmetten ayetlerimizi yalanlayan bir grubu toplayacağımız gün, artık onlar \'tutuklanıp (azap yerine) dağıtılırlar.\''),
(3243, 27, 84, 'Nihayet geldikleri zaman, (Allah) der ki: "Siz Benim ayetlerimi, bilgi bakımından kavramadığınız halde yalanladınız mı? Yoksa ne yapıyordunuz?"'),
(3244, 27, 85, 'Zulmetmelerine karşılık, söz, kendi aleyhlerine gelmiş bulunmaktadır, artık konuşmazlar.'),
(3245, 27, 86, 'Görmediler mi, Biz geceyi onda sükun bulmaları için, gündüzü de aydınlık(la görsünler) diye yarattık. Şüphesiz, iman eden bir kavim için bunda ayetler vardır.'),
(3246, 27, 87, 'Sur\'a üfürüleceği gün, Allah\'ın dilediği kimseler dışında, göklerde ve yerde olan herkes artık korkuya kapılmıştır ve her biri \'boyun bükmüş\' olarak O\'na gelmişlerdir.'),
(3247, 27, 88, 'Dağları görürsün de, donmuş sanırsın; oysa onlar bulutların sürüklenmesi gibi sürüklenirler. Herşeyi \'sapasağlam ve yerli yerinde yapan\' Allah\'ın sanatı (yapısı)dır (bu). Şüphesiz O, işlediklerinizden haberdardır.'),
(3248, 27, 89, 'Kim bir iyilikle gelirse, artık kendisine daha hayırlısı vardır ve onlar, o günün korkusuna karşı güvenlik içindedirler.'),
(3249, 27, 90, 'Kim bir kötülükle gelirse, artık onlar da ateşe yüzükoyun atılır (ve onlara:) "Yaptıklarınızdan başkasıyla mı cezalandırılıyorsunuz?" (denir).'),
(3250, 27, 91, '(De ki:) "Ben, ancak bu şehrin Rabbine ibadet etmekle emrolundum ki, O, burasını kutlu ve saygıdeğer kıldı. Herşey O\'nundur. Ve Müslümanlardan olmakla emrolundum."'),
(3251, 27, 92, '"Ve Kur\'an\'ı okumakla da (emrolundum). Artık kim hidayete gelirse, kendi nefsi için hidayete gelmiştir; kim sapacak olursa, de ki: "Ben yalnızca uyarıcılardanım."'),
(3252, 27, 93, 'Ve de ki: "Allah\'a hamdolsun, O size ayetlerini gösterecektir, siz de onları bilip tanıyacaksınız." Senin Rabbin, yaptıklarınızdan gafil değildir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Ta, Sin, Mim.'),
(3254, 28, 2, 'Bunlar, apaçık Kitab\'ın ayetleridir.'),
(3255, 28, 3, 'Mü\'min olan bir kavim için hak olmak üzere, Musa ve Firavun\'un haberinden (bir bölümünü) sana okuyacağız.'),
(3256, 28, 4, 'Gerçek şu ki, Firavun yeryüzünde (Mısır\'da) büyüklenmiş ve oranın halkını birtakım fırkalara ayırıp bölmüştü; onlardan bir bölümünü güçten düşürüyor, erkek çocuklarını boğazlayıp kadınlarını diri bırakıyordu. Çünkü o, bozgunculardandı.'),
(3257, 28, 5, 'Biz ise, yeryüzünde güçten düşürülenlere lütufta bulunmak, onları önderler yapmak ve mirasçılar kılmak istiyoruz.'),
(3258, 28, 6, 'Ve (istiyoruz ki) onları yeryüzünde \'iktidar sahipleri olarak yerleşik kılalım\', Firavun\'a, Haman\'a ve askerlerine, onlardan sakındıkları şeyi gösterelim.'),
(3259, 28, 7, 'Musa\'nın annesine: "Onu emzir, şayet onun için korkacak olursan, onu suya bırak, korkma ve üzülme; çünkü onu Biz sana tekrar geri vereceğiz ve onu gönderilen (elçilerden) kılacağız" diye vahyettik (bildirdik).'),
(3260, 28, 8, 'Nihayet Firavun\'un ailesi, onu (ileride bilmeksizin) kendileri için bir düşman ve üzüntü konusu olsun diye sahipsiz görüp aldılar. Gerçekte Firavun, Haman ve askerleri bir yanılgı içindeydi.'),
(3261, 28, 9, 'Firavun\'un karısı dedi ki: "Benim için de, senin için de bir göz bebeği; onu öldürmeyin; umulur ki bize yararı dokunur veya onu evlat ediniriz." Oysa onlar (başlarına geleceklerin) şuurunda değillerdi.'),
(3262, 28, 10, 'Musa\'nın annesi ise, yüreği boşluk içinde sabahladı. Eğer mü\'minlerden olması için kalbi üzerinde (sabrı ve dayanıklılığı) pekiştirmemiş olsaydık, neredeyse onu(n durumunu) açığa vuracaktı.'),
(3263, 28, 11, 'Ve onun kız kardeşine: "Onu izle," dedi. Böylece o da, kendileri farkında değilken onu uzaktan gözetledi.'),
(3264, 28, 12, 'Biz, daha önce ona süt analarını haram etmiştik. (Kız kardeşi:) "Ben, sizin adınıza onun bakımını üstlenecek ve ona öğüt verecek (veya eğitecek) bir aileyi size bildireyim mi?" dedi.'),
(3265, 28, 13, 'Böylelikle, gözünün aydın olması, üzülmemesi ve gerçekten Allah\'ın va\'dinin hak olduğunu bilmesi için, onu annesine geri vermiş olduk. Ancak onların çoğu bilmezler.'),
(3266, 28, 14, 'O, erginlik çağına ulaşıp olgunlaşınca, ona bir \'hüküm ve hikmet\' ve ilim verdik. Biz iyilikte bulunanları işte böyle ödüllendiririz.'),
(3267, 28, 15, '(Musa) Halkının haberi olmadığı bir zamanda şehre girdi, orda kavga etmekte olan iki adam buldu; bu kendi taraftarlarından, şu da düşmanlarından. Derken taraftarlarından olan, düşmanlarından olana karşı ondan yardım istedi. Bunun üzerine ona bir yumruk attı ve işini bitiriverdi. (Sonra da:) "Bu şeytanın işindendir; o, gerçekten açıkça saptırıcı bir düşmandır" dedi.'),
(3268, 28, 16, 'Dedi ki: "Rabbim, gerçekten, ben kendi nefsime zulmettim, artık beni bağışla." Böylece (Allah) onu bağışladı. Şüphesiz. O, bağışlayandır, esirgeyendir.'),
(3269, 28, 17, 'Dedi ki: "Rabbim, bana verdiğin nimetler adına, artık suçlu günahkarlara destekçi olmayacağım."'),
(3270, 28, 18, 'Böylece şehirde korku içinde (çevreyi) gözetleyerek sabahladı. Derken, bir de baktı ki, dün kendisinden yardım isteyen (kişi, bugün de) kendisine yardım için bağırıyor. Musa, ona dedi ki: "Sen açıkça bir azgınsın."'),
(3271, 28, 19, 'Sonunda ikisinin de düşmanı olan (adam)ı yakalamak isterken (adam ona) dedi ki: "Ey Musa dün birini öldürdüğün gibi, bugün de beni mi öldürmek istiyorsun? Sen yeryüzünde yalnızca bir zorba olmak istiyorsun, ıslah edicilerden olmak istemiyorsun."'),
(3272, 28, 20, 'Şehrin öbür yakasından bir adam koşarak gelip dedi ki: "Ey Musa, önde gelenler, seni öldürmek konusunda aralarında görüşmektedirler, artık sen çık git; gerçekten ben sana öğüt verenlerdenim."'),
(3273, 28, 21, 'Böylece oradan korku içinde (çevreyi) gözetleyerek çıkıp gitti: "Rabbim, zalimler topluluğundan beni kurtar" dedi.'),
(3274, 28, 22, 'Medyen\'e doğru yöneldiğinde de: "Umarım Rabbim, beni doğru bir yola yöneltip iletir" dedi.'),
(3275, 28, 23, 'Medyen suyuna vardığı zaman, su almakta olan bir insan topluluğu buldu. Onların gerisinde de (hayvanları su başına götürmekten çekinen) iki kadın buldu. Dedi ki: "Bu durumunuz ne?" "Çobanlar sürülerini sulamadıkça, biz sürülerimizi sulayamayız; babamız, yaşı ilerlemiş bir ihtiyardır." dediler.'),
(3276, 28, 24, 'Hemencecik onların sürülerini suladı, sonra yine gölgeye çekilerek dedi ki: "Rabbim, doğrusu bana indirdiğin her hayra muhtacım."'),
(3277, 28, 25, 'Çok geçmeden, o iki (kadın)dan biri, (utana utana) yürüyerek ona geldi. "Babam, bizim için sürüleri sulamana karşılık sana mükafaat vermek üzere seni davet etmektedir." dedi. Bunun üzerine ona gelip de olup bitenleri anlatınca o: "Korkma" dedi. "Zalimler topluluğundan kurtulmuş oldun."'),
(3278, 28, 26, 'O (kadın)lardan biri dedi ki: "Ey babacığım, onu ücretli olarak tut; çünkü ücretle tuttuklarının en hayırlısı gerçekten o kuvvetli, güvenilir (biri)dir."'),
(3279, 28, 27, '(Babaları) Dedi ki: "Doğrusu ben, sekiz yıl bana hizmet etmene karşılık olmak üzere, şu iki kızımdan birini sana nikahlamak istiyorum; şayet on (yıl)a tamamlayacak olursan, artık o da senden. Ben sana zorluk çıkarmak istemem; beni de inşaAllah salih olanlardan bulacaksın."'),
(3280, 28, 28, '(Musa) Dedi ki: "Bu, benimle senin aranda olan (bir antlaşma)dır. Bu durumda iki süreden hangisini yerine getirirsem, artık bana karşı bir haksızlık söz konusu olamaz. Allah, söylediklerimize vekildir."'),
(3281, 28, 29, 'Böylelikle Musa, süreyi tamamlayıp ailesiyle birlikte yola koyulunca, Tur tarafında bir ateş gördü. Ailesine: "Siz durun, gerçekten bir ateş gördüm; umarım ondan ya bir haber, ya da ısınmanız için bir kor parçası getiririm" dedi.'),
(3282, 28, 30, 'Derken oraya geldiğinde, o kutlu yerdeki vadinin sağ yanında olan bir ağaçtan: "Ey Musa, Alemlerin Rabbi olan Allah Benim;" diye seslenildi.'),
(3283, 28, 31, '"Asanı bırak." (Attıktan hemen sonra) onun şimdi bir yılan gibi hareket ettiğini görünce, arkasına dönüp bakmaksızın kaçmaya başladı. "Ey Musa, dön ve korkuya kapılma. Şüphesiz güvendesin."'),
(3284, 28, 32, '"Elini koynuna sok, kusursuz olarak bembeyaz çıksın. Ve (her türlü) dehşetten yana kanatlarını kendine doğru çek. İşte bunlar, senin Rabbinden Firavun ve önde gelen adamlarına iki kesin-kanıt (mucize)dır. Gerçekten onlar, fasık bir topluluktur."'),
(3285, 28, 33, 'Dedi ki: "Rabbim, gerçekten onlardan bir kişi öldürdüm, beni öldürmelerinden korkuyorum."'),
(3286, 28, 34, '"Ve kardeşim Harun; dil bakımından o benden daha düzgün konuşmaktadır, onu da benimle birlikte bir yardımcı olarak gönder, beni doğrulasın. Çünkü onların beni yalanlamalarından korkuyorum."'),
(3287, 28, 35, '(Allah) Dedi ki: "Pazunu kardeşinle pekiştirip güçlendireceğiz; sizin ikinize de öyle bir \'güç ve yetki\' vereceğiz ki, ayetlerimiz sayesinde size erişemeyecekler. Siz ve size uyanlar galip olanlarsınız."'),
(3288, 28, 36, 'Musa, onlara apaçık olan ayetlerimizle geldiği zaman: "Bu, düzüp uydurulmuş bir büyüden başkası değildir. Biz geçmiş atalarımızdan bunu işitmedik" dediler.'),
(3289, 28, 37, 'Musa dedi ki: "Rabbim, kimin Kendisi\'nden bir hidayetle geldiğini ve bu (dünya) yurdun(un) sonucunun kime ait olacağını daha iyi bilir. Gerçekten, zulmedenler, felah bulmazlar."'),
(3290, 28, 38, 'Firavun dedi ki: "Ey önde gelenler, sizin için benden başka ilah olduğunu bilmiyorum. Ey Haman, çamurun üstünde bir ateş yak da, bana yüksekçe bir kule inşa et, belki Musa\'nın ilahına çıkarım çünkü gerçekten ben onu yalancılardan (biri) sanıyorum."'),
(3291, 28, 39, 'O ve askerleri, yeryüzünde haksız yere büyüklendiler ve gerçekten Bize döndürülmeyeceklerini sandılar.'),
(3292, 28, 40, 'Bunun üzerine, onu ve askerlerini tutup suya attık. Böylelikle zulmedenlerin nasıl bir sona uğradıklarına bir bak.'),
(3293, 28, 41, 'Biz, onları ateşe çağıran önderler kıldık; kıyamet günü yardım görmezler.'),
(3294, 28, 42, 'Bu dünya hayatında onların arkasına lanet düşürdük; kıyamet gününde ise, onlar çirkinleştirilmiş olanlardır.'),
(3295, 28, 43, 'Andolsun, ilk nesilleri yıkıma uğrattıktan sonra, Musa\'ya, insanlar için (gözleri hikmetle açıp aydınlatacak) basiretler, hidayet ve rahmet olmak üzere kitap verdik. Umulur ki, öğüt alıp-düşünürler diye.'),
(3296, 28, 44, 'Musa\'ya o işi (ilahi vahyi verip) gerçekleştirdiğimiz zaman, sen (Tur\'un) batı yanında değildin ve (buna) şahid olanlardan da değildin.'),
(3297, 28, 45, 'Ancak Biz birçok nesiller inşa ettik de onların üzerinde (nice) ömür(ler) uzayıp geçti. Ve sen Medyen halkı içinde yaşayıp da ayetlerimizi onlardan okuyarak öğrenmiş değilsin. Ancak (bu bilgileri sana) gönderen Biziz.'),
(3298, 28, 46, '(Musa\'ya) Seslendiğimiz zaman da, sen Tur\'un yanında değildin. Ancak Rabbinden bir rahmet olmak üzere senden önce kendilerine bir uyarıcı gelmemiş olan bir kavmi uyarman için (gönderildin). Umulur ki, öğüt alıp düşünürler diye.'),
(3299, 28, 47, 'Kendi ellerinin öne sürdükleri dolayısıyla, onlara bir musibet isabet ettiğinde: "Rabbimiz, bize de bir elçi gönderseydin de böylece Senin ayetlerine uysaydık ve mü\'minlerden olsaydık" diyecek olmasalardı (seni göndermezdik).'),
(3300, 28, 48, 'Fakat onlara Kendi Katımız\'dan hak geldiği zaman: "Musa\'ya verilenlerin bir benzeri buna verilmeli değil miydi?" dediler. Onlar, daha önce Musa\'ya verilenleri inkar etmemişler miydi? "İki büyü birbirine arka çıktı" dediler. Ve: "Gerçekten biz hepsini inkar edenleriz" dediler.'),
(3301, 28, 49, 'De ki: "Eğer doğruysanız, bu durumda Allah Katından bu ikisinden (Musa\'ya indirilen Tevrat ve bana indirilen Kur\'an\'dan) daha doğru olan bir kitap getirin de, ona uymuş olayım."'),
(3302, 28, 50, 'Buna rağmen sana icabet etmeyecek olurlarsa, artık bil ki, onlar, gerçekten kendi heva (istek ve tutku)larına uymaktadırlar. Oysa Allah\'tan bir kılavuz (doğru yol gösterici) olmaksızın, kendi istek ve tutkularına (hevasına) uyandan daha sapık kimdir? Şüphesiz Allah, zulmeden bir kavme hidayet vermez.'),
(3303, 28, 51, 'Andolsun, Biz öğüt alıp-düşünsünler diye, sözü birbiri ardınca dizip-indirdik.'),
(3304, 28, 52, 'Bu (Kur\'an)dan önce, kitap verdiklerimiz buna inanmaktadırlar.'),
(3305, 28, 53, 'Onlara okunduğu zaman: "Biz ona inandık, gerçekten o, Rabbimiz\'den olan bir haktır, şüphesiz biz bundan önce de Müslümanlar idik" derler.'),
(3306, 28, 54, 'İşte onlar; sabretmeleri dolayısıyla ecirleri iki defa verilir ve onlar kötülüğü iyilikle uzaklaştırıp kendilerine rızık olarak verdiklerimizden infak ederler.'),
(3307, 28, 55, '\'Boş ve yararsız olan sözü\' işittikleri zaman ondan yüz çevirirler ve: "Bizim yapıp-ettiklerimiz bizim, sizin yapıp-ettikleriniz sizindir; size selam olsun, biz cahilleri benimsemeyiz" derler.'),
(3308, 28, 56, 'Gerçek şu ki, sen, sevdiğini hidayete erdiremezsin, ancak Allah, dilediğini hidayete erdirir; O, hidayete erecek olanları daha iyi bilendir.'),
(3309, 28, 57, 'Dediler ki: "Eğer seninle birlikte hidayete uyacak olursak, yerimizden (yurdumuzdan ve konumumuzdan) çekilip-kopartılırız." Oysa Biz onları, Kendi Katımız\'dan bir rızık olarak herşeyin ürününün aktarılıp toplandığı, güvenli bir haremde yerleşik kılmadık mı? Fakat onların çoğu bilmiyorlar.'),
(3310, 28, 58, 'Biz, yaşama biçimleriyle \'refah içinde şımarıp azmış\' nice şehri yıkıma uğrattık. İşte meskenleri; çok az (bir zaman) dışında (onlarda) kendilerinden sonra oturulabilmiş değildir. (Onlara) Varis olanlar Biziz.'),
(3311, 28, 59, 'Senin Rabbin, \'ana yerleşim merkezlerine\' onlara ayetlerimizi okuyan bir elçi göndermedikçe şehirleri yıkıma uğratıcı değildir. Ve Biz, halkı zulmeden şehirlerden başkasını da yıkıma uğratıcı değiliz.'),
(3312, 28, 60, 'Size verilen herşey, yalnızca dünya hayatının metaı ve süsüdür. Allah Katında olan ise, daha hayırlı ve daha süreklidir. Yine de, akıllanmayacak mısınız?'),
(3313, 28, 61, 'Şimdi, kendisine güzel bir vaadde bulunduğumuz, dolayısıyla ona kavuşan kişi, dünya hayatının metaı ile metalandırdığımız sonra kıyamet günü (azaba uğramak için) hazır bulundurulan kişi gibi midir?'),
(3314, 28, 62, 'O gün (Allah) onlara seslenerek: "Bana ortak olarak öne sürdükleriniz nerede?" der.'),
(3315, 28, 63, 'Üzerlerine (azap) sözü hak olanlar derler ki: "Rabbimiz, işte bizim azdırıp-saptırdıklarımız bunlar; kendimiz azıp saptığımız gibi, onları da azdırıp saptırdık. (Şimdiyse) Sana (gelip onlardan) uzaklaşmış bulunmaktayız. Onlar bize tapıyor da değillerdi.'),
(3316, 28, 64, 'Denir ki: "Ortaklarınızı çağırın." Böylelikle çağırırlar, ama kendilerine cevap vermezler ve azabı görürler. Hidayet bulmuş olsalardı ne olurdu.'),
(3317, 28, 65, 'O gün (Allah) onlara seslenerek: "Gönderilen (elçilere) ne cevab verdiniz?" der.'),
(3318, 28, 66, 'Artık o gün, haberler onlar için körelmiştir; birbirlerine de soramazlar.'),
(3319, 28, 67, 'Ancak kim tevbe edip iman eder ve salih amellerde bulunursa artık kurtuluşa erenlerden olmayı umabilir.'),
(3320, 28, 68, 'Rabbin, dilediğini yaratır ve seçer; seçim onlara ait değildir. Allah, onların ortak koştuklarından münezzehtir, Yücedir.'),
(3321, 28, 69, 'Rabbin onların göğüslerinin sakladıklarını ve açığa vurduklarını bilir.'),
(3322, 28, 70, 'O, Allah\'tır, Kendisi\'nden başka İlah yoktur. İlkte de, sonda da hamd O\'nundur. Hüküm O\'nundur ve O\'na döndürüleceksiniz.'),
(3323, 28, 71, 'De ki: "Gördünüz mü söyleyin; Allah, kıyamet gününe kadar geceyi sizin üzerinizde kesintisizce sürdürecek olsa, Allah\'ın dışında size aydınlık verecek İlah kimdir? Yine de dinlemeyecek misiniz?"'),
(3324, 28, 72, 'De ki: "Gördünüz mü söyleyin, Allah kıyamet gününe kadar gündüzü sizin üzerinizde kesintisizce sürdürecek olsa Allah\'ın dışında size içinde dinleneceğiniz geceyi getirecek İlah kimdir? Yine de görmeyecek misiniz?'),
(3325, 28, 73, 'Kendi rahmetinden olmak üzere O, sizin için, dinlenmeniz ve O\'nun fazlından (geçiminizi) aramanız için geceyi ve gündüzü var etti. Umulur ki şükredersiniz.'),
(3326, 28, 74, 'O gün (Allah) onlara seslenerek: "Bana ortak olarak öne sürdükleriniz nerede" der.'),
(3327, 28, 75, 'Her ümmetten bir şahid ayırıp çıkardık da: "Kesin-kanıt (burhan)ınızı getirin" dedik. Artık öğrenmiş oldular ki, hak, gerçekten Allah\'ındır ve düzüp uydurdukları kendilerinden uzaklaşıp-kaybolmuşlardır.'),
(3328, 28, 76, 'Gerçek şu ki, Karun, Musa\'nın kavmindendi, ancak onlara karşı azgınlaştı. Biz, ona öyle hazineler vermiştik ki, anahtarları, birlikte (taşımaya) davranan güçlü bir topluluğa ağır geliyordu. Hani kavmi ona demişti ki: "Şımararak sevinme, çünkü Allah, şımararak sevince kapılanları sevmez."'),
(3329, 28, 77, '"Allah\'ın sana verdiğiyle ahiret yurdunu ara, dünyadan da kendi payını (nasibini) unutma. Allah\'ın sana ihsan ettiği gibi, sen de ihsanda bulun ve yeryüzünde bozgunculuk arama. Çünkü Allah, bozgunculuk yapanları sevmez."'),
(3330, 28, 78, 'Dedi ki: "Bu, bende olan bir bilgi dolayısıyla bana verilmiştir." Bilmez mi, ki gerçekten Allah, kendisinden önceki nesillerden kuvvet bakımından kendisinden daha güçlü ve insan-sayısı bakımından daha çok olan kimseleri yıkıma uğratmıştır. Suçlu-günahkarlardan kendi günahları sorulmaz.'),
(3331, 28, 79, 'Böylelikle kendi ihtişamlı-süsü içinde kavminin karşısına çıktı. Dünya hayatını istemekte olanlar: "Ah keşke, Karun\'a verilenin bir benzeri bizim de olsaydı. Gerçekten o, büyük bir pay sahibidir" dediler.'),
(3332, 28, 80, 'Kendilerine ilim verilenler ise: "Yazıklar olsun size, Allah\'ın sevabı, iman eden ve salih amellerde bulunan kimse için daha hayırlıdır; buna da sabredenlerden başkası kavuşturulmaz" dediler.'),
(3333, 28, 81, 'Sonunda onu da, konağını da yerin dibine geçirdik. Böylece Allah\'a karşı ona yardım edecek bir topluluğu olmadı. Ve o, kendi kendine yardım edebileceklerden de değildi.'),
(3334, 28, 82, 'Dün, onun yerinde olmayı dileyenler, sabahladıklarında: "Vay, demek ki Allah, kullarından dilediğinin rızkını genişletip-yaymakta ve kısıp-daraltmaktadır. Eğer Allah, bize lütfetmiş olmasaydı, bizi de şüphesiz batırırdı. Vay, demek gerçekten inkar edenler felah bulamaz" demeye başladılar.'),
(3335, 28, 83, 'İşte ahiret yurdu; Biz onu, yeryüzünde büyüklenmeyenlere ve bozgunculuk yapmak istemeyenlere (armağan) kılarız. (Güzel) Sonuç takva sahiplerinindir.'),
(3336, 28, 84, 'Kim bir iyilikle gelirse, artık onun için daha hayırlısı vardır; kim bir kötülükle gelirse, artık kötülükleri yapanlar, yalnızca yaptıklarıyla karşılık görürler.'),
(3337, 28, 85, 'Şüphesiz, sana Kur\'an\'ı farz kılan, seni dönülecek yere elbette döndürecektir. De ki: "Rabbim, hidayetle geleni de, açıkça bir sapıklık içinde olanı da daha iyi bilmektedir."'),
(3338, 28, 86, 'Kitab\'ın sana (kalbine vahy ile) bırakılacağını umud etmezdin; (bu,) Rabbinden ancak bir rahmettir. Öyleyse sakın kafirlere arka olma.'),
(3339, 28, 87, 'Sana indirildikten sonra, sakın seni Allah\'ın ayetlerinden alıkoymasınlar. Sen Rabbine çağır ve sakın müşriklerden olma.'),
(3340, 28, 88, 'Ve Allah ile beraber başka bir İlah\'a tapma. O\'ndan başka İlah yoktur. O\'nun yüzünden (zatından) başka herşey helak olucudur. Hüküm O\'nundur ve siz O\'na döndürüleceksiniz.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Elif, Lam, Mim.'),
(3342, 29, 2, 'İnsanlar, (sadece) "İman ettik" diyerek, sınanmadan bırakılacaklarını mı sandılar?'),
(3343, 29, 3, 'Andolsun, onlardan öncekileri sınadık; Allah, gerçekten doğruları da bilmekte ve gerçekten yalancıları da bilmektedir.'),
(3344, 29, 4, 'Yoksa kötülükleri yapanlar, Bizi (aşıp) geçeceklerini mi sandılar? Ne kötü hükmediyorlar?'),
(3345, 29, 5, 'Kim Allah\'a kavuşmayı umuyorsa hiç şüphesiz Allah\'ın (tespit ettiği) süresi yaklaşarak-gelmektedir. O, işitendir, bilendir.'),
(3346, 29, 6, 'Kim cihad ederse, yalnızca kendi nefsi için cihad etmiş olur. Şüphesiz Allah, alemlerden müstağnidir.'),
(3347, 29, 7, 'İman edip salih amellerde bulunanlar ise; Biz şüphesiz onların kötülüklerini örteceğiz ve şüphesiz yaptıklarının en güzeliyle karşılık vereceğiz.'),
(3348, 29, 8, 'Biz insana, anne ve babasına (karşı) güzelliği (ilke edinmesini) tavsiye ettik. Eğer onlar, hakkında bilgin olmayan şeyle Bana ortak koşman için sana karşı çaba harcayacak olurlarsa, bu durumda, onlara itaat etme. Dönüşünüz Banadır. Artık yaptıklarınızı size haber vereceğim.'),
(3349, 29, 9, 'İman edip salih amellerde bulunanlar ise; elbette onları salihlerin arasına katacağız.'),
(3350, 29, 10, 'İnsanlardan öylesi vardır ki, "Allah\'a iman ettik" der; fakat Allah uğruna eziyet gördüğü zaman, insanların (kendisine yönelttikleri işkence ve) fitnesini Allah\'ın azabıymış gibi sayar; ama Rabbinden \'bir yardım ve zafer\' gelirse, andolsun: "Biz gerçekten sizlerle birlikteydik” demektedirler. Oysa Allah, alemlerin sinelerinde olanı daha iyi bilen değil midir?'),
(3351, 29, 11, 'Allah muhakkak iman edenleri de bilmekte ve muhakkak münafıkları da bilmektedir.'),
(3352, 29, 12, 'İnkar edenler, iman edenlere dedi ki: "Siz bizim yolumuzu izleyin, hatalarınızı biz yüklenelim.” Oysa kendileri, onların hatalarından hiçbir şeyi yüklenecek değildir. Gerçekten onlar, elbette yalancıdırlar.'),
(3353, 29, 13, 'Şüphesiz onlar, hem kendi yüklerini, hem kendi yükleriyle birlikte başka yükleri de yüklenecekler ve kıyamet günü, düzüp uydurduklarına karşı sorguya çekileceklerdir.'),
(3354, 29, 14, 'Andolsun, Biz Nuh\'u kendi kavmine (elçi olarak) gönderdik, içlerinde elli yılı eksik olmak üzere bin sene yaşadı. Sonunda onlar zulme devam ederlerken tufan kendilerini yakalayıverdi.'),
(3355, 29, 15, 'Böylece Biz onu ve gemi halkını kurtardık ve bunu alemlere bir ayet (kendisinden ders çıkarılacak bir olay) kılmış olduk.'),
(3356, 29, 16, 'İbrahim de; hani kavmine demişti ki: "Allah\'a kulluk edin ve O\'ndan sakının, eğer bilirseniz bu sizin için daha hayırlıdır."'),
(3357, 29, 17, '"Siz yalnızca Allah\'tan başka birtakım putlara tapıyor ve birtakım yalanlar uyduruyorsunuz. Gerçek şu ki, sizin Allah\'tan başka taptıklarınız, size rızık vermeye güç yetiremezler; öyleyse rızkı Allah\'ın Katında arayın, O\'na kulluk edin ve O\'na şükredin. Siz O\'na döndürüleceksiniz."'),
(3358, 29, 18, '"Eğer yalanlarsanız, sizden önceki ümmetler de (elçilerin çağrısını) yalanlamışlardır. Elçiye düşen ise, yalnızca açık bir tebliğdir."'),
(3359, 29, 19, 'Onlar görmediler mi ki, Allah yaratmaya nasıl başlıyor, sonra onu iade ediyor? Şüphesiz, bu Allah\'a göre kolaydır.'),
(3360, 29, 20, 'De ki: "Yeryüzünde gezip dolaşın da, böylelikle yaratmaya nasıl başladığına bir bakın, sonra Allah ahiret yaratmasını (veya son yaratmayı) da inşa edip yaratacaktır. Şüphesiz Allah, herşeye güç yetirendir.'),
(3361, 29, 21, 'Dilediğini azaplandırır, dilediğine merhamet eder. O\'na çevrilip-götürüleceksiniz.'),
(3362, 29, 22, 'Siz yerde ve gökte (Allah\'ı) aciz bırakamazsınız. Sizin Allah\'ın dışında veliniz yoktur, yardım edeniniz de yoktur.'),
(3363, 29, 23, 'Allah\'ın ayetlerini ve O\'na kavuşmayı \'yok sayıp inkar edenler\'; işte onlar, Benim rahmetimden umut kesmişlerdir; ve işte onlar, acı azap onlarındır.'),
(3364, 29, 24, 'Bunun üzerine kavminin (İbrahim\'e) cevabı yalnızca: "Onu öldürün ya da yakın" demek oldu. Böylece Allah onu ateşten kurtardı. Şüphesiz bunda, iman eden bir kavim için ayetler vardır.'),
(3365, 29, 25, '(İbrahim) Dedi ki: "Siz gerçekten, Allah\'ı bırakıp dünya hayatında aranızda bir sevgi-bağı olarak putları (ilahlar) edindiniz. Sonra kıyamet günü, kiminiz kiminizi inkar edip-tanımayacak ve kiminiz kiminize lanet edeceksiniz. Sizin barınma yeriniz ateştir ve hiçbir yardımcınız yoktur."'),
(3366, 29, 26, 'Bunun üzerine Lut ona iman etti ve dedi ki: "Gerçekten ben, Rabbime hicret edeceğim. Çünkü şüphesiz O, güçlü ve üstün olandır, hüküm ve hikmet sahibidir."'),
(3367, 29, 27, 'Biz ona İshak\'ı ve Yakub\'u armağan ettik ve onun soyunda (seçtiklerimize) peygamberliği ve kitabı (vahy ihsanı) kıldık, ecrini de dünyada verdik. Şüphesiz o, ahirette salih olanlardandır.'),
(3368, 29, 28, 'Lut da; hani kavmine demişti: "Siz gerçekten, sizden önce alemlerden hiç kimsenin yapmadığı \'çirkin bir utanmazlığı\' yapıyorsunuz."'),
(3369, 29, 29, '"Siz, (yine de) erkeklere yaklaşacak, yol kesecek ve biraraya gelişlerinizde çirkinlikler yapacak mısınız?" Bunun üzerine kavminin cevabı yalnızca: "Eğer doğru söylüyor isen, bize Allah\'ın azabını getir" demek oldu.'),
(3370, 29, 30, 'Dedi ki: "Rabbim, fesat çıkaran (bu) kavme karşı bana yardım et."'),
(3371, 29, 31, 'Bizim elçilerimiz İbrahim\'e bir müjde ile geldikleri zaman, dediler ki: "Gerçek şu ki, biz bu ülkenin halkını yıkıma uğratacağız. Çünkü onun halkı zalim oldular.”'),
(3372, 29, 32, 'Dedi ki: "Onun içinde Lut da vardır." Dediler ki: "Onun içinde kimin olduğunu Biz daha iyi biliriz. Kendi karısı dışında, onu ve ailesini muhakkak kurtaracağız. O (karısı) arkada kalacak olanlardandır."'),
(3373, 29, 33, 'Elçilerimiz Lut\'a geldikleri zaman o, bunlar dolayısıyla kötüleşti ve içi daraldı. Dediler ki: "Korkuya düşme ve hüzne kapılma. Karın dışında, seni ve aileni muhakak kurtaracağız. O ise, arkada kalacaktır."'),
(3374, 29, 34, '"Şüphesiz Biz, fasıklık yapmalarından dolayı, bu ülke halkının üstüne gökten iğrenç bir azap indireceğiz."'),
(3375, 29, 35, 'Andolsun, Biz akledebilecek bir kavim için orada apaçık bir ayet bırakmışızdır.'),
(3376, 29, 36, 'Medyen\'e de kardeşleri Şuayb\'ı (gönderdik) Böylece dedi ki: "Ey kavmim, Allah\'a kulluk edin ve ahiret gününü umud edin ve yeryüzünde bozguncular olarak karışıklık çıkarmayın."'),
(3377, 29, 37, 'Ancak onu yalanladılar; bunun üzerine onları amansız bir sarsıntı yakalayıverdi, böylelikle kendi yurtlarında diz üstü çökmüş olarak sabahladılar.'),
(3378, 29, 38, 'Ad\'ı ve Semud\'u da (yıkıma uğrattık). Gerçek şu ki, kendi oturdukları yerlerden size (durumları) belli olmaktadır. Kendi yaptıklarını şeytan süsleyip-çekici kıldı, böylece onları yoldan alıkoydu. Oysa onlar görebilen kimselerdi.'),
(3379, 29, 39, 'Karun\'u, Firavun\'u ve Haman\'ı da (yıkıma uğrattık). Andolsun, Musa onlara apaçık delillerle gelmişti, ancak yeryüzünde büyüklendiler. Oysa onlar (azaptan kurtulup) geçecek değillerdi.'),
(3380, 29, 40, 'İşte Biz, onların her birini kendi günahıyla yakalayıverdik. Böylece onlardan kiminin üstüne taş fırtınası gönderdik, kimini şiddetli bir çığlık sarıverdi, kimini yerin dibine geçirdik, kimini de suda boğduk. Allah onlara zulmedici değildi, ancak onlar kendi nefislerine zulmediyorlardı.'),
(3381, 29, 41, 'Allah\'ın dışında başka veliler edinenlerin örneği, kendine ev edinen örümcek örneğine benzer. Gerçek şu ki, evlerin en dayanıksız olanı örümcek evidir; bir bilselerdi.'),
(3382, 29, 42, 'Allah, Kendi dışında hangi şeye taptıklarını şüphesiz bilir. O, güçlü ve üstün olandır, hüküm ve hikmet sahibidir.'),
(3383, 29, 43, 'İşte bu örnekler; Biz bunları insanlara vermekteyiz. Ancak alimlerden başkası bunlara akıl erdirmez.'),
(3384, 29, 44, 'Allah gökleri ve yeri hak olarak yarattı. Şüphesiz, bunda iman edenler için bir ayet vardır.'),
(3385, 29, 45, 'Sana Kitap\'tan vahyedileni oku ve namazı dosdoğru kıl. Gerçekten namaz, çirkin utanmazlıklar (fahşa)dan ve kötülüklerden alıkoyar. Allah\'ı zikretmek ise muhakkak en büyük (ibadet)tür. Allah, yaptıklarınızı bilir.'),
(3386, 29, 46, 'İçlerinde zulmedenleri hariç olmak üzere, Kitap Ehliyle en güzel olan bir tarzın dışında mücadele etmeyin. Ve deyin ki: "Bize ve size indirilene iman ettik; bizim İlahımız da, sizin İlahınız da birdir ve biz O\'na teslim olmuşuz."'),
(3387, 29, 47, 'İşte Biz sana böyle bir Kitap indirdik. Bundan dolayı kendilerine kitap verdiklerimiz ona iman etmektedirler. Bunlar (putatapıcılar)dan da ona iman edecek olanlar vardır. İnkarcılardan başkası Bizim ayetlerimizi inkar etmez.'),
(3388, 29, 48, 'Bundan önce sen hiç kitap okuyan değildin ve onu sağ elinle de yazmıyordun. Böyle olsaydı, batılda olanlar kuşkuya kapılırlardı.'),
(3389, 29, 49, 'Hayır, o, kendilerine ilim verilenlerin göğüslerinde apaçık olan ayetlerdir. Zulmedenlerden başkası, Bizim ayetlerimizi inkar etmez.'),
(3390, 29, 50, 'Dediler ki: "Ona Rabbinden ayetler (birtakım mucizeler) indirilmeli değil miydi?" De ki: "Ayetler yalnızca Allah\'ın Katındadır. Ben ise, ancak apaçık bir uyarıcıyım."'),
(3391, 29, 51, 'Kendilerine okunmakta olan Kitab\'ı sana indirmemiz onlara yetmiyor mu? Şüphesiz, bunda iman eden bir kavim için gerçekten bir rahmet ve bir öğüt (zikir) vardır.'),
(3392, 29, 52, 'De ki: "Benimle sizin aranızda şahid olarak Allah yeter. O, göklerde ve yerde olanı bilir. Batıla inanan ve Allah\'ı inkar edenler ise, işte onlar hüsrana uğrayanlardır."'),
(3393, 29, 53, 'Azap konusunda senden acele (davranmanı) istiyorlar. Eğer adı konulmuş bir ecel (tayin edilmiş bir vakit) olmasaydı, herhalde onlara azap gelmiş olurdu. Fakat kendileri şuurunda olmadan, onlara kuşkusuz apansız geliverecektir.'),
(3394, 29, 54, 'Azap konusunda senden acele (davranmanı) istiyorlar. Oysa cehennem, o inkar edenleri gerçekten kuşatıp-durmaktadır.'),
(3395, 29, 55, 'Azabın onları üstlerinden ve ayaklarının altından kaplayacağı gün (Allah): "Yaptıklarınızı tadın" der.'),
(3396, 29, 56, 'Ey iman eden kullarım, şüphesiz Benim arzım geniştir; artık yalnızca Bana ibadet edin.'),
(3397, 29, 57, 'Her nefis ölümü tadıcıdır; sonra Bize döndürüleceksiniz.'),
(3398, 29, 58, 'İman edip salih amellerde bulunanlar; onları, içinde ebedi kalıcılar olarak, altından ırmaklar akan cennetin yüksek köşklerine muhakkak yerleştireceğiz. (Salih) Amellerde bulunanların ecri ne güzeldir.'),
(3399, 29, 59, 'Ki onlar, sabredenler ve Rablerine tevekkül edenlerdir.'),
(3400, 29, 60, 'Kendi rızkını taşıyamayan nice canlı vardır ki onu ve sizi Allah rızıklandırır. O, işitendir, bilendir.'),
(3401, 29, 61, 'Andolsun, onlara: "Gökleri ve yeri kim yarattı, güneşi ve ayı kim emre amade kıldı?" diye soracak olursan, şüphesiz: "Allah" diyecekler. Şu halde nasıl oluyor da çevriliyorlar?'),
(3402, 29, 62, 'Allah, kullarından dilediğine rızkı yayıp-genişletir, (ve) kısar da. Şüphesiz Allah, herşeyi bilendir.'),
(3403, 29, 63, 'Andolsun onlara: "Gökten su indirip de ölümünden sonra yeryüzünü dirilten kimdir?" diye soracak olursan, şüphesiz: "Allah" diyecekler. De ki: "Hamd Allah\'ındır." Hayır, onların çoğu akletmiyorlar.'),
(3404, 29, 64, 'Bu dünya hayatı, yalnızca bir oyun ve \'(eğlence türünden) tutkulu bir oyalanmadır\'. Gerçekten ahiret yurdu ise, asıl hayat odur. Bir bilselerdi.'),
(3405, 29, 65, 'Onlar gemiye bindikleri zaman, dini yalnızca O\'na \'halis kılan gönülden bağlılar\' olarak, Allah\'a yalvarıp yakarırlar. Ama onları karaya çıkarıp kurtarınca, hemen şirk koşarlar.'),
(3406, 29, 66, 'Kendilerine verdiğimiz (nimetler)e nankörlük etsinler ve yararlanıp-metalansınlar diye. Ancak onlar yakında bileceklerdir.'),
(3407, 29, 67, 'Görmediler mi ki, çevrelerinde insanlar kapılıp-yağma edilirken, Biz Harem (Mekke)yi güvenilir (ve dokunulmaz) kıldık? Yine de onlar, batıla inanıp Allah\'ın nimetlerine nankörlük mü ediyorlar?'),
(3408, 29, 68, 'Allah hakkında yalan uydurup iftira edenlerden veya kendisine hak geldiği zaman onu yalan sayandan daha zalim kimdir? İnkar edenlere cehennem içinde bir konaklama yeri mi yok?'),
(3409, 29, 69, 'Bizim uğrumuzda cihad edenlere, şüphesiz yollarımızı gösteririz. Gerçekten Allah, ihsan edenlerle beraberdir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Elif, Lam, Mim.'),
(3411, 30, 2, 'Rum (orduları) yenilgiye uğradı.'),
(3412, 30, 3, 'Yakın bir yerde. Ama onlar, yenilgilerinden sonra yeneceklerdir.'),
(3413, 30, 4, 'Birkaç yıl içinde. Bundan önce de, sonra da emir Allah\'ındır. Ve o gün mü\'minler sevineceklerdir.'),
(3414, 30, 5, 'Allah\'ın yardımıyla. O, dilediğine yardım eder. O, güçlü ve üstün olandır, esirgeyendir.'),
(3415, 30, 6, '(Bu,) Allah\'ın va’didir; Allah, vadinden geri dönmez. Ancak insanların çoğu bilmezler.'),
(3416, 30, 7, 'Onlar, dünya hayatından (yalnızca) dışta olanı bilirler. Ahiretten ise gafil olanlardır.'),
(3417, 30, 8, 'Kendi nefisleri konusunda düşünmüyorlar mı? Allah, gökleri, yeri ve bu ikisi arasında olanları ancak hak ile ve belirlenmiş bir süre (ecel) olarak yaratmıştır. Gerçekten, insanlardan çoğu Rablerine kavuşmayı inkar ediyorlar.'),
(3418, 30, 9, 'Yeryüzünde gezip dolaşmıyorlar mı? Böylece kendilerinden öncekilerin nasıl bir sona uğradıklarını görsünler. Onlar, güç bakımından kendilerinden daha üstün idiler, toprağı alt-üst etmişler (ekmişler, madenler, sular arayıp çıkarmışlar) ve onu, kendilerinin imar ettiğinden daha çok imar etmişlerdi. Elçileri de, onlara açık delillerle gelmişti. Demek ki Allah onlara zulmetmiyordu, ancak onlar kendi nefislerine zulmediyorlardı.'),
(3419, 30, 10, 'Sonra kötülük yapanların uğradıkları son, Allah\'ın ayetlerini yalanlamaları ve alay konusu edinmeleri dolayısıyla çok kötü oldu.'),
(3420, 30, 11, 'Allah, yaratmayı başlatır, sonra onu iade eder, sonra da siz O\'na döndürülürsünüz.'),
(3421, 30, 12, 'Kıyamet-saatinin kopacağı gün, suçlu-günahkarlar umutsuzca yıkılırlar.'),
(3422, 30, 13, '(Allah\'a eş koştukları) Ortaklarından kendilerine şefaatçi olan yoktur; onlar, ortaklarını inkar ediyorlar.'),
(3423, 30, 14, 'Kıyamet-saatinin kopacağı gün, (mü\'minlerle kafirler birbirlerinden) ayrılırlar.'),
(3424, 30, 15, 'Böylece iman edip salih amellerde bulunanlar; artık onlar \'bir cennet bahçesinde\' \'sevinç içinde ağırlanırlar\'.'),
(3425, 30, 16, 'Ancak inkar edip ayetlerimizi ve ahirete kavuşmayı yalanlayanlar ise; artık onlar da azap için hazır bulundurulurlar.'),
(3426, 30, 17, 'Öyleyse akşama girdiğiniz vakit de, sabaha erdiğiniz vakit de Allah\'ı tesbih edip (yüceltin).'),
(3427, 30, 18, 'Hamd O\'nundur; göklerde ve yerde, günün sonunda ve öğleye erdiğiniz vakit de.'),
(3428, 30, 19, 'O ölüden diriyi çıkarır ve diriden ölüyü çıkarır, ölümünden sonra da yeri diriltir. İşte siz de böyle çıkarılacaksınız.'),
(3429, 30, 20, 'Sizi topraktan yaratmış bulunması, O\'nun ayetlerindendir; sonra siz, (yeryüzünün her yanına) yayılmakta olan bir beşer (türü) oldunuz.'),
(3430, 30, 21, 'Onda \'sükun bulup durulmanız\' için, size kendi nefislerinizden eşler yaratması ve aranızda bir sevgi ve merhamet kılması da, O\'nun ayetlerindendir. Şüphesiz bunda, düşünebilen bir kavim için gerçekten ayetler vardır.'),
(3431, 30, 22, 'Göklerin ve yerin yaratılması ile dillerinizin ve renklerinizin ayrı olması, O\'nun ayetlerindendir. Şüphesiz bunda, alimler için gerçekten ayetler vardır.'),
(3432, 30, 23, 'Geceleyin ve gündüzün uyumanız ile O\'nun fazlından (geçiminizi temin için rızkınızı) aramanız, O\'nun ayetlerindendir. Şüphesiz işitebilen bir kavim için gerçekten ayetler vardır.'),
(3433, 30, 24, 'Size bir korku ve umut (unsuru) olarak şimşeği göstermesi ile gökten su indirmek suretiyle ölümünden sonra yeri onunla diriltmesi de, O\'nun ayetlerindendir. Şüphesiz bunda, aklını kullanabilecek bir kavim için gerçekten ayetler vardır.'),
(3434, 30, 25, 'Göğün ve yerin O\'nun emriyle (hareketten kesilip olduğu yerde veya bu düzen içinde) durması da, O\'nun ayetlerindendir. Sonra sizi yerden (toprağın altından) bir (kere) çağırma ile çağırdığı zaman, hemencecik siz (bir de bakarsınız ki) çıkarılmışsınız.'),
(3435, 30, 26, 'Göklerde ve yerde bulunanlar O\'nundur; hepsi O\'na \'gönülden boyun eğmiş\' bulunuyorlar.'),
(3436, 30, 27, 'Yaratmayı başlatan, sonra onu iade edecek olan O\'dur; bu O\'na göre pek kolaydır. Göklerde ve yerde en Yüce misal O\'nundur. O, güçlü ve üstün olandır, hüküm ve hikmet sahibidir.'),
(3437, 30, 28, 'Size kendi nefislerinizden bir örnek verdi: "Size rızık olarak verdiğimiz şeylerde, sağ ellerinizin malik olduklarınızdan, sizinle eşit olup kendi kendinizden korktuğunuz gibi kendilerinden de korktuğunuz (veya çekinip saygı duyduğunuz) ortaklar var mıdır? “İşte Biz, aklını kullanabilen bir kavim için ayetleri böyle birer birer açıklarız.'),
(3438, 30, 29, 'Hayır, zulmedenler, hiçbir bilgiye dayanmaksızın kendi heva (istek ve tutku)larına uymuşlardır. Allah\'ın saptırdığını kim hidayete erdirebilir? Onların hiçbir yardımcıları yoktur.'),
(3439, 30, 30, 'Öyleyse sen yüzünü Allah\'ı birleyen (bir hanif) olarak dine, Allah\'ın o fıtratına çevir; ki insanları bunun üzerine yaratmıştır. Allah\'ın yaratışı için hiçbir değiştirme yoktur. İşte dimdik ayakta duran din (budur). Ancak insanların çoğu bilmezler.'),
(3440, 30, 31, '\'Gönülden katıksız bağlılar\' olarak, O\'na yönelin ve O\'ndan korkup-sakının, dosdoğru namazı kılın ve müşriklerden olmayın.'),
(3441, 30, 32, '(O müşrikler ki,) Kendi dinlerini fırkalara ayırmış ve kendileri de parça parça olmuşlardır; ki her grup kendi elindekiyle övünüp sevinç duymaktadır.'),
(3442, 30, 33, 'İnsanlara bir zarar dokunduğu zaman, \'gönülden katıksız bağlılar\' olarak, Rablerine dua ederler; sonra kendinden onlara bir rahmet taddırınca hemencecik bir grup Rablerine şirk koşarlar.'),
(3443, 30, 34, 'Kendilerine (nimet olarak) verdiklerimize nankörlük etsinler diye. Öyleyse metalanıp-yararlanın, artık yakında bileceksiniz.'),
(3444, 30, 35, 'Yoksa Biz, onlara ispatlı bir delil indirdik de, o mu O\'na ortak koşmalarını söylüyor?'),
(3445, 30, 36, 'Biz insanlara bir rahmet taddırdığımız zaman, onunla sevinirler; kendi ellerinin takdim ettiği dolayısıyla onlara bir kötülük isabet ettiğinde, hemen umutsuzluğa kapılırlar.'),
(3446, 30, 37, 'Görmüyorlar mı ki, Allah, dilediğine rızkı yayıp-genişletir ve kısar da. Şüphesiz bunda, iman eden bir kavim için gerçekten ayetler vardır.'),
(3447, 30, 38, 'Öyleyse yakınlara hakkını ver, yoksula da, yolcuya da. Allah\'ın yüzünü (rızasını) isteyenler için bu daha hayırlıdır ve felaha erenler onlardır.'),
(3448, 30, 39, 'İnsanların mallarından artsın diye, verdiğiniz faiz Allah Katında artmaz. Ama Allah\'ın yüzünü (rızasını) isteyerek verdiğiniz zekat ise, işte (sevablarını ve gelirlerini) kat kat arttıranlar onlardır.'),
(3449, 30, 40, 'Allah; sizi yarattı, sonra size rızık verdi, sonra sizi öldürmekte, daha sonra sizi diriltmektedir. Ortaklarınızdan bunlardan herhangi birini yapacak var mı? O, şirk koştuklarından münezzeh ve Yücedir.'),
(3450, 30, 41, 'İnsanların kendi ellerinin kazandığı dolayısıyla, karada ve denizde fesad ortaya çıktı. Umulur ki, dönerler diye (Allah) onlara yaptıklarının bir kısmını kendilerine taddırmaktadır.'),
(3451, 30, 42, 'De ki: "Yeryüzünde gezip dolaşın, böylece daha öncekilerin nasıl bir sona uğradıklarını görün. Onların çoğu müşrik kimselerdi."'),
(3452, 30, 43, 'Öyleyse sen, Allah\'tan (bir takdir olarak) geri çevrilmesi mümkün olmayan gün gelmeden önce, yüzünü dimdik ayakta duran dine çevir. O gün parça parça bölünecekler.'),
(3453, 30, 44, 'Kim inkar ederse, artık onun inkarı kendi aleyhinedir; kim salih bir amelde bulunursa, artık onlar kendi lehlerine olarak (cennetteki yerlerini) döşeyip hazırlamaktadırlar.'),
(3454, 30, 45, '(Bu, Allah\'ın) Kendi fazlından iman edip salih amellerde bulunanları ödüllendirmesi içindir. Şüphesiz O, kafirleri sevmez.'),
(3455, 30, 46, 'Size Kendi rahmetinden taddırması, emriyle gemileri yürütmesi ve O\'nun fazlından (rızkınızı) aramanız ile umulur ki şükretmeniz için, rüzgarları müjde vericiler olarak göndermesi, O\'nun ayetlerindendir.'),
(3456, 30, 47, 'Andolsun, Biz senden önce kendi kavimlerine elçiler gönderdik de onlara apaçık belgeler getirdiler; böylece Biz de suçlu günahkarlardan intikam aldık. İman edenlere yardım etmek ise, Bizim üzerimizde bir haktır.'),
(3457, 30, 48, 'Allah, rüzgarları gönderir, böylece bir bulut kaldırır da onu nasıl dilerse gökte yayıp-dağıtır ve onu parça parça kılar; nihayet onun arasından yağmurun akıp çıktığını görürsün. Sonunda Kendi kullarından dilediğine verince, hemen sevince kapılıverirler.'),
(3458, 30, 49, 'Oysa onlar, bundan önce (yağmurun) üzerine inmesinden evvel umutlarını kesmişlerdi.'),
(3459, 30, 50, 'Şimdi Allah\'ın rahmetinin eserlerine bak; ölümünden sonra yeryüzünü nasıl diriltmektedir? Şüphesiz O, ölüleri de gerçekten diriltecektir. O, herşeye güç yetirendir.'),
(3460, 30, 51, 'Andolsun, Biz bir rüzgar göndersek de onu(n ekinini) sararmış görseler, mutlaka ardından nankörlük ederler.'),
(3461, 30, 52, 'Şimdi sen, ölülere (söz) duyuramazsın ve arkalarını dönüp giden sağırlara da çağrıyı duyuramazsın.'),
(3462, 30, 53, 'Ve sen kendi sapıklıkları içinde kör olanları da doğruya iletici değilsin. Sen yalnızca, Bizim ayetlerimize iman edenlere duyurabilirsin ki onlar Müslümanlardır.'),
(3463, 30, 54, 'Allah, sizi bir za\'ftan yarattı, sonra (bu) za\'fın ardından bir kuvvet kıldı, sonra bu kuvvetin ardından da bir za\'f ve yaşlılık verdi. Dilediğini yaratır. O, bilendir, güç yetirendir.'),
(3464, 30, 55, 'Kıyamet-saatinin kopacağı gün, suçlu-günahkarlar, tek bir saatin dışında (dünya hayatı) yaşamadıklarına and içerler. İşte onlar böyle çevriliyorlardı.'),
(3465, 30, 56, 'Kendilerine ilim ve iman verilenler ise, dediler ki: "Andolsun, siz Allah\'ın Kitabında (yazılı süre boyunca) diriliş gününe kadar yaşadınız; işte bu dirilme günüdür. Ancak siz bilmiyordunuz."'),
(3466, 30, 57, 'Artık o gün, zulmedenlerin ne mazeretleri bir yarar sağlayacak, ne (Allah\'tan) hoşnutluk dilekleri kabul edilecektir.'),
(3467, 30, 58, 'Andolsun, Biz bu Kur\'an\'da insanlar için her örneği gösterdik. Şüphesiz, sen onlara bir ayetle geldiğin zaman, o inkar edenler, mutlaka: "Siz ancak muptil olanlardan başkası değilsiniz" derler.'),
(3468, 30, 59, 'İşte Allah, bilmeyenlerin kalplerini böyle mühürler.'),
(3469, 30, 60, 'Öyleyse sen sabret; şüphesiz Allah\'ın va\'di haktır; kesin bilgiyle inanmayanlar sakın seni telaşa kaptırıp-hafifliğe (veya gevşekliğe) sürüklemesinler.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Elif, Lam, Mim.'),
(3471, 31, 2, 'Bunlar hikmetli Kitab\'ın ayetleridir;'),
(3472, 31, 3, 'Muhsin olanlara bir hidayet ve bir rahmettir.'),
(3473, 31, 4, 'Onlar, namazı dosdoğru kılarlar, zekatı verirler. Ve onlar kesin bir bilgiyle ahirete inanırlar.'),
(3474, 31, 5, 'İşte onlar, Rab\'lerinden bir hidayet üzerindedirler ve felah bulanlar da onlardır.'),
(3475, 31, 6, 'İnsanlardan öyleleri vardır ki, bilgisizce Allah\'ın yolundan saptırmak ve onu bir eğlence konusu edinmek için sözün \'boş ve amaçsız olanını\' satın alırlar. İşte onlar için aşağılatıcı bir azap vardır.'),
(3476, 31, 7, 'Ona ayetlerimiz okunduğunda, sanki işitmiyormuş ve kulaklarında bir ağırlık varmış gibi, büyüklük taslayarak (müstekbirce) sırtını çevirir. Artık sen ona acı bir azap ile müjde ver.'),
(3477, 31, 8, '(Ancak) Gerçekten iman edip salih amellerde bulunanlar ise; onlar için nimetlerle-donatılmış cennetler vardır.'),
(3478, 31, 9, 'Orada ebedi olarak kalıcıdırlar. Allah\'ın va\'di haktır. O, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(3479, 31, 10, 'O, gökleri dayanak olmaksızın yaratmıştır, bunu görmektesiniz. Arzda da, sizi sarsıntıya uğratır diye sarsılmaz dağlar bıraktı ve orada her canlıdan türetip yayıverdi. Biz gökten su indirdik, böylelikle orada her güzel olan çiftten bir bitki bitirdik.'),
(3480, 31, 11, 'Bu, Allah\'ın yaratmasıdır. Şu halde, O\'nun dışında olanların yarattıklarını Bana gösterin. Hayır, zulmedenler, açıkça bir sapıklık içindedirler.'),
(3481, 31, 12, 'Andolsun, Lukman\'a "Allah\'a şükret" diye hikmet verdik. Kim şükrederse, artık o, kendi lehine şükreder. Kim inkar ederse, artık şüphesiz, (Allah,) Gani (hiç kimseye ve hiçbir şeye muhtaç olmayan)dır, Hamiddir (hamd yalnızca O\'na aittir).'),
(3482, 31, 13, 'Hani Lukman oğluna -öğüt vererek- demişti ki; "Ey oğlum, Allah\'a şirk koşma. Şüphesiz şirk, gerçekten büyük bir zulümdür."'),
(3483, 31, 14, 'Biz insana anne ve babasını (onlara iyilikle davranmayı) tavsiye ettik. Annesi onu, zorluk üstüne zorlukla (karnında) taşımıştır. Onun (sütten) ayrılması, iki yıl içindedir. "Hem Bana, hem anne ve babana şükret, dönüş yalnız Banadır."'),
(3484, 31, 15, 'Bununla birlikte, onların ikisi (annen ve baban) hakkında bir bilgin olmayan şeyi Bana şirk koşman için, sana karşı çaba harcayacak olurlarsa, bu durumda onlara itaat etme ve dünya (hayatın) da onlara iyilikle (ma\'ruf üzere) sahiplen (onlarla geçin) ve Bana \'gönülden-katıksız olarak yönelenin\' yoluna tabi ol. Sonra dönüşünüz yalnızca Banadır, böylece Ben de size yaptıklarınızı haber vereceğim.'),
(3485, 31, 16, '"Ey oğlum, (yaptığın iş) gerçekten bir hardal tanesi ağırlığında olsa da, (bu,) ister bir kaya parçasından ya da göklerde veya yer(in derinliklerinde) de bulunsa bile, Allah onu getirir (açığa çıkarır). Şüphesiz Allah, latif olandır, (herşeyden) haberdardır."'),
(3486, 31, 17, '"Ey oğlum, namazı dosdoğru kıl, ma\'rufu emret, münkerden sakındır ve sana isabet eden (musibetler)e karşı sabret. Çünkü bunlar, azmedilmesi gereken işlerdendir.'),
(3487, 31, 18, '"İnsanlara yanağını çevirip (büyüklenme) ve böbürlenmiş olarak yeryüzünde yürüme. Çünkü Allah, büyüklük taslayıp böbürleneni sevmez."'),
(3488, 31, 19, '"Yürüyüşünde orta bir yol tut, sesinden de (yüksek perdeleri) eksilt. Çünkü, seslerin en çirkin olanı gerçekten eşeklerin sesidir."'),
(3489, 31, 20, 'Görmüyor musunuz ki, şüphesiz Allah, göklerde ve yerde olanları emrinize amade kılmış, açık ve gizli sizin üzerinizdeki nimetlerini genişletip-tamamlamıştır. (Buna rağmen) İnsanlardan öyleleri vardır ki, hiçbir ilme dayanmadan, bir yol gösterici ve aydınlatıcı bir kitap olmadan Allah hakkında mücadele edip durur.'),
(3490, 31, 21, 'Onlara; "Allah\'ın indirdiklerine uyun" denildiğinde, derler ki; "Hayır, biz atalarımızı üzerinde bulduğumuz şeye uyarız." Şayet şeytan, onları çılgınca yanan ateşin azabına çağırmışsa da mı (buna uyacaklar)?'),
(3491, 31, 22, 'Kim ihsanda bulunan (biri) olarak yüzünü (kendini) Allah\'a teslim ederse, artık gerçekten o kopmayan bir kulpa yapışmıştır. Bütün işlerin sonu Allah\'a varır.'),
(3492, 31, 23, 'Kim de inkar ederse, artık onun inkarı seni hüzne kaptırmasın. Onların dönüşü Bizedir, artık Biz de onlara yaptıklarını haber vereceğiz. Şüphesiz Allah, sinelerin özünde saklı olanı bilendir.'),
(3493, 31, 24, 'Biz onları az (bir şey ve zaman) olarak metalandırıp yararlandırırız, sonra onları ağır bir azaba katlandırırız.'),
(3494, 31, 25, 'Andolsun onlara; "Gökleri ve yeri kim yarattı?" diye soracak olsan, tartışmasız; "Allah" diyecekler. De ki; "Hamd Allah\'ındır." Hayır, onların çoğu bilmezler.'),
(3495, 31, 26, 'Göklerde ve yerde olanlar Allah\'ındır. Şüphesiz Allah, Gani (hiç kimseye ve hiçbir şeye muhtaç olmayan)dır, Hamid (hamd da yalnızca O\'na ait)tir.'),
(3496, 31, 27, 'Eğer yeryüzündeki ağaçların tümü kalem ve deniz de -onun ardından yedi deniz daha eklenerek- (mürekkep) olsa, yine de Allah\'ın kelimeleri (yazmakla) tükenmez. Şüphesiz Allah, üstün ve güçlüdür, hüküm ve hikmet sahibidir.'),
(3497, 31, 28, 'Sizin yaratılmanız ve diriltilmeniz yalnızca tek bir kişi(yi yaratıp sonra diriltmek) gibidir. Şüphesiz Allah, işitendir, görendir.'),
(3498, 31, 29, 'Görmüyor musun ki, gerçekten Allah, geceyi gündüze bağlayıp-katar, gündüzü de geceye bağlayıp-katar. Güneş ile ayı emre amade kılmıştır. Her biri, adı konulmuş bir süreye kadar akıp gider. Allah yaptıklarınızdan haberdardır.'),
(3499, 31, 30, 'İşte-böyle; şüphesiz Allah, O, Hak olandır ve şüphesiz O\'nun dışında taptıkları (tanrılar) ise, batıldır. Şüphesiz Allah, Yücedir, büyüktür.'),
(3500, 31, 31, 'Görmüyor musun ki, size ayetlerinden (bazılarını) göstermesi için, gemiler Allah\'ın nimetiyle denizde akıp gitmektedir! Hiç şüphesiz bunda, çok sabreden, çok şükreden için gerçekten ayetler vardır.'),
(3501, 31, 32, 'Onları kara gölgeler gibi dalgalar sarıverdiği zaman, dini yalnızca O\'na \'halis kılan gönülden bağlılar\' olarak Allah\'a yalvarıp yakarırlar (dua ederler). Böylece onları karaya çıkarıp-kurtarınca, artık onlardan bir kısmı orta yolu tutuyor. Bizim ayetlerimizi gaddar, nankör olandan başkası inkar etmez.'),
(3502, 31, 33, 'Ey insanlar, Rabbinizden korkup-sakının ve öyle bir günün azabından çekinip-korkun ki, (o gün hiç) bir baba, çocuğu için bir karşılık veremez ve (hiç)bir çocuk da babası için bir şeyi verebilecek (durumda) değildir. Şüphesiz Allah\'ın va\'di haktır. Artık dünya hayatı sizi aldatmaya sürüklemesin ve aldatıcı(lar) da sizi Allah ile aldatmasın.'),
(3503, 31, 34, 'Kıyamet saatinin bilgisi, şüphesiz Allah\'ın Katındadır. Yağmuru yağdırır; rahimlerde olanı bilir. Hiç kimse, yarın ne kazanacağını bilmez. Hiç kimse de, hangi yerde öleceğini bilmez. Hiç şüphesiz Allah bilendir, haberdardır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Elif-Lam Mim.'),
(3505, 32, 2, 'Kendisinde şüphe olmayan bu Kitab\'ın indirilişi alemlerin Rabbi tarafındandır.'),
(3506, 32, 3, 'Yoksa onlar: "Bunu uydurdu" mu diyorlar? Hayır; o, Rabbinden olan bir haktır; senden önce kendilerine bir uyarıcı gelmemiş bir kavmi uyarman için (onu sana indirdik). Umulur ki hidayet bulurlar.'),
(3507, 32, 4, 'Allah; gökleri, yeri ve ikisi arasında olanları altı günde yarattı, sonra arşa istiva etti. Sizin O\'nun dışında bir yardımcınız ve şefaatçiniz yoktur. Yine de öğüt alıp-düşünmeyecek misiniz?'),
(3508, 32, 5, 'Gökten yere her işi O evirip düzene koyar. Sonra (işler,) sizin saymakta olduğunuz bin yıl süreli bir günde yine O\'na yükselir.'),
(3509, 32, 6, 'İşte gaybı da, müşahede edilebileni de bilen, üstün ve güçlü olan, esirgeyen O\'dur.'),
(3510, 32, 7, 'Ki O, yarattığı herşeyi en güzel yapan ve insanı yaratmaya bir çamurdan başlayandır.'),
(3511, 32, 8, 'Sonra onun soyunu bir özden (sülale\'den), basbayağı bir sudan yapmıştır.'),
(3512, 32, 9, 'Sonra onu \'düzeltip bir biçime soktu\' ve ona Ruhundan üfledi. Sizin için de kulak, gözler ve gönüller var etti. Ne az şükrediyorsunuz?'),
(3513, 32, 10, 'Dediler ki: "Biz yer (toprağın için) de yok olup gittikten sonra, gerçekten biz mi yeniden yaratılmış olacağız?" Hayır, onlar Rablerine kavuşmayı inkar edenlerdir.'),
(3514, 32, 11, 'De ki: "Size vekil kılınan ölüm meleği, hayatınıza son verecek, sonra Rabbinize döndürülmüş olacaksınız."'),
(3515, 32, 12, 'Suçlu-günahkarları, Rableri huzurunda başları öne eğilmiş olarak: "Rabbimiz, gördük ve işittik; şimdi bizi (bir kere daha dünyaya) geri çevir, salih bir amelde bulunalım, artık biz gerçekten kesin bilgiyle inananlarız" (diye yalvaracakları zamanı) bir görsen.'),
(3516, 32, 13, 'Eğer Biz dilemiş olsaydık, her bir nefse kendi hidayetini verirdik. Fakat Benden çıkan şu söz gerçekleşecektir: "Andolsun, cehennemi cinlerden ve insanlardan (İnkar edenlerle) tamamıyla dolduracağım."'),
(3517, 32, 14, 'Öyleyse bu (azap) gününüzle karşılaşmayı unutmanıza karşılık azabı tadın. Biz de sizi gerçekten unuttuk; yaptıklarınıza karşılık ebedi azabı tadın.'),
(3518, 32, 15, 'Bizim ayetlerimize, ancak kendilerine hatırlatıldığı zaman, hemen secdeye kapananlar, Rablerini hamd ile tesbih edenler ve büyüklük taslamayan (müstekbir olmayan)lar iman eder.'),
(3519, 32, 16, 'Onların yanları (gece namazına kalkmak için) yataklarından uzaklaşır. Rablerine korku ve umutla dua ederler ve kendilerine rızık olarak verdiklerimizden infak ederler.'),
(3520, 32, 17, 'Artık hiçbir nefis, yaptıklarına karşılık olmak üzere kendileri için gözler aydınlığı olarak nelerin (sayısız nimetlerin) saklandığını bilmez.'),
(3521, 32, 18, 'Öyleyse, iman eden kimse, fasık olan gibi olur mu? Bunlar eşit olmazlar.'),
(3522, 32, 19, 'İman eden ve salih amellerde bulunanlar ise, artık onlar için, yaptıklarınıza karşılık olmak üzere, bir ağırlanma konağı olarak barınma cennetleri vardır.'),
(3523, 32, 20, 'Fasık olanlar içinse, artık onların da barınma yeri ateştir. Oradan her çıkmak istediklerinde, geri çevrilirler ve onlara: "Kendisini yalanladığınız ateş azabını tadın" denir.'),
(3524, 32, 21, 'Andolsun, Biz onlara belki (inkarcılıktan) dönerler diye o büyük (uhrevi) azapdan önce, yakın (dünyevi) azaptan da taddıracağız.'),
(3525, 32, 22, 'Kendisine Rabbinin ayetleri hatırlatıldıktan sonra, yüz çevirenden daha zalim kimdir? Gerçekten Biz, suçlu-günahkarlardan intikam alıcılarız.'),
(3526, 32, 23, 'Andolsun, Biz Musa’ya kitabı vermiştik; böylece sen ona kavuşmaktan kuşku içinde olma. Biz onu İsrailoğulları\'na bir yol gösterici kılmıştık.'),
(3527, 32, 24, 'Ve onların içinden, sabrettikleri zaman emrimizle doğru yola iletip-yönelten önderler kıldık; onlar Bizim ayetlerimize kesin bilgiyle inanıyorlardı.'),
(3528, 32, 25, 'Şüphesiz, senin Rabbin, ihtilafa düştükleri şeyler konusunda kıyamet günü aralarında \'hükmünü verip ayıracaktır\'.'),
(3529, 32, 26, 'Yurtlarında gezip dolaştıkları nice nesilleri kendilerinden evvel yıkıma uğratmış olmamız, hala onları doğru yola iletip yöneltmedi mi? Elbette, bunda ayetler vardır; yine de işitmiyorlar mı?'),
(3530, 32, 27, 'Görmüyorlar mı; Biz, suyu çorak toprağa sürüyoruz da onunla ekin bitiriyoruz; ondan hayvanları, kendileri yemektedir. Yine de görmüyorlar mı?'),
(3531, 32, 28, 'Derler ki: "Eğer doğru söylüyor iseniz, şu fetih ne zamanmış?"'),
(3532, 32, 29, 'De ki: "Fetih günü, inkar edenlere (o gün) inanmaları bir yarar sağlamaz ve onlara bir süre tanınmaz."'),
(3533, 32, 30, 'Öyleyse, sen onlardan yüz çevir ve bekleyedur; gerçekten onlar da beklemektedirler.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Ey Peygamber, Allah\'tan sakın, kafirlere ve münafıklara itaat etme. Şüphesiz Allah, bilendir, hüküm ve hikmet sahibidir.'),
(3535, 33, 2, 'Sana Rabbinden vahyedilene uy. Şüphesiz Allah, yaptıklarınızdan haberdardır.'),
(3536, 33, 3, 'Allah\'a tevekkül et; vekil olarak Allah yeter.'),
(3537, 33, 4, 'Allah, bir adamın kendi (göğüs) boşluğu içinde iki kalp kılmadı ve kendilerini annelerinize benzeterek yemin konusu yaptığınız (zıharda bulunduğunuz) eşlerinizi sizin anneleriniz yapmadı, evlatlıklarınızı da sizin (öz) çocuklarınız saymadı. Bu, sizin (yalnızca) ağzınızla söylemenizdir. Allah ise, hakkı söyler ve (doğru olan) yola yöneltip-iletir.'),
(3538, 33, 5, 'Onları (evlat edindiklerinizi) babalarına nisbet ederek çağırın; bu, Allah Katında daha adildir. Eğer babalarını bilmiyorsanız artık onlar, dinde sizin kardeşleriniz ve dostlarınızdır. Hata olarak yaptıklarınızda ise, sizin için bir sakınca (bir vebal) yoktur. Ancak kalplerinizin kasıt gözeterek (taammüden) yaptıklarınızda vardır. Allah, bağışlayandır, esirgeyendir.'),
(3539, 33, 6, 'Peygamber, mü\'minler için kendi nefislerinden daha evladır ve onun zevceleri de onların anneleridir. Rahim sahipleri (akrabalar) de, Allah\'ın Kitab\'ında birbirlerine öteki mü\'minlerden ve muhacirlerden daha yakındır. Ancak dostlarınıza maruf üzere yapacaklarınız başka; bunlar Kitap\'ta yazılmış bulunmaktadır.'),
(3540, 33, 7, 'Hani Biz peygamberlerden kesin sözlerini almıştık; senden, Nuh\'tan, İbrahim\'den, Musa\'dan ve Meryem oğlu İsa\'dan. Biz onlardan sapasağlam bir söz almıştık.'),
(3541, 33, 8, 'Doğru olanlara doğruluk (ve bağlılık)larını (Allah\'ın) sorması için. Kafirlere ise acı bir azap hazırlamıştır.'),
(3542, 33, 9, 'Ey iman edenler, Allah\'ın üzerinizdeki nimetini hatırlayın. Hani size ordular gelmişti; böylece Biz de onların üzerine, bir rüzgar ve sizin görmediğiniz ordular göndermiştik. Allah, yaptıklarınızı görendir.'),
(3543, 33, 10, 'Hani onlar, size hem üstünüzden, hem alt tarafınızdan gelmişlerdi; gözler kaymış, yürekler hançereye gelip dayanmıştı ve siz Allah hakkında (birtakım) zanlarda bulunuyordunuz.'),
(3544, 33, 11, 'İşte orada, iman edenler, sınanmış ve şiddetli bir sarsıntıyla sarsıntıya uğratılmışlardı.'),
(3545, 33, 12, 'Hani, münafık olanlar ve kalplerinde hastalık bulunanlar: "Allah ve Resulü, bize boş bir aldanıştan başka bir şey vadetmedi" diyorlardı.'),
(3546, 33, 13, 'Onlardan bir grup da hani şöyle demişti: "Ey Yesrib (Medine) halkı, artık sizin için (burada) kalacak yer yok, şu halde dönün." Onlardan bir topluluk da: "Gerçekten evlerimiz açıktır" diye Peygamberden izin istiyordu; oysa onlar(ın evleri) açık değildi. Onlar yalnızca kaçmak istiyorlardı.'),
(3547, 33, 14, 'Eğer onlara (şehrin her) yanından girilseydi sonra da kendilerinden fitne (karışıklık çıkarmaları) istenmiş olsaydı, hiç şüphesiz buna yanaşır ve bunda pek az (zaman) dışında (kararsız) kalmazlardı.'),
(3548, 33, 15, 'Oysa andolsun, daha önce \'arkalarını dönüp kaçmayacaklarına\' dair Allah\'a söz vermişlerdi; Allah\'a verilen söz (ahid) ise, (ağır bir) sorumluluktur.'),
(3549, 33, 16, 'De ki: "Eğer ölümden veya öldürülmekten kaçıyorsanız, kaçış size kesin olarak bir yarar sağlamaz; böyle olsa bile, pek az (bir zaman) dışında metalanıp-yararlandırılmazsınız."'),
(3550, 33, 17, 'De ki: "Size bir kötülük isteyecek olsa sizi Allah\'tan koruyacak, veya size bir rahmet isteyecek olsa (buna engel olacak) kimdir?" Onlar, kendileri için Allah\'ın dışında ne bir veli, ne bir yardımcı bulamazlar.'),
(3551, 33, 18, 'Gerçekten Allah, içinizden alıkoyanları ve kardeşlerine: "Bize gelin" diyenleri bilir. Bunlar, pek azı dışında zorlu-savaşlara gelmezler.'),
(3552, 33, 19, '(Geldiklerinde de) Size karşı \'cimri ve bencildirler.\' Şayet korku gelecek olsa, ölümden dolayı üstüne baygınlık çökmüş kimseler gibi gözleri dönerek sana bakmakta olduklarını görürsün. Korku gidince, hayra karşı oldukça düşkünlük göstererek sizi keskin dilleriyle (eleştirip inciterek) karşılarlar. İşte onlar iman etmemişlerdir; böylece Allah onların yaptıklarını boşa çıkarmıştır. Bu Allah\'a göre pek kolaydır.'),
(3553, 33, 20, 'Onlar (münafıklar, düşman) birliklerinin gitmediklerini sanıyorlardı. Eğer (askeri) birlikler gelecek olsa, çölde bedevi-Araplar arasında olup sizin haberlerinizi (ordan) sormayı cidden arzu ediyorlardı. Fakat içinizde olsalardı ancak pek az savaşırlardı.'),
(3554, 33, 21, 'Andolsun, sizin için, Allah\'ı ve ahiret gününü umanlar ve Allah\'ı çokça zikredenler için Allah\'ın Resûlü’nde güzel bir örnek vardır.'),
(3555, 33, 22, 'Mü\'minler (düşman) birliklerini gördükleri zaman ise (korkuya kapılmadan) dediler ki: "Bu, Allah\'ın ve Resûlü’nün bize vadettiği şeydir; Allah ve Resûlü doğru söylemiştir." Ve (bu,) yalnızca onların imanlarını ve teslimiyetlerini arttırdı.'),
(3556, 33, 23, 'Mü\'minlerden öyle erkek-adamlar vardır ki- Allah ile yaptıkları ahide sadakat gösterdiler; böylece onlardan kimi adağını gerçekleştirdi, kimi beklemektedir. Onlar hiçbir değiştirme ile (sözlerini) değiştirmediler.'),
(3557, 33, 24, 'Çünkü Allah, (sözüne bağlı kalıp doğru olan) sadıkları sadakatlerinden dolayı mükafaatlandıracak, münafıkları da dilerse azaplandıracak veya tevbe (nasib edip tevbe)lerini kabul edecektir. Şüphesiz Allah, çok bağışlayandır, çok esirgeyendir.'),
(3558, 33, 25, 'Allah, inkar edenleri kin ve öfkeleriyle geri çevirdi, onlar hiçbir hayra varamadılar. Savaşta Allah (yardımcı ve zafer nasib edici olarak) mü\'minlere yetti. Allah çok güçlüdür, üstün ve galib olandır.'),
(3559, 33, 26, 'Kitap Ehlinden onlara arka çıkanları da kalelerinden indirdi ve onların kalplerine korku düşürdü. Siz (onlardan) bir kısmını öldürüyordunuz, bir kısmını ise esir alıyordunuz.'),
(3560, 33, 27, 'Ve sizi onların topraklarına, yurtlarına, mallarına ve daha ayak basmadığınız bir yere mirasçı kıldı. Allah, herşeye güç yetirendir.'),
(3561, 33, 28, 'Ey peygamber, eşlerine söyle: "Eğer siz dünya hayatını ve onun süslü-çekiciliğini istiyorsanız, gelin sizi yararlandırayım ve güzel bir salma tarzıyla sizi salıvereyim."'),
(3562, 33, 29, '"Eğer siz Allah\'ı, Resûlü’nü ve ahiret yurdunu istiyorsanız artık hiç şüphesiz Allah, içinizden güzellikte bulunanlar için büyük bir ecir hazırlamıştır."'),
(3563, 33, 30, 'Ey peygamberin kadınları, sizden kim açık bir çirkin-utanmazlıkta bulunursa, onun azabı iki kat olarak artırılır. Bu da Allah\'a göre pek kolaydır.'),
(3564, 33, 31, 'Ama sizden kim Allah\'a ve Resûlü’ne gönülden -itaat eder ve salih bir amelde bulunursa, ona ecrini iki kat veririz. Ve Biz ona üstün bir rızık da hazırlamışızdır.'),
(3565, 33, 32, 'Ey peygamberin kadınları, siz kadınlardan herhangi biri (gibi) değilsiniz; eğer sakınıyorsanız, artık sözü çekicilikle söylemeyin ki, sonra kalbinde hastalık bulunan kimse tamah eder. Sözü maruf bir tarzda söyleyin.'),
(3566, 33, 33, 'Evlerinizde vakarla-oturun (evlerinizi karargah edinin), ilk cahiliye (kadınları)nın süslerini açığa vurması gibi, siz de süslerinizi açığa vurmayın; namazı dosdoğru kılın, zekatı verin, Allah\'a ve elçisine itaat edin. Ey Ehl-i Beyt, gerçekten Allah, sizden kiri (günah ve çirkinliği) gidermek ve sizi tertemiz kılmak ister.'),
(3567, 33, 34, 'Evlerinizde okunmakta olan Allah\'ın ayetlerini ve hikmeti hatırlayın. Şüphesiz Allah, latiftir, haberdar olandır.'),
(3568, 33, 35, 'Şüphesiz, Müslüman erkekler ve Müslüman kadınlar, mü\'min erkekler ve mü\'min kadınlar, gönülden (Allah\'a) itaat eden erkekler ve gönülden (Allah\'a) itaat eden kadınlar, sadık olan erkekler ve sadık olan kadınlar, sabreden erkekler ve sabreden kadınlar, saygıyla (Allah\'tan) korkan erkekler ve saygıyla (Allah\'tan) korkan kadınlar, sadaka veren erkekler ve sadaka veren kadınlar, oruç tutan erkekler ve oruç tutan kadınlar, ırzlarını koruyan erkekler ve (ırzlarını) koruyan kadınlar, Allah\'ı çokça zikreden erkekler ve (Allah\'ı çokça) zikreden kadınlar; (işte) bunlar için Allah bir bağışlanma ve büyük bir ecir hazırlamıştır.'),
(3569, 33, 36, 'Allah ve Resûlü, bir işe hükmettiği zaman, mü\'min bir erkek ve mü\'min bir kadın için o işte kendi isteklerine göre seçme hakkı yoktur. Kim Allah\'a ve Resûlü’ne isyan ederse, artık gerçekten o, apaçık bir sapıklıkla sapmıştır.'),
(3570, 33, 37, 'Hani sen, Allah\'ın kendisine nimet verdiği ve senin de kendisine nimet verdiğin kişiye: "Eşini yanında tut ve Allah\'tan sakın" diyordun; insanlardan çekinerek Allah\'ın açığa vuracağı şeyi kendi nefsinde saklı tutuyordun; oysa Allah, Kendisi\'nden çekinmene çok daha layıktı. Artık Zeyd, ondan ilişkisini kesince, Biz onu seninle evlendirdik; ki böylelikle evlatlıklarının kendilerinden ilişkilerini kestikleri (kadınları boşadıkları) zaman, onlarla evlenme konusunda mü\'minler üzerine bir güçlük olmasın. Allah\'ın emri yerine getirilmiştir.'),
(3571, 33, 38, 'Allah\'ın kendisine farz kıldığı bir şey(i yerine getirme)de peygamber üzerine hiçbir güçlük yoktur. (Bu,) Daha önce gelip geçen (ümmet)lerde Allah\'ın bir sünnetidir. Allah\'ın emri, takdir edilmiş bir kaderdir.'),
(3572, 33, 39, 'Ki onlar (o peygamberler) Allah\'ın risaletini tebliğ edenler, O\'ndan içleri titreyerek-korkanlar ve Allah\'ın dışında hiç kimseden korkmayanlardır. Hesap görücü olarak Allah yeter.'),
(3573, 33, 40, 'Muhammed, sizin erkeklerinizden hiçbirinin babası değildir; ancak O, Allah\'ın Resûlü ve peygamberlerin sonuncusudur. Allah, herşeyi bilendir.'),
(3574, 33, 41, 'Ey iman edenler, Allah\'ı çokça zikredin.'),
(3575, 33, 42, 'Ve O\'nu sabah ve akşam tesbih edin.'),
(3576, 33, 43, 'O\'dur ki, sizi karanlıklardan nura çıkarmak için size rahmet etmekte; melekleri de (size dua etmektedir). O, mü\'minleri çok esirgeyicidir.'),
(3577, 33, 44, 'O\'na kavuşacakları gün, onların dirlik temennileri: "Selam"dır. Ve O, onlara üstün bir ecir hazırlamıştır.'),
(3578, 33, 45, 'Ey Peygamber, gerçekten Biz seni bir şahid, bir müjde verici ve bir uyarıcı olarak gönderdik.'),
(3579, 33, 46, 'Ve Kendi izniyle Allah\'a çağıran ve nur saçan bir çerağ olarak (gönderdik).'),
(3580, 33, 47, 'Mü\'minlere müjde ver; gerçekten onlar için Allah\'tan büyük bir fazl vardır.'),
(3581, 33, 48, 'Kafirlere ve münafıklara itaat etme, eziyetlerine aldırma ve Allah\'a tevekkül et. Vekil olarak Allah yeter.'),
(3582, 33, 49, 'Ey iman edenler, mü’min kadınları nikahlayıp sonra onlara dokunmadan boşarsanız, bu durumda sizin için üzerlerine sayacağınız bir iddet yoktur. Artık (hemen) onları yararlandırın (onlara yetecek bir miktar verin) ve güzel bir salma tarzıyla onları salıverin.'),
(3583, 33, 50, 'Ey Peygamber, gerçekten Biz sana ücretlerini (mehirlerini) verdiğin eşlerini ve Allah\'ın sana ganimet olarak verdikleri (savaş esirleri)nden sağ elinin malik olduğu (cariyeler) ile seninle birlikte hicret eden amcanın kızlarını, halanın kızlarını, dayının kızlarını ve teyzenin kızlarını helal kıldık; bir de, kendisini peygambere hibe eden ve peygamberin kendisini almak istediği mü\'min bir kadını da, -mü\'minler için olmaksızın yalnızca sana has olmak üzere- (senin için helal kıldık). Biz, kendi eşleri ve sağ ellerinin malik olduğu (cariyeleri) konusunda onlar (mü\'minler) üzerine neyi farz kıldığımızı bildik (size bildirdik). Böylelikle senin için hiçbir güçlük olmasın. Allah çok bağışlayandır, çok esirgeyendir.'),
(3584, 33, 51, 'Onlardan dilediğini geri bırakır, dilediğini de yanına alıp-barındırabilirsin; ayrıldıklarından, istek duyduklarına (dönmende) senin için bir sakınca yoktur. Onların gözlerinin aydınlanıp hüzne kapılmamalarına ve kendilerine verdiğinle hepsinin hoşnut olmalarına en yakın (en uygun) olan budur. Allah, kalplerinizde olanı bilir. Allah bilendir, halimdir.'),
(3585, 33, 52, 'Bundan sonra (başka) kadınlar ve bunları başka eşlerle değiştirmek -güzellikleri senin hoşuna gitse bile- sana helal olmaz; ancak sağ elinin malik olduğu (cariyeler) başka. Allah herşeyi gözetleyip denetleyendir.'),
(3586, 33, 53, 'Ey iman edenler (rastgele) Peygamberin evlerine girmeyin, (Bir başka iş için girmişseniz ille de) yemek vaktini beklemeyin. (Ama yemeğe) çağrıldığınız zaman girin, yemeği yiyince dağılın ve (uzun) söze dalmayın. Gerçekten bu, peygambere eziyet vermekte ve o da sizden utanmaktadır; oysa Allah, hak (kı açıklamak)tan utanmaz. Onlardan (peygamberin eşlerinden) bir şey isteyeceğiniz zaman, perde arkasından isteyin. Bu, sizin kalpleriniz için de, onların kalpleri için de daha temizdir. Allah\'ın Resûlü’ne eziyet vermeniz ve ondan sonra eşlerini nikahlamanız size ebedi olarak (helal) olmaz. Çünkü böyle yapmanız, Allah Katında çok büyük (bir günah)tır.'),
(3587, 33, 54, 'Bir şeyi açığa vursanız da, saklı tutsanız da; şüphesiz Allah, herşeyi bilici olandır.'),
(3588, 33, 55, 'Onlar için babaları, oğulları, kardeşleri, erkek kardeşlerinin oğulları, kız kardeşlerinin oğulları, kadınları ve sağ ellerinin malik olduğu (cariyeleri) hakkında bir sakınca yoktur. (Ey Müslüman kadınlar) Allah\'tan sakının. Şüphesiz Allah, herşeye şahid olandır.'),
(3589, 33, 56, 'Şüphesiz, Allah ve melekleri Peygambere salat ederler. Ey iman edenler, siz de O\'na salat edin ve tam bir teslimiyetle O\'na selam verin.'),
(3590, 33, 57, 'Gerçek şu ki, Allah\'a ve elçisine eziyet edenler; Allah, onlara dünyada ve ahirette lanet etmiş ve onlar için aşağılatıcı bir azap hazırlanmıştır.'),
(3591, 33, 58, 'Mü\'min erkeklere ve mü\'min kadınlara irtikab etmedikleri (bir suç) sebebiyle eziyet edenler ise, gerçekten bir iftira ve açık bir günah yüklenmişlerdir.'),
(3592, 33, 59, 'Ey Peygamber, eşlerine, kızlarına ve mü\'minlerin kadınlarına dış elbiselerinden (cilbablarından) üstlerine giymelerini söyle; onların (özgür ve iffetli) tanınması ve eziyet görmemeleri için en uygun olan budur. Allah, çok bağışlayandır, çok esirgeyendir.'),
(3593, 33, 60, 'Andolsun, eğer münafıklar, kalplerinde hastalık bulunanlar ve şehirde kışkırtıcılık yapan (yalan haber yayan)lar (bu tutumlarına) bir son vermeyecek olurlarsa, gerçekten seni onlara saldırtırız, sonra orada seninle pek az (bir süre) komşu kalabilirler.'),
(3594, 33, 61, 'Lanete uğratılmışlar olarak; nerede ele geçirilseler yakalanırlar ve öldürüldükçe (sürekli) öldürülürler.'),
(3595, 33, 62, '(Bu,) Daha önceden gelip-geçenler hakkında (uygulanan) Allah\'ın sünnetidir. Allah\'ın sünnetinde kesin olarak bir değişiklik bulamazsın.'),
(3596, 33, 63, 'İnsanlar, sana kıyamet-saatini sorarlar; de ki: "Onun bilgisi yalnızca Allah\'ın Katındadır." Ne bilirsin; belki kıyamet-saati pek yakın da olabilir.'),
(3597, 33, 64, 'Gerçekten Allah, kafirleri lanetlemiş ve onlar için \'çılgın bir ateş\' hazırlamıştır.'),
(3598, 33, 65, 'Orda ebedi olarak kalıcıdırlar. Onlar ne bir veli, ne bir yardımcı bulamayacaklardır.'),
(3599, 33, 66, 'Yüzlerinin ateşte evrilip çevrileceği gün, derler ki: "Eyvahlar bize, keşke Allah\'a itaat etseydik ve Resûl’e itaat etseydik."'),
(3600, 33, 67, 'Ve dediler ki: "Rabbimiz, gerçekten biz, efendilerimize ve büyüklerimize itaat ettik, böylece onlar bizi yoldan saptırmış oldular."'),
(3601, 33, 68, '"Rabbimiz, onlara azaptan iki katını ver ve büyük bir lanet ile lanet et."'),
(3602, 33, 69, 'Ey iman edenler, Musa\'ya eziyet edenler gibi olmayın; ki sonunda Allah onu, demekte olduklarından temize çıkardı. O, Allah Katında vecihti.'),
(3603, 33, 70, 'Ey iman edenler, Allah\'tan sakının ve sözü doğru söyleyin.'),
(3604, 33, 71, 'Ki O ( Allah), amellerinizi ıslah etsin ve günahlarınızı bağışlasın. Kim Allah\'a ve elçisine itaat ederse, artık o en büyük kurtuluşla kurtulmuştur.'),
(3605, 33, 72, 'Gerçek şu ki, Biz emanetleri göklere, yere ve dağlara sunduk da onlar bunu yüklenmekten kaçındılar ve ondan korkuya kapıldılar; onu insan yüklendi. Çünkü o, çok zalim, çok cahildir.'),
(3606, 33, 73, 'Şundan ki: Allah, münafık erkekleri ve münafık kadınları, müşrik erkekleri ve müşrik kadınları azaplandıracak; mü\'min erkeklerin ve mü\'min kadınların tevbesini kabul edecektir. Allah çok bağışlayandır, çok esirgeyendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Hamd, göklerde ve yerde olanların tümü Kendisi\'ne ait olan Allah\'ındır; ahirette de hamd O\'nundur. O, hüküm ve hikmet sahibidir, haber alandır.'),
(3608, 34, 2, 'Yerin içine gireni, ondan çıkanı; gökten ineni ve oraya çıkanı bilir. O, esirgeyendir, bağışlayandır.'),
(3609, 34, 3, 'İnkar edenler, dediler ki: "Kıyamet-saati bize gelmez." De ki: "Hayır, gaybı bilen Rabbime andolsun, o muhakkak size gelecektir. Göklerde ve yerde zerre ağırlığınca hiçbir şey O\'ndan uzak (saklı) kalmaz. Bundan daha küçük olanı da, daha büyük olanı da, istisnasız, mutlaka apaçık bir kitapta (yazılı)dır."'),
(3610, 34, 4, '(Çünkü O) İman edip salih amellerde bulunanları ödüllendirecek. İşte mağfiret ve üstün rızık onlarındır.'),
(3611, 34, 5, '(Sözde) Aciz bırakmak için ayetlerimiz hakkında çaba harcamış olanlar, işte onlar; onlar için de (en) iğrenç olanından acı bir azap vardır.'),
(3612, 34, 6, 'Kendilerine ilim verilenler ise, Rabbinden sana indirilenin hakkın ta kendisi olduğunu ve üstün, güçlü, övülmeye layık olan (Allah)ın yoluna yöneltip- ilettiğini görüyorlar.'),
(3613, 34, 7, 'İnkar edenler dediler ki: "Siz darmadağın olup dağıldığınızda, gerçekten sizin yeni bir yaratılışta bulunacağınızı size haber veren bir adamı gösterelim mi size?"'),
(3614, 34, 8, '"Allah\'a karşı yalan mı düzüp uyduruyor, yoksa kendisinde bir delilik mi var?" Hayır, ahirete inanmayanlar, azapta ve uzak bir sapıklık içindedirler.'),
(3615, 34, 9, 'Onlar, gökten ve yerden önlerinde ve arkalarında olanı görmüyorlar mı? Eğer Biz dilersek, onları yerin-dibine geçirir ya da gökten üzerlerine parçalar düşürürüz. Hiç şüphesiz, bunda \'gönülden (Allah\'a) yönelen\' her kul için bir ayet vardır.'),
(3616, 34, 10, 'Andolsun, Biz Davud\'a tarafımızdan bir fazl (üstünlük) verdik. "Ey dağlar, onunla birlikte (Beni tesbih edip) yankıyla ses verin" (dedik) ve kuşlara da (aynısını emrettik). Ve ona demiri yumuşattık.'),
(3617, 34, 11, '"Geniş zırhlar yap, (onları) düzenli bir biçime sok ve hepiniz salih ameller yapın. Gerçekten ben, sizin yaptıklarınızı görenim" (diye vahyettik).'),
(3618, 34, 12, 'Süleyman için de, sabah gidişi bir ay, akşam dönüşü bir ay (mesafe) olan rüzgara (boyun eğdirdik); erimiş bakır madenini ona sel gibi akıttık. Onun eli altında Rabbinin izniyle iş gören bir kısım cinler vardı. Onlardan kim Bizim emrimizden çıkıp-sapacak olsa, ona çılgın ateşin azabından taddırırdık.'),
(3619, 34, 13, 'Ona dilediği şekilde kaleler, heykeller, havuz büyüklüğünde çanaklar ve yerinden sökülmeyen kazanlar yaparlardı. "Ey Davud ailesi, şükrederek çalışın." Kullarımdan şükredenler azdır.'),
(3620, 34, 14, 'Böylece onun (Süleymanın) ölümüne karar verdiğimiz zaman, ölümünü, onlara, asasını yemekte olan bir ağaç kurdundan başkası haber vermedi. Artık o, yere yıkılıp-düşünce, açıkça ortaya çıktı ki, şayet cinler gaybı bilmiş olsalardı böylesine aşağılanıcı bir azap içinde kalıp-yaşamazlardı.'),
(3621, 34, 15, 'Andolsun, Sebe\' (halkı)nın oturduğu yerlerde de bir ayet vardır. (Evleri) Sağdan ve soldan iki bahçeliydi. (Onlara demiştik ki:) "Rabbinizin rızkından yiyin ve O\'na şükredin. Güzel bir şehir ve bağışlayan bir Rabb(iniz var)."'),
(3622, 34, 16, 'Ancak onlar yüz çevirdiler, böylece Biz de onlara Arim selini gönderdik. Ve onların iki bahçesini, buruk yemişli, acı ılgınlı ve içinde az bir şey de sedir ağacı olan iki bahçeye dönüştürdük.'),
(3623, 34, 17, 'Böylelikle nankörlük etmeleri dolayısıyla onları cezalandırdık. Biz (nimete) nankörlük edenden başkasını cezalandırır mıyız?'),
(3624, 34, 18, 'Kendileriyle, içlerinde bereketler kıldığımız memleketler arasında (biri diğerinden) görünebilen şehirler var ettik ve orada yürüme (imkanlarını) takdir ettik: "Oralarda geceleri ve gündüzleri güvenlik içinde gezip dolaşın" (dedik).'),
(3625, 34, 19, 'Onlar ise: "Rabbimiz, seferlerimizin arasını aç (şehirlerimiz birbirine çok yakındır) dediler ve kendi nefislerine zulmetmiş oldular. Böylece Biz de onları efsaneler(e konu olan bir halk) kıldık ve onları darmadağın edip dağıttık. Şüphesiz bunda, çok sabreden ve çok şükreden herkes için gerçekten ayetler vardır.'),
(3626, 34, 20, 'Andolsun, İblis, kendileri hakkında zannını doğrulamış oldu, böylelikle iman eden bir grup dışında, ona uymuş oldular.'),
(3627, 34, 21, 'Oysa onun, kendilerine karşı hiçbir zorlayıcı-gücü yoktu; ancak Biz ahirete iman edeni, ondan kuşku içinde olandan ayırdetmek için (ona bu imkanı verdik). Senin Rabbin, herşeyin üzerinde gözetici-koruyucudur.'),
(3628, 34, 22, 'De ki: "Allah\'ın dışında (tanrı diye) öne sürdüklerinizi çağırın. Onların göklerde ve yerde bir zerre ağırlığınca bile (hiçbir şeye) güçleri yetmez; onların bu ikisinde hiçbir ortaklığı olmadığı gibi, O\'nun bunlardan hiçbir destekçi olanı da yoktur.'),
(3629, 34, 23, 'O\'nun Katında izin verdiğinin dışında (hiç kimsenin) şefaati yarar sağlamaz. En sonunda kalplerinden korku giderilince (birbirlerine:) "Rabbiniz ne buyurdu?" derler, "Hak olanı" derler. O, çok Yücedir, çok büyüktür.'),
(3630, 34, 24, 'De ki: "Sizi göklerden ve yerden rızıklandıran kim?" De ki: " Allah, gerçekten ya biz, ya da siz herhalde bir hidayet üzerindeyiz veya apaçık bir sapıklıkta."'),
(3631, 34, 25, 'De ki: "Siz, bizim işlemiş bulunduğumuz suçtan sorulacak değilsiniz ve biz de sizin yapmakta olduklarınızdan sorulacak değiliz."'),
(3632, 34, 26, 'De ki: "Rabbimiz (kıyamet günü) bizi birarada toplayacak, sonra da hak ile aramızı ayıracaktır. O, (gerçek hükmünü vererek hak ile batılın arasını) açandır, (herşeyi hakkıyla) bilendir."'),
(3633, 34, 27, 'De ki: "O\'na (kulluk etmede) eklemekte olduğunuz ortakları bana gösterin. Asla (onlar ona gerçek ortak olamazlar); hayır, O, güçlü ve üstün olan, hüküm ve hikmet sahibi olan Allah\'tır."'),
(3634, 34, 28, 'Biz seni ancak bütün insanlara bir müjde verici ve uyarıcı olarak gönderdik. Ancak insanların çoğu bilmiyorlar.'),
(3635, 34, 29, 'Onlar: "Eğer doğru sözlü iseniz, bu va\'d(ettiğiniz azap) ne zamanmış?" derler.'),
(3636, 34, 30, 'De ki: "Sizin için belirlenmiş bir gün vardır ki, ondan ne bir an ertelenebilirsiniz, ne de (bir an) öne alınabilirsiniz.'),
(3637, 34, 31, 'İnkar edenler dedi ki: "Biz kesin olarak, ne bu Kur\'an\'a inanırız, ne ondan önceki (indirile)ne." Sen o zulmedenleri, Rableri huzurunda tutuklanmış olarak görsen; sözü (suçlamaları) birbirlerine karşı evirip-çevirir (birbirlerine yöneltirler). Za\'fa uğratılan (müstaz\'af)lar, büyüklük taslayanlara derler ki: "Eğer sizler olmasaydınız, gerçekten bizler mü\'min (kimse)ler olurduk."'),
(3638, 34, 32, 'Büyüklük taslayanlar, za\'fa uğratılan (müstaz\'af)lara dediler ki: "Size hidayet geldikten sonra, sizi biz mi ondan alıkoyduk? Hayır, siz (zaten) suçlu-günahkarlardınız."'),
(3639, 34, 33, 'Za\'fa uğratılanlar da büyüklük taslayanlara: "Hayır, siz gece ve gündüz hileli düzenler (kurup) bizim Allah\'ı inkar etmemizi ve O\'na eşler koşmamızı bize emrediyordunuz" dediler. Azabı gördüklerinde pişmanlıklarını saklarlar; Biz de inkar edenlerin boyunlarına halkalar geçirdik. Onlar, yaptıklarından başkasıyla mı cezalandırılacaklardı?'),
(3640, 34, 34, 'Biz hangi ülkeye bir uyarıcı gönderdikse, mutlaka oranın \'refah içinde şımaran önde gelenleri\': "Gerçekten biz, sizin kendisiyle gönderildiğiniz şeyi tanımıyoruz" demişlerdir.'),
(3641, 34, 35, 'Ve: “Biz mallar ve evlatlar bakımından daha çoğunluktayız ve bir azaba uğratılacak da değiliz” de demişlerdir.'),
(3642, 34, 36, 'De ki: "Şüphesiz benim Rabbim rızkı dilediğine genişletir-yayar ve kısar da. Ancak insanların çoğu bilmiyorlar."'),
(3643, 34, 37, 'Bizim Katımız\'da sizi (bize) yaklaştıracak olan ne mallarınız, ne de evlatlarınızdır; ancak iman edip salih amellerde bulunanlar başka. İşte onlar; onlar için yaptıklarına karşılık olmak üzere kat kat mükafaat vardır ve onlar yüksek köşklerinde güven içindedirler.'),
(3644, 34, 38, 'Ayetlerimizi etkisiz bırakmak için çaba harcayanlar; işte onlar da azabın içine getirilmişlerdir.'),
(3645, 34, 39, 'De ki: "Şüphesiz benim Rabbim, kullarından rızkı dilediğine genişletip-yayar ve ona kısar da. Her neyi infak ederseniz, O (Allah), yerine bir başkasını verir; O, rızık verenlerin en hayırlısıdır."'),
(3646, 34, 40, 'O gün, onların hepsini birarada toplayacak (haşredecek), sonra meleklere diyecek ki: "Size tapanlar bunlar mıydı?"'),
(3647, 34, 41, '(Melekler) Derler ki: "Sen Yücesin, bizim velimiz Sensin, onlar değil. Hayır, onlar cinlere tapıyordu ve çoğu onlara iman etmişlerdi."'),
(3648, 34, 42, 'Artık bugün, bir kısmınızın bir kısmınıza yarar ve zarar sağlamaya gücü yetmez. Biz de o zulmedenlere deriz ki: "Yalanlamakta olduğunuz ateşin azabını tadın."'),
(3649, 34, 43, 'Onlara, apaçık olan ayetlerimiz okunduğunda: "Bu, sizi babalarınızın taptıkların(ilahlar)dan alıkoymak isteyen bir adamdan başkası değildir" dediler. Ve dediler ki: "Bu, düzülüp uydurulmuş bir yalan (iftira)dan başka bir şey de değildir." İnkar edenler de, kendilerine geldiği zaman hak için: "Bu, apaçık bir büyüden başka bir şey değildir" dediler.'),
(3650, 34, 44, 'Oysa Biz onlara ders alacakları kitaplar vermemiştik ve kendilerine senden önce bir uyarıcı da göndermemiştik.'),
(3651, 34, 45, 'Kendilerinden öncekiler de yalanladı. Oysa bunlar, öbürlerine verdiklerimizin onda birine bile ulaşamamışlardı. Buna rağmen (şımararak) elçilerimi yalanladılar; ancak Benim de (onları) inkarım (yıkıma uğratmam) nasıl oldu?'),
(3652, 34, 46, 'De ki: "Size bir tek öğüt veriyorum: “Allah için ikişer ikişer ve teker teker kıyam etmeniz, sonra düşünmeniz. Sizin sahibiniz (veya arkadaşınız olan Peygamber)de hiçbir delilik yoktur. O, yalnızca sizi, şiddetli bir azabın öncesinde uyarandır."'),
(3653, 34, 47, 'De ki: "Ben sizden bir ücret istemişsem, artık o sizin olsun. Benim ecrim (ücretim), yalnızca Allah\'a aittir. O, herşeye şahid olandır."'),
(3654, 34, 48, 'De ki: "Şüphesiz Rabbim hakkı (batılın yerine veya dilediği kimsenin kalbine) koyar. O, gaybleri bilendir.'),
(3655, 34, 49, 'De ki: "Hak geldi; batıl ise ne (bir şey) ortaya çıkarabilir, ne geri getirebilir.”'),
(3656, 34, 50, 'De ki: "Eğer ben sapacak olsam, artık kendi nefsim aleyhine sapmış olurum; eğer hidayeti bulacak olsam, bu da Rabbimin bana vahyetmekte olduğu (Kur\'an) sayesindedir. Şüphesiz O, işitendir, yakın olandır.”'),
(3657, 34, 51, 'Sen onları korkuya kapıldıklarında bir görsen. Artık hiçbir kaçış yoktur ve yakın bir yerden yakalanıvermişlerdir.'),
(3658, 34, 52, '"Biz ona iman ettik" derler; ancak onlara uzak bir yerden (ahiretten imana) el uzatmak nerede?'),
(3659, 34, 53, 'Oysa daha önce onu inkar etmişlerdi; onlar uzak bir yerden gayba atıp tutuyorlardı (dil uzatıyorlardı).'),
(3660, 34, 54, '(Şimdi) Kendileriyle istek duydukları şeyler arasında perde çekilmiştir; daha önce benzerlerine yapıldığı gibi. Çünkü onlar, kuşku verici bir tereddüt içinde idiler.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Hamd, gökleri ve yeri yaratan, ikişer, üçer ve dörder kanatlı melekleri elçiler kılan Allah\'ındır; O, yaratmada dilediğini arttırır. Şüphesiz Allah, herşeye güç yetirendir.'),
(3662, 35, 2, 'Allah, insanlar için rahmetinden her neyi açacak olsa, artık onu kısıp-tutacak yoktur; her neyi kısar-tutarsa, artık onu da ondan sonra salıverecek yoktur. O, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(3663, 35, 3, 'Ey insanlar, Allah\'ın üzerinizdeki nimetini anın. Gökten ve yerden sizi rızıklandıran Allah\'ın dışında bir başka Yaratıcı var mı? O\'ndan başka İlah yoktur. Öyleyse nasıl olur da çevriliyorsunuz?'),
(3664, 35, 4, 'Eğer seni yalanlıyorlarsa, senden önceki elçiler de yalanlandı. (En sonunda bütün) İşler Allah\'a döndürülür.'),
(3665, 35, 5, 'Ey insanlar, hiç şüphesiz Allah\'ın va\'di haktır; öyleyse dünya hayatı sizi aldatmasın ve aldatıcı(lar) da, sizi Allah ile (Allah\'ın adını kullanarak) aldatmasın.'),
(3666, 35, 6, 'Gerçek şu ki, şeytan sizin düşmanınızdır, öyleyse siz de onu düşman edinin. O, kendi grubunu, ancak çılgınca yanan ateşin halkından olmağa çağırır.'),
(3667, 35, 7, 'O inkar edenler; onlar için şiddetli bir azap vardır. İman edip salih amellerde bulunanlar ise; onlar için de bir bağışlanma ve büyük bir ecir vardır.'),
(3668, 35, 8, 'Kötü olarak işledikleri kendisine çekici-süslü kılınıp da onu güzel gören mi (Allah Katında kabul görecek)? Artık şüphesiz Allah, dilediğini saptırır, dilediğini hidayete eriştirir. Öyleyse, onlara karşı nefsin hasretlere kapılıp gitmesin. Gerçekten Allah, yaptıklarını bilendir.'),
(3669, 35, 9, 'Allah, rüzgarları gönderir, onlar da bulutu kaldırır, böylece Biz onu ölü bir beldeye sürükleriz, onunla, yeri ölümünden sonra diriltiriz. İşte (ölümden sonra) dirilip- yayılma da böyledir.'),
(3670, 35, 10, 'Kim izzeti istiyorsa, artık bütün izzet Allah\'ındır. Güzel söz O\'na yükselir, salih amel de onu yükseltir. Kötülükleri tasarlayıp düzenleyenler ise; onlar için şiddetli bir azap vardır. Onların tasarladıkları \'boşa çıkıp bozulur\'.'),
(3671, 35, 11, 'Allah sizi topraktan yarattı, sonra bir damla sudan. Sonra da sizi çift çift kıldı. O\'nun bilgisi olmaksızın, hiçbir dişi gebe kalmaz ve doğurmaz da. Ömür sürene, ömür verilmesi ve onun ömründen kısaltılması da mutlaka bir kitapta (yazılı)dır. Gerçekten bu, Allah\'a göre kolaydır.'),
(3672, 35, 12, 'İki deniz bir değildir. Şu, tatlı, susuzluğu keser ve içimi kolay; şu da, tuzlu ve acıdır. Ancak her birinden taze et yersiniz ve takınmakta olduğunuz süs eşyalarını çıkarırsınız. O\'nun fazlından aramanız ve umulur ki şükretmeniz için gemilerin onda (denizde) suları yara yara akıp gittiğini görürsün.'),
(3673, 35, 13, '(Allah) Geceyi gündüze bağlayıp-katar, gündüzü de geceye bağlayıp-katar; güneşi ve ayı emre amade kılmıştır, her biri adı konulmuş bir süreye kadar akıp gitmektedir. İşte bunları (yaratıp düzene koyan) Allah sizin Rabbinizdir; mülk O\'nundur. O\'ndan başka taptıklarınız ise, \'bir çekirdeğin incecik zarına\' bile malik olamazlar.'),
(3674, 35, 14, 'Eğer onlara dua ederseniz, duanızı işitmezler, işitseler bile size cevap veremezler. Kıyamet gününde ise, sizin şirk koşmanızı tanımayacaklardır. (Bunu herşeyden) Haberi olan Allah gibi sana (hiç kimse) haber vermez.'),
(3675, 35, 15, 'Ey insanlar, siz Allah\'a (karşı fakir olan) muhtaçlarsınız; Allah ise, Ğaniy (hiçbir şeye ihtiyacı olmayan)dır, Hamid (övülmeye layık)tır.'),
(3676, 35, 16, 'Dileyecek olsa, sizi giderir (yok eder) ve yepyeni bir halk getirir.'),
(3677, 35, 17, 'Bu, Allah\'a göre güç değildir.'),
(3678, 35, 18, 'Hiçbir günahkar bir başka günahkarın günahını yüklenemez. Eğer yükü ağır olan kimse (bir başkasını) onu taşımaya çağırsa, -bu, yakın-akrabası da olsa- kendisine ondan hiçbir şey yükletilmez. Sen, yalnızca gayb ile Rablerinden \'içleri titreyerek-korkmakta\' olanları ve dosdoğru namazı kılanları uyarırsın. Kim temizlenip-arınırsa, artık o, kendi nefsi için temizlenip-arınmıştır. Sonunda dönüş Allah\'adır.'),
(3679, 35, 19, 'Kör olanla (basiretle) gören bir değildir;'),
(3680, 35, 20, 'Karanlıklarla aydınlık,'),
(3681, 35, 21, 'Gölge ile sıcaklık da.'),
(3682, 35, 22, 'Diri olanlarla ölüler de bir değildir. Gerçekten Allah, dilediğine işittirir; sen ise kabirlerde olanlara işittirecek değilsin.'),
(3683, 35, 23, 'Sen, yalnızca bir uyarıcısın.'),
(3684, 35, 24, 'Şüphesiz Biz seni, hak ile bir müjde verici ve bir uyarıcı olarak gönderdik. Hiçbir ümmet yoktur ki, içinde bir uyarıcı gelip-geçmiş olmasın.'),
(3685, 35, 25, 'Eğer seni yalanlıyorlarsa, senden öncekiler de yalanlandı; elçileri ise; kendilerine apaçık ayetler, sahifeler ve aydınlatıcı kitaplar getirmişlerdi.'),
(3686, 35, 26, 'Sonra Ben de o inkar edenleri yakalayıverdim. Beni inkarları nasıl oldu (onlar gördüler)?'),
(3687, 35, 27, 'Allah\'ın gökyüzünden su indirdiğini görmedin mi? Böylece Biz onunla, renkleri değişik olan meyveler çıkardık. Dağlardan da beyaz, kırmızı renkleri değişik ve siyah yollar (kıldık).'),
(3688, 35, 28, 'İnsanlardan, hayvanlardan ve davarlardan da renkleri böyle değişik olanlar vardır. Kulları içinde ise Allah\'tan ancak alim olanlar \'içleri titreyerek-korkar\'. Şüphesiz Allah, üstün ve güçlü olandır, bağışlayandır.'),
(3689, 35, 29, 'Gerçekten Allah\'ın Kitab\'ını okuyanlar, namazı dosdoğru kılanlar ve kendilerine rızık olarak verdiklerimizden gizli ve açık infak edenler; kesin olarak zarara uğramayacak bir ticareti umabilirler.'),
(3690, 35, 30, 'Çünkü (Allah,) ecirlerini noksansız olarak öder ve Kendi fazlından onlara artırır. Şüphesiz O, bağışlayandır, şükrü kabul edendir.'),
(3691, 35, 31, 'Kendinden öncekini doğrulayıcı olarak sana Kitap\'tan vahyettiğimiz gerçeğin ta kendisidir. Şüphesiz Allah, elbette haber alandır, görendir.'),
(3692, 35, 32, 'Sonra Kitab\'ı kullarımızdan seçtiklerimize miras kıldık. Artık onlardan kimi kendi nefsine zulmeder, kimi orta bir yoldadır, kimi de Allah\'ın izniyle hayırlarda yarışır öne geçer. İşte bu, büyük fazlın kendisidir.'),
(3693, 35, 33, 'Adn cennetleri (onlarındır); oraya girerler, orada altından bileziklerle ve incilerle süslenirler. Ve orada onların elbiseleri ipek(ten)dir.'),
(3694, 35, 34, 'Derler ki: "Bizden hüznü giderip yok eden Allah\'a hamd olsun; şüphesiz Rabbimiz, gerçekten bağışlayandır, şükrü kabul edendir."'),
(3695, 35, 35, '"Ki O, bizi Kendi fazlından (ebedi olarak) kalınacak bir yurda yerleştirdi; burada bize bir yorgunluk dokunmaz ve burada bize bir bıkkınlık da dokunmaz."'),
(3696, 35, 36, 'İnkar edenlere gelince, onlar için de cehennem ateşi vardır. Onlar için ne, karar verilir, ki böylece ölüversinler, ne de kendilerine onun azabından (bir şey) hafifletilir. İşte Biz, her nankör olanı böyle cezalandırırız.'),
(3697, 35, 37, 'İçinde onlar (şöyle) çığlık atarlar: "Rabbimiz, bizi çıkar, yaptığımızdan başka salih bir amelde bulunalım." Size orda (dünyada), öğüt alabilecek olanın öğüt alabileceği kadar ömür vermedik mi? Size uyaran da gelmişti. Öyleyse (azabı) tadın; artık zalimler için bir yardımcı yoktur.'),
(3698, 35, 38, 'Şüphesiz Allah, göklerin ve yerin gaybını bilendir. Gerçek şu ki O, sinelerin özünde (saklı) olanı bilir.'),
(3699, 35, 39, 'Yeryüzünde sizi halifeler kılan O\'dur. Öyleyse kim inkar ederse, artık inkarı kendi aleyhinedir. Rableri Katında kafir olanlara kendi inkarları gazabtan başkasını arttırmaz ve kafir olanlara kendi inkarları kayıptan başkasını arttırmaz.'),
(3700, 35, 40, 'De ki: "Siz, Allah\'ın dışında taptığınız ortaklarınızı gördünüz mü? Bana haber verin; yerden neyi yaratmışlardır? Ya da onların göklerde bir ortaklığı mı var? Yoksa Biz onlara bir kitap vermişiz de onlar bundan (dolayı) apaçık bir belge üzerinde midirler? Hayır, zulmedenler, birbirlerine aldatmadan başkasını vadetmiyorlar.'),
(3701, 35, 41, 'Şüphesiz Allah, gökleri ve yeri zeval bulurlar diye (her an kudreti altında) tutuyor. Andolsun, eğer zeval bulacak olurlarsa, Kendisi\'nden sonra artık kimse onları tutamaz. Doğrusu O, Halim’dir, bağışlayandır.'),
(3702, 35, 42, 'Yeminlerinin olanca güçleriyle, kendilerine bir uyarıcı-korkutucu gelecek olsa, ümmetlerinin herhangi birinden mutlaka daha doğru olacaklarına dair, Allah\'a and içtiler. Ancak onlara bir uyarıcı-korkutucu geldiğinde (bu,) nefretlerinden başkasını artırmadı.'),
(3703, 35, 43, '(Hem de) Yeryüzünde büyüklük taslayarak ve kötülüğü tasarlayıp düzenleyerek. Oysa hileli düzen, kendi sahibinden başkasını sarıp-kuşatmaz. Artık onlar öncekilerin sünnetinden başkasını mı gözlemektedirler? Sen, Allah\'ın sünnetinde kesinlikle bir değişiklik bulamazsın ve sen, Allah\'ın sünnetinde kesinlikle bir dönüşüm de bulamazsın.'),
(3704, 35, 44, 'Yeryüzünde gezip dolaşmıyorlar mı ki, kendilerinden öncekilerin nasıl bir sona uğradıklarını görsünler; üstelik onlar kuvvet bakımından kendilerinden daha güçlüydüler. Göklerde ve yerde Allah\'ı aciz bırakacak hiçbir şey yoktur. Şüphesiz O, bilendir, güç yetirendir.'),
(3705, 35, 45, 'Eğer Allah, kazandıkları dolayısıyla insanları (azap ile) yakalayıverecek olsaydı, (yerin) sırtı üzerinde hiçbir canlıyı bırakmazdı, ancak onları, adı konulmuş bir süreye kadar ertelemektedir. Sonunda ecelleri geldiği zaman, artık şüphesiz Allah Kendi kullarını görendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Yasîn.'),
(3707, 36, 2, 'Andolsun hikmetli Kur\'an\'a,'),
(3708, 36, 3, 'Gerçekten sen, gönderilen (elçi)lerdensin.'),
(3709, 36, 4, 'Dosdoğru bir yol üzerinde(sin).'),
(3710, 36, 5, '(Kur\'an) Güçlü ve üstün olan, esirgeyen (Allah\')ın indirmesidir.'),
(3711, 36, 6, 'Babaları uyarılmamış, böylece kendileri de gafil kalmış bir kavmi uyarman için (gönderildin).'),
(3712, 36, 7, 'Andolsun, onların çoğu üzerine o söz hak olmuştur; artık inanmazlar.'),
(3713, 36, 8, 'Gerçekten Biz onların boyunlarına, çenelere kadar (dayanan) halkalar geçirdik; bu yüzden başları yukarı kalkıktır.'),
(3714, 36, 9, 'Biz önlerinde bir sed, arkalarında bir sed çektik. Böylelikle onları örtüverdik, artık görmezler.'),
(3715, 36, 10, 'Kendilerini uyarsan da, uyarmasan da onlar için birdir; inanmazlar.'),
(3716, 36, 11, 'Sen ancak, zikre (Kur\'an\'a) uyan ve gayb ile Rahman olan (Allah\')a (karşı) içi titreyerek korku duyan kimseyi uyarırsın. İşte böylesini, bir bağışlanma ve üstün bir ecirle müjdele.'),
(3717, 36, 12, 'Şüphesiz Biz, ölüleri Biz diriltiriz; onların önden takdim ettiklerini ve eserlerini Biz yazarız. Biz herşeyi, apaçık bir kitapta tespit edip korumuşuz.'),
(3718, 36, 13, 'Sen onlara, o şehir halkının örneğini ver; hani oraya elçiler gelmişti.'),
(3719, 36, 14, 'Hani onlara iki (elçi) göndermiştik, fakat ikisini yalanlamışlardı. Biz de (iki elçiyi) bir üçüncüyle güçlendirdik; böylece dediler ki: "Şüphesiz biz, size, gönderilmiş elçileriz."'),
(3720, 36, 15, 'Dediler ki: "Siz, bizim benzerimiz olan bir beşerden başkası değilsiniz, Rahman (olan Allah) da herhangi bir şey indirmiş değildir. Siz, yalnızca yalan söylüyorsunuz."'),
(3721, 36, 16, 'Dediler ki: "Rabbimiz, gerçekten size gönderilmiş elçiler olduğumuzu bilir."'),
(3722, 36, 17, '"Bizim üzerimizde de (sorumluluk ve görev olarak) apaçık bir tebliğden başkası yoktur."'),
(3723, 36, 18, 'Dediler ki: "Herhalde biz, sizlerden dolayı uğursuzluğa uğradık. Eğer (bu söylediklerinize) bir son vermeyecek olursanız, andolsun, sizi taşa tutacağız ve mutlaka bizden yana size acı bir azap dokunacaktır."'),
(3724, 36, 19, 'Dediler ki: "Uğursuzluğunuz, sizinledir. Size öğüt verildi diye mi (uğursuzluğa uğradınız)? Hayır, siz ölçüyü taşıran bir kavimsiniz."'),
(3725, 36, 20, 'Şehrin en uzak yerinden bir adam koşarak geldi: "Ey kavmim, elçilere uyun" dedi.'),
(3726, 36, 21, '"Sizden ücret istemeyenlere uyun, onlar hidayet bulmuş kimselerdir."'),
(3727, 36, 22, '"Bana ne oluyor ki, beni yaratana kulluk etmeyecekmişim? Siz O\'na döndürüleceksiniz."'),
(3728, 36, 23, '"Ben, O\'ndan başka İlahlar edinir miyim ki, Rahman (olan Allah), bana bir zarar dileyecek olsa, ne onların şefaati bana bir şeyle yarar sağlar, ne de onlar beni kurtarabilirler."'),
(3729, 36, 24, '"O durumda ise, gerçekten ben apaçık bir sapıklık içinde olmuş olurum."'),
(3730, 36, 25, '"Şüphesiz ben, sizin Rabbinize iman ettim; işte beni işitin."'),
(3731, 36, 26, 'Ona: "Cennete gir" denildi. O da: "Keşke benim kavmim de bir bilseydi" dedi.'),
(3732, 36, 27, '"Rabbimin beni bağışladığını ve ağırlananlardan kıldığını."'),
(3733, 36, 28, 'Kendisinden sonra ise, kavminin üzerine gökten bir ordu indirmedik; indirecek de değildik.'),
(3734, 36, 29, '(Ancak onlara) Yalnızca bir tek çığlık (yetti); anında sönüverdiler.'),
(3735, 36, 30, 'Yazıklar olsun kullara; ki onlara bir elçi gelmeyegörsün, mutlaka onunla alay ederlerdi.'),
(3736, 36, 31, 'Görmüyorlar mı, kendilerinden önce nice nesilleri helak ettik? Onlar, bir daha kendilerine dönmemektedirler.'),
(3737, 36, 32, 'Ancak onların hepsi, toplanmış olarak Huzurumuz\'a getirilmişlerdir.'),
(3738, 36, 33, 'Ölü toprak kendileri için bir ayettir; Biz onu dirilttik, ondan taneler çıkarttık, böylelikle ondan yemektedirler.'),
(3739, 36, 34, 'Biz, orada hurmalıklardan ve üzüm-bağlarından bahçeler kıldık ve içlerinde pınarlar fışkırttık:'),
(3740, 36, 35, 'Onun ürünlerinden ve kendi ellerinin yaptıklarından yemeleri için. Yine de şükretmiyorlar mı?'),
(3741, 36, 36, 'Yerin bitirdiklerinden, kendi nefislerinden ve daha bilmedikleri nice şeylerden bütün çiftleri yaratan (Allah çok) Yücedir.'),
(3742, 36, 37, 'Gece de kendileri için bir ayettir. Gündüzü ondan sıyırıp yüzeriz, hemen artık karanlıkta kalıvermişlerdir.'),
(3743, 36, 38, 'Güneş de, kendisi için (tespit edilmiş) olan bir müstakarra doğru akıp gitmektedir. Bu, üstün ve güçlü olan, bilen (Allah)ın takdiridir.'),
(3744, 36, 39, 'Ay\'a gelince, Biz onun için de birtakım uğrak yerleri takdir ettik; sonunda o, eski bir hurma dalı gibi döndü (döner).'),
(3745, 36, 40, 'Ne Güneş\'in Ay\'a erişip-yetişmesi gerekir, ne de gecenin gündüzün önüne geçmesi. Her biri bir yörüngede yüzüp gitmektedirler.'),
(3746, 36, 41, 'Onların soylarını dolu gemilerde taşımamız da kendileri için bir ayettir.'),
(3747, 36, 42, 'Ve onlar için binmekte oldukları bunun benzeri (nice) şeyleri yaratmamız da.'),
(3748, 36, 43, 'Eğer dilersek onları batırır-boğarız; bu durumda ne onların imdadına yetişen olur, ne de kurtulabilirler.'),
(3749, 36, 44, 'Ancak Bizden bir rahmet olması ve (onları) belirli bir zamana kadar yararlandırmamız başka.'),
(3750, 36, 45, 'Onlara: "Önünüzde ve arkanızda olandan sakının, belki esirgenirsiniz" denildiğinde, (dinlemeyip inkara devam edenler).'),
(3751, 36, 46, 'Onlara, Rablerinin ayetlerinden bir ayet gelmeyi görsün, mutlaka ondan yüz çevirirler.'),
(3752, 36, 47, 'Ve onlara: "Size Allah\'ın rızık olarak verdiklerinden infak edin" denildiği zaman, o inkar edenler iman edenlere dediler ki: "Allah\'ın, eğer dilemiş olsaydı yedireceği kimseyi biz mi yedirecek mişiz? Gerçekten siz, apaçık bir şaşkınlık içindesiniz."'),
(3753, 36, 48, 'Ve derler ki: "Eğer doğru söylüyorsanız bu tehdit (etmekte olduğunuz yıkım ve azap) ne zamanmış?"'),
(3754, 36, 49, 'Onlar, yalnızca tek bir çığlıktan başkasını gözetmezler, onlar birbirleriyle çekişip-dururken o kendilerini yakalayıverir.'),
(3755, 36, 50, 'Artık ne bir tavsiyede bulunmağa güç yetirebilirler, ne ailelerine dönebilirler.'),
(3756, 36, 51, 'Sur\'a üfürülmüştür; böylece onlar kabirlerinden (diriltilip) Rablerine doğru (dalgalar halinde) süzülüp-giderler.'),
(3757, 36, 52, 'Demişlerdir ki: "Eyvahlar bize, uykuya-bırakıldığımız yerden bizi kim diriltip-kaldırdı? Bu, Rahman (olan Allah)ın va\'dettiğidir, (demek ki) gönderilen (elçi)ler doğru söylemiş".'),
(3758, 36, 53, 'O, yalnızca bir tek çığlıktan başkası değildir; artık onların hepsi toplanmış olarak Huzurumuz\'a getirilmişlerdir.'),
(3759, 36, 54, 'İşte bugün hiç kimseye (hiç)bir şeyle zulmedilmez ve siz de yaptıklarınızdan başkasıyla karşılık görmezsiniz.'),
(3760, 36, 55, 'Gerçek şu ki, bugün cennet halkı, \'sevinç ve mutluluk dolu\' bir meşguliyet içindedirler.'),
(3761, 36, 56, 'Kendileri ve eşleri, gölgeliklerde, tahtlar üzerinde yaslanmışlardır.'),
(3762, 36, 57, 'Orada taptaze-meyveler onların ve istek duydukları herşey onlarındır.'),
(3763, 36, 58, 'Çok esirgeyen Rabb\'dan onlara bir de sözlü "Selam" (vardır).'),
(3764, 36, 59, '"Ey suçlu-günahkarlar, bugün siz bir yana çekilin."'),
(3765, 36, 60, '"Ey Ademoğulları, ben size and vermedim mi ki: Şeytana kulluk etmeyin, çünkü, o, sizin için apaçık bir düşmandır;"'),
(3766, 36, 61, '"Bana kulluk edin, doğru yol budur."'),
(3767, 36, 62, 'Andolsun o, sizden birçok insan-neslini saptırmıştı. Yine de aklınızı kullanmıyor muydunuz?'),
(3768, 36, 63, 'İşte bu, size vadedilmiş cehennemdir.'),
(3769, 36, 64, 'İnkar etmenize karşılık olmak üzere bugün oraya girin.'),
(3770, 36, 65, 'Bugün Biz onların ağızlarını mühürleriz; (günahtan ve sevaptan yana) kazandıklarını, elleri Bize söylemekte, ayakları (aleyhlerinde) şahitlik etmektedir.'),
(3771, 36, 66, 'Eğer dilemiş olsaydık, gözlerinin üstüne bastırır-kör ederdik, böylece yola dökülüp-koşuşurlardı. Fakat nasıl göreceklerdi ki?'),
(3772, 36, 67, 'Eğer dilemiş olsaydık, oldukları yerde (en görkemli çağlarında) onları bir başka kalıba sokardık; böylece ne ileri gitmeye, ne geri dönmeye güç yetirebilirlerdi.'),
(3773, 36, 68, 'Kime uzun ömür verirsek, yaratılışta onu tersine çeviririz. Yine de akıllarını kullanmayacaklar mı?'),
(3774, 36, 69, 'Biz ona (Peygambere) şiir öğretmedik; (bu,) ona yakışmaz da. O (kendisine indirilen Kitap), yalnızca bir öğüt ve apaçık bir Kur\'an\'dır.'),
(3775, 36, 70, '(Kur\'an,) Diri olanları uyarıp korkutmak ve kafirlerin üzerine sözün hak olması için (indirilmiştir).'),
(3776, 36, 71, 'Ellerimizin yaptıklarından kendileri için nice hayvanları yarattığımızı görmüyorlar mı? Böylece bunlara malik oluyorlar.'),
(3777, 36, 72, 'Biz onlara kendileri için boyun eğdirdik; işte bir kısmı binekleridir, bir kısmını(n da etini) yiyorlar.'),
(3778, 36, 73, 'Onlarda kendileri için daha nice yararlar ve içecekler vardır. Yine de şükretmeyecekler mi?'),
(3779, 36, 74, 'Yardım görürler umuduyla, Allah\'tan başka İlahlar edindiler.'),
(3780, 36, 75, 'Onların (o İlahların) kendilerine yardım etmeye güçleri yetmez; oysa kendileri onlar için hazır bulundurulmuş askerlerdir.'),
(3781, 36, 76, 'Öyleyse onların sözleri seni hüzne kaptırmasın. Gerçekten Biz, sakladıklarını da, açığa vurduklarını da biliyoruz.'),
(3782, 36, 77, 'İnsan, Bizim kendisini bir damla sudan yarattığımızı görmüyor mu? Şimdi o, apaçık bir düşman kesilmiştir.'),
(3783, 36, 78, 'Kendi yaratılışını unutarak Bize bir örnek verdi; dedi ki: "Çürümüş-bozulmuşken, bu kemikleri kim diriltecekmiş?"'),
(3784, 36, 79, 'De ki: "Onları, ilk defa yaratıp-inşa eden diriltecek. O, her yaratmayı bilir."'),
(3785, 36, 80, 'Ki O, size yeşil ağaçtan bir ateş kılandır; siz de ondan yakıyorsunuz.'),
(3786, 36, 81, 'Gökleri ve yeri yaratan, onların bir benzerini yaratmağa kadir değil mi? Elbette (öyledir); O, yaratandır, bilendir.'),
(3787, 36, 82, 'Bir şeyi dilediği zaman, O\'nun emri yalnızca: "Ol" demesidir; o da hemen oluverir.'),
(3788, 36, 83, 'Herşeyin melekutu (hükümranlık ve mülkü) elinde bulunan (Allah) ne Yücedir. Siz O\'na döndürüleceksiniz.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Saflar halinde dizilenlere andolsun,'),
(3790, 37, 2, 'Haykırıp sürükleyenlere,'),
(3791, 37, 3, 'Zikir okuyanlara,'),
(3792, 37, 4, 'Tartışmasız, sizin İlahınız gerçekten birdir.'),
(3793, 37, 5, 'Göklerin, yerin ve ikisi arasında bulunanların Rabbidir, doğuların da Rabbidir.'),
(3794, 37, 6, 'Şüphesiz Biz dünya göğünü \'çekici bir süsle\', yıldızlarla süsleyip-donattık.'),
(3795, 37, 7, 'Ve itaatten çıkmış her azgın şeytandan koruduk;'),
(3796, 37, 8, 'Ki onlar, Mele\'i A\'la\'ya kulak verip dinleyemezler, her yandan kovulup atılırlar;'),
(3797, 37, 9, 'Uzaklaştırılırlar. Onlara kesintisiz bir azap vardır.'),
(3798, 37, 10, 'Ancak (sözü hırsızlama) çalıp-kapan olursa, artık onu da delip geçen \'yakıcı bir alev\' izler (ve yok eder).'),
(3799, 37, 11, 'Şimdi onlara sor: Yaratılış bakımından onlar mı daha zorlu, yoksa Bizim yarattıklarımız mı? Doğrusu Biz onları, cıvık-yapışkan bir çamurdan yarattık.'),
(3800, 37, 12, 'Hayır, sen (bu muhteşem yaratışa ve onların inkarına) şaşırdın kaldın; onlar ise alay edip duruyorlar.'),
(3801, 37, 13, 'Kendilerine öğüt verildiğinde, öğüt almıyorlar.'),
(3802, 37, 14, 'Bir ayet (mucize) gördüklerinde de, alay konusu edinip eğleniyorlar.'),
(3803, 37, 15, '"Bu, açıkça bir büyüden başkası değildir" dediler.'),
(3804, 37, 16, '"Biz öldüğümüz, toprak ve kemik olduğumuzda mı, gerçekten biz mi diriltilecekmişiz?"'),
(3805, 37, 17, '"Veya önceki atalarımız da mı?"'),
(3806, 37, 18, 'De ki: "Evet, üstelik boyun bükmüş kimseler olarak (diriltileceksiniz).”'),
(3807, 37, 19, 'İşte o, yalnızca bir tek çığlıktan ibarettir; artık kendileri (diriltilmiş olarak) bakıp duruyorlar.'),
(3808, 37, 20, 'Derler ki: "Eyvahlar bize; bu, din günüdür."'),
(3809, 37, 21, '"Bu, sizin yalanladığınız (mü\'mini kafirden, haklıyı haksızdan) ayırma günüdür."'),
(3810, 37, 22, '"Zulmedenleri, eşlerini ve taptıklarını biraraya getirip toplayın."'),
(3811, 37, 23, '"Allah\'tan başka (taptıklarını); artık onları cehennemin yoluna yöneltip götürün."'),
(3812, 37, 24, '"Ve onları durdurup-tutuklayın, çünkü sorguya çekileceklerdir."'),
(3813, 37, 25, '(Onlara seslenilir:) "Ne oluyor size, birbirinizle (dünyada olduğu gibi) yardımlaşmıyorsunuz?"'),
(3814, 37, 26, 'Hayır, bugün onlar teslim olmuşlardır.'),
(3815, 37, 27, 'Kimi kimine yönelmiş olarak birbirlerine soruyorlar:'),
(3816, 37, 28, '"Gerçekten sizler bize sağdan (sağ duyudan ve haktan) yana gelip yanaşıyordunuz." derler.'),
(3817, 37, 29, '(Diğerleri de:) "Hayır" derler. "Zaten sizler mü\'min kimseler değildiniz."'),
(3818, 37, 30, '"Bizim üzerinizde zorlayıcı hiçbir gücümüz yoktu; hayır siz (kendiniz) azgın bir kavimdiniz."'),
(3819, 37, 31, '"Böylece Rabbimiz\'in sözü (yıkım ve azap va\'di) üzerimize hak oldu. Şüphesiz, (azabı) tadıcılarız."'),
(3820, 37, 32, '"Evet, sizi azdırdık, çünkü biz de azgın kimselerdik."'),
(3821, 37, 33, 'Artık o gün onlar azapta ortaktırlar.'),
(3822, 37, 34, 'Doğrusu Biz, suçlu-günahkarlara böyle yaparız.'),
(3823, 37, 35, 'Çünkü onlara: "Allah\'tan başka İlah yoktur" denildiği zaman, büyüklük taslarlardı.'),
(3824, 37, 36, 'Ve derlerdi ki: "Biz, ünlenmiş bir şair için ilahlarımızı terk mi edeceğiz?"'),
(3825, 37, 37, 'Hayır, o, hakkı getirmiş ve gönderilen (elçi)leri de doğrulamıştı.'),
(3826, 37, 38, 'Şüphesiz, siz, acı azabı tadıcılarsınız."'),
(3827, 37, 39, 'Yaptıklarınızdan başkasıyla cezalandırılmayacaksınız.'),
(3828, 37, 40, 'Ancak muhlis olan kullar başka.'),
(3829, 37, 41, 'İşte onlar; onlar için bilinen bir rızık vardır.'),
(3830, 37, 42, 'Çeşitli-meyveler. Onlar ikram görenlerdir.'),
(3831, 37, 43, 'Nimetlerle donatılmış (naim) cennetlerde.'),
(3832, 37, 44, 'Birbirlerine karşı, tahtlar üzerinde (otururlar).'),
(3833, 37, 45, 'Kaynaktan (doldurulmuş) kadehlerle çevrelerinde dolaşılır.'),
(3834, 37, 46, 'Bembeyaz; içenlere lezzet (veren bir içki).'),
(3835, 37, 47, 'Onda ne bir gaile vardır, ne de kendilerinden geçip, akılları çelinir.'),
(3836, 37, 48, 'Ve yanlarında bakışlarını yalnızca eşlerine çevirmiş iri gözlü kadınlar vardır.'),
(3837, 37, 49, 'Sanki onlar, saklı bir yumurta gibi (çarpıcı ve pürüzsüz).'),
(3838, 37, 50, 'Böyleyken, kimi kimine yönelmiş olarak, birbirlerine soruyorlar:'),
(3839, 37, 51, 'Bir sözcü der ki: "Benim bir yakınım vardı."'),
(3840, 37, 52, '"Derdi ki: Sen de gerçekten (dirilişi) doğrulayanlardan mısın?"'),
(3841, 37, 53, '"Bizler öldüğümüz, toprak ve kemikler olduğumuzda mı, gerçekten biz mi (yeniden diriltilip sonra da) sorguya çekilecekmişiz?"'),
(3842, 37, 54, '(Konuşan yanındakilere) Der ki: "Sizler (onun şimdi ne durumda olduğunu) biliyor musunuz?"'),
(3843, 37, 55, 'Derken, bakıverdi, onu \'çılgınca yanan ateşin\' tam ortasında gördü.'),
(3844, 37, 56, 'Dedi ki: "Andolsun Allah\'a, neredeyse beni de (şu bulunduğun yere) düşürecektin."'),
(3845, 37, 57, '"Eğer Rabbimin nimeti olmasaydı, muhakkak ben de (azap yerine getirilip) hazır bulundurulanlardan olacaktım.'),
(3846, 37, 58, '"Nasıl, biz ölecek olanlar değil miymişiz?"'),
(3847, 37, 59, '"Yalnızca birinci ölümümüzden başka (öyle mi)? Ve biz azaba uğratılacak olanlar değil miymişiz?"'),
(3848, 37, 60, 'Şüphesiz, bu, asıl büyük \'kurtuluş ve mutluluğun\' ta kendisidir.'),
(3849, 37, 61, 'Böylece çalışanlar da bunun bir benzeri için çalışmalıdır.'),
(3850, 37, 62, 'Nasıl, böyle bir konaklanma mı daha hayırlı yoksa zakkum ağacı mı?'),
(3851, 37, 63, 'Doğrusu Biz, onu kafirler için bir fitne (bir imtihan konusu) kıldık.'),
(3852, 37, 64, 'Şüphesiz o, ‘çılgınca yanan ateşin’ dibinde bitip çıkar.'),
(3853, 37, 65, 'Onun tomurcukları, şeytanların başları gibidir.'),
(3854, 37, 66, 'Artık gerçekten, ondan yiyecekler böylelikle karınlarını ondan dolduracaklar.'),
(3855, 37, 67, 'Sonra kendileri için onun üzerinde kaynar su karıştırılmış bir içkileri de vardır.'),
(3856, 37, 68, 'Sonra onların dönecekleri yer, elbette (yine) çılgınca yanan ateştir.'),
(3857, 37, 69, 'Çünkü onlar, atalarını sapık kimseler olarak bulmuşlardı.'),
(3858, 37, 70, 'Kendileri de onları izleri üzerinde koşturup-duruyorlardı.'),
(3859, 37, 71, 'Andolsun, onlardan önce, evvelkilerin çoğu da sapmıştı.'),
(3860, 37, 72, 'Andolsun, Biz onlara uyarıcılar göndermiştik.'),
(3861, 37, 73, 'Uyarılanların nasıl bir sona uğradıklarına bir bak.'),
(3862, 37, 74, 'Ancak muhlis olan kullar başka.'),
(3863, 37, 75, 'Andolsun, Nuh Bize (dua edip) seslenmişti de, ne güzel icabet etmiştik.'),
(3864, 37, 76, 'Onu ve ailesini, o büyük üzüntüden kurtarmıştık.'),
(3865, 37, 77, 'Ve onun soyunu, (dünyada) onları da baki kıldık.'),
(3866, 37, 78, 'Sonra gelenler arasında ona (hayırlı ve şerefli bir isim) bıraktık.'),
(3867, 37, 79, 'Alemler içinde selam olsun Nuh’a.'),
(3868, 37, 80, 'Gerçekten Biz, ihsanda bulunanları böyle ödüllendiririz.'),
(3869, 37, 81, 'Şüphesiz o, Bizim mü’min olan kullarımızdandı.'),
(3870, 37, 82, 'Sonra diğerlerini suda boğduk.'),
(3871, 37, 83, 'Doğrusu İbrahim de onun (soyunun) bir kolundandır.'),
(3872, 37, 84, 'Hani o, Rabbine arınmış (selim) bir kalp ile gelmişti.'),
(3873, 37, 85, 'Hani babasına ve kavmine demişti ki: “Sizler neye tapıyorsunuz?”'),
(3874, 37, 86, '“Birtakım uydurma yalanlar için mi Allah’tan başka ilahlar istiyorsunuz?”'),
(3875, 37, 87, '“Alemlerin Rabbi hakkındaki zannınız nedir?”'),
(3876, 37, 88, 'Sonra yıldızlara bir göz attı.'),
(3877, 37, 89, '“Ben, doğrusu hastayım” dedi.'),
(3878, 37, 90, 'Böylelikle arkalarını çevirip ondan kaçmaya başladılar.'),
(3879, 37, 91, 'Bunun üzerine onların ilahlarına sokulup: “Yemek yemiyor musunuz?” dedi.'),
(3880, 37, 92, '“Size ne oluyor ki konuşmuyorsunuz?”'),
(3881, 37, 93, 'Derken onların üstüne yürüyüp sağ eliyle bir darbe indirdi.'),
(3882, 37, 94, 'Çok geçmeden (halkı) birbirine girmiş durumda kendisine yönelip geldiler.'),
(3883, 37, 95, 'Dedi ki: “Yontmakta olduğunuz şeylere mi tapıyorsunuz?”'),
(3884, 37, 96, '“Oysa sizi de, yapmakta olduklarınızı da Allah yaratmıştır.”'),
(3885, 37, 97, 'Dediler ki: “Onun için (yüksekçe) bir bina inşa edin de onu çılgınca yanan ateşin içine atın.”'),
(3886, 37, 98, 'Böylelikle ona bir tuzak hazırlamak istediler. Oysa Biz, onları alçaltılmışlar kıldık.'),
(3887, 37, 99, '(İbrahim) Dedi ki: “Şüphesiz ben, Rabbime gidiciyim; O, beni hidayete erdirecektir.”'),
(3888, 37, 100, '“Rabbim, bana salihlerden (olan bir çocuk) armağan et.”'),
(3889, 37, 101, 'Biz de onu halim bir çocukla müjdeledik.'),
(3890, 37, 102, 'Böylece (çocuk) onun yanında koşabilecek çağa erişince (İbrahim ona): “Oğlum” dedi. “Gerçekten ben seni rüyamda boğazlıyorken gördüm. Bir bak, sen ne düşünüyorsun.” (Oğlu İsmail) Dedi ki: “Babacığım, emrolunduğun şeyi yap. İnşaAllah, beni sabredenlerden bulacaksın.”'),
(3891, 37, 103, 'Sonunda ikisi de (Allah’ın emrine ve takdirine) teslim olup (babası, İsmail’i kurban etmek için) onu alnı üzerine yatırdı.'),
(3892, 37, 104, 'Biz ona: “Ey İbrahim” diye seslendik.'),
(3893, 37, 105, '“Gerçekten sen, rüyayı doğruladın. Şüphesiz Biz, ihsanda bulunanları böyle ödüllendiririz.”'),
(3894, 37, 106, 'Doğrusu bu, apaçık bir imtihandı.'),
(3895, 37, 107, 'Ve ona büyük bir kurbanı fidye olarak verdik.'),
(3896, 37, 108, 'Sonra gelenler arasında ona (hayırlı ve şerefli bir isim) bıraktık.'),
(3897, 37, 109, 'İbrahim’e selam olsun.'),
(3898, 37, 110, 'Biz, ihsanda bulunanları böyle ödüllendiririz.'),
(3899, 37, 111, 'Şüphesiz o, Bizim mü’min olan kullarımızdandır.'),
(3900, 37, 112, 'Biz ona, salihlerden bir peygamber olarak İshak’ı da müjdeledik.'),
(3901, 37, 113, 'Ona ve İshak’a bereketler verdik. İkisinin soyundan, ihsanda bulunan (muhsin olan) da var, açıkça kendi nefsine zulmeden de.'),
(3902, 37, 114, 'Andolsun, Biz Musa’ya ve Harun’a lütufta bulunduk.'),
(3903, 37, 115, 'Onları ve kavimlerini o büyük üzüntüden kurtardık.'),
(3904, 37, 116, 'Onlara yardım ettik, böylece üstün gelenler oldular.'),
(3905, 37, 117, 'Ve ikisine anlatımı-açık kitabı verdik.'),
(3906, 37, 118, 'Onları dosdoğru yola yöneltip-ilettik.'),
(3907, 37, 119, 'Sonra gelenler arasında da ikisine (hayırlı ve şerefli bir isim) bıraktık.'),
(3908, 37, 120, 'Musa’ya ve Harun’a selam olsun.'),
(3909, 37, 121, 'Şüphesiz Biz, ihsanda bulunanları böyle ödüllendiririz.'),
(3910, 37, 122, 'Şüphesiz ikisi, Bizim mü’min olan kullarımızdandılar.'),
(3911, 37, 123, 'Gerçekten İlyas da, gönderilmiş (peygamber)lerdendi.'),
(3912, 37, 124, 'Hani kendi kavmine demişti ki: “Siz korkup sakınmaz mısınız?”'),
(3913, 37, 125, '“Siz Ba’le tapıp da Yaratıcıların en güzeli (olan Allah’ı) mı bırakıyorsunuz?”'),
(3914, 37, 126, '“Allah ki, sizin de Rabbiniz, önceki atalarınızın da Rabbidir.”'),
(3915, 37, 127, 'Fakat onu yalanladılar; bundan dolayı gerçekten onlar, (azap için getirilip) hazır bulundurulacak olanlardır.'),
(3916, 37, 128, 'Ancak, muhlis olan kullar başka.'),
(3917, 37, 129, 'Sonra gelenler arasında ona (hayırlı ve şerefli bir isim) bıraktık.'),
(3918, 37, 130, 'İlyas’a selam olsun.'),
(3919, 37, 131, 'Şüphesiz Biz, ihsanda bulunanları böyle ödüllendiririz.'),
(3920, 37, 132, 'Şüphesiz o, Bizim mü’min olan kullarımızdandı.'),
(3921, 37, 133, 'Gerçekten Lût da gönderilmiş (elçi)lerdendi.'),
(3922, 37, 134, 'Hani Biz onu ve ailesini topluca kurtarmıştık.'),
(3923, 37, 135, 'Geride bırakılanlar arasında bir yaşlı kadın dışında.'),
(3924, 37, 136, 'Sonra geride kalanları yerle bir ettik.'),
(3925, 37, 137, 'Siz onların üstünden muhakkak geçip gidiyorsunuz; sabah vakti.'),
(3926, 37, 138, 'Ve geceleyin. Yine de akıllanmayacak mısınız?'),
(3927, 37, 139, 'Şüphesiz Yunus da gönderilmiş (elçi)lerdendi.'),
(3928, 37, 140, 'Hani o, dolu bir gemiye kaçmıştı.'),
(3929, 37, 141, 'Böylece kur’aya katılmıştı da, kaybedenlerden olmuştu.'),
(3930, 37, 142, 'Derken onu balık yutmuştu, oysa o kınanmıştı.'),
(3931, 37, 143, 'Eğer (Allah’ı çokça) tesbih edenlerden olmasaydı,'),
(3932, 37, 144, 'Onun karnında (insanların) dirilip-kaldırılacakları güne kadar kalakalmıştı.'),
(3933, 37, 145, 'Sonunda o hasta bir durumdayken çıplak bir yere (sahile) attık.'),
(3934, 37, 146, 'Ve üzerine, sık-geniş yaprakla (kabağa benzer) türden bir ağaç bitirdik.'),
(3935, 37, 147, 'Onu yüzbin veya (sayısı) daha da artan (bir topluluk)a (peygamber olarak) gönderdik.'),
(3936, 37, 148, 'Sonunda ona iman ettiler, Biz de onları bir süreye kadar yararlandırdık.'),
(3937, 37, 149, 'Şimdi sen onlara sor: -Kızlar senin Rabbinin, erkek çocuklar onların mı?'),
(3938, 37, 150, 'Yoksa onlar, şahidlik etmekteyken Biz melekleri dişiler olarak mı yarattık?'),
(3939, 37, 151, 'Dikkat edin; gerçekten onlar, düzdükleri yalanlardan dolayı derler ki:'),
(3940, 37, 152, '“Allah doğurdu.” Onlar, hiç şüphesiz, muhakkak yalan söyleyenlerdir.'),
(3941, 37, 153, '(Allah,) Kızları, erkek çocuklara tercih mi etmiş?'),
(3942, 37, 154, 'Size ne oluyor, nasıl hüküm veriyorsunuz?'),
(3943, 37, 155, 'Hiç mi öğüt alıp-düşünmüyorsunuz?'),
(3944, 37, 156, 'Yoksa sizin apaçık olan bir deliliniz mi var?'),
(3945, 37, 157, 'Eğer doğru söylüyorsanız, öyleyse getirin kitabınızı.'),
(3946, 37, 158, 'Onlar, Kendisi\'yle (Allah ile) cinler arasında bir soy-bağı kurdular. Oysa andolsun, cinler de onların gerçekten (azap için getirilip) hazır bulundurulacaklarını bilmişlerdir.'),
(3947, 37, 159, 'Onların nitelendirdiklerinden Allah Yücedir.'),
(3948, 37, 160, 'Ancak muhlis olan kullar başka.'),
(3949, 37, 161, 'Artık siz de, tapmakta olduklarınız da.'),
(3950, 37, 162, 'O’na karşı kimseyi fitneye sürükleyecek değilsiniz.'),
(3951, 37, 163, 'Ancak kendisi çılgınca yanan ateşe girecek olan başka (onu sürüklersiniz).'),
(3952, 37, 164, '(Melekler der ki:) “Bizden her birimiz için belli bir makam vardır.”'),
(3953, 37, 165, '“Biziz, o saflar halinde dizilmiş olanlar, gerçekten biziz.”'),
(3954, 37, 166, '“Biziz, o tesbih edenler de, gerçekten biziz.”'),
(3955, 37, 167, 'Onlar (putatapıcılar), her ne kadar şöyle diyor idiyseler de:'),
(3956, 37, 168, '”Eğer yanımızda öncekilerden bir zikir (kitap) bulunmuş olsaydı.”'),
(3957, 37, 169, '“Gerçekten bizler de, Allah’ın muhlis olan kullarından olurduk.”'),
(3958, 37, 170, 'Fakat (kitap gelince) onu tanımayıp-küfrettiler; yakında bileceklerdir.'),
(3959, 37, 171, 'Andolsun, (peygamber olarak) gönderilen kullarımıza (şu) sözümüz geçmiştir:'),
(3960, 37, 172, 'Gerçekten onlar, muhakkak nusret (yardım ve zafer) bulacaklardır.'),
(3961, 37, 173, 'Ve hiç şüphesiz; Bizim ordularımız, üstün gelecek olanlar onlardır.'),
(3962, 37, 174, 'Öyleyse sen, bir süreye kadar onlardan yüz çevir.'),
(3963, 37, 175, 'Ve onları seyret; (azabı) yakında göreceklerdir.'),
(3964, 37, 176, 'Şimdi onlar, Bizim azabımızı mı acele istiyorlar?'),
(3965, 37, 177, 'Fakat (azap) onların sahasına indiği zaman uyarılıp-korkutulanların sabahı ne kötü olur.'),
(3966, 37, 178, 'Sen bir süreye kadar onlardan yüz çevir.'),
(3967, 37, 179, 'Ve seyret; (azabı) yakında göreceklerdir.'),
(3968, 37, 180, 'Üstünlük ve güç (izzet) sahibi olan senin Rabbin, onların nitelendirdiklerinden Yücedir.'),
(3969, 37, 181, 'Gönderilmiş (peygamber)lere selam olsun.'),
(3970, 37, 182, 'Ve alemlerin Rabbi olan Allah’a hamd olsun.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Sad, Zikir dolu Kur\'an\'a andolsun;'),
(3972, 38, 2, 'Hayır; o inkar edenler (boş) bir gurur ve bir parçalanma içindedirler.'),
(3973, 38, 3, 'Biz kendilerinden önce, nice kuşakları yıkıma uğrattık da onlar feryad ettiler; ancak (artık) kurtulma zamanı değildi.'),
(3974, 38, 4, 'İçlerinden kendilerine bir uyarıcının gelmesine şaştılar. Kafirler dedi ki: "Bu, yalan söyleyen bir büyücüdür."'),
(3975, 38, 5, '"İlahları bir tek ilah mı yaptı? Doğrusu bu, şaşırtıcı bir şey."'),
(3976, 38, 6, 'Onlardan önde gelen bir grup: "Yürüyün, ilahlarınıza karşı (bağlılıkta) kararlı olun; çünkü asıl istenen budur" diye çekip gitti.'),
(3977, 38, 7, '"Biz bunu, diğer dinde işitmedik, bu, içi boş bir uydurmadan başkası değildir."'),
(3978, 38, 8, '"Zikir (Kur\'an), içimizden ona mı indirildi?" Hayır, onlar Benim zikrimden bir kuşku içindedirler. Hayır, onlar henüz Benim azabımı tatmamışlardır.'),
(3979, 38, 9, 'Yoksa, güçlü ve üstün olan, karşılıksız bağışlayan Rabbinin hazineleri onların yanında mıdır?'),
(3980, 38, 10, 'Yoksa göklerin, yerin ve bu ikisi arasında bulunanların mülkü onların mı? Öyleyse, sebepler içinde (bir imkan ve güç bularak göğe) yükselsinler.'),
(3981, 38, 11, 'Onlar, burada (çeşitli) fırkalardan olma bozguna uğratılmış bir ordu(durlar).'),
(3982, 38, 12, 'Onlardan önce Nuh kavmi, Ad ve kazıklar sahibi Firavun da yalanlamıştı.'),
(3983, 38, 13, 'Semud, Lut kavmi ile Eyke halkı da. İşte onlar (Allah\'a karşı isyanda birleşen ve güç toplayan) fırkalar(dı).'),
(3984, 38, 14, 'Hepsi de elçileri yalanladılar, böylece azapla-sonuçlandırmam (onlara) hak oldu.'),
(3985, 38, 15, 'Bunlar da, (geldiğinde) bir anlık gecikmesi bile olmayan bir tek çığlıktan başkasını gözetlemiyorlar.'),
(3986, 38, 16, '(Alaylı alaylı) Dediler ki: "Rabbimiz, hesap gününden önce (azaptan bize vadettiğin) payımızı çabuklaştırıver."'),
(3987, 38, 17, 'Sen onların söylediklerine karşı sabret ve Bizim güç sahibi kulumuz Davud\'u hatırla; çünkü o, (her tutum ve davranışında Allah\'a) yönelen biriydi.'),
(3988, 38, 18, 'Doğrusu Biz dağlara boyun eğdirdik, akşam ve sabah kendisiyle birlikte (Allah\'ı) tesbih ederlerdi.'),
(3989, 38, 19, 'Ve toplanıp gelen kuşları da. Hepsi onunla (Allah\'ı tesbih etmede uyum içinde) yönelip-dönmekte olanlar idi.'),
(3990, 38, 20, 'Onun mülkünü güçlendirmiştik. Ona hikmet ve anlatım çarpıcılığını vermiştik.'),
(3991, 38, 21, 'Sana o davacıların haberi geldi mi? Hani mihraba (Davud\'un bulunduğu yere girmek için) yüksek duvardan tırmanmışlardı.'),
(3992, 38, 22, 'Davud\'a girdiklerinde, o, onlardan ürkmüştü; dediler ki: "Korkma, iki davacıyız, birimiz diğerimize haksızlıkta bulundu. Şimdi sen aramızda hak ile hükmet, kararında zulme sapma ve bizi doğru yolun ortasına yöneltip-ilet."'),
(3993, 38, 23, '"Bu benim kardeşimdir, doksan dokuz koyunu vardır, benimse bir tek koyunum var. Buna rağmen "Onu da benim payıma (koyunlarıma) kat" dedi ve bana, konuşmada üstün geldi."'),
(3994, 38, 24, '(Davud) Dedi ki: "Andolsun senin koyununu, kendi koyunlarına (katmak) istemekle sana zulmetmiştir. Doğrusu, (emek ve mali güçlerini) birleştirip katan (ortak)lardan çoğu, birbirlerine karşı tecavüz ederler; ancak iman edip salih amellerde bulunanlar başka. Onlar da ne kadar azdır." Davud, gerçekten Bizim onu imtihan ettiğimizi sandı, böylece Rabbinden bağışlanma diledi ve rüku ederek yere kapandı ve (Bize gönülden) yönelip-döndü.'),
(3995, 38, 25, 'Böylece onu bağışladık. Şüphesiz onun Bizim Katımız\'da gerçekten bir yakınlığı ve varılacak güzel bir yeri vardır.'),
(3996, 38, 26, '"Ey Davud, gerçek şu ki, Biz seni yeryüzünde bir halife kıldık. Öyleyse insanlar arasında hak ile hükmet, istek ve tutkulara (hevaya) uyma; sonra seni Allah\'ın yolundan saptırır. Şüphesiz Allah’ın yolundan sapanlara, hesap gününü unutmalarından dolayı şiddetli bir azap vardır."'),
(3997, 38, 27, 'Biz gökyüzünü, yeryüzünü ve ikisi arasında bulunan şeyleri batıl olarak yaratmadık. Bu, inkar edenlerin zannıdır. Ateşten (görecekleri azaptan) dolayı vay o inkar edenlere.'),
(3998, 38, 28, 'Yoksa Biz, iman edip salih amellerde bulunanları yeryüzünde bozgunculuk çıkaranlar gibi (bir) mi tutacağız? Ya da muttakileri facirler gibi (bir) mi tutacağız?'),
(3999, 38, 29, '(Bu Kur\'an,) Ayetlerini, iyiden iyiye düşünsünler ve temiz akıl sahipleri öğüt alsınlar diye sana indirdiğimiz mübarek bir Kitap\'tır.'),
(4000, 38, 30, 'Biz Davud\'a Süleyman\'ı armağan ettik. O, ne güzel kuldu. Çünkü o, (daima Allah\'a) yönelip-dönen biriydi.'),
(4001, 38, 31, 'Hani ona akşama yakın, bir ayağını tırnağı üstüne diken, öbür üç ayağıyla toprağı kazıyan, yağız atlar sunulmuştu.'),
(4002, 38, 32, 'O da demişti ki: "Gerçekten ben, mal (veya at) sevgisini Rabbimi zikretmekten dolayı tercih ettim." Sonunda bu atlar (koştular ve toz) perdesinin arkasına saklandılar.'),
(4003, 38, 33, '"Onları bana geri getirin" (dedi). Sonra (onların) bacaklarını ve boyunlarını okşamaya başladı.'),
(4004, 38, 34, 'Andolsun, Biz Süleyman\'ı imtihan ettik, tahtının üstünde bir ceset bıraktık. Sonra (eski durumuna) döndü.'),
(4005, 38, 35, '"Rabbim, beni bağışla ve benden sonra hiç kimseye nasib olmayan bir mülkü bana armağan et. Şüphesiz Sen, karşılıksız armağan edensin."'),
(4006, 38, 36, 'Böylece rüzgarı onun buyruğu altına verdik. Onun emriyle dilediği yöne yumuşakça eserdi.'),
(4007, 38, 37, 'Şeytanları da; her bina ustasını ve dalgıç olanı.'),
(4008, 38, 38, 'Ve (kötülük yapmamaları için) sağlam kementlerle birbirine bağlanmış diğerlerini.'),
(4009, 38, 39, '"İşte bu, bizim vergimizdir. (Ey Süleyman) Artık sen de hesaba vurmaksızın, ver ya da tut."'),
(4010, 38, 40, 'Şüphesiz, onun Bizim Katımız\'da gerçekten bir yakınlığı ve varılacak güzel bir yeri vardır.'),
(4011, 38, 41, 'Kulumuz Eyyub\'u da hatırla. Hani o: "Herhalde şeytan, bana kahredici bir acı ve azap dokundurdu" diye Rabbine seslenmişti.'),
(4012, 38, 42, '"Ayağını depret. İşte yıkanacak ve içecek soğuk (su, diye vahyettik.).'),
(4013, 38, 43, 'Katımız\'dan ona bir rahmet ve temiz akıl sahiplerine bir öğüt olmak üzere ailesini ve onlarla birlikte bir benzerini de bağışladık.'),
(4014, 38, 44, '"Ve eline bir deste (sap) al, böylece onunla vur ve andını bozma." Gerçekten, Biz onu sabredici bulduk. O, ne güzel kuldu. Çünkü o, (daima Allah\'a) yönelip-dönen biriydi.'),
(4015, 38, 45, 'Güç ve basiret sahibi olan kullarımız İbrahim\'i, İshak\'ı ve Yakub\'u da hatırla.'),
(4016, 38, 46, 'Gerçekten Biz onları, katıksızca (ahiretteki asıl) yurdu düşünüp-anan ihlas sahipleri kıldık.'),
(4017, 38, 47, 'Ve gerçekten onlar, Bizim Katımız\'da seçkinlerden ve hayırlı olanlardandır.'),
(4018, 38, 48, 'İsmail\'i, Elyesa\'ı ve Zülkifl\'i de hatırla. Hepsi de hayırlı olanlardandır.'),
(4019, 38, 49, 'Bu, bir zikirdir. Şüphesiz muttakiler için, elbette varılacak güzel bir yer vardır.'),
(4020, 38, 50, 'Adn cennetleri; kapılar onlara açılmıştır.'),
(4021, 38, 51, 'İçinde yaslanıp-dayanmışlardır; orda birçok meyve ve şarap istemektedirler.'),
(4022, 38, 52, 'Ve yanlarında bakışlarını yalnızca eşlerine çevirmiş yaşıt kadınlar vardır.'),
(4023, 38, 53, 'İşte hesap günü size va\'dedilen budur.'),
(4024, 38, 54, 'Şüphesiz bu, Bizim rızkımızdır, bitip tükenmesi de yok.'),
(4025, 38, 55, 'Bu (böyle işte); gerçekten azgınlar için de muhakkak varılacak kötü bir yer vardır.'),
(4026, 38, 56, 'Cehennem; onlar oraya girerler; ne kötü bir yataktır o.'),
(4027, 38, 57, 'İşte bu; tatsınlar onu: Kaynar su ve irin.'),
(4028, 38, 58, 'Ve onun şeklinden başka, çift çift (olan daha beter azaplar) vardır.'),
(4029, 38, 59, '(Müşrik olan hakim güçlere:) "İşte bu(nlar) da sizinle birlikte (küfür ve zulümde) göğüs gerenlerdir. Onlara bir merhaba (bile) yok. Çünkü onlar ateşe gireceklerdir." (denilir).'),
(4030, 38, 60, '(Onlara uyanlar) Derler ki: "Hayır, sizler; asıl size bir merhaba yok. Bunu (azabı) siz bizim önümüze sürdünüz. Ne kötü bir durak."'),
(4031, 38, 61, 'Derler ki: "Rabbimiz, kim bunu bizim önümüze sürdüyse, ateşteki azabını kat kat arttır."'),
(4032, 38, 62, 'Ve derler ki: "Bize ne oluyor ki, kendilerini şerir (kötü)lerden saydığımız adamları göremiyoruz."'),
(4033, 38, 63, 'Biz onları bir alay konusu edinmiştik; yoksa gözler mi onlardan kaydı?"'),
(4034, 38, 64, 'Bu, cehennem halkının birbiriyle çekişmesi kesin bir gerçektir.'),
(4035, 38, 65, 'De ki: "Ben, yalnızca bir uyarıcıyım. Bir olan, kahreden Allah\'tan başka bir İlah yoktur."'),
(4036, 38, 66, '"Göklerin, yerin ve ikisi arasında bulunanların Rabbidir, üstün ve güçlü olan, bağışlayandır."'),
(4037, 38, 67, 'De ki: "Bu (Kur\'an), büyük bir haberdir."'),
(4038, 38, 68, 'Sizler ise, ondan yüz çeviriyorsunuz.'),
(4039, 38, 69, '"Mele-i Ala (yüce topluluk) tartışıp dururken, benim hiçbir bilgim yoktur."'),
(4040, 38, 70, '"Bana ancak, yalnızca apaçık bir uyarıcı olduğum vahyolunmaktadır."'),
(4041, 38, 71, 'Hani Rabbin meleklere: "Gerçekten Ben, çamurdan bir beşer yaratacağım" demişti.'),
(4042, 38, 72, '"Onu bir biçime sokup, ona Ruhum\'dan üflediğim zaman siz onun için hemen secdeye kapanın."'),
(4043, 38, 73, 'Meleklerin hepsi topluca secde etti;'),
(4044, 38, 74, 'Yalnız İblis hariç. O büyüklük tasladı ve kafirlerden oldu.'),
(4045, 38, 75, '(Allah) Dedi ki: "Ey İblis, iki elimle yarattığıma seni secde etmekten alıkoyan neydi? Büyüklendin mi, yoksa yüksekte olanlardan mı oldun?"'),
(4046, 38, 76, 'Dedi ki: "Ben ondan daha hayırlıyım; sen beni ateşten yarattın, onu ise çamurdan yarattın."'),
(4047, 38, 77, '(Allah) Dedi ki: "Öyleyse ordan (cennetten) çık, artık sen kovulmuş bulunmaktasın."'),
(4048, 38, 78, '"Ve şüphesiz, din (kıyametteki hesap) gününe kadar Benim lanetim senin üzerinedir."'),
(4049, 38, 79, 'Dedi ki: "Rabbim, öyleyse onların dirilecekleri güne kadar bana süre tanı."'),
(4050, 38, 80, 'Dedi ki: "O halde, süre tanınanlardansın."'),
(4051, 38, 81, '"Bilinen vaktin gününe kadar."'),
(4052, 38, 82, 'Dedi ki: "Senin izzetin adına andolsun, ben, onların tümünü mutlaka azdırıp-kışkırtacağım."'),
(4053, 38, 83, '"Ancak onlardan, muhlis olan kulların hariç."'),
(4054, 38, 84, '(Allah) "İşte bu haktır ve Ben hakkı söylerim" dedi.'),
(4055, 38, 85, '"Andolsun, senden ve içlerinde sana tabi olacak olanlardan tümüyle cehennemi dolduracağım."'),
(4056, 38, 86, '(Ey Peygamber) De ki: "Ben, buna karşı sizden bir ücret istemiyorum ve (kendiliğinden) bir yükümlülük getirenlerden de değilim."'),
(4057, 38, 87, '"O (Kur\'an), alemler için yalnızca bir zikir (öğüt ve hatırlatma)dir."'),
(4058, 38, 88, '"Gerçekten onun haberini bir zaman sonra öğreneceksiniz."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, '(Bu) Kitab\'ın indirilmesi, üstün ve güçlü olan, hüküm ve hikmet sahibi Allah (katın)dandır.'),
(4060, 39, 2, 'Şüphesiz, sana bu Kitab\'ı hak ile indirdik; öyleyse sen de dini yalnızca O\'na halis kılarak Allah\'a ibadet et.'),
(4061, 39, 3, 'Haberin olsun; halis (katıksız) olan din yalnızca Allah\'ındır. O\'ndan başka veliler edinenler (şöyle derler:) "Biz, bunlara bizi Allah\'a daha fazla yaklaştırsınlar diye ibadet ediyoruz." Elbette Allah, kendi aralarında hakkında ihtilaf ettikleri şeylerden hüküm verecektir. Gerçekten Allah, yalancı, kafir olan kimseyi hidayete erdirmez.'),
(4062, 39, 4, 'Eğer Allah, çocuk edinmek isteseydi, yarattıklarından dilediğini elbette seçerdi. O, Yücedir; O, bir olan, kahredici olan Allah\'tır.'),
(4063, 39, 5, 'Gökleri ve yeri hak olarak yarattı. Geceyi gündüzün üstüne sarıp-örtüyor, gündüzü de gecenin üstüne sarıp-örtüyor. Güneşe ve aya boyun eğdirdi. Her biri adı konulmuş bir ecele (süreye) kadar akıp gitmektedir. Haberin olsun; üstün ve güçlü olan, bağışlayan O\'dur.'),
(4064, 39, 6, 'Sizi tek bir nefisten yarattı, sonra ondan kendi eşini var etti ve sizin için davarlardan sekiz çift indirdi. Sizi annelerinizin karınlarında, üç karanlık içinde, bir yaratılıştan sonra (bir başka) yaratılışa (dönüştürüp) yaratmaktadır. İşte Rabbiniz olan Allah budur, mülk O\'nundur. O\'ndan başka İlah yoktur. Buna rağmen nasıl çevriliyorsunuz?'),
(4065, 39, 7, 'Eğer inkar edecek olursanız, artık şüphesiz Allah size karşı hiçbir ihtiyacı olmayandır ve O, kulları için inkara rıza göstermez. Ve eğer şükrederseniz, sizin (yararınız) için ondan razı olur. Hiçbir günahkar, bir başkasının günah yükünü yüklenmez. Sonra Rabbinize döndürüleceksiniz, böylece yaptıklarınızı size haber verecektir. Şüphesiz O, sinelerin özünde saklı olanı bilendir.'),
(4066, 39, 8, 'İnsana bir zarar dokunduğu zaman, gönülden katıksızca yönelmiş olarak Rabbine dua eder. Sonra ona Kendinden bir nimet verdiği zaman, daha önce O\'na dua ettiğini unutur ve O\'nun yolundan saptırmak amacıyla Allah\'a eşler koşmaya başlar. De ki: "İnkarınla biraz (dünya zevklerinden) yararlan; çünkü sen, ateşin halkındansın."'),
(4067, 39, 9, 'Yoksa o, gece saatinde kalkıp da secde ederek ve kıyama durarak gönülden itaat (ibadet) eden, ahiretten sakınan ve Rabbinin rahmetini umud eden (gibi) midir? De ki: "Hiç bilenlerle bilmeyenler bir olur mu? Şüphesiz, temiz akıl sahipleri öğüt alıp-düşünürler."'),
(4068, 39, 10, 'De ki: "Ey iman eden kullarım, Rabbinizden sakının. Bu dünyada iyilik edenler için bir iyilik vardır. Allah\'ın arz\'ı geniştir. Ancak sabredenlere ecirleri hesapsızca ödenir."'),
(4069, 39, 11, 'De ki: "Ben, dini yalnızca O\'na halis kılarak Allah\'a ibadet etmekle emrolundum."'),
(4070, 39, 12, '"Ve ben, Müslümanların ilki olmakla da emrolundum."'),
(4071, 39, 13, 'De ki: "Ben, Rabbime isyan ettiğim takdirde, büyük bir günün azabından korkarım."'),
(4072, 39, 14, 'De ki: "Ben dinimi yalnızca O\'na halis kılarak Allah\'a ibadet ederim."'),
(4073, 39, 15, '"Siz, O\'nun dışında dilediklerinize ibadet edin." De ki: "Gerçekten hüsrana uğrayanlar, kıyamet günü hem kendilerini, hem yakınlarını hüsrana uğratanlardır. Haberiniz olsun; bu apaçık olan hüsranın kendisidir."'),
(4074, 39, 16, 'Onların üstlerinde ateşten tabakalar, altlarında da tabakalar vardır. İşte Allah, Kendi kullarını bununla tehdit edip-korkutuyor. Ey kullarım öyleyse Benden sakının.'),
(4075, 39, 17, 'Tağut\'a kulluk etmekten kaçınan ve Allah\'a içten yönelenler ise; onlar için bir müjde vardır, öyleyse kullarıma müjde ver.'),
(4076, 39, 18, 'Ki onlar, sözü işitirler ve en güzeline uyarlar. İşte onlar, Allah\'ın kendilerini hidayete erdirdiği kimselerdir ve onlar, temiz akıl sahipleridir.'),
(4077, 39, 19, 'Azap sözü kendisi üzerinde hak olmuş kimse mi (onlarla bir tutulur)? Ateşte olanı artık sen mi kurtaracaksın?'),
(4078, 39, 20, 'Ancak Rablerinden korkup-sakınanlar ise; onlara yüksek köşkler vardır, onların üstünde de yüksek köşkler bina edilmiştir. Onların altında ırmaklar akmaktadır. (Bu,) Allah\'ın va\'didir. Allah, va\'dinden dönmez.'),
(4079, 39, 21, 'Görmüyor musun; gerçekten Allah, gökyüzünden su indirdi de onu yerin içindeki kaynaklara yürütüp-geçirdi. Sonra onunla çeşitli renklerde ekinler çıkarıyor. Sonra kurumaya başlar, böylece onu sararmış görürsün. Sonra da onu kurumuş kırıntılar kılıyor. Şüphesiz bunda, temiz akıl sahipleri için gerçekten öğüt alınacak bir ders (zikr) vardır.'),
(4080, 39, 22, 'Allah, kimin göğsünü İslam\'a açmışsa, artık o, Rabbinden bir nur üzerinedir, (öyle) değil mi? Fakat Allah\'ın zikrinden (yana) kalpleri katılaşmış olanların vay haline. İşte onlar, apaçık bir sapıklık içindedirler.'),
(4081, 39, 23, 'Allah, müteşabih (benzeşmeli), ikişerli bir Kitap olarak sözün en güzelini indirdi. Rablerine karşı içleri titreyerek-korkanların O’ndan derileri ürperir. Sonra onların derileri ve kalpleri Allah\'ın zikrine (karşı) yumuşar-yatışır. İşte bu, Allah\'ın yol göstermesidir, onunla dilediğini hidayete erdirir. Allah, kimi saptırırsa, artık onun için de bir yol gösterici yoktur.'),
(4082, 39, 24, 'Kıyamet günü o kötü azaptan kendini yüzü ile kim koruyabilecek? Ve zalimlere "Kazandığınızı tadın" denmiştir.'),
(4083, 39, 25, 'Onlardan öncekiler de yalanladı; böylece azap onlara hiç şuurunda olmadıkları bir yerden gelip-çattı.'),
(4084, 39, 26, 'Artık Allah, onlara dünya hayatında \'horluğu ve aşağılanmayı\' taddırdı. Eğer bilmiş olsalardı, ahiretin azabı gerçekten daha büyüktür.'),
(4085, 39, 27, 'Andolsun, Biz bu Kur\'an\'da, belki öğüt alıp-düşünürler diye, insanlar için her bir örnekten verdik.'),
(4086, 39, 28, 'Çarpıklığı olmayan Arapça bir Kur\'an\'dır (bu). Umulur ki sakınırlar.'),
(4087, 39, 29, 'Allah (ortak koşanlar için) bir örnek verdi: Kendisi hakkında uyumsuz ve geçimsiz bulunan, sahipleri de çok ortaklı olan (köle) bir adam ile yalnızca bir kişiye teslim olmuş bir adam. Bu ikisinin durumu bir olur mu? Hamd, Allah\'ındır. Hayır onların çoğu bilmiyorlar.'),
(4088, 39, 30, 'Gerçek şu ki, sen de öleceksin, onlar da öleceklerdir.'),
(4089, 39, 31, 'Sonra şüphesiz sizler, kıyamet günü Rabbinizin huzurunda davalaşacaksınız.'),
(4090, 39, 32, 'Allah\'a karşı yalan söyleyenden ve kendisine geldiğinde doğruyu (Kur\'an\'ı) yalanlayandan daha zalim kimdir? Kafirler için cehennemde bir konaklama yeri mi yok?'),
(4091, 39, 33, 'Doğruyu getiren ve doğrulayanlara gelince; işte onlar muttaki (takva sahibi) olanlardır.'),
(4092, 39, 34, 'Rableri Katında dileyecekleri herşey onlarındır. İşte bu, ihsanda bulunanların ödülüdür.'),
(4093, 39, 35, 'Çünkü Allah, onların (dünyada) yaptıklarının en kötüsünü temizleyip-giderecek ve yaptıklarının en güzeliyle ecirlerini verecektir.'),
(4094, 39, 36, 'Allah, kuluna yeterli değil mi? Seni O\'ndan başkalarıyla korkutuyorlar. Allah, kimi saptırırsa, artık onun için bir yol gösterici yoktur.'),
(4095, 39, 37, 'Allah, kimi hidayete erdirirse, onun için bir saptırıcı yoktur. Allah, intikam sahibi, güçlü ve üstün olan değil midir?'),
(4096, 39, 38, 'Andolsun, onlara: "Gökleri ve yeri kim yarattı?" diye soracak olsan, elbette "Allah" diyecekler. De ki: "Gördünüz mü-haber verin; Allah\'tan başka taptıklarınız, eğer Allah bana bir zarar dileyecek olsa, O\'nun zararını kaldırabilirler mi? Ya da bana bir rahmet vermeyi istese, O\'nun rahmetini tutup-önleyebilecekler mi" De ki: "Allah, bana yeter. Tevekkül edecek olanlar, O\'na tevekkül etsinler."'),
(4097, 39, 39, 'De ki: "Ey kavmim, üzerinde bulunduğunuz duruma göre yapın-edin; elbette ben de yapıp-ederim. Artık yakında öğreneceksiniz."'),
(4098, 39, 40, 'Kendisini aşağılık kılan azap kime geliyor ve kesintisiz azap kimin üzerine çöküp-kaçınılmaz oluyor?'),
(4099, 39, 41, 'Şüphesiz, sana Biz Kitab\'ı insanlar için hak olmak üzere indirdik. Artık kim hidayete ererse, bu kendi lehinedir; kim saparsa, o da kendi aleyhine sapmış olur. Sen onların üzerinde vekil değilsin.'),
(4100, 39, 42, 'Allah, ölecekleri zaman canlarını alır; ölmeyeni de uykusunda (bir tür ölüme sokar). Böylece, kendisi hakkında ölüm kararı verilmiş olanı(n ruhunu) tutar, öbürüsünü ise adı konulmuş bir ecele kadar salıverir. Şüphesiz bunda, düşünebilen bir kavim için gerçekten ayetler vardır.'),
(4101, 39, 43, 'Yoksa Allah\'tan başka şefaat ediciler mi edindiler? De ki: "Ya onlar, hiçbir şeye malik değillerse ve akıl da erdiremiyorlarsa?"'),
(4102, 39, 44, 'De ki: "Şefaatin tümü Allah\'ındır. Göklerin ve yerin mülkü O\'nundur. Sonra O\'na döndürüleceksiniz."'),
(4103, 39, 45, 'Sadece Allah anıldığı zaman, ahirete inanmayanların kalbi öfkeyle kabarır. Oysa O\'ndan başkaları anıldığında hemen sevince kapılırlar.'),
(4104, 39, 46, 'De ki: "Ey gökleri ve yeri yaratan, gaybı ve müşahede edilebileni bilen Allah\'ım. Anlaşmazlığa düştükleri şeylerde, kullarının arasında sen hüküm vereceksin."'),
(4105, 39, 47, 'Eğer yeryüzünde olanların tümü ve bununla birlikte bir katı daha zalimlerin olmuş olsaydı, kıyamet günü o kötü azaptan (kurtulmak amacıyla) gerçekten bunları fidye olarak verirlerdi. Oysa, onların hiç hesaba katmadıkları şeyler, Allah\'tan kendileri için açığa çıkmıştır.'),
(4106, 39, 48, 'Kazandıkları kötülükler, kendileri için açığa çıkmıştır ve alay konusu edindikleri şey de kendilerini çepeçevre kuşatmıştır.'),
(4107, 39, 49, 'İnsana bir zarar dokunduğu zaman, Bize dua eder; sonra tarafımızdan ona bir nimet ihsan ettiğimizde, der ki: "Bu, bana ancak bir bilgi(m) dolayısıyla verildi." Hayır; bu bir fitne (kendisini bir deneme)dir. Ancak çoğu bilmiyorlar.'),
(4108, 39, 50, 'Bunu kendilerinden öncekiler de söylemişti; ama kazandıkları şeyler onlara hiçbir yarar sağlamadı.'),
(4109, 39, 51, 'Böylece, kazandıkları kötülükler(in acı sonucu) onlara isabet etti. Bunlardan zulmetmiş olanlara da, kazandıkları kötülükler isabet edecektir. Ve onlar (bunu kendilerine uygulamaktan Allah\'ı) aciz bırakabilecekler değildirler.'),
(4110, 39, 52, 'Onlar bilmiyorlar mı ki, gerçekten Allah, dilediğine rızkı genişletip-yayar ve (dilediğine) kısar da. Şüphesiz bunda, iman eden bir kavim için gerçekten ayetler vardır.'),
(4111, 39, 53, '(Benden onlara) De ki: "Ey kendi aleyhlerinde olmak üzere ölçüyü taşıran kullarım. Allah\'ın rahmetinden umut kesmeyin. Şüphesiz Allah, bütün günahları bağışlar. Çünkü O, bağışlayandır, esirgeyendir."'),
(4112, 39, 54, 'Azap size gelip çatmadan evvel, Rabbinize yönelip-dönün ve O\'na teslim olun. Sonra size yardım edilmez.'),
(4113, 39, 55, 'Rabbinizden, size indirilenin en güzeline uyun; siz hiç şuurunda değilken, azap apansız size gelip çatmadan evvel.'),
(4114, 39, 56, 'Kişinin (yana yakıla) şöyle diyeceği (gün): "Allah yanında (kullukta) yaptığım kusurlardan dolayı yazıklar olsun (bana) doğrusu ben, (Allah\'ın diniyle) alay edenlerdendim."'),
(4115, 39, 57, 'Veya: "Gerçekten Allah bana hidayet verseydi, elbette muttakilerden olurdum" diyeceği,'),
(4116, 39, 58, 'Ya da azabı gördüğü zaman: "Benim için bir kere daha (dünyaya dönme fırsatı) olsaydı da, ihsan edenlerden olsaydım" (diyeceği günden sakının).'),
(4117, 39, 59, '"Hayır, Benim ayetlerim sana gelmişti, fakat sen onları yalanladın, büyüklüğe kapıldın ve kafirlerden oldun."'),
(4118, 39, 60, 'Kıyamet günü, Allah\'a karşı yalan söyleyenlerin yüzlerinin kapkara olduğunu görürsün. Büyüklenenler için cehennemde bir konaklama yeri mi yok?'),
(4119, 39, 61, 'Allah, takva sahiplerini (inanarak ve inançlarını uygulayarak) zafere ulaşmaları dolayısıyla kurtarır. Onlara kötülük dokunmaz ve onlar hüzne kapılmayacaklardır.'),
(4120, 39, 62, 'Allah, herşeyin Yaratıcısı\'dır. O, herşey üzerinde vekildir.'),
(4121, 39, 63, 'Göklerin ve yerin anahtarları O\'nundur. Allah\'ın ayetlerine (karşı) inkar edenler ise; işte onlar, hüsrana uğrayanlardır.'),
(4122, 39, 64, 'De ki: "Ey cahiller, bana Allah\'ın dışında bir başkasına mı kulluk etmemi emrediyorsunuz?"'),
(4123, 39, 65, 'Andolsun, sana ve senden öncekilere vahyolundu (ki): "Eğer şirk koşacak olursan, şüphesiz amellerin boşa çıkacak ve elbette sen, hüsrana uğrayanlardan olacaksın.'),
(4124, 39, 66, '"Hayır, artık (yalnızca) Allah\'a kulluk et ve şükredenlerden ol."'),
(4125, 39, 67, 'Onlar, Allah\'ın kadrini hakkıyla takdir edemediler. Oysa kıyamet günü yer, bütünüyle O\'nun avucu (kabzası)ndadır; gökler de sağ eliyle dürülüp-bükülmüştür. O, şirk koştuklarından münezzeh ve Yücedir.'),
(4126, 39, 68, 'Sur\'a üfürüldü; böylece Allah\'ın diledikleri dışında, göklerde ve yerde olanlar çarpılıp-yıkılıverdi. Sonra bir daha ona üfürüldü, artık onlar ayağa kalkmış durumda gözetliyorlar.'),
(4127, 39, 69, 'Yer, Rabbinin nuruyla parıldadı; (orta yere) kitap kondu; peygamberler ve şahidler getirildi ve aralarında hak ile hüküm verildi, onlar haksızlığa uğratılmazlar.'),
(4128, 39, 70, 'Her bir nefse yaptığının tam karşılığı verildi. O, onların işlediklerini daha iyi bilendir.'),
(4129, 39, 71, 'İnkar edenler, cehenneme bölük bölük sevkedildiler. Sonunda oraya geldikleri zaman, kapıları açıldı ve onlara (cehennemin) bekçileri dedi ki: "Size Rabbinizin ayetlerini okuyan ve bugünle karşılaşacağınızı (söyleyip) sizi uyaran elçiler gelmedi mi?" Onlar: "Evet." dediler. Ancak azap kelimesi kafirlerin üzerine hak oldu.'),
(4130, 39, 72, 'Dediler ki: "İçinde ebedi kalıcılar olarak cehennemin kapılarından (içeri) girin. Büyüklüğe kapılanların konaklama yeri ne kötüdür."'),
(4131, 39, 73, 'Rablerinden korkup-sakınanlar da, cennete bölük bölük sevkedildiler. Sonunda oraya geldikleri zaman, kapıları açıldı ve onlara (cennetin) bekçileri dedi ki: "Selam üzerinizde olsun, hoş ve temiz geldiniz. Ebedi kalıcılar olarak ona girin."'),
(4132, 39, 74, '(Onlar da) Dediler ki: "Bize olan va’dinde sadık kalan ve bizi bu yere mirasçı kılan Allah\'a hamd olsun ki, cennetten dilediğimiz yerde konaklayabiliriz. (Salih) Amellerde bulunanların ecri ne güzeldir.'),
(4133, 39, 75, 'Melekleri de arşın etrafını çevirmişler olarak Rablerini hamd ile tesbih ettiklerini görürsün. Aralarında hak ile hüküm verilmiştir ve: "Alemlerin Rabbine hamd olsun" denilmiştir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Ha, Mîm.'),
(4135, 40, 2, 'Bu Kitab\'ın indirilmesi, Aziz, Alim olan Allah\'tandır;'),
(4136, 40, 3, 'Günahı bağışlayan, tevbeyi kabul eden, cezası pek şiddetli olan ve lütuf sahibi (Allah\'tan). O\'ndan başka İlah yoktur. Dönüş O\'nadır.'),
(4137, 40, 4, 'Allah\'ın ayetleri konusunda inkar edenlerden başkası mücadele etmez. Öyleyse onların şehirlerde dönüp dolaşması seni aldatmasın.'),
(4138, 40, 5, 'Kendilerinden önce Nuh kavmi de yalanladı ve kendilerinden sonra (sayısı çok) fırkalar da. Her ümmet, kendi elçilerini (susturmak için) yakalamaya yeltendi. Hakkı, onunla yürürlükten kaldırmak için, \'batıla-dayanarak\' mücadeleye giriştiler. Ben de onları yakalayıverdim. Artık Benim cezalandırmam nasılmış?'),
(4139, 40, 6, 'Senin Rabbinin kafirler üzerindeki: "Gerçekten onlar ateşin halkıdır" sözü böylece hak oldu.'),
(4140, 40, 7, 'Arş\'ı yüklenmekte olanlar ve çevresinde bulunanlar, Rablerini hamd ile tesbih etmekte, O\'na iman etmekte ve iman edenlere mağfiret dilemektedirler: "Rabbimiz, rahmet ve ilim bakımından herşeyi kuşatıp-sardın, tevbe edenler ve Senin yoluna tabi olanlara mağfiret et ve onları cehennem azabından koru."'),
(4141, 40, 8, '"Rabbimiz, onları Adn cennetlerine sok ki onlara (bunu) va\'dettin; babalarından, eşlerinden ve soylarından salih olanları da. Gerçekten Sen, üstün ve güçlü olansın, hüküm ve hikmet sahibisin."'),
(4142, 40, 9, '"Ve onları kötülüklerden koru. O gün Sen, kimi kötülüklerden korumuşsan, gerçekten ona rahmet etmişsin. İşte büyük \'kurtuluş ve mutluluk\' budur.'),
(4143, 40, 10, 'Şüphesiz küfredenlere de (şöyle) seslenilir: "Allah\'ın gazablanması, elbette sizin kendi nefislerinize gazablanmanızdan daha büyüktür. Çünkü siz, imana çağrıldığınız zaman inkar ediyordunuz.'),
(4144, 40, 11, 'Dediler ki: "Rabbimiz, bizi iki kere öldürdün ve iki kere dirilttin; biz de günahlarımızı itiraf ettik. Şimdi çıkış için bir yol var mı?"'),
(4145, 40, 12, '"Sizin (durumunuz) böyledir. Çünkü bir olan Allah\'a çağırıldığınız zaman inkar ettiniz. O\'na ortak koşulduğunda inanıp-onayladınız. Artık hüküm, Yüce, büyük olan Allah\'ındır."'),
(4146, 40, 13, 'O, size ayetlerini gösteriyor ve sizin için gökten rızık indiriyor. İçten (Allah\'a) yönelenden başkası öğüt alıp-düşünmez.'),
(4147, 40, 14, 'Öyleyse, dini yalnızca O\'na halis kılanlar olarak Allah\'a dua (kulluk) edin; kafirler hoş görmese de.'),
(4148, 40, 15, 'Dereceleri yükselten Arş\'ın sahibi (Allah), \'toplanma ve buluşma\' günü ile uyarıp-korkutmak için, Kendi emrinden olan ruhu kullarından dilediğine indirir.'),
(4149, 40, 16, 'O gün, orta yere çıkarlar. Onlardan hiçbir şey Allah\'a karşı gizli kalmaz. (Allah sorar:) "Bugün mülk kimindir? Bir olan, Kahhar olan Allah\'ındır."'),
(4150, 40, 17, 'Bugün her bir nefis, kendi kazandığıyla karşılık görür. Bugün zulüm yoktur. Şüphesiz Allah, hesabı seri görendir.'),
(4151, 40, 18, 'Onları, yaklaşmakta olan güne karşı uyar; o zaman yürekler gırtlaklara dayanır, yutkunur dururlar. Zalimler için ne koruyucu bir dost, ne sözü yerine getirebilir bir şefaatçi yoktur.'),
(4152, 40, 19, '(Allah,) Gözlerin hainliklerini ve göğüslerin sakladıklarını bilir.'),
(4153, 40, 20, 'Allah hak ile hükmeder. Oysa O\'nu bırakıp taptıkları hiçbir şeye hükmedemezler. Şüphesiz Allah, işitendir, görendir.'),
(4154, 40, 21, 'Onlar, yeryüzünde gezip-dolaşmıyorlar mı ki, böylece kendilerinden öncekilerin nasıl bir sona uğradıklarını bir görsünler. Onlar, kuvvet ve yeryüzündeki eserleri bakımından kendilerinden daha üstün idiler. Fakat Allah, onları günahları dolayısıyla (azapla) yakalayıverdi. Onları Allah\'tan koruyacak kimse olmadı.'),
(4155, 40, 22, 'Çünkü gerçekten onlar, Resulleri kendilerine apaçık belgeler getirirdi; fakat onlar inkar ederlerdi. Bu yüzden Allah, onları (azapla) yakalayıverdi. Şüphesiz O, kuvvetli olandır, cezalandırması şiddetlidir.'),
(4156, 40, 23, 'Andolsun, Biz Musa\'yı ayetlerimizle ve apaçık bir delille gönderdik;'),
(4157, 40, 24, 'Firavun\'a, Haman\'a ve Karun\'a. Ama onlar: (Bu,) Yalan söyleyen bir büyücüdür" dediler.'),
(4158, 40, 25, 'Böylece, o, Katımız\'dan kendilerine bir hak ile geldiği zaman, dediler ki: "Onunla birlikte iman edenlerin erkek çocuklarını öldürün; kadınlarını ise sağ bırakın." Ancak kafirlerin hileli-düzeni boşa çıkmakta olandan başkası değildir.'),
(4159, 40, 26, 'Firavun dedi ki: "Bırakın beni, Musa\'yı öldüreyim de o (gitsin) Rabbine yalvarıp-yakarsın. Çünkü ben, sizin dininizi değiştirmesinden ya da yeryüzünde fesat çıkarmasından korkuyorum."'),
(4160, 40, 27, 'Musa dedi ki: "Gerçekten ben, hesap gününe iman etmeyen her mütekebbirden, benim de Rabbim, sizin de Rabbinize sığınırım."'),
(4161, 40, 28, 'Firavun ailesinden imanını gizlemekte olan mü\'min bir adam dedi ki: "Siz, benim Rabbim Allah\'tır diyen bir adamı öldürüyor musunuz? Oysa o, size Rabbinizden apaçık belgelerle gelmiş bulunuyor. Buna rağmen o eğer bir yalancı ise yalanı kendi aleyhinedir; ve eğer doğru sözlü ise, (o zaman da) size va\'dettiklerinin bir kısmı size isabet eder. Şüphesiz Allah, ölçüyü taşıran, çok yalan söyleyen kimseyi hidayete erdirmez."'),
(4162, 40, 29, '"Ey Kavmim, bugün mülk sizindir, yeryüzünde hüküm sahibi kimselersiniz. Fakat bize Allah’tan dayanılmaz bir azap gelecek olursa bize kim yardımcı olabilecek?" Firavun dedi ki: "Ben, size yalnızca gördüğümü (kendi görüşümü) gösteriyorum ve ben sizi doğru yoldan da başkasına yöneltmiyorum."'),
(4163, 40, 30, 'İman eden (adam) dedi ki: "Ey Kavmim, ben o fırkaların gününe benzer (bir günün felaketine uğrarsınız) diye korkuyorum."'),
(4164, 40, 31, '"Nuh kavmi, Ad, Semud ve onlardan sonra gelenlerin durumuna benzer (bir gün). Allah, kullar için zulüm istemez."'),
(4165, 40, 32, '"Ve ey kavmim, doğrusu ben sizin için o feryat (edeceğiniz kıyamet) gününden korkuyorum."'),
(4166, 40, 33, '"Arkanızı dönüp kaçacağınız gün; sizi Allah\'tan koruyacak yoktur. Allah, kimi saptırırsa artık onu doğruya yöneltecek bulunmaz."'),
(4167, 40, 34, '"Andolsun, daha önce Yusuf da size apaçık belgeler getirmişti. O zaman size getirdikleri hakkında kuşkuya kapılıp durmuştunuz. Sonunda o, vefat edince, demiştiniz ki; "Allah, ondan sonra kesin olarak bir elçi göndermez." İşte Allah, ölçüyü taşıran, şüpheci kimseyi böyle saptırır."'),
(4168, 40, 35, '"Ki onlar, Allah\'ın ayetleri konusunda kendilerine gelmiş bir delil bulunmaksızın mücadele edip dururlar. (Bu,) Allah Katında da, iman edenler katında da büyük bir öfke (sebebi)dir. İşte Allah, her mütekebbir zorbanın kalbini böyle mühürler."'),
(4169, 40, 36, 'Firavun (alayla) dedi ki: "Ey Haman, bana yüksek bir kule bina et; belki o yollara ulaşabilirim,"'),
(4170, 40, 37, '"Göklerin yollarına. Böylelikle Musa\'nın ilahına çıkabilirim. Çünkü ben, onun yalancı olduğunu sanıyorum." İşte Firavun\'a, kötü ameli böyle çekici kılındı ve yoldan alıkonuldu. Firavun\'un hileli-düzeni, \'yıkım ve kayıpta\' olmaktan başka (bir şey) olmadı.'),
(4171, 40, 38, 'İman eden (adam) dedi ki: "Ey Kavmim, siz bana tabi olun, ben sizi doğru yola iletip-yönelteyim."'),
(4172, 40, 39, '"Ey kavmim, gerçekten bu dünya hayatı, yalnızca bir meta (kısa süreli bir yararlanma)dır. Şüphesiz ahiret, (asıl) karar kılınan yurt odur."'),
(4173, 40, 40, '"Kim bir kötülük işlerse, kendi mislinden başkasıyla ceza görmez; kim de -erkek olsun, dişi olsun- bir mü\'min olarak salih bir amelde bulunursa, işte onlar, içinde hesapsız olarak rızıklandırılmak üzere cennete girerler."'),
(4174, 40, 41, '"Ey kavmim, ne oluyor ki ben sizi kurtuluşa çağırıyorken, siz beni ateşe çağırıyorsunuz."'),
(4175, 40, 42, '"Siz beni Allah\'a (karşı) inkar etmeye ve hakkında bilgim olmayan şeyleri O\'na şirk koşmaya çağırıyorsunuz. Ben ise sizi, üstün ve güçlü olan, bağışlayan (Allah\')a çağırıyorum.'),
(4176, 40, 43, '"İmkanı yok; gerçekten sizin beni kendisine çağırmakta olduğunuz şeyin, dünyada da, ahirette de çağrıda bulunma (yetkisi, gücü, değeri ve bağışlama)sı yoktur. Şüphesiz, bizim dönüşümüz Allah\'adır. Ölçüyü taşıranlar, onlar ateşin halkıdırlar."'),
(4177, 40, 44, '"İşte size söylediklerimi yakında hatırlayacaksınız. Ben de işimi Allah\'a bırakıyorum. Şüphesiz Allah, kulları pek iyi görendir."'),
(4178, 40, 45, 'Sonunda Allah, onların kurdukları hileli-düzenlerinin kötülüklerinden onu korudu ve Firavun\'un çevresini de azabın en kötüsü kuşatıverdi.'),
(4179, 40, 46, 'Ateş; sabah akşam, ona sunulurlar. Kıyamet-saatinin kopacağı gün: "Firavun çevresini, azabın en şiddetli olanına sokun" (denecek).'),
(4180, 40, 47, 'Ateşin içinde, iddialar öne sürüp karşılıklı tartışırlarken zayıf olanlar, büyüklenen (müstekbir)lere derler ki: "Gerçekten biz, size uymuş (teb\'anız) olan kimselerdik. Şimdi siz, ateşten bir parçasını olsun, bizden uzaklaştırabilir misiniz?'),
(4181, 40, 48, 'Büyüklenen (müstekbir)ler derler ki: "Biz hepimiz (ateşin) içindeyiz; gerçekten Allah, kullar arasında hüküm verdi (artık)."'),
(4182, 40, 49, 'Ateşin içinde olanlar, cehennem bekçilerine dediler ki: "Rabbinize dua edin; azaptan bir günü (olsun) bize hafifletsin."'),
(4183, 40, 50, '(Bekçiler:) "Size kendi Resulleriniz açık belgelerle gelmez miydi?" dediler. Onlar: "Evet" dediler. (Bekçiler:) "Şu halde siz dua edin" dediler. Oysa kafirlerin duası, çıkmazda olmaktan başkası değildir.'),
(4184, 40, 51, 'Şüphesiz Biz elçilerimize ve iman edenlere, dünya hayatında ve şahidlerin (şahidlik için) duracakları gün elbette yardım edeceğiz.'),
(4185, 40, 52, 'Zalimlere kendi mazeretlerinin hiçbir yarar sağlamayacağı gün; lanet de onlarındır, yurdun en kötüsü de.'),
(4186, 40, 53, 'Andolsun Biz Musa\'ya hidayeti verdik ve İsrailoğulları\'na kitabı miras bıraktık.'),
(4187, 40, 54, '(Ki o,) Temiz akıl sahipleri için bir hidayet rehberi ve bir zikirdir.'),
(4188, 40, 55, 'Şu halde sen sabret. Gerçekten Allah\'ın va\'di haktır. Günahın için mağfiret dile; akşam ve sabah Rabbini hamd ile tesbih et.'),
(4189, 40, 56, 'Şüphesiz, kendilerine gelmiş bulunan hiçbir delil olmaksızın, Allah\'ın ayetleri konusunda mücadele edenlere gelince; onların göğüslerinde kendisine ulaşamayacakları bir büyüklük (isteğin)den başkası yoktur. Artık sen Allah\'a sığın. Şüphesiz O hakkıyla işiten, hakkıyla görendir.'),
(4190, 40, 57, 'Elbette göklerin ve yerin yaratılması, insanların yaratılmasından daha büyüktür. Ancak insanların çoğu bilmezler.'),
(4191, 40, 58, 'Kör olanla (basiretle) gören bir olmaz; iman edip salih amellerde bulunanlarla kötülük yapan da. Ne az öğüt alıp-düşünüyorsunuz.'),
(4192, 40, 59, 'Şüphesiz kıyamet-saati, yaklaşarak gelmektedir; bunda hiçbir kuşku yok. Ancak insanların çoğu iman etmiyorlar.'),
(4193, 40, 60, 'Rabbiniz dedi ki: "Bana dua edin, size icabet edeyim. Doğrusu Bana ibadet etmekten büyüklenen (müstekbir)ler; cehenneme boyun bükmüş kimseler olarak gireceklerdir.'),
(4194, 40, 61, 'Allah, kendisinde sükun bulmanız için geceyi, aydınlık olarak da gündüzü sizin için var etti. Şüphesiz Allah, insanlara karşı (sınırsız) bir fazl sahibidir. Ancak insanların çoğu şükretmiyorlar.'),
(4195, 40, 62, 'İşte bu, sizin Rabbiniz Allah\'tır; herşeyin Yaratıcısı\'dır; O\'ndan başka İlah yoktur. Öyleyse nasıl olur da çevriliyorsunuz?'),
(4196, 40, 63, 'İşte, Allah\'ın ayetlerini inkar edenler böyle çevriliyorlar.'),
(4197, 40, 64, 'Allah, yeryüzünü sizin için bir karar, gökyüzünü bir bina kıldı; sizi suretlendirdi, suretinizi de en güzel (bir biçim ve incelikte) kıldı ve size güzel-temiz şeylerden rızık verdi. İşte sizin Rabbiniz Allah budur. Alemlerin Rabbi Allah ne Yücedir.'),
(4198, 40, 65, 'O, Hayy (diri) olandır. O\'ndan başka İlah yoktur; öyleyse dini yalnızca Kendisi\'ne halis kılanlar olarak O\'na dua edin. Alemlerin Rabbine hamd olsun.'),
(4199, 40, 66, 'De ki: "Bana apaçık belgeler gelince, sizin Allah\'tan başka taptıklarınıza kulluk etmekten kesin olarak menedildim ve alemlerin Rabbine teslim olmakla emrolundum."'),
(4200, 40, 67, 'O\'dur ki, sizi topraktan, sonra bir damla sudan, sonra bir alak\'tan (embriyo) yarattı; sonra sizi bir bebek olarak çıkarmakta, sonra güçlü (erginlik) çağınıza erişmeniz, sonra da yaşlanmanız için size (belli bir ömür vermektedir). Sizden kiminin daha önce hayatına son verilmektedir; adı konulmuş bir ecele erişmeniz ve belki aklınızı kullanmanız için (Allah sizi böyle yaşatır).'),
(4201, 40, 68, 'Dirilten ve öldüren O\'dur. Bir işin olmasına hükmetti mi, ona yalnızca: "Ol" der, o da hemen oluverir.'),
(4202, 40, 69, 'Allah\'ın ayetleri hakkında mücadele edenleri görmüyor musun; nasıl da döndürülüyorlar?'),
(4203, 40, 70, 'Ki onlar, Kitab\'ı ve elçilerimizle gönderdiğimiz şeyleri yalanladılar. Artık yakında bileceklerdir.'),
(4204, 40, 71, 'Boyunlarında demir-halkalar ve (ayaklarında) zincirler olduğu halde sürüklenecekler;'),
(4205, 40, 72, 'Kaynar suyun içinde; sonra ateşte tutuşturulacaklar.'),
(4206, 40, 73, 'Sonra onlara denilecek: "Sizin şirk koştuklarınız nerede?"'),
(4207, 40, 74, '"Allah\'ın dışında (taptıklarınız)." Dediler ki: "Bizi bırakıp-kayboluverdiler. Hayır, biz önceleri (meğer) hiçbir şeye tapar değilmişiz." İşte Allah, kafirleri böyle şaşırtıp-saptırır.'),
(4208, 40, 75, 'İşte bu, sizin yeryüzünde haksız yere şımarıp-azmanız ve azgınca ölçüyü taşırmanız dolayısıyladır.'),
(4209, 40, 76, 'İçinde ebedi kalıcılar olarak cehennemin kapılarından girin. Artık mütekebbirlerin konaklama yeri ne kötüdür.'),
(4210, 40, 77, 'Şu halde sen sabret, hiç şüphesiz Allah\'ın va\'di haktır. Sonunda ya onlara va\'dettiğimiz (azab)in bir kısmını sana göstereceğiz ya da senin hayatına son vereceğiz. Nihayet onlar Bize döndürülecekler.'),
(4211, 40, 78, 'Andolsun, Biz senden önce elçiler gönderdik; onlardan kimini sana aktarıp-anlattık ve kimini anlatmadık. Herhangi bir elçiye, Allah\'ın izni olmaksızın bir ayeti getirmek olacak şey değildir. Allah\'ın emri geldiği zaman hak ile hüküm verilir ve işte burada (hakkı) iptal etmekte (istekli) olanlar hüsrana uğramışlardır.'),
(4212, 40, 79, 'Allah O\'dur ki, kimine binmeniz, kiminden yemeniz için size (bir yarar olmak üzere) davarları var etti.'),
(4213, 40, 80, 'Onlarda sizin için yararlar vardır. Onların üstünde göğüslerinizde olan bir hacete (ihtiyaca ve arzuya) ulaşırsınız; onların ve gemilerin üstünde taşınırsınız.'),
(4214, 40, 81, 'Size Kendi ayetlerini gösteriyor; artık Allah\'ın ayetlerinden hangisini inkar ediyorsunuz?'),
(4215, 40, 82, 'Yeryüzünde gezip dolaşmıyorlar mı ki, kendilerinden öncekilerin nasıl bir sona uğradıklarını bir görsünler. Onlar, kendilerinden (sayıca) daha çoktu ve yeryüzünde kuvvet ve eserler bakımından daha üstündüler. Fakat kazandıkları şeyler, (azaba karşı) onlara hiçbir şey sağlayamadı.'),
(4216, 40, 83, 'Resulleri kendilerine apaçık belgeler getirdiği zaman, onlar, yanlarında olan ilimden dolayı sevinip-böbürlendiler de, kendisini alay konusu edindikleri şey, onları sarıp-kuşatıverdi.'),
(4217, 40, 84, 'Bizim dayanılmaz-azabımızı gördükleri zaman, dediler ki: "Bir olan Allah\'a iman ettik ve O\'na şirk koştuğumuz şeyleri de inkar ettik."'),
(4218, 40, 85, 'Ama Bizim dayanılmaz-azabımızı gördükleri zaman, imanları kendilerine hiçbir yarar sağlamadı. (Bu,) Allah\'ın kulları arasında sürüp-giden sünnetidir. İşte kafirler burada hüsrana uğramışlardır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Ha, Mim.'),
(4220, 41, 2, '(Bu Kur\'an,) Rahman ve Rahim\'den indirilmiştir.'),
(4221, 41, 3, 'Bilen bir kavim için, ayetleri (çeşitli biçimlerde, birer birer) \'fasıllar halinde açıklanmış\' Arapça Kur\'an (veya okunan) Kitap\'tır;'),
(4222, 41, 4, 'Bir müjde verici ve bir uyarıcı olarak. Ama çoğu yüz çevirdiler. Artık onlar dinlemezler.'),
(4223, 41, 5, 'Dediler ki: "Bizi kendisine çağırdığın şeye karşı kalplerimiz bir örtü içindedir, kulaklarımızda bir ağırlık, bizimle senin aranda bir perde vardır. Artık sen, (yapabileceğini) yap, biz de gerçekten yapıyoruz."'),
(4224, 41, 6, 'De ki: "Ben ancak sizin benzeriniz olan bir beşerim. Bana yalnızca, sizin İlahınızın bir tek İlah olduğu vahyolunur. Öyleyse O\'na yönelin ve O\'ndan mağfiret dileyin. Vay haline o müşriklerin."'),
(4225, 41, 7, 'Ki onlar, zekatı vermeyenler ve ahireti inkar edenlerdir.'),
(4226, 41, 8, 'Şüphesiz, iman edip salih amellerde bulunanlar; onlar için kesintisiz bir ecir vardır.'),
(4227, 41, 9, 'De ki: "Gerçekten siz mi yeri iki günde yaratanı inkar ediyor ve O\'na birtakım eşler kılıyorsunuz? O, alemlerin Rabbidir."'),
(4228, 41, 10, 'Orda (yerde) onun üstünde sarsılmaz dağlar var etti, onda bereketler yarattı ve isteyip-arayanlar için eşit olmak üzere ordaki rızıkları dört günde takdir etti.'),
(4229, 41, 11, 'Sonra, duman halinde olan göğe yöneldi; böylece ona ve yere dedi ki: "İsteyerek veya istemeyerek gelin." İkisi de: "İsteyerek (İtaat ederek) geldik" dediler.'),
(4230, 41, 12, 'Böylece onları iki gün içinde yedi gök olarak tamamladı ve her bir göğe emrini vahyetti. Biz dünya göğünü de kandillerle süsleyip-donattık ve bir koruma (altına aldık). İşte bu, üstün ve güçlü olan, bilen (Allah)\'ın takdiridir.'),
(4231, 41, 13, 'Bu durumda eğer onlar yüz çevirirlerse, artık de ki: "Ben sizi, Ad ve Semud (kavimlerinin) yıldırımına benzer bir yıldırımla uyardım."'),
(4232, 41, 14, 'Onlara "Yalnızca Allah\'a kulluk edin" diye önlerinden ve arkalarından elçiler gelince, dediler ki: "Eğer dileseydi Rabbimiz melekler indirirdi. Bundan dolayı biz, sizin kendisiyle gönderildiğiniz şeyi inkar edicileriz.”'),
(4233, 41, 15, 'Ad (kavmin)e gelince; onlar yeryüzünde haksız yere büyüklendiler ve dediler ki: "Kuvvet bakımından bizden daha üstünü kimmiş?" Onlar, gerçekten kendilerini yaratan Allah\'ı görmediler mi? O, kuvvet bakımından kendilerinden daha üstündür. Oysa onlar, Bizim ayetlerimizi (bilerek) inkar ediyorlardı.'),
(4234, 41, 16, 'Böylece Biz de onlara dünya hayatında aşağılanma azabını taddırmak için, o uğursuz (felaketler yüklü) günlerde üzerlerine \'kulakları patlatan bir kasırga\' gönderdik. Ahiret azabı ise daha (büyük) bir aşağılanmadır. Ve onlara yardım edilmeyecektir.'),
(4235, 41, 17, 'Semud\'a gelince; Biz onlara doğru yolu gösterdik, fakat onlar körlüğü hidayete tercih ettiler. Böylece kazandıkları şeyler yüzünden onları alçaltıcı azabın yıldırımı yakalayıverdi.'),
(4236, 41, 18, 'İman edenleri ve sakınanları ise kurtardık.'),
(4237, 41, 19, 'Allah\'ın düşmanlarının biraraya getirilip-toplanacakları gün işte onlar, ateşe bölükler halinde dağıtılırlar."'),
(4238, 41, 20, 'Sonunda oraya geldikleri zaman, işitme, görme (duyuları) ve derileri kendi aleyhlerine şahitlik edecektir.'),
(4239, 41, 21, 'Kendi derilerine dediler ki: "Niye aleyhimizde şahitlik ettiniz?" Dediler ki: "Herşeye nutku verip-konuşturan Allah, bizi konuşturdu. Sizi ilk defa O yarattı ve O\'na döndürülüyorsunuz."'),
(4240, 41, 22, '"Siz, işitme, görme (duyularınız) ve derileriniz aleyhinize şahitlik eder diye sakınmıyordunuz. Aksine, yaptıklarınızın birçoğunu Allah\'ın bilmeyeceğini sanıyordunuz."'),
(4241, 41, 23, '"İşte bu sizin zannınız; Rabbiniz hakkında beslediğiniz-zannınız, sizi bir yıkıma uğrattı, böylelikle hüsrana uğrayan kimseler olarak sabahladınız."'),
(4242, 41, 24, 'Şimdi eğer sabredebilirlerse, artık onlar için konaklama yeri ateştir. Ve eğer onlar hoşnut olma (dünya)ya dönmek isterlerse, artık hoşnut olacaklardan değildirler.'),
(4243, 41, 25, 'Biz onlara birtakım yakın-kimseleri \'kabuk gibi üzerlerine kaplattık,\' onlar da, önlerinde ve arkalarında olanları kendilerine süslü gösterdiler. Cinlerden ve insanlardan kendilerinden önce gelip-geçmiş ümmetlerde (yürürlükte tutulan azap) sözü onların üzerine hak oldu. Çünkü onlar, hüsrana uğrayan kimselerdi.'),
(4244, 41, 26, 'İnkar edenler dediler ki: "Bu Kur\'an\'ı dinlemeyin ve onda (okunurken) yaygaralar koparın. Belki üstün gelirsiniz."'),
(4245, 41, 27, 'Artık gerçekten o inkar edenlere şiddetli bir azap taddıracağız ve yaptıklarının en kötüsüyle cezalandıracağız.'),
(4246, 41, 28, 'Bu, Allah\'ın düşmanlarının cezası olan ateştir. Bizim ayetlerimizi inkar etmeleri dolayısıyla bir ceza olarak, orada onlar için ebedilik yurdu vardır.'),
(4247, 41, 29, 'İnkar edenler dediler ki: "Rabbimiz, cinlerden ve insanlardan bizi saptırmış olanları bize göster, ayaklarımızın altına alalım, en aşağılarda bulunanlardan olsunlar."'),
(4248, 41, 30, 'Şüphesiz: "Bizim Rabbimiz Allah\'tır" deyip sonra dosdoğru bir istikamet tutturanlar (yok mu); onların üzerine melekler iner (ve der ki:) "Korkmayın ve hüzne kapılmayın, size vadolunan cennetle sevinin."'),
(4249, 41, 31, '"Biz, dünya hayatında da, ahirette de sizin velileriniziz. Orda nefislerinizin arzuladığı herşey sizindir ve istediğiniz herşey de sizindir."'),
(4250, 41, 32, '"Çok bağışlayan, çok esirgeyen (Allah)tan bir ağırlanma olarak."'),
(4251, 41, 33, 'Allah\'a çağıran, salih amelde bulunan ve: "Gerçekten ben Müslümanlardanım" diyenden daha güzel sözlü kimdir?'),
(4252, 41, 34, 'İyilikle kötülük eşit olmaz. Sen, en güzel olan bir tarzda (kötülüğü) uzaklaştır; o zaman, (görürsün ki) seninle onun arasında düşmanlık bulunan kimse, sanki sıcak bir dost(un) oluvermiştir.'),
(4253, 41, 35, 'Buna da, sabredenlerden başkası kavuşturulamaz. Ve buna, büyük bir pay sahibi olanlardan başkası da kavuşturulamaz.'),
(4254, 41, 36, 'Şayet sana şeytandan bir kışkırtma gelecek olursa, hemen Allah\'a sığın. Çünkü O, işitendir, bilendir.'),
(4255, 41, 37, 'Gece, gündüz, güneş ve ay O\'nun ayetlerindendir. Siz güneşe de, aya da secde etmeyin. Alah’a secde edin, ki bunları Kendisi yaratmıştır. Eğer O\'na ibadet edecekseniz.'),
(4256, 41, 38, 'Şayet onlar büyüklenecek olurlarsa, Rabbinin Katında bulunanlar, O\'nu gece ve gündüz tesbih ederler ve (bundan) bıkkınlık duymazlar.'),
(4257, 41, 39, 'O\'nun ayetlerinden biri de, senin gerçekten yeryüzünü huşu içinde (solmuş, boynu bükülmüş ve kupkuru) görmendir. Ama Biz onun üzerine suyu indirdiğimiz zaman, deprenir ve kabarır. Şüphesiz onu dirilten, ölüleri de elbette dirilticidir. Çünkü O, herşeye güç yetirendir.'),
(4258, 41, 40, 'Bizim ayetlerimiz konusunda çarpıtma yapanlar, Bize gizli kalmazlar. Öyleyse ateşin içine bırakılan mı daha hayırlıdır yoksa kıyamet günü güvenle gelen mi? Siz dilediğinizi yapın. Çünkü O yaptıklarınızı gerçekten görendir.'),
(4259, 41, 41, 'Şüphesiz, kendilerine zikir gelince onu inkar edenler (ateşin içine bırakılırlar); oysa o, aziz (şerefi yüksek, üstün) bir Kitap\'tır.'),
(4260, 41, 42, 'Batıl, ona önünden de, ardından da gelemez. (Çünkü Kur\'an,) Hüküm ve hikmet sahibi, çok övülen (Allah)tan indirilmedir.'),
(4261, 41, 43, 'Sana söylenen şeyler, senden önceki elçilere söylenenden başkası değildir. Şüphesiz, Rabbin, hem elbette mağfiret sahibidir, hem de acı bir azap sahibidir.'),
(4262, 41, 44, 'Eğer Biz onu A\'cemi (Arapça olmayan bir dilde) olan bir Kur\'an kılsaydık, herhalde derlerdi ki: "Onun ayetleri açıklanmalı değil miydi? Arap olana, A\'cemi (Arapça olmayan bir dil)mi?" De ki: "O, iman edenler için bir hidayet ve bir şifadır. İman etmeyenlerin ise kulaklarında bir ağırlık vardır ve o (Kur\'an), onlara karşı bir körlüktür. İşte onlara (sanki) uzak bir yerden seslenilir."'),
(4263, 41, 45, 'Andolsun, Musa\'ya kitabı verdik, fakat onda anlaşmazlığa düşüldü. Eğer Rabbinden (daha önce) bir söz geçmiş (verilmiş) olmasaydı, mutlaka aralarında hüküm verilmiş (iş bitirilmiş)ti. Gerçekten onlar, bundan yana kuşku verici bir tereddüt içindedirler.'),
(4264, 41, 46, 'Kim salih bir amelde bulunursa, kendi lehinedir, kim de kötülük ederse, o da kendi aleyhinedir. Senin Rabbin, kullara zulmedici değildir.'),
(4265, 41, 47, 'Kıyamet-saatinin ilmi O\'na döndürülür. O\'nun ilmi olmaksızın, hiçbir meyve tomurcuğundan çıkmaz, hiçbir dişi gebe kalmaz ve doğurmaz da. Onlara: "Benim ortaklarım nerede" diye sesleneceği gün, dediler ki: "Sana arzettik ki, bizden hiçbir şahid yok."'),
(4266, 41, 48, 'Önceden kendilerine taptıkları (bugün) onlardan kaybolup gitti ve onlar kaçacak hiçbir yerleri olmadığını anlamışlardır.'),
(4267, 41, 49, 'İnsan, hayır istemekten bıkkınlık duymaz; fakat ona bir şer dokundu mu, artık o, ye\'se düşen bir umutsuzdur.'),
(4268, 41, 50, 'Oysa ona dokunan bir zarardan sonra tarafımızdan bir rahmet taddırsak, mutlaka: "Bu benim (hakkım)dır. Ve ben kıyamet-saatinin kopacağını da sanmıyorum; eğer Rabbime döndürülsem bile, muhakkak O\'nun Katında benim için daha güzel olanı vardır." der. Ama andolsun Biz, o kafirlere yaptıklarını haber vereceğiz ve andolsun onlara, en kaba bir azaptan taddıracağız.'),
(4269, 41, 51, 'İnsana nimet verdiğimiz zaman, yüz çevirir ve yan çizer; ona bir şer dokunduğu zaman ise, artık o, geniş (kapsamlı ve derinlemesine) bir dua sahibidir.'),
(4270, 41, 52, 'De ki: "Gördünüz mü haber verin; eğer o (Kur\'an) Allah Katından ise, sonra siz onu inkar etmişseniz (bu durumda) uzak bir ayrılık içinde olandan daha sapık kimdir?"'),
(4271, 41, 53, 'Biz ayetlerimizi hem afakta, hem kendi nefislerinde onlara göstereceğiz; öyle ki, şüphesiz onun hak olduğu kendilerine açıkça belli olsun. Herşeyin üzerinde Rabbinin şahid olması yetmez mi?'),
(4272, 41, 54, 'Dikkatli olun; gerçekten onlar, Rablerine kavuşmaktan yana derin bir kuşku içindedirler. Dikkatli olun; gerçekten O, herşeyi sarıp-kuşatandır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Ha, Mim.'),
(4274, 42, 2, 'Ayn, Sin Kaf.'),
(4275, 42, 3, 'O, Aziz ve Hakim olan Allah, sana ve senden öncekilere böyle vahyetmektedir.'),
(4276, 42, 4, 'Göklerde ve yerde olanlar O\'nundur. O, Yücedir, büyüktür.'),
(4277, 42, 5, 'Gökler, neredeyse üstlerinden çatlayıp-parçalanacaklar; melekler de Rablerini hamd ile tesbih ederler ve yerde olanlara mağfiret dilerler. Haberiniz olsun; gerçekten Allah, bağışlayan ve esirgeyen O\'dur.'),
(4278, 42, 6, 'Allah\'ın dışında birtakım veliler edinenler ise; Allah, onların üzerinde gözetleyicidir. Sen onların üzerinde bir vekil değilsin.'),
(4279, 42, 7, 'İşte Biz sana, böyle Arapça bir Kur\'an vahyettik; şehirlerin anası (olan Mekke halkı)nı ve çevresinde olanları uyarman için ve kendisinde şüphe olmayan toplanma gününü (haber verip onları) uyarman için de. (O gün onların) Bir bölümü cennette, bir bölümü çılgınca yanan ateşin içerisindedirler.'),
(4280, 42, 8, 'Eğer Allah dileseydi, onları herhalde tek bir ümmet kılardı. Ancak O, dilediğini Kendi rahmetine sokar. Zalimlere gelince; onlar için ne bir veli vardır, ne bir yardımcı (bulursun).'),
(4281, 42, 9, 'Yoksa O\'nun dışında birtakım veliler mi edindiler? İşte Allah; veli O\'dur, ölüleri dirilten O\'dur. O, herşeye güç yetirendir.'),
(4282, 42, 10, 'Hakkında ihtilafa düştüğünüz herhangi bir şey; artık O\'nun hükmü Allah\'ındır. İşte Rabbim olan Allah. Ben O\'na tevekkül ettim ve yalnızca O\'na dönüp-yönelirim.'),
(4283, 42, 11, 'O, göklerin ve yerin Yaratıcısı\'dır. Size kendi nefislerinizden eşler, davarlardan da çiftler var etti. Sizleri bu tarzda türetip-yayıyor. O\'nun benzeri gibi olan hiçbir şey yoktur. O, işitendir, görendir.'),
(4284, 42, 12, 'Göklerin ve yerin anahtarları O\'nundur. O, dilediğine rızkı genişletip-yayar ve kısar da. Çünkü O, herşeyi bilendir.'),
(4285, 42, 13, 'O: "Dini dosdoğru ayakta tutun ve onda ayrılığa düşmeyin" diye dinden Nuh\'a vasiyet ettiğini ve sana vahyettiğimizi, İbrahim\'e, Musa\'ya ve İsa\'ya vasiyet ettiğimizi sizin için de teşri\' etti (bir şeriat kıldı). Senin kendilerini çağırdığın şey, müşriklere ağır geldi. Allah, dilediğini buna seçer ve içten Kendisi\'ne yöneleni hidayete erdirir.'),
(4286, 42, 14, 'Onlar, kendilerine ilim geldikten sonra, yalnızca aralarındaki \'tecavüz ve haksızlık\' dolayısıyla ayrılığa düştüler. Eğer Rabbinden, adı konulmuş bir ecele kadar geçmiş (verilmiş) bir söz olmasaydı, muhakkak aralarında hüküm verilmiş (iş bitirilmiş)ti. Şüphesiz onların ardından kitaba mirasçı olanlar ise, herhalde ona karşı kuşku verici bir tereddüt içindedirler.'),
(4287, 42, 15, 'Şu halde, sen bundan dolayı davet et ve emrolunduğun gibi doğru bir istikamet tuttur. Onların heva (istek ve tutku)larına uyma. Ve de ki: Allah\'ın indirdiği her kitaba inandım. Aranızda adaletli davranmakla emrolundum. Allah, bizim de Rabbimiz, sizin de Rabbinizdir. Bizim amellerimiz bizim, sizin amelleriniz sizindir. Bizimle aranızda ‘deliller getirerek tartışma (ya, huccete gerek)’ yoktur. Allah bizi biraraya getirip-toplayacaktır. Dönüş O\'nadır."'),
(4288, 42, 16, 'O\'na icabet olunduktan sonra, Allah hakkında (sözde) \'deliller öne sürüp tartışanların\' delilleri, Rableri Katında geçersizdir. Onların üzerinde bir gazab vardır ve şiddetli azap onlaradır.'),
(4289, 42, 17, 'Ki Allah, hak olmak üzere kitabı ve mizanı indirdi. Ne bilirsin; belki kıyamet-saati pek yakındır.'),
(4290, 42, 18, 'Onda acele edenler, (gerçekte) ona inanmayanlardır. İman edenler ise, ona karşı bir korku içindedirler ve onun gerçekten hak olduğunu bilirler. Haberiniz olsun; kıyamet-saati konusunda tartışanlar, gerçekte uzak bir sapıklık içindedirler.'),
(4291, 42, 19, 'Allah, kullarına karşı lütuf sahibidir; dilediğini rızıklandırır. O, kuvvetlidir, Azizdir.'),
(4292, 42, 20, 'Kim ahiret ekinini isterse, Biz ona kendi ekininde artırmalar yaparız. Kim dünya ekinini isterse, ona da ondan veririz; ancak onun ahirette bir nasibi yoktur.'),
(4293, 42, 21, 'Yoksa onların birtakım ortakları mı var ki, Allah\'ın izin vermediği şeyleri, dinden kendilerine teşri\' ettiler (bir şeriat kıldılar)? Eğer o fasıl kelimesi olmasaydı, elbette aralarında hüküm (karar) verilirdi. Gerçekten zalimler için acı bir azap vardır.'),
(4294, 42, 22, '(O gün) Zalimleri kazandıkları dolayısıyla korkuyla titrerlerken görürsün; o (yaptıkları) da üstlerine çöküvermiştir. İman edip salih amellerde bulunanlar ise, cennet bahçelerindedirler. Rableri Katında her diledikleri onlarındır. İşte büyük fazl (nimet ve üstünlük) budur.'),
(4295, 42, 23, 'İşte Allah, iman edip salih amellerde bulunan kullarına böyle müjde vermektedir. De ki: "Ben buna karşı yakınlıkta sevgi dışında sizden hiçbir ücret istemiyorum." Kim bir iyilik kazanırsa, Biz ondaki iyiliği artırırız. Gerçekten Allah, bağışlayandır, şükredene karşılığını verendir.'),
(4296, 42, 24, 'Yoksa onlar: "Allah\'a karşı yalan düzüp-uydurdu"mu diyorlar? Oysa eğer Allah dilerse senin de kalbini mühürler. Allah, batılı yok edip-ortadan kaldırır ve Kendi kelimeleriyle hakkı hak olarak pekiştirir (gerçekleştirir). Çünkü O, sinelerin özünde olanı bilendir.'),
(4297, 42, 25, 'Kullarından tevbeyi kabul eden, kötülükleri affeden ve işlediklerinizi bilen O\'dur.'),
(4298, 42, 26, 'O, iman edip salih amellerde bulunanlara icabet eder ve onlara Kendi fazlından arttırır. Kafirlere gelince; onlara şiddetli bir azap vardır.'),
(4299, 42, 27, 'Eğer Allah, kulları için rızkı (sınırsızca) geniş tutup-yaysaydı, gerçekten yeryüzünde azarlardı. Ancak O, dilediği miktar ile indirir. Çünkü O, kullarından haberi olandır, görendir.'),
(4300, 42, 28, 'O\'dur ki, onlar umutlarını kestikten sonra yağmuru indirir ve rahmetini serip-yayar. O, Veli\'dir, Hamid\'dir.'),
(4301, 42, 29, 'Göklerin ve yerin yaratılması ile onlarda her canlıdan türetip-yayması O\'nun ayetlerindendir. Ve O, dileyeceği zaman onların hepsini toplamaya güç yetirendir.'),
(4302, 42, 30, 'Size isabet eden her musibet, (ancak) ellerinizin kazandığı dolayısıyladır. (Allah,) Çoğunu da affeder.'),
(4303, 42, 31, 'Siz yeryüzünde (O\'nu) aciz bırakacak değilsiniz. Ve sizin Allah\'ın dışında ne bir veliniz vardır, ne bir yardımcınız.'),
(4304, 42, 32, 'Denizde yüksek dağlar gibi seyreden gemiler O\'nun ayetlerindendir.'),
(4305, 42, 33, 'Eğer dileyecek olsa, rüzgarı durdurur, böylece onun üstünde kalakalırlar. Şüphesiz, bunda çokça sabreden, çokça şükreden kimse için gerçekten ayetler vardır.'),
(4306, 42, 34, 'Ya da kazandıkları dolayısıyla onları yok eder, bir çoğunu da affeder.'),
(4307, 42, 35, '(Öyle ki) Ayetlerimiz hakkında mücadele edenler, kendileri için hiçbir kaçacak yer olmadığını bilip-öğrensinler.'),
(4308, 42, 36, 'Size verilen herhangi bir şey, dünya hayatının metaı (kısa süreli faydalanması)dır. Allah Katında olan ise, daha hayırlı ve daha süreklidir. (Bu da) iman edip Rablerine tevekkül edenler içindir;'),
(4309, 42, 37, '(Bunlar,) Büyük günahlardan ve çirkin -utanmazlıklardan kaçınanlar ve gazablandıkları zaman bağışlayanlar,'),
(4310, 42, 38, 'Rablerine icabet edenler, namazı dosdoğru kılanlar, işleri kendi aralarında şura ile olanlar ve kendilerine rızık olarak verdiklerimizden infak edenler,'),
(4311, 42, 39, 'Ve haklarına tecavüz edildiği zaman, birlik olup karşı koyanlardır.'),
(4312, 42, 40, 'Kötülüğün karşılığı, onun misli (benzeri) olan kötülüktür. Ama kim affeder ve ıslah ederse (dirliği kurup-sağlarsa) artık onun ecri Allah\'a aittir. Gerçekten O, zalimleri sevmez.'),
(4313, 42, 41, 'Kim zulme uğradıktan sonra nusret bulur (hakkını alır)sa, artık onlar için aleyhlerinde bir yol yoktur.'),
(4314, 42, 42, 'Yol, ancak insanlara zulmeden ve yeryüzünde haksız yere \'tecavüz ve haksızlıkta bulunanların\' aleyhinedir. İşte bunlara acıklı bir azap vardır.'),
(4315, 42, 43, 'Kim sabreder ve bağışlarsa, şüphesiz bu, azme değer işlerdendir.'),
(4316, 42, 44, 'Allah, kimi saptırırsa, artık bundan sonra onun hiçbir velisi yoktur. Azabı gördükleri zaman, o zalimleri bir görsen; "Geri dönmeye bir yol var mı?" derler.'),
(4317, 42, 45, 'Onları görürsün; zilletten başları önlerine düşmüş bir halde, ona (ateşe) sunulurlarken göz ucuyla sezdirmeden bakarlar. İman edenler de: "Gerçekten hüsrana uğrayanlar, kıyamet günü hem kendi nefislerini, hem yakın akraba (veya yandaş)larını da hüsrana uğratmışlardır" dediler. Haberiniz olsun; gerçekten zalimler, kalıcı bir azap içindedirler.'),
(4318, 42, 46, 'Onların Allah\'ın dışında kendilerine yardım edecek velileri yoktur. Allah kimi saptırırsa, artık onun için hiçbir (çıkış) yolu yoktur.'),
(4319, 42, 47, 'Allah\'tan, geri çevrilmesi olmayan bir gün gelmeden evvel, Rabbinize icabet edin. O gün, sizin için ne sığınılacak bir yer var, ne sizin için inkar (etmeye bir imkan).'),
(4320, 42, 48, 'Şayet onlar, sırt çevirecek olurlarsa, artık Biz seni onların üzerine bir gözetleyici olarak göndermiş değiliz. Sana düşen, yalnızca tebliğdir. Gerçek şu ki, Biz insana tarafımızdan bir rahmet taddırdığımız zaman, ona sevinir. Eğer onlara kendi ellerinin takdim ettikleri dolayısıyla bir kötülük isabet ederse, bu durumda insan bir nankör kesiliverir.'),
(4321, 42, 49, 'Göklerin ve yerin mülkü Allah\'ındır. Dilediğini yaratır. Dilediğine dişiler armağan eder, dilediğine de erkek armağan eder.'),
(4322, 42, 50, 'Veya erkekler ve dişiler olarak çift (ikiz) verir. Dilediğini kısır bırakır. Gerçekten O, bilendir, güç yetirendir.'),
(4323, 42, 51, 'Kendisiyle Allah\'ın konuşması, bir beşer için olacak (şey) değildir; ancak bir vahy ile ya da perde arkasından veya bir elçi gönderip Kendi izniyle dilediğine vahyetmesi (durumu) başka. Gerçekten O, Yüce olandır, hüküm ve hikmet sahibidir.'),
(4324, 42, 52, 'Böylece sana emrimizden bir ruh vahyettik. Sen, kitap nedir, iman nedir bilmiyordun. Ancak Biz onu bir nur kıldık; onunla kullarımızdan dilediklerimizi hidayete erdiririz. Şüphesiz sen, dosdoğru olan bir yola yöneltip-iletiyorsun.'),
(4325, 42, 53, 'Göklerde ve yerde bulunanların tümü Kendisi\'ne ait olan Allah\'ın yoluna. Haberiniz olsun; işler Allah\'a döner.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Ha, Mim.'),
(4327, 43, 2, 'Apaçık Kitab’a andolsun;'),
(4328, 43, 3, 'Gerçekten Biz onu, belki aklınızı kullanırsınız diye Arapça bir Kur\'an kıldık.'),
(4329, 43, 4, 'Şüphesiz o, Bizim Katımız\'da olan Ana kitaptadır; çok yücedir, hüküm ve hikmet doludur.'),
(4330, 43, 5, 'Siz ölçüyü taşıran bir kavimsiniz diye, şimdi o zikri (öğüt ve hatırlatma dolu Kur\'an\'ı) sizden (uzaklaştırıp) bir yana mı bırakalım?'),
(4331, 43, 6, 'Oysa Biz, öncekiler içinde nice peygamber(ler) gönderdik.'),
(4332, 43, 7, 'Onlara bir peygamber gelmeyiversin, mutlaka onunla alay ederlerdi.'),
(4333, 43, 8, 'Biz de, kuvvet bakımından onlardan daha üstün olan (toplum)ları yıkıma uğrattık. Öncekilerin örneği geçti.'),
(4334, 43, 9, 'Andolsun, onlara: "Gökleri ve yeri kim yarattı?" diye soracak olsan, tartışmasız: "Onları üstün ve güçlü (Aziz) olan, bilen (Allah) yarattı" diyecekler.'),
(4335, 43, 10, 'Ki O, yeri sizin için bir beşik kıldı ve doğru yolu bulursunuz diye onda size (birtakım) yollar var etti.'),
(4336, 43, 11, 'Ki O, belli bir miktar ile gökten su indirdi de, onunla ölü bir memleketi ‘dirilttik (ve her yanına yeniden hayat) yaydık\'; siz de böyle (kabirlerinizden diriltilip) çıkarılacaksınız.'),
(4337, 43, 12, 'Ki O, bütün çiftleri yarattı ve sizin için gemilerden ve hayvanlardan bineceğiniz şeyleri var etti.'),
(4338, 43, 13, 'Onların sırtlarına binip-doğrulmanız, sonra doğrulduğunuz zaman, Rabbinizin nimetini zikretmeniz ve: "Bunlara bizim için boyun eğdiren (Allah) ne Yücedir, yoksa biz bunu (kendi hizmetimize) yanaştıramazdık" demeniz için.'),
(4339, 43, 14, 'Ve biz elbette, Rabbimiz\'e çevrilip-döneceğiz."'),
(4340, 43, 15, '(Buna rağmen) Kendi kullarından O\'na bir parça kılıp-yakıştırdılar. Doğrusu insan, açıkça bir nankördür.'),
(4341, 43, 16, 'Yoksa O, yarattıklarından kızları (kendine) edindi ve erkekleri size mi ayırdı?'),
(4342, 43, 17, 'Oysa onlardan biri, O, Rahman (olan Allah) için verdiği örnek ile (kız çocuğunun doğumuyla) müjdelendiği zaman, yüzü simsiyah kesilmiş olarak kahrından yutkundukça yutkunur.'),
(4343, 43, 18, 'Onlar, süs içinde büyütülüp de mücadelede açık olmayan (kızlar)ı mı (Allah\'a yakıştırıyorlar)?'),
(4344, 43, 19, 'Onlar, ki Rahmanın kulları olan melekleri dişiler kıldılar. Kendileri yaratılışlarına şahit mi oldular? Onların şahitlikleri yazılacak ve (bundan dolayı) sorumlu tutulacaklar.'),
(4345, 43, 20, 'Dediler ki: "Eğer Rahman dilemiş olsaydı, biz onlara ibadet etmezdik." Onların bundan yana hiçbir bilgileri yoktur. Onlar, yalnızca \'zan ve tahminle yalan söylüyorlar.\''),
(4346, 43, 21, 'Yoksa Biz, bundan önce kendilerine bir kitap verdik de şimdi ona mı tutunuyorlar?'),
(4347, 43, 22, 'Hayır; dediler ki: "Gerçekten atalarımızı bir ümmet üzerinde bulduk ve doğrusu biz onların izleri (eserleri) üstünde doğru olana (hidayete) yönelmiş (kimse)leriz."'),
(4348, 43, 23, 'İşte böyle, senden önce de (herhangi) bir memlekete bir elçi göndermiş olmayalım, mutlaka onun \'refah içinde şımarıp azan önde gelenleri\' (şöyle) demişlerdir: "Gerçekten biz, atalarımızı bir ümmet (din) üzerinde bulduk ve doğrusu biz, onların izlerine (eserlerine) uymuş kimseleriz."'),
(4349, 43, 24, '(O peygamberlerden her biri de şöyle) Demiştir: "Ben size atalarınızı üstünde bulduğunuz şeyden daha doğru olanını getirmiş olsam da mı?" Onlar da demişlerdi ki: "Doğrusu biz, kendisiyle gönderildiğiniz şeye kafir olanlarız."'),
(4350, 43, 25, 'Böylece onlardan intikam aldık. Öyleyse, bir bak; yalan sayanların sonu nasıl oldu?'),
(4351, 43, 26, 'Hani İbrahim babasına ve kendi kavmine demişti ki: "Şüphesiz ben, sizin taptıklarınızdan uzağım."'),
(4352, 43, 27, '"(Ancak) Beni yaratan başka. İşte O beni hidayete yöneltip-iletecektir."'),
(4353, 43, 28, 'Ve bunu (bu tevhid inancını) belki (insanlar Allah\'a) dönerler diye ardında (kendi soyunda) kalıcı bir kelime olarak kıldı-bıraktı.'),
(4354, 43, 29, 'Hayır; Ben onları ve atalarını, kendilerine hak ve açıklayan bir elçi gelinceye kadar metalandırdım-yaşattım.'),
(4355, 43, 30, 'Ancak kendilerine hak gelince, dediler ki: "Bu bir büyüdür, doğrusu biz ona (karşı) kafir olanlarız."'),
(4356, 43, 31, 'Ve dediler ki: "Bu Kur\'an, iki şehirden birinin büyük bir adamına indirilmeli değil miydi?"'),
(4357, 43, 32, 'Senin Rabbinin rahmetini onlar mı paylaştırıyorlar? Dünya hayatında maişetlerini aralarında Biz paylaştırdık ve onlardan bir bölümü (diğer) bir bölümünü \'teshir etmesi için, bir bölümünü bir bölümü üzerinde derecelerle yükselttik. Rabbinin rahmeti; toplayıp-yığdıklarından daha hayırlıdır.'),
(4358, 43, 33, 'Eğer insanlar (Allah\'a karşı isyanda birleşip) tek bir ümmet olacak olmasaydı, Rahman’ı (Allah\'ı) inkar edenlerin evlerine gümüşten tavanlar ve üzerinde çıkıp-yükselecekleri merdivenler yapardık.'),
(4359, 43, 34, 'Evlerine kapılar ve üzerinde yaslanıp-dayanacakları koltuklar,'),
(4360, 43, 35, 'Ve (daha nice) çekici-süsler (de verirdik). Bütün bunlar, yalnızca dünya hayatının metaıdır. Ahiret ise, Rabbinin Katında muttakiler içindir.'),
(4361, 43, 36, 'Kim Rahman (olan Allah)ın zikrini görmezlikten gelirse, Biz bir şeytana onun \'üzerini kabukla bağlattırırız\'; artık bu, onun bir yakın dostudur.'),
(4362, 43, 37, 'Gerçekten bunlar (bu şeytanlar), onları yoldan alıkoyarlar; onlar ise, kendilerinin gerçekten hidayette olduklarını sanırlar.'),
(4363, 43, 38, 'Sonunda Bize geldiği zaman, der ki: "Keşke benimle senin aranda iki doğu (doğu ile batı) uzaklığı olsaydı. Meğer ne kötü yakın-dost(muşsun sen)."'),
(4364, 43, 39, '(Bu söylenmeleriniz,) Bugün size kesin olarak bir yarar sağlamaz. Çünkü zulmettiniz. Şüphesiz azapta da ortaksınız.'),
(4365, 43, 40, 'Öyleyse sağır olanlara sen mi dinleteceksin veya kör olan ve açıkça bir sapıklık içinde bulunanı hidayete erdireceksin?'),
(4366, 43, 41, 'Şu halde Biz seni alıp-götürürsek, elbette onlardan intikam alacağız.'),
(4367, 43, 42, 'Ya da kendilerine va\'dettiğimiz şeyi onlara gösteririz ki, Biz gerçekten onların üstünde güç yetirenleriz.'),
(4368, 43, 43, 'Şu halde, sana vahyedilene sımsıkı-tutun; çünkü sen dosdoğru bir yol üzerindesin.'),
(4369, 43, 44, 'Ve şüphesiz o (Kur\'an), senin ve kavmin için gerçekten bir zikirdir. Siz (ondan) sorulacaksınız.'),
(4370, 43, 45, 'Senden önce gönderdiğimiz elçilerimizden sor: Biz, Rahman (olan Allah)ın dışında tapılacak birtakım ilahlar kıldık mı (hiç)?'),
(4371, 43, 46, 'Andolsun, Biz Musa\'yı, Firavun\'a ve onun \'önde gelen çevresine\' ayetlerimizle gönderdik. O da, dedi ki: "Gerçekten ben, alemlerin Rabbinin elçisiyim."'),
(4372, 43, 47, 'Fakat onlara ayetlerimizle geldiği zaman, bir de ne görsün, onlar bunlara (alay edip) gülüyorlar.'),
(4373, 43, 48, 'Biz onlara biri ötekinden daha büyük olmayan hiçbir ayet göstermedik. Belki dönerler diye, onları azapla yakalayıverdik.'),
(4374, 43, 49, 'Ve onlar dediler ki: "Ey büyücü, sende olan ahdi (sana verdiği sözü) adına bizim için Rabbine dua et; gerçekten biz hidayete gelmiş olacağız."'),
(4375, 43, 50, 'Fakat onlardan azabı çekip-giderince, bir de görürsün ki onlar andlarını bozuyorlar.'),
(4376, 43, 51, 'Firavun, kendi kavmi içinde bağırdı; dedi ki: "Ey kavmim, Mısır\'ın mülkü ve şu altımda akmakta olan nehirler benim değil mi? Yine de görmeyecek misiniz?"'),
(4377, 43, 52, '"Yoksa ben, şundan daha hayırlı değil miyim ki o, aşağı (sınıftan) bir zavallı ve neredeyse (sözü) açıklamadan yoksun olan (biri)dir."'),
(4378, 43, 53, '"Bu durumda (eğer doğruysa), üzerine altından bilezikler atılmalı ya da yakınında yer almış vaziyette onunla birlikte melekler gelmeli değil miydi?"'),
(4379, 43, 54, 'Böylelikle kendi kavmini küçümsedi, onlar da ona boyun eğdiler. Gerçekten onlar, fasık olan bir kavimdi.'),
(4380, 43, 55, 'Sonunda Bizi öfkelendirince, Biz de onlardan intikam aldık, böylece onları toplu olarak suda boğduk.'),
(4381, 43, 56, 'Bu suretle onları, sonradan gelecekler için bir selef ve bir örnek kıldık.'),
(4382, 43, 57, 'Meryem oğlu (İsa) bir örnek olarak verilince, senin kavmin hemen ondan (keyifle söz edip) kahkahalarla gülüyorlar.'),
(4383, 43, 58, 'Dediler ki: "Bizim ilahlarımız mı daha hayırlı, yoksa o mu?" Onu yalnızca bir tartışma-konusu olsun diye (örnek) verdiler. Hayır, onlar \'tartışmacı ve düşman\' bir kavimdir.'),
(4384, 43, 59, 'O, yalnızca bir kuldur; kendisine nimet verdik ve onu İsrailoğulları\'na bir örnek kıldık.'),
(4385, 43, 60, 'Eğer Biz dilemiş olsaydık, elbette sizden melekler kılardık; yeryüzünde (size) halef (yerinize geçenler) olurlardı.'),
(4386, 43, 61, 'Şüphesiz o, kıyamet-saati için bir ilimdir. Öyleyse ondan (kıyametten) yana hiçbir kuşkuya kapılmayın ve Bana uyun. Dosdoğru yol budur.'),
(4387, 43, 62, 'Şeytan sakın sizi (Allah\'ın yolundan) alıkoymasın. Gerçekten o, sizin için açıkça bir düşmandır.'),
(4388, 43, 63, 'İsa, açık belgelerle gelince, dedi ki: "Ben size bir hikmetle geldim ve hakkında ihtilafa düştüklerinizin bir kısmını size açıklamak için de. Öyleyse Allah\'tan sakının ve bana itaat edin."'),
(4389, 43, 64, '"Şüphesiz Allah, O, benim de Rabbim, sizin de Rabbinizdir; şu halde O\'na kulluk edin. Dosdoğru yol budur."'),
(4390, 43, 65, 'Sonra, içlerinden birtakım fırkalar ihtilafa düştü. Artık, acı bir günün azabından vay o zulmetmiş olanlara.'),
(4391, 43, 66, 'Onlar, hiç şuurunda değilken kendilerine apansız geliverecek olan kıyamet-saatinden başkasını mı gözlüyorlar?'),
(4392, 43, 67, 'Muttakiler hariç olmak üzere, o gün, dostların kimi kimine düşmandır.'),
(4393, 43, 68, '"Ey kullarım, bugün sizin için korku yoktur ve siz mahzun olmayacaksınız."'),
(4394, 43, 69, '"Ki onlar, Benim ayetlerime iman edenler ve Müslüman olanlardır."'),
(4395, 43, 70, '"Siz ve eşleriniz cennete girin; \'sevinç içinde ağırlanacaksınız."'),
(4396, 43, 71, '"Onların etrafında altın tepsiler ve testilerle dolaşılır; orada nefislerin arzu ettiği ve gözlerin lezzet (zevk) aldığı herşey var. Ve siz orada süresiz kalacaksınız."'),
(4397, 43, 72, '"İşte, yaptıklarınız dolayısıyla mirasçı kılındığınız cennet budur."'),
(4398, 43, 73, '"Orda sizin için birçok meyveler vardır; onlardan yiyeceksiniz."'),
(4399, 43, 74, 'Şüphesiz suçlu-günahkarlar, cehennem azabı içinde süresiz kalacaklardır.'),
(4400, 43, 75, 'Onlardan (azap) hafifletilmeyecek ve orda onlar umutlarını kaybetmiş kimselerdir.'),
(4401, 43, 76, 'Biz onlara zulmetmedik; ancak onların kendileri zalimlerdir.'),
(4402, 43, 77, '(Cehennem bekçisine:) "Ey Malik (bekçi), Rabbin bizim işimizi bitirsin" diye haykırdılar. O: "Gerçek şu ki siz, (burda) kalacak kimselersiniz" dedi.'),
(4403, 43, 78, '"Andolsun, size hakkı getirdik, fakat sizin bir çoğunuz hakkı çirkin görüp-tiksinenlerdiniz."'),
(4404, 43, 79, 'Yoksa onlar, işi sıkı mı tuttular? İşte şüphesiz Biz de işi sıkı tutanlarız.'),
(4405, 43, 80, 'Yoksa onlar; gerçekten Bizim, sır tuttuklarını ve aralarındaki fısıldaşmalarını işitmediğimizi mi sanıyorlar? Hayır, (işitiyoruz) ve onların yanlarındaki elçilerimiz de (herşeyi) yazıyorlar.'),
(4406, 43, 81, 'De ki: "Eğer Rahman (olan Allah)\'ın çocuğu olsaydı, ona tapanların ilki ben olurdum."'),
(4407, 43, 82, 'Göklerin ve yerin Rabbi, Arş\'ın Rabbi (olan Allah), onların nitelendirdiklerinden Yücedir.'),
(4408, 43, 83, 'Artık onları bırak; onlara vadedilen günlerine kadar, dalsınlar ve oynaya dursunlar.'),
(4409, 43, 84, 'Göklerde İlah ve yerde İlah O\'dur. O, hüküm ve hikmet sahibidir, bilendir.'),
(4410, 43, 85, 'Göklerin, yerin ve ikisi arasında bulunanların mülkü Kendisi\'nin olan (Allah) ne Yücedir. Kıyamet-saatinin ilmi O\'nun Katındadır ve O\'na döndürüleceksiniz.'),
(4411, 43, 86, 'O\'nun dışında taptıkları şefaatte bulunmaya malik değildirler; ancak kendileri bilerek hakka şahidlik edenler başka.'),
(4412, 43, 87, 'Andolsun, onlara: "Kendilerini kim yarattı?" diye soracak olsan, elbette: "Allah" diyecekler. Öyleyse nasıl olur da çevriliyorlar?'),
(4413, 43, 88, 'Onun: "Ya Rab" demesi hakkı için şüphesiz onlar imana gelmez bir kavimdirler.'),
(4414, 43, 89, 'Şimdi sen, \'aldırış etmeksizin onlardan yüz çevir\' ve: "Selam" de. Artık onlar bileceklerdir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Ha, Mim.'),
(4416, 44, 2, 'Apaçık Kitab\'a andolsun;'),
(4417, 44, 3, 'Gerçekten Biz onu mübarek bir gecede indirdik, gerçekten Biz uyaranlarız.'),
(4418, 44, 4, 'Ki onda (o gecede) her hikmetli iş ayrılır.'),
(4419, 44, 5, 'Katımız\'dan bir emir ile; doğrusu Biz, (insanlara elçi) gönderenleriz.'),
(4420, 44, 6, 'Rabbinden bir rahmet olarak. Şüphesiz O, işitendir, bilendir.'),
(4421, 44, 7, 'Eğer kesin bir bilgiyle inanıyorsanız (Allah), göklerin, yerin ve bu ikisi arasında bulunanların Rabbidir.'),
(4422, 44, 8, 'O\'ndan başka İlah yoktur; diriltir ve öldürür. Sizin de Rabbinizdir, geçmiş atalarınızın da Rabbidir.'),
(4423, 44, 9, 'Hayır, onlar şüphe içindedirler; oynayıp-oyalanıyorlar.'),
(4424, 44, 10, 'Öyleyse sen, göğün açıkça bir duman getireceği günü gözle;'),
(4425, 44, 11, '(Bu duman) insanları sarıp-kuşatıverir. İşte bu, acı bir azaptır.'),
(4426, 44, 12, '"Rabbimiz, azabı üstümüzden açıp-gider; çünkü biz (artık) iman edicileriz."'),
(4427, 44, 13, 'Onlar için öğüt alıp-düşünmek nerede? Onlara, açıklayan bir elçi gelmişti.'),
(4428, 44, 14, 'Sonra, ondan yüz çevirdiler ve dediler ki: "(Bu,) Öğretilmiştir, bir delidir."'),
(4429, 44, 15, 'Biz sizden bu azabı biraz açıp-gidereceğiz; (ama yine) dönecek olanlarsınız siz.'),
(4430, 44, 16, 'Büyük bir şiddetle yakalayacağımız gün, elbette Biz intikam alacağız.'),
(4431, 44, 17, 'Andolsun, Biz kendilerinden önce, Firavun\'un kavmini de denedik. Onlara kerim bir elçi gelmişti;'),
(4432, 44, 18, '"Allah\'ın kullarını bana teslim edin; gerçekten ben, sizin için güvenilir bir elçiyim" (demişti).'),
(4433, 44, 19, '"Allah\'a karşı büyüklenmeyin; şüphesiz size apaçık, bir delil getiriyorum."'),
(4434, 44, 20, '"Ve doğrusu ben, sizin taşa tutmanızdan benim de Rabbim, sizin de Rabbiniz olan (Allah)a sığındım."'),
(4435, 44, 21, '"Eğer bana inanmıyorsanız, bu durumda benden kopup-ayrılın."'),
(4436, 44, 22, 'Sonunda Rabbine: "Gerçekten bunlar, suçlu-günahkar bir kavimdirler" diye dua etti.'),
(4437, 44, 23, '(Allah da:) "Öyleyse, kullarımı geceleyin yürüyüşe geçir, muhakkak takip edileceksiniz." (diye duasını kabul edip cevap verdi).'),
(4438, 44, 24, '"Denizi durgun ve açık bırak. Çünkü suda boğulacak bir ordudur."'),
(4439, 44, 25, 'Onlar nice bahçeler ve pınarlar terk etmişlerdi;'),
(4440, 44, 26, '(Nice) Ekinler, güzel konaklar,'),
(4441, 44, 27, 'Ve içlerinde \'sevinç ve mutluluk içinde\' yaşadıkları nimetler,'),
(4442, 44, 28, 'İşte böyle; Biz bunları başka bir kavme miras olarak verdik.'),
(4443, 44, 29, 'Onlar için ne gök, ne yer ağlamadı ve onlar (ın azabı) ertelenmedi.'),
(4444, 44, 30, 'Andolsun, Biz İsrailoğulları\'nı o alçaltıcı azaptan kurtardık.'),
(4445, 44, 31, 'Firavun\'dan. Çünkü, o, ölçüyü taşıran bir mütekebbirdi.'),
(4446, 44, 32, 'Andolsun, Biz onları bir ilim üzere alemlere üstün kıldık.'),
(4447, 44, 33, 'Ve onlara, her birinde açık birer imtihan bulunan ayetler verdik.'),
(4448, 44, 34, 'Muhakkak, bunlar da diyorlar ki:'),
(4449, 44, 35, '"(Bütün herşey) Bizim yalnızca ilk ölümümüzdür; biz yeniden diriltilip-kaldırılacak değiliz."'),
(4450, 44, 36, '"Eğer doğru sözlüyseniz, şu halde atalarımızı getirin bakalım."'),
(4451, 44, 37, 'Onlar mı hayırlı, yoksa Tübba\' kavmi ve onlardan öncekiler mi? Biz onları yıkıma uğrattık. Çünkü onlar, suçlu-günahkardı.'),
(4452, 44, 38, 'Biz, gökleri, yeri ve ikisi arasında bulunanları bir ‘oyun ve oyalanma konusu’ olsun diye yaratmadık.'),
(4453, 44, 39, 'Biz onları yalnızca hak ile yarattık. Ancak onların çoğu bilmezler.'),
(4454, 44, 40, 'Şüphesiz o (hakkı batıldan, haklıyı haksızdan) ayırma günü, hepsinin (hesaba çekilecekleri) vakitleridir.'),
(4455, 44, 41, 'O gün, bir dost dosttan herhangi bir şeyle yarar sağlayamaz. Ve onlara yardım edilmez.'),
(4456, 44, 42, 'Ancak Allah\'ın rahmet ettiği başka. Şüphesiz O, üstün ve güçlü olandır, esirgeyendir.'),
(4457, 44, 43, 'Doğrusu, o zakkum ağacı;'),
(4458, 44, 44, 'Günahkar olanın yemeğidir.'),
(4459, 44, 45, 'Pota gibi; karınlarda kaynar-durur;'),
(4460, 44, 46, 'Kaynar-suyun kaynaması gibi.'),
(4461, 44, 47, '"Onu tutun da cehennemin orta yerine sürükleyin."'),
(4462, 44, 48, '"Sonra kaynar suyun azabından başının üstüne dökün;"'),
(4463, 44, 49, '"(Azabı) Tad; çünkü sen, (kendince) üstün, onurluydun."'),
(4464, 44, 50, '"Gerçekten bu, sizin kuşkuya kapıldığınız şeydir."'),
(4465, 44, 51, 'Muttakilere gelince; muhakkak onlar, güvenli bir makamdadırlar.'),
(4466, 44, 52, 'Cennetlerde ve pınarlarda,'),
(4467, 44, 53, 'Hafif ipekten ve ağır işlenmiş atlastan (elbiseler) giyinirler, karşılıklı (otururlar).'),
(4468, 44, 54, 'İşte böyle; ve Biz onları iri gözlü hurilerle evlendirmişizdir.'),
(4469, 44, 55, 'Orda, güvenlik içinde her türlü meyveyi istiyorlar;'),
(4470, 44, 56, 'Orda, ilk ölümün dışında başka ölüm tadmazlar. Ve (Allah da) onları cehennem azabından korumuştur.'),
(4471, 44, 57, 'Senin Rabbinden, bir fazl ve (lütuf) olarak. İşte büyük \'mutluluk ve kurtuluş\' budur.'),
(4472, 44, 58, 'Belki onlar öğüt alıp-düşünürler diye, Biz onu (Kur\'an\'ı), senin dilinle kolaylaştırdık.'),
(4473, 44, 59, 'Öyleyse sen gözleyip-bekle; elbette onlar da gözleyip-bekliyorlar.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Ha, Mim.'),
(4475, 45, 2, 'Kitab\'ın indirilmesi, üstün ve güçlü olan, hüküm ve hikmet sahibi Allah\'tandır.'),
(4476, 45, 3, 'Şüphesiz, mü\'minler için göklerde ve yerde ayetler vardır.'),
(4477, 45, 4, 'Sizin yaratılışınızda ve türetip-yaydığı canlılarda kesin bilgiyle inanan bir kavim için ayetler vardır.'),
(4478, 45, 5, 'Gece ile gündüzün ardarda gelişinde (veya aykırılığında), Allah\'ın gökten rızık indirip ölümünden sonra yeryüzünü diriltmesinde ve rüzgarları (belli bir düzen içinde) yönetmesinde aklını kullanan bir kavim için ayetler vardır.'),
(4479, 45, 6, 'İşte bunlar, Allah\'ın ayetleridir; sana bunları hak olmak üzere okuyoruz. Öyleyse onlar, Allah\'tan ve O\'nun ayetlerinden sonra hangi söze iman edecekler?'),
(4480, 45, 7, 'Gerçeği sürekli ters yüz eden, günaha düşkün olan herkesin vay haline.'),
(4481, 45, 8, 'Kendisine Allah\'ın ayetleri okunurken işitir, sonra müstekbirce (inatla büyüklük taslayarak) sanki işitmemiş gibi ısrar eder. Artık sen onu acı bir azapla müjdele.'),
(4482, 45, 9, 'Ayetlerimizden bir şey öğrendiği zaman, alay konusu edinir. İşte onlar için aşağılatıcı bir azap vardır.'),
(4483, 45, 10, 'Arkalarından cehennem (onları izlemektedir). Kazandıkları şeyler, onlara hiçbir yarar sağlamaz. Allah\'tan başka edindikleri veliler de. Onlar için büyük bir azap vardır.'),
(4484, 45, 11, 'İşte bu (Kur\'an) bir hidayettir. Rablerinin ayetlerini inkar edenler ise, onlar için, (en) iğrenç olanından acı bir azap vardır.'),
(4485, 45, 12, 'Allah; Kendi emriyle gemiler akıp gitsin ve O\'nun fazlından ararsınız diye, sizin için denize boyun eğdirdi. Umulur ki şükredersiniz.'),
(4486, 45, 13, 'Kendinden (bir nimet olarak) göklerde ve yerde olanların tümüne sizin için boyun eğdirdi. Şüphesiz bunda, düşünebilen bir kavim için gerçekten ayetler vardır.'),
(4487, 45, 14, 'İman edenlere de ki: "(Allah\'ın) Onları kazandıklarıyla cezalandırması için, Allah\'ın günlerini ummayanları (şimdilik) bağışlasınlar."'),
(4488, 45, 15, 'Kim salih bir amelde bulunursa, kendi lehinedir, kim kötülük yaparsa, artık o da kendi aleyhinedir. Sonra siz Rabbinize döndürüleceksiniz.'),
(4489, 45, 16, 'Andolsun, Biz İsrailoğulları\'na kitap, hüküm ve peygamberlik verdik, onları temiz ve güzel şeylerle rızıklandırdık ve onları alemlere üstün kıldık.'),
(4490, 45, 17, 'Ve onlara bu emirden açık belgeler verdik. Fakat onlar, kendilerine ilim geldikten sonra, yalnızca aralarındaki \'hakka tecavüz ve azgınlıktan\' dolayı ihtilafa düştüler. Şüphesiz Rabbin, hakkında ihtilafa düştükleri şeyde kıyamet günü aralarında hüküm verecektir.'),
(4491, 45, 18, 'Sonra seni de bu emirden bir şeriat üzerine kıldık; öyleyse sen ona uy ve bilmeyenlerin heva (istek ve tutku)larına uyma.'),
(4492, 45, 19, 'Çünkü onlar, Allah\'tan (gelecek) hiçbir şeyi senden savamazlar. Şüphesiz zalimler, birbirlerinin velisidirler. Allah ise, muttakilerin velisidir.'),
(4493, 45, 20, 'Bu (Kur\'an), insanlar için basiret (nuruyla Allah\'a yönelten ayet)lerdir, kesin bilgiyle inanan bir kavim için de bir hidayet ve bir rahmettir.'),
(4494, 45, 21, 'Yoksa kötülüklere batıp-yara alanlar, kendilerini iman edip salih amellerde bulunanlar gibi kılacağımızı mı sandılar? Hayatları ve ölümleri bir mi (olacak)? Ne kötü hüküm veriyorlar.'),
(4495, 45, 22, 'Allah, gökleri ve yeri hak olarak yarattı; öyle ki, her nefis kazandıklarıyla karşılık görsün. Onlara zulmedilmez.'),
(4496, 45, 23, 'Şimdi sen, kendi hevasını ilah edinen ve Allah\'ın bir ilim üzere kendisini saptırdığı, kulağını ve kalbini mühürlediği ve gözü üstüne bir perde çektiği kimseyi gördün mü? Artık Allah\'tan sonra ona kim hidayet verecektir? Siz yine de öğüt alıp-düşünmüyor musunuz?'),
(4497, 45, 24, 'Dediler ki: "(Bütün olup biten,) Bu dünya hayatımızdan başkası değildir, ölürüz ve diriliriz; bizi "kesintisi olmayan zaman\' (dehrin akışın)dan başkası yıkıma (helake) uğratmıyor." Oysa onların bununla ilgili hiçbir bilgileri yoktur; yalnızca zannediyorlar.'),
(4498, 45, 25, 'Onlara açık belgeler olarak ayetlerimiz okunduğu zaman, onların (sözde) delilleri: "Eğer doğru sözlüler iseniz, atalarımızı (diriltip) getirin" demekten başkası değildir.'),
(4499, 45, 26, 'De ki: "Allah sizi diriltiyor, sonra sizi öldürüyor, sonra kendisinde hiçbir kuşku olmayan kıyamet günü O sizi biraraya getirip-toplayacaktır. Ancak insanların çoğu bilmezler."'),
(4500, 45, 27, 'Göklerin ve yerin mülkü Allah\'ındır. Kıyamet-saatinin kopacağı gün, (işte) o gün, batılda olanlar hüsrana uğrayacaklardır.'),
(4501, 45, 28, 'O gün sen, her ümmeti diz üstü çökmüş (veya toplanmış) olarak görürsün. Her ümmet, kendi kitabına çağrılır. "Bugün yaptıklarınızla karşılık göreceksiniz."'),
(4502, 45, 29, '"Bu Bizim kitabımızdır; sizin aleyhinizde hak ile konuşuyor. Gerçekten Biz, sizin yaptıklarınızı yazıyorduk."'),
(4503, 45, 30, 'Artık iman edip salih amellerde bulunanlara gelince; Rableri onları Kendi rahmetine sokar. İşte apaçık olan \'büyük mutluluk ve kurtuluş\' budur.'),
(4504, 45, 31, 'İnkar edenlere gelince; "Size karşı ayetlerim okunduğunda büyüklük taslayan (müstekbir olan)lar ve suçlu-günahkar bir kavim olanlar sizler değil miydiniz?"'),
(4505, 45, 32, '"Gerçekten Allah\'ın va\'di haktır, kıyamet-saatinde hiçbir kuşku yoktur" denildiği zaman, siz: "Kıyamet-saati de neymiş, biz bilmiyoruz; biz yalnızca bir zan (ve tahmin)da bulunup zannediyoruz; biz, kesin bir bilgiyle inanmakta olanlar değiliz" demiştiniz.'),
(4506, 45, 33, 'Onların yaptıkları şeylerin kötülüğü kendileri için açığa çıktı ve alay konusu edindikleri de onları sarıp-kuşattı.'),
(4507, 45, 34, 'Denildi ki: "Bugününüzle karşılaşmayı unuttuğunuz gibi, Biz de sizi bugün unutuyoruz. Barınma yeriniz ateştir. Ve sizin için hiçbir yardımcı yoktur."'),
(4508, 45, 35, '"Bunun nedeni şudur: Çünkü siz Allah\'ın ayetlerini alay konusu edindiniz; dünya hayatı da sizi aldattı." Böylece ne ordan (ateşten) çıkarılırlar, ne (Allah\'tan) hoşnutluk dilekleri kabul edilir.'),
(4509, 45, 36, 'Şu halde hamd, göklerin Rabbi, yerin Rabbi ve alemlerin Rabbi Allah\'ındır.'),
(4510, 45, 37, 'Göklerde ve yerde büyüklük O\'nundur. O, üstün ve güçlüdür, hüküm ve hikmet sahibidir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Ha, Mim.'),
(4512, 46, 2, 'Kitab\'ın indirilmesi, üstün ve güçlü, hüküm ve hikmet sahibi Allah\'tandır.'),
(4513, 46, 3, 'Biz gökleri, yeri ve ikisi arasında bulunanları ancak hak ve adı konulmuş bir ecel (belli bir süre) olarak yarattık. İnkar edenler ise, uyarıldıkları şeyden yüz çeviren(kimseler)dir.'),
(4514, 46, 4, 'De ki: "Gördünüz mü haber verin; Allah\'tan başka taptıklarınız, yerden neyi yaratmışlar, bana gösterin? Yoksa onların göklerde bir ortaklığı mı var? Eğer doğru sözlüler iseniz, bundan önce bir kitap ya da bir ilim kalıntısı (veya bir eser) varsa, bana getirin."'),
(4515, 46, 5, 'Allah\'ı bırakıp kıyamet gününe kadar kendisine icabet etmeyecek şeylere tapandan daha sapmış kimdir? Oysa onlar, bunların tapmalarından habersizdirler.'),
(4516, 46, 6, 'İnsanlar haşrolunduğu (biraraya getirildiği) zaman, (Allah\'tan başka taptıkları) onlara düşman kesilirler ve (kendilerine) ibadet etmelerini de tanımazlar.'),
(4517, 46, 7, 'Onlara açık belgeler olarak ayetlerimiz okunduğu zaman, o inkar edenler kendilerine gelmiş olan hak için dediler ki: "Bu, apaçık bir büyüdür."'),
(4518, 46, 8, 'Yoksa: "Kendisi onu uydurdu" mu diyorlar? De ki: "Eğer ben uydurdumsa, bu durumda siz, Allah\'tan bana (gelecek) hiçbir şeye malik (engel) olamazsınız. Sizin kendisi (Kur\'an) hakkında, ne taşkınlıklar yaptığınızı O daha iyi bilendir. Benimle sizin aranızda şahid olarak O yeter. O, çok bağışlayandır, çok esirgeyendir."'),
(4519, 46, 9, 'De ki: "Ben elçilerden bir türedi değilim, bana ve size ne yapılacağını da bilemiyorum. Ben, yalnızca bana vahyedilmekte olana uyuyorum ve ben, apaçık bir uyarıcıdan başkası değilim."'),
(4520, 46, 10, 'De ki: "Gördünüz mü-haber verin; eğer (bu Kur\'an,) Allah Katından ise, siz de onu inkar etmişseniz ve İsrailoğulları\'ndan bir şahid bunun bir benzerine şahidlik edip iman etmişse ve siz de büyüklük taslamışsanız (bunun sonucu ne olacak)? Şüphesiz Allah, zalim olan bir kavmi hidayete erdirmez.'),
(4521, 46, 11, 'İnkar edenler, iman edenler için dediler ki: "Eğer O (Kur\'an veya iman) hayırlı bir şey olsaydı, ona bizden önce koşup-yetişemezlerdi." Oysa onlar, onunla hidayete ermediklerinden: "Bu, eski bir yalandır" diyecekler.'),
(4522, 46, 12, 'Bundan önce de, bir rehber (imam) ve bir rahmet olarak Musa\'nın kitabı var. Bu da, zulmedenleri uyarmak ve ihsanda bulunanlara bir müjde olmak üzere (kendinden önceki kitapları) doğrulayıcı ve Arapça bir dil ile olan bir Kitap\'tır.'),
(4523, 46, 13, 'Şüphesiz: "Bizim Rabbimiz Allah\'tır" deyip sonra doğru bir istikamet tutturanlar (yok mu); artık onlar için korku yoktur ve onlar mahzun olmayacaklardır.'),
(4524, 46, 14, 'İşte onlar, cennet halkıdır; yaptıklarına karşılık olmak üzere, içinde ebedi olarak kalacaklardır.'),
(4525, 46, 15, 'Biz insana, \'anne ve babasına\' iyilikle davranmasını tavsiye ettik. Annesi onu güçlükle taşıdı ve onu güçlükle doğurdu. Onun (hamilelikte) taşınması ve sütten kesilmesi, otuz aydır. Nihayet güçlü (erginlik) çağına erip kırk yıl (yaşın)a ulaşınca, dedi ki: "Rabbim, bana, anne ve babama verdiğin nimete şükretmemi ve Senin razı olacağın salih bir amelde bulunmamı bana ilham et; benim için soyumda salahı ver. Gerçekten ben tevbe edip Sana yöneldim ve gerçekten ben Müslümanlardanım."'),
(4526, 46, 16, 'İşte bunlar; yaptıklarının en güzelini kabul ederiz ve kötülüklerinden geçeriz; (bunlar) cennet halkı içindedirler. (İşte bu,) Onlara va\'dolunan doğru bir vaaddir.'),
(4527, 46, 17, 'O kimse ki, anne ve babasına: "Öf size, benden önce nice nesiller gelip geçmişken, beni (diriltilip) çıkarılacağımla mı tehdit ediyorsunuz?" dedi. O ikisi (anne ve babası) ise Allah\'a yakararak: "Yazıklar sana, iman et, şüphesiz Allah\'ın va\'di haktır." (derler; fakat) O: "Bu, geçmişlerin masallarından başkası değildir" der.'),
(4528, 46, 18, 'İşte bunlar, cinlerden ve insanlardan kendilerinden evvel gelip-geçmiş ümmetler içinde (azap) sözü üzerlerine hak olmuş kimselerdir. Gerçekten onlar, ziyana uğrayanlardır.'),
(4529, 46, 19, 'Her biri için yaptıklarınızdan dolayı dereceler vardır; öyle ki amelleri kendilerine eksiksizce ödensin ve onlar zulme de uğratılmazlar.'),
(4530, 46, 20, 'İnkar edenler ateşe sunulacakları gün, (onlara şöyle denir:) "Siz dünya hayatınızda bütün \'güzellikleriniz ve zevklerinizi tüketip-yok ettiniz, onlarla yaşayıp-zevk sürdünüz. İşte yeryüzünde haksız yere büyüklenmeniz (istikbarınız) ve fasıklıkta bulunmanızdan dolayı, bugün alçaltıcı bir azap ile cezalandırılacaksınız."'),
(4531, 46, 21, 'Ad\'ın kardeşini hatırla; onun önünden ve ardından nice uyarıcılar gelip geçmişti; hani o, Ahkaf\'taki kavmini: "Allah\'tan başkasına kulluk etmeyin, gerçekten ben, sizin için büyük bir günün azabından korkarım" diye uyarmıştı.'),
(4532, 46, 22, 'Dediler ki: "Sen, bizi ilahlarımızdan çevirmek için mi bize geldin? Şu halde eğer doğru söylüyorsan, tehdit ettiğin şeyi, bize getir."'),
(4533, 46, 23, 'Dedi ki: "İlim ancak Allah Katındadır. Ben size gönderildiğim şeyi tebliğ ediyorum; ancak sizi cahillik eden bir kavim olarak görüyorum."'),
(4534, 46, 24, 'Derken, onu (azabı) vadilerine doğru yönelerek gelen bir bulut şeklinde gördükleri zaman, "Bu bize yağmur yağdıracak bir buluttur" dediler. Hayır, o, kendisi için acele ettiğiniz şeydir. Bir rüzgar; onda acı bir azap vardır.'),
(4535, 46, 25, 'Rabbinin emriyle herşeyi yerle bir eder. Böylece meskenlerinden başka, hiçbir şey(leri) görünemez duruma düştüler. İşte Biz, suçlu-günahkar bir kavmi böyle cezalandırırız.'),
(4536, 46, 26, 'Andolsun, Biz onları, sizleri kendisinde yerleşik kılmadığımız yerlerde (size vermediğimiz güç ve iktidar imkanlarıyla) yerleşik kıldık ve onlara işitme, görme (duygularını) ve gönüller verdik. Ancak ne işitme, ne görme (duyuları) ve ne gönülleri kendilerine herhangi bir şey sağlamadı. Çünkü onlar, Allah\'ın ayetlerini inkar ediyorlardı. Alay konusu edindikleri şey, onları sarıp-kuşattı.'),
(4537, 46, 27, 'Andolsun, Biz çevrenizde bulunan şehirlerden (birçoğunu) yıkıma uğrattık ve belki dönerler diye ayetleri çeşitli şekillerde açıkladık.'),
(4538, 46, 28, 'Bu durumda, Allah\'ı bırakıp yakınlık (sağlamak) için edindikleri ilahlar, onlara yardım etselerdi ya. Hayır, onlar, kendilerinden kaybolup gittiler. Bu (yalancı ilahlar ve onlara yükledikleri), onların yalanları ve uydurduklarıdır.'),
(4539, 46, 29, 'Hani cinlerden birkaçını, Kur\'an dinlemek üzere sana yöneltmiştik. Böylece onun huzuruna geldikleri zaman, dediler ki: "Kulak verin;" sonra bitirilince kendi kavimlerine uyarıcılar olarak döndüler.'),
(4540, 46, 30, 'Dediler ki: "Ey kavmimiz, gerçekten biz, Musa\'dan sonra indirilen, kendinden öncekileri doğrulayan bir Kitap dinledik; hakka ve doğru olan yola yöneltip-iletmektedir."'),
(4541, 46, 31, '"Ey kavmimiz, Allah\'a davet edene icabet edin ve O’na iman edin; günahlarınızdan bir kısmını bağışlasın ve sizi acı bir azaptan korusun."'),
(4542, 46, 32, '"Kim Allah\'a davet edene icabet etmezse, artık o, yeryüzünde (Allah\'ı aciz bırakacak değildir ve onun O\'ndan başka) velileri yoktur. İşte onlar, apaçık bir sapıklık içindedirler."'),
(4543, 46, 33, 'Onlar görmüyorlar mı ki, gökleri ve yeri yaratan ve onları yaratmaktan yorulmayan (Allah), ölüleri de diriltmeye güç yetirir. Hayır; gerçekten O, herşeye güç yetirendir.'),
(4544, 46, 34, 'İnkar edenler ateşe sunulacakları gün, (onlara şöyle denir:) "Bu gerçek değil miymiş?" Onlar: "Rabbimiz\'e andolsun, evet (öyledir)" derler. (Allah da:) "Öyleyse inkar ettiklerinizden dolayı azabı tadın" dedi.'),
(4545, 46, 35, 'Artık sen sabret; Resullerden azim sahiplerinin sabrettikleri gibi, Onlar için de acele etme. Onlar, tehdit edildikleri şeyi (azabı) gördükleri gün, sanki gündüzün yalnızca bir saati kadar yaşamış(olacak)lardır. (Bu,) Bir tebliğdir. Artık fasık olan bir kavimden başkası yıkıma uğratılır mı?');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'Onlar ki inkar ettiler ve Allah\'ın yolundan alıkoydular, (işte Allah da) onların amellerini giderip-boşa çıkarmıştır.'),
(4547, 47, 2, 'İman edip salih amellerde bulunan ve Muhammed\'e indirilen (Kur\'an)a -ki o Rablerinden bir haktır- iman edenlerin (Allah), kötülüklerini örtüp-bağışlamış, durumlarını düzeltip-ıslah etmiştir.'),
(4548, 47, 3, 'İşte böyle; hiç şüphesiz, inkar edenler batıl olana uymuşlar; ve hiç şüphesiz, iman edenler Rablerinden olan hakka uymuşlardır. İşte Allah, insanlara kendi örneklerini böyle vererek gösteriyor.'),
(4549, 47, 4, 'Öyleyse, inkar edenlerle (savaş sırasında) karşı karşıya geldiğiniz zaman, hemen boyunlarını vurun; sonunda onları \'iyice bozguna uğratıp zafer kazanınca da\' artık (esirler için) bağı sımsıkı tutun. Bundan sonra ya bir lütuf olarak (onları bırakın) veya bir fidye (karşılığı salıverin). Öyle ki savaş ağırlıklarını bıraksın (sona ersin). İşte böyle; eğer Allah dilemiş olsaydı, elbette onlardan intikam alırdı. Ancak (savaş,) sizleri birbirinizle denemesi içindir. Allah yolunda öldürülenlerin ise; kesin olarak (Allah,) amellerini giderip-boşa çıkarmaz.'),
(4550, 47, 5, 'Onları hidayete erdirecek ve durumlarını düzeltip-ıslah edecektir.'),
(4551, 47, 6, 'Ve onları, kendilerine tarif edip-tanıttığı cennete sokacaktır.'),
(4552, 47, 7, 'Ey iman edenler, eğer siz Allah\'a (Allah adına İslama ve Müslümanlara) yardım ederseniz, O da size yardım eder ve sizin ayaklarınızı sağlamlaştırır.'),
(4553, 47, 8, 'İnkar edenler ise, yüzükoyun-düşüş, onlara olsun; (Allah,) amellerini giderip-boşa çıkarmıştır.'),
(4554, 47, 9, 'İşte böyle; çünkü onlar, Allah\'ın indirdiğini çirkin (kerih) gördüler, bundan dolayı, O da, onların amellerini boşa çıkardı.'),
(4555, 47, 10, 'Onlar, yeryüzünde gezip dolaşmıyorlar mı ki, kendilerinden öncekilerin nasıl bir sona uğradıklarını görsünler. Allah, onları yerle bir etti. O kafirler için de bunun bir benzeri vardır.'),
(4556, 47, 11, 'İşte böyle; çünkü Allah, iman edenlerin velisidir; kafirlerin ise, velisi yoktur.'),
(4557, 47, 12, 'Şüphesiz Allah, iman edip salih amellerde bulunanları, altından ırmaklar akan cennetlere sokar. İnkar edenler ise, metalanırlar ve hayvanların yemesi gibi yerler; ateş, onlar için bir konaklama yeridir.'),
(4558, 47, 13, 'Seni sürüp-çıkaran memleketinden kuvvet bakımından daha üstün nice memleketler vardı ki, Biz onları yıkıma uğrattık da kendileri için hiçbir yardımcı yoktu.'),
(4559, 47, 14, 'Şimdi Rabbinden apaçık bir belge üzerinde bulunan kimse, kötü ameli kendisine \'süslü ve çekici gösterilmiş\' ve kendi heva (istek ve tutku)larına uyan kimseler gibi midir?'),
(4560, 47, 15, 'Takva sahiplerine va\'dedilen cennetin misali (şudur): İçinde bozulmayan sudan ırmaklar, tadı değişmeyen sütten ırmaklar, içenler için lezzet veren şaraptan ırmaklar ve süzme baldan ırmaklar vardır ve orda onlar için meyvelerin her türlüsünden ve Rablerinden bir mağfiret vardır. Hiç (böyle mükafaatlanan bir kişi), ateşin içinde ebedi olarak kalan ve bağırsaklarını \'parça parça koparan\' kaynar sudan içirilen kimseler gibi olur mu?'),
(4561, 47, 16, 'Onlardan kimi gelip seni dinler. Nitekim yanından çıkıp-gittikleri zaman, ilim verilenlere derler ki: "O biraz önce ne söyledi?" İşte onlar; Allah, onların kalplerini mühürlemiştir ve onlar kendi heva (istek ve tutku)larına uymuşlardır.'),
(4562, 47, 17, 'Hidayeti bulmuş olanlara gelince; (Allah,) hidayetlerini artırmış ve takvalarını vermiştir.'),
(4563, 47, 18, 'Artık onlar, kıyamet-saatinin kendilerine apansız gelmesinden başkasını mı gözlüyorlar? İşte onun işaretleri gelmiştir. Fakat kendilerine geldikten sonra öğüt alıp-düşünmeleri onlara neyi sağlar?'),
(4564, 47, 19, 'Şu halde bil; gerçekten, Allah\'tan başka İlah yoktur. Hem kendi günahın, hem mü\'min erkekler ve mü\'min kadınlar için mağfiret dile. Allah, sizin dönüp-dolaşacağınız yeri bilir, konaklama yerinizi de.'),
(4565, 47, 20, 'İman edenler, derler ki: "(Savaş izni için) Bir sûre indirilmeli değil miydi?" Fakat, içinde savaş (kıtal) zikri geçen muhkem bir sure indirildiği zaman, kalplerinde hastalık olanların, üzerine ölüm baygınlığı çökmüş olanların bakışı gibi sana baktıklarını gördün. Oysa onlara evla (olan):'),
(4566, 47, 21, 'İtaat ve maruf (güzel) sözdü. Fakat iş, kesinlik ve kararlılık gerektirdiği zaman, şayet Allah\'a sadakat gösterselerdi, şüphesiz onlar için daha hayırlı olurdu.'),
(4567, 47, 22, 'Demek, \'iş başına gelip yönetimi ele alırsanız\' hemen yeryüzünde fesad (bozgunculuk) çıkaracak ve akrabalık bağlarınızı koparıp parçalayacaksınız, öyle mi?'),
(4568, 47, 23, 'İşte bunlar; Allah onları lanetlemiş, böylece (kulaklarını) sağırlaştırmış ve basiret (göz)lerini de kör etmiştir.'),
(4569, 47, 24, 'Öyle olmasa, Kur\'an\'ı iyiden iyiye düşünmezler miydi? Yoksa birtakım kalpler üzerinde kilitler mi vurulmuş?'),
(4570, 47, 25, 'Şüphesiz, kendilerine hidayet açıkça belli olduktan sonra, gerisin geri (küfre) dönenleri, şeytan kışkırtmış ve uzun emellere kaptırmıştır.'),
(4571, 47, 26, 'İşte böyle; çünkü gerçekten onlar, Allah\'ın indirdiğini çirkin karşılayanlara dediler ki: "Size bazı işlerde itaat edeceğiz." Oysa Allah, sakladıkları şeyleri (sır olarak konuştuklarını) biliyor.'),
(4572, 47, 27, 'Öyleyse melekler, yüzlerine ve arkalarına vura vura canlarını aldıkları zaman nasıl olacak?'),
(4573, 47, 28, 'İşte böyle; çünkü gerçekten onlar, Allah\'ı gazablandıran şeye uydular ve O\'nu razı edecek şeyleri çirkin karşıladılar; bundan dolayı (Allah,) amellerini boşa çıkardı.'),
(4574, 47, 29, 'Yoksa kalplerinde hastalık bulunanlar, Allah\'ın kinlerini hiç (ortaya) çıkarmayacağını mı sandılar?'),
(4575, 47, 30, 'Eğer Biz dilersek, sana onları elbette gösteririz, böylelikle onları simalarından tanırsın. Andolsun, sen onları, sözlerin söyleniş tarzından da tanırsın. Allah, amellerinizi bilir.'),
(4576, 47, 31, 'Andolsun, Biz sizden mücahid olanlarla sabredenleri bilinceye (belli edip ortaya çıkarıncaya) kadar, deneyeceğiz ve haberlerinizi sınayacağız (açıklayacağız).'),
(4577, 47, 32, 'Şüphesiz inkar edenler, Allah\'ın yolundan alıkoyanlar ve kendilerine hidayet açıkça belli olduktan sonra \'elçiye karşı gelip zorluk çıkaranlar\', kesin olarak Allah\'a hiçbir şeyle zarar veremezler. (Allah,) Onların amellerini boşa çıkaracaktır.'),
(4578, 47, 33, 'Ey iman edenler, Allah\'a itaat edin, Resûl’e itaat edin ve kendi amellerinizi geçersiz kılmayın.'),
(4579, 47, 34, 'Şüphesiz, inkar edenler, Allah\'ın yolundan alıkoyanlar, sonra ölenler; işte Allah, onlara kesinlikle mağfiret etmeyecektir.'),
(4580, 47, 35, 'Öyleyse, siz üstün (bir durumda) iken, barışa çağırmak suretiyle gevşekliğe düşmeyin. Allah, sizinle beraberdir; O, sizin amellerinizi asla eksiltmez.'),
(4581, 47, 36, 'Gerçekten dünya hayatı, ancak bir oyun ve tutkulu bir oyalanmadır. Eğer iman ederseniz ve sakınırsanız, O, size ecirlerinizi verir ve mallarınızı da istemez.'),
(4582, 47, 37, 'Eğer sizden onları(n tümünü) isteyip sizi çıplak bırakacak olursa, cimrilik edersiniz ve sizin kinlerinizi de ortaya çıkarmış olur.'),
(4583, 47, 38, 'İşte sizler böylesiniz; Allah yolunda infak etmeye çağrılıyorsunuz; buna rağmen bazılarınız cimrilik ediyor. Kim cimrilik ederse, artık o, ancak kendi nefsine cimrilik eder. Allah ise, Ğaniy (hiçbir şeye ihtiyacı olmayan)dır; fakir olan sizlersiniz. Eğer siz yüz çevirecek olursanız, sizden başka bir kavmi getirip-değiştirir. Sonra onlar, sizin benzeriniz de olmazlar.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Şüphesiz, Biz sana apaçık bir fetih verdik.'),
(4585, 48, 2, 'Öyle ki Allah, senin geçmiş ve gelecek (her) günahını bağışlasın, üzerindeki nimetini tamamlasın ve seni dosdoğru bir yola yöneltsin.'),
(4586, 48, 3, 'Ve Allah, sana \'üstün ve onurlu\' bir zaferle yardım etsin.'),
(4587, 48, 4, 'Mü\'minlerin kalplerine, imanlarına iman katıp-arttırsınlar diye, \'güven duygusu ve huzur\' indiren O\'dur. Göklerin ve yerin orduları Allah\'ındır: Allah bilendir, hüküm ve hikmet sahibidir.'),
(4588, 48, 5, '(Bütün bunlar,) Mü\'min erkekleri ve mü\'min kadınları, içinde ebedi kalıcılar olmak üzere, altından ırmaklar akan cennetlere sokması ve kötülüklerini örtüp-bağışlaması içindir. İşte bu, Allah Katında \'büyük kurtuluş ve mutluluk’tur.'),
(4589, 48, 6, 'Bir de; kötü bir zan ile zanda bulunan münafık erkeklerle münafık kadınları ve müşrik erkeklerle müşrik kadınları azaplandırması için. O kötülük çemberi, tepelerine insin. Allah, onlara karşı gazablanmış, onları lanetlemiş ve onlara cehennemi hazırlamıştır. Varacakları yer ne kötüdür.'),
(4590, 48, 7, 'Göklerin ve yerin orduları Allah\'ındır. Allah, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(4591, 48, 8, 'Şüphesiz, Biz seni bir şahid, bir müjde verici ve bir uyarıcı olarak gönderdik.'),
(4592, 48, 9, 'Ki Allah\'a ve Resûlü’ne iman etmeniz, O’nu savunup-desteklemeniz, O’nu en içten bir saygıyla-yüceltmeniz ve sabah akşam O\'nu (Allah\'ı) tesbih etmeniz için.'),
(4593, 48, 10, 'Şüphesiz sana biat edenler, ancak Allah\'a biat etmişlerdir. Allah\'ın eli, onların ellerinin üzerindedir. Şu halde, kim ahdini bozarsa, artık o, ancak kendi aleyhine ahdini bozmuş olur. Kim de Allah\'a verdiği ahdine vefa gösterirse, artık O da, ona büyük bir ecir verecektir.'),
(4594, 48, 11, 'Bedevilerden geride bırakılanlar, sana diyecekler ki: "Bizi mallarımız ve ailelerimiz meşgul etti. Bundan dolayı bizim için mağfiret dile." Onlar, kalplerinde olmayan şeyi dilleriyle söylüyorlar. De ki: "Şimdi Allah, size bir zarar isteyecek ya da bir yarar dileyecek olsa, sizin için Allah\'a karşı kim herhangi bir şeyle güç yetirebilir? Hayır, Allah yaptıklarınızı haber alandır."'),
(4595, 48, 12, 'Hayır, siz Peygamberin ve mü\'minlerin, ailelerine ebedi olarak bir daha dönmeyeceklerini zannettiniz; bu, kalplerinizde çekici kılındı ve kötü bir zan ile zanda bulundunuz da, yıkıma uğramış bir topluluk oldunuz.'),
(4596, 48, 13, 'Kim Allah\'a ve Resûlü’ne iman etmezse, (bilsin ki) gerçekten Biz, kafirler için çılgınca yanan bir ateş hazırlamışızdır.'),
(4597, 48, 14, 'Göklerin ve yerin mülkü Allah\'ındır; dilediğine mağfiret eder, dilediğini azaplandırır. Allah, çok bağışlayan, çok esirgeyendir.'),
(4598, 48, 15, '(Savaştan) Geride bırakılanlar, siz ganimetleri almaya gittiğiniz zaman diyeceklerdir ki: "Bizi bırakın da sizi izleyelim." Onlar, Allah\'ın kelamını değiştirmek istiyorlar. De ki: "Siz, kesin olarak bizim izimizden gelemezsiniz. Allah, daha evvel böyle buyurdu." Bunun üzerine: "Hayır, bizi kıskanıyorsunuz" diyecekler. Hayır, onlar pek az anlayan kimselerdir.'),
(4599, 48, 16, 'Bedevilerden geride bırakılanlara de ki: "Siz yakında zorlu savaşçı olan bir kavme çağrılacaksınız; onlarla (ya) savaşırsınız ya da (onlar) Müslüman olurlar. Bu durumda eğer itaat ederseniz, Allah, size güzel bir ecir verir; eğer bundan önce sırt çevirdiğiniz gibi (yine) sırt çevirirseniz, sizi acı bir azap ile azaplandırır."'),
(4600, 48, 17, 'Kör olana güçlük (sorumluluk) yoktur, topal olana güçlük yoktur, hasta olana da güçlük yoktur. Kim Allah\'a ve Resûlü’ne itaat ederse, (Allah) onu, altından ırmaklar akan cennetlere sokar. Kim de sırt çevirirse, onu acı bir azap ile azaplandırır.'),
(4601, 48, 18, 'Andolsun, Allah, sana o ağacın altında biat ederlerken mü\'minlerden razı olmuştur, kalplerinde olanı bilmiş ve böylece üzerlerine \'güven duygusu ve huzur\' indirmiştir ve onlara yakın bir fethi sevap (karşılık) olarak vermiştir.'),
(4602, 48, 19, 'Ve alacakları birçok ganimetleri de. Allah, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(4603, 48, 20, 'Allah, alacağınız daha birçok ganimetleri size va\'detti, bunu size hemencecik verdi ve insanların ellerini sizden çekti ki, (bu,) mü\'minler için bir ayet olsun ve sizi dosdoğru bir yola yöneltsin.'),
(4604, 48, 21, 'Ve (daha) başka (nice nimetler de, ki,) siz henüz onlara güç yetirmiş değilsiniz; (ama) gerçekten Allah, onları kuşatmıştır. Allah, herşeye güç yetirendir.'),
(4605, 48, 22, 'Kafir olanlar, sizinle savaşmış olsalardı, arkalarını dönüp kaçarlardı; sonra, ne bir veli (koruyucu dost), ne bir yardımcı bulamazlardı.'),
(4606, 48, 23, '(Bu,) Allah\'ın öteden beri sürüp giden sünnetidir. Sen Allah\'ın sünnetinde kesinlikle bir değişiklik bulamazsın.'),
(4607, 48, 24, 'Onlara karşı size zafer verdikten sonra, Mekke\'nin göbeğinde ellerini sizden ve sizin de ellerinizi onlardan çeken O\'dur. Allah, yaptıklarınızı hakkıyla görendir.'),
(4608, 48, 25, 'Ki onlar, inkar ettiler, sizi Mescid-i Haram\'dan ve durdurulmakta (bekletilmekte) olan hediyeleri (kurbanları), yerlerine varmaktan alıkoydular. Eğer kendilerini bilmediğiniz mü\'min erkekler ve mü\'min kadınları, bilgisizlik dolayısıyla darmadağın edip de bu yüzden size \'dayanılmaz bir sıkıntı\' dokunmayacak olsaydı (o zaman durum farklı olurdu. Durumunun böyle olması,) Allah\'ın dilediğini rahmetine sokması içindir. Eğer (karışık yaşayan mü\'minler), seçilip ayrılmış olsalardı, muhakkak içlerinden inkar edenleri acı bir azap ile azaplandırırdık.'),
(4609, 48, 26, 'Hani o inkar edenler, kendi kalplerinde, \'öfkeli soy koruyuculuğu\'nu (hamiyeti), cahiliyenin \'öfkeli soy koruyuculuğunu\' kılıp-kışkırttıkları zaman, hemen Allah; elçisinin ve mü\'minlerin üzerine \'(kalbi teskin eden) güven ve yatışma duygusunu\' indirdi ve onları "takva sözü" üzerinde \'kararlılıkla ayakta tuttu." Zaten onlar da, buna layık ve ehil idiler. Allah, herşeyi hakkıyla bilendir.'),
(4610, 48, 27, 'Andolsun Allah, elçisinin gördüğü rüyanın hak olduğunu doğruladı. Eğer Allah dilerse, mutlaka siz Mescid-i Haram\'a güven içinde, saçlarınızı tıraş etmiş, (kiminiz de) kısaltmış olarak (ve) korkusuzca gireceksiniz. Fakat Allah, sizin bilmediğinizi bildi, böylece bundan önce size yakın bir fetih (nasib) kıldı.'),
(4611, 48, 28, 'Ki O, elçilerini hidayetle ve hak din ile, diğer bütün dinlere karşı üstün kılmak için gönderdi. Şahid olarak Allah yeter.'),
(4612, 48, 29, 'Muhammed, Allah\'ın elçisidir. Ve onunla birlikte olanlar da kafirlere karşı zorlu, kendi aralarında ise merhametlidirler. Onları, rüku edenler, secde edenler olarak görürsün; onlar, Allah\'tan bir fazl (lütuf ve ihsan) ve hoşnutluk arayıp-isterler. Belirtileri, secde izinden yüzlerindedir. İşte onların Tevrat\'taki vasıfları budur: İncil\'deki vasıfları ise: Sanki bir ekin; filizini çıkarmış, derken onu kuvvetlendirmiş, derken kalınlaşmış, sonra sapları üzerinde doğrulup-boy atmış (ki bu,) ekicilerin hoşuna gider. (Bu örnek,) Onunla kafirleri öfkelendirmek içindir. Allah, içlerinden iman edip salih amellerde bulunanlara bir mağfiret ve büyük bir ecir va\'detmiştir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Ey iman edenler, Allah\'ın Resûlü’nün huzurunda öne geçmeyin ve Allah\'tan sakının. Şüphesiz Allah, işitendir, bilendir.'),
(4614, 49, 2, 'Ey iman edenler, seslerinizi peygamberin sesi üstünde yükseltmeyin ve birbirinize bağırdığınız gibi, ona sözle bağırıp-söylemeyin; yoksa siz şuurunda değilken, amelleriniz boşa gider.'),
(4615, 49, 3, 'Şüphesiz, Allah’ın Resûlü’nün yanında seslerini alçak tutanlar; işte onlar, Allah kalplerini takva için imtihan etmiştir. Onlar için bir mağfiret ve büyük bir ecir vardır.'),
(4616, 49, 4, 'Şüphesiz, hücrelerin ardından sana seslenenler de, onların çoğu aklını kullanmıyor.'),
(4617, 49, 5, 'Eğer gerçekten, yanlarına çıkıncaya kadar sabretmiş olsalardı, herhalde (bu,) kendileri için daha hayırlı olurdu. Allah, çok bağışlayandır, çok esirgeyendir.'),
(4618, 49, 6, 'Ey iman edenler, eğer bir fasık, size bir haber getirirse, onu \'etraflıca araştırın\'. Yoksa cehalet sonucu, bir kavme kötülükte bulunursunuz da, sonra işlediklerinize pişman olursunuz.'),
(4619, 49, 7, 'Ve bilin ki Allah\'ın Resûlü içinizdedir. Eğer o, size birçok işlerde uysaydı, elbette sıkıntıya düşerdiniz. Ancak Allah size imanı sevdirdi, onu kalplerinizde süsleyip-çekici kıldı ve size inkarı, fıskı ve isyanı çirkin gösterdi. İşte onlar, doğru yolu bulmuş (irşad) olanlardır.'),
(4620, 49, 8, 'Allah\'tan bir fazl (bir ihsan ve lütuf) ve bir nimet olarak. Allah, bilendir hüküm ve hikmet sahibidir.'),
(4621, 49, 9, 'Mü\'minlerden iki topluluk çarpışacak olursa, aralarını bulup-düzeltin. Şayet biri diğerine tecavüzde bulunacak olursa, artık tecavüzde bulunanla, Allah\'ın emrine dönünceye kadar savaşın; eğer sonunda (Allah\'ın emrini kabul edip) dönerse, bu durumda adaletle aralarını bulun ve (her konuda) adil davranın. Şüphesiz Allah, adil olanları sever.'),
(4622, 49, 10, 'Mü\'minler ancak kardeştirler. Öyleyse kardeşlerinizin arasını bulup-düzeltin ve Allah\'tan korkup-sakının; umulur ki esirgenirsiniz.'),
(4623, 49, 11, 'Ey iman edenler, bir kavim (bir başka) kavimle alay etmesin, belki kendilerinden daha hayırlıdırlar; kadınlar da kadınlarla (alay etmesin), belki kendilerinden daha hayırlıdırlar. Kendi nefislerinizi (kendi kendinizi) yadırgayıp-küçük düşürmeyin ve birbirinizi \'olmadık-kötü lakablarla\' çağırmayın. İmandan sonra fasıklık ne kötü bir isimdir. Kim tevbe etmezse, işte onlar, zalim olanların ta kendileridir.'),
(4624, 49, 12, 'Ey iman edenler, zandan çok kaçının; çünkü zannın bir kısmı günahtır. Tecessüs etmeyin (birbirinizin gizli yönlerini araştırmayın). Kiminiz kiminizin gıybetini yapmasın (arkasından çekiştirmesin.) Sizden biriniz, ölü kardeşinin etini yemeyi sever mi? İşte, bundan tiksindiniz. Allah\'tan korkup-sakının. Şüphesiz Allah, tevbeleri kabul edendir, çok esirgeyendir.'),
(4625, 49, 13, 'Ey insanlar, gerçekten, Biz sizi bir erkek ve bir dişiden yarattık ve birbirinizle tanışmanız için sizi halklar ve kabileler (şeklinde) kıldık. Şüphesiz, Allah Katında sizin en üstün (kerim) olanınız, (ırk ya da soyca değil) takvaca en ileride olanınızdır. Şüphesiz Allah, bilendir, haber alandır.'),
(4626, 49, 14, 'Bedeviler, dedi ki: "İman ettik." De ki: "Siz iman etmediniz; ancak "İslam (Müslüman veya teslim) olduk deyin. İman henüz kalplerinize girmiş değildir. Eğer Allah\'a ve Resûlü’ne itaat ederseniz, O, sizin amellerinizden hiçbir şeyi eksiltmez. Şüphesiz Allah, çok bağışlayandır, çok esirgeyendir."'),
(4627, 49, 15, 'Mü\'min olanlar, ancak o kimselerdir ki, onlar, Allah\'a ve Resûlü’ne iman ettiler, sonra hiçbir kuşkuya kapılmadan Allah yolunda mallarıyla ve canlarıyla mücadele ettiler. İşte onlar, sadık (doğru) olanların ta kendileridir.'),
(4628, 49, 16, 'De ki: "Siz Allah\'a dininizi mi öğreteceksiniz? Oysa Allah, göklerde ve yerde olanları bilir. Allah, herşeyi bilendir."'),
(4629, 49, 17, 'Müslüman oldular diye sana minnet etmektedirler. De ki: "Müslümanlığınızı bana karşı minnet (konusu) etmeyin. Tam tersine, sizi imana yönelttiği için Allah size minnet etmektedir. Eğer doğru sözlüler iseniz (bunu böyle kabullenmeniz gerekir.)"'),
(4630, 49, 18, 'Şüphesiz Allah, göklerin ve yerin gaybını bilir. Allah, yaptıklarınızı görendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Kaf. \'Şerefli üstün\' Kur\'an\'a andolsun.'),
(4632, 50, 2, 'Hayır, onlara kendilerinden bir uyarıcı gelmesine şaştılar da, o kafirler: "Bu şaşılacak bir şey" dediler.'),
(4633, 50, 3, '"Biz öldüğümüz ve toprak olduğumuz zaman mı (yeniden diriltilecek mişiz)? Bu uzak bir dönüş (iddiasıdır)."'),
(4634, 50, 4, 'Doğrusu Biz, yerin onlardan ne eksilttiğini bilmişizdir. Katımız\'da (bütün bunları) saklayıp-koruyan bir kitap vardır.'),
(4635, 50, 5, 'Hayır, hak kendilerine gelince yalanladılar. Şimdi onlar, derin bir sarsıntı içinde bulunuyorlar.'),
(4636, 50, 6, 'Üzerlerindeki göğe bakmıyorlar mı? Biz, onu nasıl bina ettik ve onu nasıl süsledik? Onun hiçbir çatlağı yok.'),
(4637, 50, 7, 'Yeri de (nasıl) döşeyip-yaydık? Onda sarsılmaz dağlar bıraktık ve onda \'göz alıcı ve iç açıcı\' her çiftten (nice bitkiler) bitirdik.'),
(4638, 50, 8, '(Bunlar,) \'İçten Allah\'a yönelen\' her kul için \'hikmetle bakan bir iç göz\' ve bir zikirdir.'),
(4639, 50, 9, 'Ve gökten mübarek (bereket ve rahmet yüklü) su indirdik; böylece onunla bahçeler ve biçilecek taneler bitirdik.'),
(4640, 50, 10, 'Ve birbiri üstüne dizilmiş tomurcuk yüklü yüksek hurma ağaçları da.'),
(4641, 50, 11, 'Kullara rızık olmak üzere. Ve onunla (o suyla) ölü bir şehri dirilttik. İşte (ölümden sonra) diriliş de böyledir.'),
(4642, 50, 12, 'Onlardan önce Nuh kavmi, Ress halkı ve Semud (kavmi) de yalanladı.'),
(4643, 50, 13, 'Ad, Firavun ve Lut\'un kardeşleri,'),
(4644, 50, 14, 'Eyke halkı ve Tubba\' kavmi de. Hepsi elçileri yalanladı; böylece Benim tehdidim (onların üzerine) hak oldu.'),
(4645, 50, 15, 'Ya, Biz ilk yaratılışta güçsüz mü düştük? Hayır, onlar \'karmaşık bir kuşku\' içindedirler.'),
(4646, 50, 16, 'Andolsun, insanı Biz yarattık ve nefsinin ona ne vesveseler vermekte olduğunu biliriz. Biz ona şahdamarından daha yakınız.'),
(4647, 50, 17, 'Onun sağında ve solunda oturan iki yazıcı kaydederlerken'),
(4648, 50, 18, 'O, söz olarak (herhangi bir şey) söylemeyiversin, mutlaka yanında hazır bir gözetleyici vardır.'),
(4649, 50, 19, 'O, ölüm sarhoşluğu, bir gerçek olarak gelip de, (insana) "İşte bu, senin yan çizip-kaçmakta olduğun şeydir" (denildiği zaman da).'),
(4650, 50, 20, 'Sur\'a da üfürülmüştür. İşte bu, tehdidin (gerçekleştiği) gündür.'),
(4651, 50, 21, '(Artık) Her bir nefis, yanında bir sürücü ve bir şahid ile gelmiştir.'),
(4652, 50, 22, '"Andolsun, sen bundan gaflet içindeydin; işte Biz de senin üzerindeki örtüyü açıp-kaldırdık. Artık bugün görüş-gücün keskindir."'),
(4653, 50, 23, 'Onun yakını olan (ve yanından ayrılmayan melek) dedi ki: "İşte bu, yanımda hazır durumda olan şey."'),
(4654, 50, 24, 'Siz ikiniz (ey melekler), her inatçı nankörü atın cehennemin içine,'),
(4655, 50, 25, 'Hayra engel olan, saldırgan şüpheciyi,'),
(4656, 50, 26, 'Ki o, Allah\'la beraber başka bir İlah edinmişti. Artık ikiniz, onu en şiddetli olan azabın içine atın.'),
(4657, 50, 27, 'Onun yakın-dostu (saptırıcı) dedi ki: "Rabbimiz, ben onu kışkırtıp-azdırdım. Ancak kendisi (haktan) uzak bir sapıklık içindeydi."'),
(4658, 50, 28, '(Allah buyurur:) "Benim Huzurumda çekişip-durmayın. Ben size daha önce \'kesin bir uyarı\' göndermiştim."'),
(4659, 50, 29, '"Huzurumda söz değişikliğe uğratılmaz ve Ben kullara zulmedici değilim."'),
(4660, 50, 30, 'O gün cehenneme diyeceğiz: "Doldun mu?" O da: "Daha fazlası var mı?" diyecek.'),
(4661, 50, 31, 'Cennet de, muttakiler için, uzakta değildir, (o gün) yakınlaştırılmıştır.'),
(4662, 50, 32, 'Bu, size vadolunandır; (gönülden Allah\'a) yönelip-dönen (İslam\'ın hükümlerini) koruyan,'),
(4663, 50, 33, 'Görmediği halde Rahman\'a karşı \'içi titreyerek korku duyan\' ve \'içten Allah\'a yönelmiş\' bir kalp ile gelen içindir.'),
(4664, 50, 34, '"Ona \'esenlik ve barış (selam)la\' girin. Bu, ebedilik günüdür."'),
(4665, 50, 35, 'Orda diledikleri herşey onlarındır; Katımız\'da daha fazlası da var.'),
(4666, 50, 36, 'Biz bunlardan önce nice nesiller yıkıma uğrattık ki onlar, zorbaca yakalamak (yakıp-yıkmak, baskı ve şiddetle yönetmek, sindirmek) bakımından kendilerinden daha üstündüler; şehirlerde (yerin üstünü altına getirip, sayısız kazı, inşaat ve araştırmalarla her yanı) delik-deşik etmişlerdi. (Ama) kaçacak bir yer var mı?'),
(4667, 50, 37, 'Hiç şüphesiz, bunda, kalbi olan ya da bir şahid olarak kulak veren kimse için elbette bir öğüt (zikir) vardır.'),
(4668, 50, 38, 'Andolsun, Biz gökleri, yeri ve ikisi arasında bulunanları altı günde yarattık; Bize hiçbir yorgunluk dokunmadı.'),
(4669, 50, 39, 'Öyleyse sen, onların dediklerine karşılık sabret ve Rabbini güneşin doğuşundan önce ve batışından önce hamd ile tesbih et.'),
(4670, 50, 40, 'Gecenin bir bölümünde ve secdelerin arkasından da O\'nu tesbih et.'),
(4671, 50, 41, 'Çağırıcının, yakın bir yerden çağrıda bulunacağı güne kulak ver;'),
(4672, 50, 42, 'O gün, o çığlığı bir gerçek (hak) olarak işitirler. İşte bu, (dirilip kabirlerden) çıkış günüdür.'),
(4673, 50, 43, 'Gerçek şu ki, dirilten ve öldüren Biziz, Biz. Ve dönüş de Bizedir.'),
(4674, 50, 44, 'O gün yer, onlardan çatlayıp-ayrılır da (onlar,) hızla koşarlar. İşte bu, Bize göre oldukça-kolay olan bir haşir (sizi birarada toplama)dır.'),
(4675, 50, 45, 'Biz onların neler söylediklerini daha iyi biliriz. Sen onların üzerinde bir zorba değilsin; şu halde, Benim kesin tehdidimden korkanlara Kur\'an ile öğüt ver.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Tozu dumana katıp savuran (rüzgar)lara,'),
(4677, 51, 2, 'Derken, ağır yük taşıyan (bulut)lara.'),
(4678, 51, 3, 'Sonra kolaylıkla akıp gidenlere,'),
(4679, 51, 4, 'Sonra iş(ler)i taksim edenlere andolsun.'),
(4680, 51, 5, 'Size va\'dedilmekte olan, hiç tartışmasız doğrudur.'),
(4681, 51, 6, 'Şüphesiz din (hesap ve ceza) da mutlaka gerçekleşecektir.'),
(4682, 51, 7, '\'Özen içinde yollar ve yörüngelerle donatılmış\' göğe andolsun;'),
(4683, 51, 8, 'Siz, gerçekten birbirini tutmaz bir söz (çelişkili ve aykırı görüşler) içindesiniz.'),
(4684, 51, 9, 'Ondan çevrilen çevrilir,'),
(4685, 51, 10, 'Kahrolsun, o \'zan ve tahminle yalan söyleyenler\';'),
(4686, 51, 11, 'Ki onlar, \'bilgisizliğin kuşatması\' içinde habersizdirler.'),
(4687, 51, 12, '"Hesap ve ceza (din) günü ne zaman?" diye sorarlar.'),
(4688, 51, 13, 'O gün onlar, ateşin üstünde tutulup-eritilecekler:'),
(4689, 51, 14, '"Tadın fitnenizi. Bu, sizin pek acele isteyip durduğunuz şeydir."'),
(4690, 51, 15, 'Şüphesiz muttaki olanlar, cennetlerde ve pınarlardadırlar;'),
(4691, 51, 16, 'Rablerinin kendilerine verdiğini alanlar olarak. Çünkü onlar, bundan önce ihsanda (güzel davranışta) bulunanlardı.'),
(4692, 51, 17, 'Gece-boyunca da pek az uyurlardı.'),
(4693, 51, 18, 'Onlar, seher vakitlerinde istiğfar ederlerdi.'),
(4694, 51, 19, 'Onların mallarında dilenip-isteyen (ve iffetinden dolayı istemeyip de) yoksul olan için de bir hak vardı.'),
(4695, 51, 20, 'Yeryüzünde kesin bir bilgiyle inanacak olanlar için ayetler vardır.'),
(4696, 51, 21, 'Ve kendi nefislerinizde de. Yine de görmüyor musunuz?'),
(4697, 51, 22, 'Gökte rızkınız vardır ve size va\'dolunmakta olan da.'),
(4698, 51, 23, 'İşte, göğün ve yerin Rabbine andolsun ki, şüphesiz, o (size va\'dedilen) sizin (aranızda) konuştuklarınız kadar, elbette kesin bir gerçektir.'),
(4699, 51, 24, 'Sana İbrahim\'in ağırlanan konuklarının haberi geldi mi?'),
(4700, 51, 25, 'Hani, yanına girdiklerinde: "Selam" demişlerdi. O da: "Selam" demişti. "(Haklarında bilgim olmayan) Yabancı bir topluluk."'),
(4701, 51, 26, 'Hemen (onlara) sezdirmeden ailesine gidip, çok geçmeden semiz bir buzağı ile (geri) geldi.'),
(4702, 51, 27, 'Derken onlara yaklaştırıp (ikram etti); "Yemez misiniz?" dedi.'),
(4703, 51, 28, '(Onlar yemeyince) Bunun üzerine içine bir tür korku düştü. "Korkma" dediler ve ona bilgin bir erkek çocuk müjdesini verdiler.'),
(4704, 51, 29, 'Böylece karısı çığlıklar kopararak geldi ve yüzüne vurarak: "Kısır, yaşlı bir kadın (mı doğum yapacakmış)? dedi.'),
(4705, 51, 30, 'Dediler ki: "Öyle. (Bunu) Senin Rabbin buyurdu. Çünkü O, hüküm ve hikmet sahibidir, bilendir."'),
(4706, 51, 31, '(İbrahim) dedi ki: "Şu halde sizin asıl isteğiniz nedir, ey elçiler?"'),
(4707, 51, 32, '"Doğrusu biz, suçlu-günahkar bir kavme gönderildik" dediler.'),
(4708, 51, 33, '"Üzerlerine çamurdan (iyice sertleşip kaskatı kesilmiş) taşlar yağdırmak için."'),
(4709, 51, 34, '"(Ki bu taşların her biri,) Rabbinin Katında ölçüyü taşıranlar için (herkese ayrı ayrı) işaretlenmiştir."'),
(4710, 51, 35, 'Bu arada, mü\'minlerden orda kim varsa çıkardık.'),
(4711, 51, 36, 'Ne var ki, orda Müslümanlardan olan bir evden başkasını bulmadık.'),
(4712, 51, 37, 'Ve orada, acı bir azaptan korkanlar için bir ayet bıraktık.'),
(4713, 51, 38, 'Musa (olayın)da da (düşündürücü ayetler vardır). Hani Biz onu açık bir delille Firavun\'a göndermiştik;'),
(4714, 51, 39, 'Fakat o, \'bütün kişisel ve askeri gücüyle\' yüz çevirdi ve: "(Bu,) Ya bir büyücü veya bir delidir" dedi.'),
(4715, 51, 40, 'Bunun üzerine, Biz onu ve ordularını yakalayıp denize attık; (ki o,) \'kınanacak işler yapıyordu.\''),
(4716, 51, 41, 'Ad (kavmin)de de (ayetler vardır). Hani onların üzerine köklerini kesen (akim) bir rüzgar gönderdik.'),
(4717, 51, 42, 'Üzerinden geçtiği hiçbir şeyi bırakmıyor, mutlaka çürütüp-kül gibi dağıtıyordu.'),
(4718, 51, 43, 'Semud (kavmin)de de (ayetler vardır). Hani onlara: "Belli bir süreye kadar yararlanın" denmişti.'),
(4719, 51, 44, 'Ancak Rablerinin emrine baş kaldırdılar; böylece bakıp-dururlarken, onları yıldırım çarpıp-yakaladı.'),
(4720, 51, 45, 'Artık ne ayağa kalkmaya güç yetirebildiler, ne yardım bulabildiler.'),
(4721, 51, 46, 'Bundan önce Nuh kavmini de (yıkıma uğrattık). Çünkü onlar da fasık bir kavim idi.'),
(4722, 51, 47, 'Biz göğü \'büyük bir kudretle\' bina ettik ve şüphesiz Biz, (onu) genişleticiyiz.'),
(4723, 51, 48, 'Yeri de Biz döşeyip-yaydık; ne güzel döşeyici(yiz).'),
(4724, 51, 49, 'Ve Biz, herşeyi iki çift yarattık. Umulur ki, öğüt alıp-düşünürsünüz.'),
(4725, 51, 50, 'Öyleyse, Allah\'a doğru (yönelip, şirkten ve bozulmalardan) kaçın. Gerçekten Ben sizi, O\'ndan yana açıkça uyarıyorum.'),
(4726, 51, 51, 'Allah ile beraber başka bir İlah(ı ortak) kılmayın. Gerçekten sizi, O\'ndan yana açıkça uyarıyorum.'),
(4727, 51, 52, 'İşte böyle; onlardan öncekiler de bir elçi gelmeyiversin, mutlaka: "Büyücü ve cinlenmiş" demişlerdir.'),
(4728, 51, 53, 'Onlar bunu (tarih boyunca) birbirlerine vasiyet mi ettiler? Hayır; onlar, \'azgın ve taşkın (tağiy)\' bir kavimdirler.'),
(4729, 51, 54, 'Öyleyse sen, onlardan yüz çevir; artık kınanacak değilsin.'),
(4730, 51, 55, 'Sen öğüt verip-hatırlat; çünkü gerçekten öğütle-hatırlatma, mü\'minlere yarar sağlar.'),
(4731, 51, 56, 'Ben, cinleri ve insanları yalnızca Bana ibadet etsinler diye yarattım.'),
(4732, 51, 57, 'Ben, onlardan bir rızık istemiyorum ve onların beni doyurup-beslemelerini de istemiyorum.'),
(4733, 51, 58, 'Hiç şüphesiz, rızık veren O, metin kuvvet sahibi olan Allah\'tır.'),
(4734, 51, 59, 'Artık gerçekten, zulmedenler için, (geçmişteki) arkadaşlarının günahlarına benzer bir günah vardır. Şu halde acele etmesinler.'),
(4735, 51, 60, 'Kendilerine va\'dedilen o (azap) günlerinden dolayı vay o inkar edenlere.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Tur\'a andolsun.'),
(4737, 52, 2, 'Satır (satır) dizili kitaba,'),
(4738, 52, 3, 'Yayılmış ince deri üzerine;'),
(4739, 52, 4, 'Ma\'mur eve,'),
(4740, 52, 5, 'Yükseltilmiş tavana,'),
(4741, 52, 6, 'Kabarıp, tutuşan denize,'),
(4742, 52, 7, 'Şüphesiz senin Rabbinin azabı kesin olarak gerçekleşecektir.'),
(4743, 52, 8, 'Onu uzaklaştırıp-engel olacak yoktur.'),
(4744, 52, 9, 'O gün gök, sarsılıp çalkalanır.'),
(4745, 52, 10, 'Ve dağlar (yerlerinden oynatan) bir yürüyüşle yürür.'),
(4746, 52, 11, 'İşte o gün, yalanlayanların vay haline,'),
(4747, 52, 12, 'Ki onlar, \'daldıkları saçma bir uğraşı\' içinde oynayan-oyalananlardır.'),
(4748, 52, 13, 'Cehennem ateşine, \'küçültücü bir sürüklenme ile \' sürüklenecekleri gün;'),
(4749, 52, 14, '(Onlara şöyle denir:) "İşte sizin yalanladığınız ateş budur."'),
(4750, 52, 15, '"Bu da bir büyü mü, yoksa siz mi görmüyorsunuz."'),
(4751, 52, 16, '"Girin ona; artık ister sabredin, ister sabretmeyin. Sizin için birdir. Siz ancak, yaptıklarınızla cezalandırılıyorsunuz."'),
(4752, 52, 17, 'Hiç şüphesiz muttakiler, cennetlerde ve nimet içindedirler;'),
(4753, 52, 18, 'Rablerinin verdikleriyle \'sevinçli ve mutludurlar\'. Rableri, kendilerini \'çılgınca yanan cehennemin\' azabından korumuştur.'),
(4754, 52, 19, '"Yaptıklarınızdan dolayı afiyetle yiyin ve için."'),
(4755, 52, 20, 'Özenle dizilmiş tahtlar üzerinde yaslanmışlardır. Ve Biz onları iri-ceylan gözlü hurilerle evlendirmişiz.'),
(4756, 52, 21, 'İman edenler ve soyları kendilerini imanda izleyenler; Biz onların soylarını da kendilerine katıp-ekledik. Onların amellerinden hiçbir şeyi eksiltmedik. Her kişi kendi kazandığına karşılık bir rehindir.'),
(4757, 52, 22, 'Onlara, istek duyup-arzuladıkları meyvelerden ve etten bol bol verdik.'),
(4758, 52, 23, 'Orada bir kadeh kapışır-çekişirler ki, onda ne \'boş ve saçma bir söz\', ne günaha sokma yoktur.'),
(4759, 52, 24, 'Kendileri için (hizmet eden) civanlar, etrafında dönüp dolaşırlar; sanki (her biri) \'sedefte saklı inci gibi tertemiz, pırıl pırıl.\''),
(4760, 52, 25, 'Kimi kimine dönüp sorarlar;'),
(4761, 52, 26, 'Dediler ki: "Biz doğrusu daha önce, ailemiz (yakın akrabalarımız) içinde endişe edip-korkardık."'),
(4762, 52, 27, '"Şimdi Allah, bize lütufta bulundu ve \'hücrelere kadar işleyen kavurucu\' azaptan korudu."'),
(4763, 52, 28, '"Şüphesiz, biz bundan önce O\'na dua (kulluk) ederdik. Gerçekten O, iyiliği bol, esirgemesi çok olanın ta Kendisi\'dir."'),
(4764, 52, 29, 'Şu halde sen, öğüt verip-hatırlat; çünkü sen, Rabbinin nimetiyle ne kahinsin, ne mecnun.'),
(4765, 52, 30, 'Yoksa onlar: "Bir şairdir, biz ona zamanın (getireceği) felaketleri gözlüyoruz" mu diyorlar?'),
(4766, 52, 31, 'De ki: "Siz gözetleyedurun; çünkü ben de sizinle birlikte gözetleyenlerdenim."'),
(4767, 52, 32, 'Yoksa bunu kendilerine saçma-akılları mı emrediyor? Yoksa onlar azgın bir kavim midir?'),
(4768, 52, 33, 'Yoksa: "Onu kendisi uydurup-söyledi" mi diyorlar? Hayır; onlar iman etmiyorlar.'),
(4769, 52, 34, 'Şu halde, eğer doğru sözlüler iseler, benzeri bir söz getirsinler.'),
(4770, 52, 35, 'Yoksa onlar, hiçbir şey olmaksızın mı yaratıldılar? Yoksa yaratıcılar kendileri mi?'),
(4771, 52, 36, 'Yoksa gökleri ve yeri onlar mı yarattılar? Hayır; onlar, kesin bir bilgiyle inanmıyorlar.'),
(4772, 52, 37, 'Yoksa Rabbinin hazineleri onların yanında mıdır? Yoksa üstün güç (herşeyin denetim ve yönetim) sahipleri kendileri midir?'),
(4773, 52, 38, 'Yoksa onların bir merdivenleri mi var (ki) onunla (yükselip en yüce makamda konuşulanları) dinliyorlar? Öyleyse, dinleyenleri açık bir delil getirsin.'),
(4774, 52, 39, 'Yoksa kızlar O\'nun da, erkek-çocuklar sizin mi?'),
(4775, 52, 40, 'Yoksa sen onlardan bir ücret mi istiyorsun ki, haksız bir borçtan dolayı ağır bir yük altındalar?'),
(4776, 52, 41, 'Yoksa gayb (bilgisi) onların katında mıdır, böylece yazıp-duruyorlar?'),
(4777, 52, 42, 'Yoksa hileli-bir düzen mi kurmak istiyorlar? Fakat (asıl) ‘o inkar edenler hileli-düzene düşecek olanlardır.'),
(4778, 52, 43, 'Yoksa onların, Allah\'ın dışında başka bir ilahları mı var? Allah, onların şirk koştuklarından Yücedir.'),
(4779, 52, 44, 'Eğer gökten bir parçanın düşmekte olduğunu görseler bile: "Üst üste yığılmış bir buluttur." derler.'),
(4780, 52, 45, 'Öyleyse sen onları (en dayanılmaz azapla) çarpılacakları günlerine kavuşuncaya kadar bırak.'),
(4781, 52, 46, 'O gün, ne hileli-düzenleri kendilerine herhangi bir şeyle yarar sağlayacak, ne yardım görecekler.'),
(4782, 52, 47, 'Şüphesiz zulmedenlere bundan önce de bir azap vardır; ancak onların çoğu bilmiyorlar.'),
(4783, 52, 48, 'Artık, Rabbinin hükmüne sabret; çünkü gerçekten sen, Bizim gözlerimizin önündesin. Ve her kalkışında Rabbini hamd ile tesbih et.'),
(4784, 52, 49, 'Gecenin bir bölümünde ve yıldızların batışının ardında da O\'nu tesbih et.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Battığı zaman yıldıza andolsun;'),
(4786, 53, 2, 'Sahibiniz (arkadaşınız olan peygamber) sapmadı ve azmadı.'),
(4787, 53, 3, 'O, hevadan (kendi istek, düşünce ve tutkularına göre) konuşmaz.'),
(4788, 53, 4, 'O (söyledikleri), yalnızca vahyolunmakta olan bir vahiydir.'),
(4789, 53, 5, 'Ona (bu Kur\'an\'ı) üstün (oldukça çetin) bir güç sahibi (Cebrail) öğretmiştir.'),
(4790, 53, 6, '(Ki O,) Görünümüyle çarpıcı bir güzelliğe sahiptir. Hemen doğruldu.'),
(4791, 53, 7, 'O, en yüksek bir ufuktaydı.'),
(4792, 53, 8, 'Sonra yaklaştı, derken sarkıverdi.'),
(4793, 53, 9, 'Nitekim (ikisi arasındaki uzaklık) iki yay kadar (oldu) veya daha yakınlaştı.'),
(4794, 53, 10, 'Böylece O\'nun kuluna vahyettiğini vahyetti.'),
(4795, 53, 11, 'Onun gördüğünü gönül yalanlamadı.'),
(4796, 53, 12, 'Yine de siz gördüğü (şey) üzerinde onunla tartışacak mısınız?'),
(4797, 53, 13, 'Andolsun, onu bir de diğer inişte görmüştü.'),
(4798, 53, 14, 'Sidretü\'l-Münteha\'nın yanında.'),
(4799, 53, 15, 'Ki Cennetü\'l-Me\'va onun yanındadır.'),
(4800, 53, 16, 'Sidreyi örten örtmekte iken,'),
(4801, 53, 17, 'Göz kayıp-şaşmadı ve (sınırı) aşmadı.'),
(4802, 53, 18, 'Andolsun, o, Rabbinin en büyük ayetlerinden olanı gördü.'),
(4803, 53, 19, 'Gördünüz mü-haber verin; Lat ve Uzza\'yı.'),
(4804, 53, 20, 'Ve üçüncü (put) olan Menat\'ı(n herhangi bir güçleri var mı)?'),
(4805, 53, 21, 'Erkek (evlat) sizin, dişi O\'nun mu?'),
(4806, 53, 22, 'Eğer böyleyse, bu, çarpık bir paylaşma.'),
(4807, 53, 23, 'Bu (putlar ise,) sizin ve atalarınızın (kendi istek ve öngörünüze göre) isimlendirdiğiniz (keyfi) isimlerden başkası değildir. Allah, onlarla ilgili \'hiçbir delil\' indirmemiştir. Onlar, yalnızca zanna ve nefislerinin (alçak) heva (istek ve tutku) olarak arzu ettiklerine uyuyorlar. Oysa andolsun, onlara Rablerinden yol gösterici gelmiştir.'),
(4808, 53, 24, 'Yoksa insana \'her arzu edip dilekte bulunduğu\' şey mi var?'),
(4809, 53, 25, 'İşte son da, ilk de (ahiret ve dünya) Allah\'ındır.'),
(4810, 53, 26, 'Göklerde nice melekler vardır ki, onların şefaatleri hiçbir şeyle yarar sağlamaz; ancak Allah\'ın dileyip razı olduğu kimseye izin verdikten sonra başka.'),
(4811, 53, 27, 'Gerçek şu ki, ahirete iman etmeyenler, melekleri dişi isimlerle isimlendiriyorlar.'),
(4812, 53, 28, 'Oysa onların bununla ilgili hiçbir bilgileri yoktur. Onlar, yalnızca zanna uymaktadırlar. Oysa gerçekte zan, haktan yana hiçbir yarar sağlamaz.'),
(4813, 53, 29, 'Şu halde sen, Bizim zikrimize sırt çeviren ve dünya hayatından başkasını istemeyenden yüz çevir.'),
(4814, 53, 30, 'İşte onların ilimden yana ulaşabildikleri (son sınır) budur. Şüphesiz, senin Rabbin; Kendi yolundan sapanı en iyi bilen O\'dur ve hidayet bulanı da en iyi bilen O\'dur.'),
(4815, 53, 31, 'Göklerde ve yerde olanlar Allah\'ındır; öyle ki, kötülükte bulunanları, yaptıkları dolayısıyla cezalandırır, güzel davranışta bulunanları da daha güzeliyle ödüllendirir.'),
(4816, 53, 32, 'Ki onlar, ufak tefek günahlar dışında, günahın büyük olanından ve çirkin utanmazlıklardan kaçınırlar. Şüphesiz senin Rabbin, mağfireti geniş olandır. O, sizi daha iyi bilendir; hem sizi topraktan inşa ettiği (yarattığı) ve siz daha annelerinizin karnında cenin halinde bulunduğunuz zaman da. Öyleyse kendinizi temize çıkarıp-durmayın. O, sakınanı daha iyi bilendir.'),
(4817, 53, 33, 'Şimdi, o yüz çevireni gördün mü?'),
(4818, 53, 34, 'Azıcık verdi ve gerisini kaya gibi sımsıkı elinde tuttu.'),
(4819, 53, 35, 'Gaybın ilmi onun yanında da o mu görüyor?'),
(4820, 53, 36, 'Yoksa Musa\'nın sahifelerinde olan kendisine haber verilmedi mi?'),
(4821, 53, 37, 'Ve vefa eden İbrahim\'in (sahifelerinde) olan...'),
(4822, 53, 38, 'Doğrusu, hiçbir günahkar, bir başkasının günah yükünü yüklenmez.'),
(4823, 53, 39, 'Şüphesiz insana kendi emeğinden başkası yoktur.'),
(4824, 53, 40, 'Şüphesiz kendi emeği (veya çabası) görülecektir.'),
(4825, 53, 41, 'Sonra ona en eksiksiz karşılık verilecektir.'),
(4826, 53, 42, 'Elbette son varış Rabbine olacaktır.'),
(4827, 53, 43, 'Doğrusu, güldüren ve ağlatan O\'dur.'),
(4828, 53, 44, 'Doğrusu, öldüren ve dirilten O\'dur.'),
(4829, 53, 45, 'Doğrusu, çiftleri; erkek ve dişiyi, yaratan O\'dur.'),
(4830, 53, 46, 'Bir damla sudan (döl yatağına) meni döküldüğü zaman.'),
(4831, 53, 47, 'Gerçek şu ki, diğer diriltme (yeniden neş\'et) de O\'na aittir.'),
(4832, 53, 48, 'Doğrusu, muhtaç olmaktan O kurtardı ve sermaye verip-hoşnut kıldı.'),
(4833, 53, 49, 'Doğrusu, \'Şi\'ra (yıldızı)nın\' Rabbi O\'dur.'),
(4834, 53, 50, 'Doğrusu, önce gelen Ad (halkın)ı O yıkıma uğrattı.'),
(4835, 53, 51, 'Semud\'u da. Böylelikle (o halklardan kimseyi) bırakmadı.'),
(4836, 53, 52, 'Daha önce Nuh kavmini de. Çünkü onlar, daha zalim ve daha azgındılar.'),
(4837, 53, 53, 'Altı üstüne gelen (Lut kavminin) şehirlerini de O yerin dibine geçirdi.'),
(4838, 53, 54, 'Böylece ona (o toplumun başına) sardırdığını sardırdı.'),
(4839, 53, 55, 'Öyleyse, Rabbinin hangi nimetlerinden şüphe ediyorsun?'),
(4840, 53, 56, 'Bu önceki uyarıcılardan bir uyarıcıdır.'),
(4841, 53, 57, 'O yaklaşmakta olan yaklaştı.'),
(4842, 53, 58, 'Onu Allah\'ın dışında ortaya çıkaracak başka (hiçbir güç yoktur).'),
(4843, 53, 59, 'Şimdi siz, bu sözden mi şaşkınlığa düşüyorsunuz?'),
(4844, 53, 60, '(Alayla) Gülüyorsunuz ve ağlamıyorsunuz.'),
(4845, 53, 61, 'Ve şuursuzca baş kaldırıyorsunuz.'),
(4846, 53, 62, 'Hemen, Allah\'a secde edin ve (yalnızca O\'na) kulluk edin.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Saat (kıyamet vakti) yakınlaştı ve ay yarıldı.'),
(4848, 54, 2, 'Onlar bir ayet (mucize) görseler, sırt çevirirler ve: "(Bu,) Süregelen bir büyüdür" derler.'),
(4849, 54, 3, 'Yalanladılar ve kendi heva (istek ve tutku)larına uydular; oysa her iş \'sonunda kendi amacına varıp karar kılacaktır.\''),
(4850, 54, 4, 'Andolsun, onlara (kendilerini şirkten ve bozulmalardan) caydırıp vazgeçirtecek nice haberler geldi.'),
(4851, 54, 5, '(Ki her biri) Doruğunda-olgunlaşmış hikmettir. Fakat uyarmalar bir yarar sağlamıyor.'),
(4852, 54, 6, 'Öyleyse sen onlardan yüz çevir. O çağırıcının \'ne tanınmış, ne görülmüş\' bir şeye çağıracağı gün...'),
(4853, 54, 7, 'Gözleri \'zillet ve dehşetten düşmüş olarak\', sanki \'yayılan\' çekirgeler gibi kabirlerinden çıkarlar.'),
(4854, 54, 8, 'Boyunlarını çağırana doğru uzatmış olarak koşarlarken, kafirler derler ki: "Bu, zorlu bir gün."'),
(4855, 54, 9, 'Kendilerinden önce Nuh kavmi de yalanlamıştı; böylece kulumuz (Nuh)u yalanladılar ve: "Delidir" dediler. O \'baskı altına alınıp engellenmişti.\''),
(4856, 54, 10, 'Sonunda Rabbine dua etti: "Gerçekten ben, yenik düşmüş durumdayım. Artık Sen (bu kafir toplumdan) intikam al."'),
(4857, 54, 11, 'Biz de \'bardaktan boşanırcasına akan\' bir su ile göğün kapılarını açtık.'),
(4858, 54, 12, 'Yeri de \'coşkun kaynaklar\' halinde fışkırttık. Derken su, takdir edilmiş bir işe karşı (hükmümüzü gerçekleştirmek üzere) birleşti.'),
(4859, 54, 13, 'Ve onu da tahtalar ve çiviler(le inşa edilmiş gemi) üzerinde taşıdık;'),
(4860, 54, 14, 'Gözlerimiz önünde akıp-gitmekteydi. (Kendisi ve getirdikleri) İnkar edilmiş-nankörlük edilmiş olan (Nuh)a bir mükafaat olmak üzere.'),
(4861, 54, 15, 'Andolsun, Biz bunu bir ayet olarak bıraktık. Fakat öğüt alıp-düşünen var mı?'),
(4862, 54, 16, 'Şu halde Benim azabım ve uyarıp-korkutmam nasılmış?'),
(4863, 54, 17, 'Andolsun Biz Kur\'an\'ı zikr (öğüt alıp düşünmek) için kolaylaştırdık. Fakat öğüt alıp-düşünen var mı?'),
(4864, 54, 18, 'Ad (kavmi) de yalanladı. Şu halde Benim azabım ve uyarmam nasılmış?'),
(4865, 54, 19, 'Biz, o uğursuz (felaket yüklü ve) sürekli bir günde üzerlerine \'kulakları patlatan bir kasırga\' gönderdik.'),
(4866, 54, 20, 'İnsanları söküp atıyordu; sanki onlar, kökünden sökülüp-kopmuş hurma kütükleriymiş gibi.'),
(4867, 54, 21, 'Şu halde Benim azabım ve uyarmam nasılmış?'),
(4868, 54, 22, 'Andolsun Biz Kur\'an\'ı zikr (öğüt alıp düşünmek) için kolaylaştırdık. Fakat öğüt alıp-düşünen var mı?'),
(4869, 54, 23, 'Semud (kavmi) de uyarıları yalanladı.'),
(4870, 54, 24, 'Dediler ki: "Bizden biri olan bir beşere mi uyacağız? Bu durumda gerçekten biz bir sapıklık (delalet) ve çılgınlık içinde kalmış oluruz."'),
(4871, 54, 25, '"Zikr (vahy) içimizden ona mı bırakıldı? Hayır, o çok yalan söyleyen, kendini beğenmiş bir şımarıktır."'),
(4872, 54, 26, 'Onlar yarın, kimin çok yalan söyleyen, kendini beğenmiş bir şımarık olduğunu bilip-öğreneceklerdir.'),
(4873, 54, 27, 'Gerçek şu ki Biz, bir fitne (imtihan ve deneme konusu) olarak o dişi deveyi kendilerine göndereniz. Şu halde sen onları gözleyip-bekle ve sabret.'),
(4874, 54, 28, '"Ve onlara, suyun aralarında kesin olarak pay edildiğini haber ver. Su alış sırası (kiminse, o) hazır bulunsun."'),
(4875, 54, 29, 'Derken arkadaşlarını çağırdılar, o da bıçağını kapıp \'hayvanı ayağından biçip yere devirdi.\''),
(4876, 54, 30, 'Şu halde Benim azabım ve uyarmam nasılmış?'),
(4877, 54, 31, 'Çünkü Biz onların üzerine bir tek çığlık gönderdik. Böylece onlar, ağıldaki çalı-çırpı olan kuru ot gibi oluverdiler.'),
(4878, 54, 32, 'Andolsun Biz Kur\'an\'ı zikr (öğüt alıp düşünmek) için kolaylaştırdık. Fakat öğüt alıp-düşünen var mı?'),
(4879, 54, 33, 'Lut kavmi de uyarıları yalanladı.'),
(4880, 54, 34, 'Biz de onların üzerine taş yağdıran bir kasırga gönderdik. Yalnız Lut ailesini (bu azaptan ayrı tuttuk;) onları seher vakti kurtardık;'),
(4881, 54, 35, 'Tarafımızdan bir nimet olarak. İşte Biz, şükredenleri böyle ödüllendiririz.'),
(4882, 54, 36, 'Oysa andolsun, zorlu yakalamamıza karşı onları uyarmıştı. Fakat onlar, bu uyarıları kuşkuyla karşılayıp-yalanlamakta direttiler.'),
(4883, 54, 37, 'Andolsun onlar, onun konuklarından da murad almak için baskı yaptılar. Biz de onların gözlerini silip kör ettik. "İşte azabımı ve uyarmamı tadın."'),
(4884, 54, 38, 'Andolsun onları bir sabah vakti erkenden, üzerlerinde kararını kılmış bir azap yakalayıp-bastırıverdi.'),
(4885, 54, 39, 'Şimdi azabımı ve uyarmamı tadın.'),
(4886, 54, 40, 'Andolsun Biz Kur\'an\'ı zikr (öğüt alıp düşünmek) için kolaylaştırdık. Fakat öğüt alıp-düşünen var mı?'),
(4887, 54, 41, 'Andolsun Firavun ailesi (ve çevresi ile kavmi)ne de uyarılar geldi.'),
(4888, 54, 42, 'Onlar Bizim ayetlerimizin tümünü yalanladılar. Biz de onları üstün ve güçlü, kudretli olanın yakalayışıyla yakalayıverdik.'),
(4889, 54, 43, 'Sizin kafirleriniz onlardan daha hayırlı mıdır? Yoksa sizin için kitaplarda bir beraat mi var?'),
(4890, 54, 44, '"Biz, \'birbiriyle yardımlaşıp öcünü alan\' bir toplumuz" mu diyorlar?'),
(4891, 54, 45, 'Yakında o toplum bozguna uğratılacak ve arkalarını dönüp kaçacaklardır.'),
(4892, 54, 46, 'Daha doğrusu onlara va\'dedilen (asıl azap) (kıyamet) saatidir. O saat, \'kurtuluş olmayan daha korkunç bir bela\' ve daha acıdır.'),
(4893, 54, 47, 'Hiç şüphesiz suçlular-günahkarlar, bir sapmışlık (dalalet) ve çılgınlık içindedirler.'),
(4894, 54, 48, 'Ateşin içinde yüzükoyun sürüklenecekleri gün cehennemin dokunuşunu tadın" (denecek)'),
(4895, 54, 49, 'Hiç şüphesiz, Biz herşeyi kader ile yarattık.'),
(4896, 54, 50, 'Bizim emrimiz, bir göz kırpma gibi yalnızca \'bir keredir.\''),
(4897, 54, 51, 'Andolsun Biz sizin benzerlerinizi yıkıma uğrattık. Fakat öğüt alıp-düşünen var mı?'),
(4898, 54, 52, 'Onların işlemiş oldukları herşey kitaplarda (yazılı)dır.'),
(4899, 54, 53, 'Küçük, büyük herşey satır satır (yazılı)dır.'),
(4900, 54, 54, 'Hiç şüphesiz muttakiler, cennetlerde ve nehir (çevresin)dedirler.'),
(4901, 54, 55, 'Çok kudretli, mülkünün sonu olmayan (Allah)ın yanında doğruluk makamındadırlar.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Rahman (olan Allah)'),
(4903, 55, 2, 'Kur\'an\'ı öğretti.'),
(4904, 55, 3, 'İnsanı yarattı.'),
(4905, 55, 4, 'Ona beyanı öğretti.'),
(4906, 55, 5, 'Güneş ve ay (belli) bir hesap iledir.'),
(4907, 55, 6, 'Bitki ve ağaç (O\'na) secde etmektedirler.'),
(4908, 55, 7, 'Gökyüzü, Onu da yükseltti ve mizanı koydu.'),
(4909, 55, 8, 'Sakın mizanda \'haksızlık ve taşkınlık yapmayın.\''),
(4910, 55, 9, 'Tartıyı adaletle tutup-doğrultun ve tartıyı noksan tutmayın.'),
(4911, 55, 10, 'Yere gelince, onu da (yaratılmış bütün) varlıklar için alçalttı-koydu.'),
(4912, 55, 11, 'Onda meyveler ve salkımlı hurmalıklar var.'),
(4913, 55, 12, 'Yapraklı taneler ve güzel kokulu bitkiler.'),
(4914, 55, 13, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4915, 55, 14, 'İnsanı, ateşte pişmiş gibi kuru bir çamurdan yarattı.'),
(4916, 55, 15, 'Cann\'ı (cinni) da \'yalın-dumansız bir ateşten\' yarattı.'),
(4917, 55, 16, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4918, 55, 17, 'O, iki doğunun da Rabbidir, iki batının da Rabbidir.'),
(4919, 55, 18, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4920, 55, 19, 'Birbirleriyle kavuşmak üzere iki denizi salıverdi.'),
(4921, 55, 20, 'İkisi arasında bir engel (berzah) vardır; birbirlerinin sınırını geçmezler.'),
(4922, 55, 21, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4923, 55, 22, 'İkisinden de inci ve mercan çıkar.'),
(4924, 55, 23, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4925, 55, 24, 'Denizde koca dağlar gibi yükselen gemiler O\'nundur.'),
(4926, 55, 25, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4927, 55, 26, '(Yer) Üzerindeki herşey yok olucudur;'),
(4928, 55, 27, 'Celal ve ikram sahibi olan Rabbinin yüzü (Kendisi) baki kalacaktır.'),
(4929, 55, 28, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4930, 55, 29, 'Göklerde ve yerde olan ne varsa O\'ndan ister. O, her gün bir iştedir.'),
(4931, 55, 30, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4932, 55, 31, 'Ey (yeryüzüne yükletilmiş) iki ağırlık (olan ins ve cin), yakında (ahirette hesabınızı görmek üzere) sizin için de vakit bulacağız.'),
(4933, 55, 32, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4934, 55, 33, 'Ey cin ve ins toplulukları, eğer göklerin ve yerin bucaklarından aşıp-geçmeye güç yetirebilirseniz, hemen aşın; ancak \'üstün bir güç (sultan)\' olmaksızın aşamazsınız.'),
(4935, 55, 34, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4936, 55, 35, 'İkinizin de üzerine ateşten yalın bir alev ve (bakır gibi erimiş) kıpkızıl bir duman salıverilir de \'kurtulup-başaramazsınız.\''),
(4937, 55, 36, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4938, 55, 37, 'Sonra gök yarılıp yağ gibi erimiş olarak kıpkırmızı bir gül olduğu zaman;'),
(4939, 55, 38, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4940, 55, 39, 'İşte o gün, ne insana, ne cinne günahından sorulmaz.'),
(4941, 55, 40, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4942, 55, 41, '(Çünkü o gün) Suçlu-günahkarlar, simalarından tanınır da alınlarından ve ayaklarından yakalanırlar.'),
(4943, 55, 42, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4944, 55, 43, 'İşte bu, suçlu-günahkarların kendisini yalanladıkları cehennemdir.'),
(4945, 55, 44, 'Onlar, kendisiyle alabildiğine kaynar hale getirilmiş su arasında dönüp-dolaşırlar.'),
(4946, 55, 45, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4947, 55, 46, 'Rabbin makamından korkan kimse için ise iki cennet vardır.'),
(4948, 55, 47, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4949, 55, 48, 'Çeşit çeşit \'inceliklere ve güzelliklere\' (veya her türden sık ağaçlara) sahiptirler.'),
(4950, 55, 49, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4951, 55, 50, 'İkisinde de akmakta olan iki pınar vardır.'),
(4952, 55, 51, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4953, 55, 52, 'İkisinde de her meyveden iki çift vardır.'),
(4954, 55, 53, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4955, 55, 54, 'Astarları, ağır işlenmiş atlastan yataklar üzerinde yaslanırlar. İki cennetin de meyve-devşirmesi (ordakilere) yakın (kolay)dır.'),
(4956, 55, 55, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4957, 55, 56, 'Orada bakışlarını yalnızca eşlerine çevirmiş kadınlar vardır ki, bunlardan önce kendilerine ne bir insan, ne bir cin dokunmuştur.'),
(4958, 55, 57, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4959, 55, 58, 'Sanki onlar yakut ve mercan gibidirler.'),
(4960, 55, 59, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4961, 55, 60, 'İhsanın karşılığı ihsandan başkası mıdır?'),
(4962, 55, 61, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4963, 55, 62, 'Bu-ikisinin ötesinde iki cennet daha var.'),
(4964, 55, 63, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4965, 55, 64, 'Alabildiğine yemyeşildirler.'),
(4966, 55, 65, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4967, 55, 66, 'İçlerinde durmaksızın fışkırıp-akan iki pınar vardır.'),
(4968, 55, 67, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4969, 55, 68, 'İçlerinde (her türden) meyve, eşsiz-hurma ve eşsiz-nar vardır.'),
(4970, 55, 69, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4971, 55, 70, 'Orada huyları güzel, yüzleri güzel kadınlar vardır.'),
(4972, 55, 71, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4973, 55, 72, 'Otağlar içinde korunmuş huri kadınlar.'),
(4974, 55, 73, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4975, 55, 74, 'Bunlardan önce kendilerine ne bir insan, ne bir cin dokunmuştur.'),
(4976, 55, 75, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4977, 55, 76, 'Yeşil yastıklara ve çarpıcı güzellikteki döşeklere yaslanırlar.'),
(4978, 55, 77, 'Şu halde Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4979, 55, 78, 'Celal ve ikram sahibi olan Rabbinin adı ne Yücedir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Vakıa (kesin bir gerçek olan kıyamet) vuku bulduğu zaman,'),
(4981, 56, 2, 'Onun vukuuna (gerçekleşmesine artık) yalan diyecek yoktur.'),
(4982, 56, 3, 'O aşağılatıcı, yücelticidir.'),
(4983, 56, 4, 'Yer, şiddetli bir sarsıntıyla sarsıldığı,'),
(4984, 56, 5, 'Ve dağlar darmadağın olup ufalandığı,'),
(4985, 56, 6, 'Derken toz duman halinde dağılıp-savrulduğu,'),
(4986, 56, 7, 'Ve sizler de üç sınıf olduğunuz zaman;'),
(4987, 56, 8, 'İşte o "Ashab-ı Meymene", ne (kutludur o) "Ashab-ı Meymene".'),
(4988, 56, 9, '"Ashab-ı Meş\'eme" ne (mutsuz ve uğursuzdur o) "Ashab-ı Meş\'eme".'),
(4989, 56, 10, 'Yarışıp öne geçenler de, öne geçmiş öncülerdir.'),
(4990, 56, 11, 'İşte onlar, yakınlaştırılmış (mukarreb) olanlardır.'),
(4991, 56, 12, 'Nimetlerle-donatılmış cennetler içinde;'),
(4992, 56, 13, 'Birçoğu geçmiş (ümmet)lerden,'),
(4993, 56, 14, 'Birazı da sonrakilerden.'),
(4994, 56, 15, '\'Özenle işlenmiş mücevher\' tahtlar üzerindedirler.'),
(4995, 56, 16, 'Karşılıklı yaslanmışlardır.'),
(4996, 56, 17, 'Çevrelerinde ölümsüzlüğe ulaşmış gençler dönüp dolaşır;'),
(4997, 56, 18, 'Kaynağından (doldurulmuş) testiler, ibrikler ve kadehler,'),
(4998, 56, 19, 'Ki bundan ne başlarını bir ağrı tutar, ne de kendilerinden geçip akılları çelinir.'),
(4999, 56, 20, 'Arzulayıp-seçecekleri meyveler,'),
(5000, 56, 21, 'Canlarının çektiği kuş eti.'),
(5001, 56, 22, 'Ve iri gözlü huriler,'),
(5002, 56, 23, 'Sanki saklı inciler gibi;'),
(5003, 56, 24, 'Yaptıklarına bir karşılık olmak üzere (onlara sunulur);'),
(5004, 56, 25, 'Orada, ne \'saçma ve boş bir söz\' işitirler, ne günaha sokma.'),
(5005, 56, 26, 'Yalnızca bir söz (işitirler:) "Selam, selam."'),
(5006, 56, 27, '"Ashab-ı Yemin", ne (kutludur o) "Ashab-ı Yemin."'),
(5007, 56, 28, 'Yüklü dalları bükülmüş kiraz (ağaçları),'),
(5008, 56, 29, 'Üstüste dizili meyveleri sarkmış muz ağaçları,'),
(5009, 56, 30, 'Yayılıp-uzanmış gölgeler,'),
(5010, 56, 31, 'Durmaksızın akan su(lar);'),
(5011, 56, 32, 'Ve (daha) birçok meyveler arasında,'),
(5012, 56, 33, 'Kesilip-eksilmeyen ve yasaklanmayan (meyveler).'),
(5013, 56, 34, 'Yükseklere-kurulmuş döşekler (sedirler).'),
(5014, 56, 35, 'Gerçek şu ki, Biz onları yeni bir inşa (yaratma) ile inşa edip-yarattık.'),
(5015, 56, 36, 'Onları hep bakireler olarak kıldık,'),
(5016, 56, 37, 'Eşlerine sevgiyle tutkun (ve) hep yaşıt,'),
(5017, 56, 38, '"Ashab-ı Yemin" olanlar için.'),
(5018, 56, 39, '(Bunların) Birçoğu geçmiş (ümmet)lerden,'),
(5019, 56, 40, 'Birçoğu da sonrakilerdendir.'),
(5020, 56, 41, '"Ashab-ı Şimal", ne (mutsuzdur o) "Ashab-ı Şimal."'),
(5021, 56, 42, 'Hücrelere işleyen kavurucu bir sıcaklık ve kaynar su,'),
(5022, 56, 43, 'Ve kapkara dumandan bir gölge içindedirler.'),
(5023, 56, 44, 'Ki o, ne serindir, ne ferahlatıcı (kerim).'),
(5024, 56, 45, 'Çünkü onlar, bundan önce varlık içinde şımartılmış olanlardı.'),
(5025, 56, 46, 'Onlar, büyük günah üzerinde ısrarlı davrananlardı.'),
(5026, 56, 47, 'Ve derlerdi ki: "Biz öldüğümüz, toprak ve kemik olduğumuzda mı, gerçekten biz mi diriltilecekmişiz?"'),
(5027, 56, 48, '"Önceden gelip-geçmiş atalarımız da mı?"'),
(5028, 56, 49, 'De ki: "Şüphesiz, öncekiler de ve sonrakiler de."'),
(5029, 56, 50, '"Bilinen bir günün belli vaktinde mutlaka toplanacaklardır."'),
(5030, 56, 51, 'Sonra gerçekten siz, ey sapık olan yalanlayıcılar,'),
(5031, 56, 52, 'Şüphesiz zakkum olan bir ağaçtan yiyeceksiniz.'),
(5032, 56, 53, 'Böylece karınları(nızı) ondan dolduracaksınız.'),
(5033, 56, 54, 'Onun üzerine de alabildiğine kaynar sudan içeceksiniz.'),
(5034, 56, 55, 'Üstelik \'içtikçe susayan hasta develerin\' içişi gibi içeceksiniz.'),
(5035, 56, 56, 'İşte bu, onların din (hesap ve ceza) gününde şölenleridir.'),
(5036, 56, 57, 'Sizleri Biz yarattık, yine de tasdik etmeyecek misiniz?'),
(5037, 56, 58, 'Şimdi (rahimlere) dökmekte olduğunuz meniyi gördünüz mü?'),
(5038, 56, 59, 'Onu sizler mi yaratıyorsunuz, yoksa Yaratıcı Biz miyiz?'),
(5039, 56, 60, 'Sizin aranızda ölümü takdir eden Biziz ve Bizim önümüze geçilmiş değildir;'),
(5040, 56, 61, '(Yerinize) Benzerlerinizi getirip-değiştirme ve sizi şimdi bilemeyeceğiniz bir şekilde-inşa etme konusunda.'),
(5041, 56, 62, 'Andolsun, ilk inşa (yaratma)yı bildiniz; ama öğüt alıp-düşünmeniz gerekmez mi?'),
(5042, 56, 63, 'Şimdi ekmekte olduğunuz (tohum)u gördünüz mü?'),
(5043, 56, 64, 'Onu sizler mi bitiriyorsunuz, yoksa bitiren Biz miyiz?'),
(5044, 56, 65, 'Eğer dilemiş olsaydık, gerçekten onu bir ot kırıntısı kılardık; böylelikle şaşar-kalırdınız.'),
(5045, 56, 66, '(Şöyle de sızlanırdınız:) "Doğrusu biz, ağır bir borç altına girip-zorlandık."'),
(5046, 56, 67, '"Hayır, biz büsbütün yoksun bırakıldık."'),
(5047, 56, 68, 'Şimdi siz, içmekte olduğunuz suyu gördünüz mü?'),
(5048, 56, 69, 'Onu sizler mi buluttan indiriyorsunuz, yoksa indiren Biz miyiz?'),
(5049, 56, 70, 'Eğer dilemiş olsaydık onu tuzlu kılardık; şükretmeniz gerekmez mi?'),
(5050, 56, 71, 'Şimdi yakmakta olduğunuz ateşi gördünüz mü?'),
(5051, 56, 72, 'Onun ağacını sizler mi inşa ettiniz (yarattınız), yoksa onu inşa eden Biz miyiz?'),
(5052, 56, 73, 'Biz onu hem bir öğüt ve hatırlatma (konusu), hem ihtiyacı olanlara bir meta kıldık.'),
(5053, 56, 74, 'Şu halde büyük Rabbini ismiyle tesbih et.'),
(5054, 56, 75, 'Hayır, yıldızların yer (mevki)lerine yemin ederim.'),
(5055, 56, 76, 'Şüphesiz bu, eğer bilirseniz gerçekten büyük bir yemindir.'),
(5056, 56, 77, 'Elbette bu, bir Kur\'an-ı Kerim\'dir.'),
(5057, 56, 78, 'Saklanmış-korunmuş bir Kitap\'ta (yazılı)dır.'),
(5058, 56, 79, 'Ona, temizlenip-arınmış olanlardan başkası dokunamaz.'),
(5059, 56, 80, 'Alemlerin Rabbinden indirilmedir.'),
(5060, 56, 81, 'Şimdi siz bu sözü mü hor görüp-küçümsüyorsunuz?'),
(5061, 56, 82, 'Ve rızkınızı (Kur\'an\'dan yararlanma nimetini bırakıp onu) mutlaka yalan saymaktan ibaret mi kılıyorsunuz?'),
(5062, 56, 83, 'Hele can boğaza gelip dayandığında,'),
(5063, 56, 84, 'Ki o sırada siz (sadece) bakıp-durursunuz,'),
(5064, 56, 85, 'Biz ona sizden daha yakınız; ancak görmezsiniz.'),
(5065, 56, 86, 'İşte o vakit, eğer ceza görmeyecek iseniz,'),
(5066, 56, 87, 'Eğer doğru söylüyorsanız, onu, (çıkmakta olan canı) geri çevirsenize.'),
(5067, 56, 88, 'Eğer o (ölecek kişi), yakın kılınan (mukarreb olan)lardan ise,'),
(5068, 56, 89, 'Bu durumda rahatlık, güzel rızık ve nimetlerle donatılmış cennet (onundur).'),
(5069, 56, 90, 'Ve eğer "Ashab-ı Yemin"den ise,'),
(5070, 56, 91, 'Artık, "Ashab-ı Yemin"den selam sana.'),
(5071, 56, 92, 'Ve eğer o, yalanlayan sapıklardan ise,'),
(5072, 56, 93, 'Artık (onun için) alabildiğine kaynar sudan bir şölen vardır.'),
(5073, 56, 94, 'Ve çılgınca yanan ateşe bir atılma da.'),
(5074, 56, 95, 'Şüphesiz bu, kesin bilgi ifade eden bir gerçektir (Hakku\'l-Yakin).'),
(5075, 56, 96, 'Öyleyse büyük Rabbini ismiyle tesbih et.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Göklerde ve yerde olanların tümü Allah\'ı tesbih etmiştir. O, üstün ve güçlü (aziz) olandır, hüküm ve hikmet sahibidir.'),
(5077, 57, 2, 'Göklerin ve yerin mülkü O\'nundur. Diriltir ve öldürür. O, herşeye güç yetirendir.'),
(5078, 57, 3, 'O, Evveldir, Ahirdir, Zahirdir, Batındır. O, herşeyi bilendir.'),
(5079, 57, 4, 'Gökleri ve yeri altı günde yaratan, sonra arşa istiva eden O\'dur. Yere gireni, ondan çıkanı, gökten ineni ve ona çıkanı bilir. Her nerede iseniz, O sizinle beraberdir, Allah, yaptıklarınızı görendir.'),
(5080, 57, 5, 'Göklerin ve yerin mülkü O\'nundur. (Sonunda bütün) işler Allah\'a döndürülür.'),
(5081, 57, 6, 'Geceyi gündüze bağlayıp-katar, gündüzü de geceye bağlayıp-katar. O, göğüslerin özünde (saklı) olanı bilendir.'),
(5082, 57, 7, 'Allah\'a ve Resûlü’ne iman edin. "Sizi kendilerinde halifeler kılıp harcama yetkisi verdiği\' şeylerden infak edin. Artık sizden kim iman edip infak ederse, onlara büyük bir ecir vardır.'),
(5083, 57, 8, 'Size ne oluyor ki, elçi sizi Rabbinize iman etmeye çağırıp-dururken Allah\'a iman etmiyorsunuz? Oysa O, sizden kesin bir söz almıştı. Eğer mü\'min iseniz (inanıp sözünüzü gerçekleştirin).'),
(5084, 57, 9, 'Sizi karanlıklardan nura çıkarması için kuluna apaçık ayetler indiren O\'dur. Şüphesiz Allah, size karşı elbette şefkatli olandır, esirgeyendir.'),
(5085, 57, 10, 'Size ne oluyor ki, Allah yolunda infak etmiyorsunuz? Oysa göklerin ve yerin mirası Allah\'ındır. İçinizden, fetihten önce infak eden ve savaşanlar (başkasıyla) bir olmaz. İşte onlar, derece olarak sonradan infak eden ve savaşanlardan daha büyüktür. Allah, her birine en güzel olanı va\'detmiştir. Allah, yaptıklarınızdan haberdardır.'),
(5086, 57, 11, 'Allah\'a güzel bir borç verecek olan kimdir? Artık Allah, bunu onun için kat kat arttırır. Onun için ‘kerim (üstün ve onurlu) bir ecir vardır.'),
(5087, 57, 12, 'O gün, mü\'min erkekler ile mü\'min kadınları, nurları önlerinde ve sağlarında koşarken görürsün. "Bugün sizin müjdeniz, içinde ebedi kalıcılar (olduğunuz), altından ırmaklar akan cennetlerdir." İşte \'büyük kurtuluş ve mutluluk\' budur.'),
(5088, 57, 13, 'O gün, münafık erkekler ile münafık kadınlar, iman edenlere derler ki: "(Ne olur) Bize bir bakın, sizin nurunuzdan birazcık alıp-yararlanalım." Onlara: "Arkanıza (dünyaya) dönün de bir nur arayıp-bulmaya çalışın" denilir. Derken aralarında kapısı olan bir sur çekilmiştir; onun iç yanında rahmet, dış yanında o yönden azap vardır.'),
(5089, 57, 14, '(Münafıklar) Onlara seslenirler: "Biz sizlerle birlikte değil miydik?" Derler ki: "Evet, ancak siz kendinizi fitneye düşürdünüz, (Müslümanları acıların ve yıkımların sarmasını) gözetip-beklediniz, (Allah\'a ve İslam\'a karşı) kuşkulara kapıldınız. Sizleri kuruntular yanıltıp-aldattı. Sonunda Allah\'ın emri (olan ölüm) geliverdi; ve o aldaltıcı da sizi Allah ile (Allah\'ın adını kullanarak, hatta masumca sizden görünerek) aldatmış oldu."'),
(5090, 57, 15, 'Artık bugün sizden herhangi bir fidye alınmaz ve inkar edenlerden de.. Barınma yeriniz ateştir, sizin veliniz (size yaraşan dost) odur; o ne kötü bir gidiş yeridir.'),
(5091, 57, 16, 'İman edenlerin, Allah\'ın ve haktan inmiş olanın zikri için kalplerinin \'saygı ve korku ile yumuşaması\' zamanı gelmedi mi? Onlar, bundan önce kendilerine kitap verilmiş, sonra üzerlerinden uzun bir süre geçmiş, böylece kalpleri de katılaşmış bulunanlar gibi olmasınlar. Onlardan çoğu fasık olanlardı.'),
(5092, 57, 17, 'Bilin ki gerçekten Allah, ölümünden sonra yeryüzüne hayat verir. Şüphesiz Biz, umulur ki aklınızı kullanırsınız diye size ayetleri açıkladık.'),
(5093, 57, 18, 'Gerçek şu ki, sadaka veren erkekler ile sadaka veren kadınlar ve Allah\'a güzel bir borç verenler; onlar için kat kat arttırılır ve \'kerim (üstün ve onurlu)\' olan ecir de onlarındır.'),
(5094, 57, 19, 'Allah\'a ve O\'nun Resûlü’ne iman edenler; işte onlar Rableri Katında sıddîklar ve şehidler (veya şahid)lerdir. Onların ecirleri ve nurları vardır. İnkar edip ayetlerimizi yalanlayanlar ise; işte onlar da cehennem halkıdır.'),
(5095, 57, 20, 'Bilin ki, dünya hayatı ancak bir oyun, \'(eğlence türünden) tutkulu bir oyalama\', bir süs, kendi aranızda bir övünme (süresi ve konusu), mal ve çocuklarda bir \'çoğalma-tutkusu\'dur. Bir yağmur örneği gibi; onun bitirdiği ekin ekicilerin (veya kafirlerin) hoşuna gitmiştir, sonra kuruyuverir, bir de bakarsın ki sapsarı kesilmiş, sonra o, bir çer-çöp oluvermiştir. Ahirette ise şiddetli bir azap; Allah\'tan bir mağfiret ve bir hoşnutluk (rıza) vardır. Dünya hayatı, aldanış olan bir metadan başka bir şey değildir.'),
(5096, 57, 21, 'Rabbinizden olan bir mağfirete ve cennete (kavuşmak için) \'çaba gösterip-yarışın,\' ki (o cennet) genişliği gök ile yerin genişliği gibi olup Allah\'a ve Resûlü’ne iman edenler için hazırlanmıştır. İşte bu, Allah\'ın fazlıdır ki, onu dilediğine verir. Allah büyük fazl sahibidir.'),
(5097, 57, 22, 'Yeryüzünde olan ve sizin nefislerinizde meydana gelen herhangi bir musibet yoktur ki, Biz onu yaratmadan önce, bir kitapta (yazılı) olmasın. Şüphesiz bu, Allah\'a göre pek kolaydır.'),
(5098, 57, 23, 'Öyle ki, elinizden çıkana karşı üzüntü duymayasınız ve size (Allah\'ın) verdikleri dolayısıyla sevinip-şımarmayasınız. Allah, büyüklük taslayıp böbürleneni sevmez.'),
(5099, 57, 24, 'Ki onlar, cimrilik ederler ve insanlara cimriliği emr (tavsiye) ederler. Her kim yüz çevirirse, artık şüphesiz Allah, Ğaniy (hiçbir şeye muhtaç olmayan), Hamid (övülmeye layık olan) O\'dur.'),
(5100, 57, 25, 'Andolsun, Biz elçilerimizi apaçık belgelerle gönderdik ve insanlar adaleti ayakta tutsunlar diye, onlarla birlikte kitabı ve mizanı indirdik. Ve kendisine çetin bir sertlik ve insanlar için (çeşitli) yararlar bulunan demiri de indirdik; öyle ki Allah, Kendisi\'ne ve elçilerine gayb ile (görmedikleri halde) kimlerin yardım edeceğini bilsin (ortaya çıkarsın). Şüphesiz Allah, büyük kuvvet sahibidir, üstün olandır.'),
(5101, 57, 26, 'Andolsun, Biz Nuh\'u ve İbrahim\'i (elçi olarak) gönderdik, peygamberliği ve kitabı onların soylarında kıldık. Öyle iken, içlerinde hidayeti kabul edenler vardır, onlardan birçoğu da fasık olanlardır.'),
(5102, 57, 27, 'Sonra onların izleri üzerinde elçilerimizi birbiri ardınca gönderdik. Meryem oğlu İsa\'yı da arkalarından gönderdik; ona İncil\'i verdik ve onu izleyenlerin kalplerinde bir şefkat ve merhamet kıldık. (Bir bid\'at olarak) Türettikleri ruhbanlığı ise, Biz onlara yazmadık (emretmedik). Ancak Allah\'ın rızasını aramak için (türettiler) ama buna da gerektiği gibi uymadılar. Bununla birlikte onlardan iman edenlere ecirlerini verdik, onlardan birçoğu da fasık olanlardır.'),
(5103, 57, 28, 'Ey iman edenler, Allah\'tan sakınıp-korkun ve O\'nun elçisine iman edin, size Kendi rahmetinden iki kat (güzel karşılık) versin. Size kendisiyle yürüyeceğiniz bir nur kılsın ve size mağfiret etsin. Allah çok bağışlayandır, çok esirgeyendir.'),
(5104, 57, 29, 'Öyle ki, Kitap Ehli (Yahudi ve Hıristiyanlar) Allah\'ın fazlından hiçbir şeye \'güç yetirip-sahip olmadıklarını\' ve fazlın muhakkak Allah\'ın elinde olduğunu, onu dilediğine verdiğini bilip-öğrensin. Allah, büyük fazl (üstün lütuf ve ihsan) sahibidir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Gerçekten Allah, eşi konusunda seninle tartışan ve Allah\'a şikayette bulunan (kadın)ın sözünü işitti. Allah, aranızda geçen konuşmaları işitiyordu. Şüphesiz Allah, işitendir, görendir.'),
(5106, 58, 2, 'Sizden kadınlarına "zıhar"da bulunanlar (bilsinler ki, kadınları) onların anneleri değildir. Anneleri, yalnızca kendilerini doğuranlardır. Şüphesiz onlar, çirkin ve yalan söylemektedirler. Gerçekten Allah, çok affeden, çok bağışlayandır.'),
(5107, 58, 3, 'Kadınlarına "zıhar"da bulunanlar, sonra söylediklerinden geri dönenlerin, birbirleriyle temas etmeden önce bir köleyi özgürlüğüne kavuşturmaları gerekir. İşte size bununla öğüt verilmektedir. Allah, yaptıklarınızı haber alandır.'),
(5108, 58, 4, 'Ancak buna (imkan) bulamayanlar (için de) birbirleriyle temas etmeden önce, kesintisiz iki ay oruç (yüklenmiştir); buna güç yetiremeyenler altmış yoksulu doyursun. Bu (kolaylık), Allah\'a ve O\'nun Resûlü’ne iman etmeniz dolayısıyladır. Bunlar, Allah\'ın sınırlarıdır. Kafirler içinse acı bir azap vardır.'),
(5109, 58, 5, 'Gerçekten Allah\'a ve Resûlü’ne karşı (onların koydukları sınırları tanımayıp kendileri sınır koymaya kalkışmakla) başkaldıranlar, kendilerinden öncekilerin alçaltılması gibi alçaltılmışlardır. Oysa Biz apaçık ayetler indirdik. Kafirler için küçültücü bir azap vardır.'),
(5110, 58, 6, 'Allah, hepsini dirilteceği gün, onlara neler yaptıklarını haber verecektir. Allah, onları (yaptıklarıyla bir bir) saymıştır; onlar ise onu unutmuşlardır. Allah, herşeye şahid olandır.'),
(5111, 58, 7, 'Allah\'ın göklerde ve yerde olanların tümünü gerçekten bilmekte olduğunu görmüyor musun? (Kendi aralarında gizli toplantılar düzenleyip) Fısıldaşmakta olan üç kişiden dördüncüleri mutlaka O\'dur; beşin altıncısı da mutlaka O\'dur. Bundan az veya çok olsun, her nerede olsalar mutlaka O, kendileriyle beraberdir. Sonra yaptıklarını kıyamet günü kendilerine haber verecektir. Şüphesiz Allah, herşeyi bilendir.'),
(5112, 58, 8, '\'Gizli toplantıların fısıldaşmalarından’ (kulis) men\' edilip sonra men\' edildikleri şeye dönenleri; günah, düşmanlık ve Peygamber’e isyanı (aralarında) fısıldaşanları görmüyor musun? Onlar sana geldikleri zaman, seni Allah\'ın selamladığı biçimde selamlıyorlar. Ve kendi kendilerine: "Söylediklerimiz dolayısıyla Allah bize azap etse ya." derler. Onlara cehennem yeter; oraya gireceklerdir. Artık o, ne kötü bir gidiş yeridir.'),
(5113, 58, 9, 'Ey iman edenler, kendi aranızda gizli konuşmalarda bulunacağınız zaman, bundan böyle günah, düşmanlık ve Peygamber’e isyanı fısıldaşıp-konuşmayın; birri (iyiliği) ve takvayı konuşun ve huzurunda toplanacağınız Allah\'tan sakının.'),
(5114, 58, 10, 'Şüphesiz \'gizli toplantıların fısıldaşmaları\' (kulis), iman edenleri üzüntüye düşürmek için ancak şeytan (ürünü olan işler)dandır. Oysa Allah\'ın izni olmaksızın o, onlara hiçbir şeyle zarar verecek değildir. Şu halde mü\'minler, yalnızca Allah\'a tevekkül etsinler.'),
(5115, 58, 11, 'Ey iman edenler, size meclislerde "Yer açın" dendiği zaman, yer açın; Allah size genişlik versin. Size: "Kalkın" denildiği zaman da kalkın. Allah, sizden iman edenleri ve kendilerine ilim verilenleri derecelerle yükseltsin. Allah, yaptıklarınızdan haberdardır.'),
(5116, 58, 12, 'Ey iman edenler, Peygambere gizli bir şey arzedeceğiniz zaman, gizli konuşmanızdan önce bir sadaka verin. Bu, sizin için daha hayırlı ve daha temizdir. Şayet (buna imkan) bulamazsanız, artık şüphesiz Allah, çok bağışlayandır, çok esirgeyendir.'),
(5117, 58, 13, 'Gizli konuşmanızdan önce sadaka vermekten ürktünüz mü? Çünkü yapmadınız, Allah sizin tevbelerinizi kabul etti. Şu halde namazı dosdoğru kılın, zekatı verin ve Allah\'a ve O\'nun Resûlü’ne itaat edin. Allah, yaptıklarınızdan haberdardır.'),
(5118, 58, 14, 'Allah\'ın kendilerine karşı gazablandığı bir kavmi veli (dost ve müttefik) edinenleri görmedin mi? Onlar, ne sizdendirler, ne onlardan. Kendileri de (açıkça gerçeği) bildikleri halde, yalan üzere yemin ediyorlar.'),
(5119, 58, 15, 'Allah, onlara şiddetli bir azap hazırlamıştır. Doğrusu onların yaptıkları ne kötüdür.'),
(5120, 58, 16, 'Onlar, yeminlerini bir siper edindiler, böylece Allah\'ın yolundan alıkoydular. Artık onlar için alçaltıcı bir azap vardır.'),
(5121, 58, 17, 'Ne malları, ne çocukları onlara Allah\'a karşı hiçbir şeyle yarar sağlamaz. Onlar, ateşin halkıdır, içinde süresiz kalacaklardır.'),
(5122, 58, 18, 'Onların tümünü Allah\'ın dirilteceği gün, sizlere yemin ettikleri gibi O\'na da yemin edeceklerdir ve kendilerinin bir şey üzerine olduklarını sanacaklardır. Dikkat edin; gerçekten onlar, yalan söyleyenlerin ta kendileridir.'),
(5123, 58, 19, 'Şeytan onları sarıp-kuşatmıştır; böylelikle onlara Allah\'ın zikrini unutturmuştur. İşte onlar, şeytanın fırkasıdır. Dikkat edin; şüphesiz şeytanın fırkası, hüsrana uğrayanların ta kendileridir.'),
(5124, 58, 20, 'Hiç şüphesiz Allah\'a ve Resûlü’ne karşı (onların koydukları sınırları tanımayıp kendileri sınır koymaya kalkışmakla) başkaldıranlar; işte onlar, en çok zillete düşenler arasında olanlardır.'),
(5125, 58, 21, 'Allah, yazmıştır: "Andolsun, ben galip geleceğim ve elçilerim de." Gerçekten Allah, en büyük kuvvet sahibidir, güçlü ve üstün olandır.'),
(5126, 58, 22, 'Allah\'a ve ahiret gününe iman eden hiçbir kavim (topluluk) bulamazsın ki, Allah\'a ve elçisine başkaldıran kimselerle bir sevgi (ve dostluk) bağı kurmuş olsunlar; bunlar, ister babaları, ister çocukları, ister kardeşleri, isterse kendi aşiretleri (soyları) olsun. Onlar, öyle kimselerdir ki, (Allah) kalplerine imanı yazmış ve onları Kendinden bir ruh ile desteklemiştir. Onları, altlarından ırmaklar akan cennetlere sokacaktır; orda süresiz olarak kalacaklardır. Allah, onlardan razı olmuş, onlar da O\'ndan razı olmuşlardır. İşte onlar, Allah\'ın fırkasıdır. Dikkat edin; şüphesiz Allah\'ın fırkası olanlar, felah (umutlarını gerçekleştirip kurtuluş) bulanların ta kendileridir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Göklerde ve yerde olanların tümü Allah\'ı tesbih etmiştir. O, üstün ve güçlü olandır, hüküm ve hikmet sahibidir.'),
(5128, 59, 2, 'Kitap Ehlinden inkar edenleri ilk sürgünde yurtlarından çıkaran O\'dur. Onların çıkacaklarını siz sanmamıştınız, onlar da kalelerinin kendilerini Allah\'tan koruyacağını sanmışlardı. Böylece Allah(ın azabı) da, onlara hesaba katmadıkları bir yönden geldi, yüreklerine korku saldı; öyle ki evlerini kendi elleriyle ve mü\'minlerin elleriyle tahrip ediyorlardı. Artık ey basiret sahipleri ibret alın.'),
(5129, 59, 3, 'Eğer Allah, onlara sürgünü yazmamış olsaydı, muhakkak onları (yine) dünyada azaplandırırdı. Ahirette ise onlar için ateş azabı vardır.'),
(5130, 59, 4, 'Bu, onların Allah\'a ve O\'nun Resûlü’ne \'başkaldırıp ayrılık çıkarmaları\' dolayısıyladır. Kim Allah\'a başkaldırıp-ayrılık çıkarırsa, muhakkak Allah, cezası (ikabı) pek şiddetli olandır.'),
(5131, 59, 5, 'Hurma ağaçlarından her neyi kesmişseniz veya kökleri üzerinde dimdik bırakmışsanız, (bu) Allah\'ın izniyledir ve fasık olanları alçaltması içindir.'),
(5132, 59, 6, 'Onlardan Allah\'ın elçisine verdiği "fey\'e" gelince, ki siz buna karşı (bunu elde etmek için) ne at, ne deve sürdünüz. Ancak Allah, elçilerini dilediklerinin üstüne musallat kılar. Allah, herşeye güç yetirendir.'),
(5133, 59, 7, 'Allah\'ın o (fethedilen) şehir halkından Resûlü’ne verdiği fey, Allah\'a, Resûl’e, (ve Resûl’e) yakın akrabalığı olanlara, yetimlere, yoksullara ve yolda kalmışlara aittir. Öyle ki (bu mallar ve servet) sizden zengin olanlar arasında dönüp-dolaşan bir devlet olmasın. Resûl size ne verirse artık onu alın, sizi neden sakındırırsa artık ondan sakının ve Allah\'tan korkun. Şüphesiz Allah, cezası (ikabı) pek şiddetli olandır.'),
(5134, 59, 8, '(Bundan başka bu mallar,) Hicret eden fakirleredir ki, onlar, Allah\'tan bir fazl (lütuf ve ihsan) arayıp, Allah\'a ve O\'nun Resûlü’ne yardım ederlerken yurtlarından ve mallarından sürülüp-çıkarılmışlardır. İşte bunlar, sadık olanlar bunlardır.'),
(5135, 59, 9, 'Kendilerinden önce o yurdu (Medine\'yi) hazırlayıp imanı (gönüllerine) yerleştirenler ise, hicret edenleri severler ve onlara verilen şeylerden dolayı içlerinde bir ihtiyaç (arzusu) duymazlar. Kendilerinde bir açıklık (ihtiyaç) olsa bile (kardeşlerini) öz nefislerine tercih ederler. Kim nefsinin \'cimri ve bencil tutkularından\' korunmuşsa, işte onlar, felah (kurtuluş) bulanlardır.'),
(5136, 59, 10, 'Bir de onlardan sonra gelenler, derler ki: "Rabbimiz, bizi ve bizden önce iman etmiş olan kardeşlerimizi bağışla ve kalplerimizde iman edenlere karşı bir kin bırakma. Rabbimiz, gerçekten Sen, çok şefkatlisin, çok esirgeyicisin."'),
(5137, 59, 11, 'Münafıklık edenleri görmüyor musun ki, Kitap Ehlinden inkar eden kardeşlerine derler ki: "Andolsun, eğer siz (yurtlarınızdan) çıkarılacak olursanız, mutlaka biz de sizinle birlikte çıkarız ve size karşı olan hiç kimseye, hiçbir zaman itaat etmeyiz. "Eğer size karşı savaşılırsa elbette size yardım ederiz." Oysa Allah, şahidlik etmektedir ki onlar, gerçekten yalancıdırlar.'),
(5138, 59, 12, 'Andolsun, (yurtlarından) çıkarılacak olurlarsa onlarla birlikte çıkmazlar. Onlara karşı savaşılırsa da, kendilerine yardımda bulunmazlar; yardım etseler bile (arkalarına) dönüp-kaçarlar. Sonra kendilerine yardım edilmez.'),
(5139, 59, 13, 'Herhalde içlerinde \'dehşet ve yılgınlık uyandırma bakımından\' siz, Allah\'tan daha çetinsiniz. Bu, şüphesiz onların \'derin bir kavrayışa sahip olmamaları\' dolayısıyla böyledir.'),
(5140, 59, 14, 'Onlar, iyice korunmuş şehirlerde veya duvar arkasında olmaksızın sizinle toplu bir halde savaşmazlar. Kendi aralarındaki çarpışmaları ise pek şiddetlidir. Sen onları birlik sanırsın, oysa kalpleri paramparçadır. Bu, şüphesiz onların akletmeyen bir kavim olmaları dolayısıyla böyledir.'),
(5141, 59, 15, 'Kendilerinden önce yakın geçmişte olanların durumu gibi; onlar, yaptıklarının sonucunu tadmışlardır. Onlara acı bir azap vardır.'),
(5142, 59, 16, 'Şeytanın durumu gibi; çünkü insana "İnkar et" dedi, inkar edince de: "Gerçek şu ki, ben senden uzağım. Doğrusu ben, alemlerin Rabbi olan Allah\'tan korkarım" dedi.'),
(5143, 59, 17, 'Sonunda onların akibetleri, şüphesiz ateşin içinde ikisinin de süresiz olarak kalıcı olmalarıdır. İşte zalim olanların cezası budur.'),
(5144, 59, 18, 'Ey iman edenler, Allah\'tan korkun. Herkes yarın için neyi takdim ettiğine baksın. Allah\'tan korkun. Hiç şüphesiz Allah, yaptıklarınızdan haberdardır.'),
(5145, 59, 19, 'Kendileri Allah\'ı unutmuş, böylece O da onlara kendi nefislerini unutturmuş olanlar gibi olmayın. İşte onlar, fasık olanların ta kendileridir.'),
(5146, 59, 20, 'Ateş halkı ile cennet halkı bir olmaz. Cennet halkı \'umduklarına kavuşup mutluluk içinde olanlardır.\''),
(5147, 59, 21, 'Şayet Biz bu Kur\'an\'ı bir dağın üzerine indirmiş olsaydık, andolsun onu Allah korkusundan saygı ile baş eğmiş, parça parça olmuş görürdün. İşte Biz, belki düşünürler diye, insanlara böyle örnekler veririz.'),
(5148, 59, 22, 'O Allah ki, O\'ndan başka İlah yoktur. Gaybı da, müşahede edilebileni de bilendir. Rahman, Rahim olan O\'dur.'),
(5149, 59, 23, 'O Allah ki, O\'ndan başka İlah yoktur. Meliktir; Kuddûstur; Selam\'ır; Mü\'mindir; Müheymindir; Azizdir; Cebbardır; Mütekebbirdir. Allah, (müşriklerin) şirk koştuklarından çok Yücedir.'),
(5150, 59, 24, 'O Allah ki, Yaratan\'dır, (en güzel bir biçimde) kusursuzca var edendir, \'şekil ve suret\' verendir. En güzel isimler O\'nundur. Göklerde ve yerde olanların tümü O\'nu tesbih etmektedir. O, Aziz, Hakimdir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Ey iman edenler, Benim de düşmanım, sizin de düşmanınız olanları veliler edinmeyin. Siz onlara karşı sevgi yöneltiyorsunuz; oysa onlar haktan size geleni inkar etmişler, Rabbiniz olan Allah\'a inanmanızdan dolayı elçiyi de, sizi de (yurtlarınızdan) sürüp-çıkarmışlardır. Eğer siz, Benim yolumda cihad etmek ve Benim rızamı aramak amacıyla çıkmışsanız (nasıl) onlara karşı hala sevgi gizliyorsunuz? Ben, sizin gizlediklerinizi ve açığa vurduklarınızı bilirim. Kim sizden bunu yaparsa, artık o, elbette yolun ortasından şaşırıp-sapmış olur.'),
(5152, 60, 2, 'Eğer sizi ele geçirecek olurlarsa, size düşman kesilirler, ellerini ve dillerini kötülükle size uzatırlar. Onlar sizin inkar etmenizi içten arzu etmişlerdir.'),
(5153, 60, 3, 'Ne yakın akrabalarınız, ne çocuklarınız kıyamet günü size bir yarar sağlayamaz. (Allah) Sizin aranızı ayıracaktır. Allah, yaptıklarınızı görendir.'),
(5154, 60, 4, 'İbrahim ve onunla birlikte olanlarda size güzel bir örnek vardır. Hani kendi kavimlerine demişlerdi ki: "Biz, sizlerden ve Allah\'ın dışında taptıklarınızdan gerçekten uzağız. Sizi (artık) tanımayıp-inkar ettik. Sizinle aramızda, siz Allah\'a bir olarak iman edinceye kadar ebedi bir düşmanlık ve bir kin baş göstermiştir." Ancak İbrahim\'in babasına: "Sana bağışlanma dileyeceğim, ama Allah\'tan gelecek herhangi bir şeye karşı senin için gücüm yetmez." demesi hariç. "Ey Rabbimiz, biz Sana tevekkül ettik ve \'içten Sana yöneldik.\' Dönüş Sanadır."'),
(5155, 60, 5, '"Rabbimiz, bizi inkar edenler için fitne (deneme konusu) kılma ve bizi bağışla Rabbimiz. Şüphesiz Sen, üstün ve güçlüsün, hüküm ve hikmet sahibisin."'),
(5156, 60, 6, 'Andolsun, onlarda sizlere, Allah\'ı ve ahiret gününü umud edenlere güzel bir örnek vardır. Kim yüz çevirecek olursa, artık şüphesiz Allah, Ğaniy (hiçbir şeye ihtiyacı olmayan), Hamid (övülmeye layık olan)dır.'),
(5157, 60, 7, 'Belki Allah, sizlerle onlardan kendilerine karşı düşmanlık besledikleriniz arasında bir sevgi-bağı kılar. Allah, güç yetirendir. Allah, çok bağışlayandır, çok esirgeyendir.'),
(5158, 60, 8, 'Allah, sizinle din konusunda savaşmayan, sizi yurtlarınızdan sürüp-çıkarmayanlara iyilik yapmanızdan ve onlara adaletli davranmanızdan sizi sakındırmaz. Çünkü Allah, adalet yapanları sever.'),
(5159, 60, 9, 'Allah, ancak din konusunda sizinle savaşanları, sizi yurtlarınızdan sürüp-çıkaranları ve sürülüp-çıkarılmanız için arka çıkanları dost edinmenizden sakındırır. Kim onları dost edinirse, artık onlar zalimlerin ta kendileridir.'),
(5160, 60, 10, 'Ey iman edenler, mü\'min kadınlar hicret ederek size geldikleri zaman, onları imtihan edin. Allah, onların imanlarını daha iyi bilendir. Şayet (gerçekten) mü\'min kadınlar olduklarını bilip-öğrenirseniz, artık sakın onları kafirlere geri çevirmeyin. (Çünkü) Ne bunlar onlara helaldir, ne onlar bunlara helaldir. Onlara (kafir kocalarına kendileri için) harcadıklarını verin. Onlara (hicret eden mü\'min kadınlara) ücretlerini (mehirlerini) verdiğiniz takdirde onları nikahlamanızda size bir güçlük yoktur. Kafir (kadın)ların ismetlerini (nikahlarını) tutmayın ve (onlar için) harcadıklarınızı isteyin. Onlar da (mü\'min kadınlara) harcadıklarını istesinler. Bu, Allah\'ın hükmüdür; sizin aranızda hükmeder. Allah, bilendir, hüküm ve hikmet sahibidir.'),
(5161, 60, 11, 'Ve eğer eşlerinizden (kafirlere kaçmalarından dolayı) herhangi bir şey kafirlere geçer, böylece siz de (savaşta onları yenip) ganimete kavuşursanız, eşleri (kaçıp) gidenlere (mehir olarak) harcama yaptıklarının bir mislini verin. Kendisi\'ne iman ettiğiniz Allah\'tan sakının.'),
(5162, 60, 12, 'Ey Peygamber, mü\'min kadınlar, Allah\'a hiçbir şeyi ortak koşmamak, hırsızlık yapmamak, zina etmemek, çocuklarını öldürmemek, elleri ve ayakları arasında bir iftira düzüp-uydurmamak (gayri meşru olan bir çocuğu kocalarına dayandırmamak), ma\'ruf (iyi, güzel ve yararlı bir iş) konusunda isyan etmemek üzere, sana biat etmek amacıyla geldikleri zaman, onların biatlarını kabul et ve onlar için Allah\'tan mağfiret iste. Şüphesiz Allah, çok bağışlayandır, çok esirgeyendir.'),
(5163, 60, 13, 'Ey iman edenler, Allah\'ın kendilerine karşı gazablandığı bir kavmi veli (dost ve müttefik) edinmeyin; ki onlar, kafirlerin mezar halkından umut kesmeleri gibi ahiretten umut kesmişlerdir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Göklerde ve yerde olanların tümü Allah\'ı tesbih etmiştir. O, üstün ve güçlüdür, hüküm ve hikmet sahibidir.'),
(5165, 61, 2, 'Ey iman edenler, yapmayacağınız şeyi neden söylersiniz?'),
(5166, 61, 3, 'Yapmayacağınız şeyi söylemeniz, Allah Katında bir gazab (konusu olması) bakımından büyüdü (büyük bir suç teşkil etti).'),
(5167, 61, 4, 'Şüphesiz Allah, Kendi yolunda, sanki birbirlerine kenetlenmiş bir bina gibi saf bağlayarak çarpışanları sever.'),
(5168, 61, 5, 'Hani Musa, kavmine demişti ki: "Ey kavmim, gerçekten benim sizin için Allah\'tan gönderilmiş bir elçi olduğumu bildiğiniz halde, niçin bana eziyet ediyorsunuz?" İşte onlar eğrilip-sapınca Allah da onların kalplerini eğriltip saptırmış oldu. Allah, fasık bir kavmi hidayete erdirmez.'),
(5169, 61, 6, 'Hani Meryem oğlu İsa da: "Ey İsrailoğulları, gerçekten ben, sizin için Allah\'tan gönderilmiş bir elçiyim. Benden önceki Tevrat\'ı doğrulayıcı ve benden sonra ismi "Ahmed" olan bir elçinin de müjdeleyicisiyim" demişti. Fakat o, onlara apaçık belgelerle gelince: "Bu, açıkça bir büyüdür" dediler.'),
(5170, 61, 7, 'İslam\'a çağrıldığı halde, Allah\'a karşı yalan uyduranlardan daha zalim kimdir? Allah, zalim bir kavmi hidayete erdirmez.'),
(5171, 61, 8, 'Onlar, Allah\'ın nurunu ağızlarıyla söndürmek istiyorlar. Oysa Allah, Kendi nurunu tamamlayıcıdır; kafirler hoş görmese bile.'),
(5172, 61, 9, 'Elçilerini hidayet ve hak din üzere gönderen O\'dur. Öyle ki onu (hak din olan İslam\'ı) bütün dinlere karşı üstün kılacaktır; müşrikler hoş görmese bile.'),
(5173, 61, 10, 'Ey iman edenler, sizi acı bir azaptan kurtaracak bir ticareti haber vereyim mi?'),
(5174, 61, 11, 'Allah\'a ve O\'nun Resulü’ne iman edersiniz, mallarınızla ve canlarınızla Allah yolunda mücadele edersiniz. Bu, sizin için daha hayırlıdır; eğer bilirseniz.'),
(5175, 61, 12, 'O da sizin günahlarınızı bağışlar, sizi altlarından ırmaklar akan cennetlere ve Adn cennetlerindeki güzel konaklara yerleştirir. İşte \'büyük mutluluk ve kurtuluş\' budur.'),
(5176, 61, 13, 'Ve seveceğiniz bir başka (nimet) daha var: Allah\'tan \'yardım ve zafer (nusret)\' ve yakın bir fetih. Mü\'minleri müjdele.'),
(5177, 61, 14, 'Ey iman edenler, Allah\'ın yardımcıları olun: Meryem oğlu İsa\'nın havarilere: "Allah\'a (yönelirken) benim yardımcılarım kimlerdir?" demesi gibi. Havariler de demişlerdi ki: "Allah\'ın yardımcıları bizleriz." Böylece İsrailoğulları\'ndan bir topluluk iman etmiş, bir topluluk da inkar etmişti. Sonunda Biz iman edenleri düşmanlarına karşı destekledik, onlar da üstün geldiler.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Göklerde ve yerde olanların tümü, Melik; Kuddüs; Aziz; Hakim olan Allah\'ı tesbih eder.'),
(5179, 62, 2, 'O, ümmîler içinde, kendilerinden olan ve onlara ayetlerini okuyan, onları arındırıp-temizleyen ve onlara kitap ve hikmeti öğreten bir elçi gönderendir. Oysa onlar, bundan önce gerçekten açıkça bir sapıklık içinde idiler.'),
(5180, 62, 3, 'Ve henüz kendilerine ulaşıp-katılmamış olan diğerlerine de (peygamber gönderilmiştir); O (Allah), üstün ve güçlüdür, hüküm ve hikmet sahibidir.'),
(5181, 62, 4, 'Bu, Allah\'ın dilediğine verdiği fazl (lütuf ve ihsan)ıdır. Allah, büyük fazl sahibidir.'),
(5182, 62, 5, 'Kendilerine Tevrat yükletilip de sonra onu (içindeki derin anlamları, hikmet ve hükümleriyle gereği gibi) yüklenmemiş olanların durumu, koskoca kitap yükü taşıyan eşeğin durumu gibidir. Allah\'ın ayetlerini yalanlayan kavmin durumu ne kötüdür. Allah, zalim bir kavmi hidayete erdirmez.'),
(5183, 62, 6, 'De ki: "Ey Yahudi olanlar, eğer siz, (bütün) insanlardan ayrı olarak yalnızca sizlerin gerçekten Allah\'ın velileri (dost ve sevgili kulları) olduğunuzu öne sürüyorsanız, şu halde ölümü temenni edin; eğer doğru sözlü iseniz (bunu çekinmeden yapın)."'),
(5184, 62, 7, 'Oysa onlar, ellerinin öne takdim ettikleri dolayısıyla bunu hiçbir zaman temenni edemezler. Allah, zalimleri bilendir.'),
(5185, 62, 8, 'De ki: "Elbette sizin kendisinden kaçtığınız ölüm, şüphesiz sizinle karşılaşıp-buluşacaktır. Sonra gaybı da, müşahede edilebileni de bilen (Allah)a döndürüleceksiniz; O da size yaptıklarınızı haber verecektir."'),
(5186, 62, 9, 'Ey iman edenler, cuma günü namaz için çağrı yapıldığı zaman, hemen Allah\'ı zikretmeye koşun ve alış-verişi bırakın. Eğer bilirseniz, bu sizin için daha hayırlıdır.'),
(5187, 62, 10, 'Artık namazı kılınca, yeryüzünde dağılın. Allah\'ın fazlını isteyip-arayın ve Allah\'ı çokça zikredin; umulur ki felaha (kurtuluşa ve umduklarınıza) kavuşmuş olursunuz.'),
(5188, 62, 11, 'Oysa onlar (kendilerini tümüyle Allah\'a ve İslam\'a teslim etmeyenler) bir ticaret ya da bir eğlence gördükleri zaman, (hemen) ona sökün ettiler ve seni ayakta bıraktılar. De ki: "Allah\'ın Katında bulunan, eğlenceden ve ticaretten daha hayırlıdır. Allah, rızık verenlerin en hayırlısıdır."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Münafıklar sana geldikleri zaman: "Biz gerçekten şehadet ederiz ki, sen kesin olarak Allah\'ın elçisisin" dediler. Allah da bilir ki sen elbette O\'nun elçisisin. Allah, şüphesiz münafıkların yalan söylediklerine şahidlik eder.'),
(5190, 63, 2, 'Onlar, yeminlerini bir siper edinip Allah\'ın yolundan alıkoydular. Doğrusu ne kötü şey yapıyorlar.'),
(5191, 63, 3, 'Bu, onların iman etmeleri sonra inkar etmeleri dolayısıyla böyledir. Böylece kalplerinin üzerini mühürlemiştir, artık onlar kavrayamazlar.'),
(5192, 63, 4, 'Sen onları gördüğün zaman cüsseli yapıları beğenini kazanmaktadır. Konuştukları zaman da onları dinlersin. (Oysa) Sanki onlar (sütun gibi) dayandırılmış ahşap-kütük gibidirler. (Bu dayanıksızlıklarından dolayı da) Her çağrıyı kendileri aleyhinde sanırlar. Onlar düşmandırlar, bu yüzden onlardan kaçınıp-sakının. Allah onları kahretsin; nasıl da çevriliyorlar.'),
(5193, 63, 5, 'Onlara: "Gelin Allah\'ın Resûlü sizin için mağfiret (bağışlanma) dilesin," denildiği zaman başlarını yana çevirdiler. Sen, onların büyüklük taslamışlar olarak yüz çevirmekte olduklarını görürsün.'),
(5194, 63, 6, 'Senin onlar adına mağfiret dilemen ile mağfiret dilememen onlar için birdir. Allah, onlara kesin olarak mağfiret etmeyecektir. Şüphesiz Allah, fasık bir kavme hidayet vermez.'),
(5195, 63, 7, 'Onlar ki: "Allah\'ın Resûlü yanında bulunanlara hiçbir infak (harcama)da bulunmayın, sonunda dağılıp gitsinler," derler. Oysa göklerin ve yerin hazineleri Allah\'ındır. Ancak münafıklar kavramıyorlar.'),
(5196, 63, 8, 'Derler ki, "Andolsun, Medine\'ye bir dönecek olursak, gücü ve onuru çok olan, düşkün ve zayıf olanı elbette oradan sürüp-çıkaracaktır." Oysa izzet (güç, onur ve üstünlük) Allah\'ın, O\'nun Resûlü’nün ve mü\'minlerindir. Ancak münafıklar bilmiyorlar.'),
(5197, 63, 9, 'Ey iman edenler, ne mallarınız, ne çocuklarınız sizi Allah\'ı zikretmekten \'tutkuya kaptırarak-alıkoymasın\'; kim böyle yaparsa, artık onlar hüsrana uğrayanların ta kendileridir.'),
(5198, 63, 10, 'Sizden birinize ölüm gelip de: "Rabbim, beni yakın bir süreye (ecele) kadar geciktirsen ben de böylece sadaka versem ve salihlerden olsam" demezden önce, size rızık olarak verdiklerimizden infak edin.'),
(5199, 63, 11, 'Oysa Allah, kendi eceli gelmiş bulunan hiçbir kimseyi kesinlikle ertelemez. Allah, yaptıklarınızdan haberdardır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Göklerde ve yerde olanların tümü Allah\'ı tesbih eder. Mülk O\'nundur, hamd (övgü) de O\'nundur. O, herşeye güç yetirendir.'),
(5201, 64, 2, 'Sizi yaratan O\'dur; buna rağmen sizden kiminiz kafirdir, kiminiz mü\'min, Allah, yaptıklarınızı görendir.'),
(5202, 64, 3, 'Gökleri ve yeri hak olmak üzere yarattı ve size düzenli bir biçim (suret) verdi; suretlerinizi de güzel yaptı. Dönüş O\'nadır.'),
(5203, 64, 4, 'Göklerde ve yerde olanların tümünü bilir; sizin saklı tuttuklarınızı da, açığa vurduklarınızı da bilir. Allah, sinelerin özünde saklı duranı bilendir.'),
(5204, 64, 5, 'Bundan önce inkar edenlerin haberi size gelmedi mi? İşte onlar, işlerinin vebalini taddılar. Onlara acı bir azap vardır.'),
(5205, 64, 6, 'Bu, kendilerine apaçık belgelerle elçiler geldiği halde "bizi bir beşer mi hidayete ulaştıracak?" demeleri ve bu yüzden inkar edip saparak yüz çevirmeleri nedeniyledir. Allah da (onlara karşı) müstağni olduğunu (hiçbir şeye ihtiyacı olmadığını) gösterdi. Allah Ğani\'dir, Hamid\'dir.'),
(5206, 64, 7, 'İnkar edenler kesin olarak diriltilmeyeceklerini öne sürdüler. De ki: "Hayır, Rabbim adına andolsun, siz, muhakkak diriltileceksiniz; sonra mutlaka yaptıklarınız size haber verilecektir. Bu da Allah\'a göre oldukça kolaydır."'),
(5207, 64, 8, '"Şu halde Allah\'a, O\'nun Resûlü’ne ve indirdiğimiz nur (Kur\'an)a iman edin. Allah yaptıklarınızdan haberdardır."'),
(5208, 64, 9, 'Sizi toplanma günü için birarada toplayacağı gün; işte bu aldanma (teğabün) günüdür. Kim Allah\'a iman edip salih bir amelde bulunursa (Allah) onun kötülüklerini örter ve içinde ebedi kalıcılar olmak üzere altından ırmaklar akan cennetlere sokar. İşte büyük \'mutluluk ve kurtuluş (fevz)\' budur.'),
(5209, 64, 10, 'İnkar edip ayetlerimizi yalanlayanlara gelince; onlar da içinde sürekli kalıcılar olmak üzere, ateşin halkıdırlar. Ne kötü bir dönüş yeridir O.'),
(5210, 64, 11, 'Allah\'ın izni olmaksızın hiçbir musibet (hiç kimseye) isabet etmez. Kim Allah\'a iman ederse, onun kalbini hidayete yöneltir. Allah, herşeyi bilendir.'),
(5211, 64, 12, 'Allah\'a itaat edin ve Resûle de itaat edin. Şayet yüz çevirecek olursanız, artık elçimiz üzerine düşen (yalnızca) apaçık bir tebliğ (gerçeği en yalın biçimde size iletme)dir.'),
(5212, 64, 13, 'Allah; O\'ndan başka İlah yoktur. Öyleyse mü\'minler (yalnızca) Allah\'a tevekkül etsinler.'),
(5213, 64, 14, 'Ey iman edenler, gerçek şu ki, sizin eşlerinizden ve çocuklarınızdan bir kısmı sizler için (birer) düşmandırlar. Şu halde onlardan sakının. Yine de affeder, hoş görür (kusurlarını yüzlerine vurmaz) ve bağışlarsanız, artık elbette Allah, bağışlayandır, esirgeyendir.'),
(5214, 64, 15, 'Mallarınız ve çocuklarınız sizin için ancak bir fitne (bir deneme)dir. Allah ise, büyük ecir (en güzel karşılık) O\'nun Katında olandır.'),
(5215, 64, 16, 'Öyleyse güç yetirebildiğiniz kadar Allah\'tan korkup-sakının, dinleyin ve itaat edin. Kendi nefsinize hayır (en büyük yarar) olmak üzere infakta bulunun. Kim nefsinin bencil-tutkularından (ya da cimri tutumundan) korunursa; işte onlar, felah (kurtuluş) bulanlardır.'),
(5216, 64, 17, 'Eğer Allah\'a güzel bir borç verecek olursanız, onu sizin için kat kat arttırır ve sizi bağışlar. Allah Şekûr\'dur (şükrü kabul edip çok ihsan eden), Halim\'dir (cezayı vermekte acele etmeyendir).'),
(5217, 64, 18, 'Gaybı da, müşahede edilebileni de bilen, Aziz (üstün ve güçlü), Hakim (hüküm ve hikmet sahibi)dir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Ey Peygamber, kadınları boşadığınız zaman, iddetleri süresinde (temizlendiklerinde) boşayın ve iddeti sayın. Rabbiniz Allah\'tan korkun. Onları evlerinden çıkarmayın, onlar da çıkmasınlar; ancak açık \'çirkin bir hayasızlık\' göstermeleri durumu başka. Bunlar Allah\'ın sınırlarıdır. Kim Allah\'ın sınırlarını çiğnerse, gerçekte o, kendi nefsine zulmetmiş olur. Sen bilmezsin; olabilir ki Allah, bunun arkasından bir iş (durum) oluşturur.'),
(5219, 65, 2, 'Sonra (üç iddet bekleme) sürelerine ulaştıkları zaman, artık onları maruf (bilinen güzel bir tarz) üzere tutun, ya da maruf üzere onlardan ayrılın. İçinizden adalet sahibi iki kişiyi de şahid tutun. Şahidliği Allah için dosdoğru yerine getirin. İşte bununla, Allah\'a ve ahiret gününe iman edenlere öğüt verilir. Kim Allah\'tan korkup-sakınırsa, (Allah) ona bir çıkış yolu gösterir;'),
(5220, 65, 3, 'Ve onu hesaba katmadığı bir yönden rızıklandırır. Kim de Allah\'a tevekkül ederse, O, ona yeter. Elbette Allah, Kendi emrini yerine getirip-gerçekleştirendir. Allah, herşey için bir ölçü kılmıştır.'),
(5221, 65, 4, 'Kadınlarınızdan artık adetten kesilmiş olanlarla henüz adet görmemiş bulunanların iddet (bekleme süre)leri, -eğer şüpheye düşecek olursanız (bilin ki)- üç aydır. Hamile kadınların bekleme-süresi ise, yüklerini bırakmaları (ile biter). Kim Allah\'tan korkup-sakınırsa (Allah) ona işinde bir kolaylık gösterir.'),
(5222, 65, 5, 'Bu, Allah\'ın size indirdiği emridir. Kim Allah\'tan korkup-sakınırsa, Allah, kötülüklerini örter ve onun ecrini büyütür.'),
(5223, 65, 6, '(Boşadığınız) Kadınları, gücünüz oranında oturmakta olduğunuz yerin bir yanında oturtun, onlara \'darlık ve sıkıntıya düşürmek amacıyla\' zarar vermeyin. Eğer onlar hamile iseler, yüklerini bırakıncaya (doğumlarını yapıncaya) kadar onlara nafaka verin. Şayet sizler için (çocuğu) emzirirlerse, onlara ücretlerini ödeyin. (Durum ve ilişkilerinizi) Kendi aranızda maruf (güzellikle ve İslam\'a uygun bir tarz) üzere görüşüp-konuşun. Eğer güçlük içine girerseniz, bu durumda (çocuğu) onun (babası) için bir başkası emzirebilir.'),
(5224, 65, 7, 'Geniş-imkanları olan, nafakayı geniş imkanlarına göre versin. Rızkı kısıtlı tutulan da, artık Allah\'ın kendisine verdiği kadarıyla versin. Allah, hiçbir nefse ona verdiğinden başkasıyla yükümlülük koymaz. Allah, bir güçlüğün ardından bir kolaylığı kılıp-verecektir.'),
(5225, 65, 8, 'Ülkelerden niceleri vardır ki, Rablerinin ve O\'nun elçilerinin emrine karşı gelip azmışlar, böylece Biz de onları çetin bir hesaba çekmişiz ve onları benzeri görülmedik bir azapla azaplandırmışız.'),
(5226, 65, 9, 'Artık o (ülkelerin halkı), yaptığı kötülüğü taddı ve işinin sonucu bir hüsran oldu.'),
(5227, 65, 10, 'Allah, onlar için şiddetli bir azap hazırlamıştır; öyleyse ey iman eden temiz akıl sahipleri, Allah\'tan korkun. Doğrusu Allah, size bir zikir (uyaran, hatırlatan ve öğüt veren Kur\'an) indirmiştir.'),
(5228, 65, 11, 'İman edip salih amellerde bulunanları karanlıklardan nura çıkarması için Allah\'ın apaçık ayetlerini size okuyan bir elçi de (gönderdik). Kim iman edip salih bir amelde bulunursa, (Allah) onu içinde süresiz kalıcılar olmak üzere altından ırmaklar akan cennetlere sokar. Allah, gerçekten ona ne güzel bir rızık vermiştir.'),
(5229, 65, 12, 'Allah, yedi göğü ve yerden de onların benzerini yarattı. Emir, bunların arasında durmadan iner; sizin gerçekten Allah\'ın herşeye güç yetirdiğini ve gerçekten Allah\'ın ilmiyle herşeyi kuşattığını bilmeniz, öğrenmeniz için.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Ey Peygamber, eşlerinin hoşnutluğunu isteyerek, Allah\'ın sana helal kıldıklarını niçin haram kılıyorsun? Allah, çok bağışlayandır, çok esirgeyendir.'),
(5231, 66, 2, 'Allah, yeminlerinizin (keffaretle) çözülmesini size farz (veya meşru) kıldı. Allah, sizin mevlanız (sahibiniz, yardımcınız)dır. O, bilendir, hüküm ve hikmet sahibidir.'),
(5232, 66, 3, 'Hani Peygamber, eşlerinden bazılarına gizli bir söz söylemişti. Derken o (eşlerinden biri), bunu haber verip Allah da ona bunu açığa vurunca, O da (Peygamber) bir kısmını açıklamış bir kısmını (söylemekten) vazgeçmişti. Sonunda haberi verince (eşi) demişti ki: "Bunu sana kim haber verdi?" O da: "Bana bilen, (herşeyden) haberdar olan (Allah) haber verdi" demişti.'),
(5233, 66, 4, 'Eğer sizler (Peygamberin iki eşi) Allah\'a tevbe ederseniz (ne güzel); çünkü kalpleriniz eğrilik gösterdi. Yok eğer ona karşı birbirinize destekçi olmaya kalkışırsanız, artık Allah, onun mevlasıdır; Cibril ve mü\'minlerin salih olan(lar)ı da. Bunların arkasından melekler de onun destekçisidirler.'),
(5234, 66, 5, 'Belki onun Rabbi, -eğer o sizi boşayacak olursa- ona yerinize sizlerden daha hayırlı Müslüman, mü\'min, gönülden itaat eden, tevbe eden, ibadet eden, oruç tutan dul ve bakire eşler\' verir.'),
(5235, 66, 6, 'Ey iman edenler, kendinizi ve yakınlarınızı ateşten koruyun ki onun yakıtı insanlar ve taşlardır; üzerinde oldukça sert, güçlü melekler vardır. Allah kendilerine neyi emretmişse ona isyan etmezler ve emredildiklerini yerine getirirler.'),
(5236, 66, 7, 'Ey inkar edenler, bugün özür beyan etmeyin. Siz ancak yaptıklarınızla cezalandırılıyorsunuz.'),
(5237, 66, 8, 'Ey iman edenler, Allah\'a kesin (nasuh) bir tevbe ile tevbe edin. Olabilir ki, Allah sizin kötülüklerinizi örter ve altından ırmaklar akan cennetlere sokar. O gün Allah, Peygamberi ve onunla birlikte iman edenleri küçük düşürmeyecektir. Nurları, önlerinde ve sağ yanlarında koşar-parıldar. Derler ki: "Rabbimiz nurumuzu tamamla, bizi bağışla. Şüphesiz Sen, herşeye güç yetirensin."'),
(5238, 66, 9, 'Ey Peygamber, kafirlere ve münafıklara karşı cihad et ve onlara karşı \'sert ve caydırıcı\' davran. Onların barınma yeri cehennemdir. Ne kötü bir dönüş yeridir o.'),
(5239, 66, 10, 'Allah, inkar edenlere, Nuh\'un eşini ve Lut\'un eşini örnek verdi. İkisi de, kullarımızdan salih olan iki kulumuzun nikahları altındaydı; ancak onlara ihanet ettiler. Bundan dolayı, (kocaları) kendilerine Allah\'tan gelen hiçbir şeyle yarar sağlamadılar. İkisine de: "Ateşe diğer girenlerle birlikte girin" denildi.'),
(5240, 66, 11, 'Allah, iman edenlere de Firavun\'un karısını örnek verdi. Hani demişti ki: "Rabbim bana Kendi Katında, cennette bir ev yap; beni Firavun\'dan ve onun yaptıklarından kurtar ve beni o zalimler topluluğundan da kurtar."'),
(5241, 66, 12, 'İmran\'ın kızı Meryem\'i de. Ki o kendi ırzını korumuştu. Böylece Biz ona ruhumuzdan üfledik. O da Rabbinin kelimelerini ve kitaplarını tasdik etti. O, (Rabbine) gönülden bağlı olanlardandı.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Mülk elinde bulunan (Allah) ne Yücedir. O, herşeye güç yetirendir.'),
(5243, 67, 2, 'O, amel (davranış ve eylem) bakımından hanginizin daha iyi (ve güzel) olacağını denemek için ölümü ve hayatı yarattı. O, üstün ve güçlü olandır, çok bağışlayandır.'),
(5244, 67, 3, 'O, biri diğeriyle \'tam bir uyum’ (mutabakat) içinde yedi gök yaratmış olandır. Rahman (olan Allah)ın yaratmasında hiçbir \'çelişki ve uygunsuzluk’ (tefavüt) göremezsin. İşte gözü(nü) çevirip-gezdir; herhangi bir çatlaklık (bozukluk ve çarpıklık) görüyor musun?'),
(5245, 67, 4, 'Sonra gözünü iki kere daha çevirip-gezdir; o göz (uyumsuzluk bulmaktan) umudunu kesmiş bir halde bitkin olarak sana dönecektir.'),
(5246, 67, 5, 'Andolsun, Biz en yakın olan göğü (dünya göğünü) kandillerle süsleyip-donattık ve bunları, şeytanlar için taşlama-birimleri (rücum) kıldık. Onlar için çılgınca yanan ateşin azabını hazırladık.'),
(5247, 67, 6, 'Rablerini inkar edenler için cehennem azabı vardır. Ne kötü dönüş yeridir o.'),
(5248, 67, 7, 'İçine atıldıkları zaman, kaynayıp-feveran ederken onun korkunç homurtusunu işitirler.'),
(5249, 67, 8, 'Öfkesinin-şiddetinden neredeyse patlayıp parçalanacak. Her bir grup içine atıldığında, bekçileri onlara sorar: "Size bir uyarıcı gelmedi mi?"'),
(5250, 67, 9, 'Onlar: "Evet" derler. "Bize gerçekten bir uyarıcı geldi. Fakat biz yalanladık ve: “Allah hiçbir şey indirmedi, siz yalnızca büyük bir sapmışlık içindesiniz, dedik."'),
(5251, 67, 10, 'Ve derler ki: "Eğer dinlemiş olsaydık ya da akıl etmiş olsaydık, şu çılgınca yanan ateşin halkı arasında olmayacaktık."'),
(5252, 67, 11, 'Böylece kendi günahlarını itiraf ettiler. Çılgınca yanan ateşin halkına (Allah\'ın rahmetinden) uzaklık olsun.'),
(5253, 67, 12, 'Gerçek şu ki, Rablerinden gayb ile (O\'nu görmedikleri halde) içleri titreyerek-korkanlara gelince; onlar için bir mağfiret (bağışlanma) ve büyük bir ecir vardır.'),
(5254, 67, 13, 'Sözünüzü ister gizleyin, ister açığa vurun. Şüphesiz O, sinelerin özünde saklı duranı bilendir.'),
(5255, 67, 14, 'O, yarattığını bilmez mi? O, Latif\'tir; Habir\'dir.'),
(5256, 67, 15, 'Sizin için, yeryüzüne boyun eğdiren O\'dur. Şu halde onun omuzlarında yürüyün ve O\'nun rızkından yiyin. Sonunda gidiş O\'nadır.'),
(5257, 67, 16, 'Gökte olanın sizi yere geçirmeyeceğinden emin misiniz? Bir bakmışsınız ki, o (yeryüzü) sallanıp-çalkalanmaktadır.'),
(5258, 67, 17, 'Yoksa gökte olanın üzerinize \'taş yağdıran (fırtınalı) bir rüzgar\' göndermeyeceğinden emin misiniz? Siz o takdirde Benim uyarmam nasılmış bilip-öğreneceksiniz.'),
(5259, 67, 18, 'Andolsun, kendilerinden öncekiler de yalanladı. Fakat Beni inkar (etmelerine karşılık verdiğim azap) nasılmış?'),
(5260, 67, 19, 'Onlar, üstlerinde dizi dizi kanat açıp kapayarak uçan kuşları görmüyorlar mı? Onları Rahman (olan Allah\')tan başkası (boşlukta) tutmuyor. Şüphesiz O, herşeyi hakkıyla görendir.'),
(5261, 67, 20, 'Rahmana karşı size yardım edecek olan kimmiş? Şu sizin ordunuz mu? Kafirler yalnızca bir gurur (kesin bir aldanış) içindedirler.'),
(5262, 67, 21, 'Eğer O, rızkını tutsa (vermese), rızkınızı verecek olan kimmiş? Hayır; onlar, bir azgınlık ve nefret içinde inatla direniyorlar.'),
(5263, 67, 22, 'Şu halde yüzükoyun sürünerek yürüyen mi daha çok hidayete erer, yoksa dosdoğru yol üzerinde dümdüz yürümekte olan mı?'),
(5264, 67, 23, 'De ki: "Sizi inşa eden (yaratan), size kulak, gözler ve gönüller veren O\'dur. Ne az şükrediyorsunuz?"'),
(5265, 67, 24, 'De ki: "Sizi yeryüzünde üretip-türeten O\'dur. Siz O\'na toplanıp götürüleceksiniz."'),
(5266, 67, 25, 'Derler ki: "Eğer doğru söylüyorsanız, şu tehdit (ettiğiniz azap) ne zamanmış?"'),
(5267, 67, 26, 'De ki: "(Bununla ilgili) Bilgi ancak Allah\'ın Katındadır. Ben ancak apaçık bir uyarıcıyım."'),
(5268, 67, 27, 'Nihayet onu pek yakında gördüklerinde, o inkar edenlerin yüzleri kötüleşip-karardı. Ve: "İşte bu, sizin (gerçekleşmeyecek diye) öne sürüp durduğunuz şeydir" denildi.'),
(5269, 67, 28, 'De ki: "Haber verir misiniz; eğer Allah, beni ve benimle birlikte olanları yıkıma uğratır ya da bizi esirgerse, (peki) bu durumda kafirleri acı bir azaptan kurtaracak olan kimdir?"'),
(5270, 67, 29, 'De ki: "O (Allah) Rahman olan (esirgeyen koruyan)dır; biz O\'na iman ettik ve O\'na tevekkül ettik. Artık siz kimin açık bir sapmışlık içinde olduğunu pek yakında bileceksiniz."'),
(5271, 67, 30, 'De ki: "Haber verin; eğer suyunuz yerin dibine göçüverecek olsa, bu durumda kim size bir akar su kaynağı getirebilir?');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nun. Kaleme ve satır satır yazdıklarına andolsun.'),
(5273, 68, 2, 'Sen, Rabbinin nimetiyle bir mecnun değilsin.'),
(5274, 68, 3, 'Gerçekten senin için kesintisi olmayan bir ecir vardır.'),
(5275, 68, 4, 'Ve şüphesiz sen, pek büyük bir ahlak üzerindesin.'),
(5276, 68, 5, 'Artık yakında göreceksin ve onlar da görecekler.'),
(5277, 68, 6, 'Sizden, hanginizin \'fitneye tutulup-çıldırdığını.\''),
(5278, 68, 7, 'Elbette senin Rabbin, kimin Kendi yolundan şaşırıp-saptığını daha iyi bilendir; ve kimin hidayete erdiğini de daha iyi bilendir.'),
(5279, 68, 8, 'Şu halde yalanlayanlara itaat etme.'),
(5280, 68, 9, 'Onlar, senin kendilerine yaranmanı (uzlaşmanı) arzu ettiler; o zaman onlar da sana yaranıp-uzlaşacaklardı.'),
(5281, 68, 10, 'Şunların hiçbirine itaat etme: Yemin edip duran, aşağılık,'),
(5282, 68, 11, 'Alabildiğine ayıplayıp kötüleyen, söz getirip götüren (gizlilik içinde söz ve haber taşıyan),'),
(5283, 68, 12, 'Hayrı engelleyip sürdüren, saldırgan, olabildiğince günahkar,'),
(5284, 68, 13, 'Zorba-saygısız, sonra da kulağı kesik;'),
(5285, 68, 14, 'Mal (servet) ve çocuklar sahibi oldu diye,'),
(5286, 68, 15, 'Kendisine ayetlerimiz okunduğu zaman: "(Bunlar) Eskilerin uydurma masallarıdır" diyen.'),
(5287, 68, 16, 'Yakında Biz onun hortumu (burnu) üzerine damga vuracağız.'),
(5288, 68, 17, 'Gerçek şu ki, Biz o bahçe sahiplerine bela verdiğimiz gibi, bunlara da bela verdik. Hani onlar, sabah vakti (erkenden ve kimseye haber vermeden) onu (bahçeyi) mutlaka devşireceklerine dair and içmişlerdi.'),
(5289, 68, 18, '(Bu konuda) Hiçbir istisna yapmıyorlardı.'),
(5290, 68, 19, 'Fakat onlar, uyuyorlarken, Rabbin tarafından dolaşıp-gelen bir bela\' onun üstünü sarıp-kuşatıverdi.'),
(5291, 68, 20, 'Sonunda (bahçe) kökünden kuruyup-kapkara kesildi.'),
(5292, 68, 21, 'Nihayet sabah vakti birbirlerine seslendiler.'),
(5293, 68, 22, '"Eğer ürününüzü devşirecekseniz erkence kalkıp-çıkın."'),
(5294, 68, 23, 'Derken, aralarında fısıldaşarak çıkıp-gittiler:'),
(5295, 68, 24, '"Bugün sakın oraya hiçbir yoksul girip de karşınıza çıkmasın."'),
(5296, 68, 25, '(Yoksulları) Engellemeye güçleri yetebilirmiş gibi erkenden gittiler.'),
(5297, 68, 26, 'Ama onu görünce: "Muhakkak biz (gideceğimiz yeri) şaşırmışız" dediler.'),
(5298, 68, 27, '"Hayır, biz (herşeyden ve bütün servetimizden) yoksun bırakıldık."'),
(5299, 68, 28, '(İçlerinde) Mutedil olan biri dedi ki: "Ben size dememiş miydim? (Allah\'ı) Tesbih edip yüceltmeniz gerekmez miydi?"'),
(5300, 68, 29, 'Dediler ki: "Rabbimiz Seni tesbih eder, yüceltiriz; gerçekten bizler zalim imişiz."'),
(5301, 68, 30, 'Şimdi birbirlerine karşı kendilerini kınamaya başladılar.'),
(5302, 68, 31, '"Yazıklar bize, gerçekten bizler azgınmışız" dediler.'),
(5303, 68, 32, '"Belki Rabbimiz, onun yerine daha hayırlısını verir; şüphesiz biz, yalnızca Rabbimiz\'e rağbet eden kimseleriz."'),
(5304, 68, 33, 'İşte azap böyledir. Ahiret azabı ise, muhakkak çok daha büyüktür; bir bilseler.'),
(5305, 68, 34, 'Doğrusu, muttaki olanlar için Rableri Katında nimetlerle donatılmış cennetler vardır.'),
(5306, 68, 35, 'Öyleyse, Müslümanları suçlu-günahkar olanlar gibi (eşit) kılar mıyız?'),
(5307, 68, 36, 'Size ne oluyor? Nasıl hüküm veriyorsunuz?'),
(5308, 68, 37, 'Yoksa (elinizde) ders okumakta olduğunuz bir kitap mı var?'),
(5309, 68, 38, 'İçinde, neyi seçip-beğenirseniz, mutlaka sizin olacak diye.'),
(5310, 68, 39, 'Yoksa sizin için üzerimizde kıyamete kadar sürüp gidecek bir yemin mi var ki siz ne hüküm verirseniz o, mutlaka sizin kalacak, diye.'),
(5311, 68, 40, 'Onlara sor: "Hangisi bunun savunuculuğunu yapacak?'),
(5312, 68, 41, 'Yoksa onların ortakları mı var? Şu halde eğer doğru sözlü kimselerse, ortaklarını getirsinler.'),
(5313, 68, 42, 'Ayağın üstünden (örtünün) açılacağı ve onların secdeye çağrılacakları gün, artık güç yetiremezler.'),
(5314, 68, 43, 'Gözleri \'korkudan ve dehşetten düşük\', kendilerini de zillet sarıp-kuşatmış. Oysa onlar, (daha önce) sapasağlam iken secdeye davet edilirlerdi.'),
(5315, 68, 44, 'Artık bu sözü yalan sayanı sen Bana bırak. Biz onları, bilmeyecekleri bir yönden derece derece (azaba) yaklaştıracağız.'),
(5316, 68, 45, 'Ben, onlara süre tanıyorum. Elbette Benim düzenim (cezalandırmam) sapasağlamdır.'),
(5317, 68, 46, 'Sen, onlardan bir ücret mi istiyorsun ki, onlar, haksız bir borçtan dolayı ağır bir yük altında kalmışlar?'),
(5318, 68, 47, 'Yoksa gayb (görünmeyenin bilgisi) onların yanında mıdır ki, kendileri yazıp duruyorlar?'),
(5319, 68, 48, 'Şimdi sen, Rabbinin hükmüne sabret ve balık sahibi (Yunus) gibi olma; hani o, içi kahır dolu olarak (Rabbine) çağrıda bulunmuştu.'),
(5320, 68, 49, 'Eğer Rabbinden bir nimet ona ulaşmasaydı, mutlaka yerilmiş ve çıplak bir durumda (karaya) atılmış olacaktı.'),
(5321, 68, 50, 'Fakat Rabbi onu seçti ve onu salih olanlardan kıldı.'),
(5322, 68, 51, 'O inkar edenler, zikri (Kur\'an\'ı) işittikleri zaman, seni neredeyse gözleriyle devireceklerdi. "O, gerçekten bir delidir" diyorlar.'),
(5323, 68, 52, 'Oysa o (Kur\'an), alemlere bir zikr (öğüt, hatırlatma, hüküm ve üstün bir şeref)den başka bir şey değildir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, '\'Elbette gerçekleşecek olan\' (kıyamet).'),
(5325, 69, 2, 'Nedir o \'muhakkak gerçekleşecek olan?\''),
(5326, 69, 3, 'O gerçekleşecek olanı (kıyameti) sana bildiren nedir?'),
(5327, 69, 4, 'Semud ve Ad (toplumları), karia’yı yalan saydılar.'),
(5328, 69, 5, 'Bu nedenle Semud (halkı), korkunç bir sesle helak edildi.'),
(5329, 69, 6, 'Ad (halkın)a gelince; onlar da, uğultu yüklü, azgın bir kasırga ile helak edildiler.'),
(5330, 69, 7, '(Allah) Onu, yedi gece ve sekiz gün, aralık vermeksizin üzerlerine musallat etti. Öyle ki, o kavmin, orada sanki içi kof hurma kütükleriymiş gibi çarpılıp yere yıkıldığını görürsün.'),
(5331, 69, 8, 'Şimdi onlardan hiç arta kalan (bir şey) görüyor musun?'),
(5332, 69, 9, 'Firavun (kavmi), ondan öncekiler ve yerle bir olan şehirler (halkı da hep) o hata ile (tarih sahnesine) geldiler.'),
(5333, 69, 10, 'Böylece Rablerinin elçisine isyan ettiler. Bu yüzden onları, şiddeti gittikçe artan bir yakalayışla yakaladı.'),
(5334, 69, 11, 'Gerçek şu ki, su taştığı zaman, o gemide Biz sizi taşıdık;'),
(5335, 69, 12, 'Öyle ki, onu sizlere bir ibret (hatırlatma ve öğüt) kılalım. \'Gerçeği belleyip kavrayabilen\' kullar da onu belleyip-kavrasın.\''),
(5336, 69, 13, 'Artık sur\'a tek bir üfürülüşle üfürüleceği.'),
(5337, 69, 14, 'Yeryüzü ve dağlar yerlerinden oynatılıp kaldırılacağı, ardından tek bir çarpma ile birbirlerine çarpılıp parça parça olacağı zaman.'),
(5338, 69, 15, 'İşte o gün, vakıa (bir gerçek olan kıyamet) artık vukubulmuş (gerçekleşmiş)tur.'),
(5339, 69, 16, 'Gök yarılıp-çatlamıştır; artık o gün, \'sarkmış-za\'fa uğramıştır.\''),
(5340, 69, 17, 'Melek(ler) ise, onun çevresi üzerindedir. O gün, Rabbinin arşını onların da üstünde sekiz (melek) taşır.'),
(5341, 69, 18, 'Siz o gün arzolunursunuz; sizden yana hiçbir gizli (şey), gizli kalmaz.'),
(5342, 69, 19, 'Artık kitabı sağ-eline verilen kişi, der ki: "Alın, kitabımı okuyun."'),
(5343, 69, 20, '"Çünkü ben, gerçekten hesabıma kavuşacağımı sanmış (anlamış)tım."'),
(5344, 69, 21, 'Artık o, hoşnut bir yaşama içindedir.'),
(5345, 69, 22, 'Yüksek bir cennette.'),
(5346, 69, 23, 'Devşirilecek (meyve ve eşsiz ürün)leri pek yakındır.'),
(5347, 69, 24, '"Geride kalan günlerde, \'peşin olarak sunduklarınıza karşılık olmak üzere,\' afiyetle yiyin ve için."'),
(5348, 69, 25, 'Kitabı sol eline verilen ise; o da, der ki: "Bana keşke kitabım verilmeseydi."'),
(5349, 69, 26, '"Hesabımı hiç bilmeseydim."'),
(5350, 69, 27, '"Keşke o (ölüm herşeyi) kesip bitirseydi.'),
(5351, 69, 28, '"Malım bana hiçbir yarar sağlayamadı."'),
(5352, 69, 29, '"Güç ve kudretim yok olup gitti."'),
(5353, 69, 30, '(Allah buyruk verir:) "Onu tutuklayın, hemen bağlayın."'),
(5354, 69, 31, '"Sonra çılgın alevlerin içine atın."'),
(5355, 69, 32, '"Daha sonra onu, uzunluğu yetmiş arşın olan bir zincire vurup gönderin."'),
(5356, 69, 33, '"Çünkü, o, büyük olan Allah\'a iman etmiyordu."'),
(5357, 69, 34, '"Yoksula yemek vermeye destekçi olmazdı."'),
(5358, 69, 35, '"Bundan dolayı bugün, kendisine hiçbir sıcak dost yoktur."'),
(5359, 69, 36, '"İrin ve kan karışımından başka bir yemek yoktur."'),
(5360, 69, 37, '"Bunu da, hata edenlerden başkası yemez."'),
(5361, 69, 38, 'Hayır; gördüklerinize yemin ederim,'),
(5362, 69, 39, 'Görmediklerinize de.'),
(5363, 69, 40, 'Hiç şüphesiz o (Kur\'an), şerefli bir elçinin kesin sözüdür.'),
(5364, 69, 41, 'O, bir şairin sözü değildir. Ne az inanıyorsunuz?'),
(5365, 69, 42, 'Bir kahinin de sözü değildir. Ne az öğüt alıp-düşünüyorsunuz?'),
(5366, 69, 43, 'Alemlerin Rabbinden bir indirilmedir.'),
(5367, 69, 44, 'Eğer o, Bize karşı bazı sözleri uydurup-söylemiş olsaydı.'),
(5368, 69, 45, 'Muhakkak onun sağ-elini (bütün güç ve kudretini) çekip-alıverirdik.'),
(5369, 69, 46, 'Sonra onun can damarını elbette keserdik.'),
(5370, 69, 47, 'O zaman, sizden hiç kimse araya girerek bunu kendisinden engelleyip-uzaklaştıramazdı.'),
(5371, 69, 48, 'Çünkü o (Kur\'an, Allah\'tan sakınan) muttakiler için bir öğüttür.'),
(5372, 69, 49, 'Elbette Biz, içinizde yalanlayanların bulunduğunu biliyoruz.'),
(5373, 69, 50, 'Gerçekten o (Kur\'an), kafirler için bir hasrettir.'),
(5374, 69, 51, 'Ve şüphesiz o, kesin bir gerçektir (hakku\'l-yakîn).'),
(5375, 69, 52, 'Öyleyse, büyük Rabbini ismiyle tesbih et.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'İstekte bulunan biri, (muhakkak) gerçekleşecek olan bir azabı istedi.'),
(5377, 70, 2, 'Kafirler için olan bu (azabı) geri çevirecek yoktur.'),
(5378, 70, 3, '(Bu azap) Yüce makamlar sahibi olan Allah\'tandır.'),
(5379, 70, 4, 'Melekler ve Ruh (Cebrail), ona, süresi elli bin yıl olan bir günde çıkabilmektedir.'),
(5380, 70, 5, 'Şu halde, güzel bir sabır (göstererek) sabret.'),
(5381, 70, 6, 'Çünkü, gerçekten onlar, bunu uzak görüyorlar.'),
(5382, 70, 7, 'Biz ise, onu pek yakın görüyoruz.'),
(5383, 70, 8, 'Gökyüzünün erimiş maden gibi olacağı gün;'),
(5384, 70, 9, 'Dağlar da (etrafa uçuşmuş) rengarenk yün gibi olacak.'),
(5385, 70, 10, '(Böyle bir günde) Hiçbir yakın dost bir yakın dostu sormaz.'),
(5386, 70, 11, 'Onlar birbirlerine gösterilirler. Bir suçlu-günahkar, o günün azabına karşılık olmak üzere, oğullarını fidye olarak vermek ister;'),
(5387, 70, 12, 'Kendi eşini ve kardeşini,'),
(5388, 70, 13, 'Ve onu barındıran aşiretini de;'),
(5389, 70, 14, 'Yeryüzünde bulunanların tümünü (verse de); sonra bir kurtulsa.'),
(5390, 70, 15, 'Hayır; (hiçbiri kabul edilmez). Doğrusu o (cehennem), cayır cayır yanmakta olan ateştir:'),
(5391, 70, 16, 'Başın derisini kavurup-soyar.'),
(5392, 70, 17, 'Yüz çevirip arkasını döneni çağırır-durur.'),
(5393, 70, 18, '(Durmaksızın mal ve servet) Toplayıp bir yerde (üstüste) yığmakta olanı.'),
(5394, 70, 19, 'Gerçekten, insan, \'bencil ve haris\' olarak yaratıldı.'),
(5395, 70, 20, 'Kendisine bir şer (kötülük) dokunduğu zaman feryadı basar.'),
(5396, 70, 21, 'Ona bir hayır dokunduğunda engelleyici olur (veya cimrilik eder).'),
(5397, 70, 22, 'Ancak namaz kılanlar hariç;'),
(5398, 70, 23, 'Ki onlar, namazlarında süreklidirler.'),
(5399, 70, 24, 'Ve onların mallarında belirli bir hak vardır:'),
(5400, 70, 25, 'Yoksul ve yoksun olan(lar)için.'),
(5401, 70, 26, 'Onlar, din gününü tasdik etmektedirler.'),
(5402, 70, 27, 'Rablerinin azabına karşı (daimi) bir korku duymaktadırlar.'),
(5403, 70, 28, 'Şüphesiz Rablerinin azabından emin olunamaz.'),
(5404, 70, 29, 'Ve onlar, ırzlarını (ferç) korurlar;'),
(5405, 70, 30, 'Ancak kendi eşleri ya da sağ ellerinin malik olduğu başka; çünkü onlar (bunlardan dolayı) kınanmazlar.'),
(5406, 70, 31, 'Fakat bunun ötesini arayanlar, artık onlar sınırı çiğneyenlerdir.'),
(5407, 70, 32, '(Bir de) Onlar, kendilerine verilen emanete ve verdikleri ahde (harfiyyen) riayet edenlerdir.'),
(5408, 70, 33, 'Şahidliklerinde dosdoğru davrananlardır.'),
(5409, 70, 34, 'Namazlarını (titizlikle) koruyanlardır.'),
(5410, 70, 35, 'İşte onlar, cennetler içinde ağırlananlardır.'),
(5411, 70, 36, 'Şimdi inkar edenlere ne oluyor ki, boyunlarını sana uzatıp koşuyorlar.'),
(5412, 70, 37, 'Sağ yandan ve sol yandan bölükler halinde.'),
(5413, 70, 38, 'Onlardan her biri, nimetlerle donatılmış cennete gireceğini mi umuyor (tamah ediyor)?'),
(5414, 70, 39, 'Hayır; doğrusu Biz onları bildikleri şeyden yarattık.'),
(5415, 70, 40, 'Artık, doğuların ve batıların Rabbine yemin ederim; Biz gerçekten güç yetireniz;'),
(5416, 70, 41, 'Onların yerine kendilerinden daha hayırlılarına getirip-değiştirmeye. Üstelik Bizim önümüze geçilemez.'),
(5417, 70, 42, 'Şu halde sen, kendilerine vadedilen (azap) günlerine kavuşuncaya kadar onları bırak; dalıp-oynasınlar, oyalansınlar.'),
(5418, 70, 43, 'Kabirlerinden koşarcasına çıkarılacakları gün, sanki onlar dikili birşeye yönelmiş gibidirler.'),
(5419, 70, 44, 'Gözleri \'korkudan ve dehşetten düşük\' yüzlerini de bir zillet kaplamış; işte bu, kendilerine vadedilmekte olan (kıyamet ve azap) günüdür.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Şüphesiz, Biz Nuh\'u; "Kavmini, onlara acı bir azap gelmeden evvel uyar" diye kendi kavmine (peygamber olarak) gönderdik.'),
(5421, 71, 2, 'O da dedi ki: "Ey Kavmim, gerçek şu ki, ben size (gönderilmiş) apaçık bir uyarıcıyım."'),
(5422, 71, 3, '"Allah\'a kulluk edin, O\'ndan korkun ve bana itaat edin."'),
(5423, 71, 4, '"Ki günahlarınızı bağışlasın ve sizi adı konulmuş bir ecele kadar ertelesin. Elbette Allah\'ın eceli geldiği zaman, o ertelenmez. Bir bilmiş olsaydınız."'),
(5424, 71, 5, 'Dedi ki: "Rabbim, gerçekten kavmimi gece ve gündüz davet edip-durdum."'),
(5425, 71, 6, '"Fakat davet etmem, bir kaçıştan başkasını arttırmadı."'),
(5426, 71, 7, '"Doğrusu ben, onları bağışlaman için her davet edişimde, onlar parmaklarını kulaklarına tıkadılar, örtülerini başlarına çektiler ve büyüklük tasladıkça büyüklük gösterip-direttiler.\''),
(5427, 71, 8, '"Sonra onları açıktan açığa davet ettim."'),
(5428, 71, 9, '"Daha sonra (davamı) onlara açıkça ilan ettim ve kendilerine gizli gizli yollarla yanaşmak istedim."'),
(5429, 71, 10, '"Bundan böyle" dedim. "Rabbinizden mağfiret isteyin; çünkü gerçekten O, çok bağışlayandır.'),
(5430, 71, 11, '"(Öyle yapın ki,) Üzerinize gökten sağanak (bol miktarda yağmur) yağdırsın."'),
(5431, 71, 12, '"Size mallar ve çocuklarla yardımda bulunsun. Size (ürün yüklü) bağlar-bahçeler versin, ırmaklar da versin."'),
(5432, 71, 13, '"Size ne oluyor ki, Allah\'tan bir vakarı ummuyorsunuz?"'),
(5433, 71, 14, '"Oysa O, sizi gerçekten tavır tavır yaratmıştır."'),
(5434, 71, 15, '"Görmüyor musunuz; Allah, yedi göğü birbirleriyle bir uyum (mutabakat) içinde yaratmıştır?"'),
(5435, 71, 16, '"Ve ayı bunlar içinde bir nur kılmış, güneşi de (aydınlatıcı ve yakıcı) bir kandil yapmıştır."'),
(5436, 71, 17, '"Allah, sizi yerden bir bitki (gibi) bitirdi."'),
(5437, 71, 18, '"Sonra sizi yine oraya geri çevirecek ve sizi (diriltici) bir çıkarışla diriltip-çıkaracaktır."'),
(5438, 71, 19, '"Allah, yeri sizin için bir yaygı kıldı."'),
(5439, 71, 20, '"Öyle ki, onun içinde geniş yollarında gezip-dolaşırsınız, diye."'),
(5440, 71, 21, 'Nuh: "Rabbim, gerçekten onlar bana isyan ettiler; mal ve çocukları kendisine ziyandan başka bir şeyi artırmayan kimselere uydular."'),
(5441, 71, 22, '"Ve büyük büyük hileli-düzenler kurdular."'),
(5442, 71, 23, '"Ve dediler ki: Kendi ilahlarınızı bırakmayın; bırakmayın ne Vedd\'i, ne Suva\'ı, ne Yeğus\'u, ne Ye\'uk\'u ve ne de Nesr\'i."'),
(5443, 71, 24, '"Böylece onlar, çoğu kimseyi şaşırtıp-saptırdılar. Sen de o zalimlere sapıklıktan başkasını arttırma."'),
(5444, 71, 25, 'Bunlar, hataları dolayısıyla suda boğuldular, sonra ateşe sokuldular. O zaman da Allah\'ın dışında hiçbir yardımcı bulamadılar.'),
(5445, 71, 26, 'Nuh "Rabbim, yeryüzünde kafirlerden yurt edinen hiç kimseyi bırakma." dedi.'),
(5446, 71, 27, '"Çünkü Sen onları bırakacak olursan, Senin kullarını şaşırtıp-saptırırlar ve onlar, kötülükten sınırı aşan (facir\'den) kafirden başkasını doğurmazlar."'),
(5447, 71, 28, '"Rabbim, beni, annemi, babamı, mü\'min olarak evime gireni, iman eden erkekleri ve iman eden kadınları bağışla. Zalimlere yıkımdan başkasını arttırma."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'De ki: "Bana gerçekten şu vahyolundu: Cinlerden bir grup dinleyip de şöyle demişler: -Doğrusu biz, (büyük) hayranlık uyandıran bir Kur\'an dinledik"'),
(5449, 72, 2, '"O (Kur\'an), \'gerçeğe ve doğruya\' yöneltip-iletiyor. Bu yüzden ona iman ettik. Bundan böyle Rabbimiz\'e hiç kimseyi ortak koşmayacağız."'),
(5450, 72, 3, 'Elbette, Rabbimiz\'in şanı Yücedir. O, ne bir eş edinmiştir, ne de bir çocuk."'),
(5451, 72, 4, '"Doğrusu şu: Bizim beyinsizlerimiz, Allah\'a karşı \'bir sürü saçma şeyler\' söylemişler."'),
(5452, 72, 5, '"Oysa biz, insanların ve cinlerin Allah\'a karşı asla yalan söylemeyeceklerini sanmıştık."'),
(5453, 72, 6, '"Bir de şu gerçek var: İnsanlardan bazı adamlar, cinlerden bazı adamlara sığınırlardı. Öyle ki, onların azgınlıklarını arttırırlardı."'),
(5454, 72, 7, '"Ve onlar, sizin de sandığınız gibi Allah\'ın hiç kimseyi kesin olarak diriltmeyeceğini sanmışlardı."'),
(5455, 72, 8, '"Doğrusu biz göğü yokladık; fakat onu güçlü koruyucular ve şihablarla kaplı (doldurulmuş) bulduk."'),
(5456, 72, 9, '"Oysa gerçekte biz, dinlemek için onun oturma yerlerinde otururduk. Ama şimdi kim dinleyecek olsa, (hemen) kendisini izleyen bir şihab bulur."'),
(5457, 72, 10, '"Doğrusu bilmiyoruz; yeryüzünde olanlara bir kötülük mü istendi, yoksa Rableri kendileri için (doğruya iletici) bir hayır mı diledi?"'),
(5458, 72, 11, '"Gerçek şu ki, bizden salih olanlar vardır ve bunun dışında (ya da aşağısında) olanlar da. Biz türlü türlü yolların fırkaları olmuşuz."'),
(5459, 72, 12, '"Biz şüphesiz, Allah\'ı yeryüzünde asla aciz bırakamıyacağımızı, kaçmak suretiyle de O’nu hiçbir şekilde aciz bırakamıyacağımızı anladık."'),
(5460, 72, 13, '"Elbette biz, o yol gösterici (Kur\'an\'ı) işitince, ona iman ettik. Artık kim Rabbine iman ederse, o ne (ecrinin) eksileceğinden korkar ve ne de haksızlığa uğrayacağından."'),
(5461, 72, 14, '"Ve elbette bizden Müslüman olanlar da var, zulmedenler de. İşte (Allah\'a) teslim olanlar, artık onlar \'gerçeği ve doğruyu\' araştırıp-bulanlardır."'),
(5462, 72, 15, 'Zulmedenler ise, onlar da cehennem için odun olmuşlardır.'),
(5463, 72, 16, 'Eğer onlar (insanlar ve cinler), yol üzerinde \'dosdoğru bir istikamet tuttursalardı\', mutlaka Biz onlara bol miktarda su içirir (tükenmez bir rızık ve nimet verir)dik.'),
(5464, 72, 17, 'Ki, kendilerini bununla denemek için. Kim Rabbinin zikrinden yüz çevirirse, (Allah), onu \'gittikçe şiddeti artan\' bir azaba sürükler.'),
(5465, 72, 18, 'Şüphesiz mescidler, (yalnızca) Allah\'a aittir. Öyleyse, Allah ile beraber başka hiçbir şeye (ve kimseye) kulluk etmeyin (dua etmeyin, tapmayın).'),
(5466, 72, 19, 'Şu bir gerçek ki, Allah\'ın kulu (olan Muhammed,) O\'na dua (ibadet ve kulluk) için kalktığında, onlar (müşrikler,) neredeyse çevresinde keçeleşeceklerdi.'),
(5467, 72, 20, 'De ki: "Ben gerçekten, yalnızca Rabbime dua ediyorum ve O\'na hiç kimseyi (ve hiçbir şeyi) ortak koşmuyorum."'),
(5468, 72, 21, 'De ki: "Doğrusu ben, sizin için ne bir zarar, ne de bir yarar (irşad) sağlayabilirim."'),
(5469, 72, 22, 'De ki: "Muhakkak beni Allah\'tan (gelebilecek bir azaba karşı) hiç kimse asla kurtaramaz ve O\'nun dışında asla bir sığınak da bulamam."'),
(5470, 72, 23, '"(Benim görevim,) Yalnızca Allah\'tan olanı ve O\'nun gönderdiklerini tebliğ etmektir. Kim Allah\'a ve O\'nun elçisine isyan ederse, içinde ebedi kalıcılar olmak üzere onun için cehennem ateşi vardır."'),
(5471, 72, 24, 'Sonunda onlar, kendilerine vadedileni gördükleri zaman, yardımcı olmak bakımından kim daha zayıfmış ve sayı bakımından kim daha azmış artık öğrenmiş olacaklardır."'),
(5472, 72, 25, 'De ki: "Bilmiyorum, size vadedilen (kıyamet ve azap) yakın mı, yoksa Rabbim onun için uzun bir süre mi koymuştur?"'),
(5473, 72, 26, 'O, gaybı bilendir. Kendi gaybını (görülmez bilgi hazinesini) kimseye açık tutmaz (ona muttali kılmaz.)'),
(5474, 72, 27, 'Ancak elçileri (peygamberleri) içinde razı olduğu (seçtikleri kimseler) başka. Çünkü O, bunun önüne ve arkasına izleyici (gözetleyici)ler dizer.'),
(5475, 72, 28, 'Öyle ki onların, Rablerinden gelen risaleti (insanlara gönderilenleri) tebliğ ettiklerini bilsin. (Allah,) onların nezdinde olanları sarıp-kuşatmış ve herşeyi sayı olarak da sayıp-tespit etmiştir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Ey örtüsüne bürünen,'),
(5477, 73, 2, 'Az bir kısmı hariç olmak üzere, geceleyin kalk:'),
(5478, 73, 3, '(Gecenin) Yarısı kadar. Ya da ondan biraz eksilt.'),
(5479, 73, 4, 'Veya üzerine ilave et. Ve Kur\'an\'ı belli bir düzen içinde (tertil üzere) oku.'),
(5480, 73, 5, 'Gerçek şu ki, biz senin üzerine \'oldukça ağır\' bir söz (vahy) bırakacağız.'),
(5481, 73, 6, 'Doğrusu gece neşesi (gece ibadeti, insanın iç dünyasında uyandırdığı) etki bakımından daha kuvvetli, okumak bakımından daha sağlamdır.'),
(5482, 73, 7, 'Çünkü gündüz, senin için uzun uğraşılar vardır.'),
(5483, 73, 8, 'Rabbinin ismini zikret ve herşeyden kendini çekerek yalnızca O\'na yönel.'),
(5484, 73, 9, '(Allah,) Doğunun ve batının Rabbidir. O\'ndan başka İlah yoktur. Şu halde (yalnızca) O\'nu vekil tut.'),
(5485, 73, 10, 'Onların demelerine karşı sen sabret ve onlardan güzel bir ayrılma tarzıyla (düşünce ve eylem bakımından köklü bir tutum) ile kopup-ayrıl.'),
(5486, 73, 11, 'Yalanlamakta olan nimet (refah ve servet) sahiplerini sen Bana bırak ve onlara az bir süre tanı.'),
(5487, 73, 12, 'Çünkü Bizim yanımızda bukağılar ve cayır cayır yanan bir ateş vardır:'),
(5488, 73, 13, 'Boğazı tıkayıp kalan bir yemek ve acı bir azap vardır.'),
(5489, 73, 14, '(Öyle) Bir gün ki, yeryüzü ve dağlar titremeye-tutulur ve dağlar göçüveren bir kum yığını olur.'),
(5490, 73, 15, 'Şüphesiz size, üzerinize şahid olacak bir elçi gönderdik; Firavun\'a bir elçi gönderdiğimiz gibi.'),
(5491, 73, 16, 'Fakat Firavun elçiye isyan etti, Biz de onu pek vahim bir tarzda (azapla) yakalayıverdik.'),
(5492, 73, 17, 'Eğer inkar edecek olursanız, çocukların saçlarını ağartan bir günde kendinizi nasıl koruyacaksınız?'),
(5493, 73, 18, 'Bu nedenle gök bile yarılıp-çatlamıştır; (artık) O\'nun va\'di gerçekleştirilip-yerine getirilmiştir.'),
(5494, 73, 19, 'Şüphesiz, bu bir öğüttür. Artık dileyen Rabbine bir yol bulabilir.'),
(5495, 73, 20, 'Gerçekten Rabbin, senin gecenin üçte ikisinden biraz eksiğinde, yarısında ve üçte birinde (namaz için) kalktığını bilir; seninle birlikte olanlardan bir topluluğun da (böyle yaptığını bilir). Geceyi ve gündüzü Allah takdir eder. Sizin bunu sayamıyacağınızı bildi, böylece tevbenizi (O\'na dönüşünüzü) kabul etti. Şu halde Kur\'an\'dan kolay geleni okuyun. Allah sizden hastalar olduğunu, başkalarının Allah\'ın fazlından aramak için yeryüzünde gezip-dolaşacaklarını ve diğerlerinin Allah yolunda çarpışacaklarını bilmiştir. Öyleyse ondan (Kur\'an\'dan) kolay geleni okuyun. Namazı dosdoğru kılın, zekatı verin ve Allah\'a güzel bir borç verin. Hayır olarak kendi nefisleriniz için önceden takdim ettiğiniz şeyleri daha hayırlı ve daha büyük bir ecir (karşılık) olarak Allah Katında bulursunuz. Allah\'tan mağfiret dileyin. Şüphesiz Allah, çok bağışlayandır, çok esirgeyendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Ey bürünüp örtünen,'),
(5497, 74, 2, 'Kalk (ve) bundan böyle uyar.'),
(5498, 74, 3, 'Rabbini tekbir et (yücelt)'),
(5499, 74, 4, 'Elbiseni temizle.'),
(5500, 74, 5, 'Pislikten kaçınıp-uzaklaş.'),
(5501, 74, 6, 'Daha çok istekte bulunmak için iyilik yapma.'),
(5502, 74, 7, 'Rabbin için sabret.'),
(5503, 74, 8, 'Çünkü o boruya (sur\'a) üfürüldüğü zaman,'),
(5504, 74, 9, 'İşte o gün, zorlu bir gündür;'),
(5505, 74, 10, 'Kafirler içinse hiç kolay değildir.'),
(5506, 74, 11, 'Kendisini tek olarak (ve yapayalnız) yarattığım (şu adam)ı Bana bırak;'),
(5507, 74, 12, 'Ki Ben ona, \'alabildiğine geniş kapsamlı bir mal’ (servet) verdim.'),
(5508, 74, 13, 'Göz önünde-hazır çocuklar (verdim).'),
(5509, 74, 14, 'Ve sayısız imkan ve fırsatları önüne serdim.'),
(5510, 74, 15, 'Sonra, daha arttırmam için tamah eder (doyumsuz istekte bulunur).'),
(5511, 74, 16, 'Hayır; çünkü o, Bizim ayetlerimize karşı \'kesin bir inatçıdır."'),
(5512, 74, 17, 'Onu alabildiğine sarp bir yokuşa süreceğim.'),
(5513, 74, 18, 'Çünkü o, düşündü ve bir ölçü tespit etti.'),
(5514, 74, 19, 'Kahrolası, nasıl bir ölçü koydu?'),
(5515, 74, 20, 'Yine kahrolası, nasıl bir ölçü koydu?'),
(5516, 74, 21, 'Sonra bir baktı.'),
(5517, 74, 22, 'Sonra kaşlarını çattı ve yüzünü ekşitti.'),
(5518, 74, 23, 'Sonra da sırt çevirdi ve büyüklük tasladı (istikbar).'),
(5519, 74, 24, 'Böylece: "Bu, yalnızca \'aktarılarak öğrenilen\' bir büyüdür" dedi.'),
(5520, 74, 25, '"Bu, bir beşer sözünden başkası değildir."'),
(5521, 74, 26, 'Onu Ben, cehenneme sürükleyip-atacağım.'),
(5522, 74, 27, 'Cehennem (sakar) nedir, sen bilir misin?'),
(5523, 74, 28, 'Ne alıkoyar, ne bırakır.'),
(5524, 74, 29, 'Beşere delicesine susamıştır.'),
(5525, 74, 30, 'Onun üzerinde ondokuz vardır.'),
(5526, 74, 31, 'Biz o ateşin koruyucularını meleklerden başkasını kılmadık. Ve onların sayısını inkar edenler için yalnızca bir fitne (konusu) yaptık ki, kendilerine kitap verilenler, kesin bir bilgiyle inansın, iman edenlerin de imanları artsın; kendilerine kitap verilenler ve iman edenler (böylece) kuşkuya kapılmasın. Kalplerinde bir hastalık olanlar ile kafirler de şöyle desin: "Allah, bu örnekle neyi anlatmak istedi?" İşte Allah, dilediğini böyle şaşırtıp-saptırır, dilediğini böyle hidayete erdirir. Rabbinin ordularını Kendisi\'nden başka (hiç kimse) bilmez. Bu ise, beşer (insan) için yalnızca bir öğüttür.'),
(5527, 74, 32, 'Hayır; Ay\'a andolsun,'),
(5528, 74, 33, 'Dönüp gittiği zaman geceye,'),
(5529, 74, 34, 'Ağardığı zaman sabaha,'),
(5530, 74, 35, 'Gerçekten o, büyük (musibet)lerden biridir.'),
(5531, 74, 36, 'Beşer (insan) için bir uyarıdır.'),
(5532, 74, 37, 'Sizlerden öne geçmek veya geride kalmak isteyenler için.'),
(5533, 74, 38, 'Her nefis, kazandıklarına karşılık bir rehinedir.'),
(5534, 74, 39, 'Ancak Ashab-ı Yemin (sağ ehli) hariç.'),
(5535, 74, 40, 'Onlar cennetlerdedirler; birbirlerine sorarlar.'),
(5536, 74, 41, 'Suçlu-günahkarları;'),
(5537, 74, 42, '"Sizi şu cehenneme sürükleyip-iten nedir?"'),
(5538, 74, 43, 'Onlar: "Biz namaz kılanlardan değildik" dediler.'),
(5539, 74, 44, '"Yoksula yedirmezdik."'),
(5540, 74, 45, '"(Batıla ve tutkulara) Dalıp gidenlerle biz de dalar giderdik."'),
(5541, 74, 46, '"Din (hesap ve ceza) gününü yalan sayıyorduk."'),
(5542, 74, 47, '"Sonunda yakîn (kesin bir gerçek olan ölüm) gelip bize çattı."'),
(5543, 74, 48, 'Artık, şefaat edenlerin şefaati onlara bir yarar sağlamaz.'),
(5544, 74, 49, 'Buna rağmen, bunlara ne oluyor ki öğütten yüz çevirip duruyorlar?'),
(5545, 74, 50, 'Sanki onlar, ürkmüş yaban eşekleri gibidirler;'),
(5546, 74, 51, 'Arslandan korkup-kaçmışlar.'),
(5547, 74, 52, 'Hayır; her biri, kendisine açılmış sahifelerin verilmesini ister.'),
(5548, 74, 53, 'Hayır; onlar şüphesiz ahiretten korkmuyorlar.'),
(5549, 74, 54, 'Gerçek (şu ki), o (Kur\'an,) elbette bir öğüttür.'),
(5550, 74, 55, 'Artık kim dilerse, öğüt alıp-düşünür.'),
(5551, 74, 56, 'Allah dilemedikçe onlar öğüt almazlar; takvanın sahibi (onu kabul etmeye ehil olan) O\'dur, mağfiretin sahibi (bağışlamaya ehil olan da) O\'dur.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Hayır, kalkış (kıyamet) gününe and ederim.'),
(5553, 75, 2, 'Ve yine hayır; kendini kınayıp duran nefse de and ederim.'),
(5554, 75, 3, 'İnsan, onun kemiklerini Bizim kesin olarak biraraya getirmeyeceğimizi mi sanıyor?'),
(5555, 75, 4, 'Evet; onun parmak uçlarını dahi derleyip-(yeniden) düzene koymaya güç yetirenleriz.'),
(5556, 75, 5, 'Ancak insan, önündeki (sonsuz geleceği)ni de \'fücurla sürdürmek ister.\''),
(5557, 75, 6, '"Kıyamet günü ne zamanmış" diye sorar.'),
(5558, 75, 7, 'Ama göz \'kamaşıp da kaydığı,\''),
(5559, 75, 8, 'Ay karardığı,'),
(5560, 75, 9, 'Güneş ve ay birleştirildiği zaman;'),
(5561, 75, 10, 'İnsan o gün: "Kaçış nereye?" der.'),
(5562, 75, 11, 'Hayır, sığınacak herhangi bir yer yok.'),
(5563, 75, 12, 'O gün, \'sonunda varılıp karar kılınacak yer (müstakar)\' yalnızca Rabbinin katıdır.'),
(5564, 75, 13, 'İnsana o gün, önceden takdim ettikleri ve erteledikleri şeylerle haber verilir.'),
(5565, 75, 14, 'Hayır; insan, kendi nefsine karşı bir basirettir.'),
(5566, 75, 15, 'Kendi mazeretlerini ortaya atsa bile.'),
(5567, 75, 16, 'Onu (Kur\'an\'ı, kavrayıp belletmek için) aceleye kapılıp dilini onunla hareket ettirip-durma.'),
(5568, 75, 17, 'Şüphesiz, onu (kalbinde) toplamak ve onu (sana) okutmak Bize ait (bir iş)tir.'),
(5569, 75, 18, 'Şu halde, Biz onu okuduğumuz zaman, sen de onun okunuşunu izle.'),
(5570, 75, 19, 'Sonra muhakkak onu açıklamak Bize ait (bir iş)tir.'),
(5571, 75, 20, 'Hayır; siz çarçabuk geçmekte olanı (dünyayı) seviyorsunuz.'),
(5572, 75, 21, 'Ve ahireti terk edip-bırakıyorsunuz.'),
(5573, 75, 22, 'O gün yüzler ışıl ışıl parlar.'),
(5574, 75, 23, 'Rablerine bakıp-durur.'),
(5575, 75, 24, 'O gün, öyle yüzler vardır ki kararmış-ekşimiştir.'),
(5576, 75, 25, 'Kendisine, beli büken işlerin yapılacağını anlamaktadır.'),
(5577, 75, 26, 'Hayır; can, köprücük kemiğine gelip dayandığı zaman,'),
(5578, 75, 27, '"Son müdahaleyi yapacak kim" denir.'),
(5579, 75, 28, 'Artık gerçekten, kendisi de bir ayrılık olduğunu anlamıştır.'),
(5580, 75, 29, '(Ölüm korkusundan) Ayaklar birbirine dolaştığında;'),
(5581, 75, 30, 'O gün sevk, yalnızca Rabbinedir.'),
(5582, 75, 31, 'Fakat o, ne doğrulamış ne de namaz kılmıştı.'),
(5583, 75, 32, 'Ancak o, yalanlamış ve yüz çevirmişti.'),
(5584, 75, 33, 'Sonra çalım satarak yakınlarına gitmişti.'),
(5585, 75, 34, 'Sen buna müstahaksın, dahasına müstahaksın.'),
(5586, 75, 35, 'Yine müstahaksın, dahasına da müstahaksın.'),
(5587, 75, 36, 'İnsan, \'kendi başına ve sorumsuz\' bırakılacağını mı sanıyor?'),
(5588, 75, 37, 'Kendisi, akıtılan meniden bir damla su değil miydi?'),
(5589, 75, 38, 'Sonra bir alak (embriyo) oldu, derken (Allah, onu) yarattı ve bir \'düzen içinde biçim verdi.\''),
(5590, 75, 39, 'Böylece ondan, erkek ve dişi olmak üzere çift kıldı.'),
(5591, 75, 40, '(Öyleyse Allah,) Ölüleri diriltmeye güç yetiren değil midir?');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Gerçek şu ki, insanın üzerinden, daha kendisi anılmaya değer bir şey değilken, uzun zamanlardan (dehr) bir süre (hin) gelip-geçti.'),
(5593, 76, 2, 'Şüphesiz Biz insanı, karmaşık olan bir damla sudan yarattık. Onu deniyoruz. Bundan dolayı onu işiten ve gören yaptık.'),
(5594, 76, 3, 'Biz ona yolu gösterdik; (artık o,) ya şükredici olur ya da nankör.'),
(5595, 76, 4, 'Doğrusu Biz kafirlere zincirler, demir halkalar (tomruklar) ve çılgınca yanan bir ateş hazırladık.'),
(5596, 76, 5, 'Şüphesiz ki iyiler (ebrar), karışımı kafur olan bir kadehten içerler.'),
(5597, 76, 6, 'Allah\'ın kullarının kendisinden içtikleri bir kaynak; onu fışkırttıkça fışkırtıp akıtırlar.'),
(5598, 76, 7, 'Adaklarını yerine getirirler ve şerri (kötülüğü) yaygın olan bir günden korkarlar.'),
(5599, 76, 8, 'Kendileri, ona duydukları sevgiye rağmen yemeği, yoksula, yetime ve esire yedirirler.'),
(5600, 76, 9, '"Biz size, ancak Allah\'ın yüzü (rızası) için yediriyoruz; sizden ne bir karşılık istiyoruz, ne bir teşekkür."'),
(5601, 76, 10, '"Çünkü biz, asık suratlı, zorlu bir gün nedeniyle Rabbimiz\'den korkuyoruz."'),
(5602, 76, 11, 'Artık Allah, onları böyle bir günün şerrinden korumuş ve onlara parıltılı bir aydınlık ve bir sevinç vermiştir.'),
(5603, 76, 12, 'Ve sabretmeleri dolayısıyla cennetle ve ipekle ödüllendirmiştir.'),
(5604, 76, 13, 'Orada tahtlar üzerinde yaslanıp-dayanmışlardır. Orada ne (yakıcı) bir güneş ve ne de dondurucu bir soğuk görürler.'),
(5605, 76, 14, '(Meyvelerin) Gölgeleri onlara pek yakın ve devşirilmeleri kolaylaştırıldıkça kolaylaştırılmış.'),
(5606, 76, 15, 'Çevrelerinde gümüşten billur kablar, kupalar dolaştırılır.'),
(5607, 76, 16, 'Gümüşten billur kaplar ki, onları belli bir ölçüyle tespit etmişlerdir.'),
(5608, 76, 17, 'Orada onlara bir kadeh içirilir ki, karışımı zencefildir.'),
(5609, 76, 18, 'Bir pınar ki orada "selsebil" olarak adlandırılır.'),
(5610, 76, 19, 'Çevrelerinde (gençlikleri ve dinçlikleri) ebedi kılınmış civanlar dolaşır-durur; sen onları gördüğün zaman saçılmış birer inci sanırsın.'),
(5611, 76, 20, 'Her nereye baksan, bir nimet ve büyük bir mülk görürsün.'),
(5612, 76, 21, 'Onların üzerinde hafif ipek ve ağır işlenmiş atlastan yeşil elbiseler vardır. Gümüşten bileziklerle bezenmişlerdir. Rableri onlara tertemiz bir şarab içirmiştir.'),
(5613, 76, 22, 'Şüphesiz, bu, sizin için bir mükafaattır. Sizin çaba-harcamanız şükre değer (meşkur: makbul) görülmüştür.'),
(5614, 76, 23, 'Gerçek şu ki, Kur\'an\'ı senin üzerine \'safhalar halinde bir indirme tarzıyla (tenzil)’ indiren Biziz, Biz.'),
(5615, 76, 24, 'Öyleyse, Rabbinin hükmüne sabır göster. Onlardan günahkar veya nankör olana itaat etme.'),
(5616, 76, 25, 'Ve sabah, akşam Rabbinin adını zikret.'),
(5617, 76, 26, 'Gecenin bir bölümünde O\'na secde et ve geceleyin uzun uzadıya O\'nu tesbih et.'),
(5618, 76, 27, 'Gerçek şu ki bunlar, çarçabuk geçmekte olan (dünyay)ı seviyorlar. Önlerinde bulunan ağır bir günü bırakıyorlar.'),
(5619, 76, 28, 'Onları Biz yarattık ve bağlarını sımsıkı bağladık. Dilediğimiz zaman da onları benzerleriyle değiştiririz.'),
(5620, 76, 29, 'Şüphesiz, bu bir öğüttür. Artık dileyen Rabbine bir yol bulabilir.'),
(5621, 76, 30, 'Allah dilemedikçe siz dileyemezsiniz. Gerçekten Allah, bilendir, hüküm ve hikmet sahibidir.'),
(5622, 76, 31, 'Dilediğini Kendi rahmetine sokar. Zalimlere ise, onlar için acı bir azap hazırlamıştır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Birbiri ardınca gönderilenlere andolsun;'),
(5624, 77, 2, 'Derken kökünden koparıp savuranlara.'),
(5625, 77, 3, 'Yaydıkça yayanlara.'),
(5626, 77, 4, 'Böylece ayırdıkça ayıranlara,'),
(5627, 77, 5, 'Zikr (vahy, öğüt) bırakanlara;'),
(5628, 77, 6, 'Özür (suçu, eksikliği ortadan kaldırmak) veya uyarmak için.'),
(5629, 77, 7, 'Şüphesiz, size vaadedilen gerçekleşecektir.'),
(5630, 77, 8, 'Yıldızlar \'örtülüp (ışıkları) silindiği\' zaman,'),
(5631, 77, 9, 'Gök yarıldığı zaman'),
(5632, 77, 10, 'Dağlar, kökünden sökülüp savurulduğu zaman,'),
(5633, 77, 11, 'Ve resuller de (şahitlik için) belli bir vakitte getirildiği zaman'),
(5634, 77, 12, '(Bu,) Hangi gün için ertelenmişti?'),
(5635, 77, 13, '(Mü\'mini müşrikten, haklıyı haksızdan) Ayırma günü için.'),
(5636, 77, 14, 'Bu ayırma gününü sana ne bildirdi?'),
(5637, 77, 15, 'O gün, yalanlayanların vay haline.'),
(5638, 77, 16, 'Biz, öncekileri helak etmedik mi?'),
(5639, 77, 17, 'Sonra arkadan gelenleri onların izinde yürüteceğiz.'),
(5640, 77, 18, 'İşte Biz, suçlu-günahkarlara böyle yapıyoruz.'),
(5641, 77, 19, 'O gün, yalanlayanların vay haline.'),
(5642, 77, 20, 'Sizi basbayağı bir sudan yaratmadık mı?'),
(5643, 77, 21, 'Sonra onu savunması sağlam bir karar yerine yerleştirdik.'),
(5644, 77, 22, 'Belli bir süreye kadar;'),
(5645, 77, 23, 'İşte (buna) güç yetirdik. Demek ki, Biz ne güzel güç yetirenleriz.'),
(5646, 77, 24, 'O gün, yalanlayanların vay haline.'),
(5647, 77, 25, 'Biz yeryüzünü bir toplanma yeri kılmadık mı?'),
(5648, 77, 26, 'Dirilere ve ölülere.'),
(5649, 77, 27, 'Ve onda sabit yüksek dağlar var etmedik mi? Size tatlı bir su içirmedik mi?'),
(5650, 77, 28, 'O gün, yalanlayanların vay haline.'),
(5651, 77, 29, 'Kendisini yalanladığınız (azab)a gidin.'),
(5652, 77, 30, 'Üç dala ayrılmış bir gölgeye gidin.'),
(5653, 77, 31, 'Ne gölge altında barındırır, ne (yakıcı) alevden korur.'),
(5654, 77, 32, 'Gerçekten o, sanki her biri saray olan bir kıvılcım saçar.'),
(5655, 77, 33, 'Her biri, sanki sapsarı erkek deve sürüleri gibidir.'),
(5656, 77, 34, 'O gün, yalanlayanların vay haline.'),
(5657, 77, 35, 'Bu, onların konuşamayacakları bir gündür.'),
(5658, 77, 36, 'Ve onlara özür beyan etmeleri için izin verilmez.'),
(5659, 77, 37, 'O gün, yalanlayanların vay haline.'),
(5660, 77, 38, 'Bu, hüküm günüdür; sizi ve öncekileri \'birarada topladık.\''),
(5661, 77, 39, 'Şayet kurabileceğiniz hileli bir düzeniniz varsa, durmaksızın bana karşı kurun.'),
(5662, 77, 40, 'O gün, yalanlayanların vay haline.'),
(5663, 77, 41, 'Şüphesiz muttaki olanlar, gölgeliklerde ve pınar-başlarındadır;'),
(5664, 77, 42, 'Ve canlarının çekip-arzu ettiği meyveler (arasındadırlar).'),
(5665, 77, 43, 'Yaptıklarınıza karşılık olmak üzere, afiyetle yiyin ve için.'),
(5666, 77, 44, 'Elbette Biz, \'iyi ve güzel\' davrananları işte böyle ödüllendiririz.'),
(5667, 77, 45, 'O gün, yalanlayanların vay haline.'),
(5668, 77, 46, '(Sizler de dünyada) Yiyin ve biraz yararlanın. Çünkü siz, suçlu-günahkar kimselersiniz.'),
(5669, 77, 47, 'O gün, yalanlayanların vay haline.'),
(5670, 77, 48, 'Onlara: "Rüku edin" denildiği zaman, rüku etmezler.'),
(5671, 77, 49, 'O gün, yalanlayanların vay haline.'),
(5672, 77, 50, 'Artık onlar, bundan sonra hangi söze inanacaklar?');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Birbirlerine hangi şeyi sorup duruyorlar?'),
(5674, 78, 2, 'O büyük haberi mi?'),
(5675, 78, 3, 'Ki kendileri hakkında anlaşmazlık içindedirler.'),
(5676, 78, 4, 'Hayır; yakında bileceklerdir.'),
(5677, 78, 5, 'Yine hayır; yakında bileceklerdir.'),
(5678, 78, 6, 'Biz, yeryüzünü bir döşek kılmadık mı?'),
(5679, 78, 7, 'Dağları da birer kazık?'),
(5680, 78, 8, 'Sizi çift çift yarattık.'),
(5681, 78, 9, 'Uykunuzu bir dinlenme yaptık.'),
(5682, 78, 10, 'Geceyi bir örtü yaptık.'),
(5683, 78, 11, 'Gündüzü bir geçim-vakti kıldık.'),
(5684, 78, 12, 'Sizin üstünüze sapasağlam yedi-gök bina ettik.'),
(5685, 78, 13, 'Parıldadıkça parıldayan bir kandil (güneş) kıldık.'),
(5686, 78, 14, 'Sıkıp suyu çıkaran (bulut)lardan \'bardaktan boşanırcasına su\' indirdik.'),
(5687, 78, 15, 'Bununla taneler ve bitkiler bitirip-çıkaralım diye.'),
(5688, 78, 16, 'Ve birbirine sarmaş-dolaş bahçeleri de.'),
(5689, 78, 17, 'Şüphesiz o hüküm (fasl) günü, belirlenmiş bir vakittir.'),
(5690, 78, 18, 'Sur\'a üfürüleceği gün, artık siz dalga dalga geleceksiniz.'),
(5691, 78, 19, 'O sırada gök açılmış ve kapı kapı olmuştur.'),
(5692, 78, 20, 'Dağlar yürütülmüş, artık bir serab oluvermiştir.'),
(5693, 78, 21, 'Gerçekten cehennem, bir gözetleme yeridir.'),
(5694, 78, 22, 'Taşkınlık edip-azanlar için son bir varış yeridir.'),
(5695, 78, 23, 'Bütün zamanlar boyunca içinde kalacaklardır.'),
(5696, 78, 24, 'Orada ne serinlik tadacaklar, ne bir içecek.'),
(5697, 78, 25, 'Kaynar sudan ve irinden başka.'),
(5698, 78, 26, '(İşlediklerine) Uygun olan bir ceza olarak,'),
(5699, 78, 27, 'Doğrusu onlar, hesaba çekileceklerini ummuyorlardı.'),
(5700, 78, 28, 'Bizim ayetlerimizi yalanlayabildikleri kadar yalanlıyorlardı.'),
(5701, 78, 29, 'Oysa Biz, herşeyi yazıp saymışızdır.'),
(5702, 78, 30, 'Şimdi tadın. Size artık azaptan başkasını arttırmayacağız;'),
(5703, 78, 31, 'Gerçek şu ki, muttakiler için \'bir kurtuluş ve mutluluk\' vardır.'),
(5704, 78, 32, 'Nice bahçeler ve üzüm bağları.'),
(5705, 78, 33, 'Göğüsleri henüz tomurcuklanmış yaşıt kızlar.'),
(5706, 78, 34, 'Dopdolu kadehler.'),
(5707, 78, 35, 'İçinde, ne \'boş ve saçma bir söz\' işitirler, ne bir yalan.'),
(5708, 78, 36, 'Rabbinden bir karşılık olmak üzere yeterli bir bağış(tır bu).'),
(5709, 78, 37, 'Göklerin, yerin ve ikisi arasında bulunanların Rabbi Rahman olan (Allah); O’na hitap etmeye güç yetiremezler.'),
(5710, 78, 38, 'Ruh ve meleklerin saflar halinde duracakları gün; Rahman\'ın kendilerine izin verdikleri dışında olanlar konuşmazlar. (Konuşacak olan da,) Doğruyu söyleyecektir.'),
(5711, 78, 39, 'İşte bu, hak gündür. Şu halde dileyen Rabbine bir dönüşyolu edinsin.'),
(5712, 78, 40, 'Gerçekten Biz sizi yakın bir azab ile uyardık. Kişinin kendi ellerinin önceden takdim ettiklerine bakacağı gün, kafir olan da: "Ah, keşke ben bir toprak oluverseydim" diyecek.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Ta en derinden acıyla sökerek çıkaranlara andolsun.'),
(5714, 79, 2, 'Yumuşacık çekip alanlara,'),
(5715, 79, 3, 'Yüzdükçe yüzerek gidenlere,'),
(5716, 79, 4, 'Öncü olarak yarışıp geçenlere,'),
(5717, 79, 5, 'Derken işi bir düzen içinde evirip çevirenlere,'),
(5718, 79, 6, 'O sarsıntının sarsacağı gün,'),
(5719, 79, 7, 'Arkasından onu diğer bir sarsıntı izleyecek.'),
(5720, 79, 8, 'O gün yürekler (dehşet içinde) hoplayacak.'),
(5721, 79, 9, 'Gözler zillet içinde düşecek.'),
(5722, 79, 10, 'Derler ki: "Biz çukurda iken, gerçekten biz mi yeniden (diriltilip) döndürüleceğiz?"'),
(5723, 79, 11, '"Biz çürüyüp dağılmış kemikler olduğumuz zaman mı?"'),
(5724, 79, 12, 'Derler ki: "Şu durumda, zararına bir dönüştür bu."'),
(5725, 79, 13, 'Oysa bu, yalnızca tek bir haykırıştır.'),
(5726, 79, 14, 'Bir de bakarsın ki, onlar, yerin üstündedirler.'),
(5727, 79, 15, 'Musa\'nın haberi sana geldi mi?'),
(5728, 79, 16, 'Hani Rabbi ona, kutsal vadi Tuva\'da seslenmişti:'),
(5729, 79, 17, '"Firavun\'a git; çünkü o, azdı."'),
(5730, 79, 18, 'Ona de ki: “Temizlenmek ister misin?"'),
(5731, 79, 19, '"Seni Rabbine yönelteyim, böylece (O\'ndan) korkmuş olursun."'),
(5732, 79, 20, '(Musa) Ona büyük mucizeyi gösterdi.'),
(5733, 79, 21, 'Fakat o, yalanladı ve isyan etti.'),
(5734, 79, 22, 'Sonra (karşı yönde) çaba harcayıp sırtını döndü.'),
(5735, 79, 23, 'Sonunda (yardımcı güçlerini) topladı, seslendi;'),
(5736, 79, 24, 'Dedi ki: "Sizin en yüce Rabbiniz benim."'),
(5737, 79, 25, 'Böylelikle Allah onu, ahiret ve dünya azabıyla yakaladı.'),
(5738, 79, 26, 'Gerçekten bunda \'içi titreyerek korkacak\' kimse için elbette bir ibret (ders) vardır.'),
(5739, 79, 27, 'Yaratmak bakımından siz mi daha güçsünüz yoksa gök mü? (Allah) Onu bina etti.'),
(5740, 79, 28, 'Boyunu yükseltti, ona belli bir düzen verdi.'),
(5741, 79, 29, 'Gecesini kararttı, kuşluğunu açığa-çıkardı.'),
(5742, 79, 30, 'Bundan sonra yeryüzünü serip döşedi.'),
(5743, 79, 31, 'Ondan da suyunu ve otlağını çıkardı.'),
(5744, 79, 32, 'Dağlarını dikip-oturttu;'),
(5745, 79, 33, 'Size ve hayvanlarınıza bir yarar (meta) olmak üzere.'),
(5746, 79, 34, 'Ancak o, \'herşeyi batırıp gömen büyük-felaket’ (kıyamet) geldiği zaman.'),
(5747, 79, 35, 'O gün, insan, neye çaba harcadığını düşünüp-anlar.'),
(5748, 79, 36, 'Görebilenler için cehennem de sergilenmiştir.'),
(5749, 79, 37, 'Artık kim taşkınlık edip-azarsa,'),
(5750, 79, 38, 'Ve dünya hayatını seçerse,'),
(5751, 79, 39, 'Şüphesiz cehennem, (onun için) bir barınma yeridir.'),
(5752, 79, 40, 'Kim Rabbinin makamından korkar ve nefsi heva (istek ve tutkular) dan sakındırırsa,'),
(5753, 79, 41, 'Artık şüphesiz cennet, (onun için) bir barınma yeridir.'),
(5754, 79, 42, '"O ne zaman demir atacak?" diye, sana kıyamet-saatini soruyorlar.'),
(5755, 79, 43, 'Onunla ilgili bilgi vermekten yana, sende ne var ki…'),
(5756, 79, 44, 'En sonunda o (ve onunla ilgili bilgi), Rabbine aittir.'),
(5757, 79, 45, 'Sen, yalnızca ondan \'içi titreyerek korkanlar\' için bir uyarıcısın.'),
(5758, 79, 46, 'Onu gördükleri gün, sanki, bir akşam veya bir kuşluk-vaktinden başkasını yaşamamış gibidirler.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Surat astı ve yüz çevirdi;'),
(5760, 80, 2, 'Kendisine o kör geldi diye.'),
(5761, 80, 3, 'Nerden biliyorsun; belki o, temizlenip-arınacak?'),
(5762, 80, 4, 'Veya öğüt alacak; böylelikle bu öğüt kendisine yarar sağlayacak.'),
(5763, 80, 5, 'Fakat kendini müstağni gören (hiçbir şeye ihtiyacı olmadığını sanan) ise,'),
(5764, 80, 6, 'İşte sen, onda \'yankı uyandırmaya’ çalışıyorsun.'),
(5765, 80, 7, 'Oysa, onun temizlenip-arınmasından sana ne?'),
(5766, 80, 8, 'Ama koşarak sana gelen ise,'),
(5767, 80, 9, 'Ki o, \'içi titreyerek korkar\' bir durumdadır;'),
(5768, 80, 10, 'Sen ona aldırış etmeden oyalanıyorsun.'),
(5769, 80, 11, 'Hayır; çünkü o (Kur\'an), bir öğüttür.'),
(5770, 80, 12, 'Artık dileyen, onu \'düşünüp-öğüt alsın.\''),
(5771, 80, 13, 'O (Kur\'an), \'şerefli-üstün\' sahifelerdedir.'),
(5772, 80, 14, 'Yüceltilmiş, tertemiz (mutahhar) kılınmış.'),
(5773, 80, 15, 'Katiplerin ellerinde.'),
(5774, 80, 16, '(Ki onlar,) Üstün değerli, \'iyilik ve dürüstlük sembolü.\''),
(5775, 80, 17, 'Kahrolası insan, ne kadar nankördür.'),
(5776, 80, 18, '(Allah) Onu hangi şeyden yarattı?'),
(5777, 80, 19, 'Bir damla sudan yarattı da onu \'bir ölçüyle biçime soktu.\''),
(5778, 80, 20, 'Sonra ona yolu kolaylaştırdı.'),
(5779, 80, 21, 'Sonra onu öldürdü, böylece kabre gömdürdü.'),
(5780, 80, 22, 'Sonra dilediği zaman onu diriltir.'),
(5781, 80, 23, 'Hayır; ona (Allah\'ın) emrettiğini yerine getirmedi.'),
(5782, 80, 24, 'Bir de insan, yediğine bir bakıversin;'),
(5783, 80, 25, 'Biz şüphesiz, suyu akıttıkça akıttık,'),
(5784, 80, 26, 'Sonra yeri yardıkça yardık;'),
(5785, 80, 27, 'Böylece onda taneler bitirdik,'),
(5786, 80, 28, 'Üzümler, yoncalar,'),
(5787, 80, 29, 'Zeytinler, hurmalar,'),
(5788, 80, 30, 'Boyları birbiriyle yarışan ve içiçe girmiş ağaçlı bahçeler.'),
(5789, 80, 31, 'Meyveler ve otlaklıklar,'),
(5790, 80, 32, 'Size ve hayvanlarınıza bir yarar (meta) olmak üzere.'),
(5791, 80, 33, 'Fakat \'kulakları patlatırcasına olan o gürleme\' geldiği zaman,'),
(5792, 80, 34, 'Kişi o gün, kendi kardeşinden kaçar;'),
(5793, 80, 35, 'Annesinden ve babasından,'),
(5794, 80, 36, 'Eşinden ve çocuklarından,'),
(5795, 80, 37, 'O gün, onlardan her birisinin kendine yetecek bir işi vardır.'),
(5796, 80, 38, 'O gün, öyle yüzler vardır ki apaydınlıktır;'),
(5797, 80, 39, 'Güler ve sevinç içindedir.'),
(5798, 80, 40, 'Ve o gün, öyle yüzler de vardır ki üzerini toz bürümüştür.'),
(5799, 80, 41, 'Bir karartı sarıp-kaplamıştır.'),
(5800, 80, 42, 'İşte onlar da, kafir, facir olanlardır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Güneş, köreltildiği zaman,'),
(5802, 81, 2, 'Yıldızlar, bulanıklaşıp-döküldüğü zaman,'),
(5803, 81, 3, 'Dağlar, yürütüldüğü zaman,'),
(5804, 81, 4, 'Gebe develer, kendi başına terk edildiği zaman,'),
(5805, 81, 5, 'Vahşi-hayvanlar, toplandığı zaman,'),
(5806, 81, 6, 'Denizler, tutuşturulduğu zaman,'),
(5807, 81, 7, 'Nefisler, birleştiği zaman,'),
(5808, 81, 8, 'Ve \'diri diri toprağa gömülen kızcağıza\' sorulduğu zaman:'),
(5809, 81, 9, '"Hangi suçtan dolayı öldürüldü?"'),
(5810, 81, 10, 'Sahifeler (amel defterleri) açıldığı zaman,'),
(5811, 81, 11, 'Gök, sıyrılıp-yüzüldüğü zaman'),
(5812, 81, 12, 'Cehennem ateşi çılgınca kızıştırıldığı zaman,'),
(5813, 81, 13, 'Cennet de yakınlaştırıldığı zaman,'),
(5814, 81, 14, '(Artık her) Nefis, neyi hazırladığını bilip-öğrenmiştir.'),
(5815, 81, 15, 'Artık hayır; yemin ederim (gündüz) sinip (gece) dönen (gezegen)lere,'),
(5816, 81, 16, 'Bir akış içinde yerini alanlara;'),
(5817, 81, 17, 'Kararmaya ilk başladığı zaman, geceye andolsun,'),
(5818, 81, 18, 'Ve nefes almaya başladığı zaman, sabaha;'),
(5819, 81, 19, 'Şüphesiz o (Kur\'an), üstün onur sahibi bir elçinin gerçekten (Allah\'tan getirdiği) sözüdür;'),
(5820, 81, 20, '(Bu elçi,) Bir güç sahibidir, arşın sahibi Katında şereflidir.'),
(5821, 81, 21, 'Ona itaat edilir, sonra güvenilirdir.'),
(5822, 81, 22, 'Sizin sahibiniz bir deli değildir.'),
(5823, 81, 23, 'Andolsun o (peygamber), onu apaçık bir ufukta görmüştür.'),
(5824, 81, 24, 'O, gayb (haberlerin)e karşı (söylediklerinden dolayı) suçlanamaz (ya da cimrilikte bulunup kıskançlık yapmaz.)'),
(5825, 81, 25, 'O (Kur\'an) da kovulmuş şeytanın sözü değildir.'),
(5826, 81, 26, 'Şu halde, siz nereye kaçıp-gidiyorsunuz?'),
(5827, 81, 27, 'O (Kur\'an), alemler için yalnızca bir zikirdir;'),
(5828, 81, 28, 'Sizden dosdoğru bir yön (istikamet) tutturmak dileyenler için.'),
(5829, 81, 29, 'Alemlerin Rabbi olan Allah dilemedikçe siz dileyemezsiniz.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Gök, çatlayıp-yarıldığı zaman,'),
(5831, 82, 2, 'Yıldızlar, dağılıp-yayıldığı zaman,'),
(5832, 82, 3, 'Denizler, fışkırtılıp-taşırıldığı zaman,'),
(5833, 82, 4, 'Ve kabirlerin içi \'deşilip dışa atıldığı\' zaman;'),
(5834, 82, 5, '(Artık her) Nefis önceden takdim ettiklerini ve ertelediklerini bilip-öğrenmiştir.'),
(5835, 82, 6, 'Ey insan, \'üstün kerem sahibi\' olan Rabbine karşı seni aldatıp-yanıltan nedir?'),
(5836, 82, 7, 'Ki O, seni yarattı, \'sana bir düzen içinde biçim verdi\' ve seni bir itidal üzere kıldı.'),
(5837, 82, 8, 'Dilediği bir surette seni tertib etti.'),
(5838, 82, 9, 'Asla, hayır; siz dini yalanlıyorsunuz;'),
(5839, 82, 10, 'Oysa gerçekten sizin üzerinizde koruyucular var,'),
(5840, 82, 11, '\'Şerefli-üstün\' yazıcılar.'),
(5841, 82, 12, 'Her yapmakta olduğunuzu bilirler.'),
(5842, 82, 13, 'Şüphesiz ebrar olanlar, elbette nimetler(le donatılmış cennetler) içindedirler.'),
(5843, 82, 14, 'Ve şüphesiz facir (kötü) olanlar da, elbette çılgınca yanan ateşin içindedirler.'),
(5844, 82, 15, 'Onlar, din günü oraya yollanırlar.'),
(5845, 82, 16, 'Ve ondan ayrılıp-kaybolacak değildirler.'),
(5846, 82, 17, 'Din gününü sana bildiren şey nedir?'),
(5847, 82, 18, 'Ve yine din gününü sana bildiren şey nedir?'),
(5848, 82, 19, 'Hiçbir nefsin bir başka nefse herhangi bir şeye güç yetiremeyeceği gündür; o gün emir yalnızca Allah\'ındır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Eksik ölçüp tartanların vay haline,'),
(5850, 83, 2, 'Ki onlar, insanlardan ölçerek aldıklarında noksansız alırlar.'),
(5851, 83, 3, 'Kendileri onlara ölçtüklerinde veya tarttıklarında eksiltirler.'),
(5852, 83, 4, 'Yoksa onlar, diriltileceklerini sanmıyor mu?'),
(5853, 83, 5, 'Büyük bir günde.'),
(5854, 83, 6, 'İnsanların, alemlerin Rabbi için kalkacağı günde.'),
(5855, 83, 7, 'Hayır; facir olanların kitabı şüphesiz "Siccîn" dedir.'),
(5856, 83, 8, '"Siccîn"in ne olduğunu sana öğreten nedir?'),
(5857, 83, 9, 'Yazılı bir kitaptır.'),
(5858, 83, 10, 'O gün, yalanlayanların vay haline.'),
(5859, 83, 11, 'Ki onlar, din gününü yalanlıyorlar.'),
(5860, 83, 12, 'Oysa onu, \'sınır tanımaz, saldırgan\', günahkar olandan başkası yalanlamaz.'),
(5861, 83, 13, 'Ona ayetlerimiz okunduğu zaman: "Geçmişlerin masallarıdır" dedi.'),
(5862, 83, 14, 'Asla, hayır; onların kazandıkları, kalpleri üzerinde pas tutmuştur.'),
(5863, 83, 15, 'Hayır; gerçekten onlar, Rablerinden perdelenerek-yoksun tutulmuşlardır.'),
(5864, 83, 16, 'Sonra onlar, kuşkusuz cehenneme yollanacaklardır.'),
(5865, 83, 17, 'Sonra onlara: "İşte sizin yalanladığınız (şey) budur" denir.'),
(5866, 83, 18, 'Hayır; ebrar olanların kitabı, "İlliyîn"dedir.'),
(5867, 83, 19, '"İlliyîn"in ne olduğunu sana öğreten nedir?'),
(5868, 83, 20, 'Yazılı bir kitaptır.'),
(5869, 83, 21, 'Ona yakınlaştırılmış (mukarreb) olanlar şahid olurlar.'),
(5870, 83, 22, 'Gerçek şu ki, ebrar olanlar, elbette nimetler içindedirler.'),
(5871, 83, 23, 'Tahtlar üzerinde bakıp-seyretmektedirler.'),
(5872, 83, 24, 'Nimetin parıltılı-sevincini sen onların yüzlerinde tanırsın.'),
(5873, 83, 25, 'Onlara mühürlü, katıksız bir şaraptan içirilir.'),
(5874, 83, 26, 'Ki onun sonu misktir. Şu halde yarışmak isteyenler, bunun için yarışsınlar.'),
(5875, 83, 27, 'Onun karışımı "tesnim"dendir.'),
(5876, 83, 28, 'Bir kaynak ki, yakınlaştırılmış (mukarreb) olanlar ondan içer.'),
(5877, 83, 29, 'Doğrusu, \'suç ve günah işleyenler,\' kimi iman edenlere gülüp-geçerlerdi.'),
(5878, 83, 30, 'Yanlarına vardıkları zaman, birbirlerine kaş-göz ederlerdi.'),
(5879, 83, 31, 'Kendi yakınlarına döndükleri zaman neşeyle dönerlerdi.'),
(5880, 83, 32, 'Onları gördükleri zaman ise: "Bunlar elbette şaşkın-sapıklardır" derlerdi.'),
(5881, 83, 33, 'Oysa kendileri onların üzerine gözcü olarak gönderilmemişlerdi.'),
(5882, 83, 34, 'Artık bugün, iman edenler, kafir olanlara gülmektedirler.'),
(5883, 83, 35, 'Tahtlar üzerinde bakıp-seyretmek suretiyle.'),
(5884, 83, 36, 'Nasıl, kafir olanlar, işlediklerinin \'feci karşılığını gördüler mi?\'');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Gök, yarılıp-parçalandığı,'),
(5886, 84, 2, 'Ve \'kendi yaratılışına uygun\' Rabbine boyun eğdiği zaman;'),
(5887, 84, 3, 'Yer, düzlendiği,'),
(5888, 84, 4, 'İçinde olanları dışa atıp boşaldığı,'),
(5889, 84, 5, 'Ve \'kendi yaratılışına uygun Rabbine boyun eğdiği zaman.'),
(5890, 84, 6, 'Ey insan, gerçekten sen, hiç durmaksızın Rabbine doğru bir çaba harcayıp durmaktasın; sonunda O\'na varacaksın.'),
(5891, 84, 7, 'Artık kimin kitabı sağ yanından verilirse,'),
(5892, 84, 8, 'O, kolay bir hesap (sorgu) ile sorguya çekilecek,'),
(5893, 84, 9, 'Ve kendi yakınlarına sevinç içinde dönmüş olacaktır.'),
(5894, 84, 10, 'Kimin de kitabı ardından verilirse,'),
(5895, 84, 11, 'O da, helak (yok olmay)ı çağıracak,'),
(5896, 84, 12, 'Çılgın alevli ateşe girecek.'),
(5897, 84, 13, 'Çünkü o, (dünyada) kendi yakınları arasında sevinçliydi.'),
(5898, 84, 14, 'Doğrusu o, (Rabbine) bir daha dönmeyeceğini sanmıştı.'),
(5899, 84, 15, 'Hayır; gerçekten Rabbi, kendisini çok iyi görendi.'),
(5900, 84, 16, 'Yoo, şafak-vaktine yemin ederim,'),
(5901, 84, 17, 'Geceye ve toplayıp-taşıdığı şeylere,'),
(5902, 84, 18, 'Ondördüne girdiği zaman aya;'),
(5903, 84, 19, 'Siz, gerçekten tabakadan tabakaya bineceksiniz.'),
(5904, 84, 20, 'Şu halde onlara ne oluyor ki iman etmiyorlar?'),
(5905, 84, 21, 'Kendilerine Kur\'an okunduğunda secde etmiyorlar.'),
(5906, 84, 22, 'Tersine, o nankörler, yalanlıyorlar.'),
(5907, 84, 23, 'Oysa Allah, onların içlerinde sakladıklarını daha iyi bilendir.'),
(5908, 84, 24, 'Bu durumda sen, onlara acı bir azap ile müjde ver.'),
(5909, 84, 25, 'Ancak iman edip salih amellerde bulunanlar başka; onlar için kesintisi olmayan bir ecir (mükafaat) vardır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Burçları olan göğe andolsun,'),
(5911, 85, 2, 'O vadedilen güne,'),
(5912, 85, 3, 'Şahid olana (görene) ve şahit olunana (görülene).'),
(5913, 85, 4, 'Kahrolsun Ashab-ı Uhdud'),
(5914, 85, 5, '\'Tutuşturucu-yakıt dolu o ateş,\''),
(5915, 85, 6, 'Hani kendileri (ateş hendeğinin) çevresinde oturmuşlardı.'),
(5916, 85, 7, 'Ve mü\'minlere yaptıklarını seyrediyorlardı.'),
(5917, 85, 8, 'Onlardan, yalnızca \'üstün ve güçlü olan,\' öğülen Allah\'a iman ettiklerinden dolayı intikam alıyorlardı.'),
(5918, 85, 9, 'Ki O (Allah), göklerin ve yerin mülkü O\'nundur. Allah, herşeyin üzerinde şahid olandır.'),
(5919, 85, 10, 'Gerçek şu ki, mü\'min erkeklerle mü\'min kadınlara işkence (fitne) uygulayanlar, sonra tevbe etmeyenler; işte onlar için, cehennem azabı vardır ve yakıcı azap onlaradır.'),
(5920, 85, 11, 'Şüphesiz iman edip salih amellerde bulunanlara gelince; onlar için altından ırmaklar akan cennetler vardır. İşte büyük \'kurtuluş ve mutluluk\' budur.'),
(5921, 85, 12, 'Doğrusu, Rabbinin \'zorlu yakalayışı\' şiddetlidir.'),
(5922, 85, 13, 'Çünkü O, ilkin var eden, (sonra dirilterek) döndürecek olandır.'),
(5923, 85, 14, 'O, çok bağışlayandır, çok sevendir.'),
(5924, 85, 15, 'Arşın sahibidir; Mecid (pek Yüce)dir.'),
(5925, 85, 16, 'Her dilediğini yapıp-gerçekleştirendir.'),
(5926, 85, 17, 'Orduların haberi sana geldi mi?'),
(5927, 85, 18, 'Firavun ve Semud (ordularının)?'),
(5928, 85, 19, 'Hayır; inkar edenler, (kesintisiz) bir yalanlama içindedirler.'),
(5929, 85, 20, 'Allah ise, onları arkalarından sarıp-kuşatmıştır.'),
(5930, 85, 21, 'Hayır; o (Kitap), \'şerefli-üstün\' olan bir Kur\'an\'dır;'),
(5931, 85, 22, 'Levh-i Mahfuz\'dadır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Göğe ve Tarık\'a andolsun,'),
(5933, 86, 2, 'Tarık\'ın ne olduğunu sana bildiren nedir?'),
(5934, 86, 3, '(Karanlığı) Delen yıldızdır.'),
(5935, 86, 4, 'Üzerinde gözetleyici-koruyucu bulunmayan hiçbir nefis (kimse) yoktur.'),
(5936, 86, 5, 'İnsan bir baksın, hangi şeyden yaratıldı?'),
(5937, 86, 6, 'Dökülüp atılan bir sudan yaratıldı.'),
(5938, 86, 7, '(Bu su,) Bel kemiği ile kaburgalar arasında(ki organlar)dan çıkar.'),
(5939, 86, 8, 'Şüphesiz (Allah), onu yeniden-döndürmeye güç yetirendir.'),
(5940, 86, 9, 'Sırların orta yere çıkarılacağı gün;'),
(5941, 86, 10, 'Artık onun ne gücü vardır, ne yardımcısı.'),
(5942, 86, 11, 'Dönüşlü olan göğe andolsun.'),
(5943, 86, 12, 'Yarılan yere de.'),
(5944, 86, 13, 'Şüphesiz o (Kur\'an), ayırdeden bir sözdür.'),
(5945, 86, 14, 'O, bir şaka değildir.'),
(5946, 86, 15, 'Doğrusu onlar, hileli bir düzen planlayıp kuruyorlar;'),
(5947, 86, 16, 'Ben de bir düzen kurup hazırlıyorum.'),
(5948, 86, 17, 'Sen kafirlere bir mühlet ver, az bir süre tanı.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Rabbinin Yüce ismini tesbih et,'),
(5950, 87, 2, 'Ki O, yarattı, \'bir düzen içinde biçim verdi\','),
(5951, 87, 3, 'Takdir etti, böylece yol gösterdi,'),
(5952, 87, 4, '\'Yemyeşil-otlağı\' çıkardı.'),
(5953, 87, 5, 'Ardından onu kuru, kara bir duruma soktu.'),
(5954, 87, 6, 'Sana okutacağız, sen de unutmayacaksın.'),
(5955, 87, 7, 'Ancak Allah\'ın dilediği başka. Çünkü O, açıkta olanı da bilir, saklı duranı da.'),
(5956, 87, 8, 'Ve seni kolay olan için başarılı kılacağız.'),
(5957, 87, 9, 'Şu halde, eğer \'öğüt ve hatırlatma\' bir yarar sağlayacaksa, \'öğüt verip hatırlat.\''),
(5958, 87, 10, 'Allah\'tan ‘İçi titreyerek korkan’ öğüt alır-düşünür.'),
(5959, 87, 11, '\'Mutsuz-bedbaht\' olan ondan kaçınır.'),
(5960, 87, 12, 'Ki o, en büyük ateşe yollanacaktır.'),
(5961, 87, 13, 'Sonra onun içinde o, ne ölür, ne yaşar.'),
(5962, 87, 14, 'Doğrusu, temizlenip arınan felah bulmuştur;'),
(5963, 87, 15, 'Ve Rabbinin ismini zikredip namaz kılan.'),
(5964, 87, 16, 'Hayır siz, dünya hayatını seçip üstün tutuyorsunuz.'),
(5965, 87, 17, 'Ahiret ise daha hayırlı ve daha süreklidir.'),
(5966, 87, 18, 'Şüphesiz bu, önceki sahifelerde vardır;'),
(5967, 87, 19, 'İbrahim\'in ve Musa\'nın sahifelerinde.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, '(Her yanı yaygın olarak kuşatacak olan) Kıyametin haberi sana geldi mi?'),
(5969, 88, 2, 'O gün, öyle yüzler vardır ki, \'zillet içinde aşağılanmıştır.\''),
(5970, 88, 3, 'Çalışmış, boşuna yorulmuştur.'),
(5971, 88, 4, 'Kızgın bir ateşe yollanırlar.'),
(5972, 88, 5, 'Kaynar bir kaynaktan içirilirler.'),
(5973, 88, 6, 'Onlar için (zehirli olan) dari\' dikeninden başka bir yiyecek yoktur.'),
(5974, 88, 7, 'Ne doyurup-semirtir, ne açlıktan korur.'),
(5975, 88, 8, 'O gün, öyle yüzler de vardır ki, nimette (engin bir mutluluk içinde)dirler.'),
(5976, 88, 9, 'Harcadığı-çabadan dolayı hoşnuttur.'),
(5977, 88, 10, 'Yüksek bir cennettedir.'),
(5978, 88, 11, 'Orda anlamsız bir söz işitmez.'),
(5979, 88, 12, 'Orda \'durmaksızın akan\' bir kaynak vardır.'),
(5980, 88, 13, 'Orda \'yükseklerde kurulmuş, tahtlar da vardır;'),
(5981, 88, 14, 'Konulmuş (içecek dolu) kaplar,'),
(5982, 88, 15, 'Dizi dizi yastıklar,'),
(5983, 88, 16, 'Ve serilmiş yaygılar.'),
(5984, 88, 17, 'Bakmıyorlar mı o deveye; nasıl yaratıldı?'),
(5985, 88, 18, 'Göğe, nasıl yükseltildi?'),
(5986, 88, 19, 'Dağlara; nasıl oturtulup-kuruldu?'),
(5987, 88, 20, 'Yere; nasıl yayılıp-döşendi?'),
(5988, 88, 21, 'Artık sen, öğüt verip-hatırlat. Sen, yalnızca bir öğüt verici-bir hatırlatıcısın.'),
(5989, 88, 22, 'Onlara \'zor ve baskı\' kullanacak değilsin.'),
(5990, 88, 23, 'Ancak kim yüz çevirir ve inkar ederse'),
(5991, 88, 24, 'Allah, onu en büyük azap ile azaplandırır.'),
(5992, 88, 25, 'Şüphesiz onların dönüşleri Bizedir.'),
(5993, 88, 26, 'Sonra onları hesaba çekmek de elbette Bize aittir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Fecre andolsun,'),
(5995, 89, 2, 'On geceye,'),
(5996, 89, 3, 'Çifte ve tek\'e,'),
(5997, 89, 4, 'Akıp-gittiği zaman geceye,'),
(5998, 89, 5, 'Bunlarda, akıl sahibi olan için bir yemin var, değil mi?'),
(5999, 89, 6, 'Rabbinin Ad (kavmin)e ne yaptığını görmedin mi?'),
(6000, 89, 7, '\'Yüksek sütunlar\' sahibi İrem\'e?'),
(6001, 89, 8, 'Ki şehirler içinde onun bir benzeri yaratılmış değildi.'),
(6002, 89, 9, 'Ve vadilerde kayaları oyup biçen Semud\'a?'),
(6003, 89, 10, 'Ve kazıklar (ehramlar) sahibi Firavun\'a?'),
(6004, 89, 11, 'Ki onlar, şehirlerde azgınlaşmışlardı.'),
(6005, 89, 12, 'Böylece oralarda fesadı yaygınlaştırmış-arttırmışlardı.\''),
(6006, 89, 13, 'Bundan dolayı, Rabbin, onların üzerine bir azap kamçısı çarpıverdi.'),
(6007, 89, 14, 'Çünkü senin Rabbin, gerçekten gözetleme yerindedir.'),
(6008, 89, 15, 'Fakat insan; ne zaman Rabbi kendisini bir denemeden geçirse, ona bir keremde bulunsa, nimetler verse: "Rabbim bana ikram etti" der.'),
(6009, 89, 16, 'Ama ne zaman onu deneyerek, rızkını kıssa, hemen: "Rabbim bana ihanet etti" der.'),
(6010, 89, 17, 'Hayır; aksine, siz yetime ikram etmiyorsunuz.'),
(6011, 89, 18, 'Yoksula yedirmek için birbirinizi teşvik etmiyorsunuz.'),
(6012, 89, 19, 'Mirası, sınır tanımaz (helal, haram aldırmaz) bir tarzda yiyorsunuz.'),
(6013, 89, 20, 'Malı \'bir yığma tutkusu ve hırsıyla\' seviyorsunuz.'),
(6014, 89, 21, 'Hayır; yer, parça parça yıkılıp darmadağın olduğu,'),
(6015, 89, 22, 'Rabbin(in buyruğu) geldiği ve melekler dizi dizi durduğu zaman;'),
(6016, 89, 23, 'O gün, cehennem de getirilmiştir. İnsan o gün düşünüp-hatırlar, ancak (bu) hatırlamadan ona ne fayda?'),
(6017, 89, 24, 'Der ki: "Keşke hayatım için, (önceden bir şeyler) takdim edebilseydim."'),
(6018, 89, 25, 'Artık o gün hiç kimse (Allah\'ın) vereceği azap gibi azaplandıramaz.'),
(6019, 89, 26, 'Onun vuracağı bağı hiç kimse vuramaz.'),
(6020, 89, 27, 'Ey mutmain (tatmin bulmuş) nefis,'),
(6021, 89, 28, 'Rabbine, hoşnut edici ve hoşnut edilmiş olarak dön.'),
(6022, 89, 29, 'Artık kullarımın arasına gir.'),
(6023, 89, 30, 'Cennetime gir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Hayır; bu şehre yemin ederim,'),
(6025, 90, 2, 'Ki sen, bu şehirde oturmakta iken,'),
(6026, 90, 3, 'Babaya ve doğan-çocuğa da.'),
(6027, 90, 4, 'Andolsun, Biz insanı bir zorluk içinde yarattık.'),
(6028, 90, 5, 'O, hiç kimsenin kendisine asla güç yetiremeyeceğini mi sanıyor?'),
(6029, 90, 6, 'O: "Yığınla mal tüketip-yok ettim" diyor.'),
(6030, 90, 7, 'Kendisini hiç kimsenin görmediğini mi sanıyor?'),
(6031, 90, 8, 'Biz ona iki göz vermedik mi?'),
(6032, 90, 9, 'Bir dil ve iki dudak?'),
(6033, 90, 10, 'Biz ona \'iki yol-iki amaç\' gösterdik.'),
(6034, 90, 11, 'Ancak o, sarp yokuşa göğüs germedi.'),
(6035, 90, 12, 'Sarp yokuşun ne olduğunu sana öğreten nedir?'),
(6036, 90, 13, 'Bir boynu çözmek (bir köleye özgürlük vermek)tir;'),
(6037, 90, 14, 'Ya da açlık gününde doyurmaktır,'),
(6038, 90, 15, 'Yakın olan bir yetimi,'),
(6039, 90, 16, 'Veya sürünen bir yoksulu.'),
(6040, 90, 17, 'Sonra iman edenlerden, sabrı birbirlerine tavsiye edenlerden, merhameti birbirlerine tavsiye edenlerden olmak.'),
(6041, 90, 18, 'İşte bunlar, sağ yanın adamlarıdır (Ashab-ı Meymene).'),
(6042, 90, 19, 'Ayetlerimizi inkar edenler ise, sol yanın adamlarıdır (Ashab-ı Meş\'eme).'),
(6043, 90, 20, '"Kapıları kilitlenmiş" bir ateş onların üzerinedir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Güneş\'e ve onun parıltısına andolsun,'),
(6045, 91, 2, 'Onu izlediği zaman Ay\'a,'),
(6046, 91, 3, 'Onu (Güneş) parıldattığı zaman gündüze,'),
(6047, 91, 4, 'Onu sarıp-örttüğü zaman geceye,'),
(6048, 91, 5, 'Göğe ve onu bina edene,'),
(6049, 91, 6, 'Yere ve onu yayıp döşeyene,'),
(6050, 91, 7, 'Nefse ve ona \'bir düzen içinde biçim verene\','),
(6051, 91, 8, 'Sonra ona fücurunu (sınır tanımaz günah ve kötülüğünü) ve ondan sakınmayı ilham edene (andolsun).'),
(6052, 91, 9, 'Onu arındırıp-temizleyen gerçekten felah bulmuştur.'),
(6053, 91, 10, 'Ve onu (isyanla, günahla, bozulmalarla) örtüp-saran da elbette yıkıma uğramıştır.'),
(6054, 91, 11, 'Semud (halkı) azgınlığı dolayısıyla yalanladı;'),
(6055, 91, 12, 'En \'zorlu bedbahtları\' ayaklandığında,'),
(6056, 91, 13, 'Allah\'ın elçisi onlara dedi ki: "Allah\'ın (deneme için size gönderdiği) devesine ve onun su içme-sırasına dikkat edin."'),
(6057, 91, 14, 'Fakat, onu yalanladılar, deveyi yere yıkıp öldürdüler. Rableri de günahları dolayısıyla \'onları yerle bir etti, kırıp geçirdi\'; orasını da dümdüz etti.'),
(6058, 91, 15, '(Allah, asla) Bunun sonucundan korkmaz.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Sarıp-örttüğü zaman geceye andolsun,'),
(6060, 92, 2, 'Parıldayıp-aydınlandığı zaman gündüze,'),
(6061, 92, 3, 'Erkeği ve dişiyi yaratana;'),
(6062, 92, 4, 'Gerçekten sizin çabalarınız (çelişkili, parça parça) darmadağınıktır.'),
(6063, 92, 5, 'Fakat kim verir ve korkup-sakınırsa,'),
(6064, 92, 6, 'Ve en güzel olanı doğrularsa,'),
(6065, 92, 7, 'Biz de onu kolay olan için başarılı kılacağız.'),
(6066, 92, 8, 'Kim de cimrilik eder, kendini müstağni görürse,'),
(6067, 92, 9, 'Ve en güzel olanı yalan sayarsa,'),
(6068, 92, 10, 'Biz de ona en zorlu olanı (azaba uğramasını) kolaylaştıracağız.'),
(6069, 92, 11, 'Tereddi edeceği (başaşağı düşüşe uğrayacağı) zaman, malı ona hiç yarar sağlamaz.'),
(6070, 92, 12, 'Şüphesiz, Bize ait olan, yol göstermektir.'),
(6071, 92, 13, 'Gerçekten, son da, ilk de (ahiret ve dünya) Bizimdir.'),
(6072, 92, 14, 'Artık sizi, \'alevleri kabardıkça kabaran\' bir ateşle uyardım.'),
(6073, 92, 15, 'Ona, ancak en bedbaht olandan başkası yollanmaz;'),
(6074, 92, 16, 'Ki o, yalanlamış ve yüz çevirmişti.'),
(6075, 92, 17, 'Sakınan ise, ondan uzak tutulacaktır.'),
(6076, 92, 18, 'Ki o, malını vererek temizlenip-arınır.'),
(6077, 92, 19, 'Onun yanında hiç kimsenin karşılığı verilecek bir nimeti (borcu) yoktur.'),
(6078, 92, 20, 'Ancak Yüce Rabbinin rızasını aramak için (verir).'),
(6079, 92, 21, 'Muhakkak kendisi de ileride razı olacaktır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Kuşluk vaktine andolsun,'),
(6081, 93, 2, '\'Karanlığı iyice çöktüğü\' zaman geceye,'),
(6082, 93, 3, 'Rabbin seni terk etmedi ve darılmadı.'),
(6083, 93, 4, 'Şüphesiz senin için son olan, ilk olandan (ahiret dünyadan) daha hayırlıdır.'),
(6084, 93, 5, 'Elbette Rabbin sana verecek, böylece sen hoşnut kalacaksın.'),
(6085, 93, 6, 'Bir yetim iken, seni bulup da barındırmadı mı?'),
(6086, 93, 7, 'Ve seni yol bilmez iken, \'doğru yola yöneltip iletmedi mi?'),
(6087, 93, 8, 'Bir yoksul iken seni bulup zengin etmedi mi?'),
(6088, 93, 9, 'Öyleyse, sakın yetimi üzüp-kahretme.'),
(6089, 93, 10, 'İsteyip-dileneni azarlayıp-çıkışma.'),
(6090, 93, 11, 'Rabbinin nimetini durmaksızın anlat.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Biz, senin göğsünü yarıp-genişletmedik mi?'),
(6092, 94, 2, 'Ve yükünü indirip-atmadık mı?'),
(6093, 94, 3, 'Ki o, senin belini bükmüştü;'),
(6094, 94, 4, 'Senin zikrini (şanını) yüceltmedik mi?'),
(6095, 94, 5, 'Demek ki, gerçekten zorlukla beraber kolaylık vardır.'),
(6096, 94, 6, 'Gerçekten güçlükle beraber kolaylık vardır.'),
(6097, 94, 7, 'Şu halde boş kaldığın zaman, durmaksızın (dua ve ibadetle) yorulmaya-devam et.'),
(6098, 94, 8, 'Ve yalnızca Rabbine rağbet et.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'İncire ve zeytine andolsun,'),
(6100, 95, 2, 'Sina dağına,'),
(6101, 95, 3, 'Ve şu emin beldeye (güvenilir şehre).'),
(6102, 95, 4, 'Doğrusu, Biz insanı en güzel bir biçimde yarattık.'),
(6103, 95, 5, 'Sonra aşağıların aşağısına çevirdik.'),
(6104, 95, 6, 'Ancak iman edip salih amellerde bulunanlar başka; onlar için kesintisi olmayan bir ecir vardır.'),
(6105, 95, 7, 'Öyleyse bundan sonra, hangi şey sana dini yalanlatabilir?'),
(6106, 95, 8, 'Allah hükmedenlerin hakimi değil midir?');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Yaratan Rabbin adıyla oku.'),
(6108, 96, 2, 'O, insanı bir alak\'tan yarattı.'),
(6109, 96, 3, 'Oku, Rabbin en büyük kerem sahibidir;'),
(6110, 96, 4, 'Ki O, kalemle (yazmayı) öğretendir.'),
(6111, 96, 5, 'İnsana bilmediğini öğretti.'),
(6112, 96, 6, 'Hayır; gerçekten insan, azar.'),
(6113, 96, 7, 'Kendini müstağni gördüğünden.'),
(6114, 96, 8, 'Şüphesiz, dönüş yalnızca Rabbinedir.'),
(6115, 96, 9, 'Engellemekte olanı gördün mü?'),
(6116, 96, 10, 'Namaz kıldığı zaman bir kulu.'),
(6117, 96, 11, 'Gördün mü? Ya o (kul) doğru yol üzerinde ise,'),
(6118, 96, 12, 'Ya da takvayı emrettiyse.'),
(6119, 96, 13, 'Gördün mü? Ya (bu engellemek isteyen) yalanlıyor ve yüz çeviriyor ise.'),
(6120, 96, 14, 'O, Allah\'ın gördüğünü bilmiyor mu?'),
(6121, 96, 15, 'Hayır; eğer o, (bu tutumuna) bir son vermeyecek olursa, andolsun, onu perçeminden tutup sürükleyeceğiz;'),
(6122, 96, 16, 'O yalancı, günahkar olan alnından.'),
(6123, 96, 17, 'O zaman da meclisini (yakın çevresini ve yandaşlarını) çağırsın.'),
(6124, 96, 18, 'Biz de zebanileri çağıracağız.'),
(6125, 96, 19, 'Hayır; ona boyun eğme (Rabbine) Secde et ve yakınlaş.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Gerçek şu ki, Biz onu kadir gecesinde indirdik.'),
(6127, 97, 2, 'Kadir gecesinin ne olduğunu sana bildiren nedir?'),
(6128, 97, 3, 'Kadir gecesi, bin aydan daha hayırlıdır.'),
(6129, 97, 4, 'Melekler ve ruh, onda Rablerinin izniyle her bir iş için inerler.'),
(6130, 97, 5, 'Fecrin çıkışına kadar bir esenliktir (selamdır) o.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Kitap Ehlinden ve müşriklerden inkar edenler, kendilerine apaçık bir delil gelinceye kadar, (bulundukları durumdan) kopup-ayrılacak değillerdi.'),
(6132, 98, 2, '(O delil de) Allah\'tan gönderilmiş-bir elçi (ki,) tertemiz sahifeleri okumaktadır;'),
(6133, 98, 3, 'Onların içinde dosdoğru \'yazılı-hükümler\' vardır.'),
(6134, 98, 4, 'Kitap Ehlinden olanlar, ancak kendilerine apaçık belgeler geldikten sonra fırkalara ayrıldılar.'),
(6135, 98, 5, 'Oysa onlar, dini yalnızca O\'na halis kılan hanifler (Allah\'ı birleyenler) olarak sadece Allah\'a kulluk etmek, namazı dosdoğru kılmak ve zekatı vermekten başkasıyla emrolunmadılar. İşte en doğru (dimdik ve sapasağlam) din budur.'),
(6136, 98, 6, 'Şüphesiz, Kitap Ehlinden ve müşriklerden inkar edenler, içinde sürekli kalıcılar olmak üzere cehennem ateşindedirler. İşte onlar, yaratılmışların en kötüleridir.'),
(6137, 98, 7, 'İman edip salih amellerde bulunanlar ise; işte onlar da, yaratılmışların en hayırlılarıdır.'),
(6138, 98, 8, 'Rableri Katında onların ödülleri, içinde ebedi kalıcılar olmak üzere altından ırmaklar akan Adn cennetleridir. Allah, onlardan razı olmuştur, kendileri de O\'ndan razı (hoşnut, memnun) kalmışlardır. İşte bu, Rabbinden \'içi titreyerek korku duyan kimse\' içindir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Yer, o şiddetli sarsıntısıyla sarsıldığı,'),
(6140, 99, 2, 'Yer, ağırlıklarını dışa atıp-çıkardığı,'),
(6141, 99, 3, 'Ve insan: "Buna ne oluyor?" dediği zaman;'),
(6142, 99, 4, 'O gün (yer), haberlerini anlatacaktır.'),
(6143, 99, 5, 'Çünkü senin Rabbin, ona vahyetmiştir.'),
(6144, 99, 6, 'O gün insanlar, amelleri kendilerine gösterilsin diye, bölük bölük fırlayıp-çıkarlar.'),
(6145, 99, 7, 'Artık kim zerre ağırlığınca hayır işlerse, onu görür.'),
(6146, 99, 8, 'Artık kim zerre ağırlığınca bir şer (kötülük) işlerse, onu görür.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Soluk soluğa koşan (at)lara andolsun,'),
(6148, 100, 2, '(Tırnaklarıyla) Ateş saçanlara,'),
(6149, 100, 3, 'Sabah vakti baskın yapanlara.'),
(6150, 100, 4, 'Derken, orada tozu dumana katanlara,'),
(6151, 100, 5, 'Bununla bir (düşman) topluluğun orta yerine kadar dalanlara.'),
(6152, 100, 6, 'Gerçekten insan, Rabbine karşı nankördür.'),
(6153, 100, 7, 'Ve gerçekten, kendisi buna şahiddir.'),
(6154, 100, 8, 'Muhakkak o, mal sevgisinden dolayı (bencil ve cimri tutumundan) çok katıdır.'),
(6155, 100, 9, 'Yine de bilmeyecek mi? Kabirlerde olanların \'deşilip dışa atıldığı,\''),
(6156, 100, 10, 'Göğüslerde olanların derlenip-devşirildiği zamanı?'),
(6157, 100, 11, 'Şüphesiz, o gün Rableri, kendilerinden gerçekten haberdardır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Kaari’a...'),
(6159, 101, 2, 'Nedir kaari’a?'),
(6160, 101, 3, 'Sana o kaaria’yı bildiren nedir?'),
(6161, 101, 4, 'İnsanların, \'her yana dağılmış\' pervaneler gibi olacakları gün,'),
(6162, 101, 5, 'Ve dağların \'etrafa saçılmış\' renkli yünler gibi olacakları (gün),'),
(6163, 101, 6, 'İşte, kimin tartıları ağır basarsa,'),
(6164, 101, 7, 'Artık o, hoşnut olunan bir hayat içindedir.'),
(6165, 101, 8, 'Kimin tartıları hafif kalırsa,'),
(6166, 101, 9, 'Artık onun da anası (son durağı) "haviye"dir (uçurum).'),
(6167, 101, 10, 'Onun ne olduğunu (mahiyetini) sana bildiren nedir?'),
(6168, 101, 11, 'O, kızgın bir ateştir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, '(Mal, mülk ve servette) Çoklukla övünmek, sizi \'tutkuyla oyalayıp, kendinizden geçirdi.\''),
(6170, 102, 2, '"Öyle ki (bu,) mezarı ziyaretinize (kabre gidişinize, ölümünüze) kadar sürdü."'),
(6171, 102, 3, 'Hayır; ileride bileceksiniz.'),
(6172, 102, 4, 'Yine hayır; ileride bileceksiniz.'),
(6173, 102, 5, 'Hayır; eğer siz kesin bir bilgiyle bilmiş olsaydınız,'),
(6174, 102, 6, 'Andolsun, o çılgınca yanan ateşi de elbette görecektiniz.'),
(6175, 102, 7, 'Sonra onu, gerçekten yakîn gözüyle (Ayne\'l Yakîn) görmüş olacaksınız.'),
(6176, 102, 8, 'Sonra o gün, nimetten sorguya çekileceksiniz.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Asra andolsun;'),
(6178, 103, 2, 'Gerçekten insan, ziyandadır.'),
(6179, 103, 3, 'Ancak iman edip salih amellerde bulunanlar, birbirlerine hakkı tavsiye edenler ve birbirlerine sabrı tavsiye edenler başka.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Arkadan çekiştirip duran, kaş göz hareketleriyle alay eden her kişinin vay haline;'),
(6181, 104, 2, 'Ki o, mal yığıp biriktiren ve onu saydıkça sayandır.'),
(6182, 104, 3, 'Gerçekten malının kendisini ebedi kılacağını sanıyor.'),
(6183, 104, 4, 'Hayır; andolsun o, \'hutame\'ye atılacaktır.'),
(6184, 104, 5, '"Hutame"nin ne olduğunu sana bildiren nedir?'),
(6185, 104, 6, 'Allah\'ın tutuşturulmuş ateşidir.'),
(6186, 104, 7, 'Ki o, yüreklerin üstüne tırmanıp çıkar.'),
(6187, 104, 8, 'O, onların üzerine kilitlenecektir;'),
(6188, 104, 9, '(Kendileri de) Dikilip-yükseltilmiş sütunlarda (bağlanacaklardır).');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Rabbinin fil sahiplerine neler yaptığını görmedin mi?'),
(6190, 105, 2, 'Onların \'tasarladıkları planlarını\' boşa çıkarmadı mı?'),
(6191, 105, 3, 'Onların üzerine ebabil (sürü sürü) kuşlarını gönderdi.'),
(6192, 105, 4, 'Onlara \'pişirilip-sertleştirilmiş balçık taşları\' atıyorlardı.'),
(6193, 105, 5, 'Sonunda onları, yenik ekin yaprağı gibi kıldı.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, '(Hiç değilse kendilerini) Kureyş’i ‘bir araya getirip anlaştırdığı’'),
(6195, 106, 2, 'Yaz ve kış yolculuğunda onları (güvenliğe kavuşturduğu ya da başkalarıyla) ısındırıp yakınlaştırdığı için,'),
(6196, 106, 3, 'Şu Ev (Ka’be’n)in Rabbine kulluk etsinler;'),
(6197, 106, 4, 'Ki O, kendilerini açlıktan (kurtarıp) doyuran ve korkudan güvenliğe kavuşturandır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Dini yalanlayanı gördün mü?'),
(6199, 107, 2, 'İşte yetimi itip-kakan;'),
(6200, 107, 3, 'Yoksulu doyurmayı teşvik etmeyen odur.'),
(6201, 107, 4, 'İşte (şu) namaz kılanların vay haline,'),
(6202, 107, 5, 'Ki onlar, namazlarında yanılgıdadırlar,'),
(6203, 107, 6, 'Onlar gösteriş yapmaktadırlar'),
(6204, 107, 7, 'Ve ‘ufacık bir yardımı (veya zekatı) da engellemektedirler.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Şüphesiz, Biz sana Kevser\'i verdik.'),
(6206, 108, 2, 'Şu halde Rabbin için namaz kıl ve kurban kes.'),
(6207, 108, 3, 'Doğrusu, asıl ebter (soyu kesik) olan sana kin duyandır.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'De ki: "Ey kafirler."'),
(6209, 109, 2, '"Ben sizin taptıklarınıza tapmam."'),
(6210, 109, 3, '"Benim taptığıma siz tapacak değilsiniz."'),
(6211, 109, 4, '"Ben de sizin taptıklarınıza tapacak değilim."'),
(6212, 109, 5, '"Siz de benim taptığıma tapacak değilsiniz."'),
(6213, 109, 6, '"Sizin dininiz size, benim dinim bana."');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Allah\'ın yardımı ve fetih geldiği zaman,'),
(6215, 110, 2, 'Ve insanların Allah\'ın dinine dalga dalga girdiklerini gördüğünde,'),
(6216, 110, 3, 'Hemen Rabbini hamd ile tesbih et ve O\'ndan mağfiret dile. Çünkü O, tevbeleri çok kabul edendir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Ebu Leheb\'in iki eli kurusun; kurudu ya.'),
(6218, 111, 2, 'Malı ve kazandıkları kendisine bir yarar sağlamadı.'),
(6219, 111, 3, 'Alevi olan bir ateşe girecektir.'),
(6220, 111, 4, 'Eşi de; odun hamalı (ve)'),
(6221, 111, 5, 'Boynuna bükülmüş bir ip (bağlanmış) olarak.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'De ki: O Allah, birdir.'),
(6223, 112, 2, 'Allah, Samed\'dir (herşey O\'na muhtaçtır, daimdir, hiçbir şeye ihtiyacı olmayandır).'),
(6224, 112, 3, 'O, doğurmamıştır ve doğurulmamıştır.'),
(6225, 112, 4, 'Ve hiçbir şey O\'nun dengi değildir.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'De ki: Sabahın Rabbine sığınırım.'),
(6227, 113, 2, 'Yarattığı şeylerin şerrinden,'),
(6228, 113, 3, 'Karanlığı çöktüğü zaman gecenin şerrinden,'),
(6229, 113, 4, 'Düğümlere üfüren-kadınların şerrinden,'),
(6230, 113, 5, 'Ve hased ettiği zaman, hasetçinin şerrinden.');
INSERT INTO `tr_bulac` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'De ki: İnsanların Rabbine sığınırım.'),
(6232, 114, 2, 'İnsanların malikine,'),
(6233, 114, 3, 'İnsanların (gerçek) İlahına;'),
(6234, 114, 4, '\'Sinsice, kalplere vesvese ve şüphe düşürüp duran\' vesvesecinin şerrinden.'),
(6235, 114, 5, 'Ki o, insanların göğüslerine vesvese verir (içlerine kuşku, kuruntu fısıldar);'),
(6236, 114, 6, 'Gerek cinlerden, gerekse insanlardan (olan her hannas\'tan Allah\'a sığınırım).');
