INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'bismi-llâhi-rraḥmâni-rraḥîm.'),
(2, 1, 2, 'elḥamdü lillâhi rabbi-l`âlemîn.'),
(3, 1, 3, 'erraḥmâni-rraḥîm.'),
(4, 1, 4, 'mâliki yevmi-ddîn.'),
(5, 1, 5, 'iyyâke na`büdü veiyyâke neste`în.'),
(6, 1, 6, 'ihdine-ṣṣirâṭa-lmüsteḳîm.'),
(7, 1, 7, 'ṣirâṭa-lleẕîne en`amte `aleyhim gayri-lmagḍûbi `aleyhim vele-ḍḍâllîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'elif-lâm-mîm.'),
(9, 2, 2, 'ẕâlike-lkitâbü lâ raybe fîh. hüdel lilmütteḳîn.'),
(10, 2, 3, 'elleẕîne yü\'minûne bilgaybi veyüḳîmûne-ṣṣalâte vemimmâ razaḳnâhüm yünfiḳûn.'),
(11, 2, 4, 'velleẕîne yü\'minûne bimâ ünzile ileyke vemâ ünzile min ḳablik. vebil\'âḫirati hüm yûḳinûn.'),
(12, 2, 5, 'ülâike `alâ hüdem mir rabbihim veülâike hümü-lmüfliḥûn.'),
(13, 2, 6, 'inne-lleẕîne keferû sevâün `aleyhim eenẕertehüm em lem tünẕirhüm lâ yü\'minûn.'),
(14, 2, 7, 'ḫateme-llâhü `alâ ḳulûbihim ve`alâ sem`ihim. ve`alâ ebṣârihim gişâveh. velehüm `aẕâbün `ażîm.'),
(15, 2, 8, 'vemine-nnâsi mey yeḳûlü âmennâ billâhi vebilyevmi-l\'âḫiri vemâ hüm bimü\'minîn.'),
(16, 2, 9, 'yüḫâdi`ûne-llâhe velleẕîne âmenû. vemâ yaḫde`ûne illâ enfüsehüm vemâ yeş`urûn.'),
(17, 2, 10, 'fî ḳulûbihim meraḍun fezâdehümü-llâhü meraḍâ. velehüm `aẕâbün elîmüm bimâ kânû yekẕibûn.'),
(18, 2, 11, 'veiẕâ ḳîle lehüm lâ tüfsidû fi-l\'arḍi ḳâlû innemâ naḥnü muṣliḥûn.'),
(19, 2, 12, 'elâ innehüm hümü-lmüfsidûne velâkil lâ yeş`urûn.'),
(20, 2, 13, 'veiẕâ ḳîle lehüm âminû kemâ âmene-nnâsü ḳâlû enü\'minü kemâ âmene-ssüfehâ\'. elâ innehüm hümü-ssüfehâü velâkil lâ ya`lemûn.'),
(21, 2, 14, 'veiẕâ leḳu-lleẕîne âmenû ḳâlû âmennâ. veiẕâ ḫalev ilâ şeyâṭînihim ḳâlû innâ me`aküm innemâ naḥnü müstehziûn.'),
(22, 2, 15, 'allâhü yestehziü bihim veyemüddühüm fî ṭugyânihim ya`mehûn.'),
(23, 2, 16, 'ülâike-lleẕîne-şteravu-ḍḍalâlete bilhüdâ. femâ rabiḥat ticâratühüm vemâ kânû mühtedîn.'),
(24, 2, 17, 'meŝelühüm kemeŝeli-lleẕi-stevḳade nârâ. felemmâ eḍâet mâ ḥavlehû ẕehebe-llâhü binûrihim veterakehüm fî żulümâtil lâ yübṣirûn.'),
(25, 2, 18, 'ṣummüm bükmün `umyün fehüm lâ yerci`ûn.'),
(26, 2, 19, 'ev keṣayyibim mine-ssemâi fîhi żulümâtüv vera`düv veberḳ. yec`alûne eṣâbi`ahüm fî âẕânihim mine-ṣṣavâ`iḳi ḥaẕera-lmevt. vellâhü müḥîṭum bilkâfirîn.'),
(27, 2, 20, 'yekâdü-lberḳu yaḫṭafü ebṣârahüm. küllemâ eḍâe lehüm meşev fîhi veiẕâ ażleme `aleyhim ḳâmû. velev şâe-llâhü leẕehebe bisem`ihim veebṣârihim. inne-llâhe `alâ külli şey\'in ḳadîr.'),
(28, 2, 21, 'yâ eyyühe-nnâsu-`büdû rabbekümü-lleẕî ḫaleḳaküm velleẕîne min ḳabliküm le`alleküm tetteḳûn.'),
(29, 2, 22, 'elleẕî ce`ale lekümü-l\'arḍa firâşev vessemâe binaâ. veenzele mine-ssemâi mâen feaḫrace bihî mine-ŝŝemerâti rizḳal leküm. felâ tec`alû lillâhi endâdev veentüm ta`lemûn.'),
(30, 2, 23, 'vein küntüm fî raybim mimmâ nezzelnâ `alâ `abdinâ fe\'tû bisûratim mim miŝlih. ved`û şühedâeküm min dûni-llâhi in küntüm ṣâdiḳîn.'),
(31, 2, 24, 'feil lem tef`alû velen tef`alû fetteḳu-nnâra-lletî veḳûdühe-nnâsü velḥicârah. ü`iddet lilkâfirîn.'),
(32, 2, 25, 'vebeşşiri-lleẕîne âmenû ve`amilu-ṣṣâliḥâti enne lehüm cennâtin tecrî min taḥtihe-l\'enhâr. küllemâ ruziḳû minhâ min ŝemeratir rizḳan ḳâlû hâẕe-lleẕî ruziḳnâ min ḳablü veütû bihî müteşâbihâ. velehüm fîhâ ezvâcüm müṭahherâtüv vehüm fîhâ ḫâlidûn.'),
(33, 2, 26, 'inne-llâhe lâ yestaḥyî ey yaḍribe meŝelem mâ be`ûḍaten femâ fevḳahâ. feemme-lleẕîne âmenû feya`lemûne ennehü-lḥaḳḳu mir rabbihim. veemme-lleẕîne keferû feyeḳûlûne mâẕâ erâde-llâhü bihâẕâ meŝelâ. yüḍillü bihî keŝîrav veyehdî bihî keŝîrâ. vemâ yüḍillü bihî ille-lfâsiḳîn.'),
(34, 2, 27, 'elleẕîne yenḳuḍûne `ahde-llâhi mim ba`di mîŝâḳih. veyaḳṭa`ûne mâ emera-llâhü bihî ey yûṣale veyüfsidûne fi-l\'arḍ. ülâike hümü-lḫâsirûn.'),
(35, 2, 28, 'keyfe tekfürûne billâhi veküntüm emvâten feaḥyâküm. ŝümme yümîtüküm ŝümme yuḥyîküm ŝümme ileyhi türce`ûn.'),
(36, 2, 29, 'hüve-lleẕî ḫaleḳa leküm mâ fi-l\'arḍi cemî`an ŝümme-stevâ ile-ssemâi fesevvâhünne seb`a semâvât. vehüve bikülli şey\'in `alîm.'),
(37, 2, 30, 'veiẕ ḳâle rabbüke lilmelâiketi innî câ`ilün fi-l\'arḍi ḫalîfeh. ḳâlû etec`alü fîhâ mey yüfsidü fîhâ veyesfikü-ddimâ\'. venaḥnü nüsebbiḥu biḥamdike venüḳaddisü lek. ḳâle innî a`lemü mâ lâ ta`lemûn.'),
(38, 2, 31, 've`alleme âdeme-l\'esmâe küllehâ ŝümme `araḍahüm `ale-lmelâiketi feḳâle embiûnî biesmâi hâülâi in küntüm ṣâdiḳîn.'),
(39, 2, 32, 'ḳâlû sübḥâneke lâ `ilme lenâ illâ mâ `allemtenâ. inneke ente-l`alîmü-lḥakîm.'),
(40, 2, 33, 'ḳâle yâ âdemü embi\'hüm biesmâihim. felemmâ embeehüm biesmâihim ḳâle elem eḳul leküm innî a`lemü gaybe-ssemâvâti vel\'arḍi vea`lemü mâ tübdûne vemâ küntüm tektümûn.'),
(41, 2, 34, 'veiẕ ḳulnâ lilmelâiketi-scüdû liâdeme fesecedû illâ iblîs. ebâ vestekbera vekâne mine-lkâfirîn.'),
(42, 2, 35, 'veḳulnâ yâ âdemü-skün ente vezevcüke-lcennete vekülâ minhâ ragaden ḥayŝü şi\'tümâ. velâ taḳrabâ hâẕihi-şşecerate fetekûnâ mine-żżâlimîn.'),
(43, 2, 36, 'feezellehüme-şşeyṭânü `anhâ feaḫracehümâ mimmâ kânâ fîh. veḳulne-hbiṭû ba`ḍuküm liba`ḍin `adüvv. veleküm fi-l\'arḍi müsteḳarruv vemetâ`un ilâ ḥîn.'),
(44, 2, 37, 'feteleḳḳâ âdemü mir rabbihî kelimâtin fetâbe `aleyh. innehû hüve-ttevvâbü-rraḥîm.'),
(45, 2, 38, 'ḳulne-hbiṭû minhâ cemî`â. feimmâ ye\'tiyenneküm minnî hüden femen tebi`a hüdâye felâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(46, 2, 39, 'velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(47, 2, 40, 'yâ benî isrâîle-ẕkürû ni`metiye-lletî en`amtü `aleyküm veevfû bi`ahdî ûfi bi`ahdiküm veiyyâye ferhebûn.'),
(48, 2, 41, 'veâminû bimâ enzeltü müṣaddiḳal limâ me`aküm velâ tekûnû evvele kâfirim bih. velâ teşterû biâyâtî ŝemenen ḳalîlâ. veiyyâye fetteḳûn.'),
(49, 2, 42, 'velâ telbisü-lḥaḳḳa bilbâṭili vetektümü-lḥaḳḳa veentüm ta`lemûn.'),
(50, 2, 43, 'veeḳîmu-ṣṣalâte veâtü-zzekâte verke`û me`a-rrâki`în.'),
(51, 2, 44, 'ete\'mürûne-nnâse bilbirri vetensevne enfüseküm veentüm tetlûne-lkitâb. efelâ ta`ḳilûn.'),
(52, 2, 45, 'veste`înû biṣṣabri veṣṣalâh. veinnehâ lekebîratün illâ `ale-lḫâşi`în.'),
(53, 2, 46, 'elleẕîne yeżunnûne ennehüm mülâḳû rabbihim veennehüm ileyhi râci`ûn.'),
(54, 2, 47, 'yâ benî isrâîle-ẕkürû ni`metiye-lletî en`amtü `aleyküm veennî feḍḍaltüküm `ale-l`âlemîn.'),
(55, 2, 48, 'vetteḳû yevmel lâ teczî nefsün `an nefsin şey\'ev velâ yuḳbelü minhâ şefâ`atüv velâ yü\'ḫaẕü minhâ `adlüv velâ hüm yünṣarûn.'),
(56, 2, 49, 'veiẕ necceynâküm min âli fir`avne yesûmûneküm sûe-l`aẕâbi yüẕebbiḥûne ebnâeküm veyestaḥyûne nisâeküm. vefî ẕâliküm belâüm mir rabbiküm `ażîm.'),
(57, 2, 50, 'veiẕ feraḳnâ bikümü-lbaḥra feenceynâküm veagraḳnâ âle fir`avne veentüm tenżurûn.'),
(58, 2, 51, 'veiẕ vâ`adnâ mûsâ erbe`îne leyleten ŝümme-tteḫaẕtümü-l`icle mim ba`dihî veentüm żâlimûn.'),
(59, 2, 52, 'ŝümme `afevnâ `anküm mim ba`di ẕâlike le`alleküm teşkürûn.'),
(60, 2, 53, 'veiẕ âteynâ mûse-lkitâbe velfürḳâne le`alleküm tehtedûn.'),
(61, 2, 54, 'veiẕ ḳâle mûsâ liḳavmihî yâ ḳavmi inneküm żalemtüm enfüseküm bittiḫâẕikümü-l`icle fetûbû ilâ bâriiküm faḳtülû enfüseküm. ẕâliküm ḫayrul leküm `inde bâriiküm. fetâbe `aleyküm. innehû hüve-ttevvâbü-rraḥîm.'),
(62, 2, 55, 'veiẕ ḳultüm yâ mûsâ len nü\'mine leke ḥattâ nera-llâhe cehraten feeḫaẕetkümu-ṣṣâ`iḳatü veentüm tenżurûn.'),
(63, 2, 56, 'ŝümme be`aŝnâküm mim ba`di mevtiküm le`alleküm teşkürûn.'),
(64, 2, 57, 'veżallelnâ `aleykümü-lgamâme veenzelnâ `aleykümü-lmenne vesselvâ. külû min ṭayyibâti mâ razaḳnâküm. vemâ żalemûnâ velâkin kânû enfüsehüm yażlimûn.'),
(65, 2, 58, 'veiẕ ḳulne-dḫulû hâẕihi-lḳaryete fekülû minhâ ḥayŝü şi\'tüm ragadev vedḫulü-lbâbe süccedev veḳûlû ḥiṭṭatün nagfir leküm ḫaṭâyâküm. vesenezîdü-lmuḥsinîn.'),
(66, 2, 59, 'febeddele-lleẕîne żalemû ḳavlen gayra-lleẕî ḳîle lehüm feenzelnâ `ale-lleẕîne żalemû riczem mine-ssemâi bimâ kânû yefsüḳûn.'),
(67, 2, 60, 'veiẕi-stesḳâ mûsâ liḳavmihî feḳulne-ḍrib bi`aṣâke-lḥacer. fenfecerat minhü-ŝnetâ `aşrate `aynâ. ḳad `alime küllü ünâsim meşrabehüm. külû veşrabû mir rizḳi-llâhi velâ ta`ŝev fi-l\'arḍi müfsidîn.'),
(68, 2, 61, 'veiẕ ḳultüm yâ mûsâ len naṣbira `alâ ṭa`âmiv vâḥidin fed`u lenâ rabbeke yuḫric lenâ mimmâ tümbitü-l\'arḍu mim baḳlihâ veḳiŝŝâihâ vefûmihâ ve`adesihâ vebeṣalihâ. ḳâle etestebdilûne-lleẕî hüve ednâ billeẕî hüve ḫayr. ihbiṭû miṣran feinne leküm mâ seeltüm. veḍuribet `aleyhimü-ẕẕilletü velmeskenetü vebâû bigaḍabim mine-llâh. ẕâlike biennehüm kânû yekfürûne biâyâti-llâhi veyaḳtülûne-nnebiyyîne bigayri-lḥaḳḳ. ẕâlike bimâ `aṣav vekânû ya`tedûn.'),
(69, 2, 62, 'inne-lleẕîne âmenû velleẕîne hâdû venneṣârâ veṣṣâbi\'îne men âmene billâhi velyevmi-l\'âḫiri ve`amile ṣâliḥan felehüm ecruhüm `inde rabbihim. velâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(70, 2, 63, 'veiẕ eḫaẕnâ mîŝâḳaküm verafa`nâ fevḳakümu-ṭṭûr. ḫuẕû mâ âteynâküm biḳuvvetiv veẕkürû mâ fîhi le`alleküm tetteḳûn.'),
(71, 2, 64, 'ŝümme tevelleytüm mim ba`di ẕâlik. felevlâ faḍlü-llâhi `aleyküm veraḥmetühû leküntüm mine-lḫâsirîn.'),
(72, 2, 65, 'veleḳad `alimtümü-lleẕîne-`tedev minküm fi-ssebti feḳulnâ lehüm kûnû ḳiradeten ḫâsiîn.'),
(73, 2, 66, 'fece`alnâhâ nekâlel limâ beyne yedeyhâ vemâ ḫalfehâ vemev`iżatel lilmütteḳîn.'),
(74, 2, 67, 'veiẕ ḳâle mûsâ liḳavmihî inne-llâhe ye\'müruküm en teẕbeḥû beḳarah. ḳâlû etetteḫiẕünâ hüzüvâ. ḳâle e`ûẕü billâhi en ekûne mine-lcâhilîn.'),
(75, 2, 68, 'ḳâlü-d`u lenâ rabbeke yübeyyil lenâ mâ hî. ḳâle innehû yeḳûlü innehâ beḳaratül lâ fâriḍuv velâ bikr. `avânüm beyne ẕâlik. fef`alû mâ tü\'merûn.'),
(76, 2, 69, 'ḳâlü-d`u lenâ rabbeke yübeyyil lenâ mâ levnühâ. ḳâle innehû yeḳûlü innehâ beḳaratün ṣafrâü fâḳi`ul levnühâ tesürru-nnâżirîn.'),
(77, 2, 70, 'ḳâlü-d`u lenâ rabbeke yübeyyil lenâ mâ hiye inne-lbeḳara teşâbehe `aleynâ. veinnâ in şâe-llâhü lemühtedûn.'),
(78, 2, 71, 'ḳâle innehû yeḳûlü innehâ beḳaratül lâ ẕelûlün tüŝîru-l\'arḍa velâ tesḳi-lḥarŝ. müsellemetül lâ şiyete fîhâ. ḳâlü-l\'âne ci\'te bilḥaḳḳ. feẕebeḥûhâ vemâ kâdû yef`alûn.'),
(79, 2, 72, 'veiẕ ḳateltüm nefsen feddâra\'tüm fîhâ. vellâhü muḫricüm mâ küntüm tektümûn.'),
(80, 2, 73, 'feḳulne-ḍribûhü biba`ḍihâ. keẕâlike yuḥyi-llâhü-lmevtâ veyürîküm âyâtihî le`alleküm ta`ḳilûn.'),
(81, 2, 74, 'ŝümme ḳaset ḳulûbüküm mim ba`di ẕâlike fehiye kelḥicârati ev eşeddü ḳasveh. veinne mine-lḥicârati lemâ yetefecceru minhü-l\'enhâr. veinne minhâ lemâ yeşşeḳḳaḳu feyaḫrucü minhü-lmâ\'. veinne minhâ lemâ yehbiṭu min ḫaşyeti-llâh. veme-llâhü bigâfilin `ammâ ta`melûn.'),
(82, 2, 75, 'efetaṭme`ûne ey yü\'minû leküm veḳad kâne ferîḳum minhüm yesme`ûne kelâme-llâhi ŝümme yüḥarrifûnehû mim ba`di mâ `aḳalûhü vehüm ya`lemûn.'),
(83, 2, 76, 'veiẕâ leḳu-lleẕîne âmenû ḳâlû âmennâ. veiẕâ ḫalâ ba`ḍuhüm ilâ ba`ḍin ḳâlû etüḥaddiŝûnehüm bimâ feteḥa-llâhü `aleyküm liyüḥâccûküm bihî `inde rabbiküm. efelâ ta`ḳilûn.'),
(84, 2, 77, 'evelâ ya`lemûne enne-llâhe ya`lemü mâ yüsirrûne vemâ yü`linûn.'),
(85, 2, 78, 'veminhüm ümmiyyûne lâ ya`lemûne-lkitâbe illâ emâniyye vein hüm illâ yeżunnûn.'),
(86, 2, 79, 'feveylül lilleẕîne yektübûne-lkitâbe bieydîhim ŝümme yeḳûlûne hâẕâ min `indi-llâhi liyeşterû bihî ŝemenen ḳalîlâ. feveylül lehüm mimmâ ketebet eydîhim veveylül lehüm mimmâ yeksibûn.'),
(87, 2, 80, 'veḳâlû len temessene-nnâru illâ eyyâmem ma`dûdeh. ḳul etteḫaẕtüm `inde-llâhi `ahden feley yuḫlife-llâhü `ahdehû em teḳûlûne `ale-llâhi mâ lâ ta`lemûn.'),
(88, 2, 81, 'belâ men kesebe seyyietev veeḥâṭat bihî ḫaṭîetühû feülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(89, 2, 82, 'velleẕîne âmenû ve`amilu-ṣṣâliḥâti ülâike aṣḥâbü-lcenneh. hüm fîhâ ḫâlidûn.'),
(90, 2, 83, 'veiẕ eḫaẕnâ mîŝâḳa benî isrâîle lâ ta`büdûne ille-llâhe vebilvâlideyni iḥsânev veẕi-lḳurbâ velyetâmâ velmesâkîni veḳûlû linnâsi ḥusnev veeḳîmu-ṣṣalâte veâtü-zzekâh. ŝümme tevelleytüm illâ ḳalîlem minküm veentüm mü`riḍûn.'),
(91, 2, 84, 'veiẕ eḫaẕnâ mîŝâḳaküm lâ tesfikûne dimâeküm velâ tuḫricûne enfüseküm min diyâriküm ŝümme aḳrartüm veentüm teşhedûn.'),
(92, 2, 85, 'ŝümme entüm hâülâi taḳtülûne enfüseküm vetuḫricûne ferîḳam minküm min diyârihim. teżâherûne `aleyhim bil\'iŝmi vel`udvân. veiy ye\'tûküm üsârâ tüfâdûhüm vehüve müḥarramün `aleyküm iḫrâcühüm. efetü\'minûne biba`ḍi-lkitâbi vetekfürûne biba`ḍ. femâ cezâü mey yef`alü ẕâlike minküm illâ ḫizyün fi-lḥayâti-ddünyâ. veyevme-lḳiyâmeti yüraddûne ilâ eşeddi-l`aẕâb. veme-llâhü bigâfilin `ammâ ta`melûn.'),
(93, 2, 86, 'ülâike-lleẕîne-şteravu-lḥayâte-ddünyâ bil\'âḫirati. felâ yüḫaffefü `anhümü-l`aẕâbü velâ hüm yünṣarûn.'),
(94, 2, 87, 'veleḳad âteynâ mûse-lkitâbe veḳaffeynâ mim ba`dihî birrusüli veâteynâ `îse-bne meryeme-lbeyyinâti veeyyednâhü birûḥi-lḳudüs. efeküllemâ câeküm rasûlüm bimâ lâ tehvâ enfüsükümü-stekbertüm. feferîḳan keẕẕebtüm veferîḳan taḳtülûn.'),
(95, 2, 88, 'veḳâlû ḳulûbünâ gulf. bel le`anehümü-llâhü biküfrihim feḳalîlem mâ yü\'minûn.'),
(96, 2, 89, 'velemmâ câehüm kitâbüm min `indi-llâhi müṣaddiḳul limâ me`ahüm vekânû min ḳablü yesteftiḥûne `ale-lleẕîne keferû. felemmâ câehüm mâ `arafû keferû bih. fela`netü-llâhi `ale-lkâfirîn.'),
(97, 2, 90, 'bi\'seme-şterav bihî enfüsehüm ey yekfürû bimâ enzele-llâhü bagyen ey yünezzile-llâhü min faḍlihî `alâ mey yeşâü min `ibâdih. febâû bigaḍabin `alâ gaḍab. velilkâfirîne `aẕâbüm mühîn.'),
(98, 2, 91, 'veiẕâ ḳîle lehüm âminû bimâ enzele-llâhü ḳâlû nü\'minü bimâ ünzile `aleynâ veyekfürûne bimâ verâehû vehüve-lḥaḳḳu müṣaddiḳal limâ me`ahüm. ḳul felime taḳtülûne embiyâe-llâhi min ḳablü in küntüm mü\'minîn.'),
(99, 2, 92, 'veleḳad câeküm mûsâ bilbeyyinâti ŝümme-tteḫaẕtümü-l`icle mim ba`dihî veentüm żâlimûn.'),
(100, 2, 93, 'veiẕ eḫaẕnâ mîŝâḳaküm verafa`nâ fevḳakümu-ṭṭûr. ḫuẕû mâ âteynâküm biḳuvvetiv vesme`û. ḳâlû semi`nâ ve`aṣaynâ veüşribû fî ḳulûbihimü-l`icle biküfrihim. ḳul bi\'semâ ye\'müruküm bihî îmânüküm in küntüm mü\'minîn.'),
(101, 2, 94, 'ḳul in kânet lekümü-ddâru-l\'âḫiratü `inde-llâhi ḫâliṣatem min dûni-nnâsi fetemennevu-lmevte in küntüm ṣâdiḳîn.'),
(102, 2, 95, 'veley yetemennevhü ebedem bimâ ḳaddemet eydîhim. vellâhü `alîmüm biżżâlimîn.'),
(103, 2, 96, 'veletecidennehüm aḥraṣa-nnâsi `alâ ḥayâh. vemine-lleẕîne eşrakû yeveddü eḥadühüm lev yü`ammeru elfe seneh. vemâ hüve bimüzaḥziḥihî mine-l`aẕâbi ey yü`ammer. vellâhü beṣîrum bimâ ya`melûn.'),
(104, 2, 97, 'ḳul men kâne `adüvvel licibrîle feinnehû nezzelehû `alâ ḳalbike biiẕni-llâhi müṣaddiḳal limâ beyne yedeyhi vehüdev vebüşrâ lilmü\'minîn.'),
(105, 2, 98, 'men kâne `adüvvel lillâhi vemelâiketihî verusülihî vecibrîle vemîkâle feinne-llâhe `adüvvül lilkâfirîn.'),
(106, 2, 99, 'veleḳad enzelnâ ileyke âyâtim beyyinât. vemâ yekfüru bihâ ille-lfâsiḳûn.'),
(107, 2, 100, 'eveküllemâ `âhedû `ahden nebeẕehû ferîḳum minhüm. bel ekŝeruhüm lâ yü\'minûn.'),
(108, 2, 101, 'velemmâ câehüm rasûlüm min `indi-llâhi müṣaddiḳul limâ me`ahüm nebeẕe ferîḳum mine-lleẕîne ûtü-lkitâb. kitâbe-llâhi verâe żuhûrihim keennehüm lâ ya`lemûn.'),
(109, 2, 102, 'vettebe`û mâ tetlü-şşeyâṭînü `alâ mülki süleymân. vemâ kefera süleymânü velâkinne-şşeyâṭîne keferû yü`allimûne-nnâse-ssiḥr. vemâ ünzile `ale-lmelekeyni bibâbile hârûte vemârût. vemâ yü`allimâni min eḥadin ḥattâ yeḳûlâ innemâ naḥnü fitnetün felâ tekfür. feyete`allemûne minhümâ mâ yüferriḳûne bihî beyne-lmer\'i vezevcih. vemâ hüm biḍârrîne bihî min eḥadin illâ biiẕni-llâh. veyete`allemûne mâ yeḍurruhüm velâ yenfe`uhüm. veleḳad `alimû lemeni-şterâhü mâ lehû fi-l\'âḫirati min ḫalâḳ. velebi\'se mâ şerav bihî enfüsehüm. lev kânû ya`lemûn.'),
(110, 2, 103, 'velev ennehüm âmenû vetteḳav lemeŝûbetüm min `indi-llâhi ḫayr. lev kânû ya`lemûn.'),
(111, 2, 104, 'yâ eyyühe-lleẕîne âmenû lâ teḳûlû râ`inâ veḳûlü-nẕürnâ vesme`û. velilkâfirîne `aẕâbün elîm.'),
(112, 2, 105, 'mâ yeveddü-lleẕîne keferû min ehli-lkitâbi vele-lmüşrikîne ey yünezzele `aleyküm min ḫayrim mir rabbiküm. vellâhü yaḫteṣṣu biraḥmetihî mey yeşâ\'. vellâhü ẕü-lfaḍli-l`ażîm.'),
(113, 2, 106, 'mâ nensaḫ min âyetin ev nünsihâ ne\'ti biḫayrim minhâ ev miŝlihâ. elem ta`lem enne-llâhe `alâ külli şey\'in ḳadîr.'),
(114, 2, 107, 'elem ta`lem enne-llâhe lehû mülkü-ssemâvâti vel\'arḍ. vemâ leküm min dûni-llâhi miv veliyyiv velâ neṣîr.'),
(115, 2, 108, 'em türîdûne en tes\'elû rasûleküm kemâ süile mûsâ min ḳabl. vemey yetebeddeli-lküfra bil\'îmâni feḳad ḍalle sevâe-ssebîl.'),
(116, 2, 109, 'vedde keŝîrum min ehli-lkitâbi lev yeruddûneküm mim ba`di îmâniküm küffârâ. ḥasedem min `indi enfüsihim mim ba`di mâ tebeyyene lehümü-lḥaḳḳ. fa`fû vaṣfeḥû ḥattâ ye\'tiye-llâhü biemrih. inne-llâhe `alâ külli şey\'in ḳadîr.'),
(117, 2, 110, 'veeḳîmu-ṣṣalâte veâtü-zzekâh. vemâ tüḳaddimû lienfüsiküm min ḫayrin tecidûhü `inde-llâh. inne-llâhe bimâ ta`melûne beṣîr.'),
(118, 2, 111, 'veḳâlû ley yedḫule-lcennete illâ men kâne hûden ev neṣârâ. tilke emâniyyühüm. ḳul hâtû bürhâneküm in küntüm ṣâdiḳîn.'),
(119, 2, 112, 'belâ men esleme vechehû lillâhi vehüve muḥsinün felehû ecruhû `inde rabbih. velâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(120, 2, 113, 'veḳâleti-lyehûdü leyseti-nneṣârâ `alâ şey\'. veḳâleti-nneṣârâ leyseti-lyehûdü `alâ şey\'iv vehüm yetlûne-lkitâb. keẕâlike ḳâle-lleẕîne lâ ya`lemûne miŝle ḳavlihim. fellâhü yaḥkümü beynehüm yevme-lḳiyâmeti fîmâ kânû fîhi yaḫtelifûn.'),
(121, 2, 114, 'vemen ażlemü mimmem mene`a mesâcide-llâhi ey yüẕkera fîhe-smühû vese`â fî ḫarâbihâ. ülâike mâ kâne lehüm ey yedḫulûhâ illâ ḫâifîn. lehüm fi-ddünyâ ḫizyüv velehüm fi-l\'âḫirati `aẕâbün `ażîm.'),
(122, 2, 115, 'velillâhi-lmeşriḳu velmagribü feeynemâ tüvellû feŝemme vechü-llâh. inne-llâhe vâsi`un `alîm.'),
(123, 2, 116, 'veḳâlü-tteḫaẕe-llâhü veleden sübḥâneh. bel lehû mâ fi-ssemâvâti vel\'arḍ. küllül lehû ḳânitûn.'),
(124, 2, 117, 'bedî`u-ssemâvâti vel\'arḍ. veiẕâ ḳaḍâ emran feinnemâ yeḳûlü lehû kün feyekûn.'),
(125, 2, 118, 'veḳâle-lleẕîne lâ ya`lemûne levlâ yükellimüne-llâhü ev te\'tînâ âyeh. keẕâlike ḳâle-lleẕîne min ḳablihim miŝle ḳavlihim. teşâbehet ḳulûbühüm. ḳad beyyenne-l\'âyâti liḳavmiy yûḳinûn.'),
(126, 2, 119, 'innâ erselnâke bilḥaḳḳi beşîrav veneẕîrav velâ tüs\'elü `an aṣḥâbi-lceḥîm.'),
(127, 2, 120, 'velen terḍâ `anke-lyehûdü vele-nneṣârâ ḥattâ tettebi`a milletehüm. ḳul inne hüde-llâhi hüve-lhüdâ. veleini-tteba`te ehvâehüm ba`de-lleẕî câeke mine-l`ilmi mâ leke mine-llâhi miv veliyyiv velâ neṣîr.'),
(128, 2, 121, 'elleẕîne âteynâhümü-lkitâbe yetlûnehû ḥaḳḳa tilâvetih. ülâike yü\'minûne bih. vemey yekfür bihî feülâike hümü-lḫâsirûn.'),
(129, 2, 122, 'yâ benî isrâîle-ẕkürû ni`metiye-lletî en`amtü `aleyküm veennî feḍḍaltüküm `ale-l`âlemîn.'),
(130, 2, 123, 'vetteḳû yevmel lâ teczî nefsün `an nefsin şey\'ev velâ yuḳbelü minhâ `adlüv velâ tenfe`uhâ şefâ`atüv velâ hüm yünṣarûn.'),
(131, 2, 124, 'veiẕi-btelâ ibrâhîme rabbühû bikelimâtin feetemmehünn. ḳâle innî câ`ilüke linnâsi imâmâ. ḳâle vemin ẕürriyyetî. ḳâle lâ yenâlü `ahdi-żżâlimîn.'),
(132, 2, 125, 'veiẕ ce`alne-lbeyte meŝâbetel linnâsi veemnâ. vetteḫiẕû mim meḳâmi ibrâhîme müṣallâ. ve`ahidnâ ilâ ibrâhîme veismâ`île en ṭahhirâ beytiye liṭṭâifîne vel`âkifîne verrukke`i-ssücûd.'),
(133, 2, 126, 'veiẕ ḳâle ibrâhîmü rabbi-c`al hâẕâ beleden âminev verzuḳ ehlehû mine-ŝŝemerâti men âmene minhüm billâhi velyevmi-l\'âḫir. ḳâle vemen kefera feümetti`uhû ḳalîlen ŝümme aḍṭarruhû ilâ `aẕâbi-nnâr. vebi\'se-lmeṣîr.'),
(134, 2, 127, 'veiẕ yerfe`u ibrâhîmü-lḳavâ`ide mine-lbeyti veismâ`îl. rabbenâ teḳabbel minnâ. inneke ente-ssemî`u-l`alîm.'),
(135, 2, 128, 'rabbenâ vec`alnâ müslimeyni leke vemin ẕürriyyetinâ ümmetem müslimetel lek. veerinâ menâsikenâ vetüb `aleynâ. inneke ente-ttevvâbü-rraḥîm.'),
(136, 2, 129, 'rabbenâ veb`aŝ fîhim rasûlem minhüm yetlû `aleyhim âyâtike veyü`allimühümü-lkitâbe velḥikmete veyüzekkîhim. inneke ente-l`azîzü-lḥakîm.'),
(137, 2, 130, 'vemey yergabü `am milleti ibrâhîme illâ men sefihe nefseh. veleḳadi-ṣṭafeynâhü fi-ddünyâ. veinnehû fi-l\'âḫirati lemine-ṣṣâliḥîn.'),
(138, 2, 131, 'iẕ ḳâle lehû rabbühû eslim ḳâle eslemtü lirabbi-l`âlemîn.'),
(139, 2, 132, 'veveṣṣâ bihâ ibrâhîmü benîhi veya`ḳûb. yâ benîye inne-llâhe-ṣṭafâ lekümü-ddîne felâ temûtünne illâ veentüm müslimûn.'),
(140, 2, 133, 'em küntüm şühedâe iẕ ḥaḍara ya`ḳûbe-lmevtü iẕ ḳâle libenîhi mâ ta`büdûne mim ba`dî. ḳâlû na`büdü ilâheke veilâhe âbâike ibrâhîme veismâ`île veisḥâḳa ilâhev vâḥidâ. venaḥnü lehû müslimûn.'),
(141, 2, 134, 'tilke ümmetün ḳad ḫalet. lehâ mâ kesebet veleküm mâ kesebtüm. velâ tüs\'elûne `ammâ kânû ya`melûn.'),
(142, 2, 135, 'veḳâlû kûnû hûden ev neṣârâ tehtedû. ḳul bel millete ibrâhîme ḥanîfâ. vemâ kâne mine-lmüşrikîn.'),
(143, 2, 136, 'ḳûlû âmennâ billâhi vemâ ünzile ileynâ vemâ ünzile ilâ ibrâhîme veismâ`île veisḥâḳa veya`ḳûbe vel\'esbâṭi vemâ ûtiye mûsâ ve`îsâ vemâ ûtiye-nnebiyyûne mir rabbihim. lâ nüferriḳu beyne eḥadim minhüm. venaḥnü lehû müslimûn.'),
(144, 2, 137, 'fein âmenû bimiŝli mâ âmentüm bihî feḳadi-htedev. vein tevellev feinnemâ hüm fî şiḳâḳ. feseyekfîkehümü-llâh. vehüve-ssemî`u-l`alîm.'),
(145, 2, 138, 'ṣibgate-llâh. vemen aḥsenü mine-llâhi ṣibgah. venaḥnü lehû `âbidûn.'),
(146, 2, 139, 'ḳul etüḥâccûnenâ fi-llâhi vehüve rabbünâ verabbüküm. velenâ a`mâlünâ veleküm a`mâlüküm. venaḥnü lehû muḫliṣûn.'),
(147, 2, 140, 'em teḳûlûne inne ibrâhîme veismâ`île veisḥâḳa veya`ḳûbe vel\'esbâṭa kânû hûden ev neṣârâ. ḳul eentüm a`lemü emi-llâh. vemen ażlemü mimmen keteme şehâdeten `indehû mine-llâh. veme-llâhü bigâfilin `ammâ ta`melûn.'),
(148, 2, 141, 'tilke ümmetün ḳad ḫalet. lehâ mâ kesebet veleküm mâ kesebtüm. velâ tüs\'elûne `ammâ kânû ya`melûn.'),
(149, 2, 142, 'seyeḳûlü-ssüfehâü mine-nnâsi mâ vellâhüm `an ḳibletihimü-lletî kânû `aleyhâ. ḳul lillâhi-lmeşriḳu velmagrib. yehdî mey yeşâü ilâ ṣirâṭim müsteḳîm.'),
(150, 2, 143, 'vekeẕâlike ce`alnâküm ümmetev veseṭal litekûnû şühedâe `ale-nnâsi veyekûne-rrasûlü `aleyküm şehîdâ. vemâ ce`alne-lḳiblete-lletî künte `aleyhâ illâ lina`leme mey yettebi`u-rrasûle mimmey yenḳalibü `alâ `aḳibeyh. vein kânet lekebîraten illâ `ale-lleẕîne hede-llâh. vemâ kâne-llâhü liyüḍî`a îmâneküm. inne-llâhe binnâsi leraûfür raḥîm.'),
(151, 2, 144, 'ḳad nerâ teḳallübe vechike fi-ssemâ\'. felenüvelliyenneke ḳibleten terḍâhâ. fevelli vecheke şaṭra-lmescidi-lḥarâm. veḥayŝü mâ küntüm fevellû vucûheküm şaṭrah. veinne-lleẕîne ûtü-lkitâbe leya`lemûne ennehü-lḥaḳḳu mir rabbihim. veme-llâhü bigâfilin `ammâ ya`melûn.'),
(152, 2, 145, 'velein eteyte-lleẕîne ûtü-lkitâbe bikülli âyetim mâ tebi`û ḳibletek. vemâ ente bitâbi`in ḳibletehüm. vemâ ba`ḍuhüm bitâbi`in ḳiblete ba`ḍ. veleini-tteba`te ehvâehüm mim ba`di mâ câeke mine-l`ilmi inneke iẕel lemine-żżâlimîn.'),
(153, 2, 146, 'elleẕîne âteynâhümü-lkitâbe ya`rifûnehû kemâ ya`rifûne ebnâehüm. veinne ferîḳam minhüm leyektümûne-lḥaḳḳa vehüm ya`lemûn.'),
(154, 2, 147, 'elḥaḳḳu mir rabbike felâ tekûnenne mine-lmümterîn.'),
(155, 2, 148, 'velikülliv vichetün hüve müvellîhâ festebiḳu-lḫayrât. eyne mâ tekûnû ye\'ti bikümü-llâhü cemî`â. inne-llâhe `alâ külli şey\'in ḳadîr.'),
(156, 2, 149, 'vemin ḥayŝü ḫaracte fevelli vecheke şaṭra-lmescidi-lḥarâm. veinnehû lelḥaḳḳu mir rabbik. veme-llâhü bigâfilin `ammâ ta`melûn.'),
(157, 2, 150, 'vemin ḥayŝü ḫaracte fevelli vecheke şaṭra-lmescidi-lḥarâm. veḥayŝü mâ küntüm fevellû vucûheküm şaṭrahû liellâ yekûne linnâsi `aleyküm ḥucceh. ille-lleẕîne żalemû minhüm felâ taḫşevhüm vaḫşevnî veliütimme ni`metî `aleyküm vele`alleküm tehtedûn.'),
(158, 2, 151, 'kemâ erselnâ fîküm rasûlem minküm yetlû `aleyküm âyâtinâ veyüzekkîküm veyü`allimükümü-lkitâbe velḥikmete veyü`allimüküm mâ lem tekûnû ta`lemûn.'),
(159, 2, 152, 'feẕkürûnî eẕkürküm veşkürû lî velâ tekfürûn.'),
(160, 2, 153, 'yâ eyyühe-lleẕîne âmenü-ste`înû biṣṣabri veṣṣalâh. inne-llâhe me`a-ṣṣâbirîn.'),
(161, 2, 154, 'velâ teḳûlû limey yuḳtelü fî sebîli-llâhi emvâh. bel aḥyâüv velâkil lâ teş`urûn.'),
(162, 2, 155, 'veleneblüvenneküm bişey\'im mine-lḫavfi velcû`i venaḳṣim mine-l\'emvâli vel\'enfüsi veŝŝemerât. vebeşşiri-ṣṣâbirîn.'),
(163, 2, 156, 'elleẕîne iẕâ eṣâbethüm müṣîbetün ḳâlû innâ lillâhi veinnâ ileyhi râci`ûn.'),
(164, 2, 157, 'ülâike `aleyhim ṣalevâtüm mir rabbihim veraḥmetüv veülâike hümü-lmühtedûn.'),
(165, 2, 158, 'inne-ṣṣafâ velmervete min şe`âiri-llâh. femen ḥacce-lbeyte evi-`temera felâ cünâḥa `aleyhi ey yeṭṭavvefe bihimâ. vemen teṭavve`a ḫayran feinne-llâhe şâkirun `alîm.'),
(166, 2, 159, 'inne-lleẕîne yektümûne mâ enzelnâ mine-lbeyyinâti velhüdâ mim ba`di mâ beyyennâhü linnâsi fi-lkitâbi ülâike yel`anühümü-llâhü veyel`anühümü-llâ`inûn.'),
(167, 2, 160, 'ille-lleẕîne tâbû veaṣleḥû vebeyyenû feülâike etûbü `aleyhim. veene-ttevvâbü-rraḥîm.'),
(168, 2, 161, 'inne-lleẕîne keferû vemâtû vehüm küffârun ülâike `aleyhim la`netü-llâhi velmelâiketi vennâsi ecme`în.'),
(169, 2, 162, 'ḫâlidîne fîhâ. lâ yüḫaffefü `anhümü-l`aẕâbü velâ hüm yünżarûn.'),
(170, 2, 163, 'veilâhüküm ilâhüv vâḥid. lâ ilâhe illâ hüve-rraḥmânü-rraḥîm.'),
(171, 2, 164, 'inne fî ḫalḳi-ssemâvâti vel\'arḍi vaḫtilâfi-lleyli vennehâri velfülki-lletî tecrî fi-lbaḥri bimâ yenfe`u-nnâse vemâ enzele-llâhü mine-ssemâi mim mâin feaḥyâ bihi-l\'arḍa ba`de mevtihâ vebeŝŝe fîhâ min külli dâbbeh. vetaṣrîfi-rriyâḥi vesseḥâbi-lmüseḫḫari beyne-ssemâi vel\'arḍi leâyâtil liḳavmiy ya`ḳilûn.'),
(172, 2, 165, 'vemine-nnâsi mey yetteḫiẕü min dûni-llâhi endâdey yüḥibbûnehüm keḥubbi-llâh. velleẕîne âmenû eşeddü ḥubbel lillâh. velev yera-lleẕîne żalemû iẕ yeravne-l`aẕâbe enne-lḳuvvete lillâhi cemî`av veenne-llâhe şedîdü-l`aẕâb.'),
(173, 2, 166, 'iẕ teberrae-lleẕîne-ttübi`û mine-lleẕîne-ttebe`û veraevü-l`aẕâbe veteḳaṭṭa`at bihimü-l\'esbâb.'),
(174, 2, 167, 'veḳâle-lleẕîne-ttebe`û lev enne lenâ kerraten feneteberrae minhüm kemâ teberraû minnâ. keẕâlike yürîhimü-llâhü a`mâlehüm ḥaserâtin `aleyhim. vemâ hüm biḫâricîne mine-nnâr.'),
(175, 2, 168, 'yâ eyyühe-nnâsü külû mimmâ fi-l\'arḍi ḥalâlen ṭayyibâ. velâ tettebi`û ḫuṭuvâti-şşeyṭân. innehû leküm `adüvvüm mübîn.'),
(176, 2, 169, 'innemâ ye\'müruküm bissûi velfaḥşâi veen teḳûlû `ale-llâhi mâ lâ ta`lemûn.'),
(177, 2, 170, 'veiẕâ ḳîle lehümü-ttebi`û mâ enzele-llâhü ḳâlû bel nettebi`u mâ elfeynâ `aleyhi âbâenâ. evelev kâne âbâühüm lâ ya`ḳilûne şey\'ev velâ yehtedûn.'),
(178, 2, 171, 'vemeŝelü-lleẕîne keferû kemeŝeli-lleẕî yen`iḳu bimâ lâ yesme`u illâ dü`âev venidââ. ṣummüm bükmün `umyün fehüm lâ ya`ḳilûn.'),
(179, 2, 172, 'yâ eyyühe-lleẕîne âmenû külû min ṭayyibâti mâ razaḳnâküm veşkürû lillâhi in küntüm iyyâhü ta`büdûn.'),
(180, 2, 173, 'innemâ ḥarrame `aleykümü-lmeytete veddeme velaḥme-lḫinzîri vemâ ühille bihî ligayri-llâh. femeni-ḍṭurra gayra bâgiv velâ `âdin felâ iŝme `aleyh. inne-llâhe gafûrur raḥîm.'),
(181, 2, 174, 'inne-lleẕîne yektümûne mâ enzele-llâhü mine-lkitâbi veyeşterûne bihî ŝemenen ḳalîlen ülâike mâ ye\'külûne fî büṭûnihim ille-nnâra velâ yükellimühümü-llâhü yevme-lḳiyâmeti velâ yüzekkîhim. velehüm `aẕâbün elîm.'),
(182, 2, 175, 'ülâike-lleẕîne-şteravu-ḍḍalâlete bilhüdâ vel`aẕâbe bilmagfirah. femâ aṣberahüm `ale-nnâr.'),
(183, 2, 176, 'ẕâlike bienne-llâhe nezzele-lkitâbe bilḥaḳḳ. veinne-lleẕîne-ḫtelefû fi-lkitâbi lefî şiḳâḳum be`îd.'),
(184, 2, 177, 'leyse-lbirra en tüvellû vucûheküm ḳibele-lmeşriḳi velmagribi velâkinne-lbirra men âmene billâhi velyevmi-l\'âḫiri velmelâiketi velkitâbi vennebiyyîn. veâte-lmâle `alâ ḥubbihî ẕevi-lḳurbâ velyetâmâ velmesâkîne vebne-ssebîli vessâilîne vefi-rriḳâb. veeḳâme-ṣṣalâte veâte-zzekâh. velmûfûne bi`ahdihim iẕâ `âhedû. veṣṣâbirîne fi-lbe\'sâi veḍḍarrâi veḥîne-lbe\'s. ülâike-lleẕîne ṣadeḳû. veülâike hümü-lmütteḳûn.'),
(185, 2, 178, 'yâ eyyühe-lleẕîne âmenû kütibe `aleykümü-lḳiṣâṣu fi-lḳatlâ. elḥurru bilḥurri vel`abdü bil`abdi vel\'ünŝâ bil\'ünŝâ. femen `ufiye lehû min eḫîhi şey\'ün fettibâ`um bilma`rûfi veedâün ileyhi biiḥsân. ẕâlike taḫfîfüm mir rabbiküm veraḥmetün. femeni-`tedâ ba`de ẕâlike felehû `aẕâbün elîm.'),
(186, 2, 179, 'veleküm fi-lḳiṣâṣi ḥayâtüy yâ ûli-l\'elbâbi le`alleküm tetteḳûn.'),
(187, 2, 180, 'kütibe `aleyküm iẕâ ḥaḍara eḥadekümü-lmevtü in terake ḫayran. elveṣiyyetü lilvâlideyni vel\'aḳrabîne bilma`rûf. ḥaḳḳan `ale-lmütteḳîn.'),
(188, 2, 181, 'femem beddelehû ba`de mâ semi`ahû feinnemâ iŝmühû `ale-lleẕîne yübeddilûneh. inne-llâhe semî`un `alîm.'),
(189, 2, 182, 'femen ḫâfe mim mûṣin cenefen ev iŝmen feaṣleḥa beynehüm felâ iŝme `aleyh. inne-llâhe gafûrur raḥîm.'),
(190, 2, 183, 'yâ eyyühe-lleẕîne âmenû kütibe `aleykümu-ṣṣiyâmü kemâ kütibe `ale-lleẕîne min ḳabliküm le`alleküm tetteḳûn.'),
(191, 2, 184, 'eyyâmem ma`dûdât. femen kâne minküm merîḍan ev `alâ seferin fe`iddetüm min eyyâmin üḫar. ve`ale-lleẕîne yüṭîḳûnehû fidyetün ṭa`âmü miskîn. femen teṭavve`a ḫayran fehüve ḫayrul leh. veen teṣûmû ḫayrul leküm in küntüm ta`lemûn.'),
(192, 2, 185, 'şehru rameḍane-lleẕî ünzile fîhi-lḳur\'ânü hüdel linnâsi vebeyyinâtim mine-lhüdâ velfürḳân. femen şehide minkümü-şşehra felyeṣumh. vemen kâne merîḍan ev `alâ seferin fe`iddetüm min eyyâmin üḫar. yürîdü-llâhü bikümü-lyüsra velâ yürîdü bikümü-l`usr. velitükmilü-l`iddete velitükebbirü-llâhe `alâ mâ hedâküm vele`alleküm teşkürûn.'),
(193, 2, 186, 'veiẕâ seeleke `ibâdî `annî feinnî ḳarîb. ücîbü da`vete-ddâ`i iẕâ de`âni felyestecîbû lî velyü\'minû bî le`allehüm yerşüdûn.'),
(194, 2, 187, 'üḥille leküm leylete-ṣṣiyâmi-rrafeŝü ilâ nisâiküm. hünne libâsül leküm veentüm libâsül lehünn. `alime-llâhü enneküm küntüm taḫtânûne enfüseküm fetâbe `aleyküm ve`afâ `anküm. fel\'âne bâşirûhünne vebtegû mâ ketebe-llâhü leküm. vekülû veşrabû ḥattâ yetebeyyene lekümü-lḫayṭu-l\'ebyeḍu mine-lḫayṭi-l\'esvedi mine-lfecr. ŝümme etimmu-ṣṣiyâme ile-lleyl. velâ tübâşirûhünne veentüm `âkifûne fi-lmesâcid. tilke ḥudûdü-llâhi felâ taḳrabûhâ. keẕâlike yübeyyinü-llâhü âyâtihî linnâsi le`allehüm yetteḳûn.'),
(195, 2, 188, 'velâ te\'külû emvâleküm beyneküm bilbâṭili vetüdlû bihâ ile-lḥukkâmi lite\'külû ferîḳam min emvâli-nnâsi bil\'iŝmi veentüm ta`lemûn.'),
(196, 2, 189, 'yes\'elûneke `ani-l\'ehilleh. ḳul hiye mevâḳîtü linnâsi velḥacc. veleyse-lbirru bien te\'tü-lbüyûte min żuhûrihâ velâkinne-lbirra meni-tteḳâ. ve\'tü-lbüyûte min ebvâbihâ. vetteḳu-llâhe le`alleküm tüfliḥûn.'),
(197, 2, 190, 'veḳâtilû fî sebîli-llâhi-lleẕîne yüḳâtilûneküm velâ ta`tedû. inne-llâhe lâ yüḥibbü-lmü`tedîn.'),
(198, 2, 191, 'vaḳtülûhüm ḥayŝü ŝeḳiftümûhüm veaḫricûhüm min ḥayŝü aḫracûküm velfitnetü eşeddü mine-lḳatl. velâ tüḳâtilûhüm `inde-lmescidi-lḥarâmi ḥattâ yüḳâtilûküm fîh. fein ḳâtelûküm faḳtülûhüm. keẕâlike cezâü-lkâfirîn.'),
(199, 2, 192, 'feini-ntehev feinne-llâhe gafûrur raḥîm.'),
(200, 2, 193, 'veḳâtilûhüm ḥattâ lâ tekûne fitnetüv veyekûne-ddînü lillâh. feini-ntehev felâ `udvâne illâ `ale-żżâlimîn.'),
(201, 2, 194, 'eşşehru-lḥarâmü bişşehri-lḥarâmi velḥurumâtü ḳiṣâṣ. femeni-`tedâ `aleyküm fa`tedû `aleyhi bimiŝli me-`tedâ `aleyküm. vetteḳu-llâhe va`lemû enne-llâhe me`a-lmütteḳîn.'),
(202, 2, 195, 'veenfiḳû fî sebîli-llâhi velâ tülḳû bieydîküm ile-ttehlükeh. veaḥsinû. inne-llâhe yüḥibbü-lmuḥsinîn.'),
(203, 2, 196, 'veetimmü-lḥacce vel`umrate lillâh. fein uḥṣirtüm feme-steysera mine-lhedy. velâ taḥliḳû ruûseküm ḥattâ yeblüga-lhedyü meḥilleh. femen kâne minküm merîḍan ev bihî eẕem mir ra\'sihî fefidyetüm min ṣiyâmin ev ṣadeḳatin ev nüsük. feiẕâ emintüm. femen temette`a bil`umrati ile-lḥacci feme-steysera mine-lhedy. femel lem yecid feṣiyâmü ŝelâŝeti eyyâmin fi-lḥacci veseb`atin iẕâ raca`tüm. tilke `aşeratün kâmileh. ẕâlike limel lem yekün ehlühû ḥâḍiri-lmescidi-lḥarâm. vetteḳu-llâhe va`lemû enne-llâhe şedîdü-l`iḳâb.'),
(204, 2, 197, 'elḥaccü eşhürum ma`lûmâh. femen feraḍa fîhinne-lḥacce felâ rafeŝe velâ füsûḳa velâ cidâle fi-lḥacc. vemâ tef`alû min ḫayriy ya`lemhü-llâh. vetezevvedû feinne ḫayra-zzâdi-ttaḳvâ. vetteḳûni yâ ûli-l\'elbâb.'),
(205, 2, 198, 'leyse `aleyküm cünâḥun en tebtegû faḍlem mir rabbiküm. feiẕâ efaḍtüm min `arafâtin feẕkürü-llâhe `inde-lmeş`ari-lḥarâm. veẕkürûhü kemâ hedâküm. vein küntüm min ḳablihî lemine-ḍḍâllîn.'),
(206, 2, 199, 'ŝümme efîḍû min ḥayŝü efâḍa-nnâsü vestagfirü-llâh. inne-llâhe gafûrur raḥîm.'),
(207, 2, 200, 'feiẕâ ḳaḍaytüm menâsikeküm feẕkürü-llâhe keẕikriküm âbâeküm ev eşedde ẕikrâ. femine-nnâsi mey yeḳûlü rabbenâ âtinâ fi-ddünyâ vemâ lehû fi-l\'âḫirati min ḫalâḳ.'),
(208, 2, 201, 'veminhüm mey yeḳûlü rabbenâ âtinâ fi-ddünyâ ḥasenetev vefi-l\'âḫirati ḥasenetev veḳinâ `aẕâbe-nnâr.'),
(209, 2, 202, 'ülâike lehüm neṣîbüm mimmâ kesebû. vellâhü serî`u-lḥisâb.'),
(210, 2, 203, 'veẕkürü-llâhe fî eyyâmim ma`dûdât. femen te`accele fî yevmeyni felâ iŝme `aleyh. vemen teeḫḫara felâ iŝme `aleyhi limeni-tteḳâ. vetteḳu-llâhe va`lemû enneküm ileyhi tuḥşerûn.'),
(211, 2, 204, 'vemine-nnâsi mey yü`cibüke ḳavlühû fi-lḥayâti-ddünyâ veyüşhidü-llâhe `alâ mâ fî ḳalbihî vehüve eleddü-lḫiṣâm.'),
(212, 2, 205, 'veiẕâ tevellâ se`â fi-l\'arḍi liyüfside fîhâ veyühlike-lḥarŝe vennesl. vellâhü lâ yüḥibbü-lfesâd.'),
(213, 2, 206, 'veiẕâ ḳîle lehü-tteḳi-llâhe eḫaẕethü-l`izzetü bil\'iŝmi feḥasbühû cehennem. velebi\'se-lmihâd.'),
(214, 2, 207, 'vemine-nnâsi mey yeşrî nefsehü-btigâe merḍâti-llâh. vellâhü raûfüm bil`ibâd.'),
(215, 2, 208, 'yâ eyyühe-lleẕîne âmenü-dḫulû fi-ssilmi kâffeten. velâ tettebi`û ḫuṭuvâti-şşeyṭân. innehû leküm `adüvvüm mübîn.'),
(216, 2, 209, 'fein zeleltüm mim ba`di mâ câetkümü-lbeyyinâtü fa`lemû enne-llâhe `azîzün ḥakîm.'),
(217, 2, 210, 'hel yenżurûne illâ ey ye\'tiyehümü-llâhü fî żulelim mine-lgamâmi velmelâiketü veḳuḍiye-l\'emr. veile-llâhi türce`u-l\'ümûr.'),
(218, 2, 211, 'sel benî isrâîle kem âteynâhüm min âyetim beyyineh. vemey yübeddil ni`mete-llâhi mim ba`di mâ câethü feinne-llâhe şedîdü-l`iḳâb.'),
(219, 2, 212, 'züyyine lilleẕîne keferü-lḥayâtü-ddünyâ veyesḫarûne mine-lleẕîne âmenû. velleẕîne-tteḳav fevḳahüm yevme-lḳiyâmeh. vellâhü yerzüḳu mey yeşâü bigayri ḥisâb.'),
(220, 2, 213, 'kâne-nnâsü ümmetev vâḥideten febe`aŝe-llâhü-nnebiyyîne mübeşşirîne vemünẕirîn. veenzele me`ahümü-lkitâbe bilḥaḳḳi liyaḥküme beyne-nnâsi fîme-ḫtelefû fîh. veme-ḫtelefe fîhi ille-lleẕîne ûtûhü mim ba`di mâ câethümü-lbeyyinâtü bagyem beynehüm. fehede-llâhü-lleẕîne âmenû lime-ḫtelefû fîhi mine-lḥaḳḳi biiẕnih. vellâhü yehdî mey yeşâü ilâ ṣirâṭim müsteḳîm.'),
(221, 2, 214, 'em ḥasibtüm en tedḫulü-lcennete velemmâ ye\'tiküm meŝelü-lleẕîne ḫalev min ḳabliküm. messethümü-lbe\'sâü veḍḍarrâü vezülzilû ḥattâ yeḳûle-rrasûlü velleẕîne âmenû me`ahû metâ naṣru-llâh. elâ inne naṣra-llâhi ḳarîb.'),
(222, 2, 215, 'yes\'elûneke mâẕâ yünfiḳûn. ḳul mâ enfaḳtüm min ḫayrin felilvâlideyni vel\'aḳrabîne velyetâmâ velmesâkîni vebni-ssebîl. vemâ tef`alû min ḫayrin feinne-llâhe bihî `alîm.'),
(223, 2, 216, 'kütibe `aleykümü-lḳitâlü vehüve kürhül leküm. ve`asâ en tekrahû şey\'ev vehüve ḫayrul leküm. ve`asâ en tüḥibbû şey\'ev vehüve şerrul leküm. vellâhü ya`lemü veentüm lâ ta`lemûn.'),
(224, 2, 217, 'yes\'elûneke `ani-şşehri-lḥarâmi ḳitâlin fîh. ḳul ḳitâlün fîhi kebîr. veṣaddün `an sebîli-llâhi veküfrum bihî velmescidi-lḥarâmi veiḫrâcü ehlihî minhü ekberu `inde-llâh. velfitnetü ekberu mine-lḳatl. velâ yezâlûne yüḳâtilûneküm ḥattâ yeruddûküm `an dîniküm ini-steṭâ`û. vemey yertedid minküm `an dînihî feyemüt vehüve kâfirun feülâike ḥabiṭat a`mâlühüm fi-ddünyâ vel\'âḫirah. veülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(225, 2, 218, 'inne-lleẕîne âmenû velleẕîne hâcerû vecâhedû fî sebîli-llâhi ülâike yercûne raḥmete-llâh. vellâhü gafûrur raḥîm.'),
(226, 2, 219, 'yes\'elûneke `ani-lḫamri velmeysir. ḳul fîhimâ iŝmün kebîruv vemenâfi`u linnâs. veiŝmühümâ ekberu min nef`ihimâ. veyes\'elûneke mâẕâ yünfiḳûn. ḳuli-l`afv. keẕâlike yübeyyinü-llâhü lekümü-l\'âyâti le`alleküm tetefekkerûn.'),
(227, 2, 220, 'fi-ddünyâ vel\'âḫirah. veyes\'elûneke `ani-lyetâmâ. ḳul iṣlâḥul lehüm ḫayr. vein tüḫâliṭûhüm feiḫvânüküm. vellâhü ya`lemü-lmüfside mine-lmuṣliḥ. velev şâe-llâhü lea`neteküm. inne-llâhe `azîzün ḥakîm.'),
(228, 2, 221, 'velâ tenkiḥü-lmüşrikâti ḥattâ yü\'minn. veleemetüm mü\'minetün ḫayrum mim müşriketiv velev a`cebetküm. velâ tünkiḥü-lmüşrikîne ḥattâ yü\'minû. vele`abdüm mü\'minün ḫayrum mim müşrikiv velev a`cebeküm. ülâike yed`ûne ile-nnâr. vellâhü yed`û ile-lcenneti velmagfirati biiẕnih. veyübeyyinü âyâtihî linnâsi le`allehüm yeteẕekkerûn.'),
(229, 2, 222, 'veyes\'elûneke `ani-lmeḥîḍ. ḳul hüve eẕen fa`tezilü-nnisâe fi-lmeḥîḍi velâ taḳrabûhünne ḥattâ yaṭhürn. feiẕâ teṭahherne fe\'tûhünne min ḥayŝü emerakümü-llâh. inne-llâhe yüḥibbü-ttevvâbîne veyüḥibbü-lmüteṭahhirîn.'),
(230, 2, 223, 'nisâüküm ḥarŝül leküm. fe\'tû ḥarŝeküm ennâ şi\'tüm. veḳaddimû lienfüsiküm. vetteḳu-llâhe va`lemû enneküm mülâḳûh. vebeşşiri-lmü\'minîn.'),
(231, 2, 224, 'velâ tec`alü-llâhe `urḍatel lieymâniküm en teberrû vetetteḳû vetuṣliḥû beyne-nnâs. vellâhü semî`un `alîm.'),
(232, 2, 225, 'lâ yüâḫiẕükümü-llâhü billagvi fî eymâniküm velâkiy yüâḫiẕüküm bimâ kesebet ḳulûbüküm. vellâhü gafûrun ḥalîm.'),
(233, 2, 226, 'lilleẕîne yü\'lûne min nisâihim terabbüṣu erbe`ati eşhür. fein fâû feinne-llâhe gafûrur raḥîm.'),
(234, 2, 227, 'vein `azemu-ṭṭalâḳa feinne-llâhe semî`un `alîm.'),
(235, 2, 228, 'velmüṭalleḳâtü yeterabbaṣne bienfüsihinne ŝelâŝete ḳurû\'. velâ yeḥillü lehünne ey yektümne mâ ḫaleḳa-llâhü fî erḥâmihinne in künne yü\'minne billâhi velyevmi-l\'âḫir. vebü`ûletühünne eḥaḳḳu biraddihinne fî ẕâlike in erâdû iṣlâḥâ. velehünne miŝlü-lleẕî `aleyhinne bilma`rûf. velirricâli `aleyhinne deraceh. vellâhü `azîzün ḥakîm.'),
(236, 2, 229, 'eṭṭalâḳu merratân. feimsâküm bima`rûfin ev tesrîḥum biiḥsân. velâ yeḥillü leküm en te\'ḫuẕû mimmâ âteytümûhünne şey\'en illâ ey yeḫâfâ ellâ yüḳîmâ ḥudûde-llâh. fein ḫiftüm ellâ yüḳîmâ ḥudûde-llâhi felâ cünâḥa `aleyhimâ fîme-ftedet bih. tilke ḥudûdü-llâhi felâ ta`tedûhâ. vemey yete`adde ḥudûde-llâhi feülâike hümu-żżâlimûn.'),
(237, 2, 230, 'fein ṭalleḳahâ felâ teḥillü lehû mim ba`dü ḥattâ tenkiḥa zevcen gayrah. fein ṭalleḳahâ felâ cünâḥa `aleyhimâ ey yeterâce`â in żannâ ey yüḳîmâ ḥudûde-llâh. vetilke ḥudûdü-llâhi yübeyyinühâ liḳavmiy ya`lemûn.'),
(238, 2, 231, 'veiẕâ ṭallaḳtümü-nnisâe febelagne ecelehünne feemsikûhünne bima`rûfin ev serriḥûhünne bima`rûf. velâ tümsikûhünne ḍirâral lita`tedû. vemey yef`al ẕâlike feḳad żaleme nefseh. velâ tetteḫiẕû âyâti-llâhi hüzüvâ. veẕkürû ni`mete-llâhi `aleyküm vemâ enzele `aleyküm mine-lkitâbi velḥikmeti ye`iżuküm bih. vetteḳu-llâhe va`lemû enne-llâhe bikülli şey\'in `alîm.'),
(239, 2, 232, 'veiẕâ ṭallaḳtümü-nnisâe febelagne ecelehünne felâ ta`ḍulûhünne ey yenkiḥne ezvâcehünne iẕâ terâḍav beynehüm bilma`rûf. ẕâlike yû`ażu bihî men kâne minküm yü\'minü billâhi velyevmi-l\'âḫir. ẕâliküm ezkâ leküm veaṭher. vellâhü ya`lemü veentüm lâ ta`lemûn.'),
(240, 2, 233, 'velvâlidâtü yürḍi`ne evlâdehünne ḥavleyni kâmileyni limen erâde ey yütimme-rraḍâ`ah. ve`ale-lmevlûdi lehû rizḳuhünne vekisvetühünne bilma`rûf. lâ tükellefü nefsün illâ vus`ahâ. lâ tüḍârra vâlidetüm biveledihâ velâ mevlûdül lehû biveledihî ve`ale-lvâriŝi miŝlü ẕâlik. fein erâdâ fiṣâlen `an terâḍim minhümâ veteşâvurin felâ cünâḥa `aleyhimâ. vein erattüm en testerḍi`û evlâdeküm felâ cünâḥa `aleyküm iẕâ sellemtüm mâ âteytüm bilma`rûf. vetteḳu-llâhe va`lemû enne-llâhe bimâ ta`melûne beṣîr.'),
(241, 2, 234, 'velleẕîne yüteveffevne minküm veyeẕerûne ezvâcey yeterabbaṣne bienfüsihinne erbe`ate eşhüriv ve`aşrâ. feiẕâ belagne ecelehünne felâ cünâḥa `aleyküm fîmâ fe`alne fî enfüsihinne bilma`rûf. vellâhü bimâ ta`melûne ḫabîr.'),
(242, 2, 235, 'velâ cünâḥa `aleyküm fîmâ `arraḍtüm bihî min ḫiṭbeti-nnisâi ev eknentüm fî enfüsiküm. `alime-llâhü enneküm seteẕkürûnehünne velâkil lâ tüvâ`idûhünne sirran illâ en teḳûlû ḳavlem ma`rûfâ. velâ ta`zimû `uḳdete-nnikâḥi ḥattâ yeblüga-lkitâbü eceleh. va`lemû enne-llâhe ya`lemü mâ fî enfüsiküm faḥẕerûh. va`lemû enne-llâhe gafûrun ḥalîm.'),
(243, 2, 236, 'lâ cünâḥa `aleyküm in ṭallaḳtümü-nnisâe mâ lem temessûhünne ev tefriḍû lehünne ferîḍah. vemetti`ûhünn. `ale-lmûsi`i ḳaderuhû ve`ale-lmuḳtiri ḳaderuhü. metâ`am bilma`rûf. ḥaḳḳan `ale-lmuḥsinîn.'),
(244, 2, 237, 'vein ṭallaḳtümûhünne min ḳabli en temessûhünne veḳad feraḍtüm lehünne ferîḍaten feniṣfü mâ feraḍtüm illâ ey ya`fûne ev ya`füve-lleẕî biyedihî `uḳdetü-nnikâḥ. veen ta`fû aḳrabü littaḳvâ. velâ tensevu-lfaḍle beyneküm. inne-llâhe bimâ ta`melûne beṣîr.'),
(245, 2, 238, 'ḥâfiżû `ale-ṣṣalâvâti veṣṣalâti-lvusṭâ veḳûmû lillâhi ḳânitîn.'),
(246, 2, 239, 'fein ḫiftüm fericâlen ev rukbânâ. feiẕâ emintüm feẕkürü-llâhe kemâ `allemeküm mâ lem tekûnû ta`lemûn.'),
(247, 2, 240, 'velleẕîne yüteveffevne minküm veyeẕerûne ezvâcâ. veṣiyyetel liezvâcihim metâ`an ile-lḥavli gayra iḫrâc. fein ḫaracne felâ cünâḥa `aleyküm fî mâ fe`alne fî enfüsihinne mim ma`rûf. vellâhü `azîzün ḥakîm.'),
(248, 2, 241, 'velilmüṭalleḳâti metâ`um bilma`rûf. ḥaḳḳan `ale-lmütteḳîn.'),
(249, 2, 242, 'keẕâlike yübeyyinü-llâhü leküm âyâtihî le`alleküm ta`ḳilûn.'),
(250, 2, 243, 'elem tera ile-lleẕîne ḫaracû min diyârihim vehüm ülûfün ḥaẕera-lmevt. feḳâle lehümü-llâhü mûtû ŝümme aḥyâhüm. inne-llâhe leẕû faḍlin `ale-nnâsi velâkinne ekŝera-nnâsi lâ yeşkürûn.'),
(251, 2, 244, 'veḳâtilû fî sebîli-llâhi va`lemû enne-llâhe semî`un `alîm.'),
(252, 2, 245, 'men ẕe-lleẕî yuḳriḍu-llâhe ḳarḍan ḥasenen feyüḍâ`ifehû lehû aḍ`âfen keŝîrah. vellâhü yaḳbiḍu veyebsüṭ. veileyhi türce`ûn.'),
(253, 2, 246, 'elem tera ile-lmelei mim benî isrâîle mim ba`di mûsâ. iẕ ḳâlû linebiyyil lehümü-b`aŝ lenâ meliken nüḳâtil fî sebîli-llâh. ḳâle hel `aseytüm in kütibe `aleykümü-lḳitâlü ellâ tüḳâtilû. ḳâlû vemâ lenâ ellâ nüḳâtile fî sebîli-llâhi veḳad uḫricnâ min diyârinâ veebnâinâ. felemmâ kütibe `aleyhimü-lḳitâlü tevellev illâ ḳalîlem minhüm. vellâhü `alîmüm biżżâlimîn.'),
(254, 2, 247, 'veḳâle lehüm nebiyyühüm inne-llâhe ḳad be`aŝe leküm ṭâlûte melikâ. ḳâlû ennâ yekûnü lehü-lmülkü `aleynâ venaḥnü eḥaḳḳu bilmülki minhü velem yü\'te se`atem mine-lmâl. ḳâle inne-llâhe-ṣṭafâhü `aleyküm vezâdehû besṭaten fi-l`ilmi velcism. vellâhü yü\'tî mülkehû mey yeşâ\'. vellâhü vâsi`un `alîm.'),
(255, 2, 248, 'veḳâle lehüm nebiyyühüm inne âyete mülkihî ey ye\'tiyekümü-ttâbûtü fîhi sekînetüm mir rabbiküm vebeḳiyyetüm mimmâ terake âlü mûsâ veâlü hârûne taḥmilühü-lmelâikeh. inne fî ẕâlike leâyetel leküm in küntüm mü\'minîn.'),
(256, 2, 249, 'felemmâ feṣale ṭâlûtü bilcünûdi ḳâle inne-llâhe mübtelîküm bineher. femen şeribe minhü feleyse minnî. vemel lem yaṭ`amhü feinnehû minnî illâ meni-gterafe gurfetem biyedih. feşeribû minhü illâ ḳalîlem minhüm. felemmâ câvezehû hüve velleẕîne âmenû me`ahû ḳâlû lâ ṭâḳate lene-lyevme bicâlûte vecünûdih. ḳâle-lleẕîne yeżunnûne ennehüm mülâḳu-llâhi kem min fietin ḳalîletin galebet fieten keŝîratem biiẕni-llâh. vellâhü me`a-ṣṣâbirîn.'),
(257, 2, 250, 'velemmâ berazû licâlûte vecünûdihî ḳâlû rabbenâ efrig `aleynâ ṣabrav veŝebbit aḳdâmenâ venṣurnâ `ale-lḳavmi-lkâfirîn.'),
(258, 2, 251, 'fehezemûhüm biiẕni-llâhi veḳatele dâvûdü câlûte veâtâhü-llâhü-lmülke velḥikmete ve`allemehû mimmâ yeşâ\'. velevlâ def`u-llâhi-nnâse ba`ḍahüm biba`ḍil lefesedeti-l\'arḍu velâkinne-llâhe ẕû faḍlin `ale-l`âlemîn.'),
(259, 2, 252, 'tilke âyâtü-llâhi netlûhâ `aleyke bilḥaḳḳ. veinneke lemine-lmürselîn.'),
(260, 2, 253, 'tilke-rrusülü feḍḍalnâ ba`ḍahüm `alâ ba`ḍ. minhüm men kelleme-llâhü verafe`a ba`ḍahüm deracât. veâteynâ `îse-bne meryeme-lbeyyinâti veeyyednâhü birûḥi-lḳudüs. velev şâe-llâhü me-ḳtetele-lleẕîne mim ba`dihim mim ba`di mâ câethümü-lbeyyinâtü velâkini-ḫtelefû feminhüm men âmene veminhüm men kefer. velev şâe-llâhü me-ḳtetelû velâkinne-llâhe yef`alü mâ yürîd.'),
(261, 2, 254, 'yâ eyyühe-lleẕîne âmenû enfiḳû mimmâ razaḳnâküm min ḳabli ey ye\'tiye yevmül lâ bey`un fîhi velâ ḫulletüv velâ şefâ`ah. velkâfirûne hümu-żżâlimûn.'),
(262, 2, 255, 'allâhü lâ ilâhe illâ hû. elḥayyü-lḳayyûm. lâ te\'ḫuẕühû sinetüv velâ nevm. lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. men ẕe-lleẕî yeşfe`u `indehû illâ biiẕnih. ya`lemü mâ beyne eydîhim vemâ ḫalfehüm. velâ yüḥîṭûne bişey\'im min `ilmihî illâ bimâ şâ\'. vesi`a kürsiyyühü-ssemâvâti vel\'arḍ. velâ yeûdühû ḥifżuhümâ. vehüve-l`aliyyü-l`ażîm.'),
(263, 2, 256, 'lâ ikrâhe fi-ddîni ḳat tebeyyene-rruşdü mine-lgayy. femey yekfür biṭṭâgûti veyü\'mim billâhi feḳadi-stemseke bil`urveti-lvuŝḳâ. le-nfiṣâme lehâ. vellâhü semî`un `alîm.'),
(264, 2, 257, 'allâhü veliyyü-lleẕîne âmenû yuḫricühüm mine-żżulümâti ile-nnûr. velleẕîne keferû evliyâühümu-ṭṭâgûtü yuḫricûnehüm mine-nnûri ile-żżulümât. ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(265, 2, 258, 'elem tera ile-lleẕî ḥâcce ibrâhîme fî rabbihî en âtâhü-llâhü-lmülk. iẕ ḳâle ibrâhîmü rabbiye-lleẕî yuḥyî veyümîtü ḳâle ene uḥyî veümît. ḳâle ibrâhîmü feinne-llâhe ye\'tî bişşemsi mine-lmeşriḳi fe\'ti bihâ mine-lmagribi febühite-lleẕî kefer. vellâhü lâ yehdi-lḳavme-żżâlimîn.'),
(266, 2, 259, 'ev kelleẕî merra `alâ ḳaryetiv vehiye ḫâviyetün `alâ `urûşihâ. ḳâle ennâ yuḥyî hâẕihi-llâhü ba`de mevtihâ. feemâtehü-llâhü miete `âmin ŝümme be`aŝeh. ḳâle kem lebiŝt. ḳâle lebiŝtü yevmen ev ba`ḍa yevm. ḳâle bel lebiŝte miete `âmin fenżur ilâ ṭa`âmike veşerâbike lem yetesenneh. venżur ilâ ḥimârike velinec`aleke âyetel linnâsi venżur ile-l`iżâmi keyfe nünşizühâ ŝümme neksûhâ laḥmâ. felemmâ tebeyyene lehû ḳâle a`lemü enne-llâhe `alâ külli şey\'in ḳadîr.'),
(267, 2, 260, 'veiẕ ḳâle ibrâhîmü rabbi erinî keyfe tuḥyi-lmevtâ. ḳâle evelem tü\'min. ḳâle belâ velâkil liyaṭmeinne ḳalbî. ḳâle feḫuẕ erbe`atem mine-ṭṭayri feṣurhünne ileyke ŝümme-c`al `alâ külli cebelim minhünne cüz\'en ŝümme-d`uhünne ye\'tîneke sa`yâ. va`lem enne-llâhe `azîzün ḥakîm.'),
(268, 2, 261, 'meŝelü-lleẕîne yünfiḳûne emvâlehüm fî sebîli-llâhi kemeŝeli ḥabbetin embetet seb`a senâbile fî külli sümbületim mietü ḥabbeh. vellâhü yüḍâ`ifü limey yeşâ\'. vellâhü vâsi`un `alîm.'),
(269, 2, 262, 'elleẕîne yünfiḳûne emvâlehüm fî sebîli-llâhi ŝümme lâ yütbi`ûne mâ enfeḳû mennev velâ eẕel lehüm ecruhüm `inde rabbihim. velâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(270, 2, 263, 'ḳavlüm ma`rûfüv vemagfiratün ḫayrum min ṣadeḳatiy yetbe`uhâ eẕâ. vellâhü ganiyyün ḥalîm.'),
(271, 2, 264, 'yâ eyyühe-lleẕîne âmenû lâ tübṭilû ṣadeḳâtiküm bilmenni vel\'eẕâ kelleẕî yünfiḳu mâlehû riâe-nnâsi velâ yü\'minü billâhi velyevmi-l\'âḫir. femeŝelühû kemeŝeli ṣafvânin `aleyhi türâbün feeṣâbehû vâbilün feterakehû ṣaldâ. lâ yaḳdirûne `alâ şey\'im mimmâ kesebû. vellâhü lâ yehdi-lḳavme-lkâfirîn.'),
(272, 2, 265, 'vemeŝelü-lleẕîne yünfiḳûne emvâlehümü-btigâe merḍâti-llâhi veteŝbîtem min enfüsihim kemeŝeli cennetim birabvetin eṣâbehâ vâbilün feâtet ükülehâ ḍi`feyn. feil lem yüṣibhâ vâbilün feṭall. vellâhü bimâ ta`melûne beṣîr.'),
(273, 2, 266, 'eyeveddü eḥadüküm en tekûne lehû cennetüm min neḫîliv vea`nâbin tecrî min taḥtihe-l\'enhâru lehû fîhâ min külli-ŝŝemerâti veeṣâbehü-lkiberu velehû ẕürriyyetün ḍu`afâ\'. feeṣâbehâ i`ṣârun fîhi nârun faḥteraḳat. keẕâlike yübeyyinü-llâhü lekümü-l\'âyâti le`alleküm tetefekkerûn.'),
(274, 2, 267, 'yâ eyyühe-lleẕîne âmenû enfiḳû min ṭayyibâti mâ kesebtüm vemimmâ aḫracnâ leküm mine-l\'arḍ. velâ teyemmemü-lḫabîŝe minhü tünfiḳûne velestüm biâḫiẕîhi illâ en tugmiḍû fîh. va`lemû enne-llâhe ganiyyün ḥamîd.'),
(275, 2, 268, 'eşşeyṭânü ye`idükümü-lfaḳra veye\'müruküm bilfaḥşâ\'. vellâhü ye`idüküm magfiratem minhü vefaḍlâ. vellâhü vâsi`un `alîm.'),
(276, 2, 269, 'yü\'ti-lḥikmete mey yeşâ\'. vemey yü\'te-lḥikmete feḳad ûtiye ḫayran keŝîâ. vemâ yeẕẕekkeru illâ ülü-l\'elbâb.'),
(277, 2, 270, 'vemâ enfaḳtüm min nefeḳatin ev neẕertüm min neẕrin feinne-llâhe ya`lemüh. vemâ liżżâlimîne min enṣâr.'),
(278, 2, 271, 'in tübdu-ṣṣadeḳâti feni`immâ hî. vein tuḫfûhâ vetü\'tûhe-lfüḳarâe fehüve ḫayrul leküm. veyükeffiru `anküm min seyyiâtiküm. vellâhü bimâ ta`melûne ḫabîr.'),
(279, 2, 272, 'leyse `aleyke hüdâhüm velâkinne-llâhe yehdî mey yeşâ\'. vemâ tünfiḳû min ḫayrin felienfüsiküm. vemâ tünfiḳûne ille-btigâe vechi-llâh. vemâ tünfiḳû min ḫayriy yüveffe ileyküm veentüm lâ tużlemûn.'),
(280, 2, 273, 'lilfüḳarâi-lleẕîne uḥṣirû fî sebîli-llâhi lâ yesteṭî`ûne ḍarben fi-l\'arḍ. yaḥsebühümü-lcâhilü agniyâe mine-tte`affüf. ta`rifühüm bisîmâhüm. lâ yes\'elûne-nnâse ilḥâfâ. vemâ tünfiḳû min ḫayrin feinne-llâhe bihî `alîm.'),
(281, 2, 274, 'elleẕîne yünfiḳûne emvâlehüm billeyli vennehâri sirrav ve`alâniyeten felehüm ecruhüm `inde rabbihim. velâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(282, 2, 275, 'elleẕîne ye\'külûne-rribâ lâ yeḳûmûne illâ kemâ yeḳûmü-lleẕî yeteḫabbeṭuhü-şşeyṭânü mine-lmess. ẕâlike biennehüm ḳâlû inneme-lbey`u miŝlü-rribâ. veeḥalle-llâhü-lbey`a veḥarrame-rribâ. femen câehû mev`iżatüm mir rabbihî fentehâ felehû mâ selef. veemruhû ile-llâh. vemen `âde feülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(283, 2, 276, 'yemḥaḳu-llâhü-rribâ veyürbi-ṣṣadeḳâti. vellâhü lâ yüḥibbü külle keffârin eŝîm.'),
(284, 2, 277, 'inne-lleẕîne âmenû ve`amilu-ṣṣâliḥâti veeḳâmu-ṣṣalâte veâtevu-zzekâte lehüm ecruhüm `inde rabbihim. velâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(285, 2, 278, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe veẕerû mâ beḳiye mine-rribâ in küntüm mü\'minîn.'),
(286, 2, 279, 'feil lem tef`alû fe\'ẕenû biḥarbim mine-llâhi verasûlih. vein tübtüm feleküm ruûsü emvâliküm. lâ tażlimûne velâ tużlemûn.'),
(287, 2, 280, 'vein kâne ẕû `usratin feneżiratün ilâ meyserah. veen teṣaddeḳû ḫayrul leküm in küntüm ta`lemûn.'),
(288, 2, 281, 'vetteḳû yevmen türce`ûne fîhi ile-llâhi ŝümme tüveffâ küllü nefsim mâ kesebet vehüm lâ yużlemûn.'),
(289, 2, 282, 'yâ eyyühe-lleẕîne âmenû iẕâ tedâyentüm bideynin ilâ ecelim müsemmen fektübûh. velyektüb beyneküm kâtibüm bil`adl. velâ ye\'be kâtibün ey yektübe kemâ `allemehü-llâhü felyektüb. velyümlili-lleẕî `aleyhi-lḥaḳḳu velyetteḳi-llâhe rabbehû velâ yebḫas minhü şey\'â. fein kâne-lleẕî `aleyhi-lḥaḳḳu sefîhen ev ḍa`îfen ev lâ yesteṭî`u ey yümille hüve felyümlil veliyyühû bil`adl. vesteşhidû şehîdeyni mir ricâliküm. feil lem yekûnâ racüleyni feracülüv vemraetâni mimmen terḍavne mine-şşühedâi en teḍille iḥdâhümâ fetüẕekkira iḥdâhüme-l\'uḫrâ. velâ ye\'be-şşühedâü iẕâ mâ dü`û. velâ tes\'emû en tektübûhü ṣagîran ev kebîran ilâ ecelih. ẕâliküm aḳseṭu `inde-llâhi veaḳvemü lişşehâdeti veednâ ellâ tertâbû illâ en tekûne ticâraten ḥâḍiraten tüdîrûnehâ beyneküm feleyse `aleyküm cünâḥun ellâ tektübûhâ. veeşhidû iẕâ tebâya`tüm. velâ yüḍârra kâtibüv velâ şehîd. vein tef`alû feinnehû füsûḳum biküm. vetteḳu-llâh. veyü`allimükümü-llâh. vellâhü bikülli şey\'in `alîm.'),
(290, 2, 283, 'vein küntüm `alâ seferiv velem tecidû kâtiben ferihânüm maḳbûḍah. fein emine ba`ḍuküm ba`ḍan felyüeddi-lleẕi-\'tümine emânetehû velyetteḳi-llâhe rabbeh. velâ tektümü-şşehâdet. vemey yektümhâ feinnehû âŝimün ḳalbüh. vellâhü bimâ ta`melûne `alîm.'),
(291, 2, 284, 'lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vein tübdû mâ fî enfüsiküm ev tuḫfûhü yüḥâsibküm bihi-llâh. feyagfiru limey yeşâü veyü`aẕẕibü mey yeşâ\'. vellâhü `alâ külli şey\'in ḳadîr.'),
(292, 2, 285, 'âmene-rrasûlü bimâ ünzile ileyhi mir rabbihî velmü\'minûn. küllün âmene billâhi vemelâiketihî vekütübihî verusülih. lâ nüferriḳu beyne eḥadim mir rusülih. veḳâlû semi`nâ veeṭa`nâ gufrâneke rabbenâ veileyke-lmeṣîr.'),
(293, 2, 286, 'lâ yükellifü-llâhü nefsen illâ vus`ahâ. lehâ mâ kesebet ve`aleyhâ me-ktesebet. rabbenâ lâ tüâḫiẕnâ in nesînâ ev aḫṭa\'nâ. rabbenâ velâ taḥmil `aleynâ iṣran kemâ ḥameltehû `ale-lleẕîne min ḳablinâ. rabbenâ velâ tüḥammilnâ mâ lâ ṭâḳate lenâ bih. va`fü `annâ. vagfir lenâ. verḥamnâ. ente mevlânâ fenṣurnâ `ale-lḳavmi-lkâfirîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'elif-lâm-mîm.'),
(295, 3, 2, 'allâhü lâ ilâhe illâ hüve-lḥayyü-lḳayyûm.'),
(296, 3, 3, 'nezzele `aleyke-lkitâbe bilḥaḳḳi müṣaddiḳal limâ beyne yedeyhi veenzele-ttevrâte vel\'incîl.'),
(297, 3, 4, 'min ḳablü hüdel linnâsi veenzele-lfürḳân. inne-lleẕîne keferû biâyâti-llâhi lehüm `aẕâbün şedîd. vellâhü `azîzün ẕü-ntiḳâm.'),
(298, 3, 5, 'inne-llâhe lâ yaḫfâ `aleyhi şey\'ün fi-l\'arḍi velâ fi-ssemâ\'.'),
(299, 3, 6, 'hüve-lleẕî yüṣavviruküm fi-l\'erḥâmi keyfe yeşâ\'. lâ ilâhe illâ hüve-l`azîzü-lḥakîm.'),
(300, 3, 7, 'hüve-lleẕî enzele `aleyke-lkitâbe minhü âyâtüm muḥkemâtün hünne ümmü-lkitâbi veüḫaru müteşâbihâh. feemme-lleẕîne fî ḳulûbihim zeygun feyettebi`ûne mâ teşâbehe minhü-btigâe-lfitneti vebtigâe te\'vîlih. vemâ ya`lemü te\'vîlehû ille-llâh. verrâsiḫûne fi-l`ilmi yeḳûlûne âmennâ bihî küllüm min `indi rabbinâ. vemâ yeẕẕekkeru illâ ülü-l\'elbâb.'),
(301, 3, 8, 'rabbenâ lâ tüzig ḳulûbenâ ba`de iẕ hedeytenâ veheb lenâ mil ledünke raḥmeh. inneke ente-lvehhâb.'),
(302, 3, 9, 'rabbenâ inneke câmi`u-nnâsi liyevmil lâ raybe fîh. inne-llâhe lâ yuḫlifü-lmî`âd.'),
(303, 3, 10, 'inne-lleẕîne keferû len tugniye `anhüm emvâlühüm velâ evlâdühüm mine-llâhi şey\'â. veülâike hüm veḳûdü-nnâr.'),
(304, 3, 11, 'kede\'bi âli fir`avne velleẕîne min ḳablihim. keẕẕebû biâyâtinâ. feeḫaẕehümü-llâhü biẕünûbihim. vellâhü şedîdü-l`iḳâb.'),
(305, 3, 12, 'ḳul lilleẕîne keferû setuglebûne vetuḥşerûne ilâ cehennem. vebi\'se-lmihâd.'),
(306, 3, 13, 'ḳad kâne leküm âyetün fî fieteyni-lteḳatâ. fietün tüḳâtilü fî sebîli-llâhi veuḫrâ kâfiratüy yeravnehüm miŝleyhim ra\'ye-l`ayn. vellâhü yü\'eyyidü binaṣrihî mey yeşâ\'. inne fî ẕâlike le`ibratel liüli-l\'ebṣâr.'),
(307, 3, 14, 'züyyine linnâsi ḥubbü-şşehevâti mine-nnisâi velbenîne velḳanâṭîri-lmüḳanṭarati mine-ẕẕehebi velfiḍḍati velḫayli-lmüsevvemeti vel\'en`âmi velḥarŝ. ẕâlike metâ`u-lḥayâti-ddünyâ. vellâhü `indehû ḥusnü-lmeâb.'),
(308, 3, 15, 'ḳul eünebbiüküm biḫayrim min ẕâliküm. lilleẕîne-tteḳav `inde rabbihim cennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ veezvâcüm müṭahherâtüv veriḍvânüm mine-llâh. vellâhü beṣîrum bil`ibâd.'),
(309, 3, 16, 'elleẕîne yeḳûlûne rabbenâ innenâ âmennâ fagfir lenâ ẕünûbenâ veḳinâ `aẕâbe-nnâr.'),
(310, 3, 17, 'eṣṣâbirîne veṣṣâdiḳîne velḳânitîne velmünfiḳîne velmüstagfirîne bil\'esḥâr.'),
(311, 3, 18, 'şehide-llâhü ennehû lâ ilâhe illâ hüve velmelâiketü veülü-l`ilmi ḳâimem bilḳisṭ. lâ ilâhe illâ hüve-l`azîzü-lḥakîm.'),
(312, 3, 19, 'inne-ddîne `inde-llâhi-l\'islâm. veme-ḫtelefe-lleẕîne ûtü-lkitâbe illâ mim ba`di mâ câehümü-l`ilmü bagyem beynehüm. vemey yekfür biâyâti-llâhi feinne-llâhe serî`u-lḥisâb.'),
(313, 3, 20, 'fein ḥâccûke feḳul eslemtü vechiye lillâhi vemeni-ttebe`an. veḳul lilleẕîne ûtü-lkitâbe vel\'ümmiyyîne eeslemtüm. fein eslemû feḳadi-htedev. vein tevellev feinnemâ `aleyke-lbelâg. vellâhü beṣîrum bil`ibâd.'),
(314, 3, 21, 'inne-lleẕîne yekfürûne biâyâti-llâhi veyaḳtülûne-nnebiyyîne bigayri ḥaḳḳiv veyaḳtülûne-lleẕîne ye\'mürûne bilḳisṭi mine-nnâsi febeşşirhüm bi`aẕâbin elîm.'),
(315, 3, 22, 'ülâike-lleẕîne ḥabiṭat a`mâlühüm fi-ddünyâ vel\'âḫirah. vemâ lehüm min nâṣirîn.'),
(316, 3, 23, 'elem tera ile-lleẕîne ûtû neṣîbem mine-lkitâbi yüd`avne ilâ kitâbi-llâhi liyaḥküme beynehüm ŝümme yetevellâ ferîḳum minhüm vehüm mü`riḍûn.'),
(317, 3, 24, 'ẕâlike biennehüm ḳâlû len temessene-nnâru illâ eyyâmem ma`dûdât. vegarrahüm fî dînihim mâ kânû yefterûn.'),
(318, 3, 25, 'fekeyfe iẕâ cema`nâhüm liyevmil lâ raybe fîhi vevuffiyet küllü nefsim mâ kesebet vehüm lâ yużlemûn.'),
(319, 3, 26, 'ḳuli-llâhümme mâlike-lmülki tü\'ti-lmülke men teşâü vetenzi`u-lmülke mimmen teşâ\'. vetü`izzü men teşâü vetüẕillü men teşâ\'. biyedike-lḫayr. inneke `alâ külli şey\'in ḳadîr.'),
(320, 3, 27, 'tûlicü-lleyle fi-nnehâri vetûlicü-nnehâra fi-lleyl. vetuḫricü-lḥayye mine-lmeyyiti vetuḫricü-lmeyyite mine-lḥayy. veterzüḳu men teşâü bigayri ḥisâb.'),
(321, 3, 28, 'lâ yetteḫiẕi-lmü\'minûne-lkâfirîne evliyâe min dûni-lmü\'minîn. vemey yef`al ẕâlike feleyse mine-llâhi fî şey\'in illâ en tetteḳû minhüm tüḳâh. veyüḥaẕẕirukümü-llâhü nefseh. veile-llâhi-lmeṣîr.'),
(322, 3, 29, 'ḳul in tuḫfû mâ fî ṣudûriküm ev tübdûhü ya`lemhü-llâh. veya`lemü mâ fi-ssemâvâti vemâ fi-l\'arḍ. vellâhü `alâ külli şey\'in ḳadîr.'),
(323, 3, 30, 'yevme tecidü küllü nefsim mâ `amilet min ḫayrim muḥḍarâ. vemâ `amilet min sû\'. teveddü lev enne beynehâ vebeynehû emedem be`îdâ. veyüḥaẕẕirukümü-llâhü nefseh. vellâhü raûfüm bil`ibâd.'),
(324, 3, 31, 'ḳul in küntüm tüḥibbûne-llâhe fettebi`ûnî yuḥbibkümü-llâhü veyagfir leküm ẕünûbeküm. vellâhü gafûrur raḥîm.'),
(325, 3, 32, 'ḳul eṭî`ü-llâhe verrasûl. fein tevellev feinne-llâhe lâ yüḥibbü-lkâfirîn.'),
(326, 3, 33, 'inne-llâhe-ṣṭafâ âdeme venûḥav veâle ibrâhîme veâle `imrâne `ale-l`âlemîn.'),
(327, 3, 34, 'ẕürriyyetem ba`ḍuhâ mim ba`ḍ. vellâhü semî`un `alîm.'),
(328, 3, 35, 'iẕ ḳâleti-mraetü `imrâne rabbi innî neẕertü leke mâ fî baṭnî müḥarraran feteḳabbel minnî. inneke ente-ssemî`u-l`alîm.'),
(329, 3, 36, 'felemmâ veḍa`athâ ḳâlet rabbi innî veḍa`tühâ ünŝâ. vellâhü a`lemü bimâ veḍa`at. veleyse-ẕẕekeru kel\'ünŝâ. veinnî semmeytühâ meryeme veinnî ü`îẕühâ bike veẕürriyyetehâ mine-şşeyṭâni-rracîm.'),
(330, 3, 37, 'feteḳabbelehâ rabbühâ biḳabûlin ḥaseniv veembetehâ nebâten ḥasenev vekeffelehâ zekeriyyâ. küllemâ deḫale `aleyhâ zekeriyye-lmiḥrâbe vecede `indehâ rizḳan. ḳâle yâ meryemü ennâ leki hâẕâ. ḳâlet hüve min `indi-llâh. inne-llâhe yerzüḳu mey yeşâü bigayri ḥisâb.'),
(331, 3, 38, 'hünâlike de`â zekeriyyâ rabbeh. ḳâle rabbi heb lî mil ledünke ẕürriyyeten ṭayyibeh. inneke semî`u-ddü`â\'.'),
(332, 3, 39, 'fenâdethü-lmelâiketü vehüve ḳâimüy yüṣallî fi-lmiḥrâbi enne-llâhe yübeşşiruke biyaḥyâ müṣaddiḳam bikelimetim mine-llâhi veseyyidev veḥaṣûrav venebiyyem mine-ṣṣâliḥîn.'),
(333, 3, 40, 'ḳâle rabbi ennâ yekûnü lî gulâmüv veḳad beleganiye-lkiberu vemraetî `âḳir. ḳâle keẕâlike-llâhü yef`alü mâ yeşâ\'.'),
(334, 3, 41, 'ḳâle rabbi-c`al lî âyeh. ḳâle âyetüke ellâ tükellime-nnâse ŝelâŝete eyyâmin illâ ramzâ. veẕkür rabbeke keŝîrav vesebbiḥ bil`aşiyyi vel\'ibkâr.'),
(335, 3, 42, 'veiẕ ḳâleti-lmelâiketü yâ meryemü inne-llâhe-ṣṭafâki veṭahheraki vaṣṭafâki `alâ nisâi-l`âlemîn.'),
(336, 3, 43, 'yâ meryemu-ḳnütî lirabbiki vescüdî verke`î me`a-rrâki`în.'),
(337, 3, 44, 'ẕâlike min embâi-lgaybi nûḥîhi ileyk. vemâ künte ledeyhim iẕ yülḳûne aḳlâmehüm eyyühüm yekfülü meryem. vemâ künte ledeyhim iẕ yaḫteṣimûn.'),
(338, 3, 45, 'iẕ ḳâleti-lmelâiketü yâ meryemü inne-llâhe yübeşşiruki bikelimetim minh. ismühü-lmesîḥu `îse-bnü meryeme vecîhen fi-ddünyâ vel\'âḫirati vemine-lmüḳarrabîn.'),
(339, 3, 46, 'veyükellimü-nnâse fi-lmehdi vekehlev vemine-ṣṣâliḥîn.'),
(340, 3, 47, 'ḳâlet rabbi ennâ yekûnü lî veledüv velem yemsesnî beşer. ḳâle keẕâliki-llâhü yaḫlüḳu mâ yeşâ\'. iẕâ ḳaḍâ emran feinnemâ yeḳûlü lehû kün feyekûn.'),
(341, 3, 48, 'veyü`allimühü-lkitâbe velḥikmete vettevrâte vel\'incîl.'),
(342, 3, 49, 'verasûlen ilâ benî isrâîle ennî ḳad ci\'tüküm biâyetim mir rabbiküm ennî aḫlüḳu leküm mine-ṭṭîni kehey\'eti-ṭṭayri feenfüḫu fîhi feyekûnü ṭayram biiẕni-llâh. veübriü-l\'ekmehe vel\'ebraṣa veuḥyi-lmevtâ biiẕni-llâh. veünebbiüküm bimâ te\'külûne vemâ teddeḫirûne fî büyûtiküm. inne fî ẕâlike leâyetel leküm in küntüm mü\'minîn.'),
(343, 3, 50, 'vemüṣaddiḳal limâ beyne yedeyye mine-ttevrâti veliüḥille leküm ba`ḍa-lleẕî ḥurrime `aleyküm veci\'tüküm biâyetim mir rabbiküm fetteḳu-llâhe veeṭî`ûn.'),
(344, 3, 51, 'inne-llâhe rabbî verabbüküm fa`büdûh. hâẕâ ṣirâṭum müsteḳîm.'),
(345, 3, 52, 'felemmâ eḥasse `îsâ minhümü-lküfra ḳâle men enṣârî ile-llâh. ḳâle-lḥavâriyyûne naḥnü enṣâru-llâh. âmennâ billâh. veşhed biennâ müslimûn.'),
(346, 3, 53, 'rabbenâ âmennâ bimâ enzelte vetteba`ne-rrasûle fektübnâ me`a-şşâhidîn.'),
(347, 3, 54, 'vemekerû vemekera-llâh. vellâhü ḫayru-lmâkirîn.'),
(348, 3, 55, 'iẕ ḳâle-llâhü yâ `îsâ innî müteveffîke verâfi`uke ileyye vemüṭahhiruke mine-lleẕîne keferû vecâ`ilü-lleẕîne-ttebe`ûke fevḳa-lleẕîne keferû ilâ yevmi-lḳiyâmeh. ŝümme ileyye merci`uküm feaḥkümü beyneküm fîmâ küntüm fîhi taḫtelifûn.'),
(349, 3, 56, 'feemme-lleẕîne keferû feü`aẕẕibühüm `aẕâben şedîden fi-ddünyâ vel\'âḫirah. vemâ lehüm min nâṣirîn.'),
(350, 3, 57, 'veemme-lleẕîne âmenû ve`amilu-ṣṣâliḥâti feyüveffîhim ücûrahüm. vellâhü lâ yüḥibbu-żżâlimîn.'),
(351, 3, 58, 'ẕâlike netlûhü `aleyke mine-l\'âyâti veẕẕikri-lḥakîm.'),
(352, 3, 59, 'inne meŝele `îsâ `inde-llâhi kemeŝeli âdem. ḫaleḳahû min türâbin ŝümme ḳâle lehû kün feyekûn.'),
(353, 3, 60, 'elḥaḳḳu mir rabbike felâ teküm mine-lmümterîn.'),
(354, 3, 61, 'femen ḥâccâke fîhi mim ba`di mâ câeke mine-l`ilmi feḳul te`âlev ned`u ebnâenâ veebnâeküm venisâenâ venisâeküm veenfüsenâ veenfüseküm ŝümme nebtehil fenec`al la`nete-llâhi `ale-lkâẕibîn.'),
(355, 3, 62, 'inne hâẕâ lehüve-lḳaṣaṣu-lḥaḳḳ. vemâ min ilâhin ille-llâh. veinne-llâhe lehüve-l`azîzü-lḥakîm.'),
(356, 3, 63, 'fein tevellev feinne-llâhe `alîmüm bilmüfsidîn.'),
(357, 3, 64, 'ḳul yâ ehle-lkitâbi te`âlev ilâ kelimetin sevâim beynenâ vebeyneküm ellâ na`büde ille-llâhe velâ nüşrike bihî şey\'ev velâ yetteḫiẕe ba`ḍunâ ba`ḍan erbâbem min dûni-llâh. fein tevellev feḳûlü-şhedû biennâ müslimûn.'),
(358, 3, 65, 'yâ ehle-lkitâbi lime tüḥâccûne fî ibrâhîme vemâ ünzileti-ttevrâtü vel\'incîlü illâ mim ba`dih. efelâ ta`ḳilûn.'),
(359, 3, 66, 'hâentüm hâülâi ḥâcectüm fîmâ leküm bihî `ilmün felime tüḥâccûne fîmâ leyse leküm bihî `ilm. vellâhü ya`lemü veentüm lâ ta`lemûn.'),
(360, 3, 67, 'mâ kâne ibrâhîmü yehûdiyyev velâ naṣrâniyyev velâkin kâne ḥanîfem müslimâ. vemâ kâne mine-lmüşrikîn.'),
(361, 3, 68, 'inne evle-nnâsi biibrâhîme lelleẕîne-ttebe`ûhü vehâẕe-nnebiyyü velleẕîne âmenû. vellâhü veliyyü-lmü\'minîn.'),
(362, 3, 69, 'veddeṭ ṭâifetüm min ehli-lkitâbi lev yüḍillûneküm. vemâ yüḍillûne illâ enfüsehüm vemâ yeş`urûn.'),
(363, 3, 70, 'yâ ehle-lkitâbi lime tekfürûne biâyâti-llâhi veentüm teşhedûn.'),
(364, 3, 71, 'yâ ehle-lkitâbi lime telbisûne-lḥaḳḳa bilbâṭili vetektümûne-lḥaḳḳa veentüm ta`lemûn.'),
(365, 3, 72, 'veḳâleṭ ṭâifetüm min ehli-lkitâbi âminû billeẕî ünzile `ale-lleẕîne âmenû veche-nnehâri vekfürû âḫirahû le`allehüm yerci`ûn.'),
(366, 3, 73, 'velâ tü\'minû illâ limen tebi`a dîneküm. ḳul inne-lhüdâ hüde-llâhi ey yü\'tâ eḥadüm miŝle mâ ûtîtüm ev yüḥâccûküm `inde rabbiküm. ḳul inne-lfaḍle biyedi-llâh. yü\'tîhi mey yeşâ\'. vellâhü vâsi`un `alîm.'),
(367, 3, 74, 'yaḫteṣṣu biraḥmetihî mey yeşâ\'. vellâhü ẕü-lfaḍli-l`ażîm.'),
(368, 3, 75, 'vemin ehli-lkitâbi men in te\'menhü biḳinṭâriy yü\'eddihî ileyk. veminhüm men in te\'menhü bidînâril lâ yü\'eddihî ileyke illâ mâ dümte `aleyhi ḳâimâ. ẕâlike biennehüm ḳâlû leyse `aleynâ fi-l\'ümmiyyîne sebîl. veyeḳûlûne `ale-llâhi-lkeẕibe vehüm ya`lemûn.'),
(369, 3, 76, 'belâ men evfâ bi`ahdihî vetteḳâ feinne-llâhe yüḥibbü-lmütteḳîn.'),
(370, 3, 77, 'inne-lleẕîne yeşterûne bi`ahdi-llâhi veeymânihim ŝemenen ḳalîlen ülâike lâ ḫalâḳa lehüm fi-l\'âḫirati velâ yükellimühümü-llâhü velâ yenżuru ileyhim yevme-lḳiyâmeti velâ yüzekkîhim. velehüm `aẕâbün elîm.'),
(371, 3, 78, 'veinne minhüm leferîḳay yelvûne elsinetehüm bilkitâbi litaḥsebûhü mine-lkitâbi vemâ hüve mine-lkitâb. veyeḳûlûne hüve min `indi-llâhi vemâ hüve min `indi-llâh. veyeḳûlûne `ale-llâhi-lkeẕibe vehüm ya`lemûn.'),
(372, 3, 79, 'mâ kâne libeşerin ey yü\'tiyehü-llâhü-lkitâbe velḥukme vennübüvvete ŝümme yeḳûle linnâsi kûnû `ibâdel lî min dûni-llâhi velâkin kûnû rabbâniyyîne bimâ küntüm tü`allimûne-lkitâbe vebimâ küntüm tedrusûn.'),
(373, 3, 80, 'velâ ye\'müraküm en tetteḫiẕü-lmelâikete vennebiyyîne erbâbâ. eye\'müruküm bilküfri ba`de iẕ entüm müslimûn.'),
(374, 3, 81, 'veiẕ eḫaẕe-llâhü mîŝâḳa-nnebiyyîne lemâ âteytüküm min kitâbiv veḥikmetin ŝümme câeküm rasûlüm müṣaddiḳul limâ me`aküm letü\'minünne bihî veletenṣurunneh. ḳâle eaḳrartüm veeḫaẕtüm `alâ ẕâliküm iṣrî. ḳâlû aḳrarnâ. ḳâle feşhedû veenâ me`aküm mine-şşâhidîn.'),
(375, 3, 82, 'femen tevellâ ba`de ẕâlike feülâike hümü-lfâsiḳûn.'),
(376, 3, 83, 'efegayra dîni-llâhi yebgûne velehû esleme men fi-ssemâvâti vel\'arḍi ṭav`av vekerhev veileyhi yürce`ûn.'),
(377, 3, 84, 'ḳul âmennâ billâhi vemâ ünzile `aleynâ vemâ ünzile `alâ ibrâhîme veismâ`île veisḥâḳa veya`ḳûbe vel\'esbâṭi vemâ ûtiye mûsâ ve`îsâ vennebiyyûne mir rabbihim. lâ nüferriḳu beyne eḥadim minhüm. venaḥnü lehû müslimûn.'),
(378, 3, 85, 'vemey yebtegi gayra-l\'islâmi dînen feley yuḳbele minh. vehüve fi-l\'âḫirati mine-lḫâsirîn.'),
(379, 3, 86, 'keyfe yehdi-llâhü ḳavmen keferû ba`de îmânihim veşehidû enne-rrasûle ḥaḳḳuv vecâehümü-lbeyyinât. vellâhü lâ yehdi-lḳavme-żżâlimîn.'),
(380, 3, 87, 'ülâike cezâühüm enne `aleyhim la`nete-llâhi velmelâiketi vennâsi ecme`în.'),
(381, 3, 88, 'ḫâlidîne fîhâ. lâ yüḫaffefü `anhümü-l`aẕâbü velâ hüm yünżarûn.'),
(382, 3, 89, 'ille-lleẕîne tâbû mim ba`di ẕâlike veaṣleḥû feinne-llâhe gafûrur raḥîm.'),
(383, 3, 90, 'inne-lleẕîne keferû ba`de îmânihim ŝümme-zdâdû küfral len tuḳbele tevbetühüm. veülâike hümu-ḍḍâllûn.'),
(384, 3, 91, 'inne-lleẕîne keferû vemâtû vehüm küffârun feley yuḳbele min eḥadihim mil\'ü-l\'arḍi ẕehebev velevi-ftedâ bihî. ülâike lehüm `aẕâbün elîmüv vemâ lehüm min nâṣirîn.'),
(385, 3, 92, 'len tenâlü-lbirra ḥattâ tünfiḳû mimmâ tüḥibbûn. vemâ tünfiḳû min şey\'in feinne-llâhe bihî `alîm.'),
(386, 3, 93, 'küllu-ṭṭa`âmi kâne ḥillel libenî isrâîle illâ mâ ḥarrame isrâîlü `alâ nefsihî min ḳabli en tünezzele-ttevrâh. ḳul fe\'tû bittevrâti fetlûhâ in küntüm ṣâdiḳîn.'),
(387, 3, 94, 'femeni-fterâ `ale-llâhi-lkeẕibe mim ba`di ẕâlike feülâike hümu-żżâlimûn.'),
(388, 3, 95, 'ḳul ṣadeḳa-llâhü fettebi`û millete ibrâhîme ḥanîfâ. vemâ kâne mine-lmüşrikîn.'),
(389, 3, 96, 'inne evvele beytiv vuḍi`a linnâsi lelleẕî bibekkete mübârakev vehüdel lil`âlemîn.'),
(390, 3, 97, 'fîhi âyâtüm beyyinâtüm meḳâmü ibrâhîm. vemen deḫalehû kâne âminâ. velillâhi `ale-nnâsi ḥiccü-lbeyti meni-steṭâ`a ileyhi sebîlâ. vemen kefera feinne-llâhe ganiyyün `ani-l`âlemîn.'),
(391, 3, 98, 'ḳul yâ ehle-lkitâbi lime tekfürûne biâyâti-llâh. vellâhü şehîdün `alâ mâ ta`melûn.'),
(392, 3, 99, 'ḳul yâ ehle-lkitâbi lime teṣuddûne `an sebîli-llâhi men âmene tebgûnehâ `ivecev veentüm şühedâ\'. veme-llâhü bigâfilin `ammâ ta`melûn.'),
(393, 3, 100, 'yâ eyyühe-lleẕîne âmenû in tüṭî`û ferîḳam mine-lleẕîne ûtü-lkitâbe yeruddûküm ba`de îmâniküm kâfirîn.'),
(394, 3, 101, 'vekeyfe tekfürûne veentüm tütlâ `aleyküm âyâtü-llâhi vefîküm rasûlüh. vemey ya`teṣim billâhi feḳad hüdiye ilâ ṣirâṭim müsteḳîm.'),
(395, 3, 102, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe ḥaḳḳa tüḳâtihî velâ temûtünne illâ veentüm müslimûn.'),
(396, 3, 103, 'va`teṣimû biḥabli-llâhi cemî`av velâ teferraḳû. veẕkürû ni`mete-llâhi `aleyküm iẕ küntüm a`dâen feellefe beyne ḳulûbiküm feaṣbaḥtüm bini`metihî iḫvânâ. veküntüm `alâ şefâ ḥufratim mine-nnâri feenḳaẕeküm minhâ. keẕâlike yübeyyinü-llâhü leküm âyâtihî le`alleküm tehtedûn.'),
(397, 3, 104, 'velteküm minküm ümmetüy yed`ûne ile-lḫayri veye\'mürûne bilma`rûfi veyenhevne `ani-lmünker. veülâike hümü-lmüfliḥûn.'),
(398, 3, 105, 'velâ tekûnû kelleẕîne teferraḳû vaḫtelefû mim ba`di mâ câehümü-lbeyyinât. veülâike lehüm `aẕâbün `ażîm.'),
(399, 3, 106, 'yevme tebyeḍḍu vucûhüv vetesveddü vucûhün. feemme-lleẕîne-sveddet vucûhühüm. ekefertüm ba`de îmâniküm feẕûḳu-l`aẕâbe bimâ küntüm tekfürûn.'),
(400, 3, 107, 'veemme-lleẕîne-byeḍḍat vucûhühüm fefî raḥmeti-llâh. hüm fîhâ ḫâlidûn.'),
(401, 3, 108, 'tilke âyâtü-llâhi netlûhâ `aleyke bilḥaḳḳ. veme-llâhü yürîdü żulmel lil`âlemîn.'),
(402, 3, 109, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. veile-llâhi türce`u-l\'ümûr.'),
(403, 3, 110, 'küntüm ḫayra ümmetin uḫricet linnâsi te\'mürûne bilma`rûfi vetenhevne `ani-lmünkeri vetü\'minûne billâh. velev âmene ehlü-lkitâbi lekâne ḫayral lehüm. minhümü-lmü\'minûne veekŝeruhümü-lfâsiḳûn.'),
(404, 3, 111, 'ley yeḍurrûküm illâ eẕâ. veiy yüḳâtilûküm yüvellûkümü-l\'edbâr. ŝümme lâ yünṣarûn.'),
(405, 3, 112, 'ḍuribet `aleyhimü-ẕẕilletü eyne mâ ŝüḳifû illâ biḥablim mine-llâhi veḥablim mine-nnâsi vebâû bigaḍabim mine-llâhi veḍuribet `aleyhimü-lmeskeneh. ẕâlike biennehüm kânû yekfürûne biâyâti-llâhi veyaḳtülûne-l\'embiyâe bigayri ḥaḳḳ. ẕâlike bimâ `aṣav vekânû ya`tedûn.'),
(406, 3, 113, 'leysû sevââ. min ehli-lkitâbi ümmetün ḳâimetüy yetlûne âyâti-llâhi ânâe-lleyli vehüm yescüdûn.'),
(407, 3, 114, 'yü\'minûne billâhi velyevmi-l\'âḫiri veye\'mürûne bilma`rûfi veyenhevne `ani-lmünkeri veyüsâri`ûne fi-lḫayrât. veülâike mine-ṣṣâliḥîn.'),
(408, 3, 115, 'vemâ yef`alû min ḫayrin feley yükferûh. vellâhü `alîmüm bilmütteḳîn.'),
(409, 3, 116, 'inne-lleẕîne keferû len tugniye `anhüm emvâlühüm velâ evlâdühüm mine-llâhi şey\'â. veülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(410, 3, 117, 'meŝelü mâ yünfiḳûne fî hâẕihi-lḥayâti-ddünyâ kemeŝeli rîḥin fîhâ ṣirrun eṣâbet ḥarŝe ḳavmin żalemû enfüsehüm feehleketh. vemâ żalemehümü-llâhü velâkin enfüsehüm yażlimûn.'),
(411, 3, 118, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕû biṭânetem min dûniküm lâ ye\'lûneküm ḫabâlâ. veddû mâ `anittüm. ḳad bedeti-lbagḍâü min efvâhihim. vemâ tuḫfî ṣudûruhüm ekber. ḳad beyyennâ lekümü-l\'âyâti in küntüm ta`ḳilûn.'),
(412, 3, 119, 'hâentüm ülâi tüḥibbûnehüm velâ yüḥibbûneküm vetü\'minûne bilkitâbi küllih. veiẕâ leḳûküm ḳâlû âmennâ. veiẕâ ḫalev `aḍḍû `aleykümü-l\'enâmile mine-lgayż. ḳul mûtû bigayżiküm. inne-llâhe `alîmüm biẕâti-ṣṣudûr.'),
(413, 3, 120, 'in temsesküm ḥasenetün tesü\'hüm. vein tüṣibküm seyyietüy yefraḥû bihâ. vein taṣbirû vetetteḳû lâ yeḍurruküm keydühüm şey\'â. inne-llâhe bimâ ya`melûne müḥîṭ.'),
(414, 3, 121, 'veiẕ gadevte min ehlike tübevviü-lmü\'minîne meḳâ`ide lilḳitâl. vellâhü semî`un `alîm.'),
(415, 3, 122, 'iẕ hemmeṭ ṭâifetâni minküm en tefşelâ vellâhü veliyyühümâ. ve`ale-llâhi felyetevekkeli-lmü\'minûn.'),
(416, 3, 123, 'veleḳad neṣarakümü-llâhü bibedriv veentüm eẕilleh. fetteḳu-llâhe le`alleküm teşkürûn.'),
(417, 3, 124, 'iẕ teḳûlü lilmü\'minîne eley yekfiyeküm ey yümiddeküm rabbüküm biŝelâŝeti âlâfim mine-lmelâiketi münzelîn.'),
(418, 3, 125, 'belâ in taṣbirû vetetteḳû veye\'tûküm min fevrihim hâẕâ yümdidküm rabbüküm biḫamseti âlâfim mine-lmelâiketi müsevvimîn.'),
(419, 3, 126, 'vemâ ce`alehü-llâhü illâ büşrâ leküm velitaṭmeinne ḳulûbüküm bih. veme-nnaṣru illâ min `indi-llâhi-l`azîzi-lḥakîm.'),
(420, 3, 127, 'liyaḳṭa`a ṭarafem mine-lleẕîne keferû ev yekbitehüm feyenḳalibû ḫâibîn.'),
(421, 3, 128, 'leyse leke mine-l\'emri şey\'ün ev yetûbe `aleyhim ev yü`aẕẕibehüm feinnehüm żâlimûn.'),
(422, 3, 129, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. yagfiru limey yeşâü veyü`aẕẕibü mey yeşâ\'. vellâhü gafûrur raḥîm.'),
(423, 3, 130, 'yâ eyyühe-lleẕîne âmenû lâ te\'külü-rribâ aḍ`âfem müḍâ`afeh. vetteḳu-llâhe le`alleküm tüfliḥûn.'),
(424, 3, 131, 'vetteḳu-nnâra-lletî ü`iddet lilkâfirîn.'),
(425, 3, 132, 'veeṭî`ü-llâhe verrasûle le`alleküm türḥamûn.'),
(426, 3, 133, 'vesâri`û ilâ magfiratim mir rabbiküm vecennetin `arḍuhe-ssemâvâtü vel\'arḍu ü`iddet lilmütteḳîn.'),
(427, 3, 134, 'elleẕîne yünfiḳûne fi-sserrâi veḍḍarrâi velkâżimîne-lgayża vel`âfîne `ani-nnâs. vellâhü yüḥibbü-lmuḥsinîn.'),
(428, 3, 135, 'velleẕîne iẕâ fe`alû fâḥişeten ev żalemû enfüsehüm ẕekerü-llâhe festagferû liẕünûbihim. vemey yagfiru-ẕẕünûbe ille-llâh. velem yüṣirrû `alâ mâ fe`alû vehüm ya`lemûn.'),
(429, 3, 136, 'ülâike cezâühüm magfiratüm mir rabbihim vecennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. veni`me ecru-l`âmilîn.'),
(430, 3, 137, 'ḳad ḫalet min ḳabliküm sünenün fesîrû fi-l\'arḍi fenżurû keyfe kâne `âḳibetü-lmükeẕẕibîn.'),
(431, 3, 138, 'hâẕâ beyânül linnâsi vehüdev vemev`iżatül lilmütteḳîn.'),
(432, 3, 139, 'velâ tehinû velâ taḥzenû veentümü-l\'a`levne in küntüm mü\'minîn.'),
(433, 3, 140, 'iy yemsesküm ḳarḥun feḳad messe-lḳavme ḳarḥum miŝlüh. vetilke-l\'eyyâmü nüdâvilühâ beyne-nnâs. veliya`leme-llâhü-lleẕîne âmenû veyetteḫiẕe minküm şühedâ\'. vellâhü lâ yüḥibbu-żżâlimîn.'),
(434, 3, 141, 'veliyümeḥḥiṣa-llâhü-lleẕîne âmenû veyemḥaḳa-lkâfirîn.'),
(435, 3, 142, 'em ḥasibtüm en tedḫulü-lcennete velemmâ ya`lemi-llâhü-lleẕîne câhedû minküm veya`leme-ṣṣâbirîn.'),
(436, 3, 143, 'veleḳad küntüm temennevne-lmevte min ḳabli en telḳavh. feḳad raeytümûhü veentüm tenżurûn.'),
(437, 3, 144, 'vemâ müḥammedün illâ rasûl. ḳad ḫalet min ḳablihi-rrusül. efeim mâte ev ḳutile-nḳalebtüm `alâ a`ḳâbiküm. vemey yenḳalib `alâ `aḳibeyhi feley yeḍurra-llâhe şey\'â. veseyeczi-llâhü-şşâkirîn.'),
(438, 3, 145, 'vemâ kâne linefsin en temûte illâ biiẕni-llâhi kitâbem müeccelâ. vemey yürid ŝevâbe-ddünyâ nü\'tihî minhâ. vemey yürid ŝevâbe-l\'âḫirati nü\'tihî minhâ. veseneczi-şşâkirîn.'),
(439, 3, 146, 'vekeeyyim min nebiyyin ḳâtele me`ahû ribbiyyûne keŝîr. femâ vehenû limâ eṣâbehüm fî sebîli-llâhi vemâ ḍa`ufû veme-stekânû. vellâhü yüḥibbu-ṣṣâbirîn.'),
(440, 3, 147, 'vemâ kâne ḳavlehüm illâ en ḳâlû rabbene-gfir lenâ ẕünûbenâ veisrâfenâ fî emrinâ veŝebbit aḳdâmenâ venṣurnâ `ale-lḳavmi-lkâfirîn.'),
(441, 3, 148, 'feâtâhümü-llâhü ŝevâbe-ddünyâ veḥusne ŝevâbi-l\'âḫirah. vellâhü yüḥibbü-lmuḥsinîn.'),
(442, 3, 149, 'yâ eyyühe-lleẕîne âmenû in tüṭî`ü-lleẕîne keferû yeruddûküm `alâ a`ḳâbiküm fetenḳalibû ḫâsirîn.'),
(443, 3, 150, 'beli-llâhü mevlâküm. vehüve ḫayru-nnâṣirîn.'),
(444, 3, 151, 'senülḳî fî ḳulûbi-lleẕîne keferü-rru`be bimâ eşrakû billâhi mâ lem yünezzil bihî sülṭânâ. veme\'vâhümü-nnâr. vebi\'se meŝve-żżâlimîn.'),
(445, 3, 152, 'veleḳad ṣadeḳakümü-llâhü va`dehû iẕ teḥussûnehüm biiẕnih. ḥattâ iẕâ feşiltüm vetenâza`tüm fi-l\'emri ve`aṣaytüm mim ba`di mâ erâküm mâ tüḥibbûn. minküm mey yürîdü-ddünyâ veminküm mey yürîdü-l\'âḫirah. ŝümme ṣarafeküm `anhüm liyebteliyeküm. veleḳad `afâ `anküm. vellâhü ẕû faḍlin `ale-lmü\'minîn.'),
(446, 3, 153, 'iẕ tuṣ`idûne velâ telvûne `alâ eḥadiv verrasûlü yed`ûküm fî uḫrâküm feeŝâbeküm gammem bigammil likeylâ taḥzenû `alâ mâ fâteküm velâ mâ eṣâbeküm. vellâhü ḫabîrum bimâ ta`melûn.'),
(447, 3, 154, 'ŝümme enzele `aleyküm mim ba`di-lgammi emeneten nü`âsey yagşâ ṭâifetem minküm veṭâifetün ḳad ehemmethüm enfüsühüm yeżunnûne billâhi gayra-lḥaḳḳi żanne-lcâhiliyyeh. yeḳûlûne hel lenâ min-l\'emri min şey\'. ḳul inne-l\'emra küllehû lillâh. yuḫfûne fî enfüsihim mâ lâ yübdûne lek. yeḳûlûne lev kâne lenâ mine-l\'emri şey\'üm mâ ḳutilnâ hâhünâ. ḳul lev küntüm fî büyûtiküm leberaze-lleẕîne kütibe `aleyhimü-lḳatlü ilâ meḍâci`ihim. veliyebteliye-llâhü mâ fî ṣudûriküm veliyümeḥḥiṣa mâ fî ḳulûbiküm. vellâhü `alîmüm biẕâti-ṣṣudûr.'),
(448, 3, 155, 'inne-lleẕîne tevellev minküm yevme-lteḳe-lcem`âni inneme-stezellehümü-şşeyṭânü biba`ḍi mâ kesebû. veleḳad `afe-llâhü `anhüm. inne-llâhe gafûrun ḥalîm.'),
(449, 3, 156, 'yâ eyyühe-lleẕîne âmenû lâ tekûnû kelleẕîne keferû veḳâlû liiḫvânihim iẕâ ḍarabû fi-l\'arḍi ev kânû guzzel lev kânû `indenâ mâ mâtû vemâ ḳutilû. liyec`ale-llâhü ẕâlike ḥasraten fî ḳulûbihim. vellâhü yuḥyî veyümît. vellâhü bimâ ta`melûne beṣîr.'),
(450, 3, 157, 'velein ḳutiltüm fî sebîli-llâhi ev müttüm lemagfiratüm mine-llâhi veraḥmetün ḫayrum mimmâ yecme`ûn.'),
(451, 3, 158, 'veleim müttüm ev ḳutiltüm leile-llâhi tuḥşerûn.'),
(452, 3, 159, 'febimâ raḥmetim mine-llâhi linte lehüm. velev künte feżżan galîża-lḳalbi lenfeḍḍû min ḥavlik. fa`fü `anhüm vestagfir lehüm veşâvirhüm fi-l\'emr. feiẕâ `azemte fetevekkel `ale-llâh. inne-llâhe yüḥibbü-lmütevekkilîn.'),
(453, 3, 160, 'iy yenṣurkümü-llâhü felâ gâlibe leküm. veiy yaḫẕülküm femen ẕe-lleẕî yenṣuruküm mim ba`dih. ve`ale-llâhi felyetevekkeli-lmü\'minûn.'),
(454, 3, 161, 'vemâ kâne linebiyyin ey yegull. vemey yaglül ye\'ti bimâ galle yevme-lḳiyâmeh. ŝümme tüveffâ küllü nefsim mâ kesebet vehüm lâ yużlemûn.'),
(455, 3, 162, 'efemeni-ttebe`a riḍvâne-llâhi kemem bâe biseḫaṭim mine-llâhi veme\'vâhü cehennem. vebi\'se-lmeṣîr.'),
(456, 3, 163, 'hüm deracâtün `inde-llâh. vellâhü beṣîrum bimâ ya`melûn.'),
(457, 3, 164, 'leḳad menne-llâhü `ale-lmü\'minîne iẕ be`aŝe fîhim rasûlem min enfüsihim yetlû `aleyhim âyâtihî veyüzekkîhim veyü`allimühümü-lkitâbe velḥikmeh. vein kânû min ḳablü lefî ḍalâlim mübîn.'),
(458, 3, 165, 'evelemmâ eṣâbetküm müṣîbetün ḳad eṣabtüm miŝleyhâ ḳultüm ennâ hâẕâ. ḳul hüve min `indi enfüsiküm. inne-llâhe `alâ külli şey\'in ḳadîr.'),
(459, 3, 166, 'vemâ eṣâbeküm yevme-lteḳe-lcem`âni febiiẕni-llâhi veliya`leme-lmü\'minîn.'),
(460, 3, 167, 'veliya`leme-lleẕîne nâfeḳû. veḳîle lehüm te`âlev ḳâtilû fî sebîli-llâhi evi-dfe`û. ḳâlû lev na`lemü ḳitâlel letteba`nâküm. hüm lilküfri yevmeiẕin aḳrabü minhüm lil\'îmân. yeḳûlûne biefvâhihim mâ leyse fî ḳulûbihim. vellâhü a`lemü bimâ yektümûn.'),
(461, 3, 168, 'elleẕîne ḳâlû liiḫvânihim veḳa`adû lev eṭâ`ûnâ mâ ḳutilû. ḳul fedraû `an enfüsikümü-lmevte in küntüm ṣâdiḳîn.'),
(462, 3, 169, 'velâ taḥsebenne-lleẕîne ḳutilû fî sebîli-llâhi emvâtâ. bel aḥyâün `inde rabbihim yürzeḳûn.'),
(463, 3, 170, 'feriḥîne bimâ âtâhümü-llâhü min faḍlihî veyestebşirûne billeẕîne lem yelḥaḳû bihim min ḫalfihim ellâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(464, 3, 171, 'yestebşirûne bini`metim mine-llâhi vefaḍliv veenne-llâhe lâ yüḍî`u ecra-lmü\'minîn.'),
(465, 3, 172, 'elleẕîne-stecâbû lillâhi verrasûli mim ba`di mâ eṣâbehümü-lḳarḥ. lilleẕîne aḥsenû minhüm vetteḳav ecrun `ażîm.'),
(466, 3, 173, 'elleẕîne ḳâle lehümü-nnâsü inne-nnâse ḳad ceme`û leküm faḫşevhüm fezâdehüm îmânâ. veḳâlû ḥasbüne-llâhü veni`me-lvekîl.'),
(467, 3, 174, 'fenḳalebû bini`metim mine-llâhi vefaḍlil lem yemseshüm sûüv vettebe`û riḍvâne-llâh. vellâhü ẕû faḍlin `ażîm.'),
(468, 3, 175, 'innemâ ẕâlikümü-şşeyṭânü yüḫavvifü evliyâeh. felâ teḫâfûhüm veḫâfûni in küntüm mü\'minîn.'),
(469, 3, 176, 'velâ yaḥzünke-lleẕîne yüsâri`ûne fi-lküfr. innehüm ley yeḍurrü-llâhe şey\'â. yürîdü-llâhü ellâ yec`ale lehüm ḥażżan fi-l\'âḫirah. velehüm `aẕâbün `ażîm.'),
(470, 3, 177, 'inne-lleẕîne-şteravu-lküfra bil\'îmâni ley yeḍurrü-llâhe şey\'en. velehüm `aẕâbün elîm.'),
(471, 3, 178, 'velâ yaḥsebenne-lleẕîne keferû ennemâ nümlî lehüm ḫayrul lienfüsihim. innemâ nümlî lehüm liyezdâdû iŝmâ. velehüm `aẕâbüm mühîn.'),
(472, 3, 179, 'mâ kâne-llâhü liyeẕera-lmü\'minîne `alâ mâ entüm `aleyhi ḥattâ yemîze-lḫabîŝe mine-ṭṭayyib. vemâ kâne-llâhü liyuṭli`aküm `ale-lgaybi velâkinne-llâhe yectebî mir rusülihî mey yeşâü feâminû billâhi verusülih. vein tü\'minû vetetteḳû feleküm ecrun `ażîm.'),
(473, 3, 180, 'velâ yaḥsebenne-lleẕîne yebḫalûne bimâ âtâhümü-llâhü min faḍlihî hüve ḫayral lehüm. bel hüve şerrul lehüm. seyüṭavveḳûne mâ beḫilû bihî yevme-lḳiyâmeh. velillâhi mîrâŝü-ssemâvâti vel\'arḍ. vellâhü bimâ ta`melûne ḫabîr.'),
(474, 3, 181, 'leḳad semi`a-llâhü ḳavle-lleẕîne ḳâlû inne-llâhe feḳîruv venaḥnü agniyâ\'. senektübü mâ ḳâlû veḳatlehümü-l\'embiyâe bigayri ḥaḳḳiv veneḳûlü ẕûḳû `aẕâbe-lḥarîḳ.'),
(475, 3, 182, 'ẕâlike bimâ ḳaddemet eydîküm veenne-llâhe leyse biżallâmil lil`abîd.'),
(476, 3, 183, 'elleẕîne ḳâlû inne-llâhe `ahide ileynâ ellâ nü\'mine lirasûlin ḥattâ ye\'tiyenâ biḳurbânin te\'külühü-nnâr. ḳul ḳad câeküm rusülüm min ḳablî bilbeyyinâti vebilleẕî ḳultüm felime ḳateltümûhüm in küntüm ṣâdiḳîn.'),
(477, 3, 184, 'fein keẕẕebûke feḳad küẕẕibe rusülüm min ḳablike câû bilbeyyinâti vezzübüri velkitâbi-lmünîr.'),
(478, 3, 185, 'küllü nefsin ẕâiḳatü-lmevt. veinnemâ tüveffevne ücûraküm yevme-lḳiyâmeh. femen zuḥziḥa `ani-nnâri veüdḫile-lcennete feḳad fâz. veme-lḥayâtü-ddünyâ illâ metâ`u-lgurûr.'),
(479, 3, 186, 'letüblevunne fî emvâliküm veenfüsiküm veletesme`unne mine-lleẕîne ûtü-lkitâbe min ḳabliküm vemine-lleẕîne eşrakû eẕen keŝîrâ. vein taṣbirû vetetteḳû feinne ẕâlike min `azmi-l\'ümûr.'),
(480, 3, 187, 'veiẕ eḫaẕe-llâhü mîŝâḳa-lleẕîne ûtü-lkitâbe letübeyyinünnehû linnâsi velâ tektümûneh. fenebeẕûhü verâe żuhûrihim veşterav bihî ŝemenen ḳalîlâ. febi\'se mâ yeşterûn.'),
(481, 3, 188, 'lâ taḥsebenne-lleẕîne yefraḥûne bimâ etev veyüḥibbûne ey yuḥmedû bimâ lem yef`alû felâ taḥsebennehüm bimefâzetim mine-l`aẕâb. velehüm `aẕâbün elîm.'),
(482, 3, 189, 'velillâhi mülkü-ssemâvâti vel\'arḍ. vellâhü `alâ külli şey\'in ḳadîr.'),
(483, 3, 190, 'inne fî ḫalḳi-ssemâvâti vel\'arḍi vaḫtilâfi-lleyli vennehâri leâyâtil liüli-l\'elbâb.'),
(484, 3, 191, 'elleẕîne yeẕkürûne-llâhe ḳiyâmev veḳu`ûdev ve`alâ cünûbihim veyetefekkerûne fî ḫalḳi-ssemâvâti vel\'arḍ. rabbenâ mâ ḫalaḳte hâẕâ bâṭilâ. sübḥâneke feḳinâ `aẕâbe-nnâr.'),
(485, 3, 192, 'rabbenâ inneke men tüdḫili-nnâra feḳad aḫzeyteh. vemâ liżżâlimîne min enṣâr.'),
(486, 3, 193, 'rabbenâ innenâ semi`nâ münâdiyey yünâdî lil\'îmâni en âminû birabbiküm feâmennâ. rabbenâ fagfir lenâ ẕünûbenâ vekeffir `annâ seyyiâtinâ veteveffenâ me`a-l\'ebrâr.'),
(487, 3, 194, 'rabbenâ veâtinâ mâ ve`attenâ `alâ rusülike velâ tuḫzinâ yevme-lḳiyâmeh. inneke lâ tuḫlifü-lmî`âd.'),
(488, 3, 195, 'festecâbe lehüm rabbühüm ennî lâ üḍî`u `amele `âmilim minküm min ẕekerin ev ünŝâ. ba`ḍuküm mim ba`ḍ. felleẕîne hâcerû veuḫricû min diyârihim veûẕû fî sebîlî veḳâtelû veḳutilû leükeffiranne `anhüm seyyiâtihim veleüdḫilennehüm cennâtin tecrî min taḥtihe-l\'enhâr. ŝevâbem min `indi-llâh. vellâhü `indehû ḥusnü-ŝŝevâb.'),
(489, 3, 196, 'lâ yegurranneke teḳallübü-lleẕîne keferû fi-lbilâd.'),
(490, 3, 197, 'metâ`un ḳalîlün ŝümme me\'vâhüm cehennem. vebi\'se-lmihâd.'),
(491, 3, 198, 'lâkini-lleẕîne-tteḳav rabbehüm lehüm cennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ nüzülem min `indi-llâh. vemâ `inde-llâhi ḫayrul lil\'ebrâr.'),
(492, 3, 199, 'veinne min ehli-lkitâbi lemey yü\'minü billâhi vemâ ünzile ileyküm vemâ ünzile ileyhim ḫâşi`îne lillâhi lâ yeşterûne biâyâti-llâhi ŝemenen ḳalîlâ. ülâike lehüm ecruhüm `inde rabbihim. inne-llâhe serî`u-lḥisâb.'),
(493, 3, 200, 'yâ eyyühe-lleẕîne âmenu-ṣbirû veṣâbirû verâbiṭû vetteḳu-llâhe le`alleküm tüfliḥûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'yâ eyyühe-nnâsü-tteḳû rabbekümü-lleẕî ḫaleḳaküm min nefsiv vâḥidetiv veḫaleḳa minhâ zevcehâ vebeŝŝe minhümâ ricâlen keŝîrav venisââ. vetteḳu-llâhe-lleẕî tesâelûne bihî vel\'erḥâm. inne-llâhe kâne `aleyküm raḳîbâ.'),
(495, 4, 2, 'veâtü-lyetâmâ emvâlehüm velâ tetebeddelü-lḫabîŝe biṭṭayyib. velâ te\'külû emvâlehüm ilâ emvâliküm. innehû kâne ḥûben kebîrâ.'),
(496, 4, 3, 'vein ḫiftüm ellâ tuḳsiṭû fi-lyetâmâ fenkiḥû mâ ṭâbe leküm mine-nnisâi meŝnâ veŝülâŝe verubâ`. fein ḫiftüm ellâ ta`dilû fevâḥideten ev mâ meleket eymânüküm. ẕâlike ednâ ellâ te`ûlû.'),
(497, 4, 4, 'veâtü-nnisâe ṣadüḳâtihinne niḥleh. fein ṭibne leküm `an şey\'im minhü nefsen fekülûhü henîem merîâ.'),
(498, 4, 5, 'velâ tü\'tü-ssüfehâe emvâlekümü-lletî ce`ale-llâhü leküm ḳiyâmev verzüḳûhüm fîhâ veksûhüm veḳûlû lehüm ḳavlem ma`rûfâ.'),
(499, 4, 6, 'vebtelü-lyetâmâ ḥattâ iẕâ belegü-nnikâḥ. fein ânestüm minhüm ruşden fedfe`û ileyhim emvâlehüm. velâ te\'külûhâ isrâfev vebidâran ey yekberû. vemen kâne ganiyyen felyesta`fif. vemen kâne feḳîran felye\'kül bilma`rûf. feiẕâ defa`tüm ileyhim emvâlehüm feeşhidû `aleyhim. vekefâ billâhi ḥasîbâ.'),
(500, 4, 7, 'lirricâli neṣîbüm mimmâ terake-lvâlidâni vel\'aḳrabûn. velinnisâi neṣîbüm mimmâ terake-lvâlidâni vel\'aḳrabûne mimmâ ḳalle minhü ev keŝür. neṣîbem mefrûḍâ.'),
(501, 4, 8, 'veiẕâ ḥaḍara-lḳismete ülü-lḳurbâ velyetâmâ velmesâkînü ferzüḳûhüm minhü veḳûlû lehüm ḳavlem ma`rûfâ.'),
(502, 4, 9, 'velyaḫşe-lleẕîne lev terakû min ḫalfihim ẕürriyyeten ḍi`âfen ḫâfû `aleyhim. felyetteḳu-llâhe velyeḳûlû ḳavlen sedîdâ.'),
(503, 4, 10, 'inne-lleẕîne ye\'külûne emvâle-lyetâmâ żulmen innemâ ye\'külûne fî büṭûnihim nârâ. veseyaṣlevne se`îrâ.'),
(504, 4, 11, 'yûṣîkümü-llâhü fî evlâdiküm liẕẕekeri miŝlü ḥażżi-l\'ünŝeyeyn. fein künne nisâen fevḳa-ŝneteyni felehünne ŝülüŝâ mâ terak. vein kânet vâḥideten felehe-nniṣf. veliebeveyhi likülli vâḥidim minhüme-ssüdüsü mimmâ terake in kâne lehû veled. feil lem yekül lehû veledüv veveriŝehû ebevâhü feliümmihi-ŝŝülüŝ. fein kâne lehû iḫvetün feliümmihi-ssüdüsü mim ba`di veṣiyyetiy yûṣî bihâ ev deyn. âbâüküm veebnâüküm. lâ tedrûne eyyühüm aḳrabü leküm nef`â. ferîḍatem mine-llâh. inne-llâhe kâne `alîmen ḥakîmâ.'),
(505, 4, 12, 'veleküm niṣfü mâ terake ezvâcüküm il lem yekül lehünne veled. fein kâne lehünne veledün felekümü-rrubü`u mimmâ terakne mim ba`di veṣiyyetiy yûṣîne bihâ ev deyn. velehünne-rrubü`u mimmâ teraktüm il lem yekül leküm veled. fein kâne leküm veledün felehünne-ŝŝümünü mimmâ teraktüm mim ba`di veṣiyyetin tûṣûne bihâ ev deyn. vein kâne racülüy yûraŝü kelâleten evi-mraetüv velehû eḫun ev uḫtün felikülli vâḥidim minhüme-ssüdüs. fein kânû ekŝera min ẕâlike fehüm şürakâü fi-ŝŝülüŝi mim ba`di veṣiyyetiy yûṣâ bihâ ev deynin gayra müḍârr. veṣiyyetem mine-llâh. vellâhü `alîmün ḥalîm.'),
(506, 4, 13, 'tilke ḥudûdü-llâh. vemey yüṭi`i-llâhe verasûlehû yüdḫilhü cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. veẕâlike-lfevzü-l`ażîm.'),
(507, 4, 14, 'vemey ya`ṣi-llâhe verasûlehû veyete`addâ ḥudûdehû yüdḫilhü nâran ḫâliden fîhâ. velehû `aẕâbüm mühîn.'),
(508, 4, 15, 'vellâtî ye\'tîne-lfâḥişete min nisâiküm festeşhidû `aleyhinne erbe`atem minküm. fein şehidû feemsikûhünne fi-lbüyûti ḥattâ yeteveffâhünne-lmevtü ev yec`ale-llâhü lehünne sebîlâ.'),
(509, 4, 16, 'velleẕâni ye\'tiyânihâ minküm feâẕûhümâ. fein tâbâ veaṣleḥâ fea`riḍû `anhümâ. inne-llâhe kâne tevvâber raḥîmâ.'),
(510, 4, 17, 'inneme-ttevbetü `ale-llâhi lilleẕîne ya`melûne-ssûe bicehâletin ŝümme yetûbûne min ḳarîbin feülâike yetûbü-llâhü `aleyhim. vekâne-llâhü `alîmen ḥakîmâ.'),
(511, 4, 18, 'veleyseti-ttevbetü lilleẕîne ya`melûne-sseyyiât. ḥattâ iẕâ ḥaḍara eḥadehümü-lmevtü ḳâle innî tübtü-l\'âne vele-lleẕîne yemûtûne vehüm küffâr. ülâike a`tednâ lehüm `aẕâben elîmâ.'),
(512, 4, 19, 'yâ eyyühe-lleẕîne âmenû lâ yeḥillü leküm en teriŝü-nnisâe kerhâ. velâ ta`ḍulûhünne liteẕhebû biba`ḍi mâ âteytümûhünne illâ ey ye\'tîne bifâḥişetim mübeyyineh. ve`âşirûhünne bilma`rûf. fein kerihtümûhünne fe`asâ en tekrahû şey\'ev veyec`ale-llâhü fîhi ḫayran keŝîrâ.'),
(513, 4, 20, 'vein erattümü-stibdâle zevcim mekâne zevciv veâteytüm iḥdâhünne ḳinṭâran felâ te\'ḫuẕû minhü şey\'â. ete\'ḫuẕûnehû bühtânev veiŝmem mübînâ.'),
(514, 4, 21, 'vekeyfe te\'ḫuẕûnehû veḳad efḍâ ba`ḍuküm ilâ ba`ḍiv veeḫaẕne minküm mîŝâḳan galîżâ.'),
(515, 4, 22, 'velâ tenkiḥû mâ nekeḥa âbâüküm mine-nnisâi illâ mâ ḳad selef. innehû kâne fâḥişetev vemaḳtâ. vesâe sebîlâ.'),
(516, 4, 23, 'ḥurrimet `aleyküm ümmehâtüküm vebenâtüküm veeḫavâtüküm ve`ammâtüküm veḫâlâtüküm vebenâtü-l\'eḫi vebenâtü-l\'uḫti veümmehâtükümü-llâtî erḍa`neküm veeḫavâtüküm mine-rraḍâ`ati veümmehâtü nisâiküm verabâibükümü-llâtî fî ḥucûriküm min nisâikümü-llâtî deḫaltüm bihinn. feil lem tekûnû deḫaltüm bihinne felâ cünâḥa `aleyküm. veḥalâilü ebnâikümü-lleẕîne min aṣlâbiküm veen tecme`û beyne-l\'uḫteyni illâ mâ ḳad selef. inne-llâhe kâne gafûrar raḥîmâ.'),
(517, 4, 24, 'velmuḥṣanâtü mine-nnisâi illâ mâ meleket eymânüküm. kitâbe-llâhi `aleyküm. veüḥille leküm mâ verâe ẕâliküm en tebtegû biemvâliküm muḥṣinîne gayra müsâfiḥîn. feme-stemta`tüm bihî minhünne feâtûhünne ücûrahünne ferîḍah. velâ cünâḥa `aleyküm fîmâ terâḍaytüm bihî mim ba`di-lferîḍah. inne-llâhe kâne `alîmen ḥakîmâ.'),
(518, 4, 25, 'vemel lem yesteṭi` minküm ṭavlen ey yenkiḥa-lmuḥsenâti-lmü\'minâti femim mâ meleket eymânüküm min feteyâtikümü-lmü\'minât. vellâhü a`lemü biîmâniküm. ba`ḍuküm mim ba`ḍ. fenkiḥûhünne biiẕni ehlihinne veâtûhünne ücûrahünne bilma`rûfi muḥṣanâtin gayra mesâfiḥâtiv velâ mütteḫiẕâti aḫdân. feiẕâ uḥṣinne fein eteyne bifâḥişetin fe`aleyhinne niṣfü mâ `ale-lmuḥsenâti mine-l`aẕâb. ẕâlike limen ḫaşiye-l`anete minküm. veen taṣbirû ḫayrul leküm. vellâhü gafûrur raḥîm.'),
(519, 4, 26, 'yürîdü-llâhü liyübeyyine leküm veyehdiyeküm sünene-lleẕîne min ḳabliküm veyetûbe `aleyküm. vellâhü `alîmün ḥakîm.'),
(520, 4, 27, 'vellâhü yürîdü ey yetûbe `aleyküm veyürîdü-lleẕîne yettebi`ûne-şşehevâti en temîlû meylen `ażîmâ.'),
(521, 4, 28, 'yürîdü-llâhü ey yüḫaffife `anküm. veḫuliḳa-l\'insânü ḍa`îfâ.'),
(522, 4, 29, 'yâ eyyühe-lleẕîne âmenû lâ te\'külû emvâleküm beyneküm bilbâṭili illâ en tekûne ticâraten `an terâḍim minküm velâ taḳtülû enfüseküm. inne-llâhe kâne biküm raḥîmâ.'),
(523, 4, 30, 'vemey yef`al ẕâlike `udvânev veżulmen fesevfe nuṣlîhi nârâ. vekâne ẕâlike `ale-llâhi yesîrâ.'),
(524, 4, 31, 'in tectenibû kebâira mâ tünhevne `anhü nükeffir `anküm seyyiâtiküm venüdḫilküm müdḫalen kerîmâ.'),
(525, 4, 32, 'velâ tetemennev mâ feḍḍale-llâhü bihî ba`ḍaküm `alâ ba`ḍ. lirricâli neṣîbüm mimme-ktesebû velinnisâi neṣîbüm mimme-ktesebn. ves\'elü-llâhe min faḍlih. inne-llâhe kâne bikülli şey\'in `alîmâ.'),
(526, 4, 33, 'veliküllin ce`alnâ mevâliye mimmâ terake-lvâlidâni vel\'aḳrabûn. velleẕîne `aḳadet eymânüküm feâtûhüm neṣîbehüm. inne-llâhe kâne `alâ külli şey\'in şehîdâ.'),
(527, 4, 34, 'erricâlü ḳavvâmûne `ale-nnisâi bimâ feḍḍale-llâhü ba`ḍahüm `alâ ba`ḍiv vebimâ enfeḳû min emvâlihim. feṣṣâliḥâtü ḳânitâtün ḥâfiżâtül lilgaybi bimâ ḥafiża-llâh. vellâtî teḫâfûne nüşûzehünne fe`iżûhünne vehcürûhünne fi-lmeḍâci`i vaḍribûhünn. fein eṭa`neküm felâ tebgû `aleyhinne sebîlâ. inne-llâhe kâne `aliyyen kebîrâ.'),
(528, 4, 35, 'vein ḫiftüm şiḳâḳa beynihimâ feb`aŝû ḥakemem min ehlihî veḥakemem min ehlihâ. iy yürîdâ iṣlâḥay yüveffiḳi-llâhü beynehümâ. inne-llâhe kâne `alîmen ḫabîrâ.'),
(529, 4, 36, 'va`büdü-llâhe velâ tüşrikû bihî şey\'ev vebilvâlideyni iḥsânev vebiẕi-lḳurbâ velyetâmâ velmesâkîni velcâri ẕi-lḳurbâ velcâri-lcünübi veṣṣâḥibi bilcembi vebni-ssebîli vemâ meleket eymânüküm. inne-llâhe lâ yüḥibbü men kâne muḫtâlen feḫûrâ.'),
(530, 4, 37, 'elleẕîne yebḫalûne veye\'mürûne-nnâse bilbuḫli veyektümûne mâ âtâhümü-llâhü min faḍlih. vea`tednâ lilkâfirîne `aẕâbem mühînâ.'),
(531, 4, 38, 'velleẕîne yünfiḳûne emvâlehüm riâe-nnâsi velâ yü\'minûne billâhi velâ bilyevmi-l\'âḫir. vemey yeküni-şşeyṭânü lehû ḳarînen fesâe ḳarînâ.'),
(532, 4, 39, 'vemâẕâ `aleyhim lev âmenû billâhi velyevmi-l\'âḫiri veenfeḳû mimmâ razeḳahümü-llâh. vekâne-llâhü bihim `alîmâ.'),
(533, 4, 40, 'inne-llâhe lâ yażlimü miŝḳâle ẕerrah. vein tekü ḥasenetey yüḍâ`ifhâ veyü\'ti mil ledünhü ecran `ażîmâ.'),
(534, 4, 41, 'fekeyfe iẕâ ci\'nâ min külli ümmetim bişehîdiv veci\'nâ bike `alâ hâülâi şehîdâ.'),
(535, 4, 42, 'yevmeiẕiy yeveddü-lleẕîne keferû ve`aṣavu-rrasûle lev tüsevvâ bihimü-l\'arḍ. velâ yektümûne-llâhe ḥadîŝâ.'),
(536, 4, 43, 'yâ eyyühe-lleẕîne âmenû lâ taḳrabu-ṣṣalâte veentüm sükârâ ḥattâ ta`lemû mâ teḳûlûne velâ cünüben illâ `âbirî sebîlin ḥattâ tagtesilû. vein küntüm merḍâ ev `alâ seferin ev câe eḥadüm minküm mine-lgâiṭi ev lemestümü-nnisâe felem tecidû mâen feteyemmemû ṣa`îden ṭayyiben femseḥû bivucûhiküm veeydîküm. inne-llâhe kâne `afüvven gafûrâ.'),
(537, 4, 44, 'elem tera ile-lleẕîne ûtû neṣîbem mine-lkitâbi yeşterûne-ḍḍalâlete veyürîdûne en teḍillü-ssebîl.'),
(538, 4, 45, 'vellâhü a`lemü bia`dâiküm. vekefâ billâhi veliyyâ. vekefâ billâhi neṣîrâ.'),
(539, 4, 46, 'mine-lleẕîne hâdû yüḥarrifûne-lkelime `am mevâḍi`ihî veyeḳûlûne semi`nâ ve`aṣaynâ vesma` gayra müsme`iv verâ`inâ leyyem bielsinetihim veṭa`nen fi-ddîn. velev ennehüm ḳâlû semi`nâ veeṭa`nâ vesma` venżurnâ lekâne ḫayral lehüm veaḳveme velâkil le`anehümü-llâhü biküfrihim felâ yü\'minûne illâ ḳalîlâ.'),
(540, 4, 47, 'yâ eyyühe-lleẕîne ûtü-lkitâbe âminû bimâ nezzelnâ müṣaddiḳal limâ me`aküm min ḳabli en naṭmise vucûhen feneruddehâ `alâ edbârihâ ev nel`anehüm kemâ le`annâ aṣḥâbe-ssebt. vekâne emru-llâhi mef`ûlâ.'),
(541, 4, 48, 'inne-llâhe lâ yagfiru ey yüşrake bihî veyagfiru mâ dûne ẕâlike limey yeşâ\'. vemey yüşrik billâhi feḳadi-fterâ iŝmen `ażîmâ.'),
(542, 4, 49, 'elem tera ile-lleẕîne yüzekkûne enfüsehüm. beli-llâhü yüzekkî mey yeşâü velâ yużlemûne fetîlâ.'),
(543, 4, 50, 'ünżur keyfe yefterûne `ale-llâhi-lkeẕib. vekefâ bihî iŝmem mübînâ.'),
(544, 4, 51, 'elem tera ile-lleẕîne ûtû neṣîbem mine-lkitâbi yü\'minûne bilcibti veṭṭâgûti veyeḳûlûne lilleẕîne keferû hâülâi ehdâ mine-lleẕîne âmenû sebîlâ.'),
(545, 4, 52, 'ülâike-lleẕîne le`anehümü-llâh. vemey yel`ani-llâhü felen tecide lehû neṣîrâ.'),
(546, 4, 53, 'em lehüm neṣîbüm mine-lmülki feiẕel lâ yü\'tûne-nnâse neḳîrâ.'),
(547, 4, 54, 'em yaḥsüdûne-nnâse `alâ mâ âtâhümü-llâhü min faḍlih. feḳad âteynâ âle ibrâhîme-lkitâbe velḥikmete veâteynâhüm mülken `ażîmâ.'),
(548, 4, 55, 'feminhüm men âmene bihî veminhüm men ṣadde `anh. vekefâ bicehenneme se`îrâ.'),
(549, 4, 56, 'inne-lleẕîne keferû biâyâtinâ sevfe nuṣlîhim nârâ. küllemâ neḍicet cülûdühüm beddelnâhüm cülûden gayrahâ liyeẕûḳu-l`aẕâb. inne-llâhe kâne `azîzen ḥakîmâ.'),
(550, 4, 57, 'velleẕîne âmenû ve`amilu-ṣṣâliḥâti senüdḫilühüm cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. lehüm fîhâ ezvâcüm müṭahherah. venüdḫilühüm żillen żalîlâ.'),
(551, 4, 58, 'inne-llâhe ye\'müruküm en tü\'eddü-l\'emenâti ilâ ehlihâ veiẕâ ḥakemtüm beyne-nnâsi en taḥkümû bil`adl. inne-llâhe ni`immâ ye`iżuküm bih. inne-llâhe kâne semî`am beṣîrâ.'),
(552, 4, 59, 'yâ eyyühe-lleẕîne âmenû eṭî`ü-llâhe veeṭî`ü-rrasûle veüli-l\'emri minküm. fein tenâza`tüm fî şey\'in feruddûhü ile-llâhi verrasûli in küntüm tü\'minûne billâhi velyevmi-l\'âḫir. ẕâlike ḫayruv veaḥsenü te\'vîlâ.'),
(553, 4, 60, 'elem tera ile-lleẕîne yez`umûne ennehüm âmenû bimâ ünzile ileyke vemâ ünzile min ḳablike yürîdûne ey yeteḥâkemû ile-ṭṭâgûti veḳad ümirû ey yekfürû bih. veyürîdü-şşeyṭânü ey yüḍillehüm ḍalâlem be`îdâ.'),
(554, 4, 61, 'veiẕâ ḳîle lehüm te`âlev ilâ mâ enzele-llâhü veile-rrasûli raeyte-lmünâfiḳîne yeṣuddûne `anke ṣudûdâ.'),
(555, 4, 62, 'fekeyfe iẕâ eṣâbethüm müṣîbetüm bimâ ḳaddemet eydîhim ŝümme câûke yaḥlifûne billâhi in eradnâ illâ iḥsânev vetevfîḳâ.'),
(556, 4, 63, 'ülâike-lleẕîne ya`lemü-llâhü mâ fî ḳulûbihim fea`riḍ `anhüm ve`iżhüm veḳul lehüm fî enfüsihim ḳavlem belîgâ.'),
(557, 4, 64, 'vemâ erselnâ mir rasûlin illâ liyüṭâ`a biiẕni-llâh. velev ennehüm iż żalemû enfüsehüm câûke festagferü-llâhe vestagfera lehümü-rrasûlü levecedü-llâhe tevvâber raḥîmâ.'),
(558, 4, 65, 'felâ verabbike lâ yü\'minûne ḥattâ yüḥakkimûke fîmâ şecera beynehüm ŝümme lâ yecidû fî enfüsihim ḥaracem mimmâ ḳaḍayte veyüsellimû teslîmâ.'),
(559, 4, 66, 'velev ennâ ketebnâ `aleyhim eni-ḳtülû enfüseküm evi-ḫrucû min diyâriküm mâ fe`alûhü illâ ḳalîlüm minhüm. velev ennehüm fe`alû mâ yû`ażûne bihî lekâne ḫayral lehüm veeşedde teŝbîtâ.'),
(560, 4, 67, 'veiẕel leâteynâhüm mil ledünnâ ecran `ażîmâ.'),
(561, 4, 68, 'velehedeynâhüm ṣirâṭam müsteḳîmâ.'),
(562, 4, 69, 'vemey yüṭi`i-llâhe verrasûle feülâike me`a-lleẕîne en`ame-llâhü `aleyhim mine-nnebiyyîne veṣṣiddîḳîne veşşühedâi veṣṣâliḥîn. veḥasüne ülâike rafîḳâ.'),
(563, 4, 70, 'ẕâlike-lfaḍlü mine-llâh. vekefâ billâhi `alîmâ.'),
(564, 4, 71, 'yâ eyyühe-lleẕîne âmenû ḫuẕû ḥiẕraküm fenfirû ŝübâtin evi-nfirû cemî`â.'),
(565, 4, 72, 'veinne minküm lemel leyübeṭṭienn. fein eṣâbetküm müṣîbetün ḳâle ḳad en`ame-llâhü `aleyye iẕ lem eküm me`ahüm şehîdâ.'),
(566, 4, 73, 'velein eṣâbeküm faḍlüm mine-llâhi leyeḳûlenne keel lem teküm beyneküm vebeynehû meveddetüy yâ leytenî küntü me`ahüm feefûze fevzen `ażîmâ.'),
(567, 4, 74, 'felyüḳâtil fî sebîli-llâhi-lleẕîne yeşrûne-lḥayâte-ddünyâ bil\'âḫirah. vemey yüḳâtil fî sebîli-llâhi feyuḳtel ev yaglib fesevfe nü\'tîhi ecran `ażîmâ.'),
(568, 4, 75, 'vemâ leküm lâ tüḳâtilûne fî sebîli-llâhi velmüstaḍ`afîne mine-rricâli vennisâi velvildâni-lleẕîne yeḳûlûne rabbenâ aḫricnâ min hâẕihi-lḳaryeti-żżâlimi ehlühâ. vec`al lenâ mil ledünke veliyyâ. vec`al lenâ mil ledünke neṣîrâ.'),
(569, 4, 76, 'elleẕîne âmenû yüḳâtilûne fî sebîli-llâh. velleẕîne keferû yüḳâtilûne fî sebîli-ṭṭâgûti feḳâtilû evliyâe-şşeyṭân. inne keyde-şşeyṭâni kâne ḍa`îfâ.'),
(570, 4, 77, 'elem tera ile-lleẕîne ḳîle lehüm küffû eydiyeküm veeḳîmu-ṣṣalâte veâtü-zzekâh. felemmâ kütibe `aleyhimü-lḳitâlü iẕâ ferîḳum minhüm yaḫşevne-nnâse keḫaşyeti-llâhi ev eşedde ḫaşyeh. veḳâlû rabbenâ lime ketebte `aleyne-lḳitâl. levlâ eḫḫartenâ ilâ ecelin ḳarîb. ḳul metâ`u-ddünyâ ḳalîl. vel\'âḫiratü ḫayrul limeni-tteḳâ velâ tużlemûne fetîlâ.'),
(571, 4, 78, 'eyne mâ tekûnû yüdrikkümü-lmevtü velev küntüm fî bürûcim müşeyyedeh. vein tüṣibhüm ḥasenetüy yeḳûlû hâẕihî min `indi-llâh. vein tüṣibhüm seyyietüy yeḳûlû hâẕihî min `indik. ḳul küllüm min `indi-llâh. femâ lihâülâi-lḳavmi lâ yekâdûne yefḳahûne ḥadîŝâ.'),
(572, 4, 79, 'mâ eṣâbeke min ḥasenetin femine-llâh. vemâ eṣâbeke min seyyietin femin nefsik. veerselnâke linnâsi rasûlâ. vekefâ billâhi şehîdâ.'),
(573, 4, 80, 'mey yüṭi`i-rrasûle feḳad eṭâ`a-llâh. vemen tevellâ femâ erselnâke `aleyhim ḥafîżâ.'),
(574, 4, 81, 'veyeḳûlûne ṭâ`ah. feiẕâ berazû min `indike beyyete ṭâifetüm minhüm gayra-lleẕî teḳûl. vellâhü yektübü mâ yübeyyitûn. fea`riḍ `anhüm vetevekkel `ale-llâh. vekefâ billâhi vekîlâ.'),
(575, 4, 82, 'efelâ yetedebberûne-lḳur\'ân. velev kâne min `indi gayri-llâhi levecedû fîhi-ḫtilâfen keŝîrâ.'),
(576, 4, 83, 'veiẕâ câehüm emrum mine-l\'emni evi-lḫavfi eẕâ`û bih. velev raddûhü ile-rrasûli veilâ üli-l\'emri minhüm le`alimehü-lleẕîne yestembiṭûnehû minhüm. velevlâ faḍlü-llâhi `aleyküm veraḥmetühû letteba`tümü-şşeyṭâne illâ ḳalîlâ.'),
(577, 4, 84, 'feḳâtil fî sebîli-llâh. lâ tükellefü illâ nefseke veḥarriḍi-lmü\'minîn. `asa-llâhü ey yeküffe be\'se-lleẕîne keferû. vellâhü eşeddü be\'sev veeşeddü tenkîlâ.'),
(578, 4, 85, 'mey yeşfa` şefâ`aten ḥasenetey yekül lehû neṣîbüm minhâ. vemey yeşfa` şefâ`aten seyyietey yekül lehû kiflüm minhâ. vekâne-llâhü `alâ külli şey\'im müḳîtâ.'),
(579, 4, 86, 'veiẕâ ḥuyyîtüm biteḥiyyetin feḥayyû biaḥsene minhâ ev ruddûhâ. inne-llâhe kâne `alâ külli şey\'in ḥasîbâ.'),
(580, 4, 87, 'allâhü lâ ilâhe illâ hû. leyecme`anneküm ilâ yevmi-lḳiyâmeti lâ raybe fîh. vemen aṣdeḳu mine-llâhi ḥadîŝâ.'),
(581, 4, 88, 'femâ leküm fi-lmünâfiḳîne fieteyni vellâhü erkesehüm bimâ kesebû. etürîdûne en tehdû men eḍalle-llâh. vemey yuḍlili-llâhü felen tecide lehû sebîlâ.'),
(582, 4, 89, 'veddû lev tekfürûne kemâ keferû fetekûnûne sevâen felâ tetteḫiẕû minhüm evliyâe ḥattâ yühâcirû fî sebîli-llâh. fein tevellev feḫuẕûhüm vaḳtülûhüm ḥayŝü vecettümûhüm. velâ tetteḫiẕû minhüm veliyyev velâ neṣîrâ.'),
(583, 4, 90, 'ille-lleẕîne yeṣilûne ilâ ḳavmim beyneküm vebeynehüm mîŝâḳun ev câûküm ḥaṣirat ṣudûruhüm ey yüḳâtilûküm ev yüḳâtilû ḳavmehüm. velev şâe-llâhü leselleṭahüm `aleyküm feleḳâtelûküm. feini-`tezelûküm felem yüḳâtilûküm veelḳav ileykümü-sseleme femâ ce`ale-llâhü leküm `aleyhim sebîlâ.'),
(584, 4, 91, 'setecidûne âḫarîne yürîdûne ey ye\'menûküm veye\'menû ḳavmehüm. küllemâ ruddû ile-lfitneti ürkisû fîhâ. feil lem ya`tezilûküm veyülḳû ileykümü-sseleme veyeküffû eydiyehüm feḫuẕûhüm vaḳtülûhüm ḥayŝü ŝeḳiftümûhüm. veülâiküm ce`alnâ leküm `aleyhim sülṭânem mübînâ.'),
(585, 4, 92, 'vemâ kâne limü\'minin ey yaḳtüle mü\'minen illâ ḫaṭaâ. vemen ḳatele mü\'minen ḫaṭaen fetaḥrîru raḳabetim mü\'minetiv vediyetüm müsellemetün ilâ ehlihî illâ ey yeṣṣaddeḳû. fein kâne min ḳavmin `adüvvil leküm vehüve mü\'minün fetaḥrîru raḳabetim mü\'mineh. vein kâne min ḳavmim beyneküm vebeynehüm mîŝâḳun fediyetüm müsellemetün ilâ ehlihî vetaḥrîru raḳabetim mü\'mineh. femel lem yecid feṣiyâmü şehrayni mütetâbi`ayn. tevbetem mine-llâh. vekâne-llâhü `alîmen ḥakîmâ.'),
(586, 4, 93, 'vemey yaḳtül mü\'minem müte`ammiden fecezâühû cehennemü ḫâliden fîhâ vegaḍibe-llâhü `aleyhi vele`anehû vee`adde lehû `aẕâben `ażîmâ.'),
(587, 4, 94, 'yâ eyyühe-lleẕîne âmenû iẕâ ḍarabtüm fî sebîli-llâhi fetebeyyenû velâ teḳûlû limen elḳâ ileykümü-sselâme leste mü\'minâ. tebtegûne `araḍa-lḥayâti-ddünyâ. fe`inde-llâhi megânimü keŝîrah. keẕâlike küntüm min ḳablü femenne-llâhü `aleyküm fetebeyyenû. inne-llâhe kâne bimâ ta`melûne ḫabîrâ.'),
(588, 4, 95, 'lâ yestevi-lḳâ`idûne mine-lmü\'minîne gayru üli-ḍḍarari velmücâhidûne fî sebîli-llâhi biemvâlihim veenfüsihim. feḍḍale-llâhü-lmücâhidîne biemvâlihim veenfüsihim `ale-lḳâ`idîne deraceh. veküllev ve`ade-llâhü-lḥusnâ. vefeḍḍale-llâhü-lmücâhidîne `ale-lḳâ`idîne ecran `ażîmâ.'),
(589, 4, 96, 'deracâtim minhü vemagfiratev veraḥmeh. vekâne-llâhü gafûrar raḥîmâ.'),
(590, 4, 97, 'inne-lleẕîne teveffâhümü-lmelâiketü żâlimî enfüsihim ḳâlû fîme küntüm. ḳâlû künnâ müstaḍ`afîne fi-l\'arḍ. ḳâlû elem tekün arḍu-llâhi vâsi`aten fetühâcirû fîhâ. feülâike me\'vâhüm cehennem. vesâet meṣîrâ.'),
(591, 4, 98, 'ille-lmüstaḍ`afîne mine-rricâli vennisâi velvildâni lâ yesteṭî`ûne ḥîletev velâ yehtedûne sebîlâ.'),
(592, 4, 99, 'feülâike `asa-llâhü ey ya`füve `anhüm. vekâne-llâhü `afüvven gafûrâ.'),
(593, 4, 100, 'vemey yühâcir fî sebîli-llâhi yecid fi-l\'arḍi mürâgamen keŝîrav vese`ah. vemey yaḫruc mim beytihî mühâciran ile-llâhi verasûlihî ŝümme yüdrikhü-lmevtü feḳad veḳa`a ecruhû `ale-llâh. vekâne-llâhü gafûrar raḥîmâ.'),
(594, 4, 101, 'veiẕâ ḍarabtüm fi-l\'arḍi feleyse `aleyküm cünâḥun en taḳṣurû mine-ṣṣalâh. in ḫiftüm ey yeftinekümü-lleẕîne keferû. inne-lkâfirîne kânû leküm `adüvvem mübînâ.'),
(595, 4, 102, 'veiẕâ künte fîhim feeḳamte lehümu-ṣṣalâte felteḳum ṭâifetüm minhüm me`ake velye\'ḫuẕû esliḥatehüm. feiẕâ secedû felyekûnû miv verâiküm. velte\'ti ṭâifetün uḫrâ lem yüṣallû felyüṣallû me`ake velye\'ḫuẕû ḥiẕrahüm veesliḥatehüm. vedde-lleẕîne keferû lev tagfülûne `an esliḥatiküm veemti`atiküm feyemîlûne `aleyküm meyletev vâḥideten. velâ cünâḥa `aleyküm in kâne biküm eẕem mim meṭarin ev küntüm merḍâ en teḍa`û esliḥateküm. veḫuẕû ḥiẕraküm. inne-llâhe e`adde lilkâfirîne `aẕâbem mühînâ.'),
(596, 4, 103, 'feiẕâ ḳaḍaytümu-ṣṣalâte feẕkürü-llâhe ḳiyâmev veḳu`ûdev ve`alâ cünûbiküm. feiẕe-ṭme\'nentüm feeḳîmu-ṣṣalâh. inne-ṣṣalâte kânet `ale-lmü\'minîne kitâbem mevḳûtâ.'),
(597, 4, 104, 'velâ tehinû fi-btigâi-lḳavm. in tekûnû te\'lemûne feinnehüm ye\'lemûne kemâ te\'lemûn. vetercûne mine-llâhi mâ lâ yercûn. vekâne-llâhü `alîmen ḥakîmâ.'),
(598, 4, 105, 'innâ enzelnâ ileyke-lkitâbe bilḥaḳḳi litaḥküme beyne-nnâsi bimâ erâke-llâh. velâ tekül lilḫâinîne ḫaṣîmâ.'),
(599, 4, 106, 'vestagfiri-llâh. inne-llâhe kâne gafûrar raḥîmâ.'),
(600, 4, 107, 'velâ tücâdil `ani-lleẕîne yaḫtânûne enfüsehüm. inne-llâhe lâ yüḥibbü men kâne ḫavvânen eŝîmâ.'),
(601, 4, 108, 'yestaḫfûne mine-nnâsi velâ yestaḫfûne mine-llâhi vehüve me`ahüm iẕ yübeyyitûne mâ lâ yerḍâ mine-lḳavl. vekâne-llâhü bimâ ya`melûne müḥîṭâ.'),
(602, 4, 109, 'hâentüm hâülâi câdeltüm `anhüm fi-lḥayâti-ddünyâ femey yücâdilü-llâhe `anhüm yevme-lḳiyâmeti em mey yekûnü `aleyhim vekîlâ.'),
(603, 4, 110, 'vemey ya`mel sûen ev yażlim nefsehû ŝümme yestagfiri-llâhe yecidi-llâhe gafûrar raḥîmâ.'),
(604, 4, 111, 'vemey yeksib iŝmen feinnemâ yeksibühû `alâ nefsih. vekâne-llâhü `alîmen ḥakîmâ.'),
(605, 4, 112, 'vemey yeksib ḫaṭîeten ev iŝmen ŝümme yermi bihî berîen feḳadi-ḥtemele bühtânev veiŝmem mübînâ.'),
(606, 4, 113, 'velevlâ faḍlü-llâhi `aleyke veraḥmetühû lehemmeṭ ṭâifetüm minhüm ey yüḍillûk. vemâ yüḍillûne illâ enfüsehüm vemâ yeḍurrûneke min şey\'. veenzele-llâhü `aleyke-lkitâbe velḥikmete ve`allemeke mâ lem tekün ta`lem. vekâne faḍlü-llâhi `aleyke `ażîmâ.'),
(607, 4, 114, 'lâ ḫayra fî keŝîrim min necvâhüm illâ men emera biṣadeḳatin ev ma`rûfin ev iṣlâḥim beyne-nnâs. vemey yef`al ẕâlike-btigâe merḍâti-llâhi fesevfe nü\'tîhi ecran `ażîmâ.'),
(608, 4, 115, 'vemey yüşâḳiḳi-rrasûle mim ba`di mâ tebeyyene lehü-lhüdâ veyettebi` gayra sebîli-lmü\'minîne nüvellihî mâ tevellâ venuṣlihî cehennem. vesâet meṣîrâ.'),
(609, 4, 116, 'inne-llâhe lâ yagfiru ey yüşrake bihî veyagfiru mâ dûne ẕâlike limey yeşâ\'. vemey yüşrik billâhi feḳad ḍalle ḍalâlem be`îdâ.'),
(610, 4, 117, 'iy yed`ûne min dûnihî illâ inâŝâ. veiy yed`ûne illâ şeyṭânem merîdâ.'),
(611, 4, 118, 'le`anehü-llâh. veḳâle leetteḫiẕenne min `ibâdike neṣîbem mefrûḍâ.'),
(612, 4, 119, 'veleüḍillennehüm veleümenniyennehüm veleâmürannehüm feleyübettikünne âẕâne-l\'en`âmi veleâmürannehüm feleyügayyirunne ḫalḳa-llâh. vemey yetteḫiẕi-şşeyṭâne veliyyem min dûni-llâhi feḳad ḫasira ḫusrânem mübînâ.'),
(613, 4, 120, 'ye`idühüm veyümennîhim. vemâ ye`idühümü-şşeyṭânü illâ gurûrâ.'),
(614, 4, 121, 'ülâike me\'vâhüm cehennemü velâ yecidûne `anhâ meḥîṣâ.'),
(615, 4, 122, 'velleẕîne âmenû ve`amilu-ṣṣâliḥâti senüdḫilühüm cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. va`de-llâhi ḥaḳḳâ. vemen aṣdeḳu mine-llâhi ḳîlâ.'),
(616, 4, 123, 'leyse biemâniyyiküm velâ emâniyyi ehli-lkitâb. mey ya`mel sûey yücze bihî velâ yecid lehû min dûni-llâhi veliyyev velâ neṣîrâ.'),
(617, 4, 124, 'vemey ya`mel mine-ṣṣâliḥâti min ẕekerin ev ünŝâ vehüve mü\'minün feülâike yedḫulûne-lcennete velâ yużlemûne neḳîrâ.'),
(618, 4, 125, 'vemen aḥsenü dînem mimmen esleme vechehû lillâhi vehüve muḥsinüv vettebe`a millete ibrâhîme ḥanîfâ. vetteḫaẕe-llâhü ibrâhîme ḫalîlâ.'),
(619, 4, 126, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vekâne-llâhü bikülli şey\'im müḥîṭâ.'),
(620, 4, 127, 'veyesteftûneke fi-nnisâ\'. ḳuli-llâhü yüftîküm fîhinne vemâ yütlâ `aleyküm fi-lkitâbi fî yetâme-nnisâi-llâtî lâ tü\'tûnehünne mâ kütibe lehünne vetergabûne en tenkiḥûhünne velmüstaḍ`afîne mine-lvildâni veen teḳûmû lilyetâmâ bilḳisṭ. vemâ tef`alû min ḫayrin feinne-llâhe kâne bihî `alîmâ.'),
(621, 4, 128, 'veini-mraetün ḫâfet mim ba`lihâ nüşûzen ev i`râḍan felâ cünâḥa `aleyhimâ ey yuṣliḥâ beynehümâ ṣulḥâ. veṣṣulḥu ḫayr. veuḥḍirati-l\'enfüsü-şşüḥḥ. vein tuḥsinû vetetteḳû feinne-llâhe kâne bimâ ta`melûne ḫabîrâ.'),
(622, 4, 129, 'velen testeṭî`û en ta`dilû beyne-nnisâi velev ḥaraṣtüm felâ temîlû külle-lmeyli feteẕerûhâ kelmü`alleḳah. vein tuṣliḥû vetetteḳû feinne-llâhe kâne gafûrar raḥîmâ.'),
(623, 4, 130, 'veiy yeteferraḳâ yugni-llâhü küllem min se`atih. vekâne-llâhü vâsi`an ḥakîmâ.'),
(624, 4, 131, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. veleḳad veṣṣayne-lleẕîne ûtü-lkitâbe min ḳabliküm veiyyâküm eni-tteḳu-llâh. vein tekfürû feinne lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vekâne-llâhü ganiyyen ḥamîdâ.'),
(625, 4, 132, 'velillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vekefâ billâhi vekîlâ.'),
(626, 4, 133, 'iy yeşe\' yüẕhibküm eyyühe-nnâsü veye\'ti biâḫarîn. vekâne-llâhü `alâ ẕâlike ḳadîrâ.'),
(627, 4, 134, 'men kâne yürîdü ŝevâbe-ddünyâ fe`inde-llâhi ŝevâbü-ddünyâ vel\'âḫirah. vekâne-llâhü semî`am beṣîrâ.'),
(628, 4, 135, 'yâ eyyühe-lleẕîne âmenû kûnû ḳavvâmîne bilḳisṭi şühedâe lillâhi velev `alâ enfüsiküm evi-lvâlideyni vel\'aḳrabîn. iy yekün ganiyyen ev feḳîran fellâhü evlâ bihimâ felâ tettebi`ü-lhevâ en ta`dilû. vein telvû ev tü`riḍû feinne-llâhe kâne bimâ ta`melûne ḫabîrâ.'),
(629, 4, 136, 'yâ eyyühe-lleẕîne âmenû âminû billâhi verasûlihî velkitâbi-lleẕî nezzele `alâ rasûlihî velkitâbi-lleẕî enzele min ḳabl. vemey yekfür billâhi vemelâiketihî vekütübihî verusülihî velyevmi-l\'âḫiri feḳad ḍalle ḍalâlem be`îdâ.'),
(630, 4, 137, 'inne-lleẕîne âmenû ŝümme keferû ŝümme âmenû ŝümme keferû ŝümme-zdâdû küfral lem yeküni-llâhü liyagfira lehüm velâ liyehdiyehüm sebîlâ.'),
(631, 4, 138, 'beşşiri-lmünâfiḳîne bienne lehüm `aẕâben elîmâ.'),
(632, 4, 139, 'elleẕîne yetteḫiẕûne-lkâfirîne evliyâe min dûni-lmü\'minîn. eyebtegûne `indehümü-l`izzete feinne-l`izzete lillâhi cemî`â.'),
(633, 4, 140, 'veḳad nezzele `aleyküm fi-lkitâbi en iẕâ semi`tüm âyâti-llâhi yükferu bihâ veyüstehzeü bihâ felâ taḳ`udû me`ahüm ḥattâ yeḫûḍû fî ḥadîŝin gayrihi. inneküm iẕem miŝlühüm. inne-llâhe câmi`u-lmünâfiḳîne velkâfirîne fî cehenneme cemî`â.'),
(634, 4, 141, 'elleẕîne yeterabbeṣûne biküm. fein kâne leküm fetḥum mine-llâhi ḳâlû elem neküm me`aküm. vein kâne lilkâfirîne neṣîbün ḳâlû elem nestaḥviẕ `aleyküm venemna`küm mine-lmü\'minîn. fellâhü yaḥkümü beyneküm yevme-lḳiyâmeh. veley yec`ale-llâhü lilkâfirîne `ale-lmü\'minîne sebîlâ.'),
(635, 4, 142, 'inne-lmünâfiḳîne yüḫâdi`ûne-llâhe vehüve ḫâdi`uhüm. veiẕâ ḳâmû ile-ṣṣalâti ḳâmû küsâlâ yürâûne-nnâse velâ yeẕkürûne-llâhe illâ ḳalîlâ.'),
(636, 4, 143, 'müẕebẕebîne beyne ẕâlik. lâ ilâ hâülâi velâ ilâ hâülâ\'. vemey yuḍlili-llâhü felen tecide lehû sebîlâ.'),
(637, 4, 144, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕü-lkâfirîne evliyâe min dûni-lmü\'minîn. etürîdûne en tec`alû lillâhi `aleyküm sülṭânem mübînâ.'),
(638, 4, 145, 'inne-lmünâfiḳîne fi-dderki-l\'esfeli mine-nnâr. velen tecide lehüm neṣîrâ.'),
(639, 4, 146, 'ille-lleẕîne tâbû veaṣleḥû va`teṣamû billâhi veaḫleṣû dînehüm lillâhi feülâike me`a-lmü\'minîn. vesevfe yü\'ti-llâhü-lmü\'minîne ecran `ażîmâ.'),
(640, 4, 147, 'mâ yef`alü-llâhü bi`aẕâbiküm in şekertüm veâmentüm. vekâne-llâhü şâkiran `alîmâ.'),
(641, 4, 148, 'lâ yüḥibbü-llâhü-lcehra bissûi mine-lḳavli illâ men żulim. vekâne-llâhü semî`an `alîmâ.'),
(642, 4, 149, 'in tübdû ḫayran ev tuḫfûhü ev ta`fû `an sûin feinne-llâhe kâne `afüvven ḳadîrâ.'),
(643, 4, 150, 'inne-lleẕîne yekfürûne billâhi verusülihî veyürîdûne ey yüferriḳû beyne-llâhi verusülihî veyeḳûlûne nü\'minü biba`ḍiv venekfüru biba`ḍiv veyürîdûne ey yetteḫiẕû beyne ẕâlike sebîlâ.'),
(644, 4, 151, 'ülâike hümü-lkâfirûne ḥaḳḳâ. vea`tednâ lilkâfirîne `aẕâbem mühînâ.'),
(645, 4, 152, 'velleẕîne âmenû billâhi verusülihî velem yüferriḳû beyne eḥadim minhüm ülâike sevfe yü\'tîhim ücûrahüm. vekâne-llâhü gafûrar raḥîmâ.'),
(646, 4, 153, 'yes\'elüke ehlü-lkitâbi en tünezzile `aleyhim kitâbem mine-ssemâi feḳad seelû mûsâ ekbera min ẕâlike feḳâlû erine-llâhe cehraten feeḫaẕethümu-ṣṣâ`iḳatü biżulmihim. ŝümme-tteḫaẕü-l`icle mim ba`di mâ câethümü-lbeyyinâtü fe`afevnâ `an ẕâlik. veâteynâ mûsâ sülṭânem mübînâ.'),
(647, 4, 154, 'verafa`nâ fevḳahümu-ṭṭûra bimîŝâḳihim veḳulnâ lehümü-dḫulü-lbâbe süccedev veḳulnâ lehüm lâ ta`dû fi-ssebti veeḫaẕnâ minhüm mîŝâḳan galîżâ.'),
(648, 4, 155, 'febimâ naḳḍihim mîŝâḳahüm veküfrihim biâyâti-llâhi veḳatlihimü-l\'embiyâe bigayri ḥaḳḳiv veḳavlihim ḳulûbünâ gulf. bel ṭabe`a-llâhü `aleyhâ biküfrihim felâ yü\'minûne illâ ḳalîlâ.'),
(649, 4, 156, 'vebiküfrihim veḳavlihim `alâ meryeme bühtânen `ażîmâ.'),
(650, 4, 157, 'veḳavlihim innâ ḳatelne-lmesîḥa `îse-bne meryeme rasûle-llâh. vemâ ḳatelûhü vemâ ṣalebûhü velâkin şübbihe lehüm. veinne-lleẕîne-ḫtelefû fîhi lefî şekkim minh. mâ lehüm bihî min `ilmin ille-ttibâ`a-żżann. vemâ ḳatelûhü yeḳînâ.'),
(651, 4, 158, 'ber rafe`ahü-llâhü ileyh. vekâne-llâhü `azîzen ḥakîmâ.'),
(652, 4, 159, 'veim min ehli-lkitâbi illâ leyü\'minenne bihî ḳable mevtih. veyevme-lḳiyâmeti yekûnü `aleyhim şehîdâ.'),
(653, 4, 160, 'febiżulmim mine-lleẕîne hâdû ḥarramnâ `aleyhim ṭayyibâtin üḥillet lehüm vebiṣaddihim `an sebîli-llâhi keŝîrâ.'),
(654, 4, 161, 'veaḫẕihimü-rribâ veḳad nühû `anhü veeklihim emvâle-nnâsi bilbâṭil. vea`tednâ lilkâfirîne minhüm `aẕâben elîmâ.'),
(655, 4, 162, 'lâkini-rrâsiḫûne fi-l`ilmi minhüm velmü\'minûne yü\'minûne bimâ ünzile ileyke vemâ ünzile min ḳablike velmüḳîmîne-ṣṣalâte velmü\'tûne-zzekâte velmü\'minûne billâhi velyevmi-l\'âḫir. ülâike senü\'tîhim ecran `ażîmâ.'),
(656, 4, 163, 'innâ evḥaynâ ileyke kemâ evḥaynâ ilâ nûḥiv vennebiyyîne mim ba`dih. veevḥaynâ ilâ ibrâhîme veismâ`île veisḥâḳa veya`ḳûbe vel\'esbâṭi ve`îsâ veeyyûbe veyûnüse vehârûne vesüleymân. veâteynâ dâvûde zebûrâ.'),
(657, 4, 164, 'verusülen ḳad ḳaṣaṣnâhüm `aleyke min ḳablü verusülel lem naḳṣuṣhüm `aleyk. vekelleme-llâhü mûsâ teklîmâ.'),
(658, 4, 165, 'rusülem mübeşşirîne vemünẕirîne liellâ yekûne linnâsi `ale-llâhi ḥuccetüm ba`de-rrusül. vekâne-llâhü `azîzen ḥakîmâ.'),
(659, 4, 166, 'lâkini-llâhü yeşhedü bimâ enzele ileyke enzelehû bi`ilmih. velmelâiketü yeşhedûn. vekefâ billâhi şehîdâ.'),
(660, 4, 167, 'inne-lleẕîne keferû veṣaddû `an sebîli-llâhi ḳad ḍallû ḍalâlem be`îdâ.'),
(661, 4, 168, 'inne-lleẕîne keferû veżalemû lem yeküni-llâhü liyagfira lehüm velâ liyehdiyehüm ṭarîḳâ.'),
(662, 4, 169, 'illâ ṭarîḳa cehenneme ḫâlidîne fîhâ ebedâ. vekâne ẕâlike `ale-llâhi yesîrâ.'),
(663, 4, 170, 'yâ eyyühe-nnâsü ḳad câekümü-rrasûlü bilḥaḳḳi mir rabbiküm feâminû ḫayral leküm. vein tekfürû feinne lillâhi mâ fi-ssemâvâti vel\'arḍ. vekâne-llâhü `alîmen ḥakîmâ.'),
(664, 4, 171, 'yâ ehle-lkitâbi lâ taglû fî dîniküm velâ teḳûlû `ale-llâhi ille-lḥaḳḳ. inneme-lmesîḥu `îse-bnü meryeme rasûlü-llâhi vekelimetüh. elḳâhâ ilâ meryeme verûḥum minh. feâminû billâhi verusülih. velâ teḳûlû ŝelâŝeh. intehû ḫayral leküm. inneme-llâhü ilâhüv vâḥid. sübḥâneh ey yekûne lehû veled. lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. vekefâ billâhi vekîlâ.'),
(665, 4, 172, 'ley yestenkife-lmesîḥu ey yekûne `abdel lillâhi vele-lmelâiketü-lmüḳarrabûn. vemey yestenkif `an `ibâdetihî veyestekbir feseyaḥşüruhüm ileyhi cemî`â.'),
(666, 4, 173, 'feemme-lleẕîne âmenû ve`amilu-ṣṣâliḥâti feyüveffîhim ücûrahüm veyezîdühüm min faḍlih. veemme-lleẕîne-stenkefû vestekberû feyü`aẕẕibühüm `aẕâben elîmev velâ yecidûne lehüm min dûni-llâhi veliyyev velâ neṣîrâ.'),
(667, 4, 174, 'yâ eyyühe-nnâsü ḳad câeküm bürhânüm mir rabbiküm veenzelnâ ileyküm nûram mübînâ.'),
(668, 4, 175, 'feemme-lleẕîne âmenû billâhi va`teṣamû bihî feseyüdḫilühüm fî raḥmetim minhü vefaḍliv veyehdîhim ileyhi ṣirâṭam müsteḳîmâ.'),
(669, 4, 176, 'yesteftûnek. ḳuli-llâhü yüftîküm fi-lkelâleh. ini-mruün heleke leyse lehû veledüv velehû uḫtün felehâ niṣfü mâ terak. vehüve yeriŝühâ il lem yekül lehâ veledün. fein kânete-ŝneteyni felehüme-ŝŝülüŝâni mimmâ terak. vein kânû iḫveter ricâlev venisâen feliẕẕekeri miŝlü ḥażżi-l\'ünŝeyeyn. yübeyyinü-llâhü leküm en teḍillû. vellâhü bikülli şey\'in `alîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'yâ eyyühe-lleẕîne âmenû evfû bil`uḳûd. üḥillet leküm behîmetü-l\'en`âmi illâ mâ yütlâ `aleyküm gayra müḥilli-ṣṣaydi veentüm ḥurum. inne-llâhe yaḥkümü mâ yürîd.'),
(671, 5, 2, 'yâ eyyühe-lleẕîne âmenû lâ tüḥillû şe`âira-llâhi vele-şşehra-lḥarâme vele-lhedye vele-lḳalâide velâ âmmîne-lbeyte-lḥarâme yebtegûne faḍlem mir rabbihim veriḍvânâ. veiẕâ ḥaleltüm faṣṭâdû. velâ yecrimenneküm şeneânü ḳavmin en ṣaddûküm `ani-lmescidi-lḥarâmi en ta`tedû. vete`âvenû `ale-lbirri vettaḳvâ. velâ te`âvenû `ale-l\'iŝmi vel`udvân. vetteḳu-llâh. inne-llâhe şedîdü-l`iḳâb.'),
(672, 5, 3, 'ḥurrimet `aleykümü-lmeytetü veddemü velaḥmü-lḫinzîri vemâ ühille ligayri-llâhi bihî velmünḫaniḳatü velmevḳûẕetü velmüteraddiyetü venneṭîḥatü vemâ ekele-ssebü`u illâ mâ ẕekkeytüm vemâ ẕübiḥa `ale-nnüṣubi veen testaḳsimû bil\'ezlâm. ẕâliküm fisḳ. elyevme yeise-lleẕîne keferû min dîniküm felâ taḫşevhüm vaḫşevn. elyevme ekmeltü leküm dîneküm veetmemtü `aleyküm ni`metî veraḍîtü lekümü-l\'islâme dînâ. femeni-ḍṭurra fî maḫmeṣatin gayra mütecânifil liiŝmin feinne-llâhe gafûrur raḥîm.'),
(673, 5, 4, 'yes\'elûneke mâẕâ üḥille lehüm. ḳul üḥille lekümu-ṭṭayyibâtü vemâ `allemtüm mine-lcevâriḥi mükellibîne tü`allimûnehünne mimmâ `allemekümü-llâh. fekülû mimmâ emsekne `aleyküm veẕkürü-sme-llâhi `aleyh. vetteḳu-llâh. inne-llâhe serî`u-lḥisâb.'),
(674, 5, 5, 'elyevme üḥille lekümu-ṭṭayyibât. veṭa`âmü-lleẕîne ûtü-lkitâbe ḥillül leküm. veṭa`âmüküm ḥillül lehüm. velmuḥṣanâtü mine-lmü\'minâti velmuḥṣanâtü mine-lleẕîne ûtü-lkitâbe min ḳabliküm iẕâ âteytümûhünne ücûrahünne muḥṣinîne gayra müsâfiḥîne velâ mütteḫiẕî aḫdân. vemey yekfür bil\'îmâni feḳad ḥabiṭa `amelüh. vehüve fi-l\'âḫirati mine-lḫâsirîn.'),
(675, 5, 6, 'yâ eyyühe-lleẕîne âmenû iẕâ ḳumtüm ile-ṣṣalâti fagsilû vucûheküm veeydiyeküm ile-lmerâfiḳi vemseḥû biruûsiküm veercüleküm ile-lka`beyn. vein küntüm cünüben feṭṭahherû. vein küntüm merḍâ ev `alâ seferin ev câe eḥadüm minküm mine-lgâiṭi ev lemestümü-nnisâe felem tecidû mâen feteyemmemû ṣa`îden ṭayyiben femseḥû bivucûhiküm veeydîküm minh. mâ yürîdü-llâhü liyec`ale `aleyküm min ḥaraciv velâkiy yürîdü liyüṭahhiraküm veliyütimme ni`metehû `aleyküm le`alleküm teşkürûn.'),
(676, 5, 7, 'veẕkürû ni`mete-llâhi `aleyküm vemîŝâḳahü-lleẕî vâŝeḳaküm bihî iẕ ḳultüm semi`nâ veeṭa`nâ. vetteḳu-llâh. inne-llâhe `alîmüm biẕâti-ṣṣudûr.'),
(677, 5, 8, 'yâ eyyühe-lleẕîne âmenû kûnû ḳavvâmîne lillâhi şühedâe bilḳisṭ. velâ yecrimenneküm şeneânü ḳavmin `alâ ellâ ta`dilû. i`dilû. hüve aḳrabü littaḳvâ. vetteḳu-llâh. inne-llâhe ḫabîrum bimâ ta`melûn.'),
(678, 5, 9, 've`ade-llâhü-lleẕîne âmenû ve`amilu-ṣṣâliḥâti lehüm magfiratüv veecrun `ażîm.'),
(679, 5, 10, 'velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-lceḥîm.'),
(680, 5, 11, 'yâ eyyühe-lleẕîne âmenü-ẕkürû ni`mete-llâhi `aleyküm iẕ hemme ḳavmün ey yebsüṭû ileyküm eydiyehüm fekeffe eydiyehüm `anküm. vetteḳu-llâh. ve`ale-llâhi felyetevekkeli-lmü\'minûn.'),
(681, 5, 12, 'veleḳad eḫaẕe-llâhü mîŝâḳa benî isrâîl. vebe`aŝnâ minhümü-ŝney `aşera neḳîbâ. veḳâle-llâhü innî me`aküm. lein eḳamtümu-ṣṣalâte veâteytümü-zzekâte veâmentüm birusülî ve`azzertümûhüm veaḳraḍtümü-llâhe ḳarḍan ḥasenel leükeffiranne `anküm seyyiâtiküm veleüdḫilenneküm cennâtin tecrî min taḥtihe-l\'enhâr. femen kefera ba`de ẕâlike minküm feḳad ḍalle sevâe-ssebîl.'),
(682, 5, 13, 'febimâ naḳḍihim mîŝâḳahüm le`annâhüm vece`alnâ ḳulûbehüm ḳâsiyeh. yüḥarrifûne-lkelime `am mevâḍi`ihî venesû ḥażżam mimmâ ẕükkirû bih. velâ tezâlü teṭṭali`u `alâ ḫâinetim minhüm illâ ḳalîlem minhüm fa`fü `anhüm vaṣfaḥ. inne-llâhe yüḥibbü-lmuḥsinîn.'),
(683, 5, 14, 'vemine-lleẕîne ḳâlû innâ neṣârâ eḫaẕnâ mîŝâḳahüm fenesû ḥażżam mimmâ ẕükkirû bih. feagraynâ beynehümü-l`adâvete velbagḍâe ilâ yevmi-lḳiyâmeh. vesevfe yünebbiühümü-llâhü bimâ kânû yaṣne`ûn.'),
(684, 5, 15, 'yâ ehle-lkitâbi ḳad câeküm rasûlünâ yübeyyinü leküm keŝîram mimmâ küntüm tuḫfûne mine-lkitâbi veya`fû `an keŝîr. ḳad câeküm mine-llâhi nûruv vekitâbüm mübîn.'),
(685, 5, 16, 'yehdî bihi-llâhü meni-ttebe`a riḍvânehû sübüle-sselâmi veyuḫricühüm mine-żżulümâti ile-nnûri biiẕnihî veyehdîhim ilâ ṣirâṭim müsteḳîm.'),
(686, 5, 17, 'leḳad kefera-lleẕîne ḳâlû inne-llâhe hüve-lmesîḥu-bnü meryem. ḳul femey yemlikü mine-llâhi şey\'en in erâde ey yühlike-lmesîḥa-bne meryeme veümmehû vemen fi-l\'arḍi cemî`â. velillâhi mülkü-ssemâvâti vel\'arḍi vemâ beynehümâ. yaḫlüḳu mâ yeşâ\'. vellâhü `alâ külli şey\'in ḳadîr.'),
(687, 5, 18, 'veḳâleti-lyehûdü venneṣârâ naḥnü ebnâü-llâhi veeḥibbâüh. ḳul felime yü`aẕẕibüküm biẕünûbiküm. bel entüm beşerum mimmen ḫaleḳ. yagfiru limey yeşâü veyü`aẕẕibü mey yeşâ\'. velillâhi mülkü-ssemâvâti vel\'arḍi vemâ beynehümâ. veileyhi-lmeṣîr.'),
(688, 5, 19, 'yâ ehle-lkitâbi ḳad câeküm rasûlünâ yübeyyinü leküm `alâ fetratim mine-rrusüli en teḳûlû mâ câenâ mim beşîriv velâ neẕîr. feḳad câeküm beşîruv veneẕîr. vellâhü `alâ külli şey\'in ḳadîr.'),
(689, 5, 20, 'veiẕ ḳâle mûsâ liḳavmihî yâ ḳavmi-ẕkürû ni`mete-llâhi `aleyküm iẕ ce`ale fîküm embiyâe vece`aleküm mülûkâ. veâtâküm mâ lem yü\'ti eḥadem mine-l`âlemîn.'),
(690, 5, 21, 'yâ ḳavmi-dḫulü-l\'arḍa-lmüḳaddesete-lletî ketebe-llâhü leküm velâ terteddû `alâ edbâriküm fetenḳalibû ḫâsirîn.'),
(691, 5, 22, 'ḳâlû yâ mûsâ inne fîhâ ḳavmen cebbârîn. veinnâ len nedḫulehâ ḥattâ yaḫrucû minhâ. feiy yaḫrucû minhâ feinnâ dâḫilûn.'),
(692, 5, 23, 'ḳâle racülâni mine-lleẕîne yeḫâfûne en`ame-llâhü `aleyhime-dḫulû `aleyhimü-lbâb. feiẕâ deḫaltümûhü feinneküm gâlibûne ve`ale-llâhi fetevekkelû in küntüm mü\'minîn.'),
(693, 5, 24, 'ḳâlû yâ mûsâ innâ len nedḫulehâ ebedem mâ dâmû fîhâ feẕheb ente verabbüke feḳâtilâ innâ hâhünâ ḳâ`idûn.'),
(694, 5, 25, 'ḳâle rabbi innî lâ emlikü illâ nefsî veeḫî fefruḳ beynenâ vebeyne-lḳavmi-lfâsiḳîn.'),
(695, 5, 26, 'ḳâle feinnehâ müḥarrametün `aleyhim erbe`îne seneh. yetîhûne fi-l\'arḍi felâ te\'se `ale-lḳavmi-lfâsiḳîn.'),
(696, 5, 27, 'vetlü `aleyhim nebee-bney âdeme bilḥaḳḳ. iẕ ḳarrabâ ḳurbânen fetüḳubbile min eḥadihimâ velem yüteḳabbel mine-l\'âḫar. ḳâle leaḳtülennek. ḳâle innemâ yeteḳabbelü-llâhü mine-lmütteḳîn.'),
(697, 5, 28, 'leim besaṭte ileyye yedeke litaḳtülenî mâ ene bibâsiṭiy yediye ileyke liaḳtülek. innî eḫâfü-llâhe rabbe-l`âlemîn.'),
(698, 5, 29, 'innî ürîdü en tebûe biiŝmî veiŝmike fetekûne min aṣḥâbi-nnâr. veẕâlike cezâu-żżâlimîn.'),
(699, 5, 30, 'feṭavve`at lehû nefsühû ḳatle eḫîhi feḳatelehû feaṣbeḥa mine-lḫâsirîn.'),
(700, 5, 31, 'febe`aŝe-llâhü gurâbey yebḥaŝü fi-l\'arḍi liyüriyehû keyfe yüvârî sev\'ete eḫîh. ḳâle yâ veyletâ e`aceztü en ekûne miŝle hâẕe-lgurâbi feüvâriye sev\'ete eḫî. feaṣbeḥa mine-nnâdimîn.'),
(701, 5, 32, 'min ecli ẕâlik. ketebnâ `alâ benî isrâîle ennehû men ḳatele nefsem bigayri nefsin ev fesâdin fi-l\'arḍi fekeennemâ ḳatele-nnâse cemî`â. vemen aḥyâhâ fekeennemâ aḥye-nnâse cemî`â. veleḳad câethüm rusülünâ bilbeyyinât. ŝümme inne keŝîram minhüm ba`de ẕâlike fi-l\'arḍi lemüsrifûn.'),
(702, 5, 33, 'innemâ cezâü-lleẕîne yüḥâribûne-llâhe verasûlehû veyes`avne fi-l\'arḍi fesâden ey yüḳattelû ev yüṣallebû ev tüḳaṭṭa`a eydîhim veercülühüm min ḫilâfin ev yünfev mine-l\'arḍ. ẕâlike lehüm ḫizyün fi-ddünyâ velehüm fi-l\'âḫirati `aẕâbün `ażîm.'),
(703, 5, 34, 'ille-lleẕîne tâbû min ḳabli en taḳdirû `aleyhim. fa`lemû enne-llâhe gafûrur raḥîm.'),
(704, 5, 35, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe vebtegû ileyhi-lvesîlete vecâhidû fî sebîlihî le`alleküm tüfliḥûn.'),
(705, 5, 36, 'inne-lleẕîne keferû lev enne lehüm mâ fi-l\'arḍi cemî`av vemiŝlehû me`ahû liyeftedû bihî min `aẕâbi yevmi-lḳiyâmeti mâ tüḳubbile minhüm. velehüm `aẕâbün elîm.'),
(706, 5, 37, 'yürîdûne ey yaḫrucû mine-nnâri vemâ hüm biḫâricîne minhâ. velehüm `aẕâbüm müḳîm.'),
(707, 5, 38, 'vessâriḳu vessâriḳatü faḳṭa`û eydiyehümâ cezâem bimâ kesebâ nekâlem mine-llâh. vellâhü `azîzün ḥakîm.'),
(708, 5, 39, 'femen tâbe mim ba`di żulmihî veaṣleḥa feinne-llâhe yetûbü `aleyh. inne-llâhe gafûrur raḥîm.'),
(709, 5, 40, 'elem ta`lem enne-llâhe lehû mülkü-ssemâvâti vel\'arḍi yü`aẕẕibü mey yeşâü veyagfiru limey yeşâ\'. vellâhü `alâ külli şey\'in ḳadîr.'),
(710, 5, 41, 'yâ eyyühe-rrasûlü lâ yaḥzünke-lleẕîne yüsâri`ûne fi-lküfri mine-lleẕîne ḳâlû âmennâ biefvâhihim velem tü\'min ḳulûbühüm. vemine-lleẕîne hâdû semmâ`ûne lilkeẕibi semmâ`ûne liḳavmin âḫarîne lem ye\'tûk. yüḥarrifûne-lkelime mim ba`di mevâḍi`ih. yeḳûlûne in ûtîtüm hâẕâ feḫuẕûhü veil lem tü\'tevhü faḥẕerû. vemey yüridi-llâhü fitnetehû felen temlike lehû mine-llâhi şey\'â. ülâike-lleẕîne lem yüridi-llâhü ey yüṭahhira ḳulûbehüm. lehüm fi-ddünyâ ḫizyüv velehüm fi-l\'âḫirati `aẕâbün `ażîm.'),
(711, 5, 42, 'semmâ`ûne lilkeẕibi ekkâlûne lissuḥt. fein câûke faḥküm beynehüm ev a`riḍ `anhüm. vein tü`riḍ `anhüm feley yeḍurrûke şey\'â. vein ḥakemte faḥküm beynehüm bilḳisṭ. inne-llâhe yüḥibbü-lmuḳsiṭîn.'),
(712, 5, 43, 'vekeyfe yüḥakkimûneke ve`indehümü-ttevrâtü fîhâ ḥukmü-llâhi ŝümme yetevellevne mim ba`di ẕâlik. vemâ ülâike bilmü\'minîn.'),
(713, 5, 44, 'innâ enzelne-ttevrâte fîhâ hüdev venûr. yaḥkümü bihe-nnebiyyûne-lleẕîne eslemû lilleẕîne hâdû verrabbâniyyûne vel\'aḥbâru bime-stuḥfiżû min kitâbi-llâhi vekânû `aleyhi şühedâ\'. felâ taḫşevu-nnâse vaḫşevni velâ teşterû biâyâtî ŝemenen ḳalîlâ. vemel lem yaḥküm bimâ enzele-llâhü feülâike hümü-lkâfirûn.'),
(714, 5, 45, 'veketebnâ `aleyhim fîhâ enne-nnefse binnefsi vel`ayne bil`ayni vel\'enfe bil\'enfi vel\'üẕüne bil\'üẕüni vessinne bissinni velcürûḥa ḳiṣâṣ. femen teṣaddeḳa bihî fehüve keffâratül leh. vemel lem yaḥküm bimâ enzele-llâhü feülâike hümu-żżâlimûn.'),
(715, 5, 46, 'veḳaffeynâ `alâ âŝârihim bi`îse-bni meryeme müṣaddiḳal limâ beyne yedeyhi mine-ttevrât. veâteynâhü-l\'incîle fîhi hüdev venûruv vemüṣaddiḳal limâ beyne yedeyhi mine-ttevrâti vehüdev vemev`iżatel lilmütteḳîn.'),
(716, 5, 47, 'velyaḥküm ehlü-l\'incîli bimâ enzele-llâhü fîh. vemel lem yaḥküm bimâ enzele-llâhü feülâike hümü-lfâsiḳûn.'),
(717, 5, 48, 'veenzelnâ ileyke-lkitâbe bilḥaḳḳi müṣaddiḳal limâ beyne yedeyhi mine-lkitâbi vemüheyminen `aleyhi faḥküm beynehüm bimâ enzele-llâhü velâ tettebi` ehvâehüm `ammâ câeke mine-lḥaḳḳ. liküllin ce`alnâ minküm şir`atev veminhâcâ. velev şâe-llâhü lece`aleküm ümmetev vâḥidetev velâkil liyeblüveküm fî mâ âtâküm festebiḳu-lḫayrât. ile-llâhi merci`uküm cemî`an feyünebbiüküm bimâ küntüm fîhi taḫtelifûn.'),
(718, 5, 49, 'veeni-ḥküm beynehüm bimâ enzele-llâhü velâ tettebi` ehvâehüm vaḥẕerhüm ey yeftinûke `am ba`ḍi mâ enzele-llâhü ileyk. fein tevellev fa`lem ennemâ yürîdü-llâhü ey yüṣîbehüm biba`ḍi ẕünûbihim. veinne keŝîram mine-nnâsi lefâsiḳûn.'),
(719, 5, 50, 'efeḥukme-lcâhiliyyeti yebgûne. vemen aḥsenü mine-llâhi ḥukmel liḳavmiy yûḳinûn.'),
(720, 5, 51, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕü-lyehûde venneṣârâ evliyâ\'. ba`ḍuhüm evliyâü ba`ḍ. vemey yetevellehüm minküm feinnehû minhüm. inne-llâhe lâ yehdi-lḳavme-żżâlimîn.'),
(721, 5, 52, 'fetere-lleẕîne fî ḳulûbihim meraḍuy yüsâri`ûne fîhim yeḳûlûne naḫşâ en tüṣîbenâ dâirah. fe`ase-llâhü ey ye\'tiye bilfetḥi ev emrim min `indihî feyuṣbiḥû `alâ mâ eserrû fî enfüsihim nâdimîn.'),
(722, 5, 53, 'veyeḳûlü-lleẕîne âmenû ehâülâi-lleẕîne aḳsemû billâhi cehde eymânihim innehüm leme`aküm. ḥabiṭat a`mâlühüm feaṣbeḥû ḫâsirîn.'),
(723, 5, 54, 'yâ eyyühe-lleẕîne âmenû mey yertedde minküm `an dînihî fesevfe ye\'ti-llâhü biḳavmiy yüḥibbühüm veyüḥibbûnehû eẕilletin `ale-lmü\'minîne e`izzetin `ale-lkâfirîn. yücâhidûne fî sebîli-llâhi velâ yeḫâfûne levmete lâim. ẕâlike faḍlü-llâhi yü\'tîhi mey yeşâ\'. vellâhü vâsi`un `alîm.'),
(724, 5, 55, 'innemâ veliyyükümü-llâhü verasûlühû velleẕîne âmenü-lleẕîne yüḳîmûne-ṣṣalâte veyü\'tûne-zzekâte vehüm râki`ûn.'),
(725, 5, 56, 'vemey yetevelle-llâhe verasûlehû velleẕîne âmenû feinne ḥizbe-llâhi hümü-lgâlibûn.'),
(726, 5, 57, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕü-lleẕîne-tteḫaẕû dîneküm hüzüvev vele`ibem mine-lleẕîne ûtü-lkitâbe min ḳabliküm velküffâra evliyâ\'. vetteḳu-llâhe in küntüm mü\'minîn.'),
(727, 5, 58, 'veiẕâ nâdeytüm ile-ṣṣalâti-tteḫaẕûhâ hüzüvev vele`ibâ. ẕâlike biennehüm ḳavmül lâ ya`ḳilûn.'),
(728, 5, 59, 'ḳul yâ ehle-lkitâbi hel tenḳimûne minnâ illâ en âmennâ billâhi vemâ ünzile ileynâ vemâ ünzile min ḳablü veenne ekŝeraküm fâsiḳûn.'),
(729, 5, 60, 'ḳul hel ünebbiüküm bişerrim min ẕâlike meŝûbeten `inde-llâh. mel le`anehü-llâhü vegaḍibe `aleyhi vece`ale minhümü-lḳiradete velḫanâzîra ve`abede-ṭṭâgût. ülâike şerrum mekânev veeḍallü `an sevâi-ssebîl.'),
(730, 5, 61, 'veiẕâ câûküm ḳâlû âmennâ veḳad deḫalû bilküfri vehüm ḳad ḫaracû bih. vellâhü a`lemü bimâ kânû yektümûn.'),
(731, 5, 62, 'veterâ keŝîram minhüm yüsâri`ûne fi-l\'iŝmi vel`udvâni veeklihimü-ssuḥt. lebi\'se mâ kânû ya`melûn.'),
(732, 5, 63, 'levlâ yenhâhümü-rrabbâniyyûne vel\'aḥbâru `an ḳavlihimü-l\'iŝme veeklihimü-ssuḥt. lebi\'se mâ kânû yaṣne`ûn.'),
(733, 5, 64, 'veḳâleti-lyehûdü yedü-llâhi maglûleh. gullet eydîhim velü`inû bimâ ḳâlû. bel yedâhü mebsûṭatâni yünfiḳu keyfe yeşâ\'. veleyezîdenne keŝîram minhüm mâ ünzile ileyke mir rabbike ṭugyânev veküfrâ. veelḳaynâ beynehümü-l`adâvete velbagḍâe ilâ yevmi-lḳiyâmeh. küllemâ evḳadû nâral lilḥarbi aṭfeehe-llâhü veyes`avne fi-l\'arḍi fesâdâ. vellâhü lâ yüḥibbü-lmüfsidîn.'),
(734, 5, 65, 'velev enne ehle-lkitâbi âmenû vetteḳav lekeffernâ `anhüm seyyiâtihim veleedḫalnâhüm cennâti-nne`îm.'),
(735, 5, 66, 'velev ennehüm eḳâmü-ttevrâte vel\'incîle vemâ ünzile ileyhim mir rabbihim leekelû min fevḳihim vemin taḥti ercülihim. minhüm ümmetüm muḳteṣideh. vekeŝîrum minhüm sâe mâ ya`melûn.'),
(736, 5, 67, 'yâ eyyühe-rrasûlü bellig mâ ünzile ileyke mir rabbik. veil lem tef`al femâ bellagte risâleteh. vellâhü ya`ṣimüke mine-nnâs. inne-llâhe lâ yehdi-lḳavme-lkâfirîn.'),
(737, 5, 68, 'ḳul yâ ehle-lkitâbi lestüm `alâ şey\'in ḥattâ tüḳîmü-ttevrâte vel\'incîle vemâ ünzile ileyküm mir rabbiküm. veleyezîdenne keŝîram minhüm mâ ünzile ileyke mir rabbike ṭugyânev veküfrâ. felâ te\'se `ale-lḳavmi-lkâfirîn.'),
(738, 5, 69, 'inne-lleẕîne âmenû velleẕîne hâdû veṣṣâbiûne venneṣârâ men âmene billâhi velyevmi-l\'âḫiri ve`amile ṣâliḥan felâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(739, 5, 70, 'leḳad eḫaẕnâ mîŝâḳa benî isrâîle veerselnâ ileyhim rusülâ. küllemâ câehüm rasûlüm bimâ lâ tehvâ enfüsühüm ferîḳan keẕẕebû veferîḳay yaḳtülûn.'),
(740, 5, 71, 'veḥasibû ellâ tekûne fitnetün fe`amû veṣammû ŝümme tâbe-llâhü `aleyhim ŝümme `amû veṣammû keŝîrum minhüm. vellâhü beṣîrum bimâ ya`melûn.'),
(741, 5, 72, 'leḳad kefera-lleẕîne ḳâlû inne-llâhe hüve-lmesîḥu-bnü meryem. veḳâle-lmesîḥu yâ benî isrâîle-`büdü-llâhe rabbî verabbeküm. innehû mey yüşrik billâhi feḳad ḥarrame-llâhü `aleyhi-lcennete veme\'vâhü-nnâr. vemâ liżżâlimîne min enṣâr.'),
(742, 5, 73, 'leḳad kefera-lleẕîne ḳâlû inne-llâhe ŝâliŝü ŝelâŝeh. vemâ min ilâhin illâ ilâhüv vâḥid. veil lem yentehû `ammâ yeḳûlûne leyemessenne-lleẕîne keferû minhüm `aẕâbün elîm.'),
(743, 5, 74, 'efelâ yetûbûne ile-llâhi veyestagfirûneh. vellâhü gafûrur raḥîm.'),
(744, 5, 75, 'me-lmesîḥu-bnü meryeme illâ rasûl. ḳad ḫalet min ḳablihi-rrusül. veümmühû ṣiddîḳah. kânâ ye\'külâni-ṭṭa`âm. ünżur keyfe nübeyyinü lehümü-l\'âyâti ŝümme-nżur ennâ yü\'fekûn.'),
(745, 5, 76, 'ḳul eta`büdûne min dûni-llâhi mâ lâ yemlikü leküm ḍarrav velâ nef`â. vellâhü hüve-ssemî`u-l`alîm.'),
(746, 5, 77, 'ḳul yâ ehle-lkitâbi lâ taglû fî dîniküm gayra-lḥaḳḳi velâ tettebi`û ehvâe ḳavmin ḳad ḍallû min ḳablü veeḍallû keŝîrav veḍallû `an sevâi-ssebîl.'),
(747, 5, 78, 'lü`ine-lleẕîne keferû mim benî isrâîle `alâ lisâni dâvûde ve`îse-bni meryem. ẕâlike bimâ `aṣav vekânû ya`tedûn.'),
(748, 5, 79, 'kânû lâ yetenâhevne `am münkerin fe`alûhü. lebi\'se mâ kânû yef`alûn.'),
(749, 5, 80, 'terâ keŝîram minhüm yetevellevne-lleẕîne keferû. lebi\'se mâ ḳaddemet lehüm enfüsühüm en seḫiṭa-llâhü `aleyhim vefi-l`aẕâbi hüm ḫâlidûn.'),
(750, 5, 81, 'velev kânû yü\'minûne billâhi vennebiyyi vemâ ünzile ileyhi me-tteḫaẕûhüm evliyâe velâkinne keŝîram minhüm fâsiḳûn.'),
(751, 5, 82, 'letecidenne eşedde-nnâsi `adâvetel lilleẕîne âmenü-lyehûde velleẕîne eşrakû. veletecidenne aḳrabehüm meveddetel lilleẕîne âmenü-lleẕîne ḳâlû innâ neṣârâ. ẕâlike bienne minhüm ḳissîsîne veruhbânev veennehüm lâ yestekbirûn.'),
(752, 5, 83, 'veiẕâ semi`û mâ ünzile ile-rrasûli terâ a`yünehüm tefîḍu mine-ddem`i mimmâ `arafû mine-lḥaḳḳ. yeḳûlûne rabbenâ âmennâ fektübnâ me`a-şşâhidîn.'),
(753, 5, 84, 'vemâ lenâ lâ nü\'minü billâhi vemâ câenâ mine-lḥaḳḳi venaṭme`u ey yüdḫilenâ rabbünâ me`a-lḳavmi-ṣṣâliḥîn.'),
(754, 5, 85, 'feeŝâbehümü-llâhü bimâ ḳâlû cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. veẕâlike cezâü-lmuḥsinîn.'),
(755, 5, 86, 'velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-lceḥîm.'),
(756, 5, 87, 'yâ eyyühe-lleẕîne âmenû lâ tüḥarrimû ṭayyibâti mâ eḥalle-llâhü leküm velâ ta`tedû. inne-llâhe lâ yüḥibbü-lmü`tedîn.'),
(757, 5, 88, 'vekülû mimmâ razeḳakümü-llâhü ḥalâlen ṭayyibâ. vetteḳu-llâhe-lleẕî entüm bihî mü\'minûn.'),
(758, 5, 89, 'lâ yüâḫiẕükümü-llâhü billagvi fî eymâniküm velâkiy yüâḫiẕüküm bimâ `aḳḳattümü-l\'eymân. fekeffâratühû iṭ`âmü `aşerati mesâkîne min evseṭi mâ tuṭ`imûne ehlîküm ev kisvetühüm ev taḥrîru raḳabeh. femel lem yecid feṣiyâmü ŝelâŝeti eyyâm. ẕâlike keffâratü eymâniküm iẕâ ḥaleftüm. vaḥfeżû eymâneküm. keẕâlike yübeyyinü-llâhü leküm âyâtihî le`alleküm teşkürûn.'),
(759, 5, 90, 'yâ eyyühe-lleẕîne âmenû inneme-lḫamru velmeysiru vel\'enṣâbü vel\'ezlâmü ricsüm min `ameli-şşeyṭâni fectenibûhü le`alleküm tüfliḥûn.'),
(760, 5, 91, 'innemâ yürîdü-şşeyṭânü ey yûḳi`a beynekümü-l`adâvete velbagḍâe fi-lḫamri velmeysiri veyeṣuddeküm `an ẕikri-llâhi ve`ani-ṣṣalâh. fehel entüm müntehûn.'),
(761, 5, 92, 'veeṭî`ü-llâhe veeṭî`ü-rrasûle vaḥẕerû. fein tevelleytüm fa`lemû ennemâ `alâ rasûline-lbelâgu-lmübîn.'),
(762, 5, 93, 'leyse `ale-lleẕîne âmenû ve`amilu-ṣṣâliḥâti cünâḥun fîmâ ṭa`imû iẕâ me-tteḳav veâmenû ve`amilu-ṣṣâliḥâti ŝümme-tteḳav veâmenû ŝümme-tteḳav veaḥsenû. vellâhü yüḥibbü-lmuḥsinîn.'),
(763, 5, 94, 'yâ eyyühe-lleẕîne âmenû leyeblüvennekümü-llâhü bişey\'im mine-ṣṣaydi tenâlühû eydîküm verimâḥuküm liya`leme-llâhü mey yeḫâfühû bilgayb. femeni-`tedâ ba`de ẕâlike felehû `aẕâbün elîm.'),
(764, 5, 95, 'yâ eyyühe-lleẕîne âmenû lâ taḳtülu-ṣṣayde veentüm ḥurum. vemen ḳatelehû minküm müte`ammiden fecezâüm miŝlü mâ ḳatele mine-nne`ami yaḥkümü bihî ẕevâ `adlim minküm hedyem bâliga-lka`beti ev keffâratün ṭa`âmü mesâkîne ev `adlü ẕâlike ṣiyâmel liyeẕûḳa vebâle emrih. `afe-llâhü `ammâ selef. vemen `âde feyenteḳimü-llâhü minh. vellâhü `azîzün ẕü-ntiḳâm.'),
(765, 5, 96, 'üḥille leküm ṣaydü-lbaḥri veṭa`âmühû metâ`al leküm velissâyyârah. veḥurrime `aleyküm ṣaydü-lberri mâ dümtüm ḥurumâ. vetteḳu-llâhe-lleẕî ileyhi tuḥşerûn.'),
(766, 5, 97, 'ce`ale-llâhü-lka`bete-lbeyte-lḥarâme ḳiyâmel linnâsi veşşehra-lḥarâme velhedye velḳalâid. ẕâlike lita`lemû enne-llâhe ya`lemü mâ fi-ssemâvâti vemâ fi-l\'arḍi veenne-llâhe bikülli şey\'in `alîm.'),
(767, 5, 98, 'i`lemû enne-llâhe şedîdü-l`iḳâbi veenne-llâhe gafûrur raḥîm.'),
(768, 5, 99, 'mâ `ale-rrasûli ille-lbelâg. vellâhü ya`lemü mâ tübdûne vemâ tektümûn.'),
(769, 5, 100, 'ḳul lâ yestevi-lḫabîŝü veṭṭayyibü velev a`cebeke keŝratü-lḫabîŝ. fetteḳu-llâhe yâ ûli-l\'elbâbi le`alleküm tüfliḥûn.'),
(770, 5, 101, 'yâ eyyühe-lleẕîne âmenû lâ tes\'elû `an eşyâe in tübde leküm tesü\'küm. vein tes\'elû `anhâ ḥîne yünezzelü-lḳur\'ânü tübde leküm. `afe-llâhü `anhâ. vellâhü gafûrun ḥalîm.'),
(771, 5, 102, 'ḳad seelehâ ḳavmüm min ḳabliküm ŝümme aṣbeḥû bihâ kâfirîn.'),
(772, 5, 103, 'mâ ce`ale-llâhü mim beḥîrativ velâ sâibetiv velâ veṣîletiv velâ ḥâmiv velâkinne-lleẕîne keferû yefterûne `ale-llâhi-lkeẕib. veekŝeruhüm lâ ya`ḳilûn.'),
(773, 5, 104, 'veiẕâ ḳîle lehüm te`âlev ilâ mâ enzele-llâhü veile-rrasûli ḳâlû ḥasbünâ mâ vecednâ `aleyhi âbâenâ. evelev kâne âbâühüm lâ ya`lemûne şey\'ev velâ yehtedûn.'),
(774, 5, 105, 'yâ eyyühe-lleẕîne âmenû `aleyküm enfüseküm. lâ yeḍurruküm men ḍalle iẕe-htedeytüm. ile-llâhi merci`uküm cemî`an feyünebbiüküm bimâ küntüm ta`melûn.'),
(775, 5, 106, 'yâ eyyühe-lleẕîne âmenû şehâdetü beyniküm iẕâ ḥaḍara eḥadekümü-lmevtü ḥîne-lveṣiyyeti-ŝnâni ẕevâ `adlim minküm ev âḫarâni min gayriküm in entüm ḍarabtüm fi-l\'arḍi feeṣâbetküm müṣîbtü-lmevt. taḥbisûnehümâ mim ba`di-ṣṣalâti feyuḳsimâni billâhi ini-rtebtüm lâ neşterî bihî ŝemenev velev kâne ẕâ ḳurbâ velâ nektümü şehâdete-llâhi innâ iẕel lemine-l\'âŝimîn.'),
(776, 5, 107, 'fein `uŝira `alâ ennehüme-steḥaḳḳâ iŝmen feâḫarâni yeḳûmâni meḳâmehümâ mine-lleẕîne-steḥaḳḳa `aleyhimü-l\'evleyâni feyuḳsimâni billâhi leşehâdetünâ eḥaḳḳu min şehâdetihimâ veme-`tedeynâ. innâ iẕel lemine-żżâlimîn.'),
(777, 5, 108, 'ẕâlike ednâ ey ye\'tû bişşehâdeti `alâ vechihâ ev yeḫâfû en türadde eymânüm ba`de eymânihim. vetteḳu-llâhe vesme`û. vellâhü lâ yehdi-lḳavme-lfâsiḳîn.'),
(778, 5, 109, 'yevme yecme`u-llâhü-rrusüle feyeḳûlü mâẕâ ücibtüm. ḳâlû lâ `ilme lenâ. inneke ente `allâmü-lguyûb.'),
(779, 5, 110, 'iẕ ḳâle-llâhü yâ `îse-bne meryeme-ẕkür ni`metî `aleyke ve`alâ vâlidetik. iẕ eyyettüke birûḥi-lḳudüsi tükellimü-nnâse fi-lmehdi vekehlâ. veiẕ `allemtüke-lkitâbe velḥikmete vettevrâte vel\'incîl. veiẕ taḫlüḳu mine-ṭṭîni kehey\'eti-ṭṭayri biiẕnî fetenfüḫu fîhâ fetekûnü ṭayram biiẕnî vetübriü-l\'ekmehe vel\'ebraṣa biiẕnî. veiẕ tuḫricü-lmevtâ biiẕnî. veiẕ kefeftü benî isrâîle `anke iẕ ci\'tehüm bilbeyyinâti feḳâle-lleẕîne keferû minhüm in hâẕâ illâ siḥrum mübîn.'),
(780, 5, 111, 'veiẕ evḥaytü ile-lḥavâriyyîne en âminû bî vebirasûlî. ḳâlû âmennâ veşhed biennenâ müslimûn.'),
(781, 5, 112, 'iẕ ḳâle-lḥavâriyyûne yâ `îse-bne meryeme hel yesteṭî`u rabbüke ey yünezzile `aleynâ mâidetem mine-ssemâ\'. ḳâle-tteḳu-llâhe in küntüm mü\'minîn.'),
(782, 5, 113, 'ḳâlû nürîdü en ne\'küle minhâ vetaṭmeinne ḳulûbünâ vena`leme en ḳad ṣadaḳtenâ venekûne `aleyhâ mine-şşâhidîn.'),
(783, 5, 114, 'ḳâle `îse-bnü meryeme-llâhümme rabbenâ enzil `aleynâ mâidetem mine-ssemâi tekûnü lenâ `îdel lievvelinâ veâḫirinâ veâyetem mink. verzuḳnâ veente ḫayru-rrâziḳîn.'),
(784, 5, 115, 'ḳâle-llâhü innî münezzilühâ `aleyküm. femey yekfür ba`dü minküm feinnî ü`aẕẕibühû `aẕâbel lâ ü`aẕẕibühû eḥadem mine-l`âlemîn.'),
(785, 5, 116, 'veiẕ ḳâle-llâhü yâ `îse-bne meryeme eente ḳulte linnâsi-tteḫiẕûnî veümmiye ilâheyni min dûni-llâh. ḳâle sübḥâneke mâ yekûnü lî en eḳûle mâ leyse lî biḥaḳḳ. in küntü ḳultühû feḳad `alimteh. ta`lemü mâ fî nefsî velâ a`lemü mâ fî nefsik. inneke ente `allâmü-lguyûb.'),
(786, 5, 117, 'mâ ḳultü lehüm illâ mâ emertenî bihî eni-`büdü-llâhe rabbî verabbeküm. veküntü `aleyhim şehîdem mâ dümtü fîhim. felemmâ teveffeytenî künte ente-rraḳîbe `aleyhim. veente `alâ külli şey\'in şehîd.'),
(787, 5, 118, 'in tü`aẕẕibhüm feinnehüm `ibâdük. vein tagfir lehüm feinneke ente-l`azîzü-lḥakîm.'),
(788, 5, 119, 'ḳâle-llâhü hâẕâ yevmü yenfe`u-ṣṣâdiḳîne ṣidḳuhüm. lehüm cennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. raḍiye-llâhü `anhüm veraḍû `anh. ẕâlike-lfevzü-l`ażîm.'),
(789, 5, 120, 'lillâhi mülkü-ssemâvâti vel\'arḍi vemâ fîhinn. vehüve `alâ külli şey\'in ḳadîr.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'elḥamdü lillâhi-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa vece`ale-żżulümâti vennûr. ŝümme-lleẕîne keferû birabbihim ya`dilûn.'),
(791, 6, 2, 'hüve-lleẕî ḫaleḳaküm min ṭînin ŝümme ḳaḍâ ecelâ. veecelüm müsemmen `indehû ŝümme entüm temterûn.'),
(792, 6, 3, 'vehüve-llâhü fi-ssemâvâti vefi-l\'arḍ. ya`lemü sirraküm vecehraküm veya`lemü mâ teksibûn.'),
(793, 6, 4, 'vemâ te\'tîhim min âyetim min âyâti rabbihim illâ kânû `anhâ mü`riḍîn.'),
(794, 6, 5, 'feḳad keẕẕebû bilḥaḳḳi lemmâ câehüm. fesevfe ye\'tîhim embâü mâ kânû bihî yestehziûn.'),
(795, 6, 6, 'elem yerav kem ehleknâ min ḳablihim min ḳarnim mekkennâhüm fi-l\'arḍi mâ lem nümekkil leküm veerselne-ssemâe `aleyhim midrârâ. vece`alne-l\'enhâra tecrî min taḥtihim feehleknâhüm biẕünûbihim veenşe\'nâ mim ba`dihim ḳarnen âḫarîn.'),
(796, 6, 7, 'velev nezzelnâ `aleyke kitâben fî ḳirṭâsin felemesûhü bieydîhim leḳâle-lleẕîne keferû in hâẕâ illâ siḥrum mübîn.'),
(797, 6, 8, 'veḳâlû levlâ ünzile `aleyhi melek. velev enzelnâ melekel leḳuḍiye-l\'emru ŝümme lâ yünżarûn.'),
(798, 6, 9, 'velev ce`alnâhü melekel lece`alnâhü racülev velelebesnâ `aleyhim mâ yelbisûn.'),
(799, 6, 10, 'veleḳadi-stühzie birusülim min ḳablike feḥâḳa billeẕîne seḫirû minhüm mâ kânû bihî yestehziûn.'),
(800, 6, 11, 'ḳul sîrû fi-l\'arḍi ŝümme-nżurû keyfe kâne `âḳibetü-lmükeẕẕibîn.'),
(801, 6, 12, 'ḳul limem mâ fi-ssemâvâti vel\'arḍ. ḳul lillâh. ketebe `alâ nefsihi-rraḥmeh. leyecme`anneküm ilâ yevmi-lḳiyâmeti lâ raybe fîh. elleẕîne ḫasirû enfüsehüm fehüm lâ yü\'minûn.'),
(802, 6, 13, 'velehû mâ sekene fi-lleyli vennehâr. vehüve-ssemî`u-l`alîm.'),
(803, 6, 14, 'ḳul egayra-llâhi etteḫiẕü veliyyen fâṭiri-ssemâvâti vel\'arḍi vehüve yuṭ`imü velâ yuṭ`am. ḳul innî ümirtü en ekûne evvele men esleme velâ tekûnenne mine-lmüşrikîn.'),
(804, 6, 15, 'ḳul innî eḫâfü in `aṣaytü rabbî `aẕâbe yevmin `ażîm.'),
(805, 6, 16, 'mey yuṣraf `anhü yevmeiẕin feḳad raḥimeh. veẕâlike-lfevzü-lmübîn.'),
(806, 6, 17, 'veiy yemseske-llâhü biḍurrin felâ kâşife lehû illâ hû. veiy yemseske biḫayrin fehüve `alâ külli şey\'in ḳadîr.'),
(807, 6, 18, 'vehüve-lḳâhiru fevḳa `ibâdih. vehüve-lḥakîmü-lḫabîr.'),
(808, 6, 19, 'ḳul eyyü şey\'in ekberu şehâdeten. ḳuli-llâhü şehîdüm beynî vebeyneküm veûḥiye ileyye hâẕe-lḳur\'ânü liünẕiraküm bihî vemem belag. einneküm leteşhedûne enne me`a-llâhi âliheten uḫrâ. ḳul lâ eşhed. ḳul innemâ hüve ilâhüv vâḥidüv veinnenî berîüm mimmâ tüşrikûn.'),
(809, 6, 20, 'elleẕîne âteynâhümü-lkitâbe ya`rifûnehû kemâ ya`rifûne ebnâehüm. elleẕîne ḫasirû enfüsehüm fehüm lâ yü\'minûn.'),
(810, 6, 21, 'vemen ażlemü mimmeni-fterâ `ale-llâhi keẕiben ev keẕẕebe biâyâtih. innehû lâ yüfliḥu-żżâlimûn.'),
(811, 6, 22, 'veyevme naḥşüruhüm cemî`an ŝümme neḳûlü lilleẕîne eşrakû eyne şürakâükümü-lleẕîne küntüm tez`umûn.'),
(812, 6, 23, 'ŝümme lem tekün fitnetühüm illâ en ḳâlû vellâhi rabbinâ mâ künnâ müşrikîn.'),
(813, 6, 24, 'ünżur keyfe keẕebû `alâ enfüsihim veḍalle `anhüm mâ kânû yefterûn.'),
(814, 6, 25, 'veminhüm mey yestemi`u ileyk. vece`alnâ `alâ ḳulûbihim ekinneten ey yefḳahûhü vefî âẕânihim vaḳrâ. veiy yerav külle âyetil lâ yü\'minû bihâ. ḥattâ iẕâ câûke yücâdilûneke yeḳûlü-lleẕîne keferû in hâẕâ illâ esâṭîru-l\'evvelîn.'),
(815, 6, 26, 'vehüm yenhevne `anhü veyen\'evne `anh. veiy yühlikûne illâ enfüsehüm vemâ yeş`urûn.'),
(816, 6, 27, 'velev terâ iẕ vuḳifû `ale-nnâri feḳâlû yâ leytenâ nüraddü velâ nükeẕẕibe biâyâti rabbinâ venekûne mine-lmü\'minîn.'),
(817, 6, 28, 'bel bedâ lehüm mâ kânû yuḫfûne min ḳabl. velev ruddû le`âdû limâ nühû `anhü veinnehüm lekâẕibûn.'),
(818, 6, 29, 'veḳâlû in hiye illâ ḥayâtüne-ddünyâ vemâ naḥnü bimeb`ûŝîn.'),
(819, 6, 30, 'velev terâ iẕ vuḳifû `alâ rabbihim. ḳâle eleyse hâẕâ bilḥaḳḳ. ḳâlû belâ verabbinâ. ḳâle feẕûḳu-l`aẕâbe bimâ küntüm tekfürûn.'),
(820, 6, 31, 'ḳad ḫasira-lleẕîne keẕẕebû biliḳâi-llâh. ḥattâ iẕâ câethümü-ssâ`atü bagteten ḳâlû yâ ḥasratenâ `alâ mâ ferraṭnâ fîhâ vehüm yaḥmilûne evzârahüm `alâ żuhûrihim. elâ sâe mâ yezirûn.'),
(821, 6, 32, 'veme-lḥayâtü-ddünyâ illâ le`ibüv velehv. veleddâru-l\'âḫiratü ḫayrul lilleẕîne yetteḳûn. efelâ ta`ḳilûn.'),
(822, 6, 33, 'ḳad na`lemü innehû leyaḥzünüke-lleẕî yeḳûlûne feinnehüm lâ yükeẕẕibûneke velâkinne-żżâlimîne biâyâti-llâhi yecḥadûn.'),
(823, 6, 34, 'veleḳad küẕẕibet rusülüm min ḳablike feṣaberû `alâ mâ küẕẕibû veûẕû ḥattâ etâhüm naṣrunâ. velâ mübeddile likelimâti-llâh. veleḳad câeke min nebei-lmürselîn.'),
(824, 6, 35, 'vein kâne kebüra `aleyke i`râḍuhüm feini-steṭa`te en tebtegiye nefeḳan fi-l\'arḍi ev süllemen fi-ssemâi fete\'tiyehüm biâyeh. velev şâe-llâhü leceme`ahüm `ale-lhüdâ felâ tekûnenne mine-lcâhilîn.'),
(825, 6, 36, 'innemâ yestecîbü-lleẕîne yesme`ûn. velmevtâ yeb`aŝühümü-llâhü ŝümme ileyhi yürce`ûn.'),
(826, 6, 37, 'veḳâlû levlâ nüzzile `aleyhi âyetüm mir rabbih. ḳul inne-llâhe ḳâdirun `alâ ey yünezzile âyetev velâkinne ekŝerahüm lâ ya`lemûn.'),
(827, 6, 38, 'vemâ min dâbbetin fi-l\'arḍi velâ ṭâiriy yeṭîru bicenâḥayhi illâ ümemün emŝâlüküm. mâ ferraṭnâ fi-lkitâbi min şey\'in ŝümme ilâ rabbihim yuḥşerûn.'),
(828, 6, 39, 'velleẕîne keẕẕebû biâyâtinâ ṣummüv vebükmün fi-żżulümât. mey yeşei-llâhü yuḍlilh. vemey yeşe\' yec`alhü `alâ ṣirâṭim müsteḳîm.'),
(829, 6, 40, 'ḳul era\'eyteküm in etâküm `aẕâbü-llâhi ev etetkümü-ssâ`atü egayra-llâhi ted`ûn. in küntüm ṣâdiḳîn.'),
(830, 6, 41, 'bel iyyâhü ted`ûne feyekşifü mâ ted`ûne ileyhi in şâe vetensevne mâ tüşrikûn.'),
(831, 6, 42, 'veleḳad erselnâ ilâ ümemim min ḳablike feeḫaẕnâhüm bilbe\'sâi veḍḍarrâi le`allehüm yeteḍarra`ûn.'),
(832, 6, 43, 'felevlâ iẕ câehüm be\'sünâ teḍarra`û velâkin ḳaset ḳulûbühüm vezeyyene lehümü-şşeyṭânü mâ kânû ya`melûn.'),
(833, 6, 44, 'felemmâ nesû mâ ẕükkirû bihî fetaḥnâ `aleyhim ebvâbe külli şey\'. ḥattâ iẕâ feriḥû bimâ ûtû eḫaẕnâhüm bagteten feiẕâ hüm müblisûn.'),
(834, 6, 45, 'feḳuṭi`a dâbiru-lḳavmi-lleẕîne żalemû. velḥamdü lillâhi rabbi-l`âlemîn.'),
(835, 6, 46, 'ḳul era\'eytüm in eḫaẕe-llâhü sem`aküm veebṣâraküm veḫateme `alâ ḳulûbiküm men ilâhün gayru-llâhi ye\'tîküm bih. ünżur keyfe nüṣarrifü-l\'âyâti ŝümme hüm yaṣdifûn.'),
(836, 6, 47, 'ḳul era\'eyteküm in etâküm `aẕâbü-llâhi bagteten ev cehraten hel yühlekü ille-lḳavmu-żżâlimûn.'),
(837, 6, 48, 'vemâ nürsilü-lmürselîne illâ mübeşşirîne vemünẕirîn. femen âmene veaṣleḥa felâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(838, 6, 49, 'velleẕîne keẕẕebû biâyâtinâ yemessühümü-l`aẕâbü bimâ kânû yefsüḳûn.'),
(839, 6, 50, 'ḳul lâ eḳûlü leküm `indî ḫazâinü-llâhi velâ a`lemü-lgaybe velâ eḳûlü leküm innî melek. in ettebi`u illâ mâ yûḥâ ileyy. ḳul hel yestevi-l\'a`mâ velbeṣîr. efelâ tetefekkerûn.'),
(840, 6, 51, 'veenẕir bihi-lleẕîne yeḫâfûne ey yuḥşerû ilâ rabbihim leyse lehüm min dûnihî veliyyüv velâ şefî`ul le`allehüm yetteḳûn.'),
(841, 6, 52, 'velâ taṭrudi-lleẕîne yed`ûne rabbehüm bilgadâti vel`aşiyyi yürîdûne vecheh. mâ `aleyke min ḥisâbihim min şey\'iv vemâ min ḥisâbike `aleyhim min şey\'in fetaṭrudehüm fetekûne mine-żżâlimîn.'),
(842, 6, 53, 'vekeẕâlike fetennâ ba`ḍahüm biba`ḍil liyeḳûlû ehâülâi menne-llâhü `aleyhim mim beyninâ. eleyse-llâhü bia`leme bişşâkirîn.'),
(843, 6, 54, 'veiẕâ câeke-lleẕîne yü\'minûne biâyâtinâ feḳul selâmün `aleyküm ketebe rabbüküm `alâ nefsihi-rraḥmete ennehû men `amile minküm sûem bicehâletin ŝümme tâbe mim ba`dihî veaṣleḥa feennehû gafûrur raḥîm.'),
(844, 6, 55, 'vekeẕâlike nüfeṣṣilü-l\'âyâti velitestebîne sebîlü-lmücrimîn.'),
(845, 6, 56, 'ḳul innî nühîtü en a`büde-lleẕîne ted`ûne min dûni-llâh. ḳul lâ ettebi`u ehvâeküm ḳad ḍaleltü iẕev vemâ ene mine-lmühtedîn.'),
(846, 6, 57, 'ḳul innî `alâ beyyinetim mir rabbî vekeẕẕebtüm bih. mâ `indî mâ testa`cilûne bih. ini-lḥukmü illâ lillâh. yeḳuṣṣu-lḥaḳḳa vehüve ḫayru-lfâṣilîn.'),
(847, 6, 58, 'ḳul lev enne `indî mâ testa`cilûne bihî leḳuḍiye-l\'emru beynî vebeyneküm. vellâhü a`lemü biżżâlimîn.'),
(848, 6, 59, 've`indehû mefâtiḥu-lgaybi lâ ya`lemühâ illâ hû. veya`lemü mâ fi-lberri velbaḥr. vemâ tesḳuṭu miv veraḳatin illâ ya`lemühâ velâ ḥabbetin fî żulümâti-l\'arḍi velâ raṭbiv velâ yâbisin illâ fî kitâbim mübîn.'),
(849, 6, 60, 'vehüve-lleẕî yeteveffâküm billeyli veya`lemü mâ ceraḥtüm binnehâri ŝümme yeb`aŝüküm fîhi liyuḳḍâ ecelüm müsemmâ. ŝümme ileyhi merci`uküm ŝümme yünebbiüküm bimâ küntüm ta`melûn.'),
(850, 6, 61, 'vehüve-lḳâhiru fevḳa `ibâdihî veyürsilü `aleyküm ḥafeżah. ḥattâ iẕâ câe eḥadekümü-lmevtü teveffethü rusülünâ vehüm lâ yüferriṭûn.'),
(851, 6, 62, 'ŝümme ruddû ile-llâhi mevlâhümü-lḥaḳḳ. elâ lehü-lḥukmü vehüve esra`u-lḥâsibîn.'),
(852, 6, 63, 'ḳul mey yüneccîküm min żulümâti-lberri velbaḥri ted`ûnehû teḍarru`av veḫufyeh. lein encânâ min hâẕihî lenekûnenne mine-şşâkirîn.'),
(853, 6, 64, 'ḳuli-llâhü yüneccîküm minhâ vemin külli kerbin ŝümme entüm tüşrikûn.'),
(854, 6, 65, 'ḳul hüve-lḳâdiru `alâ ey yeb`aŝe `aleyküm `aẕâbem min fevḳiküm ev min taḥti ercüliküm ev yelbiseküm şiye`av veyüẕîḳa ba`ḍaküm be\'se ba`ḍ. ünżur keyfe nüṣarrifü-l\'âyâti le`allehüm yefḳahûn.'),
(855, 6, 66, 'vekeẕẕebe bihî ḳavmüke vehüve-lḥaḳḳ. ḳul lestü `aleyküm bivekîl.'),
(856, 6, 67, 'likülli nebeim müsteḳarr. vesevfe ta`lemûn.'),
(857, 6, 68, 'veiẕâ raeyte-lleẕîne yeḫûḍûne fî âyâtinâ fea`riḍ `anhüm ḥattâ yeḫûḍû fî ḥadîŝin gayrih. veimmâ yünsiyenneke-şşeyṭânü felâ taḳ`ud ba`de-ẕẕikrâ me`a-lḳavmi-żżâlimîn.'),
(858, 6, 69, 'vemâ `ale-lleẕîne yetteḳûne min ḥisâbihim min şey\'iv velâkin ẕikrâ le`allehüm yetteḳûn.'),
(859, 6, 70, 'veẕeri-lleẕîne-tteḫaẕû dînehüm le`ibev velehvev vegarrathümü-lḥayâtü-ddünyâ veẕekkir bihî en tübsele nefsüm bimâ kesebet. leyse lehâ min dûni-llâhi veliyyüv velâ şefî`. vein ta`dil külle `adlil lâ yü\'ḫaẕ minhâ. ülâike-lleẕîne übsilû bimâ kesebû. lehüm şerâbüm min ḥamîmiv ve`aẕâbün elîmüm bimâ kânû yekfürûn.'),
(860, 6, 71, 'ḳul ened`û min dûni-llâhi mâ lâ yenfe`unâ velâ yeḍurrunâ venüraddü `alâ a`ḳâbinâ ba`de iẕ hedâne-llâhü kelleẕi-stehvethü-şşeyâṭînü fi-l\'arḍi ḥayrân. lehû aṣḥâbüy yed`ûnehû ile-lhüde-\'tinâ. ḳul inne hüde-llâhi hüve-lhüdâ. veümirnâ linüslime lirabbi-l`âlemîn.'),
(861, 6, 72, 'veen eḳîmu-ṣṣalâte vetteḳûh. vehüve-lleẕî ileyhi tuḥşerûn.'),
(862, 6, 73, 'vehüve-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa bilḥaḳḳ. veyevme yeḳûlü kün feyekûn. ḳavlühü-lḥaḳḳ. velehü-lmülkü yevme yünfeḫu fi-ṣṣûr. `âlimü-lgaybi veşşehâdeh. vehüve-lḥakîmü-lḫabîr.'),
(863, 6, 74, 'veiẕ ḳâle ibrâhîmü liebîhi âzera etetteḫiẕü aṣnâmen âliheten. innî erâke veḳavmeke fî ḍalâlim mübîn.'),
(864, 6, 75, 'vekeẕâlike nürî ibrâhîme melekûte-ssemâvâti vel\'arḍi veliyekûne mine-lmûḳinîn.'),
(865, 6, 76, 'felemmâ cenne `aleyhi-lleylü raâ kevkebâ. ḳâle hâẕâ rabbî. felemmâ efele ḳâle lâ üḥibbü-l\'âfilîn.'),
(866, 6, 77, 'felemmâ rae-lḳamera bâzigan ḳâle hâẕâ rabbî. felemmâ efele ḳâle leil lem yehdinî rabbî leekûnenne mine-lḳavmi-ḍḍâllîn.'),
(867, 6, 78, 'felemmâ rae-şşemse bâzigaten ḳâle hâẕâ rabbî hâẕâ ekber. felemmâ efelet ḳâle yâ ḳavmi innî berîüm mimmâ tüşrikûn.'),
(868, 6, 79, 'innî veccehtü vechiye lilleẕî feṭara-ssemâvâti vel\'arḍa ḥanîfev vemâ ene mine-lmüşrikîn.'),
(869, 6, 80, 'veḥâccehû ḳavmüh. ḳâle etüḥâccûnnî fi-llâhi veḳad hedân. velâ eḫâfü mâ tüşrikûne bihî illâ ey yeşâe rabbî şey\'â. vesi`a rabbî külle şey\'in `ilmâ. efelâ teteẕekkerûn.'),
(870, 6, 81, 'vekeyfe eḫâfü mâ eşraktüm velâ teḫâfûne enneküm eşraktüm billâhi mâ lem yünezzil bihî `aleyküm sülṭânâ. feeyyü-lferîḳayni eḥaḳḳu bil\'emn. in küntüm ta`lemûn.'),
(871, 6, 82, 'elleẕîne âmenû velem yelbisû îmânehüm biżulmin ülâike lehümü-l\'emnü vehüm mühtedûn.'),
(872, 6, 83, 'vetilke ḥuccetünâ âteynâhâ ibrâhîme `alâ ḳavmih. nerfe`u deracâtim men neşâ\'. inne rabbeke ḥakîmün `alîm.'),
(873, 6, 84, 'vevehebnâ lehû isḥâḳa veya`ḳûb. küllen hedeynâ. venûḥan hedeynâ min ḳablü vemin ẕürriyyetihî dâvûde vesüleymâne veeyyûbe veyûsüfe vemûsâ vehârûn. vekeẕâlike neczi-lmuḥsinîn.'),
(874, 6, 85, 'vezekeriyyâ veyaḥyâ ve`îsâ veilyâs. küllüm mine-ṣṣâliḥîn.'),
(875, 6, 86, 'veismâ`île velyese`a veyûnüse velûṭâ. veküllen feḍḍalnâ `ale-l`âlemîn.'),
(876, 6, 87, 'vemin âbâihim veẕürriyyâtihim veiḫvânihim. vectebeynâhüm vehedeynâhüm ilâ ṣirâṭim müsteḳîm.'),
(877, 6, 88, 'ẕâlike hüde-llâhi yehdî bihî mey yeşâü min `ibâdih. velev eşrakû leḥabiṭa `anhüm mâ kânû ya`melûn.'),
(878, 6, 89, 'ülâike-lleẕîne âteynâhümü-lkitâbe velḥukme vennübüvveh. feiy yekfür bihâ hâülâi feḳad vekkelnâ bihâ ḳavmel leysû bihâ bikâfirîn.'),
(879, 6, 90, 'ülâike-lleẕîne hede-llâhü febihüdâhümu-ḳtedih. ḳul lâ es\'elüküm `aleyhi ecrâ. in hüve illâ ẕikrâ lil`âlemîn.'),
(880, 6, 91, 'vemâ ḳaderü-llâhe ḥaḳḳa ḳadrihî iẕ ḳâlû mâ enzele-llâhü `alâ beşerim min şey\'. ḳul men enzele-lkitâbe-lleẕî câe bihî mûsâ nûrav vehüdel linnâsi tec`alûnehû ḳarâṭîse tübdûnehâ vetuḫfûne keŝîrâ. ve`ullimtüm mâ lem ta`lemû entüm velâ âbâüküm. ḳuli-llâhü ŝümme ẕerhüm fî ḫavḍihim yel`abûn.'),
(881, 6, 92, 'vehâẕâ kitâbün enzelnâhü mübâraküm müṣaddiḳu-lleẕî beyne yedeyhi velitünẕira ümme-lḳurâ vemen ḥavlehâ. velleẕîne yü\'minûne bil\'âḫirati yü\'minûne bihî vehüm `alâ ṣalâtihim yüḥâfiżûn.'),
(882, 6, 93, 'vemen ażlemü mimmeni-fterâ `ale-llâhi keẕiben ev ḳâle ûḥiye ileyye velem yûḥa ileyhi şey\'üv vemen ḳâle seünzilü miŝle mâ enzele-llâh. velev terâ iẕi-żżâlimûne fî gamerâti-lmevti velmelâiketü bâsiṭû eydîhim. aḫricû enfüseküm. elyevme tüczevne `aẕâbe-lhûni bimâ küntüm teḳûlûne `ale-llâhi gayra-lḥaḳḳi veküntüm `an âyâtihî testekbirûn.'),
(883, 6, 94, 'veleḳad ci\'tümûnâ fürâdâ kemâ ḫalaḳnâküm evvele merrativ veteraktüm mâ ḫavvelnâküm verâe żuhûriküm. vemâ nerâ me`aküm şüfe`âekümü-lleẕîne za`amtüm ennehüm fîküm şürakâ\'. leḳat teḳaṭṭa`a beyneküm veḍalle `anküm mâ küntüm tez`umûn.'),
(884, 6, 95, 'inne-llâhe fâliḳu-lḥabbi vennevâ. yuḫricü-lḥayye mine-lmeyyiti vemuḫricü-lmeyyiti mine-lḥayy. ẕâlikümü-llâhü feennâ tü\'fekûn.'),
(885, 6, 96, 'fâliḳu-l\'iṣbâḥ. vece`ale-lleyle sekenev veşşemse velḳamera ḥusbânâ. ẕâlike taḳdîru-l`azîzi-l`alîm.'),
(886, 6, 97, 'vehüve-lleẕî ce`ale lekümü-nnücûme litehtedû bihâ fî żulümâti-lberri velbaḥr. ḳad feṣṣalne-l\'âyâti liḳavmiy ya`lemûn.'),
(887, 6, 98, 'vehüve-lleẕî enşeeküm min nefsiv vâḥidetin femüsteḳarruv vemüstevda`. ḳad feṣṣalne-l\'âyâti liḳavmiy yefḳahûn.'),
(888, 6, 99, 'vehüve-lleẕî enzele mine-ssemâi mââ. feaḫracnâ bihî nebâte külli şey\'in feaḫracnâ minhü ḫaḍiran nuḫricü minhü ḥabbem müterâkibâ. vemine-nnaḫli min ṭal`ihâ ḳinvânün dâniyetüv vecennâtim min a`nâbiv vezzeytûne verrummâne müştebihev vegayra müteşâbih.-nżurû ilâ ŝemerihî iẕâ eŝmera veyen`ih. inne fî ẕâliküm leâyâtil liḳavmiy yü\'minûn.'),
(889, 6, 100, 'vece`alû lillâhi şürakâe-lcinne veḫaleḳahüm veḫaraḳû lehû benîne vebenâtim bigayri `ilm. sübḥânehû vete`âlâ `ammâ yeṣifûn.'),
(890, 6, 101, 'bedî`u-ssemâvâti vel\'arḍ. ennâ yekûnü lehû veledüv velem tekül lehû ṣâḥibeh. veḫaleḳa külle şey\'. vehüve bikülli şey\'in `alîm.'),
(891, 6, 102, 'ẕâlikümü-llâhü rabbüküm. lâ ilâhe illâ hû. ḫâliḳu külli şey\'in fa`büdûh. vehüve `alâ külli şey\'iv vekîl.'),
(892, 6, 103, 'lâ tüdrikühü-l\'ebṣâr. vehüve yüdrikü-l\'ebṣâr. vehüve-lleṭîfü-lḫabîr.'),
(893, 6, 104, 'ḳad câeküm beṣâiru mir rabbiküm. femen ebṣara felinefsih. vemen `amiye fe`aleyhâ. vemâ ene `aleyküm biḥafîż.'),
(894, 6, 105, 'vekeẕâlike nüṣarrifü-l\'âyâti veliyeḳûlû deraste velinübeyyinehû liḳavmiy ya`lemûn.'),
(895, 6, 106, 'ittebi` mâ ûḥiye ileyke mir rabbik. lâ ilâhe illâ hû. vea`riḍ `ani-lmüşrikîn.'),
(896, 6, 107, 'velev şâe-llâhü mâ eşrakû. vemâ ce`alnâke `aleyhim ḥafîżâ. vemâ ente `aleyhim bivekîl.'),
(897, 6, 108, 'velâ tesübbü-lleẕîne yed`ûne min dûni-llâhi feyesübbü-llâhe `advem bigayri `ilm. keẕâlike zeyyennâ likülli ümmetin `amelehüm ŝümme ilâ rabbihim merci`uhüm feyünebbiühüm bimâ kânû ya`melûn.'),
(898, 6, 109, 'veaḳsemû billâhi cehde eymânihim lein câethüm âyetüh leyü\'minünne bihâ. ḳul inneme-l\'âyâtü `inde-llâhi vemâ yüş`iruküm ennehâ iẕâ câet lâ yü\'minûn.'),
(899, 6, 110, 'venüḳallibü ef\'idetehüm veebṣârahüm kemâ lem yü\'minû bihî evvele merrativ veneẕeruhüm fî ṭugyânihim ya`mehûn.'),
(900, 6, 111, 'velev ennenâ nezzelnâ ileyhimü-lmelâikete vekellemehümü-lmevtâ veḥaşernâ `aleyhim külle şey\'in ḳubülem mâ kânû liyü\'minû illâ ey yeşâe-llâhü velâkinne ekŝerahüm yechelûn.'),
(901, 6, 112, 'vekeẕâlike ce`alnâ likülli nebiyyin `adüvven şeyâṭîne-l\'insi velcinni yûḥî ba`ḍuhüm ilâ ba`ḍin zuḫrufe-lḳavli gurûrâ. velev şâe rabbüke mâ fe`alûhü feẕerhüm vemâ yefterûn.'),
(902, 6, 113, 'velitaṣgâ ileyhi ef\'idetü-lleẕîne lâ yü\'minûne bil\'âḫirati veliyerḍavhü veliyaḳterifû mâ hüm muḳterifûn.'),
(903, 6, 114, 'efegayra-llâhi ebtegî ḥakemev vehüve-lleẕî enzele ileykümü-lkitâbe müfeṣṣalâ. velleẕîne âteynâhümü-lkitâbe ya`lemûne ennehû münezzelüm mir rabbike bilḥaḳḳi felâ tekûnenne mine-lmümterîn.'),
(904, 6, 115, 'vetemmet kelimetü rabbike ṣidḳav ve`adlâ. lâ mübeddile likelimâtih. vehüve-ssemî`u-l`alîm.'),
(905, 6, 116, 'vein tüṭi` ekŝera men fi-l\'arḍi yüḍillûke `an sebîli-llâh. iy yettebi`ûne ille-żżanne vein hüm illâ yaḫruṣûn.'),
(906, 6, 117, 'inne rabbeke hüve a`lemü mey yeḍillü `an sebîlih. vehüve a`lemü bilmühtedîn.'),
(907, 6, 118, 'fekülû mimmâ ẕükira-smü-llâhi `aleyhi in küntüm biâyâtihî mü\'minîn.'),
(908, 6, 119, 'vemâ leküm ellâ te\'külû mimmâ ẕükira-smü-llâhi `aleyhi veḳad feṣṣale leküm mâ ḥarrame `aleyküm illâ me-ḍṭurirtüm ileyh. veinne keŝîral leyüḍillûne biehvâihim bigayri `ilm. inne rabbeke hüve a`lemü bilmü`tedîn.'),
(909, 6, 120, 'veẕerû żâhira-l\'iŝmi vebâṭineh. inne-lleẕîne yeksibûne-l\'iŝme seyüczevne bimâ kânû yaḳterifûn.'),
(910, 6, 121, 'velâ te\'külû mimmâ lem yüẕkeri-smü-llâhi `aleyhi veinnehû lefisḳ. veinne-şşeyâṭîne leyûḥûne ilâ evliyâihim liyücâdilûküm. vein eṭa`tümûhüm inneküm lemüşrikûn.'),
(911, 6, 122, 'evemen kâne meyten feaḥyeynâhü vece`alnâ lehû nûray yemşî bihî fi-nnâsi kemem meŝelühû fi-żżulümâti leyse biḫâricim minhâ. keẕâlike züyyine lilkâfirîne mâ kânû ya`melûn.'),
(912, 6, 123, 'vekeẕâlike ce`alnâ fî külli ḳaryetin ekâbira mücrimîhâ liyemkürû fîhâ. vemâ yemkürûne illâ bienfüsihim vemâ yeş`urûn.'),
(913, 6, 124, 'veiẕâ câethüm âyetün ḳâlû len nü\'mine ḥattâ nü\'tâ miŝle mâ ûtiye rusülü-llâh. allâhü a`lemü ḥayŝü yec`alü risâleteh. seyüṣîbü-lleẕîne ecramû ṣagârun `inde-llâhi ve`aẕâbün şedîdüm bimâ kânû yemkürûn.'),
(914, 6, 125, 'femey yüridi-llâhü ey yehdiyehû yeşraḥ ṣadrahû lil\'islâm. vemey yürid ey yüḍillehû yec`al ṣadrahû ḍayyiḳan ḥaracen keennemâ yeṣṣa``adü fi-ssemâ\'. keẕâlike yec`alü-llâhü-rricse `ale-lleẕîne lâ yü\'minûn.'),
(915, 6, 126, 'vehâẕâ ṣirâṭu rabbike müsteḳîmâ. ḳad feṣṣalne-l\'âyâti liḳavmiy yeẕẕekkerûn.'),
(916, 6, 127, 'lehüm dâru-sselâmi `inde rabbihim vehüve veliyyühüm bimâ kânû ya`melûn.'),
(917, 6, 128, 'veyevme yaḥşüruhüm cemî`â. yâ ma`şera-lcinni ḳadi-stekŝertüm mine-l\'ins. veḳâle evliyâühüm mine-l\'insi rabbene-stemte`a ba`ḍunâ biba`ḍiv vebelagnâ ecelene-lleẕî eccelte lenâ. ḳâle-nnâru meŝvâküm ḫâlidîne fîhâ illâ mâ şâe-llâh. inne rabbeke ḥakîmün `alîm.'),
(918, 6, 129, 'vekeẕâlike nüvellî ba`ḍa-żżâlimîne ba`ḍam bimâ kânû yeksibûn.'),
(919, 6, 130, 'yâ ma`şera-lcinni vel\'insi elem ye\'tiküm rusülüm minküm yeḳuṣṣûne `aleyküm âyâtî veyünẕirûneküm liḳâe yevmiküm hâẕâ. ḳâlû şehidnâ `alâ enfüsinâ vegarrathümü-lḥayâtü-ddünyâ veşehidû `alâ enfüsihim ennehüm kânû kâfirîn.'),
(920, 6, 131, 'ẕâlike el lem yekür rabbüke mühlike-lḳurâ biżulmiv veehlühâ gâfilûn.'),
(921, 6, 132, 'veliküllin deracâtüm mimmâ `amilû. vemâ rabbüke bigâfilin `ammâ ya`melûn.'),
(922, 6, 133, 'verabbüke-lganiyyü ẕü-rraḥmeh. iy yeşe\' yüẕhibküm veyestaḫlif mim ba`diküm mâ yeşâü kemâ enşeeküm min ẕürriyyeti ḳavmin âḫarîn.'),
(923, 6, 134, 'inne mâ tû`adûne leâtiv vemâ entüm bimü`cizîn.'),
(924, 6, 135, 'ḳul yâ ḳavmi-`melû `alâ mekânetiküm innî `âmil. fesevfe ta`lemûne men tekûnü lehû `âḳibetü-ddâr. innehû lâ yüfliḥu-żżâlimûn.'),
(925, 6, 136, 'vece`alû lillâhi mimmâ ẕerae mine-lḥarŝi vel\'en`âmi neṣîben feḳâlû hâẕâ lillâhi biza`mihim vehâẕâ lişürakâinâ. femâ kâne lişürakâihim felâ yeṣilü ile-llâh. vemâ kâne lillâhi fehüve yeṣilü ilâ şürakâihim. sâe mâ yaḥkümûn.'),
(926, 6, 137, 'vekeẕâlike zeyyene likeŝîrim mine-lmüşrikîne ḳatle evlâdihim şürakâühüm liyürdûhüm veliyelbisû `aleyhim dînehüm. velev şâe-llâhü mâ fe`alûhü feẕerhüm vemâ yefterûn.'),
(927, 6, 138, 'veḳâlû hâẕihî en`âmüv veḥarŝün ḥicr. lâ yaṭ`amühâ illâ men neşâü biza`mihim veen`âmün ḥurrimet żuhûruhâ veen`âmül lâ yeẕkürûne-sme-llâhi `aleyhe-ftirâen `aleyh. seyeczîhim bimâ kânû yefterûn.'),
(928, 6, 139, 'veḳâlû mâ fî büṭûni hâẕihi-l\'en`âmi ḫâliṣatül liẕükûrinâ vemüḥarramün `alâ ezvâcinâ. veiy yeküm meyteten fehüm fîhi şürakâ\'. seyeczîhim vaṣfehüm. innehû ḥakîmün `alîm.'),
(929, 6, 140, 'ḳad ḫasira-lleẕîne ḳatelû evlâdehüm sefehem bigayri `ilmiv veḥarramû mâ razeḳahümü-llâhü-ftirâen `ale-llâh. ḳad ḍallû vemâ kânû mühtedîn.'),
(930, 6, 141, 'vehüve-lleẕî enşee cennâtim ma`rûşâtiv vegayra ma`rûşâtiv vennaḫle vezzer`a muḫtelifen ükülühû vezzeytûne verrummâne müteşâbihev vegayra müteşâbih. külû min ŝemerihî iẕâ eŝmera veâtû ḥaḳḳahû yevme ḥaṣâdihi. velâ tüsrifû. innehû lâ yüḥibbü-lmüsrifîn.'),
(931, 6, 142, 'vemine-l\'en`âmi ḥamûletev veferşâ. külû mimmâ razeḳakümü-llâhü velâ tettebi`û ḫuṭuvâti-şşeyṭân. innehû leküm `adüvvüm mübîn.'),
(932, 6, 143, 'ŝemâniyete ezvâc. mine-ḍḍa\'ni-ŝneyni vemine-lma`zi-ŝneyn. ḳul âẕẕekerayni ḥarrame emi-l\'ünŝeyeyni emme-ştemelet `aleyhi erḥâmü-l\'ünŝeyeyn. nebbiûnî bi`ilmin in küntüm ṣâdiḳîn.'),
(933, 6, 144, 'vemine-l\'ibili-ŝneyni vemine-lbeḳari-ŝneyn. ḳul âẕẕekerayni ḥarrame emi-l\'ünŝeyeyni emme-ştemelet `aleyhi erḥâmü-l\'ünŝeyeyn. em küntüm şühedâe iẕ veṣṣâkümü-llâhü bihâẕâ. femen ażlemü mimmeni-fterâ `ale-llâhi keẕibel liyüḍille-nnâse bigayri `ilm. inne-llâhe lâ yehdi-lḳavme-żżâlimîn.'),
(934, 6, 145, 'ḳul lâ ecidü fî mâ ûḥiye ileyye müḥarramen `alâ ṭâ`imiy yaṭ`amühû illâ ey yekûne meyteten ev demem mesfûḥan ev laḥme ḫinzîrin feinnehû ricsün ev fisḳan ühille ligayri-llâhi bih. femeni-ḍṭurra gayra bâgiv velâ `âdin feinne rabbeke gafûrur raḥîm.'),
(935, 6, 146, 've`ale-lleẕîne hâdû ḥarramnâ külle ẕî żufür. vemine-lbeḳari velganemi ḥarramnâ `aleyhim şüḥûmehümâ illâ mâ ḥamelet żuhûruhümâ evi-lḥavâyâ ev me-ḫteleṭa bi`ażm. ẕâlike cezeynâhüm bibagyihim. veinnâ leṣâdiḳûn.'),
(936, 6, 147, 'fein keẕẕebûke feḳur rabbüküm ẕû raḥmetiv vâsi`ah. velâ yüraddü be\'sühû `ani-lḳavmi-lmücrimîn.'),
(937, 6, 148, 'seyeḳûlü-lleẕîne eşrakû lev şâe-llâhü mâ eşraknâ velâ âbâünâ velâ ḥarramnâ min şey\'. keẕâlike keẕẕebe-lleẕîne min ḳablihim ḥattâ ẕâḳû be\'senâ. ḳul hel `indeküm min `ilmin fetuḫricûhü lenâ. in tettebi`ûne ille-żżanne vein entüm illâ taḫruṣûn.'),
(938, 6, 149, 'ḳul felillâhi-lḥuccetü-lbâligah. felev şâe lehedâküm ecme`în.'),
(939, 6, 150, 'ḳul helümme şühedâekümü-lleẕîne yeşhedûne enne-llâhe ḥarrame hâẕâ. fein şehidû felâ teşhed me`ahüm. velâ tettebi` ehvâe-lleẕîne keẕẕebû biâyâtinâ velleẕîne lâ yü\'minûne bil\'âḫirati vehüm birabbihim ya`dilûn.'),
(940, 6, 151, 'ḳul te`âlev etlü mâ ḥarrame rabbüküm `aleyküm ellâ tüşrikû bihî şey\'â. vebilvâlideyni iḥsânâ. velâ taḳtülû evlâdeküm min imlâḳ. naḥnü nerzüḳuküm veiyyâhüm. velâ taḳrabü-lfevâḥişe mâ żahera minhâ vemâ beṭan. velâ taḳtülü-nnefse-lletî ḥarrame-llâhü illâ bilḥaḳḳ. ẕâliküm veṣṣâküm bihî le`alleküm ta`ḳilûn.'),
(941, 6, 152, 'velâ taḳrabû mâle-lyetîmi illâ billetî hiye aḥsenü ḥattâ yeblüga eşüddeh. veevfü-lkeyle velmîzâne bilḳisṭ. lâ nükellifü nefsen illâ vus`ahâ veiẕâ ḳultüm fa`dilû velev kâne ẕâ ḳurbâ. vebi`ahdi-llâhi evfû. ẕâliküm veṣṣâküm bihî le`alleküm teẕekkerûn.'),
(942, 6, 153, 'veenne hâẕâ ṣirâṭî müsteḳîmen fettebi`ûh. velâ tettebi`ü-ssübüle feteferraḳa biküm `an sebîlih. ẕâliküm veṣṣâküm bihî le`alleküm tetteḳûn.'),
(943, 6, 154, 'ŝümme âteynâ mûse-lkitâbe temâmen `ale-lleẕî aḥsene vetefṣîlel likülli şey\'iv vehüdev veraḥmetel le`allehüm biliḳâi rabbihim yü\'minûn.'),
(944, 6, 155, 'vehâẕâ kitâbün enzelnâhü mübârakün fettebi`ûhü vetteḳû le`alleküm türḥamûn.'),
(945, 6, 156, 'en teḳûlû innemâ ünzile-lkitâbü `alâ ṭâifeteyni min ḳablinâ. vein künnâ `an dirâsetihim legâfilîn.'),
(946, 6, 157, 'ev teḳûlû lev ennâ ünzile `aleyne-lkitâbü lekünnâ ehdâ minhüm. feḳad câeküm beyyinetüm mir rabbiküm vehüdev veraḥmeh. femen ażlemü mimmen keẕẕebe biâyâti-llâhi veṣadefe `anhâ. seneczi-lleẕîne yaṣdifûne `an âyâtinâ sûe-l`aẕâbi bimâ kânû yaṣdifûn.'),
(947, 6, 158, 'hel yenżurûne illâ en te\'tiyehümü-lmelâiketü ev ye\'tiye rabbüke ev ye\'tiye ba`ḍu âyâti rabbik. yevme ye\'tî ba`ḍu âyâti rabbike lâ yenfe`u nefsen îmânühâ lem tekün âmenet min ḳablü ev kesebet fî îmânihâ ḫayrâ. ḳuli-nteżirû innâ münteżirûn.'),
(948, 6, 159, 'inne-lleẕîne ferraḳû dînehüm vekânû şiye`al leste minhüm fî şey\'. innemâ emruhüm ile-llâhi ŝümme yünebbiühüm bimâ kânû yef`alûn.'),
(949, 6, 160, 'men câe bilḥaseneti felehû `aşru emŝâlihâ. vemen câe bisseyyieti felâ yüczâ illâ miŝlehâ vehüm lâ yużlemûn.'),
(950, 6, 161, 'ḳul innenî hedânî rabbî ilâ ṣirâṭim müsteḳîm. dînen ḳiyemem millete ibrâhîme ḥanîfâ. vemâ kâne mine-lmüşrikîn.'),
(951, 6, 162, 'ḳul inne ṣalâtî venüsükî vemaḥyâye vememâtî lillâhi rabbi-l`âlemîn.'),
(952, 6, 163, 'lâ şerîke leh. vebiẕâlike ümirtü veenâ evvelü-lmüslimîn.'),
(953, 6, 164, 'ḳul egayra-llâhi ebgî rabbev vehüve rabbü külli şey\'. velâ teksibü küllü nefsin illâ `aleyhâ. velâ teziru vâziratüv vizra uḫrâ. ŝümme ilâ rabbiküm merci`uküm feyünebbiüküm bimâ küntüm fîhi taḫtelifûn.'),
(954, 6, 165, 'vehüve-lleẕî ce`aleküm ḫalâife-l\'arḍi verafe`a ba`ḍaküm fevḳa ba`ḍin deracâtil liyeblüveküm fî mâ âtâküm. inne rabbeke serî`u-l`iḳâb. veinnehû legafûrur raḥîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'elif-lâm-mîm-ṣâd.'),
(956, 7, 2, 'kitâbün ünzile ileyke felâ yekün fî ṣadrike ḥaracüm minhü litünẕira bihî veẕikrâ lilmü\'minîn.'),
(957, 7, 3, 'ittebi`û mâ ünzile ileyküm mir rabbiküm velâ tettebi`û min dûnihî evliyâ\'. ḳalîlem mâ teẕekkerûn.'),
(958, 7, 4, 'vekem min ḳaryetin ehleknâhâ fecâehâ be\'sünâ beyâten ev hüm ḳâilûn.'),
(959, 7, 5, 'femâ kâne da`vâhüm iẕ câehüm be\'sünâ illâ en ḳâlû innâ künnâ żâlimîn.'),
(960, 7, 6, 'felenes\'elenne-lleẕîne ürsile ileyhim velenes\'elenne-lmürselîn.'),
(961, 7, 7, 'feleneḳuṣṣanne `aleyhim bi`ilmiv vemâ künnâ gâibîn.'),
(962, 7, 8, 'velveznü yevmeiẕin-lḥaḳḳ. femen ŝeḳulet mevâzînühû feülâike hümü-lmüfliḥûn.'),
(963, 7, 9, 'vemen ḫaffet mevâzînühû feülâike-lleẕîne ḫasirû enfüsehüm bimâ kânû biâyâtinâ yażlimûn.'),
(964, 7, 10, 'veleḳad mekkennâküm fi-l\'arḍi vece`alnâ leküm fîhâ me`âyiş. ḳalîlem mâ teşkürûn.'),
(965, 7, 11, 'veleḳad ḫalaḳnâküm ŝümme ṣavvernâküm ŝümme ḳulnâ lilmelâiketi-scüdû liâdem. fesecedû illâ iblîs. lem yeküm mine-ssâcidîn.'),
(966, 7, 12, 'ḳâle mâ mene`ake ellâ tescüde iẕ emertük. ḳâle ene ḫayrum minh. ḫalaḳtenî min nâriv veḫalaḳtehû min ṭîn.'),
(967, 7, 13, 'ḳâle fehbiṭ minhâ femâ yekûnü leke en tetekebbera fîhâ faḫruc inneke mine-ṣṣâgirîn.'),
(968, 7, 14, 'ḳâle enżirnî ilâ yevmi yüb`aŝûn.'),
(969, 7, 15, 'ḳâle inneke mine-lmünżarîn.'),
(970, 7, 16, 'ḳâle febimâ agveytenî leaḳ`udenne lehüm ṣirâṭake-lmüsteḳîm.'),
(971, 7, 17, 'ŝümme leâtiyennehüm mim beyni eydîhim vemin ḫalfihim ve`an eymânihim ve`an şemâilihim. velâ tecidü ekŝerahüm şâkirîn.'),
(972, 7, 18, 'ḳâle-ḫruc minhâ meẕ\'ûmem medḥûrâ. lemen tebi`ake minhüm leemleenne cehenneme minküm ecme`în.'),
(973, 7, 19, 'veyâ âdemü-skün ente vezevcüke-lcennete fekülâ min ḥayŝü şi\'tümâ velâ taḳrabâ hâẕihi-şşecerate fetekûnâ mine-żżâlimîn.'),
(974, 7, 20, 'fevesvese lehüme-şşeyṭânü liyübdiye lehümâ mâ vûriye `anhümâ min sev\'âtihimâ veḳâle mâ nehâkümâ rabbükümâ `an hâẕihi-şşecerati illâ en tekûnâ melekeyni ev tekûnâ mine-lḫâlidîn.'),
(975, 7, 21, 'veḳâsemehümâ innî lekümâ lemine-nnâṣiḥîn.'),
(976, 7, 22, 'fedellâhümâ bigurûr. felemmâ ẕâḳe-şşecerate bedet lehümâ sev\'âtühümâ veṭafiḳâ yaḫṣifâni `aleyhimâ miv veraḳi-lcenneh. venâdâhümâ rabbühümâ elem enhekümâ `an tilküme-şşecerati veeḳul lekümâ inne-şşeyṭâne lekümâ `adüvvüm mübîn.'),
(977, 7, 23, 'ḳâlâ rabbenâ żalemnâ enfüsenâ veil lem tagfir lenâ veterḥamnâ lenekûnenne mine-lḫâsirîn.'),
(978, 7, 24, 'ḳâle-hbiṭû ba`ḍuküm liba`ḍin `adüvv. veleküm fi-l\'arḍi müsteḳarruv vemetâ`un ilâ ḥîn.'),
(979, 7, 25, 'ḳâle fîhâ taḥyevne vefîhâ temûtûne veminhâ tuḫracûn.'),
(980, 7, 26, 'yâ benî âdeme ḳad enzelnâ `aleyküm libâsey yüvârî sev\'âtiküm verîşâ. velibâsü-ttaḳvâ ẕâlike ḫayr. ẕâlike min âyâti-llâhi le`allehüm yeẕẕekkerûn.'),
(981, 7, 27, 'yâ benî âdeme lâ yeftinennekümü-şşeyṭânü kemâ aḫrace ebeveyküm mine-lcenneti yenzi`u `anhümâ libâsehümâ liyüriyehümâ sev\'âtihimâ. innehû yerâküm hüve veḳabîlühû min ḥayŝü lâ teravnehüm. innâ ce`alne-şşeyâṭîne evliyâe lilleẕîne lâ yü\'minûn.'),
(982, 7, 28, 'veiẕâ fe`alû fâḥişeten ḳâlû vecednâ `aleyhâ âbâenâ vellâhü emeranâ bihâ. ḳul inne-llâhe lâ ye\'müru bilfaḥşâ\'. eteḳûlûne `ale-llâhi mâ lâ ta`lemûn.'),
(983, 7, 29, 'ḳul emera rabbî bilḳisṭ. veeḳîmû vucûheküm `inde külli mescidiv ved`ûhü muḫliṣîne lehü-ddîne. kemâ bedeeküm te`ûdûn.'),
(984, 7, 30, 'ferîḳan hedâ veferîḳan ḥaḳḳa `aleyhimu-ḍḍalâleh. innehümü-tteḫaẕü-şşeyâṭîne evliyâe min dûni-llâhi veyaḥsebûne ennehüm mühtedûn.'),
(985, 7, 31, 'yâ benî âdeme ḫuẕû zîneteküm `inde külli mescidiv vekülû veşrabû velâ tüsrifû. innehû lâ yüḥibbü-lmüsrifîn.'),
(986, 7, 32, 'ḳul men ḥarrame zînete-llâhi-lletî aḫrace li`ibâdihî veṭṭayyibâti mine-rrizḳ. ḳul hiye lilleẕîne âmenû fi-lḥayâti-ddünyâ ḫâliṣatey yevme-lḳiyâmeh. keẕâlike nüfeṣṣilü-l\'âyâti liḳavmiy ya`lemûn.'),
(987, 7, 33, 'ḳul innemâ ḥarrame rabbiye-lfevâḥişe mâ żahera minhâ vemâ beṭane vel\'iŝme velbagye bigayri-lḥaḳḳi veen tüşrikû billâhi mâ lem yünezzil bihî sülṭânev veen teḳûlû `ale-llâhi mâ lâ ta`lemûn.'),
(988, 7, 34, 'velikülli ümmetin ecel. feiẕâ câe ecelühüm lâ yeste\'ḫirûne sâ`atev velâ yestaḳdimûn.'),
(989, 7, 35, 'yâ benî âdeme immâ ye\'tiyenneküm rusülüm minküm yeḳuṣṣûne `aleyküm âyâtî femeni-tteḳâ veaṣleḥa felâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(990, 7, 36, 'velleẕîne keẕẕebû biâyâtinâ vestekberû `anhâ ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(991, 7, 37, 'femen ażlemü mimmeni-fterâ `ale-llâhi keẕiben ev keẕẕebe biâyâtih. ülâike yenâlühüm neṣîbühüm mine-lkitâb. ḥattâ iẕâ câethüm rusülünâ yeteveffevnehüm ḳâlû eyne mâ küntüm ted`ûne min dûni-llâh. ḳâlû ḍallû `annâ veşehidû `alâ enfüsihim ennehüm kânû kâfirîn.'),
(992, 7, 38, 'ḳâle-dḫulû fî ümemin ḳad ḫalet min ḳabliküm mine-lcinni vel\'insi fi-nnâr. küllemâ deḫalet ümmetül le`anet uḫtehâ. ḥattâ iẕe-ddârakû fîhâ cemî`an ḳâlet uḫrâhüm liûlâhüm rabbenâ hâülâi eḍallûnâ feâtihim `aẕâben ḍi`fem mine-nnâr. ḳâle liküllin ḍi`füv velâkil lâ ta`lemûn.'),
(993, 7, 39, 'veḳâlet ûlâhüm liuḫrâhüm femâ kâne leküm `aleynâ min faḍlin feẕûḳu-l`aẕâbe bimâ küntüm teksibûn.'),
(994, 7, 40, 'inne-lleẕîne keẕẕebû biâyâtinâ vestekberû `anhâ lâ tüfetteḥu lehüm ebvâbü-ssemâi velâ yedḫulûne-lcennete ḥattâ yelice-lcemelü fî semmi-lḫiyâṭ. vekeẕâlike neczi-lmücrimîn.'),
(995, 7, 41, 'lehüm min cehenneme mihâdüv vemin fevḳihim gavâş. vekeẕâlike neczi-żżâlimîn.'),
(996, 7, 42, 'velleẕîne âmenû ve`amilu-ṣṣâliḥâti lâ nükellifü nefsen illâ vus`ahâ. ülâike aṣḥâbü-lcenneh. hüm fîhâ ḫâlidûn.'),
(997, 7, 43, 'veneza`nâ mâ fî ṣudûrihim min gillin tecrî min taḥtihimü-l\'enhâr. veḳâlü-lḥamdü lillâhi-lleẕî hedânâ lihâẕâ vemâ künnâ linehtediye levlâ en hedâne-llâh. leḳad câet rusülü rabbinâ bilḥaḳḳ. venûdû en tilkümü-lcennetü ûriŝtümûhâ bimâ küntüm ta`melûn.'),
(998, 7, 44, 'venâdâ aṣḥâbü-lcenneti aṣḥâbe-nnâri en ḳad vecednâ mâ ve`adenâ rabbünâ ḥaḳḳan fehel vecettüm mâ ve`ade rabbüküm ḥaḳḳâ. ḳâlû ne`am. feeẕẕene müeẕẕinüm beynehüm el la`netü-llâhi `ale-żżâlimîn.'),
(999, 7, 45, 'elleẕîne yeṣuddûne `an sebîli-llâhi veyebgûnehâ `ivecâ. vehüm bil\'âḫirati kâfirûn.'),
(1000, 7, 46, 'vebeynehümâ ḥicâb. ve`ale-l\'a`râfi ricâlüy ya`rifûne küllem bisîmâhüm. venâdev aṣḥâbe-lcenneti en selâmün `aleyküm lem yedḫulûhâ vehüm yaṭme`ûn.'),
(1001, 7, 47, 'veiẕâ ṣurifet ebṣâruhüm tilḳâe aṣḥâbi-nnâri ḳâlû rabbenâ lâ tec`alnâ me`a-lḳavmi-żżâlimîn.'),
(1002, 7, 48, 'venâdâ aṣḥâbü-l\'a`râfi ricâley ya`rifûnehüm bisîmâhüm ḳâlû mâ agnâ `anküm cem`uküm vemâ küntüm testekbirûn.'),
(1003, 7, 49, 'ehâülâi-lleẕîne aḳsemtüm lâ yenâlühümü-llâhü biraḥmeh. üdḫulü-lcennete lâ ḫavfün `aleyküm velâ entüm taḥzenûn.'),
(1004, 7, 50, 'venâdâ aṣḥâbü-nnâri aṣḥâbe-lcenneti en efîḍû `aleynâ mine-lmâi ev mimmâ razeḳakümü-llâh. ḳâlû inne-llâhe ḥarramehümâ `ale-lkâfirîn.'),
(1005, 7, 51, 'elleẕîne-tteḫaẕû dînehüm lehvev vele`ibev vegarrathümü-lḥayâtü-ddünyâ. felyevme nensâhüm kemâ nesû liḳâe yevmihim hâẕâ vemâ kânû biâyâtinâ yecḥadûn.'),
(1006, 7, 52, 'veleḳad ci\'nâhüm bikitâbin feṣṣalnâhü `alâ `ilmin hüdev veraḥmetel liḳavmiy yü\'minûn.'),
(1007, 7, 53, 'hel yenżurûne illâ te\'vîleh. yevme ye\'tî te\'vîlühû yeḳûlü-lleẕîne nesûhü min ḳablü ḳad câet rusülü rabbinâ bilḥaḳḳ. fehel lenâ min şüfe`âe feyeşfe`û lenâ ev nüraddü fena`mele gayra-lleẕî künnâ na`mel. ḳad ḫasirû enfüsehüm veḍalle `anhüm mâ kânû yefterûn.'),
(1008, 7, 54, 'inne rabbekümü-llâhü-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa fî sitteti eyyâmin ŝümme-stevâ `ale-l`arşi yugşi-lleyle-nnehâra yaṭlübühû ḥaŝîŝev veşşemse velḳamera vennücûme müseḫḫarâtim biemrih. elâ lehü-lḫalḳu vel\'emr. tebârake-llâhü rabbü-l`âlemîn.'),
(1009, 7, 55, 'üd`û rabbeküm teḍarru`av veḫufyeh. innehû lâ yüḥibbü-lmü`tedîn.'),
(1010, 7, 56, 'velâ tüfsidû fi-l\'arḍi ba`de iṣlâḥihâ ved`ûhü ḫavfev veṭame`â. inne raḥmete-llâhi ḳarîbüm mine-lmuḥsinîn.'),
(1011, 7, 57, 'vehüve-lleẕî yürsilü-rriyâḥa büşram beyne yedey raḥmetih. ḥattâ iẕâ eḳallet seḥâben ŝiḳâlen suḳnâhü libeledim meyyitin feenzelnâ bihi-lmâe feaḫracnâ bihî min külli-ŝŝemerât. keẕâlike nuḫricü-lmevtâ le`alleküm teẕekkerûn.'),
(1012, 7, 58, 'velbeledu-ṭṭayyibü yaḫrucü nebâtühû biiẕni rabbih. velleẕî ḫabüŝe lâ yaḫrucü illâ nekidâ. keẕâlike nüṣarrifü-l\'âyâti liḳavmiy yeşkürûn.'),
(1013, 7, 59, 'leḳad erselnâ nûḥan ilâ ḳavmihî feḳâle yâ ḳavmi-`büdü-llâhe mâ leküm min ilâhin gayruh. innî eḫâfü `aleyküm `aẕâbe yevmin `ażîm.'),
(1014, 7, 60, 'ḳâle-lmeleü min ḳavmihî innâ lenerâke fî ḍalâlim mübîn.'),
(1015, 7, 61, 'ḳâle yâ ḳavmi leyse bî ḍalâletüv velâkinnî rasûlüm mir rabbi-l`âlemîn.'),
(1016, 7, 62, 'übelliguküm risâlâti rabbî veenṣaḥu leküm vea`lemü mine-llâhi mâ lâ ta`lemûn.'),
(1017, 7, 63, 'eve`acibtüm en câeküm ẕikrum mir rabbiküm `alâ racülim minküm liyünẕiraküm velitetteḳû vele`alleküm türḥamûn.'),
(1018, 7, 64, 'fekeẕẕebûhü feenceynâhü velleẕîne me`ahû fi-lfülki veagraḳne-lleẕîne keẕẕebû biâyâtinâ. innehüm kânû ḳavmen `amîn.'),
(1019, 7, 65, 'veilâ `âdin eḫâhüm hûdâ. ḳâle yâ ḳavmi-`büdü-llâhe mâ leküm min ilâhin gayruh. efelâ tetteḳûn.'),
(1020, 7, 66, 'ḳâle-lmeleü-lleẕîne keferû min ḳavmihî innâ lenerâke fî sefâhetiv veinnâ leneżunnüke mine-lkâẕibîn.'),
(1021, 7, 67, 'ḳâle yâ ḳavmi leyse bî sefâhetüv velâkinnî rasûlüm mir rabbi-l`âlemîn.'),
(1022, 7, 68, 'übelliguküm risâlâti rabbî veenâ leküm nâṣiḥun emîn.'),
(1023, 7, 69, 'eve`acibtüm en câeküm ẕikrum mir rabbiküm `alâ racülim minküm liyünẕiraküm. veẕkürû iẕ ce`aleküm ḫulefâe mim ba`di ḳavmi nûḥiv vezâdeküm fi-lḫalḳi baṣṭah. feẕkürû âlâe-llâhi le`alleküm tüfliḥûn.'),
(1024, 7, 70, 'ḳâlû eci\'tenâ lina`büde-llâhe vaḥdehû veneẕera mâ kâne ya`büdü âbâünâ. fe\'tinâ bimâ te`idünâ in künte mine-ṣṣâdiḳîn.'),
(1025, 7, 71, 'ḳâle ḳad veḳa`a `aleyküm mir rabbiküm ricsüv vegaḍab. etücâdilûnenî fî esmâin semmeytümûhâ entüm veâbâüküm mâ nezzele-llâhü bihâ min sülṭân. fenteżirû innî me`aküm mine-lmünteżirîn.'),
(1026, 7, 72, 'feenceynâhü velleẕîne me`ahû biraḥmetim minnâ veḳaṭa`nâ dâbira-lleẕîne keẕẕebû biâyâtinâ vemâ kânû mü\'minîn.'),
(1027, 7, 73, 'veilâ ŝemûde eḫâhüm ṣâliḥâ. ḳâle yâ ḳavmi-`büdü-llâhe mâ leküm min ilâhin gayruh. ḳad câetküm beyyinetüm mir rabbiküm. hâẕihî nâḳatü-llâhi leküm âyeten feẕerûhâ te\'kül fî arḍi-llâhi velâ temessûhâ bisûin feye\'ḫuẕeküm `aẕâbün elîm.'),
(1028, 7, 74, 'veẕkürû iẕ ce`aleküm ḫulefâe mim ba`di `âdiv vebevveeküm fi-l\'arḍi tetteḫiẕûne min sühûlihâ ḳuṣûrav vetenḥitûne-lcibâle büyûtâ. feẕkürû âlâe-llâhi velâ ta`ŝev fi-l\'arḍi müfsidîn.'),
(1029, 7, 75, 'ḳâle-lmeleü-lleẕîne-stekberû min ḳavmihî lilleẕîne-stuḍ`ifû limen âmene minhüm eta`lemûne enne ṣâliḥam mürselüm mir rabbih. ḳâlû innâ bimâ ürsile bihî mü\'minûn.'),
(1030, 7, 76, 'ḳâle-lleẕîne-stekberû innâ billeẕî âmentüm bihî kâfirûn.'),
(1031, 7, 77, 'fe`aḳarü-nnâḳate ve`atev `an emri rabbihim veḳâlû yâ ṣâliḥu-\'tinâ bimâ te`idünâ in künte mine-lmürselîn.'),
(1032, 7, 78, 'feeḫaẕethümü-rracfetü feaṣbeḥû fî dârihim câŝimîn.'),
(1033, 7, 79, 'fetevellâ `anhüm veḳâle yâ ḳavmi leḳad eblagtüküm risâlete rabbî veneṣaḥtü leküm velâkil lâ tüḥibbûne-nnâṣiḥîn.'),
(1034, 7, 80, 'velûṭan iẕ ḳâle liḳavmihî ete\'tûne-lfâḥişete mâ sebeḳaküm bihâ min eḥadim mine-l`âlemîn.'),
(1035, 7, 81, 'inneküm lete\'tûne-rricâle şehvetem min dûni-nnisâ\'. bel entüm ḳavmüm müsrifûn.'),
(1036, 7, 82, 'vemâ kâne cevâbe ḳavmihî illâ en ḳâlû aḫricûhüm min ḳaryetiküm. innehüm ünâsüy yeteṭahherûn.'),
(1037, 7, 83, 'feenceynâhü veehlehû ille-mraeteh. kânet mine-lgâbirîn.'),
(1038, 7, 84, 'veemṭarnâ `aleyhim meṭarâ. fenżur keyfe kâne `âḳibetü-lmücrimîn.'),
(1039, 7, 85, 'veilâ medyene eḫâhüm şu`aybâ. ḳâle yâ ḳavmi-`büdü-llâhe mâ leküm min ilâhin gayruh. ḳad câetküm beyyinetüm mir rabbiküm feevfü-lkeyle velmîzâne velâ tebḫasü-nnâse eşyâehüm velâ tüfsidû fi-l\'arḍi ba`de iṣlâḥihâ. ẕâliküm ḫayrul leküm in küntüm mü\'minîn.'),
(1040, 7, 86, 'velâ taḳ`udû bikülli ṣirâṭin tû`idûne veteṣuddûne `an sebîli-llâhi men âmene bihî vetebgûnehâ `ivecâ. veẕkürû iẕ küntüm ḳalîlen fekeŝŝeraküm. venżurû keyfe kâne `âḳibetü-lmüfsidîn.'),
(1041, 7, 87, 'vein kâne ṭâifetüm minküm âmenû billeẕî ürsiltü bihî veṭâifetül lem yü\'minû faṣbirû ḥattâ yaḥküme-llâhü beynenâ. vehüve ḫayru-lḥâkimîn.'),
(1042, 7, 88, 'ḳâle-lmeleü-lleẕîne-stekberû min ḳavmihî lenuḫricenneke yâ şu`aybü velleẕîne âmenû me`ake min ḳaryetinâ ev lete`ûdünne fî milletinâ. ḳâle evelev künnâ kârihîn.'),
(1043, 7, 89, 'ḳadi-fteraynâ `ale-llâhi keẕiben in `udnâ fî milletiküm ba`de iẕ neccâne-llâhü minhâ. vemâ yekûnü lenâ en ne`ûde fîhâ illâ ey yeşâe-llâhü rabbünâ. vesi`a rabbünâ külle şey\'in `ilmâ. `ale-llâhi tevekkelnâ. rabbene-ftaḥ beynenâ vebeyne ḳavminâ bilḥaḳḳi veente ḫayru-lfâtiḥîn.'),
(1044, 7, 90, 'veḳâle-lmeleü-lleẕîne keferû min ḳavmihî leini-tteba`tüm şu`ayben inneküm iẕel leḫâsirûn.'),
(1045, 7, 91, 'feeḫaẕethümü-rracfetü feaṣbeḥû fî dârihim câŝimîn.'),
(1046, 7, 92, 'elleẕîne keẕẕebû şu`ayben keel lem yagnev fîhâ. elleẕîne keẕẕebû şu`ayben kânû hümü-lḫâsirîn.'),
(1047, 7, 93, 'fetevellâ `anhüm veḳâle yâ ḳavmi leḳad eblagtüküm risâlâti rabbî veneṣaḥtü leküm. fekeyfe âsâ `alâ ḳavmin kâfirîn.'),
(1048, 7, 94, 'vemâ erselnâ fî ḳaryetim min nebiyyin illâ eḫaẕnâ ehlehâ bilbe\'sâi veḍḍarrâi le`allehüm yeḍḍarra`ûn.'),
(1049, 7, 95, 'ŝümme beddelnâ mekâne-sseyyieti-lḥasenete ḥattâ `afev veḳâlû ḳad messe âbâene-ḍḍarrâü vesserrâü feeḫaẕnâhüm bagtetev vehüm lâ yeş`urûn.'),
(1050, 7, 96, 'velev enne ehle-lḳurâ âmenû vetteḳav lefetaḥnâ `aleyhim berakâtim mine-ssemâi vel\'arḍi velâkin keẕẕebû feeḫaẕnâhüm bimâ kânû yeksibûn.'),
(1051, 7, 97, 'efeemine ehlü-lḳurâ ey ye\'tiyehüm be\'sünâ beyâtev vehüm nâimûn.'),
(1052, 7, 98, 'eveemine ehlü-lḳurâ ey ye\'tiyehüm be\'sünâ ḍuḥav vehüm yel`abûn.'),
(1053, 7, 99, 'efeeminû mekra-llâh. felâ ye\'menü mekra-llâhi ille-lḳavmü-lḫâsirûn.'),
(1054, 7, 100, 'evelem yehdi lilleẕîne yeriŝûne-l\'arḍa mim ba`di ehlihâ el lev neşâü eṣabnâhüm biẕünûbihim. venaṭbe`u `alâ ḳulûbihim fehüm lâ yesme`ûn.'),
(1055, 7, 101, 'tilke-lḳurâ neḳuṣṣu `aleyke min embâihâ. veleḳad câethüm rusülühüm bilbeyyinât. femâ kânû liyü\'minû bimâ keẕẕebû min ḳabl. keẕâlike yaṭbe`u-llâhü `alâ ḳulûbi-lkâfirîn.'),
(1056, 7, 102, 'vemâ vecednâ liekŝerihim min `ahd. veiv vecednâ ekŝerahüm lefâsiḳîn.'),
(1057, 7, 103, 'ŝümme be`aŝnâ mim ba`dihim mûsâ biâyâtinâ ilâ fir`avne vemeleihî feżalemû bihâ. fenżur keyfe kâne `âḳibetü-lmüfsidîn.'),
(1058, 7, 104, 'veḳâle mûsâ yâ fir`avnü innî rasûlüm mir rabbi-l`âlemîn.'),
(1059, 7, 105, 'ḥaḳîḳun `alâ el lâ eḳûle `ale-llâhi ille-lḥaḳḳ. ḳad ci\'tüküm bibeyyinetim mir rabbiküm feersil me`iye benî isrâîl.'),
(1060, 7, 106, 'ḳâle in künte ci\'te biâyetin fe\'ti bihâ in künte mine-ṣṣâdiḳîn.'),
(1061, 7, 107, 'feelḳâ `aṣâhü feiẕâ hiye ŝü`bânüm mübîn.'),
(1062, 7, 108, 'veneza`a yedehû feiẕâ hiye beyḍâü linnâżirîn.'),
(1063, 7, 109, 'ḳâle-lmeleü min ḳavmi fir`avne inne hâẕâ lesâḥirun `alîm.'),
(1064, 7, 110, 'yürîdü ey yuḫriceküm min arḍiküm. femâẕâ te\'mürûn.'),
(1065, 7, 111, 'ḳâlû ercih veeḫâhü veersil fi-lmedâini ḥâşirîn.'),
(1066, 7, 112, 'ye\'tûke bikülli sâḥirin `alîm.'),
(1067, 7, 113, 'vecâe-sseḥaratü fir`avne ḳâlû inne lenâ leecran in künnâ naḥnü-lgâlibîn.'),
(1068, 7, 114, 'ḳâle ne`am veinneküm lemine-lmüḳarrabîn.'),
(1069, 7, 115, 'ḳâlû yâ mûsâ immâ en tülḳiye veimmâ en nekûne naḥnü-lmülḳîn.'),
(1070, 7, 116, 'ḳâle elḳû. felemmâ elḳav seḥarû a`yüne-nnâsi vesterhebûhüm vecâû bisiḥrin `ażîm.'),
(1071, 7, 117, 'veevḥaynâ ilâ mûsâ en elḳi `aṣâk. feiẕâ hiye telḳafü mâ ye\'fikûn.'),
(1072, 7, 118, 'feveḳa`a-lḥaḳḳu vebeṭale mâ kânû ya`melûn.'),
(1073, 7, 119, 'fegulibû hünâlike venḳalebû ṣâgirîn.'),
(1074, 7, 120, 'veülḳiye-sseḥaratü sâcidîn.'),
(1075, 7, 121, 'ḳâlû âmennâ birabbi-l`âlemîn.'),
(1076, 7, 122, 'rabbi mûsâ vehârûn.'),
(1077, 7, 123, 'ḳâle fir`avnü âmentüm bihî ḳable en âẕene leküm. inne hâẕâ lemekrum mekertümûhü fi-lmedîneti lituḫricû minhâ ehlehâ. fesevfe ta`lemûn.'),
(1078, 7, 124, 'leüḳaṭṭi`anne eydiyeküm veercüleküm min ḫilâfin ŝümme leüṣallibenneküm ecme`în.'),
(1079, 7, 125, 'ḳâlû innâ ilâ rabbinâ münḳalibûn.'),
(1080, 7, 126, 'vemâ tenḳimü minnâ illâ en âmennâ biâyâti rabbinâ lemmâ câetnâ. rabbenâ efrig `aleynâ ṣabrav veteveffenâ müslimîn.'),
(1081, 7, 127, 'veḳâle-lmeleü min ḳavmi fir`avne eteẕeru mûsâ veḳavmehû liyüfsidû fi-l\'arḍi veyeẕerake veâlihetek. ḳâle senüḳattilü ebnâehüm venestaḥyî nisâehüm. veinnâ fevḳahüm ḳâhirûn.'),
(1082, 7, 128, 'ḳâle mûsâ liḳavmihi-ste`înû billâhi vaṣbirû. inne-l\'arḍa lillâh. yûriŝühâ mey yeşâü min `ibâdih. vel`âḳibetü lilmütteḳîn.'),
(1083, 7, 129, 'ḳâlû ûẕîne min ḳabli en te\'tiyenâ vemim ba`di mâ ci\'tenâ. ḳâle `asâ rabbüküm ey yühlike `adüvveküm veyestaḫlifeküm fi-l\'arḍi feyenżura keyfe ta`melûn.'),
(1084, 7, 130, 'veleḳad eḫaẕnâ âle fir`avne bissinîne venaḳṣim mine-ŝŝemerâti le`allehüm yeẕẕekkerûn.'),
(1085, 7, 131, 'feiẕâ câethümü-lḥasenetü ḳâlû lenâ hâẕih. vein tüṣibhüm seyyietüy yeṭṭayyerû bimûsâ vemem me`ah. elâ innemâ ṭâiruhüm `inde-llâhi velâkinne ekŝerahüm lâ ya`lemûn.'),
(1086, 7, 132, 'veḳâlû mehmâ te\'tinâ bihî min âyetil litesḥaranâ bihâ femâ naḥnü leke bimü\'minîn.'),
(1087, 7, 133, 'feerselnâ `aleyhimu-ṭṭûfâne velcerâde velḳummele veḍḍafâdi`a veddeme âyâtim müfeṣṣalâtin festekberû vekânû ḳavmem mücrimîn.'),
(1088, 7, 134, 'velemmâ veḳa`a `aleyhimü-rriczü ḳâlû yâ mûse-d`u lenâ rabbeke bimâ `ahide `indek. lein keşefte `anne-rricze lenü\'minenne leke velenürsilenne me`ake benî isrâîl.'),
(1089, 7, 135, 'felemmâ keşefnâ `anhümü-rricze ilâ ecelin hüm bâligûhü iẕâ hüm yenküŝûn.'),
(1090, 7, 136, 'fenteḳamnâ minhüm feagraḳnâhüm fi-lyemmi biennehüm keẕẕebû biâyâtinâ vekânû `anhâ gâfilîn.'),
(1091, 7, 137, 'veevraŝne-lḳavme-lleẕîne kânû yüstaḍ`afûne meşâriḳa-l\'arḍi vemegâribehe-lletî bâraknâ fîhâ. vetemmet kelimetü rabbike-lḥusnâ `alâ benî isrâîle bimâ ṣaberû. vedemmernâ mâ kâne yaṣne`u fir`avnü veḳavmühû vemâ kânû ya`rişûn.'),
(1092, 7, 138, 'vecâveznâ bibenî isrâîle-lbaḥra feetev `alâ ḳavmiy ya`küfûne `alâ aṣnâmil lehüm. ḳâlû yâ mûse-c`al lenâ ilâhen kemâ lehüm âliheh. ḳâle inneküm ḳavmün techelûn.'),
(1093, 7, 139, 'inne hâülâi mütebberum mâ hüm fîhi vebâṭilüm mâ kânû ya`melûn.'),
(1094, 7, 140, 'ḳâle egayra-llâhi ebgîküm ilâhev vehüve feḍḍaleküm `ale-l`âlemîn.'),
(1095, 7, 141, 'veiẕ enceynâküm min âli fir`avne yesûmûneküm sûe-l`aẕâb. yüḳattilûne ebnâeküm veyestaḥyûne nisâeküm. vefî ẕâliküm belâüm mir rabbiküm `ażîm.'),
(1096, 7, 142, 'vevâ`adnâ mûsâ ŝelâŝîne leyletev veetmemnâhâ bi`aşrin fetemme mîḳâtü rabbihî erbe`îne leyleh. veḳâle mûsâ lieḫîhi hârûne-ḫlüfnî fî ḳavmî veaṣliḥ velâ tettebi` sebîle-lmüfsidîn.'),
(1097, 7, 143, 'velemmâ câe mûsâ limîḳâtinâ vekellemehû rabbühû ḳâle rabbi erinî enżur ileyk. ḳâle len terânî velâkini-nżur ile-lcebeli feini-steḳarra mekânehû fesevfe terânî. felemmâ tecellâ rabbühû lilcebeli ce`alehû dekkev veḫarra mûsâ ṣa`iḳâ. felemmâ efâḳa ḳâle sübḥâneke tübtü ileyke veenâ evvelü-lmü\'minîn.'),
(1098, 7, 144, 'ḳâle yâ mûsâ inni-ṣṭafeytüke `ale-nnâsi birisâletî vebikelâmî. feḫuẕ mâ âteytüke veküm mine-şşâkirîn.'),
(1099, 7, 145, 'veketebnâ lehû fi-l\'elvâḥi min külli şey\'im mev`iżatev vetefṣîlel likülli şey\'. feḫuẕhâ biḳuvvetiv ve\'mür ḳavmeke ye\'ḫuẕû biaḥsenihâ. seürîküm dâra-lfâsiḳîn.'),
(1100, 7, 146, 'seaṣrifü `an âyâtiye-lleẕîne yetekebberûne fi-l\'arḍi bigayri-lḥaḳḳ. veiy yerav külle âyetil lâ yü\'minû bihâ. veiy yerav sebîle-rruşdi lâ yetteḫiẕûhü sebîlâ. veiy yerav sebîle-lgayyi yetteḫiẕûhü sebîlâ. ẕâlike biennehüm keẕẕebû biâyâtinâ vekânû `anhâ gâfilîn.'),
(1101, 7, 147, 'velleẕîne keẕẕebû biâyâtinâ veliḳâi-l\'âḫirati ḥabiṭat a`mâlühüm. hel yüczevne illâ mâ kânû ya`melûn.'),
(1102, 7, 148, 'vetteḫaẕe ḳavmü mûsâ mim ba`dihî min ḥuliyyihim `iclen cesedel lehû ḫuvâr. elem yerav ennehû lâ yükellimühüm velâ yehdîhim sebîlâ. itteḫaẕûhü vekânû żâlimîn.'),
(1103, 7, 149, 'velemmâ süḳiṭa fî eydîhim veraev ennehüm ḳad ḍallû ḳâlû leil lem yerḥamnâ rabbünâ veyagfir lenâ lenekûnenne mine-lḫâsirîn.'),
(1104, 7, 150, 'velemmâ race`a mûsâ ilâ ḳavmihî gaḍbâne esifen ḳâle bi\'semâ ḫaleftümûnî mim ba`dî. e`aciltüm emra rabbiküm. veelḳa-l\'elvâḥa veeḫaẕe bira\'si eḫîhi yecürruhû ileyh. ḳâle-bne ümme inne-lḳavme-staḍ`afûnî vekâdû yaḳtülûnenî. felâ tüşmit biye-l\'a`dâe velâ tec`alnî me`a-lḳavmi-żżâlimîn.'),
(1105, 7, 151, 'ḳâle rabbi-gfir lî velieḫî veedḫilnâ fî raḥmetik. veente erḥamü-rrâḥimîn.'),
(1106, 7, 152, 'inne-lleẕîne-tteḫaẕü-l`icle seyenâlühüm gaḍabüm mir rabbihim veẕilletün fi-lḥayâti-ddünyâ. vekeẕâlike neczi-lmüfterîn.'),
(1107, 7, 153, 'velleẕîne `amilü-sseyyiâti ŝümme tâbû mim ba`dihâ veâmenû. inne rabbeke mim ba`dihâ legafûrur raḥîm.'),
(1108, 7, 154, 'velemmâ sekete `am mûse-lgaḍabü eḫaẕe-l\'elvâḥ. vefî nüsḫatihâ hüdev veraḥmetül lilleẕîne hüm lirabbihim yerhebûn.'),
(1109, 7, 155, 'vaḫtâra mûsâ ḳavmehû seb`îne racülel limîḳâtinâ. felemmâ eḫaẕethümü-rracfetü ḳâle rabbi lev şi\'te ehlektehüm min ḳablü veiyyây. etühlikünâ bimâ fe`ale-ssüfehâü minnâ. in hiye illâ fitnetük. tüḍillü bihâ men teşâü vetehdî men teşâ\'. ente veliyyünâ fagfir lenâ verḥamnâ veente ḫayru-lgâfirîn.'),
(1110, 7, 156, 'vektüb lenâ fî hâẕihi-ddünyâ ḥasenetev vefi-l\'âḫirati innâ hüdnâ ileyk. ḳâle `aẕâbî üṣîbü bihî men eşâ\'. veraḥmetî vesi`at külle şey\'. feseektübühâ lilleẕîne yetteḳûne veyü\'tûne-zzekâte velleẕîne hüm biâyâtinâ yü\'minûn.'),
(1111, 7, 157, 'elleẕîne yettebi`ûne-rrasûle-nnebiyye-l\'ümmiyye-lleẕî yecidûnehû mektûben `indehüm fi-ttevrâti vel\'incîl. ye\'müruhüm bilma`rûfi veyenhâhüm `ani-lmünkeri veyüḥillü lehümu-ṭṭayyibâti veyüḥarrimü `aleyhimü-lḫabâiŝe veyeḍa`u `anhüm iṣrahüm vel\'aglâle-lletî kânet `aleyhim. felleẕîne âmenû bihî ve`azzerûhü veneṣarûhü vettebe`ü-nnûra-lleẕî ünzile me`ahû ülâike hümü-lmüfliḥûn.'),
(1112, 7, 158, 'ḳul yâ eyyühe-nnâsü innî rasûlü-llâhi ileyküm cemî`an-lleẕî lehû mülkü-ssemâvâti vel\'arḍ. lâ ilâhe illâ hüve yuḥyî veyümît. feâminû billâhi verasûlihi-nnebiyyi-l\'ümmiyyi-lleẕî yü\'minü billâhi vekelimâtihî vettebi`ûhü le`alleküm tehtedûn.'),
(1113, 7, 159, 'vemin ḳavmi mûsâ ümmetüy yehdûne bilḥaḳḳi vebihî ya`dilûn.'),
(1114, 7, 160, 'veḳaṭṭa`nâhümü-ŝnetey `aşrate esbâṭan ümemâ. veevḥaynâ ilâ mûsâ iẕi-stesḳâhü ḳavmüh eni-ḍrib bi`aṣâke-lḥacer. fembeceset minhü-ŝnetâ `aşrate `aynâ. ḳad `alime küllü ünâsim meşrabehüm. veżallelnâ `aleyhimü-lgamâme veenzelnâ `aleyhimü-lmenne vesselvâ. külû min ṭayyibâti mâ razaḳnâküm. vemâ żalemûnâ velâkin kânû enfüsehüm yażlimûn.'),
(1115, 7, 161, 'veiẕ ḳîle lehümü-skünû hâẕihi-lḳaryete vekülû minhâ ḥayŝü şi\'tüm veḳûlû ḥiṭṭatüv vedḫulü-lbâbe sücceden nagfir leküm ḫatiâtiküm. senezîdü-lmuḥsinîn.'),
(1116, 7, 162, 'febeddele-lleẕîne żalemû minhüm ḳavlen gayra-lleẕî ḳîle lehüm feerselnâ `aleyhim riczem mine-ssemâi bimâ kânû yażlimûn.'),
(1117, 7, 163, 'ves\'elhüm `ani-lḳaryeti-lletî kânet ḥâḍirate-lbaḥr. iẕ ya`dûne fi-ssebti iẕ te\'tîhim ḥîtânühüm yevme sebtihim şürra`av veyevme lâ yesbitûne lâ te\'tîhim. keẕâlike neblûhüm bimâ kânû yefsüḳûn.'),
(1118, 7, 164, 'veiẕ ḳâlet ümmetüm minhüm lime te`iżûne ḳavmen-llâhü mühlikühüm ev mü`aẕẕibühüm `aẕâben şedîdâ. ḳâlû ma`ẕiraten ilâ rabbiküm vele`allehüm yetteḳûn.'),
(1119, 7, 165, 'felemmâ nesû mâ ẕükkirû bihî enceyne-lleẕîne yenhevne `ani-ssûi veeḫaẕne-lleẕîne żalemû bi`aẕâbim beîsim bimâ kânû yefsüḳûn.'),
(1120, 7, 166, 'felemmâ `atev `am mâ nühû `anhü ḳulnâ lehüm kûnû ḳiradeten ḫâsiîn.'),
(1121, 7, 167, 'veiẕ teeẕẕene rabbüke leyeb`aŝenne `aleyhim ilâ yevmi-lḳiyâmeti mey yesûmühüm sûe-l`aẕâb. inne rabbeke leserî`u-l`iḳâb. veinnehû legafûrur raḥîm.'),
(1122, 7, 168, 'veḳaṭṭa`nâhüm fi-l\'arḍi ümemâ. minhümu-ṣṣâliḥûne veminhüm dûne ẕâlik. vebelevnâhüm bilḥasenâti vesseyyiâti le`allehüm yerci`ûn.'),
(1123, 7, 169, 'feḫalefe mim ba`dihim ḫalfüv veriŝü-lkitâbe ye\'ḫuẕûne `araḍa hâẕe-l\'ednâ veyeḳûlûne seyugferu lenâ. veiy ye\'tihim `araḍum miŝlühû ye\'ḫuẕûh. elem yü\'ḫaẕ `aleyhim mîŝâḳu-lkitâbi el lâ yeḳûlû `ale-llâhi ille-lḥaḳḳa vederasû mâ fîh. veddâru-l\'âḫiratü ḫayrul lilleẕîne yetteḳûn. efelâ ta`ḳilûn.'),
(1124, 7, 170, 'velleẕîne yümessikûne bilkitâbi veeḳâmu-ṣṣalâh. innâ lâ nüḍî`u ecra-lmuṣliḥîn.'),
(1125, 7, 171, 'veiẕ netaḳne-lcebele fevḳahüm keennehû żulletüv veżannû ennehû vâḳi`um bihim. ḫuẕû mâ âteynâküm biḳuvvetiv veẕkürû mâ fîhi le`alleküm tetteḳûn.'),
(1126, 7, 172, 'veiẕ eḫaẕe rabbüke mim benî âdeme min żuhûrihim ẕürriyyetehüm veeşhedehüm `alâ enfüsihim. elestü birabbiküm. ḳâlû belâ. şehidnâ. en teḳûlû yevme-lḳiyâmeti innâ künnâ `an hâẕâ gâfilîn.'),
(1127, 7, 173, 'ev teḳûlû innemâ eşrake âbâünâ min ḳablü vekünnâ ẕürriyyetem mim ba`dihim. efetühlikünâ bimâ fe`ale-lmübṭilûn.'),
(1128, 7, 174, 'vekeẕâlike nüfeṣṣilü-l\'âyâti vele`allehüm yerci`ûn.'),
(1129, 7, 175, 'vetlü `aleyhim nebee-lleẕî âteynâhü âyâtinâ fenseleḫa minhâ feetbe`ahü-şşeyṭânü fekâne mine-lgâvîn.'),
(1130, 7, 176, 'velev şi\'nâ lerafa`nâhü bihâ velâkinnehû aḫlede ile-l\'arḍi vettebe`a hevâh. femeŝelühû kemeŝeli-lkelb. in taḥmil `aleyhi yelheŝ ev tetrukhü yelheŝ. ẕâlike meŝelü-lḳavmi-lleẕîne keẕẕebû biâyâtinâ. faḳṣuṣi-lḳaṣaṣa le`allehüm yetefekkerûn.'),
(1131, 7, 177, 'sâe meŝelen-lḳavmü-lleẕîne keẕẕebû biâyâtinâ veenfüsehüm kânû yażlimûn.'),
(1132, 7, 178, 'mey yehdi-llâhü fehüve-lmühtedî. vemey yuḍlil feülâike hümü-lḫâsirûn.'),
(1133, 7, 179, 'veleḳad ẕera\'nâ licehenneme keŝîram mine-lcinni vel\'ins. lehüm ḳulûbül lâ yefḳahûne bihâ. velehüm a`yünül lâ yübṣirûne bihâ. velehüm âẕânül lâ yesme`ûne bihâ. ülâike kel\'en`âmi bel hüm eḍall. ülâike hümü-lgâfilûn.'),
(1134, 7, 180, 'velillâhi-l\'esmâü-lḥusnâ fed`ûhü bihâ. veẕerü-lleẕîne yülḥidûne fî esmâih. seyüczevne mâ kânû ya`melûn.'),
(1135, 7, 181, 'vemimmen ḫalaḳnâ ümmetüy yehdûne bilḥaḳḳi vebihî ya`dilûn.'),
(1136, 7, 182, 'velleẕîne keẕẕebû biâyâtinâ senestedricühüm min ḥayŝü lâ ya`lemûn.'),
(1137, 7, 183, 'veümlî lehüm. inne keydî metîn.'),
(1138, 7, 184, 'evelem yetefekkerû mâ biṣâḥibihim min cinneh. in hüve illâ neẕîrum mübîn.'),
(1139, 7, 185, 'evelem yenżurû fî melekûti-ssemâvâti vel\'arḍi vemâ ḫaleḳa-llâhü min şey\'iv veen `asâ ey yekûne ḳadi-ḳterabe ecelühüm. febieyyi ḥadîŝim ba`dehû yü\'minûn.'),
(1140, 7, 186, 'mey yuḍlili-llâhü felâ hâdiye leh. veyeẕeruhüm fî ṭugyânihim ya`mehûn.'),
(1141, 7, 187, 'yes\'elûneke `ani-ssâ`ati eyyâne mürsâhâ. ḳul innemâ `ilmühâ `inde rabbî. lâ yücellîhâ livaḳtihâ illâ hû. ŝeḳulet fi-ssemâvâti vel\'arḍ. lâ te\'tîküm illâ bagteten. yes\'elûneke keenneke ḥafiyyün `anhâ. ḳul innemâ `ilmühâ `inde-llâhi velâkinne ekŝera-nnâsi lâ ya`lemûn.'),
(1142, 7, 188, 'ḳul lâ emlikü linefsî nef`av velâ ḍarran illâ mâ şâe-llâh. velev küntü a`lemü-lgaybe lestekŝertü mine-lḫayr. vemâ messeniye-ssûü in ene illâ neẕîruv vebeşîrul liḳavmiy yü\'minûn.'),
(1143, 7, 189, 'hüve-lleẕî ḫaleḳaküm min nefsiv vâḥidetiv vece`ale minhâ zevcehâ liyesküne ileyhâ. felemmâ tegaşşâhâ ḥamelet ḥamlen ḫafîfen femerrat bih. felemmâ eŝḳalet de`ave-llâhe rabbehümâ lein âteytenâ ṣâliḥal lenekûnenne mine-şşâkirîn.'),
(1144, 7, 190, 'felemmâ âtâhümâ ṣâliḥan ce`alâ lehû şürakâe fîmâ âtâhümâ. fete`âle-llâhü `ammâ yüşrikûn.'),
(1145, 7, 191, 'eyüşrikûne mâ lâ yaḫlüḳu şey\'ev vehüm yuḫleḳûn.'),
(1146, 7, 192, 'velâ yesteṭî`ûne lehüm naṣrav velâ enfüsehüm yenṣurûn.'),
(1147, 7, 193, 'vein ted`ûhüm ile-lhüdâ lâ yettebi`ûküm. sevâün `aleyküm ede`avtümûhüm em entüm ṣâmitûn.'),
(1148, 7, 194, 'inne-lleẕîne ted`ûne min dûni-llâhi `ibâdün emŝâlüküm fed`ûhüm felyestecîbû leküm in küntüm ṣâdiḳîn.'),
(1149, 7, 195, 'elehüm ercülüy yemşûne bihâ. em lehüm eydiy yebṭişûne bihâ. em lehüm a`yünüy yübṣirûne bihâ. em lehüm âẕânüy yesme`ûne bihâ. ḳuli-d`û şürakâeküm ŝümme kîdûni felâ tünżirûn.'),
(1150, 7, 196, 'inne veliyyiye-llâhü-lleẕî nezzele-lkitâb. vehüve yetevelle-ṣṣâliḥîn.'),
(1151, 7, 197, 'velleẕîne ted`ûne min dûnihî lâ yesteṭî`ûne naṣraküm velâ enfüsehüm yenṣurûn.'),
(1152, 7, 198, 'vein ted`ûhüm ile-lhüdâ lâ yesme`û. veterâhüm yenżurûne ileyke vehüm lâ yübṣirûn.'),
(1153, 7, 199, 'ḫuẕi-l`afve ve\'mür bil`urfi vea`riḍ `ani-lcâhilîn.'),
(1154, 7, 200, 'veimmâ yenzeganneke mine-şşeyṭâni nezgun feste`iẕ billâh. innehû semî`un `alîm.'),
(1155, 7, 201, 'inne-lleẕîne-tteḳav iẕâ messehüm ṭâifüm mine-şşeyṭâni teẕekkerû feiẕâ hüm mübṣirûn.'),
(1156, 7, 202, 'veiḫvânühüm yemüddûnehüm fi-lgayyi ŝümme lâ yuḳṣirûn.'),
(1157, 7, 203, 'veiẕâ lem te\'tihim biâyetin ḳâlû levle-ctebeytehâ. ḳul innemâ ettebi`u mâ yûḥâ ileyye mir rabbî. hâẕâ beṣâiru mir rabbiküm vehüdev veraḥmetül liḳavmiy yü\'minûn.'),
(1158, 7, 204, 'veiẕâ ḳurie-lḳur\'ânü festemi`û lehû veenṣitû le`alleküm türḥamûn.'),
(1159, 7, 205, 'veẕkür rabbeke fî nefsike teḍarru`av veḫîfetev vedûne-lcehri mine-lḳavli bilgudüvvi vel\'eṣâli velâ teküm mine-lgâfilîn.'),
(1160, 7, 206, 'inne-lleẕîne `inde rabbike lâ yestekbirûne `an `ibâdetihî veyüsebbiḥûnehû velehû yescüdûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'yes\'elûneke `ani-l\'enfâl. ḳuli-l\'enfâlü lillâhi verrasûl. fetteḳu-llâhe veaṣliḥû ẕâte beyniküm. veeṭî`ü-llâhe verasûlehû in küntüm mü\'minîn.'),
(1162, 8, 2, 'inneme-lmü\'minûne-lleẕîne iẕâ ẕükira-llâhü vecilet ḳulûbühüm veiẕâ tüliyet `aleyhim âyâtühû zâdethüm îmânev ve`alâ rabbihim yetevekkelûn.'),
(1163, 8, 3, 'elleẕîne yüḳîmûne-ṣṣalâte vemimmâ razaḳnâhüm yünfiḳûn.'),
(1164, 8, 4, 'ülâike hümü-lmü\'minûne ḥaḳḳâ. lehüm deracâtün `inde rabbihim vemagfiratüv verizḳun kerîm.'),
(1165, 8, 5, 'kemâ aḫraceke rabbüke mim beytike bilḥaḳḳ. veinne ferîḳam mine-lmü\'minîne lekârihûn.'),
(1166, 8, 6, 'yücâdilûneke fi-lḥaḳḳi ba`de mâ tebeyyene keennemâ yüsâḳûne ile-lmevti vehüm yenżurûn.'),
(1167, 8, 7, 'veiẕ ye`idükümü-llâhü iḥde-ṭṭâifeteyni ennehâ leküm veteveddûne enne gayra ẕâti-şşevketi tekûnü leküm veyürîdü-llâhü ey yüḥiḳḳa-lḥaḳḳa bikelimâtihî veyaḳṭa`a dâbira-lkâfirîn.'),
(1168, 8, 8, 'liyüḥiḳḳa-lḥaḳḳa veyübṭile-lbâṭile velev kerihe-lmücrimûn.'),
(1169, 8, 9, 'iẕ testegîŝûne rabbeküm festecâbe leküm ennî mümiddüküm bielfim mine-lmelâiketi mürdifîn.'),
(1170, 8, 10, 'vemâ ce`alehü-llâhü illâ büşrâ velitaṭmeinne bihî ḳulûbüküm. veme-nnaṣru illâ min `indi-llâh. inne-llâhe `azîzün ḥakîm.'),
(1171, 8, 11, 'iẕ yügaşşîkümü-nnü`âse emenetem minhü veyünezzilü `aleyküm mine-ssemâi mâel liyüṭahhiraküm bihî veyüẕhibe `anküm ricze-şşeyṭâni veliyerbiṭa `alâ ḳulûbiküm veyüŝebbite bihi-l\'aḳdâm.'),
(1172, 8, 12, 'iẕ yûḥî rabbüke ile-lmelâiketi ennî me`aküm feŝebbitü-lleẕîne âmenû. seülḳî fî ḳulûbi-lleẕîne keferü-rru`be faḍribû fevḳa-l\'a`nâḳi vaḍribû minhüm külle benân.'),
(1173, 8, 13, 'ẕâlike biennehüm şâḳḳu-llâhe verasûleh. vemey yüşâḳiḳi-llâhe verasûlehû feinne-llâhe şedîdü-l`iḳâb.'),
(1174, 8, 14, 'ẕâliküm feẕûḳûhü veenne lilkâfirîne `aẕâbe-nnâr.'),
(1175, 8, 15, 'yâ eyyühe-lleẕîne âmenû iẕâ leḳîtümü-lleẕîne keferû zaḥfen felâ tüvellûhümü-l\'edbâr.'),
(1176, 8, 16, 'vemey yüvellihim yevmeiẕin dübürahû illâ müteḥarrifel liḳitâlin ev müteḥayyizen ilâ fietin feḳad bâe bigaḍabim mine-llâhi veme\'vâhü cehennem. vebi\'se-lmeṣîr.'),
(1177, 8, 17, 'felem taḳtülûhüm velâkinne-llâhe ḳatelehüm. vemâ rameyte iẕ rameyte velâkinne-llâhe ramâ. veliyübliye-lmü\'minîne minhü belâen ḥasenâ. inne-llâhe semî`un `alîm.'),
(1178, 8, 18, 'ẕâliküm veenne-llâhe mûhinü keydi-lkâfirîn.'),
(1179, 8, 19, 'in testeftiḥû feḳad câekümü-lfetḥ. vein tentehû fehüve ḫayrul leküm. vein te`ûdû ne`ud. velen tugniye `anküm fietüküm şey\'ev velev keŝürat veenne-llâhe me`a-lmü\'minîn.'),
(1180, 8, 20, 'yâ eyyühe-lleẕîne âmenû eṭî`ü-llâhe verasûlehû velâ tevellev `anhü veentüm tesme`ûn.'),
(1181, 8, 21, 'velâ tekûnû kelleẕîne ḳâlû semi`nâ vehüm lâ yesme`ûn.'),
(1182, 8, 22, 'inne şerra-ddevâbbi `inde-llâhi-ṣṣummü-lbükmü-lleẕîne lâ ya`ḳilûn.'),
(1183, 8, 23, 'velev `alime-llâhü fîhim ḫayral leesme`ahüm. velev esme`ahüm letevellev vehüm mü`riḍûn.'),
(1184, 8, 24, 'yâ eyyühe-lleẕîne âmenü-stecîbû lillâhi velirrasûli iẕâ de`âküm limâ yuḥyîküm. va`lemû enne-llâhe yeḥûlü beyne-lmer\'i veḳalbihî veennehû ileyhi tuḥşerûn.'),
(1185, 8, 25, 'vetteḳû fitnetel lâ tüṣîbenne-lleẕîne żalemû minküm ḫâṣṣah. va`lemû enne-llâhe şedîdü-l`iḳâb.'),
(1186, 8, 26, 'veẕkürû iẕ entüm ḳalîlüm müstaḍ`afûne fi-l\'arḍi teḫâfûne ey yeteḫaṭṭafekümü-nnâsü feâvâküm veeyyedeküm binaṣrihî verazeḳaküm mine-ṭṭayyibâti le`alleküm teşkürûn.'),
(1187, 8, 27, 'yâ eyyühe-lleẕîne âmenû lâ teḫûnü-llâhe verrasûle veteḫûnû emânâtiküm veentüm ta`lemûn.'),
(1188, 8, 28, 'va`lemû ennemâ emvâlüküm veevlâdüküm fitnetüv veenne-llâhe `indehû ecrun `ażîm.'),
(1189, 8, 29, 'yâ eyyühe-lleẕîne âmenû in tetteḳu-llâhe yec`al leküm fürḳânev veyükeffir `anküm seyyiâtiküm veyagfir leküm. vellâhü ẕü-lfaḍli-l`ażîm.'),
(1190, 8, 30, 'veiẕ yemküru bike-lleẕîne keferû liyüŝbitûke ev yaḳtülûke ev yuḫricûk. veyemkürûne veyemküru-llâh. vellâhü ḫayru-lmâkirîn.'),
(1191, 8, 31, 'veiẕâ tütlâ `aleyhim âyâtünâ ḳâlû ḳad semi`nâ lev neşâü leḳulnâ miŝle hâẕâ in hâẕâ illâ esâṭîru-l\'evvelîn.'),
(1192, 8, 32, 'veiẕ ḳâlü-llâhümme in kâne hâẕâ hüve-lḥaḳḳa min `indike feemṭir `aleynâ ḥicâratem mine-ssemâi evi-\'tinâ bi`aẕâbin elîm.'),
(1193, 8, 33, 'vemâ kâne-llâhü liyü`aẕẕibehüm veente fîhim. vemâ kâne-llâhü mü`aẕẕibehüm vehüm yestagfirûn.'),
(1194, 8, 34, 'vemâ lehüm ellâ yü`aẕẕibehümü-llâhü vehüm yeṣuddûne `ani-lmescidi-lḥarâmi vemâ kânû evliyâh. in evliyâühû ille-lmütteḳûne velâkinne ekŝerahüm lâ ya`lemûn.'),
(1195, 8, 35, 'vemâ kâne ṣalâtühüm `inde-lbeyti illâ mükâev vetaṣdiyeh. feẕûḳu-l`aẕâbe bimâ küntüm tekfürûn.'),
(1196, 8, 36, 'inne-lleẕîne keferû yünfiḳûne emvâlehüm liyeṣuddû `an sebîli-llâh. feseyünfiḳûnehâ ŝümme tekûnü `aleyhim ḥasraten ŝümme yuglebûn. velleẕîne keferû ilâ cehenneme yuḥşerûn.'),
(1197, 8, 37, 'liyemîze-llâhü-lḫabîŝe mine-ṭṭayyibi veyec`ale-lḫabîŝe ba`ḍahû `alâ ba`ḍin feyerkümehû cemî`an feyec`alehû fî cehennem. ülâike hümü-lḫâsirûn.'),
(1198, 8, 38, 'ḳul lilleẕîne keferû iy yentehû yugfer lehüm mâ ḳad selef. veiy ye`ûdû feḳad meḍat sünnetü-l\'evvelîn.'),
(1199, 8, 39, 'veḳâtilûhüm ḥattâ lâ tekûne fitnetüv veyekûne-ddînü küllühû lillâh. feini-ntehev feinne-llâhe bimâ ya`melûne beṣîr.'),
(1200, 8, 40, 'vein tevellev fa`lemû enne-llâhe mevlâküm. ni`me-lmevlâ veni`me-nneṣîr.'),
(1201, 8, 41, 'va`lemû ennemâ ganimtüm min şey\'in feenne lillâhi ḫumüsehû velirrasûli veliẕi-lḳurbâ velyetâmâ velmesâkîni vebni-ssebîli in küntüm âmentüm billâhi vemâ enzelnâ `alâ `abdinâ yevme-lfürḳâni yevme-lteḳe-lcem`ân. vellâhü `alâ külli şey\'in ḳadîr.'),
(1202, 8, 42, 'iẕ entüm bil`udveti-ddünyâ vehüm bil`udveti-lḳuṣvâ verrakbü esfele minküm. velev tevâ`attüm laḫteleftüm fi-lmî`âdi velâkil liyaḳdiye-llâhü emran kâne mef`ûlel liyehlike men heleke `am beyyinetiv veyaḥyâ men ḥayye `am beyyineh. veinne-llâhe lesemî`un `alîm.'),
(1203, 8, 43, 'iẕ yürîkehümü-llâhü fî menâmike ḳalîlâ. velev erâkehüm keŝîral lefeşiltüm veletenâza`tüm fi-l\'emri velâkinne-llâhe sellem. innehû `alîmüm biẕâti-ṣṣudûr.'),
(1204, 8, 44, 'veiẕ yürîkümûhüm iẕi-tteḳaytüm fî a`yüniküm ḳalîlev veyüḳallilüküm fî a`yünihim liyaḳdiye-llâhü emran kâne mef`ûlâ. veile-llâhi türce`u-l\'ümûr.'),
(1205, 8, 45, 'yâ eyyühe-lleẕîne âmenû iẕâ leḳîtüm fieten feŝbütû veẕkürü-llâhe keŝîral le`alleküm tüfliḥûn.'),
(1206, 8, 46, 'veeṭî`ü-llâhe verasûlehû velâ tenâza`û fetefşelû veteẕhebe rîḥuküm vaṣbirû. inne-llâhe me`a-ṣṣâbirîn.'),
(1207, 8, 47, 'velâ tekûnû kelleẕîne ḫaracû min diyârihim beṭarav veriâe-nnâsi veyeṣuddûne `an sebîli-llâh. vellâhü bimâ ya`melûne müḥîṭ.'),
(1208, 8, 48, 'veiẕ zeyyene lehümü-şşeyṭânü a`mâlehüm veḳâle lâ gâlibe lekümü-lyevme mine-nnâsi veinnî cârul leküm. felemmâ terâeti-lfietâni nekeṣa `alâ `aḳibeyhi veḳâle innî berîüm minküm innî erâ mâ lâ teravne innî eḫâfü-llâh. vellâhü şedîdü-l`iḳâb.'),
(1209, 8, 49, 'iẕ yeḳûlü-lmünâfiḳûne velleẕîne fî ḳulûbihim meraḍun garra hâülâi dînühüm. vemey yetevekkel `ale-llâhi feinne-llâhe `azîzün ḥakîm.'),
(1210, 8, 50, 'velev terâ iẕ yeteveffe-lleẕîne keferü-lmelâiketü yaḍribûne vucûhehüm veedbârahüm. veẕûḳû `aẕâbe-lḥarîḳ.'),
(1211, 8, 51, 'ẕâlike bimâ ḳaddemet eydîküm veenne-llâhe leyse biżallâmil lil`abîd.'),
(1212, 8, 52, 'kede\'bi âli fir`avne velleẕîne min ḳablihim. keferû biâyâti-llâhi feeḫaẕehümü-llâhü biẕünûbihim. inne-llâhe ḳaviyyün şedîdü-l`iḳâb.'),
(1213, 8, 53, 'ẕâlike bienne-llâhe lem yekü mügayyiran ni`meten en`amehâ `alâ ḳavmin ḥattâ yügayyirû mâ bienfüsihim veenne-llâhe semî`un `alîm.'),
(1214, 8, 54, 'kede\'bi âli fir`avne velleẕîne min ḳablihim. keẕẕebû biâyâti rabbihim. feehleknâhüm biẕünûbihim veagraḳnâ âle fir`avn. veküllün kânû żâlimîn.'),
(1215, 8, 55, 'inne şerra-ddevâbbi `inde-llâhi-lleẕîne keferû fehüm lâ yü\'minûn.'),
(1216, 8, 56, 'elleẕîne `âhette minhüm ŝümme yenḳuḍûne `ahdehüm fî külli merrativ vehüm lâ yetteḳûn.'),
(1217, 8, 57, 'feimmâ teŝḳafennehüm fi-lḥarbi feşerrid bihim men ḫalfehüm le`allehüm yeẕẕekkerûn.'),
(1218, 8, 58, 'veimmâ teḫâfenne min ḳavmin ḫiyâneten fembiẕ ileyhim `alâ sevâ\'. inne-llâhe lâ yüḥibbü-lḫâinîn.'),
(1219, 8, 59, 'velâ yaḥsebenne-lleẕîne keferû sebeḳû. innehüm lâ yü`cizûn.'),
(1220, 8, 60, 'vee`iddû lehüm me-steṭa`tüm min ḳuvvetiv vemir ribâṭi-lḫayli türhibûne bihî `adüvve-llâhi ve`adüvveküm veâḫarîne min dûnihim. lâ ta`lemûnehüm. allâhü ya`lemühüm. vemâ tünfiḳû min şey\'in fî sebîli-llâhi yüveffe ileyküm veentüm lâ tużlemûn.'),
(1221, 8, 61, 'vein ceneḥû lisselmi fecnaḥ lehâ vetevekkel `ale-llâh. innehû hüve-ssemî`u-l`alîm.'),
(1222, 8, 62, 'veiy yürîdû ey yaḫde`ûke feinne ḥasbeke-llâh. hüve-lleẕî eyyedeke binaṣrihî vebilmü\'minîn.'),
(1223, 8, 63, 'veellefe beyne ḳulûbihim. lev enfaḳte mâ fi-l\'arḍi cemî`am mâ ellefte beyne ḳulûbihim velâkinne-llâhe ellefe beynehüm. innehû `azîzün ḥakîm.'),
(1224, 8, 64, 'yâ eyyühe-nnebiyyü ḥasbüke-llâhü vemeni-ttebe`ake mine-lmü\'minîn.'),
(1225, 8, 65, 'yâ eyyühe-nnebiyyü ḥarriḍi-lmü\'minîne `ale-lḳitâl. iy yeküm minküm `işrûne ṣâbirûne yaglibû mieteyn. veiy yeküm minküm mietüy yaglibû elfem mine-lleẕîne keferû biennehüm ḳavmül lâ yefḳahûn.'),
(1226, 8, 66, 'el\'âne ḫaffefe-llâhü `anküm ve`alime enne fîküm ḍa`fâ. feiy yeküm minküm mietün ṣâbiratüy yaglibû mieteyn. veiy yeküm minküm elfüy yaglibû elfeyni biiẕni-llâh. vellâhü me`a-ṣṣâbirîn.'),
(1227, 8, 67, 'mâ kâne linebiyyin ey yekûne lehû esrâ ḥattâ yüŝḫine fi-l\'arḍ. türîdûne `araḍa-ddünyâ. vellâhü yürîdü-l\'âḫirah. vellâhü `azîzün ḥakîm.'),
(1228, 8, 68, 'levlâ kitâbüm mine-llâhi sebeḳa lemesseküm fîmâ eḫaẕtüm `aẕâbün `ażîm.'),
(1229, 8, 69, 'fekülû mimmâ ganimtüm ḥalâlen ṭayyibâ. vetteḳu-llâh. inne-llâhe gafûrur raḥîm.'),
(1230, 8, 70, 'yâ eyyühe-nnebiyyü ḳul limen fî eydîküm mine-l\'esrâ iy ya`lemi-llâhü fî ḳulûbiküm ḫayray yü\'tiküm ḫayram mimmâ üḫiẕe minküm veyagfir leküm. vellâhü gafûrur raḥîm.'),
(1231, 8, 71, 'veiy yürîdû ḫiyâneteke feḳad ḫânü-llâhe min ḳablü feemkene minhüm. vellâhü `alîmün ḥakîm.'),
(1232, 8, 72, 'inne-lleẕîne âmenû vehâcerû vecâhedû biemvâlihim veenfüsihim fî sebîli-llâhi velleẕîne evev veneṣarû ülâike ba`ḍuhüm evliyâü ba`ḍ. velleẕîne âmenû velem yühâcirû mâ leküm miv velâyetihim min şey\'in ḥattâ yühâcirû. veini-stenṣarûküm fi-ddîni fe`aleykümü-nnaṣru illâ `alâ ḳavmim beyneküm vebeynehüm mîŝâḳun. vellâhü bimâ ta`melûne beṣîr.'),
(1233, 8, 73, 'velleẕîne keferû ba`ḍuhüm evliyâü ba`ḍ. illâ tef`alûhü tekün fitnetün fi-l\'arḍi vefesâdün kebîr.'),
(1234, 8, 74, 'velleẕîne âmenû vehâcerû vecâhedû fî sebîli-llâhi velleẕîne evev veneṣarû ülâike hümü-lmü\'minûne ḥaḳḳâ. lehüm magfiratüv verizḳun kerîm.'),
(1235, 8, 75, 'velleẕîne âmenû mim ba`dü vehâcerû vecâhedû me`aküm feülâike minküm. veülü-l\'erḥâmi ba`ḍuhüm evlâ biba`ḍin fî kitâbi-llâh. inne-llâhe bikülli şey\'in `alîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'berâetüm mine-llâhi verasûlihî ile-lleẕîne `âhettüm mine-lmüşrikîn.'),
(1237, 9, 2, 'fesîḥû fi-l\'arḍi erbe`ate eşhüriv va`lemû enneküm gayru mü`cizi-llâhi veenne-llâhe muḫzi-lkâfirîn.'),
(1238, 9, 3, 'veeẕânüm mine-llâhi verasûlihî ile-nnâsi yevme-lḥacci-l\'ekberi enne-llâhe berîüm mine-lmüşrikîne verasûlüh. fein tübtüm fehüve ḫayrul leküm. vein tevelleytüm fa`lemû enneküm gayru mü`cizi-llâh. vebeşşiri-lleẕîne keferû bi`aẕâbin elîm.'),
(1239, 9, 4, 'ille-lleẕîne `âhettüm mine-lmüşrikîne ŝümme lem yenḳuṣûküm şey\'ev velem yüżahirû `aleyküm eḥaden feetimmû ileyhim `ahdehüm ilâ müddetihim. inne-llâhe yüḥibbü-lmütteḳîn.'),
(1240, 9, 5, 'feiẕe-nseleḫa-l\'eşhüru-lḥurumü faḳtülü-lmüşrikîne ḥayŝü vecettümûhüm veḫuẕûhüm vaḥṣurûhüm vaḳ`udû lehüm külle merṣad. fein tâbû veeḳâmu-ṣṣalâte veâtevu-zzekâte feḫallû sebîlehüm. inne-llâhe gafûrur raḥîm.'),
(1241, 9, 6, 'vein eḥadüm mine-lmüşrikîne-stecârake feecirhü ḥattâ yesme`a kelâme-llâhi ŝümme eblighü me\'meneh. ẕâlike biennehüm ḳavmül lâ ya`lemûn.'),
(1242, 9, 7, 'keyfe yekûnü lilmüşrikîne `ahdün `inde-llâhi ve`inde rasûlihî ille-lleẕîne `âhettüm `inde-lmescidi-lḥarâm. feme-steḳâmû leküm festeḳîmû lehüm. inne-llâhe yüḥibbü-lmütteḳîn.'),
(1243, 9, 8, 'keyfe veiy yażherû `aleyküm lâ yerḳubû fîküm illev velâ ẕimmeh. yürḍûneküm biefvâhihim vete\'bâ ḳulûbühüm. veekŝeruhüm fâsiḳûn.'),
(1244, 9, 9, 'işterav biâyâti-llâhi ŝemenen ḳalîlen feṣaddû `an sebîlih. innehüm sâe mâ kânû ya`melûn.'),
(1245, 9, 10, 'lâ yerḳubûne fî mü\'minin illev velâ ẕimmeh. veülâike hümü-lmü`tedûn.'),
(1246, 9, 11, 'fein tâbû veeḳâmu-ṣṣalâte veâtevu-zzekâte feiḫvânüküm fi-ddîn. venüfeṣṣilü-l\'âyâti liḳavmiy ya`lemûn.'),
(1247, 9, 12, 'vein nekeŝû eymânehüm mim ba`di `ahdihim veṭa`anû fî dîniküm feḳâtilû eimmete-lküfri innehüm lâ eymâne lehüm le`allehüm yentehûn.'),
(1248, 9, 13, 'elâ tüḳâtilûne ḳavmen nekeŝû eymânehüm vehemmû biiḫrâci-rrasûli vehüm bedeûküm evvele merrah. etaḫşevnehüm. fellâhü eḥaḳḳu en taḫşevhü in küntüm mü\'minîn.'),
(1249, 9, 14, 'ḳâtilûhüm yü`aẕẕibhümü-llâhü bieydîküm veyuḫzihim veyenṣurküm `aleyhim veyeşfi ṣudûra ḳavmim mü\'minîn.'),
(1250, 9, 15, 'veyüẕhib gayża ḳulûbihim. veyetûbü-llâhü `alâ mey yeşâ\'. vellâhü `alîmün ḥakîm.'),
(1251, 9, 16, 'em ḥasibtüm en tütrakû velemmâ ya`lemi-llâhü-lleẕîne câhedû minküm velem yetteḫiẕû min dûni-llâhi velâ rasûlihî vele-lmü\'minîne velîceh. vellâhü ḫabîrum bimâ ta`melûn.'),
(1252, 9, 17, 'mâ kâne lilmüşrikîne ey ya`mürû mesâcide-llâhi şâhidîne `alâ enfüsihim bilküfr. ülâike ḥabiṭat a`mâlühüm. vefi-nnâri hüm ḫâlidûn.'),
(1253, 9, 18, 'innemâ ya`müru mesâcide-llâhi men âmene billâhi velyevmi-l\'âḫiri veeḳâme-ṣṣalâte veâte-zzekâte velem yaḫşe ille-llâhe fe`asâ ülâike ey yekûnû mine-lmühtedîn.'),
(1254, 9, 19, 'ece`altüm siḳâyete-lḥâcci ve`imârate-lmescidi-lḥarâmi kemen âmene billâhi velyevmi-l\'âḫiri vecâhede fî sebîli-llâh. lâ yestevûne `inde-llâh. vellâhü lâ yehdi-lḳavme-żżâlimîn.'),
(1255, 9, 20, 'elleẕîne âmenû vehâcerû vecâhedû fî sebîli-llâhi biemvâlihim veenfüsihim a`żamü deraceten `inde-llâh. veülâike hümü-lfâizûn.'),
(1256, 9, 21, 'yübeşşiruhüm rabbühüm biraḥmetim minhü veriḍvâniv vecennâtil lehüm fîhâ ne`îmüm müḳîm.'),
(1257, 9, 22, 'ḫâlidîne fîhâ ebedâ. inne-llâhe `indehû ecrun `ażîm.'),
(1258, 9, 23, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕû âbâeküm veiḫvâneküm evliyâe ini-steḥabbü-lküfra `ale-l\'îmân. vemey yetevellehüm minküm feülâike hümu-żżâlimûn.'),
(1259, 9, 24, 'ḳul in kâne âbâüküm veebnâüküm veiḫvânüküm veezvâcüküm ve`aşîratüküm veemvâlün-ḳteraftümûhâ veticâratün taḫşevne kesâdehâ vemesâkinü terḍavnehâ eḥabbe ileyküm mine-llâhi verasûlihî vecihâdin fî sebîlihî feterabbeṣû ḥattâ ye\'tiye-llâhü biemrih. vellâhü lâ yehdi-lḳavme-lfâsiḳîn.'),
(1260, 9, 25, 'leḳad neṣarakümü-llâhü fî mevâṭine keŝîrativ veyevme ḥuneynin iẕ a`cebetküm keŝratüküm felem tugni `anküm şey\'ev veḍâḳat `aleykümü-l\'arḍu bimâ raḥubet ŝümme velleytüm müdbirîn.'),
(1261, 9, 26, 'ŝümme enzele-llâhü sekînetehû `alâ rasûlihî ve`ale-lmü\'minîne veenzele cünûdel lem teravhâ ve`aẕẕebe-lleẕîne keferû. veẕâlike cezâü-lkâfirîn.'),
(1262, 9, 27, 'ŝümme yetûbü-llâhü mim ba`di ẕâlike `alâ mey yeşâ\'. vellâhü gafûrur raḥîm.'),
(1263, 9, 28, 'yâ eyyühe-lleẕîne âmenû inneme-lmüşrikûne necesün felâ yaḳrabü-lmescide-lḥarâme ba`de `âmihim hâẕâ. vein ḫiftüm `ayleten fesevfe yugnîkümü-llâhü min faḍlihî in şâ\'. inne-llâhe `alîmün ḥakîm.'),
(1264, 9, 29, 'ḳâtilü-lleẕîne lâ yü\'minûne billâhi velâ bilyevmi-l\'âḫiri velâ yüḥarrimûne mâ ḥarrame-llâhü verasûlühû velâ yedînûne dîne-lḥaḳḳi mine-lleẕîne ûtü-lkitâbe ḥattâ yü`ṭü-lcizyete `ay yediv vehüm ṣâgirûn.'),
(1265, 9, 30, 'veḳâleti-lyehûdü `uzeyrun-bnü-llâhi veḳâleti-nneṣâra-lmesîḥu-bnü-llâh. ẕâlike ḳavlühüm biefvâhihim. yüḍâhiûne ḳavle-lleẕîne keferû min ḳabl. ḳâtelehümü-llâh. ennâ yü\'fekûn.'),
(1266, 9, 31, 'itteḫaẕû aḥbârahüm veruhbânehüm erbâbem min dûni-llâhi velmesîḥa-bne meryem. vemâ ümirû illâ liya`büdû ilâhev vâḥidâ. lâ ilâhe illâ hû. sübḥânehû `ammâ yüşrikûn.'),
(1267, 9, 32, 'yürîdûne ey yuṭfiû nûra-llâhi biefvâhihim veye\'be-llâhü illâ ey yütimme nûrahû velev kerihe-lkâfirûn.'),
(1268, 9, 33, 'hüve-lleẕî ersele rasûlehû bilhüdâ vedîni-lḥaḳḳi liyużhirahû `ale-ddîni küllihî velev kerihe-lmüşrikûn.'),
(1269, 9, 34, 'yâ eyyühe-lleẕîne âmenû inne keŝîram mine-l\'aḥbâri verruhbâni leye\'külûne emvâle-nnâsi bilbâṭili veyeṣuddûne `an sebîli-llâh. velleẕîne yeknizûne-ẕẕehebe velfiḍḍate velâ yünfiḳûnehâ fî sebîli-llâhi febeşşirhüm bi`aẕâbin elîm.'),
(1270, 9, 35, 'yevme yuḥmâ `aleyhâ fî nâri cehenneme fetükvâ bihâ cibâhühüm vecünûbühüm veżuhûruhüm. hâẕâ mâ keneztüm lienfüsiküm feẕûḳû mâ küntüm teknizûn.'),
(1271, 9, 36, 'inne `iddete-şşühûri `inde-llâhi-ŝnâ `aşera şehran fî kitâbi-llâhi yevme ḫaleḳa-ssemâvâti vel\'arḍa minhâ erbe`atün ḥurum. ẕâlike-ddînü-lḳayyimü felâ tażlimû fîhinne enfüseküm veḳâtilü-lmüşrikîne kâffeten kemâ yüḳâtilûneküm kâffeh. va`lemû enne-llâhe me`a-lmütteḳîn.'),
(1272, 9, 37, 'inneme-nnesîü ziyâdetün fi-lküfri yüḍallü bihi-lleẕîne keferû yüḥillûnehû `âmev veyüḥarrimûnehû `âmel liyüvâṭiû `iddete mâ ḥarrame-llâhü feyüḥillû mâ ḥarrame-llâh. züyyine lehüm sûü a`mâlihim. vellâhü lâ yehdi-lḳavme-lkâfirîn.'),
(1273, 9, 38, 'yâ eyyühe-lleẕîne âmenû mâ leküm iẕâ ḳîle lekümü-nfirû fî sebîli-llâhi-ŝŝâḳaltüm ile-l\'arḍ. eraḍîtüm bilḥayâti-ddünyâ mine-l\'âḫirah. femâ metâ`u-lḥayâti-ddünyâ fi-l\'âḫirati illâ ḳalîl.'),
(1274, 9, 39, 'illâ tenfirû yü`aẕẕibküm `aẕâben elîmev veyestebdil ḳavmen gayraküm velâ teḍurrûhü şey\'â. vellâhü `alâ külli şey\'in ḳadîr.'),
(1275, 9, 40, 'illâ tenṣurûhü feḳad neṣarahü-llâhü iẕ aḫracehü-lleẕîne keferû ŝâniye-ŝneyni iẕ hümâ fi-lgâri iẕ yeḳûlü liṣâḥibihî lâ taḥzen inne-llâhe me`anâ. feenzele-llâhü sekînetehû `aleyhi veeyyedehû bicünûdil lem teravhâ vece`ale kelimete-lleẕîne keferü-ssüflâ. vekelimetü-llâhi hiye-l`ulyâ. vellâhü `azîzün ḥakîm.'),
(1276, 9, 41, 'infirû ḫifâfev veŝiḳâlev vecâhidû biemvâliküm veenfüsiküm fî sebîli-llâh. ẕâliküm ḫayrul leküm in küntüm ta`lemûn.'),
(1277, 9, 42, 'lev kâne `araḍan ḳarîbev veseferan ḳâṣidel lettebe`ûke velâkim be`udet `aleyhimü-şşüḳḳah. veseyaḥlifûne billâhi levi-steṭa`nâ leḫaracnâ me`aküm. yühlikûne enfüsehüm. vellâhü ya`lemü innehüm lekâẕibûn.'),
(1278, 9, 43, '`afe-llâhü `ank. lime eẕinte lehüm ḥattâ yetebeyyene leke-lleẕîne ṣadeḳû veta`leme-lkâẕibîn.'),
(1279, 9, 44, 'lâ yeste\'ẕinüke-lleẕîne yü\'minûne billâhi velyevmi-l\'âḫiri ey yücâhidû biemvâlihim veenfüsihim. vellâhü `alîmüm bilmütteḳîn.'),
(1280, 9, 45, 'innemâ yeste\'ẕinüke-lleẕîne lâ yü\'minûne billâhi velyevmi-l\'âḫiri vertâbet ḳulûbühüm fehüm fî raybihim yeteraddedûn.'),
(1281, 9, 46, 'velev erâdü-lḫurûce lee`addû lehû `uddetev velâkin kerihe-llâhü-mbi`âŝehüm feŝebbeṭahüm veḳîle-ḳ`udû me`a-lḳâ`idîn.'),
(1282, 9, 47, 'lev ḫaracû fîküm mâ zâdûküm illâ ḫabâlev veleevḍa`û ḫilâleküm yebgûnekümü-lfitneh. vefîküm semmâ`ûne lehüm. vellâhü `alîmüm biżżâlimîn.'),
(1283, 9, 48, 'leḳadi-btegavu-lfitnete min ḳablü veḳallebû leke-l\'ümûra ḥattâ câe-lḥaḳḳu veżahera emru-llâhi vehüm kârihûn.'),
(1284, 9, 49, 'veminhüm mey yeḳûlü-\'ẕel lî velâ teftinnî. elâ fi-lfitneti seḳaṭû. veinne cehenneme lemüḥîṭatüm bilkâfirîn.'),
(1285, 9, 50, 'in tüṣibke ḥasenetün tesü\'hüm. vein tüṣibke müṣîbetüy yeḳûlû ḳad eḫaẕnâ emranâ min ḳablü veyetevellev vehüm feriḥûn.'),
(1286, 9, 51, 'ḳul ley yüṣîbenâ illâ mâ ketebe-llâhü lenâ. hüve mevlânâ. ve`ale-llâhi felyetevekkeli-lmü\'minûn.'),
(1287, 9, 52, 'ḳul hel terabbeṣûne binâ illâ iḥde-lḥusneyeyn. venaḥnü neterabbeṣu biküm ey yüṣîbekümü-llâhü bi`aẕâbim min `indihî ev bieydînâ. feterabbeṣû innâ me`aküm müterabbiṣûn.'),
(1288, 9, 53, 'ḳul enfiḳû ṭav`an ev kerhel ley yüteḳabbele minküm. inneküm küntüm ḳavmen fâsiḳîn.'),
(1289, 9, 54, 'vemâ mene`ahüm en tuḳbele minhüm nefeḳâtühüm illâ ennehüm keferû billâhi vebirasûlihî velâ ye\'tûne-ṣṣalâte illâ vehüm küsâlâ velâ yünfiḳûne illâ vehüm kârihûn.'),
(1290, 9, 55, 'felâ tü`cibke emvâlühüm velâ evlâdühüm. innemâ yürîdü-llâhü liyü`aẕẕibehüm bihâ fi-lḥayâti-ddünyâ vetezheḳa enfüsühüm vehüm kâfirûn.'),
(1291, 9, 56, 'veyaḥlifûne billâhi innehüm leminküm. vemâ hüm minküm velâkinnehüm ḳavmüy yefraḳûn.'),
(1292, 9, 57, 'lev yecidûne melceen ev megârâtin ev müddeḫalel levellev ileyhi vehüm yecmeḥûn.'),
(1293, 9, 58, 'veminhüm mey yelmizüke fi-ṣṣadeḳât. fein ü`ṭû minhâ raḍû veil lem yü`ṭav minhâ iẕâ hüm yesḫaṭûn.'),
(1294, 9, 59, 'velev ennehüm raḍû mâ âtâhümü-llâhü verasûlühû veḳâlû ḥasbüne-llâhü seyü\'tîne-llâhü min faḍlihî verasûlühû innâ ile-llâhi râgibûn.'),
(1295, 9, 60, 'inneme-ṣṣadeḳâtü lilfüḳarâi velmesâkîni vel`âmilîne `aleyhâ velmüellefeti ḳulûbühüm vefi-rriḳâbi velgârimîne vefî sebîli-llâhi vebni-ssebîl. ferîḍatem mine-llâh. vellâhü `alîmün ḥakîm.'),
(1296, 9, 61, 'veminhümü-lleẕîne yü\'ẕûne-nnebiyye veyeḳûlûne hüve üẕün. ḳul üẕünü ḫayril leküm yü\'minü billâhi veyü\'minü lilmü\'minîne veraḥmetül lilleẕîne âmenû minküm. velleẕîne yü\'ẕûne rasûle-llâhi lehüm `aẕâbün elîm.'),
(1297, 9, 62, 'yaḥlifûne billâhi leküm liyürḍûküm. vellâhü verasûlühû eḥaḳḳu ey yürḍûhü in kânû mü\'minîn.'),
(1298, 9, 63, 'elem ya`lemû ennehû mey yüḥâdidi-llâhe verasûlehû feenne lehû nâra cehenneme ḫâliden fîhâ. ẕâlike-lḫizyü-l`ażîm.'),
(1299, 9, 64, 'yaḥẕeru-lmünâfiḳûne en tünezzele `aleyhim sûratün tünebbiühüm bimâ fî ḳulûbihim. ḳuli-stehziû. inne-llâhe muḫricüm mâ taḥẕerûn.'),
(1300, 9, 65, 'velein seeltehüm leyeḳûlünne innemâ künnâ neḫûḍu venel`ab. ḳul ebillâhi veâyâtihî verasûlihî küntüm testehziûn.'),
(1301, 9, 66, 'lâ ta`teẕirû ḳad kefertüm ba`de îmâniküm. in na`fü `an ṭâifetim minküm nü`aẕẕib ṭâifetem biennehüm kânû mücrimîn.'),
(1302, 9, 67, 'elmünâfiḳûne velmünâfiḳâtü ba`ḍuhüm mim ba`ḍ. ye\'mürûne bilmünkeri veyenhevne `ani-lma`rûfi veyaḳbiḍûne eydiyehüm. nesü-llâhe fenesiyehüm. inne-lmünâfiḳîne hümü-lfâsiḳûn.'),
(1303, 9, 68, 've`ade-llâhü-lmünâfiḳîne velmünâfiḳâti velküffâra nâra cehenneme ḫâlidîne fîhâ. hiye ḥasbühüm. vele`anehümü-llâh. velehüm `aẕâbüm müḳîm.'),
(1304, 9, 69, 'kelleẕîne min ḳabliküm kânû eşedde minküm ḳuvvetev veekŝera emvâlev veevlâdâ. festemte`û biḫalâḳihim festemta`tüm biḫalâḳiküm keme-stemte`a-lleẕîne min ḳabliküm biḫalâḳihim veḫuḍtüm kelleẕî ḫâḍû. ülâike ḥabiṭat a`mâlühüm fi-ddünyâ vel\'âḫirah. veülâike hümü-lḫâsirûn.'),
(1305, 9, 70, 'elem ye\'tihim nebeü-lleẕîne min ḳablihim ḳavmi nûḥiv ve`âdiv veŝemûde veḳavmi ibrâhîme veaṣḥâbi medyene velmü\'tefikât. etethüm rusülühüm bilbeyyinât. femâ kâne-llâhü liyażlimehüm velâkin kânû enfüsehüm yażlimûn.'),
(1306, 9, 71, 'velmü\'minûne velmü\'minâtü ba`ḍuhüm evliyâü ba`ḍ. ye\'mürûne bilma`rûfi veyenhevne `ani-lmünkeri veyüḳîmûne-ṣṣalâte veyü\'tûne-zzekâte veyüṭî`ûne-llâhe verasûleh. ülâike seyerḥamühümü-llâh. inne-llâhe `azîzün ḥakîm.'),
(1307, 9, 72, 've`ade-llâhü-lmü\'minîne velmü\'minâti cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ vemesâkine ṭayyibeten fî cennâti `adn. veriḍvânüm mine-llâhi ekber. ẕâlike hüve-lfevzü-l`ażîm.'),
(1308, 9, 73, 'yâ eyyühe-nnebiyyü câhidi-lküffâra velmünâfiḳîne vagluż `aleyhim. veme\'vâhüm cehennem. vebi\'se-lmeṣîr.'),
(1309, 9, 74, 'yaḥlifûne billâhi mâ ḳâlû. veleḳad ḳâlû kelimete-lküfri vekeferû ba`de islâmihim vehemmû bimâ lem yenâlû. vemâ neḳamû illâ en agnâhümü-llâhü verasûlühû min faḍlih. feiy yetûbû yekü ḫayral lehüm. veiy yetevellev yü`aẕẕibhümü-llâhü `aẕâben elîmen fi-ddünyâ vel\'âḫirah. vemâ lehüm fi-l\'arḍi miv veliyyiv velâ neṣîr.'),
(1310, 9, 75, 'veminhüm men `âhede-llâhe lein âtânâ min faḍlihî leneṣṣaddeḳanne velenekûnenne mine-ṣṣâliḥîn.'),
(1311, 9, 76, 'felemmâ âtâhüm min faḍlihî beḫilû bihî vetevellev vehüm mü`riḍûn.'),
(1312, 9, 77, 'fea`ḳabehüm nifâḳan fî ḳulûbihim ilâ yevmi yelḳavnehû bimâ aḫlefü-llâhe mâ ve`adûhü vebimâ kânû yekẕibûn.'),
(1313, 9, 78, 'elem ya`lemû enne-llâhe ya`lemü sirrahüm venecvâhüm veenne-llâhe `allâmü-lguyûb.'),
(1314, 9, 79, 'elleẕîne yelmizûne-lmüṭṭavvi`îne mine-lmü\'minîne fi-ṣṣadeḳâti velleẕîne lâ yecidûne illâ cühdehüm feyesḫarûne minhüm. seḫira-llâhü minhüm. velehüm `aẕâbün elîm.'),
(1315, 9, 80, 'istagfir lehüm ev lâ testagfir lehüm. in testagfir lehüm seb`îne merraten feley yagfira-llâhü lehüm. ẕâlike biennehüm keferû billâhi verasûlih. vellâhü lâ yehdi-lḳavme-lfâsiḳîn.'),
(1316, 9, 81, 'feriḥa-lmüḫallefûne bimaḳ`adihim ḫilâfe rasûli-llâhi vekerihû ey yücâhidû biemvâlihim veenfüsihim fî sebîli-llâhi veḳâlû lâ tenfirû fi-lḥarr. ḳul nâru cehenneme eşeddü ḥarrâ. lev kânû yefḳahûn.'),
(1317, 9, 82, 'felyaḍḥakû ḳalîlev velyebkû keŝîrâ. cezâem bimâ kânû yeksibûn.'),
(1318, 9, 83, 'feir race`ake-llâhü ilâ ṭâifetim minhüm feste\'ẕenûke lilḫurûci feḳul len taḫrucû me`iye ebedev velen tüḳâtilû me`iye `adüvvâ. inneküm raḍîtüm bilḳu`ûdi evvele merratin faḳ`udû me`a-lḫâlifîn.'),
(1319, 9, 84, 'velâ tüṣalli `alâ eḥadim minhüm mâte ebedev velâ teḳum `alâ ḳabrih. innehüm keferû billâhi verasûlihî vemâtû vehüm fâsiḳûn.'),
(1320, 9, 85, 'velâ tü`cibke emvâlühüm veevlâdühüm. innemâ yürîdü-llâhü ey yü`aẕẕibehüm bihâ fi-ddünyâ vetezheḳa enfüsühüm vehüm kâfirûn.'),
(1321, 9, 86, 'veiẕâ ünzilet sûratün en âminû billâhi vecâhidû me`a rasûlihi-ste\'ẕeneke ülu-ṭṭavli minhüm veḳâlû ẕernâ neküm me`a-lḳâ`idîn.'),
(1322, 9, 87, 'raḍû biey yekûnû me`a-lḫavâlifi veṭubi`a `alâ ḳulûbihim fehüm lâ yefḳahûn.'),
(1323, 9, 88, 'lâkini-rrasûlü velleẕîne âmenû me`ahû câhedû biemvâlihim veenfüsihim. veülâike lehümü-lḫayrât. veülâike hümü-lmüfliḥûn.'),
(1324, 9, 89, 'e`adde-llâhü lehüm cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. ẕâlike-lfevzü-l`ażîm.'),
(1325, 9, 90, 'vecâe-lmü`aẕẕirûne mine-l\'a`râbi liyü\'ẕene lehüm veḳa`ade-lleẕîne keẕebü-llâhe verasûleh. seyüṣîbü-lleẕîne keferû minhüm `aẕâbün elîm.'),
(1326, 9, 91, 'leyse `ale-ḍḍu`afâi velâ `ale-lmerḍâ velâ `ale-lleẕîne lâ yecidûne mâ yünfiḳûne ḥaracün iẕâ neṣaḥû lillâhi verasûlih. mâ `ale-lmuḥsinîne min sebîlin. vellâhü gafûrur raḥîm.'),
(1327, 9, 92, 'velâ `ale-lleẕîne iẕâ mâ etevke litaḥmilehüm ḳulte lâ ecidü mâ aḥmilüküm `aleyh. tevellev vea`yünühüm tefîḍu mine-ddem`i ḥazenen ellâ yecidû mâ yünfiḳûn.'),
(1328, 9, 93, 'inneme-ssebîlü `ale-lleẕîne yeste\'ẕinûneke vehüm agniyâ\'. raḍû biey yekûnû me`a-lḫavâlifi veṭabe`a-llâhü `alâ ḳulûbihim fehüm lâ ya`lemûn.'),
(1329, 9, 94, 'ya`teẕirûne ileyküm iẕâ raca`tüm ileyhim. ḳul lâ ta`teẕirû len nü\'mine leküm ḳad nebbeene-llâhü min aḫbâriküm. veseyere-llâhü `ameleküm verasûlühû ŝümme türaddûne ilâ `âlimi-lgaybi veşşehâdeti feyünebbiüküm bimâ küntüm ta`melûn.'),
(1330, 9, 95, 'seyaḥlifûne billâhi leküm iẕe-nḳalebtüm ileyhim litü`riḍû `anhüm. fea`riḍû `anhüm. innehüm rics. veme\'vâhüm cehennem. cezâem bimâ kânû yeksibûn.'),
(1331, 9, 96, 'yaḥlifûne leküm literḍav `anhüm. fein terḍav `anhüm feinne-llâhe lâ yerḍâ `ani-lḳavmi-lfâsiḳîn.'),
(1332, 9, 97, 'el\'a`râbü eşeddü küfrav venifâḳav veecderu ellâ ya`lemû ḥudûde mâ enzele-llâhü `alâ rasûlih. vellâhü `alîmün ḥakîm.'),
(1333, 9, 98, 'vemine-l\'a`râbi mey yetteḫiẕü mâ yünfiḳu magramev veyeterabbeṣu bikümü-ddevâir. `aleyhim dâiratü-ssev\'. vellâhü semî`un `alîm.'),
(1334, 9, 99, 'vemine-l\'a`râbi mey yü\'minü billâhi velyevmi-l\'âḫiri veyetteḫiẕü mâ yünfiḳu ḳurubâtin `inde-llâhi veṣalevâti-rrasûl. elâ innehâ ḳurbetül lehüm. seyüdḫilühümü-llâhü fî raḥmetih. inne-llâhe gafûrur raḥîm.'),
(1335, 9, 100, 'vessâbiḳûne-l\'evvelûne mine-lmühâcirîne vel\'enṣâri velleẕîne-ttebe`ûhüm biiḥsânir raḍiye-llâhü `anhüm veraḍû `anhü vee`adde lehüm cennâtin tecrî taḥtehe-l\'enhâru ḫâlidîne fîhâ ebedâ. ẕâlike-lfevzü-l`ażîm.'),
(1336, 9, 101, 'vemimmen ḥavleküm mine-l\'a`râbi münâfiḳûn. vemin ehli-lmedîneti meradû `ale-nnifâḳi lâ ta`lemühüm. naḥnü na`lemühüm. senü`aẕẕibühüm merrateyni ŝümme yüraddûne ilâ `aẕâbin `ażîm.'),
(1337, 9, 102, 'veâḫarûne-`terafû biẕünûbihim ḫaleṭû `amelen ṣâliḥav veâḫara seyyiâ. `asa-llâhü ey yetûbe `aleyhim. inne-llâhe gafûrur raḥîm.'),
(1338, 9, 103, 'ḫuẕ min emvâlihim ṣadeḳaten tüṭahhiruhüm vetüzekkîhim bihâ veṣalli `aleyhim. inne ṣalâteke sekenül lehüm. vellâhü semî`un `alîm.'),
(1339, 9, 104, 'elem ya`lemû enne-llâhe hüve yaḳbelü-ttevbete `an `ibâdihî veye\'ḫuẕu-ṣṣadeḳâti veenne-llâhe hüve-ttevvâbü-rraḥîm.'),
(1340, 9, 105, 'veḳuli-`melû feseyere-llâhü `ameleküm verasûlühû velmü\'minûn. vesetüraddûne ilâ `âlimi-lgaybi veşşehâdeti feyünebbiüküm bimâ küntüm ta`melûn.'),
(1341, 9, 106, 'veâḫarûne mürcevne liemri-llâhi immâ yü`aẕẕibühüm veimmâ yetûbü `aleyhim. vellâhü `alîmün ḥakîm.'),
(1342, 9, 107, 'velleẕîne-tteḫaẕû mesciden ḍirârav veküfrav vetefrîḳam beyne-lmü\'minîne veirṣâdel limen ḥârabe-llâhe verasûlehû min ḳabl. veleyaḥlifünne in eradnâ ille-lḥusnâ. vellâhü yeşhedü innehüm lekâẕibûn.'),
(1343, 9, 108, 'lâ teḳum fîhi ebedâ. lemescidün üssise `ale-ttaḳvâ min evveli yevmin eḥaḳḳu en teḳûme fîh. fîhi ricâlüy yüḥibbûne ey yeteṭahherû. vellâhü yüḥibbü-lmüṭṭahhirîn.'),
(1344, 9, 109, 'efemen essese bünyânehû `alâ taḳvâ mine-llâhi veriḍvânin ḫayrun em men essese bünyânehû `alâ şefâ cürufin hârin fenhâra bihî fî nâri cehennem. vellâhü lâ yehdi-lḳavme-żżâlimîn.'),
(1345, 9, 110, 'lâ yezâlü bünyânühümü-lleẕî benev rîbeten fî ḳulûbihim illâ en teḳaṭṭa`a ḳulûbühüm. vellâhü `alîmün ḥakîm.'),
(1346, 9, 111, 'inne-llâhe-şterâ mine-lmü\'minîne enfüsehüm veemvâlehüm bienne lehümü-lcenneh. yüḳâtilûne fî sebîli-llâhi feyaḳtülûne veyuḳtelûne va`den `aleyhi ḥaḳḳan fi-ttevrâti vel\'incîli velḳur\'ân. vemen evfâ bi`ahdihî mine-llâhi festebşirû bibey`ikümü-lleẕî bâya`tüm bih. veẕâlike hüve-lfevzü-l`ażîm.'),
(1347, 9, 112, 'ettâibûne-l`âbidûne-lḥâmidûne-ssâiḥûne-rrâki`ûne-ssâcidûne-l\'âmirûne bilma`rûfi vennâhûne `ani-lmünkeri velḥâfiżûne liḥudûdi-llâh. vebeşşiri-lmü\'minîn.'),
(1348, 9, 113, 'mâ kâne linnebiyyi velleẕîne âmenû ey yestagfirû lilmüşrikîne velev kânû ülî ḳurbâ mim ba`di mâ tebeyyene lehüm ennehüm aṣḥâbü-lceḥîm.'),
(1349, 9, 114, 'vemâ kâne-stigfâru ibrâhîme liebîhi illâ `am mev`idetiv ve`adehâ iyyâh. felemmâ tebeyyene lehû ennehû `adüvvül lillâhi teberrae minh. inne ibrâhîme leevvâhün ḥalîm.'),
(1350, 9, 115, 'vemâ kâne-llâhü liyüḍille ḳavmem ba`de iẕ hedâhüm ḥattâ yübeyyine lehüm mâ yetteḳûn. inne-llâhe bikülli şey\'in `alîm.'),
(1351, 9, 116, 'inne-llâhe lehû mülkü-ssemâvâti vel\'arḍ. yuḥyî veyümît. vemâ leküm min dûni-llâhi miv veliyyiv velâ neṣîr.'),
(1352, 9, 117, 'leḳat tâbe-llâhü `ale-nnebiyyi velmühâcirîne vel\'enṣâri-lleẕîne-ttebe`ûhü fî sâ`ati-l`usrati mim ba`di mâ kâde yezîgu ḳulûbü ferîḳim minhüm ŝümme tâbe `aleyhim. innehû bihim raûfür raḥîm.'),
(1353, 9, 118, 've`ale-ŝŝelâŝeti-lleẕîne ḫullifû. ḥattâ iẕâ ḍâḳat `aleyhimü-l\'arḍu bimâ raḥubet veḍâḳat `aleyhim enfüsühüm veżannû el lâ melcee mine-llâhi illâ ileyh. ŝümme tâbe `aleyhim liyetûbû. inne-llâhe hüve-ttevvâbü-rraḥîm.'),
(1354, 9, 119, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe vekûnû me`a-ṣṣâdiḳîn.'),
(1355, 9, 120, 'mâ kâne liehli-lmedîneti vemen ḥavlehüm mine-l\'a`râbi ey yeteḫallefû `ar rasûli-llâhi velâ yergabû bienfüsihim `an nefsih. ẕâlike biennehüm lâ yüṣîbühüm żameüv velâ neṣabüv velâ maḫmeṣatün fî sebîli-llâhi velâ yeṭaûne mevṭiey yegîżu-lküffâra velâ yenâlûne min `adüvvin neylen illâ kütibe lehüm bihî `amelün ṣâliḥ. inne-llâhe lâ yüḍî`u ecra-lmuḥsinîn.'),
(1356, 9, 121, 'velâ yünfiḳûne nefeḳaten ṣagîratev velâ kebîratev velâ yaḳṭa`ûne vâdiyen illâ kütibe lehüm liyecziyehümü-llâhü aḥsene mâ kânû ya`melûn.'),
(1357, 9, 122, 'vemâ kâne-lmü\'minûne liyenfirû kâffeh. felevlâ nefera min külli firḳatim minhüm ṭâifetül liyetefeḳḳahû fi-ddîni veliyünẕirû ḳavmehüm iẕâ race`û ileyhim le`allehüm yaḥẕerûn.'),
(1358, 9, 123, 'yâ eyyühe-lleẕîne âmenû ḳâtilü-lleẕîne yelûneküm mine-lküffâri velyecidû fîküm gilżah. va`lemû enne-llâhe me`a-lmütteḳîn.'),
(1359, 9, 124, 'veiẕâ mâ ünzilet sûratün feminhüm mey yeḳûlü eyyüküm zâdethü hâẕihî îmânâ. feemme-lleẕîne âmenû fezâdethüm îmânev vehüm yestebşirûn.'),
(1360, 9, 125, 'veemme-lleẕîne fi ḳulûbihim meraḍun fezâdethüm ricsen ilâ ricsihim vemâtû vehüm kâfirûn.'),
(1361, 9, 126, 'evelâ yeravne ennehüm yüftenûne fî külli `âmim merraten ev merrateyni ŝümme lâ yetûbûne velâ hüm yeẕẕekkerûn.'),
(1362, 9, 127, 'veiẕâ mâ ünzilet sûratün neżara ba`ḍuhüm ilâ ba`ḍ. hel yerâküm min eḥadin ŝümme-nṣarafû. ṣarafe-llâhü ḳulûbehüm biennehüm ḳavmül lâ yefḳahûn.'),
(1363, 9, 128, 'leḳad câeküm rasûlüm min enfüsiküm `azîz. `aleyhi mâ `anittüm ḥarîṣun `aleyküm bilmü\'minîne raûfür raḥîm.'),
(1364, 9, 129, 'fein tevellev feḳul ḥasbiye-llâh. lâ ilâhe illâ hû. `aleyhi tevekkeltü vehüve rabbü-l`arşi-l`ażîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'elif-lâm-râ. tilke âyâtü-lkitâbi-lḥakîm.'),
(1366, 10, 2, 'ekâne linnâsi `aceben en evḥaynâ ilâ racülim minhüm en enẕiri-nnâse vebeşşiri-lleẕîne âmenû enne lehüm ḳademe ṣidḳin `inde rabbihim. ḳâle-lkâfirûne inne hâẕâ lesâḥirum mübîn.'),
(1367, 10, 3, 'inne rabbekümü-llâhü-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa fî sitteti eyyâmin ŝümme-stevâ `ale-l`arşi yüdebbiru-l\'emr. mâ min şefî`in illâ mim ba`di iẕnih. ẕâlikümü-llâhü rabbüküm fa`büdûh. efelâ teẕekkerûn.'),
(1368, 10, 4, 'ileyhi merci`uküm cemî`â. va`de-llâhi ḥaḳḳâ. innehû yebdeü-lḫalḳa ŝümme yü`îdühû liyecziye-lleẕîne âmenû ve`amilu-ṣṣâliḥâti bilḳisṭ. velleẕîne keferû lehüm şerâbüm min ḥamîmiv ve`aẕâbün elîmüm bimâ kânû yekfürûn.'),
(1369, 10, 5, 'hüve-lleẕî ce`ale-şşemse ḍiyâev velḳamera nûrav veḳadderahû menâzile lita`lemû `adede-ssinîne velḥisâb. mâ ḫaleḳa-llâhü ẕâlike illâ bilḥaḳḳ. yüfeṣṣilü-l\'âyâti liḳavmiy ya`lemûn.'),
(1370, 10, 6, 'inne fi-ḫtilâfi-lleyli vennehâri vemâ ḫaleḳa-llâhü fi-ssemâvâti vel\'arḍi leâyâtil liḳavmiy yetteḳûn.'),
(1371, 10, 7, 'inne-lleẕîne lâ yercûne liḳâenâ veraḍû bilḥayâti-ddünyâ vaṭmeennû bihâ velleẕîne hüm `an âyâtinâ gâfilûn.'),
(1372, 10, 8, 'ülâike me\'vâhümü-nnâru bimâ kânû yeksibûn.'),
(1373, 10, 9, 'inne-lleẕîne âmenû ve`amilu-ṣṣâliḥâti yehdîhim rabbühüm biîmânihim. tecrî min taḥtihimü-l\'enhâru fî cennâti-nne`îm.'),
(1374, 10, 10, 'da`vâhüm fîhâ sübḥâneke-llâhümme veteḥiyyetühüm fîhâ selâm. veâḫiru da`vâhüm eni-lḥamdü lillâhi rabbi-l`âlemîn.'),
(1375, 10, 11, 'velev yü`accilü-llâhü linnâsi-şşerra-sti`câlehüm bilḫayri leḳuḍiye ileyhim ecelühüm. feneẕeru-lleẕîne lâ yercûne liḳâenâ fî ṭugyânihim ya`mehûn.'),
(1376, 10, 12, 'veiẕâ messe-l\'insâne-ḍḍurru de`ânâ licembihî ev ḳâ`iden ev ḳâimen. felemmâ keşefnâ `anhü ḍurrahû merra keel lem yed`unâ ilâ ḍurrim messeh. keẕâlike züyyine lilmüsrifîne mâ kânû ya`melûn.'),
(1377, 10, 13, 'veleḳad ehlekne-lḳurûne min ḳabliküm lemmâ żalemû vecâethüm rusülühüm bilbeyyinâti vemâ kânû liyü\'minû. keẕâlike neczi-lḳavme-lmücrimîn.'),
(1378, 10, 14, 'ŝümme ce`alnâküm ḫalâife fi-l\'arḍi mim ba`dihim linenżura keyfe ta`melûn.'),
(1379, 10, 15, 'veiẕâ tütlâ `aleyhim âyâtünâ beyyinâtin ḳâle-lleẕîne lâ yercûne liḳâene-\'ti biḳur\'ânin gayri hâẕâ ev beddilh. ḳul mâ yekûnü lî en übeddilehû min tilḳâi nefsî. in ettebi`u illâ mâ yûḥâ ileyy. innî eḫâfü in `aṣaytü rabbî `aẕâbe yevmin `ażîm.'),
(1380, 10, 16, 'ḳul lev şâe-llâhü mâ televtühû `aleyküm velâ edrâküm bih. feḳad lebiŝtü fîküm `umüram min ḳablih. efelâ ta`ḳilûn.'),
(1381, 10, 17, 'femen ażlemü mimmeni-fterâ `ale-llâhi keẕiben ev keẕẕebe biâyâtih. innehû lâ yüfliḥu-lmücrimûn.'),
(1382, 10, 18, 'veya`büdûne min dûni-llâhi mâ lâ yeḍurruhüm velâ yenfe`uhüm veyeḳûlûne hâülâi şüfe`âünâ `inde-llâh. ḳul etünebbiûne-llâhe bimâ lâ ya`lemü fi-ssemâvâti velâ fi-l\'arḍ. sübḥânehû vete`âlâ `ammâ yüşrikûn.'),
(1383, 10, 19, 'vemâ kâne-nnâsü illâ ümmetev vâḥideten faḫtelefû. velevlâ kelimetün sebeḳat mir rabbike leḳuḍiye beynehüm fîmâ fîhi yaḫtelifûn.'),
(1384, 10, 20, 'veyeḳûlûne levlâ ünzile `aleyhi âyetüm mir rabbih. feḳul inneme-lgaybü lillâhi fenteżirû. innî me`aküm mine-lmünteżirîn.'),
(1385, 10, 21, 'veiẕâ eẕaḳne-nnâse raḥmetem mim ba`di ḍarrâe messethüm iẕâ lehüm mekrun fî âyâtinâ. ḳuli-llâhü esra`u mekrâ. inne rusülenâ yektübûne mâ temkürûn.'),
(1386, 10, 22, 'hüve-lleẕî yüseyyiruküm fi-lberri velbaḥr. ḥattâ iẕâ küntüm fi-lfülk. vecerayne bihim birîḥin ṭayyibetiv veferiḥû bihâ câethâ rîḥun `âṣifüv vecâehümü-lmevcü min külli mekâniv veżannû ennehüm üḥîṭa bihim de`avu-llâhe muḫliṣîne lehü-ddîn. lein enceytenâ min hâẕihî lenekûnenne mine-şşâkirîn.'),
(1387, 10, 23, 'felemmâ encâhüm iẕâ hüm yebgûne fi-l\'arḍi bigayri-lḥaḳḳ. yâ eyyühe-nnâsü innemâ bagyüküm `alâ enfüsiküm metâ`a-lḥayâti-ddünyâ ŝümme ileynâ merci`uküm fenünebbiüküm bimâ küntüm ta`melûn.'),
(1388, 10, 24, 'innemâ meŝelü-lḥayâti-ddünyâ kemâin enzelnâhü mine-ssemâi faḫteleṭa bihî nebâtü-l\'arḍi mimmâ ye\'külü-nnâsü vel\'en`âm. ḥattâ iẕâ eḫaẕeti-l\'arḍu zuḫrufehâ vezzeyyenet veżanne ehlühâ ennehüm ḳâdirûne `aleyhâ etâhâ emrunâ leylen ev nehâran fece`alnâhâ ḥaṣîden keel lem tagne bil\'ems. keẕâlike nüfeṣṣilü-l\'âyâti liḳavmiy yetefekkerûn.'),
(1389, 10, 25, 'vellâhü yed`û ilâ dâri-sselâm. veyehdî mey yeşâü ilâ ṣirâṭim müsteḳîm.'),
(1390, 10, 26, 'lilleẕîne aḥsenü-lḥusnâ veziyâdeh. velâ yerheḳu vucûhehüm ḳateruv velâ ẕilleh. ülâike aṣḥâbü-lcenneh. hüm fîhâ ḫâlidûn.'),
(1391, 10, 27, 'velleẕîne kesebü-sseyyiâti cezâü seyyietim bimiŝlihâ veterheḳuhüm ẕilleh. mâ lehüm mine-llâhi min `âṣim. keennemâ ugşiyet vucûhühüm ḳiṭa`am mine-lleyli mużlimâ. ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(1392, 10, 28, 'veyevme naḥşüruhüm cemî`an ŝümme neḳûlü lilleẕîne eşrakû mekâneküm entüm veşürakâüküm. fezeyyelnâ beynehüm veḳâle şürakâühüm mâ küntüm iyyânâ ta`büdûn.'),
(1393, 10, 29, 'fekefâ billâhi şehîdem beynenâ vebeyneküm in künnâ `an `ibâdetiküm legâfilîn.'),
(1394, 10, 30, 'hünâlike teblû küllü nefsim mâ eslefet veruddû ile-llâhi mevlâhümü-lḥaḳḳi veḍalle `anhüm mâ kânû yefterûn.'),
(1395, 10, 31, 'ḳul mey yerzüḳuküm mine-ssemâi vel\'arḍi emmey yemlikü-ssem`a vel\'ebṣâra vemey yuḫricü-lḥayye mine-lmeyyiti veyuḫricü-lmeyyite mine-lḥayyi vemey yüdebbiru-l\'emr. feseyeḳûlûne-llâh. feḳul efelâ tetteḳûn.'),
(1396, 10, 32, 'feẕâlikümü-llâhü rabbükümü-lḥaḳḳ. femâẕâ ba`de-lḥaḳḳi ille-ḍḍalâl. feennâ tuṣrafûn.'),
(1397, 10, 33, 'keẕâlike ḥaḳḳat kelimetü rabbike `ale-lleẕîne feseḳû ennehüm lâ yü\'minûn.'),
(1398, 10, 34, 'ḳul hel min şürakâiküm mey yebdeü-lḫalḳa ŝümme yü`îdüh. ḳuli-llâhü yebdeü-lḫalḳa ŝümme yü`îdühû feennâ tü\'fekûn.'),
(1399, 10, 35, 'ḳul hel min şürakâiküm mey yehdî ile-lḥaḳḳ. ḳuli-llâhü yehdî lilḥaḳḳ. efemey yehdî ile-lḥaḳḳi eḥaḳḳu ey yüttebe`a emmel lâ yehiddî illâ ey yühdâ. femâ leküm. keyfe taḥkümûn.'),
(1400, 10, 36, 'vemâ yettebi`u ekŝeruhüm illâ żannâ. inne-żżanne lâ yugnî mine-lḥaḳḳi şey\'â. inne-llâhe `alîmüm bimâ yef`alûn.'),
(1401, 10, 37, 'vemâ kâne hâẕe-lḳur\'ânü ey yüfterâ min dûni-llâhi velâkin taṣdîḳa-lleẕî beyne yedeyhi vetefṣîle-lkitâbi lâ raybe fîhi mir rabbi-l`âlemîn.'),
(1402, 10, 38, 'em yeḳûlûne-fterâh. ḳul fe\'tû bisûratim miŝlihî ved`û meni-steṭa`tüm min dûni-llâhi in küntüm ṣâdiḳîn.'),
(1403, 10, 39, 'bel keẕẕebû bimâ lem yüḥîṭû bi`ilmihî velemmâ ye\'tihim te\'vîlüh. keẕâlike keẕẕebe-lleẕîne min ḳablihim fenżur keyfe kâne `âḳibetu-żżâlimîn.'),
(1404, 10, 40, 'veminhüm mey yü\'minü bihî veminhüm mel lâ yü\'minü bih. verabbüke a`lemü bilmüfsidîn.'),
(1405, 10, 41, 'vein keẕẕebûke feḳul lî `amelî veleküm `amelüküm. entüm berîûne mimmâ a`melü veenâ berîüm mimmâ ta`melûn.'),
(1406, 10, 42, 'veminhüm mey yestemi`ûne ileyk. efeente tüsmi`u-ṣṣumme velev kânû lâ ya`ḳilûn.'),
(1407, 10, 43, 'veminhüm mey yenżuru ileyk. efeente tehdi-l`umye velev kânû lâ yübṣirûn.'),
(1408, 10, 44, 'inne-llâhe lâ yażlimü-nnâse şey\'ev velâkinne-nnâse enfüsehüm yażlimûn.'),
(1409, 10, 45, 'veyevme yaḥşüruhüm keel lem yelbeŝû illâ sâ`atem mine-nnehâri yete`ârafûne beynehüm. ḳad ḫasira-lleẕîne keẕẕebû biliḳâi-llâhi vemâ kânû mühtedîn.'),
(1410, 10, 46, 'veimmâ nüriyenneke ba`ḍa-lleẕî ne`idühüm ev neteveffeyenneke feileynâ merci`uhüm ŝümme-llâhü şehîdün `alâ mâ yef`alûn.'),
(1411, 10, 47, 'velikülli ümmetir rasûl. feiẕâ câe rasûlühüm ḳuḍiye beynehüm bilḳisṭi vehüm lâ yużlemûn.'),
(1412, 10, 48, 'veyeḳûlûne metâ hâẕe-lva`dü in küntüm ṣâdiḳîn.'),
(1413, 10, 49, 'ḳul lâ emlikü linefsî ḍarrav velâ nef`an illâ mâ şâe-llâh. likülli ümmetin ecelün. iẕâ câe ecelühüm felâ yeste\'ḫirûne sâ`atev velâ yestaḳdimûn.'),
(1414, 10, 50, 'ḳul era\'eytüm in etâküm `aẕâbühû beyâten ev nehâram mâẕâ yesta`cilü minhü-lmücrimûn.'),
(1415, 10, 51, 'eŝümme iẕâ mâ veḳa`a âmentüm bih. âl\'âne veḳad küntüm bihî testa`cilûn.'),
(1416, 10, 52, 'ŝümme ḳîle lilleẕîne żalemû ẕûḳû `aẕâbe-lḫuld. hel tüczevne illâ bimâ küntüm teksibûn.'),
(1417, 10, 53, 'veyestembiûneke eḥaḳḳun hû. ḳul î verabbî innehû leḥaḳḳuv vemâ entüm bimü`cizîn.'),
(1418, 10, 54, 'velev enne likülli nefsin żalemet mâ fi-l\'arḍi leftedet bih. veeserrü-nnedâmete lemmâ raevu-l`aẕâb. veḳuḍiye beynehüm bilḳisṭi vehüm lâ yużlemûn.'),
(1419, 10, 55, 'elâ inne lillâhi mâ fi-ssemâvâti vel\'arḍ. elâ inne va`de-llâhi ḥaḳḳuv velâkinne ekŝerahüm lâ ya`lemûn.'),
(1420, 10, 56, 'hüve yuḥyî veyümîtü veileyhi türce`ûn.'),
(1421, 10, 57, 'yâ eyyühe-nnâsü ḳad câetküm mev`iżatüm mir rabbiküm veşifâül limâ fi-ṣṣudûri vehüdev veraḥmetül lilmü\'minîn.'),
(1422, 10, 58, 'ḳul bifaḍli-llâhi vebiraḥmetihî febiẕâlike felyefraḥû. hüve ḫayrum mimmâ yecme`ûn.'),
(1423, 10, 59, 'ḳul era\'eytüm mâ enzele-llâhü leküm mir rizḳin fece`altüm minhü ḥarâmev veḥalâlâ. ḳul ellâhü eẕine leküm em `ale-llâhi tefterûn.'),
(1424, 10, 60, 'vemâ żannü-lleẕîne yefterûne `ale-llâhi-lkeẕibe yevme-lḳiyâmeh. inne-llâhe leẕû faḍlin `ale-nnâsi velâkinne ekŝerahüm lâ yeşkürûn.'),
(1425, 10, 61, 'vemâ tekûnü fî şe\'niv vemâ tetlû minhü min ḳur\'âniv velâ ta`melûne min `amelin illâ künnâ `aleyküm şühûden iẕ tüfîḍûne fîh. vemâ ya`zübü `ar rabbike mim miŝḳâli ẕerratin fi-l\'arḍi velâ fi-ssemâi velâ aṣgara min ẕâlike velâ ekbera illâ fî kitâbim mübîn.'),
(1426, 10, 62, 'elâ inne evliyâe-llâhi lâ ḫavfün `aleyhim velâ hüm yaḥzenûn.'),
(1427, 10, 63, 'elleẕîne âmenû vekânû yetteḳûn.'),
(1428, 10, 64, 'lehümü-lbüşrâ fi-lḥayâti-ddünyâ vefi-l\'âḫirah. lâ tebdîle likelimâti-llâh. ẕâlike hüve-lfevzü-l`ażîm.'),
(1429, 10, 65, 'velâ yaḥzünke ḳavlühüm. inne-l`izzete lillâhi cemî`â. hüve-ssemî`u-l`alîm.'),
(1430, 10, 66, 'elâ inne lillâhi men fi-ssemâvâti vemen fi-l\'arḍ. vemâ yettebi`u-lleẕîne yed`ûne min dûni-llâhi şürakâ\'. iy yettebi`ûne ille-żżanne vein hüm illâ yaḫruṣûn.'),
(1431, 10, 67, 'hüve-lleẕî ce`ale lekümü-lleyle liteskünû fîhi vennehâra mübṣirâ. inne fî ẕâlike leâyâtil liḳavmiy yesme`ûn.'),
(1432, 10, 68, 'ḳâlü-tteḫaẕe-llâhü veleden sübḥâneh. hüve-lganiyy. lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. in `indeküm min sülṭânim bihâẕâ. eteḳûlûne `ale-llâhi mâ lâ ta`lemûn.'),
(1433, 10, 69, 'ḳul inne-lleẕîne yefterûne `ale-llâhi-lkeẕibe lâ yüfliḥûn.'),
(1434, 10, 70, 'metâ`un fi-ddünyâ ŝümme ileynâ merci`uhüm ŝümme nüẕîḳuhümü-l`aẕâbe-şşedîde bimâ kânû yekfürûn.'),
(1435, 10, 71, 'vetlü `aleyhim nebee nûḥ. iẕ ḳâle liḳavmihî yâ ḳavmi in kâne kebüra `aleyküm meḳâmî veteẕkîrî biâyâti-llâhi fe`ale-llâhi tevekkeltü feecmi`û emraküm veşürakâeküm ŝümme lâ yekün emruküm `aleyküm gummeten ŝümme-ḳḍû ileyye velâ tünżirûn.'),
(1436, 10, 72, 'fein tevelleytüm femâ seeltüküm min ecr. in ecriye illâ `ale-llâhi veümirtü en ekûne mine-lmüslimîn.'),
(1437, 10, 73, 'fekeẕẕebûhü fenecceynâhü vemem me`ahû fi-lfülki vece`alnâhüm ḫalâife veagraḳne-lleẕîne keẕẕebû biâyâtinâ. fenżur keyfe kâne `âḳibetü-lmünẕerîn.'),
(1438, 10, 74, 'ŝümme be`aŝnâ mim ba`dihî rusülen ilâ ḳavmihim fecâûhüm bilbeyyinâti femâ kânû liyü\'minû bimâ keẕẕebû bihî min ḳabl. keẕâlike naṭbe`u `alâ ḳulûbi-lmü`tedîn.'),
(1439, 10, 75, 'ŝümme be`aŝnâ mim ba`dihim mûsâ vehârûne ilâ fir`avne vemeleihî biâyâtinâ festekberû vekânû ḳavmem mücrimîn.'),
(1440, 10, 76, 'felemmâ câehümü-lḥaḳḳu min `indinâ ḳâlû inne hâẕâ lesiḥrum mübîn.'),
(1441, 10, 77, 'ḳâle mûsâ eteḳûlûne lilḥaḳḳi lemmâ câeküm. esiḥrun hâẕâ. velâ yüfliḥu-ssâḥirûn.'),
(1442, 10, 78, 'ḳâlû eci\'tenâ litelfitenâ `ammâ vecednâ `aleyhi âbâenâ vetekûne leküme-lkibriyâü fi-l\'arḍ. vemâ naḥnü lekümâ bimü\'minîn.'),
(1443, 10, 79, 'veḳâle fir`avnü-\'tûnî bikülli sâḥirin `alîm.'),
(1444, 10, 80, 'felemmâ câe-sseḥaratü ḳâle lehüm mûsâ elḳû mâ entüm mülḳûn.'),
(1445, 10, 81, 'felemmâ elḳav ḳâle mûsâ mâ ci\'tüm bihi-ssiḥr. inne-llâhe seyübṭilüh. inne-llâhe lâ yuṣliḥu `amele-lmüfsidîn.'),
(1446, 10, 82, 'veyüḥiḳḳu-llâhü-lḥaḳḳa bikelimâtihî velev kerihe-lmücrimûn.'),
(1447, 10, 83, 'femâ âmene limûsâ illâ ẕürriyyetüm min ḳavmihî `alâ ḫavfim min fir`avne vemeleihim ey yeftinehüm. veinne fir`avne le`âlin fi-l\'arḍ. veinnehû lemine-lmüsrifîn.'),
(1448, 10, 84, 'veḳâle mûsâ yâ ḳavmi in küntüm âmentüm billâhi fe`aleyhi tevekkelû in küntüm müslimîn.'),
(1449, 10, 85, 'feḳâlû `ale-llâhi tevekkelnâ. rabbenâ lâ tec`alnâ fitnetel lilḳavmi-żżâlimîn.'),
(1450, 10, 86, 'veneccinâ biraḥmetike mine-lḳavmi-lkâfirîn.'),
(1451, 10, 87, 'veevḥaynâ ilâ mûsâ veeḫîhi en tebevveâ liḳavmikümâ bimiṣra büyûtev vec`alû büyûteküm ḳibletev veeḳîmu-ṣṣalâh. vebeşşiri-lmü\'minîn.'),
(1452, 10, 88, 'veḳâle mûsâ rabbenâ inneke âteyte fir`avne vemeleehû zînetev veemvâlen fi-lḥayâti-ddünyâ rabbenâ liyüḍillû `an sebîlik. rabbene-ṭmis `alâ emvâlihim veşdüd `alâ ḳulûbihim felâ yü\'minû ḥattâ yeravu-l`aẕâbe-l\'elîm.'),
(1453, 10, 89, 'ḳâle ḳad ücîbet da`vetükümâ festeḳîmâ velâ tettebi`ânni sebîle-lleẕîne lâ ya`lemûn.'),
(1454, 10, 90, 'vecâveznâ bibenî isrâîle-lbaḥra feetbe`ahüm fir`avnü vecünûdühû bagyev ve`advâ. ḥattâ iẕâ edrakehü-lgaraḳu ḳâle âmentü ennehû lâ ilâhe ille-lleẕî âmenet bihî benû isrâîle veenâ mine-lmüslimîn.'),
(1455, 10, 91, 'âl\'âne veḳad `aṣayte ḳablü vekünte mine-lmüfsidîn.'),
(1456, 10, 92, 'felyevme nüneccîke bibedenike litekûne limen ḫalfeke âyeh. veinne keŝîram mine-nnâsi `an âyâtinâ legâfilûn.'),
(1457, 10, 93, 'veleḳad bevve\'nâ benî isrâîle mübevvee ṣidḳiv verazaḳnâhüm mine-ṭṭayyibât. feme-ḫtelefû ḥattâ câehümü-l`ilm. inne rabbeke yaḳḍî beynehüm yevme-lḳiyâmeti fîmâ kânû fîhi yaḫtelifûn.'),
(1458, 10, 94, 'fein künte fî şekkim mimmâ enzelnâ ileyke fes\'eli-lleẕîne yaḳraûne-lkitâbe min ḳablik. leḳad câeke-lḥaḳḳu mir rabbike felâ tekûnenne mine-lmümterîn.'),
(1459, 10, 95, 'velâ tekûnenne mine-lleẕîne keẕẕebû biâyâti-llâhi fetekûne mine-lḫâsirîn.'),
(1460, 10, 96, 'inne-lleẕîne ḥaḳḳat `aleyhim kelimetü rabbike lâ yü\'minûn.'),
(1461, 10, 97, 'velev câethüm küllü âyetin ḥattâ yeravu-l`aẕâbe-l\'elîm.'),
(1462, 10, 98, 'felevlâ kânet ḳaryetün âmenet fenefe`ahâ îmânühâ illâ ḳavme yûnüs. lemmâ âmenû keşefnâ `anhüm `aẕâbe-lḫizyi fi-lḥayâti-ddünyâ vemetta`nâhüm ilâ ḥîn.'),
(1463, 10, 99, 'velev şâe rabbüke leâmene men fi-l\'arḍi küllühüm cemî`â. efeente tükrihü-nnâse ḥattâ yekûnû mü\'minîn.'),
(1464, 10, 100, 'vemâ kâne linefsin en tü\'mine illâ biiẕni-llâh. veyec`alü-rricse `ale-lleẕîne lâ ya`ḳilûn.'),
(1465, 10, 101, 'ḳuli-nżurû mâẕâ fi-ssemâvâti vel\'arḍ. vemâ tugni-l\'âyâtü vennüẕüru `an ḳavmil lâ yü\'minûn.'),
(1466, 10, 102, 'fehel yenteżirûne illâ miŝle eyyâmi-lleẕîne ḫalev min ḳablihim. ḳul fenteżirû innî me`aküm mine-lmünteżirîn.'),
(1467, 10, 103, 'ŝümme nüneccî rusülenâ velleẕîne âmenû keẕâlik. ḥaḳḳan `aleynâ nünci-lmü\'minîn.'),
(1468, 10, 104, 'ḳul yâ eyyühe-nnâsü in küntüm fî şekkim min dînî felâ a`büdü-lleẕîne ta`büdûne min dûni-llâhi velâkin a`büdü-llâhe-lleẕî yeteveffâküm. veümirtü en ekûne mine-lmü\'minîn.'),
(1469, 10, 105, 'veen eḳim vecheke lildîni ḥanîfâ. velâ tekûnenne mine-lmüşrikîn.'),
(1470, 10, 106, 'velâ ted`u min dûni-llâhi mâ lâ yenfe`uke velâ yeḍurruk. fein fe`alte feinneke iẕem mine-żżâlimîn.'),
(1471, 10, 107, 'veiy yemseske-llâhü biḍurrin felâ kâşife lehû illâ hû. veiy yüridke biḫayrin felâ râdde lifaḍlih. yüṣîbü bihî mey yeşâü min `ibâdih. vehüve-lgafûru-rraḥîm.'),
(1472, 10, 108, 'ḳul yâ eyyühe-nnâsü ḳad câekümü-lḥaḳḳu mir rabbiküm. femeni-htedâ feinnemâ yehtedî linefsih. vemen ḍalle feinnemâ yeḍillü `aleyhâ. vemâ ene `aleyküm bivekîl.'),
(1473, 10, 109, 'vettebi` mâ yûḥâ ileyke vaṣbir ḥattâ yaḥküme-llâh. vehüve ḫayru-lḥâkimîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'elif-lâm-râ. kitâbün uḥkimet âyâtühû ŝümme füṣṣilet mil ledün ḥakîmin ḫabîr.'),
(1475, 11, 2, 'ellâ ta`büdû ille-llâh. innenî leküm minhü neẕîruv vebeşîr.'),
(1476, 11, 3, 'veeni-stagfirû rabbeküm ŝümme tûbû ileyhi yümetti`küm metâ`an ḥasenen ilâ ecelim müsemmev veyü\'ti külle ẕî faḍlin faḍleh. vein tevellev feinnî eḫâfü `aleyküm `aẕâbe yevmin kebîr.'),
(1477, 11, 4, 'ile-llâhi merci`uküm. vehüve `alâ külli şey\'in ḳadîr.'),
(1478, 11, 5, 'elâ innehüm yeŝnûne ṣudûrahüm liyestaḫfû minh. elâ ḥîne yestagşûne ŝiyâbehüm ya`lemü mâ yüsirrûne vemâ yü`linûne. innehû `alîmüm biẕâti-ṣṣudûr.'),
(1479, 11, 6, 'vemâ min dâbbetin fi-l\'arḍi illâ `ale-llâhi rizḳuhâ veya`lemü müsteḳarrahâ vemüstevde`ahâ. küllün fî kitâbim mübîn.'),
(1480, 11, 7, 'vehüve-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa fî sitteti eyyâmiv vekâne `arşühû `ale-lmâi liyeblüveküm eyyüküm aḥsenü `amelâ. velein ḳulte inneküm meb`ûŝûne mim ba`di-lmevti leyeḳûlenne-lleẕîne keferû in hâẕâ illâ siḥrum mübîn.'),
(1481, 11, 8, 'velein eḫḫarnâ `anhümü-l`aẕâbe ilâ ümmetim ma`dûdetil leyeḳûlünne mâ yaḥbisüh. elâ yevme ye\'tîhim leyse maṣrûfen `anhüm veḥâḳa bihim mâ kânû bihî yestehziûn.'),
(1482, 11, 9, 'velein eẕaḳne-l\'insâne minnâ raḥmeten ŝümme neza`nâhâ minh. innehû leyeûsün kefûr.'),
(1483, 11, 10, 'velein eẕaḳnâhü na`mâe ba`de ḍarrâe messethü leyeḳûlenne ẕehebe-sseyyiâtü `annî. innehû leferiḥun feḫûr.'),
(1484, 11, 11, 'ille-lleẕîne ṣaberû ve`amilu-ṣṣâliḥâti. ülâike lehüm magfiratüv veecrun kebîr.'),
(1485, 11, 12, 'fele`alleke târiküm ba`ḍa mâ yûḥâ ileyke veḍâiḳum bihî ṣadruke ey yeḳûlû levlâ ünzile `aleyhi kenzün ev câe me`ahû melek. innemâ ente neẕîr. vellâhü `alâ külli şey\'iv vekîl.'),
(1486, 11, 13, 'em yeḳûlûne-fterâh. ḳul fe\'tû bi`aşri süverim miŝlihî müfterayâtiv ved`û meni-steṭa`tüm min dûni-llâhi in küntüm ṣâdiḳîn.'),
(1487, 11, 14, 'feillem yestecîbû leküm fa`lemû ennemâ ünzile bi`ilmi-llâhi veel lâ ilâhe illâ hû. fehel entüm müslimûn.'),
(1488, 11, 15, 'men kâne yürîdü-lḥayâte-ddünyâ vezînetehâ nüveffi ileyhim a`mâlehüm fîhâ vehüm fîhâ lâ yübḫasûn.'),
(1489, 11, 16, 'ülâike-lleẕîne leyse lehüm fi-l\'âḫirati ille-nnâr. veḥabiṭa mâ ṣane`û fîhâ vebâṭilüm mâ kânû ya`melûn.'),
(1490, 11, 17, 'efemen kâne `alâ beyyinetim mir rabbihî veyetlûhü şâhidüm minhü vemin ḳablihî kitâbü mûsâ imâmev veraḥmeh. ülâike yü\'minûne bih. vemey yekfür bihî mine-l\'aḥzâbi felnâru mev`idüh. felâ tekü fî miryetim minhü innehü-lḥaḳḳu mir rabbike velâkinne ekŝera-nnâsi lâ yü\'minûn.'),
(1491, 11, 18, 'vemen ażlemü mimmeni-fterâ `ale-llâhi keẕibâ. ülâike yü`raḍûne `alâ rabbihim veyeḳûlü-l\'eşhâdü hâülâi-lleẕîne keẕebû `alâ rabbihim. elâ la`netü-llâhi `ale-żżâlimîn.'),
(1492, 11, 19, 'elleẕîne yeṣuddûne `an sebîli-llâhi veyebgûnehâ `ivecâ. vehüm bil\'âḫirati hüm kâfirûn.'),
(1493, 11, 20, 'ülâike lem yekûnû mü`cizîne fi-l\'arḍi vemâ kâne lehüm min dûni-llâhi min evliyâ\'. yüḍâ`afü lehümü-l`aẕâb. mâ kânû yesteṭî`ûne-ssem`a vemâ kânû yübṣirûn.'),
(1494, 11, 21, 'ülâike-lleẕîne ḫasirû enfüsehüm veḍalle `anhüm mâ kânû yefterûn.'),
(1495, 11, 22, 'lâ cerame ennehüm fi-l\'âḫirati hümü-l\'aḫserûn.'),
(1496, 11, 23, 'inne-lleẕîne âmenû ve`amilu-ṣṣâliḥâti veaḫbetû ilâ rabbihim ülâike aṣḥâbü-lcenneh. hüm fîhâ ḫâlidûn.'),
(1497, 11, 24, 'meŝelü-lferîḳayni kel\'a`mâ vel\'eṣammi velbeṣîri vessemî`. hel yesteviyâni meŝelâ. efelâ teẕekkerûn.'),
(1498, 11, 25, 'veleḳad erselnâ nûḥan ilâ ḳavmih. innî leküm neẕîrum mübîn.'),
(1499, 11, 26, 'el lâ ta`büdû ille-llâh. innî eḫâfü `aleyküm `aẕâbe yevmin elîm.'),
(1500, 11, 27, 'feḳâle-lmeleü-lleẕîne keferû min ḳavmihî mâ nerâke illâ beşeram miŝlenâ vemâ nerâke-ttebe`ake ille-lleẕîne hüm erâẕilünâ bâdiye-rra\'y. vemâ nerâ leküm `aleynâ min faḍlim bel neżunnüküm kâẕibîn.'),
(1501, 11, 28, 'ḳâle yâ ḳavmi era\'eytüm in küntü `alâ beyyinetim mir rabbî veâtânî raḥmetem min `indihî fe`ummiyet `aleyküm. enülzimükümûhâ veentüm lehâ kârihûn.'),
(1502, 11, 29, 'veyâ ḳavmi lâ es\'elüküm `aleyhi mâlâ. in ecriye illâ `ale-llâhi vemâ ene biṭâridi-lleẕîne âmenû. innehüm mülâḳû rabbihim velâkinnî erâküm ḳavmen techelûn.'),
(1503, 11, 30, 'veyâ ḳavmi mey yenṣurunî mine-llâhi in ṭarattühüm. efelâ teẕekkerûn.'),
(1504, 11, 31, 'velâ eḳûlü leküm `indî ḫazâinü-llâhi velâ a`lemü-lgaybe velâ eḳûlü innî meleküv velâ eḳûlü lilleẕîne tezderî a`yünüküm ley yü\'tiyehümü-llâhü ḫayrâ. allâhü a`lemü bimâ fî enfüsihim. innî iẕel lemine-żżâlimîn.'),
(1505, 11, 32, 'ḳâlû yâ nûḥu ḳad câdeltenâ feekŝerte cidâlenâ fe\'tinâ bimâ te`idünâ in künte mine-ṣṣâdiḳîn.'),
(1506, 11, 33, 'ḳâle innemâ ye\'tîküm bihi-llâhü in şâe vemâ entüm bimü`cizîn.'),
(1507, 11, 34, 'velâ yenfe`uküm nuṣḥî in erattü en enṣaḥa leküm in kâne-llâhü yürîdü ey yugviyeküm. hüve rabbüküm veileyhi türce`ûn.'),
(1508, 11, 35, 'em yeḳûlûne-fterâh. ḳul ini-fteraytühû fe`aleyye icrâmî veenâ berîüm mimmâ tücrimûn.'),
(1509, 11, 36, 'veûḥiye ilâ nûḥin ennehû ley yü\'mine min ḳavmike illâ men ḳad âmene felâ tebteis bimâ kânû yef`alûn.'),
(1510, 11, 37, 'vaṣne`i-lfülke bia`yüninâ vevaḥyinâ velâ tüḫâṭibnî fi-lleẕîne żalemû. innehüm mugraḳûn.'),
(1511, 11, 38, 'veyaṣne`u-lfülke veküllemâ merra `aleyhi meleüm min ḳavmihî seḫirû minh. ḳâle in tesḫarû minnâ feinnâ nesḫaru minküm kemâ tesḫarûn.'),
(1512, 11, 39, 'fesevfe ta`lemûne mey ye\'tîhi `aẕâbüy yuḫzîhi veyeḥillü `aleyhi `aẕâbüm müḳîm.'),
(1513, 11, 40, 'ḥattâ iẕâ câe emrunâ vefâra-ttennûru ḳulne-ḥmil fîhâ min küllin zevceyni-ŝneyni veehleke illâ men sebeḳa `aleyhi-lḳavlü vemen âmen. vemâ âmene me`ahû illâ ḳalîl.'),
(1514, 11, 41, 'veḳâle-rkebû fîhâ bismi-llâhi mecrâhâ vemürsâhâ. inne rabbî legafûrur raḥîm.'),
(1515, 11, 42, 'vehiye tecrî bihim fî mevcin kelcibâli venâdâ nûḥun-bnehû vekâne fî ma`ziliy yâ büneyye-rkem me`anâ velâ teküm me`a-lkâfirîn.'),
(1516, 11, 43, 'ḳâle seâvî ilâ cebeliy ya`ṣimünî mine-lmâ\'. ḳâle lâ `âṣime-lyevme min emri-llâhi illâ mer raḥim. veḥâle beynehüme-lmevcü fekâne mine-lmugraḳîn.'),
(1517, 11, 44, 'veḳîle yâ arḍu-ble`î mâeki veyâ semâü aḳli`î vegîḍa-lmâü veḳuḍiye-l\'emru vestevet `ale-lcûdiyyi veḳîle bü`del lilḳavmi-żżâlimîn.'),
(1518, 11, 45, 'venâdâ nûḥur rabbehû feḳâle rabbi inne-bnî min ehlî veinne va`deke-lḥaḳḳu veente aḥkemü-lḥâkimîn.'),
(1519, 11, 46, 'ḳâle yâ nûḥu innehû leyse min ehlik. innehû `amelün gayru ṣâliḥ. felâ tes\'elni mâ leyse leke bihî `ilm. innî e`iżuke en tekûne mine-lcâhilîn.'),
(1520, 11, 47, 'ḳâle rabbi innî e`ûẕü bike en es\'eleke mâ leyse lî bihî `ilm. veillâ tagfir lî veterḥamnî eküm mine-lḫâsirîn.'),
(1521, 11, 48, 'ḳîle yâ nûḥu-hbiṭ biselâmim minnâ veberakâtin `aleyke ve`alâ ümemim mimmem me`ak. veümemün senümetti`uhüm ŝümme yemessühüm minnâ `aẕâbün elîm.'),
(1522, 11, 49, 'tilke min embâi-lgaybi nûḥîhâ ileyk. mâ künte ta`lemühâ ente velâ ḳavmüke min ḳabli hâẕâ. faṣbir. inne-l`âḳibete lilmütteḳîn.'),
(1523, 11, 50, 'veilâ `âdin eḫâhüm hûdâ. ḳâle yâ ḳavmi-`büdü-llâhe mâ leküm min ilâhin gayruh. in entüm illâ müfterûn.'),
(1524, 11, 51, 'yâ ḳavmi lâ es\'elüküm `aleyhi ecrâ. in ecriye illâ `ale-lleẕî feṭaranî. efelâ ta`ḳilûn.'),
(1525, 11, 52, 'veyâ ḳavmi-stagfirû rabbeküm ŝümme tûbû ileyhi yürsili-ssemâe `aleyküm midrârav veyezidküm ḳuvveten ilâ ḳuvvetiküm velâ tetevellev mücrimîn.'),
(1526, 11, 53, 'ḳâlû yâ hûdü mâ ci\'tenâ bibeyyinetiv vemâ naḥnü bitârikî âlihetinâ `an ḳavlike vemâ naḥnü leke bimü\'minîn.'),
(1527, 11, 54, 'in neḳûlü ille-`terâke ba`ḍu âlihetinâ bisû\'. ḳâle innî üşhidü-llâhe veşhedû ennî berîüm mimmâ tüşrikûn.'),
(1528, 11, 55, 'min dûnihî fekîdûnî cemî`an ŝümme lâ tünżirûn.'),
(1529, 11, 56, 'innî tevekkeltü `ale-llâhi rabbî verabbiküm. mâ min dâbbetin illâ hüve âḫiẕüm binâṣiyetihâ. inne rabbî `alâ ṣirâṭim müsteḳîm.'),
(1530, 11, 57, 'fein tevellev feḳad eblagtüküm mâ ürsiltü bihî ileyküm. veyestaḫlifü rabbî ḳavmen gayraküm. velâ teḍurrûnehû şey\'â. inne rabbî `alâ külli şey\'in ḥafîż.'),
(1531, 11, 58, 'velemmâ câe emrunâ necceynâ hûdev velleẕîne âmenû me`ahû biraḥmetim minnâ. venecceynâhüm min `aẕâbin galîż.'),
(1532, 11, 59, 'vetilke `âdün ceḥadû biâyâti rabbihim ve`aṣav rusülehû vettebe`û emra külli cebbârin `anîd.'),
(1533, 11, 60, 'veütbi`û fî hâẕihi-ddünyâ la`netev veyevme-lḳiyâmeh. elâ inne `âden keferû rabbehüm. elâ bü`del li`âdin ḳavmi hûd.'),
(1534, 11, 61, 'veilâ ŝemûde eḫâhüm ṣâliḥâ. ḳâle yâ ḳavmi-`büdü-llâhe mâ leküm min ilâhin gayruh. hüve enşeeküm mine-l\'arḍi vesta`meraküm fîhâ festagfirûhü ŝümme tûbû ileyh. inne rabbî ḳarîbüm mücîb.'),
(1535, 11, 62, 'ḳâlû yâ ṣâliḥu ḳad künte fînâ mercüvven ḳable hâẕâ etenhânâ en na`büde mâ ya`büdü âbâünâ veinnenâ lefî şekkim mimmâ ted`ûnâ ileyhi mürîbün.'),
(1536, 11, 63, 'ḳâle yâ ḳavmi era\'eytüm in küntü `alâ beyyinetim mir rabbî veâtânî minhü raḥmeten femey yenṣurunî mine-llâhi in `aṣaytühû femâ tezîdûnenî gayra taḫsîr.'),
(1537, 11, 64, 'veyâ ḳavmi hâẕihî nâḳatü-llâhi leküm âyeten feẕerûhâ te\'kül fî arḍi-llâhi velâ temessûhâ bisûin feye\'ḫuẕeküm `aẕâbün ḳarîb.'),
(1538, 11, 65, 'fe`aḳarûhâ feḳâle temette`û fî dâriküm ŝelâŝete eyyâm. ẕâlike va`dün gayru mekẕûb.'),
(1539, 11, 66, 'felemmâ câe emrunâ necceynâ ṣâliḥav velleẕîne âmenû me`ahû biraḥmetim minnâ vemin ḫizyi yevmiiẕ. inne rabbeke hüve-lḳaviyyü-l`azîz.'),
(1540, 11, 67, 'veeḫaẕe-lleẕîne żalemu-ṣṣayḥatü feaṣbeḥû fî diyârihim câŝimîn.'),
(1541, 11, 68, 'keel lem yagnev fîhâ. elâ inne ŝemûde keferû rabbehüm. elâ bü`del liŝemûd.'),
(1542, 11, 69, 'veleḳad câet rusülünâ ibrâhîme bilbüşrâ ḳâlû selâmâ. ḳâle selâmün femâ lebiŝe en câe bi`iclin ḥanîẕ.'),
(1543, 11, 70, 'felemmâ raâ eydiyehüm lâ teṣilü ileyhi nekirahüm veevcese minhüm ḫîfeh. ḳâlû lâ teḫaf innâ ürsilnâ ilâ ḳavmi lûṭ.'),
(1544, 11, 71, 'vemraetühû ḳâimetün feḍaḥiket febeşşernâhâ biisḥâḳa vemiv verâi isḥâḳa ya`ḳûb.'),
(1545, 11, 72, 'ḳâlet yâ veyletâ eelidü veenâ `acûzüv vehâẕâ ba`lî şeyḫâ. inne hâẕâ leşey\'ün `acîb.'),
(1546, 11, 73, 'ḳâlû eta`cebîne min emri-llâhi raḥmetü-llâhi veberakâtühû `aleyküm ehle-lbeyt. innehû ḥamîdüm mecîd.'),
(1547, 11, 74, 'felemmâ ẕehebe `an ibrâhîme-rrav`u vecâethü-lbüşrâ yücâdilünâ fî ḳavmi lûṭ.'),
(1548, 11, 75, 'inne ibrâhîme leḥalîmün evvâhüm münîb.'),
(1549, 11, 76, 'yâ ibrâhîmü a`riḍ `an hâẕâ. innehû ḳad câe emru rabbik. veinnehüm âtîhim `aẕâbün gayru merdûd.'),
(1550, 11, 77, 'velemmâ câet rusülünâ lûṭan sîe bihim veḍâḳa bihim ẕer`av veḳâle hâẕâ yevmün `aṣîb.'),
(1551, 11, 78, 'vecâehû ḳavmühû yühra`ûne ileyhi vemin ḳablü kânû ya`melûne-sseyyiât. ḳâle yâ ḳavmi hâülâi benâtî hünne aṭheru leküm fetteḳu-llâhe velâ tuḫzûni fî ḍayfî. eleyse minküm racülür raşîd.'),
(1552, 11, 79, 'ḳâlû leḳad `alimte mâ lenâ fî benâtike min ḥaḳḳ. veinneke leta`lemü mâ nürîd.'),
(1553, 11, 80, 'ḳâle lev enne lî biküm ḳuvveten ev âvî ilâ ruknin şedîd.'),
(1554, 11, 81, 'ḳâlû yâ lûṭu innâ rusülü rabbike ley yeṣilû ileyke feesri biehlike biḳiṭ`im mine-lleyli velâ yeltefit minküm eḥadün ille-mraetek. innehû müṣîbühâ mâ eṣâbehüm. inne mev`idehümu-ṣṣubḥ. eleyse-ṣṣubḥu biḳarîb.'),
(1555, 11, 82, 'felemmâ câe emrunâ ce`alnâ `âliyehâ sâfilehâ veemṭarnâ `aleyhâ ḥicâratem min siccîlim menḍûd.'),
(1556, 11, 83, 'müsevvemeten `inde rabbik. vemâ hiye mine-żżâlimîne bibe`îd.'),
(1557, 11, 84, 'veilâ medyene eḫâhüm şu`aybâ. ḳâle yâ ḳavmi-`büdü-llâhe mâ leküm min ilâhin gayruh. velâ tenḳuṣü-lmikyâle velmîzâne innî erâküm biḫayriv veinnî eḫâfü `aleyküm `aẕâbe yevmim müḥîṭ.'),
(1558, 11, 85, 'veyâ ḳavmi evfü-lmikyâle velmîzâne bilḳisṭi velâ tebḫasü-nnâse eşyâehüm velâ ta`ŝev fi-l\'arḍi müfsidîn.'),
(1559, 11, 86, 'beḳiyyetü-llâhi ḫayrul leküm in küntüm mü\'minîn. vemâ ene `aleyküm biḥafîż.'),
(1560, 11, 87, 'ḳâlû yâ şu`aybü eṣalâtüke te\'müruke en netruke mâ ya`büdü âbâünâ ev en nef`ale fî emvâlinâ mâ neşâ\'. inneke leente-lḥalîmü-rraşîd.'),
(1561, 11, 88, 'ḳâle yâ ḳavmi era\'eytüm in küntü `alâ beyyinetim mir rabbî verazeḳanî minhü rizḳan ḥasenâ. vemâ ürîdü en üḫâlifeküm ilâ mâ enhâküm `anh. in ürîdü ille-l\'iṣlâḥa me-steṭa`t. vemâ tevfîḳî illâ billâh. `aleyhi tevekkeltü veileyhi ünîb.'),
(1562, 11, 89, 'veyâ ḳavmi lâ yecrimenneküm şiḳâḳî ey yüṣîbeküm miŝlü mâ eṣâbe ḳavme nûḥin ev ḳavme hûdin ev ḳavme ṣâliḥ. vemâ ḳavmü lûṭim minküm bibe`îd.'),
(1563, 11, 90, 'vestagfirû rabbeküm ŝümme tûbû ileyh. inne rabbî raḥîmüv vedûd.'),
(1564, 11, 91, 'ḳâlû yâ şu`aybü mâ nefḳahû keŝîram mimmâ teḳûlü veinnâ lenerâke fînâ ḍa`îfâ. velevlâ rahṭuke leracemnâk. vemâ ente `aleynâ bi`azîz.'),
(1565, 11, 92, 'ḳâle yâ ḳavmi erahṭî e`azzü `aleyküm mine-llâh. vetteḫaẕtümûhü verâeküm żihriyyâ. inne rabbî bimâ ta`melûne müḥîṭ.'),
(1566, 11, 93, 'veyâ ḳavmi-`melû `alâ mekânetiküm innî `âmil. sevfe ta`lemûne mey ye\'tîhi `aẕâbüy yuḫzîhi vemen hüve kâẕib. verteḳibû innî me`aküm raḳîb.'),
(1567, 11, 94, 'velemmâ câe emrunâ necceynâ şu`aybev velleẕîne âmenû me`ahû biraḥmetim minnâ veeḫaẕeti-lleẕîne żalemu-ṣṣayḥatü feaṣbeḥû fî diyârihim câŝimîn.'),
(1568, 11, 95, 'keel lem yagnev fîhâ. elâ bü`del limedyene kemâ be`idet ŝemûd.'),
(1569, 11, 96, 'veleḳad erselnâ mûsâ biâyâtinâ vesülṭânim mübîn.'),
(1570, 11, 97, 'ilâ fir`avne vemeleihî fettebe`û emra fir`avn. vemâ emru fir`avne biraşîd.'),
(1571, 11, 98, 'yaḳdümü ḳavmehû yevme-lḳiyâmeti feevradehümü-nnâr. vebi\'se-lvirdü-lmevrûd.'),
(1572, 11, 99, 'veütbi`û fî hâẕihî la`netev veyevme-lḳiyâmeh. bi\'se-rrifdü-lmerfûd.'),
(1573, 11, 100, 'ẕâlike min embâi-lḳurâ neḳuṣṣuhû `aleyke minhâ ḳâimüv veḥaṣîd.'),
(1574, 11, 101, 'vemâ żalemnâhüm velâkin żalemû enfüsehüm femâ agnet `anhüm âlihetühümü-lletî yed`ûne min dûni-llâhi min şey\'il lemmâ câe emru rabbik. vemâ zâdûhüm gayra tetbîb.'),
(1575, 11, 102, 'vekeẕâlike aḫẕü rabbike iẕâ eḫaẕe-lḳurâ vehiye żâlimeh. inne aḫẕehû elîmün şedîd.'),
(1576, 11, 103, 'inne fî ẕâlike leâyetel limen ḫâfe `aẕâbe-l\'âḫirah. ẕâlike yevmüm mecmû`ul lehü-nnâsü veẕâlike yevmüm meşhûd.'),
(1577, 11, 104, 'vemâ nü\'eḫḫiruhû illâ liecelim ma`dûd.'),
(1578, 11, 105, 'yevme ye\'ti lâ tekellemü nefsün illâ biiẕnih. feminhüm şeḳiyyüv vese`îd.'),
(1579, 11, 106, 'feemme-lleẕîne şeḳû fefi-nnâri lehüm fîhâ zefîruv veşehîḳ.'),
(1580, 11, 107, 'ḫâlidîne fîhâ mâ dâmeti-ssemâvâtü vel\'arḍu illâ mâ şâe rabbük. inne rabbeke fa``âlül limâ yürîd.'),
(1581, 11, 108, 'veemme-lleẕîne sü`idû fefi-lcenneti ḫâlidîne fîhâ mâ dâmeti-ssemâvâtü vel\'arḍu illâ mâ şâe rabbük. `aṭâen gayra mecẕûẕ.'),
(1582, 11, 109, 'felâ tekü fî miryetim mimmâ ya`büdü hâülâ\'. mâ ya`büdûne illâ kemâ ya`büdü âbâühüm min ḳabl. veinnâ lemüveffûhüm neṣîbehüm gayra menḳûṣ.'),
(1583, 11, 110, 'veleḳad âteynâ mûse-lkitâbe faḫtülife fîh. velevlâ kelimetün sebeḳat mir rabbike leḳuḍiye beynehüm. veinnehüm lefî şekkim minhü mürîbün.'),
(1584, 11, 111, 'veinne küllel lemmâ leyüveffiyennehüm rabbüke a`mâlehüm. innehû bimâ ya`melûne ḫabîr.'),
(1585, 11, 112, 'festeḳim kemâ ümirte vemen tâbe me`ake velâ taṭgav. innehû bimâ ta`melûne beṣîr.'),
(1586, 11, 113, 'velâ terkenû ile-lleẕîne żalemû fetemessekümü-nnâru vemâ leküm min dûni-llâhi min evliyâe ŝümme lâ tünṣarûn.'),
(1587, 11, 114, 'veeḳimi-ṣṣalâte ṭarafeyi-nnehâri vezülefem mine-lleyl. inne-lḥasenâti yüẕhibne-sseyyiât. ẕâlike ẕikrâ liẕẕâkirîn.'),
(1588, 11, 115, 'vaṣbir feinne-llâhe lâ yüḍî`u ecra-lmuḥsinîn.'),
(1589, 11, 116, 'felevlâ kâne mine-lḳurûni min ḳabliküm ülû beḳiyyetiy yenhevne `ani-lfesâdi fi-l\'arḍi illâ ḳalîlem mimmen enceynâ minhüm. vettebe`a-lleẕîne żalemû mâ ütrifû fîhi vekânû mücrimîn.'),
(1590, 11, 117, 'vemâ kâne rabbüke liyühlike-lḳurâ biżulmiv veehlühâ muṣliḥûn.'),
(1591, 11, 118, 'velev şâe rabbüke lece`ale-nnâse ümmetev vâḥidetev velâ yezâlûne muḫtelifîn.'),
(1592, 11, 119, 'illâ mer raḥime rabbük. veliẕâlike ḫaleḳahüm. vetemmet kelimetü rabbike leemleenne cehenneme mine-lcinneti vennâsi ecme`în.'),
(1593, 11, 120, 'veküllen neḳuṣṣu `aleyke min embâi-rrusüli mâ nüŝebbitü bihî füâdek. vecâeke fî hâẕihi-lḥaḳḳu vemev`iżatüv veẕikrâ lilmü\'minîn.'),
(1594, 11, 121, 'veḳul lilleẕîne lâ yü\'minûne-`melû `alâ mekânetiküm. innâ `âmilûn.'),
(1595, 11, 122, 'venteżirû. innâ münteżirûn.'),
(1596, 11, 123, 'velillâhi gaybü-ssemâvâti vel\'arḍi veileyhi yürce`u-l\'emru küllühû fa`büdhü vetevekkel `aleyh. vemâ rabbüke bigâfilin `ammâ ta`melûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'elif-lâm-râ. tilke âyâtü-lkitâbi-lmübîn.'),
(1598, 12, 2, 'innâ enzelnâhü ḳur\'ânen `arabiyyel le`alleküm ta`ḳilûn.'),
(1599, 12, 3, 'naḥnü neḳuṣṣu `aleyke aḥsene-lḳaṣaṣi bimâ evḥaynâ ileyke hâẕe-lḳur\'ân. vein künte min ḳablihî lemine-lgâfilîn.'),
(1600, 12, 4, 'iẕ ḳâle yûsüfü liebîhi yâ ebeti innî raeytü eḥade `aşera kevkebev veşşemse velḳamera raeytühüm lî sâcidîn.'),
(1601, 12, 5, 'ḳâle yâ büneyye lâ taḳṣuṣ ru\'yâke `alâ iḫvetike feyekîdû leke keydâ. inne-şşeyṭâne lil\'insâni `adüvvüm mübîn.'),
(1602, 12, 6, 'vekeẕâlike yectebîke rabbüke veyü`allimüke min te\'vîli-l\'eḥâdîŝi veyütimmü ni`metehû `aleyke ve`alâ âli ya`ḳûbe kemâ etemmehâ `alâ ebeveyke min ḳablü ibrâhîme veisḥâḳ. inne rabbeke `alîmün ḥakîm.'),
(1603, 12, 7, 'leḳad kâne fî yûsüfe veiḫvetihî âyâtül lissâilîn.'),
(1604, 12, 8, 'iẕ ḳâlû leyûsüfü veeḫûhü eḥabbü ilâ ebînâ minnâ venaḥnü `uṣbeh. inne ebânâ lefî ḍalâlim mübîn.'),
(1605, 12, 9, 'uḳtülû yûsüfe evi-ṭraḥûhü arḍay yaḫlü leküm vechü ebîküm vetekûnû mim ba`dihî ḳavmen ṣâliḥîn.'),
(1606, 12, 10, 'ḳâle ḳâilüm minhüm lâ taḳtülû yûsüfe veelḳûhü fî gayâbeti-lcübbi yelteḳiṭhü ba`ḍu-sseyyârati in küntüm fâ`ilîn.'),
(1607, 12, 11, 'ḳâlû yâ ebânâ mâ leke lâ te\'mennâ `alâ yûsüfe veinnâ lehû lenâṣiḥûn.'),
(1608, 12, 12, 'ersilhü me`anâ gadey yerta` veyel`ab veinnâ lehû leḥâfiżûn.'),
(1609, 12, 13, 'ḳâle innî leyaḥzününî en teẕhebû bihî veeḫâfü ey ye\'külehü-ẕẕi\'bü veentüm `anhü gâfilûn.'),
(1610, 12, 14, 'ḳâlû lein ekelehü-ẕẕi\'bü venaḥnü `uṣbetün innâ iẕel leḫâsirûn.'),
(1611, 12, 15, 'felemmâ ẕehebû bihî veecme`û ey yec`alûhü fî gayâbeti-lcübb. veevḥaynâ ileyhi letünebbiennehüm biemrihim hâẕâ vehüm lâ yeş`urûn.'),
(1612, 12, 16, 'vecâû ebâhüm `işâey yebkûn.'),
(1613, 12, 17, 'ḳâlû yâ ebânâ innâ ẕehebnâ nestebiḳu veteraknâ yûsüfe `inde metâ`inâ feekelehü-ẕẕi\'b. vemâ ente bimü\'minil lenâ velev künnâ ṣâdiḳîn.'),
(1614, 12, 18, 'vecâû `alâ ḳamîṣihî bidemin kâẕib. ḳâle bel sevvelet leküm enfüsüküm emrâ. feṣabrun cemîl. vellâhü-lmüste`ânü `alâ mâ teṣifûn.'),
(1615, 12, 19, 'vecâet seyyâratün feerselû vâridehüm feedlâ delveh. ḳâle yâ büşrâ hâẕâ gulâm. veeserrûhü biḍâ`ah. vellâhü `alîmüm bimâ ya`melûn.'),
(1616, 12, 20, 'veşeravhü biŝemenim baḫsin derâhime ma`dûdeh. vekânû fîhi mine-zzâhidîn.'),
(1617, 12, 21, 'veḳâle-lleẕi-şterâhü mim miṣra limraetihî ekrimî meŝvâhü `asâ ey yenfe`anâ ev netteḫiẕehû veledâ. vekeẕâlike mekkennâ liyûsüfe fi-l\'arḍ. velinü`allimehû min te\'vîli-l\'eḥâdîŝ. vellâhü gâlibün `alâ emrihî velâkinne ekŝera-nnâsi lâ ya`lemûn.'),
(1618, 12, 22, 'velemmâ belega eşüddeh âteynâhü ḥukmev ve`ilmâ. vekeẕâlike neczi-lmuḥsinîn.'),
(1619, 12, 23, 'verâvedethü-lletî hüve fî beytihâ `an nefsihî vegalleḳati-l\'ebvâbe veḳâlet heyte lek. ḳâle me`âẕe-llâhi innehû rabbî aḥsene meŝvây. innehû lâ yüfliḥu-żżâlimûn.'),
(1620, 12, 24, 'veleḳad hemmet bih. vehemme bihâ. levlâ er raâ bürhâne rabbih. keẕâlike linaṣrife `anhü-ssûe velfaḥşâ\'. innehû min `ibâdine-lmuḫleṣîn.'),
(1621, 12, 25, 'vestebeḳe-lbâbe veḳaddet ḳamîṣahû min dübüriv veelfeyâ seyyidehâ lede-lbâb. ḳâlet mâ cezâü men erâde biehlike sûen illâ ey yüscene ev `aẕâbün elîm.'),
(1622, 12, 26, 'ḳâle hiye râvedetnî `an nefsî veşehide şâhidüm min ehlihâ. in kâne ḳamîṣuhû ḳudde min ḳubülin feṣadeḳat vehüve mine-lkâẕibîn.'),
(1623, 12, 27, 'vein kâne ḳamîṣuhû ḳudde min dübürin fekeẕebet vehüve mine-ṣṣâdiḳîn.'),
(1624, 12, 28, 'felemmâ raâ ḳamîṣahû ḳudde min dübürin ḳâle innehû min keydikünn. inne keydekünne `ażîm.'),
(1625, 12, 29, 'yûsüfü a`riḍ `an hâẕâ vestagfirî liẕembik. inneki künti mine-lḫâṭiîn.'),
(1626, 12, 30, 'veḳâle nisvetün fi-lmedîneti-mraetü-l`azîzi türâvidü fetâhâ `an nefsih. ḳad şegafehâ ḥubbâ. innâ lenerâhâ fî ḍalâlim mübîn.'),
(1627, 12, 31, 'felemmâ semi`at bimekrihinne erselet ileyhinne vea`tedet lehünne müttekeev veâtet külle vâḥidetim minhünne sikkînev veḳâleti-ḫruc `aleyhinn. felemmâ raeynâhü ekbernehû veḳaṭṭa`ne eydiyehünne veḳulne ḥâşe lillâhi mâ hâẕâ beşerâ. in hâẕâ illâ melekün kerîm.'),
(1628, 12, 32, 'ḳâlet feẕâlikünne-lleẕî lümtünnenî fîh. veleḳad râvettühû `an nefsihî festa`ṣam. veleil lem yef`al mâ âmüruhû leyüscenenne veleyekûnem mine-ṣṣâgirîn.'),
(1629, 12, 33, 'ḳâle rabbi-ssicnü eḥabbü ileyye mimmâ yed`ûnenî ileyh. veillâ taṣrif `annî keydehünne aṣbü ileyhinne veeküm mine-lcâhilîn.'),
(1630, 12, 34, 'festecâbe lehû rabbühû feṣarafe `anhü keydehünn. innehû hüve-ssemî`u-l`alîm.'),
(1631, 12, 35, 'ŝümme bedâ lehüm mim ba`di mâ raevu-l\'âyâti leyescününnehû ḥattâ ḥîn.'),
(1632, 12, 36, 'vedeḫale me`ahü-ssicne feteyân. ḳâle eḥadühümâ innî erânî a`ṣiru ḫamrâ. veḳâle-l\'âḫaru innî erânî aḥmilü fevḳa ra\'sî ḫubzen te\'külu-ṭṭayru minh. nebbi\'nâ bite\'vîlih. innâ nerâke mine-lmuḥsinîn.'),
(1633, 12, 37, 'ḳâle lâ ye\'tîkümâ ṭa`âmün türzeḳânihî illâ nebbe\'tükümâ bite\'vîlihî ḳable ey ye\'tiyekümâ. ẕâlikümâ mimmâ `allemenî rabbî. innî teraktü millete ḳavmil lâ yü\'minûne billâhi vehüm bil\'âḫirati hüm kâfirûn.'),
(1634, 12, 38, 'vetteba`tü millete âbâî ibrâhîme veisḥâḳa veya`ḳûb. mâ kâne lenâ en nüşrike billâhi min şey\'. ẕâlike min faḍli-llâhi `aleynâ ve`ale-nnâsi velâkinne ekŝera-nnâsi lâ yeşkürûn.'),
(1635, 12, 39, 'yâ ṣâḥibeyi-ssicni eerbâbüm müteferriḳûne ḫayrun emi-llâhü-lvâḥidü-lḳahhâr.'),
(1636, 12, 40, 'mâ ta`büdûne min dûnihî illâ esmâen semmeytümûhâ entüm veâbâüküm mâ enzele-llâhü bihâ min sülṭân. ini-lḥukmü illâ lillâh. emera ellâ ta`büdû illâ iyyâh. ẕâlike-ddînü-lḳayyimü velâkinne ekŝera-nnâsi lâ ya`lemûn.'),
(1637, 12, 41, 'yâ ṣâḥibeyi-ssicni emmâ eḥadükümâ feyesḳî rabbehû ḫamrâ. veemme-l\'âḫaru feyuṣlebü fete\'külu-ṭṭayru mir ra\'sih. ḳuḍiye-l\'emru-lleẕî fîhi testeftiyân.'),
(1638, 12, 42, 'veḳâle lilleẕî żanne ennehû nâcim minhüme-ẕkürnî `inde rabbik. feensâhü-şşeyṭânü ẕikra rabbihî felebiŝe fi-ssicni biḍ`a sinîn.'),
(1639, 12, 43, 'veḳâle-lmelikü innî erâ seb`a beḳarâtin simâniy ye\'külühünne seb`un `icâfüv veseb`a sümbülâtin ḫuḍriv veüḫarâ yâbisât. yâ eyyühe-lmeleü eftûnî fî ru\'yâye in küntüm lirru\'yâ ta`bürûn.'),
(1640, 12, 44, 'ḳâlû aḍgâŝü aḥlâm. vemâ naḥnü bite\'vîli-l\'aḥlâmi bi`âlimîn.'),
(1641, 12, 45, 'veḳâle-lleẕî necâ minhümâ veddekera ba`de ümmetin ene ünebbiüküm bite\'vîlihî feersilûn.'),
(1642, 12, 46, 'yûsüfü eyyühe-ṣṣiddîḳu eftinâ fî seb`i beḳarâtin simâniy ye\'külühünne seb`un `icâfüv veseb`i sümbülâtin ḫuḍriv veüḫarâ yâbisâtil le`allî erci`u ile-nnâsi le`allehüm ya`lemûn.'),
(1643, 12, 47, 'ḳâle tezra`ûne seb`a sinîne deebâ. femâ ḥaṣattüm feẕerûhü fî sümbülihî illâ ḳalîlem mimmâ te\'külûn.'),
(1644, 12, 48, 'ŝümme ye\'tî mim ba`di ẕâlike seb`un şidâdüy ye\'külne mâ ḳaddemtüm lehünne illâ ḳalîlem mimmâ tuḥṣinûn.'),
(1645, 12, 49, 'ŝümme ye\'tî mim ba`di ẕâlike `âmün fîhi yügâŝü-nnâsü vefîhi ya`ṣirûn.'),
(1646, 12, 50, 'veḳâle-lmelikü-\'tûnî bih. felemmâ câehü-rrasûlü ḳâle-rci` ilâ rabbike fes\'elhü mâ bâlü-nnisveti-llâtî ḳaṭṭa`ne eydiyehünn. inne rabbî bikeydihinne `alîm.'),
(1647, 12, 51, 'ḳâle mâ ḫaṭbükünne iẕ râvettünne yûsüfe `an nefsih. ḳulne ḥâşe lillâhi mâ `alimnâ `aleyhi min sû\'. ḳâleti-mraetü-l`azîzi-l\'âne ḥaṣḥaṣa-lḥaḳḳ. ene râvettühû `an nefsihî veinnehû lemine-ṣṣâdiḳîn.'),
(1648, 12, 52, 'ẕâlike liya`leme ennî lem eḫunhü bilgaybi veenne-llâhe lâ yehdî keyde-lḫâinîn.'),
(1649, 12, 53, 'vemâ überriü nefsî. inne-nnefse leemmâratüm bissûi illâ mâ raḥime rabbî. inne rabbî gafûrur raḥîm.'),
(1650, 12, 54, 'veḳâle-lmelikü-\'tûnî bihî estaḫliṣhü linefsî. felemmâ kellemehû ḳâle inneke-lyevme ledeynâ mekînün emîn.'),
(1651, 12, 55, 'ḳâle-c`alnî `alâ ḫazâini-l\'arḍ. innî ḥafîżun `alîm.'),
(1652, 12, 56, 'vekeẕâlike mekkennâ liyûsüfe fi-l\'arḍ. yetebevveü minhâ ḥayŝü yeşâ\'. nüṣîbü biraḥmetinâ men neşâü velâ nüḍî`u ecra-lmuḥsinîn.'),
(1653, 12, 57, 'veleecru-l\'âḫirati ḫayrul lilleẕîne âmenû vekânû yetteḳûn.'),
(1654, 12, 58, 'vecâe iḫvetü yûsüfe fedeḫalû `aleyhi fe`arafehüm vehüm lehû münkirûn.'),
(1655, 12, 59, 'velemmâ cehhezehüm bicehâzihim ḳâle-\'tûnî bieḫil leküm min ebîküm. elâ teravne ennî ûfi-lkeyle veenâ ḫayru-lmünzilîn.'),
(1656, 12, 60, 'feil lem te\'tûnî bihî felâ keyle leküm `indî velâ taḳrabûn.'),
(1657, 12, 61, 'ḳâlû senürâvidü `anhü ebâhü veinnâ lefâ`ilûn.'),
(1658, 12, 62, 'veḳâle lifityânihi-c`alû biḍâ`atehüm fî riḥâlihim le`allehüm ya`rifûnehâ iẕe-nḳalebû ilâ ehlihim le`allehüm yerci`ûn.'),
(1659, 12, 63, 'felemmâ race`û ilâ ebîhim ḳâlû yâ ebânâ müni`a minne-lkeylü feersil me`anâ eḫânâ nektel veinnâ lehû leḥâfiżûn.'),
(1660, 12, 64, 'ḳâle hel âmenüküm `aleyhi illâ kemâ emintüküm `alâ eḫîhi min ḳabl. fellâhü ḫayrun ḥâfiżâ. vehüve erḥamü-rrâḥimîn.'),
(1661, 12, 65, 'velemmâ feteḥû metâ`ahüm vecedû biḍâ`atehüm ruddet ileyhim. ḳâlû yâ ebânâ mâ nebgî. hâẕihî biḍâ`atünâ ruddet ileynâ. venemîru ehlenâ venaḥfeżu eḫânâ venezdâdü keyle be`îr. ẕâlike keylüy yesîr.'),
(1662, 12, 66, 'ḳâle len ürsilehû me`aküm ḥattâ tü\'tûni mevŝiḳam mine-llâhi lete\'tünnenî bihî illâ ey yüḥâṭa biküm. felemmâ âtevhü mevŝiḳahüm ḳâle-llâhü `alâ mâ neḳûlü vekîl.'),
(1663, 12, 67, 'veḳâle yâ benîye lâ tedḫulû mim bâbiv vâḥidiv vedḫulû min ebvâbim müteferriḳah. vemâ ugnî `anküm mine-llâhi min şey\'. ini-lḥukmü illâ lillâh. `aleyhi tevekkelt. ve`aleyhi felyetevekkeli-lmütevekkilûn.'),
(1664, 12, 68, 'velemmâ deḫalû min ḥayŝü emerahüm ebûhüm. mâ kâne yugnî `anhüm mine-llâhi min şey\'in illâ ḥâceten fî nefsi ya`ḳûbe ḳaḍâhâ. veinnehû leẕû `ilmil limâ `allemnâhü velâkinne ekŝera-nnâsi lâ ya`lemûn.'),
(1665, 12, 69, 'velemmâ deḫalû `alâ yûsüfe âvâ ileyhi eḫâhü ḳâle innî ene eḫûke felâ tebteis bimâ kânû ya`melûn.'),
(1666, 12, 70, 'felemmâ cehhezehüm bicehâzihim ce`ale-ssiḳâyete fî raḥli eḫîhi ŝümme eẕẕene müeẕẕinün eyyetühe-l`îru inneküm lesâriḳûn.'),
(1667, 12, 71, 'ḳâlû veaḳbelû `aleyhim mâẕâ tefḳidûn.'),
(1668, 12, 72, 'ḳâlû nefḳidü ṣuvâ`a-lmeliki velimen câe bihî ḥimlü be`îriv veenâ bihî za`îm.'),
(1669, 12, 73, 'ḳâlû tellâhi leḳad `alimtüm mâ ci\'nâ linüfside fi-l\'arḍi vemâ künnâ sâriḳîn.'),
(1670, 12, 74, 'ḳâlû femâ cezâühû in küntüm kâẕibîn.'),
(1671, 12, 75, 'ḳâlû cezâühû mev vucide fî raḥlihî fehüve cezâüh. keẕâlike neczi-żżâlimîn.'),
(1672, 12, 76, 'febedee biev`iyetihim ḳable vi`âi eḫîhi ŝümme-staḫracehâ miv vi`âi eḫîh. keẕâlike kidnâ liyûsüf. mâ kâne liye\'ḫuẕe eḫâhü fî dîni-lmeliki illâ ey yeşâe-llâh. nerfe`u deracâtim men neşâ\'. vefevḳa külli ẕî `ilmin `alîm.'),
(1673, 12, 77, 'ḳâlû iy yesriḳ feḳad seraḳa eḫul lehû min ḳabl. feeserrahâ yûsüfü fî nefsihî velem yübdihâ lehüm ḳâle entüm şerrum mekânâ. vellâhü a`lemü bimâ teṣifûn.'),
(1674, 12, 78, 'ḳâlû yâ eyyühe-l`azîzü inne lehû eben şeyḫan kebîran feḫuẕ eḥadenâ mekâneh. innâ nerâke mine-lmuḥsinîn.'),
(1675, 12, 79, 'ḳâle me`âẕe-llâhi en ne\'ḫuẕe illâ mev vecednâ metâ`anâ `indehû innâ iẕel leżâlimûn.'),
(1676, 12, 80, 'felemme-stey\'esû minhü ḫaleṣû neciyyâ. ḳâle kebîruhüm elem ta`lemû enne ebâküm ḳad eḫaẕe `aleyküm mevŝiḳam mine-llâhi vemin ḳablü mâ ferraṭtüm fî yûsüf. felen ebraḥa-l\'arḍa ḥattâ ye\'ẕene lî ebî ev yaḥküme-llâhü lî. vehüve ḫayru-lḥâkimîn.'),
(1677, 12, 81, 'irci`û ilâ ebîküm feḳûlû yâ ebânâ inne-bneke seraḳ. vemâ şehidnâ illâ bimâ `alimnâ vemâ künnâ lilgaybi ḥâfiżîn.'),
(1678, 12, 82, 'ves\'eli-lḳaryete-lletî künnâ fîhâ vel`îra-lletî aḳbelnâ fîhâ. veinnâ leṣâdiḳûn.'),
(1679, 12, 83, 'ḳâle bel sevvelet leküm enfüsüküm emrâ. feṣabrun cemîl. `asa-llâhü ey ye\'tiyenî bihim cemî`â. innehû hüve-l`alîmü-lḥakîm.'),
(1680, 12, 84, 'vetevellâ `anhüm veḳâle yâ esefâ `alâ yûsüfe vebyeḍḍat `aynâhü mine-lḥuzni fehüve keżîm.'),
(1681, 12, 85, 'ḳâlû tellâhi tefteü teẕküru yûsüfe ḥattâ tekûne ḥaraḍan ev tekûne mine-lhâlikîn.'),
(1682, 12, 86, 'ḳâle innemâ eşkû beŝŝî veḥuznî ile-llâhi vea`lemü mine-llâhi mâ lâ ta`lemûn.'),
(1683, 12, 87, 'yâ benîye-ẕhebû feteḥassesû miy yûsüfe veeḫîhi velâ tey\'esû mir ravḥi-llâh. innehû lâ yey\'esü mir ravḥi-llâhi ille-lḳavmü-lkâfirûn.'),
(1684, 12, 88, 'felemmâ deḫalû `aleyhi ḳâlû yâ eyyühe-l`azîzü messenâ veehlene-ḍḍurru veci\'nâ bibiḍâ`atim müzcâtin feevfi lene-lkeyle veteṣaddaḳ `aleynâ. inne-llâhe yeczi-lmüteṣaddiḳîn.'),
(1685, 12, 89, 'ḳâle hel `alimtüm mâ fe`altüm biyûsüfe veeḫîhi iẕ entüm câhilûn.'),
(1686, 12, 90, 'ḳâlû einneke leente yûsüf. ḳâle ene yûsüfü vehâẕâ eḫî. ḳad menne-llâhü `aleynâ. innehû mey yetteḳi veyaṣbir feinne-llâhe lâ yüḍî`u ecra-lmuḥsinîn.'),
(1687, 12, 91, 'ḳâlû tellâhi leḳad âŝerake-llâhü `aleynâ vein künnâ leḫâṭiîn.'),
(1688, 12, 92, 'ḳâle lâ teŝrîbe `aleykümü-lyevm. yagfiru-llâhü leküm. vehüve erḥamü-rrâḥimîn.'),
(1689, 12, 93, 'iẕhebû biḳamîṣî hâẕâ feelḳûhü `alâ vechi ebî ye\'ti beṣîrâ. ve\'tûnî biehliküm ecme`în.'),
(1690, 12, 94, 'velemmâ feṣaleti-l`îru ḳâle ebûhüm innî leecidü rîḥa yûsüfe levlâ en tüfennidûn.'),
(1691, 12, 95, 'ḳâlû tellâhi inneke lefî ḍalâlike-lḳadîm.'),
(1692, 12, 96, 'felemmâ en câe-lbeşîru elḳâhü `alâ vechihî fertedde beṣîrâ. ḳâle elem eḳul leküm innî a`lemü mine-llâhi mâ lâ ta`lemûn.'),
(1693, 12, 97, 'ḳâlû yâ ebâne-stagfir lenâ ẕünûbenâ innâ künnâ ḫâṭiîn.'),
(1694, 12, 98, 'ḳâle sevfe estagfiru leküm rabbî. innehû hüve-lgafûru-rraḥîm.'),
(1695, 12, 99, 'felemmâ deḫalû `alâ yûsüfe âvâ ileyhi ebeveyhi veḳâle-dḫulû miṣra in şâe-llâhü âminîn.'),
(1696, 12, 100, 'verafe`a ebeveyhi `ale-l`arşi veḫarrû lehû süccedâ. veḳâle yâ ebeti hâẕâ te\'vîlü ru\'yâye min ḳabl. ḳad ce`alehâ rabbî ḥaḳḳâ. veḳad aḥsene bî iẕ aḫracenî mine-ssicni vecâe biküm mine-lbedvi mim ba`di en nezega-şşeyṭânü beynî vebeyne iḫvetî. inne rabbî leṭîfül limâ yeşâ\'. innehû hüve-l`alîmü-lḥakîm.'),
(1697, 12, 101, 'rabbi ḳad âteytenî mine-lmülki ve`allemtenî min te\'vîli-l\'eḥâdîŝ. fâṭira-ssemâvâti vel\'arḍi ente veliyyî fi-ddünyâ vel\'âḫirah. teveffenî müslimev veelḥiḳnî biṣṣâliḥîn.'),
(1698, 12, 102, 'ẕâlike min embâi-lgaybi nûḥîhi ileyk. vemâ künte ledeyhim iẕ ecme`û emrahüm vehüm yemkürûn.'),
(1699, 12, 103, 'vemâ ekŝeru-nnâsi velev ḥaraṣte bimü\'minîn.'),
(1700, 12, 104, 'vemâ tes\'elühüm `aleyhi min ecr. in hüve illâ ẕikrul lil`âlemîn.'),
(1701, 12, 105, 'vekeeyyim min âyetin fi-ssemâvâti vel\'arḍi yemürrûne `aleyhâ vehüm `anhâ mü`riḍûn.'),
(1702, 12, 106, 'vemâ yü\'minü ekŝeruhüm billâhi illâ vehüm müşrikûn.'),
(1703, 12, 107, 'efeeminû en te\'tiyehüm gâşiyetüm min `aẕâbi-llâhi ev te\'tiyehümü-ssâ`atü bagtetev vehüm lâ yeş`urûn.'),
(1704, 12, 108, 'ḳul hâẕihî sebîlî ed`û ile-llâhi `alâ beṣîratin ene vemeni-ttebe`anî. vesübḥâne-llâhi vemâ ene mine-lmüşrikîn.'),
(1705, 12, 109, 'vemâ erselnâ min ḳablike illâ ricâlen nûḥî ileyhim min ehli-lḳurâ. efelem yesîrû fi-l\'arḍi feyenżurû keyfe kâne `âḳibetü-lleẕîne min ḳablihim. veledâru-l\'âḫirati ḫayrul lilleẕîne-tteḳav. efelâ ta`ḳilûn.'),
(1706, 12, 110, 'ḥattâ iẕe-stey\'ese-rrusülü veżannû ennehüm ḳad küẕibû câehüm naṣrunâ fenücciye men neşâ\'. velâ yüraddü be\'sünâ `ani-lḳavmi-lmücrimîn.'),
(1707, 12, 111, 'leḳad kâne fî ḳaṣaṣihim `ibratül liüli-l\'elbâb. mâ kâne ḥadîŝey yüfterâ velâkin taṣdîḳa-lleẕî beyne yedeyhi vetefṣîle külli şey\'iv vehüdev veraḥmetel liḳavmiy yü\'minûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'elif-lâm-mîm-râ. tilke âyâtü-lkitâb. velleẕî ünzile ileyke mir rabbike-lḥaḳḳu velâkinne ekŝera-nnâsi lâ yü\'minûn.'),
(1709, 13, 2, 'allâhü-lleẕî rafe`a-ssemâvâti bigayri `amedin teravnehâ ŝümme-stevâ `ale-l`arşi veseḫḫara-şşemse velḳamer. küllüy yecrî liecelim müsemmâ. yüdebbiru-l\'emra yüfeṣṣilü-l\'âyâti le`alleküm biliḳâi rabbiküm tûḳinûn.'),
(1710, 13, 3, 'vehüve-lleẕî medde-l\'arḍa vece`ale fîhâ ravâsiye veenhârâ. vemin külli-ŝŝemerâti ce`ale fîhâ zevceyni-ŝneyni yugşi-lleyle-nnehâr. inne fî ẕâlike leâyâtil liḳavmiy yetefekkerûn.'),
(1711, 13, 4, 'vefi-l\'arḍi ḳiṭa`um mütecâvirâtüv vecennâtüm min a`nâbiv vezer`uv veneḫîlün ṣinvânüv vegayru ṣinvâniy yüsḳâ bimâiv vâḥid. venüfeḍḍilü ba`ḍahâ `alâ ba`ḍin fi-l\'ükül. inne fî ẕâlike leâyâtil liḳavmiy ya`ḳilûn.'),
(1712, 13, 5, 'vein ta`ceb fe`acebün ḳavlühüm eiẕâ künnâ türâben einnâ lefî ḫalḳin cedîd. ülâike-lleẕîne keferû birabbihim. veülâike-l\'aglâlü fî a`nâḳihim. veülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(1713, 13, 6, 'veyesta`cilûneke bisseyyieti ḳable-lḥaseneti veḳad ḫalet min ḳablihimü-lmeŝülât. veinne rabbeke leẕû magfiratil linnâsi `alâ żulmihim. veinne rabbeke leşedîdü-l`iḳâb.'),
(1714, 13, 7, 'veyeḳûlü-lleẕîne keferû levlâ ünzile `aleyhi âyetüm mir rabbih. innemâ ente münẕiruv velikülli ḳavmin hâd.'),
(1715, 13, 8, 'allâhü ya`lemü mâ taḥmilü küllü ünŝâ vemâ tegîḍu-l\'erḥâmü vemâ tezdâd. veküllü şey\'in `indehû bimiḳdâr.'),
(1716, 13, 9, '`âlimü-lgaybi veşşehâdeti-lkebîru-lmüte`âl.'),
(1717, 13, 10, 'sevâüm minküm men eserra-lḳavle vemen cehera bihî vemen hüve müstaḫfim billeyli vesâribüm binnehâr.'),
(1718, 13, 11, 'lehû mü`aḳḳibâtüm mim beyni yedeyhi vemin ḫalfihî yaḥfeżûnehû min emri-llâh. inne-llâhe lâ yügayyiru mâ biḳavmin ḥattâ yügayyirû mâ bienfüsihim. veiẕâ erâde-llâhü biḳavmin sûen felâ meradde leh. vemâ lehüm min dûnihî miv vâl.'),
(1719, 13, 12, 'hüve-lleẕî yürîkümü-lberḳa ḫavfev veṭame`av veyünşiü-sseḥâbe-ŝŝiḳâl.'),
(1720, 13, 13, 'veyüsebbiḥu-rra`dü biḥamdihî velmelâiketü min ḫîfetih. veyürsilu-ṣṣavâ`iḳa feyüṣîbü bihâ mey yeşâü vehüm yücâdilûne fi-llâh. vehüve şedîdü-lmiḥâl.'),
(1721, 13, 14, 'lehû da`vetü-lḥaḳḳ. velleẕîne yed`ûne min dûnihî lâ yestecîbûne lehüm bişey\'in illâ kebâsiṭi keffeyhi ile-lmâi liyeblüga fâhü vemâ hüve bibâligih. vemâ dü`âü-lkâfirîne illâ fî ḍalâl.'),
(1722, 13, 15, 'velillâhi yescüdü men fi-ssemâvâti vel\'arḍi ṭav`av vekerhev veżilâlühüm bilgudüvvi vel\'eṣâl.'),
(1723, 13, 16, 'ḳul mer rabbü-ssemâvâti vel\'arḍ. ḳuli-llâh. ḳul efetteḫaẕtüm min dûnihî evliyâe lâ yemlikûne lienfüsihim nef`av velâ ḍarrâ. ḳul hel yestevi-l\'a`mâ velbeṣîru em hel testevi-żżulümâtü vennûr. em ce`alû lillâhi şürakâe ḫaleḳû keḫalḳihî feteşâbehe-lḫalḳu `aleyhim. ḳuli-llâhü ḫâliḳu külli şey\'iv vehüve-lvâḥidü-lḳahhâr.'),
(1724, 13, 17, 'enzele mine-ssemâi mâen fesâlet evdiyetüm biḳaderihâ faḥtemele-sseylü zebeder râbiyâ. vemimmâ yûḳidûne `aleyhi fi-nnâri-btigâe ḥilyetin ev metâ`in zebedüm miŝlüh. keẕâlike yaḍribü-llâhü-lḥaḳḳa velbâṭil. feemme-zzebedü feyeẕhebü cüfââ. veemmâ mâ yenfe`u-nnâse feyemküŝü fi-l\'arḍ. keẕâlike yaḍribü-llâhü-l\'emŝâl.'),
(1725, 13, 18, 'lilleẕîne-stecâbû lirabbihimü-lḥusnâ. velleẕîne lem yestecîbû lehû lev enne lehüm mâ fi-l\'arḍi cemî`av vemiŝlehû me`ahû leftedev bih. ülâike lehüm sûü-lḥisâbi veme\'vâhüm cehennem. vebi\'se-lmihâd.'),
(1726, 13, 19, 'efemey ya`lemü ennemâ ünzile ileyke mir rabbike-lḥaḳḳu kemen hüve a`mâ. innemâ yeteẕekkeru ülü-l\'elbâb.'),
(1727, 13, 20, 'elleẕîne yûfûne bi`ahdi-llâhi velâ yenḳuḍûne-lmîŝâḳa.'),
(1728, 13, 21, 'velleẕîne yeṣilûne mâ emera-llâhü bihî ey yûṣale veyaḫşevne rabbehüm veyeḫâfûne sûe-lḥisâb.'),
(1729, 13, 22, 'velleẕîne ṣaberü-btigâe vechi rabbihim veeḳâmu-ṣṣalâte veenfeḳû mimmâ razaḳnâhüm sirrav ve`alâniyetev veyedraûne bilḥaseneti-sseyyiete ülâike lehüm `uḳbe-ddâr.'),
(1730, 13, 23, 'cennâtü `adniy yedḫulûnehâ vemen ṣaleḥa min âbâihim veezvâcihim veẕürriyyâtihim velmelâiketü yedḫulûne `aleyhim min külli bâb.'),
(1731, 13, 24, 'selâmün `aleyküm bimâ ṣabertüm feni`me `uḳbe-ddâr.'),
(1732, 13, 25, 'velleẕîne yenḳuḍûne `ahde-llâhi mim ba`di mîŝâḳihî veyaḳṭa`ûne mâ emera-llâhü bihî ey yûṣale veyüfsidûne fi-l\'arḍi ülâike lehümü-lla`netü velehüm sûü-ddâr.'),
(1733, 13, 26, 'allâhü yebsüṭu-rrizḳa limey yeşâü veyaḳdir. veferiḥû bilḥayâti-ddünyâ. veme-lḥayâtü-ddünyâ fi-l\'âḫirati illâ metâ`.'),
(1734, 13, 27, 'veyeḳûlü-lleẕîne keferû levlâ ünzile `aleyhi âyetüm mir rabbih. ḳul inne-llâhe yüḍillü mey yeşâü veyehdî ileyhi men enâb.'),
(1735, 13, 28, 'elleẕîne âmenû vetaṭmeinnü ḳulûbühüm biẕikri-llâh. elâ biẕikri-llâhi taṭmeinnü-lḳulûb.'),
(1736, 13, 29, 'elleẕîne âmenû ve`amilu-ṣṣâliḥâti ṭûbâ lehüm veḥusnü meâb.'),
(1737, 13, 30, 'keẕâlike erselnâke fî ümmetin ḳad ḫalet min ḳablihâ ümemül litetlüve `aleyhimü-lleẕî evḥaynâ ileyke vehüm yekfürûne birraḥmân. ḳul hüve rabbî lâ ilâhe illâ hû. `aleyhi tevekkeltü veileyhi metâb.'),
(1738, 13, 31, 'velev enne ḳur\'ânen süyyirat bihi-lcibâlü ev ḳuṭṭi`at bihi-l\'arḍu ev küllime bihi-lmevtâ. bel lillâhi-l\'emru cemî`â. efelem yey\'esi-lleẕîne âmenû el lev yeşâü-llâhü lehede-nnâse cemî`â. velâ yezâlü-lleẕîne keferû tüṣîbühüm bimâ ṣane`û ḳâri`atün ev teḥullü ḳarîbem min dârihim ḥattâ ye\'tiye va`dü-llâh. inne-llâhe lâ yuḫlifü-lmî`âd.'),
(1739, 13, 32, 'veleḳadi-stühzie birusülim min ḳablike feemleytü lilleẕîne keferû ŝümme eḫaẕtühüm. fekeyfe kâne `iḳâb.'),
(1740, 13, 33, 'efemen hüve ḳâimün `alâ külli nefsim bimâ kesebet. vece`alû lillâhi şürakâ\'. ḳul semmûhüm. em tünebbiûnehû bimâ lâ ya`lemü fi-l\'arḍi em biżâhirim mine-lḳavl. bel züyyine lilleẕîne keferû mekruhüm veṣuddû `ani-ssebîl. vemey yuḍlili-llâhü femâ lehû min hâd.'),
(1741, 13, 34, 'lehüm `aẕâbün fi-lḥayâti-ddünyâ vele`aẕâbü-l\'âḫirati eşeḳḳ. vemâ lehüm mine-llâhi miv vâḳ.'),
(1742, 13, 35, 'meŝelü-lcenneti-lletî vu`ide-lmütteḳûn. tecrî min taḥtihe-l\'enhâr. ükülühâ dâimüv veżillühâ. tilke `uḳbe-lleẕîne-tteḳav. ve`uḳbe-lkâfirîne-nnâr.'),
(1743, 13, 36, 'velleẕîne âteynâhümü-lkitâbe yefraḥûne bimâ ünzile ileyke vemine-l\'aḥzâbi mey yünkiru ba`ḍah. ḳul innemâ ümirtü en a`büde-llâhe velâ üşrike bih. ileyhi ed`û veileyhi meâb.'),
(1744, 13, 37, 'vekeẕâlike enzelnâhü ḥukmen `arabiyyâ. veleini-tteba`te ehvâehüm ba`de mâ câeke mine-l`ilmi mâ leke mine-llâhi miv veliyyiv velâ vâḳ.'),
(1745, 13, 38, 'veleḳad erselnâ rusülem min ḳablike vece`alnâ lehüm ezvâcev veẕürriyyeh. vemâ kâne lirasûlin ey ye\'tiye biâyetin illâ biiẕni-llâh. likülli ecelin kitâb.'),
(1746, 13, 39, 'yemḥü-llâhü mâ yeşâü veyüŝbit. ve`indehû ümmü-lkitâb.'),
(1747, 13, 40, 'veim mâ nüriyenneke ba`ḍa-lleẕî ne`idühüm ev neteveffeyenneke feinnemâ `aleyke-lbelâgu ve`aleyne-lḥisâb.'),
(1748, 13, 41, 'evelem yerav ennâ ne\'ti-l\'arḍa nenḳuṣuhâ min aṭrâfihâ. vellâhü yaḥkümü lâ mü`aḳḳibe liḥukmih. vehüve serî`u-lḥisâb.'),
(1749, 13, 42, 'veḳad mekera-lleẕîne min ḳablihim felillâhi-lmekru cemî`â. ya`lemü mâ teksibü küllü nefs. veseya`lemü-lküffâru limen `uḳbe-ddâr.'),
(1750, 13, 43, 'veyeḳûlü-lleẕîne keferû leste mürselâ. ḳul kefâ billâhi şehîdem beynî vebeyneküm vemen `indehû `ilmü-lkitâb.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'elif-lâm-râ. kitâbün enzelnâhü ileyke lituḫrice-nnâse mine-żżulümâti ile-nnûri biiẕni rabbihim ilâ ṣirâṭi-l`azîzi-lḥamîd.'),
(1752, 14, 2, 'allâhi-lleẕî lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. veveylül lilkâfirîne min `aẕâbin şedîd.'),
(1753, 14, 3, 'elleẕîne yesteḥibbûne-lḥayâte-ddünyâ `ale-l\'âḫirati veyeṣuddûne `an sebîli-llâhi veyebgûnehâ `ivecâ. ülâike fî ḍalâlim be`îd.'),
(1754, 14, 4, 'vemâ erselnâ mir rasûlin illâ bilisâni ḳavmihî liyübeyyine lehüm. feyüḍillü-llâhü mey yeşâü veyehdî mey yeşâ\'. vehüve-l`azîzü-lḥakîm.'),
(1755, 14, 5, 'veleḳad erselnâ mûsâ biâyâtinâ en aḫric ḳavmeke mine-żżulümâti ile-nnûri veẕekkirhüm bieyyâmi-llâh. inne fî ẕâlike leâyâtil likülli ṣabbârin şekûr.'),
(1756, 14, 6, 'veiẕ ḳâle mûsâ liḳavmihi-ẕkürû ni`mete-llâhi `aleyküm iẕ encâküm min âli fir`avne yesûmûneküm sûe-l`aẕâbi veyüẕebbiḥûne ebnâeküm veyestaḥyûne nisâeküm. vefî ẕâliküm belâüm mir rabbiküm `ażîm.'),
(1757, 14, 7, 'veiẕ teeẕẕene rabbüküm lein şekertüm leezîdenneküm velein kefertüm inne `aẕâbî leşedîd.'),
(1758, 14, 8, 'veḳâle mûsâ in tekfürû entüm vemen fi-l\'arḍi cemî`an feinne-llâhe leganiyyün ḥamîd.'),
(1759, 14, 9, 'elem ye\'tiküm nebeü-lleẕîne min ḳabliküm ḳavmi nûḥiv ve`âdiv veŝemûd. velleẕîne mim ba`dihim. lâ ya`lemühüm ille-llâh. câethüm rusülühüm bilbeyyinâti feraddû eydiyehüm fî efvâhihim veḳâlû innâ kefernâ bimâ ürsiltüm bihî veinnâ lefî şekkim mimmâ ted`ûnenâ ileyhi mürîbün.'),
(1760, 14, 10, 'ḳâlet rusülühüm efi-llâhi şekkün fâṭiri-ssemâvâti vel\'arḍ. yed`ûküm liyagfira leküm min ẕünûbiküm veyüeḫḫiraküm ilâ ecelim müsemmâ. ḳâlû in entüm illâ beşerum miŝlünâ. türîdûne en teṣuddûnâ `ammâ kâne ya`büdü âbâünâ fe\'tûnâ bisülṭânim mübîn.'),
(1761, 14, 11, 'ḳâlet lehüm rusülühüm in naḥnü illâ beşerum miŝlüküm velâkinne-llâhe yemünnü `alâ mey yeşâü min `ibâdih. vemâ kâne lenâ en ne\'tiyeküm bisülṭânin illâ biiẕni-llâh. ve`ale-llâhi felyetevekkeli-lmü\'minûn.'),
(1762, 14, 12, 'vemâ lenâ ellâ netevekkele `ale-llâhi veḳad hedânâ sübülenâ. velenaṣbiranne `alâ mâ âẕeytümûnâ. ve`ale-llâhi felyetevekkeli-lmütevekkilûn.'),
(1763, 14, 13, 'veḳâle-lleẕîne keferû lirusülihim lenuḫricenneküm min arḍinâ ev lete`ûdünne fî milletinâ. feevḥâ ileyhim rabbühüm lenühlikenne-żżâlimîn.'),
(1764, 14, 14, 'velenüskinennekümü-l\'arḍa mim ba`dihim. ẕâlike limen ḫâfe meḳâmî veḫâfe ve`îd.'),
(1765, 14, 15, 'vestefteḥû veḫâbe küllü cebbârin `anîd.'),
(1766, 14, 16, 'miv verâihî cehennemü veyüsḳâ mim mâin ṣadîd.'),
(1767, 14, 17, 'yetecerra`uhû velâ yekâdü yüsîguhû veye\'tîhi-lmevtü min külli mekâniv vemâ hüve bimeyyit. vemiv verâihî `aẕâbün galîż.'),
(1768, 14, 18, 'meŝelü-lleẕîne keferû birabbihim a`mâlühüm keramâdin-şteddet bihi-rrîḥu fî yevmin `âṣif. lâ yaḳdirûne mimmâ kesebû `alâ şey\'. ẕâlike hüve-ḍḍalâlü-lbe`îd.'),
(1769, 14, 19, 'elem tera enne-llâhe ḫaleḳa-ssemâvâti vel\'arḍa bilḥaḳḳ. iy yeşe\' yüẕhibküm veye\'ti biḫalḳin cedîd.'),
(1770, 14, 20, 'vemâ ẕâlike `ale-llâhi bi`azîz.'),
(1771, 14, 21, 'veberazû lillâhi cemî`an feḳâle-ḍḍu`afâü lilleẕîne-stekberû innâ künnâ leküm tebe`an fehel entüm mugnûne `annâ min `aẕâbi-llâhi min şey\'. ḳâlû lev hedâne-llâhü lehedeynâküm. sevâün `aleynâ ecezi`nâ em ṣabernâ mâ lenâ mim meḥîṣ.'),
(1772, 14, 22, 'veḳâle-şşeyṭânü lemmâ ḳuḍiye-l\'emru inne-llâhe ve`adeküm va`de-lḥaḳḳi veve`attüküm feaḫleftüküm. vemâ kâne liye `aleyküm min sülṭânin illâ en de`avtüküm festecebtüm lî. felâ telûmûnî velûmû enfüseküm. mâ ene bimuṣriḫiküm vemâ entüm bimuṣriḫîy. innî kefertü bimâ eşraktümûni min ḳabl. inne-żżâlimîne lehüm `aẕâbün elîm.'),
(1773, 14, 23, 'veüdḫile-lleẕîne âmenû ve`amilu-ṣṣâliḥâti cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ biiẕni rabbihim. teḥiyyetühüm fîhâ selâm.'),
(1774, 14, 24, 'elem tera keyfe ḍarabe-llâhü meŝelen kelimeten ṭayyibeten keşeceratin ṭayyibetin aṣlühâ ŝâbitüv vefer`uhâ fi-ssemâ\'.'),
(1775, 14, 25, 'tü\'tî ükülehâ külle ḥînim biiẕni rabbihâ. veyaḍribü-llâhü-l\'emŝâle linnâsi le`allehüm yeteẕekkerûn.'),
(1776, 14, 26, 'vemeŝelü kelimetin ḫabîŝetin keşeceratin ḫabîŝetin-ctüŝŝet min fevḳi-l\'arḍi mâ lehâ min ḳarâr.'),
(1777, 14, 27, 'yüŝebbitü-llâhü-lleẕîne âmenû bilḳavli-ŝŝâbiti fi-lḥayâti-ddünyâ vefi-l\'âḫirah. veyüḍillü-llâhu-żżâlimîne veyef`alü-llâhü mâ yeşâ\'.'),
(1778, 14, 28, 'elem tera ile-lleẕîne beddelû ni`mete-llâhi küfrav veeḥallû ḳavmehüm dâra-lbevâr.'),
(1779, 14, 29, 'cehennem. yaṣlevnehâ. vebi\'se-lḳarâr.'),
(1780, 14, 30, 'vece`alû lillâhi endâdel liyüḍillû `an sebîlih. ḳul temette`û feinne meṣîraküm ile-nnâr.'),
(1781, 14, 31, 'ḳul li`ibâdiye-lleẕîne âmenû yüḳîmu-ṣṣalâte veyünfiḳû mimmâ razaḳnâhüm sirrav ve`alâniyetem min ḳabli ey ye\'tiye yevmül lâ bey`un fîhi velâ ḫilâl.'),
(1782, 14, 32, 'allâhü-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa veenzele mine-ssemâi mâen feaḫrace bihî mine-ŝŝemerâti rizḳal leküm. veseḫḫara lekümü-lfülke litecriye fi-lbaḥri biemrih. veseḫḫara lekümü-l\'enhâr.'),
(1783, 14, 33, 'veseḫḫara lekümü-şşemse velḳamera dâibeyn. veseḫḫara lekümü-lleyle vennehâr.'),
(1784, 14, 34, 'veâtâküm min külli mâ seeltümûh. vein te`uddû ni`mete-llâhi lâ tuḥṣûhâ. inne-l\'insâne leżalûmün keffâr.'),
(1785, 14, 35, 'veiẕ ḳâle ibrâhîmü rabbi-c`al hâẕe-lbelede âminev vecnübnî vebenîye en na`büde-l\'aṣnâm.'),
(1786, 14, 36, 'rabbi innehünne aḍlelne keŝîram mine-nnâs. femen tebi`anî feinnehû minnî. vemen `aṣânî feinneke gafûrur raḥîm.'),
(1787, 14, 37, 'rabbenâ innî eskentü min ẕürriyyetî bivâdin gayri ẕî zer`in `inde beytike-lmüḥarrami rabbenâ liyüḳîmu-ṣṣalâte fec`al ef\'idetem mine-nnâsi tehvî ileyhim verzuḳhüm mine-ŝŝemerâti le`allehüm yeşkürûn.'),
(1788, 14, 38, 'rabbenâ inneke ta`lemü mâ nuḫfî vemâ nü`lin. vemâ yaḫfâ `ale-llâhi min şey\'in fi-l\'arḍi velâ fi-ssemâ\'.'),
(1789, 14, 39, 'elḥamdü lillâhi-lleẕî vehebe lî `ale-lkiberi ismâ`île veisḥâḳ. inne rabbî lesemî`u-ddü`â\'.'),
(1790, 14, 40, 'rabbi-c`alnî müḳîme-ṣṣalâti vemin ẕürriyyetî. rabbenâ veteḳabbel dü`â\'.'),
(1791, 14, 41, 'rabbene-gfir lî velivâlideyye velilmü\'minîne yevme yeḳûmü-lḥisâb.'),
(1792, 14, 42, 'velâ taḥsebenne-llâhe gâfilen `ammâ ya`melu-żżâlimûne. innemâ yü\'eḫḫiruhüm liyevmin teşḫaṣu fîhi-l\'ebṣâr.'),
(1793, 14, 43, 'mühti`îne muḳni`î ruûsihim lâ yerteddü ileyhim ṭarfühüm. veef\'idetühüm hevâ\'.'),
(1794, 14, 44, 'veenẕiri-nnâse yevme ye\'tîhimü-l`aẕâbü feyeḳûlü-lleẕîne żalemû rabbenâ eḫḫirnâ ilâ ecelin ḳarîbin nücib da`veteke venettebi`i-rrusül. evelem tekûnû aḳsemtüm min ḳablü mâ leküm min zevâl.'),
(1795, 14, 45, 'vesekentüm fî mesâkini-lleẕîne żalemû enfüsehüm vetebeyyene leküm keyfe fe`alnâ bihim veḍarabnâ lekümü-l\'emŝâl.'),
(1796, 14, 46, 'veḳad mekerû mekrahüm ve`inde-llâhi mekruhüm. vein kâne mekruhüm litezûle minhü-lcibâl.'),
(1797, 14, 47, 'felâ taḥsebenne-llâhe muḫlife va`dihî rusüleh. inne-llâhe `azîzün ẕü-ntiḳâm.'),
(1798, 14, 48, 'yevme tübeddelü-l\'arḍu gayra-l\'arḍi vessemâvâtü veberazû lillâhi-lvâḥidi-lḳahhâr.'),
(1799, 14, 49, 'vetera-lmücrimîne yevmeiẕim müḳarranîne fi-l\'aṣfâd.'),
(1800, 14, 50, 'serâbîlühüm min ḳaṭirâniv vetagşâ vucûhehümü-nnâr.'),
(1801, 14, 51, 'liyecziye-llâhü külle nefsim mâ kesebet. inne-llâhe serî`u-lḥisâb.'),
(1802, 14, 52, 'hâẕâ belâgul linnâsi veliyünẕerû bihî veliya`lemû ennemâ hüve ilâhüv vâḥidüv veliyeẕẕekkera ülü-l\'elbâb.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'elif-lâm-râ. tilke âyâtü-lkitâbi veḳur\'ânim mübîn.'),
(1804, 15, 2, 'rubemâ yeveddü-lleẕîne keferû lev kânû müslimîn.'),
(1805, 15, 3, 'ẕerhüm ye\'külû veyetemette`û veyülhihimü-l\'emelü fesevfe ya`lemûn.'),
(1806, 15, 4, 'vemâ ehleknâ min ḳaryetin illâ velehâ kitâbüm ma`lûm.'),
(1807, 15, 5, 'mâ tesbiḳu min ümmetin ecelehâ vemâ yeste\'ḫirûn.'),
(1808, 15, 6, 'veḳâlû yâ eyyühe-lleẕî nüzzile `aleyhi-ẕẕikru inneke lemecnûn.'),
(1809, 15, 7, 'lev mâ te\'tînâ bilmelâiketi in künte mine-ṣṣâdiḳîn.'),
(1810, 15, 8, 'mâ nünezzilü-lmelâikete illâ bilḥaḳḳi vemâ kânû iẕem münżarîn.'),
(1811, 15, 9, 'innâ naḥnü nezzelne-ẕẕikra veinnâ lehû leḥâfiżûn.'),
(1812, 15, 10, 'veleḳad erselnâ min ḳablike fî şiye`i-l\'evvelîn.'),
(1813, 15, 11, 'vemâ ye\'tîhim mir rasûlin illâ kânû bihî yestehziûn.'),
(1814, 15, 12, 'keẕâlike neslükühû fî ḳulûbi-lmücrimîn.'),
(1815, 15, 13, 'lâ yü\'minûne bihî veḳad ḫalet sünnetü-l\'evvelîn.'),
(1816, 15, 14, 'velev fetaḥnâ `aleyhim bâbem mine-ssemâi feżallû fîhi ya`rucûn.'),
(1817, 15, 15, 'leḳâlû innemâ sükkirat ebṣârunâ bel naḥnü ḳavmüm mesḥûrûn.'),
(1818, 15, 16, 'veleḳad ce`alnâ fi-ssemâi bürûcev vezeyyennâhâ linnâżirîn.'),
(1819, 15, 17, 'veḥafiżnâhâ min külli şeyṭânir racîm.'),
(1820, 15, 18, 'illâ meni-steraḳa-ssem`a feetbe`ahû şihâbüm mübîn.'),
(1821, 15, 19, 'vel\'arḍa medednâhâ veelḳaynâ fîhâ ravâsiye veembetnâ fîhâ min külli şey\'im mevzûn.'),
(1822, 15, 20, 'vece`alnâ leküm fîhâ me`âyişe vemel lestüm lehû birâziḳîn.'),
(1823, 15, 21, 'veim min şey\'in illâ `indenâ ḫazâinüh. vemâ nünezzilühû illâ biḳaderim ma`lûm.'),
(1824, 15, 22, 'veerselne-rriyâḥa levâḳiḥa feenzelnâ mine-ssemâi mâen feesḳaynâkümûh. vemâ entüm lehû biḫâzinîn.'),
(1825, 15, 23, 'veinnâ lenaḥnü nuḥyî venümîtü venaḥnü-lvâriŝûn.'),
(1826, 15, 24, 'veleḳad `alimne-lmüstaḳdimîne minküm veleḳad `alimne-lmüste\'ḫirîn.'),
(1827, 15, 25, 'veinne rabbeke hüve yaḥşüruhüm. innehû ḥakîmün `alîm.'),
(1828, 15, 26, 'veleḳad ḫalaḳne-l\'insâne min ṣalṣâlim min ḥameim mesnûn.'),
(1829, 15, 27, 'velcânne ḫalaḳnâhü min ḳablü min nâri-ssemûm.'),
(1830, 15, 28, 'veiẕ ḳâle rabbüke lilmelâiketi innî ḫâliḳum beşeram min ṣalṣâlim min ḥameim mesnûn.'),
(1831, 15, 29, 'feiẕâ sevveytühû venefaḫtü fîhi mir rûḥî feḳa`û lehû sâcidîn.'),
(1832, 15, 30, 'fesecede-lmelâiketü küllühüm ecme`ûn.'),
(1833, 15, 31, 'illâ iblîs. ebâ ey yekûne me`a-ssâcidîn.'),
(1834, 15, 32, 'ḳâle yâ iblîsü mâ leke ellâ tekûne me`a-ssâcidîn.'),
(1835, 15, 33, 'ḳâle lem ekül liescüde libeşerin ḫalaḳtehû min ṣalṣâlim min ḥameim mesnûn.'),
(1836, 15, 34, 'ḳâle faḫruc minhâ feinneke racîm.'),
(1837, 15, 35, 'veinne `aleyke-lla`nete ilâ yevmi-ddîn.'),
(1838, 15, 36, 'ḳâle rabbi feenżirnî ilâ yevmi yüb`aŝûn.'),
(1839, 15, 37, 'ḳâle feinneke mine-lmünżarîn.'),
(1840, 15, 38, 'ilâ yevmi-lvaḳti-lma`lûm.'),
(1841, 15, 39, 'ḳâle rabbi bimâ agveytenî leüzeyyinenne lehüm fi-l\'arḍi veleugviyennehüm ecme`în.'),
(1842, 15, 40, 'illâ `ibâdeke minhümü-lmuḫleṣîn.'),
(1843, 15, 41, 'ḳâle hâẕâ ṣirâṭun `aleyye müsteḳîm.'),
(1844, 15, 42, 'inne `ibâdî leyse leke `aleyhim sülṭânün illâ meni-ttebe`ake mine-lgâvîn.'),
(1845, 15, 43, 'veinne cehenneme lemev`idühüm ecme`în.'),
(1846, 15, 44, 'lehâ seb`atü ebvâb. likülli bâbim minhüm cüz\'üm maḳsûm.'),
(1847, 15, 45, 'inne-lmütteḳîne fî cennâtiv ve`uyûn.'),
(1848, 15, 46, 'üdḫulûhâ biselâmin âminîn.'),
(1849, 15, 47, 'veneza`nâ mâ fî ṣudûrihim min gillin iḫvânen `alâ sürurim müteḳâbilîn.'),
(1850, 15, 48, 'lâ yemessühüm fîhâ neṣabüv vemâ hüm minhâ bimuḫracîn.'),
(1851, 15, 49, 'nebbi\' `ibâdî ennî ene-lgafûru-rraḥîm.'),
(1852, 15, 50, 'veenne `aẕâbî hüve-l`aẕâbü-l\'elîm.'),
(1853, 15, 51, 'venebbi\'hüm `an ḍayfi ibrâhîm.'),
(1854, 15, 52, 'iẕ deḫalû `aleyhi feḳâlû selâmâ. ḳâle innâ minküm vecilûn.'),
(1855, 15, 53, 'ḳâlû lâ tevcel innâ nübeşşiruke bigulâmin `alîm.'),
(1856, 15, 54, 'ḳâle ebeşşertümûnî `alâ em messeniye-lkiberu febime tübeşşirûn.'),
(1857, 15, 55, 'ḳâlû beşşernâke bilḥaḳḳi felâ teküm mine-lḳâniṭîn.'),
(1858, 15, 56, 'ḳâle vemey yaḳneṭu mir raḥmeti rabbihî ille-ḍḍâllûn.'),
(1859, 15, 57, 'ḳâle femâ ḫaṭbüküm eyyühe-lmürselûn.'),
(1860, 15, 58, 'ḳâlû innâ ürsilnâ ilâ ḳavmim mücrimîn.'),
(1861, 15, 59, 'illâ âle lûṭ. innâ lemüneccûhüm ecme`în.'),
(1862, 15, 60, 'ille-mraetehû ḳaddernâ innehâ lemine-lgâbirîn.'),
(1863, 15, 61, 'felemmâ câe âle lûṭini-lmürselûn.'),
(1864, 15, 62, 'ḳâle inneküm ḳavmüm münkerûn.'),
(1865, 15, 63, 'ḳâlû bel ci\'nâke bimâ kânû fîhi yemterûn.'),
(1866, 15, 64, 'veeteynâke bilḥaḳḳi veinnâ leṣâdiḳûn.'),
(1867, 15, 65, 'feesri biehlike biḳiṭ`im mine-lleyli vettebi` edbârahüm velâ yeltefit minküm eḥadüv vemḍû ḥayŝü tü\'merûn.'),
(1868, 15, 66, 'veḳaḍaynâ ileyhi ẕâlike-l\'emra enne dâbira hâülâi maḳṭû`um muṣbiḥîn.'),
(1869, 15, 67, 'vecâe ehlü-lmedîneti yestebşirûn.'),
(1870, 15, 68, 'ḳâle inne hâülâi ḍayfî felâ tefḍaḥûn.'),
(1871, 15, 69, 'vetteḳu-llâhe velâ tuḫzûn.'),
(1872, 15, 70, 'ḳâlû evelem nenheke `ani-l`âlemîn.'),
(1873, 15, 71, 'ḳâle hâülâi benâtî in küntüm fâ`ilîn.'),
(1874, 15, 72, 'le`amruke innehüm lefî sekratihim ya`mehûn.'),
(1875, 15, 73, 'feeḫaẕethümu-ṣṣayḥatü müşriḳîn.'),
(1876, 15, 74, 'fece`alnâ `âliyehâ sâfilehâ veemṭarnâ `aleyhim ḥicâratem min siccîl.'),
(1877, 15, 75, 'inne fî ẕâlike leâyâtil lilmütevessimîn.'),
(1878, 15, 76, 'veinnehâ lebisebîlim müḳîm.'),
(1879, 15, 77, 'inne fî ẕâlike leâyetel lilmü\'minîn.'),
(1880, 15, 78, 'vein kâne aṣḥâbü-l\'eyketi leżâlimîn.'),
(1881, 15, 79, 'fenteḳamnâ minhüm. veinnehümâ lebiimâmim mübîn.'),
(1882, 15, 80, 'veleḳad keẕẕebe aṣḥâbü-lḥicri-lmürselîn.'),
(1883, 15, 81, 'veâteynâhüm âyâtinâ fekânû `anhâ mü`riḍîn.'),
(1884, 15, 82, 'vekânû yenḥitûne mine-lcibâli büyûten âminîn.'),
(1885, 15, 83, 'feeḫaẕethümu-ṣṣayḥatü muṣbiḥîn.'),
(1886, 15, 84, 'femâ agnâ `anhüm mâ kânû yeksibûn.'),
(1887, 15, 85, 'vemâ ḫalaḳne-ssemâvâti vel\'arḍa vemâ beynehümâ illâ bilḥaḳḳi. veinne-ssâ`ate leâtiyetün faṣfeḥi-ṣṣafḥa-lcemîl.'),
(1888, 15, 86, 'inne rabbeke hüve-lḫallâḳu-l`alîm.'),
(1889, 15, 87, 'veleḳad âteynâke seb`am mine-lmeŝânî velḳur\'âne-l`ażîm.'),
(1890, 15, 88, 'lâ temüddenne `ayneyke ilâ mâ metta`nâ bihî ezvâcem minhüm velâ taḥzen `aleyhim vaḫfiḍ cenâḥake lilmü\'minîn.'),
(1891, 15, 89, 'veḳul innî ene-nneẕîru-lmübîn.'),
(1892, 15, 90, 'kemâ enzelnâ `ale-lmuḳtesimîn.'),
(1893, 15, 91, 'elleẕîne ce`alü-lḳur\'âne `iḍîn.'),
(1894, 15, 92, 'feverabbike lenes\'elennehüm ecme`în.'),
(1895, 15, 93, '`ammâ kânû ya`melûn.'),
(1896, 15, 94, 'faṣda` bimâ tü\'meru vea`riḍ `ani-lmüşrikîn.'),
(1897, 15, 95, 'innâ kefeynâke-lmüstehziîn.'),
(1898, 15, 96, 'elleẕîne yec`alûne me`a-llâhi ilâhen âḫar. fesevfe ya`lemûn.'),
(1899, 15, 97, 'veleḳad na`lemü enneke yeḍîḳu ṣadruke bimâ yeḳûlûn.'),
(1900, 15, 98, 'fesebbiḥ biḥamdi rabbike veküm mine-ssâcidîn.'),
(1901, 15, 99, 'va`büd rabbeke ḥattâ ye\'tiyeke-lyeḳîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'etâ emru-llâhi felâ testa`cilûh. sübḥânehû vete`âlâ `ammâ yüşrikûn.'),
(1903, 16, 2, 'yünezzilü-lmelâikete birrûḥi min emrihî `alâ mey yeşâü min `ibâdihî en enẕirû ennehû lâ ilâhe illâ ene fetteḳûn.'),
(1904, 16, 3, 'ḫaleḳa-ssemâvâti vel\'arḍa bilḥaḳḳ. te`âlâ `ammâ yüşrikûn.'),
(1905, 16, 4, 'ḫaleḳa-l\'insâne min nuṭfetin feiẕâ hüve ḫaṣîmüm mübîn.'),
(1906, 16, 5, 'vel\'en`âme ḫaleḳahâ. leküm fîhâ dif\'üv vemenâfi`u veminhâ te\'külûn.'),
(1907, 16, 6, 'veleküm fîhâ cemâlün ḥîne türîḥûne veḥîne tesraḥûn.'),
(1908, 16, 7, 'vetaḥmilü eŝḳâleküm ilâ beledil lem tekûnû bâligîhi illâ bişiḳḳi-l\'enfüs. inne rabbeküm leraûfür raḥîm.'),
(1909, 16, 8, 'velḫayle velbigâle velḥamîra literkebûhâ vezîneh. veyaḫlüḳu mâ lâ ta`lemûn.'),
(1910, 16, 9, 've`ale-llâhi ḳaṣdü-ssebîli veminhâ câir. velev şâe lehedâküm ecme`în.'),
(1911, 16, 10, 'hüve-lleẕî enzele mine-ssemâi mâel leküm minhü şerâbüv veminhü şecerun fîhi tüsîmûn.'),
(1912, 16, 11, 'yümbitü leküm bihi-zzer`a vezzeytûne venneḫîle vel\'a`nâbe vemin külli-ŝŝemerât. inne fî ẕâlike leâyetel liḳavmiy yetefekkerûn.'),
(1913, 16, 12, 'veseḫḫara lekümü-lleyle vennehâra veşşemse velḳamer. vennücûmü müseḫḫarâtüm biemrih. inne fî ẕâlike leâyâtil liḳavmiy ya`ḳilûn.'),
(1914, 16, 13, 'vemâ ẕerae leküm fi-l\'arḍi muḫtelifen elvânüh. inne fî ẕâlike leâyetel liḳavmiy yeẕẕekkerûn.'),
(1915, 16, 14, 'vehüve-lleẕî seḫḫara-lbaḥra lite\'külû minhü laḥmen ṭariyyev vetestaḫricû minhü ḥilyeten telbesûnehâ. vetera-lfülke mevâḫira fîhi velitebtegû min faḍlihî vele`alleküm teşkürûn.'),
(1916, 16, 15, 'veelḳâ fi-l\'arḍi ravâsiye en temîde biküm veenhârav vesübülel le`alleküm tehtedûn.'),
(1917, 16, 16, 've`alâmât. vebinnecmi hüm yehtedûn.'),
(1918, 16, 17, 'efemey yaḫlüḳu kemel lâ yaḫlüḳ. efelâ teẕekkerûn.'),
(1919, 16, 18, 'vein te`uddû ni`mete-llâhi lâ tuḥṣûhâ. inne-llâhe legafûrur raḥîm.'),
(1920, 16, 19, 'vellâhü ya`lemü mâ tüsirrûne vemâ tü`linûn.'),
(1921, 16, 20, 'velleẕîne yed`ûne min dûni-llâhi lâ yaḫlüḳûne şey\'ev vehüm yuḫleḳûn.'),
(1922, 16, 21, 'emvâtün gayru aḥyâ\'. vemâ yeş`urûne eyyâne yüb`aŝûn.'),
(1923, 16, 22, 'ilâhüküm ilâhüv vâḥid. felleẕîne lâ yü\'minûne bil\'âḫirati ḳulûbühüm münkiratüv vehüm müstekbirûn.'),
(1924, 16, 23, 'lâ cerame enne-llâhe ya`lemü mâ yüsirrûne vemâ yü`linûn. innehû lâ yüḥibbü-lmüstekbirîn.'),
(1925, 16, 24, 'veiẕâ ḳîle lehüm mâẕâ enzele rabbüküm ḳâlû esâṭîru-l\'evvelîn.'),
(1926, 16, 25, 'liyaḥmilû evzârahüm kâmiletey yevme-lḳiyâmeti vemin evzâri-lleẕîne yüḍillûnehüm bigayri `ilm. elâ sâe mâ yezirûn.'),
(1927, 16, 26, 'ḳad mekera-lleẕîne min ḳablihim feete-llâhü bünyânehüm mine-lḳavâ`idi feḫarra `aleyhimü-ssaḳfü min fevḳihim veetâhümü-l`aẕâbü min ḥayŝü lâ yeş`urûn.'),
(1928, 16, 27, 'ŝümme yevme-lḳiyâmeti yuḫzîhim veyeḳûlü eyne şürakâiye-lleẕîne küntüm tüşâḳḳûne fîhim. ḳâle-lleẕîne ûtü-l`ilme inne-lḫizye-lyevme vessûe `ale-lkâfirîn.'),
(1929, 16, 28, 'elleẕîne teteveffâhümü-lmelâiketü żâlimî enfüsihim. feelḳavu-sseleme mâ künnâ na`melü min sû\'. belâ inne-llâhe `alîmüm bimâ küntüm ta`melûn.'),
(1930, 16, 29, 'fedḫulû ebvâbe cehenneme ḫâlidîne fîhâ. felebi\'se meŝve-lmütekebbirîn.'),
(1931, 16, 30, 'veḳîle lilleẕîne-tteḳav mâẕâ enzele rabbüküm. ḳâlû ḫayrâ. lilleẕîne aḥsenû fî hâẕihi-ddünyâ ḥaseneh. veledâru-l\'âḫirati ḫayr. veleni`me dâru-lmütteḳîn.'),
(1932, 16, 31, 'cennâtü `adniy yedḫulûnehâ tecrî min taḥtihe-l\'enhâru lehüm fîhâ mâ yeşâûn. keẕâlike yeczi-llâhü-lmütteḳîn.'),
(1933, 16, 32, 'elleẕîne teteveffâhümü-lmelâiketü ṭayyibîne yeḳûlûne selâmün `aleykümü-dḫulü-lcennete bimâ küntüm ta`melûn.'),
(1934, 16, 33, 'hel yenżurûne illâ en te\'tiyehümü-lmelâiketü ev ye\'tiye emru rabbik. keẕâlike fe`ale-lleẕîne min ḳablihim. vemâ żalemehümü-llâhü velâkin kânû enfüsehüm yażlimûn.'),
(1935, 16, 34, 'feeṣâbehüm seyyietü mâ `amilû veḥâḳa bihim mâ kânû bihî yestehziûn.'),
(1936, 16, 35, 'veḳâle-lleẕîne eşrakû lev şâe-llâhü mâ `abednâ min dûnihî min şey\'in naḥnü velâ âbâünâ velâ ḥarramnâ min dûnihî min şey\'. keẕâlike fe`ale-lleẕîne min ḳablihim. fehel `ale-rrusüli ille-lbelâgu-lmübîn.'),
(1937, 16, 36, 'veleḳad be`aŝnâ fî külli ümmetir rasûlen eni-`büdü-llâhe vectenibu-ṭṭâgût. feminhüm men hede-llâhü veminhüm men ḥaḳḳat `aleyhi-ḍḍalâleh. fesîrû fi-l\'arḍi fenżurû keyfe kâne `âḳibetü-lmükeẕẕibîn.'),
(1938, 16, 37, 'in taḥriṣ `alâ hüdâhüm feinne-llâhe lâ yehdî mey yüḍillü vemâ lehüm min nâṣirîn.'),
(1939, 16, 38, 'veaḳsemû billâhi cehde eymânihim lâ yeb`aŝü-llâhü mey yemût. belâ va`den `aleyhi ḥaḳḳav velâkinne ekŝera-nnâsi lâ ya`lemûn.'),
(1940, 16, 39, 'liyübeyyine lehümü-lleẕî yaḫtelifûne fîhi veliya`leme-lleẕîne keferû ennehüm kânû kâẕibîn.'),
(1941, 16, 40, 'innemâ ḳavlünâ lişey\'in iẕâ eradnâhü en neḳûle lehû kün feyekûn.'),
(1942, 16, 41, 'velleẕîne hâcerû fi-llâhi mim ba`di mâ żulimû lenübevviennehüm fi-ddünyâ ḥaseneh. veleecru-l\'âḫirati ekber. lev kânû ya`lemûn.'),
(1943, 16, 42, 'elleẕîne ṣaberû ve`alâ rabbihim yetevekkelûn.'),
(1944, 16, 43, 'vemâ erselnâ min ḳablike illâ ricâlen nûḥî ileyhim fes\'elû ehle-ẕẕikri in küntüm lâ ta`lemûn.'),
(1945, 16, 44, 'bilbeyyinâti vezzübür. veenzelnâ ileyke-ẕẕikra litübeyyine linnâsi mâ nüzzile ileyhim vele`allehüm yetefekkerûn.'),
(1946, 16, 45, 'efeemine-lleẕîne mekerü-sseyyiâti ey yaḫsife-llâhü bihimü-l\'arḍa ev ye\'tiyehümü-l`aẕâbü min ḥayŝü lâ yeş`urûn.'),
(1947, 16, 46, 'ev ye\'ḫuẕehüm fî teḳallübihim femâ hüm bimü`cizîn.'),
(1948, 16, 47, 'ev ye\'ḫuẕehüm `alâ teḫavvuf. feinne rabbeküm leraûfür raḥîm.'),
(1949, 16, 48, 'evelem yerav ilâ mâ ḫaleḳa-llâhü min şey\'iy yetefeyyeü żilâlühû `ani-lyemîni veşşemâili süccedel lillâhi vehüm dâḫirûn.'),
(1950, 16, 49, 'velillâhi yescüdü mâ fi-ssemâvâti vemâ fi-l\'arḍi min dâbbetiv velmelâiketü vehüm lâ yestekbirûn.'),
(1951, 16, 50, 'yeḫâfûne rabbehüm min fevḳihim veyef`alûne mâ yü\'merûn.'),
(1952, 16, 51, 'veḳâle-llâhü lâ tetteḫiẕû ilâheyni-ŝneyn. innemâ hüve ilâhüv vâḥid. feiyyâye ferhebûn.'),
(1953, 16, 52, 'velehû mâ fi-ssemâvâti vel\'arḍi velehü-ddînü vâṣibâ. efegayra-llâhi tetteḳûn.'),
(1954, 16, 53, 'vemâ biküm min ni`metin femine-llâhi ŝümme iẕâ messekümu-ḍḍurru feileyhi tec\'erûn.'),
(1955, 16, 54, 'ŝümme iẕâ keşefe-ḍḍurra `anküm iẕâ ferîḳum minküm birabbihim yüşrikûn.'),
(1956, 16, 55, 'liyekfürû bimâ âteynâhüm. fetemette`û. fesevfe ta`lemûn.'),
(1957, 16, 56, 'veyec`alûne limâ lâ ya`lemûne neṣîbem mimmâ razaḳnâhüm. tellâhi letüs\'elünne `ammâ küntüm tefterûn.'),
(1958, 16, 57, 'veyec`alûne lillâhi-lbenâti sübḥânehû velehüm mâ yeştehûn.'),
(1959, 16, 58, 'veiẕâ büşşira eḥadühüm bil\'ünŝâ żalle vechühû müsveddev vehüve keżîm.'),
(1960, 16, 59, 'yetevârâ mine-lḳavmi min sûi mâ büşşira bih. eyümsikühû `alâ hûnin em yedüssühû fi-ttürâb. elâ sâe mâ yaḥkümûn.'),
(1961, 16, 60, 'lilleẕîne lâ yü\'minûne bil\'âḫirati meŝelü-ssev\'. velillâhi-lmeŝelü-l\'a`lâ. vehüve-l`azîzü-lḥakîm.'),
(1962, 16, 61, 'velev yüâḫiẕü-llâhü-nnâse biżulmihim mâ terake `aleyhâ min dâbbetiv velâkiy yü\'eḫḫiruhüm ilâ ecelim müsemmâ. feiẕâ câe ecelühüm lâ yeste\'ḫirûne sâ`atev velâ yestaḳdimûn.'),
(1963, 16, 62, 'veyec`alûne lillâhi mâ yekrahûne veteṣifü elsinetühümü-lkeẕibe enne lehümü-lḥusnâ. lâ cerame enne lehümü-nnâra veennehüm müfraṭûn.'),
(1964, 16, 63, 'tellâhi leḳad erselnâ ilâ ümemim min ḳablike fezeyyene lehümü-şşeyṭânü a`mâlehüm fehüve veliyyühümü-lyevme velehüm `aẕâbün elîm.'),
(1965, 16, 64, 'vemâ enzelnâ `aleyke-lkitâbe illâ litübeyyine lehümü-lleẕi-ḫtelefû fîhi vehüdev veraḥmetel liḳavmiy yü\'minûn.'),
(1966, 16, 65, 'vellâhü enzele mine-ssemâi mâen feaḥyâ bihi-l\'arḍa ba`de mevtihâ. inne fî ẕâlike leâyetel liḳavmiy yesme`ûn.'),
(1967, 16, 66, 'veinne leküm fi-l\'en`âmi le`ibrah. nüsḳîküm mimmâ fî büṭûnihî mim beyni ferŝiv vedemil lebenen ḫâliṣan sâigal lişşâribîn.'),
(1968, 16, 67, 'vemin ŝemerâti-nneḫîli vel\'a`nâbi tetteḫiẕûne minhü sekerav verizḳan ḥasenâ. inne fî ẕâlike leâyetel liḳavmiy ya`ḳilûn.'),
(1969, 16, 68, 'veevḥâ rabbüke ile-nnaḥli eni-tteḫiẕî mine-lcibâli büyûtev vemine-şşeceri vemimmâ ya`rişûn.'),
(1970, 16, 69, 'ŝümme külî min külli-ŝŝemerâti feslükî sübüle rabbiki ẕülülâ. yaḫrucü mim büṭûnihâ şerâbüm muḫtelifün elvânühû fîhi şifâül linnâs. inne fî ẕâlike leâyetel liḳavmiy yetefekkerûn.'),
(1971, 16, 70, 'vellâhü ḫaleḳaküm ŝümme yeteveffâküm veminküm mey yüraddü ilâ erẕeli-l`umüri likey lâ ya`leme ba`de `ilmin şey\'â. inne-llâhe `alîmün ḳadîr.'),
(1972, 16, 71, 'vellâhü feḍḍale ba`ḍaküm `alâ ba`ḍin fi-rrizḳ. feme-lleẕîne füḍḍilû birâddî rizḳihim `alâ mâ meleket eymânühüm fehüm fîhi sevâün. efebini`meti-llâhi yecḥadûn.'),
(1973, 16, 72, 'vellâhü ce`ale leküm min enfüsiküm ezvâcev vece`ale leküm min ezvâciküm benîne veḥafedetev verazeḳaküm mine-ṭṭayyibât. efebilbâṭili yü\'minûne vebini`meti-llâhi hüm yekfürûn.'),
(1974, 16, 73, 'veya`büdûne min dûni-llâhi mâ lâ yemlikü lehüm rizḳam mine-ssemâvâti vel\'arḍi şey\'ev velâ yesteṭî`ûn.'),
(1975, 16, 74, 'felâ taḍribû lillâhi-l\'emŝâl. inne-llâhe ya`lemü veentüm lâ ta`lemûn.'),
(1976, 16, 75, 'ḍarabe-llâhü meŝelen `abdem memlûkel lâ yaḳdiru `alâ şey\'iv vemer razaḳnâhü minnâ rizḳan ḥasenen fehüve yünfiḳu minhü sirrav vecehrâ. hel yestevûn. elḥamdü lillâh. bel ekŝeruhüm lâ ya`lemûn.'),
(1977, 16, 76, 'veḍarabe-llâhü meŝeler racüleyni eḥadühümâ ebkemü lâ yaḳdiru `alâ şey\'iv vehüve kellün `alâ mevlâhü eynemâ yüveccihhü lâ ye\'ti biḫayr. hel yestevî hüve vemey ye\'müru bil`adli vehüve `alâ ṣirâṭim müsteḳîm.'),
(1978, 16, 77, 'velillâhi gaybü-ssemâvâti vel\'arḍ. vemâ emru-ssâ`ati illâ kelemḥi-lbeṣari ev hüve aḳrab. inne-llâhe `alâ külli şey\'in ḳadîr.'),
(1979, 16, 78, 'vellâhü aḫraceküm mim büṭûni ümmehâtiküm lâ ta`lemûne şey\'ev vece`ale lekümü-ssem`a vel\'ebṣâra vel\'ef\'idete le`alleküm teşkürûn.'),
(1980, 16, 79, 'elem yerav ile-ṭṭayri müseḫḫarâtin fî cevvi-ssemâ\'. mâ yümsikühünne ille-llâh. inne fî ẕâlike leâyâtil liḳavmiy yü\'minûn.'),
(1981, 16, 80, 'vellâhü ce`ale leküm mim büyûtiküm sekenev vece`ale leküm min cülûdi-l\'en`âmi büyûten testeḫiffûnehâ yevme ża`niküm veyevme iḳâmetiküm vemin aṣvâfihâ veevbârihâ veeş`ârihâ eŝâŝev vemetâ`an ilâ ḥîn.'),
(1982, 16, 81, 'vellâhü ce`ale leküm mimmâ ḫaleḳa żilâlev vece`ale leküm mine-lcibâli eknânev vece`ale leküm serâbîle teḳîkümü-lḥarra veserabîle teḳîküm be\'seküm. keẕâlike yütimmü ni`metehû `aleyküm le`alleküm tüslimûn.'),
(1983, 16, 82, 'fein tevellev feinnemâ `aleyke-lbelâgu-lmübîn.'),
(1984, 16, 83, 'ya`rifûne ni`mete-llâhi ŝümme yünkirûnehâ veekŝeruhümü-lkâfirûn.'),
(1985, 16, 84, 'veyevme neb`aŝü min külli ümmetin şehîden ŝümme lâ yü\'ẕenü lilleẕîne keferû velâ hüm yüsta`tebûn.'),
(1986, 16, 85, 'veiẕâ rae-lleẕîne żalemü-l`aẕâbe felâ yüḫaffefü `anhüm velâ hüm yünżarûn.'),
(1987, 16, 86, 'veiẕâ rae-lleẕîne eşrakû şürakâehüm ḳâlû rabbenâ hâülâi şürakâüne-lleẕîne künnâ ned`û min dûnik. feelḳav ileyhimü-lḳavle inneküm lekâẕibûn.'),
(1988, 16, 87, 'veelḳav ile-llâhi yevmeiẕin-sseleme veḍalle `anhüm mâ kânû yefterûn.'),
(1989, 16, 88, 'elleẕîne keferû veṣaddû `an sebîli-llâhi zidnâhüm `aẕâben fevḳa-l`aẕâbi bimâ kânû yüfsidûn.'),
(1990, 16, 89, 'veyevme neb`aŝü fî külli ümmetin şehîden `aleyhim min enfüsihim veci\'nâ bike şehîden `alâ hâülâ\'. venezzelnâ `aleyke-lkitâbe tibyânel likülli şey\'iv vehüdev veraḥmetev vebüşrâ lilmüslimîn.'),
(1991, 16, 90, 'inne-llâhe ye\'müru bil`adli vel\'iḥsâni veîtâi ẕi-lḳurbâ veyenhâ `ani-lfaḥşâi velmünkeri velbagy. ye`iżuküm le`alleküm teẕekkerûn.'),
(1992, 16, 91, 'veevfû bi`ahdi-llâhi iẕâ `âhettüm velâ tenḳuḍü-l\'eymâne ba`de tevkîdihâ veḳad ce`altümü-llâhe `aleyküm kefîlâ. inne-llâhe ya`lemü mâ tef`alûn.'),
(1993, 16, 92, 'velâ tekûnû kelletî neḳaḍat gazlehâ mim ba`di ḳuvvetin enkâŝâ. tetteḫiẕûne eymâneküm deḫalem beyneküm en tekûne ümmetün hiye erbâ min ümmeh. innemâ yeblûkümü-llâhü bih. veleyübeyyinenne leküm yevme-lḳiyâmeti mâ küntüm fîhi taḫtelifûn.'),
(1994, 16, 93, 'velev şâe-llâhü lece`aleküm ümmetev vâḥidetev velâkiy yüḍillü mey yeşâü veyehdî mey yeşâ\'. veletüs\'elünne `ammâ küntüm ta`melûn.'),
(1995, 16, 94, 'velâ tetteḫiẕû eymâneküm deḫalem beyneküm fetezille ḳademüm ba`de ŝübûtihâ veteẕûḳu-ssûe bimâ ṣadettüm `an sebîli-llâh. veleküm `aẕâbün `ażîm.'),
(1996, 16, 95, 'velâ teşterû bi`ahdi-llâhi ŝemenen ḳalîlâ. innemâ `inde-llâhi hüve ḫayrul leküm in küntüm ta`lemûn.'),
(1997, 16, 96, 'mâ `indeküm yenfedü vemâ `inde-llâhi bâḳ. velenecziyenne-lleẕîne ṣaberû ecrahüm biaḥseni mâ kânû ya`melûn.'),
(1998, 16, 97, 'men `amile ṣâliḥam min ẕekerin ev ünŝâ vehüve mü\'minün felenuḥyiyennehû ḥayâten ṭayyibeh. velenecziyennehüm ecrahüm biaḥseni mâ kânû ya`melûn.'),
(1999, 16, 98, 'feiẕâ ḳara\'te-lḳur\'âne feste`iẕ billâhi mine-şşeyṭâni-rracîm.'),
(2000, 16, 99, 'innehû leyse lehû sülṭânün `ale-lleẕîne âmenû ve`alâ rabbihim yetevekkelûn.'),
(2001, 16, 100, 'innemâ sülṭânühû `ale-lleẕîne yetevellevnehû velleẕîne hüm bihî müşrikûn.'),
(2002, 16, 101, 'veiẕâ beddelnâ âyetem mekâne âyetiv vellâhü a`lemü bimâ yünezzilü ḳâlû innemâ ente müfter. bel ekŝeruhüm lâ ya`lemûn.'),
(2003, 16, 102, 'ḳul nezzelehû rûḥu-lḳudüsi mir rabbike bilḥaḳḳi liyüŝebbite-lleẕîne âmenû vehüdev vebüşrâ lilmüslimîn.'),
(2004, 16, 103, 'veleḳad na`lemü ennehüm yeḳûlûne innemâ yü`allimühû beşer. lisânü-lleẕî yülḥidûne ileyhi a`cemiyyüv vehâẕâ lisânün `arabiyyüm mübîn.'),
(2005, 16, 104, 'inne-lleẕîne lâ yü\'minûne biâyâti-llâhi lâ yehdîhimü-llâhü velehüm `aẕâbün elîm.'),
(2006, 16, 105, 'innemâ yefteri-lkeẕibe-lleẕîne lâ yü\'minûne biâyâti-llâh. veülâike hümü-lkâẕibûn.'),
(2007, 16, 106, 'men kefera billâhi mim ba`di îmânihî illâ men ükrihe veḳalbühû muṭmeinnüm bil\'îmâni velâkim men şeraḥa bilküfri ṣadran fe`aleyhim gaḍabüm mine-llâh. velehüm `aẕâbün `ażîm.'),
(2008, 16, 107, 'ẕâlike biennehümü-steḥabbü-lḥayâte-ddünyâ `ale-l\'âḫirati veenne-llâhe lâ yehdi-lḳavme-lkâfirîn.'),
(2009, 16, 108, 'ülâike-lleẕîne ṭabe`a-llâhü `alâ ḳulûbihim vesem`ihim veebṣârihim. veülâike hümü-lgâfilûn.'),
(2010, 16, 109, 'lâ cerame ennehüm fi-l\'âḫirati hümü-lḫâsirûn.'),
(2011, 16, 110, 'ŝümme inne rabbeke lilleẕîne hâcerû mim ba`di mâ fütinû ŝümme câhedû veṣaberû inne rabbeke mim ba`dihâ legafûrur raḥîm.'),
(2012, 16, 111, 'yevme te\'tî küllü nefsin tücâdilü `an nefsihâ vetüveffâ küllü nefsim mâ `amilet vehüm lâ yużlemûn.'),
(2013, 16, 112, 'veḍarabe-llâhü meŝelen ḳaryeten kânet âminetem muṭmeinnetey ye\'tîhâ rizḳuhâ ragadem min külli mekânin fekeferat bien`umi-llâhi feeẕâḳahe-llâhü libâse-lcû`i velḫavfi bimâ kânû yaṣne`ûn.'),
(2014, 16, 113, 'veleḳad câehüm rasûlüm minhüm fekeẕẕebûhü feeḫaẕehümü-l`aẕâbü vehüm żâlimûn.'),
(2015, 16, 114, 'fekülû mimmâ razeḳakümü-llâhü ḥalâlen ṭayyibâ. veşkürû ni`mete-llâhi in küntüm iyyâhü ta`büdûn.'),
(2016, 16, 115, 'innemâ ḥarrame `aleykümü-lmeytete veddeme velaḥme-lḫinzîri vemâ ühille ligayri-llâhi bih. femeni-ḍṭurra gayra bâgiv velâ `âdin feinne-llâhe gafûrur raḥîm.'),
(2017, 16, 116, 'velâ teḳûlû limâ teṣifü elsinetükümü-lkeẕibe hâẕâ ḥalâlüv vehâẕâ ḥarâmül litefterû `ale-llâhi-lkeẕib. inne-lleẕîne yefterûne `ale-llâhi-lkeẕibe lâ yüfliḥûn.'),
(2018, 16, 117, 'metâ`un ḳalîl. velehüm `aẕâbün elîm.'),
(2019, 16, 118, 've`ale-lleẕîne hâdû ḥarramnâ mâ ḳaṣaṣnâ `aleyke min ḳabl. vemâ żalemnâhüm velâkin kânû enfüsehüm yażlimûn.'),
(2020, 16, 119, 'ŝümme inne rabbeke lilleẕîne `amilü-ssûe bicehâletin ŝümme tâbû mim ba`di ẕâlike veaṣleḥû inne rabbeke mim ba`dihâ legafûrur raḥîm.'),
(2021, 16, 120, 'inne ibrâhîme kâne ümmeten ḳânitel lillâhi ḥanîfâ. velem yekü mine-lmüşrikîn.'),
(2022, 16, 121, 'şâkiral lien`umih. ictebâhü vehedâhü ilâ ṣirâṭim müsteḳîm.'),
(2023, 16, 122, 'veâteynâhü fi-ddünyâ ḥaseneh. veinnehû fi-l\'âḫirati lemine-ṣṣâliḥîn.'),
(2024, 16, 123, 'ŝümme evḥaynâ ileyke eni-ttebi` millete ibrâhîme ḥanîfâ. vemâ kâne mine-lmüşrikîn.'),
(2025, 16, 124, 'innemâ cü`ile-ssebtü `ale-lleẕîne-ḫtelefû fîh. veinne rabbeke leyaḥkümü beynehüm yevme-lḳiyâmeti fîmâ kânû fîhi yaḫtelifûn.'),
(2026, 16, 125, 'üd`u ilâ sebîli rabbike bilḥikmeti velmev`iżati-lḥaseneti vecâdilhüm billetî hiye aḥsen. inne rabbeke hüve a`lemü bimen ḍalle `an sebîlihî vehüve a`lemü bilmühtedîn.'),
(2027, 16, 126, 'vein `âḳabtüm fe`âḳibû bimiŝli mâ `ûḳibtüm bih. velein ṣabertüm lehüve ḫayrul liṣṣâbirîn.'),
(2028, 16, 127, 'vaṣbir vemâ ṣabruke illâ billâhi velâ taḥzen `aleyhim velâ tekü fî ḍayḳim mimmâ yemkürûn.'),
(2029, 16, 128, 'inne-llâhe me`a-lleẕîne-tteḳav velleẕîne hüm muḥsinûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'sübḥâne-lleẕî esrâ bi`abdihî leylem mine-lmescidi-lḥarâmi ile-lmescidi-l\'aḳṣe-lleẕî bâraknâ ḥavlehû linüriyehû min âyâtinâ. innehû hüve-ssemî`u-lbeṣîr.'),
(2031, 17, 2, 'veâteynâ mûse-lkitâbe vece`alnâhü hüdel libenî isrâîle ellâ tetteḫiẕû min dûnî vekîlâ.'),
(2032, 17, 3, 'ẕürriyyete men ḥamelnâ me`a nûḥ. innehû kâne `abden şekûrâ.'),
(2033, 17, 4, 'veḳaḍaynâ ilâ benî isrâîle fi-lkitâbi letüfsidünne fi-l\'arḍi merrateyni veleta`lünne `ulüvven kebîrâ.'),
(2034, 17, 5, 'feiẕâ câe va`dü ûlâhümâ be`aŝnâ `aleyküm `ibâdel lenâ ülî be\'sin şedîdin fecâsû ḫilâle-ddiyâr. vekâne va`dem mef`ûlâ.'),
(2035, 17, 6, 'ŝümme radednâ lekümü-lkerrate `aleyhim veemdednâküm biemvâliv vebenîne vece`alnâküm ekŝera nefîrâ.'),
(2036, 17, 7, 'in aḥsentüm aḥsentüm lienfüsiküm vein ese\'tüm felehâ. feiẕâ câe va`dü-l\'âḫirati liyesûû vucûheküm veliyedḫulü-lmescide kemâ deḫalûhü evvele merrativ veliyütebbirû mâ `alev tetbîrâ.'),
(2037, 17, 8, '`asâ rabbüküm ey yerḥameküm. vein `uttüm `udnâ. vece`alnâ cehenneme lilkâfirîne ḥaṣîrâ.'),
(2038, 17, 9, 'inne hâẕe-lḳur\'âne yehdî lilletî hiye aḳvemü veyübeşşiru-lmü\'minîne-lleẕîne ya`melûne-ṣṣâliḥâti enne lehüm ecran kebîrâ.'),
(2039, 17, 10, 'veenne-lleẕîne lâ yü\'minûne bil\'âḫirati a`tednâ lehüm `aẕâben elîmâ.'),
(2040, 17, 11, 'veyed`u-l\'insânü bişşerri dü`âehû bilḫayr. vekâne-l\'insânü `acûlâ.'),
(2041, 17, 12, 'vece`alne-lleyle vennehâra âyeteyni femeḥavnâ âyete-lleyli vece`alnâ âyete-nnehâri mübṣiratel litebtegû faḍlem mir rabbiküm velita`lemû `adede-ssinîne velḥisâb. vekülle şey\'in feṣṣalnâhü tefṣîlâ.'),
(2042, 17, 13, 'vekülle insânin elzemnâhü ṭâirahû fî `unüḳih. venuḫricü lehû yevme-lḳiyâmeti kitâbey yelḳâhü menşûrâ.'),
(2043, 17, 14, 'iḳra\' kitâbek. kefâ binefsike-lyevme `aleyke ḥasîbâ.'),
(2044, 17, 15, 'meni-htedâ feinnemâ yehtedî linefsih. vemen ḍalle feinnemâ yeḍillü `aleyhâ. velâ teziru vâziratüv vizra uḫrâ. vemâ künnâ mü`aẕẕibîne ḥattâ neb`aŝe rasûlâ.'),
(2045, 17, 16, 'veiẕâ eradnâ en nühlike ḳaryeten emernâ mütrafîhâ fefeseḳû fîhâ feḥaḳḳa `aleyhe-lḳavlü fedemmernâhâ tedmîrâ.'),
(2046, 17, 17, 'vekem ehleknâ mine-lḳurûni mim ba`di nûḥ. vekefâ birabbike biẕünûbi `ibâdihî ḫabîram beṣîrâ.'),
(2047, 17, 18, 'men kâne yürîdü-l`âcilete `accelnâ lehû fîhâ mâ neşâü limen nürîdü ŝümme ce`alnâ lehû cehennem. yaṣlâhâ meẕmûmem medḥûrâ.'),
(2048, 17, 19, 'vemen erâde-l\'âḫirate vese`â lehâ sa`yehâ vehüve mü\'minün feülâike kâne sa`yühüm meşkûrâ.'),
(2049, 17, 20, 'küllen nümiddü hâülâi vehâülâi min `aṭâi rabbik. vemâ kâne `aṭâü rabbike maḥżûrâ.'),
(2050, 17, 21, 'ünżur keyfe feḍḍalnâ ba`ḍahüm `alâ ba`ḍ. velel\'âḫiratü ekberu deracâtiv veekberu tefḍîlâ.'),
(2051, 17, 22, 'lâ tec`al me`a-llâhi ilâhen âḫara fetaḳ`ude meẕmûmem maḫẕûlâ.'),
(2052, 17, 23, 'veḳaḍâ rabbüke ellâ ta`büdû illâ iyyâhü vebilvâlideyni iḥsânâ. immâ yeblüganne `indeke-lkibera eḥadühümâ ev kilâhümâ felâ teḳul lehümâ üffiv velâ tenherhümâ veḳul lehümâ ḳavlen kerîmâ.'),
(2053, 17, 24, 'vaḫfiḍ lehümâ cenâḥa-ẕẕülli mine-rraḥmeti veḳur rabbi-rḥamhümâ kemâ rabbeyânî ṣagîrâ.'),
(2054, 17, 25, 'rabbüküm a`lemü bimâ fî nüfûsiküm. in tekûnû ṣâliḥîne feinnehû kâne lil\'evvâbîne gafûrâ.'),
(2055, 17, 26, 'veâti ẕe-lḳurbâ ḥaḳḳahû velmiskîne vebne-ssebîli velâ tübeẕẕir tebẕîrâ.'),
(2056, 17, 27, 'inne-lmübeẕẕirîne kânû iḫvâne-şşeyâṭîn. vekâne-şşeyṭânü lirabbihî kefûrâ.'),
(2057, 17, 28, 'veimmâ tü`riḍanne `anhümü-btigâe raḥmetim mir rabbike tercûhâ feḳul lehüm ḳavlem meysûrâ.'),
(2058, 17, 29, 'velâ tec`al yedeke maglûleten ilâ `unüḳike velâ tebsuṭhâ külle-lbesṭi fetaḳ`ude melûmem maḥsûrâ.'),
(2059, 17, 30, 'inne rabbeke yebsüṭu-rrizḳa limey yeşâü veyaḳdir. innehû kâne bi`ibâdihî ḫabîram beṣîrâ.'),
(2060, 17, 31, 'velâ taḳtülû evlâdeküm ḫaşyete imlâḳ. naḥnü nerzüḳuhüm veiyyâküm. inne ḳatlehüm kâne ḫiṭ\'en kebîrâ.'),
(2061, 17, 32, 'velâ taḳrabü-zzinâ innehû kâne fâḥişeten. vesâe sebîlâ.'),
(2062, 17, 33, 'velâ taḳtülü-nnefse-lletî ḥarrame-llâhü illâ bilḥaḳḳ. vemen ḳutile mażlûmen feḳad ce`alnâ liveliyyihî sülṭânen felâ yüsrif fi-lḳatl. innehû kâne menṣûrâ.'),
(2063, 17, 34, 'velâ taḳrabû mâle-lyetîmi illâ billetî hiye aḥsenü ḥattâ yeblüga eşüddeh. veevfû bil`ahd. inne-l`ahde kâne mes\'ûlâ.'),
(2064, 17, 35, 'veevfü-lkeyle iẕâ kiltüm vezinû bilḳisṭâsi-lmüsteḳîm. ẕâlike ḫayruv veaḥsenü te\'vîlâ.'),
(2065, 17, 36, 'velâ taḳfü mâ leyse leke bihî `ilm. inne-ssem`a velbeṣara velfüâde küllü ülâike kâne `anhü mes\'ûlâ.'),
(2066, 17, 37, 'velâ temşi fi-l\'arḍi meraḥâ. inneke len taḫriḳa-l\'arḍa velen teblüga-lcibâle ṭûlâ.'),
(2067, 17, 38, 'küllü ẕâlike kâne seyyiühû `inde rabbike mekrûhâ.'),
(2068, 17, 39, 'ẕâlike mimmâ evḥâ ileyke rabbüke mine-lḥikmeh. velâ tec`al me`a-llâhi ilâhen âḫara fetülḳâ fî cehenneme melûmem medḥûrâ.'),
(2069, 17, 40, 'efeaṣfâküm rabbüküm bilbenîne vetteḫaẕe mine-lmelâiketi inâŝâ. inneküm leteḳûlûne ḳavlen `ażîmâ.'),
(2070, 17, 41, 'veleḳad ṣarrafnâ fî hâẕe-lḳur\'âni liyeẕẕekkerû. vemâ yezîdühüm illâ nüfûrâ.'),
(2071, 17, 42, 'ḳul lev kâne me`ahû âlihetün kemâ yeḳûlûne iẕel lebtegav ilâ ẕi-l`arşi sebîlâ.'),
(2072, 17, 43, 'sübḥânehû vete`âlâ `ammâ yeḳûlûne `ulüvven kebîrâ.'),
(2073, 17, 44, 'tüsebbiḥu lehü-ssemâvâtü-sseb`u vel\'arḍu vemen fîhinn. veim min şey\'in illâ yüsebbiḥu biḥamdihî velâkil lâ tefḳahûne tesbîḥahüm. innehû kâne ḥalîmen gafûrâ.'),
(2074, 17, 45, 'veiẕâ ḳara\'te-lḳur\'âne ce`alnâ beyneke vebeyne-lleẕîne lâ yü\'minûne bil\'âḫirati ḥicâbem mestûrâ.'),
(2075, 17, 46, 'vece`alnâ `alâ ḳulûbihim ekinneten ey yefḳahûhü vefî âẕânihim vaḳrâ. veiẕâ ẕekerte rabbeke fi-lḳur\'âni vaḥdehû vellev `alâ edbârihim nüfûrâ.'),
(2076, 17, 47, 'naḥnü a`lemü bimâ yestemi`ûne bihî iẕ yestemi`ûne ileyke veiẕ hüm necvâ iẕ yeḳûlu-żżâlimûne in tettebi`ûne illâ racülem mesḥûrâ.'),
(2077, 17, 48, 'ünżur keyfe ḍarabû leke-l\'emŝâle feḍallû felâ yesteṭî`ûne sebîlâ.'),
(2078, 17, 49, 'veḳâlû eiẕâ künnâ `iżâmev verufâten einnâ lemeb`ûŝûne ḫalḳan cedîdâ.'),
(2079, 17, 50, 'ḳul kûnû ḥicâraten ev ḥadîdâ.'),
(2080, 17, 51, 'ev ḫalḳam mimmâ yekbüru fî ṣudûriküm. feseyeḳûlûne mey yü`îdünâ. ḳuli-lleẕî feṭaraküm evvele merrah. feseyüngiḍûne ileyke ruûsehüm veyeḳûlûne metâ hû. ḳul `asâ ey yekûne ḳarîbâ.'),
(2081, 17, 52, 'yevme yed`ûküm fetestecîbûne biḥamdihî veteżunnûne il lebiŝtüm illâ ḳalîlâ.'),
(2082, 17, 53, 'veḳul li`ibâdî yeḳûlü-lletî hiye aḥsen. inne-şşeyṭâne yenzegu beynehüm. inne-şşeyṭâne kâne lil\'insâni `adüvvem mübînâ.'),
(2083, 17, 54, 'rabbüküm a`lemü biküm. iy yeşe\' yerḥamküm ev iy yeşe\' yü`aẕẕibküm. vemâ erselnâke `aleyhim vekîlâ.'),
(2084, 17, 55, 'verabbüke a`lemü bimen fi-ssemâvâti vel\'arḍ. veleḳad feḍḍalnâ ba`ḍa-nnebiyyîne `alâ ba`ḍiv veâteynâ dâvûde zebûrâ.'),
(2085, 17, 56, 'ḳuli-d`ü-lleẕîne za`amtüm min dûnihî felâ yemlikûne keşfe-ḍḍurri `anküm velâ taḥvîlâ.'),
(2086, 17, 57, 'ülâike-lleẕîne yed`ûne yebtegûne ilâ rabbihimü-lvesîlete eyyühüm aḳrabü veyercûne raḥmetehû veyeḫâfûne `aẕâbeh. inne `aẕâbe rabbike kâne maḥẕûrâ.'),
(2087, 17, 58, 'veim min ḳaryetin illâ naḥnü mühlikûhâ ḳable yevmi-lḳiyâmeti ev mü`aẕẕibûhâ `aẕâben şedîdâ. kâne ẕâlike fi-lkitâbi mesṭûrâ.'),
(2088, 17, 59, 'vemâ mene`anâ en nürsile bil\'âyâti illâ en keẕẕebe bihe-l\'evvelûn. veâteynâ ŝemûde-nnâḳate mübṣiraten feżalemû bihâ. vemâ nürsilü bil\'âyâti illâ taḫvîfâ.'),
(2089, 17, 60, 'veiẕ ḳulnâ leke inne rabbeke eḥâṭa binnâs. vemâ ce`alne-rru\'ye-lletî eraynâke illâ fitnetel linnâsi veşşecerate-lmel`ûnete fi-lḳur\'ân. venüḫavvifühüm femâ yezîdühüm illâ ṭugyânen kebîrâ.'),
(2090, 17, 61, 'veiẕ ḳulnâ lilmelâiketi-scüdû liâdeme fesecedû illâ iblîs. ḳâle eescüdü limen ḫalaḳte ṭînâ.'),
(2091, 17, 62, 'ḳâle era\'eyteke hâẕe-lleẕî kerramte `aleyy. lein eḫḫarteni ilâ yevmi-lḳiyâmeti leaḥtenikenne ẕürriyyetehû illâ ḳalîlâ.'),
(2092, 17, 63, 'ḳâle-ẕheb femen tebi`ake minhüm feinne cehenneme cezâüküm cezâem mevfûrâ.'),
(2093, 17, 64, 'vestefziz meni-steṭa`te minhüm biṣavtike veeclib `aleyhim biḫaylike veraclike veşârikhüm fi-l\'emvâli vel\'evlâdi ve`idhüm. vemâ ye`idühümü-şşeyṭânü illâ gurûrâ.'),
(2094, 17, 65, 'inne `ibâdî leyse leke `aleyhim sülṭân. vekefâ birabbike vekîlâ.'),
(2095, 17, 66, 'rabbükümü-lleẕî yüzcî lekümü-lfülke fi-lbaḥri litebtegû min faḍlih. innehû kâne biküm raḥîmâ.'),
(2096, 17, 67, 'veiẕâ messekümu-ḍḍurru fi-lbaḥri ḍalle men ted`ûne illâ iyyâh. felemmâ neccâküm ile-lberri a`raḍtüm. vekâne-l\'insânü kefûrâ.'),
(2097, 17, 68, 'efeemintüm ey yaḫsife biküm cânibe-lberri ev yürsile `aleyküm ḥâṣiben ŝümme lâ tecidû leküm vekîlâ.'),
(2098, 17, 69, 'em emintüm ey yü`îdeküm fîhi târaten uḫrâ feyürsile `aleyküm ḳâṣifem mine-rrîḥi feyugriḳaküm bimâ kefertüm ŝümme lâ tecidû leküm `aleynâ bihî tebî`â.'),
(2099, 17, 70, 'veleḳad kerramnâ benî âdeme veḥamelnâhüm fi-lberri velbaḥri verazaḳnâhüm mine-ṭṭayyibâti vefeḍḍalnâhüm `alâ keŝîrim mimmen ḫalaḳnâ tefḍîlâ.'),
(2100, 17, 71, 'yevme ned`û külle ünâsim biimâmihim. femen ûtiye kitâbehû biyemînihî feülâike yaḳraûne kitâbehüm velâ yużlemûne fetîlâ.'),
(2101, 17, 72, 'vemen kâne fî hâẕihî a`mâ fehüve fi-l\'âḫirati a`mâ veeḍallü sebîlâ.'),
(2102, 17, 73, 'vein kâdû leyeftinûneke `ani-lleẕî evḥaynâ ileyke litefteriye `aleynâ gayrah. veiẕel letteḫaẕûke ḫalîlâ.'),
(2103, 17, 74, 'velevlâ en ŝebbetnâke leḳad kitte terkenü ileyhim şey\'en ḳalîlâ.'),
(2104, 17, 75, 'iẕel leeẕaḳnâke ḍi`fe-lḥayâti veḍi`fe-lmemâti ŝümme lâ tecidü leke `aleynâ neṣîrâ.'),
(2105, 17, 76, 'vein kâdû leyestefizzûneke mine-l\'arḍi liyuḫricûke minhâ veiẕel lâ yelbeŝûne ḫilâfeke illâ ḳalîlâ.'),
(2106, 17, 77, 'sünnete men ḳad erselnâ ḳableke mir rusülinâ velâ tecidü lisünnetinâ taḥvîlâ.'),
(2107, 17, 78, 'eḳimi-ṣṣalâte lidülûki-şşemsi ilâ gaseḳi-lleyli veḳur\'âne-lfecr. inne ḳur\'âne-lfecri kâne meşhûdâ.'),
(2108, 17, 79, 'vemine-lleyli fetehecced bihî nâfiletel lek. `asâ ey yeb`aŝeke rabbüke meḳâmem maḥmûdâ.'),
(2109, 17, 80, 'veḳur rabbi edḫilnî müdḫale ṣidḳiv veaḫricnî muḫrace ṣidḳiv vec`al lî mil ledünke sülṭânen neṣîrâ.'),
(2110, 17, 81, 'veḳul câe-lḥaḳḳu vezeheḳa-lbâṭil. inne-lbâṭile kâne zehûḳâ.'),
(2111, 17, 82, 'venünezzilü mine-lḳur\'âni mâ hüve şifâüv veraḥmetül lilmü\'minîne velâ yezîdu-żżâlimîne illâ ḫasârâ.'),
(2112, 17, 83, 'veiẕâ en`amnâ `ale-l\'insâni a`raḍa veneâ bicânibih. veiẕâ messehü-şşerru kâne yeûsâ.'),
(2113, 17, 84, 'ḳul küllüy ya`melü `alâ şâkiletih. ferabbüküm a`lemü bimen hüve ehdâ sebîlâ.'),
(2114, 17, 85, 'veyes\'elûneke `ani-rrûḥ. ḳuli-rrûḥu min emri rabbî vemâ ûtîtüm mine-l`ilmi illâ ḳalîlâ.'),
(2115, 17, 86, 'velein şi\'nâ leneẕhebenne billeẕî evḥaynâ ileyke ŝümme lâ tecidü leke bihî `aleynâ vekîlâ.'),
(2116, 17, 87, 'illâ raḥmetem mir rabbik. inne faḍlehû kâne `aleyke kebîrâ.'),
(2117, 17, 88, 'ḳul leini-cteme`ati-l\'insü velcinnü `alâ ey ye\'tû bimiŝli hâẕe-lḳur\'âni lâ ye\'tûne bimiŝlihî velev kâne ba`ḍuhüm liba`ḍin żahîrâ.'),
(2118, 17, 89, 'veleḳad ṣarrafnâ linnâsi fî hâẕe-lḳur\'âni min külli meŝel. feebâ ekŝeru-nnâsi illâ küfûrâ.'),
(2119, 17, 90, 'veḳâlû len nü\'mine leke ḥattâ tefcüra lenâ mine-l\'arḍi yembû`â.'),
(2120, 17, 91, 'ev tekûne leke cennetüm min neḫîliv ve`inebin fetüfeccira-l\'enhâra ḫilâlehâ tefcîrâ.'),
(2121, 17, 92, 'ev tüsḳiṭa-ssemâe kemâ za`amte `aleynâ kisefen ev te\'tiye billâhi velmelâiketi ḳabîlâ.'),
(2122, 17, 93, 'ev yekûne leke beytüm min zuḫrufin ev terḳâ fi-ssemâ\'. velen nü\'mine liruḳiyyike ḥattâ tünezzile `aleynâ kitâben naḳraüh. ḳul sübḥâne rabbî hel küntü illâ beşerar rasûlâ.'),
(2123, 17, 94, 'vemâ mene`a-nnâse ey yü\'minû iẕ câehümü-lhüdâ illâ en ḳâlû ebe`aŝe-llâhü beşerar rasûlâ.'),
(2124, 17, 95, 'ḳul lev kâne fi-l\'arḍi melâiketüy yemşûne muṭmeinnîne lenezzelnâ `aleyhim mine-ssemâi meleker rasûlâ.'),
(2125, 17, 96, 'ḳul kefâ billâhi şehîdem beynî vebeyneküm. innehû kâne bi`ibâdihî ḫabîram beṣîrâ.'),
(2126, 17, 97, 'vemey yehdi-llâhü fehüve-lmühted. vemey yuḍlil felen tecide lehüm evliyâe min dûnih. venaḥşüruhüm yevme-lḳiyâmeti `alâ vucûhihim `umyev vebükmev veṣummâ. me\'vâhüm cehennem. küllemâ ḫabet zidnâhüm se`îrâ.'),
(2127, 17, 98, 'ẕâlike cezâühüm biennehüm keferû biâyâtinâ veḳâlû eiẕâ künnâ `iżâmev verufâten einnâ lemeb`ûŝûne ḫalḳan cedîdâ.'),
(2128, 17, 99, 'evelem yerav enne-llâhe-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa ḳâdirun `alâ ey yaḫlüḳa miŝlehüm vece`ale lehüm ecelel lâ raybe fîh. feebe-żżâlimûne illâ küfûrâ.'),
(2129, 17, 100, 'ḳul lev entüm temlikûne ḫazâine raḥmeti rabbî iẕel leemsektüm ḫaşyete-l\'infâḳ. vekâne-l\'insânü ḳatûrâ.'),
(2130, 17, 101, 'veleḳad âteynâ mûsâ tis`a âyâtim beyyinâtin fes\'el benî isrâîle iẕ câehüm feḳâle lehû fir`avnü innî leeżunnüke yâ mûsâ mesḥûrâ.'),
(2131, 17, 102, 'ḳâle leḳad `alimte mâ enzele hâülâi illâ rabbü-ssemâvâti vel\'arḍi beṣâir. veinnî leeżunnüke yâ fir`avnü meŝbûrâ.'),
(2132, 17, 103, 'feerâde ey yestefizzehüm mine-l\'arḍi feagraḳnâhü vemem me`ahû cemî`â.'),
(2133, 17, 104, 'veḳulnâ mim ba`dihî libenî isrâîle-skünü-l\'arḍa feiẕâ câe va`dü-l\'âḫirati ci\'nâ biküm lefîfâ.'),
(2134, 17, 105, 'vebilḥaḳḳi enzelnâhü vebilḥaḳḳi nezel. vemâ erselnâke illâ mübeşşirav veneẕîrâ.'),
(2135, 17, 106, 'veḳur\'ânen feraḳnâhü litaḳraehû `ale-nnâsi `alâ mükŝiv venezzelnâhü tenzîlâ.'),
(2136, 17, 107, 'ḳul âminû bihî ev lâ tü\'minû. inne-lleẕîne ûtü-l`ilme min ḳablihî iẕâ yütlâ `aleyhim yeḫirrûne lil\'eẕḳâni süccedâ.'),
(2137, 17, 108, 'veyeḳûlûne sübḥâne rabbinâ in kâne va`dü rabbinâ lemef`ûlâ.'),
(2138, 17, 109, 'veyeḫirrûne lil\'eẕḳâni yebkûne veyezîdühüm ḫuşû`â.'),
(2139, 17, 110, 'ḳuli-d`ü-llâhe evi-d`ü-rraḥmân. eyyem mâ ted`û felehü-l\'esmâü-lḥusnâ. velâ techer biṣalâtike velâ tüḫâfit bihâ vebtegi beyne ẕâlike sebîlâ.'),
(2140, 17, 111, 'veḳuli-lḥamdü lillâhi-lleẕî lem yetteḫiẕ veledev velem yekül lehû şerîkün fi-lmülki velem yekül lehû veliyyüm mine-ẕẕülli vekebbirhü tekbîrâ.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'elḥamdü lillâhi-lleẕî enzele `alâ `abdihi-lkitâbe velem yec`al lehû `ivecâ.'),
(2142, 18, 2, 'ḳayyimel liyünẕira be\'sen şedîdem mil ledünhü veyübeşşira-lmü\'minîne-lleẕîne ya`melûne-ṣṣâliḥâti enne lehüm ecran ḥasenâ.'),
(2143, 18, 3, 'mâkiŝîne fîhi ebedâ.'),
(2144, 18, 4, 'veyünẕira-lleẕîne ḳâlü-tteḫaẕe-llâhü veledâ.'),
(2145, 18, 5, 'mâ lehüm bihî min `ilmiv velâ liâbâihim. kebürat kelimeten taḫrucü min efvâhihim. iy yeḳûlûne illâ keẕibâ.'),
(2146, 18, 6, 'fele`alleke bâḫi`un nefseke `alâ âŝârihim il lem yü\'minû bihâẕe-lḥadîŝi esefâ.'),
(2147, 18, 7, 'innâ ce`alnâ mâ `ale-l\'arḍi zînetel lehâ lineblüvehüm eyyühüm aḥsenü `amelâ.'),
(2148, 18, 8, 'veinnâ lecâ`ilûne mâ `aleyhâ ṣa`îden cüruzâ.'),
(2149, 18, 9, 'em ḥasibte enne aṣḥâbe-lkehfi verraḳîmi kânû min âyâtinâ `acebâ.'),
(2150, 18, 10, 'iẕ eve-lfityetü ile-lkehfi feḳâlû rabbenâ âtinâ mil ledünke raḥmetev veheyyi\' lenâ min emrinâ raşedâ.'),
(2151, 18, 11, 'feḍarabnâ `alâ âẕânihim fi-lkehfi sinîne `adedâ.'),
(2152, 18, 12, 'ŝümme be`aŝnâhüm lina`leme eyyü-lḥizbeyni aḥṣâ limâ lebiŝû emedâ.'),
(2153, 18, 13, 'naḥnü neḳuṣṣu `aleyke nebeehüm bilḥaḳḳ. innehüm fityetün âmenû birabbihim vezidnâhüm hüdâ.'),
(2154, 18, 14, 'verabaṭnâ `alâ ḳulûbihim iẕ ḳâmû feḳâlû rabbünâ rabbü-ssemâvâti vel\'arḍi len ned`uve min dûnihî ilâhel leḳad ḳulnâ iẕen şeṭaṭâ.'),
(2155, 18, 15, 'hâülâi ḳavmüne-tteḫaẕû min dûnihî âliheh. levlâ ye\'tûne `aleyhim bisülṭânim beyyin. femen ażlemü mimmeni-fterâ `ale-llâhi keẕibâ.'),
(2156, 18, 16, 'veiẕi-`tezeltümûhüm vemâ ya`büdûne ille-llâhe fe\'vû ile-lkehfi yenşür leküm rabbüküm mir raḥmetihî veyüheyyi\' leküm min emriküm mirfeḳâ.'),
(2157, 18, 17, 'vetera-şşemse iẕâ ṭale`at tezâveru `an kehfihim ẕâte-lyemîni veiẕâ garabet taḳriḍuhüm ẕâte-şşimâli vehüm fî fecvetim minh. ẕâlike min âyâti-llâh. mey yehdi-llâhü fehüve-lmühted. vemey yuḍlil felen tecide lehû veliyyem mürşidâ.'),
(2158, 18, 18, 'vetaḥsebühüm eyḳâżav vehüm ruḳûd. venüḳallibühüm ẕâte-lyemîni veẕâte-şşimâl. vekelbühüm bâsiṭun ẕirâ`ayhi bilveṣîd. levi-ṭṭala`te `aleyhim levelleyte minhüm firârav velemüli\'te minhüm ru`bâ.'),
(2159, 18, 19, 'vekeẕâlike be`aŝnâhüm liyetesâelû beynehüm. ḳâle ḳâilüm minhüm kem lebiŝtüm. ḳâlû lebiŝnâ yevmen ev ba`ḍa yevm. ḳâlû rabbüküm a`lemü bimâ lebiŝtüm feb`aŝû eḥadeküm biveriḳiküm hâẕihî ile-lmedîneti felyenżur eyyühâ ezkâ ṭa`âmen felye\'tiküm birizḳim minhü velyeteleṭṭaf velâ yüş`iranne biküm eḥadâ.'),
(2160, 18, 20, 'innehüm iy yażherû `aleyküm yercümûküm ev yü`îdûküm fî milletihim velen tüfliḥû iẕen ebedâ.'),
(2161, 18, 21, 'vekeẕâlike a`ŝernâ `aleyhim liya`lemû enne va`de-llâhi ḥaḳḳuv veenne-ssâ`ate lâ raybe fîhâ. iẕ yetenâza`ûne beynehüm emrahüm feḳâlü-bnû `aleyhim bünyânâ. rabbühüm a`lemü bihim. ḳâle-lleẕîne galebû `alâ emrihim lenetteḫiẕenne `aleyhim mescidâ.'),
(2162, 18, 22, 'seyeḳûlûne ŝelâŝetür râbi`uhüm kelbühüm. veyeḳûlûne ḫamsetün sâdisühüm kelbühüm racmem bilgayb. veyeḳûlûne seb`atüv veŝâminühüm kelbühüm. ḳur rabbî a`lemü bi`iddetihim mâ ya`lemühüm illâ ḳalîlün. felâ tümâri fîhim illâ mirâen żâhirâ. velâ testefti fîhim minhüm eḥadâ.'),
(2163, 18, 23, 'velâ teḳûlenne lişey\'in innî fâ`ilün ẕâlike gadâ.'),
(2164, 18, 24, 'illâ ey yeşâe-llâh. veẕkür rabbeke iẕâ nesîte veḳul `asâ ey yehdiyeni rabbî liaḳrabe min hâẕâ raşedâ.'),
(2165, 18, 25, 'velebiŝû fî kehfihim ŝelâŝe mietin sinîne vezdâdû tis`â.'),
(2166, 18, 26, 'ḳuli-llâhü a`lemü bimâ lebiŝû. lehû gaybü-ssemâvâti vel\'arḍ. ebṣir bihî veesmi`. mâ lehüm min dûnihî miv veliyy. velâ yüşrikü fî ḥukmihî eḥadâ.'),
(2167, 18, 27, 'vetlü mâ ûḥiye ileyke min kitâbi rabbik. lâ mübeddile likelimâtihî velen tecide min dûnihî mülteḥadâ.'),
(2168, 18, 28, 'vaṣbir nefseke me`a-lleẕîne yed`ûne rabbehüm bilgadâti vel`aşiyyi yürîdûne vechehû velâ ta`dü `aynâke `anhüm. türîdü zînete-lḥayâti-ddünyâ velâ tüṭi` men agfelnâ ḳalbehû `an ẕikrinâ vettebe`a hevâhû vekâne emruhû füruṭâ.'),
(2169, 18, 29, 'veḳuli-lḥaḳḳu mir rabbiküm femen şâe felyü\'miv vemen şâe felyekfür innâ a`tednâ liżżâlimîne nâran eḥâṭa bihim sürâdiḳuhâ. veiy yestegîŝû yügâŝû bimâin kelmühli yeşvi-lvucûh. bi\'se-şşerâb. vesâet mürtefeḳâ.'),
(2170, 18, 30, 'inne-lleẕîne âmenû ve`amilu-ṣṣâliḥâti innâ lâ nüḍî`u ecra men aḥsene `amelâ.'),
(2171, 18, 31, 'ülâike lehüm cennâtü `adnin tecrî min taḥtihimü-l\'enhâru yüḥallevne fîhâ min esâvira min ẕehebiv veyelbesûne ŝiyâben ḫuḍram min sündüsiv veistebraḳim müttekiîne fîhâ `ale-l\'erâik. ni`me-ŝŝevâb. veḥasünet mürtefeḳâ.'),
(2172, 18, 32, 'vaḍrib lehüm meŝeler racüleyni ce`alnâ lieḥadihimâ cenneteyni min a`nâbiv veḥafefnâhümâ binaḫliv vece`alnâ beynehümâ zer`â.'),
(2173, 18, 33, 'kilte-lcenneteyni âtet ükülehâ velem tażlim minhü şey\'ev vefeccernâ ḫilâlehümâ neherâ.'),
(2174, 18, 34, 'vekâne lehû ŝemer. feḳâle liṣâḥibihî vehüve yüḥâviruhû ene ekŝeru minke mâlev vee`azzü neferâ.'),
(2175, 18, 35, 'vedeḫale cennetehû vehüve żâlimül linefsih. ḳâle mâ eżunnü en tebîde hâẕihî ebedâ.'),
(2176, 18, 36, 'vemâ eżunnü-ssâ`ate ḳâimetev veleir rudittü ilâ rabbî leecidenne ḫayram minhâ münḳalebâ.'),
(2177, 18, 37, 'ḳâle lehû ṣâḥibühû vehüve yüḥâviruhû ekeferte billeẕî ḫaleḳake min türâbin ŝümme min nuṭfetin ŝümme sevvâke racülâ.'),
(2178, 18, 38, 'lâkinnâ hüve-llâhü rabbî velâ üşrikü birabbî eḥadâ.'),
(2179, 18, 39, 'velevlâ iẕ deḫalte cenneteke ḳulte mâ şâe-llâhü lâ ḳuvvete illâ billâh. in terani ene eḳalle minke mâlev veveledâ.'),
(2180, 18, 40, 'fe`asâ rabbî ey yü\'tiyeni ḫayram min cennetike veyürsile `aleyhâ ḥusbânem mine-ssemâi fetuṣbiḥa ṣa`îden zeleḳâ.'),
(2181, 18, 41, 'ev yuṣbiḥa mâühâ gavran felen testeṭî`a lehû ṭalebâ.'),
(2182, 18, 42, 'veüḥîṭa biŝemerihî feaṣbeḥa yüḳallibü keffeyhi `alâ mâ enfeḳa fîhâ vehiye ḫâviyetün `alâ `urûşihâ veyeḳûlü yâ leytenî lem üşrik birabbî eḥadâ.'),
(2183, 18, 43, 'velem tekül lehû fietüy yenṣurûnehû min dûni-llâhi vemâ kâne münteṣirâ.'),
(2184, 18, 44, 'hünâlike-lvelâyetü lillâhi-lḥaḳḳ. hüve ḫayrun ŝevâbev veḫayrun `uḳbâ.'),
(2185, 18, 45, 'vaḍrib lehüm meŝele-lḥayâti-ddünyâ kemâin enzelnâhü mine-ssemâi faḫteleṭa bihî nebâtü-l\'arḍi feaṣbeḥa heşîmen teẕrûhü-rriyâḥ. vekâne-llâhü `alâ külli şey\'im muḳtedirâ.'),
(2186, 18, 46, 'elmâlü velbenûne zînetü-lḥayâti-ddünyâ. velbâḳiyâtu-ṣṣâliḥâtü ḫayrun `inde rabbike ŝevâbev veḫayrun emelâ.'),
(2187, 18, 47, 'veyevme nüseyyiru-lcibâle vetera-l\'arḍa bârizetev veḥaşernâhüm felem nügâdir minhüm eḥadâ.'),
(2188, 18, 48, 've`uriḍû `alâ rabbike ṣaffâ. leḳad ci\'tümûnâ kemâ ḫalaḳnâküm evvele merrah. bel za`amtüm ellen nec`ale leküm mev`idâ.'),
(2189, 18, 49, 'vevuḍi`a-lkitâbü fetere-lmücrimîne müşfiḳîne mimmâ fîhi veyeḳûlûne yâ veyletenâ mâ lihâẕe-lkitâbi lâ yügâdiru ṣagîratev velâ kebîraten illâ aḥṣâhâ. vevecedû mâ `amilû ḥâḍirâ. velâ yażlimü rabbüke eḥadâ.'),
(2190, 18, 50, 'veiẕ ḳulnâ lilmelâiketi-scüdû liâdeme fesecedû illâ iblîs. kâne mine-lcinni fefeseḳa `an emri rabbih. efetetteḫiẕûnehû veẕürriyyetehû evliyâe min dûnî vehüm leküm `adüvv. bi\'se liżżâlimîne bedelâ.'),
(2191, 18, 51, 'mâ eşhettühüm ḫalḳa-ssemâvâti vel\'arḍi velâ ḫalḳa enfüsihim. vemâ küntü mütteḫiẕe-lmüḍillîne `aḍudâ.'),
(2192, 18, 52, 'veyevme yeḳûlü nâdû şürakâiye-lleẕîne za`amtüm fede`avhüm felem yestecîbû lehüm vece`alnâ beynehüm mevbiḳâ.'),
(2193, 18, 53, 'verae-lmücrimûne-nnâra feżannû ennehüm müvâḳi`ûhâ velem yecidû `anhâ maṣrifâ.'),
(2194, 18, 54, 'veleḳad ṣarrafnâ fî hâẕe-lḳur\'âni linnâsi min külli meŝel. vekâne-l\'insânü ekŝera şey\'in cedelâ.'),
(2195, 18, 55, 'vemâ mene`a-nnâse ey yü\'minû iẕ câehümü-lhüdâ veyestagfirû rabbehüm illâ en te\'tiyehüm sünnetü-l\'evvelîne ev ye\'tiyehümü-l`aẕâbü ḳubülâ.'),
(2196, 18, 56, 'vemâ nürsilü-lmürselîne illâ mübeşşirîne vemünẕirîn. veyücâdilü-lleẕîne keferû bilbâṭili liyüdḥiḍû bihi-lḥaḳḳa vetteḫaẕû âyâtî vemâ ünẕirû hüzüvâ.'),
(2197, 18, 57, 'vemen ażlemü mimmen ẕükkira biâyâti rabbihî fea`raḍa `anhâ venesiye mâ ḳaddemet yedâh. innâ ce`alnâ `alâ ḳulûbihim ekinneten ey yefḳahûhü vefî âẕânihim vaḳrâ. vein ted`uhüm ile-lhüdâ feley yehtedû iẕen ebedâ.'),
(2198, 18, 58, 'verabbüke-lgafûru ẕü-rraḥmeh. lev yüâḫiẕühüm bimâ kesebû le`accele lehümü-l`aẕâb. bel lehüm mev`idül ley yecidû min dûnihî mev\'ilâ.'),
(2199, 18, 59, 'vetilke-lḳurâ ehleknâhüm lemmâ żalemû vece`alnâ limehlikihim mev`idâ.'),
(2200, 18, 60, 'veiẕ ḳâle mûsâ lifetâhü lâ ebraḥu ḥattâ eblüga mecme`a-lbaḥrayni ev emḍiye ḥuḳubâ.'),
(2201, 18, 61, 'felemmâ belegâ mecme`a beynihimâ nesiyâ ḥûtehümâ fetteḫaẕe sebîlehû fi-lbaḥri serabâ.'),
(2202, 18, 62, 'felemmâ câvezâ ḳâle lifetâhü âtinâ gadâenâ. leḳad leḳînâ min seferinâ hâẕâ neṣabâ.'),
(2203, 18, 63, 'ḳâle era\'eyte iẕ eveynâ ile-ṣṣaḫrati feinnî nesîtü-lḥût. vemâ ensânîhü ille-şşeyṭânü en eẕkürah. vetteḫaẕe sebîlehû fi-lbaḥr. `acebâ.'),
(2204, 18, 64, 'ḳâle ẕâlike mâ künnâ nebgi. ferteddâ `alâ âŝârihimâ ḳaṣaṣâ.'),
(2205, 18, 65, 'fevecedâ `abdem min `ibâdinâ âteynâhü raḥmetem min `indinâ ve`allemnâhü mil ledünnâ `ilmâ.'),
(2206, 18, 66, 'ḳâle lehû mûsâ hel ettebi`uke `alâ en tü`allimeni mimmâ `ullimte ruşdâ.'),
(2207, 18, 67, 'ḳâle inneke len testeṭî`a me`iye ṣabrâ.'),
(2208, 18, 68, 'vekeyfe taṣbiru `alâ mâ lem tüḥiṭ bihî ḫubrâ.'),
(2209, 18, 69, 'ḳâle setecidünî in şâe-llâhü ṣâbirav velâ a`ṣî leke emrâ.'),
(2210, 18, 70, 'ḳâle feini-tteba`tenî felâ tes\'elnî `an şey\'in ḥattâ uḥdiŝe leke minhü ẕikrâ.'),
(2211, 18, 71, 'fenṭaleḳâ. ḥattâ iẕâ rakibâ fi-ssefîneti ḫaraḳahâ. ḳâle eḫaraḳtehâ litugriḳa ehlehâ. leḳad ci\'te şey\'en imrâ.'),
(2212, 18, 72, 'ḳâle elem eḳul inneke len testeṭî`a me`iye ṣabrâ.'),
(2213, 18, 73, 'ḳâle lâ tüâḫiẕnî bimâ nesîtü velâ türhiḳnî min emrî `usrâ.'),
(2214, 18, 74, 'fenṭaleḳâ. ḥattâ iẕâ leḳiyâ gulâmen feḳatelehû ḳâle eḳatelte nefsen zekiyyetem bigayri nefs. leḳad ci\'te şey\'en nükrâ.'),
(2215, 18, 75, 'ḳâle elem eḳul leke inneke len testeṭî`a me`iye ṣabrâ.'),
(2216, 18, 76, 'ḳâle in seeltüke `an şey\'im ba`dehâ felâ tüṣâḥibnî. ḳad belagte mil ledünnî `uẕrâ.'),
(2217, 18, 77, 'fenṭaleḳâ. ḥattâ iẕâ eteyâ ehle ḳaryetin-staṭ`amâ ehlehâ feebev ey yüḍayyifûhümâ fevecedâ fîhâ cidâray yürîdü ey yenḳaḍḍa feeḳâmeh. ḳâle lev şi\'te letteḫaẕte `aleyhi ecrâ.'),
(2218, 18, 78, 'ḳâle hâẕâ firâḳu beynî vebeynik. seünebbiüke bite\'vîli mâ lem testeṭi` `aleyhi ṣabrâ.'),
(2219, 18, 79, 'emme-ssefînetü fekânet limesâkîne ya`melûne fi-lbaḥri feerattü en e`îbehâ vekâne verâehüm meliküy ye\'ḫuẕü külle sefînetin gaṣbâ.'),
(2220, 18, 80, 'veemme-lgulâmü fekâne ebevâhü mü\'mineyni feḫaşînâ ey yürhiḳahümâ ṭugyânev veküfrâ.'),
(2221, 18, 81, 'feeradnâ ey yübdilehümâ rabbühümâ ḫayram minhü zekâtev veaḳrabe ruḥmâ.'),
(2222, 18, 82, 'veemme-lcidâru fekâne ligulâmeyni yetîmeyni fi-lmedîneti vekâne taḥtehû kenzül lehümâ vekâne ebûhümâ ṣâliḥâ. feerâde rabbüke ey yeblügâ eşüddehümâ veyestaḫricâ kenzehümâ. raḥmetem mir rabbik. vemâ fe`altühû `an emrî. ẕâlike te\'vîlü mâ lem tesṭi` `aleyhi ṣabrâ.'),
(2223, 18, 83, 'veyes\'elûneke `an ẕi-lḳarneyn. ḳul seetlû `aleyküm minhü ẕikrâ.'),
(2224, 18, 84, 'innâ mekkennâ lehû fi-l\'arḍi veâteynâhü min külli şey\'in sebebâ.'),
(2225, 18, 85, 'feetbe`a sebebâ.'),
(2226, 18, 86, 'ḥattâ iẕâ belega magribe-şşemsi vecedehâ tagrubü fî `aynin ḥamietiv vevecede `indehâ ḳavmâ. ḳulnâ yâ ẕe-lḳarneyni immâ en tü`aẕẕibe veimmâ en tetteḫiẕe fîhim ḥusnâ.'),
(2227, 18, 87, 'ḳâle emmâ men żaleme fesevfe nü`aẕẕibühû ŝümme yüraddü ilâ rabbihî feyü`aẕẕibühû `aẕâben nükrâ.'),
(2228, 18, 88, 'veemmâ men âmene ve`amile ṣâliḥan felehû cezâen-lḥusnâ. veseneḳûlü lehû min emrinâ yüsrâ.'),
(2229, 18, 89, 'ŝümme etbe`a sebebâ.'),
(2230, 18, 90, 'ḥattâ iẕâ belega maṭli`a-şşemsi vecedehâ taṭlü`u `alâ ḳavmil lem nec`al lehüm min dûnihâ sitrâ.'),
(2231, 18, 91, 'keẕâlik. veḳad eḥaṭnâ bimâ ledeyhi ḫubrâ.'),
(2232, 18, 92, 'ŝümme etbe`a sebebâ.'),
(2233, 18, 93, 'ḥattâ iẕâ belegae beyne-sseddeyni vecede min dûnihimâ ḳavmel lâ yekâdûne yefḳahûne ḳavlâ.'),
(2234, 18, 94, 'ḳâlû yâ ẕe-lḳarneyni inne ye\'cûce veme\'cûce müfsidûne fi-l\'arḍi fehel nec`alü leke ḫarcen `alâ en tec`ale beynenâ vebeynehüm seddâ.'),
(2235, 18, 95, 'ḳâle mâ mekkennî fîhi rabbî ḫayrun fee`înûnî biḳuvvetin ec`al beyneküm vebeynehüm radmâ.'),
(2236, 18, 96, 'âtûnî zübera-lḥadîd. ḥattâ iẕâ sâvâ beyne-ṣṣadefeyni ḳâle-nfüḫû. ḥattâ iẕâ ce`alehû nâran ḳâle âtûnî üfrig `aleyhi ḳiṭrâ.'),
(2237, 18, 97, 'feme-sṭâ`û ey yażherûhü veme-steṭâ`û lehû naḳbâ.'),
(2238, 18, 98, 'ḳâle hâẕâ raḥmetüm mir rabbî. feiẕâ câe va`dü rabbî ce`alehû dekkâ\'. vekâne va`dü rabbî ḥaḳḳâ.'),
(2239, 18, 99, 'veteraknâ ba`ḍahüm yevmeiẕiy yemûcü fî ba`ḍiv venüfiḫa fi-ṣṣûri fecema`nâhüm cem`â.'),
(2240, 18, 100, 've`araḍnâ cehenneme yevmeiẕil lilkâfirîne `arḍâ.'),
(2241, 18, 101, 'elleẕîne kânet a`yünühüm fî giṭâin `an ẕikrî vekânû lâ yesteṭî`ûne sem`â.'),
(2242, 18, 102, 'efeḥasibe-lleẕîne keferû ey yetteḫiẕû `ibâdî min dûnî evliyâ\'. innâ a`tednâ cehenneme lilkâfirîne nüzülâ.'),
(2243, 18, 103, 'ḳul hel nünebbiüküm bil\'aḫserîne a`mâlâ.'),
(2244, 18, 104, 'elleẕîne ḍalle sa`yühüm fi-lḥayâti-ddünyâ vehüm yaḥsebûne ennehüm yuḥsinûne ṣun`â.'),
(2245, 18, 105, 'ülâike-lleẕîne keferû biâyâti rabbihim veliḳâihî feḥabiṭat a`mâlühüm felâ nüḳîmü lehüm yevme-lḳiyâmeti veznâ.'),
(2246, 18, 106, 'ẕâlike cezâühüm cehennemü bimâ keferû vetteḫaẕû âyâtî verusülî hüzüvâ.'),
(2247, 18, 107, 'inne-lleẕîne âmenû ve`amilu-ṣṣâliḥâti kânet lehüm cennâtü-lfirdevsi nüzülâ.'),
(2248, 18, 108, 'ḫâlidîne fîhâ lâ yebgûne `anhâ ḥivelâ.'),
(2249, 18, 109, 'ḳul lev kâne-lbaḥru midâdel likelimâti rabbî lenefide-lbaḥru ḳable en tenfede kelimâtü rabbî velev ci\'nâ bimiŝlihî mededâ.'),
(2250, 18, 110, 'ḳul innemâ ene beşerum miŝlüküm yûḥâ ileyye ennemâ ilâhüküm ilâhüv vâḥid. femen kâne yercû liḳâe rabbihî felya`mel `amelen ṣâliḥav velâ yüşrik bi`ibâdeti rabbihî eḥadâ.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'kâf-hâ-yâ-`ayn-ṣâd.'),
(2252, 19, 2, 'ẕikru raḥmeti rabbike `abdehû zekeriyyâ.'),
(2253, 19, 3, 'iẕ nâdâ rabbehû nidâen ḫafiyyâ.'),
(2254, 19, 4, 'ḳâle rabbi innî vehene-l`ażmü minnî veşte`ale-rra\'sü şeybev velem eküm bidü`âike rabbi şeḳiyyâ.'),
(2255, 19, 5, 'veinnî ḫiftü-lmevâliye miv verâî vekâneti-mraetî `âḳiran feheb lî mil ledünke veliyyâ.'),
(2256, 19, 6, 'yeriŝünî veyeriŝü min âli ya`ḳûb. vec`alhü rabbi raḍiyyâ.'),
(2257, 19, 7, 'yâ zekeriyyâ innâ nübeşşiruke bigulâmin-smühû yaḥyâ lem nec`al lehû min ḳablü semiyyâ.'),
(2258, 19, 8, 'ḳâle rabbi ennâ yekûnü lî gulâmüv vekâneti-mraetî `âḳirav veḳad belagtü mine-lkiberi `itiyyâ.'),
(2259, 19, 9, 'ḳâle keẕâlik. ḳâle rabbüke hüve `aleyye heyyinüv veḳad ḫalaḳtüke min ḳablü velem tekü şey\'â.'),
(2260, 19, 10, 'ḳâle rabbi-c`al lî âyeh. ḳâle âyetüke ellâ tükellime-nnâse ŝelâŝe leyâlin seviyyâ.'),
(2261, 19, 11, 'feḫarace `alâ ḳavmihî mine-lmiḥrâbi feevḥâ ileyhim en sebbiḥû bükratev ve`aşiyyâ.'),
(2262, 19, 12, 'yâ yaḥyâ ḫuẕi-lkitâbe biḳuvveh. veâteynâhü-lḥukme ṣabiyyâ.'),
(2263, 19, 13, 'veḥanânem mil ledünnâ vezekâh. vekâne teḳiyyâ.'),
(2264, 19, 14, 'veberram bivâlideyhi velem yekün cebbâran `aṣiyyâ.'),
(2265, 19, 15, 'veselâmün `aleyhi yevme vulide veyevme yemûtü veyevme yüb`aŝü ḥayyâ.'),
(2266, 19, 16, 'veẕkür fi-lkitâbi meryem. iẕi-ntebeẕet min ehlihâ mekânen şerḳiyyâ.'),
(2267, 19, 17, 'fetteḫaẕet min dûnihim ḥicâben feerselnâ ileyhâ rûḥanâ fetemeŝŝele lehâ beşeran seviyyâ.'),
(2268, 19, 18, 'ḳâlet innî e`ûẕü birraḥmâni minke in künte teḳiyyâ.'),
(2269, 19, 19, 'ḳâle innemâ ene rasûlü rabbik. liehebe leki gulâmen zekiyyâ.'),
(2270, 19, 20, 'ḳâlet ennâ yekûnü lî gulâmüv velem yemsesnî beşeruv velem ekü begiyyâ.'),
(2271, 19, 21, 'ḳâle keẕâlik. ḳâle rabbüki hüve `aleyye heyyin. velinec`alehû âyetel linnâsi veraḥmetem minnâ. vekâne emram maḳḍiyyâ.'),
(2272, 19, 22, 'feḥamelethü fentebeẕet bihî mekânen ḳaṣiyyâ.'),
(2273, 19, 23, 'feecâehe-lmeḫâḍu ilâ ciẕ`i-nnaḫleh. ḳâlet yâ leytenî mittü ḳable hâẕâ veküntü nesyem mensiyyâ.'),
(2274, 19, 24, 'fenâdâhâ min taḥtihâ ellâ taḥzenî ḳad ce`ale rabbüki taḥteki seriyyâ.'),
(2275, 19, 25, 'vehüzzî ileyki biciẕ`i-nnaḫleti tüṣâḳiṭ `aleyki ruṭaben ceniyyâ.'),
(2276, 19, 26, 'fekülî veşrabî veḳarrî `aynâ. feimmâ terayinne mine-lbeşeri eḥaden feḳûlî innî neẕertü lirraḥmâni ṣavmen felen ükellime-lyevme insiyyâ.'),
(2277, 19, 27, 'feetet bihî ḳavmehâ taḥmilüh. ḳâlû yâ meryemü leḳad ci\'ti şey\'en feriyyâ.'),
(2278, 19, 28, 'yâ uḫte hârûne mâ kâne ebûki-mrae sev\'iv vemâ kânet ümmüki begiyyâ.'),
(2279, 19, 29, 'feeşârat ileyh. ḳâlû keyfe nükellimü men kâne fi-lmehdi ṣabiyyâ.'),
(2280, 19, 30, 'ḳâle innî `abdü-llâh. âtâniye-lkitâbe vece`alenî nebiyyâ.'),
(2281, 19, 31, 'vece`alenî mübâraken eyne mâ künt. veevṣânî biṣṣalâti vezzekâti mâ dümtü ḥayyâ.'),
(2282, 19, 32, 'veberram bivâlidetî. velem yec`alnî cebbâran şeḳiyyâ.'),
(2283, 19, 33, 'vesselâmü `aleyye yevme vulittü veyevme emûtü veyevme üb`aŝü ḥayyâ.'),
(2284, 19, 34, 'ẕâlike `îse-bnü meryem. ḳavle-lḥaḳḳi-lleẕî fîhi yemterûn.'),
(2285, 19, 35, 'mâ kâne lillâhi ey yetteḫiẕe miv veledin sübḥâneh. iẕâ ḳaḍâ emran feinnemâ yeḳûlü lehû kün feyekûn.'),
(2286, 19, 36, 'veinne-llâhe rabbî verabbüküm fa`büdûh. hâẕâ ṣirâṭum müsteḳîm.'),
(2287, 19, 37, 'faḫtelefe-l\'aḥzâbü mim beynihim. feveylül lilleẕîne keferû mim meşhedi yevmin `ażîm.'),
(2288, 19, 38, 'esmi` bihim veebṣir yevme ye\'tûnenâ lâkini-żżâlimûne-lyevme fî ḍalâlim mübîn.'),
(2289, 19, 39, 'veenẕirhüm yevme-lḥasrati iẕ ḳuḍiye-l\'emr. vehüm fî gafletiv vehüm lâ yü\'minûn.'),
(2290, 19, 40, 'innâ naḥnü neriŝü-l\'arḍa vemen `aleyhâ veileynâ yürce`ûn.'),
(2291, 19, 41, 'veẕkür fi-lkitâbi ibrâhîm. innehû kâne ṣiddîḳan nebiyyâ.'),
(2292, 19, 42, 'iẕ ḳâle liebîhi yâ ebeti lime ta`büdü mâ lâ yesme`u velâ yübṣiru velâ yugnî `anke şey\'â.'),
(2293, 19, 43, 'yâ ebeti innî ḳad câenî mine-l`ilmi mâ lem ye\'tike fettebi`nî ehdike ṣirâṭan seviyyâ.'),
(2294, 19, 44, 'yâ ebeti lâ ta`büdi-şşeyṭân. inne-şşeyṭâne kâne lirraḥmâni `aṣiyyâ.'),
(2295, 19, 45, 'yâ ebeti innî eḫâfü ey yemesseke `aẕâbüm mine-rraḥmâni fetekûne lişşeyṭâni veliyyâ.'),
(2296, 19, 46, 'ḳâle erâgibün ente `an âlihetî yâ ibrâhîm. leil lem tentehi leercümenneke vehcürnî meliyyâ.'),
(2297, 19, 47, 'ḳâle selâmün `aleyk. seestagfiru leke rabbî. innehû kâne bî ḥafiyyâ.'),
(2298, 19, 48, 'vea`tezilüküm vemâ ted`ûne min dûni-llâhi veed`û rabbî. `asâ ellâ ekûne bidü`âi rabbî şeḳiyyâ.'),
(2299, 19, 49, 'felemme-`tezelehüm vemâ ya`büdûne min dûni-llâhi vehebnâ lehû isḥâḳa veya`ḳûb. veküllen ce`alnâ nebiyyâ.'),
(2300, 19, 50, 'vevehebnâ lehüm mir raḥmetinâ vece`alnâ lehüm lisâne ṣidḳin `aliyyâ.'),
(2301, 19, 51, 'veẕkür fi-lkitâbi mûsâ. innehû kâne muḫleṣav vekâne rasûlen nebiyyâ.'),
(2302, 19, 52, 'venâdeynâhü min cânibi-ṭṭûri-l\'eymeni veḳarrabnâhü neciyyâ.'),
(2303, 19, 53, 'vevehebnâ lehû mir raḥmetinâ eḫâhü hârûne nebiyyâ.'),
(2304, 19, 54, 'veẕkür fi-lkitâbi ismâ`îl. innehû kâne ṣâdiḳa-lva`di vekâne rasûlen nebiyyâ.'),
(2305, 19, 55, 'vekâne ye\'müru ehlehû biṣṣalâti vezzekâh. vekâne `inde rabbihî merḍiyyâ.'),
(2306, 19, 56, 'veẕkür fi-lkitâbi idrîs. innehû kâne ṣiddîḳan nebiyyâ.'),
(2307, 19, 57, 'verafa`nâhü mekânen `aliyyâ.'),
(2308, 19, 58, 'ülâike-lleẕîne en`ame-llâhü `aleyhim mine-nnebiyyîne min ẕürriyyeti âdeme vemimmen ḥamelnâ me`a nûḥ. vemin ẕürriyyeti ibrâhîme veisrâîle vemimmen hedeynâ vectebeynâ. iẕâ tütlâ `aleyhim âyâtü-rraḥmâni ḫarrû süccedev vebükiyyâ.'),
(2309, 19, 59, 'feḫalefe mim ba`dihim ḫalfün eḍâ`u-ṣṣalâte vettebe`ü-şşehevâti fesevfe yelḳavne gayyâ.'),
(2310, 19, 60, 'illâ men tâbe veâmene ve`amile ṣâliḥan feülâike yedḫulûne-lcennete velâ yużlemûne şey\'â.'),
(2311, 19, 61, 'cennâti `adnini-lletî ve`ade-rraḥmânü `ibâdehû bilgayb. innehû kâne va`dühû me\'tiyyâ.'),
(2312, 19, 62, 'lâ yesme`ûne fîhâ lagven illâ selâmâ. velehüm rizḳuhüm fîhâ bükratev ve`aşiyyâ.'),
(2313, 19, 63, 'tilke-lcennetü-lletî nûriŝü min `ibâdinâ men kâne teḳiyyâ.'),
(2314, 19, 64, 'vemâ netenezzelü illâ biemri rabbik. lehû mâ beyne eydînâ vemâ ḫalfenâ vemâ beyne ẕâlik. vemâ kâne rabbüke nesiyyâ.'),
(2315, 19, 65, 'rabbü-ssemâvâti vel\'arḍi vemâ beynehümâ fa`büdhü vaṣṭabir li`ibâdetih. hel ta`lemü lehû semiyyâ.'),
(2316, 19, 66, 'veyeḳûlü-l\'insânü eiẕâ mâ mittü lesevfe uḫracü ḥayyâ.'),
(2317, 19, 67, 'evelâ yeẕküru-l\'insânü ennâ ḫalaḳnâhü min ḳablü velem yekü şey\'â.'),
(2318, 19, 68, 'feverabbike lenaḥşürannehüm veşşeyâṭîne ŝümme lenuḥḍirannehüm ḥavle cehenneme ciŝiyyâ.'),
(2319, 19, 69, 'ŝümme lenenzi`anne min külli şî`atin eyyühüm eşeddü `ale-rraḥmâni `itiyyâ.'),
(2320, 19, 70, 'ŝümme lenaḥnü a`lemü billeẕîne hüm evlâ bihâ ṣiliyyâ.'),
(2321, 19, 71, 'veim minküm illâ vâridühâ. kâne `alâ rabbike ḥatmem maḳḍiyyâ.'),
(2322, 19, 72, 'ŝümme nünecci-lleẕîne-tteḳav veneẕeru-żżâlimîne fîhâ ciŝiyyâ.'),
(2323, 19, 73, 'veiẕâ tütlâ `aleyhim âyâtünâ beyyinâtin ḳâle-lleẕîne keferû lilleẕîne âmenû eyyü-lferîḳayni ḫayrum meḳâmev veaḥsenü nediyyâ.'),
(2324, 19, 74, 'vekem ehleknâ ḳablehüm min ḳarnin hüm aḥsenü eŝâŝev veri\'yâ.'),
(2325, 19, 75, 'ḳul men kâne fi-ḍḍalâleti felyemdüd lehü-rraḥmânü meddâ. ḥattâ iẕâ raev mâ yû`adûne imme-l`aẕâbe veimme-ssâ`ah. feseya`lemûne men hüve şerrum mekânev veaḍ`afü cündâ.'),
(2326, 19, 76, 'veyezîdü-llâhü-lleẕîne-htedev hüden. velbâḳiyâtu-ṣṣâliḥâtü ḫayrun `inde rabbike ŝevâbev veḫayrum meraddâ.'),
(2327, 19, 77, 'eferaeyte-lleẕî kefera biâyâtinâ veḳâle leûteyenne mâlev veveledâ.'),
(2328, 19, 78, 'eṭṭale`a-lgaybe emi-tteḫaẕe `inde-rraḥmâni `ahdâ.'),
(2329, 19, 79, 'kellâ. senektübü mâ yeḳûlü venemüddü lehû mine-l`aẕâbi meddâ.'),
(2330, 19, 80, 'veneriŝühû mâ yeḳûlü veye\'tînâ ferdâ.'),
(2331, 19, 81, 'vetteḫaẕû min dûni-llâhi âlihetel liyekûnû lehüm `izzâ.'),
(2332, 19, 82, 'kellâ. seyekfürûne bi`ibâdetihim veyekûnûne `aleyhim ḍiddâ.'),
(2333, 19, 83, 'elem tera ennâ erselne-şşeyâṭîne `ale-lkâfirîne teüzzühüm ezzâ.'),
(2334, 19, 84, 'felâ ta`cel `aleyhim. innemâ ne`uddü lehüm `addâ.'),
(2335, 19, 85, 'yevme naḥşüru-lmütteḳîne ile-rraḥmâni vefdâ.'),
(2336, 19, 86, 'venesûḳu-lmücrimîne ilâ cehenneme virdâ.'),
(2337, 19, 87, 'lâ yemlikûne-şşefâ`ate illâ meni-tteḫaẕe `inde-rraḥmâni `ahdâ.'),
(2338, 19, 88, 'veḳâlü-tteḫaẕe-rraḥmânü veledâ.'),
(2339, 19, 89, 'leḳad ci\'tüm şey\'en iddâ.'),
(2340, 19, 90, 'tekâdü-ssemâvâtü yetefeṭṭarne minhü vetenşeḳḳu-l\'arḍu veteḫirru-lcibâlü heddâ.'),
(2341, 19, 91, 'en de`av lirraḥmâni veledâ.'),
(2342, 19, 92, 'vemâ yembegî lirraḥmâni ey yetteḫiẕe veledâ.'),
(2343, 19, 93, 'in küllü men fi-ssemâvâti vel\'arḍi illâ âti-rraḥmâni `abdâ.'),
(2344, 19, 94, 'leḳad aḥṣâhüm ve`addehüm `addâ.'),
(2345, 19, 95, 'veküllühüm âtîhi yevme-lḳiyâmeti ferdâ.'),
(2346, 19, 96, 'inne-lleẕîne âmenû ve`amilu-ṣṣâliḥâti seyec`alü lehümü-rraḥmânü vuddâ.'),
(2347, 19, 97, 'feinnemâ yessernâhü bilisânike litübeşşira bihi-lmütteḳîne vetünẕira bihî ḳavmel lüddâ.'),
(2348, 19, 98, 'vekem ehleknâ ḳablehüm min ḳarn. hel tüḥissü minhüm min eḥadin ev tesme`u lehüm rikzâ.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'ṭâ-hâ.'),
(2350, 20, 2, 'mâ enzelnâ `aleyke-lḳur\'âne liteşḳâ.'),
(2351, 20, 3, 'illâ teẕkiratel limey yaḫşâ.'),
(2352, 20, 4, 'tenzîlem mimmen ḫaleḳa-l\'arḍa vessemâvâti-l`ulâ.'),
(2353, 20, 5, 'erraḥmânü `ale-l`arşi-stevâ.'),
(2354, 20, 6, 'lehû mâ fi-ssemâvâti vemâ fi-l\'arḍi vemâ beynehümâ vemâ taḥte-ŝŝerâ.'),
(2355, 20, 7, 'vein techer bilḳavli feinnehû ya`lemü-ssirra veaḫfâ.'),
(2356, 20, 8, 'allâhü lâ ilâhe illâ hû. lehü-l\'esmâü-lḥusnâ.'),
(2357, 20, 9, 'vehel etâke ḥadîŝü mûsâ.'),
(2358, 20, 10, 'iẕ raâ nâran feḳâle liehlihi-mküŝû innî ânestü nâral le`allî âtîküm minhâ biḳabesin ev ecidü `ale-nnâri hüdâ.'),
(2359, 20, 11, 'felemmâ etâhâ nûdiye yâ mûsâ.'),
(2360, 20, 12, 'innî ene rabbüke faḫla` na`leyk. inneke bilvâdi-lmüḳaddesi ṭuvâ.'),
(2361, 20, 13, 'veene-ḫtertüke festemi` limâ yûḥâ.'),
(2362, 20, 14, 'innenî ene-llâhü lâ ilâhe illâ ene fa`büdnî veeḳimi-ṣṣalâte liẕikrî.'),
(2363, 20, 15, 'inne-ssâ`ate âtiyetün ekâdü uḫfîhâ litüczâ küllü nefsim bimâ tes`â.'),
(2364, 20, 16, 'felâ yeṣuddenneke `anhâ mel lâ yü\'minü bihâ vettebe`a hevâhü feterdâ.'),
(2365, 20, 17, 'vemâ tilke biyemînike yâ mûsâ.'),
(2366, 20, 18, 'ḳâle hiye `aṣây. etevekkeü `aleyhâ veehüşşü bihâ `alâ ganemî veliye fîhâ meâribü uḫrâ.'),
(2367, 20, 19, 'ḳâle elḳihâ yâ mûsâ.'),
(2368, 20, 20, 'feelḳâhâ feiẕâ hiye ḥayyetün tes`â.'),
(2369, 20, 21, 'ḳâle ḫuẕhâ velâ teḫaf. senü`îdühâ sîratehe-l\'ûlâ.'),
(2370, 20, 22, 'vaḍmüm yedeke ilâ cenâḥike taḫruc beyḍâe min gayri sûin âyeten uḫrâ.'),
(2371, 20, 23, 'linüriyeke min âyâtine-lkübrâ.'),
(2372, 20, 24, 'iẕheb ilâ fir`avne innehû ṭagâ.'),
(2373, 20, 25, 'ḳâle rabbi-şraḥ lî ṣadrî.'),
(2374, 20, 26, 'veyessir lî emrî.'),
(2375, 20, 27, 'vaḥlül `uḳdetem mil lisânî.'),
(2376, 20, 28, 'yefḳahû ḳavlî.'),
(2377, 20, 29, 'vec`al lî vezîram min ehlî.'),
(2378, 20, 30, 'hârûne eḫî.'),
(2379, 20, 31, 'üşdüd bihî ezrî.'),
(2380, 20, 32, 'veeşrikhü fî emrî.'),
(2381, 20, 33, 'key nüsebbiḥake keŝîrâ.'),
(2382, 20, 34, 'veneẕkürake keŝîrâ.'),
(2383, 20, 35, 'inneke künte binâ beṣîrâ.'),
(2384, 20, 36, 'ḳâle ḳad ûtîte sü\'leke yâ mûsâ.'),
(2385, 20, 37, 'veleḳad menennâ `aleyke merraten uḫrâ.'),
(2386, 20, 38, 'iẕ evḥaynâ ilâ ümmike mâ yûḥâ.'),
(2387, 20, 39, 'eni-ḳẕifîhi fi-ttâbûti faḳẕifîhi fi-lyemmi felyülḳihi-lyemmü bissâḥili ye\'ḫuẕhü `adüvvül lî ve`adüvvul leh. veelḳaytü `aleyke meḥabbetem minnî. velituṣne`a `alâ `aynî.'),
(2388, 20, 40, 'iẕ temşî uḫtüke feteḳûlü hel edüllüküm `alâ mey yekfülüh. feraca`nâke ilâ ümmike key teḳarra `aynühâ velâ taḥzen. veḳatelte nefsen fenecceynâke mine-lgammi vefetennâke fütûnâ. felebiŝte sinîne fî ehli medyene ŝümme ci\'te `alâ ḳaderiy yâ mûsâ.'),
(2389, 20, 41, 'vaṣṭana`tüke linefsî.'),
(2390, 20, 42, 'iẕheb ente veeḫûke biâyâtî velâ teniyâ fî ẕikrî.'),
(2391, 20, 43, 'iẕhebâ ilâ fir`avne innehû ṭagâ.'),
(2392, 20, 44, 'feḳûlâ lehû ḳavlel leyyinel le`allehû yeteẕekkeru ev yaḫşâ.'),
(2393, 20, 45, 'ḳâlâ rabbenâ innenâ neḫâfü ey yefruṭa `aleynâ ev ey yaṭgâ.'),
(2394, 20, 46, 'ḳâle lâ teḫâfâ innenî me`akümâ esme`u veerâ.'),
(2395, 20, 47, 'fe\'tiyâhü feḳûlâ innâ rasûlâ rabbike feersil me`anâ benî isrâîle velâ tü`aẕẕibhüm. ḳad ci\'nâke biâyetim mir rabbik. vesselâmü `alâ meni-ttebe`a-lhüdâ.'),
(2396, 20, 48, 'innâ ḳad ûḥiye ileynâ enne-l`aẕâbe `alâ men keẕẕebe vetevellâ.'),
(2397, 20, 49, 'ḳâle femer rabbükümâ yâ mûsâ.'),
(2398, 20, 50, 'ḳâle rabbüne-lleẕî a`ṭâ külle şey\'in ḫalḳahû ŝümme hedâ.'),
(2399, 20, 51, 'ḳâle femâ bâlü-lḳurûni-l\'ûlâ.'),
(2400, 20, 52, 'ḳâle `ilmühâ `inde rabbî fî kitâb. lâ yeḍillü rabbî velâ yensâ.'),
(2401, 20, 53, 'elleẕî ce`ale lekümü-l\'arḍa mehdev veseleke leküm fîhâ sübülev veenzele mine-ssemâi mââ. feaḫracnâ bihî ezvâcem min nebetin şettâ.'),
(2402, 20, 54, 'külû ver`av en`âmeküm. inne fî ẕâlike leâyâtil liüli-nnühâ.'),
(2403, 20, 55, 'minhâ ḫalaḳnâküm vefîhâ nü`îdüküm veminhâ nuḫricüküm târaten uḫrâ.'),
(2404, 20, 56, 'veleḳad eraynâhü âyâtinâ küllehâ fekeẕẕebe veebâ.'),
(2405, 20, 57, 'ḳâle eci\'tenâ lituḫricenâ min arḍinâ bisiḥrike yâ mûsâ.'),
(2406, 20, 58, 'felene\'tiyenneke bisiḥrim miŝlihî fec`al beynenâ vebeyneke mev`idel lâ nuḫlifühû naḥnü velâ ente mekânen süvâ.'),
(2407, 20, 59, 'ḳâle mev`idüküm yevmü-zzîneti veey yuḥşera-nnâsü ḍuḥâ.'),
(2408, 20, 60, 'fetevellâ fir`avnü feceme`a keydehû ŝümme etâ.'),
(2409, 20, 61, 'ḳâle lehüm mûsâ veyleküm lâ tefterû `ale-llâhi keẕiben feyüsḥiteküm bi`aẕâb. veḳad ḫâbe meni-fterâ.'),
(2410, 20, 62, 'fetenâza`û emrahüm beynehüm veeserrü-nnecvâ.'),
(2411, 20, 63, 'ḳâlû in hâẕâni lesâḥirâni yürîdâni ey yuḫricâküm min arḍiküm bisiḥrihimâ veyeẕhebâ biṭarîḳatikümü-lmüŝlâ.'),
(2412, 20, 64, 'feecmi`û keydeküm ŝümme-\'tû ṣaffâ. veḳad efleḥa-lyevme meni-sta`lâ.'),
(2413, 20, 65, 'ḳâlû yâ mûsâ immâ en tülḳiye veimmâ en nekûne evvele men elḳâ.'),
(2414, 20, 66, 'ḳâle bel elḳû. feiẕâ ḥibâlühüm ve`iṣiyyühüm yüḫayyelü ileyhi min siḥrihim ennehâ tes`â.'),
(2415, 20, 67, 'feevcese fî nefsihî ḫîfetem mûsâ.'),
(2416, 20, 68, 'ḳulnâ lâ teḫaf inneke ente-l\'a`lâ.'),
(2417, 20, 69, 'veelḳi mâ fî yemînike telḳaf mâ ṣane`û. innemâ ṣane`û keydü sâḥir. velâ yüfliḥu-ssâḥiru ḥayŝü etâ.'),
(2418, 20, 70, 'feülḳiye-sseḥaratü sücceden ḳâlû âmennâ birabbi hârûne vemûsâ.'),
(2419, 20, 71, 'ḳâle âmentüm lehû ḳable en âẕene leküm. innehû lekebîrukümü-lleẕî `allemekümü-ssiḥr. feleüḳaṭṭi`anne eydiyeküm veercüleküm min ḫilâfiv veleüṣallibenneküm fî cüẕû`i-nnaḫl. veleta`lemünne eyyünâ eşeddü `aẕâbev veebḳâ.'),
(2420, 20, 72, 'ḳâlû len nü\'ŝirake `alâ mâ câenâ mine-lbeyyinâti velleẕî feṭaranâ faḳḍi mâ ente ḳâḍ. innemâ taḳḍî hâẕihi-lḥayâte-ddünyâ.'),
(2421, 20, 73, 'innâ âmennâ birabbinâ liyagfira lenâ ḫaṭâyânâ vemâ ekrahtenâ `aleyhi mine-ssiḥr. vellâhü ḫayruv veebḳâ.'),
(2422, 20, 74, 'innehû mey ye\'ti rabbehû mücrimen feinne lehû cehennem. lâ yemûtü fîhâ velâ yaḥyâ.'),
(2423, 20, 75, 'vemey ye\'tihî mü\'minen ḳad `amile-ṣṣâliḥâti feülâike lehümü-dderacâtü-l`ulâ.'),
(2424, 20, 76, 'cennâtü `adnin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. veẕâlike cezâü men tezekkâ.'),
(2425, 20, 77, 'veleḳad evḥaynâ ilâ mûsâ en esri bi`ibâdî faḍrib lehüm ṭarîḳan fi-lbaḥri yebesâ. lâ teḫâfü derakev velâ taḫşâ.'),
(2426, 20, 78, 'feetbe`ahüm fir`avnü bicünûdihî fegaşiyehüm mine-lyemmi mâ gaşiyehüm.'),
(2427, 20, 79, 'veeḍalle fir`avnü ḳavmehû vemâ hedâ.'),
(2428, 20, 80, 'yâ benî isrâîle ḳad enceynâküm min `adüvviküm veve`adnâküm cânibe-ṭṭûri-l\'eymene venezzelnâ `aleykümü-lmenne vesselvâ.'),
(2429, 20, 81, 'külû min ṭayyibâti mâ razaḳnâküm velâ taṭgav fîhi feyeḥille `aleyküm gaḍabî. vemey yaḥlil `aleyhi gaḍabî feḳad hevâ.'),
(2430, 20, 82, 'veinnî legaffârul limen tâbe veâmene ve`amile ṣâliḥan ŝümme-htedâ.'),
(2431, 20, 83, 'vemâ a`celeke `an ḳavmike yâ mûsâ.'),
(2432, 20, 84, 'ḳâle hüm ülâi `alâ eŝerî ve`aciltü ileyke rabbi literḍâ.'),
(2433, 20, 85, 'ḳâle feinnâ ḳad fetennâ ḳavmeke mim ba`dike veeḍallehümü-ssâmiriyy.'),
(2434, 20, 86, 'ferace`a mûsâ ilâ ḳavmihî gaḍbâne esifâ. ḳâle yâ ḳavmi elem ye`idküm rabbüküm va`den ḥasenâ. efeṭâle `aleykümü-l`ahdü em erattüm ey yeḥille `aleyküm gaḍabüm mir rabbiküm feaḫleftüm mev`idî.'),
(2435, 20, 87, 'ḳâlû mâ aḫlefnâ mev`ideke bimelkinâ velâkinnâ ḥummilnâ evzâram min zîneti-lḳavmi feḳaẕefnâhâ fekeẕâlike elḳa-ssâmiriyy.'),
(2436, 20, 88, 'feaḫrace lehüm `iclen cesedel lehû ḫuvârun feḳâlû hâẕâ ilâhüküm veilâhü mûsâ fenesî.'),
(2437, 20, 89, 'efelâ yeravne ellâ yerci`u ileyhim ḳavlev velâ yemlikü lehüm ḍarrav velâ nef`â.'),
(2438, 20, 90, 'veleḳad ḳâle lehüm hârûnü min ḳablü yâ ḳavmi innemâ fütintüm bih. veinne rabbekümü-rraḥmânü fettebi`ûnî veeṭî`û emrî.'),
(2439, 20, 91, 'ḳâlû len nebraḥa `aleyhi `âkifîne ḥattâ yerci`a ileynâ mûsâ.'),
(2440, 20, 92, 'ḳâle yâ hârûnü mâ mene`ake iẕ raeytehüm ḍallû.'),
(2441, 20, 93, 'ellâ tettebi`an. efe`aṣayte emrî.'),
(2442, 20, 94, 'ḳâle yebneümme lâ te\'ḫuẕ biliḥyetî velâ bira\'sî. innî ḫaşîtü en teḳûle ferraḳte beyne benî isrâîle velem terḳub ḳavlî.'),
(2443, 20, 95, 'ḳâle femâ ḫaṭbüke yâ sâmiriyy.'),
(2444, 20, 96, 'ḳâle beṣurtü bimâ lem yebṣurû bihî feḳabaḍtü ḳabḍatem min eŝeri-rrasûli fenebeẕtühâ vekeẕâlike sevvelet lî nefsî.'),
(2445, 20, 97, 'ḳâle feẕheb feinne leke fi-lḥayâti en teḳûle lâ misâs. veinne leke mev`idel len tuḫlefeh. venżur ilâ ilâhike-lleẕî żalte `aleyhi `âkifâ. lenüḥarriḳannehû ŝümme lenensifennehû fi-lyemmi nesfâ.'),
(2446, 20, 98, 'innemâ ilâhükümü-llâhü-lleẕî lâ ilâhe illâ hû. vesi`a külle şey\'in `ilmâ.'),
(2447, 20, 99, 'keẕâlike neḳuṣṣu `aleyke min embâi mâ ḳad sebeḳ. veḳad âteynâke mil ledünnâ ẕikrâ.'),
(2448, 20, 100, 'men a`raḍa `anhü feinnehû yaḥmilü yevme-lḳiyâmeti vizrâ.'),
(2449, 20, 101, 'ḫâlidîne fîh. vesâe lehüm yevme-lḳiyâmeti ḥimlâ.'),
(2450, 20, 102, 'yevme yünfeḫu fi-ṣṣûri venaḥşüru-lmücrimîne yevmeiẕin zürḳâ.'),
(2451, 20, 103, 'yeteḫâfetûne beynehüm il lebiŝtüm illâ `aşrâ.'),
(2452, 20, 104, 'naḥnü a`lemü bimâ yeḳûlûne iẕ yeḳûlü emŝelühüm ṭarîḳaten il lebiŝtüm illâ yevmâ.'),
(2453, 20, 105, 'veyes\'elûneke `ani-lcibâli feḳul yensifühâ rabbî nesfâ.'),
(2454, 20, 106, 'feyeẕeruhâ ḳâ`an ṣafṣafâ.'),
(2455, 20, 107, 'lâ terâ fîhâ `ivecev velâ emtâ.'),
(2456, 20, 108, 'yevmeiẕiy yettebi`ûne-ddâ`iye lâ `ivece leh. veḫaşe`ati-l\'aṣvâtü lirraḥmâni felâ tesme`u illâ hemsâ.'),
(2457, 20, 109, 'yevmeiẕil lâ tenfe`u-şşefâ`atü illâ men eẕine lehü-rraḥmânü veraḍiye lehû ḳavlâ.'),
(2458, 20, 110, 'ya`lemü mâ beyne eydîhim vemâ ḫalfehüm velâ yüḥîṭûne bihî `ilmâ.'),
(2459, 20, 111, 've`aneti-lvucûhü lilḥayyi-lḳayyûm. veḳad ḫâbe men ḥamele żulmâ.'),
(2460, 20, 112, 'vemey ya`mel mine-ṣṣâliḥâti vehüve mü\'minün felâ yeḫâfü żulmev velâ haḍmâ.'),
(2461, 20, 113, 'vekeẕâlike enzelnâhü ḳur\'ânen `arabiyyev veṣarrafnâ fîhi mine-lve`îdi le`allehüm yetteḳûne ev yuḥdiŝü lehüm ẕikrâ.'),
(2462, 20, 114, 'fete`âle-llâhü-lmelikü-lḥaḳḳ. velâ ta`cel bilḳur\'âni min ḳabli ey yuḳḍâ ileyke vaḥyüh. veḳur rabbi zidnî `ilmâ.'),
(2463, 20, 115, 'veleḳad `ahidnâ ilâ âdeme min ḳablü fenesiye velem necid lehû `azmâ.'),
(2464, 20, 116, 'veiẕ ḳulnâ lilmelâiketi-scüdû liâdeme fesecedû illâ iblîs. ebâ.'),
(2465, 20, 117, 'feḳulnâ yâ âdemü inne hâẕâ `adüvvül leke velizevcike felâ yuḫricennekümâ mine-lcenneti feteşḳâ.'),
(2466, 20, 118, 'inne leke ellâ tecû`a fîhâ velâ ta`râ.'),
(2467, 20, 119, 'veenneke lâ tażmeü fîhâ velâ taḍḥâ.'),
(2468, 20, 120, 'fevesvese ileyhi-şşeyṭânü ḳâle yâ âdemü hel edüllüke `alâ şecerati-lḫuldi vemülkil lâ yeblâ.'),
(2469, 20, 121, 'feekelâ minhâ febedet lehümâ sev\'âtühümâ veṭafiḳâ yaḫṣifâni `aleyhimâ miv veraḳi-lcenneh. ve`aṣâ âdemü rabbehû fegavâ.'),
(2470, 20, 122, 'ŝümme-ctebâhü rabbühû fetâbe `aleyhi vehedâ.'),
(2471, 20, 123, 'ḳâle-hbiṭâ minhâ cemî`am ba`ḍuküm liba`ḍin `adüvv. feimmâ ye\'tiyenneküm minnî hüden femeni-ttebe`a hüdâye felâ yeḍillü velâ yeşḳâ.'),
(2472, 20, 124, 'vemen a`raḍa `an ẕikrî feinne lehû me`îşeten ḍankev venaḥşüruhû yevme-lḳiyâmeti a`mâ.'),
(2473, 20, 125, 'ḳâle rabbi lime ḥaşertenî a`mâ veḳad küntü beṣîrâ.'),
(2474, 20, 126, 'ḳâle keẕâlike etetke âyâtünâ fenesîtehâ. vekeẕâlike-lyevme tünsâ.'),
(2475, 20, 127, 'vekeẕâlike neczî men esrafe velem yü\'mim biâyâti rabbih. vele`aẕâbü-l\'âḫirati eşeddü veebḳâ.'),
(2476, 20, 128, 'efelem yehdi lehüm kem ehleknâ ḳablehüm mine-lḳurûni yemşûne fî mesâkinihim. inne fî ẕâlike leâyâtil liüli-nnühâ.'),
(2477, 20, 129, 'velevlâ kelimetün sebeḳat mir rabbike lekâne lizâmev veecelüm müsemmâ.'),
(2478, 20, 130, 'faṣbir `alâ mâ yeḳûlûne vesebbiḥ biḥamdi rabbike ḳable ṭulû`i-şşemsi veḳable gurûbihâ. vemin ânâi-lleyli fesebbiḥ veaṭrâfe-nnehâri le`alleke terḍâ.'),
(2479, 20, 131, 'velâ temüddenne `ayneyke ilâ mâ metta`nâ bihî ezvâcem minhüm zehrate-lḥayâti-ddünyâ lineftinehüm fîh. verizḳu rabbike ḫayruv veebḳâ.'),
(2480, 20, 132, 've\'mür ehleke biṣṣalâti vaṣṭabir `aleyhâ. lâ nes\'elüke rizḳâ. naḥnü nerzüḳuk. vel`âḳibetü littaḳvâ.'),
(2481, 20, 133, 'veḳâlû levlâ ye\'tînâ biâyetim mir rabbih. evelem te\'tihim beyyinetü mâ fi-ṣṣuḥufi-l\'ûlâ.'),
(2482, 20, 134, 'velev ennâ ehleknâhüm bi`aẕâbim min ḳablihî leḳâlû rabbenâ levlâ erselte ileynâ rasûlen fenettebi`a âyâtike min ḳabli en neẕille venaḫzâ.'),
(2483, 20, 135, 'ḳul küllüm müterabbiṣun feterabbeṣû. feseta`lemûne men aṣḥâbu-ṣṣirâṭi-sseviyyi vemeni-htedâ.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'iḳterabe linnâsi ḥisâbühüm vehüm fî gafletim mü`riḍûn.'),
(2485, 21, 2, 'mâ ye\'tîhim min ẕikrim mir rabbihim muḥdeŝin ille-steme`ûhü vehüm yel`abûn.'),
(2486, 21, 3, 'lâhiyeten ḳulûbühüm. veeserrü-nnecvâ. elleẕîne żalemû. hel hâẕâ illâ beşer miŝlüküm. efete\'tûne-ssiḥra veentüm tübṣirûn.'),
(2487, 21, 4, 'ḳâle rabbî ya`lemü-lḳavle fi-ssemâi vel\'arḍ. vehüve-ssemî`u-l`alîm.'),
(2488, 21, 5, 'bel ḳâlû aḍgâŝü aḥlâmim beli-fterâhü bel hüve şâ`ir. felye\'tinâ biâyetin kemâ ürsile-l\'evvelûn.'),
(2489, 21, 6, 'mâ âmenet ḳablehüm min ḳaryetin ehleknâhâ. efehüm yü\'minûn.'),
(2490, 21, 7, 'vemâ erselnâ ḳableke illâ ricâlen nûḥî ileyhim fes\'elû ehle-ẕẕikri in küntüm lâ ta`lemûn.'),
(2491, 21, 8, 'vemâ ce`alnâhüm cesedel lâ ye\'külûne-ṭṭa`âme vemâ kânû ḫâlidîn.'),
(2492, 21, 9, 'ŝümme ṣadaḳnâhümü-lva`de feenceynâhüm vemen neşâü veehlekne-lmüsrifîn.'),
(2493, 21, 10, 'leḳad enzelnâ ileyküm kitâben fîhi ẕikruküm. efelâ ta`ḳilûn.'),
(2494, 21, 11, 'vekem ḳaṣamnâ min ḳaryetin kânet żâlimetev veenşe\'nâ ba`dehâ ḳavmen âḫarîn.'),
(2495, 21, 12, 'felemmâ eḥassû be\'senâ iẕâ hüm minhâ yerküḍûn.'),
(2496, 21, 13, 'lâ terküḍû verci`û ilâ mâ ütriftüm fîhi vemesâkiniküm le`alleküm tüs\'elûn.'),
(2497, 21, 14, 'ḳâlû yâ veylenâ innâ künnâ żâlimîn.'),
(2498, 21, 15, 'femâ zâlet tilke da`vâhüm ḥattâ ce`alnâhüm ḥaṣîden ḫâmidîn.'),
(2499, 21, 16, 'vemâ ḫalaḳne-ssemâe vel\'arḍa vemâ beynehümâ lâ`ibîn.'),
(2500, 21, 17, 'lev eradnâ en netteḫiẕe lehvel letteḫaẕnâhü mil ledünnâ. in künnâ fâ`ilîn.'),
(2501, 21, 18, 'bel naḳẕifü bilḥaḳḳi `ale-lbâṭili feyedmeguhû feiẕâ hüve zâhiḳ. velekümü-lveylü mimmâ teṣifûn.'),
(2502, 21, 19, 'velehû men fi-ssemâvâti vel\'arḍ. vemen `indehû lâ yestekbirûne `an `ibâdetihî velâ yestaḥsirûn.'),
(2503, 21, 20, 'yüsebbiḥûne-lleyle vennehâra lâ yeftürûn.'),
(2504, 21, 21, 'emi-tteḫaẕû âlihetem mine-l\'arḍi hüm yünşirûn.'),
(2505, 21, 22, 'lev kâne fîhimâ âlihetün ille-llâhü lefesedetâ. fesübḥâne-llâhi rabbi-l`arşi `ammâ yeṣifûn.'),
(2506, 21, 23, 'lâ yüs\'elü `ammâ yef`alü vehüm yüs\'elûn.'),
(2507, 21, 24, 'emi-tteḫaẕû min dûnihî âliheh. ḳul hâtû bürhâneküm. hâẕâ ẕikru mem me`iye veẕikru men ḳablî. bel ekŝeruhüm lâ ya`lemûne-lḥaḳḳa fehüm mü`riḍûn.'),
(2508, 21, 25, 'vemâ erselnâ min ḳablike mir rasûlin illâ nûḥî ileyhi ennehû lâ ilâhe illâ ene fa`büdûn.'),
(2509, 21, 26, 'veḳâlü-tteḫaẕe-rraḥmânü veleden sübḥâneh. bel `ibâdüm mükramûn.'),
(2510, 21, 27, 'lâ yesbiḳûnehû bilḳavli vehüm biemrihî ya`melûn.'),
(2511, 21, 28, 'ya`lemü mâ beyne eydîhim vemâ ḫalfehüm velâ yeşfe`ûne illâ limeni-rteḍâ vehüm min ḫaşyetihî müşfiḳûn.'),
(2512, 21, 29, 'vemey yeḳul minhüm innî ilâhüm min dûnihî feẕâlike neczîhi cehennem. keẕâlike neczi-żżâlimîn.'),
(2513, 21, 30, 'evelem yera-lleẕîne keferû enne-ssemâvâti vel\'arḍa kânetâ ratḳan fefetaḳnâhümâ. vece`alnâ mine-lmâi külle şey\'in ḥayy. efelâ yü\'minûn.'),
(2514, 21, 31, 'vece`alnâ fi-l\'arḍi ravâsiye en temîde bihim vece`alnâ fîhâ ficâcen sübülel le`allehüm yehtedûn.'),
(2515, 21, 32, 'vece`alne-ssemâe saḳfem maḥfûżâ. vehüm `an âyâtihâ mü`riḍûn.'),
(2516, 21, 33, 'vehüve-lleẕî ḫaleḳa-lleyle vennehâra veşşemse velḳamer. küllün fî felekiy yesbeḥûn.'),
(2517, 21, 34, 'vemâ ce`alnâ libeşerim min ḳablike-lḫuld. efeim mitte fehümü-lḫâlidûn.'),
(2518, 21, 35, 'küllü nefsin ẕâiḳatü-lmevt. veneblûküm bişşerri velḫayri fitneh. veileynâ türce`ûn.'),
(2519, 21, 36, 'veiẕâ raâke-lleẕîne keferû iy yetteḫiẕûneke illâ hüzüvâ. ehâẕe-lleẕî yeẕküru âliheteküm. vehüm biẕikri-rraḥmâni hüm kâfirûn.'),
(2520, 21, 37, 'ḫuliḳa-l\'insânü min `acel. seürîküm âyâtî felâ testa`cilûn.'),
(2521, 21, 38, 'veyeḳûlûne metâ hâẕe-lva`dü in küntüm ṣâdiḳîn.'),
(2522, 21, 39, 'lev ya`lemü-lleẕîne keferû ḥîne lâ yeküffûne `av vucûhihimü-nnâra velâ `an żuhûrihim velâ hüm yünṣarûn.'),
(2523, 21, 40, 'bel te\'tîhim bagteten fetebhetühüm felâ yesteṭî`ûne raddehâ velâ hüm yünżarûn.'),
(2524, 21, 41, 'veleḳadi-stühzie birusülim min ḳablike feḥâḳa billeẕîne seḫirû minhüm mâ kânû bihî yestehziûn.'),
(2525, 21, 42, 'ḳul mey yekleüküm billeyli vennehâri mine-rraḥmân. bel hüm `an ẕikri rabbihim mü`riḍûn.'),
(2526, 21, 43, 'em lehüm âlihetün temne`uhüm min dûninâ. lâ yesteṭî`ûne naṣra enfüsihim velâ hüm minnâ yuṣḥabûn.'),
(2527, 21, 44, 'bel metta`nâ hâülâi veâbâehüm ḥattâ ṭâle `aleyhimü-l`umür. efelâ yeravne ennâ ne\'ti-l\'arḍa nenḳuṣuhâ min aṭrâfihâ. efehümü-lgâlibûn.'),
(2528, 21, 45, 'ḳul innemâ ünẕiruküm bilvaḥy. velâ yesme`u-ṣṣummü-ddü`âe iẕâ mâ yünẕerûn.'),
(2529, 21, 46, 'veleim messethüm nefḥatüm min `aẕâbi rabbike leyeḳûlünne yâ veylenâ innâ künnâ żâlimîn.'),
(2530, 21, 47, 'veneḍa`u-lmevâzîne-lḳiṣṭa liyevmi-lḳiyâmeti felâ tużlemü nefsün şey\'â. vein kâne miŝḳâle ḥabbetim min ḫardelin eteynâ bihâ. vekefâ binâ ḥâsibîn.'),
(2531, 21, 48, 'veleḳad âteynâ mûsâ vehârûne-lfürḳâne veḍiyâev veẕikral lilmütteḳîn.'),
(2532, 21, 49, 'elleẕîne yaḫşevne rabbehüm bilgaybi vehüm mine-ssâ`ati müşfiḳûn.'),
(2533, 21, 50, 'vehâẕâ ẕikrum mübârakün enzelnâh. efeentüm lehû münkirûn.'),
(2534, 21, 51, 'veleḳad âteynâ ibrâhîme ruşdehû min ḳablü vekünnâ bihî `âlimîn.'),
(2535, 21, 52, 'iẕ ḳâle liebîhi veḳavmihî mâ hâẕihi-ttemâŝîlü-lletî entüm lehâ `âkifûn.'),
(2536, 21, 53, 'ḳâlû vecednâ âbâenâ lehâ `âbidîn.'),
(2537, 21, 54, 'ḳâle leḳad küntüm entüm veâbâüküm fî ḍalâlim mübîn.'),
(2538, 21, 55, 'ḳâlû eci\'tenâ bilḥaḳḳi em ente mine-llâ`ibîn.'),
(2539, 21, 56, 'ḳâle ber rabbüküm rabbü-ssemâvâti vel\'arḍi-lleẕî feṭarahünn. veenâ `alâ ẕâliküm mine-şşâhidîn.'),
(2540, 21, 57, 'vetellâhi leekîdenne aṣnâmeküm ba`de en tüvellû müdbirîn.'),
(2541, 21, 58, 'fece`alehüm cüŝeŝen illâ kebîral lehüm le`allehüm ileyhi yerci`ûn.'),
(2542, 21, 59, 'ḳâlû men fe`ale hâẕâ biâlihetinâ innehû lemine-żżâlimîn.'),
(2543, 21, 60, 'ḳâlû semi`nâ fetey yeẕküruhüm yüḳâlü lehû ibrâhîm.'),
(2544, 21, 61, 'ḳâlû fe\'tû bihî `alâ a`yüni-nnâsi le`allehüm yeşhedûn.'),
(2545, 21, 62, 'ḳâlû eente fe`alte hâẕâ biâlihetinâ yâ ibrâhîm.'),
(2546, 21, 63, 'ḳâle bel fe`aleh. kebîruhüm hâẕâ fes\'elûhüm in kânû yenṭiḳûn.'),
(2547, 21, 64, 'ferace`û ilâ enfüsihim feḳâlû inneküm entümu-żżâlimûn.'),
(2548, 21, 65, 'ŝümme nükisû `alâ ruûsihim. leḳad `alimte mâ hâülâi yenṭiḳûn.'),
(2549, 21, 66, 'ḳâle efeta`büdûne min dûni-llâhi mâ lâ yenfe`uküm şey\'ev velâ yeḍurruküm.'),
(2550, 21, 67, 'üffil leküm velimâ ta`büdûne min dûni-llâh. efelâ ta`ḳilûn.'),
(2551, 21, 68, 'ḳâlû ḥarriḳûhü venṣurû âliheteküm in küntüm fâ`ilîn.'),
(2552, 21, 69, 'ḳulnâ yâ nâru kûnî berdev veselâmen `alâ ibrâhîm.'),
(2553, 21, 70, 'veerâdû bihî keyden fece`alnâhümü-l\'aḫserîn.'),
(2554, 21, 71, 'venecceynâhü velûṭan ile-l\'arḍi-lletî bâraknâ fîhâ lil`âlemîn.'),
(2555, 21, 72, 'vevehebnâ lehû isḥâḳ. veya`ḳûbe nâfileten. veküllen ce`alnâ ṣâliḥîn.'),
(2556, 21, 73, 'vece`alnâhüm eimmetey yehdûne biemrinâ veevḥaynâ ileyhim fi`le-lḫayrâti veiḳâme-ṣṣalâti veîtâe-zzekâh. vekânû lenâ `âbidîn.'),
(2557, 21, 74, 'velûṭan âteynâhü ḥukmev ve`ilmev venecceynâhü mine-lḳaryeti-lletî kânet ta`melü-lḫabâiŝ. innehüm kânû ḳavme sev\'in fâsiḳîn.'),
(2558, 21, 75, 'veedḫalnâhü fî raḥmetinâ. innehû mine-ṣṣâliḥîn.'),
(2559, 21, 76, 'venûḥan iẕ nâdâ min ḳablü festecebnâ lehû fenecceynâhü veehlehû mine-lkerbi-l`ażîm.'),
(2560, 21, 77, 'veneṣarnâhü mine-lḳavmi-lleẕîne keẕẕebû biâyâtinâ. innehüm kânû ḳavme sev\'in feagraḳnâhüm ecme`în.'),
(2561, 21, 78, 'vedâvûde vesüleymâne iẕ yaḥkümâni fi-lḥarŝi iẕ nefeşet fîhi ganemü-lḳavm. vekünnâ liḥukmihim şâhidîn.'),
(2562, 21, 79, 'fefehhemnâhâ süleymân. veküllen âteynâ ḥukmev ve`ilmâ. veseḫḫarnâ me`a dâvûde-lcibâle yüsebbiḥne veṭṭayr. vekünnâ fâ`ilîn.'),
(2563, 21, 80, 've`allemnâhü ṣan`ate lebûsil leküm lituḥṣineküm mim be\'siküm. fehel entüm şâkirûn.'),
(2564, 21, 81, 'velisüleymâne-rrîḥa `âṣifeten tecrî biemrih ile-l\'arḍi-lletî bâraknâ fîhâ. vekünnâ bikülli şey\'in `âlimîn.'),
(2565, 21, 82, 'vemine-şşeyâṭîni mey yegûṣûne lehû veya`melûne `amelen dûne ẕâlik. vekünnâ lehüm ḥâfiżîn.'),
(2566, 21, 83, 'veeyyûbe iẕ nâdâ rabbehû ennî messeniye-ḍḍurru veente erḥamü-rrâḥimîn.'),
(2567, 21, 84, 'festecebnâ lehû fekeşefnâ mâ bihî min ḍurriv veâteynâhü ehlehû vemiŝlehüm me`ahüm raḥmetem min `indinâ veẕikrâ lil`âbidîn.'),
(2568, 21, 85, 'veismâ`île veidrîse veẕe-lkifl. küllüm mine-ṣṣâbirîn.'),
(2569, 21, 86, 'veedḫalnâhüm fî raḥmetinâ. innehüm mine-ṣṣâliḥîn.'),
(2570, 21, 87, 'veẕe-nnûni iẕ ẕehebe mügâḍiben feżanne el len naḳdira `aleyhi fenâdâ fi-żżulümâti el lâ ilâhe illâ ente sübḥânek. innî küntü mine-żżâlimîn.'),
(2571, 21, 88, 'festecebnâ lehû venecceynâhü mine-lgamm. vekeẕâlike nünci-lmü\'minîn.'),
(2572, 21, 89, 'vezekeriyyâ iẕ nâdâ rabbehû rabbi lâ teẕernî ferdev veente ḫayru-lvâriŝîn.'),
(2573, 21, 90, 'festecebnâ leh. vevehebnâ lehû yaḥyâ veaṣlaḥnâ lehû zevceh. innehüm kânû yüsâri`ûne fi-lḫayrâti veyed`ûnenâ ragabev verahebâ. vekânû lenâ ḫâşi`în.'),
(2574, 21, 91, 'velletî aḥṣanet fercehâ fenefaḫnâ fîhâ mir rûḥinâ vece`alnâhâ vebnehâ âyetel lil`âlemîn.'),
(2575, 21, 92, 'inne hâẕihî ümmetüküm ümmetev vâḥideh. veenâ rabbüküm fa`büdûn.'),
(2576, 21, 93, 'veteḳaṭṭa`û emrahüm beynehüm. küllün ileynâ râci`ûn.'),
(2577, 21, 94, 'femey ya`mel mine-ṣṣâliḥâti vehüve mü\'minün felâ küfrâne lisa`yih. veinnâ lehû kâtibûn.'),
(2578, 21, 95, 'veḥarâmün `alâ ḳaryetin ehleknâhâ ennehüm lâ yerci`ûn.'),
(2579, 21, 96, 'ḥattâ iẕâ fütiḥat ye\'cûcü veme\'cûcü vehüm min külli ḥadebiy yensilûn.'),
(2580, 21, 97, 'vaḳterabe-lva`dü-lḥaḳḳu feiẕâ hiye şâḫiṣatün ebṣâru-lleẕîne keferû. yâ veylenâ ḳad künnâ fî gafletim min hâẕâ bel künnâ żâlimîn.'),
(2581, 21, 98, 'inneküm vemâ ta`büdûne min dûni-llâhi ḥaṣabü cehennem. entüm lehâ vâridûn.'),
(2582, 21, 99, 'lev kâne hâülâi âlihetem mâ veradûhâ. veküllün fîhâ ḫâlidûn.'),
(2583, 21, 100, 'lehüm fîhâ zefîruv vehüm fîhâ lâ yesme`ûn.'),
(2584, 21, 101, 'inne-lleẕîne sebeḳat lehüm minne-lḥusnâ ülâike `anhâ müb`adûn.'),
(2585, 21, 102, 'lâ yesme`ûne ḥasîsehâ. vehüm fî me-ştehet enfüsühüm ḫâlidûn.'),
(2586, 21, 103, 'lâ yaḥzünühümü-lfeza`u-l\'ekberu veteteleḳḳâhümü-lmelâikeh. hâẕâ yevmükümü-lleẕî küntüm tû`adûn.'),
(2587, 21, 104, 'yevme naṭvi-ssemâe keṭayyi-ssicilli lilkütüb. kemâ bede\'nâ evvele ḫalḳin nü`îdüh. va`den `aleynâ. innâ künnâ fâ`ilîn.'),
(2588, 21, 105, 'veleḳad ketebnâ fi-zzebûri mim ba`di-ẕẕikri enne-l\'arḍa yeriŝühâ `ibâdiye-ṣṣâliḥûn.'),
(2589, 21, 106, 'inne fî hâẕâ lebelâgal liḳavmin `âbidîn.'),
(2590, 21, 107, 'vemâ erselnâke illâ raḥmetel lil`âlemîn.'),
(2591, 21, 108, 'ḳul innemâ yûḥâ ileyye ennemâ ilâhüküm ilâhüv vâḥid. fehel entüm müslimûn.'),
(2592, 21, 109, 'fein tevellev feḳul ehentüküm `alâ sevâ\'. vein edrî eḳarîbün em be`îdüm mâ tû`adûn.'),
(2593, 21, 110, 'innehû ya`lemü-lcehra mine-lḳavli veya`lemü mâ tektümûn.'),
(2594, 21, 111, 'vein edrî le`allehû fitnetül leküm vemetâ`un ilâ ḥîn.'),
(2595, 21, 112, 'ḳâle rabbi-ḥküm bilḥaḳḳ. verabbüne-rraḥmânü-lmüste`ânü `alâ mâ teṣifûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'yâ eyyühe-nnâsü-tteḳû rabbeküm. inne zelzelete-ssâ`ati şey\'ün `ażîm.'),
(2597, 22, 2, 'yevme teravnehâ teẕhelü küllü mürḍi`atin `ammâ erḍa`at vetede`u küllü ẕâti ḥamlin ḥamlehâ vetera-nnâse sükârâ vemâ hüm bisükârâ velâkinne `aẕâbe-llâhi şedîd.'),
(2598, 22, 3, 'vemine-nnâsi mey yücâdilü fi-llâhi bigayri `ilmiv veyettebi`u külle şeyṭânim merîd.'),
(2599, 22, 4, 'kütibe `aleyhi ennehû men tevellâhü feennehû yüḍillühû veyehdîhi ilâ `aẕâbi-sse`îr.'),
(2600, 22, 5, 'yâ eyyühe-nnâsü in küntüm fî raybim mine-lba`ŝi feinnâ ḫalaḳnâküm min türâbin ŝümme min nuṭfetin ŝümme min `aleḳatin ŝümme mim muḍgatim müḫalleḳativ vegayri müḫalleḳatil linübeyyine leküm. venüḳirru fi-l\'erḥâmi mâ neşâü ilâ ecelim müsemmen ŝümme nuḫricüküm ṭiflen ŝümme liteblügû eşüddeküm. veminküm mey yüteveffâ veminküm mey yüraddü ilâ erẕeli-l`umüri likeylâ ya`leme mim ba`di `ilmin şey\'â. vetera-l\'arḍa hâmideten feiẕâ enzelnâ `aleyhe-lmâe-htezzet verabet veembetet min külli zevcim behîc.'),
(2601, 22, 6, 'ẕâlike bienne-llâhe hüve-lḥaḳḳu veennehû yuḥyi-lmevtâ veennehû `alâ külli şey\'in ḳadîr.'),
(2602, 22, 7, 'veenne-ssâ`ate âtiyetül lâ raybe fîhâ veenne-llâhe yeb`aŝü men fi-lḳubûr.'),
(2603, 22, 8, 'vemine-nnâsi mey yücâdilü fi-llâhi bigayri `ilmiv velâ hüdev velâ kitâbim münîr.'),
(2604, 22, 9, 'ŝâniye `iṭfihî liyüḍille `an sebîli-llâh. lehû fi-ddünyâ ḫizyüv venüẕîḳuhû yevme-lḳiyâmeti `aẕâbe-lḥarîḳ.'),
(2605, 22, 10, 'ẕâlike bimâ ḳaddemet yedâke veenne-llâhe leyse biżallâmil lil`abîd.'),
(2606, 22, 11, 'vemine-nnâsi mey ya`büdü-llâhe `alâ ḥarf. fein eṣâbehû ḫayrun-ṭmeenne bih. vein eṣâbethü fitnetün-nḳalebe `alâ vechih. ḫasira-ddünyâ vel\'âḫirah. ẕâlike hüve-lḫusrânü-lmübîn.'),
(2607, 22, 12, 'yed`û min dûni-llâhi mâ lâ yeḍurruhû vemâ lâ yenfe`uh. ẕâlike hüve-ḍḍalâlü-lbe`îd.'),
(2608, 22, 13, 'yed`û lemen ḍarruhû aḳrabü min nef`ih. lebi\'se-lmevlâ velebi\'se-l`aşîr.'),
(2609, 22, 14, 'inne-llâhe yüdḫilü-lleẕîne âmenû ve`amilu-ṣṣâliḥâti cennâtin tecrî min taḥtihe-l\'enhâr. inne-llâhe yef`alü mâ yürîd.'),
(2610, 22, 15, 'men kâne yeżunnü el ley yenṣurahü-llâhü fi-ddünyâ vel\'âḫirati felyemdüd bisebebin ile-ssemâi ŝümme liyaḳṭa` felyenżur hel yüẕhibenne keydühû mâ yegîż.'),
(2611, 22, 16, 'vekeẕâlike enzelnâhü âyâtim beyyinâtiv veenne-llâhe yehdî mey yürîd.'),
(2612, 22, 17, 'inne-lleẕîne âmenû velleẕîne hâdû veṣṣâbi\'îne venneṣârâ velmecûse velleẕîne eşrakû. inne-llâhe yefṣilü beynehüm yevme-lḳiyâmeh. inne-llâhe `alâ külli şey\'in şehîd.'),
(2613, 22, 18, 'elem tera enne-llâhe yescüdü lehû men fi-ssemâvâti vemen fi-l\'arḍi veşşemsü velḳameru vennücûmü velcibâlü veşşeceru veddevâbbü vekeŝîrum mine-nnâs. vekeŝîrun ḥaḳḳa `aleyhi-l`aẕâb. vemey yühini-llâhü femâ lehû mim mükrim. inne-llâhe yef`alü mâ yeşâ\'.'),
(2614, 22, 19, 'hâẕâni ḫaṣmâni-ḫteṣamû fî rabbihim. felleẕîne keferû ḳuṭṭi`at lehüm ŝiyâbüm min nârin. yüṣabbü min fevḳi ruûsihimü-lḥamîm.'),
(2615, 22, 20, 'yuṣheru bihî mâ fî büṭûnihim velcülûd.'),
(2616, 22, 21, 'velehüm meḳâmi`u min ḥadîd.'),
(2617, 22, 22, 'küllemâ erâdû ey yaḫrucû minhâ min gammin ü`îdû fîhâ veẕûḳû `aẕâbe-lḥarîḳ.'),
(2618, 22, 23, 'inne-llâhe yüdḫilü-lleẕîne âmenû ve`amilu-ṣṣâliḥâti cennâtin tecrî min taḥtihe-l\'enhâru yüḥallevne fîhâ min esâvira min ẕehebiv velü\'lüâ. velibâsühüm fîhâ ḥarîr.'),
(2619, 22, 24, 'vehüdû ile-ṭṭayyibi mine-lḳavl. vehüdû ilâ ṣirâṭi-lḥamîd.'),
(2620, 22, 25, 'inne-lleẕîne keferû veyeṣuddûne `an sebîli-llâhi velmescidi-lḥarâmi-lleẕî ce`alnâhü linnâsi sevâeni-l`âkifü fîhi velbâd. vemey yürid fîhi biilḥâdim biżulmin nüẕiḳhü min `aẕâbin elîm.'),
(2621, 22, 26, 'veiẕ bevve\'nâ liibrâhîme mekâne-lbeyti el lâ tüşrik bî şey\'ev veṭahhir beytiye liṭṭâifîne velḳâimîne verrukke`i-ssücûd.'),
(2622, 22, 27, 'veeẕẕin fi-nnâsi bilḥacci ye\'tûke ricâlev ve`alâ külli ḍâmiriy ye\'tîne min külli feccin `amîḳ.'),
(2623, 22, 28, 'liyeşhedû menâfi`a lehüm veyeẕkürü-sme-llâhi fî eyyâmim ma`lûmâtin `alâ mâ razeḳahüm mim behîmeti-l\'en`âm. fekülû minhâ veaṭ`imü-lbâise-lfeḳîr.'),
(2624, 22, 29, 'ŝümme liyaḳḍû tefeŝehüm velyûfû nüẕûrahüm velyeṭṭavvefû bilbeyti-l`atîḳ.'),
(2625, 22, 30, 'ẕâlik. vemey yü`ażżim ḥurumâti-llâhi fehüve ḫayrul lehû `inde rabbih. veüḥillet lekümü-l\'en`âmü illâ mâ yütlâ `aleyküm fectenibü-rricse mine-l\'evŝâni vectenibû ḳavle-zzûr.'),
(2626, 22, 31, 'ḥunefâe lillâhi gayra müşrikîne bih. vemey yüşrik billâhi fekeennemâ ḫarra mine-ssemâi fetaḫṭafühu-ṭṭayru ev tehvî bihi-rrîḥu fî mekânin seḥîḳ.'),
(2627, 22, 32, 'ẕâlik. vemey yü`ażżim şe`âira-llâhi feinnehâ min taḳve-lḳulûb.'),
(2628, 22, 33, 'leküm fîhâ menâfi`u ilâ ecelim müsemmen ŝümme meḥillühâ ile-lbeyti-l`atîḳ.'),
(2629, 22, 34, 'velikülli ümmetin ce`alnâ mensekel liyeẕkürü-sme-llâhi `alâ mâ razeḳahüm mim behîmeti-l\'en`âm. feilâhüküm ilâhüv vâḥidün felehû eslimû. vebeşşiri-lmuḫbitîn.'),
(2630, 22, 35, 'elleẕîne iẕâ ẕükira-llâhü vecilet ḳulûbühüm veṣṣâbirîne `alâ mâ eṣâbehüm velmüḳîmi-ṣṣalâti vemimmâ razaḳnâhüm yünfiḳûn.'),
(2631, 22, 36, 'velbüdne ce`alnâhâ leküm min şe`âiri-llâhi leküm fîhâ ḫayr. feẕkürü-sme-llâhi `aleyhâ ṣavâff. feiẕâ vecebet cünûbühâ fekülû minhâ veaṭ`imü-lḳâni`a velmü`terr. keẕâlike seḫḫarnâhâ leküm le`alleküm teşkürûn.'),
(2632, 22, 37, 'ley yenâle-llâhe lüḥûmühâ velâ dimâühâ velâkiy yenâlühü-ttaḳvâ minküm. keẕâlike seḫḫarahâ leküm litükebbirü-llâhe `alâ mâ hedâküm. vebeşşiri-lmuḥsinîn.'),
(2633, 22, 38, 'inne-llâhe yüdâfi`u `ani-lleẕîne âmenû. inne-llâhe lâ yüḥibbü külle ḫavvânin kefûr.'),
(2634, 22, 39, 'üẕine lilleẕîne yüḳâtelûne biennehüm żulimû. veinne-llâhe `alâ naṣrihim leḳadîr.'),
(2635, 22, 40, 'elleẕîne uḫricû min diyârihim bigayri ḥaḳḳin illâ ey yeḳûlû rabbüne-llâh. velevlâ def`u-llâhi-nnâse ba`ḍahüm biba`ḍil lehüddimet ṣavâmi`u vebiye`uv veṣalevâtüv vemesâcidü yüẕkeru fîhe-smü-llâhi keŝîrâ. veleyenṣuranne-llâhü mey yenṣuruh. inne-llâhe leḳaviyyün `azîz.'),
(2636, 22, 41, 'elleẕîne im mekkennâhüm fi-l\'arḍi eḳâmu-ṣṣalâte veâtevu-zzekâte veemerû bilma`rûfi venehev `ani-lmünker. velillâhi `âḳibetü-l\'ümûr.'),
(2637, 22, 42, 'veiy yükeẕẕibûke feḳad keẕẕebet ḳablehüm ḳavmü nûḥiv ve`âdüv veŝemûd.'),
(2638, 22, 43, 'veḳavmü ibrâhîme veḳavmü lûṭ.'),
(2639, 22, 44, 'veaṣḥâbü medyen. veküẕẕibe mûsâ feemleytü lilkâfirîne ŝümme eḫaẕtühüm. fekeyfe kâne nekîr.'),
(2640, 22, 45, 'fekeeyyim min ḳaryetin ehleknâhâ vehiye żâlimetün fehiye ḫâviyetün `alâ `urûşihâ vebi\'rim mü`aṭṭaletiv veḳaṣrim meşîd.'),
(2641, 22, 46, 'efelem yesîrû fi-l\'arḍi fetekûne lehüm ḳulûbüy ya`ḳilûne bihâ ev âẕânüy yesme`ûne bihâ. feinnehâ lâ ta`me-l\'ebṣâru velâkin ta`me-lḳulûbü-lletî fi-ṣṣudûr.'),
(2642, 22, 47, 'veyesta`cilûneke bil`aẕâbi veley yuḫlife-llâhü va`deh. veinne yevmen `inde rabbike keelfi senetim mimmâ te`uddûn.'),
(2643, 22, 48, 'vekeeyyim min ḳaryetin emleytü lehâ vehiye żâlimetün ŝümme eḫaẕtühâ. veileyye-lmeṣîr.'),
(2644, 22, 49, 'ḳul yâ eyyühe-nnâsü innemâ ene leküm neẕîrum mübîn.'),
(2645, 22, 50, 'felleẕîne âmenû ve`amilu-ṣṣâliḥâti lehüm magfiratüv verizḳun kerîm.'),
(2646, 22, 51, 'velleẕîne se`av fî âyâtinâ mü`âcizîne ülâike aṣḥâbü-lceḥîm.'),
(2647, 22, 52, 'vemâ erselnâ min ḳablike mir rasûliv velâ nebiyyin illâ iẕâ temennâ elḳa-şşeyṭânü fî ümniyyetih. feyenseḫu-llâhü mâ yülḳi-şşeyṭânü ŝümme yuḥkimü-llâhü âyâtih. vellâhü `alîmün ḥakîm.'),
(2648, 22, 53, 'liyec`ale mâ yülḳi-şşeyṭânü fitnetel lilleẕîne fî ḳulûbihim meraḍuv velḳâsiyeti ḳulûbühüm. veinne-żżâlimîne lefî şiḳâḳim be`îd.'),
(2649, 22, 54, 'veliya`leme-lleẕîne ûtü-l`ilme ennehü-lḥaḳḳu mir rabbike feyü\'minû bihî fetuḫbiṭa lehû ḳulûbühüm. veinne-llâhe lehâdi-lleẕîne âmenû ilâ ṣirâṭim müsteḳîm.'),
(2650, 22, 55, 'velâ yezâlü-lleẕîne keferû fî miryetim minhü ḥattâ te\'tiyehümü-ssâ`atü bagteten ev ye\'tiyehüm `aẕâbü yevmin `aḳîm.'),
(2651, 22, 56, 'elmülkü yevmeiẕil lillâh. yaḥkümü beynehüm. felleẕîne âmenû ve`amilu-ṣṣâliḥâti fî cennâti-nne`îm.'),
(2652, 22, 57, 'velleẕîne keferû vekeẕẕebû biâyâtinâ feülâike lehüm `aẕâbüm mühîn.'),
(2653, 22, 58, 'velleẕîne hâcerû fî sebîli-llâhi ŝümme ḳutilû ev mâtû leyerzüḳannehümü-llâhü rizḳan ḥasenâ. veinne-llâhe lehüve ḫayru-rrâziḳîn.'),
(2654, 22, 59, 'leyüdḫilennehüm müdḫaley yerḍavneh. veinne-llâhe le`alîmün ḥalîm.'),
(2655, 22, 60, 'ẕâlik. vemen `âḳabe bimiŝli mâ `ûḳibe bihî ŝümme bügiye `aleyhi leyenṣurannehü-llâh. inne-llâhe le`afüvvun gafûr.'),
(2656, 22, 61, 'ẕâlike bienne-llâhe yûlicü-lleyle fi-nnehâri veyûlicü-nnehâra fi-lleyli veenne-llâhe semî`um beṣîr.'),
(2657, 22, 62, 'ẕâlike bienne-llâhe hüve-lḥaḳḳu veenne mâ yed`ûne min dûnihî hüve-lbâṭilü veenne-llâhe hüve-l`aliyyü-lkebîr.'),
(2658, 22, 63, 'elem tera enne-llâhe enzele mine-ssemâi mââ. fetuṣbiḥu-l\'arḍu muḫḍarrah. inne-llâhe leṭîfün ḫabîr.'),
(2659, 22, 64, 'lehû mâ fi-ssemâvâti vemâ fi-l\'arḍ. veinne-llâhe lehüve-lganiyyü-lḥamîd.'),
(2660, 22, 65, 'elem tera enne-llâhe seḫḫara leküm mâ fi-l\'arḍi velfülke tecrî fi-lbaḥri biemrih. veyümsikü-ssemâe en teḳa`a `ale-l\'arḍi illâ biiẕnih. inne-llâhe binnâsi leraûfür raḥîm.'),
(2661, 22, 66, 'vehüve-lleẕî aḥyâküm. ŝümme yümîtüküm ŝümme yuḥyîküm. inne-l\'insâne lekefûr.'),
(2662, 22, 67, 'likülli ümmetin ce`alnâ menseken hüm nâsikûhü felâ yünâzi`anneke fi-l\'emri ved`u ilâ rabbik. inneke le`alâ hüdem müsteḳîm.'),
(2663, 22, 68, 'vein câdelûke feḳuli-llâhü a`lemü bimâ ta`melûn.'),
(2664, 22, 69, 'allâhü yaḥkümü beyneküm yevme-lḳiyâmeti fîmâ küntüm fîhi taḫtelifûn.'),
(2665, 22, 70, 'elem ta`lem enne-llâhe ya`lemü mâ fi-ssemâi vel\'arḍ. inne ẕâlike fî kitâb. inne ẕâlike `ale-llâhi yesîr.'),
(2666, 22, 71, 'veya`büdûne min dûni-llâhi mâ lem yünezzil bihî sülṭânev vemâ leyse lehüm bihî `ilm. vemâ liżżâlimîne min neṣîr.'),
(2667, 22, 72, 'veiẕâ tütlâ `aleyhim âyâtünâ beyyinâtin ta`rifü fî vucûhi-lleẕîne keferü-lmünker. yekâdûne yesṭûne billeẕîne yetlûne `aleyhim âyâtinâ. ḳul efeünebbiüküm bişerrim min ẕâliküm. ennâr. ve`adehe-llâhü-lleẕîne keferû. vebi\'se-lmeṣîr.'),
(2668, 22, 73, 'yâ eyyühe-nnâsü ḍuribe meŝelün festemi`û leh. inne-lleẕîne ted`ûne min dûni-llâhi ley yaḫlüḳû ẕübâbev velevi-cteme`û leh. veiy yeslübhümü-ẕẕübâbü şey\'el lâ yestenḳiẕûhü minh. ḍa`ufe-ṭṭâlibü velmaṭlûb.'),
(2669, 22, 74, 'mâ ḳaderü-llâhe ḥaḳḳa ḳadrih. inne-llâhe leḳaviyyün `azîz.'),
(2670, 22, 75, 'allâhü yaṣṭafî mine-lmelâiketi rusülev vemine-nnâs. inne-llâhe semî`um beṣîr.'),
(2671, 22, 76, 'ya`lemü mâ beyne eydîhim vemâ ḫalfehüm. veile-llâhi türce`u-l\'ümûr.'),
(2672, 22, 77, 'yâ eyyühe-lleẕîne âmenü-rke`û vescüdû va`büdû rabbeküm vef`alü-lḫayra le`alleküm tüfliḥûn.'),
(2673, 22, 78, 'vecâhidû fi-llâhi ḥaḳḳa cihâdih. hüve-ctebâküm vemâ ce`ale `aleyküm fi-ddîni min ḥarac. millete ebîküm ibrâhîm. hüve semmâkümü-lmüslimîne min ḳablü vefî hâẕâ liyekûne-rrasûlü şehîden `aleyküm vetekûnû şühedâe `ale-nnâs. feeḳîmu-ṣṣalâte veâtü-zzekâte va`teṣimû billâh. hüve mevlâküm. feni`me-lmevlâ veni`me-nneṣîr.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'ḳad efleḥa-lmü\'minûn.'),
(2675, 23, 2, 'elleẕîne hüm fî ṣalâtihim ḫâşi`ûn.'),
(2676, 23, 3, 'velleẕîne hüm `ani-llagvi mü`riḍûn.'),
(2677, 23, 4, 'velleẕîne hüm lilzekâti fâ`ilûn.'),
(2678, 23, 5, 'velleẕîne hüm lifürûcihim ḥâfiżûn.'),
(2679, 23, 6, 'illâ `alâ ezvâcihim ev mâ meleket eymânühüm feinnehüm gayru melûmîn.'),
(2680, 23, 7, 'femeni-btegâ verâe ẕâlike feülâike hümü-l`âdûn.'),
(2681, 23, 8, 'velleẕîne hüm liemânâtihim ve`ahdihim râ`ûn.'),
(2682, 23, 9, 'velleẕîne hüm `alâ ṣalevâtihim yüḥâfiżûn.'),
(2683, 23, 10, 'ülâike hümü-lvâriŝûn.'),
(2684, 23, 11, 'elleẕîne yeriŝûne-lfirdevs. hüm fîhâ ḫâlidûn.'),
(2685, 23, 12, 'veleḳad ḫalaḳne-l\'insâne min sülâletim min ṭîn.'),
(2686, 23, 13, 'ŝümme ce`alnâhü nuṭfeten fî ḳarârim mekîn.'),
(2687, 23, 14, 'ŝümme ḫalaḳne-nnuṭfete `aleḳaten feḫalaḳne-l`aleḳate muḍgaten feḫalaḳne-lmuḍgate `iżâmen fekesevne-l`iżâme laḥmâ. ŝümme enşe\'nâhü ḫalḳan âḫar. fetebârake-llâhü aḥsenü-lḫâliḳîn.'),
(2688, 23, 15, 'ŝümme inneküm ba`de ẕâlike lemeyyitûn.'),
(2689, 23, 16, 'ŝümme inneküm yevme-lḳiyâmeti tüb`aŝûn.'),
(2690, 23, 17, 'veleḳad ḫalaḳnâ fevḳaküm seb`a ṭarâiḳ. vemâ künnâ `ani-lḫalḳi gâfilîn.'),
(2691, 23, 18, 'veenzelnâ mine-ssemâi mâem biḳaderin feeskennâhü fi-l\'arḍ. veinnâ `alâ ẕehâbim bihî leḳâdirûn.'),
(2692, 23, 19, 'feenşe\'nâ leküm bihî cennâtim min neḫîliv vea`nâb. leküm fîhâ fevâkihü keŝîratüv veminhâ te\'külûn.'),
(2693, 23, 20, 'veşeceraten taḫrucü min ṭûri seynâe tembütü biddühni veṣibgil lil\'âkilîn.'),
(2694, 23, 21, 'veinne leküm fi-l\'en`âmi le`ibrah. nüsḳîküm mimmâ fî büṭûnihâ veleküm fîhâ menâfi`u keŝîratüv veminhâ te\'külûn.'),
(2695, 23, 22, 've`aleyhâ ve`ale-lfülki tuḥmelûn.'),
(2696, 23, 23, 'veleḳad erselnâ nûḥan ilâ ḳavmihî feḳâle yâ ḳavmi-`büdü-llâhe mâ leküm min ilâhin gayruh. efelâ tetteḳûn.'),
(2697, 23, 24, 'feḳâle-lmeleü-lleẕîne keferû min ḳavmihî mâ hâẕâ illâ beşerum miŝlüküm yürîdü ey yetefeḍḍale `aleyküm. velev şâe-llâhü leenzele melâikeh. mâ semi`nâ bihâẕâ fî âbâine-l\'evvelîn.'),
(2698, 23, 25, 'in hüve illâ racülüm bihî cinnetün feterabbeṣû bihî ḥattâ ḥîn.'),
(2699, 23, 26, 'ḳâle rabbi-nṣurnî bimâ keẕẕebûn.'),
(2700, 23, 27, 'feevḥaynâ ileyhi eni-ṣne`i-lfülke bia`yüninâ vevaḥyinâ feiẕâ câe emrunâ vefâra-ttennûru feslük fîhâ min küllin zevceyni-ŝneyni veehleke illâ men sebeḳa `aleyhi-lḳavlü minhüm. velâ tüḫâṭibnî fi-lleẕîne żalemû. innehüm mugraḳûn.'),
(2701, 23, 28, 'feiẕe-steveyte ente vemem me`ake `ale-lfülki feḳuli-lḥamdü lillâhi-lleẕî neccânâ mine-lḳavmi-żżâlimîn.'),
(2702, 23, 29, 'veḳur rabbi enzilnî münzelem mübârakev veente ḫayru-lmünzilîn.'),
(2703, 23, 30, 'inne fî ẕâlike leâyâtiv vein künnâ lemübtelîn.'),
(2704, 23, 31, 'ŝümme enşe\'nâ mim ba`dihim ḳarnen âḫarîn.'),
(2705, 23, 32, 'feerselnâ fîhim rasûlem minhüm eni-`büdü-llâhe mâ leküm min ilâhin gayruh. efelâ tetteḳûn.'),
(2706, 23, 33, 'veḳâle-lmeleü min ḳavmihi-lleẕîne keferû vekeẕẕebû biliḳâi-l\'âḫirati veetrafnâhüm fi-lḥayâti-ddünyâ mâ hâẕâ illâ beşerum miŝlüküm ye\'külü mimmâ te\'külûne minhü veyeşrabü mimmâ teşrabûn.'),
(2707, 23, 34, 'velein eṭa`tüm beşeram miŝleküm inneküm iẕel leḫâsirûn.'),
(2708, 23, 35, 'eye`idüküm enneküm iẕâ mittüm veküntüm türâbev ve`iżâmen enneküm muḫracûn.'),
(2709, 23, 36, 'heyhâte heyhâte limâ tû`adûn.'),
(2710, 23, 37, 'in hiye illâ ḥayâtüne-ddünyâ nemûtü venaḥyâ vemâ naḥnü bimeb`ûŝîn.'),
(2711, 23, 38, 'in hüve illâ racülün-fterâ `ale-llâhi keẕibev vemâ naḥnü lehû bimü\'minîn.'),
(2712, 23, 39, 'ḳâle rabbi-nṣurnî bimâ keẕẕebûn.'),
(2713, 23, 40, 'ḳâle `ammâ ḳalîlil leyuṣbiḥunne nâdimîn.'),
(2714, 23, 41, 'feeḫaẕethümu-ṣṣayḥatü bilḥaḳḳi fece`alnâhüm guŝââ. febü`del lilḳavmi-żżâlimîn.'),
(2715, 23, 42, 'ŝümme enşe\'nâ mim ba`dihim ḳurûnen âḫarîn.'),
(2716, 23, 43, 'mâ tesbiḳu min ümmetin ecelehâ vemâ yeste\'ḫirûn.'),
(2717, 23, 44, 'ŝümme erselnâ rusülenâ tetrâ. küllemâ câe ümmeter rasûlühâ keẕẕebûhü feetba`nâ ba`ḍahüm ba`ḍav vece`alnâhüm eḥâdîŝ. febü`del liḳavmil lâ yü\'minûn.'),
(2718, 23, 45, 'ŝümme erselnâ mûsâ veeḫâhü hârûne biâyâtinâ vesülṭânim mübîn.'),
(2719, 23, 46, 'ilâ fir`avne vemeleihî festekberû vekânû ḳavmen `âlîn.'),
(2720, 23, 47, 'feḳâlû enü\'minü libeşerayni miŝlinâ veḳavmühümâ lenâ `âbidûn.'),
(2721, 23, 48, 'fekeẕẕebûhümâ fekânû mine-lmühlekîn.'),
(2722, 23, 49, 'veleḳad âteynâ mûse-lkitâbe le`allehüm yehtedûn.'),
(2723, 23, 50, 'vece`alne-bne meryeme veümmehû âyetev veâveynâhümâ ilâ rabvetin ẕâti ḳarâriv veme`în.'),
(2724, 23, 51, 'yâ eyyühe-rrusülü külû mine-ṭṭayyibâti va`melû ṣâliḥâ. innî bimâ ta`melûne `alîm.'),
(2725, 23, 52, 'veinne hâẕihî ümmetüküm ümmetev vâḥidetev veenâ rabbüküm fetteḳûn.'),
(2726, 23, 53, 'feteḳaṭṭa`û emrahüm beynehüm zübürâ. küllü ḥizbim bimâ ledeyhim feriḥûn.'),
(2727, 23, 54, 'feẕerhüm fî gamratihim ḥattâ ḥîn.'),
(2728, 23, 55, 'eyaḥsebûne ennemâ nümiddühüm bihî mim mâliv vebenîn.'),
(2729, 23, 56, 'nüsâri`u lehüm fi-lḫayrât. bel lâ yeş`urûn.'),
(2730, 23, 57, 'inne-lleẕîne hüm min ḫaşyeti rabbihim müşfiḳûn.'),
(2731, 23, 58, 'velleẕîne hüm biâyâti rabbihim yü\'minûn.'),
(2732, 23, 59, 'velleẕîne hüm birabbihim lâ yüşrikûn.'),
(2733, 23, 60, 'velleẕîne yü\'tûne mâ âtev veḳulûbühüm veciletün ennehüm ilâ rabbihim râci`ûn.'),
(2734, 23, 61, 'ülâike yüsâri`ûne fi-lḫayrâti vehüm lehâ sâbiḳûn.'),
(2735, 23, 62, 'velâ nükellifü nefsen illâ vus`ahâ veledeynâ kitâbüy yenṭiḳu bilḥaḳḳi vehüm lâ yużlemûn.'),
(2736, 23, 63, 'bel ḳulûbühüm fî gamratim min hâẕâ velehüm a`mâlüm min dûni ẕâlike hüm lehâ `âmilûn.'),
(2737, 23, 64, 'ḥattâ iẕâ eḫaẕnâ mütrafîhim bil`aẕâbi iẕâ hüm yec\'erûn.'),
(2738, 23, 65, 'lâ tec\'erü-lyevme inneküm minnâ lâ tünṣarûn.'),
(2739, 23, 66, 'ḳad kânet âyâtî tütlâ `aleyküm feküntüm `alâ a`ḳâbiküm tenkisûn.'),
(2740, 23, 67, 'müstekbirîne bih. sâmiran tehcürûn.'),
(2741, 23, 68, 'efelem yeddebberü-lḳavle em câehüm mâ lem ye\'ti âbâehümü-l\'evvelîn.'),
(2742, 23, 69, 'em lem ya`rifû rasûlehüm fehüm lehû münkirûn.'),
(2743, 23, 70, 'em yeḳûlûne bihî cinneh. bel câehüm bilḥaḳḳi veekŝeruhüm lilḥaḳḳi kârihûn.'),
(2744, 23, 71, 'velevi-ttebe`a-lḥaḳḳu ehvâehüm lefesedeti-ssemâvâtü vel\'arḍu vemen fîhinn. bel eteynâhüm biẕikrihim fehüm `an ẕikrihim mü`riḍûn.'),
(2745, 23, 72, 'em tes\'elühüm ḫarcen feḫarâcü rabbike ḫayr. vehüve ḫayru-rrâziḳîn.'),
(2746, 23, 73, 'veinneke leted`ûhüm ilâ ṣirâṭim müsteḳîm.'),
(2747, 23, 74, 'veinne-lleẕîne lâ yü\'minûne bil\'âḫirati `ani-ṣṣirâṭi lenâkibûn.'),
(2748, 23, 75, 'velev raḥimnâhüm vekeşefnâ mâ bihim min ḍurril leleccû fî ṭugyânihim ya`mehûn.'),
(2749, 23, 76, 'veleḳad eḫaẕnâhüm bil`aẕâbi feme-stekânû lirabbihim vemâ yeteḍarra`ûn.'),
(2750, 23, 77, 'ḥattâ iẕâ fetaḥnâ `aleyhim bâben ẕâ `aẕâbin şedîdin iẕâ hüm fîhi müblisûn.'),
(2751, 23, 78, 'vehüve-lleẕî enşee lekümü-ssem`a vel\'ebṣâra vel\'ef\'ideh. ḳalîlem mâ teşkürûn.'),
(2752, 23, 79, 'vehüve-lleẕî ẕera\'eküm fi-l\'arḍi veileyhi tuḥşerûn.'),
(2753, 23, 80, 'vehüve-lleẕî yuḥyî veyümîtü velehu-ḫtilâfü-lleyli vennehâr. efelâ ta`ḳilûn.'),
(2754, 23, 81, 'bel ḳâlû miŝle mâ ḳâle-l\'evvelûn.'),
(2755, 23, 82, 'ḳâlû eiẕâ mitnâ vekünnâ türâbev ve`iżâmen einnâ lemeb`ûŝûn.'),
(2756, 23, 83, 'leḳad vu`idnâ naḥnü veâbâünâ hâẕâ min ḳablü in hâẕâ illâ esâṭîru-l\'evvelîn.'),
(2757, 23, 84, 'ḳul limeni-l\'arḍu vemen fîhâ in küntüm ta`lemûn.'),
(2758, 23, 85, 'seyeḳûlûne lillâh. ḳul efelâ teẕekkerûn.'),
(2759, 23, 86, 'ḳul mer rabbü-ssemâvâti-sseb`i verabbü-l`arşi-l`ażîm.'),
(2760, 23, 87, 'seyeḳûlûne lillâh. ḳul efelâ tetteḳûn.'),
(2761, 23, 88, 'ḳul mem biyedihî melekûtü külli şey\'iv vehüve yücîru velâ yücâru `aleyhi in küntüm ta`lemûn.'),
(2762, 23, 89, 'seyeḳûlûne lillâh. ḳul feennâ tüsḥarûn.'),
(2763, 23, 90, 'bel eteynâhüm bilḥaḳḳi veinnehüm lekâẕibûn.'),
(2764, 23, 91, 'me-tteḫaẕe-llâhü miv velediv vemâ kâne me`ahû min ilâhin iẕel leẕehebe küllü ilâhim bimâ ḫaleḳa vele`alâ ba`ḍuhüm `alâ ba`ḍ. sübḥâne-llâhi `ammâ yeṣifûn.'),
(2765, 23, 92, '`âlimi-lgaybi veşşehâdeti fete`âlâ `ammâ yüşrikûn.'),
(2766, 23, 93, 'ḳur rabbi immâ türiyennî mâ yû`adûn.'),
(2767, 23, 94, 'rabbi felâ tec`alnî fi-lḳavmi-żżâlimîn.'),
(2768, 23, 95, 'veinnâ `alâ en nüriyeke mâ ne`idühüm leḳâdirûn.'),
(2769, 23, 96, 'idfa` billetî hiye aḥsenü-sseyyieh. naḥnü a`lemü bimâ yeṣifûn.'),
(2770, 23, 97, 'veḳur rabbi e`ûẕü bike min hemezâti-şşeyâṭîn.'),
(2771, 23, 98, 'vee`ûẕü bike rabbi ey yaḥḍurûn.'),
(2772, 23, 99, 'ḥattâ iẕâ câe eḥadehümü-lmevtü ḳâle rabbi-rci`ûn.'),
(2773, 23, 100, 'le`allî a`melü ṣâliḥan fîmâ teraktü kellâ. innehâ kelimetün hüve ḳâilühâ. vemiv verâihim berzeḫun ilâ yevmi yüb`aŝûn.'),
(2774, 23, 101, 'feiẕâ nüfiḫa fi-ṣṣûri felâ ensâbe beynehüm yevmeiẕiv velâ yetesâelûn.'),
(2775, 23, 102, 'femen ŝeḳulet mevâzînühû feülâike hümü-lmüfliḥûn.'),
(2776, 23, 103, 'vemen ḫaffet mevâzînühû feülâike-lleẕîne ḫasirû enfüsehüm fî cehenneme ḫâlidûn.'),
(2777, 23, 104, 'telfeḥu vucûhehümü-nnâru vehüm fîhâ kâliḥûn.'),
(2778, 23, 105, 'elem tekün âyâtî tütlâ `aleyküm feküntüm bihâ tükeẕẕibûn.'),
(2779, 23, 106, 'ḳâlû rabbenâ galebet `aleynâ şiḳvetünâ vekünnâ ḳavmen ḍâllîn.'),
(2780, 23, 107, 'rabbenâ aḫricnâ minhâ fein `udnâ feinnâ żâlimûn.'),
(2781, 23, 108, 'ḳâle-ḫseû fîhâ velâ tükellimûn.'),
(2782, 23, 109, 'innehû kâne ferîḳum min `ibâdî yeḳûlûne rabbenâ âmennâ fagfir lenâ verḥamnâ veente ḫayru-rrâḥimîn.'),
(2783, 23, 110, 'fetteḫaẕtümûhüm siḫriyyen ḥattâ ensevküm ẕikrî veküntüm minhüm taḍḥakûn.'),
(2784, 23, 111, 'innî cezeytühümü-lyevme bimâ ṣaberû ennehüm hümü-lfâizûn.'),
(2785, 23, 112, 'ḳâle kem lebiŝtüm fi-l\'arḍi `adede sinîn.'),
(2786, 23, 113, 'ḳâlû lebiŝnâ yevmen ev ba`ḍa yevmin fes\'eli-l`âddîn.'),
(2787, 23, 114, 'ḳâle il lebiŝtüm illâ ḳalîlel lev enneküm küntüm ta`lemûn.'),
(2788, 23, 115, 'efeḥasibtüm ennemâ ḫalaḳnâküm `abeŝev veenneküm ileynâ lâ türce`ûn.'),
(2789, 23, 116, 'fete`âle-llâhü-lmelikü-lḥaḳḳ. lâ ilâhe illâ hû. rabbü-l`arşi-lkerîm.'),
(2790, 23, 117, 'vemey yed`u me`a-llâhi ilâhen âḫara lâ bürhâne lehû bihî feinnemâ ḥisâbühû `inde rabbih. innehû lâ yüfliḥu-lkâfirûn.'),
(2791, 23, 118, 'veḳur rabbi-gfir verḥam veente ḫayru-rrâḥimîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'sûratün enzelnâhâ veferaḍnâhâ veenzelnâ fîhâ âyâtim beyyinâtil le`alleküm teẕekkerûn.'),
(2793, 24, 2, 'ezzâniyetü vezzânî feclidû külle vâḥidim minhümâ miete celdeh. velâ te\'ḫuẕküm bihimâ ra\'fetün fî dîni-llâhi in küntüm tü\'minûne billâhi velyevmi-l\'âḫir. velyeşhed `aẕâbehümâ ṭâifetüm mine-lmü\'minîn.'),
(2794, 24, 3, 'ezzânî lâ yenkiḥu illâ zâniyeten ev müşrikeh. vezzâniyetü lâ yenkiḥuhâ illâ zânin ev müşrik. veḥurrime ẕâlike `ale-lmü\'minîn.'),
(2795, 24, 4, 'velleẕîne yermûne-lmuḥsenâti ŝümme lem ye\'tû bierbe`ati şühedâe feclidûhüm ŝemânîne celdetev velâ taḳbelû lehüm şehâdeten ebedâ. veülâike hümü-lfâsiḳûn.'),
(2796, 24, 5, 'ille-lleẕîne tâbû mim ba`di ẕâlike veaṣleḥû. feinne-llâhe gafûrur raḥîm.'),
(2797, 24, 6, 'velleẕîne yermûne ezvâcehüm velem yekül lehüm şühedâü illâ enfüsühüm feşehâdetü eḥadihim erbe`u şehâdâtim billâhi innehû lemine-ṣṣâdiḳîn.'),
(2798, 24, 7, 'velḫâmisetü enne la`nete-llâhi `aleyhi in kâne mine-lkâẕibîn.'),
(2799, 24, 8, 'veyedraü `anhe-l`aẕâbe en teşhede erbe`a şehâdâtim billâhi innehû lemine-lkâẕibîn.'),
(2800, 24, 9, 'velḫâmisete enne gaḍabe-llâhi `aleyhâ in kâne mine-ṣṣâdiḳîn.'),
(2801, 24, 10, 'velevlâ faḍlü-llâhi `aleyküm veraḥmetühû veenne-llâhe tevvâbün ḥakîm.'),
(2802, 24, 11, 'inne-lleẕîne câû bil\'ifki `uṣbetüm minküm. lâ taḥsebûhü şerral leküm. bel hüve ḫayrul leküm. likülli-mriim minhüm me-ktesebe mine-l\'iŝm. velleẕî tevellâ kibrahû minhüm lehû `aẕâbün `ażîm.'),
(2803, 24, 12, 'levlâ iẕ semi`tümûhü żanne-lmü\'minûne velmü\'minâtü bienfüsihim ḫayrav veḳâlû hâẕâ ifküm mübîn.'),
(2804, 24, 13, 'levlâ câû `aleyhi bierbe`ati şühedâ\'. feiẕ lem ye\'tû bişşühedâi feülâike `inde-llâhi hümü-lkâẕibûn.'),
(2805, 24, 14, 'velevlâ faḍlü-llâhi `aleyküm veraḥmetühû fi-ddünyâ vel\'âḫirati lemesseküm fî mâ efaḍtüm fîhi `aẕâbün `ażîm.'),
(2806, 24, 15, 'iẕ teleḳḳavnehû bielsinetiküm veteḳûlûne biefvâhiküm mâ leyse leküm bihî `ilmüv vetaḥsebûnehû heyyinâ. vehüve `inde-llâhi `ażîm.'),
(2807, 24, 16, 'velevlâ iẕ semi`tümûhü ḳultüm mâ yekûnü lenâ en netekelleme bihâẕâ. sübḥâneke hâẕâ bühtânün `ażîm.'),
(2808, 24, 17, 'ye`iżukümü-llâhü en te`ûdû limiŝlihî ebeden in küntüm mü\'minîn.'),
(2809, 24, 18, 'veyübeyyinü-llâhü lekümü-l\'âyât. vellâhü `alîmün ḥakîm.'),
(2810, 24, 19, 'inne-lleẕîne yüḥibbûne en teşî`a-lfâḥişetü fi-lleẕîne âmenû lehüm `aẕâbün elîmün fi-ddünyâ vel\'âḫirah. vellâhü ya`lemü veentüm lâ ta`lemûn.'),
(2811, 24, 20, 'velevlâ faḍlü-llâhi `aleyküm veraḥmetühû veenne-llâhe raûfür raḥîm.'),
(2812, 24, 21, 'yâ eyyühe-lleẕîne âmenû lâ tettebi`û ḫuṭuvâti-şşeyṭân. vemey yettebi` ḫuṭuvâti-şşeyṭâni feinnehû ye\'müru bilfaḥşâi velmünker. velevlâ faḍlü-llâhi `aleyküm veraḥmetühû mâ zekâ minküm min eḥadin ebedev velâkinne-llâhe yüzekkî mey yeşâ\'. vellâhü semî`un `alîm.'),
(2813, 24, 22, 'velâ ye\'teli ülü-lfaḍli minküm vesse`ati ey yü\'tû üli-lḳurbâ velmesâkîne velmühâcirîne fî sebîli-llâh. velya`fû velyaṣfeḥû. elâ tüḥibbûne ey yagfira-llâhü leküm. vellâhü gafûrur raḥîm.'),
(2814, 24, 23, 'inne-lleẕîne yermûne-lmuḥsenâti-lgâfilâti-lmü\'minâti lü`inû fi-ddünyâ vel\'âḫirah. velehüm `aẕâbün `ażîm.'),
(2815, 24, 24, 'yevme teşhedü `aleyhim elsinetühüm veeydîhim veercülühüm bimâ kânû ya`melûn.'),
(2816, 24, 25, 'yevmeiẕiy yüveffîhimü-llâhü dînehümü-lḥaḳḳa veya`lemûne enne-llâhe hüve-lḥaḳḳu-lmübîn.'),
(2817, 24, 26, 'elḫabîŝâtü lilḫabîŝîne velḫabîŝûne lilḫabîŝât. veṭṭayyibâtü liṭṭayyibîne veṭṭayyibûne liṭṭayyibât. ülâike müberraûne mimmâ yeḳûlûn. lehüm magfiratüv verizḳun kerîm.'),
(2818, 24, 27, 'yâ eyyühe-lleẕîne âmenû lâ tedḫulû büyûten gayra büyûtiküm ḥattâ teste\'nisû vetüsellimû `alâ ehlihâ. ẕâliküm ḫayrul leküm le`alleküm teẕekkerûn.'),
(2819, 24, 28, 'feil lem tecidû fîhâ eḥaden felâ tedḫulûhâ ḥattâ yü\'ẕene leküm. vein ḳîle lekümü-rci`û ferci`û hüve ezkâ leküm. vellâhü bimâ ta`melûne `alîm.'),
(2820, 24, 29, 'leyse `aleyküm cünâḥun en tedḫulû büyûten gayra meskûnetin fîhâ metâ`ul leküm. vellâhü ya`lemü mâ tübdûne vemâ tektümûn.'),
(2821, 24, 30, 'ḳul lilmü\'minîne yeguḍḍû min ebṣârihim veyaḥfeżû fürûcehüm. ẕâlike ezkâ lehüm. inne-llâhe ḫabîrum bimâ yaṣne`ûn.'),
(2822, 24, 31, 'veḳul lilmü\'minâti yagḍuḍne min ebṣârihinne veyaḥfażne fürûcehünne velâ yübdîne zînetehünne illâ mâ żahera minhâ velyaḍribne biḫumürihinne `alâ cüyûbihinn. velâ yübdîne zînetehünne illâ libü`ûletihinne ev âbâihinne ev âbâi bü`ûletihinne ev ebnâihinne ev ebnâi bü`ûletihinne ev iḫvânihinne ev benî iḫvânihinne ev benî eḫavâtihinne ev nisâihinne ev mâ meleket eymânühünne evi-ttâbi`îne gayri üli-l\'irbeti mine-rricâli evi-ṭṭifli-lleẕîne lem yażherû `alâ `avrati-nnisâ\'. velâ yaḍribne biercülihinne liyü`leme mâ yuḫfîne min zînetihinn. vetûbû ile-llâhi cemî`an eyyühe-lmü\'minûne le`alleküm tüfliḥûn.'),
(2823, 24, 32, 'venkiḥü-l\'eyâmâ minküm veṣṣâliḥîne min `ibâdiküm veimâiküm. iy yekûnû füḳarâe yugnîhimü-llâhü min faḍlih. vellâhü vâsi`un `alîm.'),
(2824, 24, 33, 'velyesta`fifi-lleẕîne lâ yecidûne nikâḥan ḥattâ yugniyehümü-llâhü min faḍlih. velleẕîne yebtegûne-lkitâbe mimmâ meleket eymânüküm fekâtibûhüm in `alimtüm fîhim ḫayrâ. veâtûhüm mim mâli-llâhi-lleẕî âtâküm. velâ tükrihû feteyâtiküm `ale-lbigâi in eradne teḥaṣṣunel litebtegû `araḍa-lḥayâti-ddünyâ. vemey yükrihhünne feinne-llâhe mim ba`di ikrâhihinne gafûrur raḥîm.'),
(2825, 24, 34, 'veleḳad enzelnâ ileyküm âyâtim mübeyyinâtiv vemeŝelem mine-lleẕîne ḫalev min ḳabliküm vemev`iżatel lilmütteḳîn.'),
(2826, 24, 35, 'allâhü nûru-ssemâvâti vel\'arḍ. meŝelü nûrihî kemişkâtin fîhâ miṣbâḥ. elmiṣbâḥu fî zücâceh. ezzücâcetü keennehâ kevkebün dürriyyüy yûḳadü min şeceratim mübâraketin zeytûnetil lâ şerḳiyyetiv velâ garbiyyetiy yekâdü zeytühâ yüḍîü velev lem temseshü nârun. nûrun `alâ nûr. yehdi-llâhü linûrihî mey yeşâ\'. veyaḍribü-llâhü-l\'emŝâle linnâs. vellâhü bikülli şey\'in `alîm.'),
(2827, 24, 36, 'fî büyûtin eẕine-llâhü en türfe`a veyüẕkera fîhe-smühû yüsebbiḥu lehû fîhâ bilgudüvvi vel\'eṣâl.'),
(2828, 24, 37, 'ricâlül lâ tülhîhim ticâratüv velâ bey`un `an ẕikri-llâhi veiḳâmi-ṣṣalâti veîtâi-zzekâti yeḫâfûne yevmen teteḳallebü fîhi-lḳulûbü vel\'ebṣâr.'),
(2829, 24, 38, 'liyecziyehümü-llâhü aḥsene mâ `amilû veyezîdehüm min faḍlih. vellâhü yerzüḳu mey yeşâü bigayri ḥisâb.'),
(2830, 24, 39, 'velleẕîne keferû a`mâlühüm keserâbim biḳî`atiy yaḥsebühu-żżam\'ânü mââ. ḥattâ iẕâ câehû lem yecidhü şey\'ev vevecede-llâhe `indehû feveffâhü ḥisâbeh. vellâhü serî`u-lḥisâb.'),
(2831, 24, 40, 'ev keżulümâtin fî baḥril lücciyyiy yagşâhü mevcüm min fevḳihî mevcüm min fevḳihî seḥâb. żulümâtüm ba`ḍuhâ fevḳa ba`ḍ. iẕâ aḫrace yedehû lem yeked yerâhâ. vemel lem yec`ali-llâhü lehû nûran femâ lehû min nûr.'),
(2832, 24, 41, 'elem tera enne-llâhe yüsebbiḥu lehû men fi-ssemâvâti vel\'arḍi veṭṭayru ṣâffât. küllün ḳad `alime ṣalâtehû vetesbîḥah. vellâhü `alîmüm bimâ yef`alûn.'),
(2833, 24, 42, 'velillâhi mülkü-ssemâvâti vel\'arḍ. veile-llâhi-lmeṣîr.'),
(2834, 24, 43, 'elem tera enne-llâhe yüzcî seḥâben ŝümme yü\'ellifü beynehû ŝümme yec`alühû rukâmen fetere-lvedḳa yaḫrucü min ḫilâlih. veyünezzilü mine-ssemâi min cibâlin fîhâ mim beradin feyüṣîbü bihî mey yeşâü veyaṣrifühû `am mey yeşâ\'. yekâdü senâ berḳihî yeẕhebü bil\'ebṣâr.'),
(2835, 24, 44, 'yüḳallibü-llâhü-lleyle vennehâr. inne fî ẕâlike le`ibratel liüli-l\'ebṣâr.'),
(2836, 24, 45, 'vellâhü ḫaleḳa külle dâbbetim mim mâ\'. feminhüm mey yemşî `alâ baṭnih. veminhüm mey yemşî `alâ ricleyn. veminhüm mey yemşî `alâ erba`. yaḫlüḳu-llâhü mâ yeşâ\'. inne-llâhe `alâ külli şey\'in ḳadîr.'),
(2837, 24, 46, 'leḳad enzelnâ âyâtim mübeyyinât. vellâhü yehdî mey yeşâü ilâ ṣirâṭim müsteḳîm.'),
(2838, 24, 47, 'veyeḳûlûne âmennâ billâhi vebirrasûli veeṭa`nâ ŝümme yetevellâ ferîḳum minhüm mim ba`di ẕâlik. vemâ ülâike bilmü\'minîn.'),
(2839, 24, 48, 'veiẕâ dü`û ile-llâhi verasûlihî liyaḥküme beynehüm iẕâ ferîḳum minhüm mü`riḍûn.'),
(2840, 24, 49, 'veiy yekül lehümü-lḥaḳḳu ye\'tû ileyhi müẕ`inîn.'),
(2841, 24, 50, 'efî ḳulûbihim meraḍun emi-rtâbû em yeḫâfûne ey yeḥîfe-llâhü `aleyhim verasûlüh. bel ülâike hümu-żżâlimûn.'),
(2842, 24, 51, 'innemâ kâne ḳavle-lmü\'minîne iẕâ dü`û ile-llâhi verasûlihî liyaḥküme beynehüm ey yeḳûlû semi`nâ veeṭa`nâ. veülâike hümü-lmüfliḥûn.'),
(2843, 24, 52, 'vemey yüṭi`i-llâhe verasûlehû veyaḫşe-llâhe veyettaḳhi feülâike hümü-lfâizûn.'),
(2844, 24, 53, 'veaḳsemû billâhi cehde eymânihim lein emertehüm leyaḫrucünn. ḳul lâ tuḳsimû. ṭâ`atüm ma`rûfeh. inne-llâhe ḫabîrum bimâ ta`melûn.'),
(2845, 24, 54, 'ḳul eṭî`ü-llâhe veeṭî`ü-rrasûl. fein tevellev feinnemâ `aleyhi mâ ḥummile ve`aleyküm mâ ḥummiltüm. vein tüṭî`ûhü tehtedû. vemâ `ale-rrasûli ille-lbelâgu-lmübîn.'),
(2846, 24, 55, 've`ade-llâhü-lleẕîne âmenû minküm ve`amilu-ṣṣâliḥâti leyestaḫlifennehüm fi-l\'arḍi keme-staḫlefe-lleẕîne min ḳablihim. veleyümekkinenne lehüm dînehümü-lleẕi-rteḍâ lehüm veleyübeddilennehüm mim ba`di ḫavfihim emnâ. ya`büdûnenî lâ yüşrikûne bî şey\'â. vemen kefera ba`de ẕâlike feülâike hümü-lfâsiḳûn.'),
(2847, 24, 56, 'veeḳîmu-ṣṣalâte veâtü-zzekâte veeṭî`ü-rrasûle le`alleküm türḥamûn.'),
(2848, 24, 57, 'lâ taḥsebenne-lleẕîne keferû mü`cizîne fi-l\'arḍ. veme\'vâhümü-nnâr. velebi\'se-lmeṣîr.'),
(2849, 24, 58, 'yâ eyyühe-lleẕîne âmenû liyeste\'ẕinkümü-lleẕîne meleket eymânüküm velleẕîne lem yeblügü-lḥulûme minküm ŝelâŝe merrât. min ḳabli ṣalâti-lfecri veḥîne teḍa`ûne ŝiyâbeküm mine-żżahîrati vemim ba`di ṣalâti-l`işâ\'. ŝelâŝü `avrâtil leküm. leyse `aleyküm velâ `aleyhim cünâḥum ba`dehünn. ṭavvâfûne `aleyküm ba`ḍuküm `alâ ba`ḍ. keẕâlike yübeyyinü-llâhü lekümü-l\'âyât. vellâhü `alîmün ḥakîm.'),
(2850, 24, 59, 'veiẕâ belega-l\'aṭfâlü minkümü-lḥulûme felyeste\'ẕinû keme-ste\'ẕene-lleẕîne min ḳablihim. keẕâlike yübeyyinü-llâhü leküm âyâtih. vellâhü `alîmün ḥakîm.'),
(2851, 24, 60, 'velḳavâ`idü mine-nnisâi-llâtî lâ yercûne nikâḥan feleyse `aleyhinne cünâḥun ey yeḍa`ne ŝiyâbehünne gayra müteberricâtim bizîneh. veey yesta`fifne ḫayrul lehünn. vellâhü semî`un `alîm.'),
(2852, 24, 61, 'leyse `ale-l\'a`mâ ḥaracüv velâ `ale-l\'a`raci ḥaracüv velâ `ale-lmerîḍi ḥaracüv velâ `alâ enfüsiküm en te\'külû mim büyûtiküm ev büyûti âbâiküm ev büyûti ümmehâtiküm ev büyûti iḫvâniküm ev büyûti eḫavâtiküm ev büyûti a`mâmiküm ev büyûti `ammâtiküm ev büyûti aḫvâliküm ev büyûti ḫâlâtiküm ev mâ melektüm mefâtiḥahû ev ṣadîḳiküm. leyse `aleyküm cünâḥun en te\'külû cemî`an ev eştâtâ. feiẕâ deḫaltüm büyûten fesellimû `alâ enfüsiküm teḥiyyetem min `indi-llâhi mübâraketen ṭayyibeh. keẕâlike yübeyyinü-llâhü lekümü-l\'âyâti le`alleküm ta`ḳilûn.'),
(2853, 24, 62, 'inneme-lmü\'minûne-lleẕîne âmenû billâhi verasûlihî veiẕâ kânû me`ahû `alâ emrin câmi`il lem yeẕhebû ḥattâ yeste\'ẕinûh. inne-lleẕîne yeste\'ẕinûneke ülâike-lleẕîne yü\'minûne billâhi verasûlih. feiẕe-ste\'ẕenûke liba`ḍi şe\'nihim fe\'ẕel limen şi\'te minhüm vestagfir lehümü-llâh. inne-llâhe gafûrur raḥîm.'),
(2854, 24, 63, 'lâ tec`alû dü`âe-rrasûli beyneküm kedü`âi ba`ḍiküm ba`ḍâ. ḳad ya`lemü-llâhü-lleẕîne yetesellelûne minküm livâẕâ. felyaḥẕeri-lleẕîne yüḫâlifûne `an emrih en tüṣîbehüm fitnetün ev yüṣîbehüm `aẕâbün elîm.'),
(2855, 24, 64, 'elâ inne lillâhi mâ fi-ssemâvâti vel\'arḍ. ḳad ya`lemü mâ entüm `aleyh. veyevme yürce`ûne ileyhi feyünebbiühüm bimâ `amilû. vellâhü bikülli şey\'in `alîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'tebârake-lleẕî nezzele-lfürḳâne `alâ `abdihî liyekûne lil`âlemîne neẕîrâ.'),
(2857, 25, 2, 'elleẕî lehû mülkü-ssemâvâti vel\'arḍi velem yetteḫiẕ veledev velem yekül lehû şerîkün fi-lmülki veḫaleḳa külle şey\'in feḳadderahû taḳdîrâ.'),
(2858, 25, 3, 'vetteḫaẕû min dûnihî âlihetel lâ yaḫlüḳûne şey\'ev vehüm yuḫleḳûne velâ yemlikûne lienfüsihim ḍarrav velâ nef`av velâ yemlikûne mevtev velâ ḥayâtev velâ nüşûrâ.'),
(2859, 25, 4, 'veḳâle-lleẕîne keferû in hâẕâ illâ ifkün-fterâhü vee`ânehû `aleyhi ḳavmün âḫarûn. feḳad câû żulmev vezûrâ.'),
(2860, 25, 5, 'veḳâlû esâṭîru-l\'evvelîne-ktetebehâ fehiye tümlâ `aleyhi bükratev veeṣîlâ.'),
(2861, 25, 6, 'ḳul enzelehü-lleẕî ya`lemü-ssirra fi-ssemâvâti vel\'arḍ. innehû kâne gafûrar raḥîmâ.'),
(2862, 25, 7, 'veḳâlû mâ lihâẕe-rrasûli ye\'külu-ṭṭa`âme veyemşî fi-l\'esvâḳ. levlâ ünzile ileyhi melekün feyekûne me`ahû neẕîrâ.'),
(2863, 25, 8, 'ev yülḳâ ileyhi kenzün ev tekûnü lehû cennetüy ye\'külü minhâ. veḳâle-żżâlimûne in tettebi`ûne illâ racülem mesḥûrâ.'),
(2864, 25, 9, 'ünżur keyfe ḍarabû leke-l\'emŝâle feḍallû felâ yesteṭî`ûne sebîlâ.'),
(2865, 25, 10, 'tebârake-lleẕî in şâe ce`ale leke ḫayram min ẕâlike cennâtin tecrî min taḥtihe-l\'enhâru veyec`al leke ḳuṣûrâ.'),
(2866, 25, 11, 'bel keẕẕebû bissâ`ati vea`tednâ limen keẕẕebe bissâ`ati se`îrâ.'),
(2867, 25, 12, 'iẕâ raethüm mim mekânim be`îdin semi`û lehâ tegayyüżav vezefîrâ.'),
(2868, 25, 13, 'veiẕâ ülḳû minhâ mekânen ḍayyiḳam müḳarranîne de`av hünâlike ŝübûrâ.'),
(2869, 25, 14, 'lâ ted`ü-lyevme ŝübûrav vâḥidev ved`û ŝübûran keŝîrâ.'),
(2870, 25, 15, 'ḳul eẕâlike ḫayrun em cennetü-lḫuldi-lletî vu`ide-lmütteḳûn. kânet lehüm cezâev vemeṣîrâ.'),
(2871, 25, 16, 'lehüm fîhâ mâ yeşâûne ḫâlidîn. kâne `alâ rabbike va`dem mes\'ûlâ.'),
(2872, 25, 17, 'veyevme yaḥşüruhüm vemâ ya`büdûne min dûni-llâhi feyeḳûlü eentüm aḍleltüm `ibâdî hâülâi em hüm ḍallü-ssebîl.'),
(2873, 25, 18, 'ḳâlû sübḥâneke mâ kâne yembegî lenâ en netteḫiẕe min dûnike min evliyâe velâkim metta`tehüm veâbâehüm ḥattâ nesü-ẕẕikr. vekânû ḳavmem bûrâ.'),
(2874, 25, 19, 'feḳad keẕẕebûküm bimâ teḳûlûne femâ testeṭî`ûne ṣarfev velâ naṣrâ. vemey yażlim minküm nüẕiḳhü `aẕâben kebîrâ.'),
(2875, 25, 20, 'vemâ erselnâ ḳableke mine-lmürselîne illâ innehüm leye\'külûne-ṭṭa`âme veyemşûne fi-l\'esvâḳ. vece`alnâ ba`ḍaküm liba`ḍin fitneh. etaṣbirûn. vekâne rabbüke beṣîrâ.'),
(2876, 25, 21, 'veḳâle-lleẕîne lâ yercûne liḳâenâ levlâ ünzile `aleyne-lmelâiketü ev nerâ rabbenâ. leḳadi-stekberû fî enfüsihim ve`atev `utüvven kebîrâ.'),
(2877, 25, 22, 'yevme yeravne-lmelâikete lâ büşrâ yevmeiẕil lilmücrimîne veyeḳûlûne ḥicram maḥcûrâ.'),
(2878, 25, 23, 'veḳadimnâ ilâ mâ `amilû min `amelin fece`alnâhü hebâem menŝûrâ.'),
(2879, 25, 24, 'aṣḥâbü-lcenneti yevmeiẕin ḫayrum müsteḳarrav veaḥsenü meḳîlâ.'),
(2880, 25, 25, 'veyevme teşeḳḳaḳu-ssemâü bilgamâmi venüzzile-lmelâiketü tenzîlâ.'),
(2881, 25, 26, 'elmülkü yevmeiẕin-lḥaḳḳu lirraḥmân. vekâne yevmen `ale-lkâfirîne `asîrâ.'),
(2882, 25, 27, 'veyevme ye`aḍḍu-żżâlimü `alâ yedeyhi yeḳûlü yâ leyteni-tteḫaẕtü me`a-rrasûli sebîlâ.'),
(2883, 25, 28, 'yâ veyletâ leytenî lem etteḫiẕ fülânen ḫalîlâ.'),
(2884, 25, 29, 'leḳad eḍallenî `ani-ẕẕikri ba`de iẕ câenî. vekâne-şşeyṭânü lil\'insâni ḫaẕûlâ.'),
(2885, 25, 30, 'veḳâle-rrasûlü yâ rabbi inne ḳavmi-tteḫaẕû hâẕe-lḳur\'âne mehcûrâ.'),
(2886, 25, 31, 'vekeẕâlike ce`alnâ likülli nebiyyin `adüvvem mine-lmücrimîn. vekefâ birabbike hâdiyev veneṣîrâ.'),
(2887, 25, 32, 'veḳâle-lleẕîne keferû levlâ nüzzile `aleyhi-lḳur\'ânü cümletev vâḥideh. keẕâlike linüŝebbite bihî füâdeke verattelnâhü tertîlâ.'),
(2888, 25, 33, 'velâ ye\'tûneke bimeŝelin illâ ci\'nâke bilḥaḳḳi veaḥsene tefsîrâ.'),
(2889, 25, 34, 'elleẕîne yuḥşerûne `alâ vucûhihim ilâ cehenneme ülâike şerrum mekânev veeḍallü sebîlâ.'),
(2890, 25, 35, 'veleḳad âteynâ mûse-lkitâbe vece`alnâ me`ahû eḫâhü hârûne vezîrâ.'),
(2891, 25, 36, 'feḳulne-ẕhebâ ile-lḳavmi-lleẕîne keẕẕebû biâyâtinâ. fedemmernâhüm tedmîrâ.'),
(2892, 25, 37, 'veḳavme nûḥil lemmâ keẕẕebü-rrusüle agraḳnâhüm vece`alnâhüm linnâsi âyeh. vea`tednâ liżżâlimîne `aẕâben elîmâ.'),
(2893, 25, 38, 've`âdev veŝemûde veaṣḥâbe-rrassi veḳurûnem beyne ẕâlike keŝîrâ.'),
(2894, 25, 39, 'veküllen ḍarabnâ lehü-l\'emŝâl. veküllen tebbernâ tetbîrâ.'),
(2895, 25, 40, 'veleḳad etev `ale-lḳaryeti-lletî ümṭirat meṭara-ssev\'. efelem yekûnû yeravnehâ. bel kânû lâ yercûne nüşûrâ.'),
(2896, 25, 41, 'veiẕâ raevke iy yetteḫiẕûneke illâ hüzüvâ. ehâẕe-lleẕî be`aŝe-llâhü rasûâ.'),
(2897, 25, 42, 'in kâde leyüḍillünâ `an âlihetinâ levlâ en ṣabernâ `aleyhâ. vesevfe ya`lemûne ḥîne yeravne-l`aẕâbe men eḍallü sebîlâ.'),
(2898, 25, 43, 'era\'eyte meni-tteḫaẕe ilâhehû hevâh. efeente tekûnü `aleyhi vekîlâ.'),
(2899, 25, 44, 'em taḥsebü enne ekŝerahüm yesme`ûne ev ya`ḳilûn. in hüm illâ kel\'en`âmi bel hüm eḍallü sebîlâ.'),
(2900, 25, 45, 'elem tera ilâ rabbike keyfe medde-żżill. velev şâe lece`alehû sâkinâ. ŝümme ce`alne-şşemse `aleyhi delîlâ.'),
(2901, 25, 46, 'ŝümme ḳabaḍnâhü ileynâ ḳabḍay yesîrâ.'),
(2902, 25, 47, 'vehüve-lleẕî ce`ale lekümü-lleyle libâsev vennevme sübâtev vece`ale-nnehâra nüşûrâ.'),
(2903, 25, 48, 'vehüve-lleẕî ersele-rriyâḥa büşram beyne yedey raḥmetih. veenzelnâ mine-ssemâi mâen ṭahûrâ.'),
(2904, 25, 49, 'linuḥyiye bihî beldetem meytev venüsḳiyehû mimmâ ḫalaḳnâ en`âmev veenâsiyye keŝîrâ.'),
(2905, 25, 50, 'veleḳad ṣarrafnâhü beynehüm liyeẕẕekkerû. feebâ ekŝeru-nnâsi illâ küfûrâ.'),
