INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'sebbeḥa lillâhi mâ fi-ssemâvâti vel\'arḍ. vehüve-l`azîzü-lḥakîm.'),
(5077, 57, 2, 'lehû mülkü-ssemâvâti vel\'arḍ. yuḥyî veyümît. vehüve `alâ külli şey\'in ḳadîr.'),
(5078, 57, 3, 'hüve-l\'evvelü vel\'âḫiru veżżâhiru velbâṭin. vehüve bikülli şey\'in `alîm.'),
(5079, 57, 4, 'hüve-lleẕî ḫaleḳa-ssemâvâti vel\'arḍa fî sitteti eyyâmin ŝümme-stevâ `ale-l`arş. ya`lemü mâ yelicü fi-l\'arḍi vemâ yaḫrucü minhâ vemâ yenzilü mine-ssemâi vemâ ya`rucü fîhâ. vehüve me`aküm eyne mâ küntüm. vellâhü bimâ ta`melûne beṣîr.'),
(5080, 57, 5, 'lehû mülkü-ssemâvâti vel\'arḍ. veile-llâhi türce`u-l\'ümûr.'),
(5081, 57, 6, 'yûlicü-lleyle fi-nnehâri veyûlicü-nnehâra fi-lleyl. vehüve `alîmüm biẕâti-ṣṣudûr.'),
(5082, 57, 7, 'âminû billâhi verasûlihî veenfiḳû mimmâ ce`aleküm müstaḫlefîne fîh. felleẕîne âmenû minküm veenfeḳû lehüm ecrun kebîr.'),
(5083, 57, 8, 'vemâ leküm lâ tü\'minûne billâh. verrasûlü yed`ûküm litü\'minû birabbiküm veḳad eḫaẕe mîŝâḳaküm in küntüm mü\'minîn.'),
(5084, 57, 9, 'hüve-lleẕî yünezzilü `alâ `abdihî âyâtim beyyinâtil liyuḫriceküm mine-żżulümâti ile-nnûr. veinne-llâhe biküm leraûfür raḥîm.'),
(5085, 57, 10, 'vemâ leküm ellâ tünfiḳû fî sebîli-llâhi velillâhi mîrâŝü-ssemâvâti vel\'arḍ. lâ yestevî minküm men enfeḳa min ḳabli-lfetḥi veḳâtel. ülâike a`żamü deracetem mine-lleẕîne enfeḳû mim ba`dü veḳâtelû. veküllev ve`ade-llâhü-lḥusnâ. vellâhü bimâ ta`melûne ḫabîr.'),
(5086, 57, 11, 'men ẕe-lleẕî yuḳriḍu-llâhe ḳarḍan ḥasenen feyüḍâ`ifehû lehû velehû ecrun kerîm.'),
(5087, 57, 12, 'yevme tera-lmü\'minîne velmü\'minâti yes`â nûruhüm beyne eydîhim vebieymânihim büşrâkümü-lyevme cennâtün tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. ẕâlike hüve-lfevzü-l`ażîm.'),
(5088, 57, 13, 'yevme yeḳûlü-lmünâfiḳûne velmünâfiḳâtü lilleẕîne âmenü-nżurûnâ naḳtebis min nûriküm ḳîle-rci`û verâeküm feltemisû nûrâ. feḍuribe beynehüm bisûril lehû bâb. bâṭinühû fîhi-rraḥmetü veżâhiruhû min ḳibelihi-l`aẕâb.'),
(5089, 57, 14, 'yünâdûnehüm elem neküm me`aküm. ḳâlû belâ velâkinneküm fetentüm enfüseküm veterabbaṣtüm vertebtüm vegarratkümü-l\'emâniyyü ḥattâ câe emru-llâhi vegarraküm billâhi-lgarûr.'),
(5090, 57, 15, 'felyevme lâ yü\'ḫaẕü minküm fidyetüv velâ mine-lleẕîne keferû. me\'vâkümü-nnâr. hiye mevlâküm. vebi\'se-lmeṣîr.'),
(5091, 57, 16, 'elem ye\'ni lilleẕîne âmenû en taḫşe`a ḳulûbühüm liẕikri-llâhi vemâ nezele mine-lḥaḳḳi velâ yekûnû kelleẕîne ûtü-lkitâbe min ḳablü feṭâle `aleyhimü-l\'emedü feḳaset ḳulûbühüm. vekeŝîrum minhüm fâsiḳûn.'),
(5092, 57, 17, 'i`lemû enne-llâhe yuḥyi-l\'arḍa ba`de mevtihâ. ḳad beyyennâ lekümü-l\'âyâti le`alleküm ta`ḳilûn.'),
(5093, 57, 18, 'inne-lmüṣṣaddiḳîne velmüṣṣaddiḳâti veaḳraḍu-llâhe ḳarḍan ḥaseney yüḍâ`afü lehüm velehüm ecrun kerîm.'),
(5094, 57, 19, 'velleẕîne âmenû billâhi verusülihî ülâike hümu-ṣṣiddîḳûn. veşşühedâü `inde rabbihim. lehüm ecruhüm venûruhüm. velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-lceḥîm.'),
(5095, 57, 20, 'i`lemû enneme-lḥayâtü-ddünyâ le`ibüv velehvüv vezînetüv vetefâḫurum beyneküm vetekâŝürun fi-l\'emvâli vel\'evlâd. kemeŝeli gayŝin a`cebe-lküffâra nebâtühû ŝümme yehîcü feterâhü muṣferran ŝümme yekûnü ḥuṭâmâ. vefi-l\'âḫirati `aẕâbün şedîdüv vemagfiratüm mine-llâhi veriḍvân. veme-lḥayâtü-ddünyâ illâ metâ`u-lgurûr.'),
(5096, 57, 21, 'sâbiḳû ilâ magfiratim mir rabbiküm vecennetin `arḍuhâ ke`arḍi-ssemâi vel\'arḍi ü`iddet lilleẕîne âmenû billâhi verusülih. ẕâlike faḍlü-llâhi yü\'tîhi mey yeşâ\'. vellâhü ẕü-lfaḍli-l`ażîm.'),
(5097, 57, 22, 'mâ eṣâbe mim müṣîbetin fi-l\'arḍi velâ fî enfüsiküm illâ fî kitâbim min ḳabli en nebraehâ. inne ẕâlike `ale-llâhi yesîr.'),
(5098, 57, 23, 'likeylâ te\'sev `alâ mâ fâteküm velâ tefraḥû bimâ âtâküm. vellâhü lâ yüḥibbü külle muḫtâlin feḫûr.'),
(5099, 57, 24, 'elleẕîne yebḫalûne veye\'mürûne-nnâse bilbuḫl. vemey yetevelle feinne-llâhe hüve-lganiyyü-lḥamîd.'),
(5100, 57, 25, 'leḳad erselnâ rusülenâ bilbeyyinâti veenzelnâ me`ahümü-lkitâbe velmîzâne liyeḳûme-nnâsü bilḳisṭ. veenzelne-lḥadîde fîhi be\'sün şedîdüv vemenâfi`u linnâsi veliya`leme-llâhü mey yenṣuruhû verusülehû bilgayb. inne-llâhe ḳaviyyün `azîz.'),
(5101, 57, 26, 'veleḳad erselnâ nûḥav veibrâhime vece`alnâ fî ẕürriyyetihime-nnübüvvete velkitâbe feminhüm mühted. vekeŝîrum minhüm fâsiḳûn.'),
(5102, 57, 27, 'ŝümme ḳaffeynâ `alâ âŝârihim birusülinâ veḳaffeynâ bi`îse-bni meryeme veâteynâhü-l\'incîle vece`alnâ fî ḳulûbi-lleẕîne-ttebe`ûhü ra\'fetev veraḥmeh. verahbâniyyeten-btede`ûhâ mâ ketebnâhâ `aleyhim ille-btigâe riḍvâni-llâhi femâ ra`avhâ ḥaḳḳa ri`âyetihâ. feâteyne-lleẕîne âmenû minhüm ecrahüm. vekeŝîrum minhüm fâsiḳûn.'),
(5103, 57, 28, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe veâminû birasûlihî yü\'tiküm kifleyni mir raḥmetihî veyec`al leküm nûran temşûne bihî veyagfir leküm. vellâhü gafûrur raḥîm.'),
(5104, 57, 29, 'liellâ ya`leme ehlü-lkitâbi ellâ yaḳdirûne `alâ şey\'im min faḍli-llâhi veenne-lfaḍle biyedi-llâhi yü\'tîhi mey yeşâ\'. vellâhü ẕü-lfaḍli-l`ażîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'ḳad semi`a-llâhü ḳavle-lletî tücâdilüke fi zevcihâ veteştekî ile-llâh. vellâhü yesme`u teḥâvurakümâ. inne-llâhe semî`um beṣîr.'),
(5106, 58, 2, 'elleẕîne yüżâhirûne minküm min nisâihim mâ hünne ümmehâtihim. in ümmehâtühüm ille-llâî velednâhüm. veinnehüm leyeḳûlûne münkeram mine-lḳavli vezûrâ. veinne-llâhe le`afüvvun gafûr.'),
(5107, 58, 3, 'velleẕîne yüżâhirûne min nisâihim ŝümme ye`ûdûne limâ ḳâlû fetaḥrîru raḳabetim min ḳabli ey yetemâssâ. ẕâliküm tû`ażûne bih. vellâhü bimâ ta`melûne ḫabîr.'),
(5108, 58, 4, 'femel lem yecid feṣiyâmü şehrayni mütetâbi`ayni min ḳabli ey yetemâssâ. femel lem yesteṭi` feiṭ`âmü sittîne miskînâ. ẕâlike litü\'minû billâhi verasûlih. vetilke ḥudûdü-llâh. velilkâfirîne `aẕâbün elîm.'),
(5109, 58, 5, 'inne-lleẕîne yüḥâddûne-llâhe verasûlehû kübitû kemâ kübite-lleẕîne min ḳablihim veḳad enzelnâ âyâtim beyyinât. velilkâfirîne `aẕâbüm mühîn.'),
(5110, 58, 6, 'yevme yeb`aŝühümü-llâhü cemî`an feyünebbiühüm bimâ `amilû. aḥṣâhü-llâhü venesûh. vellâhü `alâ külli şey\'in şehîd.'),
(5111, 58, 7, 'elem tera enne-llâhe ya`lemü mâ fi-ssemâvâti vemâ fi-l\'arḍ. mâ yekûnü min necvâ ŝelâŝetin illâ hüve râbi`uhüm velâ ḫamsetin illâ hüve sâdisühüm velâ ednâ min ẕâlike velâ ekŝera illâ hüve me`ahüm eyne mâ kânû. ŝümme yünebbiühüm bimâ `amilû yevme-lḳiyâmeh. inne-llâhe bikülli şey\'in `alîm.'),
(5112, 58, 8, 'elem tera ile-lleẕîne nühû `ani-nnecvâ ŝümme ye`ûdûne limâ nühû `anhü veyetenâcevne bil\'iŝmi vel`udvâv vema`ṣiyeti-rrasûl. veiẕâ câûke ḥayyevke bimâ lem yüḥayyike bihi-llâhü veyeḳûlûne fî enfüsihim levlâ yü`aẕẕibüne-llâhü bimâ neḳûl. ḥasbühüm cehennem. yaṣlevnehâ. febi\'se-lmeṣîr.'),
(5113, 58, 9, 'yâ eyyühe-lleẕîne âmenû iẕâ tenâceytüm felâ tetenâcev bil\'iŝmi vel`udvâni vema`ṣiyeti-rrasûli vetenâcev bilbirri vettaḳvâ. vetteḳu-llâhe-lleẕî ileyhi tuḥşerûn.'),
(5114, 58, 10, 'inneme-nnecvâ mine-şşeyṭâni liyaḥzüne-lleẕîne âmenû veleyse biḍârrihim şey\'en illâ biiẕni-llâh. ve`ale-llâhi felyetevekkeli-lmü\'minûn.'),
(5115, 58, 11, 'yâ eyyühe-lleẕîne âmenû iẕâ ḳîle leküm tefesseḥû fi-lmecâlisi fefseḥû yefseḥi-llâhü leküm. veiẕâ ḳîle-nşüzû fenşüzû yerfe`i-llâhü-lleẕîne âmenû minküm velleẕîne ûtü-l`ilme deracât. vellâhü bimâ ta`melûne ḫabîr.'),
(5116, 58, 12, 'yâ eyyühe-lleẕîne âmenû iẕâ nâceytümü-rrasûle feḳaddimû beyne yedey necvâküm ṣadeḳaten. ẕâlike ḫayrul leküm veaṭher. feil lem tecidû feinne-llâhe gafûrur raḥîm.'),
(5117, 58, 13, 'eeşfaḳtüm en tüḳaddimû beyne yedey necvâküm ṣadeḳât. feiẕ lem tef`alû vetâbe-llâhü `aleyküm feeḳîmu-ṣṣalâte veâtü-zzekâte veeṭî`ü-llâhe verasûleh. vellâhü ḫabîrum bimâ ta`melûn.'),
(5118, 58, 14, 'elem tera ile-lleẕîne tevellev ḳavmen gaḍibe-llâhü `aleyhim. mâ hüm minküm velâ minhüm veyaḥlifûne `ale-lkeẕibi vehüm ya`lemûn.'),
(5119, 58, 15, 'e`adde-llâhü lehüm `aẕâben şedîdâ. innehüm sâe mâ kânû ya`melûn.'),
(5120, 58, 16, 'itteḫaẕû eymânehüm cünneten feṣaddû `an sebîli-llâhi felehüm `aẕâbüm mühîn.'),
(5121, 58, 17, 'len tugniye `anhüm emvâlühüm velâ evlâdühüm mine-llâhi şey\'â. ülâike aṣḥâbü-nnâr. hüm fîhâ ḫâlidûn.'),
(5122, 58, 18, 'yevme yeb`aŝühümü-llâhü cemî`an feyaḥlifûne lehû kemâ yaḥlifûne leküm veyaḥsebûne ennehüm `alâ şey\'. elâ innehüm hümü-lkâẕibûn.'),
(5123, 58, 19, 'istaḥveẕe `aleyhimü-şşeyṭânü feensâhüm ẕikra-llâh. ülâike ḥizbü-şşeyṭân. elâ inne ḥizbe-şşeyṭâni hümü-lḫâsirûn.'),
(5124, 58, 20, 'inne-lleẕîne yüḥâddûne-llâhe verasûlehû ülâike fi-l\'eẕellîn.'),
(5125, 58, 21, 'ketebe-llâhü leaglibenne ene verusülî. inne-llâhe ḳaviyyün `azîz.'),
(5126, 58, 22, 'lâ tecidü ḳavmey yü\'minûne billâhi velyevmi-l\'âḫiri yüvâddûne men ḥâdde-llâhe verasûlehû velev kânû âbâehüm ev ebnâehüm ev iḫvânehüm ev `aşîratehüm. ülâike ketebe fî ḳulûbihimü-l\'îmâne veeyyedehüm birûḥim minh. veyüdḫilühüm cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ. raḍiye-llâhü `anhüm veraḍû `anh. ülâike ḥizbü-llâh. elâ inne ḥizbe-llâhi hümü-lmüfliḥûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'sebbeḥa lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vehüve-l`azîzü-lḥakîm.'),
(5128, 59, 2, 'hüve-lleẕî aḫrace-lleẕîne keferû min ehli-lkitâbi min diyârihim lievveli-lḥaşr. mâ żanentüm ey yaḫrucû veżannû ennehüm mâni`atühüm ḥuṣûnühüm mine-llâhi feetâhümü-llâhü min ḥayŝü lem yaḥtesibû veḳaẕefe fî ḳulûbihimü-rru`be yuḫribûne büyûtehüm bieydîhim veeydi-lmü\'minîne fa`tebirû yâ ûli-l\'ebṣâr.'),
(5129, 59, 3, 'velevlâ en ketebe-llâhü `aleyhimü-lcelâe le`aẕẕebehüm fi-ddünyâ. velehüm fi-l\'âḫirati `aẕâbü-nnâr.'),
(5130, 59, 4, 'ẕâlike biennehüm şâḳḳu-llâhe verasûleh. vemey yüşâḳḳi-llâhe feinne-llâhe şedîdü-l`iḳâb.'),
(5131, 59, 5, 'mâ ḳata`tüm mil lînetin ev teraktümûhâ ḳâimeten `alâ üṣûlihâ febiiẕni-llâhi veliyuḫziye-lfâsiḳîn.'),
(5132, 59, 6, 'vemâ efâe-llâhü `alâ rasûlihî minhüm femâ evceftüm `aleyhi min ḫayliv velâ rikâbiv velâkinne-llâhe yüselliṭu rusülehû `alâ mey yeşâ\'. vellâhü `alâ külli şey\'in ḳadîr.'),
(5133, 59, 7, 'mâ efâe-llâhü `alâ rasûlihî min ehli-lḳurâ felillâhi velirrasûli veliẕi-lḳurbâ velyetâmâ velmesâkîni vebni-ssebîli key lâ yekûne dûletem beyne-l\'agniyâi minküm. vemâ âtâkümü-rrasûlü feḫuẕûhü vemâ nehâküm `anhü fentehû. vetteḳu-llâh. inne-llâhe şedîdü-l`iḳâb.'),
(5134, 59, 8, 'lilfüḳarâi-lmühâcirîne-lleẕîne uḫricû min diyârihim veemvâlihim yebtegûne faḍlem mine-llâhi veriḍvânev veyenṣurûne-llâhe verasûleh. ülâike hümu-ṣṣâdiḳûn.'),
(5135, 59, 9, 'velleẕîne tebevveü-ddâra vel\'îmâne min ḳablihim yüḥibbûne men hâcera ileyhim velâ yecidûne fî ṣudûrihim ḥâcetem mimmâ ûtû veyü\'ŝirûne `alâ enfüsihim velev kâne bihim ḫaṣâṣah. vemey yûḳa şüḥḥa nefsihî feülâike hümü-lmüfliḥûn.'),
(5136, 59, 10, 'velleẕîne câû mim ba`dihim yeḳûlûne rabbene-gfir lenâ veliiḫvânine-lleẕîne sebeḳûnâ bil\'îmâni velâ tec`al fî ḳulûbinâ gillel lilleẕîne âmenû rabbenâ inneke raûfür raḥîm.'),
(5137, 59, 11, 'elem tera ile-lleẕîne nâfeḳû yeḳûlûne liiḫvânihimü-lleẕîne keferû min ehli-lkitâbi lein uḫrictüm lenaḫrucenne me`aküm velâ nüṭî`u fîküm eḥaden ebedev vein ḳûtiltüm lenenṣuranneküm. vellâhü yeşhedü innehüm lekâẕibûn.'),
(5138, 59, 12, 'lein uḫricû lâ yaḫrucûne me`ahüm. velein ḳûtilû lâ yenṣurûnehüm. velein neṣarûhüm leyüvellünne-l\'edbâr. ŝümme lâ yünṣarûn.'),
(5139, 59, 13, 'leentüm eşeddü rahbeten fî ṣudûrihim mine-llâh. ẕâlike biennehüm ḳavmül lâ yefḳahûn.'),
(5140, 59, 14, 'lâ yüḳâtilûneküm cemî`an illâ fî ḳuram müḥaṣṣanetin ev miv verâi cüdür. be\'sühüm beynehüm şedîd. taḥsebühüm cemî`av veḳulûbühüm şettâ. ẕâlike biennehüm ḳavmül lâ ya`ḳilûn.'),
(5141, 59, 15, 'kemeŝeli-lleẕîne min ḳablihim ḳarîben ẕâḳû vebâle emrihim. velehüm `aẕâbün elîm.'),
(5142, 59, 16, 'kemeŝeli-şşeyṭâni iẕ ḳâle lil\'insâni-kfür. felemmâ kefera ḳâle innî berîüm minke innî eḫâfü-llâhe rabbe-l`âlemîn.'),
(5143, 59, 17, 'fekâne `âḳibetehümâ ennehümâ fi-nnâri ḫâlideyni fîhâ. veẕâlike cezâu-żżâlimîn.'),
(5144, 59, 18, 'yâ eyyühe-lleẕîne âmenü-tteḳu-llâhe veltenżur nefsüm mâ ḳaddemet ligad. vetteḳu-llâh. inne-llâhe ḫabîrum bimâ ta`melûn.'),
(5145, 59, 19, 'velâ tekûnû kelleẕîne nesü-llâhe feensâhüm enfüsehüm. ülâike hümü-lfâsiḳûn.'),
(5146, 59, 20, 'lâ yestevî aṣḥâbü-nnâri veaṣḥâbü-lcenneh. aṣḥâbü-lcenneti hümü-lfâizûn.'),
(5147, 59, 21, 'lev enzelnâ hâẕe-lḳur\'âne `alâ cebelil lera\'eytehû ḫâşi`am müteṣaddi`am min ḫaşyeti-llâh. vetilke-l\'emŝâlü naḍribühâ linnâsi le`allehüm yetefekkerûn.'),
(5148, 59, 22, 'hüve-llâhü-lleẕî lâ ilâhe illâ hû. `âlimü-lgaybi veşşehâdeh. hüve-rraḥmânü-rraḥîm.'),
(5149, 59, 23, 'hüve-llâhü-lleẕî lâ ilâhe illâ hû. elmelikü-lḳuddûsü-sselâmü-lmü\'minü-lmüheyminü-l`azîzü-lcebbâru-lmütekebbir. sübḥâne-llâhi `ammâ yüşrikûn.'),
(5150, 59, 24, 'hüve-llâhü-lḫâliḳu-lbâriü-lmüṣavviru lehü-l\'esmâü-lḥusnâ. yüsebbiḥu lehû mâ fi-ssemâvâti vel\'arḍ. vehüve-l`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'yâ eyyühe-lleẕîne âmenû lâ tetteḫiẕû `adüvvî ve`adüvveküm evliyâe tülḳûne ileyhim bilmeveddeti veḳad keferû bimâ câeküm mine-lḥaḳḳ. yuḫricûne-rrasûle veiyyâküm en tü\'minû billâhi rabbiküm. in küntüm ḫaractüm cihâden fî sebîlî vebtigâe merḍâtî tüsirrûne ileyhim bilmeveddeh. veenâ a`lemü bimâ aḫfeytüm vemâ a`lentüm. vemey yef`alhü minküm feḳad ḍalle sevâe-ssebîl.'),
(5152, 60, 2, 'iy yeŝḳafûküm yekûnû leküm a`dâev veyebsüṭû ileyküm eydiyehüm veelsinetehüm bissûi veveddû lev tekfürûn.'),
(5153, 60, 3, 'len tenfe`aküm erḥâmüküm velâ evlâdüküm. yevme-lḳiyâmeh. yefṣilü beyneküm. vellâhü bimâ ta`melûne beṣîr.'),
(5154, 60, 4, 'ḳad kânet leküm üsvetün ḥasenetün fî ibrâhîme velleẕîne me`ah. iẕ ḳâlû liḳavmihim innâ büraâü minküm vemimmâ ta`büdûne min dûni-llâh. kefernâ biküm vebedâ beynenâ vebeynekümü-l`adâvetü vebagḍâü ebeden ḥattâ tü\'minû billâhi vaḥdehû illâ ḳavle ibrâhîme liebîhi leestagfiranne leke vemâ emlikü leke mine-llâhi min şey\'. rabbenâ `aleyke tevekkelnâ veileyke enebnâ veileyke-lmeṣîr.'),
(5155, 60, 5, 'rabbenâ lâ tec`alnâ fitnetel lilleẕîne keferû vagfir lenâ rabbenâ. inneke ente-l`azîzü-lḥakîm.'),
(5156, 60, 6, 'leḳad kâne leküm fîhim üsvetün ḥasenetül limen kâne yercü-llâhe velyevme-l\'âhir. vemey yetevelle feinne-llâhe hüve-lganiyyü-lḥamîd.'),
(5157, 60, 7, '`asa-llâhü ey yec`ale beyneküm vebeyne-lleẕîne `âdeytüm minhüm meveddeh. vellâhü ḳadîr. vellâhü gafûrur raḥîm.'),
(5158, 60, 8, 'lâ yenhâkümü-llâhü `ani-lleẕîne lem yüḳâtilûküm fi-ddîni velem yuḫricûküm min diyâriküm en teberrûhüm vetuḳsiṭû ileyhim. inne-llâhe yüḥibbü-lmuḳsiṭîn.'),
(5159, 60, 9, 'innemâ yenhâkümü-llâhü `ani-lleẕîne ḳâtelûküm fi-ddîni veaḫracûküm min diyâriküm veżâherû `alâ iḫrâciküm en tevellevhüm. vemey yetevellehüm feülâike hümu-żżâlimûn.'),
(5160, 60, 10, 'yâ eyyühe-lleẕîne âmenû iẕâ câekümü-lmü\'minâtü mühâcirâtin femteḥinûhünn. allâhü a`lemü biîmânihinn. fein `alimtümûhünne mü\'minâtin felâ terci`ûhünne ile-lküffâr. lâ hünne ḥillül lehüm velâ hüm yeḥillûne lehünn. veâtûhüm mâ enfeḳû. velâ cünâḥa `aleyküm en tenkiḥûhünne iẕâ âteytümûhünne ücûrahünn. velâ tümsikû bi`iṣami-lkevâfiri ves\'elû mâ enfaḳtüm velyes\'elû mâ enfeḳû. ẕâliküm ḥukmü-llâh. yaḥkümü beyneküm. vellâhü `alîmün ḥakîm.'),
(5161, 60, 11, 'vein fâteküm şey\'üm min ezvâciküm ile-lküffâri fe`âḳabtüm feâtü-lleẕîne ẕehebet ezvâcühüm miŝle mâ enfeḳû. vetteḳu-llâhe-lleẕî entüm bihî mü\'minûn.'),
(5162, 60, 12, 'yâ eyyühe-nnebiyyü iẕâ câeke-lmü\'minâtü yübâyi`neke `alâ el lâ yüşrikne billâhi şey\'ev velâ yesriḳne velâ yeznîne velâ yaḳtülne evlâdehünne velâ ye\'tîne bibühtâniy yefterînehû beyne eydîhinne veercülihinne velâ ya`ṣîneke fî ma`rûfin febâyi`hünne vestagfir lehünne-llâh. inne-llâhe gafûrur raḥîm.'),
(5163, 60, 13, 'yâ eyyühe-lleẕîne âmenû lâ tetevellev ḳavmen gaḍibe-llâhü `aleyhim ḳad yeisû mine-l\'âḫirati kemâ yeise-lküffâru min aṣḥâbi-lḳubûr.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'sebbeḥa lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. vehüve-l`azîzü-lḥakîm.'),
(5165, 61, 2, 'yâ eyyühe-lleẕîne âmenû lime teḳûlûne mâ lâ tef`alûn.'),
(5166, 61, 3, 'kebüra maḳten `inde-llâhi en teḳûlû mâ lâ tef`alûn.'),
(5167, 61, 4, 'inne-llâhe yüḥibbü-lleẕîne yüḳâtilûne fî sebîlihî ṣaffen keennehüm bünyânüm merṣûṣ.'),
(5168, 61, 5, 'veiẕ ḳâle mûsâ liḳavmihî yâ ḳavmi lime tü\'ẕûnenî veḳat ta`lemûne ennî rasûlü-llâhi ileyküm. felemmâ zâgû ezâga-llâhü ḳulûbehüm. vellâhü lâ yehdi-lḳavme-lfâsiḳîn.'),
(5169, 61, 6, 'veiẕ ḳâle `îse-bnü meryeme yâ benî isrâîle innî rasûlü-llâhi ileyküm müṣaddiḳal limâ beyne yedeyye mine-ttevrâti vemübeşşiram birasûliy ye\'tî mim ba`di-smühû aḥmed. felemmâ câehüm bilbeyyinâti ḳâlû hâẕâ siḥrum mübîn.'),
(5170, 61, 7, 'vemen ażlemü mimmeni-fterâ `ale-llâhi-lkeẕibe vehüve yüd`â ile-l\'islâm. vellâhü lâ yehdi-lḳavme-żżâlimîn.'),
(5171, 61, 8, 'yürîdûne liyuṭfiû nûra-llâhi biefvâhihim vellâhü mütimmü nûrihî velev kerihe-lkâfirûn.'),
(5172, 61, 9, 'hüve-lleẕî ersele rasûlehû bilhüdâ vedîni-lḥaḳḳi liyużhirahû `ale-ddîni küllihî velev kerihe-lmüşrikûn.'),
(5173, 61, 10, 'yâ eyyühe-lleẕîne âmenû hel edüllüküm `alâ ticâratin tüncîküm min `aẕâbin elîm.'),
(5174, 61, 11, 'tü\'minûne billâhi verasûlihî vetücâhidûne fî sebîli-llâhi biemvâliküm veenfüsiküm. ẕâliküm ḫayrul leküm in küntüm ta`lemûn.'),
(5175, 61, 12, 'yagfir leküm ẕünûbeküm veyüdḫilküm cennâtin tecrî min taḥtihe-l\'enhâru vemesâkine ṭayyibeten fî cennâti `adn. ẕâlike-lfevzü-l`ażîm.'),
(5176, 61, 13, 'veuḫrâ tüḥibbûnehâ. naṣrum mine-llâhi vefetḥun ḳarîb. vebeşşiri-lmü\'minîn.'),
(5177, 61, 14, 'yâ eyyühe-lleẕîne âmenû kûnû enṣâra-llâhi kemâ ḳâle `îse-bnü meryeme lilḥavâriyyîne men enṣârî ile-llâh. ḳâle-lḥavâriyyûne naḥnü enṣâru-llâhi feâmeneṭ ṭâifetüm mim benî isrâîle vekeferaṭ ṭâifeh. feeyyedne-lleẕîne âmenû `alâ `adüvvihim feaṣbeḥû żâhirîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'yüsebbiḥu lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍi-lmeliki-lḳuddûsi-l`azîzi-lḥakîm.'),
(5179, 62, 2, 'hüve-lleẕî be`aŝe fi-l\'ümmiyyîne rasûlem minhüm yetlû `aleyhim âyâtihî veyüzekkîhim veyü`allimühümü-lkitâbe velḥikmeh. vein kânû min ḳablü lefî ḍalâlim mübîn.'),
(5180, 62, 3, 'veâḫarîne minhüm lemmâ yelḥaḳû bihim. vehüve-l`azîzü-lḥakîm.'),
(5181, 62, 4, 'ẕâlike faḍlü-llâhi yü\'tîhi mey yeşâ\'. vellâhü ẕü-lfaḍli-l`ażîm.'),
(5182, 62, 5, 'meŝelü-lleẕîne ḥummilü-ttevrâte ŝümme lem yaḥmilûhâ kemeŝeli-lḥimâri yaḥmilü esfârâ. bi\'se meŝelü-lḳavmi-lleẕîne keẕẕebû biâyâti-llâh. vellâhü lâ yehdi-lḳavme-żżâlimîn.'),
(5183, 62, 6, 'ḳul yâ eyyühe-lleẕîne hâdû in za`amtüm enneküm evliyâü lillâhi min dûni-nnâsi fetemennevu-lmevte in küntüm ṣâdiḳîn.'),
(5184, 62, 7, 'velâ yetemennevnehû ebedem bimâ ḳaddemet eydîhim. vellâhü `alîmüm biżżâlimîn.'),
(5185, 62, 8, 'ḳul inne-lmevte-lleẕî tefirrûne minhü feinnehû mülâḳîküm ŝümme türaddûne ilâ `âlimi-lgaybi veşşehâdeti feyünebbiüküm bimâ küntüm ta`melûn.'),
(5186, 62, 9, 'yâ eyyühe-lleẕîne âmenû iẕâ nûdiye liṣṣalâti miy yevmi-lcümü`ati fes`av ilâ ẕikri-llâhi veẕerü-lbey`. ẕâliküm ḫayrul leküm in küntüm ta`lemûn.'),
(5187, 62, 10, 'feiẕâ ḳuḍiyeti-ṣṣâletü fenteşirû fi-l\'arḍi vebtegû min faḍli-llâhi veẕkürü-llâhe keŝîral le`alleküm tüfliḥûn.'),
(5188, 62, 11, 'veiẕâ raev ticâraten ev lehven-nfeḍḍû ileyhâ veterakûke ḳâimâ. ḳul mâ `inde-llâhi ḫayrum mine-llehvi vemine-tticârah. vellâhü ḫayru-rrâziḳîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'iẕâ câeke-lmünâfiḳûne ḳâlû neşhedü inneke lerasûlü-llâh. vellâhü ya`lemü inneke lerasûlüh. vellâhü yeşhedü inne-lmünâfiḳîne lekâẕibûn.'),
(5190, 63, 2, 'itteḫaẕû eymânehüm cünneten feṣaddû `an sebîli-llâh. innehüm sâe mâ kânû ya`melûn.'),
(5191, 63, 3, 'ẕâlike biennehüm âmenû ŝümme keferû feṭubi`a `alâ ḳulûbihim fehüm lâ yefḳahûn.'),
(5192, 63, 4, 'veiẕâ raeytehüm tü`cibüke ecsâmühüm. veiy yeḳûlû tesma` liḳavlihim. keennehüm ḫuşübüm müsennedeh. yaḥsebûne külle ṣayḥatin `aleyhim. hümü-l`adüvvu faḥẕerhüm. ḳâtelehümü-llâh. ennâ yü\'fekûn.'),
(5193, 63, 5, 'veiẕâ ḳîle lehüm te`âlev yestagfir leküm rasûlü-llâhi levvev ruûsehüm veraeytehüm yeṣuddûne vehüm müstekbirûn.'),
(5194, 63, 6, 'sevâün `aleyhim estagferte lehüm em lem testagfir lehüm. ley yagfira-llâhü lehüm. inne-llâhe lâ yehdi-lḳavme-lfâsiḳîn.'),
(5195, 63, 7, 'hümü-lleẕîne yeḳûlûne lâ tünfiḳû `alâ men `inde rasûli-llâhi ḥattâ yenfeḍḍû. velillâhi ḫazâinü-ssemâvâti vel\'arḍi velâkinne-lmünâfiḳîne lâ yefḳahûn.'),
(5196, 63, 8, 'yeḳûlûne leir raca`nâ ile-lmedîneti leyuḫricenne-l\'e`azzü minhe-l\'eẕell. velillâhi-l`izzetü velirasûlihî velilmü\'minîne velâkinne-lmünâfiḳîne lâ ya`lemûn.'),
(5197, 63, 9, 'yâ eyyühe-lleẕîne âmenû lâ tülhiküm emvâlüküm velâ evlâdüküm `an ẕikri-llâh. vemey yef`al ẕâlike feülâike hümü-lḫâsirûn.'),
(5198, 63, 10, 'veenfiḳû mimmâ razaḳnâküm min ḳabli ey ye\'tiye eḥadekümü-lmevtü feyeḳûle rabbi levlâ eḫḫartenî ilâ ecelin ḳarîbin feeṣṣaddeḳa veeküm mine-ṣṣâliḥîn.'),
(5199, 63, 11, 'veley yü\'eḫḫira-llâhü nefsen iẕâ câe ecelühâ. vellâhü ḫabîrum bimâ ta`melûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'yüsebbiḥu lillâhi mâ fi-ssemâvâti vemâ fi-l\'arḍ. lehü-lmülkü velehü-lḥamd. vehüve `alâ külli şey\'in ḳadîr.'),
(5201, 64, 2, 'hüve-lleẕî ḫaleḳaküm feminküm kâfiruv veminküm mü\'min. vellâhü bimâ ta`melûne beṣîr.'),
(5202, 64, 3, 'ḫaleḳa-ssemâvâti vel\'arḍa bilḥaḳḳi veṣavveraküm feaḥsene ṣuveraküm. veileyhi-lmeṣîr.'),
(5203, 64, 4, 'ya`lemü mâ fi-ssemâvâti vel\'arḍi veya`lemü mâ tüsirrûne vemâ tü`linûn. vellâhü `alîmüm biẕâti-ṣṣudûr.'),
(5204, 64, 5, 'elem ye\'tiküm nebeü-lleẕîne keferû min ḳabl. feẕâḳû vebâle emrihim velehüm `aẕâbün elîm.'),
(5205, 64, 6, 'ẕâlike biennehû kânet te\'tîhim rusülühüm bilbeyyinâti feḳâlû ebeşeruy yehdûnenâ. fekeferû vetevellev vestagne-llâh. vellâhü ganiyyün ḥamîd.'),
(5206, 64, 7, 'za`ame-lleẕîne keferû el ley yüb`aŝû. ḳul belâ verabbî letüb`aŝünne ŝümme letünebbeünne bimâ `amiltüm. veẕâlike `ale-llâhi yesîr.'),
(5207, 64, 8, 'feâminû billâhi verasûlihî vennûri-lleẕî enzelnâ. vellâhü bimâ ta`melûne ḫabîr.'),
(5208, 64, 9, 'yevme yecme`uküm liyevmi-lcem`i ẕâlike yevmü-ttegâbün. vemey yü\'mim billâhi veya`mel ṣâliḥay yükeffir `anhü seyyiâtihî veyüdḫilhü cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. ẕâlike-lfevzü-l`ażîm.'),
(5209, 64, 10, 'velleẕîne keferû vekeẕẕebû biâyâtinâ ülâike aṣḥâbü-nnâri ḫâlidîne fîhâ. vebi\'se-lmeṣîr.'),
(5210, 64, 11, 'mâ eṣâbe mim müṣîbetin illâ biiẕni-llâh. vemey yü\'mim billâhi yehdi ḳalbeh. vellâhü bikülli şey\'in `alîm.'),
(5211, 64, 12, 'veeṭî`ü-llâhe veeṭî`ü-rrasûl. fein tevelleytüm feinnemâ `alâ rasûline-lbelâgu-lmübîn.'),
(5212, 64, 13, 'allâhü lâ ilâhe illâ hû. ve`ale-llâhi felyetevekkeli-lmü\'minûn.'),
(5213, 64, 14, 'yâ eyyühe-lleẕîne âmenû inne min ezvâciküm veevlâdiküm `adüvvel leküm faḥẕerûhüm. vein ta`fû vetaṣfeḥû vetagfirû feinne-llâhe gafûrur raḥîm.'),
(5214, 64, 15, 'innemâ emvâlüküm veevlâdüküm fitnetün. vellâhü `indehû ecrun `ażîm.'),
(5215, 64, 16, 'fetteḳu-llâhe me-steṭa`tüm vesme`û veeṭî`û veenfiḳû ḫayral lienfüsiküm. vemey yûḳa şüḥḥa nefsihî feülâike hümü-lmüfliḥûn.'),
(5216, 64, 17, 'in tuḳriḍü-llâhe ḳarḍan ḥaseney yüḍâ`ifhü leküm veyagfir leküm. vellâhü şekûrun ḥalîm.'),
(5217, 64, 18, '`âlimü-lgaybi veşşehâdeti-l`azîzü-lḥakîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'yâ eyyühe-nnebiyyü iẕâ ṭallaḳtümü-nnisâe feṭalliḳûhünne li`iddetihinne veaḥṣü-l`iddeh. vetteḳu-llâhe rabbeküm. lâ tuḫricûhünne mim büyûtihinne velâ yaḫrucne illâ ey ye\'tîne bifâḥişetim mübeyyineh. vetilke ḥudûdü-llâh. vemey yete`adde ḥudûde-llâhi feḳad żaleme nefseh. lâ tedrî le`alle-llâhe yuḥdiŝü ba`de ẕâlike emrâ.'),
(5219, 65, 2, 'feiẕâ belagne ecelehünne feemsikûhünne bima`rûfin ev fâriḳûhünne bima`rûfiv veeşhidû ẕevey `adlim minküm veeḳîmü-şşehâdete lillâh. ẕâliküm yû`ażu bihî men kâne yü\'minü billâhi velyevmi-l\'âḫir. vemey yetteḳi-llâhe yec`al lehû maḫracâ.'),
(5220, 65, 3, 'veyerzuḳhü min ḥayŝü lâ yaḥtesib. vemey yetevekkel `ale-llâhi fehüve ḥasbüh. inne-llâhe bâligu emrih. ḳad ce`ale-llâhü likülli şey\'in ḳadrâ.'),
(5221, 65, 4, 'vellâî yeisne mine-lmeḥîḍi min nisâiküm ini-rtebtüm fe`iddetühünne ŝelâŝetü eşhüriv vellâî lem yeḥiḍn. veülâtü-l\'aḥmâli ecelühünne ey yeḍa`ne ḥamlehünn. vemey yetteḳi-llâhe yec`al lehû min emrihî yüsrâ.'),
(5222, 65, 5, 'ẕâlike emru-llâhi enzelehû ileyküm. vemey yetteḳi-llâhe yükeffir `anhü seyyiâtihî veyü`żim lehû ecrâ.'),
(5223, 65, 6, 'eskinûhünne min ḥayŝü sekentüm miv vucdiküm velâ tüḍârrûhünne litüḍayyiḳû `aleyhinn. vein künne ülâti ḥamlin feenfiḳû `aleyhinne ḥattâ yeḍa`ne ḥamlehünn. fein erḍa`ne leküm feâtûhünne ücûrahünn. ve\'temirû beyneküm bima`rûf. vein te`âsertüm fesetürḍi`u lehû uḫrâ.'),
(5224, 65, 7, 'liyünfiḳ ẕû se`atim min se`atih. vemen ḳudira `aleyhi rizḳuhû felyünfiḳ mimmâ âtâhü-llâh. lâ yükellifü-llâhü nefsen illâ mâ âtâhâ. seyec`alü-llâhü ba`de `usriy yüsrâ.'),
(5225, 65, 8, 'vekeeyyim min ḳaryetin `atet `an emri rabbihâ verusülihî feḥâsebnâhâ ḥisâben şedîdev ve`aẕẕebnâhâ `aẕâben nükrâ.'),
(5226, 65, 9, 'feẕâḳat vebâle emrihâ vekâne `âḳibetü emrihâ ḫusrâ.'),
(5227, 65, 10, 'e`adde-llâhü lehüm `aẕâben şedîden fetteḳu-llâhe yâ ûli-l\'elbâb. elleẕîne âmenû. ḳad enzele-llâhü ileyküm ẕikrâ.'),
(5228, 65, 11, 'rasûley yetlû `aleyküm âyâti-llâhi mübeyyinâtil liyuḫrice-lleẕîne âmenû ve`amilu-ṣṣâliḥâti mine-żżulümâti ile-nnûr. vemey yü\'mim billâhi veya`mel ṣâliḥay yüdḫilhü cennâtin tecrî min taḥtihe-l\'enhâru ḫâlidîne fîhâ ebedâ. ḳad aḥsene-llâhü lehû rizḳâ.'),
(5229, 65, 12, 'allâhü-lleẕî ḫaleḳa seb`a semâvâtiv vemine-l\'arḍi miŝlehünn. yetenezzelü-l\'emru beynehünne lita`lemû enne-llâhe `alâ külli şey\'in ḳadîruv veenne-llâhe ḳad eḥâṭa bikülli şey\'in `ilmâ.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'yâ eyyühe-nnebiyyü lime tüḥarrimü mâ eḥalle-llâhü leke. tebtegî merḍâte ezvâcik. vellâhü gafûrur raḥîm.'),
(5231, 66, 2, 'ḳad feraḍa-llâhü leküm teḥillete eymâniküm. vellâhü mevlâküm. vehüve-l`alîmü-lḥakîm.'),
(5232, 66, 3, 'veiẕ eserra-nnebiyyü ilâ ba`ḍi ezvâcihî ḥadîŝâ. felemmâ nebbeet bihî veażherahü-llâhü `aleyhi `arrafe ba`ḍahû vea`raḍa `am ba`ḍ. felemmâ nebbeehâ bihî ḳâlet men embeeke hâẕâ. ḳâle nebbeeniye-l`alîmü-lḫabîr.'),
(5233, 66, 4, 'in tetûbâ ile-llâhi feḳad ṣagat ḳulûbükümâ. vein teżâherâ `aleyhi feinne-llâhe hüve mevlâhü vecibrîlü veṣâliḥu-lmü\'minîn. velmelâiketü ba`de ẕâlike żahîr.'),
(5234, 66, 5, '`asâ rabbühû in ṭalleḳakünne ey yübdilehû ezvâcen ḫayram minkünne müslimâtim mü\'minâtin ḳânitâtin tâibâtin `âbidâtin sâiḥâtin ŝeyyibâtiv veebkârâ.'),
(5235, 66, 6, 'yâ eyyühe-lleẕîne âmenû ḳû enfüseküm veehlîküm nârav veḳûdühe-nnâsü velḥicâratü `aleyhâ melâiketün gilâżun şidâdül lâ ya`ṣûne-llâhe mâ emerahüm veyef`alûne mâ yü\'merûn.'),
(5236, 66, 7, 'yâ eyyühe-lleẕîne keferû lâ ta`teẕirü-lyevm. innemâ tüczevne mâ küntüm ta`melûn.'),
(5237, 66, 8, 'yâ eyyühe-lleẕîne âmenû tûbû ile-llâhi tevbeten neṣûḥâ. `asâ rabbüküm ey yükeffira `anküm seyyiâtiküm veyüdḫileküm cennâtin tecrî min taḥtihe-l\'enhâru yevme lâ yuḫzi-llâhü-nnebiyye velleẕîne âmenû me`ah. nûruhüm yes`â beyne eydîhim vebieymânihim yeḳûlûne rabbenâ etmim lenâ nûranâ vagfir lenâ. inneke `alâ külli şey\'in ḳadîr.'),
(5238, 66, 9, 'yâ eyyühe-nnebiyyü câhidi-lküffâra velmünâfiḳîne vagluż `aleyhim. veme\'vâhüm cehennem. vebi\'se-lmeṣîr.'),
(5239, 66, 10, 'ḍarabe-llâhü meŝelel lilleẕîne keferü-mraete nûḥiv vemraete lûṭ. kânetâ taḥte `abdeyni min `ibâdinâ ṣâliḥayni feḫânetâhümâ felem yugniyâ `anhümâ mine-llâhi şey\'ev veḳîle-dḫule-nnâra me`a-ddâḫilîn.'),
(5240, 66, 11, 'veḍarabe-llâhü meŝelel lilleẕîne âmenü-mraete fir`avn. iẕ ḳâlet rabbi-bni lî `indeke beyten fi-lcenneti veneccinî min fir`avne ve`amelihî veneccinî mine-lḳavmi-żżâlimîn.'),
(5241, 66, 12, 'vemeryeme-bnete `imrâne-lletî aḥṣanet fercehâ fenefaḫnâ fîhi mir rûḥinâ veṣaddeḳat bikelimâti rabbihâ vekütübihî vekânet mine-lḳânitîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'tebârake-lleẕî biyedihi-lmülk. vehüve `alâ külli şey\'in ḳadîr.'),
(5243, 67, 2, 'elleẕî ḫaleḳa-lmevte velḥayâte liyeblüveküm eyyüküm aḥsenü `amelâ. vehüve-l`azîzü-lgafûr.'),
(5244, 67, 3, 'elleẕî ḫaleḳa seb`a semâvâtin ṭibâḳâ. mâ terâ fî ḫalḳi-rraḥmâni min tefâvüt. ferci`i-lbeṣara hel terâ min füṭûr.'),
(5245, 67, 4, 'ŝümme-rci`i-lbeṣara kerrateyni yenḳalib ileyke-lbeṣaru ḫâsiev vehüve ḥasîr.'),
(5246, 67, 5, 'veleḳad zeyyenne-ssemâe-ddünyâ bimeṣâbîḥa vece`alnâhâ rucûmel lişşeyâṭîni vea`tednâ lehüm `aẕâbe-sse`îr.'),
(5247, 67, 6, 'velilleẕîne keferû birabbihim `aẕâbü cehennem. vebi\'se-lmeṣîr.'),
(5248, 67, 7, 'iẕâ ülḳû fîhâ semi`û lehâ şehîḳav vehiye tefûr.'),
(5249, 67, 8, 'tekâdü temeyyezü mine-lgayż. küllemâ ülḳiye fîhâ fevcün seelehüm ḫazenetühâ elem ye\'tiküm neẕîr.'),
(5250, 67, 9, 'ḳâlû belâ ḳad câenâ neẕîrun fekeẕẕebnâ veḳulnâ mâ nezzele-llâhü min şey\'. in entüm illâ fî ḍalâlin kebîr.'),
(5251, 67, 10, 'veḳâlû lev künnâ nesme`u ev na`ḳilü mâ künnâ fî aṣḥâbi-sse`îr.'),
(5252, 67, 11, 'fa`terafû biẕembihim. fesuḥḳal liaṣḥâbi-sse`îr.'),
(5253, 67, 12, 'inne-lleẕîne yaḫşevne rabbehüm bilgaybi lehüm magfiratüv veecrun kebîr.'),
(5254, 67, 13, 'veesirrû ḳavleküm evi-cherû bih. innehû `alîmüm biẕâti-ṣṣudûr.'),
(5255, 67, 14, 'elâ ya`lemü men ḫaleḳ. vehüve-lleṭîfü-lḫabîr.'),
(5256, 67, 15, 'hüve-lleẕî ce`ale lekümü-l\'arḍa ẕelûlen femşû fî menâkibihâ vekülû mir rizḳih. veileyhi-nnüşûr.'),
(5257, 67, 16, 'eemintüm men fi-ssemâi ey yaḫsife bikümü-l\'arḍa feiẕâ hiye temûr.'),
(5258, 67, 17, 'em emintüm men fi-ssemâi ey yürsile `aleyküm ḥâṣibâ. feseta`lemûne keyfe neẕîr.'),
(5259, 67, 18, 'veleḳad keẕẕebe-lleẕîne min ḳablihim fekeyfe kâne nekîr.'),
(5260, 67, 19, 'evelem yerav ile-ṭṭayri fevḳahüm ṣâffâtiv veyaḳbiḍn. mâ yümsikühünne ille-rraḥmân. innehû bikülli şey\'im beṣîr.'),
(5261, 67, 20, 'emmen hâẕe-lleẕî hüve cündül leküm yenṣuruküm min dûni-rraḥmân. ini-lkâfirûne illâ fî gurûr.'),
(5262, 67, 21, 'emmen hâẕe-lleẕî yerzüḳuküm in emseke rizḳah. bel leccû fî `utüvviv venüfûr.'),
(5263, 67, 22, 'efemey yemşî mükibben `alâ vechihî ehdâ emmey yemşî seviyyen `alâ ṣirâṭim müsteḳîm.'),
(5264, 67, 23, 'ḳul hüve-lleẕî enşeeküm vece`ale lekümü-ssem`a vel\'ebṣâra vel\'ef\'ideh. ḳalîlem mâ teşkürûn.'),
(5265, 67, 24, 'ḳul hüve-lleẕî ẕera\'eküm fi-l\'arḍi veileyhi tuḥşerûn.'),
(5266, 67, 25, 'veyeḳûlûne metâ hâẕe-lva`dü in küntüm ṣâdiḳîn.'),
(5267, 67, 26, 'ḳul inneme-l`ilmü `inde-llâh. veinnemâ ene neẕîrum mübîn.'),
(5268, 67, 27, 'felemmâ raevhü zülfeten sîet vucûhü-lleẕîne keferû veḳîle hâẕe-lleẕî küntüm bihî tedde`ûn.'),
(5269, 67, 28, 'ḳul era\'eytüm in ehlekeniye-llâhü vemem me`iye ev raḥimenâ femey yücîru-lkâfirîne min `aẕâbin elîm.'),
(5270, 67, 29, 'ḳul hüve-rraḥmânü âmennâ bihî ve`aleyhi tevekkelnâ. feseta`lemûne men hüve fî ḍalâlim mübîn.'),
(5271, 67, 30, 'ḳul era\'eytüm in aṣbeḥa mâüküm gavran femey ye\'tîküm bimâim me`în.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'nûn. velḳalemi vemâ yesṭurûn.'),
(5273, 68, 2, 'mâ ente bini`meti rabbike bimecnûn.'),
(5274, 68, 3, 'veinne leke leecran gayra memnûn.'),
(5275, 68, 4, 'veinneke le`alâ ḫulüḳin `ażîm.'),
(5276, 68, 5, 'fesetübṣiru veyübṣirûn.'),
(5277, 68, 6, 'bieyyikümü-lmeftûn.'),
(5278, 68, 7, 'inne rabbeke hüve a`lemü bimen ḍalle `an sebîlih. vehüve a`lemü bilmühtedîn.'),
(5279, 68, 8, 'felâ tüṭi`i-lmükeẕẕibîn.'),
(5280, 68, 9, 'veddû lev tüdhinü feyüdhinûn.'),
(5281, 68, 10, 'velâ tüṭi` külle ḥallâfim mehîn.'),
(5282, 68, 11, 'hemmâzim meşşâim binemîm.'),
(5283, 68, 12, 'mennâ`il lilḫayri mü`tedin eŝîm.'),
(5284, 68, 13, '`utüllim ba`de ẕâlike zenîm.'),
(5285, 68, 14, 'en kâne ẕâ mâliv vebenîn.'),
(5286, 68, 15, 'iẕâ tütlâ `aleyhi âyâtünâ ḳâle esâṭîru-l\'evvelîn.'),
(5287, 68, 16, 'senesimühû `ale-lḫurṭûm.'),
(5288, 68, 17, 'innâ belevnâhüm kemâ belevnâ aṣḥâbe-lcenneh. iẕ aḳsemû leyaṣrimünnehâ muṣbiḥîn.'),
(5289, 68, 18, 'velâ yesteŝnûn.'),
(5290, 68, 19, 'feṭâfe `aleyhâ ṭâifüm mir rabbike vehüm nâimûn.'),
(5291, 68, 20, 'feaṣbeḥat keṣṣarîm.'),
(5292, 68, 21, 'fetenâdev muṣbiḥîn.'),
(5293, 68, 22, 'eni-gdû `alâ ḥarŝiküm in küntüm ṣârimîn.'),
(5294, 68, 23, 'fenṭaleḳû vehüm yeteḫâfetûn.'),
(5295, 68, 24, 'el lâ yedḫulennehe-lyevme `aleyküm miskîn.'),
(5296, 68, 25, 'vegadev `alâ ḥardin ḳâdirîn.'),
(5297, 68, 26, 'felemmâ raevhâ ḳâlû innâ leḍâllûn.'),
(5298, 68, 27, 'bel naḥnü maḥrûmûn.'),
(5299, 68, 28, 'ḳâle evseṭuhüm elem eḳul leküm levlâ tüsebbiḥûn.'),
(5300, 68, 29, 'ḳâlû sübḥâne rabbinâ innâ künnâ żâlimîn.'),
(5301, 68, 30, 'feaḳbele ba`ḍuhüm `alâ ba`ḍiy yetelâvemûn.'),
(5302, 68, 31, 'ḳâlû yâ veylenâ innâ künnâ ṭâgîn.'),
(5303, 68, 32, '`asâ rabbünâ ey yübdilenâ ḫayram minhâ innâ ilâ rabbinâ râgibûn.'),
(5304, 68, 33, 'keẕâlike-l`aẕâb. vele`aẕâbü-l\'âḫirati ekber. lev kânû ya`lemûn.'),
(5305, 68, 34, 'inne lilmütteḳîne `inde rabbihim cennâti-nne`îm.'),
(5306, 68, 35, 'efenec`alü-lmüslimîne kelmücrimîn.'),
(5307, 68, 36, 'mâ leküm. keyfe taḥkümûn.'),
(5308, 68, 37, 'em leküm kitâbün fîhi tedrusûn.'),
(5309, 68, 38, 'inne leküm fîhi lemâ teḫayyerûn.'),
(5310, 68, 39, 'em leküm eymânün `aleynâ bâligatün ilâ yevmi-lḳiyâmeti inne leküm lemâ taḥkümûn.'),
(5311, 68, 40, 'selhüm eyyühüm biẕâlike za`îm.'),
(5312, 68, 41, 'em lehüm şürakâ\'. felye\'tû bişürakâihim in kânû ṣâdiḳîn.'),
(5313, 68, 42, 'yevme yükşefü `an sâḳiv veyüd`avne ile-ssücûdi felâ yesteṭî`ûn.'),
(5314, 68, 43, 'ḫâşi`aten ebṣâruhüm terheḳuhüm ẕilleh. veḳad kânû yüd`avne ile-ssücûdi vehüm sâlimûn.'),
(5315, 68, 44, 'feẕernî vemey yükeẕẕibü bihâẕe-lḥadîŝ. senestedricühüm min ḥayŝü lâ ya`lemûn.'),
(5316, 68, 45, 'veümlî lehüm. inne keydî metîn.'),
(5317, 68, 46, 'em tes\'elühüm ecran fehüm mim magramim müŝḳalûn.'),
(5318, 68, 47, 'em `indehümü-lgaybü fehüm yektübûn.'),
(5319, 68, 48, 'faṣbir liḥukmi rabbike velâ tekün keṣâḥibi-lḥût. iẕ nâdâ vehüve mekżûm.'),
(5320, 68, 49, 'levlâ en tedârakehû ni`metüm mir rabbihî lenübiẕe bil`arâi vehüve meẕmûm.'),
(5321, 68, 50, 'fectebâhü rabbühû fece`alehû mine-ṣṣâliḥîn.'),
(5322, 68, 51, 'veiy yekâdü-lleẕîne keferû leyüzliḳûneke biebṣârihim lemmâ semi`ü-ẕẕikra veyeḳûlûne innehû lemecnûn.'),
(5323, 68, 52, 'vemâ hüve illâ ẕikrul lil`âlemîn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'elḥâḳḳah.'),
(5325, 69, 2, 'me-lḥâḳḳah.'),
(5326, 69, 3, 'vemâ edrâke me-lḥâḳḳah.'),
(5327, 69, 4, 'keẕẕebet ŝemûdü ve`âdüm bilḳâri`ah.'),
(5328, 69, 5, 'feemmâ ŝemûdü feühlikû biṭṭâgiyeh.'),
(5329, 69, 6, 'veemmâ `âdün feühlikû birîḥin ṣarṣarin `âtiyeh.'),
(5330, 69, 7, 'seḫḫarahâ `aleyhim seb`a leyâliv veŝemâniyete eyyâmin ḥusûmen fetere-lḳavme fîhâ ṣar`â keennehüm a`câzü naḫlin ḫâviyeh.'),
(5331, 69, 8, 'fehel terâ lehüm mim bâḳiyeh.'),
(5332, 69, 9, 'vecâe fir`avnü vemen ḳablehû velmü\'tefikâtü bilḫâṭieh.'),
(5333, 69, 10, 'fe`aṣav rasûle rabbihim feeḫaẕehüm aḫẕeter râbiyetâ.'),
(5334, 69, 11, 'innâ lemmâ ṭaga-lmâü ḥamelnâküm fi-lcâriyeh.'),
(5335, 69, 12, 'linec`alehâ leküm teẕkiratev vete`iyehâ üẕünüv vâ`iyeh.'),
(5336, 69, 13, 'feiẕâ nüfiḫa fi-ṣṣûri nefḫatüv vâḥideh.'),
(5337, 69, 14, 'veḥumileti-l\'arḍu velcibâlü fedükketâ dekketev vâḥidetâ.'),
(5338, 69, 15, 'feyevmeiẕiv veḳa`ati-lvâḳi`ah.'),
(5339, 69, 16, 'venşeḳḳati-ssemâü fehiye yevmeiẕiv vâhiyeh.'),
(5340, 69, 17, 'velmelekü `alâ ercâihâ. veyaḥmilü `arşe rabbike fevḳahüm yevmeiẕin ŝemâniyeh.'),
(5341, 69, 18, 'yevmeiẕin tü`raḍûne lâ taḫfâ minküm ḫâfiyeh.'),
(5342, 69, 19, 'feemmâ men ûtiye kitâbehû biyemînihî feyeḳûlü hâümu-ḳraû kitâbiyeh.'),
(5343, 69, 20, 'innî żanentü ennî mülâḳin ḥisâbiyeh.'),
(5344, 69, 21, 'fehüve fî `îşetir râḍiyeh.'),
(5345, 69, 22, 'fî cennetin `âliyeh.'),
(5346, 69, 23, 'ḳuṭûfühâ dâniyeh.'),
(5347, 69, 24, 'külû veşrabû henîem bimâ esleftüm fi-l\'eyyâmi-lḫâliyeh.'),
(5348, 69, 25, 'veemmâ men ûtiye kitâbehû bişimâlihî feyeḳûlü yâ leytenî lem ûte kitâbiyeh.'),
(5349, 69, 26, 'velem edri mâ ḥisâbiyeh.'),
(5350, 69, 27, 'yâ leytehâ kâneti-lḳâḍiyeh.'),
(5351, 69, 28, 'mâ agnâ `annî mâliyeh.'),
(5352, 69, 29, 'heleke `annî sülṭâniyeh.'),
(5353, 69, 30, 'ḫuẕûhü fegullûh.'),
(5354, 69, 31, 'ŝümme-lceḥîme ṣallûh.'),
(5355, 69, 32, 'ŝümme fî silsiletin ẕer`uhâ seb`ûne ẕirâ`an feslükûh.'),
(5356, 69, 33, 'innehû kâne lâ yü\'minü billâhi-l`ażîm.'),
(5357, 69, 34, 'velâ yeḥuḍḍu `alâ ṭa`âmi-lmiskîn.'),
(5358, 69, 35, 'feleyse lehü-lyevme hâhünâ ḥamîm.'),
(5359, 69, 36, 'velâ ṭa`âmün illâ min gislîn.'),
(5360, 69, 37, 'lâ ye\'külühû ille-lḫâṭiûn.'),
(5361, 69, 38, 'felâ uḳsimü bimâ tübṣirûn.'),
(5362, 69, 39, 'vemâ lâ tübṣirûn.'),
(5363, 69, 40, 'innehû leḳavlü rasûlin kerîm.'),
(5364, 69, 41, 'vemâ hüve biḳavli şâ`ir. ḳalîlem mâ tü\'minûn.'),
(5365, 69, 42, 'velâ biḳavli kâhin. ḳalîlem mâ teẕekkerûn.'),
(5366, 69, 43, 'tenzîlüm mir rabbi-l`âlemîn.'),
(5367, 69, 44, 'velev teḳavvele `aleynâ ba`ḍa-l\'eḳâvîl.'),
(5368, 69, 45, 'leeḫaẕnâ minhü bilyemîn.'),
(5369, 69, 46, 'ŝümme leḳaṭa`nâ minhü-lvetîn.'),
(5370, 69, 47, 'femâ minküm min eḥadin `anhü ḥâcizîn.'),
(5371, 69, 48, 'veinnehû leteẕkiratül lilmütteḳîn.'),
(5372, 69, 49, 'veinnâ lena`lemü enne minküm mükeẕẕibîn.'),
(5373, 69, 50, 'veinnehû leḥasratün `ale-lkâfirîn.'),
(5374, 69, 51, 'veinnehû leḥaḳḳu-lyeḳîn.'),
(5375, 69, 52, 'fesebbiḥ bismi rabbike-l`ażîm.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'seele sâilüm bi`aẕâbiv vâḳi`.'),
(5377, 70, 2, 'lilkâfirîne leyse lehû dâfi`.'),
(5378, 70, 3, 'mine-llâhi ẕi-lme`âric.'),
(5379, 70, 4, 'ta`rucü-lmelâiketü verrûḥu ileyhi fî yevmin kâne miḳdâruhû ḫamsîne elfe seneh.'),
(5380, 70, 5, 'faṣbir ṣabran cemîlâ.'),
(5381, 70, 6, 'innehüm yeravnehû be`îdâ.'),
(5382, 70, 7, 'venerâhü ḳarîbâ.'),
(5383, 70, 8, 'yevme tekûnü-ssemâü kelmühl.'),
(5384, 70, 9, 'vetekûnü-lcibâlü kel`ihn.'),
(5385, 70, 10, 'velâ yes\'elü ḥamîmün ḥamîmâ.'),
(5386, 70, 11, 'yübeṣṣarûnehüm. yeveddü-lmücrimü lev yeftedî min `aẕâbi yevmiiẕim bibenîh.'),
(5387, 70, 12, 'veṣâḥibetihî veeḫîh.'),
(5388, 70, 13, 'vefeṣîletihi-lletî tü\'vîh.'),
(5389, 70, 14, 'vemen fi-l\'arḍi cemî`an ŝümme yüncîh.'),
(5390, 70, 15, 'kellâ. innehâ leżâ.'),
(5391, 70, 16, 'nezzâ`atel lişşevâ.'),
(5392, 70, 17, 'ted`û men edbera vetevellâ.'),
(5393, 70, 18, 'veceme`a feev`â.'),
(5394, 70, 19, 'inne-l\'insâne ḫuliḳa helû`â.'),
(5395, 70, 20, 'iẕâ messehü-şşerru cezû`â.'),
(5396, 70, 21, 'veiẕâ messehü-lḫayru menû`â.'),
(5397, 70, 22, 'ille-lmüṣallîn.'),
(5398, 70, 23, 'elleẕîne hüm `alâ ṣalâtihim dâimûn.'),
(5399, 70, 24, 'velleẕîne fî emvâlihim ḥaḳḳum ma`lûm.'),
(5400, 70, 25, 'lissâili velmaḥrûm.'),
(5401, 70, 26, 'velleẕîne yüṣaddiḳûne biyevmi-ddîn.'),
(5402, 70, 27, 'velleẕîne hüm min `aẕâbi rabbihim müşfiḳûn.'),
(5403, 70, 28, 'inne `aẕâbe rabbihim gayru me\'mûn.'),
(5404, 70, 29, 'velleẕîne hüm lifürûcihim ḥâfiżûn.'),
(5405, 70, 30, 'illâ `alâ ezvâcihim ev mâ meleket eymânühüm feinnehüm gayru melûmîn.'),
(5406, 70, 31, 'femeni-btegâ verâe ẕâlike feülâike hümü-l`âdûn.'),
(5407, 70, 32, 'velleẕîne hüm liemânâtihim ve`ahdihim râ`ûn.'),
(5408, 70, 33, 'velleẕîne hüm bişehâdetihim ḳâimûn.'),
(5409, 70, 34, 'velleẕîne hüm `alâ ṣalâtihim yüḥâfiżûn.'),
(5410, 70, 35, 'ülâike fî cennâtim mükramûn.'),
(5411, 70, 36, 'femâ lilleẕîne keferû ḳibeleke mühti`în.'),
(5412, 70, 37, '`ani-lyemîni ve`ani-şşimâli `izîn.'),
(5413, 70, 38, 'eyaṭme`u küllü-mriim minhüm ey yüdḫale cennete ne`îm.'),
(5414, 70, 39, 'kellâ. innâ ḫalaḳnâhüm mimmâ ya`lemûn.'),
(5415, 70, 40, 'felâ uḳsimü birabbi-lmeşâriḳi velmegâribi innâ leḳâdirûn.'),
(5416, 70, 41, '`alâ en nübeddile ḫayram minhüm vemâ naḥnü bimesbûḳîn.'),
(5417, 70, 42, 'feẕerhüm yeḫûḍû veyel`abû ḥattâ yülâḳû yevmehümü-lleẕî yû`adûn.'),
(5418, 70, 43, 'yevme yaḫrucûne mine-l\'ecdâŝi sirâ`an keennehüm ilâ nüṣubiy yûfiḍûn.'),
(5419, 70, 44, 'ḫâşi`aten ebṣâruhüm terheḳuhüm ẕilleh. ẕâlike-lyevmü-lleẕî kânû yû`adûn.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'innâ erselnâ nûḥan ilâ ḳavmihî en enẕir ḳavmeke min ḳabli ey ye\'tiyehüm `aẕâbün elîm.'),
(5421, 71, 2, 'ḳâle yâ ḳavmi innî leküm neẕîrum mübîn.'),
(5422, 71, 3, 'eni-`büdü-llâhe vetteḳûhü veeṭî`ûn.'),
(5423, 71, 4, 'yagfir leküm min ẕünûbiküm veyüeḫḫirküm ilâ ecelim müsemmâ. inne ecele-llâhi iẕâ câe lâ yü\'eḫḫar. lev küntüm ta`lemûn.'),
(5424, 71, 5, 'ḳâle rabbi innî de`avtü ḳavmî leylev venehârâ.'),
(5425, 71, 6, 'felem yezidhüm dü`âî illâ firârâ.'),
(5426, 71, 7, 'veinnî küllemâ de`avtühüm litagfira lehüm ce`alû eṣâbi`ahüm fî âẕânihim vestagşev ŝiyâbehüm veeṣarru vestekberü-stikbârâ.'),
(5427, 71, 8, 'ŝümme innî de`avtühüm cihârâ.'),
(5428, 71, 9, 'ŝümme innî a`lentü lehüm veesrartü lehüm isrârâ.'),
(5429, 71, 10, 'feḳultü-stagfirû rabbeküm innehû kâne gaffârâ.'),
(5430, 71, 11, 'yürsili-ssemâe `aleyküm midrârâ.'),
(5431, 71, 12, 'veyümdidküm biemvâliv vebenîne veyec`al leküm cennâtiv veyec`al leküm enhârâ.'),
(5432, 71, 13, 'mâ leküm lâ tercûne lillâhi veḳârâ.'),
(5433, 71, 14, 'veḳad ḫaleḳaküm aṭvârâ.'),
(5434, 71, 15, 'elem terav keyfe ḫaleḳa-llâhü seb`a semâvâtin ṭibâḳâ.'),
(5435, 71, 16, 'vece`ale-lḳamera fîhinne nûrav vece`ale-şşemse sirâcâ.'),
(5436, 71, 17, 'vellâhü embeteküm mine-l\'arḍi nebâtâ.'),
(5437, 71, 18, 'ŝümme yü`îdüküm fîhâ veyuḫricüküm iḫrâcâ.'),
(5438, 71, 19, 'vellâhü ce`ale lekümü-l\'arḍa bisâṭâ.'),
(5439, 71, 20, 'liteslükû minhâ sübülen ficâcâ.'),
(5440, 71, 21, 'ḳâle nûḥur rabbi innehüm `aṣavnî vettebe`û mel lem yezidhü mâlühû veveledühû illâ ḫasârâ.'),
(5441, 71, 22, 'vemekerû mekran kübbârâ.'),
(5442, 71, 23, 'veḳâlû lâ teẕerunne âliheteküm velâ teẕerunne veddev velâ süvâ`av velâ yegûŝe veye`ûḳa venesrâ.'),
(5443, 71, 24, 'veḳad eḍallû keŝîrâ. velâ tezidi-żżâlimîne illâ ḍalâlâ.'),
(5444, 71, 25, 'mimmâ ḫaṭîâtihim ugriḳû feüdḫilû nâran felem yecidû lehüm min dûni-llâhi enṣârâ.'),
(5445, 71, 26, 'veḳâle nûḥur rabbi lâ teẕer `ale-l\'arḍi mine-lkâfirîne deyyârâ.'),
(5446, 71, 27, 'inneke in teẕerhüm yüḍillû `ibâdeke velâ yelidû illâ fâciran keffârâ.'),
(5447, 71, 28, 'rabbi-gfir lî velivâlideyye velimen deḫale beytiye mü\'minev velilmü\'minîne velmü\'minât. velâ tezidi-żżâlimîne illâ tebârâ.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'ḳul ûḥiye ileyye ennehü-steme`a neferum mine-lcinni feḳâlû innâ semi`nâ ḳur\'ânen `acebâ.'),
(5449, 72, 2, 'yehdî ile-rruşdi feâmennâ bih. velen nüşrike birabbinâ eḥadâ.'),
(5450, 72, 3, 'veennehû te`âlâ ceddü rabbinâ me-tteḫaẕe ṣâḥibetev velâ veledâ.'),
(5451, 72, 4, 'veennehû kâne yeḳûlü sefîhünâ `ale-llâhi şeṭaṭâ.'),
(5452, 72, 5, 'veennâ żanennâ el len teḳûle-l\'insü velcinnü `ale-llâhi keẕibâ.'),
(5453, 72, 6, 'veennehû kâne ricâlüm mine-l\'insi ye`ûẕûne biricâlim mine-lcinni fezâdûhüm raheḳâ.'),
(5454, 72, 7, 'veennehüm żannû kemâ żanentüm el ley yeb`aŝe-llâhü eḥadâ.'),
(5455, 72, 8, 'veennâ lemesne-ssemâe fevecednâhâ müliet ḥarasen şedîdev veşühübâ.'),
(5456, 72, 9, 'veennâ künnâ naḳ`udü minhâ meḳâ`ide lissem`. femey yestemi`i-l\'âne yecid lehû şihâber raṣadâ.'),
(5457, 72, 10, 'veennâ lâ nedrî eşerrun ürîde bimen fi-l\'arḍi em erâde bihim rabbühüm raşedâ.'),
(5458, 72, 11, 'veennâ minne-ṣṣâliḥûne veminnâ dûne ẕâlik. künnâ ṭarâiḳa ḳidedâ.'),
(5459, 72, 12, 'veennâ żanennâ el len nü`cize-llâhe fi-l\'arḍi velen nü`cizehû herabâ.'),
(5460, 72, 13, 'veennâ lemmâ semi`ne-lhüdâ âmennâ bih. femey yü\'mim birabbihî felâ yeḫâfü baḫsev velâ raheḳâ.'),
(5461, 72, 14, 'veennâ minne-lmüslimûne veminne-lḳâsiṭûn. femen esleme feülâike teḥarrav raşedâ.'),
(5462, 72, 15, 'veemme-lḳâsiṭûne fekânû licehenneme ḥaṭabâ.'),
(5463, 72, 16, 'veel levi-steḳâmû `ale-ṭṭarîḳati leesḳaynâhüm mâen gadeḳâ.'),
(5464, 72, 17, 'lineftinehüm fîh. vemey yü`riḍ `an ẕikri rabbihî yeslükhü `aẕâben ṣa`adâ.'),
(5465, 72, 18, 'veenne-lmesâcide lillâhi felâ ted`û me`a-llâhi eḥadâ.'),
(5466, 72, 19, 'veennehû lemmâ ḳâme `abdü-llâhi yed`ûhü kâdû yekûnûne `aleyhi libedâ.'),
(5467, 72, 20, 'ḳul innemâ ed`û rabbî velâ üşrikü bihî eḥadâ.'),
(5468, 72, 21, 'ḳul innî lâ emlikü leküm ḍarrav velâ raşedâ.'),
(5469, 72, 22, 'ḳul innî ley yücîranî mine-llâhi eḥadüv velen ecide min dûnihî mülteḥadâ.'),
(5470, 72, 23, 'illâ belâgam mine-llâhi verisâlâtih. vemey ya`ṣi-llâhe verasûlehû feinne lehû nâra cehenneme ḫâlidîne fîhâ ebedâ.'),
(5471, 72, 24, 'ḥattâ iẕâ raev mâ yû`adûne feseya`lemûne men aḍ`afü nâṣirav veeḳallü `adedâ.'),
(5472, 72, 25, 'ḳul in edrî eḳarîbüm mâ tû`adûne em yec`alü lehû rabbî emedâ.'),
(5473, 72, 26, '`âlimü-lgaybi felâ yużhiru `alâ gaybihî eḥadâ.'),
(5474, 72, 27, 'illâ meni-rteḍâ mir rasûlin feinnehû yeslükü mim beyni yedeyhi vemin ḫalfihî raṣadâ.'),
(5475, 72, 28, 'liya`leme en ḳad eblegû risâlâti rabbihim veeḥâṭa bimâ ledeyhim veaḥṣâ külle şey\'in `adedâ.');
INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'yâ eyyühe-lmüzzemmil.'),
(5477, 73, 2, 'ḳumi-lleyle illâ ḳalîlâ.'),
(5478, 73, 3, 'niṣfehû evi-nḳuṣ minhü ḳalîlâ.'),
(5479, 73, 4, 'ev zid `aleyhi verattili-lḳur\'âne tertîlâ.'),
(5480, 73, 5, 'innâ senülḳî `aleyke ḳavlen ŝeḳîlâ.'),
(5481, 73, 6, 'inne nâşiete-lleyli hiye eşeddü vaṭ\'ev veaḳvemü ḳîlâ.'),
(5482, 73, 7, 'inne leke fi-nnehâri sebḥan ṭavîlâ.'),
(5483, 73, 8, 'veẕküri-sme rabbike vetebettel ileyhi tebtîlâ.'),
(5484, 73, 9, 'rabbü-lmeşriḳi velmagribi lâ ilâhe illâ hüve fetteḫiẕhü vekîlâ.'),
(5485, 73, 10, 'vaṣbir `alâ mâ yeḳûlûne vehcürhüm hecran cemîlâ.'),
(5486, 73, 11, 'veẕernî velmükeẕẕibîne üli-nna`meti vemehhilhüm ḳalîlâ.'),
(5487, 73, 12, 'inne ledeynâ enkâlev veceḥîmâ.'),
(5488, 73, 13, 'veṭa`âmen ẕâ guṣṣativ ve`aẕâben elîmâ.'),
(5489, 73, 14, 'yevme tercüfü-l\'arḍu velcibâlü vekâneti-lcibâlü keŝîbem mehîlâ.'),
(5490, 73, 15, 'innâ erselnâ ileyküm rasûlen şâhiden `aleyküm kemâ erselnâ ilâ fir`avne rasûlâ.'),
(5491, 73, 16, 'fe`aṣâ fir`avnü-rrasûle feeḫaẕnâhü aḫẕev vebîlâ.'),
(5492, 73, 17, 'fekeyfe tetteḳûne in kefertüm yevmey yec`alü-lvildâne şîbâ.'),
(5493, 73, 18, 'essemâü münfeṭirum bih. kâne va`dühû mef`ûlâ.'),
(5494, 73, 19, 'inne hâẕihî teẕkirah. femen şâe-tteḫaẕe ilâ rabbihî sebîlâ.'),
