INSERT INTO `tr_transliteration` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'hel etâ `ale-l\'insâni ḥînüm mine-ddehri lem yekün şey\'em meẕkûrâ.'),
