INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Rahmân ve Rahîm olan Allah\'ın ismiyle.'),
(2, 1, 2, 'Hamd o âlemlerin Rabbi,'),
(3, 1, 3, 'O Rahmân ve Rahim,'),
(4, 1, 4, 'O, din gününün maliki Allah\'ın.'),
(5, 1, 5, 'Ancak sana ederiz kulluğu, ibadeti ve ancak senden dileriz yardımı, inayeti. (Ya Rab!).'),
(6, 1, 6, 'Hidayet eyle bizi doğru yola,'),
(7, 1, 7, 'O kendilerine nimet verdiğin mutlu kimselerin yoluna; o gazaba uğramışların ve o sapmışların yoluna değil.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, '(Elif, Lâm, Mîm.)'),
(9, 2, 2, 'İşte o kitap, bunda şüphe yok, müttakiler (kötülükten korunacaklar) için hidayettir.'),
(10, 2, 3, 'Onlar ki gaybe iman edip namazı dürüst kılarlar ve kendilerine verdiğimiz rızıktan (Allah yolunda) harcarlar.'),
(11, 2, 4, 'Ve onlar ki hem sana indirilene iman ederler, hem senden önce indirilene. Ahirete de bunlar kesinlikle iman ederler.'),
(12, 2, 5, 'Bunlar, işte Rabblerinden bir hidayet üzerindedirler ve bunlar işte felaha erenlerdir.'),
(13, 2, 6, 'Şu muhakkak ki inkâr edenleri uyarsan da, uyarmasan da onlar için birdir. Onlar inanmazlar.'),
(14, 2, 7, 'Allah onların kalplerini ve kulaklarını mühürlemiştir. Gözlerinin üzerinde bir de perde vardır. Ve büyük azab onlaradır.'),
(15, 2, 8, 'İnsanlardan öyleleri de vardır ki, inanmadıkları halde, "Allah\'a ve ahiret gününe inandık." derler.'),
(16, 2, 9, 'Allah\'ı ve müminleri aldatmaya çalışırlar. Halbuki sırf kendilerini aldatırlar da farkına varmazlar.'),
(17, 2, 10, 'Kalplerinde hastalık vardır. Allah da onların hastalığını arttırmıştır. Yalan söylemelerine karşılık onlara elem verici bir azab vardır.'),
(18, 2, 11, 'Hem onlara: "Yeryüzünde fesat çıkarmayın." denildiğinde: "Biz ancak ıslah edicileriz." derler.'),
(19, 2, 12, 'İyi bilin ki, onlar ortalığı bozanların ta kendileridir, fakat anlamazlar.'),
(20, 2, 13, 'Onlara: "İnsanların (müslümanların) inandığı gibi inanın." denilince, "Biz de o beyinsizlerin inandığı gibi mi inanacağız?" derler. İyi bilin ki, asıl beyinsiz kendileridir fakat bilmezler.'),
(21, 2, 14, 'Onlar iman edenlere rastladıkları zaman: "İnandık" derler. Fakat şeytanlarıyle yalnız kaldıkları zaman: "Biz, sizinle beraberiz, biz sadece (onlarla) alay ediyoruz." derler.'),
(22, 2, 15, '(Asıl) Allah onlarla alay eder ve taşkınlıkları içinde serserice dolaşmalarına mühlet verir.'),
(23, 2, 16, 'İşte onlar o kimselerdir ki, hidayet karşılığında sapıklığı satın aldılar da, ticaretleri kâr etmedi, doğru yolu da bulamadılar.'),
(24, 2, 17, 'Onların durumu, bir ateş yakanın durumu gibidir. (Ateş) çevresini aydınlatır aydınlatmaz Allah onların (gözlerinin) nurlarını giderdi ve onları karanlıklar içinde bıraktı, artık görmezler.'),
(25, 2, 18, '(Onlar) sağırdırlar, dilsizdirler, kördürler. Artık (hakka) dönmezler.'),
(26, 2, 19, 'Yahut (onların durumu), gökten boşanan, içinde karanlıklar, gök gürlemesi ve şimşek(ler) bulunan bir yağmur(a tutulmuşun hali) gibidir. Yıldırımlardan ölmek korkusuyla parmaklarını kulaklarına tıkarlar. Oysa Allah, inkârcıları tamamen kuşatmıştır.'),
(27, 2, 20, 'O şimşek nerdeyse gözlerini (n nûrunu) kapıverecek. Önlerini aydınlattımı ışığında yürürler, karanlık üzerlerine çöktümü de dikilip kalırlar. Allah dilemiş olsaydı işitmelerini, görmelerini de alıverirdi. Şüphesiz Allah her şeye kâdirdir.'),
(28, 2, 21, 'Ey insanlar! Sizi ve sizden öncekileri yaratan Rabb\'inize kulluk edin ki (Allah\'ın) azabından korunasınız.'),
(29, 2, 22, 'O (Rabb) ki yeri sizin için bir döşek, göğü de bir bina yaptı. Gökten su indirdi, onunla size rızık olarak çeşitli ürünler çıkardı. Öyleyse siz de, bile bile, Allah\'a eşler koşmayın.'),
(30, 2, 23, 'Eğer kulumuz (Muhammed)a indirdiğimiz (Kur\'ân)den şüphe içinde iseniz, haydi onun gibi bir sûre getirin, Allah\'tan başka güvendiklerinizin hepsini çağırın; eğer doğru iseniz.'),
(31, 2, 24, 'Yok yapamadıysanız, ki hiçbir zaman yapamayacaksınız, o halde yakıtı insanlar ve taşlar olan, inkârcılar için hazırlanmış ateşten sakının.'),
(32, 2, 25, 'İnanıp yararlı işler yapanlara, altlarından ırmaklar akan cennetlerin kendilerine ait olduğunu müjdele! Onlardaki herhangi bir meyveden rızıklandırıldıklarında: "Bu daha önce de rızıklandığımız şeydir" derler ve o rızık birbirinin benzeri olmak üzere, kendilerine sunulacak. Orada çok temiz zevceler de onların. Hem onlar orada ebedî kalacaklar.'),
(33, 2, 26, 'Muhakkak ki Allah bir sivri sineği, hatta daha üstününü misal getirmekten çekinmez. İman edenler bilirler ki, o şüphesiz haktır, Rabb\'lerındandır. Ama küfre saplananlar: "Allah böyle bir misal ile ne demek istedi?" derler. Allah onunla birçoklarını şaşırtır, yine onunla birçoklarını yola getirir. Onunla ancak o fasıkları şaşırtır.'),
(34, 2, 27, 'Onlar ki, söz verip andlaştıktan sonra Allah\'a verdikleri sözü bozarlar. Allah\'ın birleştirmesini emrettiği şeyi (iman ve akrabalık bağlarını) keserler ve yeryüzünde bozgunculuk yaparlar. İşte zarara uğrayanlar onlardır.'),
(35, 2, 28, 'Allah\'ı nasıl inkâr edersiniz ki, ölü idiniz sizleri diriltti. Sonra sizleri yine öldürecek, sonra yine diriltecek, sonra da döndürülüp ona götürüleceksiniz.'),
(36, 2, 29, 'O ki, yeryüzünde ne varsa hepsini sizin için yarattı. Sonra göğe yöneldi, onları yedi gök olarak düzenledi. O, her şeyi bilir.'),
(37, 2, 30, 'Bir zamanlar Rabb\'in meleklere: "Ben yeryüzünde bir halife yaratacağım" demişti. (Melekler): "A!.. Orada bozgunculuk yapacak ve kan dökecek birisini mi yaratacaksın? Oysa biz seni överek tesbih ediyor ve seni takdis ediyoruz" dediler. (Rabb\'in): "Ben sizin bilmediklerinizi bilirim." dedi.'),
(38, 2, 31, 'Ve Âdem\'e isimlerin hepsini öğretti, sonra onları meleklere gösterip: "Haydi davanızda sadıksanız bana şunları isimleriyle haber verin." dedi.'),
(39, 2, 32, 'Dediler ki: "Yücesin sen (ya Rab!). Bizim, senin bize öğrettiğinden başka bir bilgimiz yoktur. Şüphesiz sen bilensin, hakîmsin".'),
(40, 2, 33, '(Allah): "Ey Âdem, bunlara onları isimleriyle haber ver." dedi. Bu emir üzerine Âdem onlara isimleriyle onları haber verince, (Allah): "Ben size, ben göklerin ve yerin gayblarını bilirim, sizin açıkladığınızı da, içinizde gizlediğinizi de bilirim" dememiş miydim?" dedi.'),
(41, 2, 34, 'Ve o zaman meleklere: "Âdem\'e secde edin!" dedik, hemen secde ettiler. Yalnız İblis dayattı, kibrine yediremedi, inkârcılardan oldu.'),
(42, 2, 35, 'Dedik ki: "Ey Âdem, sen ve eşin cennette oturun, ikiniz de ondan dilediğiniz yerde bol bol yeyin, fakat şu ağaca yaklaşmayın, yoksa zalimlerden olursunuz."'),
(43, 2, 36, 'Bunun üzerine şeytan onları(n ayağını) oradan kaydırdı, içinde bulundukları (cennet yurdu)ndan çıkardı. Biz de: "Birbirinize düşman olarak inin, orada belirli bir vakte kadar sizin için bir karar yeri ve bir nasib vardır." dedik.'),
(44, 2, 37, 'Derken Âdem Rabb\'ından birtakım kelimeler aldı, (onlarla tevbe etti. O da) tevbesini kabul etti. Muhakkak O, tevbeyi çok kabul eden, çok esirgeyendir.'),
(45, 2, 38, 'Onlara dedik ki: "Hepiniz oradan inin. Size benim tarafımdan bir hidayet rehberi geldiğinde, kim o hidayetçimin izinde giderse, onlar için hiçbir korku yoktur, onlar mahzun da olmayacaklardır.'),
(46, 2, 39, 'İnkâr edip âyetlerimizi yalanlayanlara gelince, onlar da cehennem ehlidirler. Orada ebedî olarak kalacaklardır.'),
(47, 2, 40, 'Ey İsrailoğulları, size verdiğim nimetimi hatırlayın, bana verdiğiniz sözü tutun ki, ben de size verdiğim sözü tutayım ve sadece benden korkun!'),
(48, 2, 41, 'Yanınızdakini (Tevrat\'ı) tasdik edici olarak indirdiğim (Kur\'ân)a iman edin, O\'nu, inkar edenlerin ilki siz olmayın, benim âyetlerimi birkaç paraya değişmeyin. Ancak benden korkun.'),
(49, 2, 42, 'Hakk\'ı batıla karıştırıp da, bile bile hakkı gizlemeyin.'),
(50, 2, 43, 'Hem namazı dosdoğru kılın, zekatı verin, rükû edenlerle birlikte siz de rükû edin.'),
(51, 2, 44, 'İnsanlara iyiliği emreder de kendinizi unutur musunuz? Halbuki kitab (Tevrat)ı okuyorsunuz. Hâlâ aklınızı başınıza almayacak mısınız?'),
(52, 2, 45, 'Bir de sabırla, namazla yardım isteyin. Şüphesiz bu, (Allah\'a) saygılı olanlardan başkasına ağır gelir.'),
(53, 2, 46, 'Onlar ki, Rablerine kavuşacaklarını ve gerçekten O\'na döneceklerini bilirler.'),
(54, 2, 47, 'Ey İsrailoğulları! Size verdiğim nimeti ve vaktiyle sizi âlemlere üstün kıldığımı hatırlayın.'),
(55, 2, 48, 'Ve öyle bir günden korunun ki, kimse kimsenin yerine bir şey ödeyemez, kimseden şefaat da kabul edilmez, kimseden fidye de alınmaz ve onlara hiçbir yardım da yapılmaz.'),
(56, 2, 49, '(Hem hatırlayın ki bir zaman) sizi Firavun ailesinden de kurtardık, (onlar) size azabın en kötüsünü reva görüyor, oğullarınızı boğazlıyor, kadınlarınızı sağ bırakıyorlardı. Ve bunda size Rabbiniz tarafından büyük bir imtihan vardı.'),
(57, 2, 50, 'Hani bir zamanlar sizin için denizi yarıp, sizi kurtardık da Firavun\'un adamlarını suda boğduk, siz de bakıp duruyordunuz.'),
(58, 2, 51, 'Hani bir zamanlar Musa\'ya kırk gecelik vaad verdik de sonra siz onun arkasından buzağıyı put edindiniz ve o halinizle zalimler idiniz.'),
(59, 2, 52, 'Sonra yine de sizi affettik, artık şükretmeniz gerekiyordu.'),
(60, 2, 53, 'Ve hani bir zamanlar Musa\'ya o kitabı ve furkanı verdik, gerekirdi ki, doğru yolda gidesiniz.'),
(61, 2, 54, 'Hani bir zamanlar Musa kavmine dedi ki; Ey kavmim cidden siz o buzağıyı put edinmekle kendi kendinize zulmettiniz, bari gelin Rabbinize tevbe ile dönün de nefislerinizi öldürün. Böyle yapmanız Bârî Teâlânız katında sizin için hayırlıdır, böylece tevbenizi kabul buyurdu. Gerçekten de o Tevvab ve Rahîm\'dir.'),
(62, 2, 55, 'Hani bir zamanlar "Ey Musa biz Allah\'ı açıkça görmedikçe senin sözünle asla inanmayacağız." demiştiniz de bunun üzerine sizi yıldırım çarpmıştı ve siz de bakakalmıştınız.'),
(63, 2, 56, 'Sonra şükredesiniz diye sizi ölümünüzün ardından yeniden diriltmiştik.'),
(64, 2, 57, 'Ve üstünüze o bulutu gölge yaptık, ve size ihsan ettiğimiz hoş rızıklardan yiyin, diye üzerinize kudret helvası ve bıldırcın indirdik. Onlar, bize zulmetmediler, lakin kendi nefislerine zulmediyorlardı.'),
(65, 2, 58, 'Hani bir zamanlar "Şu şehre girin de onun nimetlerinden dilediğiniz şekilde bol bol yiyin ve kapıdan secde ederek girin ve "hıtta" (bizi bağışla!) deyin ki, size, hatalarınızı mağfiret ediverelim, iyilik yapanlara nimetlerimizi daha da arttıracağız" dedik.'),
(66, 2, 59, 'Bunun üzerine o zulme devam edenler sözü değiştirdiler, onu kendilerine söylenildiğinden başka bir şekle soktular. Biz de kötülük yaptıkları için o zalimlere murdar bir azap indirdik.'),
(67, 2, 60, 'Hani bir zamanlar Musa, kavmi için su istemişti, biz de "asanla taşa vur!" demiştik, bunun üzerine o taştan on iki pınar fışkırmıştı. Her kısım insan kendi su alacağı yeri bildi. Allah\'ın rızkından yiyin ve için de bozgunculuk ve saldırganlık yaparak yeryüzünü fesada vermeyin.'),
(68, 2, 61, 'Hani bir zamanlar, "Ey Musa, biz tek çeşit yemeğe asla katlanamayacağız, yeter artık bizim için Rabbine dua et de bize yerin yetiştirdiği şeylerden; sebzesinden, kabağından, sarmısağından, mercimeğinden ve soğanından çıkarsın." dediniz. O da size "O üstün olanı daha aşağı olanla değiştirmek mi istiyorsunuz? Bir kasabaya konaklayın o vakit istediğiniz elbette olacaktır." dedi. Üzerlerine zillet ve meskenet damgası vuruldu ve nihayet Allah\'dan bir gazaba uğradılar. Evet öyle oldu, çünkü Allah\'ın âyetlerini inkâr ediyorlar ve haksız yere peygamberleri öldürüyorlardı. Evet öyle oldu, çünkü isyana dalıyorlar ve aşırı gidiyorlardı.'),
(69, 2, 62, 'Şüphe yok ki, iman edenler, yahudiler, hıristiyanlar ve sabiîler, bunlardan her kim Allah\'a ve ahiret gününe gerçekten iman eder ve salih amel işlerse elbette Rabbleri katında bunların ecirleri vardır, bunlara bir korku yoktur, bunlar mahzun da olacak değillerdir.'),
(70, 2, 63, 'Hani bir zamanlar sizden mîsak (sağlam bir söz) almıştık, Tur\'u üstünüze kaldırıp demiştik ki; size verdiğimiz kitaba kuvvetle tutunun ve içindekilerden gafil olmayın, gerek ki, korunursunuz.'),
(71, 2, 64, 'Sonra verdiğiniz sözün arkasından yüz çevirdiniz, eğer üzerinizde Allah\'ın lütfu ve rahmeti olmasa idi herhalde zarara uğrayanlardan olurdunuz.'),
(72, 2, 65, 'İçinizden cumartesi günü yasağını çiğneyenleri elbette bilirsiniz. İşte bundan dolayı onlara "sefil maymunlar olun!" dedik.'),
(73, 2, 66, 'Bu ibret dolu cezayı öncekilere ve sonrakilere bir ders, korunacaklara da bir nasihat, bir öğüt yaptık.'),
(74, 2, 67, 'Hani bir zamanlar Musa kavmine demişti ki Allah, size bir bakara (sığır) boğazlamanızı emrediyor. Onlar da "ayol sen bizimle eğleniyor, alay mı ediyorsun?" dediler. Musa da: "Böyle cahillerden biri olmaktan Allah\'a sığınırım." dedi.'),
(75, 2, 68, 'Onlar, "Bizim için Rabbine dua et, her ne ise onu bize açıklasın." dediler. Musa, "Rabbim buyuruyor ki, o ne pek yaşlı, ne de pek taze, ikisi arası dinç bir sığırdır, haydi emrolunduğunuz işi yapınız." dedi.'),
(76, 2, 69, 'Onlar, "Bizim için Rabbine dua et, rengi ne ise onu bize açıklasın." dediler. Musa, "Rabbim buyuruyor ki, o, bakanlara sürur veren, sapsarı bir sığırdır." dedi.'),
(77, 2, 70, 'Onlar, "Bizim için Rabbine dua et, o nedir bize iyice açıklasın, çünkü o bize biraz karışık geldi, bununla beraber Allah dilerse onu elbette buluruz." dediler.'),
(78, 2, 71, 'Musa, "Rabbim buyuruyor ki o, ne çifte koşulup tarla süren, ne de ekin sulayan, ne de salma gezen ve hiç alacası olmayan bir sığırdır". Onlar da: "İşte tam şimdi gerçeği ortaya koydun." dediler. Nihayet onu bulup boğazladılar. Az kaldı yapmayacaklardı.'),
(79, 2, 72, 'Hani bir zamanlar siz bir adam öldürmüştünüz de onun hakkında birbirinizle atışmış ve onu üstünüzden atmıştınız, halbuki Allah, saklamış olduğunuzu açığa çıkaracaktı.'),
(80, 2, 73, 'İşte bundan dolayı, o sığırın bir parçası ile o ölüye vurun, dedik. Allah ölüleri işte böyle diriltir ve size âyetlerini gösterir, belki aklınızı başınıza toplarsınız.'),
(81, 2, 74, 'Sonra bunun arkasından yine kalbleriniz katılaştı, şimdi de taş gibi, ya da taştan da beter hale geldi. Çünkü taşlardan öylesi var ki; içinden nehirler kaynıyor, yine öylesi var ki, çatlıyor da bağrından sular fışkırıyor, öylesi de var ki, Allah korkusundan yerlerde yuvarlanıyor... Ve sizin neler yaptığınızdan Allah gafil değildir.'),
(82, 2, 75, 'Şimdi bunların, size hemen inanacaklarını ümit mi ediyorsunuz? Halbuki bunlardan bir grup vardı ki, Allah\'ın kelâmını işitirlerdi de sonra ona akılları yattığı halde bile bile onu tahrif ederlerdi.'),
(83, 2, 76, 'Üstelik iman edenlere rastladıklarında inandık derler, birbirleriyle başbaşa kaldıkları zaman, "Rabbinizin huzurunda aleyhinize delil olarak kullansınlar diye mi tutup Allah\'ın size açıkladığı gerçekleri onlara da söylüyorsunuz? Hiç aklınız yok mu be?" derlerdi.'),
(84, 2, 77, 'Peki bilmezler mi ki, onlar neyi sır olarak saklar ve neyi açıkça söylerlerse Allah hepsini bilir.'),
(85, 2, 78, 'Bunların bir de ümmî (okuma yazması olmayan) kısmı vardır, kitabı bilmezler, ancak birtakım kuruntu yığınına, boş saplantılara kapılır ve zan içinde dolaşır dururlar.'),
(86, 2, 79, 'Artık o kimselerin vay haline ki, kendi elleriyle kitap yazarlar da sonra biraz para almak için "Bu Allah katındandır." derler. Artık vay o elleriyle yazdıkları yüzünden onlara, vay o kazandıkları vebal yüzünden onlara!..'),
(87, 2, 80, 'Bir de dediler ki: "Bize sayılı birkaç günden başka asla ateş azabı dokunmaz". De ki; "Siz Allah\'dan bir ahit mi aldınız? Böyle ise Allah sözünden dönmez. Yoksa siz Allah\'a karşı bilemeyeceğiniz şeyleri mi söylüyorsunuz?"'),
(88, 2, 81, 'Evet kim bir günah işlemiş de kendi günahı kendisini her yandan kuşatmış ise, işte öyleleri ateş ehlidirler ve orada ebedî kalıcıdırlar.'),
(89, 2, 82, 'İman edip salih ameller işleyenler, işte öyleleri de cennet ehlidirler ve orada ebedî kalıcıdırlar.'),
(90, 2, 83, 'Hani bir vakitler İsrailoğulları\'ndan şöylece mîsak (kesin bir söz) almıştık: Allah\'dan başkasına tapmayacaksınız, anababaya iyilik, yakınlığı olanlara, öksüzlere, çaresizlere de iyilik yapacaksınız, insanlara güzellikle söz söyleyecek, namazı kılacak, zekatı vereceksiniz. Sonra çok azınız müstesna olmak üzere sözünüzden döndünüz, hâlâ da dönüyorsunuz.'),
(91, 2, 84, 'Yine bir zamanlar mîsakınızı almıştık; birbirinizin kanlarını dökmeyeceksiniz, nüfusunuzu diyarınızdan çıkarmıyacaksınız. Sonra siz buna ikrar da verdiniz ve ikrarınıza şahit de oldunuz.'),
(92, 2, 85, 'Sonra sizler öyle kimselersiniz ki, kendilerinizi öldürüyorsunuz ve sizden olan bir grubu diyarlarından çıkarıyorsunuz, onlar aleyhinde kötülük ve düşmanlık güdüyor ve bu konuda birleşip birbirinize arka çıkıyorsunuz, şayet size esir olarak gelirlerse fidyeleşmeye kalkıyorsunuz. Halbuki yurtlarından çıkarılmaları size haram kılınmış idi. Yoksa siz kitabın bir kısmına inanıp bir kısmını inkâr mı ediyorsunuz? Şu halde içinizden böyle yapanlar, netice olarak dünya hayatında perişanlıktan başka ne kazanırlar, kıyamet gününde de en şiddetli azaba uğratılırlar. Allah, yaptıklarınızdan gafil değildir.'),
(93, 2, 86, 'Bunlar ahireti, dünya hayatına satmış kimselerdir. Onun için bunlardan azap hafifletilmez ve kendilerine bir yerden yardım da gelmez.'),
(94, 2, 87, 'Celâlim hakkı için Musa\'ya o kitabı verdik, arkasından birtakım peygamberler de gönderdik, hele Meryem oğlu İsa\'ya apaçık mucizeler verdik, onu Rûhu\'lKudüs ile de destekledik. Size nefislerinizin hoşlanmayacağı bir emirle gelen her peygambere kafa mı tutacaksınız? Kibrinize dokunduğu için onların bir kısmına yalan diyecek, bir kısmını da öldürecek misiniz?'),
(95, 2, 88, '(Yahudiler, peygamberimize karşı alaylı bir ifade ile): "Bizim kalblerimiz kılıflıdır." dediler. Bilakis Allah, onları kâfirlikleri yüzünden lanetledi. Bundan dolayı çok az imana gelirler.'),
(96, 2, 89, 'Yanlarındakini tasdik etmek üzere onlara Allah katından bir kitap gelince, daha önceleri inanmayanlara karşı onunla yardım isteyip durdukları halde, o tanıdıkları kendilerine gelince, bu sefer kendileri onu inkâr ettiler. İşte bundan dolayı Allah\'ın laneti kâfirleredir.'),
(97, 2, 90, 'Ne kadar çirkindir o uğruna kendilerini sattıkları şey ki; Allah\'ın kullarından dilediğine kendi lütuf ve kereminden vahiy indirmesine kafa tutarak, Allah ne indirdiyse hepsini inkâr ettiler. İşte bu yüzden de gazap üstüne gazaba uğradılar. Can yakıcı azap asıl kâfirler içindir.'),
(98, 2, 91, 'Onlara, "Allah ne indirdiyse ona iman edin." denildiği zaman, onlar "Biz kendimize indirilene iman ederiz." derler ve ondan başkasını inkâr ederler. Oysa yanlarındaki Tevrat\'ı tasdik eden gerçek vahiy odur. Onlara de ki; "Peki madem gerçek mümin sizsiniz de ne diye daha önce Allah\'ın peygamberlerini öldürüyordunuz?'),
(99, 2, 92, 'Celâlim hakkı için Musa size belgelerle gelmişti de onun arkasından tuttunuz o buzağıya taptınız. Siz işte o zâlimlersiniz.'),
(100, 2, 93, 'Bir zamanlar size, "verdiğimiz kitaba kuvvetle sarılın ve onu dinleyin." diye Tûr\'u tepenize kaldırıp mîsakınızı aldık. (O yahudiler): "Duyduk, dinledik, isyan ettik." dediler, kâfirlikleri yüzünden o danayı yüreklerinde besleyip büyüttüler. De ki, " Eğer siz mümin kimseler iseniz, bu imanınız size ne çirkin şeyler emrediyor!'),
(101, 2, 94, 'De ki; Allah yanında ahiret yurdu (cennet) başkalarının değil de yalnızca sizin ise, eğer iddianızda da sadık iseniz haydi hemen ölümü temenni ediniz, ölmeyi cana minnet biliniz.'),
(102, 2, 95, 'Fakat elleriyle işledikleri yüzünden onu hiçbir zaman temenni edemiyecekler. Allah o zâlimleri bilir.'),
(103, 2, 96, 'Elbette onları insanların hayata en hırslı, en düşkün olanları olarak bulacak, hatta müşriklerden bile daha düşkün bulacaksın. Onların her biri bin sene ömür sürmeyi arzular, oysa uzun yaşamak kendisini azaptan kurtarıp uzaklaştıracak değildir. Allah, onların neler yaptığını görüp duruyor.'),
(104, 2, 97, 'Söyle; her kim Cebrail\'e düşman ise iyi bilsin ki, Kur\'ân\'ı senin kalbine Allah\'ın izniyle kendinden önceki vahiyleri onaylayıcı, müminlere hidayet ve müjde kaynağı olmak üzere o indirdi.'),
(105, 2, 98, 'Her kim Allah\'a, Allah\'ın meleklerine, peygamberlerine, Cebrail ile Mîkâil\'e düşman olursa, iyi bilsin ki, Allah da o kâfirlerin düşmanıdır.'),
(106, 2, 99, 'Şanım hakkı için sana çok açık âyetler; parlak mucizeler indirdik. Öyle ki, iman sahasından uzaklaşmış fasıklardan başkası onları inkâr etmez.'),
(107, 2, 100, 'O fasıklar hem bunları tanımıyacaklar, hem de ne zaman bir ahd üzerine antlaşma yapsalar, her defasında mutlaka içlerinden bir güruh çıkıp onu bozacak ve atıverecek öyle mi? Hatta az bir güruh değil, onların çoğu ahit tanımaz imansızlardır.'),
(108, 2, 101, 'Üstelik Allah tarafından onlara, yanlarındaki kitabı tasdik edici bir peygamber gelince, daha önce kendilerine kitap verilenlerden bir kısmı, Allah\'ın kitabını sırtlarından geriye attılar, sanki hiçbir şey bilmiyorlarmış gibi yaptılar.'),
(109, 2, 102, 'Tuttular da Süleyman mülküne dair şeytanların uydurup izledikleri şeyin ardına düştüler. Halbuki Süleyman inkâr edip kâfir olmadı, lakin o şeytanlar kâfirlik ettiler; insanlara sihir öğretiyorlar ve Bâbil\'de Harut ve Marut\'a, bu iki meleğe indirilen şeyleri öğretiyorlardı. Halbuki o ikisi "biz ancak ve ancak sizi denemek için gönderildik, sakın sihir yapıp da kâfir olmayın!" demeden kimseye birşey öğretmezlerdi. İşte bunlardan karı ile kocanın arasını ayıracak şeyler öğreniyorlardı. Fakat Allah\'ın izni olmadıkça bununla kimseye zarar verebilecek değillerdi. Kendi kendilerine zarar verecek ve bir fayda sağlamayacak bir şey öğreniyorlardı. Yemin olsun ki, onu her kim satın alırsa, onu alanın ahirette bir nasibi olmayacağını da çok iyi biliyorlardı. Hakkiyle bilselerdi, uğruna canlarını sattıkları şey ne çirkin bir şeydi.'),
(110, 2, 103, 'Şayet onlar iman edip de korunmuş olsalardı, elbette Allah tarafından verilecek mükafat çok hayırlı olacaktı. Keşke bunu bilselerdi.'),
(111, 2, 104, 'Ey iman edenler! "râine" demeyin, "unzurna" deyin ve iyi dinleyin, kâfirler için elemli bir azap vardır.'),
(112, 2, 105, 'Ne Kitap ehlinden, ne de müşriklerden hiçbiri, size Rabbinizden bir hayır indirilsin istemez. Allah ise, üstünlüğü, rahmetiyle dilediğine mahsus kılar ve Allah çok büyük lütuf sahibidir.'),
(113, 2, 106, 'Biz bir âyetten her neyi nesheder veya unutturursak, ondan daha hayırlısını yahut mislini getiririz. Bilmez misin ki, Allah her şeye kâdirdir.'),
(114, 2, 107, 'Bilmez misin ki, hakikaten göklerin ve yerin mülkü Allah\'ındır, hepsi O\'nundur. Size de Allah\'dan başka ne bir dost, ne de bir yardımcı vardır.'),
(115, 2, 108, 'Yoksa siz peygamberinizi, bundan önce Musa\'ya sorulduğu gibi, sorguya çekmek mi istiyorsunuz? Halbuki her kim imanı küfürle değiştirirse artık düz yolun ortasında sapıtmış olur.'),
(116, 2, 109, 'Ehli kitaptan birçoğu arzu etmektedir ki, sizi imanınızdan sonra çevirip kâfir etsinler: Hak kendilerine iyice belirdikten sonra bile sırf nefsaniyetlerinden ve kıskançlıktan dolayı bunu yaparlar. Buna rağmen siz şimdi af ile, hoşgörüyle davranın tâ Allah emrini verinceye kadar. Şüphe yok ki Allah her şeye kâdirdir.'),
(117, 2, 110, 'Siz namazı hakkıyle kılmaya bakın ve zekatı verin! Kendi nefsiniz için her ne hayır yaparsanız, Allah katında onu bulursunuz. Muhakkak ki, Allah bütün yaptıklarınızı görmektedir.'),
(118, 2, 111, 'Bir de "yahudi ve hıristiyanlardan başkası asla cennete giremeyecek" dediler. Bu onların kendi kuruntularıdır. Sen de onlara de ki; "Eğer doğru iseniz, haydi bakalım getirin delilinizi."'),
(119, 2, 112, 'Hayır, hayır! Kim özü iyilik dolu olarak yüzünü Allah\'a tertemiz döndürür ve teslim ederse, işte onun Rabbi katında ecri vardır. Onlara hiçbir korku yoktur ve onlar mahzun da olacak değiller.'),
(120, 2, 113, 'Yahudiler dediler ki, "Hıristiyanlar birşey üzerinde değiller", Hristiyanlar da "Yahudiler bir şey üzerinde değiller" dediler. Oysa hepsi de kitabı okuyorlar. Hiçbir bilgisi olmayanlar da öyle onların dedikleri gibi dediler. İşte bundan dolayı Allah, ihtilafa düştükleri bu gibi şeylerde, kıyamet günü aralarında hüküm verecektir.'),
(121, 2, 114, 'Allah\'ın mescitlerini, içlerinde Allah\'ın isminin anılmasından meneden ve onların harap olmalarına çalışan kimselerden daha zâlim kim olabilir! İşte bunlar, oralara korka korka girmekten başka birşey yapmazlar. Bunlara dünyada perişanlık, ahirette de büyük bir azap vardır.'),
(122, 2, 115, 'Bununla beraber, doğu da Allah\'ın, batı da Allah\'ındır. Artık nereye dönerseniz dönün, orası Allah\'a çıkar. Şüphe yok ki, Allah(ın rahmeti) geniştir, O, her şeyi bilendir.'),
(123, 2, 116, 'O zalimler, "Allah kendisine çocuk edindi." dediler. Hâşâ, O sübhândır. Doğrusu, göklerde ve yerde ne varsa O\'nundur. Hepsi O\'na boyun eğmiştir.'),
(124, 2, 117, 'O, göklerin ve yerin yoktan var edicisidir ve O, bir işin olmasını murad edince, ona yalnızca "ol!" der, o da hemen oluverir.'),
(125, 2, 118, 'Bilgiden nasibi olmayanlar da "Allah bizimle konuşsa ya, yahut bize de bir mucize gelse ya!" dediler. Bunlardan öncekiler de tıpkı böyle, bunların dedikleri gibi demişlerdi. Onların kalbleri birbirlerine benzedi. Gerçekten de yakîne ermek (hakikati bilmek) isteyen bir kavim için biz mucizeleri çok açık seçik gösterdik.'),
(126, 2, 119, 'Şüphe yok ki, Biz seni hak ile rahmetimizin müjdecisi ve azabımızın habercisi olarak gönderdik. Sen, o cehennemliklerden sorumlu değilsin.'),
(127, 2, 120, 'Sen onların milletlerine tabi olmadıkça ne yahudiler, ne de hıristiyanlar senden asla hoşnud ve razı olmayacaklar. De ki, gerçekten de Allah\'ın hidayeti, hidayetin ta kendisidir. Şânım hakkı için, sana vahiyle gelen bu kadar bilgiden sonra, kalkıp da onların arzu ve heveslerine uyacak olursan, sana Allah\'dan ne bir dost bulunur, ne de bir yardımcı.'),
(128, 2, 121, 'Kendilerine kitabı verdiğimiz ehliyetli kimseler onu, tilavetinin hakkını vererek okurlar. İşte onlar, ona iman ederler. Her kim de onu inkâr ederse, işte o inkârcılar hüsran içindedirler.'),
(129, 2, 122, 'Ey İsrailoğulları! Sizlere ihsan ettiğim nimetimi ve sizi vaktiyle âlemdeki ümmetlere üstün tuttuğumu hatırlayın!'),
(130, 2, 123, 'Ve öyle bir günden sakının ki, o gün kimse, kimsenin yerine bir şey ödeyemez, kimseden fidye kabul edilmez ve ona şefaat de fayda vermez, hiçbir taraftan yardım da görmezler.'),
(131, 2, 124, 'Şunu da unutmayın ki, bir zamanlar İbrahim\'i Rabbi, birtakım kelimeler ile imtihan etti, o, onları sona erdirince, Rabbi ona, "Ben seni bütün insanlara imam yapacağım." buyurdu. İbrahim, "Zürriyetimden de yap!" dedi. Rabbi ona "zâlimler benim ahdime nail olamaz!" buyurdu.'),
(132, 2, 125, 'Biz ta o zaman bu Beyt\'i, insanlar için bir sevap kazanma ve bir güven yeri kıldık. Siz de Makamı İbrahim\'den kendinize bir namazgah edinin. Ayrıca İbrahim ile İsmail\'e şöyle ahid verdik: "Beytimi, hem tavaf edenler için, hem ibadete kapananlar için, hem de rükû ve secde edenler için tertemiz tutun!"'),
(133, 2, 126, 'Ve o vakit İbrahim "Ey Rabbim, burasını güvenli bir belde kıl, halkından Allah\'a ve ahiret gününe iman edenleri çeşitli meyvalarla rızıklandır" diye yalvardı. Allah buyurdu ki: "küfredeni dahi rızıklandırır da hayattan biraz nasip aldırırım, sonra da onu ateş azabına uğratırım ki, orası ne yaman bir duraktır!"'),
(134, 2, 127, 'Ve ne vakit ki İbrahim, Beyt\'in temellerini yükseltmeye başladı, İsmail ile birlikte şöyle dua ettiler: Ey Rabbimiz, bizden kabul buyur, hiç şüphesiz işiten sensin, bilen sensin.'),
(135, 2, 128, 'Ey bizim Rabbimiz, hem bizim ikimizi yalnız senin için boyun eğen müslümanlar kıl, hem de soyumuzdan yalnız senin için boyun eğen müslüman bir ümmet meydana getir ve bize ibadetimizin yollarını göster, tevbemize rahmetle bakıver. Hiç şüphesiz Tevvâb sensin, Rahîm sensin.'),
(136, 2, 129, 'Ey bizim Rabbimiz, bir de onlara içlerinden öyle bir peygamber gönder ki, onlara senin âyetlerini tilavet eylesin, kendilerine kitabı ve hikmeti öğretsin, içlerini ve dışlarını tertemiz yapıp onları pâk eylesin. Hiç şüphesiz Azîz sensin, hikmet sahibi Sensin.'),
(137, 2, 130, 'İbrahim\'in milletinden, kendine kıyan beyinsizden başka kim yüz çevirir? Biz onu dünyada seçkin birisi yaptık, hiç şüphesiz o, ahirette de iyilerden biridir.'),
(138, 2, 131, 'Rabbi ona, "İslâm ol!" emrini verince, o "Ben âlemlerin Rabbine teslim oldum." dedi.'),
(139, 2, 132, 'Bu dini İbrahim, kendi oğullarına vasiyyet etti, Yakub da öyle yaptı: "Ey oğullarım! Muhakkak ki, bu dini size Allah seçti, başka dinlerden uzak durun, yalnızca müslüman olarak can verin!" dedi.'),
(140, 2, 133, 'Yoksa siz de olaya şahit mi oldunuz; Yakub\'a ölüm hali gelip çattığı zaman, oğullarına; "Benden sonra neye ibadet edeceksiniz?" dediği zaman, oğulları; "Senin Allah\'ına ve ataların İbrahim, İsmail ve İshak\'ın Allah\'ına, tek olan o Allah\'a ibadet edeceğiz. Biz ancak O\'na boyun eğen müslümanlarız." dediler.'),
(141, 2, 134, 'Onlar bir ümmetti, geldi geçti. Onlara kendi kazandıkları, size de kendi kazandığınız. Siz onların yaptıklarından sorguya çekilecek değilsiniz.'),
(142, 2, 135, 'Bir de: "yahudi veya hıristiyan olunuz ki, hidayet bulasınız." dediler. Sen onlara de ki: "Hayır! Hanif olarak hakka tapan İbrahim\'in dinine (uyarız) ki, o hiçbir zaman müşriklerden olmadı."'),
(143, 2, 136, 'Deyiniz ki, "Biz, Allah\'a iman ettik ve bize ne indirildiyse İbrahim\'e, İsmail\'e, İshak\'a, Yakup\'a ve torunlarına ne indirildiyse, Musa\'ya ve İsa\'ya ne indirildiyse ve bütün peygamberlere Rablerinden ne verildiyse hepsine iman ettik. Biz onların arasında fark gözetmeyiz ve biz ancak O\'na boyun eğen müslümanlarız."'),
(144, 2, 137, 'Eğer onlar da sizin iman ettiğiniz gibi iman ederlerse doğru yola girmiş, hidayeti bulmuş olurlar. Yok eğer yüz çevirirlerse onlar sadece ve sadece didişmenin içindedirler. Allah onlara karşı sana yeter. Ve O, işitendir, bilendir.'),
(145, 2, 138, 'Allah\'ın boyasına bak, (vaftiz nolacak?) Kim, Allah\'dan daha güzel boya vurabilir ki? İşte biz O\'na ibadet edenleriz.'),
(146, 2, 139, 'De ki: "Allah hakkında bizimle didişmeye mi gireceksiniz? Oysa O, bizim de Rabbimiz, sizin de Rabbinizdir. Bizim amellerimiz bize, sizin amelleriniz de size. Şu kadar var ki, biz O\'na ihlas ile sarılıyoruz.'),
(147, 2, 140, '"Yoksa siz, İbrahim de, İsmail de, İshak da, Yakup da ve torunları da hep yahudi ve hıristiyan idiler mi demek istiyorsunuz?" De ki: "Siz mi daha iyi bilirsiniz, yoksa Allah mı?" Allah\'ın şahitlik ettiği bir hakikatı bile bile inkar edenden daha zâlim kim olabilir? Allah, yaptıklarınızdan gafil değildir.'),
(148, 2, 141, 'Onlar bir ümmet idiler, gelip geçtiler. Onlara kendi kazandıkları, size de kendi kazandıklarınız. Ve siz onların yaptıklarından sorumlu tutulacak değilsiniz.'),
(149, 2, 142, 'İnsanlar içinde bir kısım beyinsizler takımı, "Bunları bulundukları kıbleden çeviren nedir?" diyecekler. De ki: "Doğu da, batı da Allah\'ındır. O, kimi dilerse onu hidayete erdirir."'),
(150, 2, 143, 'Ve işte böyle, sizi ortada yürüyen bir ümmet kıldık ki, siz bütün insanlar üzerine adalet örneği ve hakkın şahitleri olasınız, Peygamber de sizin üzerinize şahit olsun. Daha önce içinde durduğun Kâ\'be\'yi kıble yapmamız da şunun içindir: Peygamber\'in izince gidecekleri, iki ökçesi üzerinde geri döneceklerden ayıralım. Bu iş elbette Allah\'ın hidayet ettiği kimselerin dışındakilere çok ağır gelecekti. Allah imanınızı kaybedecek değildir. Hiç şüphesiz Allah, bütün insanlara çok şefkatlidir, çok merhametlidir.'),
(151, 2, 144, 'Doğrusu, biz, yüzünün semaya yöneldiğini, orada şekilden şekile geçerek, aranıp durduğunu görüyorduk. Artık seni hoşnud olacağın bir kıbleye çevireceğiz. Haydi bakalım, yüzünü Mescidi Haram\'a doğru çevir. Siz de ey müminler, nerede olursanız olun, yüzünüzü o tarafa doğru çevirin! Kendilerine kitap verilmiş olanlar da kesinlikle bilirler ki, Rabblerinden gelen o emir haktır. Ve Allah, onların yaptıklarından ve yapmakta olduklarından gafil değildir.'),
(152, 2, 145, 'Celâlim için, sen o kitap verilmiş olanlara, bütün delilleri de getirsen, yine de senin kıblene tabi olmazlar, sen de onların kıblesine tabi olmazsın. Zaten onlar da birbirlerinin kıblesine tabi değiller. Celâlim hakkı için, sana gelen bunca ilmin arkasından sen tutar da onların arzu ve heveslerine uyacak olursan, o zaman hiç şüphesiz, sen de zâlimlerden olursun.'),
(153, 2, 146, 'O kendilerine kitap verdiğimiz ümmetlerin âlimleri onu o peygamberi oğullarını tanır gibi tanırlar, böyle iken içlerinden bir takımı gerçeği bile bile gizlerler.'),
(154, 2, 147, 'O hak, Rabbindendir. Artık şüpheye düşenlerden olma sakın!'),
(155, 2, 148, 'Ümmetlerden her birinin bir yönü vardır, o ona yönelir, haydin, hep hayırlara koşun, yarışın. Her nerede olsanız Allah sizi toplar, bir araya getirir. Şüphesiz ki Allah her şeye kâdirdir.'),
(156, 2, 149, 'Hem her nereden yola çıkarsan (namazda) hemen Mescidi Haram\'a doğru yüzünü çevir. Bu emir şüphesiz hak, Rabbinden olduğu gerçektir. Allah yaptıklarınızdan habersiz de değildir.'),
(157, 2, 150, 'Her nereden yola çıkarsan yüzünü Mescidi Haram\'a doğru çevir, ve her nerede olsanız yüzünüzü ona doğru çevirin ki insanlar için aleyhinizde bir delil olmasın. Ancak içlerinden haksızlık edenler başka. Siz de onlardan korkmayın, benden korkun. Hem üzerinizdeki nimetimi tamamlayayım, hem gerek ki doğru yolu bulasınız.'),
(158, 2, 151, 'Nitekim içinizden size bir peygamber gönderdik. O size âyetlerimizi okuyor, sizi temizliyor, size kitabı ve hikmeti öğretiyor. Size bilmediğiniz şeyleri öğretiyor.'),
(159, 2, 152, 'O halde beni anın, ben de sizi anayım. Bana şükredin de nankörlük etmeyin.'),
(160, 2, 153, 'Ey iman edenler! Sabır ve namazla yardım isteyin. Şüphe yok ki Allah, sabredenlerle beraberdir.'),
(161, 2, 154, 'Allah yolunda öldürülenlere "ölüler" demeyin. Hayır, onlar diridirler. Fakat siz sezemezsiniz.'),
(162, 2, 155, 'Çaresiz biz sizi biraz korku, biraz açlık, biraz da mallardan, canlardan ve ürünlerden eksiltme ile imtihan edeceğiz. Müjdele o sabredenleri!'),
(163, 2, 156, 'Onlar başlarına bir musibet geldiği zaman: "Biz Allah\'a aidiz ve sonunda O\'na döneceğiz." derler.'),
(164, 2, 157, 'İşte onlar var ya, Rablerinden, mağfiretler ve rahmet onlaradır. İşte hidayete erenler de onlardır.'),
(165, 2, 158, 'Gerçekten Safâ ile Merve Allah\'ın alâmetlerindendir. Onun için her kim hac veya umre niyetiyle Kâ\'be\'yi ziyaret ederse, bunları tavaf etmesinde ona bir günah yoktur. Her kim de gönlünden koparak bir hayır işlerse, şüphesiz Allah iyiliğin karşılığını verir, o her şeyi bilir.'),
(166, 2, 159, 'İndirdiğimiz apaçık delilleri ve hidayetin kendisi olan âyetleri insanlar için biz kitapta açıkladıktan sonra gizleyenler var ya mutlaka onlara Allah lanet eder. Lanet edebilecek olanlar da lanet ederler.'),
(167, 2, 160, 'Ancak tevbe edip halini düzelterek gerçeği söyleyenler başka. İşte onları ben bağışlarım. Ben çok merhamet ediciyim, tevbeleri çokça kabul ederim.'),
(168, 2, 161, 'Ama âyetlerimizi inkar etmiş ve kâfir olarak can vermiş olanlara gelince, işte Allah\'ın laneti, meleklerin laneti ve insanların laneti hep onların üzerine olsun.'),
(169, 2, 162, 'Onlar ebedi olarak onun altında kalırlar. Ne azabları hafifletilir, ne de kendilerine göz açtırılır.'),
(170, 2, 163, 'Her halde hepinizin ilâhı, bir tek ilâhtır. Ondan başka bir ilâh yoktur. O Rahmân ve Rahîm\'dir.'),
(171, 2, 164, 'Şüphesiz göklerin ve yerin yaratılışında, gece ile gündüzün birbiri ardınca gelişinde, insanlara yarar şeylerle denizde akıp giden gemide, Allah\'ın yukarıdan bir su indirip de onunla yeri ölümünden sonra diriltmesinde, diriltip de üzerinde deprenen hayvanları yaymasında, rüzgarları değiştirmesinde, gök ile yer arasında emre hazır olan bulutta şüphesiz akıllı olan bir topluluk için elbette Allah\'ın birliğine deliller vardır.'),
(172, 2, 165, 'İnsanlardan kimi de Allah\'tan başka şeyleri O\'na eş tutuyorlar da onları, Allah\'ı sever gibi seviyorlar. Oysa iman edenlerin Allah sevgisi daha kuvvetlidir. O zulmedenler, azabı görecekleri zaman bütün kuvvetin Allah\'a ait olduğunu ve Allah\'ın azabının gerçekten çok şiddetli bulunduğunu keşke anlasalardı.'),
(173, 2, 166, 'O zaman kendilerine uyulan kimseler, azabı görerek kendilerine uyanlardan kaçıp uzaklaşmışlar ve aralarındaki bütün bağlar parça parça kopmuştur.'),
(174, 2, 167, 'Onlara uyanlar da şöyle demektedirler: "Ah, bizim için dünyaya bir dönüş olsaydı da onların bizden uzaklaştıkları gibi biz de onlardan uzaklaşsaydık!" İşte böylece Allah onlara bütün amellerini, üzerlerine yığılmış hasretler (pişmanlık ve üzüntüler) halinde gösterecektir. Onlar bu ateşten çıkacak değillerdir.'),
(175, 2, 168, 'Ey insanlar! Bütün yeryüzündeki nimetlerimden helal olmak, temiz olmak şartıyla yiyin. Fakat şeytanın adımlarına uymayın. Çünkü o size belli bir düşmandır.'),
(176, 2, 169, 'O size hep çirkin ve murdar işleri emreder, Allah\'a karşı bilmediğiniz şeyler söylemenizi ister.'),
(177, 2, 170, 'Onlara: "Allah\'ın indirdiğine uyun." dendiği vakit de: "Yok, atalarımızı neyin üzerinde bulduysak ona uyarız." dediler. Ya ataları bir şeye akıl erdiremez ve doğruyu seçemez idiyseler de mi onlara uyacaklar?'),
(178, 2, 171, 'O kâfirlerin hali, sadece bir çağırma veya bağırmadan başkasını işitmeyerek haykıranın haline benzer; onlar sağırdırlar, dilsizdirler, kördürler, akıl da etmezler.'),
(179, 2, 172, 'Ey iman edenler! Size kısmet ettiğimiz rızıkların hoş ve temiz olanlarından yiyin ve Allah\'a şükredin, eğer yalnız O\'na kulluk ediyorsanız.'),
(180, 2, 173, 'O, size yalnız şunları haram kıldı: Ölü hayvan, kan, domuz eti, bir de Allah\'tan başkası adına kesilen hayvanlar. Sonra kim bunlardan yemeye mecbur kalırsa, başkasının hakkına tecavüz etmemek ve zaruret ölçüsünü geçmemek şartıyla ona da bir günah yükletilmez. Çünkü Allah çok bağışlayıcıdır, çok merhametlidir.'),
(181, 2, 174, 'Allah\'ın indirdiği kitaptan bir şeyi gizleyip de bununla biraz para alanlar gerçekten karınları dolusu ateşten başka birşey yemezler. Kıyamet günü Allah onlara ne söz söyler, ne de kendilerini temize çıkarır. Onlara sadece acı veren bir azab vardır.'),
(182, 2, 175, 'İşte onlar, hidayeti verip sapıklığı, affedilmeyi bırakıp azabı satın alan kimselerdir. Bunlar, ateşe karşı ne kadar da sabırlıdırlar!'),
(183, 2, 176, 'Şüphesiz ki Allah kitabı hak bir sebeple indirmiştir. Kitap hakkında ihtilafa düşenler ise, şüphesiz haktan uzak, bir anlaşmazlık içindedirler.'),
(184, 2, 177, 'Yüzlerinizi bazan doğu, bazan batı tarafına çevirmeniz erginlik değildir. Fakat eren o kimselerdir ki, Allah\'a, ahiret gününe, meleklere, kitaba ve bütün peygamberlere iman edip, yakınlığı olanlara, öksüzlere, yoksullara, yolda kalmışa, dilenenlere ve esirleri kurtarmaya seve seve mal verirler. Namazı kılarlar, zekatı verirler. Bir de andlaştıkları zaman sözlerini yerine getirenler, hele sıkıntı ve hastalık durumlarında ve harbin şiddetli zamanında sabır ve kararlılık gösterenler var ya, işte doğru olanlar da bunlardır, korunanlar da bunlardır.'),
(185, 2, 178, 'Ey iman edenler! Öldürmede kısas size farz kılındı. Hüre hür, köleye köle, kadına kadın. Ama her kim, ölenin kardeşi tarafından bir şey karşılığı bağışlanırsa, o zaman örfe uyması, ona diyeti güzellikle ödemesi gerekir. Bu, Rabbiniz tarafından bir hafifletme ve bir rahmettir. Her kim bunun arkasından yine saldırırsa, artık ona acı veren bir azab vardır.'),
(186, 2, 179, 'Ey temiz akıl sahipleri! Kısasta sizin için bir hayat vardır. Ümit edilir ki, korunursunuz.'),
(187, 2, 180, 'Birinize ölüm geldiği vakit, bir hayır (bir mal) bırakacaksa, babası, anası ve en yakın akrabası için meşru bir surette vasiyet etmek, Allah\'tan korkan kimseler üzerine yerine getirilmesi vacib bir hak olarak size farz kılındı.'),
(188, 2, 181, 'Şimdi her kim, bunu duyduktan sonra onu değiştirirse, her halde vebali, sırf o değiştirenlerin boynunadır. Şüphe yok ki Allah, her şeyi işitir ve bilir.'),
(189, 2, 182, 'Her kim de vasiyet edenin, bir hata işlemesinden veya bir günaha girmesinden endişe eder de tarafların arasını düzeltirse, ona bir vebal yoktur. Şüphesiz ki, Allah çok bağışlayıcıdır, çok merhamet edicidir.'),
(190, 2, 183, 'Ey iman edenler! Oruç, sizden öncekilere farz kılındığı gibi size de farz kılındı. Umulur ki korunursunuz.'),
(191, 2, 184, '(Size farz kılınan oruç), sayılı günlerdedir. İçinizden hasta olan veya yolculukta bulunan ise, diğer günlerde, tutamadığı günler sayısınca tutar. Ona dayanıp kalacaklar üzerine de bir yoksulu doyuracak kadar fidye gerekir. Her kim de hayrına fidyeyi artırırsa, hakkında daha hayırlıdır. Bununla beraber, eğer bilirseniz, oruç tutmanız sizin için daha hayırlıdır.'),
(192, 2, 185, 'O Ramazan ayı ki, insanları irşad için, hak ile batılı ayıracak olan, hidayet rehberi ve deliller halinde bulunan Kur\'ân onda indirildi. Onun için sizden her kim bu aya şahit olursa onda oruç tutsun. Kim de hasta, yahut yolculukta ise tutamadığı günler sayısınca diğer günlerde kaza etsin. Allah size kolaylık diler zorluk dilemez. Sayıyı tamamlamanızı, size doğru yolu gösterdiğinden dolayı Allah\'ı tekbir etmenizi ister. Umulur ki şükredersiniz.'),
(193, 2, 186, 'Şayet kullarım, sana benden sordularsa, gerçekten ben çok yakınımdır. Bana dua edince, duacının duasını kabul ederim. O halde onlar da benim davetime koşsunlar ve bana hakkıyla iman etsinler ki, doğru yola gidebilsinler.'),
(194, 2, 187, 'Oruç gecesi kadınlarınıza yaklaşmanız, size helâl kılındı. Onlar, sizin için bir örtü, siz de onlar için bir örtü durumundasınız. Allah, nefsinize güvenemeyeceğinizi bildiği için müracaatınızı kabul buyurdu ve sizi bağışladı. Şimdi onlara yaklaşın ve Allah\'ın sizler için yazdığını isteyin. Ta fecrin beyaz ipliği siyah iplikden size seçilinceye kadar yiyin, için. Sonra da ertesi geceye kadar orucu tam tutun. Bununla beraber siz mescitlerde îtikaf halinde iken onlara yaklaşmayın. Bunlar, Allah\'ınsınırlarıdır, sakın onlara yaklaşmayın. Allah, âyetlerini insanlara böyle açıklıyor ki sakınıp korunsunlar.'),
(195, 2, 188, 'Bir de aranızda mallarınızı batıl sebeplerle yemeyin. İnsanların mallarından bir kısmını bile bile günah ile yemek için, o malları hakimlere rüşvet olarak vermeyin.'),
(196, 2, 189, 'Sana hilâllerden soruyorlar. De ki: Onlar insanlar için de, hac için de vakit ölçüleridir. Bununla beraber iyilik, evlere arkalarından gelmeniz değildir. Fakat iyiliğe eren, kötülükten korunan kimsedir. Evlere kapılarından gelin, Allah\'tan korkun ki, kurtuluşa eresiniz.'),
(197, 2, 190, 'Size savaş açanlarla Allah yolunda çarpışın. Fakat haksız saldırıda bulunmayın. Çünkü Allah, haksız saldırıda bulunanları sevmez.'),
(198, 2, 191, 'Onları nerede yakalarsanız öldürün ve sizi çıkardıkları yerden onları çıkarın. O fitne, öldürmeden daha şiddetlidir. Yalnız Mescidi Haram yanında onlar sizinle savaşmadıkça siz de onlarla savaşmayın. Fakat sizi öldürmeye kalkışırlarsa, hemen onları öldürün. Kâfirlerin cezası böyledir.'),
(199, 2, 192, 'Artık şirkten vazgeçerlerse, şüphesiz ki Allah çok bağışlayıcıdır, çok merhamet edicidir.'),
(200, 2, 193, 'Hem bir fitne kalmayıp, din yalnız Allah\'ın oluncaya kadar onlarla çarpışın. Vazgeçerlerse, düşmanlık ancak zalimlere karşıdır.'),
(201, 2, 194, 'Hürmetli ay hürmetli aya ve bütün hürmetler birbirine karşılıktır. O halde kim size saldırdıysa, siz de ona yaptığı saldırının aynıyle saldırın da ileri gitmeye Allah\'tan korkun ve bilin ki Allah, takva sahipleriyle beraberdir.'),
(202, 2, 195, 'Allah yolunda mal harcayın da kendinizi ellerinizle tehlikeye bırakmayın ve güzel hareket edin. Çünkü Allah güzellik ve iyilik edenleri sever.'),
(203, 2, 196, 'Hac ve umreyi de Allah için tamam yapın. Eğer bunlardan alıkonursanız, o zaman kolayınıza gelen bir kurban gönderin. Bununla beraber bu kurban, kesileceği yere varıncaya kadar başlarınızı tıraş etmeyin. İçinizden hasta olana veya başından bir rahatsızlığı bulunana tıraş için oruç veya sadaka yahut da kurbandan ibaret bir fidye gerekir. Engellemeden kurtulduğunuz zaman da her kim hacca kadar umre ile sevab kazanmak isterse, ona da kolayına gelen bir kurban gerekir. Bunu bulamayana ise üç gün hacda, yedi de döndüğünüzde ki tam on gün oruç tutması lazım gelir. Bu hüküm, ailesi Mescidi Haram civarında oturmayanlar içindir. Allah\'tan korkun ve bilin ki Allah\'ın azabı gerçekten çok şiddetlidir.'),
(204, 2, 197, 'Hac, bilinen aylardadır. Her kim o aylarda hacca başlayıp kendisine farz ederse; artık hacda kadına yaklaşmak, günah işlemek ve kavga etmek yoktur. Siz hayırdan ne işlerseniz, Allah onu bilir. Kendinize azık edinin. Şüphesiz ki azıkların en hayırlısı Allah korkusudur. Ey akıl sahipleri! Benden korkun!'),
(205, 2, 198, 'Rabbinizin lütfunu istemenizde size bir günah yoktur. Arafat\'tan indiğiniz zaman Meş\'ari Haram yanında (Müzdelife\'de) Allah\'ı zikredin. O\'nu, size gösterdiği şekilde zikredin. Doğrusu siz, bundan önce gerçekten sapmışlardandınız.'),
(206, 2, 199, 'Sonra insanların akıp geldiği yerden siz de akıp gelin. Allah\'tan bağışlanmanızı isteyin. Çünkü Allah çok bağışlayıcıdır, çok merhamet edicidir.'),
(207, 2, 200, 'Nihayet hac ibadetlerinizi bitirdiğiniz zaman, önceleri babalarınızı andığınız gibi, hatta daha kuvvetli bir anışla Allah\'ı anın. İnsanlardan kimisi: "Ey Rabbimiz! Bize dünyada ver!" der. Onun için ahirette hiçbir kısmet yoktur.'),
(208, 2, 201, 'Yine onlardan: "Ey Rabbimiz! Bize dünyada bir güzellik ve ahirette de bir güzellik ver ve bizi ateş azabından koru!" diyenler vardır.'),
(209, 2, 202, 'İşte onlar için, kazandıklarından bir nasib vardır. Allah, hesabı çok çabuk görür.'),
(210, 2, 203, 'Bir de sayılı günlerde Allah\'ı zikredin (tekbir alın). Bunlardan kim iki gün içinde (Mina\'dan) dönmek için acele ederse ona günah yoktur. Kim geri kalırsa ona da günah yoktur. Ama bu, takva sahipleri içindir. Allah\'tan korkun ve bilin ki, siz ancak O\'nun huzuruna varıp toplanacaksınız.'),
(211, 2, 204, 'İnsanlardan kimi de vardır ki, dünya hayatı hakkındaki sözleri senin hoşuna gider ve o kalbindekine Allah\'ı şahit tutar. Halbuki O, İslâm düşmanlarının en yamanıdır.'),
(212, 2, 205, 'İş başına geçti mi yeryüzünde bozgunculuk çıkarmak, ekini ve nesli helak etmek için koşar. Allah ise bozgunculuğu sevmez.'),
(213, 2, 206, 'Ona: "Allah\'tan kork!" dendiği zaman da kendisini onuru (gururu) günah işlemeye sevkeder. Cehennem de onun hakkından gelir. O ne kötü bir yataktır!'),
(214, 2, 207, 'Yine insanlardan kimi de vardır ki, Allah\'ın rızasına ermek için kendini feda eder. Allah ise kullarına çok merhametlidir.'),
(215, 2, 208, 'Ey iman edenler! Hepiniz barış ve selamete girin de şeytanın adımlarına uymayın. Çünkü o sizin aranızı açan belli bir düşmandır.'),
(216, 2, 209, 'Size bunca deliller geldikten sonra yine kayarsanız, iyi bilin ki, Allah çok güçlüdür, hüküm ve hikmet sahibidir.'),
(217, 2, 210, 'Onlar sadece gözetiyorlar ki, Allah, buluttan gölgelikler içinde meleklerle birlikte geliversin de iş bitiriliversin. Halbuki bütün işler Allah\'a döndürülüp götürülür.'),
(218, 2, 211, 'İsrailoğullarına sor: Biz onlara ne kadar açık âyetler vermiştik. Fakat Allah\'ın nimetini her kim kendisine geldikten sonra değiştirirse, şüphe yok ki, Allah\'ın azabı çok şiddetlidir.'),
(219, 2, 212, 'Dünya hayatı, inkar edenler için bezendi. (Onlar), iman edenlerle eğleniyorlar. Halbuki takva sahibi olan o müminler, kıyamet günü onların üstündedir. Allah dilediğine hesapsız rızık verir.'),
(220, 2, 213, 'İnsanlar tek bir ümmetti. Ayrılmaları üzerine Allah, rahmetinin müjdecileri ve azabının habercileri olmak üzere peygamberler gönderdi ve beraberlerinde hak ile ilgili kitap indirdi ki, insanların, aralarında ihtilaf ettikleri şeyler hakkında hakem olsun. Bunda da sırf o kitap verilenler, kendilerine bunca deliller geldikten sonra tuttular, aralarındaki hırs ve kıskançlık yüzünden anlaşmazlığa düştüler. Bunun üzerine Allah kendi izniyle, iman edenleri, onların hakkında anlaşmazlığa düştükleri hakka, ulaştırdı. Allah, dilediğini doğru yola iletir.'),
(221, 2, 214, 'Yoksa siz, kendinizden önce gelip geçenlerin hali (uğradıkları sıkıntılar) başınıza gelmeden cennete girivereceğinizi mi sandınız? Onlara öyle yoksulluklar, öyle sıkıntılar dokundu ve öyle sarsıldılar ki, hatta peygamber ve beraberinde iman edenler: "Allah\'ın yardımı ne zaman?" derlerdi. Bak işte! Gerçekten Allah\'ın yardımı yakındır.'),
(222, 2, 215, 'Ey Muhammed! Sana nereye infak edeceklerini soruyorlar. De ki: Hayır olarak verdiğiniz nafaka, ana baba, yakınlar, öksüzler, yoksullar ve yolda kalmışlar içindir. Hayır olarak daha ne yaparsanız herhalde Allah onu bilir.'),
(223, 2, 216, 'Savaş size farz kılındı, gerçi o size hoş gelmez. Olabilir ki siz, bir şeyden hoşlanmazsınız; oysa ki o sizin için bir hayırdır. Yine olabilir ki, siz bir şeyi seversiniz, oysaki o sizin için bir kötülüktür. Allah bilir, siz bilmezsiniz.'),
(224, 2, 217, 'Ey Muhammed! Sana haram aydan ve o ayda savaşmaktan soruyorlar. De ki: O ayda savaşmak, büyük bir günahtır. Bununla beraber Allah yolundan alıkoymak, O\'nu inkar etmek, insanları, Mescidi Haram\'dan menetmek ve halkını oradan çıkarmak, Allah yanında daha büyük bir günahtır ve fitne, öldürmekten daha büyük bir vebaldir. Onlar, güçleri yeterse, sizi dininizden döndürmek için sizinle savaşmaktan hiçbir zaman geri durmazlar. Sizden de her kim, dininden döner ve kâfir olarak can verirse artık onların bütün amelleri, dünyada ve ahirette boşa gitmiştir. İşte onlar, cehennemliklerdir. Onlar orada ebedi olarak kalacaklardır.'),
(225, 2, 218, 'Şüphesiz ki iman edenlere, Allah yolunda hicret edip, cihad edenlere gelince, işte onlar, Allah\'ın rahmetini umarlar. Allah, çok bağışlayıcıdır, çok merhamet edicidir.'),
(226, 2, 219, 'Ey Muhammed! Sana şarap ve kumardan soruyorlar. De ki: Bu ikisinde büyük bir günah, bir de insanlar için bazı menfaatler vardır. Fakat günahları, menfaatlerinden daha büyüktür. Yine sana neyi infak edeceklerini soruyorlar. De ki: İhtiyaçtan fazlasını infak edin. İşte böylece Allah, size âyetlerini açıklıyor. Umulur ki siz düşünürsünüz.'),
(227, 2, 220, 'Dünya ve ahiret hakkında (düşünürsünüz.) Sana bir de yetimlerden soruyorlar. De ki: Onlar hakkında yapacağınız bir ıslah, işlerine karışmamaktan daha hayırlıdır. Eğer onlara karışırsanız, onlar sizin kardeşlerinizdir. Allah, bozguncuyla ıslah ediciyi bilir, birbirinden ayırd eder. Eğer Allah dileseydi, sizi zora koşardı. Şüphesiz ki Allah çok güçlüdür, hüküm ve hikmet sahibidir.'),
(228, 2, 221, 'Müşrik kadınları, iman etmedikçe nikâhlamayın. Bir müşrik kadın, sizin hoşunuza gitse bile, iman etmiş olan bir cariye herhalde ondan daha hayırlıdır. Müşrik erkeklere de mümin kadınları nikâh ettirmeyin. Bir müşrik, sizin hoşunuza gitse bile, mümin bir köle elbette ondan daha hayırlıdır. Onlar sizi ateşe davet ederler, Allah ise, kendi izniyle cennete ve mağfirete davet ediyor ve âyetlerini insanlara açıklıyor. Umulur ki onlar hatırda tutup, öğüt alırlar.'),
(229, 2, 222, 'Ey Muhammed! Sana kadınların ay başı halinden de soruyorlar. De ki: O bir eziyettir Onun için ay başı halinde oldukları zaman kadınlardan çekilin ve temizleninceye kadar onlara yaklaşmayın. İyice temizlendikleri zaman ise Allah\'ın emrettiği yerden onlara varın, yaklaşın Şüphesiz ki Allah çok tövbe edenleri de sever, çok temizlenenleri de sever.'),
(230, 2, 223, 'Kadınlarınız, sizin için bir tarladır. O halde tarlanıza dilediğiniz gibi varın ve kendiniz için ileriye hazırlık yapın. Allah\'tan korkun ve bilin ki siz mutlaka O\'nun huzuruna varacaksınız. Ey Muhammed, müminleri müjdele!'),
(231, 2, 224, 'Sözünüzde durmanız, kötülükten sakınmanız ve insanların arasını düzeltmeniz için, Allah\'ı yeminlerinize hedef veya siper edip durmayın. Allah, her şeyi işitir ve bilir.'),
(232, 2, 225, 'Allah, sizi yeminlerinizde bilmeyerek ettiğiniz lağıv (herhangi bir kasıt olmadan, kanaate göre yanlış yere yapılan yemin)dan sorumlu tutmaz. Fakat kalbinizin kazandığı yalan yere yapılan yeminden sorumlu tutar. Allah çok bağışlayıcıdır, çok halimdir.'),
(233, 2, 226, 'Kadınlarından îlâ edenler (onlara yaklaşmamaya yemin edenler) için dört ay beklemek vardır. Eğer bu yeminlerinden dönerlerse, şüphesiz ki Allah çok bağışlayıcıdır, çok merhamet edicidir.'),
(234, 2, 227, 'Yok eğer boşamaya karar vermişlerse, şüphesiz ki Allah söylediklerini işitir, kurduklarını bilir.'),
(235, 2, 228, 'Boşanan kadınlar, kendi kendilerine üç adet süresi beklerler ve Allah\'ın rahimlerinde yarattığını gizlemeleri, kendilerine helâl olmaz. Eğer Allah\'a ve ahiret gününe inanıyorlarsa gizlemezler. Kocaları da, barışmak istedikleri takdirde o süre içersinde onları geri almaya daha layıktırlar. O kadınların, üzerlerindeki meşru hak gibi, kendilerinin de hakları vardır. Yalnız erkekler için, onların üzerinde bir derece vardır. Allah çok güçlüdür, hüküm ve hikmet sahibidir.'),
(236, 2, 229, 'Boşamak (talak) iki defadır. Ondan sonrası ya iyilikle tutmak veya güzellikle salmaktır. Onlara verdiklerinizden bir şey almanız da size helâl olmaz. Ancak Allah\'ın çizdiği hudutta duramayacaklarından korkmaları başka. Eğer siz de bunların, Allah\'ın çizdiği hudutta duramayacaklarından korkarsanız, kadının, ayrılmak için hakkından vazgeçmesinde artık ikisine de günah yoktur. İşte bunlar, Allah\'ın çizdiği hudududur. Sakın bunları aşmayın, Her kim Allah\'ın hududunu aşarsa, işte onlar zalimlerdir.'),
(237, 2, 230, 'Eğer kadını bir daha boşarsa, bundan sonra artık başka bir kocaya varıncaya kadar ona helâl olmaz. Eğer ikinci koca da onu boşarsa, Allah\'ın hududunu sağlam tutacaklarını ümid ettikleri takdirde öncekilerin birbirlerine dönmelerinde her ikisine de günah yoktur. İşte bunlar, Allah\'ın tayin ettiği hudududur. Bunları, bilen bir kavim için açıklıyor.'),
(238, 2, 231, 'Kadınları boşadığınız zaman iddetlerini bitirdiklerinde, artık kendilerini ya iyilikle tutun veya güzellikle salın. Yoksa haklarına tecavüz için zararlarına olarak onları tutmayın. Her kim bunu yaparsa nefsine zulmetmiş olur. Sakın Allah\'ın âyetlerini alay konusu edinmeyin, Allah\'ın üzerinizdeki nimetini, size kendisiyle öğüt vermek üzere indirdiği kitap ve hikmeti hatırlayıp, düşünün. Hem Allah\'tan korkun ve bilin ki Allah her şeyi bilir.'),
(239, 2, 232, 'Kadınları boşadığınız zaman iddetlerini bitirdiklerinde, aralarında meşru bir şekilde rızalaştıkları takdirde, kendilerini kocalarıyla nikâhlanacaklar diye sıkıştırıp, engellemeyin. İşte bu, içinizden Allah\'a ve ahiret gününe iman edenlere verilen bir öğüttür. Bu, sizin hakkınızda daha hayırlı ve daha nezihtir. Allah bilir, siz bilemezsiniz.'),
(240, 2, 233, 'Anneler, çocuklarını, emzirmenin tamamlanmasını isteyenler için tam iki yıl emzirirler. Çocuk kendisine ait olan babaya da emzirenlerin yiyecekleri ve giyecekleri geleneklere uygun olarak bir borçtur. Bununla beraber herkes ancak gücüne göre mükellefolur. Çocuğu sebebiyle bir anne de, çocuğu sebebiyle bir baba da zarara sokulmasın. Varise düşen de yine aynı borçtur. Eğer ana ve baba birbirleriyle istişare edip, her ikisinin de rızasıyla çocuğu memeden ayırmak isterlerse kendilerine bir günah yoktur. Eğer çocuklarınızı başkalarına emzirtmek isterseniz vereceğinizi güzel güzel verdikten sonra bunda da size bir günah yoktur. Bununla beraber Allah\'tan korkun ve bilin ki, Allah yaptıklarınızı görür.'),
(241, 2, 234, 'İçinizden vefat edip de geride eşler bırakan kimselerin hanımları, kendi başlarına dört ay on gün beklerler. İddet (bekleme) sürelerini bitirdikleri zaman, artık kendileri hakkında meşru bir şekilde yapacakları hareketten size bir günah yoktur. Allah, yaptıklarınızdan haberdardır.'),
(242, 2, 235, 'Böyle kadınlara evlenme isteğinizi üstü kapalı biçimde çıtlatmanızda veya gönlünüzde tutmanızda size bir vebal yoktur. Allah biliyor ki siz onları mutlaka anacaksınız. Fakat meşru bir söz söylemekten başka bir şekilde kendileriyle gizlice sözleşmeyin. Farz olan iddet sona erinceye kadar da nikâh akdine azmetmeyin (kesin karar vermeyin). Bilin ki Allah gönlünüzdekini bilir. Öyle ise O\'nun azabından sakının. Yine bilin ki Allah çok bağışlayıcıdır, çok yumuşaktır.'),
(243, 2, 236, 'Eğer kadınları, kendilerine dokunmadan veya onlara bir mehir takdir etmeden boşarsanız (bunda) size bir vebal yoktur. Şu kadar ki onlara (mal verip) faydalandırın. Eli geniş olan hâline göre, eli dar olan da haline göre ve güzellikle faydalandırmalıdır. Bu, iyilik yapanlar üzerine bir borçtur.'),
(244, 2, 237, 'Eğer onları, kendilerine dokunmadan önce boşar ve mehri de kesmiş bulunursanız, o zaman borç, o kestiğiniz miktarın yarısıdır. Ancak kadınlar veya nikâh akdini elinde bulunduran kimse bağışlarsa başka. Ey erkekler! sizin bağışlamanız ise takvaya daha yakındır. Aranızdaki fazileti unutmayın şüphesiz ki Allah, her ne yaparsanız hakkiyle görür.'),
(245, 2, 238, 'Namazlara ve orta namaza devam edin ve Allah için boyun eğerek kalkıp namaza durun.'),
(246, 2, 239, 'Eğer bir korku hâlindeyseniz, yaya veya binekli olarak giderken kılın, (korkudan) emin olduğunuz zaman da böyle bilmediğiniz şeyleri size öğrettiği şekilde Allah\'ı zikredin (namazlarınızı yine her zamanki gibi huşû ile kılın).'),
(247, 2, 240, 'İçinizden hanımlarını geride bırakarak vefat edecek olanlar, eşleri için senesine kadar evlerinden çıkarılmaksızın kendilerine yetecek bir malı vasiyet ederler. Bununla birlikte eğer kendileri çıkarlarsa, kendi haklarında yaptıkları meşru bir hareketten dolayı size bir sorumluluk yoktur. Allah çok güçlüdür, hüküm ve hikmet sahibidir.'),
(248, 2, 241, 'Boşanmış kadınlar için de meşru ve geleneğe uygun şekilde bir meta\'(intifa hakkı) vardır ki verilmesi, Allah\'tan korkanlar üzerine bir borçtur.'),
(249, 2, 242, 'İşte akıllarınız ersin diye, Allah size âyetlerini böylece açıklıyor.'),
(250, 2, 243, 'Görmedin mi o kimseleri ki kendileri binlerce kişi iken ölüm korkusuyla yurtlarından çıktılar. Allah da kendilerine "ölün!" dedi, sonra da onlara bir hayat verdi. Şüphesiz ki Allah, insanlara karşı bir lütuf sahibidir. Fakat insanların pek çokları şükretmezler.'),
(251, 2, 244, 'O halde Allah yolunda çarpışın ve bilin ki Allah, her şeyi işitir ve bilir.'),
(252, 2, 245, 'Kimdir o adam ki Allah\'a güzel bir ödünç versin de Allah da ona birçok katlarını ödesin. Allah darlık da verir, genişlik de verir. Hepiniz de O\'na döndürülüp götürüleceksiniz.'),
(253, 2, 246, 'Baksana, İsrail oğullarının Musa\'dan sonra ileri gelenlerine! Hani onlar, bir peygamberlerine: "Bize bir kumandan gönder de Allah yolunda savaşalım..." dediler. O da: "Size savaş farz kılınırsa, acaba yapmamazlık eder misiniz?" dedi. Onlar: "Bize ne oldu da yurtlarımızdan çıkarıldığımız ve çocuklarımızdan ayrıldığımız halde Allah yolunda savaşmayalım?" dediler. Bunun üzerine savaş kendilerine farz kılınınca da onlardan pek azı hariç, yüz çevirdiler. Ama Allah, o zalimleri bilir.'),
(254, 2, 247, 'Peygamberleri onlara: "Allah, size hükümdar olmak üzere Talût\'u gönderdi." demişti. Onlar: "Ona bizim üzerimize hükümdar olmak nereden geldi? Oysa hükümdarlığa biz ondan daha lâyıkız, ona maldan bir genişlik, bir bolluk da verilmemiştir." dediler. Peygamberleri de "Onu sizin başınıza Allah seçmiş ve ona bilgi ve vücut bakımından bir güç, bir genişlik vermiştir." dedi. Hem Allah, mülkünü dilediğine verir. Allah\'ın rahmeti geniştir, o her şeyi bilir.'),
(255, 2, 248, 'Peygamberleri, onlara şunu da söylemişti: Haberiniz olsun, Onun hükümdarlığının alâmeti, size o tabutun gelmesi olacaktır ki onda Rabbinizden bir sekine (sükûnet, gönül rahatlığı), Musa ve Harun ailelerinin bıraktıklarından bir bakiyye (kalıntı) vardır. Onu melekler getirecektir. Eğer iman etmiş kimselerden iseniz, bunda sizin için kesin bir ibret, bir alâmet vardır.'),
(256, 2, 249, 'Talut, ordu ile hareket edince dedi ki: "Allah sizi mutlaka bir nehirle imtihan edecek. Kim ondan içerse, benden değildir. Kim de onu tatmazsa, işte o bendendir. Ancak eliyle bir avuç alan başka (bu kadarına ruhsat vardır)." Derken içlerinden pek azı hariç, hepsi de varır varmaz ondan içtiler. Talut ve beraberindeki iman eden kimseler nehri geçtiklerinde. "Bizim bugün, Calut ile ordusuna karşı duracak gücümüz yok." dediler. Allah\'a kavuşacaklarına inanıp, bilenler ise şu cevabı verdiler: "Nice az topluluklar, Allah\'ın izniyle nice çok topluluklara galip gelmişlerdir. Allah, sabırlılarla beraberdir."'),
(257, 2, 250, 'Calut ve ordusuna karşı savaş meydanına çıktıkları zaman da şöyle dediler: "Ey Rabbimiz! Üzerlerimize sabır dök, ayaklarımızı sabit tut ve kâfirler topluluğuna karşı bize yardım et!"'),
(258, 2, 251, 'Derken, Allah\'ın izniyle onları tamamen bozdular. Davud, Calut\'u öldürdü ve Allah, kendisine hükümdarlık ve hikmet (peygamberlik) verdi ve ona dilediği şeylerden de öğretti. Eğer Allah\'ın, insanları birbirleriyle savması olmasaydı, yeryüzü mutlaka bozulur giderdi. Fakat Allah, bütün âlemlere karşı büyük bir lütuf sahibidir.'),
(259, 2, 252, 'İşte bunlar, Allah\'ın âyetleridir. Onları sana hakkıyla okuyoruz. Şüphesiz ki sen o gönderilen resullerdensin.'),
(260, 2, 253, 'O işaret olunan resuller yok mu, biz onların bazısını, bazısından üstün kıldık. İçlerinden kimi var ki Allah, kendisiyle konuştu, bazısını da derecelerle daha yükseklere çıkardı. Biz Meryem oğlu İsa\'ya da o delilleri verdik ve kendisini Rûhu\'lKudüs (Cebrail) ile kuvvetlendirdik. Eğer Allah dileseydi, bunların arkasındaki ümmetler, kendilerine o deliller geldikten sonra birbirlerinin kanına girmezlerdi. Fakat ihtilâfa düştüler, kimi iman etti, kimi inkâr etti. Yine Allah dileseydi, birbirlerininkanına girmezlerdi. Fakat Allah dilediğini yapar.'),
(261, 2, 254, 'Ey iman edenler! Kendisinde hiçbir alış verişin, hiçbir dostluğun ve hiçbir şefaatin bulunmadığı bir gün gelmeden önce, size verdiğimiz rızıklardan Allah yolunda harcayın. Kâfirlere gelince, onlar zalimlerdir.'),
(262, 2, 255, 'Allah\'tan başka hiçbir ilâh yoktur. O daima diridir (hayydır), bütün varlığın idaresini yürüten (kayyum)dir. O\'nu ne gaflet basar, ne de uyku. Göklerde ve yerde ne varsa hepsi O\'nundur. İzni olmadan huzurunda şefaat edecek olan kimdir? O, kullarının önlerinde ve arkalarında ne varsa hepsini bilir. Onlar ise, O\'nun dilediği kadarından başka ilminden hiç bir şey kavrayamazlar. O\'nun kürsisi, bütün gökleri ve yeri kucaklamıştır. Onların her ikisini de görüp gözetmek O\'na bir ağırlık vermez. O çok yücedir, çok büyüktür.'),
(263, 2, 256, 'Dinde zorlama yoktur. Çünkü doğruluk, sapıklıktan ayırd edilmiştir. Artık her kim tâğutu inkar edip, Allah\'a inanırsa, sağlam bir kulpa yapışmıştır ki, o hiçbir zaman kopmaz. Allah, her şeyi işitir ve bilir.'),
(264, 2, 257, 'Allah, iman edenlerin velisidir. Onları karanlıklardan aydınlığa çıkarır. İnkâr edenlerin velileri de tağuttur, onları aydınlıktan karanlıklara çıkarırlar. İşte onlar cehennemliklerdir. Orada ebedî olarak kalırlar.'),
(265, 2, 258, 'Allah, kendisine hükümdarlık verdi diye, Rabbi hakkında İbrahim\'le tartışanı görmedin mi? Hani İbrahim, ona: "Benim Rabbim odur ki, hem diriltir, hem öldürür." dediği zaman: "Ben de diriltir ve öldürürüm." demişti. İbrahim: "Allah güneşi doğudan getiriyor, haydi sen onu batıdan getir!" deyince o inkâr eden herif şaşırıp kaldı. Öyle ya, Allah zalimler topluluğunu doğru yola iletmez.'),
(266, 2, 259, 'Yahut o kimse gibisini (görmedin mi) ki, bir şehre uğramıştı, altı üstüne gelmiş, ıpıssız yatıyordu. "Bunu bu ölümünden sonra Allah, nerden diriltecek?" dedi. Bunun üzerine Allah onu yüz sene öldürdü, sonra diriltti, "Ne kadar kaldın?" diye sordu. Oda: "Bir gün, yahut bir günden eksik kaldım." dedi. Allah buyurdu ki: "Hayır, yüz sene kaldın, öyle iken bak yiyeceğine, içeceğine henüz bozulmamış, hele eşeğine bak, hem bunlar, seni insanlara karşı kudretimizin bir işareti kılalım diyedir. Hele o kemiklere bak, onları nasıl birbirinin üzerine kaldırıyoruz? Sonra onlara nasıl et giydiriyoruz?" Böylece gerçek ona açıkça belli olunca: "Şimdi biliyorum ki, Allah her şeye kadirdir." dedi.'),
(267, 2, 260, 'Bir zamanlar İbrahim de: "Ey Rabbim! Ölüleri nasıl dirilttiğini bana göster!" demişti. Allah: "İnanmadın mı ki?" buyurdu. İbrahim: "İnandım, fakat kalbim iyice yatışsın diye istiyorum." dedi. Allah buyurdu ki: "Öyle ise kuşlardan dördünü tut da onları kendine çevir, iyice tanıdıktan sonra (kesip) her dağın başına onlardan birer parça dağıt, sonra da onları çağır, koşa koşa sana gelecekler ve bil ki, Allah gerçekten çok güçlüdür, hüküm ve hikmet sahibidir."'),
(268, 2, 261, 'Mallarını Allah yolunda harcayanların durumu, bir tanenin durumu gibidir ki, yedi başak bitirmiş ve her başakta yüz tane var. Allah, dilediğine daha da katlar. Allah\'ın rahmeti geniştir. O, her şeyi bilir.'),
(269, 2, 262, 'Allah yolunda mallarını infak eden, sonra verdiklerinin arkasından başa kakmayı, gönül incitmeyi uygun görmeyen kimselerin Rableri yanında mükafatları vardır. Onlara hiçbir korku yoktur ve onlar, üzülmeyeceklerdir.'),
(270, 2, 263, 'Bir tatlı dil ve kusurları bağışlamak, arkasından eza ve gönül bulantısı gelecek bir sadakadan daha hayırlıdır. Allah, hiçbir şeye muhtaç değildir, halimdir, yumuşak davranır.'),
(271, 2, 264, 'Ey iman edenler! Sadakalarınızı, başa kakmak, gönül kırmakla boşa gidermeyin. O adam gibi ki, insanlara gösteriş için malını dağıtır da ne Allah\'a inanır, ne ahiret gününe. Artık onun hâli, bir kayanın hâline benzer ki, üzerinde biraz toprak varmış, derken şiddetli bir sağnak inmiş de onu yalçın bir kaya halinde bırakıvermiş. Öyle kimseler, kazandıklarından hiçbir şey elde edemezler. Allah, kâfirler topluluğunu doğru yola iletmez.'),
(272, 2, 265, 'Allah\'ın rızasını aramak, kendilerini veya kendilerinden bir kısmını Allah yolunda sabit kılmak için mallarını Allah yolunda harcayanların hâli ise, bir tepedeki güzel bir bahçenin hâline benzer ki, ona kuvvetli bir sağnak düşmüş de yemişlerini iki kat vermiştir. Böyle bir bahçeye yağmur düşmese bile mutlaka bir çisenti vardır. Allah, yaptıklarınızı görür.'),
(273, 2, 266, 'Hiç biriniz ister mi ki, kendisinin hurmalık ve üzümlüklerden bir bahçesi olsun, altında ırmaklar aksın, içinde her türlü ürünü bulunsun da, kendi üzerine de ihtiyarlık çökmüş ve elleri ermez, güçleri yetmez küçük, zayıf çocukları olsun. Derken ona ateşli bir bora isabet ediversin de o bahçe yanıversin. İşte Allah, âyetlerini size böylece açıklıyor. Umulur ki, düşünürsünüz.'),
(274, 2, 267, 'Ey iman edenler! İnfakı gerek kazandıklarınızın, gerek sizin için yerden çıkardıklarımızın temizlerinden yapın. Kendinizin göz yummadan alıcısı olamıyacağınız fenasını vermeye yeltenmeyin. Biliniz ki, Allah sadakalarınıza muhtaç değildir ve hamde layık olandır.'),
(275, 2, 268, 'Şeytan sizi fakirlikle korkutup çirkin çirkin şeylere teşvik eder. Allah da lütfundan ve bağışlamasından birtakım vaatlerde bulunuyor. Allah\'ın lütfu geniştir. O herşeyi bilendir.'),
(276, 2, 269, 'Dilediğine hikmet verir, hikmet verilene ise pek çok hayır verilmiş demektir. Ve bunu ancak üstün akıllılar anlar.'),
(277, 2, 270, 'Her ne çeşit nafaka verdinizse veya ne türlü bir adak adadınızsa, Allah onu kesinlikle bilir. Ve zalimlere hiçbir şekilde yardım olunmayacaktır.'),
(278, 2, 271, 'Sadakaları açıkça verirseniz o, ne iyi olur; yok eğer onları gizler de fakirlere öyle verirseniz bu sizin için daha hayırlıdır ve günahlarınızın birçoğunun bağışlanmasına sebep olur. Bilin ki, Allah, her ne yaparsanız hepsinden haberdardır.'),
(279, 2, 272, 'Onları yola getirmek senin boynuna borç değildir, ancak Allah dilediğini yola getirir. Yaptığınız her iyilik sırf kendiniz içindir. Siz yalnızca Allah rızasını gözetmenin dışında infak etmezsiniz. İyilik cinsinden ne infak ederseniz o size aynen ödenir. Size hiçbir şekilde haksızlık yapılmaz.'),
(280, 2, 273, 'Sadakalarınızı, kendilerini Allah yoluna adamış olan fakirlere veriniz. Onlar yeryüzünde gezip dolaşmaya güç yetiremezler. Utangaç olduklarından dolayı, bilmeyenler, onları zengin sanırlar. Oysa sen onları yüzlerinden tanırsın. Yüzsüzlük yapıp kimseden birşey de isteyemezler. Ne türden bir iyilik yaparsanız, şüphe yok ki, Allah onu bilir.'),
(281, 2, 274, 'Mallarını gece ve gündüz, gizlice ve açıkça infak edenler yok mu, işte onların Rableri katında ecir ve mükafatları vardır. Ve onlara herhangi bir korku yoktur, onlar hiçbir zaman mahzun da olmazlar.'),
(282, 2, 275, 'Riba (faiz) yiyen kimseler, şeytan çarpan kimse nasıl kalkarsa ancak öyle kalkarlar. Bu ceza onlara, "alışveriş de faiz gibidir" demeleri yüzündendir. Oysa Allah, alışverişi helal, faizi de haram kılmıştır. Bundan böyle her kim, Rabbinden kendisine gelen bir öğüt üzerine faizciliğe son verirse, geçmişte olanlar kendisine ve hakkındaki hüküm de Allah\'a kalmıştır. Her kim de yeniden faize dönerse işte onlar cehennem ehlidirler ve orada süresiz kalacaklardır.'),
(283, 2, 276, 'Allah faizi mahveder, oysa sadakaları bereketlendirir. Allah günahta ve inkârda direnen hiç kimseyi sevmez.'),
(284, 2, 277, 'İman edip iyi işler yapan, namazı dosdoğru kılıp zekatı verenlerin Rabbleri katında elbette mükafatları vardır. Onlara hiçbir korku olmadığı gibi, onlar mahzun da olmazlar.'),
(285, 2, 278, 'Ey iman edenler! Allah\'tan korkun ve artık faizin peşini bırakın, eğer gerçekten müminler iseniz.'),
(286, 2, 279, 'Eğer böyle yapmazsanız, o zaman Allah ve Resulü tarafından size savaş açılmış olduğunu bilin. Eğer tevbe ederseniz, sermayeleriniz sizindir. Haksızlık etmezsiniz, haksızlığa da uğramazsınız.'),
(287, 2, 280, 'Eğer borçlu darlık içindeyse, ona ödeme kolaylığına kadar bir süre tanıyın. Ve bu gibi borçlulara alacağınızı bağışlayıp sadaka etmeniz eğer bilirseniz sizin için, daha hayırlıdır.'),
(288, 2, 281, 'Öyle bir günden korkunuz ki, o gün Allah\'a döndürüleceksiniz. Sonra da herkese kazancı tamamıyla ödenecek ve hiç kimse haksızlığa uğramayacaktır.'),
(289, 2, 282, 'Ey iman edenler! Belli bir vade ile karşılıklı borç alış verişinde bulunduğunuz vakit onu yazın. Hem aranızda doğruluğuyla tanınmış yazı bilen biri yazsın. Yazı bilen biri, Allah\'ın, kendisine öğrettiği gibi yazmaktan kaçınmasın da yazsın. Bir de hak kendi üzerinde olan adam söyleyip yazdırsın ve herbiri yazarken Rabbi olan Allah\'dan korksun da haktan birşey eksiltmesin. Şayet borçlu bir bunak veya küçük bir çocuk veya söyleyip yazdıramıyacak durumda biri ise velisi doğrusunu söyleyip yazdırsın. Erkeklerinizden hazırda olan iki kişiyi şahit de yapın. Şayet iki tane erkek hazırda yoksa, o zaman doğruluğuna güvendiğiniz şahitlerden bir erkekle iki kadın ki, birisi unutunca, öbürü hatırlatsın, şahitler de çağırıldıklarında kaçınmasınlar; siz yazanlar da az olmuş, çok olmuş, onu vadesine kadar yazmaktan usanmayın. Bu, Allah katında adalete daha uygun olduğu gibi; hem şahitlik için daha sağlam, hem şüpheye düşmemeniz için daha elverişlidir. Meğer ki, aranızda hemen devredeceğiniz bir ticaret olsun, o zaman bunu yazmamanızda sizin için bir sakınca yoktur. Alım satım yaptığınız vakit de yine şahit tutun. Ayrıca ne yazan, ne de şahitlik eden bir zarar görmesin. Eğer onlara zarar verirseniz, o işte mutlaka size dokunacak bir günah olur. Üstelik Allah\'dan korkun. Allah size ayrıntılarıyla öğretiyor ve Allah her şeyi bilir.'),
(290, 2, 283, 'Şayet siz sefer üzere olur bir kâtip de bulamazsanız, o vakit alınmış bir rehin belge yerine geçer. Yok eğer birbirinize güveniyorsanız kendisine güvenilen adam Rabbi olan Allah\'dan korksun da üzerindeki emaneti ödesin. Bir de şahitliğinizi inkâr edip gizlemeyin, onu kim inkâr ederse mutlaka onun kalbi vebal içindedir. Her ne yaparsanız Allah onu bilir.'),
(291, 2, 284, 'Göklerde ne var, yerde ne varsa hepsi Allah\'ındır. Siz içinizdekileri açığa vursanız da gizli tutsanız da Allah onunla sizi hesaba çeker. Sonra dilediğini bağışlar, dilediğine de azab eder. Allah her şeye kadirdir.'),
(292, 2, 285, 'Peygamber, Rabbi\'nden kendisine ne indirildiyse ona iman etti. Müminlerin de hepsi Allah\'a, meleklerine, kitaplarına ve peygamberlerine iman ettiler. "Biz Allah\'ın peygamberleri arasında ayırım yapmayız, duyduk ve itaat ettik. Ey Rabbimiz, bağışlamanı dileriz, dönüş ancak sanadır." dediler.'),
(293, 2, 286, 'Allah hiç kimseye gücünün yeteceğinden başka yük yüklemez. Herkesin kazandığı hayır kendisine, yaptığı kötülüğün zararı yine kendisinedir. Ey Rabbimiz, eğer unuttuk ya da yanıldıysak bizi tutup sorguya çekme! Ey Rabbimiz, bize bizden öncekilere yüklediğin gibi ağır yük yükleme! Ey Rabbimiz, bize gücümüzün yetmeyeceği yükü de yükleme! Bağışla bizi, mağfiret et bizi, rahmet et bize! Sensin bizim Mevlamız, kâfir kavimlere karşı yardım et bize.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'Elif, Lâm Mîm,'),
(295, 3, 2, 'Allah, kendisinden başka tanrı olmayan, hayy ve kayyûmdur'),
(296, 3, 3, 'O, sana kendisinden öncekileri tasdik edip doğrulayan bu kitabı hak ile indirdi. Daha önce insanlara hidayet olarak Tevrat\'ı ve İncil\'i de yine O indirmişti.. Evet bu Furkan\'ı da O indirdi. Gerçek şu ki, Allah\'ın âyetlerini inkâr edenler için çetin bir azap vardır. Allah çok güçlüdür, intikamını alır.'),
(297, 3, 4, 'O, sana kendisinden öncekileri tasdik edip doğrulayan bu kitabı hak ile indirdi. Daha önce insanlara hidayet olarak Tevrat\'ı ve İncil\'i de yine O indirmişti.. Evet bu Furkan\'ı da O indirdi. Gerçek şu ki, Allah\'ın âyetlerini inkâr edenler için çetin bir azap vardır. Allah çok güçlüdür, intikamını alır.'),
(298, 3, 5, 'Şu da kesindir ki, ne yerde, ne de gökte hiçbir şey Allah\'a gizli kalmaz.'),
(299, 3, 6, 'Sizi, rahimlerde dilediği gibi şekillendiren O\'dur. Kendisinden başka tanrı olmayan, şan, şeref ve hikmet sahibi olan O\'dur.'),
(300, 3, 7, 'Sana bu kitabı indiren O\'dur. Bunun âyetlerinden bir kısmı muhkemdir ki, bu âyetler, kitabın anası (aslı) demektir. Diğer bir kısmı da müteşabih âyetlerdir. Kalblerinde kaypaklık olanlar, sırf fitne çıkarmak için, bir de kendi keyflerine göre te\'vil yapmak için onun müteşabih olanlarının peşine düşerler. Halbuki onun te\'vilini Allah\'dan başka kimse bilmez. İlimde uzman olanlar, "Biz buna inandık, hepsi Rabbimiz katındandır." derler. Üstün akıllılardan başkası da derin düşünmez.'),
(301, 3, 8, 'Ey Rabbimiz! Bize ihsan ettiğin hidayetten sonra kalblerimizi haktan saptırma, bize kendi katından rahmet ihsan eyle! Şüphesiz ki, Sen bol ihsan sahibisin.'),
(302, 3, 9, 'Ey Rabbimiz! Muhakkak ki, Sen, geleceğinde hiç şüphe olmayan bir günde bütün insanları bir araya toplayacaksın. Muhakkak ki Allah, hiç sözünden caymaz.'),
(303, 3, 10, 'Gerçek şu ki, kâfirlere, Allah\'tan gelecek bir zararı, ne malları, ne de evlatları engelleyemez. İşte onlar, o ateşin yakıtı olacaklar.'),
(304, 3, 11, 'Gidişatları, Firavun soyunun ve daha öncekilerin gidişatı gibidir. Onlar, âyetlerimizi yalan saymışlardı. Bunun üzerine Allah da onları işledikleri günahlar yüzünden yakalayıp alaşağı etti. Allah, cezası çetin olandır.'),
(305, 3, 12, 'O inkârcı kâfirlere de ki, siz mutlaka yenilgiye uğrayacak ve toplanıp cehenneme doldurulacaksınız. Orası ne fena bir döşektir.'),
(306, 3, 13, 'Hiç şüphesiz karşı karşıya gelen iki toplulukta size bir âyet, bir işaret ve ibret vardır. Onlardan biri Allah yolunda savaşıyordu, öbürü de kâfirdi ve karşılarındakini göz kararıyla kendilerinin iki katı görüyorlardı. Allah da gönderdiği yardımla dilediğini destekliyordu. Gören gözleri olanlar için elbette bunda apaçık bir ibret vardır.'),
(307, 3, 14, 'İnsanlara kadınlar, oğullar, yüklerle altın ve gümüş yığınları, salma atlar, davarlar, ekinler kabilinden aşırı sevgiyle bağlanılan şeyler çok süslü gösterilmiştir. Halbuki bunlar dünya hayatının geçici faydalarını sağlayan şeylerdir. Oysa varılacakyerin (ebedî hayatın) bütün güzellikleri Allah katındadır.'),
(308, 3, 15, 'De ki, size, o istediklerinizden daha hayırlısını haber vereyim mi? Korunan kullar için Rablerinin yanında cennetler var ki, altlarından ırmaklar akar, içlerinde ebedî kalmak üzere onlara, hem tertemiz eşler var, hem de Allah\'dan bir rıza vardır. Allah, o kulları görür.'),
(309, 3, 16, 'Onlar ki, "Ey Rabbimiz! Biz inandık, iman getirdik, artık bizim suçlarımızı bağışla ve bizi ateş azabından koru!" derler.'),
(310, 3, 17, 'O sabredenleri, o doğruluktan şaşmayanları, o elpençe divan duranları, o nafaka verenleri ve seher vakitlerinde o istiğfar edip yalvaranları (görür).'),
(311, 3, 18, 'Allah şehadet eyledi şu gerçeğe ki, başka tanrı yok, ancak O vardır. Bütün melekler ve ilim uluları da dosdoğru olarak buna şahittir ki, başka tanrı yok, ancak O aziz, O hakîm vardır.'),
(312, 3, 19, 'Doğrusu Allah katında din, İslâm\'dır; o kitap verilenlerin anlaşmazlıkları ise sırf kendilerine ilim geldikten sonra aralarındaki taşkınlık ve ihtirastan dolayıdır. Her kim Allah\'ın âyetlerini inkâr ederse iyi bilsin ki, Allah hesabı çabuk görendir.'),
(313, 3, 20, 'Buna karşı seninle münakayaşa kalkışırlarsa de ki: "Ben, bana uyanlarla birlikte kendi özümü Allah\'a teslim etmişimdir". Kendilerine kitap verilenlere ve (kitap verilmeyen) ümmîlere de ki: "Siz de İslâm\'ı kabul ettiniz mi?" Eğer İslâm\'a girerlerse hidayete ermiş olurlar. Eğer yüz çevirirlerse, sana düşen şey ancak tebliğ etmektir. Allah kulları görendir.'),
(314, 3, 21, 'Allah\'ın âyetlerini inkâr edenler ve haksız yere peygamberleri öldürenler, insanlar içinde adaleti emredenlerin canına kıyanlar yok mu? Bunlarıacıklı bir azapla müjdele!'),
(315, 3, 22, 'İşte bunlar öyle kimselerdir ki, dünyada da ahirette de bütün yaptıkları boşa gitmiştir. Onların hiçbir yardımcıları da olmayacaktır.'),
(316, 3, 23, 'Görmüyor musun, o kendilerine kitaptan bir nasip verilmiş olanlar, aralarında hüküm vermek için Allah\'ın kitabına davet olunuyorlar da, sonra içlerinden bir kısmı yüz çevirerek dönüp gidiyorlar.'),
(317, 3, 24, 'Bunun sebebi, onların "belli günlerden başka bize asla ateş azabı dokunmaz" demeleridir. Uydurageldikleri yalanlar dinlerinde kendilerini aldatmaktadır.'),
(318, 3, 25, 'O geleceğinde hiç şüphe olmayan günde kendilerini bir araya topladığımız ve hiç kimseye haksızlık edilmeden herkese ne kazandıysa tamamen ödendiği vakit halleri nasıl olacaktır?'),
(319, 3, 26, 'De ki: "Ey mülkün sahibi Allah\'ım! Sen mülkü dilediğine verirsin, dilediğinden de onu çeker alırsın, dilediğini aziz edersin, dilediğini zelil edersin. Hayır Senin elindedir. Muhakkak ki, Sen her şeye kâdirsin.'),
(320, 3, 27, 'Geceyi gündüzün içine sokarsın, gündüzü gecenin içine sokarsın; ölüden diri çıkarırsın, diriden ölü çıkarırsın. Dilediğine de hesapsız rızık verirsin.'),
(321, 3, 28, 'Müminler, müminleri bırakıp da kâfirleri dost edinmesin ve onu her kim yaparsa Allah\'dan ilişiği kesilmiş olur, ancak onlardan bir korunma yapmanız başkadır. Bununla beraber Allah sizi kendisinden korunmanız hususunda uyarır. Nihâyet gidiş Allah\'adır.'),
(322, 3, 29, 'De ki, göğüslerinizdekini gizleseniz de, açığa vursanız da Allah onu bilir. Göklerde ne var, yerde ne varsa hepsini bilir. Hiç şüphesiz Allah, her şeye kadirdir.'),
(323, 3, 30, 'O gün her nefis, ne hayır işlemişse, ne kötülük yapmışsa onları önünde hazır bulur. Yaptığı kötülüklerle kendi arasında uzak bir mesafe bulunsun ister. Allah, size asıl kendisinden çekinmenizi emreder. Şüphesiz ki Allah, kullarını çok esirger.'),
(324, 3, 31, 'De ki, siz gerçekten Allah\'ı seviyorsanız bana uyun ki, Allah da sizi sevsin ve suçlarınızı bağışlasın. Çünkü Allah çok esirgeyici ve bağışlayıcıdır.'),
(325, 3, 32, 'De ki, Allah\'a ve Peygamber\'e itaat edin! Eğer aksine giderlerse, şüphe yok ki Allah kâfirleri sevmez.'),
(326, 3, 33, 'Gerçekten Allah, Adem\'i, Nuh\'u, İbrahim soyunu ve İmran soyunu âlemler üzerine seçkin kıldı.'),
(327, 3, 34, 'Bir zürriyet olarak birbirinden gelmişlerdir. Allah her şeyi işitendir, bilendir.'),
(328, 3, 35, 'İmran\'ın karısı: "Rabbim, karnımdakini tam hür olarak sana adadım, benden kabul buyur, şüphesiz sen işitensin, bilensin." demişti.'),
(329, 3, 36, 'Onu doğuruncaAllah onun ne doğurduğunu bilip dururkenşöyle dedi: "Rabbim, onu kız doğurdum; erkek, kız gibi değildir. Ona Meryem adını verdim. Onu ve soyunu koğulmuş şeytanın şerrinden sana ısmarlıyorum".'),
(330, 3, 37, 'Bunun üzerine Rabbi onu güzel bir şekilde kabul buyurdu ve onu güzel bir bitki gibi yetiştirdi ve Zekeriyya\'nın himayesine verdi. Zekeriyya ne zaman kızın bulunduğu mihraba girse, onun yanında yeni bir yiyecek bulurdu. "Meryem! Bu sana nereden geldi?" deyince, o da: "Bu, Allah katındandır." derdi. Şüphesiz Allah, dilediğine hesapsız rızık verir.'),
(331, 3, 38, 'Orada Zekeriyya, Rabbine dua etti: "Rabbim! Bana katından hayırlı bir nesil ver. Şüphesiz sen, duayı hakkıyle işitensin" dedi.'),
(332, 3, 39, 'Zekeriyya mabedde namaz kılarken melekler ona: "Allah sana, Allah\'dan bir kelimeyi doğrulayıcı, efendi, nefsine hakim ve iyilerden bir peygamber olarak Yahya\'yı müjdeler." diye ünlediler.'),
(333, 3, 40, 'Zekeriyya: "Ey Rabbim, benim nasıl oğlum olabilir? Bana ihtiyarlık gelip çattı, karım ise kısırdır." dedi. Allah: "Öyledir, fakat Allah dilediğini yapar." buyurdu.'),
(334, 3, 41, 'Zekeriyya: "Rabbim! (oğlum olacağına dair) bana bir alâmet ver" dedi. Allah da buyurdu ki: "Senin için alâmet, insanlara üç gün, işaretten başka söz söyleyememendir. Ayrıca Rabbini çok an, sabah akşam tesbih et".'),
(335, 3, 42, 'Hani melekler: "Ey Meryem! Allah seni seçti, seni tertemiz yarattı ve seni dünya kadınlarına üstün kıldı.'),
(336, 3, 43, 'Ey Meryem! Rabbine divan dur ve secdeye kapan ve rüku\' edenlerle beraber rüku\' et" demişlerdi.'),
(337, 3, 44, 'İşte bu, sana vahyettiğimiz gayb haberlerindendir. (Yoksa) "Meryem\'i kim himayesine alıp koruyacak?" diye kalemlerini (kur\'a için) atarlarken sen yanlarında değildin. (Bu hususta) Tartışırlarken de yanlarında bulunmadın.'),
(338, 3, 45, 'Melekler şöyle demişti: "Ey Meryem! Allah sana kendisinden bir kelimeyi müjdeliyor ki, adı Meryem oğlu İsa Mesih\'dir; dünyada da ahirette de itibarlı, aynı zamanda Allah\'a çok yakınlardandır.'),
(339, 3, 46, 'Beşikte de, yetişkin çağında da insanlarla konuşacak ve iyilerden olacaktır.'),
(340, 3, 47, '(Meryem): "Ey Rabbim, bana bir beşer dokunmamışken benim nasıl çocuğum olur?" dedi. Allah: "Öyle ama, Allah dilediğini yaratır, bir şeyin olmasını dilediğinde ona sadece \'ol!\' der, o da hemen oluverir." dedi.'),
(341, 3, 48, 'Allah ona kitab (okuma ve yazmay)ı, hikmeti ve Tevrat ile İncil\'i öğretir.'),
(342, 3, 49, 'Allah onu İsrailoğullarına (şöyle diyecek) bir peygamber olarak gönderir: "Şüphesiz ki ben size Rabbinizden bir âyet (mucize, belge) getirdim: Size, kuş biçiminde çamurdan birşey yaparım da içine üflerim, Allah\'ın izniyle o, kuş olur; anadan doğma körü ve alacalıyı iyileştiririm ve Allah\'ın izniyle ölüleri diriltirim. Evlerinizde ne yiyor ve neleri biriktiriyorsanız size haber veririm".'),
(343, 3, 50, '"Önümdeki Tevrat\'ı doğrulayıcı olarak ve size haram kılınan bazı şeyleri helal kılmak için (geldim) ve Rabbiniz tarafından size bir mucize getirdim. Artık Allah\'tan korkun da bana uyun".'),
(344, 3, 51, '"Şüphesiz Allah, benim de Rabbim, sizin de Rabbinizdir. Onun için hep O\'na kulluk edin! İşte bu, doğru yoldur".'),
(345, 3, 52, 'İsa onların inkârlarını hissedince: "Allah yolunda yardımcılarım kim?" dedi. Havariler: "Allah yolunda yardımcılar biziz. Allah\'a iman ettik. Şahit ol ki, biz muhakkak müslümanlarız." dediler.'),
(346, 3, 53, 'Ey Rabbimiz, senin indirdiğine iman ettik, o peygambere de uyduk. Artık bizi şahidlerle beraber yaz.'),
(347, 3, 54, 'Onlar hileye başvurdular, Allah da onların tuzağını boşa çıkardı. Allah hileleri boşa çıkaranların en hayırlısıdır.'),
(348, 3, 55, 'O zaman Allah şöyle dedi: "Ey İsa, şüphesiz ki seni öldüreceğim, seni kendime yükselteceğim ve seni inkârcılardan temizleyeceğim. Hem sana uyanları, kıyamete kadar o küfredenlerin üstünde tutacağım. Sonra dönüşünüz banadır, ayrılığa düştüğünüz hususlarda aranızda hükmedeceğim".'),
(349, 3, 56, '"İnkâr edenlere gelince, onlara dünyada da, ahirette de şiddetli bir şekilde azab edeceğim, onların hiçbir yardımcıları da olmayacaktır".'),
(350, 3, 57, '"İman edip iyi işler yapanlara gelince, Allah onların mükafatlarını tastamam verecektir. Allah zalimleri sevmez".'),
(351, 3, 58, 'İşte bu sana okuduğumuz, âyetlerden ve hikmetli Kur\'ân\'dandır.'),
(352, 3, 59, 'Doğrusu Allah katında İsa\'nın (yaratılışındaki) durumu, Âdem\'in durumu gibidir; onu topraktan yarattı, sonra ona "ol!" dedi, o da oluverdi.'),
(353, 3, 60, 'Bu hak (gerçek) senin rabbindendir, o halde şüphecilerden olma.'),
(354, 3, 61, 'Sana (gerekli) bilgi geldikten sonra artık kim bu konuda seninle tartışacak olursa, de ki: "Gelin, oğullarımızı ve oğullarınızı, kadınlarımızı ve kadınlarınızı, kendimizi ve kendinizi çağıralım, sonra da lanetleşelim; Allah\'ın lanetinin yalancılara olmasını dileyelim".'),
(355, 3, 62, 'İşte (İsa hakkında söylenen) gerçek kıssa budur. Allah\'tan başka hiçbir tanrı yoktur. Muhakkak ki Allah çok güçlüdür ve hikmet sahibidir.'),
(356, 3, 63, 'Eğer (haktan) yüz çevirirlerse, şüphesiz ki Allah bozguncuları çok iyi bilendir.'),
(357, 3, 64, 'De ki: Ey kitap ehli! Sizinle bizim aramızda ortak olan bir söze geliniz. Allah\'tan başkasına kulluk etmeyelim, O\'na hiçbir şeyi eş tutmayalım ve Allah\'ı bırakıp da kimimiz kimimizi ilâhlaştırmasın. Eğer onlar yine yüz çevirirlerse, deyin ki: "Şahit olun biz müslümanlarız".'),
(358, 3, 65, 'Ey Kitap ehli! İbrahim hakkında niçin tartışıyorsunuz? Oysa Tevrat da, İncil de ondan sonra indirilmiştir. Siz hiç düşünmüyor musunuz?'),
(359, 3, 66, 'İşte siz böylesiniz. Haydi biraz bilginiz olan şey hakkında tartıştınız, ya hiç bilginiz olmayan şey hakkında niçin tartışıyorsunuz? Allah bilir, siz bilmezsiniz.'),
(360, 3, 67, 'İbrahim, ne yahudi, ne de hıristiyandı; fakat o, Allah\'ı bir tanıyan dosdoğru bir müslümandı, müşriklerden de değildi.'),
(361, 3, 68, 'Doğrusu onların İbrahim\'e en yakın olanı, ona uyanlar, şu Peygamber ve iman edenlerdir. Allah da müminlerin dostudur.'),
(362, 3, 69, 'Kitap ehlinden bir grup sizi saptırmak istediler, halbuki sırf kendilerini saptırıyorlar da farkına varmıyorlar.'),
(363, 3, 70, 'Ey kitap ehli! (gerçeği) gördüğünüz halde, niçin Allah\'ın âyetlerini inkâr ediyorsunuz?'),
(364, 3, 71, 'Ey kitap ehli! Niçin hakkı batıla karıştırıyor ve bile bile gerçeği gizliyorsunuz?'),
(365, 3, 72, 'Kitap ehlinden bir grup: "Müminlere indirilene günün başlangıcında inanın, sonunda da inkâr edin, belki onlar da dönerler." dedi.'),
(366, 3, 73, '"Ve kendi dininize uyanlardan başkasına inanmayın" (dediler). De ki: "Şüphesiz doğru yol, Allah\'ın yoludur". (Onlar kendi aralarında): "Size verilenin benzerinin hiçbir kimseye verilmiş olduğuna, yahut Rabbinizin huzurunda sizin aleyhinize deliller getireceklerine" (de inanmayın dediler). De ki: "Lütuf Allah\'ın elindedir, onu dilediğine verir. Allah, rahmeti bol olan, her şeyi hakkıyla bilendir".'),
(367, 3, 74, 'Rahmetini dilediğine tahsis eder. Allah, büyük lütuf ve kerem sahibidir.'),
(368, 3, 75, 'Kitap ehlinden öylesi vardır ki, ona yüklerle mal emanet etsen, onu sana eksiksiz iade eder. Fakat öylesi de vardır ki, ona bir dinar emanet etsen, tepesine dikilip durmadıkça onu sana iade etmez. Bu da onların, "Ümmîlere karşı yaptıklarımızdan bize vebal yoktur." demelerinden dolayıdır. Ve onlar, bile bile Allah\'a karşı yalan söylerler.'),
(369, 3, 76, 'Hayır, kim sözünü yerine getirir ve kötülüklerden korunursa, şüphesiz Allah da korunanları sever.'),
(370, 3, 77, 'Allah\'a verdikleri sözü ve yeminlerini az bir paraya satanlar var ya, işte onların ahirette bir payı yoktur; Allah kıyamet günü onlarla hiç konuşmayacak, onlara bakmayacak ve onları temizlemeyecektir. Onlar için acı bir azab vardır.'),
(371, 3, 78, 'Kitap ehlinden öyle bir güruh da vardır ki, siz onu kitaptan sanasınız diye, dillerini kitaba doğru eğip bükerler. Halbuki o, kitaptan değildir. "Bu, Allah katındandır." derler; oysa o, Allah katından değildir. Allah\'a karşı, kendileri bilip dururken, yalan söylerler.'),
(372, 3, 79, 'İnsanlardan hiçbir kimseye, Allah kendisine kitap, hüküm ve peygamberlik verdikten sonra, kalkıp insanlara: "Allah\'ı bırakıp bana kul olun." demesi yakışmaz. Fakat onun: "Öğrettiğiniz ve okuduğunuz kitap gereğince Rabb\'e halis kullar olun" (demesi uygundur).'),
(373, 3, 80, 'Ve O size: "Melekleri ve peygamberleri tanrılar edinin." diye de emretmez. Siz müslüman olduktan sonra, size hiç inkârı emreder mi?'),
(374, 3, 81, 'Allah peygamberlerden şöyle söz almıştı: "Andolsun ki size kitab ve hikmet verdim, sonra yanınızda bulunan (kitaplar)ı doğrulayıcı bir peygamber geldiğinde ona muhakkak inanacak ve ona yardım edeceksiniz! Bunu kabul ettiniz mi? Ve bu hususta ağır ahdimi üzerinize aldınız mı?" demişti. Onlar: "Kabul ettik" dediler. (Allah da) dedi ki: "Öyleyse şahit olun, ben de sizinle beraber şahit olanlardanım".'),
(375, 3, 82, 'Artık bundan sonra her kim dönerse, işte onlar yoldan çıkmışların ta kendileridir.'),
(376, 3, 83, 'Onlar, Allah\'ın dininden başkasını mı arıyorlar? Halbuki göklerde ve yerde ne varsa hepsi, ister istemez O\'na boyun eğmiştir ve O\'na döndürülüp götürüleceklerdir.'),
(377, 3, 84, 'De ki: "Allah\'a, bize indirilen (Kur\'ân)e, İbrahim\'e, İsmail\'e, İshak\'a, Yakub\'a ve torunlarına indirilene, Musa\'ya, İsa\'ya ve peygamberlere Rablerinden verilenlere inandık. Onların arasında hiçbir fark gözetmeyiz, biz O\'na teslim olmuşlarız".'),
(378, 3, 85, 'Kim İslâm\'dan başka bir din ararsa ondan asla kabul edilmeyecek ve o ahirette de zarar edenlerden olacaktır.'),
(379, 3, 86, 'İnandıktan, Peygamber\'in hak olduğuna şehadet ettikten ve kendilerine açık deliller geldikten sonra, inkâra sapan bir milleti Allah nasıl doğru yola eriştirir? Allah zalimler güruhunu doğru yola iletmez.'),
(380, 3, 87, 'İşte onların cezaları, Allah\'ın, meleklerin, insanların hepsinin laneti onların üzerlerindedir.'),
(381, 3, 88, 'Onlar bu (lanetin) içinde ebedî kalacaklardır. Kendilerinden ne bu azab hafifletilir, ne de yüzlerine bakılır.'),
(382, 3, 89, 'Ancak bundan sonra tevbe edip kendini düzeltenler başka. Şüphesiz ki Allah, çok bağışlayan ve çok esirgeyendir.'),
(383, 3, 90, 'Şüphesiz imanlarının arkasından küfreden, sonra da küfrünü artırmış olanların tevbeleri asla kabul olunmaz. İşte onlar sapıkların ta kendileridir.'),
(384, 3, 91, 'Muhakkak ki inkâr edenler ve kâfir oldukları halde de ölenler, yeryüzü dolusu altın fidye verseler bile hiç birisinden asla kabul edilmeyecektir. İşte dayanılmaz azab onlar içindir. Onların hiçbir yardımcıları da yoktur.'),
(385, 3, 92, 'Sevdiğiniz şeylerden (Allah yolunda) harcamadıkça, gerçek iyiliğe asla erişemezsiniz. Her ne harcarsanız Allah onu hakkıyla bilir.'),
(386, 3, 93, 'Tevrat indirilmeden önce, İsrail (Yakub)in kendisine haram kıldığı dışında, yiyeceklerin hepsi İsrailoğullarına helal idi. De ki: "Eğer doğrulardan iseniz, haydi Tevrat\'ı getirip okuyun".'),
(387, 3, 94, 'Kim bundan sonra Allah\'a karşı yalan uydurursa, işte onlar zalimlerin ta kendileridir.'),
(388, 3, 95, 'De ki: "Allah doğru söylemiştir. Öyle ise dosdoğru, Allah\'ı birleyici olarak İbrahim\'in dinine uyun. O, müşriklerden değildi".'),
(389, 3, 96, 'Şüphesiz insanlar için kurulan ilk mabed, Mekke\'deki çok mübarek ve bütün âlemlere hidayet kaynağı olan Beyt (Kabe)dir.'),
(390, 3, 97, 'Onda apaçık deliller, İbrahim\'in makamı vardır. Oraya giren güvene erer. Ona bir yol bulabilenlerin Beyt\'i haccetmesi Allah\'ın insanlar üzerinde bir hakkıdır. Kim inkâr ederse, şüphesiz Allah bütün âlemlerden müstağni (kimseye muhtaç değil, her şey ona muhtaç)dir.'),
(391, 3, 98, 'De ki: "Ey kitap ehli! Allah yaptıklarınızı görüp dururken niçin Allah\'ın âyetlerini inkâr ediyorsunuz?"'),
(392, 3, 99, 'De ki: "Ey kitap ehli! Gerçeği görüp bildiğiniz hâlde niçin Allah\'ın yolunu eğri göstermeye yeltenerek müminleri Allah\'ın yolundan çevirmeye kalkışıyorsunuz? Allah yaptıklarınızdan habersiz değildir".'),
(393, 3, 100, 'Ey iman edenler! Kendilerine kitap verilenlerden herhangi bir gruba uyarsanız, imanınızdan sonra sizi döndürüp kâfir yaparlar.'),
(394, 3, 101, 'Size Allah\'ın âyetleri okunup dururken ve Allah\'ın elçisi de aranızda iken nasıl inkâra saparsınız? Kim Allah\'a sımsıkı bağlanırsa, kesinlikle doğru yola iletilmiştir.'),
(395, 3, 102, 'Ey iman edenler! Allah\'tan, O\'na yaraşır şekilde korkun ve ancak müslümanlar olarak can verin.'),
(396, 3, 103, 'Hep birlikte Allah\'ın ipine (kitabına, dinine) sımsıkı sarılın. Parçalanıp ayrılmayın. Allah\'ın üzerinizdeki nimetini düşünün. Hani siz birbirinize düşmanlar idiniz de, O, kalplerinizi birleştirmişti. İşte O\'nun (bu) nimeti sayesinde kardeşler olmuştunuz. Yine siz, bir ateş çukurunun tam kenarında iken oradan da sizi O kurtarmıştı. İşte Allah size âyetlerini böyle apaçık bildiriyor ki, doğru yola eresiniz.'),
(397, 3, 104, 'İçinizden hayra çağıran, iyiliği emredip kötülükten men eden bir topluluk bulunsun. İşte kurtuluşa eren onlardır.'),
(398, 3, 105, 'Kendilerine apaçık deliller geldikten sonra parçalanıp ayrılığa düşenler gibi olmayın. İşte bunlar için büyük bir azap vardır.'),
(399, 3, 106, 'O gün bazı yüzler ağarır, bazı yüzler kararır. Yüzleri kararanlara: "İmanınızdan sonra küfrettiniz ha? Öyle ise inkâr etmenize karşılık azabı tadın" (denecektir).'),
(400, 3, 107, 'Yüzleri ağaranlara gelince, (onlar) Allah\'ın rahmeti içindedirler. Onlar orada ebedî kalacaklardır.'),
(401, 3, 108, 'Bunlar Allah\'ın, sana gerçek olarak okuyageldiğimiz, âyetleridir. Allah âlemlere hiçbir haksızlık etmek istemez.'),
(402, 3, 109, 'Göklerde ve yerde olanların hepsi Allah\'ındır. Bütün işler Allah\'a döndürülür.'),
(403, 3, 110, 'Siz insanlar için çıkarılmış en hayırlı ümmetsiniz. İyiliği emreder, kötülükten vazgeçirmeğe çalışır ve Allah\'a inanırsınız. Kitap ehli de inansaydı kendileri için elbette daha hayırlı olurdu. İçlerinden iman edenler de var, ama pek çoğu yoldan çıkmışlardır.'),
(404, 3, 111, 'Onlar size eziyetten başka bir zarar veremezler. Eğer sizinle savaşmaya kalkışsalar, size arkalarını dönüp kaçarlar. Sonra kendilerine yardım da edilmez.'),
(405, 3, 112, 'Onlar nerede bulunurlarsa bulunsunlar, üzerlerine alçaklık damgası vurulmuştur. Meğer ki Allah\'ın ipine ve insanlar (müminler)ın ahdine sığınmış olsunlar. Onlar Allah\'ın hışmına uğradılar ve üzerlerine de miskinlik damgası vuruldu. Bunun sebebi, onların Allah\'ın âyetlerini inkâr etmiş olmaları ve haksız yere peygamberleri öldürmeleridir. Ayrıca isyan etmiş ve haddi de aşmışlardı.'),
(406, 3, 113, 'Hepsi bir değildirler. Kitap ehli içinde doğruluk üzere bulunan bir ümmet (topluluk) vardır ki, gecenin saatlerinde onlar secdeye kapanarak Allah\'ın âyetlerini okurlar.'),
(407, 3, 114, 'Allah\'a ve ahiret gününe inanırlar, iyiliği emrederler, kötülükten vazgeçirmeye çalışırlar, hayır işlerinde de birbirleriyle yarışırlar. İşte onlar iyi insanlardandır.'),
(408, 3, 115, 'Onlar ne hayır işlerlerse karşılıksız bırakılmayacaklardır. Allah kendisinden gereği gibi sakınanları bilir.'),
(409, 3, 116, 'O inkâr edenler (var ya), onların ne malları, ne de evlatları, onlara Allah\'a karşı hiçbir fayda sağlamayacaktır. Onlar, ateş halkıdır; orada ebedi kalacaklardır.'),
(410, 3, 117, 'Onların bu dünya hayatında harcadıklarının durumu, kendilerine zulmeden bir topluluğun ekinlerini vurup da mahveden kavurucu ve soğuk bir rüzgarın hali gibidir. Allah onlara zulmetmedi. Fakat kendileri, kendilerine zulmediyorlar.'),
(411, 3, 118, 'Ey iman edenler! Kendi dışınızdakilerden sırdaş edinmeyin. Çünkü onlar size fenalık etmekten asla geri kalmazlar, hep sıkıntıya düşmenizi isterler. Kin ve düşmanlıkları ağızlarından taşmaktadır. Kalplerinde gizledikleri ise daha büyüktür. Düşünürseniz, biz size âyetleri açıkladık.'),
(412, 3, 119, 'İşte siz öyle kimselersiniz ki, onları seversiniz, halbuki onlar sizi sevmezler, siz kitap(lar)ın hepsine inanırsınız, onlarsa sizinle buluştukları zaman "inandık" derler. Başbaşa kaldıkları zaman da kinlerinden dolayı parmaklarının uçlarını ısırırlar. De ki: "kininizle geberin!". Şüphesiz ki Allah göğüslerin (gönüllerin) özünü bilir.'),
(413, 3, 120, 'Size bir iyilik dokunsa fenalarına gider, başınıza bir kötülük gelse onunla sevinirler. Eğer sabreder ve Allah\'dan gereğince korkarsanız, onların hileleri size hiçbir zarar vermez; çünkü Allah onları kendi amelleriyle kuşatmıştır.'),
(414, 3, 121, 'Hani sen sabah erkenden müminleri savaş mevzilerine yerleştirmek için ailenden ayrılmıştın. Allah, hakkıyla işiten ve bilendir.'),
(415, 3, 122, 'O zaman içinizden iki takım bozulmaya yüz tutmuştu. Halbuki Allah onların yardımcısı idi. İnananlar, yalnız Allah\'a dayanıp güvensinler.'),
(416, 3, 123, 'Andolsun, sizler güçsüz olduğunuz halde Allah size Bedir\'de yardım etmişti. Allah\'tan sakının ki, O\'na şükretmiş olasınız.'),
(417, 3, 124, 'O zaman sen müminlere: "Rabbinizin size, indirilmiş üç bin melek ile yardım etmesi size yetmez mi?" diyordun.'),
(418, 3, 125, 'Evet, sabreder ve (Allah\'tan) korkarsanız, onlar ansızın üzerinize gelseler, Rabbiniz size nişanlı nişanlı beş bin melekle yardım eder.'),
(419, 3, 126, 'Allah, bunu size sırf bir müjde olsun ve kalpleriniz bununla yatışsın diye yaptı. Yardım, yalnız daima galip ve hikmet sahibi olan Allah katındandır.'),
(420, 3, 127, '(Allah bu yardımı) inkâr edenlerden bir kısmını kessin veya perişan etsin de umutsuz olarak dönüp gitsinler (diye yaptı).'),
(421, 3, 128, 'Bu işten sana hiçbir şey düşmez. (Allah), ya onların tevbesini kabul eder, yahut onlara, zalim olduklarından dolayı azab eder.'),
(422, 3, 129, 'Göklerde ve yerde olanların hepsi Allah\'ındır. Dilediğini bağışlar, dilediğine azab eder. Allah, çok bağışlayan, çok esirgeyendir.'),
(423, 3, 130, 'Ey iman edenler! Kat kat artırılmış olarak faiz yemeyin. Allah\'tan sakının ki kurtuluşa eresiniz.'),
(424, 3, 131, 'Kâfirler için hazırlanmış olan ateşten sakının.'),
(425, 3, 132, 'Allah ve Peygambere itaat edin ki, size de merhamet edilsin.'),
(426, 3, 133, 'Rabbinizin bağışına ve genişliği göklerle yer arası kadar olan, Allah\'tan gereği gibi korkanlar için hazırlanmış bulunan cennete koşun!'),
(427, 3, 134, 'O (Allah\'tan hakkıyla korka)nlar, bollukta ve darlıkta Allah için harcarlar, öfkelerini yutarlar, insanları affederler. Allah iyilik edenleri sever.'),
(428, 3, 135, 'Ve onlar çirkin bir günah işledikleri, yahut nefislerine zulmettikleri zaman Allah\'ı hatırlayarak hemen günahlarının bağışlanmasını dilerler. Allah\'tan başka günahları kim bağışlayabilir? Bir de onlar, bile bile, işledikleri (günah) üzerinde ısrar etmezler.'),
(429, 3, 136, 'İşte onların mükafatı (ödülleri) Rableri tarafından bağışlanma ve altından ırmaklar akan, ebedî kalacakları cennetlerdir. Çalışanların mükafatı ne güzeldir!'),
(430, 3, 137, 'Muhakkak ki sizden önce birçok olaylar, şeriatler gelip geçmiştir. Yeryüzünde gezin, dolaşın da yalancıların sonunun nasıl olduğunu bir görün.'),
(431, 3, 138, 'Bu (Kur\'ân) insanlar için bir açıklama, Allah\'dan gereğince korkanlar için doğru yolu gösterme ve bir öğüttür.'),
(432, 3, 139, 'Gevşemeyin, üzülmeyin, eğer hakikaten inanıyorsanız, muhakkak üstün olan sizsinizdir.'),
(433, 3, 140, 'Eğer size (Uhud savaşında) bir yara değmişse, (Bedir harbinde) o topluma da benzeri bir yara dokunmuştu. O günler ki, biz onları insanlar arasında döndürür dururuz. (Bu da) Allah\'ın sizden iman edenleri ayırt etmesi ve sizden şahitler edinmesi içindir. Allah zalimleri sevmez.'),
(434, 3, 141, 'Bir de bu, Allah\'ın iman edenleri tertemiz seçip, kâfirleri yok etmesi içindir.'),
(435, 3, 142, 'Yoksa siz, Allah içinizden cihad edenleri belli etmeden, sabredenleri ortaya çıkarmadan cennete girivereceğinizi mi sandınız?'),
(436, 3, 143, 'Andolsun ki siz ölümle karşılaşmadan önce onu arzuluyordunuz. İşte onu gördünüz, ama bakıp duruyorsunuz.'),
(437, 3, 144, 'Muhammed, ancak bir peygamberdir. Ondan önce de peygamberler gelip geçmiştir. Şimdi o ölür veya öldürülürse gerisin geriye (eski dininize) mi döneceksiniz? Kim (böyle) geri dönerse, Allah\'a hiçbir şekilde zarar veremez. Allah şükredenleri mükafatlandıracaktır.'),
(438, 3, 145, 'Allah\'ın izni olmadıkça hiçbir kimseye ölmek yoktur. (Ölüm) belirli bir süreye göre yazılmıştır. Kim dünya menfaatini dilerse, kendisine ondan veririz. Kim de ahiret sevabını isterse ona da ondan veririz. Biz şükredenleri mükafatlandıracağız.'),
(439, 3, 146, 'Nice peygamberler vardı ki, kendileriyle beraber birçok Allah dostları çarpıştılar; Allah yolunda başlarına gelenlerden yılgınlık göstermediler, zaafa düşmediler, boyun eğmediler. Allah sabredenleri sever.'),
(440, 3, 147, 'Onların sözleri ancak: "Rabbimiz! Bizim günahlarımızı ve işlerimizdeki taşkınlıklarımızı bağışla ve (yolunda) ayaklarımızı diret, Kâfirler güruhuna karşı da bize yardım et!" demekten ibaretti.'),
(441, 3, 148, 'Allah da onlara hem dünya nimetini, hem de ahiret sevabının güzelliğini verdi. Allah güzel davrananları sever.'),
(442, 3, 149, 'Ey iman edenler! Siz eğer kâfir olanlara uyarsanız, sizi topuklarınız üstünde gerisin geriye çevirirler. O zaman büsbütün kaybedersiniz.'),
(443, 3, 150, 'Hayır! Sizin mevlanız Allah\'tır. O, yardım edenlerin en hayırlısıdır.'),
(444, 3, 151, 'Allah\'ın, hakkında hiçbir delil indirmediği şeyleri O\'na ortak koşmalarından dolayı, inkâr edenlerin kalplerine korku salacağız. Onların yurtları ateştir. Zalimlerin dönüp varacağı yer ne kötüdür!'),
(445, 3, 152, 'Siz Allah\'ın izni ile düşmanlarınızı öldürürken, Allah, size olan vaadini yerine getirmiştir. Allah size sevdiğiniz (galibiyeti) gösterdikten sonra zaafa düştünüz. (Peygamber\'in verdiği) emir hakkında tartışmaya kalkıştınız ve isyan ettiniz. Kiminiz dünyayı istiyordu, kiminiz ahireti istiyordu. Sonra Allah sizi, denemek için onlardan geri çevirdi ve sizi bağışladı. Allah müminlere karşı çok lütufkârdır.'),
(446, 3, 153, 'Peygamber sizi arkanızdan çağırıp dururken, siz boyuna uzaklaşıyor, hiç kimseye dönüp bakmıyordunuz. Bundan dolayı Allah, size gam üstüne gam verdi ki, ne elinizden gidene, ne de başınıza gelene üzülmeyesiniz. Allah yaptıklarınızdan haberdardır.'),
(447, 3, 154, 'Sonra o kederin ardından (Allah) üzerinize öyle bir eminlik, öyle bir uyku indirdi ki, o, içinizden bir zümreyi örtüp bürüyordu. Bir zümre de canları sevdasına düşmüştü. Allah\'a karşı, cahiliyet zannı gibi, hakka aykırı bir zan besliyorlar ve "Bu işten bize ne?" diyorlardı. De ki: "Bütün iş Allah\'ındır". Onlar sana açıklamayacaklarını içlerinde saklıyorlar (ve) diyorlar ki: "Bize bu işten bir şey olsaydı burada öldürülmezdik". Onlara şöyle söyle: "Eğer siz evlerinizde olsaydınız bile, üzerlerine öldürülmesi yazılmış olanlar yine muhakkak yatacakları (öldürülecekleri) yerlere çıkıp gidecekti. Allah (bunu) göğüslerinizin içindekini denemek ve yüreklerinizdekini temizlemek için yaptı. Allah göğüslerin içinde olanı bilir.'),
(448, 3, 155, 'İki toplumun karşılaştığı gün, içinizden yüz çevirip gidenler var ya, şeytan onların kazandıkları bazı şeylerden dolayı ayaklarını kaydırmak istedi. Ama yine de Allah onları affetti. Kuşkusuz Allah çok bağışlayandır, halim(çok yumuşak)dir.'),
(449, 3, 156, 'Ey iman edenler! Sizler inkâr edenler ve yeryüzünde sefere veya savaşa çıkan kardeşleri için: "Eğer bizim yanımızda olsalardı ölmezlerdi ve öldürülmezlerdi." diyenler gibi olmayın. Allah bunu, onların kalplerine bir hasret (yarası) olarak koydu. Allah, diriltir ve öldürür. Allah yaptıklarınızı görmektedir.'),
(450, 3, 157, 'Eğer Allah yolunda öldürülür veya ölürseniz, Allah\'ın bağışlaması ve rahmeti, (sizin için) onların topladıkları (dünyalıkları)ndan daha hayırlıdır.'),
(451, 3, 158, 'Andolsun, ölseniz de, öldürülseniz de Allah\'ın huzurunda toplanacaksınız.'),
(452, 3, 159, 'Sen (o zaman), sırf Allah\'ın rahmetiyle onlara karşı yumuşak davrandın. Eğer kaba, katı yürekli olsaydın, onlar senin etrafından dağılıp giderlerdi. Artık onları sen bağışla, onlar için Allah\'dan mağfiret dile. (Yapacağın) işlerde onlara da danış, bir kere de azmettin mi, artık Allah\'a dayan. Muhakkak ki Allah kendine dayanıp güvenenleri sever.'),
(453, 3, 160, 'Allah size yardım ederse, sizi yenecek yoktur. Eğer sizi yardımsız bırakırsa, artık ondan sonra size kim yardım edebilir? Müminler ancak Allah\'a güvenip dayansınlar.'),
(454, 3, 161, 'Hiçbir peygambere ganimet malını gizlemesi (devletmillet malını aşırması) yaraşmaz. Kim böyle bir aşırma ve ihanette bulunursa kıyamet günü aşırdığını boynuna yüklenerek getirir. Sonra da herkese kazandığının karşılığı tastamam ödenir, onlar haksızlığa da uğramazlar.'),
(455, 3, 162, 'Allah\'ın rızasına uyan kimse, Allah\'ın hışmına uğrayan ve varacağı yer cehennem olan kimse gibi midir? Varış yeri olarak ne kötüdür orası!'),
(456, 3, 163, 'Onlar (insanlar) Allah katında derece derecedirler. Allah, onların yaptıklarını görmektedir.'),
(457, 3, 164, 'Andolsun ki Allah, müminlere kendilerinden, onlara kendi âyetlerini okuyan, onları arındıran ve onlara kitab ve hikmeti öğreten bir Peygamber göndermekle büyük bir lütufta bulunmuştur. Oysa onlar, daha önce apaçık bir sapıklık içindeydiler.'),
(458, 3, 165, '(Bedir\'de düşmanı) iki katına uğrattığınız bir musibet (Uhud\'da) size çarpınca mı: "Bu nereden" dediniz? De ki: "Bu başınıza gelen kendinizdendir". Şüphesiz Allah her şeye kâdirdir.'),
(459, 3, 166, 'İki topluluğun karşılaştığı günde başınıza gelen musibet de Allah\'ın izniyledir. Bu da müminleri belirlemesi ve hem de münafıklık yapanları ayırt etmesi içindir. Ve onlara: "Geliniz, Allah yolunda savaşınız veya (hiç olmazsa) savunmaya geçiniz." denilmişti. Onlar ise: "Biz savaşmasını (veya savaş olacağını) bilseydik arkanızdan gelirdik." demişlerdi. Onlar, o gün, imandan çok küfre yakındılar. kalblerinde olmayanı ağızlarıyla söylüyorlardı. Allah neyi gizlediklerini daha iyi bilendir.'),
(460, 3, 167, 'İki topluluğun karşılaştığı günde başınıza gelen musibet de Allah\'ın izniyledir. Bu da müminleri belirlemesi ve hem de münafıklık yapanları ayırt etmesi içindir. Ve onlara: "Geliniz, Allah yolunda savaşınız veya (hiç olmazsa) savunmaya geçiniz." denilmişti. Onlar ise: "Biz savaşmasını (veya savaş olacağını) bilseydik arkanızdan gelirdik." demişlerdi. Onlar, o gün, imandan çok küfre yakındılar. kalblerinde olmayanı ağızlarıyla söylüyorlardı. Allah neyi gizlediklerini daha iyi bilendir.'),
(461, 3, 168, 'Kendileri oturup kaldıkları halde kardeşleri için: "Eğer bize uysalardı öldürülmezlerdi" dediler. Onlara de ki: "Eğer iddianızda doğru iseniz, kendinizden ölümü uzaklaştırınız".'),
(462, 3, 169, 'Allah yolunda öldürülenleri sakın ölüler sanma. Bilakis onlar diridirler, Rab\'leri katında rızıklanmaktadırlar.'),
(463, 3, 170, 'Allah\'ın lütfundan verdiği nimetle sevinçlidirler. Arkalarından kendilerine ulaşamayan kimselere de hiç bir korku olmayacağını ve üzülmeyeceklerini müjdelemek isterler.'),
(464, 3, 171, 'Onlar, Allah\'ın nimetini, keremini ve Allah\'ın, müminlerin ecrini zayi etmeyeceğini müjdelerler.'),
(465, 3, 172, 'Kendilerine yara dokunduktan sonra da Allah ve Peygamberi\'nin davetine uydular. Hele onlardan iyilik edenlere ve gereğince Allah\'tan korkanlara büyük bir mükafat vardır.'),
(466, 3, 173, 'İnsanlar onlara: "Düşmanlarınız size karşı ordu topladı, onlardan korkun." dediklerinde, bu, onların imanını artırdı ve şöyle dediler: "Allah bize yeter. O ne güzel vekildir".'),
(467, 3, 174, 'Bunun üzerine kendilerine hiç bir kötülük dokunmadan Allah\'ın nimeti ve lütfuyla geri döndüler ve Allah\'ın rızasına uydular. Allah büyük lütuf sahibidir.'),
(468, 3, 175, '(Size o haberi getiren) ancak şeytandır, (sadece) kendi dostlarını korkutabilir. Onlardan korkmayın, eğer mümin iseniz benden korkun.'),
(469, 3, 176, 'Küfürde yarışanlar seni üzmesin. Onlar, Allah\'a hiç bir şekilde zarar veremezler. Allah onlara ahirette bir pay vermemek istiyor. Onlar için büyük bir azap vardır.'),
(470, 3, 177, 'İman karşılığında inkarı satın alanlar Allah\'a hiç bir zarar veremezler. Onlar için acı bir azap vardır.'),
(471, 3, 178, 'Kâfirler, kendilerine mühlet vermemizin, şahısları için hayırlı olduğunu sanmasınlar. Biz onlara bu mühleti, ancak günahlarını artırsınlar diye veriyoruz. Onlar için alçaltıcı bir azap vardır.'),
(472, 3, 179, 'Allah, müminleri içinde bulunduğunuz şu durumda bırakacak değildir, pisi temizden ayıracaktır. Ve Allah sizi gayba vakıf kılacak da değildir. Fakat Allah, peygamberlerinden dilediğini seçip (gaybı bildirir). O halde Allah\'a ve peygamberlerine iman edin. Eğer iman eder ve günahlardan korunursanız, sizin için büyük bir mükafat vardır.'),
(473, 3, 180, 'Allah\'ın, kendilerine lütfundan verdiği nimetlere karşı cimrilik edenler, bunun, kendileri için hayırlı olduğunu sanmasınlar. Hayır o, kendileri için şerdir. Cimrilik ettikleri şey, kıyamet gününde boyunlarına dolanacaktır. Göklerin ve yerin mirası Allah\'a aittir. Allah yaptıklarınızdan haberdardır.'),
(474, 3, 181, 'Allah, "Şüphesiz Allah fakirdir, biz zenginiz." diyenlerin lafını elbette duymuştur. Onların söylediklerini ve peygamberleri haksız yere öldürmelerini yazacağız ve şöyle diyeceğiz: "Tadın o yakıcı azabı!".'),
(475, 3, 182, '"Bu, kendi ellerinizin yapıp öne sürdüğünün karşılığıdır". Allah kullar(ın)a asla zulmetmez.'),
(476, 3, 183, '"Ateşin yiyeceği bir kurban getirmedikçe hiç bir peygambere iman etmeyeceğimize dair Allah bize ahidde bulundu." diyenlere de ki: "Benden önce size bazı peygamberler açık belgelerle ve sizin dediğiniz şeyle geldi. Eğer doğru insanlarsanız, ya onlarıniçin öldürdünüz?"'),
(477, 3, 184, 'Eğer seni yalanladılarsa, senden önce açık deliller, hikmetli sayfalar ve aydınlatıcı kitap getiren peygamberler de yalanlanmıştı.'),
(478, 3, 185, 'Her canlı ölümü tadacaktır. Kıyamet günü ecirleriniz size eksiksiz olarak verilecektir. Kim cehennemden uzaklaştırılıp cennete konursa o, gerçekten kurtuluşa ermiştir. Dünya hayatı, aldatıcı zevkten başka birşey değildir.'),
(479, 3, 186, 'Muhakkak siz, mallarınız ve canlarınız hususunda imtihan olunacaksınız. Sizden önce kendilerine kitap verilenlerden ve Allah\'a ortak koşanlardan size eziyet verici bir çok söz işiteceksiniz. Eğer sabreder ve Allah\'dan gereği gibi korkarsanız, şüphesiz işte bu azmi gerektiren işlerdendir.'),
(480, 3, 187, 'Bir zaman Allah, kendilerine kitap verilenlerden, "Onu mutlaka insanlara açıklayacaksınız, onu gizlemiyeceksiniz." diye söz almıştı. Onlar ise bunu kulak ardı ettiler ve onu az bir dünyalığa değiştiler. Yaptıkları bu alışveriş ne kadar kötüdür.'),
(481, 3, 188, 'O yaptıklarına sevinen ve yapmadıkları şeylerle de övülmek isteyenlerin (onacaklarını) sanma! Onların azaptan kurtulacaklarını da sanma! Onlar için can yakıcı bir azap vardır.'),
(482, 3, 189, 'Göklerin ve yerin mülkü Allah\'ındır. Allah her şeye kâdirdir.'),
(483, 3, 190, 'Göklerin ve yerin yaratılışında, gece ile gündüzün birbiri ardınca gelip gidişinde selim akıl sahipleri için gerçekten açık, ibretli deliller vardır.'),
(484, 3, 191, 'Onlar ayaktayken, otururken ve yanları üzerine yatarken Allah\'ı anarlar; göklerin ve yerin yaratılışı üzerinde düşünürler. Ve "Rabbimiz! Sen bunu boş yere yaratmadın, Sen yücesin, bizi ateşin azabından koru." derler.'),
(485, 3, 192, '"Rabbimiz! Sen kimi cehennem ateşine sokarsan onu rezil etmişsindir. Zalimlerin hiç yardımcıları yoktur".'),
(486, 3, 193, '"Rabbimiz! Biz, \'Rabbinize iman edin\' diye imana çağıran bir davetçi işittik, hemen iman ettik. Rabbimiz! Günahlarımızı bağışla, kötülüklerimizi ört, bizleri sana ermiş kullarınla beraber yanına al".'),
(487, 3, 194, '"Rabbimiz! bize peygamberlerine vaad ettiğini ver, kıyamet günü bizi rezil etme. Muhakkak sen verdiğin sözden dönmezsin".'),
(488, 3, 195, 'Rableri onlara şu karşılığı verdi: "Ben, erkek olsun, kadın olsun, sizden, hiçbir çalışanın amelini zayi etmeyeceğim. Sizler birbirinizdensiniz. Göç edenler, yurtlarından çıkarılanlar, yolumda eziyet edilenler, savaşanlar ve öldürülenler... Onların günahlarını elbette örteceğim ve Allah katından bir mükafat olmak üzere, onları altından ırmaklar akan cennetlere de koyacağım. En güzel mükafat Allah katındadır".'),
(489, 3, 196, 'Kâfirlerin diyar diyar dolaşmaları sakın seni aldatmasın.'),
(490, 3, 197, 'Bu, az bir geçimliktir. Sonra onların varacakları yer cehennemdir. Ne kötü bir yataktır orası!'),
(491, 3, 198, 'Fakat Rablerinden gereğince korkanlar için altlarından ırmaklar akan cennetler vardır. Onlar orada ebedî olarak kalacaklar, Allah katından ağırlanacaklardır. İyiler için Allah katındakiler daha hayırlıdır.'),
(492, 3, 199, 'Kitap ehlinden öyleleri var ki, Allah\'a inanırlar, size indirilene ve kendilerine indirileneAllah\'a boyun eğerek inanırlar. Allah\'ın âyetlerini az bir değere değişmezler. Onların mükafatı da Allah katındadır. Şüphesiz Allah, hesabı çabuk görendir.'),
(493, 3, 200, 'Ey iman edenler! Sabredin, düşmanlarınıza karşı sebat gösterin, nöbet bekleşin, Allah\'dan gereğince korkun ki, kurtuluşa eresiniz.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'Ey insanlar! Sizi bir tek nefisten yaratan ve ondan eşini yaratıp ikisinden bir çok erkekler ve kadınlar üreten Rabbinizden korkun; kendi adına birbirinizden dilekte bulunduğunuz Allah\'dan ve akrabalık (bağlarını kırmak)tan sakının. Şüphesiz Allah sizin üzerinizde gözeticidir.'),
(495, 4, 2, 'Öksüzlere mallarını verin ve kötüsünü (onlara vererek) iyisiyle değiştirmeyin. Onların mallarını, kendi mallarınıza karıştırıp yemeyin. Zira bu, büyük bir günahtır.'),
(496, 4, 3, 'Eğer öksüz kızlarla evlendiğinizde onlara karşı adaletli davranamamaktan korkarsanız, hoşunuza giden diğer kadınlardan iki, üç ve dörde kadar evlenebilirsiniz. Eğer adaleti gözetmemekten korkarsanız, o zaman bir tane ile veya elinizin altındakiyle (sahip olduğunuz câriye ile) yetinin. Doğruluktan ayrılmamak için bu daha elverişlidir.'),
(497, 4, 4, 'Kadınlara mehirlerini gönül hoşluğuyla verin. Eğer onlar gönül rızasıyla size bir şey bağışlarlarsa onu afiyetle yiyin.'),
(498, 4, 5, 'Allah\'ın, sizi başına diktiği mallarınızı aklı ermezlere vermeyin; o mallarla onları besleyin, giydirin ve onlara güzel söz söyleyin.'),
(499, 4, 6, 'Evlenme çağına gelinceye kadar yetimleri gözetip deneyin. Onların akılca olgunlaştıklarını görürseniz, mallarını kendilerine teslim edin. "Büyüyecekler de mallarına sahip olacaklar" endişesiyle onları israf ederek, tez elden yemeyin. Zengin olan, onların malını yemekten çekinsin. Fakir olan ise, meşrû sûrette yesin. Mallarını kendilerine verdiğiniz zaman, bunu şahitler karşısında yapın. Hesap görücü olarak Allah yeter.'),
(500, 4, 7, 'Ana, baba ve akrabaların miras olarak bıraktıklarında erkeklerin hissesi vardır. Kadınların da ana, baba ve akrabaların bıraktıklarında hisseleri vardır. Bunlar, az olsun çok olsun, farz kılınmış bir hissedir.'),
(501, 4, 8, 'Paylaşma sırasında akrabalar, öksüzler, yoksullar hazır bulunurlarsa, onlara da bir şey verin ve onlara güzelce sözler söyleyerek gönüllerini alın.'),
(502, 4, 9, 'Kendileri, geriye zayıf çocuklar bıraktıkları takdirde, onların geleceğinden endişe duyacak olanlar, (yetimler hakkında da aynı) endişeyi duysunlar, Allah\'dan sakınsınlar ve doğru söz söylesinler.'),
(503, 4, 10, 'Yetimlerin mallarını haksız yere yiyenler, muhakkak ki karınlarını ateşle doldurmuş olurlar ve cehennemi boylarlar.'),
(504, 4, 11, 'Allah size evlatlarınızın miras taksimini şöyle emrediyor: Çocuklarınızda, erkeğe iki kadın payı kadar, eğer hepsi kadın olmak üzere ikiden de fazla iseler, bunlara mirasın üçte ikisi ve eğer bir tek kadın ise o zaman ona malın yarısı vardır. Eğer ölen, ana ve baba ile birlikte çocuklar da bırakmışsa ana babanın her birine ölenin terekesinden altıda bir; şâyet ölenin çocuğu yok da, mirasçı olarak ana ve babası kalmışsa, ananın payı üçte birdir. Eğer ölenin kardeşleri varsa terekenin altıda biriananındır. Bu paylar, ölenin borçları ödenip, vasiyeti de yerine getirildikten sonra hak sahiplerine verilir. Baba ve çocuklardan, hangisinin size fayda bakımından daha yakın olduğunu, siz bilmezsiniz. Bütün bunlar Allah tarafından farz kılınmıştır. Şüphesiz Allah alîmdir, hakîmdir.'),
(505, 4, 12, 'Eğer hanımlarınızın çocukları yoksa, bıraktıkları mirasın yarısı sizindir. Şâyet bir çocukları varsa o zaman mirasın dörtte biri sizindir. Bu paylar, ölenin vasiyeti yerine getirildikten ve varsa, borcu ödendikten sonra verilir. Eğer siz çocuk bırakmadan ölürseniz, geriye bıraktığınız mirasın dörtte biri hanımlarınızındır. Şâyet çocuklarınız varsa o zaman bıraktığınız mirasın sekizde biri hanımlarınızındır. Bu paylar, yaptığınız vasiyetler yerine getirilip ve varsa borcunuz ödendikten sonra verilir. Eğer ölen bir erkek veya kadının çocuğu ve babası bulunmadığı halde kelâle olarak (yan koldan) mirasına konuluyor ve kendisinin bir erkek veya kızkardeşi bulunuyorsa, bunlardan herbirinin miras payı terekenin altıda biridir. Eğer mevcut olan kardeşler bundan daha çok iseler, bu takdirde kardeşler mirasın üçte birini zarara uğratılmaksızın aralarında eşit olarak taksim ederler. Bu paylar ölenin vasiyeti yerine getirilip ve varsa borcu ödendikten sonra verilir. Bunlar, Allah tarafından bir emirdir. Allah her şeyi bilen ve yarattıklarına çok yumuşak davranandır.'),
(506, 4, 13, 'İşte bütün bu hükümler, Allah\'ın koyduğu hükümler ve çizdiği sınırlardır. Kim Allah\'a ve Peygamberine itâat ederse Allah onu altlarından ırmaklar akan cennetlere koyar. Onlar, orada ebedî olarak kalacaklardır. İşte büyük kurtuluş budur.'),
(507, 4, 14, 'Kim de Allah\'a ve Peygamberine isyan eder ve Allah\'ın koyduğu sınırları aşarsa Allah onu da ebedî kalacağı cehennem ateşine koyar. Onun için alçaltıcı bir azab vardır.'),
(508, 4, 15, 'Kadınlarınızdan zina edenlere karşı, içinizden dört şahit getirin. Eğer onlar, şahitlik yaparlarsa, bu kadınları, ölüm alıp götürünceye kadar veya Allah onlara bir çıkış yolu açıncaya kadar evlerde hapsedin.'),
(509, 4, 16, 'Sizlerden zina edenlerin her ikisine de eziyet edin. Eğer onlar tevbe edip kendilerini ıslah ederlerse onlardan vazgeçin. Çünkü Allah tevbeleri kabul eden ve çok merhamet edendir.'),
(510, 4, 17, 'Ancak Allah\'ın kabul etmesini vaad buyurduğu tevbe, o kimseler içindir ki, bilmeyerek günah işleyip hemen tevbe edenlerin tevbesidir. İşte Allah bunların tevbelerini kabul eder. Allah alîmdir hakîmdir. (Her şeyi bilendir, hikmet sahibidir).'),
(511, 4, 18, 'Yoksa günah işleyip de kendisine ölüm gelince: "İşte ben şimdi tevbe ettim." diyen kimselerin tevbesi kabul edilmez. Kâfir olarak ölenlerin de tevbeleri kabul edilmez. İşte bunlara ahirette can yakıcı bir azap hazırlamışızdır.'),
(512, 4, 19, 'Ey iman edenler! Kadınlara zorla varis olmanız size helal değildir. Verdiğiniz mehrin bir kısmını kurtaracaksınız diye, onları sıkıştırmanız da helal değildir. Ancak açık bir hayasızlık yapmış olurlarsa başka. Onlarla iyi geçinin. Eğer kendilerinden hoşlanmadınızsa, olabilir ki, siz bir şeyden hoşlanmasanız da Allah onda bir çok hayır takdir etmiş bulunur.'),
(513, 4, 20, 'Eğer bir eşi bırakıp da yerine diğer bir eş almak isterseniz, öncekine yüklerle mehir vermiş de bulunsanız, ondan bir şey geri almayın. O malı bir iftira ve açık bir günah isnadı yaparak geri alır mısınız?'),
(514, 4, 21, 'Birbirinizle kaynaşıp başbaşa kalmışken ve onlar sizden kuvvetli bir teminat almışken verdiğinizi nasıl geri alabilirsiniz?'),
(515, 4, 22, 'Cahiliye devrinde geçenler müstesna, babalarınızın nikahladığı kadınlarla evlenmeyiniz. Şüphe yok ki o, pek çirkindi, iğrenç idi, o ne fena bir âdetti.'),
(516, 4, 23, 'Size şunları nikahlamak haram kılındı: Anneleriniz, kızlarınız, kız kardeşleriniz, halalarınız, teyzeleriniz, erkek ve kız kardeşlerinizin kızları, sizi emziren süt anneleriniz, süt kızkardeşleriniz ve karılarınızın anneleri, ve kendileri ile zifafa girdiğiniz kadınlarınızdan olan ve evlerinizde bulunan üvey kızlarınız. Eğer üvey kızlarınızın anneleri ile zifafa girmemişseniz onlarla evlenmenizde size bir günah yoktur. Sulbünüzden gelen (öz) oğullarınızın hanımları ile evlenmeniz ve iki kız kardeşi birlikte nikahlamanız da haramdır. Ancak cahiliyyet devrinde geçen geçmiştir. Şüphesiz ki Allah gafur (çok bağışlayıcı) ve çok merhamet edicidir.'),
(517, 4, 24, 'Bir de harb esiri olarak sahibi bulunduğunuz cariyeler müstesna, evli kadınlarla evlenmeniz de size haram kılındı. Bütün bunlar Allah\'ın üzerinize farz kıldığı hükümlerdir. Bunların dışında kalanlar ise iffetli olarak zina etmeksizin mallarınızla mehir vermek suretiyle evlenmek istemeniz size helal kılındı. O halde onlardan nikah ile faydalanmanıza karşılık mehirlerini kendilerine verin ki, bu farzdır. O mehri takdir edip kesinleştirdikten sonra birbirinizi razı etmenizde bir mahzur yoktur. Şüphesiz ki Allah her şeyi çok iyi bilendir, hüküm ve hikmet sahibidir.'),
(518, 4, 25, 'Sizden her kim hür mümin kadınları nikah edecek bir zenginliğe gücü yetmiyorsa, ona da ellerinizin altındaki mümin cariyelerinizden efendilerinin rızası ile nikahlamak var. Allah sizin imanınızı daha iyi bilir. Siz birbirinizdensiniz. O halde sahiplerinin izni ile ve mehirlerini örfe göre vermek suretiyle cariyelerden iffetli olan, zina etmeyen, dost da edinmeyenlerle evlenin. Evlendikten sonra bir fuhuş yaparlarsa, o vakit hür kadınlar hakkında gerekli bulunan cezanın yarısı kendilerine lazım gelir. Bu hükümler, içinizden günah işlemekten korkanlaradır. Sabretmeniz ise, sizin için daha hayırlıdır. Allah Gafûrdur, Rahimdir (çok bağışlayıcıdır, çok merhamet edicidir).'),
(519, 4, 26, 'Allah, sizlere bilmediklerinizi bildirmek, sizden öncekilerin yollarını size göstermek ve tevbenizi kabul etmek istiyor. Allah, her şeyi çok iyi bilendir, hüküm ve hikmet sahibidir.'),
(520, 4, 27, 'Allah sizin tevbenizi kabul etmek istiyor. Halbuki şehvetlerine uyanlar ise, sizin doğru yoldan büyük bir meyl ile sapmanızı istiyorlar.'),
(521, 4, 28, 'Allah, din hususundaki ağır teklifleri sizden hafifletmek istiyor. Çünkü insan sabır ve tahammül bakımından zayıf yaratılmıştır.'),
(522, 4, 29, 'Ey iman edenler! Mallarınızı aranızda haksızlıkla yemeyin. Ancak kendi rızanızla yaptığınız ticaretle yemeniz helaldir. Birbirinizin canına kıymayın. Şüphesiz Allah, size karşı çok merhametlidir.'),
(523, 4, 30, 'Kim, zulüm ve tecavüz yolu ile bu yasakları işlerse, yakında onu cehennem ateşine atacağız. Onu ateşe atmak da Allah\'a pek kolaydır.'),
(524, 4, 31, 'Eğer siz, yasaklandığınız büyük günahlardan sakınırsanız, diğer kusurlarınızı örter, sizi güzel bir makama koyarız.'),
(525, 4, 32, 'Bir de Allah\'ın bazınıza, diğerinden fazla verdiği şeyleri temenni etmeyin. Erkeklere hak ettiklerinden bir pay vardır. Kadınlara da kendi kazandıklarından bir pay vardır. İsteklerinizi Allah\'ın fazlından ve kereminden isteyin. Gerçekten Allah her şeyi hakkıyla bilendir.'),
(526, 4, 33, 'Anne, baba ve akrabaların bıraktıkları her şey için bir mirasçı tayin ettik. Yemin akdiyle mirasçı kıldıklarınızın paylarını da verin. Şüphesiz Allah, her şeye şahittir.'),
(527, 4, 34, 'Erkekler, kadın üzerine idareci ve hakimdirler. Çünkü Allah birini (cihad, imamet, miras gibi işlerde) diğerinden üstün yaratmıştır. Bir de erkekler mallarından (aile fertlerine) harcamaktadırlar. İyi kadınlar, itaatkar olanlar ve Allah\'ın korunmasını emrettiği şeyleri kocalarının bulunmadığı zamanlarda da koruyanlardır. Fenalık ve geçimsizliklerinden korktuğunuz kadınlara gelince: Önce kendilerine öğüt verin, yataklarından ayrılın. Bunlar da fayda vermezse dövün. Eğer size itaat ederlerse kendilerini incitmeye başka bir bahane aramayın. Çünkü Allah çok yücedir, çok büyüktür.'),
(528, 4, 35, 'Eğer karıkoca arasının açılmasından endişeye düşerseniz bir hakem erkeğin tarafından, bir hakem de kadının ailesinden kendilerine gönderin. Bu arabulucu hakemler gerçekten barıştırmak isterlerse, Allah karıkoca arasındaki dargınlık yerine geçim verir. Şüphesiz ki Allah hakkıyla bilendir, her şeyin aslından haberdardır.'),
(529, 4, 36, 'Allah\'a ibadet edin ve O\'na hiçbir şeyi ortak koşmayın. Sonra anaya, babaya, akrabaya, yetimlere, yoksullara, akraba olan komşulara, yakın komşulara, yanında bulunan arkadaşa, yolda kalanlara, sahip olduğunuz kölelere iyilik edin. Şüphesiz Allah, kibirlenen ve övünen kimseyi sevmez.'),
(530, 4, 37, 'Onlar ki hem kıskanır, cimrilik ederler, hem de herkese cimrilik tavsiye ederler ve Allah\'ın kendilerine lütfundan verdiği nimeti gizlerler. Biz kâfirlere alçaltıcı bir azap hazırladık.'),
(531, 4, 38, 'Bunlar, Allah\'a ve ahiret gününe iman etmedikleri halde mallarını, insanlara gösteriş yapmak için harcarlar. Şeytan kimin arkadaşı olursa, o ne kötü arkadaştır!'),
(532, 4, 39, 'Bunlar, Allah\'a ve ahiret gününe iman etselerdi ve Allah\'ın verdiği rızıktan gösterişsiz harcasalardı kendilerine ne zarar gelirdi? Allah onların söz ve işlerini çok iyi bilendir.'),
(533, 4, 40, 'Şüphesiz ki Allah, hiç kimseye zerre kadar zulüm etmez. Eğer yapılan iyilik zerre kadar da olsa, onun sevabını kat kat artırır. Ve kendi katından büyük bir mükafat verir.'),
(534, 4, 41, 'Her ümmetten bir şahit getirdiğimiz ve seni de onların üzerine bir şahit yaptığımız zaman bakalım kâfirlerin hali ne olacak!..'),
(535, 4, 42, 'Allah\'ı, inkar edip peygambere isyan edenler, o kıyamet günü yerle bir olmayı isterler. Allah\'tan hiçbir sözü gizleyemezler.'),
(536, 4, 43, 'Ey iman edenler! Sarhoş iken ne söylediğinizi bilinceye kadar namaza yaklaşmayın. Cünüb iken de yolcu olanlar müstesna gusül edinceye kadar namaza yaklaşmayın. Eğer hasta olur, veya yolculukta bulunursanız veyahut biriniz abdest bozmaktan gelince veya cinsî münasebette bulunup, su da bulamazsanız o zaman tertemiz bir toprak ile teyemmüm edin. Niyetle yüzlerinize ve ellerinize sürün. Şüphesiz ki Allah çok affedicidir, çok bağışlayıcıdır.'),
(537, 4, 44, 'Kendilerine kitaptan bir nasib verilmiş olanları görmüyor musun? Onlar, sapıklığı satın alıyorlar ve sizin de yoldan sapmanızı istiyorlar.'),
(538, 4, 45, 'Allah sizin düşmanlarınızı çok iyi bilir. Gerçek bir dost olarak Allah yeter. Ve yardımcı olarak da Allah yeter.'),
(539, 4, 46, 'Yahudilerden bir kısmı, (Allah\'ın kitabındaki) kelimeleri esas mânâsından kaydırıp; dillerini eğerek ve dine saldırarak, "Sözünü işittik, emirlerine isyan ettik, dinle, dinlemez olası ve râinâ (bizi gözet)" diyorlar. Halbuki onlar, "İşittik ve itaatettik; dinle ve bize de bak" deselerdi bu, kendileri için daha hayırlı ve daha doğru olurdu. Fakat Allah, küfürleri yüzünden kendilerini lanetlemiştir. Artık onlar, pek azı müstesna, iman etmezler.'),
(540, 4, 47, 'Ey kendilerine kitap verilenler! Gelin yanınızda bulunan (Tevrat)ı tasdik etmek üzere indirdiğimiz bu kitaba iman edin. Biz birtakım yüzleri silip de enselerine çevirmeden yahut cumartesi halkını (yahudileri) lanetlediğimiz gibi onları lanetlemeden önce iman edin. Yoksa Allah\'ın emri mutlaka yerine gelecektir.'),
(541, 4, 48, 'Doğrusu Allah, kendisine ortak koşulmasını asla affetmez. Ondan başkasını (diğer günahları) ise, dilediği kimseler için bağışlar ve mağfiret buyurur. Her kim Allah\'a şirk koşarsa gerçekten pek büyük bir günah ile iftira etmiş olur.'),
(542, 4, 49, 'Kendi nefislerini temize çıkaranları görmüyor musun? Hayır! Ancak Allah, dilediğini temize çıkarır. Onlara kıl kadar zulmedilmez.'),
(543, 4, 50, 'Bak nasıl da Allah\'a yalan uyduruyorlar. Apaçık bir günah olarak bu yeter.'),
(544, 4, 51, '"Şu kendilerine kitaptan (okuma yazmadan) bir nasib verilmiş olanları görmüyor musun! Onlar puta ve şeytana inanıyorlar. Ve Allah\'ı tanımayanlara, "Bunlar, müminlerden daha doğru yoldadır." diyorlar.'),
(545, 4, 52, 'Onlar, Allah\'ın lanet ettiği kimselerdir. Allah kime lanet ederse artık ona asla bir yardımcı bulamazsın.'),
(546, 4, 53, 'Yoksa onların mülkten bir payı mı vardır. Eğer öyle olsaydı, insanlara bir çekirdeğin zerresini bile vermezlerdi.'),
(547, 4, 54, 'Yoksa onlar, Allah\'ın lütuf ve kereminden insanlara verdiği nimetleri kıskanıyorlar mı? Şüphesiz biz, İbrahim ailesine de kitap ve hikmeti vermiştik. Hem de onlara büyük bir mülk ve saltanat ihsan ettik.'),
(548, 4, 55, 'İşte o yahudilerden bir kısmı ona iman etti. Bir kısmı da ondan yüz çevirdi. O iman etmeyenlere cehennem alevi yeter.'),
(549, 4, 56, 'Şüphesiz ki âyetlerimizi inkâr eden kâfirleri biz yarın bir ateşe atacağız. Derileri piştikçe azabı duysunlar diye, kendilerine başka deriler vereceğiz. Çünkü, Allah gerçekten çok güçlüdür, hüküm ve hikmet sahibidir.'),
(550, 4, 57, 'İman edip salih ameller işliyenleri ise, altlarından ırmaklar akan cennetlere koyacağız. Orada ebedî olarak kalacaklar. Onlara orada tertemiz eşler vardır. Onları, koyu gölgeler altında bulunduracağız.'),
(551, 4, 58, 'Allah size, emanetleri ehline vermenizi ve insanlar arasında hükmettiğiniz zaman adaletle hükmetmenizi emrediyor. Allah, bununla size ne güzel öğüt veriyor. Şüphesiz ki Allah her şeyi hakkıyla işiten, hakkıyla görendir.'),
(552, 4, 59, 'Ey iman edenler! Allah\'a itaat edin, Peygambere de itaat edin ve sizden olan emir sahibine de itaat edin. Eğer herhangi bir şeyde anlaşmazlığa düşerseniz; Allah\'a ve ahiret gününe gerçekten inanıyorsanız, onu Allah ve Resulüne arz edin. Bu, daha iyidir ve sonuç bakımından da daha güzeldir.'),
(553, 4, 60, 'Şunları görmüyor musun? Kendilerinin sana indirilene ve senden önce indirilene inandıklarını ileri sürüyorlar da tağuta inanmamaları kendilerine emrolunduğu halde, tağut önünde muhakemeleşmek istiyorlar. Şeytan da onları bir daha dönemeyecekleri kadar iyice sapıklığa düşürmek istiyor.'),
(554, 4, 61, 'Onlara: "Allah\'ın indirdiğine ve Peygambere gelin!" denince, münafıkların senden büsbütün uzaklaştıklarını görürsün.'),
(555, 4, 62, 'Ya nasıl, elleriyle yaptıkları yüzünden başlarına bir felaket gelince, hemen sana geldiler de: "Biz sadece iyilik etmek ve arayı bulmak istedik." diye Allah\'a yemin ediyorlar.'),
(556, 4, 63, 'Onlar, Allah\'ın kalblerindekini bildiği kimselerdir; Onlara aldırma, onlara öğüt ver ve onların içlerine tesir edecek güzel söz söyle!'),
(557, 4, 64, 'Biz hangi peygamberi gönderdikse, sırf Allah\'ın izni ile itaat edilmek üzere gönderdik. Eğer onlar kendilerine zulmettikleri zaman sana gelseler de Allah\'tan günahlarının bağışlanmasını dileselerdi ve Resul de onların bağışlanmasını dileseydi, elbette Allah\'ı affedici, merhametli bulurlardı.'),
(558, 4, 65, 'Hayır! Rabbine andolsun ki iş bildikleri gibi değil, onlar aralarında çıkan çekişmeli işlerde seni hakem yapıp sonra da senin verdiğin hükme karşı içlerinde hiçbir sıkıntı duymaksızın, tam bir teslimiyetle boyun eğmedikçe iman etmiş olamazlar.'),
(559, 4, 66, 'Eğer biz onlara: "Kendinizi öldürün, veya yurtlarınızdan çıkın." diye yazmış olsaydık, içlerinden pek azı hariç, bunu yapamazlardı. Fakat kendilerine verilen öğütleri tutsalardı, elbette haklarında hem daha hayırlı, hem de daha sağlam olurdu.'),
(560, 4, 67, 'Ve o zaman elbette kendilerine katımızdan büyük mükafat verirdik.'),
(561, 4, 68, 'Ve onları elbette doğru yola iletirdik.'),
(562, 4, 69, 'Kim Allah\'a ve Peygambere itaat ederse işte onlar, Allah\'ın kendilerine nimet verdiği peygamberlerle, sıddıklarla, şehidlerle, iyilerle birliktedir. Bunlar ne güzel arkadaştır!'),
(563, 4, 70, 'Bu lütuf Allah\'tandır. Bilen olarak Allah yeter.'),
(564, 4, 71, 'Ey iman edenler! Düşmana karşı her türlü savunma tedbirinizi alınız. Onlara karşı ya küçük birlikler halinde hareket ediniz veya topyekün seferber olunuz.'),
(565, 4, 72, 'Şüphesiz içinizden bir kısmı vardır ki, pek ağır davranır. Eğer başınıza bir musibet gelirse: "Allah bana lutfetti de onlarla beraber bulunmadım." der.'),
(566, 4, 73, 'Ve eğer Allah\'tan size bir lütuf ve zafer erişecek olsa, sizinle kendisi arasında hiç sevgi yokmuş gibi, bu sefer de hiç şüphesiz şöyle diyecek: "Ah ne olurdu, onlarla beraber olaydım da büyük murada ereydim."'),
(567, 4, 74, 'O halde geçici dünya hayatını, ebedî ahiret hayatı karşılığında satacak olanlar, Allah yolunda savaşsınlar. Her kim Allah yolunda savaşır da öldürülür veya galip gelirse, her iki durumda da biz ona yarın pek büyük bir mükafat vereceğiz.'),
(568, 4, 75, 'Hem size ne oluyor ki, Allah yolunda: "Ey Rabbimiz! bizleri bu halkı zâlim olan memleketten çıkar, tarafından bizi iyi idare edecek bir sahip ve bize katından bir kurtarıcı gönder" diye yalvarıp duran zayıf ve zavallı erkekler, kadınlar ve çocukların kurtarılması uğrunda savaşa çıkmıyorsunuz?'),
(569, 4, 76, 'İman edenler, Allah yolunda savaşırlar. İnkâr edenler de tağut yolunda savaşırlar. O halde siz şeytanın taraftarlarına karşı savaşın. Çünkü şeytanın hilesi zayıftır.'),
(570, 4, 77, 'Kendilerine, "Ellerinizi savaştan çekin, namazı kılın, zekatı verin" denilenleri görmedin mi? Üzerlerine savaş yazılınca hemen içlerinden bir kısmı insanlardan, Allah\'tan korkar gibi, hatta daha çok korkarlar ve "Rabbimiz! Niçin bize savaş yazdın? Ne olurdu bize azıcık bir müddet daha tanımış olsaydın da biraz daha yaşasaydık?" derler. Onlara de ki: "Dünya zevki ne de olsa azdır, ahiret, Allah\'a karşı gelmekten sakınan için daha hayırlıdır ve size kıl kadar haksızlık edilmez."'),
(571, 4, 78, 'Her nerede olursanız olun ölüm size yetişir, son derece sağlam kaleler içinde de bulunsanız yine kurtulamazsınız. Onlara bir iyilik erişirse "Bu, Allahtandır" derler, bir kötülüğe uğrarlarsa, "Bu, senin yüzündendir." derler. Ey Muhammed! De ki: "Hepsi Allah\'tandır." Bu topluma ne oluyor ki, hiç söz anlamaya yanaşmıyorlar?'),
(572, 4, 79, '(Ey insanoğlu!) sana gelen her iyilik Allah\'tandır, sana ne kötülük dokunursa kendindendir. Ey Muhammed! Biz seni bütün insanlara bir elçi olarak gönderdik. Buna şahit olarak da Allah yeter.'),
(573, 4, 80, 'Kim peygambere itaat ederse Allah\'a itaat etmiş olur. Kim de yüz çevirirse, biz seni onlara bekçi olarak göndermedik.'),
(574, 4, 81, 'Sana "Peki" derler, fakat senin yanından çıktıklarında, içlerinden birtakımı, geceleyin (gündüz) söylemiş olduklarının tersini kurarlar. Allah onların geceleyin tasarladıklarını yazıyor. Sen onlara aldırma. Allah\'a güven. Vekil olarak Allah yeter.'),
(575, 4, 82, 'Onlar hâlâ Kur\'ân\'ı gereği gibi düşünüp anlamaya çalışmazlar mı? Eğer o Allah\'tan başkası tarafından indirilmiş olsaydı mutlaka onda birçok çelişkiler bulurlardı.'),
(576, 4, 83, 'Kendilerine güven veya korku hususunda bir haber geldiğinde onu hemen yayıverirler. Halbuki onu peygambere ve aralarında yetkili kimselere götürselerdi, onlardan sonuç çıkarmaya gücü yetenler, onu anlarlardı. Allah\'ın üzerinizdeki lütfu ve rahmeti olmasaydı, pek azınız hariç, şeytana uyardınız.'),
(577, 4, 84, '(Ey Muhammed) Allah yolunda savaş! Sen ancak kendi yaptığından sorumlusun. Müminleri de savaşa teşvik et. Umulur ki, Allah kâfirlerin gücünü kırar. Hiç şüphesiz ki Allah kuvvet ve kudretçe çok daha güçlü, ve cezası daha çetindir.'),
(578, 4, 85, 'Kim güzel bir işte aracılık ederse, ona o işin sevabından bir pay vardır. Kim de kötü bir şeyde aracılık yaparsa, ona da o kötülükten bir pay vardır. Allah her şeyi gözetip karşılığını verir.'),
(579, 4, 86, 'Siz bir selam ile selamlandığınız zaman, siz de ondan daha güzeliyle karşılık verin veya verilen selamı aynen iade edin. Şüphesiz Allah, her şeyin hesabını gereği gibi yapandır.'),
(580, 4, 87, 'Kendinden başka ilâh olmayan Allah, sizi kıyamet gününde mutlaka biraraya toplayacaktır. Bunda asla şüphe yoktur. Allah\'tan daha doğru sözlü kim olabilir?'),
(581, 4, 88, 'O halde, siz niçin münafıklar hakkında iki gruba ayrılıyorsunuz? Allah onları kazandıkları günah yüzünden terslerine döndürdüğü halde Allah\'ın saptırdığını yola getirmek mi istiyorsunuz? Allah kimi saptırırsa, sen onun için bir çıkış yolu bulamazsın.'),
(582, 4, 89, 'Onlar, küfür işledikleri gibi, sizin de küfür işleyip kendileriyle bir olmanızı arzu ettiler. Onun için, onlar Allah yolunda hicret edinceye kadar içlerinden dost edinmeyin. Eğer bundan yüz çevirirlerse onları yakalayın ve bulduğunuz yerde öldürün; Onlardan ne bir dost, ne de bir yardımcı edinmeyin.'),
(583, 4, 90, 'Ancak o kimselere dokunmayın ki, sizinle aralarında anlaşma olan bir kavme sığınmış bulunurlar. Yahut ne sizinle, ne de kendi kavimleriyle savaşmayı gönüllerine sığdıramayıp tarafsız olarak size gelmişlerdir. Eğer Allah dileseydi, onları size musallat kılardı, onlar da sizinle savaşırlardı. Eğer onlar sizden uzak dururlar, sizinle savaşmayıp size barış teklif ederlerse, Allah, sizin için onlar aleyhine bir yol vermemiştir.'),
(584, 4, 91, 'Diğer birtakım kimseleri de bulacaksınız ki; hem sizden emin olmak, hem de kavimlerinden emin olmak isterler. Fitne için her davet olunuşlarında onun içine başaşağı dalarlar. Eğer bunlar sizden çekinmezlerse, kendilerini bulduğunuz yerde yakalayın ve öldürün. İşte bunlar aleyhinde size açık bir ferman verdik.'),
(585, 4, 92, 'Hata dışında bir mümin, diğer bir mümini öldüremez. Ve kim bir mümini yanlışlıkla öldürürse, mümin bir köle azad etmesi ve ölenin ailesine (varislerine) teslim edilecek bir diyet vermesi gerekir. Ancak ölünün ailesinin bağışlaması müstesnadır. Eğer öldürülen, mümin olmakla beraber size düşman bir kavimden ise, o zaman, öldürenin bir köle azad etmesi gerekir. Eğer öldürülen sizinle aralarında antlaşma olan bir kavimden ise, öldürenin, ölenin ailesine diyet vermesi ve mümin bir köle azad etmesi gerekir. Bunlara gücü yetmeyenin de Allah tarafından tevbesinin kabulü için arka arkaya iki ay oruç tutması gerekir. Allah, Alimdir (her şeyi bilendir), Hakimdir (hüküm ve hikmet sahibidir).'),
(586, 4, 93, 'Kim bir mümini kasten öldürürse, cezası, içinde ebedî olarak kalacağı cehennemdir. Allah ona gazab ve lanet etmiş ve onun için büyük bir azab hazırlamıştır.'),
(587, 4, 94, 'Ey İman edenler! Allah yolunda cihada çıktığınız zaman, mümini kâfirden ayırmak için iyice araştırın. Size selam veren kimseye, dünya hayatının menfaatini gözeterek, "Sen mümin değilsin" demeyin. Allah katında çok ganimetler var. İslâm\'a ilk önce girdiğiniz zaman siz de öyle idiniz. Sonra Allah size lutufta bulundu. Onun için iyice araştırın. Şüphesiz ki Allah, yaptıklarınızdan haberdardır.'),
(588, 4, 95, 'Müminlerden özür sahibi olmaksızın oturanlarla Allah yolunda mallarıyla, canlarıyla cihad edenler eşit olamazlar. Allah, mallarıyla, canlarıyla cihad edenleri, derece itibariyle, oturanlardan üstün kıldı. Allah onların hepsine de cenneti vaad etmiştir. Bununla beraber Allah mücahitlere, oturanların üzerinde büyük bir ecir vermiştir.'),
(589, 4, 96, 'Kendi katından derece derece rütbeler, bir mağfiret ve rahmet vermiştir. Öyle ya, O çok bağışlayıcı, çok merhamet edicidir.'),
(590, 4, 97, 'Melekler, kendilerine zulmeden kişilerin canlarını aldıklarında, onlara, "Ne işte idiniz?" derler. Onlar da: "Biz yer yüzünde zayıf kimselerdik." derler. Melekler: "Allah\'ın yeryüzü geniş değil miydi, siz de orada hicret etseydiniz ya?" derler. İşte bunların varacakları yer cehennemdir. O ne kötü gidiş yeridir.'),
(591, 4, 98, 'Ancak gerçekten aciz ve zayıf olan, çaresiz kalan ve hicret etmeye yol bulamayan erkekler, kadınlar ve çocuklar hariç...'),
(592, 4, 99, 'Umulur ki, Allah bu kimseleri affeder. Allah çok affedici, çok bağışlayıcıdır.'),
(593, 4, 100, 'Her kim Allah yolunda hicret ederse, yeryüzünde gidecek çok yer de bulur, genişlik de bulur. Her kim Allah\'a ve Peygamberine hicret etmek maksadıyla evinden çıkar da sonra kendisine ölüm yetişirse, kuşkusuz onun mükafatı Allah\'a düşer. Allah çok bağışlayıcıdır, çok merhamet edicidir.'),
(594, 4, 101, 'Yeryüzünde sefere çıktığınızda kâfirlerin size bir kötülük yapacağından korkarsanız namazı kısaltmanızda size bir vebal yoktur. Kuşkusuz kâfirler sizin apaçık düşmanınızdır.'),
(595, 4, 102, 'Sen onların aralarında bulunup da onlara namaz kıldırdığında içlerinden bir kısmı seninle beraber namaza dursun. Silahlarını da yanlarına alsınlar. Bunlar secdeye vardıklarında diğer bir kısmı arkanızda beklesin. Sonra o namaz kılmamış olan diğer kısım gelsin seninle beraber kılsınlar ve ihtiyatlı bulunsunlar, silahlarını yanlarına alsınlar. Kâfirler arzu ederler ki, silahlarınızdan ve eşyanızdan bir gafil olsanız da size ani bir baskın yapsalar. Eğer size yağmur gibi bir eziyet erişir veya hasta olursanız silahlarınızı bırakmanızda bir vebal yoktur. Bununla beraber ihtiyatı elden bırakmayın. Kuşkusuz Allah kâfirlere alçaltıcı bir azap hazırlamıştır.'),
(596, 4, 103, 'O korkulu zamanda namazı kıldınız mı gerek ayakta, gerek otururken ve gerek yanlarınız üzerinde hep Allah\'ı zikredin. Korkudan kurtulduğunuzda namazı tam erkanı ile kılın. Çünkü namaz müminlere belirli vakitlerde yazılı bir farzdır.'),
(597, 4, 104, 'Düşman topluluğunu takip etmede gevşeklik göstermeyin. Eğer siz acı duyuyorsanız, kuşkusuz onlar da sizin acı duyduğunuz gibi acı çekiyorlar. Oysa siz Allah\'tan onların ümit edemeyecekleri şeyleri umuyorsunuz. Kuşkusuz Allah her şeyi bilendir, hikmet sahibidir.'),
(598, 4, 105, 'Biz sana Kitab (Kur\'ân)ı hak olarak indirdik ki, insanlar arasında Allah\'ın sana gösterdiği şekilde hüküm veresin. Sakın hainlerin savunucusu olma!'),
(599, 4, 106, 'Allah\'tan bağışlanmanı dile. Şüphesiz, Allah bağışlayıcıdır, esirgeyicidir.'),
(600, 4, 107, 'Kendilerine hainlik edenleri savunma. Muhakkak Allah hain günahkârları sevmez.'),
(601, 4, 108, 'Bunlar, insanlardan (hainliklerini) gizlerler de, Allah\'tan gizlemezler. Oysa O, geceleyin istemediği şeyi kurarlarken onların yanı başlarındadır. Allah, onların yaptıklarını (ilmiyle) kuşatmıştır.'),
(602, 4, 109, 'Haydi siz dünya hayatında onları savunuverdiniz (diyelim). Peki kıyamet gününde Allah\'ın huzurunda onları kim savunacaktır? Yahut onlara kim vekil olacaktır?'),
(603, 4, 110, 'Kim bir kötülük işler, yahut nefsine zulmeder, sonra da Allah\'tan bağışlanmasını dilerse, Allah\'ı bağışlayıcı ve esirgeyici bulur.'),
(604, 4, 111, 'Kim bir kötülük işlerse, kendi nefsine kötülük etmiş olur. Allah her şeyi hakkıyle bilendir, hikmet sahibidir.'),
(605, 4, 112, 'Kim bir hata veya bir günah işler de sonra onu bir suçsuzun üzerine atarsa, muhakkak iftira etmiş ve apaçık bir günah yüklenmiş olur.'),
(606, 4, 113, 'Eğer Allah\'ın sana lütuf ve merhameti olmasaydı, onlardan bir güruh seni sapıtmaya çalışırdı. Halbuki onlar, ancak kendi nefislerini saptırırlar, sana hiçbir zarar veremezler. Allah, sana Kitab (Kur\'an)ı ve hikmeti indirmiş ve sana bilmediğin şeyleri öğretmiştir. Allah\'ın sana olan lütfu büyüktür.'),
(607, 4, 114, 'Bir sadaka vermeyi yahut iyilik yapmayı veyahut da insanlar arasını düzeltmeyi emreden(ler)inki hariç, onların aralarındaki gizli gizli konuşmalarının çoğunda hiçbir hayır yoktur. Kim bunları sırf Allah\'ın rızasını kazanmak için yaparsa, yakında ona büyük bir mükafat vereceğiz.'),
(608, 4, 115, 'Kim kendisine doğru yol besbelli olduktan sonra Peygamber\'e karşı çıkar, müminlerin yolundan başkasına uyup giderse onu döndüğü yolda bırakırız ve cehenneme sokarız. Orası ne kötü bir gidiş yeridir.'),
(609, 4, 116, 'Şüphesiz Allah kendisine ortak koşulmasını bağışlamaz. Bunun dışında dilediğini bağışlar. Allah\'a ortak koşan, muhakkak ki, derin bir sapıklığa düşmüştür.'),
(610, 4, 117, 'Onlar, Allah\'ı bırakırlar da, yalnız dişilere taparlar. Böylece ancak inatçı şeytana tapmış olurlar.'),
(611, 4, 118, 'Allah o şeytana lanet etti. Ve o da: "Elbette senin kullarından belirli bir pay alacağım, onları mutlaka saptıracağım, onları boş kuruntulara sokacağım, ve onlara emredeceğim de hayvanların kulaklarını yaracaklar, onlara emredeceğim de Allah\'ın yaratışını değiştirecekler" dedi. Kim Allah\'ı bırakıp da şeytanı dost edinirse, şüphesiz o, apaçık bir ziyana uğramış olur.'),
(612, 4, 119, 'Allah o şeytana lanet etti. Ve o da: "Elbette senin kullarından belirli bir pay alacağım, onları mutlaka saptıracağım, onları boş kuruntulara sokacağım, ve onlara emredeceğim de hayvanların kulaklarını yaracaklar, onlara emredeceğim de Allah\'ın yaratışını değiştirecekler" dedi. Kim Allah\'ı bırakıp da şeytanı dost edinirse, şüphesiz o, apaçık bir ziyana uğramış olur.'),
(613, 4, 120, 'Şeytan onlara vaad eder ve onları boş umutlarla oyalar. Oysa şeytanın onlara vaadi, aldatmadan başka bir şey değildir.'),
(614, 4, 121, 'Bunların varacakları yer cehennemdir. Ondan kurtulmak için çare bulamazlar.'),
(615, 4, 122, 'İman edip iyi işler yapanları da altlarından ırmaklar akan cennetlere sokacağız, orada ebedî olarak kalacaklardır. Bu, Allah\'ın gerçek vaadidir. Allah\'dan daha doğru sözlü kim olabilir?'),
(616, 4, 123, '(İş), ne sizin kuruntunuza, ne de kitap ehlinin kuruntusuna göredir. Kötülük yapan, o yüzden cezalandırılır. O, kendisine Allah\'tan başka ne bir dost, ne de bir yardımcı bulabilir.'),
(617, 4, 124, 'Erkek veya kadın, kim mümin olur da güzel amellerden işlerse, işte onlar cennete girerler. Zerre kadar da haksızlığa uğratılmazlar.'),
(618, 4, 125, 'İyilik yaparak kendisini Allah\'a teslim eden ve İbrahim\'in dinine dosdoğru olarak tâbi olan kimseden, din bakımından daha iyi kim olabilir? Allah, İbrahim\'i dost edinmişti.'),
(619, 4, 126, 'Göklerde ve yerde olanların hepsi Allah\'ındır. Allah, her şeyi kuşatıcıdır.'),
(620, 4, 127, 'Kadınlar hakkında senden fetva isterler. De ki: Onlar hakkındaki fetvayı size Allah veriyor: Yazılmış hakları olan mirası kendilerine vermediğiniz ve nikahlanmayı istemediğiniz öksüz kızlar ve zavallı çocuklara ve bir de yetimlere adaletle davranmanız hakkında Kitap\'ta size okunan âyetler vardır. Sizin her yaptığınız iyiliği, muhakkak Allah bilir.'),
(621, 4, 128, 'Eğer bir kadın kocasının geçimsizliğinden, yahut kendisinden yüz çevirmesinden endişe ederse, aralarında bir sulh yapmalarında, onlara bir günah yoktur. Sulh hep hayırlıdır. Zaten nefisler kıskançlığa hazırdır. Eğer iyi geçinir ve geçimsizlikten sakınırsanız, şüphesiz Allah yaptıklarınızdan haberdardır.'),
(622, 4, 129, 'Kadınlarınız arasında her yönden adaletli davranmaya ne kadar uğraşsanız buna güç yetiremezsiniz. Bari birisine tamamen kapılıp da diğerini askıya alınmış gibi bırakmayın. Eğer arayı düzeltir ve haksızlıktan korunursanız, şüphesiz Allah çok bağışlayıcı ve esirgeyicidir.'),
(623, 4, 130, 'Eğer karıkoca birbirlerinden ayrılacak olurlarsa, Allah, onların her birini geniş lutfuyla muhtaç bırakmaz. Allah\'ın lutfu geniştir, hikmeti büyüktür.'),
(624, 4, 131, 'Göklerde ve yerde ne varsa hepsi Allah\'ındır. Sizden önce kendilerine kitap verilenlere ve size Allah\'tan korkmanızı emrettik. Eğer inkâr ederseniz, biliniz ki, göklerde ve yerde ne varsa hepsi Allah\'ındır. Allah hiçbir şeye muhtaç değildir, hamd vesenâ O\'na yakışır.'),
(625, 4, 132, 'Göklerde ve yerde ne varsa hepsi Allah\'ındır. Vekil olarak Allah yeter.'),
(626, 4, 133, 'Ey insanlar! Eğer Allah dilerse sizi giderir de başkalarını getirir. Ve Allah, buna kadirdir.'),
(627, 4, 134, 'Kim dünya nimetini isterse, bilsin ki dünya ve ahiret nimeti Allah katındadır. Allah her şeyi çok iyi işiten ve çok iyi görendir.'),
(628, 4, 135, 'Ey iman edenler! Adaleti ayakta tutan ve kendiniz, anababanız ve yakın akrabanız aleyhine de olsa, yalnız Allah için şahitlik eden kimseler olunuz. Zira zengin de olsa, fakir de olsa, Allah ikisine de (sizden) daha yakındır. Nefsinizin arzusuna uyarak adaletten uzaklaşmayın. Eğer (şahitlik ederken) dilinizi eğer, bükerseniz veya çekinirseniz, şüphesiz Allah yaptıklarınızdan haberdardır.'),
(629, 4, 136, 'Ey iman edenler! Allah\'a, Peygamberine, Peygamberine indirdiği Kitab\'a, ve daha önce indirdiği kitaba iman edin. Kim Allah\'ı, meleklerini, kitaplarını, peygamberlerini ve ahiret gününü inkâr ederse sapıklığın en koyusuna düşmüş olur.'),
(630, 4, 137, 'İman edip sonra inkâr eden, sonra iman edip tekrar inkâr eden, sonra da inkârlarında ileri gidenleri Allah ne bağışlayacak, ne de doğru yola eriştirecektir.'),
(631, 4, 138, 'Münafıklara da haber ver ki, kendileri için çok acı bir azab vardır.'),
(632, 4, 139, 'Onlar, müminleri bırakıp kâfirleri dost ediniyorlar. Onların yanında izzet ve şeref mi arıyorlar? Halbuki bütün izzet ve şeref Allah\'a aittir.'),
(633, 4, 140, 'Allah size Kitab (Kur\'an)da: "Allah\'ın âyetlerinin inkâr edildiğini ve onlarla alay edildiğini işittiğiniz zaman, başka bir söze geçmedikleri müddetçe, o kâfirlerle oturmayın. Aksi halde siz de onlar gibi olursunuz" diye hüküm indirdi. Muhakkak ki Allah, münafıkların ve kâfirlerin hepsini cehennemde toplayacaktır.'),
(634, 4, 141, 'Onlar sizi gözetleyip dururlar. Eğer Allah tarafından size bir zafer nasip olursa: "Biz sizinle beraber değil miydik?" derler. Şayet kâfirlerin zaferden bir payı olursa: (Bu defa da onlara): "Size üstünlük sağlayarak sizi müminlerden korumadık mı?" derler. Allah, kıyamet gününde aranızda hükmünü verecektir. Allah, müminlerin aleyhine kâfirlere hiçbir yol vermeyecektir.'),
(635, 4, 142, 'Münafıklar, Allah\'ı aldatmaya çalışırlar. Halbuki Allah, onların oyunlarını başlarına geçirecektir. Onlar, namaza kalktıkları zaman tembel tembel kalkarlar. İnsanlara gösteriş yaparlar. Allah\'ı pek az anarlar.'),
(636, 4, 143, 'Münafıklar, küfür ile iman arasında bocalamaktadırlar. Ne bu müminlere bağlanırlar, ne de şu kâfirlere. Allah kimi doğru yoldan saptırırsa, sen artık ona kurtuluş yolu bulamazsın.'),
(637, 4, 144, 'Ey iman edenler! Müminleri bırakıp da kâfirleri dost edinmeyin. Kendi aleyhinizde Allah\'a apaçık bir delil mi vermek istiyorsunuz?'),
(638, 4, 145, 'Şüphesiz ki münafıklar, cehennem ateşinin en aşağı tabakasındadırlar. Onlara bir yardım edici de bulamazsın.'),
(639, 4, 146, 'Ancak tevbe edenler, durumlarını düzeltenler, Allah\'a sarılanlar ve Allah için dinlerine samimi olarak bağlananlar müstesna. İşte bunlar müminlerle beraberdirler. Allah, müminlere büyük bir mükafat verecektir.'),
(640, 4, 147, 'Eğer şükreder ve iman ederseniz Allah size azabı ne yapar? Allah, şükredenlerin mükafatını veren ve her şeyi bilendir.'),
(641, 4, 148, 'Allah, zulme uğrayanların dışında, çirkin sözün açıkça söylenmesinden hoşlanmaz. Allah her şeyi hakkıyla işiten, hakkıyla bilendir.'),
(642, 4, 149, 'Bir hayrı açıklar yahut gizlerseniz, yahut da bir kötülüğü bağışlarsanız, biliniz ki, Allah da çok bağışlayıcıdır, her şeye hakkıyla kadirdir.'),
(643, 4, 150, 'Onlar, Allah\'ı ve peygamberlerini inkâr ederler, Allah ile peygamberlerinin arasını ayırmak isterler. "Kimine inanırız, kimini inkâr ederiz" derler. Bu ikisinin (imanla küfrün) arasında bir yol tutmak isterler.'),
(644, 4, 151, 'İşte onlar gerçek kâfirlerdir. Biz de kâfirlere alçaltıcı bir azab hazırlamışızdır.'),
(645, 4, 152, 'Allah\'a ve peygamberlerine iman edenler ve onlar arasında ayırım yapmayanlara (Allah) pek yakında mükafatlarını verecektir. Allah çok bağışlayıcıdır, çok merhametlidir.'),
(646, 4, 153, 'Kitap ehli, senden, kendilerine gökten bir kitap indirmeni istiyorlar. Musa\'dan bundan daha büyüğünü istemişler ve: "Allah\'ı bize açıkça göster" demişlerdi. Haksızlıkları sebebiyle onları yıldırım çarptı. Sonra kendilerine açık deliller geldiği halde buzağıyı (tanrı) edinmişlerdi. Onları bundan dolayı da affettik. Ve Musa\'ya açık bir delil (yetki) verdik.'),
(647, 4, 154, 'Söz vermeleri için Tur dağını üzerlerine kaldırdık. Onlara: "O kapıdan secde ederek girin" dedik. Yine onlara: "Cumartesi yasağını çiğnemeyin" dedik ve onlardan sağlam bir söz aldık.'),
(648, 4, 155, 'Verdikleri sözden dönmeleri, Allah\'ın âyetlerini inkâr etmeleri, haksız yere peygamberlerini öldürmeleri ve "kalblerimiz kılıflıdır" demelerinden dolayı (başlarına türlü belalar verdik). Doğrusu Allah, inkârları sebebiyle onların kalplerini mühürlemiştir. Pek azı hariç onlar inanmazlar.'),
(649, 4, 156, '(Kalblerinin mühürlenmesinin diğer bir sebebi de İsa\'yı) inkâr etmeleri ve Meryem\'e büyük bir iftirada bulunmalarıdır.'),
(650, 4, 157, 'Bir de "Biz Allah\'ın peygamberi Meryem oğlu İsa Mesih\'i öldürdük" demeleridir. Oysa onu ne öldürdüler, ne de astılar. Fakat öldürdükleri kimse, onlara İsa gibi gösterildi. Onun hakkında anlaşmazlığa düşenler, ondan yana tam bir kuşku içindedirler. O hususta bir bilgileri yoktur. Sadece zanna uyuyorlar. Onu kesinlikle öldürmediler.'),
(651, 4, 158, 'Fakat Allah onu kendisine yükseltmiştir. Allah, aziz (daima üstün)dir, hikmet sahibidir.'),
(652, 4, 159, 'Kitap ehlinden hiçbir kimse yoktur ki, ölmeden önce ona (İsa\'ya) iman etmiş olmasın. Kıyamet gününde o, onlara şahitlik edecektir.'),
(653, 4, 160, 'Yahudilerin zulmetmeleri ve birçok kimseleri Allah yolundan alıkoymaları, yasaklandıkları halde faiz almaları ve insanların mallarını haksız yere yemeleri sebebiyle daha önce kendilerine helâl kılınan temiz şeyleri haram kıldık. Onlardan kâfir olanlara can yakıcı bir azap hazırladık.'),
(654, 4, 161, 'Yahudilerin zulmetmeleri ve birçok kimseleri Allah yolundan alıkoymaları, yasaklandıkları halde faiz almaları ve insanların mallarını haksız yere yemeleri sebebiyle daha önce kendilerine helâl kılınan temiz şeyleri haram kıldık. Onlardan kâfir olanlara can yakıcı bir azap hazırladık.'),
(655, 4, 162, 'Fakat onlardan ilimde derinleşmiş olanlar ve iman edenler, sana indirilene ve senden önce indirilenlere iman ederler. Onlar, namazı kılan, zekatı veren, Allah\'a ve ahiret gününe iman edenlerdir. İşte onlara büyük bir mükafat vereceğiz.'),
(656, 4, 163, 'Muhakkak biz, Nuh\'a ve ondan sonra gelen peygamberlere vahyettiğimiz gibi, sana da vahyettik. İbrahim\'e, İsmail\'e, İshak\'a, Yakub\'a, torunlarına, İsa\'ya, Eyyûb\'a, Yunus\'a, Harun\'a ve Süleyman\'a da vahyettik. Davud\'a da Zebur\'u verdik.'),
(657, 4, 164, 'Daha önce sana anlattığımız peygamberlerle, anlatmadığımız başka peygamberlere de (vahyettik). Ve Allah Musa ile de konuştu.'),
(658, 4, 165, 'Peygamberleri müjdeciler ve azab habercileri olarak gönderdik ki, peygamberlerden sonra insanların Allah\'a karşı bir bahaneleri olmasın. Allah mutlak üstündür, yegane hikmet sahibidir.'),
(659, 4, 166, 'Fakat Allah, sana indirdiğini kendi ilmiyle indirmiş olduğuna şahitlik eder. Melekler de buna şahitlik ederler. Allah\'ın şahitliği de kafidir.'),
(660, 4, 167, 'Şüphesiz inkâr edip, insanları Allah yolundan alıkoyanlar, derin bir sapıklığa düşmüşlerdir.'),
(661, 4, 168, 'Muhakkak Allah, inkâr edenleri ve zulmedenleri ne bağışlar, ne de doğru bir yola eriştirir.'),
(662, 4, 169, 'Onları ancak cehennemin yoluna (iletecek ve) onlar orada ebedî olarak kalacaklardır. Bu ise Allah\'a çok kolaydır.'),
(663, 4, 170, 'Ey insanlar, Resul size, Rabbi\'nizden hakkı (gerçeği) getirdi. Kendi yararınıza olarak ona inanın. Eğer inkâr ederseniz, bilin ki göklerde ve yerde olanların hepsi Allah\'ındır. Allah bilendir, hikmet sahibidir.'),
(664, 4, 171, 'Ey kitab ehli! Dininizde taşkınlık etmeyin ve Allah hakkında ancak doğru olanı söyleyin! Meryem oğlu İsa Mesih, sadece Allah\'ın elçisi, Meryem\'e atmış olduğu kelimesi ve O\'ndan bir ruhtur. Allah\'a ve peygamberlerine inanın (Allah) üçtür demeyin. Kendi yararınız için buna son verin. Muhakkak ki Allah tek bir ilâhtır. O, çocuk sahibi olmaktan yüce (münezzeh)dir. Göklerdeki ve yerdekilerin hepsi O\'nundur. Vekil olarak Allah yeter.'),
(665, 4, 172, 'Hiçbir zaman Mesih de Allah\'ın bir kulu olmaktan çekinmez, Allah\'a yakın melekler de. Kim O\'na kulluk etmekten çekinir ve büyüklük taslarsa bilsin ki O, onların hepsini huzuruna toplayacaktır.'),
(666, 4, 173, 'İnanıp güzel işler yapanlara gelince, onların mükafatlarını eksiksiz ödeyecek ve lütfundan onlara daha fazlasını da verecektir. Allah\'a kulluktan çekinip büyüklük taslayanlara da şiddetli bir şekilde azab edecek ve onlar Allah\'dan başka kendilerine ne bir dost, ne de bir yardımcı bulamayacaklardır.'),
(667, 4, 174, 'Ey insanlar! Size Rabbinizden bir delil (Muhammed) geldi ve size apaçık bir nur indirdik.'),
(668, 4, 175, 'Allah\'a inanıp O\'na sımsıkı sarılanları (Allah), kendisinden bir rahmet ve lutfa sokacak ve kendisine varan dosdoğru yola iletecektir.'),
(669, 4, 176, 'Senden fetva istiyorlar. Deki: "Allah size kelâle (babasız ve çocuksuz kimse) nin mirası hakkında hükmünü açıklıyor: Çocuğu olmayan, fakat kız kardeşi bulunan bir kişi ölürse, bıraktığı malın yarısı o (kız kardeşi)nundur. Çocuğu olmayan kız kardeş ölürse, erkek kardeş ona varis olur. Eğer (ölenin) iki kız kardeşi varsa, bıraktığının üçte ikisi onlarındır. Eğer kardeşler erkek ve kız olurlarsa, erkeğin hissesi, iki kızın hissesi kadardır. Şaşırmamanız için Allah size (hükümlerini) açıklıyor. Allah, her şeyi hakkıyla bilendir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'Ey iman edenler! Sözleşmeleri yerine getirin. İhramlı iken avlanmayı helal saymamanız şartıyla, çeşitli hayvanlar size helal kılındı. Ancak haram oldukları size okunacak olanlar müstesna. Şüphesiz Allah dilediği hükmü verir.'),
(671, 5, 2, 'Ey iman edenler! Allah\'ın alâmetlerine, haram aya, kurbanlık hediyelere, gerdanlıklarına ve Rablerinden lutuf ve rıza bekleyerek Kabe\'ye yönelenlere sakın saygısızlık etmeyin. İhramdan çıktığınız zaman avlanabilirsiniz. Sizi Mescidi Haram\'dan çevirdiklerinden dolayı bir topluma karşı olan kininiz, sizi saldırıya sevk etmesin. İyilik ve takva üzerinde yardımlaşın, günah ve düşmanlık üzerinde yardımlaşmayın. Allah\'tan korkun. Çünkü Allah\'ın azabı çetindir.'),
(672, 5, 3, 'Leş, kan, domuz eti, Allah\'tan başkasının adı anılarak kesilen; boğulmuş, vurulmuş, yukardan düşmüş, boynuzlanmış, canavar yırtmış olup da canlı iken kesmedikleriniz; dikili taşlar (putlar) üzerine boğazlanan hayvanlar ve fal oklarıyla kısmet (şans) aramanız size haram kılındı. Bunların hepsi doğru yoldan çıkmaktır. Bugün kâfirler, dininize karşı ümitsizliğe düşmüşlerdir. Onlardan korkmayın, benden korkun. Bugün dininizi kemale erdirdim, size nimetimi tamamladım. Size din olarak İslâmı beğendim. Kim açlıktan daralır, günaha istekle yönelmeden bunlardan yemek zorunda kalırsa, ona günah yoktur. Çünkü Allah bağışlayan, merhamet edendir.'),
(673, 5, 4, 'Sana, kendilerine neyin helal kılındığını soruyorlar. De ki: "Size iyi ve temiz şeyler helal kılındı." Allah\'ın size öğrettiğinden öğreterek yetiştirdiğiniz avcı hayvanların sizin için tuttuklarını yiyin ve üzerine Allah\'ın adını anın (besmele çekin), Allah\'tan korkun. Muhakkak Allah, hesabı çabuk görendir.'),
(674, 5, 5, 'Bugün size iyi ve temiz şeyler helal kılındı. Kendilerine kitap verilenlerin yiyecekleri size helal olduğu gibi, sizin yiyeceğiniz de onlara helâldir. Ve müminlerden iffetli hür kadınlar ve sizden önce kendilerine kitap verilenlerden namuslu hür kadınlar, zina etmeksizin, gizli dost tutmaksızın, namuslu bir şekilde mehirlerini ödediğiniz takdirde, size helâldir. Her kim imanı inkâr ederse, ameli boşa gitmiş olur ve o, ahirette zarara uğrayanlardandır.'),
(675, 5, 6, 'Ey iman edenler! Namaz kılmaya kalktığınız zaman, yüzlerinizi ve dirseklere kadar ellerinizi yıkayın. Başlarınızı meshedin, iki topuğa kadar da ayaklarınızı yıkayın. Eğer cünüp iseniz temizlenin. Hasta iseniz, yahut yolculukta iseniz, yahut biriniz abdest bozmaktan gelmişse yahut kadınlara dokunmuşsanız, su da bulamamışsanız, temiz bir toprağa teyemmüm edin. Bunun için de yüzlerinizi ve ellerinizi o toprakla meshedin. Allah size bir güçlük çıkarmak istemiyor, fakat sizi temizlemek ve şükredesiniz diye de üzerinizdeki nimetini tamamlamak istiyor.'),
(676, 5, 7, 'Allah\'ın, üzerinizdeki nimetini ve "İşittik, itaat ettik" dediğinizde sizden aldığı ve kendisiyle sizi bağladığı ahdini hatırlayın. Allah\'tan korkun, çünkü Allah göğüslerin özünü çok iyi bilir.'),
(677, 5, 8, 'Ey iman edenler, Allah için hakkı ayakta tutanlar ve adaletle şahitlik yapanlar olunuz. Bir kavme olan kininiz, sizi adaletsizliğe sevketmesin. Adaletli olun, çünkü o, takvaya daha yakındır. Allah\'tan korkun. Şüphesiz Allah, yaptıklarınızdan haberdardır.'),
(678, 5, 9, 'Allah, iman edenlere ve salih amel işleyenlere şöyle vaad etmiştir: Onlar için mağfiret ve büyük bir mükafat vardır.'),
(679, 5, 10, 'İnkâr eden ve âyetlerimizi yalanlayanlara gelince, işte onlar, cehennemliktirler.'),
(680, 5, 11, 'Ey iman edenler! Allah\'ın size olan nimetini hatırlayın. Hani bir topluluk size el uzatmaya (tecavüze) yeltenmişti de, O (Allah) onların ellerini sizden çekmişti. Allah\'tan korkun. Müminler yalnız Allah\'a dayansınlar.'),
(681, 5, 12, 'Allah, İsrailoğularından söz almıştı. İçlerinden on iki müfettiş göndermiştik... Allah şöyle demişti: " Ben, muhakkak sizinle beraberim. Namazı dosdoğru kıldığınız, zekatı verdiğiniz, peygamberlerime iman ettiğiniz ve onlara yardımda bulunduğunuz, (mallarınızı) Allah yolunda güzelce sarfettiğiniz takdirde, günahlarınızı mutlaka örter ve sizi altından ırmaklar akan cennetlere korum. Fakat sizden her kim de, bundan sonra küfrederse, dosdoğru yoldan sapmış olur.'),
(682, 5, 13, 'Sözlerini bozdukları için onları lanetledik ve kalblerini katılaştırdık. Kelimeleri yerlerinden değiştiriyorlar. Uyarıldıkları şeyden pay almayı unuttular. İçlerinden pek azı hariç, daima onlardan hainlik görürsün. Yine de onları affet, aldırma. Çünkü Allah güzel davrananları sever.'),
(683, 5, 14, '"Biz hıristiyanız" diyenlerden de söz almıştık. Onlar da kendilerine hatırlatılan şeylerin çoğunu unutmuşlardı. Biz de onların arasına, kıyamete kadar sürecek kin ve düşmanlık soktuk. Allah, ne yapmış olduklarını onlaraelbette haber verecektir.'),
(684, 5, 15, 'Ey kitap ehli! Kitaptan gizlemiş olduğunuz şeylerin çoğunu açıklayan, çoğundan da vazgeçen peygamberimiz size geldi. Ayrıca size, Allah\'tan bir nur ve apacık bir kitap da gelmiştir.'),
(685, 5, 16, 'Allah o kitabla rızasına uygun hareket edenleri selamet yollarına iletir. Onları izniyle karanlıklardan aydınlığa çıkarır ve onları dosdoğru yola sevk eder.'),
(686, 5, 17, 'Muhakkak ki, "Allah, ancak Meryemoğlu İsa Mesih\'tir" diyenler kâfir olmuşlardır. (Onlara) de ki: " Allah, Meryemoğlu İsa Mesih\'i, anasını ve bütün yeryüzündekileri helak etmek istese O\'na kim engel olabilir? " Göklerin, yerin ve ikisi arasındakilerin mülkiyeti sadece Allah\'a aittir. O, dilediğini yaratır. Allah, her şeye kadirdir.'),
(687, 5, 18, 'Yahudiler ve hıristiyanlar, "Biz Allah\'ın oğulları ve sevgilileriyiz" dediler. De ki: " O halde niçin günahlarınızdan ötürü (Allah ) size azab ediyor?" Hayır, siz de O\'nun yaratıklarından birer insansınız. O dilediğini bağışlar, dilediğine azab eder. Göklerin, yerin ve ikisi arasında bulunan her şeyin mülkü Allah\'ındır. Nihayet dönüş de O\'nadır.'),
(688, 5, 19, 'Ey kitap ehli! Peygamberlerin arasının kesildiği bir sırada size Resulümüz geldi, gerçekleri açıklıyor ki, (yarın kıyamet gününde): "Bize bir müjdeleyici ve uyarıcı gelmedi" demeyiniz. İşte müjdeleyici ve uyarıcı geldi. Allah, her şeye kadirdir.'),
(689, 5, 20, 'Musa kavmine şöyle demişti: "Ey kavmim! Allah\'ın üzerinizdeki nimetini hatırlayın. O, içinizden peygamberler çıkardı. Sizi hükümdarlar yaptı. Ve âlemlerde hiçbir kimseye vermediğini size verdi."'),
(690, 5, 21, '"Ey kavmim, Allah\'ın size yazdığı kutsal toprağa girin, geriye dönmeyin, yoksa kayba uğrarsınız."'),
(691, 5, 22, 'Onlar da: "Ey Musa! Orada zorba bir kavim var. Onlar oradan çıkmadıkça biz oraya asla giremeyiz. Eğer oradan çıkarlarsa, şüphesiz biz de gireriz" dediler.'),
(692, 5, 23, 'Allah\'tan korkan ve Allah\'ın kendilerine nimet verdiği iki adam şöyle dedi: "Onların üzerlerine kapıdan girin. Oradan girerseniz muhakkak galip gelirsiniz. Eğer layıkıyla inanıyorsanız yalnız Allah\'a dayanın.'),
(693, 5, 24, 'Kavmi Musa\'ya: "Ey Musa! Onlar orada olduğu sürece biz oraya asla girmeyiz. Sen ve Rabb\'in gidin savaşın. Biz burada oturacağız" dediler.'),
(694, 5, 25, 'Musa: "Ey Rabbim! Ben, kendimle kardeşimden başkasına söz geçiremiyorum, artık bizimle bu fâsık kavmin arasını ayır" dedi.'),
(695, 5, 26, 'Allah Musa\'ya şöyle dedi: "Kırk sene o mukaddes yer onlara haram kılınmıştır. Yeryüzünde şaşkın şaşkın dolaşacaklar. O fâsık kavim için üzülme!".'),
(696, 5, 27, 'Onlara Âdem\'in iki oğluyla ilgili haberi hakkıyle oku. Hani her ikisi birer kurban sunmuşlardı, birinden kabul edilmiş, diğerinden kabul edilmemişti. (Kurbanı kabul edilmeyen, ötekine): " Seni öldüreceğim" demişti. Diğeri ise şöyle demişti: "Allah, yalnız kendisinden korkanlardan kabul eder".'),
(697, 5, 28, '"Allah\'a yemin ederim ki, sen beni öldürmek için bana el uzatsan da, ben seni öldürmek için sana el uzatacak değilim, ben âlemlerin Rabb\'i olan Allah\'tan korkarım.'),
(698, 5, 29, '"Ben isterim ki sen, benim günahımı da, kendi günahını da yüklenip ateş halkından olasın! Zalimlerin cezası budur".'),
(699, 5, 30, 'Bunun üzerine kurbanı kabul edilmeyenin nefsi kendisini, kardeşini öldürmeye teşvik etti ve onu öldürdü. Böylece zarara uğrayanlardan oldu.'),
(700, 5, 31, 'Derken Allah bir karga gönderdi, ona kardeşinin cesedini nasıl gömeceğini göstermek için toprağı eşeliyordu. "Yazıklar olsun bana, şu karga kadar olup da kardeşimin cesedini gömmekten âciz miyim ben?" dedi ve pişman olanlardan oldu.'),
(701, 5, 32, 'Bunun içindir ki, İsrâiloğulları\'na: "Kim, bir cana kıymayan veya yeryüzünde bozgunculuk çıkarmayan bir nefsi öldürürse, bütün insanları öldürmüş gibi olur. Kim de bir nefsin yaşamasına sebep olursa, bütün insanları yaşatmış gibi olur" hükmünü yazdık (farz kıldık). Şüphesiz ki onlara peygamberlerimiz açık delillerle geldiler. Yine de bundan sonra onların birçoğu yeryüzünde aşırı gitmektedirler.'),
(702, 5, 33, 'Allah ve Resulüne karşı savaşan ve yeryüzünde fesat çıkarmaya çalışanların cezası, ancak öldürülmeleri veya asılmaları yahut ayak ve ellerinin çaprazlama kesilmesi, ya da yeryüzünde başka bir yere sürgün edilmeleridir. Bu, dünyada onlar için bir zillettir. Ahirette ise onlar için büyük bir azab vardır.'),
(703, 5, 34, 'Ancak kendilerini yakalamanızdan önce tevbe edenler başka. Bilin ki Allah, çok bağışlayan ve çok merhamet edendir.'),
(704, 5, 35, 'Ey inananlar, Allah\'tan korkun, O\'na yaklaşmaya yol arayın ve O\'nun yolunda cihad edin ki, kurtuluşa eresiniz.'),
(705, 5, 36, 'Bütün yeryüzündekiler ve bir o kadarı daha inkâr edenlerin olsa, bunlar kıyamet gününün azabından kurtulmak için hepsini fidye olarak verseler yine onlardan kabul edilmez. Onlar için can yakıcı bir azap vardır.'),
(706, 5, 37, 'Cehennem ateşinden çıkmak isterler. Ama oradan çıkacak değillerdir. Onlar için devamlı bir azap vardır.'),
(707, 5, 38, 'Hırsızlık eden erkek ve kadının, yaptıklarına karşılık Allah\'dan bir ceza olarak ellerini kesin. Allah daima üstündür, hikmet sahibidir.'),
(708, 5, 39, 'Kim yaptığı haksızlıktan sonra tevbe eder, halini düzeltirse, şüphesiz Allah, onun tevbesini kabul eder. Çünkü Allah bağışlayan, merhamet edendir.'),
(709, 5, 40, 'Göklerin ve yerin mülkünün Allah\'a ait olduğunu, dilediğine azap edip dilediğini de bağışladığını bilmedin mi? Allah herşeye kâdirdir.'),
(710, 5, 41, 'Ey peygamber, ağızlarıyla "inandık" deyip, kalbleriyle inanmamış olanlardan ve yahudilerden küfürde yarış edenler seni üzmesin. Onlar yalana kulak verirler, sana gelmeyen diğer bir topluluğa kulak verirler, kelimeleri yerlerinden değiştirirler, "eğer size bu verilirse alın, bu verilmezse sakının" derler. Allah birini şaşırtmak isterse, sen onun için Allah\'a karşı hiçbir şey yapamazsın. Onlar öyle kimselerdir ki, Allah, onların kalblerini temizlemek istememiştir. Onlar için dünyada rezillik var ve yine onlar için ahirette de büyük bir azab vardır.'),
(711, 5, 42, 'Onlar, yalana çok kulak verirler ve çok haram yerler. Eğer sana gelirlerse, ister aralarında hükmet, ister onlardan yüz çevir. Eğer onlardan yüz çevirirsen, sana hiçbir zarar veremezler. Eğer aralarında hükmedersen adaletle hükmet. Şüphesiz Allah, adaletli davrananları sever.'),
(712, 5, 43, 'İçinde Allah\'ın hükmü bulunan Tevrat yanlarında dururken seni nasıl hakem yapıyorlar da ondan sonra da dönüveriyorlar? Onlar inanıcı değillerdir.'),
(713, 5, 44, 'İçinde hidayet ve nûr bulunan Tevrat\'ı, elbette biz indirdik. Müslüman olan peygamberler, yahudiler hakkında hükmederler, kendilerini Tanrıya adamış zâhitler, âlimler de, Allah\'ın kitabını korumakla görevlendirildiklerinden (onunla hüküm verirler) ve onun Allah\'ın kitabı olduğuna şahitlik ederlerdi. İnsanlardan korkmayın, benden korkun, âyetlerimi az bir paraya satmayın. Kim Allah\'ın indirdiğiyle hükmetmezse, işte onlar kâfirlerin ta kendileridir.'),
(714, 5, 45, 'Biz Tevrat\'ta onlara, cana can, göze göz, buruna burun, kulağa kulak, dişe diş ve yaralara karşılıklı kısas (ödeşme) yazdık. Bununla beraber kim kısas hakkını bağışlarsa, bu kendi günahlarına keffaret olur. Ve kim Allah\'ın indirdiğiyle hükmetmezse, işte onlar zalimlerin ta kendileridir.'),
(715, 5, 46, 'O peygamberlerin ardından, yanlarındaki Tevrat\'ı doğrulayıcı olarak Meryemoğlu İsa\'yı gönderdik ve ona içinde hidayet ve nur olan, kendinden önceki Tevrat\'ı tasdik eden ve Allah\'dan korkanlar için bir hidayet rehberi ve bir öğüt olan İncil\'i verdik.'),
(716, 5, 47, 'İncil ehli de Allah\'ın ona indirdikleriyle hükmetsinler. Kim, Allah\'ın indirdiği ile hükmetmezse, işte onlar fâsıkların ta kendileridir.'),
(717, 5, 48, 'Sana da (ey Muhammed) geçmiş kitapları tasdik eden ve onları kollayıp koruyan Kitab (Kur\'ân)ı hak ile indirdik. Onların aralarında Allah\'ın indirdiği ile hükmet. Onların arzu ve heveslerine uyarak, sana gelen haktan sapma. Biz, herbiriniz için bir şeriat ve yol belirledik. Eğer Allah dileseydi sizi tek bir ümmet yapardı, fakat size verdiklerinde sizi denemek istedi. Öyleyse iyiliklere koşun. Hepinizin dönüşü Allah\'adır. O, ihtilafa düştüğünüz şeyleri size haber verir.'),
(718, 5, 49, 'Aralarında Allah\'ın indirdiğiyle hükmet. Onların keyiflerine uyma. Allah\'ın sana indirdiğinin bir kısmından seni saptırmalarından sakın. Eğer Allah\'ın hükmünden yüzçevirirlerse, bil ki Allah, bir kısım günahları sebebiyle onları musibete uğratmak istiyor. Muhakkak ki insanların çoğu yoldan çıkanlardır.'),
(719, 5, 50, 'Yoksa cahiliyye hükmünü mü arıyorlar? kesinlikle bilen bir toplum için Allah\'tan daha güzel hüküm veren kim olabilir?'),
(720, 5, 51, 'Ey iman edenler! Yahudileri ve hıristiyanları dost edinmeyin. Onlar birbirlerinin dostudurlar. Sizden kim onları dost edinirse, şüphesiz o onlardan olur. Şüphesiz Allah, zalim kavmi doğru yola iletmez.'),
(721, 5, 52, 'Kalblerinde hastalık bulunanların: " Bize bir felaket gelmesinden korkuyoruz" diyerek, onların arasına koşuştuklarını görürsün. Umulur ki Allah, bir fetih ihsan eder veya katından bir emir (iş) getirir de içlerinde gizlediklerine pişman olurlar.'),
(722, 5, 53, 'İman edenler: "Sizinle beraber olduklarına dair, Allah\'a bütün güçleriyle yemin edenler bunlar mı?" derler. Onların bütün amelleri boşa gitmiştir ve kaybedenlerden olmuşlardır.'),
(723, 5, 54, 'Ey iman edenler! Sizden kim dininden dönerse, bilsin ki Allah yakında öyle bir toplum getirir ki, Allah onları sever, onlar da Allah\'ı severler; müminlere karşı yumuşak, kâfirlere karşı da onurlu ve şiddetlidirler; Allah yolunda mücahede eder, hiçbirkınayıcının kınamasından da korkmazlar. Bu, Allah\'ın bir lütfudur, onu dilediğine verir. Allah, geniş ihsan sahibidir, her şeyi çok iyi bilendir.'),
(724, 5, 55, 'Sizin asıl dostunuz Allah\'tır, O\'nun Resulüdür ve namazlarını kılan zekatlarını veren ve rükû eden müminlerdir.'),
(725, 5, 56, 'Kim Allah\'ı, O\'nun Resulünü ve müminleri dost edinirse, (iyi bilsin ki) Allah\'ın taraftarları galip geleceklerdir.'),
(726, 5, 57, 'Ey iman edenler! Sizden önce kendilerine kitap verilmiş olanlardan ve kâfirlerden, dininizi alay ve eğlence konusu yapanları dost edinmeyin. Eğer (gerçekten) iman ediyorsanız, Allah\'dan gereğince korkun.'),
(727, 5, 58, 'Namaza çağırdığınız zaman, onu alay ve eğlence konusu yaparlar. Bu onların, akıllarını kullanmayan bir toplum olmalarından dolayıdır.'),
(728, 5, 59, 'De ki: "Ey kitap ehli! Sadece Allah\'a, bize indirilene ve bizden önce indirilene inandığımız için mi bizden hoşlanmıyorsunuz? Oysa çoğunuz yoldan çıkmışlarsınız".'),
(729, 5, 60, 'De ki: "Allah katında cezaya çarptırılma bakımından bunlardan daha kötüsünü size haber vereyim mi? Allah, kimlere lanet etmiş ve gazabına uğratmışsa; kimlerden maymunlar, domuzlar ve şeytana tapanlar yapmışsa, işte bunların makamı daha kötüdür ve onlar düz yoldan daha çok sapmışlardır".'),
(730, 5, 61, 'Onlar, size geldikleri zaman, "iman ettik" dediler. Oysa yanınıza kâfir olarak girip, kâfir olarak çıkmışlardır. Allah, onların gizlediklerini çok iyi bilir.'),
(731, 5, 62, 'Onlardan çoğunu, günah işlemede, düşmanlıkta ve haram yemede yarış ederken görürsün. Bu yaptıkları şeyler ne kötüdür!'),
(732, 5, 63, 'Gerçek dindarların ve din bilginlerinin, onları günah olan bir söz söylemekten ve haram yemekten men etmeleri gerekmez miydi? Yaptıkları şey ne kötüdür!'),
(733, 5, 64, 'Yahudiler, "Allah\'ın eli çok sıkıdır" dediler. Söyledikleri söz sebebiyle onların elleri bağlansın ve lanete uğrasınlar! Aksine Allah\'ın elleri açıktır, dilediği gibi verir. Andolsun, Rabbinden sana indirilen, onların çoğunun azgınlığını ve küfrünü azdırıyor. Biz, onların aralarına tâ kıyamete kadar düşmanlık ve kin atmışızdır. Ne zaman savaş için bir ateş yakmışlarsa, Allah onu söndürmüştür. Onlar yeryüzünde bozğunculuğa koşarlar. Şüphesiz Allah bozguncuları sevmez.'),
(734, 5, 65, 'Eğer kitap ehli iman etmiş ve layıkıyla korunmuş olsalardı, onların kötülüklerini örter, nimeti bol olan cennetlere koyardık.'),
(735, 5, 66, 'Eğer onlar, Tevrat\'ı, İncil\'i ve kendilerine indirileni gereğince uygulasalardı, hem üstlerindeki, hem de ayaklarının altındaki (nimetlerden bol bol) yerlerdi. Onların arasında ılımlı bir grup da vardı. Böyle olmakla beraber onların çoğunun yaptıkları ne kadar kötüdür!'),
(736, 5, 67, 'Ey şanlı Resul! Rabbinden sana indirileni tebliğ et! Eğer bunu yapmazsan O\'nun peygamberlik görevini yapmamış olursun. Allah seni insanlardan korur. Doğrusu Allah, kâfirler toplumunu doğru yola iletmez.'),
(737, 5, 68, 'De ki: "Ey kitap ehli! Tevrat\'ı, İncil\'i ve Rabbinizden size indirileni uygulamadıkça bir esas üzerinde değilsiniz. Şüphesiz ki, Rabbinden sana indirilenler, onların çoğunun azgınlığını ve inkârını artıracaktır. Şu halde kâfir olan bir toplum için üzülme!'),
(738, 5, 69, 'Muhakkak ki inananlar, yahudiler, sabiiler ve hıristiyanlardan kim Allah\'a ve ahiret gününe iman eder ve güzel amel işlerse, onlar için bir korku yoktur, onlar mahzun da olmayacaklardır.'),
(739, 5, 70, 'Andolsun biz, İsrailoğulları\'ndan söz aldık ve onlara peygamberler gönderdik. Fakat ne zaman onlara bir peygamber nefislerinin hoşlanmadığı bir şey getirmişse, bunlardan bir kısmını yalanlamışlar, bir kısmını da öldürmüşlerdir.'),
(740, 5, 71, 'Onlar, bir fitne kopmayacak sandılar, kör ve sağır kesildiler. Sonra Allah onların tevbesini kabul etti. Sonra yine onların çoğu kör, sağır kesildiler. Allah, onların yaptıklarını görüyor.'),
(741, 5, 72, 'Andolsun, "Allah, Meryem\'in oğlu Mesih\'tir" diyenler elbette kâfir olmuşlardır. Oysa Mesih onlara: "Ey İsrailoğulları, hem benim, hem de sizin Rabbiniz olan Allah\'a ibadet edin. Kim Allah\'a ortak koşarsa, şüphesiz Allah ona cenneti haram kılmıştır ve onun varacağı yer cehenemdir. Zalimlerin yardımcıları da yoktur" demişti.'),
(742, 5, 73, '"Allah, üçün üçüncüsüdür" diyenler elbette kâfir olmuşlardır. Oysa tek ilâhtan başka ilâh yoktur. Eğer söylediklerinden vazgeçmezlerse, elbette onlardan inkâr edenlere acı bir azap dokunacaktır.'),
(743, 5, 74, 'Hâlâ Allah\'a tevbe edip O\'ndan af dilemiyorlar mı? Allah çok bağışlayandır, çok merhamet edendir.'),
(744, 5, 75, 'Meryem\'in oğlu Mesih (İsa), sadece bir peygamberdir. Ondan önce de peygamberler gelip geçmiştir. Anası da dosdoğru bir kadındır. Her ikisi de yemek yerlerdi. Bak onlara âyetleri nasıl açıklıyoruz. Sonra yine bak nasıl yüz çeviriyorlar!'),
(745, 5, 76, 'De ki: "Allah\'ı bırakıp da size ne zarar, ne de fayda vermeye gücü yetmeyen şeylere mi tapıyorsunuz? Oysa Allah işitendir, bilendir".'),
(746, 5, 77, 'De ki: "Ey kitap ehli! Dininizde haksız yere aşırı gitmeyin. Daha önce sapmış, birçoklarını da saptırmış ve böylece doğru yolu kaybetmiş bir kavmin keyiflerine uymayın".'),
(747, 5, 78, 'İsrailoğulları\'ndan küfredenler, Davud ve Meryem\'in oğlu İsa diliyle lanetlenmişlerdir. Bu, onların isyan etmeleri ve aşırı gitmeleri yüzündendi.'),
(748, 5, 79, 'Onlar, yaptıkları kötülüklerden vazgeçmiyorlardı. Yaptıkları şey ne kötü idi.'),
(749, 5, 80, 'Onlardan birçoğunun kâfirleri dost edindiklerini görürsün. Nefislerinin kendilerine sunduğu şey ne kadar kötüdür! Allah onlara gazabetmiştir. Onlar ebedî olarak azap içinde kalacaklardır.'),
(750, 5, 81, 'Eğer onlar, Allah\'a, Peygamber\'e ve ona indirilen Kur\'ân\'a inanmış olsalardı, kâfirleri dost tutmazlardı. Fakat onların çoğu yoldan çıkmış kimselerdir.'),
(751, 5, 82, 'İman edenlere karşı düşmanlık yönünden insanların en şiddetlisi olarak yahudileri ve Allah\'a ortak koşanları bulursun. Ve yine iman edenlere sevgi bakımından en yakın olarak da: "Biz hıristiyanlarız" diyenleri bulursun. Çünkü onların içlerinde keşişler ve rahipler vardır. Ve onlar büyüklük taslamazlar.'),
(752, 5, 83, 'Peygamber\'e indirilen (Kur\'ân)i dinledikleri zaman, onun hak olduğunu öğrendiklerinden dolayı gözlerinin yaşla dolup taştığını görürsün. Onlar: " Ey Rabb\'imiz iman ettik, bizi de şahitlerden yaz" derler.'),
(753, 5, 84, '"Hem biz Rabb\'imizin bizi iyi kişilerle birlikte (cennete) sokmasını arzulayıp dururken, neden Allah\'a ve hak olarak bize gelen şeylere inanmayalım!".'),
(754, 5, 85, 'Böyle demeleri sebebiyle Allah onları altlarından ırmaklar akan cennetlerle mükafatlandırmıştır. Orada ebedî olarak kalacaklardır. İşte iyilik yapanların mükafatı budur.'),
(755, 5, 86, 'İnkar edip âyetlerimizi yalanlayanlar da cehennem ehlidir.'),
(756, 5, 87, 'Ey iman edenler! Allah\'ın size helal kıldığı temiz şeyleri haram saymayın. Ve aşırı da gitmeyin. Çünkü Allah aşırı gidenleri sevmez.'),
(757, 5, 88, 'Allah\'ın size verdiği rızıklardan helal ve temiz olarak yeyin ve inandığınız Allah\'tan korkun.'),
(758, 5, 89, 'Allah sizi, kasıtsız olarak yaptığınız yeminlerinizden sorumlu tutmaz. Fakat kasıtlı yaptığınız yeminlerinizden sizi sorumlu tutar. Bozulan yeminin keffareti (cezası), ailenize yedirdiğinizin ortalamasından on yoksulu yedirmek veya giydirmek yahut da bir köle azad etmektir. Verecek bir şey bulamayan kimse için de üç gün oruç tutmaktır. İşte yemin ettiğiniz zaman yeminlerinizi bozmanın cezası budur. Yeminlerinizi koruyun. İşte Allah âyetlerini size böyle açıklar ki, şükredesiniz.'),
(759, 5, 90, 'Ey iman edenler! İçki, kumar, dikili taşlar (putlar) ve fal okları şeytan işi birer pisliktir. Bunlardan kaçının ki, kurtuluşa eresiniz.'),
(760, 5, 91, 'Şeytan, içki ve kumarla sizin aranıza düşmanlık ve kin sokmak ve sizi Allah\'ı anmaktan ve namazdan alıkoymak ister. Artık bunlardan vazgeçtiniz değil mi?'),
(761, 5, 92, 'Allah\'a itaat edin, Peygamber\'e de itaat edin. Kötülüklerden sakının. Eğer yüz çevirirseniz, biliniz ki, Peygamber\'imize düşen sadece apaçık tebliğdir.'),
(762, 5, 93, 'İman edip salih amel işleyenler, Allah\'tan korktukları, imanlarında sebat ettikleri, salih amel işlemeye devam ettikleri, sonra Allah\'tan sakındıkları, imanlarından ayrılmadıkları, yine Allah\'tan korktukları ve iyilikte bulundukları müddetçe, daha önce yediklerinden dolayı kendilerine bir günah yoktur. Allah iyilikte bulunanları sever.'),
(763, 5, 94, 'Ey iman edenler! Allah sizi ellerinizin ve mızraklarınızın erişeceği bir avla dener ki, gizlide kendisinden korkanları meydana çıkarsın. Kim bundan sonra saldırıda bulunursa onun için acı bir azab vardır.'),
(764, 5, 95, 'Ey iman edenler, ihramlı iken av hayvanı öldürmeyin. İçinizden kim kasten onu öldürürse, yaptığı işin vebalini tatması için, öldürdüğü hayvanın dengi ona cezadır ki, Kâbe\'ye ulaşacak bir kurban olmak üzere buna yine içinizden iki adaletli kişi hükmeder; yahut (ceza olmak üzere) bir keffarettir ki, ya o nisbette fakirleri doyurmak, yahut onun dengi oruç tutmaktır. Allah geçmişi affetmiştir. Fakat kim de bu suçu tekrarlarsa, Allah ondan intikamını alır. Allah damia gâliptir, intikam sahibidir.'),
(765, 5, 96, 'Size ve yolculara yiyecek olmak üzere, deniz avı ve onu yemek helal kılındı. Kara avı ise, ihramlı olduğunuz müddetçe size haram edilmiştir. Huzurunda toplanacağınız Allah\'tan korkun.'),
(766, 5, 97, 'Allah, Kâbe\'yi, o Beyti haram\'ı, haram ayı, kurbanı ve (kurbanlardaki) gerdanlıkları insanlar için bir nizam kıldı. Bu, Allah\'ın göklerde ve yerde olan herşeyi bildiğini ve Allah\'ın herşeyi hakkıyle bilici olduğunu sizin de bilmeniz içindir.'),
(767, 5, 98, 'İyi bilin ki Allah, hem cezası çok şiddetli olandır, hem de çok bağışlayıcı, çok merhametlidir.'),
(768, 5, 99, 'Peygamber\'in üzerine düşen sadece duyurmadır. Allah, açıkladıklarınızı da gizlediklerinizi de bilir.'),
(769, 5, 100, 'De ki: "Pis olan şeyle temiz olan şey bir olmaz, pis olanın çokluğu hoşuna gitse bile". Ey selim akıl sahipleri Allah\'tan korkun ki kurtuluşa eresiniz.'),
(770, 5, 101, 'Ey iman edenler! Açıklandığı zaman hoşunuza gitmeyecek olan şeylerden sormayın. Eğer onları Kur\'ân indirilirken sorarsanız size açıklanır. Halbuki Allah onlardan geçmiştir. Allah çok bağışlayan ve çok yumuşak davranandır.'),
(771, 5, 102, 'Sizden önce gelen bir kavim bunları sormuştu da sonra inkâr etmişti.'),
(772, 5, 103, 'Allah, ne "bahîre"yi, ne "sâibe"yi, ne "vesile"yi ve ne de "hâm"ı meşru kılmıştır. Fakat küfredenler, Allah\'a yalan iftira etmektedirler. Onların çoğunun akılları ermez.'),
(773, 5, 104, 'Onlara: " Allah\'ın indirdiği (kitabı)ne ve peygamber\'e gelin" dendiği zaman: " Atalarımızı üzerinde bulduğumuz şey bize yeter" derler. Ataları bir şey bilmeyen ve doğru yolu da bulamayan kimseler olsa da mı?'),
(774, 5, 105, 'Ey inananlar, kendinize dikkat edin. Siz doğru yolda olduğunuz takdirde doğru yoldan sapanlar size zarar veremezler. Hepinizin dönüşü Allah\'adır. Yaptıklarınızı size O haber verecektir.'),
(775, 5, 106, 'Ey iman edenler! İçinizden birine ölüm (emareleri) geldiği zaman, vasiyet sırasında aranızdaki şahitliğin hükmü, kendi içinizden iki adaletli şahit, yahut yeryüzünde yolculuğa çıkmış iseniz, ölüm (emareleri de) size gelip çatmışsa, sizden olmayan diğer iki şahit tutmaktır. Eğer (bunlardan) şüpheye düşerseniz, namazdan sonra onları alıkorsunuz. Onlar da Allah\'a şöyle yemin ederler: "Akraba bile olsa, yemini bir çıkar karşılığı satmayacağız, Allah\'ın şahitliğini gizlemeyeceğiz. Aksi halde günahkârlardan oluruz".'),
(776, 5, 107, 'Eğer o iki şahidin bir günah işledikleri anlaşılırsa ölene daha yakın olan hak sahiplerinden diğer iki kişi onların yerine geçerler ve: "Bizim şahitliğimiz, önceki iki kişinin şahitliğinden daha doğrudur. Biz kimsenin hakkına tecavüz etmedik. Aksi halde biz de zalimlerden olurduk" diye Allah\'a yemin ederler.'),
(777, 5, 108, 'İşte bu, şahitliklerini gerektiği gibi yapmaları, yahut yeminlerinden sonra yeminlerinin kabul edilmemesinden korkmaları için en iyi yoldur. Allah\'tan korkun ve emirlerini dinleyin. Allah, doğru yoldan çıkan bir topluluğu hidayete erdirmez.'),
(778, 5, 109, 'Allah, Resulleri topladığı gün: " Size ne cevap verildi? "der. "Bizim bilgimiz yok" derler, "gizlileri bilen yalnız sensin, sen!".'),
(779, 5, 110, 'Allah şöyle diyecektir: "Ey Meryemoğlu İsa! Sana ve annene olan nimetimi hatırla! Hani seni Rûhu\'lKudüs (Cebrâil) ile desteklemiştim. Beşikteyken ve kemâle ermişken insanlarla konuşuyordun. Sana yazıyı, hikmeti, Tevrat\'ı ve İncil\'i öğretmiştim. İznimle çamurdan kuş şeklinde bir şey yapmış ve ona üflemiştin, o da iznimle kuş olmuştu. Anadan doğma kör olanı ve alaca hastalığına yakalanmış kimseyi iznimle iyileştirmiştin. Ölüleri iznimle (hayata) çıkarmıştın. İsrailoğulları\'na âyetlerle geldiğin ve onlardan inkâr edenlerin: "Bu ancak apaçık bir sihirdir" dedikleri zaman seni, onlardan korumuştum.'),
(780, 5, 111, 'Hani Havarilere: " Bana ve Resulüme iman edin" diye ilham etmiştim. Onlar da: "İman ettik, bizim şüphesiz müslümanlar olduğumuza şahit ol" demişlerdi.'),
(781, 5, 112, 'Havariler: " Ey Meryemoğlu İsa, Rabbin bize gökten bir sofra indirebilir mi?" dediler. İsa da: "İnanıyorsanız Allah\'tan korkun" dedi.'),
(782, 5, 113, 'Havâriler: "İstiyoruz ki ondan yiyelim, kalblerimiz iyice yatışsın, senin bize doğru söylediğini bilelim ve bunu bizzat görenlerden olalım" dediler.'),
(783, 5, 114, 'Meryemoğlu İsa da: "Allah\'ım, Rabbımız, bizim üzerimize gökten bir sofra indir ki, bizim için, önce ve sonra gelenlerimiz için bir bayram ve senden bir mucize olsun. Bizi rızıklandır, sen rızık verenlerin en hayırlısısın!" dedi.'),
(784, 5, 115, 'Allah buyurdu ki: " Ben onu size indireceğim. Fakat bundan sonra içinizden kim inkâr ederse, ben ona âlemlerden hiç kimseye yapmayacağım bir azabı yaparım".'),
(785, 5, 116, 'Ve Allah demişti ki: "Ey Meryemoğlu İsa, sen mi insanlara: \'Beni ve annemi, Allah\'tan başka iki tanrı edinin\' dedin?". "Hâşâ, dedi, sen yücesin, benim için gerçek olmayan birşeyi söylemem bana yakışmaz. Eğer demiş olsam, sen bunu bilirsin, sen benim nefsimde olanı bilirsin, ben ise senin nefsinde olanı bilmem, çünkü gaybları bilen yalnız sensin, sen!".'),
(786, 5, 117, '"Ben onlara sadece, senin bana emrettiklerini söyledim. Benim ve sizin Rabbınız olan Allah\'a kulluk edin, dedim. Aralarında olduğum müddetçe onlara şahit idim, fakat sen beni vefat ettirince onları gözetleyen yalnız sen oldun. Sen herşeyi görensin.'),
(787, 5, 118, '"Eğer onlara azab edersen, onlar senin kullarındır, eğer onları bağışlarsan, şüphesiz sen daima üstünsün, hikmet sahibisin".'),
(788, 5, 119, 'Allah buyurdu ki: "Bu, sadıklara doğruluklarının fayda sağladığı gündür. Onlar için altlarından ırmaklar akan, içinde ebedî kalacakları cennetler vardır". Allah onlardan razı olmuş, onlar da O\'ndan razı olmuşlardır. İşte büyük kurtuluş budur.'),
(789, 5, 120, 'Göklerin, yerin ve bunlarda bulunan herşeyin mülkü Allah\'ındır. O herşeye kâdirdir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Hamd, gökleri ve yeri yaratan, karanlıkları ve aydınlığı var eden Allah\'a mahsustur. Böyleyken kâfirler hâlâ Rablerine başkalarını eşit sayıyorlar.'),
(791, 6, 2, 'Sizi çamurdan yaratan, sonra size bir ecel takdir eden O\'dur. Tayin edilen bir ecel de (kıyamet zamanı) O\'nun katındadır. Sonra bir de şüphe ediyorsunuz.'),
(792, 6, 3, 'O, göklerde de, yerde de (tek) Allah\'tır. Sizin gizlinizi, açığınızı ve ne kazandığınızı bilir.'),
(793, 6, 4, 'Onlara Rab\'lerinin âyetlerinden hiçbir âyet gelmez ki, ondan yüz çevirmesinler.'),
(794, 6, 5, 'Hak, kendilerine gelince onu yalanladılar. Alaya aldıkları şeyin haberi yakında kendilerine gelecektir.'),
(795, 6, 6, 'Kendilerinden önce nice nesilleri helak ettiğimizi görmediler mi? Yeryüzünde size vermediğimiz imkanları onlara vermiştik. Onlara gökten bol bol yağmur indirmiş, altlarından ırmaklar akıtmıştık. Fakat onları günahlarından dolayı helak ettik. Ve kendilerinden sonra başka bir nesil yarattık.'),
(796, 6, 7, 'Eğer sana kağıtta yazılı bir kitap indirmiş olsak da onu elleriyle tutsalardı, yine de o kâfirler: "Muhakkak ki bu, apaçık bir sihirdir" derlerdi.'),
(797, 6, 8, '"O\'na bir melek indirilmeli değil miydi?" dediler. Eğer bir melek indirseydik, iş bitirilmiş olurdu, sonra kendilerine hiç göz açtırılmazdı.'),
(798, 6, 9, 'Eğer Peygamberi, biz bir melek yapsaydık, yine de onu bir adam şeklinde yapardık ve onları yine düştükleri kuşkuya düşürürdük.'),
(799, 6, 10, 'Senden önce de peygamberlerle alay edilmişti. Fakat onlardan alay edenleri, alay ettikleri şey kuşatıverdi.'),
(800, 6, 11, 'De ki: "Yeryüzünde dolaşın da yalanlayanların sonu nasıl olmuş, görün!".'),
(801, 6, 12, 'De ki: "Göklerde ve yerde olanlar kimindir?" "Allah\'ındır" de. O, rahmet etmeyi kendi nefsine yazmıştır. Sizi, varlığında asla şüphe olmayan kıyamet gününde toplayacaktır. Ama kendilerini zarara sokanlar inanmazlar.'),
(802, 6, 13, 'Gecede, gündüzde barınan her şey O\'nundur. O, işitendir, bilendir.'),
(803, 6, 14, 'De ki: "Gökleri ve yeri yoktan var eden, besleyen, fakat kendisi beslenmeyen Allah\'tan başka dost mu tutayım?" "Ben İslâm olanların ilki olmakla emrolundum" de ve sakın Allah\'a ortak koşanlardan olma.'),
(804, 6, 15, 'De ki: "Eğer Rabbime isyan edersem, büyük bir günün azabından korkarım".'),
(805, 6, 16, 'O gün kimden azab giderilirse, kuşkusuz Allah ona rahmet etmiştir. İşte apaçık kurtuluş budur.'),
(806, 6, 17, 'Allah sana bir zarar dokundurursa, onu yine kendisinden başka açacak yoktur. Ve eğer sana bir hayır dokundursa, kuşkusuz O, herşeyi yapabilendir.'),
(807, 6, 18, 'O, kullarının üstünde tam hâkimdir. O, hüküm ve hikmet sahibidir, herşeyden haberdardır.'),
(808, 6, 19, 'De ki: "Şahitlik yönünden hangi şey daha büyüktür?". De ki: "Allah, benimle sizin aranızda şahittir ve bana bu Kur\'ân vahyolundu ki, onunla hem sizi, hem de sizden sonra kendisine ulaşan herkesi uyarayım. Allah\'la beraber başka ilâhlar olduğuna siz gerçekten şahitlik eder misiniz?" De ki: "Ben buna şahitlik etmem". "O, ancak ve ancak bir tek ilâhtır ve gerçekten ben, sizin ortak tuttuğunuz şeylerden uzağım"de.'),
(809, 6, 20, 'Kendilerine Kitap verdiğimiz kimseler, Peygamber\'i, kendi oğullarını bildikleri gibi, bilirler. Kendilerine yazık edenler var ya! İşte onlar iman etmezler.'),
(810, 6, 21, 'Allah\'a iftira ederek yalan uydurandan veya âyetlerini yalanlayandan daha zalim kim olabilir? Hiç şüphe yok ki zalimler kurtuluşa eremezler.'),
(811, 6, 22, 'O gün hepsini mahşere toplayacağız. Sonra Allah\'a ortak koşanlara: " Hani nerede o Allah\'a ortak saydığınız ortaklarınız?" diyeceğiz.'),
(812, 6, 23, 'Sonra, (Onlar): "Rabbimiz, Allah\'a yemin ederiz ki, biz müşriklerden değildik" demekten başka bir özür bulamayacaklar.'),
(813, 6, 24, 'Bak, vicdanlarına karşı nasıl yalan söylediler! O uydurdukları putlar da kendilerinden kaybolup gitti.'),
(814, 6, 25, 'İçlerinden seni dinleyenler de vardır, fakat biz, onu anlamalarına engel olmak için kalblerinin üstüne örtüler, kulaklarının içine de ağırlık koyduk. Onlar, bütün delilleri görseler bile yine ona inanmazlar. Hatta sana geldiklerinde seninle tartışırlar. Ve o kâfirler: "Bu, öncekilerin masallarından başka bir şey değildir" derler.'),
(815, 6, 26, 'Onlar, insanları Kur\'ân\'a iman etmekten menederler, hem de kendileri ondan uzak dururlar. Böylece yalnız kendilerini mahvediyorlar ama farkında değiller.'),
(816, 6, 27, 'Onların, ateşin üzerinde durduruldukları zaman: "Ne olurdu dünyaya döndürülseydik, Rabb\'imizin âyetlerini yalanlamasaydık da müminlerden olsaydık" dediklerini bir görsen!'),
(817, 6, 28, 'Hayır, daha önce gizleyip durdukları karşılarına çıktı da ondan, yoksa geri çevrilselerdi yine menedildikleri şeyi yapmaya dönerlerdi. Çünkü onlar yalancıdırlar.'),
(818, 6, 29, 'Dediler ki: " Dünya hayatımızdan başka bir hayat yoktur, biz diriltilecek değiliz".'),
(819, 6, 30, 'Rablerinin huzurunda durduruldukları zaman onları bir görsen! Rableri onlara şöyle der: "Bu, bir gerçek değil midir?". Onlar da: "Rabbimize yemin ederiz ki gerçektir" derler. Rableri de onlara: "Öyleyse inkârınız sebebiyle azabı tadın!" der.'),
(820, 6, 31, 'Allah\'ın huzuruna çıkmayı yalanlayanlar, gerçekten hüsrana uğramışlardır. Kıyamet günü ansızın gelince onlar, günahlarını sırtlarına yüklenmiş olarak şöyle derler: "Dünyada yaptığımız kusurlardan dolayı yazıklar olsun bize!" Bakın yüklendikleri günah ne kötüdür!'),
(821, 6, 32, 'Dünya hayatı, eğlence ve oyundan başka bir şey değildir. Ahiret yurdu ise, Allah\'tan korkanlar için daha hayırlıdır. Aklınızı kullanmaz mısınız?'),
(822, 6, 33, 'Onların söylediklerinin seni üzdüğünü elbette biliyoruz. Onlar aslında seni yalanlamıyorlar, fakat, o zalimler Allah\'ın âyetlerini inkâr ediyorlar.'),
(823, 6, 34, 'Senden önce de peygamberler yalanlanmıştı. Kendilerine yardımımız gelinceye kadar yalanlanmaya ve eziyet olunmaya sabrettiler. Allah\'ın sözlerini değiştirecek hiçbir kimse yoktur. Şüphesiz ki sana, peygamberlerin haberlerinden bir kısmı gelmiştir.'),
(824, 6, 35, 'Eğer onların yüz çevirmesi sana ağır geldiyse, haydi gücün yetiyorsa yerin içine (inebileceğin) bir delik, ya da göğe (çıkabileceğin) bir merdiven ara ki onlara bir mucize getiresin! Allah dileseydi, elbette onları hidayet üzerinde toplardı. O halde cahillerden olma!'),
(825, 6, 36, 'Daveti ancak dinleyenler kabul ederler. Ölülere gelince, Allah onları diriltir, sonra O\'na döndürülürler.'),
(826, 6, 37, 'Dediler ki: "Ona Rabbinden bir mucize indirilmeli değil miydi?" De ki: "Şüphesiz ki Allah, bir mucize indirmeye kâdirdir, fakat çokları bilmezler".'),
(827, 6, 38, 'Yeryüzünde yürüyen hiçbir hayvan ve iki kanadıyla uçan hiçbir kuş yoktur ki, sizin gibi birer ümmet olmasınlar. Biz kitapta hiçbir şeyi eksik bırakmamışızdır, sonra hepsi Rablerinin huzurunda toplanırlar.'),
(828, 6, 39, 'Âyetlerimizi yalanlayanlar, karanlıklar içinde kalmış sağır ve dilsizlerdir. Allah dilediği kimseyi şaşırtır, dilediği kimseyi de doğru yola koyar.'),
(829, 6, 40, 'De ki: "Kendinizi hiç düşündünüz mü, Allah\'ın azabı size gelse veya kıyamet vakti gelse, Allah\'tan başkasına mı yalvarırsınız? Eğer sözünde doğru kimselerseniz cevap verin".'),
(830, 6, 41, 'Hayır, yalnız o Allah\'a yalvarırsınız. O da dilerse kaldırılmasını istediğiniz belayı kaldırır ve o zaman ortak koştuğunuz şeyleri unutursunuz.'),
(831, 6, 42, 'Şüphesiz ki senden önceki ümmetlere de peygamberler gönderdik. Bize yalvarsınlar diye onları darlık ve sıkıntı ile yakalayıp cezalandırdık.'),
(832, 6, 43, 'Hiç olmazsa kendilerine baskınımız geldiği zaman olsun, yalvarmalı değiller miydi? Fakat kalbleri katılaştı ve şeytan yaptıklarını kendilerine güzel gösterdi.'),
(833, 6, 44, 'Kendilerine hatırlatılanları unuttuklarında, onlara her şeyin kapısını açtık. Nihayet kendilerine verilen o nimetlerle sevinip zevke dalınca onları azabımızla ansızın yakalayıverdik. Hemen ümitsizliğe kapılıp şaşkına döndüler.'),
(834, 6, 45, 'Böylece zulmeden kavmin kökü kesildi. Âlemlerin Rabbi olan Allah\'a hamdolsun.'),
(835, 6, 46, 'De ki: "Söyleyin bakalım, eğer Allah kulaklarınızı ve gözlerinizi alır da kalblerinize mühür vurursa, Allah\'tan başka onları size getirecek tanrı kimdir?". Dikkat et, âyetlerimizi nasıl türlü türlü açıklıyoruz, sonra da onlar yüz çeviriyorlar?'),
(836, 6, 47, 'De ki: "Söyler misiniz bana! Size Allah\'ın azabı ansızın veya açıkça gelirse, zalim toplumdan başkası mı helak olur?"'),
(837, 6, 48, 'Biz peygamberleri, ancak rahmetimizin müjdecileri ve azabımızın habercileri olmak üzere göndeririz. Artık kim iman edip durumunu düzeltirse, onlara hiç korku yoktur. Onlar mahzun da olmayacaklardır.'),
(838, 6, 49, 'Âyetlerimizi yalanlayanlara gelince, yapmakta oldukları fenalıklar yüzünden onlara azap dokunacaktır.'),
(839, 6, 50, 'De ki: "Size Allah\'ın hazineleri benim yanımdadır, demiyorum. Gaybı da bilmiyorum. Ve size, ben bir meleğim de demiyorum. Ben sadece bana vahyolunana uyuyorum." De ki: "Kör ile gören bir olur mu? Hiç düşünmez misiniz?"'),
(840, 6, 51, 'Rablerinin huzurunda toplanacaklarından korkanları Kur\'an\'la uyar. Onlar için Allah\'tan başka ne bir dost, ne de bir şefaatçi vardır. Gerekir ki Allah\'tan korkarlar.'),
(841, 6, 52, 'Sırf Allah\'ın rızasını dileyerek sabah akşam Rab\'lerine dua edenleri huzurundan kovma. Onların hesabından sen sorumlu değilsin, onlar da senin hesabından sorumlu değiller. Onları yanından kovduğun takdirde zalimlerden olursun.'),
(842, 6, 53, 'Biz onlardan kimini kimi ile, "Allah aramızdan bunlara mı lutfunu layık gördü" desinler diye, işte böyle imtihan ettik. Allah, şükredenleri daha iyi bilen değil midir?'),
(843, 6, 54, 'Âyetlerimize inananlar sana geldikleri zaman onlara şöyle söyle: Selam olsun size! Rabbiniz rahmeti kendi üzerine yazdı. Sizden her kim bilmeyerek bir kötülük işleyip de sonra arkasından tevbe eder, kendini düzeltirse, muhakkak ki O, bağışlayan, esirgeyendir".'),
(844, 6, 55, 'Suçluların tuttuğu yol açığa çıksın diye, âyetleri işte böyle genişçe açıklıyoruz.'),
(845, 6, 56, 'De ki: "Şüphesiz ki bana, Allah\'tan başka yalvardıklarınıza ibadet etmem yasaklandı". De ki: "Sizin çarpık isteklerinize uymayacağım, (eğer uyarsam) o zaman sapıtmış olur, doğru yolda gidenlerden olmamış olurum".'),
(846, 6, 57, 'De ki: "Ben Rabbimden apaçık bir delile dayanmaktayım, siz ise onu yalanladınız. O çabuk gelmesini istediğiniz azab benim elimde değildir, hüküm ancak Allah\'a aittir, gerçeği O anlatır ve O, hakkı bâtıldan ayırdedenlerin en hayırlısıdır".'),
(847, 6, 58, 'De ki: "Sizin çabuk gelmesini istediğiniz azab benim elimde olsaydı, benimle sizin aranızdaki durum herhalde sonuçlanmış olurdu. Allah, zulmedenleri en iyi bilendir".'),
(848, 6, 59, 'Gaybın anahtarları O\'nun katındadır, onları O\'ndan başkası bilmez, karada ve denizde olanları O bilir ve bir yaprak düşmez ki, onu O bilmesin; ne toprağın karanlıklarında bir tane, ne de kuru ve yaş hiçbir şey yoktur ki, o herşeyi açıklayan Kitap\'ta bulunmasın.'),
(849, 6, 60, 'Sizi geceleyin ölü gibi uyutan, gündüzün ne yaptıklarınızı bilen, sonra ölüm ânı gelinceye kadar gündüzleri sizi uyandırıp kaldıran O\'dur. Sonunda da dönüşünüz ancak O\'nadır. Sonra bütün yaptıklarınızı size O haber verecektir.'),
(850, 6, 61, 'O, kulları üzerinde hükümranlığı sürdürür ve size koruyucular gönderir, sonunda sizden birinize ölüm geldiği vakit elçilerimiz, hiç eksiklik yapmadan, onun canını alırlar.'),
(851, 6, 62, 'Sonra da gerçek Mevlâlarına döndürülürler. Dikkatli olun, hüküm ancak O\'nundur ve O, hesap görenlerin en süratlisidir.'),
(852, 6, 63, 'De ki: "Bizi bu tehlikeden kurtarırsa elbette şükredenlerden olacağız" diye gizli ve aşikâr O\'na yalvarıp dururken, karanın ve denizin karanlıklarından sizi kim kurtarır?'),
(853, 6, 64, 'De ki: "Allah, sizi ondan ve bütün sıkıntılardan kurtarır, sonra da siz yine ortak koşarsınız".'),
(854, 6, 65, 'De ki: "O\'nun üstünüzden ve ayaklarınızın altından azab göndermeye, yahut sizi fırkalara ayırıp kiminizin kiminize hıncını tattırmaya gücü yeter". Bak, âyetlerimizi nasıl inceden inceye açıklıyoruz ki, onlar iyice anlasınlar.'),
(855, 6, 66, 'Kavmin o (Kur\'ân\'ı) yalan saydı, halbuki o gerçektir. De ki: " Ben sizin vekiliniz değilim".'),
(856, 6, 67, 'Her haberin kararlaştırılmış bir zamanı vardır, siz de onu yakında bileceksiniz.'),
(857, 6, 68, 'Âyetlerimiz hakkında münasebetsizliğe dalanları gördüğün zaman hemen onlardan uzaklaş ki, ondan başka söze dalsınlar. Eğer şeytan bunu sana unutturursa hatırladıktan sonra hemen kalk, o zalimler topluluğuyla oturma.'),
(858, 6, 69, 'Allah\'tan korkanlara o zalimlerin hesabından bir sorumluluk yoktur. Fakat bu bir hatırlatmadır. Gerekir ki sakınırlar.'),
(859, 6, 70, 'Dinlerini bir oyun ve bir eğlence edinen ve kendilerini dünya hayatının aldattığı kimseleri bırak! Ve hiçbir kimsenin kazandığı şey yüzünden kendisini helake atmamasını, kendisi için Allah\'tan başka hiç bir dost ve hiçbir şefaatçi bulunmadığını Kur\'ân ile hatırlat. O, azaptan kurtulmak için bütün varını feda etse, kendisinden alınmaz. Onlar kazandıkları şey yüzünden helake uğratılmışlardır. Onlar için, inkâr ettiklerinden dolayı kaynar bir içecek ve can yakıcı bir azab vardır.'),
(860, 6, 71, 'De ki: "Biz Allah\'ı bırakıp da bize fayda veya zarar vermeyen şeylere mi yalvaralım? Allah bizi doğru yola kavuşturduktan sonra ardımıza mı dönelim? Arkadaşları, bize gel, diye doğru yola çağırdıkları halde yeryüzünde şaşkın şaşkın dolaşıp, şeytanların ayartarak uçuruma çektikleri ahmak gibi mi olalım?". De ki: "Allah\'ın gösterdiği yol, yegane doğru yoldur. Bize, bütün âlemlerin Rabb\'ine teslim olmamız emrolundu".'),
(861, 6, 72, 'Bize: "Namazı dosdoğru kılın, Allah\'a karşı gelmekten sakının" (diye emredildi), toplanacağınız yer O\'nun huzurudur.'),
(862, 6, 73, 'Gökleri ve yeri, yerli yerince yaratan O\'dur. Bir şeye "ol" dediği gün hemen oluverir. O\'nun sözü haktır. "Sûr"a üfürüldüğü gün de mülk ancak O\'nundur. O, gizliyi ve açığı bilendir. O, hikmet sahibi, her şeyden haberdardır.'),
(863, 6, 74, 'İbrahim, babası Âzer\'e demişti ki: "sen putları tanrı mı ediniyorsun? Doğrusu ben seni ve kavmini açık bir sapıklık içinde görüyorum".'),
(864, 6, 75, 'Böylece biz İbrahim\'e göklerin ve yerin melekûtunu (muhteşem varlıklarını) gösteriyorduk ki, kesin inananlardan olsun.'),
(865, 6, 76, 'Üzerine gece bastırınca, bir yıldız gördü: "Rabb\'im budur" dedi. Yıldız batınca da: " Ben batanları sevmem" dedi.'),
(866, 6, 77, 'Ay\'ı doğarken gördü: "Rabb\'im budur" dedi. O da batınca: "Yemin ederim ki, Rabbim bana doğru yolu göstermeseydi, elbette sapıklığa düşen topluluktan olurdum" dedi.'),
(867, 6, 78, 'Güneş\'i doğarken görünce: "Rabb\'im budur, bu hepsinden büyük" dedi. O da batınca dedi ki: "Ey kavmim! Ben sizin (Allah\'a) ortak koştuğunuz şeylerden uzağım".'),
(868, 6, 79, '"Ben yüzümü tamamen, gökleri ve yeri yoktan var edene çevirdim ve artık ben asla Allah\'a ortak koşanlardan değilim".'),
(869, 6, 80, 'Kavmi onunla tartışmaya başladı. O da onlara dedi ki: "Beni doğru yola eriştirdiği halde Allah hakkında benimle mücadele mi ediyorsunuz? O\'na ortak koştuklarınızdan hiç korkmuyorum, ancak Rabbimin dilediği şey hariç. Rabbim ilmiyle her şeyi kuşatmıştır. Hiç düşünmez misiniz?"'),
(870, 6, 81, '"Hakkında hiçbir delil indirmediği halde, siz Allah\'a ortak koşmaktan korkmuyorsunuz da, ben sizin ortak koştuklarınızdan nasıl korkarım?" Eğer bilirseniz söyleyin, bu iki topluluktan hangisi güven içinde olmaya daha layıktır?'),
(871, 6, 82, 'İman edenler ve imanlarını zulüm ile karıştırmayanlar... İşte güven onlarındır ve doğru yolu bulanlar da onlardır.'),
(872, 6, 83, 'İşte bunlar, kavmine karşı İbrahim\'e verdiğimiz delillerimizdir. Dilediğimizi derecelerle yükseltiriz. Muhakkak Rabbin hikmet sahibidir, bilendir.'),
(873, 6, 84, 'Biz ona İshak\'ı ve Yakub\'u da hediye ettik: Hepsine de doğru yolu gösterdik. Nitekim daha önce Nuh\'a ve onun soyundan Davud\'a, Süleyman\'a, Eyyub\'a, Yusuf\'a, Musa\'ya ve Harun\'a da yol göstermiştik. Biz güzel davrananlara böyle karşılık veririz.'),
(874, 6, 85, 'Zekeriyya, Yahya, İsa ve İlyas\'a da (hidayet ettik). Hepsi de salih kullarımızdandı.'),
(875, 6, 86, 'İsmail, Elyesa, Yunus ve Lut\'u da (hidayete erdirdik). Hepsini âlemlere üstün kıldık.'),
(876, 6, 87, 'Babalarından, çocuklarından ve kardeşlerinden bazılarını da (üstün kıldık). Onları seçtik ve doğru yola ilettik.'),
(877, 6, 88, 'İşte bu, Allah\'ın doğru yoludur. Kullarından dilediğini o doğru yola iletir. Eğer onlar Allah\'a ortak koşsalardı, yaptıkları bütün amelleri boşa giderdi.'),
(878, 6, 89, 'İşte onlar, kendilerine kitap, hüküm (hikmet ve hükümranlık) ve peygamberlik verdiğimiz kimselerdir. Bunlar, ona inanmayacak olurlarsa, yerlerine, onu tanımamazlık etmiyecek bir toplum getiririz.'),
(879, 6, 90, 'Bunlar, Allah\'ın hidayet ettiği kimselerdir. Sen de onların hidayetine uy. De ki: "Ben ona karşılık sizden bir ücret istemiyorum. O, sadece bütün âlemlere bir öğüttür.'),
(880, 6, 91, 'Onlar: "Allah insanlara hiçbir şey göndermemiştir" demekle, Allah\'ı gereği gibi tanıyamadılar. De ki: Musa\'nın insanlara aydınlık ve hidayet olmak üzere getirdiği, sizin parça parça kâğıtlara çevirdiğiniz, bir kısmını belli ettiğiniz, birçoğunu gizlediğiniz; sizinle babalarınızın, sayesinde bilmediğiniz birçok şeyleri öğrendiğiniz Kitab\'ı kim gönderdi? (Onlara karşı sen) "Allah" de. Sonra onları bırak, boş laflara dalarak oyalansınlar.'),
(881, 6, 92, 'Bu Kitap (Kur\'ân), kendinden önceki kitapları tasdik eden, şehirler anası (Mekke) halkını ve çevresindeki bütün insanlığı uyarman için indirdiğimiz mübarek bir kitaptır. Ahiret gününe iman edenler bu Kitab\'a da iman ederler ve onlar namazlarına da devamlıdırlar.'),
(882, 6, 93, 'Allah\'a karşı yalan uyduran, yahut kendisine hiçbir şey vahyolunmadığı halde: "bana vahyedildi" diyen ve: "Allah\'ın indirdiği gibi bir kitap da ben indireceğim" diye iddiada bulunandan daha zalim kim olabilir? O zalimlerin halini ölüm şiddeti içindeyken bir görsen! Melekler onlara ellerini uzatırlar ve: " Ruhunuzu teslim edin. Bugün, Allah\'a karşı haksız şeyler söylediğinizden ve O\'nun âyetlerine karşı böbürlenmenizden dolayı alçaltıcı bir azapla cezalandıralacaksınız" derler.'),
(883, 6, 94, 'Bugün, sizi ilk defa yarattığımız zamanki gibi yapayalnız huzurumuza geldiniz, size verdiğimiz herşeyi arkanızda bıraktınız. Allah\'ın size göre ortağı olduklarını iddia ederek yardımlarına, şefaatlarına güvendiğiniz ortakları yanınızda görmüyoruz. Aranızdaki bütün bağlar artık kesilmiş, güvendiklerinizin hepsi kaybolup gitmiştir.'),
(884, 6, 95, 'Şüphesiz ki taneleri ve çekirdekleri yaran Allah\'tır. O, ölüden diriyi çıkarır, diriden de ölüyü çıkaran O\'dur. İşte Allah budur. O halde nasıl yüz çevirirsiniz?'),
(885, 6, 96, 'Karanlığı yarıp tanyerini ağartan O\'dur. Geceyi, dinlenmek için; Güneş\'i, Ay\'ı (vakitlerinizi) hesaplamak için yaratmıştır. İşte bu, her şeye galip gelen ve her şeyi bilen Allah\'ın takdiridir.'),
(886, 6, 97, 'Kara ve denizin karanlıklarında yolunuzu bulasınız diye yıldızları sizin için yaratan O\'dur. Şüphesiz biz, bilen bir toplum için âyetleri geniş bir şekilde açıkladık.'),
(887, 6, 98, 'Sizi bir tek candan yaratan O\'dur. Sonra sizin için bir karar yeri, bir de emanet yeri vardır. Biz âyetlerimizi, anlayan bir toplum için apaçık beyan ettik.'),
(888, 6, 99, 'Gökten suyu indiren O\'dur. Onunla her çeşit bitkiyi çıkardık, o bitkiden bir yeşillik çıkardık, ondan da birbiri üzerine binmiş taneler; hurmanın tomurcuğundan sarkan salkımlar, üzüm bağları, zeytin ve nar (bahçeleri) çıkarıyoruz. (Bunların) kimi birbirine benzer, kimi benzemez. Bunlar meyvelendikleri zaman meyvelerinin olgunlaşmasına bakın! Bunlarda inanan bir toplum için ibretler vardır.'),
(889, 6, 100, 'Onlar, Allah\'a cinlerden de ortak koştular. Halbuki onları yaratan O\'dur. Bilgileri olmadan O\'na oğullar, kızlar uydurdular. O\'nun şânı onların uydurdukları sıfatlardan münezzeh ve yücedir.'),
(890, 6, 101, 'Gökleri ve yeri yoktan var eden O\'dur. Eşi de olmadığı halde, nasıl olur da çocuğu olur? Her şeyi yaratan O\'dur. Ve O, herşeyi bilendir.'),
(891, 6, 102, 'İşte Rabbiniz Allah bu! O\'ndan başka ilâh yoktur; O, her şeyin yaratanıdır. O\'na kulluk edin, O her şeye vekildir.'),
(892, 6, 103, 'Gözler onu göremez, O ise bütün gözleri görür; O, lütuf sahibidir, her şeyden haberlidir.'),
(893, 6, 104, 'Muhakkak size Rabbinizden basiretler (kalb gözleri) geldi. Artık kim hakkı görürse faydası kendisine, kim de körlük ederse zararı kendisinedir. Ben sizin bekçiniz değilim!'),
(894, 6, 105, 'İşte böylece âyetleri türlü türlü çevirip açıklıyoruz ki, onlar sana: "Sen bunları bir yerlerden okuyup öğrenmişsin" desinler ve bilen bir toplum için de onu iyice beyan edelim.'),
(895, 6, 106, 'Rabbinden sana vahyedilene uy. O\'ndan başka ilâh yoktur. Ortak koşanlardan da yüz çevir.'),
(896, 6, 107, 'Allah dileseydi, ortak koşmazlardı. Biz, seni onlar üzerine bekçi yapmadık, sen onlara vekil de değilsin!'),
(897, 6, 108, 'Onların Allah\'tan başka yalvardıklarına sövmeyin ki, onlar da bilmeyerek sınırı aşıp Allah\'a sövmesinler. Biz, her ümmete yaptıkları işi böyle süslü gösterdik. Sonunda dönüşleri Rablerinedir. O, onlara ne yaptıklarını haber verir.'),
(898, 6, 109, 'Müşrikler, kendilerine bir mucize gelirse ona mutlaka iman edeceklerine dair en ağır yeminleriyle Allah\'a yemin ettiler. De ki: "Mucizeler ancak Allah katındadır". Onlara mucizeler geldiğinde de iman etmeyeceklerini siz nerden bileceksiniz?'),
(899, 6, 110, 'Biz onların kalblerini ve gözlerini çeviririz de, onlar, ilkin iman etmedikleri gibi, gene de iman etmezler. Biz de onları taşkınlıkları içerisinde kör ve şaşkın bırakırız.'),
(900, 6, 111, 'Eğer biz onlara melekleri indirseydik, ölüler de kendileriyle konuşsaydı ve her şeyi toplayıp karşılarına getirseydik, Allah\'ın diledikleri hariç, yine de inanacak değillerdi, fakat çokları bunu bilmezler.'),
(901, 6, 112, 'Biz böylece, her peygambere insan ve cin şeytanlarını düşman yaptık. Bunlar birbirini aldatmak için süslü sözlerle vesvese verirler. Rabbin dileseydi onu yapamazlardı. Artık onları iftiraları ile başbaşa bırak.'),
(902, 6, 113, 'Bir de ahirete iman etmeyenlerin kalbleri, o yaldızlı söze kansın, ondan hoşlansın ve işledikleri suçları işlemeye devam etsinler diye böyle yaparlar.'),
(903, 6, 114, 'Allah, size Kitab\'ı (Kur\'ân\'ı) açıklanmış olarak indirdiği halde, ondan başka bir hakem mi arayayım? Kendilerine kitap verdiklerimiz, o Kur\'ân\'ın, gerçekten Rabbin katından hak olarak indirilmiş olduğunu bilirler. O halde sakın şüphe edenlerden olma.'),
(904, 6, 115, 'Rabbinin sözü hem doğrulukça, hem de adaletçe tamamlanmıştır. O\'nun sözlerini değiştirebilecek hiç kimse yoktur. O, işitendir, bilendir.'),
(905, 6, 116, 'Eğer yeryüzündekilerin çoğunluğuna uyarsan seni Allah yolundan saptırırlar. Çünkü onlar sadece "zann"a uyarlar ve saçmalarlar.'),
(906, 6, 117, 'Şüphesiz ki Rabbin, yolundan kimlerin saptığını çok iyi bilir. O, doğru yolda olanları da çok iyi bilir.'),
(907, 6, 118, 'Eğer Allah\'ın âyetlerine iman ediyorsanız, Allah\'ın adı anılarak kesilen hayvanlardan yiyin.'),
(908, 6, 119, 'Size ne oluyor da Allah\'ın adı anılarak kesilenlerden yemiyorsunuz? Halbuki O size, mecbur kalmanızın dışında haram olan şeyleri genişce açıklamıştır. Doğrusu birçokları bilmeden keyiflerine uyarak insanları doğru yoldan saptırıyorlar. Muhakkak ki, Rabbin, sınırı aşanları çok iyi bilir.'),
(909, 6, 120, 'Günahın açığını da, gizlisini de bırakın! Günah kazananlar, yaptıklarının cezasını çekecekler.'),
(910, 6, 121, 'Üzerlerine Allah\'ın ismi anılmamış olanlardan yemeyin, çünkü onu yemek yoldan çıkmaktır. Şeytanlar, dostlarına, sizinle mücadele etmeleri için telkinde bulunurlar. Eğer onlara uyarsanız, muhakkak ki, Allah\'a ortak koşanlardan olursunuz.'),
(911, 6, 122, 'Ölü iken hidayetle dirilttiğimiz, kendisine insanlar arasında yürüyecek bir nûr verdiğimiz kimse, karanlıklar içinde kalıp, ondan çıkamayan kimse gibi olur mu? Fakat kâfirlere, yaptıkları, böyle süslü gösterilir.'),
(912, 6, 123, 'Böylece, her kentte ileri gelenleri, oranın suçluları yaptık ki, orada hileler çevirsinler. Halbuki bunlar, kötülüğü başkasına değil kendilerine yapıyorlar da farkına varmıyorlar.'),
(913, 6, 124, 'Onlara bir âyet geldiği zaman: "Allah\'ın peygamberlerine verilenin aynısı bize de verilmedikçe iman etmeyiz" derler. Allah peygamberliğini kime vereceğini daha iyi bilir. Suçlu olanlara, yaptıkları hilelerinden dolayı Allah katından bir zillet ve şiddetli bir azap erişecektir.'),
(914, 6, 125, 'Allah kimi hidayete erdirmek isterse, onun gönlünü İslâm\'a açar. Kimi de saptırmak isterse, sanki göğe yükseliyormuş gibi, göğsünü dar ve sıkıntılı yapar. Allah, inanmayanları işte böyle pislik içinde bırakır.'),
(915, 6, 126, 'İşte Rabbinin doğru yolu budur. Şüphesiz biz, hatırlayıp ibret alan bir kavim için âyetleri geniş bir şekilde açıkladık.'),
(916, 6, 127, 'Onlar için Rableri katında selâmet yurdu vardır. Yaptıkları iyi amellerden dolayı, Allah onların dostudur.'),
(917, 6, 128, '(Allah), onların hepsini topladığı gün, cinlere: "Ey cin topluluğu! İnsanların çoğunu yoldan çıkardınız" der. İnsanlardan cinlerin dostu olanlar da şöyle derler: "Rabbimiz! Biz birbirimizden faydalandık. Nihayet bize tayin ettiğin vademize ulaştık". Allah da: "Sizin durağınız cehennemdir. Orada, Allah\'ın dilemesi müstesna, ebedi olarak kalacaksınız" der. Şüphesiz Rabbin hikmet sahibidir, her şeyi bilendir.'),
(918, 6, 129, 'İşte biz böylece, kazandıkları günahlardan dolayı zalimlerin bir kısmını, diğer bir kısmına dost yaparız.'),
(919, 6, 130, '(Allah) "Ey cin ve insan topluluğu! İçinizden size âyetlerimi anlatan ve bugününüze kavuşacağınız hususunda sizi uyaran peygamberler gelmedi mi?" deyince onlar: "Kendi aleyhimize şahidiz" derler. Dünya hayatı onları aldattı ve kendilerinin kâfir olduklarına şahitlik ettiler.'),
(920, 6, 131, 'Bu (şundan dolayıdır ki) Rabbin, halkı habersiz iken ülkeleri zulüm ile helak edici değildir.'),
(921, 6, 132, 'Her birinin yaptıklarına göre dereceleri vardır. Rabbin onların yaptıklarından habersiz değildir.'),
(922, 6, 133, 'Rabb\'ın, hiçbir şeye muhtaç değildir, merhamet sahibidir. Sizi, başka bir kavmin soyundan getirdiği gibi, dilerse, sizi de yok edip, sizden sonra yerinize dilediğini getirir.'),
(923, 6, 134, 'Size vaad edilenler muhakkak gelecektir, siz, onun önüne geçemezsiniz.'),
(924, 6, 135, 'De ki: "Ey kavmim! Gücünüz yettiğince yapacağınızı yapın, ben de yapıyorum. Yakında (dünya) yurdunun sonunun kimin olduğunu bileceksiniz. Muhakkak zalimler kurtuluşa eremezler".'),
(925, 6, 136, 'Allah\'ın yarattığı ekin ve hayvanlardan Allah\'a bir hisse ayırmakta ve kendilerince: "Bu, Allah\'a ait; şu da ortaklarımıza ait" demektedirler. Ortakları için olan hisse Allah\'a ulaşmamakta, fakat Allah\'a ayrılan hisse ortaklarına ulaşmaktadır. Verdikleri hüküm ne kötüdür.'),
(926, 6, 137, 'Yine ortakları, müşriklerden çoğuna evlatlarını öldürmeyi güzel gösterdi ki, hem kendilerini mahvetsinler, hem de dinlerini karıştırıp bozsunlar. Allah dileseydi bunu yapamazlardı. O halde onları, uydurduklarıyla baş başa bırak!'),
(927, 6, 138, 'Zanlarınca dediler ki: "Bunlar dokunulmaz hayvanlar ve ekinlerdir. Bunları bizim dilediğimizden başkası yiyemez. Bunlar da sırtına binilmesi yasaklanmış hayvanlar." Bir kısım hayvanları da üzerlerine Allah\'ın adını anmadan boğazlarlar. Bütün bunları Allah\'a iftira ederek yaparlar. Allah onları iftiralarıyla cezalandıracaktır.'),
(928, 6, 139, 'Dediler ki: "Bu hayvanların karınlarındakiler sadece erkeklerimize ait olup kadınlarımıza haramdır". Eğer ölü doğarsa o zaman hepsi onda ortaktır. Bu nitelemelerinden dolayı Allah onların cezasını verecektir. Çünkü O hikmet sahibidir, her şeyi bilendir.'),
(929, 6, 140, 'Bilgisizlik yüzünden beyinsizce çocuklarını öldürenler ve Allah\'ın kendilerine verdiği rızkı, Allah\'a iftira ederek haram kılanlar muhakkak ki, ziyana uğradılar. Bunlar, doğru yoldan sapmışlardır; hidayete erecek de değillerdir.'),
(930, 6, 141, 'Asmalı ve asmasız (üzüm) bahçeleri, hurmaları, ürünleri çeşit çeşit ekinleri, zeytinleri ve narları, birbirine benzer ve benzemez biçimde yaratan O\'dur. Her biri meyve verince meyvesinden yiyin, hasat günü de hakkını (zekat ve sadakasını) verin; amaisraf etmeyin, çünkü O, israf edenleri sevmez.'),
(931, 6, 142, 'Hayvanlardan da (çeşit çeşit yarattı). Kimi yük taşır, kiminin yününden döşek yapılır. Allah\'ın size verdiği rızıktan yiyin ve şeytanın adımlarına uymayın (peşinden gitmeyin); çünkü o, sizin için apaçık bir düşmandır.'),
(932, 6, 143, 'Sekiz çift: Koyundan iki, keçiden iki. De ki: "(Allah), iki erkeği mi haram kıldı yoksa iki dişiyi mi, ya da iki dişinin rahimlerinde bulunan yavruları mı? Eğer doğru iseniz bana ilimle haber verin."'),
(933, 6, 144, 'Ve deveden iki, sığırdan iki. De ki: (Allah), "İki erkeği mi haram kıldı, yoksa iki dişiyi mi, ya da iki dişinin rahimlerinde bulunan yavruları mı? Yoksa, Allah\'ın size böyle vasiyet ettiğine şahitler mi oldunuz? (O\'nun yanında mıydınız?). Böyle hiçbir bilgiye dayanmadan, insanları saptırmak için, Allah\'a karşı yalan uydurandan daha zalim kim olabilir? Şüphesiz Allah, o zalimler topluluğunu doğru yola iletmez"'),
(934, 6, 145, 'De ki: "Bana vahyolunanda, (bu haram dediklerinizi) yiyen kimse için haram edilmiş bir şey bulamıyorum. Ancak leş, veya akıtılmış kan, yahut domuz etiki bu gerçekten pistir yahut Allah\'tan başkası adına kesilmiş bir hayvan olursa, bunlar haramdır. Ama kim çaresiz kalırsa, (başkasının hakkına) tecavüz etmemek ve zaruret sınırını aşmamak üzere (bunlardan yiyebilir)" Çünkü Rabbin çok bağışlayandır, merhamet edendir.'),
(935, 6, 146, 'Yahudilere bütün tırnaklı hayvanları haram kıldık. Sırtlarında, yahut bağırsaklarında bulunan, ya da kemiğe karışan yağlar dışında, sığır ve koyunun da, yağlarını onlara haram ettik. Saldırganlıkları yüzünden onları böyle cezalandırdık. Biz elbette doğru söyleyenleriz.'),
(936, 6, 147, 'Eğer seni yalanladılarsa, de ki: "Rabbiniz geniş rahmet sahibidir. Bununla beraber O\'nun azabı da suçlu toplumdan geri çevrilmez."'),
(937, 6, 148, 'Allah\'a ortak koşanlar diyecekler ki: "Allah dileseydi ne biz ortak koşardık, ne de atalarımız ortak koşardı, hiçbir şeyi de haram kılmazdık." Onlardan önce yalanlayanlar da böyle söylemişlerdi de sonunda azabımızı tatmışlardı. De ki: "Yanınızda bize çıkarabileceğiniz bir bilgi mi var? Siz, sadece zanna uyuyorsunuz ve siz sadece saçmalıyorsunuz."'),
(938, 6, 149, 'De ki: "En kesin ve üstün delil, Allah\'ındır. Allah isteseydi, elbette hepinizi doğru yola iletirdi."'),
(939, 6, 150, 'De ki: "Haydi, Allah bunu yasak etti diye tanıklık edecek şahitlerinizi getirin.". Eğer onlar şahitlik ederlerse, sen onlarla beraber şahitlik etme. Âyetlerimi yalanlayanların ve ahirete inanmayanların keyiflerine uyma. Çünkü onlar Rablerine başkasını denk tutuyorlar.'),
(940, 6, 151, 'De ki: Rabbinizin size neleri haram kıldığını okuyayım: O\'na hiçbir şeyi ortak koşmayın, ana babaya iyilik edin, fakirlik korkusuyla çocuklarınızı öldürmeyin, sizin de onların da rızkını biz veriyoruz. Kötülüklerin açığına da, gizlisine de yaklaşmayın. Haksız yere Allah\'ın haram kıldığı cana kıymayın. Düşünesiniz diye Allah size bunları emretti.'),
(941, 6, 152, 'Yetimin malına yaklaşmayın; yalnız erginlik çağına erişinceye kadar (malına) en güzel biçimde (yaklaşabilir ve uygun şekilde harcayabilirsiniz). Ölçü ve tartıyı tam adaletle yapın. Biz kimseye gücünün yettiğinden fazlasını teklif etmeyiz. Söylediğiniz zaman da, yakınınız da olsa âdil olun ve Allah\'a verdiğiniz sözü tutun. Öğüt alıp düşünesiniz diye Allah bunları size emretmiştir.'),
(942, 6, 153, 'İşte benim doğru yolum budur; ona uyun. Sizi O\'nun yolundan ayıracak başka yollara uymayın. (Azabından) korunmanız için Allah size böyle tavsiye etmiştir.'),
(943, 6, 154, 'Sonra iyilik edenlere (nimetimizi) tamamlamak, her şeyi açıklamak ve doğru yola iletici ve rahmet olmak üzere Musa\'ya Kitab\'ı verdik ki, Rablerinin huzuruna varacaklarına inansınlar.'),
(944, 6, 155, 'İşte bu (Kur\'ân) da mübarek bir Kitap\'tır. Onu biz indirdik. Ona uyun ve Allah\'tan korkun ki, size rahmet edilsin.'),
(945, 6, 156, '(Onu size indirdik ki:) "Kitap, sadece bizden önceki iki topluluğa (yahudi ve hıristiyanlara) indirildi; biz ise, onların okumasından habersizdik (o kitapları okuyamıyor ve dillerini anlayamıyorduk)" demeyesiniz.'),
(946, 6, 157, 'Yahut: "Eğer bize kitap indirilseydi, biz onlardan daha çok doğru yolda olurduk", demeyesiniz. İşte size de Rabbinizden açık delil, hidayet ve rahmet geldi. Allah\'ın âyetlerini yalanlayıp, onlardan yüz çevirenden daha zalim kim olabilir? Âyetlerimizden yüz çevirenleri, yüz çevirmeleri sebebiyle azabın en kötüsüyle cezalandıracağız.'),
(947, 6, 158, '(İnanmak için) ille meleklerin gelmesini, yahut Rabbinin gelmesini, ya da Rabbinin bazı âyetlerinin gelmesini mi bekliyorlar? Ama Rabbinin (azab) işaretlerinin geldiği gün, daha önce iman etmemiş, yahut imanında bir hayır kazanmamış kimseye, artık inanması bir fayda sağlamaz. De ki: "Bekleyin; biz de beklemekteyiz."'),
(948, 6, 159, 'Dinlerini parça parça edip, grup grup olanlar var ya, senin onlarla hiçbir ilişkin yoktur. Onların işi Allah\'a kalmıştır, sonra (Allah) onlara yaptıklarını haber verecektir.'),
(949, 6, 160, 'Kim iyilik getirirse, ona o (getirdiği)nin on katı vardır. Kim kötülük getirirse, sadece onun dengiyle cezalandırılır; onlar haksızlığa uğratılmazlar.'),
(950, 6, 161, 'De ki: Rabbim, beni doğru yola iletti. Dosdoğru dine, Allah\'ı birleyen İbrahim\'in dinine. O, ortak koşanlardan değildi.'),
(951, 6, 162, 'De ki: Benim namazım, ibadetim, hayatım ve ölümüm hep âlemlerin Rabbi Allah içindir.'),
(952, 6, 163, 'Onun ortağı yoktur. Bana böyle emrolundu ve ben müslümanların ilkiyim.'),
(953, 6, 164, 'De ki: Allah herşeyin Rabbi iken, ben O\'ndan başka Rab mi arayayım? Herkesin kazandığı yalnız kendisine aittir. Kendi (günah) yükünü taşıyan hiç kimse, bir başkasının (günah) yükünü taşımaz. Sonra dönüşünüz Rabbinizedir. O, ayrılığa düştüğünüz gerçeği size haber verecektir.'),
(954, 6, 165, 'Sizi yeryüzünün halifeleri yapan, size verdiği şeylerde, sizi denemek için, kiminizi kiminizden derecelerle üstün kılan O\'dur. Şüphesiz Rabbin, cezası çabuk olandır ve O, bağışlayan, esirgeyendir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'Elif, lâm, mîm, sâd.'),
(956, 7, 2, '(Bu,) sana indirilen bir Kitab\'tır. Onunla (insanları) uyarman ve inananlara öğüt (vermen) hususunda göğsünde bir sıkıntı olmasın.'),
(957, 7, 3, '(Ey insanlar) Rabbinizden, size indirilene uyun ve O\'ndan başka dostlara uymayın. Ne kadar da az öğüt alıyorsunuz!'),
(958, 7, 4, 'Nice kentler helak ettik. Gece yatarlarken, yahut gündüz uyurlarken, azabımız onlara geliverdi.'),
(959, 7, 5, 'Azabımız onlara geldiğinde "Biz gerçekten zalimlermişiz!" demelerinden başka yalvarışları kalmadı.'),
(960, 7, 6, 'Kendilerine elçi gönderilmiş olanlara da soracağız, gönderilen elçilere de soracağız.'),
(961, 7, 7, 'Ve elbette onlara, olanbiten herşeyi bir bilgi ile anlatacağız; çünkü biz onlardan uzak değiliz.'),
(962, 7, 8, 'O gün (amelleri tartacak) terazi haktır. Kimin (sevap) tartıları ağır gelirse, işte onlar kurtulanlardır.'),
(963, 7, 9, 'Kimin (sevap) tartıları hafif gelirse, işte onlar da âyetlerimize haksızlık etmelerinden ötürü kendilerini ziyana sokanlardır.'),
(964, 7, 10, 'Doğrusu Biz sizi yeryüzünde, yerleştirdik, orada size geçimlikler verdik; ne kadar da az şükrediyorsunuz!'),
(965, 7, 11, 'Sizi yarattık, sonra size biçim verdik, sonra da meleklere: "Âdem\'e secde edin" dedik; hepsi secde ettiler, yalnız İblis, secde edenlerden olmadı.'),
(966, 7, 12, '(Allah) buyurdu: "Sana emrettiğim zaman, seni secde etmekten alıkoyan nedir?" (İblis): "Ben, dedi, ondan hayırlıyım; beni ateşten yarattın, onu çamurdan yarattın."'),
(967, 7, 13, '(Allah) buyurdu: "Öyleyse oradan in, orada büyüklük taslamak senin haddin değildir. Çık, çünkü sen aşağılıklardansın."'),
(968, 7, 14, '(İblis) dedi: (Bari) bana (insanların) tekrar diriltilecekleri güne kadar süre ver."'),
(969, 7, 15, '(Allah) buyurdu: "Haydi sen süre verilmişlerdensin."'),
(970, 7, 16, '"Öyleyse, dedi, beni azdırmana karşılık, and içerim ki, ben de onlar(ı saptırmak) için senin doğru yolunun üstüne oturacağım."'),
(971, 7, 17, '"Sonra (onların) önlerinden arkalarından, sağlarından sollarından onlara sokulacağım ve sen, çoklarını şükredenlerden, bulmayacaksın."'),
(972, 7, 18, '(Allah) buyurdu: "Haydi, sen, yerilmiş ve kovulmuş olarak oradan çık. And olsun ki, onlardan sana kim uyarsa, (bilin ki) sizin hepinizden (derleyip) cehennemi dolduracağım."'),
(973, 7, 19, '(Sonra Allah, Âdem\'e hitab etti): "Ey Âdem! Sen ve eşin cennette durun, dilediğiniz yerden yeyin; fakat şu ağaca yaklaşmayın, yoksa zalimlerden olursunuz."'),
(974, 7, 20, 'Derken onların, kendilerinden gizli kalan çirkin yerlerini kendilerine göstermek için onlara fısıldadı: "Rabbiniz, başka bir sebepten dolayı değil, sırf ikiniz de birer melek ya da ebedî kalıcılardan olursunuz diye sizi şu ağaçtan men etti." dedi.'),
(975, 7, 21, 'Ve onlara: "Elbette ben size öğüt verenlerdenim." diye de yemin etti.'),
(976, 7, 22, 'Böylece onları aldatarak aşağı sarkıttı (önceki mevkilerinden indirdi). Ağacı(n meyvesini) tadınca, çirkin yerleri kendilerine göründü ve cennet yapraklarını üst üste yamayıp üzerlerini örtmeğe başladılar. Rableri onlara seslendi: "Ben sizi o ağaçtan men etmedim mi ve şeytan size apaçık düşmandır, demedim mi?"'),
(977, 7, 23, 'Dediler ki: "Ey Rabbimiz! Biz kendimize zulmettik, eğer bizi bağışlamaz ve bize rahmetinle muamele etmezsen muhakkak ziyana uğrayacaklardan oluruz!"'),
(978, 7, 24, '(Allah) buyurdu: "Birbirinize düşman olarak inin, sizin yeryüzünde bir süreye kadar kalıp geçinmeniz gerekmektedir."'),
(979, 7, 25, '"Orada yaşayacaksınız, orada öleceksiniz ve yine oradan (dirilip) çıkarılacaksınız!" dedi.'),
(980, 7, 26, 'Ey Âdemoğulları, size çirkin yerlerinizi örtecek giysi, süslenecek elbise indirdik. Hayırlı olan, takva elbisesidir. İşte bu(nlar), Allah\'ın âyetlerindendir, belki düşünüp öğüt alırlar.'),
(981, 7, 27, 'Ey Âdemoğulları. Şeytan, ana babanızı, çirkin yerlerini onlara göstermek için elbiselerini soyarak cennetten çıkardığı gibi, sizi de (şaşırtıp) bir belaya düşürmesin! Çünkü o ve kabilesi, sizin onları göremeyeceğiniz yerden sizi görürler. Biz, şeytanları, inanmayanların dostu yaptık.'),
(982, 7, 28, 'Onlar bir kötülük yaptıkları zaman: "Babalarımızı bu yolda bulduk, bunu bize Allah emretti." derler. De ki: "Allah kötülüğü emretmez. Allah\'a karşı bilmediğiniz şeyleri mi söylüyorsunuz?"'),
(983, 7, 29, 'De ki: "Rabbim bana adaleti emretti. Her mescidde yüzünüzü O\'na doğrultun ve dini yalnız kendisine has kılarak O\'na yalvarın. İlkin sizi yarattığı gibi yine O\'na döneceksiniz."'),
(984, 7, 30, '(O) bir topluluğu doğru yola iletti, bir topluluğa da sapıklık hak oldu. Çünkü onlar, şeytanları Allah\'tan başka dostlar tuttular ve kendilerinin de doğru yolda olduklarını sanıyorlar.'),
(985, 7, 31, 'Ey Âdemoğulları! Her mescide gidişinizde güzel giysilerinizi giyin ve yiyin, için, fakat israf etmeyin, Çünkü Allah israf edenleri sevmez.'),
(986, 7, 32, 'De ki: "Allah\'ın kulları için çıkardığı zinetleri ve tertemiz rızıkları kim haram kılmış?" De ki: "Bunlar, bu dünya hayatında inananlar içindir, kıyamet gününde de yalnız onlara mahsustur". İşte böylece biz âyetleri bilen bir topluluğa uzun uzun açıklıyoruz.'),
(987, 7, 33, 'De ki: "Rabbim, sadece fuhşiyatı, onun açık ve gizli olanını, günahları, haksız yere isyanı, haklarında hiç bir delil indirmediği şeyleri Allah\'a ortak koşmanızı ve Allah hakkında bilmediğiniz şeyleri söylemenizi yasaklamıştır".'),
(988, 7, 34, 'Her ümmetin bir eceli vardır. O ecel geldiğinde, ne bir ân erteleyebilirler, ne de öne alabilirler.'),
(989, 7, 35, 'Ey Âdemoğulları! Size içinizden peygamberler gelip âyetlerimi anlattıklarında, kim Allah\'tan korkar ve kendini düzeltirse, işte onlar için korku yoktur. Onlar üzülmeyeceklerdir de.'),
(990, 7, 36, 'Kim de âyetlerimizi yalanlar ve onlara karşı büyüklük taslarsa, işte onlar cehennemliktirler ve orada ebedî olarak kalacaklardır.'),
(991, 7, 37, 'Allah\'a karşı yalan uyduran yahut âyetlerini yalanlayandan daha zalim kim olabilir? Onlara Kitap\'tan nasipleri erişir. Canlarını alacak elçilerimiz gelince onlara: "Allah\'tan başka taptıklarınız nerede?" derler. Onlar: "O taptıklarımız bizden sapıp ayrıldılar." derler. Böylece kendilerinin kâfir olduklarına bizzat şahitlik ederler.'),
(992, 7, 38, 'Allah onlara: "Sizden önce geçmiş cin ve insan topluluklarıyla beraber cehennem ateşine girin!" der. Cehenneme giren her ümmet kendi din kardeşine lanet eder. Nihayet hepsi oraya toplandığında, sonrakiler öncekiler hakkında derler ki: "Rabbimiz! İşte şunlar bizi doğru yoldan saptırdı. Onlara cehennem ateşinden kat kat azab ver". Allah der ki: "Herkesin azabı kat kattır, fakat siz bilemezsiniz".'),
(993, 7, 39, 'Öncekiler de sonrakilere derler ki: "Sizin bizden bir üstünlüğünüz yoktur. O halde yaptıklarınızdan dolayı azabı tadın".'),
(994, 7, 40, 'Bizim âyetlerimizi yalanlayan ve onlara inanmaya tenezzül etmeyenler var ya, işte onlara göğün kapıları açılmayacak ve deve (veya halat) iğne deliğinden geçinceye kadar onlar cennete giremeyeceklerdir. İşte suçluları böyle cezalandırırız.'),
(995, 7, 41, 'Onlara cehennemde ateşten bir yatak, üstlerine de (ateşten) örtüler vardır. Biz zalimleri işte böyle cezalandırırız.'),
(996, 7, 42, 'İman edenler ve iyi amellerde bulunanlarki biz hiç kimseye gücünün üstünde bir şey teklif etmeyiz işte onlar cennet ehlidir ve orada ebedî olarak kalacaklardır.'),
(997, 7, 43, 'Orada kalblerinde bulunan kini çıkarıp atarız. Onların altlarından ırmaklar akar. "Bizi buna erdiren Allah\'a hamdolsun. Eğer Allah bizi doğru yola sevk etmeseydi biz doğru yola erişemezdik. Şüphesiz Rabbimizin peygamberleri bize gerçeği getirmişler." derler. Onlara şöyle seslenilir: "İşte size cennet! Yaptıklarınıza karşılık buna varis oldunuz".'),
(998, 7, 44, 'Cennet ehli, cehennem ehline: "Rabbimizin bize vaad ettiğini gerçek bulduk. Siz de Rabbinizin size vaad ettiğini gerçek buldunuz mu?" diye seslenirler. Onlar da "evet" derler. Bunun üzerine aralarında bir çağırıcı şöyle seslenir: "Allah\'ın laneti zalimler üzerine olsun!'),
(999, 7, 45, 'Onlar, Allah\'ın yolundan men ederler ve onu eğriltmek isterler, ahireti de inkâr ederlerdi".'),
(1000, 7, 46, 'Cennetliklerle cehennemlikler arasında bir perde vardır. A\'raf üzerinde de, her iki taraftakileri simalarından tanıyan kişiler vardır. Bunlar cennetliklere: "selâm olsun size" diye seslenirler. Bunlar henüz cennete girmemiş, fakat girmeyi arzu eden kimselerdir.'),
(1001, 7, 47, 'Gözleri cehennemlikler tarafına çevrilince de: "Rabbimiz! Bizi zalim toplulukla beraber eyleme!" derler.'),
(1002, 7, 48, 'A\'raftakiler yüzlerinden tanıdıkları kişilere seslenerek şöyle derler: "Ne topluluğunuz, ne de büyüklük taslamanız, size hiç bir yarar sağlamadı".'),
(1003, 7, 49, '"Allah onları hiç bir rahmete erdirmiyecek, diye yemin ettiğiniz kimseler bunlar mıydı?" (Cennetliklere dönerek): "Girin cennete, artık size ne korku vardır, ne de siz üzüleceksiniz" derler.'),
(1004, 7, 50, 'Cehennemdekiler, cennettekilere: "Bize biraz su akıtın veya Allah\'ın size verdiği rızıktan bize de verin." diye seslenirler. Cennettekiler de: "Allah, bunların ikisini de kâfirlere haram kıldı." derler.'),
(1005, 7, 51, 'Onlar ki, dinlerini bir eğlence ve oyun yerine koydular ve dünya hayatı kendilerini aldattı. Onlar, bugüne kavuşacaklarını nasıl unuttular ve âyetlerimizi nasıl inkâr ettilerse, biz de bugün onları öyle unuturuz.'),
(1006, 7, 52, 'Gerçekten onlara, bilgiye göre açıkladığımız, inanan bir toplum için yol gösterici ve rahmet olan bir Kitap getirdik.'),
(1007, 7, 53, 'İlle onun te\'vilini mi gözetiyorlar? Onun te\'vili geldiği (verdiği haberler ortaya çıktığı) gün, önceden onu unutmuş olanlar derler ki: "Doğrusu Rabbimizin elçileri gerçeği getirmiş. Şimdi bizim şefaatçilerimiz var mı ki bize şefaat etsinler, yahut tekrar geri döndürülmemiz mümkün mü ki eski yaptıklarımızdan başkasını yapalım?" Onlar, kendilerini zarara soktular ve uydurdukları şeyler kendilerinden saptı, kaybolup gitti.'),
(1008, 7, 54, 'Şüphesiz Rabbiniz Allah, gökleri ve yeri altı günde yarattı, sonra Arş üzerine hükümran oldu. O, geceyi durmadan onu kovalayan gündüze bürüyüp örter; güneş, ay ve yıldızlar emrine âmâdedir. İyi biliniz ki yaratma ve emir O\'nundur. Âlemlerin Rabbi olan Allah ne yücedir.'),
(1009, 7, 55, 'Rabbinize yalvara yalvara ve gizlice dua edin. Çünkü O, haddi aşanları sevmez.'),
(1010, 7, 56, 'Düzeltildikten sonra yeryüzünde bozgunculuk yapmayın. O\'na, korkarak ve rahmetini umarak dua edin. Muhakkak ki Allah\'ın rahmeti, iyilik edenlere yakındır.'),
(1011, 7, 57, 'Rahmetinin önünde müjdeci olarak rüzgarları gönderen O\'dur. O rüzgarlar, yağmur yüklü bulutları yüklenince, onu kurak bir memlekete gönderir, sonra onunla yağmur yağdırır ve onunla her çeşit ürünü yetiştiririz. İşte Biz, ölüleri de böyle diriltiriz. Gerekir ki düşünür, ibret alırsınız.'),
(1012, 7, 58, 'Güzel memleketin bitkisi, Rabbinin izniyle çıkar; kötü olandan ise yararsız bitkiden başka bir şey çıkmaz. İşte biz, şükreden bir toplum için âyetleri böyle açıklarız.'),
(1013, 7, 59, 'Andolsun ki Nûh\'u elçi olarak kavmine gönderdik de dedi ki: "Ey kavmim! Allah\'a kulluk edin sizin O\'ndan başka bir ilâhınız yoktur. Doğrusu ben, üstünüze gelecek büyük bir günün azabından korkuyorum."'),
(1014, 7, 60, 'Kavminden ileri gelenler dediler ki: "Biz seni apaçık bir sapıklık içinde görüyoruz".'),
(1015, 7, 61, '(Nûh) dedi ki: "Ey kavmim! Bende herhangi bir sapıklık yok, ben âlemlerin Rabbi tarafından gönderilmiş bir elçiyim."'),
(1016, 7, 62, '"Size Rabbimin gönderdiği gerçekleri duyuruyorum, size öğüt veriyorum ve Allah tarafından, sizin bilmediğiniz şeyleri biliyorum."'),
(1017, 7, 63, '(Allah\'ın azabından) sakınıp da rahmete nail olmanız için, içinizden sizi uyaracak bir adam vasıtasıyla size bir zikir(kitap) gelmesine şaştınız mı?"'),
(1018, 7, 64, 'O\'nu yalanladılar, biz de O\'nu ve O\'nunla beraber gemide bulunanları kurtardık, âyetlerimizi yalanlayanları boğduk! Çünkü onlar, kalb gözleri körleşmiş bir kavim idiler.'),
(1019, 7, 65, 'Âd (kavmin)e de kardeşleri Hûd\'u (gönderdik): "Ey kavmim! Allah\'a kulluk edin, sizin O\'ndan başka bir ilâhınız yoktur. (O\'na karşı gelmekten) sakınmaz mısınız?" dedi.'),
(1020, 7, 66, 'Kavminden ileri gelen kâfirler dediler ki: "Biz seni bir çılgınlık içinde görüyoruz, ve gerçekten seni yalancılardan sanıyoruz."'),
(1021, 7, 67, '(Hûd), "Ey kavmim! Bende çılgınlık yok, ben âlemlerin Rabbi tarafından gönderilmiş bir elçiyim." dedi.'),
(1022, 7, 68, '"Size Rabbimin gönderdiği gerçekleri tebliğ ediyorum ve ben sizin için güvenilir bir öğütçüyüm."'),
(1023, 7, 69, '"Sizi uyarması için içinizden bir adam aracılığı ile, size bir zikir gelmesine şaştınız mı? Düşünün ki (Allah) sizi, Nûh kavminden sonra, onların yerine hâkimler yaptı ve yaratılışta sizi onlardan üstün kıldı. Allah\'ın nimetlerini hatırlayın ki, kurtuluşa eresiniz."'),
(1024, 7, 70, 'Dediler ki: "Ya, demek sen tek Allah\'a kulluk edelim ve atalarımızın taptıklarını bırakalım diye mi (bize) geldin? Eğer doğrulardan isen bizi tehdit ettiğin (o azabı) bize getir!"'),
(1025, 7, 71, '(Hûd) dedi ki: "Artık size Rabbinizden bir azap ve bir hışım inmiştir. Haklarında Allah\'ın hiç bir delil indirmediği, sadece sizin ve atalarınızın taktığı kuru isimler hususunda benimle tartışıyor musunuz? Bekleyin öyleyse, şüphesiz ben de sizinle beraber bekleyenlerdenim!'),
(1026, 7, 72, 'Onu ve onunla beraber olanları rahmetimizle kurtardık ve âyetlerimizi yalanlayıp da iman etmeyenlerin kökünü kestik.'),
(1027, 7, 73, 'Semûd kavmine de kardeşleri Sâlih\'i (gönderdik): "Ey kavmim dedi, Allah\'a kulluk edin, sizin O\'ndan başka bir ilâhınız yoktur. Size Rabbinizden açık bir delil geldi. İşte şu, Allah\'ın devesi, size bir mucizedir; bırakın onu Allah\'ın yeryüzünde yesin (içsin), sakın ona bir kötülük etmeyin, yoksa sizi acı bir azap yakalar."'),
(1028, 7, 74, 'Düşünün ki (Allah) Âd\'dan sonra sizi hükümdarlar kıldı. Ve yer yüzünde sizi yerleştirdi: O\'nun düzlüklerinde saraylar yapıyorsunuz, dağlarında evler yontuyorsunuz. Artık Allah\'ın nimetlerini hatırlayın da yeryüzünde fesatçılar olarak karışıklık çıkarmayın.'),
(1029, 7, 75, 'Kavminden büyüklük taslayan ileri gelenler, içlerinden zayıf görünen müminlere: "Siz, dediler, Sâlih\'in, gerçekten Rabbi tarafından gönderildiğini biliyor musunuz?" (Onlar da): "(Evet), doğrusu biz onunla gönderilene inananlarız!" dediler.'),
(1030, 7, 76, 'Büyüklük taslayanlar: "Biz, sizin inandığınızı inkâr edenleriz!" dediler.'),
(1031, 7, 77, 'Derken dişi deveyi boğazladılar ve Rablerinin buyruğundan dışarı çıktılar; "Ey Sâlih, eğer hakikaten elçilerdensen, bizi tehdit ettiğin (o azabı) bize getir! "dediler.'),
(1032, 7, 78, 'Bunun üzerine hemen onları, o sarsıntı yakaladı, yurtlarında diz üstü çökekaldılar.'),
(1033, 7, 79, 'Sâlih de o zaman onlardan yüz çevirdi ve şöyle dedi: "Ey kavmim! And olsun ki ben size Rabbimin elçiliğini tebliğ ettim ve size öğüt verdim, fakat siz öğüt verenleri sevmiyorsunuz."'),
(1034, 7, 80, 'Lût\'u da (peygamber olarak) gönderdik. Kavmine dedi ki: "Sizden önce âlemlerden hiç birinin yapmadığı fuhuşu mu yapıyor sunuz?'),
(1035, 7, 81, 'Çünkü siz kadınları bırakıp da şehvetle erkeklere gidiyorsunuz. Belki de siz haddi aşan bir kavimsiniz.'),
(1036, 7, 82, 'Kavminin cevabı: "Onları (Lût\'u ve taraftarlarını) kentinizden çıkarın, çünkü onlar, fazla temizlenen insanlarmış! "demelerinden başka bir şey olmadı.'),
(1037, 7, 83, 'Biz de onu ve ailesini kurtardık, yalnız karısı(nı kurtarmadık) çünkü o, geride kalanlardan oldu.'),
(1038, 7, 84, 'Ve üzerlerine bir (azab) yağmuru yağdırdık. Bak ki günahkârların sonu nasıl oldu!'),
(1039, 7, 85, 'Medyen\'e de kardeşleri Şuayb\'ı (gönderdik): "Ey kavmim, dedi, Allah\'a kulluk edin, sizin O\'ndan başka bir ilâhınız yoktur. Size Rabbinizden açık bir delil geldi: Ölçüyü ve tartıyı tam yapın, insanların eşyalarını eksik vermeyin, düzeltildikten sonra yeryüzünde bozgunculuk yapmayın; eğer inanan (insan)lar iseniz, böylesi sizin için daha iyidir!"'),
(1040, 7, 86, 'Tehdit ederek, inananları Allah yolundan alıkoyarak ve o yolun eğriliğini arayarak öyle her yolun başında oturmayın. Düşünün ki siz az idiniz de O sizi çoğalttı. Bakın ki bozguncuların sonu nasıl olmuştur.'),
(1041, 7, 87, 'Eğer içinizden bir grup benimle gönderilene inanır, bir grup da inanmazsa, Allah aramızda hükmedinceye kadar sabredin. O, hüküm verenlerin en hayırlısıdır.'),
(1042, 7, 88, 'Kavminden ileri gelen kibirliler dediler ki: "Ey Şu\'ayb! Ya mutlaka seni ve seninle beraber inananları kentimizden çıkarırız, ya da dinimize dönersiniz!" Dedi ki; "İstemesek de mi (bizi yurdumuzdan çıkaracak veya dinimizden döndüreceksiniz?)"'),
(1043, 7, 89, '(Andolsun ki), Allah bizi ondan (kâfirlikten) kurtardıktan sonra tekrar sizin dininize dönersek, Allah\'a karşı iftira etmiş oluruz. Rabbimiz Allah\'ın dilemesi hali müstesna geri dönmemiz bizim için olacak şey değildir. Rabbimizin ilmi her şeyi kuşatmıştır. Biz sadece Allah\'a dayanırız. Ey Rabbimiz! Bizimle kavmimiz arasında adaletle hükmet. Çünkü sen hükmedenlerin en hayırlısısın.'),
(1044, 7, 90, 'Kavminden ileri gelen kâfirler dediler ki: "Eğer Şu\'ayb\'a uyarsanız o takdirde siz mutlaka ziyana uğrarsınız."'),
(1045, 7, 91, 'Derken o (müthiş) sarsıntı onları yakalayıverdi, yurtlarında diz üstü çökekaldılar.'),
(1046, 7, 92, 'Şu\'ayb\'ı yalanlayanlar, sanki yurtlarında hiç şenlik tutmamış gibi oldular. Şu\'ayb\'ı yalanlayanlar var ya işte ziyana uğrayanlar, onlar oldular.'),
(1047, 7, 93, '(Şu\'ayb) onlardan öteye döndü de: "Ey kavmim! dedi, ben size Rabbimin gönderdiği gerçekleri duyurdum ve size öğüt verdim, artık kâfir bir kavme nasıl acırım?"'),
(1048, 7, 94, 'Biz hangi ülkeye bir peygamber gönderdiysek, onun halkınıyalvarıp yakarsınlar diye mutlaka yoksulluk ve darlıkla sıkmışızdır.'),
(1049, 7, 95, 'Sonra kötülüğü değiştirip yerine iyilik (bolluk) getirdik, nihayet çoğaldılar ve: "Atalarımıza da böyle darlık ve sevinç dokunmuştu." dediler ve hemen onları, hiç farkında olmadıkları bir sırada ansızın yakaladık.'),
(1050, 7, 96, '(O) ülkelerin halkı inanıp (Allah\'ın azabından) korunsalardı, elbette üzerlerine gökten ve yerden bolluklar açardık; fakat yalanladılar, biz de onları kazandıklarıyla yakaladık.'),
(1051, 7, 97, 'Acaba o ülkelerin halkı, geceleyin uyurlarken kendilerine azabımızın gelmeyeceğinden emin mi idiler?'),
(1052, 7, 98, 'Yoksa o ülkelerin halkı, kuşluk vakti eğlenirlerken onlara azabımızın gelmeyeceğinden emin mi idiler?'),
(1053, 7, 99, 'Allah\'ın tuzağından (kurtulacaklarına) emin mi oldular? Ziyana uğrayan topluluktan başkası, Allah\'ın tuzağından emin olmaz.'),
(1054, 7, 100, 'Önceki sahiplerinden sonra yeryüzüne vâris olanlara hâlâ şu gerçek belli olmadı mı ki: Eğer biz dileseydik onları da günahlarından dolayı musibetlere uğratırdık! Biz onların kalplerini mühürleriz de onlar (gerçekleri) işitmezler.'),
(1055, 7, 101, 'İşte o ülkeler ki, sana onların haberlerinden bir kısmını anlatıyoruz Andolsun ki, peygamberleri onlara apaçık deliller (mucizeler) getirmişlerdi. Fakat önceden yalanladıkları gerçeklere iman edecek değillerdi. İşte o kâfirlerin kalplerini Allah böyle mühürler.'),
(1056, 7, 102, 'Onların çoğunda, sözde durma (diye bir şey) bulamadık. Gerçek şu ki, onların çoğunu yoldan çıkmış bulduk.'),
(1057, 7, 103, 'Sonra onların arkasından Musa\'yı mucizelerimizle Firavun\'a ve topluluğuna gönderdik. Tuttular o mucizeleri inkâr ettiler. Ettiler de bak, o bozguncuların âkıbetleri nasıl oldu!'),
(1058, 7, 104, 'Musa: "Ey Firavun! Bil ki ben âlemlerin Rabbi tarafından gönderilmiş bir peygamberim." dedi.'),
(1059, 7, 105, 'Allah\'a karşı ilk görevim, hak olandan başka bir şey söylemememdir. Gerçekten ben size Rabbinizden bir mucize getirdim, artık İsrailoğullarını benimle gönder.'),
(1060, 7, 106, 'Firavun: "Eğer bir mucize getirdiysen ve eğer doğru söyleyenlerden isen onu göster" dedi.'),
(1061, 7, 107, 'Bunun üzerine Musa, asâsını yere bırakıverdi, o da birdenbire kocaman bir ejderha kesiliverdi.'),
(1062, 7, 108, 'Ve Musa elini koynundan çıkarıverdi, eli bembeyaz olmuş, bakanların gözünü kamaştırıyordu.'),
(1063, 7, 109, 'Firavun\'un kavminden ileri gelenler, "Muhakkak bu çok bilgili bir sihirbazdır." dediler.'),
(1064, 7, 110, 'O, sizi yurdunuzdan çıkarmak istiyor. (Firavun): "O halde siz ne diyorsunuz?" dedi.'),
(1065, 7, 111, 'Onlar da "onu ve kardeşini beklet, şehirlere de toplayıcılar gönder." dediler.'),
(1066, 7, 112, '"Bütün bilgiç sihirbazları sana getirsinler."'),
(1067, 7, 113, 'O sihirbazlar Firavun\'a geldiler: "Galip gelirsek bize muhakkak mükâfat var değil mi?" dediler.'),
(1068, 7, 114, '"Evet" dedi (Firavun), "Üstelik o zaman benim yakınlarımdan olacaksınız."'),
(1069, 7, 115, 'Sihirbazlar, Musa\'ya: "Ey Musa! Önce sen mi hünerini ortaya koyacaksın, yoksa biz mi?" dediler.'),
(1070, 7, 116, 'Musa, "Siz atın" dedi. Atacaklarını atınca herkesin gözünü büyülediler ve onları dehşete düşürdüler. Doğrusu büyük bir sihir gösterdiler.'),
(1071, 7, 117, 'Biz de Musa\'ya "Sen de asânı bırakıver." diye vahyettik. Birdenbire asâ, onların bütün uydurduklarını yakalayıp yutuverdi.'),
(1072, 7, 118, 'Artık hakikat ortaya çıkmış ve onların bütün yaptıkları boşa gitmişti.'),
(1073, 7, 119, 'Orada mağlup olmuş ve küçük düşmüşlerdi.'),
(1074, 7, 120, 'Sihirbazlar hep birden secdeye kapandılar.'),
(1075, 7, 121, '"Âlemlerin Rabbine iman ettik." dediler.'),
(1076, 7, 122, '"Musa\'nın ve Harun\'un Rabbine."'),
(1077, 7, 123, 'Firavun: "Ben size izin vermeden iman ettiniz ha!" dedi. "Şüphesiz bu bir hiledir, siz bunu şehirde kurmuşsunuz, yerli halkı oradan çıkarmak istiyorsunuz, sonra anlayacaksınız!"'),
(1078, 7, 124, '"Ellerinizi ve ayaklarınızı çaprazlama kestireceğim, sonra da bilin ki, sizi astıracağım."'),
(1079, 7, 125, 'Onlar da: "Şüphesiz o takdirde biz Rabbimize döneceğiz." dediler.'),
(1080, 7, 126, '"Senin bize kızman da sırf Rabbimizin âyetleri gelince onlara iman etmemizden dolayıdır. Ey Rabbimiz! Üzerimize sabır yağdır ve canımızı müslüman olarak al." derler.'),
(1081, 7, 127, 'Firavun kavminin ileri gelenleri dediler ki: "Seni ve ilâhlarını terketsinler de yeryüzünde fesat çıkarsınlar diye mi Musa\'yı ve kavmini serbest bırakacaksın?" Firavun da dedi ki: "Onların oğullarını öldüreceğiz, kızlarını sağ bırakacağız ve onlar üzerinde kahredici bir üstünlüğe sahibiz."'),
(1082, 7, 128, 'Musa, kavmine dedi ki: "Allah\'ın yardımını ve lütfunu isteyin ve sabır gösterin. Şüphesiz ki yeryüzü Allah\'ındır. Kullarından dilediğini ona mirasçı kılar. Sonunda kurtuluş müttakilerindir."'),
(1083, 7, 129, 'Kavmi de dediler ki: "Sen bize gelmeden önce de eziyet gördük, sen geldikten sonra da." Musa dedi ki: "Umulur ki, Rabbiniz düşmanlarınızı helak edip de sizi yeryüzünde halife kılacaktır ve sizin nasıl işler yaptığınıza bakacaktır."'),
(1084, 7, 130, 'Gerçekten biz, Firavun sülâlesini, senelerce kıtlık ve gelir noksanlığı içinde tutup kıvrandırdık ki, düşünüp ibret alsınlar.'),
(1085, 7, 131, 'Fakat kendilerine iyilik geldiği zaman, işte bu bizim hakkımızdır, dediler, başlarına bir kötülük gelince de, işte bu Musa ile yanındakilerin uğursuzluğu yüzünden, dediler. İyi bilin ki, onların uğursuzluğu Allah katındandır. Lâkin çoğu bunu bilmezler.'),
(1086, 7, 132, '"Ve sen büyülemek için her ne mucize getirirsen getir, biz sana inanacak değiliz," dediler.'),
(1087, 7, 133, 'Biz de kudretimizin ayrı ayrı alâmetleri olmak üzere başlarına tufan, çekirge, haşereler, kurbağalar ve kan gönderdik, yine inad edip direndiler ve çok mücrim (suçlu) bir kavim oldular.'),
(1088, 7, 134, 'Ne zaman ki, azap üzerlerine çöktü, dediler ki, "Ey Musa! Bizim için Rabbine dua et, sana olan ahdi hürmetine eğer bizden bu azabı kaldırır uzaklaştırırsan, yemin olsun ki, sana kesinlikle iman edeceğiz. Ve İsrailoğullarını seninle birlikte göndereceğiz."'),
(1089, 7, 135, 'Ne zaman ki, belli bir süreye kadar onlardan azabı kaldırdık, derhal yeminlerini bozdular.'),
(1090, 7, 136, 'Biz de, âyetlerimizi inkâr ettikleri ve onlara kulak vermedikleri için kendilerinden intikam aldık da hepsini denizde boğduk.'),
(1091, 7, 137, 'Ve o hırpalanıp ezilmekte olan kavmi de yeryüzünün, bereketle donattığımız doğusuna ve batısına mirasçı yaptık. Ve böylece Rabbinin, İsrailoğullarına olan o güzel vaadi, sabırları yüzünden gerçekleşti. Biz de Firavun ile kavminin yapageldikleri sanat eserlerini ve diktikleri binaları yerle bir ettik.'),
(1092, 7, 138, 'Ve İsrailoğullarının denizden geçmelerini sağladık? Derken bir kavme vardılar ki, onlar, kendilerine mahsus bir takım putlara tapıyorlardı. Dediler ki; Ey Musa! Onların tanrıları gibi, sen de bize bir tanrı yap! Musa da onlara dedi ki: Siz gerçekten cahillik eden bir kavimsiniz.'),
(1093, 7, 139, 'Çünkü o gördüklerinizin içinde bulundukları din, yok olmaya mahkûmdur ve bütün yaptıkları batıldır.'),
(1094, 7, 140, 'Sizi âlemlere üstün kılan Allah olduğu halde, ben size O\'ndan başka ilâh mı arayayım! dedi.'),
(1095, 7, 141, 'Hani sizi, Firavun sülâlesinin elinden kurtardığımız zaman, hatırlasanıza, size azabın kötüsünü yapıyorlardı; oğullarınızı öldürüyorlar, kızlarınızı sağ bırakıyorlardı. Bunda sizin için Rabbiniz tarafından büyük imtihan vardı.'),
(1096, 7, 142, 'Ve Musa\'ya otuz geceye vaat verdik ve süreye bir on gece daha ekledik ve böylece Rabbinin mikatı (tayin ettiği vakit) tam kırk gece oldu. Musa, kardeşi Harun\'a şöyle dedi: Kavmim içinde benim yerime geç, ıslaha çalış ve bozguncuların yolundan gitme!'),
(1097, 7, 143, 'Ne zaman ki, Musa, mikatımıza geldi, Rabbi ona kelâmıyla ihsanda bulundu. "Ey Rabbim, göster bana kendini de bakayım sana". dedi. Rabbi ona buyurdu ki; "Beni katiyyen göremezsin ve lâkin dağa bak, eğer o yerinde durabilirse, sonra sen de beni göreceksin". Daha sonra Rabbi dağa tecelli edince onu yerle bir ediverdi, Musa da baygın düştü. Ayılıp kendine gelince, "Sen sübhansın", "tevbe ettim, sana döndüm ve ben inananların ilkiyim," dedi.'),
(1098, 7, 144, 'Allah buyurdu: Ey Musa! Sana verdiğim peygamberlikle ve kelâmımla seni insanlar üzerine seçkin kıldım. Sana verdiğime sıkı sarıl ve şükredenlerden ol!'),
(1099, 7, 145, 'Ve onun için o levhalarda her şeyden yazdık, nasihat ve hükümlerin ayrıntılarına ait herşeyi (belirttik). Haydi bunlara sıkı sarıl, kavmine de emret, onlar da en güzeline sarılsınlar. Size yakında o fasıkların yurdunu göstereceğim.'),
(1100, 7, 146, 'Yeryüzünde haksız yere büyüklük taslayanları, âyetlerimizi anlamaktan uzak tutacağım. Onlar ki, bütün âyetlerimizi görseler de onlara iman etmezler. Doğru yolu görseler de o yolu tutup gitmezler. Eğer sapıklık yolunu görürlerse tutar onu izlerler. Çünkü onlar âyetlerimizi inkâr etmeyi âdet edinmişler ve onlardan hep gafil olagelmişlerdir.'),
(1101, 7, 147, 'Âyetlerimizi ve ahiretteki karşılaşmayı inkâr edenlerin amelleri hepten boşa gitmiştir. Çekecekleri ceza kendi yaptıklarından başkası mı olacaktır?'),
(1102, 7, 148, 'Musa\'nın arkasından kavmi, tutmuş süs takılarından böğüren bir buzağı heykeli edinmişlerdi. O buzağının kendilerine bir söz söylemediğini ve bir yol gösteremediğini görmemişler miydi? Fakat yine de onu tanrı edindiler ve zalimlerden oldular.'),
(1103, 7, 149, 'Ne zaman ki, ellerine kırağı düşürüldü (yaptıklarına pişman oldular), o zaman sapıtmış olduklarını gördüler. "Yemin olsun ki; eğer Rabbimiz bize merhamet etmez ve bizi bağışlamazsa, muhakkak biz kötü akıbete düşenlerden olacağız." dediler.'),
(1104, 7, 150, 'Musa, öfkeli ve üzüntülü olarak kavmine döndüğünde şöyle dedi: "Bana arkamdan ne kötü bir halef oldunuz! Rabbinizin emriyle dönüşümü beklemeden acele mi ettiniz?" Elindeki levhaları bıraktı ve kardeşi Harun\'u başından tutarak kendine doğru çekmeye başladı. Harun, "Ey anamın oğlu!" dedi, "inan ki, bu kavim beni güçsüz buldu, az daha beni öldürüyorlardı, sen de bana böyle yaparak düşmanları sevindirme ve beni bu zalim kavimle bir tutma."'),
(1105, 7, 151, 'Musa dedi ki: "Ey Rabbim! Beni ve kardeşimi bağışla! Bizi rahmetinin içine al. Sen merhametlilerin en merhametlisisin."'),
(1106, 7, 152, 'Şüphesiz o buzağıyı tanrı edinenlere Rablerinden bir gazap, dünya hayatında iken de bir zillet erişecektir. İşte biz, iftiracıları böyle cezalandırırız.'),
(1107, 7, 153, 'O kötü amelleri işleyip de sonra arkasından tevbe ve iman edenler için hiç şüphe yok ki, Rabbin bundan sonra yine de affedici ve merhamet edicidir.'),
(1108, 7, 154, 'Musa\'nın öfkesi geçince levhaları aldı. Onlardaki yazıda, ancak Rablerinden korkanlar için bir hidayet ve rahmet vardı.'),
(1109, 7, 155, 'Bir de Musa, mîkatımız için (tayin ettiğimiz vakitte tevbe için) kavminden yetmiş erkek seçti. Ne zaman ki, bunları o sarsıntı yakaladı, işte o zaman Musa: "Rabbim! dedi, dileseydin bunları da, beni de daha önce helâk ederdin. Şimdi bizi, içimizdekio beyinsizlerin yaptıkları yüzünden helâk mi edeceksin? O iş de senin imtihanından başka bir şey değildi. Sen bu imtihanla dilediğini sapıklıkta bırakır, dilediğini de hidayete erdirirsin. Bizim velimiz sensin. Artık bizi bağışla, merhamet et, sen bağışlayanların en hayırlısısın."'),
(1110, 7, 156, '"Ve bize hem bu dünyada bir iyilik yaz, hem de ahirette. Biz gerçekten de tevbe edip senin hidayetine döndük." Buyurdu ki, azabım var, onu dilediğime isabet ettiririm, rahmetim de vardır, o ise her şeyi kaplamış ve kuşatmıştır. Onu da özellikle korunanlara, zekatını verenlere ve âyetlerimize inananlara mahsus kılacağım.'),
(1111, 7, 157, 'Onlar ki, o ümmî peygambere uyarlar, yanlarındaki Tevrat ve İncil\'de yazılmış bulacakları o peygambere uyup, onun izinden giderler ki, o, onlara iyiyi emreder ve onları kötülüklerden alıkoyar, temiz ve hoş şeyleri kendilerine helâl kılar, murdar ve kötü şeyleri de üzerlerine haram kılar, sırtlarından ağır yükleri indirir, üzerlerindeki bağları ve zincirleri kırar atar, işte o vakit ona iman eden, ona kuvvetle saygı gösteren, ona yardımcı olan ve onun peygamberliği ile birlikte indirilen nuru izleyen kimseler var ya, işte asıl murada eren kurtulmuşlar onlardır.'),
(1112, 7, 158, 'De ki; ey insanlar! Ben sizin hepinize Allah\'ın resulüyüm. O Allah ki, göklerin ve yerin bütün mülkü O\'nundur. O\'ndan başka hiçbir ilâh yoktur. Öldüren de, dirilten de O\'dur. Bundan dolayı gelin, Allah\'a ve resulüne iman edin. Allah\'a ve Allah\'ın bütün kelâmlarına iman etmiş bulunan o ümmî peygambere, evet ona uyun ki, hidayete erebilesiniz.'),
(1113, 7, 159, 'Musa\'nın kavminden doğru yolu gösteren ve doğrulukla adalet yapan bir topluluk da vardı.'),
(1114, 7, 160, 'Biz onları oniki kabileye, o kadar ümmete ayırdık. Ve kavmi kendisinden su istediği zaman Musa\'ya, elindeki asâ ile taşa vur, diye vahyettik, vurunca hemen o taştan oniki pınar akmaya başladı. Halkın her biri su alacağı yeri iyice öğrendi. Bulutu da üzerlerine gönderdik, gölgeledik. Onlara kudret helvası ve bıldırcın indirdik. Size rızık olarak ihsan ettiğimiz nimetlerin temizinden yiyiniz, dedik. Onlar zulmü bize yapmadılar, lakin kendi kendilerine zulmediyorlardı.'),
(1115, 7, 161, 'Ve o vakit onlara denilmişti ki; Şu şehre yerleşin ve orada dilediğiniz şeylerden yiyin, "hitta" (günahlarımızı bağışla.) deyin ve secde ederek kapısından girin ki, suçlarınızı bağışlayalım. İyilere nimetlerimizi daha da arttıracağız.'),
(1116, 7, 162, 'İçlerinden bir kısım zalimler, sözü değiştirdiler, kendilerine söylenenden başka şekle soktular. Zulmü alışkanlık haline getirdikleri için biz de üzerlerine gökten azap yağdırdık.'),
(1117, 7, 163, 'Bir de onlara, o deniz kıyısındaki şehrin başına gelenleri sor. O sırada onlar cumartesi yasağına riayet etmiyorlardı. Cumartesi günü balıklar akın akın geliyorlardı, yasak olmadığı gün gelmiyorlardı. Yoldan çıkıp sapıklık yaptıkları için biz de onları işte böyle sınıyorduk.'),
(1118, 7, 164, 'İçlerinden bir topluluk, "Allah\'ın helâk edeceği, ya da çetin bir azapla cezalandıracağı bir kavme ne diye nasihat ediyorsunuz" dediği vakit, o uyarıda bulunanlar dediler ki; "Rabbiniz tarafından mazur görülmemiz için, bir de belki günahlardan sakınırlar diye."'),
(1119, 7, 165, 'Onlar yapılan bunca nasihatı unuttukları zaman, o kötülükten sakındıranları kurtardık, o zalimleri de fena hareketlerinden dolayı şiddetli bir azaba uğrattık.'),
(1120, 7, 166, 'Böylece onlar kibre kapılıp yasak kılınan şeylerden vazgeçmeyince, biz de onlara, hor ve zelil maymunlar olun, dedik.'),
(1121, 7, 167, 'O Vakit Rabbin işte şu ahdi ilan edip bildirdi ki: Kıyamet gününe kadar onlara en kötü muameleyi yapacak olan kimseleri başlarına gönderecektir. Muhakkak ki, Rabbin hızla cezalandırandır ve yine muhakkak ki O, çok affedici, çok merhametlidir.'),
(1122, 7, 168, 'Ve onları yeryüzünde birçok ümmetlere ayırdık. İçlerinde iyi olanları da vardı, olmayanları da. Onları biz, bazan nimetlerle, bazan da musibetlerle imtihana çektik. Sonunda belki hakka dönerler diye.'),
(1123, 7, 169, 'Derken kitabı (Tevrat\'ı) miras alan bozuk bir nesil bunların yerini aldı. Bize nasıl olsa mağfiret edilecek diyerek, şu alçak dünya malını alıyorlar, yine onun gibi bir mal ve rüşvet gelse onu da alırlar. Allah\'a karşı haktan başka bir şey söylemeyeceklerine dair kendilerinden o kitabın hükmü üzere misak alınmamış mıydı? Ve onun içindekileri okuyup öğrenmemişler miydi? Oysa ahiret yurdu Allah\'tan korkanlar için daha hayırlıdır. Hâlâ aklınızı başınıza almayacak mısınız?'),
(1124, 7, 170, 'Kitaba sarılanlara ve namazı kılmaya devam edenlere gelince, biz o iyilerin ecrini hiçbir zaman yitirmeyiz.'),
(1125, 7, 171, 'Hani bir zamanlar biz o dağı gölgelik gibi tepelerine çekmiştik de üzerlerine düşüyor zannettikleri bir sırada demiştik ki; "size verdiğimiz kitabı kuvvetle tutun ve içindekini hatırınızdan çıkarmayın, umulur ki korunursunuz."'),
(1126, 7, 172, 'Bir de Rabbin, Âdemoğullarından, bellerindeki zürriyetlerini alıp da onları kendi nefislerine şahit tutarak: Ben sizin Rabbiniz değil miyim?" dediği vakit, "pekâlâ Rabbimizsin, şahidiz" dediler. (Bunu) kıyamet günü "Bizim bundan haberimiz yoktu." demeyesiniz diye (yapmıştık).'),
(1127, 7, 173, 'Yahut, atalarımız daha önce şirk koşmuşlardı. Biz onlardan sonra gelen bir nesil idik, şimdi o batıl yolu tutanların yaptıkları yüzünden bizi helâk mi edeceksin, demeyesiniz diye (yapmıştık).'),
(1128, 7, 174, 'Ve işte biz, âyetleri böyle ayrıntılı olarak açıklıyoruz ki, belki dönerler.'),
(1129, 7, 175, 'Onlara, kendisine âyetlerimizi sunduğumuz o adamın kıssasını da anlat; âyetlerden sıyrılıp çıktı, derken onu şeytan arkasına taktı, en sonunda da helak olanlardan oldu.'),
(1130, 7, 176, 'Ve eğer dileseydik onu o âyetlerle yüceltirdik, fakat o alçaklığa saplandı kaldı ve kendi keyfinin ardına düştü. Artık onun ibret verici hali o köpeğin haline benzer ki, üzerine varsan da dilini uzatır solur, bıraksan da solur. İşte bu, âyetlerimizi inkâr eden kavmin misalidir. Bu kıssayı iyice anlat, belki biraz düşünürler.'),
(1131, 7, 177, 'Âyetlerimizi inkâr edip, sırf kendilerine zulmeden o kavmin hali ne kadar kötüdür!'),
(1132, 7, 178, 'Allah kime hidayet ederse, o hidayete erer, kimi de dalalette bırakırsa, işte onlar hüsrana uğrayanların ta kendileri olurlar.'),
(1133, 7, 179, 'Andolsun ki, cinlerden ve insanlardan birçoğunu cehennem için yarattık. Onların kalbleri vardır, fakat onunla gerçeği anlamazlar. Gözleri vardır, fakat onlarla görmezler. Kulakları vardır, fakat onlarla işitmezler. İşte bunlar hayvanlar gibidirler. Hatta daha da aşağıdırlar. Bunlar da gafillerin ta kendileridir.'),
(1134, 7, 180, 'Oysa en güzel isimler Allah\'ındır. Bundan dolayı Allah\'a onlarla dua edin. Onun isimlerinde sapıklık eden mülhidleri (inkârcıları) terkedin. Onlar yakında yaptıklarının cezasını çekecekler.'),
(1135, 7, 181, 'Yine bizim yarattığımız insanlardan öyle bir ümmet var ki, onlar hakka yol gösterirler ve o hak ile adaleti yerine getirirler.'),
(1136, 7, 182, 'Âyetlerimizi inkâr edenlere gelince, biz onları, bilemiyecekleri yönlerden derece derece düşüşe yuvarlayacağız.'),
(1137, 7, 183, 'Ayrıca ben onlara mühlet de veririm. Fakat benim tuzak kurup helâk edişim pek çetindir.'),
(1138, 7, 184, 'Onlar arkadaşlarında herhangi bir cinnet bulunmadığını hiç düşünmediler mi? O, açık bir uyarıcıdan başka biri değildir.'),
(1139, 7, 185, 'Allah\'ın göklerdeki ve yerdeki mülkiyet ve tasarrufuna, Allah\'ın yaratmış olduğu herhangi bir şeye ve ecellerinin gerçekten yaklaşmış olması ihtimaline hiç bakmadılar mı? Artık bu Kur\'ân\'dan sonra başka hangi söze inanacaklar.'),
(1140, 7, 186, 'Allah kimi saptırırsa onu yola getirecek bir kimse yoktur. O, onları kendi hâllerine bırakır ve kendi azgınlıkları içinde yuvarlanıp giderler.'),
(1141, 7, 187, 'Sana, ne zaman kopacak diye kıyamet vaktini soruyorlar. De ki; onun bilgisi yalnızca Rabbimin katındadır. Onu tam vaktinde koparacak olan O\'ndan başkası değildir. Onun ağırlığına göklerde ve yerde dayanacak bir kimse yoktur. O size ansızın gelecektir. Sanki sen onu çok iyi biliyormuşsun gibi sana soruyorlar. De ki, onun bilgisi Allah katındadır. Fakat insanların çoğu bunu bilmezler.'),
(1142, 7, 188, 'De ki, ben kendi kendime Allah\'ın dilediğinden başka ne bir menfaat elde etmeye, ne de bir zararı önlemeye malik değilim. Ben eğer gaybı bilseydim daha çok hayır yapardım ve kötülük denilen şey yanıma uğramazdı. Ben iman edecek bir kavme müjde veren ve uyaran bir peygamberden başka biri değilim.'),
(1143, 7, 189, 'Sizi bir tek nefisten yaratan, onunla sükûnet bulsun diye eşini de ondan yaratan Allah\'tır. O, eşini kucaklayıp sarılınca (ona yaklaşınca), eşi hafif bir yük yüklendi (hâmile kaldı). Bir müddet böyle geçti, derken yükü ağırlaştı. O vakit ikisi birden Rableri olan Allah\'a şöyle dua ettiler: "Eğer bize salih bir evlat verirsen, biz muhakkak şükredenlerden olacağız."'),
(1144, 7, 190, 'Fakat Allah, kendilerine salih bir evlat verince, her ikisi de tuttular verdiği evlatlar üzerine ona ortak koşmaya başladılar. Allah, onların koştukları şirkten münezzehtir.'),
(1145, 7, 191, 'Hiçbir şey yaratmayan ve kendileri yaratılmış olan putları mı Allah\'a ortak ediyorlar, ona eş koşuyorlar?'),
(1146, 7, 192, 'Bu putlar, ne o tapınanlara, ne de kendi kendilerine yardım edebilirler.'),
(1147, 7, 193, 'Eğer siz onları doğru yola çağırsanız, size uymazlar. Onları ha çağırmışsınız, ha çağırmayıp susmuşsunuz, hiç fark etmez.'),
(1148, 7, 194, 'Allah\'ı bırakıp taptıklarınız da tıpkı sizin gibi kullardır. Eğer iddianızda doğru iseniz haydi onları çağırın da size cevap versinler.'),
(1149, 7, 195, 'Onların yürüyecek ayakları, tutacak elleri, görecek gözleri veya işitecek kulakları mı var? De ki: "Haydi çağırın o ortaklarınızı, sonra bana istediğiniz tuzağı kurun ve elinizden gelirse göz açtırmayın."'),
(1150, 7, 196, '"Zira benim velim, o kitabı indiren Allah\'tır. Ve O, salih kullarına sahip çıkar."'),
(1151, 7, 197, '"Sizin Allah\'tan başka taptıklarınız ise ne size yardım edebilirler, ne de kendi kendilerine yardımları dokunur."'),
(1152, 7, 198, '"Siz onları doğru yola çağıracak olsanız da duymazlar." Onların sana baktıklarını görürsün, bakarlar, ama görmezler.'),
(1153, 7, 199, 'Sen yine de affa sarıl, iyiliği emret ve cahillerden yüz çevir.'),
(1154, 7, 200, 'Eğer şeytandan bir vesvese, bir gıcık gelirse hemen Allah\'a sığın. Muhakkak ki, Allah hakkıyla işiten, kemaliyle bilendir.'),
(1155, 7, 201, 'Allah\'tan korkanlar, kendilerine şeytandan bir vesvese iliştiği zaman, durup düşünürler de derhal kendi basiretlerine sahib olurlar.'),
(1156, 7, 202, 'Şeytanların kardeşlerine gelince, onlar öbürlerini sapıklığa sürüklerler, sonra da yakalarını bırakmazlar.'),
(1157, 7, 203, 'Onlara (arzularına göre) bir âyet getirmediğin zaman, derleyip toplasaydın ya derler, sen de de ki; ben ancak Rabbimden bana ne vahyolunuyorsa ona uyarım, işte bütünüyle bu Kur\'ân, Rabbinizden gelen basiretlerdir (kalp gözünü açacak beyanlardır), iman eden bir kavim için hidayettir, rahmettir.'),
(1158, 7, 204, 'Kur\'ân okunduğu zaman, hemen susup onu dinleyin, umulur ki, rahmete nâil olursunuz.'),
(1159, 7, 205, 'Sabah akşam demeden, kendi içinden, korkarak ve yalvararak, alçak sesle Rabbini an ve gafillerden olma.'),
(1160, 7, 206, 'Zira Rabbinin katında olanlar, Allah\'a kulluk etmekten asla kibirlenmezler, O\'nu tenzih eder, şanını ulularlar ve yalnızca O\'na secde ederler.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Sana ganimetlerin bölüştürülmesini soruyorlar. De ki, ganimetlerin taksimi Allah\'a ve Resulüne aittir. Onun için siz gerçekten mümin kimseler iseniz Allah\'tan korkun da biribirinizle aranızı düzeltin. Allah\'a ve Resulü\'ne itaat edin.'),
(1162, 8, 2, 'Gerçek müminler ancak o müminlerdir ki, Allah anıldığı zaman yürekleri ürperir, âyetleri okunduğu zaman imanlarını arttırır. Ve bunlar yalnızca Rablerine tevekkül ederler.'),
(1163, 8, 3, 'Onlar ki, namazı gereği gibi kılarlar ve kendilerine rızık olarak verdiğimiz şeylerden Allah yoluna harcarlar.'),
(1164, 8, 4, 'İşte gerçekten mümin olanlar onlardır. Onlara Rablerinin katında dereceler vardır, bağışlanma ve değerli rızık vardır.'),
(1165, 8, 5, 'Nitekim Rabbin seni, hak uğruna savaşmak için evinden çıkarmıştı. Oysa müslümanların bir kısmı o zaman bundan hoşlanmamışlardı.'),
(1166, 8, 6, 'Ve gerçek, gün gibi açığa çıktıktan sonra bile seninle münakaşaya devam etmişlerdi; sanki göz göre göre ölüme sürükleniyorlardı.'),
(1167, 8, 7, 'İşte o zaman Allah size iki taifeden (kervan veya kureyş ordusundan) birini vaad ediyordu ki, sizin olacaktı. Siz ise arzu ediyordunuz ki, şanı ve şerefi olmayan şey (kervan) sizin olsun. Halbuki Allah, âyetleriyle hakkı yerine oturtmak ve kâfirlerinarkasını kesmek istiyordu.'),
(1168, 8, 8, 'Ki, hakkın hak olduğunu tanıtsın ve batılı büsbütün yok etsin, varsın o günahkârlar istemesin.'),
(1169, 8, 9, 'O vakit siz Rabbinizden yardım diliyordunuz. O da: "Ben işte ardarda bin melekle size yardım ediyorum" diye duanızı kabul buyurmuştu.'),
(1170, 8, 10, 'Bunu da Allah size sırf bir müjde olsun ve bununla kalbleriniz yatışsın diye yapmıştı. Yoksa zafer ancak Allah katındandır. Gerçekten Allah mutlak galiptir ve hikmet sahibidir.'),
(1171, 8, 11, 'O sırada size, yine katından bir güven ve esenlik olmak üzere bir uyku sardırıyordu, sizi temizlemek, şeytanın vesvesesini sizden gidermek, yüreklerinize kuvvet vermek ve ayaklarınızı sağlam durdurmak için gökten üzerinize yağmur indiriyordu.'),
(1172, 8, 12, 'İşte o anda Rabbin meleklere şöyle vahyediyordu: Ben sizinle beraberim, müminlere sebat verin. Kâfirlerin yüreğine korku salacağım, hemen boyunlarının üstüne vurun, parmaklarına, parmaklarına vurun".'),
(1173, 8, 13, 'Çünkü onlar Allah\'a ve Resulüne karşı geldiler. Kim Allah\'a ve Resulüne karşı gelirse, bilsin ki Allah\'ın azabı çok çetindir.'),
(1174, 8, 14, 'İşte gördünüz ya, şimdilik siz bunu tadın, şu da kesindir ki, ahirette kâfirlere cehennem azabı vardır.'),
(1175, 8, 15, 'Ey iman edenler! Toplu olarak kâfirlerle karşılaştığınız zaman, onlara arkalarınızı dönmeyin (kaçmayın).'),
(1176, 8, 16, 'Böyle bir günde her kim onlara, tekrar dönüp çarpışmak için geri çekilmek veya diğer bir safta yeniden mevzilenmek hâlleri dışında, arkasını dönerse, muhakkak Allah\'dan bir gazaba uğramış olur ve varacağı yer cehennemdir, orası da ne kötü bir akıbettir.'),
(1177, 8, 17, 'Sonra onları siz öldürmediniz, lâkin Allah öldürdü. Attığın zaman da sen atmadın, lâkin Allah attı. Bu da müminlere güzel bir imtihan geçirtmek içindi. Allah işitendir, bilendir.'),
(1178, 8, 18, 'Gördünüz ya, Allah, kâfirlerin kurduğu tuzağı işte böyle boşa çıkarır.'),
(1179, 8, 19, 'Fetih istiyorsanız, işte size fetih gelmiştir, eğer aşırı gitmez de son verirseniz, hakkınızda daha hayırlıdır. Yok eğer dönerseniz, biz de döneriz. O vakit askeriniz çok da olsa size hiç bir şekilde fayda vermez. İyi biliniz ki, Allah müminlerle beraberdir.'),
(1180, 8, 20, 'Ey iman edenler, Allah\'a ve Resulü\'ne itaat edin. İşitip durduğunuz halde onun emirlerinden yüz çevirmeyin!'),
(1181, 8, 21, 'Ve işitmedikleri halde "işittik" diyenler gibi olmayın!'),
(1182, 8, 22, 'Çünkü yeryüzünde dolaşan canlıların Allah katında en kötüsü anlamayan ve düşünmeyen sağırlarla dilsizlerdir.'),
(1183, 8, 23, 'Allah onlarda hayır görseydi onlara işittirirdi, işittirseydi yine de aldırmaz arka dönerlerdi.'),
(1184, 8, 24, 'Ey iman edenler! Peygamber sizi, size hayat verecek şeylere davet ettiği zaman, Allah\'a ve Resul\'e icabet edin. Ve bilin ki Allah, kişi ile kalbi arasına girer. Ve siz kesinkes O\'nun huzurunda toplanacaksınız.'),
(1185, 8, 25, 'Ve öyle bir fitneden sakının ki, içinizden yalnızca zulüm yapanlara dokunmakla kalmaz. Ve bilin ki, Allah\'ın cezası şiddetlidir.'),
(1186, 8, 26, 'Düşünün ve hatırlayın o zamanları ki, hani bir vakitler siz yeryüzünde güçsüzdünüz, hor görülen bir azınlıktınız. İnsanların sizi hırpalamasından korkuyordunuz, öyle iken O, sizi barındırdı ve sizi yardımıyla destekleyip güçlendirdi ve şükretmeniz için temizlerinden rızık verdi.'),
(1187, 8, 27, 'Ey iman edenler! Allah\'a ve Resul\'e hainlik etmeyiniz ki, bile bile kendi emanetlerinize hıyanet etmiş olmayasınız.'),
(1188, 8, 28, 'Ve iyi biliniz ki, mallarınız ve evlatlarınız birer imtihan aracından başka birşey değildir. Allah katında büyük ecir vardır.'),
(1189, 8, 29, 'Ey iman edenler! Allah\'a karşı gelmekten sakınırsanız, O, size bir furkan (hakkı batıldan ayırdedecek bir anlayış) verir ve günahlarınızı örtbas eder, sizi bağışlar. Allah büyük lütuf sahibidir.'),
(1190, 8, 30, 'Hani bir vakitler, o kâfirler, seni tutup bağlamak veya öldürmek veya sürüp çıkarmak için sana tuzak kuruyorlardı da, onlar tuzak kurarken Allah da karşılığında tuzak kuruyordu. Öyle ya, Allah tuzakların en hayırlısını kurar.'),
(1191, 8, 31, 'Onlara âyetlerimiz okunduğu zaman, "işittik, dilersek bunun gibisini biz de söyleriz, bu, eskilerin efsanelerinden başka bir şey değildir" diyorlardı.'),
(1192, 8, 32, 'Bir vakit de, "Ey Allah, eğer bu Senin katından gelmiş bir hak kitap ise, hiç durma üstümüze gökten taşlar yağdır veya bize daha acı bir azap ver" demişlerdi.'),
(1193, 8, 33, 'Halbuki sen içlerinde iken Allah, onlara azab edecek değildi. İstiğfar ettikleri sürece de Allah onlara azab edecek değildir.'),
(1194, 8, 34, 'Şimdi ise Allah\'ın kendilerine azab etmemesi için neleri var ki? Oysa Mescidi Haram\'dan menediyorlar. Üstelik onun hizmetine ehil kişiler de değiller. Çünkü onun hizmetine ehil olanlar ancak müttakilerdir. Lâkin çoğu bunu bilmezler.'),
(1195, 8, 35, 'Kâbe huzurunda onların duaları ise ıslık çalıp el çırpmaktan başka birşey değildir. O halde inkârınızdan (ve nankörlüğünüzden) dolayı bu azabı tadın bakalım.'),
(1196, 8, 36, 'Mallarını, Allah yolundan engellemek için sarfeden o kâfirler, hiç şüphesiz yine onu sarfedecekler. Varsın sarfetsinler, sonra o yüreklerine inen bir acı olacak, sonra da mağlup olacaklar. Zaten kâfirler toplanıp cehenneme gönderilecekler.'),
(1197, 8, 37, 'Allah, murdarı temizden ayırdetmek için ve bir de murdar kısmını birbiri üzerine bindirip hepsini bir araya getirmek ve topunu birden cehenneme koymak için böyle yapar. İşte bunlar o hüsran içinde kalanların ta kendileridir.'),
(1198, 8, 38, 'O kâfirlere de ki: Eğer bu işe son verirlerse daha önce yaptıkları bağışlanacak. Yok yine karşı koymaya başlar, isyana dönerlerse, önceki ümmetlere uygulanan kurallar kendilerine de uygulanacak. (Artık o ilâhî uygulamayı beklesinler.)'),
(1199, 8, 39, 'Ortalıkta fitne kalmayıp, din tamamıyla Allah\'ın dini oluncaya kadar onlarla savaşın. Eğer vazgeçerlerse muhakkak ki, Allah yaptıklarını görür.'),
(1200, 8, 40, 'Yok vazgeçmez de tekrar eskiye dönerlerse artık bilin ki, Allah sizin yardımcınızdır. O ne güzel mevla, ne güzel yardımcıdır.'),
(1201, 8, 41, 'Şunu da biliniz ki, ganimet olarak aldığınız her hangi bir şeyden beşte biri mutlaka Allah içindir. O da peygambere ve ona yakınlığı olanlara, yetimlere, miskinlere ve yolda kalmışlara aittir. Eğer siz Allah\'a iman etmiş, hak ile batılın ayrıldığı o gün, iki ordunun karşı karşıya geldiği o (Bedir) günü kulumuza indirdiğimiz âyetlere iman getirmiş iseniz bunu böyle biliniz. Ve biliniz ki, Allah, herşeye kâdirdir.'),
(1202, 8, 42, 'O vakit siz vadinin yakın bir yamacında idiniz, onlarsa uzak yamacında idiler. Kervan da sizden daha aşağıda idi. Öyle ki, şayet onlarla sözleşmiş olsaydınız, öyle bir buluşma yeri için mutlaka anlaşmazlık çıkarırdınız. Fakat olması gereken (zafer)in olması için Allah böyle takdir etti. Tâ ki, helak olan apaçık bir delil gördükten sonra helak olsun, sağ kalanlar da yine apaçık bir delilden sonra yaşasın. Kesindir ki Allah, işitendir, bilendir.'),
(1203, 8, 43, 'Hani o vakitler Allah sana uykunda (rüyanda) onları az gösteriyordu. Eğer Allah sana onları kalabalık gösterseydi korkacaktınız ve savaş konusunda anlaşmazlığa düşecektiniz. Fakat Allah böyle bir şeyden sizi uzak tuttu. Çünkü O, gönüllerde yatanı da bilir.'),
(1204, 8, 44, 'Ve işte onlarla karşılaştığınız vakit onları sizin gözünüze az gösteriyordu, sizi de onların gözlerinde azaltıyordu. Çünkü Allah o mukadder olan işi yerine getirecekti. Bütün işler Allah\'a döndürülür.'),
(1205, 8, 45, 'Ey iman edenler, bir düşman topluluğu ile karşılaştığınız zaman sebat edin ve Allah\'ı çokça zikredin ki, kurtuluşa eresiniz.'),
(1206, 8, 46, 'Ayrıca Allah\'a ve Resulü\'ne itaat edin. Ve birbirinizle didişmeyin. Sonra içinize korku düşer ve kuvvetiniz elden gider. Sabırlı olun, çünkü Allah sabredenlerle beraberdir.'),
(1207, 8, 47, 'Çalım atarak ve halka gösteriş yaparak yurtlarından çıkanlar ve Allah yoluna engel koyanlar gibi olmayın. Allah onların bütün yaptıklarını çepeçevre kuşatmıştır.'),
(1208, 8, 48, 'Şeytan, onlara amellerini güzel gösterdiği zaman, "Bu gün insanlardan size galip gelecek yoktur, ben de size yardımcıyım." demişti. Fakat iki tarafın karşı karşıya geldiği görününce arkasını dönüp kaçtı ve şöyle dedi: "Ben sizden kesinlikle uzağım. Ben sizin göremeyeceğiniz şeyler görüyorum ve ben Allah\'dan korkarım. Ayrıca Allah\'ın azabı çok çetindir."'),
(1209, 8, 49, 'O sırada münafıklar ve kalblerinde hastalık bulunanlar, (müslümanlar hakkında) "şu adamları dinleri aldattı" diyorlardı. Oysa her kim Allah\'a tevekkül ederse bilsin ki, Allah galiptir, güçlüdür ve hikmet sahibidir.'),
(1210, 8, 50, 'Melekler, o kâfirlerin yüzlerine ve sırtlarına vura vura ve "Tadın bakalım cehennem azabını!" diye diye canlarını alırken hallerini bir görmeliydin.'),
(1211, 8, 51, 'İşte bu, sizin kendi ellerinizle meydana getirdiğiniz bir sonuçtur. Hiç şüphesiz Allah, kullarına hiçbir şekilde zalim biri değildir.'),
(1212, 8, 52, 'Tıpkı Firavun\'un izinden gidenlerle onlardan öncekilerin gidişi gibi onlar da Allah\'ın âyetlerini tanımadılar, Allah da kendilerini günahları yüzünden tutuklayıverdi. Çünkü Allah çok kuvvetli ve azabı çok çetin olandır.'),
(1213, 8, 53, 'Bu, Allah\'ın bir kavme verdiği nimeti, onlar kendilerini değiştirmedikçe değiştirmemesinden dolayıdır. Gerçekten de Allah hakkiyle işiten, herşeyi bilendir.'),
(1214, 8, 54, 'Tıpkı Firavun\'un izinden gidenlerle onlardan öncekilerin gidişi gibi, Rabblerinin âyetlerini yalanladılar. Biz de onları günahları yüzünden helâk ettik. Firavun ile arkasından gidenleri suda boğduk. Hepsi de zalim idiler.'),
(1215, 8, 55, 'Allah katında kımıldayıp debelenen canlıların en kötüsü, inkara saplanıp da bir türlü iman etmeyenlerdir.'),
(1216, 8, 56, 'Onlar, kendileriyle antlaşma yaptığın halde her defasında antlaşmalarını bozarlar ve bundan hiç çekinmezler.'),
(1217, 8, 57, 'Bundan dolayı onları harpte yakalarsan, kendilerinden sonrakilere de gözdağı olacak şekilde ağır bir cezaya çarptır, belki ibret alırlar.'),
(1218, 8, 58, 'Eğer bir kavmin, sözleşmeye aykırı bir hainlik yapmasından korkarsan, savaştan önce aynı şekilde antlaşmayı bozduğunu kendilerine bildir. Çünkü Allah hainleri sevmez.'),
(1219, 8, 59, 'O kâfirler ileri geçip kurtulduklarını sanmasınlar. Onlar kesinlikle (bizi) aciz bırakamazlar.'),
(1220, 8, 60, 'Siz de gücünüzün yettiği kadar onlara karşı her çeşitten kuvvet biriktirin ve cihad için atlar hazırlayın ki, onlarla hem Allah\'ın düşmanlarını, hem de kendi düşmanlarınızı, ayrıca Allah\'ın bilip de sizin bilmediğiniz daha başkalarını korkutasınız. Allah yolunda her ne harcarsanız onun sevabı size eksiksiz ödenir ve asla haksızlığa uğratılmazsınız.'),
(1221, 8, 61, 'Eğer onlar barıştan yana olurlarsa, sen de barıştan yana ol! Ve Allah\'a güven. Çünkü işiten ve bilen O\'dur.'),
(1222, 8, 62, 'Eğer sana hile yapmak isterlerse, muhakkak ki sana Allah yeter. Seni yardımıyla ve müminlerle güçlendirecek olan O\'dur.'),
(1223, 8, 63, 'Müminlerin kalplerini birbirlerine O ısındırdı. Yoksa yeryüzünde ne varsa sen hepsini harcasaydın yine de onların kalblerini (böylesine) ısındıramazdın. Lâkin Allah, kalplerini kaynaştırdı. Muhakkak ki, O azizdir, hakimdir.'),
(1224, 8, 64, 'Ey Peygamber! Sana Allah yetişir, arkandan gelen müminlerle beraber.'),
(1225, 8, 65, 'Ey Peygamber! Müminleri cihada teşvik eyle. Eğer sizden sabredecek yirmi kişi olursa ikiyüze galip gelirler ve eğer sizden yüz kişi olursa kâfirlerden bin kişiye galip gelirler. Çünkü onlar hakkı ve akıbeti düşünmeyen anlayışsız bir kavimdirler.'),
(1226, 8, 66, 'Şimdi Allah sizden yükü hafifletti ve sizde bir zaaf olduğunu bildi. O halde sizden sabredecek yüz kişi olursa ikiyüz düşmana galip gelirler, sizden bin kişi olursa Allah\'ın izniyle ikibin düşmana galip gelirler. Allah sabredenlerle beraberdir.'),
(1227, 8, 67, 'Hiçbir peygamberin, yeryüzünde ağır basmadıkça (kesin zafere ulaşıp üstün gelmedikçe) esirleri olması layık değildir. Siz dünya malını istersiniz, oysa Allah ahireti kazanmanızı murad eder. Allah azizdir, hakimdir.'),
(1228, 8, 68, 'Eğer Allah\'dan bir yazı (hüküm) bulunmasa idi aldığınız fidyeden dolayı size mutlaka büyük bir azab dokunurdu.'),
(1229, 8, 69, 'Artık elde ettiğiniz ganimetten helâl ve hoş olarak yiyin ve Allah\'a karşı gelmekten sakının. Muhakkak ki, Allah bağışlayıcıdır ve merhamet edicidir.'),
(1230, 8, 70, 'Ey Peygamber, elinizdeki esirlere de ki: "Eğer Allah sizin kalblerinizde bir hayır bulursa, sizden alınandan daha hayırlısını size verir ve günahlarınızı bağışlar. Çünkü Allah bağışlayıcıdır."'),
(1231, 8, 71, 'Eğer sana hıyanet etmek isterlerse iyi bilsinler ki, bundan önce Allah\'a hainlik ettiklerinden dolayı Allah onların ezilmelerine imkân verdi. Allah her şeyi hakkıyla bilen hüküm ve hikmet sahibidir.'),
(1232, 8, 72, 'Gerçekten de iman edip hicret eden, mallarıyla ve canlarıyla Allah yolunda cihad veren, onları barındırıp yardım edenler, işte bunlar birbirlerinin dostlarıdırlar. İman ettiği halde henüz hicret etmemiş olanlar, hicret edinceye kadar onlar üzerinde herhangi bir velayet hakkınız yoktur. Bununla beraber dinde sizden yardım isterlerse, sizinle arasında antlaşma bulunanlar aleyhine bir durum olmadıkça, onlara yardım etmeniz de üzerinize borçtur. Allah bütün yaptıklarınızı görüp duruyor.'),
(1233, 8, 73, 'Kâfirler de aslında birbirlerinin dostları ve yardımcılarıdırlar. Eğer siz de öyle yapmazsanız, yeryüzünde büyük bir fitne ve fesat çıkar.'),
(1234, 8, 74, 'O kimseler ki, iman ettiler, hicret ettiler ve Allah yolunda cihada katıldılar, bir kısımları da onları barındırıp yer, yurt sahibi yaptılar ve yardıma koştular, işte bunlar hakkıyla mümin olanlardır. Bunlara bir mağfiret ve cömertçe bir rızık vardır.'),
(1235, 8, 75, 'Daha sonradan hicret edip sizinle beraber savaşa katılanlar da sizdendirler. Bir de akraba olanlar, Allah\'ın kitabına göre, birbirlerine daha yakındırlar. Şüphe yok ki, Allah her şeyi bilir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Allah\'dan ve Resulü\'nden bir ültimatomdur bu, kendileriyle antlaşma yaptığınız müşriklere:'),
(1237, 9, 2, 'Bundan böyle yeryüzünde dört ay daha istediğiniz gibi gezip dolaşın. Şunu da bilin ki, Allah\'ı aciz bırakacak değilsiniz. Allah kâfirleri mutlaka perişan edecektir.'),
(1238, 9, 3, 'Ayrıca büyük hac günü Allah ve Rasulü tarafından insanlara bir ilandır ki, Allah da Resulü de müşriklerle yapılan antlaşmalara artık bağlı değildir. Eğer hemen tevbe ederseniz, bu sizin için hayırlıdır. Yok yine tevbeden yüz çevirirseniz biliniz ki, Allah\'ı yıldıracak değilsiniz. Kâfirleri acı bir azap ile müjdele.'),
(1239, 9, 4, 'Ancak kendileriyle antlaşma yapmış olduğunuz müşriklerden size olan ahitlerinde hiçbir eksiklik yapmamış ve sizin aleyhinize hiçbir kimseye yardımda bulunmamış olanlar bunun dışındadır. Siz de onlarla olan antlaşmanızın hükümlerine antlaşma süresinin sonuna kadar uyunuz. Muhakkak ki, Allah müttakileri sever.'),
(1240, 9, 5, 'Şu haram aylar bir çıktı mı artık o müşrikleri nerede bulursanız öldürün, yakalayın, hapsedin ve bütün geçit başlarını tutun. Eğer tevbe ederler ve namaz kılıp zekatı verirlerse onları serbest bırakın. Muhakkak ki, Allah çok bağışlayandır, çok merhamet edendir.'),
(1241, 9, 6, 'Eğer müşriklerden biri aman dilerse, ona aman ver. Ta ki, Allah\'ın kelâmını dinlesin. Sonra onu güvenlik içinde olduğu yere kadar gönder. Çünkü bunlar gerçekten de bilgisiz bir kavimdirler.'),
(1242, 9, 7, 'O müşriklerin Allah katında ve Resulü katında herhangi bir ahdi nasıl olabilir? Ancak Mescidi Haram yanında antlaşma yaptıklarınız var ki, bunlar size karşı doğru durdukça siz de onlara doğru olun. Allah (hainlikten) sakınanları elbette sever.'),
(1243, 9, 8, 'Onlarla nasıl sözleşme olabilir ki, sizin aleyhinize ellerine bir fırsat geçse, hakkınızda ne bir antlaşma gözetirler, ne de bir yemin. Dil ucuyla sizi hoşnud etmeye çalışırlar, fakat kalbleri o kadarına da razı olmaz. Zaten onların çoğu fasıktırlar.'),
(1244, 9, 9, 'Allah\'ın âyetlerini az bir çıkara değiştirdiler de Allah yolundan engellediler. Gerçekten de bunlar ne fena şeyler yapageldiler.'),
(1245, 9, 10, 'Bir mümin hakkında ne bir yemin gözetirler, ne de bir antlaşma. Bunlar işte böyle haddi aşan kimselerdir.'),
(1246, 9, 11, 'Eğer tevbe ederler, namazı kılarlar, zekatı verirlerse dinde kardeşleriniz olurlar. Biz âyetleri, bilen bir kavme açıklarız.'),
(1247, 9, 12, 'Eğer verdikleri sözden sonra yeminlerini bozar ve dininize dil uzatırlarsa, o küfür öncülerini hemen öldürün. Çünkü onların yeminleri yoktur. Ola ki, vazgeçerler.'),
(1248, 9, 13, 'Yeminlerini bozan, Peygamber\'i yurdundan çıkarmaya azmeden ve üstelik ilk önce size saldırmaya başlayanlara karşı savaşmaz mısınız? Yoksa onlardan korkuyor musunuz? Eğer mümin iseniz her şeyden önce Allah\'dan korkmalısınız.'),
(1249, 9, 14, 'Onlarla savaşın ki Allah, sizin ellerinizle onların cezasını versin ve... onları rezil ve rüsvay etsin, yardımıyla sizi onlara muzaffer kılsın. Ve mümin bir kavmin yüreklerini ferahlandırsın.'),
(1250, 9, 15, 'Ve kalblerindeki öfkeyi gidersin. Allah dilediğine tevbeyi nasib eder. Allah her şeyi bilir, hüküm ve hikmet sahibidir.'),
(1251, 9, 16, 'Yoksa siz hep kendi halinize terk olunacağınızı mı sandınız? Allah\'ın, içinizden cihad edenleri ve Allah\'tan, Resulü\'nden, müminlerden başka kimseye sığınmayan ve başkaca sığınacak bir yer aramayanları görmediğini mi (zannediyorsunuz)? Allah bütün yaptıklarınızdan haberdardır.'),
(1252, 9, 17, 'Müşrikler kendi inkârlarına kendileri şahit olup dururlarken Allah\'ın mescidlerini imar etmeleri mümkün değildir. Onların bütün yaptıkları boşa gitmiştir. Ve onlar ateş içinde ebedi olarak kalacaklardır.'),
(1253, 9, 18, 'Allah\'ın mescidlerini, ancak Allah\'a ve ahiret gününe inanan, namazı kılan, zekatı veren ve Allah\'dan başkasından korkmayan kimseler imar ederler. İşte hidayet üzere oldukları umulanlar bunlardır.'),
(1254, 9, 19, 'Siz hacılara su dağıtma ve Mescidi Haram\'ı imar etme işiyle Allah\'a ve ahiret gününe iman edip, Allah yolunda cihad edenlerin yaptığı işi bir mi tutuyorsunuz? Bunlar Allah katında eşit olamazlar. Allah zalimler topluluğuna hidayet ihsan etmez.'),
(1255, 9, 20, 'İman edip de hicret edip, mallarıyla, canlarıyla Allah yolunda cihad edenler, Allah katında en büyük dereceye sahiptirler. İşte bunlar murada ermiş olan mutlu kullardır.'),
(1256, 9, 21, 'Rab\'leri, onları kendi katından bir rahmet, bir rıza ve bir cennetle müjdeler ki o cennette onlar için bitmez tükenmez nimetler vardır.'),
(1257, 9, 22, 'Onlar orada ebedi kalırlar. Çünkü en büyük mükâfat Allah katındadır.'),
(1258, 9, 23, 'Ey iman edenler! Eğer babalarınız ve kardeşleriniz imana karşılık küfürden hoşlanıyorlarsa, onları dost edinmeyiniz. Sizden her kim onları dost edinirse işte onlar da zalimlerin ta kendileridir.'),
(1259, 9, 24, 'Onlara de ki; eğer babalarınız, oğullarınız, kardeşleriniz, kadınlarınız, akrabalarınız, kabileniz, elde ettiğiniz mallar, kesada uğramasından korktuğunuz ticaret, hoşlandığınız evler ve meskenler, size Allah ve Resulünden ve Allah yolunda cihaddan daha sevimli ise, artık Allah\'ın emri gelinceye kadar bekleyin. Allah böyle fasıklar topluluğuna hidayet nasip etmez.'),
(1260, 9, 25, 'İnkâr kabul etmez bir durumdur ki, Allah size birçok yerde yardım etti. Özellikle Huneyn Günü ki, o gün kendi çokluğunuz size güven vermişti de o gün size onun bir faydası olmamıştı. Yeryüzü bütün genişliğine rağmen başınıza dar gelmişti. Sonra da bozguna uğrayarak gerisin geri dönüp kaçmaya başlamıştınız.'),
(1261, 9, 26, 'Sonra Allah, Resulünün üzerine ve müminlerin üzerine sekinetini (kalplere huzur veren rahmetini) indirdi ve gözle görmediğiniz ordular indirdi de kendisini tanımayan kâfirleri azaba uğrattı. Ve o kâfirlerin cezası işte budur.'),
(1262, 9, 27, 'Sonra bütün bu olup bitenlerin arkasından Allah, dilediğine tevbe nasib eder. Allah çok bağışlayıcıdır, çok merhamet edicidir.'),
(1263, 9, 28, 'Ey iman edenler! Müşrikler bir pisliktirler. Artık bu yıldan sonra Mescidi Haram\'a yaklaşmasınlar. Eğer yoksulluktan korkarsanız Allah sizi dilediğinde lütuf ve ihsanıyla zenginleştirecektir. Allah gerçekten alîmdir, hakîmdir.'),
(1264, 9, 29, 'Kendilerine kitap verilenlerden oldukları halde ne Allah\'a, ne ahiret gününe inanmayan, Allah\'ın ve Resulünün haram kıldığını haram tanımayan ve hak dini din edinmeyen kimselere alçalmış oldukları halde elden cizye verecekleri hale gelinceye kadar savaş yapın.'),
(1265, 9, 30, 'Yahudiler, "Uzeyir Allah\'ın oğlu" dediler, Hıristiyanlar da "Mesih Allah\'ın oğlu", dediler. Bu onların kendi ağızlarıyla uydurdukları sözlerdir. Daha önce inkâra sapmış olanların sözlerine benzetiyorlar. Allah onları kahretsin, nasıl da saptırıyorlar!'),
(1266, 9, 31, 'Onlar, Allah\'dan başka bilginlerini ve rahiplerini de kendilerine Rab edindiler, Meryem oğlu Mesih\'i de. Oysa onlar bir olan Allah\'a ibadet etmekle emrolunmuşlardı. Allah\'dan başka hiçbir ilâh yoktur. O, müşriklerin ortak koştuğu şeylerden de münezzehtir.'),
(1267, 9, 32, 'Allah\'ın nurunu ağızlarıyla söndürmek istiyorlar, Allah da razı olmuyor. Fakat kâfirler istemeseler de Allah nurunu tamamlamayı diliyor.'),
(1268, 9, 33, 'O öyle bir Allah\'dır ki, Resulünü hidayetle ve hak dinle bütün dinlere üstün kılmak için göndermiştir. Müşrikler hoşlanmasalar da.'),
(1269, 9, 34, 'Ey iman edenler, şurası bir gerçektir ki, yahudi hahamları ile hıristiyan rahiplerinin bir çoğu insanların mallarını haksız yere yerler ve Allah yolundan saptırırlar. Bir de altın ve gümüşü hazineye doldurup, onları Allah yolunda sarfetmeyenleri bu yüzden acıklı bir azap ile müjdele!'),
(1270, 9, 35, 'O gün o altın ve gümüşlerin üstü cehennem ateşinde kızdırılacak da bunlarla alınları, yanları ve sırtları dağlanacak (onlara): "İşte bu kendi canınız için saklayıp biriktirdiğiniz şeydir. Haydi şimdi tadın bakalım şu biriktirdiğiniz şeyin tadını!" denilecek.'),
(1271, 9, 36, 'Doğrusu, Allah katında ayların sayısı oniki aydır. Gökleri ve yeri yarattığı günkü Allah yazısında (böyle yazılmıştır). Bunlardan dördü haram aylardır. Bu da doğru olan dinin hükmüdür. Bu sebeple bunlar hakkında nefislerinize haksızlık yapmayınız. Müşrikler size karşı topyekün savaştıkları gibi siz de onlara karşı topyekün savaş açın. Ve iyi bilin ki, Allah müttakilerle beraberdir.'),
(1272, 9, 37, 'O "Nesi\'" (denilen bir haram ayı geciktirmek âdeti), olsa olsa küfürde fazlalıktır ki, kâfirler onunla şaşırtılır, onu bir yıl helâl, bir yıl haram sayarlar ki, Allah\'ın haram kıldığının sayısına uydursunlar da Allah\'ın haram kıldığını helâl kılsınlar. İşte böylece kendilerine kötü işleri güzel gösterildi. Allah da kâfir olan bir kavmi doğru yola iletmez.'),
(1273, 9, 38, 'Ey iman edenler! Size ne oldu ki, "Allah yolunda cihada çıkın." denilince olduğunuz yere yığılıp kaldınız. Yoksa ahiretten vazgeçip dünya hayatına razı mı oldunuz? Fakat dünya hayatının zevki ahiretin yanında ancak pek az birşeydir.'),
(1274, 9, 39, 'Eğer topluca savaşa katılmazsanız, O sizi acı bir azaba uğratır ve yerinize başka bir kavmi getirir ve siz O\'na zerrece bir zarar veremezsiniz. Allah\'ın herşeye gücü yeter.'),
(1275, 9, 40, 'Eğer siz ona (Peygamber\'e) yardım etmezseniz, Allah ona yardım eder. Hani o kâfirler, onu Mekke\'den çıkardıkları vakit sadece iki kişiden biri iken, ikisi de mağarada bulundukları sırada arkadaşına "Üzülme, çünkü Allah bizimledir." diyordu. Allah onun kalbine sükûnet ve kuvvet indirmişti ve onu görmediğiniz bir orduyla desteklemişti. Kâfirlerin sözünü alçaltmıştı. Yüce olan Allah\'ın kelimesidir. Ve Allah güçlüdür, hikmet sahibidir.'),
(1276, 9, 41, 'Ey müminler! İster hafif techizatla, ister ağırlıklı olarak seferber olun ve mallarınızla, canlarınızla Allah yolunda cihad edin. Eğer bilirseniz böylesi sizin için daha hayırlıdır.'),
(1277, 9, 42, 'Eğer o sefer, yakın bir ganimet ve kolay bir sefer olsaydı mutlaka peşine düşer gelirlerdi. Fakat o meşakkatli yolculuk kendilerine uzun bir sefer geldi. Bununla beraber, "Bizim de gücümüz yetseydi, sizinle beraber elbette sefere çıkardık." diyerek Allah\'a yemin edecekler, nefislerini helake sürükleyecekler. Allah biliyor ki, onlar iyice yalancıdırlar.'),
(1278, 9, 43, 'Allah seni affetsin. Doğru söyleyenler kimler, gerçekten yalancılar kimlerdir, bunların iyice belli olmasını beklemeden niçin onlara izin verdin?'),
(1279, 9, 44, 'Allah\'a ve ahiret gününe inananlar, mallarıyla ve canlarıyla cihad etmeyi görev bildiklerinden (zaten geri kalmak için) senden izin istemezler. Allah o muttakilerin kimler olduğunu bilir.'),
(1280, 9, 45, 'Senden izin isteyenler, olsa olsa Allah\'a ve ahiret gününe inanmayanlar olabilir. Onların kalbleri hep işkillidir. Bundan dolayı şüphe içinde bocalayıp dururlar.'),
(1281, 9, 46, 'Eğer sizinle beraber cihada çıkmak isteselerdi, elbette onunla ilgili olarak bir takım hazırlıklar yaparlardı. Fakat Allah davranmalarını istemedi de onları yoldan alıkoydu ve (kendilerine): "oturun oturanlarla beraber" denildi.'),
(1282, 9, 47, 'Eğer içinizde sizinle beraber cihada çıkmış olsalardı, bozgunculuk etmekten başka şeye yaramayacaklardı ve aranıza fitne sokmak için uğraşacaklardı. İçinizde onların laflarına kanacaklar da vardı. Allah, o zalimleri iyi bilir.'),
(1283, 9, 48, 'Şurası kesindir ki, bunlar daha önce de fitne çıkarmak istediler ve sana türlü işler çevirdiler. Nihayet hak yerini buldu ve Allah\'ın emri onların zoruna gitmesine rağmen açığa çıktı.'),
(1284, 9, 49, 'İçlerinden "Aman bana izin ver, başımı derde sokma" diyen de var. Dikkat et, başlarını asıl kendileri derde soktular. Hiç şüphesiz cehennem, kâfirleri elbette kuşatacaktır.'),
(1285, 9, 50, 'Eğer sana bir iyilik dokunursa fenalarına gider. Eğer sana bir musibet gelirse "Biz zaten tedbirimizi önceden almıştık." derler ve sevine sevine dönüp giderler.'),
(1286, 9, 51, 'De ki: "Hiçbir zaman bize Allah\'ın bizim için takdir ettiğinden başkası dokunmaz. O bizim mevlamızdır. Müminler yalnızca Allah\'a tevekkül etsinler."'),
(1287, 9, 52, 'De ki: "Siz bizde iki güzelliğin (Zafer veya şehitliğin) birinden başkasını mı gözetirsiniz? Biz ise size Allah\'ın kendi katından veya bizim elimizle bir azap indirmesini gözetiyoruz. Haydi siz gözetedurun, biz de sizinle beraber gözetmekteyiz."'),
(1288, 9, 53, 'O münafıklara şunu da de ki; gerek isteyerek, gerek istemeyerek infak edip durun. O infak ettikleriniz sizden hiçbir zaman kabul edilmeyecektir. Çünkü siz fasık bir kavimsiniz.'),
(1289, 9, 54, 'İnfakların onlardan kabul olunmamasına sebep, gerçekte Allah\'a ve Resulüne inanmamaları, namaza ancak üşene üşene gelmeleri, verdiklerini de ancak istemeye istemeye vermeleridir.'),
(1290, 9, 55, 'Onların malları da, evlatları da sakın seni imrendirmesin. Bu olsa olsa, Allah\'ın onları dünya hayatında bu gibi şeylerle azaba uğratmasından ve canlarının kâfir olarak çıkmasını murat etmiş olmasından başka birşey değildir.'),
(1291, 9, 56, 'Hiç şüphesiz onlar, sizden olduklarına dair yemin de ederler. Halbuki sizden değildirler. Fakat onlar öyle bir kavimdirler ki, korkudan ödleri patlıyor.'),
(1292, 9, 57, 'Eğer sığınacak bir yer veya barınacak mağaralar veyahut girilecek bir delik bulsalardı başlarını diker o tarafa doğru koşarlardı.'),
(1293, 9, 58, 'İçlerinde (topladığın) sadakalar hakkında sana tariz eden (dil uzatan) ler de var. Eğer o sadakalardan kendilerine verilmişse hoşnut olurlar, verilmemişse hemen kızarlar.'),
(1294, 9, 59, 'Ne olurdu bunlar, Allah ve Resulünün kendilerine verdiğine razı olsalar da "Bize Allah yeter. Allah bize lütuf ve ihsanından yine lutfeder, verir. Bizim bütün rağbetimiz Allah\'adır" deselerdi.'),
(1295, 9, 60, 'Sadakalar ancak şunlar içindir: Fakirler, yoksullar, o işte çalışan görevliler, müellefei kulûb (kalbleri İslâm\'a ısındırılacaklar), köleler, borçlular, Allah yolundakiler, yolda kalmışlar. Allah tarafından böyle farz kılındı. Allah her şeyi bilendir, hüküm ve hikmet sahibidir.'),
(1296, 9, 61, 'Yine onların içinde öyleleri vardır ki, Peygamber\'i incitiyorlar ve "O her söyleneni dinleyen bir kulaktır." diyorlar. De ki; "Sizin için bir hayır kulağıdır. Allah\'a inanır, müminlere inanır, ayrıca sizden iman edenlere de bir rahmettir". Allah\'ın Resulünü incitenlere acıklı bir azap vardır.'),
(1297, 9, 62, 'Gönlünüzü hoş etmek için gelir size yemin ederler. Bunlar eğer mümin iseler Allah\'ı ve Resulünü razı etmeleri daha doğrudur.'),
(1298, 9, 63, 'Bilmiyorlar mı ki, kim Allah\'a ve Resulüne karşı gelirse, ona muhakkak ki içinde ebedi kalınacak cehennem ateşi vardır. İşte rüsvaylığın büyüğü de budur.'),
(1299, 9, 64, 'Münafıklar, kalblerindekileri bütünüyle haber verecek bir sûrenin tepelerine inmesinden çekinirler. De ki, alay edip durun bakalım, Allah o sizin çekindiğiniz şeyi kesinlikle ortaya çıkaracaktır.'),
(1300, 9, 65, 'Eğer kendilerine sorarsan, "Biz sırf lafa dalmış, şakalaşıyorduk." derler. De ki: "Allah ile, âyetleri ile ve peygamberi ile mi alay ediyorsunuz?"'),
(1301, 9, 66, 'Boşuna özür dilemeyin, iman ettik dedikten sonra küfrünüzü açığa vurdunuz. İçinizden bir kısmını affetsek bile bir kısmını suçlarında ısrar ettikleri için azabımıza uğratacağız.'),
(1302, 9, 67, 'Münafıkların erkekleri de kadınları da birbirlerine benzerler. Kötülüğü emreder, iyilikten sakındırırlar ve Allah yolunda harcamaktan ellerini sıkı tutarlar. Allah\'ı unuttular da, Allah da onları unuttu. Gerçekten de münafıklar hep fâsık kimselerdir.'),
(1303, 9, 68, 'Allah, erkek kadın bütün münafıklara ve bütün kâfirlere cehennem ateşini ebedî olarak vaad buyurdu. O ateş onlara yeter. Allah onlara lânet etmiştir. Onlara bitmez tükenmez bir azap vardır.'),
(1304, 9, 69, '(Ey münafıklar!) siz de tıpkı kendinizden öncekiler gibisiniz. Oysa onlar sizden daha güçlü, kuvvetli, mal ve evlatça sizden daha varlıklı idiler. Dünya nimetlerinden paylarına düşen kadar zevk sürdüler. Sizden öncekiler kısmetlerine düşen kadarıyla nasıl zevk sürmek istedilerse siz de onlar gibi kısmetinize düşen kadarıyla zevk sürmeye baktınız, siz de sizden önce batağa dalanlar gibi batağa daldınız. İşte bunların dünyada ve ahirette bütün amelleri heder olup gitti ve işte bunlar hep hüsran içinde kalanlardır.'),
(1305, 9, 70, 'Onlara, kendilerinden öncekilerin; Nuh Kavmi\'nin, Âd\'in, Semûd\'un, İbrahim Kavmi\'nin, Medyen Ashabı\'nın ve o mü\'tefikelerin haberi gelmedi mi? Onların hepsine peygamberleri delillerle gelmişlerdi. Demek ki Allah, onlara zulmetmiş değildi, lâkin onlar kendi kendilerine zulmediyorlardı.'),
(1306, 9, 71, 'Erkek ve kadın bütün müminler birbirlerinin dostları ve velileridirler. İyiliği emrederler, kötülükten vazgeçirirler, namazı kılarlar, zekâtı verirler, Allah\'a ve Resulüne itaat ederler. İşte bunları Allah rahmetiyle yarlığayacaktır. Çünkü Allah azîzdir, hakîmdir.'),
(1307, 9, 72, 'Allah mümin erkeklere ve mümin kadınlara, altlarından ırmaklar akan cennetler vaad buyurdu. Orada ebedi kalacaklardır. Hem de Adn cennetlerinde hoş meskenler vaad etmiştir. Allah\'ın rızası ise hepsinden büyüktür. İşte asıl büyük kurtuluş da budur.'),
(1308, 9, 73, 'Ey Peygamber, kâfirlerle ve münafıklarla savaş. Onlara karşı katı ol. Onların varacakları yer cehennemdir ve orası ne kötü bir yerdir.'),
(1309, 9, 74, 'Onlar, kötü bir şey söylemedik, diyerek Allah\'a yemin ederler. Onlar o küfür kelimesini kesinlikle söylediler. İslâm\'a girdikten sonra yine kâfirlik ettiler. Ve o başaramadıkları cinayeti tasarladılar. Halbuki intikam almaları için Allah\'ın, Resulü ile onları lütfundan zenginleştirmiş olmasından başka bir sebep yoktu. Eğer tevbe ederlerse haklarında hayırlı olur. Yok yanaşmazlarsa Allah onları dünyada da, ahirette de acıklı bir azaba uğratır. Yeryüzünde onları koruyacak veya onlara yardım edecek bir kimse de bulunmaz.'),
(1310, 9, 75, 'Yine onlardan kimi de Allah\'a şöyle ahdetmişlerdi: "Eğer bize lütuf ve kereminden ihsan ederse biz de elbette zekâtı veririz ve kesinlikle salihlerden oluruz." diye söz vermişlerdi.'),
(1311, 9, 76, 'Ne zaman ki, Allah lutfedip onlara ihsanda bulundu, onlar da cimrilik edip yüz çevirdiler ve zaten yan çizip duruyorlardı.'),
(1312, 9, 77, 'Allah\'a verdikleri sözü tutmadıkları ve yalan söyledikleri için, O da bu yaptıklarının sonucunu kıyamet gününe kadar yüreklerinde sürüp gidecek bir münafıklığa çevirdi.'),
(1313, 9, 78, 'Allah\'ın, onların sırlarını da, fısıltılarını da bilip durduğunu ve Allah\'ın bütün bilinmeyenleri bildiğini hâlâ öğrenemediler mi?'),
(1314, 9, 79, 'Müminlerden zekâttan fazla olarak kendi gönülleriyle bağışta bulunanlara, bir de güçlerinin yettiğinden fazlasını bulamayanlara bakıp da onlarla alay edenleri Allah, maskaraya çevirmiştir. Onlara pek acıklı bir azap vardır.'),
(1315, 9, 80, 'Onlar için Allah\'dan ister mağfiret dile, ister dileme. Onlar için yetmiş kere mağfiret dilesen de yine Allah onları affetmeyecektir. Bu, onların Allah\'ı ve Resulünü inkâr etmelerinden dolayı böyledir. Allah, böylesine baştan çıkmış fasıklar güruhuna hidayet etmez.'),
(1316, 9, 81, 'Savaştan geri kalan münafıklar, Resulullah\'ın hilafına, onun savaşa gitmesine karşılık, oturup kalmalarıyla ferahladılar ve mallarıyla, canlarıyla Allah yolunda cihad etmekten hoşlanmadılar, üstelik "Bu sıcakta savaşa gitmeyin." dediler. De ki: "Cehennem ateşi daha sıcaktır." Keşke anlayabilselerdi.'),
(1317, 9, 82, 'Kazandıkları günahın cezası olarak, artık az gülsünler, çok ağlasınlar.'),
(1318, 9, 83, 'Eğer Allah, seni onlardan bir kısmının yanına döndürür de onlar başka bir cihada seninle birlikte çıkmak için senden izin isterlerse, de ki; "Artık siz hiçbir zaman benimle çıkamayacaksınız. Daha önce oturup kalmaktan hoşlanıyordunuz. Bundan böyle artık geride kalanlarla beraber oturup kalın."'),
(1319, 9, 84, 'Ve onlardan biri ölürse asla namazını kılma ve kabirinin başına gidip durma. Çünkü onlar Allah\'ı ve Resulünü tanımadılar. Ve fasık olarak can verdiler.'),
(1320, 9, 85, 'Onların ne malları, ne de evlatları seni imrendirmesin. Allah, onları dünyada bunlarla cezalandırmayı ve canlarının kâfir olarak çıkmasını murad ediyor, başka değil.'),
(1321, 9, 86, '"Allah\'a iman edin ve Resulü ile birlikte cihada gidin." diye bir sûre indirildiği zaman, içlerinden mal mülk sahibi olanlar senden izin istediler ve "Bırak bizi oturanlarla beraber oturalım." dediler.'),
(1322, 9, 87, 'Onlar, oturanlarla beraber oturmaktan hoşlandılar. Kalblerine mühür vuruldu. Bundan dolayı onlar anlayışsızdırlar.'),
(1323, 9, 88, 'Fakat Peygamber ve onunla beraber olan müminler mallarıyla, canlarıyla cihad ettiler. İşte bütün hayırlar onlarındır. Murada erenler de işte onlardır.'),
(1324, 9, 89, 'Allah onlara, altından ırmaklar akan cennetler hazırladı. İçlerinde ebedi kalacaklar. İşte o büyük kurtuluş budur.'),
(1325, 9, 90, 'Bedevilerden özür bahane edenler, kendilerine izin verilsin diye geldiler. Allah\'a ve Resulüne yalan söyleyenler de oturdular kaldılar. Bunlardan kâfir olanlara acıklı bir azap isabet edecektir.'),
(1326, 9, 91, 'Allah ve Resulü adına nasihat ettikleri takdirde ne zayıflara, ne hastalara, ne de verecek birşey bulamayan yoksullara savaştan kalmaktan dolayı bir günah yoktur. İyilik edenleri ayıplamaya bir yol yoktur. Allah gafurdur, rahîmdir.'),
(1327, 9, 92, 'Kendilerini bindirip savaşa gönderesin diye gönüllü olarak sana geldiklerinde, "Sizi bindirecek birşey bulamıyorum." dediğin zaman, bu uğurda harcayacakları birşey bulamadıklarından dolayı üzülüp gözlerinden yaş döke döke geri dönüp gidenlere de bir günah yoktur.'),
(1328, 9, 93, 'Kınamaya yol, ancak zengin oldukları halde geri kalmak için senden izin isteyenleredir. Bunlar geri kalanlarla beraber olmayı tercih ettiler. Allah da kalblerini mühürledi. Onlar, artık başlarına geleceği bilmezler.'),
(1329, 9, 94, 'Savaştan dönüp yanlarına geldiğinizde size özür beyan edecekler. De ki: "Özür beyan etmeyin. Size kesinlikle inanmayız. Allah bize, sizin durumunuzdan haberler verdi". Bundan sonra da Allah ve Resulü yaptıklarınızı görecektir. Daha sonra da gizliyi ve âşikârı bilen Allah\'a döndürüleceksiniz. O vakit O, size neler yapmış olduğunuzu tek tek haber verecektir.'),
(1330, 9, 95, 'Dönüp de yanlarına geldiğinizde kendilerinden yüz çeviresiniz (hesaba çekmekten vazgeçesiniz) diye Allah\'a yemin edecekler. Siz de onlardan yüz çevirin. Çünkü onlar gerçekten murdar kimselerdir. Yaptıklarının cezası olarak nihayet varacakları yer cehennemdir.'),
(1331, 9, 96, 'Kendilerinden razı olasınız diye size yemin ederler. Eğer siz onlardan razı olursanız, şunu bilin ki Allah, o fasıklar güruhundan kesinlikle razı olmaz.'),
(1332, 9, 97, 'Bedeviler inkâr ve münafıklık bakımından daha beterdirler. Bununla beraber Allah\'ın, Resulüne indirdiği (hükümlerin) sınırlarını bilmemeye daha yatkındırlar. Allah alîmdir, hakîmdir,'),
(1333, 9, 98, 'Bedevilerden kimi de var ki, verdiğini angarya sayar ve sizin üzerinize belalar gelmesini bekler. O çirkin belalar kendi başlarına olsun! Allah herşeyi işitendir, bilendir.'),
(1334, 9, 99, 'Yine bedevilerden kimi de vardır ki, Allah\'a ve ahiret gününe inanır ve harcadığını Allah katında yakınlıklara ve Peygamber\'in dualarını almaya vesile sayar. Gerçekten de bu, onlar için bir yakınlıktır. Allah onları rahmeti içine koyacaktır. Şüphesizki, Allah bağışlayıcıdır ve rahmet edicidir.'),
(1335, 9, 100, 'Muhacir ve Ensar\'dan İslâm\'a ilk önce girenlerin başta gelenleri ve iyi amellerle onların ardınca gidenler var ya, işte Allah onlardan razı oldu, onlar da Allah\'dan razı oldular ve onlara, altlarında ırmaklar akan cennetler hazırladı ki, içlerinde ebedi kalacaklar. İşte büyük ve muhteşem kurtuluş budur.'),
(1336, 9, 101, 'Hem çevrenizdeki bedevilerden münafıklar var, hem de Medine halkından münafıklıkta ısrar edenler var. Sen onları bilmezsin. Onları biz biliriz. Biz onları iki kere azaba uğratacağız. Daha sonra da büyük bir azaba itilecekler.'),
(1337, 9, 102, 'Onlardan bir kısmı günahlarını itiraf ettiler. Ve iyi bir amelle kötü bir ameli karıştırdılar. Ola ki, Allah tevbelerini kabul eder. Çünkü Allah gafurdur, rahîmdir.'),
(1338, 9, 103, 'Onların mallarından sadaka al ki, onunla kendilerini temizlersin, tertemiz edersin. Bir de haklarında hayır dua et. Çünkü senin duan kalblerini yatıştırır. Allah işitendir, bilendir.'),
(1339, 9, 104, 'Onlar bilmiyorlar mı ki, Allah kullarının tevbesini kabul eder ve sadakaları da alır. Allah tevbeleri kabul edendir, çok merhametlidir.'),
(1340, 9, 105, 'Ve de ki; "Çalışın! Yaptıklarınızı hem Allah görecek, hem Resulü, hem de müminler görecektir. Sonra da gizliyi ve açığı bilen Allah\'ın huzuruna iletileceksiniz. İşte o zaman, neler yaptığınızı size O bildirecektir.'),
(1341, 9, 106, 'Savaşa katılmayanlardan diğer bir kısmının affı da Allah\'ın emrini beklemek için geri bırakılmıştır. Ya kendilerini cezalandırır ya da tevbelerini kabul eder. Allah alîmdir, hakîmdir.'),
(1342, 9, 107, 'Bir de müslümanlara zarar vermek, kâfirlik etmek ve müslümanların arasına ayrılık sokmak ve daha önce Allah ve Resulü\'ne karşı savaş açmış olanı beklemek için mescid yapanlar var. "İyilikten başka bir maksadımız yoktu." diye yemin de edecekler. Fakat bunların kesinlikle yalancı olduklarına Allah şahittir.'),
(1343, 9, 108, 'O mescit içinde sen kesinlikle namaza durma. Ta ilk gününde temeli takva üzerine kurulan mescit elbette içinde namaz kılmana daha layıktır. Onun içinde günahlarından arınmayı seven kişiler vardır. Allah da arınmış, ak pak olmuş olanları sever.'),
(1344, 9, 109, 'O halde binasını Allah korkusu ve Allah rızası üzerine kurmuş olan mı hayırlıdır, yoksa binasını yıkılmak üzere olan bir uçurumun kenarına kurup da onunla birlikte cehenneme yuvarlanan mı daha hayırlı? Allah, zalimler güruhunu hidayete erdirmez.'),
(1345, 9, 110, 'Onların kurmuş oldukları bu türlü binalar, kalpleri parça parça olmadıkça, kalblerinde bir nifak düğümü olup kalacaktır. Allah, alîmdir, hakîmdir.'),
(1346, 9, 111, 'Allah, müminlerden, canlarını ve mallarını, kendilerine cennet vermek üzere satın almıştır: Allah yolunda çarpışacaklar da öldürecekler ve öldürülecekler. Bu, Tevrat\'ta da, İncil\'de de Kur\'ân\'da da Allah\'ın kendi üzerine yüklendiği bir ahittir. Allah\'dan ziyade ahdine riayet edecek kim vardır? O halde yaptığınız alışveriş ahdinden dolayı size müjdeler olsun! Ve işte o büyük kurtuluş budur.'),
(1347, 9, 112, '(Bunlar), O tevbekâr olanlar, o ibadet edenler, o hamd edenler, o oruçlular, o rükua varanlar, o secdeye kapananlar, iyiliği emredip, kötülükten vazgeçirenler, Allah\'ın hududunu koruyanlar (emirleriyle yasaklarının ölçülerine riayet edenler)dır. Müjde ver o müminlere, müjde!'),
(1348, 9, 113, 'Ne peygambere, ne iman edenlere akraba bile olsalar cehennemlik oldukları iyice belli olduktan sonra müşriklere istiğfar etmek yoktur.'),
(1349, 9, 114, 'İbrahim\'in babası için istiğfar etmesi de sırf ona vermiş olduğu bir sözden dolayı idi. Böyle iken onun bir Allah düşmanı olduğu kendisine açıklanınca o işten vazgeçti. Şüphesiz ki İbrahim, çok bağrı yanık, çok halim birisi idi.'),
(1350, 9, 115, 'Allah, bir kavmi hidayete erdirdikten sonra, nelerden sakınacaklarını kendilerine iyice açıklamadıkça dalalete düşürmez. Gerçek şu ki, Allah her şeyi bilir.'),
(1351, 9, 116, 'Hiç şüphesiz, göklerin ve yerin mülkü Allah\'ındır. O, diriltir de, öldürür de. Size O\'ndan başka ne bir dost vardır, ne de bir yardımcı.'),
(1352, 9, 117, 'Andolsun ki, Allah, yine peygambere ve en zor gününde ona uyan Muhacirler\'le Ensar\'a, içlerinden bir kısmının kalbleri az kalsın kayacak gibi olmuşken, tevbe nasip etti de lutfedip tevbelerini kabul buyurdu. Çünkü O, gerçekten çok şefkatli, çok bağışlayıcıdır.'),
(1353, 9, 118, 'Allah, haklarında hüküm beklenen o üç kişiyi de bağışladı. Çünkü o derece bunalmışlardı ki, yeryüzü bütün genişliğine rağmen onlara dar gelmeye başlamıştı, vicdanları da kendilerini sıkıntıya sokmuştu. Allah\'dan kurtuluşun, ancak Allah\'a sığınmakta olduğunu anlamışlardı. Sonra da Allah, onları tevbekâr olmaya muvaffak kıldı da tevbelerini kabul buyurdu. Şüphesiz ki Allah, tevbeleri çok çok kabul edendir, çok merhametli olandır.'),
(1354, 9, 119, 'Ey iman edenler! Allah\'dan korkun ve doğrularla beraber olun.'),
(1355, 9, 120, 'Medine halkına ve civardaki bedevilere, Resulullah\'ın emrine aykırı hareket etmek uygun olmadığı gibi, onun katlandığı zahmetlere öbürlerinin katlanmaya yanaşmamaları da yakışık almaz. Çünkü onların Allah yolunda çektikleri hiçbir susuzluk, hiçbir yorgunluk ve hiçbir açlık, ayrıca kâfirleri öfkelendirecek ayak bastıkları hiçbir yer veya düşmana karşı elde ettikleri hiçbir başarı yoktur ki, karşılığında kendilerine salih bir amel yazılmış olmasın. Çünkü Allah, güzel iş yapanların mükafatını zayi etmez.'),
(1356, 9, 121, 'Onların, Allah yolunda yaptıkları küçük veya büyük her harcama veya geçtikleri her vadi karşılığında, yaptıkları işin daha güzeliyle Allah\'ın kendilerini mükâfatlandırması için sevap yazılmaması mümkün değildir.'),
(1357, 9, 122, 'Bununla beraber müminlerin hepsinin birden topyekün savaşa katılmaları uygun değildir. Her kabileden bir kısım insanlar da din ilimlerinde derinleşmeli ve kabileleri savaştan dönüp gelince onları uyarmalıdır ki, böylece Allah\'ın azabından sakınırlar.'),
(1358, 9, 123, 'Ey iman edenler, önce yakın çevrenizdeki kâfirlerle savaşın ki, sizde bir güç ve kuvvet olduğunu görsünler. Ve iyi bilin ki, Allah müttakilerle beraberdir.'),
(1359, 9, 124, 'Bir sûre indirildiği zaman, içlerinden biri çıkar, "Bu sûre hanginizin imanını arttırdı?" der. Fakat müminlere gelince, aslında her inen sûre onların imanını arttırmıştır ve onlar sürekli olarak müjdelenip duruyorlar.'),
(1360, 9, 125, 'Kalblerinde bir hastalık olanlara gelince, onların da murdarlıklarına (küfürlerine) murdarlık (küfür) katmıştır ve kâfir olarak ölüp gitmişlerdir.'),
(1361, 9, 126, 'Onlar (münafıklar) her yıl bir veya iki kere kendilerinin çeşitli belalara uğratıldıklarını görmüyorlar mı? Böyle iken yine de tevbe etmiyor ve ibret almıyorlar.'),
(1362, 9, 127, 'Aleyhlerinde bir sûre indirilince, "Sizi birisi görüyor mu?" diye birbirlerine göz ederler, sonra da sıvışır giderler. Allah onların kalblerini (imandan) çevirmiştir. Bu yüzden onlar anlayışsız bir kavimdirler.'),
(1363, 9, 128, 'Andolsun size içinizden öyle bir peygamber geldi ki, gayet izzetli ve şereflidir. Sıkıntıya düşmeniz ona çok ağır gelir üstünüze titrer, müminlere gayet merhametli ve şefkatlidir.'),
(1364, 9, 129, 'Eğer aldırmazlarsa onlara de ki: Bana Allah yeter. O\'ndan başka ilâh yoktur. Ben O\'na dayanmaktayım ve O, o büyük Arş\'ın Rabbidir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'Elif, Lâm, Râ. İşte bunlar o hikmetli kitabın âyetleridir.'),
(1366, 10, 2, 'İnsanları (eğri yolun sonundan) korkut, inananlara Rableri nezdindeki yüksek makamları müjdele, diye içlerinden bir adama vahyimizi göndermemiz onlara tuhaf mı geldi? Kâfirler: "Hiç şüphesiz bu besbelli bir sihirbaz." dediler.'),
(1367, 10, 3, 'Rabbiniz o Allah\'dır ki, gökleri ve yeri altı günde yarattı, sonra arş üzerine istiva etti (onu hükmü altına aldı), işi tedbir eyliyor. O\'nun izni olmaksızın hiç kimse şefaatçi olamaz. İşte Rabbiniz olan Allah budur. O\'na ibadet ediniz! Hâlâ düşünüp ibret almayacak mısınız?'),
(1368, 10, 4, 'Dönüşünüz hep O\'nadır. Allah\'ın vaadi haktır. Herşeyi ilk baştan yaratan O\'dur. Sonra iman edip salih amel işleyenleri hak ettikleri ölçüde mükâfatlandırmak için geri döndürecek olan yine O\'dur. Kâfirlere de inkâr ettikleri için kaynar sudan bir içki ve acıklı bir azap vardır.'),
(1369, 10, 5, 'O Allah\'dır ki, senelerin sayısını ve hesabını bilesiniz diye güneşi bir ışık, ayı da bir nur yaptı. Ve aya menziller tayin etti. Allah bunu hak olarak yarattı. O, bilecek olan bir kavim için âyetlerini ayrıntılı olarak açıklar.'),
(1370, 10, 6, 'Elbette gece ile gündüzün birbiri ardınca değişip durmasında ve Allah\'ın göklerde ve yerde yarattıklarında sakınan bir kavim için bir çok delil vardır.'),
(1371, 10, 7, 'Bize kavuşmayı ummayanlar, dünya hayatına razı olup onunla tatmin bulanlar ve bizim âyetlerimizden gafil olanlar da vardır muhakkak.'),
(1372, 10, 8, 'İşte bunların kendi elleriyle ettikleri yüzünden varacakları yer cehennemdir.'),
(1373, 10, 9, 'Hiç şüphesiz iman edip salih ameller işleyenleri, imanlarından dolayı Rableri hidayete erdirir. Naîm cennetlerinde altlarından ırmaklar akar durur.'),
(1374, 10, 10, 'Onların oradaki duaları: "Allahım, sen yücelerden yücesin"; sağlık dilekleri "selâm", dualarının sonu da "Âlemlerin Rabbi Allah\'a hamdolsun." diye şükretmek olacaktır.'),
(1375, 10, 11, 'Eğer Allah, insanlara, hayrı çarçabuk istedikleri gibi, şerri de alelacele verseydi, onların hemen ecellerini getiriverirdi. Fakat bize kavuşmayı ummayanları kendi hallerine bırakırız da azgınlıkları içinde bocalayıp giderler.'),
(1376, 10, 12, 'İnsana bir sıkıntı dokunduğu zaman, gerek yan yatarken, gerek otururken, gerek dikilirken bize dua eder. Kendisinden sıkıntısını gideriverdik mi sanki kendisine dokunan o sıkıntı için bize hiç yalvarmamış gibi aldırmadan geçer gider. İşte o aşırı gidenlere yaptıkları şeyler böyle güzel gelir.'),
(1377, 10, 13, 'Andolsun ki, sizden önceki devirlerin bir çok kavmini, peygamberleri kendilerine bir çok belge ile geldikleri halde zulmettikleri ve imana gelmedikleri için helak ettik. İşte günahkârlar topluluğunu biz böyle cezalandırırız.'),
(1378, 10, 14, 'Sonra onların ardından sizi yeryüzüne halifeler yaptık ki, bakalım nasıl ameller işleyeceksiniz.'),
(1379, 10, 15, 'Böyle iken, âyetlerimiz, kesin birer belge olarak kendilerine okunduğu zaman, o bizimle karşılaşmayı ummayanlar, "Bundan başka bir Kur\'ân getir veya bunu değiştir." dediler. De ki, "Onu kendiliğimden değiştiremem, benim açımdan bu olacak bir şey değildir. Ben ancak bana vahyolunana uyarım. Rabbime isyan edersem, şüphesiz büyük bir günün azabından korkarım."'),
(1380, 10, 16, 'De ki, "Eğer Allah dileseydi ben onu size okumazdım. O da onu hiçbir şekilde size bildirmezdi. Bilirsiniz ki, ben sizin içinizde bundan önce yıllarca bulundum. Siz hâlâ aklınızı başınıza toplamayacak mısınız?"'),
(1381, 10, 17, 'Artık bir yalanı Allah\'a iftira eden veya O\'nun âyetlerini inkar edenden daha zalim kim olabilir? Hiç şüphesiz o mücrimler iflah olmayacaklar.'),
(1382, 10, 18, 'Allah\'ı bırakıyorlar da, kendilerine ne fayda, ne de zarar verebilecek olan şeylere tapıyorlar ve "Bunlar bizim Allah katında şefaatçilerimizdir." diyorlar. De ki, "Siz Allah\'a göklerde ve yerde O\'nun bilmediği bir şeyi mi haber veriyorsunuz?" Allah onların ortak koştukları şeylerin hepsinden münezzehtir.'),
(1383, 10, 19, 'İnsanlar, aslında bir tek ümmet idiler, sonra ihtilafa düşüp ayrı ayrı oldular. Eğer Rabbinden bir karar çıkmamış olsa idi, ihtilaf edip durdukları şeyler hakkında şimdiye kadar aralarında çoktan hüküm verilmiş olurdu.'),
(1384, 10, 20, 'Bir de "Ona Rabbinden daha başka bir âyet indirilse ya!" diyorlar. De ki: "Gaybı bilmek ancak Allah\'a mahsustur, bekleyiniz bakalım, ben de sizinle beraber bekleyeceğim şüphesiz."'),
(1385, 10, 21, 'İnsanlara dokunan bir sıkıntıdan sonra kendilerine bir rahmet tattırdığımız zaman, âyetlerimiz hakkında derhal bir takım hilekârlıklara girişirler. De ki: "Allah\'ın hilesi daha çabuktur. Haberiniz olsun ki elçilerimiz yaptığınız hileleri yazıp duruyorlar".'),
(1386, 10, 22, 'Sizi karada ve denizde gezdirip dolaştıran O\'dur. Hatta gemilerde bulunduğunuz ve o gemiler, içindekilerle beraber hoş bir esinti ile akıp gittikleri ve tam keyiflendikleri sırada o gemilere şiddetli bir fırtına gelir çatar ve her taraftan onlara dalgalar gelmeye başlar. Bütünüyle kuşatılıp artık bittiklerini sanırlar. İşte o vakit tam ihlas ile Allah\'a yalvarır ve dindar olurlar: "Eğer bizi buradan kurtarırsan, andolsun ki, şükredenlerden olacağız." derler. Sonra Allah onları oradan kurtarır, kurtulur kurtulmaz yeryüzünde çeşitli taşkınlıklara başlarlar. Ey insanlar taşkınlığınız sırf kendi zararınızadır. Şu değersiz dünya hayatının bir süre tadını çıkarınız, sonra nasıl olsa dönüp bize geleceksiniz. Biz de bütün yaptıklarınızı tek tek size haber vereceğiz.'),
(1387, 10, 23, 'Sonra Allah onları oradan kurtarır, kurtulur kurtulmaz yeryüzünde çeşitli taşkınlıklara başlarlar. Ey insanlar taşkınlığınız sırf kendi zararınızadır. Şu değersiz dünya hayatının bir süre tadını çıkarınız, sonra nasıl olsa dönüp bize geleceksiniz. Biz de bütün yaptıklarınızı tek tek size haber vereceğiz.'),
(1388, 10, 24, 'Dünya hayatının misali şöyledir: Gökten indirdiğimiz su ile, insanların ve hayvanların yediği bitkiler birbirine karışmıştır. Nihayet yeryüzü süslerini takınıp süslendiği ve sahipleri kendilerini ona gücü yeter sandıkları bir sırada, geceleyin veya gündüzün, ona emrimiz gelivermiştir, ansızın ona öyle bir tırpan atıvermişiz de sanki bir gün önce orada hiçbir şenlik yokmuş gibi oluvermiştir. Düşünen bir kavim için âyetlerimizi işte böyle açıklarız.'),
(1389, 10, 25, 'Allah, selamet yurduna çağırıyor ve dilediğini de doğru yola hidayet ediyor.'),
(1390, 10, 26, 'İyi iş, güzel amel yapanlara daha güzeli ve daha fazlasıyla karşılık vardır. Yüzlerine ne kara bulaşır, ne de aşağılanırlar. Cennet ehli işte bunlardır. Orada ebedî kalacaklardır.'),
(1391, 10, 27, 'Kötülük kazanmış olanlara gelince, kötülüğün cezası, misli kadardır. Ve onları bir aşağılık ve eziklik kaplar. Onlar için Allah\'dan başka hiçbir kurtarıcı yoktur. Yüzleri karanlık gecelerden bir parçaya bürünmüş gibidir. İşte onlar cehennem ehlidir. Orada ebedî kalacaklardır.'),
(1392, 10, 28, 'O gün ki, hepsini mahşere toplayacağız, sonra da o şirk koşanlara "Haydi yerlerinize! Siz de, ortak koştuklarınız da!" diyeceğiz. Artık aralarını iyice açmışız. O ortak koştukları şeyler, "Siz bize tapmıyordunuz ki." diyecekler.'),
(1393, 10, 29, '"Şimdi sizinle bizim aramızda şahit olarak Allah yeter. Sizin bize ibadet ettiğinizden bizim haberimiz yoktur" (diyecekler).'),
(1394, 10, 30, 'İşte burada herkes geçmişte yaptığını bulacak. Ve gerçek mevlaları olan Allah\'a döndürülecekler. İftira edip uydurdukları şeyler de kendilerinden büsbütün uzaklaşıp gidecek.'),
(1395, 10, 31, 'De ki, "size gökten ve yerden kim rızık veriyor? O, kulaklara ve gözlere hükmeden kim? Ölüden diriyi, diriden ölüyü çıkaran kim? İşleri idare eden kim?" Hemen "Allah\'dır" diyecekler. De ki, "O halde Allah\'a karşı gelmekten sakınmaz mısınız?"'),
(1396, 10, 32, 'İşte o Allah sizin gerçek Rabbinizdir. Gerçeğin dışında sapıklıktan başka ne vardır? O halde haktan nasıl çevriliyorsunuz?'),
(1397, 10, 33, 'Hak dinden çıkmış fasıklara Rabbinin kelimesi şöyle gerçekleşti: Onlar artık imana gelmezler.'),
(1398, 10, 34, 'De ki: "Allah\'a eş tuttuğunuz ortaklarınızdan, önce yaratıp, sonra da onu çevirip yeniden diriltecek var mı?" De ki, "Önce yaratıp, sonra da onu yeniden yaratacak olan Allah\'dır. O halde nasıl yoldan saptırılıyor, döndürülüyorsunuz?"'),
(1399, 10, 35, 'De ki, "Ortak koştuklarınızdan doğru yolu gösterecek olan var mıdır?" Deki, "Allah, hak olan doğru yola hidayet eder. O halde doğru yola hidayet eden mi kendisine uyulmaya daha layıktır, yoksa kendisine yol gösterilmeyince onu bulamayan mı daha layıktır. O halde ne oluyorsunuz? Nasıl hükmediyorsunuz?"'),
(1400, 10, 36, 'Onların birçoğu zandan başka bir şeye uymaz. Zan ise haktan hiç bir şeyin yerini tutmaz. Şüphesiz ki, Allah onların ne yaptıklarını bilir.'),
(1401, 10, 37, 'Bu Kur\'ân, Allah\'dan başkası tarafından uydurulamaz, lâkin kendinden önceki kitapları tasdik eder ve o kitabı (levhi mahfuzu) ayrıntılı olarak açıklar. Onda şüphe edilecek hiç bir şey yoktur. Âlemlerin Rabbi tarafından indirilmiştir.'),
(1402, 10, 38, '"Onu o (peygamber) uydurdu" mu diyorlar? De ki; "Haydi siz de onun gibi bir sûre getirin ve Allah\'dan başka, çağırabileceğiniz kim varsa onu da yardıma çağırın. Eğer sözünüzde sadık iseniz (bunu yapın).'),
(1403, 10, 39, 'Hayır. Onlar bilgileriyle kavrayamadıkları, te\'vili de kendilerine hiç gelmemiş olan bir şeyi yalan saydılar. Bunlardan önce gelip geçenler de yine böyle inkâr etmişlerdi, amma bak zalimlerin akıbeti nasıl oldu.'),
(1404, 10, 40, 'Onlardan ona (Kur\'ân\'a) inanacaklar da var, inanmayacaklar da var. Rabbin fesatçıları en iyi bilendir.'),
(1405, 10, 41, 'Eğer seni inkâr etmeyi sürdürürlerse, de ki; "Benim amelim bana, sizin ameliniz de size aittir. Benim yapacağım sizi ilgilendirmez, sizin yapacağınız da beni ilgilendirmez."'),
(1406, 10, 42, 'İçlerinden seni dinlemeye gelenler de var. Sen, sağırlara, üstelik akılsız da olanlara dinletebilir misin?'),
(1407, 10, 43, 'İçlerinden sana bakanlar da var. Fakat sen, körlere, üstelik basiretleri de yoksa hidayet edip yol gösterebilecek misin?'),
(1408, 10, 44, 'Şurası kesindir ki Allah, insanlara zerre kadar zulmetmez. Ne var ki, insanlar kendi kendilerine zulmedip duruyorlar.'),
(1409, 10, 45, 'Allah\'ın onları haşredip toplayacağı günde, sanki onlar dünyada gündüz bir parça kalmışlar da aralarında tanışmışlar gibi olacak. Allah\'ın huzuruna çıkacaklarına inanmamış ve doğru yolu tutmamış olanlar hiç şüphesiz en büyük ziyana uğramış olacaklar.'),
(1410, 10, 46, 'Onlara vaad ettiğimizin bir kısmını sana göstersek de, göstermeden seni vefat ettirsek de, sonunda onların dönüşü bize olacak. Sonra onların ne yapacaklarına Allah şahit olacaktır.'),
(1411, 10, 47, 'Her ümmetin bir peygamberi vardır. O peygamberleri gelince aralarında adaletle hüküm verilir. Onlar hiç zulüm görmezler.'),
(1412, 10, 48, 'Onlar, "Eğer doğru söylüyorsanız bu vaad ne zaman yerine gelecek?" diyorlar.'),
(1413, 10, 49, 'De ki, "Ben, Allah\'ın dilediğinin dışında kendi kendime ne bir zarar ne bir fayda verebilirim". Her ümmetin bir eceli vardır. Ecelleri gelince artık ne bir an geri, ne bir an ileri gidebilirler.'),
(1414, 10, 50, 'De ki: "O\'nun azabı size geceleyin uykuda veya güpe gündüz gelecek olsa, ne dersiniz? Günahkârların onu alelacele istemeleri için ne sebep vardır?"'),
(1415, 10, 51, 'Bu azap meydana geldikten sonra mı iman edeceksiniz, yoksa şimdi mi? Halbuki onun çarçabuk gelmesini istiyordunuz.'),
(1416, 10, 52, 'Sonra o zulüm yapanlara "Tadın bakalım şu ebedi azabı!" denilecek. Vaktiyle kazandığınızdan başkası ile mi cezalandırılacaksınız?"'),
(1417, 10, 53, '"O azap gerçek mi?" diye sana soruyorlar. De ki; "Evet. Rabbim hakkı için o kesin bir gerçektir. Ve siz bundan yakayı kurtaramazsınız."'),
(1418, 10, 54, 'Zulüm yapmış olan herkes, azabı görünce yeryüzündeki her şeyin sahibi olsa da, (o azaptan kurtulmak için) hepsini feda ederdi. Ve içten içe pişmanlık duyardı. Fakat aralarında adaletle hüküm verilir ve hiçbirine zulüm yapılmaz.'),
(1419, 10, 55, 'Haberiniz olsun ki, göklerde ve yerde ne varsa Allah\'ındır. Açın gözünüzü, Allah\'ın vaadi muhakkak ki, haktır, gerçektir. Lâkin onların çoğu bunu bilmezler.'),
(1420, 10, 56, 'O, hem can veren, hem can alandır. Ve hepiniz O\'na döndürülüp götürüleceksiniz.'),
(1421, 10, 57, 'Ey insanlar! Size Rabbinizden bir öğüt, gönüller derdine bir şifa, müminlere bir hidayet ve rahmet geldi.'),
(1422, 10, 58, 'De ki, "Allah\'ın ihsanıyla ve rahmetiyle, yalnızca bunlarla sevinç duysunlar. Bu, onların biriktirip durduklarından daha hayırlıdır."'),
(1423, 10, 59, 'De ki, "Baksanıza, Allah sizin için nice rızıklar indirdi, siz onlardan bir kısmını haram, bir kısmını helâl yaptınız". De ki, "Size Allah mı izin verdi, yoksa siz Allah\'a iftira mı ediyorsunuz?"'),
(1424, 10, 60, 'Allah\'a yalanı iftira edenler kıyamet gününü ne sanıyorlar? Allah, insanlara çok ihsanda bulunmuştur, lâkin insanların çoğu şükretmezler.'),
(1425, 10, 61, 'Hangi işi yaparsan yap, Kur\'ân\'dan ne okursan oku, ne işte çalışırsan çalış, unutmayın ki, siz ona dalıp gitmişken, biz sizin üzerinizde şahidiz. Ne yerde, ne de gökte zerre kadar hiç bir şey Rabbinin gözünden kaçmaz. Ne zerreden daha küçük, ne de ondan daha büyük! Ancak bunların hepsi apaçık bir kitaptadır.'),
(1426, 10, 62, 'Açın gözünüzü! Allah\'ın dostları üzerine ne korku vardır, ne de onlar mahzun olurlar.'),
(1427, 10, 63, 'Onlar ki, iman etmişler ve Allah\'a karşı gelmekten sakınmışlardır.'),
(1428, 10, 64, 'Onlara dünya hayatında da, ahiret hayatında da müjdeler vardır. Allah\'ın sözlerinde değişiklik yoktur. İşte bu en büyük kurtuluştur.'),
(1429, 10, 65, 'Habibim, onların lafları seni üzmesin. Çünkü şan ve şeref bütünüyle Allah\'ındır. O her şeyi işitiyor, hepsini görüyor.'),
(1430, 10, 66, 'Açın gözünüzü! Göklerde kim var, yerde kim varsa hep Allah\'ındır. Allah\'dan başkasına tapanlar dahi, Allah\'a ortak koştuklarına uymuş olmuyorlar, ancak zanna uymuş oluyorlar. Ve yalandan başka bir şey söylemiyorlar.'),
(1431, 10, 67, 'O, öyle bir Allah\'dır ki, içinde dinlenesiniz diye sizin için geceyi, göresiniz diye de gündüzü yaptı. Elbette bunda söz dinleyecek olan bir kavim için âyetler (ibretler) vardır.'),
(1432, 10, 68, 'Dediler ki: "Allah, kendine çocuk edindi". O, böyle şeylerden münezzehtir. O, müstağnidir. Göklerde ve yerde ne varsa hepsi O\'nundur. Bu hususta elinizde hiç bir delil yoktur. Allah\'a karşı bilmediğiniz bir şeyi neden söylüyorsunuz?'),
(1433, 10, 69, 'De ki: Allah\'a iftira edenler elbette felah bulmazlar.'),
(1434, 10, 70, 'Dünyadaki zevkler çabuk biter. Sonra dönüşleri bize olacaktır. Daha sonra da inkâr ettiklerinden dolayı o çetin azabı biz onlara tattıracağız.'),
(1435, 10, 71, 'Bir de onlara Nuh\'un kıssasını oku: Hani o bir zamanlar kavmine demişti ki: "Ey kavmim, eğer benim aranızda duruşum ve Allah\'ın âyetleriyle öğüt verişim size ağır geliyorsa, şunu bilin ki, ben yalnızca Allah\'a dayanmışımdır, artık siz ve ortaklarınız her ne yapacaksanız toplanıp bütün gücünüzle karar veriniz. Sonra bu işiniz size dert olmasın. Sonra bana ne yapacaksanız yapın, bana mühlet de vermeyin".'),
(1436, 10, 72, 'Eğer yüz çevirirseniz çevirin, ben de sizden bir ücret istemedim ya! Benim mükafatımı ancak Allah verir. Ve ben O\'nun emrine boyun eğen müslümanlardan olmakla emrolundum.'),
(1437, 10, 73, 'Buna rağmen yine de onu inkâr ettiler. Biz de onu ve gemide kendisiyle beraber olanları kurtardık. Ve onları yeryüzüne halifeler yaptık. Âyetlerimizi inkâr edenleri ise suda boğduk. Bak işte uyarılanların akıbeti nasıl oldu.'),
(1438, 10, 74, 'Sonra onun arkasından birçok peygamberleri kavimlerine gönderdik. Onlara açık mucizelerle geldiler. Fakat onlar bir defa yalan dediklerine sonuna kadar bir türlü inanmadılar. İşte biz, haddi aşanların kalblerini böyle mühürleriz.'),
(1439, 10, 75, 'Sonra bunların arkasından Musa ile Harun\'u âyetlerimizle Firavun\'a ve cemaatine gönderdik. İman etmeyi kibirlerine yediremediler ve günahkâr bir kavim oldular.'),
(1440, 10, 76, 'Kendilerine tarafımızdan hak gelince, "Muhakkak ki bu, apaçık bir sihirdir." dediler.'),
(1441, 10, 77, 'Musa dedi ki, "Size hak gelince, ona böyle mi diyorsunuz? Bu sihir midir?" Halbuki sihirbazlar iflah olmazlar.'),
(1442, 10, 78, 'Dediler ki: "Sen bizi, atalarımızdan kalan yoldan çeviresin de yeryüzünde saltanat ikinizin olsun diye mi geldin? Biz ikinize de inanmayız".'),
(1443, 10, 79, 'Firavun da: "Bana bütün bilgili sihirbazları toplayıp getirin!" dedi.'),
(1444, 10, 80, 'Sihirbazlar gelince, Musa onlara: "Ortaya ne atacaksanız atın!" dedi.'),
(1445, 10, 81, 'Onlar ortaya atınca Musa dedi ki, "Sizin yaptığınız şey sihirdir. Muhakkak ki, Allah onu iptal edecektir. Şüphe yok ki, Allah fesatçıların işlerini düze çıkarmaz."'),
(1446, 10, 82, 'Allah, hakkın hak ve gerçek olduğunu kelimeleriyle ispat eder, günahkârların hoşuna gitmese de'),
(1447, 10, 83, 'Firavun ve adamlarının kendilerini belaya uğratacağı korkusundan dolayı Musa\'ya kendi kavminin bir oymağından başka kimse iman etmedi. Çünkü orada Firavun çok üstün idi ve o kesinlikle aşırı giden taşkınlardandı.'),
(1448, 10, 84, 'Musa dedi ki: "Ey kavmim! Siz gerçekten Allah\'a iman ettinizse, O\'na samimiyetle teslim olan müslümanlardan oldunuzsa artık O\'na güvenin!"'),
(1449, 10, 85, 'Onlar da: "Biz Allah\'a güvendik. Ey Rabbimiz, bizi o zalim kavmin fitnesine uğratma!" dediler.'),
(1450, 10, 86, '"Bizi rahmetinle o kâfir kavmin elinden kurtar!"'),
(1451, 10, 87, 'Biz Musa ile kardeşine şöyle vahyettik: "Kavminiz için Mısır\'da birtakım evler hazırlayın ve evlerinizi kıbleye karşı yapın ve namazı kılın ve müminlere müjde verin."'),
(1452, 10, 88, 'Musa dedi: "Ey Rabbimiz! Sen Firavun\'a ve adamlarına şu dünya hayatında göz kamaştırıcı zenginlik ve bol bol servet verdin. Ey Rabbimiz! Senin yolundan saptırsınlar diye mi? Ey Rabbimiz! Onların mallarını sil süpür ve kalblerine sıkıntı düşür. Çünkü onlar o acıklı azabı görmedikçe iman etmeyecekler."'),
(1453, 10, 89, 'Allah buyurdu: "Her ikinizin de duası kesinlikle kabul olundu. Siz yine doğru ve dürüst olmaya devam edin. Kendini bilmeyenlerin yoluna sakın uymayın."'),
(1454, 10, 90, 'Ve sonra İsrailoğulları\'nı denizden aşırdık. Firavun, düşmanca saldırmak için derhal adamlarını ve askerlerini arkalarına düşürdü. Ta ki, suda boğulmaya başlayınca "İnandım, gerçekten de İsrailoğulları\'nın iman ettiğinden başka tanrı yoktur. Ben de ona teslim olanlardanım." dedi.'),
(1455, 10, 91, 'Şimdi mi? Oysa bundan önce hep isyan etmiştin ve fesatçılardan idin.'),
(1456, 10, 92, 'Biz de bugün senin bedenini arkandan gelenlere bir ibret olsun diye kurtaracağız. Bununla beraber, insanların birçoğu âyetlerimizden yine de gafildirler.'),
(1457, 10, 93, 'Gerçekten İsrailoğulları\'nı çok güzel bir yurda yerleştirdik ve onlara hoş nimetlerden rızıklar verdik. Anlaşmazlığa düşmeleri de kendilerine ilim geldikten sonra oldu. Şüphe yok ki, Rabbin, o anlaşmazlığa düştükleri konularda kıyamet günü aralarında hüküm verecektir.'),
(1458, 10, 94, 'Sana indirdiklerimizde herhangi bir şüpheye düşersen, senden önce kitap okuyanlara sor. Andolsun ki, sana Rabbinden hak gelmiştir. Sakın şüphe edenlerden olma!'),
(1459, 10, 95, 'Ve sakın Allah\'ın âyetlerini inkar edenlerden olma, sonra hüsrana uğrayanlardan olursun.'),
(1460, 10, 96, 'Doğrusu, aleyhlerinde Rabbinin hükmü kesinleşmiş olanlar imana gelmezler.'),
(1461, 10, 97, 'Onlara bütün mucizeler hep birden gelse, yine de o acıklı azabı görünceye kadar inanmazlar.'),
(1462, 10, 98, 'Fakat o vakit iman edip de imanları kendilerine fayda vermiş bir kasaba olsaydı? Ancak Yunus\'un kavmi iman ettikleri vakit, dünya hayatında o rezillik azabını üzerlerinden kaldırmış ve bir süre onları rahata kavuşturmuştuk.'),
(1463, 10, 99, 'Eğer Rabbin dileseydi, yeryüzünde kim varsa hepsi toptan iman ederlerdi. O halde insanları hep mümin olsunlar diye sen mi zorlayacaksın?'),
(1464, 10, 100, 'Allah\'ın izni olmadıkça hiçbir kişinin iman etmesi mümkün değildir. Akıllarını kullanmayanlar üzerine Allah bir uğursuzluk yükler.'),
(1465, 10, 101, 'De ki: "Göklerde ve yerde olup bitenlere dikkatle bakın!" Fakat o uyarmalar ve o âyetler, iman etmeyen bir kavme fayda vermez ki!'),
(1466, 10, 102, 'Onlar, kendilerinden önce gelmiş geçmiş olanların uğradıkları felaket günleri gibisinden başkasını mı bekliyorlar? De ki, "Bekleyin, ben de sizinle beraber bekleyenlerden olacağım."'),
(1467, 10, 103, 'Sonra biz, peygamberlerimizi ve iman edenleri kurtarırız. İşte biz böyleyiz. Müminleri kurtarmak üzerimize düşen bir görevdir.'),
(1468, 10, 104, 'De ki: "Ey insanlar! Eğer benim dinimde bir şüpheniz varsa, şunu bilin ki, Allah\'ı bırakıp da sizin taptıklarınıza tapmam. Lâkin sizin de canınızı alacak olan Allah\'a taparım. Bana müminlerden olmam emredilmiştir".'),
(1469, 10, 105, '"Ayrıca yüzünü tevhid dininden ayırma ve sakın müşriklerden olma!" (diye emrolundum).'),
(1470, 10, 106, '"Ve Allah\'dan başka, sana faydası da, zararı da dokunmayacak olan şeylere yalvarma! Eğer yalvarırsan, o zaman hiç şüphesiz sen zalimlerden olursun.'),
(1471, 10, 107, 'Ve eğer Allah, sana bir zarar dokunduracak olursa, onu O\'ndan başka giderecek yoktur. Ve eğer sana bir hayır dilerse, o zaman da O\'nun hayrını engelleyebilecek kimse yoktur. O, lütfunu dilediği kuluna nasip eder. Allah çok yarlığayıcı, çok esirgeyicidir.'),
(1472, 10, 108, 'De ki: "Ey insanlar! İşte size Rabbinizden hak geldi. Artık kim hidayeti kabul ederse kendi canı için kabul etmiş olur. Kim sapıklık ederse kendi zararına sapıklık etmiş olur. Ve ben sizin üzerinize vekil değilim."'),
(1473, 10, 109, 'Sana vahyolunana uy! Ve Allah hükmünü verinceye kadar sabret. Çünkü O, hüküm verenlerin en hayırlısıdır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'ElifLâmRâ. Bu öyle bir kitaptır ki, âyetleri muhkem kılınmış, sonra da herşeyden haberdar olan hikmet sahibi Allah tarafından âyetleri ayrıntılı olarak açıklanmıştır.'),
(1475, 11, 2, '(Şöyle ki:) Allah\'dan başkasına kulluk etmeyin. Ben size O\'nun tarafından müjde vermek ve uyarmak için gönderilmiş gerçek bir peygamberim.'),
(1476, 11, 3, 'Ve Rabbinizin mağfiretini isteyin, sonra ona tevbe edin ki sizi, belli bir süreye kadar güzel güzel yaşatsın. Ve her fazilet sahibine layık olduğu ihsanı versin. Eğer yüz çevirirseniz, ben sizin için büyük bir günün azabından korkarım.'),
(1477, 11, 4, 'Dönüşünüz yalnızca Allah\'adır. O\'nun da herşeye gücü yeter.'),
(1478, 11, 5, 'Dikkat edin! Görmüyor musunuz, onlar düşmanlıklarını gizlemek için göğüslerini çeviriyorlar. İyi bilin ki, onlar örtülerine bürünürlerken, neyi gizleyip, neyi açığa vurduklarını Allah biliyor. Muhakkak ki Allah, gönülde gizlenenleri de bilir.'),
(1479, 11, 6, 'Yeryüzünde rızkı Allah\'a ait olmayan hiçbir canlı yoktur. O, onların karar kıldıkları yerleri de, emaneten durdukları yerleri de bilir. Onların hepsi apaçık bir kitaptadır.'),
(1480, 11, 7, 'O, öyle bir Allah\'dır ki, hanginizin daha güzel amel işleyeceğini imtihan etmek için gökleri ve yeri altı günde yarattı. Arşı da su üstündeydi. Onlara "öldükten sonra tekrar dirileceksiniz" dersen, o kâfirler de kesinlikle sana: " Bu apaçık bir sihirden başka birşey değildir." diyecekler.'),
(1481, 11, 8, 'Ve eğer bunlardan bir kısmının göreceği azabı belli bir süreye kadar erteleyecek olursak, o zaman da "onu engelleyen nedir ki?" diyecekler. İyi bilin ki, o azap onlara geldiği gün kendilerinden geri çevrilecek değildir. Ve o alay ettikleri şey kendilerini kuşatmış olacaktır.'),
(1482, 11, 9, 'Ve şayet insana tarafımızdan bir rahmet tattırır, sonra da onu kendisinden geri alırsak, şüphesiz o ümitsiz ve nankör bir kimse olur.'),
(1483, 11, 10, 'Ve şayet ona dokunan bir sıkıntıdan sonra bir nimet tattırırsak, "Artık benden bütün kötülükler silinip gitti." der, mutlaka böbürlenir ve şımarır.'),
(1484, 11, 11, 'Ancak (her iki halde de) sabır gösterip iyi ameller işleyenler müstesnadır. İşte onlara bir mağfiret ve büyük bir mükafat vardır.'),
(1485, 11, 12, '(Ey Resulüm!) Şimdi belki sen, "Ona bir hazine indirilse, ya da beraberinde bir melek gezip dolaşsa ya!" diyorlar diye sana vahyolunan vahyin bir kısmını terkedecek olursun ve bundan dolayı da göğsün daralır. Sen yalnızca bir uyarıcısın. Allah ise her şeye vekildir.'),
(1486, 11, 13, 'Yoksa "onu kendi uydurdu" mu diyorlar? O halde sen de onlara de ki: "Haydi siz de onun gibi uydurulmuş on sûre getirin. Allah\'dan başka çağırabileceğiniz kim varsa onları da yardıma çağırın. Eğer doğru söylüyorsanız" (bunu yaparsınız).'),
(1487, 11, 14, 'Yok eğer bunun üzerine size cevap vermedilerse, artık bilin ki, bu Kur\'ân ancak Allah\'ın ilmiyle indirilmiştir. O\'ndan başka ilâh yoktur. Artık müslüman oluyorsunuz, değil mi?'),
(1488, 11, 15, 'Her kim dünya hayatını ve güzelliklerini isterse biz onlara amellerinin karşılığını orada tamamen öderiz. Bu hususta kendilerine bir densizlik yapılmaz.'),
(1489, 11, 16, 'Fakat onlar öyle kimselerdir ki, ahirette kendilerine ateşten başka bir şey yoktur. İşledikleri şeyler orada boşuna gitmiştir. Zaten bütün yaptıkları da batıldır.'),
(1490, 11, 17, 'O dünyayı isteyenler, hiç Rabbinden açık bir belge üzere olan kimse gibi midir? O belgeyi yine Allah\'dan gelen bir şahid olarak Kur\'ân izliyor, ondan önce de bir rehber ve rahmet olan kitap, Musa\'nın kitabı yine onu destekliyor. Böyle olanlar Kur\'ân\'a inanırlar. Hangi hizipten olursa olsun kim onu inkâr ederse, ona vaad edilen yer ateştir. İşte bütün bunlardan dolayı sen de bu Kur\'ân\'dan şüphe içinde olma. Kesinlikle o haktır, Rabbindendir. Fakat insanların çoğu iman etmezler.'),
(1491, 11, 18, 'Üstelik bir yalanı Allah\'a iftira edenden daha zalim kim olabilir? Bunlar Rablerinin huzuruna arzolunacaklar, şahitler de şöyle diyecekler: "İşte bunlar Rablerine karşı yalan söyleyenlerdir". İyi bilin ki: Allah\'ın laneti zalimlerin üzerinedir.'),
(1492, 11, 19, 'Onlar ki, Allah yolundan döndürmeye çalışırlar ve o yolu eğri büğrü yapmak isterler. Üstelik onlar, evet onlar ahirete de inanmazlar.'),
(1493, 11, 20, 'Onlar yeryüzünde (herkesi) yıldıracak değillerdir. Kendilerini koruyacak Allah\'dan başka kimseleri de yoktur. Onların azabı kat kat olacaktır. Üstelik onlar hakkı işitmeye tahammül edemiyorlardı ve de görmüyorlardı.'),
(1494, 11, 21, 'Onlar kendilerine yazık etmiş olan kimselerdir. O iftira edip uydurdukları da kendilerinden yüz çevirip gitmişlerdir.'),
(1495, 11, 22, 'Kesinlikle bunlar ahirette de en ziyade hüsrana uğrayacak olanlardır.'),
(1496, 11, 23, 'Fakat iman edip salih amel işleyenler ve Rablerine karşı edepli olanlar, güvenen ve itaat edenler var ya, işte bunlar da cennet ehlidirler. Onlar orada ebedi kalırlar.'),
(1497, 11, 24, 'Bu iki ayrı grubun meseli, kör ve sağır ile gören ve işiten gibidir. Bunlar hiç eşit olabilirler mi? Hâlâ düşünmeyecek misiniz?'),
(1498, 11, 25, 'Andolsun ki, vaktiyle Nuh\'u da kavmine gönderdik, O, onlara şöyle dedi: "Ben sizin için apaçık bir uyarıcıyım."'),
(1499, 11, 26, '"Allah\'dan başkasına ibadet etmeyin! Ben, size gelecek acı bir günün azabından korkarım."'),
(1500, 11, 27, 'Buna karşılık, kavminin ileri gelen kâfirlerinden bir kısmı dediler ki: "Biz seni bizim gibi insanlardan biri olarak görüyoruz, başka değil. İlk bakışta bizim ayak takımımızdan başkasının senin arkana düştüğünü görmüyoruz. Sizin bizden fazla bir meziyetinizi de görmüyoruz. Aksine sizi yalancılar sanıyoruz."'),
(1501, 11, 28, 'Nuh dedi ki; "Ey kavmim! Peki şu söyleyeceğime ne diyeceksiniz? Ben Rabbimden apaçık bir delil üzere isem ve O, bana kendi tarafından bir rahmet bahşetmişse, size de onu görecek göz verilmemişse biz, istemediğiniz halde onu size zorla mı kabul ettireceğiz?"'),
(1502, 11, 29, '"Ey kavmim! Ben sizden herhangi bir mal mülk istemiyorum. Benim mükafatım ancak Allah\'a aittir. Ve ben ona iman edenleri kovacak değilim. Onlar elbette Rablerine kavuşacaklar. Fakat ben de sizi cahillik eden bir kavim görüyorum."'),
(1503, 11, 30, '"Ey kavmim, ben onları etrafımdan kovacak olursam, Allah\'dan beni kim kurtarabilir? Siz hiç düşünmez misiniz?"'),
(1504, 11, 31, 'Ben size "Allah\'ın hazineleri benim yanımdadır." demiyorum ki. Ben size "Ben bir meleğim." de demiyorum. O sizin kendinize göre, hor gördükleriniz hakkında "Allah onlara hiçbir hayır vermez." de demiyorum. Onların içlerindeki niyeti, en iyi Allah bilir. (Bu söylediklerimin aksini iddia etseydim) asıl o zaman zalimlerden olurdum.'),
(1505, 11, 32, 'Dediler ki; "Ey Nuh! Bizimle didişip durdun, didişmende de çok ileri gittin. Eğer doğru söylüyorsan, bizi tehdit ettiğin şu azabı getir de görelim."'),
(1506, 11, 33, 'Nuh dedi ki; "Onu ancak Allah dilerse getirir. Ve siz O\'nu yıldıracak değilsiniz."'),
(1507, 11, 34, 'Ben size öğüt vermek istemiş olsam da, eğer Allah sizi helâk etmeyi murad ediyorsa, zaten öğüt vermemin size bir faydası olmaz. Rabbiniz O\'dur ve nihayet O\'na döndürüleceksiniz.'),
(1508, 11, 35, 'Yoksa "Onu uydurdu" mu diyorlar? De ki; "Eğer uydurdumsa vebali benim boynumadır. Bense sizin yüklendiğiniz vebalden uzağım".'),
(1509, 11, 36, 'Ayrıca Nuh\'a şöyle vahyettik: "Bil ki kavminden şimdiye kadar iman etmiş olanlardan başka artık kimse iman etmeyecektir. Onun için yaptıkları şeylerden dolayı kederlenme."'),
(1510, 11, 37, 'Bizim gözetimimiz altında ve vahyimize göre gemiyi yap. Zulüm yapanlar hakkında da bana bir şey söyleme. Çünkü onlar kesinlikle suda boğulacaklardır.'),
(1511, 11, 38, 'Gemiyi yapıyordu, kavminden bazı ileri gelen gruplar, onun yanından gelip geçtikçe, onunla alay ediyorlardı. Nuh dedi ki: "Bizimle eğleniyorsunuz, biz de sizinle tıpkı bizimle eğlendiğiniz gibi alay edip eğleneceğiz."'),
(1512, 11, 39, 'O perişan edici azabın kime geleceğini ve o sürekli azabın kimin başına ineceğini ilerde bileceksiniz.'),
(1513, 11, 40, 'Nihayet emrimiz geldiği ve tennur (tandır veya geminin kazanı) tutuşup parladığı zaman dedik ki; "Erkeği ve dişisi olan her canlıdan ikişer tane, aleyhlerinde hüküm verilmiş olanların dışında, aileni ve iman etmiş olanları geminin içine yükle". Zaten beraberinde iman edenler çok az idi.'),
(1514, 11, 41, 'Nuh dedi ki; "Allah\'ın adıyla binin içine. Onun akışı da, duruşu da (O\'nun adıyladır). Hiç şüphesiz Rabbim gerçekten çok bağışlayıcı, çok esirgeyicidir.'),
(1515, 11, 42, 'Gemi içindekilerle birlikte, dağlar gibi dalgalar arasında akıp gidiyordu. Nuh ayrı bir yere çekilmiş olan oğluna bağırdı: "Yavrucuğum, gel, bizimle beraber bin! Kâfirlerle beraber olma!"'),
(1516, 11, 43, 'O, dedi ki; "Ben, beni sudan koruyacak bir dağa çıkacağım". Nuh da "Bu gün Allah\'ın merhamet ettiğinden başkasını, Allah\'ın bu emrinden koruyacak kimse yoktur." dedi. Derken dalga aralarına giriverdi. O da boğulanlardan oldu.'),
(1517, 11, 44, 'Allah tarafından denildi ki: "Ey yeryüzü suyunu yut! Ey gökyüzü sen de suyunu kes! Ve sular çekildi. Emir yerine gelmiş oldu. Gemi de Cudi dağı üzerine oturdu. O zalim kavme böylece dünyadan uzak olun denildi.'),
(1518, 11, 45, 'Nuh Rabbine niyaz edip dedi ki: "Ey Rabbim! Oğlum benim ehlimdendi senin vaadin de elbette haktır ve gerçektir. Ve sen hakimler hakimisin."'),
(1519, 11, 46, 'Allah: "Ey Nuh! O kesinlikle senin ehlin (âilen)\'den değildir. Çünkü o salih olmayan bir amelin sahibidir. Hakkında bilgin olmayan bir şeyi benden isteme! Ben, seni, cahillerden olmaktan sakındırırım."'),
(1520, 11, 47, 'Nuh: "Ey Rabbim! Ben bilmediğim bir şeyi istemiş olmaktan dolayı sana sığınırım. Sen beni bağışlamazsan, bana merhamet etmezsen ben hüsrana uğrayanlardan olurum.'),
(1521, 11, 48, '"Ey Nuh!" denildi, " Bizden bir selâm sana ve seninle birlikte olanlardan gelecek ümmetlere, kutluluk dileğiyle gemiden in. İlerde kendilerini bir çok nimetten faydalandıracağımız, sonra da bu yüzden kendilerine tarafımızdan acıklı bir azap dokunacaknice ümmetler olacaktır."'),
(1522, 11, 49, 'İşte bunlar gayb haberlerindendir. Bunları sana vahiyle bildiriyoruz. Bundan önce bunları ne sen bilirdin, ne de kavmin. O halde sabret, akıbet muhakkak muttakilerindir.'),
(1523, 11, 50, 'Âd kavmine de kardeşleri Hud\'u gönderdik. Dedi ki: "Ey kavmim! Allah\'a kulluk edin. Sizin O\'ndan başka bir ilâhınız yoktur. Siz sadece iftira edip duruyorsunuz."'),
(1524, 11, 51, '"Ey kavmim! Bu iş için sizden bir ücret istemiyorum. Benim ecrim ancak beni yaratana aittir. Artık akıllanmayacak mısınız?"'),
(1525, 11, 52, '"Ey kavmim! Rabbinizden mağfiret isteyin, sonra O\'na tevbe edin ki, üzerinize gökten bol bol bereket indirsin ve sizi kuvvetinize kuvvet katarak çoğaltsın. Gelin günahkâr olarak dönüp gitmeyin."'),
(1526, 11, 53, 'Dediler ki; "Ey Hud! Sen bize açık bir mucize getirmedin. Biz de senin sözünle tanrılarımızı terk etmeyiz. Ve biz sana inanmayız."'),
(1527, 11, 54, '"Ancak şu kadarını diyebiliriz ki; "tanrılarımızdan bazısı seni fena çarpmış". O da dedi ki; "Allah\'ı şahit tutuyorum, siz de şahid olun ki ben, Allah\'a koştuğunuz ortaklardan uzağım."'),
(1528, 11, 55, '"O\'ndan başka herşeyden uzağım, artık hepiniz toplanın bana istediğiniz tuzağı kurun, sonra hiç bekletmeyin.'),
(1529, 11, 56, '"Ben muhakkak ki, hem benim Rabbim, hem de sizin Rabbiniz olan Allah\'a dayanmaktayım. Yeryüzünde hiçbir canlı yoktur ki, idaresi ve yönetimi O\'nun elinde olmasın. Benim Rabbim, hiç şüphe yok ki, doğru yoldadır."'),
(1530, 11, 57, '"Eğer, yine de yüz çevirirseniz, ben size ne ile gönderilmişsem, işte onu tebliğ ettim. Ayrıca Rabbim, sizin yerinize başka bir kavmi getirir de siz O\'na zerrece zarar veremezsiniz. Hiç şüphesiz O, herşeyi koruyup gözetendir.'),
(1531, 11, 58, 'Ne zaman ki emrimiz geldi, Hud\'u ve beraberindeki iman edenleri, tarafımızdan bir rahmet ile kurtardık, ayrıca onları çok ağır bir azaptan da kurtardık.'),
(1532, 11, 59, 'İşte Âd kavmi buydu. Rablerinin âyetlerini bile bile inkâr ettiler ve peygamberlerine isyan ettiler. Başa geçen her zorbanın emrine uyup arkasından gittiler.'),
(1533, 11, 60, 'Hem bu dünyada, hem de kıyamet gününde bir lânetle izlendiler. Bilin ki, Âd kavmi, gerçekten Rablerini inkâr ettiler. Yine bilin ki, Hud\'un kavmi olan Âd, defolup gittiler.'),
(1534, 11, 61, 'Semud kavmine de kardeşleri Salih\'i gönderdik. Dedi ki, "Ey kavmim! Allah\'a kulluk edin. Sizin O\'ndan başka bir tanrınız daha yoktur. Sizi topraktan O meydana getirdi. Sizi orada ömür sürmeye O memur etti. Bu sebepten O\'nun mağfiretini isteyin, sonra O\'na tevbe edin. Şüphesiz Rabbim yakındır, dualarınızı kabul eder."'),
(1535, 11, 62, 'Dediler: "Ey Salih,! Bundan önce sen bizim içimizde ümit beslenir bir zat idin. Şimdi bizi babalarımızın taptıklarına tapmaktan mı engelliyorsun? Biz, doğrusunu istersen bizi davet ettiğin şeyden kuşkulandıran bir şüphe içindeyiz."'),
(1536, 11, 63, 'Salih dedi: "Ey kavmim! Eğer ben Rabbimden açık bir mucize üzerinde isem ve o bana tarafından bir rahmet bahşetmiş ise, ben Allah\'a isyan ettiğim takdirde beni O\'ndan kim kurtarabilir? Demek ki, siz bana zarar vermekten başka bir şey yapmıyorsunuz."'),
(1537, 11, 64, '"Ey kavmim! İşte şu, Allah\'ın dişi devesi, size bir mucizedir. Bırakın onu Allah\'ın yer yüzünde (otlaklarında) otlasın. Ve ona kötü bir maksatla el sürmeyin, sonra sizi yakın bir azap yakalar."'),
(1538, 11, 65, 'Derken, o deveyi kestiler. Bunun üzerine Salih dedi ki: "Yurdunuzda üç gün daha yaşayın. İşte bu, yalan çıkmayacak olan kesin bir vaaddir."'),
(1539, 11, 66, 'Ne zaman ki, azap emrimiz geldi, Salih\'i ve beraberindeki iman edenleri, tarafımızdan bir rahmet sayesinde kurtardık, üstelik o günün perişanlığından da kurtardık. Hiç şüphesiz Rabbin güçlüdür, mutlak üstündür.'),
(1540, 11, 67, 'O zalimleri, korkunç bir gürültü yakalayıverdi de oldukları yerde çöküp kaldılar.'),
(1541, 11, 68, 'Sanki orada güzel güzel yaşayıp durmamışlardı. Bak işte Semud, gerçekten de Rablerine küfretmişlerdi. Bak işte nasıl yok olup gittiler.'),
(1542, 11, 69, 'Andolsun ki, İbrahim\'e de elçilerimiz (melekler) müjde ile geldiler ve "selâm" dediler, o da "selâm" dedi ve hemen gidip onlara kızartılmış bir buzağı getirdi.'),
(1543, 11, 70, 'Fakat onların o buzağıya el sürmediklerini görünce, tuhafına gitti ve içinde onlara karşı bir korku uyandı. Onlar da "Korkma, biz Lut\'un kavmine gönderildik." dediler.'),
(1544, 11, 71, 'İbrahim\'in karısı ayakta duruyordu bunun üzerine yüzü güldü. Ona İshak\'ı ve İshak\'ın arkasından da Ya\'kub\'u müjdeledik.'),
(1545, 11, 72, '"Vay başıma gelene!" dedi, "Ben bir kocakarıyım, kocam da yaşlı bir adam. Bu gerçekten çok tuhaf bir şey!"'),
(1546, 11, 73, 'Dediler: "Sen Allah\'ın emrine mi şaşıyorsun? Allah\'ın rahmeti ve berekâtı üzerinizdedir. Ey ev halkı! Muhakkak ki O, hamiddir (övülmeye lâyıktır), meciddir (cömertliği boldur)."'),
(1547, 11, 74, 'İbrahim\'den korku iyice geçip gidince, bu müjde de kendisine gelince, bizim (meleklerimiz)le Lut kavmi hakkında tartışmaya girişti:'),
(1548, 11, 75, 'Çünkü İbrahim, çok yumuşak huylu ve çok yufka yürekli (yanık kalbli) idi.'),
(1549, 11, 76, 'Melekler: "Ey İbrahim! Bu konuda bizimle tartışmaktan vazgeç. Çünkü Rabbinin emri kesin olarak geldi ve onlara geri çevrilmesi mümkün olmayan bir azap gelecektir.'),
(1550, 11, 77, 'Ne zaman ki, elçilerimiz Lut\'a geldiler, bunların gelişleri yüzünden Lut fenalaştı, eli ayağı birbirine dolaştı ve "Bu gün çetin bir gündür." dedi.'),
(1551, 11, 78, 'Daha önceleri çirkin işler yapmış olan kavmi harıl harıl koşup geldiler. Lut onlara: "Ey kavmim! İşte size kızlarım, onlar sizin için daha temizdirler. Gelin Allah\'tan korkun, beni misafirlerime rezil rüsvay etmeyin. İçinizde hiç aklı başında bir adam yok mu?" dedi.'),
(1552, 11, 79, 'Onlar: "Sen de bilirsin ki, bizim senin kızlarınla bir ilgimiz yoktur. Sen bizim ne istediğimizi gayet iyi biliyorsun." dediler.'),
(1553, 11, 80, 'Lut dedi: "Ne olurdu size karşı bir kuvvetim olsaydı, ya da çok sarp bir yere sığınabilseydim."'),
(1554, 11, 81, 'Melekler dediler: "Ey Lut! Şundan emin ol ki, biz Rabbinin elçileriyiz. Onlar sana asla zarar veremezler. Sen, gecenin bir kısmı olunca ailenle birlikte hemen buradan çık git. İçinizden hiç kimse geri kalmasın, eşin başka. Çünkü ona da onlara gelecek olan musibet gelecektir. Haberin olsun, helâk zamanları sabah vaktidir. Zaten sabah yakın değil mi?"'),
(1555, 11, 82, 'Ne zaman ki, emrimiz geldi, o ülkenin altını üstüne getirdik ve üzerlerine istif edilip pişirilmiş çamurdan taşlar yağdırdık.'),
(1556, 11, 83, 'Bu taşlar Rabbinin katında damgalanmışlardı. Bunlar zalimlerden uzak şeyler değildir.'),
(1557, 11, 84, 'Medyen\'e de kardeşleri Şu\'ayb\'i gönderdik. Dedi ki: "Ey kavmim! Allah\'a kulluk edin. Sizin O\'ndan başka ilâhınız yoktur. Ölçeği de, teraziyi de eksik tutmayın. Ben sizi hayır (bolluk) içinde görüyorum. Bununla beraber yine de sizi kuşatacak bir gününazabından korkuyorum."'),
(1558, 11, 85, '"Ey kavmim! Ölçerken ve tartarken adaleti yerine getirin. Halkın malına densizlik etmeyin ve yeryüzünde fesatçılık yaparak fenalık etmeyin."'),
(1559, 11, 86, 'Eğer mümin iseniz, Allah\'ın helâlinden size ihsan ettiği kâr sizin için daha hayırlıdır. Bununla beraber ben sizin üzerinize gözcü değilim."'),
(1560, 11, 87, 'Dediler ki; "Ey Şu\'ayb, atalarımızın taptıklarını terketmemizi veya mallarımızda dilediğimizi yapmaktan vazgeçmemizi sana namazın mı emrediyor? Oysa ki sen yumuşak huylusun ve aklı başında bir adamsın."'),
(1561, 11, 88, 'Şu\'ayb dedi ki: "Ey kavmim! Şayet ben Rabbimden ispat edici bir delil üzerinde bulunuyorsam ve şayet bana, O kendi katından güzel bir rızık ihsan etmişse, söyleyin bakalım ben ne yapmalıyım? Ben size karşı çıkmakla sizi menettiğim şeylere kendim düşmek istemiyorum. Ben sadece gücümün yettiği kadar ıslah etmeye çalışıyorum. Muvaffakiyetim de ancak Allah\'ın yardımı ile olacaktır. Ben yalnızca O\'na dayandım ve ancak O\'na döneceğim."'),
(1562, 11, 89, '"Ey kavmim! Bana karşı gelmeniz sakın sizi, Nuh kavminin veya Hud kavminin veya Salih kavminin başlarına gelen musibetler gibi bir musibete uğratmasın. Lut kavmi de sizden uzak değildir.'),
(1563, 11, 90, 'Rabbinizden mağfiret dileyin, sonra O\'na tevbe ile yönelin. Şüphesiz ki, benim Rabbim çok merhametlidir, çok sevendir.'),
(1564, 11, 91, 'Dediler ki: "Ey Şu\'ayb! Biz senin söylediklerinin çoğundan birşey anlamıyoruz. Ayrıca seni içimizde çok zayıf biri olarak görüyoruz. Eğer akrabaların olmasaydı mutlaka seni recmederdik (taşa tutardık). Senin bize hiçbir üstünlüğün yoktur."'),
(1565, 11, 92, 'Şu\'ayb dedi: "Ey kavmim! Benim akrabalarım size Allah\'dan daha mı değerli ki, Allah\'a sırt çevirip, onu unuttunuz? Muhakkak ki, Rabbim bütün yaptıklarınızı çepeçevre kuşatmıştır."'),
(1566, 11, 93, '"Ey kavmim! Var gücünüzle yapacağınız ne varsa yapın! Ben de görevimi yapmaya devam edeceğim. Perişan edecek azabın kime geleceğini ve yalancının kim olduğunu ilerde anlayacaksınız. Bekleyiniz, ben de sizinle beraber bekleyeceğim."'),
(1567, 11, 94, 'Ne zaman ki, emrimiz geldi, Şu\'ayb ve beraberindeki müminler, tarafımızdan bir rahmet sayesinde kurtuldular. Ve o zalimleri korkunç bir gürültü yakaladı da oldukları yerde çöküp kaldılar.'),
(1568, 11, 95, 'Sanki orada hiç güzel gün görmemişlerdi. Dikkat edin, Semud kavmi nasıl helâk olup gittiyse Medyen de öyle yok olup gitti.'),
(1569, 11, 96, 'Andolsun Musa\'yı da âyetlerimizle ve apaçık bir belge ile gönderdik.'),
(1570, 11, 97, 'Firavun\'a ve cemaatine. Bunlar Firavun\'un emrine uydular. Halbuki Firavun\'un emri hak değildir.'),
(1571, 11, 98, 'Kıyamet günü, kavminin önüne düşer. Artık o bunları ateşe götürmüştür. O varılan yer, ne kötü bir yerdir.'),
(1572, 11, 99, 'Hem burada, hem de kıyamet gününde lanetle izlendiler. Onlara verilen bu karşı destek ne fena bir destektir!'),
(1573, 11, 100, 'İşte bu helâk olmuş memleketlerin önemli haberlerindendir. Sana onu kıssa olarak anlatıyoruz. Onlardan yerinde duranlar da var, biçilenler (yok olup gidenler) de.'),
(1574, 11, 101, 'Biz onlara zulmetmedik, onlar kendi kendilerine zulmettiler. Allah\'ı bırakıp da taptıkları tanrılar, Rabbinin emri gelince kendilerine hiçbir fayda sağlayamadılar. Hasarlarını arttırmaktan başka bir şeye yaramadılar.'),
(1575, 11, 102, 'İşte Rabbin, zalim memleketleri cezalandırdığı zaman böyle cezalandırır. Çünkü O\'nun cezası çok acı, çok çetindir.'),
(1576, 11, 103, 'Ahiret azabından korkanlar için bunda muhakkak ki, bir ibret vardır. O, öyle bir gündür ki, bütün insanlar onun için toplanacaktır ve o, öyle bir gündür ki, mutlaka görülecektir.'),
(1577, 11, 104, 'Biz onu sadece belli bir süreye kadar geciktiriyoruz.'),
(1578, 11, 105, 'O gün gelince Allah\'ın izni olmadan hiç kimse konuşamaz. Onların kimi bedbaht, kimi de mutludur.'),
(1579, 11, 106, 'Bedbaht olanlar ateştedirler. Onlar orada başka türlü soluyacak, başka türlü haykıracaklar.'),
(1580, 11, 107, 'Onlar orada gökler ve yer durdukça duracaklar. Ancak Rabb\'inin diledikleri başka. Çünkü Rabbin dilediğini yapandır.'),
(1581, 11, 108, 'Mutlu olanlar ise cennettedirler. Orada gökler ve yer durdukça duracaklar, ancak Rabbinin diledikleri başka. (Bu) ardı arası kesilmeyen bir ihsan olacak.'),
(1582, 11, 109, 'O halde sakın şunların ibadet edişlerinden şüpheye düşme. Daha önce ataları nasıl ibadet ediyor idiyseler bunlar da öyle ibadet ediyorlar. Biz de kendilerine nasiplerini elbette eksiksiz olarak öderiz.'),
(1583, 11, 110, 'Andolsun ki, Musa\'ya kitabı verdik, yine de onda ihtilafa düşüldü. Eğer Rabbinden daha önce verilmiş bir karar olmasa idi, elbette haklarında hüküm verilmiş bitmişti. Muhakkak ki onlar, bundan kuşkulu bir şüphe içindedirler.'),
(1584, 11, 111, 'Gerçekten de onların her biri öyle kimselerdir ki, yaptıklarının karşılığını Rabbin kendilerine hakkiyle ödeyecektir. Çünkü O, onların yaptıkları her şeyden haberdardır.'),
(1585, 11, 112, 'İşte bundan dolayı emrolunduğun gibi doğru ol! Beraberindeki tevbe edenler de (doğru olsunlar). Aşırı gitmeyin! Muhakkak ki O, bütün yaptıklarınızı görüp durmaktadır.'),
(1586, 11, 113, 'Ve zulüm yapanlara yakınlık göstermeyin ki, size de ateş dokunmasın. Allah\'dan başka yardımcılarınız da yoktur. Sonra yardım da göremezsiniz.'),
(1587, 11, 114, 'Gündüzün her iki tarafında ve gecenin saçaklarında (gündüze yakın olan saatlerinde) namaz kıl! Muhakkak ki, iyilik kötülükleri giderir. Bu ise, düşünebilenlere bir öğüttür.'),
(1588, 11, 115, 'Ve sabret! Çünkü Allah iyilik edenlerin mükafatını yitirmez.'),
(1589, 11, 116, 'Sizden önceki devirlerden bakıyye sahipleri (kitap ehli) yeryüzünde bozgunculuktan vazgeçirmeye çalışsalardı ne iyi olurdu. Fakat onların içinden kurtardığımız pek az kimse bunu yaptı. O zulmedenler ise şımartıldıkları refahın peşine düştüler ve hepsi de suçlu oldular.'),
(1590, 11, 117, 'Senin Rabbin, halkları iyi ve ıslahatçı iken, o memleketleri haksız yere helak edecek değildir.'),
(1591, 11, 118, 'Eğer Rabbin dileseydi elbette bütün insanları tek bir ümmet yapardı. Halbuki yine de ihtilaf edip duracaklardı.'),
(1592, 11, 119, 'Ancak Rabbinin rahmetle yarlığadığı kimseler başka. Onun içindir ki, onları yarattı. Ve Rabbinin "Andolsun ki cehennemi cinlerden ve insanlardan tamamen dolduracağım" sözü böylece tamam oldu.'),
(1593, 11, 120, 'Peygamberlere ait haberlerden kalbini yatıştıracak olanlardan her türlüsünü sana kıssa olarak anlatıyoruz. Bunda da sana bir hakikat, müminlere de bir öğüt ve ibret gelmiştir.'),
(1594, 11, 121, 'İmana gelmeyen o kâfirlere de ki: "Elinizden geleni geri koymayın! Biz de yapacağımızı yapacağız."'),
(1595, 11, 122, 'Siz bekleyin görün, biz de bekleyip göreceğiz.'),
(1596, 11, 123, 'Göklerin ve yerin gaybını bilmek yalnızca Allah\'a mahsustur. Her iş O\'na döndürülür. Sen yalnızca O\'na ibadet et ve yalnızca O\'na dayan. Rabbin yaptıklarınızın hiçbirinden gafil değildir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'Elif, Lâm, Râ. İşte bunlar sana o açık seçik kitabın âyetleridir.'),
(1598, 12, 2, 'Muhakkak ki, biz onu anlayasınız diye Arapça bir kitap olarak indirdik.'),
(1599, 12, 3, 'Sana bu Kur\'ân\'ı vahyetmekle biz, sana kıssaların en güzelini anlatıyoruz. Gerçek şu ki, daha önce senin bundan hiç haberin yoktu.'),
(1600, 12, 4, 'Hani bir vakitler Yusuf, babasına demişti ki: "Babacığım, ben rüyada onbir yıldızla güneşi ve ayı bana secde ederken gördüm."'),
(1601, 12, 5, '(Babası) "Yavrucuğum! "dedi, "rüyanı kardeşlerine anlatma. Sonra sana bir tuzak kurarlar. Çünkü şeytan insanın açıkça düşmanıdır."'),
(1602, 12, 6, '"Ve işte böyle, Rabbin seni seçecek ve sana rüya tabirinden bilgiler öğretecek. Bundan önce ataların İbrahim\'e ve İshak\'a tamamladığı gibi, nimetini hem sana, hem de Yakup soyuna tamamlayacaktır. Muhakkak ki, Rabbin alîmdir, hakîmdir."'),
(1603, 12, 7, 'Andolsun ki, Yusuf ve kardeşleri kıssasında soranlara ibret alacak âyetler vardır.'),
(1604, 12, 8, 'Hani demişlerdi ki: "Yusuf ve kardeşi (Bünyamin) babamıza bizden daha sevgili, biz ise güçlü ve tutkun bir grubuz. Doğrusu, babamız belli ki, çok açık bir yanılgı içindedir."'),
(1605, 12, 9, '"Yusuf\'u öldürün, ya da bir yere atın ki, babanızın yüzü (sevgisi) size kalsın, sonra yine salih bir kavim olursunuz."'),
(1606, 12, 10, 'İçlerinden bir söz sahibi şöyle dedi: "Yusuf\'u öldürmeyin, bir kuyunun dibine bırakın da ordan geçen kafilenin biri onu bulup alsın. Eğer yapacaksanız böyle yapın."'),
(1607, 12, 11, 'Dediler ki: "Ey babamız! Sen bize Yusuf için neden güvenmiyorsun? Halbuki biz onun iyiliğini istiyoruz."'),
(1608, 12, 12, '"Yarın onu bizimle beraber gönder de gezsin, oynasın. Kesinlikle biz onu koruruz."'),
(1609, 12, 13, 'Babaları dedi ki: "Onu götürmeniz beni üzer, korkarım ki onu kurt yer de sizin haberiniz bile olmaz."'),
(1610, 12, 14, 'Dediler ki: "Vallahi biz böyle güçlü kuvvetli bir topluluk iken, buna rağmen onu kurt yerse, o zaman biz kesinlikle hüsrana uğrayanlardan olmuş oluruz."'),
(1611, 12, 15, 'Nihayet kardeşleri, Yusuf\'u alıp götürdüler ve kuyunun dibine bırakmaya topluca karar verdiler. Biz de ona şöyle vahyettik: "Andolsun ki, sen onlara ilerde hiç beklemedikleri bir sırada bu yaptıklarını haber vereceksin".'),
(1612, 12, 16, 'Ve yatsı vakti, ağlayarak babalarına geldiler.'),
(1613, 12, 17, 'Dediler ki: "Ey babamız! Biz gittik, aramızda yarış yapıyorduk. Yusuf\'u da eşyamızın yanına bırakmıştık. Bir de baktık ki, onu kurt yemiş. şu anda biz doğru da söylesek, yine de sen bize inanacak değilsin."'),
(1614, 12, 18, 'Bir de gömleğinin üzerinde yalandan bir kan getirmişlerdi. Babaları dedi ki: "Hayır, nefisleriniz aldatmış da size bir iş yaptırtmış. Artık bana güzel bir sabır gerekiyor. Bu anlattıklarınıza karşılık yardımına sığınılacak olan ancak Allah\'dır."'),
(1615, 12, 19, 'Daha sonra bir kafile gelmiş, sucularını da göndermişlerdi. Vardı, kovasını kuyuya saldı, "Müjde hey, müjde! İşte bir çocuk!" dedi. Ve onu satılık bir mal olarak gizleyip korudular. Allah ise onların ne yapacaklarını biliyordu.'),
(1616, 12, 20, 'Ve onu düşük bir değerle birkaç dirheme sattılar. Ona fazla önem vermemişlerdi.'),
(1617, 12, 21, 'Onu satın alan Mısırlı, eşine dedi ki: "Buna güzel bak. Bize faydalı olabilir, ya da evlat ediniriz." Yusuf\'u böylece oraya yerleştirdik. Ona rüyaların tabirini de öğrettik. Allah emrinde galiptir. Fakat insanların çoğu bunu bilmezler.'),
(1618, 12, 22, 'O, tam erginlik çağına gelince, kendisine ilim ve hüküm verdik. İşte biz, güzel iş yapanları böyle mükafatlandırırız.'),
(1619, 12, 23, 'Derken, evinde bulunduğu hanım, onun nefsinden murad alıp yararlanmak istedi. Kapıları kilitledi ve "Haydi beri gel!" dedi. Yusuf: "Allah\'a sığınırım! Muhakkak ki, o (kocan), benim efendim, bana çok güzel baktı. Doğrusu zalimler hiç iflah olmazlar" dedi.'),
(1620, 12, 24, 'O hanım, ona gerçekten niyeti bozmuştu. Eğer Rabbinin burhanını görmese idi. Yusuf da ona özenip gitmişti. Aslında ondan fuhşu ve fenalığı uzak tutalım diye böyle olmuştu. Çünkü o bizim ihlasa erdirilmiş kullarımızdan biriydi.'),
(1621, 12, 25, 'İkisi de kapıya koştular. Hanım, onun gömleğini arkadan yırttı. Ve kapının yanında hanımın efendisiyle karşı karşıya geldiler. Hanım hemen dedi ki: "Senin eşine fenalık yapmak isteyenin cezası, zindana atılmaktan veya acı bir azaba uğratılmaktan başka ne olabilir?"'),
(1622, 12, 26, 'Yusuf: "kendisi benden yararlanmak istedi" dedi. Hanımın akrabasından biri de şöyle şahitlik etti: "Eğer gömleği önden yırtılmış ise hanım doğru söylemiştir, o zaman bu, yalancılardandır."'),
(1623, 12, 27, '"Yok eğer gömleği arkadan yırtılmış ise hanım yalan söylemiştir, o zaman bu doğru söyleyenlerdendir."'),
(1624, 12, 28, 'Ne zaman ki, gömleğin arkadan yırtılmış olduğunu gördü, o zaman dedi ki: "Bu iş, siz kadınların tuzağındandır. Gerçekten de sizin tuzağınız çok büyüktür".'),
(1625, 12, 29, '"Yusuf! Sakın sen bundan bahsetme! Kadın! Sen de günahından dolayı istiğfar et. Sen gerçekten günahkarlardan oldun".'),
(1626, 12, 30, 'Şehirde bazı kadınlar da "Azizin karısı, delikanlısından murad almaya kalkmış, sevgi yüreğini yakıp kavuruyormuş, görüyoruz ki, kadın çıldırmış besbelli..." dediler.'),
(1627, 12, 31, 'Azizin karısı, onların gizliden gizliye dedikodu yaydıklarını işitince, onlara davetçi gönderdi ve onlara mükellef bir sofra hazırladı. Her birine bir bıçak verdi, beri taraftan da Yusuf\'a "çık karşılarına" dedi. Görür görmez hepsi onu gözlerinde çok büyüttüler ve (şaşkınlıkla) ellerini kestiler. Dediler ki: "Hâşâ! Allah için, bu bir insan değil, olsa olsa yüce bir melektir."'),
(1628, 12, 32, '"İşte" dedi, "bu gördüğünüz, beni hakkında kınadığınız (gençtir). Yemin ederim ki, ben bunun nefsinden yararlanmak istedim de o, namuslu davrandı. Yine yemin ederim ki, emrimi yerine getirmezse, muhakkak zindana atılacak ve kesinlikle zelillerden olacaktır".'),
(1629, 12, 33, 'Yusuf dedi ki: "Ey Rabbim! Zindan bana, bunların beni davet ettikleri şeyden daha sevimlidir. Eğer sen, bu kadınların tuzaklarını benden uzak tutmazsan, ben onların tuzağına düşerim ve cahillik edenlerden olurum".'),
(1630, 12, 34, 'Bunun üzerine Rabbi, onun duasını kabul buyurdu da ondan onların tuzaklarını bertaraf etti. Muhakkak ki O, evet O, hakkiyle işiten, hakkiyle bilendir.'),
(1631, 12, 35, 'Bu kadar delili gördükleri halde, sonra yine de Yusuf\'u bir süre için zindana atma düşüncesi ağır bastı.'),
(1632, 12, 36, 'Zindana onunla birlikte iki delikanlı daha girdi. Birisi dedi ki: "Rüyada kendimi şarap sıkarken gördüm". Öteki de dedi ki: "Ben de başımın üstünde ekmek taşıdığımı, kuşların da ondan yediğini gördüm. Bize bunun yorumunu haber ver. Çünkü biz seni iyilik edenlerden görüyoruz."'),
(1633, 12, 37, 'Yusuf dedi ki: "Size yiyecek olarak verilecek bir yemek gelmeden önce onun tabirini size bildiririm. Bu, Rabbimin bana öğrettiği ilimlerdendir. Çünkü ben Allah\'a inanmayan ve ahireti inkâr eden bir kavmin dinini terkettim."'),
(1634, 12, 38, '"Atalarım İbrahim, İshak ve Yakub\'un dinine uydum. Bizim, Allah\'a hiçbir şeyi ortak tutmamız olmaz. Bu, bize ve insanlara Allah\'ın bir lutfudur. Fakat insanların çoğu şükretmezler."'),
(1635, 12, 39, '"Ey benim zindan arkadaşlarım! Ayrı ayrı birçok tanrılar mı daha hayırlı, yoksa herşeye hakim ve galip olan bir tek Allah mı?"'),
(1636, 12, 40, '"Sizin Allah\'ı bırakıp da o taptıklarınız, sizin ve atalarınızın uydurduğu birtakım isimlerden başka bir şey değildir. Bunlara tapmanız için Allah hiçbir delil indirmiş değildir. Hüküm ancak Allah\'a aittir: O, size, kendisinden başkasına tapmamanızı emretti. İşte dosdoğru din budur. Fakat insanların çoğu bunu bilmezler."'),
(1637, 12, 41, '"Ey benim zindan arkadaşlarım! Biriniz efendisine yine şarap sunacak. Diğeri de asılacak, kuşlar başından yiyecekler. İşte öğrenmek istediğiniz iş böylece halloldu."'),
(1638, 12, 42, 'Yusuf, hapisten kurtulacağına inandığı o ikiden birine dedi ki: "Beni efendinin yanında an". (Benden söz et ki, beni kurtarsın). Fakat Şeytan, ona, efendisinin yanında anmayı unutturdu. Bu yüzden Yusuf, daha yıllarca zindanda kaldı.'),
(1639, 12, 43, 'Bir gün melik (hükümdar) dedi ki: "Ben rüyamda yedi cılız ineğin yedi semiz ineği yediğini ve yedi yeşil başakla yedi kuru başak görüyorum. Ey ileri gelenler! Siz rüya tabir edebiliyorsanız benim bu rüyamın tabirini bana bildirin."'),
(1640, 12, 44, 'Dediler ki: "Rüya dediğin şey karmakarışık hayallerdir. Biz ise böyle karışık hayallerin yorumunu bilemeyiz."'),
(1641, 12, 45, 'O ikiden kurtulmuş olanı nice zamandan sonra hatırladı da dedi ki: "Ben size o rüyanın tabirini haber veririm, hemen beni gönderin."'),
(1642, 12, 46, '"Ey Yusuf, ey doğru sözlü! Bize şunu hallet: Yedi semiz ineği, yedi cılız inek yiyor. Ve yedi yeşil başakla diğer yedi kuru başak. Umarım ki, o insanlara doğru cevap ile dönerim, onlar da (senin kadrini) bilirler."'),
(1643, 12, 47, 'Dedi ki: "Yedi sene eskisi gibi ekeceksiniz, biçtiklerinizi başağında bırakınız, biraz yiyeceğinizden başka. "'),
(1644, 12, 48, '"Sonra onun arkasından yedi kurak sene gelecek, önceki biriktirdiklerinizin biraz saklayacağınızdan başkasını yiyip bitirecek."'),
(1645, 12, 49, '"Sonra da onun arkasından yağışlı bir sene gelecek ki, halk onda sıkıntıdan kurtulacak, (üzüm, zeytin gibi mahsülleri) sıkıp faydalanacak."'),
(1646, 12, 50, 'O hükümdar "Onu bana getirin" dedi. Emir üzerine Yusuf\'a gönderilen adam yanına gelince, Yusuf ona dedi ki: "Haydi efendine geri dön de, ona sor bakalım, o ellerini kesen kadınların maksatları ne imiş? Hiç şüphe yok ki, Rabbim, onların oyunlarını çokiyi bilir."'),
(1647, 12, 51, 'Hükümdar, o kadınlara "Derdiniz neydi ki, o vakit Yusuf\'un nefsinden murad almaya kalktınız?" dedi. Onlar "Hâşâ, Allah için, biz onun aleyhinde hiçbir fenalık bilmiyoruz" dediler. Aziz\'in, karısı da: "Şimdi hak ve hakikat olduğu gibi ortaya çıktı. Aslında onun nefsinden ben murad almak istedim. O ise şeksiz şüphesiz doğrulardandır" dedi.'),
(1648, 12, 52, '(Yusuf dedi ki): İşte bu şunun içindir: Bilsin ki, ben ona arkasından hainlik etmedim. Gerçekten Allah hainlerin hilesini başarıya ulaştırmaz.'),
(1649, 12, 53, 'Ben yine de nefsimi temize çıkarmıyorum. Çünkü nefis şiddetle kötülüğü emreder. Ancak Rabbimin rahmetiyle yarlığadığı müstesna. Muhakkak ki, Rabbim bağışlayıcı ve merhametlidir.'),
(1650, 12, 54, 'Hükümdar dedi ki: "Onu bana getirin, kendime tahsis edeyim." Sonra onunla konuşunca da: "Sen bugün yanımızda gerçekten büyük bir mevki sahibisin, güvenilir birisin" dedi.'),
(1651, 12, 55, 'O da, ona dedi ki: "Beni bu ülkenin hazineleri üzerine getir. Çünkü iyi korurum, iyi bilirim."'),
(1652, 12, 56, 'Ve işte biz böylece Yusuf\'u o yerde temkin ettik (yerleştirdik). Neresinde isterse orada makam tutuyordu. Biz rahmetimizi dilediğimize nasip ederiz. Ve iyilik edenlerin mükafatını zayi etmeyiz.'),
(1653, 12, 57, 'İman edip takva yolunu tutanlar için elbette ahiret mükafatı daha hayırlıdır.'),
(1654, 12, 58, '(Bir gün) Yusuf\'un kardeşleri çıkageldiler ve onun yanına girdiler. O, onları görür görmez tanıdı, oysa onlar onu tanıyamamışlardı.'),
(1655, 12, 59, 'Ne zaman ki onların bütün hazırlıklarını tamamladı, o zaman dedi ki: "Babanızdan olan öbür kardeşinizi de bana getirin. Görüyorsunuz ya, ben ölçeği tam ölçüyorum ve ben konukseverlerin en hayırlısıyım."'),
(1656, 12, 60, '"Siz eğer onu bana getirmezseniz, bir daha size hiç kile yok, (bir ölçek bile zahire alamazsınız) yanıma da yaklaşmayın".'),
(1657, 12, 61, 'Dediler ki: "Onun için babasından izin almaya çalışacağız. Her hâlü kârda bunu yapacağz."'),
(1658, 12, 62, 'Yusuf bir taraftan da adamlarına tenbih etti: "Sermayelerini yüklerinin içine koyuverin, belki ailelerinin yanına dönünce farkına varırlar ve belki yine gelirler" dedi.'),
(1659, 12, 63, 'Böylece dönüp babalarına geldikleri vakit, dediler ki: "Ey babamız! Bizden ölçek menedildi (bize zahire verilmeyecek). Bu kere kardeşimizi de bizimle gönder ki, ölçek alabilelim. Biz onu kesinlikle koruyacağız."'),
(1660, 12, 64, 'Babaları dedi ki: "Ben onu size nasıl emanet ederim? Ya bundan önce kardeşini emanet ettiğimde olan gibi olursa! En hayırlı koruyucu Allah\'dır ve O, merhamet edenlerin en merhametlisidir."'),
(1661, 12, 65, 'Derken yüklerini açtılar ve sermayelerini kendilerine geri verilmiş olarak buldular. Dediler ki: "Ey babamız! Daha ne isteriz? İşte sermayelerimiz de bize iade edilmiş. Bununla yine ailemize zahire alır getiririz, kardeşimizi de koruruz, üstelik biryük daha fazla zahire alırız. Zaten bu aldığımız pek az bir zahiredir."'),
(1662, 12, 66, 'Babaları dedi ki: "Hepiniz çaresiz kalmadıkça onu bana mutlaka getireceğinize dair Allah\'dan bir yemin vermedikçe, onu, kesinlikle sizinle göndermem". Onlar da Allah\'a and içerek babalarına söz verince, babaları dedi ki: "Bu söylediklerinize Allah vekildir".'),
(1663, 12, 67, 'Ve dedi ki: "Ey yavrularım! (şehre) hepiniz bir kapıdan girmeyin de ayrı ayrı kapılardan girin. Gerçi ben ne yapsam, Allah\'ın takdirini sizden engelleyemem. Hüküm yalnızca Allah\'ındır. Onun için bütün tevekkül edenler O\'na tevekkül etmelidirler."'),
(1664, 12, 68, 'Ne zaman ki, şehre vardılar, o zaman babalarının kendilerine emrettiği şekilde girdiler. (Gerçi bu şekilde girmeleri) onlar hakında Allah\'ın takdir ettiği hiçbir şeyi önleyemezdi, bu sadece Yakub\'un içinden geçirdiği bir isteğin yerine getirilmesi oldu. Şüphesiz o, ilim sahibiydi, çünkü ona biz öğretmiştik. Fakat insanların çoğu bunu bilmezler.'),
(1665, 12, 69, 'Yusuf\'un yanına girdikleri vakit, o, kardeşini (Bünyamin\'i) yanında alıkoydu. Dedi ki: "Bilesin, ben, senin kardeşinim! İşte bundan dolayı onların yapacaklarına sakın üzülme!"'),
(1666, 12, 70, 'Sonra onların bütün hazırlıklarını görünce, su kabını kardeşinin yükünün içine koydu. Sonra bir tellal şöyle bağırdı: "Hey kervan! Siz hırsızsınız, hırsız!"'),
(1667, 12, 71, 'Bunlara döndüler de dediler ki: "Ne arıyorsunuz?'),
(1668, 12, 72, 'Onlar da dediler ki: "Hükümdarın su kabını arıyoruz. Onu bulup getirene bir yük zahire var. Üstelik o tas bana zimmetlidir".'),
(1669, 12, 73, '"Allah\'a yemin ederiz ki," dediler, "Muhakkak siz de anlamışsınızdır ya, biz buraya fesat çıkarmak için gelmedik. Biz hırsız da değiliz."'),
(1670, 12, 74, '"Peki yalancı çıkarsanız onun (hırsızlık edenin) cezası nedir?" dediler.'),
(1671, 12, 75, '"Kimin yükünde çıkarsa, o kendisi onun cezasıdır. Biz zalimlere işte böyle ceza veririz."'),
(1672, 12, 76, 'Bunun üzerine Yusuf, kardeşinin eşyalarından önce onların eşyalarını aramaya başladı. Sonra su kabını kardeşinin yükünün içinden çıkardı. İşte Yusuf\'a biz böyle bir oyun öğrettik. Melikin kanunlarına göre, kardeşini alıkoymasına imkan yoktu. Ancak Allah dilerse o başka. Biz dilediğimizi derecelerle yükseltiriz. Ve her bilgi sahibinin üstünde bir başka bilen vardır.'),
(1673, 12, 77, 'Dediler ki: "Eğer o çalmışsa, daha önce bunun kardeşi de çalmıştı". O vakit Yusuf bunu içine attı, onlara hiç belli etmeden: "Siz çok fena bir mevkidesiniz, ne sıfat verdiğinizi Allah çok iyi biliyor" dedi.'),
(1674, 12, 78, 'Dediler ki: "Ey vezir! Emin ol ki, bunun çok yaşlı bir babası var. Onun için yerine birimizi al. Gerçekten de biz seni iyilik edenlerden görüyoruz."'),
(1675, 12, 79, 'O dedi ki: "Eşyamızı yanında bulduğumuzdan başkasını tutuklamaktan Allah korusun. Çünkü öyle yaparsak zalimlerden oluruz."'),
(1676, 12, 80, 'Ne zaman ki, onlar, onu kurtarmaktan ümit kestiler, o zaman fısıldaşarak oradan uzaklaştılar. Büyükleri dedi ki: "Babanızın sizden Allah adına ahit aldığını ve daha önce Yusuf konusunda ettiğiniz kusuru bilmiyor musunuz? Babam bana izin verinceye veya Allah hakkımda bir hüküm verinceye kadar ben artık burdan ayrılmam. Allah, hüküm verenlerin en hayırlısıdır."'),
(1677, 12, 81, '"Siz dönün de babanıza deyin ki: Ey babamız! İnan ki, oğlun hırsızlık yaptı. Biz ancak bildiğimize şahitlik ediyoruz. Yoksa gaybın bekçileri değiliz."'),
(1678, 12, 82, '"Hem orada bulunduğumuz şehir halkına, hem içinde bulunduğumuz kervana sor. Ve emin ol ki, biz kesinlikle doğru söylüyoruz."'),
(1679, 12, 83, 'Babaları dedi ki: "Hayır, sizi nefisleriniz altadıp bir işe sürüklemiş. Artık bana güzel güzel sabretmek düşüyor. Belki Allah hepsini birden bana geri getirir. Çünkü O, her şeyi bilir, hüküm ve hikmet sahibidir."'),
(1680, 12, 84, 'Ve onlardan yüz çevirdi de: "Ey Yusuf\'un ateşi, yetti artık, yetti!" dedi. Ve üzüntüden gözlerine ak düştü. Artık yutkunuyor da yutkunuyordu.'),
(1681, 12, 85, 'Dediler ki: "Hâlâ Yusuf\'u sayıklayıp duruyorsun. Allah\'a yemin ederiz ki, sonunda eriyip gideceksin, tükenip helak olacaksın. Hayret doğrusu!"'),
(1682, 12, 86, 'Dedi ki: "Ben hüznümü, kederimi ancak Allah\'a şikayet ederim ve Allah tarafından sizin bilmediğiniz şeyleri de bilirim."'),
(1683, 12, 87, '"Ey oğullarım, gidin, Yusuf\'u ve kardeşini araştırın. Allah\'ın rahmetinden ümit kesmeyin; zira kâfir kavimden başkası Allah\'ın rahmetinden ümit kesmez."'),
(1684, 12, 88, 'Sonra (Mısır\'a gidip) onun huzuruna girince, dediler ki: "Ey şanlı vezir! Biz ve çoluk çocuğumuz sıkıntı içindeyiz. Pek az bir sermaye ile geldik. Sen bize yine ölçek (zahire) ver, ayrıca sadaka da ihsan eyle. Çünkü Allah sadaka verenleri muhakkak mükafatlandırır."'),
(1685, 12, 89, 'O dedi ki: "Siz cahilliğinizde Yusuf\'a ve kardeşine ne yaptığınızı biliyor musunuz?"'),
(1686, 12, 90, 'Onlar "Yoksa sen, sahiden Yusuf musun?" dediler. O da "Ben Yusuf\'um, bu da kardeşim" dedi, "Doğrusu Allah, bizi, lutfuyla nimetlendirdi. Gerçekten de kim Allah\'dan korkar ve sabrederse, Allah, muhakkak ki, güzel işler yapanların mükafatını zayi etmez."'),
(1687, 12, 91, 'Dediler ki: "Allah\'a yemin olsun, Allah seni bize üstün kıldı. Biz gerçekten de büyük hata işlemiştik".'),
(1688, 12, 92, 'Yusuf dedi: "Bugün size bir ayıplama ve azarlama yoktur. Allah, sizi, mağfiretiyle bağışlasın. O, merhamet edenlerin en merhametlisidir."'),
(1689, 12, 93, 'Alın şu gömleğimi götürün de babamın yüzüne sürün, gözü açılır. Ve bütün ailenizle toplanıp bana gelin."'),
(1690, 12, 94, 'Ne zaman ki, kafile (Mısır\'dan) ayrıldı, öteden babaları dedi ki: "Eğer bana bunak demezseniz, doğrusu ben Yusuf\'un kokusunu alıyorum."'),
(1691, 12, 95, 'Dediler ki: "Vallahi sen hâlâ o eski şaşkınlığındasın."'),
(1692, 12, 96, 'Fakat ne zaman ki, gerçekten müjdeci geldi, gömleği Yakub\'un yüzüne koydu, hemen gözü açıldı. "Ben size demedim mi, ben Allah\'dan sizin bilmediklerinizi bilirim." dedi.'),
(1693, 12, 97, 'Dediler ki: "Ey babamız, bizim için Allah\'a istiğfar eyle. Biz gerçekten büyük günah işlemiştik."'),
(1694, 12, 98, 'Dedi ki: "Sizin için Rabbimden ilerde bağışlanma dileyeceğim. Şüphesiz o çok bağışlayıcıdır, çok merhamet edicidir.'),
(1695, 12, 99, 'Ne zaman ki, onlar Yusuf\'un yanına vardılar, işte o zaman Yusuf anasını ve babasını kucakladı, yanına aldı ve "Buyurun Allah\'ın dilemesiyle güven içinde Mısır\'a girin" dedi.'),
(1696, 12, 100, 'Anasıyla babasını yüksek bir taht üzerine oturttu ve hepsi birden Yusuf için secdeye kapandılar. Bunun üzerine Yusuf dedi ki: "İşte bu durum, o rüyamın çıkmasıdır. Gerçekten Rabbim onu hak rüya kıldı. Şeytan benimle kardeşlerimin arasını bozduktan sonra, beni zindandan çıkarmakla ve sizi çölden getirmekle Rabbim bana hakikaten ihsan buyurdu. Doğrusu Rabbim dilediğine lutfunu ihsan eder. Şüphesiz O, her şeyi bilir, hüküm ve hikmet sahibidir."'),
(1697, 12, 101, '"Ey Rabbim! Sen bana dünya mülkünden nasip verdin ve bana rüyaların tabirinden bir ilim öğrettin. Ey gökleri ve yeri yoktan var eden Rabbim! Benim velim sensin, benim canımı müslüman olarak al ve beni salih kulların arasına kat!" {*} Hasılı, ne zamanki, Yusuf\'a vardılar, yani Yusuf\'un daha önce kardeşlerine tenbih edip istediği gibi, başta babaları olmak üzere bütün aile bireyleri topluca Mısır\'a gelip Yusuf\'un yanına vardılar. Rivayet olunur ki, Yusuf ve Melik, yanlarında dört bin asker, birtakım devlet adamları ve Mısır halkından çok sayıda insan, gelen kafileyi karşılamaya çıkmışlardı. Yakub Aleyhisselam, oğlu Yahuda\'ya dayanarak yürüyordu, karşıdan gelen kafileye ve atlılara bakıp, "Ey Yahuda, şu karşıdaki adam Mısır\'ın Firavun\'u mu?" diye sordu. O da "Hayır, Firavun değil, oğlun" dedi. Yaklaştıkları zaman Yusuf\'dan önce Yakup selam verdi ve "Selam sana ey hüzünleri gideren" dedi{*}ilh.'),
(1698, 12, 102, 'İşte bu, sana vahiyle bildirdiğimiz gayb haberlerindendir. Yoksa onlar yapacaklarına karar verip mekir (oyun) yaparlarken sen yanlarında değildin.'),
(1699, 12, 103, 'Sen ne kadar şiddetle arzulasan da, insanların çoğu iman edecek değildir.'),
(1700, 12, 104, 'Buna karşılık onlardan herhangi bir ücret de istemiyorsun. O Kur\'ân, âlemlere ancak bir öğüttür.'),
(1701, 12, 105, 'Bununla beraber göklerde ve yerde ne kadar âyet var ki, onunla yüz yüze gelirler de yine de yüz çevirip geçerler.'),
(1702, 12, 106, 'Onların çoğu şirk koşmadan Allah\'a iman etmezler (imanlarına az çok bir şirk karıştırırlar).'),
(1703, 12, 107, 'Yoksa bunlar Allah\'ın azabından hepsini saracak bir felaket gelmesinden veya farkında değillerken ansızın başlarına kıyametin kopuvermesinden güven içinde midirler?'),
(1704, 12, 108, 'De ki: İşte benim yolum budur; basiret üzere Allah\'a davet ediyorum. Ben ve bana uyanlar (işte böyleyiz). Ben Allah\'ı tesbih ederim ve ben müşriklerden değilim.'),
(1705, 12, 109, 'Senden önce gönderdiğimiz peygamberler de o memleketlerin halkındandı, onlar da kendilerine vahiy verdiğimiz birtakım erkeklerden başkası değillerdi. Şimdi o yerlerde şöyle bir gezip görmediler mi? Kendilerinden önce gelip geçenlerin akıbetlerinin nasıl olduğuna bir baksalar ya!... Elbette ahiret yurdu müttakiler için daha hayırlıdır. Hâlâ aklınızı başınıza toplamayacak mısınız?'),
(1706, 12, 110, 'Nihayet peygamberleri (onların iman etmelerinden) ümit kesecek hale gelince ve kendilerinin yalancı durumuna düştüklerini sanınca, onlara yardımımız geldi, yetişti; dilediklerimiz kurtarıldı. Suçlular topluluğundan bizim azabımız geri çevrilemez.'),
(1707, 12, 111, 'Gerçekten de onların kıssalarında üstün akıllılar için bir ibret vardır. Bu Kur\'ân uydurulmuş herhangi bir söz değildir. Lâkin kendisinden önce gelen kitapların tasdiki her şeyin ayrıntılarıyla açıklayıcısı ve iman edecek bir kavim için hidayet ve rahmettir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'Elif, Lâm, Mîm, Ra. İşte bunlar sana o kitabın âyetleridir ve sana Rabbinden indirilen haktır. Lâkin insanların çoğu iman etmezler.'),
(1709, 13, 2, 'Allah O\'dur ki, gökleri direksiz yükseltti, onu görüyorsunuz, sonra arş üzerine istiva etti, güneşi ve ayı emrine boyun eğdirdi. Her biri belli bir vakte kadar akar gider. Bütün işleri O yönetiyor. Âyetleri O açıklıyor ki, Rabbinizin huzuruna çıkacağınızı iyi bilesiniz.'),
(1710, 13, 3, 'Yeryüzünü enine boyuna yayıp döşeyen, onda oturaklı dağlar ve ırmaklar meydana getiren ve yeryüzünde meyvelerin hepsinden iki çift yapan O\'dur. Sürekli olarak gece ile gündüzü birbirine dolamaktadır. Düşünecek olan bir kavim için bunda muhakkak ki, ibretler vardır.'),
(1711, 13, 4, 'Yeryüzünde birbirine komşu kıtalar vardır. Üzüm bağları, ekinler, çatallı ve çatalsız hurmalıklar vardır ki, hepsi bir tek su ile sulanır. Halbuki meyvelerinde birini öbürüne üstün kılıyoruz. Aklı eren bir kavim için bunda muhakkak ibretler vardır.'),
(1712, 13, 5, 'Eğer şaşıyorsan, asıl şaşılacak şey onların şu sözleridir: "Biz toprak olup gittikten sonra mı, yani biz gerçekten yeniden mi yaratılacağız?" İşte bunlar Rablerini inkâr etmişlerdir. Bunlar boyunlarında demir halkalar bulunanlardır. Ve işte bunlar cehennemliktirler, orada ebedî kalacaklardır.'),
(1713, 13, 6, 'Ayrıca senden iyilikten önce hemen kötülüğü getirmeni isterler. Oysa daha önce onlara misal olacak cezalar gelip geçmiştir. Ve gerçekten Rabbin, zulümlerine karşılık insanlara mağfiret sahibidir. Bununla beraber Rabbinin azabı da cidden çok çetindir.'),
(1714, 13, 7, 'O kâfirler: "Rabbinden ona bir mucize indirilmeli değil miydi?" derler. Sen bir uyarıcıdan başka bir şey değilsin ve her kavim için bir hidayetçi vardır.'),
(1715, 13, 8, 'Her dişinin neye gebe olduğunu Allah bilir. Ve rahimler ne eksiltir, ne arttırır, onu da bilir. O\'nun katında her şeyin bir ölçüsü vardır.'),
(1716, 13, 9, 'Allah görünmeyeni de bilir, görüneni de. Büyüktür ve yücelerden yücedir.'),
(1717, 13, 10, 'Sizden sözü gizleyenle açığa vuran, gece gizlenenle gündüz açığa çıkan, O\'nun açısından eşittir (hepsini görür ve bilir).'),
(1718, 13, 11, 'Her insan için önünden ve arkasından takip edenler vardır. Allah\'ın emrinden dolayı onu gözetirler. Allah bir kavme verdiğini, o kavim kendisini bozup değiştirmedikçe değiştirmez. Allah bir kavme de kötülük murad etti mi, artık onun geri çevrilmesine de imkan yoktur. Onlar için Allah\'dan başka bir veli de bulunmaz.'),
(1719, 13, 12, 'Size korku ve ümit içinde şimşeği gösteren ve o yağmur yüklü bulutları meydana getiren O\'dur.'),
(1720, 13, 13, 'Gök gürültüsü O\'na hamd ile, melekler de O\'nun korkusundan dolayı O\'nu tesbih ederler. O yıldırımlar gönderir, onunla dilediğini çarpar. Onlar Allah hakkında mücadele edip duruyorlar. Oysa Allah\'ın çarpması pek çetindir.'),
(1721, 13, 14, 'Gerçek dua O\'nadır. O\'nun dışında yalvarıp durdukları ise onlara hiçbir şeyle cevap veremezler. Onlar olsa olsa ağzına su gelsin diye iki avucunu açana benzer ki, o, ona gelmez. Kâfirlerin duası hep bir sapıklık içindedir.'),
(1722, 13, 15, 'Oysa göklerde ve yerde kim varsa ister istemez kendileri de gölgeleri de sabah akşam Allah\'a secde ederler.'),
(1723, 13, 16, 'De ki: "Göklerin ve yerin Rabbi kimdir?" De ki: "Allah\'dır". De ki: "Allah\'dan başkalarını, o kendi kendilerine ne bir fayda, ne de bir zarar verebilenleri dostlar mı ediniyorsunuz?" De ki: "Hiç kör ile gören bir olur mu? Hiç karanlıklarla aydınlık bir olur mu?" Yoksa Allah\'a, O\'nun gibi yaratan birtakım ortaklar buldular da, bu yaratış kendilerince birbirine benzer mi göründü? De ki: "Allah, her şeyi yaratandır. O, birdir. Her şeye üstün ve kahredicidir."'),
(1724, 13, 17, 'Gökten bir su indirdi de vadiler, kendi miktarlarınca sel olup aktılar. Sel de suyun yüzüne çıkan bir köpük yüklendi. Bir zinet eşyası veya bir değerli mal yapmak için, ateşte üzerini körükledikleri madenlerden de onun gibi bir köpük meydana gelir. İşte Allah hak ile batılı böyle çarpıştırır. Fakat köpük atılır gider, insanlara faydası olan ise yerde kalır. İşte Allah böyle misaller verir.'),
(1725, 13, 18, 'Rablerinin emirlerine uyanlar için daha güzeli vardır. O\'na itaat etmeyenler ise, yeryüzünde bulunan ne varsa hepsi kendilerinin olsa da onu ve bir o kadarını bütünüyle kurtuluş fidyesi olarak verirlerdi. İşte onlar, hesabın kötüsü kendileri için olanlardır. Varacakları yer de cehennemdir. Orası da ne fena yataktır.'),
(1726, 13, 19, 'Şimdi Rabbinden sana indirilenin gerçekten hak olduğunu bilen bir kimse, kör olan bir kimse gibi olur mu? Fakat bunu ancak üstün akıllı ve temiz vicdanlı kimseler idrak ederler.'),
(1727, 13, 20, 'Onlar ki, Allah\'ın ahdini yerine getirirler ve antlaşmayı bozmazlar.'),
(1728, 13, 21, 'Ve onlar ki, Allah\'ın riayet edilmesini emrettiği şeye riayet ederler ve Rablerine saygı gösterirler ve hesabın kötülüğünden korkarlar.'),
(1729, 13, 22, 'Rablerinin rızasını kazanmak arzusuyla sabrederler ve namazı dosdoğru kılarlar ve kendilerine verdiğimiz rızıklardan gizli ve açıkça Allah yolunda harcarlar ve çirkinlikleri güzelliklerle yok ederler. İşte bunlar, bu hayatın akibeti kendilerinin olacak olanlardır.'),
(1730, 13, 23, 'Adn cennetlerine girecekler, atalarından, eşlerinden ve zürriyetlerinden salih olanlarla birlikte olacaklar. Melekler de her kapıdan yanlarına girip şöyle diyecekler:'),
(1731, 13, 24, '"Sabrettiğiniz için size selam olsun. Ahiret yurdu ne güzeldir!"'),
(1732, 13, 25, 'Allah\'ın ahdini misak ile belgeledikten sonra bozanlar ve Allah\'ın birleştirilmesini emrettiği bağlantıları koparanlar ve yeryüzünü bozguna verenler varya, işte lanet olsun onlara! Ve yurdun kötüsü de onlaradır.'),
(1733, 13, 26, 'Allah, dilediği kimseye rızkı genişletir de, daraltır da. Onlar ise dünya hayatı ile ferahlanmaktalar. Oysa düna hayatı ahiret hayatının yanında bir yol azığından ibarettir.'),
(1734, 13, 27, 'Yine o iman etmeyenler diyorlar ki: "Ona Rabbinden bir âyet indirilseydi ya." De ki: "Hakikaten Allah, dilediğini şaşırtır ve kendisine gönül vereni de hidayete erdirir."'),
(1735, 13, 28, 'Onlar, iman etmiş ve kalbleri Allah zikriyle yatışmış olanlardır. Evet, iyi bilin ki, kalbler Allah\'ın zikri ile yatışır.'),
(1736, 13, 29, 'Onlar ki, iman etmişler ve salih ameller işlemişlerdir, ne mutlu onlara, varacakları yer de ne güzeldir!'),
(1737, 13, 30, 'İşte seni böyle, kendilerinden önce nice ümmetler gelip geçmiş olan bir ümmet içinde gönderdik ki, onlar Rahmân\'a küfredip dururlarken, sen onlara sana vahyettiğimiz kitabı okuyasın. De ki: "O Rahmân benim Rabbimdir, O\'ndan başka tanrı yoktur. Ben O\'na dayandım, tevbem de O\'nadır.'),
(1738, 13, 31, 'Bir Kur\'ân ki, onunla dağlar yürütülse veya onunla yer parçalansa veya onunla ölüler konuşturulsa (o yine bu Kur\'an olurdu). Fakat emir bütünüyle Allah\'ındır. İman edenler, kâfirlerden ümit kesip daha anlamadılar mı ki, Allah dileseydi, elbette insanların hepsine toptan hidayet buyururdu. O küfürde direnenlerin kendi sanatlarıyla başlarına musibet inip duracak, ya da yurtlarının yakınına konacak. Nihayet Allah\'ın vaadi gelecek. Muhakkak ki, Allah vaad ettiği zamanı şaşırmaz.'),
(1739, 13, 32, 'Andolsun ki, senden önceki peygamberlerle de alay edildi. Ben de o kâfirlere bir süre için meydan verdim. Sonra da tuttum onları cezalandırdım. O vakit azabım nasıl imiş (gördüler).'),
(1740, 13, 33, 'Bütün kazandıklarıyla her bir nefsin üzerinde böylesine hükümran olan başka kim vardır? Böyle iken tuttular da Allah\'a ortaklar uydurdular. De ki: "Onlara isimler verip durun bakalım. Siz O\'na yeryüzünde bilmediği bir şey mi haber vereceksiniz? Yoksa anlamı olmayan kuru bir laf mı? Doğrusu küfre sapanlara kendi oyunları güzel gösterildi de yoldan saptırıldılar. Allah her kimi saptırırsa, artık onu yola getirecek kimse yoktur.'),
(1741, 13, 34, 'Onlara dünya hayatında bir azap vardır. Ahiret azabı ise elbette daha çetindir. Onları Allah\'dan koruyacak da yoktur.'),
(1742, 13, 35, 'Müttakilere vaad olunan cennetin misali şöyledir: Altından ırmaklar akar durur, yemişleri süreklidir, gölgeleri de. İşte bu, takva yolunu tutanların akıbetidir. Kâfirlerin akıbeti de ateştir.'),
(1743, 13, 36, 'Bir de kendilerine kitap verdiklerimiz, sana indirilen (vahiy) le sevinç duyuyorlar. Bununla beraber hizipleşenlerden, âyetlerin bir kısmını inkâr edenler de vardır. De ki: "Ben ancak Allah\'a kulluk etmekle ve O\'na şirk koşmamakla emrolundum. Ben O\'na davet ediyorum, dönüşüm de O\'nadır."'),
(1744, 13, 37, 'Ve işte biz o Kur\'ân\'ı Arapça bir hüküm olarak indirdik. Yemin olsun ki, eğer sen, sana vahiyle gelen bu bilgiden sonra onların keyiflerine uyacak olursan, sana Allah\'dan ne bir dost vardır, ne de bir koruyucu.'),
(1745, 13, 38, 'Andolsun ki, biz senden önce de peygamberler gönderdik. Onlara da eşler ve çocuklar verdik. Allah\'ın izni olmadan herhangi bir âyet getirmek ise hiçbir peygamberin haddi değildir. Her ecel için bir yazı vardır.'),
(1746, 13, 39, 'Allah dilediğini imha eder, dilediğini de yerinde bırakır. Ana kitap O\'nun katındadır.'),
(1747, 13, 40, 'Onlara vaad ettiğimiz azabın bir kısmını sana göstersek, yahut seni, onu görmeden vefat ettirsek, yine de sana düşen sadece tebliğ etmek, bize düşen de hesaba çekmektir.'),
(1748, 13, 41, 'Görmüyorlar mı ki, biz yeri etrafından eksiltip duruyoruz. Allah öyle hükmeder ki, O\'nun hükmünü engelleyecek kimse yoktur. O çok hızlı hesap görür.'),
(1749, 13, 42, 'Onlardan öncekiler de hileler yapmışlardı. Fakat sonuçta bütün hileler(in cezası) Allah\'a aittir. Her nefsin ne kazandığını O bilir. Bu dünyanın akıbetinin kime ait olduğunu kâfirler de yakında bilecekler.'),
(1750, 13, 43, 'O kâfirler: "Sen Allah tarafından gönderilmiş bir peygamber değilsin" diyorlar. De ki: "Benimle sizin aranızda şahit olarak Allah yeter, bir de yanında kitap ilmi bulunan (yeter)."');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'Elif, Lâm, Râ. Bu Kur\'ân öyle büyük bir kitaptır ki, insanları Rablerinin izni ile karanlıklardan aydınlığa, her şeye galip ve hamde lâyık olan Allah\'ın yoluna çıkarman için onu sana indirdik.'),
(1752, 14, 2, 'O Allah\'ın (yolu) ki, göklerde ve yerde ne varsa hepsi O\'nundur. Şiddetli bir azabdan dolayı vay kâfirlerin haline!'),
(1753, 14, 3, 'Onlar, o kimselerdir ki dünya hayatını ahirete tercih ederler, (insanları) Allah\'ın yolundan çevirirler ve onun eğrilmesini isterler. İşte bunlar, çok büyük bir sapıklık içindedirler.'),
(1754, 14, 4, 'Biz, her peygamberi, ancak bulunduğu kavminin diliyle gönderdik ki, onlara apaçık anlatsın. Bu itibarla Allah dilediğini sapıklıkta bırakır, dilediğini de hidayete erdirir. O her şeye galibdir, hükmünde hikmet sahibidir.'),
(1755, 14, 5, 'And olsun ki Musa\'yı âyetlerimizle gönderdik. Ona şöyle dedik: Kavmini karanlıklardan aydınlığa çıkar, onlara Allah\'ın (felaket) günlerini hatırlat. Şüphe yok ki bunda her sabredip şükreden için nice ibretler vardır.'),
(1756, 14, 6, 'Musa kavmine demişti ki: "Allah\'ın üzerinizdeki nimetini hatırlayın. Çünkü O, bir vakit sizi Firâvun ailesinden kurtardı. Onlar sizi işkencenin en kötüsüne sürüyorlar ve oğullarınızı kesip kadınlarınızı da diri bırakıyorladı. Ve bunda Rabbinizden size büyük bir imtihan vardır."'),
(1757, 14, 7, 'Ve hatırlayın ki Rabbiniz size şöyle bildirmişti: Yüceliğim hakkı için şükrederseniz elbette size (nimetimi) artırırım ve eğer nankörlük ederseniz hiç şüphesiz azabım çok şiddetlidir.'),
(1758, 14, 8, 'Musa dedi ki: Siz ve yeryüzünde bulunanların hepsi nankörlük etseniz, iyi biliniz ki Allah hepinizden zengindir, hamdedilmeye layıktır.'),
(1759, 14, 9, 'Sizden öncekilerin; Nuh, Âd ve Semûd kavimlerinin ve onlardan sonra gelenlerin haberleri size gelmedi mi? Onları, Allah\'tan başkası bilmez. Peygamberleri onlara mucizeler getirdi de onlar ellerini ağızlarına koydular ve dediler ki: "Biz sizinle gönderileni inkâr ettik ve bizi çağırdığınız şeyden de şüphe ve endişe içindeyiz."'),
(1760, 14, 10, 'Peygamberleri dedi ki: "Gökleri ve yeri yaratan, Allah hakkında da şüphe mi var? O, sizi günahlarınızı bağışlamak için çağırıyor ve belirlenmiş bir süreye kadar size müsade ediyor." Onlar da: "Siz sadece bizim gibi bir insansınız, bizi babalarımızıntaptıklarından alıkoymak istiyorsunuz. O halde bize apaçık bir delil getirin!" dediler.'),
(1761, 14, 11, 'Peygamberleri onlara dediler ki: "(Evet) biz ancak sizin gibi bir insanız, ama Allah kullarından dilediğine nimetini lütfeder. Ve Allah\'ın izni olmadıkça bizim size bir delil getirmemize imkan yoktur. Müminler ancak Allah\'a dayansınlar.'),
(1762, 14, 12, 'Bize yollarımızı göstermişken neden biz Allah\'a dayanıp güvenmeyelim? Elbette bize yaptığınız eziyetlere katlanacağız. Tevekkül edenler yalnız Allah\'a tevekkül etsinler."'),
(1763, 14, 13, 'İnkâr edenler peygamberlerine dediler ki: "Ya sizi mutlaka yurdumuzdan çıkaracağız, ya da mutlaka dinimize döneceksiniz!" Rableri de onlara: "Zâlimleri mutlaka helak edeceğiz" diye vahyetti.'),
(1764, 14, 14, 'Ve Onlardan sonra sizi mutlaka o yerde yerleştireceğiz. Bu, makamımdan ve tehdidimden korkan içindir.'),
(1765, 14, 15, '(Peygamberler, düşmanlarına karşı) fetih istediler, ve her zorba inatçı hüsrana uğradı.'),
(1766, 14, 16, 'Ardından da Cehennem vardır, orada kendisine irinli su içirilecektir.'),
(1767, 14, 17, 'Onu yutmaya çalışacak, fakat boğazından geçiremeyecek ve her yandan ona ölüm gelecek, fakat o ölemez. Arkasından da çetin bir azab gelecektir.'),
(1768, 14, 18, 'Rabblerini inkâr edenlerin durumu tıpkı fırtınalı bir günde rüzgarın şiddetle savurduğu bir küle benzer. Kazandıklarından hiçbir şeyi elde edemezler. İşte asıl uzak sapıklık budur.'),
(1769, 14, 19, 'Gökleri ve yeri gerçekten Allah\'ın yarattığını görmedin mi? O dilerse sizi yok edip yepyeni bir halk getirir.'),
(1770, 14, 20, 'Bu, Allah\'a göre önemli bir şey değildir.'),
(1771, 14, 21, '(Kıyamet günü) İnsanların hepsi Allah\'ın huzuruna çıkacaklar. Ve zayıflar büyüklük taslayanlara şöyle diyecekler: "Bizler, sizlere uymuştuk. Şimdi siz, Allah\'ın azabından en ufak bir şeyi bizden savabilir misiniz?" Onlar da diyecekler ki: "Allah bizi hidayete erdirseydi, biz de size doğru yol gösterirdik. Artık şimdi bizler sızlansak da sabretsek de birdir. Çünkü kaçacak yerimiz yoktur."'),
(1772, 14, 22, 'İş bitince şeytan onlara şöyle diyecek: "Şüphesiz ki Allah size gerçek olanı vaad etti, ben de size vaad ettim, ama sonra caydım! Zaten benim size karşı bir gücüm yoktu. Ancak ben sizi (küfür ve isyana) çağırdım, siz de geldiniz. O halde beni kınamayın, kendi kendinizi kınayın! Ne ben sizi kurtarabilirim, ne de siz beni kurtarabilirsiniz! Ben, önceden beni Allah\'a ortak koşmanızı da kabul etmemiştim." Doğrusu zalimler için acı bir azab vardır!'),
(1773, 14, 23, 'İman edip salih ameller işleyenler ise, Rablerinin izniyle içinde sürekli kalacakları ve altından ırmaklar akan cennetlere konulurlar. Oradaki dirlik temennileri "selâm!"dır.'),
(1774, 14, 24, 'Görmedin mi? Allah nasıl bir misal verdi. Güzel bir söz, kökü (yerde) sabit, dalları gökte olan güzel bir ağaç gibidir.'),
(1775, 14, 25, '(O ağaç) Rabbinin izniyle her zaman meyve verir. Öğüt alsınlar diye Allah insanlara böyle misaller verir.'),
(1776, 14, 26, 'Kötü sözün durumu da, yerden koparılmış, kökü olmayan kötü bir ağaca benzer.'),
(1777, 14, 27, 'Allah, iman edenleri, dünya hayatında da, ahirette de sağlam bir söz üzerinde tutar; zalimleri de saptırır ve Allah, dilediğini yapar.'),
(1778, 14, 28, 'Allah\'ın nimetlerine nankörlükle karşılık veren ve sonunda milletlerini helak yurduna konduranları görmedin mi?'),
(1779, 14, 29, 'Onlar, cehenneme girecekler. O ne kötü karargâhtır.'),
(1780, 14, 30, 'Allah\'ın yolundan saptırmak için Allah\'a eşler koştular. De ki: "Şimdilik eğleniniz! Çünkü varacağınız yer ateştir. "'),
(1781, 14, 31, '(Ey Muhammed!) İman eden kullarıma söyle: "Namazı dosdoğru kılsınlar, alışveriş ve dostluğun olmadığı bir günün gelmesinden önce, kendilerine verdiğimiz rızıktan açık ve gizli (Allah için) harcasınlar."'),
(1782, 14, 32, 'Allah öyle bir Allah\'tır ki; gökleri ve yeri yarattı, gökten su indirdi, onunla size rızık olarak çeşitli meyveler çıkardı; emri gereğince denizde yüzüp gitmeleri için gemileri emrinize verdi, ırmakları da emrinize verdi.'),
(1783, 14, 33, 'Sürekli olarak yörüngelerinde hareket eden ay ve güneşi, geceyi ve gündüzü sizin emrinize verdi.'),
(1784, 14, 34, 'O, Kendisinden isteyebileceğiniz her şeyi size verdi. Allah\'ın nimetini saymak isterseniz sayamazsınız! Doğrusu insan çok zalim, çok nankördür.'),
(1785, 14, 35, 'Hatırla ki; Bir zaman İbrahim şöyle demişti: "Rabbim! Bu şehri güvenli kıl! Beni ve oğullarımı putlara tapmaktan uzak tut!'),
(1786, 14, 36, '"Rabbim! Çünkü onlar (putlar) insanlardan birçoğunun sapmasına sebep oldular. Şimdi kim bana uyarsa, o bendendir; kim bana karşı gelirse, artık sen gerçekten çok bağışlayan ve çok merhamet edensin.'),
(1787, 14, 37, '"Rabbimiz! Ben çocuklarımdan bir kısmını namazı dosdoğru kılmaları için, senin Beyti Haram\'ının yanında, ekinsiz bir vadiye yerleştirdim. Artık sen de insanlardan bir kısmını onlara meylettir. Ve onları bazı meyvelerle rızıklandır ki şükretsinler.'),
(1788, 14, 38, '"Ey Rabbimiz! Sen bizim gizlediğimizi de açığa vurduğumuzu da şüphesiz bilirsin. Çünkü yerde ve gökte, hiçbir şey Allah\'tan gizli kalmaz.'),
(1789, 14, 39, '"İhtiyarlık halimde bana İsmail\'i ve İshak\'ı lutfeden Allah\'a hamd olsun. Şüphesiz ki Rabbim duamı çok iyi işitir.'),
(1790, 14, 40, '"Ey Rabbim! Beni ve soyumdan gelecekleri namazını dosdoğru kılanlardan eyle! Ey Rabbimiz! duamı kabul et!'),
(1791, 14, 41, '"Ey Rabbimiz! Herkesin hesaba çekileceği günde beni, anababamı ve müminleri bağışla!"'),
(1792, 14, 42, 'Ey Peygamber! Sakın zalimlerin yaptıklarından Allah\'ın gâfil olduğunu sanma! Ancak Allah, onların cezalarını, gözlerin dışa fırlayacağı güne erteler.'),
(1793, 14, 43, 'O gün, başlarını dikerek koşacaklar, gözleri kendilerine bile dönmeyecek ve gönülleri bomboş kalacaktır.'),
(1794, 14, 44, 'Ey Peygamber! İnsanları, azabın geleceği gün ile korkut. O gün, zalimler şöyle diyecekler: "Ey Rabbimiz! Bizi yakın bir zamana kadar ertele de senin davetine uyalım ve peygamberlere tâbi olalım." Onlara: "Daha önce ahirete intikal etmeyeceğinize dair yemin etmemiş miydiniz?" denilir.'),
(1795, 14, 45, 'Siz, kendilerine zulmedenlerin yurtlarında oturdunuz. Onlara nasıl azab ettiğimiz size apaçık belli oldu. Ve size misaller de vermiştik.'),
(1796, 14, 46, 'Gerçekten onlar çeşitli hileler ve tuzaklar kurdular. Allah katında da onlara hilelerine karşı azab var; isterse onların hileleri dağları yerinden oynatacak olsun'),
(1797, 14, 47, 'O halde sakın Allah\'ın peygamberlerine olan vaadinden cayacağını sanma! Şüphesiz Allah her şeye galiptir, intikam sahibidir.'),
(1798, 14, 48, 'O gün yeryüzü bir başka yere, gökler, başka göklere çevirilecek ve bütün varlıklar, kabirlerinden çıkıp bir ve gücüne karşı durulmaz olan Allah\'ın huzuruna toplanacaklardır.'),
(1799, 14, 49, 'O gün, suçluların zincire vurulmuş olduğunu görürsün.'),
(1800, 14, 50, 'Gömlekleri katrandandır ve yüzlerini ateş kaplar.'),
(1801, 14, 51, 'Çünkü Allah, herkesi kazandığı ile cezalandıracaktır. Gerçekten Allah, hesabı çabuk görendir.'),
(1802, 14, 52, 'Bu Kur\'ân, kendisiyle uyarılsınlar, Allah\'ın ancak bir tek ilâh olduğunu bilsinler ve akıl sahipleri öğüt alsınlar diye insanlara gönderilmiş bir tebliğdir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'Elif, Lâm, Râ. Bunlar kitabın ve apaçık bir Kur\'ân\'ın âyetleridir.'),
(1804, 15, 2, 'Bir zaman gelecek ki inkâr edenler, keşke müslüman olsaydık temennisinde bulunacaklardır.'),
(1805, 15, 3, 'Onları bırak yesinler, içsinler, zevk alsınlar; arzu onları oyalasın ilerde bileceklerdir.'),
(1806, 15, 4, 'Biz hiçbir memleketi (Allah katında) bilinen bir zamanı olmaksızın helak etmedik.'),
(1807, 15, 5, 'Hiçbir millet, ecelinin önüne geçemez ve onu geciktiremez.'),
(1808, 15, 6, 'Dediler ki: "Ey kendisine Kur\'ân indirilen (Muhammed)! Sen mutlaka bir mecnunsun."'),
(1809, 15, 7, '"Eğer peygamberlik davanda doğru kimselerdensen, bize melekleri getirmeliydin."'),
(1810, 15, 8, 'Biz o melekleri ancak, hak ile indiririz. Ve indirildikleri vakit de onlara (kâfirlere) hiç mühlet verilmez.'),
(1811, 15, 9, 'Hiç şüphe yok ki, Kur\'ân\'ı biz indirdik, elbette onu yine biz koruyacağız.'),
(1812, 15, 10, 'Andolsun, senden önceki milletler arasında da peygamberler gönderdik.'),
(1813, 15, 11, 'Onlara hiçbir peygamber gelmiyordu ki onunla alay etmiş olmasınlar.'),
(1814, 15, 12, 'Biz o küfrü suçluların kalbine işte böyle sokarız.'),
(1815, 15, 13, 'Kur\'âna iman etmezler, halbuki öncekilerin sünneti (inanmadıkları için başlarına gelenler) gelip geçmiştir.'),
(1816, 15, 14, 'Onlara gökten bir kapı açsak da oradan yukarı çıksalar,'),
(1817, 15, 15, '"Gözlerimiz perdelendi, daha doğrusu bize büyü yapılmıştır" derler.'),
(1818, 15, 16, 'Andolsun biz, gökte birtakım burçlar yarattık ve bakanlar için onu süsledik.'),
(1819, 15, 17, 'Ve göğü taşlanan bütün şeytanlardan koruduk.'),
(1820, 15, 18, 'Ancak kulak hırsızlığı eden şeytan hariç, onu apaçık bir alev sütunu takip eder.'),
(1821, 15, 19, 'Yeryüzünü düzgün bir şekilde yarattık ve oraya sabit dağlar yerleştirdik. Orada hikmetle ölçülmüş her şeyden bitkiler bitirdik.'),
(1822, 15, 20, 'Orada hem sizin için, hem de sizin rızıklarını veremediğiniz kimseler için geçim yollarını yarattık.'),
(1823, 15, 21, 'Her şeyin hazineleri yalnız bizim yanımızdadır. Fakat biz, onu ancak ihtiyaca göre, belli ölçülerde veririz.'),
(1824, 15, 22, 'Biz rüzgarları aşılayıcı olarak gönderdik ve gökten bir su indirip sizi onunla suladık. O suyu hazinelerde tutan da siz değilsiniz.'),
(1825, 15, 23, 'Elbette biz diriltiriz ve biz öldürürüz! Ve hepsinin varisleri de biziz.'),
(1826, 15, 24, 'Andolsun ki biz, içinizden İslâm\'da öne geçmek isteyenleri de biliriz, geri kalmak isteyenleri de biliriz.'),
(1827, 15, 25, 'Şüphesiz Rabbin O\'dur ki, onları kıyamet gününde hesaba çekmek için toplayacaktır. O, hikmet sahibidir, bilendir.'),
(1828, 15, 26, 'Andolsun ki biz insanı kuru bir çamurdan, şekillenmiş bir balçıktan yarattık.'),
(1829, 15, 27, 'Cinleri de daha önce insan vücudunun gözeneklerinden geçebilen güçlü bir ateşten yarattık.'),
(1830, 15, 28, 'Ey Peygamber! Rabbinin meleklere şöyle dediğini hatırla: "Ben, kuru balçıktan, şekil verilmiş kokuşmuş çamurdan bir insan yaratacağım."'),
(1831, 15, 29, 'Ben, onun yaratılışını tamamladığım ve ona ruhumdan üflediğim zaman, siz hemen onun için secdeye kapanın."'),
(1832, 15, 30, 'Bunun üzerine meleklerin hepsi toptan secde ettiler.'),
(1833, 15, 31, 'Yalnız İblis hariç. O secde edenlerle beraber olmaktan çekinmişti.'),
(1834, 15, 32, 'Allah buyurdu ki: "Ey İblis! Ne oluyor sana da, secde edenlerle beraber olmuyorsun?"'),
(1835, 15, 33, 'İblis şöyle dedi: "Kuru bir çamurdan, şekillenmiş bir balçıktan yarattığın bir insana secde edemezdim."'),
(1836, 15, 34, 'Allah şöyle buyurdu: "Öyle ise oradan çık! Sen, artık kovulmuş birisin."'),
(1837, 15, 35, '"Kıyamet gününe kadar lanet senin üzerindedir."'),
(1838, 15, 36, 'İblis: "Rabbim! Öyle ise insanların kabirlerinden kaldırılacakları güne (kıyamete) kadar bana mühlet ver" dedi.'),
(1839, 15, 37, 'Allah buyurdu ki: "Sen mühlet verilenlerdensin."'),
(1840, 15, 38, '"Allah katında bilinen vaktin gününe kadar..."'),
(1841, 15, 39, 'İblis şöyle dedi: "Rabbim! Beni saptırdığın için, mutlaka ben de yeryüzünde onlara günahları süsleyeceğim ve onların hepsini mutlaka azdıracağım!"'),
(1842, 15, 40, '"Ancak içlerinden ihlaslı kulların müstesnâdır."'),
(1843, 15, 41, 'Allah şöyle buyurdu: "İşte bana ulaşan dosdoğru yol budur."'),
(1844, 15, 42, '"Sana uyan azgınlardan başka, kullarımın üzerinde hiçbir nüfuzun yoktur."'),
(1845, 15, 43, '"Şüphesiz ki onların hepsine vaad edilen yer cehennemdir."'),
(1846, 15, 44, '"Cehennemin yedi kapısı vardır. O kapıların herbiri için birer grup ayrılmıştır."'),
(1847, 15, 45, 'Allahtan korkanlar, elbette cennetlerde ve pınarların başındadırlar.'),
(1848, 15, 46, 'Onlara: "Selametle güven içinde oraya girin" denir.'),
(1849, 15, 47, 'Biz o cennetliklerin kalblerindeki kinleri çıkarır atarız. Hepsi kardeşler olarak sevinç içinde karşılıklı koltuklara otururlar.'),
(1850, 15, 48, 'Orada kendilerine hiçbir yorgunluk gelmeyecek. Oradan çıkarılacak da değillerdir.'),
(1851, 15, 49, 'Kullarıma haber ver ki, gerçekten ben çok bağışlayıcı ve pek merhamet ediciyim.'),
(1852, 15, 50, 'Bununla beraber azabım da çok acıklı bir azabdır. Bunları geçmişten bazı örneklerle açıklamak üzere:'),
(1853, 15, 51, 'Hem o kullara, İbrahim\'in misafirlerinden de haber ver.'),
(1854, 15, 52, 'Hani melekler, İbrahim\'in yanına girdikleri zaman, "selam" demişler, İbrahim de onlara: "Biz sizden korkuyoruz" demişti.'),
(1855, 15, 53, 'Melekler: "Korkma! Gerçekten biz sana bilgin bir oğul müjdeliyoruz" dediler.'),
(1856, 15, 54, 'İbrahim dedi ki: "Bana ihtiyarlık gelmişken, beni mi müjdeliyorsunuz, neye dayanarak beni müjdeliyorsunuz?"'),
(1857, 15, 55, 'Melekler: "Seni gerçekle müjdeliyoruz. Sakın Allah\'ın rahmetinden ümidini kesenlerden olma!" dediler.'),
(1858, 15, 56, 'İbrahim dedi ki: "Rabbimin rahmetinden, sapıklardan başka kim ümit keser?"'),
(1859, 15, 57, '"Ey elçiler! Başka ne işiniz var?" dedi.'),
(1860, 15, 58, 'Melekler şöyle dediler: "Biz suçlu bir kavmi cezalandırmak için gönderildik.'),
(1861, 15, 59, 'Ancak Lût ailesi müstesnâdır. Biz, onların hepsini muhakkak kurtaracağız.'),
(1862, 15, 60, 'Yalnız Lût\'un karısı müstesnâ, çünkü onun helak edilenlerle birlikte yok edilmesini takdir ettik.'),
(1863, 15, 61, 'Melek olan elçiler, Lût kavmine gelince,'),
(1864, 15, 62, 'Lût dedi ki: "Doğrusu siz ürkülecek bir kavimsiniz."'),
(1865, 15, 63, 'Elçiler dediler ki: "Bilakis biz sana onların şüphe ettiği azabı getirdik."'),
(1866, 15, 64, '"Sana gerçeği getirdik; biz elbette doğru söylüyoruz."'),
(1867, 15, 65, '"Gecenin bir bölümünde aileni yola çıkar, sen de arkalarından yürü ve sizden kimse ardına bakmasın; istenen yere gidin."'),
(1868, 15, 66, 'Biz, Lût\'a şu kesin emri vahyettik: "Bu kâfirler sabaha çıkarken muhakkak kökleri kesilmiş olacaktır."'),
(1869, 15, 67, 'Şehir halkı, insan şeklindeki güzel yüzlü melekleri görünce, onlara iğrenç işlerini yapabileceklerini düşünüp sevinerek geldiler.'),
(1870, 15, 68, 'Lût, kavmine şöyle dedi: "Bunlar benim misafirlerimdir, beni rüsvay etmeyin."'),
(1871, 15, 69, '"Allah\'tan korkun! Beni mahcub etmeyin."'),
(1872, 15, 70, 'Lût kavmi şöyle dedi: "Biz sana kimsenin koruyuculuğunu yapmamanı söylememiş miydik?"'),
(1873, 15, 71, 'Lût şöyle dedi: "İşte kızlarım! Düşündüğünüzü yapacaksanız (onlarla evlenin).'),
(1874, 15, 72, 'Resulüm! Ömrüne yemin olsun ki gerçekten onlar, sarhoşlukları içinde bocalayıp duruyorlardı.'),
(1875, 15, 73, 'Güneş doğarken o korkunç çığlık onları yakaladı.'),
(1876, 15, 74, 'Biz, onların şehirlerinin üstünü altına geçirdik ve üzerlerine de balçıktan pişirilmiş taşlar yağdırdık.'),
(1877, 15, 75, 'Gerçekten bunda, düşünen keskin anlayışlılar için ibretler vardır.'),
(1878, 15, 76, 'Hem o Lût kavminin bulunduğu şehir harabesi bir yol üzerinde bulunmaktadır.'),
(1879, 15, 77, 'Şüphesiz ki, bunda iman edenler için bir ibret vardır.'),
(1880, 15, 78, 'Eyke halkı da gerçekten zalimlerdi.'),
(1881, 15, 79, 'Biz Eyke halkından da intikâm aldık. İkisi de (Eyke ve Medyen) açık bir yol üzerindedir.'),
(1882, 15, 80, 'Şüphesiz ki, Hıcr halkı da peygamberleri yalanladılar.'),
(1883, 15, 81, 'Biz, onlara âyetlerimizi vermiştik de onlar, yüz çeviriyorlardı'),
(1884, 15, 82, 'Onlar, dağlardan emniyetli emniyetli evler yontuyorlardı.'),
(1885, 15, 83, 'Onları da sabahleyin korkunç bir çığlık yakaladı.'),
(1886, 15, 84, 'Kazanmakta oldukları şeyler, onlardan hiçbir zararı savmadı.'),
(1887, 15, 85, 'Biz gökleri, yeri ve aralarındaki varlıkları ancak hak ve hikmetle yarattık ve elbette ki, kıyamet kopacaktır. (Ey Peygamber!) Şimdi sen onlara yumuşak davran ve güzel muamele et.'),
(1888, 15, 86, 'Şüphesiz Rabbin kemaliyle yaratandır ve iyi bilendir.'),
(1889, 15, 87, 'Andolsun ki, biz sana tekrarlanan yedi âyeti (Fatihayı) ve yüce Kur\'ân\'ı verdik.'),
(1890, 15, 88, 'Sakın o kâfirlerden birtakımlarına verip de kendilerini zevklendirdiğimiz şeye (mal ve servete) heveslenip göz dikeyim deme. Onlardan dolayı üzülme. Müminlere merhamet kanatlarını indir.'),
(1891, 15, 89, 'De ki: "Şüphesiz ben apaçık bir uyarıcıyım."'),
(1892, 15, 90, '(İnanmazsanız başınıza) tıpkı o taksimcilere (yahudi ve hıristiyanlara) indirdiğimiz azap gibi (bir azab inecektir).'),
(1893, 15, 91, 'Onlar, Kur\'ân\'ın bir kısmına inanıp bir kısmına inanmayarak onu kısım kısım böldüler.'),
(1894, 15, 92, 'Rabbin hakkı için biz, mutlaka onların hepsini yaptıklarından dolayı hesaba çekeceğiz.'),
(1895, 15, 93, 'Rabbin hakkı için biz, mutlaka onların hepsini yaptıklarından dolayı hesaba çekeceğiz.'),
(1896, 15, 94, 'Şimdi sen emrolunduğunu açıkça tebliğ et. Müşriklerden yüz çevir.'),
(1897, 15, 95, 'Muhakkak ki alay edenlere karşı biz sana yeteriz.'),
(1898, 15, 96, 'Onlar Allah ile birlikte başkasını ilâh edinenlerdir. Onlar yakında bileceklerdir.'),
(1899, 15, 97, 'Gerçekten biliriz ki, onların söylediklerine göğsün daralıyor.'),
(1900, 15, 98, 'O halde Rabbini hamd ile tesbih et. Ve secde edenlerden ol.'),
(1901, 15, 99, 'Ve sana ölüm gelinceye kadar Rabbine ibadet et.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'Allah\'ın emri geldi, sakın onu acele edip istemeyiniz. Allah, müşriklerin koştukları ortaklardan münezzeh ve yücedir.'),
(1903, 16, 2, 'Kendi emrinden ruh (vahiy) ile melekleri, kullarından dilediği peygamberlere indirip şu gerçeği insanlara bildirin, buyuruyor: Benden başka hiçbir ilâh yoktur. Ancak benden korkun.'),
(1904, 16, 3, 'Allah gökleri ve yeri hikmeti ile yarattı. O, kâfirlerin ortak koştukları şeylerden çok yücedir.'),
(1905, 16, 4, 'O, insanı bir meniden (spermadan) yarattı. Bir de bakarsın ki o, Rabbine karşı apaçık bir düşmandır.'),
(1906, 16, 5, 'Hayvanları da O yarattı. Onlarda sizi ısıtacak şeyler ve birçok faydalar vardır. Ve siz onlardan bir kısmını da yersiniz.'),
(1907, 16, 6, 'O hayvanları, akşam vakti getirirken ve sabahleyin salarken, onlarda sizin için bir güzellik ve zevk vardır.'),
(1908, 16, 7, 'Bu hayvanlar, ancak güçlükle varabileceğiniz bir memlekete yüklerinizi taşır. Rabbiniz, şüphesiz çok şefkatlidir, çok merhametlidir.'),
(1909, 16, 8, 'Hem kendilerine binesiniz, hem de zinet olsun diye atları, katırları, ve merkepleri yarattı. Ve şu anda bilemeyeceğiniz daha nice şeyler yaratacak.'),
(1910, 16, 9, 'Doğru yolu göstermek Allah\'a aittir. Onun eğrisi de vardır. Allah dileseydi, sizin hepinizi hidayete erdirirdi.'),
(1911, 16, 10, 'Sizin için gökten su indiren O\'dur. İçecek su ondandır; hayvanlarınızı otlattığınız bitkiler de o su ile yetişir.'),
(1912, 16, 11, 'Allah, sizin için, o su ile ekin, zeytin, hurmalıklar, üzümler ve her çeşit meyveleri bitirir. Şüphesiz ki bunda düşünecek bir topluluk için büyük bir ibret vardır. ()'),
(1913, 16, 12, 'Geceyi, gündüzü, güneşi ve ayı sizin hizmetinize O verdi. Bütün yıldızlar da O\'nun emrine boyun eğmişlerdir. Şüphesiz ki bunda aklını kullanan bir toplum için ibretler vardır.'),
(1914, 16, 13, 'Yeryüzünde sizin için yarattığı değişik renklerdeki şeyleri de sizin hizmetinize sunmuştur. Elbette bunda öğüt alan kimseler için bir ibret vardır.'),
(1915, 16, 14, 'Yine denizden taze et (balık) yiyesiniz ve ondan takındığınız süs eşyasını çıkarasınız diye, denizi emrinize veren Allah\'tır. Gemilerin denizde suyu yararak gittiklerini görüyorsun. Lütfundan rızık aramanız ve şükretmeniz için Allah böyle yapmıştır.'),
(1916, 16, 15, 'Allah, yeryüzü sizi sarsmasın diye oraya sabit dağlar yerleştirdi. Yolunuzu bulmanız için de nehirler ve yollar yarattı.'),
(1917, 16, 16, 'Daha birçok âlametler yarattı. İnsanlar geceleyin de Allah\'ın yarattığı yıldızlarla yönlerini bulurlar.'),
(1918, 16, 17, 'Hiç yaratan (Allah), yaratmayan (putlar) gibi olur mu? Artık siz düşünmez misiniz?'),
(1919, 16, 18, 'Halbuki Allah\'ın nimetlerini teker teker saymaya kalkışsanız, onları sayamazsınız. Muhakkak ki Allah çok bağışlayıcıdır, çok merhametlidir.'),
(1920, 16, 19, 'Allah, gizlediğinizi de açıkladığınızı da bilir.'),
(1921, 16, 20, 'Kâfirlerin Allah\'tan başka yalvardıkları (putlar) ise, hiçbir şey yaratamazlar. Çünkü onlar, kendileri yaratılmışlardır.'),
(1922, 16, 21, 'O putlar, hep ölüdürler, diri değildirler ve insanların öldükten sonra ne zaman dirileceklerini de bilmezler.'),
(1923, 16, 22, 'İlâhınız bir tek ilâhtır. Bununla beraber ahirete inanmayanların kalbleri inkârcı, kendileri de böbürlenen kimselerdir.'),
(1924, 16, 23, 'Şüphesiz ki Allah, onların gizlediklerini de açığa vurduklarını da bilir. Doğrusu Allah, kendilerini büyük görüp hakkı kabul etmeyenleri sevmez.'),
(1925, 16, 24, 'Onlara: "Rabbiniz ne indirdi? denildiği zaman "Öncekilerin efsanelerini" dediler.'),
(1926, 16, 25, 'Bunu söylemelerinin sebebi şu: Kıyamet günü, kendi günahlarını tam olarak yüklendikten başka, bilgisizlikleri yüzünden saptırmakta oldukları kimselerin günahlarından bir kısmını da yükleneceklerdir. Dikkat edin, yüklendikleri günah ne kötüdür!'),
(1927, 16, 26, 'Onlardan öncekiler de tuzak kurdular. Fakat Allah onların binalarını temelinden sarstı, çatı tepelerinden üzerlerine çöktü ve azap onlara farkedemedikleri bir yönden geldi.'),
(1928, 16, 27, 'Sonra kıyamet günü Allah, O kâfirleri rezil rüsvay edecek ve diyecek ki: "Hani uğrunda müminlere karşı düşman kesildiğiniz ortaklarım nerede?" Kendilerine ilim verilmiş olanlar: "Şüphesiz bugünün rezilliği ve kötülüğü kâfirleredir." diyeceklerdir.'),
(1929, 16, 28, '(O kâfirler), kendilerine zulmetmiş kimseler olarak, meleklerin, canlarını aldıkları kimselerdir. O vakit onlar şöyle diyerek teslim olurlar: "Biz, bir kötülükten dolayı yapmıyorduk." (Onlara): "Hayır, Allah sizin ne maksatla yaptığınızı elbette çok iyi bilendir."'),
(1930, 16, 29, '"O halde içinde ebedî kalacağınız cehennemin kapılarından girin" denir. Kibirlenenlerin yeri ne kötüdür!'),
(1931, 16, 30, 'Kötülüklerden sakınanlara: "Rabbiniz ne indirdi?" denilince: "Hayır indirdi" derler. Bu dünyada güzel amel işleyenlere güzel bir mükafat var. Elbette ahiret yurdu ise daha hayırlıdır. Allah\'tan korkanların yurdu ne güzeldir!'),
(1932, 16, 31, 'O girecekleri yer, Adn cennetleridir ki, altından ırmaklar akar. Orada Allah\'tan korkanlara diledikleri nimetler vardır. İşte Allah, takva sahiplerini böyle mükafatlandırır.'),
(1933, 16, 32, 'Takva sahipleri o kimselerdir ki, melekler, canlarını hoş ve rahat halde alırlar. "Selam size, yapmış olduğunuz güzel işlerin mükafatı olarak girin cennet\'e..." derler.'),
(1934, 16, 33, 'Ancak kendilerine, ruhlarını alacak meleklerin gelmesini veya Rabbinin azab emrinin (kıyametin) gelip çatmasını bekliyorlar! Kendilerinden öncekiler de böyle yapmışlardı. Allah onlara zulmetmedi, fakat onlar kendilerine zulmetmişlerdi.'),
(1935, 16, 34, 'Bunun için, sonunda yaptıklarının cezası başlarına felaket oldu ve alay edip durdukları o azap, kendilerini kuşattı.'),
(1936, 16, 35, 'Allah\'a ortak koşanlar dediler ki: "Allah dileseydi, ne biz, ne atalarımız O\'ndan başka hiçbir şeye tapmazdık ve O\'nun emri dışında hiçbir şeyi haram kılmazdık" Kendilerinden öncekiler de böyle yaptılar. Buna karşı peygamberlerin vazifesi, ancak açık seçik bir tebliğden, ibarettir.'),
(1937, 16, 36, 'Andolsun ki biz her ümmete, "Allah\'a ibadet edin ve putlara tapmaktan sakının." diye bir peygamber gönderdik. Allah, bu ümmetlerden bir kısmına hidayet etti, bir kısmına da sapıklık hak olmuştur. Şimdi yer yüzünde bir gezip dolaşın da bakın ki, peygamberleri yalanlayanların sonunun ne olduğunu bir görün?'),
(1938, 16, 37, '(Ey Muhammed!) Sen o kâfirlerin hidayete ermelerini ne kadar istesen de Allah, saptırdığı kimseyi hidayete erdirmez. Onların hiçbir yardımcısı da yoktur.'),
(1939, 16, 38, 'Kâfirler, "Allah ölen kimseyi diriltmez." diye en kuvvetli yeminleriyle Allah\'a yemin ettiler. Hayır, bu ölüleri diriltmek, Allah\'ın kendisine karşı bir vaadidir. Ancak insanların çoğu bunu bilmezler.'),
(1940, 16, 39, 'Allah ölüleri diriltecek ki, o kâfirlerin, hakkında ihtilaf ettikleri şeyi onlara açıkça göstersin ve bunu inkâr edenler kendilerinin yalancı olduklarını bilsinler.'),
(1941, 16, 40, 'Biz bir şeyi dilediğimiz zaman, ona sözümüz sadece "ol" dememizdir. O da hemen oluverir.'),
(1942, 16, 41, 'Zulme uğradıktan sonra Allah yolunda hicret edenlere gelince, biz dünyada mutlaka onları güzel bir yere yerleştiririz. Halbuki bilirlerse ahiretin mükafatı elbette daha büyüktür.'),
(1943, 16, 42, 'O Muhacirler, müşriklerin eziyetlerine sabredenler ve Rablerine tevekkül edenlerdir.'),
(1944, 16, 43, '(Ey Peygamber!) Senden önce de, kendilerine vahyettiğimiz erkeklerden başkasını peygamber olarak göndermedik. Eğer bunu bilmiyorsanız Tevrat ve İncil âlimlerine sorun.'),
(1945, 16, 44, 'Biz o peygamberleri mucizelerle ve kitaplarla gönderdik. Ey Peygamberim! Sana da Kur\'ân\'ı indirdik ki, insanlara vahyedileni açıklayasın. Belki onlar da düşünürler.'),
(1946, 16, 45, 'Sinsice kötü tuzaklar kuranlar, Allah\'ın kendilerini yerin dibine geçiremeyeceğinden, yahut bilemeyecekleri bir yerden azabın gelmeyeceğinden emin mi oldular?'),
(1947, 16, 46, 'Yahut (rızık için) dolaşıp dururlarken (Allah\'ın azabının) kendilerini yakalayıvermesinden emin mi oldular? Üstelik onlar, azabı engelleyici de değillerdir.'),
(1948, 16, 47, 'Yahut ta kendilerini azar azar yakalayıp helak etmesinden emin mi oldular? Şüphesiz Rabbiniz çok şefkatlidir, çok merhametlidir.'),
(1949, 16, 48, 'Onlar, Allah\'ın yarattığı birtakım şeyleri görmediler mi ki? Gölgeleri Allah\'ın kudretine boyun eğip secde ederek, sağa sola döner, dolaşır.'),
(1950, 16, 49, 'Göklerde ve yer yüzünde bulunan canlılar ve bütün melekler, kibirlenmeden Allah\'a secde ederler.'),
(1951, 16, 50, 'Kendilerine hakim olan Rabblerinden korkarlar ve emrolundukları her şeyi yaparlar.'),
(1952, 16, 51, 'Allah, buyurmuştur ki: İki ilâh edinmeyin. O, ancak bir ilâhdır. Onun için yalnız benden korkun.'),
(1953, 16, 52, 'Göklerde ve yerde olan her şey yalnız O\'nundur. Din de daima O\'nundur. Böyle iken, siz Allah\'tan başkasından mı korkarsınız?'),
(1954, 16, 53, 'Sizde nimet namına ne varsa hep Allah dandır. Sonra size sıkıntı dokununca Allah a feryad edersiniz.  '),
(1955, 16, 54, 'Sonra Allah bu sıkıntıyı sizden kaldırdığı zaman, bir de bakarsınız ki, içinizden bir topluluk, hemen Rablerine ortak koşarlar.'),
(1956, 16, 55, 'Bunu kendilerine verdiğimiz nimete nankörlük etmek için yaparlar. Şimdi eğlenin bakalım! Fakat yakında bileceksiniz.'),
(1957, 16, 56, 'Bir de müşrikler kendilerine rızık olarak verdiğimiz şeylerden tutuyorlar mahiyetini bilmedikleri şeylere (putlara) pay ayırıyorlar. Allah\'a andolsun ki, siz bu yaptığınız iftiralardan mutlaka hesaba çekileceksiniz.'),
(1958, 16, 57, 'Onlar, Allah\'a kızlar isnad ediyorlar. O, bundan münezzehtir. Kendilerine ise erkek çocukları isnad ederler.'),
(1959, 16, 58, 'Halbuki onlardan birine, kız doğum haberi müjdelendiği zaman içi öfkeyle dolar, yüzü kapkara kesilir.'),
(1960, 16, 59, 'Kendisine verilen müjdenin kötülüğü, dolayısıyla kavminden gizlenir. Şimdi acaba o çocuğu zillet ve horluğa katlanarak saklayacak mı? Yoksa toprağa mı gömecek? Dikkat edin verdikleri hüküm ne kötüdür!'),
(1961, 16, 60, 'Ahirete iman etmeyenler için kötü sıfatlar var. En yüce sıfatlar ise, Allah\'ındır. O çok güçlüdür, hüküm ve hikmet sahibidir.'),
(1962, 16, 61, 'Eğer Allah insanları zulümleri yüzünden hesaba çekseydi, yeryüzünde kımıldayan tek canlı bırakmazdı. Fakat Allah onları, belli bir vakte kadar erteler. Müddetleri (ecelleri) geldiği zaman, onu ne bir saat erteleyebilirler, ne de öne alabilirler.'),
(1963, 16, 62, 'Müşrikler, kendilerinin hoşlanmadıkları şeyleri, Allah\'a isnad ediyorlar. Dilleri, en güzel şeylerin kendilerine ait olduğunu yalan yere durmadan söyler. Hiç şüphesiz onlar için, sadece ateş vardır. Oraya en önde gidip kalacaklardır.'),
(1964, 16, 63, 'Allah\'a yemin olsun ki, biz senden önce bir çok ümmetlere peygamberler gönderdik. Ne var ki şeytan, onlara amellerini bezeyip süslü gösterdi. Bugün de o şeytan, kâfirlerin dostudur. Onlar için acı bir azab vardır.'),
(1965, 16, 64, '(Ey Resulüm!) Biz, sana bu kitabı (Kur\'ânı) sırf hakkında ihtilafa düştükleri şeyi insanlara açıklaman için ve iman edecek topluma bir hidayet, bir rahmet olsun diye indirdik.'),
(1966, 16, 65, 'Allah gökten bir su indirdi ve onunla yeryüzüne ölümünden sonra hayat verdi. Şüphesiz ki bunda dinleyen bir millet için büyük bir ibret vardır.'),
(1967, 16, 66, 'Gerçekten süt veren hayvanlarda da size bir ibret vardır. Size işkembelerindeki yem artıklarıyla kandan meydana gelen, içenlere içimi kolay halis bir süt içirmekteyiz.'),
(1968, 16, 67, 'Hurma ve üzüm ağaçlarının meyvalarından da hem içki, hem de güzel gıdalar edinirsiniz. Şüphesiz ki bunda aklını kullanan kimseler için büyük bir ibret vardır.'),
(1969, 16, 68, 'Senin Rabbin bal arısına şöyle vahyetti: Dağlardan, ağaçlardan ve insanların kuracakları kovanlardan kendine evler edin.'),
(1970, 16, 69, 'Sonra meyvaların hepsinden ye de, Rabbinin (sana) kolay kıldığı yollara gir, diye ilham etti. Onların karınlarından renkleri çeşitli bir bal çıkar ki, onda insanlar için şifâ vardır. Şüphesiz ki bunda düşünen bir millet için, büyük bir ibret vardır.'),
(1971, 16, 70, 'Allah, sizi yarattı, sonra da sizi öldürecektir. İçinizden kimi de, biraz bilgiden sonra eşyayı önceki bildiği gibi bilmesin diye, ömrün en kötü çağına kadar yaşatılır. Şüphesiz ki Allah çok bilgili ve büyük kudret sahibidir.'),
(1972, 16, 71, 'Allah, rızık yönünden bir kısmınızı diğerlerinden üstün kıldı. Kendilerine bol rızık verilenler, rızıklarını ellerinin altındakilere vermiyorlar ki, onda eşit olsunlar. Durum böyle iken Allah\'ın nimetini inkâr mı ediyorlar?'),
(1973, 16, 72, 'Allah, size kendi cinsinizden eşler, o eşlerinizden de oğullar ve torunlar yarattı. Sizi helal ve güzel gıdalarla rızıklandırdı. Onlar, hâlâ batıla mı inanıyorlar? ve Allah\'ın nimetini inkâr mı ediyorlar?'),
(1974, 16, 73, 'Müşrikler, Allah\'ı bırakıp, göklerden ve yerden kendileri için hiçbir rızka sahip olmayan ve sahip olmaya da güçleri yetmeyen şeylere taparlar.'),
(1975, 16, 74, 'Artık Allah\'a ortaklar koşmayın. Çünkü Allah, (eşi bulunmadığını) bilir, siz bilmezsiniz.'),
(1976, 16, 75, 'Allah, hiçbir şeye gücü yetmeyen, başkasının malı olmuş bir köle ile, kendisine güzel bir rızık verilen ve o rızıkdan gizli ve açık olarak harcayan hür bir insanı misal verdi. Hiç bunlar eşit olur mu? Bütün hamd Allah\'a mahsustur. Doğrusu insanların çoğu bilmezler.'),
(1977, 16, 76, 'Allah şu iki adamı da misal verdi: Bunlardan biri dilsizdir, hiçbir şeye gücü yetmez; efendisine bir yüktür. Onu nereye gönderse bir hayır getiremez. Şimdi, bu adamla, adaletle emreden ve doğru yolda bulunan adam eşit olur mu?'),
(1978, 16, 77, 'Göklerin ve yerin gaybını bilmek Allah\'a aittir. Kıyametin kopuşu yalnız bir göz kırpması veya daha az bir zamandan başkası değildir. Şüphesiz Allah her şeye kadirdir.'),
(1979, 16, 78, 'Allah sizi annelerinizin karnından çıkardığı zaman hiçbir şey bilmiyordunuz. Şükredesiniz diye size işitme (duygusu), gözler ve gönüller verdi.'),
(1980, 16, 79, 'Göğün boşluğunda Allah\'ın emrine boyun eğdirilerek uçuşan kuşlara bakmadılar mı? Şüphesiz bunda inanan bir toplum için âyetler (ibretler) vardır.'),
(1981, 16, 80, 'Allah size evlerinizden bir huzur ve dinlenme yeri yaptı. Hayvanların derilerinden gerek yolculuğunuzda ve gerekse konaklama zamanlarınızda kolayca taşıyacağınız hafif evler (çadırlar v. s.) ve yünlerinden, yapağılarından ve kıllarından bir süreye kadar (giyinecek, kuşanacak, serilecek ve döşenecek) bir eşya ve ticaret malı yaptı.'),
(1982, 16, 81, 'Allah, yarattıklarından sizin için gölgeler yaptı ve sizin için dağlarda barınaklar yarattı. Sizi sıcaktan koruyacak elbiseler ve savaşta sizi koruyan elbiseler (zırhlar) yarattı. İşte böylece Allah müslüman olasınız diye üzerinize nimetini tamamlamaktadır.'),
(1983, 16, 82, 'Buna rağmen eğer yüz çevirirlerse, ey Muhammed! Artık sana düşen sadece açık bir şekilde tebliğden ibarettir.'),
(1984, 16, 83, 'Hem Allah\'ın nimetini bilirler, sonra da onu inkâr ederler. Onların çoğu kâfir kimselerdir.'),
(1985, 16, 84, 'Her ümmetten bir şahid getireceğimiz gün, artık kâfirlere ne izin verilecek, ne de onlardan özür dilemeleri istenecektir.'),
(1986, 16, 85, 'O zulmedenler, azabı gördükleri zaman, artık onlardan ne azab hafifletilir, ne de onlara süre verilir.'),
(1987, 16, 86, 'Ve o Allah\'a ortak koşanlar, ortak koştuklarını (putları) gördükleri zaman: "Rabbimiz! İşte bunlar, seni bırakıp da kendilerine taptığımız ortaklarımızdır" diyecekler. Koştukları ortaklar da onlara; "Siz mutlaka yalancılarsınız" diye söz atarlar.'),
(1988, 16, 87, 'O gün Allah\'a teslim bayrağını çekerler, bütün o uydurdukları şeyler kendilerini bırakıp kaybolup gitmişlerdir.'),
(1989, 16, 88, 'İnkâr eden ve (insanları) Allah yolundan çevirenler, diğer kimseleri de bozdukları için onlara azab üstüne azab artırdık.'),
(1990, 16, 89, 'Biz o gün, her ümmet içinde, kendilerinden kendi üzerlerine bir şahit göndereceğiz. Seni de onların üzerine şahit getireceğiz. Bu kitabı da, her şeyi açıklayan ve müslümanlara doğruyu gösteren bir rehber, bir rahmet kaynağı ve bir müjdeleyici olarak indirdik.'),
(1991, 16, 90, 'Şüphesiz ki Allah, size adaleti, iyilik yapmayı ve yakınlara bakmayı emreder; hayasızlıktan, fenalıktan ve azgınlıktan nehyeder. Öğüt almanız için size böyle öğüt verir.'),
(1992, 16, 91, 'Bir de anlaşma yaptığınızda Allah\'ın ahdini yerine getirin ve pekiştirdikten sonra yeminleri bozmayın. Allah\'ı üzerinize şahid tuttuğunuz halde, nasıl olur da bozarsınız! Şüphesiz ki Allah yaptıklarınızı bilir.'),
(1993, 16, 92, 'Bir ümmet, diğer bir ümmetten (sayıca ve malca) daha çok olduğu için, yeminlerinizi aranızda aldatma vasıtası yaparak, ipliğini sağlamca eğirdikten sonra onu söküp bozmaya çalışan kadın gibi olmayın. Allah sizi bununla imtihan eder ve şüphesiz hakkında ihtilaf ettiğiniz şeyleri kıyamet günü size mutlaka açıklayacaktır.'),
(1994, 16, 93, 'Allah dileseydi elbette hepinizi tek bir ümmet yapardı. Fakat Allah dilediğini saptırır ve dilediğine de hidayet verir. Şüphesiz ki, (kıyamet gününde) bütün yaptıklarınızdan sorumlu tutulacaksınız.'),
(1995, 16, 94, 'Yeminlerinizi aranızda aldatma ve fesada vasıta edinmeyin, sonra sağlam basmışken bir ayak kayar da Allah yolundan saptığınız için, dünyada kötü azabı tadarsınız. Ahirette de size büyük bir azab olur.'),
(1996, 16, 95, 'Allah\'ın ahdini az bir bedel karşılığında değişmeyin. Eğer bilirseniz muhakkak ki Allah katındaki sevap sizin için daha hayırlıdır.'),
(1997, 16, 96, 'Sizin yanınızdaki dünya malı tükenir, Allah\'ın katındakiler ise tükenmez. Muhakkak ki biz, Allah yolunda sabredenleri, yaptıkları amelin daha güzeliyle mükafatlandıracağız.'),
(1998, 16, 97, 'Erkekten ve dişiden, mümin olarak kim iyi amel işlerse muhakkak onu güzel bir hayat ile yaşatacağız ve yapmakta oldukları amellerin daha güzeliyle mükafatlarını elbette vereceğiz.'),
(1999, 16, 98, 'Şimdi Kur\'ân okumak istediğin zaman önce o kovulmuş şeytandan Allah\'a sığın.'),
(2000, 16, 99, 'Şüphesiz ki iman edip de Rablerine tevekkül edenler üzerinde o şeytanın hiçbir nüfuzu yoktur.'),
(2001, 16, 100, 'Şeytanın nüfuzu, ancak onu dost edinenlere ve Allah\'a ortak koşanlaradır.'),
(2002, 16, 101, 'Biz bir âyeti değiştirip yerine başka bir âyet getirdiğimiz zaman Allah ne indirdiğini pek iyi bilmiş iken kâfirler Peygambere: "Sen, ancak bir iftiracısın" dediler. Hayır öyle değil; onların çoğu bilmezler.'),
(2003, 16, 102, '(Ey Muhammed!) Onlara de ki: "Kur\'ân\'ı Cebrail, iman edenlere sebat vermek, müslümanlara bir hidayet ve bir müjde olmak için Rabbinin katından hak olarak indirdi.'),
(2004, 16, 103, 'Muhakkak biliyoruz ki kâfirler: "Kur\'ân\'ı Muhammed\'e bir insan öğretiyor" diyorlar. Peygambere öğretiyor zannında bulundukları kimsenin dili yabancıdır. Bu Kur\'ân ise apaçık bir Arapçadır.'),
(2005, 16, 104, 'Allah\'ın âyetlerine iman etmeyenleri, muhakkak ki Allah hidayete erdirmez ve onlara can yakıcı bir azab vardır.'),
(2006, 16, 105, 'Yalanı ancak Allah\'ın âyetlerine inanmayanlar uydurur. İşte onlar yalancıların ta kendileridir.'),
(2007, 16, 106, 'Kalbi iman ile sükûnet bulduğu halde (dinden dönmeye) zorlananlar dışında, her kim imanından sonra küfre kalbini açarsa, mutlaka onların üzerine Allah\'tan bir gazab gelir ve kendilerine çok büyük bir azab vardır.'),
(2008, 16, 107, 'Bu (azab) şundan dolayıdır ki, onlar, dünya hayatını sevmiş ve onu ahirete tercih etmişlerdir. Allah da kâfirler topluluğunu hidayete erdirmez.'),
(2009, 16, 108, 'Bunlar, o kimselerdir ki; Allah kalblerini, kulaklarını ve gözlerini mühürlemiştir. Ve onlar, gafillerin ta kendileridir.'),
(2010, 16, 109, 'Hiç şüphesiz onlar, ahirette perişan olup hüsrana uğrayanların ta kendileridir.'),
(2011, 16, 110, 'Sonra şüphesiz Rabbin, eziyet edildikten sonra hicret eden, sonra cihad eden ve sabreden kimselerin yardımcısıdır. Bunlardan sonra Rabbin elbette çok bağışlayıcıdır, çok merhametlidir.'),
(2012, 16, 111, 'O gün, herkes nefsini kurtarmak için uğraşarak gelir ve herkese yaptığı işin karşılığı tamamiyle ödenir ve hiç kimseye de zulmedilmez.'),
(2013, 16, 112, 'Allah bir şehri misal olarak verdi: Bu şehir güvenli, huzurlu idi, Oraya her yerden rızkı bol bol geliyordu. Ne var ki onlar Allah\'ın nimetlerine karşı nankörlük ettiler. Allah da onlara, yaptıkları işler yüzünden açlık ve korku elbisesini (felâketini) tattırdı.'),
(2014, 16, 113, 'Andolsun ki, onlara içlerinden bir peygamber geldi de onu yalanladılar. Bunun üzerine zulüm yaparlarken azab da onları yakalayıverdi.'),
(2015, 16, 114, 'Artık Allah\'ın size rızık olarak verdiği şeylerden helal ve temiz olarak yiyin. Allah\'ın nimetine şükredin, eğer gerçekten O\'na ibadet edecekseniz.'),
(2016, 16, 115, 'O size ancak ölü hayvanı, kanı, domuz etini ve Allah\'tan başkası adına kesilenleri haram kıldı. Her kim bu haram şeyleri yemeye mecbur kalırsa (başkasının hakkına) saldırmadan ve aşırı gitmeden yiyebilir. Şüphesiz Allah, çok bağışlayıcıdır, çok merhametlidir.'),
(2017, 16, 116, 'Dillerinizin yalan vasfetmesi ile: "Şu helaldir, şu haramdır" demeyin; aksi halde Allah\'a iftira etmiş olursunuz. Şüphesiz Allah\'a yalan uyduranlar asla kurtulamazlar.'),
(2018, 16, 117, 'Onlar için dünyada pek az bir menfaat var, ahirette ise çok acıklı bir azab vardır.'),
(2019, 16, 118, 'Sana anlattıklarımızı, daha önce yahudilere de haram kılmıştık. Biz onlara zulmetmemiştik. Fakat onlar kendi kendilerine zulmetmişlerdi.'),
(2020, 16, 119, 'Sonra şüphe yok ki Rabbin, bir cahillikle günah işleyip ardından tevbe eden ve durumunu düzelten kimseleri bağışlar. Şüphesiz ki Rabbin, bu tevbeden sonra Gafurdur, Rahîmdir (çok bağışlayıcıdır, çok merhametlidir.)'),
(2021, 16, 120, 'Şüphesiz İbrahim Allah\'a itaat eden, Hakk\'a yönelen bir önderdi. Ve hiçbir zaman müşriklerden olmadı.'),
(2022, 16, 121, 'Allah\'ın nimetlerine şükredendi. Allah onu seçmiş ve doğru yola iletmişti.'),
(2023, 16, 122, 'Ve biz ona (İbrahim\'e) iyilik verdik. Şüphesiz ki o, ahirette de salihlerdendir.'),
(2024, 16, 123, 'Sonra da (ey Muhammed!) sana: "Hakk\'a yönelen ve müşriklerden olmayan İbrahim\'in dinine tabi ol" diye vahyettik.'),
(2025, 16, 124, 'Cumartesi günü (avlanmamak), ancak onda ihtilafa düşenlere farz kılındı. Şüphesiz Rabbin onların ihtilaf edip durdukları şeyler hakkında kıyamet günü, aralarında elbette hükmünü verecektir.'),
(2026, 16, 125, '(Ey Resulüm!) Rabbinin yoluna hikmetle ve güzel öğütle çağır! Ve onlarla en güzel şekilde mücadele et. Şüphesiz Rabbin kendi yolundan sapanları en iyi bilendir ve O, hidayete kavuşanları da en iyi bilendir.'),
(2027, 16, 126, 'Eğer (bir suçtan dolayı) ceza verecek olursanız size yapılan azab ve cezanın misli ile ceza verin. Ama sabrederseniz, elbette o, sabredenler için daha hayırlıdır.'),
(2028, 16, 127, '(Ey Peygamber!) Sabret! Sabrın da ancak Allah\'ın yardımı iledir. Onlardan dolayı üzülme! Kurdukları tuzaklardan telaş edip sıkıntıya düşme!'),
(2029, 16, 128, 'Şüphesiz Allah, takva sahipleri ile ve iyilikte bulunanlarla beraberdir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Kulu Muhammed\'i geceleyin, Mescidi Haram\'dan kendisine bazı âyetlerimizi göstermek için, etrafını mübarek kıldığımız Mescidi Aksâ\'ya götüren Allah, her türlü noksan sıfatlardan münezzehtir. Şüphesiz ki her şeyi hakkıyla işiten, hakkıyla gören O\'dur.'),
(2031, 17, 2, 'Musa\'ya da kitap verdik ve beni bırakıp başkasını vekil edinmeyiniz diye onu İsrail oğulları için bir hidayet rehberi kıldık.'),
(2032, 17, 3, 'Ey Nuh\'la beraber gemiye taşıyarak kurtardığımız kimselerin soyundan olanlar! Doğrusu o çok şükredici bir kuldu.'),
(2033, 17, 4, 'Biz İsrailoğulları\'na Tevrat\'ta şu hükmü verdik: "Muhakkak siz, yeryüzünde iki defa fesat çıkaracaksınız ve muhakkak büyük bir yükselişle yükseleceksiniz."'),
(2034, 17, 5, 'Birincisinin zamanı gelince, üzerinize güçlü kuvvetli kullarımızı gönderdik. Onlar, evlerin aralarına girip araştırdılar. Bu yerine getirilmesi gereken bir vaad idi.'),
(2035, 17, 6, 'Sonra sizi tekrar o istilacılar üzerine galip kıldık ve size mallarla ve oğullarla yardım ettik. Ve toplum olarak sizin sayınızı artırdık.'),
(2036, 17, 7, 'Eğer iyilik ederseniz, kendinize iyilik etmiş olursunuz ve eğer kötülük ederseniz yine kendinizedir. Artık diğer fesadınızın zamanı gelince, yüzlerinizi üzüntüye sokmaları, kötülük yapmaları ve ilk kez girdikleri gibi yine Beyti Makdis\'e girmeleri, ele geçirdikleri yerleri mahvetmeleri için onları tekrar göndereceğiz.'),
(2037, 17, 8, 'Olur ki Rabbiniz size merhamet eder. Ama siz tekrar dönerseniz biz de döneriz. Cehennemi, kâfirler için kuşatıcı bir zindan yaptık.'),
(2038, 17, 9, 'Şüphesiz ki bu Kur\'ân, insanları en doğru ve en sağlam yola iletir ve salih amel işleyen müminlere büyük bir ecir olduğunu müjdeler.'),
(2039, 17, 10, 'Ahirete inanmayanlara da can yakıcı bir azab hazırlamışızdır.'),
(2040, 17, 11, 'İnsan, hayrın gelmesine dua ettiği gibi kötülüğün gelmesine de dua eder. İnsan pek acelecidir.'),
(2041, 17, 12, 'Biz geceyi ve gündüzü varlığımıza delalet eden birer delil kıldık. Sonra Rabbinizden bir lütuf aramanız, yılların sayısını ve hesabını bilmeniz için gecenin karanlığını silip (yerine) eşyayı aydınlatan gündüzün aydınlığını getirdik. İşte biz her şeyi uzun uzadıya anlattık.'),
(2042, 17, 13, 'Her insanın amel defterini boynuna doladık, kıyamet günü açılmış bulacağı kitabı önüne çıkarırız.'),
(2043, 17, 14, '"Kitabını oku! Bugün hesap görücü olarak sana nefsin yeter!" deriz.'),
(2044, 17, 15, 'Kim doğru yola gelirse sırf kendi iyiliği için gelir. Kim de saparsa ancak kendi aleyhine sapar. Hiçbir günahkar başkasının günah yükünü çekmez. Biz bir Peygamber göndermedikçe, hiç kimseye azab edecek değiliz.'),
(2045, 17, 16, 'Biz bir ülkeyi yok etmek istediğimiz zaman, şımarık varlıklılarına emrederiz, onlar itaat etmeyip orada kötülük işlerler. Böylece, o ülke helaka müstahak olur, biz de onu yerle bir ederiz.'),
(2046, 17, 17, 'Hem Nuh\'tan sonra nice nesilleri helak ettik. Kullarının günahlarını bilmek ve görmekte Rabbin yeter.'),
(2047, 17, 18, 'Her kim peşin isterse, dünyada ona, istediğimiz kimseye, dilediğimiz kadarını peşin veririz. Sonra ona cehennemi hazırlarız; kınanmış ve (rahmetimizden) kovulmuş olarak oraya girer.'),
(2048, 17, 19, 'Kim de ahireti isterse ve mümin olarak kendine yaraşır bir çaba ile onun için çalışırsa, öylelerinin çalışmalarının karşılığı verilir.'),
(2049, 17, 20, 'Hepsine; (dünyayı isteyenlere de, ahireti isteyenlere de) Rabbinin ihsanından veririz. Rabbinin ihsanı kısıtlanmış değildir.'),
(2050, 17, 21, 'Bak! Onların bir kısmını diğerine nasıl üstün kıldık! Elbette ahiret, hem dereceler bakımından daha büyüktür, hem de üstünlük bakımından daha büyüktür.'),
(2051, 17, 22, 'Allah ile birlikte başka bir ilâh edinme! Yoksa kınanmış ve yalnız başına bırakılmış olarak oturup kalırsın.'),
(2052, 17, 23, 'Rabbin kesin olarak şunları emretti: Ancak kendisine ibadet edin, anne ve babaya iyilik edin. Onlardan biri veya her ikisi senin yanında yaşlanırsa, sakın onlara "öf" bile deme ve onları azarlama. İkisine de tatlı ve güzel söz söyle.'),
(2053, 17, 24, 'İkisine de acıyarak tevazu kanatlarını indir. Ve şöyle de: "Ey Rabbim! Onların beni küçükten terbiye edip yetiştirdikleri gibi, sen de kendilerine merhamet et."'),
(2054, 17, 25, 'Rabbiniz içinizden geçenleri çok iyi bilir. Eğer iyi kimseler olursanız elbette Allah çok tevbe edenleri bağışlayıcıdır.'),
(2055, 17, 26, 'Akrabaya, yoksula ve yolda kalmışa hakkını ver. Bununla beraber malını saçıp savurma.'),
(2056, 17, 27, 'Çünkü (malını) saçıp savuranlar, şeytanların kardeşleridir. Şeytan ise Rabbine karşı çok nankördür.'),
(2057, 17, 28, 'Eğer Rabbinden beklediğin bir rahmet (rızık) için, onlardan yüz çevirmek mecburiyetinde kalırsan, o vakit de onlara yumuşak ve tatlı bir söz söyle.'),
(2058, 17, 29, 'Elini boynuna asıp bağlama (cimri olma), hem de onu büsbütün açıp saçma (israf etme); aksi halde kınanmış olursun ve eli boş açıkta kalırsın.'),
(2059, 17, 30, 'Gerçekten senin Rabbin, kullarından dilediğinin rızkını genişletir ve dilediğini kısar. Şüphesiz ki Allah, kullarının durumlarından haberdardır, her şeyi görendir.'),
(2060, 17, 31, 'Bir de geçim korkusuyla çocuklarınızı öldürmeyin, onlara da, size de rızkı biz veririz. Şüphesiz ki onları öldürmek, çok büyük bir suçtur.'),
(2061, 17, 32, 'Zinaya da yaklaşmayın, çünkü o pek çirkindir ve kötü bir yoldur.'),
(2062, 17, 33, 'Haklı bir sebep olmadıkça, Allah\'ın öldürülmesini haram kıldığı canı öldürmeyin. Kim haksız yere öldürülürse, biz onun velisine bir yetki verdik. O da öldürmede aşırı gitmesin. Çünkü ona (dinin kendisine verdiği yetki ile) yardım olunmuştur.'),
(2063, 17, 34, 'Yetimin malına da yaklaşmayın. Ancak rüşdüne erinceye kadar en güzel bir şekilde yaklaşabilirsiniz. Ahdi de yerine getirin. Çünkü verilen sözde elbette sorumluluk bulunuyor.'),
(2064, 17, 35, 'Ölçtüğünüz zaman tam ölçün ve doğru terazi ile tartın. Bu hem daha hayırlıdır ve sonuç itibariyle de daha güzeldir.'),
(2065, 17, 36, 'Bir de hiç bilmediğin bir şeyin ardına düşme! Çünkü kulak, göz, gönül, bunların her biri yaptıklarından sorumludurlar.'),
(2066, 17, 37, 'Yeryüzünde kibir ve azametle yürüme! Çünkü sen asla yeri yaramazsın ve boyca da dağlara erişemezsin.'),
(2067, 17, 38, 'Kötü olan bütün bu yasaklar, Rabbinizin sevmediği şeylerdir.'),
(2068, 17, 39, 'İşte bunlar, Rabbinin sana vahyettiği hikmetlerdendir. Sakın Allah\'la beraber başka bir ilâh uydurma. Aksi halde kötülenmiş ve Allah\'ın rahmetinden uzaklaştırılmış olarak cehenneme atılırsın.'),
(2069, 17, 40, 'Rabbiniz, size oğulları tahsis etti de, kendisi meleklerden dişiler mi edindi? Gerçekten siz çok büyük bir söz söylüyorsunuz.'),
(2070, 17, 41, 'Biz, bu Kur\'ân\'da akıllarını başlarına almaları için türlü şekillerde (ikaz ve ihtarı) açıkladık. Fakat bu açıklamalar ancak onların nefretini artırmıştır.'),
(2071, 17, 42, '(Ey Muhammed!) De ki: "Eğer dedikleri gibi Allah ile birlikte ilâhlar olsaydı, o zaman bu ilâhlar Arş\'ın sahibine bir yol ararlardı."'),
(2072, 17, 43, 'Allah, onların dediklerinden çok münezzeh ve çok yüksek, hem pek büyük bir yükseklikle yücedir.'),
(2073, 17, 44, 'Yedi gök, yer ve bunların içinde bulunanlar, Allah\'ı tesbih ederler. O\'nu hamd ile tesbih etmeyen hiçbir varlık yoktur. Fakat siz, onların tesbihlerini iyi anlamazsınız. Şüphesiz O, halimdir çok bağışlayandır.'),
(2074, 17, 45, 'Sen Kur\'ân\'ı okuduğun zaman biz, seninle ahirete inanmayanların arasına görünmez bir perde çekeriz.'),
(2075, 17, 46, 'Ve kalblerinin üzerine, Kur\'ân\'ı anlamalarına engel perdeler geçiririz ve kulaklarına bir ağırlık veririz. Rabbini Kur\'ân\'da bir tek olarak andığın zaman da ürkerek arkalarına döner kaçarlar.'),
(2076, 17, 47, 'Biz onların, seni dinlerken nasıl dinlediklerini çok iyi biliriz. Birbiriyle fısıldaşırlarken de o zalimlerin: "Siz büyülenmiş bir adamdan başkasına uymuyorsunuz!" dediklerini biz çok iyi biliriz.'),
(2077, 17, 48, 'Bak senin için nasıl misaller verdiler de bu yüzden nasıl sapıklığa düştüler! Artık hak yolu bulmaya güçleri yetmez.'),
(2078, 17, 49, 'Bir de onlar dediler ki: "Biz, bir kemik yığını olduğumuz ve ufalanıp toz olduğumuz vakit mi, gerçekten biz mi, yeni bir yaratılışla diriltileceğiz?'),
(2079, 17, 50, 'De ki: "İster taş olun, ister demir..."'),
(2080, 17, 51, '"İsterse gönlünüzde büyüyen başka bir yaratık olun, (Muhakkak öldürülecek ve diriltileceksiniz.) "Onlar: "Bizi kim tekrar diriltecek?" diyecekler. De ki: "Sizi ilk defa yaratmış olan o kudret sahibi." Sana başlarını sallayarak: "Ne zamandır bu." diyecekler. De ki: "Yakın olması gerekir!".'),
(2081, 17, 52, '(Allah) sizi çağıracağı gün, tam bir hürmetle onun emrine koşacaksınız ve zannedeceksiniz ki, kabirlerinizde pek az bir müddet kaldınız.'),
(2082, 17, 53, 'Mümin kullarıma söyle de (kâfirlere) en güzel olan sözü söylesinler. Çünkü şeytan aralarına fesat sokar. Şüphesiz şeytan, insan için apaçık bir düşmandır.'),
(2083, 17, 54, 'Rabbiniz sizi çok daha iyi bilir. Dilerse tevbeniz sebebiyle size merhamet eder, dilerse azab eder. Seni de onların üzerine vekil göndermedik.'),
(2084, 17, 55, 'Rabbin göklerde ve yerde olan kimselerin hepsini en iyi bilendir. Andolsun ki biz, peygamberlerin kimini kimine üstün kıldık. Davud\'a da Zebur\'u verdik.'),
(2085, 17, 56, 'De ki: "Allah\'tan başka, ilâh olduğunu sandığınız şeyleri çağırın, size yardım etsinler. Onlar, ne sizden sıkıntıyı kaldırabilirler, ne de değiştirebilirler.'),
(2086, 17, 57, 'Onların yalvardıkları da, Rablerine daha yakın olmak için vesile ararlar. Ve O\'nun merhametini umarlar, azabından korkarlar. Çünkü Rabbinin azabı korkunçtur.'),
(2087, 17, 58, 'Hiç bir şehir (halkı) yoktur ki, kıyamet gününden önce biz onu helak etmeyelim, yahut şiddetli bir azab ile azablandırmayalım. Bu, Kitap\'ta (Levhi Mahfuzda) yazılıdır.'),
(2088, 17, 59, 'Bizi, âyetler (mucizeler) ve peygamber göndermekten alıkoyan şey, ancak öncekilerin onları yalanlamış olmalarıdır. Semûd\'a, açık bir mucize olarak o dişi deveyi vermiştik de ona zulmetmişlerdi (deveyi boğazlayarak kendilerine yazık etmişlerdi). Oysa biz, o mucizeleri ancak korkutmak için göndeririz.'),
(2089, 17, 60, 'Vaktiyle sana şöyle vahyettiğimizi hatırla: "Şüphesiz Rabbin insanları kuşatmıştır." (İsrâ gecesi) sana açıkça gösterdiğimiz o temâşâyı ve Kur\'ân\'da lanet edilen ağacı da, yalnız insanlara bir imtihan için yapmışızdır. Biz onları, korkutuyoruz, fakat bu onlara ancak büyük bir taşkınlıktan başka bir sonuç vermiyor.'),
(2090, 17, 61, '(Yine unutma ki) Bir vakit meleklere: "Âdem\'e secde edin" demiştik. İblis\'ten başka hepsi secde ettiler. O ise: "Ben bir çamurdan yarattığın kimseye mi secde ederim?" demişti.'),
(2091, 17, 62, '(Yine İblis) dedi ki: "Şu benden üstün kıldığını gördün mü? Yemin ederim ki, eğer beni kıyamet gününe kadar ertelersen, pek azı hariç, onun zürriyetini kendi buyruğum altına alacağım."'),
(2092, 17, 63, 'Allah buyurdu ki: "Haydi git! Onlardan kim sana uyarsa, şüphesiz ki, cezanız cehennemdir, hem de mükemmel bir ceza. "'),
(2093, 17, 64, '"Onlardan gücünün yettiğini yerinden oynat. Atlıların ve yayalarınla onların üzerine yaygarayı bas! Mallarda ve çocuklarda onlara ortak ol! Ve onlara vaadlerde bulun." Fakat şeytan onlara aldatmadan başka bir şey vaad etmez.'),
(2094, 17, 65, 'Doğrusu benim (ihlaslı) kullarım üzerinde senin hiçbir hakimiyetin yoktur. Vekil olarak Rabbin yeter.'),
(2095, 17, 66, 'Rabbiniz, lütfundan nasib arayasınız diye, sizin için denizde gemileri yürüten kudret sahibidir. Şüphesiz O, size çok merhametlidir.'),
(2096, 17, 67, 'Denizde başınıza bir felaket geldiği zaman, Allah\'tan başka yalvardığınız bütün putlar kaybolur. Allah sizi tehlikeden kurtarıp karaya çıkarınca da yüz çevirirsiniz. Zaten insan çok nankördür.'),
(2097, 17, 68, '(Denizden karaya çıktığınızda) O\'nun sizi karada yerin dibine geçirmeyeceğinden, yahut üzerinize taş yağdıran bir kasırga gördermeyeceğinden emin misiniz? Sonra kendinize bir vekil de bulamazsınız.'),
(2098, 17, 69, 'Yoksa sizi tekrar denize döndürüp de üzerinize kasırgalar göndermeyeceğinden ve böylece ettiğiniz nankörlük sebebiyle sizi boğmayacağından emin misiniz? Sonra bu yaptığımıza karşı, bizim aleyhimize size yardım edecek bir koruyucu bulamazsınız.'),
(2099, 17, 70, 'Andolsun ki biz, insanoğlunu şan ve şeref sahibi kıldık. Karada ve denizde taşıtlara yükledik ve temiz yiyeceklerden onları rızıklandırdık. Onları yarattıklarımızın birçoğundan üstün kıldık.'),
(2100, 17, 71, 'Kıyamet günü bütün insanları önderleriyle çağıracağız. O gün, kimin amel defteri sağ eline verilirse, işte onlar kitaplarını okuyacaklar ve en küçük bir haksızlığa uğratılmayacaklar.'),
(2101, 17, 72, 'Her kim bu dünyada (manen) kör ise ahirette de kördür. Ve gidişçe daha şaşkındır.'),
(2102, 17, 73, '(Ey Muhammed!) Az kalsın seni bile, sana vahyettiğimizden başkasını bize karşı iftira edesin diye, fitneye düşüreceklerdi ve o takdirde seni dost edineceklerdi.'),
(2103, 17, 74, 'Eğer biz sana sebat vermemiş olsaydık, nerdeyse sen onlara birazcık meyledecektin.'),
(2104, 17, 75, 'O takdirde, muhakkak hayatın da, ölümün de azabını sana kat kat tattırırdık. Sonra bize karşı kendin için hiçbir yardımcı bulamazdın.'),
(2105, 17, 76, '(Ey Muhammed!) Yakında seni yurdundan çıkarmak için, muhakkak ki rahatsız edecekler ve o takdirde onlar da senin ardından pek az kalacaklardır.'),
(2106, 17, 77, 'Bu, senden önce gönderdiğimiz bütün peygamberlerimiz hakkındaki sünnetimizdir. Bizim sünnetimizde herhangi bir değişme göremezsin.'),
(2107, 17, 78, 'Güneşin batıya kaymasından, gecenin karanlığına kadar (belirli vakitlerde) gereği üzere namazı kıl, bir de sabah namazını kıl. Çünkü sabah namazında, gece ve gündüz melekleri hazır bulunur.'),
(2108, 17, 79, 'Gecenin bir kısmında da sadece sana mahsus bir nafile olmak üzere uykudan kalk, Kur\'ân ile teheccüd namazı kıl, Rabbinin seni bir makamı mahmuda (şefaat makamına) göndermesi kesindir.'),
(2109, 17, 80, '(Ey Muhammed!) De ki: "Rabbim! Beni, takdir ettiğin yere gönül rahatlığı ve huzur içinde koy ve çıkacağım yerden de dürüstlükle ve selametle çıkmamı sağla. Bana katından yardım edici bir kuvvet ver."'),
(2110, 17, 81, '(Ey Muhammed!) De ki: "Hak geldi, batıl yok oldu. Elbette batıl yok olmaya mahkumdur."'),
(2111, 17, 82, 'Biz Kur\'ân\'dan, iman edenler için bir şifa ve rahmet kaynağı olan âyetler indiriyoruz. Zalimlerin de ancak zararını artırır.'),
(2112, 17, 83, 'Biz insana nimet verdiğimiz zaman, Allah\'ı anmaktan yüz çevirip uzaklaşır. Ona fenalık dokununca da ümitsizliğe kapılır.'),
(2113, 17, 84, 'De ki: "Herkes bulunduğu hal ve niyetine göre iş yapar. Bu durumda kimin en doğru yolda olduğunu Rabbiniz daha iyi bilir."'),
(2114, 17, 85, 'Ey Muhammed! Sana ruhtan soruyorlar. De ki: "Ruh Rabbimin bildiği bir iştir ve size ilimden ancak az bir şey verilmiştir."'),
(2115, 17, 86, 'Yemin olsun ki, dilersek sana vahyettiğimizi ortadan kaldırırız; sonra bize karşı kendine bir vekil (koruyucu) bulamazsın.'),
(2116, 17, 87, 'Fakat Rabbinden bir rahmet olarak (biz bunu yapmadık). Gerçekten O\'nun senin üzerindeki lütfu çok büyüktür.'),
(2117, 17, 88, 'Ey Muhammed! De ki: "Yemin olsun, eğer insanlar ve cinler bu Kur\'ân\'ın benzerini getirmek üzere toplansalar ve birbirlerine yardımcı olsalar bile, yine onun bir benzerini meydana getiremeyeceklerdir."'),
(2118, 17, 89, 'Yemin olsun ki biz bu Kur\'ân\'da insanlar için çeşitli misaller vermişizdir. Yine de insanların çoğu inkârlarında ısrar ederler.'),
(2119, 17, 90, 'Kâfirler şöyle dediler: "Sen, bizim için yerden suyu kesilmeyen bir kaynak fışkırtmadıkça sana asla inanmayacağız."'),
(2120, 17, 91, '"Veyahut hurmalıklardan ve üzümlüklerden senin bir bahçen olsun da ortasından şarıl şarıl ırmaklar akıtmalısın."'),
(2121, 17, 92, '"Yahut söyleyip zannettiğin gibi, göğü başımıza parça parça düşüresin veya Allah\'ı ve melekleri söylediğine şahit getiresin. "'),
(2122, 17, 93, '"Yahut altından bir evin olsun, ya da göğe çıkmalısın. Ona çıktığına da asla inanmayız. Ta ki bize, okuyacağımız bir kitap indiresin." De ki: "Rabbimi tenzih ederim. Nihayet ben de, peygamber olan bir insandan başka bir şey değilim."'),
(2123, 17, 94, 'Kendilerine doğru yolu gösteren peygamber gelince, insanların iman etmelerine engel olan sebep sadece: "Allah bir insanı mı Peygamber gönderdi?" demeleridir.'),
(2124, 17, 95, '(Ey Muhammed! Mekkelilere) şöyle de: "Eğer yeryüzünde huzur içinde yürüyüp duran melekler olsaydı, elbette onlara gökten peygamber olarak bir melek indirirdik."'),
(2125, 17, 96, 'De ki: "Benimle sizin aranızda şahit olarak Allah yeter. Çünkü O, kullarının yaptığından haberdardır, yaptıklarını çok iyi görendir."'),
(2126, 17, 97, 'Allah kime hidayet verirse, o doğru yoldadır. Kimi de hidayetten uzak tutarsa, artık bunlar için Allah\'tan başka hiçbir yardımcı bulamazsın. Ve biz, o kâfirleri kıyamet günü kör, dilsiz ve sağır oldukları halde, yüzleri üstü sürünerek haşredeceğiz. Varacakları yer cehennemdir; ateşi dindikçe onun ateşini artırırız.'),
(2127, 17, 98, 'Bu onların cezasıdır! Çünkü onlar, âyetlerimizi inkâr etmişler ve: "Sahi bizler, bir yığın kemik ve ufalanmış toz olduğumuz zaman mı, yeni bir yaratılışla diriltilmiş olacağız?" demişlerdir.'),
(2128, 17, 99, 'Onlar, gökleri ve yeri yaratan Allah\'ın, kendilerinin aynı olan insanları yaratmaya da kadir olduğunu görüp bilmediler mi? Allah onlar için şüphe edilmeyen bir vâde takdir etmiştir. Fakat zalimler, inkârlarında yine de ısrar ederler.'),
(2129, 17, 100, '(Ey Muhammed!) De ki: "Eğer siz Rabbimin rahmet hazinelerine sahip olsaydınız, fakirlik korkusunu yine de elden bırakmazdınız." Doğrusu insan çok cimridir.'),
(2130, 17, 101, 'Andolsun biz Musa\'ya apaçık dokuz mucize verdik. (Ey Peygamber!) İsrailoğullarına sor, Musa kendilerine geldiğinde Firavun ona: "Ey Musa! Ben senin büyülenmiş olduğunu sanıyorum" demişti.'),
(2131, 17, 102, 'Musa dedi ki: "Ey Firavun! Pekâlâ bilirsin ki, bu mucizeleri, birer ibret olmak üzere, ancak göklerin ve yerin Rabbi indirdi. Ey Firavun! Ben de seni helak olmuş zannediyorum."'),
(2132, 17, 103, 'Derken Firavun, Musa\'yı ve İsrailoğullarını Mısır\'dan sürmek istedi. Biz de onu ve beraberindekilerin hepsini suda boğduk.'),
(2133, 17, 104, 'Arkasından İsrailoğullarına şöyle dedik: "Firavun"un sizi çıkarmak istediği arazide siz oturun! Sonra ahiret vaadi (kıyamet) geldiği vakit, hepinizi toplayıp bir araya getireceğiz."'),
(2134, 17, 105, 'Biz bu Kur\'an\'ı hak olarak indirdik, O, bütün hakikatleri içinde toplayarak indi. Ey Peygamber! Biz seni ancak müjdeci ve uyarıcı olarak gönderdik.'),
(2135, 17, 106, 'Sana Kur\'ân\'ı verdik ve onu insanlara sindire sindire okuyasın diye (kısımlara) ayırdık ve biz onu yavaş yavaş indirdik.'),
(2136, 17, 107, 'Ey Muhammed! De ki: İster ona (Kur\'ân\'a) inanın, ister inanmayın; o daha önce kendilerine ilim verilenlere okunduğunda onlar, yüzleri üstü secdeye kapanırlar.'),
(2137, 17, 108, 'Ve derler ki: Rabbimizi tenzih ederiz. Şüphesiz ki Rabbimizin vaadi gerçekleşir.'),
(2138, 17, 109, 'Ve ağlayarak yüzleri üstü secdeye kapanırlar. Hem de bu Kur\'ân\'ı işitmek onların Allah\'a teslimiyetlerini daha da artırır.'),
(2139, 17, 110, '(Sen onlara) de ki: İster "Allah" deyin, ister "Rahmân" deyin, nasıl çağırırsanız çağırın. En güzel isimler O\'nundur. Namazında sesini pek yükseltme, çok da gizli okuma, orta yolu seç.'),
(2140, 17, 111, 'Ve şöyle de: Hamd o Allah\'a ki, hiçbir çocuk edinmedi, mülkte ortağı yoktur, aciz olmayıp bir yardımcıya da ihtiyacı yoktur. Tekbir getirerek O\'nu noksanlıklardan yücelt de yücelt.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'Hamd, o Allah\'a mahsustur ki kulu (Muhammed\'e) kitabı indirdi ve ona hiçbir eğrilik koymadı.'),
(2142, 18, 2, 'Onu dosdoğru (bir kitap) olarak (indirdi) ki katından gelecek şiddetli azaba karşı (insanları) uyarsın ve yararlı işler yapan müminlere kendileri için güzel bir mükafat bulunduğunu müjdelesin.'),
(2143, 18, 3, 'Onlar orada sürekli kalacaklardır.'),
(2144, 18, 4, 'Ve "Allah çocuk edindi" diyenleri de uyarsın.'),
(2145, 18, 5, 'Bu hususta ne kendilerinin, ne de atalarının hiçbir bilgisi yoktur. Ağızlarından çıkan söz ne büyük bir iftiradır. Onlar, yalandan başka bir şey söylemiyorlar.'),
(2146, 18, 6, '(Ey Muhammed!) Demek onlar, bu söze (kitaba) inanmazlarsa, onların peşinde üzüle üzüle kendini helak edeceksin!'),
(2147, 18, 7, 'Biz yeryüzündeki şeyleri kendisine süs olsun diye yarattık ki, insanların hangisinin daha güzel amel edeceğini deneyelim.'),
(2148, 18, 8, 'Şüphesiz biz, yeryüzünde olanları kupkuru bir toprak yapacağız.'),
(2149, 18, 9, 'Yoksa sen Ashabı Kehf\'i ve Rakim\'i (isimlerinin yazılı bulunduğu taş kitabeyi) şaşılacak âyetlerimizden mi sandın?'),
(2150, 18, 10, 'O gençler mağaraya sığınınca şöyle dediler: "Rabbimiz! Bize katından bir rahmet ver ve bizim için şu işimizden bir kurtuluş yolu hazırla."'),
(2151, 18, 11, 'Bunun üzerine biz de kulaklarını tıkayarak mağarada onları yıllarca uyuttuk.'),
(2152, 18, 12, 'Sonra da iki gruptan hangisinin, onların mağarada kaldıkları süreyi daha iyi hesapladığını anlamak için, onları tekrar uyandırdık.'),
(2153, 18, 13, 'Biz sana onların kıssalarını gerçek olarak anlatacağız. Hakikaten onlar, Rablerine iman eden birkaç genç idi. Biz de onların hidayetlerini artırdık.'),
(2154, 18, 14, '(Oranın hükümdarı karşısında) ayağa kalkarak dediler ki: "Bizim Rabbimiz, göklerin ve yerin Rabbidir. Biz, O\'ndan başkasına ilâh deyip tapmayız, yoksa saçma sapan konuşmuş oluruz.'),
(2155, 18, 15, 'Şu bizim kavmimiz, Allah\'tan başka ilâh edindiler. Onların ilâh olduğuna dair açık bir delil getirselerdi ya! Allah\'a karşı yalan uydurandan daha zalim kim olabilir?'),
(2156, 18, 16, '(İçlerinden biri şöyle demişti:) "Mademki siz, onlardan ve Allah\'tan başka taptıkları putlardan ayrıldınız, o halde mağaraya sığının ki, Rabbiniz rahmetinden size genişlik versin ve işinizi rast getirip kolaylaştırsın."'),
(2157, 18, 17, 'Ey Muhammed! Baksaydın güneşin doğduğu zaman mağaranın sağ tarafına yöneldiğini, batarken de sol taraftan onları makaslayıp geçtiğini görürdün. Onlar, mağaranın geniş bir yerinde idiler. İşte bu Allah\'ın mucizelerindendir. Allah kime hidayet ederse, işte o, hakka ulaşmıştır; kimi de hidayetten mahrum ederse, artık ona doğru yolu gösterecek bir dost bulamazsın.'),
(2158, 18, 18, 'Bir de onları mağarada görseydin uyanık sanırdın. Halbuki onlar uykudadırlar. Biz onları sağa sola çevirirdik. Köpekleri de girişte ön ayaklarını ileri doğru uzatmıştı. Eğer onları görseydin, arkana bakmadan kaçardın ve için korku ile dolardı.'),
(2159, 18, 19, 'Onları bir mucize olarak uyuttuğumuz gibi, birbirlerine sorsunlar diye kendilerini uyandırdık da içlerinden bir sözcü şöyle dedi: "Ne kadar durup kaldınız?" (Kimi) "Bir gün ya da günün bir parçası kadar kaldık" dediler. (Kimi de) şöyle dediler: "Ne kadar durduğunuzu, Rabbiniz daha iyi bilir. Şimdi siz birinizi, bu gümüş paranızla şehre gönderin de baksın, hangi yiyecek daha temiz ise, ondan size azık getirsin. Hem çok dikkatli davransın ve sizi kimseye sezdirmesin."'),
(2160, 18, 20, '"Çünkü şehir halkı, sizi ellerine geçirirlerse muhakkak sizi taşlayarak öldürürler veya kendi dinlerine çevirirler ki, o zaman siz dünyada da ahirette de asla kurtuluşa eremezsiniz."'),
(2161, 18, 21, 'Böylece insanları onlardan haberdar kıldık ki, öldükten sonra dirilmenin hak olduğunu ve kıyamet gününden şüphe edilemeyeceğini bildirmek için, öylece şehir halkına buldurduk. Onları mağarada bulanlar, aralarında durumlarını tartışıyorlardı. Dedilerki: "Üstlerine bir bina (kilise) yapın. Bununla beraber Rableri, onları daha iyi bilir." Sözlerinde üstün gelen müminler: "Üzerlerine muhakkak bir mescid yapacağız." dediler.'),
(2162, 18, 22, 'Ashabı Kehf\'in sayılarında ihtilaf edenlerden bazıları: Onlar, üç kişidir, dördüncüleri köpekleridir" diyecekler. Diğer bazıları da "Onlar, beş kişidir, altıncıları köpekleridir " diyecekler. Her ikisi de bilinmeyen hakkında tahmin yürütmektir. (kimileri de:) "Onlar, yedi kişidir; sekizincisi köpekleridir" derler. De ki: "Onların sayılarını Rabbim daha iyi bilir." Onları ancak pek azı bilir, Bu sebeple onlar hakkında bu bildirilenler dışında bir münakaşaya girişme ve bunlar hakkında hiç kimseye de bir şey sorma!'),
(2163, 18, 23, 'Hiçbir şey için, Allah\'ın dilemesi dışında: "Ben yarın onu yapacağım deme"'),
(2164, 18, 24, 'Ancak Allah dilerse (yapacağım de). Ve unuttuğun vakit Allah\'ı an ve "Umarım Rabbim beni, doğruya daha yakın olana eriştirir." de.'),
(2165, 18, 25, 'Onlar, mağaralarında üçyüz yıl kadar kaldılar ve dokuz yıl da buna ilave etmişlerdir.'),
(2166, 18, 26, 'De ki: "Onların ne kadar kaldıklarını Allah daha iyi bilir." Göklerin ve yerin gaybı O\'na aittir. O ne güzel görendir! O ne mükemmel işitendir! Onların, O\'ndan başka bir yardımcısı yoktur. O, kendi hükümranlığına kimseyi ortak etmez.'),
(2167, 18, 27, 'Rabbinin kitabından sana vahyolunanı oku! Onun sözlerini değiştirecek kimse yoktur. Ve O\'ndan başka bir sığınılacak da bulamazsın.'),
(2168, 18, 28, 'Nefsince de, sabah akşam rızasını isteyerek Rablerine yalvaranlarla beraber candan sabret. Sen dünya hayatının süsünü isteyerek onlardan gözlerini ayırma. Kalbini, bizi anmaktan gafil kıldığımız, nefsinin kötü arzusuna uymuş ve işi hep aşırılık olan kimseye uyma.'),
(2169, 18, 29, 'Ve de ki: O hak Rabbimizdendir. Artık dileyen iman etsin, dileyen inkâr etsin. Çünkü biz zalimler için öyle bir ateş hazırlamışız ki, duvarları, çepeçevre onları içine alacaktır. Eğer feryad edip yardım isteseler, erimiş maden gibi yüzleri haşlayan bir su ile cevap verilir. O ne kötü bir içecek ve ne kötü bir dayanma yeri!'),
(2170, 18, 30, 'İman edip de güzel davranışlarda bulunanlar var ya, şüphe yok ki biz öyle güzel işler yapanların mükafatını zayi etmeyiz.'),
(2171, 18, 31, 'İşte onlara Adn cennetleri vardır; altlarından ırmaklar akar, orada altın bileziklerle süslenecekler, ince ve kalın ipekliden yeşil elbiseler giyerek koltuklar üzerine dayanıp kurulacaklar. O ne güzel karşılık ve ne güzel kalma yeri!'),
(2172, 18, 32, 'Onlara, şu iki adamı misal olarak anlat: Biz bunlardan birine her türlü üzümden iki bağ vermişiz, her ikisinin etrafını hurmalarla donatmışız, aralarında da bir ekinlik yapmışız.'),
(2173, 18, 33, 'İki bağın ikisi de yemişlerini vermiş, hiçbir şey noksan bırakmamış, ikisinin ortasından bir de nehir akıtmışız.'),
(2174, 18, 34, 'İki bağın sahibinin ayrıca başka geliri vardı. Bundan dolayı bu adam arkadaşıyla münakaşa ederken: "Ben malca senden daha zengin ve insan sayısı bakımından da senden daha güçlü ve üstünüm" dedi.'),
(2175, 18, 35, 'Adam, bu şekilde kendine zulmederek bağına girdi ve şöyle dedi: "Bunun hiç yok olacağını sanmıyorum"'),
(2176, 18, 36, '"Kıyametin kopacağını da zannetmem. Şayet Rabbimin huzuruna götürürlürsem, muhakkak orada bundan daha hayırlı bir sonuç bulurum".'),
(2177, 18, 37, 'Bunun üzerine kendisiyle münakaşa eden arkadaşı da ona şöyle dedi: "Seni topraktan, sonra seni bir damla sudan yaratan, daha sonra da seni insan haline getireni mi inkar ediyorsun?'),
(2178, 18, 38, '"Fakat ben iman ederek diyorum ki: O Allah, benim Rabbimdir, ben Rabbime kimseyi ortak koşmam."'),
(2179, 18, 39, '"Kendi bağına girdiğin zaman: "Bu Allah\'dandır, benim kuvvetimle değil, Allah\'ın kuvveti ile olmuştur, deseydin ya! Her ne kadar beni, malca ve evlatça kendinden az görüyorsan da."'),
(2180, 18, 40, 'Belki Rabbim, bana, senin bağından daha hayırlısını verir; senin bağına ise gökten yıldırımlar gönderir de, bağın yalçın bir toprak haline gelir."'),
(2181, 18, 41, '"Yahut, bağının suyu yerin dibine çekilir de bir daha suyunu çıkarıp bağını sulayamazsın."'),
(2182, 18, 42, 'Derken serveti yok edildi. Bunun üzerine bağına yaptığı masraflara karşı ellerini oğuşturmaya başladı. Bağ, çardakları üzerine yıkılmış kalmıştı, "Ah Keşke Rabbime hiçbir şeyi ortak koşmasaydım" diyordu.'),
(2183, 18, 43, 'Onun Allah\'tan başka yardım edecek adamları yoktur ve Allah\'a karşı kendi nefsini de kurtaramadı.'),
(2184, 18, 44, 'İşte burada yardım, yalnız hak olan Allah\'a aittir. O\'nun verdiği mükâfat da daha hayırlıdır, netice de daha hayırlıdır.'),
(2185, 18, 45, 'Ey Muhammed! Sen onlara dünya hayatının misalini ver. Dünya hayatı, gökten indirdiğimiz bir su gibidir ki, bu su sayesinde yeryüzünün bitkileri (her renk ve çiçekten) birbirine karışmış, nihayet bir çöp kırıntısı olmuştur. Rüzgarlar onu savurur gider. Allah her şeye muktedirdir.'),
(2186, 18, 46, 'Mal ve oğullar, dünya hayatının süsüdür. Bakî kalacak olan iyi ameller ise, Rabbinin katında, sevabca da hayırlıdır, ümid yönünden de daha hayırlıdır.'),
(2187, 18, 47, 'O kıyamet gününü hatırla ki, dağları yürüteceğiz ve yeryüzünü çırılçıplak göreceksin. Bütün insanları, mahşerde toplayacağız hiçbir kimseyi bırakmayacağız.'),
(2188, 18, 48, 'Onlar, saf halinde Rabbine arz edilmişlerdir. Allah, onlara şöyle diyecektir: "Şüphesiz sizi ilk önce yarattığımız gibi bize geldiniz. Fakat, size kıyamet için yaptığımız vaadi yerine getirmeyeceğimizi sanmıştınız, değil mi?'),
(2189, 18, 49, 'O gün herkesin amel defteri ortaya konulmuştur. Ey Muhammed! Günahkârların, amel defterlerinden korkarak: "Eyvah bize! Bu nasıl deftermiş ki, büyük küçük hiçbir şey bırakmadan hepsini saymış dökmüş" dediklerini görürsün. Onlar, bütün yaptıklarını hazır bulmuşlardır. Senin Rabbin hiç kimseye zulmetmez.'),
(2190, 18, 50, 'Yine o vakti hatırla ki biz, meleklere: "Âdem\'e secde edin!" demiştik. İblis hariç olmak üzere onlar hemen secde ettiler. İblis cinlerdendi, Rabbinin emrinden dışarı çıktı. Şimdi siz beni bırakıp da İblis\'i ve soyunu dostlar mı ediniyorsunuz? Halbuki onlar sizin düşmanınızdır. Zalimler için bu ne kötü bir değişmedir.'),
(2191, 18, 51, 'Ben, onları (İblis ve soyunu) ne göklerin ve yerin yaratılışında, ne de kendilerinin yaratılışında şahit tutmadım ve hiçbir zaman doğru yoldan çıkanları yardımcı edinmiş değilim.'),
(2192, 18, 52, 'Ve o (kıyamet) günü Allah kâfirlere şöyle buyuracak: "Ortaklarım ve şefaatçılarınız diye zannettiğiniz putlarınızı çağırın." Müşrikler onları çağırırlar, fakat kendilerine cevap vermezler. Biz, kâfirlerle ilâhları arasına ateşten bir engel koymuşuzdur.'),
(2193, 18, 53, 'Günahkârlar ateşi görmüşler de artık ona düşeceklerini anlamışlardır. Fakat ondan kaçıp sığınacak bir yer bulamazlar.'),
(2194, 18, 54, 'Şüphesiz biz, bu Kur\'ân\'da insanlara çeşitli mânâları türlü misallerle açık olarak verdik. İnsan ise, her şeyden çok mücadelecidir.'),
(2195, 18, 55, 'Kendilerine doğru yolu gösteren peygamber geldiğinde insanları, iman etmekten ve Rabblerinden günahlarının mağfiretini istemekten alıkoyan şey sadece geçmiş milletlerin başlarına gelen felaketlerin kendilerine de gelmesini veya ahiret azabının ansızın göz göre göre gelip çatmasını beklemek olmuştur.'),
(2196, 18, 56, 'Halbuki biz peygamberleri ancak müjdeciler ve uyarıcılar olarak göndeririz. Kâfir olanlar ise hakkı, batılla ortadan kaldırmak için mücadele ediyorlar. Onlar, âyetlerimizi ve korkutuldukları azabı da alaya almışlardır.'),
(2197, 18, 57, 'Rabbinin âyetleriyle nasihat edilip de onlardan yüz çeviren ve daha önce işlediği günahları unutandan daha zalim kim olabilir? Biz onların kalbleri üzerine (Kur\'ân\'ı) anlamalarına engel olan bir ağırlık, kulaklarına da sağırlık verdik. Ey Muhammed! Sen onları doğru yola çağırsan da onlar asla hidayete ermezler.'),
(2198, 18, 58, 'Bununla beraber rahmet sahibi olan Rabbin çok bağışlayıcıdır, tevbe eden kullarına rahmeti boldur. Eğer Allah, işledikleri günahlar yüzünden onları hemen cezalandıracak olsaydı, onlara hemen azab ederdi. Fakat onlara vaad edilen bir zaman vardır ki, o geldiğinde Allah\'ın azabından bir kurtuluş yeri bulamazlar.'),
(2199, 18, 59, 'İşte zulmettikleri için helak ettiğimiz şehirler! Biz onların helâkleri için de belirli bir zaman tayin etmiştik.'),
(2200, 18, 60, 'Ey Muhammed! Bir vakit Musa genç adamına demişti ki: "İki denizin birleştiği yere ulaşıncaya kadar gideceğim, yahut senelerce gideceğim."'),
(2201, 18, 61, 'Bunun üzerine ikisi de iki denizin birleştiği yere vardıklarında balıklarını unuttular. Bu arada balık, denizde yolunu bulup kaybolmuştu.'),
(2202, 18, 62, 'İki denizin birleştiği yeri geçtikleri zaman, Musa genç arkadaşına: "Kuşluk yemeğimizi getir. Gerçekten biz bu yolculuğumuzda epey yorulduk" dedi.'),
(2203, 18, 63, 'Adam: "Gördün mü! dedi. Kayaya sığındığımız vakit doğrusu ben balığı unutmuşum. Onu hatırlamamı, muhakkak şeytan bana unutturdu. O denizde garip bir yol tutup gitmişti."'),
(2204, 18, 64, 'Musa da demişti ki: "İşte aradığımız o idi." Bunun üzerine izlerine dönüp gerisin geri gittiler.'),
(2205, 18, 65, 'Nihayet kullarımızdan bir kul buldular ki, biz ona katımızdan bir rahmet vermiş ve tarafımızdan bir ilim öğretmiştik.'),
(2206, 18, 66, 'Musa ona: "Allah\'ın sana öğrettiği ilim ve hikmetten bana da öğretmen için sana tabi olabilir miyim?" dedi.'),
(2207, 18, 67, '(Hızır) dedi ki: "Doğrusu sen benimle asla sabredemezsin.'),
(2208, 18, 68, '"İçyüzünü kavrayamadığın şeye nasıl sabredeceksin?"'),
(2209, 18, 69, 'Musa: "İnşaallah beni sabırlı bulacaksın ve senin hiçbir işine karşı gelmeyeceğim" dedi.'),
(2210, 18, 70, '(Hızır) dedi ki: "O halde bana tabi olacaksın; ben sana sırrını anlatmadıkça, hiçbir şey hakkında bana soru sorma!"'),
(2211, 18, 71, 'Bunun üzerine ikisi beraber yürüdüler. Nihayet gemiye bindikleri zaman, o kul (Hızır) gemiyi deldi. Musa, ona şöyle dedi: "Geminin içindekileri boğmak için mi deldin? Doğrusu çok kötü bir iş yaptın."'),
(2212, 18, 72, '(Hızır:) "Sen benimle asla sabredemezsin, demedim mi?" dedi.'),
(2213, 18, 73, 'Musa dedi ki: "Unuttuğum şeyden dolayı beni suçlama ve bu işimden dolayı bana bir güçlük çıkarma."'),
(2214, 18, 74, 'Yine gittiler. Nihayet bir erkek çocuğa rastladıklarında Hızır hemen onu öldürdü. Musa: "Kısas olmadan masum bir cana nasıl kıyarsın? Doğrusu sen çok fena bir şey yaptın" dedi.'),
(2215, 18, 75, 'Hızır dedi ki: "Doğrusu sen benimle asla sabredemezsin demedim mi sana?"'),
(2216, 18, 76, '(Musa) dedi ki: "Eğer bundan sonra sana bir şey sorarsam bana arkadaş olma! Hakikaten benim tarafımdan ileri sürülebilecek son mazerete ulaştın.'),
(2217, 18, 77, 'Bunun üzerine yine yürüdüler. Nihayet bir köy halkına varıp onlardan yemek istediler. Ancak köy halkı onları misafir etmekten kaçındılar. Derken orada yıkılmak üzere olan bir duvar buldular. Hızır hemen onu doğrulttu. Musa: "İsteseydin elbet buna karşı bir ücret alırdın" dedi.'),
(2218, 18, 78, 'Hızır dedi ki: "İşte bu, seninle benim aramızın ayrılmasıdır. Şimdi sana o sabredemediğin şeylerin içyüzünü haber vereceğim."'),
(2219, 18, 79, '"Gemi, denizde çalışan bir kaç yoksula aitti. Onu kusurlu kılmak istedim, çünkü onların ilerisinde her sağlam gemiye zorla el koyan bir hükümdar vardı."'),
(2220, 18, 80, '"Oğlana gelince, onun anababası mümin kimselerdi. Çocuğun onları azgınlık ve inkâra sürüklemesinden korktuk."'),
(2221, 18, 81, '"İstedik ki Rabbleri onun yerine kendilerine ondan temizlikçe daha hayırlı ve daha çok merhamet eden birini versin."'),
(2222, 18, 82, '"Duvar ise, o şehirde iki yetim oğlana ait idi. Duvarın altında onların bir hazinesi vardı. Babaları da iyi bir kimse idi. Onun için Rabbin istedi ki o iki çocuk erginlik çağlarına ersinler ve Rabbinden bir rahmet olarak hazinelerini çıkarsınlar. Ve ben bunların hiçbirini kendiliğimden yapmadım. İşte senin sabredemediğin şeylerin içyüzleri budur."'),
(2223, 18, 83, 'Bir de sana Zülkarneyn\'den soruyorlar. De ki: Size ondan bir hatıra okuyacağım.'),
(2224, 18, 84, 'Gerçekten biz onu (Zülkarneyn\'i) yeryüzünde iktidar sahibi yaptık ve ona ulaşmak istediği her şeyi elde etmesinin bir yolunu verdik.'),
(2225, 18, 85, 'Derken o da bu yollardan birini tutup gitti.'),
(2226, 18, 86, 'Nihayet güneşin battığı yere vardığı zaman, güneşi, (sanki) kara bir balçıkta batıyor buldu. Bir de bunun yanında bir kavim buldu. Biz ona dedik ki: "Ey Zülkarneyn! Onları ya cezalandırırsın veya onların hakkında iyi davranırsın."'),
(2227, 18, 87, 'O da demişti ki: "Kim haksızlık ederse muhakkak ona azab edeceğiz; Sonra Rabbine geri döndürülecek, O da onu görülmemiş bir azabla cezalandırır."'),
(2228, 18, 88, '"Amma her kim de iman edip iyi bir iş yaparsa, buna da en güzel mükâfat vardır. Biz ona dünyada kolaylık gösterir zor işlere koşmayız."'),
(2229, 18, 89, 'Sonra Zülkarneyn yine bir yol tuttu.'),
(2230, 18, 90, 'Nihayet güneşin doğduğu yere vardığında, güneşin kendilerini ondan koruyacak bir siper yapmadığımız bir kavim üzerine doğmakta olduğunu gördü.'),
(2231, 18, 91, 'İşte Zülkarneyn\'in kudret ve saltanatı böyleydi. Ve biz onun yanında olan her şeyi bilgimizle kuşatmıştık.'),
(2232, 18, 92, 'Sonra yine bir yol tuttu.'),
(2233, 18, 93, 'Nihayet iki dağ arasına ulaştığında onların önünde, hemen hiç söz anlamayan bir kavim bulmuştu.'),
(2234, 18, 94, 'Dediler ki: "Ey Zülkarneyn! Ye\'cuc ve Me\'cuc bu yerde fesat çıkarıyorlar. Onun için, bizimle onlar arasında bir sed yapman şartıyla sana bir vergi versek olur mu?"'),
(2235, 18, 95, 'Dedi ki: "Rabbimin bana vermiş olduğu servet ve saltanat, sizin vereceğiniz şeyden daha hayırlıdır. Bana maddî yardımda bulunun da sizinle onların arasına en sağlam seddi yapayım.'),
(2236, 18, 96, '"Bana, demir kütleleri getirin." Nihayet dağın iki ucunu denkleştirdiği vakit: "Ateş yakıp körükleyin" dedi. Demiri bir ateş koru haline getirince. "Bana erimiş bakır getirin üzerine dökeyim" dedi.'),
(2237, 18, 97, 'Artık Ye\'cuc ve Me\'cuc bu seti ne aşabildiler ne de delebildiler.'),
(2238, 18, 98, 'Zülkarneyn dedi ki: "Bu Rabbimin bir lütfudur. Rabbimin vaadi geldiği vakit de onu dümdüz yapacaktır. Rabbimin vaadi de haktır.'),
(2239, 18, 99, 'Biz o gün (kıyamet günü) onları bırakıvermişizdir. Dalgalar halinde birbirlerine girerler, Sûr\'a da üfürülmüştür. Böylece onların hepsini bir araya toplamışızdır.'),
(2240, 18, 100, 'Ve cehennemi o gün kâfirlere öyle bir göstereceğiz ki!'),
(2241, 18, 101, 'Onlar ki, beni hatırlatan âyetlerimden gözleri bir örtü içindeydi. İşitmeye de tahammül edemiyorlardı.'),
(2242, 18, 102, 'O kâfirler, beni bırakıp da kullarımı dostlar edineceklerini mi sandılar? Doğrusu biz cehennemi o kâfirlere bir konukluk olarak hazırladık.'),
(2243, 18, 103, 'De ki: Amelleri en çok boşa gidenleri size bildirelim mi?'),
(2244, 18, 104, 'Onların dünya hayatında çalışmaları boşa gitmiştir. Oysa onlar güzel işler yaptıklarını sanıyorlardı.'),
(2245, 18, 105, 'İşte onlar, Rabblerinin âyetlerini ve O\'nun huzuruna çıkacaklarını inkâr etmişlerdir de bu yüzden iyilik altında yaptıkları bütün amelleri boşa gitmiştir. Artık kıyamet günü onlar için hiçbir ölçü tutturmayız.'),
(2246, 18, 106, 'İşte böyle, onların cezaları cehennemdir. Çünkü inkâr etmişler ve benim âyetlerimi, peygamberlerimi alaya almışlardır.'),
(2247, 18, 107, 'İman edip salih ameller işleyenlere gelince, onlar için Firdevs cennetleri konak olmuştur.'),
(2248, 18, 108, 'İçlerinde ebedî olarak kalacaklar, oradan hiç ayrılmak istemeyeceklerdir. Bu hatırlatma ve uyarmayı yeterli görmeyip de daha fazla açıklama isteyenlere karşı ey Muhammed!'),
(2249, 18, 109, 'Deki: "Eğer Rabbimin sözlerini yazmak için deniz mürekkep olsa, Rabbimin sözleri tükenmeden önce, deniz muhakkak tükenecekti, bir mislini daha yardımcı getirsek bile."'),
(2250, 18, 110, 'De ki: "Ben de sizin gibi ancak bir beşerim. Ne var ki, bana ilâhınızın ancak bir ilâh olduğu vahyolunuyor. Onun için her kim Rabbine kavuşmayı arzu ederse iyi amel işlesin ve Rabbine yaptığı ibadete hiç kimseyi ortak etmesin."');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'Kâf, Hâ, Yâ, Ayn, Sâd.'),
(2252, 19, 2, 'Bu, Rabbinin, kulu Zekeriyya\'ya olan rahmetini anmadır.'),
(2253, 19, 3, 'Bir zamanlar o, Rabbine gizlice (içinden) yalvarmıştı.'),
(2254, 19, 4, 'Şöyle demişti: "Ey Rabbim! Şüphesiz (artık öyle bir durumdayım ki) benim kemiğim zayıflayıp gevşedi ve başım(ın saçı) bembeyaz alev gibi tutuştu. Sana dua etmekle de ey Rabbim, hiçbir zaman bedbaht olmadım."'),
(2255, 19, 5, '"Gerçekten ben, arkamdan yerime geçecek varislerden endişedeyim. Karım da kısır bulunuyor. Onun için katından bana bir çocuk ihsan et."'),
(2256, 19, 6, '"Ki bana da mirasçı olsun, Yakub ailesine de mirascı olsun. Rabbim, onu sen rızana kavuştur."'),
(2257, 19, 7, '(Allah şöyle buyurdu): "Ey Zekeriyya! Şüphesiz biz sana Yahya isminde bir oğlanı müjdeliyoruz. Bundan önce ona hiçbir adaş yapmadık."'),
(2258, 19, 8, 'Zekeriyya: "Rabbim! Karım kısır, ben de son derece kocamışken nasıl oğlum olabilir?" dedi.'),
(2259, 19, 9, '(Allah yahut Cebrail ona şöyle) dedi: "Dediğin gibidir, (fakat) Rabbin buyurdu ki, bu işi yapmak bana kolaydır. Nitekim bundan önce seni yarattım. Halbuki sen hiçbir şey değildin."'),
(2260, 19, 10, 'Zekeriyya şöyle dedi: "Rabbim! Bana alâmet ver." Allah: "Senin alâmetin, sapasağlam olduğun halde, üç gün, üç gece insanlarla konuşamaz hale gelmendir." buyurdu.'),
(2261, 19, 11, 'Nihayet (birgün konuşamayınca) mihrabdan kavmine karşı çıktı da onlara "Sabah ve akşam (Rabbinizi) tesbih edin" diye işaret etti.'),
(2262, 19, 12, '"Ey Yahya! Kitaba kuvvetle sarıl" (dedik) ve daha çocukken ona hikmet verdik.'),
(2263, 19, 13, 'Hem de katımızdan bir merhamet ve (günahlardan) paklık verdik, o çok takva sahibi idi.'),
(2264, 19, 14, 'Anne ve babasına karşı iyi davranan bir kimse idi, zorba ve isyankâr değildi.'),
(2265, 19, 15, 'Doğduğu gün, öleceği gün ve dirileceği gün ona selam olsun.'),
(2266, 19, 16, '(Ey Muhammed!) Kur\'ân\'daki Meryem kıssasını da an (insanlara anlat). Hani o, ailesinden ayrılarak (evinin veya mescidin) doğu tarafında bir yere çekilmişti.'),
(2267, 19, 17, 'Sonra ailesiyle kendisi arasına bir perde koymuştu. Biz ona meleğimiz (Cebrail)i gönderdik de ona tam bir insan şeklinde göründü.'),
(2268, 19, 18, 'Meryem: "Ben senden Rahmân (olan Allah) a sığınırım. Eğer Allah\'dan korkuyorsan (dokunma bana)" dedi.'),
(2269, 19, 19, 'Melek: "Ben, sana temiz bir oğlan bağışlamak için, Rabbinin gönderdiği bir elçiyim" dedi.'),
(2270, 19, 20, 'Meryem: "Benim nasıl çocuğum olabilir? Bana hiçbir insan dokunmamıştır. Ben iffetsiz de değilim" dedi.'),
(2271, 19, 21, 'Melek: "Bu, dediğin gibidir. Ancak Rabbin buyurdu ki: Bu (babasız çocuk vermek), bana pek kolaydır. Hem biz onu nezdimizden insanlara bir mucize ve rahmet kılacağız. Hem, bu önceden (ezelde) kararlaştırılmış bir iştir." dedi.'),
(2272, 19, 22, 'Nihayet (Allah\'ın emri gerçekleşti) Meryem İsa\'ya gebe kaldı ve o haliyle uzak bir yere çekildi.'),
(2273, 19, 23, 'Sonra doğum sancısı onu bir hurma dalına tutunup dayanmaya zorladı. "Keşke bundan önce ölseydim de unutulup gitseydim" dedi.'),
(2274, 19, 24, 'Melek, Meryem\'e, aşağı tarafından şöyle seslendi. "Sakın üzülme, Rabbin alt tarafında bir ırmak akıttı."'),
(2275, 19, 25, '"Hurma dalını kendine doğru silkele, üzerine devşirilmiş taze hurmalar dökülsün."'),
(2276, 19, 26, '"Ye, iç, gözün aydın olsun. Eğer insanlardan birini görürsen, ben Rahmân (olan Allah)a bir oruç (susmak) adadım. Onun için bugün hiçbir kimseyle konuşmayacağım" de.'),
(2277, 19, 27, 'Sonra Meryem onu (İsa\'yı) yüklenerek kavmine getirdi. Onlar (hayretler içinde şöyle) dediler: "Ey Meryem! doğrusu sen görülmemiş bir şey yaptın."'),
(2278, 19, 28, '"Ey Harun\'un kızkardeşi! Senin baban kötü bir adam değildi, annen de iffetsiz bir kadın değildi."'),
(2279, 19, 29, 'Bunun üzerine Meryem çocuğu gösterdi. Onlar; "Biz beşikteki bir çocukla nasıl konuşuruz?" dediler.'),
(2280, 19, 30, '(Allah\'ın bir mucizesi olarak İsa şöyle) dedi: "Şüphesiz ben Allah\'ın kuluyum. O bana kitab verdi ve beni bir peygamber yaptı."'),
(2281, 19, 31, '"Beni, nerede olursam olayım mübarek kıldı. Hayatta bulunduğum müddetçe namaz kılmamı ve zekat vermemi emretti."'),
(2282, 19, 32, '"Beni anneme hürmetkar kıldı. Beni zorba ve isyankar yapmadı."'),
(2283, 19, 33, '"Doğduğum gün, öleceğim gün ve dirileceğim gün selam ve emniyet benim üzerimedir."'),
(2284, 19, 34, 'İşte hakkında (yahudilerle hıristiyanların) ihtilaf edip durdukları Meryemoğlu İsa\'ya dair Allah\'ın sözü budur.'),
(2285, 19, 35, 'Çocuk edinmek asla Allah\'ın şanına yakışmaz. O bundan münezzehtir. O, bir şeyin olmasını dilerse, ona sadece "ol" der, o da oluverir.'),
(2286, 19, 36, '"Şüphesiz benim de Rabbim, sizin de Rabbiniz Allah\'tır. O halde ona ibadet edin, işte dosdoğru yol budur."'),
(2287, 19, 37, 'Ne var ki, fırkalar (yahudi ve hıristiyanlar) kendi aralarında ihtilafa düştüler. O büyük (dehşetli) günü görecek kâfirlerin vay haline!'),
(2288, 19, 38, 'Bize gelecekleri gün, neler işitecekler, neler görecekler! Fakat o zalimler bugün apaçık bir sapıklık içindedirler.'),
(2289, 19, 39, '(Ey Muhammed!) İnsanların pişmanlık duyacağı ve işin bitmiş olacağı (kıyamet) günü ile onları uyar. Onlar hâlâ gaflet içindedirler, onlar iman etmezler.'),
(2290, 19, 40, 'Şüphesiz biz bütün yeryüzüne ve üzerindekilere varis olacağız. Ve onlar da mutlaka bize döndürüleceklerdir.'),
(2291, 19, 41, 'Kur\'ân\'da İbrahim\'i(n kıssasını da) an. Şüphesiz ki o, sıddık (özü, sözü doğru) bir peygamberdi.'),
(2292, 19, 42, 'O, bir zaman babasına şöyle demişti: "Babacığım! İşitmeyen, görmeyen ve sana hiçbir faydası olmayan şeylere niçin tapıyorsun?"'),
(2293, 19, 43, '"Babacığım! Doğrusu sana gelmeyen bir ilim bana geldi. O halde bana uy da, seni doğru bir yola eriştireyim."'),
(2294, 19, 44, '"Babacığım! Şeytana tapma, çünkü şeytan Rahmân (olan Allah)a âsî oldu."'),
(2295, 19, 45, '"Babacığım! Doğrusu ben korkarım ki, sana Rahmân\'dan bir azab dokunur da şeytana (cehennemde arkadaş) olursun."'),
(2296, 19, 46, 'Babası "Ey İbrahim! Sen benim ilâhlarımdan yüz mü çeviriyorsun? Yemin ederim ki, eğer (onları kötülemekten) vazgeçmezsen, seni muhakkak taşlarım. (gerçektenveya söz ilesana taş atarım). Haydi uzun bir müddet benden uzak ol" dedi.'),
(2297, 19, 47, 'İbrahim şöyle dedi: "Selâm sana olsun, senin için Rabbimden mağfiret dileyeceğim. Çünkü o, bana çok lütufkârdır."'),
(2298, 19, 48, '"Ben, sizden ve Allah\'tan başka taptığınız şeylerden çekilip ayrılırım da Rabbime dua (ibadet) ederim. Rabbime yalvarışımda mahrum kalmayacağımı umarım."'),
(2299, 19, 49, 'İbrahim, kavminden ve onların Allah\'tan başka ibadet ettikleri şeylerden uzaklaşınca, biz ona İshak\'ı ve (İshak\'ın oğlu) Yakub\'u ihsan ettik. Ve hepsini de peygamber yaptık.'),
(2300, 19, 50, 'Biz onlara rahmetimizden lütuflarda bulunduk. Hepsine de dillerde güzel ve yüksek bir övgü verdik.'),
(2301, 19, 51, 'Kur\'ân\'da Musa\'yı da an; Şüphesiz ki o, ihlaslı bir kuldu ve gönderilmiş bir peygamberdi.'),
(2302, 19, 52, 'Biz ona Tur dağının sağ yanından seslendik ve onu hususi bir konuşmada bulunmak üzere kendimize yaklaştırdık.'),
(2303, 19, 53, 'Rahmetimizden de ona, kardeşi Harun\'u bir peygamber olarak ihsan eyledik. Meâli Şerifi'),
(2304, 19, 54, 'Kur\'ân\'da İsmail\'i de an; çünkü o, vaadine sadık bir kuldu ve gönderilmiş bir peygamberdi.'),
(2305, 19, 55, 'Ailesine ve çevresine namaz kılmayı ve zekat vermeyi emrederdi ve Rabbinin katında hoşnutluğa ermişti.'),
(2306, 19, 56, 'Kitapta İdris\'i de an; çünkü o, çok sadık (özü, sözü pek doğru) bir peygamberdi.'),
(2307, 19, 57, 'Biz onu yüce bir yere yükselttik.'),
(2308, 19, 58, 'İşte bunlar, Allah\'ın kendilerine nimetler verdiği peygamberlerden, Âdem\'in soyundan ve gemide Nuh ile beraber taşıdıklarımızın neslinden, İbrahim ve İsrail\'in soyundan, hidayete erdirdiğimiz ve seçtiğimiz kimselerdir. Kendilerine Rahmân (olan Allah)ın âyetleri okunduğu zaman ağlayarak secdeye kapanırlardı.'),
(2309, 19, 59, 'Sonra bunların ardından öyle bir nesil geldi ki, namazı terkettiler, heva ve heveslerine uydular; onlar bu taşkınlıklarının karşılığını mutlaka göreceklerdir. (Cehennemdeki "Gayya" vadisini boylayacaklardır.)'),
(2310, 19, 60, 'Fakat tevbe edip iman eden ve salih amel işleyen bunun dışındadır. Bunlar cennete girecekler ve hiçbir haksızlığa uğratılmayacaklardır.'),
(2311, 19, 61, 'O cennet, Rahmân (olan Allah)ın kullarına görmedikleri halde vadettiği "Adn" cennetleridir. Şüphesiz O\'nun vaadi mutlaka yerini bulacaktır.'),
(2312, 19, 62, 'Onlar orada boş bir söz işitmezler. Ancak "Selam" işitirler. Orada sabah akşam rızıkları da hazırdır.'),
(2313, 19, 63, 'İşte kullarımızdan takva sahibi olanlara vereceğimiz cennet budur.'),
(2314, 19, 64, '"(Cebrail dedi ki: Ey Muhammed!) "Biz senin Rabbinin emri olmadıkça inmeyiz. Önümüzdeki ve ardımızdaki (bütün geçmiş ve gelecek şeyler) ve bunların arasındakiler hep O\'nundur. Rabbin de (seni) unutmuş değildir?"'),
(2315, 19, 65, 'O, göklerin, yerin ve aralarındakilerin Rabbidir. O halde, O\'na ibadet et ve O\'na ibadet etmekte sabırlı ol. Hiç sen Allah\'ın ismini taşıyan başka birini bilir misin?'),
(2316, 19, 66, 'Halbuki insan şöyle der: "Ben öldüğüm zaman, ileride gerçekten diri olarak (mezardan) çıkarılacak mıyım?"'),
(2317, 19, 67, 'O insan, daha önce hiçbir şey değilken kendisini yoktan var ettiğimizi hatırlamaz mı?'),
(2318, 19, 68, 'Rabbine andolsun ki biz onları (öldükten sonra dirilmeyi inkâr eden kâfirleri) şeytanları ile beraber elbette ve elbette mahşerde toplayacağız. Sonra onları muhakkak cehennemin etrafında dizleri üstü hazır bulunduracağız (ki cennetlikleri görüp hasret çeksinler.).'),
(2319, 19, 69, 'Sonra her zümreden Rahmân\'a karşı en ziyade isyankâr hangileri ise, muhakkak ayırıp atacağız.'),
(2320, 19, 70, 'Sonra o cehenneme atılmaya layık olanların kimler bulunduğunu elbette biz daha iyi biliriz.'),
(2321, 19, 71, 'İçinizden hiçbiri istisna edilmemek üzere mutlaka herkes cehenneme varacaktır. Bu, Rabbinin katında kesinleşmiş bir hükümdür.'),
(2322, 19, 72, 'Sonra Allah\'dan korkup, sakınanları kurtaracağız ve zalimleri de toptan cehennemde bırakacağız.'),
(2323, 19, 73, 'Âyetlerimiz kendilerine apaçık okunduğu zaman, o inkâr edenler, iman edenlere dediler ki: "Bu iki zümreden (Mümin ve kâfirlerden) hangisi mevki bakımından daha iyi, meclis ve topluluk itibariyle daha güzeldir?"'),
(2324, 19, 74, 'Halbuki biz, kendilerinden evvel, mal ve gösterişce daha güzel nice asırlar halkını helak etmişizdir.'),
(2325, 19, 75, 'Onlara de ki: "Kim sapıklık içinde ise, Rahmân ona mal ve evlatça ziyadelik ve azgınlığında mühlet verir. Nihayet kendilerine vaad edilen azabı, yahut kıyamet günü cehennemi gördükleri vakit, artık bilecekler kimin mevkii daha fena ve yardımcıları daha zayıfmış.'),
(2326, 19, 76, 'Allah, hidayeti kabul edenlere, daha çok hidayet verir. Baki kalacak olan salih ameller, Rabbinin katında sevap bakımından da daha hayırlıdır, sonuç bakımından da daha hayırlıdır.'),
(2327, 19, 77, 'Şimdi âyetlerimizi inkâr eden ve "Elbette bana mal ve evlat verilecektir." diyen adamı gördün mü?'),
(2328, 19, 78, 'O (kâfir), gaybı mı bildi? Yoksa Rahmân (olan Allah) katından bir söz mü aldı?'),
(2329, 19, 79, 'Hayır, asla öyle değil; biz onun söylediklerini yazacağız ve azabını çoğalttıkça çoğaltacağız.'),
(2330, 19, 80, 'O söylediği (mal ve evlat gibi) şeyleri de hep elinden alacağız ve o, tek başına bize gelecektir.'),
(2331, 19, 81, 'Onlar, kendilerine kuvvet ve şeref kazandırsın diye, Allah\'dan başka ilâh edindiler.'),
(2332, 19, 82, 'Hayır, (zannettikleri gibi değil) tapındıkları ilâhlar onların ibadetlerini inkâr edecekler ve aleyhlerine dönüp düşman olacaklardır.'),
(2333, 19, 83, 'Görmedin mi? Biz şeytanları o kâfirler üzerine musallat ettik. Onları (günaha) kışkırtıp duruyorlar.'),
(2334, 19, 84, 'Öyleyse onların hemen azaba uğratılmalarını isteme. Biz onların (ecel) günlerini sayıyoruz.'),
(2335, 19, 85, 'O gün, takva sahiplerini, heyet olarak Rahmân\'ın huzuruna toplayacağız.'),
(2336, 19, 86, 'Suçluları da susuz olarak cehenneme süreceğiz.'),
(2337, 19, 87, '(O gün) Rahmân (olan Allah)\'ın katında bir ahd almış olan kimseden başkaları şefaat etme hakkına sahip olamayacaklardır.'),
(2338, 19, 88, '(Yahudilerle hıristiyanlar) "Rahmân, çocuk edindi" dediler.'),
(2339, 19, 89, 'Yemin olsun ki, siz çok çirkin bir şey söylediniz.'),
(2340, 19, 90, 'Az kalsın, söyledikleri sözden gökler çatlayacak, yer yarılacak ve dağlar parçalanıp dağılacaktı,'),
(2341, 19, 91, 'O Rahmân\'a çocuk isnad ettiler diye...'),
(2342, 19, 92, 'Halbuki Rahmân\'a çocuk edinmek yaraşmaz.'),
(2343, 19, 93, 'Göklerde ve yerde bulunan hiçbir kimse yoktur ki (kıyamet günü) Rahmân\'ın huzuruna kul olarak çıkmasın.'),
(2344, 19, 94, 'And olsun ki Allah onların hepsini kuşatmış, kendilerini ve yaptıklarını bir bir saymıştır.'),
(2345, 19, 95, 'Kıyamet günü onların herbiri Allah\'ın huzuruna tek başına çıkacaktır.'),
(2346, 19, 96, 'İman edip, salih amel işleyenler var ya, Rahmân (olan Allah) onları (gönüllere) sevdirecektir.'),
(2347, 19, 97, '(Ey Muhammed!) Biz Kur\'ân\'ı senin dilin üzere kolaylaştırdık ki, onunla Allah\'tan korkup sakınanları müjdeleyesin, inat edenleri de korkutasın.'),
(2348, 19, 98, 'Hem onlardan önce nice nesilleri helak ettik. (Şimdi) onlardan hiçbirini görüyor musun, yahud onların hafif bir sesini işitiyor musun?');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'Tâ, Hâ,'),
(2350, 20, 2, 'Ey Muhammed! Kur\'ân\'ı sana sıkıntıya düşesin diye indirmedik.'),
(2351, 20, 3, 'Ancak Allah\'tan korkan kimse için bir öğüt olarak (indirdik.)'),
(2352, 20, 4, 'Yeri ve yüce gökleri yaratanın katından yavaş yavaş bir indirilişle (onu) indirdik.'),
(2353, 20, 5, 'O Rahmân (kudret ve hakimiyyetiyle) Arş\'a hakim oldu.'),
(2354, 20, 6, 'Bütün göklerde olanlar, bütün yerdekiler, bu ikisinin arasında ve toprağın altıda bulunanlar O\'nundur.'),
(2355, 20, 7, 'Sen (Allah\'a ettiğin dua ve zikirle) sesini yükseltirsen (bilki Allah bundan mustağnîdir.). Çünkü O şüphesiz gizliyi de, gizlinin gizlisini de bilir.'),
(2356, 20, 8, 'Allah O\'dur ki, kendisinden başka hiçbir ilâh yoktur. En güzel isimler O\'nundur.'),
(2357, 20, 9, '(Habîbim!) Musa\'nın (başından geçen hayat) hikayesi sana geldi mi?'),
(2358, 20, 10, 'Hani o bir ateş görmüştü de, ailesine: "Yerinizde durun, benim gözüme bir ateş ilişti, belki size bir kor getiririm, yahut ateşin yanında bir yol gösterici bulurum" demişti.'),
(2359, 20, 11, 'Ateşe vardığı zaman şöyle çağrıldı: "Ey Musa!'),
(2360, 20, 12, '"Ben şüphesiz senin Rabbinim. Hemen ayakkabılarını çıkar, çünkü sen kutsal bir vadi olan Tuvâ\'dasın."'),
(2361, 20, 13, '"Ben seni seçtim, şimdi (sana) vahyolunacak şeyleri dinle."'),
(2362, 20, 14, 'Şüphesiz ben Allah\'ım, benden başka hiçbir ilâh yoktur. Onun için bana kulluk et ve beni anmak için namaz kıl.'),
(2363, 20, 15, 'Çünkü kıyamet muhakkak gelecektir. Onun vaktini gizli tutuyorum ki, herkes yaptığının karşılığını görsün.'),
(2364, 20, 16, 'Sakın kıyamete inanmayıp, kendi heva ve hevesine uyan kimse seni, ona iman etmekten alıkoymasın; sonra helak olursun.'),
(2365, 20, 17, 'Ey Musa! Sağ elindeki nedir?'),
(2366, 20, 18, 'Musa dedi: "O benim asâm (değneğim) dır, ona dayanırım, onunla davarlarıma yaprak silkerim ve onda başka hacetlerim (faydalanacağım şeyler) de var"'),
(2367, 20, 19, 'Allah: "Ey Musa! onu (yere) bırak"dedi.'),
(2368, 20, 20, 'Musa da onu bıraktı, bir de ne görsün! o bir yılan olmuş koşuyor.'),
(2369, 20, 21, 'Allah buyurdu ki: "Tut onu, korkma; biz onu yine eski durumuna çevireceğiz"'),
(2370, 20, 22, '"Bir de diğer bir mucize olmak üzere elini koynuna koy ki, kusursuz olarak bembeyaz çıksın."'),
(2371, 20, 23, '"Bunları sana en büyük mucizelerimizden (bir kısmını) gösterelim diye yaptık."'),
(2372, 20, 24, '"Firavun\'a git, çünkü o hakikaten azdı."'),
(2373, 20, 25, 'Musa dedi ki: "Ey Rabbim! Benim göğsüme genişlik ver,'),
(2374, 20, 26, 'İşimi kolaylaştır,'),
(2375, 20, 27, 'Dilimden düğümü çöz'),
(2376, 20, 28, 'Ki, sözümü iyi anlasınlar.'),
(2377, 20, 29, 'Bir de bana ailemden bir vezir ver.'),
(2378, 20, 30, 'Kardeşim Harun\'u (ver).'),
(2379, 20, 31, 'Onunla arkamı kuvvetlendir.'),
(2380, 20, 32, '(Elçilik) işimde onu bana ortak et.'),
(2381, 20, 33, 'Ki seni çok tesbih edelim.'),
(2382, 20, 34, 'Seni çok analım.'),
(2383, 20, 35, 'Şüphe yok ki sen bizi görüp duruyorsun."'),
(2384, 20, 36, 'Allah buyurdu: "Ey Musa! Dilediğin (şeyler) sana verildi."'),
(2385, 20, 37, '"And olsun biz, sana diğer bir defa daha ihsan etmiştik"'),
(2386, 20, 38, 'Hani bir vakit ilham edilmesi gereken (ancak ilham ile bilinebilen) şu ilhamı annene verdik:'),
(2387, 20, 39, '"Onu (Musa\'yı) tabut içine koy da denize bırak. Deniz de onu sahile atsın. Onu hem bana düşman, hem ona düşman olan biri alsın." Bir de benim gözetimim altında yetiştirilmen için, üzerine katımdan bir sevgi bırakmıştım. (Ey Musa!)'),
(2388, 20, 40, 'Hani kız kardeşin (Firavun\'un sarayına) giderek: "Ona bakacak birini size buluvereyim mi? diyordu. Böylece seni tekrar annene verdik ki, gözü aydın olsun da kederlenmesin. Hem sen, bir adam öldürdün de seni gamdan kurtardık. Seni çeşitli musibetlerle imtihan ettik. Bu sebeple yıllarca Medyen halkı içinde kaldın. Sonra ey Musa! Belli bir çağa (peygamberlik görevini yüklenecek bir yaşa) geldin.'),
(2389, 20, 41, 'Ben, seni kendime (peygamber) seçtim.'),
(2390, 20, 42, 'Sen kardeşinle birlikte mucizelerimle git. İkiniz de beni anmakta gevşeklik etmeyin.'),
(2391, 20, 43, 'Firavun\'a gidin, çünkü o gerçekten azdı.'),
(2392, 20, 44, 'Varın da ona yumuşak söz söyleyin; olur ki, öğüt dinler, yahut korkar.'),
(2393, 20, 45, '(Musa ile Harun) "Rabbimiz! Onun bize kötülük yapmasından veya azgınlığını artırmasından korkarız" dediler.'),
(2394, 20, 46, 'Allah buyurdu ki: "Korkmayın, zira ben sizinle beraberim, işitir ve görürüm."'),
(2395, 20, 47, 'Hemen gidin de Firavun\'a deyin ki: "Biz Rabbinin (sana gönderilen) elçileriyiz. Artık İsrailoğulları\'nı bizimle gönder, onlara azab etme; biz sana Rabbinden bir mucize ile geldik. Selam doğru yolda gidenleredir."'),
(2396, 20, 48, '"Bize kesin olarak vahyolundu ki, azab şüphesiz (gerçeği) inkâr edip ona sırt çevirenleredir."'),
(2397, 20, 49, 'Firavun: "Ey Musa! Sizin Rabbiniz kimdir?" dedi.'),
(2398, 20, 50, 'Musa: "Bizim Rabbimiz her şeye şeklini veren, sonra da yolunu gösterendir." dedi.'),
(2399, 20, 51, 'Firavun: "Öyleyse geçmiş asırlar (daki insanlar)ın durumu nedir?" dedi.'),
(2400, 20, 52, 'Musa dedi ki: "Onların bilgisi Rabbimin katında bir kitapta (yazılı)dır. Rabbim yanlış yapmaz ve unutmaz."'),
(2401, 20, 53, '"Yeryüzünü sizin için bir döşek yapan, oradan sizin için yollar açan ve gökten bir su indiren O\'dur." İşte biz o su ile türlü türlü bitkilerden çiftler çıkardık.'),
(2402, 20, 54, 'Hem siz yiyin, hem de hayvanlarınızı otlatın. Akıl sahibleri için bunda nice ibretler vardır!'),
(2403, 20, 55, 'Sizi yerden (topraktan) yarattık, yine (ölümünüzden sonra) ona döndüreceğiz. Hem de ondan sizi bir kere daha çıkaracağız.'),
(2404, 20, 56, 'And olsun ki, biz, Firavun\'a mucizelerimizin hepsini gösterdik. Böyle iken o yine onları yalan sayıp kabulden çekindi.'),
(2405, 20, 57, '(Firavun Musa\'ya şöyle) dedi: "Ey Musa! Sen sihrinle bizi yerimizden çıkarmak için mi geldin bize?"'),
(2406, 20, 58, '"O halde biz de senin sihrin gibi bir sihirle sana geleceğiz (karşına çıkacağız); şimdi bizimle senin aranda bir vakit ve bir buluşma yeri tayin et ki; ne senin, ne bizim caymayacağımız uygun bir yer olsun."'),
(2407, 20, 59, 'Musa: "Sizinle buluşma zamanı, süs (bayramı) günü ve insanların toplanacağı kuşluk vaktidir." dedi.'),
(2408, 20, 60, 'Bunun üzerine Firavun döndü gitti ve bütün hile vasıtalarını topladıktan sonra geldi.'),
(2409, 20, 61, 'Musa onlara dedi ki: "Yazıklar olsun size! Allah\'a yalan uydur mayın. Sonra bir azab ile kökünüzü keser. Gerçekten (Allah\'a) iftira eden hüsrana uğramıştır."'),
(2410, 20, 62, 'Sihirbazlar aralarında işlerini tartıştılar ve konuşmalarını gizli tuttular'),
(2411, 20, 63, '(Sihirbazlar daha sonra Musa ve Harun\'u göstererek şöyle) dediler: "Bu ikisi muhakkak sihirbazdır; büyüleriyle sizi yurdunuzdan çıkarmak ve de örnek dininizi yok etmek istiyorlar."'),
(2412, 20, 64, '"Onun için bütün tuzaklarınızı bir araya getirin, sonra hep bir sıra halinde gelin. Bugün üstün gelen muhakkak zafer kazanmıştır."'),
(2413, 20, 65, 'Sihirbazlar: "Ey Musa! Ya sen at, yahud ilk atan biz olalım" dediler.'),
(2414, 20, 66, 'Musa dedi ki: "Hayır, siz atın." Bir de ne görsün! Onların ipleri ve değnekleri, yaptıkları sihirden ötürü kendisine sanki yürüyorlarmış gibi geldi.'),
(2415, 20, 67, 'Bu yüzden Musa içinde bir korku hissetti.'),
(2416, 20, 68, 'Biz dedik ki: "Korkma, çünkü sen muhakkak üstünsün (galib geleceksin) "'),
(2417, 20, 69, '"Sağ elindekini atıver, o, onların yaptıklarını yutar. Çünkü onların yaptıkları ancak bir büyücü tuzağıdır. Büyücü ise, her nerede olursa olsun başarıya ulaşamaz."'),
(2418, 20, 70, 'Sonunda bütün sihirbazlar secdeye kapandılar, "Musa ile Harun\'un Rabbine iman ettik" dediler.'),
(2419, 20, 71, 'Firavun: "Ben size izin vermeden mi ona iman ettiniz? O, muhakkak size sihir öğreten büyüğünüzdür. And olsun ki, ellerinizi ve ayaklarınızı çaprazlama keseceğim ve muhakkak sizi hurma dallarına asacağım. Böylece hangimizin azabının daha şiddetli ve devamlı olduğunu bileceksiniz" dedi.'),
(2420, 20, 72, '(İman eden sihirbazlar şöyle) dediler: "Bize gelen bu açık mucizeler ve bizi yaratana karşı, asla seni tercih edemeyiz. Ne hüküm vereceksen ver. Sen, ancak bu dünya hayatına hükmedebilirsin."'),
(2421, 20, 73, '"Doğrusu biz hem günahlarımıza, hem bizi zorladığın sihre karşı, bizi bağışlasın diye, Rabbimize iman ettik. Allah (sevabça senden) daha hayırlı ve (azab verme bakımından da) daha devamlıdır."'),
(2422, 20, 74, 'Her kim Rabbine suçlu olarak varırsa, şüphesiz ki ona cehennem vardır. Orada ne ölür, ne de dirilir.'),
(2423, 20, 75, 'Kim de ona bir mümin olarak salih ameller işlemiş olduğu halde varırsa, işte onlara en yüksek dereceler vardır.'),
(2424, 20, 76, 'Adn cennetleri vardır ki, altlarından ırmaklar akar, onlar, orada ebedî olarak kalacaklardır. Ve işte bu, (küfür ve isyandan) arınanların mükafatıdır. Meâli Şerifi'),
(2425, 20, 77, 'Gerçekten Musa\'ya şöyle vahyettik: "Kullarımla geceleyin yürü (Mısır\'dan çık) de (asânı vurarak) onlara denizde kuru bir yol aç; (artık firavun tarafından) yetişilmekten korkmazsın ve (boğulmaktan) endişe de etmezsin."'),
(2426, 20, 78, 'Firavun ordularıyla hemen onları takip etti, denizden kendilerini sarıveren (korkunç boğulma) sarıverdi'),
(2427, 20, 79, 'Böylece Firavun kavmini yanlış yola sürükledi ve doğru yola götürmedi.'),
(2428, 20, 80, 'Ey İsrailoğulları! Sizleri düşmanınızdan kurtardık ve Tûr dağının sağ yanında size söz verdik, üzerinize de kudret helvası ve bıldırcın indirdik.'),
(2429, 20, 81, 'Size verdiğimiz rızıkların en temizlerinden yiyin ve bunda taşkınlık etmeyin, sonra üzerinize gazabım iner. Kimin üzerine de gazabım inerse, muhakkak o mahvolur.'),
(2430, 20, 82, 'Bununla beraber, şüphe yok ki ben, tevbe eden, iman edip salih amel işleyen, sonra da hak yolda sebat gösteren kimse için çok bağışlayıcıyım.'),
(2431, 20, 83, '"Ey Musa! Seni kavminden (ayırıp) daha çabuk (gelmeye) sevkeden nedir?" (dedik.)'),
(2432, 20, 84, 'Musa: "Onlar benim izimdeler (arkamdan beni takip edip geliyorlar). Ben sana acele ettim (geldim) ki, hoşnud olasın" dedi.'),
(2433, 20, 85, 'Allah: "Doğrusu biz senden sonra kavmini imtihan ettik. Sâmirî onları saptırdı" dedi.'),
(2434, 20, 86, 'Hemen Musa öfkeli ve üzgün olarak kavmine döndü (onlara şöyle) dedi: "Ey kavmim! Rabbiniz size güzel bir vaad ile söz vermedi mi? Size bu süre mi çok uzun geldi, yoksa Rabbinizden size bir gazab inmesini arzu ettiniz de mi, bana olan vaadinizden caydınız?"'),
(2435, 20, 87, 'Onlar dediler ki: "Biz sana verdiğimiz sözden, kendiliğimizden caymadık. Fakat biz o (Kıbtî) kavminin süs eşyasından bir takım ağırlıklar yüklenmiştik. Onları (ateşe) attık. Sâmirî de (kendi mücevheratını) böylece atmıştı."'),
(2436, 20, 88, 'Nihayet Sâmirî onlara böğüren bir buzağı heykeli ortaya çıkardı. Bunun üzerine Sâmirî ve adamları: "İşte sizin de, Musa\'nın da ilâhı budur, ama o unuttu" dediler.'),
(2437, 20, 89, 'Onlar görmüyorlar mıydı ki, o buzağı, kendilerine hiçbir sözle karşılık veremiyor; onlara ne bir zarar, ne de bir yarar vermeye sahip bulunamıyordu.'),
(2438, 20, 90, 'And olsun ki Harun daha önce onlara: "Ey kavmim! Siz bununla (buzağı ile) imtihana çekildiniz. Sizin gerçek Rabbiniz Rahmân\'dır. Gelin bana uyun ve emrime itaat edin" demişti.'),
(2439, 20, 91, 'Onlar (cevap olarak şöyle) demişlerdi: "Musa bize dönüp gelinceye kadar, biz ona tapmaya elbette devam edeceğiz."'),
(2440, 20, 92, '(Musa gelince kardeşine şöyle) dedi: "Ey Harun! bunların sapıklığa düştüğünü gördüğün vakit, seni engelleyen ne oldu?"'),
(2441, 20, 93, '"(Neden) benim yolumu takip etmedin, benim emrime karşı mı geldin?"'),
(2442, 20, 94, 'Harun: "Ey anamın oğlu! Sakalımı ve başımı (saçımı) tutma. Ben senin \'İsrailoğulları arasında ayrılık çıkardın, sözüme bakmadın\' diyeceğinden korktum." dedi.'),
(2443, 20, 95, '(Hz. Musa bu defa Sâmirî\'ye dönerek) "Ey Sâmirî! Senin bu yaptığın nedir?" dedi.'),
(2444, 20, 96, 'Sâmirî: "Onların görmedikleri bir şey gördüm: (Sana gelen) ilâhî elçinin (Cebrail\'in) izinden bir avuç (toprak) aldım ve onu (erimiş mücevheratın içine) attım. Bunu, bana böylece nefsim hoş gösterdi" dedi.'),
(2445, 20, 97, '(Musa ona şöyle) dedi: "Haydi çekil git. Artık senin için hayat boyunca, \'benimle temas yok\' diye söylemen var (bir vahşi gibi yapayalnız yaşamağa mahkum olacaksın). Hem senin için asla kaçamayacağın bir ceza daha vardır. Bir de ibadet edip durduğun ilâhına bak; elbette biz onu yakacağız, sonra da kül edip muhakkak onu denize savuracağız."'),
(2446, 20, 98, 'Sizin ilâhınız, ancak kendisinden başka hiçbir ilâh bulunmayan Allah\'dır. Onun ilmi her şeyi kuşatmıştır.'),
(2447, 20, 99, '(Ey Muhammed!) Sana geçmişin haberlerinden bir kısmını böylece anlatıyoruz. Şüphe yok ki, sana katımızdan bir zikir (düşünüp kendisinden ibret alınacak bir kitab) verdik.'),
(2448, 20, 100, 'Kim ondan yüz çevirirse, şüphesiz o, kıyamet günü bir günah yüklenecektir.'),
(2449, 20, 101, 'Devamlı o azabın altında kalacaklar. Kıyamet günü onlar için, bu ne fena bir yüktür!'),
(2450, 20, 102, 'Sûr\'a üfürüleceği gün ki biz suçluları o gün, (gözleri korkudan) göğermiş olarak mahşerde toplayacağız.'),
(2451, 20, 103, '"Siz dünyada sadece on(gün) kaldınız" diye kendi aralarında gizli gizli konuşurlar.'),
(2452, 20, 104, 'Aralarında ne konuşacaklarını biz çok iyi biliriz. Görüşü en üstün olan: "Ancak bir gün kaldınız" diyecektir.'),
(2453, 20, 105, '(Ey Muhammed!) Sana dağlar(ın kıyametteki durumunu) sorarlar, de ki: "Rabbim onları ufalayıp savuracak."'),
(2454, 20, 106, '"Böylece yerlerini dümdüz boş bir halde bırakacak."'),
(2455, 20, 107, '"Orada ne bir çukur, ne de bir tümsek göreceksin."'),
(2456, 20, 108, 'O gün, hiçbir tarafa sapmadan o davetçiye (Sûr\'a üfleyenin çağrısına) uyarlar. Öyleki, Rahmân\'ın heybetinden sesler kısılmıştır. Artık bir fısıltıdan başka hiçbir şey işitemezsin.'),
(2457, 20, 109, 'O gün, Rahmân\'ın kendisine izin verdiği ve sözünden hoşnud olduğu kimselerden başkasının şefaatı fayda vermez.'),
(2458, 20, 110, 'Allah, onların geleceklerini de, geçmişlerini de bilir. Onlar ise O\'nu ilmen kavrayamazlar.'),
(2459, 20, 111, 'Bütün yüzler, diri ve bütün yarattıklarını gözetip duran Allah\'a baş eğmiştir. Bir zulüm yüklenen gerçekten hüsrana uğramıştır.'),
(2460, 20, 112, 'Her kim de mümin olarak salih amelleri işlerse, artık o, ne bir haksızlıktan ve ne de çiğnenmekden korkar.'),
(2461, 20, 113, 'İşte böylece biz onu Arapça bir Kur\'ân olarak indirdik. Onda tehditlerden nice türlüsünü tekrar tekrar açıkladık ki belki sakınırlar, yahut onlara bir ibret ve uyanış verir.'),
(2462, 20, 114, 'Hükmü her yerde geçerli gerçek hükümdar olan Allah yücedir. (Ey Muhammed!) Kur\'ân sana vahyedilirken, vahiy bitmeden önce (unutma korkusu ile) Kur\'ân\'ı okumada acele etme; "Rabbim! benim ilmimi artır" de.'),
(2463, 20, 115, 'Doğrusu bundan önce Âdem\'e (bu ağaçtan yeme diye) emrettik, fakat unuttu ve biz onda bir azim (bir kararlılık) bulmadık.'),
(2464, 20, 116, 'Bir vakit meleklere: "Âdem(e hürmet) için secde edin" demiştik; İblis\'ten başka hepsi secde etmiş, o çekinmişti.'),
(2465, 20, 117, 'Biz de (Âdem\'e) şöyle demiştik: "Ey Âdem! Şüphesiz bu (İblis) sana ve eşine düşmandır. Sakın sizi cennetten çıkarmasın, sonra bedbaht olursun (sıkıntı çeker, perişan olursun)."'),
(2466, 20, 118, '"Doğrusu senin acıkmaman ve çıplak kalmaman (ancak) cennettedir. "'),
(2467, 20, 119, 'Ve sen orada ne susarsın, ne de güneşin sıcağında kalırsın"'),
(2468, 20, 120, 'Nihayet şeytan ona vesvese verdi. Şöyle dedi: "Ey Âdem! Sana sonsuzluk ağacını ve çökmesi olmayan bir saltanatı göstereyim mi?"'),
(2469, 20, 121, 'Bunun üzerine ikisi de o ağaçtan yediler. Hemen ayıp yerleri kendilerine açılıp görünüverdi. Ve üzerlerine cennet yaprağından örtüp yamamaya başladılar. Âdem Rabbinin emrinden çıktı da şaşırdı.'),
(2470, 20, 122, 'Sonra Rabbi, onu seçti de tevbesini kabul buyurdu ve ona doğru yolu gösterdi.'),
(2471, 20, 123, 'Allah (onlara) şöyle dedi: "Birbirinize düşman olmak üzere hepiniz oradan (cennetten) inin. Artık benden size bir hidayet (kitab) geldiği zaman, kim benim hidayetime uyarsa işte o, sapıklığa düşmez ve (ahirette) zahmet çekmez.'),
(2472, 20, 124, 'Her kim de benim zikrimden (Kur\'ân\'dan) yüz çevirirse, (bilsin ki) ona dar bir geçim vardır ve onu kıyamet günü kör olarak haşrederiz.'),
(2473, 20, 125, '(O zaman Kur\'ândan yüz çeviren kimse) "Rabbim! beni niçin kör olarak haşrettin, oysa ben gören bir kimseydim" der.'),
(2474, 20, 126, 'Allah: "Böyledir, sana âyetlerimiz gelmişti de onları sen unutmuştun, bugün de öylece unutulursun" der.'),
(2475, 20, 127, 'İşte haddi aşanları, Rabbinin âyetlerine inanmayanları biz böyle cezalandırırız. Ve muhakkak ki ahiret azabı (dünya azabından) daha şiddetli ve daha devamlıdır.'),
(2476, 20, 128, 'Onları, yerlerinde gezip durdukları şu kendilerinden önce yok ettiğimiz bunca nesiller(in o korkunç akibeti) doğru yola sevk etmedi mi? Doğrusu bunda ibret alacak aklı olanlar için nice deliller vardır.'),
(2477, 20, 129, 'Eğer Rabbinin verdiği bir hüküm ve tayin ettiği bir süre olmasaydı, hemen azaba uğrarlardı.'),
(2478, 20, 130, 'O halde, dediklerine sabret; güneşin doğmasından önce ve batmasından önce Rabbini hamd ile tesbih et. Gecenin bir kısım vakitlerinde ve gündüzün etrafında da tesbih et ki hoşnudluğa eresin.'),
(2479, 20, 131, 'Kâfirlerden bir kısmına, onları sınamak için dünya hayatının zineti olarak verdiğimiz ve onunla kendilerini geçindirdiğimiz şeye (mal ve saltanata) sakın rağbetle bakma. Rabbinin (ahiretteki) rızkı daha hayırlı ve daha devamlıdır.'),
(2480, 20, 132, '(Ey Muhammed!) Ehline namaz kılmalarını emret, kendin de ona sabırla devam et. Biz senden bir rızık istemiyoruz. Seni biz rızıklandırırız. Güzel akibet takva sahiplerinindir.'),
(2481, 20, 133, '(İnkâr edenler): "Rabbinden bize bir mucize getirse ya" dediler. Onlara önceki kitablarda olan apaçık deliller gelmedi mi?'),
(2482, 20, 134, 'Eğer biz, onları bundan (peygamber veya Kur\'ân\'dan) önce bir azab ile yok etseydik, muhakkak "Ey Rabbimiz! bize bir peygamber gönderseydin de, alçak ve rezil olmadan önce âyetlerine uysaydık, olmaz mıydı?" diyeceklerdi.'),
(2483, 20, 135, 'De ki: "Hepimiz beklemekteyiz, siz de bekleyedurun. Şüphesiz düz yolun sahiplerinin kimler olduğunu ve kimlerin doğru yolda bulunduğunu yakında bileceksiniz.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'İnsanların hesab (görme) zamanı yaklaştı. Onlar ise hâlâ gaflet içinde, yan çizip aldırmıyorlar.'),
(2485, 21, 2, 'Rablerinden kendilerine gelen her yeni hatırlatmayı hep eğlenerek dinliyorlar.'),
(2486, 21, 3, 'Kalbleri hep eğlencede (gaflette), hem o zalimler aralarında şu gizli fısıltıyı yaptılar: "Bu, ancak sizin gibi bir insan. Artık göz göre göre sihre mi gidip uyarsınız?"'),
(2487, 21, 4, 'Peygamber: "Benim Rabbim gökte ve yerde (söylenen) her sözü bilir. O, her şeyi işitir, her şeyi bilir" dedi.'),
(2488, 21, 5, 'Onlar: "Hayır, bunlar karışık rüyalardır; yok, onu kendisi uydurdu, yok o bir şairdir. Böyle değilse önceki peygamberler gibi, o da bize bir mucize getirsin" dediler.'),
(2489, 21, 6, 'Onlardan önce yok ettiğimiz hiçbir memleket halkı iman etmedi. Şimdi bunlar mı iman edecekler?'),
(2490, 21, 7, '(Ey Muhammed!) Biz, senden önce de ancak kendilerine vahyettiğimiz birtakım erkek(peygamber)ler gönderdik. Bilmiyorsanız kitap ehli olanlara sorun.'),
(2491, 21, 8, 'Biz onları yemek yemez birer cesed kılmadık ve onlar ölümsüz de değillerdi.'),
(2492, 21, 9, 'Sonra biz onlara verdiğimiz sözü yerine getirdik; hem onları, hem de dilediğimiz kimseleri kurtardık, aşırı gidenleri yok ettik.'),
(2493, 21, 10, '(Ey Kureyş topluluğu!) And olsun, size öyle bir kitab indirdik ki, bütün şan ve şerefiniz ondadır. Hâlâ akıllanmayacak mısınız?'),
(2494, 21, 11, 'Biz halkı zalim olan nice memleketleri kırıp geçirdik ve onlardan sonra başka milletler var ettik.'),
(2495, 21, 12, 'Onlar azabımızın şiddetini hissettikleri zaman oradan kaçmaya koyuluyorlardı.'),
(2496, 21, 13, '"Koşup kaçmayın; size nimet verilen yere, yurtlarınıza dönün ki, sorguya çekileceksiniz" dedik.'),
(2497, 21, 14, 'Onlar da: "Vay bizlere! Biz gerçekten zalimler idik" dediler.'),
(2498, 21, 15, 'Biz, onları biçilmiş bir ekin ve bir yığın kül haline getirinceye kadar hep sözleri bu feryad olmuştur.'),
(2499, 21, 16, 'Biz gök ile yeri ve aralarındaki şeyleri, boş bir eğlence için yaratmadık.'),
(2500, 21, 17, 'Eğer bir eğlence edinmek isteseydik, elbette onu katımızdan edinirdik. Yapacak olsaydık öyle yapardık.'),
(2501, 21, 18, 'Hayır, biz hakkı batılın başına çarparız da onun beynini parçalar. Bir de bakarsın (batıl) o anda yok olup gitmiştir. Allah\'a yakıştırdığınız vasıflardan ötürü size yazıklar olsun.'),
(2502, 21, 19, 'Göklerde ve yerde olan bütün varlıklar O\'nundur. Katında olanlar O\'na kulluk etmekten ne çekinirler, ne de yorulurlar.'),
(2503, 21, 20, 'Gece gündüz (hep Allah\'ı) tesbih ederler, usanmazlar.'),
(2504, 21, 21, 'Yoksa (Mekke müşrikleri) birtakım ilâhlar edindiler de yerden ölüleri onlar mı diriltecekler?'),
(2505, 21, 22, 'Eğer yer ile gökte Allah\'tan başka ilâhlar olsaydı, bunların ikisi de muhakkak fesada uğrar yok olurdu. O halde Arş\'ın Rabbi olan Allah, onların vasfetmekte oldukları şeylerden (bütün noksanlıklardan) beridir, münezzehtir.'),
(2506, 21, 23, 'O, yaptığından sorumlu olmaz, onlar ise sorumlu tutulacaklardır.'),
(2507, 21, 24, 'Yoksa O\'ndan başka ilâhlar mı edindiler? De ki: "Kesin delilinizi getirin. İşte benimle beraber olanların kitabı ve benden öncekilerin kitabı." Hayır, onların çoğu gerçeği bilmezler de onun için yüz çevirirler.'),
(2508, 21, 25, 'Senden önce hiçbir peygamber göndermedik ki, ona şöyle vahyetmiş olmayalım: "Gerçek şu ki benden başka ilâh yoktur. Onun için bana ibadet edin."'),
(2509, 21, 26, 'Böyle iken dediler ki: "Rahmân çocuk edindi." Allah bundan münezzehtir. Doğrusu melekler (Allah\'ın çocukları değil.) ikram olunmuş kullardır.'),
(2510, 21, 27, 'Onlar Allah\'ın sözünün önüne geçmezler, hep O\'nun emriyle hareket ederler.'),
(2511, 21, 28, 'Allah, onların önlerindekini de, arkalarındakini de (yaptıklarını ve yapacaklarını) bilir. Onlar, Allah\'ın hoşnud olduğu kimseden başkasına şefaat etmezler. Hepsi de O\'nun korkusundan titrerler.'),
(2512, 21, 29, 'İçlerinden kim: "Ben, O\'ndan başka bir ilâhım" derse, biz ona cehennemi ceza olarak veririz. Zalimleri biz böyle cezalandırırız.'),
(2513, 21, 30, 'O kâfir olanlar, görmediler mi ki, göklerle yer bitişik bir halde iken biz onları ayırdık. Hayatı olan her şeyi sudan yarattık. Hâlâ inanmıyorlar mı?'),
(2514, 21, 31, 'Yeryüzünde, insanlar sarsılmasın diye sabit dağlar yarattık, rahat gidebilsinler diye dağların aralarında geniş yollar var ettik.'),
(2515, 21, 32, 'Gökyüzünü de korunmuş bir tavan yaptık. Kâfirler ise, gökyüzünün alâmetlerinden (Allah\'ın kudret ve azametine delalet eden delillerinden) yüz çeviriyorlar.'),
(2516, 21, 33, 'Geceyi, gündüzü, güneşi ve ayı yaratan O\'dur. Bunların her biri kendi dairesinde dolaşmaktadır.'),
(2517, 21, 34, 'Ey Muhammed! Senden önce de hiçbir insanı ölümsüz kılmadık, sen ölürsün de onlar baki kalır mı? Senin ölmenle rahata kavuşacaklarını mı sanıyorlar?'),
(2518, 21, 35, 'Her nefis ölümü tadacaktır. Sizi bir imtihan olarak kötülük ve iyilikle deneyeceğiz. Hepiniz de sonunda bize döndürüleceksiniz.'),
(2519, 21, 36, 'O inkârcılar seni gördükleri zaman, seni alaya alıyorlar ve "İlâhlarınızı diline dolayan bu mudur?" diyorlar. Halbuki onlar Rahmân\'ın kitabını inkâr ediyorlar.'),
(2520, 21, 37, 'İnsan aceleci olarak yaratılmıştır. Size yakında (azaba dair) alametlerimi göstereceğim. Şimdi siz acele etmeyin.'),
(2521, 21, 38, '"Doğru sözlü iseniz (bildirin) bu vaad ne zamandır?" derler.'),
(2522, 21, 39, 'Bu kâfirler ateşi yüzlerinden ve sırtlarından men edemeyecekleri ve yardım da göremeyecekleri zamanı, bir bilseler!'),
(2523, 21, 40, 'Doğrusu bu azap onlara ansızın gelecek de kendilerini şaşırtacaktır. Artık ne geri çevrilmesine güçleri yetecek, ne de kendilerine mühlet verilecektir.'),
(2524, 21, 41, 'Yemin olsun ki, senden önce birçok peygamberle alay edildi de içlerinden alay edenleri, o alay ettikleri şey (azap) kuşatıverdi.'),
(2525, 21, 42, 'De ki: "Geceleyin ve gündüzün sizi Rahmân\'dan kim koruyabilir?" Ama onlar Rablerinin kitabından yüz çevirmektedirler.'),
(2526, 21, 43, 'Yoksa kendilerini bize karşı savunacak tanrıları mı var? O tanrılar kendilerine bile yardım edemezler, katımızdan da dostluk görmezler.'),
(2527, 21, 44, 'Doğrusu biz o kâfirleri ve atalarını yaşattık, hatta o ömür onlara uzun geldi. Fakat şimdi memleketlerini her yandan eksilttiğimizi görmüyorlar mı? O halde üstün gelen onlar mıdır?'),
(2528, 21, 45, 'De ki: "Ben sizi ancak vahiyle korkutup uyarıyorum," uyarıldıkları zaman sağırlar çağrıyı duymazlar.'),
(2529, 21, 46, 'Yemin olsun ki, Rabbinin azabından az bir şey onlara dokunursa, muhakkak "Vay bizlere, biz gerçekten zalimlerdik" diyeceklerdir.'),
(2530, 21, 47, 'Biz kıyamet günü için doğru teraziler kurarız; hiçbir kimse hiçbir haksızlığa uğratılmaz. Yapılan amel, bir hardal tanesi ağırlığınca da olsa, onu getirir (tartıya koyarız.). Hesap görenler olarak da biz kâfiyiz.'),
(2531, 21, 48, 'Yemin olsun ki, Musa ve Harun\'a eğriyi doğrudan ayıran kitabı, takva sahibleri için bir ışık ve öğüt olarak verdik.'),
(2532, 21, 49, 'Onlar görmedikleri halde Rablerinden korkarlar, kıyamet saatinden de titrerler.'),
(2533, 21, 50, 'İşte bu (Kwr\'ân) da indirdiğimiz kutsal bir kitaptır. Şimdi siz bunu mu inkâr ediyorsunuz?'),
(2534, 21, 51, 'And olsun ki biz daha önce İbrahim\'e de rüşdünü vermiştik (akla uygun olanı göstermiştik). Biz onu biliyorduk.'),
(2535, 21, 52, 'O zaman o, babasına ve kavmine: "Bu tapınıp durduğunuz heykeller nedir?" demişti.'),
(2536, 21, 53, 'Onlar: "Biz atalarımızı bunlara tapar bulduk" dediler.'),
(2537, 21, 54, 'İbrahim: "And olsun ki sizler de, atalarınız da apaçık bir sapıklık içindesiniz" dedi.'),
(2538, 21, 55, 'Onlar: "Sen bize gerçeği mi getirdin (Sen ciddi mi söylüyorsun), yoksa şaka mı ediyorsun?" dediler.'),
(2539, 21, 56, 'O şöyle dedi: "Hayır Rabbiniz göklerin ve yerin Rabbidir ki onları O yaratmıştır. Ben de buna şahidlik edenlerdenim."'),
(2540, 21, 57, '"Allah\'a yemin ederim ki, siz arkanızı dönüp gittikten sonra, ben putlarınıza elbette bir tuzak kuracağım."'),
(2541, 21, 58, 'Derken o, bunları parça parça etti. Yalnız kendisine başvursunlar diye onların büyüğünü sağlam bıraktı.'),
(2542, 21, 59, '(Kavmi) "Tanrılarımıza bunu kim yaptı? Doğrusu o zalimlerden biridir." dediler.'),
(2543, 21, 60, '(Bazıları) "İbrahim denen bir gencin, onları diline doladığını duymuştuk" dediler.'),
(2544, 21, 61, '"O halde onu insanların gözleri önüne getirin, olur ki (aleyhinde) şahidlik ederler" dediler.'),
(2545, 21, 62, '(İbrahim gelince ona) "Ey İbrahim! bunu tanrılarımıza sen mi yaptın?" dediler'),
(2546, 21, 63, 'İbrahim: "Belki onu şu büyükleri yapmıştır, konuşabiliyorlarsa onlara sorun" dedi.'),
(2547, 21, 64, 'Bunun üzerine vicdanlarına dönüp (kendi kendilerine) dediler ki: "Doğrusu siz haksızsınız."'),
(2548, 21, 65, 'Sonra yine (eski) kafalarına döndüler: "And olsun ki (ey İbrahim!) bunların konuşmayacağını (sen de) bilirsin." dediler.'),
(2549, 21, 66, '(İbrahim) dedi: "O halde, Allah\'ı bırakıp da size hiçbir fayda ve zarar veremeyecek olan putlara mı tapıyorsunuz?"'),
(2550, 21, 67, '"Size de, Allah\'ı bırakıp taptıklarınıza da yazıklar olsun, siz hâlâ akıllanmayacak mısınız?"'),
(2551, 21, 68, 'Onlar: "Bir şey yapacaksanız, şunu yakın da tanrılarınıza yardım edin" dediler.'),
(2552, 21, 69, 'Biz: "Ey ateş! İbrahim\'e karşı serin ve zararsız ol" dedik.'),
(2553, 21, 70, 'Ona düzen kurmak istediler, fakat biz kendilerini daha fazla hüsrana uğrattık.'),
(2554, 21, 71, 'Onu da, Lût\'u da, âlemler için bereketli ve kutsal kıldığımız yere ulaştırıp kurtardık.'),
(2555, 21, 72, 'Ona (İbrahim\'e) İshak\'ı, üstelik bir de Yakub\'u ihsan ettik ve herbirini salih kimseler kıldık.'),
(2556, 21, 73, 'Onları buyruğumuz altında (insanlara) doğru yolu gösterecek önderler kıldık. Kendilerine hayırlı işler yapmayı, namaz kılmayı, zekat vermeyi vahyettik. Onlar bize kulluk eden kimselerdir.'),
(2557, 21, 74, 'Biz Lût\'a da bir hüküm, bir ilim verdik. Onu çirkin işler işleyen kasabadan kurtardık. Doğrusu onlar kötü, fasık bir kavimdi.'),
(2558, 21, 75, 'Onu ise rahmetimizin içine aldık. Çünkü o salihlerdendi.'),
(2559, 21, 76, 'Nuh da daha önceleri bize yalvarmıştı; biz de onun duasını kabul ettik, kendisini ve ailesini büyük sıkıntıdan kurtardık.'),
(2560, 21, 77, 'Âyetlerimizi yalanlayan kavminden onun öcünü aldık. Şüphesiz onlar kötü bir kavimdiler. Biz de hepsini (suda) boğduk.'),
(2561, 21, 78, 'Davud ve Süleyman\'ı da (hatırla). Hani onlar ekin hakkında hüküm veriyorlardı. Hani milletin koyunları (geceleyin) içinde yayılmıştı, biz onların hükmüne şahittik.'),
(2562, 21, 79, 'Biz onu(n hükmünü) hemen Süleyman\'a bildirmiştik; (zaten) herbirine hüküm ve ilim vermiştik. Davud\'la beraber tesbih etsinler diye, dağları ve kuşları buyruk altına aldık. (Bütün bunları) yapan bizdik.'),
(2563, 21, 80, 'Ona, sizi savaşta korumak için zırh yapma sanatını öğrettik, artık şükreder misiniz?'),
(2564, 21, 81, 'Bereketli kıldığımız yere doğru, Süleyman\'ın emriyle yürüyen şiddetli rüzgarı, onun buyruğuna verdik. Biz her şeyi biliyorduk.'),
(2565, 21, 82, 'Onun için dalgıçlık yapan ve bundan başka işler de gören şeytanlardan da onun buyruğu altına verdik. Onların hepsini biz gözetiyorduk.'),
(2566, 21, 83, 'Eyyûb da: "Başıma bir bela geldi, (sana sığındım), sen merhametlilerin en merhametlisisin" diye Rabbine nida etti.'),
(2567, 21, 84, 'Biz de onun duasını kabul ettik de başına gelenleri kaldırdık. Katımızdan bir rahmet ve kulluk edenlere bir hatıra olmak üzere, ona tekrar ailesini ve kaybettikleriyle bir mislini daha verdik.'),
(2568, 21, 85, 'İsmail, İdris ve Zülkifl\'i de (hatırla). Onların hepsi de sabredenlerdendi.'),
(2569, 21, 86, 'Onları da rahmetimizin içine aldık. Onlar gerçekten salih olanlardandı.'),
(2570, 21, 87, 'Zünnun\'u (balık sahibi Yunus\'u) da hatırla. Hani o, öfkelenerek gitmişti de, bizim kendisini hiçbir zaman sıkıştırmayacağımızı sanmıştı. Fakat sonunda karanlıklar içinde: "Senden başka ilâh yoktur, sen münezzehsin, Şüphesiz ben haksızlık edenlerden oldum" diye seslenmişti.'),
(2571, 21, 88, 'Biz de duasını kabul ile icabet ettik, kendisini üzüntüden kurtardık. İşte biz iman edenleri böyle kurtarırız.'),
(2572, 21, 89, 'Zekeriya da hani Rabbine: "Rabbim! Beni tek başıma bırakma, sen varislerin en hayırlısısın" diye nida etmişti.'),
(2573, 21, 90, 'Biz de duasını kabul ile icabet ettik de kendisine Yahya\'yı ihsan ettik. Ve eşini (doğum yapmaya) elverişli hale getirdik. Doğrusu onlar iyiliklerde yarışıyorlar, umarak ve korkarak bize yalvarıyorlardı. Bize karşı derin saygı duyuyorlardı.'),
(2574, 21, 91, 'Irzını koruyan Meryem\'e ruhumuzdan üflemiş, onu ve oğlunu, âlemler için bir mucize kılmıştık.'),
(2575, 21, 92, 'Doğrusu bu sizin ümmetiniz (tevhid dini olan müslümanlık), bir tek ümmettir (bir tek din olarak sizin dininizdir). Ben de sizin Rabbinizim. O halde bana kulluk edin.'),
(2576, 21, 93, 'Ama insanlar din konusunda aralarında bölüklere ayrıldılar ama, hepsi bize döneceklerdir.'),
(2577, 21, 94, 'İnanmış olarak yararlı iş işleyenin emeği inkâr edilmeyecektir. Biz şüphesiz onu yazmaktayız.'),
(2578, 21, 95, 'Yok ettiğimiz bir memleket (ahalisinin ahiretteki cezasını da çekmek üzere) bize dönmemesi gerçekten imkansızdır.'),
(2579, 21, 96, 'Nihayet Ye\'cûc ve Me\'cûc(un seddi) açıldığı zaman, ki onlar her dere ve tepeden akın edip çıkarlar.'),
(2580, 21, 97, 'Ve gerçek vaad yaklaştığında, işte o zaman kâfir olanların gözleri beleriverir. "Eyvah bizlere! Doğrusu biz bundan gaflet içindeydik, hayır biz zalim kimselerdik." derler.'),
(2581, 21, 98, 'Siz ve Allah\'dan başka taptıklarınız, cehennemin yakıtısınız; oraya gireceksiniz.'),
(2582, 21, 99, 'Eğer onlar ilâh olsalardı, oraya girmeyeceklerdi. Hepsi orada temelli kalacaktır.'),
(2583, 21, 100, 'Orada onların bir inlemeleri vardır. Bunlar orada (sağır olup) bir şey de işitemezler.'),
(2584, 21, 101, 'Şüphesiz katımızdan kendileri için güzel şeyler takdir edilmiş olanlar, işte oradan (cehennemden) uzak tutulanlardır.'),
(2585, 21, 102, 'Bunlar onun (cehennemin) uğultusunu bile duymazlar. Canlarının istediği şeyler içinde temelli kalırlar.'),
(2586, 21, 103, 'O en büyük korku bunları üzmez; kendilerini melekler: "Size söz verilen gün işte bugündür" diye karşılarlar.'),
(2587, 21, 104, 'Göğü, kitab dürer gibi dürdüğümüz zaman, yaratmaya ilk başladığımız gibi, katımızdan verilmiş bir söz olarak onu tekrar var edeceğiz. Doğrusu biz bunları yaparız.'),
(2588, 21, 105, 'And olsun ki, Tevrat\'tan sonra Zebûr\'da da yeryüzüne ancak iyi kullarımın mirasçı olduğunu yazmıştık.'),
(2589, 21, 106, 'Şüphesiz bu Kur\'ân\'da kulluk eden kimseler için kâfi bir öğüt vardır.'),
(2590, 21, 107, '(Ey Muhammed!) biz seni ancak âlemlere rahmet olarak gönderdik.'),
(2591, 21, 108, 'De ki, bana ancak şöyle vahyolunuyor: "İlâhınız ancak tek bir ilâhtır. Şimdi siz artık müslüman oluyor musunuz?"'),
(2592, 21, 109, 'Eğer (yine de) yüz çevirirlerse, de ki: "Size düpedüz açıkladım; tehdit olunduğunuz şeyin yakın mı, uzak mı olduğunu bilmem."'),
(2593, 21, 110, 'Şüphesiz Allah açığa vurulan sözü de bilir, gizlediklerinizi de bilir.'),
(2594, 21, 111, 'Bilmem belki bu gecikme sizi denemek ve bir süreye kadar geçindirmek içindir.'),
(2595, 21, 112, '(Hz. Peygamber şöyle) dedi: "Ey Rabbim! Aramızda gerçekle hükmet ve Rabbimiz O Rahmân\'dır ki, isnad ettiğiniz (yalan) vasıflarınıza karşı yardımına sığınılacak olan ancak O\'dur. "');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Ey İnsanlar! Rabbinizden sakının; şüphesiz o kıyamet gününün sarsıntısı çok büyük bir şeydir.'),
(2597, 22, 2, 'Onu göreceğiniz gün, her emzikli kadın emzirdiğinden geçer. Ve her hamile kadın çocuğunu düşürür. İnsanları hep sarhoş görürsün, halbuki sarhoş değillerdir. Fakat Allah\'ın azabı çok şiddetlidir.'),
(2598, 22, 3, 'İnsanlardan bazıları Allah hakkında bir bilgisi olmadığı halde tartışır da her azılı şeytanın ardına düşer.'),
(2599, 22, 4, '(O şeytanki) hakkında şöyle hüküm verilmiştir: Şüphesiz kim onu dost edinirse, o muhakkak onu saptırır ve doğruca cehennem azabına götürür.'),
(2600, 22, 5, 'Ey insanlar! Eğer öldükten sonra dirilmekten şüphede iseniz, (bilin ki) ne olduğunuzu size açıklamak için şüphesiz biz sizi topraktan, sonra nutfeden (spermadan) sonra bir alekadan (embriodan) sonra yapısı belli belirsiz bir et parçasından yaratmışızdır. Dilediğimizi belli bir süreye kadar rahimlerde tutarız. Sonra sizi bir çocuk olarak çıkartırız, sonra sizi, olgunluk çağına erişmeniz için bırakırız. Bununla beraber kiminiz öldürülür, kiminiz de önceki bilgisinden sonra, hiçbir şey bilmemek üzere, ömrünün en fena zamanına ulaştırılır. Bir de yeryüzünü görürsün ki kupkurudur; fakat biz onun üzerine su indirdiğimiz zaman, harekete geçer, kabarır ve her güzel çiftten bitkiler bitirir.'),
(2601, 22, 6, 'İşte bunlar gösteriyor ki, Allah şüphesiz haktır. Şüphesiz ölüleri o diriltir ve o her şeye kadirdir.'),
(2602, 22, 7, 'Kıyamet ise şüphesiz gelecek ve muhakkak ki Allah bütün kabirlerde olan kimseleri tekrar diriltecektir.'),
(2603, 22, 8, 'İnsanlardan kimi de vardır ki ne bir bilgiye, ne bir delile, ne de aydınlatıcı bir kitaba dayanmaksızın Allah hakkında tartışır.'),
(2604, 22, 9, 'Allah yolundan şaşırtmak (saptırmak) için büyüklük taslayarak (tartışır). Dünyada ona bir rezillik vardır. Kıyamet gününde ise ona cehennem azabını tattıracağız'),
(2605, 22, 10, 'Ona "Bunlar, senin ellerinle kazandığın günahlar sebebiyledir" denir. Şüphesiz Allah kullarına zulmeden değildir.'),
(2606, 22, 11, 'İnsanlardan kimi de Allah\'a bir yar kenarındaymış gibi ibadet eder, eğer kendisine bir iyilik gelirse ona gönlü yatışır ve eğer başına bir bela gelirse yüzüstü dönüverir. Dünyayı da ahireti de kaybeder. İşte apaçık kayıp budur.'),
(2607, 22, 12, 'Allah\'ı bırakır da kendine ne zarar, ne menfaat veremeyecek şeylere yalvarır. İşte derin sapıklık budur.'),
(2608, 22, 13, 'Herhalde o, zararı faydasından daha yakın olana yalvarıyor. Yalvardığı şey ne kötü yardımcı ve ne kötü yoldaştır.'),
(2609, 22, 14, 'Şüphe yok ki Allah, iman edip salih amelleri işleyenleri altından ırmaklar akan cennetlere koyacak. Şüphesiz Allah dilediğini yapar.'),
(2610, 22, 15, 'Allah\'ın ona (peygambere) dünyada ve ahirette yardım etmeyeceğini sanan kimse hemen yukarıya bir ip uzatsın, sonra (kendini intihar edip) boğsun da baksın bu hilesi kendisini öfkelendiren şeyi giderecek mi?'),
(2611, 22, 16, 'İşte biz onu (Kur\'ân\'ı) böylece, apaçık âyetler olarak indirdik. Şüphesiz Allah dilediğini doğru yola eriştirir.'),
(2612, 22, 17, 'Şüphesiz o iman edenler, yahudi olanlar, sabiîler (yıldıza tapanlar), hıristiyanlar, ateşe tapanlar ve (Allah\'a) eş koşanlar (yok mu?) Allah, kıyamet günü bunların arasını şüphesiz ayıracaktır. Çünkü Allah her şeyi hakkıyla görüp bilendir.'),
(2613, 22, 18, 'Görmedin mi, göklerdeki kimseler, yerdeki kimseler, güneş, ay ve yıldızlar, dağlar, ağaçlar, bütün hayvanlar ve insanlardan birçoğu hep Allah\'a secde ediyor. Birçoğunun üzerine de azab hak olmuştur. Allah kimi hor ve hakir kılarsa artık ona ikram edecek yoktur. Şüphesiz Allah dilediği şeyi yapar.'),
(2614, 22, 19, 'Şu ikisi Rableri hakkında tartışmaya girmiş iki hasımdır. O\'nu inkar edenler için ateşten elbiseleri biçilmiştir. Başlarının üstünden kaynar su dökülür.'),
(2615, 22, 20, 'Bununla karınlarındaki ve derileri eritilir.'),
(2616, 22, 21, 'Bir de bunlara demirden kamçılar vardır.'),
(2617, 22, 22, 'Uğradıkları gamdan (dolayı) oradan ne zaman çıkmak isteseler, her defasında oraya geri çevrilirler: "Yakıcı azabı tadın" denir.'),
(2618, 22, 23, 'Şüphesiz Allah iman edip yararlı iş işleyenleri, altından ırmaklar akan cennetlere koyacak, orada altın bilezikler ve inciler takınacaklar. Oradaki elbiseleri de ipektendir.'),
(2619, 22, 24, 'Hem sözün güzelini işitecek duruma ulaştırılmışlar, hem de övülmeye layık (olan Allah\'ın) yoluna eriştirilmişlerdir.'),
(2620, 22, 25, 'Şüphesiz inkâr edenlere, Allah\'ın yolundan, yerli ve yolcu bütün insanlar için eşit kılınan Mescidi Haram\'dan alıkoyanlara ve orada zulümle yanlış yola saptırmak isteyene can yakıcı bir azab tattırırız.'),
(2621, 22, 26, 'Bir zamanlar Kâbe\'nin yerini İbrahim\'e şu şekilde hazırlamıştık: Sakın bana hiçbir şeyi ortak koşma; tavaf edenler, orada (kıyama) duranlar, ruku edenler ve secdeye varanlar için evimi tertemiz et.'),
(2622, 22, 27, 'İnsanları hacca çağır; yürüyerek veya incelmiş binekler üstünde (uzak yollardan) her derin vadiyi aşarak sana gelsinler.'),
(2623, 22, 28, 'Ta ki kendilerine ait birtakım menfaatlere şahid olsunlar; Allah\'ın kendilerine rızık olarak verdiği hayvanları belli günlerde kurban ederken O\'nun adını ansınlar. Siz de onlardan yiyin, yoksulu, fakiri de doyurun.'),
(2624, 22, 29, 'Sonra kirlerini giderip temizlensinler. Adaklarını yerine getirsinler. Kâbeyi tavaf etsinler.'),
(2625, 22, 30, 'Emir budur, Allah\'ın yasaklarına kim saygı gösterirse, bu, kendisi için Rabbinin katında şüphesiz hayırdır. Size bildirilegelenden başka bütün hayvanlar helal kılınmıştır. O halde o pis putlardan kaçının ve yalan sözden sakının.'),
(2626, 22, 31, 'Allah için, O\'na eş koşmayan, O\'nun birliğine inanmış kimseler olun. Allah\'a ortak koşan kimse, gökten düşüp de kuşların kaptığı veya rüzgarın bir uçuruma sürüklediği şeye benzer.'),
(2627, 22, 32, 'Bu böyledir; kim Allah\'ın nişanelerine, kurbanlıklarına saygı gösterirse, şüphesiz o kalblerin takvasındandır.'),
(2628, 22, 33, 'Sizin için onlarda belli bir süreye kadar bir takım faydalar vardır. Sonra bunlar Beyti atik (kâbe) de son bulurlar.'),
(2629, 22, 34, 'Her ümmet için Allah\'ın kendilerine rızık olarak verdiği kurbanlık hayvanların üzerine O\'nun adını ansınlar diye bir mabed yapmışızdır. Hepinizin ilâhı bir tek ilâhtır. Onun için yalnız O\'na teslim olan müslümanlar olun. (Ey Muhammed!) Allah\'a itaat eden alçak gönüllüleri müjdele.'),
(2630, 22, 35, 'Ki Allah anıldığı vakit onların kalpleri titrer. Onlar başlarına gelene sabreden, namaz kılan kimselerdir. Kendilerine verdiğimiz rızıktan Allah yolunda harcarlar.'),
(2631, 22, 36, 'Kurbanlık deve ve sığırları Allah\'ın size olan nişanelerinden kıldık. Sizin için onlarda hayır vardır. Ön ayaklarının biri bağlı halde keserken üzerlerine Allah\'ın adını anın. Yanları yere yaslandığı vakit de onlardan yiyin, kanaat edip istemeyene de, isteyene de yedirin. Böylece onları sizin buyruğunuza verdik ki, şükredesiniz.'),
(2632, 22, 37, 'Elbette onların etleri ve kanları Allah\'a ulaşmayacaktır. Ancak O\'na sizin takvanız erecektir. Onları bu şekilde sizin buyruğunuza verdi ki, size yolunu gösterdiğinden dolayı, Allah\'ı tekbir ile yüceltesiniz. (Ey Muhammed!) Vazifelerini güzelce yapan iyilik sevenleri müjdele.'),
(2633, 22, 38, 'Şüphesiz Allah inananları savunur. Çünkü Allah hâin ve nankörlerin hiçbirini sevmez.'),
(2634, 22, 39, 'Kendilerine savaş açılan kimselere (kâfirlere karşı koymak için) izin verildi. Çünkü onlar zulme uğradılar. Şüphesiz Allah onları zafere ulaştırmaya kadirdir.'),
(2635, 22, 40, 'Onlar "Rabbimiz Allah\'tır" demelerinden başka bir sebep olmaksızın haksız yere yurtlarından çıkarıldılar. Eğer Allah insanların bir kısmını bir kısmı ile defetmeseydi manastırlar, kiliseler, havralar ve içinde Allah\'ın adı çok anılan mescidler elbette yıkılırdı. Şüphesiz Allah kendi (dini) ne yardım edene yardım edecektir. Şüphesiz Allah çok güçlüdür, çok izetlidir (her şeye galiptir).'),
(2636, 22, 41, 'Onlar (o müminlerdir) ki, eğer kendilerini yeryüzünde iktidar mevkiine getirirsek namazı kılarlar, zekatı verirler, iyiliği emrederler ve fenalığı yasak ederler. Bütün işlerin sonu sırf Allah\'a âittir.'),
(2637, 22, 42, '(Ey Muhammed!) Eğer seni (müşrikler) yalanlıyorlarsa bil ki onlardan önce Nûh kavmi, Âd ve Semûd (kavimleri de kendi peygamberlerini) yalancı saydılar.'),
(2638, 22, 43, 'İbrahim\'in kavmi de, Lût\'un kavmi de (peygamberlerini) yalancı saydılar.'),
(2639, 22, 44, '(Şuayb\'ın kavmi olan) Medyen halkı da (Şûayb\'ı) yalanladı. Musa da (Firavun tarafından) yalanlandı. Ben de o kâfirlere bir süre verdim. Sonra da onları yakalayıverdim. Beni tanımamak nasılmış görsünler.'),
(2640, 22, 45, 'Nice memleketler vardı ki, zulüm yaparlarken biz onları yok ettik. Artık damları çökmüş, duvarları üzerine yıkılmıştır. (Geride) Nice terkedilmiş kuyularla bomboş kalmış yüksek saraylar (bırakılmıştır.)'),
(2641, 22, 46, 'Yeryüzünde dolaşmıyorlar mı ki olanları akledecek kalbleri, işitecek kulakları olsun. Gerçek şudur ki, gözler kör olmaz, fakat asıl göğüslerin içindeki kalpler kör olur.'),
(2642, 22, 47, 'Bir de senden acele azab istiyorlar. Elbette Allah sözünden caymaz. Bununla beraber Rabbinin katında birgün, sizin sayacaklarınızdan bin sene gibidir.'),
(2643, 22, 48, 'Zulmedip dururlarken kendilerine mühlet verdiğim nice memleket halkı vardı ki, sonunda onları yakalayıvermiştim. Dönüş ancak banadır.'),
(2644, 22, 49, '(Habîbim!) De ki: "Ey insanlar! Ben size ancak apaçık anlatan bir uyarıcıyım."'),
(2645, 22, 50, 'İşte iman edip salih amel işleyenler için hem bir mağfiret, hem de (cennette) tükenmez bir rızık vardır.'),
(2646, 22, 51, 'Âyetlerimizi tartışarak bozmaya uğraşanlara gelince, işte onlar cehennemliktirler. Böyle de ve temennilere uyma. Çünkü:'),
(2647, 22, 52, '(Ey Muhammed!) Biz senden önce hiçbir elçi ve hiçbir peygamber göndermedik ki o bir şey temenni ettiği zaman, şeytan onun arzusuna şüpheler karıştırmasın. Bunun üzerine Allah şeytanın karıştırdığı şüpheyi giderir. Sonra da Allah, âyetlerini tahkim eder (güçlendirir). Allah Alîm\'dir (herşeyi bilir), Hakîmdir (Hikmet sahibidir)'),
(2648, 22, 53, 'Allah, şeytanın karıştırdığını, kalblerinde hastalık bulunan ve kalpleri kaskatı olan kimseleri sınamaya vesile kılar. Zalimler şüphesiz (haktan uzak) derin bir ayrılık içindedirler.'),
(2649, 22, 54, 'Bir de kendilerine ilim verilmiş olanlar, Kur\'ân\'ın şüphesiz Rabbinden gelen bir gerçek olduğunu bilsinler ve ona iman etsinler de kalpleri ona saygı duysun. Çünkü Allah, iman edenleri doğru yola eriştirir.'),
(2650, 22, 55, 'İnkâr edenler de, kendilerine ansızın kıyamet gelinceye veya akîm (kısır) bir günün azabı gelinceye kadar, Kur\'ân\'dan şüphe etmekte devam edip giderler.'),
(2651, 22, 56, 'O gün hükümranlık yalnız Allah\'ındır, O aralarında hükmünü verir. Artık iman edip yararlı iş işleyenler nimet cennetlerindedirler.'),
(2652, 22, 57, 'İnkâr edip âyetlerimizi yalan sayanlar ise, işte bunlar için hakîr düşüren bir azab vardır.'),
(2653, 22, 58, 'Allah yolunda hicret edip de sonra öldürülmüş veya ölmüş olanlara gelince, elbette Allah, onları güzel bir rızıkla rızıklandıracaktır. Çünkü Allah rızık verenlerin en hayırlısıdır.'),
(2654, 22, 59, 'Allah onları hoşnud olacakları bir yere (cennete) elbette koyacaktır. Şüphesiz Allah Alîmdir (herşeyi bilir) Halîmdir, (Kullarına yumuşak davranır.).'),
(2655, 22, 60, 'Bu böyledir, kim kendisine yapılan cezaya aynı ile karşılık verir de, sonra yine kendisine zulüm yapılırsa, muhakkak ki, Allah ona yardım eder. Allah şüphesiz çok af edicidir, çok bağışlayıcıdır.'),
(2656, 22, 61, 'Çünkü Allah, geceyi gündüzün içine sokar, gündüzü de gecenin içine sokar. Şüphesiz Allah, Semîdir (herşeyi işitir) Basîrdir (herşeyi görür).'),
(2657, 22, 62, '(Bu sonsuz güç şundandır) Çünkü Allah, varlığı kendinden olan Hak\'tır. Müşriklerin O\'nu bırakıp da tapındıkları putlar ise hep bâtıldır. Şüphesiz Allah, yücedir, büyüktür.'),
(2658, 22, 63, 'Görmedin mi Allah\'ın gökten indirdiği su ile yeryüzü (nasıl) yemyeşil oluyor? Gerçekten Allah çok lütufkârdır, her şeyden haberdardır.'),
(2659, 22, 64, 'Göklerde ve yerde ne varsa hep O\'nundur. Doğrusu Allah müstağnîdir, övülmeğe layıktır.'),
(2660, 22, 65, 'Görmedin mi ki, Allah bütün yerdekileri ve emriyle denizlerde akıp giden gemileri hep sizin buyruğunuz altına verdi. Göğü de izni olmaksızın yere düşmekten o (koruyup havada) tutuyor. Şüphesiz Allah insanlara çok şefkatlidir, çok merhametlidir.'),
(2661, 22, 66, 'Size (ilk defa) hayat veren, sonra öldürecek olan, sonra da yeniden diriltecek olan O\'dur. İnsan gerçekten pek nankördür.'),
(2662, 22, 67, 'Biz her ümmet için bir şeriat tayin ettik ki, onlar onunla amel ederler. Bunun için (ey Muhammed!) bu konuda seninle hiçbir zaman çekişmesinler. (İnsanları) Rabbine (ibadet etmeye) çağır. Şüphesiz sen gerçekten hidayete götüren doğru bir yol üzerindesin.'),
(2663, 22, 68, 'Eğer seninle tartışırlarsa, de ki: "Allah yaptıklarınızı çok iyi bilir."'),
(2664, 22, 69, 'Ayrılığa düştüğünüz şeyler hakkında kıyamet günü Allah aranızda hükmünü verecektir.'),
(2665, 22, 70, 'Bilmez misin ki, Allah, gökte ve yerde ne varsa hepsini bilir. Şüphesiz bunlar bir kitabtadır. Hiç şüphe yok ki bunlar Allah\'a pek kolaydır.'),
(2666, 22, 71, 'Onlar Allah\'ı bırakıp da O\'nun, haklarında hiçbir delil indirmediği ve kendilerinde de bir bilgi bulunmayan şeylere taparlar. Zalimler için hiçbir yardımcı yoktur.'),
(2667, 22, 72, 'Âyetlerimiz kendilerine apaçık olarak okunduğu zaman, o kâfirlerin yüzlerinden inkârlarını anlarsın. Neredeyse, kendilerine âyetlerimizi okuyanlara saldıracaklar. De ki: "Şimdi size ondan daha kötü olanını haber vereyim mi? O, ateştir. Allah bunu kâfir olanlara vaad buyurdu. O ne kötü bir dönüş yeridir."'),
(2668, 22, 73, 'Ey insanlar! Bir misal verilmektedir, şimdi ona iyi kulak verin: Sizin Allah\'ı bırakıp taptıklarınız bir araya gelseler, bir sinek bile yaratamayacaklardır. Sinek onlardan bir şey kapsa onu kurtaramazlar. İsteyen de, istenen de âcizdir.'),
(2669, 22, 74, 'Allah\'ın büyüklüğünü gereği gibi değerlendirip bilemediler. Şüphesiz ki Allah çok kuvvetlidir, her şeye üstündür.'),
(2670, 22, 75, 'Allah hem meleklerden, hem de insanlardan elçiler seçer. Şüphesiz Allah her şeyi işitir, her şeyi görür.'),
(2671, 22, 76, 'O geçmişlerini ve geleceklerini bilir. Bütün işler Allah\'a döndürülür.'),
(2672, 22, 77, 'Ey iman edenler! rükû edin, secdeye varın, Rabbinize kulluk edin, iyilik yapın ki kurtulabilesiniz.'),
(2673, 22, 78, 'Allah uğrunda gerektiği gibi cihad edin. Sizi o seçmiş, babanız İbrahim\'in yolu olan dinde sizin için bir zorluk kılmamıştır. Daha önce ve Kur\'ân\'da, Peygamberin size şahid olması, sizin de insanlara şahid olmanız için, size müslüman adını veren O\'dur. Artık namaz kılın, zekat verin, Allah\'a sarılın. O sizin sahibinizdir. O ne güzel sahip ve ne güzel yardımcıdır!');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'Gerçekten müminler kurtuluşa ermiştir,'),
(2675, 23, 2, 'Onlar ki, namazlarında huşû içindedirler,'),
(2676, 23, 3, 'Onlar ki, boş ve yararsız şeylerden yüz çevirirler,'),
(2677, 23, 4, 'Onlar ki, zekat (vazifelerini) yerine getirirler,'),
(2678, 23, 5, 'Ve onlar ki, iffetlerini korurlar,'),
(2679, 23, 6, 'Ancak eşleri ve ellerinin sahip olduğu (cariyeleri) hariç. (Bunlarla ilişkilerinden dolayı) kınanmış değillerdir.'),
(2680, 23, 7, 'Şu halde, kim bunun ötesine gitmeyi isterse, işte bunlar, haddi aşan kimselerdir.'),
(2681, 23, 8, 'Yine onlar ki, emanetlerine ve ahidlerine riayet ederler,'),
(2682, 23, 9, 'Ve onlar ki, namazlarını muhafaza ederler,'),
(2683, 23, 10, 'İşte asıl onlar varislerdir.'),
(2684, 23, 11, 'Ki, Firdevs\'e varis olan bu kimseler orada ebedî kalırlar.'),
(2685, 23, 12, 'And olsun biz insanı, çamurdan, bir sülâleden (süzülüp çıkarılmış çamurdan) yarattık.'),
(2686, 23, 13, 'Sonra onu emin ve sağlam bir karargahta (rahimde) nutfe (sperma) haline getirdik.'),
(2687, 23, 14, 'Sonra nutfeyi bir alaka (embrio) yarattık, derken o alakayı bir mudga (bir çiğnem et parçası halinde) yarattık, derken o mudgayı bir takım kemik yarattık, derken o kemiklere bir et giydirdik, sonra onu diğer bir yaratık olarak teşekkül ettirdik. Yapıp yaratanların en güzeli olan Allah, pek yücedir.'),
(2688, 23, 15, 'Sonra siz bunun ardından, muhakkak ki öleceksiniz.'),
(2689, 23, 16, 'Sonra da siz, şüphesiz, kıyamet gününde tekrar diriltileceksiniz.'),
(2690, 23, 17, 'Andolsun biz, sizin üstünüzde yedi yol yarattık. Biz, yaratmaktan habersiz değiliz.'),
(2691, 23, 18, 'Gökten uygun bir ölçüde yağmur indirip onu yerde durgunlaştırdık. Bizim onu gidermeye de elbet gücümüz yeter.'),
(2692, 23, 19, 'Böylece onun (yağmurun) sayesinde sizin yararınıza hurma bahçeleri ve üzüm bağları meydana getirdik ki, bunlarda sizin için bir çok meyveler vardır ve siz onlardan yersiniz.'),
(2693, 23, 20, 'Tûrı Sinâ\'da (dahi) yetişen bir ağaç da meydana getirdik ki, bu ağaç, hem yağ, hem de yiyenlerin ekmeğine katık edecekleri (zeytin) verir.'),
(2694, 23, 21, 'Hayvanlarda da sizin için elbette ibretler vardır. Onların karınlarındakilerden size içiririz. Onlarda sizin için birtakım faydalar daha vardır; ayrıca etlerini yersiniz.'),
(2695, 23, 22, 'Hem onlara ve hem gemiye yüklenirsiniz.'),
(2696, 23, 23, 'And olsun biz, Nûh\'u kavmine gönderdik. "Ey kavmim dedi, Allah\'a kulluk edin. O\'ndan başka tanrınız yoktur. Hâlâ sakınmaz mısınız?"'),
(2697, 23, 24, 'Bunun üzerine, kavminin içinden kâfir kodaman topluluğu "Bu, dediler, tıpkı sizin gibi bir beşer olmaktan başka bir şey değildir. Size üstün ve hakim olmak istiyor. Eğer Allah (peygamber göndermek) isteseydi, muhakkak ki bir melek gönderirdi. Biz geçmişteki atalarımızdan böyle bir şey duymadık."'),
(2698, 23, 25, '"Bu, yalnızca kendisinde delilik bulunan bir kimsedir. Öyle ise, bir süreye kadar ona katlanıp (durumu) gözetleyin bakalım."'),
(2699, 23, 26, 'Nuh: "Rabbim! dedi, beni yalana çıkarmalarına karşı bana yardım et!"'),
(2700, 23, 27, 'Bunun üzerine ona şöyle vahyettik: Bizim nezaretimiz altında ve vahyimizle gemiyi yap. Bizim emrimiz gelip de tandır kaynayınca, her cinsten eşler halinde iki tane ve bir de içlerinden, daha önce kendisi aleyhinde hüküm verilmiş olanların dışındaki aileni gemiye al. Zulmetmiş olanlar konusunda bana hiç yalvarma! Zira onlar kesinlikle boğulacaklardır!'),
(2701, 23, 28, 'Sen, yanındakilerle beraber gemiye yerleştiğinde: "Bizi zalimler topluluğundan kurtaran Allah\'a hamdolsun" de.'),
(2702, 23, 29, 'Ve de ki: "Rabbim! Beni mübarek bir yere indir. Sen, konuklatanların en hayırlısısın."'),
(2703, 23, 30, 'Şüphesiz bunda sizin için birtakım ibretler vardır. Çünkü biz, kullarımızı böyle denemişizdir.'),
(2704, 23, 31, 'Sonra onların ardından bir başka nesil getirdik.'),
(2705, 23, 32, 'Bunun üzerine, onlar arasından kendilerine, "Allah\'a kulluk edin; çünkü sizin O\'ndan başka bir tanrınız yoktur. Hâlâ Allah\'tan korkmaz mısınız? (mesajını ileten) bir resul gönderdik.'),
(2706, 23, 33, 'Onun kavminden, kâfir olup ahirete ulaşmayı yalanlayan ve dünya hayatında kendilerine refah verdiğimiz kodaman güruh dedi ki: "Bu dediler, sadece sizin gibi bir insandır; sizin yediğinizden yer, sizin içtiğinizden içer."'),
(2707, 23, 34, '"Gerçekten, tıpkı kendiniz gibi bir beşere itaat ederseniz herhalde ziyan edersiniz."'),
(2708, 23, 35, '"Size, öldüğünüz, toprak ve kemik yığını haline geldiğinizde, mutlak surette sizin (tekrar) meydana çıkarılacağınızı mı vaad ediyor?"'),
(2709, 23, 36, '"Heyhât o size vaad edilen şey ne kadar uzak!"'),
(2710, 23, 37, '"Dünya hayatından başka gerçek yoktur. (Kimimiz) ölürüz, (kimimiz) yaşarız; bir daha diriltilecek değiliz."'),
(2711, 23, 38, '"Bu adam, sadece Allah hakkında yalan uyduran bir kimsedir; biz ona inanmıyoruz."'),
(2712, 23, 39, 'O Peygamber: "Rabbim, dedi, beni yalanlamalarına karşı bana yardımcı ol!"'),
(2713, 23, 40, 'Allah şöyle buyurdu: "Pek yakında onlar pişman olacaklar!"'),
(2714, 23, 41, 'Nitekim, Hak tarafından korkuç bir ses yakalayıverdi onları! Kendilerini hemen çepeçevre kuşattık. Zalimler topluluğunun canı cehenneme!'),
(2715, 23, 42, 'Sonra onların ardından bir başka nesil getirdik.'),
(2716, 23, 43, 'Hiçbir ümmet, ecelini ne öne alabilir, ne de erteleyebilir.'),
(2717, 23, 44, 'Sonra biz peyderpey peygamberlerimizi gönderdik. Herhangi bir ümmete peygamberlerinin geldiği her defasında, onlar bu peygamberi yalanladılar; biz de onları birbiri ardından (yokluğa) yuvarladık ve onları efsâne yaptık. Artık iman etmeyen kavmin canı cehenneme!'),
(2718, 23, 45, 'Sonra birtakım âyetlerimiz ve açık bir ferman ile Musa\'yı ve kardeşi Harun\'u gönderdik.'),
(2719, 23, 46, 'Firavun\'a ve ileri gelenlerine de (gönderdik). Bunun üzerine onlar kibire kapıldılar ve ululuk taslayan zorba bir kavim oldular.'),
(2720, 23, 47, 'Onun için: Biz, dediler, "kavimleri bize kölelik ederken bizim benzerimiz olan bu iki adama inanacak mıyız?"'),
(2721, 23, 48, 'Böylece onları yalanladılar, bu yüzden de helâk edilenlerden oldular.'),
(2722, 23, 49, 'Andolsun biz Musa\'ya belki onlar yola gelirler diye, o kitabı da verdik.'),
(2723, 23, 50, 'Meryemoğlunu ve annesini de (kudretimize) bir alâmet kıldık; onları, yerleşmeye elverişli, sulu bir tepeye yerleştirdik.'),
(2724, 23, 51, 'Ey peygamberler! Temiz ve helal olan şeylerden yiyin; güzel amel ve hareketlerde bulunun. Çünkü ben sizin yaptıklarınızı bilirim.'),
(2725, 23, 52, '"Ve işte bu sizin ümmetiniz bir tek ümmet ve ben de sizin Rabbinizim. Öyle ise benden sakının." (denildi).'),
(2726, 23, 53, 'Derken insanlar kendi aralarındaki işlerini parça parça böldüler. Her grup, kendinde bulunan ile sevinip böbürlendi.'),
(2727, 23, 54, 'Sen şimdi onları bir zamana kadar gaflet ve sapıklıkları ile başbaşa bırak!'),
(2728, 23, 55, 'Sanıyorlar mı ki, onlara verdiğimiz servet ve oğullar ile,'),
(2729, 23, 56, 'Kendilerine faydalar sağlamak için can atıyoruz. Hayır, onlar işin farkına varamıyorlar.'),
(2730, 23, 57, 'Rablerine olan saygıdan dolayı titreyenler,'),
(2731, 23, 58, 'Rablerinin âyetlerine inananlar,'),
(2732, 23, 59, 'Rablerine ortak tanımayanlar,'),
(2733, 23, 60, 'Ve, Rablerine dönecekleri için yapmakta oldukları işleri kalpleri titreyerek yapanlar;'),
(2734, 23, 61, 'İşte onlar, iyiliklere koşuşurlar ve iyilik için yarışırlar.'),
(2735, 23, 62, 'Biz hiç kimseyi, gücünün yettiğinden başkası ile yükümlü kılmayız. Nezdimizde hakkı söyleyen bir kitap vardır ve onlar haksızlığa uğratılmazlar.'),
(2736, 23, 63, 'Hayır, onların kalpleri bu hususta cehalet içindedir. Ayrıca onların bundan öte birtakım kötü işleri vardır ki, onlar bu işleri yapar dururlar.'),
(2737, 23, 64, 'Nihayet, refah ve bolluk içinde olanlarını sıkıntıya uğrattığımızda, bakarsın ki onlar feryadı basarlar.'),
(2738, 23, 65, 'Boşuna feryad etmeyin bugün! Zira bizden yardım göremeyeceksiniz.'),
(2739, 23, 66, 'Çünkü âyetlerimiz size okunurdu da, buna karşı siz arkanızı dönerdiniz.'),
(2740, 23, 67, 'Kafa tutardınız ve geceleyin hezeyanlar savururdunuz.'),
(2741, 23, 68, 'Onlar bu sözü (Kur\'ân\'ı) hiç düşünmediler mi? Yoksa kendilerine, daha önce geçmişteki atalarına gelmeyen bir şey mi geldi?'),
(2742, 23, 69, 'Yoksa peygamberlerini tanımadılar da bu yüzden mi onu inkâr ediyorlar?'),
(2743, 23, 70, 'Yoksa onda bir delilik olduğunu mu söylüyorlar? Aksine o, kendilerine hakkı getirmiştir. Halbuki onlar haktan hoşlanmamaktadırlar.'),
(2744, 23, 71, 'Eğer hak, onların kötü arzu ve isteklerine uysaydı, mutlaka gökler ve yer ile bunlarda bulunan kimseler bozulur giderdi. Hayır, biz onlara şan ve şereflerini getirdik; fakat onlar kendi şereflerine sırt çevirirler.'),
(2745, 23, 72, '(Resulüm!) Yoksa sen onlardan bir haraç mı istiyorsun? Rabbinin vergisi daha hayırlıdır. O, rızık verenlerin en hayırlısıdır.'),
(2746, 23, 73, 'Gerçek şu ki sen onları doğru bir yola çağırıyorsun.'),
(2747, 23, 74, 'Fakat ahirete inanmayanlar ise, ısrarla yoldan çıkmaktadırlar.'),
(2748, 23, 75, 'Eğer onlara acıyıp da için de bulundukları sıkıntıyı giderseydik, iyice körleşerek azgınlıklarında büsbütün direnirlerdi.'),
(2749, 23, 76, 'Andolsun, biz onları sıkıntıya düşürdük de yine Rablerine boyun eğmediler, tazarru\' ve niyazda da bulunmadılar.'),
(2750, 23, 77, 'Nihayet üzerlerine, azabı çok şiddetli bir kapı açtığımız zaman, bir de bakarsın ki onlar orada şaşkın ve ümitsiz kalmışlardır!'),
(2751, 23, 78, 'Halbuki sizin için o kulağı, o gözleri ve o gönülleri yaratan O\'dur. Ne de az şükrediyorsunuz!'),
(2752, 23, 79, 'Ve sizi yeryüzünde yaratıp türeden O\'dur. Sırf O\'nun huzuruna toplanacaksınız.'),
(2753, 23, 80, 'Ve O, yaşatan ve öldürendir; gecenin ve gündüzün değişmesi O\'nun eseridir. Hâlâ aklınızı kullanmaz mısınız?'),
(2754, 23, 81, 'Hayır, öncekilerin söylediklerinin benzerini söylediler.'),
(2755, 23, 82, 'Dediler ki: "Sahi biz, ölüp de bir toprak ve kemik yığını haline gelmişken, mutlaka yeniden diriltileceğiz öyle mi?"'),
(2756, 23, 83, '"Yemin ederiz ki, gerek bize, gerekse daha önce atalarımıza böyle bir vaadde bulunuldu; (fakat) bu geçmiştekilerin masallarından başka bir şey değildir!"'),
(2757, 23, 84, '(Resulüm!) de ki: "Eğer biliyorsanız (söyleyin bakalım), bu dünya ve onda bulunanlar kime aittir?"'),
(2758, 23, 85, '"Allah\'a aittir" diyecekler. "Öyle ise siz hiç düşünüp taşınmaz mısınız?" de.'),
(2759, 23, 86, '"Yedi kat göklerin Rabbi, azametli Arş\'ın Rabbi kimdir?" diye sor.'),
(2760, 23, 87, '"(Onlar da) Allah\'ındır." diyecekler. "Şu halde siz Allah\'tan korkmaz mısınız?" de.'),
(2761, 23, 88, '"Eğer biliyorsanız (söyleyin), her şeyin melekûtu (mülkiyeti ve yönetimi) kendisinin elinde olan, kendisi her şeyi koruyup kollayan; fakat kendisi korunmayan (buna muhtaç olmayan) kimdir?" diye sor.'),
(2762, 23, 89, '"(Bunlar da) Allah\'ındır." diyecekler. "Öyle ise nasıl olur da büyülenirsiniz?" de.'),
(2763, 23, 90, 'Doğrusu biz onlara hakkı getirdik; onlar ise cidden yalancıdırlar.'),
(2764, 23, 91, 'Allah evlat edinmemiştir; O\'nunla beraber hiçbir ilâh da yoktur. Aksi takdirde her ilâh kendi yarattığını sevk ve idare eder ve bir gün mutlaka onlardan biri diğerine galip gelirdi. Allah, onların yakıştırdıkları şeylerden münezzehtir.'),
(2765, 23, 92, 'Allah, gaybı da, açık olanı da bilir. O, müşriklerin ortak koştukları şeylerden çok yüce ve münezzehtir.'),
(2766, 23, 93, '(Resulüm!) De ki: Rabbim! Eğer onlara yöneltilen tehdidi (dünyevî sıkıntıyı ve uhrevî azabı) mutlaka göstereceksen,'),
(2767, 23, 94, 'Bu durumda beni, o zalimler topluluğunda bulundurma, Rabbim!'),
(2768, 23, 95, 'Biz, onlara yönelttiğimiz tehdidi sana göstermeye elbette ki kadiriz.'),
(2769, 23, 96, 'Sen, kötülüğü en güzel bir tutumla sav, çünkü biz onların yakıştırmakta oldukları şeyi çok iyi bilmekteyiz.'),
(2770, 23, 97, 'Ve de ki: Rabbim! Şeytanların kışkırtmalarından sana sığınırım!'),
(2771, 23, 98, 'Onların yanımda bulunmalarından da sana sığınırım.'),
(2772, 23, 99, 'Nihayet onlardan (müşriklerden) birine ölüm gelip çattığında, "Rabbim, der, lütfen beni (dünyaya) geri gönder,"'),
(2773, 23, 100, '"Ta ki, boşa geçirdiğim dünyada iyi iş (ve hareketler) yapayım." Hayır! Onun söylediği bu söz (boş) laftan ibarettir. Onların gerisinde ise, yeniden dirilecekleri güne kadar (süren) bir berzah vardır.'),
(2774, 23, 101, 'Sûr\'a üflendiği zaman aralarında artık ne soysop (çekişmesi) vardır, ne de birbirlerini soruşturacaklardır.'),
(2775, 23, 102, 'Böylece kimlerin tartıları ağır basarsa, işte asıl bunlar kurtuluşa erenlerdir.'),
(2776, 23, 103, 'Kimlerin de tartıları hafif gelirse, artık bunlar da kendilerine yazık etmişlerdir; (çünkü onlar) ebedî cehennemdedirler.'),
(2777, 23, 104, 'Orada dişleri sırıtır halde iken ateş yüzlerini yalar.'),
(2778, 23, 105, '(Allah Teâlâ,) Size âyetlerim okunurdu da, siz onları yalanlardınız değil mi?... der.'),
(2779, 23, 106, 'Derler ki: Rabbimiz! Azgınlığımız bizi altetti; biz, bir sapıklar topluluğu idik.'),
(2780, 23, 107, 'Rabbimiz! Bizi buradan çıkar. Eğer bir daha (ettiklerimize) dönersek, artık belli ki biz zalim insanlarız.'),
(2781, 23, 108, '(Allah) buyurur ki: Alçaldıkça alçalın orada! Bana konuşmayın artık.'),
(2782, 23, 109, 'Çünkü kullarımdan bir zümre "Rabbimiz! Biz iman ettik; öyle ise bizi bağışla, bize merhamet et, sen, merhametlilerin en iyisisin." diyorlardı.'),
(2783, 23, 110, 'İşte siz onları alaya aldınız; sonunda bu davranışınız size beni yâd etmeyi unutturdu; çünkü siz onlara gülüyordunuz.'),
(2784, 23, 111, 'Bugün ben onlara, sabrettiklerinin karşılığını verdim; onlar, hakikaten muradlarına erenlerdir.'),
(2785, 23, 112, '(Allah inkârcılara) "Yeryüzünde kaç yıl kaldınız?" diye sorar.'),
(2786, 23, 113, '"Bir gün veya günün bir kısmı kadar kaldık. İşte bilenlere sor." derler.'),
(2787, 23, 114, '(Allah) buyurur ki: Sadece az bir süre kaldınız; keşke siz (bunu) bilmiş olsaydınız!'),
(2788, 23, 115, 'Sizi sadece boş yere yarattığımızı ve sizin hakikaten huzurumuza geri getirilmeyeceğinizi mi sandınız?'),
(2789, 23, 116, 'Mutlak hâkim ve hak olan Allah, çok yücedir. O\'ndan başka ilâh yoktur. O, bereketli Arş\'ın sahibidir.'),
(2790, 23, 117, 'Her kim Allah ile birlikte diğer bir tanrıya taparsaki bu hususla ilgili hiçbir delili yoktur o kimsenin hesabı ancak Rabbinin nezdindedir. Şurası muhakkak ki, kâfirler kurtuluşa eremezler.'),
(2791, 23, 118, 'Resulüm! De ki: "Rabbim, bağışla ve merhamet et! Sen merhametlilerin en iyisisin."');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '(İşte bu âyetler) bizim indirdiğimiz ve (hükümlerini üzerinize) farz kıldığımız bir sûredir. Belki düşünüp öğüt alırsınız diye onda açık açık âyetler indirdik.'),
(2793, 24, 2, 'Zina eden kadın ve zina eden erkekten her birine yüz sopa vurun; Allah\'a ve ahiret gününe inanıyorsanız, Allah dini(ni tatbik) hususunda sizi sakın acıma duygusu kaplamasın! Müminlerden bir grup da onlara uygulanan cezaya şahit olsun.'),
(2794, 24, 3, 'Zina eden erkek, zina eden veya müşrik olan bir kadından başkası ile evlenemez; zina eden bir kadınla da ancak zina eden veya müşrik olan erkek evlenebilir. Bu, müminlere haram kılınmıştır.'),
(2795, 24, 4, 'Namuslu kadınlara zina isnadında bulunup, sonra (bunu ispat için) dört şahit getiremeyenlere seksener sopa vurun ve artık onların şahitliğini hiçbir zaman kabul etmeyin. Onlar tamamen günahkardırlar.'),
(2796, 24, 5, 'Ancak bundan sonra tevbe edip ıslah olanlar müstesnadır. Çünkü Allah çok bağışlayıcı ve merhametlidir.'),
(2797, 24, 6, 'Eşlerine zina isnadında bulunup da kendilerinden başka şahitleri olmayanlara gelince, onların her birinin şahitliği kendisinin doğru söyleyenlerden olduğuna dair dört defa Allah adına yemin ederek şahitlik etmesidir.'),
(2798, 24, 7, 'Beşinci defa da, eğer yalan söyleyenlerden ise, Allah\'ın lanetinin kendi üzerine olmasını dilemesidir.'),
(2799, 24, 8, 'Kadının, kocasının yalan söyleyenlerden olduğuna dair dört defa Allah adına yemin ve şahitlik etmesi,'),
(2800, 24, 9, 'Beşinci defa da, eğer (kocası) doğru söyleyenlerden ise, Allah\'ın gazabının kendi üzerine olmasını dilemesi kendisinden cezayı kaldırır.'),
(2801, 24, 10, 'Ya Allah\'ın size bol lütfu ve merhameti olmasaydı ve Allah tevbeleri kabul eden hüküm ve hikmet sahibi olmasaydı (haliniz nice olurdu.)?'),
(2802, 24, 11, 'Haberiniz olsun ki (Muhammed\'in eşine) bu ağır ifki (iftirayı) uyduranlar sizin içinizden bir gruptur. Bunu kendiniz için bir kötülük saymayın; aksine o, sizin için bir iyiliktir. Onlardan herbir kişiye, günah olarak ne işlemişse (onun karşılığı ceza) vardır. (Elebaşlılık yapan, bu yüzden de) bu günahın büyüğünü yüklenen kimse için de çok büyük bir azap vardır.'),
(2803, 24, 12, 'Erkek ve kadın müminlerin, bu iftirayı işittiklerinde kendi vicdanları ile hüsnü zanda bulunup da, "bu apaçık bir iftiradır" demeleri gerekmez miydi?'),
(2804, 24, 13, '(Bu iddiayı ortaya atanların) da bu konuda dört şahit getirmeleri gerekmez miydi? Madem ki şahitler getirip ispat edemediler, öyle ise onlar Allah nezdinde yalancıların ta kendisidirler.'),
(2805, 24, 14, 'Eğer dünyada ve ahirette Allah\'ın lütuf ve merhameti üstünüzde olmasaydı, size mutlaka büyük bir azab isabet ederdi.'),
(2806, 24, 15, 'Çünkü siz bu iftirayı, gelişi güzel birbirinizin ağzından alıyor ve hakkında bilgi sahibi olmadığınız (bu uydurma haberi) ağızlarınızda geveleyip duruyorsunuz. Bunun önemsiz olduğunu sanıyorsunuz. Halbuki bu, Allah katında çok büyük bir suçtur.'),
(2807, 24, 16, 'Onu duyduğunuzda "Bunu konuşup yaymamız bize yakışmaz. Haşâ! Bu, çok büyük bir iftiradır..." demeli değil miydiniz?'),
(2808, 24, 17, 'Eğer inanmış insanlarsanız, Allah, bir daha buna benzer tutumu tekrarlamaktan sizi sakındırıp uyarıyor.'),
(2809, 24, 18, 'Ve Allah âyetlerini size açıklıyor. Allah, (işin iç yüzünü) çok iyi bilir, tam bir hüküm ve hikmet sahibidir.'),
(2810, 24, 19, 'İnananlar arasında kötü söz ve davranışın yayılmasını arzulayan kimseler için dünyada da, ahirette de acı veren bir azab vardır. (Her şeyi) Allah bilir; siz bilmezsiniz.'),
(2811, 24, 20, 'Ya sizin üstünüze Allah\'ın lütuf ve merhameti olmasaydı; Allah çok şefkatli ve merhametli olmasaydı (haliniz nice olurdu)?'),
(2812, 24, 21, 'Ey iman edenler! Şeytanın adımlarını takip etmeyin. Kim şeytanın adımlarını takip ederse, şunu bilsin ki o, edepsizlikleri ve kötülüğü emreder. Eğer üstünüzde Allah\'ın lütuf ve merhameti olmasaydı, içinizden hiçbir kimse temize çıkmazdı. Fakat Allah, dilediğini arındırır. Allah işitir ve bilir.'),
(2813, 24, 22, 'İçinizden faziletli ve servet sahibi kimseler akrabaya, yoksullara, Allah yolunda göç edenlere (mallarından) vermeyeceklerine yemin etmesinler; bağışlasınlar, feragat göstersinler. Allah\'ın sizi bağışlamasını arzulamaz mısınız? Allah çok bağışlayandır, çok merhametlidir.'),
(2814, 24, 23, 'Namuslu, kötülüklerden habersiz mümin kadınlara zina isnadında bulunanlar, dünya ve ahirette lanetlenmişlerdir. Onlar için çok büyük bir azab vardır.'),
(2815, 24, 24, 'O gün dilleri, elleri ve ayakları, yapmış olduklarından dolayı aleyhlerinde şahitlik edecektir.'),
(2816, 24, 25, 'O gün Allah onlara gerçek cezalarını tastamam verecek ve onlar Allah\'ın gerçek olduğunu anlayacaklar.'),
(2817, 24, 26, 'Kötü kadınlar, kötü erkeklere, kötü erkekler ise kötü kadınlara; temiz kadınlar temiz erkeklere, temiz erkekler de temiz kadınlara yaraşır. İşte bu temiz olan, (iftiracıların) söylediklerinden çok uzaktırlar. Kendileri için bağışlanma ve güzel bir rızık vardır.'),
(2818, 24, 27, 'Ey iman edenler! Kendi evinizden başka evlere, geldiğinizi farkettirip ev halkına selam vermedikçe girmeyin. Bu sizin için daha iyidir. Herhalde (bunu) düşünüp anlarsınız.'),
(2819, 24, 28, 'Orada kimse bulamazsanız, size izin verilinceye kadar oraya girmeyin. Eğer size, "Geri dönün!" denilirse, hemen dönün. Çünkü bu, sizin için daha temiz bir davranıştır. Allah, yaptığınızı bilir.'),
(2820, 24, 29, 'İçinde kendinize ait bir şeylerin bulunduğu oturulmayan bir eve girmenizde herhangi bir sakınca yoktur. Allah, sizin açığa vurduklarınızı da, gizlediklerinizi de bilir.'),
(2821, 24, 30, '(Resulüm!) Mümin erkeklere, gözlerini (harama) dikmemelerini, ırzlarını da korumalarını söyle. Çünkü bu, kendileri için daha temiz bir davranıştır. Şüphesiz Allah, onların yapmakta olduklarından haberdardır.'),
(2822, 24, 31, 'Mümin kadınlara da söyle: Gözlerini (harama bakmaktan) korusunlar; namus ve iffetlerini esirgesinler. Görünen kısımları müstesna olmak üzere, zinetlerini teşhir etmesinler. Baş örtülerini, yakalarının üzerine (kadar) örtsünler. Kocaları, babaları, kocalarının babaları, kendi oğulları, kocalarının oğulları, erkek kardeşleri, erkek kardeşlerinin oğulları, kız kardeşlerinin oğulları, kendi kadınları (mümin kadınlar), ellerinin altında bulunan (köleleri), erkeklerden, kadına ihtiyacı kalmamış (cinsî güçten düşmüş) hizmetçiler, yahut henüz kadınların gizli kadınlık hususiyetlerinin farkında olmayan çocuklardan başkasına zinetlerini göstermesinler. Gizlemekte oldukları zinetleri anlaşılsın diye, ayaklarını yere vurmasınlar. Ey müminler! Hep birden Allah\'a tevbe ediniz ki, kurtuluşa eresiniz.'),
(2823, 24, 32, 'Aranızdaki bekarları, kölelerinizden ve cariyelerinizden iyi davranışta olanları evlendirin. Eğer bunlar fakir iseler, Allah kendi lütfu ile onları zenginleştirir. Allah, (lütfu) geniş olan ve (her şeyi) bilendir.'),
(2824, 24, 33, 'Evlenme imkanını bulamayanlar ise, Allah, lütfu ile kendilerini varlıklı kılıncaya kadar iffetlerini korusunlar. Ellerinizin altında bulunanlardan (köleler ve cariyelerden) mükatebe yapmak isteyenlerle, eğer kendilerinde (hürriyete kavuşmalarında kendileri için) bir iyilik görüyorsanız, hemen mükatebe yapın. Allah\'ın size vermiş olduğu malından siz de onlara verin. Dünya hayatının geçici menfaatlerini elde edeceksiniz diye, namuslu kalmak isteyen cariyelerinizi fuhşa zorlamayın. Kim onları zor altında bırakırsa, bilinmelidir ki, zorlanmalarından sonra Allah (onlar için) çok bağışlayıcı ve merhametlidir.'),
(2825, 24, 34, 'Andolsun ki biz size açık açık bildiren âyetler, sizden önce yaşayıp gitmiş olanlardan örnekler ve takvaya ulaşmış kimseler için öğütler indirdik.'),
(2826, 24, 35, 'Allah, göklerin ve yerin nurudur (aydınlatıcısıdır). O\'nun nurunun temsili, içinde lamba bulunan bir kandil gibidir. O lamba bir billur içindedir; o billur da sanki inciye benzer bir yıldız gibidir ki, doğuya da batıya da nisbet edilemeyen mübarek bir ağaçtan çıkan yağdan tutuşturulur. (Bu öyle bir ağaç ki) yağı, nerdeyse, kendisine ateş değmese bile ışık verir. (Bu ışık) nur üstüne nurdur. Allah dilediği kimseyi nuruyla hidayete iletir. Allah insanlara (işte böyle) misal verir; Allah her şeyi bilir.'),
(2827, 24, 36, '(Bu kandil) birtakım evlerdedir ki, Allah (o evlerin) yücelmesine ve içlerinde isminin okunmasına izin vermiştir. Orada sabah akşam O\'nu tesbih ederler.'),
(2828, 24, 37, 'Birtakım insanlar (Allahı tesbih ederler) ki, ne ticaret ne de alış veriş onları Allah\'ı anmaktan, namaz kılmaktan ve zekat vermekten alıkoymaz. Onlar, kalplerin ve gözlerin allak bullak olduğu bir günden korkarlar.'),
(2829, 24, 38, 'Çünkü Allah, kendilerine işledikleri amellerin en güzeli ile ecir verecek, lütfundan fazlasını da bahşedecektir ve Allah, dilediğine hesapsız rızık verir.'),
(2830, 24, 39, 'Küfredenlere gelince, onların amelleri, ıssız çöllerdeki serap gibidir ki, susayan onu su zanneder, nihayet ona vardığında orada herhangi bir şey bulamamış, üstelik yanıbaşında da (inanmadığı, kendisinden sakınmadığı) Allah\'ı bulmuştur. Allah ise onun hesabını tastamam görmüştür. Allah hesabı çok çabuk görür.'),
(2831, 24, 40, 'Yahut (o kâfirlerin duygu, düşünce ve davranışları) engin bir denizdeki yoğun karanlıklar gibidir ki, onu dalga üstüne dalga kaplıyor; üstünde de bulut. Bir biri üstüne karanlıklar... İnsan, elini çıkarıp uzatsa, nerdeyse onu dahi göremez. Bir kimseye Allah, nur vermemişse, artık o kimsenin ışık ve aydınlıktan nasibi yoktur.'),
(2832, 24, 41, 'Görmez misin ki, göklerde ve yerde bulunanlarla dizi dizi kanat çırpıp uçan kuşların Allah\'ı tesbih ettiklerini? Her biri kendi tesbihini ve duâsını bilmiştir. Allah, onların yapmakta olduklarını hakkıyla bilir.'),
(2833, 24, 42, 'Göklerin ve yerin mülkü Allah\'ındır; dönüş de ancak O\'nadır.'),
(2834, 24, 43, 'Görmez misin ki Allah bulutları (dilediği yere) sürüklüyor; sonra onları biraraya getirip üstüste yığıyor. İşte görüyorsun ki bunlar arasında yağmur çıkıyor. O, gökten, sanki oradaki dağlardan da dolu indirir. Artık onu dilediğine isabet ettirir; dilediğinden de onu uzak tutar; bu bulutlardan çıkan şimşeğin parıltısı nerdeyse gözleri alır!'),
(2835, 24, 44, 'Allah gece ile gündüzü evirip çeviriyor. Şüphesiz bunda (hakikatı gören) gözlere sahip olanlar için mutlak bir ibret vardır.'),
(2836, 24, 45, 'Allah, her hayvanı sudan yarattı. İşte bunlardan kimi karnı üstünde sürünür, kimi iki yağı üstünde yürür, kimi dört ayağı üstünde yürür... Allah dilediğini yapar; çünkü Allah her şeye kâdirdir.'),
(2837, 24, 46, 'Andolsun biz (her şeyi) apaçık bildiren âyetler indirdik. Allah dilediğini doğru yola iletir.'),
(2838, 24, 47, 'Bir de "Allah\'a ve Resulüne inandık ve itaat ettik" diyorlar da, sonra bunun arkasından yan çiziyorlar; bunlar mümin değillerdir.'),
(2839, 24, 48, 'Aralarında hükmetmesi için Allah\'a ve Resulüne çağrıldıkları zaman, bakarsın ki, içlerinden birkısmı yüz çevirip dönerler.'),
(2840, 24, 49, 'Ama, eğer (Allah ve Resulünün hükmettiği) hak kendi lehlerine ise, ona, gönülden bağlı olarak saygı ile gelirler.'),
(2841, 24, 50, 'Kalplerinde bir hastalık mı var? Yoksa şüphe ve tereddüd içinde midirler? Yoksa Allah ve Resulünün kendilerine zulüm ve haksızlık edeceğinden mi korkuyorlar? Hayır, asıl zalimler kendileridir!'),
(2842, 24, 51, 'Aralarında hüküm vermesi için Allah\'a ve Resulüne davet edildiklerinde müminlerin sözü ancak "işittik ve itaat ettik" demeleridir. İşte bunlar asıl kurtuluşa erenlerdir.'),
(2843, 24, 52, 'Her kim Allah\'a ve Resulüne itaat eder, Allah\'a saygı duyar ve O\'ndan sakınırsa, işte asıl bunlar bedbahtlıktan kurtulanlardır.'),
(2844, 24, 53, 'Ötekiler (münafıklar), sen hakikaten kendilerine emrettiğin takdirde mutlaka (savaşa) çıkacaklarına dair, en ağır yeminleri ile Allah\'a yemin ettiler. De ki: Yemin etmeyin. İtaatiniz malumdur! Bilin ki Allah, yaptıklarınızdan haberdardır.'),
(2845, 24, 54, 'De ki: Allah\'a itaat edin; Peygambere de itaat edin. Eğer yüz çevirirseniz şunu bilin ki, Peygamberin sorumluluğu kendine yüklenen, sizin sorumluğunuz da size yüklenendir. Eğer ona itaat ederseniz, doğru yolu bulmuş olursunuz. Peygambere düşen, sadece açık açık duyurmaktır.'),
(2846, 24, 55, 'Allah, sizlerden iman edip iyi davranışlarda bulunanlara, kendilerinden öncekileri sahip ve hakim kıldığı gibi, kendilerini de yeryüzüne sahip ve hakim kılacağını, onlar için beğenip seçtiği dini (İslâm\'ı) onların iyiliğine yerleştirip koruyacağını ve geçirdikleri korku döneminden sonra, bunun yerine onlara güven sağlayacağnı vaad etti. Çünkü onlar bana kulluk ederler. Hiçbir şeyi bana eş tutmazlar. Artık bundan sonra kim inkâr ederse, işte bunlar asıl büyük günahkarlardır.'),
(2847, 24, 56, 'Hem namazı kılın, zekatı verin ve peygambere itaat edin ki rahmete eresiniz.'),
(2848, 24, 57, 'İnkâr edenlerin, yeryüzünde (Allah\'ı) aciz bırakacaklarını sanmayasın! Onların varacağı yer cehennemdir. Ne kötü varış yeridir orası!'),
(2849, 24, 58, 'Ey iman edenler! Ellerinizin altında bulunan (köle ve cariyeleriniz) ve içinizden henüz erginlik çağına girmemiş olanlar, sabah namazından önce, öğleyin soyunduğunuz vakit ve yatsı namazından sonra (yanınıza gireceklerinde) sizden üç defa izin istesinler. Bunlar mahrem halde bulunabileceğiniz üç vakittir. Bu vakitlerin dışında ne sizin için, ne de onlar için bir mahzur yoktur. (Birbirinizin yanına girip çıkabilirsiniz.) İşte Allah, âyetlerini size böyle açıklar. Allah her şeyi bilendir, hüküm ve hikmet sahibidir.'),
(2850, 24, 59, 'Sizden olan çocuklarınız erginlik çağına girdiklerinde, kendilerinden öncekiler (büyükleri) izin istedikleri gibi, onlar da izin istesinler. İşte Allah, âyetlerini size böyle açıklar. Allah her şeyi bilendir, hüküm ve hikmet sahibidir.'),
(2851, 24, 60, 'Bir nikah ümidi kalmayan, çocuktan kesilmiş yaşlı kadınların ise, zinetlerini (yabancı erkeklere) göstermeksizin dış elbiselerini çıkarmalarında kendilerine bir vebal yoktur. Yine de iffetli olmaları kendileri için daha hayırlıdır. Allah işitendir, bilendir.'),
(2852, 24, 61, 'A\'maya güçlük yoktur; topala güçlük yoktur; hastaya da güçlük yoktur. Sizin için de gerek kendi evlerinizden, gerekse babalarınızın evlerinden, annelerinizin evlerinden, erkek kardeşlerinizin evlerinden, kız kardeşlerinizin evlerinden, amcalarınızın evlerinden halalarınızın evlerinden, dayılarınızın evlerinden, teyzelerinizin evlerinden veya anahtarlarına malik olduğunuz yerlerden, yahut dostlarınızın evlerinden yemenizde bir sakınca yoktur. Toplu halde veya ayrı ayrı yemenizde de bir güçlük ve günah yoktur. Evlere girdiğiniz zaman Allah tarafından mübarek ve güzel bir yaşama dileği olarak kendinize (birbirinize) selam verin. İşte Allah düşünüp anlayasınız diye size âyetlerini böyle açıklar.'),
(2853, 24, 62, 'Müminler ancak, Allah\'a ve Resülüne gönülden inanmış kimselerdir. Onlar o Peygamber ile birlikte sosyal bir işle meşgul iken ondan izin istemedikçe bırakıp gitmezler. (Resulüm!) Şu senden izin isteyenler, hakikaten Allah\'a ve Resulüne iman etmiş kimselerdir. Öyle ise, bazı işleri için senden izin istediklerinde, sen de onlardan dilediğine izin ver; onlar için Allah\'tan bağış dile; çünkü Allah mağfiret edicidir, merhametlidir.'),
(2854, 24, 63, '(Ey müminler!) Peygamberin davetini, aranızdan bazınızın bazınıza daveti gibi zannetmeyin. İçinizden, birini siper ederek sıvışıp gidenleri muhakkak ki Allah bilmektedir. Bu sebeple, O\'nun emrine aykırı davrananlar, başlarına bir bela gelmesinden veya kendilerine çok elemli bir azap isabet etmesinden sakınsınlar.'),
(2855, 24, 64, 'Bilmiş olun ki, göklerde ve yerde ne varsa Allah\'ındır. O, sizin ne yolda, ne durumda olduğunuzu iyi bilir. Huzuruna döndürülecekleri günde ise, yapmış olduklarını hemen kendilerine haber verir. Allah, her şeyi hakkıyla bilendir..');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, '"Tebareke" ne yüce feyyazdır o ki, dünyaları uyarmak üzere kulu Muhammed\'e, hakkı batıldan ayırdeden Kur\'ân\'ı indirdi.'),
(2857, 25, 2, 'O öyle bir ilâhtır ki, göklerin ve yerin hükümranlığı kendisinindir. O hiç çocuk edinmedi, hükümranlıkta ortağı yoktur. O, her şeyi yaratıp bir ölçüye göre düzenleyerek takdir etmiştir.'),
(2858, 25, 3, 'Kâfirler, O\'nu bırakıp bir şey yaratamayan, bilakis kendileri yaratılmış olan, kendilerine ne zarar ve ne de fayda verebilen; öldürmeye, diriltmeye ve ölümden sonra tekrar canlandırmaya güçleri yetmeyen tanrılar edindiler.'),
(2859, 25, 4, 'İnkâr edenler: "Bu Kur\'ân Muhammed\'in uydurmasıdır, ona başka bir topluluk yardım etmiştir" diyerek haksız ve asılsız bir söz uydurdular.'),
(2860, 25, 5, '"Kur\'ân öncekilerin masallarıdır; başkalarına yazdırmış da sabah akşam kendisine okunmaktadır" dediler.'),
(2861, 25, 6, 'Ey Muhammed! De ki: "Onu, göklerin ve yerin sırrını bilen indirmiştir. Şüphesiz O, bağışlayandır, merhamet edendir."'),
(2862, 25, 7, 'Şöyle dediler: "Bu ne biçim peygamber ki, yemek yer, sokaklarda gezer? Ona, beraberinde bulunup uyaran bir melek indirilseydi ya!"'),
(2863, 25, 8, '"Yahut kendisine bir hazine verilseydi veya besleneceği bir bahçe olsaydı ya!" Bu zalimler, inananlara "Siz sadece büyülenmiş bir adama uyuyorsunuz" dediler.'),
(2864, 25, 9, 'Ey Muhammed! sana nasıl misaller getirdiklerine bir bak! Onlar sapmışlardır, yol bulamazlar.'),
(2865, 25, 10, 'Öyle yücedir O ki, dilerse sana ondan daha iyisini, altından ırmaklar akan cennetler verir, sana köşkler de yapar.'),
(2866, 25, 11, 'Fakat onlar o saati (kıyameti) de yalanladılar. Biz ise o saati yalanlayanlara çılgın alevli bir ateş hazırladık.'),
(2867, 25, 12, 'Ki, cehennem ateşi uzak bir mesafeden kendilerine görününce, onun bir hışımlanmasını (kaynamasını) ve uğultusunu işitirler.'),
(2868, 25, 13, 'Elleri boyunlarına bağlı olarak onun dar bir yerine atıldıkları zaman da, oracıkta yok olmayı isterler.'),
(2869, 25, 14, '(Onlara şöyle denilir) Bu gün bir yok olmayı değil, nice yok olmaları isteyin!'),
(2870, 25, 15, 'De ki: Bu mu daha iyi, yoksa takva sahiplerine vaad olunan ebedilik cenneti mi? Çünkü orası, onlar için bir mükafattır ve bir varış yeridir.'),
(2871, 25, 16, 'Onlar için orada ne isterlerse var, hem orada ebedî kalacaklar. Çünkü bu Rabbinden yerine getirilmesi istenen bir vaaddir.'),
(2872, 25, 17, 'Hele o gün Rabbin onları Allah\'tan başka taptıkları şeylerle toplar da, der ki: "Siz mi saptırdınız şu kullarımı, yoksa kendileri mi yolu kaybettiler?"'),
(2873, 25, 18, 'Onlar: "Sübhansın seni tenzih ederiz. Seni bırakıp da senden başka dostlar edinmek bize yaraşmaz; fakat sen onlara ve atalarına o kadar nimet verdin ki, sonunda seni anmayı unuttular ve helaki hak eden bir kavim oldular." derler.'),
(2874, 25, 19, '(Bunun üzerine ötekilere hitaben şöyle denilir.) İşte (taptıklarınız) sizi söylediklerinizde yalancı çıkardılar. Artık ne (azabınızı) geri çevirebilir, ne de bir yardıma çare bulabilirsiniz ve içinizden kim zulmederse, ona büyük bir azab tattıracağız.'),
(2875, 25, 20, '(Resulüm!) Biz senden evvel de peygamberleri başka türlü göndermedik. Şüphesiz onlar hem yemek yiyorlar, hem çarşılarda geziyorlardı (sokaklarda yürüyorlardı). Sizin bir kısmınızı bir diğerine fitne (imtihan sebebi) kılmışızdır ki, bakalım sabredecek misiniz? Zira Rabbin her şeyi hakkıyla görmektedir.'),
(2876, 25, 21, 'Bununla beraber, bize kavuşmayı ummayanlar "Bize ya melekler indirilmeliydi, ya da Rabbimizi görmeliydik" dediler. Andolsun ki, doğrusu nefislerinde kendilerini büyük gördüler ve büyük azgınlık ettiler.'),
(2877, 25, 22, 'Melekleri görecekleri gün, işte o gün, günahkarlara hiçbir sevinç haberi yoktur. Ve yasak yasak, diyeceklerdir.'),
(2878, 25, 23, 'Onların yaptıkları her bir iyi işi dikkate alırız, fakat onu saçılmış zerreler haline getiririz.'),
(2879, 25, 24, 'O gün cennetliklerin kalacakları yer çok iyi, dinlenecekleri yer pek güzeldir.'),
(2880, 25, 25, 'O gün gökyüzü beyaz bulutlar halinde yarılacak ve melekler bölük bölük indirileceklerdir.'),
(2881, 25, 26, 'İşte o gün gerçek hükümranlık, çok merhametli olan Allah\'ındır. Kâfirler için ise o, pek çetin bir gündür.'),
(2882, 25, 27, 'O gün zalim kimse ellerini ısıracak: "Eyvah!" diyecek, "keşke Peygamberin yanında bir yol tutsaydım!"'),
(2883, 25, 28, '"Eyvah!" diyecek, "keşke falancayı dost edinmeseydim.'),
(2884, 25, 29, 'Çünkü zikir (Kur\'ân) bana gelmişken o, hakikaten beni ondan saptırdı. Şeytan insanı (uçuruma sürükleyip sonra) yapayalnız ve yardımcısız bırakmaktadır.'),
(2885, 25, 30, 'Peygamber dedi ki: "Ey Rabbim! Kavmim bu Kur\'ân\'ı terkedilmiş (bir şey yerinde) tuttular."'),
(2886, 25, 31, '(Resulüm!) Ve işte biz böyle her peygamber için günahkarlardan bir düşman yapmışızdır. Bununla beraber hidayet verici ve yardımcı olarak Rabbin yeter.'),
(2887, 25, 32, 'Yine o inkâr edenler dediler ki: "O Kur\'ân ona, hepsi birden indirilseydi ya"! Biz onu senin kalbine iyice yerleştirmek için böyle (parça parça indirdik) ve onu tane tane (ayırarak) okuduk.'),
(2888, 25, 33, 'Hem onlar sana karşı herhangi bir mesel ile gelmezler ki, biz sana (onun karşılığında) doğrusunu ve tefsirin daha güzelini getirmiş olmayalım.'),
(2889, 25, 34, 'O yüzleri üstü cehenneme toplanacaklar var ya! işte onlar, yerleri en kötü, yolları en sapık olanlardır.'),
(2890, 25, 35, 'Andolsun ki Musa\'ya kitap verdik, kardeşi Harun\'u da ona yardımcı yaptık.'),
(2891, 25, 36, '"Haydi âyetlerimizi yalan sayan o kavme gidin" dedik. Sonunda (yola gelmediklerinden) onları yerle bir ettik.'),
(2892, 25, 37, 'Nuh kavmine gelince, Peygamberleri yalancılıkla itham ettiklerinde, onları suda boğduk ve kendilerini insanlar için bir ibret yaptık. Biz zalimler için acıklı bir azab hazırlamışızdır.'),
(2893, 25, 38, 'Ad\'ı, Semud\'u, Ress halkını ve bunlar arasında daha bir çok nesilleri de (inkârcılıkları yüzünden helak ettik)'),
(2894, 25, 39, 'Onların herbirine misaller getirdik; (ama ögüt almadıkları için) hepsini kırdık geçirdik.'),
(2895, 25, 40, '(Resulüm!) Andolsun ki, (bu Mekke\'li putperestler), bela ve fenalık yağmuruna tutulmuş olan beldeye uğramışlardır. Peki onu da görmüyorlar mıydı? Hayır! Onlar öldükten sonra dirilmeyi ummamaktadırlar.'),
(2896, 25, 41, 'Seni gördükleri zaman "Bu mu Allah\'ın Peygamber olarak gönderdiği?" diye hep seni alaya alıyorlar.'),
(2897, 25, 42, '"Şayet tanrılarımıza inanmakta sebat göstermeseydik, gerçekten de bizi neredeyse tanrılarımızdan saptıracaktı" diyorlar. Azabı gördükleri zaman, kimin yolunun sapık olduğunu bilecekler!'),
(2898, 25, 43, 'Kötü duygularını kendisine tanrı edinen kimseyi gördün mü? Şimdi ona sen mi vekil olacaksın?'),
(2899, 25, 44, 'Yoksa sen, onların çoğunun gerçekten söz dinleyeceğini yahut akıllanacağını mı sanıyorsun? Gerçekte onlar hayvanlar gibidir, hatta gidişçe daha sapıktırlar.'),
(2900, 25, 45, 'Rabbinin gölgeyi nasıl uzatmakta olduğunu görmedin mi? Dileseydi onu elbet hareketsiz de kılardı. Sonra biz güneşi, ona (gölgeye) delil kılmışızdır.'),
(2901, 25, 46, 'Sonra da onu yavaş yavaş kendimize (başka yöne) çekmekteyiz.'),
(2902, 25, 47, 'Sizin için geceyi örtü, uykuyu istirahat kılan, gündüzü yayılıp çalışma (zamanı) yapan O\'dur.'),
(2903, 25, 48, 'Rüzgarları rahmetinin önünde müjdeci olarak gönderen ve gökten tertemiz bir su indiren O\'dur.'),
(2904, 25, 49, 'Ki biz (o suyla) ölü toprağa can verelim, yarattığımız nice hayvanlara ve insanlara su sağlayalım, diye.'),
(2905, 25, 50, 'Andolsun bunu, insanların öğüt almaları için, aralarında çeşit çeşit şekillerde anlatmışızdır; ama insanların çoğu ille nankörlük edip diretmiştir.'),
(2906, 25, 51, '(Habibim!) Şayet dileseydik elbette her köye bir uyarıcı (peygamber) gönderirdik.'),
(2907, 25, 52, '(Madem ki yalnız seni gönderdik) Öyleyse kâfirlere boyun eğme ve bununla (Kur\'ân ile) onlara karşı olanca gücünle büyük bir savaş ver!'),
(2908, 25, 53, 'Birinin suyu tatlı ve susuzluğu giderici, diğerininki tuzlu ve acı iki denizi salıveren ve aralarına bir engel, aşılmaz bir serhat koyan O\'dur.'),
(2909, 25, 54, 'O (hakir) sudan, bir insan yaratıp ona bir neseb bahşeden ve sıhriyet bağı ile akraba yapan O\'dur. Rabbinin her şeye gücü yeter.'),
(2910, 25, 55, '(Böyle iken inkârcılar) Allah\'ı bırakıp kendilerine ne fayda, ne zarar veremeyen şeylere kulluk ediyorlar. İnkârcı olan kimse Rabbine karşı uğraşıp durmaktadır.'),
(2911, 25, 56, '(Halbuki) biz seni ancak müjdeci ve uyarıcı olarak gönderdik.'),
(2912, 25, 57, 'De ki: "Ben, buna karşı sizden bir ücret değil, ancak Rabbine doğru bir yol tutmayı dileyen kimseler (olmanızı) istiyorum."'),
(2913, 25, 58, 'Sen, ölümsüz ve daima diri olan Allah\'a güvenip dayan. O\'nu hamd ile tesbih et. Kullarının günahlarından haberdar olarak O yeter.'),
(2914, 25, 59, 'Gökleri yeri ve ikisinin arasındakileri altı günde yaratan, sonra Arş\'a hükmeden Rahmân\'dır. Haydi ne dileyeceksen o her şeyden haberdar olan (Rahmân)dan dile.'),
(2915, 25, 60, 'Onlara "Rahmân\'a secde edin" dendiği zaman, "Rahmân da neymiş? Senin bize emrettiğine secde eder miyiz hiç?" derler ve bu emir onların nefretini artırır.'),
(2916, 25, 61, 'Gökte burçları var eden, onların içinde bir kandil (güneş) ve nurlu bir ay barındıran Allah, yüceler yücesidir.'),
(2917, 25, 62, 'İbret almak veya şükretmek dileyen kimseler için gece ile gündüzü birbiri ardınca getiren O\'dur.'),
(2918, 25, 63, 'O çok merhametli Allah\'ın (has) kulları onlardır ki, yeryüzünde tevazu ile yürürler ve cahil kimseler kendilerine laf attığı zaman (incitmeksizin) "selam" derler (geçerler).'),
(2919, 25, 64, 'Ve onlar ki, Rablerine secdeler ve kıyamlar ederek yatarlar.'),
(2920, 25, 65, 'Onlar ki, şöyle derler: Cehennem azabını üzerimizden sav! Doğrusu onun azabı geçici bir şey değildir.'),
(2921, 25, 66, 'Orası cidden ne kötü bir uğrak, ne kötü bir konaktır.'),
(2922, 25, 67, 'Ve onlar ki, harcadıklarında ne israf ne de cimrilik ederler; ikisi arasında orta bir yol tutarlar.'),
(2923, 25, 68, 'Yine onlar ki, Allah ile beraber başka bir tanrıya yalvarmazlar, Allah\'ın haram kıldığı cana haksız yere kıymazlar ve zina etmezler. Bunları yapan günahı(nın cezasını) bulur.'),
(2924, 25, 69, 'Kıyamet günü azabı kat kat olur ve orada alçaltılmış olarak temelli kalır.'),
(2925, 25, 70, 'Ancak tevbe ve iman edip iyi davranışlarda bulunanlar başka; Allah onların kötülüklerini iyiliklere çevirir. Allah çok bağışlayıcıdır, engin merhamet sahibidir.'),
(2926, 25, 71, 'Ve her kim tevbe edip iyi davranış gösterirse, şüphesiz o, tevbesi kabul edilmiş olarak Allah\'a döner.'),
(2927, 25, 72, 'Ve onlar ki, yalan şahitlik etmezler, boş bir şeye rastladıkları zaman vakar ile (oradan) geçip giderler.'),
(2928, 25, 73, 'Kendilerine Rablerinin âyetleri hatırlatıldığında ise, onlara karşı sağır ve kör davranmazlar.'),
(2929, 25, 74, 'Ve onlar ki: "Ey Rabbimiz! Bize gözümüzü aydınlatacak eşler ve zürriyetler bağışla ve bizi takva sahiplerine önder kıl" derler.'),
(2930, 25, 75, 'İşte onlar, sabretmelerine karşılık cennetin en yüksek makamları ile mükafatlandırılacaklar, orada hürmet ve selamla karşılanacaklardır.'),
(2931, 25, 76, 'Orada ebedî kalacaklar, orası ne güzel bir konak ve ne güzel bir makamdır.'),
(2932, 25, 77, '(Resulüm!) De ki: "Rabbim size ne kıymet verir duanız olmasa? (Ey inkârcılar! Size bildirdiklerini) kesinkes yalan saydınız; o halde azab yakanızı bırakmayacaktır!');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'Tâ, Sîn, Mîm.'),
(2934, 26, 2, 'Bunlar sana apaçık kitabın âyetleridir.'),
(2935, 26, 3, '(Resulüm!) Onlar iman etmiyorlar diye adeta kendine kıyacaksın!'),
(2936, 26, 4, 'Biz dilersek onların üzerlerine gökten bir âyet (mucize) indiririz de, ona boyunları eğilekalır.'),
(2937, 26, 5, 'Bununla beraber kendilerine O Rahmân\'dan yeni bir öğüt gelmeyedursun, ille ondan yüz çevirirler.'),
(2938, 26, 6, 'Üstelik (ona) "yalandır" dediler; fakat onlara alay edip durdukları şeyin haberleri yakında gelecektir.'),
(2939, 26, 7, 'Yeryüzüne bir bakmadılar mı? Biz orada her güzel çiftten nice bitkiler yetiştirmişiz.'),
(2940, 26, 8, 'Şüphesiz ki bunda mutlak bir âyet (nişane) vardır; ama onların çoğu iman etmezler.'),
(2941, 26, 9, 'Ve şüphe yok ki Rabbin, galip ve engin merhamet sahibidir.'),
(2942, 26, 10, 'Bir vakit de Rabbin, Musa\'ya nida edip "Git o zalim kavme" dedi.'),
(2943, 26, 11, '"Firavun kavmine, hâlâ sakınmayacaklar mı?"'),
(2944, 26, 12, '(Musa) şöyle seslendi: "Ya Rab! Doğrusu ben korkarım ki beni yalancı sayarlar."'),
(2945, 26, 13, '"Ve göğsüm daralır, dilim dönmez, onun için Harun\'a da elçilik ver."'),
(2946, 26, 14, '"Hem onların bana isnad ettikleri bir suç var. Ondan dolayı korkarım ki, hemen beni öldürürler."'),
(2947, 26, 15, '(Allah): "Hayır hayır" buyurdu, "haydi ikiniz âyetlerimizle (mucizelerimizle) gidin. Şüphesiz ki, biz sizinle beraberiz. (Onları) işitiyoruz."'),
(2948, 26, 16, '"Haydin Firavun\'a gidin de deyin ki: İnan biz, âlemlerin Rabbinin elçisiyiz.'),
(2949, 26, 17, 'İsrail oğullarını bizimle beraber gönder."'),
(2950, 26, 18, '"Â, dedi, biz seni çocukken himayemize alıp büyütmedik mi? Hayatının bir çok yıllarını aramızda geçirmedin mi?"'),
(2951, 26, 19, '"Sonunda o yaptığın (kötü) işi de yaptın. Sen nankörün birisin!"'),
(2952, 26, 20, 'Musa, "Ben, dedi, o işi o anda yaptım ki şaşkınlardandım."'),
(2953, 26, 21, '"Sizden korkunca da hemen aranızdan kaçtım. Sonra Rabbim bana hikmet bahşetti ve beni peygamberlerden kıldı."'),
(2954, 26, 22, '"O başıma kaktığın nimet de (aslında) İsrail oğullarını kendine köle edinmiş olmandır. "'),
(2955, 26, 23, 'Firavun şöyle dedi: "Âlemlerin Rabbi dediğin nedir ki?"'),
(2956, 26, 24, 'Musa cevap olarak: "Eğer işin gerçeğini düşünüp anlayan kişiler olsanız (itiraf edersiniz ki) O, göklerin, yerin ve ikisi arasında bulunan her şeyin Rabbi\'dir."'),
(2957, 26, 25, '(Firavun) etrafında bulunanlara: "İşitmiyor musunuz?" dedi.'),
(2958, 26, 26, 'Musa dedi ki: "O sizin de Rabbiniz, daha önce ki atalarınızın da Rabbidir."'),
(2959, 26, 27, '(Firavun): "Size gönderilen bu elçiniz mutlaka delidir" dedi.'),
(2960, 26, 28, 'Musa devamla şöyle söyledi: "Şayet aklınızı kullansanız (anlarsınız ki), O, doğunun, batının ve ikisinin arasında bulunanların Rabbidir."'),
(2961, 26, 29, 'Firavun: "Benden başkasını ilâh tutarsan, andolsun ki seni zindana kapatılmışlardan ederim" dedi.'),
(2962, 26, 30, 'Musa sordu: "Sana apaçık bir şey getirmiş olsam da mı?"'),
(2963, 26, 31, 'Firavun: "Haydi getir onu bakayım, doğrulardan isen" dedi.'),
(2964, 26, 32, 'Bunun üzerine Musa asâsını bırakıverdi; apaçık bir ejderha oluverdi.'),
(2965, 26, 33, 'Elini de (koynundan) çekti çıkardı; bakanlara bembeyaz (görünen, nur saçan bir şey) oluverdi.'),
(2966, 26, 34, 'Firavun çevresinde bulunan ileri gelenlere: "Bu dedi, herhalde çok bilgili bir sihirbaz!"'),
(2967, 26, 35, '"Sizi sihriyle yurdunuzdan çıkarmak istiyor. Şimdi ne buyurursunuz?"'),
(2968, 26, 36, 'Dediler ki: "Bunu ve kardeşini eğle, şehirlere de toplayıcılar gönder."'),
(2969, 26, 37, '"Bütün bilgiç sihirbazları sana getirsinler."'),
(2970, 26, 38, 'Böylece, sihirbazlar belli bir günün tayin edilen vaktinde bir araya getirildi.'),
(2971, 26, 39, 'Halka, "Siz de toplanıyor musunuz? (Haydi çabuk olun)" denildi.'),
(2972, 26, 40, '"Üstün gelirlerse herhalde sihirbazlara uyarız" dediler.'),
(2973, 26, 41, 'Sihirbazlar geldiklerinde Firavun\'a "Şayet biz üstün gelirsek, muhakkak bize bir ücret vardır, değil mi?" dediler.'),
(2974, 26, 42, 'Firavun cevaben: "Evet, o takdirde hiç şüphe etmeyin, gözde kimselerden olacaksınız" dedi.'),
(2975, 26, 43, 'Musa onlara "Atın, ne atacaksanız" dedi.'),
(2976, 26, 44, 'Bunun üzerine iplerini ve değneklerini attılar ve "Firavun\'un kudreti hakkı için şüphesiz elbette bizler galip geleceğiz" dediler.'),
(2977, 26, 45, 'Ardından Musa asâsını attı; bir de ne görsünler, onların uydurduklarını yutuyor!'),
(2978, 26, 46, 'Sihirbazlar derhal secdeye kapandılar.'),
(2979, 26, 47, '"İman ettik, dediler, Âlemlerin Rabbine "'),
(2980, 26, 48, '"Musa ve Harun\'un Rabbine!"'),
(2981, 26, 49, 'Firavun (kızgınlık içinde) dedi ki: "Ben size izin vermeden O\'na iman ettiniz ha! Anlaşıldı ki o size sihri öğreten büyüğünüzmüş! Ama şimdi bileceksiniz: Andolsun, ellerinizi ve ayaklarınızı çaprazlama ke stireceğim, hepinizi çarmıha gerdireceğim!"'),
(2982, 26, 50, '"Zararı yok dediler nasıl olsa biz Rabbimize döneceğiz."'),
(2983, 26, 51, '"Herhalde biz müminlerin evveli olduğumuzdan dolayı, Rabbimizin bize mağfiret buyuracağını ümit ederiz"'),
(2984, 26, 52, 'Biz, Musa\'ya: "Kullarımı geceleyin yola çıkar, çünkü takip edileceksiniz" diye vahyettik.'),
(2985, 26, 53, 'Firavun da şehirlere (asker) toplayıcılar gönderdi:'),
(2986, 26, 54, '"Esasen bunlar, sayıları azar azar, bölük pörçük bir cemaattır."'),
(2987, 26, 55, '"(Böyle iken) hakkımızda çok gayz (öfke) besliyorlar. "'),
(2988, 26, 56, '"Biz ise, elbette uyanık (ve tekvücut) bir cemaatız." (diyor ve dedirtiyordu.)'),
(2989, 26, 57, 'Ama (sonunda) biz, onları (Firavun ve kavmini) bahçelerden, pınarlardan,'),
(2990, 26, 58, 'Hazinelerden ve şerefli makamlardan çıkardık.'),
(2991, 26, 59, 'Ve onlara İsrail oğullarını mirasçı yaptık.'),
(2992, 26, 60, 'Derken (Firavun ve adamları) güneş doğmuştu ki, onların ardına düştüler.'),
(2993, 26, 61, 'İki topluluk birbirini görünce, Musa\'nın adamları "Eyvah, yakalandık! dediler.'),
(2994, 26, 62, 'Musa: "Hayır, aslâ! dedi, Rabbim şüphesiz benimledir, bana yolunu gösterecektir."'),
(2995, 26, 63, 'Bunun üzerine Musa\'ya "Vur asân ile denize" diye vahyettik; vurunca bir infilak etti, her bölük koca bir dağ gibi oluverdi,'),
(2996, 26, 64, 'Ötekilerini de buraya yanaştırıvermiştik.'),
(2997, 26, 65, 'Musa ve beraberindekilerin hepsini kurtardık,'),
(2998, 26, 66, 'Sonra da ötekileri suda boğduk.'),
(2999, 26, 67, 'Şüphesiz bunda bir âyet (ibret) vardır; ama çokları iman etmiş değillerdir.'),
(3000, 26, 68, 'Ve şüphesiz, işte o Rabbin, mutlak galip ve engin merhamet sahibidir.'),
(3001, 26, 69, '(Resulüm!) onlara İbrahim\'in kıssasını da naklet.'),
(3002, 26, 70, 'Hani o, babasına ve kavmine, "Neye tapıyorsunuz?" demişti.'),
(3003, 26, 71, '"Birtakım putlara taparız da onlar sayesinde toplanırız" dediler.'),
(3004, 26, 72, 'İbrahim "Peki, dedi, yalvardığınızda onlar sizi işitiyorlar mı?"'),
(3005, 26, 73, '"Veya size fayda veya zararları olur mu?"'),
(3006, 26, 74, '"Yok, dediler, ama biz babalarımızı böyle yapar bulduk."'),
(3007, 26, 75, 'İbrahim dedi ki: "İyi ama, ister sizin, ister önceki atalarınızın olsun, neye taptığınızı (biraz olsun) düşündünüz mü?"'),
(3008, 26, 76, 'İbrahim dedi ki: "İyi ama, ister sizin, ister önceki atalarınızın olsun, neye taptığınızı (biraz olsun) düşündünüz mü?"'),
(3009, 26, 77, '"Hep onlar benim düşmanımdır; ancak âlemlerin Rabbi (benim dostumdur)"'),
(3010, 26, 78, '"O ki, beni yaratan ve bana doğru yolu gösterendir,"'),
(3011, 26, 79, '"Beni yediren, içirendir,"'),
(3012, 26, 80, '"Hastalandığım zaman bana O, şifâ verir."'),
(3013, 26, 81, '"O ki, benim canımı alacak, sonra diriltecektir. "'),
(3014, 26, 82, '"Ve hesap günü, hatamı bağışlayacağını umduğumdur."'),
(3015, 26, 83, '"Ya Rab! Bana hikmet (hüküm) ver ve beni iyiler (zümresin)e kat."'),
(3016, 26, 84, '"Sonra gelecekler içinde beni doğrulukla anılanlardan eyle!"'),
(3017, 26, 85, '"Ve beni naîm (nimeti bol) cennetin varislerinden eyle!"'),
(3018, 26, 86, '"Babamı da bağışla, çünkü o yanlış gidenlerdendir. "'),
(3019, 26, 87, '"(İnsanların) diriltilecekleri gün, beni mahcub etme."'),
(3020, 26, 88, '"O gün ki ne mal fayda verir ne oğullar!"'),
(3021, 26, 89, '"Ancak Allah\'a temiz bir kalple gelenler o günde (kurtuluşa erer)."'),
(3022, 26, 90, '(O gün) Cennet müttakilere yaklaştırılmıştır.'),
(3023, 26, 91, 'Azgınlar için de cehennem hortlatılmıştır.'),
(3024, 26, 92, 'Onlara, "Allah\'ı bırakıp da taptıklarınız, hani nerede? Size yardım edebiliyorlar mı veya kendilerini kurtarabiliyorlar mı?" denilir.'),
(3025, 26, 93, 'Onlara, "Allah\'ı bırakıp da taptıklarınız, hani nerede? Size yardım edebiliyorlar mı veya kendilerini kurtarabiliyorlar mı?" denilir.'),
(3026, 26, 94, 'Ve arkasından hep onlar (putlar ve azgınlar) o cehennemin içine fırlatılmaktadırlar.'),
(3027, 26, 95, 'Ve bütün o İblis orduları onun içinde birbirleriyle çekişirlerken dediler ki:'),
(3028, 26, 96, 'Ve bütün o İblis orduları onun içinde birbirleriyle çekişirlerken dediler ki:'),
(3029, 26, 97, '"Vallahi biz, gerçekten apaçık bir sapıklık içindeymişiz."'),
(3030, 26, 98, '"Çünkü biz sizi, âlemlerin Rabbi ile bir seviyede tutuyorduk."'),
(3031, 26, 99, '"Ve bizi hep o günahkarlar saptırdı."'),
(3032, 26, 100, '"Bak bizim için ne şefaatçiler var,"'),
(3033, 26, 101, '"Ne de yakın bir dost."'),
(3034, 26, 102, '"Ah keşke (dünyaya) bir kere daha dönebilsek de, müminlerden olabilseydik."'),
(3035, 26, 103, 'Şüphesiz bunda bir âyet (alınacak bir ders) vardır; oysa çokları iman etmiş değillerdir.'),
(3036, 26, 104, 'Ve şüphesiz Rabbin, işte O, mutlak galip ve engin merhamet sahibidir.'),
(3037, 26, 105, 'Nuh kavmi de peygamberleri yalancılıkla itham etti.'),
(3038, 26, 106, 'Hani kardeşleri Nuh onlara şöyle demişti: "Siz Allah\'tan korkmaz mısınız?"'),
(3039, 26, 107, '"Haberiniz olsun ki ben, size gönderilmiş güvenilir bir Peygamberim.'),
(3040, 26, 108, '"Gelin artık, Allah\'tan korkun ve bana itaat edin."'),
(3041, 26, 109, '"Buna karşılık ben sizden hiçbir ücret istemiyorum. Benim mükafaatımı verecek olan ancak, âlemlerin Rabbidir."'),
(3042, 26, 110, '"Gelin, artık, Allah\'tan korkun ve bana itaat edin."'),
(3043, 26, 111, '"Â, dediler, senin ardına hep düşük kimseler düşmüşken, biz sana hiç inanır mıyız?"'),
(3044, 26, 112, 'Nuh dedi ki: "Onların yaptıkları hakkında bir bilgim yoktur."'),
(3045, 26, 113, '"Onların hesabı ancak Rabbime aittir. Düşünsenize!"'),
(3046, 26, 114, '"Hem ben iman edenleri kovmaya memur değilim."'),
(3047, 26, 115, '"Ben ancak apaçık bir uyarıcıyım."'),
(3048, 26, 116, 'Dediler ki: "Ey Nuh! Eğer vazgeçmezsen, iyi bil ki, taşa tutulanlardan olacaksın!"'),
(3049, 26, 117, 'Nuh: "Rabbim! dedi, kavmim beni yalancılıkla itham etti."'),
(3050, 26, 118, '"Artık benimle onların arasında sen hükmünü ver. Beni ve beraberimdeki müminleri kurtar."'),
(3051, 26, 119, 'Bunun üzerine biz de onu ve beraberindekileri, o dolu gemide taşıyarak kurtardık.'),
(3052, 26, 120, 'Sonra da arkasında kalanları suda boğduk.'),
(3053, 26, 121, 'Şüphesiz bunda mutlak bir âyet (alınacak ders) vardır; ama çokları iman etmiş değillerdir.'),
(3054, 26, 122, 'Ve şüphesiz Rabbin, işte O, mutlak galip ve engin merhamet sahibidir.'),
(3055, 26, 123, 'Âd (kavmi) de peygamberleri yalancılıkla itham etti.'),
(3056, 26, 124, 'Hani kardeşleri Hûd onlara şöyle demişti: "Siz Allah\'tan korkmaz mısınız?"'),
(3057, 26, 125, '"Haberiniz olsun ki ben, size gönderilmiş, güvenilir bir Peygamberim."'),
(3058, 26, 126, '"Gelin artık Allah\'tan korkun ve bana itaat edin."'),
(3059, 26, 127, '"Buna karşılık ben sizden hiçbir ücret istemiyorum. Benim mükafatımı verecek olan ancak âlemlerin Rabbidir. "'),
(3060, 26, 128, '"Siz her tepeye bir alâmet bina edip eğlenir durur musunuz?"'),
(3061, 26, 129, '"Temelli kalacağınızı umarak sağlam yapılar mı edinirsiniz?"'),
(3062, 26, 130, '"Hem tuttuğunuz zaman merhametsiz zorbalar gibi tutuyorsunuz."'),
(3063, 26, 131, '"Artık Allah\'tan korkun ve bana itaat edin."'),
(3064, 26, 132, '"O Allah\'tan korkun ki, size o bildiğiniz şeyleri vermekte,"'),
(3065, 26, 133, '"Davarlar, oğullar,"'),
(3066, 26, 134, '"Cennet gibi bağlar, bahçeler, pınarlar ihsan etmektedir."'),
(3067, 26, 135, '"Cidden ben sizin hakkınızda büyük bir günün azabından korkuyorum."'),
(3068, 26, 136, '"Dediler ki: "Sen ha vaaz etmişsin, ha vaaz edenlerden olmamışsın, bizce birdir."'),
(3069, 26, 137, '"Bu sırf eskilerin âdetidir."'),
(3070, 26, 138, '"Biz azaba uğratılacak da değiliz."'),
(3071, 26, 139, 'Böylece onu yalancı saydılar; biz de kendilerini helak ettik. Şüphesiz bunda mutlak bir âyet (alınacak bir ders) vardır, ama çokları iman etmiş değillerdir.'),
(3072, 26, 140, 'Ve şüphesiz Rabbin, işte O, mutlak galip ve engin merhamet sahibidir.'),
(3073, 26, 141, 'Semûd (kavmi) de peygamberleri yalancılıkla itham etti.'),
(3074, 26, 142, 'Hani kardeşleri Salih onlara şöyle demişti: "Siz Allah\'tan korkmaz mısınız?"'),
(3075, 26, 143, '"Haberiniz olsun ki ben size gönderilmiş güvenilir bir peygamberim."'),
(3076, 26, 144, '"Gelin artık, Allah\'tan korkun ve bana itaat edin."'),
(3077, 26, 145, '"Buna karşılık ben sizden hiçbir ücret istemiyorum. Benim mükafatımı verecek olan ancak âlemlerin Rabbidir."'),
(3078, 26, 146, '"Siz burada güven içinde bırakılacak mısınız?"'),
(3079, 26, 147, '"Bahçelerin, pınarların içinde,"'),
(3080, 26, 148, '"Ekinlerin, salkımları sarkmış hurmalar arasında,"'),
(3081, 26, 149, 'Ki bir de dağlardan keyifli keyifli kâşâneler oyuyorsunuz."'),
(3082, 26, 150, '"Gelin! Allah\'tan korkun da bana itaat edin."'),
(3083, 26, 151, '"Yeryüzünde bozgunculuk yapıp dirlik düzenlik vermeyen bozguncuların emrine uymayın."'),
(3084, 26, 152, '"Yeryüzünde bozgunculuk yapıp dirlik düzenlik vermeyen bozguncuların emrine uymayın."'),
(3085, 26, 153, '"Sen dediler, olsa olsa iyice büyülenmiş birisin!"'),
(3086, 26, 154, '"Sen de ancak bizim gibi bir beşersin. Eğer doğru söyleyenlerden isen, haydi bize bir âyet (mucize) getir."'),
(3087, 26, 155, 'Salih "İşte (mucize) bu dişi devedir; su içme hakkı (bir gün) onundur, belli bir günün içme hakkı da sizin" dedi.'),
(3088, 26, 156, '"Sakın ona bir kötülükle ilişmeyin, yoksa sizi büyük bir günün azabı yakalayıverir."'),
(3089, 26, 157, 'Derken onu kestiler; fakat pişman da oldular.'),
(3090, 26, 158, 'Çünkü kendilerini azap yakalayıverdi. Şüphesiz bunda bir âyet (alınacak bir ders) vardır, ama çokları iman etmiş değillerdir.'),
(3091, 26, 159, 'Ve şüphesiz Rabbin, işte O mutlak galip ve engin merhamet sahibidir.'),
(3092, 26, 160, 'Lût (kavmi) de peygamberleri yalancılıkla itham etti.'),
(3093, 26, 161, 'Hani kardeşleri Lût onlara şöyle demişti: "Siz Allah\'tan kormaz mısınız?"'),
(3094, 26, 162, '"Haberiniz olsun ki, ben size gönderilmiş güvenilir bir peygamberim."'),
(3095, 26, 163, '"Gelin artık, Allah\'tan korkun ve bana itaat edin."'),
(3096, 26, 164, '"Buna karşılık ben sizden bir ücret istemiyorum. Benim mükafatımı verecek olan ancak âlemlerin Rabbidir."'),
(3097, 26, 165, '"İnsanlar içinden erkeklere mi gidiyorsunuz?"'),
(3098, 26, 166, '"Bırakıyorsunuz da sizler için yarattığı eşleri! Doğrusu siz insanlıktan çıkmış bir kavimsiniz!"'),
(3099, 26, 167, 'Onlar şöyle dediler: "Ey Lût! (Bu davadan) vazgeçmezsen, iyi bilki, sürülenlerden olacaksın."'),
(3100, 26, 168, 'Lût "Doğrusu ben, dedi, sizin bu işinize buğzedenlerdenim."'),
(3101, 26, 169, '"Yâ Rabbi! Beni ve ailemi onların yapageldiklerin(in vebalin)den kurtar."'),
(3102, 26, 170, 'Biz de onu ve ailesinin tamamını kurtardık,'),
(3103, 26, 171, 'Ancak (geride) bir yaşlı kadın kaldı.'),
(3104, 26, 172, 'Sonra geridekilerin hepsini helak ettik.'),
(3105, 26, 173, 'Ve üzerlerine öyle bir yağmur yağdırdık ki, (uyarılanların) o yağmuru ne kötü bir yağmurdu!'),
(3106, 26, 174, 'Şüphesiz bunda bir âyet (alınacak bir ders) vardır. Ama çokları iman etmiş değillerdir.'),
(3107, 26, 175, 'Ve şüphesiz Rabbin, işte O mutlak galip ve engin merhamet sahibidir.'),
(3108, 26, 176, 'Eyke halkı da peygamberleri yalancılıkla itham etti.'),
(3109, 26, 177, 'Hani Şuayb onlara şöyle demişti: "Siz Allah\'tan korkmaz mısınız?"'),
(3110, 26, 178, '"Haberiniz olsun ki ben size gönderilmiş güvenilir bir peygamberim."'),
(3111, 26, 179, '"Gelin, Allah\'tan korkun ve bana itaat edin."'),
(3112, 26, 180, '"Buna karşılık ben sizden bir ücret istemiyorum. Benim mükafatımı verecek olan yalnız âlemlerin Rabbidir."'),
(3113, 26, 181, '"Ölçeği tam ölçün de hak yiyenlerden olmayın."'),
(3114, 26, 182, '"Ve doğru terazi ile tartın."'),
(3115, 26, 183, '"Halkın eşyalarını değerinden düşürmeyin. Yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın."'),
(3116, 26, 184, '"O sizi ve sizden önceki nesilleri yaratan Allah\'tan korkun."'),
(3117, 26, 185, 'Onlar şöyle dediler: "Sen, olsa olsa iyice büyülenmiş birisin."'),
(3118, 26, 186, '"Sen de bizim gibi bir beşerden başka nesin? Bil ki, biz seni ancak yalancılardan biri sayıyoruz."'),
(3119, 26, 187, '"Şayet doğru sözlülerden isen, üstümüze gökten bir parça düşürüver."'),
(3120, 26, 188, 'Şuayb, "Rabbim, yaptıklarınızı en iyi bilendir" dedi.'),
(3121, 26, 189, 'Hülasa, onu yalancı saydılar da kendilerini o gölge gününün azabı yakalayıverdi. O cidden büyük bir günün azabı idi!'),
(3122, 26, 190, 'Şüphesiz bunda bir âyet (alınacak bir ders) vardır. Ama çokları iman etmiş değillerdir.'),
(3123, 26, 191, 'Ve şüphesiz Rabbin, işte O, mutlak galip ve engin merhamet sahibidir.'),
(3124, 26, 192, 'Ve muhakkak ki bu (Kur\'ân) âlemlerin Rabbinin indirmesidir.'),
(3125, 26, 193, '(Resulüm!) Onu Rûhu\'lemin (Cebrail) indirdi;'),
(3126, 26, 194, 'Uyarıcılardan olasın diye senin kalbin üzerine;'),
(3127, 26, 195, 'Açık parlak bir Arapça lisan ile.'),
(3128, 26, 196, 'O, şüphesiz daha öncekilerin kitaplarında da vardı.'),
(3129, 26, 197, 'İsrailoğulları bilginlerinin onu bilmesi, onlar için bir âyet (delil) değil midir?'),
(3130, 26, 198, 'Biz onu Arapça bilmeyenlerden birine indirseydik de, bunu o okusaydı, yine de ona iman etmezlerdi.'),
(3131, 26, 199, 'Biz onu Arapça bilmeyenlerden birine indirseydik de, bunu o okusaydı, yine de ona iman etmezlerdi.'),
(3132, 26, 200, 'Böylece onu günahkarların kalplerine soktuk. (okuyup anladılar, ama yine de) acıklı azabı görünceye kadar ona iman etmezler.'),
(3133, 26, 201, 'Böylece onu günahkarların kalplerine soktuk. (okuyup anladılar, ama yine de) acıklı azabı görünceye kadar ona iman etmezler.'),
(3134, 26, 202, 'İşte bu (azab) onlara, kendileri farkında olmadan, ansızın geliverecektir.'),
(3135, 26, 203, 'O zaman "Bize (iman etmemiz için) mühlet verilir mi acaba?... diyeceklerdir.'),
(3136, 26, 204, '(Oysa dünyada iken) Onlar bizim azabımızı çarçabuk istiyorlardı.'),
(3137, 26, 205, 'Gördün ya artık onlara senelerce zevk ettirsek,'),
(3138, 26, 206, 'Sonra kendilerine vaad edilen (azab) gelip çatarsa,'),
(3139, 26, 207, 'O yaşadıkları zevkin kendilerine hiçbir faydası olmayacaktır.'),
(3140, 26, 208, 'Bununla birlikte, biz hangi memleketi helak ettikse muhakkak onu uyarıcı (peygamberleri) olmuştur.'),
(3141, 26, 209, '(Onlar) ihtar edilmiştir ve biz zulmetmiş değiliz.'),
(3142, 26, 210, 'Onu (Kur\'ân\'ı) şeytanlar indirmedi.'),
(3143, 26, 211, 'Bu onlara hem yaraşmaz hem güçleri yetmez.'),
(3144, 26, 212, 'Şüphesiz onlar vahyi işitmekten uzak tutulmuşlardır.'),
(3145, 26, 213, 'O halde sakın Allah ile beraber başka tanrıya kulluk edip yalvarma, yoksa azaba uğratılanlardan olursun.'),
(3146, 26, 214, '(Önce) en yakın hısımlarını uyar.'),
(3147, 26, 215, 'Ve sana uyan müminlere kanadını indir.'),
(3148, 26, 216, 'Şayet sana karşı gelirlerse, de ki: "Ben sizin yaptıklarınızdan muhakkak uzağım."'),
(3149, 26, 217, 'Sen O, mutlak galip ve engin merhamet sahibine güvenip dayan.'),
(3150, 26, 218, 'O ki, (gece namaza) kalktığın zaman seni görüyor.'),
(3151, 26, 219, 'Ve secde edenler arasında dolaşmanı da (görüyor.)'),
(3152, 26, 220, 'Çünkü her şeyi işiten, her şeyi bilen O\'dur.'),
(3153, 26, 221, 'Şeytanların kime ineceğini size haber vereyim mi?'),
(3154, 26, 222, 'Onlar, günaha, iftiraya düşkün olan herkesin üzerine inerler.'),
(3155, 26, 223, 'Onlar, (şeytanlara) kulak verirler ve onların çoğu yalancıdır.'),
(3156, 26, 224, 'Şairler(e gelince), onlara da sapıklar uyar.'),
(3157, 26, 225, 'Onların her vadide şaşkın şaşkın dolaştıklarını ve gerçekte yapmadıkları şeyleri söylediklerini görmedin mi?'),
(3158, 26, 226, 'Onların her vadide şaşkın şaşkın dolaştıklarını ve gerçekte yapmadıkları şeyleri söylediklerini görmedin mi?'),
(3159, 26, 227, 'Ancak iman edip iyi ameller işleyenler, Allah\'ı çok çok ananlar ve haksızlığa uğratıldıklarında kendilerini savunanlar müstesna; haksızlık edenler, hangi dönüşe (hangi akibete) döndürüleceklerini yakında bileceklerdir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'Tâ, Sîn. Bunlar sana, Kur\'ân\'ın ve apaçık bir kitabın âyetleridir.'),
(3161, 27, 2, 'İman eden müminler için hidayet rehberi ve müjdeci olmak üzere.'),
(3162, 27, 3, 'Ki o (müminler) namazı dosdoğru kılarlar, zekatı verirler ve ahirete de kesin olarak iman ederler.'),
(3163, 27, 4, 'Şüphesiz biz, ahirete inanmayanların işlerini kendilerine süslü gösterdik de onlar ilerisini göremezler, kalpleri körelmiştir.'),
(3164, 27, 5, 'İşte bunlar, kendileri için oldukça ağır bir azab bulunan kimselerdir, ahirette en çok ziyana uğrayacaklar da onlardır.'),
(3165, 27, 6, '(Resulüm!) Şüphesiz ki bu Kur\'ân, sana hikmet sahibi ve her şeyi bilen Allah tarafından indirilmektedir.'),
(3166, 27, 7, 'Hani Musa, ailesine şöyle demişti: "Gerçekten ben bir ateş gördüm, (gidip) size oradan bir haber getireceğim yahut bir kor ateş getireyim, umarım ki ısınırsınız."'),
(3167, 27, 8, 'Oraya geldiğinde şöyle seslenilmişti: "Ateşin bulunduğu yerdeki ve çevresindekiler mübarek kılınmıştır! Âlemlerin Rabbi olan Allah, eksikliklerden münezzehtir!"'),
(3168, 27, 9, '"Ey Musa! İyi bil ki, ben, mutlak galip ve hikmet sahibi olan Allah\'ım!"'),
(3169, 27, 10, '"Asânı at!" (Asâyı atıp) onu yılan gibi deprenir görünce dönüp arkasına bakmadan kaçtı. (Dedik ki): "Ey Musa korkma! Çünkü benim huzurumda peygamberler korkmaz."'),
(3170, 27, 11, '"Ancak, kim haksızlık yapar, sonra yaptığı kötülüğü iyiliğe çevirirse, bilsin ki ben (ona karşı da) çok bağışlayıcıyım, çok merhamet sahibiyim."'),
(3171, 27, 12, '"Elini koynuna sok; kusursuz bembeyaz çıkacaktır. Dokuz mucize ile Firavun ve kavmine (git), çünkü onlar yoldan çıkmış bir kavim olmuşlardır."'),
(3172, 27, 13, 'Bu şekilde âyetlerimiz onların gözleri önüne serilince, "Bu apaçık bir sihirdir" dediler.'),
(3173, 27, 14, 'Ve vicdanları bunlar(ın doğruluğun)a tam bir kanaat getirdiği halde, zulüm ve kibirlerinden ötürü onları bile bile inkâr ettiler. Bozguncuların sonunun nice olduğuna bir bak!'),
(3174, 27, 15, 'Andolsun ki biz, Davud\'a ve Süleyman\'a bir ilim verdik. Onlar: "Bizi mümin kullarının birçoğundan üstün kılan Allah\'a hamd olsun" dediler.'),
(3175, 27, 16, 'Süleyman Davud\'a varis olup dedi ki: "Ey insanlar! Bize kuş dili öğretildi ve bize her şeyden (nasip) verildi. Doğrusu bu apaçık bir lütuftur."'),
(3176, 27, 17, 'Cinlerden, insanlardan ve kuşlardan müteşekkil orduları Süleyman\'ın hizmetinde toplandı, hepsi bir arada (onun tarafından) düzenli olarak sevkediliyordu.'),
(3177, 27, 18, 'Nihayet karınca vâdisine geldikleri zaman, bir karınca: "Ey karıncalar! Yuvalarınıza girin; Süleyman ve ordusu farkına varmadan sizi ezmesin!" dedi.'),
(3178, 27, 19, '(Süleyman) onun sözüne gülümseyerek dedi ki: "Ey Rabbim! Bana ve ana babama verdiğin nimete şükretmemi ve hoşnut olacağın iyi iş yapmamı gönlüme getir. Rahmetinle, beni iyi kulların arasına kat."'),
(3179, 27, 20, '(Süleyman) Kuşları gözden geçirdikten sonra şöyle dedi: "Hüdhüd\'ü niçin göremiyorum? Yoksa kayıplara mı karıştı?"'),
(3180, 27, 21, '"Ya bana (mazeretini gösteren) apaçık bir delil getirecek, ya da onu şiddetli bir azaba uğratacağım, yahut boğazlıyacağım!"'),
(3181, 27, 22, 'Çok geçmeden (Hüdhüd) gelip: "Ben, dedi, senin bilmediğin bir şeyi öğrendim. Sebe\'den sana çok doğru (ve önemli) bir haber getirdim.'),
(3182, 27, 23, '"Gerçekten, onlara (Sebelilere) hükümdarlık eden, kendisine her türlü imkan verilmiş ve büyük bir tahta sahip olan bir kadınla karşılaştım."'),
(3183, 27, 24, '"Onun ve kavminin, Allah\'ı bırakıp güneşe secde ettiklerini gördüm. Şeytan, kendilerine yaptıklarını süslü göstermiş de onları doğru yoldan alıkoymuş. Bunun için hidayete giremiyorlar."'),
(3184, 27, 25, '"Göklerde ve yerde gizleneni açığa çıkaran, gizlediğinizi ve açıkladığınızı bilen Allah\'a secde etmezler."'),
(3185, 27, 26, '"(Halbuki) O büyük Arş\'ın sahibi olan Allah\'tan başka tapılacak yoktur."'),
(3186, 27, 27, '(Süleyman Hüdhüd\'e) dedi ki: "Doğru mu söyledin, yoksa yalancılardan mısın, bakacağız."'),
(3187, 27, 28, '"Şu mektubumu götür, onu kendilerine ver, sonra onlardan biraz çekil de, ne sonuca varacaklarına bak."'),
(3188, 27, 29, '(Süleyman\'ın mektubunu alan Sebe melikesi): "Beyler, ulular! Bana çok önemli bir mektup bırakıldı" dedi.'),
(3189, 27, 30, '"Mektup Süleyman\'dandır, Rahmân ve Rahîm Allah\'ın adıyla (başlamakta)dır. "'),
(3190, 27, 31, '"Bana karşı baş kaldırmayın, teslimiyet göstererek bana gelin diye (yazmaktadır)."'),
(3191, 27, 32, '(Sonra Melike) dedi ki: "Beyler, ulular! Bu işimde bana bir fikir verin. (Bilirsiniz) siz yanımda olmadan hiçbir işi kestirip atmam."'),
(3192, 27, 33, 'Onlar, şöyle cevap verdiler: "Biz güçlü kuvvetli kimseleriz, zorlu savaş erbabıyız, buyruk ise senindir; artık ne emredeceğini düşün taşın."'),
(3193, 27, 34, 'Melike, "Hükümdarlar bir memlekete girdiler mi orayı perişan ederler ve halkının ulularını hakir hâle getirirler. (Herhalde) Onlar da böyle yapacaklardır" dedi.'),
(3194, 27, 35, '"Ben (şimdi) onlara bir hediye göndereyim de, bakayım elçiler ne (gibi bir sonuç) ile dönecekler."'),
(3195, 27, 36, '(Elçiler, hediyelerle) gelince Süleyman şöyle dedi: "Siz bana mal ile yardım mı etmek istiyorsunuz? Allah\'ın bana verdiği, size verdiğinden daha iyidir. Ama siz, hediyenizle böbürlenirsiniz."'),
(3196, 27, 37, '"(Ey elçi) Onlara var (söyle); iyi bilsinler ki, kendilerine asla karşı koyamayacakları ordularla gelir, onları, muhakkak surette hor ve hakir halde oradan çıkarırız!"'),
(3197, 27, 38, '(Sonra Süleyman müşavirlerine) dedi ki: "Ey ulular! Onlar teslimiyet gösterip bana gelmeden önce, hanginiz o Melike\'nin tahtını bana getirebilir?"'),
(3198, 27, 39, 'Cinlerden bir ifrit, "Sen makamından kalkmadan ben onu sana getiririm. Gerçekten bu işe gücüm ve güvenim var." dedi.'),
(3199, 27, 40, 'Kitaptan ilmi olan kimse ise, "Gözünü açıp kapamadan, ben onu sana getiririm" dedi. (Süleyman) onu (Melike\'nin tahtını) yanıbaşına yerleşivermiş görünce, "Bu, dedi, şükür mü edeceğim, yoksa nankörlük mü edeceğim diye beni sınamak üzere Rabbimin (gösterdiği) lütfundandır. Şükreden ancak kendisi için şükretmiş olur; nankörlük edene gelince, o bilsin ki Rabbim müstağnidir, çok kerem sahibidir."'),
(3200, 27, 41, '(Süleyman devamla) dedi ki: "Onun tahtını bilemeyeceği bir vaziyete sokun; getirin bakalım tanıyabilecek mi, yoksa tanıyamayanlardan mı olacak?"'),
(3201, 27, 42, 'Melike gelince, "Senin tahtın da böyle mi?" dendi. O şöyle cevap verdi: "Tıpkı o! Zaten bize daha önce bilgi verilmiş ve biz teslimiyet göstermiştik."'),
(3202, 27, 43, 'O\'nu, Allah\'tan başka taptığı şeyler alıkoymuştu. Çünkü kendisi inkârcı bir kavimdendi.'),
(3203, 27, 44, 'Ona "köşke gir!" dendi. Melike onu görünce derin bir su sandı ve eteğini çekti. Süleyman "Bu billurdan yapılmış, şeffaf bir zemindir" dedi. Melike dedi ki: "Rabbim! Ben gerçekten kendime yazık etmiştim. Süleyman\'ın maiyyetinde, âlemlerin Rabbi olan Allah\'a teslim oldum."'),
(3204, 27, 45, 'Andolsun ki, Allah\'a ibadet edin diye Semud\'a da kardeşleri Salih\'i gönderdik. Hemen birbirleriyle çekişen iki zümre oluverdiler.'),
(3205, 27, 46, 'Salih dedi ki: "Ey benim kavmim! İyilik dururken niçin kötülüğe koşuyorsunuz? Ne olur Allah\'a istiğfar etseniz, belki rahmetine ulaşırdınız."'),
(3206, 27, 47, 'Cevap verdiler: "Senin ve beraberindekilerin yüzünden uğursuzluğa uğradık." Salih: "Size çöken uğursuzluk (sebebi) Allah katında (yazılı)dır. Belki siz imtihana çekilen bir kavimsiniz" dedi.'),
(3207, 27, 48, 'O şehirde dokuz çete vardı ki, bunlar yeryüzünde bozgunculuk yapıyorlar, iyilik tarafına hiç yanaşmıyorlardı.'),
(3208, 27, 49, 'Allah\'a and içerek birbirlerine şöyle dediler: "Gece ona ve ailesine baskın yapalım; sonra da velisine, \'Biz o ailenin yok edilişi sırasında orada değildik, inanın ki doğru söylüyoruz\' diyelim."'),
(3209, 27, 50, 'Onlar böyle bir tuzak kurdular, biz de kendileri farkında olmadan onların planlarını altüst ettik.'),
(3210, 27, 51, 'İşte bak! Tuzaklarının akibeti nice oldu: Onları da, kavimlerini de toptan helak ettik.'),
(3211, 27, 52, 'İşte haksızlıkları yüzünden çökmüş evleri! Bilen bir kavim için elbette bunda bir ibret vardır.'),
(3212, 27, 53, 'İman edip Allah\'a karşı gelmekten sakınanları da kurtardık.'),
(3213, 27, 54, 'Lût\'u da (peygamber olarak kavmine gönderdik). O, kavmine şöyle demişti: "Göz göre göre hala o hayasızlığı yapacak mısınız?"'),
(3214, 27, 55, '"Siz ille de kadınları bırakıp şehvetle erkeklere yaklaşacak mısınız? Doğrusu siz beyinsizlikte devam edegelen bir kavimsiniz!"'),
(3215, 27, 56, 'Buna kavminin cevabı sadece: "Lût ailesini memleketinizden çıkarın; baksanıza onlar (bizim yaptıklarımızdan) temiz kalmak isteyen insanlarmış!" demelerinden ibaret oldu.'),
(3216, 27, 57, 'Bunun üzerine onu ve ailesini kurtardık. Yalnız karısı müstesna; onun geride (azaba uğrayanların içinde) kalmasını takdir ettik.'),
(3217, 27, 58, 'Onların üzerlerine öyle bir yağmur indirdik ki, ne kötü idi uyarılanların yağmuru!'),
(3218, 27, 59, '(Resulüm!) de ki: "Hamd olsun Allah\'a, selam olsun seçkin kıldığı kullarına. Allah mı hayırlı, yoksa O\'na koştukları ortaklar mı?"'),
(3219, 27, 60, '(Onlar mı hayırlı) yoksa, gökleri ve yeri yaratan, gökten size su indiren mi? Çünkü biz onunla, bir ağacını bile bitirmeye gücünüzün yetmediği güzel güzel bahçeler bitirmişizdir. Allah\'la beraber başka bir ilâh mı var! Doğrusu onlar sapıklıkta devameden bir güruhtur.'),
(3220, 27, 61, '(Onlar mı hayırlı) yoksa, yeryüzünü oturmaya elverişli kılan, aralarında nehirler akıtan, onun için sabit dağlar yaratan, iki deniz arasına engel koyan mı? Allah\'ın yanında başka bir ilâh mı var? Hayır onların çoğu (hakikatları) bilmiyorlar.'),
(3221, 27, 62, '(Onlar mı hayırlı) yoksa, kendine yalvardığı zaman bunalmışa karşılık veren ve başındaki sıkıntıyı gideren, sizi yeryüzünün hakimleri yapan mı? Allah\'ın yanında başka bir ilâh mı var? Ne kıt düşünüyorsunuz!'),
(3222, 27, 63, '(Onlar mı hayırlı) yoksa, karanın ve denizin karanlıkları içinde size yolu bulduran, rahmetinin (yağmurun) önünde rüzgarları müjdeci olarak gönderen mi? Allah\'ın yanında başka bir ilâh mı var? Allah onların koştukları ortaklardan çok yücedir, münezzehtir.'),
(3223, 27, 64, '(Onlar mı hayırlı) yoksa, önce yaratan, sonra yaratmayı tekrar eden ve sizi hem gökten, hem yerden rızıklandıran mı? Allah ile beraber başka bir ilâh mı var? De ki: Eğer doğru söylüyorsanız, siz kesin delilinizi getirin haydi!'),
(3224, 27, 65, 'De ki: Göklerde ve yerde Allah\'tan başka kimse gaybı bilmez. Ne zaman diriltileceklerini de bilmezler.'),
(3225, 27, 66, 'Fakat ahiret hakkında bilgiler onlara ardarda gelmektedir. Ama onlar bundan bir şüphe içindedirler. Çünkü onlar bundan yana kördürler.'),
(3226, 27, 67, 'İnkârcılar dediler ki: "Sahi biz ve atalarımız toprak olduktan sonra gerçekten (diriltilip) çıkarılacak mıyız?"'),
(3227, 27, 68, '"And olsun ki, bu tehdit bize yapıldığı gibi, daha önce atalarımıza da yapılmıştır. Bu öncekilerin masallarından başka bir şey değildir."'),
(3228, 27, 69, 'De ki: "Hele bir yeryüzünde gezin de, günahkarların sonu nice oldu, bir bakın!"'),
(3229, 27, 70, '(Habibim!) Onlara karşı mahzun olma, kurmakta oldukları tuzaklardan ötürü de sıkıntı duyma!'),
(3230, 27, 71, 'Bir de, "Eğer doğru söylüyorsanız bu vaad (ettiğiniz azab) hani, ne zaman?" derler.'),
(3231, 27, 72, 'De ki: "Çabucak gelmesini istediğiniz şeyin (azabın) bir kısmı herhalde yakında ensenize binecektir."'),
(3232, 27, 73, 'Şüphesiz Rabbin, insanlara karşı lütuf sahibidir; fakat insanların çoğu şükretmezler.'),
(3233, 27, 74, 'Rabbin elbette onların sinelerinin gizlediklerini de, açığa vurduklarını da bilir.'),
(3234, 27, 75, 'Gökte ve yerde gizli hiçbir şey yoktur ki apaçık bir kitapta (Lehvi mahfuzda) bulunmasın.'),
(3235, 27, 76, 'Haberiniz olsun ki bu Kur\'ân, İsrail oğullarına, hakkında ihtilaf edegeldikleri şeylerin pek çoğunu anlatmaktadır.'),
(3236, 27, 77, 'Ve o, müminler için gerçekten bir hidayet rehberi ve rahmettir.'),
(3237, 27, 78, 'Rabbin şüphesiz, onlar arasında kendi hükmünü verecektir. O, mutlak galiptir, hikmet sahibidir.'),
(3238, 27, 79, 'Ve o halde sen Allah\'a güven. Çünkü sen, apaçık hakikatin üzerindesin.'),
(3239, 27, 80, 'Bil ki sen, ölülere işittiremezsin, arkasını dönüp kaçmakta olan sağırlara da daveti duyuramazsın.'),
(3240, 27, 81, 'Sen körleri sapıklıklarından çevirip doğru yola getirecek değilsin. Ancak (gönülden) teslim olarak âyetlerimize iman edenlere duyurabilirsin.'),
(3241, 27, 82, 'Söylenen başlarına geleceği vakit, bunlar için yerden bir "dâbbe" (canlı) çıkarırız ki bu, onlara insanların âyetlerimize kesin bir iman getirmemiş olduklarını söyler.'),
(3242, 27, 83, 'Ve her ümmetin âyetlerimizi yalan sayanlarından bir cemaati toplayacağımız gün, artık onlar bir arada tutulup (hesap yerine) sevkedilirler.'),
(3243, 27, 84, 'Nihayet (oraya) geldikleri vakit Allah buyurur: "Siz benim âyetlerimi, ne olduğunu kavramadan yalan saydınız öyle mi? Yoksa yaptığınız başka neydi?"'),
(3244, 27, 85, 'Yaptıkları haksızlıktan dolayı, o söz gerçekleşmiştir; artık onlar konuşamazlar.'),
(3245, 27, 86, 'Görmediler mi ki, dinlensinler diye geceyi yarattık ve (çalışsınlar diye) gündüzü apaydınlık yaptık. İman eden bir kavim için elbette bunda ibretler vardır.'),
(3246, 27, 87, 'Sûr\'a üfürüldüğü gün Allah\'ın diledikleri müstesna göklerde ve yerde bulunanlar hep dehşete kapılır. Hepsi boyunları bükük olarak O\'na gelirler.'),
(3247, 27, 88, 'Sen dağları görürsün de, yerinde durur sanırsın. Oysa onlar bulutun yürümesi gibi yürümektedirler. Bu, her şeyi sapasağlam yapan Allah\'ın sanatıdır. Şüphesiz ki O, yaptıklarınızdan tamamıyla haberdardır.'),
(3248, 27, 89, 'Kim iyilikle gelirse, ona daha iyisi verilir ve onlar o gün korkudan da emin kalırlar.'),
(3249, 27, 90, 'Her kim de kötülükle gelirse artık yüzleri ateşte sürtülür. "Başka değil ancak yaptığınız amellerin cezasını çekeceksiniz." (denir).'),
(3250, 27, 91, '(De ki): "Ben ancak her şeyin sahibi olan ve burayı kutlu kılan bu şehrin (Mekke\'nin) Rabbine kulluk etmekle emrolundum. Yine bana müslümanlardan olmam emredildi."'),
(3251, 27, 92, '"Ve Kur\'ân\'ı okumam emredildi." Artık kim doğru yola gelirse, yalnız kendisi için gelmiş olur; kim de saparsa ona de ki: "Ben sadece uyarıcılardanım."'),
(3252, 27, 93, 'Ve şöyle de: Hamd, Allah\'a mahsustur. O, âyetlerini size gösterecek, siz de onları görüp tanıyacaksınız. Rabbin, yaptıklarınızdan habersiz değildir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'Tâ, Sîn, Mîm.'),
(3254, 28, 2, 'Bunlar, apaçık kitabın âyetleridir.'),
(3255, 28, 3, 'İman edecek bir kavim için Musa ile Firavun\'un haberlerinden bir kısmını sana dosdoğru okuyacağız.'),
(3256, 28, 4, 'Çünkü Firavun, (Mısır) toprağında gerçekten azmış, halkını parça parça etmişti. Onlardan bir zümreyi güçsüz buluyor, bunların oğullarını boğazlıyor, kızlarını ise sağ bırakıyordu. Belli ki o bozgunculardandı.'),
(3257, 28, 5, 'Biz ise istiyorduk ki, o yerde güçsüz düşürülenlere lütufta bulunalım, onları önderler yapalım, onlara (ötekilerin) yerini aldıralım.'),
(3258, 28, 6, 'Ve o yerde onları hakim kılalım, Firavun ile Hâmân ve ordularına, onlardan çekinmekte oldukları şeyi gösterelim.'),
(3259, 28, 7, 'O esnada Musa\'nın anasına "Onu emzir, kendisine zarar geleceğinden kaygılandığında onu denize (Nil nehrine) bırakıver, hiç korkup kaygılanma, çünkü biz onu tekrar sana vereceğiz ve onu peygamberlerden biri yapacağız" diye bildirdik.'),
(3260, 28, 8, 'Nihayet Firavun ailesi onu yitik olarak aldı. Çünkü o, sonunda kendileri için bir düşman ve bir tasa olacaktı. Şüphesiz Firavun ile Hâmân ve askerleri yanılıyorlardı.'),
(3261, 28, 9, 'Firavun\'un karısı (sepetin içinden çocuk çıkınca kocasına), "İkimizin de gözü aydın! Onu öldürmeyin, belki bize faydası dokunur, ya da onu evlad ediniriz" dedi. Halbuki onlar işin sonunu sezemiyorlardı.'),
(3262, 28, 10, 'Musa\'nın anasının yüreği (tasadan) bomboş kalıverdi. Eğer biz, (vaadimize) inananlardan olması için onun kalbini pekiştirmemiş olsaydık, neredeyse işi meydana çıkaracaktı.'),
(3263, 28, 11, 'Annesi Musa\'nın ablasına, "Onun izini takip et" dedi. O da, onlar farkına varmadan uzaktan kardeşini gözetledi.'),
(3264, 28, 12, 'Biz (annesine geri vermezden) daha önce, onun süt analarının sütünü kabulüne müsade etmedik. Bunun üzerine ablası, "Size, onun bakımını sizin namınıza üstlenecek, hem de ona iyi davranacak bir aile göstereyim mi?" dedi.'),
(3265, 28, 13, 'Böylelikle biz onu, gözü aydın olsun, gam çekmesin ve Allah\'ın vaadinin gerçek olduğunu bilsin, diye anasına geri verdik. Fakat yine de pek çoğu (bunu) bilmezler.'),
(3266, 28, 14, 'Musa yiğitlik çağına girip olgunlaşınca, biz ona hikmet ve ilim verdik. İşte güzel davrananları biz böyle mükafatlandırırız.'),
(3267, 28, 15, 'Musa, halkının habersiz olduğu bir sırada şehre girdi. Orada, biri kendi tarafından diğeri düşman tarafından olan iki adamı birbirleriyle döğüşür buldu. Kendi tarafı olan, düşmana karşı ondan yardım diledi. Musa da ötekine bir yumruk indirip onun ölümüne sebep oldu. "Bu, şeytan işidir. O, gerçekten saptırıcı, apaçık bir düşmandır" dedi.'),
(3268, 28, 16, 'Musa, "Rabbim! Doğrusu kendimi ziyana uğrattım. Beni bağışla!" dedi; Allah da, onu bağışladı. Çünkü, çok bağışlayıcı, çok merhamet edici olan ancak O\'dur.'),
(3269, 28, 17, 'Musa, "Rabbim! Bana lutfettiğin nimetlere andolsun ki, artık suçlulara asla arka olmayacağım" dedi.'),
(3270, 28, 18, 'Şehirde korku içinde, (etrafı) gözetleyerek sabahladı. Bir de ne görsün, dün kendisinden yardım isteyen kimse feryad ederek yine ondan imdat istiyor. Musa ona dedi ki: "Doğrusu sen, besbelli bir azgınsın!"'),
(3271, 28, 19, 'Musa, ikisinin de düşmanı olan adamı yakalamak isteyince, o adam dedi ki: "Ey Musa! Dün bir cana kıydığın gibi, bana da mı kıymak istiyorsun? Demek arabuluculardan olmak istemiyor da, bu yerde ille yaman bir zorba olmayı arzuluyorsun sen!"'),
(3272, 28, 20, 'Şehrin öbür ucundan bir adam geldi ve dedi ki: "Ey Musa! İleri gelenler seni öldürmek için hakkında müzakere ediyorlar. Derhal (buradan) çık! İnan ki ben senin iyiliğini isteyenlerdenim."'),
(3273, 28, 21, 'Musa korka korka, (etrafı) gözetleyerek oradan çıktı. "Rabbim! Beni zalimler güruhundan kurtar" dedi.'),
(3274, 28, 22, 'Medyen\'e doğru yöneldiğinde: "Umarım Rabbim beni doğru yola iletir." dedi.'),
(3275, 28, 23, 'Musa, Medyen suyuna varınca, orada (hayvanlarını) sulayan bir çok insan buldu. Onların gerisinde de (hayvanlarını suyun olduğu yerden) geri çeken iki kadın gördü. Onlara "Derdiniz nedir?" dedi. Şöyle cevap verdiler: "Çobanlar sulayıp çekilmeden biz ( onların içine sokulup hayvanlarımızı) sulamayız; babamız da çok yaşlıdır. "'),
(3276, 28, 24, 'Bunun üzerine Musa, onların davarlarını suladı. Sonra gölgeye çekildi ve "Rabbim! Doğrusu bana indireceğin her hayra muhtacım" dedi.'),
(3277, 28, 25, 'Derken, o iki kadından biri utana utana yürüyerek ona geldi. "Babam, dedi, bizim yerimize (hayvanları) sulamanın karşılığını ödemek için seni çağırıyor." Musa, ona (Hz. Şuayb\'a) gelip başından geçeni anlatınca o, "korkma, o zalim kavimden kurtuldun"dedi.'),
(3278, 28, 26, '(Şuayb\'ın) iki kızından biri: "Babacığım! Onu ücretle (çoban) tut. Çünkü ücretle istihdam edeceğin en iyi kimse, bu güçlü ve güvenilir adamdır" dedi.'),
(3279, 28, 27, '(Şuayb) Dedi ki: "Bana sekiz yıl çalışmana karşılık şu iki kızımdan birini sana nikahlamak istiyorum. Eğer on yıla tamamlarsan artık o kendinden; yoksa sana ağırlık vermek istemem. İnşaallah beni iyi kimselerden bulacaksın."'),
(3280, 28, 28, 'Musa şöyle cevap verdi: "Bu seninle benim aramdadır. Bu iki süreden hangisini doldurursam doldurayım demek ki, bana karşı husumet yok. Söylediklerimize Allah vekildir."'),
(3281, 28, 29, 'Artık Musa süreyi doldurup ailesiyle yola çıkınca, Tûr tarafından bir ateş gördü. Ailesine: "Siz (burada) bekleyin; ben bir ateş gördüm, belki oradan size bir haber, yahut ısınmanız için o ateşten bir parça getiririm" dedi.'),
(3282, 28, 30, 'Oraya gelince, o mübarek yerdeki vâdinin sağ kıyısından, (oradaki) ağaç tarafından kendisine şöyle seslenildi: "Ey Musa! Bil ki ben, bütün âlemlerin Rabbi olan Allah\'ım."'),
(3283, 28, 31, 'Ve "Asânı at!" denildi. Musa (attığı) asâyı yılan gibi debrenir görünce, dönüp arkasına bakmadan kaçtı. "Ey Musa! Beri gel, korkma. Çünkü sen emniyette olanlardansın." (buyuruldu.)'),
(3284, 28, 32, '"Elini koynuna sok, kusursuz bembeyaz çıkacaktır. Korkudan (açılan) kollarını kendine çek. İşte bu ikisi Firavun ve onun adamlarına karşı Rabbin tarafından iki kesin delildir. Çünkü onlar, yoldan çıkan bir kavim olmuşlardır." (diye seslenildi)'),
(3285, 28, 33, 'Musa dedi ki: "Rabbim! Ben onlardan birini öldürmüştüm, beni öldürmelerinden korkuyorum."'),
(3286, 28, 34, '"Kardeşim Harun\'un dili benimkinden daha düzgündür. Onu da beni doğrulayan bir yardımcı olarak benimle birlikte gönder. Zira bana yalancılık ithamında bulunmalarından endişe ediyorum."'),
(3287, 28, 35, 'Allah buyurdu: "Seni kardeşinle destekliyeceğiz ve size öyle bir kudret vereceğiz ki, âyetlerimiz sayesinde onlar size erişemeyecekler. Siz ve size tabi olanlar üstün geleceksiniz."'),
(3288, 28, 36, 'Musa onlara apaçık âyetlerimizi getirince, "Bu, olsa olsa uydurulmuş bir sihirdir. Biz önceki atalarımızdan böylesini işitmemiştik" dediler.'),
(3289, 28, 37, 'Musa şöyle dedi: "Rabbim, kendi katından kimin hidayet rehberi getirdiğini ve hayırlı akibetin kime nasip olacağını en iyi bilendir. Muhakkak ki zalimler, kurtuluşa eremezler."'),
(3290, 28, 38, 'Firavun: "Ey ileri gelenler! Sizin için benden başka bir ilâh tanımıyorum. Ey Hâmân, haydi benim için çamur üzerine ateş yak (ve tuğla imal et), bana bir kule yap ki, Musa\'nın ilâhına çıkayım; ama sanıyorum, o mutlaka yalan söyleyenlerdendir." dedi.'),
(3291, 28, 39, 'O ve askerleri, yeryüzünde haksız yere büyüklük tasladılar ve gerçekten bize döndürülmeyeceklerini sandılar.'),
(3292, 28, 40, 'Biz de onu ve askerlerini yakalayıp denize atıverdik. Bir bak, zalimlerin sonu nice oldu!'),
(3293, 28, 41, 'Onları ateşe çağıran öncüler kıldık. Kıyamet günü onlar yardım görmeyeceklerdir.'),
(3294, 28, 42, 'Bu dünyada arkalarına lanet taktık. Onlar, kıyamet gününde de kötülenmişler arasındadır.'),
(3295, 28, 43, 'Andolsun ki biz, ilk nesilleri yok ettikten sonra Musa\'ya olur ki düşünür, öğüt alırlar diye, insanlar için apaçık deliller, hidayet rehberi ve rahmet olarak o Kitab\'ı (Tevrat\'ı) vermişizdir.'),
(3296, 28, 44, '(Resulüm!) Musa\'ya emrimizi vahyettiğimiz sırada sen batı yönünde bulunmuyordun ve (o hadiseyi) görenlerden değildin.'),
(3297, 28, 45, 'Bilakis biz (o zamandan senin zamanına kadar) nice nesiller var ettik de, onların üzerinden uzun zamanlar geçti. Sen onlara âyetlerimizi okuyarak, Medyen halkı arasında bulunanlardan da değildin; aksine biz (başka) peygamber göndermiştik.'),
(3298, 28, 46, '(Musa\'ya) seslendiğimiz zaman da, Tûr\'un yanında değildin. Bilakis senden önce kendilerine uyarıcı (peygamber) gelmeyen bir kavmi uyarman için Rabbinden bir rahmet olarak (orada geçenleri sana bildirdik), ola ki onlar düşünüp öğüt alırlar.'),
(3299, 28, 47, 'Bizzat kendi yaptıklarından dolayı başlarına bir musibet geldiğinde, "Rabbimiz! Ne olurdu bize bir peygamber gönderseydin de, âyetlerine uysak ve müminlerden olsaydık" diyecek olmasalardı (seni göndermezdik).'),
(3300, 28, 48, 'Fakat onlara tarafımızdan o hak (peygamber) gelince, "Musa\'ya verilen (mucizeler) gibi ona da verilmeli değil miydi?" dediler. Peki daha önce Musa\'ya verileni de inkâr etmemişler miydi? "Birbirini destekleyen iki sihir" demişler ve şunu söylemişlerdi: "Doğrusu biz hiçbirine inanmıyoruz."'),
(3301, 28, 49, '(Resulüm!) De ki: "Eğer doğru sözlüler iseniz, Allah katından bu ikisinden (bana ve Musa\'ya inen kitaplardan) daha doğru bir kitap getirin de ben ona uyayım!"'),
(3302, 28, 50, 'Eğer sana cevap vermezlerse, bil ki onlar, sırf heveslerine uymaktadırlar. Allah\'tan bir yol gösterici olmaksızın kendi hevesine uyandan daha sapık kim olabilir? Elbette Allah zalim kavmi doğru yola iletmez.'),
(3303, 28, 51, 'Andolsun ki biz, düşünüp öğüt alsınlar diye, sözü (vahyi) birbiri ardınca ulamışızdır.'),
(3304, 28, 52, 'Ondan (Kur\'ân\'dan) önce kendilerine kitap verdiklerimiz, ona da iman ederler.'),
(3305, 28, 53, 'Onlara (Kur\'ân) okunduğu zaman "O\'na iman ettik. Çünkü o, Rabbimizden gelmiş hakikattir. Esasen biz daha önce de müslüman idik" derler.'),
(3306, 28, 54, 'İşte onlara, sabretmelerinden ötürü mükafatları iki defa verilecektir. Bunlar kötülüğü iyilikle savarlar, kendilerine verdiğimiz rızıktan da Allah rızası için harcarlar.'),
(3307, 28, 55, 'Onlar, boş söz işittikleri zaman, ondan yüz çevirirler ve "Bizim işlerimiz bize, sizin işleriniz size. Size selam olsun. Biz kendini bilmezleri istemeyiz" derler.'),
(3308, 28, 56, '(Resulüm!) Sen sevdiğini hidayete eriştiremezsin; bilakis, Allah dilediğine hidayet verir ve hidayete girecek olanları en iyi O bilir.'),
(3309, 28, 57, '"Biz seninle beraber doğru yola uyarsak, yurdumuzdan atılırız" dediler. Biz onları, kendi katımızdan bir rızık olarak her şeyin ürünlerinin toplanıp getirildiği, güvenli, dokunulmaz bir yere (Mekkei Mükerreme\'ye) yerleştirmedik mi? Fakat onların çoğu bilmezler.'),
(3310, 28, 58, 'Biz, maişetleriyle şımarmış nice memleketi helak etmişizdir. İşte yerleri! Kendilerinden sonra oralarda pek az oturulabilmiştir. Onlara biz varis olmuşuzdur.'),
(3311, 28, 59, 'Rabbin, kendilerine âyetlerimizi okuyan bir peygamberi memleketlerin ana merkezlerine göndermedikçe, memleketleri helâk edici değildir. Zaten biz, ancak halkı zalim olan memleketleri helâk etmişizdir.'),
(3312, 28, 60, 'Size verilen şeyler, dünya hayatının geçim vasıtası ve debdebesidir. Allah katında olanlar ise, daha hayırlı ve daha kalıcıdır. Hâlâ buna aklınız ermeyecek mi?'),
(3313, 28, 61, 'Şu halde, kendisine güzel bir vaadde bulunduğumuz, ardından ona kavuşan kimse, (sırf) dünya hayatının geçici zevkini yaşattığımız ve sonra kıyamet gününde (azab için) huzurumuza getirilenler arasında bulunan kimse gibi midir?'),
(3314, 28, 62, 'O gün Allah onları çağırarak, "Benim ortaklarım olduklarını iddia ettikleriniz, hani nerede?" diyecektir.'),
(3315, 28, 63, '(O gün) haklarında azaba itilme, hükmü gerçekleşen kimseler, "Rabbimiz! Biz nasıl azmışsak, işte bu azmışları da öylece azdırdık. (Onların suçlarından) beri olduğumuzu sana arzederiz. Zaten onlar aslında bizlere tapmıyorlardı." derler.'),
(3316, 28, 64, '"(Allah\'a koştuğunuz) ortaklarınızı çağırın!" denir, onlar da çağırırlar; fakat kendilerine cevap vermezler ve (karşılarında) azabı görürler. Ne olurdu (dünyada iken) doğru yola girselerdi!'),
(3317, 28, 65, 'O gün Allah onları çağırıp "Peygamberlere ne cevap verdiniz?" diyecektir.'),
(3318, 28, 66, 'İşte o gün onlara bütün haberler kapkaranlık olmuştur; onlar birbirlerine de soramayacaklardır.'),
(3319, 28, 67, 'Fakat tevbe ederek, iman edip iyi işler yapan kimseye gelince, o, kurtuluşa erenler arasında olmayı umabilir.'),
(3320, 28, 68, 'Rabbin, dilediğini yaratır ve seçer. Onların seçim hakkı yoktur. Allah, onların ortak koştuklarından münezzehtir ve şanı yücedir.'),
(3321, 28, 69, 'Rabbin, onların, sinelerinde gizlediklerini de, açığa vurduklarını da bilir.'),
(3322, 28, 70, 'İşte O, Allah\'tır. O\'ndan başka tanrı yoktur. Önünde de, sonunda da hamd O\'nundur, hüküm O\'nundur. Ve ancak O\'na döndürüleceksiniz.'),
(3323, 28, 71, '(Resulüm!) De ki: "Düşündünüz mü hiç, eğer Allah üzerinizde geceyi tâ kıyamet gününe kadar aralıksız devam ettirse, Allah\'tan başka size ışık getirecek tanrı kimdir? Hâlâ işitmeyecek misiniz?"'),
(3324, 28, 72, 'De ki: "Haber verin bakayım, eğer Allah üzerinizde gündüzü ta kıyamet gününe kadar aralıksız devam ettirse, Allah\'tan başka, istirahat edeceğiniz geceyi size getirecek tanrı kimdir? Hâlâ görmeyecek misiniz?"'),
(3325, 28, 73, 'Rahmetinden dolayı, Allah, geceyi ve gündüzü yarattı ki geceleyin dinlenesiniz (gündüzün) ise O\'nun lütuf ve kereminden (rızkınızı) arayasınız. Umulur ki şükredersiniz.'),
(3326, 28, 74, 'Ve hele o gün Allah onları çağırarak: "Benim ortaklarım olduklarını iddia ettikleriniz hani, nerede?" diyecektir.'),
(3327, 28, 75, '(O gün) her ümmetten bir şahit çıkarır, "Haydin, kesin delilinizi getirin!" deriz. O zaman bilirler ki, hakikat Allah\'a aittir ve uydurageldikleri şeyler (putlar) de kendilerinden ayrılıp kaybolmuşlardır.'),
(3328, 28, 76, 'Karun, Musa\'nın kavminden idi de, onlara karşı azgınlık etmişti. Biz ona öyle hazineler vermiştik ki, anahtarlarını güçlü kuvvetli bir topluluk zor taşırdı. Kavmi ona demişti ki: "Şımarma! Bil ki Allah şımarıkları sevmez."'),
(3329, 28, 77, '"Allah\'ın sana verdiğinden (O\'nun yolunda harcayarak) ahiret yurdunu gözet, ama dünyadan da nasibini unutma! Allah\'ın sana ihsan ettiği gibi, sen de (insanlara) iyilik et. Yeryüzünde bozgunculuğu arzulama. Şüphesiz ki Allah, bozguncuları sevmez."'),
(3330, 28, 78, 'Karun ise: "O (servet) bana ancak kendimdeki bilgi sayesinde verildi." demiştir. Bilmiyor muydu ki Allah, kendinden önceki nesillerden, ondan daha güçlü, ondan daha çok taraftarı olan kimseleri helak etmişti. Günahkarlardan günahları sorulmaz (Allah onların hepsini bilir).'),
(3331, 28, 79, 'Derken Karun, ihtişam içinde kavminin karşısına çıktı. Dünya hayatını arzulayanlar, "Keşke Karun\'a verilenin benzeri bizim de olsaydı. Hakikat şu ki o, çok büyük devlet sahibidir" dediler.'),
(3332, 28, 80, 'Kendilerine ilim verilmiş olanlar ise, şöyle dediler: "Yazıklar olsun size! İman edip iyi işler yapanlara göre Allah\'ın mükafatı daha üstündür. Ona da ancak sabredenler kavuşabilir."'),
(3333, 28, 81, 'Derken biz onu da, sarayını da yerin dibine geçirdik. Artık Allah\'a karşı kendisine yardım edecek taraftarları olmadığı gibi, o, kendini savunup kurtarabilecek kimselerden de değildi.'),
(3334, 28, 82, 'Daha dün onun yerinde olmayı isteyenler de: "Demek ki Allah kullarından dilediğine rızkı çok da, az da verir. Şayet Allah bize lütufta bulunmuş olmasaydı, bizi de yerin dibine geçirirdi. Demek ki inkârcılar iflah olmazmış" demeye başladılar.'),
(3335, 28, 83, 'İşte ahiret yurdu! Biz onu yeryüzünde böbürlenmeyi ve bozgunculuğu arzulamayan kimselere veririz. (En güzel) akıbet, takva sahiplerinindir.'),
(3336, 28, 84, 'Kim bir iyilik getirirse ona ondan daha üstün karşılık vardır. Kim bir kötülük getirirse, o kötülükleri işleyenler, ancak yaptıkları kadar ceza görürler.'),
(3337, 28, 85, '(Resulüm!) Kur\'ân\'ı (okumayı, tebliğ etmeyi ve ona uymayı) sana farz kılan Allah, elbette seni (yine) dönülecek yere döndürecektir. De ki: "Rabbim, kimin hidayetle geldiğini ve kimin apaçık bir sapıklık içinde olduğunu en iyi bilendir."'),
(3338, 28, 86, 'Sen, bu kitabın sana vahyolunacağını ummuyordun. Bu ancak Rabbinden bir rahmettir. O halde sakın kâfirlere arka çıkma!'),
(3339, 28, 87, 'Allah\'ın âyetleri sana indirildikten sonra, artık sakın onlar seni bu âyetlerden alıkoymasınlar. Rabbine davet et. Asla müşriklerden olma!'),
(3340, 28, 88, 'Allah ile birlikte başka bir tanrıya tapıp yalvarma! O\'ndan başka tanrı yoktur. O\'nun zatından başka her şey helak olacaktır. Hüküm O\'nundur ve siz ancak O\'na döndürüleceksiniz.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'Elif, Lâm, Mîm.'),
(3342, 29, 2, 'İnsanlar, imtihandan geçirilmeden, sadece "İman ettik" demeleriyle bırakılıvereceklerini mi sandılar?'),
(3343, 29, 3, 'Andolsun ki, biz onlardan öncekileri de imtihandan geçirmişizdir. Elbette Allah, doğruları ortaya çıkaracak, yalancıları da mutlaka ortaya koyacaktır.'),
(3344, 29, 4, 'Yoksa kötülükleri yapanlar bizden kaçabileceklerini mi sandılar? Ne kadar kötü (ve yanlış) hüküm veriyorlar!'),
(3345, 29, 5, 'Her kim Allah\'a kavuşmayı umuyorsa bilsin ki, Allah\'ın tayin ettiği o vakit elbette gelecektir. O her şeyi işiten ve bilendir.'),
(3346, 29, 6, 'Cihad eden ancak kendisi için cihad etmiş olur. Şüphesiz Allah, âlemlerden müstağnidir.'),
(3347, 29, 7, 'İman edip iyi işler yapanların kötülüklerini elbette örteriz ve onlara, yaptıklarının daha güzeli ile karşılık veririz.'),
(3348, 29, 8, 'Biz insana, ana babasına iyi davranmasını tavsiye etmişizdir. Eğer onlar, seni, hakkında bilgin olmayan bir şeyi (körü körüne) bana ortak koşman için zorlarlarsa, onlara itaat etme. Dönüşünüz ancak banadır. O zaman, size yapmış olduklarınızı haber vereceğim.'),
(3349, 29, 9, 'İman edip iyi işler yapanları, muhakkak salihler (zümresi) içine katarız.'),
(3350, 29, 10, 'İnsanlardan kimi vardır ki, "Allah\'a inandık" der; fakat Allah uğrunda eziyete uğratıldığı zaman, insanların işkencesini Allah\'ın azabı gibi tutar. Halbuki Rabbinden bir yardım gelecek olsa, mutlaka, "Doğrusu biz de sizinle beraberdik" derler. Acaba Allah, herkesin kalbindekileri en iyi bilen değil midir?'),
(3351, 29, 11, 'Allah, elbette (O\'na gönülden) iman edenleri de, iki yüzlüleri de bilir.'),
(3352, 29, 12, 'Kâfirler, iman edenlere, "Bizim yolumuza uyun, sizin günahlarınızı biz yüklenelim" derler. Halbuki onların hiçbir günahını yüklenecek değillerdir. Gerçekte onlar, kesinlikle yalan söylemektedirler.'),
(3353, 29, 13, '(Fakat gerçek şu ki) elbette kendi yüklerini, kendi yükleriyle birlikte nice yükleri (başkalarını saptırmanın vebalini) taşıyacaklar ve uydurup durdukları şeylerden kıyamet günü mutlaka sorguya çekileceklerdir.'),
(3354, 29, 14, 'Andolsun ki Nuh\'u kendi kavmine gönderdik de, o dokuz yüz elli yıl onların arasında kaldı. Sonunda, onlar zulümlerini sürdürürken tufan kendilerini yakalayıverdi.'),
(3355, 29, 15, 'Fakat biz onu ve gemidekileri kurtardık ve bunu âlemlere bir ibret yaptık.'),
(3356, 29, 16, 'İbrahim\'i de gönderdik. O kavmine şöyle demişti: "Allah\'a kulluk edin, O\'na karşı gelmekten sakının. Eğer bilmiş olsanız bu sizin için daha hayırlıdır."'),
(3357, 29, 17, '"Siz Allah\'ı bırakıp sadece birtakım putlara tapıyor, asılsız sözler uyduruyorsunuz. Bilmelisiniz ki, Allah\'ı bırakıp da taptıklarınız, size rızık veremezler. O halde rızkı Allah katında arayın. O\'na kulluk edin. Ancak O\'na döndürüleceksiniz."'),
(3358, 29, 18, 'Eğer (size tebliğ edileni) yalan sayarsanız, bilin ki sizden önceki birçok milletler de yalan saymışlardı. Peygambere düşen yalnız açık bir tebliğdir.'),
(3359, 29, 19, 'Allah\'ın mahlukunu ilk baştan nasıl yarattığını, sonra bunu tekrarladığını görmediler mi? Şüphesiz bu, Allah\'a göre kolaydır.'),
(3360, 29, 20, 'De ki: "Yeryüzünde gezip dolaşın da, Allah ilk baştan nasıl yaratmış bakın. İşte Allah bundan sonra (aynı şekilde) ahiret hayatını da yaratacaktır." Gerçekten Allah her şeye kadirdir.'),
(3361, 29, 21, 'O, dilediğine azab eder, dilediğine rahmet eder. Ancak O\'na döndürüleceksiniz.'),
(3362, 29, 22, 'Siz ne yeryüzünde, ne de gökte (Allah\'ı) aciz bırakamazsınız. Allah\'tan başka bir dost ve yardımcı da bulamazsınız.'),
(3363, 29, 23, 'Allah\'ın âyetlerini ve O\'na kavuşmayı inkâr edenler var ya, işte onlar benim rahmetimden ümitlerini kesmişlerdir ve onlar için acıklı bir azab vardır.'),
(3364, 29, 24, 'Kavminin (İbrahim\'e) cevabı ise, "Onu öldürün, yahut yakın!" demelerinden ibaret oldu. Ama Allah onu ateşten kurtardı. Doğrusu bunda, iman eden bir kavim için ibretler vardır.'),
(3365, 29, 25, '(İbrahim onlara) dedi ki: "Siz, sırf aranızdaki dünya hayatına has muhabbet uğruna Allah\'ı bırakıp birtakım putlar edindiniz. Sonra kıyamet günü (geldiğinde) ise, kiminiz kiminizi tanımayacak, kiminiz kiminizi lanetleyecektir. Varacağınız yer cehennemdir. Ve hiç yardımcınız da yoktur."'),
(3366, 29, 26, 'Bunun üzerine ona sadece Lut iman etti. (İbrahim) de dedi ki: "Ben Rabbime hicret edeceğim. Şüphe yok ki O çok güçlüdür, hüküm ve hikmet sahibidir."'),
(3367, 29, 27, 'O\'na İshak ve Yakub\'u bağışladık. Peygamberliği ve kitapları, onun soyundan gelenlere verdik. Onu dünyada mükafatlandırdık. Şüphesiz o, ahirette de salihler (zümresin)dendir.'),
(3368, 29, 28, 'Lut\'u da gönderdik. O kavmine demişti ki: "Gerçekten siz, daha önce hiçbir milletin yapmadığı bir hayasızlığı yapıyorsunuz!"'),
(3369, 29, 29, '"(Bu ilâhî ikazdan sonra) siz, ille de erkeklere yaklaşacak, yol kesecek ve toplantılarınızda edepsizlik yapacak mısınız?" Kavminin cevabı ise, şöyle demelerinden ibaret oldu: "Doğru söyleyenlerden isen Allah\'ın azabını getir bize!"'),
(3370, 29, 30, '(Lut:) "Ey Rabbim! Şu fesatçılar güruhuna karşı bana yardım eyle" dedi.'),
(3371, 29, 31, 'Elçilerimiz İbrahim\'e (iki oğul vereceğimize dair) müjdeyi getirdiklerinde şöyle dediler: "Biz bu memleket halkını helak edeceğiz. Çünkü oranın halkı zalim kimselerdir."'),
(3372, 29, 32, '(İbrahim) dedi ki: "Ama orada Lut var!" Şöyle cevap verdiler: "Biz orada kimlerin bulunduğunu çok iyi biliyoruz. Onu ve ailesini elbette kurtaracağız. Yalnız karısı müstesna; o geride (azabda) kalacaklar arasındadır. "'),
(3373, 29, 33, 'Elçilerimiz Lut\'a gelince, onlar hakkında tasalandı. Ve onlar(ı düşünmesi) sebebiyle takatten düştü. O\'na: "Korkma, tasalanma! Çünkü biz seni de, aileni de kurtaracağız. Yalnız (azabda) kalacaklar arasında bulunan karın müstesna" dediler.'),
(3374, 29, 34, '"Biz şüphesiz bu memleket halkının üzerine, yoldan çıkmalarına karşılık (feci) bir azab indireceğiz."(dediler).'),
(3375, 29, 35, 'Andolsun ki biz, aklını kullanacak bir kavim için oradan apaçık bir ibret nişanesi bırakmışızdır.'),
(3376, 29, 36, 'Medyen\'e de kardeşleri Şuayb\'ı gönderdik ve Şuayb, "Ey kavmim! Allah\'a kulluk edin, ahiret gününe ümit bağlayın, yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın!" dedi.'),
(3377, 29, 37, 'Fakat onu yalancılıkla itham ettiler. Derken, kendilerini bir sarsıntı yakalayıverdi ve yurtlarında diz üstü çökekaldılar.'),
(3378, 29, 38, 'Ad ve Semud\'u da (helak ediverdik). Sizin için, (onların başına nelerin geldiği) oturdukları yerlerden apaçık anlaşılmaktadır. Şeytan onlara yaptıkları işleri güzel gösterip onları doğru yoldan çıkardı. Oysa bakıp görebilecek durumdaydılar.'),
(3379, 29, 39, 'Karun\'u, Firavun\'u ve Hâmân\'ı da (helak ettik). Andolsun ki, Musa onlara apaçık deliller getirmişti de onlar yeryüzünde büyüklük taslamışlardı. Halbuki (azabımızı aşıp ) geçebilecek değillerdi.'),
(3380, 29, 40, 'Nitekim onlardan herbirini günahları sebebiyle suç üstü yakaladık: Kiminin üzerine taşlar savuran rüzgarlar gönderdik, kimini korkunç bir ses yakaladı, kimini yerin dibine geçirdik, kimini de suda boğduk. Allah onlara zulmetmiyor, asıl onlar kendilerine yazık ediyorlardı.'),
(3381, 29, 41, 'Allah\'tan başka dost edinenlerin durumu, kendine yuva yapan örümceğin durumu gibidir. Halbuki, evlerin en çürüğü şüphesiz örümcek yuvasıdır. Keşke bilselerdi.'),
(3382, 29, 42, 'Allah, onların kendisini bırakıpta hangi şeye yalvardıklarını şüphesiz ki bilir. O mutlak güç ve hikmet sahibidir.'),
(3383, 29, 43, 'İşte biz bu temsilleri insanlar için getiriyoruz; fakat onları ancak bilenler düşünüp anlayabilir.'),
(3384, 29, 44, 'Allah gökleri ve yeri hak olarak yarattı. Şüphesiz bunda, iman edenler için bir nişane bulunmaktadır.'),
(3385, 29, 45, 'Sana vahyedilen Kitabı oku ve namazı kıl. Muhakkak ki namaz hayasızlıktan ve kötülükten alıkoyar. Allah\'ı anmak elbette en büyük ibadettir. Allah yaptıklarınızı bilir.'),
(3386, 29, 46, 'İçlerinden zulmedenleri bir yana, ehli kitapla ancak, en güzel yoldan mücadele edin ve deyin ki: "Bize indirilene de, size indirilene de iman ettik. Bizim ilâhımız da, sizin ilâhınız da birdir ve biz O\'na teslim olmuşuzdur."'),
(3387, 29, 47, '(Resulüm!) İşte sana (önceki kitapları tasdik eden) bu kitabı indirdik. Onun için, kendilerine kitap verdiklerimiz ona iman ediyorlar. Şunlardan da ona iman eden nice kimseler vardır. Ayetlerimizi ancak kâfirler bile bile inkâr eder.'),
(3388, 29, 48, 'Sen bundan önce, ne bir yazı okur, ne de elinle onu yazardın. Öyle olsaydı, batıla uyanlar kuşku duyarlardı.'),
(3389, 29, 49, 'Hayır, o (Kur\'ân), kendilerine ilim verilenlerin sinelerinde (yer eden) apaçık âyetlerdir. Ayetlerimizi ancak ve ancak zalimler bile bile inkâr eder.'),
(3390, 29, 50, '"Ona Rabbinden (başkaca) mucize indirilmeli değil miydi?" derler. Cevaben de ki: "Mucizeler ancak Allah\'ın katındadır. Ben ise sadece apaçık bir uyarıcıyım."'),
(3391, 29, 51, 'Sana indirdiğimiz ve onlara okunmakta olan kitap, kendilerine yetmedi mi? Bunda iman edecek bir kavim için elbette bir rahmet ve öğüt vardır.'),
(3392, 29, 52, 'De ki: Benimle sizin aranızda şahit olarak Allah yeter. O, göklerde ve yerde ne varsa bilir. Batıla inanıp inkâr edenler var ya, işte ziyana uğrayacaklar onlardır.'),
(3393, 29, 53, 'Senden azabı çarçabuk (getirmeni) istiyorlar. Eğer önceden tayin edilmiş bir vade olmasaydı, azab elbette onlara gelip çatmıştı. Fakat yine de, hiç farkına varmadıkları bir sırada o kendilerine mutlaka gelecektir.'),
(3394, 29, 54, '(Evet) senden azabı çarçabuk (getirmeni) istiyorlar. Halbuki cehennem, hiç şüpheleri olmasın, kâfirleri kuşatacaktır.'),
(3395, 29, 55, 'O günde azap, onları hem üstlerinden, hem ayaklarının altından saracak ve Allah (onlara), "Yaptıklarınızın cezasını tadın!" diyecektir.'),
(3396, 29, 56, 'Ey iman eden kullarım! Şüphesiz benim yarattığım yeryüzü geniştir. O halde yalnız bana kulluk edin.'),
(3397, 29, 57, 'Her can ölümü tadacaktır. Sonunda bize döndürüleceksiniz.'),
(3398, 29, 58, 'İman edip güzel işler yapanları, (evet) muhakkak ki onları, altlarından ırmaklar akan ve içinde ebedî kalacakları cennet köşklerine yerleştireceğiz. (Böyle iyi) işler yapanların mükafatı ne güzeldir!'),
(3399, 29, 59, 'Ki onlar, sabretmiş olup yalnız Rablerine güvenip dayanmaktadırlar.'),
(3400, 29, 60, 'Nice hayvanlar var ki, rızkını (biriktirip yanında) taşımıyor. Çünkü onların da, sizin de rızkınızı Allah veriyor. O, her şeyi işitir ve bilir.'),
(3401, 29, 61, 'Andolsun ki onlara, "Gökleri ve yeri yaratan, güneşi ve ayı buyruğu altında tutan kimdir?" diye sorsan "Allah" derler. O halde nasıl (haktan) çevrilip döndürülüyorlar?'),
(3402, 29, 62, 'Allah, kullarından dilediğine rızkı bol bol verir, dilediğine de kısar. Şüphesiz Allah, her şeyi hakkıyla bilendir.'),
(3403, 29, 63, 'Andolsun ki onlara, "Gökten su indirip, onunla ölümünün ardından yeryüzünü canlandıran kimdir?" diye sorsan, mutlaka, "Allah " derler. De ki: (Öyleyse) hamd de Allah\'a mahsustur. Fakat çokları akıllarını kullanmazlar.'),
(3404, 29, 64, 'Bu dünya hayatı sadece bir oyun ve oyalanmadan ibarettir. Ahiret yurduna gelince, işte asıl hayat odur. Keşke bilmiş olsalardı.'),
(3405, 29, 65, 'Baksana, gemiye bindikleri zaman, dini yalnız O\'na has kılarak (ihlasla) Allah\'a yalvarırlar. Fakat onları salimen karaya çıkarınca, bir bakarsın ki, (Allah\'a) ortak koşmaktadırlar.'),
(3406, 29, 66, 'Kendilerine verdiklerimize nankörlük etsinler ve safâ sürsünler bakalım! Ama yakında bilecekler.'),
(3407, 29, 67, 'Çevrelerinde insanlar kapılıp götürülürken (öldürülürken, ya da esir edilirken), bizim (Mekke\'yi) güven içinde kudsî bir yer yaptığımızı görmediler mi? Hâlâ batıla inanıp Allah\'ın nimetine nankörlük mü ediyorlar?'),
(3408, 29, 68, 'Allah\'a karşı yalan uyduran, yahut kendisine hak gelmişken onu yalan sayandan daha zalim kimdir? Cehennemde kâfirlere yer mi yok?'),
(3409, 29, 69, 'Ama bizim yolumuzda cihad edenleri, elbette kendi yollarımıza eriştireceğiz. Hiç şüphe yok ki Allah iyi davrananlarla beraberdir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'Elif, Lâm, Mim.'),
(3411, 30, 2, 'Rumlar yenildi.'),
(3412, 30, 3, '(Arapların bulunduğu bölgeye) en yakın bir yerde onlar, bu yenilgilerinin ardından mutlaka galib geleceklerdir.'),
(3413, 30, 4, '(Bu da) birkaç yıl içinde (olacaktır). Onların bu yenilgilerinden önce de sonra da emir Allah\'ındır ve o gün müminler, sevineceklerdir.'),
(3414, 30, 5, '(Bu da) Allah\'ın yardımıyla (olacaktır). Allah dilediğine yardım eder, galip kılar. O çok güçlüdür, çok merhamet edicidir.'),
(3415, 30, 6, 'Allah\'ın vaadi budur. Allah, vaadinden caymaz. Fakat insanların çoğu bilmezler.'),
(3416, 30, 7, 'Onlar, sadece bu dünya hayatının dış yüzünü bilirler. Ahiretten ise onlar hep gafildirler.'),
(3417, 30, 8, 'Kendi içlerinde hiç düşünmediler mi ki, Allah göklerde, yerde ve bu ikisi arasında bulunan her şeyi ancak hak ile ve belirlenmiş bir süre için yaratmıştır? Gerçekten insanların çoğu, Rablerine kavuşmayı inkâr etmektedirler.'),
(3418, 30, 9, 'Onlar, yeryüzünde gezmediler mi ki, kendilerinden öncekilerin sonu nasıl olmuş baksınlar? Onlar, kendilerinden daha güçlüydüler. Toprağı sürmüşler ve onu, bunların imar ettiklerinden daha çok imar etmişlerdi. Onlara da peygamberleri delillerle gelmişlerdi. Demek Allah onlara zulmetmiyordu. Fakat onlar, kendilerine zulmediyorlardı.'),
(3419, 30, 10, 'Sonra o kötülük edenlerin sonu çok kötü oldu. Çünkü onlar, Allah\'ın âyetlerini yalan saydılar ve onlarla alay ediyorlardı.'),
(3420, 30, 11, 'Allah yaratmayı ilkin yapar, sonra da çevirir, onu yeniden yapar. Sonra hep döndürülüp O\'na götürüleceksiniz.'),
(3421, 30, 12, 'Kıyamet saatinin gelip çattığı gün suçlular, her ümidi keserler.'),
(3422, 30, 13, 'Allah\'a ortak koştuklarından, kendilerine şefaat edecekler de bulunmaz. Onlar, o zaman Allah\'a koştukları ortakları inkâr ederler.'),
(3423, 30, 14, 'Kıyamet saatinin gelip çattığı gün varya, o gün (inananlarla inanmayanlar) ayrılırlar.'),
(3424, 30, 15, 'Şimdi iman edip salih ameller yapmış olanlara gelince, onlar bir bahçe içinde neşelenirler.'),
(3425, 30, 16, 'Âyetlerimizi ve âhiret buluşmasını yalan sayıp da küfredenlere gelince, işte onlar o zaman azab içinde hazır bulundurulurlar.'),
(3426, 30, 17, 'O halde akşama girdiğiniz zaman da, sabaha girdiğiniz zaman da tesbih Allah\'ındır. (daima O, tesbih edilir).'),
(3427, 30, 18, 'Göklerde ve yerde, ikindileyin de, öğleye erdiğiniz zaman da hamd O\'na mahsustur.'),
(3428, 30, 19, 'O, ölüden diri çıkarır, diriden ölü çıkarır ve toprağa ölümünden sonra hayat verir. Sizler de işte öyle çıkarılacaksınız.'),
(3429, 30, 20, 'O\'nun âyetlerinden (kudretinin delillerinden)dir ki, sizi bir topraktan yarattı. Sonra da siz şimdi yeryüzünde dağılıp yayılan insanlar oluverdiniz.'),
(3430, 30, 21, 'Yine O\'nun âyetlerindendir ki, sizin için nefislerinizden kendilerine ısınırsınız diye eşler yaratmış, aranıza bir sevgi ve merhamet koymuştur. Şüphesiz ki bunda düşünecek bir kavim için nice ibretler vardır.'),
(3431, 30, 22, 'Yine göklerin ve yerin yaratılışı ile dillerinizin ve renklerinizin farklı oluşu da O\'nun âyetlerindendir. Şüphesiz ki bunda bilenler için nice ibretler vardır.'),
(3432, 30, 23, 'Yine gecede ve gündüzde uyumanız ve lütfundan nasib aramanız da O\'nun âyetlerindendir. Şüphesiz ki bunda dinleyecek bir kavim için nice ibretler vardır.'),
(3433, 30, 24, 'Yine O\'nun âyetlerindendir ki, size hem korku ve hem de umut vermek için şimşeği gösteriyor. Ve gökten bir su indiriyor da onunla yeryüzüne ölümünden sonra hayat veriyor. Şüphesiz ki bunda aklını kullanacak bir kavim için nice ibretler vardır.'),
(3434, 30, 25, 'Yine göğün ve yerin, emriyle durması da O\'nun âyetlerindendir. Sonra sizi bir tek çağırışla çağırdığı zaman bir de bakarsınız ki (yerden diriltilip çıkarılıyorsunuz).'),
(3435, 30, 26, 'Göklerde ve yerde kim varsa hepsi O\'nundur. Hepsi de O\'na itaat etmektedirler.'),
(3436, 30, 27, 'Hem yaratmayı ilkin yapan O\'dur. Sonra onu çevirip yeniden yapacak olan da O\'dur ki, bu O\'na çok kolaydır. Göklerde ve yerde en yüksek şan ve şeref O\'nundur. O çokgüçlüdür, hüküm ve hikmet sahibidir.'),
(3437, 30, 28, 'Allah, size kendinizden bir misâl verdi: Hiç size rızık olarak verdiğimiz şeylerde elleriniz altındaki kölelerinizden ortaklarınız bulunur da onlarla siz eşit olur, aranızda birbirinizi saydığınız gibi, onları da sayar mısınız? İşte biz, düşünecek bir kavim için âyetleri böyle açıklıyoruz.'),
(3438, 30, 29, 'Fakat zulmedenler, bilgisizce hevalarına uydular. Artık Allah\'ın şaşırdığını kim yola getirebilir? Onların yardımcıları da yoktur.'),
(3439, 30, 30, 'O halde yüzünü, Allah\'ı bir tanıyarak dine, Allah\'ın insanları üzerine yaratmış olduğu fıtratına doğrult. Allah\'ın yaratışında değişiklik bulunmaz. Dosdoğru din budur. Fakat insanların çoğu bilmezler.'),
(3440, 30, 31, 'Başkasından geçerek hep O\'na gönül verin ve O\'ndan sakının. Namaza devam edin ve müşrilerden olmayın.'),
(3441, 30, 32, 'O müşriklerden (olmayın ki) onlar, dinlerini ayırıp öbek öbek olmuşlardır. Her grup kendilerindekine güvenmektedir.'),
(3442, 30, 33, 'Bununla beraber insanlara bir keder dokunduğu zaman her şeyden geçerek Rablerine yalvarır, dua ederler; sonra tarafından bir rahmet tattırıverdiği zaman da bakarsın onlardan bir kısmı tutar, O Rablerine ortak koşarlar.'),
(3443, 30, 34, 'Bunu da kendilerine verdiğimiz nimetlere nankörlük etmek için yaparlar. Haydi geçinedurun bakalım, yakında bileceksiniz.'),
(3444, 30, 35, 'Yoksa biz onlara bir delil indirmişiz de O\'na ortak koşmalarını o mu söylüyor?'),
(3445, 30, 36, 'Bir de biz insanlara bir rahmet tattırdığımız zaman ona güveniyorlar da; ellerinin önceden yaptığı şeyler sebebiyle başlarına bir fenalık gelirse, hemen her ümidi kesiveriyorlar.'),
(3446, 30, 37, 'Onlar görmediler mi ki, Allah dilediği kimseye rızkı serer ve daraltır. Şüphesiz ki bunda iman edecek bir kavim için ibretler vardır.'),
(3447, 30, 38, 'O halde akrabaya da hakkını ver, yoksula da, yolcuya da... Bu, Allah\'ın rızasını dileyenler için daha hayırlıdır. Kurtuluşa erecek olanlar da işte onlardır.'),
(3448, 30, 39, 'İnsanların malları içinde artsın diye verdiğiniz faiz, Allah yanında artmaz. Allah\'ın rızasını dileyerek verdiğiniz zekata gelince, işte onlar, malları kat kat artmış olanlardır.'),
(3449, 30, 40, 'Allah, O\'dur ki, sizi yarattı, sonra da size rızık verdi, sonra sizi öldürür, sonra sizi diriltir. Hiç sizin ortak koştuklarınızdan, bunlardan birini yapacak olan var mı? Allah, onların ortak koştuklarından münezzeh ve yücedir.'),
(3450, 30, 41, 'Yaptıklarının bir kısmını tatsınlar diye insanların kendi ellerinin kazandığı şeyler yüzünden karada ve denizde fesat ortaya çıktı. Umulur ki onlar hakka dönerler.'),
(3451, 30, 42, 'De ki, yeryüzünde bir gezin de bakın, bundan öncekilerin sonu nasıl olmuş! Onların pek çoğu müşrik idiler.'),
(3452, 30, 43, 'Allah\'tan geri çevrilmesine hiçbir çare olmayan bir gün gelmeden önce yüzünü dosdoğru, sabit dine çevir. O gün (gelince) insanlar birbirlerinden ayrılırlar.'),
(3453, 30, 44, 'Her kim inkâr ederse, inkârı kendi aleyhinedir. Kim de salih amel işlerse, onlar kendileri için rahat bir yer hazırlamış olurlar.'),
(3454, 30, 45, 'Çünkü O, iman edip salih amel işleyenlere lütfundan mükafat verecektir. Çünkü O, kâfirleri sevmez.'),
(3455, 30, 46, 'Rüzgarları müjdeciler olarak göndermesi, size rahmetinden tattırması, emriyle gemilerin akıp gitmesi ve lütfundan rızık isteyip kazanmanız O\'nun âyetlerindendir. Hem gerek ki şükredesiniz.'),
(3456, 30, 47, 'Andolsun ki biz, senden önce birçok peygamberleri kavimlerine gönderdik de, onlara apaçık delillerle vardılar. Onun üzerine günah işleyenlerden intikam aldık. Müminlere yardım ise, bizim nezdimizde bir hak oldu.'),
(3457, 30, 48, 'Allah O\'dur ki, rüzgarları gönderir de bir bulut savururlar. Derken onu gökyüzünde nasıl dilerse öyle serer, parça parça da eder. Derken yağmuru görürsün, aralarından çıkar. Derken onu kullarından kimlere diliyorsa döküverdi mi derhal yüzleri güler.'),
(3458, 30, 49, 'Halbuki onlar, daha önce üzerlerine yağmur indirilmeden evvel ümidi kesmişlerdi.'),
(3459, 30, 50, 'Şimdi bak Allah\'ın rahmetinin eserlerine! yeryüzünü ölümünden sonra nasıl diriltiyor? Şüphe yok ki O, mutlaka ölüleri diriltir. O her şeye kâdirdir.'),
(3460, 30, 51, 'Andolsun ki biz, bir rüzgâr göndersek de onu (rahmetin eseri olan ekini) sararmış görseler, mutlaka onun arkasından nankörlüğe başlarlar.'),
(3461, 30, 52, 'Çünkü sen ölülere işittiremezsin. O daveti, arkalarını dönmüş giderlerken sağırlara da duyuramazsın.'),
(3462, 30, 53, 'Körleri de sapıklıklarından hidayete getiremezsin. Sen ancak âyetlerimizi iman edeceklere duyurursun da onlar müslüman olur, selâmeti bulurlar.'),
(3463, 30, 54, 'Allah O\'dur ki, sizi güçsüz olarak yaratır, sonra güçsüzlüğün arkasından kuvvet verir. Sonra kuvvetin arkasından yine güçsüzlüğe ve ihtiyarlığa getirir. O dilediğini yaratır. Ve O, her şeyi bilir, her şeye gücü yeter.'),
(3464, 30, 55, 'Kıyamet kopacağı gün günahkarlar dünyada bir saatten fazla durmadıklarına yemin ederler. Onlar önceden de böyle haktan çevriliyorlardı.'),
(3465, 30, 56, 'Kendilerine ilim ve iman verilenler de şöyle diyecekler: "Andolsun ki, Allah\'ın kitabında takdir edilmiş olan tekrar dirilme gününe kadar kaldınız. İşte bu, dirilme günüdür. Fakat siz bunu bilmiyordunuz.'),
(3466, 30, 57, 'Artık o gün zulmedenlere mazeretleri fayda vermeyecektir. Onların dertlerinin çaresine de bakılmayacaktır.'),
(3467, 30, 58, 'Andolsun ki, biz insanlar için bu Kur\'ân\'da her türlü meselden örnekler getirdik. Yemin ederim ki, sen onlara başka bir âyet de getirsen o kâfirler yine: "Siz yalancılardan (uydurduğunuz sözü Allah\'a nispet edenlerden) başkası değilsiniz." diyeceklerdir.'),
(3468, 30, 59, 'İşte bilmeyenlerin kalblerini Allah böyle mühürler.'),
(3469, 30, 60, 'Şimdi sen sabret. Çünkü Allah\'ın vaadi mutlaka haktır. Sakın imanı sağlam olmayanlar seni hafifliğe sevketmesinler.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'Elif, Lâm, Mîm.'),
(3471, 31, 2, 'Bunlar, o hikmetli kitabın âyetleridir.'),
(3472, 31, 3, 'O, güzellik ve iyilik yapanlar için bir hidayet ve rahmettir.'),
(3473, 31, 4, 'Onlar, namazı kılarlar, zekatı verirler, âhirete de kesin olarak inanırlar.'),
(3474, 31, 5, 'İşte bunlar, Rableri tarafından bir hidayet üzeredirler. Kurtuluşa erecek olanlar da işte onlardır.'),
(3475, 31, 6, 'Bayağı insanlardan kimi de vardır ki, bilgisizce Allah yolundan saptırmak ve onu eğlence yerine tutmak için laf eğlencesi (veya boş söz) satın alırlar. İşte onlar için aşağılayıcı bir azab vardır.'),
(3476, 31, 7, 'Onun karşısında âyetlerimiz okunduğu zaman da sanki onları işitmemiş, sanki kulaklarında bir ağırlık varmış gibi büyüklük taslayarak yüz çevirir. İşte onu, acı verecek bir azab ile müjdele.'),
(3477, 31, 8, 'Fakat iman edip de salih amel işleyenlere gelince, onlar için nimet cennetleri vardır.'),
(3478, 31, 9, 'Onlar orada ebedî olarak kalacaklardır. Bu, Allah\'ın gerçek bir vaadidir. O, çok güçlüdür, hüküm ve hikmet sahibidir.'),
(3479, 31, 10, 'O, gökleri direksiz yarattı, onları görüyorsunuz. Yeryüzüne de sizi çalkalar diye ağır baskılar (sabit ve büyük dağlar) bıraktı ve orada herbir hayvandan üretti. Hem biz gökten bir su indirdik de orada her güzel çiftten (veya her hoş çeşitten) bitkiler yetiştirdik.'),
(3480, 31, 11, 'İşte bu, Allah\'ın yarattığıdır. Haydi gösterin bana O\'ndan başkaları ne yaratmıştır? Fakat o zalimler, apaçık bir sapıklık içindedirler.'),
(3481, 31, 12, 'Andolsun ki biz, Lokman\'a "Allah\'a şükret!" diye hikmet verdik. Kim şükrederse kendi iyiliğine eder. Kim de nankörlük ederse, şüphesiz ki Allah, hiçbir şeye muhtaç değildir, daima övülmeye layıktır.'),
(3482, 31, 13, 'Hani bir zaman Lokman, oğluna öğüt vererek demişti ki: "Yavrucuğum! Allah\'a ortak koşma, çünkü Allah\'a ortak koşmak (şirk), elbette büyük bir zulümdür."'),
(3483, 31, 14, 'Gerçi biz insana, anasına ve babasına itaati de tavsiye ettik. Anası onu zayıflık üstüne zayıflıkla taşıdı. Onun sütten ayrılması da iki yıl içindedir. (Biz insana): "Bana, anana ve babana şükret" diye de tavsiye ettik. Dönüş, ancak banadır.'),
(3484, 31, 15, 'Bununla beraber eğer her ikisi de bilmediğin bir şeyi, bana ortak koşman hususunda seni zorlarsa, onlara itaat etme. Fakat dünyada onlarla iyi geçin ve bana yönelenlerin yolunu tut. Sonra dönüşünüz ancak banadır. O zaman ben de size yaptıklarınızı haber vereceğim.'),
(3485, 31, 16, '"Yavrucuğum! Haberin olsun ki, yaptığın bir hardal tanesi ağırlığınca olsa da, bir kaya içinde veya göklerde, yahut yerin dibinde gizlense, Allah onu getirir, mizanına kor. Çünkü Allah en ince şeyleri bilir, her şeyden haberdardır."'),
(3486, 31, 17, '"Yavrucuğum! Namazı kıl, iyiliği emret, kötülükten sakındır. Başına gelenlere sabret, çünkü bunlar, azmi gerektiren işlerdendir."'),
(3487, 31, 18, '"Hem insanlara karşı avurdunu şişirme (kibirlenme) ve yeryüzünde çalımla yürüme. Çünkü Allah övünen ve kuruntu edenlerin hiçbirini sevmez.'),
(3488, 31, 19, 'Yürüyüşünde tabii ol, sesini alçalt, çünkü seslerin en çirkini elbette eşeklerin sesidir.'),
(3489, 31, 20, 'Görmediniz mi ki, Allah göklerde ve yerde ne varsa hepsini sizin hizmetinize vermiş, gizli ve açık olarak nimetlerini üzerinize yaymıştır. Bununla beraber insanlar içinde kimi de var ki, ne bir ilme, ne bir mürşide ve ne aydınlatıcı bir kitaba dayanmaksızın Allah hakkında mücadele ediyor.'),
(3490, 31, 21, 'Onlara: "Allah\'ın indirdiğine tabi olun!"dendiği zaman: "Hayır, biz atalarımızı neyin üzerinde bulduksa, onun ardınca gideriz." diyorlar. Ya şeytan onları cehennnem azabına çağırıyor idiyse de mi onlara uyacaklar?'),
(3491, 31, 22, 'Oysa her kim iyilik yaparak yüzünü tertemiz Allah\'a tutarsa, o gerçekten en sağlam kulpa yapışmıştır. Öyle ya bütün işlerin sonu Allah\'a dayanır.'),
(3492, 31, 23, 'Kim de inkâr ederse, artık onun inkârı seni üzmesin. Onlar dönüp bize gelecekler. O zaman biz onlara bütün yaptıklarını haber vereceğiz. Gerçekten Allah, bütün kalblerin özünü bilir.'),
(3493, 31, 24, 'Biz onlara biraz zevk ettiririz de sonra kendilerini ağır bir azaba zorlarız.'),
(3494, 31, 25, 'Andolsun ki onlara: "Gökleri ve yeri kim yarattı?" diye sorsan, elbette "Allah" diyecekler. "Allah\'a hamd olsun." de. Fakat onların çoğu bilmezler.'),
(3495, 31, 26, 'Göklerde ve yerde ne varsa hepsi Allah\'ındır. Gerçekten Allah, hiçbir şeye muhtaç değildir, daima övülmeye lâyıktır.'),
(3496, 31, 27, 'Eğer yeryüzündeki ağaçlar hep kalem olsa, deniz de arkasından yedi deniz daha kendisine destek olduğu halde mürekkep olsa, yine de Allah\'ın kelimeleri yazmakla tükenmez. Şüphesiz ki Allah çok güçlüdür, hüküm ve hikmet sahibidir.'),
(3497, 31, 28, 'Sizin yaratılmanız da tekrar diriltilmeniz de ancak bir tek nefsin yaratılması ve tekrar diriltilmesi gibidir. Gerçekten Allah her şeyi işitir ve görür.'),
(3498, 31, 29, 'Görmedin mi ki, Allah geceyi gündüze sokuyor, gündüzü geceye sokuyor. Güneş ile ayı da emrine boyun eğdirmiştir. Her biri belirli bir süreye kadar akıp gidiyor. Şüphesiz ki Allah, yaptıklarınızdan haberdardır.'),
(3499, 31, 30, 'Bu da şundandır ki, Allah hakkın ta kendisidir. (İnsanların) O\'ndan başka taptıkları ise mutlaka batıldır. Şüphesiz ki Allah, çok yücedir, çok büyüktür.'),
(3500, 31, 31, 'Görmedin mi ki Allah, âyetlerinden bir kısmını size göstersin diye gemiler, Allah\'ın nimetiyle denizde akıp gidiyor. Şüphesiz bunda çok sabredenler ve çok şükredenler için nice ibretler vardır.'),
(3501, 31, 32, 'Onları kara bulutlar gibi bir dalga sardığı zaman, dini yalnız kendisine has kılarak Allah\'a yalvarırlar. Onları kurtarıp karaya çıkardığı zaman ise içlerinden doğru giden de bulunur. Bizim âyetlerimizi öyle nankör gaddarlardan başkası inkâr etmez.'),
(3502, 31, 33, 'Ey insanlar! Rabbinizden sakının ve bir günden korkun ki, baba çocuğuna hiçbir fayda veremez. Çocuk da babasına hiçbir şeyle fayda sağlayacak değildir. Şüphesiz Allah\'ın vaadi gerçektir. O halde dünya hayatı sizi aldatmasın, sakın o çok aldatıcı şeytan sizi Allah\'ın affına güvendirerek aldatmasın.'),
(3503, 31, 34, 'Şüphesiz ki, kıyamet saatinin bilgisi Allah yanındadır. Yağmuru O yağdırır, rahimlerde ne varsa (erkek veya dişi oluşunu, renk ve özelliklerini) O bilir. Hiçbir kimse yarın ne kazanacağını bilmez. Hiçbir kimse hangi yerde öleceğini de bilemez. Şüphesiz ki Allah her şeyi hakkıyla bilir, her şeyden haberdardır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'Elif, Lâm, mim.'),
(3505, 32, 2, 'Kendisinde şüphe olmayan bu kitabın indirilişi, âlemlerin Rabbi olan Allah tarafındandır.'),
(3506, 32, 3, 'Yoksa onu (Muhammed) uydurdu mu diyorlar? Hayır, o senden önce kendilerine bir uyarıcı gelmemiş olan bir kavmi korkutman için, Rabbin tarafından gelen bir haktır. Gerek ki, hidayeti kabul ederler.'),
(3507, 32, 4, 'Allah O\'dur ki, gökleri, yeri ve ikisi arasındakileri altı günde yaratmış, sonra Arş üzerine istivâ buyurmuştur (hakim olmuştur). Sizin için O\'ndan başka ne bir dost vardır, ne de bir şefaatçi! Artık düşünmeyecek misiniz?'),
(3508, 32, 5, 'O, gökten yere, (yukarıdan aşağıya) işleri düzenler, sonra da o işler, sizin saydıklarınızdan bin yıl kadar olan bir günde O\'na yükselir.'),
(3509, 32, 6, 'İşte görüleni de görülmeyeni de bilen, her şeye gücü yeten, çok merhametli olan O\'dur.'),
(3510, 32, 7, 'Yarattığı her şeyi güzel yaratan ve insanı yaratmaya bir çamurdan başlayan O\'dur.'),
(3511, 32, 8, 'Sonra da onun soyunu süzülmüş bir özden, değersiz bir sudan yaratmıştır.'),
(3512, 32, 9, 'Sonra onu düzenli bir şekle sokup, içine kendi ruhundan üfürdü. Ve sizin için kulaklar, gözler ve gönüller var etti. Siz pek az şükrediyorsunuz!'),
(3513, 32, 10, 'Onlar: "Biz yerde kaybolup gittikten sonra, gerçekten biz mi yeni bir yaratılışta bulunacağız?" dediler. Fakat onlar Rablerine kavuşmayı (O\'nun huzuruna varacaklarını) inkâr eden kâfirlerdir.'),
(3514, 32, 11, 'De ki: "Size vekil kılınmış olan ölüm meleği canınızı alacak, sonra döndürülüp Rabbinize götürüleceksiniz."'),
(3515, 32, 12, 'Ey Muhammed! Günahkârların, Rablerinin huzurunda başları öne eğilmiş olarak: "Ey Rabbimiz! Gördük ve dinledik, şimdi bizi geri çevir de salih bir amel işleyelim, çünkü biz artık kesin bir şekilde inanıyoruz." derlerken bir görsen!'),
(3516, 32, 13, 'Eğer biz dilemiş olsaydık her nefse hidâyetini verirdik. Fakat benden: "Bütün insanlar ve cinlerden cehennemi elbette dolduracağım." sözü hak olmuştur.'),
(3517, 32, 14, '"O halde bu gününüzle karşılaşmayı unuttuğunuzdan dolayı tadın azabı! İşte biz de sizi unuttuk. Yapmakta olduğunuz işler yüzünden tadın ebedî azabı!"'),
(3518, 32, 15, 'Bizim âyetlerimize öyle kimseler iman eder ki, onlarla kendilerine öğüt verildiği zaman secdelere kapanırlar ve Rablerine hamd ile tesbih ederler de büyüklük taslamazlar.'),
(3519, 32, 16, 'Onların yanları yataklardan uzaklaşır, korku ve ümid içinde Rablerine dua ederler ve kendilerine verdiğimiz rızıklardan hayıra sarfederler.'),
(3520, 32, 17, 'Şimdi hiç kimse kendileri için, yaptıklarına karşılık gözler aydınlığı olacak şeylerden neler gizlenmiş olduğunu bilemez.'),
(3521, 32, 18, 'Öyle ya iman eden kimse, fâsık olan gibi olur mu? Onlar eşit olamazlar.'),
(3522, 32, 19, 'Evet, iman edip de salih amelleri işleyen kimselerin, yaptıklarına karşılık bir konukluk (ağırlanma) olarak me\'vâ (barınak) cennetleri vardır.'),
(3523, 32, 20, 'Ama fâsıklık etmiş olanların barınakları ateştir. Oradan her çıkmak istediklerinde oraya geri çevrilirler ve kendilerine: "Haydi tadın o ateşin yalanlayıp durduğunuz azabını!" denir.'),
(3524, 32, 21, 'Şu bir gerçek ki, onlara o en büyük azabdan önce yakın azabdan (dünyada) da tattıracağız. Umulur ki, (kötülükten) dönerler.'),
(3525, 32, 22, 'Rabbinin âyetleriyle kendisine öğüt verilip de, sonra onlardan yüz çeviren kimseden daha zalim kim olabilir? Gerçekten biz, günahkârlardan intikam alacağız.'),
(3526, 32, 23, 'Andolsun ki biz vaktiyle Musa\'ya kitap vermiştik. Şimdi de sen ona (öyle bir kitaba) kavuşmaktan şüphe içinde olma. Biz onu İsrailoğullarına doğru yolu göstren bir rehber kılmıştık.'),
(3527, 32, 24, 'Onların içinden, sabrettikleri zaman bizim emrimizle doğru yola ileten önderler yetiştirmiştik. Onlar, bizim âyetlerimize kesin bir şekilde inanıyorlardı.'),
(3528, 32, 25, 'Şimdi ihtilafa düştükleri şeyler hakkında şüphesiz ki Rabbin kıyamet günü aralarında ayırıcı hükmü verecektir.'),
(3529, 32, 26, 'Kendilerinden önce, yurtlarında gezip dolaşmakta oldukları nice kuşakları helâk etmiş olmamız, daha onları doğru yola iletmedi mi? Şüphesiz bunda nice ibretler vardır. Hâlâ kulak vermeyecekler mi?'),
(3530, 32, 27, 'Ya hiç görmediler mi ki, biz kır yere suyu salıveriyoruz da onunla bir ekin çıkarıyoruz. Ondan hayvanları da yiyor, kendileri de. Hâlâ gözlerini açmayacaklar mı?'),
(3531, 32, 28, 'Bir de "Ne zaman o fetih, eğer doğru söylüyorsanız?" diyorlar.'),
(3532, 32, 29, 'De ki: "İnkâr edenlere o fetih günü iman etmeleri fayda vermez ve onlara göz açtırılmaz."'),
(3533, 32, 30, 'Şimdi sen onlardan yüz çevir de gözet. Çünkü onlar da gözetmektedirler.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Ey peygamber! Allah\'tan kork, kâfirlere ve münafıklara itaat etme. Muhakkak ki Allah her şeyi bilir, hüküm ve hikmet sahibidir.'),
(3535, 33, 2, 'Rabbinden sana ne vahyediliyorsa onun ardınca git. Muhakkak ki Allah ne yaparsanız haberdardır.'),
(3536, 33, 3, 'Allah\'a güven, vekil olarak Allah yeter.'),
(3537, 33, 4, 'Allah bir adam için içinde iki kalb yapmamıştır. Kendilerinden zıhar yaptığınız eşlerinizi analarınız kılmamıştır. Evlatlıklarınızı da oğullarınız kılmamıştır. O sizin ağzınızdaki lafınızdır. Allah ise hakkı söylüyor ve doğru yolu gösteriyor.'),
(3538, 33, 5, 'Onları (evlatlıkları) babaları adına çağırın. Allah yanında o daha doğrudur. Eğer babalarını bilmiyorsanız, onlar sizin dinde kardeşleriniz ve dostlarınızdır. Bununla beraber hata ettiklerinizde üzerinize bir günah yoktur. Fakat kalblerinizin kasdettiğinde vardır. Allah, çok bağışlayıcı ve çok merhamet edicidir.'),
(3539, 33, 6, 'Peygamber, müminlere kendi nefislerinden önce gelir. O\'nun hanımları da onların analarıdır. Akraba da Allah\'ın kitabında birbirlerine, diğer müminlerden ve muhacirlerden daha yakındırlar. Ancak dostlarınıza bir maruf (uygun bir vasiyet) yapmanız müstesnâdır. Bu, kitapta yazılıdır.'),
(3540, 33, 7, 'Unutma o peygamberlerden mîsaklarını (kesin sözlerini) aldığımız vakti! Hele senden, Nuh, İbrahim, Musa ve Meryemoğlu İsa\'dan ki onlardan ağır bir mîsak (sağlam bir söz) aldık.'),
(3541, 33, 8, '(Bunu Allah), sadıklara sadakatlerinden sormak için yaptı. Kâfirler için ise acı verecek bir azab hazırladı.'),
(3542, 33, 9, 'Ey iman edenler! Allah\'ın üzerinizdeki nimetini anın. Hani size ordular gelmişti de üzerlerine bir rüzgâr ve sizin görmediğiniz ordular salıvermiştik. Allah ne yaptığınızı görüyordu.'),
(3543, 33, 10, 'O zaman onlar, hem üstünüzden gelmişlerdi, hem aşağı tarafınızdan, ve o vakit gözler kaymış, yürekler gırtlaklara dayanmıştı. Siz Allah\'a türlü türlü zanlarda bulunuyordunuz.'),
(3544, 33, 11, 'İşte burada müminler imtihan edilmiş ve şiddetli bir sarsıntı ile sarsılmışlardı.'),
(3545, 33, 12, 'O vakit münâfıklar ve kalblerinde bir hastalık bulunanlar: "Allah ve Resulü bize bir aldanıştan başka bir vaad yapmamış." diyorlardı.'),
(3546, 33, 13, 'O vakit bunlardan bir grup: "Ey Medine halkı! Sizin için duracak yer yok, hemen dönün." diyorlardı. Yine onlardan bir kısmı da Peygamberden izin istiyor, evlerimiz gerçekten (düşmana) açıktır." diyorlardı, halbuki açık değildi, sadece kaçmak istiyorlardı.'),
(3547, 33, 14, 'Eğer onların her tarafından üzerlerine girilse de sonra fitne çıkarmaları istenilse derhal onu yapacaklardı. Ama onunla da pek az duracaklardı.'),
(3548, 33, 15, 'Halbuki bundan önce Allah\'a ahid vermişlerdi. Arkalarını dönmeyeceklerdi. Allah\'a verilen ahid ise mesuliyetlidir, mutlaka sorulur.'),
(3549, 33, 16, 'De ki: "Eğer ölümden veya öldürülmekten kaçıyorsanız, kaçmak size asla fayda vermez. Vereceğini var saydığınız takdirde de ancak pek az faydalandırılırsınız."'),
(3550, 33, 17, 'De ki: "Eğer Allah size bir felâket diler veya bir rahmet murad ederse, sizi Allah\'tan saklamak kimin haddine?" Hem onlar kendilerine Allah\'tan başka bir veli de bulamazlar, bir yardımcı da.'),
(3551, 33, 18, 'Şüphesiz Allah, içinizden o savsaklayanları ve kardeşlerine: "Bize gelin" diyenleri biliyor. Onlar harbe pek az geliyorlardı.'),
(3552, 33, 19, 'Size karşı kıskançlık ediyorlardı. Derken o korku hali gelince, gördün onları ki, ölümden baygınlık sarmış kimse gibi gözleri dönerek sana bakıyorlardı. O korku gidince, size keskin keskin diller sıyırdılar. Onlar hayra karşı kıskançlık ediyorlardı. İşte bunlar iman etmediler de Allah amellerini boşa çıkardı. Bu Allah\'a göre önemsizdir.'),
(3553, 33, 20, 'Onlar ahzabı (düşman birliklerini) gitmedi sanıyorlardı. Eğer o birlikler bir daha gelecek olursa, çölde bedevi Araplar içinde yer alıp, sizin haberlerinizden (başınıza geleceklerden) sormayı isterler. Onlar içinizde kalacak olsalar da pek az harb ederler.'),
(3554, 33, 21, 'Şanım hakkı için muhakkak ki size Resullulah\'da pek güzel bir örnek vardır. Allah\'a ve son güne ümit besler olup da Allah\'ı çok zikreden kimseler için.'),
(3555, 33, 22, 'Müminler, ahzabı (düşman birliklerini) gördükleri zaman: "İşte bu, Allah\'ın ve Resulü\'nün bize vaad ettiği şeydir. Allah ve Resulü doğru söyledi." dediler. Bu onların imanını ve teslimiyetini artırmaktan başka bir şey yapmadı.'),
(3556, 33, 23, 'Müminlerdendir o erler ki Allah\'a verdikleri ahde sadakat gösterdiler. Kimi adağını ödedi (canını verdi), kimi de beklemektedir. Onlar, ahidlerini hiç değiştirmediler.'),
(3557, 33, 24, 'Çünkü Allah sadıklara sadakatleriyle mükafat verecek, dilerse münafıklara da azab edecek veya tevbe nasib edecektir. Şüphe yok ki Allah çok bağışlayıcıdır. Çok merhamet edicidir.'),
(3558, 33, 25, 'Hem Allah kâfirleri herhangi bir hayra ulaşmadan hınçlarıyle defetti. Bu şekilde Allah, müminlere savaşta kâfi geldi. Allah çok güçlüdür, çok üstündür.'),
(3559, 33, 26, 'Hem de kitap ehlinden onlara yardım edenleri kalplerine korku düşürerek kalelerinden indirdi, siz onların bir kısmını katlediyordunuz, bir kısmını da esir alıyordunuz.'),
(3560, 33, 27, '(Allah) onların arazilerini, yurtlarını ve mallarını size miras kıldı. Bir de henüz ayak basmadığınız bir yeri (size miras kıldı). Allah, her şeye kâdirdir.'),
(3561, 33, 28, 'Ey peygamber! Hanımlarına şöyle söyle: "Eğer dünya hayatını ve zinetini istiyorsanız, haydi gelin, sizi donatayım ve güzellikle bırakıp salıvereyim.'),
(3562, 33, 29, 'Yok eğer Allah ve Resulünü ve ahiret yurdunu istiyorsanız, haberiniz olsun ki, Allah içinizden güzellik edenlere pek büyük bir ecir hazırlamıştır.'),
(3563, 33, 30, 'Ey peygamberin hanımları! sizden her kim bir terbiyesizlik ederse ona azab iki kat katlanır. Bu Allah\'a göre çok kolaydır.'),
(3564, 33, 31, 'Yine sizden her kim Allah\'a ve Resulü\'ne boyun eğer, salih bir amel işlerse, ona da mükâfatını iki kat veririz. Hem onun için bol bir rızık hazırlamışızdır.'),
(3565, 33, 32, 'Ey peygamberin hanımları! Siz kadınlardan herhangi biri gibi değilsiniz. Eğer takva ile korunacaksanız, konuşurken kırıtmayın da kalbinde bir hastalık bulunan kimse tamaha düşmesin. Güzel ve dosdoğru söz söyleyin.'),
(3566, 33, 33, 'Hem vakarınızla evlerinizde durun da önceki cahiliyet devrinde olduğu gibi süslenip çıkmayın. Namazı kılın, zekatı verin. Allah ve Resulü\'ne itaat edin. Ey ehli beyt! Allah sizden kiri gidermek ve sizi tertemiz, pampak yapmak istiyor.'),
(3567, 33, 34, 'Oturun da evlerinizde okunan Allah\'ın âyetlerini ve hikmeti anın. Şüphe yok ki Allah lütuf sahibidir ve her şeyden haberdardır.'),
(3568, 33, 35, 'Şüphe yok ki müslüman erkeklerle müslüman kadınlar, mümin erkeklerle mümin kadınlar, itaat eden erkeklerle itaat eden kadınlar, sadık erkeklerle sadık kadınlar, sabreden erkeklerle sabreden kadınlar, mütevazi erkeklerle mütevazi kadınlar, sadaka veren erkeklerle sadaka veren kadınlar, oruç tutan erkeklerle oruç tutan kadınlar, ırzlarını koruyan erkeklerle ırzlarını koruyan kadınlar, Allah\'ı çok zikreden erkeklerle Allah\'ı çok zikreden kadınlar var ya, işte onlar için Allah bir mağfiret ve büyük bir mükâfat hazırlamıştır.'),
(3569, 33, 36, 'Bununla beraber Allah ve Resulü bir işe hükmettiği zaman, gerek mümin bir erkek ve gerekse mümin bir kadın için, o işlerinde başka bir tercih hakkı yoktur. Her kim de Allah ve Resulüne âşi olursa açık bir sapıklık etmiş olur.'),
(3570, 33, 37, 'Hem hatırla o vakti ki, o kendisine Allah\'ın nimet verdiği ve senin de ikramda bulunduğun kimseye: "Hanımını kendine sıkı tut ve Allah\'tan kork" diyordun da nefsinde Allah\'ın açacağı şeyi gizliyordun. İnsanlardan çekiniyordun. Halbuki Allah kendisini saymana daha lâyıktı. Sonra Zeyd o kadından ilişiğini kestiği zaman, biz onu sana eş yaptık ki, oğulluklarının ilişkilerini kestikleri hanımlarını nikâhlamada müminlere bir darlık olmasın. Allah\'ın emri de yerine getirilmiştir.'),
(3571, 33, 38, 'Peygambere Allah\'ın takdir ettiği, mübah kıldığı şeyde bir darlık yoktur. Bundan önce geçen bütün peygamberler hakkında Allah\'ın sünneti böyledir. Allah\'ın emri ise biçilmiş bir kaderdir.'),
(3572, 33, 39, 'Onlar, Allah\'ın gönderdiklerini tebliğ ederler ve O\'ndan korkarlar, Allah\'tan başka kimseden korkmazlardı. Hesap görücü olarak da Allah yeter.'),
(3573, 33, 40, 'Muhammed, sizin adamlarınızdan hiçbirinin babası değildir. Ama Allah\'ın Resulü ve peygamberlerin sonuncusudur. Allah her şeyi hakkiyle bilendir.'),
(3574, 33, 41, 'Ey iman edenler! Allah\'ı çokça anın.'),
(3575, 33, 42, 'Ve O\'nu sabah akşam tesbih edin.'),
(3576, 33, 43, 'Sizleri karanlıklardan aydınlığa çıkarmak için melekleri ile birlikte üzerinize rahmet ve bereket indiren O\'dur ve O, müminlere çok merhametlidir.'),
(3577, 33, 44, 'O\'na kavuşacakları gün müminlere esenlik dileği selâmdır. (Allah) onlar için cömertçe bir mükafat hazırlamıştır.'),
(3578, 33, 45, 'Ey peygamber! Biz seni hem bir şahit, hem bir müjdeci, hem bir uyarıcı olarak gönderdik.'),
(3579, 33, 46, 'Ve hem de izniyle Allah\'a bir davetçi ve nurlar saçan bir kandil (olarak gönderdik).'),
(3580, 33, 47, 'Müminlere müjdele! Onlara Allah\'tan bir mükafat vardır...'),
(3581, 33, 48, 'Kâfirlere ve münafıklara itaat etme, onların ezalarını bırak (aldırma) da Allah\'a tevekkül et. Allah vekil olarak hepsine yeter.'),
(3582, 33, 49, 'Ey iman edenler! Mümin kadınları nikâh edip de sonra onlara dokunmadan boşadığınız zaman, sizin için üzerlerinde sayacağınız bir iddet hakkınız yoktur. Derhal müt\'alarını (mehirleri belirlenmediği takdirde yararlanacakları bir mal) verip onları güzel bir şekilde salıverin.'),
(3583, 33, 50, 'Ey peygamber! Biz bilhassa sana şunları helâl kıldık: Mehirlerini vermiş olduğun eşlerini, Allah\'ın sana ganimet olarak ihsan buyurduklarından sahip olduğun cariyeleri, amcalarının kızlarından, halalarının kızlarından, dayılarının kızlarından, teyzelerinin kızlarından seninle beraber hicret etmiş olanları, bir de mümin bir kadın kendini peygambere hibe ederse, peygamber nikâh etmek istediği takdirde, onu başka müminlere değil de sadece sana mahsus olmak üzere helâl kıldık. Onlara eşleri ve cariyeleri hakkında neyi farz kıldığımızı biliyoruz. Bunlar sana hiçbir darlık olmaması içindir. Allah, çok bağışlayıcıdır, çok merhamet edicidir.'),
(3584, 33, 51, 'Onlardan dilediğini geri bırakır, dilediğini yanına alırsın. Sırasını geri bıraktığın kadınlardan dilediğini yanına almanda da sana bir günah yoktur. Onların gözleri aydın olup üzülmemelerine ve kendilerine verdiğin ile hepsinin hoşnut olmalarına en elverişli olan budur. Allah kalblerinizdekini bilir. Allah her şeyi bilir ve yumuşak davranır.'),
(3585, 33, 52, 'Bundan başka kadınlar sana helâl olmaz. Bunları başka eşlerle değiştirmek de olmaz. İsterse güzellikleri hoşuna gitsin. Ancak sahip olduğun cariyen başka. Allah her şeye gözcü bulunuyor.'),
(3586, 33, 53, 'Ey iman edenler! Peygamberin evlerine vaktine bakmaksızın ve yemeğe izin verilmedikçe girmeyin. Fakat çağırıldığınız vakit girin. Yemeği yediğinizde de hemen dağılın. Sohbet etmek için de izinsiz girmeyin. Çünkü bu haliniz peygambere eziyet veriyor, ama o sizden utanıyor. Fakat Allah gerçeği söylemekten utanmaz. Hem O\'nun hanımlarına bir ihtiyaç soracağınız vakit de perde arkasından sorun. Böyle yapmanız hem sizin kalbleriniz ve hem de onların kalbleri için daha temizdir. Hem sizin Resulullah\'a eziyet etmeye hakkınız yoktur. Ondan sonra hanımlarını da ebediyyen nikâh edemezsiniz. Çünkü bu Allah katında çok büyük bir günahtır.'),
(3587, 33, 54, 'Siz bir şeyi açıklasanız da gizleseniz de şüphe yok ki Allah her şeyi bilmektedir.'),
(3588, 33, 55, 'Onlar (peygamberin eşleri) için babaları, oğulları, kardeşleri, erkek kardeşlerinin oğulları, kız kardeşlerinin oğulları, kendi kadınları (kadın dostları) ve sahip oldukları köleleri hakkında bir günah yoktur. Bununla beraber (ey Peygamberin hanımları) Allah\'tan korkun. Çünkü Allah her şeye şahit bulunuyor.'),
(3589, 33, 56, 'Gerçekten Allah ve melekleri Peygambere salât ederler. Ey iman edenler! siz de ona teslimiyetle salât ve selâm edin.'),
(3590, 33, 57, 'Şüphesiz ki Allah\'a ve Resulü\'ne eziyet verenlere Allah hem dünyada, hem ahirette lânet etmiştir. Onlara aşağılayıcı bir azab hazırlamıştır.'),
(3591, 33, 58, 'Mümin erkeklere ve mümin kadınlara yapmadıkları bir şeyden dolayı eziyet edenler de bir iftira ve açık bir günah yüklenmişlerdir.'),
(3592, 33, 59, 'Ey peygamber! Hanımlarına, kızlarına ve müminlerin kadınlarına hep söyle de cilbablarından (dış elbiselerinden) üzerlerini sımsıkı örtsünler. Bu onların tanınmalarına, tanınıp da eziyet edilmemelerine en elverişli olandır. Bununla beraber Allah çok bağışlayıcıdır, çok merhamet edicidir.'),
(3593, 33, 60, 'Andolsun ki, eğer münafıklar ve kalblerinde bir hastalık olanlar ve Medine\'de dedikodu yapanlar, bu yaptıklarından vaz geçmezlerse, mutlaka seni onlara musallat ederiz. Sonra seninle orada az bir zamandan fazla komşu kalamazlar.'),
(3594, 33, 61, 'Melun olarak nerede bulunurlarsa yakalanırlar ve öldürülürler.'),
(3595, 33, 62, 'Allah\'ın bundan önce geçenler hakkındaki kanunu budur. Ve sen Allah\'ın kanununu değiştirmeye asla çare bulamazsın.'),
(3596, 33, 63, 'İnsanlar sana kıyamet saaatini soruyorlar. De ki: "Onun ilmi ancak Allah\'ın nezdindedir. Ne bilirsin belki kıyamet yakında olur."'),
(3597, 33, 64, 'Şu muhakkak ki, Allah kâfirleri lânetlemiş ve onlara çılgın bir ateş hazırlamıştır.'),
(3598, 33, 65, '(Onlar) orada ebedî kalırlar ve ne bir dost bulabilirler, ne de bir yardımcı.'),
(3599, 33, 66, 'O gün yüzleri ateş içinde çevirilirken: "Ah keşke Allah\'a itaat etseydik, peygambere itaat etseydik!" derler.'),
(3600, 33, 67, 'Yine derler ki: "Ey Rabbimiz! Biz beylerimize ve büyüklerimize itaat ettik de bizi yanlış yola götürdüler."'),
(3601, 33, 68, 'Ey Rabbimiz! Onlara azabın iki katını ver ve kendilerini büyük bir lânet ile lânetle."'),
(3602, 33, 69, 'Ey iman edenler: Sizler Musa\'ya eziyet edenler gibi olmayın. Eziyet ettiler de Allah onu, onların söylediklerinden temize çıkardı. O, Allah yanında mevki sahibi idi.'),
(3603, 33, 70, 'Ey iman edenler! Allah\'tan korkun ve sağlam söz söyleyin,'),
(3604, 33, 71, 'Ki (Allah) işlerinizi yoluna koysun ve günahlarınızı bağışlasın. Her kim Allah\'a ve Resulü\'ne itaat ederse, o gerçekten büyük murada ermiştir.'),
(3605, 33, 72, 'Biz o emaneti göklere, yere ve dağlara arz ettik, onlar, onu yüklenmeye yanaşmadılar, ondan korktular da onu insan yüklendi. O gerçekten çok zalim ve çok cahildir.'),
(3606, 33, 73, 'Çünkü Allah münafık erkeklerle münafık kadınlara, müşrik erkeklerle müşrik kadınlara azab edecek, mümin erkeklerle mümin kadınların da tevbelerini kabul edecektir. Allah çok bağışlayıcıdır, çok merhamet edicidir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Hamd, o Allah\'ındır ki göklerde ne var, yerde ne varsa hep O\'nundur. Ahirette de hamd O\'nundur. O hüküm ve himet sahibidir, herşeyden haberdardır.'),
(3608, 34, 2, 'Yere ne giriyor ve ondan ne çıkıyor, gökten ne iniyor ve ona ne çıkıyorsa (Allah) hepsini bilir. O çok merhamet edicidir. Çok bağışlayıcıdır.'),
(3609, 34, 3, 'İnkâr edenler: "Bize o kıyamet saati gelmez." dediler. De ki: "Hayır, öyle değil, gaybı bilen Rabbim hakkı için kıyamet size mutlaka gelecektir. O\'nun ilminden göklerde ve yerde zerre kadar bir şey kaçmaz. Bundan daha küçük ve daha büyük ne varsa, hepsi muhakkak açık bir kitaptadır."'),
(3610, 34, 4, 'Çünkü Allah iman edip iyi ameller işleyenlere mükafat verecektir. İşte onlar için bir mağfiret ve cömertçe verilmiş bol rızık vardır.'),
(3611, 34, 5, 'Âyetlerimizi hükümsüz bırakmak için yarışanlara gelince, onlar için de pek kötü ve elem verici bir azab vardır.'),
(3612, 34, 6, 'Kendilerine ilim verilmiş olanlar görüyorlar ki, Rabbinden sana indirilen Kur\'ân, hakkın kendisidir. O, gücüne nihayet olmayan, her hamde lâyık bulunan Allah\'ın yolunu gösteriyor.'),
(3613, 34, 7, 'Böyle iken inkâr edenler şöyle dediler: "Siz öldükten sonra, didik didik parçalandığınız vakit, yeniden bir yaratılış içinde bulunacaksınız diye, size birtakım haberler veren kişiyi gösterelim mi?"'),
(3614, 34, 8, 'O, bir yalanı Allah\'a iftira mı etti, yoksa kendisinde bir delilik mi var?" Hayır, doğrusu âhirete inanmayanlar, derin bir sapıklıkla azab içindedirler.'),
(3615, 34, 9, 'Ya gökten ve yerden önlerindekine ve arkalarındakine bir bakmazlar mı? Dilesek kendilerini yere geçiriveririz. Yahut gökten üzerlerine parçalar düşürüveririz. Şüphesiz bunda Allah\'a yönelen (hakka gönül veren) her kul için bir ibret vardır.'),
(3616, 34, 10, 'Andolsun ki, biz Davud\'a tarafımızdan bir fazilet verdik. "Ey dağlar! Onunla beraber tesbih edin." dedik ve bunu kuşlara da (emrettik) ve ona demiri yumuşattık.'),
(3617, 34, 11, 'Bol bol zırhlar yap ve biçimlemede ölçüyü gözet dedik. Siz de iyi işler yapın, çünkü ben her yapacağınızı gözetiyorum.'),
(3618, 34, 12, 'Süleyman\'ın emrine de rüzgarı verdik. Sabah gidişi bir aylık, akşam dönüşü bir aylık yol idi. Erimiş bakır menbaını da ona sel gibi akıttık. Hem Rabbi\'nin izniyle elinin altında cinlerden de çalışan vardı. Onlardan da kim emrimizden dışarı çıkarsa ona ateş azabından tattırırdık.'),
(3619, 34, 13, 'Onlar, ona mihrablar, timsaller (heykeller) ve havuzlar gibi çanaklar ve sâbit kazanlardan her ne isterse yaparlardı. Çalışın ey Davud hanedanı, şükür için çalışın. Ama kullarım içinde şükreden azdır.'),
(3620, 34, 14, 'Ne zaman ki Süleyman\'a ölümü hükmettik, cinlere onun ölümünü sezdiren olmadı. Yalnız bir güve böceği yere dayandığı asâsını yiyordu. Bu sebeple Süleyman yere yıkılınca ortaya çıktı ki, cinler eğer gaybı bilir olsalar o zilletli azab içinde bekleyip durmazlardı.'),
(3621, 34, 15, 'Andolsun ki Sebe\' kavmi için oturdukları yerde bir ibret vardı: Sağ ve soldan iki bahçe! (onlara): "Rabbinizin rızkından yiyin de O\'na şükredin, ne güzel bir belde ve çok bağışlayıcı bir Rab!" (denildi).'),
(3622, 34, 16, 'Fakat onlar (şükürden yüz çevirdiler) bakmadılar. Biz de üzerlerine Arim selini salıverdik ve o güzelim iki bahçelerini buruk yemişli, ılgınlık ve içinde biraz da sidir ağacı bulunan iki harap bahçeye çevirdik.'),
(3623, 34, 17, 'Bunu onlara nankörlüklerinin cezası yaptık ve biz hep böyle çok nankör olanları cezalandırırız.'),
(3624, 34, 18, 'Biz onlarla o bereket verdiğimiz memleketler arasında, sırt sırta şehirler meydana getirmiştik. Ve onlar da muntazam gidiş geliş düzenledik. (Onlara): Buralarda gecelerce ve gündüzlerce emniyet içinde gezip yürüyün (dedik).'),
(3625, 34, 19, 'Buna karşı onlar: "Ey Rabbimiz! Seferlerimizin arasını uzaklaştır" dediler ve nefislerine zulmettiler. Biz de onları efsanelere çevirdik ve tamamen didik didik dağıttık. Şüphesiz ki bunda çok şükredecek her sabırlı için elbette ibretler vardır.'),
(3626, 34, 20, 'Yine yemin ederim ki, İblis onlar hakkındaki zannını hakikaten doğru buldu da içlerinde müminlerden ibaret bir gruptan başkası ona uydular.'),
(3627, 34, 21, 'Halbuki İblis\'in onlar üzerinde hiçbir saltanat kudreti yoktu. Fakat biz ahirete imanı olanı belli edecek, ondan şüphe içinde bulunandan ayırt edecektik. Öyle ya Rabb\'in her şeyi gözetleyendir.'),
(3628, 34, 22, 'De ki: "Allah\'ı bırakıp da tanrı saydığınız putlarınıza istediğiniz kadar yalvarın. Onların ne göklerde, ne yerde zerre kadar güçleri yetmez. Onların, bunlarda bir ortaklığı da yok. Allah\'ın da onlardan bir yardımcısı yoktur."'),
(3629, 34, 23, 'Allah\'ın huzurunda şefaat da fayda vermez. Ancak izin verdiği kimseninki müstesna. Nihayet kalblerinden dehşet giderildiği zaman "Rabbiniz ne buyurdu?" derler. (Şefaat sahipleri de): "Hakkı söyledi" derler. O, her şeyden yüksek ve büyüktür.'),
(3630, 34, 24, 'De ki: "Size göklerden ve yerden rızık veren kimdir?" Yine de ki: "Allah\'tır, herhalde ya biz, ya da siz mutlak bir hidayet üzerindeyiz veya açık bir sapıklık içindeyiz."'),
(3631, 34, 25, 'De ki: "Siz bizim yaptığımız günahlardan sorumlu tutulmazsınız. Biz de sizin yaptıklarınızdan sorumlu olmayız."'),
(3632, 34, 26, 'De ki: "Rabbimiz hepimizi bir araya toplayacak, sonra da hak hükmü ile aramızı ayıracaktır. Asıl hüküm veren ve her şeyi bilen O\'dur."'),
(3633, 34, 27, 'De ki: "O\'na ortak diye takıştırdıklarınızı bana gösterin bakayım! Hayır, öyle şey yoktur, doğrusu güçlü ve hikmet sahibi olan ancak Allah\'tır."'),
(3634, 34, 28, 'Biz seni ancak bütün insanlara bir müjdeci ve bir uyarıcı olarak gönderdik. Fakat insanların çoğu bilmezler.'),
(3635, 34, 29, 'Ve: "Eğer gerçekçiyseniz bu vaad ne zaman olacak?" diyorlar.'),
(3636, 34, 30, 'De ki: "Size vaad edilen öyle bir gündür ki, ondan ne bir an geri kalabilirsiniz, ne de ileri geçebilirsiniz."'),
(3637, 34, 31, 'Kâfirler: "Biz ne bu Kur\'ân\'a inanırız, ne de ondan öncekilere." dediler. Fakat o zalimler yakalanıp Rablerinin huzuruna durduruldukları zaman, birbirlerine söz atarken bir görsen! Bir taraftan zayıf düşürülenler, o büyüklük taslayanlara: "Siz olmasaydınız biz mutlaka mümin olurduk" derler.'),
(3638, 34, 32, 'Diğer taraftan büyüklük taslayanlar, zayıf düşürülenlere: "Size hidayet geldikten sonra, sizi ondan biz mi çevirdik? Hayır, siz kendiniz suçluydunuz." derler.'),
(3639, 34, 33, 'O zayıf düşürülenler de o büyüklük taslayanlara: "Hayır, (işiniz) gece, gündüz hilekârlıktı. Çünkü siz bize Allah\'ı inkâr etmemizi ve O\'na eş koşmamızı emrediyordunuz." derler. Bunlar azabı gördükleri zaman içlerinden pişmanlık getirmektedirler. Biz de o kâfirlerin boyunlarına demir halkalar geçirmişizdir. Onlar sadece yaptıklarının cezasını çekiyorlardır.'),
(3640, 34, 34, 'Biz herhangi bir memlekete tehlikeyi haber veren bir uyarıcı gönderdikse, mutlaka oranın refah ile şımartılmış olanları: "Biz sizin gönderildiğiniz şeyleri tanımayız." dediler.'),
(3641, 34, 35, 'Ve yine dediler ki: "Biz malca da daha çoğuz, evlatça da, bize azab edilmez."'),
(3642, 34, 36, 'De ki: "Rabbim rızkı dilediğine genişletir, dilediğine sıkar. Fakat insanların çoğu bilmezler."'),
(3643, 34, 37, 'Halbuki sizi huzurumuza yaklaştıracak olan, mallarınız ve evlatlarınız değildir. Ancak iman edip de salih amel işleyenlere gelince, işte onların amellerine karşı kendilerine kat kat mükafat vardır. Onlar cennet köşklerinde emniyet içindedirler.'),
(3644, 34, 38, 'Âyetlerimizi hükümsüz bırakmak için yarışanlara gelince, işte onlar Hakk\'ın huzuruna azab içinde getirileceklerdir.'),
(3645, 34, 39, 'De ki: "Gerçekten Rabbim kullarından dilediği kimseye rızkı hem genişletir, hem daraltır. Her neyi hayra harcarsanız O, onun yerine başkasını verir. Hem O, rızık verenlerin en hayırlısıdır."'),
(3646, 34, 40, 'O gün Allah, onları hep birlikte mahşere toplayacak, sonra meleklere: "Şunlar size mi tapıyorlardı?" diyecektir.'),
(3647, 34, 41, 'Onlar da: "Seni tenzih ederiz. Bizim onlara karşı sığınacak velimiz sensin. Hayır, onlar cinlere tapıyorlardı. Çoğu onlara inanmışlardı." diyecekler.'),
(3648, 34, 42, 'İşte o gün birbirinize ne bir menfaate, ne de bir zarara sahip olabilirsiniz. Ve biz o zulmedenlere: "Tadın bakalım o yalan deyip durduğunuz ateşin azabını!" deriz.'),
(3649, 34, 43, 'Karşılarında açık deliller halinde âyetlerimiz okunduğu zaman o zalimler: "Bu, başka değil, sırf sizi atalarınızın taptığı tanrılardan men etmek isteyen bir adam." dediler. Ve: "Bu (Kur\'ân), başka bir şey değil, sırf uydurulmuş bir iftira" dediler. O kâfirler, hak kendilerine geldiği zaman: "Bu apaçık bir sihirden başka bir şey değil." dediler.'),
(3650, 34, 44, 'Halbuki biz onlara öyle ders alacakları kitaplar göndermedik. Kendilerine senden önce bir uyarıcı da göndermedik.'),
(3651, 34, 45, 'Onlardan öncekiler de yalanlamışlardı. Hem bunlar, onlara verdiklerimizin onda birine eremediler. Peygamberlerimi yalanladılar, ama beni inkâr edişin sonu nasıl oldu?'),
(3652, 34, 46, 'De ki: "Size sadece bir tek nasihat edeceğim. Şöyle ki: Allah için ikişer, üçer ve teker teker kalkarsınız, sonra da iyi düşünürsünüz." Arkadaşınızda (peygamberde) delilikten eser yoktur. O, yalnız şiddetli bir azabın önünde, sizi sakındıracak bir peygaberdir.'),
(3653, 34, 47, 'De ki: "Ben sizden herhangi bir ücret istemem, O sizin içindir. Benim ecrim ancak Allah\'a aittir. O, her şeye şahittir."'),
(3654, 34, 48, 'De ki: "Gerçekten Rabbim, hakkı yerli yerine koyar. O, gaybları hakkıyla bilendir."'),
(3655, 34, 49, 'De ki: "Hak geldi, batılın önü de kalmaz, sonu da."'),
(3656, 34, 50, 'De ki: "Eğer ben yanılırsam, yalnız kendi adıma yanılırım. Ve eğer hidayeti bulmuşsam, bilinmeli ki Rabbimin bana vahiy vermesiyledir. Çünkü O, yakındır, işitir, işittirir."'),
(3657, 34, 51, 'Onları telaşa düştükleri zaman görsen: Artık kaçamak yoktur. Yakın yerden yakalanmışlardır.'),
(3658, 34, 52, 'Ve: "O\'na iman ettik" demektedirler. Fakat onlar için (âhiret gibi) uzak bir yerden (imana) el sunmak (ulaşabilmek) nerede?'),
(3659, 34, 53, 'Halbuki daha önce (dünyada) O\'nu inkâr etmişlerdi. Uzak yerden gayba taş atıyorlardı.'),
(3660, 34, 54, 'Artık kendileriyle arzularının arasına set çekilmiştir. Tıpkı bundan önce benzerlerine yapıldığı gibi. Çünkü hepsi işkilli bir şüphe içinde bulunuyorlardı');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Hamd, gökleri ve yeri yaratan, melekleri ikişer, üçer, dörder kanatlı elçiler kılan Allah\'a mahsustur. O, yaratmada dilediği kadar artırır. Gerçekten Allah her şeye kâdirdir.'),
(3662, 35, 2, 'Allah, insanlara rahmetinden neyi açarsa artık onu tutacak, kısacak olan yoktur. Her neyi de tutar kısarsa, onu da, ondan sonra salacak yoktur. O, çok güçlüdür, hüküm ve hikmet sahibidir.'),
(3663, 35, 3, 'Ey insanlar! Allah\'ın üzerinizdeki nimetini anın. Allah\'tan başka bir yaratıcı mı var? O size gökten ve yerden rızık verir. O\'ndan başka ilâh yoktur. O halde (haktan) nasıl çevrilirsiniz?'),
(3664, 35, 4, 'Eğer onlar seni yalanlıyorlarsa, senden önce birçok peygamberler de yalanlandılar. Bütün işler Allah\'a döndürülür.'),
(3665, 35, 5, 'Ey insanlar! Haberiniz olsun ki, Allah\'ın vaadi muhakkak haktır. Sakın bu dünya hayatı sizi aldatmasın, sakın o aldatıcı şeytan sizi, Allah hakkında da aldatmasın.'),
(3666, 35, 6, 'Çünkü şeytan size düşmandır. Siz de onu düşman tutun. O etrafına toplanan taraftarlarını ancak cehennemliklerden olsunlar diye davet eder.'),
(3667, 35, 7, 'İnkâr edenler için şiddetli bir azab vardır. İman edip salih amel işleyenler için de bir bağışlanma ve büyük bir mükafat vardır.'),
(3668, 35, 8, 'Ya kötü ameli kendisine allanmış pullanmış da onu güzel görmüş olan kimse de mi (iman edip salih amel işleyenler gibi olacak)? Şüphe yok ki Allah dilediğini şaşırtır, dilediğini de doğru yola çıkarır. O halde canın onlara karşı hasretlerle (üzüntülerle) sıkılıp gitmesin. Çünkü Allah, onların bütün yaptıklarını bilir.'),
(3669, 35, 9, 'Rüzgârları gönderip bir bulut kaldıran da Allah\'tır. Derken biz o (bulutu) ölmüş bir beldeye sevketmişizdir. Böylece yeryüzüne ölmünden sonra onunla hayat veririz. İşte o dirilme de böyledir.'),
(3670, 35, 10, 'Her kim izzet istiyorsa bilsin ki izzet tamamıyla Allah\'ındır. O\'na hoş kelimeler yükselir, onu da salih amel yükseltir. Kötülükler kuranlara gelince, onlara şiddetli bir azab vardır. Onların tuzakları hep darmadağın olur.'),
(3671, 35, 11, 'Hem Allah sizi bir topraktan, sonra bir damla sudan yarattı. Sonra sizi çiftler kıldı. O\'nun bilgisi olmadan ne bir dişi hamile olur, ne doğurur. Kendisine ömür verilenin de ömrünün uzatılması da, ömründen kısaltılması da mutlaka bir kitapta yazılıdır. Şüphe yok ki bu, Allah\'a göre kolaydır.'),
(3672, 35, 12, 'Hem iki deniz eşit olmuyor. Şu tatlı, hararet keser, içerken (boğazdan) kayar; şu da tuzlu, yakar kavurur. Bununla beraber her birinden taze bir et yersiniz ve bir ziynet çıkarır, giyinirsiniz. Allah\'ın lütfundan nasib arayasınız diye suyu yara yara giden gemileri de görürsün. Gerek ki şükredeceksiniz.'),
(3673, 35, 13, 'O, geceyi gündüze sokuyor, gündüzü de geceye sokuyor. Güneşi ve ayı emrine âmâde kılmıştır. Her biri mukadder bir gayeye akıp gidiyor. İşte bu gördüklerinizi yapan Allah sizin Rabbinizdir. Mülk (hükümranlık) O\'nundur. O\'ndan başka taptıklarınız ise, bir çekirdek zarını bile idare edemezler.'),
(3674, 35, 14, 'Kendilerine dua ederseniz duanızı işitmezler. İşitseler bile size cevabını veremezler. Kıyamet günü de kendilerini Allah\'a ortak koştuğunuzu inkâr ederler. Sana her şeyden haberdar olan (Allah) gibi bir haber veren olmaz.'),
(3675, 35, 15, 'Ey insanlar! Siz Allah\'a muhtaçsınız. Allah ise zengin ve her hamde lâyıktır.'),
(3676, 35, 16, 'Eğer O dilerse sizi yok eder ve yerinize yeni bir halk getirir.'),
(3677, 35, 17, 'Ve bu, Allah\'a göre zor bir şey değildir.'),
(3678, 35, 18, 'Hem günah çeken bir kimse, başkasının günahını çekmeyecek; yükü ağır basan, onun yüklenilmesine çağırsa da ondan bir şey yüklenilmeyecek, isterse bir yakını olsun. Fakat sen ancak o kimseleri sakındırısın ki, gaybda Rablerinin korkusunu duyarlar, namazı dürüst kılarlar. Temizlenen de sırf kendisi için temizlenir. Nihayet dönüş Allah\'adır.'),
(3679, 35, 19, 'Ne kör ile gören eşit olur,'),
(3680, 35, 20, 'Ne de karanlıklar ile aydınlık,'),
(3681, 35, 21, 'Ve ne de gölge ile sıcaklık.'),
(3682, 35, 22, 'Ölülerle diriler de eşit olmaz. Gerçi Allah, her dilediğine işittirirse de sen, kabirlerdekine işittirecek değilsin.'),
(3683, 35, 23, 'Sen sadece bir uyarıcısın.'),
(3684, 35, 24, 'Muhakkak ki biz seni hak ile hem bir müjdeci, hem bir uyarıcı olarak gönderdik. Hiçbir ümmet de yoktur ki, içlerinde bir uyarıcı geçmiş olmasın.'),
(3685, 35, 25, 'Seni yalanlıyorlarsa, onlardan öncekiler de yalanlamışlardı. Onlara peygamberleri mucizelerle, sahifelerle ve aydınlatıcı kitaplarla gelmişlerdi.'),
(3686, 35, 26, 'Sonra ben o inkâr edenleri tutup yakaladım. O zaman beni inkâr etmek nasıl oldu?'),
(3687, 35, 27, 'Görmedin mi Allah gökten bir su indirdi. Biz onunla renkleri başka başka meyveler çıkardık. Dağlarda da yollar, beyazlı kırmızılı çeşitli renklerde ve kapkara topraklar var.'),
(3688, 35, 28, 'Yine insanlardan, hayvanlardan ve davarlardan da türlü renklileri vardır. Kulları içinde Allah\'tan ancak âlimler korkar. Şüphe yok ki Allah çok güçlüdür. Hüküm ve hikmet sahibidir.'),
(3689, 35, 29, 'Allah\'ın kitabını okuyan, namazı kılan ve kendilerine verdiğimiz rızıktan gizli ve açık olarak verenler, kesinlikle batma ihtimali olmayan bir ticaret umarlar.'),
(3690, 35, 30, 'Çünkü Allah mükafatlarını kendilerine tamamen ödedikten başka, lütfundan onlara fazlasını da verecektir. Çünkü O çok bağışlayıcı ve şükrün karşılığını vericidir.'),
(3691, 35, 31, 'Kitaplar içinde sana vahyettiğimiz kitap da kendinden öncekileri tasdik edici olmak üzere bir haktır. Şüphe yok ki, Allah, kullarının bütün hallerinden haberdardır ve her şeyi görendir.'),
(3692, 35, 32, 'Sonra biz o kitabı kullarımızdan süzüp seçtiklerimize miras bıraktık. Onlardan da nefislerine zulmeden var, orta yolu tutan var, Allah\'ın izniyle hayırlarda ileri geçenler var. İşte bu büyük lütuftur.'),
(3693, 35, 33, 'Onlara Adn cennetleri vardır. Onlar oraya gireceklerdir. Orada altın bilezikler ve incilerle süsleneceklerdir. Orada elbiseleri de ipektir.'),
(3694, 35, 34, 'Onlar orada şöyle derler: "Hamd olsun Allah\'a, bizden o üzüntüyü giderdi. Gerçekten Rabbimiz çok bağışlayıcı ve şükrün karşılığını vericidir."'),
(3695, 35, 35, '"Lütfundan bizi durulacak bir yurda kondurdu. Burada bize yorgunluk gelmeyecek, burada bize usanç gelmeyecektir."'),
(3696, 35, 36, 'İnkâr edenlere gelince, onlara cehennem ateşi vardır. Hüküm verilmez ki ölsünler, kendilerinden biraz azab da hafifletilmez. İşte biz her nankörü böyle cezalandırırız.'),
(3697, 35, 37, 'Onlar, orada şöyle feryad ederler: "Ey Rabbimiz! Bizleri çıkar, yapageldiklerimizden başka salih bir amel yapalım." (Onlara): "Size düşünecek olanın düşüneceği kadar bir ömür vermedik mi? Hem size uyarıcı da gelmişti. O halde azabı tadın. Çünkü zalimleri kurtaracak yoktur." (denir).'),
(3698, 35, 38, 'Şüphe yok ki Allah, göklerin ve yerin gaybını bilir. Elbette o, sinelerin içinde olanları da bilir.'),
(3699, 35, 39, 'Sizi yeryüzünde halifeler yapan O\'dur. Artık kim küfrederse, küfrü kendi aleyhinedir. Kâfirlerin küfürleri, Rablerinin katında kendilerine buğzdan başka bir şey artırmaz, kâfirlerin küfürleri kendilerine zarardan başka bir şey artırmaz.'),
(3700, 35, 40, 'De ki: "Gördünüz ya, Allah\'ı bırakıp da tapmakta olduğunuz ortaklarınızı! Gösterin bana, yer yüzünden neyi yaratmışlardır?" Yoksa onların gök yüzünde bir ortaklığı mı var? Yoksa biz kendilerine bir kitap vermişiz de ondan bir delil üzerinde mi bulunuyorlar? Hayır o zalimler, birbirlerine aldatmadan başka bir vaadde bulunmuyorlar.'),
(3701, 35, 41, 'Doğrusu gökleri ve yeri yok oluvermekten, Allah tutuyor. Andolsun ki eğer yok oluverirlerse, onları O\'ndan başka kimse tutamaz. Gerçekten O, çok yumuşak davranır, çok bağışlayıcıdır.'),
(3702, 35, 42, 'Olanca güçleriyle Allah\'a yemin etmişlerdi ki, kendilerine uyarıcı bir peygamber gelirse, mutlaka ilerideki ümmetlerin herhagi birinden daha doğru yolda olacaklardı. Fakat kendilerine uyarıcı bir peygamber geldiği zaman bu, onların sırf ürküntüleriniartırdı.'),
(3703, 35, 43, '(Bu da) yeryüzünde bir kibirlenme ve bir suikast düzenidir. Halbuki fena düzen ancak sahibinin başına geçer. O halde öncekilerin kanunundan başka ne gözetiyorlar? Sen Allah\'ın sünnetinde asla bir değişme bulamazsın. Sen Allah\'ın sünnetinde asla bir başkalaşma da bulamazsın.'),
(3704, 35, 44, 'Yeryüzünde gezip bir bakmadılar mı, kendilerinden öncekilerin sonu nasıl olmuş? Halbuki onlar, bunlardan daha kuvvetliydiler. Ne göklerde ve ne de yerde hiçbir şey Allah\'ı aciz bırakamaz. Çünkü o her şeyi bilendir, her şeye kâdir olandır.'),
(3705, 35, 45, 'Bununla beraber Allah, insanları kazandıkları (günahlar) yüzünden hemen yakalayıverseydi, yeryüzünde hiçbir canlı bırakmazdı. Fakat onları belli bir süreye kadar erteliyor. Nihayet ecelleri gelince gereğini yapar. Şüphe yok ki Allah, kullarını görmektedir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Yâsîn.'),
(3707, 36, 2, 'Ey Muhammed! Hikmetli Kur\'ân\'a andolsun ki, sen risâlet görevi ile gönderilen peygamberlerdensin.'),
(3708, 36, 3, 'Ey Muhammed! Hikmetli Kur\'ân\'a andolsun ki, sen risâlet görevi ile gönderilen peygamberlerdensin.'),
(3709, 36, 4, 'Dosdoğru bir yol üzerindesin.'),
(3710, 36, 5, 'Babaları korkutulmamış ve kendileri de gafil olan bir kavmi, çok güçlü ve çok merhametli olan Allah\'ın indirdiği (Kur\'ân) ile korkutasın.'),
(3711, 36, 6, 'Babaları korkutulmamış ve kendileri de gafil olan bir kavmi, çok güçlü ve çok merhametli olan Allah\'ın indirdiği (Kur\'ân) ile korkutasın.'),
(3712, 36, 7, 'Andolsun ki onların çoğunun üzerine azab sözü hak olmuştur. Onlar imana gelmezler.'),
(3713, 36, 8, 'Çünkü biz onların boyunlarına kelepçeler geçirmişiz. O kelepçeler çenelerine dayanmıştır da burunları yukarı, gözleri aşağı somurtmaktadırlar.'),
(3714, 36, 9, 'Hem önlerinden bir sed, arkalarından bir sed çekmişiz, kendilerini sarmışızdır. Baksalar da görmezler.'),
(3715, 36, 10, 'Onları korkutsan da korkutmasan da onlara göre birdir, inanmazlar.'),
(3716, 36, 11, 'Sen ancak Kur\'ân\'a tabi olan ve görünmediği halde Rahman olan Allah\'tan korkan kimseyi sakındırırsın. İşte onu bir bağışlanma ve çok şerefli bir mükafatla müjdele.'),
(3717, 36, 12, 'Gerçekten biz ölüleri diriltiriz, onların önceden yapıp gönderdiklerini ve bıraktıkları eserlerini yazarız. Zaten biz her şeyi açık bir kütükte, bir "imamı mübin"de (ana kitapta, yani Levhi mahfuzda) sayıp tesbit etmişizdir.'),
(3718, 36, 13, 'Sen onlara, o şehir halkını örnek ver. Hani oraya peygamberler gelmişti.'),
(3719, 36, 14, 'Hani biz onlara iki peygamber göndermiştik, fakat onlar ikisini de yalanlamışlardı. Biz de (onları) üçüncü bir peygamberle destekledik. Onlara: "Şüphesiz ki biz size gönderilmiş elçileriz." dediler.'),
(3720, 36, 15, 'Onlar da: "Siz bizim gibi insandan başka birşey değilsiniz, hem Rahman olan Allah, hiçbir şey indirmedi. Siz sadece yalan söylüyorsunuz." dediler.'),
(3721, 36, 16, 'Peygamberler dediler ki: "Rabbimiz biliyor ki biz gerçekten size gönderilmiş elçileriz."'),
(3722, 36, 17, '"Bize düşen de sadece apaçık tebliğdir."'),
(3723, 36, 18, 'Onlar dediler ki: "Herhalde biz sizin yüzünüzden uğursuzluğa uğradık. Eğer bu işten vazgeçmezseniz, andolsun ki, sizi hiç tınmadan taşlarız ve mutlaka bizden size pek acıklı bir azab dokunur."'),
(3724, 36, 19, 'Peygamberler de şöyle cevap verdiler: "Sizin uğursuzluğunuz beraberinizdedir. Size öğüt verildi diye mi (uğursuzluğa uğradınız)? Doğrusu siz israfı âdet etmiş bir kavimsiniz."'),
(3725, 36, 20, 'O sırada şehrin ta ucundan bir adam koşarak geldi ve: "Ey kavmim! Uyun o elçilere!"'),
(3726, 36, 21, '"Uyun sizden hiçbir ücret istemeyen o zatlara ki, onlar hidayete ermişlerdir."'),
(3727, 36, 22, '"Bana ne oluyor da kulluk etmeyecekmişim beni yaratana? Hep döndürülüp O\'na götürüleceksiniz."'),
(3728, 36, 23, '"Hiç ben O\'ndan başka ilâhlar edinir miyim? Eğer O Rahman, bana bir zarar dileyecek olsa, onların şefaati benden yana hiçbir şeye yaramaz ve onlar beni kurtaramazlar."'),
(3729, 36, 24, '"Şüphesiz ki ben, o zaman apaçık bir sapıklık içinde olurum."'),
(3730, 36, 25, '"Şüphesiz ki ben, Rabbinize iman getirdim, gelin dinleyin beni."'),
(3731, 36, 26, '(Sonra ona) "haydi gir cennete!" denildi. O da dedi ki: "Ne olurdu kavmim bilseydi!"'),
(3732, 36, 27, '"Rabbimin beni bağışladığını ve beni kendilerine ikram edilen kullarından kıldığını."'),
(3733, 36, 28, 'Biz arkasından kavminin üzerine bir ordu indirmedik, indirecek de değildik.'),
(3734, 36, 29, 'Sadece bir gürültü oldu, onlar da hemen sönüverdiler.'),
(3735, 36, 30, 'Yazıklar olsun o kullara ki, kendilerine glen her bir peygamberle mutlaka alay ediyorlardı.'),
(3736, 36, 31, 'Görmediler mi ki, kendilerinden önce nice kuşakları helak etmişiz. Onlar artık kendilerine dönüp gelmiyorlar.'),
(3737, 36, 32, 'Onların hepsi toplanıp, sadece bizim huzurumuza getirilmişlerdir.'),
(3738, 36, 33, 'Hem bir delildir onlara ölü toprak. Biz ona hayat verdik ve ondan taneler çıkardık da ondan yiyip duruyorlar.'),
(3739, 36, 34, 'Biz orada hurmalıklardan, üzüm bağlarından bahçeler yaptık. İçlerinde pınarlardan sular fışkırttık.'),
(3740, 36, 35, '(Bunu), Onun ürününden ve kendi elleriyle yaptıklarından yesinler diye (yaptık). Hâlâ şükretmeyecekler mi?'),
(3741, 36, 36, 'Yerin bitkilerinden, kendi nefislerinden ve daha bilemeyecekleri şeylerden bütün çiftleri yaratan Allah\'ın şanı ne yücedir.'),
(3742, 36, 37, 'Gece de onlara bir delildir. Biz ondan gündüzü soyar çıkarırız, bir de bakarlar ki karanlığa dalmışlar.'),
(3743, 36, 38, 'Güneş de bir delildir ki kendi yolunda akıp gidiyor. İşte bu çok güçlü ve her şeyi bilen Allah\'ın takdiridir.'),
(3744, 36, 39, 'Ay\'a gelince, ona menziller tayin ettik. Nihayet o eski hurma salkımının çöpü gibi (yay haline) dönmüştür.'),
(3745, 36, 40, 'Ne güneşin aya çatması yaraşır, ne de gece gündüzü geçebilir; onların her biri kendi yörüngesinde yüzerler.'),
(3746, 36, 41, 'Onlar için bir delil de bizim, onların neslini dolu bir gemide taşımamızdır.'),
(3747, 36, 42, 'Yine kendileri için onun gibi binecek şeyler yaratmamızdır.'),
(3748, 36, 43, 'Eğer dilesek onları boğarız da o zaman ne onların feryadına yetişen bulunur, ne de onlar kurtarılır.'),
(3749, 36, 44, 'Ancak tarafımızdan bir rahmet ve bir zamana kadar yaşatmak başka.'),
(3750, 36, 45, 'Durum böyle iken onlara: "Önünüzdekinden ve arkanızdakinden korkun ki size rahmet edilsin" denildiği zaman,'),
(3751, 36, 46, 'Ve kendilerine Rablerinin âyetlerinden herhangi bir âyet geldiği zaman mutlaka ondan yüz çevirirler.'),
(3752, 36, 47, 'Onlara: "Allah\'ın size rızık olarak verdiği şeylerden hayra harcayın" dendiği zaman, o kâfirler, müminler için: "Allah\'ın dileyince doyurabileceği kimseyi biz mi doyuracağız? Siz apaçık bir sapıklık içinde değil de nesiniz?" dediler.'),
(3753, 36, 48, 'Yine onlar: "Eğer doğru söylüyorsanız bu (kıyamet) vaadi ne zaman?" diyorlar.'),
(3754, 36, 49, 'Onlar sadece bir tek çığlığa bakıyorlar, bir çığlık ki, onlar çekişip dururken kendilerini yakalayıverir.'),
(3755, 36, 50, 'O zaman bir vasiyette bile bulunamazlar. Ailelerine de dönemezler.'),
(3756, 36, 51, 'Sûr\'a üfürülmüştür, bir de ne baksınlar kabirlerinden Rablerine doğru akın ediyorlar.'),
(3757, 36, 52, 'Onlar: "Eyvah başımıza gelenlere! Mezarımızdan bizi kim kaldırdı? O Rahmân\'ın vaad buyurduğu işte bu imiş. Gönderilen peygamberler de doğru söylemişler" derler.'),
(3758, 36, 53, 'Başka değil, sadece bir tek çığlık olmuş, derhal hepsi toplanmış huzurumuza getirilmişlerdir.'),
(3759, 36, 54, 'Artık bugün hiç kimseye zerre kadar zulmedilmez. Ancak yaptıklarınızın cezasını çekeceksiniz.'),
(3760, 36, 55, 'Gerçekten cennetlik olanlar bugün bir meşguliyet içinde zevk etmektedirler.'),
(3761, 36, 56, 'Kendileri ve eşleri gölgelerde koltuklar üzerine kurulmuşlardır.'),
(3762, 36, 57, 'Onlara orada bir meyve vardır. İsteyecekleri her şey onlarındır.'),
(3763, 36, 58, '(Onlara) Rahîm olan Rab\'den "selâm" sözü vardır.'),
(3764, 36, 59, 'Ey günahkârlar! Bugün siz bir tarafa ayrılın.'),
(3765, 36, 60, '"Ey Âdemoğulları! Şeytana tapmayın, o size apaçık bir düşmandır ve bana kulluk edin, doğru yol budur, diye size and vermedim mi?" (buyurulacak)'),
(3766, 36, 61, '"Ey Âdemoğulları! Şeytana tapmayın, o size apaçık bir düşmandır ve bana kulluk edin, doğru yol budur, diye size and vermedim mi?" (buyurulacak)'),
(3767, 36, 62, 'Böyle iken o sizden birçok nesilleri yoldan çıkardı. Ya o zaman düşünmüyor muydunuz?'),
(3768, 36, 63, 'İşte bu size vaad edilen cehennemdir.'),
(3769, 36, 64, 'Bugün yaslanın ona bakalım inkâr ettiğiniz için.'),
(3770, 36, 65, 'Bugün biz onların ağızlarını mühürleriz de neler kazandıklarını bize elleri söyler, ayakları da şahitlik eder.'),
(3771, 36, 66, 'Hem dileseydik gözlerini üzerinden silme kör ediverirdik de yola dökülürlerdi. Fakat nereden görecekler?'),
(3772, 36, 67, 'Yine dileseydik oldukları yerde kılıklarını değiştirirdik de ne ileri gidebilirlerdi, ne de geri dönebilirlerdi.'),
(3773, 36, 68, 'Bununla beraber kimin ömrünü uzatıyorsak, yaratılışta onu (güç ve kuvvetini alarak) tersine çeviriyoruz. Hâlâ akıllanmayacaklar mı?'),
(3774, 36, 69, 'Biz ona şiir öğretmedik. Bu ona yaraşmaz da... O sadece bir öğüt ve apaçık bir Kur\'ân\'dır.'),
(3775, 36, 70, '(Bu), diri olanları uyarmak ve kâfirlere de azab sözünün hak olması içindir.'),
(3776, 36, 71, 'Şunu da görmediler mi: Biz onlar için kudretimizin meydana getirdiklerinden birtakım hayvanlar yaratmışız da onlara sahip bulunuyorlar.'),
(3777, 36, 72, 'Onları, kendilerinin hizmetine vermişiz de, hem onlardan binekleri var, hem de onlardan yiyorlar.'),
(3778, 36, 73, 'Onlarda daha birçok menfaatleri ve türlü içecekleri de var. Hâlâ şükretmeyecekler mi?'),
(3779, 36, 74, 'Onlar, Allah\'tan başka birtakım ilâhlar edindiler. Güya yardım olunacaklar.'),
(3780, 36, 75, 'Onların, onlara yardıma güçleri yetmez. Kendileri ise onlar için bazı askerlerdir.'),
(3781, 36, 76, 'O halde onların sözleri seni üzmesin. Biz onların içlerini de biliriz, dışlarını da.'),
(3782, 36, 77, 'İnsan, kendisini bir damla sudan yarattığımızı görmedi mi de, şimdi apaçık bir hasım kesildi?'),
(3783, 36, 78, 'Yaratılışını unutarak bize bir de mesel fırlattı: "Kim diriltecekmiş o çürümüş kemikleri?" dedi.'),
(3784, 36, 79, 'De ki: "Onları ilk defa yaratan diriltecek ve o her yaratmayı bilir."'),
(3785, 36, 80, 'Size o yeşil ağaçtan bir ateş yapan O\'dur. Şimdi siz ondan tutuşturmaktasınız.'),
(3786, 36, 81, 'Gökleri ve yeri yaratan, onlar gibisini yaratmaya kâdir değil midir? Elbette kâdirdir. Çünkü o her şeyi yaratandır, her şeyi bilendir.'),
(3787, 36, 82, 'O\'nun emri, bir şeyi dileyince ona sadece "Ol!" demektir. O da hemen oluverir.'),
(3788, 36, 83, 'O halde her şeyin mülkü ve tasarrufu (hükümranlığı) elinde bulunan Allah\'ın şanı ne yücedir. Siz de yalnız O\'na döndürüleceksiniz.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'Andolsun o saf bağlayıp duranlara.'),
(3790, 37, 2, 'O haykırıp da sürenlere.'),
(3791, 37, 3, 'Ve o yolda zikir okuyanlara.'),
(3792, 37, 4, 'Ki sizin ilâhınız birdir.'),
(3793, 37, 5, 'O, göklerin, yerin ve aralarındakilerin Rabbidir, bütün doğuların da Rabbidir.'),
(3794, 37, 6, 'Gerçekten biz dünya göğünü (o yakın göğü) bir zinetle, yıldızlarla süsledik.'),
(3795, 37, 7, 'Onu her inatçı şeytandan koruduk.'),
(3796, 37, 8, 'Onlar yüksek (melekler) topluluğunu dinleyemezler. Her taraftan kovulup atılırlar.'),
(3797, 37, 9, 'Uzaklaştırılırlar. Onlara ardı arkası kesilmez bir azab vardır.'),
(3798, 37, 10, 'Ancak kulak hırsızlığı yapanlar olur. Onu da yakıcı bir alev takip eder.'),
(3799, 37, 11, 'Şimdi onlara sor: "Yaradılışça kendileri mi daha çetin, yoksa bizim yarattıklarımız mı?" Gerçekten biz onları cıvık bir çamurdan yarattık.'),
(3800, 37, 12, 'Fakat sen onlara şaşıyorsun, ama onlar (seninle) eğleniyorlar.'),
(3801, 37, 13, 'Kendilerine hatırlatıldığında da düşünmüyorlar.'),
(3802, 37, 14, 'Bir mucize gördükleri zaman da eğlenceye alıyorlar.'),
(3803, 37, 15, 'Ve diyorlar ki: "Bu apaçık büyüden başka bir şey değildir."'),
(3804, 37, 16, '"Öldüğümüz ve bir toprakla bir yığın kemik olduğumuz zaman mı biz tekrar dirilecekmişiz?"'),
(3805, 37, 17, '"Önceki atalarımız da mı?.."'),
(3806, 37, 18, 'De ki: "Evet, hem de sizler çok aşağılanmış olarak (dirileceksiniz)."'),
(3807, 37, 19, 'Çünkü O (sura üfürmek) zorlu bir kumandadan ibarettir ki, derhal onların gözleri açılıverir.'),
(3808, 37, 20, '"Eyvah bizlere! İşte bu hesap günüdür." derler.'),
(3809, 37, 21, '(Onlara): "İşte bu, sizin yalanlamakta olduğunuz (iyi ve kötüyü) ayırt etme günüdür" denir.'),
(3810, 37, 22, 'Toplayın mahşere o zulmedenleri, eşlerini ve Allah\'tan başka taptıkları şeyleri. Toplayın da götürün onları sırata (cehennem köprüsüne) doğru.'),
(3811, 37, 23, 'Toplayın mahşere o zulmedenleri, eşlerini ve Allah\'tan başka taptıkları şeyleri. Toplayın da götürün onları sırata (cehennem köprüsüne) doğru.'),
(3812, 37, 24, 'Ve durdurun onları, çünkü sorguya çekilecekler.'),
(3813, 37, 25, '(Onlara): "Ne oldu sizlere de yardımlaşmıyorsunuz?" (denilir.)'),
(3814, 37, 26, 'Hayır, bugün onlar teslim olmuşlardır.'),
(3815, 37, 27, 'Onlar, birbirine dönmüş soruşuyorlar.'),
(3816, 37, 28, 'Onlar: "Siz bize (uğurlu görünerek) sağdan gelir dururdunuz" derler.'),
(3817, 37, 29, '(İleri gelenler de) derler ki: "Hayır, siz inanmamıştınız."'),
(3818, 37, 30, '"Bizim de size karşı bir gücümüz yoktu. Fakat siz azmış bir kavimdiniz."'),
(3819, 37, 31, '"Onun için üzerimize Rabbimizin azab sözü hak oldu. Şüphesiz azabımızı tadacağız."'),
(3820, 37, 32, '"Evet biz, sizi kışkırttık. Çünkü biz azgındık."'),
(3821, 37, 33, 'O halde hepsi o gün azabda ortaktırlar.'),
(3822, 37, 34, 'İşte biz günahkarlara böyle yaparız.'),
(3823, 37, 35, 'Çünkü onlar, kendilerine: "Allah\'tan başka ilâh yoktur" denildiği zaman kafa tutuyorlardı.'),
(3824, 37, 36, 'Ve: "Biz, hiçbir mecnun (deli) şair için ilâhlarımızı bırakır mıyız?" diyorlardı.'),
(3825, 37, 37, 'Hayır o, hak ile geldi ve bütün peygamberleri tasdik etti.'),
(3826, 37, 38, 'Elbette siz o acı azabı tadacaksınız.'),
(3827, 37, 39, 'Bununla beraber başka değil, hep yaptığınız amellerinizle cezalandırılacaksınız.'),
(3828, 37, 40, 'Sadece Allah\'ın ihlaslı kulları müstesnadır.'),
(3829, 37, 41, 'İşte onlar için belli bir rızık vardır.'),
(3830, 37, 42, 'Meyveler (vardır), Naîm cennetlerinde onlara hep ikram edilir.'),
(3831, 37, 43, 'Meyveler (vardır), Naîm cennetlerinde onlara hep ikram edilir.'),
(3832, 37, 44, '(Onlar) Karşılıklı tahtlar üzerindedirler.'),
(3833, 37, 45, 'İçenlere lezzet veren, pınardan doldurulmuş bembeyaz bir kadehle onların etrafında dolaşılır.'),
(3834, 37, 46, 'İçenlere lezzet veren, pınardan doldurulmuş bembeyaz bir kadehle onların etrafında dolaşılır.'),
(3835, 37, 47, 'Onda ne bir zararlı sonuç vardır, ne de sarhoşluk verir.'),
(3836, 37, 48, 'Yanlarında iri gözlü, bakışlarını kocalarından başkalarına çevirmeyen hanımlar vardır.'),
(3837, 37, 49, 'Sanki onlar örtülüp saklanmış yumurta gibidirler.'),
(3838, 37, 50, 'Derken birbirine dönüp sorarlar:'),
(3839, 37, 51, 'İçlerinden bir sözcü der ki: "Gerçekten benim bir arkadaşım vardı."'),
(3840, 37, 52, 'Derdi ki: "Sen gerçekten inananlardan mısın?"'),
(3841, 37, 53, '"Öldüğümüz ve bir toprakla bir yığın kemik olduğumuz zaman biz hakikaten cezalanacak mıyız?"'),
(3842, 37, 54, '"Siz onu tanır mısınız?" der.'),
(3843, 37, 55, 'Derken bakınır ve onu cehennemin ta ortasında görür.'),
(3844, 37, 56, 'Ona şöyle der: "Allah\'a yemin ederim ki, doğrusu sen az daha beni helak edecektin."'),
(3845, 37, 57, '"Rabbimin nimeti olmasaydı, ben de bu tutuklananlardan olacaktım."'),
(3846, 37, 58, '"Nasılmış bak. Biz ilk ölümümüzden başka bir daha ölmeyecek miymişiz? Biz azaba uğratılmayacak mıymışız?'),
(3847, 37, 59, '"Nasılmış bak. Biz ilk ölümümüzden başka bir daha ölmeyecek miymişiz? Biz azaba uğratılmayacak mıymışız?'),
(3848, 37, 60, 'İşte bu büyük kurtuluştur.'),
(3849, 37, 61, 'Çalışanlar işte böyle bir kurtuluş için çalışsınlar.'),
(3850, 37, 62, 'Nasıl, bu mu daha hayırlı konukluk için, yoksa zakkum ağacı mı?'),
(3851, 37, 63, 'Gerçekten biz onu zalimler için bir fitne (imtihan) yaptık.'),
(3852, 37, 64, 'O bir ağaçtır ki cehennemin dibinde çıkar.'),
(3853, 37, 65, 'Tomurcukları şeytanların başları gibidir.'),
(3854, 37, 66, 'Mutlaka onlar, ondan yiyecekler de karınlarını bundan dolduracaklardır.'),
(3855, 37, 67, 'Sonra üzerine onlar için kaynar bir içecek vardır.'),
(3856, 37, 68, 'Sonra da dönecekleri yer, şüphesiz cehennemdir.'),
(3857, 37, 69, 'Çünkü onlar, atalarını sapıklıkta buldular.'),
(3858, 37, 70, 'Şimdi de kendileri onların izlerinde koşturuyorlar.'),
(3859, 37, 71, 'Andolsun ki, onlardan öncekilerin çoğu sapıklıkta idiler.'),
(3860, 37, 72, 'Gerçekten biz onlara içlerinden uyarıcı peygamberler de gönderdik.'),
(3861, 37, 73, 'Sonra da bak o uyarılanların sonu nasıl oldu?'),
(3862, 37, 74, 'Ancak Allah\'ın ihlas ile seçilen kulları başka.'),
(3863, 37, 75, 'Andolsun ki Nuh bize seslenip dua etmişti de biz de ne güzel kabul etmiştik.'),
(3864, 37, 76, 'Biz hem onu, hem ailesini o büyük sıkıntıdan kurtardık.'),
(3865, 37, 77, 'Hem onun neslini bâki kalanlar kıldık.'),
(3866, 37, 78, 'Hem de sonradan gelenler içinde güzel bir namını bıraktık.'),
(3867, 37, 79, 'Bütün âlemler içinde Nuh\'a selam olsun.'),
(3868, 37, 80, 'İşte biz iyilik yapanları böyle mükafatlandırırız.'),
(3869, 37, 81, 'Çünkü o bizim mümin kullarımızdandı.'),
(3870, 37, 82, 'Sonra diğerlerini suda boğduk.'),
(3871, 37, 83, 'Şüphesiz ki İbrahim de onun kolundandı.'),
(3872, 37, 84, 'Çünkü o, Rabbine tertemiz bir kalb ile gelmişti.'),
(3873, 37, 85, 'O babasına ve kavmine şöyle demişti: "Siz nelere tapıyorsunuz?"'),
(3874, 37, 86, '"Yalancılık etmek için mi Allah\'tan başka ilâhlar istiyorsunuz?"'),
(3875, 37, 87, '"Siz âlemlerin Rabbini ne zannediyorsunuz?"'),
(3876, 37, 88, 'Derken yıldızlara bir baktı da: "Ben gerçekten hastayım" dedi.'),
(3877, 37, 89, 'Derken yıldızlara bir baktı da: "Ben gerçekten hastayım" dedi.'),
(3878, 37, 90, 'O zaman arkalarını dönerek başından kaçışıverdiler.'),
(3879, 37, 91, 'Derken bir kurnazlıkla onların ilâhlarına vardı da, "Buyursanıza, yemez misiniz?" dedi.'),
(3880, 37, 92, '(Cevap vermediklerini görünce de): "Neyiniz var da konuşmuyorsunuz?" (dedi).'),
(3881, 37, 93, 'Nihayet bir yolunu bulup onlara kuvvetli bir darbe indirdi.'),
(3882, 37, 94, 'Bunun üzerine birbirlerine girerek ona yürüdüler.'),
(3883, 37, 95, 'İbrahim dedi ki: "A, siz kendi yonttuğunuz şeylere mi tapıyorsunuz?"'),
(3884, 37, 96, '"Halbuki sizi de yaptıklarınızı da Allah yaratmıştır."'),
(3885, 37, 97, 'Onlar: "Haydin onun için bir yapı yapın da onu ateşe atın." dediler.'),
(3886, 37, 98, 'Böylece ona bir tuzak kurmak istediler. Biz de kendilerini daha alçak düşürdük.'),
(3887, 37, 99, 'Bir de dedi ki: "Ben Rabbime gidiyorum, o bana yolunu gösterir."'),
(3888, 37, 100, '"Ey Rabbim! Bana salihlerden (bir oğul) ihsan et!"'),
(3889, 37, 101, 'Biz de kendisine yumuşak huylu bir oğul müjdeledik.'),
(3890, 37, 102, 'Oğlu, yanında koşacak çağa gelince: "Ey oğlum! Ben seni rüyamda boğazladığımı görüyorum. Artık bak, ne düşünürsün?" dedi. Çocuk da: "Babacığım sana ne emrediliyorsa yap, inşaallah beni sabredenlerden bulacaksın" dedi.'),
(3891, 37, 103, 'Ne zaman ki ikisi de bu şekilde Allah\'a teslim oldular, İbrahim oğlunu şakağı üzerine yatırdı.'),
(3892, 37, 104, 'Biz de ona şöyle seslendik: "Ey İbrahim! "'),
(3893, 37, 105, '"Rüyana gerçekten sadakat gösterdin, şüphesiz ki, biz iyilik yapanları böyle mükafatlandırırız."'),
(3894, 37, 106, '"Şüphesiz ki bu apaçık bir imtihandı." (dedik)'),
(3895, 37, 107, 'Ve ona büyük bir kurbanlık fidye verdik.'),
(3896, 37, 108, 'Kendisine sonradan gelenler içinde iyi bir nâm bıraktık.'),
(3897, 37, 109, 'Selam olsun İbrahim\'e...'),
(3898, 37, 110, 'İşte biz iyilik yapanları böyle mükafatlandırırız.'),
(3899, 37, 111, 'Çünkü o bizim mümin kullarımızdandı.'),
(3900, 37, 112, 'Ona bir de salihlerden bir peygamber olmak üzere İshak\'ı müjdeledik.'),
(3901, 37, 113, 'Hem ona hem İshak\'a bereketler verdik. Her ikisinin neslinden de hem iyilik yapanlar var, hem de açıkça kendi nefsine zulmedenler var.'),
(3902, 37, 114, 'Andolsun ki biz Musa ile Harun\'a da nimetler verdik.'),
(3903, 37, 115, 'Hem kendilerini ve kavimlerini o büyük sıkıntıdan kurtardık.'),
(3904, 37, 116, 'Hem yardım ettik onlara da, galip gelenler onlar oldular.'),
(3905, 37, 117, 'Hem kendilerine o belli kitabı (Tevrat\'ı) verdik.'),
(3906, 37, 118, 'Kendilerini doğru yola çıkardık.'),
(3907, 37, 119, 'Sonrakiler içinde onlara iyi bir nam bıraktık:'),
(3908, 37, 120, 'Selam olsun, Musa ile Harun\'a.'),
(3909, 37, 121, 'İşte biz iyilik yapanları böyle mükafatlandırırız.'),
(3910, 37, 122, 'Çünkü onların ikisi de bizim mümin kullarımızdandı.'),
(3911, 37, 123, 'Şüphesiz İlyas da gönderilen peygamberlerdendir.'),
(3912, 37, 124, 'Hani o kavmine: "Siz Allah\'tan korkmaz mısınız? Yaratanların en güzeli olan, sizin de Rabbiniz, daha önceki atalarınızın da Rabbi bulunan Allah\'ı bırakıp da "Ba\'l\'e" (Ba\'l ismindeki puta) mi yalvarıyorsunuz?" dedi.'),
(3913, 37, 125, 'Hani o kavmine: "Siz Allah\'tan korkmaz mısınız? Yaratanların en güzeli olan, sizin de Rabbiniz, daha önceki atalarınızın da Rabbi bulunan Allah\'ı bırakıp da "Ba\'l\'e" (Ba\'l ismindeki puta) mi yalvarıyorsunuz?" dedi.'),
(3914, 37, 126, 'Hani o kavmine: "Siz Allah\'tan korkmaz mısınız? Yaratanların en güzeli olan, sizin de Rabbiniz, daha önceki atalarınızın da Rabbi bulunan Allah\'ı bırakıp da "Ba\'l\'e" (Ba\'l ismindeki puta) mi yalvarıyorsunuz?" dedi.'),
(3915, 37, 127, 'Fakat onlar, onu yalanladılar. Bu yüzden onlar mutlaka (cehennemde) hazır bulundurulacaklardır.'),
(3916, 37, 128, 'Ancak Allah\'ın ihlaslı kulları müstesna.'),
(3917, 37, 129, 'Ona da sonrakiler içinde şunu bıraktık:'),
(3918, 37, 130, 'Selam olsun İlyâsîn\'e.'),
(3919, 37, 131, 'İşte biz iyilik yapanları böyle mükafatlandırırız.'),
(3920, 37, 132, 'Çünkü o bizim mümin kullarımızdandı.'),
(3921, 37, 133, 'Şüphesiz Lût da gönderilen peygamberlerdendir.'),
(3922, 37, 134, 'Hani biz onu ve ailesinin tamamını kurtarmıştık.'),
(3923, 37, 135, 'Ancak geride kalıp batanlar içinde kalan yaşlı bir kadın hariç.'),
(3924, 37, 136, 'Sonra diğerlerini helak etmiştik.'),
(3925, 37, 137, 'Ve siz elbette sabahleyin ve geceleyin onlara uğrar ve üzerlerinden geçersiniz. Hâlâ akıl edip düşünmez misiniz?'),
(3926, 37, 138, 'Ve siz elbette sabahleyin ve geceleyin onlara uğrar ve üzerlerinden geçersiniz. Hâlâ akıl edip düşünmez misiniz?'),
(3927, 37, 139, 'Şüphesiz Yunus da gönderilen peygamberlerdendir.'),
(3928, 37, 140, 'Hani o bir zaman dolu bir gemiye kaçmıştı.'),
(3929, 37, 141, '(Oradakilerle) kur\'a çekmiş de kaydırılanlardan (yenilenlerden) olmuştu.'),
(3930, 37, 142, 'Derken (denize atılmış ve) kendisini balık yutmuştu. (Kendi nefsini) kınıyordu.'),
(3931, 37, 143, 'Eğer çok tesbih edenlerden olmasaydı, yeniden dirilecekleri güne kadar onun karnında kalırdı.'),
(3932, 37, 144, 'Eğer çok tesbih edenlerden olmasaydı, yeniden dirilecekleri güne kadar onun karnında kalırdı.'),
(3933, 37, 145, 'Biz onu hasta bir halde bir alana çıkardık.'),
(3934, 37, 146, 'Üzerine kabak cinsinden bir ağaç bitirdik.'),
(3935, 37, 147, 'Biz onu (Yunus\'u) yüz bin veya daha çok insana peygamber olarak gönderdik.'),
(3936, 37, 148, 'O zaman ona iman ettiler de biz onları bir zamana kadar yaşattık.'),
(3937, 37, 149, 'Şimdi sor o seninkilere: Kızlar, Rabbinin de, oğlanlar onların mı?'),
(3938, 37, 150, 'Yoksa biz melekleri dişi yaratmışız da onlar şahit mi bulunuyorlarmış?'),
(3939, 37, 151, 'Ha!.. Onlar, şüphesiz uydurdukları iftiralarından dolayı: "Allah doğurdu" derler. Hiç şüphesiz onlar, yalancıdırlar.'),
(3940, 37, 152, 'Ha!.. Onlar, şüphesiz uydurdukları iftiralarından dolayı: "Allah doğurdu" derler. Hiç şüphesiz onlar, yalancıdırlar.'),
(3941, 37, 153, '(Allah) kızları oğullara tercih mi etmiş?'),
(3942, 37, 154, 'Size ne oldu? Nasıl hükmediyorsunuz?'),
(3943, 37, 155, 'Hiç düşünmüyor musunuz?'),
(3944, 37, 156, 'Yoksa sizin için açık bir delil mi var?'),
(3945, 37, 157, 'O halde, eğer doğru söylüyorsanız getirin kitabınızı.'),
(3946, 37, 158, 'Onlar, Allah ile cinler arasında bir neseb (hısımlık bağı) uydurdular. Oysa andolsun cinler bilirler ki, o yalancılar mutlaka cehenneme götürüleceklerdir.'),
(3947, 37, 159, 'Allah, onların yakıştırdıkları vasıflardan münezzeh ve yücedir.'),
(3948, 37, 160, 'Fakat Allah\'ın ihlas ile seçilen kulları başka (onlar, Allah\'ı böyle şirk ile vasıflamazlar).'),
(3949, 37, 161, 'Çünkü siz ve taptıklarınız, kendiliğinden cehenneme saldıran kimseden başkasını, Allah\'a karşı kandırıp, saptıramazsınız.'),
(3950, 37, 162, 'Çünkü siz ve taptıklarınız, kendiliğinden cehenneme saldıran kimseden başkasını, Allah\'a karşı kandırıp, saptıramazsınız.'),
(3951, 37, 163, 'Çünkü siz ve taptıklarınız, kendiliğinden cehenneme saldıran kimseden başkasını, Allah\'a karşı kandırıp, saptıramazsınız.'),
(3952, 37, 164, '(Melekler): "Bizden her birimizin belli bir makamı vardır. Biziz o saf saf dizilenler, biziz! Biziz o tesbih edenler, biziz!" derler.'),
(3953, 37, 165, '(Melekler): "Bizden her birimizin belli bir makamı vardır. Biziz o saf saf dizilenler, biziz! Biziz o tesbih edenler, biziz!" derler.'),
(3954, 37, 166, '(Melekler): "Bizden her birimizin belli bir makamı vardır. Biziz o saf saf dizilenler, biziz! Biziz o tesbih edenler, biziz!" derler.'),
(3955, 37, 167, '(Müşrikler) şöyle diyorlardı: "Eğer yanımızda önceki (ümmet)lerden bir kitap olsaydı, elbette biz de Allah\'ın ihlas ile seçilmiş kullarından olurduk."'),
(3956, 37, 168, '(Müşrikler) şöyle diyorlardı: "Eğer yanımızda önceki (ümmet)lerden bir kitap olsaydı, elbette biz de Allah\'ın ihlas ile seçilmiş kullarından olurduk."'),
(3957, 37, 169, '(Müşrikler) şöyle diyorlardı: "Eğer yanımızda önceki (ümmet)lerden bir kitap olsaydı, elbette biz de Allah\'ın ihlas ile seçilmiş kullarından olurduk."'),
(3958, 37, 170, 'Fakat şimdi onu inkâr ettiler. Ama ilerde bileceklerdir.'),
(3959, 37, 171, 'Andolsun ki peygamberlikle gönderilen kullarımız hakkında şu sözümüz geçmiştir: "Onlar var ya, elbette onlar muzaffer olacaklardır ve elbette bizim ordularımız mutlaka galip geleceklerdir."'),
(3960, 37, 172, 'Andolsun ki peygamberlikle gönderilen kullarımız hakkında şu sözümüz geçmiştir: "Onlar var ya, elbette onlar muzaffer olacaklardır ve elbette bizim ordularımız mutlaka galip geleceklerdir."'),
(3961, 37, 173, 'Andolsun ki peygamberlikle gönderilen kullarımız hakkında şu sözümüz geçmiştir: "Onlar var ya, elbette onlar muzaffer olacaklardır ve elbette bizim ordularımız mutlaka galip geleceklerdir."'),
(3962, 37, 174, 'Onun için sen, bir süreye kadar onlardan yüz çevir.'),
(3963, 37, 175, 'Onlara (inecek azabı) gözetle. Yakında onlar da göreceklerdir.'),
(3964, 37, 176, 'Ya şimdi onlar, bizim azabımıza uğramakta acele mi ediyorlar?'),
(3965, 37, 177, 'Fakat (azabımız) onların sahasına indiği zaman, (o acı sonuçla) uyarılanların sabahı ne kötüdür!'),
(3966, 37, 178, 'Yine sen, bir süreye kadar onlardan yüz çevir.'),
(3967, 37, 179, '(İnecek azabı) gözetle! Yakında onlar da göreceklerdir.'),
(3968, 37, 180, 'Senin güç ve kuvvet sahibi Rabbin, onların yakıştırdıkları vasıflardan münezzeh ve yücedir.'),
(3969, 37, 181, 'Gönderilen bütün peygamberlere selam olsun.'),
(3970, 37, 182, 'Hamd, âlemlerin Rabbi Allah\'a mahsustur.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'Sâd. Bu zikirle dolu Kur\'ân\'a bak!'),
(3972, 38, 2, 'O inkâr edenler bir gurur ve ayrılık içindedirler.'),
(3973, 38, 3, 'Kendilerinden önce nicelerini helak ettik. Onlar çağrıştılar. Ama artık kurtuluş vakti değildi.'),
(3974, 38, 4, 'İçlerinden kendilerine uyarıcı bir peygamber geldiğine şaştılar da kâfirler: "Bu bir sihirbazdır, yalancıdır" dediler.'),
(3975, 38, 5, '"İlâhları, bir tek ilâh mı kılmış? Bu gerçekten şaşılacak bir şey, çok tuhaf!"'),
(3976, 38, 6, 'İçlerinden ileri gelenler fırladılar ve dediler ki: "İlâhlarınız üzerinde sabır ve sebat edin. Bu, gerçekten arzu edilen bir murad!"'),
(3977, 38, 7, '"Biz bunu başka bir dinde işitmedik, bu mutlaka bir uydurmadır."'),
(3978, 38, 8, '"Kur\'ân aramızdan ona mı indirilmiş?" dediler. Doğrusu onlar benim Kur\'ân\'ımdan bir kuşku içindeler. Ve doğrusu onlar henüz azabımı tatmadılar.'),
(3979, 38, 9, 'Yoksa sana o Kur\'ân\'ı veren çok güçlü ve ihsan sahibi Rabbinin hazineleri onların yanında mı?'),
(3980, 38, 10, 'Yoksa bütün o göklerin, yerin ve aralarındakilerin mülkü onların mı? Öyle ise bütün imkanlarını seferber ederek yükselsinler de görelim!'),
(3981, 38, 11, 'Onlar burada çeşitli partilerden (gruplardan) bozguna uğramış bir ordudur.'),
(3982, 38, 12, 'Onlardan önce Nuh kavmi, Âd kavmi ve saltanat sahibi Firavun da yalanlamışlardı.'),
(3983, 38, 13, 'Semûd kavmi, Lut kavmi ve Eykeliler (Şuayb kavmi) de yalanlamışlardı. İşte o çeşitli partiler bunlardır.'),
(3984, 38, 14, 'Hepsi de gönderilen peygamberleri yalanladılar da azabım böyle hak oldu.'),
(3985, 38, 15, 'Onlar da bir tek haykırışa bakıyorlar. Öyle ki onun gecikmesi de yoktur.'),
(3986, 38, 16, 'Bir de: "Ey Rabbimiz! Hesap gününden önce bizim azabdan payımızı acele ver" dediler.'),
(3987, 38, 17, 'Şimdi sen onların dediklerine sabret de kuvvetli kulumuz Davud\'u hatırla. Çünkü o, zikir ve tesbih ile bize yönelmişti.'),
(3988, 38, 18, 'Biz, dağları onun emrine vermiştik. Akşamsabah onunla birlikte tesbih ederlerdi.'),
(3989, 38, 19, 'Kuşları da toplu olarak onun emrine vermiştik. Hepsi de ona uyarak zikir ve tesbih ederlerdi.'),
(3990, 38, 20, 'Biz onun mülkünü kuvvetlendirmiş ve kendisine hikmet ve hakkı batıldan ayırt etme kabiliyeti vermiştik.'),
(3991, 38, 21, 'Bir de davacıların kıssası geldi mi sana? Hani surdan aşarak mihraba ulaşmışlardı.'),
(3992, 38, 22, 'Davud\'un yanına giriverdiler de onlardan telaşe düştü. Ona "Korkma!" dediler, biz iki davacıyız. Birimiz, birimize haksızlık etti. Şimdi sen aramızda hak ile hüküm ver ve aşırı gitme de bizi doğru yolun ortasına çıkar.'),
(3993, 38, 23, 'Biri: "İşte bu benim kardeşim. Onun doksan dokuz dişi koyunu var, benim ise bir tek dişi koyunum var. Böyle iken: Onu da bana ver, dedi ve tartışmada beni yendi" diye anlattı.'),
(3994, 38, 24, 'Davud dedi ki: "Doğrusu senin bir koyununu kendi koyunlarına katmak istemesiyle sana zulmetmiştir. Gerçekten bir cemiyette yaşayanların çoğu mutlaka birbirlerine haksızlık ediyorlar. Ancak iman edip de salih amel işleyenler başka. Ama onlar da pek az." Davud, bizim kendisini imtihan ettiğimizi sanmıştı. Hemen Rabbinden mağfiret diledi, rüku ederek yere kapandı, tevbe ile Allah\'a yöneldi.'),
(3995, 38, 25, 'Biz de o zannettiği şeyi kendisine bağışladık. Şüphesiz yanımızda onun bir yakınlığı ve güzel bir dönüş yeri vardır.'),
(3996, 38, 26, 'Ey Davud! Gerçekten biz seni yeryüzünde bir halife yaptık. Artık insanlar arasında hak ile hüküm ver. Keyfe, arzuya uyma ki, seni Allah yolundan saptırmasın. Çünkü Allah yolundan sapanlar, hesap gününü unuttukları için kendilerine çok şiddetli bir azab vardır.'),
(3997, 38, 27, 'Hem o göğü, yeri ve aralarındakileri biz boşuna yaratmadık. O, kâfirlerin zannıdır. Onun için vay ateşe girecek olan kâfirlerin haline!'),
(3998, 38, 28, 'Yoksa, iman edip de salih amel işleyenleri biz, o yeryüzündeki bozguncular gibi yapar mıyız? Yoksa o takva sahiplerini azgın günahkarlar gibi yapar mıyız?'),
(3999, 38, 29, 'Bu, sana indirdiğimiz mübarek bir kitaptır ki, insanlar onun âyetlerini düşünsünler ve temiz akıl sahipleri ibret alsınlar.'),
(4000, 38, 30, 'Bir de Davud\'a Süleyman\'ı bahşettik. Süleyman ne güzel kuldu. Çünkü o seslice tesbih edip Allah\'a yönelirdi.'),
(4001, 38, 31, 'Hani kendisine bir zaman akşam üstü iyi cins ve rahvan atlar gösterilmişti.'),
(4002, 38, 32, '"Ben, dedi, at sevgisini, Rabbimi anmaktan ötürü tercih ettim." Nihayet atlar perdenin arkasına gizlendi.'),
(4003, 38, 33, '"Geri getirin onları bana!" dedi ve artık onların bacaklarını, boyunlarını silmeye başladı.'),
(4004, 38, 34, 'Andolsun ki Süleyman\'ı imtihan da ettik ve tahtının üzerine bir ceset bıraktık. Sonra tekrar tevbe ile önceki haline döndü.'),
(4005, 38, 35, 'Süleyman: "Ey Rabbim! Beni bağışla ve bana öyle bir mülk ihsan et ki, ardımdan hiç kimseye yaraşmasın. Şüphesiz, bütün dilekleri veren sensin." dedi.'),
(4006, 38, 36, 'Bunun üzerine biz rüzgarı onun emrine verdik. Onun emriyle istediği yere yumuşacık akardı.'),
(4007, 38, 37, 'Dalgıç ve yapı ustası şeytanları da.'),
(4008, 38, 38, 'Ve daha diğerlerini de zincirlerde bağlı olarak (Onun emrine verdik).'),
(4009, 38, 39, '"İşte bu, bizim ihsanımızdır. Artık sen dilersen başkalarına ver veya verme. Bundan hesaba çekilmeyeceksin" dedik.'),
(4010, 38, 40, 'Şüphesiz ki ona huzurumuzda bir yakınlık ve güzel bir makam vardır.'),
(4011, 38, 41, 'Kulumuz Eyyub\'u da an. Bir zaman o, Rabbine şöyle nida etmişti: "Meşakkat ve acı ile bana şeytan dokundu."'),
(4012, 38, 42, '(Biz ona): "Ayağını yere vur! İşte sana yıkanılacak ve içilecek soğuk bir su" dedik.'),
(4013, 38, 43, 'Ve ona, bütün ailesini ve beraberlerinde bir mislini daha tarafımızdan bir rahmet olarak bahşettik ki, akıl sahipleri için bir ibret olsun.'),
(4014, 38, 44, '(Bir de dedik ki): "Eline bir demet al da onunla (eşine) vur; yemininde durmamazlık etme." Doğrusu biz onu sabırlı bulduk. O ne güzel kul! O hakikaten daima Allah\'a yönelmektedir.'),
(4015, 38, 45, 'Kullarımız İbrahim\'i, İshak\'ı ve Yakub\'u da an. Onlar eller ve gözler sahipleri idiler.'),
(4016, 38, 46, 'Çünkü biz onları temiz bir hasletle, hâlis yurt (ahiret) düşüncesine ermiş has kullarımızdan kılmışızdır.'),
(4017, 38, 47, 'Çünkü onlar, nezdimizde seçilmiş en hayırlı kimselerdendir.'),
(4018, 38, 48, 'İsmail\'i, Elyasa\'yı, Zü\'lKifl\'i de an. Hepsi de en hayırlı kimselerdendir.'),
(4019, 38, 49, 'İşte bu bir öğüttür. Şüphesiz korunan müttakiler için herhalde güzel bir istikbal (güzel bir dönüş yeri) vardır.'),
(4020, 38, 50, 'Bütün kapıları kendilerine açılmış olan Adn cennetleri vardır.'),
(4021, 38, 51, 'İçlerine kurularak orada birçok yemişle, bambaşka bir içki isteyeceklerdir.'),
(4022, 38, 52, 'Yanlarında da bakışları yalnız kocalarına dönük hep aynı yaşta dilberler vardır.'),
(4023, 38, 53, 'O hesap günü için size vaad edilen işte budur.'),
(4024, 38, 54, 'İşte bu, bizim rızkımız; muhakkak ki ona hiç tükenmek yoktur.'),
(4025, 38, 55, 'Bu, böyledir. Şüphesiz azgınlar için de fena bir gelecek vardır.'),
(4026, 38, 56, 'Cehennem! Ona yaslanacaklar, fakat o ne çirkin döşektir.'),
(4027, 38, 57, 'İşte artık tatsınlar onu ki, o kaynar su ve irindir.'),
(4028, 38, 58, 'Ve o şekilden çifter çifter tadacakları diğer acılar da vardır.'),
(4029, 38, 59, 'İşte şunlar da sizin peşinize düşenlerdir. Onlara merhaba yok. Çünkü onlar cehenneme salınıyorlar.'),
(4030, 38, 60, '(Arkadan gelenler öncekilere:) Derler ki: "Hayır, asıl size merhaba yok. Çünkü cehennemi bize siz takdim ettiniz. Bakın o ne kötü yatak!"'),
(4031, 38, 61, '"Ey Rabbimiz! Bize bunu takdim edenin ateşteki azabını kat kat artır" derler.'),
(4032, 38, 62, 'Bir de derler ki: "Kötülerden saydığımız birtakım adamları (fakir müminleri) niye göremiyoruz?"'),
(4033, 38, 63, '"Onları eğlence yerine tutmuştuk ha! Yoksa bu gözler onlardan kaydı mı?"'),
(4034, 38, 64, 'Şüphesiz ki bu haktır. Ateş ehlinin birbiriyle tartışması muhakkak olacaktır.'),
(4035, 38, 65, 'De ki: "Ben ancak korkuyu haber veren bir peygamberim. O tek ve kahredici olan Allah\'tan başka tanrı da yoktur."'),
(4036, 38, 66, '"O, göklerin, yerin ve ikisi arasındakilerin Rabbidir. O çok güçlüdür, çok bağışlayıcıdır."'),
(4037, 38, 67, 'De ki: "Bu, bir büyük haberdir."'),
(4038, 38, 68, '"Siz ondan yüz çeviriyorsunuz."'),
(4039, 38, 69, '"Münakaşa ederlerken, benim melekler yüksek topluluğuna ait ne bilgim olabilirdi?"'),
(4040, 38, 70, '"Ancak ben açıktan açığa korkutmakla görevli olduğum için o bilgi bana vahyediliyor."'),
(4041, 38, 71, 'Hani Rabbin meleklere demişti ki: "Ben çamurdan bir insan yaratmaktayım."'),
(4042, 38, 72, '"Onu tesviye edip, düzeltip de ruhumdan ona üfledim mi derhal ona secdeye kapanın."'),
(4043, 38, 73, 'Bunun üzerine meleklerin hepsi toptan secde ettiler.'),
(4044, 38, 74, 'Yalnız İblis etmedi, büyüklük tasladı ve kâfirlerden oldu.'),
(4045, 38, 75, 'Allah: "Ey İblis! O benim kudretimle yarattığıma secde etmene ne engel oldu? Kibirlenmek mi istedin? Yoksa yüksek derecelerde bulunanlardan mı oldun?" dedi.'),
(4046, 38, 76, 'İblis dedi ki: "Ben ondan hayırlıyım. Beni ateşten yarattın, onu ise çamurdan yarattın."'),
(4047, 38, 77, 'Allah: "Hemen çık oradan, artık sen kovuldun."'),
(4048, 38, 78, '"Ve elbette lanetim ceza gününe kadar senin üzerindedir." buyurdu.'),
(4049, 38, 79, 'İblis: "Ya Rab! O halde insanların diriltilecekleri güne kadar bana mühlet ver." dedi.'),
(4050, 38, 80, 'Allah: "Haydi belirli bir vakte kadar mühlet verilenlerdensin" buyurdu.'),
(4051, 38, 81, 'Allah: "Haydi belirli bir vakte kadar mühlet verilenlerdensin" buyurdu.'),
(4052, 38, 82, 'İblis: "Öyle ise izzet ve şerefine yemin ederim ki, ben onların hepsini mutlaka aldatır, saptırırım."'),
(4053, 38, 83, '"Ancak içlerinden ihlas ile seçilmiş has kulların müstesna" dedi.'),
(4054, 38, 84, 'Allah buyurdu ki: "O doğru, ben hep doğruyu söylerim."'),
(4055, 38, 85, '"Andolsun ki, cehennemi mutlaka senden ve onların sana uyanlarından, topunuzdan tıka basa dolduracağım."'),
(4056, 38, 86, 'Ey Muhammed! De ki: "Ben o Kur\'ân\'a karşı sizden bir ücret istemiyorum. Ve ben kendiliğimden bir şey de teklif etmiyorum."'),
(4057, 38, 87, '"O Kur\'ân, bütün âlemler için bir zikir, bir öğüttür. "'),
(4058, 38, 88, '"Herhalde onun haberini bir zaman sonra bileceksiniz."');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Bu kitabın indirilişi, Azîz ve Hakîm olan Allah tarafındandır.'),
(4060, 39, 2, 'Emin ol, biz sana kitabı hakkıyla indirdik. Onun için dini yalnız kendisine halis kılarak Allah\'a ibadet ve kulluk et.'),
(4061, 39, 3, 'İyi bil ki, halis din ancak Allah\'ındır. O\'ndan başka birtakım dostlar tutanlar da şöyle demektedirler: "Biz onlara sadece bizi Allah\'a daha çok yaklaştırsınlar diye ibadet ediyoruz." Şüphe yok ki Allah, onların aralarında ihtilaf edip durdukları şeyde hükmünü verecektir. Herhalde yalancı ve nankör olan kimseyi Allah doğru yola çıkarmaz.'),
(4062, 39, 4, 'Eğer Allah bir çocuk edinmek isteseydi, elbette yaratacağından, dileyeceğini seçecekti. Ama o bundan münezzehtir. O, tek ve kahredici olan Allah\'tır.'),
(4063, 39, 5, 'O, gökleri ve yeri hak ile yarattı, geceyi gündüzün üstüne sarıyor, gündüzü de gecenin üstüne sarıyor. Güneşi ve ay\'ı emrine âmade kılmış, her biri belli bir süreye kadar akıp gitmektedir. İyi bil ki, çok güçlü ve çok bağışlayıcı olan ancak O\'dur.'),
(4064, 39, 6, 'O, sizi bir nefisten yarattı. Hem sonra onun eşini de ondan var etti. Sizin için yumuşak başlı hayvanlardan sekiz çift indirdi. Sizi analarınızın karınlarında üç karanlık içinde yaratılıştan yaratılışa yaratıp duruyor. İşte Rabbiniz Allah O\'dur. Mülk O\'nundur, O\'ndan başka tanrı yoktur. O halde nasıl haktan çevrilirsiniz?'),
(4065, 39, 7, 'Eğer inkâr ederseniz, şüphe yok ki Allah\'ın size ihtiyacı yoktur. Bununla beraber kulları hesabına küfre razı olmaz. Eğer şükrederseniz sizin hesabınıza ona razı olur. Hiçbir günahkar da diğerinin günahını çekecek değildir. Sonra dönüşünüz, Rabbinizedir. O vakit, O size bütün yaptıklarınızı haber verecektir. Çünkü O, bütün kalplerin özünü bilir.'),
(4066, 39, 8, 'İnsana bir sıkıntı dokunduğu zaman bütün gönlünü vererek Rabbine dua eder. Sonra kendisine tarafından bir nimet lütfettiği zaman da önceden O\'na dua ettiği hali unutur da, yolundan sapıtmak için Allah\'a ortaklar koşmaya başlar. Ey Muhammed! De ki: "Küfrünle biraz zevk et, çünkü sen, o ateşliklerdensin."'),
(4067, 39, 9, 'Yoksa o, gece saatlerinde kalkan, secdeye kapanıp, kıyama durarak daima vazifesini yapan, ahireti hesaba katan ve Rabbinin rahmetini uman kimse gibi olur mu? De ki: "Hiç bilenlerle bilmeyenler bir olur mu?" Ancak temiz akıl sahibi olanlar anlar.'),
(4068, 39, 10, 'Ey Muhammed! Tarafımdan söyle: "Ey iman eden kullarım! Rabbinizden korkun. Bu dünyada güzellik yapanlara bir güzellik vardır. Allah\'ın yeryüzü geniştir. Ancak sabredenlere mükafatları hesapsız ödenecektir."'),
(4069, 39, 11, 'De ki: "Bana, dini sadece kendisine halis kılarak Allah\'a ibadet etmem emredildi."'),
(4070, 39, 12, '"Hem O\'nun birliğine teslim olan müslümanların ilki olmam da bana emredildi."'),
(4071, 39, 13, 'De ki: "Eğer Rabbime isyan edersem, büyük bir günün azabından korkarım."'),
(4072, 39, 14, 'De ki: "Ben dinimi kendisine halis kılarak yalnız Allah\'a kulluk ederim."'),
(4073, 39, 15, '"Siz de O\'ndan başka dilediğinize kul olun." De ki: "Asıl hüsrana düşenler, kıyamet günü kendilerine ve mensuplarına ziyan edenlerdir. Evet, işte asıl açık hüsran budur."'),
(4074, 39, 16, 'Onların üstlerinde ateşten tabakalar, altlarında yine ateşten tabakalar vardır. İşte Allah, kullarını bundan korkutuyor, "Ey kullarım! benden korkun." (diyor).'),
(4075, 39, 17, 'Tağuttan, ona kulluk etmekten kaçınıp da tam gönülle Allah\'a yönelenlere gelince, müjde onlaradır. Haydi müjdele kullarımı.'),
(4076, 39, 18, 'O kullarımı ki, onlar sözü dinlerler, sonra da en güzeline uyarlar. İşte onlar, Allah\'ın kendilerine hidayet verdiği kimselerdir. İşte temiz akıllılar da onlardır.'),
(4077, 39, 19, 'Ya üzerine azab kelimesi hak olmuş kimse de mi (böyledir)? Artık o ateşteki kimseyi sen mi çıkaracaksın?'),
(4078, 39, 20, 'Fakat o Rablerine sığınarak korunanlar için altlarından ırmaklar akan, üzerlerinden şehnişinler yapılmış, şehnişinli (balkonlu) köşkler vardır. Bu, Allah\'ın vaadidir. Allah vaadinden caymaz.'),
(4079, 39, 21, 'Allah\'ın gökten bir su indirip de onu bir yoluyla yeryüzündeki menbalara koyduğunu görmedin mi? Sonra onunla türlü renklerde bir ekin çıkarır, sonra onun olgunlaşıp sarardığını görürsün. Sonra da onu bir çöpe çevirir. Elbette bunda temiz akıllılar için bir ihtar vardır.'),
(4080, 39, 22, 'Allah, kimin bağrını İslâm\'a açmış ise işte o, Rabbinden bir nur üzerinde değil midir? Artık Allah\'ın zikri hususunda kalpleri katılaşmış olanların vay haline! İşte bunlar, apaçık bir sapıklık içindedirler.'),
(4081, 39, 23, 'Allah, kelamın en güzelini ikizli, ahenkli bir kitap olarak indirdi. () Ondan Rablerine saygısı olanların derileri ürperir. Sonra derileri de, kalpleri de Allah\'ın zikrine karşı yumuşar. İşte bu Allah\'ın rehberidir. Allah, onunla dilediğini doğru yola çıkarır. Her kimi de Allah şaşırtırsa, artık ona doğru yolu gösterecek yoktur.'),
(4082, 39, 24, 'O halde kıyamet günü zalimlere: "Tadın bakalım kazanıp durduklarınızı!" denilirken, o kötü azabdan yüzü ile korunacak kimse ne olur? ()'),
(4083, 39, 25, 'Onlardan öncekiler de yalanladılar da kendilerine, hatırlarına gelmez yönden azab geliverdi.'),
(4084, 39, 26, 'Allah, onlara dünya hayatında zilleti tattırdı. Ahiret azabı ise elbette daha büyüktür. Keşke bilselerdi!'),
(4085, 39, 27, 'Yemin ederim ki, bu Kur\'ân\'da insanlar için her türlüsünden temsil getirdik. Gerek ki iyi düşünsünler.'),
(4086, 39, 28, 'Pürüzsüz Arapça bir Kur\'ân (indirdik ki, Allah\'ın azabından) korunsunlar.'),
(4087, 39, 29, 'Allah, şöyle bir misal vermiştir: Bir adam ve birtakım ortakları var, hırçın hırçın çekişip duruyorlar. Bir de yalnız bir kişiye bağlı selamet içinde olan bir adam var. Bu ikisinin hali hiç bir olur mu? Hamd Allah\'ındır, fakat pek çokları bilmezler.'),
(4088, 39, 30, 'Sen elbette öleceksin, onlar da elbette öleceklerdir.'),
(4089, 39, 31, 'Sonra siz muhakkak kıyamet gününde Rabbinizin huzurunda birbirinizden davacı olacaksınız.'),
(4090, 39, 32, 'Allah\'a karşı yalan söyleyen ve doğru kendisine geldiği zaman onu yalan sayandan daha zalim (daha haksız) kim olabilir? Kâfirlerin yeri cehennemde değil midir?'),
(4091, 39, 33, 'Doğruyu getiren ve onu tasdik edene gelince, işte onlar kötülükten korunan müttakilerdir.'),
(4092, 39, 34, 'Onlara, Rablerinin yanında ne dilerlerse vardır. İşte bu, iyilik yapanların mükafatıdır.'),
(4093, 39, 35, 'Çünkü Allah, onların önceden yaptıkları amelin en kötüsünü bile keffaretle örtüp, işlemekte bulundukları güzel amellerin en güzeline göre mükafatlarını kendilerine verecektir.'),
(4094, 39, 36, 'Allah, kuluna kâfi değil midir? Durmuşlar da seni O\'ndan başkalarıyla korkutuyorlar. Her kimi ki Allah şaşırtırsa, artık ona hidayet edecek yoktur.'),
(4095, 39, 37, 'Her kime de Allah hidayet verirse artık onu da şaşırtacak yoktur. Allah aziz (çok güçlü) ve intikam sahibi değil midir?'),
(4096, 39, 38, 'Andolsun ki onlara: "O gökleri ve yeri kim yarattı?" diye soracak olsan: "Elbette Allah!" diyeceklerdir. O halde gördünüz ya Allah\'tan başka çağırdıklarınızı! Eğer Allah bana bir zarar vermek isterse, onlar O\'nun zararını giderebilirler mi? Yahut bana bir rahmet dilerse, onlar O\'nun rahmetini tutabilirler mi? De ki: "Allah, bana yeter." Tevekkül edenler, hep O\'na dayanırlar.'),
(4097, 39, 39, 'De ki: "Ey kavmim! Haliniz üzere çalışın. Ben de kendi halime göre çalışıyorum. Artık ileride bileceksiniz."'),
(4098, 39, 40, '"Kendisini rezil edecek azabın kime geleceğini ve sürekli bir azabın kimin üzerine konacağını."'),
(4099, 39, 41, 'Biz bu kitabı sana, insanlar için hak ile indirdik. O halde kim doğru yola gelirse kendi lehinedir. Kim de saparsa, sırf kendi aleyhine olarak sapar. Sen onların üzerine vekil değilsin.'),
(4100, 39, 42, 'Allah, o canları öldükleri zaman, ölmeyenleri de uyuduklarında alır. Sonra haklarında ölüm hükmü verdiklerini alıkor, diğerlerini de takdir edilmiş bir süreye kadar salıverir. Şüphesiz ki bunda düşünecek bir kavim için nice ibretler vardır.'),
(4101, 39, 43, 'Yoksa Allah\'tan başka şefaatçiler mi edindiler? De ki: "Onlar hiçbir şeye güç yetiremezler ve akıl erdiremezlerse de mi (böyle yapacaksınız)?"'),
(4102, 39, 44, 'De ki: "Bütün şefaat Allah\'ındır. Göklerin ve yerin mülkü O\'nundur. Sonra hep döndürülüp O\'na götürüleceksiniz."'),
(4103, 39, 45, 'Böyle iken, Allah bir olarak anıldığı zaman ahirete inanmayanların yürekleri burkulur da, O\'ndan başkaları anıldığı zaman derhal yüzleri güler.'),
(4104, 39, 46, 'De ki: "Ey gökleri ve yeri yaratan, görüleni ve görülmeyeni bilen Allah\'ım! Kulların arasında, o ihtilaf edip durdukları şeyler hakkında sen hüküm vereceksin."'),
(4105, 39, 47, 'Eğer bütün yeryüzündekiler ve bir o kadarı da beraber o zulmedenlerin olsaydı, kıyamet günü azabın kötülüğünden kurtulmak için onu mutlaka feda ederlerdi. Ancak ne var ki, hiç hesaba katmadıkları şeyler, Allah tarafından karşılarına çıkarılır.'),
(4106, 39, 48, 'Öyle ki, yaptıkları amellerin kötülükleri karşılarına çıkmış ve alay edip durdukları şeyler, kendilerini sarmıştır.'),
(4107, 39, 49, 'Fakat insana bir sıkıntı dokunuverince bize yalvarır, sonra kendisine tarafımızdan bir nimet bahşettiğimiz zaman da: "O bana bir bilgi üzerine verildi." der. Belki bu bir imtihandır, fakat pek çokları bilmezler.'),
(4108, 39, 50, 'Onu, bunlardan öncekiler de söyledi. Fakat o kazandıkları, kendilerini kurtarmadı.'),
(4109, 39, 51, 'Neticede kazandıklarının kötülükleri, başlarına geçti. Şunlardan o zulmedenlerin de kazandıkları kötülükleri başlarına geçecektir. Onlar da bunu atlatacak değillerdir.'),
(4110, 39, 52, 'Hâlâ bilmediler mi ki; Allah, rızkı dilediğine açar ve kısar. Şüphesiz ki bunda iman edecek bir kavim için nice ibretler vardır.'),
(4111, 39, 53, 'De ki: "Ey haddi aşarak nefislerine karşı israf etmiş olan kullarım! Allah\'ın rahmetinden ümid kesmeyin. Çünkü Allah, bütün günahları bağışlar. Şüphesiz ki O, çok bağışlayıcıdır, çok merhamet edicidir."'),
(4112, 39, 54, 'Onun için ümidi kesmeyin de başınıza azab gelmeden önce tevbe ile Rabbinize yönelin ve O\'na teslim olun. Sonra kurtulamazsınız.'),
(4113, 39, 55, 'Haberiniz olmayarak ansızın başınıza azab gelmeden önce (halis müslüman olun da) Rabbinizden size indirilenin en güzelini takib ve tatbik edin.'),
(4114, 39, 56, '(O günden sakının ki günahkar) nefis şöyle diyecektir: "Allah\'ın yanında yaptığım kusurlardan dolayı yazık bana! Doğrusu ben alay edenlerdendim."'),
(4115, 39, 57, 'Yahut şöyle diyecektir: "Allah bana doğru yolu gösterseydi, her halde ben müttakilerden olurdum."'),
(4116, 39, 58, 'Veya azabı gördüğü zaman şöyle diyecektir: "Bana bir geri dönüş olsaydı da ben de o iyilik yapanlardan olsaydım."'),
(4117, 39, 59, '(Ona): "Hayır sana âyetlerim geldi de onlara yalan dedin, kibirlenmek istedin ve kâfirlerden oldun." (denir.)'),
(4118, 39, 60, 'Hem o kıyamet günü görürsün ki, Allah\'a karşı yalan söyleyenlerin yüzleri kararmıştır. Kibirlenenlerin yeri cehennem değil mi?'),
(4119, 39, 61, 'Kötülükten sakınan müttakileri ise Allah başarılarından dolayı kurtuluşa çıkarır. Onlara fenalık dokunmaz ve onlar üzülecek de değillerdir.'),
(4120, 39, 62, 'Allah, her şeyin yaratıcısıdır. Her şey üzerine vekil de O\'dur.'),
(4121, 39, 63, 'Bütün göklerin ve yerin kilitleri O\'nundur. Allah\'ın âyetlerini inkâr edenlere gelince, işte onlar, kendilerine yazık edenlerdir.'),
(4122, 39, 64, 'De ki: "Ey cahiller! Şimdi bana o Allah\'tan başkasına mı kulluk etmemi emrediyorsunuz?"'),
(4123, 39, 65, 'Andolsun ki, sana da, senden öncekilere de şu vahyedildi: "Yemin ederim ki, eğer şirk koşarsan bütün çalışmaların boşa gider ve mutlaka kendine yazık edenlerden olursun."'),
(4124, 39, 66, 'Hayır, onun için yalnız Allah\'a kulluk et ve şükredenlerden ol.'),
(4125, 39, 67, 'Allah\'ı hakkıyla takdir edemediler. Halbuki bütün yer kıyamet günü O\'nun avucundadır. Gökler de kudretiyle dürülmüştür. O, onların ortak koştuklarından münezzeh ve çok yüksektir.'),
(4126, 39, 68, 'Ve sûra üflenmiştir. Göklerde kim var, yerde kim varsa çarpılıp yıkılmıştır. Ancak Allah\'ın dilediği müstesna. Sonra ona bir daha üflenmiştir. Bu defa da hep onlar kalkmışlar bakıyorlardır.'),
(4127, 39, 69, 'Yer, Rabbinin nuru ile parlamıştır. Kitap konmuş, peygamberler ve şahitler getirilmiş ve aralarında hak ile hüküm verilmektedir. Hem onlara hiç haksızlık yapılmaz.'),
(4128, 39, 70, 'Herkese ne amel yaptıysa karşılığı tam olarak ödenmiştir. O (Allah), onların yaptıklarını en iyi şekilde bilmektedir.'),
(4129, 39, 71, 'İnkâr edenler bölük bölük cehenneme sevkedilmektedir. Nihayet oraya vardıklarında kapıları açılır ve bekçileri onlara: "İçinizden size Rabbinizin âyetlerini okuyan, bu gününüzle karşılaşacağınıza dair sizi uyaran peygamberler gelmedi mi?" derler. Onlar da: "Evet geldi" derler. Fakat kâfirler üzerine azab kelimesi hak oldu.'),
(4130, 39, 72, '(Onlara): "Ebedî olarak içinde kalmak üzere girin cehennemin kapılarından" denir. Bak, büyüklük taslayanların yeri ne kötüdür!'),
(4131, 39, 73, 'Rablerinden korkanlar da bölük bölük cennete sevk edilmektedir. Nihayet oraya vardıkları zaman kapıları açılır ve bekçileri onlara: "Selâm sizlere, ne hoşsunuz! Ebedî olarak içinde kalmak üzere haydi girin oraya!" derler.'),
(4132, 39, 74, 'Onlar da: "Hamdolsun o Allah\'a ki, bize vaadini doğru çıkardı ve bizi cennet arzına varis kıldı. Cennette istediğimiz yerde oturuyoruz" derler. Bak ne güzeldir mükafatı o iyi amel işleyenlerin!'),
(4133, 39, 75, 'Meleklerin de arşın etrafını kuşatarak, Rablerine hamd ile tesbih ettiklerini görürsün. Artık halk arasında hak ile hüküm icra edilip "âlemlerin Rabbi Allah\'a hamdolsun" denilmektedir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'Hâ Mîm.'),
(4135, 40, 2, 'Bu kitabın indirilişi, çok güçlü ve her şeyi bilen Allah tarafındandır.'),
(4136, 40, 3, 'O, günah bağışlayıcı, tevbe kabul edici, azabı şiddetli, kerem sahibi Allah\'tandır ki O\'ndan başka ilâh yoktur. Hem dönüş O\'nadır.'),
(4137, 40, 4, 'Allah\'ın âyetleri hakkında ancak kâfirler mücadele ederler. Şimdi onların beldeler içinde dönüp dolaşmaları seni aldatmasın.'),
(4138, 40, 5, 'Onlardan önce Nuh kavmi, arkalarından da çeşitli topluluklar yalanlamışlardı. Her ümmet, kendi peygamberlerini yakalamak kastında bulundu. Hakkı batılla gidermek için boşuna mücadele ettiler. Ben de onları tuttum, alıverdim. (Bak o zaman) azabım nasıl oldu?'),
(4139, 40, 6, 'İşte o nankörlük eden kâfirlere Rabbinin (azab) sözü öyle hak oldu. Onlar, mutlaka cehennemliktirler.'),
(4140, 40, 7, 'Arşı taşıyanlar ve onun etrafındakiler, Rablerinin hamdiyle tesbih ederler ve O\'na inanırlar. İman etmişler için de şöyle bağışlanma dilerler: "Ey Rabbimiz! Rahmetin ve ilmin her şeyi kuşatmıştır. O, tevbe edip senin yoluna uyanları bağışla, onları cehennem azabından koru."'),
(4141, 40, 8, '"Ey Rabbimiz! Hem onları, hem onların atalarından, zevcelerinden ve zürriyetlerinden iyi olanları kendilerine vaad buyurduğun Adn cennetlerine koy. Şüphesiz çok güçlü, hüküm ve hikmet sahibi olan sensin."'),
(4142, 40, 9, '"Onları fenalıklardan koru. Sen her kimi fenalıklardan korursan, o gün muhakkak onu rahmetinle yarlığamışsındır. İşte asıl büyük kurtuluş da budur."'),
(4143, 40, 10, 'O kâfirlere mutlaka şöyle bağırılacaktır: "Elbette Allah\'ın buğzu, sizin nefislerinize buğzunuzdan daha büyüktür. Çünkü siz imana davet ediliyordunuz da inkâr ediyordunuz."'),
(4144, 40, 11, 'Kâfirler diyecekler ki: "Ey Rabbimiz! Sen bizi iki defa öldürdün, iki defa dirilttin. Şimdi günahlarımızı anladık. Fakat çıkmaya bir yol var mı?"'),
(4145, 40, 12, '(Onlara şöyle cevap verilir): "Bu azab size şu sebeptendir: Siz tek Allah\'a davet edildiğiniz zaman inkâr ettiniz. Ama O\'na ortak koşulunca inandınız. Artık hüküm, o yüce ve büyük Allah\'ındır."'),
(4146, 40, 13, 'Size âyetlerini gösteren, sizin için gökten bir rızık indiren O\'dur. Fakat onları ancak gönül verip düşünenler anlar.'),
(4147, 40, 14, 'O halde siz, dini Allah için halis kılarak hep O\'na yalvarın. İsterse kâfirler hoşlanmasınlar.'),
(4148, 40, 15, 'O dereceleri yükselten Arş\'ın sahibi Allah, o buluşma gününün (kıyametin) dehşetini haber vermek için kullarından dilediği kimseye emrinden ruh (melek) indiriyor.'),
(4149, 40, 16, 'O gün onlar kabirlerinden meydana fırlarlar. Kendilerinin hiçbir şeyi Allah\'a karşı gizli kalmaz. "Bugün mülk kimindir?" (diye sorulur. Cevaben): "Tek ve kahhar olan Allah\'ındır." (denir).'),
(4150, 40, 17, 'Bugün her nefis kazandığı ile cezalanacaktır. Bugün zulüm yoktur. Şüphesiz Allah, hesabı çabuk görendir.'),
(4151, 40, 18, 'Yaklaşmakta olan o felaket (kıyamet) gününü de onlara haber ver. O dem ki yürekler gırtlaklara dayanmıştır, yutkunup dururlar. Zalimler için ne ısınacak bir dost vardır, ne de sözü dinlenecek bir şefaatçi.'),
(4152, 40, 19, 'Allah, gözlerin hain bakışını da bilir, gönüllerin gizlediğini de.'),
(4153, 40, 20, 'Allah hakkı yerine getirir. Onların O\'ndan başka yalvardıkları ise hiçbir şeyi yerine getiremezler. Çünkü hakkıyla işiten ve gören ancak Allah\'tır.'),
(4154, 40, 21, 'Yeryüzünde bir gezmediler mi? Baksalar ya kendilerinden öncekilerin sonları nasıl olmuş? Onlar yeryüzünde gerek kuvvetçe ve gerek eserce kendilerinden daha üstündüler. Öyle iken Allah onları günahları sebebiyle tutup alıverdi. Kendilerini Allah\'ın azabından koruyacak biri bulunmadı.'),
(4155, 40, 22, 'O, şundandı: Onlara peygamberleri apaçık delillerle geliyorlardı. Ama onlar inkâr ettiler. Allah da tuttu kendilerini alıverdi. Çünkü O\'nun kuvveti çok, azabı şiddetlidir.'),
(4156, 40, 23, 'Andolsun Musa\'yı âyetlerimizle ve açık bir delil ile gönderdik.'),
(4157, 40, 24, 'Firavun\'a, Hâmân\'a ve Karun\'a da onlar: "Bu bir sihirbaz, bir yalancıdır" dediler.'),
(4158, 40, 25, 'Bunun üzerine Musa, kendilerine tarafımızdan hakkı getirince de: "Onunla beraber iman etmiş olanların oğullarını öldürün, kadınlarını diri tutun." dediler. Fakat o kâfirlerin tuzağı da hep boşa çıkmaktadır.'),
(4159, 40, 26, 'Bir de Firavun: "Bırakın beni, öldüreyim Musa\'yı da o Rabbine dua etsin. Çünkü ben onun, dininizi değiştirmesinden veya yeryüzünde bir bozgunculuk çıkarmasından korkuyorum" dedi.'),
(4160, 40, 27, 'Musa da: "Ben hesap gününe inanmayan her kibirliden, benim de Rabbim, sizin de Rabbiniz olan Allah\'a sığınırım" dedi.'),
(4161, 40, 28, 'Firavun ailesinden imanını saklayan bir adam da şöyle dedi: "Bir adamı, Rabbim Allah dediği için öldürecek misiniz? Halbuki o size Rabbinizden delillerle gelmiştir. Hem o bir yalancı ise çok sürmez, yalanı boynuna geçer. Fakat doğru ise size yaptığı tehditlerin birkısmı olsun başınıza gelir. Şüphe yok ki Allah aşırı giden bir yalancıyı doğru yola çıkarmaz."'),
(4162, 40, 29, '"Ey kavmim! Bugün mülk sizindir. Dünyada yüze çıkmış bulunuyorsunuz. Eğer gelecek olursa Allah\'ın hışmından bizi kim kurtarır?" Firavun: "Ben size görüşümden başkasını göstermiyorum ve herhalde ben size doğru yolu gösteriyorum" dedi.'),
(4163, 40, 30, 'O iman etmiş olan kimse de: "Ey kavmim! Doğrusu ben sizin hakkınızda Ahzab (önceki çeşitli toplumlar)ın günleri gibi bir günden korkuyorum."'),
(4164, 40, 31, '"Nuh Kavmi\'nin, Âd\'ın, Semud\'un ve daha sonrakilerin maceraları gibi (bir günün geleceğinden korkuyorum). Allah, kulları için bir zulüm istemez."'),
(4165, 40, 32, '"Ey kavmim! Ben size gelecek o çağrışma gününden (kıyamet gününden) korkuyorum."'),
(4166, 40, 33, '"O gün arkanıza dönüp kaçacaksınız. Fakat sizi Allah\'tan koruyacak olan yoktur. Her kimi Allah şaşırtırsa, artık ona bir yol gösterici bulunmaz."'),
(4167, 40, 34, 'Bundan önce size delillerle Yusuf gelmişti. O zaman da onun size getirdiği hakikatte şüphe edip durmuştunuz. Nihayet vefat ettiğinde de "Bundan sonra Allah asla peygamber göndermez" dediniz. İşte aşırı şüpheci olanları Allah böyle şaşırtır.'),
(4168, 40, 35, 'Onlar, kendilerine gelmiş bir delil olmaksızın, Allah\'ın âyetleri hakkında mücadele ederler. Bu durum, Allah katında ve iman edenler yanında büyük bir buğzu gerektirir. İşte Allah, her böbürlenen zorbanın kalbini öyle bir tabiat ile mühürler.'),
(4169, 40, 36, 'Firavun dedi ki: "Ey Hâmân! Bana bir kule yap, belki ben o yollara ulaşabilirim."'),
(4170, 40, 37, '"Göklerin yollarına ulaşabilirim de, Musa\'nın ilâhının ne olduğunu anlarım. Ben onu mutlaka yalancı sanıyorum." İşte böylece Firavun\'a kötü ameli süslü gösterildi de yoldan çıkarıldı. Çünkü Firavun düzeni hep boşa çıkar.'),
(4171, 40, 38, 'O iman etmiş olan kimse dedi ki: "Ey kavmim! Bana uyun ki size doğru yolu göstereyim."'),
(4172, 40, 39, '"Ey kavmim! Bu dünya hayatı ancak geçici bir menfaatten ibarettir. Ahiret ise durulacak karar yurdudur."'),
(4173, 40, 40, '"Her kim bir kötülük yaparsa, ona ancak yaptığının bir misli ile ceza verilir. Erkek veya kadın, her kim de mümin olarak iyi bir amel işlerse, işte onlar cennete girerler. Orada kendilerine hesapsız rızık verilir."'),
(4174, 40, 41, '"Hem ey kavmim! Niçin ben sizi kurtuluşa davet ederken, siz beni ateşe davet ediyorsunuz?"'),
(4175, 40, 42, '"Siz beni Allah\'ı inkâr etmeye ve bence hiç ilimde yeri olmayan şeyleri O\'na ortak koşmaya davet ediyorsunuz. Ben ise sizi o çok güçlü ve çok bağışlayıcı olan Allah\'a davet ediyorum."'),
(4176, 40, 43, '"Hiç inkâr edilemez ki, gerçekten sizin beni davet ettiğiniz şeyin dünyada da, ahirette de bir davet hakkı yoktur. Hepimizin dönüşü Allah\'adır. Şüphesiz haddi aşanların hepsi cehennemliktir."'),
(4177, 40, 44, '"Siz benim söylediklerimi sonra anlayacaksınız. Ben işimi Allah\'a havale ediyorum. Şüphesiz Allah, kullarını görür, gözetir."'),
(4178, 40, 45, 'Allah o mümini, onların kurdukları tuzakların kötülüklerinden korudu. Firavun\'un adamlarını ise, o kötü azab kuşattı.'),
(4179, 40, 46, 'Onlar, sabah akşam ateşe arzolunurlar. Kıyamet kopacağı gün de: "Firavun hanedanını azabın en şiddetlisine tıkın!" (denilecektir).'),
(4180, 40, 47, 'Hele ateş içinde birbirlerini protesto ederlerken, zayıf olanlar, büyüklük taslayanlara: "Hani bizler size tabi idik. Şimdi siz bizden bir ateş nöbetini savabiliyor musunuz?" derler.'),
(4181, 40, 48, 'Büyüklük taslayanlar da şöyle derler: "Evet, hepimiz onun içindeyiz. Allah kulları arasında hükmünü vermiştir."'),
(4182, 40, 49, 'Ateştekiler, cehennem bekçilerine derler ki: "Rabbinize dua edin de bir gün olsun bizden azabı biraz hafifletsin."'),
(4183, 40, 50, 'Bekçiler de: "Size peygamberleriniz mucizelerle gelmiyorlar mıydı?" diye sorarlar. Onlar: "Evet" derler. Bekçiler: "Öyle ise kendiniz dua edin" derler. Kâfirlerin duası ise hep çıkmazdadır.'),
(4184, 40, 51, 'Biz peygamberimize ve inananlara hem dünya hayatında hem de şahitlerin şahitlik edecekleri günde (kıyamette) elbette yardım ederiz.'),
(4185, 40, 52, 'O gün zalimlere özür dilemeleri fayda vermez. Onlara lanet vardır, onlara yurdun kötüsü (cehennem) vardır.'),
(4186, 40, 53, 'Andolsun ki biz Musa\'ya o hidayeti verdik ve İsrailoğullarına o kitabı miras kıldık.'),
(4187, 40, 54, '(Bunu) Aklı başında olanlara bir yol gösterici ve bir hatırlatma olsun diye (böyle yaptık).'),
(4188, 40, 55, 'O halde sabret. Çünkü Allah\'ın vaadi haktır. Hem günahından dolayı istiğfar et ve akşam sabah Rabbini hamdiyle tesbih et.'),
(4189, 40, 56, 'Kendilerine gelmiş kesin bir delil olmaksızın, Allah\'ın âyetleri hakkında mücadele edenlerin göğüslerinde ancak yetişemeyecekleri bir kibir vardır. Sen hemen Allah\'a sığın. Çünkü her şeyi işiten ve gören O\'dur.'),
(4190, 40, 57, 'Elbette göklerin ve yerin yaratılması, insanların yaratılmasından daha büyüktür. Fakat insanların çoğu bilmezler.'),
(4191, 40, 58, 'Kör ile gören bir olmaz, iman edip salih ameller işleyen kimseler ile kötülük yapan da bir değildir. Ne kadar da az düşünüyorsunuz!'),
(4192, 40, 59, 'Herhalde o saat (kıyamet) muhakkak gelecektir. Onda şüphe yok. Fakat insanların çoğu inanmazlar.'),
(4193, 40, 60, 'Halbuki Rabbiniz: "Bana yalvarın, dua edin ki size karşılık vereyim. Çünkü bana ibadet etmekten kibirlenip yüz çevirenler yarın horlanmış olarak cehenneme gireceklerdir." buyurdu.'),
(4194, 40, 61, 'İçinde dinlenesiniz diye geceyi, göz açıcı bir aydınlık olarak da gündüzü sizin için yaratan Allah\'tır. Gerçekten Allah insanlara karşı bir lütuf sahibidir. Fakat insanların çoğu şükretmezler'),
(4195, 40, 62, 'İşte Rabbiniz, her şeyin yaratıcısı olan o Allah\'tır. O\'ndan başka ilâh yoktur. O halde (haktan) nasıl çevrilirsiniz?'),
(4196, 40, 63, 'İşte Allah\'ın âyetlerini inkâr edenler böyle çevriliyorlar.'),
(4197, 40, 64, 'Allah, O\'dur ki sizin için yeri bir karargâh, göğü de bir bina yapmıştır. Size şekil vermiş, sonra şekillerinizi güzelleştirmiştir. Hoş nimetlerden size rızık vermiştir. İşte Rabbiniz o Allah\'tır. Âlemlerin Rabbi olan Allah ne yücedir!'),
(4198, 40, 65, 'Daimî bir hayat sahibi ancak O\'dur. O\'ndan başka ilâh yoktur. Onun için dini halis kılarak O\'na, hep O\'na yalvarın. Hamd, âlemlerin Rabbi olan Allah\'a mahsustur.'),
(4199, 40, 66, 'De ki: "Bana Rabbimden apaçık deliller geldiği zaman, ben o sizin Allah\'ı bırakıp taptıklarınıza ibadet etmekten kesinlikle men edildim ve bana âlemlerin Rabbine teslim olmam emredildi."'),
(4200, 40, 67, '"Sizi (önce) bir topraktan, sonra bir damla sudan, sonra bir aleka (embriyo)dan yaratan, sonra sizi bir bebek olarak çıkaran, sonra güçlü kuvvetli bir çağa erişmeniz, sonra da ihtiyarlar olmanız için yaşatıp büyüten O\'dur. İçinizden kimi de daha önce vefat ettiriliyor. (Bunları Allah) belirli bir süreye ulaşasınız ve aklınızı kullanasınız diye (böyle yapıyor)."'),
(4201, 40, 68, 'O, hem yaşatır, hem öldürür. O, bir şey yapmak isteyince ona sadece "ol!" der, o şey de hemen oluverir.'),
(4202, 40, 69, 'Bakmaz mısın şimdi Allah\'ın âyetleri hakkında mücadeleye kalkanlara! (Haktan) nasıl döndürülüyorlar?'),
(4203, 40, 70, 'Kitaba ve Resullerimizi gönderdiğimiz şeylere yalan diyenler, artık ilerde bilecekler.'),
(4204, 40, 71, 'O zaman boyunlarında halkalar ve zincirler olduğu halde sürükleneceklerdir.'),
(4205, 40, 72, 'Kaynar suda, sonra da ateşte kaynatılacaklardır.'),
(4206, 40, 73, 'Sonra da onlara: "Nerede o ortak koştuklarınız?" denilecek.'),
(4207, 40, 74, 'O Allah\'tan başkaları (nerede denilecek). Onlar da diyecekler ki: "Hepsi bizden uzaklaşıp gittiler. Daha doğrusu biz bundan önce hiçbir şeye ibadet etmiyormuşuz." İşte Allah, o kâfirleri böyle şaşırtır.'),
(4208, 40, 75, 'Bunun sebebi şudur: Çünkü siz yeryüzünde haksız yere seviniyor ve güveniyordunuz.'),
(4209, 40, 76, 'İçlerinde ebedî olarak kalmak üzere cehennemin kapılarından girin. Bak ne kötü o kibirlenenlerin yeri?'),
(4210, 40, 77, 'Ey Muhammed! Sen sabret, şüphesiz Allah\'ın vaadi haktır, mutlaka gerçekleşecektir. Onlara yaptığımız tehdidin bir kısmını sana göstersek de veya seni vefat ettirsek de onlar mutlaka döndürülüp bize getirileceklerdir.'),
(4211, 40, 78, 'Andolsun ki biz senin önünden nice peygamberler göndermişizdir. Onlardan kimini sana anlatmışız, kimini de anlatmamışızdır. Hiçbir peygamber, Allah\'ın izni olmaksızın bir mucize getiremez. Allah\'ın emri gelince de hak yerine getirilir. Batıl bir dava peşinde koşanlar, işte bu noktada hüsrana uğrarlar.'),
(4212, 40, 79, 'Kimine binesiniz, kimini de yiyesiniz diye sizin için o yumuşak başlı hayvanları yaratan Allah\'tır.'),
(4213, 40, 80, 'Sizin için onlarda daha nice menfaatler vardır. Onların üzerinde gönüllerinizdeki bir arzuya erersiniz. Hem onlar üzerinde, hem de gemiler üzerinde taşınırsınız.'),
(4214, 40, 81, 'Allah size âyetlerini gösteriyor. Şimdi Allah\'ın âyetlerinin hangisini inkâr edersiniz?'),
(4215, 40, 82, 'Daha yeryüzünde gezip de bir bakmazlar mı? Kendilerinden öncekilerin sonu nasıl olmuş? Onlar kendilerinden hem daha çok, hem de kuvvetçe ve yeryüzündeki eserlerinin sağlamlığı bakımından daha çetindiler. Öyle iken o kazandıkları şeyler, kendilerini kurtaramadı.'),
(4216, 40, 83, 'Çünkü onlara peygamberleri, delillerle geldikleri zaman, kendilerinde bulunan ilme güvendiler de o alay ettikleri şey onları kuşatıverdi.'),
(4217, 40, 84, 'O zaman hışmımızı gördüklerinde: "Allah\'ın birliğine inandık ve O\'na şirk koştuğumuz şeyleri inkâr ettik" dediler.'),
(4218, 40, 85, 'Ama hışmımızı gördükleri zamanki imanları kendilerine fayda verecek değildi. Allah\'ın, kulları hakkındaki geçe gelen kanunu budur. İşte kâfirler bu noktada hüsrana düştüler.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'Hâ Mîm.'),
(4220, 41, 2, 'Bu Kur\'ân Rahmân ve Rahîm olan Allah tarafından indirilmiştir.'),
(4221, 41, 3, 'Bu, Arapça bir Kur\'an olarak, âyetleri bilen bir kavim için ayırt edilip açıklanmış bir kitaptır.'),
(4222, 41, 4, 'O, müjdeleyici ve uyarıcı olarak gönderilmiştir. Fakat insanların çoğu yüz çevirmişlerdir. Artık onlar gerçeği işitmezler.'),
(4223, 41, 5, 'Onlar: "Ey Muhammed! Senin bizi davet ettiğin şeye karşı kalplerimiz kapalıdır. Kulaklarımızda da bir ağırlık vardır. Seninle bizim aramızda anlaşmamıza engel bir de perde vardır. Sen istediğini yap, çünkü biz yapıyoruz" dediler.'),
(4224, 41, 6, 'Ey Muhammed! De ki: "Ben sadece sizin gibi bir insanım, ancak bana ilâhınızın bir tek ilâh olduğu vahyediliyor. Artık hep O\'na yönelin ve O\'ndan bağışlanma dileyin. Vay O\'na ortak koşanların haline!'),
(4225, 41, 7, 'Onlar, zekatı vermezler, ahireti de inkâr ederler.'),
(4226, 41, 8, 'Şüphesiz ki, iman edip, salih amel işleyenler için de bitmez tükenmez bir mükafat vardır.'),
(4227, 41, 9, 'De ki: "Siz yeri iki günde yaratanı gerçekten inkâr edip duracak mısınız? Bir de O\'na eşler koşuyorsunuz ha? O bütün âlemlerin Rabbidir."'),
(4228, 41, 10, 'O, yerin üstünde sabit dağlar yarattı. Orada bereketler meydana getirdi. Orada araştırıp soranlar için rızıkları tam dört günde belli bir seviyede takdir edip, düzene koydu.'),
(4229, 41, 11, 'Sonra duman halinde bulunan göğe yöneldi. Ona ve yerküreye: "İsteyerek veya istemeyerek buyruğuma gelin." dedi. Her ikisi de: "İsteyerek geldik" dediler.'),
(4230, 41, 12, 'Böylece Allah onları iki günde yedi gök olmak üzere yerine koydu. Her göğe kendi işini bildirdi. Biz en yakın göğü kandillerle süsledik ve koruduk. İşte bu çok güçlü ve her şeyi bilen Allah\'ın takdiridir.'),
(4231, 41, 13, 'Eğer onlar, yine yüz çevirirlerse de ki: "Ben sizi Âd ve Semud\'un başına gelen yıldırıma benzer bir yıldırıma karşı uyardım."'),
(4232, 41, 14, 'Onlara Allah\'tan başkasına kulluk etmeyin diye önlerinden ve arkalarından peygamberler geldiği zaman: "Eğer Rabbimiz dileseydi mutlaka melekler indirirdi. Biz sizin tebliğ için gönderildiğiniz şeylere inanmayız." dediler.'),
(4233, 41, 15, 'Âd kavmine gelince onlar yeryüzünde büyüklük tasladılar ve: "Bizden daha kuvvetli kim vardır?" dediler. Onlar kendilerini yaratan Allah\'ın kendilerinden daha kuvvetli olduğunu görmediler mi? Onlar bizim âyetlerimizi bile bile inkâr ediyorlardı.'),
(4234, 41, 16, 'Bu yüzden biz de onlara dünya hayatında rezillik azabını tattırmak için o uğursuz günlerde dondurucu bir kasırga gönderdik. Ahiret azabı ise elbette daha çok rezil edicidir. Onlara yardım da edilmeyecektir.'),
(4235, 41, 17, 'Semûd kavmine gelince, biz onlara doğru yolu gösterdik. Fakat onlar körlüğü doğru yola tercih ettiler. Bunun üzerine kazandıkları kötülük yüzünden alçaltıcı azabın yıldırımı onları çarpıverdi.'),
(4236, 41, 18, 'Biz iman edenleri ve kötülükten sakınanları ise kurtardık.'),
(4237, 41, 19, 'O gün Allah\'ın düşmanları cehennem ateşine sürülmek üzere hep bir araya toplanırlar.'),
(4238, 41, 20, 'Nihayet oraya vardıkları zaman kulakları, gözleri ve derileri yaptıkları şeyler hakkında onların aleyhinde şahitlik ederler.'),
(4239, 41, 21, 'Onlar derilerine: "Niçin aleyhimize şahitlik ettiniz?" derler. Derileri de: "Bizi her şeyi konuşturan Allah konuşturdu, sizi ilk defa yaratan O\'dur ve siz yine O\'na döndürülüyorsunuz" derler.'),
(4240, 41, 22, 'Siz kulaklarınızın, gözlerinizin ve derilerinizin aleyhinizde şahitlik edeceğinden korkarak kötülükten sakınmıyordunuz. Fakat yaptıklarınızdan birçoğunu Allah\'ın bilmeyeceğini zannediyordunuz.'),
(4241, 41, 23, 'İşte Rabbiniz hakkında beslediğiniz bu zannınız sizi helak etti de zarara uğrayanlardan oldunuz.'),
(4242, 41, 24, 'Şimdi eğer dayanabilirlerse onların yeri ateştir. Yok eğer hoşnutluğa dönmek isterlerse bile artık onlar hoşnut edileceklerden değildirler.'),
(4243, 41, 25, 'Biz onlara birtakım arkadaşlar musallat ettik de onlar kendilerine önlerinde ve arkalarında ne varsa hepsini güzel gösterdiler. Böylece kendilerinden önce gelip, geçmiş olan cin ve insan toplulukları hakkındaki, azab sözü onlar için de hak oldu. Doğrusu onların hepsi de kendilerine yazık etmişlerdir.'),
(4244, 41, 26, 'İnkâr edenler: "Bu Kur\'ânı dinlemeyin, okunurken gürültü yapın, belki üstün gelirsiniz" dediler.'),
(4245, 41, 27, 'Biz mutlaka inkâr edenlere şiddetli bir azab tattıracağız. Ve onlara yaptıkları amellerin en kötüsünün cezasını vereceğiz.'),
(4246, 41, 28, 'İşte Allah\'ın düşmanlarının cezası ateştir. Âyetlerimizi bile bile inkâr etmelerinin cezası olarak, onlar için orada ebedî olarak kalacakları cehennem yurdu vardır.'),
(4247, 41, 29, 'İnkâr edenler: "Ey Rabbimiz! Cinlerden ve insanlardan bizi doğru yoldan saptıranları bize göster de onları ayaklarımızın altına alalım, böylece cehennemin en altında kalanlardan olsunlar." diyeceklerdir.'),
(4248, 41, 30, '"Rabbimiz Allah\'tır" deyip, sonra da doğrulukta devam edenlere gelince, onların üzerine melekler iner ve derler ki: "Korkmayın, üzülmeyin, size vaad edilen cennetle sevinin."'),
(4249, 41, 31, '"Biz dünya hayatında da, ahirette de sizin dostlarınızız. Cennette sizin için canınızın çektiği ve istediğiniz her şey vardır."'),
(4250, 41, 32, 'Bunlar çok bağışlayıcı ve çok merhametli olan Allah tarafından bir ağırlamadır.'),
(4251, 41, 33, 'Allah\'a davet eden, salih amel işleyen ve: "Ben gerçekten müslümanlardanım" diyen kimseden daha güzel sözlü kim olabilir?'),
(4252, 41, 34, 'Hem iyilik de bir değildir, kötülük de. Kötülüğü en güzel bir şekilde sav. O zaman seninle kendi arasında bir düşmanlık olan kişinin, sanki samimi bir dost gibi olduğunu görürsün.'),
(4253, 41, 35, 'Bu olgunluğa ancak sabredenler kavuşturulur, buna ancak hayırdan büyük bir pay sahibi olan kavuşturulur.'),
(4254, 41, 36, 'Eğer şeytandan gelen kötü bir düşünce seni dürtecek olursa hemen Allah\'a sığın. Çünkü O her şeyi işitir ve bilir.'),
(4255, 41, 37, 'Gece ile gündüz ve güneş ile ay Allah\'ın kudretinin delillerindendir. Güneşe ve aya secde etmeyin. Eğer sadece Allah\'a kulluk yapmak istiyorsanız, onları yaratan Allah\'a secde edin.'),
(4256, 41, 38, 'Eğer onlar büyüklük taslarlarsa bilsinler ki, Rabbinin yanındaki melekler gece gündüz O\'nu tesbih ederler ve hiç usanmazlar.'),
(4257, 41, 39, 'Senin yeryüzünü boynu bükük, kupkuru görmen de Allah\'ın kudretinin delillerindendir. Biz onun üzerine suyu indirdiğimiz zaman titreşir ve kabarır. Şüphesiz ki ona hayat veren Allah mutlaka ölüleri de diriltir. Doğrusu O\'nun her şeye gücü yeter.'),
(4258, 41, 40, 'Âyetlerimiz hakkında doğruluktan ayrılıp inkâra sapanlar bize gizli kalmazlar. O halde ateşe atılacak olan mı daha hayırlıdır, yoksa kıyamet günü güven içinde gelecek olan mı? İstediğinizi yapın. Şüphesiz ki Allah, yaptığınız şeyleri hakkıyla görür.'),
(4259, 41, 41, 'Kur\'ân kendilerine geldiğinde onu inkâr edenler, mutlaka cezalarını çekceklerdir. O gerçekten çok değerli bir kitaptır.'),
(4260, 41, 42, 'Ona ne önünden, ne de ardından batıl gelemez. O hüküm ve hikmet sahibi, öğülmeye layık olan Allah tarafından indirilmiştir.'),
(4261, 41, 43, 'Ey Muhammed! Sana senden önceki peygamberlere söylenenden başka bir şey söylenmiyor. Şüphesiz ki senin Rabbin hem mağfiret sahibidir hem de acı verecek bir azap sahibidir.'),
(4262, 41, 44, 'Eğer biz onu yabancı dilden bir Kur\'ân yapsaydık onlar mutlaka: "Bu kitabın âyetleri genişçe açıklanmalı değil miydi? Arap bir peygambere yabancı dil, öyle mi?" derlerdi. Sen de ki: "O, iman edenler için bir hidayet ve şifadır." İman etmeyenlerin kulaklarında ise bir ağırlık vardır. Kur\'ân onlara göre bir körlüktür. Sanki onlar uzak bir yerden çağrılıyorlar (da duymuyorlar).'),
(4263, 41, 45, 'Andolsun ki biz Musa\'ya Tevrat\'ı vermiştik de onda ihtilafa düşmüşlerdi. Eğer Rabbin tarafından azabın ertelenmesine dair bir söz geçmeseydi mutlaka aralarında hüküm verilirdi. Gerçekten onlar Kur\'ân hakkında bir şüphe ve tereddüt içindedirler.'),
(4264, 41, 46, 'Her kim iyi bir iş yaparsa, kendi lehine yapmış olur. Kim de bir kötülük yaparsa, kendi aleyhine yapmış olur. Rabbin kullara zulmedecek değildir.'),
(4265, 41, 47, 'Kıyamet zamanını bilmek ancak Allah\'a havale edilir. Onun bilgisi dışında hiçbir meyve kabuğundan çıkmaz, hiçbir dişi gebe kalmaz ve doğurmaz. Allah onlara: "Bana koştuğunuz ortaklarım nerede?" diye seslendiği gün, onlar: "Senin ortağın olduğuna dair bizden hiçbir şahit olmadığını sana arz ederiz." derler.'),
(4266, 41, 48, 'Önceden tapmakta oldukları şeyler, kendilerinden uzaklaşıp kaybolmuştur. Onlar da kendileri için kaçacak bir yer olmadığını anlamışlardır.'),
(4267, 41, 49, 'İnsan hayır istemekten usanmaz, fakat kendisine bir kötülük dokununca üzülür ve ümitsizliğe düşer.'),
(4268, 41, 50, 'Andolsun ki kendisine dokunan bir zarardan sonra, biz ona tarafımızdan bir rahmet tattırsak, O: "Bu benim hakkımdır, kıyametin kopacağını da sanmıyorum, Rabbime döndürülmüş olsam bile mutlaka O\'nun yanında benim için daha güzel şeyler vardır" der. Biz o inkâr edenlere yaptıkları şeyleri mutlaka haber vereceğiz ve onlara ağır bir azap tattıracağız.'),
(4269, 41, 51, 'Biz insana bir nimet verdiğimiz zaman o yüz çevirir, yan çizer. Ona bir kötülük dokunduğu zaman da uzun uzun yalvarır.'),
(4270, 41, 52, 'Ey Muhammed! De ki: "Ne dersiniz? O Kur\'ân Allah tarafından gelmiş olup da sonra siz onu inkâr etmişseniz, o takdirde Hak\'tan uzak bir ayrılığa düşenden daha sapık kim olabilir?"'),
(4271, 41, 53, 'Biz onlara hem ufuklarda ve hem kendi nefislerinde delillerimizi göstereceğiz ki, Kur\'ân\'ın hak olduğu kendilerine açıkça belli olsun. Senin Rabbinin her şeye şahit olması kafi değil mi?'),
(4272, 41, 54, 'İyi bilin ki onlar Rablerine kavuşmaktan bir şüphe içindedirler, yine iyi bilin ki, Allah her şeyi ilmiyle kuşatmıştır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'Hâ, mîm, ayn, sîn, kaf.'),
(4274, 42, 2, 'Hâ, mîm, ayn, sîn, kaf.'),
(4275, 42, 3, 'Ey Muhammed! Çok güçlü hüküm ve hikmet sahibi olan Allah sana da senden öncekilere de böylece vahyeder.'),
(4276, 42, 4, 'Göklerde ve yerde ne varsa, hepsi O\'nundur. O çok yücedir, çok büyüktür.'),
(4277, 42, 5, 'Nerde ise gökler O\'nun azametinden tâ üstlerinden çatlayacak gibi titreşiyorlar. Melekler Rablerini hamd ile tesbih ediyorlar ve yeryüzünde bulunan kimseler için mağfiret diliyorlar. İyi bilin ki Allah çok bağışlayıcıdır, çok merhamet edicidir.'),
(4278, 42, 6, 'Allah\'tan başka dostlar edinenlere gelince, Allah onların üzerinde devamlı bir gözetleyicidir. Ama sen onların üzerinde bir vekil değilsin.'),
(4279, 42, 7, 'Böylece biz sana Arapça bir Kur\'ân indirdik ki, şehirlerin anası (olan Mekke) halkını ve etrafındakileri uyarasın ve hakkında hiç şüphe olmayan kıyamet gününün dehşetinden onları korkutasın. Bir grup cennettedir, bir grup da cehennemdedir.'),
(4280, 42, 8, 'Eğer Allah dileseydi bütün insanları bir tek ümmet yapardı. Fakat O yalnız dilediğini rahmetinin içine almaktadır. Zalimler için ne bir dost vardır, ne de bir yardımcı.'),
(4281, 42, 9, 'Yoksa onlar Allah\'tan başka dostlar mı edindiler? Oysa asıl dost Allah\'tır. Ölüleri diriltecek olan da O\'dur. O\'nun her şeye gücü yeter.'),
(4282, 42, 10, 'Hakkında ihtilafa düştüğünüz herhangi bir şeyin hükmü Allah\'a aittir. İşte benim Rabbim olan Allah budur. Ben yalnız O\'na güvendim ve yalnız O\'na yöneliyorum.'),
(4283, 42, 11, 'O göklerin ve yerin yaratıcısıdır. O sizin için kendi nefsinizden eşler ve hayvanlardan da çiftler yaratmıştır. O, sizi bu düzen içerisinde üretip çoğaltıyor. O\'nun benzeri olan hiçbir şey yoktur. O, her şeyi işitir ve görür.'),
(4284, 42, 12, 'Göklerin ve yerin kilitleri O\'na aittir. O dilediğine rızkı genişletir ve daraltır. Şüphesiz ki O, her şeyi hakkıyla bilir.'),
(4285, 42, 13, 'Allah dinden Nuh\'a tavsiye buyurduğu şeyi sizin için de bir kanun yaptı ve (Ey Muhammed!) sana vahyettiğimizi, İbrahim\'e, Musa\'ya ve İsa\'ya tavsiye buyurduğumuzu da şeriat kıldı. Şöyle ki: Dini doğru tutun ve onda ayrılığa düşmeyin. Fakat senin kendilerini davet ettiğin şey, müşriklere ağır geldi. Allah dilediğini kendine seçer ve kendisine yöneleni de doğru yola iletir.'),
(4286, 42, 14, 'Onlar kendilerine bilgi geldikten sonra, ancak aralarındaki, çekememezlik yüzünden ayrılığa düştüler. Eğer Rabbin tarafından azabın ertelendiğine dair bir söz geçmemiş olsaydı aralarında mutlaka hüküm verilirdi. Kendilerinden sonra Kitab\'a vâris kılınan kitap ehli de Kur\'ân hakkında bir şüphe ve tereddüt içindedirler.'),
(4287, 42, 15, 'Ey Muhammed! İşte bunun için insanları tevhide davet et ve sana emredildiği gibi dosdoğru ol. Onların keyiflerine uyma ve de ki: "Ben Allah\'ın kitaptan indirdiğine inandım ve bana aranızda adaleti gerçekleştirmem emredildi. Allah bizim de rabbimiz sizin de Rabbinizdir. Bizim yaptıklarımız bize, sizin yaptıklarınız da size aittir. Sizinle bizim aramızda hiçbir tartışmaya yer yoktur. Allah hepimizi biraraya toplayacaktır. Dönüş yalnız O\'nadır.'),
(4288, 42, 16, 'Allah\'ın davetine uyulduktan sonra, hâlâ O\'nun dini hakkında mücadele edenlerin, getirdikleri deliller Rableri yanında batıldır. Onların üzerinde bir gazab ve kendileri için şiddetli bir azab vardır.'),
(4289, 42, 17, 'Bu kitabı ve ölçüyü hakla indiren Allah\'tır. Ne bilirsin, belki de kıyamet saati yakındır!'),
(4290, 42, 18, 'O\'na inanmayanlar kıyametin çabuk gelmesini istiyorlar. İnananlar ise O\'ndan korkarlar ve O\'nun hak olduğunu bilirler. İyi bilin ki, kıyamet saati hakkında tartışanlar derin bir sapıklık içindedirler.'),
(4291, 42, 19, 'Allah kullarına çok lütufkârdır. Dilediğine rızık verir. O çok kuvvetlidir, çok güçlüdür.'),
(4292, 42, 20, 'Her kim ahiret kazancını isterse, biz onun kazancını artırırız, her kim de dünya kazancını isterse ona da ondan veririz, ama onun ahirette hiçbir nasibi yoktur.'),
(4293, 42, 21, 'Yoksa onların, Allah\'ın dinde izin vermediği şeyi kendilerine meşru kılacak ortakları mı vardır? Eğer azabın ertelenmesine dair kesin yargı sözü olmasaydı, aralarında hemen hüküm verilir, işleri bitirilirdi. Gerçekten zalimler için acı bir azab vardır.'),
(4294, 42, 22, 'Sen kıyamet günü kazandıkları şeyin cezası başlarına gelirken zalimlerin korkudan titrediklerini görürsün. İman edip salih amel işleyenler ise cennet bahçelerindedirler. Rablerinin yanında onlar için istedikleri her şey vardır. İşte büyük lütuf budur.'),
(4295, 42, 23, 'İşte Allah iman edip salih amel işleyen kullarını bununla müjdeler. Ey Muhammed! De ki: "Ben bu tebliğime karşı sizden akrabalıkta sevgiden başka hiçbir ücret istemiyorum." Her kim bir iyilik yaparsa biz onun iyiliğini artırırız. Şüphesiz ki Allah çok bağışlayıcıdır, şükrün karşılığını verir.'),
(4296, 42, 24, 'Yoksa onlar, senin hakkında: "Allah\'a karşı yalan uydurdu." mu diyorlar? Eğer Allah dilerse senin de kalbini mühürler; batılı yok eder ve sözleriyle hakkı gerçekleştirir. Şüphesiz ki O kalplerde bulunan şeyleri hakkıyla bilir.'),
(4297, 42, 25, 'Kullarının tevbesini kabul eden, kötülükleri affeden ve sizin yaptıklarınızı bilen O\'dur.'),
(4298, 42, 26, 'Allah iman edip, salih amel işleyenlerin tevbesini kabul eder, onlara lütfundan daha fazlasını verir. Kâfirler için ise şiddetli bir azap vardır.'),
(4299, 42, 27, 'Eğer Allah rızkı kullarına bol bol verseydi, mutlaka yeryüzünde azgınlık ederlerdi. Fakat O dilediğini belli bir ölçüye göre indiriyor. Şüphesiz ki O, kullarından haberdardır, onları hakkıyla görür.'),
(4300, 42, 28, 'İnsanlar ümitlerini kestikten sonra yağmuru indiren ve rahmetini her tarafa yayan O\'dur. Övülmeye layık olan gerçek dost O\'dur.'),
(4301, 42, 29, 'Gökleri yeri ve her ikisinde yaydığı canlıları yaratması da Allah\'ın kudretinin delillerindendir. O\'nun dilediği zaman onları biraraya toplamaya da gücü yeter.'),
(4302, 42, 30, 'Başınıza gelen herhangi bir musibet kendi ellerinizle kazandıklarınız yüzündendir. Bununla beraber Allah yine de çoğunu affeder.'),
(4303, 42, 31, 'Siz yeryüzünde (O\'nu) aciz bırakamazsınız. Sizin Allah\'tan başka bir dostunuz ve yardımcınız da yoktur.'),
(4304, 42, 32, 'Denizlerde yüce dağlar gibi gemilerin yürümesi de O\'nun kudretinin delillerindendir.'),
(4305, 42, 33, 'Eğer O dilerse rüzgarı durdurur da yelkenle giden gemiler denizin üzerinde duruverirler. Şüphesiz ki bunda sabırlı olan ve çok şükreden kimseler için nice ibretler vardır.'),
(4306, 42, 34, 'Yahut da Allah kazandıkları günahlar yüzünden onları helâk eder ve birçoğunu da bağışlar.'),
(4307, 42, 35, 'Âyetlerimiz hakkında mücadele edenler bilsinler ki kendileri için kaçacak bir yer yoktur.'),
(4308, 42, 36, 'Size verilen herhangi bir şey sadece dünya hayatının geçici bir menfaatidir. Allah katında bulunanlar ise iman edip sadece Rablerine güvenen kimseler için daha hayırlı ve daha kalıcıdır.'),
(4309, 42, 37, 'O iman edenler, büyük günahlardan ve hayasızlıktan kaçınırlar. Onlar öfkelendikleri zaman da kusurları bağışlarlar.'),
(4310, 42, 38, 'Onlar, Rablerinin davetini kabul ederler ve namazı dosdoğru kılarlar. Onların işleri de kendi aralarında bir istişare iledir. Kendilerine verdiğimiz rızıktan onlar Allah yolunda harcarlar.'),
(4311, 42, 39, 'Onlar, bir zulüm ve saldırıya uğradıkları zaman birbirleriyle yardımlaşırlar.'),
(4312, 42, 40, 'Bir kötülüğün cezası yine onun gibi bir kötülüktür, ama kim affeder, bağışlarsa onun mükafatı Allah\'a aittir. Şüphesiz ki Allah, zalimleri sevmez.'),
(4313, 42, 41, 'Zulme uğradıktan sonra hakkını alan kimseye gelince, işte onların aleyhinde ceza vermek için herhangi bir yol yoktur.'),
(4314, 42, 42, 'Yol ancak insanlara zulmedenler ve yeryüzünde haksız yere taşkınlık edenler aleyhinedir. İşte onlar için acı bir azap vardır.'),
(4315, 42, 43, 'Her kim de sabreder ve kusuru bağışlarsa, işte bu elbette azmedilecek işlerdendir.'),
(4316, 42, 44, 'Allah kimi saptırırsa artık bundan sonra onun için hiçbir dost yoktur. Sen, azabı gördüklerinde zalimlerin: "Acaba dönecek bir yol var mıdır?" dediklerini görürsün.'),
(4317, 42, 45, 'Sen, onların aşağılıktan dolayı başları öne eğilmiş, göz ucuyla gizli gizli etrafa bakarlarken ateşe sunulduklarını görürsün, iman edenler de: "Gerçekten zarara uğrayanlar hem kendilerine hem de ailelerine kıyamet günü yazık etmiş olan kimselerdir."diyeceklerdir. İyi bilin ki zalimler devamlı bir azap içerisindedirler.'),
(4318, 42, 46, 'Onların Allah\'tan başka kendilerine yardım edecek hiçbir dostları yoktur. Allah kimi saptırırsa, artık onun için çıkar bir yol yoktur.'),
(4319, 42, 47, 'Allah tarafından, geri çevrilemeyecek kıyamet günü gelmeden önce, Rabbinizin davetine uyun, çünkü o gün, sizin için sığınacak bir yer yoktur ve siz inkâr da edemezsiniz.'),
(4320, 42, 48, 'Ey Muhammed! Eğer onlar yüz çevirirlerse bilsinler ki, biz seni onların üzerine bir bekçi olarak göndermedik. Sana düşen sadece tebliğdir. Gerçekten biz insana tarafımızdan bir rahmet tattırırsak ona sevinir, ama elleriyle yaptıkları yüzünden kendilerine bir kötülük isabet ederse, o zaman görürsün ki insan çok nankördür.'),
(4321, 42, 49, 'Göklerin ve yerin hükümranlığı yalnız Allah\'a aittir. O dilediğini yaratır, dilediğine kız çocuk, dilediğine de erkek çocuk bahşeder.'),
(4322, 42, 50, 'Yahut Allah onları erkek ve kız olmak üzere çift verir, dilediğini de kısır yapar. Şüphesiz ki O her şeyi bilir. O\'nun her şeye gücü yeter.'),
(4323, 42, 51, 'Allah bir insanla ancak vahiy yoluyla veya perde arkasından konuşur. Yahut da bir elçi gönderir de izniyle ona dilediğini vahyeder. Şüphesiz ki O çok yücedir, hüküm ve hikmet sahibidir.'),
(4324, 42, 52, 'İşte biz böylece sana da emrimizden Kur\'ân\'ı vahyettik. Yoksa sen kitap nedir? İman nedir? bilmiyordun. Fakat biz onu bir nur kıldık. Onunla kullarımızdan dilediğimizi doğru yola iletiyoruz. Şüphesiz ki sen de insanları doğru bir yola götürüyorsun.'),
(4325, 42, 53, 'Göklerde ve yerde bulunanların sahibi olan Allah\'ın yoluna götürüyorsun. İyi bilin ki bütün işler sonunda yalnız Allah\'a dönecektir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'Hâ, mîm.'),
(4327, 43, 2, 'Apaçık kitaba andolsun ki biz onu iyice anlayasınız diye Arapça bir Kur\'an yaptık.'),
(4328, 43, 3, 'Apaçık kitaba andolsun ki biz onu iyice anlayasınız diye Arapça bir Kur\'an yaptık.'),
(4329, 43, 4, 'Gerçekten o bizim nezdimizde bulunan ana kitapta mevcut yüce ve hikmet dolu bir kitaptır.'),
(4330, 43, 5, 'Siz haddi aşan bir kavim oldunuz diye Kur\'an\'ı size göndermekten vaz mı geçelim?'),
(4331, 43, 6, 'Biz öncekilere de nice peygamberler göndermiştik.'),
(4332, 43, 7, 'Onlar kendilerine gelen her peygamberle mutlaka alay ediyorlardı.'),
(4333, 43, 8, 'Biz onlardan daha kuvvetli olanları helâk ettik. Kur\'an\'da öncekilerin örneği de geçmiştir.'),
(4334, 43, 9, 'Eğer sen onlara: "Gökleri ve yeri kim yarattı?" diye sorsan elbette: "Onları çok güçlü ve herşeyi bilen Allah yarattı." derler.'),
(4335, 43, 10, 'O, yeryüzünü sizin için bir beşik yaptı ve doğru gidesiniz diye orada sizin için yollar meydana getirdi.'),
(4336, 43, 11, 'Allah gökten belli bir ölçüye göre su indirdi. Biz onunla ölü bir memlekete yeniden hayat verdik. İşte siz de kabirlerinizden böyle diriltilip çıkarılacaksınız.'),
(4337, 43, 12, 'Allah bütün çiftleri yaratmıştır. Sizin için bineceğiniz gemiler ve hayvanlar var etmiştir.'),
(4338, 43, 13, 'Siz onların sırtına binip üzerlerine yerleştiğiniz zaman, Rabbinizin nimetini anarak şöyle diyesiniz: "Bunları bizim hizmetimize veren Allah\'ı tenzih ve tesbih ederiz. Yoksa bizim bunlara gücümüz yetmezdi."'),
(4339, 43, 14, '"Gerçekten biz Rabbimize döneceğiz."'),
(4340, 43, 15, 'Buna rağmen insanlar, Allah\'ın kullarından bir kısmını O\'nun bir parçası saydılar. Gerçekten de insan apaçık bir nankördür.'),
(4341, 43, 16, 'Yoksa O, yarattıklarından kendisine kızlar edindi de erkek çocukları size mi seçti?'),
(4342, 43, 17, 'Onlardan biri Rahman olan Allah\'a isnad ettiği kız çocuğu ile müjdelendiği zaman yüzü simsiyah kesilir de öfkesinden yutkunur durur.'),
(4343, 43, 18, 'Yoksa onlar, süs ve zinet içerisinde yetiştirilip de mücadelede erkek gibi kendisini savunmaya açık olmayan kızları mı O\'na isnad ediyorlar?'),
(4344, 43, 19, 'Onlar Rahman olan Allah\'ın kulları olan melekleri de dişi saydılar. Onlar meleklerin yaratılışını gördüler mi? Onların şahitlikleri yazılacak ve onlar sorguya çekileceklerdir.'),
(4345, 43, 20, 'Onlar: "Eğer Rahman olan, Allah dileseydi, biz o meleklere tapmazdık." dediler. Onların bu hususta hiçbir bilgileri yoktur. Onlar sadece yalan söylüyorlar.'),
(4346, 43, 21, 'Yoksa biz kendilerine bundan önce bir kitap verdik de onlar, ona mı sarılıyorlar?'),
(4347, 43, 22, 'Hayır, onlar sadece: "Biz babalarımızı bu din üzerinde bulduk, biz de onların izinde gidiyoruz." dediler.'),
(4348, 43, 23, 'Ey Muhammed! Yine böyle biz senden önce de hangi memlekete bir uyarıcı göndermişsek, mutlaka oranın şımarık varlıklı kimseleri: "Biz babalarımızı bir din üzerinde bulduk, biz de onların izlerine uyarız." dediler.'),
(4349, 43, 24, 'Gönderilen uyarıcı; "Eğer size babalarınızı üzerinde bulduğunuz dinden daha doğrusunu getirmişsem de mi bana uymazsınız?" deyince, onlar: "Gerçekten biz sizin tebliğ için gönderildiğiniz şeyi tanımıyoruz." dediler.'),
(4350, 43, 25, 'Biz de onlardan intikam aldık. Bak peygamberleri yalanlayanların sonu nasıl oldu!'),
(4351, 43, 26, 'Hani İbrahim babasına ve kavmine: "Gerçekten ben sizin taptığınız şeylerden uzağım.'),
(4352, 43, 27, 'Ben ancak beni yaratana taparım. Şüphesiz ki O, beni doğru yola iletecektir." dedi.'),
(4353, 43, 28, 'İbrahim, bu sözü, ardından gelecek olanlara devamlı kalacak bir miras olarak bıraktı ki, onlar doğru yola dönsünler.'),
(4354, 43, 29, 'Doğrusu ben bunları da babalarını da kendilerine hak olan kitap ve gerçeği açıklayan bir peygamber gelinceye kadar faydalandırıp geçindirdim.'),
(4355, 43, 30, 'Kendilerine hak geldiği zaman onlar: "Bu bir büyüdür doğrusu biz onu tanımıyoruz." dediler.'),
(4356, 43, 31, 'Yine Onlar: "Bu Kur\'an, şu iki şehirden bir büyük adama indirilmeli değil miydi?" dediler.'),
(4357, 43, 32, 'Ey Muhammed! Rabbinin rahmetini onlar mı taksim ediyorlar? Dünya hayatında onların geçimliklerini aralarında biz taksim ettik. Birbirlerine işlerini gördürsünler diye biz onların bir kısmını diğerlerinden derecelerle üstün kıldık. Rabbinin rahmeti onların biriktirdikleri şeylerden daha hayırlıdır.'),
(4358, 43, 33, 'Eğer insanlar küfre sapan bir ümmet haline gelmeyecek olsalardı, biz O Rahman olan Allah\'ı inkâr eden kimselerin evlerine gümüşten tavanlar ve üzerine çıkacakları merdivenler yapardık.'),
(4359, 43, 34, 'Onların evleri için gümüşten kapılar, üzerine yaslanacakları koltuklar yapardık.'),
(4360, 43, 35, 'Daha nice altın ziynetler verirdik. Çünkü bunların bizce hiçbir kıymeti yoktur. Bütün bunlar dünya hayatının geçici menfaatinden başka bir şey değildir. Ahiret ise Rabbin katında takva sahipleri içindir.'),
(4361, 43, 36, 'Her kim Rahman olan Allah\'ın zikrinden yüz çevirirse biz ona bir şeytan musallat ederiz. Artık o şeytan onun yakın dostudur.'),
(4362, 43, 37, 'Şüphesiz ki bu şeytanlar onları yoldan çıkarırlar. Onlar da kendilerinin doğru yolda olduklarını sanırlar.'),
(4363, 43, 38, 'Nihayet kıyamet günü bize gelince, arkadaşına: "Keşke seninle benim aramda doğu ile batı arasındaki kadar bir uzaklık olsaydı. Sen ne kötü arkadaşmışsın!" der.'),
(4364, 43, 39, 'Onlara: "Bugün pişmanlık duymanız size hiçbir fayda sağlamayacaktır. Çünkü siz zulmettiniz. Şimdi de hepiniz azapta ortaksınız." denir.'),
(4365, 43, 40, 'Ey Muhammed! O halde sağırlara sen mi işittireceksin? Yahut körlere ve apaçık bir sapıklık içinde bulunanlara sen mi doğru yolu göstereceksin?'),
(4366, 43, 41, 'Eğer biz seni onlara azap gelmeden önce alıp götürsek bile onlardan intikam alırız.'),
(4367, 43, 42, 'Yahut da onlara vaad ettiğimiz azabı sana gösteririz. Çünkü bizim onlara azap etmeye gücümüz yeter.'),
(4368, 43, 43, 'Öyleyse sen, sana vahyedilen Kur\'an\'a sarıl. Şüphesiz ki sen doğru bir yol üzerindesin.'),
(4369, 43, 44, 'Doğrusu o Kur\'an, senin için de, kavmin için de bir öğüttür ve siz ondan sorguya çekileceksiniz.'),
(4370, 43, 45, 'Ey Muhammed! Senden önce gönderdiğimiz peygamberlerimize de sor, biz Rahman olan Allah\'tan başka kendisine ibadet edilecek ilâhlar yapmış mıyız?'),
(4371, 43, 46, 'Andolsun ki, biz Musa\'yı mucizelerimizle Firavun\'a ve ileri gelen adamlarına gönderdik. Musa: "Ben gerçekten âlemlerin Rabbi olan Allah\'ın peygamberiyim." dedi.'),
(4372, 43, 47, 'Musa onlara mucizelerimizi getirince onlar hemen bu mucizelere gülüverdiler.'),
(4373, 43, 48, 'Bizim onlara gösterdiğimiz her bir mucize diğerinden daha büyüktü. Belki doğru yola dönerler diye biz onları azapla yakaladık.'),
(4374, 43, 49, 'Onlar azâbı görünce: "Ey sihirbaz! Sende olan ahdi hürmetine bizim için Rabbine dua et. Biz gerçekten doğru yola gireceğiz." dediler.'),
(4375, 43, 50, 'Fakat azabı kendilerinden kaldırdığımız zaman hemen sözlerinden dönüverdiler.'),
(4376, 43, 51, 'Firavun kavmine seslenerek dedi ki: "Ey kavmim! Mısır hükümdarlığı ve altımdan akıp giden şu ırmaklar benim değil mi? Görmüyor musunuz?'),
(4377, 43, 52, 'Yoksa ben, nerede ise meramını anlatamayan şu zavallıdan daha hayırlı değil miyim?'),
(4378, 43, 53, 'Eğer O\'nun dediği doğru ise üzerine altın bilezikler atılmalı veya kendisiyle beraber onu tasdik eden melekler gelmeli değil miydi?"'),
(4379, 43, 54, 'Firavun kavmini küçümsedi. Onlar da O\'na itaat ettiler. Çünkü onlar fâsık bir kavimdi.'),
(4380, 43, 55, 'Nihayet bizi gazaplandırdıkları zaman onlardan intikam aldık. Hepsini suda boğduk.'),
(4381, 43, 56, 'Onları sonradan gelecekler için ibret ve örnek kıldık.'),
(4382, 43, 57, 'Meryem oğlu İsâ bir misal olarak anlatılınca, senin kavmin hemen ondan bir delil bulduklarını sanarak bağrışmaya başladılar.'),
(4383, 43, 58, 'Onlar dediler ki: "Bizim ilâhlarımız mı daha hayırlıdır, yoksa İsâ mı?" Bu misâli sırf seninle tartışmak için ortaya attılar. Doğrusu onlar çok kavgacı bir topluluktur.'),
(4384, 43, 59, 'İsâ, ancak kendisine nimet verdiğimiz ve İsrailoğullarına örnek kıldığımız bir kuldur.'),
(4385, 43, 60, 'Eğer biz dileseydik, sizden yeryüzünde yerinize geçecek melekler yaratırdık.'),
(4386, 43, 61, 'Gerçekten o, (İsâ\'nın yere inişi) kıyâmetin yaklaştığını gösteren bir bilgidir. Sakın kıyâmet hakkında şüpheye düşmeyip, bana uyun, bu doğru yoldur.'),
(4387, 43, 62, 'Sakın şeytan sizi doğru yoldan alıkoymasın. Gerçekten o sizin için apaçık bir düşmandır.'),
(4388, 43, 63, 'İsâ mucizelerle indiği zaman dedi ki: "Ben size hikmeti getirdim ve hakkında ihtilâfa düştüğünüz şeylerin bir kısmını size açıklamak için geldim. O halde Allah\'tan korkun, ve bana itaat edin.'),
(4389, 43, 64, 'Gerçekten benim de Rabbim sizin de Rabbiniz Allah\'tır. Öyle ise O\'na kulluk edin. Bu doğru bir yoldur.'),
(4390, 43, 65, 'Fakat aralarından çıkan gruplar, İsâ hakkında ihtilâfa düştüler. Acı bir günün azâbından dolayı vay zulmedenlerin hâline!'),
(4391, 43, 66, 'Onlar kendileri farkına varmadan ansızın kıyâmetin başlarına gelmesini mi bekliyorlar?'),
(4392, 43, 67, 'O gün Allah\'tan korkanlar hariç dost olanlar birbirlerine düşmandırlar.'),
(4393, 43, 68, 'Allah, takva sahiplerine şöyle nida eder: "Ey âyetlerimize imân edip müslüman olan kullarım! Bugün size hiçbir korku yoktur ve siz üzülmeyeceksiniz.'),
(4394, 43, 69, 'Allah, takva sahiplerine şöyle nida eder: "Ey âyetlerimize imân edip müslüman olan kullarım! Bugün size hiçbir korku yoktur ve siz üzülmeyeceksiniz.'),
(4395, 43, 70, 'Siz ve eşleriniz cennete girin. Orada ağırlanıp sevindirileceksiniz."'),
(4396, 43, 71, 'Onların etrafında yiyecek ve içecekler altın tepsiler ve kadehlerle dolaştırılır. Orada canların çektiği ve gözlerin hoşlandığı herşey vardır. Siz orada ebedi olarak kalacaksınız.'),
(4397, 43, 72, 'İşte yaptıklarınıza karşılık size miras verilen cennet budur.'),
(4398, 43, 73, 'Orada sizin için bol bol meyveler vardır. Onlardan yersiniz.'),
(4399, 43, 74, 'Şüphesiz ki suçlular, cehennem azâbında ebedi olarak kalacaklardır.'),
(4400, 43, 75, 'Onların azâbı hafifletilmez ve onlar azab içersinde ümitsizdirler.'),
(4401, 43, 76, 'Biz onlara zulmetmedik, fakat onlar kendileri zâlimler oldular.'),
(4402, 43, 77, 'Onlar cehennem bekçisine: "Ey Mâlik! Rabbin artık bizi öldürsün." diye seslenirler. Mâlik de: "Siz böylece kalacaksınız." der.'),
(4403, 43, 78, 'Andolsun ki biz size hakkı getirdik. Fakat sizin çoğunuz haktan hoşlanmıyorsunuz.'),
(4404, 43, 79, 'Yoksa onlar hakka karşı gelmek için bir iş mi kararlaştırdılar? Biz de onları cezalandırmak için kararlıyız.'),
(4405, 43, 80, 'Yoksa onlar bizim sırlarını ve gizli konuşmalarını işitmediğimizi mi sanıyorlar? Hayır, işitiriz ve yanlarında bulunan elçi meleklerimiz de her yaptıklarını yazıyorlar.'),
(4406, 43, 81, 'Ey Muhammed! de ki: "Eğer Rahman olan Allah\'ın bir çocuğu olsaydı, ona ibâdet edenlerin birincisi ben olurdum."'),
(4407, 43, 82, 'Göklerin ve yerin Rabbi, arşın Rabbi onların nitelendirdikleri şeyden münezzehtir, yücedir.'),
(4408, 43, 83, 'Şimdi sen bırak onları, tehdit edildikleri günlerine kavuşuncaya kadar batıla dalsınlar oynasınlar.'),
(4409, 43, 84, 'Gökteki ilâh da yerdeki ilâh da O\'dur. O hüküm ve hikmet sahibidir herşeyi bilir.'),
(4410, 43, 85, 'Göklerin, yerin ve her ikisi arasındakilerin hükümranlığı kendisine ait olan Allah\'ın şanı yücedir. Kıyâmet saatinin bilgisi de yalnız onun yanındadır. Siz sadece O\'na döndürüleceksiniz.'),
(4411, 43, 86, 'Onların Allah\'ı bırakıp da tapdıkları putlar şefaat hakkına sahip değillerdir. Ancak bilerek hakka şahitlik edenler şefâat edebilir.'),
(4412, 43, 87, 'Eğer sen onlara kendilerini kimin yarattığını sorsan elbette: "Allah" derler. O halde nasıl haktan çevriliyorlar?'),
(4413, 43, 88, 'Peygamberin sözü şu olmuştur: "Ey Rabbim! Bunlar gerçekten imân etmeyen bir kavimdir."'),
(4414, 43, 89, 'Ey Muhammed! Şimdilik sen onlara aldırma ve: "Size selâm olsun." de. Onlar yakında bilecekler!');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'Hâ, mîm.'),
(4416, 44, 2, 'O apaçık Kitab\'a andolsun ki biz onu gerçekten mübarek bir gecede indirdik. Çünkü biz onunla insanları uyarmaktayız.'),
(4417, 44, 3, 'O apaçık Kitab\'a andolsun ki biz onu gerçekten mübarek bir gecede indirdik. Çünkü biz onunla insanları uyarmaktayız.'),
(4418, 44, 4, 'O gecede her hikmetli iş tarafımızdan bir emirle ayrılır. Gerçekten biz Rabbin tarafından bir rahmet olarak peygamberler göndeririz. Şüphesiz ki O, herşeyi işitir ve bilir.'),
(4419, 44, 5, 'O gecede her hikmetli iş tarafımızdan bir emirle ayrılır. Gerçekten biz Rabbin tarafından bir rahmet olarak peygamberler göndeririz. Şüphesiz ki O, herşeyi işitir ve bilir.'),
(4420, 44, 6, 'O gecede her hikmetli iş tarafımızdan bir emirle ayrılır. Gerçekten biz Rabbin tarafından bir rahmet olarak peygamberler göndeririz. Şüphesiz ki O, herşeyi işitir ve bilir.'),
(4421, 44, 7, 'Siz eğer kesin olarak inanıyorsanız, iyi bilin ki Allah göklerin, yerin ve ikisi arasındakilerin Rabbidir.'),
(4422, 44, 8, 'Ondan başka hiçbir ilâh yoktur. O hem yaşatır, hem öldürür. O sizin de Rabbiniz, sizden önceki babalarınızın da Rabbidir.'),
(4423, 44, 9, 'Fakat kâfirler bir şüphe içinde oynayıp eğleniyorlar.'),
(4424, 44, 10, 'Ey Muhammed! Şimdi sen göğün, insanları bürüyecek açık bir duman getireceği günü gözetle. Bu acı bir azabdır.'),
(4425, 44, 11, 'Ey Muhammed! Şimdi sen göğün, insanları bürüyecek açık bir duman getireceği günü gözetle. Bu acı bir azabdır.'),
(4426, 44, 12, 'O gün insanlar: "Ey Rabbimiz! Bizden azabı kaldır. Artık biz inanıyoruz" derler.'),
(4427, 44, 13, 'Onlar için bunu düşünüp öğüt almak nerede? Oysa kendilerine gerçeği açıklayan bir de peygamber gelmişti.'),
(4428, 44, 14, 'Sonra onlar, o peygamberden yüz çevirdiler ve: "Bu öğretilmiş bir delidir." dediler.'),
(4429, 44, 15, 'Biz o azabı sizden birazcık kaldırırız. Ama siz mutlaka eski halinize dönersiniz.'),
(4430, 44, 16, 'Biz o büyük şiddetle çarptığımız gün mutlaka intikamımızı alırız.'),
(4431, 44, 17, 'Andolsun ki, biz onlardan önce Firavun kavmini de denemiştik. Onlara çok kıymetli bir peygamber gelmişti.'),
(4432, 44, 18, 'O peygamber onlara şöyle demişti: "Esaretiniz altındaki Allah\'ın kullarını bana teslim edin. Çünkü ben size gönderilmiş güvenilir bir peygamberim.'),
(4433, 44, 19, 'Allah\'a karşı üstünlük taslamayın. Şüphesiz ki ben size apaçık bir delil getiriyorum.'),
(4434, 44, 20, 'Gerçekten ben, beni taşlamanızdan dolayı benim de Rabbim, sizin de Rabbiniz olan Allah\'a sığındım.'),
(4435, 44, 21, 'Eğer siz bana iman etmezseniz hemen yanımdan uzaklaşın."'),
(4436, 44, 22, 'Musa: "Şüphesiz ki bunlar suçlu bir kavimdir." diyerek yardım etmesi için Rabbine yalvardı.'),
(4437, 44, 23, 'Allah buyurdu ki: "Kullarımı geceleyin yürüt. Çünkü siz takib edileceksiniz.'),
(4438, 44, 24, 'Karşıya geçince denizi olduğu gibi açık bırak. Çünkü onlar suda boğulacak bir ordudur."'),
(4439, 44, 25, 'Onlar neler bırakmışlardı, ne bahçeler, ne pınarlar!'),
(4440, 44, 26, 'Ne ekinler, ne güzel kaynaklar,'),
(4441, 44, 27, 'Ve içinde eğlenip durdukları nice nimetler ve refah!'),
(4442, 44, 28, 'İşte böylece biz onları başka bir kavme miras bıraktık.'),
(4443, 44, 29, 'Gök ve yer onların üzerine ağlamadı. Onlara mühlet de verilmedi.'),
(4444, 44, 30, 'Andolsun ki biz İsrailoğullarını o aşağılayıcı azabdan kurtardık.'),
(4445, 44, 31, 'Firavun\'dan da kurtardık çünkü o üstünlük taslayıp haddi aşan bir zorbaydı.'),
(4446, 44, 32, 'Andolsun ki biz onları bilerek o zamanki alemlere üstün kıldık.'),
(4447, 44, 33, 'Biz onlara içinde apaçık bir imtihan bulunan mucizeler verdik.'),
(4448, 44, 34, 'Gerçekten şu kâfirler diyorlar ki:'),
(4449, 44, 35, '"Bizim ilk ölümümüzden başka bir şey yoktur. Biz tekrar diriltilecek değiliz.'),
(4450, 44, 36, 'Eğer siz doğru söyleyen kimselerseniz babalarınızı bize getirin."'),
(4451, 44, 37, 'Onlar mı daha hayırlıdır, yoksa Tükba kavmi ile onlardan öncekiler mi? Biz onların hepsini de helak ettik. Çünkü onlar suçluydular.'),
(4452, 44, 38, 'Biz gökleri, yeri ve ikisi arasındakileri bir oyun ve eğlence olsun diye yaratmadık.'),
(4453, 44, 39, 'Biz onları hak ve hikmetle yarattık. Fakat onların çoğu bunu bilmezler.'),
(4454, 44, 40, 'Şüphesiz ki hakkı batıldan ayırd etme günü onların hepsinin bir araya toplanacağı gündür.'),
(4455, 44, 41, 'O gün dostun dosta hiçbir faydası olmaz. Onlara yardım da edilmez.'),
(4456, 44, 42, 'Ancak Allah\'ın merhamet ettiği kimseler böyle değildir. Şüphesiz ki Allah çok güçlüdür, çok merhamet edicidir.'),
(4457, 44, 43, 'Gerçekten zakkum ağacı,'),
(4458, 44, 44, 'Günahkârların yemeğidir.'),
(4459, 44, 45, 'O pota gibi karınlarda kaynar.'),
(4460, 44, 46, 'O, kızgın bir sıvının kaynaması gibidir.'),
(4461, 44, 47, 'Allah meleklere şöyle emreder. "Şunu tutun da Cehennem\'in ortasına sürükleyin."'),
(4462, 44, 48, '"Sonra onun başının üstüne kaynar su azabından dökün."'),
(4463, 44, 49, 'Ona şöyle denir! "Tat bakalım azabı! hani sen kendine göre çok güçlü ve çok üstündün.'),
(4464, 44, 50, 'İşte sizin inkâr edip durduğunuz şey budur."'),
(4465, 44, 51, 'Şüphesiz ki kötülükten sakınanlar güvenli bir makamdadırlar.'),
(4466, 44, 52, 'Bahçelerde ve pınar başlarındadırlar.'),
(4467, 44, 53, 'Onlar ince ipekten ve parlak atlastan elbiseler giyerek karşılıklı olarak otururlar.'),
(4468, 44, 54, 'İşte böyle, biz onları ayrıca iri siyah gözlü hurilerle evlendiririz.'),
(4469, 44, 55, 'Onlar orada güven içinde her çeşit meyveyi isteyebilirler.'),
(4470, 44, 56, 'Onlar orada ilk ölümden başka bir ölüm tatmazlar. Allah onları cehennem azabından korumuştur.'),
(4471, 44, 57, '(Bunların hepsi) Rabbinden bir lütuf olarak (verilmiştir.) İşte büyük kurtuluş budur.'),
(4472, 44, 58, 'Biz Kur\'ân\'ı senin dilinle indirip kolaylaştırdık. Umulur ki onlar öğüt alırlar.'),
(4473, 44, 59, 'Artık sen onların başlarına gelecekleri bekle: Çünkü onlar da bekleyip durmaktadırlar.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'Hâ, mîm'),
(4475, 45, 2, 'Bu kitap, Azîz ve Hakîm olan Allah tarafından indirilmiştir.'),
(4476, 45, 3, 'Şüphesiz göklerde ve yerde müminler için birçok âyetler vardır.'),
(4477, 45, 4, 'Sizin yaratılışınızda ve çeşitli canlıları yeryüzüne yaymasında kesin olarak inanan kimseler için ibretler vardır.'),
(4478, 45, 5, 'Gece ile gündüzün değişmesinde ve Allah\'ın gökten bir rızık sebebi olan yağmuru indirip de onunla yeryüzünü ölümünden sonra diriltmesinde ve rüzgârları yönlendirmesinde aklını kullanan bir topluluk için nice deliller vardır.'),
(4479, 45, 6, 'İşte bunlar, Allah\'ın âyetleridir. Sana onları hakkıyla okuyoruz. Artık Allah\'a ve âyetlerine inanmadıktan sonra hangi söze inanacaklar?'),
(4480, 45, 7, 'Her günahkâr kişinin vay haline!'),
(4481, 45, 8, 'O kimse Allah\'ın kendisine okunan âyetlerini işitir de, sonra sanki kibrinden hiç işitmemiş gibi ısrar eder. İşte sen onu, can yakıcı bir azabla müjdele!'),
(4482, 45, 9, 'Âyetlerimizden birşey öğrendiği zaman, onu alaya alıyor. İşte onlar için rezil ve rüsvay edici bir azap vardır.'),
(4483, 45, 10, 'Ötelerinde cehennem var. Ne kazandıkları şeyler, ne de Allah\'tan başka edindikleri dostlar, kendilerinden hiçbir şeyi (azabı) kaldıramaz. Onlar için büyük bir azab vardır.'),
(4484, 45, 11, 'Bu Kur\'an bir hidâyettir. Rablerinin âyetlerini inkâr edenlere ise, en şiddetlisinden acıklı bir azab vardır.'),
(4485, 45, 12, 'Allah O (yüce) zâttır ki, emriyle içinde gemilerin seyretmesi, sizin de O\'nun lütfundan rızık aramanız ve şükretmeniz için denizi emrinize vermiştir.'),
(4486, 45, 13, 'O, göklerde ve yerde bulunan herşeyi kendinden bir lütuf olarak sizin hizmetinize vermiştir. Şüphesiz bunda düşünen topluluklar için ibret ve deliller vardır.'),
(4487, 45, 14, 'Ey Muhammed! İman edenlere söyle: Allah\'ın cezalandıracağı günlerin geleceğini ummayanları şimdilik bağışlasınlar. Çünkü Allah her kavmi kazandıklarıyla cezalandıracaktır.'),
(4488, 45, 15, 'Her kim iyi bir iş yaparsa onun faydası kendisinedir. Kim de kötülük yaparsa zararı yine kendinedir. Sonra hep Rabbinize döndürüleceksiniz.'),
(4489, 45, 16, 'Andolsun ki biz, vaktiyle İsrailoğulları\'na kitap, hüküm ve peygamberlik vermiştik. Onları temiz rızıklarla rızıklandırmıştık. Ve onları âlemlerden üstün kılmıştık.'),
(4490, 45, 17, 'Din hususunda onlara apaçık deliller verdik. Fakat onlar, kendilerine ilim geldikten sonra aralarındaki çekememezlik ve düşmanlık yüzünden ayrılığa düşmüşlerdi. Şüphesiz Rabbin, ayrılığa düştükleri şeylerde, kıyâmet günü aralarında hükmedecektir.'),
(4491, 45, 18, 'Sonra (Ey Muhammed) seni din hususunda apaçık bir şeriat sahibi kıldık. Sen ona uy, bilmeyenlerin hevâ ve heveslerine uyma.'),
(4492, 45, 19, 'Çünkü onlar Allah\'tan gelecek hiçbir şeyi senden uzaklaştıramazlar. Şüphesiz zâlimler, birbirlerinin dostlarıdır. Allah ise müttakilerin dostudur.'),
(4493, 45, 20, 'Bu (Kur\'an) insanların kalb gözünü açan bir nur, kesin bilgi edinmek isteyen bir toplum için de hidâyet ve rahmettir.'),
(4494, 45, 21, 'Yoksa, kötülük işleyenler, hayatlarında ve ölümlerinde kendilerini, iman edip iyi ameller işleyen kimselerle bir tutacağımızı mı zannettiler? Ne kötü hüküm veriyorlar!'),
(4495, 45, 22, 'Halbuki Allah, gökleri ve yeri hak ile yarattı. Hem de herkese yaptığının karşılığı verilmek üzere, onlara asla haksızlık edilmez.'),
(4496, 45, 23, '(Ey Muhammed!) Hevâ ve hevesini kendine ilâh edinen, Allah\'ın kendi ilmi dahilinde saptırdığı, kulağını ve kalbini mühürleyip gözüne perde çektiği kimseyi görüyor musun? Şimdi onu Allah\'tan başka kim hidâyete erdirebilir? Hala düşünmez misiniz?'),
(4497, 45, 24, 'Hem müşrikler dediler ki: "Hayat, ancak bu dünya hayatımızdan ibarettir. Ölürüz ve yaşarız. Bizi ancak geçen zaman yokluğa sürükler. Halbuki onların bu hususta hiçbir bilgileri yoktur. Onlar, sadece böyle zannederler.'),
(4498, 45, 25, 'Kendilerine âyetlerimiz açıkça okunduğu zaman; "Eğer sözünüzde doğru iseniz atalarımızı diriltip getirin." demekten başka söylenecek hiçbir delil yoktur.'),
(4499, 45, 26, '(Ey Muhammed!) De ki: "Allah sizi diriltir. Sonra sizi o öldürür, sonra da geleceğinde şüphe olmayan kıyamet gününde (diriltip) bir araya toplar. Fakat insanların çoğu bilmezler.'),
(4500, 45, 27, 'Göklerin ve yerin mülkü sadece Allah\'ındır. Kıyâmetin kapacağı gün varya, işte o gün batıla sapanlar hep hüsrana düşecekler.'),
(4501, 45, 28, 'O gün her ümmeti, diz çökmüş görürsün. Her ümmet, kendi kitabına çağırılır, onlara: "Bugün yaptığınız amellerin cezası verilecektir.'),
(4502, 45, 29, 'İşte kitabınız, yüzünüze karşı hakkı söylüyor, çünkü biz sizin yaptıklarnızı hep kaydediyorduk." (denir).'),
(4503, 45, 30, 'İman edip iyi işler yapanlara gelince; Rableri onları rahmeti içine koyacaktır. İşte apaçık kurtuluş budur.'),
(4504, 45, 31, 'Ama kâfirlere gelince; onlara da denilir ki; "Size âyetlerim okunmadı mı? Siz büyüklük tasladınız ve günah işleyen bir kavim oldunuz değil mi?'),
(4505, 45, 32, 'Allah\'ın vaadi gerçektir. "O kıyâmetin geleceğinde şüphe yoktur." denildiğinde "Kıyamet nedir bilmiyoruz." Yalnız bir zandan ibârettir sanıyoruz. Fakat bu hususta kesin bir bilgimiz yok." derdiniz.'),
(4506, 45, 33, 'Derken yaptıkları amellerin kötülüğü gözlerinin önüne serildi, alay edip durdukları şey onları kuşatıverdi.'),
(4507, 45, 34, 'O gün kâfirlere şöyle denilir; "Siz, dünyada bugüne kavuşmayı nasıl unuttuysanız, biz de bugün sizi öylece unutacağız. Yeriniz ateştir ve sizin için yardımcılardan bir kimse de yoktur."'),
(4508, 45, 35, 'Bunun sebebi şudur; Siz Allah\'ın âyetlerini alaya aldınız, dünya hayatı sizi aldattı. Artık bugün onlar, ateşten çıkarılmayacaklar ve kendilerinden özür dilemeleri de kabul edilmeyecektir.'),
(4509, 45, 36, 'Hamd, göklerin Rabbi, yerin Rabbi ve âlemlerin Rabbi olan Allah\'a mahsustur.'),
(4510, 45, 37, 'Göklerde ve yerde büyüklük ve hâkimiyet O\'nundur. O, Aziz\'dir (herşeye galiptir); Hakîm\'dir (hüküm ve hikmet sahibidir).');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'Hâ mîm.'),
(4512, 46, 2, 'Bu kitabın indirilişi, çok güçlü, hüküm ve hikmet sahibi olan Allah tarafındandır.'),
(4513, 46, 3, 'Biz gökleri, yeri ve ikisi arasındakileri ancak hak ile ve belirli bir süre için yarattık. İnkâr edenler uyarıldıkları şeyden yüz çeviriyorlar.'),
(4514, 46, 4, 'Ey Muhammed! De ki: "Allah\'tan başka yalvardıklarınızı gördünüz mü? Onlar yerden ne yaratmışlar bana gösterin. Yoksa onların göklerin yaradılışında bir ortaklıkları mı var? Eğer siz doğru söyleyen kimseler iseniz bana bu Kur\'an\'dan önce indirilmiş bir kitap veya ilimden bir eser getirin."'),
(4515, 46, 5, 'Allah\'ı bırakıp da kıyamet gününe kadar kendisine hiç bir cevap veremeyecek olan putlara dua eden kimseden daha sapık kim olabilir? Oysa taptıkları şeylerin, onların yalvarışlarından haberleri bile yoktur.'),
(4516, 46, 6, 'Kıyamet günü insanlar biraraya toplandığı zaman taptıkları şeyler kendilerine düşman kesilirler. Ve onların kendilerine tapmalarını inkâr ederler.'),
(4517, 46, 7, 'Bizim âyetlerimiz kendilerine apaçık okunduğu zaman inkâr edenler kendilerine gelen hak kitap için: "Bu apaçık bir büyüdür." dediler.'),
(4518, 46, 8, 'Yoksa, "Onu (Muhammed) uydurdu." mu diyorlar? Sen de ki: "Eğer onu ben uydurmuşsam Allah\'tan bana gelecek cezayı savmaya sizin gücünüz yetmez. O sizin yaptığınız taşkınlıkları daha iyi bilir. Sizinle benim aramda şahit olarak Allah yeter. O çok bağışlayıcıdır, çok merhamet edicidir.'),
(4519, 46, 9, 'Ey Muhammed! De ki: "Ben Peygamberlerin ilki değilim. Bana ve size ne yapılacağını da bilmem. Ben ancak bana vahyedilene tabi oluyorum. Ben ancak apaçık bir uyarıcıyım.'),
(4520, 46, 10, 'De ki: "Ne dersiniz, eğer bu Kur\'an Allah tarafından ise ve siz de onu inkâr etmişseniz, bununla birlikte İsrailoğulları\'ndan bir şahit de onun bir benzerini (Tevrat\'ta görüp) inanmışken siz hala büyüklük taslarsanız (haksızlık etmiş olmaz mısınız)? Şüphesiz ki, Allah zalim bir topluluğu doğru yola iletmez."'),
(4521, 46, 11, 'İnkâr edenler, iman ednler için: "Eğer İslâm\'da bir hayır olsaydı onlar, onu kabulde bizi geçemezlerdi." derler. Bununla muvaffak olamayınca da: "Bu eski bir yalandır." diyeceklerdir.'),
(4522, 46, 12, 'Kur\'ân\'dan önce de bir rehber ve rahmet olarak Musa\'nın kitabı Tevrat vardı. Bu Kur\'ân ise zulmedenleri uyarmak, iyilik yapanları müjdelemek için Arap lisanı ile indirilen ve kendinden öncekileri tasdik eden bir kitaptır.'),
(4523, 46, 13, '"Gerçekten Rabbimiz Allah\'tır." deyip, sonra da dosdoğru olanlara gelince onlar için hiçbir korku yoktur ve onlar üzülmeyeceklerdir.'),
(4524, 46, 14, 'İşte onlar cennetlikdirler, yaptıklarına karşılık orada ebedi olarak kalacaklardır.'),
(4525, 46, 15, 'Biz insana ana ve babasına iyilik yapmayı tavsiye ettik. Anası onu zahmetle karnında taşıdı ve zahmetle doğurdu. Onun ana karnında taşınması ile sütten kesilme süresi otuz aydır. Nihayet insan olgunluk çağına ulaşıp, kırk yaşına geldiğinde der ki: "Ey Rabbim! Bana ve ana babama ihsan ettiğin nimetlerine şükretmemi ve senin hoşnut olacağın salih amel işlememi ilham et. Benim neslimden gelenleri de salih kimseler kıl. Doğrusu ben tevbe edip sana yöneldim. Ve ben gerçekten müslümanlardanım."'),
(4526, 46, 16, 'İşte yaptıklarının en güzelini kendilerinden kabul edeceğimiz ve günahlarını bağışlayacağımız bu kimseler cennetlikler arasındadırlar. Bu onlara vaad edilmiş olan dosdoğru bir sözdür.'),
(4527, 46, 17, 'Ana ve babasına: "Öf size! siz bana öldükten sonra tekrar dirilip kabrimden çıkarılacağımı mı vaad ediyorsunuz? Oysa benden önce nice nesiller gelip geçmiştir." diyen kimseye ana ve babası Allah\'a sığınarak "Yazıklar olsun sana! Gel iman et, şüphesiz ki, Allah\'ın vaadi gerçektir." dediklerinde o: "Bu Kur\'ân öncekilerin masallarından başka bir şey değildir" diyordu.'),
(4528, 46, 18, 'İşte onlar kendilerinden önce gelip geçmiş olan cin ve insan toplulukları içerisinde haklarında azab vaadi hak olmuş kimselerdir. Onlar gerçekten hüsrana uğramışlardır.'),
(4529, 46, 19, 'Herkesin yaptıklarına göre dereceleri vardır. Allah onlara yaptıklarının karşılığını tam olarak verir. Onlara haksızlık edilmez.'),
(4530, 46, 20, 'İnkâr edenler ateşe arzedilecekleri gün onlara: "Siz dünya hayatınızda bütün güzel şeylerinizi harcadınız, onların zevkini sürdünüz, artık bugün yeryüzünde haksız yere büyüklük taslamanız ve yoldan çıkmış olmanızdan dolayı aşağılayıcı bir azabla cezalandırılacaksınız." (denir).'),
(4531, 46, 21, 'Ey Muhammed! Âd kavminin kardeşi Hud\'u hatırla. Hani O, Ahkâf denilen yerde kavmini uyarmıştı. O\'ndan önce ve sonra da nice peygamberler gelip geçmiştir. Hud, kavmine: "Allah\'tan başkasına kulluk etmeyin. Çünkü ben sizin için büyük bir günün azabından korkuyorum." demişti.'),
(4532, 46, 22, 'Onlar: "Sen bizi ilâhlarımızdan çevirmek için mi geldin? Eğer doğru söyleyenlerden isen o bize vaad edip durduğun azabı haydi getir." dediler.'),
(4533, 46, 23, 'Hud: "O azabın ne zaman geleceğine dair ilim Allah katındadır. Ben size benimle gönderileni tebliğ ediyorum. Fakat ben sizi cahillik eden bir kavim olarak görüyorum." dedi.'),
(4534, 46, 24, 'O azabı, vadilerine doğru yayılan bir bulut halinde gördükleri zaman: "Bu bize yağmur yağdıracak yaygın bir buluttur." dediler. Hud ise: "O sizin acele gelmesini istediğiniz şeydir. O bir rüzgârdır ki, içerisinde acı bir azab vardır.'),
(4535, 46, 25, 'O rüzgâr, Rabbinin emri ile herşeyi yıkar mahveder." dedi. Nihayet helâk oldular ve evlerinden başka hiçbir şey görünmez oldu. İşte biz günahkâr kavmi böyle cezalandırırız.'),
(4536, 46, 26, 'And olsun ki, biz onlara size vermediğimiz imkanlar vermiştik. Onlara kulaklar, gözler ve kalpler vermiştik. Fakat kulakları, gözleri ve kalpleri onlara hiçbir fayda sağlamadı. Çünkü onlar Allah\'ın âyetlerini bile bile inkâr ediyorlardı. Alay etmekte oldukları şey de onları sarıp kuşattı.'),
(4537, 46, 27, 'Andolsun ki, biz sizin etrafınızda bulunan bir çok memleketleri helak ettik. Belki tevhide dönerler diye ayetlerimizi çeşitli şekillerde açıkladık.'),
(4538, 46, 28, 'Allah\'ı bırakıp da kendilerine yakınlık sağlamak için edindikleri ilâhları onlara yardım etselerdi ya! Ama hayır, aksine onlardan kaybolup gittiler. İşte bu onların yalanları ve uydurup durdukları iftiralarıdır.'),
(4539, 46, 29, 'Ey Muhammed! Hani biz cinlerden bir grubu Kur\'ân\'ı dinlemeleri için sana yöneltmiştik. Onlar Kur\'ân\'ı dinlemek için hazır bulundukları zaman birbirlerine "susun" dediler. Kur\'ân\'ın okunması bitince de birer uyarıcı olarak kavimlerine döndüler.'),
(4540, 46, 30, 'Onlar kavimlerine şöyle dediler: "Ey kavmimiz! Gerçekten biz Musa\'dan sonra indirilen ve kendisinden öncekileri tasdik eden bir kitap dinledik. O kitap gerçeği ve doğru yolu gösteriyor.'),
(4541, 46, 31, 'Ey kavmimiz! Allah\'ın davetçisine uyun ve O\'na iman edin ki, Allah da sizin günahlarınızı bağışlasın ve sizi acı bir azabdan korusun."'),
(4542, 46, 32, 'Her kim Allah\'ın davetçisine uymazsa bilsin ki, yeryüzünde Allah\'ı aciz bırakacak değildir. Onun Allah\'tan başka dostları da yoktur. İşte onlar apaçık bir sapıklık içerisindedirler.'),
(4543, 46, 33, 'Onlar gökleri ve yeri yaratan ve onları yaratmakla yorulmayan Allah\'ın ölüleri diriltmeye de kadir olduğunu görmüyorlar mı? Evet şüphesiz ki, O\'nun herşeye gücü yeter.'),
(4544, 46, 34, 'İnkâr edenler ateşe arz olunacakları gün onlara: "Bu gerçek değil miymiş?" denir. Onlar da: "Rabbimiz Hakk\'ı için gerçekmiş!" derler. Allah onlara: "O halde inkâr ettiğinizden dolayı şimdi tadın azabı!" der.'),
(4545, 46, 35, 'Ey Muhammed! Azim sahibi peygamberlerin sabrettikleri gibi sen de sabret! Onlar için (azab hususunda) acele etme. Sanki onlar kendilerine vaad edilen azabı gördükleri gün dünyada sadece gündüzün bir saati kadar kaldıklarını sanırlar. Bu bir tebliğdir. Hiç yoldan çıkan fasıklar topluluğundan başkası helak edilir mi?');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'İnkâr edenlerin ve Allah yolundan alıkoyanların amellerini Allah boşa çıkarır.'),
(4547, 47, 2, 'İman edip salih amel işleyenlerin ve Rableri tarafından bir gerçek olarak Muhammed\'e indirilen kitaba inananların kötülüklerini Allah örter ve durumlarını düzeltir.'),
(4548, 47, 3, 'Bu, inkâr edenlerin batıla uymaları ve iman edenlerin de Rablerinden gelen gerçeğe tâbi olmalarından dolayı böyledir. İşte böylece Allah insanlara kendi misallerini anlatır.'),
(4549, 47, 4, 'Savaşta inkâr edenlerle karşılaştığınız zaman hemen boyunlarını vurun. Nihayet onlara üstün geldiğiniz zaman bağı sıkı bağlayıp esir alın. Sonra harp ağırlıklarını atıp, savaş bitince de onları ya karşılıksız olarak, ya da fidye ile salıverin. Allah\'ın emri budur. Eğer Allah dileseydi onlardan başka türlü de intikam alırdı. Fakat böyle olması sizi birbirinizle denemek içindir. Allah yolunda öldürülenlere gelince, Allah onların amellerini asla boşa çıkarmaz.'),
(4550, 47, 5, 'Allah onları doğru yola iletecek ve durumlarını düzeltecektir.'),
(4551, 47, 6, 'Onları, kendilerine tanıttığı cennete koyacaktır.'),
(4552, 47, 7, 'Ey iman edenler! Eğer siz Allah\'ın dinine yardım ederseniz Allah da size yardım eder ve ayaklarınızı sabit tutar.'),
(4553, 47, 8, 'İnkâr edenlere gelince, artık yıkım onlara. Allah onların amellerini boşa çıkarmıştır.'),
(4554, 47, 9, 'Bu onların, Allah\'ın indirdiklerini beğenmediklerinden dolayıdır. Allah da bunun için onların amellerini boşa çıkarmıştır.'),
(4555, 47, 10, 'Onlar yeryüzünde bir gezmediler mi? Baksalar ya kendilerinden öncekilerin sonları nasıl olmuş? Allah onların üzerlerine helak yağdırmıştır. Bu kâfirlere de onların başına gelenlerin benzerleri yaraşır.'),
(4556, 47, 11, 'Bu böyledir. Çünkü Allah iman edenlerin yardımcısıdır. İnkâr edenlerin ise yardımcısı yoktur.'),
(4557, 47, 12, 'Şüphesiz ki, Allah iman edip salih amel işleyenleri, altlarından ırmaklar akan cennetlere koyar. İnkâr edenler ise dünyada zevk edip geçinirler. Hayvanların yediği gibi yerler. Onların varacakları yer ateştir.'),
(4558, 47, 13, 'Ey Muhammed! Seni yurdundan çıkaran şehirden daha kuvvetli olan nice şehirler vardı ki biz onları helâk ettik de onlara yardım eden çıkmadı.'),
(4559, 47, 14, 'Rabbi tarafından apaçık bir delil üzerinde bulunan kimse, kötü işleri kendisine güzel gösterilmiş de heveslerinin peşine düşmüş kimseler gibi olur mu?'),
(4560, 47, 15, 'Kötülükten sakınanlara vaad edilen cennetin durumu şöyledir: Orada bozulmayan temiz sudan ırmaklar, tadı değişmeyen sütten ırmaklar, içenlere lezzet veren şaraptan ırmaklar ve süzme baldan ırmaklar vardır. Onlar için cennette her çeşit meyve ve Rablerinden bir bağışlanma vardır. Bunların durumu, ateşte ebedî olarak kalacak olan ve bağırsaklarını parçalayacak kaynar su içirilen kimsenin durumu gibi olur mu?'),
(4561, 47, 16, 'Ey Muhammed! Onlardan seni dinlemeye gelenler de var. Senin yanından çıktıkları zaman kendilerine ilim verilen kimselere alay yoluyla: "O demin ne söyledi?" diye sorarlar. İşte onlar Allah\'ın kalplerini mühürlediği kimselerdir. Onlar sadece kendi heva ve heveslerine uyarlar.'),
(4562, 47, 17, 'Doğru yola girenlere gelince, Allah onların hidayetlerini artırmış ve onlara kötülükten sakınma çarelerini ilham etmiştir.'),
(4563, 47, 18, 'Artık onlar, kıyamet saatinin kendilerine ansızın gelivermesine mi bakıyorlar? Şüphesiz onun alametleri gelmiştir. Artık kıyamet kendilerine gelip çatınca anlamaları neye yarar?'),
(4564, 47, 19, 'Ey Muhammed! Bil ki, Allah\'tan başka hiçbir ilâh yoktur. Hem kendi günahın için, hem de mümin erkekler ve mümin kadınlar için Allah\'tan bağışlanma dile. Allah, sizin gezip dolaştığınız yeri de duracağınız yeri de bilir.'),
(4565, 47, 20, 'İman edenler: "Keşke cihad hakkında bir sûre indirilse." derlerdi. Ama hükmü açık bir sûre indirilip de, içerisinde savaş zikredilince kalplerinde hastalık olanların ölüm korkusuyla baygınlık geçiren bir kimsenin bakışı gibi sana baktığını görürsün. Oysa onlar için ölüm yaşamaktan daha uygundur.'),
(4566, 47, 21, 'Onların vazifesi itaat ve güzel söz söylemekti. Sonra iş kesinleşince Allah\'ın emrine sadakat gösterselerdi, elbette kendileri için daha hayırlı olurdu.'),
(4567, 47, 22, 'Demek siz iş başına gelecek olursanız yeryüzünde bozgunculuk çıkaracaksınız ve akrabalık bağlarınızı koparacaksınız öyle mi?'),
(4568, 47, 23, 'İşte onlar, Allah\'ın lanetlediği, kulaklarını sağır, gözlerini kör ettiği kimselerdir.'),
(4569, 47, 24, 'Onlar Kur\'an\'ı düşünmüyorlar mı? Yoksa kalplerinin üzerinde kilitleri mi var?'),
(4570, 47, 25, 'Gerçekten doğru yol kendilerine açıkça belli olduktan sonra gerisin geri küfre dönenlere şeytan, kötülüklerini güzel göstermiş ve onları uzun emellere düşürmüştür.'),
(4571, 47, 26, 'Çünkü onlar Allah\'ın indirdiğini beğenmeyen kimselere: "Bazı işlerde biz size itaat edeceğiz." demişlerdi. Oysa Allah onların gizlediklerini biliyordu.'),
(4572, 47, 27, 'Melekler onların yüzlerine ve arkalarına vurarak canlarını alırken durumları nasıl olacak?'),
(4573, 47, 28, 'Bu onların Allah\'ı gazablandıran şeylere uymaları ve O\'nun rızasına sebep olacak şeyleri beğenmemelerinden dolayıdır. Allah onların amellerini boşa çıkarmıştır.'),
(4574, 47, 29, 'Yoksa kalplerinde hastalık olanlar Allah kendilerinin kinlerini hiç ortaya çıkarmaz mı sandılar?'),
(4575, 47, 30, 'Ey Muhammed! Eğer biz dileseydik onları sana gösterirdik. Sen de onları yüzlerinden tanırdın. Andolsun ki, sen onları sözlerinin üslubundan da tanırsın. Allah ise bütün yaptıklarınızı bilir.'),
(4576, 47, 31, 'Andolsun ki, biz içinizden cihad edenlerle sabredenleri ortaya çıkarıncaya ve yaptıklarınızla ilgili haberlerinizi açıklayıncaya kadar sizi deneyeceğiz.'),
(4577, 47, 32, 'Şüphesiz ki, inkâr edenler, Allah yolundan menedenler ve kendilerine doğru yol açıkça belli olduktan sonra Peygamber\'e karşı gelenler Allah\'a hiçbir zarar veremeyeceklerdir. Allah onların yaptıklarını boşa çıkaracaktır.'),
(4578, 47, 33, 'Ey iman edenler! Allah\'a itaat edin, Peygamber\'e itaat edin ve amellerinizi boşa çıkarmayın.'),
(4579, 47, 34, 'Şüphesiz ki, inkâr edip, Allah yolundan saptıran, sonra da kâfir olarak ölenlere gelince Allah onları asla bağışlamayacaktır.'),
(4580, 47, 35, 'Sakın gevşemeyin ve üstün olduğunuz halde barışa çağırmayın. Allah sizinle beraberdir. O sizin amellerinizi eksiltmeyecektir.'),
(4581, 47, 36, 'Dünya hayatı ancak bir oyun ve eğlenceden ibarettir. Eğer iman eder kötülükten sakınırsanız, Allah size mükâfatınızı verir. Ve sizden bütün mallarınızı harcamanızı da istemez.'),
(4582, 47, 37, 'Eğer sizden onların tamamını isteyip de sizi zorlasaydı cimrilik ederdiniz. Bu da sizin bütün kinlerinizi ortaya çıkarırdı.'),
(4583, 47, 38, 'İşte sizler Allah yolunda harcamaya çağrılan kimselersiniz. İçinizden kiminiz cimrilik ediyor. Ama cimrilik eden ancak kendi zararına cimrilik eder. Allah zengindir, siz ise fakirsiniz. Eğer siz Hakk\'tan yüz çevirirseniz Allah yerinize başka bir kavim getirir. Sonra onlar sizin gibi olmazlar.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Doğrusu biz sana apaçık bir fetih ihsân ettik.'),
(4585, 48, 2, 'Böylece Allah senin geçmiş ve gelecek günahını bağışlar. Sana olan nimetini tamamlar ve seni doğru yola iletir.'),
(4586, 48, 3, 'Ve sana Allah, şanlı bir zaferle yardım eder.'),
(4587, 48, 4, 'İmanlarına iman katsınlar diye müminlerin kalplerine güven indiren O\'dur. Göklerin ve yerin orduları Allah\'ındır. Allah bilendir, herşeyi hikmetle yapandır.'),
(4588, 48, 5, 'Mümin erkeklerle mümin kadınları, içinde ebedi kalacakları, altlarından ırmaklar akan cennetlere koyması, onların günahlarını örtmesi içindir. İşte bu, Allah katında büyük bir kurtuluştur.'),
(4589, 48, 6, 'Ve o Allah hakkında kötü zanda bulunan münâfık erkeklere ve münâfık kadınlara, Allah\'a ortak koşan erkeklere ve ortak koşan kadınlara azap etmesi içindir. Kötülük onların başlarına gelmiştir. Allah onlara gazap etmiş, lânetlemiş ve cehennemi kendilerine hazırlamıştır. Orası ne kötü bir yerdir!'),
(4590, 48, 7, 'Göklerin ve yerin orduları Allah\'ındır. Allah çok güçlüdür, hüküm ve hikmet sahibidir.'),
(4591, 48, 8, 'Şüphesiz biz seni, şâhit, müjdeleyici ve uyarıcı olarak gönderdik.'),
(4592, 48, 9, 'Ki, Allah\'a ve Resulüne iman edesiniz, ve bunu takviye edip, O\'na saygı gösteresiniz ve sabah akşam O\'nu tesbih edesiniz.'),
(4593, 48, 10, 'Herhalde sana bey\'at edenler ancak Allah\'a bey\'at etmektedirler. Allah\'ın eli onların ellerinin üzerindedir. Kim ahdi bozarsa ancak kendi aleyhine bozmuş olur. Kim de Allah\'a verdiği ahde vefa gösterirse Allah ona büyük bir mükâfat verecektir.'),
(4594, 48, 11, 'yakında a\'râbilerden geri kalmış olanlar sana diyecekler ki, "Mallarımız ve ailelerimiz bizi alıkoydu. Allah\'tan bizim bağışlanmamızı dile." Onlar kalplerinde olmayanı dilleriyle söylerler. De ki: Allah size bir zarar gelmesini dilerse veya bir fayda elde etmenizi isterse O\'na karşı kimin bir şeye gücü yetebilir? Hayır! Allah yaptıklarınızdan haberdardır.'),
(4595, 48, 12, 'Aslında siz Peygamber ve müminlerin, ailelerine geri dönmeyeceklerini sanmıştınız. Bu sizin gönüllerinize güzel göründü de kötü zanda bulundunuz ve helâki hak etmiş bir topluluk oldunuz.'),
(4596, 48, 13, 'Kim Allah\'a ve Rasulüne iman etmezse şüphesiz biz, kâfirler için çılgın bir ateş hazırlamışızdır.'),
(4597, 48, 14, 'Göklerin ve yerin mülkü Allah\'ındır. O, dilediğini bağışlar dilediğini azaplandırır. Allah çok bağışlayan çok merhamet edendir.'),
(4598, 48, 15, 'Siz ganimetleri almak için gittiğinizde geri kalanlar: "Bırakın biz de arkanıza düşelim." diyeceklerdir. Onlar, Allah\'ın sözünü değiştirmek isterler. De ki: Siz bizimle gelemeyeceksiniz. Allah daha önce böyle buyurmuştur. Onlar size: "Bizi kıskanıyorsunuz." diyeceklerdir. Bilakis onlar, pek az anlayan kimselerdir.'),
(4599, 48, 16, 'A\'rabilerin geri bırakılmış olanlarına de ki: Siz yakında çok kuvvetli bir kavme karşı savaşmaya çağırılacaksınız. Onlarla savaşırsınız veya müslüman olurlar. Eğer itaat ederseniz, Allah size güzel bir mükâfat verir. Ama önceden döndüğünüz gibi yine dönecek olursanız sizi acıklı bir azaba uğratır.'),
(4600, 48, 17, 'Köre vebal yoktur, topala da vebal yoktur, hastaya da vebal yoktur. Bununla beraber kim Allah\'a ve peygamberine itâat ederse, Allah onu, altından ırmaklar akan cennetlere sokar. Kim de geri kalırsa, onu acı bir azaba uğratır.'),
(4601, 48, 18, 'Andolsun o ağacın altında (Hudeybiye\'de) sana bey\'at ederlerken Allah, müminlerden razı olmuştur. Kalplerinde olanı bilmiş onlara güven indirmiş ve onları pek yakın bir fetih ile mükâfatlandırmıştır.'),
(4602, 48, 19, 'Allah onları elde edecekleri birçok ganimetlerle de mükâfatlandırdı. Allah çok güçlüdür, hüküm ve hikmet sahibidir.'),
(4603, 48, 20, 'Allah size, elde edeceğiniz birçok ganimetler vaad etmiştir. Bunu size hemen vermiş ve insanların ellerini sizden çekmiştir ki bu, müminlere bir işaret olsun ve Allah sizi doğru yola iletsin.'),
(4604, 48, 21, 'Bundan başka sizin güç yetiremediğiniz, ama Allah\'ın sizin için kuşattığı ganimetler de vardır. Allah herşeye kâdirdir.'),
(4605, 48, 22, 'Eğer kâfirler sizinle savaşsalardı arkalarına dönüp kaçarlardı. Sonra bir dost ve yardımcı da bulamazlardı.'),
(4606, 48, 23, 'Allah\'ın öteden beri gelen kanunu budur. Allah\'ın kanununda asla bir değişiklik bulamazsın.'),
(4607, 48, 24, 'O sizi onlara karşı muzaffer kıldıktan sonra Mekke\'nin göbeğinde onların ellerini sizden, sizin ellerinizi de onlardan çekendir. Allah, yaptıklarınızı görendir.'),
(4608, 48, 25, 'Onlar inkâr eden ve sizin Mescidi Haram\'ı ziyaretinizi ve bekletilen kurbanların yerlerine ulaşmasını men edenlerdir. Eğer kendilerini henüz tanımadığınız mümin erkeklerle, mümin kadınları bilmeyerek ezmek suretiyle bir vebalin altında kalmanız ihtimali olmasaydı, Allah savaşı önlemezdi. Dilediklerine rahmet etmek için Allah böyle yapmıştır. Eğer onlar birbirinden ayrılmış olsalardı elbette onlardan inkâr edenleri elemli bir azaba çarptırırdık.'),
(4609, 48, 26, 'O zaman inkâr edenler, kalplerine taassubu, câhiliyet taassubunu yerleştirmişlerdi. Allah da elçisine ve müminlere sükûnet ve güvenini indirdi. Onları takva sözü üzerinde durdurdu. Zaten onlar buna pek layık ve ehil kimselerdi. Allah herşeyi bilendir.'),
(4610, 48, 27, 'Andolsun ki Allah, elçisinin rüyasını doğru çıkardı. Allah dilerse siz güven içinde başlarınızı tıraş etmiş ve saçlarınızı kısaltmış olarak, korkmadan Mescidi Haram\'a gireceksiniz. Allah sizin bilmediğinzi bilir. İşte bundan önce size yakın bir fetih verdi.'),
(4611, 48, 28, 'Bütün dinlerden üstün kılmak üzere, Peygamberini hidayet ve hak din ile gönderen O\'dur. Şahit olarak Allah yeter.'),
(4612, 48, 29, 'Muhammed Allah\'ın elçisidir. Onun yanında bulunanlar da kâfirlere karşı çetin, kendi aralarında merhametlidirler. Onları rükûa varırken secde ederken görürsün. Allah\'tan lütuf ve rıza isterler. Yüzlerinde secdelerin izinden nişanları vardır. Bu, onların Tevrat\'taki vasıflarıdır. İncil\'deki vasıfları da şöyledir: Onlar filizini yarıp çıkarmış, gittikçe onu kuvvetlendirerek kalınlaşmış, gövdesi üzerine dikilmiş bir ekine benzerler ki bu, ziraatçıların da hoşuna gider. Allah böylece onları çoğaltıp kuvvetlendirmekle kâfirleri öfkelendirir. Allah inanıp iyi işler yapanlara mağfiret ve büyük bir mükâfat vaad etmiştir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Ey iman edenler! Allah\'ın ve Resulünün huzurunda öne geçmeyin. Allah\'tan korkun. Şüphesiz Allah işitendir, bilendir.'),
(4614, 49, 2, 'Ey iman edenler! Seslerinizi Peygamber\'in sesinden fazla yükseltmeyin. Birbirinize bağırdığınız gibi, Peygamber\'e yüksek sesle bağırmayın. Öyle yaparsanız, siz farkına varmadan amelleriniz boşa gider.'),
(4615, 49, 3, 'Allah\'ın elçisinin huzurunda seslerini kısanlar, şüphesiz Allah\'ın kalplerini takva ile imtihan ettiği kimselerdir. Onlara mağfiret ve büyük bir mükâfat vardır.'),
(4616, 49, 4, '(Resülüm!) Sana odaların arkasından bağıranların çokları, aklı ermez kimselerdir.'),
(4617, 49, 5, 'Eğer onlar, sen yanlarına çıkıncaya kadar sabretselerdi, elbette kendileri için daha iyi olurdu. Bununla beraber Allah çok bağışlayan, çok merhamet edendir.'),
(4618, 49, 6, 'Ey iman edenler! Eğer fasıkın biri size bir haber getirirsen onun doğruluğunu araştırın. Yoksa bilmeden bir topluluğa sataşırsınız da sonra yaptığınızdan pişman olursunuz.'),
(4619, 49, 7, 'Hem bilin ki, içinizde Allah\'ın elçisi vardır. Şayet o, birçok işlerde size uysaydı, sıkıntıya düşerdiniz. Fakat Allah size imanı sevdirmiş ve onu kalplerinize zinet yapmıştır. Küfrü, fasıklığı ve isyanı da size çirkin göstermiştir. İşte doğru yolda olanlar bunlardır.'),
(4620, 49, 8, 'Bu, Allah\'tan bir lütuf ve nimettir. Allah herşeyi bilir, hüküm ve hikmet sahibidir.'),
(4621, 49, 9, 'Eğer müminlerden iki grup birbirleriyle vuruşurlarsa aralarını düzeltin. Şayet biri ötekine saldırırsa, Allah\'ın buyruğuna dönünceye kadar saldıran tarafla savaşın. Eğer dönerse aralarını adaletle düzeltin ve (her işte) adaletli davranın. Şüphesiz ki Allah, adil davrananları sever.'),
(4622, 49, 10, 'Müminler ancak kardeştirler. Öyleyse kardeşlerinizin arasını düzeltin ve Allah\'tan korkun ki rahmete eresiniz.'),
(4623, 49, 11, 'Ey iman edenler! Bir topluluk diğer bir toplulukla alay etmesin. Belki de onlar, kendilerinden daha iyidirler. Kadınlar da kadınları alaya almasınlar. Belki onlar kendilerinden daha iyidirler. Kendi kendinizi ayıplamayın, birbirinizi kötü lakaplarla çağırmayın. İmandan sora fâsıklık ne kötü bir isimdir! Kim de tevbe etmezse işte bu kimseler zalimlerdir.'),
(4624, 49, 12, 'Ey iman edenler! Zannın bir çoğundan kaçının. Çünkü zannın bir kısmı günahtır. Birbirinizin kusurunu araştırmayın. Biriniz diğerini arkasından çekiştirmesin. Biriniz, ölmüş kardeşinin etini yemekten hoşlanır mı? İşte bundan tiksindiniz. O halde Allah\'tan korkun. Şüphesiz Allah, tevbeyi çok kabul edendir, çok merhamet edendir.'),
(4625, 49, 13, 'Ey insanlar! Doğrusu biz sizi bir erkekle bir dişiden yarattık. Ve birbirinizle tanışmanız için sizi milletlere ve kabilelere ayırdık. Muhakkak ki Allah yanında en değerli ve en üstününüz O\'ndan en çok korkanınızdır. Şüphesiz Allah bilendir, herşeyden haberdar olandır.'),
(4626, 49, 14, 'Bedevîler "inandık" dediler. De ki: Siz iman etmediniz ama "İslâm olduk." deyin. Henüz iman kalplerinize yerleşmedi. Eğer Allah\'a ve Resulüne itaat ederseniz, Allah işlerinizden hiçbir şeyi eksiltmez. Çünkü Allah çok bağışlayan, çok merhamet edendir.'),
(4627, 49, 15, 'Gerçek müminler ancak Allah\'a ve Resulüne iman eden, ondan sonra asla şüpheye düşmeyen, Allah yolunda mallarıyla ve canlarıyla savaşanlardır. İşte doğrular ancak onlardır.'),
(4628, 49, 16, 'De ki: Siz dininizi Allah\'a mı öğretiyorsunuz? Oysa Allah göklerde olanları da bilir, yerde olanları da. Allah herşeyi hakkıyla bilendir.'),
(4629, 49, 17, 'Onlar İslâm\'a girdikleri için sana minnet ediyorlar. De ki: Müslümanlığınızı benim başıma kakmayın. Bilakis sizi imana erdirdiği için Allah sizin başınıza kakar. Eğer doğrulardan iseniz (Allah\'a minnettar olmanız gerekir.)'),
(4630, 49, 18, 'Şüphesiz Allah, göklerin ve yerin görülmeyen esrarını bilir. Allah yaptıklarınızı görür.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Kâf. Şanlı ve şerefli Kur\'an\'a andolsun ki,'),
(4632, 50, 2, 'Doğrusu kâfirler kendi içlerinden uyarıcı bir peygamber geldiğine şaşırdılar da dediler ki: "Bu şaşılacak bir şeydir!'),
(4633, 50, 3, 'Öldüğümüz ve bir toprak olduğumuz vakit mi (tekrar) dirileceğiz? bu dönüş çok uzaktır."'),
(4634, 50, 4, 'Fakat biz toprağın onlardan neyi eksilttiğini elbette biliyoruz. Yanımızda herşeyi kaydedip muhafaza eden bir kitap vardır.'),
(4635, 50, 5, 'Doğrusu hak kendilerine geldiği zaman yalanladılar da şimdi karmakarışık bir ıztırap içindeler.'),
(4636, 50, 6, 'Artık üstlerindeki göğe bakmazlar mı ki, onu nasıl bina etmiş ve süslemişiz, onun hiç bir çatlağı yoktur.'),
(4637, 50, 7, 'Yeri de nasıl uzatmış, üzerine sabit dağlar oturtmuşuz. Orada görünüşü güzel her çeşit bitkiden çiftler yetiştirdik.'),
(4638, 50, 8, 'Bunlar, Allah\'a yönelen her kula gönül gözünü açmak ve ona ibret vermek içindir.'),
(4639, 50, 9, 'Bir de gökten bereketli bir su indirip de onunla bağlar, bahçeler ve biçilecek taneler bitirmekteyiz.'),
(4640, 50, 10, 'Tomurcukları birbiri üzerine dizilmiş uzun boylu hurma ağaçları yetiştirdik.'),
(4641, 50, 11, 'Bunları kullara rızık olması için (yetiştirmekteyiz). O su ile ölü bir toprağa can verdik, işte hayata çıkış da böyledir.'),
(4642, 50, 12, 'Onlardan önce Nuh\'un kavmi, Ress halkı ve Semûd da yalanlamıştı.'),
(4643, 50, 13, 'Âd, Firavun, Lût\'un kardeşleri de (yalanladılar).'),
(4644, 50, 14, 'Eyke halkı ve Tübbâ kavmi de, bunların hepsi peygamberleri yalanladılar da (onlara) azabım hak oldu.'),
(4645, 50, 15, 'Biz ilk yaratmada acizlik mi gösterdik? Doğrusu, onlar yeni bir yaratılıştan şüphe içindedirler.'),
(4646, 50, 16, 'Andolsun insanı biz yarattık ve nefsinin kendisine fısıldadıklarını biliriz. Ve biz ona şah damarından daha yakınız.'),
(4647, 50, 17, 'Onun sağında ve solunda oturmuş iki melek zabıt tutarken,'),
(4648, 50, 18, 'İnsan hiçbir söz söylemez ki yanında (onu) gözetleyen, dediklerini zapteden bir melek hazır bulunmasın.'),
(4649, 50, 19, 'Ölüm sarhoşluğu gerçekten geldiğinde, "Ey insan! İşte bu senin öteden beri kaçtığın şeydir." denir.'),
(4650, 50, 20, 'Sur\'a üfürülür, işte bu, tehdid(in gerçekleşme) günüdür.'),
(4651, 50, 21, 'Her can, kendisiyle beraber bir sevk memuru ve bir şahid bulunduğu halde gelir.'),
(4652, 50, 22, '(Allah ona) "Andolsun sen bundan gaflet içinde idin. Şimdi senden gaflet perdesini kaldırdık. Bugün artık gözün keskindir." der.'),
(4653, 50, 23, 'Beraberindeki melek "işte yanımdaki hazır" der.'),
(4654, 50, 24, '(Allah iki meleğe buyurur ki:) "Haydi ikiniz, atın cehenneme her inatçı nankörü!'),
(4655, 50, 25, 'İyiliklere (sürekli) engel olan, saldırgan, şüpheciyi.'),
(4656, 50, 26, 'O ki Allah\'ın yanında başka ilâh edinmiştir. Haydi ikiniz birlikte onu şiddetli azaba atın."'),
(4657, 50, 27, 'Yanındaki arkadaşı (şeytan) der ki: "Rabbimiz! Ben onu azdırmadım. Fakat kendisi derin bir sapıklık içindeydi".'),
(4658, 50, 28, 'Allah buyurur ki: "Huzurumda çekişmeyin! Ben size daha önce uyarıcı göndermiştim."'),
(4659, 50, 29, 'Benim huzurumda söz değiştirilmez. Ve ben kullara asla zulmedici değilim.'),
(4660, 50, 30, 'Biz O gün cehenneme: "Doldun mu?" diyeceğiz. O da: "Daha fazla var mı?" diyecektir.'),
(4661, 50, 31, 'Cennet de kötülükten sakınanlara yaklaştırılır. Zaten uzak değildir.'),
(4662, 50, 32, 'Onlara denir ki: "İşte size vaad edilen bu cennet, Allah\'a yönelen, O\'nun emirlerine riayet eden, görmediği halde Rahman olan Allah\'tan korkan ve O\'na yönelen bir kalple gelenlere mahsustur.'),
(4663, 50, 33, 'Onlara denir ki: "İşte size vaad edilen bu cennet, Allah\'a yönelen, O\'nun emirlerine riayet eden, görmediği halde Rahman olan Allah\'tan korkan ve O\'na yönelen bir kalple gelenlere mahsustur.'),
(4664, 50, 34, '"Şimdi selam ve selametle oraya girin. İşte sonsuzluk günü budur."'),
(4665, 50, 35, 'Orada onlara ne isterlerse vardır. Katımızda daha fazlası da vardır.'),
(4666, 50, 36, 'Ey Muhammed! Biz onlardan önce kendilerinden daha kuvvetli olan ve beldeleri delik deşik eden nice nesilleri helak ettik, hiç kurtuluş var mı?'),
(4667, 50, 37, 'Şüphesiz ki bunda kalbi olan ve hazır bulunup kulak veren kimse için elbette bir öğüt vardır.'),
(4668, 50, 38, 'Andolsun ki biz gökleri, yeri ve ikisi arasındakileri altı günde yarattık, Bize hiçbir yorgunluk da dokunmadı.'),
(4669, 50, 39, 'Ey Muhammed! Onların söylediklerine karşı sabret. Güneşin doğuşundan önce (sabah namazını) ve batışından önce de (öğle ve ikindi namazalarını kılarak) Rabbini Hamd ile tesbih et.'),
(4670, 50, 40, 'Geceleyin (akşam ve yatsı namazlarını kılarak), namazlardan sonra da (vitir ve nafile kılarak) O\'nu tesbih et.'),
(4671, 50, 41, 'Bir münadinin yakın bir yerden sesleneceği güne kulak ver.'),
(4672, 50, 42, 'O gün insanlar, o çağrıyı gerçek olarak duyarlar. İşte bugün, kabirlerden çıkış günüdür.'),
(4673, 50, 43, 'Gerçekten biz hem yaşatırız, hem öldürürüz. Sonunda dönüş yalnız bizedir.'),
(4674, 50, 44, 'O gün yer yarılır, insanlar kabirlerinden çabucak çıkarlar. İşte bu, sadece bize göre kolay bir toplanmadır.'),
(4675, 50, 45, 'Biz onların söylediklerini daha iyi biliriz. Sen onlara karşı zor kullanacak değilsin. O halde sen, benim tehdidimden korkanlara bu Kur\'ân ile öğüt ver.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'O tozdurup savuranlara,'),
(4677, 51, 2, 'Derken bir ağırlık taşıyanlara,'),
(4678, 51, 3, 'Derken bir kolaylıkla akanlara,'),
(4679, 51, 4, 'Derken bir emir taksim edenlere andolsun ki,'),
(4680, 51, 5, 'O size vaad edilen elbette doğrudur.'),
(4681, 51, 6, 'Ceza ve hesap günü şüphesiz olacaktır.'),
(4682, 51, 7, 'Yollara sahip göğe andolsun ki,'),
(4683, 51, 8, 'Siz elbette çelişkili sözler içindesiniz.'),
(4684, 51, 9, 'Ondan çevrilen (imana) çevrilir.'),
(4685, 51, 10, 'Kahrolsun (o fikir adına) kendi tahminlerini ileri sürenler!'),
(4686, 51, 11, 'Onlar bir sarhoşluk ve cehalet içinde şuursuzdurlar.'),
(4687, 51, 12, 'Onlar: "Hesap ve ceza günü ne zaman?" diye soruyorlar.'),
(4688, 51, 13, 'O gün, onların ateş üzerinde azap görecekleri gündür.'),
(4689, 51, 14, 'Onlara: "Tadın inkarınızın cezasını, işte sizin acele istediğiniz budur!" denecektir.'),
(4690, 51, 15, 'Şüphesiz ki takva sahipleri Rablerinin kendilerine verdiği sevabı almış olarak cennet bahçelerinde ve pınar başlarında bulunacaklardır. Çünkü onlar bundan önce iyilik yapıyorlardı.'),
(4691, 51, 16, 'Şüphesiz ki takva sahipleri Rablerinin kendilerine verdiği sevabı almış olarak cennet bahçelerinde ve pınar başlarında bulunacaklardır. Çünkü onlar bundan önce iyilik yapıyorlardı.'),
(4692, 51, 17, 'Onlar geceleyin pek az uyurlardı.'),
(4693, 51, 18, 'Onlar seher vakitlerinde Allah\'tan bağışlanma dilerlerdi.'),
(4694, 51, 19, 'Onların mallarında isteyen ve istemeyen yoksullar için bir hak vardı.'),
(4695, 51, 20, 'Kesin olarak inananlar için, yeryüzünde ve kendi nefislerinde nice ibretler vardır. Hiç görmüyor musunuz?'),
(4696, 51, 21, 'Kesin olarak inananlar için, yeryüzünde ve kendi nefislerinde nice ibretler vardır. Hiç görmüyor musunuz?'),
(4697, 51, 22, 'Sizin rızkınız da size vaad edilen sevap ve ceza da göktedir.'),
(4698, 51, 23, 'Gök ve yerin Rabbine andolsun ki size edilen o vaad, herhalde haktır. O tıpkı sizin konuşmanız gibi gerçektir.'),
(4699, 51, 24, 'Ey Muhammed! İbrahim\'in şerefli misafirlerinin haberi sana geldi mi?'),
(4700, 51, 25, 'Hani onlar İbrahim\'in huzuruna girmişlerdi de "Selam sana!" demişlerdi. İbrahim: "Size de selam" demiş, ve içinden: "Bunlar tanınmamış bir topluluk!" diye geçirmişti.'),
(4701, 51, 26, 'İbrahim, sonra ailesine giderek semiz bir buzağı (eti) getirdi.'),
(4702, 51, 27, 'Onu önlerine sürerek: "Yemez misiniz?" dedi.'),
(4703, 51, 28, 'Yemediklerini görünce onlardan içine bir korku düştü. Onlar İbrahim\'e: "Korkma!" dediler ve onu çok bilgili bir oğul ile müjdelediler.'),
(4704, 51, 29, 'Bunun üzerine karısı (Sâre) bir çığlık atarak geldi ve elini yüzüne vurarak: "Ben kısır bir kocakarıyım, nasıl çocuğum olur?" dedi.'),
(4705, 51, 30, 'Misafir melekler: "Evet bu böyledir. Rabbin böyle buyurdu. Gerçekten O hüküm ve hikmet sahibidir. Herşeyi hakkıyla bilir." dediler.'),
(4706, 51, 31, 'İbrahim, kendisine misafir olarak gelen meleklere: "Acaba sizin asıl önemli işiniz nedir ey elçiler?" dedi.'),
(4707, 51, 32, 'Onlar: "Gerçekten biz günahkâr bir kavim (olan Lût kavmine) gönderildik.'),
(4708, 51, 33, 'Onların üzerine çamurdan pişirilmiş sert taşlar yağdıracağız.'),
(4709, 51, 34, 'O taşlardan herbirinin haddi aşanlardan kime isabet edeceği Rabbin katında işaretlenmiştir." dediler.'),
(4710, 51, 35, 'Nihayet biz müminlerden orada bulunan kimseleri çıkardık.'),
(4711, 51, 36, 'Fakat biz orada müslümanlardan bir ev halkından başka kimseyi de bulamadık.'),
(4712, 51, 37, 'Biz orada acı bir azabdan korkan kimseler için bir ibret nişanesi bıraktık.'),
(4713, 51, 38, 'Musa\'nın kıssasında da ibret vardır. Hani biz onu apaçık bir delille Firavun\'a göndermiştik.'),
(4714, 51, 39, 'Firavun ise ordusuyla birlikte yüz çevirmiş, onun hakkında: "Bu bir sihirbazdır, ya da bir delidir." demişti.'),
(4715, 51, 40, 'Nihayet biz onu ve ordularını yakalayıp hepsini denize attık. Firavun ise o sırada (inadından dolayı pişmanlık duyarak) kendi kendini kınıyordu.'),
(4716, 51, 41, 'Âd kavminin helâkinde de bir ibret vardır. Hani biz onların üzerine köklerini kesecek bir rüzgar göndermiştik.'),
(4717, 51, 42, 'O rüzgar üzerine uğradığı hiçbir şeyi bırakmıyor, mutlaka onu kül gibi dağıtıyordu.'),
(4718, 51, 43, 'Semud kavminin helâkinde de bir ibret vardır. Hani onlara: "Belirli bir süreye kadar dünyadan yararalanıp, geçinin!" denmişti.'),
(4719, 51, 44, 'Onlarsa Rablerinin emrine karşı büyüklük tasladılar. Bunun üzerine kendilerini, bakıp dururlarken yıldırım yakalayıp, çarptı.'),
(4720, 51, 45, 'Artık onlar, ne kendi kendilerine ayağa kalkabildiler, ne de yardım gördüler.'),
(4721, 51, 46, 'Daha önce de Nuh kavmini helâk etmiştik. Çünkü onlar yoldan çıkmış fâsık bir kavimdiler.'),
(4722, 51, 47, 'Biz göğü kudretimizle bina ettik. Hiç şüphesiz biz, çok genişlik ve kudret sahibiyiz.'),
(4723, 51, 48, 'Yeryüzünü de biz döşedik. Bakın biz onu ne güzel döşüyoruz!'),
(4724, 51, 49, 'Biz herşeyden iki çift yarattık. Umulur ki, iyice düşünürsünüz.'),
(4725, 51, 50, 'Ey Muhammed! de ki: "Öyleyse Allah\'a koşun, gerçekten ben size O\'nun tarafından gönderilmiş apaçık bir uyarıcıyım.'),
(4726, 51, 51, 'Allah\'la beraber başka bir tanrı uydurmayın (O\'na ortak koşmayın). Gerçekten ben size O\'nun tarafından gönderilmiş apaçık bir uyarıcıyım."'),
(4727, 51, 52, 'Böylece onlardan öncekilere de herhangi bir peygamber gelince, onun hakkında da mutlaka: "Bir sihirbazdır veya bir delidir." dediler.'),
(4728, 51, 53, 'Onlar birbirlerine bunu mu tavsiye ettiler? Hayır onlar azgın bir kavimdir.'),
(4729, 51, 54, 'Ey Muhammed! Sen onlardan yüz çevir. Artık sen kınanacak değilsin.'),
(4730, 51, 55, 'Sen öğüt verip hatırlat. Çünkü, hatırlatmak müminlere fayda verir.'),
(4731, 51, 56, 'Ben cinleri ve insanları ancak bana ibadet etsinler diye yarattım.'),
(4732, 51, 57, 'Ben onlardan herhangi bir rızık istemiyorum. Beni yedirmelerini de istemiyorum.'),
(4733, 51, 58, 'Şüphesiz ki, rızık veren O sağlam kuvvet sahibi olan Allah\'tır.'),
(4734, 51, 59, 'Şüphsiz ki, zulmedenlerin geçmiş arkadaşlarının payı gibi, dolgun bir azab payı vardır. Ama şimdi onu acele istemesinler.'),
(4735, 51, 60, 'Kendilerine vaad edilen günlerinde uğrayacakaları azabdan dolayı vay inkâr edenlerin haline!.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Andolsun Tûr\'a,'),
(4737, 52, 2, 'Yayılmış ince deri üzerine, satır satır yazılmış kitaba,'),
(4738, 52, 3, 'Yayılmış ince deri üzerine, satır satır yazılmış kitaba,'),
(4739, 52, 4, 'Ma\'mur eve,'),
(4740, 52, 5, 'Yükseltilmiş tavana,'),
(4741, 52, 6, 'Kaynatılmış denize, (andolsun ki)'),
(4742, 52, 7, 'Rabbinin azabı mutlaka vuku bulacaktır.'),
(4743, 52, 8, 'Ona engel olacak (hiçbir şey de) yoktur.'),
(4744, 52, 9, 'O gün gök, bir çalkanış çalkalanır'),
(4745, 52, 10, 'Dağlar da bir yürüyüş yürür.'),
(4746, 52, 11, 'Vay haline o gün yalanlayanların!'),
(4747, 52, 12, 'Ki onlar, daldıkları bir batak (bâtıl)da oynayıp duruyorlar.'),
(4748, 52, 13, 'O gün onlar cehennem ateşine itilip kakılacaklar.'),
(4749, 52, 14, '(Onlara): "İşte yalanlayıp durduğunuz ateş budur" (denilecek).'),
(4750, 52, 15, '"Bu da mı bir sihir? Yoksa siz görmüyor musunuz?'),
(4751, 52, 16, 'Girin oraya, ister sabredin ister etmeyin artık sizin için birdir. Siz hep yaptıklarınıza göre cezalandırılacaksınız" (denilecek).'),
(4752, 52, 17, 'Şüphesiz (günahlardan) korunanlar da cennetlerde, nimetler içindedirler.'),
(4753, 52, 18, 'Rablerinin kendilerine verdiği ile zevk ü sefâ sürerler. Rableri onları, cehennem azabından korumuştur.'),
(4754, 52, 19, '(Onlara): "Yaptıklarınıza karşılık afiyetle yeyin, için" (denilir.)'),
(4755, 52, 20, 'Sıra sıra dizilmiş koltuklara yaslanırlar. Ayrıca biz onları ceylan gözlü hûrilerle evlendirdik.'),
(4756, 52, 21, 'İman edip zürriyetleri de iman ile kendilerine tâbi olanlar (yok mu?); işte biz, onların nesillerini de kendilerine kattık. Kendilerinin amellerinden birşey de eksiltmedik. Herkes kendi kazandığına bağlıdır.'),
(4757, 52, 22, 'Onlara canlarının istediği meyvalar ve etlerden bol bol verdik.'),
(4758, 52, 23, 'Orada bir kadeh kapışırlar ki, onda ne bir saçmalama vardır, ne de günaha sokma.'),
(4759, 52, 24, 'Kendilerine ait bir takım hizmetçiler de onların etrafında dönerler. Bu gençler sanki sedefleri içine gizlenmiş inci gibidirler.'),
(4760, 52, 25, 'Birbirlerine yönelip soruyorlar.'),
(4761, 52, 26, 'Ve diyorlar ki: "Gerçekte biz daha önce (dünya hayatında) âilemiz içinde (âkibetimizden) korkardık".'),
(4762, 52, 27, '"Allah bize lutfetti de bizi (vücûdun) içine işleyen (kavurucu) azabdan korudu."'),
(4763, 52, 28, '"Gerçekten biz bundan önce O\'na yalvarıyorduk. Çünkü iyilik eden, esirgeyen ancak O\'dur."'),
(4764, 52, 29, '(Ey Muhammed!) sen hatırlat, öğüt ver. Rabbinin nimeti sayesinde sen ne kâhinsin, ne de mecnûn.'),
(4765, 52, 30, 'Yoksa onlar (senin için): "Bir şâirdir, zamanın felaketlerine çarpılmasını gözetliyoruz." mu diyorlar?'),
(4766, 52, 31, 'De ki: Bekleyin, çünkü ben de sizinle beraber bekleyenlerdenim.'),
(4767, 52, 32, 'Onların akılları mı bunu emreder yoksa onlar azgın bir topluluk mudur?'),
(4768, 52, 33, 'Yoksa "Onu uydurdu" mu diyorlar? Hayır onlar inanmıyorlar.'),
(4769, 52, 34, 'Eğer doğru iseler onun benzeri bir söz meydana getirsinler.'),
(4770, 52, 35, 'Yoksa onlar, hiçbir şey olmadan (yani yaratıcısız) mı yaratıldılar? Yoksa kendileri yaratıcı mıdırlar?'),
(4771, 52, 36, 'Yoksa gökleri ve yeri onlar mı yarattılar? Hayır, onlar düşünüp hakikati anlamazlar.'),
(4772, 52, 37, 'Yoksa Rabbinin hazineleri onların yanında mıdır? Yahut hâkim (her şeyin yöneticisi) kendileri midir?'),
(4773, 52, 38, 'Yoksa kendilerine mahsus (üzerine çıkıp sırları) dinleyecekleri bir merdivenleri mi var? Öyleyse dinleyenleri, açık bir delil getirsin.'),
(4774, 52, 39, 'Demek kızlar O\'na, oğullar size öyle mi?'),
(4775, 52, 40, 'Yoksa sen kendilerinden bir ücret istiyorsun da, bu yüzden onlar ağır bir borç altında mı kalıyorlar?'),
(4776, 52, 41, 'Yoksa gayb kendilerinin yanında da onlar mı yazıyorlar?'),
(4777, 52, 42, 'Yoksa bir tuzak mı kurmak istiyorlar? Fakat o küfredenlerin kendileri tuzağa düşeceklerdir.'),
(4778, 52, 43, 'Yoksa onların Allah\'tan başka bir ilâhı mı var? Allah, onların ortak koştukları şeylerden uzaktır.'),
(4779, 52, 44, 'Gökten bir parçanın düştüğünü görseler, "Üst üste yığılmış bulutlardır." derler.'),
(4780, 52, 45, 'Artık çarpılacakları günlerine kavuşuncaya kadar onları (kendi hallerine) bırak.'),
(4781, 52, 46, 'O gün hiçbir tedbirlerinin kendilerine zerre kadar faydası olmayacak ve hiçbir şekilde yardım da görmeyeceklerdir.'),
(4782, 52, 47, 'Şüphesiz o zulmedenlere ondan başka da azab vardır. Fakat çokları bilmezler.'),
(4783, 52, 48, 'Rabbinin hükmüne sabret. Çünkü sen gözlerimizin önündesin. Kalktığın zaman Rabbini hamd ile tesbih et.'),
(4784, 52, 49, 'Gecenin bir kısmında ve yıldızların batışında da O\'nu tesbih et');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'İnmekte olan yıldıza andolsun ki,'),
(4786, 53, 2, 'Arkadaşınız (Muhammed) sapmadı, azmadı.'),
(4787, 53, 3, 'O, hevâdan (arzularına göre) konuşmaz.'),
(4788, 53, 4, 'O(nun konuşması kendisine ) vahyedilenden başkası değildir.'),
(4789, 53, 5, 'Onu, müthiş kuvvetleri olan biri öğretti'),
(4790, 53, 6, '(Ki o) akıl ve görüşünde kuvvetli (bir melek)dir. Hemen (gerçek meleklik şekliyle) doğruldu.'),
(4791, 53, 7, 'O, en yüksek ufukta idi.'),
(4792, 53, 8, 'Sonra (Cebrail ona) yaklaştı ve (aşağıya doğru) sarktı.'),
(4793, 53, 9, 'Onunla arasındaki mesafe, iki yay kadar, yahut daha az kaldı.'),
(4794, 53, 10, '(Allah), kuluna verdiği vahyi verdi.'),
(4795, 53, 11, 'Onun gördüğünü kalb(i) yalanlamadı.'),
(4796, 53, 12, 'Onun gördükleri hakkında şimdi kendisi ile tartışacak mısınız.'),
(4797, 53, 13, 'Andolsun onu bir kez daha görmüştü.'),
(4798, 53, 14, 'Sidretü\'lMüntehâ\'nın yanında.'),
(4799, 53, 15, 'Ki Cennetü\'lMe\'vâ onun yanındadır.'),
(4800, 53, 16, 'Sidre\'yi kaplayan kaplıyordu.'),
(4801, 53, 17, '(Peygamberin) gözü şaşmadı ve sınırı aşmadı.'),
(4802, 53, 18, 'Andolsun ki o, Rabbinin âyetlerinden en büyüğünü gördü.'),
(4803, 53, 19, 'Siz de gördünüz değil mi o Lât ve Uzza\'yı?'),
(4804, 53, 20, 'Ve üçüncü olarak da öteki (put) Menat\'ı?'),
(4805, 53, 21, 'Size erkek O\'na dişi öyle mi?'),
(4806, 53, 22, 'Öyle ise bu çok insafsızca bir taksim.'),
(4807, 53, 23, 'Onlar hiçbir şey değil, sırf sizin ve babalarınızın taktığınız (boş) isimlerdir. Allah onlar hakkında hiçbir delil indirmedi. Onlar yalnız zanna ve nefislerin sevdasına uyuyorlar. Halbuki onlara Rableri tarafından yol gösterici gelmiştir.'),
(4808, 53, 24, 'Yoksa her arzu ettiği şey, insanın kendisinin mi (olacak) dir?'),
(4809, 53, 25, 'Son da ilk de (ahiret de dünya da) Allah\'ındır.'),
(4810, 53, 26, 'Göklerde nice melek var ki Allah\'ın dileyip razı olduğuna izin vermeden önce onların şefaatları hiç bir işe yaramaz.'),
(4811, 53, 27, 'Ahirete iman etmeyenler meleklere dişilerin adlarını takıp duruyorlar'),
(4812, 53, 28, 'Onların bu hususta bir bilgileri yoktur. Sadece zanna uyuyorlar. Zan ise, şüphesiz hakikat bakımından birşey ifade etmez.'),
(4813, 53, 29, 'Onun için bizi anmaktan yüz çeviren ve dünya hayatından başka bir şey istemeyenlerden yüz çevir.'),
(4814, 53, 30, 'İşte onların ilimden erişebilecekleri (son sınır) budur. Şüphesiz, Rabbin, yolundan sapanı da iyi bilir; O, hidayette olanı da iyi bilir.'),
(4815, 53, 31, 'Göklerde ve yerde bulunanlar hep Allah\'ındır. Akıbet (sonuçta) kötülük yapanları yaptıkları ile cezalandıracak, güzel davrananları da daha güzeliyle mükafatlandıracaktır.'),
(4816, 53, 32, 'Onlar ki günahın büyüklerinden ve çirkin işlerden kaçınırlar, yalnız bazı küçük kusurlar hariç. Şüphesiz Rabbinin affı geniştir. O, sizi daha topraktan yarattığı zaman ve siz annelerinizin karınlarında bulunduğunuz sırada, sizi en iyi bilendir. Bunun için kendinizi temize çıkarmayın. Çünkü O, kötülükten sakınanı daha iyi bilir.'),
(4817, 53, 33, 'Şimdi gördün mü O yüz çevireni?'),
(4818, 53, 34, 'Azıcık verip (sonra vermemekte) direneni?'),
(4819, 53, 35, 'Gaybın bilgisi kendi yanındadır da, o mu görüyor?'),
(4820, 53, 36, 'Yoksa haber verilmedi mi Musa\'nın sahifelerinde yazılı olanlar?'),
(4821, 53, 37, 'Ve çok vefakâr olan İbrahim\'in sahifelerindekiler?'),
(4822, 53, 38, 'Ki hiçbir günahkâr başkasının günah yükünü yüklenmez.'),
(4823, 53, 39, 'Doğrusu insana çalışmasından başka bir şey yoktur.'),
(4824, 53, 40, 'Ve çalışması da yakında görülecektir.'),
(4825, 53, 41, 'Sonra ona karşılığı tastamam verilecektir.'),
(4826, 53, 42, 'Ve şüphesiz en son varış, Rabbinedir.'),
(4827, 53, 43, 'Doğrusu güldüren de ağlatan da O\'dur.'),
(4828, 53, 44, 'Öldüren de dirilten de O\'dur.'),
(4829, 53, 45, 'Şüphesiz erkeği, dişiyi iki eş yaratan O\'dur,'),
(4830, 53, 46, 'Atıldığı zaman bir nutfeden.'),
(4831, 53, 47, 'Şüphesiz tekrar diriltmek de O\'na aittir.'),
(4832, 53, 48, 'Şüphesiz zengin eden de sermaye veren de O\'dur.'),
(4833, 53, 49, 'Doğrusu Şi\'râ yıldızının Rabbi O\'dur.'),
(4834, 53, 50, 'O, helak etti önce gelen Âd\'ı.'),
(4835, 53, 51, 'Ve Semûd\'u da bırakmadı.'),
(4836, 53, 52, 'Önceden de Nuh kavmini (helak etmişti), çünkü onlar zulmetmiş ve azmıştı.'),
(4837, 53, 53, 'Altı üstüne getirilmiş şehirleri devirip yıktı.'),
(4838, 53, 54, 'Onları neler kapladı neler!'),
(4839, 53, 55, 'O halde Rabbinin hangi nimetinden kuşku duyuyorsun.'),
(4840, 53, 56, 'Bu da ilk uyarıcılardan bir uyarıcıdır.'),
(4841, 53, 57, 'Yaklaşan yaklaştı.'),
(4842, 53, 58, 'Onu Allah\'tan başka açığa çıkaracak yoktur.'),
(4843, 53, 59, 'Şimdi siz bu sözden mi hayret ediyorsunuz?'),
(4844, 53, 60, 'Gülüyorsunuz da ağlamıyorsunuz?'),
(4845, 53, 61, 'Ve siz mi kafa tutuyorsunuz ey gafiller?'),
(4846, 53, 62, 'Haydi Allah için secdeye kapanın ve O\'na kulluk edin.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Kıyamet saati yaklaştı, Ay yarıldı.'),
(4848, 54, 2, 'Bir mucize görseler hemen yüz çevirirler ve "süregelen bir büyüdür" derler.'),
(4849, 54, 3, 'Yalanladılar, nefislerinin arzularına uydular. Halbuki her iş yerini bulacaktır.'),
(4850, 54, 4, 'Andolsun ki onlara (kötülükten) vazgeçirecek nice önemli haberler gelmiştir.'),
(4851, 54, 5, 'Bunlar üstün bir hikmettir fakat uyarılar fayda vermiyor.'),
(4852, 54, 6, 'Sen de onlardan yüz çevir ki, o gün çağırıcı, görülmedik müthiş bir şeye çağırır.'),
(4853, 54, 7, 'Gözleri düşkün düşkün (zelil ve hakir) kabirlerinden çıkarlar, sanki yayılan çekirgeler gibidirler.'),
(4854, 54, 8, 'O çağırana koşarak, kâfirler: "Bu çetin bir gündür." derler.'),
(4855, 54, 9, 'Onlardan önce Nuh\'un kavmi de yalanlamıştı. Kulumuzu yalanladılar ve: "Cinlenmiştir." dediler. Ve (Nuh davetten vazgeçmeye) zorlandı.'),
(4856, 54, 10, 'Bunun üzerine Rabbine: "Ben yenik düştüm, bana yardım et!" diyerek yalvardı.'),
(4857, 54, 11, 'Biz de boşalan bir su ile göğün kapılarını açtık.'),
(4858, 54, 12, 'Yeri de kaynaklar halinde fışkırttık, derken sular takdir edilmiş bir iş için birleşti.'),
(4859, 54, 13, 'Nuh\'u da tahtalardan yapılmış, çivilerle (çakılmış gemi) üzerinde taşıdık.'),
(4860, 54, 14, 'Nankörlük edilen (kulumuz)e bir mükafat olmak üzere (gemi), gözlerimizin önünde akıp gidiyordu.'),
(4861, 54, 15, 'Bunu bir ibret olarak bıraktık, ibret alan yok mudur?'),
(4862, 54, 16, 'Benim azabım ve uyarılarım nasılmış (görsünler)'),
(4863, 54, 17, 'Andolsun biz Kur\'ân\'ı öğüt almak için kolaylaştırdık. Öğüt alan yok mudur?'),
(4864, 54, 18, 'Âd (kavmi) da yalanladı, azabım ve uyarılarım nasıl oldu?'),
(4865, 54, 19, 'Biz onların üstüne, uğursuzluğu devam eden bir günde dondurucu bir rüzgar gönderdik.'),
(4866, 54, 20, '(O rüzgar) insanları, sökülmüş hurma kütükleri gibi yere seriyordu.'),
(4867, 54, 21, 'Nasılmış benim azabım ve uyarım?'),
(4868, 54, 22, 'Andolsun biz Kur\'ân\'ı öğüt almak için kolaylaştırdık. Öğüt alan yok mudur?'),
(4869, 54, 23, 'Semûd da o uyarıları yalanladılar.'),
(4870, 54, 24, '"Bizden bir insana mı uyacağız? O takdirde biz apaçık bir sapıklık ve çılgınlık içine düşmüş oluruz." dediler.'),
(4871, 54, 25, '"Zikir, aramızdan ona mı bırakıldı? Hayır o, yalancı, küstahın biridir" (dediler).'),
(4872, 54, 26, 'Yarın onlar, yalancı, küstahın kim olduğunu bilecekler.'),
(4873, 54, 27, 'Biz onlara, kendilerini imtihan etmek için dişi deveyi göndereceğiz. Onun için sen onları gözet ve sabırlı ol.'),
(4874, 54, 28, 'Onlara suyun aralarında paylaştırılacağını haber ver; her içene düşen miktar, hazır kılınmıştır.'),
(4875, 54, 29, 'Bunun üzerine arkadaşlarına bağırdılar. O da (bıçağı) çekerek (deveyi) kesti.'),
(4876, 54, 30, 'Ama azabım ve uyarılarım nasıl oldu.'),
(4877, 54, 31, 'Biz onların üzerine tek sayha (korkunç bir ses) gönderdik; ağılcının topladığı çalı çırpı kırıntıları gibi kırılıp dökülüverdiler.'),
(4878, 54, 32, 'Andolsun biz Kur\'ân\'ı öğüt almak için kolaylaştırdık. Öğüt alan yok mudur?'),
(4879, 54, 33, 'Lût kavmi de uyarıları yalanladı.'),
(4880, 54, 34, 'Biz de onların üzerlerine (taşlar savuran) bir fırtına gönderdik. Yalnız Lût ailesini seher vakti kurtardık,'),
(4881, 54, 35, 'Katımızdan bir nimet olarak. Biz şükredeni böyle mükafatlandırırız.'),
(4882, 54, 36, '(Lût), onları bizim yakalamamıza karşı uyarmıştı. Fakat ikazlara karşı kuşku duydular,'),
(4883, 54, 37, 'Onun konuklarından murad almaya kalkıştılar. Biz de gözlerini siliverdik. "Haydi azabımı ve uyarılarımı tadın!" (dedik).'),
(4884, 54, 38, 'Sabah erken, onları kararlı bir azab yakaladı.'),
(4885, 54, 39, '"Azabımı ve uyarılarımı tadın!" (dedik).'),
(4886, 54, 40, 'Andolsun biz Kur\'ân\'ı öğüt almak için kolaylaştırdık. Öğüt alan yok mudur?'),
(4887, 54, 41, 'Şüphesiz Firavun ailesine de uyarıcı peygamberler geldi.'),
(4888, 54, 42, 'Lakin onlar bütün âyetlerimizi yalanladılar. Biz de onları çok kuvvetli ve kudretli bir yakalayışla yakaladık. Bu kıssalardan hisseye gelince;'),
(4889, 54, 43, 'Şimdi sizin kâfirleriniz, onlardan hayırlı mı? Yoksa kitaplarda sizin için bir beraet mi var?'),
(4890, 54, 44, 'Yoksa "Biz birbirimize yardım eden bir topluluğuz." mu diyorlar?'),
(4891, 54, 45, 'Her halde o topluluk bozulacak ve geriye dönüp kaçacaklardır.'),
(4892, 54, 46, 'Bilakis kıyamet onlara vaad edilen asıl saattir. Saat cidden çok feci ve acıdır.'),
(4893, 54, 47, 'Muhakkak ki suçlular sapıklık ve çılgınlık içindedirler.'),
(4894, 54, 48, 'O gün yüzleri üstü ateşte sürüklenecekler, "Cehennemin dokunuşunu tadın!" (denilecek).'),
(4895, 54, 49, 'Haberiniz olsun ki, biz her şeyi bir kadere göre yarattık.'),
(4896, 54, 50, 'Buyruğumuz yalnız bir tekdir, göz açıp yumma gibidir.'),
(4897, 54, 51, 'Andolsun biz, sizin benzerlerinizi hep helak ettik. Öğüt alan yok mudur?'),
(4898, 54, 52, 'İşledikleri her şey, kitaplarda mevcuttur.'),
(4899, 54, 53, 'Küçük, büyük hepsi satır satır yazılmıştır.'),
(4900, 54, 54, 'Takva sahipleri cennetlerde, nur içindedirler.'),
(4901, 54, 55, 'Güçlü padişahın huzurunda doğruluk koltuklarındadırlar.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Rahmân (çok merhametli olan Allah)'),
(4903, 55, 2, 'Kurân\'ı öğretti.'),
(4904, 55, 3, 'İnsanı yarattı.'),
(4905, 55, 4, 'Ona beyanı öğretti.'),
(4906, 55, 5, 'Güneş de ay da bir hesab iledir.'),
(4907, 55, 6, 'Bitkiler ve ağaçlar secde etmektedirler.'),
(4908, 55, 7, 'Göğü yükseltti ve mizanı koydu.'),
(4909, 55, 8, 'Sakın tartıda taşkınlık etmeyin.'),
(4910, 55, 9, 'Tartıyı adaletle yapın, terazide eksiklik yapmayın.'),
(4911, 55, 10, '(Allah) yeri mahlukat için (aşağıya) koydu.'),
(4912, 55, 11, 'Orada meyvalar ve salkımlı hurma ağaçları vardır.'),
(4913, 55, 12, 'Yapraklı taneler ve hoş kokulu bitkiler vardır.'),
(4914, 55, 13, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4915, 55, 14, 'Allah insanı, pişmiş bir çamura benzeyen bir balçıktan yarattı.'),
(4916, 55, 15, 'Cinleri de hâlis ateşten yarattı.'),
(4917, 55, 16, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4918, 55, 17, '(O) iki doğunun ve iki batının Rabbidir.'),
(4919, 55, 18, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4920, 55, 19, '(Acı ve tatlı) iki denizi salıverdi birbirine kavuşuyorlar.'),
(4921, 55, 20, 'Fakat aralarında bir engel vardır, birbirlerine geçip karışmıyorlar.'),
(4922, 55, 21, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4923, 55, 22, 'İkisinden de inci ve mercan çıkar.'),
(4924, 55, 23, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4925, 55, 24, 'Denizde koca dağlar gibi yükselen gemiler de onundur.'),
(4926, 55, 25, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4927, 55, 26, 'Yer üzerinde bulunan her şey fânidir.'),
(4928, 55, 27, 'Yalnız celâl ve ikram sahibi Rabbinin yüzü (zâtı) baki kalacaktır.'),
(4929, 55, 28, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4930, 55, 29, 'Göklerde ve yerde bulunanlar, O\'ndan isterler. O, her gün yeni bir iştedir.'),
(4931, 55, 30, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4932, 55, 31, 'Ey insan ve cin! sizin de hesabınızı ele alacağız.'),
(4933, 55, 32, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4934, 55, 33, 'Ey cin ve insan toplulukları! Göklerin ve yerin çevresinden geçmeye gücünüz yeterse geçin gidin. Allah\'ın verdiği bir güç olmadan geçemezsiniz.'),
(4935, 55, 34, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4936, 55, 35, 'Üzerinize ateşten alev ve duman gönderilir, kendinizi savunamazsınız.'),
(4937, 55, 36, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz'),
(4938, 55, 37, 'Gök yarılıp da, erimiş yağ gibi kıpkırmızı bir gül olduğu zaman...'),
(4939, 55, 38, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4940, 55, 39, 'İşte o gün, ne insana ne de cinne günahından sorulmaz.'),
(4941, 55, 40, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4942, 55, 41, 'Suçlular simalarından tanınır, alınlarından ve ayaklarından tutulur.'),
(4943, 55, 42, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4944, 55, 43, 'İşte bu, suçluların yalanladığı cehennemdir.'),
(4945, 55, 44, 'Onunla kaynar su arasında dolaşırlar.'),
(4946, 55, 45, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4947, 55, 46, 'Rabbinin makamından korkan kimselere iki cennet vardır.'),
(4948, 55, 47, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4949, 55, 48, 'İkisinin de çeşitli ağaçları, meyvaları vardır.'),
(4950, 55, 49, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4951, 55, 50, 'İkisinde de akıp giden iki kaynak vardır.'),
(4952, 55, 51, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4953, 55, 52, 'İkisinde de her türlü meyvadan çift çift vardır.'),
(4954, 55, 53, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4955, 55, 54, 'Astarları atlastan yataklara yaslanırlar. İki cennetin de devşirmesi yakındır.'),
(4956, 55, 55, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4957, 55, 56, 'Oralarda gözlerini yalnız eşlerine çevirmiş dilberler var ki, bunlardan önce onlara ne insan ne de cin dokunmuştur.'),
(4958, 55, 57, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4959, 55, 58, 'Sanki onlar yâkut ve mercandırlar.'),
(4960, 55, 59, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4961, 55, 60, 'İyiliğin karşılığı, yalnız iyilik değil midir?'),
(4962, 55, 61, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4963, 55, 62, 'Bu ikisinden başka iki cennet daha vardır.'),
(4964, 55, 63, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4965, 55, 64, '(Bu cennetler) yemyeşildirler.'),
(4966, 55, 65, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4967, 55, 66, 'İkisinde de fışkıran iki kaynak vardır.'),
(4968, 55, 67, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4969, 55, 68, 'İkisinde de her türlü meyva, hurma ve nar vardır.'),
(4970, 55, 69, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4971, 55, 70, 'İçlerinde güzel huylu, güzel yüzlü kadınlar vardır.'),
(4972, 55, 71, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4973, 55, 72, 'Çadırlar içerisinde gözlerini yalnız kocalarına çevirmiş hûriler vardır.'),
(4974, 55, 73, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4975, 55, 74, 'Bunlardan önce onlara ne insan ne de cin dokunmuştur.'),
(4976, 55, 75, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4977, 55, 76, 'Yeşil yastıklara ve hârikulâde güzel işlemeli döşeklere yaslanırlar.'),
(4978, 55, 77, 'Şimdi Rabbinizin hangi nimetlerini yalanlıyorsunuz?'),
(4979, 55, 78, 'Büyüklük ve ikram sahibi Rabbinin adı ne yücedir!');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Olacak vak\'a olduğu zaman'),
(4981, 56, 2, 'Onun oluşunu yalanlayacak kimse yoktur.'),
(4982, 56, 3, 'O, alçaltıcıdır, yükselticidir.'),
(4983, 56, 4, 'Yer şiddetle sarsıldığı'),
(4984, 56, 5, 'Dağlar serpildikçe serpildiği'),
(4985, 56, 6, 'Dağılıp toz duman haline geldiği'),
(4986, 56, 7, 'Ve sizler üç sınıf olduğunuz zaman'),
(4987, 56, 8, 'Sağın adamları (var ya) ne mutludurlar onlar!'),
(4988, 56, 9, 'Solun adamları ise ne uğursuzdurlar onlar!'),
(4989, 56, 10, 'Önde olanlar (var ya), onlar öncüdürler.'),
(4990, 56, 11, 'İşte o yaklaştırılanlar,'),
(4991, 56, 12, 'Nimet cennetlerindedirler.'),
(4992, 56, 13, 'Çoğu önceki ümmetlerden,'),
(4993, 56, 14, 'Birazı da sonrakilerden.'),
(4994, 56, 15, '(Onlar) cevherlerle işlenmiş tahtlar üzerindedirler.'),
(4995, 56, 16, 'Karşılıklı olarak onların üzerinde yaslanırlar.'),
(4996, 56, 17, 'Çevrelerinde, ölümsüzlüğe ulaşmış gençler dolaşırlar.'),
(4997, 56, 18, 'Kaynağından doldurulmuş, testiler, ibrikler ve kadehlerle.'),
(4998, 56, 19, 'Ondan ne başları ağrıtılır, ne de akılları giderilir.'),
(4999, 56, 20, 'Beğendikleri meyvalar,'),
(5000, 56, 21, 'Canlarının çektiği kuş etleri,'),
(5001, 56, 22, 'İri gözlü hûriler,'),
(5002, 56, 23, 'Saklı inciler gibi,'),
(5003, 56, 24, 'Yaptıklarına karşılık olarak verilir.'),
(5004, 56, 25, 'Orada boş bir söz ve günaha sokan bir laf işitmezler.'),
(5005, 56, 26, 'Duydukları söz, yalnız "selam", "selam" dır.'),
(5006, 56, 27, 'Sağın adamları, nedir o sağın adamları!'),
(5007, 56, 28, 'Dalbastı kirazlar,'),
(5008, 56, 29, 'Meyva dizili muzlar,'),
(5009, 56, 30, 'Uzamış gölgeler,'),
(5010, 56, 31, 'Fışkıran sular.'),
(5011, 56, 32, 'Pek çok meyva arasında,'),
(5012, 56, 33, 'Tükenmeyen ve yasaklanmayan'),
(5013, 56, 34, 'Ve yükseltilmiş döşekler üstündedirler.'),
(5014, 56, 35, 'Biz kadınları yeniden inşa ettik (yarattık).'),
(5015, 56, 36, 'Onları bâkireler yaptık.'),
(5016, 56, 37, 'Hep yaşıt sevgililer,'),
(5017, 56, 38, 'Sağın adamları içindir.'),
(5018, 56, 39, 'Bir çoğu öncekilerdendir.'),
(5019, 56, 40, 'Bir çoğu da sonrakilerdendir.'),
(5020, 56, 41, 'Solun adamları, nedir o solcular!'),
(5021, 56, 42, 'İçlerine işleyen bir ateş ve kaynar şu içinde,'),
(5022, 56, 43, 'Kapkara dumandan bir gölge altındadırlar.'),
(5023, 56, 44, 'Ki ne serindir, ne de faydalı.'),
(5024, 56, 45, 'Çünkü onlar bundan önce varlık içinde sefâhete dalmışlardı.'),
(5025, 56, 46, 'Büyük günahı işlemekte ısrar ediyorlardı.'),
(5026, 56, 47, 'Ve diyorlardı ki: "Biz ölüp, toprak ve kemik yığını olduktan sonra, biz mi bir daha diriltileceğiz?"'),
(5027, 56, 48, '"Önceki atalarımızda mı?"'),
(5028, 56, 49, 'De ki: "Öncekiler ve sonrakiler"'),
(5029, 56, 50, '"Belli bir günün belli vaktinde mutlaka toplanacaklardır."'),
(5030, 56, 51, 'Sonra siz, ey sapık yalanlayıcılar!'),
(5031, 56, 52, 'Elbette bir ağaçtan, zakkum ağacından yiyeceksiniz.'),
(5032, 56, 53, 'Karınlarınızı hep onunla dolduracaksınız.'),
(5033, 56, 54, 'Üstüne de kaynar su içeceksiniz.'),
(5034, 56, 55, 'Susuzluk illetine tutulmuş develerin içişi gibi içeceksiniz.'),
(5035, 56, 56, 'İşte ceza gününde onlara sunulacak ziyafet budur.'),
(5036, 56, 57, 'Biz sizi yarattık; tasdik etmeniz gerekmez mi?'),
(5037, 56, 58, 'Attığınız meniyi gördünüz mü?'),
(5038, 56, 59, 'Onu siz mi yaratıyorsunuz yoksa yaratan biz miyiz?'),
(5039, 56, 60, 'Aranızda ölümü takdir eden biziz ve bizim önümüze geçilmez.'),
(5040, 56, 61, 'Böylece sizin yerinize benzerlerinizi getirelim ve sizi bilmediğiniz bir yaratılışta tekrar var edelim diye (böyle yapıyoruz).'),
(5041, 56, 62, 'Andolsun, ilk yaratılışı bildiniz. Düşünüp ibret almanız gerekmez mi?'),
(5042, 56, 63, 'Ektiğinizi gördünüz mü?'),
(5043, 56, 64, 'Onu siz mi bitiriyorsunuz, yoksa bitiren biz miyiz?'),
(5044, 56, 65, 'Dileseydik, onu kuru bir çöp yapardık. Hayret eder dururdunuz.'),
(5045, 56, 66, '"Doğrusu borç altına girdik."'),
(5046, 56, 67, '"Doğrusu, biz yoksul bırakıldık" (derdiniz).'),
(5047, 56, 68, 'İçtiğiniz suya baktınız mı?'),
(5048, 56, 69, 'Buluttan onu siz mi indirdiniz, yoksa indiren biz miyiz?'),
(5049, 56, 70, 'Dileseydik onu tuzlu yapardık. O halde şükretseniz ya!'),
(5050, 56, 71, 'Yaktığınız ateşi gördünüz mü?'),
(5051, 56, 72, 'Onun ağacını siz mi yarattınız, yoksa yaratan biz miyiz?'),
(5052, 56, 73, 'Biz onu bir ibret ve çölden gelip geçenlere bir fayda yaptık.'),
(5053, 56, 74, 'Öyleyse büyük Rabbinin adını yücelt.'),
(5054, 56, 75, 'Hayır, yıldızların yerlerine yemin ederim.'),
(5055, 56, 76, 'Bilirseniz bu büyük bir yemindir.'),
(5056, 56, 77, 'O, elbette şerefli bir Kur\'ân\'dır.'),
(5057, 56, 78, 'Korunmuş bir kitaptadır.'),
(5058, 56, 79, 'Ona temizlenenlerden başkası el süremez.'),
(5059, 56, 80, '(O), âlemlerin Rabbinden indirilmiştir.'),
(5060, 56, 81, 'Şimdi siz bu sözü mü küçümsüyorsunuz?'),
(5061, 56, 82, 'Rızkınızı, yalanlamanızdan ibaret mi kılıyorsunuz?'),
(5062, 56, 83, 'Can boğaza dayandığı zaman'),
(5063, 56, 84, 'Ki o zaman siz (ölmek üzere olana) bakar durursunuz.'),
(5064, 56, 85, 'Biz ona sizden daha yakınız, fakat siz görmezsiniz.'),
(5065, 56, 86, 'Eğer cezalandırılmayacak iseniz,'),
(5066, 56, 87, 'Onu geri çevirsenize; şayet iddianızda doğru iseniz.'),
(5067, 56, 88, 'Fakat ölen kişiye gelince, eğer o rahmete yaklaştırılanlardan ise,'),
(5068, 56, 89, 'Ona rahatlık, güzel rızık ve Naîm cenneti vardır.'),
(5069, 56, 90, 'Eğer O, sağın adamlarından ise,'),
(5070, 56, 91, '"(Ey sağcı), sana sağcılardan selam!"'),
(5071, 56, 92, 'Ama yalanlayıcı sapıklardan ise;'),
(5072, 56, 93, 'İşte ona da kaynar sudan bir ziyafet vardır.'),
(5073, 56, 94, 'Ve cehenneme atılma vardır.'),
(5074, 56, 95, 'Kesin gerçek budur işte.'),
(5075, 56, 96, 'Öyle ise Rabbini o büyük ismiyle tesbih et.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Göklerde ve yerde bulunan her şey Allah\'ı tesbih etmektedir. O, çok güçlüdür, hüküm ve hikmet sahibidir.'),
(5077, 57, 2, 'Göklerin ve yerin mülkü O\'nundur. O, diriltir, öldürür, O, her şeye kadirdir.'),
(5078, 57, 3, 'O ilktir, sondur, zahirdir, bâtındır. O herşeyi bilendir.'),
(5079, 57, 4, 'O\'dur ki gökleri ve yeri altı günde yarattı. Sonra arş üzerine istivâ etti (hükümran oldu). Yere gireni, ondan çıkanı, gökten ineni, ona çıkanı bilir. Nerede olsanız O sizinle beraberdir. Allah yaptıklarınızı görmektedir.'),
(5080, 57, 5, 'Göklerin ve yerin mülkü O\'nundur. Bütün işler O\'na döndürülecektir.'),
(5081, 57, 6, 'Geceyi gündüzün içine sokar, gündüzü gecenin içine sokar. O, göğüslerin özünü bilir.'),
(5082, 57, 7, 'Allah\'a ve Resulüne iman edin. Sizi hâkim kıldığı, sizin yönetiminize verdiği şeylerden harcayın. Sizden, inanan ve harcayanlar için büyük mükafat vardır.'),
(5083, 57, 8, 'Size ne oldu ki, Resul sizi Rabbinize inanmanız için davet ettiği halde Allah\'a inanmıyorsunuz? Oysa O, sizden kesin söz almıştı. Eğer inanacaksanız.'),
(5084, 57, 9, 'Sizi karanlıklardan aydınlığa çıkarmak için kuluna apaçık âyetler indiren O\'dur. Şüphesiz Allah, size karşı çok şefkatli, çok merhametlidir.'),
(5085, 57, 10, 'Neden siz Allah yolunda harcamayasınız ki? Göklerin ve yerin mirası zaten Allah\'ındır. Elbette içinizden, fetihten önce harcayan ve savaşan bir olmaz. Onların derecesi, sonradan infak eden ve savaşanlardan daha büyüktür. Bununla beraber Allah hepsinede en güzel sonucu vaad etmiştir. Allah yaptıklarınızdan haberdardır.'),
(5086, 57, 11, 'Kimdir o, Allah\'a güzel bir borç verecek olan ki, Allah da onun verdiğini kat kat artırsın ve onun için şerefli bir mükafat da versin.'),
(5087, 57, 12, 'O gün inanan erkekleri ve inanan kadınları görürsün ki nurları, önlerinde ve sağlarında koşuyor. (Kendilerine): "Bugün müjdeniz altlarından ırmaklar akan, içlerinde ebedi kalacağınız cennetlerdir." (denilir) İşte büyük kurtuluş budur!'),
(5088, 57, 13, 'O gün münafık erkekler ve münafık kadınlar o iman edenlere şöyle diyeceklerdir: "Bize bakın da sizin nurunuzdan alalım?" Onlara: "Arkanıza dönün de nur arayın!" denilir. Aralarına kapılı bir sur çekilir ki, onun içinde rahmet, dışında da azap vardır.'),
(5089, 57, 14, '(Münafıklar) onlara: "Biz sizinle beraber değil miydik?" diye seslenirler. (Müminler) de derler ki: "Evet ama, siz kendi canlarınıza kötülük ettiniz, gözlediniz, şüpheye düştünüz ve kuruntular sizi aldattı. O çok aldatan (şeytan) sizi, Allah hakkında bile aldattı. Nihayet Allah\'ın emri gelip çattı.'),
(5090, 57, 15, 'Bugün artık ne sizden ne de inkar edenlerden fidye kabul edilir, varacağınız yer ateştir. Size yaraşan odur. Orası ne kötü bir dönüş yeridir!'),
(5091, 57, 16, 'İnananlar için hâlâ vakit gelmedi mi ki, kalbleri Allah\'ın zikrine ve inen hakka saygı duysun ve bundan önce kendilerine verilmiş, sonra üzerlerinden uzun zaman geçmekle kalbleri katılaşmış, çoğu da yoldan çıkmış kimseler gibi olmasınlar?'),
(5092, 57, 17, 'Biliniz ki Allah yer yüzünü ölümünden sonra diriltir. Belki aklınızı kullanırsınız diye size âyetleri açıkladık.'),
(5093, 57, 18, 'Şüphesiz sadaka veren erkeklere ve sadaka veren kadınlara ve Allah\'a güzel bir ödünç verenlere, verdikleri kat kat artırılır ve onlara şerefli bir mükafat vardır.'),
(5094, 57, 19, 'Allah\'a ve peygamberine iman edenler var ya, işte onlar, Rableri yanında sözü özü doğru olanlar ve şehitlik mertebesine erenlerdir. Onların mükafatları ve nurları vardır. İnkar edip de âyetlerimizi yalanlayanlara gelince, onlar da cehennemin adamlarıdır.'),
(5095, 57, 20, 'Biliniz ki dünya hayatı bir oyun, bir eğlence, bir süs ve kendi aranızda övünme, mal ve evlat çoğaltma yarışından ibarettir. Bu, tıpkı bir yağmura benzer ki; bitirdiği ot, ekincilerin hoşuna gider, sonra kurur, onu sapsarı görürsün, sonra çerçöp olur. Ahirette ise çetin bir azab; Allah\'tan mağfiret ve rıza vardır. Dünya hayatı, aldatıcı bir zevkten başka bir şey değildir.'),
(5096, 57, 21, 'Rabbinizden bir mağfirete; Allah\'a ve peygamberine inananlar için hazırlanmış olup, genişliği gökle yerin genişliği kadar olan cennete koşuşun. İşte bu Allah\'ın lütfudur. Onu dilediğine verir. Allah büyük lütuf sahibidir.'),
(5097, 57, 22, 'Yeryüzünde vuku bulan ve sizin başınıza gelen herhangi bir musibet yoktur ki biz onu yaratmadan önce, bir kitapta yazılmış olmasın. Şüphesiz bu Allah\'a göre kolaydır.'),
(5098, 57, 23, 'Böylece elinizden çıkana üzülmeyesiniz ve Allah\'ın size verdiği nimetlerle şımarmayasınız. Çünkü Allah, kendini beğenip böbürlenen kimseleri sevmez.'),
(5099, 57, 24, 'Onlar cimrilik edip insanlara da cimriliği emrederler. Kim yüz çevirirse Allah, zengindir, övgüye layıktır.'),
(5100, 57, 25, 'Andolsun biz peygamberlerimizi açık delillerle gönderdik ve insanların adaleti yerine getirmeleri için beraberlerinde kitabı ve ölçüyü indirdik. Biz demiri de indirdik ki onda büyük bir kuvvet ve insanlar için faydalar vardır. Bu, Allah\'ın dinine ve peygamberlerine görmeden yardım edenleri belirlemesi içindir. Şüphesiz Allah kuvvetlidir, daima üstündür.'),
(5101, 57, 26, 'Andolsun, Nuh\'u ve İbrahim\'i elçi gönderdik, peygamberliği ve kitabı bunların zürriyetleri arasına koyduk. Onlardan yola gelen de vardı, ama onlardan çoğu yoldan çıkmışlardı.'),
(5102, 57, 27, 'Sonra bunların izinden ard arda peygamberlerimizi gönderdik. Meryem oğlu İsa\'yı da arkalarından gönderdik, ona İncil\'i verdik ve ona uyanların yüreklerine bir şefkat ve merhamet koyduk. Uydurdukları ruhbanlığa gelince onu, biz yazmadık. Fakat kendileri Allah rızasını kazanmak için yaptılar. Ama buna da gereği gibi uymadılar. Biz de onlardan iman edenlere mükafatlarını verdik. İçlerinden çoğu da yoldan çıkmışlardır.'),
(5103, 57, 28, 'Ey inananlar! Allah\'tan korkun, O\'nun Resulü\'ne inanın ki size rahmetinden iki pay versin, sizin için ışığında yürüyeceğiniz bir nur yaratsın ve sizi bağışlasın. Allah çok bağışlayan, çok merhamet edendir'),
(5104, 57, 29, 'Böylece Kitab ehli, Allah\'ın lütfundan hiçbir şey elde edemiyeceklerini bilsinler. Lütuf bütünüyle Allah\'ın elindedir, onu dilediğine verir. Allah, büyük lütuf sahibidir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'Kocası hakkında seninle tartışan ve Allah\'a şikayette bulunan kadının sözünü Allah işitmiştir. Allah, sizin konuşmanızı işitir. Çünkü Allah, işitendir, bilendir.'),
(5106, 58, 2, 'İçinizde zıhâr yapanların kadınları, onların anaları değildir. Onların anaları ancak kendilerini doğuran kadındır. Şüphesiz onlar çirkin ve yalan bir laf söylüyorlar. Kuşkusuz Allah, affedici, bağışlayıcıdır.'),
(5107, 58, 3, 'Kadınlardan zıhâr ile ayrılmak isteyip de sonra söylediklerinden dönenlerin, karılarıyla temas etmeden önce bir köleyi hürriyete kavuşturmaları gerekir. Size öğütlenen budur. Allah, yaptıklarınızdan haberi olandır.'),
(5108, 58, 4, 'Buna imkan bulamayan kimse, temas etmeden önce aralıksız olarak iki ay oruç tutmalıdır. Buna da gücü yetmeyen, altmış fakiri doyurur. Bu (hafifletme), Allah\'a ve Resulüne inanmanızdan dolayıdır. Bunlar Allah\'ın hükümleridir. Kâfirler için acı bir azap vardır.'),
(5109, 58, 5, 'Allah\'a ve Resulüne karşı gelenler, kendilerinden öncekilerin alçaltıldığı gibi alçaltılacaklardır. Biz apaçık âyetler indirmişizdir. Kâfirler için küçük düşürücü bir azap vardır.'),
(5110, 58, 6, 'O gün Allah onların hepsini diriltecek ve yaptıklarını kendilerine haber verecektir. Allah onları bir bir saymıştır. Onlar ise unutmuşlardır. Allah her şeye şahiddir'),
(5111, 58, 7, 'Göklerde ve yerde olanları, Allah\'ın bildiğini görmüyor musunuz? Üç kişinin gizli konuştuğu yerde dördüncüsü mutlaka O\'dur. Beş kişinin gizli konuştuğu yerde altıncısı mutlaka O\'dur. Bunlardan az veya çok olsunlar ve nerede bulunurlarsa bulunsunlar mutlak O, onlarla beraberdir. Sonra kıyamet günü onlara yaptıklarını haber verecektir. Doğrusu Allah, her şeyi bilendir.'),
(5112, 58, 8, 'Gizli konuşmaktan menedildikten sonra yine o menedildikleri şeyi yapmaya kalkışarak günah, düşmanlık ve Peygamber\'e karşı gelmek hususunda gizlice konuşanları görmedin mi? Onlar sana geldikleri zaman seni, Allah\'ın selamlamadığı bir tarzda selamlıyorlar. Kendi içlerinden de "bu söylediklerimiz yüzünden Allah\'ın bize azap etmesi gerekmez miydi?" derler. Cehennem onlara yeter. Oraya gireceklerdir, ne kötü dönüş yeridir orası!'),
(5113, 58, 9, 'Ey iman edenler! Aranızda gizli konuşacağınız zaman günahı düşmanlığı ve Peygamber\'e karşı gelmeyi fısıldamayın. İyilik ve takvayı konuşun. Huzuruna toplanacağınız Allah\'tan korkun.'),
(5114, 58, 10, 'Gizli konuşmalar şeytandandır. Bu iman edenleri üzmek içindir. Oysa şeytan, Allah\'ın izni olmadıkça, müminlere hiçbir zarar veremez. Müminler Allah\'a dayanıp güvensinler.'),
(5115, 58, 11, 'Ey iman edenler! Size: "Meclislerde yer açın." denilince yer açın ki Allah da size genişlik versin. Size "Kalkın." denilince de kalkın ki Allah sizden inananları ve kendilerine ilim verilenleri derecelerle yükseltsin. Allah yaptıklarınızdan haberi olandır.'),
(5116, 58, 12, 'Ey iman edenler! Peygamber ile gizli bir şey konuşacağınız zaman bu konuşmanızdan önce bir sadaka veriniz. Bu sizin için daha hayırlı ve daha temizdir. Şayet bir şey bulamazsanız, artık Allah bağışlayan ve merhamet edendir..'),
(5117, 58, 13, 'Gizli (özel) bir şey konuşmanızdan önce sadaka vermekten korktunuz da mı yerine getirmediniz? Fakat Allah da sizi affetti. Şu halde namazı kılın, zekatı verin, Allah\'a ve Resulüne itaat edin. Allah, yaptıklarınızdan haberi olandır.'),
(5118, 58, 14, 'Allah\'ın kendilerine gazap ettiği bir topluluğu dost edinenleri görmedin mi? Onlar ne sizdendirler, ne de onlardan. Bilerek yalan yere yemin ediyorlar.'),
(5119, 58, 15, 'Allah onlara çetin bir azab hazırlamıştır. Onlar ne kötü işler yapıyorlar!'),
(5120, 58, 16, 'Yeminlerini kalkan yapıp Allah\'ın yolundan çevirdiler. Onlar için küçük düşürücü bir azab vardır.'),
(5121, 58, 17, 'Onların ne malları, ne de evlatları, kendilerinden, Allah\'dan hiçbir şey savamaz. Onlar ateş halkıdır. Orada ebedî kalacaklardır.'),
(5122, 58, 18, 'Allah onların hepsini tekrar dirilttiği gün, dünyada size yemin ettikleri gibi O\'na da yemin edecekler ve kendilerinin bir şey üzerinde bulunduklarını, sanacaklardır. İyi bilin ki onlar yalancıdırlar.'),
(5123, 58, 19, 'Şeytan onları istilâ etmiş, onlara Allah\'ı anmayı unutturmuştur. Onlar, şeytanın hizbi (partisi)dir. İyi bilin ki şeytanın partisi kaybedecektir.'),
(5124, 58, 20, 'Allah\'a ve Resulüne düşman olanlar var ya, onlar en alçaklar arasındadırlar.'),
(5125, 58, 21, 'Allah: "Elbette ben ve elçilerim galip geleceğiz." diye yazmıştır. Şüphesiz Allah güçlüdür, galipdir.'),
(5126, 58, 22, 'Allah\'a ve ahiret gününe inanan bir milletin, babaları, oğulları, kardeşleri, yahut akrabaları da olsa Allah\'a ve Resulüne düşman olanlarla dostluk ettiğini görmezsiniz. Onlar o kimselerdir ki Allah kalblerine iman yazmış ve onları kendinden bir ruh ile desteklemiştir. Onları, altlarından ırmaklar akan cennetlere sokacak, orada ebedî kalacaklardır. Allah onlardan razı olmuş, onlar da O\'ndan razı olmuşlardır. İşte onlar Allah\'ın hizbi (dininin yardımcıları)dir. İyi bil ki, kurtuluşa ulaşacak olanlar, Allah\'ın hizbidir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Göklerde ve yerde olanların hepsi Allah\'ı tesbih etmektedir, O üstündür, hikmet sahibidir.'),
(5128, 59, 2, 'Ehli kitaptan inkar edenleri, ilk sürgünleri yurtlarından çıkaran O\'dur. Siz onların çıkacaklarını sanmamıştınız. Onlar da kalelerinin, kendilerini Allah\'tan koruyacağını sanmışlardı. Ama Allah\'ın azabı, onlara beklemedikleri yerden geliverdi. O, yüreklerine korku düşürdü; öyle ki evlerini hem kendi elleriyle, hem de müminlerin elleriyle harap ediyorlardı. Ey akıl sahipleri! İbret alın.'),
(5129, 59, 3, 'Eğer Allah onlara sürgünü yazmamış olsaydı, elbette, onları dünyada başka şekilde cezalandıracaktı. Ahirette de onlar için ateş azabı vardır.'),
(5130, 59, 4, 'Bunun sebebi şudur: Onlar Allah\'a ve Resulüne karşı geldiler; Kim Allah\'a karşı gelirse Allah\'ın azabı şiddetlidir.'),
(5131, 59, 5, 'Hurma ağaçlarından her hangi bir şey kesmeniz veya kökleri üzerinde bırakmanız hep Allah\'ın izniyle ve O\'nun, yoldan çıkanları cezalandırması içindir.'),
(5132, 59, 6, 'Allah\'ın, onlardan peygamberine verdiği ganimetlere gelince siz onun üzerine ne at, ne de deve sürmediniz. Fakat Allah peygamberini, dilediği kimselerin üzerine salar. Allah her şeye kadirdir.'),
(5133, 59, 7, 'Allah\'ın o kent halkından, Resulüne verdiği ganimetler, Allah\'a, Resul\'e, ona akrabalığı bulunanlara, yetimlere, yoksullara, yolcuya aittir. Ta ki içinizden yalnız zenginler arasında dolaşan bir şey olmasın. Peygamber size ne verdiyse onu alın. Sizeneyi yasakladıysa ondan sakının ve Allah\'tan korkun. Çünkü Allah\'ın azabı şiddetlidir.'),
(5134, 59, 8, 'Bir de göç eden fakirlere aittir ki yurtlarından ve mallarından çıkarılmışlardır, Allah\'ın lütuf ve rızasını ararlar; Allah\'a ve Resulüne yardım ederler. İşte doğru olanlar onlardır.'),
(5135, 59, 9, 'Ve onlardan önce o yurda yerleşen imana sarılanlar kendilerine göç edip gelenleri severler ve onlara verilenlerden ötürü göğüslerinde bir ihtiyaç duymazlar. Kendilerinin ihtiyaçları olsa dahi, onları öz canlarına tercih ederler. Kim nefsinin cimriliğinden korunursa, işte onlar umduklarına erenlerdir.'),
(5136, 59, 10, 'Onlardan sonra gelenler derler ki: "Rabbimiz, bizi ve bizden önce inanan kardeşlerimizi bağışla, kalplerimizde inananlara karşı bir kin bırakma! Rabbimiz! Sen çok şefkatli, çok merhametlisin!"'),
(5137, 59, 11, 'Münafıkların, kitap ehlinden inkar eden dostlarına "Eğer siz yurdunuzdan çıkarılırsanız, mutlaka biz de sizinle beraber çıkarız sizin aleyhinizde kimseye asla uymayız. Eğer savaşa tutuşursanız, mutlaka yardım ederiz." dediklerini görmedin mi? Allah, onların yalancı olduklarına şahitlik eder.'),
(5138, 59, 12, 'Andolsun eğer onlar, çıkarılırsalar, onlarla beraber çıkmazlar; savaşa tutuşmuş olsalar, onlara yardım etmezler; yardım etseler bile arkalarını dönüp kaçarlar, sonra kendilerine de yardım edilmez.'),
(5139, 59, 13, 'Onların kalblerinde sizin korkunuz, Allah\'ın korkusundan fazladır. Böyledir, çünkü onlar anlamayan bir topluluktur.'),
(5140, 59, 14, 'Onlar toplu olarak sizinle savaşamazlar, ancak, müstahkem şehirlerde yahut duvarların ardından (sizinle savaşmak isterler). Kendi aralarındaki çekişmeleri şiddetlidir. Sen onları toplu sanırsın, oysa onların kalbleri dağınıktır. Böyledir, çünkü onlar aklını kullanmayan bir topluluktur.'),
(5141, 59, 15, '(Bu yahudilerin durumu) kendilerinden az önce, işlerinin günahını tatmış olan, ahirette de kendileri için acı bir azab bulunan kimselerin (Bedir\'de cezalarını bulan putperestlerin) durumu gibidir.'),
(5142, 59, 16, '(Yahudileri kandıran münafıkların durumu da) tıpkı şeytanın durumuna benzer ki insana "İnkâr et." dedi, (insan) inkar edince de: "Ben senden uzağım, ben âlemlerin Rabb\'i Allah\'tan korkarım!" dedi.'),
(5143, 59, 17, 'Nihayet ikisinin sonu, ebedi olarak ateşte oldu. Zalimlerin cezası budur.'),
(5144, 59, 18, 'Ey inananlar, Allah\'tan korkun ve kişi, yarın için ne (yapıp) gönderdiğine baksın. Allah\'tan korkun; çünkü Allah, yaptıklarınızdan haberdardır.'),
(5145, 59, 19, 'Allah\'ı unutup da Allah\'ın da kendilerini unutturduğu kimseler gibi olmayın onlar, yoldan çıkan kimselerdir.'),
(5146, 59, 20, 'Cehennem ehli ile cennet ehli bir olmaz. Cennet ehli kurtularak isteklerine erişenlerdir.'),
(5147, 59, 21, 'Biz bu Kur\'ân\'ı bir dağa indirseydik, Allah\'ın korkusundan onu baş eğmiş, parça, parça olmuş görürdün. Bu misalleri düşünsünler diye insanlara veriyoruz.'),
(5148, 59, 22, 'O, öyle Allah\'tır ki O\'ndan başka tanrı yoktur. Görülmeyeni ve görüleni bilendir. O, esirgeyen bağışlayandır.'),
(5149, 59, 23, 'O, öyle bir Allah\'tır ki, kendisinden başka hiçbir tanrı yoktur. O, mâlik ve sahiptir, münezzehtir, selâmet verendir, emniyete kavuşturandır, gözetip koruyandır, üstündür, istediğini zorla yaptıran, büyüklükte eşi olmayandır. Allah puta tapanların ortak koştukları şeylerden münezzehtir.'),
(5150, 59, 24, 'O, yaratan, var eden, varlıklara şekil veren Allah\'tır. En güzel isimler O\'nundur. Göklerde ve yerde olanlar O\'nun şânını yüceltmektedirler. O, gâlib olan, her şeyi hikmeti uyarınca yapandır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Ey inananlar! Benim de düşmanım, sizin de düşmanınız olan kimseleri dost edinmeyin. Onlar size gelen gerçeği inkar ettikleri, Rabbiniz Allah\'a inandığınızdan dolayı Resulü ve sizi (yurdunuzdan sürüp) çıkardıkları halde siz onlara sevgi ulaştırıyorsunuz. Eğer benim yolumda savaşmak ve benim rızamı kazanmak için çıktınızsa içinizde onlara sevgi mi gizliyorsunuz? Oysa ben sizin gizlediğiniz ve açığa vurduğunuz her şeyi bilirim. Sizden kim bunu yaparsa doğru yoldan sapmış olur.'),
(5152, 60, 2, 'Şayet onlar sizi ele geçirirlerse, size düşman kesilecekler, size ellerini ve dillerini kötülükle uzatacaklardır. Zaten inkar edivermenizi istemektedirler.'),
(5153, 60, 3, 'Kıyamet günü yakınlarınız ve çocuklarınız size fayda vermezler. Çünkü Allah aranızı ayırır. Allah yaptıklarınızı görendir.'),
(5154, 60, 4, 'İbrahim\'de ve onunla beraber bulunanlarda sizin için güzel bir misal vardır, onlar kavimlerine demişlerdi ki: "Biz sizden ve sizin Allah\'tan başka taptıklarınızdan uzağız. Sizi tanımıyoruz. Siz bir tek Allah\'a inanıncaya kadar sizinle bizim aramızda sürekli bir düşmanlık ve nefret belirmiştir." Yalnız İbrahim\'in babasına: "Senin için mağfiret dileyeceğim, fakat senin için Allah\'tan (gelecek) hiçbir şeyi (önlemeye) gücüm yetmez." demesi hariç. Rabbimiz! Yalnız sana dayandık, sana yöneldik. Dönüşümüz de ancak sanadır.'),
(5155, 60, 5, '"Rabbimiz! Bizi inkar edenler için bir fitne kılma, (onlara mağlub etme!) bizi bağışla! Ey Rabbimiz! Yegane gâlib ve hikmet sahibi ancak sensin."'),
(5156, 60, 6, 'Andolsun, onlarda sizin için, Allah\'ı ve ahiret gününü arzulayanlara güzel bir örnek vardır. Kim yüz çevirirse şüphesiz Allah, zengindir, hamde layık olandır.'),
(5157, 60, 7, 'Olur ki Allah sizinle düşmanlarınız arasında yakında bir dostluk meydana getirir. Allah gücü yetendir. Allah çok bağışlayan, çok merhamet edendir.'),
(5158, 60, 8, 'Allah sizi, din hakkında sizinle savaşmayan ve sizi yurtlarınızdan çıkarmayan kimselere iyilik etmekten, onlara adaletli davranmaktan men etmez. Çünkü Allah adalet yapanları sever.'),
(5159, 60, 9, 'Allah sizi, ancak sizinle din hakkında savaşan, sizi yurtlarınızdan çıkaran ve çıkarılmanız için yardım eden kimselere dost olmaktan men eder. Kim onlarla dost olursa işte zalimler onlardır.'),
(5160, 60, 10, 'Ey iman edenler! Mümin kadınlar hicret ederek size geldiği zaman, onları imtihan edin. Allah onların imanlarını daha iyi bilir. Eğer siz de onların inanmış kadınlar olduğunu öğrenirseniz onları kâfirlere geri döndürmeyin. Bunlar onlara helal değildir. Onlar da bunlara helal olmazlar. Onların (kocalarının) sarfettiklerini (mehirleri) geri verin. Mehirlerini kendilerine verdiğiniz zaman onlarla evlenmenizde size bir günah yoktur. Kâfir kadınları nikâhınızda tutmayın, sarfettiğinizi isteyin. Onlarda sarfettiklerini istesinler. Allah\'ın hükmü budur. Aranızda O, hükmeder, Allah bilendir, hikmet sahibidir.'),
(5161, 60, 11, 'Eğer eşlerinizden biri, sizden kâfirlere kaçar da siz de savaşta galip durumda olursanız, eşleri gitmiş olanlara ganimetten, harcadıkları kadar verin. İnandığınız Allah\'a karşı gelmekten sakının.'),
(5162, 60, 12, 'Ey Peygamber! İnanmış kadınlar sana gelip Allah\'a hiçbir şeyi ortak koşmamaları, hırsızlık etmemeleri, zina etmemeleri, çocuklarını öldürmemeleri, elleri ile ayakları arasında bir iftira uydurup getirmemeleri, iyi bir işte sana karşı gelmemeleri hususunda sana bey\'at ederlerse onların bey\'atlarını al ve onlar için Allah\'tan mağfiret dile. Şüphesiz Allah, çok bağışlayan, çok merhamet edendir.'),
(5163, 60, 13, 'Ey inananlar, Allah\'ın gazab ettiği kimselerle dostluk etmeyin. Kâfirler, mezarlık halkından nasıl ümidi kesmişse, onlar da ahiretten öyle ümidi kesmişlerdi.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Göklerdekilerin ve yerdekilerin hepsi Allah\'ı tesbih eder. O, üstündür, hikmet sahibidir.'),
(5165, 61, 2, 'Ey iman edenler! Yapmayacağınız şeyi niçin söylüyorsunuz?'),
(5166, 61, 3, 'Yapmayacağınızı söylemeniz, Allah yanında şiddetli bir buğza sebeb olur.'),
(5167, 61, 4, 'Allah, kendi yolunda kenetlenmiş bir duvar gibi saf bağlayarak savaşanları sever.'),
(5168, 61, 5, 'Bir zaman Musa, kavmine: "Ey kavmim! Benim, Allah\'ın size gönderdiği elçisi olduğumu bildiğiniz halde niçin beni incitiyorsunuz?" demişti. Onlar eğrilince, Allah da kalblerini eğriltti. Allah fasıkları doğru yola iletmez.'),
(5169, 61, 6, 'Meryem oğlu İsa da: "Ey İsrailoğulları! ben size Allah\'ın elçisiyim. benden önce gelen Tevrat\'ı doğrulayıcı ve benden sonra gelecek Ahmed adında bir peygamberi müjdeleyici olarak (geldim)." demişti. Fakat onlara apaçık delillerle gelince "Bu, apaçık bir büyüdür." dediler.'),
(5170, 61, 7, 'İslâm\'a davet olunduğu halde Allah üzerine yalan uydurandan daha zalim kim olabilir? Allah zalim toplumu doğru yola iletmez.'),
(5171, 61, 8, 'Ağızlarıyla Allah\'ın nurunu söndürmek istiyorlar. Halbuki kâfirler hoş görmese de Allah nurunu tamamlayacaktır.'),
(5172, 61, 9, 'O, Resulünü hidayet ve hak dinle gönderdi ki, müşrikler istemese de onu, bütün dinlerin üstüne çıkarsın.'),
(5173, 61, 10, 'Ey İman edenler! Sizi acı bir azabdan kurtaracak ticareti size göstereyim mi?'),
(5174, 61, 11, 'Allah\'a ve Resulüne inanırsınız, mallarınızla ve canlarınızla Allah yolunda savaşırsınız. Eğer bilirseniz sizin için en iyisi budur.'),
(5175, 61, 12, '(Eğer böyle yaparsanız Allah) sizin günahlarınızı bağışlar ve sizi altlarından ırmaklar akan cennetlere, Adn cennetlerinde hoş yerlere koyar. İşte büyük kurtuluş budur.'),
(5176, 61, 13, 'Seveceğiniz bir şey daha var: Allah\'tan yardım ve yakın bir fetih.. Müminleri müjdele.'),
(5177, 61, 14, 'Ey inananlar, Allah\'ın yardımcıları olun. Nitekim Meryem oğlu İsa da havarilere: "Allah\'a (giden yolda) benim yardımcılarım kimdir?" demişti. Havariler: "Allah (yolun)un yardımcıları biziz." dediler. İsrail oğullarından bir zümre inandı, bir zümre inkar etti. Biz de inananları, düşmanlarına karşı destekledik, onlar üstün geldiler.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Göklerde ve yerde olanların hepsi padişah, mukaddes, azîz ve hakîm olan Allah\'ı tesbih etmektedir.'),
(5179, 62, 2, 'O\'dur ki ümmiler içinde, kendilerinden olan ve onlara Allah\'ın âyetlerini okuyan, onları temizleyen, onlara kitap ve hikmeti öğreten bir Peygamber gönderdi. Oysa onlar, önceden apaçık bir sapıklık içinde idiler.'),
(5180, 62, 3, 'Henüz onlara katılmamış bulunan diğer insanlara da (o Peygamberi göndermiştir). O, çok güçlüdür, hüküm ve hikmet sahibidir.'),
(5181, 62, 4, 'Bu, Allah\'ın lütfudur. Allah, büyük lütuf sahibidir.'),
(5182, 62, 5, 'Kendilerine Tevrat yükletilip de sonra onu taşımayanların durumu, kitaplar taşıyan eşeğin durumu gibidir. Allah\'ın âyetlerini yalanlayanların durumu ne kötüdür. Allah zalim toplumu doğru yola iletmez.'),
(5183, 62, 6, 'De ki: "Ey Yahudi olanlar! Eğer insanlar arasında yalnız sizin, Allah\'ın dostları olduğunuzu sanıyorsanız, o halde ölümü temenni edin, doğru iseniz?"'),
(5184, 62, 7, 'Ama onlar, ellerinin (yapıp) öne sürdüğü (işler) yüzünden ölümü asla temenni etmezler. Allah zalimleri bilir.'),
(5185, 62, 8, 'De ki: "Sizin kendisinden kaçtığınız ölüm, muhakkak sizi bulacaktır. Sonra görünmeyeni ve görüneni bilene döndürüleceksiniz. O size (bütün) yaptıklarınızı haber verecektir.'),
(5186, 62, 9, 'Ey inananlar! Cuma günü namaz için çağrıldığı(nız) zaman, Allah\'ı anmaya koşun, alışverişi bırakın. Eğer bilirseniz, bu sizin için daha hayırlıdır.'),
(5187, 62, 10, 'Namaz kılındıktan sonra yeryüzüne dağılın ve Allah\'ın lütfundan (nasibinizi) arayın. Allah\'ı çok anın ki kurtuluşa eresiniz.'),
(5188, 62, 11, 'Bir ticaret ve eğlence gördükleri zaman hemen dağılıp ona gittiler ve seni ayakta bıraktılar. De ki: "Allah\'ın yanında bulunan, eğlenceden ve ticaretten de hayırlıdır. Allah, rızık verenlerin en hayırlısıdır."');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'Münafıklar sana geldikleri vakit: "Şahitlik ederiz ki sen muhakkak Allah\'ın elçisisin." derler. Senin mutlaka kendisinin elçisi olduğunu Allah bilir ve Allah münafıkların yalancı olduklarına şahitlik eder.'),
(5190, 63, 2, 'Yeminlerini kalkan yapıp (insanları) Allah\'ın yolundan çevirdiler. Onların yaptıkları ne kötüdür!'),
(5191, 63, 3, 'Bunun sebebi şudur: Onlar inandılar, sonra inkar ettiler, bu yüzden kalblerinin üzeri mühürlendi. Artık onlar anlamazlar.'),
(5192, 63, 4, 'Onları gördüğün zaman kalıpları hoşuna gider, konuşurlarsa sözlerini dinlersin. Onlar sanki dayanmış keresteler gibidirler. Her gürültüyü kendi aleyhlerine sanırlar. Onlar düşmandır, onlardan sakın. Allah onları kahretsin! Nasıl olup da döndürülüyorlar?'),
(5193, 63, 5, 'Onlara: "Gelin, Allah\'ın Resulü sizin için mağfiret dilesin." denildiği zaman başlarını çevirirler ve onların, büyüklük taslayarak yüz çevirdiklerini görürsün.'),
(5194, 63, 6, 'Onlara mağfiret dilesen de, dilemesen de onlar için birdir. Allah onları bağışlamayacaktır. Çünkü Allah, yoldan çıkmış bir toplumu yola iletmez.'),
(5195, 63, 7, 'Onlar öyle kimselerdir ki: "Allah\'ın elçisinin yanında bulunanları beslemeyin ki dağılıp gitsinler." diyorlar. Oysa göklerin ve yerin hazineleri Allah\'ındır, fakat münafıklar anlamazlar.'),
(5196, 63, 8, 'Diyorlar ki: "Andolsun, eğer Medine\'ye dönersek, daha üstün olan, daha alçak olanı oradan mutlaka çıkaracaktır." Üstünlük, ancak Allah\'a, O\'nun elçisine ve müminlere mahsustur. Fakat münafıklar bilmezler.'),
(5197, 63, 9, 'Ey İnananlar! Mallarınız ve çocuklarınız sizi Allah\'ı anmaktan alıkoymasın. Kim bunu yaparsa işte onlar ziyana uğrayanlardır.'),
(5198, 63, 10, 'Birinize ölüm gelip de: "Rabbim, beni yakın bir süreye kadar erteleseydin de sadaka verip iyilerden olsaydım!" demesinden önce, size verdiğimiz rızıktan (Allah) için harcayın.'),
(5199, 63, 11, 'Allah süresi geldiği zaman hiç bir canı ertelemez. Allah yaptıklarınızdan haberdardır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Göklerde ve yerde ne varsa hepsi Allah\'ı tesbih eder. Mülk O\'nundur, hamd O\'nadır. Her şeye gücü yeten O\'dur.'),
(5201, 64, 2, 'Sizi O yarattı. Kiminiz kâfirdir, kiminiz mümin. Allah yaptıklarınızı görmektedir.'),
(5202, 64, 3, 'Zira gökleri ve yeri hak ile yarattı. Sizi şekillendirdi ve şekillerinizi de güzel yaptı. Dönüş ancak O\'nadır.'),
(5203, 64, 4, 'Göklerde ve yerde olanları, gizlediğiniz ve açığa vurduğunuz şeyleri bilir. Allah, göğüslerin özünü bilir.'),
(5204, 64, 5, 'Önceden inkâr edenlerin haberi size gelmedi mi? (Onlar) işlerinin vebalini tattılar ve onlar için acı bir azap vardır.'),
(5205, 64, 6, 'Böyledir, çünkü onlara peygamberleri, açık deliller getirirlerdi, fakat onlar: "Bir insan mı bize yol gösterecek?" dediler ve yüz çevirdiler. Allah da muhtaç olmadığını gösterdi. Allah zengindir, övülmeye lâyıktır.'),
(5206, 64, 7, 'İnkâr edenler, katiyyen diriltilmeyeceklerini sandılar. De ki: "Hayır! Rabbim hakkı için mutlaka diriltileceksiniz, sonra yaptıklarınız size haber verilecektir. Bu, Allah\'a göre kolaydır".'),
(5207, 64, 8, 'Artık Allah\'a, Resulüne ve indirdiğimiz nura (Kur\'ân\'a) inanın. Allah yaptıklarınızdan haberdardır.'),
(5208, 64, 9, 'Toplanma günü için sizi topladığı zaman var ya, işte o gün, kimin aldandığının açığa çıkacağı aldanma günüdür. Kim Allah\'a inanır ve yararlı iş yaparsa, Allah onun kötülüklerini örter ve onu, içinde ebedi kalacakları, altlarından ırmaklar akan cennetlere sokar. İşte büyük kurtuluş budur.'),
(5209, 64, 10, 'İnkâr eden ve âyetlerimizi yalanlayanlara gelince, işte onlar cehennem ehlidirler. Orada ebedi kalacaklardır. Ne kötü gidilecek yerdir orası!'),
(5210, 64, 11, 'Allah\'ın izni olmayınca hiç bir musibet isabet etmez. Kim Allah\'a inanırsa, Allah onun kalbini doğruya götürür. Allah her şeyi bilendir.'),
(5211, 64, 12, 'Allah\'a itaat edin, Peygamber\'e de itaat edin. Yüz çevirirseniz bilin ki, elçimize düşen apaçık bir duyurmadır.'),
(5212, 64, 13, 'Allah ki O\'ndan başka tanrı yoktur. Müminler Allah\'a dayansınlar.'),
(5213, 64, 14, 'Ey iman edenler! Eşlerinizden ve çocuklarınızdan size düşman olanlar da vardır. Onlardan sakının. Ama affeder, kusurlarını başlarına kakmaz, hoş görür ve bağışlarsanız, bilin ki Allah çok bağışlayan çok merhamet edendir.'),
(5214, 64, 15, 'Doğrusu mallarınız ve çocuklarınız sizin için bir imtihandır. Büyük mükafat ise Allah\'ın yanındadır.'),
(5215, 64, 16, 'O halde gücünüzün yettiği kadar Allah\'tan korkun, dinleyin, itaat edin, kendi iyiliğinize olarak harcayın. Kim nefsinin cimriliğinden korunursa işte onlar kurtuluşa erenlerdir.'),
(5216, 64, 17, 'Eğer Allah\'a güzel bir borç verirseniz, Allah onu sizin için kat kat yapar ve sizi bağışlar. Allah çok mükafat verendir, halimdir.'),
(5217, 64, 18, 'Görünmeyeni ve görüneni bilendir. Üstündür, hikmet sahibidir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Ey Peygamber! Kadınları boşamak istediğiniz zaman onları iddetleri içinde boşayın ve iddeti de sayın. Rabbiniz Allah\'tan korkun. Apaçık bir hayasızlık yapmaları hali bir yana, onları evlerinden çıkarmayın, kendileri de çıkmasınlar. Bunlar Allah\'ın sınırlarıdır. Kim Allah\'ın sınırlarını aşarsa, şüphesiz kendine zulmetmiş olur. Bilmezsin, olur ki Allah, bundan sonra bir durum ortaya çıkarıverir.'),
(5219, 65, 2, 'Sürelerinin sonuna vardıklarında onları güzelce tutun, yahut güzellikle onlardan ayrılın. İçinizden adalet sahibi iki kişiyi şahit tutun. Şahidliği Allah için yapın. İşte Allah\'a ve son güne inanan kimseye öğütlenen budur. Kim Allah\'tan korkarsa Allah ona bir çıkış yolu yaratır.'),
(5220, 65, 3, 'Ve onu ummadığı yerden rızıklandırır. Kim Allah\'a güvenirse O, ona yeter. Allah, emrini yerine getirendir. Allah her şey için bir ölçü koymuştur.'),
(5221, 65, 4, 'Kadınlarınız içinden âdetten kesilmiş olanlarla, henüz âdetini görmemiş bulunanlardan eğer şüphe ederseniz (iddetlerinin nasıl olacağında tereddüt ederseniz), onların bekleme süresi üç aydır. Gebe olanların bekleme süresi ise, yüklerini bırakmaları, doğum yapmalarıdır. Kim Allah\'tan korkarsa, Allah ona işinde bir kolaylık verir.'),
(5222, 65, 5, 'Bu, Allah\'ın size indirdiği buyruğudur. Kim Allah\'tan korkarsa Allah onun kötülüklerini örter ve onun mükafatını büyütür.'),
(5223, 65, 6, 'O kadınları, gücünüz ölçüsünde oturduğunuz yerin bir bölümünde oturtun ve onları sıkıştırmak için kendilerine zarar vermeye kalkışmayın. Şayet gebe iseler, yüklerini bırakıncaya kadar onları besleyin. Sonra sizin için emzirirlerse ücretlerini verin ve aranızda güzellikle konuşup danışın. Güçlük çekerseniz çocuğu, başka bir kadın emzirecektir.'),
(5224, 65, 7, 'Eli geniş olan genişliğine göre nafaka versin. Rızkı kısılmış bulunan da Allah\'ın kendisine verdiğinden versin. Allah bir kişiye ne vermişse ancak onu teklif eder. Allah bir güçlükten sonra bir kolaylık yaratacaktır.'),
(5225, 65, 8, 'Nice kent var ki Rablerinin ve O\'nun elçilerinin emrine başkaldırdı, biz de onları çetin bir hesaba çektik ve onlara görülmemiş şekilde azab ettik.'),
(5226, 65, 9, 'İşlerinin vebalini tattılar. İşlerinin sonucu tam bir hüsran olmuştur.'),
(5227, 65, 10, 'Allah onlara şiddetli bir azap hazırlamıştır. O halde ey inanan aklı selim sahipleri! Allah\'tan korkun, Allah size bir uyarıcı gönderdi.'),
(5228, 65, 11, 'Size Allah\'ın açık açık âyetlerini okuyan bir elçi (gönderdi) ki inanıp faydalı işler yapanları, karanlıklardan aydınlığa çıkarsın. Kim Allah\'a inanır ve yararlı iş yaparsa (Allah) onu, altlarından ırmaklar akan, içinde ebedi kalacakları cennetlere sokar. Allah ona gerçekten ne güzel rızık vermiştir.'),
(5229, 65, 12, 'Allah O\'dur ki yedi göğü ve yerden de onlar kadarını yarattı. Emir bunlar arasında iner ki Allah\'ın her şeye kâdir olduğunu ve Allah\'ın bilgisinin, her şeyi kuşattığını bilesiniz.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Ey Peygamber! Eşlerinin rızasını arayarak Allah\'ın sana helâl kıldığı şeyi niçin sen kendine haram ediyorsun? Allah çok bağışlayan çok esirgeyendir.'),
(5231, 66, 2, 'Allah size yeminlerinizi çözmeyi meşrû kılmıştır. Allah sizin sahibinizdir. O bilendir, hikmetle yönetendir.'),
(5232, 66, 3, 'Peygamber, eşlerinden birine gizlice bir söz söylemişti. Fakat eşi, o sözü başkalarına haber verip Allah da bunu Peygamber\'e açıklayınca, Peygamber (eşine) bir kısmını bildirmiş bir kısmından da vazgeçmişti. Peygamber bunu ona haber verince eşi: "Bunu sana kim söyledi?" dedi. Peygamber "Bilen, her şeyden haberi olan Allah bana söyledi." dedi.'),
(5233, 66, 4, 'Eğer ikiniz de Allah\'a tevbe ederseniz ne iyi, çünkü kalpleriniz eğildi. Ve eğer Peygamber\'e karşı birbirinize arka olursanız (bilin ki) onun dostu ve yardımcısı Allah, Cibrîl ve müminlerin iyileridir. Bunun ardından melekler de ona arkadır.'),
(5234, 66, 5, 'Eğer o sizi boşarsa belki de Rabbi ona, sizden daha hayırlı, kendisini Allah\'a teslim eden, inanan, gönülden itaat eden, tevbe eden, oruç tutan dul ve bakire eşler verir.'),
(5235, 66, 6, 'Ey inananlar! Kendinizi ve ailenizi bir ateşten koruyun ki onun yakıtı insanlar ve taşlardır. Onun başında gayet katı, şiddetli, Allah\'ın kendilerine buyurduğuna karşı gelmeyen ve emredildikleri şeyi yapan melekler vardır.'),
(5236, 66, 7, '(İnkâr edenlere): "Ey kâfirler! Bugün özür dilemeyin. Siz ancak işlediklerinizin cezasını çekeceksiniz." (denilir.)'),
(5237, 66, 8, 'Ey iman edenler! Samimi bir tevbe ile Allah\'a dönün. Umulur ki Rabbiniz sizin kötülüklerinizi örter, Peygamber\'i ve onunla birlikte iman edenleri utandırmayacağı günde Allah sizi, içlerinden ırmaklar akan cennetlere sokar. Çünkü onların nurları, önlerinde ve yanlarında koşar da, "Ey Rabbimiz! Nurumuzu tamamla, bizi bağışla, çünkü sen her şeye kâdirsin." derler.'),
(5238, 66, 9, 'Ey Peygamber! Kâfirler ve münafıklarla savaş, onlara karşı sert davran. Onların varacağı yer cehennemdir. O gidilecek yer, ne de kötüdür!'),
(5239, 66, 10, 'Allah, inkâr edenlere, Nuh\'un karısı ile Lut\'un karısını misal verdi. Bu ikisi, kullarımızdan iki salih kulun (nikahı) altında idiler, onlara hıyanet ettiler. (Kocaları,) Allah\'tan hiçbir şeyi onlardan savamadı. (Onlara): "Haydi girenlerle birlikte siz de ateşe girin!" denildi.'),
(5240, 66, 11, 'Allah, inananlara da Firavun\'un karısını örnek gösterdi. O şöyle demişti: "Rabbim! Bana yanında cennetin içinde bir ev yap, beni Firavun\'dan ve onun (kötü) işinden kurtar. Ve beni şu zalim toplumdan kurtar!"'),
(5241, 66, 12, 'Irzını korumuş olan, İmrân kızı Meryem\'i de Allah örnek gösterdi. Biz, ona ruhumuzdan üfledik ve Rabbinin sözlerini ve kitaplarını tasdik etti. O, gönülden itaat edenlerdendi.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Mutlak hükümranlık elinde bulunan Allah, yüceler yücesidir ve O\'nun her şeye gücü yeter.'),
(5243, 67, 2, 'O, hanginizin daha güzel iş yapacağınızı denemek için ölümü ve hayatı yarattı. O, üstündür, bağışlayandır.'),
(5244, 67, 3, 'O, yedi göğü, birbiri üzerine yarattı. Rahmân\'ın yaratmasında bir aykırılık, uygunsuzluk görmezsin. Gözünü döndür de bak, bir bozukluk görüyor musun?'),
(5245, 67, 4, 'Sonra gözünü tekrar tekrar döndür (bak). Göz (aradığı bozukluğu bulmaktan) âciz ve bitkin halde sana dönecektir.'),
(5246, 67, 5, 'Andolsun biz, en yakın göğü kandillerle donattık ve onları, şeytanlar için taşlamalar yaptık. Ve onlar için alevli ateş azabını hazırladık.'),
(5247, 67, 6, 'Rablerini inkâr edenler için cehennem azabı vardır. Ne kötü gidilecek yerdir o!'),
(5248, 67, 7, 'Oraya atıldıklarında, onun kaynarken çıkardığı uğultuyu işitirler.'),
(5249, 67, 8, 'Az daha öfkeden çatlayacak. Her ne zaman oraya bir topluluk atılsa, onun bekçileri onlara: "Size korkutucu bir peygamber gelmemiş miydi?" diye sorarlar.'),
(5250, 67, 9, 'Derler: "Evet, bize uyarıcı geldi ama biz yalanladık ve Allah hiçbir şey indirmedi, siz ancak büyük bir sapıklık içindesiniz." dedik.'),
(5251, 67, 10, 'Ve derler ki: "Eğer biz dinleseydik, yahut düşünüp anlasaydık şu çılgın ateşin halkı arasında bulunmazdık!"'),
(5252, 67, 11, 'Böylece günahlarını itiraf ederler. (Artık) o çılgın ateş halkı (Allah\'ın rahmetinden) uzak olsunlar!'),
(5253, 67, 12, 'Fakat daha görmeden Rablerinden korkanlar var ya, işte onlar için bağışlanma ve büyük bir mükâfat vardır.'),
(5254, 67, 13, 'Sözünüzü ister gizleyin, ister açığa vurun; bilin ki, O, göğüslerin özünü bilir.'),
(5255, 67, 14, 'Hiç yaratan bilmez mi? O, en ince işleri görüp bilmektedir ve her şeyden haberdardır.'),
(5256, 67, 15, 'O size yeri boyun eğer kıldı. Haydi onun omuzlarında (dağlarında, tepelerinde) yürüyün ve Allah\'ın rızkından yeyin. Dönüş ancak O\'nadır.'),
(5257, 67, 16, 'Her şeyi kuşatmış olan Allah ın yeri sizinle birlikte göçürüvermesinden emin misiniz? O zaman yer çalkalanıyordur.'),
(5258, 67, 17, 'Yoksa siz, gökte olanın üzerinize taş yağdıran bir kasırga göndermeyeceğinden emin misiniz? Tehdidim nasılmış bileceksiniz.'),
(5259, 67, 18, 'Andolsun, onlardan öncekiler de yalanladılar. Ama beni inkâr nasıl oldu?'),
(5260, 67, 19, 'Üstlerinde kanatlarını açıp yumarak uçan kuşları görmüyorlar mı? Onları Rahmân\'dan başkası tutmuyor. Doğrusu O, her şeyi görmektedir.'),
(5261, 67, 20, 'Rahmân olan Allah\'a karşı şu size yardım edecek askerleriniz hani kimlerdir? İnkârcılar, ancak derin bir gaflet içinde bulunmaktadırlar.'),
(5262, 67, 21, 'Allah size verdiği rızkı kesiverse, size rızık verecek olabilen kimdir? Hayır, onlar azgınlık ve nefrette direnip durmaktadırlar.'),
(5263, 67, 22, 'Şimdi yüz üstü kapanarak yürüyen mi doğru gider, yoksa dosdoğru yolda yürüyen mi?'),
(5264, 67, 23, 'De ki: "Sizi yaratan, size kulaklar gözler ve gönüller veren O\'dur. Ne kadar az şükrediyorsunuz!"'),
(5265, 67, 24, 'De ki: "Sizi yerden üreten O\'dur ve O\'na toplanıp götürüleceksiniz."'),
(5266, 67, 25, '(Onlar): "Doğru iseniz bu tehdit ne zaman olacak?" diyorlar'),
(5267, 67, 26, 'De ki: "(O\'na ait) bilgi, Allah\'ın yanındadır. Ben ancak apaçık bir uyarıcıyım."'),
(5268, 67, 27, 'Onu yakın görünce inkâr edenlerin yüzleri kötüleşti. Ve: "İşte çağırıp durduğunuz şey budur!" dendi.'),
(5269, 67, 28, 'De ki: "Baksanıza, eğer Allah beni ve benimle beraber olanları öldürse, yahut bize merhamet etse, kâfirleri acı bir azabdan kim kurtarabilir?'),
(5270, 67, 29, 'De ki: "O çok merhametlidir. O\'na inanmış, O\'na dayanmışızdır. Yakında kimin apaçık bir sapıklık içinde olduğunu bileceksiniz."'),
(5271, 67, 30, 'De ki: "Baksanıza, eğer suyunuz çekilse, size kim bir akarsu getirebilir?"');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'Nûn, Kaleme ve yazdıklarına andolsun.'),
(5273, 68, 2, 'Sen Rabbinin nimetiyle mecnun değilsin.'),
(5274, 68, 3, 'Kuşkusuz senin için tükenmez bir ecir var.'),
(5275, 68, 4, 'Sen elbette yüce bir ahlak üzeresin.'),
(5276, 68, 5, 'Sen de göreceksin, onlar da görecek.'),
(5277, 68, 6, 'Hanginizde imiş o fitne ve cinnet.'),
(5278, 68, 7, 'Doğrusu Rabbin, yolundan sapanı en iyi bilendir. Hidayete ereni de en iyi bilen O\'dur.'),
(5279, 68, 8, 'O halde, yalanlayıcılara itaat etme.'),
(5280, 68, 9, 'Onlar istediler ki yumuşak davranasın da onlar da sana yumuşak davransınlar.'),
(5281, 68, 10, 'Şunların hiçbirine boyun eğme: Yemin edip duran aşağılık,'),
(5282, 68, 11, 'Daima kusur arayıp kınayan, hep lâf götürüp getiren,'),
(5283, 68, 12, 'Hayra engel olan, saldırgan, günahkâr,'),
(5284, 68, 13, 'Kaba ve haşin, sonra da kötülükle damgalı,'),
(5285, 68, 14, 'Mal ve oğulları var diye (böyle davranır).'),
(5286, 68, 15, 'Kendisine âyetlerimiz okunduğunda: "Eskilerin masalları" der.'),
(5287, 68, 16, 'Yakında biz onu hortumunun (burnunun) üzerinden damgalayacağız.'),
(5288, 68, 17, 'Biz onlara da belâ verdik, bahçe sahiplerine verdiğimiz gibi. Hani onlar sabah olunca bahçeyi mutlaka devşireceklerine yemin etmişlerdi.'),
(5289, 68, 18, 'İstisna da etmiyorlardı ("inşaallah" demiyorlardı).'),
(5290, 68, 19, 'Fakat onlar uyurken dolaşıcı bir belâ onu sardı da,'),
(5291, 68, 20, 'Bahçe simsiyah kesiliverdi.'),
(5292, 68, 21, 'Derken sabahleyin birbirlerine seslendiler:'),
(5293, 68, 22, '"Haydi, devşirecekseniz erkenden ekininize gidin" diye.'),
(5294, 68, 23, 'Derken fırladılar, aralarında fısıldaşıyorlardı.'),
(5295, 68, 24, '"Sakın bugün hiçbir yoksul bahçeye girip yanınıza sokulmasın" diyorlardı.'),
(5296, 68, 25, '(Zanlarınca yoksulları) engellemeye güçleri yeterek erkenden gittiler.'),
(5297, 68, 26, 'Fakat bahçeyi gördüklerinde: "Biz herhalde yanlış gelmişiz" dediler.'),
(5298, 68, 27, '"Yok, biz mahrum edilmişiz." (dediler).'),
(5299, 68, 28, 'İçlerinde en makul olanı şöyle dedi: "Ben size Rabbinizi tesbih etsenize dememiş miydim?"'),
(5300, 68, 29, '"Rabbimizi tesbih ederiz, doğrusu biz zalimler imişiz." (dediler).'),
(5301, 68, 30, 'Ardından suçu birbirlerine yüklemeye başladılar.'),
(5302, 68, 31, 'Yazıklar olsun bize, dediler, biz azgınlarmışız.'),
(5303, 68, 32, 'Ola ki Rabbimiz bize onun yerine daha hayırlısını verir. Biz Rabbimize yönelir, ondan umarız.'),
(5304, 68, 33, 'İşte azap böyledir. Elbette ahiret azabı daha büyüktür. Fakat bilselerdi.'),
(5305, 68, 34, 'Kuşkusuz korunanlar için de, Rableri katında nimetleri bol bahçeler vardır.'),
(5306, 68, 35, 'Öyle ya, teslimiyet gösterenleri suçlular gibi tutar mıyız hiç?'),
(5307, 68, 36, 'Neyiniz var, nasıl hüküm veriyorsunuz?'),
(5308, 68, 37, 'Yoksa size ait bir kitap var da onda mı okuyorsunuz?'),
(5309, 68, 38, 'O kitapta, "beğendiğiniz her şey sizindir" diye mi yazılı?'),
(5310, 68, 39, 'Yoksa, "ne hükmederseniz mutlaka sizindir" diye sizin lehinize olarak tarafımızdan verilmiş, kıyamet gününe kadar geçerli kesin sözler mi var?'),
(5311, 68, 40, 'Sor bakalım onlara, içlerinden ona kefil hangisi?'),
(5312, 68, 41, 'Yoksa ortakları mı var onların? Doğru iseler ortaklarını getirsinler.'),
(5313, 68, 42, 'O gün işler zorlaşır ve secdeye davet edilirler. Fakat güç yetiremezler.'),
(5314, 68, 43, 'Gözleri düşük bir halde kendilerini bir zillet kaplar. Oysa onlar sapasağlam iken de secdeye davet ediliyorlardı.'),
(5315, 68, 44, 'Bu sözü yalanlayanı bana bırak. Onları bilmedikleri yönden derece derece azaba yaklaştıracağız.'),
(5316, 68, 45, 'Onlara mühlet veriyorum. Doğrusu benim tuzağım sağlamdır.'),
(5317, 68, 46, 'Yoksa onlardan bir ücret istiyorsun da bu yüzden onlar ağır bir borç altında mı kalıyorlar?'),
(5318, 68, 47, 'Yoksa gayb onların yanlarında da onlar mı yazıyorlar?'),
(5319, 68, 48, 'Rabbinin hükmüne sabret, balık sahibi gibi olma. Hani o öfkeye boğulmuş da nida etmişti.'),
(5320, 68, 49, 'Rabbinden bir nimet yetişmiş olmasaydı, elbette kınanacak bir halde ıssız bir diyara atılacaktı.'),
(5321, 68, 50, 'Fakat Rabbi onu seçti de iyilerden kıldı.'),
(5322, 68, 51, 'O kafirler Kur\'ân\'ı işittikleri zaman neredeyse seni gözleri ile devireceklerdi. Bir de durmuşlar "o bir deli" diyorlar.'),
(5323, 68, 52, 'Halbuki o âlemler için bir öğüttür.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, '(Gerçekleşecek) Kıyamet!'),
(5325, 69, 2, 'Nedir, o Kıyamet?'),
(5326, 69, 3, 'Gerçekleşenin (Kıaymetin) ne olduğunu sen nerden bileceksin?'),
(5327, 69, 4, 'Semûd ve Âd, kapılarını çalacak olan o felaketi yalan saymışlardı.'),
(5328, 69, 5, 'Semûd kavmi korkunç bir sesle yok edildi.'),
(5329, 69, 6, 'Âd kavmi ise gürültülü ve azgın bir fırtına ile yok edildiler.'),
(5330, 69, 7, 'Allah o fırtınayı üzerlerine yedi gece sekiz gündüz musallat etmişti. Öyle ki, o kavmi içi boş hurma kütükleri gibi oracıkta yere serilmiş halde görürdün.'),
(5331, 69, 8, 'Bak şimdi görebilir misin onlardan bir kalıntı?'),
(5332, 69, 9, 'Firavun, ondan öncekiler ve altı üstüne getirilen beldeler de hep o hatayı işleyegeldiler.'),
(5333, 69, 10, 'Hep Rablerinin elçilerine karşı geldiler. O da onları pek şiddetli bir şekilde yakalayıverdi.'),
(5334, 69, 11, 'Kuşkusuz, sular kabarınca sizi gemide biz taşıdık.'),
(5335, 69, 12, 'Onu size bir ibret yapalım ve belleyici kulaklar bellesin diye.'),
(5336, 69, 13, 'Sûr\'a bir tek üfleme üflendiği,'),
(5337, 69, 14, 'Arz ve dağlar yerlerinden kaldırılıp şiddetle birbirine çarpılarak darmadağın olduğu zaman,'),
(5338, 69, 15, 'İşte o gün olacak olur.'),
(5339, 69, 16, 'O gün gök yarılmış, sarkmıştır.'),
(5340, 69, 17, 'Melekler de onun etrafındadır, O gün Rabbinin Arşını bunların da üstünde sekiz melek yüklenir.'),
(5341, 69, 18, 'O gün (hesap için Allah\'a) arz olunursunuz, öyle ki gizli bir haliniz kalmaz.'),
(5342, 69, 19, 'Kitabı sağından verilen, "alın okuyun kitabımı.."'),
(5343, 69, 20, '"Çünkü ben hesabıma kavuşacağımı sezmiştim" der.'),
(5344, 69, 21, 'Artık o hoşnut bir hayattadır.'),
(5345, 69, 22, 'Yüksek bir cennettedir.'),
(5346, 69, 23, 'Ki o cennetin meyveleri sarkmıştır.'),
(5347, 69, 24, '"Geçmiş günlerde yaptığınız işlerden ötürü afiyetle yeyin, için." (denir).'),
(5348, 69, 25, 'Kitabı sol tarafından verilen ise der ki: "Keşke kitabım verilmeseydi de,'),
(5349, 69, 26, 'Hesabımın ne olduğunu bilmeseydim,'),
(5350, 69, 27, 'Ne olurdu o ölüm, iş bitirici olsaydı.'),
(5351, 69, 28, 'Malım bana hiç fayda vermedi.'),
(5352, 69, 29, 'Gücüm de benden yok olup gitti."'),
(5353, 69, 30, '(Zebanilere şöyle denir): "Onu yakalayın da bağlayın."'),
(5354, 69, 31, '"Sonra cehenneme atın onu."'),
(5355, 69, 32, '"Sonra da boyu yetmiş arşın zincir içerisinde onu oraya sokun."'),
(5356, 69, 33, 'Çünkü o, büyük Allah\'a inanmıyordu.'),
(5357, 69, 34, 'Yoksula yedirmeye teşvik etmiyordu.'),
(5358, 69, 35, 'Bu sebeple bugün burada onun candan bir dostu yoktur.'),
(5359, 69, 36, 'Bir irinden başka yiyecek de yok.'),
(5360, 69, 37, 'Onu günahkârlardan başkası yemez.'),
(5361, 69, 38, 'Andolsun gördüklerinize,'),
(5362, 69, 39, 'Ve görmediklerinize..'),
(5363, 69, 40, 'Kuşkusuz Kur\'ân, şerefli bir peygamberin (Allah\'tan) getirdiği sözdür.'),
(5364, 69, 41, 'O bir şair sözü değildir, siz çok az inanıyorsunuz.'),
(5365, 69, 42, 'Bir kâhin sözü de değildir, ne de az düşünüyorsunuz!'),
(5366, 69, 43, 'O, âlemlerin Rabbi tarafından indirilmedir.'),
(5367, 69, 44, 'O, bize isnâden bazı sözler uydurmaya kalkışsaydı,'),
(5368, 69, 45, 'Elbette biz onu bundan dolayı kuvvetle yakalardık.'),
(5369, 69, 46, 'Sonra da onun şah damarını keser atardık.'),
(5370, 69, 47, 'O vakit sizden hiçbiriniz ona siper de olamazdınız.'),
(5371, 69, 48, 'O hiç kuşkusuz, takva sahipleri için unutulmayacak bir öğüttür.'),
(5372, 69, 49, 'Bununla beraber biz biliyoruz ki sizden inanmayanlar var.'),
(5373, 69, 50, 'Kuşkusuz bu Kur\'ân kafirler için bir pişmanlık vesilesidir.'),
(5374, 69, 51, 'Gerçekten o, şüphe götürmez bir bilgidir.'),
(5375, 69, 52, 'O halde, haydi tesbih et Rabbinin yüce ismiyle');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Bir isteyen, olacak azabı istedi.'),
(5377, 70, 2, 'Kâfirler için onu savacak yok.'),
(5378, 70, 3, 'O, derece ve makamların sahibi Allah\'tandır.'),
(5379, 70, 4, 'Melekler ve Ruh miktarı ellibin yıl süren bir gün içinde ona çıkar.'),
(5380, 70, 5, 'O halde güzel bir sabır ile sabret.'),
(5381, 70, 6, 'Çünkü onlar onu uzak görürler.'),
(5382, 70, 7, 'Biz ise onu yakın görüyoruz.'),
(5383, 70, 8, 'O gün gök erimiş bir maden gibi olur.'),
(5384, 70, 9, 'Dağlar da atılmış renkli yün gibi olur.'),
(5385, 70, 10, 'Dost dostun halini soramaz.'),
(5386, 70, 11, 'Birbirlerine gösterilirler. Suçlu o günün azabından kurtulmak için fidye vermek ister; oğullarını,'),
(5387, 70, 12, 'Eşini ve kardeşini,'),
(5388, 70, 13, 'Kendisini barındıran, içinde yetiştiği tüm ailesini,'),
(5389, 70, 14, 'Ve yeryüzünde bulunanların hepsini ki, tek kendini kurtarabilsin.'),
(5390, 70, 15, 'Hayır, o alevlenen bir ateştir.'),
(5391, 70, 16, 'Derileri kavurur, soyar.'),
(5392, 70, 17, 'Çağırır, sırtını dönüp gideni,'),
(5393, 70, 18, 'Mal toplayıp kasada yığanı,'),
(5394, 70, 19, 'Doğrusu insan dayanıksız ve huysuz yaratılmıştır.'),
(5395, 70, 20, 'Kendisine kötülük dokundu mu sızlanır.'),
(5396, 70, 21, 'Kendisine hayır dokundu mu cimrilik eder.'),
(5397, 70, 22, 'Ancak namaz kılanlar bunun dışındadır.'),
(5398, 70, 23, 'Onlar ki namazlarını sürekli kılarlar.'),
(5399, 70, 24, 'Onların mallarında belli bir hak vardır,'),
(5400, 70, 25, 'Hem isteyen için, hem de istemekten utanan yoksul için.'),
(5401, 70, 26, 'Onlar ki ceza gününü tasdik ederler.'),
(5402, 70, 27, 'Rablerinin azabından korkarlar.'),
(5403, 70, 28, 'Çünkü Rablerinin azabından emin olunmaz.'),
(5404, 70, 29, 'Onlar ki ırzlarını korurlar.'),
(5405, 70, 30, 'Ancak zevcelerine ve cariyelerine karşı hariç. Çünkü onlara yaklaştıklarında kınanmazlar.'),
(5406, 70, 31, 'Bundan ötesini isteyenler, var ya işte onlar haddi aşanlardır.'),
(5407, 70, 32, 'Onlar emanetlerini ve ahitlerini gözetirler.'),
(5408, 70, 33, 'Şahitliklerinde dürüsttürler.'),
(5409, 70, 34, 'Namazlarına devam ederler.'),
(5410, 70, 35, 'İşte bunlar cennetlerde ağırlanırlar.'),
(5411, 70, 36, 'Şimdi ne oluyor o inkâr edenlere ki, sana doğru boyunlarını uzatarak koşuyorlar:'),
(5412, 70, 37, 'Sağdan ve soldan bölük bölük.'),
(5413, 70, 38, 'Onlardan herbiri, bir nimet cennetine sokulacağını mı umuyor?'),
(5414, 70, 39, 'Hayır, biz onları bildikleri şeyden yarattık.'),
(5415, 70, 40, 'Artık o doğuların ve batıların Rabbine yemine ne gerek, elbette bizim gücümüz yeter.'),
(5416, 70, 41, 'Onları kendilerinden daha hayırlı olanlarla değiştirebiliriz ve bizim önümüze geçilmez.'),
(5417, 70, 42, 'O halde bırak onları, kendilerine vaad edilen günlerine kavuşuncaya kadar dalıp oynayadursunlar.'),
(5418, 70, 43, 'O gün kabirlerden hızlı hızlı çıkacaklar, sanki putlara gidiyorlarmış gibi fırlayacaklar.'),
(5419, 70, 44, 'Gözleri düşük, kendilerini bir alçaklık saracak da saracak. İşte onlara vaad edilen gün, o gündür.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'Gerçekten biz Nûh\'u kavmine gönderdik, "kavmine acı bir azap gelmezden önce onları uyar" diye.'),
(5421, 71, 2, 'Dedi ki, "ey kavmim! Gerçekten ben size açık bir uyarıcıyım".'),
(5422, 71, 3, 'Şöyle ki, "Allah\'a kulluk edin, ondan korkun ve bana itaat edin."'),
(5423, 71, 4, '"Günahlarınızı bağışlasın ve sizi belli bir süreye kadar ertelesin. Kuşkusuz Allah\'ın takdir ettiği süre gelince ertelenmez. Eğer bilseydiniz.." (inanırdınız).'),
(5424, 71, 5, 'Nûh dedi ki: "Ey Rabbim! Ben kavmimi gece gündüz davet ettim."'),
(5425, 71, 6, '"Fakat benim çağırmam, onların sadece kaçmalarını artırdı."'),
(5426, 71, 7, '"Ben onları senin bağışlaman için her davet ettiğimde, onlar parmaklarını kulaklarına tıkadılar, elbiselerine büründüler, ısrar ettiler, kibirlendikçe kibirlendiler."'),
(5427, 71, 8, '"Sonra ben onları açık açık çağırdım."'),
(5428, 71, 9, '"Sonra hem ilan ederek söyledim onlara, hem gizli gizli. "'),
(5429, 71, 10, '"Gelin, dedim, Rabbinizin sizi bağışlamasını isteyin. Çünkü o çok bağışlayıcıdır."'),
(5430, 71, 11, '"Üzerinize gökten bol yağmur yağdırsın."'),
(5431, 71, 12, '"Mallar ve oğullar vererek sizin imdadınıza koşsun. Sizin için bahçeler yapsın, ırmaklar yapsın."'),
(5432, 71, 13, '"Niçin siz Allah\'a bir vakar yakıştıramıyorsunuz?"'),
(5433, 71, 14, '"Oysa o sizi aşama aşama yaratmıştır."'),
(5434, 71, 15, '"Görmediniz mi Allah yedi göğü uygun tabakalar halinde nasıl yaratmış?"'),
(5435, 71, 16, 'Ve Ay\'ı bunların içinde bir nur yapmış, güneşi de bir lamba kılmış.'),
(5436, 71, 17, 'Allah sizi yerden bir bitki bitirir gibi bitirdi.'),
(5437, 71, 18, 'Sonra sizi tekrar oraya geri çevirecek ve tekrar çıkaracaktır.'),
(5438, 71, 19, 'Allah sizin için yeri bir yaygı yapmıştır.'),
(5439, 71, 20, 'Ki, ondan açılan geniş geniş yollarda gidesiniz.'),
(5440, 71, 21, 'Nûh dedi ki: "Ey Rabbim! Onlar bana isyan ettiler; malı ve çocuğu hüsrandan başka bir şeyini artırmayan kimsenin ardına düştüler."'),
(5441, 71, 22, '"Büyük büyük tuzaklar kurdular."'),
(5442, 71, 23, 'Dediler ki: "Sakın tanrılarınızı bırakmayın, ne Vedd\'i, ne Suva\'ı ve ne de Yeğus\'u, Yeûk\'u ve Nesr\'i."'),
(5443, 71, 24, 'Çok kişiyi yoldan saptırdılar. Sen de o zalimlerin sadece şaşkınlıklarını artır.'),
(5444, 71, 25, 'Hatalarından dolayı boğuldular, ateşe sokuldular, kendilerine Allah\'a karşı yardımcılar da bulamadılar.'),
(5445, 71, 26, 'Nûh dedi ki: "Yeryüzünde kafirlerden bir tek kişi bırakma."'),
(5446, 71, 27, '"Zira sen onları bırakırsan kullarını yoldan çıkarırlar ve sadece ahlâksız ve kâfir çocuklar doğururlar."'),
(5447, 71, 28, '"Ey Rabbim! Bana, babama, anama, mümin olarak evime girene ve bütün inanmış erkek ve kadınlara mağfiret buyur. Zalimlerin de sadece helakini artır."');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'Deki: Hakikat bir takım cinnin Kur\'ân dinleyip de şöyle dedikleri bana vahyedildi. Şüphesiz biz, hayret verici bir Kur\'ân dinledik.'),
(5449, 72, 2, 'O Kur\'ân hidayete erdiriyor, biz de ona iman ettik. Rabbimize hiçbir şeyi ortak koşmayacağız.'),
(5450, 72, 3, 'Doğrusu, Rabbimizin şanı çok yüksektir. Ne bir arkadaş edinmiştir, ne de bir çocuk.'),
(5451, 72, 4, 'Meğer bizim beyinsiz (İblis), Allah hakkında saçma şeyler söylüyormuş.'),
(5452, 72, 5, 'Doğrusu biz insanları ve cinleri Allah\'a karşı asla yalan söylemez sanmışız.'),
(5453, 72, 6, 'Doğrusu insanlardan bazı erkekler, cinlerden bazı erkeklere sığınırlardı da onların şımarıklıklarını artırırlardı.'),
(5454, 72, 7, 'Doğrusu onlar sizin zannettiğiniz gibi, zannetmişlerdi ki, Allah asla kimseyi Peygamber göndermeyecek.'),
(5455, 72, 8, '(Cinler, dediler ki): "Biz göğe dokunduk, onu kuvvetli bekçiler ve alevlerle dolu bulduk."'),
(5456, 72, 9, '"Doğrusu biz göğün bazı mevkilerinde dinlemek için otururduk. Fakat şimdi her kim dinleyecek olursa kendini gözetleyen parlak bir alev buluyor."'),
(5457, 72, 10, '"Doğrusu biz bilmiyoruz, yeryüzündekilere kötülük mü murat edildi, yoksa Rableri onlara bir hayır mı diledi?"'),
(5458, 72, 11, 'Doğrusu bizler; bizden iyi olanlar da var, olmayanlar da var. Biz çeşitli yollara ayrılmışız.'),
(5459, 72, 12, '"Doğrusu biz anladık ki, Allah\'ı yerde acze düşürmemize imkân yok. Kaçmakla da O\'nu asla âciz bırakamayacağız."'),
(5460, 72, 13, '"Doğrusu biz o hidayet rehberini dinlediğimizde ona iman ettik. Kim Rabbine inanırsa, ne hakkının eksik verilmesinden korkar, ne de kendisine kötülük edilmesinden."'),
(5461, 72, 14, '"Ve biz, bizlerden müslümanlar da var, hak yoldan sapanlar da var. Müslüman olanlar, işte onlar doğru yolu arayanlardır."'),
(5462, 72, 15, 'Ama yoldan çıkanlar, işte onlar cehenneme odun olmuşlardır.'),
(5463, 72, 16, 'Onlar gerçekten o yol üzere dosdoğru gitselerdi, elbette kendilerine bol bir su verirdik.'),
(5464, 72, 17, 'Ki onları onunla sınayalım. Kim Rabbini anmaktan yüz çevirirse, Rabbi onu gittikçe yükselen bir azaba sokar.'),
(5465, 72, 18, 'Mescitler kuşkusuz Allah\'ındır. O halde Allah ile birlikte kimseye yalvarmayın.'),
(5466, 72, 19, 'Allah\'ın kulu (Hz. Peygamber) kalkmış O\'na dua ederken, neredeyse (cinler) onun etrafında keçe gibi birbirlerine geçeceklerdi.'),
(5467, 72, 20, 'De ki: "Ben ancak Rabbime dua eder ve O\'na hiçbir şeyi ortak koşmam"'),
(5468, 72, 21, 'De ki, "Haberiniz olsun, ben size kendiliğimden ne bir zarar verebilirim, ne de bir yol gösterebilirim."'),
(5469, 72, 22, 'De ki, "Allah\'tan beni kimse kurtaramaz ve ben O\'ndan başka bir sığınacak bulamam."'),
(5470, 72, 23, '"Benim yapabileceğim, sadece Allah\'tan size duyuru yapmak ve O\'nun elçilik görevlerini yerine getirmektir." Artık kim Allah\'a ve onun elçisine baş kaldırırsa, ona içinde ebedi kalacakları cehennem ateşi vardır.'),
(5471, 72, 24, 'Kendilerine vaad edilen şeyi gördükleri zaman, kimin yardımcısının en zayıf ve en az olduğunu bileceklerdir.'),
(5472, 72, 25, 'De ki: "Ben bilmem, o size vaad edilen şey yakın mı, yoksa Rabbim onun için uzun bir süre mi koyar.."'),
(5473, 72, 26, 'O bütün gaybı bilir. Fakat gaybını hiç kimseye açmaz.'),
(5474, 72, 27, 'Ancak seçtiği elçiye açar. Çünkü onun önünden ve ardından gözetleyiciler salar.'),
(5475, 72, 28, 'Bilsin diye ki, onlar Rablerinin elçiliklerini yerine getirmişlerdir. Allah onlarda bulunan her şeyi kuşatmış ve her şeyi bir bir saymıştır');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Ey örtünen! (Peygamber)'),
(5477, 73, 2, 'Gecenin birazı hariç olmak üzere geceleyin kalk (namaz kıl).'),
(5478, 73, 3, 'Gecenin yarısında kalk, yahut yarısından biraz eksilt.'),
(5479, 73, 4, 'Veya bunu artır ve ağır ağır Kur\'ân oku.'),
(5480, 73, 5, 'Doğrusu biz, senin üzerine ağır bir söz bırakacağız (Kur\'an vahyedeceğiz).'),
(5481, 73, 6, 'Çünkü gece kalkışı hem daha etkili, hem de söz bakımından daha sağlamdır.'),
(5482, 73, 7, 'Çünkü gündüz senin için uzun bir meşguliyet vardır.'),
(5483, 73, 8, 'Rabbinin adını an ve bütün gönlünle ona yönel.'),
(5484, 73, 9, 'O, doğunun ve batının Rabbidir. Ondan başka tanrı yoktur. O halde yalnız O\'nu vekil tut.'),
(5485, 73, 10, 'Başkalarının diyeceklerine sabret, güzellikle onlardan ayrıl.'),
(5486, 73, 11, 'O yalanlayıcı zevk ve refah sahiplerini bana bırak, onlara biraz mühlet ver.'),
(5487, 73, 12, 'Zira bizim yanımızda bukağılar var, bir cehennem var.'),
(5488, 73, 13, 'Boğaza duran bir yiyecek, elem verici bir azap var.'),
(5489, 73, 14, 'O gün yer ve dağlar sarsılacak, dağlar erimiş bir kum yığınına dönecek.'),
(5490, 73, 15, 'Doğrusu biz size tanıklık edecek bir elçi gönderdik. Nitekim Firavun\'a da bir elçi göndermiştik.'),
(5491, 73, 16, 'Firavun o elçiye isyan etmişti. Biz de onu ağır bir yakalayışla yakaladık.'),
(5492, 73, 17, 'Peki inkâr ederseniz, çocukları ihtiyarlatacak o günden (kıyamet gününden) kendinizi nasıl kurtaracaksınız?'),
(5493, 73, 18, 'O günün dehşetinden gök yarılır. Allah\'ın sözü kesinlikle gerçekleşmiştir.'),
(5494, 73, 19, 'İşte bu bir öğüttür. Artık dileyen Rabbine bir yol tutar.'),
(5495, 73, 20, 'Rabbin, senin gecenin üçte ikisinden daha azında, yarısında ve üçte birinde kalktığını, seninle beraber bulunanlardan bir topluluğun da böyle yaptığını biliyor. Gece ve gündüzü Allah takdir eder. O, sizin onu sayamayacağınızı bildi de sizi affetti. Bundan böyle Kur\'ân\'dan size ne kolay gelirse okuyun. Allah, içinizden hastalar, yeryüzünde gezip Allah\'ın lütfunu arayan başka kimseler ve Allah yolunda savaşan daha başka insanlar olacağını bilmiştir. Onun için Kur\'ân\'dan kolayınıza geldiği kadar okuyun, namazı kılın, zekatı verin ve Allah\'a güzel bir borç verin (Hayırlı işlere mal sarfedin). Kendiniz için gönderdiğiniz her iyiliği, Allah katında daha hayırlı ve sevapça daha büyük olarak bulacaksınız. Allah\'tan bağış dileyin. Kuşkusuz Allah bağışlayandır, merhamet edendir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Ey örtüsüne bürünen (Peygamber)!'),
(5497, 74, 2, 'Kalk artık uyar.'),
(5498, 74, 3, 'Sadece Rabbini yücelt.'),
(5499, 74, 4, 'Elbiseni temizle.'),
(5500, 74, 5, 'Pislikten sakın.'),
(5501, 74, 6, 'Yaptığını çok görerek başa kakma.'),
(5502, 74, 7, 'Rabbin için sabret.'),
(5503, 74, 8, 'O sûra üflendiği zaman,'),
(5504, 74, 9, 'İşte o gün pek zorlu bir gündür.'),
(5505, 74, 10, 'Kâfirler için hiç kolay değildir.'),
(5506, 74, 11, 'Tek olarak yarattığım o kimseyi bana bırak.'),
(5507, 74, 12, 'Hem ona bol servet verdim.'),
(5508, 74, 13, 'Hem göz önünde oğullar verdim.'),
(5509, 74, 14, 'Hem ona büyük imkânlar sağladım.'),
(5510, 74, 15, 'Sonra da şiddetle arzu eder ki daha da artırayım.'),
(5511, 74, 16, 'Hayır, çünkü o bizim âyetlerimize karşı bir inatçı kesildi.'),
(5512, 74, 17, 'Ben onu dimdik bir yokuşa sardıracağım.'),
(5513, 74, 18, 'Çünkü o bir düşündü, ölçtü, biçti.'),
(5514, 74, 19, 'Kahrolası nasıl da ölçtü, biçti.'),
(5515, 74, 20, 'Yine kahrolası, nasıl ölçtü biçti.'),
(5516, 74, 21, 'Sonra baktı.'),
(5517, 74, 22, 'Sonra kaşını çattı, surat astı.'),
(5518, 74, 23, 'Sonra arkasını döndü ve büyüklük tasladı.'),
(5519, 74, 24, '"Bu, dedi, başka değil öğretilegelen bir sihirdir."'),
(5520, 74, 25, '"Bu, sadece bir insan sözüdür."'),
(5521, 74, 26, 'Ben onu Sekar\'a (cehenneme) sokacağım.'),
(5522, 74, 27, 'Bilir misin sen, nedir o sekar?'),
(5523, 74, 28, 'Ne geriye bir şey kor, ne bırakır.'),
(5524, 74, 29, 'Durmadan derileri kavurur.'),
(5525, 74, 30, 'Üzerinde ondokuz (melek) vardır.'),
(5526, 74, 31, 'Biz o ateşin muhafızlarını hep melekler yaptık. Bunların sayılarını da ancak kâfirler için bir imtihan kıldık ki, kendilerine kitap verilenler kesin bilgi edinsinler, iman edenlerin de imanı artsın. Kendilerine kitap verilenler ve müminler şüpheye düşmesinler. Kalplerinde hastalık bulunanlarla kâfirler de: "Allah bu misalle ne demek istedi?" desinler. İşte böyle, Allah dilediğini şaşırtır, dilediğini de yola getirir. Rabbinin ordularını ancak Rabbin bilir. Bu, insanlar için uyarıdan başka bir şey değildir.'),
(5527, 74, 32, 'Hayır, andolsun aya,'),
(5528, 74, 33, 'Döndüğü an o geceye,'),
(5529, 74, 34, 'Ve açtığı sıra o sabaha.'),
(5530, 74, 35, 'Kuşkusuz o Sekar, büyük belalardan biridir.'),
(5531, 74, 36, 'Uyarmak için insanları..'),
(5532, 74, 37, 'İçinizden ileri gitmek veya geri kalmak isteyen kimseleri..'),
(5533, 74, 38, 'Her nefis kendi kazancına bağlıdır.'),
(5534, 74, 39, 'Ancak amel defterleri sağından verilenler hariç.'),
(5535, 74, 40, 'Onlar cennettedirler, sorup dururlar.'),
(5536, 74, 41, 'Suçluların durumunu.'),
(5537, 74, 42, '"Nedir sizi Sekar\'a sokan?" diye.'),
(5538, 74, 43, 'Suçlular der ki: "Biz namaz kılanlardan değildik."'),
(5539, 74, 44, '"Yoksula da yedirmezdik."'),
(5540, 74, 45, '"Boş şeylere dalanlarla dalar giderdik."'),
(5541, 74, 46, '"Ceza gününü yalanlardık."'),
(5542, 74, 47, '"Nihayet bize ölüm gelip çattı."'),
(5543, 74, 48, 'Artık onlara şefaatçilerin şefaatı fayda vermez.'),
(5544, 74, 49, 'Şimdi o Kur\'ân\'dan yüz çevirirlerken ne mazeretleri var?'),
(5545, 74, 50, 'Sanki onlar ürkmüş yaban eşekleri.'),
(5546, 74, 51, 'Arslandan kaçmaktalar.'),
(5547, 74, 52, 'Hayır, onlardan her kişi kendisine açılmış sayfalar verilmesini istiyor.'),
(5548, 74, 53, 'Yok, yok onlar ahiretten korkmuyorlar.'),
(5549, 74, 54, 'Hayır, hayır, O kur\'ân kuşkusuz bir öğüttür.'),
(5550, 74, 55, 'Dileyen onu düşünür.'),
(5551, 74, 56, 'Bununla beraber Allah dilemedikçe onlar öğüt alamazlar. Koruyacak da O\'dur, bağışlayacak da.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Hayır, yemin ederim o kıyamet gününe.'),
(5553, 75, 2, 'Yine hayır, yemin ederim o sürekli kendini kınayan nefse.'),
(5554, 75, 3, 'İnsan, kendisinin kemiklerini bir araya toplayamayacağımızı mı sanıyor?'),
(5555, 75, 4, 'Evet, bizim onun parmak uçlarını bile aynen eski haline getirmeye gücümüz yeter.'),
(5556, 75, 5, 'Fakat insan günahı devam ettirmek ister.'),
(5557, 75, 6, 'O kıyamet günü ne zaman? diye sorar.'),
(5558, 75, 7, 'Ne zaman ki o göz şimşek çakar,'),
(5559, 75, 8, 'Ay tutulur,'),
(5560, 75, 9, 'Güneş ve ay toplanır,'),
(5561, 75, 10, 'İşte o gün insan, "kaçacak yer neresi?" der.'),
(5562, 75, 11, 'Hayır, hayır, yok bir siper.'),
(5563, 75, 12, 'O gün varılıp durulacak yer, ancak Rabbinin huzurudur.'),
(5564, 75, 13, 'O gün insana, yapıp öne sürdüğü ve geri bıraktığı ne varsa bildirilir.'),
(5565, 75, 14, 'Doğrusu insan kendi nefsini görür,'),
(5566, 75, 15, 'Bir takım özürler ortaya atsa da.'),
(5567, 75, 16, 'Onu hemen okumak için dilini depretme.'),
(5568, 75, 17, 'Kuşkusuz onu toplamak ve okumak bize aittir.'),
(5569, 75, 18, 'O halde biz onu okuduğumuz zaman sen onun okunuşunu takip et.'),
(5570, 75, 19, 'Sonra onu açıklamak da bize aittir.'),
(5571, 75, 20, 'Hayır, siz peşin olanı (dünyayı) seviyorsunuz da'),
(5572, 75, 21, 'Ahireti bırakıyorsunuz.'),
(5573, 75, 22, 'Yüzler var ki o gün ışıl ışıl parlar.'),
(5574, 75, 23, 'Rabbine bakar.'),
(5575, 75, 24, 'Yüzler de var ki o gün asıktır.'),
(5576, 75, 25, 'Anlar ki kendisine belkıran (bel kemiklerini kıran belalı bir iş) yapılır.'),
(5577, 75, 26, 'Hayır hayır, ne zaman ki can köprücük kemiklerine dayanır,'),
(5578, 75, 27, '"Tedavi edebilecek kimdir?" denilir.'),
(5579, 75, 28, 'Can çekişen bunun o ayrılık anı olduğunu anlar.'),
(5580, 75, 29, 'Bacak bacağa dolaşır..'),
(5581, 75, 30, 'İşte o gün sevk, ancak Rabbinedir.'),
(5582, 75, 31, 'Fakat o, ne sadaka verdi, ne namaz kıldı.'),
(5583, 75, 32, 'Fakat yalanladı ve döndü.'),
(5584, 75, 33, 'Sonra da çalım sata sata ailesine gitti.'),
(5585, 75, 34, 'Gerektir o bela sana, gerek.'),
(5586, 75, 35, 'Evet, gerektir o bela sana gerek.'),
(5587, 75, 36, 'İnsan başıboş bırakılacağını mı sanır?'),
(5588, 75, 37, 'O, dökülen erlik suyundan bir damla (sperm) değil miydi?'),
(5589, 75, 38, 'Sonra bir aleka (embriyon) oldu da Rabbi onu biçime koydu, sonra şekil verdi.'),
(5590, 75, 39, 'Ondan da iki cinsi; erkek ve dişiyi var etti.'),
(5591, 75, 40, 'Peki, bunu yapanın ölüleri diriltmeye gücü yetmez mi?');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'Gerçekten insan üzerine dehirden (zamandan) öyle bir müddet geldi ki o zaman o, anılmaya değer bir şey değildi.'),
(5593, 76, 2, 'Doğrusu biz insanı, imtihan etmek için karışık bir nutfeden (erkek ve kadın sularından) yarattık da onu işitici, görücü yaptık.'),
(5594, 76, 3, 'Kuşkusuz biz ona yolu gösterdik; ister şükredici olsun, ister nankör.'),
(5595, 76, 4, 'Çünkü biz, kâfirler için zincirler, demir halkalar ve alevli bir ateş hazırlamışızdır.'),
(5596, 76, 5, 'Kuşkusuz iyiler de karışımı kâfûr olan dolgun bir kadehten içerler.'),
(5597, 76, 6, 'Bir kaynak ki ondan Allah\'ın kulları içerler, güzel yollar açarak akıtırlar onu.'),
(5598, 76, 7, 'O kullar adaklarını yerine getirirler ve fenalığı salgın (olan) bir günden korkarlar.'),
(5599, 76, 8, 'Düşküne, yetime ve esire seve seve yemek yedirirler.'),
(5600, 76, 9, '"Size sırf Allah rızası için yemek yediriyoruz. Sizden ne bir karşılık, ne de bir teşekkür bekliyoruz."'),
(5601, 76, 10, '"Biz sert ve belalı bir günde Rabbimizden korkarız." derler.'),
(5602, 76, 11, 'Allah da onları o günün fenalığından korur, yüzlerine parlaklık, gönüllerine sevinç verir.'),
(5603, 76, 12, 'Sabırlarına karşılık onlara bir cennet ve ipekten elbiseler verir.'),
(5604, 76, 13, 'Orada donatılmış koltuklar üzerine dayanmışlardır: Orada ne yakıcı güneş görürler, ne de şiddetli soğuk.'),
(5605, 76, 14, 'Üzerlerine cennet gölgeleri sarkmış, meyveleri bol bol önlerine konmuştur.'),
(5606, 76, 15, 'Yanlarında gümüşten kaplar, billur kupalar dolaştırılır.'),
(5607, 76, 16, 'Gümüşten öyle kadehler ki onları türlü türlü biçimlere koymuşlardır.'),
(5608, 76, 17, 'Onlara orada bir dolu kadeh sunulur ki, karışımı zencefildir.'),
(5609, 76, 18, 'Bu orada bir pınardır ki, adına "selsebil" derler.'),
(5610, 76, 19, 'Etraflarında ölümsüz hizmetçiler dolaşır, onları görünce saçılmış inciler sanırsın.'),
(5611, 76, 20, 'Orada nereye baksan bir nimet ve pek büyük bir mülk görürsün.'),
(5612, 76, 21, 'Üstlerinde zarif ve yeşil, kalın ipekten bir elbise vardır. Gümüş bileziklerle süslenmişlerdir. Rableri onlara temiz bir içecek içirmiştir.'),
(5613, 76, 22, '(Onlara şöyle denir): "İşte bu sizin bir mükâfatınızdı. Gayretiniz karşılığını bulmuştur."'),
(5614, 76, 23, 'Kur\'ân\'ı sana kısım kısım biz indirdik biz.'),
(5615, 76, 24, 'O halde Rabbinin hüküm vermesi için sabret. Onlardan hiçbir günahkâra yahut nanköre itaat etme.'),
(5616, 76, 25, 'Sabahakşam Rabbinin ismini an.'),
(5617, 76, 26, 'Gecenin bir bölümünde de O\'na secde et (akşam ve yatsı namazlarını kıl). Hem de O\'nu uzun bir gece tesbih et (teheccüd namazı kıl).'),
(5618, 76, 27, 'Çünkü onlar bu dünyayı seviyorlar ve önlerindeki ağır bir günü arkaya atıyorlar.'),
(5619, 76, 28, 'Onları biz yarattık ve mafsallarını sımsıkı bağladık. Dilediğimiz vakit de kılıklarını değiştiririz.'),
(5620, 76, 29, 'İşte bu bir öğüttür. Dileyen Rabbine giden yolu tutar.'),
(5621, 76, 30, 'Allah dilemedikçe siz dileyemezsiniz. Kuşkusuz Allah, bilendir, hüküm ve hikmet sahibidir.'),
(5622, 76, 31, 'Allah dilediğini rahmetine sokar. Zalimlere ise, acıklı bir azap hazırlamıştır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Andolsun birbiri ardınca gönderilenlere,'),
(5624, 77, 2, 'Büküp devirenlere,'),
(5625, 77, 3, 'Yaydıkça yayanlara,'),
(5626, 77, 4, 'Seçip ayıranlara,'),
(5627, 77, 5, 'Bir öğüt bırakanlara,'),
(5628, 77, 6, 'Gerek özür için olsun, gerek uyarı için,'),
(5629, 77, 7, 'Herhalde size vaad olunan kesinlikle olacaktır.'),
(5630, 77, 8, 'Hani o yıldızlar silindiği zaman,'),
(5631, 77, 9, 'Gök yarıldığı zaman,'),
(5632, 77, 10, 'Dağlar savrulduğu zaman,'),
(5633, 77, 11, 'Elçiler, tayin edilen vakitlerine erdirildikleri zaman,'),
(5634, 77, 12, 'Bunlar hangi güne ertelendiler?'),
(5635, 77, 13, 'Hüküm gününe..'),
(5636, 77, 14, 'Bildin mi, nedir o hüküm günü?'),
(5637, 77, 15, 'O gün yalanlayanların vay haline!'),
(5638, 77, 16, 'Biz, öncekileri helak etmedik mi?'),
(5639, 77, 17, 'Sonra geridekileri de onlara katarız.'),
(5640, 77, 18, 'Biz suçlulara böyle yaparız.'),
(5641, 77, 19, 'O gün yalanlayanların vah haline!'),
(5642, 77, 20, 'Biz sizi âdi bir sudan yaratmadık mı?'),
(5643, 77, 21, 'Onu sağlam bir yerde oturttuk.'),
(5644, 77, 22, 'Belli bir süreye kadar.'),
(5645, 77, 23, 'Demek ki biçimlendirmişiz. Ne güzel biçimlendireniz biz.'),
(5646, 77, 24, 'O gün yalanlayanların vay haline!'),
(5647, 77, 25, 'Yeryüzünü bir tokat (toplanma yeri) yapmadık mı?'),
(5648, 77, 26, 'Gerek diriler, gerekse ölüler için.'),
(5649, 77, 27, 'Orada yüksek yüksek dağlar oturtup da size bir tatlı su sunmadık mı?'),
(5650, 77, 28, 'O gün yalanlayanların vay haline!'),
(5651, 77, 29, '(Kıyameti yalanlayanlara şöyle denir): "Haydin gidin o yalanladığınız şeye doğru."'),
(5652, 77, 30, '"Haydi gidin o üç çatallı gölgeye (cehenneme)."'),
(5653, 77, 31, 'O, ne gölgelendirir, ne alevden korur.'),
(5654, 77, 32, 'O, saray gibi kıvılcımlar atar.'),
(5655, 77, 33, 'Sanki o kıvılcımlar, sarı sarı (erkek deve sürüleridir).'),
(5656, 77, 34, 'O gün yalanlayanların vay haline!'),
(5657, 77, 35, 'Bugün, konuşamıyacakları gündür.'),
(5658, 77, 36, 'Kendilerine izin de verilmez ki, özür beyan etsinler.'),
(5659, 77, 37, 'O gün yalanlayanların vay haline!'),
(5660, 77, 38, 'Bu, işte o hüküm günüdür. Sizi ve öncekileri bir araya topladık.'),
(5661, 77, 39, 'Bir hileniz varsa beni atlatın.'),
(5662, 77, 40, 'O gün yalanlayanların vay haline!'),
(5663, 77, 41, 'Kuşkusuz takva sahipleri gölgeler altında ve pınar başlarındadır.'),
(5664, 77, 42, 'Canlarının çektiğinden türlü meyveler arasındadırlar.'),
(5665, 77, 43, '(Onlara): "Yaptıklarınıza karşılık afiyetle yiyin, için" (denir).'),
(5666, 77, 44, 'İşte biz güzel amel işleyenleri böyle mükafatlandırırız.'),
(5667, 77, 45, 'O gün yalanlayanların vay haline!'),
(5668, 77, 46, 'Yiyin, zevklenin biraz, çünkü siz suçlularsınız.'),
(5669, 77, 47, 'O gün yalanlayanların vay haline!'),
(5670, 77, 48, 'Onlara: "Rüku edin" denildiği zaman etmezler.'),
(5671, 77, 49, 'Vay haline o gün yalanlayanların!'),
(5672, 77, 50, 'Artık bundan (Kur\'an\'dan) sonra hangi söze inanacaklar?');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Birbirlerine neyi soruyorlar?'),
(5674, 78, 2, 'O büyük haberden (kıyametten) mi?'),
(5675, 78, 3, 'Ki onlar onda ayrılığa düşmektedirler.'),
(5676, 78, 4, 'Hayır, ilerde bilecekler.'),
(5677, 78, 5, 'Hayır hayır, ilerde bilecekler.'),
(5678, 78, 6, 'Biz yeryüzünü bir beşik yapmadık mı?'),
(5679, 78, 7, 'Dağları da birer kazık kılmadık mı?'),
(5680, 78, 8, 'Sizleri çift çift yarattık.'),
(5681, 78, 9, 'Uykunuzu bir dinlenme yaptık.'),
(5682, 78, 10, 'Geceyi bir örtü yaptık.'),
(5683, 78, 11, 'Gündüzü de bir geçim zamanı yaptık.'),
(5684, 78, 12, 'Üstünüze yedi sağlam bina (gök) çattık.'),
(5685, 78, 13, 'İçlerine ışık saçan bir kandil astık.'),
(5686, 78, 14, 'Yoğunlaşmış bulutlardan şarıl şarıl bir su indirdik.'),
(5687, 78, 15, 'Onunla taneler ve otlar çıkaralım diye.'),
(5688, 78, 16, 'Ve sarmaş dolaş bağlar bahçeler (çıkaralım diye).'),
(5689, 78, 17, 'Kuşkusuz o hüküm günü kararlaştırılmış bir vakit olmuştur.'),
(5690, 78, 18, 'O gün Sûr\'a üflenir, bölük bölük gelirsiniz.'),
(5691, 78, 19, 'Gök de açılmış, kapı kapı olmuştur.'),
(5692, 78, 20, 'Dağlar yürütülmüş, serap olmuştur.'),
(5693, 78, 21, 'Kuşkusuz Cehennem gözetleme yeri olmuştur.'),
(5694, 78, 22, 'Azgınlar için son varılacak yer olmuştur.'),
(5695, 78, 23, 'Orada çağlarca kalacaklardır.'),
(5696, 78, 24, 'Orada ne bir serinlik tadacaklar, ne de içecek bir şey.'),
(5697, 78, 25, 'Ancak bir kaynar su ve irin (içecekler).'),
(5698, 78, 26, 'Bir ceza ki tam yaptıklarına uygun.'),
(5699, 78, 27, 'Çünkü onlar hiçbir hesap ummazlardı.'),
(5700, 78, 28, 'Âyetlerimizi yalanlaya yalanlaya tam bir yalancı olmuşlardı.'),
(5701, 78, 29, 'Biz ise herşeyi sayıp bir kitaba geçirmişiz.'),
(5702, 78, 30, '(Onlara): "Şimdi tadın (cezanızı). Artık size azabınızı artırmaktan başka bir şey yapmayacağız" (denir).'),
(5703, 78, 31, 'Kuşkusuz takva sahipleri için bir kurtuluş var.'),
(5704, 78, 32, 'Bahçeler var, bağlar var.'),
(5705, 78, 33, 'Memeleri tomurcuklanmış yaşıt kızlar var.'),
(5706, 78, 34, 'Dopdolu kadehler var.'),
(5707, 78, 35, 'Orada ne boş bir söz işitirler, ne de bir yalan.'),
(5708, 78, 36, '(Bunlar) Rabbinden yeterli bir bağış olarak (verilir).'),
(5709, 78, 37, 'O, göklerin, yerin ve bu ikisi arasındakilerin Rabbidir. Rahmân\'dır. Hiç kimse ondan bir hitaba mâlik olamaz.'),
(5710, 78, 38, 'O gün Ruh ve melekler sıra sıra dururlar. Rahmân\'ın izin verdikleri dışında hiç kimse konuşamaz. İzin verilen de doğruyu söyler.'),
(5711, 78, 39, 'İşte bu hak gündür. Artık dileyen Rabbine bir yol tutar.'),
(5712, 78, 40, 'Biz sizi yakın bir azap ile uyardık. O gün kişi ellerinin ne takdim ettiğine bakacak ve kâfir diyecek ki: "Ah ne olaydı, ben bir toprak olaydım."');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Andolsun şiddetle çekip çıkaranlara,'),
(5714, 79, 2, 'Usulcacık çekenlere,'),
(5715, 79, 3, 'Yüzüp yüzüp gidenlere,'),
(5716, 79, 4, 'Yarışıp geçenlere,'),
(5717, 79, 5, 'Derken bir iş çevirenlere kasem olsun (ki kıyamet var).'),
(5718, 79, 6, 'O gün deprem sarsar,'),
(5719, 79, 7, 'Onu ikinci bir sarsıntı izler.'),
(5720, 79, 8, 'Yürekler vardır, o gün kaygıdan hoplar.'),
(5721, 79, 9, 'Gözler kalkmaz saygıdan.'),
(5722, 79, 10, 'Diyorlar ki: "Biz tekrar eski halimize mi döndürülecekmişiz?'),
(5723, 79, 11, '"Biz, çürümüş kemikler olduktan sonra ha?"'),
(5724, 79, 12, '"Öyleyse bu çok zararlı bir dönüştür." dediler.'),
(5725, 79, 13, 'Fakat o bir tek haykırıştır.'),
(5726, 79, 14, 'Bir de bakarsın hepsi meydandadır.'),
(5727, 79, 15, 'Musa\'nın haberi sana geldi mi?'),
(5728, 79, 16, 'Hani Rabbi ona kutsal vaadi Tuva\'da seslenmişti:'),
(5729, 79, 17, '"Haydi, demişti, git Firavun\'a, çünkü o çok azdı."'),
(5730, 79, 18, 'De ki: İster misin arınasın?'),
(5731, 79, 19, 'Seni Rabbinin yoluna ileteyim de ondan korkasın.'),
(5732, 79, 20, 'Musa Firavun\'a o büyük mucizeyi gösterdi.'),
(5733, 79, 21, 'Fakat Firavun yalanladı, karşı geldi.'),
(5734, 79, 22, 'Sonra koşarak dönüp gitti.'),
(5735, 79, 23, 'Derken adamlarını topladı da bağırdı:'),
(5736, 79, 24, '"Ben sizin en yüce Rabbinizim" dedi.'),
(5737, 79, 25, 'Allah da onu tuttu, dünya ve ahiret azabıyla yakalayıverdi.'),
(5738, 79, 26, 'Kuşkusuz bunda, saygı duyacaklar için bir ibret vardır.'),
(5739, 79, 27, 'Yaratılışça siz mi daha çetinsiniz, yoksa gök mü? Onu Allah bina etti.'),
(5740, 79, 28, 'Tavanını yükseltti, onu bir düzene koydu.'),
(5741, 79, 29, 'Gecesini kararttı, kuşluğunu çıkardı.'),
(5742, 79, 30, 'Bundan sonra da yeryüzünü döşedi.'),
(5743, 79, 31, 'Ondan suyunu ve otlağını çıkardı.'),
(5744, 79, 32, 'Dağlarını oturttu.'),
(5745, 79, 33, 'Sizin ve hayvanlarınızın geçimi için.'),
(5746, 79, 34, 'Fakat o her şeyi bastıran büyük felaket geldiği vakit,'),
(5747, 79, 35, 'O, insanın neyin peşinde koştuğunu anladığı gün,'),
(5748, 79, 36, 'Gören kimseler için cehennem hortlatıldığı vakit,'),
(5749, 79, 37, 'Artık her kim azgınlık etmiş,'),
(5750, 79, 38, 'Ve dünya hayatını tercih etmişse,'),
(5751, 79, 39, 'Kuşkusuz onun varacağı yer cehennemdir.'),
(5752, 79, 40, 'Kim de Rabbinin divanında durmaktan korkmuş, nefsini boş heveslerden menetmiş ise,'),
(5753, 79, 41, 'Kuşkusuz onun varacağı yer cennettir.'),
(5754, 79, 42, 'Sana o kıyameti soruyorlar, ne zaman kopacak diye.'),
(5755, 79, 43, 'Sen nerde, onu anlatmak nerde?!'),
(5756, 79, 44, 'Onun son ilmi Rabbine aittir.'),
(5757, 79, 45, 'Sen ancak ondan korkacak olanları uyarıcısın.'),
(5758, 79, 46, 'Onlar o kıyameti görecekleri gün sanki dünyada bir akşam veya kuşluğundan başka durmamışa dönecekler.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, '(Peygamber) Yüzünü ekşitti ve döndü.'),
(5760, 80, 2, 'Kendisine âmâ geldi, diye.'),
(5761, 80, 3, 'Ne bilirsin, belki o temizlenecek?'),
(5762, 80, 4, 'Veya öğüt belleyecek de öğüt ona fayda verecek.'),
(5763, 80, 5, 'Ama buna ihtiyaç hissetmeyene gelince,'),
(5764, 80, 6, 'Sen ona yöneliyorsun.'),
(5765, 80, 7, 'Onun temizlenmemesinden sana ne?'),
(5766, 80, 8, 'Ama sana can atarak gelen,'),
(5767, 80, 9, 'Allah\'tan korkarak gelmişken,'),
(5768, 80, 10, 'Sen onunla ilgilenmiyorsun.'),
(5769, 80, 11, 'Hayır hayır, sakın. Çünkü o Kur\'ân bir öğüttür.'),
(5770, 80, 12, 'Artık dileyen onu düşünür.'),
(5771, 80, 13, 'O, değerli sahifelerdedir.'),
(5772, 80, 14, 'Yüksek tutulan tertemiz sahifelerde.'),
(5773, 80, 15, 'Yazıcıların ellerindedir,'),
(5774, 80, 16, 'Değerli, iyi yazıcıların.'),
(5775, 80, 17, 'O kahrolası insan, ne nankör şey.'),
(5776, 80, 18, 'O yaratan onu hangi şeyden yarattı?'),
(5777, 80, 19, 'Bir damla sudan, onu yarattı da biçime koydu.'),
(5778, 80, 20, 'Sonra ona yolunu kolaylaştırdı.'),
(5779, 80, 21, 'Sonra onu öldürdü de kabre koydurdu.'),
(5780, 80, 22, 'Sonra dilediği vakit onu tekrar diriltir.'),
(5781, 80, 23, 'Hayır hayır, doğrusu o, hiç Allah\'ın emrini tam yerine getirmedi,'),
(5782, 80, 24, 'Bir de o insan yiyeceğine baksın.'),
(5783, 80, 25, 'Biz o suyu bol bol döktük.'),
(5784, 80, 26, 'Sonra toprağı nasıl da yardık.'),
(5785, 80, 27, 'Bu suretle orada ekinler bitirdik.'),
(5786, 80, 28, 'Üzümler, yoncalar,'),
(5787, 80, 29, 'Zeytinlikler, hurmalıklar,'),
(5788, 80, 30, 'İri ve sık ağaçlı bahçeler,'),
(5789, 80, 31, 'Meyveler, çayırlar bitirdik.'),
(5790, 80, 32, 'Siz ve hayvanlarınız faydalansın diye.'),
(5791, 80, 33, 'Kulakları sağır eden o gürültü geldiğinde,'),
(5792, 80, 34, 'O gün kişi kaçar, kardeşinden...'),
(5793, 80, 35, 'Anasından, babasından..'),
(5794, 80, 36, 'Eşinden ve oğullarından.'),
(5795, 80, 37, 'Onlardan her birinin o gün başından aşan işi vardır.'),
(5796, 80, 38, 'Yüzler var ki, o gün parıl parıl,'),
(5797, 80, 39, 'Güler, sevinir.'),
(5798, 80, 40, 'Yüzler de var ki, o gün tozlanmış,'),
(5799, 80, 41, 'Onları karanlık bürümüş,'),
(5800, 80, 42, 'İşte onlardır kâfirler, haktan sapanlar.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Güneş katlanıp dürüldüğünde,'),
(5802, 81, 2, 'Yıldızlar bulandığında,'),
(5803, 81, 3, 'Dağlar yürütüldüğünde,'),
(5804, 81, 4, 'Kıyılmaz mallar bırakıldığında,'),
(5805, 81, 5, 'Vahşi hayvanlar bir araya toplandığında,'),
(5806, 81, 6, 'Denizler ateşlendiğinde (suları çekilip, volkanlar halinde ateş püskürdüğünde),'),
(5807, 81, 7, 'Nefisler eşleştirildiğinde (iyiler iyilerle, kötüler kötülerle bir araya toplandığında),'),
(5808, 81, 8, 'Diri diri toprağa gömülen kıza sorulduğunda,'),
(5809, 81, 9, '"Hangi günahtan dolayı öldürüldü?" diye.'),
(5810, 81, 10, 'Amel defterleri açıldığında,'),
(5811, 81, 11, 'Gök sıyrılıp açıldığında,'),
(5812, 81, 12, 'Cehennem kızıştırıldığında,'),
(5813, 81, 13, 'Ve cennet yaklaştırıldığında,'),
(5814, 81, 14, 'Herkes ne getirmiş olduğunu anlar.'),
(5815, 81, 15, 'Şimdi yemin ederim o sinenlere (gündüzleri gözden kaybolan yıldızlara),'),
(5816, 81, 16, 'O akıp akıp yuvasına gidenlere,'),
(5817, 81, 17, 'Yöneldiği an geceye,'),
(5818, 81, 18, 'Nefeslendiği (ağardığı) an sabaha ki,'),
(5819, 81, 19, 'Kuşkusuz o Kur\'an, değerli bir elçinin sözüdür.'),
(5820, 81, 20, 'O elçi güçlüdür, Arş\'ın sahibinin yanında çok itibarlıdır.'),
(5821, 81, 21, 'Orada ona itaat edilir, güvenilir.'),
(5822, 81, 22, 'Arkadaşınızı cin çarpmış değildir.'),
(5823, 81, 23, 'Andolsun o, Cebrail\'i açık ufukta gördü.'),
(5824, 81, 24, 'O, gayb hakkında cimri de değildir.'),
(5825, 81, 25, 'O, kovulmuş bir şeytanın sözü değildir.'),
(5826, 81, 26, 'Hâl böyle iken, siz nereye gidiyorsunuz?'),
(5827, 81, 27, 'O, âlemler için öğütten başka bir şey değildir,'),
(5828, 81, 28, 'İçinizden doğru gitmek isteyenler için.'),
(5829, 81, 29, 'Âlemlerin Rabbi olan Allah dilemeyince, siz dileyemezsiniz.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Gök çatladığı vakit,'),
(5831, 82, 2, 'Yıldızlar döküldüğü vakit,'),
(5832, 82, 3, 'Denizler yarılıp akıtıldığı vakit,'),
(5833, 82, 4, 'Kabirlerin içi dışına getirildiği vakit,'),
(5834, 82, 5, 'Herkes neyi önünden gönderdiğini ve neyi geri bıraktığını bilir.'),
(5835, 82, 6, 'Ey insan! İhsanı bol Rabb\'ine karşı seni aldatan nedir?'),
(5836, 82, 7, 'O Allah ki seni yarattı, seni düzgün yapılı kılıp ölçülü bir biçim verdi.'),
(5837, 82, 8, 'Seni dilediği her hangi bir şekilde parçalardan oluşturdu.'),
(5838, 82, 9, 'Hayır hayır, siz cezayı yalanlıyorsunuz.'),
(5839, 82, 10, 'Oysa üzerinizde koruyucular var.'),
(5840, 82, 11, 'Değerli yazıcılar'),
(5841, 82, 12, 'Onlar, siz her ne yaparsanız bilirler'),
(5842, 82, 13, 'Kuşkusuz iyiler nimet içindedirler.'),
(5843, 82, 14, 'Kötüler de cehennemdedirler.'),
(5844, 82, 15, 'Ceza günü ona girecekler.'),
(5845, 82, 16, 'Onlar o cehennemin gözünden kaçamazlar.'),
(5846, 82, 17, 'Ceza gününün ne olduğunu sen bilir misin?'),
(5847, 82, 18, 'Evet, bilir misin nedir acaba o ceza günü?'),
(5848, 82, 19, 'O gün, hiç kimsenin başkası için hiçbir şeye sahip olamadığı gündür. O gün buyruk yalnız Allah\'ındır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Eksik ölçüp tartanların vay haline!'),
(5850, 83, 2, 'Onlar insanlardan kendilerine bir şey aldıkları zaman tam ölçerler.'),
(5851, 83, 3, 'Kendileri başkalarına bir şey ölçtükleri veya tarttıkları zaman eksik ölçer ve tartarlar.'),
(5852, 83, 4, 'Onlar tekrar diriltileceklerini zannetmiyorlar mı?'),
(5853, 83, 5, 'Büyük bir gün için.'),
(5854, 83, 6, 'Öyle bir gün ki, insanlar o gün Rabblerinin huzurunda divan duracaklar.'),
(5855, 83, 7, 'Hayır hayır, kötülerin yazısı muhakkak Siccin\'dedir.'),
(5856, 83, 8, 'Bildin mi sen, Siccin nedir?'),
(5857, 83, 9, 'Yazılmış bir kitaptır o.'),
(5858, 83, 10, 'Vay haline yalanlayanların o gün!'),
(5859, 83, 11, 'Onlar ceza gününü yalanlayanlardır.'),
(5860, 83, 12, 'Onu ancak sınırı aşan ve günaha düşkün olanlar yalanlar.'),
(5861, 83, 13, 'Ona âyetlerimiz okunduğu zaman, "eskilerin masalları" der.'),
(5862, 83, 14, 'Hayır hayır, öyle değil. Aksine onların kazandığı günahlar kalplerinin üzerine pas olmuştur.'),
(5863, 83, 15, 'Hayır hayır, doğrusu onlar o gün Rablerini görmekten mahrumdurlar.'),
(5864, 83, 16, 'Sonra onlar muhakkak cehenneme girecekler.'),
(5865, 83, 17, 'Sonra da onlara: "İşte bu, yalanlayıp durduğunuz şeydir" denilecek.'),
(5866, 83, 18, 'Hayır hayır, iyilerin yazısı muhakkak Illiyyîn\'dedir.'),
(5867, 83, 19, 'Bildin mi sen, Illiyyîn nedir?'),
(5868, 83, 20, 'Yazılmış bir kitaptır o.'),
(5869, 83, 21, 'Allah\'a yaklaştırılmış melekler ona tanık olurlar.'),
(5870, 83, 22, 'Haberiniz olsun ki, iyiler nimet içindedir.'),
(5871, 83, 23, 'Tahtlar üzerinde etrafa bakarlar.'),
(5872, 83, 24, 'Yüzlerinde nimet ve mutluluğun sevincini görürsün.'),
(5873, 83, 25, 'Onlara damgalı saf bir içki sunulur.'),
(5874, 83, 26, 'Onun sonu misktir. İşte ona imrensin artık imrenenler.'),
(5875, 83, 27, 'Karışımı Tesnim\'dendir (En üstün cennet şarabındandır).'),
(5876, 83, 28, 'Allah\'a yakın olanların içecekleri bir kaynaktır o.'),
(5877, 83, 29, 'Doğrusu o suç işleyenler inananlara gülüyorlardı.'),
(5878, 83, 30, 'Onlara uğradıkları vakit birbirlerine göz kırpıyorlardı.'),
(5879, 83, 31, 'Evlerine döndükleri zaman zevklenerek dönüyorlardı.'),
(5880, 83, 32, 'Müminleri gördükleri vakit; "işte bunlar sapıklar" diyorlardı.'),
(5881, 83, 33, 'Oysa onlar müminler üzerine bekçi olarak gönderilmemişlerdi.'),
(5882, 83, 34, 'İşte bugün de inananlar kâfirlere gülecek.'),
(5883, 83, 35, 'Koltuklar üzerinde etrafa bakacaklar.'),
(5884, 83, 36, 'Nasıl, kâfirler yaptıklarının cezasını buldular mı?');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Gök yarıldığı,'),
(5886, 84, 2, 'Rabbini dinleyip kendisine yaraşır şekilde boyun eğdiği vakit,'),
(5887, 84, 3, 'Yer uzatılıp düzlendiği,'),
(5888, 84, 4, 'İçinde ne varsa attığı ve tamamen boşaldığı'),
(5889, 84, 5, 'Ve Rabbini dinleyip kendisine yaraşır şekilde boyun eğdiği vakit,'),
(5890, 84, 6, 'Ey insan! Kuşkusuz sen Rabbine doğru çaba üstüne çaba sarfetmektesin, nihayet O\'na varacaksın.'),
(5891, 84, 7, 'O vakit kitabı sağ eline verilen,'),
(5892, 84, 8, 'Kolay bir hesapla hesaba çekilecek,'),
(5893, 84, 9, 'Ve sevinçli olarak ailesine dönecektir.'),
(5894, 84, 10, 'Ama kitabı arkasından verilen,'),
(5895, 84, 11, '"Yetiş ey ölüm!" diye bağıracak'),
(5896, 84, 12, 'Ve alevli ateşe girecektir.'),
(5897, 84, 13, 'Çünkü o ailesi içinde sevinçli idi.'),
(5898, 84, 14, 'Hiç Rabbine dönmeyeceğini sanmıştı.'),
(5899, 84, 15, 'Hayır Rabbi onu görmekte idi.'),
(5900, 84, 16, 'Şimdi, yemin ederim o şafağa,'),
(5901, 84, 17, 'Geceye ve içinde barındırdığı şeylere,'),
(5902, 84, 18, 'Derlendiği zaman o aya,'),
(5903, 84, 19, 'Ki, siz elbette halden hale geçeceksiniz.'),
(5904, 84, 20, 'Böyleyken onlar neden acaba iman etmezler?'),
(5905, 84, 21, 'Karşılarında Kur\'ân okunduğu vakit secde etmezler?'),
(5906, 84, 22, 'Aksine o nankörler yalanlıyorlar.'),
(5907, 84, 23, 'Oysa Allah içlerinde sakladıklarını biliyor.'),
(5908, 84, 24, 'Onun için onlara elem verici bir azabı müjdele.'),
(5909, 84, 25, 'Ancak iman edip iyi ameller işleyenler başkadır. Onlara tükenmez bir ecir vardır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Burçlar sahibi gökyüzüne,'),
(5911, 85, 2, 'Vaad olunan o güne,'),
(5912, 85, 3, 'Şahitlik edene ve edilene andolsun ki,'),
(5913, 85, 4, 'Kahroldu o hendeğin sahipleri,'),
(5914, 85, 5, 'O çıralı ateşin,'),
(5915, 85, 6, 'Hani o ateşin başına oturmuşlar,'),
(5916, 85, 7, 'Müminlere yaptıklarını seyrediyorlardı.'),
(5917, 85, 8, 'Müminlere kızmalarının sebebi de, onların yalnız çok güçlü ve övgüye lâyık olan Allah\'a iman etmeleri idi.'),
(5918, 85, 9, 'O Allah ki, göklerin ve yerin hükümranlığı O\'nundur ve Allah her şeye şahittir.'),
(5919, 85, 10, 'İnanan erkek ve kadınlara işkence yapıp sonra da tevbe etmeyenlere cehennem azabı ve yangın azabı vardır.'),
(5920, 85, 11, 'İnanan ve iyi amel yapanlar için de altından ırmaklar akan cennetler vardır. İşte büyük kurtuluş odur.'),
(5921, 85, 12, 'Kuşkusuz Rabbinin yakalaması serttir.'),
(5922, 85, 13, 'Yoktan o yaratır ve tekrar o diriltir.'),
(5923, 85, 14, 'Bununla beraber çok bağışlayandır, çok sevendir.'),
(5924, 85, 15, 'Arş\'ın sahibidir, yücedir.'),
(5925, 85, 16, 'Dilediğini yapandır.'),
(5926, 85, 17, 'O orduların kıssası sana geldi mi?'),
(5927, 85, 18, 'Yani Firavun ve Semud\'un?'),
(5928, 85, 19, 'Fakat o inkarcılar hâlâ bir yalanlama içinde.'),
(5929, 85, 20, 'Oysa Allah onları arkalarından kuşatmıştır.'),
(5930, 85, 21, 'Hayır o şerefli bir Kur\'ân\'dır.'),
(5931, 85, 22, 'Levhi Mahfuz\'dadır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Andolsun o göğe ve Târık\'a,'),
(5933, 86, 2, 'Târık nedir, bildin mi?'),
(5934, 86, 3, 'O, karanlığı delen yıldızdır.'),
(5935, 86, 4, 'Hiçbir nefis yoktur ki başında bir denetleyici bulunmasın.'),
(5936, 86, 5, 'Onun için insan neden yaratıldığına bir baksın.'),
(5937, 86, 6, 'Atılan bir sudan yaratıldı.'),
(5938, 86, 7, 'O su, erkeğin sulbü ile kadının göğüs kemikleri arasından çıkar.'),
(5939, 86, 8, 'Elbette Allah\'ın onu döndürmeye gücü yeter.'),
(5940, 86, 9, 'O gün bütün sırlar yoklanıp, meydana çıkarılır.'),
(5941, 86, 10, 'İnsanın o gün ne bir gücü vardır, ne de bir yardımcısı.'),
(5942, 86, 11, 'Andolsun o dönüşlü göğe,'),
(5943, 86, 12, 'O yarılıp çatlayan yere,'),
(5944, 86, 13, 'Kuşkusuz Kur\'ân, ayırıcı bir sözdür.'),
(5945, 86, 14, 'O asla bir şaka değildir.'),
(5946, 86, 15, 'Haberin olsun ki, kâfirler hep hile kuruyorlar.'),
(5947, 86, 16, 'Ben de hilelerine karşılık veririm.'),
(5948, 86, 17, 'Onun için sen kâfirlere mühlet ver, onlara az bir zaman tanı.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Rabbinin yüce adını tesbih et.'),
(5950, 87, 2, 'Yaratıp düzene koyan O\'dur.'),
(5951, 87, 3, 'Takdir edip hidayeti gösteren O\'dur.'),
(5952, 87, 4, 'Otlağı çıkaran,'),
(5953, 87, 5, 'Sonra da onu karamsı bir sel köpüğü haline getiren O\'dur.'),
(5954, 87, 6, 'Bundan böyle sana Kur\'ân\'ı okutacağız da unutmayacaksın.'),
(5955, 87, 7, 'Yalnız Allah\'ın dilediği başkadır. Çünkü o açığı da bilir, gizliyi de.'),
(5956, 87, 8, 'Seni en kolay yola muvaffak kılacağız.'),
(5957, 87, 9, 'Onun için öğüt ver, eğer öğüt fayda verirse.'),
(5958, 87, 10, 'Saygısı olan öğüt alacaktır.'),
(5959, 87, 11, 'Pek bedbaht olan da ondan kaçınacaktır.'),
(5960, 87, 12, 'O ki, en büyük ateşe girecektir.'),
(5961, 87, 13, 'Sonra ne ölecek onda, ne de hayat bulacaktır.'),
(5962, 87, 14, 'Doğrusu felah buldu (günahtan) temizlenen.  '),
(5963, 87, 15, 'Rabbinin adını anıp namaz kılan.'),
(5964, 87, 16, 'Fakat siz dünya hayatını tercih ediyorsunuz.'),
(5965, 87, 17, 'Oysa ahiret daha hayırlı ve daha kalıcıdır.'),
(5966, 87, 18, 'Kuşkusuz bu ilk sahifelerde vardır,'),
(5967, 87, 19, 'İbrahim ve Musa\'nın sahifelerinde.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'O her şeyi kuşatacak olan Kıyamet\'in haberi sana geldi mi?'),
(5969, 88, 2, 'Yüzler var ki, o gün eğilmiş, zillete düşmüştür.'),
(5970, 88, 3, 'Çalışmış, yorulmuştur.'),
(5971, 88, 4, 'Kızışmış bir ateşe girer.'),
(5972, 88, 5, 'Onlara kızgın bir kaynaktan su verilir.'),
(5973, 88, 6, 'Onlar için kuru bir dikenden başka yiyecek de yoktur.'),
(5974, 88, 7, 'O da ne besler, ne de açlığı giderir.'),
(5975, 88, 8, 'Yüzler de var ki, o gün nimetle mutludur.'),
(5976, 88, 9, 'Yaptığından hoşnuttur.'),
(5977, 88, 10, 'Yüksek bir cennettedir.'),
(5978, 88, 11, 'Orada boş bir söz işitmez.'),
(5979, 88, 12, 'Orada akan bir kaynak,'),
(5980, 88, 13, 'Yükseltilmiş divanlar,'),
(5981, 88, 14, 'Konulmuş kadehler,'),
(5982, 88, 15, 'Dizilmiş koltuklar, yastıklar,'),
(5983, 88, 16, 'Serilmiş halılar vardır.'),
(5984, 88, 17, 'Bakmıyorlar mı o develere, nasıl yaratılmış?'),
(5985, 88, 18, 'Göğe bakmıyorlar mı, nasıl yükseltilmiş?'),
(5986, 88, 19, 'Bakmıyorlar mı dağlara, nasıl dikilmiş?'),
(5987, 88, 20, 'Yere bakmıyorlar mı, nasıl yayılmış?'),
(5988, 88, 21, 'Haydi öğüt ver; sen şimdi sırf bir öğütçüsün.'),
(5989, 88, 22, 'Onların üzerinde bir zorba değilsin.'),
(5990, 88, 23, 'Ancak kim yüz çevirir ve kâfir olursa,'),
(5991, 88, 24, 'Allah ona en büyük azap ile azap edecek.'),
(5992, 88, 25, 'Kuşkusuz onlar döne dolaşa bize gelecekler.'),
(5993, 88, 26, 'Sonra da bize hesap verecekler.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Andolsun fecre.'),
(5995, 89, 2, 'On geceye (Zilhicce ayının ilk on gecesine).'),
(5996, 89, 3, 'Çifte ve teke.'),
(5997, 89, 4, 'Gitmekte olan geceye.'),
(5998, 89, 5, 'Nasıl, bunlarda bir akıl sahibi için yemin var değil mi?'),
(5999, 89, 6, 'Görmedin mi Rabbin ne yaptı Âd kavmine?'),
(6000, 89, 7, 'Sütunlar sahibi İrem\'e?'),
(6001, 89, 8, 'Ki ülkeler içinde onun benzeri yaratılmamıştı.'),
(6002, 89, 9, 'Vâdide kayaları yontan Semud kavmine?'),
(6003, 89, 10, 'Kazıklar sahibi (güçlü, kuvvetli) Firavun\'a?'),
(6004, 89, 11, 'Bunlar ülkelerde azmışlardı.'),
(6005, 89, 12, 'Oralarda çok bozgunculuk yapmışlardı.'),
(6006, 89, 13, 'Bu yüzden Rabbin onların üstüne azap kamçısı yağdırdı.'),
(6007, 89, 14, 'Kuşkusuz Rabbin her an gözetlemededir.'),
(6008, 89, 15, 'Ama insan, her ne zaman Rabbi onu sınayıp da ikramda bulunur, nimet verirse, "Rabbim bana ikram etti." der.'),
(6009, 89, 16, 'Ama her ne zaman da sınayıp rızkını daraltırsa, o vakit de, "Rabbim beni zillete düşürdü." der.'),
(6010, 89, 17, 'Hayır hayır, doğrusu siz yetime ikram etmiyorsunuz.'),
(6011, 89, 18, 'Birbirinizi yoksulu yedirmeye teşvik etmiyorsunuz.'),
(6012, 89, 19, 'Oysa mirası öyle bir yiyorsunuz ki, haramhelal gözetmeden.'),
(6013, 89, 20, 'Malı öyle bir seviyorsunuz ki, yığmacasına.'),
(6014, 89, 21, 'Hayır hayır, yer birbiri ardınca sarsılıp dümdüz olduğu zaman,'),
(6015, 89, 22, 'Rabbinin emri gelip melekler sıra sıra dizildiği zaman,'),
(6016, 89, 23, 'Ki cehennem de o gün getirilmiştir. İşte o gün insan anlar. Fakat bu anlamanın ona ne yararı var?'),
(6017, 89, 24, '"Keşke hayatım için bir şeyler yapıp gönderseydim." der.'),
(6018, 89, 25, 'Artık o gün Allah\'ın edeceği azabı kimse edemez.'),
(6019, 89, 26, 'Onun vuracağı bağı kimse vuramaz.'),
(6020, 89, 27, 'Ey, Rabbine, itaat edip huzura eren nefis!'),
(6021, 89, 28, 'Hem hoşnut edici, hem de hoşnut edilmiş olarak Rabbine dön.'),
(6022, 89, 29, 'Kullarımın arasına gir.'),
(6023, 89, 30, 'Cennetime gir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'Andolsun bu beldeye'),
(6025, 90, 2, 'Ki sen bu beldede oturmaktasın.'),
(6026, 90, 3, 'Ve and olsun baba ve çocuğuna.'),
(6027, 90, 4, 'Biz insanı gerçekten bir sıkıntı içinde yarattık.'),
(6028, 90, 5, 'İnsan, kendisine karşı kimse güç yetiremez mi sanıyor?'),
(6029, 90, 6, 'Ben, yığın yığın mal yok ettim diyor.'),
(6030, 90, 7, 'Kendisini bir gören olmadı mı sanıyor?'),
(6031, 90, 8, 'Biz ona iki göz vermedik mi?'),
(6032, 90, 9, 'Bir dil ve iki dudak?'),
(6033, 90, 10, 'Ona iki yolu gösterdik.'),
(6034, 90, 11, 'Fakat o, o sarp yokuşa göğüs veremedi.'),
(6035, 90, 12, 'Bildin mi sen, o sarp yokuş nedir?'),
(6036, 90, 13, 'Köle azat etmek,'),
(6037, 90, 14, 'Veya salgın bir kıtlık gününde yemek yedirmektir,'),
(6038, 90, 15, 'Yakınlığı olan bir yetime,'),
(6039, 90, 16, 'Veya hiçbir şeyi olmayan yoksula.'),
(6040, 90, 17, 'Sonra da iman edip de sabrı tavsiye eden ve merhamet tavsiye edenlerden olmaktır.'),
(6041, 90, 18, 'İşte bunlar, amel defterleri sağlarından verilenlerdir.'),
(6042, 90, 19, 'Âyetlerimizi tanımayanlar ise, onlardır işte amel defterleri sollarından verilenler.'),
(6043, 90, 20, 'Onların üzerlerine bir ateş bastırılıp kapıları kapanacaktır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Güneş\'e ve onun parıltısına,'),
(6045, 91, 2, 'Güneş\'in ardından gelen Ay\'a,'),
(6046, 91, 3, 'Güneş\'i açıp ortaya çıkaran gündüze,'),
(6047, 91, 4, 'Onu örten geceye,'),
(6048, 91, 5, 'Göğe ve onu bina edene,'),
(6049, 91, 6, 'Yere ve onu döşeyene,'),
(6050, 91, 7, 'Nefse ve onu biçimlendirene,'),
(6051, 91, 8, 'Sonra da ona kötülük ve takva kabiliyetini verene yemin olsun ki,'),
(6052, 91, 9, 'Elbette nefsini temizleyip parlatan kurtulmuştur.'),
(6053, 91, 10, 'Onu kirletip gömen de ziyan etmiştir.'),
(6054, 91, 11, 'Semud, azgınlığıyla Hakk\'ı yalanladı,'),
(6055, 91, 12, 'En azgınları ileri atılınca,'),
(6056, 91, 13, 'Allah\'ın Rasulü (Salih peygamber) onlara: "Allah\'ın devesini ve onun su nöbetini gözetin." demişti.'),
(6057, 91, 14, 'Fakat onlar peygamberi yalanlayıp deveyi kestiler. Rableri de günahlarını başlarına geçiriverdi de orayı dümdüz etti.'),
(6058, 91, 15, 'Öyle ya, Allah bu işin sonundan korkacak değil ya.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Örttüğü zaman geceye,'),
(6060, 92, 2, 'Açıldığı zaman gündüze,'),
(6061, 92, 3, 'Erkeği ve dişiyi yaratana and olsun ki,'),
(6062, 92, 4, 'Gerçekten sizin işiniz başka başkadır.'),
(6063, 92, 5, 'Bundan böyle her kim malını hayır için verir ve korunursa,'),
(6064, 92, 6, 'Ve en güzel olanı doğrularsa,'),
(6065, 92, 7, 'Biz onu en kolay yola muvaffak kılacağız.'),
(6066, 92, 8, 'Kim de cimrilik eder ve kendini hiçbir şeye ihtiyacı kalmamış görür.'),
(6067, 92, 9, 'Ve en güzeli de yalanlarsa,'),
(6068, 92, 10, 'Onu da en zor yola hazırlarız.'),
(6069, 92, 11, 'Çukura yuvarlandığı zaman malı onu kurtaramayacak.'),
(6070, 92, 12, 'Doğru yolu göstermek muhakkak bize aittir.'),
(6071, 92, 13, 'Kuşkusuz ahiret de dünya da bizimdir.'),
(6072, 92, 14, 'Ben sizi köpürdükçe köpüren bir ateşe karşı uyardım.'),
(6073, 92, 15, 'Ona ancak en azgın olan girer.'),
(6074, 92, 16, 'Öyle azgın ki, yalanlamış ve sırtını dönmüştür.'),
(6075, 92, 17, 'En çok korunan ise ondan uzaklaştırılacaktır.'),
(6076, 92, 18, 'O ki, Allah yolunda malını verir, temizlenir.'),
(6077, 92, 19, 'Onun yanında, başka bir kimse için karşılığı verilecek hiçbir nimet yoktur.'),
(6078, 92, 20, 'O ancak yüce Rabbinin rızasını aramak için verir.'),
(6079, 92, 21, 'Elbette yakında kendisi de hoşnut olacaktır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Andolsun kuşluk vaktine.'),
(6081, 93, 2, 'Ve sakinleştiği zaman geceye ki,'),
(6082, 93, 3, 'Rabbin seni bırakmadı ve darılmadı.'),
(6083, 93, 4, 'Ahiret senin için dünyadan iyi olacaktır.'),
(6084, 93, 5, 'Rabbın sana verecek ve sen hoşnut olacaksın.'),
(6085, 93, 6, 'O seni yetim bulup da barındırmadı mı?'),
(6086, 93, 7, 'Seni yol bilmez bulup yola iletmedi mi?'),
(6087, 93, 8, 'Seni yoksul bulup zengin etmedi mi?'),
(6088, 93, 9, 'Öyleyse sakın yetimi ezme.'),
(6089, 93, 10, 'Dilenciyi de azarlama.'),
(6090, 93, 11, 'Fakat Rabbinin nimetini anlat da anlat.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Biz senin için (mutluluğun) göğsünü açmadık mı?'),
(6092, 94, 2, 'Senden yükünü indirmedik mi?'),
(6093, 94, 3, 'O senin sırtını ezen yükü.'),
(6094, 94, 4, 'Senin şanını yüceltmedik mi?'),
(6095, 94, 5, 'Demek ki, zorlukla beraber bir kolaylık vardır.'),
(6096, 94, 6, 'Evet, zorlukla beraber bir kolaylık vardır.'),
(6097, 94, 7, 'O halde boş kaldın mı, yine kalk (başka bir iş ve ibadetle) yorul.'),
(6098, 94, 8, 'Ancak Rabbine yönel.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Tîn\'e ve Zeytun\'a,'),
(6100, 95, 2, 'Sina dağına'),
(6101, 95, 3, 'Ve bu güvenli beldeye andolsun ki,'),
(6102, 95, 4, 'Biz insanı en güzel biçimde yarattık.'),
(6103, 95, 5, 'Sonra da çevirdik aşağıların aşağısına attık.'),
(6104, 95, 6, 'Ancak iman edip iyi işler yapanlar başka; onlar için kesintisiz bir ecir vardır.'),
(6105, 95, 7, 'O halde sana dini ne yalanlatır?'),
(6106, 95, 8, 'Allah, hakimlerin hakimi değil mi?');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Yaratan Rabbinin adıyla oku!'),
(6108, 96, 2, 'O, insanı bir alekadan (embriyodan) yarattı.'),
(6109, 96, 3, 'Oku! Rabbin sonsuz kerem sahibidir.'),
(6110, 96, 4, 'O Rab ki kalemle yazmayı öğretti.'),
(6111, 96, 5, 'İnsana bilmediği şeyleri öğretti.'),
(6112, 96, 6, 'Hayır! Doğrusu (kâfir) insan azgınlık eder.'),
(6113, 96, 7, 'Kendisinin muhtaç olmadığını zannettiği için.'),
(6114, 96, 8, 'Muhakkak ki dönüş mutlaka Rabbinedir.'),
(6115, 96, 9, 'Namaz kıldığı zaman, bir kulu engelleyeni gördün mü?'),
(6116, 96, 10, 'Namaz kıldığı zaman, bir kulu engelleyeni gördün mü?'),
(6117, 96, 11, 'Gördün mü (ne dersin?), ya o (kul) doğru yolda olur,'),
(6118, 96, 12, 'Veya kötülüklerden sakınmayı emrederse?'),
(6119, 96, 13, 'Gördün mü, ya bu (adam, hakkı) yalanlar, yüzçevirirse,'),
(6120, 96, 14, 'O adam, Allah\'ın kendini gördüğünü hiç bilmiyor mu?'),
(6121, 96, 15, 'Hayır, hayır! Eğer o, bu davranışından vazgeçmezse, and olsun ki biz, onu perçeminden, o günahkâr ve yalancı perçeminden tutup cehenneme sürükleriz.'),
(6122, 96, 16, 'Hayır, hayır! Eğer o, bu davranışından vazgeçmezse, and olsun ki biz, onu perçeminden, o günahkâr ve yalancı perçeminden tutup cehenneme sürükleriz.'),
(6123, 96, 17, 'O zaman o taraftarlarını yardıma çağırsın.'),
(6124, 96, 18, 'Biz de Zebanileri çağıracağız.'),
(6125, 96, 19, 'Sakın onu dinleme de (Rabbine) secde et ve yaklaş.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Biz o (Kur\'ân)nu Kadir gecesinde indirdik.'),
(6127, 97, 2, 'Kadir gecesinin ne olduğunu sen nereden bileceksin?'),
(6128, 97, 3, 'Kadir gecesi bin aydan daha hayırlıdır.'),
(6129, 97, 4, 'Melekler ve Ruh (Cebrail veya Ruh adındaki melek) o gece Rablerinin izniyle, her iş için inerler.'),
(6130, 97, 5, 'O gece, tanyeri ağarıncaya kadar süren bir selâmettir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Kitap ehlinden ve müşriklerden (Hakk\'ı) tanımayanlar, kendilerine açık delil gelinceye kadar inkârlarından ayrılacak değillerdi.'),
(6132, 98, 2, '(Bu delil), tertemiz sayfaları okuyan, Allah tarafından gönderilmiş bir peygamberdir.'),
(6133, 98, 3, 'O sayfalarda, en doğru hükümler vardır.'),
(6134, 98, 4, 'Kitap ehli, ancak kendilerine apaçık delil geldikten sonra ayrılığa düştüler.'),
(6135, 98, 5, 'Halbuki onlar, dini sadece Allah\'a tahsis ederek, Allah\'ı birleyerek, ancak Allah\'a ibadet etmekle, namazı kılmakla ve zekatı vermekle emrolunmuşlardır. İşte dosdoğru din budur.'),
(6136, 98, 6, 'Kâfirler, gerek kitap ehlinden olsun gerek puta tapanlardan olsun muhakkak, cehennem ateşindedirler. Orada ebedî olarak kalacaklardır. Onlar, insanların en şerlileridir.'),
(6137, 98, 7, 'İnanan ve güzel amel işleyenler de insanların en hayırlılarıdır.'),
(6138, 98, 8, 'Rableri katında onların mükâfatı, altlarından ırmaklar akan Adn cennetleridir. Orada ebedî olarak kalacaklardır. Allah onlardan razı olmuş, onlar da O\'ndan razı olmuşlardır. İşte bu mükâfat, Rabbine saygı gösterene mahsustur.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Yer o yaman sarsıntı ile sarsıldığı,'),
(6140, 99, 2, 'Yer, içindeki ağırlıkları çıkarıp dışarı attığı,'),
(6141, 99, 3, 'Ve insan: "Ona ne oluyor?" dediği zaman.'),
(6142, 99, 4, 'O gün yer, Rabbinin ona vahyetmesiyle haberlerini anlatacaktır.'),
(6143, 99, 5, 'O gün yer, Rabbinin ona vahyetmesiyle haberlerini anlatacaktır.'),
(6144, 99, 6, 'O gün insanlar, amellerinin karşılığı kendilerine gösterilmek üzere bölük bölük çıkacaklardır.'),
(6145, 99, 7, 'Her kim zerre kadar hayır işlemişse onu görecektir.'),
(6146, 99, 8, 'Her kim, zerre kadar şer işlemişse onu görecektir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'O harıl harıl (savaşa) koşanlara,'),
(6148, 100, 2, '(Tırnaklarıyla yerden) ateş çıkaranlara,'),
(6149, 100, 3, 'Sabahleyin akın edenlere,'),
(6150, 100, 4, 'Tozu dumana karıştıranlara,'),
(6151, 100, 5, 'Derken bir topluluğun ortasına dalanlara yemin ederim ki,'),
(6152, 100, 6, 'Şüphesiz insan, Rabbine karşı çok nankördür.'),
(6153, 100, 7, 'Ve kendisi de buna şahittir.'),
(6154, 100, 8, 'Gerçekten o dünya malını çok sevdiği için katıdır.'),
(6155, 100, 9, 'Bilmiyor mu ki, kabirlerin içindekiler fırlatılacak.'),
(6156, 100, 10, 'Ve sinelerin içindekiler derlenecek.'),
(6157, 100, 11, 'O gün Rableri onların bütün yaptıklarından haberdardır');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Kâria! (Çarpacak kıyamet) Nedir o kâria? Kârianın ne olduğunu sen bilir misin?'),
(6159, 101, 2, 'Kâria! (Çarpacak kıyamet) Nedir o kâria? Kârianın ne olduğunu sen bilir misin?'),
(6160, 101, 3, 'Kâria! (Çarpacak kıyamet) Nedir o kâria? Kârianın ne olduğunu sen bilir misin?'),
(6161, 101, 4, 'O gün insanlar yayılmış pervaneler gibi olurlar.'),
(6162, 101, 5, 'Dağlar atılmış renkli yünler gibi olur.'),
(6163, 101, 6, 'O gün kimin tartıları ağır basarsa o, hoşnut olacağı bir hayat içindedir.'),
(6164, 101, 7, 'O gün kimin tartıları ağır basarsa o, hoşnut olacağı bir hayat içindedir.'),
(6165, 101, 8, 'Kimin tartıları hafif gelirse, onun anası da (varacağı yer, sığınacağı durağı) hâviye (uçurum)dır.'),
(6166, 101, 9, 'Kimin tartıları hafif gelirse, onun anası da (varacağı yer, sığınacağı durağı) hâviye (uçurum)dır.'),
(6167, 101, 10, 'O uçurumun ne olduğunu sen nereden bileceksin?'),
(6168, 101, 11, 'O, kızgın bir ateştir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Çoklukla övünmek, sizi kabirlere varıncaya kadar oyaladı.'),
(6170, 102, 2, 'Çoklukla övünmek, sizi kabirlere varıncaya kadar oyaladı.'),
(6171, 102, 3, 'Hayır! Yakında bileceksiniz.'),
(6172, 102, 4, 'Yine hayır! Yakında bileceksiniz (hatanızı).'),
(6173, 102, 5, 'Hayır! Eğer kesin bilgi ile bilseniz, elbette cehennemi görürsünüz.'),
(6174, 102, 6, 'Hayır! Eğer kesin bilgi ile bilseniz, elbette cehennemi görürsünüz.'),
(6175, 102, 7, 'Sonra, yemin olsun ki, cehennemi yakin gözüyle göreceksiniz.'),
(6176, 102, 8, 'Sonra, yemin olsun ki, o gün (size verilen) her nimetten sorulacaksınız');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Asra yemin olsun ki,'),
(6178, 103, 2, 'İnsan mutlaka ziyandadır.'),
(6179, 103, 3, 'Ancak iman edenler, salih amel (iyi işler) işleyenler, birbirlerine hakkı tavsiye eden ve sabrı tavsiye edenler bunun dışındadır');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Mal toplayıp onu tekrar tekrar sayan, insanları arkadan çekiştirip, kaş göz hareketleriyle alay edenlerin (hümeze ve lümezenin) vay haline!'),
(6181, 104, 2, 'Mal toplayıp onu tekrar tekrar sayan, insanları arkadan çekiştirip, kaş göz hareketleriyle alay edenlerin (hümeze ve lümezenin) vay haline!'),
(6182, 104, 3, 'Malının, kendisini ebedi yaşatacağını sanır.'),
(6183, 104, 4, 'Hayır, andolsun ki, o hutame (cehennem)ye atılacaktır.'),
(6184, 104, 5, 'Hutame\'nin ne olduğunu bilir misin?'),
(6185, 104, 6, 'O, kalplerin içine işleyecek, Allah\'ın tutuşturulmuş bir ateşidir.'),
(6186, 104, 7, 'O, kalplerin içine işleyecek, Allah\'ın tutuşturulmuş bir ateşidir.'),
(6187, 104, 8, 'Cehennemlikler, dikilmiş direklere bağlı oldukları halde, o ateşin kapıları üzerlerine kapatılacaktır.'),
(6188, 104, 9, 'Cehennemlikler, dikilmiş direklere bağlı oldukları halde, o ateşin kapıları üzerlerine kapatılacaktır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Görmedin mi Rabb\'in fil sahiplerine ne yaptı?'),
(6190, 105, 2, 'Onların tuzaklarını boşa çıkarmadı mı?'),
(6191, 105, 3, 'Üzerlerine sürü sürü kuşlar gönderdi.'),
(6192, 105, 4, 'Onlara çamurdan sertleşmiş taşlar atıyorlardı.'),
(6193, 105, 5, 'Ve onları, yenilmiş ekin yaprağı gibi yaptı.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Kureyş\'in ilâfı (güven ve barış andlaşmalarından faydalanmalarını sağlamak) için.'),
(6195, 106, 2, 'Kış ve yaz seferlerinde (faydalandıkları andlaşmaların) kadrini bilmiş olmaları için.'),
(6196, 106, 3, 'Bu Beyt (Kâbe)nin Rabbine kulluk etsinler.'),
(6197, 106, 4, 'O, kendilerini açlıktan kurtararak beslemiştir ve her tehlikeye karşı onlara emniyet vermiştir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Dini yalanlayanı gördün mü?'),
(6199, 107, 2, 'İşte o, öksüzü iter, kakar.'),
(6200, 107, 3, 'Yoksulu doyurmaya önayak olmaz.'),
(6201, 107, 4, 'Vay haline o namaz kılanların ki,'),
(6202, 107, 5, 'Kıldıkları namazın değerine aldırış etmezler.'),
(6203, 107, 6, 'Gösteriş yaparlar onlar,'),
(6204, 107, 7, 'Ve yardımlığı sakınırlar (zekatı vermezler).');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Muhakkak biz sana Kevser\'i verdik.'),
(6206, 108, 2, 'Öyleyse Rabb\'in için namaz kıl ve kurban kes.'),
(6207, 108, 3, 'Muhakkak ki sonu kesik olan, sana buğzedendir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'De ki: Ey kâfirler'),
(6209, 109, 2, 'Sizin taptıklarınıza ben tapmam.'),
(6210, 109, 3, 'Siz de benim taptığıma tapıcılar değilsiniz.'),
(6211, 109, 4, 'Ben asla sizin taptıklarınıza tapacak değilim.'),
(6212, 109, 5, 'Siz de benim taptığıma tapacak değilsiniz.'),
(6213, 109, 6, 'Sizin dininiz size, benim dinim banadır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'Allah\'ın yardımı ve fetih geldiğinde,'),
(6215, 110, 2, 'Ve insanların dalga dalga Allah\'ın dinine girdiklerini gördüğünde,'),
(6216, 110, 3, 'Rabbini öğerek tesbih et, O\'ndan bağışlanmanı dile, çünkü O, tevbeleri çok kabul edendir.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Ebu Leheb\'in elleri kurusun (yok olsun o), zaten yok oldu ya.'),
(6218, 111, 2, 'Ne malı ne de kazandığı onu kurtaramadı.'),
(6219, 111, 3, '(O), alevli bir ateşe girecektir.'),
(6220, 111, 4, 'Karısı da odun hamalı olarak (onunla beraber girecektir).'),
(6221, 111, 5, 'Boynunda da hurma lifinden bir ip olacaktır.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'De ki; O Allah bir tektir.'),
(6223, 112, 2, 'Allah eksiksiz, sameddir (Bütün varlıklar O\'na muhtaç, fakat O, hiç bir şeye muhtaç değildir'),
(6224, 112, 3, 'Doğurmadı ve doğurulmadı'),
(6225, 112, 4, 'O \'na bir denk de olmadı.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'De ki: "Ben, ağaran sabahın Rabbine sığınırım,'),
(6227, 113, 2, 'Yarattığı şeylerin şerrinden,'),
(6228, 113, 3, 'Karanlığı çöktüğü zaman gecenin şerrinden,'),
(6229, 113, 4, 'Ve düğümlere üfleyen büyücülerin şerrinden,'),
(6230, 113, 5, 'Ve hased ettiği zaman hasetçinin şerrinden.');
INSERT INTO `tr_yazir` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'De ki: Sığınırım ben insanların Rabbine,'),
(6232, 114, 2, 'İnsanların hükümdârına,'),
(6233, 114, 3, 'İnsanların ilâhına,'),
(6234, 114, 4, 'O sinsi vesvesecinin şerrinden.'),
(6235, 114, 5, 'O ki, insanların göğüslerine vesveseler fısıldar.'),
(6236, 114, 6, 'Gerek cinlerden, gerek insanlardan.');
