INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'Rahman ve Rahim ALLAH\'ın ismiyle.'),
(2, 1, 2, 'Övgü, evrenlerin Rabbi ALLAH\'adır.'),
(3, 1, 3, 'Rahman, Rahim (Merhametli),'),
(4, 1, 4, 'Yargı (Din) Gününün sahibi.'),
(5, 1, 5, 'Ancak sana tapar, ancak senden yardım dileriz.'),
(6, 1, 6, 'Bizi doğru yola ilet.'),
(7, 1, 7, 'Gazaba uğrayanların ve sapmışların değil; kendilerine iyilikte bulunduğun kimselerin yoluna...');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'A. L. M.'),
(9, 2, 2, 'Bu, kuşkusuz, erdemliler için yol gösterici bir kitaptır.'),
(10, 2, 3, 'Onlar ki duyularıyla algılayamadıkları gerçeklere de inanırlar, namazı (salat) gözetirler, kendilerine verdiğimiz rızıktan muhtaçlara verirler'),
(11, 2, 4, 'Sana indirilene ve senden önce indirilene inanırlar. Ahiret konusunda da hiçbir kuşkuları yoktur.'),
(12, 2, 5, 'İşte, Rableri tarafından yol gösterilenler ve mutluluğa erenler bunlardır.'),
(13, 2, 6, 'İnkar edenlere gelince, onları uyarsan da uyarmasan da onlar için birdir; onlar inanmazlar.'),
(14, 2, 7, 'ALLAH kalplerini ve kulaklarını mühürler. Gözlerinde perde vardır ve büyük azap onlar içindir.'),
(15, 2, 8, 'Halktan öyle kimseler var ki aslında inanmadıkları halde "ALLAH\'a ve ahiret gününe inandık," derler.'),
(16, 2, 9, 'ALLAH\'ı ve müminleri aldatmak isterler. Halbuki kendi kendilerini aldatıyorlar. Farkında bile değiller.'),
(17, 2, 10, 'Kalplerinde hastalık var. ALLAH da hastalıklarını arttırır. Yalanları yüzünden acı bir azabı hakkederler.'),
(18, 2, 11, 'Kendilerine, "Yeryüzünde bozgunculuk yapmayın," denildiğinde "Bizler sadece düzeltenleriz," derler.'),
(19, 2, 12, 'Oysa onlardır asıl bozguncu; farkında bile değiller.'),
(20, 2, 13, 'Kendilerine, "Şu halkın inandığı gibi inanın," denildiğinde, "Beyinsizlerin inandığı gibi mi inanacağız," derler. Gerçek beyinsizler onlardır; fakat bilmezler.'),
(21, 2, 14, 'İnananlarla karşılaştıkları vakit, "İnanıyoruz," derler; fakat şeytanlarıyla başbaşa kaldıklarında, "Sizinle beraberiz. Biz sadece alay etmekteyiz," derler.'),
(22, 2, 15, 'ALLAH da, taşkınlıkları içinde bocalar durumda bırakarak onlarla alay eder.'),
(23, 2, 16, 'Onlar hidayet karşılığında sapıklığı satın aldı. Ticaretleri ne kâr bırakır, ne de onları gerçeğe ulaştırır.'),
(24, 2, 17, 'Durumları, ateş yakan kimselerin şu durumuna benzer: Ateş çevrelerini aydınlatmaya başlayınca ALLAH onların ışığını giderir ve onları karanlıklar içinde görmez bir halde bırakır.'),
(25, 2, 18, 'Sağır, dilsiz ve kördürler; yönlerini değiştiremezler.'),
(26, 2, 19, 'Ya da, karanlık, gökgürültüsü ve şimşekler arasında gökten boşanan bir yağmur altında yıldırımlardan ölmek korkusuyla parmaklarını kulaklarına tıkayan kimseye benzerler. ALLAH inkarcıları böyle kuşatır.'),
(27, 2, 20, 'Şimşek neredeyse gözlerini kapıverecek! Önlerini aydınlattıkça ışığında yürürler. Üzerlerine karanlık basınca da dikilir kalırlar. ALLAH dileseydi işitme ve görmelerini giderirdi. ALLAH herşeye gücü yetendir.'),
(28, 2, 21, 'İnsanlar! Sizi ve sizden öncekileri yaratan Rabbinize kulluk edin ki korunasınız.'),
(29, 2, 22, 'O, yeryüzünü sizin için oturulabilir hale soktu ve göğü de bir yapı kıldı. Gökten su indirdi ve onunla rızık olarak size çeşitli ürünler çıkardı. Bile bile ALLAH\'a eşler koşmayın.'),
(30, 2, 23, 'Kulumuza indirdiğimizden kuşku içinde iseniz, buna benzer bir sure getirin. ALLAH\'tan başka tüm tanıklarınızı da yardıma çağırın, doğru sözlü iseniz.'),
(31, 2, 24, 'Bunu yapamazsanız -ki asla yapamıyacaksınız- o taktirde inkarcılar için hazırlanan ve yakıtı insanlar ile taşlar olan ateşten sakının.'),
(32, 2, 25, 'İnanıp erdemli davrananları, içlerinde ırmaklar akan cennetlerle (bahçelerle) müjdele. Kendilerine oradaki ürünlerden rızıklar sunulduğunda "Bu, daha önce bize sunulan nimetlerdir," derler. Böylece, kendilerine mecazi tanımlar (benzetmeler) verilir. Onlar için orada tertemiz eşler vardır ve onlar orada ebedi kalıcıdırlar.'),
(33, 2, 26, 'ALLAH bir sivrisineği hatta ondan daha küçüğünü örnek vermekten çekinmez. İnananlar, bunun Rab\'lerinden gelen bir gerçek olduğunu bilir. İnkarcılar ise "ALLAH bu benzetme ile neyi amaçladı," derler. O, bununla bir çok kişiyi saptırır ve birçok kişiyide doğruya iletir. O, bununla sadece fasıkları saptırır.'),
(34, 2, 27, 'Onlar ki ALLAH ile yaptıkları anlaşmaya bağlılık sözü verdikten sonra onu bozarlar, ALLAH\'ın birleştirilmesini emrettiği şeyi keserler ve yeryüzünde bozgunculuk yaparlar. İşte onlar kaybedenlerdir.'),
(35, 2, 28, 'ALLAH\'ı nasıl inkar edersiniz? Siz ölüler idiniz o sizi diriltti. Sonra sizi öldürür ve tekrar diriltir ve sonunda ona döndürülürsünüz.'),
(36, 2, 29, 'Yeryüzünde herşeyi sizin için yaratan O\'dur. Sonra göğe yönelip onu yedi gök olarak düzenledi. O, her şeyi Bilir.'),
(37, 2, 30, 'Rabbin, meleklere şöyle demişti: "Yeryüzüne bir halife yerleştireceğim." Melekler de: "Orada bozgunculuk yapacak, kan akıtacak birisini mi yerleştireceksin? Halbuki biz seni överek yüceltiyor ve mutlak otoriteni onaylıyoruz," dediler. "Bilmediğinizi Ben bilirim," dedi.'),
(38, 2, 31, 'Adem\'e tüm isimleri (nitelemeleri) öğretti, sonra onları meleklere sunup, "Doğru iseniz, şunların isimlerini (özelliklerini, niteliklerini) siz bana bildirin," dedi.'),
(39, 2, 32, 'Dediler: "Sen Yücesin, senin bize öğrettiğinden başka bir bilgimiz yok. Sen Bilensin, Bilgesin."'),
(40, 2, 33, 'Dedi: "Adem! Onların isimlerini şunlara haber ver." İsimlerini onlara haber verince, "Size, yerin ve göklerin sırlarını biliyorum, açıkladığınızı da gizlediğinizi de biliyorum dememiş miydim," dedi.'),
(41, 2, 34, 'Meleklere, "Adem\'e secde edin," dedik. İblis hariç hepsi secde ettiler, o ise diretti, büyüklük tasladı ve nankörlük etti.'),
(42, 2, 35, '"Adem! Eşinle birlikte cennette kal. Dilediğiniz yerde ondan bolca yeyin; ancak şu ağaca yaklaşmayın, yoksa zalimlerden olursunuz!," dedik.'),
(43, 2, 36, 'Şeytan, onları oradan kaydırıp bulundukları yerden çıkarttı. Nihayet, "Birbirinize düşman olarak aşağı inin. Yeryüzünde belli bir süre kalıp yaşayacaksınız," dedik.'),
(44, 2, 37, 'Adem, Rabb\'inden kelimeler aldı. Bunun üzerine onun tevbesini kabul etti. O, yönelişlere karşılık verendir, Rahim\'dir.'),
(45, 2, 38, '"Oradan topluca ininiz," dedik, "Benden size bir yol gösterici geldiği zaman, o yol göstericiye uyanlar için artık bir korku yok ve onlar üzülmeyecekler."'),
(46, 2, 39, '"Ayetlerimizi yalanlayıp inkar edenler ise ateşe mahkumdur; orada sürekli kalacaklar."'),
(47, 2, 40, 'İsrailoğulları! Size verdiğim nimetimi hatırlayın, bana verdiğiniz sözü tutun ki ben de size verdiğim sözü tutayım; yalnız benden korkun!'),
(48, 2, 41, 'Yanınızdakini doğrulayıcı olarak indirdiğime inanın. Ona karşı çıkanların ilki olmayın. Ayetlerimi değeri düşük şeylerle değişmeyin; sadece benden çekinin.'),
(49, 2, 42, 'Bile bile gerçeği yanlış ile karıştırmayın, gerçeği gizlemeyin.'),
(50, 2, 43, 'Namazı gözetin, zekatı verin ve eğilenlerle birlikte eğilin.'),
(51, 2, 44, 'Halkı iyilik yapmağa çağırıp dururken kendinizi unutuyor musunuz? Üstelik Kitabı da okuyorsunuz? Aklınızı kullanmaz mısınız?'),
(52, 2, 45, 'Güçlüklere karşı direnerek (sabır) ve namazla yardım isteyiniz. Elbette bu, halka ağır gelir; ancak saygılı olanlar hariç.'),
(53, 2, 46, 'Nitekim onlar, Rab\'lerine kavuşacaklarına ve O\'na döneceklerine inanırlar.'),
(54, 2, 47, 'İsrailoğulları! Size verdiğim nimetlerimi ve sizi tüm halklara üstün tutmamı hatırlayın.'),
(55, 2, 48, 'Öyle bir günden sakının ki, kimse kimsenin yerine birşey ödeyemez, aracılık (şefaat) kabul edilmez, kimseden bir fidye alınmaz ve yardım da edilmez.'),
(56, 2, 49, 'İşkencenin en kötüsünü size uygulayan, kadınlarınızı bırakıp oğullarınızı öldüren Firavun\'un adamlarından sizi kurtarmıştık. Bu, Rabbinizden büyük bir sınav idi.'),
(57, 2, 50, 'Denizi yararak sizi kurtarmış, Firavun\'un adamlarını da gözlerinizin önünde boğmuştuk.'),
(58, 2, 51, 'Musa\'yla kırk gece için sözleşmiştik. Ancak siz onun ardından kendinize zulmederek buzağıya taptınız.'),
(59, 2, 52, 'Bunlara rağmen, şükredersiniz diye sizi affettik.'),
(60, 2, 53, 'Yola gelmeniz için de Musa\'ya kitabı ve yasayı verdik.'),
(61, 2, 54, 'Musa, halkına demişti ki: "Ey halkım, sizler buzağıya tapmakla nefsinize (kişilik, öz, ruh) zulmettiniz. Yaratıcınıza tövbe edin ve nefsinizi (egonuzu) öldürün. Bu, yaratıcınız katında sizin için daha iyidir." O, sizi affeder. Elbette O, tövbeleri kabul edendir, Rahim\'dir.'),
(62, 2, 55, 'Bir zamanlar, "Ey Musa, ALLAH\'ı fiziksel olarak görmedikçe inanmayız," demiştiniz. Bakınıp dururken size yıldırım çarpmıştı.'),
(63, 2, 56, 'Sonra, belki şükredersiniz diye ölümünüzün ardından sizi diriltmiştik.'),
(64, 2, 57, 'Sizi bulutlarla gölgelendirmiş ve sizin için menna ve bıldırcın indirmiştik: "Size verdiğimiz iyi rızıklardan yiyin." Fakat onlar bize değil, sadece kendilerine zulmediyorlardı.'),
(65, 2, 58, '"Bu şehre girin. Orada dilediğiniz yerden bol bol yiyin. Kapıdan alçak gönüllü olarak girin ve dostça konuşun ki hatalarınızı bağışlayalım. Güzel davrananlara daha fazlasını veririz," demiştik.'),
(66, 2, 59, 'Ancak içinizdeki zalimler, kendilerine verilen kelimeleri başka kelimelerle değiştirdiler. Nitekim, yoldan çıktıkları için zulmedenlerin üzerine gökten bir azap indirdik.'),
(67, 2, 60, 'Musa, bir zamanlar halkı için su aramıştı. "Değneğinle taşa vur," demiştik. Bunun üzerine taştan on iki pınar fışkırmıştı. Her kabile, içmesi için ayrılan pınarı bilmişti: "ALLAH\'ın rızkından yiyin için, yeryüzünde bozgunculuk yaparak dolaşmayın."'),
(68, 2, 61, 'Fakat siz, "Musa! Artık tek bir çeşit yiyeceğe dayanamıyacağız. Rabbini bizim için çağır da bize fasulye, kabak, sarımsak, mercimek, soğan gibi toprağın bitirdiğinden yetiştirsin," demiştiniz de, "İyi olanı daha düşük olanla mı (özgürlüğü kölelikle mi) değiştirmek istiyorsunuz? İsterseniz Mısır\'a geri dönün, orada aradığınızı bulabilirsiniz!," demişti. Böylece alçaklık ve yoksulluğa mahkum edildiler ve ALLAH\'ın gazabına uğradılar. Çünkü onlar ALLAH\'ın ayetlerine karşı sürekli nankörce davranıyorlar, peygamberleri haksız yere öldürüyorlardı. Çünkü onlar, karşı gelip taşkınlıkta bulunuyorlardı.'),
(69, 2, 62, 'İnananlar, Yahudiler, Hristiyanlar ve diğer dinlerden her kim: ALLAH\'a ve ahirete inanır ve erdemli bir hayat sürdürürse, onların ödülleri Rab\'leri katındadır. Onlar için korku ve üzüntü yoktur.'),
(70, 2, 63, 'Sina dağını üzerinize kaldırarak bir zamanlar sizden söz almıştık: "Size verdiğimize kuvvetle sarılın, içindekileri hatırlayın ki korunasınız," demiştik.'),
(71, 2, 64, 'Fakat bundan sonra da yüz çevirdiniz. ALLAH\'ın size bol nimeti ve merhameti olmasaydı kaybederdiniz.'),
(72, 2, 65, 'Sizden Cumartesi yasağını çiğneyenleri elbette biliyorsunuz. Onlara "Aşağılık maymunlar olun," dedik.'),
(73, 2, 66, 'Bu cezayı çağdaşlarına ve sonraki kuşaklara bir ibret ve erdemli insanlar için de bir öğüt yaptık.'),
(74, 2, 67, 'Hani, Musa halkına: "ALLAH bir düve boğazlamanızı emrediyor," demişti. "Bizimle alay mı ediyorsun," deyince de "Cahilce davranmaktan ALLAH\'a sığınırım," dedi.'),
(75, 2, 68, '"Bizim için Rabbini çağır da onun niteliğini bize açıklasın," dediler. "O diyor ki, o ne yaşlı ne genç, ikisinin ortasında bir düvedir. Size emredileni yapın," dedi.'),
(76, 2, 69, '"Bizim için Rabbini çağır da onun rengini de açıklasın," dedi ki: "O diyor ki, o rengi parlak sarı bir düvedir, bakanların içini açar," dediler.'),
(77, 2, 70, '"Bizim için Rabbini çağır da, onun niteliğini bize daha da açıklasın. Çünkü düveler bizce birbirine benziyor. ALLAH dilerse yolu buluruz," dediler.'),
(78, 2, 71, '"O diyor ki, o düve yeri sürüp ekini sulayarak boyunduruk altında ezilmemiş, kusursuz, alacasız bir düvedir," dedi. "İşte şimdi gerçeği getirdin!," diyerek sonunda düveyi boğazladılar; az kalsın bunu yapmıyacaklardı.'),
(79, 2, 72, 'Hani bir kişiyi öldürmüş ve suçu birbirinize atmıştınız. Oysa ALLAH gizlediklerinizi açığa çıkaracaktı.'),
(80, 2, 73, '"(Düvenin) bir parçasıyla ona (öldürülene) vurun," dedik. İşte, ALLAH ölüleri böyle diriltir ve düşünesiniz diye ayetlerini (mucizelerini) böyle gösterir.'),
(81, 2, 74, 'Tüm bunlara rağmen yine kalpleriniz katılaştı; taş gibi, hatta daha katı. Çünkü öyle taşlar var ki kendisinden ırmaklar fışkırır. Bazıları yarılır, bağrından su çıkarır. Bazıları ise ALLAH\'a olan saygıdan dolayı siner. ALLAH yaptıklarınızdan habersiz değildir.'),
(82, 2, 75, 'Onların size inanacaklarını mı umuyorsunuz? Halbuki onların bir kısmı, ALLAH\'ın sözünü işitip kavradıktan sonra, bile bile onu değiştirirlerdi.'),
(83, 2, 76, 'İnananlarla karşılaşınca, "İnandık," derler; başbaşa kaldıklarında ise "Rabbiniz katında size karşı delil olarak kullanmaları için, ALLAH\'ın size açıkladığını mı onlara anlatıyorsunuz, akletmez misiniz," derler.'),
(84, 2, 77, 'Bilmezler mi ki ALLAH gizledikleri ve açıkladıkları herşeyi biliyor?'),
(85, 2, 78, 'Aralarında ümmiler var ki kuruntu ve söylentilerin dışında kitabı bilmezler; bildiklerini zannederler.'),
(86, 2, 79, 'Kitab\'ı elleriyle yazdıktan sonra onu ucuz bir fiyata satmak için onun ALLAH\'tan olduğunu söyleyenlerin vay haline. Ellerinin yazdığından dolayı vay haline onların. Kazandıklarından dolayı vay haline onların!'),
(87, 2, 80, '"Sayılı birkaç gün dışında ateş bize değmeyecek," dediler. De ki: "ALLAH\'tan böyle bir söz mü aldınız -ki ALLAH verdiği sözden dönmez- yoksa ALLAH adına bilmediğiniz bir şeyi mi söylüyorsunuz?"'),
(88, 2, 81, 'Günah işleyip suçu kendisini kuşatan kimseler ateş halkıdır; orada sürekli kalırlar.'),
(89, 2, 82, 'İnanıp erdemli bir hayat sürenler ise cennet halkıdır; onlar da orada sürekli kalırlar.'),
(90, 2, 83, 'İsrailoğullarından şöyle söz almıştık: ALLAH\'tan başkasına tapmayacak, anaya babaya, yakınlara, yetimlere ve yoksullara iyilik edeceksiniz. İnsanlarla dostça konuşacaksınız. Namazı gözetecek, zekatı vereceksiniz. Fakat bundan sonra pek azınız hariç döndünüz.'),
(91, 2, 84, 'Birbirinizin kanını dökmeyeceksiniz ve birbirinizi yurtlarından çıkarmıyacaksınız diye de sizden söz almıştık. Bunu kabul etmiş ve tanık olmuştunuz.'),
(92, 2, 85, 'Tüm bunlardan sonra, sizler hala birbirinizi öldürüyor, aranızdan bir grubu yurtlarından çıkarıyor, kötülük ve düşmanlıkta onlara karşı birleşiyorsunuz. Onları yurtlarından çıkarmanız zaten size haramken, bu yetmiyormuş gibi size esir düştüklerinde bir de onlardan fidye istiyorsunuz. Yoksa siz kitabın bir kısmına inanıp bir kısmını inkar mı ediyorsunuz? Böyle davrananların cezası dünya hayatında rezil olmak ve Diriliş Gününde de azabın en çetinine uğratılmaktan başka ne olabilir? ALLAH yaptıklarınızdan gafil değil.'),
(93, 2, 86, 'Onlar, ahiret karşılığında dünya hayatını satın alan kimselerdir. Bu yüzden azapları hafifletilmez ve kendilerine yardım edilmez.'),
(94, 2, 87, 'Musa\'ya kitabı verdik ve ondan sonra ard arda elçiler gönderdik. Meryemoğlu İsa\'ya da apaçık deliller verdik ve onu Kutsal Ruh ile destekledik. Hoşunuza gitmeyen bir şeyle ne zaman size bir elçi geldiyse büyüklük taslayarak onu yalanlamadınız mı veya öldürmediniz mi?'),
(95, 2, 88, 'Hatta "Sabit fikirliyiz," dediler. Halbuki, inkarlarından dolayı ALLAH onları lanetlemişti! Bu yüzden onların pek azı inanır.'),
(96, 2, 89, 'İnkarcılara karşı yardım beklemelerine rağmen, onlara ALLAH katından yanlarındakini doğrulayıcı bir kitap, bu bekledikleri şey, kendilerine gelince onu inkar ettiler. ALLAH\'ın laneti, inkarcılara olsun!'),
(97, 2, 90, 'ALLAH\'ın, lütfunu kullarından dilediğine indirmesini çekemiyerek ALLAH\'ın indirdiğini inkar etmek için kişiliklerini satmaları ne kötü! Böylece gazap üstüne gazaba uğradılar. Kafirlere alçaltıcı bir azap var.'),
(98, 2, 91, 'Kendilerine, "ALLAH\'ın indirdiğine inanın!," denildiğinde, "Bize indirilene inanırız," diyerek ondan sonrasını inkar ederler. Oysa bu, yanlarında bulunanı doğrulayan gerçektir. "İnanmış idiyseniz, neden daha önce ALLAH\'ın peygamberlerini öldürüyordunuz," de.'),
(99, 2, 92, 'Musa, size mucizelerle gelmişti; fakat onun ardından buzağıyı tanrı edinerek zalimlerden oldunuz.'),
(100, 2, 93, 'Hani üzerinize Tur dağını kaldırıp sizden söz almıştık: "Size verdiğim emirlere sıkıca sarılın ve dinleyin." Fakat "Dinledik ve karşı geldik," dediler. İnkarlarından dolayı kalpleri buzağı ile kandı. De ki: "İnanmışsanız, inancınız size ne de kötü yön veriyor!"'),
(101, 2, 94, 'De ki: "İleri sürdüğünüz gibi, ahiret yurdu ALLAH tarafından hiçkimseye değil sadece size ayrılmışsa ve bu savınızda samimi iseniz haydi ölümü isteyin!"'),
(102, 2, 95, 'Ellerinin işlediklerinden ötürü bunu asla dilemeyeceklerdir. ALLAH zalimleri bilir.'),
(103, 2, 96, 'Onları, yaşamaya en düşkün insanlar olarak bulacaksın; putperestlerden bile fazla... Onlardan herbiri bin sene yaşamak ister. Oysa, uzun yaşaması onu azaptan uzaklaştırmaz. ALLAH yaptıklarını görendir.'),
(104, 2, 97, 'Şunu de: "Kendinden öncekileri doğrulayıcı, inananlara yol gösterici ve müjde olarak ALLAH\'ın izniyle bunu kalbine indiren Cibril\'e her kim düşman olursa,'),
(105, 2, 98, '"Evet, ALLAH\'a, meleklerine, elçilerine, Cibril\'e ve Mikal\'e kim düşman olursa bilsin ki ALLAH da kafirlerin düşmanıdır."'),
(106, 2, 99, 'Sana apaçık ayetler indirmekteyiz. Yoldan çıkmış olanlardan başkası onları inkar etmez.'),
(107, 2, 100, 'Her ne zaman bir anlaşma yaptılarsa onlardan bir grup onu bozup atmadı mı? Zaten onların çoğu inanmaz.'),
(108, 2, 101, 'Yanlarındakini doğrulayan bir elçi ALLAH tarafından görevli olarak kendilerine gelince, kitap verilenlerin bazısı, ALLAH\'ın kitabını sırtlarının ardına attı. Bilmezlermiş gibi...'),
(109, 2, 102, 'Süleyman\'ın otoritesi hakkında şeytanların anlattığına uydular. Oysa Süleyman inkar etmedi; halka büyücülüğü ve Babil\'de Harut ve Marut adlı iki meleğe indirileni öğreten şeytanlar inkar etmişti. Bu ikisi: "Bu bir sınavdır, (bu bilgiyi kötüye kullanıp) nankör olmayın!," demedikçe kimseye onu öğretmezlerdi. Fakat o ikisinden öğrendiklerini, koca ile karısının arasını açmak için kullandılar. Oysa ALLAH\'ın izni olmadan onlar hiç kimseye bir zarar veremezdi. Kendilerine yarar vereni değil, zarar vereni öğreniyorlardı. Üstelik, ona müşteri olanların ahirette bir payı olmadığını da iyi biliyorlardı. Karşılığında kişiliklerini sattıkları şey ne kötü. Bir bilselerdi!'),
(110, 2, 103, 'Onlar inanıp günahlardan sakınmış olsalardı elbette ALLAH\'tan alacakları ödül çok daha hayırlı olurdu. Bir bilselerdi!'),
(111, 2, 104, 'İnananlar! "Raina (bize çobanlık et)," demeyin; "Unzurna (bizi gözet)," deyin ve dinleyin. İnkarcılar için acı bir azap var.'),
(112, 2, 105, 'Kitaplıların inkarcıları da, putperestler de Rabbinizden size bir iyilik indirilmesini istemez. ALLAH rahmetini dilediğine verir. ALLAH büyük lütuf sahibi.'),
(113, 2, 106, 'Daha iyisini veya benzerini getirmedikçe bir mucize veya sistemi yürürlükten kaldırmaz veya unutturmayız. ALLAH\'ın herşeye gücü yettiğini bilmez misin?'),
(114, 2, 107, 'Yerin, göklerin egemenliğinin ALLAH\'a ait olduğunu ve ALLAH\'tan başka bir sahip ve yardımcın olmadığını bilmez misin?'),
(115, 2, 108, 'Daha önce Musa\'dan istendiği gibi, siz de elçinizden isteklerde mi bulunmayı arzuluyorsunuz? Kim inancı inkar ile değiştirirse o doğru yolu kaybetmiştir.'),
(116, 2, 109, 'Kitap ehlinin birçoğu, gerçek kendilerine belli olduğu halde, özlerindeki kıskançlıktan ötürü sizi imanınızdan sonra inkara döndürmeyi arzular. ALLAH emrini getirinceye kadar onları affet, görmemezlikten gel. ALLAH herşeye gücü yetendir.'),
(117, 2, 110, 'Namazı gözetin, zekatı verin. Kendiniz için yapıp gönderdiğiniz her iyiliği elbette ALLAH katında bulacaksınız. ALLAH yaptığınız herşeyi görür.'),
(118, 2, 111, '"Yahudi veya Hristiyanlardan başkası cennete giremez," dediler. Bu, onların kuruntusudur. De ki: "Doğru sözlüler iseniz delilinizi getirin."'),
(119, 2, 112, 'Doğrusu, kim iyilik yaparak kendini ALLAH\'a teslim ederse, onun ödülü Rabbinin yanındadır; onlara korku yoktur ve onlar üzülmeyecektir.'),
(120, 2, 113, 'Yahudiler: "Hristiyanların bir temeli yok," derken, Hristiyanlar da: "Yahudilerin bir temeli yok," dediler. Oysa hepsi de kitabı okuyorlar. Cahiller de tıpkı onlar gibi konuşur. Diriliş günü ALLAH ayrılığa düştükleri konularda aralarında hüküm verecektir.'),
(121, 2, 114, 'ALLAH\'ın mescidlerinde, O\'nun isminin anılmasına engel olan ve oraların yıkılması için çalışan kimseden daha kötü kim olabilir? Halbuki onların oralara ancak korkarak girmeleri gerekir. Onlar için dünyada rezillik, ahirette de büyük azap var.'),
(122, 2, 115, 'Doğu da batı da ALLAH\'ındır. Nereye yönelirseniz ALLAH\'ın yüzü oradadır. ALLAH her şeyi kuşatan ve her şeyi bilendir.'),
(123, 2, 116, '"ALLAH çocuk edindi," dediler. Haşa, O yücedir. Göklerde ve yerde olanların hepsi O\'nundur, hepsi O\'na boyun eğmiştir'),
(124, 2, 117, 'Gökleri ve yeri yoktan var edendir. Bir işin olmasını dilerse, ona sadece "Ol," der ve olur.'),
(125, 2, 118, 'Cahiller, "ALLAH bizimle konuşmalı veya bize bir ayet (mucize) gelmeli değil miydi," dediler. Daha öncekiler de onlar gibi konuşmuşlardı. Kafaları birbirine benziyor. Biz mucizeleri, inanacak olanlara sergileriz.'),
(126, 2, 119, 'Biz seni, gerçekle, müjdeleyici ve uyarıcı olarak gönderdik. Cehennem halkından sen sorumlu değilsin.'),
(127, 2, 120, 'Dinlerine girmedikçe ne Yahudiler, ne de Hristiyanlar, senden hoşnut olmazlar. De ki: "Doğru yol ALLAH\'ın yoludur." Sana gelen bilgiden sonra onların arzularına uyarsan ALLAH\'a karşı seni savunacak ne bir dost ne de bir destekleyici bulamazsın.'),
(128, 2, 121, 'Kendilerine verdiğimiz kitabı gereği gibi izleyenler buna inanır; inkar edenlerse kaybeder.'),
(129, 2, 122, 'İsrailoğulları, size verdiğim nimetimi ve diğer halklardan daha çok size bağışta bulunduğumu hatırlayın.'),
(130, 2, 123, 'Kimsenin kimseden yana bir şey ödeyemediği, hiç kimseden fidye alınmadığı ve hiç kimseye şefaatin (aracılığın) yarar sağlamadığı ve yardımın kesildiği bir günden sakının'),
(131, 2, 124, 'Rabbi, bir zamanlar İbrahim\'i birtakım kelimelerle sınamış; o da onlara eklemişti: (Tanrı) "Seni insanlara önder yapacağım," demişti. "Soyumdan da?..," deyince, "Zalimler benim sözüme dahil olmaz" buyurmuştu.'),
(132, 2, 125, 'Kabeyi halk için bir odak noktası ve bir güven yeri kıldık. İbrahim\'in makamını bir namaz yeri olarak kullanın. "Ziyaretçiler, kendini ibadete verenler ve eğilip secde edenler için ikiniz Evimi temiz tutun," diye İbrahim ve İsmail\'i görevlendirmiştik.'),
(133, 2, 126, 'İbrahim, "Rabbim, burayı güvenlik yeri kıl. ALLAH\'a ve ahiret gününe inanan halkına ürünlerle rızık ver," deyince, "İnkar edene de rızık vereceğim. Onu kısa bir süre geçindirir, sonra onu ateş cezasına mahkum ederim. Ne kötü bir uğrak yeridir orası!," dedi.'),
(134, 2, 127, 'İbrahim, İsmail ile birlikte evin (kabenin) temellerini yükseltiyor: "Rabbimiz, bizden kabul et, şüphesiz sen İşitensin, Bilensin."'),
(135, 2, 128, '"Rabbimiz, ikimizi sana teslim olanlar (müslümanlar) yap. Soyumuzdan da sana teslim olan bir topluluk çıkar. Bize nasıl ibadet edeceğimizi göster, sana yönelmemizi kabul et, kuşkusuz sen yönelişleri kabul edensin, Rahimsin."'),
(136, 2, 129, '"Rabbimiz, onların arasından, ayetlerini onlara okuyacak, onlara kitabı ve bilgeliği öğretecek ve onları temizleyecek bir elçi gönder. Sen Yücesin, Bilgesin."'),
(137, 2, 130, 'Kendini kandırandan başkası İbrahim\'in dininden yüz çevirmez. Onu bu dünyada seçtik, ahirette de erdemli kişilerden olacak.'),
(138, 2, 131, 'Rabb\'i, kendisine "Teslim ol," dediğinde; "Evrenlerin Rabbine teslim oldum," diye karşılık vermişti.'),
(139, 2, 132, 'İbrahim ve daha sonra Yakup şunu çocuklarına öğütledi: "Evlatlarım! ALLAH sizin için bu dini seçti; müslüman olarak ölmeye bakın."'),
(140, 2, 133, 'Yakup can verirken tanık olsaydınız. O, çocuklarına: "Benden sonra kime kulluk edeceksiniz," diye sormuştu. Onlar da, "Senin Tanrın, ataların İbrahim, İsmail ve İshak\'ın Tanrısı olan tek Tanrıya kulluk edeceğiz, biz O\'na teslim olanlarız," demişlerdi.'),
(141, 2, 134, 'Geçmişteki bir toplum bu! Onların kazandıkları kendilerinin, sizin kazandıklarınız sizindir. Siz onların yaptıklarından sorulmazsınız.'),
(142, 2, 135, '"Yahudi veya Hristiyan olun ki doğru yolu bulasınız," dediler. De ki: "Hayır, biz İbrahim\'in tektanrıcı dinine uyarız. O, ortak koşanlardan olmadı."'),
(143, 2, 136, '"ALLAH\'a, bize indirilene, İbrahim, İsmail, İshak, Yakub ve torunlarına indirilenlere, Musa\'ya, İsa\'ya verilene ve tüm peygamberlere Rab\'leri tarafından verilenlere inandık. Onların hiçbiri arasında ayırım yapmayız. Biz sadece O\'na teslim olanlarız," deyiniz.'),
(144, 2, 137, 'Sizin inandığınız gibi inansalar doğru yolu bulmuş olurlar. Yüz çevirirlerse, karşıt olmuş olurlar. Onlara karşı ALLAH sana yeter. O İşitendir, Bilendir.'),
(145, 2, 138, 'Budur ALLAH\'ın sistemi! Kimin sistemi ALLAH\'ınkinden daha iyidir? "Biz yalnız O\'na kulluk ederiz."'),
(146, 2, 139, '"Bizimle ALLAH hakkında mı tartışıyorsunuz? Oysa O, bizim de Rabbimiz, sizin de Rabbinizdir. Biz işlediğimizden, siz de işlediğinizden sorumlusunuz. Biz kendimizi sadece O\'na adadık," de.'),
(147, 2, 140, 'İbrahim, İsmail, İshak, Yakub ve torunlarının Yahudi veya Hristiyan olduğunu mu ileri sürüyorsunuz? De ki: "Siz mi daha iyi biliyorsunuz yoksa ALLAH mı? ALLAH\'ın bildirdiğini gizleyenden daha zalim kim olabilir? ALLAH yaptıklarınızdan gafil değil."'),
(148, 2, 141, 'İşte onlar bir toplumdu; gelip geçtiler. Onların kazandıkları kendilerinin, sizin kazandıklarınız sizindir. Siz onların yaptıklarından sorulmazsınız.'),
(149, 2, 142, 'Halktan bazı beyinsizler: "Yöneldikleri kıbleden onları çeviren nedir?," diyecekler. De ki: "Doğu da batı da ALLAH\'ındır. O dileyeni doğru yola iletir."'),
(150, 2, 143, 'Böylece sizi açık fikirli bir toplum kıldık ki halkın arasında tanıklar olabilesiniz ve elçi de aranızda tanık olabilsin. Elçiye uyanlarla topukları üzerinde geriye dönenleri birbirinden ayırmak için eskiden yöneldiğin kıbleyi değiştirdik. ALLAH\'ın yol gösterdiği kimseden başkasına elbette bu ağır gelir. ALLAH imanınızı boşa çıkarmaz. ALLAH insanlara Şefkatlidir, Rahimdir.'),
(151, 2, 144, 'Yüzünü göğe çevirip durduğunu görüyoruz. Seni, hoşlanacağın bir kıbleye çevireceğiz. Artık yüzünü Kutsal Mescid\'e çevir. Nerede olursanız olun yüzlerinizi o yöne çevirin. Kuşkusuz, kendilerine kitap verilenler, bunun Rab\'lerinden gelen bir gerçek olduğunu bilirler. ALLAH onların yaptığından gafil değildir.'),
(152, 2, 145, 'Kitap verilenlere her türlü ayeti (mucizeyi) getirsen de onlar yine senin kıblene yönelmez. Sen de onların kıblesine uyacak değilsin. Onlar birbirlerinin kıblesine de uymaz. Sana gelen bu bilgiden sonra, onların keyfine uyarsan zalimlerden olursun.'),
(153, 2, 146, 'Kendilerine kitap verdiklerimiz, bu gerçekleri çocuklarını tanıdıkları gibi tanırlar; ama yine de onlardan bir grup bile bile gerçeği gizler.'),
(154, 2, 147, 'Bu, Rabbinden gelen gerçektir; artık kuşkulanma'),
(155, 2, 148, 'Her birinizin seçtiği bir yön ve yöntem var; siz iyilikte yarışın. Nerede olursanız olun ALLAH sizi bir araya getirir. ALLAH elbette her şeye gücü yetendir'),
(156, 2, 149, 'Her nereden yola çıkarsan çık, (namaz için) yüzünü Kutsal Mescide doğru çevirmelisin. Bu, elbette Rabbinden gelen bir gerçektir. ALLAH yaptığınız hiç bir şeyden habersiz değildir.'),
(157, 2, 150, 'Her nereden yola çıkarsan çık, (namaz için) yüzünü Kutsal Mescide doğru çevir. Nerede olursanız olun, yüzünüzü onun tarafına çevirin ki halkın size karşı bir eleştiri malzemesi olmasın. Zalimlere gelince, onlardan çekinmeyin, benden çekinin ki size olan nimetimi tamamlayayım ve siz de doğruya ulaşabilesiniz.'),
(158, 2, 151, 'Nitekim, size ayetlerimi okuyacak, sizleri temizleyecek, size kitap ve bilgeliği öğretecek, bilmediklerinizi bildirecek bir elçiyi aranızdan seçip gönderdik.'),
(159, 2, 152, 'Beni hatırlayın ki ben de sizi hatırlayayım. Bana teşekkür edin; nankörlük etmeyin.'),
(160, 2, 153, 'İnananlar! Güçlüklere karşı sabır ve namaz ile yardım dileyin. ALLAH sabredenlerle beraberdir.'),
(161, 2, 154, 'ALLAH yolunda öldürülenlere, "Onlar ölülerdir," demeyin. Aksine onlar diridir fakat siz farkında değilsiniz.'),
(162, 2, 155, 'Sizi korkuyla, açlıkla, para, can, ve ürün kaybıyla sınayacağız. Müjde ver sabredenlere…'),
(163, 2, 156, 'Ki onlara bir musibet geldiğinde, "Biz ALLAH\'a aidiz ve O\'na dönücüyüz," derler'),
(164, 2, 157, 'İşte onlara, Rab\'lerinden destek ve rahmet vardır. Onlar doğruya ulaşanlardır.'),
(165, 2, 158, 'Safa ve Merve (tepeleri) ALLAH\'ın işaretlerindendir. Hacc veya ziyaret (umre) için Ev\'e varan birisi o ikisi arasında gidip gelebilir. Kim gönülden bir iyilik yaparsa, ALLAH Şükrede, Bilir.'),
(166, 2, 159, 'İndirdiğimiz açık delilleri ve hidayeti, -biz kitapta halka açıkladıktan sonra- gizleyenleri hem ALLAH ve hem de tüm lanetleyenler lanetler.'),
(167, 2, 160, 'Ancak, yönelip kendilerini düzeltenler ve (kitabı) açıklayanlar hariç; onların tevbesini kabul ederim. Ben tevbeleri kabul edenim, Rahim\'im.'),
(168, 2, 161, 'İnkar edip inkarcı olarak ölenler ise hem ALLAH\'ın, hem meleklerin ve hem halkın lanetini kazanır.'),
(169, 2, 162, 'O durumda sürekli kalırlar. Azapları hafifletilmez ve ertelenmez.'),
(170, 2, 163, 'Tanrınız bir tek Tanrı; O\'ndan başka tanrı yoktur. Rahman\'dır, Rahim\'dir.'),
(171, 2, 164, 'Göklerin ve yerin yaratılışında, gecenin ve gündüzün birbiriyle yer değiştirmesinde, insanların yararı için okyanusta akıp giden gemilerde, ALLAH\'ın gökten su indirip ölümünden sonra yeryüzünü diriltmesinde ve orada yaydığı her çeşit canlıda, rüzgarları ve gök ile yer arasında hazır bekleyen bulutları evirip çevirmesinde aklını kullanan bir toplum için elbette ayetler (dersler ve kanıtlar) vardır.'),
(172, 2, 165, 'ALLAH\'tan başkasını tanrı edinen ve ALLAH\'ı sever gibi onları seven kimseler var. İnananlar ise en çok ALLAH\'ı sever. O zulmedenler, azabı gördükleri zaman tüm gücün ALLAH\'a ait ve ALLAH\'ın azabının çetin olduğunu anlayacaklarını bir bilselerdi!'),
(173, 2, 166, 'O zaman, uyulanlar kendilerine uyanlardan uzak duracaktır. Artık azabı görmüşlerdir ve aralarındaki tüm bağlar kesilmiştir.'),
(174, 2, 167, 'Uyanlar şöyle diyecek: "Keşke bir şansımız daha olsaydı da onların şimdi bizden uzak durdukları gibi biz de onlardan uzak dursaydık." Böylece ALLAH yaptıklarını pişmanlığa dönüştürür; ateşten çıkamazlar.'),
(175, 2, 168, 'İnsanlar! Yerin helal ve temiz ürünlerinden yeyin, Şeytanın adımlarını izlemeyin; o size açık düşmandır.'),
(176, 2, 169, 'O size kötülüğü, hayasızlığı ve ALLAH\'a bilmediğiniz şeyleri yakıştırmanızı emreder.'),
(177, 2, 170, 'Onlara, "ALLAH\'ın indirdiğine uyun," dense, "Hayır, biz atalarımızın izlediği yolu izleriz," derler. Peki, ataları bir şey düşünemiyen ve doğru yolu bulamıyan kimseler olsalar da mı?!'),
(178, 2, 171, 'İnkarcıların durumu, sözleri ancak bağırma ve çağırma biçiminde algılayarak (anlamadan) tekrarlayan kişi gibidir. Sağır, dilsiz ve kördürler; düşünmezler.'),
(179, 2, 172, 'İnananlar, size verdiğimiz rızıkların iyilerinden yeyin. ALLAH\'a şükredin, sadece O\'na tapıyorsanız.'),
(180, 2, 173, 'O, size sadece leş, kan, domuz eti ve ALLAH\'tan başkası adına kesileni haram kılmıştır. Her kim (bunları yemeye) mecbur kalırsa, (suistimal yolunu) aramamak ve sınırı aşmamak koşuluyla günah işlemiş olmaz. ALLAH Bağışlayandır, Rahimdir.'),
(181, 2, 174, 'ALLAH\'ın indirdiği kitaptan bir şey gizleyip onu az bir değere değişenler, karınlarına ateş tıkınıyorlar. Diriliş Gününde ALLAH onlarla konuşmaz ve onları arındırmaz. Onlara acı verici bir azap var.'),
(182, 2, 175, 'Onlar, hidayet karşılığında sapıklığı ve affedilme karşılığında azabı satın almışlardır. Ateşe ne kadar da dayanıklıdırlar!'),
(183, 2, 176, 'Böyledir; çünkü ALLAH gerçeği içeren kitabı indirmiştir ve kitap hakkında tartışanlar ise doğrusu derin bir anlaşmazlık içindedir.'),
(184, 2, 177, 'Yüzlerinizi doğu veya batı yönüne çevirmeniz iyilik değil. İyiler o kimseler ki ALLAH\'a, ahiret gününe, meleklere, kitaba ve peygamberlere inanırlar; akrabalara, yetimlere, muhtaçlara, yolda kalmışlara, dilencilere ve köleleri özgürlüğe kavuşturmaya seve seve para yardımında bulunurlar; namazı gözetir, zekatı verir, sözleştikleri vakit sözlerinde dururlar; zorluğa, sıkıntıya ve zulme karşı direnirler. İşte doğru olanlar onlardır, erdemli olanlar da onlardır.'),
(185, 2, 178, 'İnananlar! Öldürmede size eşitlik farz kılındı. Hürre karşı hür, köleye köle, kadına kadın... Ama kim maktulun hısımları tarafından bağışlanırsa, o zaman uygun olanı yapması ve diyeti güzelce ödemesi gerekir. Bu, Rabbinizden bir hafifletme ve rahmettir. Bundan sonra kim sınırı aşarsa onun için acı bir azap var.'),
(186, 2, 179, 'Sizin için bu eşitlikte hayat kurtarma vardır, ey akıl sahipleri, böylece korunursunuz.'),
(187, 2, 180, 'Sizden birine ölüm yaklaştığında, bir mal bırakacaksa anaya babaya, yakınlara, uygun bir biçimde vasiyet etmesi farz kılındı. Bu, erdemliler için bir görevdir.'),
(188, 2, 181, 'Kim işittikten sonra onu değiştirirse, günahı onu değiştirenleredir. ALLAH İşitendir, Bilendir.'),
(189, 2, 182, 'Vasiyet edenin yanlışlık veya haksız bir paylaşım yapacağını sezen birisi olaya karışıp durumu düzeltirse günah işlemiş olmaz. ALLAH Bağışlayandır, Rahimdir.'),
(190, 2, 183, 'İnananlar, sizden öncekilere oruç farz kılındığı gibi, sakınmanız için size de farz kılındı.'),
(191, 2, 184, 'Sayılı günlerde... Hasta olanlarınız veya yolculukta bulunanlarınız tutamadığı günlerin sayısınca diğer günlerde tutar. Güç yetirenler bir yoksulu doyurarak adakta bulunsunlar. Kim gönül isteğiyle (daha fazla yoksulu doyurmak için) iyilik yaparsa kendisi için daha iyidir; ancak oruç tutmanız sizin için en iyisidir, bir bilseniz!'),
(192, 2, 185, 'Ramazan, insanlara yol gösterici, apaçık bir öğreti ve yasa kitabı olan Kuran\'ın indirildiği aydır. Kim o aya ulaşırsa oruç tutsun. Hasta veya yolcu olanlarınız, tutamadığı günlerin sayısınca diğer günlerde oruç tutar. ALLAH sizin için kolaylık ister, zorluk istemez. Böylece (oruç günlerinin) sayısını tamamlar, sizi doğruya ulaştıran ALLAH\'ı yüceltip şükredersiniz.'),
(193, 2, 186, 'Kullarım beni sana soracak olurlarsa bilsinler ki ben yakınım. Beni çağırdığı vakit çağıranın çağrısına karşılık veririm. Doğru yolu bulmaları için onlar da bana karşılık vermeli ve bana inanmalı.'),
(194, 2, 187, 'Oruç gecelerinde kadınlarınızla cinsel ilişkide bulunmanız size helal kılındı. Onlar sizin (sırlarınızı gizleyen) örtüleriniz, siz de onların örtülerisiniz. ALLAH, kendinizi kandırıp durduğunuzu bildi de tevbenizi kabul etti ve sizi bağışladı. Artık ALLAH\'ın sizin için belirlediğini dileyerek onlarla cinsel ilişkide bulunabilirsiniz. Şafağın beyaz ve siyah ipliğini birbirinden ayırdedinceye kadar yeyin, için. Sonra geceye kadar orucu tamamlayın. Mescitlere kapanmış durumdayken onlarla cinsel ilişkide bulunmayın. Bunlar ALLAH\'ın koyduğu sınırlardır; onları çiğnemeyin. ALLAH korunmaları için ayetlerini halka böyle açıklar.'),
(195, 2, 188, 'Paralarınızı aranızda haksız yollarla yemeyin. Halkın parasını haksız yere yemek amacıyla bile-bile memurlara/yöneticiler rüşvet vermeyin.'),
(196, 2, 189, 'Sana ayın evrelerini soruyorlar. De ki o, insanlar ve hac ibadeti için bir zaman ölçüsüdür. İyilik, lafı dolandırmak değildir, iyilik sakınmaktır. Dürüst olun. Kurtuluşunuz için ALLAH\'ı dinleyin.'),
(197, 2, 190, 'Sizinle savaşanlarla ALLAH yolunda savaşın. Saldırgan olmayın. ALLAH saldırganları sevmez.'),
(198, 2, 191, 'Onları yakaladığınız yerde öldürün ve sizi çıkardıkları yerden onları çıkarın; zulüm ve işkence öldürmekten beterdir. Kutsal Mescid\'in yanında sizinle savaşmadıkça onlarla savaşmayın. Size saldırırlarsa siz de onlara saldırın. İnkarcıların cezası böyledir.'),
(199, 2, 192, 'Son verirlerse, ALLAH Bağışlayandır, Rahimdir.'),
(200, 2, 193, 'Zulüm ve işkence ortadan kalkıncaya ve din ALLAH için oluncaya kadar onlarla savaşın. Son verirlerse, artık zalimlerden başkasına düşmanlık yoktur.'),
(201, 2, 194, 'Kutsal ay ancak iki taraflı gözetilebilir. Ateşkese uymak karşılıklıdır. Size saldırırlarsa onlara aynen saldırın. ALLAH\'ı dinleyin ve bilin ki ALLAH erdemlilerin yanındadır.'),
(202, 2, 195, 'ALLAH yolunda harcayın, kendi kendinizi zarara sokmayın. İyilik edin. ALLAH iyilik edenleri sever.'),
(203, 2, 196, 'Hac ve umreyi ALLAH için tamamlayın. Engellenirseniz kolayınıza gelen bir kurban gönderin. Kurban, yerine ulaşıncaya kadar başınızı traş etmeyin; ancak hasta olan veya başından bir rahatsızlığı bulunanlar, oruç, sadaka veya herhangi bir ibadetle fidye vermeli. Güven ortamında, her kim Hac zamanına kadar (ihramdan çıkarak) umreden yararlanırsa, kolayına gelen bir kurban göndermeli. Bunu yerine getiremiyenler, hac anında üç gün, ve döndükten sonra da yedi gün olmak üzere toplam on gün oruç tutmalı. Bu, ailesi Kutsal Mescid civarında oturmayanlar içindir. ALLAH\'ı dinleyin ve bilin ki ALLAH\'ın azabı çetindir.'),
(204, 2, 197, 'Hac, bilinen aylarda uygulanmalı. Kim o aylarda hacca karar vermişse bilsin ki, hacda cinsel ilişki, kötülük yapmak, tartışmak yoktur. Yaptığınız her iyiliği ALLAH bilir. Yol için azığınızı hazırlarken en hayırlı azığın erdemlilik olduğunu unutmayın. Anlayış sahipleri! Beni dinleyin'),
(205, 2, 198, 'Rabbinizin lütfunu (hac bölgesinde ticaret yaparak) aramanızda sakınca yok. Arafat\'tan ayrıldığınızda Kutsal Bölgede ALLAH\'ı anın. Sizi doğru yola ilettiği için O\'nu anın; bundan önce siz sapıtmıştınız.'),
(206, 2, 199, 'Sonra, halkın topluca yayıldığı yerden siz de yayılın, ALLAH\'tan bağışlanma dileyin. ALLAH Bağışlayandır, Rahimdir.'),
(207, 2, 200, 'Töreninizi bitirdiğiniz zaman, atalarınızı andığınız gibi, hatta daha güçlü olarak ALLAH\'ı anmayı sürdürün. Halktan kimi "Rabbimiz, bize bu dünyada ver," der; onun ahiretten bir payı olmaz.'),
(208, 2, 201, 'Kimi de, "Rabbimiz, bize dünyada da iyilik ver, ahirette de iyilik ver. Bizi ateşin azabından koru," der.'),
(209, 2, 202, 'Hepsinin kazandıklarından bir payı vardır. ALLAH hesabı çabuk görendir.'),
(210, 2, 203, 'Sayılı günlerde (Mina\'da) ALLAH\'ı anın. Erdemli davrandıkları sürece, bunu iki günde bitirmek için acele edene bir günah yoktur, geri kalana da bir günah yoktur. ALLAH\'ı dinleyin, ve O\'nun huzurunda toplanacağınızı unutmayın.'),
(211, 2, 204, 'Dünya hayatı hakkında, sözleri senin hoşuna giden bazı kişiler var. Azılı bir düşman olduğu halde kalbinde olana ALLAH\'ı tanık tutar.'),
(212, 2, 205, 'Dönüp gidince, yeryüzünde bozgunculuk yapmak, malı ve nesli yok etmek için çabalar. ALLAH bozgunculuğu sevmez.'),
(213, 2, 206, 'Kenidisine "ALLAH\'ı dinle," dendiğinde kibir ve gurur içinde hiddetlenir. Onun hakkından cehennem gelir; ne kötü bir meskendir!'),
(214, 2, 207, 'Halktan öylesi de var ki kendini ALLAH\'ın rızasını kazanmaya adar. ALLAH kullarına çok şefkatlidir.'),
(215, 2, 208, 'İnananlar, tümüyle teslim olun. Şeytanın adımlarını izlemeyin; çünkü o sizin apaçık düşmanınızdır.'),
(216, 2, 209, 'Size apaçık deliller gelmesine rağmen doğru yoldan kayarsanız, bilesiniz ki ALLAH Üstündür, Bilgedir.'),
(217, 2, 210, 'ALLAH\'ın ve meleklerin yoğun bulutlar arasından kendilerine gelmesini mi bekliyorlar? O zaman her şey biterdi ve her şey ALLAH\'a döndürülürdü.'),
(218, 2, 211, 'İsrailoğullarına sor, onlara nice apaçık ayetler verdik! Kendisine ALLAH\'ın nimeti geldikten sonra onu değiştirenler için ALLAH ağır bir ceza verir.'),
(219, 2, 212, 'İnkar edenler için dünya hayatı çekicidir. Bu yüzden inananlarla alay ederler. Oysa erdemliler Diriliş Günü onların üstündedir. ALLAH dilediğine hesapsız şekilde nimetler bahşeder.'),
(220, 2, 213, 'İnsanlar bir tek topluluktu. ALLAH peygamberleri müjdeleyici ve uyarıcı olarak gönderdi ve anlaşmazlığa düştükleri konularda halkın arasında hükmetmeleri için onlarla birlikte gerçeği içeren kitabı indirdi. Oysa kitap verilenler kendilerine açık kanıtlar geldikten sonra aralarındaki kıskançlıktan ötürü onun hakkında anlaşmazlığa düştüler. Fakat ALLAH, izniyle inananları onların anlaşmazlığa düştüğü gerçeğe ulaştırdı. ALLAH dilediğini/dileyeni doğru yola iletir.'),
(221, 2, 214, 'Yoksa siz, sizden öncekilerin başına gelenler gibisi sizin de başınıza gelmeden cennete gireceğinizi mi sanıyorsunuz? Onlar zorluk ve sıkıntıya uğradılar ve öylesine sarsıldılar ki elçi ve beraberindeki inananlar, "ALLAH\'ın yardımı ne zaman," dediler. İyi bilin ki ALLAH\'ın yardımı yakındır.'),
(222, 2, 215, 'Nasıl yardım edeceklerini soruyorlar sana. De ki, "Para ve mal yardımlarınızı ana-babaya, yakınlara, öksüzlere, yoksullara ve yolda kalmışlara yapmalısınız." İşlediğiniz her iyiliği ALLAH bilir.'),
(223, 2, 216, 'Sevmediğiniz halde savaş size zorunlu kılındı. Sevmediğiniz bir şey sizin için iyi ve sevdiğiniz bir şey de sizin için kötü olabilir. Siz bilmeseniz de ALLAH bilir.'),
(224, 2, 217, 'Sana, Kutsal Ayda savaşmak konusunu da soruyorlar. De ki: "Onda savaş büyük bir günahtır. Fakat ALLAH\'ın yolundan çevirmek, O\'na ve Kutsal Mescid\'e nankörlük etmek ve halkını oradan çıkarmak ALLAH yanında daha büyük bir günahtır. Çünkü baskı ve zulüm, öldürmekten daha büyük bir suçtur." Güçleri yetse sizi dininizden döndürünceye kadar sizinle savaşırlar. Sizden kim dininden döner ve inkarcı olarak ölürse, tüm yaptıkları dünyada ve ahirette boşa çıkar. Onlar ateş halkıdır ve orada sürekli kalıcıdır.'),
(225, 2, 218, 'İnananlar, ALLAH yolunda göç edenler ve çaba harcayanlar ALLAH\'ın rahmetini umar. ALLAH Bağışlayandır, Rahimdir.'),
(226, 2, 219, 'Sana sarhoş edicilerden ve kumardan sorarlar: "O ikisinde büyük bir günah ve insanlar için yararlar var; ancak günahları yararlarından daha büyüktür," de. Ayrıca, sadaka olarak neyi vereceklerini senden sorarlar: "Artanı," de. ALLAH ayetlerini işte böyle açıklıyor ki düşünesiniz...'),
(227, 2, 220, 'bu dünya ve ahiret hakkında... Sana bir de öksüzler hakkında sorarlar: De ki, "Onları erdemli kişiler olarak yetiştirmeniz en büyük iyiliktir. Mallarını mallarınıza katarsanız aile bireyiniz olurlar." ALLAH bozanı düzeltenden ayırdetmesini bilir. ALLAH dileseydi sizi zora sokardı. ALLAH Güçlüdür, Bilgedir.'),
(228, 2, 221, 'İnanmadıkça, ortak koşan kadınlarla evlenmeyin. İnanan bir hizmetçi kadın ortak koşan bir kadından daha hayırlıdır; ondan hoşlansanız bile. Ortak koşan erkeklerle, inanıncaya kadar kızlarınızı evlendirmeyin. İnanan bir hizmetçi erkek ortak koşandan daha hayırlıdır, hoşlansanız bile... Onlar, ateşe çağırıyor. ALLAH ise cennete ve bağışlanmaya çağırır ve öğüt almaları için ayetlerini halka açıklar.'),
(229, 2, 222, 'Sana aybaşı halini sorarlar, De ki: "O bir rahatsızlıktır. Aybaşı halinde olan kadınlarla cinsel ilişkiye girmeyin ve ondan kurtuluncaya kadar onlara yaklaşmayın. Kurtuldukları zaman ALLAH\'ın size uygun gördüğü yerden onlarla cinsel ilişkide bulunun. ALLAH yönelenleri sever, arınanları sever."'),
(230, 2, 223, 'Kadınlarınız, (tohum ektiğiniz) tarlalarınızdır. Tarlanıza dilediğiniz gibi varın. Kendiniz için geleceğe hazırlanın. ALLAH\'ı dinleyin ve onunla mutlaka karşılaşacağınızı bilin. İnananlara müjde ver.'),
(231, 2, 224, 'Halk arasında iyi, erdemli ve dürüst görünmek için ALLAH\'ı yeminlerinize alet etmeyin. ALLAH İşitir, Bilir.'),
(232, 2, 225, 'ALLAH yaptığınız kasıtsız yeminlerinizden sizi sorumlu tutmaz; ancak kalbinizdeki gerçek niyetinizden sorumlu tutar. ALLAH Bağışlayandır, Şefkatlidir.'),
(233, 2, 226, 'Kadınlarını boşamayı tasarlayanlar (sağlıklı bir karar için) dört ay beklemeli. Vazgeçip uzlaşırlarsa ALLAH Bağışlayandır, Rahimdir.'),
(234, 2, 227, 'Boşamaya kararlıysalar, ALLAH İşiten ve Bilendir.'),
(235, 2, 228, 'Boşanan kadınlar (başkasıyla evlenmeden önce) kendi kendilerine üç aybaşı beklemeli. ALLAH\'a ve ahiret gününe inanıyorlarsa ALLAH\'ın rahimlerinde yarattığını gizlemeleri uygun olmaz. Bu durumda (gebe olmaları halinde) kocaları barışmak isterlerse onları geri almağa daha fazla hak sahibidirler. Erkeklerin kadınlar üzerindeki hakları gibi, kadınların da erkekler üzerinde eşit hakları vardır. (Gebelik durumunda) Erkeklerin onların üzerinde bir derecesi vardır. ALLAH Güçlüdür, Bilgedir.'),
(236, 2, 229, 'Boşama iki defadır. Boşanmış kadınları ya iyilikle barındırmak ya da iyilikle bırakmak gerekir. Onlara vermiş bulunduğunuz şeyleri geri almanız size helal olmaz; eşler ALLAH\'ın sınrını gözetememekten korkarlarsa başka. ALLAH\'ın sınırlarını gözetememekten korkarsanız, o zaman kadının isteyerek geri verdiği şeylerden dolayı günaha girmezler. Bunlar ALLAH\'ın sınırlarıdır; onları çiğnemeyin. ALLAH\'ın sınırlarını kimler çiğnerse işte zalimler onlardır.'),
(237, 2, 230, 'Kadını yine (üçüncü kez) boşarsa, kadın başka birisiyle evlenmedikça ona bundan sonra helal olmaz. ALLAH\'ın yasasını gözeteceklerine inanırlarsa tekrar birbirlerine dönmelerinde bir sakınca yoktur. Bu ALLAH\'ın yasasıdır; onu bilen bir toplum için açıkladık.'),
(238, 2, 231, 'Kadınları boşadığınız zaman, bekleme sürelerini (üç aybaşını) bitirdiler mi onları ya iyilikle yanınızda tutun veya iyilikle bırakın. İntikam almak için onlara zarar verecek şekilde tutmayın. Bunu yapan kendisine zulmetmiş olur. ALLAH\'ın ayetlerini hafife almayın. ALLAH\'ın size verdiği nimetlerini ve size öğüt vermek için size indirdiği kitabı ve hikmeti düşünün. ALLAH\'ı dinleyin ve bilin ki ALLAH her şeyi Bilir'),
(239, 2, 232, 'Boşanan kadınlar bekleme sürelerini bitirdikten sonra, kocalarıyla güzellikle anlaştıkları taktirde o kadınların tekrar evlenmelerine engel olmayın. İçinizden ALLAH\'a ve ahiret gününe inanan kimseler bundan öğüt alır. Bu sizin için daha arı ve daha sağlıklıdır. Siz bilmeseniz de ALLAH bilir.'),
(240, 2, 233, 'Emzirme süresini tamamlamak istiyenler için analar bebeklerini tam iki yıl emzirmeli. Ananın yiyecek ve giyecek ihtiyacını ise çocuğun babası güzel ve uygun bir şekilde karşılamalı. Kimse kapasitesinin üzerinde sorumlu tutulamaz. Ne anne çocuğu yüzünden, ne de babası çocuğu yüzünden zarara sokulmasın. Bunu gerçekleştirmek mirasçısının da görevidir. Ana ve baba danışıp anlaştıktan sonra sütten kesmek isterse, ikisine de bir günah yoktur. Çocuklarınızı sütanneye emzirtmek isterseniz ücretini uygun bir biçimde ödediğiniz sürece size bir sorumluluk yoktur. ALLAH\'ı dinleyin, ALLAH yaptıklarınızı Görür.'),
(241, 2, 234, 'İçinizden ölen erkeklerin geride bıraktığı eşleri, dört ay ve on (gün) beklerler. Sürelerini doldurunca artık kendileri için uygun olanı yapmalarında size sorumluluk yoktur. ALLAH yaptıklarınızdan Haberlidir.'),
(242, 2, 235, 'Evlenme önerilerinizi o kadınlara bildirmenizden, ya da bu niyetinizi içinizde tutmanızdan dolayı size bir günah yoktur. ALLAH onlar hakkında düşüneceğinizi bilir. Onlarla uygun ve erdemli bir şekilde konuşma amacının dışında gizlice sözleşip buluşmayın. Bekleme süreleri dolmadan onlarla nikah bağını kurmaya kalkışmayın. Bilin ki ALLAH içinizde olanı bilir; öyleyse O\'ndan çekinin. Yine bilin ki ALLAH Bağışlayandır, Şefkatlidir.'),
(243, 2, 236, 'Henüz dokunmadan, yahut mehirlerini biçmeden önce kadınları boşamanızda bir sakınca yok. Bu durumda ödemede bulunun. Zengin, kendi gücü oranında, yoksul da kendi gücü oranında verdikleri zarara uygun bir tazminat vermeli. Bu, güzel davrananların görevidir.'),
(244, 2, 237, 'Mehir biçtikten sonra, ancak onlara dokunmadan önce onları boşamışsanız kendilerine söz verdiğiniz mehrin yarısını vermelisiniz. Ancak, kadın hakkından vazgeçerse veya koca mehrin tümünü vermek isterse başka... Vazgeçmeniz daha erdemli bir davranıştır. Aranızdaki dostluğu unutmayın. ALLAH yaptıklarınızı Görendir.'),
(245, 2, 238, 'Namazlara, özellikle orta namaza dikkat edin. Kendinizi tümüyle ALLAH\'a vererek namaza durun.'),
(246, 2, 239, 'Bir kaygı ve endişeniz varsa, yaya veya binmiş olarak (namazı kılın.) Güvene kavuştuğunuz zaman, bilmediğiniz şeyleri size öğrettiği gibi ALLAH\'ı anın.'),
(247, 2, 240, 'Ölüp de geriye eşler bırakan erkekleriniz, eşlerinin evlerinden çıkarılmaksızın bir yıl boyunca geçimlerinin sağlanmasını vasiyet etsinler. Çıkarlarsa, kendileri için uygun olanı yapmalarından siz sorumlu değilsiniz. ALLAH Güçlüdür, Bilgedir.'),
(248, 2, 241, 'Boşanmış kadınlar için de uygun bir geçim yardımı yapılmalı. Bu, erdemli kişilerin görevidir.'),
(249, 2, 242, 'Düşünesiniz diye ALLAH ayetlerini böyle açıklar.'),
(250, 2, 243, 'Sayıları binlerce olduğu halde ölüm korkusuyla yurtlarını terkedenler dikkatini çekti mi? ALLAH onlara "Ölün," dedi de sonra onları diriltti. ALLAH insanlara lütuf sahibidir; ancak insanların çoğu şükretmez.'),
(251, 2, 244, 'ALLAH yolunda savaşın. Bilesiniz ki ALLAH İşitir, Bilir.'),
(252, 2, 245, 'Kat kat ödenecek güzel bir ödüncü kim ALLAH\'a, sunar? Kısan da açan da ALLAH\'tır. O\'na döndürüleceksiniz.'),
(253, 2, 246, 'Şu olay dikkatini çekmedi mi? İsrailoğullarının ileri gelenleri, Musa\'dan sonra peygamberlerinden birine, "Bize bir kral atasan da ALLAH uğrunda savaşsak," demişlerdi. "Ya savaş gerekir de savaşmazsanız?!," demişti. Onlar, "Yurdumuzdan ve çocuklarımızdan uzaklaştırılmış durumda iken neden ALLAH yolunda savaşmayalım," diye karşılık vermişlerdi. Fakat kendilerine savaş farzedilince pek azı hariç yüz çevirdiler. ALLAH zalimleri Bilir.'),
(254, 2, 247, 'Peygamberleri onlara, "ALLAH size kral olarak Talut\'u atadı," dedi. Onlar, "Biz yönetime ondan daha layık olduğumuz halde nasıl olur da üzerimize buyruk sahibi olabilir? Üstelik zengin biri de değil," dediler. O da, "ALLAH onu üzerinize seçti. Onun bilgi ve beden gücünü arttırdı," dedi. ALLAH mülkünü dilediğine verir. ALLAH Cömerttir, Bilendir.'),
(255, 2, 248, 'Peygamberleri onlara: "Onun hükümdarlığının kanıtı, sandığın size gelmesidir. Onda Rabbinizden bir huzur ve Musa ile Harun\'un halkının geriye bıraktığı bir kalıntı bulacaksınız. Onu melekler taşımaktadır. İnanıyorsanız bunda sizi ikna edecek bir delil var."'),
(256, 2, 249, 'Talut ordunun kumandasını alınca şunları bildirdi: "ALLAH sizi bir ırmakla sınayacak. Kim ondan içerse benden değildir. Kim onu tatmayıp sadece eliyle bir yudum alırsa bendendir." Pek azı dışında hepsi ondan içti. O, beraberindeki inananlarla ırmağıgeçince, "Bugün Calut ve ordusuna karşı koyacak gücümüz yok," dediler. ALLAH ile karşılaşacaklarına inananlar ise şöyle dediler: "Sayıca az nice bölük, ALLAH\'ın izniyle kalabalık bölükleri yenmiştir. ALLAH sabredenlerle beraberdir."'),
(257, 2, 250, 'Calut ve ordusuyla karşılaştıklarında şöyle dediler: "Rabbimiz, bize direnme gücü ver, ayaklarımızı sağlam tut, inkarcılara karşı bize yardım et."'),
(258, 2, 251, 'Nihayet ALLAH\'ın izniyle onları bozguna uğrattılar. Davut Calut\'u öldürdü. ALLAH ona hükümdarlık ve anlayış verdi, ona dilediğini öğretti. ALLAH insanların bir kısmıyla bir kısmını savmasaydı yeryüzü bozulurdu. Fakat ALLAH tüm yaratıklara karşı lütufsahibidir'),
(259, 2, 252, 'Bunlar, sana gerçek olarak okuduğumuz ALLAH\'ın ayetleridir ve elbette sen elçilerden birisin.'),
(260, 2, 253, 'O elçilerin bazısına diğerlerinden daha fazla lütufta bulunduk. Örneğin, kimileriyle ALLAH konuştu, kimilerini de derecelerle yükseltti. Meryem oğlu İsa\'ya apaçık deliller verdik ve onu Kutsal Ruh ile destekledik. ALLAH dileseydi, onların ardından gelenler kendilerine apaçık deliller geldikten sonra birbirleriyle kavga etmezlerdi. Fakat anlaşmazlığa düştüler. Kimisi inandı, kimisi inkar etti. ALLAH dileseydi birbirleriyle savaşmazlardı. Fakat, ALLAH dilediğini yapar.'),
(261, 2, 254, 'İnananlar, ne alışverişin ne dostluğun ve ne de şefaatin (aracılığın) olmadığı gün gelmeden önce size verdiklerimizden dağıtın. İnkar edenler zalimlerdir.'),
(262, 2, 255, 'ALLAH: O\'ndan başka tanrı yoktur, Yaşayandır, Sonsuzdur. Kendisini ne dalgınlık ne de uyuklama tutmaz. Göklerde ve yerde ne varsa O\'nundur. İzni olmadan kim O\'nun katında aracılık edebilir? Onların geçmişini ve geleceğini bilir. Dilediği miktar dışında O\'nun bilgisinden hiç bir şeyi kavrayamazlar. Egemenliği gökleri ve yeri kapsamıştır. Onları düzen içinde korumak onu yormaz. O Yücedir, Büyüktür.'),
(263, 2, 256, 'Dinde zorlama yoktur. Artık doğruluk, sapıklıktan ayrılmıştır. Kim ki tağutu (despotları ve şeytani doktrinleri) inkar edip ALLAH\'a inanırsa, kopmaz ve sağlam bir bağa yapışmıştır. ALLAH İşitir, Bilir.'),
(264, 2, 257, 'ALLAH inananların egemeni ve dostudur; onları karanlıklardan aydınlığa çıkarır. İnkar edenlerin dostları ise azgın kişilerdir; onları aydınlıktan karanlığa çıkarır. Onlar ateş halkıdır, onlar orada sürekli kalıcıdır.'),
(265, 2, 258, 'ALLAH kendisine hükümdarlık verdiği halde, İbrahim ile Rabbi hakkında tartışan dikkatini çekmedi mi? İbrahim, "Benim Rabbim O\'dur ki yaşatır ve öldürür," deyince, "Ben de yaşatır ve öldürürüm," demişti. İbrahim, "ALLAH güneşi doğudan getiriyor. Sen de batıdan getirsene," deyince inkarcı adam şaşırıp kalmıştı. ALLAH zalim toplumu doğruya iletmez.'),
(266, 2, 259, 'Yahut şu kimse gibisi de (dikkatini çekmedi mi?)... Altı üstüne gelmiş yıkıntı bir kasabaya uğrar ve "ALLAH bunu ölümünden sonra nasıl diriltebilir," der. Bunun üzerine ALLAH onu yüz sene ölü bıraktıktan sonra diriltti. "Burada ne kadar kaldın," dedi. "Bir gün yahut günün bir parçası kadar kaldım," dedi. "Hayır, sen yüz yıl kaldın. Yiyeceğine ve içeceğine bak hala bozulmamış. Eşeğine de bak. Seni halk için bir delil yaptık. Kemiklere dikkat et, onları nasıl üstüste koyuyor, sonra onlara nasıl daet giydiriyoruz." Durum kendisine aydınlanınca, "Artık ALLAH\'ın her şeye gücü yettiğini biliyorum," dedi.'),
(267, 2, 260, 'İbrahim, "Rabbim, ölüleri nasıl dirilttiğini bana göster," demişti. "Yoksa inanmıyor musun," dedi. "Evet; ancak kalbimi güçlendirmesi için.," dedi. "Dört kuş al ve onları iyice incele (farklı özelliklerini tanı). Sonra her bir dağın üzerine onlardan bir parça yerleştir. Daha sonra onları çağır. Sana hemen gelecekler. Bilesin ki ALLAH Güçlüdür, Bilgedir," dedi.'),
(268, 2, 261, 'Paralarını ALLAH yolunda harcayanların örneği, herbirinde yüz tane olmak üzere yedi başak veren tanenin durumuna benzer. ALLAH dileyene katlayarak verir. ALLAH Cömerttir, Bilendir.'),
(269, 2, 262, 'Paralarını ALLAH yolunda harcadıktan sonra iyiliklerini başa kakıp eziyet etmeyenler için Rab\'leri katında ödülleri vardır. Onlara korku ve üzüntü yok.'),
(270, 2, 263, 'Güzel bir söz ve hoşgörü, peşinden onur kırıcı davranışlar getiren bir yardımdan daha hayırlıdır. ALLAH Zengindir, Şefkatlidir.'),
(271, 2, 264, 'İnananlar! ALLAH\'a ve ahiret gününe inanmadığı halde halka gösteriş için yardımda bulunan kişi gibi yardımlarınızı başa kakmakla ve eziyet etmekle boşa çıkarmayın. Bu tip davranışın örneği, üzerinde toz toprak biriken bir kayaya benzer ki şiddetli bir sağanak onu çıplak bırakır. Yaptıklarından hiç bir şey kazanamazlar. ALLAH inkarcı toplumu doğruya iletmez.'),
(272, 2, 265, 'ALLAH\'ın rızasını kazanmak istedikleri ve buna ikna oldukları için paralarını harcayanların örneği, bol yağmur aldığında iki kat ürün veren, bol yağmur almasa bile çisintiyle yetinen yüksek verimli bir bahçe örneği gibidir. ALLAH yaptıklarınızı görmektedir.'),
(273, 2, 266, 'Biriniz ister mi ki, altından ırmaklar akan, her çeşit meyveyi içeren hurma ve üzüm bahçelerine ve küçük çocuklara sahip olsun da, kendisine yaşlılık bahçesine de ateşli bir kasırga vursun yakıp kül etsin. Düşünmeniz için ALLAH size ayetlerini böyleaçıklar.'),
(274, 2, 267, 'İnananlar, kazandıklarınızdan ve sizin için yerden çıkardığımız nimetlerden yardım olarak verin. Size verilse, ancak gözünüz kapalı olarak kabul edebileceğiniz kötü mallarınızı yardım olarak vermeye kalkmayın. Bilin ki ALLAH Zengindir, Övgüye layıktır.'),
(275, 2, 268, 'Şeytan size fakirliği söz verir ve kötülüğü emreder. ALLAH ise kendi tarafından sizin için bağışlama ve lütuf söz verir. ALLAH Cömerttir, Bilendir.'),
(276, 2, 269, 'Bilgeliği dilediğine verir. Kime bilgelik verilmişse ona çok büyük iyilik yapılmıştır. Düşünce sahiplerinden başkası öğüt almaz.'),
(277, 2, 270, 'ALLAH ettiğiniz her ekonomik yardımı, adadığınız her adağı bilir. Zalimlerin yardımcısı yoktur.'),
(278, 2, 271, 'Yardımlarınızı açıklasanız, ne güzel. Ancak onları gizleyerek muhtaçlara verseniz daha iyidir ve bu bazı günahlarınızı örter. ALLAH yaptığınızı haber alır.'),
(279, 2, 272, 'Onları doğruya iletmek sana düşmez. Ancak ALLAH dilediğini doğruya iletir. Muhtaçlara ettiğiniz her iyilik kendi yararınızadır. Yardımlarınız yalnız ALLAH için olmalı. Yaptığınız her iyiliğin karşılığı size eksiksiz ödenecektir. Haksızlığa uğratılmayacaksınız.'),
(280, 2, 273, 'ALLAH yolundaki çalışmasından ötürü özgürlükleri kısıtlanarak göç etme imkanından yoksun bırakılmış ihtiyaç sahiplerine verin. Onları tanımayanlar, onurlu tavırlarından ötürü onları zengin sanır. Onları yüzlerinden tanırsın. Halktan yardım dilenmezler. Ettiğiniz her iyiliği ALLAH bilir.'),
(281, 2, 274, 'Paralarını gece ve gündüz gizli ve açık yardım için verenlerin ödülü Rab\'lerinin yanındadır. Onlara korku yoktur ve onlar üzülmezler.'),
(282, 2, 275, 'Tefecilikle para yiyenler, şeytanın çarptığı kimse gibi ayağa kalkarlar. Bu, onların, "Tefecilik alışveriş gibidir," demelerinden ötürüdür. Halbuki ALLAH alışverişi helal, tefeciliği ise haram kıldı. Kime Rabbinden bir öğüt gelir de vazgeçerse, geçmişte kazandıklarını tutabilir; işi de ALLAH\'a kalmıştır. Devam edenler ise cehennem halkıdır ve orada sürekli kalırlar.'),
(283, 2, 276, 'ALLAH tefeciliği mahkum eder, karşılıksız yardımı destekler. ALLAH hiçbir günahkar nankörü sevmez.'),
(284, 2, 277, 'İnanıp erdemli bir hayat sürerek namazı gözetenlerin ve zekatı verenlerin ödülleri Rab\'leri katındadır. Onlar için korku yoktur ve onlar üzülmezler.'),
(285, 2, 278, 'Ey inananlar, gerçekten inanıyorsanız ALLAH\'ı dinleyin ve her çeşit tefecilik kalıntısını terkedin.'),
(286, 2, 279, 'Böyle yapmazsanız ALLAH\'tan ve elçisinden bir savaş bekleyin. Tevbe ederseniz anaparanız yine sizindir. Ne haksızlık edin ne de haksızlığa uğrayın.'),
(287, 2, 280, '(Borçlu) darlık içinde ise, bir kolaylığa çıkıncaya kadar beklemek gerekir. Borcu karşılıksız yardım olarak bağışlarsanız sizin için daha iyidir, bir bilseniz.'),
(288, 2, 281, 'ALLAH\'a döndürüleceğiniz günden sakının. Herkese kazandığının karşılığı haksızlık edilmeden ödenecek.'),
(289, 2, 282, 'İnananlar! Belirli bir süre için birbirinize borç verdiğiniz zaman onu yazın. Sizden bir yazıcı onu adaletle yazsın. Yazıcı, ALLAH\'ın kendisine öğrettiği gibi yazmaktan kaçınmasın, yazsın. Borçlanan kişi de dikte ettirsin. Rabbi olan ALLAH\'tan korksun, onda sahtekarlık yapmasın. Borçlanan kişi aklı ermez, veya çaresiz, ya da dikte etmekten aciz ise velisi adaletle yazdırmalı. Erkeklerinizden iki şahit te tanıklık etsin. İki erkek şahit bulamazsanız dilediğiniz şahitlerden bir erkek ve iki kadın seçiniz ki kadınlardan biri yanıldığında diğeri ona hatırlatsın. Şahitler, çağrıldıkları vakit çekinmesin. Az olsun, çok olsun, ödeme tarihi ile birlikte onu yazmaktan üşenmeyin. Bu, ALLAH katında daha adaletli, tanıklık açısından daha sağlam ve kuşkulanmamanız için daha uygundur. Yalnız, ticaret peşin olursa onu yazmamanızda bir sakınca yok. Alışveriş yaptığınızda tanıklarınız bulunsun. Yazana da tanığa da zarar verilmesin. Aksi halde kendinize kötülük edersiniz. ALLAH\'ı dinleyin. ALLAH size öğretiyor. ALLAH herşeyi bilir.'),
(290, 2, 283, 'Yolculukta olup bir yazıcı bulamasanız, ödemeyi garantileyecek bir senet veya makbuz gönderin. Birbirinize bu şekilde güvenirseniz, senedin sahibi ödemeyi zamanında yapsın ve Rabbi olan ALLAH\'ı saysın. Tanıklığı gizlemeyin. Kim gizlerse kalbi günahkardır. ALLAH tüm yaptıklarınızı bilir.'),
(291, 2, 284, 'Yerde ve göklerde olanlar ALLAH\'ındır. İçinizdekini açıklasanız da gizleseniz de ALLAH sizi ondan sorumlu tutar. Dilediğini bağışlar, dilediğini cezalandırır. ALLAH herşeye gücü yetendir.'),
(292, 2, 285, 'Elçi, Rabbinden kendisine indirilene inandı, inananlar da... Hepsi, ALLAH\'a, meleklerine, kitaplarına ve elçilerine inanırlar: "Elçilerinin hiçbirisi arasında ayırım yapmayız." Derler ki: "İşittik ve uyduk. Rabbimiz bizi bağışla; dönüş sanadır."'),
(293, 2, 286, 'ALLAH kişiye ancak kapasitesi kadar yükler. Herkesin kazandığı iyilik kendi yararına, kazandığı kötülükse kendi zararınadır. "Rabbimiz, unutur yahut yanılırsak bizi sorumlu tutma! Rabbimiz, bizden öncekilere yüklediğin gibi bize ağır sorumluluk yükleme! Rabbimiz, gücümüzün yetmeyeceği şeyleri bize yükletme! Bizi hoşgör, bizi bağışla ve bize acı! Sensin bizim mevlamız (efendimiz ve egemenimiz). İnkarcılar topluluğuna karşı bize yardım et!');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'A. L. M'),
(295, 3, 2, 'ALLAH: O\'ndan başka tanrı yoktur; Diridir, Gözetendir.'),
(296, 3, 3, 'Sana bu kitabı, kendisinden öncekileri onaylayıcı olarak gerçekle indirdi. Tevrat\'ı ve İncil\'i de indirdi,'),
(297, 3, 4, 'Bundan önce insanlara yol gösterici olarak... Yasalar kitabını indirdi. ALLAH\'ın ayetlerini inkar edenlere çetin bir azap vardır. ALLAH Üstündür, Öc Alandır.'),
(298, 3, 5, 'Yerde ve gökte hiçbir şey ALLAH\'a gizli kalmaz.'),
(299, 3, 6, 'Rahimlerde size dilediği şekilde biçim veren O\'dur. O\'ndan başka tanrı yoktur; Üstündür, Bilgedir.'),
(300, 3, 7, 'O ki sana bu kitabı indirdi. Onun bazı ayetleri kesin anlamlıdır (muhkem), ki bunlar kitabın özüdür. Diğerleri de benzer anlamlıdır (müteşabih). Kalplerinde hastalık bulunanlar, insanları şaşırtmak ve farklı anlam vermek için benzer anlamlı olanlarının ardına düşerler. Onların gerçek anlamını ise kimse bilmez, ancak istisnadır ALLAH ve derin bilgiye sahip olanlar "Ona inandık, hepsi Rabbimiz katındandır," derler. Akıl ve anlayış sahiplerinden başkası öğüt almaz.'),
(301, 3, 8, '"Rabbimiz, bizi doğruya ulaştırdıktan sonra kalplerimizin eğrilmesine izin verme. Üzerimize rahmetini yağdır; kuşkusuz sen Bağışta Bulunansın."'),
(302, 3, 9, '"Rabbimiz, gerçekleşmesi kuşku gütürmeyen günde insanları toplayacak olan sensin." ALLAH sözünden dönmez.'),
(303, 3, 10, 'İnkarcıların ne paraları ne de çocukları ALLAH\'a karşı hiç bir yarar sağlamaz. Onlar ateşin yakıtıdır.'),
(304, 3, 11, 'Firavun\'un halkı veya onlardan öncekilerin durumu gibi... Ayetlerimizi (vahiy ve mucizelerimizi) yalanladılar ve ALLAH da onları suçüstü yakaladı. ALLAH\'ın cezalandırması şiddetlidir.'),
(305, 3, 12, 'İnkarcılara şunu söyle: "Yenilecek ve cehenneme sürüleceksiniz." Ne kötü bir konuttur.'),
(306, 3, 13, 'Karşılaşan iki orduda sizin için bir ders var. Ordulardan birisi ALLAH yolunda savaşırken diğeri de inkarcı idi. Onları (inananları), gözleriyle kendilerinin iki katı olarak görüyorlardı. ALLAH dilediğini zaferle destekler. Bunda görüş sahipleri için bir ibret vardır.'),
(307, 3, 14, 'Kadınları, çocukları, yığınlarla altın ve gümüşü, eğitilmiş atları, davarları ve ekinleri sevmek gibi zevkler insanlara alımlı görünür. Bunlar dünya hayatının nimetleridir. Oysa gidilecek en güzel yer ALLAH katındadır.'),
(308, 3, 15, 'De ki: "Bundan daha iyisini size haber vereyim mi? Erdemliler için Rab\'lerinin yanında altlarından ırmaklar akan, içinde sonsuz kalacakları cennetler (bahçeler), tertemiz eşler ve ALLAH\'ın onayını kazanmaktan dolayı mutluluk vardır." ALLAH kulları görür.'),
(309, 3, 16, 'Onlar ki: "Rabbimiz, biz inandık, günahlarımızı bağışla ve bizi cehennem azabından koru," derler.'),
(310, 3, 17, 'Onlar, sabredenlerdir, doğru sözlülerdir, gönülden teslim olanlardır, insanlara yardım edenler ve seherlerde bağışlanma dileyenlerdir.'),
(311, 3, 18, 'ALLAH, kendisinden başka tanrı olmadığına şehadet eder; melekler ve adaleti gözeten ilim sahipleri de... O\'ndan başka tanrı yoktur. Üstündür, Bilgedir.'),
(312, 3, 19, 'ALLAH tarafından onaylanan biricik din, Teslim Olmak (islam) dır. Kitap verilmiş olanlar, kendilerine bilgi geldikten sonra sırf aralarındaki kıskançlık yüzünden anlaşmazlığa düştüler. ALLAH\'ın ayetlerini inkar edenler için ALLAH hesabı çabuk görür'),
(313, 3, 20, 'Seninle tartışırlarsa, "Ben ve beni izleyenler kendimizi ALLAH\'a teslim ettik," de. Kitap verilenlere ve kitap verilmeyenlere (ümmilere), "Teslim oldunuz mu," de. Teslim olurlarsa, doğruyu bulurlar. Yüz çevirirlerse, görevin sadece duyurmaktır. ALLAH kulları görür.'),
(314, 3, 21, 'ALLAH\'ın ayetlerini inkar edenlere, haksız yere peygamberleri öldürenlere ve halkın arasında adaleti savunanları öldürenlere acıklı bir azabı müjdele.'),
(315, 3, 22, 'Onlar, dünya ve ahirette yaptıkları boşa çıkanlardır. Onlara yardım eden de olmayacak'),
(316, 3, 23, 'Kitaptan kendilerine bir pay verilmiş olanlara baksana, aralarında hükmetmeleri için ALLAH\'ın kitabına çağrılıyorlar da onlardan bir grup yüz çevirip geri dönüyor.'),
(317, 3, 24, 'Çünkü onlar, "Cehennem sayılı bir kaç günün dışında bize dokunmayacak," dediler. Uydurdukları şeyler onları dinlerinde böylece yanıltmıştır.'),
(318, 3, 25, 'Herkese kazandığının karşılığı haksızlık yapılmadan ödeneceği, gerçekleşmesinde kuşku olmayan o gün için onları topladığımızda halleri nice olacak?'),
(319, 3, 26, 'De ki: "Yöneticilerin Yöneticisi olan Tanrım, yönetimi dilediğine verir, dilediğini de yönetimden indirirsin. Dilediğini yükseltir, dilediğini de alçaltırsın. Tüm iyilikler senin elinde. Sen her şeye gücü yetensin!"'),
(320, 3, 27, '"Geceyi gündüze katarsın, gündüzü de geceye katarsın. Diriyi ölüden çıkarırsın, ölüyü de diriden çıkarırsın. Dilediğini hesapsız rızıklandırırsın."'),
(321, 3, 28, 'İnananlar, inananları bırakıp inkarcıları dost edinmesin. Kim bunu yaparsa ALLAH ile bir ilgisi kalmaz. Ancak onlardan korkmanız hali başka... ALLAH kendisi hakkında sizi uyarıyor. Dönüş ALLAH\'adır.'),
(322, 3, 29, 'De ki, "İçinizdeki düşüncelerinizi ister gizleyin, ister açıklayın, ALLAH bilir." Göklerde ve yerde olan her şeyi bilir. ALLAH\'ın gücü herşeye yeter.'),
(323, 3, 30, 'Gün gelecek, her kişi yaptığı her iyiliği önünde hazır bulacaktır. İşlediği her kötülük ile kendisi arasında uzun bir mesafe olmasını isteyecek. ALLAH kendisi hakkında sizi uyarıyor. ALLAH kullara şefkatlidir.'),
(324, 3, 31, 'De ki, " ALLAH\'ı seviyorsanız beni izleyin ki ALLAH da sizi sevsin ve günahlarınızı bağışlasın. ALLAH Bağışlayandır, Rahimdir."'),
(325, 3, 32, '"ALLAH\'a ve elçiye itaat edin," de. Yüz çevirirlerse, elbette ALLAH inkarcıları sevmez.'),
(326, 3, 33, 'ALLAH Adem\'i, Nuh\'u, İbrahim ailesini ve İmran ailesini halkların arasından (elçi olarak) seçti.'),
(327, 3, 34, 'Birbirlerinin soyundan olarak... ALLAH İşitir, Bilir.'),
(328, 3, 35, 'İmran\'ın karısı demişti ki: "Rabbim, karnımdaki (bebeği) tamamiyle sana adadım, adağımı kabul buyur. Sen İşitensin, Bilensin."'),
(329, 3, 36, 'Onu doğurunca, "Rabbim, onu kız doğurdum," dedi -ALLAH onun ne doğuracağını biliyordu- "Erkek kız gibi değil. Ona Meryem adını verdim. Onu ve soyunu, kovulmuş şeytandan koruman için sana yalvarırım."'),
(330, 3, 37, 'Rabbi, adağını güzel bir şekilde kabul etti ve onu Zekeriya\'nın himayesinde güzel bir çiçek gibi yetiştirdi. Zekeriya, tapınakta onun yanına her girişinde yanında yiyecekler bulurdu. "Meryem, bunlar sana nereden geliyor," diye sorduğunda, "Bu, ALLAH katındandır. ALLAH dilediğini hesapsız rızık verir," derdi.'),
(331, 3, 38, 'Zekeriyya oracıkta Rabbine dua etti: "Rabbim," dedi, "katından bana tertemiz bir soy ver, Sen duaları işitensin."'),
(332, 3, 39, 'Tapınakta durmuş namaz kılıyorken melekler ona, "ALLAH seni, ALLAH\'ın Sözünü doğrulayacak, onurlu, iffetli ve erdemli bir peygamber olan Yahya ile müjdeliyor," diye seslendiler.'),
(333, 3, 40, '"Rabbim, ben yaşlı ve karım kısırken nasıl olur da bir oğlum olur," deyince, o, "Evet; ama ALLAH dilediğini yapar," dedi.'),
(334, 3, 41, '"Rabbim, bana bir alamet ver," dedi. "Alametin, üç gün işaretle anlaşmanın dışında halk ile konuşmamandır. Rabbini çokça an, akşam sabah onu düşün."'),
(335, 3, 42, 'Melekler şöyle demişti: "Meryem, ALLAH seni seçip arındırdı; seni tüm kadınların üzerine seçti.'),
(336, 3, 43, '"Meryem, Rabbine teslim ol, secdeye kapan, eğilenlerle birlikte eğil."'),
(337, 3, 44, 'Bunlar, sana vahyettiğimiz gayb haberlerindendir. Meryem\'e hangisi kefil olacak diye zar atarlarken sen onların yanında değildin; çekiştikleri zaman da sen onların yanında değildin.'),
(338, 3, 45, 'Melekler demişti ki: "Meryem, ALLAH seni kendisinden bir Söz ile müjdeliyor. Onun adı Meryem oğlu İsa Mesih\'dir. Dünyada da ahirette de önderlerdendir ve (Tanrı\'ya) yakın olanlardandır.'),
(339, 3, 46, '"Beşikteyken de büyüyünce de insanlarla konuşacak ve iyilerden olacak."'),
(340, 3, 47, '(Meryem), "Rabbim! Bana bir beşer dokunmamışken nasıl olur da çocuğum olur," deyince, (Melek) şöyle cevap verdi: "ALLAH dilediğini böyle yaratır. Herhangi bir şeyin olmasını dilediği an ona \'Ol\' der ve o şey oluverir.'),
(341, 3, 48, '"Ona kitabı, bilgeliği, Tevrat\'ı ve İncil\'i öğretecek.'),
(342, 3, 49, '"İsrailoğullarına gönderilen bir elçi olarak (diyecek ki): \'Ben size, Rabbinizden bir kanıt ile geldim. Size balçıktan kuş heykeli yapıp ona üflerim ve ALLAH\'ın izniyle bir kuş oluverir. ALLAH\'ın izniyle körü ve cüzzamlıyı iyileştirir, ölüyü diriltirim. Ne yediğinizi ve evlerinizde neler biriktirdiğinizi size bildirebilirim. İnanacaksanız bu kanıtlar sizin için yeterlidir.'),
(343, 3, 50, '"Benden önceki Tevrat\'ı doğrulamak ve size haram edilen bazı şeyleri helal etmek için gönderildim. Size Rabbinizden bir kanıt getirdim. ALLAH\'ı dinleyin ve beni izleyin.'),
(344, 3, 51, '" ALLAH benim de Rabbim, sizin de Rabbiniz; O\'na kulluk edin. Budur doğru yol.\'"'),
(345, 3, 52, 'İsa onlardan inkarcı bir tavır sezince, "ALLAH yolunda kim bana yardımcı olacak," dedi. Öğrencileri, "Biz ALLAH\'ın yardımcılarıyız; ALLAH\'a inandık. Müslüman oluşumuza tanık ol," diye karşılık verdiler.'),
(346, 3, 53, '"Rabbimiz, indirdiğine inandık ve elçiye uyduk, bizi tanıklar listesine yaz!"'),
(347, 3, 54, 'Tuzak kurdular, ALLAH da tuzak kurdu. ALLAH tuzak kuranların en iyisidir.'),
(348, 3, 55, 'ALLAH İsa\'ya şöyle demişti: "Senin dünyadaki hayatına son vereceğim ve kendime yükselteceğim. Seni inkar edenlerden kurtaracağım ve sana uyanları Diriliş Gününe kadar inkar edenlerin üzerinde tutacağım. Sonra, dönüşünüz banadır ve anlaşmazlığa düştüğünüz konularda aranızda ben hüküm vereceğim."'),
(349, 3, 56, '"Kafirleri ise dünyada ve ahirette şiddetli bir şekilde cezalandıracağım; yardımcıları da olmayacak."'),
(350, 3, 57, 'İnanıp erdemli bir hayat sürenlere gelince, ödüllerini tam olarak ödeyecek. ALLAH zalimleri sevmez.'),
(351, 3, 58, 'Sana bu okuduklarımız, ayetlerden ve hikmetli mesajdandır.'),
(352, 3, 59, 'ALLAH\'a göre İsa\'nın örneği, Adem\'in örneği gibidir; topraktan biçimlendirdikten sonra ona "Ol," dedi ve o da oldu.'),
(353, 3, 60, 'Bu, Rabbinden gelen gerçektir; kuşkulananlardan olma.'),
(354, 3, 61, 'Sana gelen bu bilgiden sonra her kim bu konuda seninle tartışırsa, de ki: "Gelin, çocuklarımızı, çocuklarınızı, kadınlarımızı, kadınlarınızı çağırarak bizlerle sizler bir araya gelelim ve sonra ALLAH\'ın lanetinin yalancıların üzerine olması için lanetleşelim.'),
(355, 3, 62, 'İşte, söylentilerin gerçeği bu. ALLAH\'tan başka hiç bir tanrı yoktur ve elbette ALLAH Güçlüdür, Bilgedir.'),
(356, 3, 63, 'Yüz çevirirlerse, elbette ALLAH bozguncuları bilir'),
(357, 3, 64, 'De ki, "Kitaplılar! Bizimle sizin aranızda aynı olan bir ilkeye geliniz: ALLAH\'tan başkasına kulluk etmeyelim ve O\'na hiçbir şeyi ortak koşmayalım, birimiz diğerini ALLAH\'tan sonra rabler edinmesin." Kabul etmezlerse, "Şahit olun, biz müslümanlarız!," deyin.'),
(358, 3, 65, 'Ey kitap halkı, neden İbrahim hakkında tartışıp duruyorsunuz? Oysa İncil de Tevrat da ondan sonra indirildi. Aklınızı kullanmaz mısınız?'),
(359, 3, 66, 'Bilgi sahibi olduğunuz konularda tartışıp duruyorsunuz! Peki hakkında hiç bir bilgiye sahip olamadığınız bir konuda nasıl tartışabiliyorsunuz? Siz bilmeyebilirsiniz; ama ALLAH bilir.'),
(360, 3, 67, 'İbrahim ne Yahudi idi ne de Hıristiyan; tektanrıcı bir müslümandı. Hiç bir zaman ortak koşanlardan olmadı.'),
(361, 3, 68, 'İnsanların İbrahim\'e en yakın olanları, onu izleyenler, bu peygamber ve inananlardır. ALLAH inananların egemeni ve dostudur.'),
(362, 3, 69, 'Kitap halkından bir grup sizi saptırmak ister. Onlar sadece kendilerini saptırıyor. Farkında bile değiller.'),
(363, 3, 70, 'Kitap halkı! Tanık olduğunuz halde neden ALLAH\'ın ayetlerini inkar ediyorsunuz?'),
(364, 3, 71, 'Kitap halkı! Neden doğru ile yanlışı birbirine karıştırıyor ve bile bile gerçeği gizliyorsunuz?'),
(365, 3, 72, 'Kitap halkından bir grup dedi ki: "İnananlara indirilmiş olana gündüzün başında inanın ve sonunda inkar edin. Böylece belki dinlerinden dönerler."'),
(366, 3, 73, '"Sizin dininize uyandan başkasına uymayın." Sen de onlara, "Hidayet ALLAH\'ın hidayetidir," de. "Size verilenin benzerininin bir başkasına verildiğine de inanmayın; yoksa Rabbiniz hakkında sizinle tartışma hakkı kazanırlar," derlerse, "Lütuf ALLAH\'ın elindedir, dilediğine verir," de. ALLAH Cömerttir, Bilendir.'),
(367, 3, 74, 'Rahmetini dilediğine özgü kılar. ALLAH Büyük Lütuf Sahibidir.'),
(368, 3, 75, 'Kitap halkından öylesi var ki kendisine yığınla emanet bıraksan sana aynen öder. Fakat onlardan öylesi de var ki kendisine bir Dinar emanet etsen, başına dikilip durmadıkça geri ödemez. "Ümmilere karşı bizim bir sorumluluğumuz yok" dedikleri için böyle davranıyorlar ve bile bile yalanlarını ALLAH\'a yakıştırıyorlar.'),
(369, 3, 76, 'Kim sözünü yerine getirir ve erdemli bir yaşam sürerse, bilsin ki ALLAH erdemlileri sever.'),
(370, 3, 77, 'ALLAH\'a verdikleri sözü ve yeminlerini az bir fiyata değişenlerin ahirette bir payları yoktur. ALLAH onlarla konuşmaz, Diriliş Gününde onlara bakmaz ve onları arındırmaz. Onlar için acıklı bir azap var.'),
(371, 3, 78, 'Onların bazısı, kitapta olmayanı kitaptan sanasınız diye dillerini bükerek kitabı taklit eder ve ALLAH katından olmadığı halde, "Bu ALLAH katındandır," derler. Bile bile, ALLAH adına yalan söylerler.'),
(372, 3, 79, 'ALLAH\'ın kendisine kitap, bilgelik ve peygamberlik verdiği hiç bir insan, "ALLAH\'tan sonra bana da kulluk ediniz," diye halkı kendisine çağırmaz. Aksine, "Öğrenip öğrettiğiniz kitap gereğince kendisini Rabbine adayan kullar olun," der'),
(373, 3, 80, 'Ve size, melekleri ve peygamberleri rabler edinmenizi de emretmez. Siz müslüman olduktan sonra, size hiç inkarı emreder mi?'),
(374, 3, 81, 'ALLAH peygamberlerden (nebilerden) şöyle misak almıştı: "Size kitap ve hikmet vereceğim. Daha sonra, beraberinizdekileri doğrulayan bir elçi (resul) geldiğinde ona inanacak ve onu destekleyeceksiniz. Bunu kabul ettiniz mi ve bu sözleşmeyi yerine getireceğinize söz verdiniz mi," demişti. Onlar "Kabul ettik," deyince, "Öyleyse şahid olun, ben de sizinle beraber şahid olanlardanım," demişti.'),
(375, 3, 82, 'Bundan sonra kim dönerse yoldan çıkmıştır.'),
(376, 3, 83, 'ALLAH\'ın dininden başkasını mı arıyorlar? Oysa göklerde ve yerde ne varsa ister istemez O\'na teslim olmuştur ve hepsi O\'na döndürülecektir'),
(377, 3, 84, 'De ki: "ALLAH\'a, bize indirilene, İbrahim\'e, İsmail\'e, İshak\'a, Yakub\'a ve torunlara indirilene; Musa\'ya, İsa\'ya ve peygamberlere Rab\'leri tarafından verilene inandık. Onların hiçbiri arasında ayırım yapmayız ve biz O\'na teslim olanlarız."'),
(378, 3, 85, 'Kim islamdan (Tanrı\'ya teslim olmaktan) başka bir din ararsa kendisinden kabul edilmeyecek ve o ahirette kaybedenlerden olacaktır'),
(379, 3, 86, 'İman ettikten, elçinin doğru olduğuna tanık olduktan ve kendilerine açık deliller geldikten sonra, inkar eden bir toplumu, ALLAH nasıl olur da doğruya iletir? ALLAH zalim toplumu hidayete ulaştırmaz.'),
(380, 3, 87, 'Onlar, ALLAH\'ın, meleklerin ve insanların toplu lanetini hakketmişlerdir.'),
(381, 3, 88, 'Orada sürekli kalıcıdırlar; azapları hafifletilmez ve kendilerine bakılmaz.'),
(382, 3, 89, 'Ancak, bundan sonra yönelip durumlarını düzeltenler başka. ALLAH Bağışlayandır, Rahimdir.'),
(383, 3, 90, 'İmanlarından sonra inkar edip de inkarlarında ileri gidenlerin tevbeleri kabul edilmez; onlar artık tümüyle sapmış demektir.'),
(384, 3, 91, 'İnkar edip inkarcı olarak ölenler, dünya dolusu altını fidye olarak verseler dahi kendilerinden kabul edilmeyecektir. Onlar acıklı bir azabı hakkettiler ve hiç bir yardımcıları da olmayacak.'),
(385, 3, 92, 'Sevdiğiniz şeylerden ekonomik yardım olarak vermedikçe erdemli bir kişi olamazsınız. Her neyi verirseniz ALLAH mutlaka onu bilir'),
(386, 3, 93, 'Tevrat indirilmeden önce İsrail kendisine haram kılıncaya kadar İsrailoğullarına tüm yiyecekler helal idi. "Doğru sözlülerseniz Tevrat\'ı getirip okuyun," de.'),
(387, 3, 94, 'Artık kim yalanları ALLAH\'a yakıştırırsa, işte zalimler onlardır.'),
(388, 3, 95, 'De ki: "ALLAH doğruyu söyler; İbrahim\'in tektanrıcı dinine uyun. O puta tapanlardan olmadı."'),
(389, 3, 96, 'Halk için kurulan en önemli ev, tüm halklara bir hidayet kaynağı olan Bekke\'deki kutlu evdir.'),
(390, 3, 97, 'Orada apaçık işaretler var: İbrahim\'in (putperestliğe karşı) duruş yeridir. Oraya giren güvenlik içinde olmalı. Halktan yolculuğa gücü yetenler o Ev\'i (Kabeyi) ALLAH için haccetmekle yükümlüdür. Kim inkar ederse bilsin ki ALLAH kimseye muhtaç değildir.'),
(391, 3, 98, 'De ki, "Kitap halkı, ALLAH yaptıklarınıza tanıkken neden ALLAH\'ın ayetlerini tanımıyorsunuz?"'),
(392, 3, 99, 'De ki, "Kitap halkı, doğruya tanık olduğunuz halde neden ALLAH\'ın yolunu değiştirmeye yeltenerek inananları saptırıyorsunuz? ALLAH yaptıklarınızdan habersiz değildir."'),
(393, 3, 100, 'İnananlar! Kitap halkından bir kısmına uyarsanız iman etmenizden sonra sizi inkarcılığa döndürürler.'),
(394, 3, 101, 'ALLAH\'ın ayetleri size okunduğu ve O\'nun elçisi aranızda olduğu halde nasıl olur da inkar edersiniz? Kim ALLAH\'a sarılırsa dosdoğru yola iletilmiştir'),
(395, 3, 102, 'İnananlar! ALLAH\'ı gereğince dinleyin ve müslümanlar (Tanrı\'ya teslim olanlar) olarak ölmeye bakın.'),
(396, 3, 103, 'ALLAH\'ın ipine topluca sımsıkı sarılın; ayrılığa düşmeyin. ALLAH\'ın size olan nimetini anımsayın. Siz birbirinize düşmanlar idiniz de kalplerinizi birleştirdi ve O\'nun nimeti sayesinde kardeşler oldunuz. Bir ateş çukurunun kenarında idiniz, sizi ondan kurtardı. Yola gelesiniz diye ALLAH ayetlerini böyle açıklıyor.'),
(397, 3, 104, 'İçinizde, iyiliğe çağıran, sağduyuyu öğütleyen ve kötülükten sakındıran bir topluluk olsun. Bunlar başaranlardır'),
(398, 3, 105, 'Kendilerine apaçık deliller geldikten sonra ayrılık ve anlaşmazlığa düşenler gibi olmayınız. Onlar için büyük bir azap var'),
(399, 3, 106, 'O gün bazı yüzler aklanır, bazı yüzlerse kararır. Yüzleri kararanlara: "İman ettikten sonra inkar mı ettiniz? İnkarınızdan dolayı azabı tadın."'),
(400, 3, 107, 'Yüzleri aklananlar ise ALLAH\'ın rahmeti içindedir. Orada sürekli kalırlar.'),
(401, 3, 108, 'Bunlar, sana dosdoğru olarak okuduğumuz ALLAH\'ın ayetleridir. ALLAH hiç kimseye zulmetmek istemez.'),
(402, 3, 109, 'Göklerde ve yerde ne varsa ALLAH\'ındır ve tüm işler ALLAH tarafından kontrol edilir'),
(403, 3, 110, 'Siz, halk için çıkarılmış en iyi topluluksunuz. İyiliği öğütler, kötülükten sakındırır ve ALLAH\'a inanırsınız. Kitap halkı inansaydı kendileri için iyi olurdu. İçlerinde inananlar olmakla birlikte çoğunluğu yoldan çıkmıştır'),
(404, 3, 111, 'Size hakaretten başka bir zarar veremezler. Sizinle savaşırlarsa dönüp kaçarlar; kazanamazlar.'),
(405, 3, 112, 'Nerede bulunurlarsa bulunsunlar -ALLAH\'ın ipine, ya da insanların ipine (sözleşmesine) sığınanlar hariç- horlandılar. ALLAH\'ın gazabını hakkettiler ve yoksulluğa mahkum edildiler. Zira onlar, ALLAH\'ın ayetlerini inkar ediyor ve peygamberleri haksız yere öldürüyorlardı. Tüm bunlar, karşı gelmeleri ve sınırı aşmalarının bir sonucu idi.'),
(406, 3, 113, 'Ancak hepsi bir değil. Kitap halkından bir grup var ki geceleri ALLAH\'ın ayetlerini okuyup secde ederler.'),
(407, 3, 114, 'ALLAH\'a ve ahiret gününe inanırlar, iyiliği öğütler kötülükten sakındırır ve iyi işlere koşuşurlar. İşte onlar iyilerdendir.'),
(408, 3, 115, 'Yaptıkları hiç bir iyilik karşılıksız kalmıyacaktır. ALLAH erdemlileri çok iyi bilir.'),
(409, 3, 116, 'İnkar edenlerin ne paraları ne de çocukları ALLAH\'a karşı kendilerine hiç bir yarar sağlamıyacaktır. Onlar ateşin halkıdır ve orada sürekli kalıcıdırlar.'),
(410, 3, 117, 'Bu dünya hayatındaki harcamaları, kendi kendilerine zulmeden bir topluluğun ekinine vurup mahfeden dondurucu bir rüzgar gibidir. ALLAH onlara zulmetmedi; fakat onlar kendi kendilerine zulmediyorlardı'),
(411, 3, 118, 'İnananlar, size kötülük etmekten geri durmayan, sizin sıkıntıya düşmenizi isteyen kimseleri sırdaş edinmeyin. Ağızlarından kin ve nefret taşmaktadır. Göğüslerinde gizledikleri ise daha büyük. Size ayetleri açıkladık; düşünürseniz,'),
(412, 3, 119, 'İşte sizler, onlar sizi sevmediği halde onları sevenlersiniz! Üstelik siz bütün kitaplara/kitabın tümüne inanırsınız. Sizinle karşılaştıkları zaman "İnandık," derler, yalnız kaldıklarında ise size olan öfkelerinden dolayı parmaklarını ısırırlar. De ki: "Öfkenizden canınız çıksın." Kuşkusuz ALLAH göğüslerin özünü Bilendir.'),
(413, 3, 120, 'Size bir iyilik dokunsa onlara üzüntü verir, başınıza bir kötülük gelse ona sevinirler. Zorluklara karşı direnir ve erdemli davranırsanız entrikaları size hiç bir zarar vermez. ALLAH tüm yaptıklarını kontrol eder.'),
(414, 3, 121, 'Hani sen, sabah erkenden ailenden ayrılarak inananları savaşta tutacakları noktalara yerleştiriyordun. Elbette ALLAH İşitir, Bilir.'),
(415, 3, 122, 'Sizden iki grup nerede ise bozguna uğradı. Oysa ALLAH onların Sahibiydi. İnananlar ALLAH\'a güvensin.'),
(416, 3, 123, 'Nitekim siz zayıf durumda iken, Bedir\'de, ALLAH size yardım edip zafere ulaştırdı. Teşekkürünüzü belirtmek için ALLAH\'ı dinlemelisiniz'),
(417, 3, 124, 'İnananlara şunları diyordun: "İndirilmiş üç bin melek ile Rabbinizin sizi desteklemesi size yetmez mi?"'),
(418, 3, 125, 'Evet, güçlüklere karşı direnir ve erdemli davranırsanız size şu anda ani bir baskın yapsalar, Rabbiniz sizi eğitilmiş beş bin melek ile destekler'),
(419, 3, 126, 'ALLAH bunu, kalbinizi yatıştıracak bir müjde olsun diye anlatıyor. Zafer, yalnızca Güçlü ve Bilge olan ALLAH katındandır.'),
(420, 3, 127, 'Böylece inkarcıların bir kısmını imha eder veya onları etkisiz hale getirip bozguna uğratır.'),
(421, 3, 128, 'Tevbelerini kabul etmesi veya işledikleri zulümden dolayı onları azaplandırması seni ilgilendirmez.'),
(422, 3, 129, 'Göklerde ve yerde bulunan her şey ALLAH\'ındır. Affedilmeyi hakkedeni affeder, cezayı hakkedeni cezalandırır. ALLAH Bağışlayandır, Rahimdir'),
(423, 3, 130, 'İnananlar, faizi kat kat arttırılmış olarak yemeyin. Başarılı olmak için ALLAH\'ı dinleyin.'),
(424, 3, 131, 'İnkarcılar için hazırlanmış olan ateşten sakının.'),
(425, 3, 132, 'Merhamet edilmeniz için ALLAH\'a ve elçiye uyun.'),
(426, 3, 133, 'Rabbiniz tarafından bağışlanmaya, eni göklerle yeri kapsayan ve erdemliler için hazırlanmış olan cennete doğru koşun.'),
(427, 3, 134, 'O erdemliler ki bollukta ve darlıkta yardım için para harcarlar, öfkelerini kontrol ederler, insanların kusurlarını görmemezlikten gelirler. ALLAH iyilik yapanları sever.'),
(428, 3, 135, 'Onlar ki bir günah işledikleri, yahut kendilerine zulmettikleri zaman ALLAH\'ı anımsar ve günahları için bağışlanma dilerler -Zaten ALLAH\'tan başka kim günahları bağışlayabilir ki?- ve onlar bile bile günah işlemeye devam etmez'),
(429, 3, 136, 'Onlara, Rab\'lerinden bağışlanma ve altından ırmaklar akan cennetlerle karşılık verilir; orada sürekli kalırlar. Çalışanların ödülü ne güzeldir.'),
(430, 3, 137, 'Sizden önce de yasalar uygulanmıştı. Yeryüzünde dolaşın da yalanlayıcıların sonunun ne olduğunu görün.'),
(431, 3, 138, 'Bu, insanlara bir bildiri, erdemlilere de bir yol gösterici ve öğüttür.'),
(432, 3, 139, 'Gevşemeyin, üzülmeyin; inanıyorsanız üstün sizsiniz!'),
(433, 3, 140, 'Size bir sıkıntı dokunduysa, düşman topluluğa da benzeri bir sıkıntı dokunmuştur. Böyle günleri, halkın arasında döndürüp duruyoruz ki ALLAH gerçek inananları ayırsın ve sizden bazılarını şahitler edinsin. ALLAH zalimleri sevmez'),
(434, 3, 141, 'ALLAH böylece inananları sağlamlaştırır ve kafirlerin gururunu kırar'),
(435, 3, 142, 'ALLAH aranızdaki çalışanları ortaya çıkarmadan, sabredenleri ayırmadan cennete gireceğinizi mi sandınız?'),
(436, 3, 143, 'Siz ölümle karşılaşmadan önce onu arzuluyordunuz. Fakat şimdi onu gördüğünüz halde bekliyorsunuz.'),
(437, 3, 144, 'Muhammed sadece bir elçidir ve ondan önce de nice elçiler gelip geçmiştir. Ölür yahut öldürülürse geriye mi döneceksiniz. Dönekler ALLAH\'a hiç bir zarar veremez. ALLAH şükredenleri ödüllendirecektir'),
(438, 3, 145, 'ALLAH\'ın izni olmadan hiç kimse ölemez. Ölümün belirlenmiş bir süresi var. Kim dünya nimetini isterse ona ondan veririz, kim ahiret nimetini isterse ona da ondan veririz. Şükredenleri ödüllendireceğiz.'),
(439, 3, 146, 'Nice peygamberler var ki kendilerini Rab\'lerine adamış birçok kişi onlarla birlikte savaştı. ALLAH yolunda başlarına gelenden ötürü gevşemediler, yılmadılar, boyun eğmediler. ALLAH direnenleri sever.'),
(440, 3, 147, 'Sözleri yalnızca şuydu: "Rabbimiz, günahlarımızı ve işlerimizdeki aşırılıklarımızı bağışla, ayaklarımızı sağlam tut ve inkarcı topluluğa karşı bize zafer ver."'),
(441, 3, 148, 'ALLAH da onlara hem dünya nimetini hem de ahiret nimetinin en güzelini verdi. ALLAH güzel davrananları sever.'),
(442, 3, 149, 'Ey inananlar, inkar edenlere uyarsanız sizi gerisin geriye döndürürler ve kaybedenlerden olursunuz.'),
(443, 3, 150, 'Hayır, mevlanız (sahibiniz ve dostunuz) ALLAH\'tır. O, yardım edenlerin en iyisidir'),
(444, 3, 151, 'Güçsüz ve yetkisiz kişileri ALLAH\'a ortak koştukları için inkarcıların kalplerine korku salacağız. Yerleri ateştir. Zalimlerin durağı ne kötü!'),
(445, 3, 152, 'ALLAH size verdiği sözde durdu ve nitekim izniyle onları darmadağın ettiniz. Ama, sevdiğiniz (zaferi) size gösterdikten sonra duraksadınız, savaş hakkında birbirinizle çekiştiniz ve emirleri dinlemediniz. Kiminiz dünyayı istiyordu, kiminiz de ahireti... Sonra, sınamak için sizi onlardan çevirdi ve her şeye rağmen sizi affetti. ALLAH\'ın inananlara nimeti boldur'),
(446, 3, 153, 'Elçi arkanızdan çağırmasına rağmen siz hiç kimseye dönüp bakmaksızın tepeyi tırmanıyordunuz. Yitirdiklerinize ve başınıza gelenlere üzülmeyesiniz diye size keder üstüne keder verdi. ALLAH yaptıklarınızdan Haberdardır.'),
(447, 3, 154, 'Sonra o üzüntünün ardından, içinizden bir grubu sakinleştiren güven dolu bir uykuyu üzerinize indirdi. Bir kısmınız bencilce kendi derdine düşmüş, cahiliye döneminde olduğu gibi ALLAH hakkında yanlış düşünceler üretiyor ve, "Bu işte bir yetkimiz varmı," diyordu. "Tüm yetki ALLAH\'ındır," de. Sana açmadıklarını içlerinde gizliyorlardı. "Bizim bir yetkimiz olsaydı burada öldürülmezdik," diyorlar. De ki, "Evlerinizde dahi olsaydınız, aranızda ölmesi kararlaştırılanlar devrilecekleri yere doğru sürüneceklerdi." ALLAH böylece göğüslerinizdekini dener ve kalplerinizdekini arıtır. ALLAH en gizli düşünceleri bilir'),
(448, 3, 155, 'İki ordunun çarpıştığı gün geriye dönenlerinizi yaptıkları bazı işlerden dolayı şeytan kaydırmak istemişti. Ancak ALLAH onları affetti. ALLAH gerçekten çok Bağışlayıcı, çok Şefkatlidir.'),
(449, 3, 156, 'İnananlar! Yolculuğa çıkan veya savaşa giden kardeşleri için, "Bizimle kalsalardı ne ölür ne de öldürülürlerdi," diyen inkarcılar gibi olmayın. ALLAH bu tip düşünceyi kalplerinde bir üzüntü kaynağı yapar. Yaşatan da öldüren de ALLAH\'tır. ALLAH yaptıklarınızı Görüyor.'),
(450, 3, 157, 'ALLAH yolunda ölür yahut öldürülürseniz, ALLAH\'ın bağışlaması ve rahmetine kavuşmuş olursunuz ki bu onların tüm topladıklardından daha iyidir.'),
(451, 3, 158, 'Ölürseniz de öldürülürseniz de ALLAH\'ın huzuruna çıkarılacaksınız.'),
(452, 3, 159, 'ALLAH\'ın sana bir bağışı olarak onlara yumuşak davrandın. Kaba ve katı yürekli olsaydın çevrenden dağılır giderlerdi. Onları affet, bağışlanmalarını dile ve yapılacak işler hakkında onlara danış. Karar verince de ALLAH\'a güven; ALLAH güvenenleri sever.'),
(453, 3, 160, 'ALLAH sizi desteklerse hiç bir güç sizi yenemez. Sizi terk ederse, artık size kim yardım edebilir ki? İnananlar ALLAH\'a güvensin.'),
(454, 3, 161, 'Bir peygamber ganimet mallarından hakkından fazlasını alamaz. Kim hakkından fazlasını alırsa diriliş günü o fazlalıkla gelir. Sonra herkese kazandığının karşılığı tam ödenir ve haksızlığa uğratılmazlar.'),
(455, 3, 162, 'ALLAH\'ın rızasını gözeten bir kimse ALLAH\'ın öfkesine uğrayan gibi olur mu? Onun yeri cehennemdir; o ne kötü bir sonuçtur!'),
(456, 3, 163, 'Onlar ALLAH katında derece derecedir. ALLAH yaptıkları herşeyi görmektedir.'),
(457, 3, 164, 'ALLAH inananların içinden, onlara ayetlerini okuyan, onları arındıran, onlara kitap ve bilgelik öğreten bir elçiyi göndermekle iyilikte bulundu. Oysa onlar daha önce apaçık bir sapıklık içindeydiler!'),
(458, 3, 165, 'Uğradığınız zararın iki katını (düşmanınıza) verdiğiniz halde, "Bu da niçin," dediniz. De ki, "(O) musibet, kendi tarafınızdandır." ALLAH her şeye Kadirdir.'),
(459, 3, 166, 'İki ordu çarpıştığı gün başınıza gelenler ALLAH\'ın izniyle oldu. İnananlar böylece ayırdedilir.'),
(460, 3, 167, 'İkiyüzlüler de böylece açığa çıkarılır. Kendilerine, "Gelin, ALLAH yolunda savaşın ya da katkıda bulunun," denildiğinde, onlar, "Savaştan anlasaydık size katılırdık," dediler. O an onlar imandan daha çok inkara yakın idiler. Kalplerinde olmayanı ağızlarıyla söylüyorlar. Halbuki ALLAH onların gizlediğini çok iyi biliyor.'),
(461, 3, 168, 'Onlar oturup, akrabaları için şunu söylediler: "Bize uysalardı öldürülmeyeceklerdi." De ki, "Ölümü kendinizden savın da doğru sözlü olduğunuzu kanıtlayın."'),
(462, 3, 169, 'ALLAH yolunda vurulanları ölü sanmayın; bilakis onlar Rableri katında nimetler içinde diridirler.'),
(463, 3, 170, 'ALLAH\'ın onlara bolca verdiği nimetlerle neşe içindedirler. Kendilerine daha katılmamış yoldaşlarına bir korku ve üzüntü olmadığını müjdelemek isterler.'),
(464, 3, 171, 'ALLAH\'ın bol nimetini ve ALLAH\'ın inananların ödülünü yitirmeyeceği gerçeğini müjdelemek isterler'),
(465, 3, 172, 'O inananlar ki, savaşta yara aldıktan sonra bile ALLAH\'ın ve elçisinin çağrısına uydular. İyilik yapıp erdemli davrananları için büyük bir ödül var.'),
(466, 3, 173, 'Onlar ki insanlar kendilerine, "Halk size karşı birleşip harekete geçmis; korkun onlardan," dediklerinde, bu onların ancak imanını arttırır ve şöyle derler: "Bize ALLAH yeter; o ne güzel Koruyucudur."'),
(467, 3, 174, 'Böylece, kendilerine hiçbir kötülük dokunmadan, ALLAH\'tan bir nimet ve lütufla geri döndüler.. ALLAH\'ın rızasını gözetmişlerdi. Büyük lütfun sahibi ALLAH\'tır.'),
(468, 3, 175, 'Şeytan ancak kendi dostlarına korku verir. Onlardan korkmayın, benden korkun; gerçekten inanmışsanız.'),
(469, 3, 176, 'İnkarcılıkta yarışanlar seni üzmesin. Onlar ALLAH\'a hiçbir zarar veremez. ALLAH ahirette onlara bir pay vermemek istiyor. Onlar için büyük bir azap var.'),
(470, 3, 177, 'İmanı bırakıp küfrü satın alanlar ALLAH\'a hiçbir zarar veremezler ve onlar için acıklı bir azap var.'),
(471, 3, 178, 'İnkarcılar, vermiş bulunduğum sürenin kendileri için iyi olduğunu sanmasınlar. Günahlarının artması için kendilerine süre veriyoruz. Onlara alçaltıcı bir azap var'),
(472, 3, 179, 'ALLAH inananları şu içinde bulunduğunuz durumda bırakacak değil. İyiyi kötüden ayıracaktır. ALLAH size geleceği bildirecek de değil. Fakat ALLAH elçilerinden dilediğini seçer. ALLAH\'a ve elçilerine inanın. İnanıp erdemli davranırsanız size büyük birödül var'),
(473, 3, 180, 'ALLAH\'ın lütfundan kendilerine bağışladığı nimetlerde cimrilik edenler onu kendileri için hayırlı sanmasın. Aksine, o kendileri için zararlıdır. Toplayıp biriktirdikleri, Diriliş Günü boyunlarına dolanacak. Göklerin ve yerin mirası ALLAH\'ındır. ALLAH yaptıklarınızdan haberdardır.'),
(474, 3, 181, '"ALLAH fakir, biz ise zenginiz," diyenlerin sözünü ALLAH işitmekte. Söylediklerini ve haksız yere peygamberleri öldürmelerini kaydedeceğiz ve "Yakıcı azabı tadın," diyeceğiz.'),
(475, 3, 182, '"Bu, kendi yaptıklarınızın bir sonucudur." Kuşkusuz ALLAH kullara karşı zalim değildir.'),
(476, 3, 183, 'Onlar, "Ateşin yiyeceği bir kurban getirmedikçe hiçbir elçiye inanmamamız konusunda ALLAH bizden söz aldı," diyorlar. De ki, "Benden önce, apaçık delillerle birlikte bu dediğinizi de size getiren elçiler geldi. Sözünüzün eriyseniz niçin onları öldürdünüz?"'),
(477, 3, 184, 'Seni yalanlarlarsa (şaşma), senden önce apaçık deliller, Zeburlar (Tanrı sevgisini dile getiren ilahiler) ve aydınlatıcı kitap getiren elçiler de yalanlanmıştı.'),
(478, 3, 185, 'Herkes ölümü tadacaktır. Diriliş günü ödülleriniz size eksiksiz olarak verilir. Kim ateşten kurtarılıp cennete sokulursa, zafer kazanmış olur. Dünya hayatı ancak aldatıcı bir zevkten ibarettir'),
(479, 3, 186, 'Paralarınızla, canlarınızla sınanacaksınız. Sizden önce kendilerine kitap verilenlerden ve ortak koşanlardan çok hakaretler işiteceksiniz. Direnir ve erdemli bir hayat sürerseniz büyük iş yapmış olursunuz.'),
(480, 3, 187, 'ALLAH kendilerine kitap verilenlerden, "Onu mutlaka insanlara açıklayacaksınız, gizlemiyeceksiniz," diye söz almıştı. Fakat onlar, onu arkalarına atıp az bir fiyata sattılar. Ne kötü bir alış veriş!'),
(481, 3, 188, 'Sanma ki verdikleriyle gururlanan ve yapmadıkları şeylerden ötürü övülmeyi sevenler, evet sanma ki onlar azaptan kurtulacak. Onlara acıklı bir azap var'),
(482, 3, 189, 'Göklerin ve yerin egemenliği ALLAH\'ındır. ALLAH herşeye Kadirdir'),
(483, 3, 190, 'Göklerin ve yerin yaratılmasında, gece ile gündüzün birbirini izlemesinde akıl sahipleri için ayetler var'),
(484, 3, 191, 'Onlar ki ayaktayken, otururken ve yanları üzerine yatarken ALLAH\'ı anarlar. Göklerin ve yerin yapısı ve yaratılışı hakkında düşünürler: "Rabbimiz, sen bunları boş yere yaratmadın, sen yücesin, bizi ateş azabından koru.'),
(485, 3, 192, '"Rabbimiz, sen kimi ateşe sokarsan elbette onu rezil etmişsindir. Zalimlere yardım eden olmaz.'),
(486, 3, 193, '"Rabbimiz, biz, \'Rabbinize inanın\' diye imana çağıran bir davetçiyi işittik ve inandık. Rabbimiz, günahlarımızı bağışla, kötülüklerimizi ört ve iyi kişiler olarak canımızı al.'),
(487, 3, 194, '"Rabbimiz, elçilerin yoluyla bize söz verdiğin şeyi ver, diriliş gününde bizi rezil etme. Sen sözünden hiç caymazsın."'),
(488, 3, 195, 'Rab\'leri onlara cevap verdi: "Ben, sizden hiçbir çalışanın yaptığını ödülsüz bırakmam, ister erkek olsun, ister kadın olsun; hepiniz eşitsiniz. Göç edenler, yurtlarından çıkarılanlar, yolumda işkence ve hakarete uğrayanlar, vuruşanlar, öldürülenler... Onların kötülüklerini örteceğim ve onları içlerinde ırmaklar akan bahçelere yerleştireceğim. ALLAH\'tan bir karşılık olarak... En güzel karşılık ALLAH\'ın yanındadır.'),
(489, 3, 196, 'İnkarcıların ülkelerdeki egemenliği seni aldatmasın.'),
(490, 3, 197, 'Bu kısa süreli bir yararlanmadır, sonra cehennemi boylayacaklar; ne kötü bir durak!'),
(491, 3, 198, 'Rab\'lerini dinleyenler için içlerinde ırmaklar akan cennetler var. ALLAH tarafından bir konut olarak orada ebedi kalacaklar. İyiler için ALLAH\'ın yanında bulunanlar daha hayırlıdır'),
(492, 3, 199, 'Kitaplıların öyleleri var ki ALLAH\'a, size indirilene ve kendilerine indirilene inanırlar. ALLAH\'a karşı derin saygıya sahiptirler. ALLAH\'ın ayetlerini satmazlar. Onların ödülleri Rab\'lerinin yanındadır. ALLAH hesabı çabuk görendir'),
(493, 3, 200, 'İnananlar! Başarmak için, güçlüklere karşı tek tek direnin, topluca direnin, birlik olun ve ALLAH\'ı dinleyin.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'İnsanlar! Sizi bir tek nefisten yaratan, ondan da eşini yaratan, ikisinden pek çok erkek ve kadın üreten Rabbinizi dinleyin. Adına birbirinizden isteklerde bulunduğunuz ALLAH\'a saygı gösterin; akrabalara da... ALLAH elbette sizi Gözetlemektedir.'),
(495, 4, 2, 'Yetimlere mallarını verin. Temiz olanı pis olanla değişmeyin. Mallarını kendi mallarınıza katıp yemeyin. Bu büyük bir adaletsizlik olur.'),
(496, 4, 3, 'Yetimler hakkında adaletli davranamamaktan korkuyorsanız uygun gördüğünüz kadınlarla ikişer, üçer, dörder evlenebilirsiniz. Onlara eşit davranamamaktan korkuyorsanız bir taneyle veya yeminlerinizin/anlaşmalarınızın hak sahibi olduklari ile yetinin. Sapmamanız için en uygunu budur.'),
(497, 4, 4, 'Kadınlara mehirlerini tam verin. Kendi istekleriyle o mehrin bir kısmını size bağışlarlarsa onu günül huzuruyla yiyebilirsiniz.'),
(498, 4, 5, 'ALLAH\'ın sizi gözetici kıldığı mallarınızı aklı ermezlere vermeyin. O mallarla onları (yetimleri) besleyin, giydirin ve onlarla güzel iletişim kurun'),
(499, 4, 6, 'Evlenme yaşına eriştiklerinde yetimleri sınayın. Onlarda olgunluk görürseniz kendilerine mallarını verin. Onlar büyüyecek diye savurganlık ve aceleyle mallarını yemeyin. Zengin, (malın korumasına gösterdiği çabanın ücretinden) vazgeçsin. Fakir ise uygun bir miktarda harcasın. Mallarını kendilerine tanıklar huzurunda geri verin. Hesap görücü olarak ALLAH yeter.'),
(500, 4, 7, 'Ana babanın ve akrabaların bıraktığından erkeklere bir pay vardır. Ana babanın ve akrabaların bıraktığından kadınlara da bir pay vardır. Miras ister az olsun ister çok olsun (kadın) bir pay almalıdır.'),
(501, 4, 8, 'Miras bölüşümünde, akrabalar, yetimler ve yoksullar da hazır bulunursa güzel sözler söyleyerek onlara da verin.'),
(502, 4, 9, 'Geriye zayıf çocuklar bırakan ve onların durumlarından endişe duyanlar dikkat etsinler! ALLAH\'ı düşünsünler ve uygun kararlar versinler.'),
(503, 4, 10, 'Yetimlerin mallarını haksız yere yiyenler karınlarını ateşle doldurmuş olurlar ve cehennemi boylayacaklar.'),
(504, 4, 11, 'ALLAH size çocuklarınız hakkında öğütte bulunuyor. Erkek, kadının iki katı pay alır. Mirasçılar sadece kadın olup iki kişiden fazla iseler terekenin üçte ikisi onlarındır. Çocuk sadece bir kadınsa terekenin yarısı onundur. Ölen kişi ardında çocuk bırakmışsa, ana ve babasının her birisine altıda bir düşer. Çocuğu yok da kendisine sadece ana ve babası varis oluyorsa bu durumda annesine üçte bir pay düşer. Kardeşi varsa bu durumda annesine altıda bir düşer. Tüm bu paylaşma oranları, ölenin yaptığı vasiyetten ve borçların ödenmesinden sonra gelir. Analarınız, babalarınız ve çocuklarınızdan hangisinin size daha yararlı olduğunu bilemezsiniz. Bu ALLAH\'ın yasasıdır. ALLAH Bilendir, Bilgedir.'),
(505, 4, 12, 'Çocukları yoksa, hanımlarınızın bıraktığı mirasın yarısı sizindir. Çocukları var ise, bıraktıklarının dörtte biri sizindir. Bu pay, borçlarının ödenmesinden ve yaptıkları vasiyetteki payların dağıtılmasından sonradır. Çocuklarınız yoksa bıraktığınızın dörtte biri onlarındır. Çocuklarınız varsa, bıraktığınızın sekizde biri onlarındır. Bu pay, borçlarınızın ödenmesinden ve yaptığınız vasiyetteki payların dağıtılmasından sonradır. Miras bırakan erkeğin veya kadının, çocuğu ve eşi olmayıp bir erkek veya bir kız kardeşi var ise bu durumda herbirine altıda bir düşer. Bundan fazla iseler, üçte biri paylaşırlar. Bu paylaşım vasiyetteki payların dağıtılmasından ve borçların ödenmesinden sonra uygulanmalıdır ki kimseye zarar verilmesin. Bu, ALLAH\'tan bir vasiyettir. ALLAH Bilir, Şefkatlidir.'),
(506, 4, 13, 'Bunlar ALLAH\'ın yasalarıdır. ALLAH\'a ve elçisine uyanları içinden ırmaklar akan cennetlere yerleştirir. Orada sürekli kalırlar. Bu, en büyük başarıdır.'),
(507, 4, 14, 'Kim ALLAH\'a ve elçisine isyan eder ve yasalarını çiğnerse, onu içinde sürekli kalacağı bir ateşe sokar. Onun için alçaltıcı bir azap var.'),
(508, 4, 15, 'Kadınlarınızdan fuhuş yapanlara karşı içinizden dört tanık getirin. Tanıklık ederlerse, onları, ölünceye veya ALLAH onlara bir yol açıncaya kadar evlerde tutun.'),
(509, 4, 16, 'Sizden zina işleyen çifti cezalandırın. Tevbe edip düzelirlerse onlardan vazgeçin. ALLAH yönelişleri kabul edendir, Rahim\'dir.'),
(510, 4, 17, 'Cahillik yüzünden bir kötülük işleyen ve hemen ardından tövbe edenlerin tövbesinin kabulu ALLAH\'a aittir. ALLAH tövbelerini kabul eder. ALLAH Bilendir, Bilgedir.'),
(511, 4, 18, 'Sürekli kötülük işleyen ve kendilerini ölüm yakalayınca, "Ben artık tövbe ettim," diyenlerin tövbesi geçersizdir. İnkarcı olarak ölenlerin de tövbesi geçersizdir. Onlar için acıklı bir azap var.'),
(512, 4, 19, 'İnananlar! Kadınların mirasına onların isteklerine aykırı olarak mirasçı olmanız sizin için yasal değil. Kendilerine önceden vermiş bulunduğunuz malları onlardan geri almak için onları sıkıştırmayın. Bir fuhuş işlemeleri hali istisna. Onlarla iyi geçinin. Onlardan hoşlanmıyorsanız, olur ki hoşlanmadığınız bir şeye ALLAH çok hayır koymuştur.'),
(513, 4, 20, 'Mevcut eşinizi bırakıp bir başka eşle evlenmek istiyorsanız, birincisine büyük miktarda mal vermişseniz bile ondan hiç bir şeyi geri almayın. Sahtekarlık ve iftira ederek ve günah işleyerek mi geri alacaksınız?'),
(514, 4, 21, 'Bir zamanlar birbirinizin en yakın içli dışlı dostu olmanıza ve sizden sağlam bir söz almalarına rağmen nasıl olur da onu geri alırsınız?'),
(515, 4, 22, 'Babanızın evlenmiş bulunduğu kadınlarla artık evlenmeyin. Fakat geçmişte yaptığınız bu tip evlilikleri de bozmayın. O davranış büyük bir günahtır, iğrenç ve kötü bir yoldur.'),
(516, 4, 23, 'Size şunlar yasaklandı: Anneleriniz, kızlarınız, kız kardeşleriniz, halalarınız, teyzeleriniz, kardeş kızları, kız kardeş kızları, sizi emziren analarınız, süt bacılarınız, karılarınızın anaları, kendileriyle gerdeğe girdiğiniz karılarınızdan olup evlerinizde bulunan üvey kızlarınız -eğer onlarla gerdeğe girmemişseniz kızlarıyla evlenebilirsiniz-, öz oğullarınızın eşleri ve iki kız kardeşi bir arada almanız... Mevcut evlilikler bu yasanın dışındadır. ALLAH Bağışlayandır, Rahimdir.'),
(517, 4, 24, 'Ayrıca yeminlerinizin/anlaşmalarınızın hak sahibi oldukları hariç, evli kadınlar... Bunlar, ALLAH\'ın üzerinize farz kıldığı yasalardır. Bunların dışındakiler, iffetli yaşamanız, zina etmemeniz ve mehirleriyle istemeniz koşuluyla size helaldir. Onlardan hoşlandıklarınıza, bir farz olarak mehirlerini ödeyin. Bu farzı yerine getirirken mehri ayarlamak için karşılıklı anlaşmanızda bir sakınca yoktur. ALLAH Bilendir, Bilgedir.'),
(518, 4, 25, 'Sizden özgür kadınlarla evlenmeye güç yetiremiyenler, inanmış yeminlerinizin/anlaşmalarınızın hak sahibi oldukları ile evlensinler. İnancınızı en iyi bilen ALLAH\'tır. Birbirinize eşitsiniz. Sahiplerinin izniyle ve uygun şekilde mehirlerini ödeyerek onlarla nikahlanın. İffetli yaşasınlar, zina etmesinler ve gizli dostlar edinmesinler. Evlilik yoluyla özgürlüklerine kavuştuktan sonra zina yaparlarsa, kendilerine özgür kadınlara verilen cezanın yarısı uygulanmalı. Bu, günaha girmekten korkanlarınız içindir. Sabrederseniz sizin için daha iyi. ALLAH Bağışlayandır, Rahimdir'),
(519, 4, 26, 'ALLAH size bunları açıklamak, sizden öncekilerin yoluna sizi iletmek ve kendisine yönelişinize (tövbenize) karşılık vermek ister. ALLAH Bilendir, Bilgedir.'),
(520, 4, 27, 'ALLAH sizin kendisine yönelişinize karşılık vermek ister; heva ve heveslerine uyanlar ise sizin büsbütün sapıtmanızı ister'),
(521, 4, 28, 'ALLAH ise yükünüzü hafifletmek istiyor. Çünkü insan zayıf yaratıldı'),
(522, 4, 29, 'İnananlar! Birbirinizin malını haksızlık ve hile ile yemeyin; karşılıklı rıza ile yapılan ticaret olursa başka. Birbirinizi öldürmeyin. ALLAH size Rahimdir'),
(523, 4, 30, 'Kim düşmanlık ve zulüm ile bunu yaparsa onu ateşe göndeririz. Bu, ALLAH\'a kolaydır'),
(524, 4, 31, 'Büyük yasaklardan kaçınırsanız günahlarınızı örter ve sizi onurlu bir girişle kabul ederiz'),
(525, 4, 32, 'ALLAH\'ın herbirinize bağışladığı yeteneklere imrenmeyin. Erkeklere kazandıklarından bir pay, kadınlara da kazandıklarından bir pay var. ALLAH\'tan size lütfunu bağışlamasını isteyin. ALLAH her şeyi Bilir.'),
(526, 4, 33, 'Ananın, babanın, akrabaların ve evlenme yoluyla size akraba olanların bıraktıklarından herbiriniz için paylar ayırdık. Herbirine payını vermelisiniz. Elbette ALLAH her şeye Tanıktır.'),
(527, 4, 34, 'Erkekler kadınları gözetirler. Zira ALLAH herbirine farklı yetenekler ve özellikler vermiştir. Nitekim erkekler evin geçiminden sorumludur. Erdemli kadınlar, (Tanrı\'nın yasasına) boyun eğer ve ALLAH\'ın korumasını emrettiği (onur ve iffetlerini) tek başlarına bile olsalar korurlar. İffetlerinden endişe duyduğunuz kadınlara öğüt verin, yataklarınızı ayırın ve nihayet onları çıkarın. Size itaat ederlerse onlara karşı bir yol aramayın. ALLAH Yücedir, Büyüktür.'),
(528, 4, 35, 'Evli çiftin aralarının açılmasından endişeleniyorsanız, erkeğin ailesinden bir hakem, kadının ailesinden de bir hakem atamalısınız. (Karı ve koca) barışmayı isterlerse ALLAH ikisinin arasını bulur. ALLAH Bilir, Haber alır.'),
(529, 4, 36, 'ALLAH\'a kulluk edin O\'na hicbirşeyi ve kimseyi ortak koşmayın. Ana-babaya, yakınlara, yetimlere, yoksullara, yakın komşuya, uzak komşuya, yanınızdaki arkadaşa, yolcuya ve yeminlerinizin/anlaşmalarınızın hak sahibi olduklarına iyi davranın. ALLAH kendini beğenmiş kibirli kişileri sevmez.'),
(530, 4, 37, 'Bunlar üstelik cimridirler ve halka cimriliği öğütlerler. ALLAH\'ın kendi lütfundan onlara verdiğini gizlerler. İnkarcılara acıklı bir azap hazırladık'),
(531, 4, 38, 'Bunlar, mallarını gösteriş için halka verir. ALLAH\'a ve ahiret gününe inanmazlar. Kimin arkadaşı şeytan olmuşsa çok kötü bir arkadaşa sahip olmuştur'),
(532, 4, 39, 'ALLAH\'a ve ahiret gününe inansalardı ve ALLAH\'ın kendilerine vermiş olduğu rızıktan yardım olarak harcasalardı ne olurdu? ALLAH onları Bilir.'),
(533, 4, 40, 'ALLAH bir atom ağırlığınca bile haksızlık yapmaz. Aksine iyiliğin karşılığını kat kat verir ve kendi katından büyük bir ödül de verir.'),
(534, 4, 41, 'Her ulusun üzerine bir tanık getirdiğimizde, seni de bunların üzerine tanık olarak getirsek halleri nice olur?'),
(535, 4, 42, 'İnkar edenler ve elçiye karşı gelenler o gün yerle bir olmak isterler. ALLAH\'tan hiç bir söz gizleyemezler.'),
(536, 4, 43, 'İnananlar! Sarhoşken, ne dediğinizi bilinceye kadar, yolcu olanlar hariç cinsel ilişkiden sonra yıkanıncaya kadar namaza durmayın. Hasta veya yolcu iseniz, yahut biriniz tuvaletten gelmişse, yahut kadınlarla cinsel ilişkiye girmiş olup da su bulamamışsanız, temiz ve kuru bir toprağa dokunup yüzünüze ve ellerinize sürerek teyemmüm edin. ALLAH Affeder, Bağışlar.'),
(537, 4, 44, 'Kendilerine kitaptan bir pay verilenlerin, sapıklığı satın aldıklarını ve sizin de yolu sapıtmanızı istediklerini görmüyor musun?'),
(538, 4, 45, 'ALLAH düşmanlarınızı daha iyi bilir. Dost ve sahip olarak ALLAH yeter, yardımcı olarak ALLAH yeter'),
(539, 4, 46, 'Yahudilerin bir kısmı kelimelerin anlamını değiştirir ve "İşittik ancak kabul etmiyoruz," veya "Sözünüz sağır kulağa giriyor" veya dinle alay etmek için dillerini eğip bükerek, "Raina (çobanımız ol)," derler. Onlar, "İşittik ve itaat ettik," "Dinliyoruz" ve "Bizi gözet," deselerdi kendileri için daha iyi ve daha doğru olurdu. Ne var ki ALLAH inkarlarından ötürü onları lanetlemiştir. Çokları inanmaz.'),
(540, 4, 47, 'Kitaplılar! Bazı yüzleri çevirip sürgüne göndermeden ve Cumartesi Halkını lanetlediğimiz gibi lanetlemeden önce, yanınızdakileri onaylayıcı olarak indirdiğimize inanın. ALLAH\'ın emri sürekli uygulanmıştır.'),
(541, 4, 48, 'ALLAH kendisine ortak koşulmasını bağışlamaz; bundan başkasını dilediğine bağışlar. Kim ALLAH\'a ortak koşarsa çok büyük bir hakaret etmiş olur'),
(542, 4, 49, 'Kendilerini temize çıkaranları görmüyor musun? Oysa ALLAH dilediğini temize çıkarır. Onlara en ufak bir haksızlık yapılmaz'),
(543, 4, 50, 'Bak, nasıl da yalan yere ALLAH\'a iftira ediyorlar. Büyük bir hakaret olarak bu yeter.'),
(544, 4, 51, 'Kendilerine kitaptan bir pay verilenleri görmüyor musun? Putperestliğe ve yanlış doktrinlere inanıyorlar ve inkar edenler için, "Bunlar, inananlardan daha doğru yoldadır," diyorlar.'),
(545, 4, 52, 'Onlar ALLAH\'ın lanetine uğradılar. ALLAH kimi lanetlerse onun için bir yardımcı bulamazsın.'),
(546, 4, 53, 'Yoksa onların yönetimde bir payı mı var? Öyle olsaydı insanlara bir çekirdek bile vermezlerdi.'),
(547, 4, 54, 'Yoksa ALLAH\'ın lütfundan insanlara verdiğini mi çekemiyorlar? Oysa biz, İbrahim ailesine kitap ve bilgelik verdik. Onlara büyük bir otorite verdik.'),
(548, 4, 55, 'Onlardan kimi ona inandı, kimi de ondan yüz çevirdi. Onlara ceza olarak cehennem yeter.'),
(549, 4, 56, 'Ayet ve mucizelerimizi inkar edenleri bir ateşe atacağız. Derileri yandıkça, azabı tadmaları için başka derilerle yenileyeceğiz. ALLAH Üstündür, Bilgedir.'),
(550, 4, 57, 'İnanıp erdemli davrananları ise içinden ırmaklar akan cennetlere (bahçelere) yerleştireceğiz; orada sürekli kalırlar. Onlar için orada tertemiz eşler var. Onları serin gölgelere sokacağız.'),
(551, 4, 58, 'ALLAH emaneti sahiplerine vermenizi, insanlar arasında hükmettiğiniz zaman adaletle hükmetmenizi emreder. ALLAH size ne güzel öğüt veriyor. ALLAH İşitendir, Görendir.'),
(552, 4, 59, 'İnananlar! ALLAH\'a uyun, elçisine uyun; sizden görev başında olanlara da. Her hangi bir konuda anlaşmazlığa düşerseniz onu ALLAH\'a ve elçisine havale ediniz. ALLAH\'a ve ahiret gününe inanıyorsanız... Bu, sizin için daha iyi ve en güzel çözüm yoludur.'),
(553, 4, 60, 'Sana ve senden önce indirilenlere inandıklarını iddia ettikleri halde azgınların ve despotların yasalarını uygulamak isteyenleri görmüyor musun? Oysa onu inkar etmekle emredilmişlerdi; fakat şeytan onları büsbütün saptırmak istiyor.'),
(554, 4, 61, 'Kendilerine, "ALLAH\'ın indirdiğine ve elçisine geliniz," dendiğinde, o ikiyüzlülerin (münafıkların) senden alabildiğine kaçtıklarını görüyorsun.'),
(555, 4, 62, 'İşlediklerinin sonucu olarak başlarına bir musibet gelse hemen sana gelerek, "Amacımız ancak iyilik yapmak ve uzlaştırmaktı," diye ALLAH adına yemin ederler'),
(556, 4, 63, 'ALLAH onların kalplerindekini bilir. Onlardan yüz çevir, onları aydınlat, ve kişiliklerini etkileyecek güzel sözler söyle.'),
(557, 4, 64, 'Biz her elçiyi, ALLAH\'ın izniyle kendisine itaat edilsin diye gönderdik. Onlar, kişiliklerine zulmettikleri zaman sana gelip ALLAH\'tan günahlarının bağışlanmasını dileselerdi ve elçi de onlar için bağışlanma dileseydi, elbette ALLAH\'ı Affedici ve Rahim bulacaklardı.'),
(558, 4, 65, 'Hayır, Rabbine andolsun, anlaşmazlıklarında seni aralarında hakem kılmadıkça ve verdiğin kararı, kalplerinde bir burukluk duymadan kabul etmedikçe ve tam teslim olmadıkça inanmış olmazlar.'),
(559, 4, 66, '"Canınızı adayın" yahut "Yurtlarınızdan çıkın," diye emretmiş olsaydık, pek azı hariç bunu yapmazlardı. Kendilerine öğütleneni uygulasalardı onlar için daha iyi ve daha sağlam olurdu.'),
(560, 4, 67, 'Kendilerine katımızdan büyük bir ödül verirdik.'),
(561, 4, 68, 'Ve onları doğru bir yola iletirdik.'),
(562, 4, 69, 'ALLAH\'a ve elçisine uyanlar, ALLAH\'ın kendilerine iyilikte bulunduğu peygamberler, doğrulayıcılar, şehitler ve salihler ile beraberdirler. Onlar ne güzel arkadaştır.'),
(563, 4, 70, 'Bu lütuf ALLAH\'tandır. Bilen olarak ALLAH yeter.'),
(564, 4, 71, 'İnananlar! Tetikte durun. Bölük bölük ya da topluca harekete geçin.'),
(565, 4, 72, 'İçinizde ağır davrananlar var. Size bir musibet gelirse, "ALLAH bana iyilik etti de onlarla birlikte şehit düşmedim," der'),
(566, 4, 73, 'ALLAH\'tan size bir lütuf erişirse sanki önce aranızda hiç bir dostluk yokmuş gibi, "Keşke ben de onlarla birlikte olsaydım da büyük bir başarı kazansaydım," der.'),
(567, 4, 74, 'Dünya hayatını ahirete değişenler ALLAH yolunda savaşsınlar. Kim ALLAH yolunda savaşır, ölür yahut yenerse, kendisine büyük bir ödül vereceğiz.'),
(568, 4, 75, 'Size ne oldu ki "Rabbimiz, bizi halkı zalim olan bu ülkeden kurtar, bize sahip çık, bize yardım et," diye feryad eden ezilmiş erkekler, kadınlar ve çocuklara rağmen hâlâ ALLAH yolunda savaşmıyorsunuz?'),
(569, 4, 76, 'İnananlar ALLAH yolunda savaşırlar. Kafirler ise azgınların ve despotların yolunda savaşırlar. Öyleyse şeytanın dostlarıyla savaşın; şeytanın planı zayıftır'),
(570, 4, 77, 'Kendilerine, "Elinizi savaştan çekin, namazı gözetin, zekatı verin," denilenlere dikkat etmedin mi? Kendilerine savaşmaları emredildiğinde, insanlardan ALLAH\'tan korkar gibi, belki daha fazla korkmaya başladılar ve "Rabbimiz, neden bize savaşı yükledin, bizi yakın bir zamana kadar erteleyemez miydin!," dediler. De ki, "Bu dünyanın varlığı azdır, erdemliler için ahiret daha hayırlıdır; en ufak bir haksızlığa uğratılmayacaksınız."'),
(571, 4, 78, 'Nerede olursanız olun, sağlam kalelerde bile olsanız ölüm sizi yakalar. Başlarına iyi bir şey gelse, "Bu ALLAH tarafındandır," derler. Kendilerine bir kötülük dokunsa, "Bu senin tarafındandır," derler. De ki, "Hepsi ALLAH tarafındandır." Bu topluma ne oluyor ki neredeyse hiç bir söz anlamıyorlar!'),
(572, 4, 79, 'Sana gelen her iyilik ALLAH\'tan, sana gelen her kötülük ise kendindendir. Seni insanlara elçi olarak gönderdik. Tanık olarak ALLAH yeter.'),
(573, 4, 80, 'Elçiye uyan ALLAH\'a uymuş olur. Kim yüz çevirirse çevirsin; biz seni onların üzerine bekçi göndermedik.'),
(574, 4, 81, '"Tamam, itaat ediyoruz," derler. Ancak senin yanından çıktıklarında onlardan bir takımı, söylemiş olduklarının tersini kuruyor. ALLAH onların planlayıp kurdukları şeyleri kaydediyor. Onlara aldırma; ALLAH\'a güven. Destekleyici olarak ALLAH yeter.'),
(575, 4, 82, 'Kuran\'ı incelemiyorlar mı? ALLAH\'tan başkasının olsaydı onda bir çok çelişki bulacaklardı.'),
(576, 4, 83, 'Onlara güvenlik ve tehlikeyle ilgili bir söylenti ulaşsa onu yayarlar. Durumu elçiye ve aralarındaki yetkililere iletselerdi uzmanları onu değerlendirirdi. Size ALLAH\'ın lütfu ve rahmeti olmasaydı pek azınız hariç şeytana uyacaktınız.'),
(577, 4, 84, 'ALLAH yolunda savaşmalısın. Sen ancak kendinden sorumlusun. İnananları da aynı şeye özendir ki ALLAH kafirlerin gücünü etkisiz hale getirsin. ALLAH\'ın gücü daha şiddetli, cezası daha çetindir'),
(578, 4, 85, 'Kim bir iyiliğe aracılık ederse kendisi için ondan bir kredi var. Kim bir kötülüğe aracılık ederse, kendisi için ondan bir pay var. ALLAH herşeyi kontrol eder.'),
(579, 4, 86, 'Herhangi bir selam ile selamlandığınız zaman daha güzeliyle veya aynen iade ederek selamı alın. Kuşkusuz ALLAH herşeyi hesaba katar.'),
(580, 4, 87, 'ALLAH: O\'ndan başka tanrı yoktur. Gerçekleşmesi mutlak olan diriliş gününde sizleri biraraya toplayacaktır. ALLAH\'tan daha doğru sözlü kim olabilir?'),
(581, 4, 88, 'Size ne oluyor da ikiyüzlüler hakkında iki gruba ayrılıyorsunuz? İşlediklerinden ötürü ALLAH onları başaşağı çevirdi. ALLAH\'ın saptırdığını siz mi doğru yola ulaştırmak istiyorsunuz? ALLAH\'ın saptırdığı kimseye sen hiç bir yol bulamazsın.'),
(582, 4, 89, 'Sizin de kendileri gibi inkar etmenizi istediler ki onlarla bir olasınız. ALLAH yolunda harekete geçinceye kadar onlardan kimseyi dost edinmeyin. Size karşı dönerlerse onları yakalayın, onları bulduğunuz yerde öldürün. Onları ne dost ne de yardımcı edinmeyin;'),
(583, 4, 90, 'Ancak aranızda anlaşma bulunan bir topluluğa katılan, yahut sizinle veya kendi halkıyla savaşmaktan dolayı göğüslerinde sıkıntı duyarak size gelenler hariç... ALLAH dileseydi onları başınıza musallat eder ve onlar da sizinle savaşırdı. Sizi yalnız bırakır, sizinle savaşmaz ve size barış önerirlerse ALLAH sizin onlara saldırmanıza izin vermez.'),
(584, 4, 91, 'Hem sizinle hem kendi toplumlarıyla güven içinde durmak isteyen bir başka grup bulacaksın. Ne zaman kargaşalığa çağrılsalar içine dalarlar. Sizi yalnız bırakmaz, barış yapmak istemez ve ellerini sizden çekmezlerse karşılaştığınız yerde onları öldürebilirsiniz. Böylelerine karşı size apaçık yetki verdik.'),
(585, 4, 92, 'İnanmış bir kişi, kaza hali hariç inanmış birisini öldüremez. Kim bir inananı kazara öldürmüşse inanmış bir köleyi salmalı ve ölenin ailesine diyet ödemeli. Ancak diyetten vazgeçip sadaka olarak kabul ederlerse başka. Öldürülen, sizinle savaş halinde olan bir topluluğa mensup bir inanan ise, o zaman inanan bir köleyi salmalısınız. Ancak, maktul aranızda anlaşma olan bir topluluktan ise ailesine diyet vermeli ve inanmış bir köleyi salmalısınız. Kim (gerekli parayı veya salacağı bir köle) bulamıyorsa, ALLAH tarafından tevbesinin kabul edilmesi için iki ay aralıksız oruç tutmalıdır. ALLAH Bilendir, Bilgedir.'),
(586, 4, 93, 'Kim inanmış birisini kasden öldürürse yeri, sürekli kalacağı cehennemdir. ALLAH kendisine gazap ve lanet etmiştir. Ve onun için büyük bir azap hazırlamıştır.'),
(587, 4, 94, 'İnananlar, ALLAH yolunda çarpışmağa çıktığınız zaman dikkatli olun. Size barış önerene, dünya hayatının geçici yararını gözeterek "Sen inanmıyorsun," demeyin. Çünkü ALLAH\'ın yanında çok ganimetler var. Daha önceleri siz de onlar gibiydiniz; fakat ALLAH size lütfetti. Öyleyse iyice araştırıp emin olun. ALLAH yaptıklarınızdan Haberdardır.'),
(588, 4, 95, 'Geçerli özre sahip olmaksızın yerlerinde oturanlar ile mallarıyla canlarıyla ALLAH yolunda savaşım verenler bir olmaz. ALLAH mallarıyla, canlarıyla savaşım verenleri derece bakımından oturanlardan üstün kıldı. ALLAH hepsine güzellikler söz veriyor. Bununla birlikte, ALLAH savaşım verenleri oturanlar üzerine daha büyük bir ödülle üstün kıldı;'),
(589, 4, 96, 'Kendi katından derecelerle, bağışlama ve rahmet ile... ALLAH Bağışlayandır, Rahimdir.'),
(590, 4, 97, 'Kendilerine zulmedenlerin canlarını alırken melekler: "Ne halde idiniz," derler. "Bizler yeryüzünde ezilmiş kimselerdik," diye cevap verirler. "ALLAH\'ın yeri geniş değil miydi ki oralara göç edeydiniz," derler. Onların yeri cehennem. O ne kötü bir dönüş noktası.'),
(591, 4, 98, 'Ancak, hiç bir çıkış yoluna gücü yetmeyen ve göç etmeye yol bulamayan erkek, kadın ve çocuk ezilmişler hariç.'),
(592, 4, 99, 'Böylelerinin ALLAH tarafından affedilmesi umulur. ALLAH Affedicidir, Bağışlayandır.'),
(593, 4, 100, 'Kim ALLAH yolunda göç ederse yeryüzünde barınacak çok yer ve bolluk bulur. Kim ALLAH\'a ve elçisine göç etmek için evinden çıkar ve sonra kendisini ölüm yakalarsa, ödülünü vermek ALLAH\'a düşer. ALLAH Bağışlayandır, Rahimdir.'),
(594, 4, 101, 'Yeryüzünde savaş için yolculuğa çıktığınız zaman inkarcıların size saldırmasından korkuyorsanız namazı kısaltmanızda bir sakınca yok. Kuşkusuz inkarcılar sizin açık düşmanınızdır'),
(595, 4, 102, 'Sen içlerinde olup onlara namaz kıldırdığın zaman, onlardan bir grup sizi korumak için yanınızda bulunsun ve silahlarını da yanlarına alsın. Namaza duranlar secdeye vardıklarında arkanızda bulunsunlar. Sonra namaz kılmamış olan grup gelsin ve seninle birlikte namaza dursunlar, (namazı bitirmiş olan grup ise) silahlarını alıp nöbet tutsunlar. İnkarcılar, silahlarınız ve eşyanız hakkında dikkatsiz davranmanızı ve böylece sizi ani bir baskınla bozguna uğratmayı umarlar. Yağmur ve hastalık gibi özürlerden ötürü silahlarınızı bırakmanızda bir sakınca yok. Ancak alarmda olun. ALLAH kafirlere alçaltıcı bir azap hazırlıyor.'),
(596, 4, 103, 'Namazı bitirdiğiniz zaman ayakta, oturarak ve uzanarak ALLAH\'ı anın. Güvene kavuştuğunuzda namazı gözetiniz. Namaz, inananlar üzerine belirli vakitlerde farz kılınmıştır.'),
(597, 4, 104, 'O topluluğu izlemekte gevşeklik göstermeyin. Siz acı çekiyorsanız onlar da sizin gibi acı çekiyor. Fakat siz, onların ALLAH\'tan ummadığını umuyorsunuz. ALLAH Bilendir, Bilgedir.'),
(598, 4, 105, 'Halkın arasında ALLAH\'ın sana gösterdiği gibi yargı veresin diye Biz sana gerçeği içeren kitabı indirdik. Hainlerden yana olma.'),
(599, 4, 106, 'ALLAH\'tan bağışlanma dile. ALLAH Bağışlayandır, Rahimdir.'),
(600, 4, 107, 'Kendi özlerine hainlik edenleri savunma; ALLAH ihanet edenleri ve günah işleyenleri sevmez.'),
(601, 4, 108, 'Halktan gizlerler de ALLAH\'ı umursamazlar. Oysa O\'nun hoşlanmadığı düşünceleri kurarlarken O onlarla beraberdir. ALLAH onların tüm yaptıklarını Kuşatır.'),
(602, 4, 109, 'İşte sizler bu tür kişileri dünya hayatında savunuyorsunuz. Peki, diriliş gününde ALLAH\'a karşı kim onları savunacak? Ya da kim onlara avukat olacak?'),
(603, 4, 110, 'Kim bir kötülük işler veya özüne zulmeder de sonra ALLAH\'tan bağışlanma dilerse ALLAH\'ı Bağışlayıcı ve Rahim bulur.'),
(604, 4, 111, 'Kim bir günah kazanırsa kendisine karşı kazanmış olur. ALLAH Bilendir, Bilgedir.'),
(605, 4, 112, 'Kim bir hata yahut bir günah işler de sonra onu bir suçsuzun üzerine atarsa şüphesiz büyük bir iftira ve apaçık bir günah yüklenmiş olur.'),
(606, 4, 113, 'ALLAH\'ın sana lütfu ve rahmeti olmasaydı, onlardan bir grup seni nerdeyse saptıracaktı. Onlar, yalnız kendilerini saptırır; sana hiçbir zarar veremezler. ALLAH sana kitap ve bilgeliği indirdi ve sana bilmediğin şeyleri öğretti. ALLAH\'ın sana olan lütfu büyüktür.'),
(607, 4, 114, 'Onların gizli görüşmelerinin çoğunda hayır yoktur; sadakayı, iyiliği veya halkın arasını düzeltmeyi savunmaları başka. Kim ALLAH\'ın rızasını arayarak bunları yaparsa ona büyük bir ödül vereceğiz.'),
(608, 4, 115, 'Doğru yol kendisine belli olduktan sonra kim elçiye karşı gelir ve inananların yolundan farklı bir yol edinirse, seçtiği yöne kendisini yöneltir ve onu cehenneme atarız. Ne kötü bir dönüş noktasıdır orası.'),
(609, 4, 116, 'ALLAH kendisine ortak koşulmasını (ölünceye kadar sürdüğü taktirde) affetmez; bunun aşağısındaki günahları ise dilediği kişiye affeder. Kim ALLAH\'a ortak koşarsa tamamen sapıtmış olur.'),
(610, 4, 117, 'O\'ndan ayrı olarak tanrıçalara taparlar. Aslında inatçı şeytandan başkasına tapmıyorlar.'),
(611, 4, 118, 'ALLAH kendisine lanet etmiş ve o da, "Elbette senin kullarından belli bir pay alacağım," demişti,'),
(612, 4, 119, '"Onları saptıracağım, onları kuruntularla oyalayacağım, hayvanların kulaklarını yarmalarını (böylece etlerini haram etmelerini) emeredeceğim, ALLAH\'ın yaratıklarını değiştirmelerini emredeceğim." Kim ALLAH yerine şeytanı dost ve egemen edinirse apaçık bir kayba uğramıştır.'),
(613, 4, 120, 'Onlara söz verir, ümit verir. Gerçekte, şeytanın onlara verdiği söz kandırmadan başka bir şey değil.'),
(614, 4, 121, 'Varacakları nokta cehennemdir; ondan kaçamazlar.'),
(615, 4, 122, 'İnanıp erdemli işler yapanları, içlerinde ırmaklar akan cennetlere yerleştireceğiz; orada ebedi kalacaklar. ALLAH\'ın sözü gerçektir. ALLAH\'tan daha doğru sözlü kim olabilir?'),
(616, 4, 123, 'Bu, ne sizin kuruntularınız ne de kitap halkının kuruntularıra göredir: Kim bir kötülük işlerse karşılığını bulur ve ALLAH\'tan başka bir Sahip ve Yardımcı bulamaz'),
(617, 4, 124, 'Erkek veya kadın, her kim inanarak erdemli bir hayat sürerse cennete girer ve en ufak bir haksızlık görmez'),
(618, 4, 125, 'ALLAH\'a teslim olarak iyilik yapan ve İbrahim\'in tektanrıcı dinine uyandan daha güzel bir dine kim sahip olabilir? ALLAH İbrahimi arkadaş edindi.'),
(619, 4, 126, 'Yerde ve göktekilerin hepsi ALLAH\'ındır. ALLAH her şeyi Kuşatır'),
(620, 4, 127, 'Sana kadınlar hakkında soruyorlar. De ki, "ALLAH bu Kitap yoluyla kadınlar hakkında sizi aydınlatıyor. Yasal hakları olan mallarını vermeyip kendileriyle evlenmek istediğiniz kadınların yetimleri ve zavallı çoçukları hakkında Kitapta size okunan ayetler yetimlere adaletli davranmanızı emretmektedir. Yaptığınız her iyiliği ALLAH Biliyor."'),
(621, 4, 128, 'Bir kadın kocasının huysuzluğundan, yahut kendisinden yüz çevirmesinden endişe ediyorsa uzlaşmayla tekrar aralarını düzeltmeleri daha uygun. Uzlaşma daha iyidir. Kişi bencil ve kıskanç davranmağa eğilimlidir. İyilik yapar ve erdemli davranırsanız, elbette ALLAH yaptıklarınızı haber alır.'),
(622, 4, 129, 'Ne kadar isteseniz de kadınlara eşit davranamazsınız. Öyleyse tamamen birine yönelip de diğerini askıdaymış gibi bırakmayın. Bu durumu düzeltip erdemli olursanız ALLAH Bağışlayandır, Rahimdir.'),
(623, 4, 130, 'Çiftler birbirlerinden ayrılırlarsa, ALLAH her birisini bol nimetiyle yoksulluktan kurtarır. ALLAH Cömerttir, Bilgedir.'),
(624, 4, 131, 'Göklerde ve yerde ne varsa ALLAH\'ındır. Sizden önce kendilerine kitap verilenlere de size de, "ALLAH\'ı sayıp dinleyin," diye öğütledik. İnkar ederseniz, bilesiniz ki göklerde ve yerde ne varsa ALLAH\'ındır. Kuşkusuz ALLAH kimseye muhtaç değildir, övgüye layıktır'),
(625, 4, 132, 'Göklerde ve yerde ne varsa ALLAH\'ındır. Koruyucu olarak ALLAH yeter'),
(626, 4, 133, 'İnsanlar! O dilerse sizi ortadan kaldırıp başkalarını getirir. ALLAH\'ın gücü buna yeter.'),
(627, 4, 134, 'Her kim dünya nimetini isterse, bilsin ki hem dünya hem ahiret nimeti ALLAH\'ın yanındadır. ALLAH İşitendir, Görendir.'),
(628, 4, 135, 'İnananlar! Kendiniz, ananınz, babanız ve yakınlarınız aleyhinde dahi olsa ALLAH için tanıklık ederek adaleti gözetin. İster zengin, ister fakir olsun, ALLAH her iki gruba da bakar. Öyleyse, kişisel çıkar ve duygularınıza uyarak taraflı davranmayın. Gerçeği çarpıtırsanız veya tanıklık etmekten çekinirseniz, bilesiniz ki ALLAH yaptıklarınızı haber alır.'),
(629, 4, 136, 'İnananlar! ALLAH\'a, elçisine, elçisine indirdiği kitaba ve daha önceden indirdiği kitaplara inanın. Kim ALLAH\'ı, meleklerini, kitaplarını, elçilerini ve ahiret gününü inkar ederse büsbütün sapıtmış olur.'),
(630, 4, 137, 'İnanıp sonra inkar eden, tekrar inanıp tekrar inkar eden ve daha sonra inkarlarında ileri giden kimseleri ALLAH affetmeyecek ve onlara yol göstermeyecek'),
(631, 4, 138, 'İkiyüzlülere acıklı bir azabı müjdele.'),
(632, 4, 139, 'Onlar ki inananlardan ayrı inkarcılarla işbirliği yapar. Onların yanında onur mu arıyorlar? Oysa, onur tümüyle ALLAH\'a aittir'),
(633, 4, 140, 'Size kitapta bildirmiştir ki: ALLAH\'ın ayetlerinin inkar edilip alaya alındığını işittiğiniz zaman, onlar başka bir söze dalıncaya kadar onlarla birlikte oturmayın; yoksa siz de onlar gibi olursunuz. ALLAH bütün ikiyüzlüleri ve inkarcıları cehennemde toplayacak.'),
(634, 4, 141, 'Onlar sizi gözetleyip dururlar. ALLAH\'tan size bir zafer geldiğinde, "Biz sizinle birlikte değil miydik," derler. Kazanma sırası inkarcılara gelirse bu sefer, "Biz size destek olarak sizi inananlardan korumadık mı," derler. Diriliş günü, ALLAH aranızda hüküm verecektir. ALLAH inkarcılara, inananlar üzerine egemen olma fırsatını vermeyecek'),
(635, 4, 142, 'İkiyüzlüler ALLAH\'ı aldattıklarını zanneder. Halbuki O, onları (böylece) aldanmış bırakır. Onlar namaza kalktıklarında üşene üşene kalkar. Çünkü insanlara gösteriş yaparlar ve ALLAH\'ı pek az anarlar.'),
(636, 4, 143, 'Arada bocalayıp dururlar; ne bunlara ne de onlara katılırlar. ALLAH\'ın şaşırttığına bir yol bulamazsın.'),
(637, 4, 144, 'İnananlar, inananları bırakıp inkarcıları dost edinmeyin. ALLAH\'a, size karşı kullanacağı bir delil mi vermek istiyorsunuz!'),
(638, 4, 145, 'İkiyüzlüler ateşin en aşağı katındadır. Onlara yardım edecek kimse bulamazsın.'),
(639, 4, 146, 'Ancak tevbe eden, durumunu düzelten, ALLAH\'a sarılan ve dinlerini sadece ALLAH\'a ait kılanlar başka... Bunlar, inananlarla birliktedir. ALLAH inananlara daha sonra büyük bir ödül verecek.'),
(640, 4, 147, 'Sizler inanır ve şükrederseniz ALLAH sizi neden cezalandırsın ki? ALLAH Şükredendir, Bilendir.'),
(641, 4, 148, 'ALLAH kötü dil kullanılmasını sevmez. Zulme uğrayanlar başka. ALLAH İşitendir, Bilendir.'),
(642, 4, 149, 'Bir iyiliği açığa vurur veya gizlerseniz, yahut bir kötülüğü affederseniz, ALLAH Affedendir, Güçlüdür.'),
(643, 4, 150, 'ALLAH\'a ve elçisine karşı çıkanlar, ALLAH ile elçilerinin arasını ayırmak isteyenler, "Bir kısmına inanıp bir kısmını inkar ederiz," diyenler ve bu ikisi arasında bir yol edinmek isteyenler...'),
(644, 4, 151, 'İşte bunlar gerçek inkarcılardır. İnkarcılar için acıklı bir azap hazırlıyoruz'),
(645, 4, 152, 'ALLAH\'a ve elçilerine inanan ve onların hiçbiri arasında ayırım yapmayanların da ödüllerini ileride verecek. ALLAH Bağışlayandır, Rahimdir.'),
(646, 4, 153, 'Kitap halkı, senin kendilerine gökten bir kitap indirmeni istiyor. Musa\'dan bunun daha büyüğünü istemiş ve, "Bize ALLAH\'ı fiziksel olarak göster," demişlerdi. Böyle sınırı aşmalarından ötürü onları yıldırım çarptı. Kendilerine apaçık deliller gelmesine rağmen buzağıya taptılar. Onları yine affettik. Musa\'ya da apaçık bir yetki verdik.'),
(647, 4, 154, 'Kendilerinden söz alırken Sina Dağını üzerlerine kaldırdık. Onlara, "Kapıdan alçak gönüllü olarak girin," dedik. Onlara, "Cumartesi yasağını çiğnemeyin," dedik; onlardan sağlam bir söz aldık.'),
(648, 4, 155, 'Sözlerini bozmalarından, ALLAH\'ın ayet ve delillerini inkar etmelerinden, peygamberleri haksız yere öldürmelerinden ve "Sabit fikirliyiz," demelerinden ötürü, evet inkarlarından ötürü ALLAH onların anlayışlarını mühürledi. Bunun için pek azı inanırlar.'),
(649, 4, 156, 'İnkarlarından ve Meryem hakkında büyük bir yalan uydurmalarından ötürü...'),
(650, 4, 157, 'Ve ALLAH\'ın elçisi "Meryem oğlu İsa Mesih\'i öldürdük," demelerinden ötürü... Oysa onu öldürmediler ve onu asmadılar; fakat öyle yaptıklarını sandılar. Tüm mezhepler bu konuda kuşku içindedir. Onların bu konuda bir bilgisi yok; sadece zanna uyuyorlar. Kesin olarak onu öldürmediler.'),
(651, 4, 158, 'Tersine, ALLAH onu kendisine yükseltti; ALLAH Üstündür, Bilgedir'),
(652, 4, 159, 'Kendilerine kitap verilenlerden her biri ölümünden önce ona inanmak zorunda idi. Diriliş Günü ise o onlara karşı tanık olacaktır'),
(653, 4, 160, 'Yahudilerin işledikleri zulümlerden ve bir çok kişiyi ALLAH yolundan çevirmelerinden dolayı kendilerine daha önce helal kılınmış temiz nimetleri yasakladık'),
(654, 4, 161, 'Menedildikleri halde tefecilik yapmalarından ve halkın parasını haksızlıkla yemelerinden ötürü... Onların inkarcılarına acıklı bir azap hazırladık.'),
(655, 4, 162, 'Ancak aralarındaki derin ilim sahipleri ve inananlar, sana indirilene ve senden önce indirilen(ler)e inanır. Namazı gözetir, zekatı verir, ALLAH\'a ve ahiret gününe inanırlar; bunlara büyük bir ödül vereceğiz.'),
(656, 4, 163, 'Nuh\'a ve ondan sonra gelen peygamberlere vahyettiğimiz gibi sana da vahyettik. İbrahim\'e, İsmail\'e, İshak\'a, Yakub\'a ve soyuna, İsa\'ya, Eyyub\'a, Yunus\'a, Harun\'a, ve Süleyman\'a da vahyetmiş, Davud\'a da Zebur vermiştik.'),
(657, 4, 164, 'Sana önceden anlattığımız elçilere ve anlatmadığımız elçilere de... Ve ALLAH Musa ile de kelimelerle konuşmuştu.'),
(658, 4, 165, 'Müjdeler ve uyarılar getiren elçiler... Ki tüm bu elçilerden sonra insanların ALLAH\'a karşı bir bahaneleri kalmasın. ALLAH Üstündür, Bilgedir.'),
(659, 4, 166, 'Fakat ALLAH, sana indirdiğine tanıklık eder. Onu kendi bilgisiyle indirdi. Melekler de buna tanıklık eder. Tanık olarak ALLAH yeter.'),
(660, 4, 167, 'İnkar edenler ve ALLAH\'ın yolundan alıkoyanlar tamamıyla sapıtmışlardır'),
(661, 4, 168, 'İnkar edip zulmedenleri ALLAH ne bağışlayacak, ne de onlara bir yol gösterecektir.'),
(662, 4, 169, 'Yalnız cehennemin yolu hariç. Orada sürekli kalacaklar. Bu, ALLAH\'a pek kolaydır.'),
(663, 4, 170, 'İnsanlar! Elçi Rabbinizden gerçeği getirmiştir. Kendi yararınız için inanmalısınız. İnkar ederseniz, bilin ki göklerde ve yerde ne varsa ALLAH\'ındır. ALLAH Bilendir, Bilgedir.'),
(664, 4, 171, 'Kitaplılar! Dininizde sınırı aşmayın. ALLAH hakkında yalnız gerçeği söyleyin. Meryem oğlu İsa Mesih, sadece ALLAH\'ın elçisi ve Meryem\'e gönderdiği kelimesi ve O\'ndan gelen bir candır. ALLAH\'a ve elçilerine inanın. "Üçtür," demeyin. Kendi yararınız için buna son verin. Biricik tanrı yalnız ALLAH\'tır. O, çocuk sahibi olmaktan yücedir. Göklerde ve yerde ne varsa O\'nundur. Vekil olarak ALLAH yeter.'),
(665, 4, 172, 'Ne Mesih, ne de yakın melekler ALLAH\'ın kulu olmayı küçüklük saymaz. Kim O\'na kulluğu küçük görür de büyüklük taslarsa, bilsin ki O, onların hepsini huzuruna toplayacak.'),
(666, 4, 173, 'İnanıp erdemli davrananların ödüllerini eksiksiz ödeyecek ve lütfundan onlara daha fazla verecektir. Küçümseyip kibirlenenleri ise acıklı bir azapla cezalandıracak. Kendilerine ALLAH\'tan başka ne bir sahip ve ne de bir yardımcı bulamıyacaklardır'),
(667, 4, 174, 'İnsanlar! Rabbinizden size bir kanıt geldi ve üzerinize apaçık bir ışık indirdik.'),
(668, 4, 175, 'ALLAH\'a inanıp O\'na sarılanları ise kendi rahmetine ve lütfuna kabul edecek, onları dosdoğru bir yola sokarak kendisine yöneltecektir.'),
(669, 4, 176, 'Sana danışıyorlar. De ki: "ALLAH size eşsiz ve çocuksuz olan kişinin mirası hakkında şu hükmü açıklıyor: Ölen erkeğin çocuğu olmayıp bir kız kardeşi varsa, bıraktığı mirasın yarısı kız kardeşinindir. Fakat, (ölen kişi) çocuğu olmayan kız kardeşse, erkek kız kardeşinin mirasının tamamını alır. Varisler iki kız kardeş ise, bırakılanın üçte ikisi onlarındır. Kardeşler, erkeklerden ve kadınlardan oluşuyorsa, erkeğe kadının iki katı kadar pay verilir." Şaşırmamanız için ALLAH size böylece açıklıyor. ALLAH her şeyi Bilendir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'İnananlar, anlaşmalarınızı uygulayın. Size okunacak olanların dışındaki hayvanlar size helal kılındı. Yalnız ihramda iken avlanmayı helal saymamak koşuluyla... ALLAH dilediği hükmü verir.'),
(671, 5, 2, 'İnananlar! Ne ALLAH\'ın koyduğu dinsel törenlere, ne kutsal aya, ne kurbana, ne onları işaretleyen çelenklere ve ne de Rab\'lerinin lütuf ve rızasını aramak için Kutsal Ev\'e doğru yola çıkanlara saygısızlık etmeyin. İhramdan çıktığınız zaman avlanabilirsiniz. Sizi Kutsal Mescid\'den çeviren bir topluluğa olan kininizden dolayı provakasyona gelip saldırganlaşmayın. İyilik ve erdemlilikte yardımlaşın. Kötülük ve düşmanlıkta yardımlaşmayın. ALLAH\'ı dinleyin. ALLAH\'ın cezası çetindir.'),
(672, 5, 3, 'Leş, kan, domuzun eti ve ALLAH\'tan başkasına adananlar size haram kılındı. Canları çıkmadan kesmeniz hariç, boğulmuş, vurulmuş, düşmüş, boynuzlanmış ve canavar tarafından yenmiş hayvanlar, kutsallaştırılmış taşlar üzerinde boğazlanarak fal oklarıyladağıtılanlar, evet bunlar kötüdür. Bugün size dininizi olgunlaştırdım, size nimetimi tamamladım ve size din olarak islam\'ı beğendim. Kim açlıktan dolayı zorda kalırsa, günaha istekle yönelmeden yerse ona günah yoktur. Çünkü ALLAH Bağışlayandır, Rahimdir.'),
(673, 5, 4, 'Kendilerine neyin helal olduğunu sana soruyorlar. De ki, "Size temiz yiyecekler helal edilmiştir. ALLAH\'ın size öğrettiğinden öğreterek yetiştirdiğiniz köpek ve şahin gibi avcı hayvanların sizin için yakaladıklarını da yiyin ve üzerlerinde ALLAH\'ın ismini anın." ALLAH\'ı dinleyin. ALLAH hesabı çabuk görür.'),
(674, 5, 5, 'Bugün size iyi ve temiz şeyler helal kılındı. Kitap halkının yiyeceği size helaldir. Sizin de yiyecekleriniz onlara helaldir. Mehirlerini ödemeniz, zina etmeyip namuslu davranmanız ve gizli dost tutmamanız koşuluyla, inananlardan iffetli kadınlarla ve sizden önce kendilerine kitap verilenlerden iffetli kadınlarla evlenmeniz size helaldir. Kim inanmayı kabul etmezse tüm yaptıkları boşa çıkmıştır ve o, ahirette de kaybedenlerdendir.'),
(675, 5, 6, 'İnananlar! Namaza kalktığınız zaman: (1) Yüzünüzü yıkayın, (2) ellerinizi dirseklere kadar yıkayın, (3) başınızı sıvazlayın, (4) ve ayaklarınızı da topuklara kadar (sıvazlayın/yıkayın). Cinsel ilişkide bulunmuşsanız yıkanınız. Hasta veya yolcu iseniz, yahut tuvaletten gelmiş, yahut kadınlarla cinsel ilişkide bulunmuş ve su bulamamışsanız, temiz bir toprağa yönelip yüzünüzü ve kollarınızı onunla sıvazlayın. ALLAH size güçlük çıkarmak istemez. Ancak sizi temizlemek ve üzerinizdeki nimetini tamamlamak istiyor. Olur ki şükredersiniz.'),
(676, 5, 7, 'ALLAH\'ın üzerinizdeki nimetini ve O\'nunla yaptığınız sözleşmeyi hatırlayın: "İşittik ve itaat ettik," demiştiniz. ALLAH\'ı dinleyin; ALLAH içinizde olanları biliyor.'),
(677, 5, 8, 'İnananlar! ALLAH için adaleti gözeterek tanıklık edin. Bir topluluğa olan kininiz sizi adaletli davranmaktan alıkoymasın. Adaletli davranmak daha erdemlidir. ALLAH\'ı dinleyin. ALLAH yaptıklarınızı haber alır.'),
(678, 5, 9, 'ALLAH, inanıp iyi işler yapanlara bir bağışlanma ve büyük bir ödülü söz verdi.'),
(679, 5, 10, 'İnkar edip ayetlerimizi yalanlayanlara gelince, onlar cehennem halkıdır.'),
(680, 5, 11, 'İnananlar! ALLAH\'ın size olan nimetini hatırlayın: Hani bir topluluk size el uzatmağa yeltenmişti de onların ellerini sizden çekmişti. ALLAH\'ı dinleyin. İnananlar ALLAH\'a güvensin.'),
(681, 5, 12, 'ALLAH, İsrail oğullarından söz almıştı ve içlerinden on iki başkan göndermiştik. ALLAH demişti ki: "Namazı gözetirseniz, zekatı verirseniz, elçilerime inanıp onlara saygılı olursanız ve (erdemli bir yaşam sürmek suretiyle) ALLAH\'a güzel bir borç verirseniz sizinle beraberim. Günahlarınızı örter, içlerinden ırmaklar akan bahçelerde ağırlarım. Artık sizden kim bundan sonra inkar ederse doğru yolu sapıtmış olur."'),
(682, 5, 13, 'Sözlerini bozdukları için onları lanetledik, kalplerini katılaştırdık. Sözlerin anlamını bağlamından kaydırırlar. Uyarıldıkları şeylerin bir kısmını unuttular. Onların çoğundan sürekli ihanet göreceksin. Onları affet ve aldırma. ALLAH güzel davrananları sever.'),
(683, 5, 14, '"Biz Hristiyanız," diyenlerden de söz almıştık. Ancak onlar da uyarıldıkları şeylerin bir kısmını unuttu. Bu yüzden diriliş gününe kadar aralarına düşmanlık ve kin saldık. Yaptıkları her şeyi ALLAH onlara bildirecek'),
(684, 5, 15, 'Kitap halkı, kitabın gizlediğiniz bir çok bölümünü açığa çıkaran ve bir çoğunu da yüzünüze vurmayan elçimiz geldi size. ALLAH\'tan bir ışık ve apaçık bir kitap da geldi size.'),
(685, 5, 16, 'Onunla ALLAH, rızasını gözetenleri barış ve huzur yollarına ulaştırır, izniyle onları karanlıklardan aydınlığa çıkararak onları dosdoğru yola iletir.'),
(686, 5, 17, '"ALLAH Meryem oğlu Mesih\'tir," diyenler elbette kafir olmuşlardır. De ki: "ALLAH Meryem oğlu Mesih\'i, annesini ve hatta yeryüzündekilerin tümünü helak etmek isterse, kim buna engel olabilir?" Göklerin, yerin ve arasındakilerin egemenliği ALLAH\'a aittir. Dilediğini yaratır. ALLAH herşeye Gücü Yetendir.'),
(687, 5, 18, 'Yahudiler ve Hristiyanlar, "Biz ALLAH\'ın çocukları ve sevgilileriyiz," dediler. "Öyleyse günahlarınızdan ötürü neden sizi cezalandırıyor? Siz sadece O\'nun yarattığı insanlardansınız," de. Dileyeni/dilediğini de bağışlar, dileyeni/dilediğini de cezalandırır. Göklerin, yerin ve bunlar arasındakilerin egemenliği ALLAH\'a ait olup dönüş de O\'nadır'),
(688, 5, 19, 'Kitap halkı! Elçiler arasındaki bir boşluk döneminden sonra size elçimiz gelmiş bulunuyor ve size gerçekleri anlatıyor ki "Bize bir müjdeleyici ve uyarıcı gelmedi," demeyesiniz. ALLAH her şeye Gücü Yetendir.'),
(689, 5, 20, 'Musa, halkına şöyle demişti: "Ey halkım, ALLAH\'ın üzerinizdeki nimetini hatırlayın: aranızdan peygamberler çıkardı, sizi özgür kimseler kıldı ve toplumların hiçbirine vermediğini size verdi."'),
(690, 5, 21, '"Halkım! ALLAH\'ın size ayırdığı kutsal toprağa girin. Geri dönmeyin, yoksa kaybedersiniz."'),
(691, 5, 22, 'Dediler ki: "Musa, orada zorba bir topluluk var. Onlar oradan çıkmadıkça biz asla oraya girmeyiz. Çıkarlarsa gireriz."'),
(692, 5, 23, 'Korku duyanların arasında, ALLAH\'ın kendisine nimet verdiği iki kişi, "Üstlerine kapıdan yürüyün. Kapıdan girerseniz kesinlikle siz yeneceksiniz. İnanıyorsanız ALLAH\'a güvenin," dedi.'),
(693, 5, 24, '"Musa, onlar orada oldukça biz oraya asla girmeyiz. Sen ve Rabbin gidip savaşın; biz burda oturuyoruz," dediler.'),
(694, 5, 25, 'O da bunun üzerine: "Rabbim, ben ancak kendime ve kardeşime söz geçirebiliyorum. Bizimle yoldan çıkmış bu topluluğun arasını ayır," dedi.'),
(695, 5, 26, 'Orası onlara kırk yıl boyunca yasaklanmıştır; yeryüzünde şaşkın şaşkın dolaşacaklar. Yoldan çıkmış bir topluluk için üzme kendini.'),
(696, 5, 27, 'Onlara Adem\'in iki oğlunun olayını doğru anlat. Birer kurban adamışlardı da, birisinden kabul edilmiş, diğerinden edilmemişti. "Seni öldüreceğim!," dedi. "ALLAH ancak erdemli olanlardan kabul eder," dedi.'),
(697, 5, 28, '"Beni öldürmek için elini bana uzatırsan, seni öldürmek için ben elimi sana uzatmayacağım. Ben, tüm yaratıkların sahibi ALLAH\'tan korkarım. "'),
(698, 5, 29, '"Günahımı günahınla birlikte yüklenerek cehenneme girmeni isterim. Zalimler böyle cezalandırılır."'),
(699, 5, 30, 'Egosu onu, kardeşini öldürmeye kışkırttı. Onu öldürdü ve böylece kaybetti.'),
(700, 5, 31, 'ALLAH kardeşinin cesedini nasıl gömeceğini göstermek için yeri eşeleyen bir karga gönderdi. "Yazık bana; kardeşimin cesedini gömme konusunda bu karga kadar bile olamadım," diyerek pişman oldu.'),
(701, 5, 32, 'Bunun için İsrail oğullarına şunu yazdık: Kim, cinayet işlememiş veya yeryüzünde bozgunculuk yapmamış bir kişiyi öldürürse tüm insanları öldürmüş gibidir. Kim de o canı yaşatırsa, bütün insanları yaşatmış gibi olur. Elçilerimiz onlara apaçık delillerle geldiler. Buna rağmen onların çoğu hemen sonra yeryüzünde azgınlık yapmaya başladılar.'),
(702, 5, 33, 'ALLAH ve elçisi ile savaşan ve yeryüzünde bozgunculuk için uğraşanların cezası: Öldürülmeleri veya asılmaları, veya el ve ayaklarının çaprazlama kesilmesi, veya yerlerinden sürülmeleridir. Bu, dünyada görecekleri bir aşağılanma. Ahirette ise büyük bir ceza var.'),
(703, 5, 34, 'Kendilerini yakalamadan önce tevbe edenler başka. Bilesiniz ki ALLAH Bağışlayandır, Rahimdir.'),
(704, 5, 35, 'İnananlar ALLAH\'ı dinleyin, O\'na ulaşmak için yol arayın ve O\'nun yolunda cihad edin ki başarasınız.'),
(705, 5, 36, 'İnkarcılar, yeryüzündekilerin hepsine ve bir o kadarına da sahip olsalardı ve onları diriliş gününün azabından kurtulmak için fidye verselerdi kendilerinden kabul edilmezdi. Onlara acıklı bir azap var.'),
(706, 5, 37, 'Ateşten çıkmak isterler; ama çıkamazlar. Onlar sürekli bir cezaya mahkum olmuşlardır.'),
(707, 5, 38, 'Erkek hırsızın ve kadın hırsızın ellerini, yaptıklarına karşılık kesin. Bu ALLAH\'ın öngördüğü bir caydırma yöntemidir. ALLAH Güçlüdür, Bilgedir'),
(708, 5, 39, 'Kim yaptığı bu haksızlıktan sonra tevbe ederek düzelirse, ALLAH yönelişini kabul eder. ALLAH kuşkusuz Bağışlayandır, Rahimdir.'),
(709, 5, 40, 'Göklerin ve yerin egemenliğinin ALLAH\'a ait olduğunu bilmez misin? Dilediğine azap eder, dilediğini bağışlar. ALLAH her şeye Gücü Yetendir.'),
(710, 5, 41, 'Ağızlarıyla "İnandık," dedikleri halde kalpleriyle inanmıyanların inkarcılıktaki gayretleri seni üzmesin. Yahudilerin bir grubu var ki yalana kulak veriyor, seninle hiç karşılaşmamış bir topluluğu dinliyor. Kelimelerin anlamını kaydırıp. "Size bu verilirse alın, bu verilmezse sakının," diyorlar. ALLAH birini şaşırtmak isterse ALLAH\'a karşı kimse ona yardım edemez. İşte onlar, ALLAH\'ın kalplerini temizlemeyi dilemediği kişiler. Onlar için dünyada aşağılanma, ve ahirette de büyük bir azap var.'),
(711, 5, 42, 'Yalana kulak veriyor, yasadışı yoldan yiyorlar. Sana gelirlerse, ister aralarında hüküm ver, istersen yüz çevir. Onlardan yüz çevirdiğin taktirde sana hiç bir zarar veremezler. Hüküm verirsen, aralarında adaletle hüküm ver. ALLAH adaletli olanları sever.'),
(712, 5, 43, 'İçinde ALLAH\'ın hükmü bulunan Tevrat yanlarında dururken nasıl olur da ondan yüz çevirip de seni hakem yapıyorlar? Onlar aslında inanmıyor.'),
(713, 5, 44, 'İçinde hidayet ve ışık bulunan Tevrat\'ı biz indirdik. müslüman peygamberler onunla Yahudiler arasında hüküm veriyorlardı. Hahamlar ve din bilginleri de ALLAH\'ın kitabından emredildikleri şeylerle hüküm verirler ve onun üzerine tanık olurlardı. Halkıululamayın, beni ululayın ve ayetlerimi ucuz bir fiyata satmayın. ALLAH\'ın indirdiği ile hüküm vermeyenler inkarcıdır.'),
(714, 5, 45, 'Orada onlara: Cana can, göze göz, buruna burun, kulağa kulak, dişe diş ve yaralara ödeşmeyi emrettik. Kim bu hakkından vazgeçerse günahlarını örter. ALLAH\'ın indirdiği ile hüküm vermeyenler zalimdir.'),
(715, 5, 46, 'Onların ardından, önceki Tevrat\'ı doğrulayıcı olarak Meryem oğlu İsa\'yı gönderdik. Ona, içinde hidayet ve ışık bulunan, önceki Tevratı doğrulayan ve erdemliler için bir kılavuz ve öğüt olan İncil\'i verdik.'),
(716, 5, 47, 'İncil halkı ALLAH\'ın onda indirdiğiyle hüküm versin. Kim ALLAH\'ın indirdiği ile hüküm vermezse işte onlar yoldan çıkanlardır.'),
(717, 5, 48, 'Kendinden önceki kitapları doğrulayan, onların yerine geçen bu kitabı, gerçekleri kapsayıcı olarak sana indirdik. ALLAH\'ın sana indirdiğiyle aralarında hüküm ver. Sana gelen gerçekleri bırakıp onların hevesine uyma. Her biriniz için bir yasa ve yöntem belirledik. ALLAH dileseydi hepinizi bir tek toplum yapardı. Ancak, size verdikleriyle sizleri sınıyor. İyilikte yarışın. Hepinizin dönüşü ALLAH\'adır. Ayrılığa düştüğünüz konuları size bildirecek.'),
(718, 5, 49, 'Aralarında ALLAH\'ın indirdiği ile hüküm vermelisin. Onların keyfine uyma. ALLAH\'ın sana indirdiklerinin bir kısmından sakın seni şaşırtmasınlar. Yüz çevirirlerse, demek ki ALLAH bazı günahları yüzünden onları cezalandırmak istiyor. Gerçekten insanların çoğu yoldan çıkmıştır.'),
(719, 5, 50, 'Yoksa cahillik dönemindeki yasaları mı arıyorlar? Bilgiye dayanan kesin inanç sahipleri için ALLAH\'tan daha güzel yasa koyucu olabilir mi?'),
(720, 5, 51, 'İnananlar, Yahudileri ve Hristiyanları dost edinmeyin. Onlar birbirlerinin dostudur. Sizden kim onlarla dost olursa onlardan sayılır. ALLAH zalim toplumu doğru yola iletmez.'),
(721, 5, 52, 'Kalplerinde hastalık bulunanların, "Başımıza bir bela gelmesinden korkuyoruz," diyerek onların arasında dolanıp durduklarını göreceksin. Olur ki ALLAH, zaferi veya kendi tarafından bir emri getirir de, içlerinde gizledikleri düşüncelerinden dolayı pişman olurlar.'),
(722, 5, 53, 'O zaman inananlar, "Sizinle beraber olduklarına bütün güçleriyle ALLAH adına yemin edenler bunlar mı," diyecek. Çabaları boşa çıkmış ve zarar etmişlerdir.'),
(723, 5, 54, 'İnananlar, kim dininden dönerse şunu bilsin: ALLAH sevdiği bir toplumu getirir; onlar da O\'nu sever. İnananlara karşı alçak gönüllü, kafirlere karşı güçlü ve pektirler. ALLAH yolunda cihad ederler, kınayanın kınamasından çekinmezler. Bu, ALLAH\'ın lütfudur; onu dilediğine verir. ALLAH Cömerttir, Bilendir.'),
(724, 5, 55, 'Gerçek dostlarınız, ALLAH, elçisi ve namazı gözetip alçak gönüllü olarak zekatı veren müminlerdir.'),
(725, 5, 56, 'Kim ALLAH\'ı, elçisini ve inananları dost edinirse bilsin ki ALLAH\'ın partisi üstün gelecek.'),
(726, 5, 57, 'İnananlar, sizden önceki kitap halkı ve inkarcılar arasında dininizi alay ve eğlence konusu yapanları dost edinmeyin. İnanmış iseniz ALLAH\'ı dinlemelisiniz.'),
(727, 5, 58, 'Namaza çağırdığınızda onunla alay edip eğlendiler. Düşünmeyen bir topluluktur onlar.'),
(728, 5, 59, 'De: "Kitaplılar! ALLAH\'a, bize indirilene ve önceki indirilenlere inandık diye mi yoldan çıkmış olan çoğunluğunuzla bize karşı nefret besliyorsunuz?"'),
(729, 5, 60, 'De: "ALLAH katında bundan daha kötü bir durumu size bildireyim mi? ALLAH kime lanet ve gazap ederek maymunlar, domuzlar, putperestler haline sokmuşsa, işte o kimseler yer bakımından daha kötü ve doğru yoldan en çok sapmış olanlardır.'),
(730, 5, 61, 'Size geldiklerinde, "İnandık," dediler. Oysa yanınıza inkarlarıyla girip inkarlarıyla çıkmışlardı. Onların gizlediklerini ALLAH daha iyi biliyor.'),
(731, 5, 62, 'Onların çoğunu günahta, düşmanlıkta ve haram yemekte koşuşurken görürsün. Yaptıkları ne kötü!'),
(732, 5, 63, 'Hahamları ve din adamları onları bu günah sözleri söylemekten, haram yemekten menetmeli değil miydi? İşledikleri ne fena!'),
(733, 5, 64, 'Yahudiler "ALLAH\'ın eli bağlı," dedi. Aslında elleri bağlı olanlar kendileri. Halbuki O\'nun iki eli de açık olup dilediği gibi harcamakta. Rabbinden sana indirilenler çoğunun azgınlığını ve inkarını arttıracak. Diriliş Gününe kadar aralarına düşmanlık ve kin saldık. Her ne zaman savaş için ateş yaksalar ALLAH onu söndürmüştür. Yeryüzünde bozgunculuk yapmak için dolaşırlar. ALLAH ise bozguncuları sevmez.'),
(734, 5, 65, 'Kitap halkı inanıp erdemli davransa günahlarını örterek onları bol nimetli bahçelere yerleştirirdik.'),
(735, 5, 66, 'Tevrat\'ı, İncil\'i ve kendilerine indirilenleri uygulasalardı onları baştan ayağa nimetlere boğardık. İçlerinde ılımlı bir toplum varsa da çoğu kötülük işlemekte.'),
(736, 5, 67, 'Elçi, Rabbinden sana indirileni duyur. Bunu yapmazsan O\'nun elçiliğini duyurmamış olursun. ALLAH seni halktan koruyacak. ALLAH inkarcıları doğru yola iletmez.'),
(737, 5, 68, 'De, "Kitaplılar! Tevrat\'ı, İncil\'i ve Rabbinizden size indirileni uygulamadıkça hiç bir dayanağınız olmaz." Rabbinden sana indirilenler, onların çoğunun azgınlık ve inkarını arttırır. İnkarcı toplum için kendini üzme.'),
(738, 5, 69, 'İnananlar, Yahudiler, diğer dinlerden olanlar ve Hıristiyanlardan kim () ALLAH\'a ve () ahiret gününe inanır ve () erdemli bir yaşam sürerse onlar için bir korku yoktur ve onlar üzülmeyecekler de...'),
(739, 5, 70, 'İsrail oğullarından söz almış, onlara elçiler göndermiştik. Her ne zaman hoşlarına gitmeyen bir şeyle onlara bir elçi gittiyse bir kısmını yalanlayıp bir kısmını da öldürdüler.'),
(740, 5, 71, 'Test edilmeyeceklerini sandılar, körleşip sağırlaştılar. Sonra ALLAH yönelişlerini kabul etti; fakat buna rağmen çoğu yine körleşip sağırlaştı. ALLAH Yaptıklarını görmekte.'),
(741, 5, 72, '"ALLAH, Meryem oğlu Mesih\'tir," diyenler elbette inkar etmiştir. Oysa Mesih, "İsrail oğulları! benim de sizin de Rabbiniz olan ALLAH\'a kul olun," demişti. Kim ALLAH\'a ortak koşarsa ALLAH ona cenneti yasaklar, yeri de ateş olur. Zalimler için yardımcıda bulunmaz.'),
(742, 5, 73, '"ALLAH, üçün üçüncüsüdür," diyenler inkar etmiştir. Oysa bir tek Tanrı\'dan başka tanrı yok. Bu sözlerine son vermezlerse, inkarcılarına acıklı bir azap dokunacak.'),
(743, 5, 74, 'Hâlâ ALLAH\'a yönelip ondan bağışlanma dilemiyecekler mi? ALLAH Bağışlayandır, Rahimdir.'),
(744, 5, 75, 'Meryem oğlu Mesih elçiden başka bir şey değildi. Ondan önce de nice elçiler gelip geçmişti. Annesi de doğru sözlüydü. Her ikisi de yemek yerdi. Ayetlerimizi onlara nasıl açıkladığımıza dikkat et de buna rağmen nasıl da yüz çevirmekte olduklarını gör!'),
(745, 5, 76, 'De, "ALLAH\'tan başka, size zarar ve yarar veremiyenlere mi tapıyorsunuz? ALLAH İşitir, Bilir."'),
(746, 5, 77, 'De, "Kitaplılar, dininiz konusunda gerçeğin ötesine gitmeyin. Daha önce sapan, çok kimseyi de saptıran, böylece doğru yolu kaybeden bir topluluğun heveslerini izlemeyin.'),
(747, 5, 78, 'İsrail oğullarının inkarcıları, hem Davud\'un hem Meryem oğlu İsa\'nın diliyle lanetlenmişti. Zira onlar isyan edip sınır tanımıyorlardı.'),
(748, 5, 79, 'Yaptıkları kötülükler konusunda birbirlerini uyarmıyorlardı. Yaptıkları ne kötü idi.'),
(749, 5, 80, 'Çoklarının inkarcılarla dost olduğunu görürsün. Nefislerinin kendileri için gönderdiği şey ne kötü. ALLAH onlara gazap etmiştir; azapta sürekli kalacaklar.'),
(750, 5, 81, 'ALLAH\'a, peygambere ve ona indirilene inansalardı, onları dost edinmezlerdi. Ne var ki, çoğu yoldan çıkmış bulunuyor'),
(751, 5, 82, 'İnsanların arasında inananların en azılı düşmanı olarak Yahudileri ve müşrikleri bulacaksın. İnananlara sevgice en yakınları da "Biz Hristiyanız," diyenleri bulursun. Çünkü onlar arasında büyüklük taslamayan papazlar ve rahipler var.'),
(752, 5, 83, 'Elçiye inenleri işittiklerinde, gerçeği tanımalarından ötürü gözlerinin yaşla dolup taştığını görürsün. Derler ki, "Rabbimiz, inandık, bizi tanıklardan say.'),
(753, 5, 84, '"Rabbimizin bizi iyiler arasına katmasını umduğumuz halde neden ALLAH\'a ve bize ulaşan gerçeklere inanmıyalım?"'),
(754, 5, 85, 'Bu sözlerinden ötürü ALLAH onlara, içlerinde ırmaklar akan ve sürekli kalacakları cennetler verdi. İyi davrananların karşılığı budur.'),
(755, 5, 86, 'İnkar edip ayet ve mucizelerimizi yalanlayanlar ise cehennem halkı'),
(756, 5, 87, 'İnananlar, ALLAH\'ın size helal yaptığı iyi şeyleri haram etmeyin. Sınırı aşmayın. ALLAH sınırı aşanları sevmez'),
(757, 5, 88, 'ALLAH\'ın size rızık olarak verdiklerini, helal ve temiz olarak yeyin. İnandığınız ALLAH\'ı dinleyin.'),
(758, 5, 89, 'ALLAH rastgele ettiğiniz yeminlerden sizi sorumlu tutmaz. Ancak bile bile ettiklerinizden sizi sorumlu tutar. Yemininizi bozarsanız cezası, ailenize genellikle yedirdiğiniz yemeklerden on yoksulu doyurmak veya giydirmek veya bir köleyi salmaktır. Kim bulamazsa üç gün oruç tutmalı. Bu, bile bile ettiğiniz yeminlerinizin cezası. Yeminlerinizi tutun. ALLAH güzel karşılık veresiniz diye ayetlerini böyle açıklıyor.'),
(759, 5, 90, 'İnananlar, sarhoş edici maddeler, kumar, kutsal taş ve türbeler, şans oyunları şeytan işi birer pisliktir. Bunlardan sakının ki kurtulasınız.'),
(760, 5, 91, 'Şeytan, sarhoş edicilerle, kumarla aranıza düşmanlık ve kin sokmak, sizi ALLAH\'ı anmaktan, namazdan alıkoymak ister. Artık vazgeçiyorsunuz değil mi?'),
(761, 5, 92, 'ALLAH\'a uyun, elçiye uyun, dikkatli olun. Yüz çevirirseniz bilesiniz ki elçimize düşen görev, açıkça bildirmektir.'),
(762, 5, 93, 'İnanıp erdemli işler yapanlar, emirlere uyarak inanıp erdemli davrandıkları, günahlardan sakınıp inandıkları ve yine sakınıp iyilik yaptıkları sürece yediklerinden ötürü kendilerine bir günah yoktur. ALLAH iyi davrananları sever.'),
(763, 5, 94, 'İnananlar, ellerinizin ve mızraklarınızın ulaşacağı avlarla ALLAH sizi sınayacak ki yalnızken de kendisinden korkanları ALLAH ayırsın. Kim bundan sonra sınırı aşarsa onun için acıklı bir azap var.'),
(764, 5, 95, 'İnananlar, ihramdayken (Haj için kutsal bölgedeyken) av hayvanı öldürmeyin. Sizden kim kasden onu öldürürse cezası, aranızdan iki adaletli kişinin kararlaştıracağı ona denk bir evcil hayvanı kurban olarak Kabeye göndermesidir. Yahut ceza olarak, yoksulları doyurması ya da buna denk oruç tutması gerekir. Böylece yaptığının vebalini tatmış olsun. Geçmiştekileri ALLAH affetti. Kim bunu tekrarlarsa ALLAH ondan öc alır. ALLAH Üstündür, Öc alandır.'),
(765, 5, 96, 'Deniz hayvanlarını avlamak ve onları yemek size helal kılındı. Size ve yolculara geçimlik olmak üzere... İhramlı bulunduğunuz sürece kara avı size yasaklandı. Huzuruna toplanacağınız ALLAH\'ı dinleyin.'),
(766, 5, 97, 'ALLAH, kutsal ev Kabeyi, kutsal ayları, adakları ve onları işaretleyen çelenkleri, insanlar için güvenlik unsuru yaptı. Bilesiniz ki ALLAH göklerde ve yerde olanları biliyor. ALLAH her şeyi Bilir.'),
(767, 5, 98, 'Bilin ki ALLAH\'ın cezası çetindir. ALLAH Bağışlayandır, Rahimdir'),
(768, 5, 99, 'Elçiye düşen görev sadece duyurmak. ALLAH ise açıkladıklarınızı da gizlediklerinizi de bilir'),
(769, 5, 100, 'De: "Kötü ile iyi bir olmaz; kötünün çokluğu ilgini çekse bile... Akıl sahipleri, başarmak istiyorsanız (azınlığa düşme pahasına da olsa) ALLAH\'ı dinleyin."'),
(770, 5, 101, 'İnananlar, açıklandığı vakit hoşunuza gitmeyecek şeyler hakkında sorular sormayın. Kuran\'ın ışığında sorarsanız size açık olurlar. ALLAH özellikle onlardan söz etmedi. ALLAH Bağışlayandır, Yumuşaktır.'),
(771, 5, 102, 'Sizden önce bir topluluk o tip soruları sordu da, o sorularından dolayı inkarcı oldular.'),
(772, 5, 103, 'Belli bir erkek ve dişi kombinezonuyla yavrulayanların, yemin sonucu salıverilenlerin, arka arkaya iki erkek doğuranların ve on kez döl veren erkek develerin haram edilişini ALLAH onaylamıyor; inkarcılar ALLAH\'a iftira ediyor. Çoğu akletmez onların.'),
(773, 5, 104, 'Kendilerine, "ALLAH\'ın indirdiğine ve elçiye gelin," denildiğinde, "Atalarımızı üzerinde bulduğumuz yol bize yeter!," derler. Ataları, bir şey bilmeyen ve doğru yolu bulamayan kimseler olsa da mı?'),
(774, 5, 105, 'İnananlar, siz kendinize bakın. Doğru yolda iseniz sapıtan kimse size zarar veremez. Hepinizin dönüşü ALLAH\'adır. Yaptıklarınızı size haber verecek.'),
(775, 5, 106, 'İnananlar, birinize ölüm yaklaşınca, vasiyet anında aranızdan iki adil şahit tanık bulunsun. Yolculuk anında size ölüm gelirse, sizden olmayan iki kişi... Kuşkulanıyorsanız, namazdan sonra tanıkları alıkoyup ALLAH adıyla: "Akraba dahi olsa tanıklığımızı hiç bir değerle değiştirmeyeceğiz, ALLAH\'ın tanıklığını gizlemiyeceğiz. Aksi taktirde, günahkarlardan oluruz," diye yemin ettirin.'),
(776, 5, 107, 'Tanıkların taraflı olduğu anlaşılırsa, önceki tanıkların haksızlığına uğrayan taraftan iki tanık onların yerine geçer ve ALLAH adına şöyle yemin ederler: "Tanıklığımız diğer ikisinin tanıklığından daha doğrudur. Biz sapmayacağız; yoksa zalim oluruz."'),
(777, 5, 108, 'Bu uygulama, tanıklığı gereği gibi yapmalarını ve yemin ettikten sonra yeminlerinin reddedilmesinden korkmalarını sağlamak içindir. ALLAH\'ı sayın ve dinleyin. ALLAH yoldan iyice çıkmış topluluğu doğru yola iletmez.'),
(778, 5, 109, 'ALLAH elçileri topladığı gün: "Size ne cevap verildi," der. "Bir bilgimiz yok. Gizemleri sen bilirsin," derler.'),
(779, 5, 110, 'ALLAH diyecek ki: Ey Meryem oğlu İsa, sana ve annene bağışladığım nimetimi hatırla. Seni Kutsal Ruh ile desteklemiştim; böylece beşikteyken de yetişkin iken de halkla konuşuyordun. Sana kitabı, bilgeliği, Tevratı ve İncili öğretmiştim. İznimle balçıktan kuş heykeli yaratıyordun ve ona üfleyince de iznimle kuş oluveriyordu. Körü ve cüzzamlıyı iznimle iyileştiriyordun. Yine benim iznimle ölüleri diriltiyordun. İsrail oğullarına apaçık kanıtlar götürmene rağmen, içlerindeki inkarcılar, "Bu apaçık bir büyüden başka bir şey değil," demişlerdi de seni onlardan kurtarmıştım.'),
(780, 5, 111, 'Havarilere (öğrencilere), "Bana ve elçime inanın," diye vahyettiğimde; "İnandık, bizim müslüman (teslim) oluşumuza tanık ol," demişlerdi.'),
(781, 5, 112, 'Havariler, "Meryem oğlu İsa, Rabbin bize gökten bir ziyafet çekebilir mi," demişlerdi. "İnanıyorsanız ALLAH\'a saygı gösterin," demişti'),
(782, 5, 113, '"İstiyoruz ki ondan yiyelim, kalbimiz yatışsın, senin bize doğru söylediğini bilelim ve ona tanık olalım."'),
(783, 5, 114, 'Meryem oğlu İsa: "Tanrımız, Rabbimiz, bize gökten bir ziyafet indir de hepimiz ve herbirimiz için bir bayram ve Sen\'den bir delil olsun. Bizi rızıklandır. Sen en iyi rızık verensin," dedi.'),
(784, 5, 115, 'ALLAH, "Onu size indireceğim," dedi, "Kim artık bundan sonra inkar ederse, onu, hiç kimseye vermediğim bir azapla cezalandıracağım."'),
(785, 5, 116, 'ALLAH şöyle diyecek: "Ey Meryem oğlu İsa, sen mi halka, \' ALLAH tan başka beni ve annemi de tanrı edinin\' dedin?" (İsa) şöyle cevap verecek: "Sen yücesin, hakkım olmayan bir şeyi söylemek bana yakışmaz. Zaten böyle bir şey söylemiş olsaydım sen bilirdin. Sen benim düşüncemi bilirsin; ancak ben senin düşünceni bilmem. Sen tüm gizemleri biliyorsun."'),
(786, 5, 117, '"Ben onlara \'Rabbim ve Rabbiniz olan ALLAH\'a kulluk edin\' diye bana emrettiğinden başkasını demedim. Aralarında bulunduğum sürece onlara tanıktım. Canımı aldıktan sonra ise sen onların üzerine gözetleyici oldun. Sen her şeye Tanıksın."'),
(787, 5, 118, '"Onları cezalandırırsan, onlar senin yaratıklarındır. Onları bağışlarsan, kuşkusuz sen Üstünsün, Bilgesin."'),
(788, 5, 119, 'ALLAH ilan edecek: "Bu, doğrulara doğruluklarının yarar sağladığı gündür." Onlar için içlerinden ırmaklar akan ve orada sürekli kalacakları cennetler (bahçeler) var. ALLAH onlardan, onlar da O\'ndan hoşnut olmuştur. Büyük başarı işte bu...'),
(789, 5, 120, 'Göklerin, yerin ve aralarındaki her şeyin egemenliği ALLAH\'a aittir. O\'nun gücü herşeye yeter.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'Övgü, gökleri ve yeri yaratan, karanlığı ve ışığı vareden ALLAH\'a yaraşır. Buna rağmen, inkarcılar Rab\'lerini başkalarıyla denk tutuyor.'),
(791, 6, 2, 'O ki sizi balçıktan yarattı ve sonra yaşam süresi belirledi. Belirlenmiş süre O\'nun katındadır. Siz ise hâlâ kuşku duyuyorsunuz'),
(792, 6, 3, 'O, göklerde ve yerde olan ALLAH. Sizin gizlinizi açığınızı bilir, kazandıklarınızı da bilir'),
(793, 6, 4, 'Onlara Rab\'lerinin ayetlerinden (delil ve mucizelerinden) hangi bir ayet gelmişse ondan yüz çevirmişlerdir.'),
(794, 6, 5, 'Kendilerine gerçek gelince onu yalanladılar. Alay ettikleri şeyin haberleri yakında kendilerine gelecek'),
(795, 6, 6, 'Kendilerinden önce nice nesilleri yok ettiğimizi görmüyorlar mı? Sizi yerleştirmediğimiz bir biçimde onları yeryüzüne yerleştirmiştik. Onları nimetlere boğmuş, ülkelerini ırmaklarla donatmıştık. Fakat günahlarından ötürü onları yok ettik ve onlardan sonra başka nesiller yetiştirdik.'),
(796, 6, 7, 'Sana kağıt üzerine yazılı bir kitap indirseydik ve elleriyle dokunsalardı bile, inkarcılar, "Bu ancak apaçık bir büyüdür," diyeceklerdi.'),
(797, 6, 8, '"Ona bir melek indirilmeli değil miydi!," diyorlar. Bir melek indirseydik, iş bitirilmiş olurdu. Onlara zaman da verilmezdi.'),
(798, 6, 9, 'Onu bir melek yapsaydık, onu bir adam biçiminde gönderir ve mevcut kuşkuları içinde bırakırdık.'),
(799, 6, 10, 'Senden önceki elçiler de eğlence konusu yapılmıştı. Ne var ki, alay edenleri eğlendikleri gerçek kuşatıverdi.'),
(800, 6, 11, 'De: "Yeryüzünü dolaşın da yalanlayıcıların sonu nasıl olmuş bir bakın."'),
(801, 6, 12, 'De: "Göklerde ve yerde olanlar kimin?" "ALLAH\'ın," de. Kendisine merhametli olmayı gerekli kıldı. Hakkında kuşku olmayan Diriliş Günü sizi toplayacak. Kişiliklerini yitirenler inanmıyanlardır.'),
(802, 6, 13, 'Geceleyinde gündüzünde barınan her şey O\'nundur. O İşitendir, Bilendir.'),
(803, 6, 14, '"Gökleri ve yeri yoktan var eden, besleyen, fakat beslenmeyen ALLAH\'tan başka sahip mi edineyim," de. "Ben, herkesten önce teslim olmakla emrolundum," de. Ortak koşmayın!'),
(804, 6, 15, '"Rabbime isyan ettiğim taktirde büyük günün azabından korkarım," de.'),
(805, 6, 16, 'O gün kimden azap çevrilirse (Tanrı) ona rahmet etmiştir. En büyük zafer budur.'),
(806, 6, 17, 'ALLAH sana bir zarar dokundurmuşsa O\'ndan başkası onu gideremez. Sana bir iyilikte bulunmuşsa O her şeye Kadirdir.'),
(807, 6, 18, 'O, kullarına tam anlamıyla egemendir. O Bilgedir, Haber Alandır.'),
(808, 6, 19, 'Sor: "Kimin tanıklığı büyüktür?" De ki: "Benimle sizin aranızda ALLAH tanıktır. Sizi ve ulaştığı herkesi uyarmak için bana bu Kuran verildi. ALLAH\'tan başka tanrı olduğuna mı tanıklık ediyorsunuz?" "Ben böyle tanıklık etmem," de ve ardından şunu da söyle: "O bir tek tanrı, ben sizin ortak koştuğunuz şeyden uzağım."'),
(809, 6, 20, 'Kendilerine kitap verdiklerimiz, çocuklarını tanır gibi bunu tanır. Kişiliklerini yitirenler inanmıyanlardır'),
(810, 6, 21, 'ALLAH\'a iftira eden veya ayetlerini inkar edenden daha zalim kim olabilir? Zalimler başarıya ulaşamaz.'),
(811, 6, 22, 'Hepsini topladığımız gün ortak koşanlara, "Nerede iddia etmekte olduğunuz ortaklarınız?"'),
(812, 6, 23, 'Onların tek savunması şu olacak: "Rabbimiz ALLAH\'a andolsun ki biz ortak koşmadık."'),
(813, 6, 24, 'Kendilerine karşı nasıl yalan söylediklerine dikkat et. Uydurdukları şeyler kendilerini nasıl da terketti.'),
(814, 6, 25, 'Onların bir kısmı seni dinler. Fakat, kalpleri üzerine anlamalarına engel olacak örtüler, kulaklarına da ağırlık koyarız. Her bir mucizeyi görseler de ona inanmazlar. Bundan ötürü sana geldiklerinde seninle tartışır ve inkarcılar, "Bu ancak bir efsanedir," der.'),
(815, 6, 26, 'Kendileri uzaklaştıkları gibi başkasını da ondan menediyorlar. Böylece farkında olmadan kendilerini mahfediyorlar.'),
(816, 6, 27, 'Ateşin başında durduruldukları vakit onların "Keşke geri döndürülseydik de Rabbimizin ayetlerini (vahiy ve mucizelerini) inkar etmeseydik ve inananlardan olsaydık," dediklerini bir görsen!'),
(817, 6, 28, 'Daha önce gizledikleri ortaya çıktığı için (bu itirafı yapıyorlar). Geri döndürülselerdi, yine menedildikleri şeye dönerlerdi. Yalan söylüyorlar.'),
(818, 6, 29, '"Bizim için sadece dünya hayatı vardır, diriltilecek değiliz," diyorlardı.'),
(819, 6, 30, 'Rablerinin huzurunda durdurulmuş halde onları bir görsen! "Bu doğru değil mi," diyecek. "Evet, Rabbimize and olsun doğru," diyecekler. "İnkar etmenizden ötürü azabı tadın," diyecek'),
(820, 6, 31, 'Kaybedenler, ALLAH ile karşılaşmayı yalanlayanlardır. O an (dünyanın sonu) kendilerini ansızın yakalayınca, günah yüklerini sırtlarında taşıyarak, "Oradaki hayatımızı boşa harcamamızdan ötürü vay halimize," diyecekler. Yüklendikleri şey ne de kötü!'),
(821, 6, 32, 'Dünya hayatı ancak bir oyun ve eğlence. Erdemliler için ahiret yurdu daha hayırlı. Düşünmez misiniz?'),
(822, 6, 33, 'Söyledikleri şeylerin seni üzdüğünü biliyoruz. O zalimler seni değil ALLAH\'ın ayetlerini reddediyor.'),
(823, 6, 34, 'Senden önceki elçileri de yalancılıkla suçladılar. Yalanlanmalara karşı direndiler. Zaferimiz ulaşıncaya dek sıkıntı çektiler. ALLAH\'ın sistemi değişmeyecek. Nitekim elçilerin tarihi sana ulaşmış bulunuyor.'),
(824, 6, 35, 'Yüz çevirmeleri sana ağır geldiyse, bilesin ki, yerin içine bir delik açmaya ya da göğe merdiven dayamaya gücün yetse de bir mucize ortaya koysan yine kâr etmez.. ALLAH dileseydi onları doğru yola toplardı. Öyleyse cahillerden olma'),
(825, 6, 36, 'Ancak dinleyenler yönelir. Ölüleri ALLAH diriltir, sonra da herşey O\'na döndürülür.'),
(826, 6, 37, '"Ona Rabbinden bir ayet (mucize) inmeli değil miydi!," diyorlar. De ki: "ALLAH istediği an bir ayet indirebilir; fakat çoğu bunu bilmez."'),
(827, 6, 38, 'Kanatlarıyla uçan kuşlar dahil yeryüzündeki tüm yaratıklar sizin gibi birer toplum. Biz kitapta hiçbir şeyi eksik bırakmadık. Sonra onlar (tüm yaratıklar) Rab\'lerinin huzuruna toplanacak.'),
(828, 6, 39, 'Ayetlerimizi yalan diyenler karanlıklar içinde bulunan birer sağır ve dilsizdirler. ALLAH kimi dilerse saptırır, kimi de dilerse doğru yola koyar.'),
(829, 6, 40, 'De: "Doğruysanız söyleyin bakalım, size ALLAH\'ın azabı, yahut Saat (dünyanın sonu) gelse, ALLAH\'tan başkasına mı yalvarırsınız?"'),
(830, 6, 41, 'Doğrusu, yalnız O\'na yalvarırsınız. Dilerse yalvardığınız konudaki probleminizi giderir, siz de ortak koştuklarınızı unutursunuz.'),
(831, 6, 42, 'Senden önceki toplumlara da elçiler gönderdik. Yalvarsınlar diye onları darlık ve sıkıntılarla sınadık.'),
(832, 6, 43, 'Hiç olmazsa başlarına musibetimiz geldiğinde yalvarsalardı! Fakat kalpleri katılaştı ve şeytan işlerini kendilerine süslü gösterdi'),
(833, 6, 44, 'Kendilerine iletilen mesajı unuttuklarında kendilerine her şeyin kapısını ardına kadar açtık. Kendilerine verilenlerle şımarınca onları ansızın yakaladık ve böylece şaşkın ve umutsuz kaldılar.'),
(834, 6, 45, 'Zulmeden topluluğun ardı böyle kesilir. Övgü, evrenlerin Rabbi ALLAH\'a yaraşır.'),
(835, 6, 46, 'De: "Bir düşünün, ALLAH sizin işitme ve görme duyunuzu alıp belleğinizi kapasa, ALLAH\'tan başka hangi tanrı onları size geri verebilir?" Ayetleri nasıl açıkladığımıza ve buna rağmen nasıl da yüz çevirdiklerine dikkat et!'),
(836, 6, 47, 'De: "ALLAH\'ın azabı size ansızın veya duyurudan sonra gelse... Zalim toplumdan başkası mı yok edilir?"'),
(837, 6, 48, 'Biz elçileri, ancak müjdeleyiciler ve uyarıcılar olarak gönderiyoruz. Kim inanıp kendini düzeltirse, onlara bir korku yok, onlar üzülmeyecekler de.'),
(838, 6, 49, 'Ayetlerimizi yalanlayanlara gelince, yoldan çıktıklarından ötürü onlara azap dokunacak.'),
(839, 6, 50, 'De: "ALLAH\'ın hazineleri benim yanımda demiyorum size. Gizlilikleri de bilmiyorum. Size, bir melek olduğumu da söylemiyorum. Sadece bana vahyedileni izliyorum." Şunu da söyle: "Kör ile gören bir mi?"'),
(840, 6, 51, 'Rab\'lerinin huzuruna çıkacaklarının heyecanıyla dolanları onunla (Kuran\'la) uyar: O\'ndan başka bir sahipleri ve şefaatçıları (aracıları) yok. Belki korunurlar.'),
(841, 6, 52, 'Sabah akşam Rab\'lerine yalvarıp sadece O\'nun hoşnutluğunu arzulayanları kovma. Onların hesabından sen sorumlu değilsin. Senin hesabından da onlar sorumlu değil. Onları kovarsan zalim olursun.'),
(842, 6, 53, 'Birbirinizle böylece sınadık ki "ALLAH\'ın aramızdan seçip nimetlendirdiği kişiler bunlar mı," desinler. ALLAH güzel karşılık verenleri daha iyi bilmiyor mu!'),
(843, 6, 54, 'Ayetlerimize inananlar sana gelince, "Size selam olsun. Rabbiniz merhametli davranmayı prensip edindi. Bunun için, her kim bilmeyerek işlediği bir kötülükten sonra tevbe ederek kendini düzeltirse, bilsin ki O Bağışlayandır, Rahimdir."'),
(844, 6, 55, 'Suçluların yolunun besbelli olması için ayetleri böyle açıklarız'),
(845, 6, 56, 'De: "ALLAH\'ın dışında çağırdıklarınıza tapmaktan menedildim." De: "Sizin keyfinize uymayacağım. Aksi taktirde sapar ve doğru yolu bulamam."'),
(846, 6, 57, 'De: "Ben Rabbimden bir delile dayanmaktayım. Siz ise onu yalanladınız. Ona meydan okuyarak istediğiniz şeyi getirmek benim elimde değil. Hüküm, ancak ve ancak ALLAH\'ın. Gerçeği anlatıyor. O, en iyi ayırandır.'),
(847, 6, 58, 'De: "Sizin acele istediğiniz şeye sahip olsaydım aramızda iş biterdi. ALLAH zalimleri iyi biliyor."'),
(848, 6, 59, 'Gizliliklerin anahtarı onun elinde. O\'ndan başkası onları bilmez. Karada ve denizde ne varsa onları bilir. Bilgisi dışında bir yaprak bile düşmez. Yerin karanlığında bir tane, yaş veya kuru hiçbir şey yok ki apaçık bir kitapta kayıtlı olmasın.'),
(849, 6, 60, 'O\'dur, geceleyin sizi öldüren, gündüzün ne işlediğinizi bilen, belli yaşam süresi dolsun diye gündüzleyin sizi dirilten... Sonra dönüşünüz O\'nadır ve yaptıklarınızı size haber verecektir.'),
(850, 6, 61, 'O, kulları üzerinde egemendir ve üzerinize koruyucu melekler gönderir. Sizden birine ölüm geldiği zaman elçilerimiz onun canını hiç vakit geçirmeden alırlar.'),
(851, 6, 62, 'Sonra gerçek Mevlaları olan ALLAH\'a döndürülürler. Bilesiniz ki yargı O\'nundur ve O, en hızlı hesap görendir.'),
(852, 6, 63, 'De ki: "Gizli ve açık olarak \'Bizi bundan kurtarsan şükredenlerden olacağız.\' diye O\'na yalvardığınızda sizi karanın ve denizin karanlıklarından kim kurtarabilir?"'),
(853, 6, 64, 'De ki: "Ondan ve bütün sıkıntılardan sizi ALLAH kurtarıyor. Buna rağmen siz yine ortak koşuyorsunuz."'),
(854, 6, 65, 'De ki: "Üstünüzden veya ayaklarınızın altından size bir azap göndermeğe, yahut sizleri mezheplere bölüp birbirinizin kötülüğünü tattırmağa O\'nun gücü yeter." Bak, anlasınlar diye ayetlerimizi nasıl da açıklıyoruz.'),
(855, 6, 66, 'Gerçek olmasına rağmen senin halkın bunu yalanladı. De ki: "Ben üzerinizde gözetici değilim."'),
(856, 6, 67, 'Her bir haberin gerçekleşeceği bir zaman vardır. İleride bilecekler.'),
(857, 6, 68, 'Ayet ve mucizelerimizle alay edenleri gördüğün vakit, başka bir söze dalıncaya kadar onlardan yüz çevir. Şeytan sana unutturursa, hatırladıktan sonra böyle zalim kimselerle birlikte oturma.'),
(858, 6, 69, 'Erdemliler böylelerin hesabından sorumlu tutulmaz. Fakat, olur ki dinlerler diye hatırlatmak iyi olur.'),
(859, 6, 70, 'Dinlerini oyun eğlenceye alanları ve dünya hayatına aldananları bırak. Sen bununla (Kuran\'la) hatırlat ki, bir kişi kazandığının felaketli sonucunu çekmesin. Onun ALLAH\'tan başka bir Sahibi ve Şefaatçisi yoktur. Her türlü fidyeyi verse bile kendisinden kabul edilmez. Bunlar, kazandıklarından dolayı felaketli sonuca uğrayanlarlardır; inkarları yüzünden kaynar sudan bir içkiyi ve acı verici bir azabı hakketmişlerdir'),
(860, 6, 71, 'De ki: "ALLAH\'tan başka, bize yarar ve zarar veremiyenlere mi yalvaralım? ALLAH bizi doğru yola ulaştırdıktan sonra ökçelerimiz üzerinde gerisin geriye mi dönelim? Şeytanların ayartıp yeryüzünde şaşırttığı ve arkadaşlarının, \'Gel, bizimle birlikte doğru yola gel\' diye çağırdıkları şaşkın bir kimse gibi mi olalım?" De ki: "Gerçek yol gösterme ALLAH\'ın yol göstermesidir. Evrenlerin Rabbine teslim olmakla emredildik."'),
(861, 6, 72, '"Namazı gözetmeli ve O\'nu sayıp dinlemelisiniz. Huzurunda toplanacağınız O\'dur."'),
(862, 6, 73, 'O\'dur gökleri ve yeri yaratan. "Ol," dediği gün hemen oluverir. Sözü mutlak doğrudur. Boruya üfürüldüğü gün egemenlik tümüyle O\'nundur. Gizliyi ve açığı Bilendir. Bilgedir, herşeyden Haberdardır.'),
(863, 6, 74, 'İbrahim, babası Azer\'e şöyle demişti: "Heykelleri mi tanrı ediniyorsun? Seni ve halkını tümüyle apaçık bir sapıklık içinde görüyorum."'),
(864, 6, 75, 'Kesin bir inanca sahip olması için, İbrahim\'e göklerin ve yerin yönetimini şöylece gösterdik:'),
(865, 6, 76, 'Üzerine gece basınca bir gezegen gördü, "Budur benim Rabbim!," dedi. Batınca, "Ben batanları sevmem," dedi.'),
(866, 6, 77, 'Ay\'ı doğarken görünce, "Budur benim Rabbim!," dedi. Batınca da, "Rabbim bana doğru yolu göstermezse sapıtanlardan olurum," dedi.'),
(867, 6, 78, 'Güneşi doğarken görünce, "Budur benim Rabbim. Bu, daha büyük!," dedi. O da batınca: "Ey halkım, ben sizin ortak koştuğunuz şeylerden uzağım," dedi.'),
(868, 6, 79, 'Ben yüzümü tümüyle, gökleri ve yeri yaratana çevirdim ve ben ortak koşanlardan değilim.'),
(869, 6, 80, 'Halkı onunla tartıştı. "Beni doğruya ulaştırmış iken benimle hâlâ ALLAH hakkında mı tartışıyorsunuz? Sizin ortak koştuklarınızdan korkmam, meğer Rabbim bir şey dilerse... Rabbim, bilgice her şeyi kuşatmıştır. Öğüt almıyor musunuz?"'),
(870, 6, 81, '"ALLAH\'ın kendileri hakkında size hiç bir delil vermediklerini O\'na ortak koşmaktan korkmuyorsunuz da ben nasıl olur da sizin ortak koştuklarınızdan korkarım? Hangi taraf güvenliği daha çok hakketmektedir? Bir bilseniz!"'),
(871, 6, 82, 'İnananlar ve imanlarını bir zulümle bulaştırmayanlar güvenliği hakketmişlerdir ve onlar doğruyu bulmuşlardır.'),
(872, 6, 83, 'Bunlar, halkına karşı kullanması için İbrahim\'e verdiğimiz tartışma yöntemidir. Dilediğimizi derecelerle yükseltiriz. Rabbin Bilgedir, Bilendir.'),
(873, 6, 84, 'Ona İshak\'ı ve Yakub\'u bağışladık. Her birisine yol gösterdik. Daha önce de Nuh\'a ve onun soyundan Davud\'a, Süleyman\'a, Eyyub\'a, Yusuf\'a, Musa\'ya ve Harun\'a da yol gösterdik. Güzel davrananları işte böyle ödüllendiririz.'),
(874, 6, 85, 'Zekeriyya, Yahya, İsa ve İlyas\'a da... Hepsi de iyilerden idi'),
(875, 6, 86, 'İsmail, El-Yasa\', Yunus ve Lut\'a da... Hepsini halklara üstün kıldık.'),
(876, 6, 87, 'Atalarından, soylarından, kardeşlerinden bir çoğunu seçip doğru yola ilettik.'),
(877, 6, 88, 'ALLAH\'ın hidayeti böyledir. Kullarından dilediğini ve/veya dileyeni ona ulaştırır. Ortak koşsalardı yaptıkları boşa çıkardı.'),
(878, 6, 89, 'İşte onlar, kendilerine kitap, hikmet ve peygamberlik verdiklerimizdir. Eğer şu halk, bunları inkar ederse, biz onları inkar etmeyecek bir toplumu yerlerine geçiririz.'),
(879, 6, 90, 'Bunlar, ALLAH\'ın doğru yola ulaştırdıklarıdır. Onların yoluna uy. De ki: "Ben buna karşılık sizden bir ücret istemiyorum. Bu, sadece tüm halklara bir mesajdır."'),
(880, 6, 91, '"ALLAH hiç bir insana bir şey indirmez," demekle ALLAH\'ı gereği gibi değerlendirmediler. De ki: "Halka bir hidayet ve ışık olarak Musa\'nın getirdiği kitabı kim indirdi -ki göstermek için onu kağıtlara yazdığınız halde çoğunu gizliyordunuz. Sizin ve atalarınızın bilmediği şeyleri onun yoluyla öğrendiniz-?" "ALLAH" de ve onları daldıkları sapıklıkta bırak, oynayadursunlar.'),
(881, 6, 92, 'Bu, kendisinden öncekileri doğrulayan kutlu bir kitap olup ülkelerin anasını ve etrafındakileri uyarman için indirilmiştir. Ahirete inananlar ona inanırlar ve onlar namazlarına da devam ederler'),
(882, 6, 93, 'ALLAH adına yalan uydurandan ve kendisine hiç bir şey vahyedilmediği halde, "Bana vahyediliyor," diyenden ve " ALLAH\'ın indirdiği gibi ben de indireceğim," diyenden daha zalim kim olabilir! Can çekişmesi anında zalimleri bir görsen! Melekler, ellerini uzatmıştır: "Canınızı verin! ALLAH hakkında gerçek olmayanı söylemenizden ve onun ayetlerini (vahyini ve mucizelerini) kibir ve gururla karşılamanızdan dolayı bugün utanç verici azapla cezalandırılacaksınız. "'),
(883, 6, 94, '"Sizi ilk defa yarattığımız gibi bize tek olarak geldiniz. Size hayal gibi gösterdiğimiz şeyleri ardınızda bıraktınız. Hakkınızda (verilecek kararda Tanrı ile) ortak davranacaklarını ileri sürdüğünüz şefaatçılarınızı yanınızda görmüyoruz. Aranızdaki bağlar kesilmiştir. İleri sürdükleriniz sizi terketmiştir."'),
(884, 6, 95, 'ALLAH taneyi ve çekirdeği yarıp filizlendirendir. Ölüden diriyi çıkarır ve diriden de ölüyü çıkarandır. İşte budur ALLAH. Nasıl yüz çevirebilirsiniz!'),
(885, 6, 96, 'Sabahı ortaya çıkarandır. Geceyi dinlenme zamanı, güneşi ve ay\'ı da bir hesaplama aracı yapmıştır. Bu, her şeyden Üstün ve her şeyi Bilen\'in planıdır.'),
(886, 6, 97, 'Karanın ve denizin karanlıklarında yollarınızı bulasınız diye sizin için yıldızları yaratan O\'dur. Bilen bir toplum için ayetleri detaylı olarak açıklamış bulunuyoruz.'),
(887, 6, 98, 'O\'dur sizi bir tek kişiden inşa eden. Nitekim bir karar kılmayı bir ayrılış izler. Ayetleri, anlayan bir toplum için açıklamışızdır'),
(888, 6, 99, 'O\'dur gökten su indiren. Onunla her çeşit bitkiyi çıkardık. O bitkiden ürettiğimiz yeşil madde ile de sayısız taneler çıkardık. Tomurcuğundan salkımlar sarkan hurma ağaçları, üzüm bağları, zeytin ve nar gibi birbirine benzeyen ve benzemeyen ürünler çıkardık. Her birinin meyve verme zamanında meyvesine ve olgunluğuna bakın. İnanan bir toplum için bunda deliller ve ibretler vardır'),
(889, 6, 100, 'ALLAH\'a cinleri ortak koştular. Halbuki onları O yaratmıştır. Bilmeden O\'na oğullar ve kızlar yakıştırdılar. O\'nun şanı yücedir, onların nitelemelerinden çok üstündür.'),
(890, 6, 101, 'Gökleri ve yeri yoktan var edendir. Eşi olmadığı halde nasıl olur da çocuğu olur? Herşeyi O yaratmıştır ve O, herşeyi bilendir.'),
(891, 6, 102, 'Rabbiniz ALLAH işte budur. O\'ndan başka tanrı yoktur. Herşeyin Yaratıcısıdır. O\'na kulluk edin. O, herşeyi Kontrol Edendir.'),
(892, 6, 103, 'Gözler O\'na erişemez O, gözlere erişir. O Şefkatlidir, Haberdardır'),
(893, 6, 104, 'Rabbinizden size aydınlatıcı bilgiler gelmiş bulunuyor. Kim görürse kendi yararına, kim körlük ederse kendi zararınadır. Ben üzerinize bekçi değilim.'),
(894, 6, 105, '"Sen ders almışsın," demeleri için ve bilen bir topluma açıklamak için ayetleri böyle sergiliyoruz.'),
(895, 6, 106, 'Rabbinden sana indirilene uy. O\'ndan başka tanrı yoktur. Ortak koşanlardan da yüz çevir.'),
(896, 6, 107, 'ALLAH isteseydi ortak koşamazlardı. Seni onların üzerine bekçi yapmadık. Onların avukatı da değilsin.'),
(897, 6, 108, 'ALLAH\'ın dışında yalvardıklarına sövmeyiniz ki onlar da sınırı aşıp cehaletten dolayı ALLAH\'a sövmesinler. Biz her topluluğa yaptıkları işi böyle süslü gösterdik. Sonunda dönüşleri Rab\'lerinedir ve onlara yapmış bulunduklarını haber verir.'),
(898, 6, 109, 'Kendilerine bir mucize gelse ona mutlaka inanacaklarına dair tüm güçleriyle ALLAH\'a yemin ederler. De ki: "Mucizeler ALLAH\'ın yanındadır." Mucize geldiği zaman da onların inanmıyacağını bilmez misiniz?'),
(899, 6, 110, 'İlk başta inanmamaya karar verdikleri için anlaklarını ve gözlerini çevirip azgınlıkları içinde bocalar durumda bırakırız'),
(900, 6, 111, 'Onlara melekleri indirsek, kendileriyle ölüler konuşsa ve her şeyi getirip önlerine toplasak, ALLAH\'ın diledikleri hariç, yine inanacak değillerdir. Fakat çoğu bu gerçeği bilmez.'),
(901, 6, 112, 'Böylece, her peygambere insanlardan ve cinlerden olan şeytanları düşman kıldık. Aldatmak için birbirlerine yaldızlı sözleri vahyederler. Rabbin dileseydi bunu yapamazlardı. Onlara ve ettikleri iftiralara aldırma.'),
(902, 6, 113, 'Ahirete inanmıyanların kalbi ona kansın, ondan hoşlansın ve gerçekten yapmak istediklerini yapabilsinler diye...'),
(903, 6, 114, 'ALLAH\'tan başka yasa koyucu mu arayayım? O ki size kitabı detaylı olarak indirmiştir. Kendilerine kitap vermiş olduklarımız onun Rabbin tarafından indirilmiş olduğunu bilirler. O halde kuşkulananlardan olma.'),
(904, 6, 115, 'Rabbinin kelimeleri doğruluk ve adaletle tamamlanmıştır. O\'nun sözlerini değiştirebilecek yoktur. O, İşitendir, Bilendir.'),
(905, 6, 116, 'Yeryüzündekilerin çoğuna uyarsan seni ALLAH\'ın yolundan saptırırlar. Onlar ancak zanna uyuyorlar ve onlar sadece tahminde bulunup saçmalıyorlar'),
(906, 6, 117, 'Rabbin, yolundan sapanları en iyi bilendir. Doğru yolda olanları da o daha iyi bilir.'),
(907, 6, 118, 'Ayetlerine inanıyorsanız, üzerinde ALLAH\'ın ismi anılanlardan yeyin.'),
(908, 6, 119, 'Size ne oluyor ki, üzerinde ALLAH\'ın ismi anılanlardan yemiyorsunuz? Halbuki zorunlu haller haricinde size yasaklananları detayıyla size açıklamış bulunuyor. Birçokları, bilgiye dayanmayan kişisel görüşleriyle halkı saptırıyor. Rabbin sınırı aşanları en iyi bilendir.'),
(909, 6, 120, 'Günahların açığını da gizlisini de bırakın. Günah kazananlar işlediklerinin karşılığını alacaklardır'),
(910, 6, 121, 'Üzerinde ALLAH\'ın ismi anılmayanlardan yemeyin. Çünkü o, yoldan çıkmadır. Şeytanlar, sizinle tartışmaları için dostlarına vahyeder. Onlara uyarsanız siz de ortak koşmuş olursunuz'),
(911, 6, 122, 'Ölüyken dirilttiğimiz ve kendisine insanlar arasında yürüyebileceği bir ışık verdiğimiz kimse, karanlıklar içinde olup da ondan çıkamıyan kimse gibi olur mu? İnkarcıların yaptıkları işler böyle süslü gösterilmiştir.'),
(912, 6, 123, 'Her ülkenin önde gelen suçlularına, orada hile ve entrika yapmaları için izin verdik. Aslında kendilerinden başkasını kandırmıyorlar. Ama farkında değiller.'),
(913, 6, 124, 'Kendilerine bir mucize geldiğinde: "ALLAH\'ın elçilerine verilenlerin benzeri bize verilmedikçe inanmayız," derler. ALLAH mesajını nereye koyacağını daha iyi bilir. Yaptıkları hilelerden ötürü suçlulara ALLAH katından bir aşağılama ve çetin bir azap dokunacaktır.'),
(914, 6, 125, 'ALLAH kimi doğru yola ulaştırmak dilerse onun gönlünü islama (Tanrı\'ya teslim olmaya) açar. Kimi de saptırmak dilerse göğsünü, göğe yükseliyormuş gibi dar ve sıkıntılı kılar. ALLAH inanmıyanları işte böyle kötü duruma sokar.'),
(915, 6, 126, 'Bu, Rabbinin dosdoğru yoludur. Öğüt alan bir toplum için ayetleri detayıyla açıklamış bulunuyoruz'),
(916, 6, 127, 'Onlar, Rablerinin yanında barış yurdunu hakketmişlerdir. Yaptıklarından ötürü onların sahibi O\'dur.'),
(917, 6, 128, 'Hepsini sürüp topladığı gün: "Ey cinler topluluğu, siz çok sayıda insan harcadınız." Onların insanlardan olan dostları: "Rabbimiz, bize verdiğin sürenin sonuna erişinceye kadar birbirimizden hoşlandık," derler. "Yeriniz ateştir," der. ALLAH\'ın dilemesi hariç, orada ebedi kalacaklardır. Rabbin Bilgedir, Bilendir.'),
(918, 6, 129, 'Zalimleri böylece eşleyerek birbirinin dostları yaparız. Yaptıklarından ötürü...'),
(919, 6, 130, 'Ey cinler ve insanlar topluluğu, size, ayetlerimi anlatan ve bu günle karşılaşmanız konusunda sizi uyaran sizden elçiler gelmedi mi? "Kendimize karşı tanığız," dediler. Dünya hayatı onları aldattı ve inkarcı olduklarına dair kendi kendilerine karşı tanıklık ettiler.'),
(920, 6, 131, 'Bu böyledir. Rabbin, halkı habersiz bir durumda iken ülkeleri zulüm ile helak etmez.'),
(921, 6, 132, 'Yaptıklarına karşılık olarak her biri için dereceler vardır. Rabbin, onların yaptıklarından habersiz değil.'),
(922, 6, 133, 'Rabbin Zengindir, Rahmet Sahibidir. Dilerse sizi götürür ve sizi başkaların soyundan nasıl getirmişse sizden sonra da yerinize dilediklerini yerleştirir'),
(923, 6, 134, 'Size söz verilenler elbette yerine gelecektir ve onun önüne geçemezsiniz.'),
(924, 6, 135, 'De ki: "Ey halkım, elinizden geleni yapın, ben de elimden geleni yapacağım. Yakında kimin nihai başarıya ulaştığını göreceksiniz!" Zalimler onmazlar.'),
(925, 6, 136, 'ALLAH\'ın ürettiğı ekinlerden ve çiftlik hayvanlarından O\'na bir pay ayırarak, kafalarına göre, "Bu ALLAH\'ın, bu da ortaklarımızındır," dediler. Ortaklarının payı ALLAH\'a ulaşmıyor; ancak ALLAH\'ın payı ortaklarına ulaşıyor! Ne de kötü hüküm veriyorlar.'),
(926, 6, 137, 'Böylece onların (Tanrı adına hüküm veren) ortakları, çocuklarını öldürmeyi bile müşriklerin çoğuna iyi bir şeymiş gibi gösterdiler. Böylece onları mahvettiler ve dinlerini karıştırıp bozdular. ALLAH dileseydi bunu yapamazlardı. Onlardan ve uydurdukları şeylerden uzak dur.'),
(927, 6, 138, 'Zanlarına göre şöyle dediler: "Bunlar dokunulmaz çiftlik hayvanları ve ekinlerdir. Dilediğimizden başkası bunları yiyemez." Binilmesi yasaklanan çiftlik hayvanları... ALLAH\'ın ismini üzerlerinde anmadıkları çiftlik hayvanları... (Çeşitli haramları) O\'na yakıştırdılar. Onları iftiralarıyla cezalandıracaktır.'),
(928, 6, 139, '"Şu çiftlik hayvanlarının karınlarındakiler sadece erkeklerimize ait olup kadınlarımıza haramdır. Ölü doğarlarsa o zaman paylaşabilirler," dediler. Bu nitelemelerinin hesabını onlara ödetecektir. O Bilgedir, Bilendir.'),
(929, 6, 140, 'Cehaletleri yüzünden ALLAH\'a iftiralar ederek çocuklarını budalaca öldürenler ve ALLAH\'ın kendilerine verdiği rızıkları haram edenler kaybetmişlerdir, şaşırmışlardır. Doğruyu göremezler.'),
(930, 6, 141, 'Çardaklı ve çardaksız bahçeleri, ürünleri çeşit çeşit hurmaları, ekinleri, zeytinleri ve narları -benzer veya farklı- yaratan O\'dur. Her biri meyve verdiği zaman meyvesinden yeyin ve hakkını da hasat gününde verin. Savurganlık yapmayın; O, savurganları sevmez.'),
(931, 6, 142, 'Çiftlik hayvanları, binmeniz, kesmeniz veya kendilerinden yatak malzemesi çıkarmanız içindir. ALLAH\'ın size verdiği rızıklardan yeyin ve şeytanın adımlarını izlemeyin; çünkü o sizin apaçık düşmanınızdır.'),
(932, 6, 143, 'Sekiz çift: Koyundan iki, keçiden iki... De ki: "İki erkeği mi haram etti, iki dişiyi mi, yoksa o iki dişinin rahimlerindekileri mi?" Doğru sözlüler iseniz, bir bilgiye dayanarak bana cevap verin.'),
(933, 6, 144, 'Deveden iki, sığırdan iki. De ki: "İki erkeği mi haram etti, iki dişiyi mi, yoksa o iki dişinin rahimlerindekini mi? ALLAH\'ın size böyle emrettiğine tanık mı oldunuz? Halkı bilgisizce yoldan saptırmak için, yalan uydurup onları ALLAH\'a yakıştırandandaha zalim kim olabilir? ALLAH zalim toplumu doğru yola iletmez."'),
(934, 6, 145, 'De ki: "Bana vahyedilende, yiyen birisi için şunların dışında haram edilmiş bir madde bulamıyorum: () Leş, () akıtılmış kan, () domuzun eti -ki pistir-, () ALLAH\'tan başkasına sapıkça adanmış yiyecekler." Zorda kalan bir kimse, istekli olmaz ve sınırı aşmazsa kuşkusuz senin Rabbin Bağışlayandır, Rahimdir.'),
(935, 6, 146, 'Yahudilere tüm tırnaklı hayvanları haram kıldık. Sığır ve koyunun da yağlarını haram ettik; ancak sırtlarında yahut bağırsaklarında taşıdıkları, veya kemiklerine karışmış olanlar hariç. Aşırı gitmelerinden ötürü onları böyle cezalandırdık. Biz doğru sözlüyüz.'),
(936, 6, 147, 'Seni yalanlarlarsa: "Rabbiniz geniş rahmet sahibidir ve O\'nun cezası suçlu toplumlardan geri çevrilemez," de.'),
(937, 6, 148, 'Ortak koşanlar, "ALLAH dilemeseydi, ne biz, ne atalarımız ortak koşmaz ve hiç bir şeyi de haram etmezdik," diyeceklerdir. Onlardan öncekiler de azabımızı tadıncaya kadar aynı şekilde yalanlamışlardı. De ki: "Yanınızda bize göstereceğiniz her hangi bir bilgi var mı? Siz ancak zanna (şüpheli ve çelişkili rivayetlere) uyuyorsunuz ve siz sadece tahminde bulunuyorsunuz."'),
(938, 6, 149, 'De ki: "En güçlü delil ALLAH\'ındır. Dileseydi, elbette hepinizi doğruya ulaştırırdı."'),
(939, 6, 150, 'De ki: "ALLAH\'ın şunu haram ettiğine tanıklık edecek tanıklarınızı getirin." Tanıklık ederlerse onlarla beraber tanıklık etme. Ayetlerimizi yalanlayanların ve ahirete inanmıyanların keyfine uyma. Onlar, Rab\'lerine başkalarını eş koşmaktadırlar.'),
(940, 6, 151, 'De ki: "Gelin, Rabbinizin size neleri haram ettiğini söyleyeyim: O\'na hiçbir şeyi ortak koşmayın. Ana babaya iyi davranın. Yoksulluk korkusuyla çocuklarınızı öldürmeyin; sizi de onları da biz besliyoruz. Büyük günahların açığına da gizlisine de yaklaşmayın ve ALLAH\'ın kutsal saydığı cana haksız yere kıymayın. Bunlar, düşünesiniz diye O\'nun size verdiği öğütlerdir.'),
(941, 6, 152, 'Erginlik çağına erişinceye kadar yetim malına yaklaşmayın. Ölçüyü ve tartıyı doğru dürüst yapın. Kişiye gücünün yetmediğini yüklemeyiz. Konuştuğunuz zaman akrabanız aleyhinde bile olsa doğru olun. ALLAH\'a verdiğiniz sözü tutunuz. Bunlar, ders alasınız diye O\'nun size verdiği öğütlerdir.'),
(942, 6, 153, 'İşte bu benim dosdoğru yolumdur. Ona uyun, başka yollara uymayın; çünkü onlar, sizi O\'nun yolundan ayırırlar. Bunlar, dinlersiniz diye O\'nun size verdiği öğütlerdir.'),
(943, 6, 154, 'Nitekim, en güzel biçimde tamamlanmış, her şeyin detaylı açıklaması, hidayet ve rahmet olarak Musa\'ya Kitabı verdik ki Rab\'leriyle kavuşmaya inansınlar.'),
(944, 6, 155, 'Bu da indirdiğimiz kutlu bir kitaptır. Öyleyse size merhamet edilmesi için ona uymalısınız, erdemli davranmalısınız.'),
(945, 6, 156, 'Artık, "Bizden önceki iki gruba kitap verildi; biz ise onların öğretilerinden habersizdik," diyemezsiniz.'),
(946, 6, 157, 'Yahut, "Bize de kitap indirilseydi onlardan daha doğru yolda olurduk," da diyemezsiniz. Size Rabbinizden bir delil (beyyine), bir hidayet ve bir rahmet gelmiş bulunuyor. ALLAH\'ın ayetlerini yalanlayıp ondan yüz çevirenden daha zalim kim olabilir? Ayetlerimizden yüz çevirenleri, yüz çevirmelerinden ötürü azabın en kötüsüyle cezalandıracağız.'),
(947, 6, 158, 'Kendilerine meleklerin gelmesini mi, yoksa Rabbinin, yahut Rabbinin bazı işaretlerinin gelmesini mi bekliyorlar? Rabbinin bazı işaretleri geldiği gün, daha önce inanmamış veya imanında bir hayır kazanmamış kişiye bir yarar sağlamaz. De ki: "Bekleyin, biz de beklemekteyiz."'),
(948, 6, 159, 'Dinlerini parçalara ayırıp grup grup olanlarla senin hiç bir ilişkin yoktur. Onların işi ALLAH\'a kalmıştır; sonra onlara durumlarını haber verecektir.'),
(949, 6, 160, 'Kim iyilik getirirse, kendisine getirdiğinin on katı vardır. Kim kötülük getirirse, ancak dengiyle cezalandırılır ve kendilerine haksızlık edilmez.'),
(950, 6, 161, 'De ki: "Rabbim beni dosdoğru olan yola iletmiş bulunuyor: monoteist olan İbrahim\'in mükemmel dinine... O, ortak koşanlardan olmadı."'),
(951, 6, 162, 'De ki: "Namazım, ibadetlerim, hayatım ve ölümüm evrenlerin Rabbi olan ALLAH içindir."'),
(952, 6, 163, '"O\'nun ortağı yoktur. Bununla emredildim ve ben herkesten önce teslim olurum."'),
(953, 6, 164, 'De ki: "Her şeyin Rabbi iken, ALLAH\'tan başka Rab mi arayayım? Herkes kendi yaptığından yarar görür ve kimse kimsenin yükünü çekmez. Sonunda dönüşünüz Rabbinizedir. Ayrılığa düştüğünüz konuda size haber verecektir.'),
(954, 6, 165, 'O ki sizi yeryüzünün mirasçıları kıldı, ve size verdikleriyle sizi sınamak için kiminizi kiminize derecelerle üstün kıldı. Rabbin çabuk sonuçlandırandır. Bağışlayandır, Rahimdir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'A. L. M. SS.'),
(956, 7, 2, 'Bu, sana indirilen bir kitaptır. Onunla uyarman ve inananlara öğüt vermen konusunda göğsünde bir kuşku ve sıkıntı olmasın'),
(957, 7, 3, 'Rabbinizden size indirileni izleyin, O\'ndan başkalarını dost edinerek izlemeyin. Ne kadar az öğüt alıyorsunuz!'),
(958, 7, 4, 'Yok ettiğimiz nice topluluklar, uyurlarken yahut gündüzün işlerine dalmış haldeyken azabımız kendilerine ulaştı.'),
(959, 7, 5, 'Kendilerine azabımız geldiği vakit, "Gerçekten bizler zalimler imişiz," demekten başka sözleri olmadı.'),
(960, 7, 6, 'Kendilerine mesaj gönderilenleri de sorguya çekeceğiz, elçileri de sorguya çekeceğiz.'),
(961, 7, 7, 'Onlara bir bilgiyle bildireceğiz; biz onlardan hiç uzak kalmadık.'),
(962, 7, 8, 'O gün tartı dosdoğrudur. Tartıları ağır gelenler başarmış olanlardır.'),
(963, 7, 9, 'Tartıları hafif gelenler ise ayetlerimize karşı haksız davranarak kişiliklerini yitirmiş olanlardır.'),
(964, 7, 10, 'Sizleri yeryüzüne yerleştirdik ve orada size yaşama imkânları sağladık. Ne kadar da az şükrediyorsunuz.'),
(965, 7, 11, 'Sizi yarattık, sonra size biçim verdik, sonra meleklere, "Adem\'e secde edin," dedik. İblis hariç hepsi secde etti; o secde edenlerden olmadı.'),
(966, 7, 12, 'Dedi: "Sana emrettiğim zaman seni secde etmekten ne alıkoydu?" (İblis:) "Ben ondan daha iyiyim; Sen beni ateşten yarattın, onu ise balçıktan yarattın," dedi.'),
(967, 7, 13, '"Oradan aşağı in," dedi, "Orada büyüklük taslayamazsın. Defol. Değerini yitirdin!"'),
(968, 7, 14, '"Dirilecekleri güne kadar bana süre tanı," dedi.'),
(969, 7, 15, '"Sana süre tanınmıştır," dedi.'),
(970, 7, 16, '"Beni saptırmana karşılık, onlar için senin dosdoğru yolun üzerine sinsice oturacağım."'),
(971, 7, 17, '"Sonra önlerinden, arkalarından, sağlarından ve sollarından kendilerine sokulacağım. Böylece çoklarını şükreder bulmayacaksın."'),
(972, 7, 18, 'Dedi ki: "Horlanmış ve kovulmuş olarak oradan çık. Onlardan sana uyanlara gelince, hepinizle cehennemi dolduracağım."'),
(973, 7, 19, '"Adem, sen ve eşin cennette durup dilediğiniz yerden yeyin. Şu ağaçtan yemeyin; yoksa zalimlerden olursunuz."'),
(974, 7, 20, 'Şeytan, kendilerinden gizlenmiş olan bedenlerini ortaya çıkarmak için onlara fısıldadı: "Rabbinizin sizi bu ağaçtan menetmesinin sebebi, ikinizin birer melek veya birer ebedi varlık olmamanız içindir," dedi.'),
(975, 7, 21, 'Kendilerine yemin de etti: "Ben size öğüt veriyorum."'),
(976, 7, 22, 'Böylece onları yalanlarla aldattı. Ağacı tadınca bedenleri kendilerine göründü. Üzerlerini cennet yapraklarıyla örtmeye başladılar. Rableri kendilerini çağırdı: "O ağaçtan ikinizi menetmedim mi ve şeytanın ikinize düşman olduğunu söylemedim mi?"'),
(977, 7, 23, 'Her ikisi: "Rabbimiz, kendimize zulmettik. Bizi bağışlamaz ve bize acımazsan kaybedenlerden oluruz," dediler.'),
(978, 7, 24, 'Dedi ki: "Birbirinize düşmanlar olarak aşağı ininiz. Bir süreye kadar yeryüzünde yerleşip geçineceksiniz."'),
(979, 7, 25, 'Dedi ki: "Orada yaşayacak, orada ölecek ve oradan çıkarılacaksınız."'),
(980, 7, 26, 'Adem oğulları, size, bedenimizi örtecek ve süsleyecek elbiseler hazırladık. Erdemlilik elbisesi ise daha hayırlıdır. Bunlar, ALLAH\'ın işaretleridir, olur ki öğüt alırsınız.'),
(981, 7, 27, 'Adem oğulları, şeytan, ana babanızın vücutlarını kendilerine göstermek için elbiselerini soyarak cennetten çıkardığı gibi sizi de şaşırtmasın. O ve kabilesi sizin onları görmediğiniz yerden sizi görürler. Biz, şeytanları, inanmıyanların dostları yaptık.'),
(982, 7, 28, 'Herhangi bir kötü şey işledikleri zaman, "Atalarımızı böyle yapar bulduk; ALLAH da bize öyle emretti," derler. De ki: "ALLAH kötü bir şeyi emretmez. ALLAH hakkında nasıl olur da bilmediklerinizi söylersiniz?"'),
(983, 7, 29, 'De ki: "Rabbim adaleti emreder. Her mescitte (ibadet yerinde) dini sadece O\'na ait kılarak O\'na yalvarın. Sizi ilk yarattığı gibi O\'na döneceksiniz."'),
(984, 7, 30, 'Bir grubu doğru yola iletti, bir grup da sapıklığı hakketti. Onlar, şeytanları ALLAH\'tan başka dostlar edindiler ama kendilerini doğru yolda sanıyorlar.'),
(985, 7, 31, 'Adem oğulları, mescitlere giderken süsleniniz. Yiyiniz içiniz; ancak oburluk ve savurganlık yapmayınız. O, oburları ve savurganları sevmez.'),
(986, 7, 32, 'De ki: "ALLAH\'ın, kendi kulları için yarattığı süsleri ve güzel rızıkları kim haram edebilir?" De ki: "Onlar dünya hayatında inananlar içindir, ahirette ise sadece onlar içindir." Bilen bir toplum için ayetlerimizi böyle detaylı açıklarız.'),
(987, 7, 33, 'De ki: "Rabbim, ancak kötü işlerin açığını ve gizlisini, günahları, haksız yere saldırmayı, kendilerine hiç bir otorite ve güç vermediği bir şeyi ALLAH\'a ortak koşmanızı ve ALLAH hakkında bilmediğiniz şeyleri söylemenizi haram kılmıştır."'),
(988, 7, 34, 'Her bir toplumun bir süresi vardır. Süreleri gelince ne bir an erteleyebilirler, ne de öne alabilirler.'),
(989, 7, 35, 'Adem oğulları, size içinizden elçiler gelip size ayetlerimi bildirdikleri zaman dinleyip kendilerini düzeltenlere ne bir korku vardır ne de onlar üzülürler.'),
(990, 7, 36, 'Büyüklük taslayarak ayetlerimizi yalanlayanlar ise ateş halkıdır; orada ebedi kalıcıdırlar.'),
(991, 7, 37, 'ALLAH\'a yalan iftira edenden veya ayetlerini yalanlayandan daha zalim kim olabilir? Onların kitapta anlatılan payları kendilerine erişecektir. Elçilerimiz kendilerine gelip canlarını alırken: "ALLAH\'ın dışında taptıklarınız nerede," dediklerinde, "Bizi terkettiler," derler. İnkarcı olduklarına dair kendi aleyhlerinde tanıklık ederler.'),
(992, 7, 38, '"Sizden önce geçen cin ve insan topluluklarıyla birlikte ateşe girin," dedi. Her ne zaman bir topluluk girdiyse bir öncekine lanet etti. Hepsi oraya vardığında sonrakiler öncekiler için: "Rabbimiz, bizi bunlar saptırdı. Bunlara ateşten bir kat daha fazla azap ver," dediler. "Hepsi için iki kat vardır. Ancak bilmezsiniz," dedi.'),
(993, 7, 39, 'Öncekiler sonrakilere, "Sizin bize bir üstünlüğünüz yoktur. Kazandıklarınızdan ötürü azabı tadın," dediler.'),
(994, 7, 40, 'Ayetlerimizi inkar edenlere ve onlara karşı büyüklük taslayanlara göğün kapısı açılmaz ve deve iğne deliğinden geçmedikçe de cennete girmezler. Suçluları böyle cezalandırırız.'),
(995, 7, 41, 'Onlar için cehennemden bir yatak ve üstlerinde de bir örtü vardır. Zalimleri işte böyle cezalandırırız.'),
(996, 7, 42, 'İnanıp erdemli bir hayat sürenlere gelince, biz hiç kimseye kapasitesinin üstünde sorumluluk yüklemeyiz; onlar cennet halkıdır. Onlar orada ebedi kalıcıdırlar.'),
(997, 7, 43, 'Göğüslerinden kin ve kıskançlık duygularını çıkardık. Altlarından nehirler akarken: "Bizi buna ulaştıran ALLAH\'a övgüler olsun. ALLAH bizi doğruya iletmeseydi biz doğruyu bulamazdık. Rabbimizin elçileri gerçeği getirmişlerdi," dediler. Kendilerine: "İşte bu cennet sizindir. Yaptıklarınıza karşılık olarak onu miras aldınız," diye seslendik'),
(998, 7, 44, 'Cennet halkı cehennem halkına seslenir: "Rabbimizin bize söz verdiğini gerçek olarak bulduk. Rabbinizin size söz verdiğini siz de gerçek olarak buldunuz mu?" "Evet!," derler. Biri aralarında şunu ilan eder: "ALLAH\'ın laneti zalimlerin üzerine olsun."'),
(999, 7, 45, 'Onlar ki ALLAH\'ın yolundan alıkoyarlar ve onu eğriltmek isterler. Ahiret konusunda da inkarcıdırlar.'),
(1000, 7, 46, 'Aralarını bir perde böler. Orta yerde de bazı kimseler var ki herkesi görünüşlerinden tanırlar. Cennet halkına, "Selam size," diye seslenirler. Bunlar oraya (cennete), canları istedikleri halde giremediler.'),
(1001, 7, 47, 'Gözleri ateş halkına çevrildiğinde, "Rabbimiz, bizi zalim toplulukla birlikte bulundurma," derler.'),
(1002, 7, 48, 'Orta yerde bulunanlar, görünüşlerinden tanıdıkları kimselere seslenirler: "Sizin cemaatiniz ve büyüklük taslamış olmanız size hiç bir yarar sağlamadı."'),
(1003, 7, 49, '"ALLAH onlara bir rahmet dokundurmayacak diye yemin ettiğiniz kimseler bunlar mıydı?" (Orta Yerdekilere şunlar denecektir:) "Cennete girin; size bir korku yoktur ve üzülmeyeceksiniz de."'),
(1004, 7, 50, 'Ateş halkı, cennet halkına seslendi: "Suyunuzdan, yahut ALLAH\'ın size verdiği bazı nimetlerden üstümüze akıtın." Onlar da dediler ki: "ALLAH bu ikisini kafirlere haram kılmıştır."'),
(1005, 7, 51, 'Onlar dinlerini hafife aldılar ve dünya hayatı kendilerini aldattı. Bugünleriyle karşılaşacaklarını unuttukları ve ayetlerimizi reddettikleri için bugün biz de onları unuturuz!'),
(1006, 7, 52, 'Bilgiyle detaylandırdığımız, inanan bir toplum için yol gösterici ve rahmet olan bir kitabı onlara getirdik.'),
(1007, 7, 53, 'Onun haberlerinin gerçekleşmesini mi bekliyorlar? Haberleri gerçekleştiği gün, onu daha önce önemsemeyenler, "Rabbimizin elçileri gerçeği getirmişlerdi. Bizim için aracılık edecek bir şefaatçı var mı? Yahut, öncekinden farklı davranmak için geri gönderilsek," derler. Kişiliklerini yitirmişlerdir ve uydurdukları şeyler de onları terketmiştir.'),
(1008, 7, 54, 'Rabbiniz ALLAH, gökleri ve yeri altı günde yaratan ve sonra otoritesini kurandır. Geceyi, onu durmadan kovalayan gündüze bürüyüp örter. Güneş, ay ve yıldızlar O\'nun yönetimine boyun eğmiştir. Yaratılış da, yönetim de O\'na aittir. Evrenlerin Rabbi olan ALLAH ne uludur!'),
(1009, 7, 55, 'Rabbinizi içtenlikle ve gizlice çağırın. O, sınırı aşanları sevmez.'),
(1010, 7, 56, 'Düzeltildikten sonra yeryüzünde bozgunculuk yapmayın. O\'na korku ve umutla yalvarın. ALLAH\'ın rahmeti güzel davrananlara yakındır'),
(1011, 7, 57, 'O, rahmetinin bir ön müjdesi olarak rüzgarları gönderendir. Toplanıp yüklendikten sonra ağır bulutları ölü bir toprağa süreriz. Onunla su indirir ve onunla her çeşit ürünü çıkarırız. Ölüleri de böyle çıkarırız. Belki bundan bir öğüt alırsınız.'),
(1012, 7, 58, 'İyi arazi, Rabbinin izniyle bitki verir. Kötü olanı ise, pek yararlı bir şey vermez. Şükreden bir topluluk için ayetleri böyle açıklarız'),
(1013, 7, 59, 'Nuh\'u halkına gönderdik: "Ey halkım, ALLAH\'a kulluk edin. O\'ndan başka bir tanrınız yoktur. Sizin için büyük günün azabından korkarım," dedi.'),
(1014, 7, 60, 'Halkının ileri gelenleri, "Seni apaçık bir sapıklık içinde gürüyoruz," dediler.'),
(1015, 7, 61, 'Dedi ki: "Halkım, bende bir sapıklık yoktur; ancak ben evrenlerin Rabbi tarafından gönderilmiş bir elçiyim."'),
(1016, 7, 62, '"Size Rabbimin mesajını iletiyor ve size öğüt veriyorum. Sizin bilmediklerinizi ALLAH aracılığıyla biliyorum."'),
(1017, 7, 63, '"Sizi uyarmak için ve erdemli bir hayat sürüp merhamet edilmeniz için sizden bir adama Rabbinizden bir mesajın gelmesine mi şaştınız?"'),
(1018, 7, 64, 'Onu yalanladılar. Bunun üzerine onu ve gemide beraberinde bulunanları kurtardık. Ayetlerimizi yalanlayanları ise boğduk. Onlar kör bir halktı.'),
(1019, 7, 65, 'Ad halkına da kardeşleri Hud\'u gönderdik. "Halkım, ALLAH\'a kulluk edin. O\'ndan başka bir tanrınız yoktur. Erdemli olmayacak mısınız," dedi.'),
(1020, 7, 66, 'Halkından ileri gelen inkarcılar, "Biz seni beyinsizlik içinde görüyoruz. Biz seni yalancı sanıyoruz."'),
(1021, 7, 67, 'Dedi ki: "Ey halkım, ben beyinsiz değilim; fakat ben, evrenlerin Rabbinden bir elçiyim."'),
(1022, 7, 68, '"Size Rabbimin mesajını iletiyorum. Sizin için güvenilir bir öğütçüyüm."'),
(1023, 7, 69, '"Sizi uyarmak amacıyla Rabbinizden bir mesajın aranızdan bir adama gelmesine mi şaştınız? Nuh\'un halkından sonra sizi varisler yaptığını ve yaratılışta sizi onlardan güçlü kıldığını hatırlayın. Başarmanız için ALLAH\'ın nimetlerini düşünün."'),
(1024, 7, 70, 'Dediler ki: "Sadece ALLAH\'a kulluk edelim ve atalarımızın taptıklarını terkedelim diye mi bize geldin? Doğru sözlü isen bizi kendisiyle tehdit ettiğin şeyi getir bakalım."'),
(1025, 7, 71, 'Dedi ki: "Rabbinizden bir azap ve öfkeye mahkum edilmiş bulunuyorsunuz. Sizin ve atalarınızın uydurduğu ve ALLAH\'ın kendilerine hiçbir güç vermediği isimler konusunda mı benimle tartışıyorsunuz? Bekleyiniz, ben de sizinle birlikte bekleyenlerdenim."'),
(1026, 7, 72, 'Bizden bir rahmet olarak, onu ve onunla birlik olanları kurtardık. Ayetlerimizi yalanlayarak inanmıyanların da ardını kestik.'),
(1027, 7, 73, 'Semud halkına da kardeşleri Salih\'i gönderdik. Dedi ki: "Ey halkım, ALLAH\'a kulluk edin. O\'ndan başka bir tanrınız yoktur. Rabbinizden, size bir beyyine (kanıt) gelmiştir. Şu ALLAH\'ın devesi sizin için bir işaret olacaktır. Onu bırakın, ALLAH\'ın toprağında otlasın. Ona bir zarar vermeyin; yoksa sizi acı bir azap yakalar."'),
(1028, 7, 74, '"Ad halkından sonra sizi varisler kıldığını hatırlayın. Sizi yeryüzüne yerleştirdi. Düzlüklerinde köşkler kurup dağlarında evler yontuyorsunuz. ALLAH\'ın nimetlerini hatırlayın da yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın."'),
(1029, 7, 75, 'Halkının büyüklük taslayan ileri gelenleri, aşağılayıp zulmettikleri inananlara, "Salih\'in gerçekten Rabbi tarafından gönderilmiş olduğunu nereden biliyorsunuz," dediler. Onlar da: "Biz onun getirdiği mesaja inanıyoruz," dediler.'),
(1030, 7, 76, 'Büyüklük taslayanlar, "Biz, sizin inandığınız şeyi inkar ediyoruz," dediler.'),
(1031, 7, 77, 'Deveyi işkence ederek kestiler ve Rab\'lerinin emrine baş kaldırdılar: "Salih, gerçekten elçilerden birisi isen, bizi tehdit ettiğin azabı başımıza getir," dediler.'),
(1032, 7, 78, 'Bunun üzerine, onları o sarsıntı yakaladı; evlerinde dizüstü çöküverdiler.'),
(1033, 7, 79, 'O da onlardan yüz çevirdi ve "Ey halkım, size Rabbimin elçiliğini ilettim, size öğüt verdim; fakat siz öğüt verenleri sevmiyorsunuz," dedi.'),
(1034, 7, 80, 'Ve Lut\'u da gönderdik. Halkına dedi ki: "Sizden önce hiç kimsenin kıramadığı bir günahın rekorunu mu kırıyorsunuz?"'),
(1035, 7, 81, '"Siz kadınları bırakıp erkeklere cinsel duygularla yöneliyorsunuz. Siz sınırı aşan bir topluluksunuz!"'),
(1036, 7, 82, 'Halkının cevabı, ancak şunları söylemek oldu: "Onları kentinizden çıkarın. Bunlar çok temiz insanlarmış!"'),
(1037, 7, 83, 'Onu ve ailesini kurtardık. Karısı hariç. O geride kalanlardan oldu.'),
(1038, 7, 84, 'Üstlerine bir yağmur yağdırdık. Suçluların sonuna bak!'),
(1039, 7, 85, 'Medyen\'e de kardeşleri Şuayb\'ı gönderdik. Dedi ki: "Ey halkım, ALLAH\'a kulluk edin. O\'ndan başka tanrınız yoktur. Rabbinizden size açık bir kanıt gelmiş bulunuyor. Ölçü ve tartıyı tam uygulayın. Halkın hakkını yemeyin. Düzeltildikten sonra yeryüzünde bozgunculuk yapmayın. İnanıyorsanız bunlar sizin için daha hayırlıdır."'),
(1040, 7, 86, '"ALLAH\'ın yolunu eğriltmek ve inananları tehdit edip ondan çevirmek için her yolun başına oturmayın. Sayıca az iken sizi nasıl da çoğalttığını düşünün. Bozguncuların sonunun nasıl olduğuna bir bakın."'),
(1041, 7, 87, '"Madem ki sizden bir kısmı benimle gönderilen mesaja inanmış, bir kısmı da inanmamış bulunuyor, öyleyse ALLAH aramızda hükmedinceye kadar sabredin. O, hükmedenlerin en iyisidir."'),
(1042, 7, 88, 'Halkının büyüklük taslayan ileri gelenleri, "Şuayb, ya seni ve seninle beraber inananları kentimizden süreceğiz ya da dinimize geri dönersin!," dediler. O da şöyle dedi: "Biz istemesek de mi?"'),
(1043, 7, 89, '"ALLAH bizi sizin dininizden kurtardıktan sonra ona dönersek ALLAH\'a yalan yakıştırmış oluruz. Rabbimiz ALLAH dilemedikçe ona tekrar dönmemiz söz konusu olamaz. Rabbimiz her şeyi bilgisiyle kuşatmıştır. Biz yalnız ALLAH\'a güvendik. Bizimle halkımızın arasını hak ile aç ey Rabbimiz. Sen açığa çıkaranların en hayırlısısın.'),
(1044, 7, 90, 'Halkının ileri gelen inkarcıları, "Şuayb\'a uyarsanız kaybedenlerden olursunuz," dediler.'),
(1045, 7, 91, 'Bunun üzerine onları bir sarsıntı yakaladı da evlerinde diz üstü çöküverdiler.'),
(1046, 7, 92, 'Şuayb\'ı yalanlayanlar sanki hiç orada yaşamamışlar gibi yok oldular. Şuayb\'ı yalanlayanlar, asıl kaybedenler onlar oldular.'),
(1047, 7, 93, 'Onlardan yüz çevirdi ve "Ey halkım, size Rabbimin mesajını ilettim, öğüt verdim. İnkarcı bir topluluğa nasıl acırım?"'),
(1048, 7, 94, 'Her ne zaman bir ülkeye bir peygamber gönderdiysek, yalvarsınlar diye halkını darlık ve sıkıntıya uğrattık.'),
(1049, 7, 95, 'Sonra kötülüğün yerine iyiliği getirdik. Ne var ki anlayışlarını yitirdiler: "Sıkıntı ve refah atalarımıza da dokunmuştu," dediler. Bunun üzerine, haberleri olmadan onları ansızın yakaladık.'),
(1050, 7, 96, 'Ülkelerin halkları inanıp erdemli davransalardı, göklerden ve yerden üzerlerine bolluk kapısını açardık. Ama yalanladılar ve bunun üzerine kazandıklarıyla birlikte onları yakaladık.'),
(1051, 7, 97, 'Ülkelerin halkları, azabımızın geceleyin onlar uyurlarken kendilerine gelmeyeceğinden emin mi oldular?'),
(1052, 7, 98, 'Ülkelerin halkları, azabımızın gündüzün onlar oynarlarken kendilerine gelmeyeceğinden emin mi oldular?'),
(1053, 7, 99, 'ALLAH\'ın planından emin mi oldular? Kaybedenlerden başkası ALLAH\'ın planından emin olmaz.'),
(1054, 7, 100, 'Önceki nesillerin yerine yeryüzüne varis olanlara belli olmadı mı ki, eğer dilesek onları da günahlarıyla cezalandırarak kalplerini mühürleriz de işitemezler.'),
(1055, 7, 101, 'Bunlar, sana haberlerini aktardığımız toplumlardır. Elçileri, onlara apaçık delillerle gitmişlerdi. Ama daha önceden yalanladıklarına inanacak değillerdi. ALLAH kafirlerin kalplerini böyle damgalar.'),
(1056, 7, 102, 'Çoğunu, verdikleri sözü tutanlar olarak bulmadık, aksine çoğunu yoldan çıkmış bulduk.'),
(1057, 7, 103, 'Sonra, onların ardından Firavun ve erkanına Musa\'yı ayet ve mucizelerimizle gönderdik; fakat ayet ve mucizelerimize karşı haksızlık ettiler. Bozguncuların sonunun nasıl olduğuna dikkat et!'),
(1058, 7, 104, 'Musa: "Firavun, ben alemlerin Rabbinden bir elçiyim."'),
(1059, 7, 105, '"ALLAH hakkında gerçeğin dışında bir şey söylememek benim görevimdir. Rabbinizden size bir kanıt ile geldim; İsrail oğullarını benimle birlikte salıver."'),
(1060, 7, 106, 'Dedi: "Bir ayet (mucize) ile gelmişsen ve gerçekten doğru sözlüysen getir onu bakalım."'),
(1061, 7, 107, 'Asasını attı, iri bir yılan oluverdi.'),
(1062, 7, 108, 'Elini çıkardı, bakanlar için bembeyaz oluverdi.'),
(1063, 7, 109, 'Firavun\'un halkından ileri gelenler, durumu tartıştılar ve "Bu, uzman bir sihirbazdır," dediler,'),
(1064, 7, 110, '"Sizi yurdunuzdan çıkarmak istiyor. Ne önerirsiniz?"'),
(1065, 7, 111, 'Dediler ki: "Onu ve kardeşini beklet ve şehirlere toplayıcılar yolla."'),
(1066, 7, 112, '"Bütün uzman sihirbazları sana getirsinler."'),
(1067, 7, 113, 'Sihirbazlar Firavun\'a geldiler ve "Kazanırsak bize bir ödül var mı," dediler.'),
(1068, 7, 114, '"Evet," dedi, "Siz benim yakın çevreme de girersiniz."'),
(1069, 7, 115, 'Dediler: "Musa, sen at, yoksa biz atacağız."'),
(1070, 7, 116, '"Siz atın!," dedi. Onlar atınca, halkın gözünü büyülediler, onları korkuttular ve böylece büyük bir sihir ortaya koydular.'),
(1071, 7, 117, 'Biz de Musa\'ya "Asanı at," diye vahyettik. Nitekim o, onların uydurduklarını toplayıp yutuverdi.'),
(1072, 7, 118, 'Böylece, gerçek gerçekleşti ve yaptıkları boşa çıktı.'),
(1073, 7, 119, 'İşte orada yenildiler ve küçük düşürüldüler.'),
(1074, 7, 120, 'Ve sihirbazlar secdeye kapandılar.'),
(1075, 7, 121, '"Evrenlerin Rabbine inandık," dediler,'),
(1076, 7, 122, '"Musa\'nın ve Harun\'un Rabbine..."'),
(1077, 7, 123, 'Firavun: "Ben size izin vermeden mi ona inandınız? Bu bir plandır. Şehirde bu planı kurdunuz ki halkını oradan çıkarasınız. Ama yakında bileceksiniz!," dedi,'),
(1078, 7, 124, '"Ellerinizi ve ayaklarınızı çaprazlama keseceğim ve sonra topunuzu asacağım!"'),
(1079, 7, 125, 'Dediler ki: "Biz zaten Rabbimize döneceğiz."'),
(1080, 7, 126, '"Rabbimizin işaretleri bize geldiğinde ona inandık diye bizden öc alıyorsun. Rabbimiz, bize dayanma gücü ver ve canımızı müslümanlar olarak al."'),
(1081, 7, 127, 'Firavun\'un halkının elit takımı, "Musa\'yı ve halkını, seni ve tanrılarını bıraksınlar ve yeryüzünde bozgunculuk çıkarsınlar diye mi bırakıyorsun," dediler. (Firavun:) "Kadınlarını yaşatıp oğullarını öldüreceğiz. Biz onlardan çok daha güçlüyüz," dedi.'),
(1082, 7, 128, 'Musa, halkına: "ALLAH\'tan yardım dileyin, sabırla direnin. Yeryüzü ALLAH\'ındır ve onu kullarından dilediğine verir. Sonuç erdemli davrananlarındır," dedi.'),
(1083, 7, 129, '"Sen bize gelmeden önce de, bize geldikten sonra da eziyet edildik," dediler. (Musa da) "Umulur ki Rabbiniz düşmanınızı yok ederek onların yerine sizleri yeryüzüne yerleştirsin ve sonra nasıl davranacağınıza baksın," dedi.'),
(1084, 7, 130, 'Firavun tarafını, öğüt alsınlar diye yıllarca kuraklık ve ürün kıtlığına mahkum ettik.'),
(1085, 7, 131, 'Kendilerine bir iyilik geldiği zaman, "Bunu biz hakkettik," derler, kendilerine bir kötülük dokunduğu zaman ise Musa ve beraberindekileri uğursuzlukla suçlarlardı. Doğrusu, onların uğursuzluğu ALLAH tarafından kararlaştırılır. Ancak çokları bilmezler.'),
(1086, 7, 132, '"Bizi büyülemek için ne kadar mucize (ayet) getirirsen getir, biz sana inanacak değiliz," dediler.'),
(1087, 7, 133, 'Üzerlerine tufan, çekirge, haşerat, kurbağa ve kan gibi ayrı ayrı işaretler gönderdik. Buna rağmen büyüklük taslamaya devam ettiler ve suçlu bir topluluk oldular.'),
(1088, 7, 134, 'Her ne zaman başlarına bir musibet gelse, "Ey Musa, sana verdiği sözden dolayı Rabbine yalvar. Bizi bu felaketlerden kurtarırsan sana inanır ve İsrail oğullarını da seninle beraber yollarız," dediler.'),
(1089, 7, 135, 'Onları söz konusu felaketlerden herhangi bir süre için kurtarınca da sözlerinden dönüyorlardı.'),
(1090, 7, 136, 'Bunun üzerine onlardan öc aldık. Ayetlerimizi yalanlayıp aldırış etmedikleri için onları denizde boğduk'),
(1091, 7, 137, 'Ülkenin verimli kıldığımız doğularına ve batılarına, horlanan ve zayıf düşürülen insanları mirasçı kıldık. Sıkıntılara direndikleri için, Rabbinin İsrail oğullarına verdiği güzel söz gerçekleşti. Firavun ve halkının oluşturduğu yapı ve kurumları da yerle bir ettik.'),
(1092, 7, 138, 'İsrail oğullarını denizden geçirdik. Kendilerine özgü heykellere tapan bir topluluğa rastladılar ve: "Musa, bunların tanrıları gibi bize de bir tanrı yap," dediler. "Siz, gerçekten cahil bir topluluksunuz!," dedi.'),
(1093, 7, 139, '"Bunlar, daldıkları bu şeylerle helak olacaklar ve yaptıkları ise hiç bir temele dayanmıyor."'),
(1094, 7, 140, '"Size ALLAH\'ın dışında bir tanrı mı arayayım? Oysa O, lütfuyla sizi başkalarına tercih etti. "'),
(1095, 7, 141, '"Hani size en kötü azabı uygulayan Firavun\'un taraftarlarından sizi kurtarmıştık. Kadınlarınızı sağ bırakıp çocuklarınızı öldürüyorlardı. Bunda Rabbinizden sizin için büyük bir sınav vardı."'),
(1096, 7, 142, 'Musa ile otuz gece için sözleştik ve ona on gece daha ekledik. Böylece Rabbinin belirlediği vakit kırk geceye tamamlandı. Musa, kardeşi Harun\'a, "Halkım içinde benim yerime geç, doğru davran. Bozgunculuk yapanların yoluna da uyma," dedi.'),
(1097, 7, 143, 'Musa, belirlenen vakitte bize gelince ve Rabbi kendisiyle konuşunca, "Rabbim, bana görün, sana bakayım," dedi. "Beni göremezsin. Ancak şu dağa bak; yerinde durursa o zaman beni göreceksin," dedi. Rabbi, dağa görününce onu paramparça etti ve bunun üzerine Musa kendinden geçti. Ayılınca, "Sen yücesin, sana yöneliyorum. Ben (buna) inananların en önde olanıyım," dedi.'),
(1098, 7, 144, 'Dedi ki: "Musa, mesajlarımla ve seninle konuşmamla seni halkın üzerine seçtim. Sana verdiklerimi al ve şükredenlerden ol."'),
(1099, 7, 145, 'Her şeyin detaylı açıklaması ve öğüt olarak her ne varsa Musa için levhalara yazdık: "Bu öğretilere sıkı sarıl, halkına da söyle ona en güzel biçimde sarılsınlar. Yoldan çıkmışların son durağını size göstereceğim."'),
(1100, 7, 146, 'Yeryüzünde haksız yere büyüklenenleri mucizelerimden çevireceğim. Her türlü mucizeyi de görseler inanmazlar. Doğru yolu görseler onu yol edinmezler. Ama azgınlık yolunu görseler onu yol edinirler. Zira onlar ayetlerimizi yalanladılar ve aldırış etmediler.'),
(1101, 7, 147, 'Ayetlerimizi ve ahiret buluşmasını inkar edenlerin yaptıkları boşunadır. Yaptıklarının karşılığını almayacaklar mı?'),
(1102, 7, 148, 'Musa\'nın halkı kendisinden sonra, süs eşyalarından, böğürmesi olan bir buzağı heykeli yapıp putlaştırdılar. Onun, konuşmaktan ve kendilerine yol göstermekten aciz olduğunu görmediler mi? Onu benimseyerek zalimlerden oldular.'),
(1103, 7, 149, 'Yaptıklarına pişman olup sapmış olduklarını anlayınca da, "Rabbimiz bize acımaz ve bizi bağışlamazsa kaybedenlerden oluruz," dediler.'),
(1104, 7, 150, 'Musa, kızgın ve hayal kırıklığına uğramış olarak halkına döndüğü zaman, "Benden sonra arkamdan ne kötü işler yaptınız! Rabbinizin emrine dayanamadınız mı," dedi, levhaları yere attı, kardeşinin başını tutup kendine doğru çekti. (Kardeşi): "Anamın oğlu, dedi, bu halk benim zayıflığımdan yararlandı, nerdeyse beni öldüreceklerdi. Üzerime vararak düşmanı güldürme, beni bu zalim halkla bir tutma."'),
(1105, 7, 151, '(Musa) dedi ki: "Rabbim, beni ve kardeşimi bağışla. Bize acı. Sen merhametlilerin en merhametlisisin."'),
(1106, 7, 152, 'Buzağıyı putlaştıranlar, Rab\'lerinden bir gazaba ve dünya hayatında bir alçaklığa uğrayacaktır. İftiracıları böyle cezalandırırız.'),
(1107, 7, 153, 'Kötülük işledikten sonra tevbe edip inananlar için Rabbin elbette ondan sonra Bağışlayandır, Rahimdir.'),
(1108, 7, 154, 'Musa\'nın sinirleri yatışınca levhaları aldı. Onda Rab\'lerini sayanlar için bir yol gösterme ve rahmet yazılıydı.'),
(1109, 7, 155, 'Bizimle randevu için, Musa, halkı arasından yetmiş kişi seçti. Kendilerini sarsıntı tutunca: "Rabbim, dileseydin beni ve onları daha önce yok ederdin. İçimizde ki beyinsizlerin yaptıklarından ötürü bizi yok mu edeceksin? Bu, Senin düzenlediğin bir sınav olmalı. Dilediğini onunla saptırır, dilediğini de onunla doğru yola iletirsin. Sahibimiz sensin; bizi bağışla bize acı. Sen en iyi Bağışlayansın," dedi,'),
(1110, 7, 156, '"Bu dünyada da bizim için iyilik yaz, ahirette de... Biz sana yöneldik." Dedi ki: "Azabım dilediğim kimseye dokunacaktır. Rahmetim ise her şeyi kapsamıştır; onu özellikle: erdemli bir hayat sürenlere, zekatı verenlere ve ayetlerimize inananlara yazacağım."'),
(1111, 7, 157, 'Nitekim onlar (vakti gelince) yanlarındaki Tevrat ve İncil\'de yazılı buldukları o elçiyi, o ümmi (Tevrat ve İncil okumamış) peygamberi izlerler. (O peygamber) onlara iyiliği emreder ve onları kötülükten meneder; onlara güzel şeyleri helal, pis şeyleri de haram kılar; üzerlerindeki ağırlıkları ve onlara vurulan prangaları kaldırıp atar. Ona inananlar, ona saygı duyanlar, ona yardım edenler, kendisiyle birlikte indirilen ışığı izleyenler başarıya ulaşanlardır.'),
(1112, 7, 158, 'De ki: "Ey halk, ben, hepiniz için, göklerin ve yerin egemenliğine sahip olan ALLAH\'ın elçisiyim. O\'ndan başka tanrı yok. Diriltir, öldürür." ALLAH\'a ve ümmi olan elçisine inanın, nitekim o da ALLAH\'a ve sözlerine inanmaktadır. Ona uyun ki doğruyu bulasınız.'),
(1113, 7, 159, 'Musa\'nın halkından bir topluluk var ki gerçeği gösterirler ve onunla adalet ederlerdi.'),
(1114, 7, 160, 'Onları on iki kabile halinde topluluklara ayırdık. Halkı kendisinden su istediği zaman Musa\'ya, "Asan ile taşa vur," diye vahyettik. Ondan on iki göze fışkırdı ve her kabile içeceği yeri bildi. Ayrıca onları bulutlarla gölgelendirdik ve üzerlerine menna ve bıldırcın indirdik: "Size verdiğim rızıklardan yeyin." Onlar bize haksızlık etmiyorlardı, kendi kendilerine haksızlık ediyorlardı.'),
(1115, 7, 161, 'Hatırla ki kendilerine, "Bu şehirde oturun, oradan dilediğiniz gibi yiyin, dostça konuşun ve kapıdan alçak gönüllü olarak girin ki hatalarınızı bağışlayalım. İyilik yapanlara fazlasını vereceğiz," denildiğinde,'),
(1116, 7, 162, 'İçlerindeki zalimler kendilerine emredileni kendilerine emredilmeyenle değiştirdiler. Biz de haksızlık etmelerinden ötürü üzerlerine gökten bir felaket gönderdik.'),
(1117, 7, 163, 'Onlara deniz kenarındaki topluluktan sor. Hani Cumartesi çalışma yasağını çiğniyorlardı. Cumartesiye uydukları gün balıkları onlara akın akın geliyordu; ancak Cumartesiyi uygulamadıkları gün onlara balık gelmiyordu. Yoldan çıktıkları için onları böyle sınıyorduk.'),
(1118, 7, 164, 'İçlerinden bir topluluk: "ALLAH\'ın helak edeceği veya çetin bir biçimde azaplandıracağı bir halka ne diye öğüt veriyorsunuz," dedi. Dediler ki: "Rabbinizden özür dileyin," belki dinleyip kurtulurlar.'),
(1119, 7, 165, 'Kendilerine hatırlatılanları önemsemeyip unutunca, kötülüklerle mücadele edenleri kurtardık; haksızlık edenleri de yoldan çıkmalarına karşılık olarak feci bir azap ile yakaladık.'),
(1120, 7, 166, 'Kendilerine yasaklananlara uymayınca da onlara, "Aşağılık maymunlar olun!," dedik.'),
(1121, 7, 167, 'Ayrıca Rabbin, diriliş gününe kadar onlara cezaların en kötüsünü verecek kimseler göndereceğini bildirdi. Rabbin çabuk sonuçlandırandır ve elbette O, Bağışlayandır, Rahimdir.'),
(1122, 7, 168, 'Onları yeryüzünde topluluklara ayırdık. Bir kısmı iyi, diğer bir kısmı ise aşağılık durumdaydı. Dönerler diye onları iyilikler ve kötülüklerle sınadık.'),
(1123, 7, 169, 'Onların yerine, kitaba mirasçı olan başka nesiller geçti. Ancak, "Nasıl olsa bağışlanacağız," diyerek aşağılık ve geçici menfaatleri seçtiler. Kendilerine, benzeri geçici menfaatler gelse yine seçerler. Onlardan, kitaba bağlı kalacaklarına ve ALLAH hakkında ancak gerçeği söyleyeceklerine dair söz alınmamış mıydı? Onu okuyup öğrenmediler mi? Erdemliler için ahiret yurdu daha hayırlıdır. Anlamıyor musunuz?'),
(1124, 7, 170, 'Kitaba sarılanlar ve namaz kılanlara gelince, iyiliğe çalışanları ödülsüz bırakmayız.'),
(1125, 7, 171, 'Dağı bir şemsiye gibi üzerlerinde sarsmıştık. Öyle ki tepelerine düşeceğini sanmışlardı: "Size verdiğime sımsıkı sarılın. Kurtulabilmeniz için içeriği üzerinde düşünün."'),
(1126, 7, 172, 'Rabbin, Adem oğullarının bellerinden soylarını çıkarırken onları kendi kendilerine tanık tutar: "Ben, Rabbiniz değil miyim?" "Evet, tanıklık ediyoruz," derler. Böylece diriliş günü, "Biz bundan habersizdik," diyemezsiniz'),
(1127, 7, 173, 'Yahut, "Atalarımız önceden ortak koştu ve biz de onlardan sonra gelen soylarıyız, bizi bidat ve hurafelere dalanlardan dolayı mı yok edeceksin," diyemezsiniz.'),
(1128, 7, 174, 'Ayetleri böyle açıklıyoruz ki (bize) dönebilsinler.'),
(1129, 7, 175, 'Kendisine ayetlerimizi verdiğimiz, ancak onlardan sıyrılmış-geçmiş kimsenin ne duruma düştüğünü anlat onlara. Şeytan onu saptırıncaya kadar izlemişti.'),
(1130, 7, 176, 'Dileseydik onu ayetlerimizle yükseltirdik. Fakat o, toprağa yapışmakta direndi ve hevesinin ardına takıldı. Onun durumu, üstüne varsan da bıraksan da dili dışarda soluyan huysuz bir köpeğin durumuna benzer. Ayetlerimizi yalanlayan toplulukların durumu işte böyledir. Bu olayı aktar, olur ki düşünürler.'),
(1131, 7, 177, 'Ayetlerimizi yalanlayan ve yalnızca kendi kendilerine haksızlık eden topluluğun örnekliği ne kötüdür!'),
(1132, 7, 178, 'ALLAH kimi doğruya iletirse, doğruyu bulan odur. Kimi de saptırırsa, kaybedenler de onlardır.'),
(1133, 7, 179, 'İnsanlardan ve cinlerden çok sayıda kişiyi cehenneme mahkum ettik. Kalpleri var, fakat kavrayamazlar; gözleri var, fakat görmezler; kulakları var, fakat işitmezler. Onlar, çiftlik hayvanları gibidir, hatta daha da kötü... Ve onlar, olup bitenden habersizdirler.'),
(1134, 7, 180, 'En güzel isimler ALLAH\'ındır; öyleyse O\'nu onlarla çağırın. O\'nun isimlerini tahrif edenleri bırakın; yaptıklarının cezasını çekeceklerdir.'),
(1135, 7, 181, 'Yarattıklarımızın arasında bir topluluk var ki gerçek ile yol gösterirler ve yine onunla hakkı gözetirler.'),
(1136, 7, 182, 'Ayetlerimizi yalanlayanlara gelince, onlar farketmeden onları yavaş yavaş sonlarına yaklaştıracağız.'),
(1137, 7, 183, 'Hatta onlara umut veririm. Planım çetindir.'),
(1138, 7, 184, 'Düşünmezler mi? Arkadaşlarında hiç bir delilik yoktur. O, ancak apaçık bir uyarıcıdır.'),
(1139, 7, 185, 'Göklerin ve yerin egemenliğine ve ALLAH\'ın yarattığı şeylere bakmazlar mı? Son anlarının yaklaşmış olabileceğini hiç düşünmezler mi? Bundan sonra artık hangi hadise (söze) inanırlar?'),
(1140, 7, 186, 'ALLAH\'ın saptırdığı kimseler için yol gösterici bulunmaz. Onları azgınlıkları içinde bocalar durur halde bırakır.'),
(1141, 7, 187, 'Sana o saatin (dünyanın sonunun) ne zaman geleceğini soruyorlar. "O\'nun bilgisi Rabbimin yanındadır," de. Onu vakti gelince O\'ndan başkası ortaya çıkarmaz. Göklere ve yere ağır gelen o saat size ansızın gelecektir. Sanki ondan haberdar imişsin gibi sana soruyorlar. "Onun bilgisi ALLAH\'ın yanındadır," de. Fakat insanların çoğu bilmez.'),
(1142, 7, 188, 'De ki: "ALLAH\'ın dilediğinden başka ben kendime ne bir yarar ne de bir zarar veremem. Gizliyi bilseydim mal varlığımı arttırırdım, bana kötülük de dokunmazdı. Ben ancak inanan bir topluma bir müjdeci ve uyarıcıyım."'),
(1143, 7, 189, 'O sizi bir tek nefisten (aynı genetik özellikten) yarattı. Ondan da eşini yarattı ki dinginlik bulsun. Eşine yaklaşınca, hafif bir yükle yüklendi ve onunla gezindi. Yükü ağırlaşınca her ikisi Rab\'leri ALLAH\'a: "Bize kusursuz bir çocuk verirsen şükredenlerden olacağız," diye yalvardılar.'),
(1144, 7, 190, 'Onlara kusursuz bir çocuk verince, O\'nun kendilerine verdiği bu hediyeyle ilgili olarak O\'na ortaklar koşmaya başladılar. ALLAH onların ortak koştukları her şeyden çok yücedir.'),
(1145, 7, 191, 'Bir şey yaratamıyan, aksine yaratılmış olan şeyleri mi eş koşuyorlar?'),
(1146, 7, 192, 'Putlar, ne onlara yardım edebilir, ne de kendilerine yardım edebilirler.'),
(1147, 7, 193, 'Onları doğruya çağırsanız size uymazlar. Onları ha çağırmışsınız, ha sessiz kalmışsınız, sizin için birdir.'),
(1148, 7, 194, 'ALLAH\'ın dışında çağırdıklarınız, sizin gibi kullardır. Haydi onları çağırın da size cevap versinler, sözünüzde doğru iseniz!'),
(1149, 7, 195, 'Üzerinde yürüyecekleri ayakları mı var? Tutmaları için elleri mi var? Görmeleri için gözleri mi var? İşitmeleri için kulakları mı var? De ki, "Ortaklarınızı çağırın ve benim için plan kurun. Hiç durmayın!"'),
(1150, 7, 196, '"Biricik Sahibim (Velim), kitabı indiren ALLAH\'tır. O, dürüst insanları korur."'),
(1151, 7, 197, '"O\'nun dışında çağırdıklarınız ne size yardım edebilirler, ne de kendilerine yardım edebilirler."'),
(1152, 7, 198, 'Onları hidayete çağırsan işitmezler. Onların sana baktığını görürsün; fakat onlar görmezler.'),
(1153, 7, 199, 'Affedici ol, toleransı öğütle ve cahillere aldırış etme.'),
(1154, 7, 200, 'Şeytandan ne zaman kötü bir düşünce zihnini tırmalarsa, ALLAH\'a sığın; O İşitendir, Bilendir.'),
(1155, 7, 201, 'Erdemlilere her ne zaman şeytandan karanlık bir öneri ulaşsa hemen hatırlarlar. Böylece hemen görürler.'),
(1156, 7, 202, '(Şeytanlar) kardeşlerini ise azgınlığa sürüklerler ve bundan hiç geri durmazlar.'),
(1157, 7, 203, 'Kendilerine bir mucize getirmediğin zaman: "Mucize isteseydin ne olurdu," derler. De ki: "Ben, ancak Rabbimden bana vahyedilene uyuyorum." Bunlar, Rabbinizden aydınlatmalardır, inanan bir toplum için bir hidayet ve rahmettir.'),
(1158, 7, 204, 'Kuran okunduğu zaman, onu dinleyip kulak verin ki merhamet edilesiniz.'),
(1159, 7, 205, 'Rabbini gönülden yalvararak, gizlice ve sessizce sabah akşam an; gafillerden olma.'),
(1160, 7, 206, 'Rabbinin yanındakiler, ona kulluk etmekten kaçınıp büyüklenmezler, O\'nu yüceltirler ve O\'na secde ederler.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'Savaş ganimetleri hakkında senden soruyorlar. De ki: "Ganimetler, ALLAH\'ın ve elçisinindir." ALLAH\'ı dinleyin, aranızı düzeltin. İnanıyorsanız, ALLAH\'a ve elçisine uyun.'),
(1162, 8, 2, 'İnananlar öyle kimselerdir ki, ALLAH anıldığında yürekleri titrer, O\'nun ayetleri kendilerine okunduğunda inançları artar ve Rab\'lerine güvenirler.'),
(1163, 8, 3, 'Onlar ki namazı gözetirler ve kendilerine verdiğimiz rızıklardan yardım olarak verirler.'),
(1164, 8, 4, 'Böyleleri gerçek inananlardır. Onlar için Rab\'leri yanında dereceler, bağışlanma ve tükenmez rızık vardır.'),
(1165, 8, 5, 'Nitekim Rabbin seni belli bir planın gerçekleşmesi için evinden çıkarmıştı. İnananlardan bir grup bundan hoşlanmamıştı.'),
(1166, 8, 6, 'Gerçek ortaya çıkmasına rağmen, göz göre göre ölüme sürükleniyorlarmış gibi seninle onu tartışıyorlardı.'),
(1167, 8, 7, 'ALLAH iki gruptan birisini (yenmeyi) size söz vermişti; siz ise, güçsüz olanıyla karşılaşmayı istiyordunuz. Oysa ALLAH kelimeleriyle gerçeği gerçekleştirmek ve kafirlerin ardını kesmek diliyor.'),
(1168, 8, 8, 'Ki suçlular hoşlanmasa da hakkı gerçekleştirsin ve haksızlığı ortadan kaldırsın.'),
(1169, 8, 9, 'Sizi kurtarması için Rabbinize yalvarıyordunuz, ve O da, "Ben sizi birbiri ardınca bin melekle destekleyeceğim," diye size karşılık vermişti.'),
(1170, 8, 10, 'ALLAH bunu, ancak bir müjde olsun ve kalplerinizi yatıştırsın diye yaptı. Zafer yalnız ALLAH\'tandır. ALLAH Üstündür, Bilgedir.'),
(1171, 8, 11, 'Kendisinden bir güvenlik olarak sizi bir uyuklama bürüyüp yatıştırıyordu. (Susuzluğun oluşturduğu bunalımdan) sizi temizlemek, şeytanın pisliğini (umutsuzluğu) sizden savmak, kalbinizi ikna etmek ve ayaklarınızı pekiştirmek için üzerinize gökten su indiriyordu.'),
(1172, 8, 12, 'Rabbin meleklere, "Sizinle beraberim, inananları destekleyin. İnkarcıların yüreğine korku salacağım. Vurun boyunlara, vurun onların her parmağına," diye vahyediyordu.'),
(1173, 8, 13, 'Çünkü onlar ALLAH\'a ve elçisine karşı aktif olarak savaştılar. Kim ki ALLAH ve elçisiyle savaşırsa elbette ALLAH\'ın cezası çetindir.'),
(1174, 8, 14, 'İşte bunu tadın. Kafirler için bir de ateş azabı var.'),
(1175, 8, 15, 'Ey inananlar, size karşı düşmanca harekata geçen kafirlerle karşılaştığınızda, onlara arkanızı dönmeyin.'),
(1176, 8, 16, 'Kim o gün, savaş taktiği veya başka bir birliğe katılma amacınının dışında sırtını dönüp kaçarsa ALLAH\'tan bir gazaba uğramıştır ve yeri de cehennemdir. Ne kötü bir duraktır.'),
(1177, 8, 17, 'Onları siz öldürmediniz, fakat ALLAH öldürdü. Attığın zaman da sen atmıyordun; ALLAH atıyordu. Fakat böylece inananları güzel bir sınavla ödüllendirir. ALLAH İşitendir, Bilendir.'),
(1178, 8, 18, 'Bu yolla, ALLAH, kafirlerin planını bozar.'),
(1179, 8, 19, 'Zafer istiyorsanız (ey kafirler), işte size zafer geldi! Saldırmaktan vazgeçerseniz sizin için daha iyi olur. Dönerseniz biz de döneriz ve ordunuz sayıca çok da olsa sizden hiç bir şeyi savamaz. ALLAH inananlarla beraberdir.'),
(1180, 8, 20, 'Ey inananlar, ALLAH\'a ve elçisine uyunuz. İşittiğiniz halde ondan yüz çevirmeyiniz.'),
(1181, 8, 21, 'İşitmedikleri halde, "İşittik," diyenler gibi olmayın.'),
(1182, 8, 22, 'ALLAH yanında, yaratıkların en kötüsü, akıllarını kullanmayan sağır ve dilsizlerdir.'),
(1183, 8, 23, 'ALLAH onlarda bir iyilik olduğunu görseydi, onlara işittirirdi. Onlara işittirseydi bile yine yüz çevirerek dönerlerdi.'),
(1184, 8, 24, 'Ey inananlar, size hayat verecek şeylere çağırdığında ALLAH\'a ve elçisine yanıt verin. Bilin ki ALLAH kişi ile kalbi arasına girer ve O\'nun huzuruna toplanacaksınız.'),
(1185, 8, 25, 'Sizden sadece zalimlere dokunmayacak olan fitneden sakının. Bilesiniz ki ALLAH\'ın cezası çetindir.'),
(1186, 8, 26, 'Sayıca az olduğunuz, yeryüzünde ezilip horlandığınız ve insanların sizi kapıp kaçıracaklarından korktuğunuz zamanları hatırlayın. O size barınak sağladı, sizi yardımıyla destekledi ve güzel şeylerden size rızıklar verdi ki şükredesiniz'),
(1187, 8, 27, 'Ey inananlar, ALLAH\'a ve elçisine ihanet etmeyin; sorumluklarınıza bile bile ihanet etmiş olursunuz.'),
(1188, 8, 28, 'Bilesiniz ki mallarınız ve evlatlarınız sizin için bir sınavdır ve ALLAH\'ın yanında büyük ödül vardır.'),
(1189, 8, 29, 'Ey inananlar, ALLAH\'ı dinlerseniz, size doğruyu yanlıştan ayıracak anlayışı verir, kötülüklerinizi örter ve sizi bağışlar. ALLAH Büyük Lütuf Sahibidir.'),
(1190, 8, 30, 'İnkarcılar seni etkisiz hale getirmek, veya seni öldürmek ya da seni sürmek için planlar yapıyorlardı. Onlar plan kuruyorlarsa ALLAH da plan kuruyor. ALLAH plan kuranların en ustasıdır'),
(1191, 8, 31, 'Ayetlerimizi dinledikleri zaman, "İşittik," diyorlardı, "İstesek biz de bunun bir benzerini getiririz. Bu, geçmişlerin efsanelerinden başka bir şey değil."'),
(1192, 8, 32, 'Hatta, "Rabbimiz, bu senden gelen bir gerçek ise, üstümüze gökten taşlar yağdır, veya başımıza acıklı bir azap getir," diyorlardı.'),
(1193, 8, 33, 'Sen onların arasında bulunduğun sürece ALLAH onlara azap edecek değildir. Onlar, bağışlanma dilerlerken de ALLAH onları cezalandıracak değildir.'),
(1194, 8, 34, 'Başkalarını Kutsal Mescid\'den menederlerken neden ALLAH\'ın azabını haketmesinler? Oysa onlar onun koruyucuları değiller. Onun gerçek koruyucuları inananlardır; ancak çokları bunu bilmez.'),
(1195, 8, 35, 'Onların, Kabe\'deki namazları hile ve menetmekten başka bir şey değil. İnkarınızdan dolayı azabı tadın.'),
(1196, 8, 36, 'İnkar edenler, insanları ALLAH yolundan menetmek için paralarını harcarlar ve daha da harcamaya devam edecekler. Fakat bu, sonradan kendileri için bir üzüntü kaynağı olacak ve ardından yenilecekler. İnkar edenler cehenneme sürüleceklerdir.'),
(1197, 8, 37, 'Böylece ALLAH kötüyü iyiden ayırdeder, kötüleri üst üste koyup topluca yığar ve cehenneme yollar. İşte kaybedenler onlardır.'),
(1198, 8, 38, 'İnkar edenlere söyle: "Son verirlerse geçmişte yaptıkları bağışlanacaktır. Dönerlerse, daha öncekilerin sünneti (yasası) geçerlidir."'),
(1199, 8, 39, 'Baskı ve zulmu ortadan kaldırıncaya ve dini sadece ALLAH\'a ait kılıncaya dek onlarla savaşın. Düşmanlığa son verirlerse, elbette ALLAH yaptıklarınızı Görür.'),
(1200, 8, 40, 'Dönerlerse, bilin ki sahibiniz ALLAH\'tır. O en iyi Mevla (Egemen/Dost/Koruyucu) ve en iyi Yardımcıdır.'),
(1201, 8, 41, 'Ayırım gününde, iki ordunun karşılaştığı günde kulumuza indirdiğimize ve ALLAH\'a inanıyorsanız, bilin ki elinize geçen her ganimetin beşte biri ALLAH\'ın ve Elçisinindir. Bu pay, akrabalar, öksüzler, yoksullar ve yolda kalmışların hakkıdır. ALLAH herşeye Güç Yetirendir.'),
(1202, 8, 42, 'Hani siz vadinin beri yamacında, onlar da vadinin öte yamacında mevzilenmişti. Kervan da sizden daha aşağıda idi. Bu karşılaşmayı siz kararlaştırsaydınız karşılaşma konusunda anlaşmazlığa düşecektiniz. Oysa ALLAH, yapılması önceden planlanmış bir işi gerçekleştirmekte idi. Böylece, yok edilen, apaçık bir delille yok edilsin, yaşayan da apaçık bir delille yaşatılsın. ALLAH İşitendir, Bilendir.'),
(1203, 8, 43, 'ALLAH onları rüyanda sana az gösteriyordu. Onları sana çok gösterseydi, dağılacak ve o konuda çekişip duracaktınız. Fakat ALLAH (sizi) kurtardı. O, göğüslerin özünü bilendir'),
(1204, 8, 44, 'Zamanı gelip kendileriyle karşılaştığınızda sizin gözlerinizde onları az gösteriyor, onların da gözlerinde sizi az gösteriyordu ki ALLAH önceden planlanmış işi gerçekleştirsin. İşler, sonunda ALLAH\'a döner.'),
(1205, 8, 45, 'Ey inananlar bir ordu ile karşılaştığınızda dayanın ve ALLAH\'ı çokça anın ki başarasınız.'),
(1206, 8, 46, 'ALLAH\'a ve elçisine uyun, çekişmeyin, yoksa zayıflayıp gücünüzü yitirirsiniz. Güçlüklere karşı direnin. Elbette ALLAH sabredenlerle beraberdir.'),
(1207, 8, 47, 'ALLAH\'ın yoluna engel olmak amacıyla, halka gösteriş yaparak yurtlarından çıkan o kendini beğenmişler gibi olmayın. ALLAH yaptıklarını çepeçevre Kuşatır.'),
(1208, 8, 48, 'Şeytan, işlerini onlara süslemiş ve: "Bu gün halktan kimse sizi yenemez, ben sizin yanınızdayım," demişti. İki ordu yüz yüze gelince de, topukları üzerine geri dönüp, "Sizinle bir ilgim yok, sizin görmediğinizi görüyorum ve ALLAH\'tan korkarım. ALLAH\'ın cezası çetindir," demişti.'),
(1209, 8, 49, 'İkiyüzlüler ve kalplerinde hastalık bulunanlar, "Bunları dinleri aldatmış," diyorlardı. Kim ALLAH\'a güvenirse, kuşkusuz ALLAH Üstündür, Bilgedir.'),
(1210, 8, 50, 'İnkar edenlerin canlarını melekler alırken bir görseydin! Yüzlerine ve arkalarına (saldıran ve kaçan düşmanlarınıza) vuruyorlardı: "Tadın yakıcı azabı!"'),
(1211, 8, 51, '"Bu, ellerinizin yaptığına karşılıktır. ALLAH elbette kullara zalim değildir."'),
(1212, 8, 52, 'Tıpkı Firavun\'un yandaşlarının ve onlardan öncekilerin gidişi gibi... ALLAH\'ın ayetlerini inkar ettiler. Günahlarından ötürü ALLAH da onları yakalayıp cezalandırdı. ALLAH Güçlüdür, cezası çetindir.'),
(1213, 8, 53, 'Zira, bir topluluk kendilerini değiştirmedikçe, ALLAH onlara verdiği nimetleri değiştirecek değildir. ALLAH İşitendir, Bilendir.'),
(1214, 8, 54, 'Örneğin, Firavun\'un yandaşları ve onlardan öncekiler gibi. Rab\'lerinin ayetlerini yalanladılar. Biz de günahlarından ötürü onları yıkıma uğrattık. Firavun\'un yandaşlarını suda boğduk. Hepsi de zalim idiler.'),
(1215, 8, 55, 'ALLAH yanında yaratıkların en kötüsü kafirlerdir; onlar inanmazlar.'),
(1216, 8, 56, 'Onlar, sen kendileriyle anlaşma yaptığın halde her defasında anlaşmalarını bozanlardır. Onlar erdemli davranmazlar.'),
(1217, 8, 57, 'Savaşta onları yakalarsan, onları arkalarındakilere ibret olacak şekilde darmadağın et. Belki ders alırlar.'),
(1218, 8, 58, 'Bir topluluğun ihaneti seni endişelendiriyorsa, sen de aynı şekilde (diplomatik ilişkiyi kesip) at. Kuşkusuz ALLAH hainleri sevmez.'),
(1219, 8, 59, 'Kafirler, kurtulduklarını sanmasınlar. Kaçamazlar.'),
(1220, 8, 60, 'Onlar için elinizden gelen kuvvet ve atlı birlikler (savaş araçları) hazırlayıp seferber edin. Böylece onlarla ALLAH\'ın düşmanlarını, düşmanlarınızı ve onlardan başka bilmediğiniz, ancak ALLAH\'ın bildiği kimseleri caydırırsınız. ALLAH yolunda ne harcarsanız size tam olarak ödenir ve hiç haksızlığa uğratılmazsınız.'),
(1221, 8, 61, 'Onlar barışa eğilim gösterirlerse sen de ona eğilim göster ve ALLAH\'a güven. O, İşitendir, Bilendir.'),
(1222, 8, 62, 'Seni aldatmak isterlerse, ALLAH sana yeter. O ki seni yardımıyla ve inananlarla destekledi.'),
(1223, 8, 63, 'Ve onların kalplerini uzlaştırdı. Sen, yeryüzünde bulunanların hepsini harcasaydın bile kalplerini uzlaştıramazdın. Ancak, onların kalplerini ALLAH uzlaştırdı. O, Üstündür, Bilgedir.'),
(1224, 8, 64, 'Ey peygamber, ALLAH sana ve seni izleyen inananlara yeter.'),
(1225, 8, 65, 'Ey peygamber, inananları savaşa özendir. Sizden dirençli yirmi kişi olsa, iki yüz kişiyi yenebilir. Sizden yüz kişi, kafirlerden bin kişiyi yenebilir. Çünkü onlar, anlamayan bir topluluktur.'),
(1226, 8, 66, 'Ancak, ALLAH sizde zayıflık bulunduğunu bildiğinden şimdi sizden yükü hafifletti. Sizden dirençli yüz kişi olsa, iki yüz kişiyi yenebilir. Sizden bin kişi olsa, ALLAH\'ın izniyle iki bin kişiyi yenebilir. ALLAH sabredip direnenlerle beraberdir.'),
(1227, 8, 67, 'Hiç bir peygambere, yeryüzünde savaşa katılmaksızın esirler edinmesi yakışmaz. Siz bu dünyanın geçici malını istiyorsunuz; ALLAH ise (sizin için) ahireti ister. ALLAH Üstündür, Bilgedir.'),
(1228, 8, 68, 'ALLAH\'ın daha önce koyduğu bir yasa olmasaydı aldıklarınızdan ötürü size büyük bir azap dokunacaktı.'),
(1229, 8, 69, 'Artık ele geçirdiğiniz ganimetleri helal ve temiz olarak yiyebilirsiniz. ALLAH\'ı dinleyin. ALLAH Bağışlayandır, Rahimdir.'),
(1230, 8, 70, 'Ey peygamber, elinizde bulunan esirlere de ki: "ALLAH kalbinizde bir iyilik bilse, yitirdiklerinizden daha iyisini size verir ve sizi bağışlar. ALLAH Bağışlayandır, Rahimdir.'),
(1231, 8, 71, 'Yok, sana ihanet etmek istiyorlarsa, zaten daha önce ALLAH\'a ihanet etmişlerdi. Bunun üzerine onları yenik düşürdü. ALLAH Bilendir, Bilgedir.'),
(1232, 8, 72, 'İnananlar, ALLAH yolunda göç edenler, mallarıyla ve canlarıyla cihad edenler ve onları barındırıp onlara yardım edenler birbirlerinin dostudur. İnandığı halde sizinle birlikte göç etmeyenler göç edinceye kadar onlardan sorumlu değilsiniz. Ancak onlar, din bağından ötürü sizden yardım isterlerse, aranızda anlaşma bulunan bir topluma karşı olmaması koşuluyla kendilerine yardım etmelisiniz. ALLAH yaptıklarınızı Görendir.'),
(1233, 8, 73, 'İnkar edenler de birbirlerinin dostudur. Bu emirleri uygulamazsanız, yeryüzünde kaos ve büyük bir fesad olur.'),
(1234, 8, 74, 'İnananlar, ALLAH yolunda göç edip çaba gösterenler ve onları barındırıp yardım edenler, işte bunlar gerçek inananlardır. Onlar için bağışlanma ve bol rızık vardır.'),
(1235, 8, 75, 'Sonradan inananlar, göç edenler ve sizinle birlikte çaba gösterenler de sizdendir. ALLAH\'ın Kitabına göre akrabalar birbirlerine önceliklidir. ALLAH herşeyi Bilendir');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'Bu, ALLAH ve elçisinden, kendileriyle anlaşma yapmış bulunduğunuz putperestlere bir ültimatomdur:'),
(1237, 9, 2, 'Yeryüzünde dört ay daha dolaşın, bilin ki siz ALLAH\'ı aciz bırakamazsınız ve ALLAH inkarcıları rezil eder.'),
(1238, 9, 3, 'Bu, aynı zamanda, ALLAH ve elçisinden tüm halka, büyük hac günü yayımlanmış bir bildiridir: ALLAH putperestlerden uzaktır, elçisi de... Tevbe ederseniz sizin için daha iyidir. Dönerseniz, bilin ki siz ALLAH\'ı aciz bırakamazsınız. İnkarcılara acı birazabı müjdele.'),
(1239, 9, 4, 'Ancak, kendileriyle yaptığınız anlaşmanın koşullarına eksiksiz uyan ve size karşı başkalarıyla işbirliğinde bulunmayan putperestlerin anlaşmasını tanıdığınız süreye kadar uygulayın. ALLAH erdemlileri sever.'),
(1240, 9, 5, 'Kutsal aylar çıkınca, (hâlâ barışa yanaşmıyorlarsa) o putperestleri yakaladığınız yerde öldürün. Onları yakalayın, onları kuşatın ve her hareketlerini izleyin. Tevbe edip namaz kılar ve zekat verirlerse yollarını serbest bırakın. ALLAH Bağışlayandır, Rahimdir.'),
(1241, 9, 6, 'Putperestlerden biri sizden geçiş emniyeti dilerse ona koruma sağla ki ALLAH\'ın sözünü işitsin; sonra onu kendisinin güvenlik bölgesine ulaştır. Çünkü onlar bilgisiz bir topluluktur.'),
(1242, 9, 7, 'Putperestlerin ALLAH ve elçisi yanında nasıl bir anlaşması olabilir ki? Kutsal Mescid\'de sizinle anlaşma yapmış olanlar hariç. Onlar sizin haklarınızı gözettikleri sürece siz de onların haklarını gözetin. ALLAH erdemlileri sever.'),
(1243, 9, 8, 'Nasıl olabilir ki? Size üstün gelselerdi ne bir akrabalık ilişkisini gözetirlerdi, ne de bir anlaşmayı... Ağızlarıyla sizi pasifleştirecek hoş sözler söylerler, fakat kalpleri tersini ister. Çokları yoldan çıkmıştır.'),
(1244, 9, 9, 'ALLAH\'ın ayetlerini az bir fiyata satıp onun yolundan saptılar. Yaptıkları ne kadar da kötüdür.'),
(1245, 9, 10, 'İnanmış birisi hakkında ne bir akrabalık bağı ne de bir anlaşma gözetmezler; saldırganlar ve haddi aşanlar onlardır.'),
(1246, 9, 11, 'Tevbe ederlerse, namaz kılar ve zekat verirlerse din kardeşleriniz olurlar. Bilenlere ayetleri böyle açıklarız.'),
(1247, 9, 12, 'Anlaşma yaptıktan sonra andlarını bozar ve dininize saldırırlarsa, o inkarcılığın önderleriyle savaşın; çünkü onların andı artık geçersizdir. Belki vazgeçerler.'),
(1248, 9, 13, 'Andlarını bozan, elçiyi sürmeye yeltenen ve sizinle (savaşı) ilk defa başlatan topluluğa karşı savaşmıyacak mısınız? Yoksa onlardan korkuyor musunuz? İnanıyorsanız asıl çekinmeniz gereken ALLAH\'tır.'),
(1249, 9, 14, 'Onlarla savaşın ki, ALLAH ellerinizle onları cezalandırıp rezil etsin, sizi zafere ulaştırsın ve inanan toplumun göğsünü ferahlatsın,'),
(1250, 9, 15, 'Yüreklerinin öfkesini gidersin. ALLAH dilediğinin tevbesini kabul eder. ALLAH Bilendir, Bilgedir.'),
(1251, 9, 16, 'ALLAH, içinizden kendisi uğrunda cihad edenleri, ALLAH\'tan, elçisinden ve inananlardan başkasını yakın dost edinmeyenleri bilip ayırmadan bırakılacağınızı mı sandınız? ALLAH yaptıklarınızı Haber Alır.'),
(1252, 9, 17, 'Putperestler, kendi inkarlarına bizzat tanık oldukları halde, ALLAH\'ın mescidlerini sıkça ziyaret etmemeli. İşleri boşa çıkmıştır. Onlar ateşte ebedidirler.'),
(1253, 9, 18, 'ALLAH\'ın mescidlerini, ancak ALLAH\'a ve ahiret gününe inananlar, namazı gözetenler, zekatı verenler ve ALLAH\'tan başkasından korkmayanlar sıkça ziyaret ederler. Onlar, doğru yolu bulanlardandır.'),
(1254, 9, 19, 'Hacılara su verme ve Kutsal Mescid\'i ziyarete hazır bulundurma işini, ALLAH\'a ve ahiret gününe inanma, namazı gözetme, zekat verme ve ALLAH yolunda cihad etme ile bir mi tutuyorsunuz? ALLAH yanında onlar bir değildir. ALLAH zalim toplumu doğruya ulaştırmaz.'),
(1255, 9, 20, 'İnananlar, göç edenler, paralarıyla canlarıyla ALLAH yolunda çaba gösterenler için ALLAH yanında daha büyük bir derece vardır. Onlar kazananlardır.'),
(1256, 9, 21, 'Rableri onlara, kendisinden bir rahmet, bir onama ve içinde ebedi nimetlerin bulunduğu cennetleri müjdeler.'),
(1257, 9, 22, 'Orada ebedi kalıcılar. Büyük ödül ALLAH katındadır.'),
(1258, 9, 23, 'Ey inananlar, inkarı imana tercih ettikleri taktirde ana babalarınızı ve kardeşlerinizi dost edinmeyin. Kim onları dost edinirse onlar zalimlerdir.'),
(1259, 9, 24, 'De ki: "Ana babalarınız, çocuklarınız, kardeşleriniz, eşleriniz, aileniz, kazandığınız paralar, bozuk gitmesinden korktuğunuz iş ve hoşlandığınız evler ALLAH ve elçisinden ve O\'nun yolunda çaba göstermekten daha sevgili ise, ALLAH emrini getirinceye kadar bekleyiniz." ALLAH yoldan çıkmış toplumları doğruya iletmez.'),
(1260, 9, 25, 'ALLAH bir çok durumda size yardım etti. Huneyn günü sayısal çokluğunuz sizi böbürlendirmişti. Fakat sayınızın size hiç bir yararı da olmamıştı. Nitekim, tüm genişliğine rağmen yeryüzü size dar gelmiş ve sonunda dönüp kaçmıştınız.'),
(1261, 9, 26, 'Sonra ALLAH, elçisi ve inananların üzerine tarafından güven ve rahatlık indirdi, ek olarak, görmediğiniz ordular indirdi ve böylece kafirleri cezalandırdı. Kafirlerin cezası işte budur.'),
(1262, 9, 27, 'Nitekim ALLAH, sonunda dilediğinin tevbesini kabul eder. ALLAH Bağışlayandır, Rahimdir.'),
(1263, 9, 28, 'Ey inananlar, putperestler pistir; bu yıldan sonra Kutsal Mescid\'e yaklaşmasınlar. Yoksulluğa düşmekten korkuyorsanız, (bilin ki) ALLAH dilerse sizi kendi lütfuyla zengileştirecektir. ALLAH Bilendir, Bilgedir.'),
(1264, 9, 29, 'Kendilerine kitap verilenler arasından, ALLAH\'a ve ahiret gününe inanmıyan, ALLAH\'ın ve elçisinin yasakladığını yasaklamıyan ve gerçek dine uymayan kimselerle boyunlarını eğip elleriyle vergi verinceye kadar savaşın.'),
(1265, 9, 30, 'Yahudiler: "Uzeyr ALLAH\'ın oğludur," dediler. Hıristiyanlar da, "Mesih ALLAH\'ın oğludur," dediler. Bu, ağızlarından çıkan sözleridir. Önceden inkar etmiş olanların sözlerini taklid ediyorlar. ALLAH onları mahkum eder. Nasıl da çevriliyorlar?'),
(1266, 9, 31, 'Din bilginlerini, din adamlarını ve Meryem oğlu Mesih\'i ALLAH\'tan ayrı rabler edindiler. Oysa, yalnız tek Tanrı\'ya kulluk etmekle emredilmişlerdi. O\'ndan başka tanrı yoktur. O, eş koştukları kimselerden de çok Yücedir.'),
(1267, 9, 32, 'ALLAH\'ın ışığını ağızlarıyla söndürmek isterler. Kafirler istemese de ALLAH ışığını tamamlayacaktır.'),
(1268, 9, 33, 'O, elçisini hidayetle ve gerçek dinle gönderdi ki onu tüm dinlere üstün kılsın. Putperestler istemese de...'),
(1269, 9, 34, 'Ey inananlar, din bilginlerinin ve din adamlarının çoğu halkın parasını hakketmeden yerler ve ALLAH\'ın yolundan saptırırlar. Altın ve gümüşü yığıp ALLAH yolunda harcamayanlara acı bir azap müjdele.'),
(1270, 9, 35, 'Gün gelir o biriktirdikleri altınlar ve paralar cehennem ateşinde ısıtılarak onlarla alınları, yanları ve sırtları dağlanır: "Kendiniz için biriktirdiğiniz işte budur. Biriktirdiğinizi tadın."'),
(1271, 9, 36, 'Gökleri ve yeri yarattığı gün ALLAH\'ın kitabında ayların sayısı, ALLAH\'a göre on ikidir. Bunlardan dördü ise kutsaldır. İşte kusursuz din budur; o aylarda (savaşarak) kendinize zulmetmeyiniz. Ama putperestler sizinle toptan savaşırlarsa siz de onlarla toptan savaşın. Bilesiniz ki ALLAH erdemli davrananların yanındadır.'),
(1272, 9, 37, 'Kutsal Ayların yerini değiştirmek inkarın ileri noktasıdır. İnkarcılar onunla saptırılırlar. ALLAH\'ın kutsal saydığı ayların sayısına denk düşürmek amacıyla onu bir yıl helal, bir yıl da kutsal sayarlar. Böylece, ALLAH\'ın kutsal kıldığı ayların kutsallığını çiğnerler. İşledikleri kötülükler, gözlerinde güzel görünüyor. ALLAH inkarcı topluluğu doğruya ulaştırmaz.'),
(1273, 9, 38, 'Ey inananlar, size ne oldu ki, "ALLAH yolunda harekete geçin," dendiği zaman yere çakılıp kaldınız? Ahiret yerine bu dünya hayatına mı razı oldunuz? Dünya hayatının nimetleri ahiret hayatına oranla pek azdır.'),
(1274, 9, 39, 'Harekete geçmezseniz sizi acıklı bir azapla cezalandırarak yerinize başka bir toplumu geçirir. Siz O\'na asla zarar veremezsiniz. ALLAH her şeye Gücü Yetendir.'),
(1275, 9, 40, 'Siz ona yardım etmezseniz, ALLAH ona yardım etmişti. (Örneğin) kafirler onu, ikinin ikincisi olarak çıkarmışlardı. Hani ikisi mağarada iken, arkadaşına, "Üzülme, ALLAH bizimle beraberdir," diyordu... ALLAH ona huzur ve güven indirdi; görmediğiniz ordularla destekleyerek inkarcıların sözünü alçalttı. Yüce olan, yalnızca ALLAH\'ın sözüdür. ALLAH Üstündür, Bilgedir.'),
(1276, 9, 41, 'İster hafif ister ağır olarak savaşa çıkınız. Paralarınızla ve canlarınızla ALLAH yolunda cihad edin. Bu sizin için daha iyidir, bir bilseydiniz.'),
(1277, 9, 42, 'Yakın bir menfaat ve orta uzunlukta bir yolculuk olsaydı seni izleyeceklerdi; ancak zorluk onlara fazla geldi. ALLAH\'a yemin edeceklerdir: "Gücümüz yetseydi sizinle birlikte çıkacaktık." Böylece kendilerini mahfediyorlar. ALLAH onların yalancı olduklarını bilir.'),
(1278, 9, 43, 'ALLAH seni affetti: Doğrular ve yalancılar sana belli olmadan önce neden onlara izin verdin?'),
(1279, 9, 44, 'ALLAH\'a ve ahiret gününe inananlar, paralarıyla ve canlarıyla çaba göstermekten (kaçmak için) senden izin istemezler. ALLAH erdemlileri hakkıyla Bilir.'),
(1280, 9, 45, 'Ancak ALLAH\'a ve ahiret gününe inanmıyanlar senden izin ister. Kalpleri kuşku ile doludur. Onlar kuşkuları içinde bocalamaktadırlar.'),
(1281, 9, 46, 'Savaşa çıkmayı istemiş olsalardı, onun için bir hazırlık yaparlardı. Fakat ALLAH onların katkısını istemedi ve onları yüreksiz ve isteksiz yaptı. Kendilerine, "Oturanlarla birlikte oturun!," denildi.'),
(1282, 9, 47, 'Sizinle çıksalardı, size yalnız karışıklık katarlardı, aranıza fitne ve çekişme sokarlardı. İçinizde de onlara kulak verenler var. ALLAH zalimleri çok iyi Bilir.'),
(1283, 9, 48, 'Daha önce de kargaşalık çıkarmayı arzulamışlardı ve senin işlerini tersine çevirmişlerdi. Nihayet gerçek geldi ve ALLAH\'ın yasası, onlara rağmen egemen oldu.'),
(1284, 9, 49, 'Onlardan bazıları, "Bana izin ver, beni sıkıntıya sokma," diyor. Onlar zaten bu tavırlarıyla sıkıntının içine düşmüşlerdir; kafirleri cehennem kuşatmaktadır.'),
(1285, 9, 50, 'Sana bir iyilik dokunsa onları üzer, sana bir kötülük dokunsa, "Biz önceden önlem almıştık," derler ve sevinç içinde dönüp giderler.'),
(1286, 9, 51, 'De ki: "ALLAH\'ın bizim için yazdığından başkası bize dokunmaz. O\'dur bizim mevlamız, sahibimiz. İnananlar, ALLAH\'a güvensinler."'),
(1287, 9, 52, 'De ki: "Biz sizin için, ALLAH\'ın, ya kendi tarafından veya bizim elimizle sizi cezalandırmasını umarken siz bizim için ancak iki güzelden birini (gazilik veya şehitlik) umabilirsiniz. Umutla bekleyin, biz de umutla bekliyoruz.'),
(1288, 9, 53, 'De ki: "Yoksullara ister gönüllü, ister gönülsüz yardım edin; sizden kabul edilmeyecektir. Çünkü siz yoldan çıkmış bir topluluksunuz."'),
(1289, 9, 54, 'Yardımlarının kabul edilmesine engel sadece şudur: ALLAH\'ı ve elçisini inkar ettiler, namaza ancak üşenerek yaklaşırlar ve yardımları da isteksiz yaparlar.'),
(1290, 9, 55, 'Onların paraları ve çocukları seni etkilemesin. ALLAH bunlarla, onları dünya hayatında cezalandırmayı ve canlarının inkarcı olarak çıkmasını istiyor.'),
(1291, 9, 56, 'Sizden olduklarına dair ALLAH\'a yemin ederler; oysa sizden değiller, onlar anlaşmazlık çıkaran bir topluluktur.'),
(1292, 9, 57, 'Nitekim onlar, sığınacak bir yer, mağaralar yahut girilecek bir yer bulsalardı panik içinde oraya yönelirlerdi-'),
(1293, 9, 58, 'Onların bir kısmı, sadakaların dağıtımı konusunda seni eleştiriyor; kendilerine ondan verilince razı oluyorlar, ancak ondan kendilerine verilmeyince öfkeleniyorlar.'),
(1294, 9, 59, 'Onlar, ALLAH\'ın ve elçisinin kendilerine verdikleri paya razı olmalı ve "ALLAH bize yeter. ALLAH elçisi yoluyla kendi lütfundan bize verecektir. Biz sadece ALLAH\'ı arzularız," demeliydiler.'),
(1295, 9, 60, 'Sadakalar, ALLAH\'tan bir yükümlülük olarak, yoksullara, düşkünlere, bu konuda çalışan görevlilere, sempatizanlara, kölelerin özgürlüğü için, borçlulara, ALLAH yoluna ve yolda kalmışlara verilmeli. ALLAH Bilendir, Bilgedir.'),
(1296, 9, 61, 'Onlardan kimi de, "O (haber toplayan) bir kulaktır," diyerek peygamberi incitiyorlar. De ki: "O, sizin için bir hayır kulağıdır. O, ALLAH\'a inanır ve inananlara güvenir. İçinizdeki inananlar için de bir rahmettir. ALLAH\'ın elçisini incitenler için acı bir azap vardır.'),
(1297, 9, 62, 'Sizi hoşnud etmek için ALLAH\'a yemin edip dururlar. Gerçekten inanıyorlarsa bilmelidirler ki ALLAH ve elçisi hoşnud edilmeye daha layıktır.'),
(1298, 9, 63, 'Bilmediler mi ki, kim ALLAH ve elçisine karşı gelirse, içinde ebedi kalacağı cehenneme mahkum olur. Bu büyük bir aşağılanmadır.'),
(1299, 9, 64, 'İkiyüzlüler, kalplerindekini kendilerine haber verecek bir surenin inmesinden çekiniyorlar. De ki: "Alay edin bakalım, ALLAH korktuğunuz şeyi açığa çıkaracak."'),
(1300, 9, 65, 'Kendilerine sorarsan, "Biz sadece şakalaşıp oynuyorduk," derler. De ki: "Siz, ALLAH ile, ayetleriyle ve elçisiyle mi alay ediyorsunuz?"'),
(1301, 9, 66, 'Özür dilemeyiniz. Siz inandıktan sonra inkar ettiniz. Sizden bir kısmını affetsek bile, suç işlemiş oldukları için bir kısmını cezalandıracağız.'),
(1302, 9, 67, 'İkiyüzlü erkekler ve ikiyüzlü kadınlar birbirlerindendir. Onlar, kötülüğü öğütlerler, iyilikten menederler. Elleri sıkıdır. Onlar ALLAH\'ı unuttular, O da onları unuttu. İkiyüzlüler, iyice yoldan çıkmış olanlardır.'),
(1303, 9, 68, 'ALLAH, ikiyüzlü erkeklere, ikiyüzlü kadınlara ve kafirlere ebedi kalacakları cehennem ateşini söz verir. O onlara yeter. ALLAH onları lanetlemiştir ve onlar için tükenmez bir azap vardır.'),
(1304, 9, 69, 'Tıpkı sizden öncekilere benziyorsunuz; sizden daha güçlüydüler, sizden daha çok mal ve çocuklara sahiptiler. Kendilerine düşen paydan hoşlandılar, sizden öncekilerin kendi paylarından hoşlanmaları gibi siz de kendi payınızdan hoşlandınız. Kendilerini kaptıranlar gibi siz de kaptırdınız. Onlar, işleri dünya ve ahirette boşa çıkmış kimselerdir. Onlar, kaybedenlerdir.'),
(1305, 9, 70, 'Nuh\'un halkı, Ad, Semud, İbrahim\'in halkı, Medyen sakinleri ve altüst olmuş ülkelerin (Sadom ve Gomore) halkları gibi kendilerinden öncekilerin haberleri onlara ulaşmadı mı? Elçileri kendilerine apaçık belgelerle gitmişlerdi. ALLAH onlara zulmediyor değildi, aksine onlar kendi kendilerine zulmediyorlardı.'),
(1306, 9, 71, 'İnanan erkekler ve inanan kadınlar birbirlerinin dostudur. İyiliği emrederler, kötülükten menederler, namazı gözetirler, zekatı verirler, ALLAH\'a ve elçisine uyarlar. İşte onlara ALLAH rahmet edecektir. ALLAH Üstündür, Bilgedir.'),
(1307, 9, 72, 'ALLAH, inanan erkeklere ve inanan kadınlara içinden ırmaklar akan ebedi kalacakları cennetler ve Adn cennetlerinde güzel evler söz vermiştir. ALLAH\'ın hoşnud olması ise hepsinden daha büyük bir şeydir. İşte en büyük kurtuluş budur.'),
(1308, 9, 73, 'Ey peygamber, kafirler ve ikiyüzlülerle mücadele et ve onlara karşı güçlü ve sert ol. Varacakları yer cehennemdir. Ne kötü bir dönüş yeridir orası!'),
(1309, 9, 74, 'İnkar sözlerini konuşmalarına ve teslim olduktan sonra inkar etmelerine rağmen, onları söylemediklerine dair ALLAH\'a yemin ediyorlar. Aslında, hiç bir zaman ulaşamadıkları gerçeğe karşı durdular. ALLAH ve elçisi O\'nun lütfuyla kendilerini zenginleştirdikten sonra öc almaya kalktılar! Tevbe ederlerse kendileri için iyi olur. Yüz çevirirlerse, ALLAH onları dünya ve ahirette acı bir azapla cezalandırır; yeryüzünde ne bir dostları ne de bir yardımcıları olur.'),
(1310, 9, 75, 'Hatta bazıları, "Bize lütfundan verirse, paramızı yardım için harcayacağız ve dürüst insanlardan olacağız," diye ALLAH\'a söz verdiler'),
(1311, 9, 76, 'Fakat, onlara kendi lütfundan verince, cimrileştiler ve yüz çevirerek döndüler.'),
(1312, 9, 77, 'ALLAH\'a verdikleri sözden caydıkları ve yalan söyledikleri için kendisiyle karşılacakları güne kadar kalplerine iki yüzlülük soktu.'),
(1313, 9, 78, 'Bilmezler mi ki, ALLAH onların hem gizlediklerini hem açıkladıklarını bilir ve ALLAH tüm gizlileri Bilendir?'),
(1314, 9, 79, 'Gönüllü olarak yardımda bulunan inananların cömertliklerini eleştirenler ve aynı zamanda fazla veremiyen yoksullarla da alay edenler yok mu, ALLAH onları küçük düşürür. Onlar için acıklı bir azap vardır.'),
(1315, 9, 80, 'İster onlar için bağışlanma dile, ister bağışlanma dileme. Onlar için yetmiş kere bağışlanma dilesen bile ALLAH onları bağışlamıyacaktır. Çünkü onlar ALLAH ve elçisini yalanladılar. ALLAH yoldan iyice çıkmış toplumları doğruya iletmez.'),
(1316, 9, 81, 'Geride kalanlar, ALLAH elçisinin ardındaki yerlerini beğendiler, ALLAH yolunda paralarıyla, canlarıyla çaba göstermekten hoşlanmadılar ve: "Bu sıcakta harekete geçmeyin," dediler. "Cehennem ateşi daha sıcaktır," de. Bir anlasalardı!'),
(1317, 9, 82, 'Kazandıklarına karşılık az gülüp çok ağlasınlar.'),
(1318, 9, 83, 'Sefere çıkma konusunda onlardan bir grubun senden izin isteyecekleri bir fırsatı ALLAH sana tekrar verse, "Benimle birlikte ebediyyen harekata çıkmayacaksınız, ve benimle birlikte hiçbir düşmanla savaşmayacaksınız. Çünkü siz, ilk başta oturmayı seçmiştiniz. Öyle ise, geri kalanlarla beraber oturun," de.'),
(1319, 9, 84, 'Onlardan ölen birisi için namaz kılma, mezarı başında da durma. Çünkü onlar ALLAH\'a ve elçisine karşı geldiler ve yoldan çıkmışlar olalarak öldüler.'),
(1320, 9, 85, 'Paraları ve çocukları seni etkilemesin. ALLAH bunlarla, onları dünyada cezalandırmayı ve canlarının inkarcılar olarak çıkmasını ister.'),
(1321, 9, 86, '"ALLAH\'a inanın, elçisiyle birlikte cihad edin," diye bir sure indiği zaman, onların güçlü ve varlıklı olanları bile senden izin istediler ve, "Bizi bırak oturanlarla beraber olalım," dediler.'),
(1322, 9, 87, 'Geride kalanlarla birlikte olmaya razı oldular. Sonunda kalpleri mühürlendi. Bundan ötürü anlayamazlar.'),
(1323, 9, 88, 'Fakat elçi ve beraberinde bulunanlar, paralarıyla ve canlarıyla cihad ettiler. Tüm iyilikleri onlar hakketmiştir. Kazananlar onlardır.'),
(1324, 9, 89, 'ALLAH onlara ebedi kalacakları ve içinden ırmaklar akan cennetler söz vermiştir. Bu, en büyük başarıdır.'),
(1325, 9, 90, 'Araplardan özür uyduranlar, izin almak için sana geldiler. ALLAH ve elçisini yalanlayanlar böylece oturdular. İnkarcılarına acı bir azap dokunacaktır.'),
(1326, 9, 91, 'ALLAH\'a ve elçisine içten bağlı oldukları taktirde, zayıflara, hastalara ve yardım için verecek bir şeyi bulunmayanlara bir ayıplama yoktur. İyi davrananlar kınanamaz. ALLAH Bağışlayandır, Rahimdir.'),
(1327, 9, 92, 'Aynı şekilde, kendilerini bindirmen için sana geldiklerinde, "Sizi bindirecek bir taşıt bulamıyorum," dediğin ve mali yetersizliklerinden dolayı üzüntüden gözlerinden yaş akarak geri dönenler de kınanamaz.'),
(1328, 9, 93, 'Yalnız, zengin oldukları halde senden izin isteyenler kınanabilir. Geride kalanlarla birlikte olmayı istediler, ALLAH da kalplerini damgaladı. Bunun için bilmezler.'),
(1329, 9, 94, '(Savaştan gelip) kendilerine döndüğünüzde size özürler sayarlar. De ki: "Boşuna özür saymayın; size artık inanmayız, ALLAH durumunuzu bize bildirmiş bulunuyor. ALLAH ve elçisi sizin işlerinizi görecek ve sonra da gizliyi ve açığı Bilenin huzuruna döndürüleceksiniz. O yaptıklarınız her şeyi size bildirecektir.'),
(1330, 9, 95, 'Onlara döndüğünüzde, kendilerinden vazgeçesiniz diye ALLAH\'a yemin ederler. Onlardan vazgeç. Onlar kirlidir. Kazandıklarına karşılık, yerleri cehennemdir.'),
(1331, 9, 96, 'Kendilerinden hoşnut olasınız diye yemin ediyorlar. Onlardan hoşnut olsanız bile ALLAH yoldan çıkmış toplumlardan hoşnud olmaz.'),
(1332, 9, 97, 'Araplar inkarcılıkta ve ikiyüzlülükte en aşırıdırlar. ALLAH\'ın elçisine indirdiğini tanımamaya da en yakındırlar. ALLAH Bilendir, Bilgedir.'),
(1333, 9, 98, 'Bazı Araplar, yardımlarını bir kayıp ve angarya sayar ve sizin için felaketler gözetlerler. En kötü felaketler onlar içindir. ALLAH İşitendir, Bilendir.'),
(1334, 9, 99, 'Araplardan, ALLAH\'a ve ahiret gününe inananlar da vardır. Harcadıklarını ise ALLAH\'a yaklaştıracak bir vesile ve elçiye destek sayarlar. Gerçekten o, onlar için bir yaklaşma vesilesidir. ALLAH onları rahmetine sokacaktır. ALLAH Bağışlayandır, Rahimdir.'),
(1335, 9, 100, 'Göç edenlerin (mühacir) ve yardımcıların (ensar) öncülerinden ve onları güzelce izleyenlerden ALLAH razı olmuştur, onlar da O\'ndan razı olmuşlardır. Onlara, içlerinde ırmaklar akan ve ebedi kalacakları cennetler hazırlamıştır. İşte büyük zafer budur.'),
(1336, 9, 101, 'Gerek çevrenizden ve gerekse şehir halkından olan Araplardan bazıları ikiyüzlüdür. İkiyüzlülükte küstahlaşmışlardır. Sen onları bilmezsin, biz onları biliyoruz. Onları iki kat azapla cezalandıracağız ve sonra da büyük bir azaba uğratılacaklardır.'),
(1337, 9, 102, 'Diğerleri ise günahlarını itiraf ettiler, iyi işlerle kötü işleri birbirine karıştırdılar. Umulur ki ALLAH kendilerini affetsin. ALLAH Bağışlayandır, Rahimdir.'),
(1338, 9, 103, 'Onları temizlemek ve yüceltmek için paralarından bir sadaka al ve onları özendir/destekle, zira senin desteğin onları memnun eder. ALLAH İşitendir, Bilendir.'),
(1339, 9, 104, 'Bilmezler mi ki kullarından tevbeleri kabul eden, sadakaları alan ALLAH\'tır ve ALLAH Tevbeleri Kabul Edendir, Rahimdir.'),
(1340, 9, 105, '"Çalışın; ALLAH, elçisi ve inananlar yaptığınızı görecektir. Gizliyi ve açığı Bilene döndürüleceksiniz ve O da yapmış olduklarınızı size bildirecektir," de.'),
(1341, 9, 106, 'Bir başka grup da ALLAH\'ın hükmünü bekliyor. Kendilerini ya cezalandırır veya tevbelerini kabul eder. ALLAH Bilendir, Bilgedir.'),
(1342, 9, 107, 'Zarar vermek, inkarcılığı uygulamak, inananların arasını açmak ve önceden ALLAH ve elçisiyle savaşmış olanlara bir gözetleme yeri hazırlamak için mescid kullananlar da var. "Amacımız sadece iyiliktir," diye yemin ederler. ALLAH onların yalancılığına tanıktır.'),
(1343, 9, 108, 'Böyle bir yerde ebediyyen namaza durma. İlk günden itibaren erdemlilik üzere kurulan mescid, namaz kılman için çok daha uygundur. Orada temizlenmek isteyen insanlar vardır ve ALLAH temizlenenleri sever.'),
(1344, 9, 109, 'Yapısını ALLAH\'ın onayı ve erdemlilik üzerine kuran mı, yoksa yapısını bir uçurumun kenarına kurup onunla birlikte cehennem ateşine yuvarlanan mı daha iyidir? ALLAH zulmeden bir toplumu doğruya ulaştırmaz.'),
(1345, 9, 110, 'Kurdukları yapı, yürekleri parçalanıncaya dek kalplerinde bir kuşku kaynağı olarak kalacaktır. ALLAH Bilendir, Bilgedir.'),
(1346, 9, 111, 'ALLAH inananların canlarını ve mallarını cennet karşılığında satın almıştır. ALLAH yolunda savaşırlar, öldürürler ve öldürülürler. Bu, O\'nun Tevrat\'ta, İncil\'de ve Kuran\'da verdiği bir sözdür. Verdiği sözü, ALLAH\'tan daha iyi kim yerine getirebilir? Öyleyse bu alışverişinizden dolayı sevinin. En büyük başarı budur.'),
(1347, 9, 112, 'Onlar: Tevbe eden, kulluk yapan, hamdeden, aktif, eğilen, secde eden, iyiliği savunan, kötülükten meneden ve ALLAH\'ın yasalarını koruyan insanlardır. İnananları müjdele.'),
(1348, 9, 113, 'Akraba bile olsalar, ne peygamber, ne de inananlar, cehennem halkı oldukları kendilerine belli olduktan sonra ortak koşanlar için bağışlanma dileyemez.'),
(1349, 9, 114, 'İbrahim\'in babası için bağışlanma dilemesinin biricik sebebi ona önceden söz vermiş olmasıydı. Ne zaman ki onun ALLAH düşmanı olduğu kendisine belli olunca ondan uzaklaştı. İbrahim, çok duygulu ve çok şefkatli idi.'),
(1350, 9, 115, 'ALLAH bir toplumu doğru yola ilettikten sonra, sakınacakları şeyleri bildirmeden, onları sapıklığa mahkum edecek değildir. ALLAH her şeyi Bilendir.'),
(1351, 9, 116, 'Göklerin ve yerin egemenliği ALLAH\'a aittir. Diriltir, öldürür. Sizin için ALLAH\'tan başka ne bir sahip ne de bir yardımcı yoktur.'),
(1352, 9, 117, 'ALLAH, peygamberin, göç edenlerin ve yardım edenlerin tevbesini kabul etmiştir. Onlar, içlerinden bir bölümünün neredeyse kalplerinin kaydığı güç anda onu izlemişlerdi. Sonra onların yönelişini (tevbesini) kabul etti. O, onlara karşı çok Şefkatlidir, Rahimdir.'),
(1353, 9, 118, 'Geride kalan o üç kişinin de (tevbesini kabul etti). Yeryüzü, tüm genişliğine rağmen onlara dar gelmişti. Bunalmışlardı. Sonunda, ALLAH\'tan kaçamıyacaklarını anladılar. Bunun üzerine, yönelmeleri için O, onlara yöneldi. ALLAH yönelişleri Onaylayandır, Rahimdir.'),
(1354, 9, 119, 'Ey inananlar, ALLAH\'ı dinleyin ve doğrularla beraber olun.'),
(1355, 9, 120, 'Ne şehir halkı, ne de onların çevresindeki Araplar, ALLAH\'ın elçisinden geri kalmamalı ve kendilerini ona tercih etmemelidir. Zira ALLAH yolunda uğrayacakları her bir susuzluk, bir yorgunluk ve bir açlık, kafirleri öfkelendirecek her bir adım ve düşmana karşı kazandıkları her bir başarı, kendileri için bir kredi olarak yazılır. ALLAH iyi davrananların mükafatını yitirmez.'),
(1356, 9, 121, 'Büyük veya küçük, yaptıkları her yardım ve aştıkları her vadi onlar için kredi olarak yazılır. ALLAH yaptıklarının en güzeliyle kendilerini ödüllendirir.'),
(1357, 9, 122, '(Mekke\'de kalan) İnananların, topluca (Medine\'ye hicret eden peygamberi ziyaret için) yola çıkmaları doğru olmaz. Her gruptan sadece bir kaç kişi, dini anlayıp öğrenmek için harekete geçmeli. Nitekim böylece, halklarına geri döndüklerinde, halklarının uyanık bulunması için onları uyarabilsinler.'),
(1358, 9, 123, 'Ey inananlar, sizi kuşatıp saldıran kafirlerle savaşın. Sizde sertlik bulsunlar. Bilin ki ALLAH erdemlilerle beraberdir.'),
(1359, 9, 124, 'Her ne zaman bir sure inse, "Bu sure hanginizin inancını arttırdı," diye soranlar var. Gerçekten inananların inancını arttırmıştır, ve onlar (her surenin inişiyle) sevinip birbirlerini müjdelerler.'),
(1360, 9, 125, 'Kalplerinde hastalık bulunanlara gelince... Onların da kötülüklerine kötülük katmıştır ve onlar kafirler olarak ölürler.'),
(1361, 9, 126, 'Her sene, bir iki defa belalarla sınandıklarını görmüyorlar mı? Buna rağmen ne tevbe ediyorlar, ne de öğüt alıyorlar.'),
(1362, 9, 127, 'Ne zaman bir sure inse, "Kimse sizi görüyor mu," diye bakışırlar. Sonra da dönüp giderler. Anlamaz bir topluluk olduklarından ALLAH kalplerini çevirmiştir.'),
(1363, 9, 128, 'İçinizden size öyle bir elçi gelmiş bulunuyor ki sıkıntıya uğramanız ona ağır gelir; size düşkün, inananlara şefkatli, merhametlidir'),
(1364, 9, 129, 'Yüz çevirirlerse de ki: "Tanrı bana yeter! O\'ndan başka tanrı yoktur. O\'na dayandım; O büyük Arş\'ın sahibidir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'A. L. R. Bunlar (bu harfler), hikmet sahibi Kitab\'ın mucizeleridir.'),
(1366, 10, 2, 'Kendilerinden bir adama: "İnsanları uyar ve inananlara Rab\'leri katında önemli bir yere sahip olduklarını müjdele," diye vahyetmemiz halka garip mi geliyor? İnkarcılar, "Bu, apaçık bir büyücüdür," dediler.'),
(1367, 10, 3, 'Gökleri ve yeri altı günde yaratan ve sonra tüm otoriteyi kuran Rabbiniz (sahibiniz) ALLAH\'tır. Her işi yönetir. İzni olmadan hiç kimse aracılık (şefaat) edemez. Rabbiniz ALLAH budur, O\'na kulluk edin. Öğüt almaz mısınız?'),
(1368, 10, 4, 'Hepinizin dönüşü O\'nadır. Bu, ALLAH\'ın verdiği gerçek sözdür. O, yaratılışı başlatır ve sonra tekrarlar; böylece inanıp erdemli işler yapanları adaletle ödüllendirir. Kafirlere ise, inkarlarından ötürü sıcak bir içecek ve acı bir azap vardır.'),
(1369, 10, 5, 'O, güneşi bir ışık kaynağı, ayı da bir ışık yaptı. Yılların sayısını ve hesabı bilmeniz için ona evreler düzenleyip belirledi. ALLAH bunları ancak bir gerçek için yaratmıştır. Bilen bir topluma ayetleri böyle açıklar.'),
(1370, 10, 6, 'Gecenin gündüzle yer değiştirmesinde ve ALLAH\'ın göklerde ve yerde yarattığı şeylerde erdemli bir toplum için deliller ve ibretler vardır.'),
(1371, 10, 7, 'Bizimle karşılaşmayı ummayanlar, dünya hayatını sevip ona bağlananlar ve ayetlerimizden gafil olanlar...'),
(1372, 10, 8, 'İşte onların yeri, kazandıklarından ötürü ateştir.'),
(1373, 10, 9, 'İnanıp erdemli işler yapanlara gelince, Rab\'leri inançlarından dolayı onları doğruya iletir. Nimet cennetlerinde ayaklarının altından ırmaklar akar.'),
(1374, 10, 10, 'Oradaki duaları: "Tanrımız, sen yücesin", oradaki selamlaşmaları: "Selam," ve dualarının sonu da: "Evrenlerin Rabbi ALLAH\'a övgüler olsun," dur.'),
(1375, 10, 11, 'İnsanların iyiliği acele istemeleri gibi, ALLAH da onlara azabı acele verse idi, süreleri bitirilmiş olurdu. Bizimle karşılaşmayı ummayanları, azgınlıkları içinde şaşkın durumda bırakırız.'),
(1376, 10, 12, 'İnsana bir zarar dokundu mu, yanı üzerine yatarken veya ayaktayken veya oturmuşken bizi çağırır. Ona zarar veren şeyi kendisinden giderdik mi, sanki kendisine dokunan o zarardan dolayı bizi hiç çağırmamış gibi davranır. Sınırı aşanlara, yaptıkları işler işte böyle süslü gösterilir.'),
(1377, 10, 13, 'Sizden önceki nice nesilleri, zulmettikleri zaman yok etmişizdir. Elçileri kendilerine açık delillerle gitmişlerdi, ancak onlar inanmayı reddetmişlerdi. Suçlu toplumları böyle cezalandırırız.'),
(1378, 10, 14, 'Sonra, ne yapacağınızı görmek için onların yerine sizi yeryüzüne varis kıldık.'),
(1379, 10, 15, 'Onlara apaçık olan ayetlerimiz okunduğu zaman, bize kavuşmayı ummayanlar, "Bundan başka bir Kuran getir, yahut onu değiştir!," derler. De ki: "Onu kendi tarafımdan değiştiremem. Ben yalnız bana vahyedilene uyarım. Rabbime karşı gelirsem, büyük gününazabından korkarım."'),
(1380, 10, 16, 'De ki: "ALLAH dilemeseydi onu size okuyamazdım ve sizin de ondan haberiniz olmazdı. Bundan önce bir ömür boyu aranızda yaşadım. Anlamaz mısınız?"'),
(1381, 10, 17, 'ALLAH\'a yalan uydurup iftira eden, veya mucize ve ayetlerini yalanlayandan daha zalim kim olabilir? Suçlular onmaz.'),
(1382, 10, 18, 'ALLAH\'ı bırakıp, kendilerine ne zarar ne de yarar veremiyenlere tapıyorlar ve "Bunlar, ALLAH yanında bize şefaat edecekler," diyorlar. De ki: "ALLAH\'ın göklerde ve yerde bilmediği şeyleri mi O\'na bildiriyorsunuz? O çok yücedir, ortak koştuklarınızdan uzaktır."'),
(1383, 10, 19, 'İnsanlar bir tek toplumdu; sonra anlaşmazlığa düştüler. Rabbinin vermiş olduğu bir sözü olmasaydı, anlaşmazlığa düştükleri konuda hemen aralarında hüküm verilecekti.'),
(1384, 10, 20, '"O\'na Rabbinden bir mucize indirilmeli değil mi," diyorlar. De ki: "Gizemler ALLAH\'ın yanındadır; öyleyse bekleyin, ben de sizinle birlikte beklemekteyim."'),
(1385, 10, 21, 'Kendilerine dokunan bir sıkıntıdan sonra insanlara bir rahmet tattırdığımız zaman, ayet ve mucizelerimize karşı hemen bir plan düzenlerler. De ki: "Plan yapmada ALLAH daha hızlıdır. Elçilerimiz, düzenlediğiniz tüm planları kaydetmektedir."'),
(1386, 10, 22, 'Sizi karada ve denizde yürüten O\'dur. (Düşünün ki) Gemidesiniz... Bir meltemle yolcuları alıp götürdüğü zaman gemiden memnundurlar. Aniden şiddetli bir fırtınaya yakalanırlar. Her yönden gelen dalgaların arasında tümüyle kuşatıldıklarını sandıkları an, dini sadece ALLAH\'a ait kılarak, "Bizi bundan kurtarırsan şükredenlerden olacağız," diye yalvarırlar.'),
(1387, 10, 23, 'Onları kurtarınca da yeryüzünde haksız yere taşkınlık yapmaya başlarlar. Ey insanlar, taşkınlığınız kendinize karşıdır. Dünya hayatının geçici zevkine düşkünsünüz; halbuki dönüşünüz bizedir ve yapmış olduğunuz her şeyi size haber veririz.'),
(1388, 10, 24, 'Dünya hayatı, tıpkı şu örnek gibidir; gökten indirdiğimiz su insanların ve hayvanların yediği bitkilerin yapısına karışır. Bu durum, yeryüzünün süslenip bezendiği ve halkının da artık doğaya egemen olduklarını sandıkları ana kadar sürer. Nihayet geceleyin veya gündüzün ona emrimiz gelir. Sanki bir önceki gün hiç bir şeye sahip değilmiş gibi onu kökünden biçilmiş bir duruma sokarız. Düşünen bir toplum için ayetleri böyle açıklarız.'),
(1389, 10, 25, 'ALLAH barış yurduna çağırır ve dilediğini doğru bir yola iletir.'),
(1390, 10, 26, 'Güzel davrananlara daha fazlası vardır. Yüzlerini ne bir yoksunluk ne de bir aşağılanma duygusu kaplar. Onlar cennet halkıdır. Orada ebedi kalıcıdırlar.'),
(1391, 10, 27, 'Kötülük ve günah kazananlara ise kötülüğünün aynısıyla karşılık verilir. Yüzlerini bir aşağılanma duygusu kaplar. ALLAH\'tan başka bir koruyucuları yoktur. Yüzleri sanki karanlık geceden bir parçayla bürünmüştür. Onlar ateş halkıdır; onlar orada ebedi kalacaklardır.'),
(1392, 10, 28, 'Onları bir araya topladığımız gün, ortak koşanlara; "Siz ve ortaklarınız haydi yerinize!," diyerek birbirlerinden ayırırız. Ortakları ise: "Siz bize kulluk etmiyordunuz," derler'),
(1393, 10, 29, '"Bizimle sizin aranızda ALLAH tanıktır ki, biz sizin kulluk etmenizden habersizdik."'),
(1394, 10, 30, 'İşte orada her can, geçmişte yapmış olduğunu bulur. Gerçek Mevlaları olan ALLAH\'a döndürülürler. Uydurdukları (ortaklar) ise kendilerinden uzaklaşıp kaybolur.'),
(1395, 10, 31, 'De ki, "Size gökten ve yerden kim rızık veriyor? İşitme ve görme duyularınızı kim kontrol ediyor? Ölüden diriyi, diriden ölüyü kim çıkarıyor? Tüm işleri kim yönetiyor?" "ALLAH," diyecekler. De ki: "Öyleyse neden erdemli davranmıyorsunuz?"'),
(1396, 10, 32, 'İşte Gerçek Rabbiniz ALLAH budur. Öyleyse gerçekten (yüz çevirmenizden) sonra sapıklıktan başka ne kalır? Nasıl da (bunları düşünmekten) çevriliyorsunuz?'),
(1397, 10, 33, 'Yoldan çıkmış olanların inanmıyacağına dair Rabbinin sözü işte böyle gerçekleşir.'),
(1398, 10, 34, 'De ki, "Ortaklarınızdan hangisi yaratmayı başlatıp tekrarlayabilir?" De ki: "ALLAH yaratmayı başlatır ve onu tekrarlar. Nasıl da çevriliyorsunuz?"'),
(1399, 10, 35, 'De ki "Ortaklarınızdan hangisi gerçeğe ulaştırabilir?" De ki, "ALLAH gerçeğe ulaştırır. Gerçeğe ulaştıran mı uyulmaya daha yaraşır, yoksa gerçeğe ulaştırılmadıkça gerçeği bulamıyan mı? Ne biçim hüküm veriyorsunuz?"'),
(1400, 10, 36, 'Onların çoğu, ancak zanna uyarlar. Zan ise gerçeğin yerini tutamaz. ALLAH onların yaptıklarını Bilendir.'),
(1401, 10, 37, 'Bu Kuran, ALLAH\'tan başkası tarafından düzenlenen bir kitap değildir. Ancak kendisinden öncekileri onaylayan ve kitabın (yasaların) detaylı bir açıklamasıdır. Bunda kuşkunuz olmasın; evrenlerin Rabbindendir.'),
(1402, 10, 38, '"Onu uydurdu" mu diyorlar? De ki: "Doğru sözlüler iseniz, buna benzer bir sure getirin ve ALLAH\'tan başka çağırabildiklerinizi çağırın."'),
(1403, 10, 39, 'Hayır, onlar bilgisini kavramadan ve asıl anlamına ulaşmadan onu yalanladılar. Kendilerinden öncekiler de böyle yalanladı. Zalimlerin sonuna dikkatle bak.'),
(1404, 10, 40, 'Onlardan kimi buna inanır, kimi de inanmaz. Rabbin bozgunculuk yapanları daha iyi bilir.'),
(1405, 10, 41, 'Seni yalanlarlarsa de ki: "Benim yaptığım bana, sizin yaptığınız ise size! Siz benim yaptığımdan sorumlu değilsiniz, ben de sizin yaptığınızdan sorumlu değilim."'),
(1406, 10, 42, 'Onlardan seni dinleyenler de var; akıllarını kullanmayan sağırlara sen mi işittireceksin?'),
(1407, 10, 43, 'Onlardan sana bakanlar da var; görüşü olmayan körleri sen mi doğruya ileteceksin?'),
(1408, 10, 44, 'ALLAH insanlara hiç zulmetmez; ancak insanlar kendi kendilerine zulmederler.'),
(1409, 10, 45, 'Onları topladığımız gün, sanki gündüzden bir saat kadar (dünyada) kalmışlar gibi birbirlerini tanırlar. ALLAH ile karşılaşmayı yalanlayanlar kaybedenlerdir. Doğruyu da bulmamışlardır.'),
(1410, 10, 46, 'Onlara söz verdiklerimizin bir kısmını sana göstersek de veya canını alsak da, onların son dönüş yeri bizedir. Sonra ALLAH onların yaptıkları her şeye de tanıktır.'),
(1411, 10, 47, 'Her bir toplum için bir elçi vardır. Elçileri geldiği zaman adaletle yargılanırlar ve hiç zulmedilmezler.'),
(1412, 10, 48, '"Bu söz ne zaman gerçekleşecek," diyorlar.'),
(1413, 10, 49, 'De ki: "ALLAH\'ın dilemesi dışında, ben kendime dahi ne bir zarar ne de bir yarar verme gücüne sahip değilim. Her toplumun bir süresi vardır. Süreleri bitince ne bir saat geciktirilir ne de öne alınırlar.'),
(1414, 10, 50, 'De ki: "O\'nun azabı ister gece veya gündüz gelsin, ama neden suçlular o konuda acele içindedirler?"'),
(1415, 10, 51, '"Gerçekleştikten sonra mı ona inanacaksınız? Öyleyse neden onu şimdi çabuk isteyip duruyorsunuz?"'),
(1416, 10, 52, 'Sonra zulmedenlere: "Ebedi azabı tadın," denir, "Kazandığınızdan başka bir karşılık mı bekliyordunuz?"'),
(1417, 10, 53, '"Bu sahiden doğru mudur," diye senden haber bekliyorlar. De ki: "Elbette, Rabbime andolsun o gerçektir ve siz onu engelleyemezsiniz!"'),
(1418, 10, 54, 'Zulmetmiş bir can yeryüzündeki her şeye sahip olsa, onu fidye olarak vermek isteyecektir. Azabı gördüklerinde derin pişmanlık duyacaklar. Adaletle yargılanacaklar ve onlara zulmedilmeyecektir.'),
(1419, 10, 55, 'Göklerde ve yerde olanlar elbette ALLAH\'ındır. ALLAH\'ın sözü tamamıyla gerçektir; fakat onların çoğu bilmez.'),
(1420, 10, 56, 'O diriltir ve öldürür. O\'na döndürüleceksiniz.'),
(1421, 10, 57, 'Ey halk, Rabbinizden size bir öğüt, gönülleri sıkan her şeye karşı bir şifa, inananlara bir yol gösterici ve bir rahmet gelmiş bulunuyor.'),
(1422, 10, 58, 'De ki, "Sadece, ALLAH\'ın lütfu ve rahmetiyle sevinsinler. Bu, topladıkları herşeyden daha hayırlıdr."'),
(1423, 10, 59, 'De ki, "ALLAH\'ın size indirdiği rızıkların bir kısmını helal, bir kısmını da haram ettiğinizi görmez misiniz?" De ki, "ALLAH mı size izin verdi, yoksa ALLAH\'a iftira mı ediyorsunuz?"'),
(1424, 10, 60, 'ALLAH adına yalan uyduranlar diriliş gününü düşünmezler mi? ALLAH insanlara karşı bol lütuf sahibidir; ancak onların çoğu şükretmez.'),
(1425, 10, 61, 'Ne durumda bulunursanız bulunun, Kuran\'dan ne okursanız okuyun, hangi işi yaparsanız yapın, siz onlarla uğraşırken biz mutlaka size tanık oluruz. Göklerde ve yerde bulunan bir atom ağırlığı bile Rabbinin kontrolünün dışına çıkamaz. Atomdan da küçük olsun büyük olsun, her şey apaçık bir kitapta kayıtlıdır.'),
(1426, 10, 62, 'ALLAH\'ın dostları için ne bir korku vardır, ne de üzülürler.'),
(1427, 10, 63, 'Onlar ki, inandılar ve erdemli davrandılar.'),
(1428, 10, 64, 'Dünya hayatında da ahirette de mutluluk onlarındır. ALLAH\'ın kelimeleri (verdiği söz) değişmez. İşte bu, en büyük zaferdir.'),
(1429, 10, 65, 'Sözleri seni üzmesin. Üstünlük tümüyle ALLAH\'a aittir. O İşitendir, Bilendir.'),
(1430, 10, 66, 'Göklerde ve yerde ne varsa ALLAH\'ındır. ALLAH\'tan başkalarına yalvaranlar aslında koştukları ortakları izlememektedirler. Onlar sadece tahmin yürütüyorlar ve sadece saçmalıyorlar.'),
(1431, 10, 67, 'O ki geceyi sizin dinlenmeniz için elverişli, gündüzü de aydınlık kıldı. Dinleyen bir topluluk için bunda deliller vardır.'),
(1432, 10, 68, '"ALLAH çocuk edindi!," dediler. O bundan çok yücedir. O, hiç bir şeye ihtiyacı olmayandır. Göklerde ve yerde ne varsa O\'nundur. Sizin bu konuda hiç bir kanıtınız yoktur. ALLAH hakkında bilmediğiniz bir şeyi mi söylüyorsunuz?'),
(1433, 10, 69, 'De ki: "ALLAH hakkında yalan uyduranlar başarıya ulaşamazlar."'),
(1434, 10, 70, 'Dünyada paylarını alırlar, sonra dönüşleri bizedir. Sonra da inkarlarından ötürü onlara şiddetli azabı tattırırız.'),
(1435, 10, 71, 'Onlara Nuh\'un tarihini anlat. Halkına şunları demişti: "Ey halkım, durumum ve ALLAH\'ın ayetlerini hatırlatmam size ağır geldiyse, ben ALLAH\'a güveniyorum. Ortaklarınız ve liderlerinizle birlikte toplanın, pişman olmayacağınız son kararınızı alın ve bekletmeden bana karşı uygulayın."'),
(1436, 10, 72, '"Yüz çevirirseniz, halbuki ben sizden her hangi bir ücret istememiştim. Benim ücretim ancak ALLAH\'ın üzerinedir. Müslümanlardan olmakla emredildim."'),
(1437, 10, 73, 'Onu yalanladılar. Bunun üzerine onu ve gemide onunla beraber olanları kurtardık. Onları (yeryüzünün) yöneticileri yaptık. Ayetlerimizi yalanlayanları ise boğduk. Uyarılanların akıbetine dikkat et.'),
(1438, 10, 74, 'Sonra onun ardından, elçiler gönderdik, halklarına apaçık deliller getirdiler. Geçmişte yalanlamış bulunduklarına inanacak değillerdi. Azgınların kalplerini böyle damgalarız.'),
(1439, 10, 75, 'Sonra onların ardından Musa ve Harun\'u Firavun\'a ve konseyine mucizelerimizle gönderdik. Fakat büyüklük tasladılar ve suçlu bir topluluk oldular.'),
(1440, 10, 76, 'Tarafımızdan kendilerine gerçek gelince, "Bu apaçık bir büyüdür," dediler.'),
(1441, 10, 77, 'Musa, "Size gelen gerçeği böyle mi nitelendiriyorsunuz? Bu bir büyü müdür? Oysa büyücüler başarıya ulaşamaz."'),
(1442, 10, 78, 'Dediler: "Sen, yeryüzünde büyüklük ikinize kalsın diye, atalarımızı üzerinde bulduğumuz şeyden bizi çevirmek için mi bize geldin? Biz size asla inanmayız."'),
(1443, 10, 79, 'Firavun, "Tüm uzman büyücüleri bana getirin!," dedi.'),
(1444, 10, 80, 'Büyücüler gelince, Musa onlara, "Ne atacaksanız atın!," dedi.'),
(1445, 10, 81, 'Onlar atınca, Musa: "Sizin bu getirdiğiniz bir büyüdür. ALLAH elbette onu boşa çıkaracaktır. ALLAH bozguncuların işlerini düzeltmez," dedi.'),
(1446, 10, 82, 'ALLAH sözleriyle hakkı gerçekleştirir. Suçlular hoşlanmasa da...'),
(1447, 10, 83, 'Firavun ve erkanının işkence ve baskısından korktukları için Musa\'ya, halkından ancak bir kaç kişi inandı. Firavun, yeryüzünde haddi çok aşan bir tiran idi.'),
(1448, 10, 84, 'Musa: "Ey halkım, eğer gerçekten ALLAH\'a inanmış ve O\'na teslim olmuş iseniz O\'na güvenin."'),
(1449, 10, 85, 'Dediler ki: "ALLAH\'a güvendik. Rabbimiz, bizi zalim halkın baskısına maruz bırakma."'),
(1450, 10, 86, '"Rahmetinle bizi inkarcı halktan kurtar."'),
(1451, 10, 87, 'Musa\'ya ve kardeşine: "Halkınız için Mısır\'da evler hazırlayın. Evlerinizi tapınak yapın ve namazı gözetin. İnananları müjdeleyin," diye vahyettik.'),
(1452, 10, 88, 'Musa dedi ki: "Rabbimiz, sen Firavun ve konseyine dünya hayatında lüks ve mal verdin. Rabbimiz, senin yolundan saptırsınlar diye mi? Rabbimiz, onların mallarını silip süpür ve acı azabı görünceye kadar inanmasınlar diye kalplerini katılaştır."'),
(1453, 10, 89, 'Dedi ki: "İkinizin duası kabul edilmiştir. Doğru olun ve bilmeyenlerin yolunu izlemeyin."'),
(1454, 10, 90, 'İsrail oğullarını denizden geçirdik. Firavun ve ordusu ise küstahça ve düşmanca arkalarına düştü. Boğulmak üzereyken, "İsrail oğullarının inandığından başka tanrı olmadığına inandım, ben müslümanım," dedi.'),
(1455, 10, 91, '"Çok geç! Daha önce baş kaldırmış ve bozgunculardan olmuştun."'),
(1456, 10, 92, '"Senden sonraki kuşaklara ibret olman için bugün senin cesedini koruyacağız. Ne var ki insanların çoğunluğu işaretlerimizden habersizdirler."'),
(1457, 10, 93, 'İsrail oğullarına onurlu bir yer bağışladık ve onlara güzel rızıklar verdik. Fakat, kendilerine ilim geldikten sonra ayrılığa düştüler. Rabbin, diriliş günü, ayrılığa düştükleri konuda aralarında hüküm verecektir'),
(1458, 10, 94, 'Sana indirdiğimiz hakkında bir kuşkun varsa, kitabı (senden) önce okumuş olanlara sor. Sana Rabbinden gerçek gelmiş bulunuyor. Şüphecilerden olma.'),
(1459, 10, 95, 'Sakın, ALLAH\'ın ayetlerini yalanlayanlardan olma. Yoksa kaybedenlerden olursun.'),
(1460, 10, 96, 'Rabbinin kararıyla mahkum edilenler inanmazlar.'),
(1461, 10, 97, 'Onlara her çeşit mucize gelse bile, acı azabı görünceye kadar (inanmazlar).'),
(1462, 10, 98, 'Hangi toplum inanırsa, inancı ona yarar sağlar. Örneğin; Yunus\'un halkı: İnandıkları zaman, bu dünya hayatındaki aşağılayıcı azabı kendilerinden kaldırdık. Bir süreye kadar onları nimetlerle yaşattık.'),
(1463, 10, 99, 'Rabbin dileseydi, yeryüzündekilerin hepsi inanırdı. Öyleyse, sen mi halkı inanmaları için zorlayacaksın?'),
(1464, 10, 100, 'Hiç bir kişi ALLAH\'ın izni olmadan inanamaz ve O, akıllarını kullanmayanları rezilliğe mahkum eder.'),
(1465, 10, 101, 'De ki: "Göklerde ve yerde neler var, bir bakın! İnanmıyan bir topluma deliller ve uyarılar kâr etmez."'),
(1466, 10, 102, 'Kendilerinden öncekilerin yaşadığı günlerin bir benzerini mi bekliyorlar? De ki, "Bekleyin, ben de sizinle birlikte beklemekteyim."'),
(1467, 10, 103, 'Sonunda elçilerimizi ve inananları kurtarırız. Evet, inananları kurtarmak bizim değişmez bir yasamızdır.'),
(1468, 10, 104, 'De ki "Ey halk, dinimden bir kuşku duyuyorsanız, bilesiniz ki, sizin ALLAH\'tan başka taptığınız kimselere tapmam. Ben ancak, sizin canınızı alan ALLAH\'a taparım. İnananlardan olmakla emrolundum."'),
(1469, 10, 105, 'Ve: "Tektanrıcı olarak dine yönel; ortak koşanlardan olma."'),
(1470, 10, 106, '"ALLAH\'ı bırakıp, sana yarar ve zarar veremiyenleri çağırma. Böyle yaparsan o zaman zalimlerden olursun."'),
(1471, 10, 107, 'ALLAH sana bir zarar dokundurursa onu, O\'ndan başka ortadan kaldıracak yoktur. Senin için bir iyilik dilerse, O\'nun lütfunu da geri çevirecek yoktur. Kullarından dilediğine bunu verir. O, Bağışlayandır, Rahimdir.'),
(1472, 10, 108, 'De ki, "Ey halk, Rabbinizden size gerçek gelmiş bulunuyor. Kim yola gelirse kendisi için yola gelmiş olur, kim de saparsa kendi zararına sapar. Ben, sizden sorumlu değilim."'),
(1473, 10, 109, 'Sana vahyedileni izle; ALLAH hüküm verinceye kadar sabret. O, hüküm verenlerin en iyisidir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'A. L. R. (Bu) öyle bir kitaptır ki, Bilge ve her şeyden Haberdar biri tarafından ayetleri sağlamlaştırılmış ve sonra da açıklanıp detaylanmıştır.'),
(1475, 11, 2, 'Ki ALLAH\'tan başkasına tapmayasınız. Kuşkusuz ben, O\'ndan size bir uyarıcı ve bir müjdeciyim.'),
(1476, 11, 3, 'Rabbinizin bağışlamasını dileyip O\'na tevbe etmelisiniz. O zaman, sizi belli bir süreye kadar güzelce geçindirir ve lütfu hakkeden herkese lütfundan verir. Dönerseniz, sizin için büyük günün azabından korkarım.'),
(1477, 11, 4, 'Dönüşünüz ALLAH\'adır. O her şeye Gücü Yetendir.'),
(1478, 11, 5, 'Onlar, göğüslerindekini bilinç altına itip O\'ndan gizlemek isterler. İyi bilin ki elbiselerini büründükleri zaman dahi onların gizlediklerini ve açıkladıklarını bilir. O, gizli düşünceleri bilendir.'),
(1479, 11, 6, 'Yeryüzündeki hiç bir yaratık yoktur ki rızkı ALLAH tarafından garanti edilmesin. Onların konaklarını ve uğrak yerlerini bilir. Tüm bunlar apaçık bir kitaptadır.'),
(1480, 11, 7, 'O, hanginizin daha iyi iş yaptığını test etmek için gökleri ve yeri altı günde yaratandır. Egemenliği suyun üzerinde idi. Buna rağmen, kafirlere, "Siz ölümden sonra diriltileceksiniz," desen, kafirler, "Bu, ancak açık bir büyüdür (kandırıcı bir sözdür)," diyeceklerdir.'),
(1481, 11, 8, 'Onlardan azabı belli bir süre için ertelesek, "Onu tutan nedir," derler. Doğrusu, onlara geldiği gün, kendilerinden geri çevrilemez ve alay ettikleri şey kendilerini kuşatacaktır.'),
(1482, 11, 9, 'İnsana kendimizden bir rahmet tattırsak ve sonra ondan çekip alsak, umutsuzlaşır, nankörleşir.'),
(1483, 11, 10, 'Kendisine dokunan zararlardan sonra ona nimetler tattırsak, "Kötülükler benden gitti," der. Bu kez sevinçlidir, kibirlidir.'),
(1484, 11, 11, 'Sabredenler ve erdemli işler yapanlar için ise bir bağışlanma ve büyük bir ödül vardır.'),
(1485, 11, 12, '"Ona bir hazine, yahut onunla birlikte bir melek inmeli değil miydi," dedikleri için belki göğsün daralacak ve sana vahyedilenin bir kısmını terkedeceksin. Sen yalnız bir uyarıcısın; ALLAH her şeyi kontrol edendir.'),
(1486, 11, 13, '"Onu (Kuran\'ı) o uydurdu" mu diyorlar? De ki: "Haydi ona benzer, uydurulmuş on sure getirin. ALLAH\'tan başka tüm dostlarınızı da çağırın, doğru sözlülerseniz!"'),
(1487, 11, 14, 'Size cevap veremedilerse bilesiniz ki o ALLAH\'ın bilgisiyle indirilmiştir ve O\'ndan başka tanrı yoktur. Artık müslüman olacak mısınız?'),
(1488, 11, 15, 'Kim dünya hayatını ve lüksünü ararsa, yaptıklarının karşılığını orada tam öderiz ve onlar orada hiç bir eksikliğe uğratılmazlar.'),
(1489, 11, 16, 'Bunlar için ahirette, yalnız ateş vardır. Yaptıklarının hepsi orada boş çıkmıştır. Bütün yaptıkları geçersizdir.'),
(1490, 11, 17, 'Daha önce Musa\'nın kitabı bir önder ve rahmet iken, şimdi Rab\'lerinden gelen bir tanığın ilettiği kesin bir kanıta sahip olanlar varya işte onlar ona inanırlar. Hangi grup onu inkar ederse yeri ateştir. Ondan hiç bir kuşkun olmasın. O, Rabbinden gelen bir gerçektir. Ne var ki halkın çoğunluğu inanmaz.'),
(1491, 11, 18, 'Uydurmaları ALLAH\'a yakıştırandan daha zalim kim olabilir? Onlar Rab\'lerinin huzuruna çıkarılır ve tanıklar: "İşte Rab\'leri hakkında yalan uyduranlar bunlardır," diyecekler. ALLAH\'ın laneti zalimleredir.'),
(1492, 11, 19, 'Onlar ki ALLAH\'ın yolundan alıkoyarlar ve onu çarpıtmak isterler. Onlar ahireti de inkar ederler.'),
(1493, 11, 20, 'Onlar, yeryüzünde kaçamazlar ve ALLAH\'tan başka dost ve sahipleri yoktur. Onlar için azap katlanır. Çünkü onlar işitemediler, göremediler.'),
(1494, 11, 21, 'Onlar, kişiliklerini yitirenlerdir. Uydurdukları şeyler bile kendilerini terkedip kaybolmuştur.'),
(1495, 11, 22, 'Hiç kuşku yok ki, ahirette en çok kaybedenler onlardır.'),
(1496, 11, 23, 'İnanıp erdemli davrananlar ve Rab\'lerine gönülden boyun eğenler ise cennet halkıdır. Onlar orada ebedi kalıcıdır.'),
(1497, 11, 24, 'Her iki grubun örneği, kör ve sağır olan biri ile gören ve işiten biri gibidir. Durumları bir midir? Öğüt almaz mısınız?'),
(1498, 11, 25, 'Nuh\'u halkına gönderdik: "Ben sizin için apaçık bir uyarıcıyım,"'),
(1499, 11, 26, '"ALLAH\'tan başkasına kulluk etmeyin. Acı bir günün azabına uğramanızdan korkarım."'),
(1500, 11, 27, 'Halkından inkar eden ileri gelenler, "Seni sadece bizim gibi bir insan olduğunu görüyoruz ve aramızdaki sığ görüşlü ayak takımının dışında kimsenin seni izlemediğini görüyoruz. Sizin bizden herhangi bir üstünlüğünüzü de görmüyoruz. Tersine, biz sizin yalancı olduğunuzu sanıyoruz," dediler.'),
(1501, 11, 28, 'Dedi ki: "Ey halkım, ya ben Rabbimden gelen kesin bir kanıta sahip isem ve bana bir rahmet vermiş de bunlar gözünüze görünmüyorsa? Siz onu istemezken sizi ona biz mi zorlayacağız?"'),
(1502, 11, 29, '"Ey halkım, buna karşılık sizden herhangi bir para istemiyorum. Ücretim ancak ALLAH\'tan gelir. İnananları da kovamam; onlar, Rab\'leriyle karşılacaklar. Fakat, sizi cahillik eden bir topluluk olarak görüyorum."'),
(1503, 11, 30, '"Ey halkım, onları kovarsam ALLAH\'a karşı kim bana yardım edecek? Düşünmez misiniz?"'),
(1504, 11, 31, '"ALLAH\'ın hazinelerinin yanımda olduğunu söylemiyorum, gizliyi bilmiyorum, melek olduğumu da ileri sürmüyorum. Gözlerinizin horladığı kimselere ALLAH\'ın bir iyilik bağışlamıyacağını da söylemiyorum. Onların gizli düşüncelerini ALLAH daha iyi bilir. Aksi taktirde zalimlerden olurum."'),
(1505, 11, 32, 'Dediler ki: "Ey Nuh, sen bizimle tartıştın ve bizimle tartışmayı uzattın. Doğru sözlü isen haydi bizi tehdit ettiğin şeyi getir bakalım."'),
(1506, 11, 33, 'Dedi ki: "Onu, dilerse, sizin başınıza ancak ALLAH getirebilir. Siz engel olamazsınız."'),
(1507, 11, 34, '"ALLAH azmanızı dilemişse, ben size öğüt vermek istesem de size o öğüdümün bir yararı olmayacaktır. O\' dur Rabbiniz ve siz O\'na döndürüleceksiniz."'),
(1508, 11, 35, '"Bunu (bu aktarılan tarihsel tartışmayı) o uydurdu" mu diyorlar? (Ey Muhammed) De ki: "Onu ben uydurmuş isem, suçumdan ben sorumlu olacağım, ve sizin işlediğiniz suçlarla da benim bir ilişkim yok."'),
(1509, 11, 36, 'Nuh\'a vahyedildi: "Şu ana kadar inanmış olanların dışında artık halkından hiç kimse inanmıyacaktır. Onların tavırları seni üzmesin."'),
(1510, 11, 37, '"Gözetimimiz altında vahyimizle gemiyi yap. Zalimler için bana baş vurma; onlar suda boğulacaklardır."'),
(1511, 11, 38, 'Gemiyi yapıyorken, halkının ileri gelenleri her yanından geçişte onunla alay ediyorlardı. Dedi ki: "Bizimle alay ediyorsanız, sizin bizimle alay ettiğiniz gibi biz de sizinle alay ediyoruz."'),
(1512, 11, 39, '"Alçaltıcı azabın kime geleceğini ve kimin kalıcı azaba mahkum olacağını yakında bileceksiniz."'),
(1513, 11, 40, 'Nihayet emrimiz gelip de gök kaynayıp taşınca, kendisine dedik ki: "Her türden birer çifti, daha önce mahkum edilmiş olanlar hariç, çoluk çocuğunu ve inananları ona yükle." Kendisiyle birlikte inanmış olanlar zaten bir kaç kişiydi.'),
(1514, 11, 41, 'Dedi ki: "Gemiye binin. Akması ve durması ALLAH\'ın adıyladır. Rabbim çok Bağışlayandır, Rahimdir."'),
(1515, 11, 42, 'Gemi, onları tepeler gibi dalgaların arasından geçirirken, Nuh bir kenarda ayrı duran oğluna seslendi: "Yavrum, gel bizimle birlikte bin. Kafirlerle birlik olma."'),
(1516, 11, 43, 'O ise, "Beni sudan koruması için bir tepeye sığınacağım," dedi. (Nuh): "Bugün ALLAH\'ın yargısından koruyacak hiç bir şey yoktur; ancak O\'nun acıdıkları hariç," dedi. Dalgalar ikisi arasından geçti; o, boğulanların arasındaydı.'),
(1517, 11, 44, 'Ve denildi ki: "Ey toprak suyunu yut, ey gök sen de tut." Su yatıştı, karar yerine getirildi, Cudi (Judea) üzerine oturdu ve "Zalimler uzak olsunlar!," dendi.'),
(1518, 11, 45, 'Nuh Rabbine seslendi: "Ey Rabbim, oğlum benim ailemdendir, Senin verdiğin söz ise elbette gerçektir ve Sen, yargıda bulunanların en iyisisin."'),
(1519, 11, 46, 'Dedi ki: "Ey Nuh, o senin ailenden olamaz. Bilmediğin bir konuda benden istekte bulunman erdemli bir tavır değildir. Cahillerden olmamanı sana öğütlerim."'),
(1520, 11, 47, 'Dedi ki: "Rabbim, bilgim olmayan bir konuda sana yalvardığım için sana sığınırım. Beni bağışlamaz ve bana acımazsan kaybedenlerden olurum."'),
(1521, 11, 48, 'Dendi ki: "Ey Nuh, sana ve seninle birlikte olan uluslara bizden barış ve bereketlerle in. Ayrıca öyle uluslar var ki onları bir süre yaşatacağız ve sonra onlara bizden acı bir azap dokunacaktır."'),
(1522, 11, 49, 'Bunlar, sana vahyettiğimiz gayb haberleridir. Ne sen, ne de senin halkın bundan önce onları bilmezdi. Sabret. Sonuç, erdemlilerindir.'),
(1523, 11, 50, 'Ad halkına da kardeşleri Hud\'u... Dedi ki: "Ey halkım, ALLAH\'a kulluk edin. O\'ndan başka tanrınız yoktur. Siz ancak uyduruyorsunuz."'),
(1524, 11, 51, '"Ey halkım, buna karşılık olarak sizden herhangi bir ücret te istemiyorum. Benim ücretim, beni ilk yaratandan başkasına düşmez. Aklınızı kullanmıyor musunuz?"'),
(1525, 11, 52, '"Ey halkım, Rabbinizden bağışlanma dileyin, sonra O\'na yönelin ki gökten üzerinize bol rahmet yağdırsın, gücünüze güç katsın. Suçlular olarak yüz çevirmeyin!"'),
(1526, 11, 53, 'Dediler ki: "Ey Hud, sen bize kesin bir kanıt ile gelmedin. Biz, sırf senin sözünle dinimizi bırakacak değiliz, sana inanacak değiliz."'),
(1527, 11, 54, '"Senin tanrılarımızdan biri tarafından çarpıldığını söyleriz," dedi ki: "ALLAH\'ı tanık tutuyorum, siz de tanık olun ki ben uzağım sizin ortak koştuğunuz "'),
(1528, 11, 55, '"O\'ndan başkalarından. Haydi, topunuz birleşip bana karşı plan hazırlayın. Hiç de beklemeyin."'),
(1529, 11, 56, '"Ben, Rabbim ve Rabbiniz olan ALLAH\'a güvendim. O\'nun kontrol etmediği hiç bir yaratık yoktur. Rabbim dosdoğru yolun üzerindedir."'),
(1530, 11, 57, '"Yüz çevirirseniz, artık ben, kendisiyle gönderildiğim mesajı size bildirdim. Rabbim yerinize başka bir halk geçirecek ve siz O\'na hiç bir zarar veremezsiniz. Benim Rabbim her şeyi Gözetip Kontrol Edendir."'),
(1531, 11, 58, 'Emrimiz gelince Hud\'u ve beraberindeki inananları bizden bir rahmetle kurtardık. Onları dehşetli bir azaptan kurtardık.'),
(1532, 11, 59, 'İşte Ad (halkı) böyleydi. Rab\'lerinin ayetlerini reddettiler, elçilerine isyan ettiler ve her inatçı zorbanın emrini izlediler.'),
(1533, 11, 60, 'Bunun üzerine, hem dünya hayatında ve hem diriliş gününde bir lanete uğradılar. Kısacası, Ad halkı, Rab\'lerine karşı çıktı. Hud\'un halkı Ad yok olmuştur.'),
(1534, 11, 61, 'Semud\'a da kardeşleri Salih\'i... Dedi ki: "Ey halkım, ALLAH\'a kulluk edin, O\'ndan başka tanrı yoktur. Sizi yerden çıkarıp yaratan, sizi oraya yerleştiren O\'dur. Öyleyse O\'ndan bağışlanma dileyin ve sonra O\'na yönelin. Kuşkusuz Rabbim Yakındır, Yanıtlayandır."'),
(1535, 11, 62, 'Dediler ki: "Ey Salih, sen bundan önce, aramızda popüler bir umut kaynağıydın. Atalarımızın tapmış olduklarından sen şimdi bizi men mi ediyorsun? Biz, senin bizi çağırdığın şeylerden kuşku içindeyiz."'),
(1536, 11, 63, 'Dedi ki: "Ey halkım, ya ben Rabbimden kesin bir delile sahipsem ve kendisinden bana rahmet vermişse? O\'na isyan edecek olursam beni ALLAH\'tan kim kurtarabilir? Siz ancak benim kayıplarımı arttırabilirsiniz?"'),
(1537, 11, 64, '"Ey halkım, ALLAH\'ın bu devesi sizin için bir ayettir. ALLAH\'ın toprağında bırakın otlansın. Ona kötülük dokundurmayın. Yoksa sizi yakın bir azap yakalar."'),
(1538, 11, 65, 'Fakat, onu işkenceyle kestiler. "Yurdunuzda yaşamanız için üç gününüz var. Bu, yalan olmayan bir sözdür," dedi.'),
(1539, 11, 66, 'Emrimiz gelince, Salih\'i ve beraberinde inanmış olanları bizden bir rahmet ile o günün rezilliğinden kurtardık. Rabbin, kuşkusuz Güçlü ve Üstündür.'),
(1540, 11, 67, 'Zalimleri korkunç bir ses yakaladı, yurtlarında çöküp kaldılar.'),
(1541, 11, 68, 'Sanki orada hiç yaşamamışlardı. Doğrusu Semud (halkı) Rab\'lerine karşı geldi; Semud yok olup gitti.'),
(1542, 11, 69, 'Elçilerimiz İbrahim\'e müjdeyi götürdüklerinde "Selam!" (Barış olsun) dediler. O da "Selam!," dedi ve hemen (onlara) kızartılmış bir buzağı sundu.'),
(1543, 11, 70, 'Ellerinin ona uzanmadığını görünce, durumlarını beğenmedi; içine bir korku düştü. "Korkma," dediler, "Biz Lut\'un halkına gönderilmiş bulunuyoruz."'),
(1544, 11, 71, 'Ayakta duran karısı (bu habere sevinip) gülünce biz de ona İshak\'ı ve İshak\'ın ardından Yakub\'u müjdeledik.'),
(1545, 11, 72, '"Vay başıma gelenler," dedi, "Ben bir yaşlı kadın ve kocam da ihtiyar bir adam iken çocuk mu doğuracağım? Bu çok garip bir şey!"'),
(1546, 11, 73, '"ALLAH\'ın işine mi şaşıyorsun," dediler, "ALLAH\'ın rahmeti ve bereketi sizin üzerinizedir ey ev halkı. O Övgüye En Layıktır, Yücelerin Yücesidir."'),
(1547, 11, 74, 'İbrahim\'den korku gidip kendisine bu müjde verilince Lut halkı hakkında bizimle tartışmaya başladı.'),
(1548, 11, 75, 'Doğrusu, İbrahim çok yumuşak, çok duygulu ve sürekli (Tanrı\'ya) yönelen biriydi.'),
(1549, 11, 76, '"Bundan vazgeç İbrahim. Rabbinin emri gelmiş bulunuyor; onlar önlenemez bir azaba mahkum olmuşlardır."'),
(1550, 11, 77, 'Elçilerimiz Lut\'a gelince, onlara kötü davranıldı. Bu durum (Lut\'un) canını sıktı ve (kendi kendine), "Bu, zor bir gün olacak," dedi.'),
(1551, 11, 78, 'Halkı koşarak oraya geldiler; günah işlemeyi adet haline getirmişlerdi. "Ey halkım, işte kızlarım, onlar sizin için daha temizdir. ALLAH\'ı sayın, konuklarım önünde beni rezil etmeyin. İçinizde aklı başında bir adam yok mu," dedi.'),
(1552, 11, 79, '"Bilirsin ki senin kızlarına bizim bir ihtiyacımız yok. Sen bizim ne istediğimizi iyi bilirsin," dediler.'),
(1553, 11, 80, '(Lut:) "Keşke size gücüm yetseydi veya güçlü dostlara sahip olsaydım," dedi.'),
(1554, 11, 81, '(Melekler) "Ey Lut," dediler, "Biz, Rabbinin elçileriyiz. Onlar sana dokunamazlar. Aileni gecenin bir anında yürüyüşe çıkart, sizden hiç kimse geriye bakmasın; fakat senin karın istisna. Ötekilerin başına gelecek olanlar onun da başına gelecektir. Onların belirlenmiş zamanı sabahtır. Sabah yakın değil mi?"'),
(1555, 11, 82, 'Emrimiz gelince onun üstünü altına getirdik, üzerlerine sertleşmiş kat kat taş yağdırdık.'),
(1556, 11, 83, 'Zalimleri bulsun diye Rabbin tarafından belirlenmiş (taşlar)..'),
(1557, 11, 84, 'Medyen\'e de kardeşleri Şuayb\'ı... Dedi ki: "Ey halkım, ALLAH\'a kulluk edin, O\'ndan başka tanrınız yoktur. Ölçüyü tartıyı eksik tutmayın. Sizi refah içinde görüyorum ve sizin için, kuşatıcı bir günün azabından korkuyorum."'),
(1558, 11, 85, '"Ey halkım, ölçüyü ve tartıyı adaletle tam uygulayın. Halkın hakkını eksik vermeyin ve yeryüzünde bozgunculuk yaparak kötülük işlemeyin."'),
(1559, 11, 86, '"İnanıyorsanız, ALLAH\'ın (helal) bıraktığı, sizin için daha iyidir. Ben sizin üzerinize bekçi değilim."'),
(1560, 11, 87, 'Dediler ki: "Şuayb, atalarımızın tapmış olduklarını veya ticaretimizi dilediğimiz gibi çevirmekten vazgeçmemizi, senin namazın mı gerektiriyor? Sen aslında yumuşak huylusun, akıllısın."'),
(1561, 11, 88, '"Halkım," dedi, "Bakın, ya ben Rabbimden kesin bir kanıta sahip isem ve bana kendisinden güzel bir nimet bağışlamışsa...? Sizi bazı şeylerden yasaklamam, size zıt gitmek istediğim için değildir. Ben ancak, gücüm yettiğince reform yapmak istiyorum. Başarım ancak ALLAH\'tandır. O\'na güvendim ve O\'na yöneliyorum."'),
(1562, 11, 89, '"Halkım, bana karşı çıkışınız, sakın sizi Nuh halkının, veya Hud halkının, yahut Salih halkının başına gelenler gibi bir felakete uğratmasın. Üstelik, Lut halkı sizden pek uzakta değil."'),
(1563, 11, 90, '"Rabbinizden bağışlanma dileyin, sonra O\'na tevbe edin. Kuşkusuz Rabbim Rahimdir, Sevendir."'),
(1564, 11, 91, '"Şuayb," dediler, "Senin söylediklerinin bir çoğunu anlamıyoruz. Üstelik biz, seni aramızda güçsüz görüyoruz. Kabilen olmasaydı seni taşlardık. Senin bize karşı herhangi bir üstünlüğün yoktur."'),
(1565, 11, 92, 'Dedi ki: "Ey halkım, kabilem size göre ALLAH\'tan daha mı üstündür ki O\'na sırt çevirdiniz? Rabbim, sizin tüm yaptıklarınızı Kuşatandır."'),
(1566, 11, 93, '"Halkım, elinizden geleni yapın; ben de yapacağım. Alçaltıcı azabın kime geleceğini ve kimin yalancı olduğunu ileride bileceksiniz. Umutla bekleyin, ben de sizinle birlikte umutla bekliyorum."'),
(1567, 11, 94, 'Emrimiz gelince Şuayb\'ı ve kendisiyle beraber inanmış olanları bizden bir rahmetle kurtardık. Zulmedenleri korkunç gürültü yakaladı; yurtlarında çöküp kaldılar.'),
(1568, 11, 95, 'Sanki orada hiç yaşamamışlar gibi... Semud, nasıl yok edildiyse, Medyen de öylece yok edildi.'),
(1569, 11, 96, 'Musa\'yı ayetlerimizle (mucize ve vahyimizle) ve kesin bir yetkiyle gönderdik'),
(1570, 11, 97, 'Firavun ve konseyine... Ancak onlar Firavun\'un buyruğuna uydular. Firavunun buyruğu ise akıllıca değildi.'),
(1571, 11, 98, 'Diriliş gününde halkına öncülük edip onları ateşe sokacaktır. Girdikleri yer ne kötü bir mekandır!'),
(1572, 11, 99, 'Burada da, ahirette de peşlerine lanet takılmıştır. Aldıkları armağan ne kötü armağandır!'),
(1573, 11, 100, 'Sana bu aktardıklarımız, o kentlerin haberlerindendir. Onlardan kimi hala ayakta, kimi de biçilmiştir.'),
(1574, 11, 101, 'Biz onlara zulmetmedik, onlar kendi kendilerine zulmettiler. Rabbin emri geldiği zaman, ALLAH\'tan başka yalvardıkları tanrıları onları hiç bir şeyden kurtaramadı. Aslında, onların yalnızca ziyanlarını arttırdı.'),
(1575, 11, 102, 'İşte Rabbin, zulmetmekte olan kentleri yakaladığı zaman böyle yakalar. O\'nun yakalaması acıdır, çetindir.'),
(1576, 11, 103, 'Ahiret azabından korkanlar için bunda bir ders vardır. Halkın toplandığı bir gündür o. Tanık olunan bir gündür o'),
(1577, 11, 104, 'Onu ancak sayılı bir süre için erteliyoruz.'),
(1578, 11, 105, 'O gün geldiği zaman, hiç kimse O\'nun izni olmadan konuşamaz. Onlardan kimi talihsiz, kimi de mutludur.'),
(1579, 11, 106, 'Talihsizler ateştedir. Onlar orada sızlayıp inlerler.'),
(1580, 11, 107, 'Gökler ve yer durduğu sürece orada kalıcıdırlar; ancak Rabbin dilerse başka. Rabbin, dilediğini Yapandır'),
(1581, 11, 108, 'Mutluluğu hakkedenler ise, gökler ve yer kaldığı sürece cennette kalıcıdırlar. Rabbinin dilerse başka. Kesintisiz bir ödüldür bu.'),
(1582, 11, 109, 'Şunların taptıklarından hiç bir kuşkun olmasın. Aynen daha önceki atalarının taptığı gibi tapıyorlar. Onların nasiplerini eksiksiz olarak kendilerine ödeyeceğiz.'),
(1583, 11, 110, 'Musa\'ya kitabı verdik; ancak onda anlaşmazlığa düştüler. Rabbinin verilmiş bir sözü olmasaydı aralarında hüküm verilecekti. Onlar ondan kuşku içindedirler, kararsızdırlar.'),
(1584, 11, 111, 'Rabbin hepsinin yaptıklarının karşılığını tam olarak verecektir. O, onların yaptıklarından Haberdardır.'),
(1585, 11, 112, 'Emredildiğin gibi dosdoğru ol, seninle beraber yönelmiş olanlarla birlikte... Aşma ve azgınlaşma. O, sizin yaptıklarınızı Görendir.'),
(1586, 11, 113, 'Zalimlere sakın sempati duymayın, onları desteklemeyin, yoksa size ateş dokunur. Sizin ALLAH\'tan başka dostlarınız yoktur, sonra size yardım da edilmez.'),
(1587, 11, 114, 'Gündüzün iki ucunda, gecenin yakın kısmında namazı gözet. İyilikler kötülükleri silip götürür. Bu, öğüt alacak olanlara bir öğüttür.'),
(1588, 11, 115, 'Sabret, çünkü ALLAH, iyilik yapanların ödülünü savsamaz.'),
(1589, 11, 116, 'Sizden önceki nesillerin erdem sahibi olanları, yeryüzünde kötülüklere engel olmalı değil miydiler? Onlardan kurtardığımız az kişi hariç... Zalimler ise, kendilerine verilen refaha dalıp şımardılar; böylece suçlulara katıldılar.'),
(1590, 11, 117, 'Halkı erdemli davrandığı sürece, Rabbin kentleri yok edecek değildir.'),
(1591, 11, 118, 'Rabbin dileseydi halkı tek bir toplum yapardı. Fakat onlar sürekli olarak (gerçeği) tartışıp duracaklar.'),
(1592, 11, 119, 'Rabbinin acıdıkları hariç. Bunun içindir ki onları yarattı. Rabbinin sözü yerine gelmiştir: "Cehennemi cinler ve insanlarla, topluca dolduracağım."'),
(1593, 11, 120, 'Günlünü pekiştirmek için elçilerin tarihlerinden sana yeterince aktarmaktayız. Bunda, senin için gerçek, ve inananlar için de bir aydınlatma ve uyarı gelmiştir.'),
(1594, 11, 121, 'İnanmıyanlara de ki: "Elinizden geleni yapın, biz de yapacağız."'),
(1595, 11, 122, '"Sonra bekleyin, biz de beklemekteyiz."'),
(1596, 11, 123, 'Göklerin ve yerin gizlilikleri ALLAH\'a aittir. Tüm işler sonunda ona döner. O\'na kulluk edin ve O\'na güvenin. Rabbin onların yaptığından habersiz değildir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'A. L. R. Bu (harfler), apaçık kitabın mucizeleridir.'),
(1598, 12, 2, 'Onu Arapça bir Kuran olarak indirdik ki anlayasınız.'),
(1599, 12, 3, 'Sana bu Kuran\'ı vahyederek, sana en güzel bir anlatımla tarihi aktarıyoruz. Sen daha önce bundan habersizdin.'),
(1600, 12, 4, 'Yusuf, bir zamanlar babasına: "Babacığım, on bir gezegeni, güneşi ve ay\'ı gördüm, onların bana secde ettiklerini gördüm," dedi.'),
(1601, 12, 5, '(Babası Yakup:) "Yavrum," dedi, "Rüyanı kardeşlerine anlatma, olur ki sana karşı bir plan kurarlar. Çünkü şeytan, insana apaçık bir düşmandır.'),
(1602, 12, 6, '"Böylece Rabbin seni seçmekte, sana rüyaların yorumunu öğretmekte ve daha önceki ataların İbrahim ve İshak\'a nimetini tamamladığı gibi sana ve Yakup ailesine de tamamlamaktadır. Rabbin Bilendir, Bilgedir."'),
(1603, 12, 7, 'Dileyenler için, Yusuf ve kardeşlerinde dersler vardır:'),
(1604, 12, 8, '(Kardeşleri) demişlerdi ki: "Babamız, Yusuf ve (öz) kardeşini bizden daha çok seviyor, biz ise çoğunluğuz. Babamız açıkça yanlış bir tutum içindedir.'),
(1605, 12, 9, '"Yusuf\'u öldürün, ya da onu bir uzak yere yollayın ki babanızın ilgisi sizde odaklansın. Daha sonra da erdemli bir topluluk olursunuz."'),
(1606, 12, 10, 'Onlardan birisi şöyle dedi: "Yusuf\'u öldürmeyin, onu kuyunun dibine atın. Böyle yaparsanız kervanlardan biri onu bulup götürebilir."'),
(1607, 12, 11, 'Dediler ki: "Ey babamız, neden Yusuf hakkında bize güvenmiyorsun? Oysa biz onun candan dostlarıyız."'),
(1608, 12, 12, '"Onu bizimle birlikte yolla ki koşup oynasın. Kuşkusuz biz onu koruyacağız."'),
(1609, 12, 13, 'Dedi ki: "Onu götürmeniz beni endişelendirir. Korkarım ki haberiniz yokken onu kurt yer."'),
(1610, 12, 14, 'Dediler ki: "Biz kalabalık bir grup olduğumuz halde onu kurt yerse, o zaman gerçekten kaybedenler oluruz."'),
(1611, 12, 15, 'Onu götürdükleri ve kuyunun dibine atmak için topluca karar verdikleri sırada biz ona: "(Üzülme) Onların bu yaptıklarını, hiç farkında olmayacakları bir anda onlara anlatacaksın," diye vahyettik'),
(1612, 12, 16, 'Akşamleyin ağlayarak babalarına geldiler.'),
(1613, 12, 17, '"Ey babamız," dediler, "Gittik, yarışıyorduk. Yusuf\'u da eşyamızın yanında bırakmıştık. Sonunda onu kurt yedi. Doğru konuşsak bile sen bize inanmıyacaksın."'),
(1614, 12, 18, 'Gömleğine yalandan kan bulaştırmışlardı. Dedi ki: "Doğrusu, egonuz sizi bir işe sürüklemiş. Bana düşen (bu acıya) güzelce dayanmaktır. Sizin komplonuza karşı ancak ALLAH\'tan yardım istenir."'),
(1615, 12, 19, 'Oradan geçmekte olan bir kervan, sucularını gönderdi, kovasını sarkıtınca: "Müjde, burada bir erkek çocuğu var!," dedi. Onu ticari bir mal olarak sakladılar. ALLAH onların yaptıklarını biliyordu.'),
(1616, 12, 20, 'Ona ihtiyaçları olmadığı için ucuz bir fiyata, bir kaç dirheme sattılar.'),
(1617, 12, 21, 'Onu Mısır\'da satın alan kişi, karısına, "Ona iyi bak, olur ki bize yararı dokunur, ya da onu evlat ediniriz," dedi. Böylece Yusuf\'u yeryüzünde yerleştirip ona rüyaların yorumunu öğretiyorduk. ALLAH işini sonuçlandırandır; fakat halkın çoğu bunu bilmez.'),
(1618, 12, 22, 'Büyüyüp erginleşince ona bilgelik ve bilgi verdik. Güzel davrananları böyle ödüllendiririz.'),
(1619, 12, 23, 'Evinde bulunduğu kadın onu baştan çıkarmak istedi, kapıları kitledi ve: "Tümüyle sana aidim," dedi. Dedi ki: "ALLAH\'a sığınırım. O benim Rabbimdir (efendimdir), ki bana iyi bir yer verdi. Zalimler başarmaz."'),
(1620, 12, 24, 'Kadın onu çok arzulamıştı, Rabbinin işaretini görmeseydi o da onu çok arzulamıştı. Böylece kötülük ve fenalığı ondan çevirdik. O, katışıksız kullarımızdandı.'),
(1621, 12, 25, 'Kapıya doğru koşuştular. Kadın onun gömleğini arkadan çekip yırttı. Kapının yanında kadının beyiyle karşılaştılar. Kadın: "Karına tecavüz etmek isteyen kişinin ya hapsedilmesi veya acı bir azapla cezalandırılması gerekmez mi," dedi.'),
(1622, 12, 26, 'Dedi ki: "Aslında senin karın beni baştan çıkarmak istedi." Aileden bir tanık: "Gömleği ön taraftan yırtılmışsa kadın doğruyu söylüyor, o ise bir yalancıdır."'),
(1623, 12, 27, '"Yok eğer gömleği arkadan yırtılmışsa, o zaman kadın yalancı, o doğru sözlüdür," diye tanıklıkta bulundu.'),
(1624, 12, 28, 'Gömleğinin arkadan yırtılmış olduğunu görünce, "Bu, siz kadınların planıdır. Planınız müthiştir," dedi.'),
(1625, 12, 29, '"Yusuf, sen bu olayı unut. Sen de (ey kadınım) günahından dolayı bağışlanma dile. Sen hata işledin."'),
(1626, 12, 30, 'Şehirde bir takım kadınlar: "Valinin karısı hizmetçisini baştan çıkarmaya çalışıyor," dediler, "Ona çılgıncasına aşık. Onu açık bir sapıklık içinde görüyoruz."'),
(1627, 12, 31, 'Kadın, onların dedikodusunu işitince onları davet etti. Onlar için konforlu yerler hazırladı. Her birine birer bıçak verdi. (Kadınlar, meyvelerini soymakla meşgul iken Yusuf\'a:) "Onların huzuruna çık," dedi. Kadınlar onu görünce, öyle etkilendiler ki ellerini kestiler ve: "ALLAH korusun, bu bir insan değil; bu ancak asil bir melektir," dediler.'),
(1628, 12, 32, '(Kadın:) "İşte siz beni bunun için suçlamıştınız. Onu baştan çıkarmak istedim, ancak (iffetini) koruyup beni reddetti. Kendisine emrettiğimi yapmazsa hapishaneye atılacak ve aşağılanacaktır," dedi.'),
(1629, 12, 33, '(Yusuf:) "Rabbim, hapishane, onların beni çağırdığı şeyden daha iyidir. Planlarını benden savmazsan onlara meyledip cahillerden olurum," dedi.'),
(1630, 12, 34, 'Rabbi onun duasına yanıt verdi ve kadınların planlarını ondan savdı. O İşitendir, Bilendir.'),
(1631, 12, 35, 'Sonra, (vali ve adamları Yusuf\'un suçzuzluğuna dair) kanıtları görmelerine rağmen, onu belli bir süreye kadar hapishaneye atmayı uygun gördüler.'),
(1632, 12, 36, 'Onunla birlikte hapishaneye iki genç adam da girdi. Onlardan biri: "(Rüyamda) kendimi şarap yaparken gördüm," dedi. Diğeri: "Ben de başımın üzerinde ekmek taşırken kendimi gördüm, onu kuşlar yiyordu. Bunların yorumunu bize bildir. Seni iyilerden görüyoruz."'),
(1633, 12, 37, '"Size ayrılan karavana yemeği elinize geçmeden önce size onun yorumunu bildirebilirim. Bunlar, Rabbimin bana öğrettiği şeylerdendir. Ben, ALLAH\'a inanmıyan bir toplumun dinini terketmiş bulunuyorum, onlar ahiret konusunda da inkarcıdırlar."'),
(1634, 12, 38, '"Ben, atalarım İbrahim, İshak ve Yakub\'un dinine uydum. ALLAH\'a herhangi bir şeyi ortak koşmak bize yakışmaz. Bu, ALLAH\'ın bize ve halka olan lütfudur. Ancak insanların çoğu şükretmez."'),
(1635, 12, 39, '"Ey hapishane arkadaşlarım, çeşitli rabler mi daha iyidir, yoksa Tek ve her şeye Egemen olan ALLAH mı?"'),
(1636, 12, 40, '"O\'ndan başka taptıklarınız, sizin ve atalarınızın uydurduğu isimlerden ibarettir. ALLAH onlara her hangi bir güç vermemiştir. Hüküm, ancak ALLAH\'ındır. Yalnızca kendisine kulluk etmenizi emretmiştir. Dosdoğru din işte budur. Ne var ki halkın çoğu bunu bilmiyor."'),
(1637, 12, 41, '"Hapishane arkadaşlarım, biriniz rabbine (efendisine) şarap sunacak, diğeriniz ise çarmıha gerilecek ve kuşlar onun başından yiyecek. Sorduğunuz iş çözülmüştür."'),
(1638, 12, 42, 'O ikisinden, kurtulacağını öngürdüğü kimseye, "Beni rabbinin yanında an," dedi. Rabbini anmayı Şeytan ona (Yusuf\'a) unutturunca hapiste birkaç yıl daha kaldı.'),
(1639, 12, 43, 'Kıral dedi ki: "Rüyamda yedi semiz inek görüyorum, onları yedi zayıf inek yiyor; ayrıca yedi yeşil ve yedi kuru başak... Ey seçkinler, rüyaların yorumunu yapabiliyorsanız bu rüyamı bana çözüverin."'),
(1640, 12, 44, '"Karmakarışık rüyalardır bunlar. Biz böyle rüyaların yorumunu bilmeyiz," dediler.'),
(1641, 12, 45, 'O iki kişiden kurtulmuş olanı, nice zaman sonra hatırladı ve, "Beni (Yusuf\'a) gönderirseniz size onun yorumunu bildireceğim," dedi.'),
(1642, 12, 46, '(Zindana gidip:) "Yusuf, ey can arkadaşım, bize yedi sıska inek tarafından yenen yedi semiz inek, yedi yeşil ve kuru başak hakkında açıklama getir de halka bilgi vereyim."'),
(1643, 12, 47, 'Dedi ki: "Yedi yıl boyunca ekip biçtiğinizi, yediğiniz az bir kısmının dışında, başaklarında bırakıp depolayın."'),
(1644, 12, 48, '"Sonra, onun ardından yedi kurak (yıl) gelecek ve sizin depoladığınızın az bir bölümü hariç, o yıllar için önceden biriktirdiklerinizi yiyip bitirecektir."'),
(1645, 12, 49, '"Ondan sonra ise öyle bir yıl gelir ki onda halk ferahlanacak ve onda (meyve) sıkıp, (hayvan) sağacaklar."'),
(1646, 12, 50, 'Kral, "Onu bana getirin," dedi. Elçi ona gelince (Yusuf,) "Rabbine dön ve parmaklarını kesen kadınların durumunu ondan sor. Kuşkusuz benim Rabbim, onların entrikalarını bilir," dedi'),
(1647, 12, 51, '(Kral kadınlara:) "Yusuf\'u baştan çıkarmak isterken durumunuz neydi?" Kadınlar, "ALLAH korusun; biz ondan bir kötülük görmedik!," dediler. Bunun üzerine, valinin karısı şöyle dedi: "Gerçek artık ortaya çıktı. Onu ben yoldan çıkarmaya çalışmıştım. O doğru konuşuyor."'),
(1648, 12, 52, '"Umarım ki arkasında kendisine ihanet etmediğimi bilir, zira ALLAH hainlerin planını onaylamaz."'),
(1649, 12, 53, '"Suçsuz olduğumu ileri sürmüyorum. Kuşkusuz ego, kötülüğü arzular. Ben ancak Rabbimin merhametini umuyorum; Rabbim Bağışlayandır, Rahimdir."'),
(1650, 12, 54, 'Kral dedi ki: "Getirin onu, yanımda çalışması için onu atayayım" Kendisiyle konuşup görüşünce, "Sen artık bugün yanımızda önemli bir makamı olan güvenilir birisin," dedi.'),
(1651, 12, 55, 'Dedi ki: "Beni ülkenin maliye işlerinin başına getir. Kuşkusuz ben iyi korur, iyi bilirim," dedi'),
(1652, 12, 56, 'Böylece Yusuf\'u o ülkede yerleştirdik. Dilediği gibi hareket edebiliyordu. Dilediğimiz kimseye rahmetimizi yağdırırız. Güzel davrananları ödülsüz bırakmayız.'),
(1653, 12, 57, 'İnananların ve erdemli davrananların ahiret ödülü elbette daha da iyidir.'),
(1654, 12, 58, 'Yusuf\'un kardeşleri gelip onun huzuruna girdiler. (Yusuf) onları tanıdı; ancak onlar onu tanımadı.'),
(1655, 12, 59, 'Yüklerini hazırlatınca şöyle dedi: "Baba tarafından olan kardeşinizi getirin. Görmüyor musunuz ben ölçüyü tartıyı tam, evsahipliğini de iyi yapıyorum."'),
(1656, 12, 60, '"Onu bana getirmezseniz, artık benden ölçülüp tartılacak bir şey beklemeyin ve bana yaklaşmayın."'),
(1657, 12, 61, 'Dediler ki: "O\'nun hakkında babasını ikna etmeye çalışacağız. Kuşkun olmasın ki buna çalışacağız."'),
(1658, 12, 62, '(Yusuf) Emri altındakilere de şöyle emir verdi: "Ailelerine döndüklerinde bulmaları için, takas eşyalarını yüklerinin içine sokun, belki böylece geri dönerler."'),
(1659, 12, 63, 'Babalarına döndüklerinde, "Ey babamız," dediler, "Bundan böyle bize erzak yasaklandı. Kardeşimizi bizimle yolla da erzak alalım. Biz onu koruruz," dediler.'),
(1660, 12, 64, '"Daha önce kardeşi için size nasıl güvendiysem şimdi de onun içinde mi size güveneyim? ALLAH en iyi Koruyucudur ve O, Merhametlilerin Merhametlisidir."'),
(1661, 12, 65, 'Erzak yüklerini açınca, eşyalarının kendilerine geri verilmiş olduğunu gördüler ve, "Ey babamız, daha ne isteriz? İşte eşyalarımız bize geri verilmiş. Ailemizin geçimini sağlar, kardeşimizi korur ve bir deve yükü daha erzak alırız. Bu, kolay bir iştir," dediler.'),
(1662, 12, 66, '"Hepiniz kuşatılıp engellenmedikçe onu bana getireceğinize dair bana, ALLAH adına sağlam bir söz vermezseniz, onu sizinle beraber göndermem. Onlar ona söz verince, "Söylediklerimize ALLAH tanıktır," dedi.'),
(1663, 12, 67, 'Dedi ki: "Yavrularım, bir tek kapıdan girmeyin; farklı kapılardan girin. Ne var ki, ALLAH\'ın önceden belirlediği şeyden sizi kurtaramam. Hüküm ALLAH\'ındır ancak. Ben O\'na güvendim. Güvenenler O\'na güvenmeli."'),
(1664, 12, 68, 'Babalarının kendilerine emrettiği yerlerden girdiler. Bu, onları ALLAH\'ın hiç bir takdirinden kurtaramazdı; ancak Yakup onlardan bunu istemekte özel bir nedene sahipti. O, kendisine öğrettiğimiz belli bir bilgiye sahipti; fakat halkın çoğu bilmez.'),
(1665, 12, 69, 'Yusuf\'un huzuruna girdiklerinde, kardeşini yanına yaklaştırdı ve, "Ben senin kardeşinim; onların yaptıklarına üzülme," dedi.'),
(1666, 12, 70, 'Onların erzak yükünü hazırlatınca bir su kabını kardeşinin torbasına koydu. Daha sonra bir ünleyici şöyle seslendi: "Ey kervan siz hırsızlarsınız."'),
(1667, 12, 71, 'Onları karşılayarak, "Neyi kaybettiniz," dediler.'),
(1668, 12, 72, '"Kralın su tasını kaybettik. Kim onu getirirse ona bir deve yükü ödül verilecektir. Ben bunu garantiliyorum."'),
(1669, 12, 73, 'Dediler ki: "ALLAH\'a andolsun, siz de bilirsiniz ki biz bu ülkede bozgunculuk çıkarmaya gelmedik, biz hırsız da değiliz."'),
(1670, 12, 74, 'Dediler: "Peki, yalan söylüyorsanız onun cezası nedir?"'),
(1671, 12, 75, '"Onun cezası," dediler, "Kimin torbasında bulunursa o kişi alıkonur. Biz zalimleri böyle cezalandırırız."'),
(1672, 12, 76, '(Yusuf) kardeşinin yükünden önce onların yüklerinden başladı ve sonra su kabını kardeşinin yükünden çıkardı. Biz Yusuf\'a böyle bir planı öğretmiştik. Kralın yasasını uygulasaydı kardeşini alıkoyamazdı; ALLAH dilese başka. Dilediğimizi derecelerle yükseltiriz. Her bilenin üstünde daha iyi bir bilen vardır.'),
(1673, 12, 77, '"O çalmışsa, daha önce kardeşi de çalmıştı," dediler. Yusuf onlara belli etmeden: "Siz gerçekten kötüsünüz. ALLAH anlattığınızın içyüzünü bilir," diye söylendi.'),
(1674, 12, 78, 'Dediler ki: "Sayın ekselans, onun oldukça yaşlı bir babası var; onun yerine bizden birimizi al. Biz seni iyilik edenlerden olduğunu görüyoruz."'),
(1675, 12, 79, 'Dedi ki: "Yanında eşyamızı bulduğumuz kimseden başkasını alıkoymaktan ALLAH\'a sığınırız, o zaman biz zalimlerden oluruz."'),
(1676, 12, 80, 'Ondan ümitlerini kesince, danışmak üzere bir kenara çekildiler. Büyükleri: "Babanızın sizden ALLAH adına bir söz aldığını ve daha önce Yusuf konusunda da sınırı aşmış olduğunuzu bilmez misiniz? Babam bana izin verinceye, yahut ALLAH hakkımda hüküm verinceye kadar buradan ayrılmayacağım. O, en iyi Hükmedendir," dedi,'),
(1677, 12, 81, '"Babanıza dönün ve şunları söyleyin: \'Ey babamız, oğlun hırsızlık yaptı. Biz ancak bildiğimiz şeye tanıklık ediyoruz. Gizli işlenen bir şeyi önleyemezdik.\'"'),
(1678, 12, 82, '\'Bulunduğumuz topluluğa ve beraber geldiğimiz kervana sor. Biz doğruyu anlatıyoruz."'),
(1679, 12, 83, '"Hayır, egonuz sizi bir işe sürüklemiş. Bana düşen artık güzelce sabretmektir. Olur ki ALLAH, bana onları topluca getirir. O, Bilendir, Bilgedir," dedi.'),
(1680, 12, 84, 'Onlardan yüz çevirdi ve, "Vah, yazık oldu Yusuf\'a!," dedi. Üzüntüden gözlerine ak düştü, acısını içine gömdü.'),
(1681, 12, 85, 'Dediler ki: "ALLAH\'a andolsun, Yusuf\'u anmaya devam edersen ya hasta düşeceksin veya öleceksin."'),
(1682, 12, 86, '"Ben kahrımı ve tasamı ancak ALLAH\'a şikayet ederim, sizin bilmediğinizi ALLAH\'tan biliyorum," dedi.'),
(1683, 12, 87, '"Oğullarım, gidin, Yusuf\'u ve kardeşini araştırın. ALLAH\'ın rahmetinden umut kesmeyin. İnkarcılardan başkası ALLAH\'ın rahmetinden umut kesmez."'),
(1684, 12, 88, '(Yusuf\'un) Huzuruna girince, "Sayın ekselans, bize ve ailemize darlık dokundu; bu yüzden değersiz bir eşya ile geldik. Ama sen bize tam ölçü ver, bize yardımda bulun. ALLAH sadaka verenleri ödüllendirir," dediler.'),
(1685, 12, 89, '"Sizler cahiller iken, Yusuf ve kardeşine neler yaptığınızı biliyor musunuz," dedi.'),
(1686, 12, 90, '"Yoksa sen, sen gerçekten Yusuf musun," dediler. "Ben Yusuf\'um, bu da kardeşimdir. ALLAH bize iyilikte bulundu. Kim erdemli davranır ve sabrederse elbette ALLAH güzel davrananları ödülsüz bırakmaz," dedi.'),
(1687, 12, 91, '"ALLAH\'a andolsun ki ALLAH seni bizden üstün tutmuştur. Biz gerçekten hatalıydık," dediler.'),
(1688, 12, 92, 'Dedi ki: "Bugün size kınama yoktur. ALLAH sizi affetsin. O, merhametlilerin En Merhametlisidir."'),
(1689, 12, 93, '"Bu gömleğimi götürün ve onu babamın yüzüne atın; gözü açılsın. Tüm ailenizle birlikte bana gelin."'),
(1690, 12, 94, 'Kervan (Mısır\'dan) ayrılınca babaları, "Beni bunamış saymayın, Yusuf\'un kokusunu işitiyorum," dedi.'),
(1691, 12, 95, '"ALLAH\'a andolsun sen hâlâ eski şaşkınlığın içindesin," dediler.'),
(1692, 12, 96, 'Müjdeci gelip de onu (gömleği) onun yüzüne atınca, tekrar görmeye başladı ve şöyle dedi: "ALLAH\'tan sizin bilmediğiniz şeyleri biliyorum dememiş miydim?"'),
(1693, 12, 97, 'Dediler ki: "Ey babamız, günahlarımızın bağışlanmasını dile. Biz gerçekten yanlış davrandık."'),
(1694, 12, 98, 'Dedi ki: "Sizin için Rabbimden bağışlanma dileyeceğim; O, Bağışlayandır, Rahimdir."'),
(1695, 12, 99, 'Yusuf\'un yanına vardıklarında, anababasını kucakladı ve: "ALLAH\'ın izniyle güven içinde Mısır\'a girin," dedi.'),
(1696, 12, 100, 'Ana ve babasını tahtın üzerine kaldırdı. Hepsi onun için secdeye kapandılar. Dedi ki: "Babacığım, bu, önceden görmüş bulunduğum rüyanın gerçekleşmesidir. Rabbim onu gerçekleştirdi. şeytan, benimle kardeşlerimin arasını açtıktan sonra O beni hapishaneden çıkararak ve sizi çölden getirerek bana iyilikte bulundu. Gerçekten Rabbim dilediğine karşı çok şefkatlidir. O, Bilendir, Bilgedir."'),
(1697, 12, 101, '"Rabbim, sen bana hükümranlık verdin ve rüyaların yorumunu öğrettin. Yeri ve göğü ayırarak yaratansın. Dünya ve ahirette sensin benim Velim (sahibim). Canımı Müslüman olarak al ve beni iyilere kat."'),
(1698, 12, 102, 'Bunlar, sana vahyettiğimiz geçmişin haberleridir. Onlar topluca karar alıp düzen kurarlarken sen onların yanında değildin.'),
(1699, 12, 103, 'Ne kadar istesen de halkın çoğunluğu inanmıyacaktır.'),
(1700, 12, 104, 'Halbuki sen onlardan herhangi bir ücret te istemiyorsun. Bu, sadece halka bir uyarıdır.'),
(1701, 12, 105, 'Göklerde ve yerde nice deliller var ki yanından dikkatsizce geçerler.'),
(1702, 12, 106, 'Onların da çoğu, ortak koşmadan ALLAH\'a inanmaz.'),
(1703, 12, 107, 'Onlar, ALLAH tarafından kuşatıcı bir azabın kendilerine çatmasından, yahut dünyanın sonunun (Saat) aniden kendilerine gelmesinden emin mi oldular?'),
(1704, 12, 108, 'De ki, "Benim yolum şudur: Açık bir delille ALLAH\'a çağırırım, aynı şekilde beni izleyenler de... ALLAH Yücedir, ben ortak koşan birisi değilim."'),
(1705, 12, 109, 'Senden önce, kentler halkının arasından (seçip) vahyettiğimiz adamlardan başkasını göndermedik. Yeryüzünü dolaşıp kendilerinden öncekilerin akıbetine bakmazlar mı? Erdemliler için ahiret yurdu daha iyidir; anlamaz mısınız?'),
(1706, 12, 110, 'Ne zaman ki elçiler umutlarını kestiler ve kendilerinin yalancı çıkarıldığını sandılar, işte o zaman onlara zaferimiz geldi. Nitekim, dilediğimiz kurtulur. Azabımız suçlular topluluğundan geri çevrilemez.'),
(1707, 12, 111, 'Onların tarihinde, bilinç sahipleri için bir ders vardır. Bu, uydurma bir hadis değil; fakat kendisinden öncekilerin doğrulayıcısı, herşeyin detaylı açıklaması ve inananlar için bir hidayet ve Rahmettir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'A. L. M. R. Bu (harfler), kitabın mucizeleridir. Rabbinden sana indirilen, gerçektir; fakat halkın çoğu inanmaz.'),
(1709, 13, 2, 'Görebileceğiniz bir direk olmadan gökleri yükselten, sonra tüm yönetime egemen olan, güneşi ve ayı buyruk altına alan ALLAH\'tır. Hepsi belli bir süre için akıp gitmektedir. Tüm işleri kontrol eder ve ayetleri detaylı olarak açıklar ki Rabbinizle kavuşma konusunda kuşkunuz kalmasın'),
(1710, 13, 3, 'O ki yeryüzünü inşa edip üzerine sağlam dağlar, ırmaklar ve erkekli dişili yarattığı her çeşit meyveyi yerleştirdi. Geceyi gündüze bürüyor. Düşünen bir toplum için elbette bunda işaretler ve dersler vardır.'),
(1711, 13, 4, 'Yeryüzünde, birbirine komşu toprak parçaları, üzüm bağları, ekinler, çatallı ve çatalsız hurmalıklar vardır. Bunların hepsi aynı su ile sulanmalarına rağmen lezzetçe birbirinden farklı kılmışızdır. Aklını kullanan bir toplum için elbette bunda işaretler ve dersler vardır.'),
(1712, 13, 5, 'Şaşacaksan, onların şu sözlerine şaşmalısın: "Biz toprak olduktan sonra yeniden mi yaratılacağız?" İşte onlar, Rab\'lerini inkar edenlerdir. Onlar, boyunlarına prangalar hakedenlerdir. Onlar ateşin halkıdır; nitekim orada ebedi kalıcıdırlar.'),
(1713, 13, 6, 'İyilik isteyeceklerine, başlarına hemen bir felaket getirmeni isterler; oysa kendilerinden önce örnekler gelip geçti. Rabbin, işledikleri zulme rağmen halka karşı bağışlayıcıdır. Rabbinin azabı çetindir.'),
(1714, 13, 7, 'İnkarcılar, "Ona bir mucize inmeli değil miydi," diyorlar. Sen ancak bir uyarıcısın. Her toplum için bir Yol Gösterici vardır.'),
(1715, 13, 8, 'ALLAH her dişinin yüklendiğini, rahimlerin eksilttiğini ve arttırdığını bilir. O\'nun yanında her şey belli bir ölçüye göredir.'),
(1716, 13, 9, 'Gizliyi ve açığı Bilendir; Büyüktür, Yücedir.'),
(1717, 13, 10, 'Sözü ister gizleyin, ister açıklayın, ister gecenin karanlığında saklanın, ister gündüzün ortaya çıkın O\'nun için birdir.'),
(1718, 13, 11, '(Onların herbiri için) önünden ve ardından nöbetleşerek izleyenler vardır. Onu ALLAH\'ın emriyle korurlar. Bir topluluk kendisini değiştirmedikçe ALLAH onların durumunu değiştirmez. ALLAH bir topluluk için fenalık istedi mi, hiç bir güç onu durduramaz; onların O\'ndan başka bir koruyup kollayanları da olmaz.'),
(1719, 13, 12, 'O, size korku ve umut olarak şimşeği gösteren ve ağır bulutları oluşturandır.'),
(1720, 13, 13, 'Gök gürlemesi O\'nu överek, melekler de O\'nu saygıyla yüceltirler. O, yıldırımları gönderir ve dilediğini onlarla çarpar. Onlar hâlâ ALLAH hakkında tartışıyorlar. O, dayanılmaz güce sahiptir.'),
(1721, 13, 14, 'Gerçek dua O\'na yöneltilendir. O\'nun dışında çağırdıkları ise onlara hiç bir şekilde karşılık veremezler. Tıpkı suya avuçlarını uzatan; ancak ağzına hiç bir şey ulaşmıyan kimse gibi... İnkarcıların duası boşunadır.'),
(1722, 13, 15, 'Göklerde ve yerde olan herkes ister istemez ALLAH\'a secde eder. Gölgeleri de sabah akşam...'),
(1723, 13, 16, 'De ki: "Göklerin ve yerin Rabbi kim?" De ki: "ALLAH" De ki: "O\'ndan başka, kendilerine dahi yarar ve zarar veremiyen kimseleri evliyalar mı edindiniz?" De ki: "Hiç körle gören bir olur mu, yahut hiç karanlıkla ışık bir olur mu?" Yoksa ALLAH\'ın yarattığı gibi yaratan ortaklar buldular da yaratmalarını birbiriyle mi karıştırdılar? De ki: "ALLAH her şeyin yaratıcısıdır, O Tektir, Egemendir."'),
(1724, 13, 17, 'Gökten bir su indirir, dereler onunla dolar taşar ve sel bol köpük yüklenir. Aynı şekilde, mücevherat yahut eşya yapmak için erittikleri maddelerden de benzer bir köpük elde ederler. ALLAH gerçeği ve batılı böyle bir örnekle tanıtır. Köpük, kaybolup gider; ancak insanlara yarar veren ise yerde kalır. İşte ALLAH örnekleri böyle verir.'),
(1725, 13, 18, 'Rab\'lerinin çağrısına uyanlar güzeli hak ederler. O\'nun çağrısına uymayanlar ise, yeryüzündekilerin hepsine ve bir o kadarına sahip olsalar dahi kurtulmak için hepsini fidye olarak vermeye hazır olacaklardır. Sorgulamanın en kötüsü onlar içindir ve yerleri cehennemdir; ne de felaketli bir konaktır o!'),
(1726, 13, 19, 'Sana indirilenin gerçek olduğunu bilen biri, görmeyen gibi midir? Ancak akıl sahipleri öğüt alır.'),
(1727, 13, 20, 'Onlar ki ALLAH\'a verdikleri sözü yerine getirirler ve sözleşmeyi bozmazlar.'),
(1728, 13, 21, 'Onlar ki ALLAH\'ın birleştirmesini emrettiği şeyi birleştirirler, Rab\'lerini sayarlar ve kötü hesaptan korkarlar.'),
(1729, 13, 22, 'Ve onlar ki sadece Rab\'lerinin onayını kazanmak için sabredip direnirler, namazı gözetirler, kendilerine verdiğimiz rızıklardan gizli ve açık yardım için harcarlar ve kötülüğü iyilik ile savarlar. Son durağı onlar hak etmişlerdir.'),
(1730, 13, 23, 'Adn cennetlerine girerler. Atalarından, eşlerinden ve soylarından iyi olanlar da onunla beraberdir. Melekler de her kapıdan yanlarına varırlar.'),
(1731, 13, 24, '"Sabrettiğinizden ötürü selam (barış ve huzur) size olsun. Bu ne güzel son durak!"'),
(1732, 13, 25, 'ALLAH\'a verdikleri sözden sonra sözleşmeyi bozanlar, ALLAH\'ın birleştirmeyi emrettiğini birleştirmeyenler ve yeryüzünde bozgunculuk yapanlar laneti hakketmişlerdir ve onlar için kötü bir sonuç vardır.'),
(1733, 13, 26, 'ALLAH dilediğine rızkı açar veya kısar. Onlar dünya hayatı ile sevindiler, halbuki bu dünya hayatı ahiret ile karşılaştırıldığında bir geçimlikten ibarettir.'),
(1734, 13, 27, 'İnkar edenler, "Ona Rabbinden bir mucize indirilmeli değil miydi," diyorlar. De ki: "ALLAH dileyeni saptırır ve yöneleni doğruya ulaştırır."'),
(1735, 13, 28, 'Onlar ki inanırlar ve kalpleri ALLAH\'ı anmakla huzur bulur. Bilesiniz ki, kalpler ALLAH\'ı anmakla yatışır.'),
(1736, 13, 29, 'İnanıp erdemli bir hayat sürenler için müjde ve en güzel dönüş yeri vardır.'),
(1737, 13, 30, 'Daha önceki toplumlara olduğu gibi seni de bu topluma gönderdik ki sana vahyettiğimizi onlara okuyasın, halbuki onlar Rahman\'a nankörlük ediyorlar. De ki: "O\'dur benim Rabbim; O\'ndan başka tanrı yoktur. O\'na güvendim; dönüş O\'nadır."'),
(1738, 13, 31, 'Kendisiyle dağlar yürütülen, yahut yeryüzü parçalanan, yahut ölüler dirilten bir Kuran olsaydı bile (onlar yine inanmazdı). Tüm işler ALLAH\'ın kontrolündedir. İnananlar hâlâ anlamadılar mı ki ALLAH dileseydi tüm insanları doğruya ulaştırırdı. İnkar edenler, ALLAH\'ın sözü yerine gelinceye kadar yaptıklarına karşılık olarak ya başlarına ya da yakınlarına konacak bir felakete uğrayıp duracaklardır. ALLAH sözünden dönmez.'),
(1739, 13, 32, 'Senden önceki elçilerle de alay edildi. İnkarcılara fırsat verdim ve sonunda onları yakaladım. Sonuç nasıldı?'),
(1740, 13, 33, 'Herkesin yaptığını kontrol eden O değil midir? Buna rağmen ALLAH\'a ortaklar koştular. De ki: "Onları (dilediğiniz) isimlerle tanımlayın! Siz O\'nun yeryüzünde bilmediği bir şeyi mi O\'na haber veriyorsunuz, yoksa boş sözler mi uyduruyorsunuz?" Aslında, inkarcıların hileleri kendilerine süslü gösterilir ve böylece yoldan saparlar. ALLAH kimi saptırırsa ona doğruyu gösterecek yoktur.'),
(1741, 13, 34, 'Onlar dünya hayatında azap hakketmişlerdir. Ahiret azabı ise daha kötüdür. Onları ALLAH\'tan kimse kurtaramaz.'),
(1742, 13, 35, 'Erdemlilere söz verilen cennetin örneği şudur: Altından ırmaklar akar, yemişi de süreklidir gölgesi de. Bu, erdemlilerin sonudur. Kafirlerin sonu ise ateştir.'),
(1743, 13, 36, 'Kendilerine kitap vermiş olduklarımız sana indirilene sevinirler. Bazı gruplar onun bir kısmını inkar ederler. De ki: "ALLAH\'a kul olmakla ve O\'na ortak koşmamakla emredildim. O\'na çağırırım ve dönüşüm de O\'nadır."'),
(1744, 13, 37, 'Böylece onu Arapça bir yasa olarak indirdik. Sana gelen bilgiden sonra onların keyfine uyarsan ALLAH\'a karşı ne bir dostun ne de bir koruyucun olamaz.'),
(1745, 13, 38, 'Senden önce elçiler gönderdik ve onlara eşler ve çocuklar verdik. ALLAH\'ın izni olmadan hiç bir elçi bir mucize getiremez. Her dönemin bir yazgısı vardır.'),
(1746, 13, 39, 'ALLAH dilediğini siler, dilediğini bırakır. Ana Kitap O\'nun yanındadır.'),
(1747, 13, 40, 'Onlara söz verilenlerin bir kısmını sana göstersek de, senin canını alsak da, sana düşen görev bildirmektir. Hesap ise bize düşer.'),
(1748, 13, 41, 'Yeryüzünün uçlarından eksilttiğimizi görmüyorlar mı? ALLAH hüküm verir ve O\'nun hükmünü izleyip çevirecek de yoktur. O, en hızlı hesaplıyandır.'),
(1749, 13, 42, 'Onlardan öncekiler de düzenler kurdular; halbuki tüm düzenler ALLAH\'a aittir. Her kişinin kazandığını bilir. Kafirler de zaferin kime ait olduğunu bileceklerdir.'),
(1750, 13, 43, 'İnkar edenler, "Sen bir elçi değilsin!," diyorlar. De ki, "ALLAH ve kitabın bilgisine sahip olanlar benimle sizin aranızda şahit olarak yeter."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'A. L. R. Bir kitaptır. Onu sana indirdik ki, halkı Rab\'lerinin izniyle karanlıklardan aydınlığa çıkarasın, O Güçlü ve Övgüye layık olanın yoluna...'),
(1752, 14, 2, 'Yani ALLAH\'ın. O ki göklerde ve yerde bulunanların hepsine sahiptir. Hakkettikleri çetin azaptan dolayı vay kafirlerin haline.'),
(1753, 14, 3, 'Onlar ki dünya hayatını ahirete yeğlerler, ALLAH\'ın yolundan saptırırlar ve onu eğriltmek isterler. Bunlar, alabildiğine sapmışlardır.'),
(1754, 14, 4, 'Biz her elçiyi ancak halkının diliyle göndeririz ki onlara bildirebilsin. ALLAH dileyeni/dilediğini saptırır, dileyeni/dilediğini de doğruya ulaştırır. O, Güçlüdür, Bilgedir.'),
(1755, 14, 5, 'Musa\'yı, "Halkını karanlıklardan aydınlığa çıkar ve onlara ALLAH\'ın günlerini hatırlat," diye mucizelerimizle gönderdik. Sabreden ve şükreden herkes için elbette bunda dersler vardır.'),
(1756, 14, 6, 'Hani, Musa halkına, "ALLAH\'ın üzerinizdeki nimetini hatırlayın. Size işkencenin en kötüsünü uygulayan, oğullarınızı kesip kadınlarınızı sağ bırakan Firavun\'un adamlarından sizi O kurtardı. Bu, Rabbinizden sizin için büyük bir test idi," demişti.'),
(1757, 14, 7, 'Rabbin, "Şükrederseniz size daha bol veririm, ama nankörlük ederseniz azabım çetindir," diye bildirmişti'),
(1758, 14, 8, 'Musa: "Yeryüzünde bulunan herkesle birlikte nankörlük etseniz, bilesiniz ki ALLAH muhtaç değildir, övgüye layıktır," demişti.'),
(1759, 14, 9, 'Sizden öncekilerin, Nuh, Aad ve Semud halkının ve onlardan sonra gelip de sadece ALLAH\'ın bildiği kimselerin haberleri size ulaşmadı mı? Elçileri onlara apaçık delillerle gittiler, fakat onları küçümsediler ve "Biz getirdiğiniz şeyi inkar ediyoruz ve bizi çağırdığınız mesaj hakkında kuşkumuz ve şüphemiz var," dediler.'),
(1760, 14, 10, 'Elçileri: "Gökleri ve yeri yarıp yaratan ALLAH\'tan mı kuşkulanıyorsunuz? Günahlarınızı bağışlamak için sizi çağırıyor ve size belli bir süre tanıyor," dediler. Onlar da, "Siz, ancak bizim gibi insanlarsınız, atalarımızın tapmakta olduğu şeyden bizi çevirmek istiyorsunuz. Bize açık bir yetki belgesi getiriniz," dediler.'),
(1761, 14, 11, 'Elçileri ise kendilerine şöyle dediler: "Biz, elbette sizin gibi birer insanız. Ancak, ALLAH, kullarından dilediğini seçerek ona lütufta bulunur. ALLAH\'ın izni olmadan size bir yetki belgesi getirmemiz olanaksızdır. İnananlar ALLAH\'a güvenmeli.'),
(1762, 14, 12, '"Bize yollarımızı göstermişken neden ALLAH\'a güvenmiyelim? Sizin bize yaptığınız eziyete karşı sabırla direneceğiz. Güvenenler ALLAH\'a güvenmeli."'),
(1763, 14, 13, 'İnkarcılar elçilerine, "Ya bizim dinimize geri dönersiniz ya da sizi yurdumuzdan kovarız!," dediler. Rab\'leri onlara, "Zalimleri yok edeceğiz," diye vahyetti,'),
(1764, 14, 14, '"Onlardan sonra o yurda sizi yerleştireceğiz. Bu, otoriteme saygı duyan ve tehditlerimden korkanlar içindir."'),
(1765, 14, 15, 'Zafer istediler, böylece her inatçı zorba perişan oldu.'),
(1766, 14, 16, 'Ardından da cehennem... Tadı bozuk sudan içirilirler'),
(1767, 14, 17, 'Onu yutmaya çalışır, ama boğazından geçiremez. Ölüm kendisine her yandan gelir, ama ölemiyecek. Ardından da çetin bir azap gelecektir.'),
(1768, 14, 18, 'Rab\'lerini inkar edenlerin yaptıkları işler, fırtınalı bir günde rüzgarın savurduğu kül gibidir. Kazandıklarından hiç bir şey elde edemezler. İşte gerçek kayıp budur.'),
(1769, 14, 19, 'ALLAH\'ın gökleri ve yeri belli bir amaç için yarattığını görmez misiniz? Dilerse sizi götürüp, yerinize yeni yaratıklar getirir.'),
(1770, 14, 20, 'Bu, ALLAH için güç değildir.'),
(1771, 14, 21, 'Hepsi ALLAH\'ın huzuruna durduklarında. Zayıflar, büyüklük taslamış olanlara: "Biz sizin izleyicileriniz idik. Bizi ALLAH\'ın azabından bir parça kurtarabilir misiniz," dediler. Onlar da: "ALLAH bize bir yol gösterseydi biz de size gösterirdik. Artık sızlansak da dayansak da bizim için birdir, kaçıp kurtulacak bir yerimiz yok."'),
(1772, 14, 22, 'Karar yayımlandıktan sonra şeytan onlara şöyle dedi: "ALLAH size gerçeği söz verdi, ben ise size söz verdim ve sözümden caydım. Benim sizin üzerinize herhangi bir gücüm yoktu; ben sizi çağırdım, siz de bana katıldınız. Bundan dolayı beni kınamayın, yalnızca kendinizi kınayın. Ne siz beni kurtarabilirsiniz ne de ben sizi kurtarabilirim. Beni ortak koşmanızı zaten önce de inkar etmiştim. Zalimler için acı bir azap vardır.'),
(1773, 14, 23, 'İnanan ve erdemli davrananlar, içinden ırmaklar akan cennetlere sokulmuşlardır. Rab\'lerinin izniyle orada sürekli kalırlar. Oradaki dirlik temennileri, "Selam," dır.'),
(1774, 14, 24, 'Görmez misin ki ALLAH güzel bir sözü güzel bir ağaca benzetir: Kökü sabit, dalları ise göktedir.'),
(1775, 14, 25, 'Rabbinin izniyle her mevsim meyvelerini verir. Öğüt almaları için ALLAH insanlara böyle örnekler verir.'),
(1776, 14, 26, 'Kötü bir söz de, yerden koparılmış köksüz kötü bir ağaç gibidir.'),
(1777, 14, 27, 'ALLAH inananları dünya hayatında da, ahirette de kanıtlanmış sözle destekler. ALLAH zalimleri ise saptırır ve ALLAH dilediğini yapar.'),
(1778, 14, 28, 'ALLAH\'ın nimetini nankörlükle karşılayarak halklarını felaket yurduna mahkum edenleri görmedin mi?'),
(1779, 14, 29, 'Cehennemde yanacaklardır. Ne kötü bir duraktır o!'),
(1780, 14, 30, 'ALLAH\'ın yolundan saptırmak için O\'na eşler koştular. De ki: "Biraz eğlenin, son yolculuğunuz ateşe olacaktır."'),
(1781, 14, 31, 'İnanan kullarıma söyle, alış-verişin ve dostluğun olmadığı gün gelmeden önce namazı gözetsinler, kendilerine verdiğimiz rızıklardan gizli ve açık yardım için versinler.'),
(1782, 14, 32, 'ALLAH gökleri ve yeri yaratan, gökten bir su indirip sizin için çeşitli meyvelerden besinler çıkarandır. Koyduğu yasaya göre sizi denizde taşıması için gemileri emrinize verdi. Aynı şekilde ırmakları da emrinize verdi.'),
(1783, 14, 33, 'Görevlerini aksatmayan güneşi ve ay\'ı hizmetinize sundu, geceyi ve gündüzü de hizmetinize sundu.'),
(1784, 14, 34, 'O\'ndan ne dilediyseniz size verdi. ALLAH\'ın nimetlerini sayarsanız bitiremezsiniz. İnsanoğlu gerçekten çok zalimdir, çok nankördür.'),
(1785, 14, 35, 'Bir zamanlar İbrahim demişti ki: "Rabbim, bu beldeyi güvenli kıl, beni ve çocuklarımı putlara tapmaktan koru."'),
(1786, 14, 36, '"Rabbim, onlar, insanlardan bir çoğunu saptırdılar. Beni izleyenler bendendir. Bana karşı gelenlere gelince, elbette sen Bağışlayansın, Rahimsin."'),
(1787, 14, 37, '"Rabbimiz, ben çocuklarımdan bazısını, Kutsal Evinin yanındaki ekinsiz bir vadiye yerleştirdim. Rabbimiz, onlar namazı gözetsinler diye... İnsanların gönüllerini onlara karşı sempatiyle doldur ve onları ürünlerle rızıklandır ki şükretsinler."'),
(1788, 14, 38, '"Rabbimiz, bizim gizlediğimizi ve açıkladığımızı bilirsin. Yerde ve gökte hiç bir şey ALLAH\'a gizli kalmaz."'),
(1789, 14, 39, '"İhtiyarlık çağımda bana İsmail\'i ve İshak\'ı bahşeden ALLAH\'a övgüler olsun. Rabbim, dualarımı işitir."'),
(1790, 14, 40, '"Rabbim, beni namazı gözeten biri kıl, çocuklarımı da... Rabbimiz dualarımı kabul et."'),
(1791, 14, 41, '"Rabbimiz, hesabın görüleceği gün, beni, anamı babamı ve inananları bağışla."'),
(1792, 14, 42, 'Zalimlerin yapmakta olduklarından ALLAH\'ı habersiz sanma. Onları sadece, gözlerin dehşetten donup kalacağı güne kadar ertelemektedir.'),
(1793, 14, 43, 'Başları boyunları üstüne kaskatı dikilmiş, göz kapakları kıpırdamaksızın korkudan dolayı zihinleri bomboş bir halde koşuşurlar.'),
(1794, 14, 44, 'Halkı, azabın kendilerine geleceği gün konusunda uyar. Zalimler, "Rabbimiz bizi yakın bir süreye kadar ertele de senin çağrına katılalım ve elçilere uyalım," diyecekler. Oysa daha önce siz, sonunuzun gelmeyeceğine yemin etmemiş miydiniz?'),
(1795, 14, 45, 'Hatta, sizden önce kendilerine zulmetmiş olanların konutlarına yerleşmiştiniz ve onlara yaptıklarımız da size belli olmuştu. Üstelik, size örnekler de vermiştik.'),
(1796, 14, 46, 'Onlar planlarını yaptılar, halbuki planları ALLAH\'ın kontrolündeydi. Planları dağları yerinden oyanatacak kadar olsa bile..'),
(1797, 14, 47, 'ALLAH\'ın, elçilerine verdiği sözden döneceğini sanmayın. ALLAH elbette Üstündür, Öc alandır.'),
(1798, 14, 48, 'Yerin başka bir yer ile göklerin de başka göklerle değiştirildiği, herkesin Tek ve En yüce olan ALLAH\'ın huzuruna getirildiği gün...'),
(1799, 14, 49, 'O gün suçluları, zincirlere vurulmuş olarak görürsün.'),
(1800, 14, 50, 'Giysileri katrandandır ve yüzlerini ateş kaplar.'),
(1801, 14, 51, 'Böylece ALLAH herkese yaptığının karşılığını verir. ALLAH hızlı hesap görendir.'),
(1802, 14, 52, 'Bu, insanlara bir bildiridir. Bu uyarıyı dinlesinler, O\'nun yalnız tek Tanrı olduğunu bilsinler ve akıl sahipleri öğüt alsınlar.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'A. L. R. Bu (harfler) kitabın ve apaçık Kuran\'ın mucizesidir.'),
(1804, 15, 2, 'İnkar edenler, keşke müslüman olsaydık diye arzulayacaklardır.'),
(1805, 15, 3, 'Bırak onları, yesinler, eğlensinler ve umutlarıyla oyalansınlar; mutlaka öğreneceklerdir.'),
(1806, 15, 4, 'Biz hiç bir topluluğu belirlenmiş bir yazgı olmaksızın yok etmeyiz.'),
(1807, 15, 5, 'Hiç bir toplum belirlenmiş süresini ne geçebilir, ne de gerisinde kalır.'),
(1808, 15, 6, 'Dediler ki: "Ey kendisine zikir (mesaj) indirilmiş olan, sen bir delisin."'),
(1809, 15, 7, '"Doğru sözlü isen bize melekleri getirsene."'),
(1810, 15, 8, 'Biz melekleri ancak belli bir amaç için göndeririz, o zaman da kimseye süre tanınmaz.'),
(1811, 15, 9, 'Kuşkusuz zikri (mesajı) biz, evet biz indirdik ve onu koruyacak da elbette yine biziz.'),
(1812, 15, 10, 'Senden önce, geçmiş topluluklara da elçiler gönderdik.'),
(1813, 15, 11, 'Onlara her ne zaman bir elçi geldiyse onu alaya aldılar.'),
(1814, 15, 12, 'İşte suçluların kalbine böyle (bir tavrı) sokarız.'),
(1815, 15, 13, 'Nitekim, ona inanmazlar. Kendilerinden öncekilerin sünneti (adeti) de böyleydi.'),
(1816, 15, 14, 'Onlara gökten bir kapı açsak ve onun içinde yükselecek olsalardı,'),
(1817, 15, 15, '"Gözlerimiz sarhoş edildi, büyülendik," diyeceklerdi.'),
(1818, 15, 16, 'Gökte galaksiler yerleştirdik, ve gözleyenler için onları süsledik.'),
(1819, 15, 17, 'Ve onları her kovulmuş şeytandan koruduk.'),
(1820, 15, 18, 'Ancak onlardan kulak hırsızlığı eden olursa onu alevli bir ateş mermisi kovalar'),
(1821, 15, 19, 'Yeryüzünü genişletip içine sağlam dağlar yerleştirdik ve orada her şeyi mükemmel bir ölçüye göre bitirdik.'),
(1822, 15, 20, 'Orada hem sizin için ve hem sizin beslemediğiniz yaratıklar için yaşanacak bir ortam oluşturduk.'),
(1823, 15, 21, 'Hiç bir şey yoktur ki onun hazineleri bizim yanımızda olmasın, ancak biz onu belirlenmiş bir ölçüyle indiririz.'),
(1824, 15, 22, 'Rüzgarı aşılayıcı olarak gönderdik; gökten bir su indirip sizi onunla suladık. Oysa siz onu depo edemezdiniz.'),
(1825, 15, 23, 'Biz, elbette biz diriltir ve öldürürüz, sonunda asıl varis olanlar da biziz.'),
(1826, 15, 24, 'İlerleyenlerinizi de biliriz, geride kalanları da biliriz.'),
(1827, 15, 25, 'Onları, senin Rabbin toplayacak. O Bilgedir, Bilendir.'),
(1828, 15, 26, 'İnsanı, kurumuş, yıllanmış balçıktan yarattık.'),
(1829, 15, 27, 'Cinleri de önceden, içe işleyen parlak ateşten yarattık.'),
(1830, 15, 28, 'Rabbin meleklere, "Kurumuş, yıllanmış balçıktan bir insan yaratacağım," demişti.'),
(1831, 15, 29, '"Onu düzenleyip ona ruhumdan üflediğimde hemen onun için secdeye varın," demişti.'),
(1832, 15, 30, 'Tüm melekler topluca secde ettiler;'),
(1833, 15, 31, 'Ancak İblis (şeytan) hariç. Secde edenlerle beraber olmayı red etti.'),
(1834, 15, 32, 'Dedi ki: "Seni secde edenlerle beraber olmaktan alıkoyan şey nedir, İblis?"'),
(1835, 15, 33, 'Dedi ki: "Kurumuş, yıllanmış balçıktan yarattığın insana secde edecek değilim."'),
(1836, 15, 34, '(Tanrı:) "Öyleyse çık oradan; sen kovuldun!"'),
(1837, 15, 35, '"Yargı gününe kadar laneti hakkettin," dedi.'),
(1838, 15, 36, '"Rabbim, dirilecekleri güne kadar beni ertele," dedi.'),
(1839, 15, 37, 'Dedi ki: "Tamam, sen ertelendin,"'),
(1840, 15, 38, '"Bilinen vaktin gününe kadar...."'),
(1841, 15, 39, 'Dedi ki: "Rabbim, beni yoldan çıkarttığın için, onları yeryüzünde ayartıp topluca saptıracağım."'),
(1842, 15, 40, '"Kendilerini sadece sana adayanlar hariç."'),
(1843, 15, 41, 'Dedi ki: "İşte benim değişmez yasam budur."'),
(1844, 15, 42, '"Elbette, sadece bana kulluk edenlere karşı bir gücün yoktur. Ancak sana uyan sapıklara gücün yeter."'),
(1845, 15, 43, '"Cehennem hepsinin buluşma yeridir."'),
(1846, 15, 44, '"Onun yedi kapısı vardır ve her bir kapı için onlardan belli bir pay vardır."'),
(1847, 15, 45, 'Erdemliler ise bahçeler ve pınarlar içindedir.'),
(1848, 15, 46, 'Oraya barış ve güvenlik içinde girin.'),
(1849, 15, 47, 'Göğüslerindeki kıskançlığı kaldırırız; kardeşçe karşılıklı yerleştirilmiş koltuklar üzerindedirler.'),
(1850, 15, 48, 'Orada onlara hiç bir yorgunluk dokunmaz ve oradan da çıkarılacak değillerdir.'),
(1851, 15, 49, 'Kullarıma haber ver ki ben Bağışlayıcıyım, Rahimim.'),
(1852, 15, 50, 'Ve azabım da çok acı bir azaptır.'),
(1853, 15, 51, 'Onlara İbrahim\'in konuklarından haber ver.'),
(1854, 15, 52, 'Onun yanına varıp, "Selam!," demişlerdi. O da, "Biz sizden korkuyoruz," demişti.'),
(1855, 15, 53, '"Endişelenme, biz sana bilgin bir oğul müjdesini veriyoruz," demişlerdi.'),
(1856, 15, 54, '"İyice yaşlanmışken beni mi müjdeliyorsunuz! Beni ne ile müjdeliyorsunuz," dedi.'),
(1857, 15, 55, '"Sana gerçeği müjdeledik, umudunu kesme," dediler.'),
(1858, 15, 56, '"Sapıklardan başka Rabbinin rahmetinden kim umut keser," dedi.'),
(1859, 15, 57, '"Ey elçiler, göreviniz nedir," dedi.'),
(1860, 15, 58, '"Biz, suçlu bir topluma gönderildik;"'),
(1861, 15, 59, '"Ancak Lut\'un ailesi hariç. Hepsini kurtaracağız."'),
(1862, 15, 60, '"Yalnız karısı hariç; onun geride kalanlardan olmasını kararlaştırdık," dediler.'),
(1863, 15, 61, 'Elçiler, Lut\'un ailesine geldiklerinde'),
(1864, 15, 62, '(Lut:) "Siz, bizce tanınmayan bir topluluksunuz," dedi.'),
(1865, 15, 63, 'Dediler ki: " Onların kuşkulandıkları şeyi sana getirdik."'),
(1866, 15, 64, '"Sana gerçeği getirdik, biz elbette doğru konuşuyoruz."'),
(1867, 15, 65, '"Geceleyin ailenle birlikte çık. Arkalarından izle ve hiç biriniz arkaya bakmasın. Size emredilen yere gidin."'),
(1868, 15, 66, '\'Şu halk, sabahleyin yok edilecektir\' şeklindeki emri kendisine bildirdik.'),
(1869, 15, 67, 'Kent halkı neşe içinde geldiler.'),
(1870, 15, 68, '"Bunlar konuklarımdır, sakın beni utandırmayın."'),
(1871, 15, 69, '"ALLAH \'tan korkun, beni rezil etmeyin," dedi.'),
(1872, 15, 70, '"İnsanlarla diyalog kurmaktan seni menetmemiş miydik," dediler.'),
(1873, 15, 71, '"İşte benim kızlarım," dedi, "İlla da istiyorsanız!"'),
(1874, 15, 72, 'Ne yazık ki onlar, sarhoşlukları içinde bocalıyorlardı.'),
(1875, 15, 73, 'Tan ağarırken onları felaketli bir gürültü yakaladı.'),
(1876, 15, 74, 'Onun altını üstüne getirdik. Üzerlerine çamurdan yapılmış sert taşlar indirdik.'),
(1877, 15, 75, 'Bunda, inceleyip araştıranlar için dersler vardır.'),
(1878, 15, 76, 'Ve o (yıkıntı kent), yol üzerinde durmaktadır.'),
(1879, 15, 77, 'Bunda, inananlar için bir işaret vardır.'),
(1880, 15, 78, 'Gerçekten Eyke halkı da zalimlerden idi.'),
(1881, 15, 79, 'Onlardan öc aldık. Her ikisi de belgelenmiştir.'),
(1882, 15, 80, 'Hicr halkı da elçileri yalanladı.'),
(1883, 15, 81, 'Kendilerine ayetlerimizi verdik, fakat ondan yüz çevirdiler.'),
(1884, 15, 82, 'Dağlardan güvenli evler yontuyorlardı.'),
(1885, 15, 83, 'Sabahleyin onları da o korkunç gürültü yakaladı.'),
(1886, 15, 84, 'Kazandıklarının hiç biri onları kurtaramadı.'),
(1887, 15, 85, 'Biz, gökleri, yeri ve aralarındaki şeyleri belli bir amaç için yarattık. O saat (dünyanın sonu) elbette gelecektir, öyleyse onlara güzel ve yumuşak davran.'),
(1888, 15, 86, 'Rabbin elbette Yaratandır, Bilendir.'),
(1889, 15, 87, 'Biz sana yedi çifti ve büyük Kuran\'ı verdik.'),
(1890, 15, 88, 'Onlardan (elçilerden) bazılarına verdiklerimizi kıskanma ve onlardan (inkarcılardan) ötürü de üzülme. İnananlara kanatlarını indir.'),
(1891, 15, 89, 'De ki: "Ben apaçık bir uyarıcıyım."'),
(1892, 15, 90, 'Aynı şekilde o bölücülerle de ilgileneceğiz.'),
(1893, 15, 91, 'Onlar ki Kuran\'ı parçalara ayırdılar.'),
(1894, 15, 92, 'Rabbine and olsun ki, hepsinden soracağız'),
(1895, 15, 93, 'Yaptıkları şeylerden.'),
(1896, 15, 94, 'Öyleyse sana emredileni açıkça ortaya koy ve müşriklere de aldırma.'),
(1897, 15, 95, 'Alay edenlere karşı biz sana yeteriz.'),
(1898, 15, 96, 'Onlar ki ALLAH ile beraber başka tanrılar oluşturdular. Yakında bilecekler.'),
(1899, 15, 97, 'Söylediklerinden ötürü göğsünün daraldığını biliyoruz'),
(1900, 15, 98, 'Rabbini yüceltip coşkuyla an ve yerlere kapan.'),
(1901, 15, 99, 'Rabbine kulluk et ki kesin bir inanca sahip olasın.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'ALLAH\'ın emri gelmiş bulunuyor, öyleyse acele etme. Onların ortak koştuklarından uzaktır, yücedir.'),
(1903, 16, 2, 'Kullarından dilediğine, melekleri vahiyle indirerek, emrini iletir: "İnsanları uyarın: Benden başka tanrı yoktur ve beni dinleyin."'),
(1904, 16, 3, 'Gökleri ve yeri belli bir amaç için yaratmıştır. Ortak koştuklarından çok yücedir.'),
(1905, 16, 4, 'İnsanı küçük bir damladan yarattı, fakat buna rağmen o, apaçık bir düşman kesildi.'),
(1906, 16, 5, 'Ve, çiftlik hayvanlarını da sizin için yarattı, onlarda ısıtma ve diğer yararlar vardır ve onlardan yersiniz.'),
(1907, 16, 6, 'Ayrıca boş zamanlarınızda ve seyahatlerinizde sizin için onlarda bir konfor ve zevk vardır.'),
(1908, 16, 7, 'Ve sizin yüklerinizi de, kendi kendinize ancak zorlukla varacağınız bölgelere taşırlar. Rabbiniz elbette Rauf\'tur, Rahimdir.'),
(1909, 16, 8, 'Binmeniz ve eğlenmeniz için atları, katırları ve eşekleri yarattı ve daha bilmediğiniz bir çok şeyi de yaratır.'),
(1910, 16, 9, 'Yolları göstermek ALLAH\'a aittir; bazısı eğridir. Dileseydi hepinizi doğruya iletirdi.'),
(1911, 16, 10, 'O ki gökten bir su indirdi, ondan içersiniz ve yine yararlandığınız ağaçları onunla sularsınız.'),
(1912, 16, 11, 'Onunla sizin için ekin, zeytin, hurma ağaçları, üzümler ve her çeşit meyveler bitirmektedir. Düşünen bir toplum için bunda elbette bir ayet (ders) vardır.'),
(1913, 16, 12, 'Geceyi, gündüzü, güneşi ve ayı hizmetinize sundu. Yıldızlar da O\'nun emri altındadır. Aklını kullanan bir toplum için elbette bunda işaretler vardır.'),
(1914, 16, 13, 'Ve sizin için yeryüzünde yarattığı rengarenk nesneleri de... Düşünen bir toplum için elbette bunda bir ders vardır.'),
(1915, 16, 14, 'O ki denizi emrinize sunmuştur; ondan taze et yersiniz, ondan giyim ve takı için süsler çıkarırsınız. Gemilerin denizi yara yara akıp gittiğini görürsünüz. Böylece O\'nun lütfunu ararsınız ve belki ona şükredersiniz.'),
(1916, 16, 15, 'Ve sarsılmamanız için yeryüzüne denge sağlayıcılar yerleştirdi, yolu bulmanız için de nehirler, yollar,'),
(1917, 16, 16, 'Ve göze çarpan işaretler... Yıldızlarla da yol bulurlar.'),
(1918, 16, 17, 'Yaratan, yaratamıyan gibi olur mu? Düşünmez misiniz?'),
(1919, 16, 18, 'ALLAH\'ın nimetini saysanız bitiremezsiniz. ALLAH Bağışlayandır, Rahimdir.'),
(1920, 16, 19, 'ALLAH gizlediklerinizi de açıkladıklarınızı da bilir.'),
(1921, 16, 20, 'ALLAH\'ın dışında çağırdıkları kişiler hiç bir şey yaratamazlar, aksine kendileri yaratılmışlardır.'),
(1922, 16, 21, 'Ölüdürler, diri değildirler. Ne zaman dirileceklerini de bilmezler.'),
(1923, 16, 22, 'Tanrınız bir tek tanrıdır. Ahirete inanmıyanların kalpleri inkarcıdır ve onlar büyüklük taslarlar.'),
(1924, 16, 23, 'Kuşkusuz ALLAH onların gizlediklerini de açığa vurduklarını da bilir. O, büyüklük taslayanları sevmez.'),
(1925, 16, 24, 'Kendilerine, "Rabbiniz ne indirdi," denildiğinde, "Geçmişlerin efsanelerini...," diye yanıtlarlar.'),
(1926, 16, 25, 'Diriliş gününde kendi günahlarının tamamını, ayrıca saptırdıkları kimselerin günah yüklerinden de bir payı yüklenirler. Yükleri ne de kötüdür.'),
(1927, 16, 26, 'Kendilerinden öncekiler de tuzak kurmuşlardı; ancak ALLAH, binalarını temelinden yıkmış, üzerlerindeki tavan başlarına çökmüştü; azap onlara ummadıkları yerden gelmişti.'),
(1928, 16, 27, 'Sonra, diriliş gününde onları rezil eder ve, "Uğrunda tartışıp mücadele verdiğiniz \'ortaklarım\' nerede," der. Kendilerine bilgi verilenler de şöyle derler: "Bu gün rezillik ve kötülük kafirleredir."'),
(1929, 16, 28, 'Onlar ki, nefislerine zulmedip dururlarken melekler canlarını alır. Sonunda teslim olup "Biz herhangi bir kötülük yapmıyorduk," derler. Hayır, ALLAH sizin yaptıklarınızı iyi bilir.'),
(1930, 16, 29, 'İçinde sürekli kalacağınız cehennemin kapsından girin. Kibirlenenlerin yeri ne kötüdür.'),
(1931, 16, 30, 'Erdemlilere, "Rabbiniz ne indirdi," denir. "İyilik," diye karşılık verirler. Bu dünyada güzel davrananlar için güzellik vardır. Ahiret yurdu ise daha hayırlıdır. Erdemlilerin yurdu ne de iyidir.'),
(1932, 16, 31, 'İçlerinden ırmaklar akan Adn cennet (bahçe) lerine girerler. Orada her diledikleri şeyi bulurlar. ALLAH erdemlileri işte böyle ödüllendirir.'),
(1933, 16, 32, 'İyi durumdayken melekler canlarını almaya geldiklerinde, "Selam size olsun. Yaptıklarınızın karşılığı olarak cennete giriniz," derler.'),
(1934, 16, 33, 'Kendilerine meleklerin gelmesini, yahut Rabbinin emrinin gelmesini mi bekliyorlar? Kendilerinden öncekiler de aynı şekilde davranmışlardı. Onlara ALLAH zulmetmedi, onlar kendi kendilerine zulmettiler.'),
(1935, 16, 34, 'Yapmış olduklarının kötü sonuçları onlara dokundu ve alaya almış oldukları şeyler onları kuşattı.'),
(1936, 16, 35, 'Ortak koşanlar, ALLAH dilemeseydi ne biz, ne de atalarımız O\'ndan başka bir şeye tapmaz ve O\'nun haram ettiğinden başkasını da haram kılmazdık. Kendilerinden öncekiler de böyle davranmıştı. Elçinin açıkça bildirmekten başka bir görevi mi var?'),
(1937, 16, 36, 'Her bir toplum içinde, "ALLAH\'a kulluk edin ve putperestlikten sakının," diyen bir elçi gönderdik. Onlardan kimine ALLAH yol gösterdi, kimi de sapıklıkta kalmağa mahkum oldu. Yeryüzünü dolaşın ve yalanlayanların sonunun nasıl olduğuna dikkat edin.'),
(1938, 16, 37, 'Doğruyu bulmaları için ne kadar uğraşsan da, ALLAH saptırdığını doğruya ulaştırmaz. Onların bir yardımcısı da olmaz.'),
(1939, 16, 38, '"ALLAH ölmüş olanı diriltmez diye tüm güçleriyle ALLAH\'a yemin ederler. Hayır, O\'nun verdiği gerçek bir sözdür o. Ancak insanların çoğu bunu bilmiyor.'),
(1940, 16, 39, 'Anlaşmazlığa düştükleri konuları onlara açıklasın ve kafirler de kendilerinin yalancı olduğunu öğrensin.'),
(1941, 16, 40, 'Bir şeyin olmasını istediğimiz zaman ona sadece "Ol," deriz ve o da olur.'),
(1942, 16, 41, 'Zulme uğradıktan sonra ALLAH uğrunda göç edenleri, dünyada güzelce yerleştireceğiz. Ahiret ödüllleri ise daha büyüktür; bir bilseler...'),
(1943, 16, 42, 'Onlar, sabrederek Rab\'lerine güvenenlerdir.'),
(1944, 16, 43, 'Senden önce, adamlardan/iki ayak üzerinde yürüyenlerden başkasını elçi olarak göndermedik; onlara vahyettik. Bilmiyorsanız uzmanlara sorun.'),
(1945, 16, 44, 'Belgeler ve kitaplarla... Sana bu mesajı indirdik ki, kendilerine indirileni halka açıklayasın (bildiresin) ve onlar da düşünsünler.'),
(1946, 16, 45, 'Kötülük planlıyanlar, ALLAH\'ın kendilerini yerin dibine geçirmeyeceğinden, yahut ummadıkları bir yerden kendilerine azabın gelmiyeceğinden emin mi oldular?'),
(1947, 16, 46, 'Yahut (yataklarında) dönüp dururlarken kendilerini yakalamayacağından..? Onlar kurtulamazlar.'),
(1948, 16, 47, 'Yahut onları korku ve endişe içinde iken yakalamayacağından..? Rabbiniz elbette Şefkatlidir, Rahimdir.'),
(1949, 16, 48, 'ALLAH\'ın yarattığı şeylere bakmıyorlar mı ki, gölgeleri ALLAH\'a secde ederek sağdan ve soldan sürünerek gidip gelir'),
(1950, 16, 49, 'Göklerde ve yerde bulunan tüm yaratıklar ve melekler ALLAH\'a secde ederler ve büyüklük taslamazlar.'),
(1951, 16, 50, 'Kendilerinden çok yüce olan Rab\'lerini sayarlar ve kendilerine emredileni uygularlar.'),
(1952, 16, 51, 'ALLAH, "İki tanrı edinmeyin, O\'ndan başka tanrı yoktur. Sadece benden çekinin," demiştir.'),
(1953, 16, 52, 'Göklerde ve yerde ne varsa O\'nundur. Din de sürekli sadece O\'nundur. ALLAH\'tan başkasını mı sayıyorsunuz?'),
(1954, 16, 53, 'Size ulaşan her nimet ALLAH\'tandır. Nitekim, bir sıkıntıya uğradığınızda sadece O\'na yalvarırsınız.'),
(1955, 16, 54, 'Ne var ki, sizden o sıkıntıyı kaldırdığı zaman, sizden bir grup hemen Rab\'lerine ortak koşmaya başlar,'),
(1956, 16, 55, 'Kendilerine verilene nankörlük ederek... Keyfinize bakın; yakında mutlaka bileceksiniz!'),
(1957, 16, 56, 'Kendilerine verdiğimiz rızıklardan bilinçsizlere bir pay ayırırlar. ALLAH\'a andosun ki siz bu uydurduğunuz şeylerden sorulacaksınız.'),
(1958, 16, 57, 'Yüce ALLAH\'a kızları ayırıyorlar; kendilerine de hoşlandıklarını..'),
(1959, 16, 58, 'Onlardan birine dişi müjdelendiği zaman, büyük bir öfkeyle yüzü kapkara kesilir.'),
(1960, 16, 59, 'Kendisine müjdelenen \'kötülükten\' utanarak halkından kendini gizlemeye çalışır. Şimdi onu utana utana tutsun mu, yoksa toprağa mı gömsün! Ne kötü yargıda bulunuyorlar.'),
(1961, 16, 60, 'Ahirete inanmıyanlar kötü örneği oluştururlar; halbuki en yüce örnekler ALLAH\'ındır. O Üstündür, Bilgedir.'),
(1962, 16, 61, 'ALLAH zulümlerinden dolayı insanları cezalandırsaydı orada bir yaratık bırakmazdı; ne var ki onları belli bir süreye kadar erteler. Sürelerinin sonu geldiği zaman da ne bir saat öne alınırlar, ne de ertelenirler.'),
(1963, 16, 62, 'Hoşlanmadıkları şeyleri ALLAH\'a malediyorlar ve kendilerinin iyi hakkettikleri yalanını dillerinde geveliyorlar. Elbette, ateşe terkedilecekler.'),
(1964, 16, 63, 'ALLAH\'a andolsun ki senden önceki toplumlara da elçiler gönderdiğimizde şeytan işlerini onlara güzel gösterdi. Nitekim onların velisi (yakın arkadaşı) bugün odur. Onlar acı bir azaba mahkum olmuşlardır.'),
(1965, 16, 64, 'Sana bu kitabı indirdik ki, anlaşmazlığa düştükleri konuları kendilerine bildiresin. Bu kitap, inanan bir topluluk için bir yol göstericidir, bir rahmettir.'),
(1966, 16, 65, 'ALLAH gökten bir su indirir ve onunla yeryüzünü ölümünden sonra diriltir. Dinleyen bir toplum için bunda bir işaret vardır.'),
(1967, 16, 66, 'Sizin için çiftlik hayvanlarında da dersler vardır. İçmeniz için karınlarından, yani sindirilmiş yiyecek ile kan arasından çıkan bir maddeyi size sunuyoruz: İçimi lezzetli saf süt.'),
(1968, 16, 67, 'Ve hurma ağaçlarının meyvelerinden, üzümlerden hem sarhoş edici içkiler ve hem de güzel bir rızık elde edersiniz. Akleden bir toplum için bunda bir işaret vardır.'),
(1969, 16, 68, 'Ve Rabbin arıya vahyetti: "Dağlardan, ağaçlardan ve kurdukların (kovanlar) dan evler edin,"'),
(1970, 16, 69, '"Sonra, her çeşit meyveden ye ve Rabbinin planını aynen izle." Karınlarından, insanlar için şifa içeren çeşitli renklerden bir içecek çıkar. Düşünen insanlar için bunda bir işaret vardır.'),
(1971, 16, 70, 'Ve sizi ALLAH yarattı, sonra da yaşamınıza son verir. Sizden bazıları hayatın en kötü zamanına kadar yaşatılır ki bilgiden sonra hiçbir şeyi bilmesin. ALLAH Bilendir, Her Şeye Gücü Yetendir.'),
(1972, 16, 71, 'ALLAH rızık (varlık) açısından sizi birbirinize üstün kılmıştır. Nitekim, üstün kılınanlar, emirleri altındakilerle varlıklarını eşit paylaşmazlar. ALLAH\'ın nimetini mi reddediyorlar?'),
(1973, 16, 72, 'Ve ALLAH kendi cinsinizden sizin için eşler yarattı, eşlerinizden de sizin için çocuklar ve torunlar yarattı ve size güzel rızıklar verdi. Böyleyken onlar batıla inanıp ALLAH\'ın nimetine nankörlük mü ediyorlar?'),
(1974, 16, 73, 'ALLAH\'ı bırakıp, kendilerine göklerden ve yerden rızık sağlamıyan aciz kişilere kulluk ediyorlar.'),
(1975, 16, 74, 'Öyleyse, ALLAH\'a örnekler vermeyiniz; siz bilmezken ALLAH bilir.'),
(1976, 16, 75, 'ALLAH, hiçbir şeye gücü yetmeyen, sahiplenilmiş bir köle ile kendi tarafımızdan güzelce rızıklandırdığımız, o rızıktan gizli ve açık yardım için harcayan kişiyi örnek olarak verir. Bu ikisi hiç bir olur mu? Övgü ALLAH\'adır, ancak çokları bilmezler.'),
(1977, 16, 76, 'ALLAH şu iki adamı da örnek verir: Birisi bir şey yapacak yeteneğe sahip olmayıp tümüyle efendisine/sahibine bağımlıdır, onu ne işe koşsa bir şey beceremez. İşte böyle bir kişi ile doğru yol üzerinde bulunup adaleti uygulayan kişi hiç bir olur mu?'),
(1978, 16, 77, 'Göklerin ve yerin gizemleri ALLAH\'a aittir. Saat, (dünyanın sonu) bir göz kırpması kadar veya daha kısadır. ALLAH her şeye Gücü Yetendir'),
(1979, 16, 78, 'ALLAH sizi annelerinizin karnından çıkardığı zaman bir şey bilmiyordunuz. Size işitme, görme duyuları ve beyinler verdi ki şükredesiniz.'),
(1980, 16, 79, 'Göğün atmosferine uygun yaratılmış kuşları görmüyorlar mı? Onları ALLAH\'tan başkası tutmaz. Bunda inanan bir toplum için elbette ayetler vardır.'),
(1981, 16, 80, 'Ve ALLAH evlerinizi sizin için barınaklar yaptı. Çiftlik hayvanlarının derilerinden, yolculuk günü ve ikamet günü kullanacağınız portatif evler sağladı. Yünlerinden, kürklerinden ve kıllarından da bir süreye kadar giyip döşeyeceğiniz eşyalar çıkardı.'),
(1982, 16, 81, 'Ve ALLAH yarattığı şeylerden sizin için gölgeler oluşturdu. Sizin için dağları sığınak yaptı. Sizi sıcaktan koruyacak elbiseler ve sizi savaşlarınızda koruyacak elbiseler hazırladı. Teslim olasınız diye nimetlerini size böyle tamamlıyor.'),
(1983, 16, 82, 'Yüz çevirirlerse, sana düşen, yalnızca (mesajı) açık bir biçimde iletmektir.'),
(1984, 16, 83, 'ALLAH\'ın nimetlerini çok iyi tanıdıkları halde onları inkar ederler. Onların çoğu kafirlerdir.'),
(1985, 16, 84, 'Her topluluktan bir tanık gönderdiğimiz gün, inkar edenlere ne izin verilir ne de özürleri kabul edilir.'),
(1986, 16, 85, 'Zulmedenler azabı gördükleri zaman artık onlar için hafifletilmez, ertelenmez.'),
(1987, 16, 86, 'Ortak koşanlar, ortak koştukları kimseleri gördükleri zaman, "Rabbimiz, senden başka çağırdığımız ortaklarımız işte bunlardır," derler. (Onlar da), "Siz yalancılarsınız (biz sizin bu suçunuza hiç bir zaman ortak olmadık)," diye karşılık verirler.'),
(1988, 16, 87, 'O gün tamamıyla ALLAH\'a teslim olmuşlardır ve uydurdukları şeyler de kendilerini terketmiştir.'),
(1989, 16, 88, 'İnkar edip ALLAH\'ın yolundan sapanlar... Yaptıkları bozgunculuktan ötürü azap üstüne azap arttırırız.'),
(1990, 16, 89, 'Her topluluk içinden, kendilerine karşı bir tanık gönderdiğimiz, şunlara karşı da seni tanık olarak getirdiğimiz gün... Biz sana bu kitabı, her şeyi açıklayan, bir yol gösterici, bir rahmet ve müslümanlara bir müjde olarak indirdik.'),
(1991, 16, 90, 'ALLAH adaleti, iyilik yapmayı ve akrabaya yardım etmeyi emreder. Kötülükten, fenalıktan ve azgınlıktan ise sizi meneder. Öğüt almanız için sizi böyle aydınlatır.'),
(1992, 16, 91, 'Anlaşma yaptığınızda ALLAH\'ın ahdini yerine getirin. Pekiştirdiğiniz yeminleri bozmayın. ALLAH\'ı kendinize kefil kılmıştınız. ALLAH yaptıklarınızı bilir.'),
(1993, 16, 92, 'İpliğini sağlamca eğirdikten sonra çözen kadın gibi olmayın. Bir topluluğun diğer bir topluluktan güçlü olmasından yararlanarak yeminlerinizi aranızda kötüye kullanmayın. ALLAH sizi böylece dener. Anlaşmazlığa düştüğünüz konuları diriliş günü size açıklayacak.'),
(1994, 16, 93, 'ALLAH dileseydi sizi bir tek toplum kılardı. Ancak, dilediğini saptırır, dilediğine de yol gösterir. Elbette yaptıklarınızdan sorulacaksınız.'),
(1995, 16, 94, 'Yeminlerinizi aranızda kötüye kullanmayın ki sağlam basmış ayağınız kayar. ALLAH\'ın yolundan saptığınız için perişanlığı tadar ve acı bir azabı hakkedersiniz.'),
(1996, 16, 95, 'ALLAH\'a verdiğiniz sözü ucuza satmayın. Sizin için ALLAH\'ın yanındakiler daha iyidir; bir bilseniz.'),
(1997, 16, 96, 'Sizin yanınızda bulunanlar biter; ancak ALLAH\'ın yanındakiler ise süreklidir. Güçlüklere karşı direnenlerin ödüllerini, yaptıkları iyiliklere uygun olarak elbette vereceğiz.'),
(1998, 16, 97, 'Erkek olsun, kadın olsun, her kim inançlı olarak iyi bir iş yaparsa ona bu dünyada güzel bir hayat bağışlarız ve yaptıkları iyi işlere karşılık ödüllerini de tam veririz.'),
(1999, 16, 98, 'Kuran okuduğunda kovulmuş şeytandan ALLAH\'a sığın.'),
(2000, 16, 99, 'İnanıp Rab\'lerine güvenenlere onun bir gücü yoktur.'),
(2001, 16, 100, 'Onun gücü, kendisini dost edinenlere ve onu Rab\'lerine ortak koşanlaradır.'),
(2002, 16, 101, 'Biz bir delilin yerine bir başka delili getirdiğimiz zaman ki ALLAH neyi indirdiğini iyi bilir "Sen, ancak bir iftiracısın!," derler. Gerçekten çokları bilmiyor.'),
(2003, 16, 102, 'De ki, "İnananları sağlamlaştırsın, müslümanlara bir yol gösterici ve müjde olsun diye Kutsal Ruh onu Rabbinden gerçekle indirdi.'),
(2004, 16, 103, '"Ona bir insan öğretiyor" biçimindeki sözlerini elbette biliyoruz. Amaçladıkları kişinin dili yabancıdır, bu ise apaçık Arapça bir dildir.'),
(2005, 16, 104, 'ALLAH\'ın ayetlerine inanmıyanları ALLAH doğru yola iletmez; onlar için acı bir azap vardır.'),
(2006, 16, 105, 'Yalan uydurup iftira edenler ALLAH\'ın ayetlerine inanmıyanlardır. Onlar gerçek yalancılardır.'),
(2007, 16, 106, 'Kalbi imanla dolu olduğu halde (inkara) zorlanan dışında, imandan sonra inkara göğsünü açıp ALLAH\'ı inkar edenler ALLAH\'tan bir gazabı hakketmişlerdir ve onlar için büyük bir azap vardır.'),
(2008, 16, 107, 'Çünkü onlar dünya hayatını ahirete tercih ettiler. İnkarcı topluluğu ALLAH doğru yola iletmez.'),
(2009, 16, 108, 'İşte onlar, ALLAH\'ın kalplerini, işitme ve görüşlerini mühürlediği kişilerdir. Onlar gafillerdir.'),
(2010, 16, 109, 'Hiç kuşku yok ki onlar ahirette kaybedeceklerdir.'),
(2011, 16, 110, 'Sonra Rabbin, baskıya uğradıktan sonra göç eden, cihad eden ve direnenler için, evet senin Rabbin, kuşkusuz onlar için Bağışlayandır, Rahimdir.'),
(2012, 16, 111, 'O gün herkes gelir kendini kurtarmaya çalışır ve herkese yaptığının karşılığı tam ödenir ve onlara asla zulmedilmez.'),
(2013, 16, 112, 'ALLAH güven içinde başarılı bir topluluğu örnek olarak verir: O topluluğun rızkı kendilerine her taraftan bol miktarda ulaşırdı. Ancak daha sonra, ALLAH\'ın nimetlerine karşı nankör davranınca ALLAH onlara açlık ve korku belasını tattırdı.'),
(2014, 16, 113, 'Onlara kendilerinden bir elçi geldi, onu yalanladılar. Sonunda, zulmederlerken onları azap yakaladı.'),
(2015, 16, 114, 'ALLAH\'ın size verdiği helal ve güzel rızıklardan yeyin ve ALLAH\'ın nimetlerine şükredin; sadece O\'na kulluk ediyorsanız.'),
(2016, 16, 115, 'Size yalnızca leş, kan, domuz eti ve ALLAH\'tan başkası için adananları haram kılmıştır. Kim (bunları yemek) zorunda kalırsa, istekli olmamak ve sınırı aşmamak koşuluyla ALLAH Bağışlayandır, Rahimdir.'),
(2017, 16, 116, 'Dillerinizin yalan yere nitelendirmesinden ötürü "Bu helaldir, bu haramdır," demeyin. Böylece ALLAH\'a yalan yakıştırmış olursunuz. ALLAH\'a yalan yakıştıranlar başarıya ulaşamazlar.'),
(2018, 16, 117, 'Kısa süren bu zevkten sonra onlar için acı bir azap vardır.'),
(2019, 16, 118, 'Yahudilere, daha önce sana anlattığımız şeyleri haram kıldık. Biz onlara zulmetmedik; fakat onlar kendi kendilerine zulmediyorlardı'),
(2020, 16, 119, 'Bunlara rağmen Rabbin, bilgisizlik yüzünden kötülük işledikten sonra ardından tevbe edip düzelenlere, evet senin Rabbin ondan sonra Bağışlayandır, Rahimdir.'),
(2021, 16, 120, 'İbrahim, ALLAH\'a boyun eğen, monoteist bir öncü idi. Hiç bir vakit ortak koşmadı.'),
(2022, 16, 121, 'O\'nun nimetlerine şükredici idi. Onu seçti ve onu doğru yola iletti.'),
(2023, 16, 122, 'Ve ona bu dünyada mutluluk verdik, ahirette ise erdemlilerle birlikte olacaktır.'),
(2024, 16, 123, 'Nitekim, İbrahim\'in dinini bir monoteist olarak izlemen için sana vahyettik; o asla putperestlerden olmadı.'),
(2025, 16, 124, 'Cumartesi (tatili), sadece onda ayrılığa düşenlere farz kılınmıştı. Rabbin, ayrılığa düştükleri konuda diriliş günü aralarında hüküm verecektir.'),
(2026, 16, 125, 'Rabbinin yoluna bilgelikle ve güzel bir aydınlatma ile çağır. Onlarla en güzel biçimde tartış. Rabbin, yolundan sapanları ve doğru yolda olanları en iyi bilendir.'),
(2027, 16, 126, 'Karşılık verecekseniz, size verilen karşılığın bir benzeriyle karşılık verin. Sabrederseniz, elbette bu sabredenler için daha hayırlıdır.'),
(2028, 16, 127, 'Sabret, sabrın ancak ALLAH\'ın yardımıyladır. Onlar için üzülme ve onların tuzaklarından da endişelenme.'),
(2029, 16, 128, 'ALLAH erdemli davrananlar ve iyilikte bulunanlar ile beraberdir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'Bazı ayetlerimizi kendisine göstermek için kulunu geceleyin (Mekke\'deki) Kutsal Mescitten, çevresini kutlu kıldığı en uzak mescide (secde yerine) alıp götüren çok Yücedir. O kuşkusuz İşitendir, Görendir'),
(2031, 17, 2, 'Aynı şekilde, Musa\'ya kitabı vermiştik. İsrail oğullarını şu gerçeğe iletmek için: "Benden başka bir sahip edinmeyin."'),
(2032, 17, 3, 'Onlar, Nuh ile birlikte taşıttığımız kimselerin soyudur; o şükreden bir kuldu'),
(2033, 17, 4, 'Kitapta, İsrail oğullarına: "Yeryüzünde iki kere bozgunculuk çıkaracaksınız ve alabildiğine kibirleneceksiniz," diye bildirdik,'),
(2034, 17, 5, '"Birincisinin zamanı gelince, büyük güce sahip kullarımızı üstünüze göndeririz. Evlerinize kadar girerek araştırırlar. Gerçekleşmesi gereken bir sözdü bu."'),
(2035, 17, 6, '"Sonra onları yenme olanağını size vereceğiz, sizi mal ve soy ile destekleyerek savaşçılarınızı çoğaltacağız."'),
(2036, 17, 7, '"İyi davranırsanız, kendiniz için iyi davranmış olursunuz. Kötü davranırsanız yine kendiniz içindir. Sonuncusunun zamanı gelince, sizi kedere boğacaklar ve ilk defa girdikleri gibi mescide girecekler. Ele geçirdiklerini yerle bir edecekler."'),
(2037, 17, 8, 'Rabbiniz size rahmet eder. Siz (bozgunculuk yapmaya) dönerseniz biz de (cezalandırmaya) döneriz. Cehennemi kafirler için kuşatıcı kıldık.'),
(2038, 17, 9, 'Bu Kuran en iyi yola ulaştırır ve erdemli davranan müminleri büyük bir ödülle müjdeler'),
(2039, 17, 10, 'Ahirete inanmıyanlara gelince, onlar için acı bir azap hazırlamış bulunuyoruz.'),
(2040, 17, 11, 'İnsan, iyi bir şey için dua ettiğini sanırken aslında kötü bir şey için dua eder. İnsan çok acelecidir'),
(2041, 17, 12, 'Geceyi ve gündüzü iki ayet (delil) kıldık. Rabbinizin nimetlerini arayasınız ve yılların hesabını bilesiniz diye gecenin ayetini sildik, gündüzün ayetini aydınlık kıldık. Biz her şeyi ayrıntısıyla açıklarız.'),
(2042, 17, 13, 'Her insanın kaderini kendi boynuna (kişisel seçimine) bağlamışızdır. Diriliş gününde, kendisi için bir kayıt çıkarıp yayımlarız'),
(2043, 17, 14, 'Kaydını oku. Bugün hesap görücü olarak sen kendine yetersin'),
(2044, 17, 15, 'Kim doğru yola gelirse kendisi için yola gelmiş bulunur. Kim saparsa kendi aleyhine sapar. Hiçkimse başkasının yükünü çekmez. Biz bir elçi göndermeden hiç kimseyi cezalandırmayız'),
(2045, 17, 16, 'Biz bir toplumu yok etmek istediğimiz zaman onun ileri gelen varlıklılarının orada kötülük yapmasına izin veririz. Böylece o topluma verilmiş söz gerçekleşir ve onu yerle bir ederiz.'),
(2046, 17, 17, 'Nuh\'tan sonra nice toplumları yok ettik. Kullarının günahlarını haber alıcı ve görücü olarak Rabbin yeter'),
(2047, 17, 18, 'Kim bu geçici dünyayı isterse, orada istediğimize dilediğimiz kadar veririz. Ancak daha sonra onu, kınanmış ve kovulmuş olarak cehenneme mahkum ederiz.'),
(2048, 17, 19, 'Kim ahireti seçer ve mümin olarak gereken çabayı gösterirse, işte onların çabası takdir edilir.'),
(2049, 17, 20, 'Hepsine, onlara da bunlara da, Rabbinin nimetlerinden ulaştırırız. Rabbinin nimetleri sınırlanmamıştır.'),
(2050, 17, 21, 'İnsanları birbirinden nasıl üstün kıldığımıza dikkat et. Ahiretin dereceleri ve üstünlükleri daha büyüktür'),
(2051, 17, 22, 'ALLAH ile birlikte başka tanrı edinme, yoksa kınanmış ve yalnız bırakılmış olarak oturup kalırsın'),
(2052, 17, 23, 'Rabbin, yalnız kendisine kulluk etmenizi ve anaya babaya karşı iyi davranmanızı emretti. Onlardan biri veya ikisi yanında yaşlanırsa onlara "Öf" bile deme ve onları azarlama. Onlarla güzel bir biçimde konuş'),
(2053, 17, 24, 'Onlara merhamet ederek alçak gönüllük kanadını ger ve de ki, "Rabbim, beni küçükken yetiştirdikleri gibi sen de onlara acı."'),
(2054, 17, 25, 'Rabbiniz içinizdekileri çok iyi bilir. Erdemli davranırsanız, elbette O, tevbe edenleri bağışlayandır'),
(2055, 17, 26, 'Akrabalara haklarını ver. İhtiyaç sahiplerine ve yolcuya da... Ancak saçıp savurma'),
(2056, 17, 27, 'Kuşkusuz, saçıp savuranlar şeytanların dostlarıdır ve şeytan Rabbine karşı nankördür'),
(2057, 17, 28, 'Rabbinden umduğun bir rahmeti elde etmek için onlardan yüz çevirmek zorunda kalırsan onlara yumuşak söz söyle'),
(2058, 17, 29, 'Elini boynuna bağlama ve tümüyle de açma, yoksa pişman olur, üzülürsün.'),
(2059, 17, 30, 'Rabbin, dilediğine rızkını bol verir, veya kısar. Kuşkusuz O, kullarından haberdardır, onları görendir'),
(2060, 17, 31, 'Fakirlik korkusuyla çocuklarınızı öldürmeyin. Sizi de onları da biz rızıklandırıyoruz. Onları öldürmek, büyük bir suçtur'),
(2061, 17, 32, 'Zinaya yaklaşmayın; çünkü o büyük bir günah ve kötü bir davranıştır'),
(2062, 17, 33, 'ALLAH\'ın kutsal kıldığı canı haksız yere öldürmeyin. Kim haksızlığa uğrayarak öldürülürse onun mirasçılarına yetki vermişizdir. İntikam duygusuyla öldürmede sınırı aşmasın; zira kendisine yardım edilmiştir'),
(2063, 17, 34, 'Öksüzlerin malına, erginlik çağına ulaşıncaya kadar dokunmayın; yararlarına olursa başka'),
(2064, 17, 35, 'Ölçtüğünüz zaman ölçüyü tam yapın ve doğru teraziyle tartın. Elbette bu daha iyidir ve sonucu daha güzeldir'),
(2065, 17, 36, 'Bilmediğin bir şeye inanıp ardına düşme, çünkü işitme, görme duyusu ve beyin, hepsi ondan sorumludur'),
(2066, 17, 37, 'Yeryüzünde kibirli kibirli dolaşma sen ne yeri delebilirsin ne de dağlar kadar boylu olabilirsin.'),
(2067, 17, 38, 'Tüm bunlar, Rabbin tarafından hoş görülmeyen kötü davranışlardır.'),
(2068, 17, 39, 'Bunlar, Rabbinin sana vahyettiği hikmetlerdendir. ALLAH ile birlikte başka tanrı edinme; aksi taktirde kınanmış ve kovulmuş olarak cehenneme atılırsın'),
(2069, 17, 40, 'Rabbiniz oğulları size seçti de kendisine meleklerden kızlar mı edindi? Çok büyük bir savda bulunuyorsunuz.'),
(2070, 17, 41, 'Biz, öğüt almaları için Kuran\'da açıkladık. Ne var ki bu, sadece onların nefretini arttırır'),
(2071, 17, 42, 'De ki: "İleri sürüdükleri gibi beraberinde başka tanrılar olsaydı, onlar da Egemenliğin Sahibine doğru yol ararlardı."'),
(2072, 17, 43, 'O, onların dediklerinden uzaktır ve çok yücedir.'),
(2073, 17, 44, 'Yedi gök, ve onların içindekiler O\'nu yücelterek anarlar. Hiç bir şey yoktur ki O\'nu överek ve yücelterek anmasın; ama siz onların anışlarını anlamazsınız. O Şefkatlidir, Bağışlayandır'),
(2074, 17, 45, 'Kuran okuduğun zaman, seninle ahirete inanmıyanlar arasına görülmez bir engel yerleştiririz'),
(2075, 17, 46, 'Ve onu anlamalarını engellemek için kalplerine kabuklar, kulaklarına da ağırlık koyarız. Rabbini yalnızca Kuran\'da andığın zaman nefretle geriye dönerler'),
(2076, 17, 47, 'Seni dinlerlerken nasıl işittiklerini ve kendi aralarında konuşurlarken zalimlerin, "Siz sadece büyülenmiş bir adamı izliyorsunuz," dediklerini iyi biliyoruz.'),
(2077, 17, 48, 'Dikkat et, seni nasıl da tanımlayarak sapıyorlar ve artık bir daha yol bulamazlar'),
(2078, 17, 49, 'Dediler ki: "Kemik ve ufak parçalar haline geldikten sonra mı yeni bir yaratılışla diriltileceğiz?!"'),
(2079, 17, 50, 'De ki: "İsterse taş veya demire dönüşün,"'),
(2080, 17, 51, '"Yahut sizce imkansız görünen bir biçime girin, farketmez." Buna karşılık, "Bizi kim geri döndürecek," diyecekler. De ki: "Sizi ilk önce kim yarattıysa O!" Sonra başlarını sallayıp, "Peki ne zaman," diyecekler. De ki: "Belki düşündüğünüzden daha yakın..."'),
(2081, 17, 52, 'Sizi çağıracağı gün, siz ona övgüyle karşılık verirsiniz ve çok kısa bir süre kalmış olduğunuzu anlarsınız.'),
(2082, 17, 53, 'Kullarıma söyle: En güzel biçimde konuşup tartışsınlar. Çünkü şeytan aralarına girer. Şeytan, insanın apaçık düşmanıdır'),
(2083, 17, 54, 'Rabbiniz sizi daha iyi bilir. Dilerse size acır, dilerse sizi cezalandırır. Seni onlara avukatlık yapasın diye göndermedik'),
(2084, 17, 55, 'Rabbin göklerdekileri ve yerdekileri en iyi bilendir. Peygamberlerden bir kısmını diğerlerine üstün kıldık. Örneğin, Davud\'a Zebur\'u verdik'),
(2085, 17, 56, 'De ki: "O\'nun dışında (bir güce sahip olduğunu) iddia ettiklerinizi çağırın bakalım, onlar sizden ne sıkıntıyı kaldırabilirler ne de önleyebilirler."'),
(2086, 17, 57, 'Onların çağırdıkları kişiler bile Rab\'lerine yaklaşmak için yol ararlar. O\'nun rahmetini umar, cezasından korkarlar. Rabbinin cezası sakınılacak bir şeydir'),
(2087, 17, 58, 'Hiç bir ülke yok ki diriliş gününden önce onu yok edecek veya şiddetli bir biçimde cezalandıracak olmayalım. Bu, kitapta yazılmış bulunuyor.'),
(2088, 17, 59, 'Bizi ayetler (mucizeler) göndermekten alıkoyan şey, öncekilerin onları yalanlamış olmasıdır. Örneğin, Semud\'a açık bir (mucize) olarak deveyi vermiştik. Fakat ona haksızlık ettiler. Biz mucizeleri yalnızca uyarı amacıyla göndeririz'),
(2089, 17, 60, 'Rabbinin insanları kuşatmış olduğunu sana bildirmiştik. Fakat sana gösterdiğimiz görüntü ile ve Kuran\'da lanetlenen ağaçla adece insanları denedik. Biz onları uyarırız, ancak bu, onların azgınlığına büyük bir katkıda bulunmaktan başka sonuç vermiyor.'),
(2090, 17, 61, 'Meleklere, "Adem\'e secde edin!," dediğimizde, İblis hariç hepsi secde ettiler. "Balçıktan yarattığına mı secde edecekmişim," dedi'),
(2091, 17, 62, '"Benden üstün kıldığın şu kişiyi görüyor musun? Beni diriliş gününe kadar geciktirirsen, onun soyunu, pek azı hariç sahiplenip perişan edeceğim.," dedi'),
(2092, 17, 63, 'Dedi ki: "Onlardan seni izleyenler ile birlikte git. Tam size göre bir ceza olan cehenneme kadar yolunuz var."'),
(2093, 17, 64, '"Onlardan gücünün yettiğini sesinle yerinden oynat, onlara karşı tüm gücünü ve adamlarını seferber et, paralarının ve çocuklarının bir bölümüne ortak ol ve onlara umut ver. Şeytan, onlara ancak sahte umutlar verir."'),
(2094, 17, 65, '"Kullarıma gelince, senin onlar üzerine hiç bir gücün yoktur." Koruyucu olarak Rabbin yeter.'),
(2095, 17, 66, 'Rabbiniz, nimetlerini aramanız için gemileri okyanuslarda yüzdürendir. O size karşı Rahim\'dir.'),
(2096, 17, 67, 'Okyanusta size bir sıkıntı dokunursa O\'ndan başka çağırmakta olduklarınız kaybolur. Fakat sizi kurtarıp karaya çıkarınca, dönersiniz. İnsan nankördür'),
(2097, 17, 68, 'Kıyıyı üstünüze çevirip sizi yutmasından, yahut üzerinize şiddetli bir kasırga yollamasından emin misiniz? Sonra hiç bir koruyucu da bulamazsınız'),
(2098, 17, 69, 'Sizi tekrar denize göndermeyeceğinden ve üstünüze kırıp geçiren bir fırtına salarak inkarınızdan dolayı sizi boğmayacağından emin misiniz? Sonra sizin için peşinize düşecek birini de bulamazsınız'),
(2099, 17, 70, 'Adem oğullarına onur verdik. Onları karada ve denizde taşıdık. Onları güzel nimetlerle besledik. Yarattıklarımızın bir çoğundan daha üstün kıldık'),
(2100, 17, 71, 'Her bir halkı önderleriyle birlikte çağırdığımız gün, kitabı sağından verilenler kitaplarını okurlar ve en ufak bir haksızlığa uğratılmazlar.'),
(2101, 17, 72, 'Bu dünyada (Kitaba) kör olanlara gelince, onlar ahirette de kördür ve yol bakımından daha da sapıktır'),
(2102, 17, 73, 'Başka bir şeyi uydurup bize yakıştırman için nerdeyse seni sana vahyettiğimizden ayırıp saptıracaklardı. İşte o zaman seni dost edineceklerdi.'),
(2103, 17, 74, 'Seni sağlamlaştırmasaydık, onlara neredeyse bir parça meyledecektin'),
(2104, 17, 75, 'O zaman da hayatın ve ölümün azabını katlayarak sana tattırırdık. Sonra bize karşı bir yardımcı da bulamazdın'),
(2105, 17, 76, 'Seni ülkeden çıkarmak için neredeyse seni zorla sürecekler. Bu taktirde senden sonra onlar da fazla kalmayacaklar'),
(2106, 17, 77, 'Senden önce gönderdiğimiz tüm elçiler için öngördüğümüz sistem (sünnet) budur. Sistemimizde herhangi bir değişiklik göremezsin.'),
(2107, 17, 78, 'Güneşin kaymasından gecenin kararmasına kadar namazı gözet. Sabah Kuran\'ını da gözet. Sabahleyin Kuran (okuması) tanık olunur'),
(2108, 17, 79, 'Fazladan bir kredi olarak geceleyin meditasyonda bulun ki Rabbin seni onurlu bir makama yükseltsin'),
(2109, 17, 80, 'Ve de ki: "Rabbim, beni doğru bir girişle kabul et ve beni doğru bir çıkışla çıkar. Katından beni destekleyecek bir güç ver."'),
(2110, 17, 81, 'Ve şunu bildir ki: "Gerçek gelmiş, yanlış ise ortadan kalkmıştır. Zaten yanlış, yok olmağa mahkumdur."'),
(2111, 17, 82, 'Kuran\'ı, inananlar için bir şifa ve rahmet olarak indirdik. Zalimlerin ise ancak zararını arttırır'),
(2112, 17, 83, 'İnsana nimet verdiğimiz zaman, yüz çevirip yan çizer. Kendisine kötülük dokununca da umutsuzluğa düşer'),
(2113, 17, 84, 'De ki: "Her kes inancına göre davranır. Kimin daha doğru yolda olduğunu ise Rabbim daha iyi bilir."'),
(2114, 17, 85, 'Sana ruhtan (vahiyden) sorarlar. De ki: "Vahiy Rabbimden gelir. Size verilen bilgi ise pek azdır."'),
(2115, 17, 86, 'Dilesek sana vahyettiğimizi geri alırız ve bize karşı her hangi bir koruyucu da bulamazsın'),
(2116, 17, 87, 'Ancak Rabbinin rahmeti var... O\'nun sana olan nimeti büyüktür.'),
(2117, 17, 88, 'De ki: "Tüm insanlar ve cinler bu Kuran\'ın bir benzerini oluşturmak amacıyla toplansalar ve bu konuda birbirlerine destek olsalar bile onun bir benzerini oluşturamazlar."'),
(2118, 17, 89, 'Biz bu Kuran\'da her türlü örneği verdik, ne var ki halkın çoğunluğu inkarda direniyor.'),
(2119, 17, 90, 'Dediler ki: "Yerden bize bir kaynak fışkırtmadıkça sana inanmayız."'),
(2120, 17, 91, '"Veya hurma ve üzüm bahçelerin olup aralarında ırmaklar fışkırtmalısın."'),
(2121, 17, 92, '"Veya ileri sürdüğün gibi gökten üzerimize parçalar düşürmeli, yahut ALLAH\'ı ve melekleri karşımıza getirmelisin."'),
(2122, 17, 93, '"Ya da altın bir evin olmalı, veya göğe yükselmelisin. Yükselsen bile okuyacağımız bir kitabı üzerimize indirmedikçe ona inanmayız." De ki: "Rabbim yücedir. Ben elçi olan bir insandan başka bir şey miyim ki."'),
(2123, 17, 94, 'Kendilerine hidayet geldiğinde, halk: "ALLAH bir insanı mı elçi olarak gönderdi," diyerek inanmadı'),
(2124, 17, 95, 'De ki: "Yeryüzünde yerleşip dolaşan melekler olsaydı onlara gökten bir meleği elçi gönderirdik."'),
(2125, 17, 96, 'De ki: "Benimle sizin aranızda ALLAH tanıktır. O, kullarından haber alır, görür."'),
(2126, 17, 97, 'ALLAH kime yol gösterirse o kişi doğruyu bulmuştur. Kimi de saptırırsa onlar için O\'ndan başka bir koruyucu da bulamazsın. Diriliş günü de onları kör, dilsiz ve sağır olarak yaka paça toplarız. Cehennemdir yerleri. Soğudukça alevlerini arttırırız'),
(2127, 17, 98, 'Ayetlerimizi yalanladıkları, "Kemik ve ufalanmış toprak olduktan sonra biz mi yeni bir yaratılışla diriltileceğiz," dedikleri için cezaları budur'),
(2128, 17, 99, 'Gökleri ve yeri yaratan ALLAH\'ın onların bir benzerini tekrar yaratmaya gücü yeteceğini düşün müyorlar mı? Nitekim onlar için belli ve kesin bir süre koymuştur. Zalimler hala inkar ediyorlar.'),
(2129, 17, 100, 'De ki: "Rabbimin rahmet hazineleri sizin elinizde olsaydı, harcamakla tükenir korkusuyla onları tutacaktınız. İnsan cimridir."'),
(2130, 17, 101, 'Musa\'ya apaçık dokuz mucize verdik. Dilersen İsrail oğullarına sor. Onlara gittiğinde, Firavun ona, "Musa, senin büyülenmiş olduğunu sanıyorum," demişti'),
(2131, 17, 102, '"Göklerin ve yerin Rabbi\'nden başkasının bu delilleri indirmediğini iyi biliyorsun. Firavun, seni mahvolmuş biri olarak görüyorum!"'),
(2132, 17, 103, 'Onları ülkeden çıkarmak isteyince de onu ve beraberindekileri topluca suda boğduk'),
(2133, 17, 104, 'Ondan sonra İsrail oğullarına, "Bu ülkede yerleşin. Verilen son söz geldiğinde sizi bir araya toplayacağız," dedik.'),
(2134, 17, 105, 'Gerçekten onu biz indirdik ve o gerçek ile indi. Seni de ancak bir müjdeleyici ve uyarıcı olarak gönderdik'),
(2135, 17, 106, 'Uzun bir zaman dilimi içerisinde halka okuman için ayırdığımız bir Kuran\'dır. Onu topluca indirmiştik'),
(2136, 17, 107, 'De ki: "Ona ister inanın, ister inanmayın!" Daha önce kendilerine bilgi verilmiş olanlara okunduğu zaman secde ederek yüz üstü kapanırlar'),
(2137, 17, 108, '"Rabbimiz yücedir. Rabbimizin sözü gerçekleşmiştir," derler'),
(2138, 17, 109, 'Ağlayarak yüz üstü kapanırlar; çünkü o (ayetler) onların saygısını arttırır.'),
(2139, 17, 110, 'De ki: "İster ALLAH diye çağırın, ister Rahman diye çağırın. Hangisiyle çağırırsanız, tüm güzel isimler O\'nundur. " Namazında ne ilan et, ne de gizle; ikisinin arasında bir yol tut.'),
(2140, 17, 111, 'Ve de ki: "Övgü, ALLAH\'adır. O çocuk edinmemiştir, yönetimde ortağı ve zayıflıktan ötürü de bir yardımcısı yoktur." O\'nu alabildiğine Yücelt.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'ALLAH\'a övgü olsun, kuluna kitabı indirmiş ve onda hiç bir eğriliğe yer vermemiştir.'),
(2142, 18, 2, 'Kusursuz bir (kitap)... O\'ndan gelecek şiddetli bir cezaya karşı uyarsın ve erdemli davranan müminlere kendileri için güzel bir ödülü müjdelesin.'),
(2143, 18, 3, 'Ki orada sürekli kalacaklardır.'),
(2144, 18, 4, 'Ve "ALLAH çocuk edindi," diyenleri de uyarsın.'),
(2145, 18, 5, 'Ne onların, ne de atalarının bu konuda bir bilgileri yoktur. Ağızlarından ne büyük bir söz çıkıyor! Yalandan başka şey söylemiyorlar.'),
(2146, 18, 6, 'Bu söze inanmazlarsa onların ardından kendini sorumlu tutarak suçlayacaksın, üzüleceksin (öyle mi)?'),
(2147, 18, 7, 'Biz, kimlerin erdemli davranacağını sınamak için, üzerindeki maddelerle yeryüzünü süsledik.'),
(2148, 18, 8, 'Ve elbette biz onun üzerinde bulunanları çorak bir toprak haline dönüştüreceğiz.'),
(2149, 18, 9, 'Mağaradakilerin ve onlarla ilgili rakamların ilginç kanıtlarımızdan başka bir şey olduğunu mu sandın?'),
(2150, 18, 10, 'Gençler mağaraya sığındıklarında, "Rabbimiz bize merhametini yağdır ve bu durumdan bize bir kurtuluş yolu göster," demişlerdi.'),
(2151, 18, 11, 'Bunun üzerine sayılı yıllar süresince mağarada kulaklarını kapadık.'),
(2152, 18, 12, 'Sonra onları dirilttik ki onların orada kalış sürelerini hangi grubun daha iyi hesaplayacağını bilelim.'),
(2153, 18, 13, 'Onların haberini sana gerçek olarak anlatıyoruz. Onlar Rab\'lerine inanmış gençlerdi. Onların hidayetini arttırmıştık.'),
(2154, 18, 14, 'Kalktıklarında kalplerini sağlamlaştırmıştık. "Rabbimiz, göklerin ve yerin Rabbidir. O\'ndan başka tanrı edinmeyeceğiz. Yoksa, yanlış söz söylemiş oluruz.'),
(2155, 18, 15, '"Şu halkımız O\'ndan başkasını tanrılar edindi. Onların tanrı olduğunu açık bir delille kanıtlamaları gerekmez miydi? ALLAH\'a karşı yalan uydurandan daha zalim kim olabilir?"'),
(2156, 18, 16, '"Madem ki siz onlardan ve ALLAH\'ın dışında taptıklarından ayrıldınız, öyleyse mağaraya sığının. Olur ki Rabbiniz size rahmetini yağdırır, işinizde size olumlu sonuç hazırlar."'),
(2157, 18, 17, 'Tan ağardığında, onlar mağara boşluğunda iken, mağaralarının üzerinden Güneşin sağa doğru hareket ettiğini, battığı zaman da onları yalayıp sola doğru kaydığını görürdün. Bu, ALLAH\'ın işaretlerindendir. ALLAH kime yol gösterirse o kişi doğruyu bulmuştur; kimi de saptırırsa onun için aydınlatıcı bir dost bulamazsın.'),
(2158, 18, 18, 'Uykuda olmalarına rağmen onları uyanık sanırsın. Onları sağa ve sola doğru çeviririz. Köpekleri de kollarını eşikte uzatmıştır. Onlara baksaydın onlardan dönüp kaçardın ve onlardan dolayı korkuyla dolardın.'),
(2159, 18, 19, 'Böylece onları uyandırdık ki birbirlerine sorsunlar. Onlardan biri, "Ne kadar kaldınız," diye sordu. "Bir gün, yahut günün bir parçası kadar kaldık," dediler. "Ne kadar kaldığınızı Rabbiniz daha iyi bilir. Birinizi şu para ile şehre gönderelim de en temiz ve leziz yiyecekleri seçip size bir azık getirsin. Dikkatli davranarak kimsenin dikkatini üstüne çekmesin," diye eklediler.'),
(2160, 18, 20, '"Onlar sizi farkederlerse sizi taşlarlar veya sizi zorla kendi dinlerine döndürürler ki bu durumda asla başarıya ulaşamazsınız."'),
(2161, 18, 21, 'Böylece onları (halka) buldurduk ki ALLAH\'ın sözünün gerçek olduğunu öğrensinler ve Saat (dünyanın sonu) konusunda da kuşkuları kalmasın. Halk onların durumunu aralarında tartışırken bir kısmı, "Onların üzerine bir bina yapın," dedi. Rab\'leri onları daha iyi bilir. Onların durumuna karar verme yetkisini ellerine geçirenler, "Onların üstüne bir mescid yapacağız," dediler.'),
(2162, 18, 22, 'Tahminde bulunanların bazıları, "Onlar üçtür, dördüncüleri köpekleridir," derken diğerleri de, "Beştir, altıncıları köpekleridir," diyecekler. Başkaları ise, "Yedidir, sekizincileri köpekleridir," diyecekler. De ki, "Onların sayısını en iyi bilen Rabbimdir." Onları bilen azdır. Onlarla yüzeysel olması hariç tartışmaya girme ve onlardan hiç kimseye de bu konuyu danışma.'),
(2163, 18, 23, 'Hiç bir şey için, "Bunu yarın yapacağım," deme.'),
(2164, 18, 24, 'Ancak, "ALLAH dilerse (yapacağım)," diyerek Rabbini an. Unutursan, o zaman da, "Belki Rabbim bir dahaki sefere beni daha iyiye ulaştırır," de.'),
(2165, 18, 25, 'Mağaralarında üç yüz yıl kalıp dokuz arttırdılar.'),
(2166, 18, 26, '"Onların orada ne kadar kaldıklarını ALLAH daha iyi bilir," de. Göklerin ve yerin tüm gizemleri O\'nundur. O ne güzel Görendir! O ne güzel İşitendir! Onların O\'ndan başka bir yardımcısı yoktur. O, hükmüne kimseyi ortak etmez.'),
(2167, 18, 27, 'Rabbinin kitabından sana vahyedileni oku. Kelimelerini hiç bir şey değiştirip kaldıramaz ve O\'ndan başka bir sığınak da bulamazsın.'),
(2168, 18, 28, 'Rab\'lerinin rızasını dileyerek sabah akşam kulluk edenlerle birlikte olmaya çalış. Dünya hayatının çekici materyallerini arzu edip de gözlerini onlardan ayırma. Kalbini mesajımızdan gafil kıldığımız ve hevesine uyarak işlerini karıştıran kimseleri izleme.'),
(2169, 18, 29, 'De ki, "Bu gerçek senin Rabbindendir." Dileyen inansın, dileyen inkar etsin. Biz zalimler için onları çepeçevre saracak bir ateş hazırladık. Onlar her ne zaman feryad ederek yardım isteseler, derişik asit gibi yüzleri haşlayan bir su sunulur. Ne kötü bir içecek, ne kötü bir son!'),
(2170, 18, 30, 'İnanıp erdemli davrananlara gelince, elbette biz iyi işlerde bulunanların ödülünü yitirmeyiz.'),
(2171, 18, 31, 'Onlar için, içlerinden ırmaklar akan Adn bahçeleri (cennetleri) vardır. Orada altından bileziklerle süslenirler, ipek ve kadifeden dokunmuş yeşil elbiseler giyerler. Orada koltuklar üzerine yaslanırlar. Ne güzel bir ödül ve ne güzel bir durak…'),
(2172, 18, 32, 'Onlara iki adamın örneğini ver: Birisine, etrafını hurma ağaçlarıyla çevirip aralarına başka ekinler bitirdiğimiz iki üzüm bahçesi vermiştik.'),
(2173, 18, 33, 'Aralarına bir ırmak yerleştirdiğimiz için, her iki bağ da yemişini hiç esirgemeden cömertçe vermişti.'),
(2174, 18, 34, 'Artık bir ürüne sahipti! Nitekim, sohbet ettiği arkadaşına, "Ben senden daha zenginim ve daha fazla adama sahibim," dedi.'),
(2175, 18, 35, 'Böylece nefsine zulmederek bağına girdi ve, "Bunun yok olacağını hiç sanmıyorum," dedi.'),
(2176, 18, 36, '"Dünyanın sonunun da geleceğini sanmıyorum. Rabbime döndürülürsem de orada daha iyisini bulurum.'),
(2177, 18, 37, 'Kendisiyle konuşan arkadaşı ona dedi ki: "Seni topraktan, ufak bir damladan yaratıp seni bir adam biçimine sokan Rabbini mi inkar ediyorsun?'),
(2178, 18, 38, '"Bana gelince... O ALLAH benim Rabbimdir, ben Rabbime hiç bir şeyi ortak koşmam.'),
(2179, 18, 39, '"Bağına girerken, \'Bu ALLAH\'ın bir bağışıdır (Maşallah), kuvvet ancak ALLAH iledir (La kuvvete illa billah)\' demen gerekmez miydi? Senden daha az para ve çocuğa sahip olduğumu görüyor olabilirsin ama'),
(2180, 18, 40, '"Rabbim, belki senin bahçenden daha iyisini bana verir ve olur ki bahçene gökten bir felaket gönderir de kupkuru bir toprağa dönüşür.'),
(2181, 18, 41, '"Yahut suyu dibe çöker de bir daha su bulamazsın."'),
(2182, 18, 42, 'Derken, ürünü yok edildi. Çardakları üzerine yığılmış olan bağına verdiği emekten ötürü matem tutmaya ve "Keşke Rabbime ortak koşmasaydım," demeye başladı.'),
(2183, 18, 43, 'ALLAH\'tan başka kendisine yardım edecek bir destek bulamadı. Kendini de kurtaramadı.'),
(2184, 18, 44, 'O an, velayet (güç ve egemenlik) tümüyle gerçek olan ALLAH\'ındır. O, en iyi ödülü ve en iyi sonucu verendir.'),
(2185, 18, 45, 'Onlara dünya hayatının örneği olarak, gökten indirdiğimiz suyla beslenen ve sonra rüzgarın savurduğu çer çöpe dönen yeryüzünün bitkisini ver. ALLAH her şeye gücü yetendir.'),
(2186, 18, 46, 'Para ve çocuklar bu dünya hayatının çekici materyalleridir. Ama, sürekli kalacak erdemli işler, Rabbinin katında bir ödül ve umut olarak çok daha iyidir.'),
(2187, 18, 47, 'Dağları silip götürdüğümüz ve yeri çıplak bıraktığımız gün onları toplarız; bir tanesini bile bırakmadan.'),
(2188, 18, 48, 'Ve sırayla Rabbine sunulurlar. Bize, ilk başta sizi nasıl yarattıysak öyle gelirsiniz. Oysa, böyle bir buluşmayı gerçekleştirmeyeceğimizi ileri sürüyordunuz.'),
(2189, 18, 49, 'Kitap ortaya konmuştur. Suçluların onun içeriğinden korkuya kapıldığını ve "Bize yazıklar olsun, meğer bu kitap küçük büyük hiç bir şeyi bırakmayıp saymış," dediklerini görürsün. Yaptıkları her şeyi önlerinde hazır bulmuşlardır. Rabbin hiç kimseye haksızlık etmez.'),
(2190, 18, 50, 'Meleklere, "Adem\'e secde edin!," dediğimiz zaman hepsi secde ettiler; yalnız İblis hariç. Rabb\'inin emrine karşı geldiği için cinlerden oldu. Beni bırakıp onu ve soyunu mu dostlar ediniyorsunuz? Oysa onlar, sizin düşmanlarınızdır. Zalimler için bu ne kötü bir değişmedir.'),
(2191, 18, 51, 'Onları ne göklerin ve yerin yaratılışına, ne de kendilerinin yaratılışına tanık yapmadım. Saptıranları hiç bir zaman egemenliğimde görevlendirmem.'),
(2192, 18, 52, '"Ortaklarım olduğunu ileri sürdüğünüz kişileri çağırın," diye emrettiği gün, onları çağırırlar da onlar kendilerine karşılık vermezler. Onların arasına bir öfke ve nefret uçurumu koymuşuzdur.'),
(2193, 18, 53, 'Suçlular ateşi gördüler ve içine düşeceklerini anladılar; ondan kaçacak bir yer de bulamadılar.'),
(2194, 18, 54, 'Biz bu Kuran\'da halk için her türlü örneği verdik. Fakat insan tartışmaya çok düşkündür.'),
(2195, 18, 55, 'Kendilerine yol gösterici geldiğinde, halkı inanmaktan ve Rab\'lerinden bağışlanma dilemekten alıkoyan şey, öncekilerin sünnetinin (benzer uygulamanın, örneğin, geçmiş toplumlara verilen mucizelerin bir benzerinin) kendilerine de gelmesini veya azabın açıkça karşılarına gelmesini dilemeleridir.'),
(2196, 18, 56, 'Biz, elçileri ancak müjdeleyiciler ve uyarıcılar olarak gönderdik. Kafirler, gerçeği sahte ve yanlış ile ortadan kaldırmak için gayret ediyor. Ayetlerimi (işaret ve mucizeleri) ve uyarıldıkları şeyleri alaya aldılar.'),
(2197, 18, 57, 'Rabbinin ayet ve mucizeleri kendisine hatırlatıldığı halde, yaptıklarını unutarak ondan yüz çevirenden daha zalim kim olabilir? Kalplerine, onu (Kuran\'ı) anlamalarına engel olacak bir örtü, kulaklarına da bir ağırlık koymuşuzdur. Onları hidayete ne kadar çağırırsan çağır, onlar asla doğruyu bulamaz.'),
(2198, 18, 58, 'Rabbin Bağışlayandır, Merhamet Sahibidir. Yaptıklarıyla hemen onları hesaba çekse onlar için azabı çabuklaştırmış olurdu. Ancak, onlara belirli bir süre verir de onun ötesine kaçamazlar.'),
(2199, 18, 59, 'İşte, haksızlık ettikleri için yok ettiğimiz toplumlar... Hepsinin helakı için belli bir süre belirlemiştik.'),
(2200, 18, 60, 'Musa, genç yoldaşına, "Yıllarca yürümeyi gerektirse de iki denizin birleştiği yere varmadan dinlenmeyeceğim," demişti.'),
(2201, 18, 61, 'İki denizin birleştiği yere vardıklarında, balıklarını unuttular. Sinsice kayıp denizin yolunu tuttu.'),
(2202, 18, 62, 'Orayı geçtiklerinde, genç yoldaşına, "Yemeğimizi getir. Bu yolculuğumuz bizi gerçekten yordu," dedi.'),
(2203, 18, 63, 'Dedi ki: "Bak gördün mü, o kayalığa vardığımızda balığı unuttum. Onu bana şeytan unutturdu ve böylece denizde yolunu tutup gitti. Ne kadar da ilginç!"'),
(2204, 18, 64, '(Musa:) "İşte aradığımız yer orası idi," dedi ve böylece izleri üzerinde geri döndüler.'),
(2205, 18, 65, 'Katımızdan kendisine rahmet verdiğimiz ve bilgimizden öğrettiğimiz kullarımızdan bir kulu buldular.'),
(2206, 18, 66, 'Musa ona, "Sana öğretilenden, aydınlatıcı prensipleri bana öğretmen için seni izleyebilir miyim," deyince,'),
(2207, 18, 67, '"Sen benimle birlikte olmaya dayanamazsın," dedi,'),
(2208, 18, 68, '"Bilmediğin bir şeye nasıl dayanabilirsin?"'),
(2209, 18, 69, 'Dedi: "ALLAH dilerse beni sabırlı bulacaksın; sana hiç bir işte karşı gelmeyeceğim."'),
(2210, 18, 70, 'Dedi: "Bana uyacaksan, ben sana anlatmadıkça bana hiç bir şey hakkında soru sorma."'),
(2211, 18, 71, 'Böylece yürüdüler. Nihayet gemiye bindiklerinde, onda delik açtı. "Halkını boğmak için mi gemide delik açtın? Gerçekten müthiş bir iş yaptın," dedi.'),
(2212, 18, 72, '"Bana dayanamıyacağını sana söylememiş miydim," diye karşılık verdi.'),
(2213, 18, 73, '"Unuttuğum şeyden dolayı beni kınama; bu işimde bana güçlük çıkarma," dedi.'),
(2214, 18, 74, 'Böylece yürüdüler. Nihayet bir delikanlıya rastladıklarında onu hemen öldürdü. "Kimseye kıymamış suçsuz bir kimseyi niçin öldürdün? Sen, çok kötü bir iş yaptın," dedi'),
(2215, 18, 75, '"Bana dayanamıyacağını sana söylememiş miydim," diye tekrarladı.'),
(2216, 18, 76, '"Sana daha başka bir şey sorarsam o zaman artık benimle arkadaş olma. Benden yeterli özür dinledin," dedi.'),
(2217, 18, 77, 'Böylece yürüdüler. Nihayet bir köy halkına rastladılar ve halkından yiyecek istediler. Fakat onları misafir kabul etmeyi reddettiler. Derken orada yıkılmak isteyen bir duvar buldular, hemen onu doğrultuverdi. "Dileseydin, o işten dolayı bir ücret alabilirdin," dedi.'),
(2218, 18, 78, 'Dedi ki: "İşte bu, benim seninle olan beraberliğimin sonudur. Dayanamadığın şeylerin açıklamasını ise sana bildireceğim."'),
(2219, 18, 79, '"Gemi, denizde çalışan yoksul (balıkçı)lara aitti. Gemiyi kusurlu yapmak istedim; zira peşlerinde, tüm gemileri zorla ele geçiren bir kral vardı."'),
(2220, 18, 80, '"Çocuğa gelince, ana babası iki inanan kişi idi. Taşkınlık ve nankörlük ile o ikisine yük olmasından endişelendik."'),
(2221, 18, 81, '"İstedik ki Rab\'leri onun yerine kendilerine ondan daha temiz ve merhametli birini versin."'),
(2222, 18, 82, '"Duvar ise kentteki iki öksüze aitti. Duvarın altında onlara ait bir hazine vardı. Babaları da erdemli birisiydi. Rabbin diledi ki onlar büyüyüp tam güçlerine kavuştuktan sonra Rabbinden bir rahmet olarak hazinelerini ortaya çıkarsınlar. Bunları kendi irademle yapmadım. İşte bunlar, dayanamadığın şeylerin açıklamasıdır."'),
(2223, 18, 83, 'Sana, iki nesil kadar yönetime sahip olan kişiden sorarlar. De ki, "Size ondan bir öğüt ve hatırlatma aktaracağım."'),
(2224, 18, 84, 'Biz ona yeryüzünde yönetim gücünü ve her türlü imkanı verdik'),
(2225, 18, 85, 'Nitekim, o bir yol izledi'),
(2226, 18, 86, 'Uzak batıya varınca güneşi büyük bir okyanusta batar buldu ve orada bir topluluk ile karşılaştı. "Ey İki Nesil Sahibi, dilersen onları cezalandır, dilersen onlara iyi davran," dedik.'),
(2227, 18, 87, 'Dedi ki, "Kim haksızlık ederse onu cezalandıracağız, Rabbine döndürülünce de onu görülmemiş bir cezaya çarpacaktır."'),
(2228, 18, 88, '"İnanıp erdemli davranana gelince, ona güzel bir ödül vardır. Ona kolaylık göstereceğiz."'),
(2229, 18, 89, 'Sonra bir yol tuttu.'),
(2230, 18, 90, 'Uzak doğuya varınca, güneşi, kendilerini güneşten koruyacak herhangi bir şeye sahip olmayan bir topluluk üzerine doğar buldu.'),
(2231, 18, 91, 'İşte böyle... Onun her bulduğunu tamamıyla biliyorduk.'),
(2232, 18, 92, 'Sonra yine bir yol tuttu.'),
(2233, 18, 93, 'İki seddin arasına varınca, ötesinde, nerdeyse söz anlamayan bir topluluk buldu'),
(2234, 18, 94, 'Dediler ki, "Ey İki Nesle Sahip Olan (Zül Karneyn), Yecuc ve Mecuc yeryüzünde kötülük işliyorlar. Bizimle onların arasında bir engel koyman için sana bir vergi ödeyebilir miyiz?"'),
(2235, 18, 95, 'Dedi ki, "Rabbimin bana verdikleri daha iyidir. Bana gücünüzle yardım edin de sizinle onların arasına bir duvar kurayım."'),
(2236, 18, 96, '"Bana demir kütleleri getirin." Her iki barikatın arasını doldurunca, "Üfleyin!," dedi. Onu bir ateş haline sokunca da, "Getirin, üstüne erimiş bakır dökeyim," dedi.'),
(2237, 18, 97, 'Artık onu ne aşabildiler, ne de delebildiler.'),
(2238, 18, 98, '"Bu, Rabbimden bir rahmettir," dedi. "Rabbimin belirlediği an gelince onu paramparça eder. Rabbimin sözü gerçektir."'),
(2239, 18, 99, 'O gün onları bırakırız. Dalgalar halinde birbirlerine girerler. Boruya üflenir; hepsini bir araya toplarız.'),
(2240, 18, 100, 'O gün cehennemi sunarız o kafirlere...'),
(2241, 18, 101, 'Onlar ki mesajıma karşı gözleri perdeliydi, dinleyemezlerdi de...'),
(2242, 18, 102, 'İnkarcılar, benim dışımda kullarımı veliler edinerek kurtulacaklarını mı sandılar. O inkarcıların konağı olarak cehennemi belirledik.'),
(2243, 18, 103, 'De ki, "İş yapanların en kötüsünün kim olduğunu size bildireyim mi?"'),
(2244, 18, 104, '"Onlar, iyi iş yaptıklarını zannettikleri halde dünyadaki çabaları boşa çıkanlardır."'),
(2245, 18, 105, 'Onlar, Rab\'lerinin ayetlerini ve O\'nunla karşılaşmayı inkar edenlerdir. Bu yüzden işleri boşa çıkar ve diriliş gününde de onlar için bir değer biçmeyiz.'),
(2246, 18, 106, 'İnkar ettikleri, ayetlerimi ve elçilerimi hafife aldıkları için, onlara en uygun ceza cehennemdir.'),
(2247, 18, 107, 'İnanıp erdemli davrananlara gelince, onlar durak olarak neşe ve mutluluk dolu cennetleri haketmişlerdir.'),
(2248, 18, 108, 'Orada sürekli kalıcıdırlar, orayı hiç bir şeyle değişmek istemezler.'),
(2249, 18, 109, 'De ki, "Rabbimin sözleri için okyanus mürekkep olsa ve hatta bir o kadarını da katsak, Rabbimin sözleri tükenmeden okyanus tükenir."'),
(2250, 18, 110, 'De ki, "Ben sizin gibi bir insandan başka bir şey değilim. Tanrınızın bir tek Tanrı olduğu bana vahyedildi. Rabbiyle karşılaşmayı uman herkes erdemli işler yapsın ve Rabbine olan kulluğa hiç kimseyi ortak koşmasın."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'K. H. Y. \'A. SS. (Kef Ha Ya \'Ayn Sad)'),
(2252, 19, 2, 'Bu, Rabbinin, kulu Zekeriyya\'ya olan rahmetini anıştır.'),
(2253, 19, 3, 'Rabbine gizli bir yalvarışla seslenmişti'),
(2254, 19, 4, '"Rabbim," dedi, "Vücudumdaki kemik gevşedi, başım ağarıp tutuştu. Sana yalvarışta, Rabbim, hiç bir vakit umut kesmedim."'),
(2255, 19, 5, '"Bana bağımlı olanların benden sonraki durumundan endişeleniyorum. Karım da kısır. Katından bana bir kalıtçı bağışla."'),
(2256, 19, 6, '"Bana ve Yakup boyuna varis olsun. Rabbim, onu beğendiğin biri yap."'),
(2257, 19, 7, '"Zekeriyya, sana bir oğul müjdeleriz. İsmi Yahya\'dır. Onun gibisini daha önce yaratmadık."'),
(2258, 19, 8, '"Rabbim, benim nasıl bir oğlum olabilir? Karım kısır, bense alabildiğine yaşlıyım," dedi.'),
(2259, 19, 9, '"Evet öyledir," dedi, "Rabbin, \'O iş bana kolaydır. Seni daha önce yaratmıştım ve sen hiç bir şey değildin\' diyor."'),
(2260, 19, 10, '"Rabbim, bana bir işaret ver," dedi. "Senin işaretin, birbirini izleyen üç gece boyunca halkla konuşmamandır."'),
(2261, 19, 11, 'Tapınaktan halkının arasına çıktı ve "O\'nu sabah akşam düşünüp anın," diye onlara işaretle bildirdi.'),
(2262, 19, 12, '"Yahya, kitaba iyice sarıl." Çocuk yaşta kendisine bilgelik vermiştik.'),
(2263, 19, 13, 'Ek olarak katımızdan bir şefkat ve dürüstlük... Erdemli birisiydi.'),
(2264, 19, 14, 'Ana babasına karşı iyi davranırdı, asla bir zorba ve isyankâr olmadı.'),
(2265, 19, 15, 'Doğduğu gün, öleceği gün ve diri olarak kaldırılacağı gün ona selam olsun!'),
(2266, 19, 16, 'Kitapta Meryem\'i de an. Ailesinden ayrılıp doğu tarafında bir yere çekilmişti.'),
(2267, 19, 17, 'Kendisiyle onlar arasına bir perde çekmişti. Bu durumda ona Ruhumuzu gönderdik ve önünde mükemmel bir insan olarak biçimlendi.'),
(2268, 19, 18, '"Senden Rahman\'a sığınırım," dedi, "Erdemliysen..."'),
(2269, 19, 19, '(Melek:) "Ben, sana tertemiz bir erkek çocuğu vermek için görevlendirilmiş Rabbinin bir elçisiyim," dedi.'),
(2270, 19, 20, '"Bana hiç bir insan eli değmemiş ve ben iffetsizlik etmemişken nasıl olur da bir oğlum olur," dedi.'),
(2271, 19, 21, '"Öyledir," dedi, "Rabbin, \'O iş bana kolaydır. Onu halk için bir işaret ve bizden bir rahmet kılacağız. Bu, artık kararlaştırılmış bir iştir\' diyor."'),
(2272, 19, 22, 'Ona gebe kalınca onunla uzak bir bölgeye çekildi.'),
(2273, 19, 23, 'Doğum sancısı onu bir hurma dalına kadar sürükledi. "Keşke bundan önce ölseydim, unutulsaydım," dedi.'),
(2274, 19, 24, '"Üzülme, Rabbin senin altında bir su arkı hazırlamıştır," diye (ağacın) altından kendisine seslendi.'),
(2275, 19, 25, '"Hurmanın dalını kendine doğru silkele, üzerine olgun hurmalar dökülsün."'),
(2276, 19, 26, '"Ye, iç ve gözün aydın olsun. İnsanlardan birini görürsen, \'Ben Rahman için oruç tutmaya karar verdim. Bugün hiç bir insanla konuşmayacağım\' de."'),
(2277, 19, 27, 'Onu alıp halkına getirdi. "Meryem, sen şaşılacak bir şey işledin!," dediler.'),
(2278, 19, 28, '"Ey Harun\'un kız kardeşi, baban kötü bir adam değildi. Annen de iffetsiz değildi."'),
(2279, 19, 29, '(Tanıklık için) Onu gösterdi. "Nasıl olur da beşikteki bir çocukla konuşuruz," dediler.'),
(2280, 19, 30, '(Çocuk:) "Ben ALLAH\'ın bir kuluyum. Bana kitap verdi ve beni peygamber kıldı," dedi.'),
(2281, 19, 31, '"Nerede bulunursam bulunayım beni kutlu kıldı. Yaşadığım sürece bana namazı ve zekatı ve "'),
(2282, 19, 32, '"anneme karşı iyi huylu olmamı emretti. Beni baş kaldıran bir zorba yapmadı."'),
(2283, 19, 33, '"Doğduğum gün, öldüğüm gün ve diri olarak kaldırılacağım gün bana selam olsun."'),
(2284, 19, 34, 'İşte, Meryem oğlu İsa böyleydi. Hakkında kuşkuya düştükleri konunun gerçeği budur.'),
(2285, 19, 35, 'ALLAH için bir çocuk edinmek söz konusu olamaz. O yücedir. Bir iş diledi mi, ona sadece "Ol," der, o da olur.'),
(2286, 19, 36, '"ALLAH benim de Rabbim, sizin de Rabbinizdir; O\'na kulluk ediniz. Doğru yol budur."'),
(2287, 19, 37, 'Mezhepler, (İsa\'nın kimliği üzerinde) aralarında anlaşmazlığa düştüler. Büyük bir güne tanık olacak kafirlerin vay haline!'),
(2288, 19, 38, 'Bize geldikleri gün onları dinle ve seyret! Zalimler, o gün apaçık bir sapıklık içindedirler.'),
(2289, 19, 39, 'Yargının noktalanacağı Keder Günü hakkında onları uyar. Onlar hâlâ aymazlık içinde inanmıyorlar.'),
(2290, 19, 40, 'Yer ve üzerindekiler bize kalacak; onlar bize döndürülecekler.'),
(2291, 19, 41, 'Kitapta İbrahim\'i an; peygamber olan bir doğrucu idi.'),
(2292, 19, 42, 'Babasına, "Babacığım, işitmeyen, görmeyen ve sana bir yararı dokunmayan şeylere niye tapıyorsun," demişti.'),
(2293, 19, 43, '"Babacığım, bana, sana gelmeyen bir bilgi geldi. Beni izle de seni düzgün yola ileteyim."'),
(2294, 19, 44, '"Babacığım, şeytana tapma. Şeytan, Rahman\'a karşı çıkmıştır."'),
(2295, 19, 45, '"Babacığım, Rahman tarafından bir cezaya çarpılman ve şeytana dost olmandan korkuyorum."'),
(2296, 19, 46, '(Babası:) "Sen benim tanrılarıma yüz mü çeviriyorsun İbrahim? Buna son vermezsen seni taşlarım. Benden uzaklaş!," dedi.'),
(2297, 19, 47, '"Sana selam (barış) olsun," dedi, "Senin bağışlanman için Rabbim\'e yalvaracağım; O, bana karşı çok merhametlidir."'),
(2298, 19, 48, '"Sizden ve ALLAH dışında yalvardıklarınızdan ayrılıyorum. Ben Rabbime yalvarıyorum. Umarım ki Rabbime yalvarmakla bahtsız olmam."'),
(2299, 19, 49, 'Onları ve ALLAH dışında taptıklarını terkedince ona İshak\'ı ve Yakub\'u verdik. Hepsini peygamber yaptık.'),
(2300, 19, 50, 'Onlara rahmetimizden verdik. Onlara, doğru ve onurlu bir dil bağışladık.'),
(2301, 19, 51, 'Kitapta Musa\'yı an. O kendini tümüyle adayan biriydi. Peygamber olan bir elçiydi.'),
(2302, 19, 52, 'Ona Tur dağının sağ tarafından seslendik. Konuşmak için onu yaklaştırdık.'),
(2303, 19, 53, 'Katımızdan bir rahmet olarak kardeşi Harun\'u kendisine peygamber olarak armağan ettik.'),
(2304, 19, 54, 'Kitapta İsmail\'i an. O sözünde duran biriydi. Aynı zamanda peygamber olan bir elçiydi.'),
(2305, 19, 55, 'Ailesine namazı gözetmeyi ve zekatı vermeyi emrederdi. Rabbi tarafından beğenilmişti.'),
(2306, 19, 56, 'Kitapta İdris\'i an. O peygamber olan bir doğrucu idi.'),
(2307, 19, 57, 'Onu yüce bir makama yükselttik.'),
(2308, 19, 58, 'İşte bunlar, ALLAH\'ın nimetlendirdiği peygamberlerin bir kısmıdır. Adem\'in soyundan, Nuh ile birlikte taşıdıklarımızdan, İbrahim\'in ve İsrail\'in soyundan... Doğruya ulaştırdığımız ve seçtiğimiz kimselerdir onlar. Kendilerine Rahman\'ın ayetleri okunduğu zaman ağlayarak secdeye varırlar.'),
(2309, 19, 59, 'Onlardan sonra gelenler namazı yitirdiler ve heveslerine uydular. Nitekim, felakete uğrayacaklar.'),
(2310, 19, 60, 'Tevbe eden, inanan ve erdemli davrananlar hariç. Onlar en ufak bir haksızlığa uğramadan cennete girerler.'),
(2311, 19, 61, 'Rahman\'ın kulları için söz verdiği, duyular ötesi Adn cennetlerine... O\'nun sözü, kuşkusuz yerine gelecektir.'),
(2312, 19, 62, 'Orada boş söz işitmezler; sadece barış... Rızıklarını da sabah akşam alırlar.'),
(2313, 19, 63, 'Erdemli kullarımıza vereceğimiz cennet işte budur.'),
(2314, 19, 64, 'Biz (melekler) ancak Rabbinin emriyle ineriz. Geçmişimiz, geleceğimiz ve ikisi arasında ne varsa O\'na aittir. Rabbin unutkan değildir.'),
(2315, 19, 65, 'Göklerin, yerin ve bunlar arasında bulunanların Rabbidir. O\'na kulluk et ve O\'na olan kulluğunda dirençli ol. O\'na benzer birini biliyor musun?'),
(2316, 19, 66, '"Öldüğüm zaman diri olarak çıkarılacak mıyım," diye soruyor insan.'),
(2317, 19, 67, 'İnsan, önceden hiç bir şey değilken kendisini nasıl yarattığımızı düşünmez mi?'),
(2318, 19, 68, 'Rabbine and olsun, onları ve şeytanları toplayacağız. Sonra onları cehennemin çevresine getireceğiz. Diz çökmüş halde...'),
(2319, 19, 69, 'Sonra her gruptan, Rahman\'a karşı azgınlıkta ileri gidenleri ayıklayacağız.'),
(2320, 19, 70, 'Orada yanmayı en çok kimin hakkettiğini elbette biz gayet iyi biliriz.'),
(2321, 19, 71, 'İçinizden oraya gelmeyecek yoktur; bu, Rabbinin gerçekleştireceği kesin bir karardır.'),
(2322, 19, 72, 'Sonra, erdemlileri kurtaracağız. Zalimleri ise orada diz üstü bırakacağız.'),
(2323, 19, 73, 'Kendilerine ayetlerimiz apaçık olarak okunduğu zaman inkar edenler inananlara, "Hangimiz daha gönençli ve hangimiz sayısal üstünlüğe sahiptir," derler.'),
(2324, 19, 74, 'Onlardan önce, daha varlıklı ve daha gösterişli nice nesilleri yok ettik.'),
(2325, 19, 75, 'De ki, "Sapıklığa dalanlara Rahman bir süre verir. Cezayı veya dünyanın sonunu görünceye kadar... Kötü yere ve zayıf orduya kimin sahip olduğunu öğrenecekler."'),
(2326, 19, 76, 'ALLAH, doğru yolu seçenlerin hidayetini arttırır. Erdemli işler, Rabbinin katında ebedi olarak en iyi ödülü ve en iyi sonucu hakkeder.'),
(2327, 19, 77, 'Ayetlerimizi reddeden ve, "Bana mal ve çocuk verilecek!," diyeni gördün mü?'),
(2328, 19, 78, 'Geleceğin bilgisine mi sahip oldu? Yoksa Rahman\'dan bir söz mü aldı?'),
(2329, 19, 79, 'Hayır. Söylediklerini kaydedeceğiz ve cezasını arttıracağız.'),
(2330, 19, 80, 'Sözünü ettikleri bize kalacak ve bize yalnız gelecektir.'),
(2331, 19, 81, 'Kendilerine destek olsunlar diye ALLAH\'ın yanında tanrılar edindiler.'),
(2332, 19, 82, 'Tam tersine! Bu tapınmayı reddedeceklerdir ve onlara karşıt olacaklardır.'),
(2333, 19, 83, 'İnkarcıların üzerlerine şeytanları yolladığımızı görmez misin? Onları kışkırtıp duruyorlar.'),
(2334, 19, 84, 'Acele etme; biz onlar için saydıkça sayıyoruz.'),
(2335, 19, 85, 'Erdemlileri, Rahman\'ın huzurunda konuk grupları halinde toplayacağımız,'),
(2336, 19, 86, 'Ve suçluları, susuz olarak cehenneme sürdüğümüz gün,'),
(2337, 19, 87, 'Rahman\'ın yanında söz almış olanlardan başkası şefaat (aracılık) edemez.'),
(2338, 19, 88, 'Hatta "Rahman çocuk edindi," dediler'),
(2339, 19, 89, 'Siz, küstahça bir tez ileri sürdünüz.'),
(2340, 19, 90, 'Bu küstahlıktan ötürü neredeyse gökler parçalanacak, yer yarılacak ve dağlar göçecektir.'),
(2341, 19, 91, 'Rahman\'a çocuk yakıştırdılar diye...'),
(2342, 19, 92, 'Çocuk edinmek Rahman\'a yakışmaz.'),
(2343, 19, 93, 'Göklerde ve yerde kim varsa, Rahman\'a kul olarak gelecektir.'),
(2344, 19, 94, 'Onları kuşatmış ve tek tek saymıştır.'),
(2345, 19, 95, 'Onların hepsi, Diriliş günü O\'na tek başına gelecektir.'),
(2346, 19, 96, 'İnanıp erdemli davrananlara Rahman sevgi bağışlar.'),
(2347, 19, 97, 'Bunu senin dilinle kolaylaştırdık; onunla erdemlileri müjdeleyesin ve inatçı toplumu uyarasın diye.'),
(2348, 19, 98, 'Onlardan önce nice toplumları yok ettik: hiçbirini algılıyor musun, ya da fısıltılarını işitiyor musun?');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'TT. H.'),
(2350, 20, 2, 'Biz bu Kuran\'ı sıkıntı çekesin diye göndermedik.'),
(2351, 20, 3, 'Ancak saygı duyanlar için bir öğüttür.'),
(2352, 20, 4, 'Yeri ve yüksek gökleri Yaratan tarafından indirilmiştir.'),
(2353, 20, 5, 'Rahman, yönetim ve egemenliği elinde bulundurur.'),
(2354, 20, 6, 'Göklerde, yerde, her ikisinin arasında ve hatta toprağın altında ne varsa onundur.'),
(2355, 20, 7, '(Niyetini) Sözle açıklasan da (açıklamasan da) O, gizliyi ve gizlinin gizlisini bilir.'),
(2356, 20, 8, 'ALLAH, O\'ndan başka tanrı yoktur. Tüm güzel isimler O\'na aittir.'),
(2357, 20, 9, 'Musa\'nın haberi sana ulaştı mı?'),
(2358, 20, 10, 'Bir ateş görmüştü ve ailesine, "Burada durun, ben bir ateş gördüm. Olur ki size ondan bir kor getiririm, yahut ateşin yanında bir yol gösterici bulurum," demişti.'),
(2359, 20, 11, 'Oraya varınca, "Ey Musa!," diye seslenildi.'),
(2360, 20, 12, '"Ben, evet Ben senin Rabbinim. Sandallarını çıkar. Çünkü sen kutsal vadide, Tuva\'dasın."'),
(2361, 20, 13, '"Ben seni seçtim, öyleyse vahyolanı dinle."'),
(2362, 20, 14, '"Ben, evet Ben ALLAH\'ım; Benden başka tanrı yoktur. Bana kulluk et ve Beni anmak için namazı gözet."'),
(2363, 20, 15, 'Dünyanın sonu elbette gelecektir. Herkes yaptığının karşılığını görsün diye Ben nerdeyse onu gizleyeceğim.'),
(2364, 20, 16, 'Ona inanmayıp hevesine uyanlar seni ondan saptırmasın, sonra başüstü düşersin.'),
(2365, 20, 17, '"Şu elindeki nedir, Musa?"'),
(2366, 20, 18, '"O, benim değneğimdir. Ona dayanırım, onunla koyunlarıma yaprak silkelerim ve bana daha başka yararları da dokunmaktadır," dedi.'),
(2367, 20, 19, '"At onu Musa!," dedi.'),
(2368, 20, 20, 'Onu atınca, hareketli bir yılana dönüşüverdi.'),
(2369, 20, 21, 'Dedi, "Al onu, korkma. Onu ilk durumuna sokacağız."'),
(2370, 20, 22, '"Bir başka delil olarak, elini koltuğunun altına koy; lekesiz bembeyaz olarak çıksın."'),
(2371, 20, 23, '"Böylece sana en büyük mucizelerimizden bazılarını gösterelim."'),
(2372, 20, 24, '"Firavun\'a git, çünkü o azdı."'),
(2373, 20, 25, '"Rabbim," dedi, "göğsümü aç."'),
(2374, 20, 26, '"Bana işimi kolaylaştır."'),
(2375, 20, 27, '"Dilimdeki düğümü çöz."'),
(2376, 20, 28, '"Ki sözümü anlasınlar."'),
(2377, 20, 29, '"Ailemden bana bir yardımcı ata."'),
(2378, 20, 30, '"Kardeşim Harun\'u..."'),
(2379, 20, 31, '"Beni onunla destekleyip güçlendir."'),
(2380, 20, 32, '"Bu işimde onu bana ortak yap."'),
(2381, 20, 33, '"Ki seni çokça yüceltelim."'),
(2382, 20, 34, '"Seni çokça analım."'),
(2383, 20, 35, '"Sen, elbette bizi Görensin."'),
(2384, 20, 36, 'Dedi ki: "Dilediğin sana verildi, Musa."'),
(2385, 20, 37, '"Sana bir kez daha lütufta bulunduk."'),
(2386, 20, 38, '"Hani annene şu vahyi vahyetmiştik:"'),
(2387, 20, 39, '"\'Onu bir sandığa koyup ırmağa at. Irmak da onu kıyıya atsın ve Bana da ona da düşman olan birisi onu alsın.\' diye... Gözümün önünde yetişesin diye sana sevgimi yağdırmıştım."'),
(2388, 20, 40, '"Hani kız kardeşin gidip, \'ona bakacak birini size göstereyim mi?\' diyordu. Böylece, gözü aydınlansın ve üzülmesin diye seni annene geri döndürmüştük. Hatta sen bir kişiyi öldürmüştün de seni tasadan kurtarmış ve çeşitli testlerden geçirmiştik. Medyen halkı arasında yıllarca kaldıktan sonra belli bir plan gereği şimdi geri gelmiş bulunuyorsun, Musa."'),
(2389, 20, 41, '"Seni kendim için yarattım."'),
(2390, 20, 42, '"Sen ve kardeşin mucize ve ayetlerimi iletin. Beni anmakta gevşek olmayın. "'),
(2391, 20, 43, '"İkiniz Firavun\'a gidin; çünkü o azdı."'),
(2392, 20, 44, '"Ona yumuşak bir dil kullanın; olur ki öğüt alır veya saygı duyar."'),
(2393, 20, 45, 'Dediler ki: "Rabbimiz, onun bize karşı saldırı ve taşkınlıkta bulunmasından korkuyoruz."'),
(2394, 20, 46, '"Korkmayın," dedi, "Ben sizinle birlikteyim; görüyorum ve işitiyorum."'),
(2395, 20, 47, '"Ona varın ve deyin ki, \'Biz ikimiz Rabbinin elçileriyiz. İsrail oğullarına yaptığın işkenceye son ver ve onları bizimle gönder. Biz sana, Rabbinden bir ayet ile geldik. Doğru yolu izleyenlere selam (barış) olsun."'),
(2396, 20, 48, '"Bize vahyedildi: Yalanlayıp yüz çevirenler cezalandırılacaktır."'),
(2397, 20, 49, '(Firavun) dedi ki: "Rabbiniz kimdir, Musa?"'),
(2398, 20, 50, '"Rabbimiz, Her şeye biçimini veren ve sonra yolunu gösterendir," dedi.'),
(2399, 20, 51, '"Peki geçmiş nesillerin hali ne olacak," dedi.'),
(2400, 20, 52, '"Onların bilgisi Rabbimin yanında kayıtlıdır. Rabbim yanılmaz, unutmaz."'),
(2401, 20, 53, 'O ki yeryüzünü sizin için yaşanılır kıldı, sizin için onda yollar açtı, gökten bir su indirdi ve nitekim onunla çeşit çeşit bitkiler çıkardık.'),
(2402, 20, 54, 'Çiftlik hayvanlarını yiyiniz, otlatınız. Zeka sahipleri için bunda işaretler vardır.'),
(2403, 20, 55, 'Sizi o yerden yarattık, sizi tekrar ona döndüreceğiz ve bir kez daha sizi ondan çıkartacağız.'),
(2404, 20, 56, 'Ona tüm işaret ve delillerimizi göstermemize rağmen yalanlayıp reddetti.'),
(2405, 20, 57, 'Dedi ki, "Sen bizi büyünle yurdumuzdan çıkarmak için mi geldin, Musa?"'),
(2406, 20, 58, '"Biz de sana benzer bir büyü göstereceğiz. Her iki taraf için de uygun olan yerde ne senin ne de bizim caymayacağımız bir randevu zamanı belirle. "'),
(2407, 20, 59, 'Dedi ki: "Buluşma zamanınız bayram günüdür. Öğleden önce halk orada toplansın."'),
(2408, 20, 60, 'Firavun gitti, planını hazırlayıp geri döndü.'),
(2409, 20, 61, 'Musa onlara dedi ki: "Size yazıklar olsun. ALLAH\'a karşı yalan uydurmayın. Sonra sizi bir azap ile perişan eder. İftira edenler kuşkusuz kaybedecektir."'),
(2410, 20, 62, 'Aralarında işlerini tartıştılar ve konuşmalarını gizli tuttular.'),
(2411, 20, 63, 'Dediler ki, "Bu iki büyücünün tek amacı, büyüleriyle sizi yurdunuzdan çıkarmak ve sizin ideal yaşam biçiminizi yıkmaktır."'),
(2412, 20, 64, '"Haydi, planlarınızı birleştirip birleşik bir cephe oluşturun. Bugün üstün gelen başarmıştır."'),
(2413, 20, 65, '"Musa," dediler, "Ya sen at, yahut ilk önce atan biz olalım?"'),
(2414, 20, 66, '"Hayır, siz atın!," dedi. Bunun üzerine, büyülerinden (hipnotik telkin ve illüzyondan) ötürü, halatları ve değnekleri ona (Musa\'ya) sanki hareket ediyorlarmış gibi göründü.'),
(2415, 20, 67, 'Musa içinde bir korku duydu.'),
(2416, 20, 68, '"Korkma," dedik, "Sen üstün geleceksin."'),
(2417, 20, 69, '"Sağ elindekini at, onların uydurdukları şeyleri yutacaktır. Onların yaptığı bir büyücü planından ibarettir. Büyücünün ortaya koyduğu şeyler başarıya ulaşamaz."'),
(2418, 20, 70, 'Büyücüler, "Harun\'un ve Musa\'nın Rabbine inandık," diyerek secdeye kapandılar.'),
(2419, 20, 71, '"Size izin vermeden mi ona inandınız? O, size büyü öğreten ustanız olmalı. Ellerinizi ve ayaklarınızı çaprazlama kesip sizi hurma ağaçlarına asacağım. Hangimizin cezası daha çetin ve sürekli imiş öğreneceksiniz," dedi.'),
(2420, 20, 72, 'Dediler ki: "Bize gelen apaçık kanıtları ve bizi Yaratan\'ı bırakıp seni seçmeyiz. Nasıl yargı vereceksen ver. Yargın bu dünya hayatıyla sınırlıdır!"'),
(2421, 20, 73, '"Biz Rabbimize inandık ki hatalarımızı ve bizi zorla yaptırdığın büyücülüğü bağışlasın. ALLAH daha iyidir ve daha Süreklidir."'),
(2422, 20, 74, 'Kim Rabbine suçlu olarak gelirse cehennemi hakkeder; orada ne ölür, ne de yaşar.'),
(2423, 20, 75, 'İnanmış ve erdemli bir hayat sürmüş olarak O\'na gelenler ise, yüksek dereceleri hakkederler:'),
(2424, 20, 76, 'Adn bahçeleri ki altından ırmaklar akar. Orada ebedi kalıcıdırlar. Arınanların ödülü işte böyledir.'),
(2425, 20, 77, 'Musa\'ya: "Kullarımı geceleyin yürüyüşe çıkar ve onlar için denizde kuru bir yol aç. Yakalanmaktan korkma, endişe etme," diye vahyettik.'),
(2426, 20, 78, 'Firavun, ordusuyla birlikte ardlarına düştü. Ne var ki, deniz üstlerine kapanıp onları içine aldı.'),
(2427, 20, 79, 'Firavun, halkını saptırdı, doğru yola iletmedi.'),
(2428, 20, 80, 'Ey İsrail oğulları, sizi düşmanlarınızdan kurtarmış, Sina dağının sağ yanında size söz vermiş ve üzerinize Menna ve bıldırcın indirmiştik.'),
(2429, 20, 81, 'Size verdiğimiz rızıkların iyilerinden yeyiniz ve bu konuda taşkınlıkta bulunmayın; yoksa gazabıma uğrarsınız. Gazabıma uğrayanlar düşmüştür.'),
(2430, 20, 82, 'Tevbe eden, inanan, erdemli yaşayan ve sürekli doğruyu arayanlar için Bağışlayıcıyım.'),
(2431, 20, 83, '"Niçin halkını bırakmakta acele ettin, Musa?"'),
(2432, 20, 84, '"Onlar öğretimi izliyorlar," dedi, "Hoşnut olasın diye sana doğru acele ettim, Rabbim."'),
(2433, 20, 85, '"Halkını, senden sonra sınadık. Samiri onları saptırdı," dedi.'),
(2434, 20, 86, 'Musa, kızgın ve hayal kırıklığına uğramış olarak halkına döndü ve, "Halkım, Rabbiniz size güzel bir söz vermemiş miydi? Bekleyemediniz mi? Yoksa, Rabbinizin gazabına uğramak için mi benimle yaptığınız sözleşmeyi bozdunuz," dedi.'),
(2435, 20, 87, '"Sana verdiğimiz sözü kendi kafamıza göre bozmadık. O halkın süs eşyaları bize taşıtıldı. Onları attık. Samiri işte böyle bir şey ortaya çıkardı," dediler.'),
(2436, 20, 88, 'Onlar için, böğüren bir buzağı heykeli çıkardı. "İşte sizin ve Musa\'nın tanrısı budur, fakat o (Musa) unuttu," dediler.'),
(2437, 20, 89, 'Görmediler mi ki, o, onlara ne bir yanıt verebiliyor, ne de onlara bir zarar ve yarar dokundurabiliyordu'),
(2438, 20, 90, 'Harun ise: "Halkım, onunla sınanıyorsunuz. Rabbiniz Rahman\'dır. Beni izleyin ve emrime uyun," diye onları önceden uyarmıştı.'),
(2439, 20, 91, '"Musa gelinceye kadar ona tapmaya devam edeceğiz," diye karşılık vermişlerdi.'),
(2440, 20, 92, 'Dedi ki, "Harun! Seni engelleyen neydi, onları saparken gördüğün zaman,"'),
(2441, 20, 93, '"Bana uymazlarken? Emrime karşı mı geldin?"'),
(2442, 20, 94, 'Dedi ki, "Anamın oğlu, sakalımı ve başımı çekme. \'İsrail oğullarını neden böldün, neden sözümü tutmadın?\' diye bana çıkışacağından korktum."'),
(2443, 20, 95, 'Dedi ki, "Peki, senin savunman nedir, Samiri?"'),
(2444, 20, 96, 'Dedi ki, "Onların görmediğini gördüm, elçinin öğretisinden bir kısmını alıp attım. Böyle uygun gördüm."'),
(2445, 20, 97, 'Dedi ki, "Defol! Hayatın boyunca yakına bile gelme. Sana söz verilen bir an var ki ondan kaçamıyacaksın. Tapmakta olduğun tanrına bak, biz onu yakıp denize savuracağız."'),
(2446, 20, 98, 'Tanrınız, kendisinden başka tanrı olmayan ALLAH\'tır. Bilgisi her şeyi içine almıştır.'),
(2447, 20, 99, 'Geçmişlerin haberlerini, sana böylece aktarıyoruz. Sana katımızdan bir mesaj vermiş bulunuyoruz.'),
(2448, 20, 100, 'Kim ondan yüz çevirirse Diriliş Gününde bir (günah) yükü taşıyacaktır.'),
(2449, 20, 101, 'Orada ebedi kalırlar. Diriliş günü bu onlar için ne de kötü bir yüktür.'),
(2450, 20, 102, 'Boruya üfürüldüğü gün, suçluları o gün mavi (kederden yüzleri morarmış) olarak toplarız'),
(2451, 20, 103, 'Aralarında gizli gizli konuşurlar, "Siz (dünyada) sadece on kaldınız."'),
(2452, 20, 104, 'Onların ne konuştuğunu iyi biliriz. En doğru görüşlüleri, "Siz sadece bir gün kaldınız," diyordu.'),
(2453, 20, 105, 'Senden dağları sorarlar. De ki, "Rabbim onları ufalayıp savuracak."'),
(2454, 20, 106, '"Yerlerini dümdüz, bomboş bırakacaktır."'),
(2455, 20, 107, '"Orda ne ufak bir eğrilik ne de bir tümsek göreceksin."'),
(2456, 20, 108, 'O gün, en ufak bir sapma göstermeden çağırıcıya uyarlar. Sesler Rahman\'ın huzurunda kısılmıştır; fısıltıdan başka bir şey işitemezsin.'),
(2457, 20, 109, 'O gün, Rahman\'ın izin verdiği ve sözünü uygun gördüğü kimseden başkasının şefaatı (aracılığı) yarar vermez.'),
(2458, 20, 110, 'Hiç kimse O\'nu bilgice kavrayamazken, O onların geçmişini de geleceğini de bilir.'),
(2459, 20, 111, 'Tüm yüzler O Yaşayan, Ebedi Yönetici\'ye çevrilmiştir. Zulüm yüklenenler kaybedecektir.'),
(2460, 20, 112, 'İnançlı olduğu halde erdemli davrananlar herhangi bir haksızlıktan ve güçlükten korkmayacaktır.'),
(2461, 20, 113, 'Biz, böylece onu Arapça bir Kuran olarak indirdik. Erdemli davranırlar veya onlar için bir öğüt olur diye onda geleceğin haberlerini çeşitli biçimlerde açıkladık.'),
(2462, 20, 114, 'Gerçek Yönetici olan ALLAH çok yücedir. Sana vahyi tamamlanmadan önce Kuran\'ı (anlamak için) acele etme ve, "Rabbim, bilgimi arttır," de.'),
(2463, 20, 115, 'Geçmişte Adem\'den söz almıştık; ancak unuttu. Biz onda bir azim ve kararlılık görmedik.'),
(2464, 20, 116, '"Adem\'e secde edin," dediğimizde melekler secde ettiler, ancak İblis hariç; o reddetti.'),
(2465, 20, 117, '"Adem," dedik, "Bu senin ve eşinin düşmanıdır. Sakın sizi cennetten çıkarmasın, yoksa perişan olursun."'),
(2466, 20, 118, '"Burda ne acıkırsın, ne de açıkta kalırsın."'),
(2467, 20, 119, '"Burda ne susuzluk çeker, ne de sıcaktan bunalırsın."'),
(2468, 20, 120, 'Şeytan, "Adem, sana ebedilik ağacını ve tükenmez bir egemenliği göstereyim mi," diye ona fısıldadı.'),
(2469, 20, 121, 'İkisi de ondan yediler ve bunun üzerine vucutları kendilerine göründü. Cennetin yaprakları ile örtünmeye çalıştılar. Adem Rabbine karşı geldi ve şaşırdı.'),
(2470, 20, 122, 'Sonra Rabbi onu seçip affetti ve ona yol gösterdi.'),
(2471, 20, 123, 'Dedi ki, "Birbirinize düşmanlar olarak ordan aşağı inin. Size benden bir yol gösterici geldiğinde, kim benim yoluma uyarsa o sapmaz ve perişan olmaz."'),
(2472, 20, 124, '"Kim mesajımdan yüz çevirirse sıkıntılarla dolu bir hayata mahkum olur. Diriliş günü de onu kör olarak meydana çıkarırız."'),
(2473, 20, 125, '"Rabbim," der, "Toplantı alanına beni niye kör olarak sürdün, oysa ben görür idim?"'),
(2474, 20, 126, 'Der ki: "Çünkü sana ayetlerimiz ve mucizelerimiz geldiğinde sen onları unuttun. Bugün de sen unutuluyorsun."'),
(2475, 20, 127, 'Sınırı aşanları ve Rabbinin ayet ve mucizelerine inanmıyanları işte böyle cezalandırırız. Ahiretin cezası elbette daha çetin ve daha süreklidir.'),
(2476, 20, 128, 'Önceki nesillerin yerleşim bölgelerinde dolaşanlar, onları yok edişimizden ders almazlar mı? Bunda akıl sahipleri için işaretler ve kanıtlar vardır.'),
(2477, 20, 129, 'Rabbinin verilmiş bir sözü ve önceden belirlediği plan olmasaydı bunların da icabına hemen bakılırdı.'),
(2478, 20, 130, 'Sözlerine karşı dayanıklı ol, güneşin doğumundan ve batımından önce Rabbini yücelterek an. Geceleyin ve günün her iki ucunda da an ki mutlu olabilesin.'),
(2479, 20, 131, 'Onların bazılarına, sınamak için verdiğimiz dünya hayatının süslerine gözünü dikme. Rabbinin senin için hazırladığı nimetler daha iyidir ve süreklidir.'),
(2480, 20, 132, 'Ailene namazı emret ve bu konunun üstünde önemle dur. Biz senden herhangi bir rızık beklemiyoruz. Aksine biz seni besliyoruz. Sonuç, erdemlilerindir.'),
(2481, 20, 133, '"Bize hiç olmazsa bir ayet (mucize) getirmeliydi!," dediler. Daha önceki kitaplarda bulunan beyyine (delil) kendilerine gelmedi mi?'),
(2482, 20, 134, 'Onları, ondan önce bir ceza ile helak etseydik, "Rabbimiz, bize bir elçi gönderseydin de böyle alçak ve rezil olmadan önce senin ayetlerine uysaydık!," derlerdi.'),
(2483, 20, 135, 'De ki: "Herkes gözlemekte. Siz de gözleyin. Düzgün yolun sahiplerinin kimler olduğunu ve kimlerin hidayet üzerinde olduğunu ileride bileceksiniz!"');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'İnsanların hesapları yaklaştı; ancak onlar hâlâ bir aymazlık içinde yüz çevirmektedirler.'),
(2485, 21, 2, 'Her ne zaman Rab\'lerinden kendilerine yeni bir mesaj (zikr) gelse, onu ciddiye almadan dinlerler.'),
(2486, 21, 3, 'Kalpleri pervasızdır. Zalimler gizlice birbirleriyle görüştüler: "Bu adam sizin gibi bir insan değil mi? Göz göre göre büyüye mi kapılacaksınız?"'),
(2487, 21, 4, 'Dedi ki, "Rabbim yerde ve gökte her sözü bilir. O İşitendir, Bilendir. "'),
(2488, 21, 5, 'Hatta, "Boş hayallerdir," "Onu o uydurmuş," ve "O bir şairdir, daha önceki elçiler gibi o da bize mucizeler getirsin," dediler.'),
(2489, 21, 6, 'Bunlardan önce yok ettiğimiz toplumlardan hiç biri inanmamıştı. Şimdi bunlar mı inanacak?'),
(2490, 21, 7, 'Senden önce, insanların dışında elçi göndermedik; onlara vahyediyorduk. Bilmiyorsanız uzmanlara sorunuz'),
(2491, 21, 8, 'Onları, yemek yemeyen bedenler olarak yaratmadık. Onlar ölümsüz de değillerdi.'),
(2492, 21, 9, 'Sonra onlara verdiğimiz sözü yerine getirdik; onları dilediklerimizle birlikte kurtardık; aşırı gidenleri de helak ettik.'),
(2493, 21, 10, 'Size, size olan mesajı içeren bir kitabı indirmiş bulunuyoruz. Aklınızı kullanmaz mısınız?'),
(2494, 21, 11, 'Nice ülkeleri, haksızlık etmelerinden ötürü kırıp geçirdik ve ardlarından başka toplumlar varettik.'),
(2495, 21, 12, 'Azabımızı hissettikleri anda ondan kaçmaya çalışıyorlardı.'),
(2496, 21, 13, 'Kaçmayın, lüks ve savurganlık için yaşadığınız yere, evlerinize dönünüz. Çünkü sorguya çekileceksiniz.'),
(2497, 21, 14, '"Vay bize, biz gerçekten zulmedenlermişiz," dediler.'),
(2498, 21, 15, 'Onları biçip tüketinceye kadar bu yalvarışlarını tekrarlayıp duracaklar.'),
(2499, 21, 16, 'Göğü, yeri ve aralarındakileri oyun oynamak için yaratmadık.'),
(2500, 21, 17, 'Bir eğlence edinmek dileseydik, onu kendi katımızdan edinirdik. Evet, böyle bir işi dileseydik!'),
(2501, 21, 18, 'Hayır, biz gerçeği batılın üstüne atarız da onu tepeler ve yok eder. Yakıştırdıklarınızdan ötürü vay halinize.'),
(2502, 21, 19, 'Göklerde ve yerde kim varsa O\'na aittir. Yanındakiler, O\'na kulluk etmekten büyüklenmez ve duraksamazlar.'),
(2503, 21, 20, 'Gece ve gündüz, hiç yorulmadan O\'nu yüceltip anarlar.'),
(2504, 21, 21, 'Onlar, diriltebilecek yeteneğe sahip tanrılar mı edindiler yeryüzünden?'),
(2505, 21, 22, 'O ikisinde (gökler ve yerde) ALLAH\'tan başka tanrılar olsaydı ikisi de kaosa girecekti. Mutlak otoritenin sahibi ALLAH onların nitelemelerinden çok yücedir.'),
(2506, 21, 23, 'O, yaptığından sorulmaz; fakat onlar sorulurlar.'),
(2507, 21, 24, 'O\'nun dışında tanrılar mı edindiler? De ki, "Delilinizi getirin. Bu, benim çağımdakilerin de mesajıdır, benden öncekilerin de mesajıdır." Ne var ki, onların çoğu gerçeği bilmediğinden yüz çevirirler.'),
(2508, 21, 25, 'Senden önce bir elçi göndermedik ki kendisine, "Benden başka tanrı yoktur; sadece Bana kulluk ediniz," diye vahyetmiş olmayalım.'),
(2509, 21, 26, '"Rahman bir çocuk edindi," dediler. O yücedir. Oysa onlar onurlu kullardır.'),
(2510, 21, 27, 'Onlar O\'ndan önce söz söylemezler; O\'nun emirlerini ise titizlikle uygularlar.'),
(2511, 21, 28, 'O onların geçmişini ve geleceğini bilir. Onlar O\'nun hoşnut olduğu kullarından başkası için şefaat etmezler. Onlar bile O\'nun karşısında saygıyla titrerler.'),
(2512, 21, 29, 'Onlardan kim, "Ben O\'nun yanında bir tanrıyım," derse onu cehennemle cezalandırırız; zalimleri biz böyle ödüllendiririz.'),
(2513, 21, 30, 'İnkar edenler, görmezler mi ki gökler ve yer bitişik durumda idi de biz onları patlattık? Ayrıca her canlıyı da sudan yarattık. Hâlâ inanmıyacaklar mı?'),
(2514, 21, 31, 'Onları sarsmasın diye yeryüzüne dağları yerleştirdik. Yolu bulmaları için onda geniş geçitler açtık.'),
(2515, 21, 32, 'Göğü korunmuş bir tavan yaptık. Buna rağmen onlar ondaki işaretlere ilgisiz durmaktadırlar.'),
(2516, 21, 33, 'O ki geceyi, gündüzü, güneşi ve ayı yaratmıştır. Bunların hepsi bir yörüngede yüzer.'),
(2517, 21, 34, 'Senden önce hiç bir insanı ölümsüz kılmadık. Sen ölürsen onlar ebedi mi kalacaklar?'),
(2518, 21, 35, 'Her can ölümü tadacaktır. Sizi bir test olarak iyi ve kötü olaylarla sınarız ve dönüşünüz bizedir.'),
(2519, 21, 36, 'Kafirler (gerçeği örtenler) seni gördüklerinde, "Tanrılarınızı diline dolayan bu mu," diye alaylarına hedef yapmaktan başka bir tepki göstermiyorlar. Rahman\'ın mesajını tümüyle inkar etmektedir onlar.'),
(2520, 21, 37, 'İnsanlar aceleci olarak yaratılmıştır. Size ayetlerimi (işaretlerimi) göstereceğim; acele etmeyin.'),
(2521, 21, 38, '"Doğru sözlü iseniz O verilen söz ne zaman gerçekleşecek," diye meydan okuyorlar.'),
(2522, 21, 39, 'İnkar edenler, yüzlerinden ve arkalarından ateşi savamıyacakları ve yardım da görmeyecekleri anı bir bilselerdi.'),
(2523, 21, 40, 'Nitekim, onlara ansızın gelecek ve onları şaşkına çevirecektir. Ne onu geri çevirmeye güçleri yeter, ne de kendilerine süre verilir.'),
(2524, 21, 41, 'Senden önceki elçilerle de alay edildi. Ancak onlarla alay edenleri, eğlenceye aldıkları şey kuşatıverdi.'),
(2525, 21, 42, 'De ki, "Sizi Rahman\'dan başka kim gece ve gündüz koruyabilir?" Ama onlar, Rab\'lerinin mesajından tümüyle yüz çeviriyorlar.'),
(2526, 21, 43, 'Yoksa onları bize karşı savunacak tanrıları mı var? Halbuki onlar kendilerine bile yardım edemezler ve biz de sahip çıkmayız.'),
(2527, 21, 44, 'Halbuki biz onları ve atalarını yaşlanıncaya kadar nimetlendirdik. Yeryüzünün uçlarından habire eksilttiğimizi görmüyorlar mı? Buna rağmen onlar mı üstün gelecek?'),
(2528, 21, 45, '"Ben sizi ancak vahiy ile uyarıyorum," de. Ne var ki, sağırlar uyarıldıkları vakit çağrıyı işitmez.'),
(2529, 21, 46, 'Kendilerine, Rabbinin azabından bir esinti dokunsa, "Vay bize, biz gerçekten zalimlermişiz," derler.'),
(2530, 21, 47, 'Diriliş günü için adalet terazileri kurarız. Kimseye hiç bir haksızlık edilmez. Hardal tanesi kadar bir ağırlığı bile hesaba katacağız. Biz, hesapçı olarak yeteriz.'),
(2531, 21, 48, 'Musa\'ya ve Harun\'a Yasalar Kitabını, erdemliler için bir ışığı, bir mesajı verdik.'),
(2532, 21, 49, 'Onlar ki kimse kendilerini görmezken bile Rab\'lerini sayarlar ve Saatin dehşetini duyarlar.'),
(2533, 21, 50, 'Bu, da ona indirdiğimiz kutsal bir mesajdır. Siz şimdi ona karşı mı çıkıyorsunuz?'),
(2534, 21, 51, 'Biz daha önce de İbrahim\'e anlama ve kavrama yeteneğini bağışlamıştık. Biz onu çok iyi biliyorduk.'),
(2535, 21, 52, 'Babasına ve halkına, "Kendinizi adadığınız bu heykeller de neyin nesidir," dedi.'),
(2536, 21, 53, '"Atalarımızı onlara tapar bulduk," dediler.'),
(2537, 21, 54, '"Doğrusu, siz ve atalarınız apaçık bir sapıklık içine düşmüşsünüz," deyince,'),
(2538, 21, 55, '"Bize gerçeği mi getirdin, yoksa oyun mu oynuyorsun," dediler.'),
(2539, 21, 56, 'Dedi ki, "Aslında sizin Rabbiniz (Sahibiniz) göklerin ve yerin Rabbidir; onları ayırarak yaratmıştır. Ben buna tanıklık edenlerdenim."'),
(2540, 21, 57, '"ALLAH\'a and içerim ki, siz gider gitmez, ardınızdan heykellerinize karşı bir plan uygulayacağım."'),
(2541, 21, 58, 'Hepsini param parça etti; ancak belki ona danışırlar diye en büyüklerine dokunmadı.'),
(2542, 21, 59, '"Her kim Tanrılarımıza bunu yaptıysa gerçek bir zalimdir!," dediler.'),
(2543, 21, 60, '"Onları diline dolayan bir delikanlı işittik, kendisine İbrahim deniliyormuş," dediler.'),
(2544, 21, 61, '"Onu kamunun huzuruna çıkarın ki tanık olsunlar," dediler.'),
(2545, 21, 62, '"İbrahim, tanrılarımıza bunu sen mi yaptın," dediler.'),
(2546, 21, 63, '"Hayır, o işi işte şu büyükleri yaptı. Onlara sorun, eğer konuşurlarsa!," dedi.'),
(2547, 21, 64, 'Kendi vicdanlarına dönüp, kendi kendilerine şunu söylediler: "Gerçekten sizler haksızsınız."'),
(2548, 21, 65, 'Sonra tekrar eski kafalarına döndüler: "Bunların konuşamadığını sen gayet iyi bilirsin!"'),
(2549, 21, 66, '"ALLAH\'ın yanında Size hiç bir yararı ve zararı dokunmayan şeylere mi tapıyorsunuz," dedi.'),
(2550, 21, 67, '"Yuh size ve ALLAH\'ın yanında taptıklarınıza. Aklınızı kullanmaz mısınız?"'),
(2551, 21, 68, '"Bir şey yapacaksanız onu yakın da tanrılarınızı destekleyin," dediler.'),
(2552, 21, 69, '"Ey ateş, İbrahim\'e karşı serin ve güvenilir ol," dedik.'),
(2553, 21, 70, 'Böylece onun için bir plan uygulamak istediler de biz onları başarısızlığa mahkum ettik.'),
(2554, 21, 71, 'Onu ve Lut\'u, tüm insanlar için kutsal kıldığımız topraklara ulaştırıp kurtardık.'),
(2555, 21, 72, 'Ona ödül olarak İshak\'ı ve Yakub\'u verdik. Hepsini erdemli kıldık'),
(2556, 21, 73, 'Biz onları, emrimize göre yol gösteren önderler kıldık. Onlara iyi işlerin nasıl yapılacağını, namazın nasıl gözetileceğini ve zekatın nasıl verileceğini vahyettik. Onlar bize kulluk edenlerdi.'),
(2557, 21, 74, 'Lut\'a da bilgi ve bilgelik verdik. Onu, çirkin işler işleyen topluluktan kurtardık. Onlar, yoldan çıkmış kötü bir toplumdu'),
(2558, 21, 75, 'Onu merhametimizin kapsamına aldık, çünkü o erdemlilerden idi.'),
(2559, 21, 76, 'Daha önce Nuh da bizi çağırmıştı. Onu ve ailesini büyük sıkıntıdan kurtararak duasına cevap verdik.'),
(2560, 21, 77, 'Ayetlerimizi inkar eden toplumlara karşı onu destekledik. Onlar, kötü bir toplum olduklarından hepsini boğduk.'),
(2561, 21, 78, 'Davut ve Süleyman da... Bir defasında, halkın koyunlarının yayıldığı birilerinin ekini hakkında hüküm veriyorlardı. Biz onların kararına tanık olduk.'),
(2562, 21, 79, 'Süleyman\'a, doğru anlama yeteneği bağışladık. Herbirine bilgi ve bilgelik verdik. Davud\'un emrine dağları ve kuşları verdik. Biz bunları yapmıştık.'),
(2563, 21, 80, 'Ve sizi savaşlarınızda korusun diye ona zırh yapmayı öğrettik. Artık şükreder misiniz?'),
(2564, 21, 81, 'Süleyman\'a da, bereketli kıldığımız topraklara doğru esen boranın kumandasını verdik. Biz her şeyi iyi biliriz.'),
(2565, 21, 82, 'Ve onun için dalgıçlık yapan ve bunun yanında başka işler de gören şeytanları da... Onları biz gözetiyorduk.'),
(2566, 21, 83, 'Eyyub da... Rabbine şöyle yalvarmıştı: "Bana felaket dokundu. Sen merhametlilerin en merhametlisisin."'),
(2567, 21, 84, 'Biz ona cevap vererek ne sıkıntısı varsa onu giderdik. Katımızdan bir rahmet, kulluk edenlere bir hatırlatma olarak kendisine, ailesini ve onların bir mislini verdik.'),
(2568, 21, 85, 'Ayrıca İsmail, İdris ve ZülKifl de... Hepsi güçlüklere karşı dirençli kişilerdi.'),
(2569, 21, 86, 'Biz onları rahmetimiz kapsamına aldık; çünkü onlar erdemli kişilerdi.'),
(2570, 21, 87, 'ZanNun (yani isminde \'Nun\' harfi bulunan Yunus) da... Protesto ederek görevini terketmişti. Kendisini kontrol edemiyeceğimizi sandı. Sonunda, (balığın karnındaki) karanlıklar içinde, "Senden başka tanrı yok. Sen yücesin. Ben yanlış davrandım," diye yalvardı.'),
(2571, 21, 88, 'Yalvarışına karşılık verdik ve onu üzüntüden kurtardık. İnananları işte böyle kurtarırız.'),
(2572, 21, 89, 'Zekeriya da... Rabbine şöyle yalvarmıştı: "Rabbim, beni tek bırakma; sen kalıtçıların en iyisisin."'),
(2573, 21, 90, 'Duasını kabul ettik ve ona Yahya\'yı verdik. Kendisi için karısının durumunu düzelttik. Çünkü onlar iyi işlerde yarışıyorlar ve bize hem umutluyken ve hem de korku içindeyken yalvarıyorlardı. Onlar bize saygı duyanlardı.'),
(2574, 21, 91, 'Ve ırzını koruyan kadın da... Nitekim ona ruhumuzdan üflemiştik. Onu ve oğlunu tüm dünyaya bir işaret yaptık.'),
(2575, 21, 92, 'İşte sizin topluluğunuz böyle bir (elçiler) topluluğudur. Ben sizin Rabbinizim; sadece bana kulluk edin.'),
(2576, 21, 93, 'Fakat onlar işlerinde ayrılığa düştüler; hepsi bize döneceklerdir.'),
(2577, 21, 94, 'Kim inançlı olarak erdemli işler yaparsa onun bu çabası boşa gitmeyecektir; biz sürekli olarak kaydetmekteyiz.'),
(2578, 21, 95, 'Helak ettiğimiz bir toplumun tekrar dönmesi yasaktır.'),
(2579, 21, 96, 'Nihayet, Yecuc ve Mecuc\'un önü açıldığı zaman, onlar her yönden saldırırlar.'),
(2580, 21, 97, 'Hak sözün gerçekleşmesi yaklaşmış ve kafirlerin gözleri korkudan dona kalmıştır: "Vah bize, Biz bundan gaflet içinde idik. Biz gerçekten zalimler olduk."'),
(2581, 21, 98, 'Siz ve ALLAH\'ın yanında taptıklarınız cehennemin yakıtısınız; sizler oraya girmeye layıksınız.'),
(2582, 21, 99, 'Onlar tanrılar olsaydı oraya girmeyeceklerdi. Oysa hepsi orada ebedi kalıcıdırlar.'),
(2583, 21, 100, 'Onlar için orada iç çekip inlemek vardır; hiç bir şey de işitemezler.'),
(2584, 21, 101, 'Ancak kendilerine mutlu bir son belirlediklerimiz hariç, onlar ondan uzaklaştırılacaklardır.'),
(2585, 21, 102, 'Onun uğultusunu işitmezler. Canlarının istediği şeyler içinde ebedi kalırlar.'),
(2586, 21, 103, 'O en büyük korku onları üzmez. Kendilerini melekler, "İşte bu, size söz verilen gününüzdür!," diye karşılar.'),
(2587, 21, 104, 'O gün göğü dosyaları dürer gibi katlar ve yaratılışın ilk durumunu nasıl başlatmışsak ona çeviririz.'),
(2588, 21, 105, 'Zikir\'den sonra Zebur\'da da, "Yeryüzüne benim erdemli kullarım varis olacak," diye yazıp belirtmiştik.'),
(2589, 21, 106, 'Kulluk eden bir toplum için bunda bir bildiri vardır.'),
(2590, 21, 107, 'Biz seni tüm halklara bir rahmet olarak gönderdik'),
(2591, 21, 108, 'De ki, "Sizin tanrınızın bir tek tanrı olduğu bana vahyediliyor. Artık teslim olacak mısınız?"'),
(2592, 21, 109, 'Eğer yüz çevirirlerse de ki, "Size yeterli ölçüde bildirdim. Size söz verilen şeyin yakın mı, yoksa uzak mı olduğunu bilmem."'),
(2593, 21, 110, '"O, açıklanan sözü de bilir, gizlediklerinizi de bilir."'),
(2594, 21, 111, '"Hiç bilmiyorum; belki sizin için bir test ve belli bir süreye kadar bir hoşlanma vesilesi olur."'),
(2595, 21, 112, 'De ki, "Rabbim, hükmünü gerçekleştir. Sizin yakıştırdıklarınıza karşı sadece Rahman olan Rabbimizden yardım istenir."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'Ey halk, Rabbinize saygı gösteriniz, çünkü Saatin (dünyanın sonunun) depremi korkunç bir şeydir.'),
(2597, 22, 2, 'Ona tanık olduğunuz gün, her emziren ana emzirdiğini atar, her gebe kadın yükünü bırakır. Sarhoş olmadıkları halde insanları sarhoş gibi göreceksin. Halbuki ALLAH\'ın cezası şiddetlidir.'),
(2598, 22, 3, 'İnsanlardan öyleleri var ki ALLAH hakkında bilgisizce tartışır ve her azılı şeytana uyar.'),
(2599, 22, 4, '"Bu, kendisini dost edinen kimseyi saptırır ve alevli azaba yol gösterir," diye hakkında hüküm verilmiştir.'),
(2600, 22, 5, 'Ey insanlar, diriliş konusunda kuşku besliyorsanız, (hatırlayın ki) sizi topraktan, sonra bir damlacıktan, sonra asılı duran bir madde (embriyo) dan, sonra biçimi belli ve belirsiz bir dölütten yarattık. Böylece size bildiriyoruz. Neyi dilemişsek belli bir süreye kadar onu rahimlerde tutarız. Sonra sizi bir bebek olarak çıkarırız, ve ardından olgunlaşıp erginleşirsiniz. Kiminizin hayatına son verilir, kiminiz de en kötü yaşa kadar ulaştırılır. Böylece bir bilgiye sahip olduktan sonra bir şey bilemez olsun. Toprağı kuru ve ölü görürsün, ancak üzerine su yağdırdığımız zaman titreşip kabarır ve çeşit çeşit güzel bitkiler bitirir.'),
(2601, 22, 6, 'Çünkü, ALLAH tek gerçektir; ölüleri diriltir; herşeye gücü yeter;'),
(2602, 22, 7, 'Ve dünyanın sonu hiç bir kuşkuya yer bırakmadan gelmekte ve ALLAH mezarlardakileri diriltecektir.'),
(2603, 22, 8, 'İnsanlardan kimi, bir bilgiye, bir yol göstericiye ve aydınlatıcı bir kitaba sahip olmadan ALLAH hakkında tartışır.'),
(2604, 22, 9, 'ALLAH\'ın yolundan saptırmak için kibirle kasılarak gayret eder. Bu dünya hayatında onun için rezillik vardır. Diriliş gününde de ona yakıcı azabı tattırırız.'),
(2605, 22, 10, '"Bu, ellerinin senin için yapıp öne sürdüğü işlerden ötürüdür." ALLAH kullara zulmedici değildir'),
(2606, 22, 11, 'İnsanlardan öyleleri var ki ALLAH\'a koşullu olarak kulluk eder. İşleri yolunda gidince sevinir; ancak başına bir bela gelince yüzünü çevirir. Böylece dünyayı da ahireti de kaybeder. Gerçek kayıp işte budur.'),
(2607, 22, 12, 'ALLAH\'tan ayrı olarak, kendisine yarar ve zarar veremiyenlere yalvarır. Sapıklığın en aşırı noktası budur.'),
(2608, 22, 13, 'Zararı yararından daha yakın olana yalvarır. Ne kötü bir mevla ve ne kötü bir arkadaştır.'),
(2609, 22, 14, 'ALLAH inanıp erdemli davrananları, içinde ırmaklar akan cennetlere sokar. ALLAH istediğini yapar.'),
(2610, 22, 15, 'Kim ALLAH\'ın dünya ve ahirette kendisine yardım edemiyeceğini sanıyorsa göğe (Tanrı\'ya) doğru yönelsin ve sonra (umut bağladığı diğerlerinden) umudunu kessin de uyguladığı bu planın, kendisinin canını sıkan her şeyden kurtarıp kurtarmadığına bir baksın.'),
(2611, 22, 16, 'Böylece, biz onu apaçık ayetler olarak indirdik. ALLAH dilediğini ve dileyeni doğruya ulaştırır.'),
(2612, 22, 17, 'İnananlar, yahudiler, din değiştirenler, hıristiyanlar, zerdüştiler ve ortak koşanlar... ALLAH diriliş günü aralarında hüküm verecektir. ALLAH her şeye tanıktır.'),
(2613, 22, 18, 'Göklerde ve yerde bulunan kimselerin, güneşin, ayın, yıldızların, dağların, ağaçların, hayvanların ve insanlardan bir çok kişinin ALLAH\'a secde ettiklerini (boyun eğdiklerini) görmez misin? Bir çok kişi de azabı hakketmiştir. ALLAH\'ın alçalttığını hiç kimse onurlu kılamaz. ALLAH dilediğini yapar.'),
(2614, 22, 19, 'İşte şunlar, Rab\'leri hakkında tartışan iki karşıt grubtur. İnkarcı olanlar için ateşten elbiseler biçilir ve başlarından aşağı kaynar su dökülür.'),
(2615, 22, 20, 'Onunla karınlarında ne varsa, derileriyle birlikte eritilir.'),
(2616, 22, 21, 'Onlar için demirden topuzlar vardır.'),
(2617, 22, 22, 'Her ne vakit oradaki sıkıntıdan çıkmak isteseler oraya geri çevrilirler: "Yakıcı azabı tadın."'),
(2618, 22, 23, 'ALLAH inanıp erdemli bir hayat sürenleri içlerinden ırmaklar akan cennetlere (bahçelere) sokar. Orada altın bilezikler ve inciler takınırlar. Orada giysileri de ipektir.'),
(2619, 22, 24, 'Onlar sözün güzeline iletilmişlerdir, onlar En Çok Övülen\'in yoluna iletilmişlerdir.'),
(2620, 22, 25, 'İnkar edenler, yerli ve dışarıdan gelen tüm insanlar için eşit olarak ayırdığımız Kutsal Mescid\'den ve ALLAH\'ın yolundan geri çevirenler ve orada zulüm ve sapıklık arayanlar, tarafımızdan acı bir azap tadacaklardır.'),
(2621, 22, 26, 'İbrahim\'i Evin (Kabe\'nin) mekanına yerleştirmiştik: "Bana hiç bir şeyi ortak etme. Evimi de, ziyaretçiler, orada yerleşenler, rukü ve secde edenler için temizle."'),
(2622, 22, 27, '"İnsanlara Hac ziyaretini ilan et. Sana yaya olarak veya çeşitli taşıt araçlarıyla uzaklardan gelsinler."'),
(2623, 22, 28, 'Ki kendileri için bir takım (sosyal, politik, ekonomik) yararlara tanık olsunlar ve kendilerine çiftlik hayvanlarını rızık olarak verdiği için ALLAH\'ın ismini bilinen günlerde ansınlar. "Onlardan yeyin ve sıkıntı içindeki yoksullara da yedirin."'),
(2624, 22, 29, 'Sonra yükümlülüklerini tamamlasınlar, vermiş oldukları sözlerini yerine getirsinler ve o Tarihi Evi ziyaret etsinler.'),
(2625, 22, 30, 'İşte böyle. Kim ALLAH\'ın yasaklarına saygı gösterirse Rabbinin yanında kendisi için daha iyidir. Size özellikle bildirilenlerin haricindeki tüm çiftlik hayvanları helal (yasal) kılınmıştır. O halde putperestliğin felaketinden kaçının, yalan sözden sakının.'),
(2626, 22, 31, 'ALLAH\'a ortak koşmayarak, kendinizi sadece O\'na adayın. Kim ALLAH\'a ortak koşarsa sanki gökten düşmüş ve kendisini akbaba kapmış veya rüzgar tarafından derin bir fiyortta sürükleniyor gibidir.'),
(2627, 22, 32, 'Kimler ALLAH\'ın emirlerine saygı gösterirse kalplerindeki erdemi sergilemiştir.'),
(2628, 22, 33, 'Onları o Tarihi Eve bağışlanmadan önce o (hayvan) lardan belli bir süre yararlanırsınız.'),
(2629, 22, 34, 'Kendilerine çiftlik hayvanlarını rızık olarak vermesinden ötürü ALLAH\'ın ismini anmaları için, her topluluğa ayrı bir ibadet biçimi belirledik. Tanrınız bir tek tanrıdır. Sadece O\'na teslim olun. İtaat edenlere müjde ver.'),
(2630, 22, 35, 'Onlar öyle kimselerdir ki, ALLAH\'tan söz edildiğinde yürekleri ürperir. Başlarına gelene sabrederler, namazı gözetirler ve kendilerine verdiğimiz rızıktan yardım için harcarlar.'),
(2631, 22, 36, 'ALLAH\'a kulluğun bir işareti olarak, hayvanların kurban edilmesinde sizin için yararlar mevcuttur. Onlar sırada diziliyken üzerlerinde ALLAH\'ın ismini anmalısınız. Yanları üzerine düştükleri zaman da onlardan yeyin ve dilenene de dilenmeyen yoksula da yedirin. Şükredesiniz diye onları size amade kıldık.'),
(2632, 22, 37, 'ALLAH\'a ne onların etleri, ne de kanları ulaşır; O\'na ancak sizin erdemli davranışınız ulaşır. Onları böylece sizin hizmetinize vermiştir ki, sizi doğruya ulaştırdığı için ALLAH\'ın büyüklüğünü anasınız.'),
(2633, 22, 38, 'ALLAH inananları savunur. ALLAH hainleri ve nankörleri sevmez.'),
(2634, 22, 39, 'Zulmedilerek kendilerine savaş açılanlara izin verilmiştir. ALLAH onları desteklemeye elbette kadirdir.'),
(2635, 22, 40, 'Onlar ki yalnızca, "Rabbimiz ALLAH\'tır," dedikleri için haksız yere ülkelerinden çıkarıldılar. ALLAH halkın bir kısmını bir kısmına karşı savunmasaydı içlerinde ALLAH\'ın isminin çokça anıldığı manastırlar, kiliseler, sinagoglar ve mescidler yıkılırdı. ALLAH kendisine yardım edenlere elbette yardım edecektir. ALLAH Kuvvetlidir, Güçlüdür,'),
(2636, 22, 41, 'Onlar ki kendilerini yeryüzüne yöneticiler kıldığımız zaman namazı gözetir, zekatı verir, iyiliği emreder ve kötülükten menederler. Son karar ALLAH\'a aittir.'),
(2637, 22, 42, 'Seni yalanlarlarsa, onlardan önce Nuh, Ad ve Semud halkları da yalanlamışlardı'),
(2638, 22, 43, 'İbrahim\'in halkı, Lut\'un halkı da...'),
(2639, 22, 44, 'Medyen yerlileri de... Musa dahi yalanlandı. Ben o kafirlere süre tanıdım ve sonra onları hesaba çektim; beni tanımamak da nasılmış!..'),
(2640, 22, 45, 'Nice kentlerin halkını, işledikleri haksızlıklardan ötürü helak ettik. Onlardan arta kalanlar, çökmüş çatılar, terkedilmiş kuyular ve ıssız saraylardı...'),
(2641, 22, 46, 'Düşünen beyinlerle ve işiten kulaklarla yeryüzünü dolaşmadılar mı? Gerçek körlük, gözlerin körlüğü değil; göğüslerdeki gönüllerin körlüğüdür.'),
(2642, 22, 47, 'O azabı çabuk getirmen için sana meydan okuyorlar. ALLAH sözünü bozmaz. Rabbinin katında bir gün, onların hesabıyla bin sene gibidir.'),
(2643, 22, 48, 'Nice kentlerin halkına, zalim oldukları halde süre tanıdım ve sonra onları hesaba çektim; son dönüş Banadır.'),
(2644, 22, 49, 'De ki: "Ey insanlar, ben sizin için ancak apaçık bir uyarıcıyım."'),
(2645, 22, 50, 'İnanıp erdemli işler yapanlar, bağışlanma ve bol nimet hakketmişlerdir.'),
(2646, 22, 51, 'Ayetlerimize karşı mücadele edenler ise cehennemin halkı olmayı hakketmişlerdir.'),
(2647, 22, 52, 'Senden önce, arzularına şeytanın karışmadığı hiç bir elçi ve hiç bir peygamberi göndermedik. ALLAH şeytanın attığı şeyleri ortadan kaldırır ve sonra ALLAH ayetlerini sağlamlaştırır. ALLAH Bilendir, Bilgedir.'),
(2648, 22, 53, 'Böylece şeytanın attığını kalplerinde hastalık bulunanlar ve kalpleri katı olanlar için bir test haline sokar. Zalimler, elbette apaçık bir karşıtlık içindedirler.'),
(2649, 22, 54, 'Bu durum, aynı zamanda, kendilerine bilgi verilmiş olanların bunun Rabbinden gelen bir gerçek olduğunu anlamalarını sağlar. Böylece kalpleri onu benimser ve iman ederler. ALLAH, elbette, inananları doğru yola ulaştırır.'),
(2650, 22, 55, 'İnkarcılar ise, Saat (dünyanın sonu) kendilerine ansızın gelinceye kadar, yahut o müthiş günün cezası kendilerine çatıncaya kadar sürekli ondan kuşku duyacaklardır'),
(2651, 22, 56, 'Yönetim ve otorite, o gün tümüyle ALLAH\'a aittir. Onların arasında hüküm verecektir. İnanıp erdemli davrananlar nimet cennetlerindedir.'),
(2652, 22, 57, 'Ayetlerimizi yalanlayıp inkar edenler ise küçük düşürücü bir cezayı hakkederler.'),
(2653, 22, 58, 'ALLAH yolunda göç ettikten sonra ölenler veya öldürülenler, ALLAH tarafından güzel bir rızık ile besleneceklerdir. Kuşkusuz ALLAH rızık verenlerin en iyisidir.'),
(2654, 22, 59, 'Hoşnut olacakları bir kabul ile katına alacaktır onları. ALLAH Bilendir, Şefkatlidir.'),
(2655, 22, 60, 'Bu böyledir. Kim, kendisine uygulanan haksızlığa karşı aynı şekilde karşılık verir de bundan dolayı da kendisine saldırılırsa ALLAH ona yardım edecektir. Elbette ALLAH Affedendir, Bağışlayandır.'),
(2656, 22, 61, 'Bu gerçekleşecektir. Çünkü ALLAH geceyi gündüze sokar, gündüzü geceye sokar ve ALLAH İşitendir, Bilendir.'),
(2657, 22, 62, 'Bu gerçekleşecektir. Çünkü ALLAH Gerçektir, O\'nun dışında yalvardıkları ise asılsızdır ve ALLAH Yücedir, Büyüktür.'),
(2658, 22, 63, 'ALLAH\'ın gökten bir su indirdiğini ve yeryüzünün yeşerdiğini görmez misin? ALLAH Şefkatlidir, Haberdardır.'),
(2659, 22, 64, 'Göklerde ve yerde ne varsa O\'na aittir. Elbette ALLAH Zengindir, En Çok Övülendir.'),
(2660, 22, 65, 'ALLAH\'ın yerdeki şeyleri ve emriyle denizde akıp giden gemileri hizmetinize sunduğunu ve göğü, izni olmadan yerin üzerine düşmekten alıkoyduğunu görmez misin? ALLAH insanlara karşı Şefkatlidir, Rahimdir.'),
(2661, 22, 66, 'O\'dur size can veren, sonra sizi öldüren ve sonra sizi dirilten... İnsan elbette pek nankördür.'),
(2662, 22, 67, 'Her bir topluluğu, uygulayacakları bir dizi kural ile yükümlü kıldık. Onun için bu konuda seninle çekişmesinler. Sen Rabbine çağır. Kuşkusuz sen doğru bir yol üzerindesin.'),
(2663, 22, 68, 'Seninle çekişirlerse de ki, "ALLAH yaptığınız her şeyi en iyi bilendir."'),
(2664, 22, 69, 'Tartıştığınız konularda, diriliş günü aranızda ALLAH hüküm verecektir.'),
(2665, 22, 70, 'ALLAH\'ın gökte ve yerde olan her şeyi bildiğini bilmez misin? Tüm bunlar bir kitapta kayıtlıdır. Elbette bu ALLAH\'a kolaydır.'),
(2666, 22, 71, 'ALLAH\'ın yanında öylelerine tapıyorlar ki, kendilerine hiç bir güç verilmemiştir ve haklarında bir bilgiye de sahip değillerdir. Bu zalimlerin hiç bir yardımcısı yoktur.'),
(2667, 22, 72, 'Kendilerine apaçık ayetlerimiz okunduğunda, inkarcıların yüzünde inkarcılığı okursun. Kendilerine ayetlerimizi okuyanların üzerine neredeyse saldıracaklardır. De ki, "Bundan daha kötüsünü size bildireyim mi? Ateş! ALLAH onu kafirlere vadetmiştir. Ne kötü bir sonuçtur!"'),
(2668, 22, 73, 'İnsanlar, işte size, dikkatle dinleyeceğiniz bir örnek: ALLAH\'ın yanında taptıklarınız, toplansalar dahi bir sinek bile yaratamazlar. Hatta sinek onlardan bir şey kapsa onu ondan geri alamazlar. İsteyen de aciz, istenen de...'),
(2669, 22, 74, 'Onlar ALLAH\'a gereken değeri vermediler. ALLAH elbette Güçlüdür, Üstündür.'),
(2670, 22, 75, 'ALLAH meleklerden ve insanlardan elçiler seçer. Elbette ALLAH İşitendir, Görendir.'),
(2671, 22, 76, 'Onların geçmişini ve geleceğini bilir. Her şeyin nihai kontrol ve yönetimi ALLAH\'a aittir.'),
(2672, 22, 77, 'İnananlar, eğiliniz, secde ediniz, Rabbinize kulluk ediniz ve iyilik işleyiniz ki başarasınız.'),
(2673, 22, 78, 'Ve ALLAH uğrunda gereken çaba ve gayreti gösteriniz. O\'dur sizi seçen. O, babanız İbrahim\'in yolu olan bu dini, sizin için güç ve ağır kılmadı. Elçinin size tanık olması, sizin de halka tanık olmanız için, sizi, daha önce de şimdi de "müslümanlar = teslim olanlar" olarak adlandıran O\'dur. Namazı gözetin, zekatı verin ve ALLAH\'a sarılın; Mevlanız (Sahibiniz) O\'dur. Ne güzel sahip ve ne güzel Yardımcıdır!');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'İnananlar başarıya ulaşmışlardır.'),
(2675, 23, 2, 'Nitekim onlar namazlarında saygılıdırlar.'),
(2676, 23, 3, 'Boş sözlerden yüz çevirirler.'),
(2677, 23, 4, 'Zekatı pratiğe geçirirler.'),
(2678, 23, 5, 'Ve cinsel ilişkilerden sakınırlar;'),
(2679, 23, 6, 'Ancak eşleri veya sahip oldukları hariç. Onlar kınanmazlar.'),
(2680, 23, 7, 'Kim bunun ötesini ararsa sınırı aşmış olur.'),
(2681, 23, 8, 'Onlar ki kendilerine emanet edilen şeylere dikkat ederler. Verdikleri sözleri de yerine getirirler.'),
(2682, 23, 9, 'Onlar ki namazlarını düzenli olarak gözetirler.'),
(2683, 23, 10, 'İşte varis olacaklar onlardır.'),
(2684, 23, 11, 'Cennete mirasçı olacak ve orada ebedi kalacaklar.'),
(2685, 23, 12, 'Biz, insanı özel bir balçıktan yarattık'),
(2686, 23, 13, 'Sonra onu sağlam bir bekleme yerinde bir damlacık haline getirdik.'),
(2687, 23, 14, 'Sonra, o damlacığı, embriyo olarak biçimlendirdik, sonra embriyoyu dölüt olarak biçimlendirdik, sonra dölütü kemik olarak biçimlendirdik, sonra kemiğe et giydirdik ve sonra onu yeni bir yaratık haline soktuk. Biçim verenlerin en güzeli ALLAH çok yücedir.'),
(2688, 23, 15, 'Sonra siz, bunun ardından öleceksiniz.'),
(2689, 23, 16, 'Sonra siz, kıyamet (ayağa kalkış) günü diriltileceksiniz.'),
(2690, 23, 17, 'Üstünüzde yedi yol yarattık ve biz asla yaratıklardan habersiz olmadık.'),
(2691, 23, 18, 'Gökten belli bir oranda su indirdik ve onu toprakta depoladık. Kuşkusuz onu gidermeye de gücümüz yeter.'),
(2692, 23, 19, 'Onunla, sizin için bir çok meyvelar içeren hurma ve üzüm bahçeleri yaptık; onlardan yiyip duruyorsunuz.'),
(2693, 23, 20, 'Sina Dağı civarında yetişen ve yiyenler için yağ ve lezzet üreten bir ağaç...'),
(2694, 23, 21, 'Çiftlik hayvanlarında sizin için bir ders vardır. Karınlarındaki maddeden sizi içiriyoruz, onlarda sizin için bir çok yararlar mevcuttur; hatta onlardan yiyorsunuz.'),
(2695, 23, 22, 'Onların üzerinde ve gemiler üzerinde taşınıyorsunuz.'),
(2696, 23, 23, 'Nuh\'u halkına göndermiştik de, "Halkım, ALLAH\'a kulluk ediniz. O\'ndan başka bir tanrınız yoktur. Saygı duyup erdemli davranmaz mısınız," demişti.'),
(2697, 23, 24, 'Halkının ileri gelen inkarcıları, "Bu, sizin gibi bir insandan başka bir şey değildir. Üzerinize egemen olmak istiyor. ALLAH dileseydi bir melek indirirdi. İlk atalarımızdan böyle bir şey işitmedik.'),
(2698, 23, 25, 'O, sadece deli bir adamdır. Hele bir süreye kadar onu gözleyin.'),
(2699, 23, 26, 'Dedi ki, "Rabbim, beni yalanlamalarına karşılık bana yardım et.'),
(2700, 23, 27, 'Bunun üzerine kendisine şöyle vahyettik: "Gözümüzün önünde ve vahyimize uygun olarak gemiyi yap. Emrimiz gelince, kaynaklar kaynayıp taşınca her çeşit (evcil hayvanı) ve aleyhlerine hüküm verilmiş olanların dışındaki aileni al. Zalimler adına benimle konuşma; onlar boğulacaklardır."'),
(2701, 23, 28, '"Sen ve beraberindekiler gemiye yerleştiğinizde, \'Bizi o zalim halktan kurtaran ALLAH\'a övgüler olsun,\' de."'),
(2702, 23, 29, '"Ve, \'Rabbim, beni kutlu bir yere indir. Sen indirenlerin en iyisisin,\' de."'),
(2703, 23, 30, 'Bunda işaretler ve dersler vardır. Biz elbette sizleri denemekteyiz.'),
(2704, 23, 31, 'Sonra, onların ardından, başka bir soy yetiştirdik.'),
(2705, 23, 32, 'Onlara, aralarından bir elçi gönderdik: "ALLAH\'a kulluk ediniz, sizin ondan başka bir tanrınız yoktur. Saygı gösterip erdemli davranmıyacak mısınız?"'),
(2706, 23, 33, 'Dünya hayatında kendilerine alabildiğine nimetler bağışlamamıza rağmen, ahiret karşılaşmasını yalanlayıp inkar eden, halkının ileri gelenleri şöyle dediler: "Bu, yalnızca sizin gibi bir insandır. Sizin yediğinizden yiyor, sizin içtiğinizden içiyor."'),
(2707, 23, 34, '"Sizin gibi bir insana uyarsanız, siz o zaman gerçekten kaybedersiniz."'),
(2708, 23, 35, '"Siz öldükten, toprak ve kemik haline dönüştükten sonra, sizin geri çıkacağınızı mı söz veriyor?"'),
(2709, 23, 36, '"Size söz verilen bu şey imkansızdır, imkansız!"'),
(2710, 23, 37, '"Yaşantımız sadece bu dünyadadır. Yaşarız, ölürüz. Asla dirilecek değiliz."'),
(2711, 23, 38, '"O, ALLAH\'a yalan yakıştıran bir adamdan başkası değildir. Biz ona inanacak değiliz."'),
(2712, 23, 39, 'Dedi ki, "Rabbim, yalanlamalarına karşılık bana yardım et."'),
(2713, 23, 40, 'Dedi ki, "Az sonra onlar pişman olacaklardır."'),
(2714, 23, 41, 'Korkunç felaket onları eşitçe yakaladı ve böylece onları süprüntü yığınına çevirdik. O zalim halk yok olmayı hakketmişti.'),
(2715, 23, 42, 'Sonra, onların da ardından başka soylar yetiştirdik.'),
(2716, 23, 43, 'Hiç bir toplum kendisi için belirlenmiş süreyi çabuklaştıramaz, geciktiremez.'),
(2717, 23, 44, 'Sonra biz, elçilerimizi ardı ardına gönderdik. Her ne zaman bir elçi toplumuna gittiyse onu yalanladılar. Biz de onları bir biri peşinden sürüp tarihe gömdük. İnanmayan bir topluluk yok olmayı hakketmiştir.'),
(2718, 23, 45, 'Sonra biz, Musa\'yı ve kardeşi Harun\'u ayetlerimizle ve apaçık bir delille gönderdik.'),
(2719, 23, 46, 'Firavun ve ileri gelen takımına... Ancak onlar büyüklendiler. Onlar küstah bir topluluk olmuştu.'),
(2720, 23, 47, '"O ikisinin halkı bize kölelik ederken şimdi biz tutup bizim gibi iki insana mı inanalım," dediler.'),
(2721, 23, 48, 'İkisini yalanladılar ve sonuç olarak yok edilenlerden oldular.'),
(2722, 23, 49, 'Doğruyu bulurlar diye Musa\'ya Kitabı vermiştik.'),
(2723, 23, 50, 'Meryem oğlunu ve annesini bir işaret kıldık, ve onları yerleşmeye elverişli ve pınarı olan bir tepede barındırdık.'),
(2724, 23, 51, 'Elçiler, iyi nimetlerden yiyiniz ve erdemli işler yapınız. Ben sizin yaptıklarınızı çok iyi bilirim.'),
(2725, 23, 52, 'Sizin bu toplumunuz bir tek toplumdur. Ben sizin Rabbinizim beni sayın.'),
(2726, 23, 53, 'Fakat, onlar işlerini çeşitli kitaplara ayırdılar. Her grup kendi yanında bulunandan hoşnut...'),
(2727, 23, 54, 'Belli bir süreye kadar onları şaşkınlıkları içinde bırak.'),
(2728, 23, 55, 'Sanıyorlar mı ki, kendilerine bağışladığımız paralar ve çocuklar ile,'),
(2729, 23, 56, 'Onların iyiliğine koşuyoruz? Hayır, farkında değiller.'),
(2730, 23, 57, 'Rab\'lerine olan saygıdan ötürü alabildiğine dikkatli olanlar,'),
(2731, 23, 58, 'Rab\'lerininin ayetlerine inananlar,'),
(2732, 23, 59, 'Rab\'lerine ortak koşmayanlar,'),
(2733, 23, 60, 'Rab\'lerine döneceklerinin bilincinde olarak verenler,'),
(2734, 23, 61, 'İşte onlar, iyiliklerde yarışanlardır; ve onlar iyilik yapmakta öncüdürler.'),
(2735, 23, 62, 'Bir kişiye ancak kapasitesi kadar yükleriz. Katımızda gerçeği konuşan bir kitap vardır. Hiç kimse haksızlığa uğratılmayacaktır.'),
(2736, 23, 63, 'Zihinleri bundan (mesajdan) gafil olup buna aykırı işlerde çalışıp durmaktadırlar.'),
(2737, 23, 64, 'Varlıklılarını cezaya çarptığımızda, yakınmaya başlarlar.'),
(2738, 23, 65, 'Yakınmayın; bu gün tarafımızdan hiç bir yardım görmezsiniz.'),
(2739, 23, 66, 'Size ayetlerim okunuyordu da ardınıza dönüyordunuz.'),
(2740, 23, 67, 'Ona karşı büyüklük taslıyordunuz, saçmalayarak geceliyordunuz.'),
(2741, 23, 68, 'Onlar bu sözü incelemediler mi, yoksa geçmiş atalarına gelmeyen bir şey mi kendilerine geldi?'),
(2742, 23, 69, 'Yoksa, kendilerine gönderilen elçiyi tanımadıkları için mi onu inkar ediyorlar?'),
(2743, 23, 70, 'Yoksa, onun deli olduğuna mı karar verdiler? Halbuki onlara gerçeği getirmişti. Ne var ki onların çoğu gerçekten hoşlanmaz.'),
(2744, 23, 71, 'Gerçek onların arzularına uysaydı, gökler, yer ve içlerindekiler kaosa girerdi. Halbuki onlara mesajlarını verdik, ancak çokları mesajlarından yüz çevirmekte.'),
(2745, 23, 72, 'Yoksa sen onlardan bir maaş mı istedin? Rabbinin maaşı çok daha iyidir. O, rızık verenlerin en iyisidir.'),
(2746, 23, 73, 'Kuşku yok ki sen onları dosdoğru yola çağırıyorsun.'),
(2747, 23, 74, 'Ama ahirete inanmıyanlar yoldan sapmaktadırlar.'),
(2748, 23, 75, 'Kendilerine acıyıp başlarına gelen perişanlıklarını giderseydik bile, azgınlıklarına dalıp bocalayacaklardı.'),
(2749, 23, 76, 'Onları cezaya çarptırmamıza rağmen Rab\'lerine boyun eğmediler, yalvarmadılar.'),
(2750, 23, 77, 'Kendilerine çetin bir azabın kapısını açtığımız zaman şaşırıp şoke oldular.'),
(2751, 23, 78, 'O\'dur sizin için işitme, görme duyularını ve beyinler yaratan. Ne kadar da az şükrediyorsunuz!'),
(2752, 23, 79, 'O\'dur sizi yeryüzüne yerleştiren; O\'nun huzurunda toplanacaksınız.'),
(2753, 23, 80, 'O\'dur yaşatan ve öldüren; gecenin ve gündüzün değişmesi O\'na bağlı. Aklınızı kullanmaz mısınız?'),
(2754, 23, 81, 'Ancak onlar, öncekilerin dediklerini tekrarladılar.'),
(2755, 23, 82, 'Ve şöyle dediler: "Biz öldüğümüz, toprak ve kemik olduğumuz zaman mı dirileceğiz?"'),
(2756, 23, 83, '"Bize ve atalarımıza daha önce aynı şey söz verilmişti. Bu, geçmişlerin efsanesinden başka bir şey değildir."'),
(2757, 23, 84, 'De ki, "Biliyorsanız, yer, gökler ve içlerinde bulunanlar kimindir?"'),
(2758, 23, 85, '"ALLAH\'ın," diyecekler. De ki, "Düşünmez misiniz?"'),
(2759, 23, 86, 'De ki, "Yedi göğün Rabbi, büyük yönetimin Rabbi kimdir?"'),
(2760, 23, 87, '"ALLAH.," diyecekler. De ki, "Öyleyse neden erdemli davranmıyorsunuz?"'),
(2761, 23, 88, 'De ki, "Biliyorsanız, koruyup kollayan, fakat kendisi korunup kollanmayan; her şeyin egemenliğini elinde bulunduran kimdir?"'),
(2762, 23, 89, '"ALLAH.," diyeceklerdir. De ki, "O halde nasıl da aldanıyorsunuz?"'),
(2763, 23, 90, 'Kendilerine gerçeği getirmemize rağmen onlar yalanlamaktadırlar.'),
(2764, 23, 91, 'ALLAH çocuk edinmemiştir, O\'nunla beraber bir tanrı da yoktur. Aksi taktirde her tanrı yarattığı şeylerle birlikte bağımsızlığını ilan ederek yönetim için bir biriyle çekişmeye girerdi. ALLAH, onların niteledikleri şeylerden çok uzaktır.'),
(2765, 23, 92, 'Tüm sırları ve tanık olunanları Bilendir; onların ortak koştukları şeylerden yücedir.'),
(2766, 23, 93, 'De ki, "Rabbim, onlara verilen sözü bana gösterirsen,"'),
(2767, 23, 94, '"Rabbim, beni o zalim toplum içinde bırakma."'),
(2768, 23, 95, 'Biz elbette, kendilerine söz verileni sana gösterebiliriz.'),
(2769, 23, 96, 'Kötülüğe iyilikle karşılık ver. Biz onların iddialarını iyi biliriz.'),
(2770, 23, 97, 'Şeytandan Korunmak İçin'),
(2771, 23, 98, 'Ve de ki, "Rabbim, şeytanların fısıltılarından sana sığınırım."'),
(2772, 23, 99, 'Onlardan birine ölüm gelip çattığı zaman şöyle der, "Rabbim, beni geri döndürünüz."'),
(2773, 23, 100, '"Ki terketmiş bulunduğum şeylerde erdemli işler yapayım." Hayır. Bu onun söylediği bir laftan ibarettir. Diriliş gününe kadar onların ardında bir engel vardır.'),
(2774, 23, 101, 'Boruya üfürüldüğünde o gün artık aralarında akrabalık yoktur ve birbirleriyle de ilgilenemezler.'),
(2775, 23, 102, 'Tartıları ağır gelenler, işte onlar kazanacaklardır.'),
(2776, 23, 103, 'Tartıları hafif gelenler ise, kendilerini zarara soktukları için cehennemde ebedi kalacak olanlardır.'),
(2777, 23, 104, 'Onlar orada perişan durumda iken, ateş de yüzlerini yalayacaktır.'),
(2778, 23, 105, 'Ayetlerim size okunmuyor muydu ve siz de onları yalanlamıyor muydunuz?'),
(2779, 23, 106, '"Rabbimiz," diyecekler, "Bizi talihsizliğimiz yendi; biz sapıtan bir toplum olduk."'),
(2780, 23, 107, '"Rabbimiz, bizi buradan çıkar. Eğer (eski durumumuza) dönersek artık biz gerçekten zalimleriz."'),
(2781, 23, 108, 'Diyecek ki, "Sinin orada, benimle konuşmayın."'),
(2782, 23, 109, '"Kullarımdan bir grup, \'Rabbimiz, inandık, bizi bağışla, bize merhamet et, sen merhamet edenlerin en merhametlisisin.\' derdi."'),
(2783, 23, 110, '"Sizse onları alaya aldınız ve onlar yüzünden beni anmayı unuttunuz. Onlara gülüp duruyordunuz."'),
(2784, 23, 111, '"Bugün ben, onlara sabretmelerinin karşılığını verdim. Kazananlar işte bunlardır."'),
(2785, 23, 112, 'Dedi ki, "Yeryüzünde kaç sene kaldınız?"'),
(2786, 23, 113, '"Bir gün veya günün bir kısmı kadar kaldık. Sayanlara sor," dediler.'),
(2787, 23, 114, 'Dedi ki, "Siz gerçekten çok kısa bir süre kaldınız, keşke bilseydiniz."'),
(2788, 23, 115, '"Sizi boş yere yarattığımızı ve bize dönmeyeceğinizi mi sandınız?"'),
(2789, 23, 116, 'Gerçek Yönetici olan ALLAH çok yücedir. O\'ndan başka tanrı yoktur ve O, cömert yönetimin Rabbidir.'),
(2790, 23, 117, 'Her kim ALLAH\'la birlikte, hiç bir delile sahip olmayan başka bir tanrıya da kulluk ederse, onun hesabı Rabbinin katındadır. Kuşkusuz kafirler başaramazlar.'),
(2791, 23, 118, 'De ki: "Rabbim, (bizi) bağışla, merhamet et; sen, merhamet edenlerin en iyisisin."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'Bu, indirerek yasa olarak yayımladığımız bir sure olup öğüt alasınız diye içine apaçık ayetler yerleştirdik.'),
(2793, 24, 2, 'Zina eden kadın ve erkeğin her birine yüz celde vurunuz. ALLAH\'a ve ahiret gününe inanıyorsanız, onlara olan acıma duygunuz ALLAH\'ın yasasını uygulamakta size engel olmamalı. İnananlardan bir grup onların cezalandırılma işlemine tanık olsun.'),
(2794, 24, 3, 'Zina eden bir erkek, eninde sonunda, zina eden veya müşrik bir kadınla evlenir; zina eden bir kadın da eninde sonunda, zina eden veya müşrik bir erkekle evlenir. Bu, inananlar için yasaktır.'),
(2795, 24, 4, 'Evli kadınları suçladıktan sonra dört tanık getiremiyenlere seksen celde vurunuz ve onların tanıklığını ebedi olarak kabul etmeyin. Onlar yoldan çıkmış kimselerdir.'),
(2796, 24, 5, 'Bununla birlikte, bundan sonra tevbe edip kendilerini düzeltenler için ALLAH Bağışlayandır, Rahimdir.'),
(2797, 24, 6, 'Eşlerini suçlayıp kendilerinden başka tanıkları olmayan erkeklerden birinin tanıklığı, doğru konuştuğuna dair dört kez ALLAH\'a yemin etmesiyle geçerli olabilir.'),
(2798, 24, 7, 'Beşinci yeminde de, yalancı olduğu taktirde ALLAH\'ın lanetinin kendisi üzerine olmasını diler.'),
(2799, 24, 8, 'Dört kez ALLAH\'a yemin ederek kocasının yalan söylediğine tanıklıkta bulunduğu taktirde kadın cezayı savar.'),
(2800, 24, 9, 'Beşinci yeminde de, kocasının doğru olması halinde kendisinin ALLAH\'ın gazabına uğramasını diler.'),
(2801, 24, 10, 'ALLAH size bağışı ve merhametiyle davranmasaydı, ALLAH Tevbeleri Kabul Eden ve Bilge olmasaydı..'),
(2802, 24, 11, 'O iftirayı, sizden bir çete uydurdu. Onun sizin için kötü olduğunu sanmayın; aksine sizin için iyi (bir ders) dir. Bu arada, onlardan her biri günahı paylaşmıştır. Elebaşılık yapanları da büyük bir cezayı haketmiştir.'),
(2803, 24, 12, 'Onu işittiğinizde, siz inanan erkek ve kadınların birbiriniz hakkında olumlu düşüncelere sahip olarak, "Bu, açıkça büyük bir yalandır," demeleri gerekmez miydi?'),
(2804, 24, 13, 'Dört tanık getirmeli değiller miydi? Tanıklar getiremediklerine göre onlar ALLAH yanında yalancılardandır.'),
(2805, 24, 14, 'ALLAH\'ın size dünya ve ahirette bağışı ve merhameti olmasaydı daldığınız o olaydan ötürü size büyük bir ceza dokunacaktı.'),
(2806, 24, 15, 'Onu dilinize dolamış ve hakkında bir bilginiz olmayan bir konuda ağzınızı açıp konuşuyordunuz. Onu basit bir şey sanmıştınız. Oysa o, ALLAH yanında büyüktür.'),
(2807, 24, 16, 'Onu işittiğinizde, "Bunu konuşmamız doğru değil. Sen Yücesin. Bu büyük bir iftiradır," demeniz gerekmez miydi?'),
(2808, 24, 17, 'Benzerini tekrarlamayasınız diye ALLAH size öğüt veriyor. İnananlar iseniz.'),
(2809, 24, 18, 'ALLAH size ayetlerini böyle açıklıyor. ALLAH Bilendir, Bilgedir.'),
(2810, 24, 19, 'İnananların arasından hayasızlığın yaygınlaşmasını arzulayanlar, dünya ve ahirette acı verici bir cezayı hakketmişlerdir. ALLAH bilir, siz bilmezsiniz.'),
(2811, 24, 20, 'ALLAH\'ın size bağış ve rahmeti olmasaydı, ALLAH En Şefkatli ve Rahim (En Merhametli) olmasaydı.'),
(2812, 24, 21, 'Ey inananlar, Şeytanın adımlarını izlemeyin. Kim Şeytanın adımlarını izlerse, kendisine hayasızlığı ve kötülüğü emreder. ALLAH\'ın size bağışı ve merhameti olmasaydı sizden hiç biriniz ebediyen temizlenemezdi. Ancak ALLAH dileyeni temizler. ALLAH İşitendir, Bilendir.'),
(2813, 24, 22, 'Sizden mal ve imkana sahip olanlar, akrabalara, yoksullara ve ALLAH yolunda göç edenlere vermemek için yemin etmesinler. Affetsinler, geçsinler. ALLAH\'ın sizi bağışlamasını sevmez misiniz? ALLAH Bağışlayandır, Rahimdir.'),
(2814, 24, 23, 'İffetli ve (kötülüklerden) habersiz inanan kadınlara iftira edenler, dünya ve ahirette lanetlenirler ve onlar için büyük bir ceza vardır.'),
(2815, 24, 24, 'O gün ki, dilleri, elleri ve ayakları onların yapmış oldukları her şeye tanıklık edeceklerdir.'),
(2816, 24, 25, 'ALLAH o gün, hakettiklerini tam olarak ödeyecek ve ALLAH\'ın apaçık bir gerçek olduğunu öğreneceklerdir.'),
(2817, 24, 26, 'Kötü kadınlar, kötü erkekler için, kötü erkekler de kötü kadınlar içindir. İyi kadınlar iyi erkekler için ve iyi erkekler de iyi kadınlar içindir. Bunlar (ikinci grup), onların söylentilerinden uzaktır. Bunlar bağışlanma ve cömert bir ödül haketmişlerdir.'),
(2818, 24, 27, 'Ey inananlar, evlerinizden başka evlere, izin almadan, halkına selam vermeden girmeyiniz. Düşünürseniz bunun sizin için daha iyi olduğunu görürsünüz.'),
(2819, 24, 28, 'Orada hiç kimseyi bulamazsanız (yetkililer tarafından) izin verilmeden oraya girmeyiniz. Size "Geri dönün," dendiğinde dönüp gidiniz. Bu, sizin için daha temiz bir davranıştır. ALLAH yaptıklarınızı Bilendir.'),
(2820, 24, 29, 'Oturulmayan ve içinde eşyanız bulunan bir eve girmenizde bir sakınca yoktur. ALLAH açığa vurduklarınızı da gizlediklerinizi de bilir.'),
(2821, 24, 30, 'İnananlara, gözlerini sakınmalarını (kadınlara gözünü dikip bakmamalarını) ve iffetlerini korumalarını bildir. Bu, onlar için daha temiz bir davranıştır. Elbette ALLAH yaptıklarından haberdardır.'),
(2822, 24, 31, 'İnanan kadınlara da söyle, gözlerini sakınsınlar, iffetlerini korusunlar ve açıkta olması gereken yerleri hariç, alımlı yerlerini göstermesinler. Örtülerini göğüslerinin üzerine kapasınlar. Vücutlarının alımlı yerlerini kimseye göstermesinler; ancak kocaları, babaları, kocalarının babaları, oğulları, kocalarının oğulları, kardeşleri, erkek kardeşlerinin oğulları, kızkardeşlerinin oğulları, diğer kadınlar, cinsel iktidara sahip olmayan erkek hizmetkarlar ve işçiler ve kadınların cinsel yönlerini henüz anlamayan çocuklar hariç. Gizledikleri alımlı bölgelerini sergilemek/bildirmek için ayaklarını yere vurmasınlar. Ey inananlar, topluca ALLAH\'a yöneliniz ki başarılı olasınız.'),
(2823, 24, 32, 'Bekarlarınızı, erdemli kadın ve erkek hizmetçilerinizi evlendiriniz; eğer yoksul iseler, ALLAH onları kendi lütfundan zengin edecektir. ALLAH Cömerttir, Bilendir.'),
(2824, 24, 33, 'Evlenme imkanına sahip olmayanlar da, ALLAH kendilerini lütfuyla zenginleştirene kadar iffetli davransınlar. Evlenme yoluyla özgürlük belgesi almak isteyen esirleri -niyetleri dürüstse- özgürlüklerine kavuşturun. Ve ALLAH\'ın size verdiği parasından onlara veriniz. İffetli olmak isteyen kızlarınızı, bu dünyanın geçici menfaatini arzulayarak fuhuşa zorlamayın. Kim onları zorlarsa, o zaman ALLAH onların zorlandığını hesaba katar; O Bağışlayandır, Rahimdir.'),
(2825, 24, 34, 'Size apaçık ayetler, sizden önce geçmiş kimselerden örnekler ve erdemliler için bir öğüt indirmiş bulunuyoruz'),
(2826, 24, 35, 'ALLAH göklerin ve yerin ışığıdır. Işığının örneği şuna benzer: içinde lamba bulunan bir oyuk... Lamba bir cam kap içindedir. O cam kap ise, incimsi bir gezegen gibidir. Yakıtı, ne batıya ne de doğuya bağıntısı olmayan, zeytinyağı üreten bereketli bir ağaçtandır. Yağı, neredeyse ateş değmeden aydınlık verir. Işık üzerine ışıktır. ALLAH dileyeni/dilediğini ışığına ulaştırır. İşte ALLAH halka böyle örnekler verir. ALLAH her şeyi bilir.'),
(2827, 24, 36, '(Böyle bir ışık ve hidayet), içlerinde ismi anıldığı için ALLAH\'ın onaylayıp yükselttiği evlerdedir. Orada, O\'nu sabah akşam yücelten...'),
(2828, 24, 37, 'Kişiler vardır. Onları, ALLAH\'ı anmaktan, namazı gözetmekten ve zekatı vermekten ne bir iş ne de bir ticaret alıkoymaz. Onlar, kalplerin ve gözlerin ters döneceği günden korkarlar.'),
(2829, 24, 38, 'ALLAH onların güzel işlerini kuşkusuz ödüllendirecek ve onlara lütfunu arttıracaktır. ALLAH dilediğini hesapsız olarak rızıklandırır.'),
(2830, 24, 39, 'İnkarcılara gelince, işleri, çöldeki bir serap gibidir. Susayan onu su sanır. Yanına varınca onu bulamaz, ancak orada ALLAH\'ı bulur; O da onun hesabını tam görür. Kuşkusuz ALLAH hesabı çabuk görendir.'),
(2831, 24, 40, 'Yahut, yoğun bir sisle birlikte dalga üstüne dalganın örttüğü engin bir denizin ortasındaki karanlıklara benzer. Üstüste yığılmış karanlıklar... İnsan elini uzattığı zaman nerdeyse onu bile göremez. ALLAH\'ın ışıktan yoksun bıraktığı kimsenin hiç birışığı olamaz.'),
(2832, 24, 41, 'Diziler halinde uçan kuşlar dahil, göklerde ve yerde bulunan herkesin ALLAH\'ı yücelttiğini görmez misin? Her birisi kendi duasını ve yüceltme yöntemini bilir. ALLAH onların yaptıklarını iyi bilir.'),
(2833, 24, 42, 'Göklerin ve yerin yönetimi ALLAH\'a aittir. Dönüş de ALLAH\'adır'),
(2834, 24, 43, 'Bilmez misin ki ALLAH bulutları sürer, sonra onları birleştirir, sonra onları birbiri üstüne yığar ve sen de yağmurun onların arasından çıktığını görürsün? Gökten büyük kütlelerden dolu yağdırır, dilediği kimseyi ona uğratır ve dilediğinden de onu uzak tutar. Şimşeğinin parıltısı neredeyse gözleri alır.'),
(2835, 24, 44, 'ALLAH gece ile gündüzü çevirip kontrol eder. Gözleri olanlar için elbette bunda bir ders vardır.'),
(2836, 24, 45, 'ALLAH bütün canlıları sudan yaratır. Onlardan kimi karnı üzerinde yürür, kimi iki ayakları üzerinde yürür, kimi de dört ayak üzerinde yürür. ALLAH dilediğini yaratır. ALLAH her şeye gücü yetendir.'),
(2837, 24, 46, 'Apaçık ayetler indirmiş bulunuyoruz. ALLAH dilediğini/dileyeni dosdoğru yola iletir.'),
(2838, 24, 47, '"ALLAH\'a ve elçiye inandık ve itaat ettik," derler. Fakat sonra, bunun ardından, onlardan bir grup yüz çevirir; bunlar inanmış değildir.'),
(2839, 24, 48, 'Aralarında hüküm vermesi için ALLAH\'a ve elçisine çağrıldıkları zaman onlardan bir grup hoşlanmaz.'),
(2840, 24, 49, 'Ancak karar lehlerine olursa, ona gönüllü koşarlar.'),
(2841, 24, 50, 'Kalplerinde bir hastalık mı var? Kuşku mu duyuyorlar? ALLAH\'ın ve elçisinin kendilerine haksızlık yapacağından mı korkmaktadırlar? Aslında onlar zalimlerdir.'),
(2842, 24, 51, 'Aralarında hüküm vermek üzere, ALLAH\'a ve elçisine çağrıldıkları zaman inananlar yalnızca şunu söylerler: "İşittik ve uyduk." Kazananlar işte bunlardır.'),
(2843, 24, 52, 'ALLAH\'a ve elçisine uyan, ALLAH\'ı sayıp O\'nu dinleyen kimseler üstün gelenlerdir.'),
(2844, 24, 53, 'Kendilerine emrettiğin taktirde çıkacaklarına dair yeminlerinin en ciddi olanıyla ALLAH\'a yemin ettiler. De ki, "Yemin etmeyin. Nasıl itaat ettiğiniz ortada. ALLAH yaptığınız her şeyden haberdardır."'),
(2845, 24, 54, 'De ki, "ALLAH\'a ve elçiye uyunuz. Reddederseniz, o kendi görevinden sorumludur, siz de kendi görevinizden sorumlusunuz. Ona uyarsanız, doğruyu bulursunuz. Elçinin tek görevi, mesajı açıkça bildirmekten ibarettir.'),
(2846, 24, 55, 'ALLAH, inanıp erdemli davrananlarınızı, kendilerinden öncekileri egemen kıldığı gibi onları da egemen yapacağına, kendileri için seçtiği dini yerleştireceğine ve korkularını güvene çevireceğine söz vermiştir. Çünkü onlar bana kulluk ederler ve bana hiçbir şeyi ortak koşmazlar. Bundan sonra inkar edenler, yoldan çıkmış olanlardır.'),
(2847, 24, 56, 'Namazı gözetiniz, zekatı veriniz ve elçiye uyunuz ki merhamet edilesiniz.'),
(2848, 24, 57, 'İnkar edenlerin bizi aciz bırakacaklarını sanmayın. Onların varacağı yer ateştir; ne kötü bir varış noktasıdır.'),
(2849, 24, 58, 'Ey inananlar, emriniz altında çalışanlar ve sizden henüz erginliğe ermemiş olanlar üç kez izin almalıdırlar: Sabah namazından önce, öğle vaktinde dinlenmek için elbisenizi çıkardığınız zaman ve Akşam namazından sonra... Bunlar, sizin özel üç vaktinizdir. Bunların dışında, birbirinizin yanına girip çıkmakta bir sakınca yoktur. ALLAH ayetleri size böyle açıklar. ALLAH Bilendir, Bilgedir.'),
(2850, 24, 59, 'Çocuklarınız erginlik çağına erişince, kendilerinden önceki yetişkinlerin izin istedikleri gibi onlar da (özel odanıza girmeden önce) izin istesinler. ALLAH ayetlerini size böyle açıklar. ALLAH Bilendir, Bilgedir.'),
(2851, 24, 60, 'Evlenme beklentisi olmayan yaşlı kadınların, alımlı yerlerini açıp saçmamak koşuluyla dış elbiselerini bırakmalarında bir sakınca yoktur. İffetli davranmaları kendileri için daha iyidir. ALLAH İşitendir, Bilendir.'),
(2852, 24, 61, 'Siz, evlerinizde, babalarınızın evlerinde, annelerinizin evlerinde, kardeşlerinizin evlerinde, kızkardeşlerinizin evlerinde, amcalarınızın evlerinde, halalarınızın evlerinde, dayılarınızın evlerinde, teyzelerinizin, anahtarlarına sahip olduğunuz ve arkadaşlarınızın evlerinde yemenizden dolayı kınanmazsınız. Aynı şekilde kör kınanmaz, topal kınanmaz, sakat ve hasta da kınanmaz. Topluca yahut ayrı ayrı yemenizde de bir sakınca yoktur. Bir eve girdiğinizde ALLAH\'tan güzel, kutlu bir yaşam dileyerek selamlayınız. ALLAH size ayetleri böyle açıklar ki anlayasınız.'),
(2853, 24, 62, 'Gerçek inananlar, ALLAH\'a ve elçisine inanan ve toplumsal bir sorun için onunla beraber iken ondan izinsiz terketmeyenlerdir. Senden izin isteyenler, ALLAH\'a ve elçisine inananlardır. Bazı işleri için senden izin istediklerinde dilediğine izin verebilirsin. Onlar için ALLAH\'tan bağışlanma dile. ALLAH Bağışlayandır, Rahimdir.'),
(2854, 24, 63, 'Elçinin davetini, aranızda herhangi birinin diğerini davet etmesi gibi tutmayın. ALLAH sizden eften püften özürler göstererek sıvışıp gidenleri biliyor. Onun emirlerine aykırı davrananlar, başlarına bir felaketin gelmesinden veya acı bir cezaya çarpılmaktan sakınsınlar.'),
(2855, 24, 64, 'Göklerde ve yerde olanlar hep ALLAH\'a aittir. İçinde bulunduğunuz her durumu bilir. Nitekim kendisine döndürülecekleri gün, yaptıkları her şeyi onlara bildirecektir. ALLAH her şeyi bilendir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'Tüm dünyaya bir uyarı olarak, Yasalar Kitabını kuluna indiren çok Yücedir.'),
(2857, 25, 2, 'Göklerin ve yerin yönetimi O\'na aittir. Çocuk edinmemiştir, yönetiminde de kendisinin ortağı yoktur. Her şeyi yaratmış ve en ayrıntılı ölçülerle ona düzen vermiştir.'),
(2858, 25, 3, 'Buna rağmen O\'nun yanında, bir şey yaratamıyan, kendileri yaratılan, kendilerine bile yarar ve zarar veremiyen, öldüremiyen, yaşatamıyan ve diriltemiyen bir takım tanrılar edindiler.'),
(2859, 25, 4, 'İnkar edenler, "Bu, başkalarının yardımıyla onun uydurduğu bir yalandan başka bir şey değildir," diyerek haksız ve asılsız bir tez ortaya koydular.'),
(2860, 25, 5, 'Dediler, "Yazıp durduğu şey evvelkilerin masallarıdır; gece gündüz kendisine dikte edilmektedir."'),
(2861, 25, 6, 'De ki, "Onu, göklerin ve yerin gizliliklerini bilen indirmiştir. O, Bağışlayandır, Rahimdir."'),
(2862, 25, 7, 'Ve dediler, "Nasıl olur da bu elçi yemek yiyor ve çarşılarda dolaşıyor? Kendisiyle birlikte uyarıcı olarak bir melek inseydi ya!"'),
(2863, 25, 8, '"Yahut kendisine bir hazine atılsaydı, veya kendisinin bir bahçesi olsaydı ve ondan yeseydi!" Hatta zalimler, "Siz, büyülenmiş bir adama uyuyorsunuz," dediler.'),
(2864, 25, 9, 'Bak, senin için ne örnekler verdiler de saptılar, yolu asla bulamıyacaklardır.'),
(2865, 25, 10, 'Dilerse sana, bunlardan daha iyi olan, içlerinden ırmaklar akan cennetler (bahçeler) ve köşkler verebilen O çok yücedir.'),
(2866, 25, 11, 'Doğrusu, onlar Saati (dünyanın sonunu) yalanladılar. Saati yalanlayanlara alevli cehennemi hazırladık.'),
(2867, 25, 12, 'Cehennem onları uzaktan görünce, onun öfkesini ve uğultusunu işitirler.'),
(2868, 25, 13, 'Zincirlenmiş olarak onun dar bir yerinden atıldıkları zaman ölümü çağırırlar'),
(2869, 25, 14, 'Bu gün bir ölüm değil, birçok ölüm çağırın.'),
(2870, 25, 15, 'De ki, "Bu mu, yoksa erdemlilere bir karşılık ve dönüş yeri olarak söz verilmiş ebedi cennet mi daha iyi?"'),
(2871, 25, 16, 'Onlar orada ebedi olarak dilediklerini elde ederler. Bu, Rabbin\'in mutlak surette gerçekleşecek sözüdür.'),
(2872, 25, 17, 'Onları ve onların ALLAH\'tan başka taptıklarını topladığı gün, "Bu kullarımı siz mi saptırdınız, yoksa onlar mı yolu saptılar?" der.'),
(2873, 25, 18, '"Sen yücesin, senin yanında kimseyi veliler kabul etmek bize yakışmaz. Fakat, sen onlara ve atalarına nimetler bağışladın. Nihayet, onlar mesajı unutup yozlaşan bir toplum oldular," diye karşılık verdiler.'),
(2874, 25, 19, 'Onlar sizin bu söylediklerinizi yalanlamış bulunuyorlar. Siz ne veto edebilir, ne de yardım edebilirsiniz. Hatta sizden zulmeden olursa ona da büyük bir azap tattırırız.'),
(2875, 25, 20, 'Senden önce gönderdiğimiz tüm elçiler de yemek yerler, çarşılarda dolaşırlardı. Sizi birbiriniz için bir sınav yaptık; dayanabilecek misiniz? Rabbin Görendir.'),
(2876, 25, 21, 'Bizimle karşılaşmayı ummayanlar, "Bize ya melekler inmeli, yahut Rabbimizi görmeliyiz!," dediler. Kendi kendilerine büyüklük taslamışlar ve azgınlıkta pek ileri gitmişlerdi.'),
(2877, 25, 22, 'Melekleri gördükleri gün, suçlular için iç açıcı bir gün olmayacaktır. "Tümüyle kuşatıldık," derler.'),
(2878, 25, 23, 'Yapmış oldukları işlere bakar ve onları tamamıyla etkisiz hale getiririz.'),
(2879, 25, 24, 'O gün cennet halkının kalacağı yer çok daha iyi olup daha güzel haberler işiteceklerdir.'),
(2880, 25, 25, 'Göğün bulut kütleleri halinde parçalanacağı ve meleklerin topluca indirildiği gün,'),
(2881, 25, 26, 'İşte o gün, yönetim tümüyle ve mutlak olarak Rahman\'ındır. İnkarcılar için zor bir gün olacaktır.'),
(2882, 25, 27, 'O gün zalim kimse (üzüntüden) elini ısırıp şöyle der: "Keşke, elçi ile birlikte aynı yolu tutsaydım."'),
(2883, 25, 28, '"Vay bana, keşke falancayı arkadaş edinmeseydim."'),
(2884, 25, 29, '"Beni, bana ulaşan mesajdan saptırdı. Gerçekten, şeytan insanı yarı yolda bırakır."'),
(2885, 25, 30, 'Elçi de, "Rabbim, halkım Kuran\'ı terketti," der.'),
(2886, 25, 31, 'Biz ayrıca her peygambere suçlulardan bir düşman var ettik. Yol gösterici ve yardımcı olarak Rabbin yeter.'),
(2887, 25, 32, 'İnkarcılar, "Kuran, ona neden bir defada indirilmedi," dediler. Biz böylece onu belleğine yerleştirmekte ve onu belirlenmiş bir dizilişe göre okumaktayız.'),
(2888, 25, 33, 'Onların sana yönelttikleri her teze karşı, biz sana gerçeği ve en güzel açıklamayı getiririz.'),
(2889, 25, 34, 'Cehenneme zorla sürülenler, en kötü yere ve en çarpık yola sahip olanlardır'),
(2890, 25, 35, 'Biz Musa\'ya kitabı vermiş ve kardeşi Harun\'u da kendisine yardımcı olarak atamıştık.'),
(2891, 25, 36, '"Siz ikiniz, ayetlerimizi yalanlayan şu topluma gidin," dedik. Bunun ardından onları yakıp yok ettik.'),
(2892, 25, 37, 'Benzer şekilde, Nuh\'un toplumu da elçileri yalanlayınca, halk için bir ders olsun diye onları boğduk. Zalimler için acı bir ceza hazırlamışızdır.'),
(2893, 25, 38, 'Ad, Semud, Res halkı ve bunların arasında bir çok nesilleri de...'),
(2894, 25, 39, 'Hepsine yeterli örnekler vermiştik, sonunda hepsini kırdık geçirdik'),
(2895, 25, 40, 'Felaket yağmuruna tutulmuş bulunan ülkenin (Sodom) yanından geçmiş bulunuyorlar. Onu görmediler mi? Aslında onlar yeniden dirilmeye inanmıyorlardı.'),
(2896, 25, 41, 'Seni her gördüklerinde seni alaya alırlar: "ALLAH\'ın elçi olarak gönderdiği kişi bu mu?"'),
(2897, 25, 42, '"Direnmeseydik, neredeyse bizi tanrılarımızdan saptırıp ayıracaktı." Azabı gördüklerinde kimin gerçekten sapık yolda olduğunu öğreneceklerdir.'),
(2898, 25, 43, 'Egosunu tanrı edinen kişiyi gördün mü? Sen mi ona avukatlık edeceksin?'),
(2899, 25, 44, 'Onların çoğunun gerçekten işittiklerini yahut anladıklarını mı sanıyorsun? Onlar sadece çiftlik hayvanları gibidir; hayır, yolca daha da sapıktırlar.'),
(2900, 25, 45, 'Rabbinin gölgeyi nasıl uzattığına dikkat ettin mi? Nitekim dileseydi onu hareketsiz de yapardı. Nitekim, güneşi ona delil kıldık (gölgenin varlığını ışığa bağlı kıldık).'),
(2901, 25, 46, 'Sonra onu yavaş yavaş çekip alırız.'),
(2902, 25, 47, 'Ve O, geceyi size bir örtü, uykuyu bir dinlenme ve gündüzü de bir dirilme zamanı yapandır.'),
(2903, 25, 48, 'Ve O, rahmetinden önce müjdeleyici olarak rüzgarları gönderendir. Ve biz gökten tertemiz bir su indiririz.'),
(2904, 25, 49, 'Ki onunla ölü bir ülkeyi diriltelim ve yarattığımız nice çiftlik hayvanlarını ve insanları onunla sulayalım.'),
(2905, 25, 50, 'Öğüt almaları için onu aralarında dağıtıp çevirdik. Ne var ki insanların çoğunluğu nankörlükte diretmektedir.'),
(2906, 25, 51, 'Dileseydik her kente bir uyarıcı gönderirdik'),
(2907, 25, 52, 'Öyleyse, inkarcılara uyma ve bununla (bu Kuran ile) onlara karşı büyük bir cihad ile savaşım ver.'),
(2908, 25, 53, 'O, iki denizi salmıştır; bu taze ve tatlıdır, şu tuzlu ve acıdır. Her ikisinin arasına, karışmalarını engelleyen sağlam bir engel koymuştur.'),
(2909, 25, 54, 'Ve O, sudan bir insan yarattı ve ona soy sop verdi. Rabbin her şeye gücü yetendir.'),
(2910, 25, 55, 'ALLAH\'ın yanısıra kendilerine ne yarar ve ne de zarar veremiyenlere de kulluk ediyorlar. İnkarcı kimse Rabbine karşı çıkandır.'),
(2911, 25, 56, 'Biz seni bir müjdeleyici ve uyarıcı olmaktan başka bir görevle göndermedik.'),
(2912, 25, 57, 'De ki, "Ben görevime karşılık sizden herhangi bir ücret istemiyorum. Sadece, Rabbine ulaşacak yolu arayanlar olmanızı istiyorum."'),
(2913, 25, 58, 'Ölümsüz ve Diri olana güven; O\'nu överek yücelt. Kullarının günahlarını, O\'nun bilmesi yeter.'),
(2914, 25, 59, 'O ki gökleri, yeri ve aralarındakileri altı günde yarattı ve sonra tüm otoritesini kurdu. Rahman\'dır; O\'nu iyi bilenlere sor.'),
(2915, 25, 60, 'Onlara, "Rahman\'a secde edin," dendiği zaman, "Rahman da neymiş? Senin bize karşı savunduğun şeye mi secde edeceğiz?" derler. Ve bu, ancak onların nefretini arttırır.'),
(2916, 25, 61, 'Göğe takımyıldızlar vareden ve ona bir lamba ve parlayan bir ay yerleştiren çok yücedir.'),
(2917, 25, 62, 'O, geceyi ve gündüzü birbirini izler yaptı; öğüt almak veya şükretmek isteyenler için...'),
(2918, 25, 63, 'Rahman\'ın kulları öyle kimselerdir ki yeryüzünde gösterişsizce yürürler. Cahiller kendilerine laf atınca da barış önerirler.'),
(2919, 25, 64, 'Gecenin yalnızlığında Rab\'lerine secde edip düşünceye dalarlar.'),
(2920, 25, 65, 'Ve onlar şöyle derler, "Rabbimiz, cehennem cezasını bizden çevir. Onun cezası korkunçtur."'),
(2921, 25, 66, '"O kötü bir durak ve kötü bir yerdir."'),
(2922, 25, 67, 'Onlar verirken savurganlık ve cimrilik yapmaz, ikisi arasında dengeli harcarlar.'),
(2923, 25, 68, 'Onlar, ALLAH ile birlikte başka tanrılar çağırmazlar; ALLAH\'ın yasakladığı canı haksız yere öldürmezler ve zina etmezler. Bunları işleyen, günahların faturasını öder.'),
(2924, 25, 69, 'Diriliş günü cezaları katlanır ve horlanmış olarak orada ebedi kalırlar'),
(2925, 25, 70, 'Ancak inanıp erdemli bir yaşam sürenler hariç. ALLAH onların günahlarını iyiliklere çevirir. ALLAH Bağışlayandır, Rahimdir.'),
(2926, 25, 71, 'Tevbe edip iyi davrananlar ise ALLAH\'a gereği gibi dönmüş olur'),
(2927, 25, 72, 'Onlar yalan tanıklıkta bulunmazlar. Boş sözlerle karşılaştıklarında önemsemeyip geçerler.'),
(2928, 25, 73, 'Kendilerine Rab\'lerinin ayetleri hatırlatıldığında, onlara karşı sağır ve kör davranmazlar.'),
(2929, 25, 74, 'Onlar ki, "Rabbimiz, eşlerimizi, çocuklarımızı bizim için bir mutluluk kaynağı yap ve bizi önde giden erdemlilerden kıl," derler.'),
(2930, 25, 75, 'Bunlar, sabrettiklerinden ötürü cennet odalarıyla ödüllendirilirler; orada mutlu bir yaşam ve barışa kavuşturulurlar.'),
(2931, 25, 76, 'Orada ebedi kalıcıdırlar. Ne güzel bir duraktır, ne güzel bir yerdir.'),
(2932, 25, 77, 'De ki, "Duanız olmasa Rabbimin yanında değeriniz yoktur. Yalanlarsanız sonucuna katlanacaksınız."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'TT. S. M.'),
(2934, 26, 2, 'Bunlar (harfler), açıklayıcı kitabın mucizeleridir.'),
(2935, 26, 3, 'İnanmıyorlar diye kendini kahrediyor olabilirsin'),
(2936, 26, 4, 'Dilesek onların üzerine gökten bir mucize indiririz de ona boyun eğip kalırlar.'),
(2937, 26, 5, 'Her ne zaman Rahman\'dan kendilerine yeni bir mesaj gelse, mutlaka ondan yüz çevirirler.'),
(2938, 26, 6, 'Yalanladıkları için, eğlenceye aldıkları şeylerin haberleri kendilerine ulaşacaktır.'),
(2939, 26, 7, 'Yeryüzüne bakmazlar mı, onda değişik türden nice güzel bitkiler bitirmişiz.'),
(2940, 26, 8, 'Bunda bir işaret vardır. Ama çokları inanacak değildir.'),
(2941, 26, 9, 'Kuşkusuz senin Rabbin Güçlüdür, Rahimdir.'),
(2942, 26, 10, 'Bir zamanlar Rabbin Musa\'ya seslenmişti: "O zalim topluma git."'),
(2943, 26, 11, '"Firavun\'un halkına; dinleyip düzelmiyecekler mi?"'),
(2944, 26, 12, 'Dedi ki, "Rabbim, onların beni yalanlamalarından korkuyorum."'),
(2945, 26, 13, '"Göğsüm daralıyor, dilim açılmıyor; kardeşim Harun\'u gönder."'),
(2946, 26, 14, '"Ayrıca, onların yanında suçlu biriyim. Korkarım ki beni öldürsünler."'),
(2947, 26, 15, 'Dedi ki, "Hayır, siz ikiniz ayetler (vahiy ve mucizeler) imizle gidin. Biz sizinle birlikteyiz; dinliyoruz."'),
(2948, 26, 16, '"İkiniz Firavun\'a varıp deyin ki, \'Biz evrenlerin Rabbinin elçileriyiz."'),
(2949, 26, 17, '"İsrail oğullarını bizimle birlikte gönder."'),
(2950, 26, 18, 'Dedi ki, "Biz seni daha bebekken alıp yetiştirmedik mi ve hayatının nice yıllarını aramızda geçirmedin mi?"'),
(2951, 26, 19, '"Sonunda yapacağını yaptın. Sen nankör birisin."'),
(2952, 26, 20, 'Dedi ki, "O işi yaptığım zaman yanlış yoldaydım."'),
(2953, 26, 21, '"Sonra, sizden korktuğum için sizden kaçtım ve Rabbim bana bilgelik verip beni elçilikle görevlendirdi."'),
(2954, 26, 22, '"Başıma kaktığın bu iyilik de, İsrail oğullarını köleleştirmen yüzündendir!"'),
(2955, 26, 23, 'Firavun, "Evrenlerin Rabbi de ne demek?" dedi.'),
(2956, 26, 24, 'Dedi ki, "Kesinlikle inanacaksanız O, göklerin, yerin ve aralarındakilerin Rabbidir."'),
(2957, 26, 25, 'Etrafındakilere dönerek, "İşitiyor musunuz?" dedi.'),
(2958, 26, 26, 'Dedi ki, "Sizin Rabbiniz ve evvelki atalarınızın Rabbidir."'),
(2959, 26, 27, 'Dedi ki, "Size gönderilen elçi, kesinlikle bir deli."'),
(2960, 26, 28, 'Dedi ki, "Aklınızı kullanıyorsanız, O doğunun, batının ve aralarındakilerin de Rabbidir."'),
(2961, 26, 29, 'Dedi ki, "Benden başka bir tanrı (otorite) edinirsen seni hapis cezasına çarpacağım."'),
(2962, 26, 30, 'Dedi ki, "Size apaçık bir şey getirmiş olsam da mı?"'),
(2963, 26, 31, 'Dedi ki, "Doğru sözlüysen getir bakalım onu."'),
(2964, 26, 32, 'Değneğini atınca apaçık bir yılan oluverdi.'),
(2965, 26, 33, 'Elini çıkarınca bakanlara bembeyaz görünüverdi.'),
(2966, 26, 34, 'Çevresindeki ileri gelenlere dedi ki, "Bu, gerçekten çok usta bir büyücü imiş."'),
(2967, 26, 35, '"Büyüsüyle sizi toprağınızdan çıkarmak istiyor. Ne önerirsiniz?"'),
(2968, 26, 36, 'Dediler ki, "Onu ve kardeşini alıkoy ve kentlere toplayıcılar gönder de,"'),
(2969, 26, 37, '"Sana tüm usta büyücüleri getirsinler."'),
(2970, 26, 38, 'Belirlenmiş günün randevusu için büyücüler bir araya getirildiler.'),
(2971, 26, 39, 'Halka da, "Siz de toplanır mısınız?" denildi.'),
(2972, 26, 40, '"Büyücüler üstün gelirse onlara uyabiliriz."'),
(2973, 26, 41, 'Büyücüler geldiklerinde Firavun\'a, "Eğer biz üstün gelirsek bize bir ücret ödenecek mi?" dediler.'),
(2974, 26, 42, '"Evet," dedi, "Hatta siz benim konseyime gireceksiniz."'),
(2975, 26, 43, 'Musa onlara, "Atacağınızı atın," dedi.'),
(2976, 26, 44, 'İplerini ve değneklerini attılar, "Firavun\'un onuru için biz üstün geleceğiz," dediler.'),
(2977, 26, 45, 'Sonra Musa değneğini attı; hemen onların uydurduklarını yutmaya başladı.'),
(2978, 26, 46, 'Büyücüler secdeye kapandılar.'),
(2979, 26, 47, 'Dediler, "Evrenlerin Rabbine inandık,"'),
(2980, 26, 48, '"Musa\'nın ve Harun\'un Rabbine..."'),
(2981, 26, 49, 'Dedi ki, "Ben size izin vermeden mi ona inandınız? O, size büyücülüğü öğreten ustanız olmalı. Şimdi göreceksiniz: Ellerinizi, ayaklarınızı çaprazlama keseceğim ve hepinizi asacağım."'),
(2982, 26, 50, '"Umurumuzda değil," dediler, "Biz zaten Rabbimize döneceğiz."'),
(2983, 26, 51, '"İlk inananlar olduğumuz için umarız ki Rabbimiz hatalarımızı bağışlar."'),
(2984, 26, 52, 'Musa\'ya, "Kullarımı yola çıkar, siz izleneceksiniz," diye vahyettik.'),
(2985, 26, 53, 'Firavun, kentlere kitle propagandacıları gönderdi:'),
(2986, 26, 54, '"Bunlar küçük bir çetedir."'),
(2987, 26, 55, '"Bize karşı öfkeyle ayaklanmaktadırlar."'),
(2988, 26, 56, '"Biz ise çoğunluk olarak alarmda olmalıyız."'),
(2989, 26, 57, 'Sonunda, onları çıkardık: Bahçelerden, çeşmelerden,'),
(2990, 26, 58, 'Hazinelerden, yüksek makamlardan...'),
(2991, 26, 59, 'Daha sonra onları İsrail oğullarına miras yaptık.'),
(2992, 26, 60, 'Onları doğuya doğru izlediler.'),
(2993, 26, 61, 'Her iki topluluk birbirini görünce, Musa\'nın arkadaşları, "İşte yakalanıyoruz," dediler.'),
(2994, 26, 62, '"Asla. Rabbim benimle birliktedir; bana bir çıkış yolu gösterecektir," dedi.'),
(2995, 26, 63, 'Musa\'ya, "Değneğini denize vur," diye vahyettik. Bunun üzerine yarıldı ve her bölüm koca bir tepe gibi oldu.'),
(2996, 26, 64, 'Sonra, diğerlerini yaklaştırdık.'),
(2997, 26, 65, 'Musa\'yı ve kendisiyle beraber olan herkesi kurtardık.'),
(2998, 26, 66, 'Sonra, diğerlerini boğduk.'),
(2999, 26, 67, 'Elbette bunda bir ders vardır; ama çokları inanmazlar.'),
(3000, 26, 68, 'Kuşkusuz, senin Rabbin Üstündür, Rahimdir.'),
(3001, 26, 69, 'Onlara İbrahim\'in tarihini anlat.'),
(3002, 26, 70, 'Babasına ve halkına, "Neye tapıyorsunuz?" demişti.'),
(3003, 26, 71, '"Heykellere tapıyoruz; biz kendimizi onlara adamış bulunuyoruz," dediler.'),
(3004, 26, 72, '"Kendilerini çağırdığınızda sizi işitiyorlar mı?" dedi,'),
(3005, 26, 73, '"Yahut size yarar veya zarar verebiliyorlar mı?"'),
(3006, 26, 74, '"Hayır; ancak biz atalarımızın böyle yaptıklarını gördük," dediler.'),
(3007, 26, 75, '"Peki," dedi, "Tapmakta olduklarınızı gördünüz mü,"'),
(3008, 26, 76, '"Siz ve geçmiş atalarınız?"'),
(3009, 26, 77, '"Onlar benim düşmanımdır; yalnız Evrenlerin Rabbi hariç;"'),
(3010, 26, 78, '"Beni yaratan ve bana yol gösteren O\'dur."'),
(3011, 26, 79, '"Beni yediren ve içiren O\'dur."'),
(3012, 26, 80, '"Hastalandığım zaman bana şifa veren O\'dur."'),
(3013, 26, 81, '"Beni öldüren ve sonra dirilten O\'dur."'),
(3014, 26, 82, '" Yargı gününde, kusurlarımı bağışlayacağını umduğum da O\'dur."'),
(3015, 26, 83, '"Rabbim, bana bilgelik ver ve beni iyiler arasına kat."'),
(3016, 26, 84, '"Beni, sonraki nesiller için iyi bir örnek kıl."'),
(3017, 26, 85, '"Beni, Nimetler Cennetine varis olanlardan yap."'),
(3018, 26, 86, '"Babamı bağışla, zira o sapıtmış bulunuyor."'),
(3019, 26, 87, '"Diriliş gününde beni utandırma."'),
(3020, 26, 88, 'O gün, paranın ve çocukların yararı olmayacaktır.'),
(3021, 26, 89, 'ALLAH\'a mükemmel bir kalp ile gelenler hariç.'),
(3022, 26, 90, 'Erdemlilere cennet sunulacaktır.'),
(3023, 26, 91, 'Azgınlar için de cehennem ortaya konacaktır.'),
(3024, 26, 92, 'Onlara şöyle denir, "Hani taptıklarınız nerede -"'),
(3025, 26, 93, '"- O ALLAH\'tan başka? Size şimdi yardım edebiliyorlar mı? Kendilerine bile yardımları dokunabiliyor mu?"'),
(3026, 26, 94, 'Azgınlarla birlikte tepetakla oraya atılacaklardır'),
(3027, 26, 95, 'İblis\'in tüm askerleri de...'),
(3028, 26, 96, 'Orada çekişerek şöyle konuşacaklar:'),
(3029, 26, 97, '"ALLAH\'a andolsun, biz gerçekten çok açık bir sapıklık içinde imişiz."'),
(3030, 26, 98, '"Çünkü sizi evrenlerin Rabbine denk tutuyorduk."'),
(3031, 26, 99, '"Bizi saptıranlar suçlulardı."'),
(3032, 26, 100, '"Şimdi bizim ne şefaatçımız var."'),
(3033, 26, 101, '"Ne de yakın bir dostumuz."'),
(3034, 26, 102, '"Bir şansımız daha olsaydı da, inananlar olsaydık."'),
(3035, 26, 103, 'Bunda bir ders var; ancak çoğunluk inanmaz.'),
(3036, 26, 104, 'Kuşkusuz senin Rabbin Üstündür, Rahim\'dir.'),
(3037, 26, 105, 'Nuh\'un halkı elçileri yalanladı.'),
(3038, 26, 106, 'Kardeşleri Nuh onlara demişti ki, "Dinleyip erdemli davranmaz mısınız?"'),
(3039, 26, 107, '"Ben size gönderilmiş güvenilir bir elçiyim."'),
(3040, 26, 108, '"ALLAH\'ı dinleyip bana uymalısınız."'),
(3041, 26, 109, '"Buna karşılık sizden herhangi bir ücret te istemiyorum. Benim ücretim, ancak evrenlerin Rabbine aittir."'),
(3042, 26, 110, '"ALLAH\'ı dinleyip bana uymalısınız."'),
(3043, 26, 111, 'Dediler ki, "Seni izleyenler bayağı ve kötü kimseler iken, nasıl olur da sana inanırız?"'),
(3044, 26, 112, 'Dedi ki, "Onların yaptıklarından bir bilgim yok."'),
(3045, 26, 113, '"Hesapları, yalnız Rabbime aittir; keşke anlasanız."'),
(3046, 26, 114, '"Kesinlikle hiç bir inananı kovamam."'),
(3047, 26, 115, '"Ben ancak apaçık bir uyarıcıyım."'),
(3048, 26, 116, 'Dediler ki, "Bak Nuh, bu davranışına bir son vermezsen taşlananlardan olacaksın."'),
(3049, 26, 117, 'Dedi ki, "Rabbim, halkım beni yalanladı."'),
(3050, 26, 118, '"Benimle onların arasını aç; beni ve beraberimdeki inananları kurtar."'),
(3051, 26, 119, 'Onu ve yanındakileri yüklü bir gemiyle kurtardık.'),
(3052, 26, 120, 'Sonra bunun ardından, geride kalanları boğduk.'),
(3053, 26, 121, 'Bunda bir ders var; ancak çoğunluk inanmaz.'),
(3054, 26, 122, 'Kuşkusuz senin Rabbin Üstündür, Rahimdir.'),
(3055, 26, 123, 'Ad (halkı) da elçileri yalanladı.'),
(3056, 26, 124, 'Kardeşleri Hud onlara demişti ki, "Erdemli davranmaz mısınız?"'),
(3057, 26, 125, '"Ben, size gönderilmiş güvenilir bir elçiyim."'),
(3058, 26, 126, '"ALLAH\'ı dinleyip bana uyun."'),
(3059, 26, 127, '"Buna karşılık sizden herhangi bir ücret te istemiyorum. Benim ücretim, ancak evrenlerin Rabbine aittir."'),
(3060, 26, 128, '"Her tepenin üzerine bir işaret (bir yapı) yerleştirip oyalanıyor musunuz?"'),
(3061, 26, 129, '"Ebedi kalırsınız diye sağlam yapılar mı edinirsiniz?"'),
(3062, 26, 130, '"Yakaladığınız vakit acımasız yakalıyorsunuz."'),
(3063, 26, 131, '"ALLAH\'ı dinleyip bana uymalısınız."'),
(3064, 26, 132, '"Bildiğiniz her şeyi size vereni dinleyin."'),
(3065, 26, 133, '"Size çiftlik hayvanları ve çocuklar verdi."'),
(3066, 26, 134, '"Üstelik bahçeler, pınarlar..."'),
(3067, 26, 135, '"Sizin için müthiş bir günün cezasından korkarım."'),
(3068, 26, 136, 'Dediler ki, "Öğüt versen de vermesen de bizce birdir."'),
(3069, 26, 137, '"Bu, bizden öncekilerin izlediği yaşantı biçimidir."'),
(3070, 26, 138, '"Biz, cezalandırılacak da değiliz."'),
(3071, 26, 139, 'Böylece onu yalanladılar. Nihayet biz de onları yok ettik. Bunda bir ders var; ancak çoğunluk inanmaz.'),
(3072, 26, 140, 'Kuşkusuz senin Rabbin Üstündür, Rahimdir.'),
(3073, 26, 141, 'Semud (halkı) da elçileri yalanladı.'),
(3074, 26, 142, 'Kardeşleri Salih onlara demişti ki, "Erdemli olmaz mısınız?"'),
(3075, 26, 143, '"Ben size gönderilmiş güvenilir bir elçiyim."'),
(3076, 26, 144, '"ALLAH\'ı dinleyip bana uymalısınız."'),
(3077, 26, 145, '"Buna karşılık sizden bir ücret te istemiyorum. Benim ücretimi ancak evrenlerin Rabbi öder."'),
(3078, 26, 146, '"Şurada güvenlik içinde mi bırakılacaksınız?"'),
(3079, 26, 147, '"Bahçeler, pınarlar, "'),
(3080, 26, 148, '"Ekinler ve olgun meyveli hurmalıklar içindesiniz."'),
(3081, 26, 149, '"Ve dağlardan lüks köşkler yontuyorsunuz."'),
(3082, 26, 150, '"ALLAH\'ı dinleyip beni izlemelisiniz."'),
(3083, 26, 151, '"Sınırı aşanların emrine uymayın."'),
(3084, 26, 152, '"Onlar yeryüzünde iyilik değil kötülük işlerler."'),
(3085, 26, 153, 'Dediler ki, "Sen büyülenmişsin."'),
(3086, 26, 154, '"Sen bizim gibi bir insansın. Doğru sözlü isen bize bir mucize getir bakalım."'),
(3087, 26, 155, 'Dedi ki, "İşte şu deve. Onun su içeceği belli bir zamanı vardır. Sizin de su içeceğiniz belli bir gününüz vardır."'),
(3088, 26, 156, '"Ona bir kötülük dokundurmayın. Yoksa büyük bir günün cezasına çarpılırsınız."'),
(3089, 26, 157, 'Nihayet onu kestiler; ancak pişman oldular.'),
(3090, 26, 158, 'Ve ceza onları yakaladı. Bunda bir ders var; ancak çoğunluk inanmaz."'),
(3091, 26, 159, 'Kuşkusuz senin Rabbin Üstündür, Rahimdir.'),
(3092, 26, 160, 'Lut\'un halkı da elçileri yalanladı.'),
(3093, 26, 161, 'Kardeşleri Lut onlara demişti ki, "Erdemli olmayacak mısınız?"'),
(3094, 26, 162, '"Ben size gönderilmiş güvenilir bir elçiyim."'),
(3095, 26, 163, '"ALLAH\'ı dinleyip bana uyun."'),
(3096, 26, 164, '"Buna karşı sizden herhangi bir ücret de istemiyorum. Benim ücretim ancak evrenlerin Rabbine aittir."'),
(3097, 26, 165, '"Siz halkın arasından erkeklere mi yöneliyorsunuz?"'),
(3098, 26, 166, '"Rabbinizin sizin için yarattığı eşlerinizi terkederek? Siz gerçekten haddi çok aşan bir toplumsunuz."'),
(3099, 26, 167, 'Dediler, "Bak Lut, bu tavrına son vermezsen sürülenlerden olacaksın."'),
(3100, 26, 168, 'Dedi ki, "Ben, bu davranışınızı iğrenç buluyorum."'),
(3101, 26, 169, '"Rabbim, beni ve ailemi bu yaptıklarından kurtar."'),
(3102, 26, 170, 'Onu ve tüm ailesini kurtardık'),
(3103, 26, 171, 'Yalnız bir yaşlı kadın hariç; geride kalanlardan idi.'),
(3104, 26, 172, 'Sonra diğerlerini yerle bir ettik.'),
(3105, 26, 173, 'Üzerlerine bir çeşit yağmur yağdırdık; uyarılanların yağmuru ne felaketli bir yağmurdur.'),
(3106, 26, 174, 'Bunda bir ders var; ancak çokları inanmaz.'),
(3107, 26, 175, 'Kuşkusuz senin Rabbin Üstündür, Rahimdir.'),
(3108, 26, 176, 'Eyke halkı da elçileri yalanladı.'),
(3109, 26, 177, 'Kardeşleri Şuayb onlara demişti ki, "Erdemli olmayacak mısınız?"'),
(3110, 26, 178, '"Ben size gönderilmiş güvenilir bir elçiyim."'),
(3111, 26, 179, '"ALLAH\'ı dinleyin ve beni izleyin."'),
(3112, 26, 180, '"Buna karşı sizden herhangi bir ücret te istemiyorum. Benim ücretim ancak evrenlerin Rabbine aittir."'),
(3113, 26, 181, '"Ölçüyü tam uygulayın. Kandıranlardan olmayın."'),
(3114, 26, 182, '"Doğru ölçek ile tartınız."'),
(3115, 26, 183, '"Halkın hakkını kısmayın ve yeryüzünde kötülük işleyerek karışıklık çıkarmayın."'),
(3116, 26, 184, '"Sizi ve önceki nesilleri yaratanı sayıp dinleyin."'),
(3117, 26, 185, 'Dediler ki, "Sen büyülenmişsin."'),
(3118, 26, 186, '"Sen sadece bizim gibi bir insansın ve biz senin yalan söylediğine inanıyoruz."'),
(3119, 26, 187, '"Doğru sözlü isen üzerimize gökten kütleler indir."'),
(3120, 26, 188, 'Dedi ki, "Rabbim sizin ne yaptığınızı çok iyi bilir."'),
(3121, 26, 189, 'Onu yalanladılar ve sonuç olarak Sayvan Gününün cezası kendilerini yakaladı; müthiş bir günün cezasıydı.'),
(3122, 26, 190, 'Bunda bir ders var; ancak çoğunluk inanmaz.'),
(3123, 26, 191, 'Kuşkusuz, senin Rabbin Üstündür, Rahimdir.'),
(3124, 26, 192, 'Bu, evrenlerin Rabbinin indirdiği vahiydir.'),
(3125, 26, 193, 'Onu Güvenilir Ruh (Cebrail) indirmiştir.'),
(3126, 26, 194, 'Senin kalbine... Uyarıcılardan biri olasın diye.'),
(3127, 26, 195, 'Apaçık Arapça bir dille.'),
(3128, 26, 196, 'Daha önceki kitaplarda da anılmıştır.'),
(3129, 26, 197, 'İsrailoğulları bilginlerinin onu bilmiş olması onlar için yeterli bir delil oluşturmuyor mu?'),
(3130, 26, 198, 'Onu bir takım yabancılara indirseydik,'),
(3131, 26, 199, 'Ve onu onlara okusaydı ona inanmıyacaklardı.'),
(3132, 26, 200, 'İşte biz onu suçluların kalplerine böylece (yabancı bir dil gibi) sokarız.'),
(3133, 26, 201, 'Acı azabı görünceye kadar ona inanmazlar.'),
(3134, 26, 202, 'Onlara ansızın, beklemedikleri bir anda gelecektir.'),
(3135, 26, 203, 'O zaman, "Bize biraz daha süre verilmez mi?" derler.'),
(3136, 26, 204, 'Onlar, hâlâ cezamıza karşı meydan mı okuyorlar?'),
(3137, 26, 205, 'Gördüğün gibi, biz onları yıllarca yaşatsak'),
(3138, 26, 206, 'Ve sonra kendilerine söz verilen başlarına gelse,'),
(3139, 26, 207, 'O tattıkları nimetler kendilerine bir yarar sağlamaz.'),
(3140, 26, 208, 'Biz uyarıcıları olmayan hiç bir kenti yok etmedik.'),
(3141, 26, 209, 'Bu bir uyarı ve mesajdır; çünkü biz haksızlık etmeyiz.'),
(3142, 26, 210, 'Onu şeytanlar indirmemiştir.'),
(3143, 26, 211, 'Onlar bunu ne yaparlar, ne de becerirler.'),
(3144, 26, 212, 'Çünkü onlar işitmekten men edilmişlerdir.'),
(3145, 26, 213, 'ALLAH ile birlikte bir başka tanrı çağırma; yoksa cezalandırılırsın.'),
(3146, 26, 214, 'Sana en yakın olan insanları uyar.'),
(3147, 26, 215, 'Ve seni izleyen inananlara kanadını indir.'),
(3148, 26, 216, 'Sana karşı gelirlerse, "Yaptıklarınızdan uzağım," de.'),
(3149, 26, 217, 'Üstün ve Rahman olana güven.'),
(3150, 26, 218, 'O ki (ibadet ve düşünme için) kalktığın/uyandığın zaman seni görür.'),
(3151, 26, 219, 'Ve senin secde edenler arasındaki hareketini de.'),
(3152, 26, 220, 'Çünkü O İşitendir, Bilendir.'),
(3153, 26, 221, 'Şeytanların kime indiğini size bildireyim mi?'),
(3154, 26, 222, 'Onlar her günahkar iftiracıya iner.'),
(3155, 26, 223, 'Kulak verirler; ancak çoğu yalancıdır.'),
(3156, 26, 224, 'Şairlere ise azgınlar uyar.'),
(3157, 26, 225, 'Onların her vadide koştuklarını (duruma göre yön değiştirdiklerini) görmez misin?'),
(3158, 26, 226, 'Ve onlar yapmadıkları şeyleri söylerler.'),
(3159, 26, 227, 'Ancak inananlar, erdemli davrananlar, ALLAH\'ı çok ananlar ve haksızlığa karşı mücadele edenler hariç. Zalimler, nasıl bir devrim ile devrileceklerini bileceklerdir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'TT. S. Bu (harfler) Kuran\'ın, apaçık bir kitabın mucizeleridir.'),
(3161, 27, 2, 'İnananlar için bir kılavuz ve müjdedir.'),
(3162, 27, 3, 'Onlar ki namazı gözetirler, zekatı verirler ve ahiret konusunda da kuşkuları yoktur.'),
(3163, 27, 4, 'Ahirete inanmıyanların ise yaptıklarını kendilerine süslü göstermişizdir, bocalayıp dururlar.'),
(3164, 27, 5, 'Onlar en kötü cezayı haketmişlerdir ve ahirette de en çok kayba uğrayanlardır.'),
(3165, 27, 6, 'Kuşkusuz sen bu Kuran\'ı, Bilge ve Bilen birisinden almaktasın.'),
(3166, 27, 7, 'Hani Musa ailesine şöyle demişti: "Ben bir ateş gördüm, size ondan bir haber getireyim, yahut size bir meşale getireyim de ısınasınız.'),
(3167, 27, 8, 'Oraya varınca kendisine, "Ateşin içinde bulunan da, çevresinde olan da kutludur," diye seslenildi. Evrenlerin Rabbi olan ALLAH çok yücedir.'),
(3168, 27, 9, '"Musa, bu Benim, Ben Üstün ve Bilge olan ALLAH\'ım."'),
(3169, 27, 10, '"Değneğini at." Onu küçük bir yılan gibi titreştiğini görünce, arkasına dönüp bakmadan kaçtı. "Musa, korkma; elçiler huzurumda korkmazlar."'),
(3170, 27, 11, '"Ancak kim zulmederse, sonra günahlarını bırakıp iyilik yaparsa ona karşı ben Bağışlayıcıyım, Rahimim."'),
(3171, 27, 12, '"Elini koynuna sok da; kusursuz bembeyaz olarak çıksın. Firavun ve halkına göstereceğin dokuz mucizeden biridir. Onlar yoldan çıkan bir toplum olmuşlardır."'),
(3172, 27, 13, 'Onlara açıkça görünen mucizelerimiz geldiğinde, "Bu apaçık bir büyüdür," dediler.'),
(3173, 27, 14, 'Zulüm ve kibirlerinden dolayı kendilerinin haklı oduğuna inandılar ve onları reddettiler. Bozguncuların sonunun nasıl olduğuna dikkat et.'),
(3174, 27, 15, 'Biz Davud\'a ve Süleyman\'a bilgi vermiştik de, "Bize, inanan kullarının birçoğundan daha fazla bağışta bulunan ALLAH\'a övgüler olsun," demişlerdi.'),
(3175, 27, 16, 'Süleyman Davud\'a varis oldu. Dedi ki, "Ey halk, bize kuşların dilini anlamak öğretildi ve bize her şeyden verildi. Bu apaçık bir lütuftur."'),
(3176, 27, 17, 'Süleyman için, cinlerden, insanlardan ve kuşlardan ordular oluşturulmuştu ve düzenle sevkediliyorlardı.'),
(3177, 27, 18, 'Karınca vadisine varınca, bir karınca, "Ey karıncalar, yuvalarınıza giriniz ki Süleyman ve orduları farkında olmayarak sizi ezmesinler."'),
(3178, 27, 19, 'Onun sözüne gülerek, "Rabbim, senin bana ve ana babama bağışladığın nimetlerine şükretmeye ve hoşnut olacağın erdemli davranışlarda bulunmaya beni yönelt. Rahmetinle, beni erdemli kullarının arasına sok."'),
(3179, 27, 20, 'Kuşları denetledi ve, "Neden hüdhüdü görmüyorum, yoksa kaçak mı?" dedi.'),
(3180, 27, 21, 'Bana (özür olarak) açık bir delil getirmezse, onu şiddetli bir cezaya çarpacağım, yahut onu keseceğim.'),
(3181, 27, 22, 'Çok geçmeden (hüdhüd) geldi ve, "Senin bilmediğin bir şeyi öğrendim," dedi, "Sana Sebe\'den önemli bir haber getirdim."'),
(3182, 27, 23, '"Onları yöneten bir kadın buldum. Kendisine her şeyden verilmiş ve büyük bir sarayı var."'),
(3183, 27, 24, '"Onu ve halkını ALLAH\'ın dışında güneşe secde eder buldum. Şeytan onların işlerini kendilerine süslemiş ve onları yoldan çıkarmış ve bu yüzden doğruyu görmüyorlar."'),
(3184, 27, 25, '"Halbuki onlar, göklerde ve yerde gizleneni açığa çıkaran ALLAH\'a secde etmeliydiler. O, onların gizlediklerini de açığa vurduklarını da bilir."'),
(3185, 27, 26, 'ALLAH: O\'ndan başka tanrı yoktur. O büyük yönetimin Rabbidir.'),
(3186, 27, 27, 'Dedi ki, "Doğru mu konuştun, yoksa yalancılardan mısın, göreceğiz."'),
(3187, 27, 28, '"Şu mektubumu onlara götürüp ilet ve sonra bir yana çekilip tepkilerini gözle."'),
(3188, 27, 29, '(Kadın lider) dedi, "Ey konseyim, bana onurlu bir mektup gelmiş bulunuyor."'),
(3189, 27, 30, '"O, Süleyman\'dandır, ve o, \'Rahman ve Rahim ALLAH\'ın İsmiyle\' dir"'),
(3190, 27, 31, '"\'Bana karşı büyüklük taslamayın; bana müslümanlar olarak gelin,\' diye uyarmaktadır."'),
(3191, 27, 32, '"Ey konseyim, şu işte bana bir fikir verin. Siz yanımda bulunmadıkça bir işte karar vermem," dedi.'),
(3192, 27, 33, '"Biz güçlüyüz, yaman savaşçılarız ve son karar sana aittir. Kararını uygula," dediler.'),
(3193, 27, 34, '(Kadın lider) dedi ki, "Krallar girdikleri ülkeleri bozarlar ve onların onurlu halkını aşağılarlar. Böyle davranırlar."'),
(3194, 27, 35, '"Onlara bir armağan göndereyim de, bakayım elçiler nasıl bir tepki ile dönecekler."'),
(3195, 27, 36, '(Elçi) geldiğinde Süleyman kendisine şunları dedi, "Siz bana para ve mal mı vermek istiyorsunuz? ALLAH\'ın bana verdikleri sizin bana verdiğinizden çok daha iyidir. Armağanınızla (ben değil) siz sevinebilirsiniz."'),
(3196, 27, 37, '"Onlara dön, (ve bildir ki) karşı konulamıyacak ordularla üzerlerine varacağız ve onları aşağılanmış ve küçültülmüş olarak oradan çıkartacağız."'),
(3197, 27, 38, 'Dedi ki, "Ey konseyim, onlar bana müslümanlar olarak gelmeden önce hanginiz onun tahtını bana getirebilir?"'),
(3198, 27, 39, 'Cinlerden bir ifrit, "Sen daha yerinden kalkmadan onu sana getirebilirim. Bunu becerebilecek güce sahibim," dedi.'),
(3199, 27, 40, 'Kitap bilgisine sahip olan birisi de, "Ben onu, gözünü kırpman için geçen süreden daha çabuk getirebilirim," dedi. Onu yanında duruyor görünce, "Bu Rabbimin bir lütfudur. Şükür mü, yoksa nankörlük mü edeceğimi sınıyor. Şükreden kendisi için şükretmiş olur. Nankörlük eden de, bilsin ki benim Rabbim Zengindir, Şereflidir," dedi.'),
(3200, 27, 41, '"Tahtının biçimini değiştirin, bakalım doğruyu bulabilecek mi, yoksa doğruyu bulamıyanlardan mı olacak?"'),
(3201, 27, 42, '(Kadın lider) varınca kendisine, "Senin tahtın buna mı benziyor?" dendi. "Tıpkı o," dedi, "Bize ondan önce bilgi verilmişti ve biz müslüman idik."'),
(3202, 27, 43, 'Onu, ALLAH\'tan başka taptıkları saptırmıştı. İnkarcı bir topluluğun bir bireyi idi.'),
(3203, 27, 44, 'Ona, "Sarayın salonuna gir," dendi. Sarayın salonunu görünce, su havuzu sanarak bacaklarını sıvadı. (Süleyman) dedi ki, "Bu, kristalle döşenmiş bir salondur." (Kadın lider), "Rabbim, kendime haksızlık etmişim. Süleyman ile birlikte evrenlerin Rabbi ALLAH\'a teslim oldum," dedi'),
(3204, 27, 45, 'Semud\'a kardeşleri Salih\'i, "ALLAH\'a kulluk edin," desin diye gönderdik. Bunun üzerine, çekişen iki gruba ayrıldılar.'),
(3205, 27, 46, '"Halkım," dedi, "neden iyilik yerine kötülükte acele ediyorsunuz? Merhamet edilmeniz için ALLAH\'tan bağışlanma dilemeniz gerekmez miydi?"'),
(3206, 27, 47, 'Dediler ki, "Sen ve beraberindekiler bize uğursuzluk getirdiniz." Dedi ki, " Sizin uğursuzluğunuz ALLAH\'tan gelmektedir. Doğrusu siz sınava sokulan bir toplumsunuz."'),
(3207, 27, 48, 'Kentte dokuzlu bir çete vardı; bölgede bozgunculuk çıkarıyorlar ve asla iyi bir şey yapmıyorlardı.'),
(3208, 27, 49, 'ALLAH\'a and içerek birbirlerine, "Onu ve ailesine bir gece baskını yapalım ve sonra onun kabilesine, onların ölümü hakkında hiç bir şey bilmediğimizi ve doğru konuştuğumuzu söyleyelim," dediler.'),
(3209, 27, 50, 'Böylece bir plan kurdular, ancak onlar farkına varmadan biz de bir plan kurduk.'),
(3210, 27, 51, 'Planlarının nasıl sonuçlandığına bak; biz onları, halklarıyla birlikte yerle bir ettik.'),
(3211, 27, 52, 'Zulmetmeleri yüzünden, işte çökmüş evleri... Bilen bir toplum için bunda bir ders olmalı.'),
(3212, 27, 53, 'İnananları ve erdemli davrananları kurtardık.'),
(3213, 27, 54, 'Lut da halkına demişti ki, "Gördüğünüz halde nasıl olur da böyle bir kötülüğü işliyorsunuz?"'),
(3214, 27, 55, '"Kadınları bırakıp erkeklerle mi cinsel ilişki kuruyorsunuz? Siz gerçekten pek cahil bir topluluksunuz."'),
(3215, 27, 56, 'Halkının yanıtı sadece, "Lut\'un ailesini kasabanızdan çıkarın, onlar temiz kalmak istiyorlar," demek oldu.'),
(3216, 27, 57, 'Biz de onu ve ailesini kurtardık, yalnız karısı hariç; onu geride kalanlardan saydık.'),
(3217, 27, 58, 'Onların üzerine bir çeşit yağmur yağdırdık. Uyarılmış bulunanların yağmuru ne de kötü idi.'),
(3218, 27, 59, 'De ki, "Övgü ALLAH\'a özgüdür. Selam (barış) da seçtiği kullarına olsun. ALLAH mı, yoksa onların ortak koştukları mı daha iyidir?"'),
(3219, 27, 60, 'Gökleri ve yeri yaratan, gökten bir su yağdırıp, onunla sizin bir ağacını dahi bitiremiyeceğiniz güzel bahçeler bitiren kimdir? ALLAH ile birlikte bir başka tanrı mı? Doğrusu, onlar sapan bir toplumdur.'),
(3220, 27, 61, 'Yeryüzünü bir yerleşim merkezi yapan, aralarından ırmaklar çıkaran, üzerine sağlam dağlar yerleştiren ve her iki suyun arasına bir engel koyan kimdir? ALLAH ile birlikten bir başka tanrı mı? Doğrusu, onların çoğu bilmez.'),
(3221, 27, 62, 'Darda kalmışın çağrısına karşılık veren, kötülüğü gideren ve sizi yeryüzünün varisleri kılan kimdir? ALLAH ile birlikte bir başka tanrı mı? Ne kadar az öğüt alırsınız?'),
(3222, 27, 63, 'Karanın ve denizin karanlıklarında size yol gösteren kimdir? Rahmetinden önce rüzgarları müjde olarak gönderen kimdir? ALLAH ile birlikte bir başka tanrı mı? ALLAH ortak koştuklarından çok yücedir.'),
(3223, 27, 64, 'Yaratılışı başlatan ve sonra onu tekrarlayan kimdir? Sizi gökten ve yerden rızıklandıran kimdir? ALLAH ile birlikte bir başka tanrı mı? De ki, "Doğru sözlüler iseniz delilinizi getirin."'),
(3224, 27, 65, 'De ki, "Göklerde ve yerde, ALLAH\'tan başka kimse geleceği bilemez. Ne zaman dirileceklerinin bile farkına varmazlar."'),
(3225, 27, 66, 'Doğrusu, onların ahiret hakkındaki bilgileri derme-çatmadır. Aslında ondan kuşku içindedirler. Daha doğrusu, onlar ondan yana tümüyle kördürler.'),
(3226, 27, 67, 'İnkar edenler dediler, "Biz ve atalarımız toprak olduktan sonra mı çıkarılacağız?"'),
(3227, 27, 68, '"Bize ve atalarımıza da daha önce aynı söz verilmişti. Bunlar geçmişlerin masallarından başka bir şey değildir."'),
(3228, 27, 69, 'De ki, "Yeryüzünü dolaşın da suçluların sonunun ne olduğuna bir bakın."'),
(3229, 27, 70, 'Onlara üzülme, planladıkları şeylerden ötürü de canını sıkma.'),
(3230, 27, 71, '"Doğru iseniz, bu söz ne zaman gerçekleşecek?" diyorlar.'),
(3231, 27, 72, '"Belki, meydan okuyarak istediğiniz şeylerin bir kısmı size çatmıştır bile," de.'),
(3232, 27, 73, 'Kuşkusuz Rabbin halka karşı Lütuf Sahibidir, fakat çokları şükretmez.'),
(3233, 27, 74, 'Ve Rabbin onların göğüslerinin gizlediğini de açığa vurduğunu da elbette bilir.'),
(3234, 27, 75, 'Göklerde ve yerde gizli olan her şey, istisnasız apaçık bir kitaptadır.'),
(3235, 27, 76, 'Bu Kuran, İsrailoğullarının hâlâ tartışmakta olduğu bir çok konuyu anlatmaktadır.'),
(3236, 27, 77, 'Elbette o bir kılavuzdur ve inananlar için bir rahmettir.'),
(3237, 27, 78, 'Rabbin, onların arasında kararını verecektir. O Üstündür, Bilendir.'),
(3238, 27, 79, 'ALLAH\'a güven. Sen apaçık gerçeği izlemektesin.'),
(3239, 27, 80, 'Sen ölülere duyuramazsın, aynı şekilde arkalarını dönen sağırlara da çağrıyı duyuramazsın.'),
(3240, 27, 81, 'Körü de sapıklığından çıkarıp yola iletemezsin. Sen ancak, ayetlerimize inananlara duyurabilirsin; onlar (anlattığın gerçeği) kabul ederler.'),
(3241, 27, 82, 'Zamanı gelince, onlara topraktan mamul bir yaratık çıkaracağız; onlara, halkın ayet ve mucizelerimize inanmadığını bildirecek.'),
(3242, 27, 83, 'Gün gelecek, her toplum içinde, ayetlerimizi ve mucizelerimizi yalanlayan kimseleri toplayıp süreceğiz.'),
(3243, 27, 84, 'Geldikleri zaman, "Ayet ve mucizelerimi bilginizle kavramadığınız halde yalanladınız mı? Yoksa yaptığınız neydi?" der.'),
(3244, 27, 85, 'Haksızlık ettikleri için verilen söz başlarına gelir ve artık konuşamazlar.'),
(3245, 27, 86, 'Geceyi dinlenmelerine elverişli, gündüzü de aydınlık yaptığımızı görmediler mi? İnanan bir toplum için elbette bunda işaretler vardır.'),
(3246, 27, 87, 'Boruya üfürüldüğü gün, göklerde ve yerde bulunan herkes, ALLAH\'ın diledikleri hariç korkuya kapılacaklardır. Hepsi ona boyun bükerek gelirler.'),
(3247, 27, 88, 'Dağları durgun sanırsın, halbuki bulutlar hareket ettiği gibi hareket etmektedir. Her şeyi sapasağlam yaratan ALLAH\'ın sanatıdır. O, yaptıklarınızı bilendir.'),
(3248, 27, 89, 'Kim iyilik getirirse, ona ondan daha iyisi verilir ve onlar o günün korkusundan güvenlikte olurlar.'),
(3249, 27, 90, 'Kim kötülük getirirse, ateşe yüzüstü kapaklanır." Yaptıklarının karşılığından başkasını mı bekliyordun? "'),
(3250, 27, 91, 'Ben, sadece bu şehrin Rabbine kulluk etmekle emredildim. O burayı kutsal kılmıştır ve her şey O\'nundur. Ben müslümanlardan olmakla emredildim.'),
(3251, 27, 92, 'Ve Kuran\'ı okumakla da... Kim doğruyu bulursa kendi yararına doğruyu bulmuştur. Kim saparsa ben ancak uyarıcılardan biriyim.'),
(3252, 27, 93, 'De ki, "Övgü ALLAH\'adır; ayetlerini size gösterecek, siz de onları tanıyacaksınız. Rabbin onların yaptığından habersiz değildir."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'TT. S. M.'),
(3254, 28, 2, 'Bu (harf) ler bu apaçık Kitabın mucizeleridir.'),
(3255, 28, 3, 'İnanan bir toplum için, Musa ile Firavun\'un bir kısım haberini sana doğru olarak anlatmaktayız.'),
(3256, 28, 4, 'Firavun yeryüzünde despotça davrandı. Halkını sınıflara ayırıp onlardan bir grubu ezip sömürüyor, kızlarını yaşatıp oğullarını kesiyordu. O, bir bozguncu idi.'),
(3257, 28, 5, 'Yeryüzünde güçsüzleştirilip ezilenlere lütfederek diledik ki onları önderler ve varisler yapalım.'),
(3258, 28, 6, 'Ve onları yeryüzüne yerleştirelim, Firavun, Haman ve ordularına, korktukları şeyi gösterelim.'),
(3259, 28, 7, 'Musa\'nın annesine: "Onu emzir, ancak ne zaman onun can güvenliğinden endişelenirsen onu ırmağa at. Korkma, üzülme. Biz onu sana döndürecek ve onu elçilerden biri yapacağız," diye vahyettik.'),
(3260, 28, 8, 'Nihayet Firavun\'un ailesi, kendileri için bir düşman ve üzüntü kaynağı olacak o bebeği aldı. Firavun, Haman ve orduları suçlular idiler.'),
(3261, 28, 9, 'Firavun\'un karısı, "Gözümüz aydın olsun. Onu öldürme. Belki bize yararı dokunur, yahut onu evlat ediniriz." Hiç bir şeyden haberleri yoktu.'),
(3262, 28, 10, 'Musa\'nın annesinin gönlü bomboş sabahladı. İnanması için gönlünü pekiştirmeseydik, neredeyse durumu açığa vuracaktı'),
(3263, 28, 11, 'Kızkardeşine, "Onu izle," dedi. O da kimse farketmeden uzaktan izledi.'),
(3264, 28, 12, 'Önceden, onun süt annelerini kabul etmemesini sağladık. Nihayet (kızkardeşi), "Sizin için onun bakımını üstlenecek ve ona iyi davranacak bir aileyi göstereyim mi?" dedi.'),
(3265, 28, 13, 'Böylece onu annesine döndürdük ki gözü aydınlansın, üzülmesin ve ALLAH\'ın sözünün gerçek olduğunu bilsin. Ne var ki çokları bilmez.'),
(3266, 28, 14, 'Erginlik çağına gelip olgunlaşınca ona bilgelik ve bilgi verdik. İyi davrananları böyle ödüllendiririz.'),
(3267, 28, 15, 'Halkının haberi olmadığı bir sırada kente girmişti. Orada iki adamın kavga ettiklerini gördü; biri onun tarafından (İbrani), diğeri de düşman tarafından (Mısırlı) idi. Tarafından olan adam düşmanına karşı ondan yardım istedi. Bunun üzerine Musa da ötekine bir yumruk indirip işini bitirdi. "Bu şeytanın bir işidir; o bir düşmandır, apaçık bir saptırıcıdır," dedi.'),
(3268, 28, 16, '"Rabbim," dedi, "ben kendime haksızlık ettim, beni bağışla." O da onu bağışladı. Çünkü O Bağışlayandır, Rahimdir.'),
(3269, 28, 17, '"Rabbim," dedi, "bana bağışladığın nimetlere karşılık olarak bundan böyle suçlulara yardım etmeyeceğim."'),
(3270, 28, 18, 'Korku içinde, etrafı kollayarak şehirde sabahladı. Kendisinden dün yardım istemiş olan adam, feryad ederek yine kendisinden yardım istiyordu. Musa ona, "Belli ki sen bir belalısın," dedi.'),
(3271, 28, 19, 'Ortak düşmanlarını yakalamak isteyince, "Musa, dün birisini öldürdüğün gibi bu gün de beni mi öldürmek isitiyorsun? Sen, yeryüzünde erdemli biri olmak istemiyorsun; bir zorba olmak istiyorsun," dedi.'),
(3272, 28, 20, 'Şehrin öte yanından koşa koşa gelen bir adam, "Musa, ileri gelenler seni öldürmek için aralarında görüşüyorlar. Çabuk burayı terket; ben sana öğüt vermekteyim," dedi.'),
(3273, 28, 21, 'Korku içinde, çevreyi kollayarak oradan kaçtı. "Rabbim, beni şu zalim toplumdan kurtar," dedi.'),
(3274, 28, 22, 'Medyen\'e doğru yönelince, "Umarım Rabbim beni doğruya iletir," dedi.'),
(3275, 28, 23, 'Medyen suyuna vardığında, orada hayvanlarını sulayan halktan bir grup buldu. Onların gerisinde itilip kakılan iki kadın gördü. Onlara, "Ne yapmak istiyorsunuz?" dedi. "Çobanlar sulayıp çekilmeden sulayamayız. Babamız da çok yaşlı bir adamdır," dediler.'),
(3276, 28, 24, 'Onlar için suladı, sonra gölgeye çekildi ve, "Rabbim, senin bana bağışlayacağın her şeye muhtacım," dedi.'),
(3277, 28, 25, 'Çok geçmeden onlardan birisi utana utana ona doğru yürüyerek geldi ve "Babam, bizim için sulamana karşılık olarak sana ücret vermek istiyor," dedi. Onunla karşılaşıp ona başından geçenleri anlatınca o, "Korkma," dedi, "Zalim topluluktan kurtulmuş bulunuyorsun."'),
(3278, 28, 26, 'Onlardan biri, "Babacığım, ona iş ver. O, senin ücretle tuttuklarının en iyisidir; hem güçlü, hem de güvenilir biri," dedi.'),
(3279, 28, 27, 'Dedi ki, "Bana sekiz hac boyunca çalışman koşuluyla seni şu kızlarımdan biriyle nikahlamak istiyorum. Dilersen onu on (yıl)a tamamlayabilirsin. Sana zorluk çıkarmak istemiyorum. ALLAH dilerse, beni erdemli davranan biri olarak bulacaksın."'),
(3280, 28, 28, 'Dedi ki, "Bu, seninle benim arasında bir sözleşmedir. Hangi süreyi yerine getirirsem bana düşmanlık yok. Konuştuklarımıza ALLAH garantördür," dedi.'),
(3281, 28, 29, 'Musa, o süreyi bitirip ailesiyle birlikte yola çıkınca Tur\'un yamacında bir ateş gördü ve ailesine, "Burada dur, ben bir ateş gördüm. Belki size ondan bir haber veya bir ateş koru getiririm de onunla ısınırsınız," dedi.'),
(3282, 28, 30, 'Oraya varınca (yanan) ağacın bulunduğu kutsal bölgede, sağ yamacın kenarından kendisine seslenildi: "Musa, Ben evrenlerin Rabbi ALLAH\'ım."'),
(3283, 28, 31, '"Değneğini at." Onu bir yılan gibi titreşir görünce, ardına bile bakmadan dönüp kaçtı. "Musa, dön, korkma, sen güvencedesin."'),
(3284, 28, 32, '"Elini koynuna sok, kusursuz bembeyaz çıksın. Korkudan açılan kanatlarını katlayıp indir. Bu ikisi, Firavun ve ileri gelen ekibine Rabbinden iki delildir. Onlar yoldan çıkan bir toplum olmuşlardır."'),
(3285, 28, 33, 'Dedi ki, "Rabbim, ben onlardan bir kişiyi öldürmüştüm. Beni öldüreceklerinden korkarım."'),
(3286, 28, 34, '"Ayrıca, kardeşim Harun benden daha iyi konuşur. Onu benimle birlikte, beni destekleyen bir yardımcı olarak gönder. Beni yalanlamalarından korkarım."'),
(3287, 28, 35, 'Dedi ki, "Senin pazunu kardeşinle güçlendireceğiz ve ikinize öyle bir yetki vereceğiz ki size dokunamıyacaklar. Siz ikiniz ve sizi izleyenler mucizelerimizle zafer kazanacaksınız."'),
(3288, 28, 36, 'Musa onlara apaçık ayet ve mucizelerimizle gidince, "Bu ancak uydurma bir büyüdür. Biz böyle bir şeyi önceki atalarımızdan işitmedik," dediler.'),
(3289, 28, 37, 'Musa dedi ki, "Rabbim, kendisinden bir hidayetle gelenin kim olduğunu ve sonunda kimin kazançlı çıkacağını iyi bilir. Zalimler kesinlikle başaramazlar."'),
(3290, 28, 38, 'Firavun, "Ey ileri gelenler, ben sizin için benden daha iyi bir tanrı bilmiyorum. Haman, benim için balçığın üzerinde ateş yakarak tuğla hazırla ve Musa\'nın tanrısına ulaşabilmem için bir kule yap. Ben onun yalancı olduğuna inanıyorum," dedi.'),
(3291, 28, 39, 'O ve ordusu yeryüzünde haksız yere büyüklendi ve bize dönmeyeceklerini sandılar.'),
(3292, 28, 40, 'Onları ve ordusunu yakalayıp denize attık. Zalimlerin sonunun nasıl olduğuna dikkat et.'),
(3293, 28, 41, 'Onları, ateşe çağıran önderler kıldık; diriliş gününde de yardım görmezler.'),
(3294, 28, 42, 'Bu dünyada onları lanetle yaftaladık, diriliş gününde de aşağılanacaklardır.'),
(3295, 28, 43, 'İlk nesilleri (Nuh, Hud, Salih, Lut ve Şuayb\'ın halklarını) yok ettikten sonra Musa\'ya, halkı aydınlatan bir kılavuz ve rahmet olarak kitabı verdik; belki öğüt alırlar diye.'),
(3296, 28, 44, 'Musa\'ya emri ilettiğimiz zaman sen batı tarafında bulunmuyordun; sen tanık değildin.'),
(3297, 28, 45, 'Fakat biz bir çok nesiller yarattık. Üzerinden çok zaman geçti. Sen Medyen halkı arasında bulunup ayetlerimizi okuyor da değildin. Ancak, biz elçiler göndeririz.'),
(3298, 28, 46, '(Musa\'ya) Seslendiğimizde Tur\'un yamacında da bulunmuyordun. Ancak, senden önce kendilerine bir uyarıcı gelmemiş bir toplumu uyarman için Rabbinden bir rahmet olarak (sana bilgi veriyoruz); belki öğüt alırlar.'),
(3299, 28, 47, 'Kendi elleriyle işledikleri yüzünden başlarına bir musibet geldiğinde, "Rabbimiz, bize bir elçi gönderseydin de senin ayetlerine uysaydık ve inananlardan olsaydık," diyemesinler diye...'),
(3300, 28, 48, 'Tarafımızdan kendilerine gerçek ulaştığında, "Keşke Musa\'ya verilen (mucizeler) in bir benzeri bize de getirilseydi!" dediler. (Aynı kafa yapısına sahip olanlar) daha önce de Musa\'ya verilenleri inkar etmemişler miydi? "Birbirini destekleyen iki büyü (kitabı) dır. Biz ikisini de reddediyoruz," dediler.'),
(3301, 28, 49, 'De ki, "Doğru sözlü iseniz, ALLAH katından bu ikisinden daha iyi yol gösteren bir kitap getirin, ben ona uyayım."'),
(3302, 28, 50, 'Sana cevap vermezlerse bil ki sadece kuruntularına uyuyorlar. ALLAH\'tan gelen bir yol gösterici olmadan, kuruntularına uyandan daha sapık kim olabilir? Kuşkusuz ALLAH zalim halkı doğruya iletmez.'),
(3303, 28, 51, 'Öğüt alırlar diye kendilerine mesajı iletip durduk.'),
(3304, 28, 52, 'Kendilerine daha önceki kitapları verdiklerimiz buna inanırlar.'),
(3305, 28, 53, 'Kendilerine okunduğu zaman, "Ona inandık. Bu, Rabbimizden gelen gerçektir. Zaten ondan önce de müslümanlar idik," derler.'),
(3306, 28, 54, 'Onların ödülleri, sabretmelerinden ötürü iki kez verilecektir. Kötülüğü iyilikle savarlar ve kendilerine verdiğimiz nimetlerden verirler.'),
(3307, 28, 55, 'Boş sözleri işittiklerinde ondan yüz çevirirler ve, "Bizim işimiz bize, sizin işiniz size. Size selam (barış) olsun. Biz cahillerle uğraşmak istemeyiz," derler.'),
(3308, 28, 56, 'Sen sevdiğini doğruya iletemezsin. Dilediğini doğruya ileten sadece ALLAH\'tır. Doğruya ulaşmayı hakedenleri en iyi bilen de O\'dur.'),
(3309, 28, 57, 'Dediler ki, "Senin hidayetine uysak yurdumuzdan ediliriz. Onları, katımızdan her çeşit ürünün rızık olarak toplanıp götürüldüğü güvenli ve kutsal bir bölgeye yerleştirmedik mi? Ne var ki çokları bilmez.'),
(3310, 28, 58, 'Kendilerine verilen nimetlere nankörlük eden nice toplumları yok etmişizdir. İşte şunlar, kendilerinden sonra pek az kimse tarafından ikametgah edinilmiş evleridir. Oralara biz varis olduk.'),
(3311, 28, 59, 'Rabbin, toplumların merkezine, kendilerine ayetlerimizi okuyan bir elçi göndermedikçe onları yok etmez. Biz, halkı zalim olmayan toplumları asla yok etmeyiz.'),
(3312, 28, 60, 'Size verilen her şey, bu dünya hayatının malı ve süsüdür. ALLAH\'ın yanında olanlar ise daha iyi ve süreklidir. Anlamaz mısınız?'),
(3313, 28, 61, 'Kavuşacağı güzel bir ödülü kendisine söz verdiğimiz bir kimse, kendisini dünya hayatının malıyla nimetlendirdiğimiz ve sonra diriliş günü hesaba çekilen kimse gibi midir?'),
(3314, 28, 62, 'Gün gelir, onlara, "Ortaklarım olduklarını iddia etmekte olduklarınız hani nerede?" diye seslenir.'),
(3315, 28, 63, 'Aleyhlerinde yargının gerçekleştiği kimseler, "Rabbimiz, şunlar bizim saptırdığımız kimselerdir; biz kendimiz sapmış olduğumuz için onları saptırdık. Onları bırakıp sana sığınıyoruz. Onlar aslında bize tapmıyorlardı," derler.'),
(3316, 28, 64, 'Onlara, "Haydi ortaklarınızı (yardıma) çağırın." Bunun üzerine onları çağırırlar, ancak kendilerine karşılık vermezler. Azabı görürler ve doğru yolda bulunmadıklarına yanarlar.'),
(3317, 28, 65, 'O gün onlara seslenerek, "Elçilere nasıl bir cevap verdiniz?" der.'),
(3318, 28, 66, 'O günde gerçekleşen olaylar karşısında şaşkına dönmüşlerdir.'),
(3319, 28, 67, 'Kim tevbe eder, inanır ve erdemli davranırsa işte o zaman başarılı olabilir.'),
(3320, 28, 68, 'Rabbin dilediğini yaratır, dilediğini seçer. Seçim onlara ait değildir. ALLAH onların ortak koştuklarından uzaktır, yücedir.'),
(3321, 28, 69, 'Çünkü Rabbin, onların açığa vurduklarını bildiği gibi göğüslerinin gizlediklerini de bilir.'),
(3322, 28, 70, 'O ALLAH\'tır; O\'ndan başka tanrı yoktur. Başta da Sonda da övgüler O\'nadır. Tüm yargı da O\'na aittir ve O\'na döndürüleceksiniz.'),
(3323, 28, 71, 'De ki, "ALLAH geceyi diriliş gününe kadar üzerinize sürekli kılsaydı ne dersiniz? ALLAH\'tan başka hangi Tanrı size bir ışık getirebilir? İşitmiyor musunuz?"'),
(3324, 28, 72, 'De ki, "ALLAH gündüzü diriliş gününe kadar üzerinize sürekli kılsaydı ne dersiniz? ALLAH\'tan başka hangi Tanrı dinleneceğiniz geceyi size getirebilir? Görmüyor musunuz?'),
(3325, 28, 73, 'Geceyi ve gündüzü, birinde dinlenmeniz ve diğerinde onun nimetlerini aramanız için düzenlemesi O\'nun rahmetindendir. Belki şükredersiniz.'),
(3326, 28, 74, 'Onlara seslendiği gün, "Benim ortaklarım olduğunu sandığınız kimseler nerede?" der.'),
(3327, 28, 75, 'Her bir toplumdan bir tanık seçer ve, "Delilinizi getirin," deriz. Böylece, tüm gerçeğin ALLAH\'a ait olduğunu öğrenecekler ve uydurmuş oldukları şeyler kendilerini bırakıp kaybolacaktır.'),
(3328, 28, 76, 'Karun, Musa\'nın halkından olmasına rağmen ihanet edip onlara zulmetti. Kendisine öyle hazineler vermiştik ki anahtarları güçlü bir topluluğa bile ağır geliyordu. Halkı ona şöyle demişti: "Şımarma, ALLAH şımaranları sevmez."'),
(3329, 28, 77, '"ALLAH\'ın sana verdiği nimetlerle, dünyadaki payını unutmadan ahiret yurdunu ara. ALLAH sana nasıl iyilikte bulunmuşsa sen de iyilikte bulun. Yeryüzünde bozgunculuk yapmayı arama. ALLAH bozguncuları sevmez."'),
(3330, 28, 78, 'Dedi ki, "Tüm bunlar bana, bilgi ve becerimden dolayı verildi." Ondan önce, kendisinden daha büyük bir güce ve sayısal çoğunluğa sahip nice nesilleri ALLAH\'ın yok ettiğini bilmezmi? Suçluların suçları kendilerinden sorulmaz.'),
(3331, 28, 79, 'Bir ara, tüm görkemiyle halkının arasına çıkmıştı. Dünya hayatını seçenler, "Keşke Karun\'a verilenlerin bir benzeri bize de verilseydi. Gerçekten, o çok şanslı biri," dediler.'),
(3332, 28, 80, 'Kendilerine bilgi verilmiş olanlar, "Yazık size, inanan ve erdemli davranan için ALLAH\'ın ödülü daha iyidir," dediler. Buna ancak sabredenler kavuşturulur.'),
(3333, 28, 81, 'Onu eviyle birlikte yerin dibine geçirdik. ALLAH\'ın dışında kendisine yardım edecek bir bölüğü yoktu; kazananlardan olmadı.'),
(3334, 28, 82, 'Bir önceki gün onun durumuna imrenenler, "Demek ki ALLAH kullarından dilediğine rızkı bol verir, dilediğine de kısar. ALLAH bize lütfetmeseydi bizi de batırırdı. Demek kafirler başarıya ulaşamazlar," demeye başladılar.'),
(3335, 28, 83, 'Bu ahiret yurdunu, yeryüzünde böbürlenmeyi ve bozgunculuğu istemiyenlere ayırdık. Sonuç erdemlilerindir.'),
(3336, 28, 84, 'Kim iyilikle gelirse kendisine ondan daha iyisi verilecektir. Kim kötülük ile gelirse, o kötülükleri işleyenlere ancak yaptıkları kadar bir karşılık verilecektir.'),
(3337, 28, 85, 'Sana Kuran\'ı özgüleyen, kuşkusuz seni kararlaştırılmış noktaya döndürecektir. De ki, "Kimin hidayet getirdiğini ve kimin açık bir sapıklık içinde olduğunu Rabbim iyi bilir."'),
(3338, 28, 86, 'Sana bu kitabın verileceğini ummazdın; ancak bu Rabbinden bir rahmettir. Kafirlere arka olma.'),
(3339, 28, 87, 'Sana indirildikten sonra seni ALLAH\'ın ayetlerinden saptırmasınlar. Rabbine çağır; ortak koşanlardan olma.'),
(3340, 28, 88, 'ALLAH ile birlikte hiç bir tanrıyı çağırma. O\'ndan başka tanrı yoktur. O\'nun varlığı hariç her şey yok olmaya mahkumdur. Yargı tümüyle O\'nundur ve siz O\'na döndürüleceksiniz.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'A. L. M.'),
(3342, 29, 2, 'İnsanlar, sadece "İnandık" demeleriyle, hiç sınanmadan bırakılacaklarını mı sanıyor?'),
(3343, 29, 3, 'Kendilerinden öncekileri sınadık. Elbette ALLAH doğrucuları ile yalancıları birbirinden ayıracaktır.'),
(3344, 29, 4, 'Kötülükleri işleyenler, bizi atlatacaklarını mı sanıyorlar? Ne kötü bir yargıda bulunuyorlar.'),
(3345, 29, 5, 'Kim ALLAH ile karşılaşmayı umuyorsa, (şunu bilsin ki) ALLAH\'la randevu mutlaka gerçekleşecektir. O, İşitendir, Bilendir.'),
(3346, 29, 6, 'Kim bizim için çaba gösterirse, kendisi için çaba göstermiş olur. ALLAH hiç kimseye muhtaç değildir.'),
(3347, 29, 7, 'İnanıp erdemli davrananların kötülüklerini elbette örteceğiz ve yapmış olduklarının daha iyisiyle onları ödüllendireceğiz.'),
(3348, 29, 8, 'Biz insana, anne ve babasına iyi davranmasını öğütlemişizdir. Ancak onlar, hakkında bilgin olmayan bir şeyi bana ortak koşman için seninle mücadele ederlerse ikisine de uyma. Hepinizin dönüşü banadır ve yaptıklarınız şeyler hakkında size bilgi vereceğim.'),
(3349, 29, 9, 'İnanıp erdemli davrananları erdemliler arasına sokacağız.'),
(3350, 29, 10, 'İnsanlardan, "ALLAH\'a inandık,"diyenlerden bazıları var ki, ALLAH yolunda sıkıntı çekince halkın zulüm ve işkencesini ALLAH\'ın cezası gibi sayar. Ama Rabbinden sana bir zafer gelse, "Biz sizinle birlikte idik,"derler. ALLAH herkesin en gizli düşüncelerini bilmez mi?'),
(3351, 29, 11, 'ALLAH elbette inananlarla ikiyüzlüleri açığa çıkaracaktır.'),
(3352, 29, 12, 'İnkarcılar inananlara, "Yolumuza uyun; günahlarınızı biz yükleniriz,"dediler. Oysa onların günahlarından hiç bir şeyi yüklenecek değillerdir; yalancıdırlar.'),
(3353, 29, 13, 'Gerçekte, kendi yüklerine ek olarak (sorumlu oldukları) başka yükleri de yüklenecekler ve uydurdukları şeylerden diriliş günü sorulacaklardır.'),
(3354, 29, 14, 'Nuh\'u, halkına göndermiştik ve onların arasında bin seneden elli yıl eksik kaldı. Ancak zulmettikleri için onları tufan yakaladı.'),
(3355, 29, 15, 'Onu ve gemi halkını kurtarıp herkese ibret yaptık.'),
(3356, 29, 16, 'İbrahimi de... Halkına demişti ki, "ALLAH\'a kulluk edin ve O\'nu saygıyla dinleyin. Bilirseniz, bu sizin için daha iyidir."'),
(3357, 29, 17, '"Yalan uydurup ALLAH\'tan başka putlara tapıyorsunuz. ALLAH\'ın dışında taptıklarınız size hiç bir rızık veremez. Öyleyse rızkınızı sadece ALLAH\'ın yanında arayın, O\'na kulluk edin ve O\'na şükredin; dönüşünüz O\'nadır."'),
(3358, 29, 18, 'Yalanlarsanız, sizden önceki toplumlar da yalanlamışlardı. Elçinin görevi ancak açıkça bildirmektir.'),
(3359, 29, 19, 'ALLAH\'ın yaratılışı nasıl başlatıp, nasıl tekrarladığını görmediler mi? Bu, elbette ALLAH için kolaydır.'),
(3360, 29, 20, 'De ki, "Yeryüzünü dolaşın ve yaratılışın nasıl başladığını görün." Sonra, yine ALLAH (ahiretteki) son yaratılışı başlatacaktır. ALLAH\'ın her şeye gücü yeter.'),
(3361, 29, 21, 'Dilediğini cezalandırır ve dilediğine acır; O\'na çevrileceksiniz.'),
(3362, 29, 22, 'Siz ne yeryüzünde ne de gökte bunu engelleyemezsiniz. Sizin ALLAH\'tan başka Sahibiniz ve Yardımcınız yoktur.'),
(3363, 29, 23, 'ALLAH\'ın ayet ve mucizelerini ve O\'nunla karşılaşmayı inkar edenler, Rahmetimden ümit kesenlerdir. Onlar acı bir cezayı haketmişlerdir.'),
(3364, 29, 24, 'Halkının karşılığı, "Onu öldürün, yahut yakın,"demelerinden başka bir şey olmadı. Ancak ALLAH onu ateşten kurtardı. İnanan bir toplum için bunda dersler vardır.'),
(3365, 29, 25, 'Dedi ki, "Sizin ALLAH\'ın yanında putlara tapmanız, sadece dünya hayatında aranızdaki dostluğu korumak amacıyladır. Fakat sonra, diriliş gününde birbirinizi inkar edersiniz, birbirinizi lanetlersiniz. Varacağınız yer ateştir ve orada yardımcı bulamazsınız."'),
(3366, 29, 26, 'Bunun üzerine ona Lut inandı ve, "Ben Rabbime göç ediyorum. Kuşkusuz O Üstündür, Bilgedir,"dedi.'),
(3367, 29, 27, 'O\'na (İbrahim\'e) İshak ve Yakub\'u bağışladık. Soyuna peygamberlik ve kitap verdik. Ödülünü bu dünyada verdik ve ahirette de erdemlilerle birlikte olacaktır.'),
(3368, 29, 28, 'Lutu da gönderdik. Halkına dedi ki: "Sizden önce hiç kimsenin işlemediği boyutta bir günahı işliyorsunuz."'),
(3369, 29, 29, '"Siz erkeklerle cinsel ilişki kuruyor, yolları kesiyor ve toplantılarınızda her kötülüğü işliyorsunuz." Halkının biricik karşılığı, "Doğrulardan isen ALLAH\'ın azabını getir bakalım,"demeleri oldu.'),
(3370, 29, 30, '"Rabbim, şu bozguncu topluluğa karşı bana zafer ver,"dedi.'),
(3371, 29, 31, 'Elçilerimiz İbrahim\'e müjdeyle vardıklarında, "Bu kentin (Sodom) halkını yok etmek üzereyiz. Çünkü oranın halkı zalim oldular,"dediler.'),
(3372, 29, 32, 'Dedi ki, "Ama orada Lut var." Onlar da, "Orada kimlerin bulunduğunu iyi biliyoruz. Onu ve ailesini kurtaracağız, fakat karısı hariç; o geride kalacaklardandır,"dediler.'),
(3373, 29, 33, 'Elçilerimiz Lut\'a vardıklarında elçilerimize yapılan muameleden dolayı fenalaştı, zor durumda kaldı. Kendisine dediler ki, "Korkma, üzülme. Biz seni ve aileni kurtaracağız. Karın hariç; o geride kalanlardan olmuştur."'),
(3374, 29, 34, '"Yoldan çıktıkları için, biz bu kentin üzerine gökten bir felaket indireceğiz."'),
(3375, 29, 35, 'Aklını kullanan bir toplum için apaçık bir ders olarak oradan bazı kalıntılar bıraktık.'),
(3376, 29, 36, 'Medyen\'e de kardeşleri Şuayb\'ı... "Halkım, ALLAH\'a kulluk edin, ahiret gününü arayın ve yeryüzünde bozgunculuk yaparak karışıklık çıkarmayın,"demişti.'),
(3377, 29, 37, 'Onu yalanladılar, nihayet kendilerini bir deprem yakaladı ve evlerinde diz üstü çöküp kaldılar.'),
(3378, 29, 38, 'Aynı şekilde, Ad ve Semud\'u da... Akibetleri, oturmuş oldukları yerlerden size belli olmaktadır. Şeytan işlerini onlara süslü göstererek onları yoldan saptırdı. Halbuki görüp anlayacak yeteneğe sahiptiler.'),
(3379, 29, 39, 'Karun, Firavun ve Haman\'ı da... Musa, onlara apaçık delillerle gitmişti; ancak onlar yeryüzünde büyüklük tasladılar. Atlatıp kaçamadılar.'),
(3380, 29, 40, 'Hepsini günahlarıyla yakaladık. Onlardan kimine çılgın bir fırtına gönderdik, kimini korkunç bir ses yakaladı, kimini yerin dibine geçirdik, kimini de boğduk. Onlara zulmeden ALLAH değildi; onlar kendi kendilerine zulmediyorlardı.'),
(3381, 29, 41, 'ALLAH\'ın dışında edindikleri velilerin örneği, bir ev edinen dişi örümceğin örneğine benzer. Gerçek şu ki, evlerin en çürüğü (en az güvenilir olanı) dişi örümceğin evidir, keşke bilselerdi.'),
(3382, 29, 42, 'ALLAH onların kendisinden başka taptıklarının aslında hiç bir şey olmadığını iyi bilir. O Üstündür, Bilgedir.'),
(3383, 29, 43, 'Bu örnekleri halk için veririz ve onları bilen kimselerden başkası düşünüp anlamaz.'),
(3384, 29, 44, 'ALLAH gökleri ve yeri belli bir amaç için yarattı. Bunda inananlar için bir kanıt vardır.'),
(3385, 29, 45, 'Sana vahyetmiş olduğumuz kitaptan oku ve namazı gözet. Çünkü namaz, iğrenç ve kötü şeylerden vazgeçirir. ALLAH\'ı (namaz yoluyla) anmak en önemlidir. ALLAH ne yaptığınızı bilir.'),
(3386, 29, 46, 'Kitap halkıyla, zalim olanları hariç en güzel bir biçimde tartışın ve "Bize indirilene inandık ve size de indirilene inandık. Bizim Tanrımız, sizin de Tanrınız birdir. Biz sadece O\'na teslim olduk,"deyin.'),
(3387, 29, 47, 'İşte sana bu kitabı indirdik. Kendilerine kitap vermiş olduklarımız ona inanacaklardır. Şunlardan (ümmilerden) da bazıları inanacaktır. Kafirlerden başkası ayetlerimizi reddetmez.'),
(3388, 29, 48, 'Sen daha önceki kitaplardan hiç birini okumuyordun ve onları elinle de yazmıyordun. Bu durumda, yanlışı savunanların kuşkulanması için bir bahane oluşacaktı.'),
(3389, 29, 49, 'Gerçekte o, kendilerine bilgi verilmiş olanların göğsünde apaçık ayetlerdir. Zalimlerden başkası ayetlerimizi reddetmez.'),
(3390, 29, 50, '"Ona Rabbinden bir mucize inmeli değil miydi?" dediler. De ki, "Mucizeler ALLAH\'ın katındadır. Ben sadece apaçık bir uyarıcıyım."'),
(3391, 29, 51, 'Bu kitabı sana indirmiş olmamız ve kendilerine okunması onlara yetmez mi? Bunda inanan bir toplum için bir rahmet ve öğüt vardır.'),
(3392, 29, 52, 'De ki, "Benimle sizin aranızda tanık olarak ALLAH yeter. Göklerde ve yerde ne varsa bilir. Yanlışa inanıp, ALLAH\'a inanmıyanlar, asıl zarara uğrayanlardır."'),
(3393, 29, 53, 'Azabı getirmen için sana meydan okuyorlar! Belirlemiş olduğumuz bir zaman olmasaydı, o azap onlara gelecekti. Onlara ansızın, haberleri olmadan gelecektir.'),
(3394, 29, 54, 'Sana azabı getirmen için meydan okuyorlar. Halbuki cehennem kafirleri kuşatmış bile.'),
(3395, 29, 55, 'Gün gelecek azap üstlerinden ve ayaklarının altından onları sarıp örtecek ve (Tanrı onlara), "Yaptıklarınızı tadın,"diyecektir.'),
(3396, 29, 56, 'Ey inanan kullarım, toprağım geniştir, sadece bana kulluk edin.'),
(3397, 29, 57, 'Her nefis ölümü tadacak ve sonra bize döndürüleceksiniz.'),
(3398, 29, 58, 'İnanan ve erdemli davrananları içinden ırmaklar akan cennetin köşklerine yerleştireceğiz; orada ebedi kalırlar. Çalışanların karşılığı ne güzeldir.'),
(3399, 29, 59, 'Onlar ki direndiler ve Rab\'lerine güvendiler.'),
(3400, 29, 60, 'Besinlerini taşıyamıyan nice yaratıklar vardır ki onları da sizi de ALLAH besler'),
(3401, 29, 61, 'Onlara, "Gökleri ve yeri kim yarattı? Güneşi ve ay\'ı kim emrinize verdi?" diye sorsan, "ALLAH,"diye karşılık verecekler. Öyleyse neden sapıyorlar?'),
(3402, 29, 62, 'ALLAH kullarından dilediğine rızkı arttırır ve kısar. ALLAH her şeyi Bilendir.'),
(3403, 29, 63, 'Onlara, "Gökten suyu kim indirip ölümünden sonra toprağı canlandırıyor?" diye sorsan, "ALLAH,"diyecekler. De ki, "Övgü ALLAH\'a aittir." Ancak çokları anlamaz.'),
(3404, 29, 64, 'Bu dünya hayatı bir eğlence ve oyundan başka bir şey değildir. Ahiret yurdu ise gerçek hayattır; bir bilselerdi.'),
(3405, 29, 65, 'Gemiye bindikleri zaman, dini sadece ALLAH\'a ait kılarak O\'nu çağırırlar. Ne zaman ki onları denizden kurtarırız ortak koşmaya tekrar başlarlar.'),
(3406, 29, 66, 'Bırak, kendilerine verdiklerimize nankörlük etsinler, zevklensinler. İleride bilecekler.'),
(3407, 29, 67, 'Çevrelerindeki insanlar kapılıp götürülürken, kutsal bölgeyi güvenli kıldık. Batıla inanıp ALLAH\'ın nimetini mi reddediyorlar?'),
(3408, 29, 68, 'Yalan uydurup ALLAH\'a yakıştırandan ve kendisine gelen gerçeği yalanlayandan daha zalim kim olabilir? Kafirler için cehennemde bir yer yok mu?'),
(3409, 29, 69, 'Uğrumuzda çaba gösterenleri yollarımıza ileteceğiz. ALLAH hiç kuşkusuz iyilik edenlerle beraberdir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'A. L. M.'),
(3411, 30, 2, 'Romalılar yenildiler,'),
(3412, 30, 3, '(Arapların yaşadığı) bölgeye en yakın yerde... Yenilgilerinden sonra yeneceklerdir.'),
(3413, 30, 4, 'Bir kaç sene içinde. İş, önünde sonunda ALLAH\'a aittir. O gün inananlar sevineceklerdir.'),
(3414, 30, 5, 'ALLAH\'ın zaferiyle... O dilediğini zafere ulaştırır. O Üstündür, Rahimdir.'),
(3415, 30, 6, 'ALLAH\'ın vermiş olduğu sözdür. ALLAH sözünü bozmaz; fakat insanların çoğu bilmez.'),
(3416, 30, 7, 'Onlar dünya hayatının dış yüzünü yanını bilirler. Ahiretten ise habersizdirler.'),
(3417, 30, 8, 'Kendi kendilerine hiç düşünmediler mi ki ALLAH gökleri, yeri ve aralarındakileri bir amaç için ve belli bir süre için yaratmıştır. Buna rağmen, insanların çoğunluğu Rab\'leriyle karşılaşmayı inkar etmektedirler.'),
(3418, 30, 9, 'Yeryüzünde dolaşıp, kendilerinden öncekilerin sonunun nasıl olduğuna bakmazlar mı? Onlardan daha güçlü idiler, toprağı işlediler ve onlardan daha fazla üretimde bulundular. Elçileri onlara apaçık delillerle gitmişlerdi. Onlara zulmeden ALLAH değildi; onlar kendi kendilerine zulmediyorlardı.'),
(3419, 30, 10, 'Kötülük işleyenlerin sonu kötü oldu. Çünkü ALLAH\'ın ayet ve mucizelerini yalanladılar ve onlarla eğlendiler.'),
(3420, 30, 11, 'Yaratılışı başlatan, sonra onu tekrarlayan ALLAH\'tır. Ve sonunda siz O\'na döndürüleceksiniz.'),
(3421, 30, 12, 'Saatin gerçekleştiği gün suçlular şoke olurlar.'),
(3422, 30, 13, 'Koştukları ortaklarından da kendilerine şefaat eden hiç bir kimse çıkmaz; ortaklarını inkar ederler.'),
(3423, 30, 14, 'Saatin gerçekleştiği gün gruplara ayrılırlar.'),
(3424, 30, 15, 'İnanıp erdemli davrananlar ise bir bahçe içinde neşelendirilirler.'),
(3425, 30, 16, 'İnkar edip, ayetlerimizi ve ahiret buluşmasını yalanlayanlar da azabın içine sokulurlar.'),
(3426, 30, 17, 'Öyleyse geceleyin bir köşeye çekildiğiniz ve sabahleyin kalktığınız zaman ALLAH\'ı övgüyle anın.'),
(3427, 30, 18, 'Akşam ve öğle tatilinde göklerde ve yerde tüm övgüler O\'nadır.'),
(3428, 30, 19, 'Ölüden diriyi çıkarır, diriden de ölüyü çıkarır ve ölümünden sonra yeryüzünü diriltir; siz işte böyle çıkarılacaksınız.'),
(3429, 30, 20, 'Sizi bir topraktan yaratması ve sonra yayılıp çoğalan insanlar olmanız O\'nun ayetlerindendir.'),
(3430, 30, 21, 'Kendileriyle rahatlayıp huzur bulasınız diye sizin için türünüzden eşler yaratması ve aranıza dostluk sevgisi ve merhamet koyması O\'nun ayetlerindendir. Düşünen bir toplum için bunda işaretler vardır.'),
(3431, 30, 22, 'Göklerin ve yerin yaratılışı, dillerinizin ve renklerinizin farklılığı da O\'nun ayetlerindendir. Bilenler için bunda işaretler vardır.'),
(3432, 30, 23, 'Geceleyin ve gündüzün uyumanız ve O\'nun lütfundan aramanız da O\'nun ayetlerindendir. Dinleyen bir toplum için bunda işaretler vardır.'),
(3433, 30, 24, 'Korku ve umut kaynağı olarak size şimşeği göstermesi ve gökten bir su indirerek ölümünden sonra onunla yeri diriltmesi de O\'nun ayetlerindendir. Aklını kullanan bir toplum için bunda işaretler vardır.'),
(3434, 30, 25, 'Göğün ve yerin O\'nun buyruğuyla ayakta durması da O\'nun ayetlerindendir. Sonra sizi yerden bir tek çağrıyla çağırdığı vakit siz çıkıverirsiniz.'),
(3435, 30, 26, 'Göklerde ve yerde bulunan herkes O\'na aittir; hepsi O\'na boyun eğmektedir.'),
(3436, 30, 27, 'Yaratılışı başlatıp tekrarlayan da O\'dur; bu O\'na çok kolaydır. Göklerde ve yerde en yüce örnek O\'na aittir. O Üstündür, Bilgedir.'),
(3437, 30, 28, 'İşte size kendi içinizden bir örnek veriyor: Emriniz altındaki kimseleri, size verdiğimiz rızıklarda size eşit ortaklar olarak kabul eder misiniz? Birbirinizden çekindiğiniz gibi onlardan da çekinir misiniz? Aklını kullanan bir toplum için ayetleri b öyle detaylı açıklarız.'),
(3438, 30, 29, 'Aslında, zalimler bilgisizce kendi kafalarına uydular. ALLAH\'ın saptırdığını kim doğruya iletebilir? Onlara hiç kimse yardım etmez.'),
(3439, 30, 30, 'Bir tek Tanrıcı olarak kendini dine adamalısın. Nitekim, ALLAH insanları böyle bir yaratılış ile donatarak yaratmıştır. ALLAH\'ın yaratışında değişiklik olmaz. Bu, tam yetkin bir dindir, fakat insanların çoğu bilmez.'),
(3440, 30, 31, 'O\'na yönelin; O\'nu sayıp dinleyin. Namazı gözetin ve ortak koşanlardan olmayın.'),
(3441, 30, 32, 'Onlar ki dinlerini parçaladılar ve mezhep mezhep oldular. Her parti kendine ait (imam ve kitap) larla sevinip övünmektedir.'),
(3442, 30, 33, 'Halka bir zarar dokunduğu gün tümüyle Rab\'lerine yönelerek dua ederler. Fakat sonra, kendilerine bir rahmet tattırınca bazıları Rab\'lerine ortak koşar.'),
(3443, 30, 34, 'Böylece kendilerine verdiklerimize karşı nankörlük ederler. Zevklenin bakalım; ileride bileceksiniz.'),
(3444, 30, 35, 'Yoksa onlara, ortak koşmalarını destekleyen bir delil mi indirdik?'),
(3445, 30, 36, 'Halka bir rahmet tattırdığımız vakit onunla sevinirler. Yaptıklarının karşılığı olarak kendilerine bir kötülük dokunsa derhal umutsuzluğa düşerler.'),
(3446, 30, 37, 'ALLAH\'ın, dilediğine rızkı arttırdığını ve kıstığını görmezler mi? İnanan bir toplum için bunda dersler vardır.'),
(3447, 30, 38, 'Öyleyse yakınlığı bulunanlara haklarını ver, yoksula ve yolcuya da... ALLAH\'ın rızasını dileyenler için bu daha iyidir; onlar başarıya ulaşanlardır.'),
(3448, 30, 39, 'Halkın malları içinde artması için verdiğiniz tefecilik parası ALLAH\'ın yanında artmaz. Ancak, ALLAH\'ın rızasını dileyerek verdiğiniz bir zekata gelince, onu verenler yatırımlarını katlarlar.'),
(3449, 30, 40, 'ALLAH sizi yarattı, sonra size rızık verdi, sonra sizi öldürür, sonra sizi diriltir. Ortaklarınızdan hangisi bunlardan birisini yapabilir? Övgüler O\'na aittir. Onların ortak koştuklarından çok yücedir.'),
(3450, 30, 41, 'Halkın elleriyle işledikleri yüzünden karada ve denizde felaketler yaygınlaştı. Böylece, (kötülükten) dönerler diye yaptıklarının bir kısmını onlara tattırıyor.'),
(3451, 30, 42, 'De ki, "Yeryüzünde dolaşın da öncekilerin sonunun nasıl olduğuna bakın." Çokları ortak koşanlardı.'),
(3452, 30, 43, 'Öyleyse, ALLAH\'tan, geri çevrilmesi olanaksız bir gün gelmeden önce kendini dosdoğru olan dine ada. O gün şoke olurlar.'),
(3453, 30, 44, 'Kim inkar ederse, inkarı kendi aleyhinedir. Kimler de erdemli davranırlarsa kişiliklerini güçlendirmiş ve geliştirmiş olurlar.'),
(3454, 30, 45, 'Çünkü O, inananları ve erdemli davrananları lütfundan ödüllendirecektir. O, inkarcıları sevmez.'),
(3455, 30, 46, 'Size rahmetini tattırmak, emriyle gemileri yürütmesi, lütfunu (ticaret yoluyla) aramanız ve şükretmeniz için rüzgarları müjdeci olarak göndermesi de O\'nun ayetlerindendir.'),
(3456, 30, 47, 'Senden önce elçileri halklarına apaçık delillerle gönderdik. Suç işleyenleri de cezalandırdık. İnananlara yardım etmek görevimizdir.'),
(3457, 30, 48, 'ALLAH rüzgarları göndererek bulutları sürer, onları dilediği gibi gökte yayıp üstüste yığar ve sen de onların arasından yağmurun çıktığını görürsün. Kullarından dilediğinin üzerine onu yağdırınca hemen sevinirler.'),
(3458, 30, 49, 'Halbuki onlar, onun kendilerine yağmadan önce umutsuzdular.'),
(3459, 30, 50, 'ALLAH\'ın rahmetinin ürünlerine bak; ölümünden sonra toprağı nasıl da diriltiyor. Bunun gibi ölüleri de diriltecektir. O her şeye gücü yetendir.'),
(3460, 30, 51, 'Ancak, O bir fırtına gönderir de onlar (ekinlerin kuruyup) sarardığını görseler hemen inkar ve nankörlük etmeye başlarlar.'),
(3461, 30, 52, 'Sen ne ölülere işittirebilirsin, ne de arkalarını dönüp giden sağırlara çağrıyı duyurabilirsin.'),
(3462, 30, 53, 'Körü de sapıklığından kurtarıp yola getiremezsin. Sen ancak ayetlerimize inananlara işittirebilirsin ki onlar hemen teslim olurlar.'),
(3463, 30, 54, 'ALLAH sizi zayıf olarak yarattı, zayıflıktan sonra size güç verdi, güçten sonra zayıflık ve ağarmış saçlar verdi. O dilediğini yaratır. O Bilendir, Güçlüdür.'),
(3464, 30, 55, 'Saat (dünyanın sonu) gerçekleştiği zaman, suçlular, (dünyada) ancak bir saat kaldıklarına and içerler. Onlar işte böyle yanılıyorlardı.'),
(3465, 30, 56, 'Kendilerine bilgi ve inanç verilenler, "Siz ALLAH\'ın yasasına göre, diriliş gününe kadar kaldınız. Bu, diriliş günüdür; fakat siz bilemediniz," diyeceklerdir.'),
(3466, 30, 57, 'O gün, zalimlerin özrü kendilerine bir yarar sağlamıyacaktır; kendilerini temize çıkarmaları da istenmez.'),
(3467, 30, 58, 'Biz bu Kuran\'da halka her türlü örneği verdik. Onlara bir mucize getirsen, kafirler, "Siz yanılanlarsınız," diyeceklerdir.'),
(3468, 30, 59, 'ALLAH bilmeyenlerin kalplerini işte böyle damgalar.'),
(3469, 30, 60, 'Öyleyse sabret. ALLAH\'ın sözü gerçektir. Kesin inanca sahip olmayanlar seni gevşekliğe sürüklemesin.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'A. L. M.'),
(3471, 31, 2, 'Bun (harf) lar bu bilge kitabın mucizeleridir.'),
(3472, 31, 3, 'Güzel davrananlara bir yol gösterici ve bir rahmettir.'),
(3473, 31, 4, 'Onlar ki namazı gözetirler, zekatı verirler; ahiret hakkında da kuşkuları yoktur.'),
(3474, 31, 5, 'Rab\'leri tarafından gösterilen bir yolu izleyenler ve kazananlar bunlardır.'),
(3475, 31, 6, 'İnsanlardan bazısı var ki, halkı bilgisizce ALLAH\'ın yolundan saptırmak ve onu hafife almak için temelsiz hadislere sarılırlar. Onlar için aşağılayıcı bir azap vardır.'),
(3476, 31, 7, 'Kendisine ayetlerimiz okunduğu zaman, sanki onu hiç işitmemiş gibi, sanki kulaklarında ağırlık varmış gibi büyüklenerek ardını döner.'),
(3477, 31, 8, 'İnanıp erdemli davrananlar için nimet cennetleri vardır.'),
(3478, 31, 9, 'Orada ebedi kalırlar. ALLAH\'ın sözü gerçektir. O Üstündür, Bilgedir.'),
(3479, 31, 10, 'Gökleri, gördüğün bir direk olmadan yaratmıştır. Ve sarsılmamanız için yeryüzüne denge sağlayıcılar atıp yerleştirdi ve orada her çeşit yaratığı yaydı. Gökten bir su yağdırdık ve orada her güzel çifti bitirdik.'),
(3480, 31, 11, 'Bu ALLAH\'ın yaratışıdır. O\'ndan başkasının neler yarattığını bana gösterin. Zalimler, gerçekten apaçık bir sapıklık içindedirler.'),
(3481, 31, 12, 'Lokman\'a bilgelik verdik: "ALLAH\'a şükretmelisin." Kim şükrederse kendisi için şükreder; kim nankörlük ederse, elbette ALLAH muhtaç değildir, Çok Övülendir.'),
(3482, 31, 13, 'Lokman oğluna öğüt verirken şunu demişti: "Sevgili oğlum, ALLAH\'a ortak koşma, kuşkusuz şirk (Tanrı\'ya ortak koşmak) büyük bir zulümdür."'),
(3483, 31, 14, 'Biz insana ana babasını öğütledik. Annesi onu büyük bir güçlükle taşır. Sütten kesilmesi iki yıl içinde olur. Bana ve ana babana teşekkür etmelisin. Dönüş banadır.'),
(3484, 31, 15, 'Hakkında bilgin olmayan bir şeyi bana ortak koşman için seninle mücadele ederlerse ikisine de uyma. Ancak dünyada onlara iyi davranmalısın. Bana yönelenlerin yoluna uy. Sonunda dönüşünüz banadır ve yaptığınız herşeyi size bildireceğim.'),
(3485, 31, 16, '"Sevgili oğlum, hardal tanesi ağırlığınca bir şey, ister bir kayanın içinde bulunsun, ister göklerde veya yerde olsun, ALLAH onu getirir. ALLAH Latiftir, Haberdardır.'),
(3486, 31, 17, '"Sevgili oğlum, namazı gözet, iyiliği emret, kötülükten menet ve başına gelene sabret. Bunlar temel davranışlardandır."'),
(3487, 31, 18, '"Büyüklük taslayarak halkı küçümseme, yeryüzünde de böbürlenerek dolaşma. ALLAH kendini beğenip övüneni sevmez."'),
(3488, 31, 19, '"Yürüyüşünde gösterişten kaçın ve sesini de kıs. En çirkin ses eşeklerin sesidir."'),
(3489, 31, 20, 'ALLAH\'ın göklerde ve yerde ne varsa emrinize verdiğini, nimetlerini hem açık ve hem gizli olarak üzerinize yağdırdığını görmez misiniz? Halktan bazıları vardır ki ALLAH hakkında bilgisizce, ne bir kılavuzu, ne de bir kitabı olmadan tartışır.'),
(3490, 31, 21, 'Kendilerine, "ALLAH\'ın indirdiğine uyun," denildiği zaman, "Hayır, biz atalarımızı üzerinde bulduğumuz yolu izleriz," derler.? eytan kendilerini alevli ateşin azabına çağırıyor olsada mı?'),
(3491, 31, 22, 'Kim, güzel davranarak tümüyle ALLAH\'a yönelirse en sağlam bağa sarılmıştır. Tüm işleri ALLAH kontrol eder.'),
(3492, 31, 23, 'İnkar edenlerin inkarları seni üzmesin. Onların dönüşü bizedir ve yapmış olduklarını onlara bildireceğiz. ALLAH gizli düşünceleri iyi bilendir'),
(3493, 31, 24, 'Onları biraz yaşatırız, sonra da ağır bir cezaya süreriz.'),
(3494, 31, 25, 'Onlara, "Gökleri ve yeri kim yarattı?" diye sorarsan, "ALLAH," diyecekler. De ki, "Övgü ALLAH\'adır." Ne var ki çokları bilmiyor.'),
(3495, 31, 26, 'Göklerde ve yerde olan herşey ALLAH\'ındır. Kuşkusuz ALLAH Zengindir, Övülendir.'),
(3496, 31, 27, 'Yeryüzünde bulunan tüm ağaçlar kalem olsa, denizlere yedi deniz eklenerek kullanılsa ALLAH\'ın kelimeleri tükenmez. ALLAH Üstündür, Bilgedir.'),
(3497, 31, 28, 'Hepinizin yaratılışı ve dirilişi bir tek kişininki gibidir. ALLAH Dinleyendir, Görendir.'),
(3498, 31, 29, 'ALLAH\'ın geceyi gündüzün içine soktuğunu, gündüzü de gecenin içine soktuğunu, güneşi ve ayı kontrolü altında tuttuğunu, herbirinin belirlenmiş bir süreye kadar akıp gitmekte olduğunu ve ALLAH\'ın onların yaptıklarından haberdar olduğunu görmez misin?'),
(3499, 31, 30, 'Çünkü ALLAH gerçektir, O\'nun dışında çağırdıkları batıldır ve ALLAH En Yüce ve En Büyüktür.'),
(3500, 31, 31, 'Size bazı ayetlerini göstermek için, ALLAH\'ın lütfuyla gemilerin denizde akıp gittiğini görmez misin? Her sabreden ve şükreden için bunda dersler vardır.'),
(3501, 31, 32, 'Onları koca dalgalar sardığında, dini sadece ALLAH\'a has kılarak O\'na yalvarmaya başlarlar. Onları karaya çıkarıp kurtardığımız zaman bir kısmı orta bir yol tutar. Hain nankörlerden başkası ayetlerimizi reddetmez.'),
(3502, 31, 33, 'Ey halk, Rabbinizi dinleyin, babanın çocuğuna yardım edemiyeceği ne de çocuğun babaya yardım edemiyeceği günden korkun. ALLAH\'ın sözü gerçektir. Dünya hayatı sizi aldatmasın; kandırıcılar sizi ALLAH ile aldatmasınlar.'),
(3503, 31, 34, 'Saatin (dünyanın son saatinin) bilgisi ALLAH\'ın yanındadır. Yağmuru O yağdırır ve rahimlerde ne varsa bilir. Hiç kimse kendisine yarın ne olacağını bilmez, yine hiç kimse nerede öleceğini bilmez. ALLAH Bilendir, Haberdardır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'A. L. M.'),
(3505, 32, 2, 'Bu kitabın indirilişi, hiç kuşkusuz, evrenlerin Rabbi tarafındandır.'),
(3506, 32, 3, '"Onu uydurdu" mu diyorlar? Halbuki bu, senden önce kendilerine bir uyarıcı ulaşmamış bir toplumu, belki yola gelirler diye uyarman için Rabbinden gelen bir gerçektir.'),
(3507, 32, 4, 'ALLAH gökleri, yeri ve aralarındakileri altı günde yaratan ve sonra tüm otoriteyi kurandır. Sizin için O\'ndan ayrı bir veli (sahip) ve şefaatçı (aracı) yoktur. Öğütten anlamaz mısınız?'),
(3508, 32, 5, 'Gökten yere kadar bütün işleri O kontrol eder. Sonra sizin saydığınızdan bin yıla eşit bir gün içinde kendisine yükselirler.'),
(3509, 32, 6, 'Gizliyi de açığı da bilen, Üstün ve Rahim işte böyledir.'),
(3510, 32, 7, 'O yarattığı her şeyi mükemmel hale soktu. İnsanın yaratılışına balçıktan başladı.'),
(3511, 32, 8, 'Sonra onun soyunu bayağı bir sudan devam ettirdi.'),
(3512, 32, 9, 'Sonra onu biçimlendirip ona ruhundan üfledi. Size işitme ve görme yeteneği ile beyinler verdi; siz pek seyrek şükrediyorsunuz.'),
(3513, 32, 10, 'Ve, "Biz toprağa karışıp kaybolduktan sonra yeniden mi yaratılacağız?" dediler. Doğrusu, onlar Rab\'leri ile kavuşmayı inkar edenlerdir.'),
(3514, 32, 11, 'De ki, "Üzerinize görevlendirilen ölüm meleği canınızı alacak ve sonra Rabbinize döndürüleceksiniz."'),
(3515, 32, 12, 'Suçluları, Rab\'leri huzurunda başlarını öne eğmiş durumda iken bir görseydin: "Rabbimiz, gördük ve işittik. Bizi döndür de erdemli davranalım. şimdi biz kesin olarak inandık."'),
(3516, 32, 13, 'Dileseydik herkese hidayetini verirdik. Ancak, cinlerin ve insanların bir kısmıyla cehennemi topluca dolduracağıma dair sözüm gerçekleşmiştir.'),
(3517, 32, 14, 'Bugünkü karşılaşmanızı önemsememenizin sonucunu tadın, biz de sizi önemsemeyiz. Yaptıklarınızın bir sonucu olarak ebedi azabı tadın.'),
(3518, 32, 15, 'Ayet ve mucizelerimize gerçekten inananlar, onları işittikleri zaman secdeye varırlar ve büyüklük taslamadan Rab\'lerini yüceltirler.'),
(3519, 32, 16, 'Yataklarından kalkıp Rab\'lerine saygı ve umutla yalvarırlar. Kendilerine verdiğimiz nimetlerden de verirler.'),
(3520, 32, 17, 'Yaptıklarının karşılığı olarak kendilerini ne kadar büyük bir neşe ve mutluluk beklediğini hiç kimse bilemez.'),
(3521, 32, 18, 'İnanan biri yoldan çıkmış biri gibi midir? Elbette eşit olamazlar.'),
(3522, 32, 19, 'İnanıp erdemli davrananlar için cennet konakları vardır. Yaptıklarının bir karşılığı olarak.'),
(3523, 32, 20, 'Yoldan çıkmış olanların konağı ise ateştir. Oradan her çıkmak istediklerinde, tekrar oraya çevrilirler. Onlara, "Yalanlamakta olduğunuz ateş azabını tadın," denir.'),
(3524, 32, 21, 'Belki (ibret alıp) dönerler diye büyük azaptan önce biz onlara yakın (dünya) azabı tattırırız.'),
(3525, 32, 22, 'Kendisine Rabbinin ayetleri hatırlatıldıktan sonra onlardan yüz çevirenden daha zalim kim olabilir? Biz suçlulardan elbette öc alacağız.'),
(3526, 32, 23, 'Musa\'ya kitabı verdik; bu konuda hiçbir kuşkun olmasın. Onu İsrail oğulları için bir rehber yaptık.'),
(3527, 32, 24, 'Sabrettikleri ve ayetlerimize kesin olarak inandıkları zaman, emrimiz doğrultusunda yol gösteren önderler atamıştık içlerinden.'),
(3528, 32, 25, 'Rabbin, ayrılığa düştükleri konularda onların arasında diriliş günü karar verecek olandır.'),
(3529, 32, 26, 'Kendilerinden önce nice nesilleri yok etmiş olmamız üzerinde hiç düşünmediler mi? Ki şimdi onların yurtlarında dolaşıyorlar. Bunda dersler ve işaretler vardır. İşitmezler mi?'),
(3530, 32, 27, 'Kıraç toprağa suyu sürerek onların ve çiftlik hayvanlarının yediği ekinleri çıkardığımızı kavramazlar mı? Görmezler mi?'),
(3531, 32, 28, '"O zafer ne zaman gerçekleşecek, doğru sözlüyseniz?" diye meydan okuyorlar.'),
(3532, 32, 29, 'De ki, "Zafer günü, inkarcılara inanmaları bir yarar sağlamıyacaktır; kendilerine bir başka şans da tanınmayacaktır."'),
(3533, 32, 30, 'Öyleyse onlardan yüz çevir ve bekle; onlar da beklemektedirler.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'Ey peygamber, ALLAH\'ı gözet, inkarcılara ve ikiyüzlülere uyma. ALLAH Bilendir, Bilgedir.'),
(3535, 33, 2, 'Rabbinden sana vahyedilene uy. ALLAH yaptığınız her şeyden haber almaktadır.'),
(3536, 33, 3, 'ALLAH\'a güven. Koruyucu olarak ALLAH yeter'),
(3537, 33, 4, 'ALLAH hiç bir adamın içine iki kalp koymamıştır. Annenize benzeterek kendinize haram kıldığınız eşlerinizi annelerinize çevirmemiş, evlatlıklarınızı da öz çocuklarınız yapmamıştır. Tüm bunlar, ağzınızdan çıkan sözlerinizden ibarettir. ALLAH doğruyu söylüyor ve O doğru yola iletir.'),
(3538, 33, 5, 'Onları, öz babalarıyla bağlarını kesmeyecek biçimde çağırın. Bu davranış, ALLAH katında daha doğrudur. Babalarını bilmiyorsanız, o zaman onlar sizin din kardeşlerinizdir ve ailenizin bireyleridir. Kasıtlı olarak yaptıklarınız bir yana, yanılarak yaptığınız şeylerde size bir sorumluluk yoktur. ALLAH Bağışlayandır, Rahimdir.'),
(3539, 33, 6, 'Peygamber, inananlara, kendilerinden daha yakındır ve hanımları ise anneleridir. ALLAH\'ın kitabına göre, inananlar, göçedenlerden kendilerine akraba olanlara daha öncelikli davranmalıdır. Ancak dostlarınıza iyilik yapmanız hariç. Bunlar, bu kitapta yasallaşmıştır.'),
(3540, 33, 7, 'Nebilerden (peygamberlerden) misak (söz) almıştık. Senden, Nuh\'dan, İbrahim\'den, Musa\'dan ve Meryem oğlu İsa\'dan... Onlardan sapasağlam bir söz almıştık.'),
(3541, 33, 8, 'Böylece doğrulardan doğruluklarını soracağız. İnkarcılar içinse acı bir azap hazırlamıştır.'),
(3542, 33, 9, 'Ey inananlar, size ordular saldırdığı zaman, üzerlerine bir rüzgar ve görmediğiniz orduları gönderen ALLAH\'ın size yaptığı iyiliği hatırlayın. ALLAH yaptığınız her şeyi Görendir.'),
(3543, 33, 10, 'Üstünüzden ve altınızdan size saldırmışlardı. Gözler dönmüştü, kalpler ağızlara gelmişti ve siz ALLAH hakkında çeşitli kuşkular besliyordunuz.'),
(3544, 33, 11, 'İnananlar orada denenmiş ve şiddetle sarsılmışlardı.'),
(3545, 33, 12, 'İkiyüzlüler ve kalplerinde hastalık bulunan kimseler, "ALLAH\'ın ve elçisinin bize verdiği söz boş bir hayalden başka bir şey değilmiş!" diyorlardı.'),
(3546, 33, 13, 'Onlardan bir grup ise, "Yesrib halkı, artık tutunamazsınız; geri dönün," diyordu. Onlardan diğer grup ise, evleri korunduğu halde, "Evlerimiz korumasız kaldı," diyerek peygamberden izin istiyorlardı. Tüm amaçları kaçmak idi.'),
(3547, 33, 14, 'Orası işgal edilse ve düşmana katılmaları istenseydi pek tereddüt etmeden onlara katılırlardı.'),
(3548, 33, 15, 'Daha önce de, arkalarını dönüp kaçmayacaklarına dair ALLAH\'a söz vermişlerdi. ALLAH\'a verilmiş her söz sorulacaktır.'),
(3549, 33, 16, 'De ki, "Ölümden veya savaştan kaçmak size yarar sağlamıyacak. Kaçsanız da pek fazla yaşamıyacaksınız."'),
(3550, 33, 17, 'De ki, "ALLAH sizin için bir kötülük dilese veya bir rahmet dilese O\'na karşı sizi kim koruyacak? Onlar ALLAH\'tan başka bir sahip ve yardımcı bulamayacaklardır.'),
(3551, 33, 18, 'ALLAH, içinizden alıkoyanları ve kardeşlerine, "Bize gelin," diyenleri bilmektedir. Onlar ender olarak savunmaya katılırlar.'),
(3552, 33, 19, 'Sizinle ilişkilerinde pek cimridirler. Tehlike geldiğinde, üzerine ölüm baygınlığı çökmüş kimse gibi gözleri korkudan dönmüş bir halde sana baktıklarını görürsün. Tehlike geçince de, mala düşkünlük göstererek sizi sivri dillerle incitirler. Bu tip kimseler inanmış değillerdir. Bu yüzden ALLAH onların işlerini boşa çıkarır. Bu, ALLAH için kolaydır.'),
(3553, 33, 20, 'Partilerin gitmediğini sanıyorlar. Partiler tekrar gelse kendilerinin çöldeki bedevi Araplar arasında olmayı ve sizin haberlerinizi uzaktan sormayı dilerlerdi. Aranızda olsalardı dahi pek az savaşırlardı.'),
(3554, 33, 21, 'ALLAH\'ı ve ahiret gününü arzulayan ve ALLAH\'ı sıkça ananlarınız için ALLAH\'ın elçisinde güzel bir örnek vardır.'),
(3555, 33, 22, 'İnananlar, partileri (saldırıya hazır) görünce, "İşte bu, ALLAH\'ın ve elçisinin bize söz verdiğidir. ALLAH ve elçisi doğru söylemiştir," dediler. Bu (tehlikeli durum), onların ancak inançlarını ve teslimiyetlerini güçlendirdi.'),
(3556, 33, 23, 'İnananlardan öyle kimseler var ki ALLAH\'a vermiş oldukları sözü gerçekleştirmişlerdir. Onlardan kimi adağını yerine getirdi (şehid oldu), kimi de hazır bekliyor. Hiç bir vakit kararsızlığa düşmediler.'),
(3557, 33, 24, 'ALLAH böylece doğruları doğrulukları ile ödüllendirir. İkiyüzlüleri de dilerse cezalandırır veya tevbelerini kabul eder. ALLAH Bağışlayandır, Rahimdir.'),
(3558, 33, 25, 'ALLAH inkarcıları öfkeleriyle birlikte elleri boş olarak geri çevirdi. ALLAH böylece inananları savaştan esirgedi. ALLAH Güçlüdür, Üstündür.'),
(3559, 33, 26, 'Ve kitap halkından onlara destek olanları kalelerinden indirdi ve yüreklerine korku düşürdü. Bir kısmını öldürüyor, bir kısmını da tutsak alıyordunuz.'),
(3560, 33, 27, 'Sizi onların toprağına, evlerine, paralarına ve henüz ayağınızı dahi basmamış olduğunuz topraklara mirasçı kıldı. ALLAH her şeye gücü yetendir.'),
(3561, 33, 28, 'Peygamber, hanımlarına de ki, "Siz dünya hayatının süsünü istiyorsanız, gelin size bağışta bulunayım ve sizi güzellikle bırakayım."'),
(3562, 33, 29, '"Yok, siz ALLAH\'ı, elçisini ve ahiret yurdunu istiyorsanız, ALLAH sizden güzel davrananlara büyük bir ödül hazırlamıştır."'),
(3563, 33, 30, 'Ey peygamberin hanımları, sizden kim büyük ve açık bir günah işlerse onun cezası ikiye katlanır. ALLAH için elbette bu kolaydır.'),
(3564, 33, 31, 'Sizden kim ALLAH\'ın elçisine uyar ve erdemli davranırsa, onun da ödülünü iki kat veririz ve onun için bol bir rızık hazırlamışızdır.'),
(3565, 33, 32, 'Ey peygamberin hanımları, siz herhangi bir kadın gibi değilsiniz. Erdemliyseniz, işveli konuşmayın ki kalbinde hastalık bulunan birileri size yönelmesin. Güzel ve normal biçimde konuşun.'),
(3566, 33, 33, 'Evlerinizde oturun ve eski cahiliyye dönemindeki gibi halkla fazla içiçe olmayın. Namazı gözetin, zekatı verin ve ALLAH ve elçisine uyun. Ey Kabe\'nin çevresinde oturanlar, ALLAH sizden günahları gidermek ve sizi tamamıyla arındırmak istiyor.'),
(3567, 33, 34, 'Evlerinizde okunan ALLAH\'ın ayetlerini ve hikmeti hatırlayın. ALLAH Latiftir, Haberdardır.'),
(3568, 33, 35, 'Müslüman erkekler ve müslüman kadınlar, inanan erkekler ve inanan kadınlar, söz dinleyen erkekler ve söz dinleyen kadınlar, doğru sözlü erkekler ve doğru sözlü kadınlar, sabreden erkekler ve sabreden kadınlar, saygılı erkekler ve saygılı kadınlar, yardımsever erkekler ve yardımsever kadınlar, oruç tutan erkekler ve oruç tutan kadınlar, iffetli erkekler ve iffetli kadınlar, ALLAH\'ı çok anan erkekler ve çok anan kadınlar; işte ALLAH onların hepsine bağışlanma ve büyük bir ödül hazırlamıştır.'),
(3569, 33, 36, 'ALLAH ve elçisi bir işte hüküm verdiği zaman, hiçbir inanan erkek ve kadın o işte seçim hakkına sahip değildir. Kim ALLAH\'a ve elçisine isyan ederse açık bir biçimde sapmış olur.'),
(3570, 33, 37, 'ALLAH tarafından kendisine iyilik yapılan ve senin de iyilikte bulunduğun kişiye, "Karını tut ve ALLAH\'ı gözet," diyordun. Böylece ALLAH\'ın açığa vuracağı bir şeyi gizliyordun. ALLAH\'tan çekinmen gerekirken halktan çekiniyordun. Zeyd eşiyle ilişiğini kestiğinde biz seni onunla evlendirmiştik ki, inananlar, evlatlıkları eşleriyle ilişiklerini kestiklerinde onlarla evlenmekte güçlükle karşılaşmasın. ALLAH\'ın buyruğu yerine getirilmelidir.'),
(3571, 33, 38, 'ALLAH\'ın kendisine yasallaştırdığı bir konuda peygambere herhangi bir engel yoktur. Bu, öteden beri, gelmiş geçmişlere uygulanan ALLAH\'ın sünneti (yasası) dır. ALLAH\'ın emri, belirlenmiş ve kesinleşmiştir.'),
(3572, 33, 39, 'ALLAH\'ın elçiliğini duyuranlar sadece O\'nu sayarlar ve ALLAH\'tan başka hiç kimseden çekinmezler. Hesap sorucu olarak ALLAH yeter.'),
(3573, 33, 40, 'Muhammed, adamlarınızdan herhangi birisinin babası olmadı. Ancak o ALLAH\'ın elçisi (rasulü) ve son peygamber (nebi) oldu. ALLAH her şeyi iyi bilir.'),
(3574, 33, 41, 'Ey inananlar ALLAH\'ı çok sık olarak anın.'),
(3575, 33, 42, 'O\'nu sabah akşam yüceltin.'),
(3576, 33, 43, 'O ki sizi karanlıklardan ışığa çıkarmak için melekleri ile birlikte sizi destekler. O, inananlara karşı Rahimdir.'),
(3577, 33, 44, 'O\'nunla karşılaştıkları gün dirlik dilekleri, Selam (Barış/Güvenlik)\'tır. Onlara cömert bir ödül hazırlamıştır.'),
(3578, 33, 45, 'Ey peygamber, biz seni bir tanık olarak gönderdik, bir müjdeleyici ve bir uyarıcı,'),
(3579, 33, 46, 'ALLAH\'a, O\'nun izniyle çağıran bir davetçi ve aydınlatıcı bir ışık olarak...'),
(3580, 33, 47, 'İnananlara, ALLAH\'tan büyük bir lütfa ulaşacaklarını müjdele.'),
(3581, 33, 48, 'İnkarcılara ve ikiyüzlülere uyma, eziyetlerine aldırma ve ALLAH\'a güven; koruyucu olarak ALLAH yeter.'),
(3582, 33, 49, 'Ey inananlar, inanan kadınları nikahladıktan sonra, onlarla cinsel ilişkiye girmeden boşarsanız, (bir başkasıyla evlenmelerinden önce) onların size bir bekleme süresi borcu yoktur.'),
(3583, 33, 50, 'Ey peygamber, mehirlerini vermiş bulunduğun eşlerini ve ALLAH\'ın sana bağışladığı elinin altındakileri, seninle birlikte göç eden amcanın kızlarını, halalarının kızlarını, dayının kızlarını ve teyzelerinin kızlarını sana helal kılmışısızdır. Ayrıca, peygamber dilerse, kendisini inananlara değil sadece peygambere mehirsiz olarak hibe eden birisiyle nikahlanabilir. Biz, eşleri ve ellerinin altında bulunanlar hakkında üzerlerine yüklediğimiz sorumlulukları bildirmiştik ki güç bir duruma düşmeyesin. ALLAH Bağışlayandır, Rahimdir.'),
(3584, 33, 51, 'Onlardan dilediğini erteler, dilediğini de yanına alabilirsin. Sırasını geri bırakmış olduğun birisini tekrar istemende bir sakınca yoktur. Böylece onlar hoşnut olurlar, üzülmezler ve senin herbirisine verdiğine razı olurlar. Kalbinizde olanı ALLAH iyi bilir. ALLAH Bilendir,? efkatlidir.'),
(3585, 33, 52, 'Bunların ötesinde kadınlar sana helal değildir, ve eşlerinden her hangi birisini de onlarla değişemezsin. Güzellikleri senin ilgini çekse bile. Ancak elinin altındakiler ile yetin. ALLAH her şeyi gözetleyendir.'),
(3586, 33, 53, 'Ey inananlar, peygamberin evine, yemeğe çağrılmadan girmeyin. Öyle bir çağrıyı da beklemeyin. Çağrıldığınız vakit girebilirsiniz. Yemeği yedikten sonra ayrılıp dağılın. Hadislere (sözlere) dalmayın. Bu durum peygamberi üzüyor; ancak size bildirmekten utanıyordu. ALLAH gerçeği bildirmekten çekinmez. Onun hanımlarından bir şey istediğinizde onu bir perde ardından isteyiniz. Bu sizin kalbiniz ve onların kalpleri için daha temiz bir davranıştır. ALLAH\'ın elçisini üzmemelisiniz ve ondan sonra onun eşleriyle asla evlenmemelisiniz, zira bu, ALLAH katında pek büyük bir günahtır.'),
(3587, 33, 54, 'Bir şeyi açığa vursanız da, gizleseniz de ALLAH her şeyi iyi bilir.'),
(3588, 33, 55, 'Babaları, oğulları, kardeşleri, kardeşlerinin oğulları, kız kardeşlerinin oğulları, diğer kadınlar ve (kadın) hizmetçileri karşısında (serbest bir biçimde giyinmelerinde) kadınlar için bir sakınca yoktur. ALLAH\'ı saysınlar. ALLAH her şeye Tanıktır.'),
(3589, 33, 56, 'ALLAH ve melekleri peygamberi desteklemektedir. Ey inananlar siz de onu destekleyin, gereken saygı ve itaati gösterin.'),
(3590, 33, 57, 'ALLAH\'a ve elçisine hakaret edenlere, ALLAH dünya ve ahirette lanet eder ve onlar için aşağılayıcı bir ceza hazırlamıştır.'),
(3591, 33, 58, 'İnanan erkeklere ve kadınlara, haksız yere hakaret edenler, bir iftira ve apaçık bir günah yüklenmişlerdir.'),
(3592, 33, 59, 'Ey peygamber, hanımlarına, kızlarına ve inananların kadınlarına örtülerini üzerlerine salmalarını söyle. Bu, onların (erdemli kadınlar olarak) tanınıp hakarete uğramamaları için daha elverişlidir. ALLAH Bağışlayandır, Rahimdir.'),
(3593, 33, 60, 'İkiyüzlüler, kalplerinde hastalık bulunanlar ve kentteki saldırganlar (size karşı olan saldırılarına) son vermezlerse seni onlara musallat ederiz; sonra orada, senin çevrende kısa bir süre kalabilirler.'),
(3594, 33, 61, 'Lanetlenirler; nerede rastlansalar, (saldırıdan vazgeçmezlerse) yakalanıp öldürülebilirler.'),
(3595, 33, 62, 'Öncekilere uygulanmış ALLAH\'ın sünneti (yasasıdır). ALLAH\'ın sünnetinde herhangi bir değişme bulamazsın.'),
(3596, 33, 63, 'Halk senden Saat\'i (dünyanın son saatini) soruyor. De ki, "Onun bilgisi ALLAH\'ın katındadır; ne bilirsin, belki de o Saat yakındır."'),
(3597, 33, 64, 'ALLAH inkarcıları lanetlemiş ve onlara cehennemi hazırlamıştır.'),
(3598, 33, 65, 'Orada ebedi kalıcıdırlar. Ne bir veli (sahip) ne de bir yardımcı bulamazlar.'),
(3599, 33, 66, 'Yüzleri ateşte çevrildiği gün, "Keşke ALLAH\'a itaat etseydik, keşke elçisine itaat etseydik," derler.'),
(3600, 33, 67, 'Derler ki, "Rabbimiz, sadatlarımıza/şeyhlerimize ve büyüklerimize uyduk; onlar da bizi yoldan saptırdılar."'),
(3601, 33, 68, '"Rabbimiz, onlara iki kat ceza ver, onları büyük bir lanetle lanetle."'),
(3602, 33, 69, 'Ey inananlar, Musa\'yı incitenler gibi olmayın. ALLAH onu onların suçlamalarından akladı. ALLAH\'ın katında itibarı olan biriydi.'),
(3603, 33, 70, 'Ey inananlar, ALLAH\'ı sayın ve doğru söz söyleyin.'),
(3604, 33, 71, 'Ki işlerinizi düzeltsin, günahlarınızı bağışlasın. Kim ALLAH\'a ve elçisine uyarsa büyük bir zafer kazanmıştır.'),
(3605, 33, 72, 'Biz sorumluluğu (sınanmayı) göklere, yere, dağlara sunmuştuk da onlar onu yüklenmekten çekinmişler ve kabul etmemişlerdi. Ancak onu insan yüklendi; o zalim ve cahil olmuştu.'),
(3606, 33, 73, 'Böylece, ALLAH ikiyüzlü erkekleri ve kadınları, ortak koşan erkekleri ve kadınları cezalandırır ve ALLAH inanan erkeklerin ve kadınların tevbelerini kabul eder. ALLAH Bağışlayandır, Rahimdir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'Övgü, göklerde ve yerde ne varsa kendisine ait bulunan ALLAH\'adır. Ahirette de övgü O\'nadır. O Bilgedir, Haber Alandır.'),
(3608, 34, 2, 'Yere gireni, ondan çıkanı; gökten ineni ve oraya yükseleni bilir. O Rahimdir, Bağışlayandır.'),
(3609, 34, 3, 'İnkarcılar, "Saat, hiç bir vakit gerçekleşmeyecek," dediler. De ki, "Kesinlikle, Rabbime andolsun size gelecektir. O, geleceği Bilendir. Göklerde ve yerde bir atom ağırlığı O\'ndan gizli kalmaz. İster ondan küçük olsun, yahut büyük olsun... Hepsi apaçık bir kitaptadır."'),
(3610, 34, 4, 'Böylece, inananları ve erdemli davrananları ödüllendirir. Onlar için bir bağışlanma ve bol rızık vardır.'),
(3611, 34, 5, 'Ayetlerimiz ve mucizelerimize karşı meydan okuyup duranlar için acıklı ve korkunç bir ceza vardır.'),
(3612, 34, 6, 'Kendilerine bilgi verilmiş olanlar, Rabbinden sana indirilenin gerçek olduğunu ve onun Üstün ve en çok Övülen\'in yoluna ilettiğini görürler.'),
(3613, 34, 7, 'İnkarcılar dediler, "Siz parça parça dağıldıktan sonra yeni bir yaratılışla diriltileceğinizi bildiren bir adamı size gösterelim mi?"'),
(3614, 34, 8, '"ALLAH\'a yalan mı yakıştırıyor, yoksa kendisinde delilik mi var?" Halbuki ahirete inanmıyanlar, uzak bir sapıklık ve azap içindedirler.'),
(3615, 34, 9, 'Göklerde ve yerde, önlerinde ve arkalarında bulunanlara bakmadılar mı? Dilersek onları yere batırır veya gökten bir kütle düşürürüz. Her yönelen kul için elbette bunda bir işaret vardır.'),
(3616, 34, 10, 'Katımızdan Davud\'a lütufta bulunmuştuk: "Ey dağlar, ilahisine katılın; ey kuşlar sizler de." Ona demiri yumuşatmıştık.'),
(3617, 34, 11, '"Ölçüsü ve dokumasıyla kusursuz zırhlar yap. Erdemli davranın. Ben, yaptığınız her şeyi Görenim."'),
(3618, 34, 12, 'Süleyman\'a da, sabah gidişi bir ay (lık yol alan), akşam dönüşü de bir ay (lık yol alan) rüzgarı verdik ve onun için petrol kaynağını fışkırtarak akıttık. Rabbinin izniyle, cinlerden de bir kısım emrinde çalışırdı. Onlardan kim emrimizden saparsa onu çetin bir cezayı tattırırdık.'),
(3619, 34, 13, 'O ne dilerse onun için yaparlardı: Mihraplar, heykeller, derin havuzlar ve ağır kazanlar... Ey Davut ailesi, şükür göstergesi olarak çalışın. Kullarımdan pek azı şükredicidir.'),
(3620, 34, 14, 'Ölümüne hükmettiğimiz zaman, onlara onun ölmüş olduğunu, ancak değneğini yiyen bir yer yaratığı gösterdi. Yıkıldığı zaman cinler gerçeği farketti: Görülmeyeni bilselerdi o küçültücü zor işe devam etmezlerdi.'),
(3621, 34, 15, 'Sebe\'lilerin evleri, sağlı sollu iki bahçeyle birlikte bir harikaydı. Rabbinizin rızkından yeyin ve O\'na şükredin. Güzel bir ülke ve Bağışlayan bir Rab...'),
(3622, 34, 16, 'Fakat onlar yüz çevirdiler. Biz de barajın selini onlara göndererek çifte bahçelerini, acı meyveler, ılgın ve biraz da sedir ağacı içeren çifte bahçelere çevirdik'),
(3623, 34, 17, 'Nankörlükleri yüzünden onları böyle cezalandırdık. Biz nankörden başkasını mı cezalandırırız?'),
(3624, 34, 18, 'Onlarla bereketli kıldığımız bölgeler arasına vahalar yerleştirdik ve orayı yolculuğa elverişli yaptık: "Geceleri ve gündüzleri güvenlik içinde orada dolaşın."'),
(3625, 34, 19, '"Rabbimiz, seferlerimizin arası uzaklaştı," diyerek kendilerine zulmettiler. Sonunda onları darmadağın edip tarihe gömdük. Her sabırlı ve şükreden kişi için bunda dersler vardır.'),
(3626, 34, 20, 'İblis, onlar hakkındaki beklentilerini doğru çıkardı. İnanan bir grup hariç ona uydular.'),
(3627, 34, 21, 'Aslında onun onlar üzerine hiç bir otoritesi yoktu. Ancak, ahirete inananı ondan kuşku duyandan böylece ayırırız. Rabbin her şeyi kontrol etmektedir.'),
(3628, 34, 22, 'De ki, "ALLAH\'ın dışında ileri sürdüklerinizi çağırın. Onlar göklerde ve yerde bir atom ağırlığına bile sahip değillerdir. Onların her ikisinde de bir ortaklıkları yoktur. O da onlardan hiç birisini yardımcı edinmemiştir."'),
(3629, 34, 23, 'O\'nun katında şefaat yarar sağlamaz; ancak izin verdiği hariç. Nihayet, yüreklerindeki korkuları giderilince, "Rabbiniz ne dedi?" derler. "Gerçeği söyledi" derler. O En Yücedir, En Büyüktür.'),
(3630, 34, 24, 'De ki, "Sizin için göklerden ve yerden kim besin sağlıyor?" De ki, "ALLAH." Öyleyse ya biz veya siz, ya doğru yoldadır, ya da açık bir sapıklık içindedir.'),
(3631, 34, 25, 'De ki, "Bizim suçlarımızdan siz sorumlu değilsiniz, biz de sizin yaptıklarınızdan sorumlu değiliz."'),
(3632, 34, 26, 'De ki, "Rabbimiz hepimizi toplar ve sonra aramızda gerçeğe göre hüküm verir. O Yargıçtır, Bilendir."'),
(3633, 34, 27, 'De ki, "O\'na kattığınız ortakları bana gösteriniz. Asla! O Üstün ve Bilge olan ALLAH\'tır."'),
(3634, 34, 28, 'Biz seni tüm halka bir müjdeci ve uyarıcı olarak gönderdik. Ancak halkın çoğu bilmez.'),
(3635, 34, 29, '"Doğru söylüyorsanız o söz ne zaman gerçekleşecek?" diyerek meydan okuyorlar.'),
(3636, 34, 30, 'De ki, "Size bir saat gecikmeyecek, bir saat de erken gelmeyecek belirlenmiş bir gününüz vardır."'),
(3637, 34, 31, 'İnkarcılar, "Biz ne bu Kuran\'a ne de ondan öncekilere inanmayız," dediler. Zalimleri, Rab\'leri huzurunda duruşma sırasında birbiriyle atışırken bir görseydin! (Öğrenim, araştırma, ekonomik, politik v. b. yönlerden) güçsüzleştirilenler, büyüklük taslamış olanlara, "Siz olmasaydınız biz inanan kişiler olurduk," derler.'),
(3638, 34, 32, 'Büyüklenenler, güçsüzleştirilen izleyicilerine, "Size hidayet geldikten sonra biz mi sizi saptırdık? Hayır, suçlu olanlar sizdiniz," derler.'),
(3639, 34, 33, 'Güçsüzleştirilenler, büyüklenenlere, "Asıl sizdiniz. Bizim ALLAH\'a karşı nankörlük göstermemiz ve O\'na ortaklar koşmamız için gece gündüz planlar kuruyordunuz." Azabı gördüklerinde içlerinde pişmanlık duyarlar. İnkarcıların boyunlarına prangalar vurmuşuzdur. Yalnız yaptıklarının karşılığını almıyorlar mı?'),
(3640, 34, 34, 'Bir kente her ne zaman bir uyarıcı gönderirsek, elitleri, "Sizin kendisiyle gönderildiğiniz mesajı reddediyoruz," dediler.'),
(3641, 34, 35, 'Hatta, "Bizim paramız ve çocuklarımız daha çoktur; biz cezalandırılmayacağız," dediler.'),
(3642, 34, 36, 'De ki, "Rabbim nimetleri dilediğine bol verir veya kısar. Ama halkın çoğu bilmez."'),
(3643, 34, 37, 'Sizi bize yaklaştıran şey ne paralarınızdır, ne de çocuklarınızdır. Ancak inanan ve erdemli davrananlar hariç. Onlara, yaptıklarının iki kat karşılığı verilecektir ve odalarında güvenlik içindedirler.'),
(3644, 34, 38, 'Ayetlerimize meydan okuyanlar azapta kalacaklar.'),
(3645, 34, 39, 'De ki, "Rabbim, kullarından dilediğine nimeti bol verir ve onu kısar. Neyi verirseniz, onu size ödeyecektir. O, rızık verenlerin en iyisidir."'),
(3646, 34, 40, 'Hepsini topladığı gün meleklere, "Şunlar mı size tapıyordu?" der.'),
(3647, 34, 41, 'Dediler ki, "Sen yücesin, velimiz (dostumuz) onlar değil, Sensin. Hayır, onlar cinlere tapıyorlardı. Çokları onlara inanıyordu."'),
(3648, 34, 42, 'O gün biribirinize yarar ve zarar veremezsiniz. Zalimlere, "Yalanlamakta olduğunuz ateş azabını tadın," deriz.'),
(3649, 34, 43, 'Kendilerine apaçık ayetlerimiz okunduğunda, "Bu, kesinlikle, sizi atalarınızın tapınma yolundan saptırmak isteyen bir adamdır." Dahası, "Bu uydurulmuş bir iftiradan başka bir şey değildir," dediler. İnkarcılar, kendilerine gelen gerçek için, "Besbelli, bu bir büyüdür," dediler.'),
(3650, 34, 44, 'Onlara, inceleyecekleri bir Kitap vermemiş ve senden önce onlara bir uyarıcı göndermemiştik.'),
(3651, 34, 45, 'Onlardan öncekiler yalanladılar. Halbuki onlara verilenlerin onda birine ulaşmamışlardı. Elçilerimi yalanladılar. Beni tanımamak da nasılmış?'),
(3652, 34, 46, 'De ki, "Size bir tek öğüdüm var: ALLAH için ikişer ikişer, yahut teker teker kalkın, sonra düşünün. Sizin arkadaşınızda bir delilik yoktur. O sadece, çetin bir cezadan önce sizi uyaran birisidir."'),
(3653, 34, 47, 'De ki, "Ben sizden her hangi bir ücret istemiyorum; o sizin olsun. Benim ücretim ancak ALLAH\'tandır. O her şeye Tanıktır."'),
(3654, 34, 48, 'De ki, "Benim Rabbim gerçeği etkili kılar. O tüm gizemleri Bilendir."'),
(3655, 34, 49, 'De ki, "Gerçek gelmiştir; batıl ise ne yeni bir şeyi başlatabilir, ne de tekrarlayabilir."'),
(3656, 34, 50, 'De ki, "Saparsam, kendi kusurumdandır, yolu bulursam Rabbimin bana vahyetmesindendir. O İşitendir, Bilendir."'),
(3657, 34, 51, 'Korkuya kapıldıkları anı bir görsen; kaçamazlar; yakın bir yerden yakalanmışlardır.'),
(3658, 34, 52, '"Ona inandık," derler. Uzak bir yerden ona nasıl ulaşabilirler?'),
(3659, 34, 53, 'Halbuki daha önce onu yalanlamışlardı. Bilgileri olmayan konularda uzaktan atıp tutuyorlardı.'),
(3660, 34, 54, 'Sonunda, arzuladıkları şeylerden yoksun bırakılırlar. Kendilerinden önceki benzerlerine de aynı şey yapılmıştı. Onlar aşırı bir kuşku içinde idiler.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'Gökleri ve yeri yaratan; melekleri ikişer, üçer, dörder kanatlı elçiler kılan ALLAH\'a övgüler olsun. Yaratmada dilediğini arttırır. ALLAH her şeye gücü yetendir.'),
(3662, 35, 2, 'ALLAH\'ın insanlara bağışladığı bir rahmeti engelleyebilecek yoktur. Onun alıkoyduğunu da O\'ndan başka gönderebilecek yoktur. O Üstündür, Bilgedir.'),
(3663, 35, 3, 'Ey insanlar, ALLAH\'ın size olan nimetlerini hatırlayın. Sizi gökten ve yerden rızıklandıran ALLAH\'tan başka bir yaratıcı mı var? O\'ndan başka tanrı yoktur. Nasıl da saptırılıyorsunuz?'),
(3664, 35, 4, 'Seni yalanlarlarsa, senden önceki elçiler de yalanlandı. Tüm işlerin kontrolü ALLAH\'a aittir.'),
(3665, 35, 5, 'Ey insanlar ALLAH\'ın sözü gerçektir; dünya hayatı sizi aldatmasın. Kandırıcı, sizi ALLAH hakkında kandırmasın.'),
(3666, 35, 6, 'Şeytan sizin düşmanınızdır, siz de onu düşman edinin. O partisini sadece cehennem halkı olmaya çağırır.'),
(3667, 35, 7, 'İnkar edenler çetin bir cezaya mahkum olmuştur. İnanıp erdemli davrananlar ise bir bağışlanma ve büyük bir ödül hakketmişlerdir.'),
(3668, 35, 8, 'Kötü işi, kendisine süslenip de onu güzel gören kimseye ne dersin? ALLAH dileyeni saptırır ve dileyeni yola iletir. Öyleyse onlar için kendini üzüp durma. ALLAH onların yaptıklarını iyi Bilir.'),
(3669, 35, 9, 'ALLAH, bulutları karıştıran rüzgarları gönderendir. Daha sonra onları ölü bir toprağa süreriz de ölümünden sonra toprağı diriltiriz. Diriliş de böyledir.'),
(3670, 35, 10, 'Onuru arzulayanlar, bilsin ki tüm onur ALLAH\'a aittir. Güzel söz O\'na çıkar, iyi iş de onu yükseltir. Kötülük planlıyanlar için çetin bir azap vardır ve onların planı başarısızlıkla sonuçlanacaktır.'),
(3671, 35, 11, 'ALLAH sizi topraktan, sonra bir damlacıktan yaratmış ve sonra da sizi çiftler halinde üretmiştir. Bir dişinin gebe kalması ve doğurması ancak O\'nun bilgisiyledir. Bir canlının uzun ömürlü olması da ömrünün kısa tutulması da ancak bir kitapta kayda göredir. Bu, ALLAH\'a kolaydır.'),
(3672, 35, 12, 'İki deniz birbirine eşit değildir. Bu taze ve tatlı olup içimi yumuşaktır, şu ise tuzlu ve acıdır. Hepsinden taze et yersiniz ve takınacağınız mücevherleri çıkarırsınız. O\'nun lütfunu arayan gemilerin onu yararak aktığını görürsün. Belki artık şükredersiniz.'),
(3673, 35, 13, 'Geceyi gündüzün içine sokar, gündüzü de gecenin içine sokar. Belirlenmiş bir süreye kadar akıp giden güneşi ve ayı emri altına almıştır. Rabbiniz ALLAH işte böyledir; egemenlik O\'na aittir. O\'nun dışında çağırdıklarınız ise bir çekirdek zarını bile yönetemezler.'),
(3674, 35, 14, 'Onları çağırsanız sizin çağrınızı işitemezler. İşitseler bile size cevap veremezler. Diriliş gününde de sizin ortak koşmanızı inkar ederler. Her şeyden Haberi Olan gibi kimse sana haber veremez.'),
(3675, 35, 15, 'Ey insanlar, siz ALLAH\'a muhtaçsınız, ALLAH ise hiç kimseye muhtaç değildir, Övülendir.'),
(3676, 35, 16, 'Dilerse sizi götürür ve yerinize yeni yaratıkları getirir.'),
(3677, 35, 17, 'Bu, ALLAH için zor değildir.'),
(3678, 35, 18, 'Kimse kimsenin günahını yüklenmez. Günahla yüklenmiş birisi yükünü taşımak üzere akrabalarını bile çağırsa onun yükünden hiç bir şey taşınmaz. Sen yalnızca, kendi başlarına iken Rab\'lerini sayan ve namazı gözeten kişileri uyarabilirsin. Kim kendisini arındırırsa kendisi yararına arınmıştır. Dönüş ALLAH\'adır.'),
(3679, 35, 19, 'Kör ile gören bir olmaz.'),
(3680, 35, 20, 'Ne karanlıklar ile ışık.'),
(3681, 35, 21, 'Ne gölge ile sıcaklık.'),
(3682, 35, 22, 'Ne de diriler ile ölüler birdir. ALLAH, kim/kimi dilerse ona işittirir. Sen mezarlarda bulunanlara işittiremezsin.'),
(3683, 35, 23, 'Sen ancak bir uyarıcısın.'),
(3684, 35, 24, 'Seni bir müjdeci ve uyarıcı olarak gerçekle gönderdik. Her toplum içinde mutlaka bir uyarıcı gelmiştir.'),
(3685, 35, 25, 'Seni yalanlıyorlarsa, senden öncekiler de yalanlamışlardı. Elçileri kendilerine apaçık belgeler, ilahiler ve aydınlatıcı kitaplar getirdiler'),
(3686, 35, 26, 'Sonra ben de inkar edenleri yakaladım. Beni inkar da nasılmış!'),
(3687, 35, 27, 'ALLAH\'ın gökten bir su indirdiğini görmedin mi? Onunla çeşitli renklerde ürünler çıkarırız. Hatta dağlarda bile beyaz, kırmızı veya rengarenk katmanlar vardır. Kargalar ise siyahtır.'),
(3688, 35, 28, 'Aynı şekilde, insanlar, hayvanlar, çiftlik hayvanları da çeşitli renklerdedir. Bundan dolayıdır ki kulları arasında ALLAH\'ı gereği gibi sayanlar bilim adamlarıdır. ALLAH Üstündür, Bağışlayandır.'),
(3689, 35, 29, 'ALLAH\'ın kitabını okuyanlar, namazı gözetenler ve kendilerine verdiğimiz rızıklardan gizli ve açık yardım için verenler, tükenmeyen bir kazanç umarlar.'),
(3690, 35, 30, 'Çünkü onların ücretlerini tam verecek ve onlara olan lütfunu arttıracaktır. O Bağışlayandır, Takdir Edendir.'),
(3691, 35, 31, 'Bu kitapta sana vahyettiklerimiz, kendisinden öncekileri doğrulayan gerçektir. ALLAH kullarından Haberdardır, Görendir.'),
(3692, 35, 32, 'Sonra kullarımızdan seçtiklerimizi kitaba varis kıldık. Onlardan kimi kendilerine zulmedenlerdir, kimi orta yolu tutar, kimi de ALLAH\'ın izniyle iyi işlerde öncüdür. İşte büyük lütuf budur.'),
(3693, 35, 33, 'Sonsuzluğa dek süren cennetlere gireceklerdir. Orada altın bilezikler ve inciler takacaklardır, oradaki elbiseleri ise ipektendir.'),
(3694, 35, 34, 'Ve derler: "Endişelerimizi gideren ALLAH\'a övgüler olsun. Rabbimiz gerçekten Bağışlayandır, Takdir Edendir."'),
(3695, 35, 35, '"O ki lütfuyla bizi durulacak yurda yerleştirdi. Orada ne bir yorulma ne de bir bıkkınlık duyarız."'),
(3696, 35, 36, 'İnkar edenler için cehennem ateşi vardır. Ne ölmelerine izin verilir, ne de onlardan cehennem cezası hafifletilir. Nankörleri böyle cezalandırırız.'),
(3697, 35, 37, 'Orada, "Rabbimiz, bizi çıkar da yapmış olduğumuzdan farklı işler yapalım," diye feryat ederler. Öğüt alabilecek bir kişinin öğüt alabileceği kadar uzun bir süre sizi yaşatmadık mı? Size uyarıcı da gelmişti. Öyleyse tadın. Zalimlerin yardımcısı yoktur.'),
(3698, 35, 38, 'ALLAH göklerin ve yerin geleceğini ve gizemlerini bilendir. O, göğüslerin gizlediğini Bilendir.'),
(3699, 35, 39, 'O ki sizi yeryüzünde yöneticiler kıldı. Kim inkar ederse kendi zararına inkar etmiştir. İnkarcıların inkarı, Rab\'leri katında nefretten başka bir şey artırmaz. İnkarcıların inkarı kayıptan başka birşey artırmaz'),
(3700, 35, 40, 'De ki, "ALLAH\'ın dışında çağırdığınız ortaklarınızı düşündünüz mü; onlar yeryüzünde neyi yaratmışlar bana gösterin?" Yoksa onların gökte bir ortaklıkları mı var? Veya onlara bir kitap verdik de ondaki bir delile mi dayanıyorlar? Doğrusu, zalimlerin birbirlerine ancak aldatıcı sözler verirler.'),
(3701, 35, 41, 'ALLAH, gökleri ve yeri kaos içine girmekten alıkoymaktadır. Kaosa girseler O\'ndan başka kim onu engelleyebilir? O Şefkatlidir, Bağışlayandır.'),
(3702, 35, 42, 'Kendilerine bir uyarıcı gitse tüm topluluklardan daha doğru yolda olacaklarına dair var güçleriyle ALLAH\'a yemin ettiler. Kendilerine bir elçi gittiğinde, bu durum, onların ancak nefretini artırdı'),
(3703, 35, 43, 'Yeryüzünde büyüklendiler, kötülük planladılar. Halbuki kötü plan sahibine geri teper. Geçmişlere uygulanan sünnet (yasa) dan başkasını mı bekliyorlar? ALLAH\'ın sünnetinde bir değişiklik göremezsin; ALLAH\'ın sünnetinde bir sapma göremezsin.'),
(3704, 35, 44, 'Kendilerinden öncekilerin sonunun nasıl olduğuna bakmak için yeryüzünü dolaşmadılar mı? Onlar kendilerinden daha güçlüydüler. Göklerde ve yerde hiç bir şey ALLAH\'ı aciz bırakamaz. O, Bilendir, Güçlüdür.'),
(3705, 35, 45, 'ALLAH insanları yaptıkları yüzünden cezalandırsaydı yeryüzünde hiç bir yaratık bırakmazdı. Fakat, onları belli bir süreye kadar ertelemektedir. Ne zaman ki süreleri biter, işte o zaman ALLAH kullarının (hesabını) görür.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'Y. S.'),
(3707, 36, 2, 'Bilge Kuran\'a and olsun.'),
(3708, 36, 3, 'Sen elbette elçilerden birisin.'),
(3709, 36, 4, 'Dosdoğru bir yol üzerinde.'),
(3710, 36, 5, 'Bu, Üstün ve Rahim olanın indirdiği bir vahiydir.'),
(3711, 36, 6, 'Ataları uyarılmadığından tümüyle habersiz kalmış bir toplumu uyarman için...'),
(3712, 36, 7, 'Çoklarının inanmıyacağına dair söz gerçekleşmiştir.'),
(3713, 36, 8, 'Boyunlarına, çenelerine kadar varan prangalar taktık da kafaları yukarıya dikilmiştir.'),
(3714, 36, 9, 'Önlerinden bir set ve arkalarından bir set çekerek onları perdeledik; artık göremezler.'),
(3715, 36, 10, 'Onları uyarsan da uyarmasan da onlar için birdir; inanmazlar.'),
(3716, 36, 11, 'Sen ancak, mesaja uyan ve yalnız başına iken Rahman\'a karşı saygılı olan bir kimseyi uyarabilirsin. Onu bağışlanma ve bol bir ödülle müjdele.'),
(3717, 36, 12, 'Ölüleri biz, evet biz diriltiriz, onların yaptıklarını ve (ölümlerinden sonraki) sonuçlarını yazarız. Biz herşeyi apaçık bir kitapta saymışızdır.'),
(3718, 36, 13, 'Onlara, bir kent halkının kendilerine gelen elçilere gösterdiği tavrın örneğini ver.'),
(3719, 36, 14, 'Onlara iki elçi göndermiştik, ikisini de yalanladılar. Bunun üzerine üçüncü biriyle desteklemiştik. "Biz size gönderilen elçileriz," demişlerdi.'),
(3720, 36, 15, 'Dediler ki, "Siz de bizim gibi insandan başka bir şey değilsiniz. Rahman ise hiçbir şey indirmemiştir. Siz yalan söylüyorsunuz."'),
(3721, 36, 16, 'Dediler ki, "Rabbimiz bilir ki biz size gönderildik."'),
(3722, 36, 17, '"Bizim görevimiz, açıkça duyurmaktan ibarettir."'),
(3723, 36, 18, 'Dediler ki, "Sizin yüzünüzden uğursuzluğa uğradık. Eğer son vermezseniz sizi taşlarız ve bizden size acı bir ceza dokunacaktır."'),
(3724, 36, 19, 'Dediler ki, "Uğursuzluğunuz sizden kaynaklanmaktadır. Size uyarıda bulunulduğu için mi? Siz gerçekten sınırı aşan bir topluluksunuz."'),
(3725, 36, 20, 'Kentin en uzak yakasından bir adam koşarak, "Ey halkım," dedi, "Elçilere uyun."'),
(3726, 36, 21, '"Sizden bir ücret istemiyenlere uyun. Onlar doğru yoldadır."'),
(3727, 36, 22, '"Beni yaratana ne diye kulluk etmeyeyim? Siz de O\'na döneceksiniz."'),
(3728, 36, 23, '"O\'nun dışında tanrılar mı edineyim? Eğer Rahman bana zarar vermek dilese, ne onların şefaati bana bir yarar sağlayabilir ne de beni kurtarabilirler."'),
(3729, 36, 24, '"O zaman tümüyle sapıtmış olurum."'),
(3730, 36, 25, '"Ben sizin Rabbinize inandım; lütfen beni dinleyin."'),
(3731, 36, 26, '(Ölüm anında) Kendisine, "Cennete gir," denir. "Keşke benim halkım bir bilseydi..."'),
(3732, 36, 27, '"Rabbimin beni bağışladığını ve beni ağırladığını..."'),
(3733, 36, 28, 'Ondan sonra biz, halkının üzerine gökten bir ordu indirmedik; indirmeğe gerek duymadık.'),
(3734, 36, 29, 'Sadece bir patlama... Hemen donakaldılar.'),
(3735, 36, 30, 'Halkın durumu pek yazık. Kendilerine her ne zaman bir elçi gelse onunla alay ederlerdi.'),
(3736, 36, 31, 'Kendilerinden önce nice nesilleri yok ettiğimizi ve onların bir daha kendilerine dönmediklerini görmezler mi?'),
(3737, 36, 32, 'Hepsi toplanıp huzurumuza getirileceklerdir.'),
(3738, 36, 33, 'Ölü toprak onlar için bir ayettir: Onu diriltiriz ve oradan taneler çıkarırız da ondan yerler.'),
(3739, 36, 34, 'Orada hurma ağaçları ve üzümlerden oluşan bağ ve bahçeler yetiştirdik ve pınarlar fışkırttık.'),
(3740, 36, 35, 'Ki onun ürünlerinden ve elleriyle yetiştirdiklerinden yesinler. Şükretmiyecekler mi?'),
(3741, 36, 36, 'Yerin bitirdiklerinden, kendi cinslerinden ve daha bilmedikleri nice şeylerden türlü çiftleri yaratan pek yücedir.'),
(3742, 36, 37, 'Gece de onlar için bir ayettir: Ondan gündüzü soyarız da onlar karanlıkta kalırlar.'),
(3743, 36, 38, 'Güneş belirlenmiş olan rotasında akıp gitmektedir. Bu Üstün ve Bilgin olanın kurduğu bir düzendir.'),
(3744, 36, 39, 'Aya da, kuru bir hurma dalına dönüşünceye kadar çeşitli evreler belirledik.'),
(3745, 36, 40, 'Ne güneş aya yetişebilir, ne de gece, gündüzü geçebilir. Her biri bir yörüngede yüzmektedir.'),
(3746, 36, 41, 'Onlar için bir başka ayette, insan soyunu yüklü gemide taşımamızdır.'),
(3747, 36, 42, 'Aynı şekilde, sürmeleri için onun bir benzerini yarattık.'),
(3748, 36, 43, 'Dileseydik onları boğardık; ne bir çığlıklarına yetişen olurdu, ne de kurtulabilirlerdi.'),
(3749, 36, 44, 'Bunun yerine, bizden bir merhamet görürler ve belli bir süreye kadar yaşatılırlar.'),
(3750, 36, 45, 'Kendilerine, "Geçmişinizden ibret alıp ve geleceğiniz için sakının ki merhamet edilesiniz," denilmişti.'),
(3751, 36, 46, 'Rab\'lerinin ayetlerinden bir ayet kendilerine geldiğinde, ondan yüz çevirmeyi adet edinmişlerdi.'),
(3752, 36, 47, 'Kendilerine, "ALLAH\'ın size verdiği rızıklardan verin," denildiğinde, inkar edenler inananlara, "ALLAH\'ın, dilediği taktirde besleyebileceği kimseleri mi besleyelim? Siz gerçekten iyice sapıtmışsınız," derler.'),
(3753, 36, 48, 'Aynı zamanda, "Doğru sözlü iseniz o söz ne zaman gerçekleşecek?" diye meydan okurlar.'),
(3754, 36, 49, 'Çekişip dururlarken kendilerini yakalayacak bir tek patlamayı beklemektedirler.'),
(3755, 36, 50, 'Ne bir vasiyet bırakmaya vakit bulurlar ne de ailelerine dönebilirler.'),
(3756, 36, 51, 'Boruya üflenince, onlar mezarlarından kalkıp Rab\'lerine koşacaklar.'),
(3757, 36, 52, '"Vay halimize" derler, "Yattığımız yerden bizi kim kaldırdı? Bu, Rahman\'ın söz verdiği şeydi. Demek elçiler doğru söylemişti."'),
(3758, 36, 53, 'Sadece bir patlama... Hemen huzurumuza toplanıp getirilirler.'),
(3759, 36, 54, 'Bu gün hiç kimseye en ufak bir haksızlık edilmez ve yaptığınızın karşılığından başkasını da görmezsiniz.'),
(3760, 36, 55, 'Cennet halkı o gün zevk ve eğlence ile meşguldürler.'),
(3761, 36, 56, 'Eşleriyle birlikte gölgeliklerde, koltuklara yaslanmışlardır.'),
(3762, 36, 57, 'Onlar için meyveler ve istedikleri her şey vardır.'),
(3763, 36, 58, 'Rahim olan Rab\'den söz olarak "selam" vardır.'),
(3764, 36, 59, 'Ey suçlular, siz bugün ayrılın.'),
(3765, 36, 60, 'Ey Adem\'in çocukları, şeytana tapmayacağınıza dair sizden söz almamış mıydım? O sizin açık düşmanınızdır.'),
(3766, 36, 61, 'Bana kulluk edin. Bu en doğru yoldur.'),
(3767, 36, 62, 'Buna rağmen o, sizden bir çok nesilleri saptırdı. Hiç aklınızı kullanmaz mıydınız?'),
(3768, 36, 63, 'İşte, size söz verilen cehennem budur!'),
(3769, 36, 64, 'İnkarınızın bir sonucu olarak orada yanınız.'),
(3770, 36, 65, 'O gün ağızlarına mühür vururuz da, bizimle elleri konuşur ve yapmış olduklarına da ayakları tanıklık eder.'),
(3771, 36, 66, 'Dilesek gözlerini büsbütün silerdik. Yolu bulmaya çalıştıklarında göremezlerdi.'),
(3772, 36, 67, 'Dilesek onları oldukları yerde dondurur ne ileri gidebilir ne de geri dönebilirlerdi.'),
(3773, 36, 68, 'Kime çok ömür verirsek, yaratılışını tersine çeviririz. Anlamaz mısınız?'),
(3774, 36, 69, 'Ona şiir öğretmiş değiliz, zaten ona uygun düşmez. Bu, ancak bir mesaj ve apaçık bir Kuran\'dır.'),
(3775, 36, 70, 'Dirileri uyarır ve inkarcıları açığa çıkarır.'),
(3776, 36, 71, 'Görmezler mi, kendi ellerimizle onlar için çiftlik hayvanlarını yarattık da onlara sahip olmaktadırlar?'),
(3777, 36, 72, 'Onları kendilerine boyun eğdirdik; bir kısmına binmekte bir kısmından da yemektedirler'),
(3778, 36, 73, 'Ve onlar için onlarda başka yararlar ve içecekler vardır. Şükretmiyecekler mi?'),
(3779, 36, 74, 'ALLAH\'tan başka tanrılar edindiler. Belki kendilerine yardım ederler diye.'),
(3780, 36, 75, 'Oysa onlara yardım edemezler; hatta tam tersine kendileri onları korumak için nöbet bekleyen askerlerdir.'),
(3781, 36, 76, 'Sözleri seni üzmesin. Gizledikleri ve açıkladıkları her şeyi çok iyi biliriz.'),
(3782, 36, 77, 'İnsan, kendisini bir damlacıktan yarattığımızı görmez mi ki bize karşı apaçık bir düşman kesilir?'),
(3783, 36, 78, 'Ve yaradılışını unutarak bize örnekli bir soru yöneltti: "Çürüdükten sonra kemikleri kim diriltecek?"'),
(3784, 36, 79, 'De ki, "Kim onları ilk kez yarattıysa onları yine O diriltecek. O her türlü yaratmayı bilendir."'),
(3785, 36, 80, 'O ki, size yeşil (klorofilli) ağaçtan ateş çıkarandır. Nitekim onu yakıyorsunuz.'),
(3786, 36, 81, 'Gökleri ve yeri yaratan onların benzerini yaratmaya güç yetiremez mi? Gerçekten O, Yaratandır, Bilendir.'),
(3787, 36, 82, 'Bir şeyi dilediği zaman, ona sadece "Ol!" der ve o da hemen oluverir.'),
(3788, 36, 83, 'Her şeyin yönetimini elinde bulunduran çok yücedir ve siz de O\'na döndürüleceksiniz.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'And olsun sıralar halinde dizenlere,'),
(3790, 37, 2, 'İtekleyip sürenlere,'),
(3791, 37, 3, 'Ve mesajı okuyanlara...'),
(3792, 37, 4, 'Ki sizin Tanrınız birdir.'),
(3793, 37, 5, 'Göklerin, yerin ve her ikisinin arasında bulunanların Rabbidir, doğuların Rabbidir.'),
(3794, 37, 6, 'Biz en aşağıdaki göğü gezegenler ile süsleyip,'),
(3795, 37, 7, 'Her türlü inatçı şeytana karşı bir koruma yaptık.'),
(3796, 37, 8, 'Yüce topluluğu dinleyemezler; her yandan atılırlar.'),
(3797, 37, 9, 'Kovulurlar; sürekli bir azabı hakketmişlerdir.'),
(3798, 37, 10, 'Bir söz kapan olursa, onu, delici bir ışın izler.'),
(3799, 37, 11, 'Sor onlara, "Yaratılış bakımından onlar mı daha çetin, yoksa bizim yarattıklarımız mı?" Onları yapışkan bir balçıktan yarattık.'),
(3800, 37, 12, 'Sen hayranlık duyarken onlar alay ediyorlar.'),
(3801, 37, 13, 'Kendilerine hatırlatıldığında öğüt almıyorlar.'),
(3802, 37, 14, 'Bir delil gördüklerinde onu alaya alıyorlar.'),
(3803, 37, 15, 'Derler, "Bu, apaçık bir büyüden başka bir şey değildir."'),
(3804, 37, 16, '"Ölüp, toprak ve kemik olduktan sonra mı, biz mi diriltilecekmişiz?"'),
(3805, 37, 17, '"Hatta bizden önceki atalarımız da mı?"'),
(3806, 37, 18, 'De ki, "Evet, hem de horlanarak."'),
(3807, 37, 19, 'O, bir tek dokunmadır. O zaman (kalkıp) bakınırlar.'),
(3808, 37, 20, '"Vay halimize!" derler, "Bu Yargı Günüdür."'),
(3809, 37, 21, 'Bu, sizin yalanlamış olduğunuz karar günüdür.'),
(3810, 37, 22, 'Zalimleri toplayın. Eşlerini ve,'),
(3811, 37, 23, 'ALLAH\'tan başka taptıklarını... Onlara cehennemin yolunu gösterin.'),
(3812, 37, 24, 'Ve durdurun onları; sorguya çekileceklerdir.'),
(3813, 37, 25, '"Neden bir birinize yardım etmiyorsunuz?"'),
(3814, 37, 26, 'Hayır, o gün tümüyle teslim olmuşlardır.'),
(3815, 37, 27, 'Dönüp birbirlerini sorgularlar.'),
(3816, 37, 28, '"Siz bize sağ yanımızdan yaklaşıyordunuz," derler.'),
(3817, 37, 29, 'Derler ki, "Aslında siz inanmış kimseler değildiniz."'),
(3818, 37, 30, '"Bizim sizin üzerinizde her hangi bir gücümüz yoktu. Aksine siz azmış bir topluluktunuz."'),
(3819, 37, 31, '"Rabbimizin hakkımızdaki sözü gerçekleşti, artık tadacağız."'),
(3820, 37, 32, '"Biz azmıştık. Sizi de azdırdık."'),
(3821, 37, 33, 'Böylece, o gün onlar azabta ortaktırlar.'),
(3822, 37, 34, 'Biz suçlulara böyle yaparız.'),
(3823, 37, 35, 'Kendilerine "La ilahe illa ALLAH" denildiğinde büyükleniyorlardı.'),
(3824, 37, 36, '"Tanrılarımızı deli bir şair için mi terkedeceğiz?" diyorlardı.'),
(3825, 37, 37, 'Doğrusu, o, gerçeği getirmiş ve elçileri doğrulamıştır. Yine O diriltecektir. O her türlü yaratmayı bilendir.'),
(3826, 37, 38, 'Siz elbette acı azabı tadacaksınız.'),
(3827, 37, 39, 'Sadece yapmış olduklarınızın karşılığını görüyorsunuz.'),
(3828, 37, 40, 'Kendilerini sadece ALLAH\'a adamış kulları hariç.'),
(3829, 37, 41, 'Onlar bilinen bir rızkı haketmişlerdir.'),
(3830, 37, 42, 'Meyvelerle ağırlanacaklardır.'),
(3831, 37, 43, 'Nimet cennetlerinde.'),
(3832, 37, 44, 'Karşılıklı koltuklar üzerinde.'),
(3833, 37, 45, 'Onlara pınarlardan doldurulmuş kadehler sunulur.'),
(3834, 37, 46, 'Durudur, içenlere zevk ve lezzet verir.'),
(3835, 37, 47, 'Onda ne başağrısı ne de sarhoşluk vardır.'),
(3836, 37, 48, 'Yanlarında da, gözlerinin içine bakan güzel eşler...'),
(3837, 37, 49, 'Kornumuş yumurtalar gibidirler.'),
(3838, 37, 50, 'Birbirlerine dönüp soruşurlar.'),
(3839, 37, 51, 'İçlerinden biri der ki, "Benim bir arkadaşım vardı."'),
(3840, 37, 52, '"Şöyle konuşurdu, \'Sen de doğruluyor musun?"'),
(3841, 37, 53, '"Biz toprak ve kemik olduktan sonra mı, biz mi dirileceğiz?"'),
(3842, 37, 54, '(Yanındakilere,) "Bakar mısınız?" der.'),
(3843, 37, 55, 'Baktığında, onu cehennemin ortasında bulur.'),
(3844, 37, 56, '"ALLAH\'a andolsun, az kalsın sen beni de mahfedecektin," der.'),
(3845, 37, 57, '"Rabbimin nimeti olmasaydı, ben de şimde seninle birlikte olurdum."'),
(3846, 37, 58, '"(Sana göre), biz öldüğümüzde,"'),
(3847, 37, 59, '"İlk ölüm hariç, cezalandırılmayacaktık hani?"'),
(3848, 37, 60, 'İşte büyük zafer budur.'),
(3849, 37, 61, 'Çalışanlar bunun için çalışmalı.'),
(3850, 37, 62, 'Bu mu daha iyi bir duraktır, yoksa zakkum ağacı mı?'),
(3851, 37, 63, 'Biz onu zalimler için bir test kıldık.'),
(3852, 37, 64, 'O, cehennemin dibinden çıkan bir ağaçtır.'),
(3853, 37, 65, 'Tomurcukları şeytanların başı gibidir.'),
(3854, 37, 66, 'Onlar ondan yiyerek karınlarını doyuracaklar.'),
(3855, 37, 67, 'Bunun üstüne onlar için cehennemi bir kokteyl vardır.'),
(3856, 37, 68, 'Sonra dönüşleri yine cehennemedir.'),
(3857, 37, 69, 'Onlar, atalarını sapık kimseler olarak bulmuşlardı.'),
(3858, 37, 70, 'Ve onların izlerini körükörüne izliyorlardı.'),
(3859, 37, 71, 'Kendilerinden önce de niceleri aynı şekilde sapmıştı.'),
(3860, 37, 72, 'İçlerinden uyarıcılar göndermiştik.'),
(3861, 37, 73, 'Uyarılanların sonunun nasıl olduğuna bir bak.'),
(3862, 37, 74, 'Kendilerini sadece ALLAH\'a adayan kulları hariç.'),
(3863, 37, 75, 'Nuh bize seslenmişti de ne güzel karşılık vermiştik.'),
(3864, 37, 76, 'Onu ve ailesini o büyük felaketten kurtarmıştık.'),
(3865, 37, 77, 'Onun soyunu ise yaşattık.'),
(3866, 37, 78, 'Ve biz onu daha sonrakiler için bıraktık.'),
(3867, 37, 79, 'Tarih boyunca Nuh\'a selam.'),
(3868, 37, 80, 'Biz güzel davrananları işte böyle ödüllendiririz.'),
(3869, 37, 81, 'O, bizim inanan kullarımızdandı.'),
(3870, 37, 82, 'Sonra diğerlerini boğduk.'),
(3871, 37, 83, 'İbrahim onun bir kolundan idi.'),
(3872, 37, 84, 'Rabbine tertemiz bir kalp ile gelmişti.'),
(3873, 37, 85, 'Babasına ve halkına, "Neye tapıyorsunuz?" demişti.'),
(3874, 37, 86, '"ALLAH\'ın dışında, uyduruk tanrılar mı istiyorsunuz?"'),
(3875, 37, 87, '"Evrenlerin Rabbini ne zannediyorsunuz?"'),
(3876, 37, 88, 'Yıldızlara bir göz attı.'),
(3877, 37, 89, '"Bıktım, yoruldum artık," dedi.'),
(3878, 37, 90, 'Onlar da onu bırakıp gittiler.'),
(3879, 37, 91, 'Sonra, tanrılarına yöneldi ve "Yemez misiniz?" dedi.'),
(3880, 37, 92, '"Neyiniz var, neden konuşmuyorsunuz?"'),
(3881, 37, 93, 'Ve üzerlerine yürüyüp tüm gücüyle vurdu.'),
(3882, 37, 94, 'Hemen ona doğru koşuştular'),
(3883, 37, 95, 'Dedi ki, "Yonttuğunuz şeylere mi tapıyorsunuz?".'),
(3884, 37, 96, '"ALLAH, sizi de ve yaptığınız şeyleri de yaratandır."'),
(3885, 37, 97, 'Dediler ki, "Onun için bir yapı kurun ve onu ateşe atın."'),
(3886, 37, 98, 'Onun için bir plan düşündüler, fakat biz onları altettik.'),
(3887, 37, 99, 'Dedi ki, "Ben Rabbime gidiyorum; O bana yol gösterir."'),
(3888, 37, 100, '"Rabbim, bana erdemli birini bağışla."'),
(3889, 37, 101, 'Biz de ona yumuşak huylu bir erkek çocuk bağışladık.'),
(3890, 37, 102, 'Onunla birlikte çalışma çağına varınca, "Oğlum," dedi, "Rüyamda seni boğazlamam gerektiğini görüyorum. Ne düşünüyorsun?" "Babacığım," dedi, "Sana emredileni uygula. ALLAH dilerse beni sabırlı bulacaksın."'),
(3891, 37, 103, 'Böylece ikisi de teslim oldu ve onu alnı üzerine yıktı.'),
(3892, 37, 104, 'Kendisine, "İbrahim!" diye seslendik,'),
(3893, 37, 105, '"Sen rüyanı uyguladın." İyileri böyle ödüllendiririz.'),
(3894, 37, 106, 'Gerçekten bu apaçık bir sınavdı.'),
(3895, 37, 107, 'Ve biz ona fidye olarak büyük bir kurban verdik.'),
(3896, 37, 108, 'Sonrakiler için onun tarihini koruduk.'),
(3897, 37, 109, 'İbrahim\'e selam olsun.'),
(3898, 37, 110, 'Biz iyi davrananları böyle ödüllendiririz.'),
(3899, 37, 111, 'O, bizim inanan kullarımızdandı.'),
(3900, 37, 112, 'Ona İshak\'ı müjdeledik, erdemlilerden bir peygamber olarak.'),
(3901, 37, 113, 'Ona da İshak\'a da lütufta bulunduk. Kuşkusuz, ikisinin de soyundan hem iyi davrananlar var, hem kendisine zulmedenler.'),
(3902, 37, 114, 'Biz Musa\'ya ve Harun\'a iyilikte bulunmuştuk.'),
(3903, 37, 115, 'İkisini ve halklarını o büyük felaketten kurtardık.'),
(3904, 37, 116, 'Onlara yardım ettik de üstün geldiler.'),
(3905, 37, 117, 'Ve o ikisine apaçık anlaşılan kitabı verdik.'),
(3906, 37, 118, 'Her ikisini doğru yola ilettik.'),
(3907, 37, 119, 'O ikisinin tarihini sonrakiler için koruduk.'),
(3908, 37, 120, 'Musa\'ya ve Harun\'a selam (barış) olsun.'),
(3909, 37, 121, 'Biz, iyi davrananları işte böyle ödüllendiririz.'),
(3910, 37, 122, 'O ikisi bizim inanan kullarımızdandı.'),
(3911, 37, 123, 'İlyas elçilerden biriydi.'),
(3912, 37, 124, 'Halkına, "Erdemli olmayacak mısınız?" dedi.'),
(3913, 37, 125, 'En güzel Yaratanı bırakıp Ba\'le mi taparsınız?'),
(3914, 37, 126, 'ALLAH sizin ve geçmişteki atalarınızın Rabbidir.'),
(3915, 37, 127, 'Onu yalanladılar; onlar hesaba çekileceklerdir.'),
(3916, 37, 128, 'Kendilerini sadece ALLAH\'a adayan kulları hariç.'),
(3917, 37, 129, 'Sonrakiler için onun tarihini koruduk.'),
(3918, 37, 130, 'İlyasin\'e salam olsun.'),
(3919, 37, 131, 'İyi davrananları biz böyle ödüllendiririz.'),
(3920, 37, 132, 'O bizim inanan kullarımızdandı.'),
(3921, 37, 133, 'Lut da elçilerden biriydi.'),
(3922, 37, 134, 'Onu ve ailesini topluca kurtardık.'),
(3923, 37, 135, 'Ancak geride kalan yaşlı kadın hariç.'),
(3924, 37, 136, 'Sonra diğerlerini yok ettik.'),
(3925, 37, 137, 'Siz yıkıntılarının yanından geçiyorsunuz; sabahleyin,'),
(3926, 37, 138, 'Ve geceleyin. Aklınızı kullanmaz mısınız?'),
(3927, 37, 139, 'Yunus da elçilerden biriydi.'),
(3928, 37, 140, 'Dolu bir gemiye kaçmıştı.'),
(3929, 37, 141, 'Karşı çıktı ve kayanlardan oldu.'),
(3930, 37, 142, 'Balık onu yuttu, bundan o sorumluydu.'),
(3931, 37, 143, '(Tanrı\'yı) anıp düşünmeseydi,'),
(3932, 37, 144, 'Diriliş Gününe kadar onun karnında kalacaktı.'),
(3933, 37, 145, 'Onu çöl gibi bir sahile attık, yorgun ve bitkin...'),
(3934, 37, 146, 'Ve onun için orada geniş yapraklı ağaç yetiştirdik.'),
(3935, 37, 147, 'Biz onu yüzbin veya daha çok kişiye gönderdik.'),
(3936, 37, 148, 'İnandılar, biz de onları bir süreye kadar geçindirdik.'),
(3937, 37, 149, 'Sor onlara, kızları senin Rabbine, erkekleri kendilerine mi ayırıyorlar?"'),
(3938, 37, 150, 'Yoksa melekleri, onların gözü önünde dişi olarak mı yarattık?'),
(3939, 37, 151, 'Aslında onlar uydurdukları yüzünden diyorlar ki:'),
(3940, 37, 152, '"ALLAH doğurdu." Onlar yalancıdırlar.'),
(3941, 37, 153, 'Kızları erkeklere mi tercih etti?'),
(3942, 37, 154, 'Size ne oldu, nasıl karar veriyorsunuz?'),
(3943, 37, 155, 'Öğüt almaz mısınız?'),
(3944, 37, 156, 'Yoksa apaçık bir delile mi sahipsiniz?'),
(3945, 37, 157, 'Doğruysanız kitabınızı getirin.'),
(3946, 37, 158, 'Hatta O\'nunla cinler arasında bir akrabalık uydurdular. Halbuki cinler sorguya çekileceklerini bilirler.'),
(3947, 37, 159, 'ALLAH onları yakıştırmalarından çok Yücedir.'),
(3948, 37, 160, 'Kendilerini sadece ALLAH\'a adayan kulları hariç.'),
(3949, 37, 161, 'Siz ve tapmakta olduklarınız,'),
(3950, 37, 162, 'O\'na karşı kimseyi saptıramazsınız.'),
(3951, 37, 163, 'Ancak cehennemde yanacaklar hariç.'),
(3952, 37, 164, 'Her birimizin belli bir görevi vardır.'),
(3953, 37, 165, 'Biz, dizenleriz,'),
(3954, 37, 166, 'Biz, anıp yüceltenleriz.'),
(3955, 37, 167, 'Diyorlardı ki,'),
(3956, 37, 168, '"Yanımızda öncekilerden bir uyarı bulunsaydı,"'),
(3957, 37, 169, '"Kendimizi ALLAH\'a adar, sadece O\'na kul olurduk."'),
(3958, 37, 170, 'Böylece onu inkar ettiler; ileride bilecekler.'),
(3959, 37, 171, 'Elçilikle görevli kullarımız için söz verilmiştir.'),
(3960, 37, 172, 'Onlar elbette zafere ulaşacaklar.'),
(3961, 37, 173, 'Bizim ordumuz kesinlikle üstün gelecektir.'),
(3962, 37, 174, 'Öyleyse bir süre için onlara aldırış etme.'),
(3963, 37, 175, 'Onları seyret; onlar da görecekler.'),
(3964, 37, 176, 'Azabımıza mı meydan okuyorlar?'),
(3965, 37, 177, 'Yurtlarına inince uyarılanların sabahı ne kötü olur!'),
(3966, 37, 178, 'Bir süreye kadar onlara aldırış etme.'),
(3967, 37, 179, 'Onları gözle; onlar da görecekler.'),
(3968, 37, 180, 'Üstünlük ve onurun sahibi olan Rabbin, onların nitelemelerinden çok yücedir.'),
(3969, 37, 181, 'Gönderilmiş elçilere selam olsun.'),
(3970, 37, 182, 'Evrenlerin Rabbi olan ALLAH\'a övgüler olsun.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'SS; mesajı içeren bu Kuran\'a andolsun.'),
(3972, 38, 2, 'Doğrusu, inkar edenler kibir ve ayrılık içindedir.'),
(3973, 38, 3, 'Onlardan önce nice nesilleri yok ettik. Feryat ettiler, ancak artık kurtuluş zamanı değildi.'),
(3974, 38, 4, 'Onlara, kendilerinden bir uyarıcının gelmesini yadırgadılar. İnkarcılar, "Bu pek yalancı bir büyücüdür," dediler,'),
(3975, 38, 5, '"Tanrıları tek tanrı mı yaptı? Bu, gerçekten çok tuhaf!"'),
(3976, 38, 6, 'Onların liderleri öne fırladılar, "Yürüyün, tanrılarınıza bağlı kalın. Sizden istenen sadece budur."'),
(3977, 38, 7, '"Son dinde böylesini işitmedik. Bu bir uydurmadır."'),
(3978, 38, 8, '"Mesaj, neden aramızdan ona indirildi?" Aslında, onlar mesajımdan kuşku içindedirler. Hayır, onlar azabı henüz tatmadılar.'),
(3979, 38, 9, 'Yoksa Üstün olan ve Bahşeden Rabbinin rahmet hazineleri onların yanında mıdır?'),
(3980, 38, 10, 'Yoksa göklerin, yerin ve aralarındakilerin yönetimi onlara mı aittir? Bırak, kendi kendilerini yüceltsinler.'),
(3981, 38, 11, 'Aksine onlar parti parti yenilgiye uğramış bir ordudur.'),
(3982, 38, 12, 'Onlardan önce Nuh halkı, Ad ve piramitler sahibi Firavun da yalanlamıştı.'),
(3983, 38, 13, 'Semud, Nuh halkı ve Eykeliler de... Onlar partilerdi.'),
(3984, 38, 14, 'Hepsi de elçileri yalanladılar ve cezamı hakkettiler.'),
(3985, 38, 15, 'Bunlar, onması olmayan bir tek patlama bekliyorlar.'),
(3986, 38, 16, 'Ve, "Rabbimiz, Hesap Gününden önce acele olarak payımızı ver," diye meydan okudular.'),
(3987, 38, 17, 'Onların sözlerine karşı sabırlı ol, becerikli bir kulumuz olan Davud\'u anımsa; o (Tanrı\'ya) sürekli itaat ederdi.'),
(3988, 38, 18, 'Dağları onun emrine vermiştik; onunla birlikte akşamleyin ve tan doğumu (Tanrı\'yı) yüceltirlerdi.'),
(3989, 38, 19, 'Kuşlar da toplanmıştı; hepsi onun buyruğunu izlerdi.'),
(3990, 38, 20, 'Yönetimini güçlendirdik; ona bilgelik ve çok iyi bir yargılama gücü verdik.'),
(3991, 38, 21, 'Davacıların haberi sana ulaştı mı? Hani mabedine tırmanmışlardı.'),
(3992, 38, 22, 'Davud\'un yanına girdiklerinde onlardan irkilmişti. "Korkma" demişlerdi, "Bir birinin hakkını çiğneyen iki davacı... Aramızda gerçeğe göre hüküm ver, haksızlık etme. Bize yolun ortasını göster."'),
(3993, 38, 23, '"Bu benim kardeşimdir. Onun doksan dokuz koyunu, benim ise bir tek koyunum var. Böyle iken, "Onu da bana ver," dedi ve tartışmada bana üstün geldi.'),
(3994, 38, 24, 'Dedi ki, "Senin koyununu kendi koyunlarına katmayı istemekle sana haksızlık etmiştir. Doğrusu, ortakçıların çoğu bir birinin hakkına el uzatır. İnanıp erdemli davrananlar bunun dışındadır, onlar ise sayıca ne kadar azdır!" Davud, kendisini sınadığımızı sanarak bağışlanma diledi, eğildi ve tevbe etti.'),
(3995, 38, 25, 'Böylece onu bağışladık. Yanımızda onun yakınlığı ve güzel bir yeri vardır.'),
(3996, 38, 26, 'Ey Davud, biz seni yeryüzünde yönetici kıldık. Halkın arasında adaletle yargı ver, hevesine ve duygularına kapılma, sonra seni ALLAH\'ın yolundan saptırır. ALLAH\'ın yolundan sapanlara, Hesap Gününü unuttukları için çetin bir ceza vardır.'),
(3997, 38, 27, 'Göğü, yeri ve aralarındakileri boş yere yaratmadık. Bu inkar edenlerin sanısıdır. Kendilerini ateşe soktukları için inkar edenlere yazıklar olsun.'),
(3998, 38, 28, 'İnanıp iyi davrananları, yeryüzünde bozgunculuk yapanlar gibi mi tutacağız? Erdemlilere, yoldan çıkanlar gibi mi davranacağız?'),
(3999, 38, 29, 'Sana indirdiğimiz bu kitap kutludur; ayetlerini incelesinler ve akıl sahipleri öğüt alsınlar.'),
(4000, 38, 30, 'Davud\'a, Süleyman\'ı verdik. İyi bir kuldu, (Tanrı\'ya) sürekli yönelen biriydi.'),
(4001, 38, 31, 'Bir akşam, ona güzel koşu atları sunulmuştu.'),
(4002, 38, 32, 'Dedi ki, "Mal ve mülk sevgisi beni Rabbimi anmaktan alıkoydu. Ta ki, o (güneş) bir örtünün ardından kayboldu."'),
(4003, 38, 33, '"Onları bana geri getirin," dedi, (veda etmek için) bacaklarını ve boyunlarını okşadı.'),
(4004, 38, 34, 'Süleyman\'ı böylece sınadık; onun hükümranlığına maddi zenginlik kattık; ancak o tümüyle (Tanrı\'ya) yöneldi.'),
(4005, 38, 35, 'Dedi ki, "Rabbim beni bağışla. Bana, benden sonra kimsenin ulaşamıyacağı bir yönetim ver. Sen Bahşedensin."'),
(4006, 38, 36, 'Bunun üzerine komutuyla hareket eden rüzgarı onun emrine verdik. Dilediği yere yağmur yağdırırdı.'),
(4007, 38, 37, 'Şeytanları da, yapı ustaları ve dalgıçlar olarak...'),
(4008, 38, 38, 'Ve zincirlerle birbirine bağlı bekleyen yedekleri de...'),
(4009, 38, 39, '"Bu bizim bağışımızdır. İster ver, ister tut, tükenmez."'),
(4010, 38, 40, 'Onun bizim yanımızda yakınlığı ve güzel yeri vardı.'),
(4011, 38, 41, 'Kulumuz Eyyub\'u an: "Rabbim, şeytan bana bitkinlik ve acı dokundurdu," diye Rabbine seslenmişti.'),
(4012, 38, 42, '"Ayağını yere vur. İşte yıkanacak ve içilecek soğuk bir kaynak."'),
(4013, 38, 43, 'Çoluk çocuğunu bir katıyla birlikte ona geri verdik. Bu, akıl sahiplerine bizden bir rahmet ve mesajdır.'),
(4014, 38, 44, '"Şimdi eline bir değnek alıp yola çık. Yeminini bozma." Onu sabırlı bulduk. Ne iyi bir kul! Sürekli yönelirdi.'),
(4015, 38, 45, 'Kullarımız İbrahim, İshak ve Yakub\'u anımsa. Becerikli idiler, ileri görüşlü idiler.'),
(4016, 38, 46, 'Salt ahireti düşündükleri için onları salt/dupduru kıldık.'),
(4017, 38, 47, 'Onlar bizim yanımızda iyilerden seçilmiş kimselerdi.'),
(4018, 38, 48, 'İsmail\'i, Elyesa\'ı ve Zülkifl\'i de anımsa; hepsi iyilerdendi.'),
(4019, 38, 49, 'Bu bir mesajdır: Erdemliler için güzel bir gelecek,'),
(4020, 38, 50, 'Ve kapıları kendilerine açılmış Adn cennetleri vardır.'),
(4021, 38, 51, 'Orada konfor içinde bol meyve ve içecek isterler.'),
(4022, 38, 52, 'Yanlarında gözlerinin içine bakan yaşıtları vardır.'),
(4023, 38, 53, 'Hesap Günü için size söz verilen budur.'),
(4024, 38, 54, 'Bizim bu rızkımız tükenmez.'),
(4025, 38, 55, 'Bu böyledir; azgınlar ise kötü bir yeri hakederler.'),
(4026, 38, 56, 'Cehennemde yanarlar; ne kötü bir duraktır.'),
(4027, 38, 57, 'İşte onu tatsınlar: Kaynar su ve irin.'),
(4028, 38, 58, 'Bunlara benzer daha başkaları da vardır.'),
(4029, 38, 59, '"Bunlar sizinle birlikte paldır küldür sürüklenen bir gruptur." (denilince, cehennemdeki liderler:) "Onlar hoş gelmediler. Onlar ateşte yanacaklar."'),
(4030, 38, 60, 'Onlar da derler ki, "Aslında siz hoş gelmediniz. Bizi bu duruma siz soktunuz; ne kötü bir son!"'),
(4031, 38, 61, '"Rabbimiz, kim bizi bu duruma soktuysa onun cezasını ateşte ikiye katlayarak arttır," diye eklerler.'),
(4032, 38, 62, '"Nasıl oluyor da kötü olarak saydığımız insanları göremiyoruz?"'),
(4033, 38, 63, '"Onlarla alay edip durduk. Yoksa onları gözlerimizden mi kaçırdık?"'),
(4034, 38, 64, 'Cehennem halkının birbiriyle çekişmesi bir gerçektir.'),
(4035, 38, 65, 'De ki, "Ben sadece bir uyarıcıyım. Tek ve Egemen olan ALLAH\'tan başka bir tanrı yoktur."'),
(4036, 38, 66, 'Göklerin, yerin ve aralarındakilerin Rabbi; Üstündür, Bağışlayandır.'),
(4037, 38, 67, 'De ki, "Bu, büyük bir haberdir."'),
(4038, 38, 68, '"Siz ise ondan yüz çeviriyorsunuz."'),
(4039, 38, 69, '"Onlar tartışırlarken Yüce Toplum\'da neler olup bittiği hakkında bir bilgim yoktu."'),
(4040, 38, 70, '"Apaçık bir uyarıcı olduğum için bana vahyediliyor."'),
(4041, 38, 71, 'Rabbin meleklere demişti ki, "Balçıktan bir insan yaratacağım."'),
(4042, 38, 72, '"Onu düzenlediğim ve ona ruhumdan üflediğim zaman ona secdeye kapanın."'),
(4043, 38, 73, 'Tüm melekler ona secdeye kapandı;'),
(4044, 38, 74, 'Ancak İblis hariç. Büyüklük tasladı ve nankör oldu.'),
(4045, 38, 75, '"Ey İblis, ellerimle yarattığıma secde etmekten seni alıkoyan nedir? Büyüklük mü tasladın? Yoksa baş mı kaldırdın?" dedi.'),
(4046, 38, 76, '"Ben ondan daha üstünüm," dedi, "Beni ateşten yarattın, onu ise balçıktan yarattın."'),
(4047, 38, 77, '"Çık oradan," dedi, "Sen kovuldun."'),
(4048, 38, 78, '"Yargı Gününe kadar lanetimi hakettin."'),
(4049, 38, 79, 'Dedi ki, "Rabbim, dirilecekleri güne dek beni ertele."'),
(4050, 38, 80, 'Dedi ki, "Sana süre verilmiştir;"'),
(4051, 38, 81, '"Bilinen vaktin gününe kadar."'),
(4052, 38, 82, 'Dedi ki, "Büyüklüğüne andolsun, tümünü azdıracağım."'),
(4053, 38, 83, '"Ancak onlardan kendilerini sadece sana adayan kulların hariç."'),
(4054, 38, 84, 'Dedi ki, "Bu gerçektir ve ben sadece gerçeği söylerim:"'),
(4055, 38, 85, '"Cehennemi seninle ve onlardan seni izliyenlerle topluca dolduracağım."'),
(4056, 38, 86, 'De ki, "Buna karşılık olarak sizden bir ücret istemiyorum. Ben bir sahtekar değilim."'),
(4057, 38, 87, '"Bu, tüm dünyaya bir mesajdır."'),
(4058, 38, 88, '"Ve onun haberlerini bir süre sonra öğreneceksiniz."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'Kitabın indirilmesi, Üstün ve Bilge olan ALLAH\'tandır.'),
(4060, 39, 2, 'Biz bu kitabı sana gerçekle indirdik, öyleyse ALLAH\'a kulluk et; dinini sadece O\'na has kılarak...'),
(4061, 39, 3, 'Kesinlikle, din sadece ALLAH\'a aittir. O\'nun dışındakileri evliya (dostlar) olarak edinenler, "Onlar bizi ALLAH\'a daha fazla yaklaştırsın diye biz onlara tapıyoruz." (derler). Ayrılığa düştükleri bu konuda onların arasında ALLAH karar verecektir. ALLAH kuşkusuz, yalancıları ve nankörleri doğru yola iletmez.'),
(4062, 39, 4, 'ALLAH bir çocuk dileseydi, yarattıklarının arasından dilediğini seçerdi. O çok yücedir. O, Tek ve Egemen olan ALLAH\'tır'),
(4063, 39, 5, 'Gökleri ve yeri gerçek ile yarattı. Geceyi gündüzün üzerine yuvarlar ve gündüzü de gecenin üzerine yuvarlar. Güneşi ve ayı da emri altına sokmuş ve onların her biri belli bir süreye kadar akıp gitmektedir. Kesinlikle, O Üstündür, Bağışlayandır.'),
(4064, 39, 6, 'Sizi bir tek kişiden yarattı, sonra ondan da eşini var etti. Size sekiz tür çiftlik hayvanı indirdi. Sizi annelerinizin karınlarında, üç karanlık içinde yaratılıştan yaratılışa geçirerek yaratmaktadır. Rabbiniz olan ALLAH işte budur. Egemenlik O\'na aittir. O\'ndan başka tanrı yoktur. Nasıl da çevriliyorsunuz?'),
(4065, 39, 7, 'İnkar ederseniz, ALLAH\'ın size ihtiyacı yoktur. Fakat O, kullarının inkarcı olmasından hoşlanmaz. Şükrederseniz sizden hoşnut olur. Kimse kimsenin günah yükünü çekmez. Sonra dönüşünüz Rabbinizedir ve yapmış olduklarınızı size haber verecektir. O, göğüslerin özünü bilir'),
(4066, 39, 8, 'İnsana bir kötülük dokundu mu Rabbine yönelerek O\'nu çağırır. Ona kendisinden bir iyilik verdi mi, daha önce kime yalvarmış olduğunu unutur ve O\'nun yolundan saptırmak için ALLAH \'a eşler koşmağa başlar. De ki, "İnkarınla biraz yaşa, sen ateş halkındansın."'),
(4067, 39, 9, 'Geceleyin secde ederek ve ayakta durarak meditasyonda bulunan, ahiretin bilincinde olan ve Rabbinin rahmetini uman birine ne dersiniz? De ki, "Bilenlerle bilmeyenler bir olur mu?" Ancak akıl sahipleri öğüt alır'),
(4068, 39, 10, 'De ki, "Ey inanan kullar, Rabbinizi sayın. Bu dünya hayatında güzel davrananlara güzellik vardır. ALLAH\'ın yeri geniştir. Gerçek uğrunda direnenlerin ücreti hesapsız olarak verilecektir.'),
(4069, 39, 11, 'De ki, "Dini yalnız ALLAH\'a has kılarak O\'na tapmakla emredildim."'),
(4070, 39, 12, '"Ve en ileri derecede müslüman olmam emredildi."'),
(4071, 39, 13, 'De ki, "Rabbime karşı gelirsem Büyük Günün azabından korkarım."'),
(4072, 39, 14, 'De ki, "Dinimi yalnız ALLAH\'a has kılarak O\'na tapıyorum."'),
(4073, 39, 15, '"Siz de O\'nun dışında dilediğinize tapınız."De ki, "Asıl kaybedenler, ahiret gününde kendilerine ve ailelerine kaybettirenlerdir." Apaçık kayıp budur'),
(4074, 39, 16, 'Onların üstünde de altlarında da kat kat ateş vardır. ALLAH kullarını böylece uyarır. Kullarım, beni sayın'),
(4075, 39, 17, 'Kim Tağut\'a (Azgın\'a) kulluk etmekten kaçınır ve ALLAH\'a yönelirse onlar için müjde vardır. Kullarımı müjdele.'),
(4076, 39, 18, 'Onlar ki sözü (kavl) dinlerler ve en güzeline uyarlar. Onlar, ALLAH\'ın yol gösterdiği kimselerdir. Onlar akıl sahipleridir'),
(4077, 39, 19, 'Azap sözünü hakedenlere gelince, sen ateştekini kurtarabilir misin?'),
(4078, 39, 20, 'Ancak Rab\'lerini sayanlar için üstüste kurulmuş köşkler vardır; altlarından ırmaklar akar. ALLAH\'ın sözüdür. Kuşkusuz ALLAH sözünden caymaz'),
(4079, 39, 21, 'Görmez misin ki ALLAH gökten bir su indirir ve onu yeryüzündeki kaynaklara geçirir ve sonra onunla çeşitli renklerde bitkiler çıkarır. Sonra o kurur ve onu sararmış görürsün. Sonra da onu çerçöpe çevirir. Bunda akıl sahipleri için öğüt vardır'),
(4080, 39, 22, 'ALLAH kimin göğsünü İslama açarsa o Rabbinden bir ışık üzerindedir. ALLAH\'ın mesajına karşı kalpleri katılaşanların vay haline. Onlar açık bir sapıklıktadır.'),
(4081, 39, 23, 'ALLAH en güzel hadisi, tutarlı ve ikişerli bir kitap halinde indirdi. Rab\'lerini sayanların derileri ondan dolayı ürperir. Sonra derileri ve kalpleri ALLAH\'ın mesajına karşı yumuşar. Bu, ALLAH\'ın yol göstermesidir; dilediğini ve/veya dileyeni ona ulaştırır. ALLAH\'ın saptırdığı bir kimseye rehber bulunmaz'),
(4082, 39, 24, 'Diriliş Gününün azabından yüzünü koruyan kimse gibisi var mıdır? Zalimlere, "Kazandığınızın sonucunu tadın," denir'),
(4083, 39, 25, 'Onlardan öncekiler de yalanladılar ve ceza, onların beklemedikleri bir yerden gelmişti.'),
(4084, 39, 26, 'ALLAH onlara dünya hayatında rezilliği tattırdı. Ahiret cezası ise daha büyüktür. Bir bilselerdi.'),
(4085, 39, 27, 'Biz bu Kuran\'da, insanlara, her türlü örneği verdik ki öğüt alsınlar'),
(4086, 39, 28, 'Arapça, pürüzsüz bir Kuran, belki erdemli olurlar'),
(4087, 39, 29, 'ALLAH, çelişen ortaklara sahip bir adam ile bir tek kişiye bağlı olan adamın örneğini verir. Bu ikisinin durumu hiç eşit olur mu? Övgü ALLAH\'adır. Ancak çokları bilmez.'),
(4088, 39, 30, 'Sen öleceksin, onlar da ölecekler'),
(4089, 39, 31, 'Sonra Diriliş Günü, Rabbinizin huzurunda duruşmaya çıkacaksınız'),
(4090, 39, 32, 'ALLAH\'a karşı yalan uydurandan ve kendisine gerçek geldiğinde onu yalanlayandan daha zalim kim olabilir? Kafirler için cehennemde bir yer yok mu?'),
(4091, 39, 33, 'Gerçeği getiren ve onu doğrulayana gelince, işte onlar erdemlilerdir'),
(4092, 39, 34, 'Onlar için Rab\'leri katında diledikleri herşey vardır. İyi davrananların ödülü böyledir'),
(4093, 39, 35, 'Böylece ALLAH onların en kötü işlerini bağışlar ve yapmış olduklarının karşılığını da en güzel bir biçimde verir'),
(4094, 39, 36, 'ALLAH kuluna yetmez mi? Seni O\'ndan başkalarıyla korkutuyorlar. ALLAH kimi saptırırsa ona bir yol gösteren bulunmaz'),
(4095, 39, 37, 'ALLAH\'ın yol gösterdiğini de hiç kimse saptıramaz. ALLAH Üstün ve Öc Alan değil midir?'),
(4096, 39, 38, 'Onlara, "Gökleri ve yeri kim yarattı?" diye sorsan, "ALLAH," diyecekler. De ki, "ALLAH\'ın dışında taptıklarınıza ne dersiniz? ALLAH benim için bir zarar dilese onlar O\'nun zararını giderebilir mi? Yahut O benim için bir rahmet dilese onlar O\'nun rahmetini tutabilir mi? De ki, "ALLAH bana yeter." Güvenenler O\'na güvensinler'),
(4097, 39, 39, 'De ki, "Ey halkım, bildiğinizi yapın, ben de yapacağım. İleride bileceksiniz:"'),
(4098, 39, 40, '"Alçaltıcı azap kime geliyormuş ve sürekli azabı kim hakediyormuş!"'),
(4099, 39, 41, 'Biz bu kitabı sana halk için gerçekle indirdik. Kim doğruyu bulursa kendisi içindir. Kim saparsa da kendisi aleyhine sapmış olur. Sen onların avukatı değilsin.'),
(4100, 39, 42, 'ALLAH ölümü anında nefsi (bilinci) alır; ölmeyenleri de uyku anında... Hakkında ölüm kararı verdiklerini tutar ve diğerlerini de belli bir süreye kadar salıp gönderir. Düşünen bir topluluk için bunda dersler ve işaretler vardır.'),
(4101, 39, 43, 'ALLAH\'ın dışında şefaatçılar mı edindiler? De ki, "Onlar hiç bir şeye sahip değilseler ve düşünemiyorlarsa da mı?"'),
(4102, 39, 44, 'De ki, "Tüm şefaat ALLAH\'a aittir." Göklerin ve yerin yönetimi O\'na aittir. Sonra O\'na döndürüleceksiniz.'),
(4103, 39, 45, 'ALLAH tek başına anıldığı an ahirete inanmıyanların kalpleri huzursuz olarak ürker. Fakat O\'nun dışındakiler anıldığı zaman hemen yüzleri güler.'),
(4104, 39, 46, 'De ki, "Gökleri ve yeri yoktan var eden, gizliyi ve açığı Bilen Tanrım, ayrılığa düştükleri konularda kulların arasında hükmü yalnız sen verirsin."'),
(4105, 39, 47, 'Zalimler, yerdekilerin tümüne ve hatta onun bir katına daha sahip olsaydı bile, Diriliş Gününün o müthiş azabından kurtulmak için onu fidye olarak vereceklerdi. Kendilerine, ALLAH tarafından hiç beklemedikleri bir şey gösterilecektir'),
(4106, 39, 48, 'İşledikleri kötülükler onlara gösterilir ve alay etmekte oldukları şeyler ise onları çepeçevre sarar.'),
(4107, 39, 49, 'İnsana bir kötülük dokunduğu zaman bizi çağırır; ancak ona bir nimet verdiğimiz zaman ise: "Bu, bana bilgimden dolayı verilmiştir," der. Aslında o bir testtir, ne var ki çokları bilmez'),
(4108, 39, 50, 'Kendilerinden öncekiler aynı şeyi söylediler, ancak kazandıkları şeylerin kendilerine hiç bir yararı dokunmadı'),
(4109, 39, 51, 'Kazandıkları kötülükler, sonunda başlarına geldi. Aynı biçimde şu zulmedenlere de kazandıkları kötülükler çatacaktır; engelleyemezler'),
(4110, 39, 52, 'ALLAH\'ın dilediğine rızkı genişletip daralttığını bilmezler mi? İnanan bir toplum için bunda dersler vardır.'),
(4111, 39, 53, 'Onlara bildir: "Kendilerine karşı sınırı aşan kullarım, ALLAH\'ın rahmetinden ümit kesmeyin. ALLAH tüm günahları affedicidir. O Bağışlayandır, Rahimdir."'),
(4112, 39, 54, 'Rabbinize yönelin ve cezaya çarpılmadan önce O\'na teslim olun, zira o zaman yardım görmezsiniz'),
(4113, 39, 55, 'O azap beklemediğiniz anda sizi ansızın yakalamadan önce, Rabbiniz tarafından size indirilen en güzel öğretiye uyun.'),
(4114, 39, 56, 'Ki kişi, "ALLAH\'ın öğretisine aldırmamaktan ötürü vay halime; doğrusu ben alay edenlerdendim," demesin'),
(4115, 39, 57, 'Veya, "ALLAH beni doğruya iletseydi kesinlikle erdemlilerden biri olacaktım," demesin'),
(4116, 39, 58, 'Yahut azabı gördüğü an, "Keşke ikinci bir şansım daha olsaydı da güzel davrananlardan olsaydım," demesin'),
(4117, 39, 59, 'Evet, sana ayet ve mucizelerim geldi, fakat sen onları yalanladın, kibirlendin ve inkarcılardan oldun'),
(4118, 39, 60, 'Diriliş günü, ALLAH hakkında yalan uyduranların suratlarını asık görürsün. Cehennem, azgınlara tam uygun bir yer değil midir?'),
(4119, 39, 61, 'ALLAH erdemlileri başarılarıyla kurtarır. Onlara kötülük dokunmaz ve onlar üzülmezler de'),
(4120, 39, 62, 'ALLAH her şeyin Yaratıcısıdır. O, her şeyi kontrol eder'),
(4121, 39, 63, 'Göklerin ve yerin kararları O\'na aittir. ALLAH\'ın ayetlerini yalanlayanlar asıl kaybedenlerdir'),
(4122, 39, 64, 'De ki, "ALLAH\'tan başkasına mı kulluk etmeye beni teşvik ediyorsunuz ey cahiller?"'),
(4123, 39, 65, 'Sana ve senden öncekilere şöyle vahyedilmiştir: ortak koşarsan, tüm yaptıkların boşa gider ve kaybedenlerden olursun'),
(4124, 39, 66, 'Öyleyse yalnız ALLAH\'a kul ol ve şükret.'),
(4125, 39, 67, 'ALLAH\'ı gereği gibi değerlendiremediler. Halbuki, diriliş günü, tüm yer O\'nun avucu içindedir, gökler de O\'nun sağ elinde dürülmüştür. O Yücedir ve onların ortak koştuklarından çok üstündür.'),
(4126, 39, 68, 'Boruya üflenir üflenmez göklerde ve yerde kim varsa, ALLAH\'ın diledikleri hariç kendinden geçip bayılırlar. Sonra ona tekrar üflenir de onlar ayağa kalkıp bakışırlar'),
(4127, 39, 69, 'Yer, Rabbinin ışığıyla parlar. Kitap konur. Peygamberler ve tanıklar getirilir. Aralarında gerçeğe göre hüküm verilir ve onlara zulmedilmez'),
(4128, 39, 70, 'Her kişiye yaptığının karşılığı tam olarak verilir. O, onların yapmış olduğu her şeyi en iyi bilendir'),
(4129, 39, 71, 'İnkar edenler yığınlar halinde cehenneme götürülürler. Oraya vardıklarında onun kapıları açılır ve bekçileri onlara, "Size, Rabbinizin ayetlerini okuyan ve bu gününüzle karşılaşacağınız konusunda sizi uyaran sizden elçiler gelmemiş miydi?" derler. "Evet. Ancak \'ceza\' sözü inkarcılar hakkında gerçekleşmiştir," diye karşılık verirler'),
(4130, 39, 72, '"Temelli kalacağınız cehennemin kapılarından girin. Büyüklük taslayanların sonu ne de kötüymüş!" denir'),
(4131, 39, 73, 'Rablerini sayıp dinleyenler ise yığınlar halinde cennete götürülürler. Oraya vardıklarında onun kapıları açılır ve bekçileri onlara, "Size barış olsun; kazandınız. Haydi temelli kalmak üzere oraya giriniz," derler'),
(4132, 39, 74, '"Bize verdiği sözü gerçekleştiren, yeri bize miras bırakan ve cennette dilediğimiz yerde oturmamızı sağlayan ALLAH\'a övgüler olsun," derler. Çalışanların ücreti ne de güzelmiş!'),
(4133, 39, 75, 'Melekleri, yönetim merkezinin etrafını çevirmiş halde Rab\'lerini överek yüceltirken görürsün. Aralarında gerçeğe göre hüküm verilmiştir ve "Evrenin Rabbi ALLAH\'a övgüler olsun," denir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'HH. M.'),
(4135, 40, 2, 'Bu kitabın indirilmesi, Üstün ve Her Şeyi Bilen ALLAH katındandır.'),
(4136, 40, 3, 'Günahları Bağışlayan, Tevbeyi Kabul Eden, Cezası Şiddetli, Sonsuz Güç Sahibi... O\'ndan başka tanrı yoktur. Dönüş O\'nadır'),
(4137, 40, 4, 'İnkarcılardan başkası ALLAH\'ın ayetleri ve mucizelerine karşı tartışmaz. Onların görünür başarıları seni aldatmasın.'),
(4138, 40, 5, 'Onlardan önce Nuh\'un halkı da yalanlamıştı ve onlardan sonra bir çok parti de... Her topluluk elçilerini etkisiz hale getirmeye çalıştı. Gerçeği gidermek için boş ve yalnış şeylerle tartıştılar. Sonunda onları yakaladım; cezalandırmam nasılmış?'),
(4139, 40, 6, 'Rabbinin, "Onlar cehennemin halkıdır," diye inkarcılar hakkında verdiği sözü böylece gerçekleşir.'),
(4140, 40, 7, 'Yönetim merkezine hizmet edenler ve etrafındakiler Rab\'lerini överek yüceltirler ve O\'na inanırlar. İnananlar için bağışlanma dilerler: "Rabbimiz, rahmetin ve bilgin her şeyi içine almıştır. Tevbe edenler ve senin yoluna uyanları bağışla ve onları ateşin azabından koru."'),
(4141, 40, 8, '"Rabbimiz onları, erdemli atalarını, eşlerini ve çocuklarını söz verdiğin Adn cennetlerine sok. Kuşkusuz sen Üstünsün, Bilgesin."'),
(4142, 40, 9, '"Onları kötülüklerden koru. O gün kimi kötülüklerden korursan onlara rahmet etmişsindir. Büyük zafer budur."'),
(4143, 40, 10, 'İnkar etmiş olanlara, "ALLAH\'ın hoşnutsuzluğu, sizin kendi kendinize olan hoşnutsuzluğunuzdan daha büyüktür. İmana çağrıldığınızda inkar ederdiniz," diye seslenilir.'),
(4144, 40, 11, 'Diyecekler ki, "Rabbimiz, bizi iki kez öldürdün ve iki kez dirilttin. Şimdi günahlarımızı itiraf ettik. Buradan bir çıkış yolu var mı?"'),
(4145, 40, 12, 'Çünkü, YALNIZ ALLAH çağrıldığı zaman inkar ederdiniz. Ancak kendisine ortak koşulduğunda inanırdınız. Hüküm, Üstün ve Büyük olan ALLAH\'a aittir.'),
(4146, 40, 13, 'O ki size ayetlerini (kanıtlarını ve işaretlerini) göstermekte ve sizin için gökten bir rızık indirmektedir. Tümüyle yönelip teslim olandan başkası ibret almaz.'),
(4147, 40, 14, 'İnkarcılar hoşlanmasa da dini sadece ALLAH\'a ait kılarak O\'na kulluk edin.'),
(4148, 40, 15, 'Dereceleri Yükselten, Yönetim Sahibi. Buluşma gününe karşı uyarsın diye kullarından dilediğine, emirlerini içeren vahyini indirir.'),
(4149, 40, 16, 'O gün onlar tümüyle açığa çıkarılacaklardır. Hiç bir şey onları ALLAH\'tan gizleyemez. O gün yönetim kime aittir? Tek ve En Üstün olan ALLAH\'a aittir.'),
(4150, 40, 17, 'O gün her kişiye yaptığının karşılığı ödenir. O gün haksızlık yoktur. ALLAH hesabı çabuk görendir.'),
(4151, 40, 18, 'Onları yaklaşan gün hakkında uyar, o zaman yürekler gırtlaklara dayanmıştır, yutkunurlar. Zalimler için ne bir dost ne de sözü dinlenir bir şefaatçı vardır.'),
(4152, 40, 19, 'Gözlerin göremediğini ve göğüslerin gizlediğini bilir.'),
(4153, 40, 20, 'ALLAH adaletle yargı verir. O\'ndan başka çağırdıkları ise hiç bir şeye yargı veremezler. ALLAH İşitendir, Görendir.'),
(4154, 40, 21, 'Kendilerinden öncekilerin sonucunun nasıl olduğuna bakmak için yeryüzünde dolaşmıyorlar mı? Onlardan daha fazla güce sahiptiler, yeryüzünde daha çok üreticiydiler. Ancak ALLAH onları günahlarıyla yakaladı. ALLAH\'a karşı onları koruyabilecek yoktur.'),
(4155, 40, 22, 'Çünkü, elçiler onlara apaçık delillerle gittiklerinde inkar ediyorlardı. Sonunda ALLAH onları yakaladı. O Güçlüdür, Cezalandırması Şiddetlidir.'),
(4156, 40, 23, 'Musa\'yı ayetlerimiz ve apaçık bir yetki ile gönderdik.'),
(4157, 40, 24, 'Firavun\'a, Haman\'a ve Karun\'a... "Bu sihirbazın ve yalancının biridir," dediler.'),
(4158, 40, 25, 'Onlara bizden bir gerçeği götürünce, "Onunla birlikte inananların oğullarını öldürün, kadınlarını ise yaşatın," dediler. İnkarcıların planı hep sapıkçadır.'),
(4159, 40, 26, 'Firavun dedi, "Beni bırakın Musa\'yı öldüreyim de o da Rabbine yalvarsın. Sizin dininizi değiştireceğinden veya yeryüzünde kötülük çıkaracağından endişeleniyorum."'),
(4160, 40, 27, 'Musa dedi, "Ben, hesap gününe inanmıyan her azgından Rabbime ve sizin Rabbinize sığındım," dedi.'),
(4161, 40, 28, 'Firavun\'un tarafından inancını gizleyen inanan bir adam dedi ki, "Siz \'benim Rabbim ALLAH\'tır\' diyen bir adamı mı öldüreceksiniz? Halbuki size Rabbinizden apaçık deliller getirmiş bulunuyor. Yalancı ise, bu onun problemidir; yok doğru sözlü ise onun size anlattıkları gerçekleşir. Kuşkusuz ALLAH sınırı aşan yalancıları sevmez."'),
(4162, 40, 29, '"Ey halkım, yönetim bugün sizindir ve ülkede siz egemensiniz. Ancak ALLAH\'ın hışmı bize gelirse kim ona karşı bize yardım edebilir?" Firavun da, "Size kendi görüşümü öğütlüyorum; size doğru yolu gösteriyorum," dedi.'),
(4163, 40, 30, 'İnanmış adam dedi ki, "Ey halkım daha önceki partilerin günü gibi bir sonuca uğramanızdan korkuyorum."'),
(4164, 40, 31, '"Nuh, Ad, Semud ve onlardan sonrakilerin durumu gibi. Elbette ALLAH kullara haksızlık etmek dilemez."'),
(4165, 40, 32, '"Halkım, sizin için Toplanma Gününden korkuyorum."'),
(4166, 40, 33, '"O gün arkanızı dönüp kaçacaksınız; ancak sizi ALLAH\'tan koruyacak yoktur. ALLAH\'ın saptırdığına yol gösteren bulunmaz."'),
(4167, 40, 34, 'Daha önce Yusuf da size apaçık delillerle gelmişti; fakat size getirdiği mesajı sürekli olarak kuşku ile karşılamıştınız. Nihayet o ölünce, "ALLAH ondan sonra elçi göndermeyecektir (O son elçidir)," demiştiniz. ALLAH, kuşkuda sınırı aşanı böylece saptırır."'),
(4168, 40, 35, 'ALLAH\'ın ayet ve mucizelerine karşı, hiç bir delile sahip olmadan tartışanlar, hem ALLAH katında ve hem de inananlar katında büyük bir öfkeye muhataptır. ALLAH her kibirli zorbanın kalbini işte böyle mühürler.'),
(4169, 40, 36, 'Firavun dedi ki, "Haman, benim için bir kule dik de yollara erişeyim."'),
(4170, 40, 37, '"Göklerin yollarına... Böylece Musa\'nın tanrısına bir bakayım. Onun bir yalancı olduğuna inanıyorum." Firavun\'un kötü tavrı kendisine böyle süslenmişti ve böylece yoldan çıkarıldı. Firavun\'un planı elbette boşa çıkacaktı.'),
(4171, 40, 38, 'İnanan adam dedi ki, "Ey halkım, beni izleyin ki size doğru yolu göstereyim."'),
(4172, 40, 39, '"Ey halkım, bu dünya hayatı geçici bir geçinmedir. Ahiret yurdu ise ebedi bir duraktır."'),
(4173, 40, 40, 'Kim kötülük işlerse kendisine ancak onun kadar bir ceza verilir. Erkek veya kadın kim inançlı olarak erdemli işler yaparsa onlar cennete girerler ve orada hesapsız olarak nimetlenirler.'),
(4174, 40, 41, '"Ey halkım, neden ben sizi kurtuluşa çağırırken siz beni ateşe çağırıyorsunuz?"'),
(4175, 40, 42, '"Siz beni ALLAH\'a karşı nankör olmaya ve hakkında bilgim olmayan şeyleri ona ortak koşmaya çağırıyorsunuz. Bense sizi O Üstün ve Bağışlayıcı olana çağırıyorum."'),
(4176, 40, 43, '"Kuşku yok ki, beni kendisine çağırdığınız şeyin ne dünyada ne de ahirette bir dayanağı yoktur. Dönüşümüz ALLAH\'adır. Sınırı aşanlar, cehennemi boylayacaktır."'),
(4177, 40, 44, '"Size bu söylediklerimi ileride hatırlayacaksınız. Ben işimi ALLAH\'a bırakıyorum. ALLAH kullarını Görendir."'),
(4178, 40, 45, 'ALLAH onu onların planladıkları kötülüklerden korudu. Firavun\'un taraftarlarını azabın en kötüsü kuşattı.'),
(4179, 40, 46, 'Onlara gündüz ve akşam ateş sunulur. Dünyanın son gününde ise: "Firavun\'un taraftarlarını azabın en çetinine sokun."'),
(4180, 40, 47, 'Ateşte tartışırlarken, güçsüzler, büyüklük taslamış olanlara, "Biz sizin izleyicileriniz idik, öyleyse bu ateşin bir parçasını olsun bizden savabilir misiniz?" derler.'),
(4181, 40, 48, 'Büyüklük taslamış olanlar, "Hepimiz onun içindeyiz. ALLAH kulları arasında artık hüküm vermiştir," derler.'),
(4182, 40, 49, 'Ateştekiler, cehennemin gardiyanlarına, "Rabbinize yalvarın da bizden azabı hiç değilse bir gün hafifletsin," derler.'),
(4183, 40, 50, 'Onlar da derler ki, "Elçileriniz size apaçık delillerle gelmemiş miydi?" "Evet" derler. Bunun üzerine onlar, "Öyleyse kendiniz yalvarın. Ne var ki inkarcıların yalvarması sonuç vermez."'),
(4184, 40, 51, 'Biz elçilerimize ve inananlara bu dünya hayatında ve tanıkların duruşmalarda bulunacakları günde yardım ederiz.'),
(4185, 40, 52, 'O gün zalimlere özürleri bir yarar sağlamaz. Onlar laneti ve en kötü sonu hakkederler.'),
(4186, 40, 53, 'Biz Musa\'ya hidayeti verdik ve İsrailoğullarını kitaba varis kıldık.'),
(4187, 40, 54, 'Anlayış sahiplerine bir yol gösterici ve bir uyarı olarak.'),
(4188, 40, 55, 'Öyleyse sabret. Çünkü ALLAH\'ın sözü gerçektir. Günahların için de bağışlanma iste ve Rabbini akşam sabah överek yücelt.'),
(4189, 40, 56, 'Hiç bir delile sahip olmadan ALLAH\'ın ayet ve mucizelerine karşı tartışanların göğüslerinde, erişemiyecekleri bir büyüklenme vardır. Öyleyse ALLAH\'a sığın. O İşitendir, Görendir.'),
(4190, 40, 57, 'Göklerin ve yerin yaratılışı, insanın yaratılışından daha büyük bir şeydir. Ne var ki halkın çoğu bilmez.'),
(4191, 40, 58, 'Kör ile gören bir olmaz. İnanıp erdemli davrananlarla kötülük işleyenler de bir olmaz. Ne kadar az düşünürsünüz.'),
(4192, 40, 59, 'Dünyanın sonu (Saat) elbette gelecektir, bunda kuşku olmasın. Ne var ki insanların çoğu inanmaz.'),
(4193, 40, 60, 'Rabbiniz der ki, "Bana yalvarın size cevap vereyim." Büyüklük taslayarak bana kulluk etmekten kaçınanlar aşağılanarak cehenneme gireceklerdir.'),
(4194, 40, 61, 'ALLAH, geceyi dinlenmeniz için yaratan ve gündüzü de aydınlık yapandır. ALLAH halka karşı büyük lütuf sahibidir, fakat halkın çoğu şükretmez.'),
(4195, 40, 62, 'İşte Rabbiniz ALLAH, her şeyin Yaratıcısıdır; O\'ndan başka tanrı yoktur. Nasıl da çevriliyorsunuz?'),
(4196, 40, 63, 'ALLAH\'ın ayet ve mucizelerini red edenler işte böyle saptırılırlar.'),
(4197, 40, 64, 'ALLAH yeryüzünü sizin için bir yerleşim ve göğü de bir yapı kılandır. Sizi biçimlendirip biçiminizi güzel yapan ve sizi besinli gıdalarla besleyendir. Rabbiniz olan ALLAH budur. Evrenin Rabbi olan ALLAH ne yücedir!'),
(4198, 40, 65, 'O, Diridir; O\'ndan başka tanrı yoktur. Dini sadece O\'na ait kılarak O\'nu çağırın. Evrenin Rabbi ALLAH\'a övgüler olsun.'),
(4199, 40, 66, 'De ki, "Bana açık deliller geldikten sonra, ALLAH\'ın yanında yalvardıklarınıza tapmaktan menedildim ve evrenin Rabbine teslim olmakla emrolundum."'),
(4200, 40, 67, 'O ki sizi topraktan, sonra bir damladan, sonra asılı duran bir embriyodan yaratmıştır. Olgunlaşmanız ve ihtiyarlamanız için daha sonra sizi çocuk olarak dışarı çıkarır. Sizden bazılarının canı daha erken alınır. Daha önce belirlenmiş yaşa ulaşırsınızki kavrayasınız.'),
(4201, 40, 68, 'Dirilten ve öldüren O\'dur. Herhangi bir işi diledi mi, ona "Ol" der, o da oluverir.'),
(4202, 40, 69, 'ALLAH\'ın ayet ve mucizelerine karşı mücadele edenlerin nasıl da çevrildiklerini görmez misin?'),
(4203, 40, 70, 'Onlar, kitabı ve elçilerimiz yoluyla gönderdiğimiz mesajı yalanladılar. İleride bilecekler.'),
(4204, 40, 71, 'Boyunlarında prangalar ve zincirlerle sürüklenecekler.'),
(4205, 40, 72, 'Kaynar suda, sonra ateşte yakılacaklardır.'),
(4206, 40, 73, 'Sonra onlara şöyle denir, "Nerde ortak koştuklarınız,"'),
(4207, 40, 74, '"ALLAH\'ın yanında?" Onlar da derler ki, "Bizi terkettiler. Meğer biz daha önce hiç bir şeye yalvarmıyormuşuz." ALLAH inkarcıları işte böyle saptırır.'),
(4208, 40, 75, 'Çünkü siz yeryüzünde gerçeğe dayanmadan seviniyor ve şımarıyordunuz.'),
(4209, 40, 76, 'Sürekli kalmak üzere cehennemin kapılarından giriniz. Büyüklük taslayanların yeri ne de kötüdür.'),
(4210, 40, 77, 'Öyleyse sabret; ALLAH\'ın sözü gerçektir. Onlara söz verdiğimiz (cezalandırmanın) bir kısmını sana göstersek de, ondan önce hayatına son versek de, onlar bize döndürüleceklerdir.'),
(4211, 40, 78, 'Senden önce göndermiş bulunduğumuz elçilerin bir kısmından sana söz ettik, bir kısmını da sana anlatmadık. Hiç bir elçi, ALLAH\'ın izni olmadan bir mucize getiremez. ALLAH\'ın emri gelince gerçeğe göre yargı verilir ve yanlışı savunanlar orada hüsrana uğrarlar.'),
(4212, 40, 79, 'Binesiniz ve onlardan yiyesiniz diye çiftlik hayvanlarını sizin için yaratan ALLAH\'tır.'),
(4213, 40, 80, 'Sizin için onlarda çeşitli yararlar vardır. Gönlünüz dilediği gibi onlardan yararlanırsınız. Onlarla ve gemilerle taşınırsınız.'),
(4214, 40, 81, 'Size işaretlerini gösterir. ALLAH\'ın hangi işaretini inkar ediyorsunuz?'),
(4215, 40, 82, 'Kendilerinden öncekilerin sonunun nasıl olduğuna bakmak için yeryüzünü dolaşmazlar mı? Onlardan sayıca daha çok, daha güçlü olup yeryüzünde daha çok üretimde bulunmuşlardı. Ancak, kazandıkları şeyler kendilerini kurtaramadı.'),
(4216, 40, 83, 'Elçileri apaçık kanıtlarla kendilerine vardıklarında, yanlarındaki bilgiyle gururlandılar. Alaya aldıkları şey kendilerini sarıverdi.'),
(4217, 40, 84, 'Nihayet cezamızı gördüklerinde, "Artık SADECE ALLAH\'a inandık ve ortak koşmuş olduklarımızı inkar ettik," derler-'),
(4218, 40, 85, 'Azabımızı gördüklerinde inanmaları kendilerine bir yarar sağlamaz. Bu, daha önceki kulları hakkında sürekli uygulanan ALLAH\'ın sünneti (yasası) dır. İşte o zaman inkarcılar hüsrana uğramışlardır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'HH. M.'),
(4220, 41, 2, 'Rahman ve Rahim\'den bir indirilmiştir.'),
(4221, 41, 3, 'Bilen bir topluluk için, Arapça bir Kuran olarak ayetleri açıklanmış bir kitaptır.'),
(4222, 41, 4, 'Bir müjdeci ve bir uyarıcıdır. Ancak onların çoğunluğu ondan yüz çevirmiştir; onlar işitmezler.'),
(4223, 41, 5, 'Dediler ki: "Senin bizi çağırdığın şeye karşı sabit fikirliyiz, kulaklarımızda ağırlık ve bizimle sizin aranızda bir perde vardır. İstediğini yap, biz de yapacağız.'),
(4224, 41, 6, 'De ki, "Ben, sadece sizin gibi bir insanım. Bana, sizin tanrınızın bir tek tanrı olduğu vahyediliyor. O\'na yönelin, O\'ndan bağışlanma dileyin. Vay ortak koşanlara!"'),
(4225, 41, 7, '"Ki zekatı vermezler ve onlar ahireti de inkar ederler."'),
(4226, 41, 8, 'İnanıp erdemli davrananlar için sürekli bir ödül vardır.'),
(4227, 41, 9, 'De ki, "Siz yeri iki günde yaratanı mı inkar ediyorsunuz ve O\'na eşler mi koşuyorsunuz? O, evrenlerin Rabbidir."'),
(4228, 41, 10, 'Onun üstüne denge unsurları (olan dağlar) yerleştirdi ve onu bereketli kıldı. Tüm arayıp isteyenler için onun gıdalarını dört günde ölçüp düzenledi.'),
(4229, 41, 11, 'Sonra duman halindeki göğe yönelerek ona ve yere, "İsteyerek veya istemeyerek (kaostan çıkıp) gelin," dedi. Onlar da, "İsteyerek geldik," dediler.'),
(4230, 41, 12, 'Böylece onları iki günde yedi gök olarak tamamladı ve her göğe özel yasaları bildirdi. Ve biz en aşağıdaki göğü ise lambalarla ve koruma sistemiyle donattık. Bu, Üstün ve herşeyi Bilen\'in planıdır.'),
(4231, 41, 13, 'Yüz çevirirlerse de ki, "Sizi Ad ve Semud yıldırımına benzer bir yıldırımla uyardım."'),
(4232, 41, 14, 'Onlara, "ALLAH\'tan başkasına tapmayın," diye önlerinden ve arkalarından elçiler gitmişti. Onlar ise, "Rabbimiz dileseydi bir melek indirebilirdi. Bundan ötürü sizin getirdiğiniz mesaja inanmıyoruz," dediler.'),
(4233, 41, 15, 'Ad\'a gelince, onlar yeryüzünde haksız yere büyüklük tasladılar ve "Bizden daha güçlü kim var?" dediler. Kendilerini yaratan ALLAH\'ın onlardan daha güçlü olduğunu anlamadılar mı? Onlar ayet ve mucizelerimizi bilerek inkar ediyorlardı.'),
(4234, 41, 16, 'Nihayet, onlara dünya hayatında aşağılayıcı azabı tattırmak için uğursuz günlerde üzerlerine sert bir rüzgar gönderdik. Ahiret azabı ise daha aşağılayıcıdır ve onlar yardım görmezler.'),
(4235, 41, 17, 'Semud\'a gelince, onlara yolu gösterdik. Ne var ki onlar körlüğü hidayete tercih ettiler. Sonunda, kazandıklarına karşılık, onları alçaltıcı azabın yıldırımı yakaladı.'),
(4236, 41, 18, 'İnananları ve erdemli davrananları ise kurtardık.'),
(4237, 41, 19, 'ALLAH düşmanlarının ateşe sürüklenerek toplatılacakları gün...'),
(4238, 41, 20, 'Nihayet oraya vardıklarında, onların işitme, görme organları ve derileri yaptıkları hakkında tanıklık eder.'),
(4239, 41, 21, 'Derilerine, "Niçin bize karşı tanıklık ettiniz?" diye sorarlar. Onlar da, "Her şeyi konuşturan ALLAH bizi konuşturdu. Sizi ilk yaratan O\'dur ve işte O\'na döndürülüyorsunuz," derler'),
(4240, 41, 22, 'Ne işitme ve görme organlarınızın, ne de derilerinizin aleyhinizdeki tanıklığını gizlemeye gücünüz yetmez. Buna rağmen siz yaptıklarınızın çoğunu ALLAH\'ın bilemiyeceğini sanıyordunuz.'),
(4241, 41, 23, 'Rabbiniz hakkında bu düşünceniz sizi kaydırdı ve kaybedenlerden oldunuz.'),
(4242, 41, 24, 'Dayanırlarsa yerleri ateştir. Yok, hoş görülmek isterlerse özürleri kabul edilmeyecektir.'),
(4243, 41, 25, 'Geçmişlerini ve geleceklerini onlara güzel gösteren birtakım arkadaşları onlara atadık. Kendilerinden önce gelip geçmiş cin ve insan toplumları için gerçekleşen o söz kendileri için de geçerli oldu. Onlar kaybetmişlerdi.'),
(4244, 41, 26, 'İnkar edenler, "Başa çıkmanız için, bu Kuran\'ı dinlemeyin, anlaşılmasını engelleyin," dediler.'),
(4245, 41, 27, 'İnkar edenlere şiddetli bir azabı tattıracağız ve yaptıklarının en kötüsüyle onlara karşılık vereceğiz.'),
(4246, 41, 28, 'ALLAH düşmanlarının cezası işte bu ateştir. Ayetlerimizi bile bile inkar etmelerinin bir karşılığı olarak orası onların ebedi yurdu olacaktır.'),
(4247, 41, 29, 'İnkar edenler, "Rabbimiz, cinlerden ve insanlardan bizi saptıranları bize göster, onları ayaklarımızın altına alalım da en alçak olanlardan olsunlar," derler.'),
(4248, 41, 30, '"Rabbimiz ALLAH\'tır," dedikten sonra doğru yolu izleyenlerin üzerine melekler iner:"Korkmayın, üzülmeyin ve davetli olduğunuz cennetle sevinin."'),
(4249, 41, 31, '"Dünya hayatında da, ahirette de sizin dostlarınız biziz. Orada canınızın çektiği ve istediğiniz her şey vardır."'),
(4250, 41, 32, '"Bağışlayan ve Rahim olandan bir ağırlama olarak."'),
(4251, 41, 33, 'ALLAH\'a çağıran, erdemli davranan ve "Ben teslim olanlardanım," diyenden daha güzel sözlü kim olabilir?'),
(4252, 41, 34, 'İyilik ile kötülük bir olmaz. Sen en iyi bir biçimde karşılık ver. O zaman, aranızda düşmanlık bulunan kişi belki en iyi dostun oluverir.'),
(4253, 41, 35, 'Bu duruma ancak dirençli olanlar eriştirilir. Bu duruma ancak alabildiğine şanslı olanlar ulaştırılır.'),
(4254, 41, 36, 'Şeytandan herhangi bir düşünce seni etkisi altına alırsa ALLAH\'a sığın. O İşitendir, Bilendir'),
(4255, 41, 37, 'Gece, gündüz, güneş ve ay O\'nun ayetlerindendir. Güneşe ve ay\'a secde etmeyin, onları yaratan ALLAH\'a secde edin. O\'na kulluk edecekseniz.'),
(4256, 41, 38, 'Büyüklenirlerse, Rabbinin yanındakiler hiç yorulmadan gece gündüz O\'nu yüceltirler.'),
(4257, 41, 39, 'Kupkuru gördüğün toprağın üzerine su yağdırdığımızda onun titreşip kabarması da O\'nun ayetlerindendir. Onu dirilten ölüleri de diriltir. O, herşeye gücü yetendir.'),
(4258, 41, 40, 'Ayetlerimiz konusunda çarpıtmalar yapanlar bizden gizli kalmaz. Ateşe atılan mı, yoksa diriliş gününde güven içinde bize gelen mi daha iyidir? Dilediğiniz gibi davranın. O, yaptıklarınızı elbette Görendir.'),
(4259, 41, 41, 'Kendilerine mesaj ulaştıktan sonra onu inkar edenler bilsin ki o üstün bir kitaptır.'),
(4260, 41, 42, 'Batıl, onun ne önündenne de ardından yaklaşamaz. Bilge ve övgüye layık olanın indirdiği bir vahiydir.'),
(4261, 41, 43, 'Senin için söylenilenler, senden önceki elçiler için söylenilenlerin aynısıdır. Kuşkusuz senin Rabbin hem bağışlanma sahibi, hem de acı azap sahibidir.'),
(4262, 41, 44, 'Onu yabancı dilde bir Kuran kılsaydık, "Onun ayetleri açıklanmalı değil miydi?" diyeceklerdi. İster yabancı dil, ister Arapça olsun, de ki, "O, inananlar için (dilleri sözkonusu olmaksızın) bir rehber ve şifadır. İnanmıyanların ise kulaklarında ağırlık vardır. Onlara sanki uzak bir yerden sesleniliyor gibi onlara kapalıdır."'),
(4263, 41, 45, 'Biz Musa\'ya kitabı (İbranice) verdik, onda da anlaşmazlığa düştüler. Rabbinin vermiş olduğu bir söz olmasaydıaralarında yargı verilirdi. Gerçekten onlar ondan kuşku ve şüphe içindedirler.'),
(4264, 41, 46, 'Kim erdemli davranırsa kendi yararınadır. Kim kötü davranırsa kendi zararınadır. Rabbin, kullara zulmetmez.'),
(4265, 41, 47, 'Saatın (dünyanın sonunun) bilgisi O\'nun katındadır. O\'nun bilgisi olmadan ne meyveler kabuklarından çıkabilir, ne de bir dişi gebe kalır veya doğum yapar. "Hani benim ortaklarım nerede?" diye onlara seslendiği gün, onlar: "Hiçbirimizin buna şahitlik etmediğini sana arzederiz," derler.'),
(4266, 41, 48, 'Daha önce yardım için çağırdıkları (dine ortak koştukları kişiler) onlardan uzaklaşmıştır. Artık kaçacak bir yerleri olmadığını anlarlar.'),
(4267, 41, 49, 'İnsan, iyilik istemekten bıkmaz. Ancak kendisine bir kötülük dokunduğunda umutsuzdur, kötümserdir.'),
(4268, 41, 50, 'Sıkıntısının ardından, kendisine bizden bir rahmet tattırsak, "Bu bana aittir. Dünyanın sonunun da geleceğini sanmam. Rabbime döndürülürsem de O\'nun katında benim için daha güzel şeyler vardır," der. İnkarcılara, yapmış olduklarının gerçek niteliğini elbette haber vereceğiz. Çetin bir azaptan tattıracağız onlara.'),
(4269, 41, 51, 'İnsana bir nimet verdik mi, yüz çevirip yan çizer. Başına bir kötülük geldiğinde ise yalvarıp durur.'),
(4270, 41, 52, 'De ki, "Ya o (mesaj) ALLAH\'tan idiyse ve siz de onu yalanlamış iseniz? Buna karşı kesin bir tavır alandan daha sapık kim olabilir?'),
(4271, 41, 53, 'Onun gerçek olduğu onlara apaçık oluncaya kadar onlara, ufuklarda ve kendi içlerinde ayetlerimizi (işaret ve kanıtlarımızı)göstereceğiz. Rabbinin her şeye tanık olması yetmez mi?'),
(4272, 41, 54, 'Aslında onlar Rab\'lerinden bir kuşku içindedirler. O, her şeyi (bilgi ve yönetimiyle) kuşatmıştır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'HH. M.'),
(4274, 42, 2, '\'A. S. Q.'),
(4275, 42, 3, 'Üstün ve Bilge olan ALLAH sana ve senden öncekilere böyle vahyeder.'),
(4276, 42, 4, 'Göklerde, yerde ne varsa O\'nundur. O Yücedir, Büyüktür.'),
(4277, 42, 5, 'Gökler (O\'na olan saygıdan ötürü) nerdeyse üstlerinden çatlayacak. Melekler Rab\'lerini överek yüceltirler ve yeryüzündekiler için bağışlanma dilerler. ALLAH kesinlikle Bağışlayandır, Rahimdir.'),
(4278, 42, 6, 'O\'ndan başkasını veliler edinenleri ALLAH kollamaktadır. Sen onların avukatı değilsin.'),
(4279, 42, 7, 'Başkent ve çevresini uyarman için ve gerçekleşecek olan Toplanma Gününe karşı uyarman için biz kusursuz bir dile sahip olan bir Kuran\'ı böylece sana vahyediyoruz. Onlardan bir kısmı cennete, bir kısmı da cehenneme girecektir.'),
(4280, 42, 8, 'ALLAH dileseydi onları bir tek toplum kılardı. Ancak O, dilediğini rahmetine sokar. Zalimlerin bir sahibi ve yardımcısı yoktur.'),
(4281, 42, 9, 'O\'nun dışında veliler (sahipler) mi edindiler? Biricik veli ALLAH\'tır, ölüleri O diriltir ve O herşeye Kadirdir.'),
(4282, 42, 10, 'Bu mesajın her hangi bir bölümünde anlaşmazlığa düşerseniz hüküm ALLAH\'a aittir. Rabbim ALLAH işte böyledir. Ben O\'na güvendim ve O\'na yönelirim.'),
(4283, 42, 11, 'O gökleri ve yeri yararak yaratandır. İçinizden sizin için eşler yarattı ve çiftlik hayvanlarını da çiftler halinde yarattı. Böylece sizi orada üretiyor. O\'na benzer hiç bir şey yoktur. O, İşitendir, Görendir.'),
(4284, 42, 12, 'Göklerin ve yerin tüm kontrolu O\'na aittir. Dilediğine rızkı bol verir veya kısar. O her şeyi Bilendir.'),
(4285, 42, 13, 'Daha önce Nuh\'a buyurduğu dini size yasa olarak belirledik. Sana vahyettiğimiz gibi İbrahim\'e, Musa\'ya ve İsa\'ya da öğütledik: "Bu dini doğru tutun ve onda ayrılığa düşmeyin." Fakat kendilerini çağırdığın şey, ortak koşanlara ağır gelmektedir. ALLAH dileyeni kendine seçer ve kendisine yöneleni doğruya ulaştırır.'),
(4286, 42, 14, 'Kendilerine bilgi ulaştıktan sonra sırf aralarındaki çekememezlik yüzünden ayrılığa düştüler. Belli bir zaman için Rabbinin verilmiş bir söz olmasaydı onların arasında hemen hüküm verilirdi. Onlardan sonra kitaba mirasçı olanlar da ondan kuşku ve şüphe içindedirler.'),
(4287, 42, 15, 'Sen buna çağır ve sana emredildiğin gibi dosdoğru uygula. Onların arzularına uyma ve de ki "ALLAH\'ın bana indirdiği kitaba inandım. Sizin aranızda adaletle davranmakla emrolundum. ALLAH bizim de Rabbimiz, sizin de Rabbinizdir. Bizim işlediklerimiz bize, sizin işledikleriniz ise sizedir. Bizimle sizin aranızda herhangi bir tartışma yoktur. Bizi ALLAH biraraya getirecektir. Sonunda dönüş O\'nadır.'),
(4288, 42, 16, 'ALLAH hakkında, O\'nun çağrısına yanıt verildikten sonra tartışanların delilleri Rab\'leri katında geçersizdir. Gazabı haketmişlerdir ve onlara çetin bir azap vardır'),
(4289, 42, 17, 'O ALLAH ki kitabı gerçek ve adaletle indirmiştir. Ne bilirsin, belki Saat (dünyanın sonu) yakındır.'),
(4290, 42, 18, 'Ona inanmıyanlar onun hakkında meydan okuyorlar. Ona inananlar ise ondan kaygı duyarlar ve onun gerçek olduğunu bilirler. Saat konusunda kuşku duyanlar büyük bir sapıklık içindedirler.'),
(4291, 42, 19, 'ALLAH kullarına lütfedendir. Dilediğini ve/veya dileyeni rızıklandırır. O Güçlüdür, Üstündür.'),
(4292, 42, 20, 'Kim ahiret ödülünü isterse onun ödülünü arttırırız. Dünya ödülünü isteyene de onu veririz; ancak onun ahiretten bir payı olmaz.'),
(4293, 42, 21, 'Yoksa ALLAH\'ın izni olmadığı halde onlar için dini kurallar ve yasalar ortaya koyan ortakları mı var? Daha önce belirlenmiş bir karar olmasaydı onların arasında yargı verilirdi. Zalimlere acı bir azap vardır.'),
(4294, 42, 22, 'Yaptıkları işler başlarına gelirken zalimleri kaygı içinde görürsün. İnanıp erdemli davrananlar cennetlerin bahçelerindedir. Rableri katında diledikleri her şeyi alırlar. İşte bu, büyük lütuftur.'),
(4295, 42, 23, 'ALLAH, inanıp erdemli davranan kullarını böyle müjdeler. De ki "Ben sizden, akrabalık sevgisi dışında herhangi bir ücret istemiyorum." Kim bir iyilik işlerse onun iyilğini arttırırız. ALLAH Bağışlayandır, takdir edendir.'),
(4296, 42, 24, 'Onlar, "O ALLAH hakkında yalan mı uydurdu." mu diyorlar? ALLAH dilese senin kalbini mühürler. ALLAH yanlışı siler ve sözleriyle gerçeği yerleştirir. O en gizli düşünceleri bilir.'),
(4297, 42, 25, 'O, kullarından tevbeleri kabul eder, günahları affeder ve yaptıklarınızı bilir.'),
(4298, 42, 26, 'İnanıp erdemli davrananların çağrısına cevap verir ve onlara lütfunu arttırır. İnkarcılar ise çetin bir cezayı hakketmiştir.'),
(4299, 42, 27, 'ALLAH kullarına rızkı bollaştırsaydı, yeryüzünde azacaklardı. Nitekim dilediği ölçüde gönderir. O, kullarından Haberdardır, Görendir.'),
(4300, 42, 28, 'Umutlarını kesmelerinden sonra yağmuru indiren ve rahmetini yayandır. O, Övgüye Layık bir Dosttur.'),
(4301, 42, 29, 'Gökleri ve yeri yaratıp onlarda çeşitli yaratıklar yayması O\'nun ayetlerindendir. O, dilediği zaman onları toplayabilir.'),
(4302, 42, 30, 'Size dokunan bir kötülük, işlediklerinizin bir sonucudur. O, bir çoğunu da affeder.'),
(4303, 42, 31, 'Siz yeryüzünde kaçamazsınız. Sizin ALLAH \'tan başka bir sahibiniz ve yardımcınız yoktur.'),
(4304, 42, 32, 'Okyanusta dağlar gibi akıp giden gemiler de O\'nun ayetlerindendir.'),
(4305, 42, 33, 'Dilerse rüzgarı durdurur ve onlar suyun üzerinde hareketsiz kalırdı. Bunda, her sabreden ve şükreden kişi için ibretler vardır.'),
(4306, 42, 34, 'Yahut, yaptıkları yüzünden onları yok eder. Bunun yerine bir çoğunu da bağışlar.'),
(4307, 42, 35, 'Ayetlerimiz ve mucizelerimiz üzerinde tartışanlar kendilerinin kaçacak bir yeri olmadığını bilirler.'),
(4308, 42, 36, 'Size ne verildiyse dünya hayatının geçimliğidir. Ancak inananlar ve Rab\'lerine güvenenler için ALLAH\'ın yanında bulunanlar daha iyidir ve süreklidir.'),
(4309, 42, 37, 'Onlar ki büyük günahlardan çirkin işlerden kaçınırlar ve kızdıkları zaman bağışlarlar.'),
(4310, 42, 38, 'Onlar, Rab\'lerinin çağrısına karşılık verirler, namazı gözetirler, işlerini aralarında danışma ile kararlaştırırlar ve kendilerine verdiğimiz rızıklardan yardım için verirler.'),
(4311, 42, 39, 'Haksızlığa uğradıklarında kendilerini savunurlar.'),
(4312, 42, 40, 'Kötülüğün cezası, benzeri bir kötülüktür; ancak kim affeder ve erdemli davranırsa ALLAH tarafından ödüllendirilir. O, zalimleri sevmez.'),
(4313, 42, 41, 'Haksızlığa uğradıktan sonra hakları için direnenler kınanmazlar, cezalandırılmazlar.'),
(4314, 42, 42, 'Ancak, halka zulmedenlere ve yeryüzünde haksız yere saldıranlara karşı durulmalıdır. Onlara acı bir azap vardır.'),
(4315, 42, 43, 'Saberedip bağışlamak, sağlam bir karakteri gösterir.'),
(4316, 42, 44, 'ALLAH kimi saptırmışsa, artık O\'ndan sonra onun bir koruyucusu yoktur. Azabı gördüklerinde, zalimlerin, "Bizim için bir şans daha yok mu?" dediklerini görürsün.'),
(4317, 42, 45, 'Aşağılanmış ve başları eğilmiş olarak ateşe sunulurlarken göz ucuyla çevrelerine bakındıklarını görürsün. İnananlar, "Gerçek kaybedenler, Diriliş Gününde kendilerini ve ailelerini kaybedenlerdir. Zalimler sürekli bir azaba mahkum olmuştur," derler.'),
(4318, 42, 46, 'Onların, ALLAH\'tan başka kendilerine yardım edecek bir dostları yoktur. ALLAH\'ın saptırdığı kimse için bir yol bulunmaz.'),
(4319, 42, 47, 'ALLAH katından, geri çevrilmesi olanaksız olan gün gelmezden önce Rabbinize cevap veriniz. O gün sizin için ne bir sığınak ne de bir koruyucu vardır.'),
(4320, 42, 48, 'Yüz çevririrlerse, biz seni onlara bir bekçi olarak göndermedik. Sana düşen sadece bildirmektir. Biz insana tarafımızdan bir rahmet taddırdığımız zaman onunla sevinir; ama kendi yaptıklarının bir sonucu olarak başlarına bir kötülük gelse, o zaman insan nankörleşir.'),
(4321, 42, 49, 'Göklerin ve yerin yönetimi ALLAH\'ındır. Dilediğini yaratır. Dilediğine dişiler, dilediğine de erkekler verir.'),
(4322, 42, 50, 'Yahut hem erkek hem dişi olarak çift verir. Dilediğini de kısır yapar. O Bilendir, her şeye gücü yetendir.'),
(4323, 42, 51, 'ALLAH bir insanla ancak vahiy yoluyla veya bir perde arkasından iletişim kurar, yahut bir elçi gönderip izniyle dilediğini vahyeder. O, Yücedir, Bilgedir.'),
(4324, 42, 52, 'Biz böylece sana katımızdan bir ruh vahyettik. Sen kitap nedir iman nedir bilmezdin. Ancak onu, dilediğimiz kulları doğruya ulaştıran bir ışık kıldık. Sen elbette doğru yola kılavuzluk ediyorsun.'),
(4325, 42, 53, 'Göklerde ve yerde bulunan herşeyin sahibi ALLAH\'ın yoluna... Kesinlikle, tüm işler ALLAH\'a döner.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'HH. M.'),
(4327, 43, 2, 'Apaçık kitaba andolsun ki,'),
(4328, 43, 3, 'Anlamanız için onu kusursuz bir dile sahip bir Kuran yaptık.'),
(4329, 43, 4, 'O, ana kitapta (korunur), katımızda üstündür, bilgedir.'),
(4330, 43, 5, 'Haddi aşan bir toplumsunuz diye mesajı size iletmekten vaz mı geçelim?'),
(4331, 43, 6, 'Öncekilere nice peygamberler göndermişizdir.'),
(4332, 43, 7, 'Fakat, kendilerine giden her peygamberle alay ettiler.'),
(4333, 43, 8, 'Şunlardan daha güçlülerini yok ettik. Öncekilerin örneği geçmiştir.'),
(4334, 43, 9, 'Onlara, "Gökleri ve yeri kim yarattı?" diye sorsan, "Onları, Üstün ve herşeyi bilen yarattı," diyeceklerdir.'),
(4335, 43, 10, 'O ki, yeryüzünü oturmanıza elverişli kıldı ve doğru gitmeniz için sizin için orada yollar açtır.'),
(4336, 43, 11, 'O ki gökten bir ölçüye göre su indirdi de onunla ölü bir ülkeyi dirilttik. İşte böyle çıkarılırsınız.'),
(4337, 43, 12, 'O ki bütün çiftleri yarattı ve binesiniz diye sizin için gemiler ve çiftlik hayvanları yarattı.'),
(4338, 43, 13, 'Ki onların üstüne binesiniz ve onlara bindiğiniz zaman Rabbinizin size olan nimetini düşünerek şunları diyesiniz: "Bunu bizim emrimize veren çok yücedir. Onları kendi başımıza kontrol altına alamazdık."'),
(4339, 43, 14, '"Ve biz, sonunda Rabbimize döneceğiz."'),
(4340, 43, 15, 'Kullarından bazılarını O\'na bir pay olarak ayırdılar. İnsan gerçekten apaçık bir nankördür.'),
(4341, 43, 16, 'Yaratıklarından kızları kendisine ayırdı da oğulları size mi seçti?'),
(4342, 43, 17, 'Onlardan birisi, Rahman\'a yakıştırdığı (kız çocuğu) ile müjdelenince, morali bozularak yüzü simsiyah kesilir:'),
(4343, 43, 18, '"Süsler içinde yetiştirilmesine rağmen kavgada beceriksiz olan mı!?"'),
(4344, 43, 19, 'Rahman\'ın kulları olan melekleri dişi saydılar! Onların yaratılışlarına mı tanık oldular? Bu tanıklıkları kaydedilercek ve sorguya çekileceklerdir.'),
(4345, 43, 20, 'Hatta, "Rahman dileseydi biz onlara tapmazdık," dediler. Onların bu konuda bir bilgileri yoktur. Onlar sadece tahminde bulunuyorlar.'),
(4346, 43, 21, 'Onlara bundan önce bir kitap mı verdik de ona mı dayanıyorlar?'),
(4347, 43, 22, 'Hayır, "Biz atalarımızı bir yol üzerinde bulduk ve biz onların öğretilerini izliyoruz," dediler.'),
(4348, 43, 23, 'Tıpkı bunun gibi, senden önce, bir kente her ne zaman bir uyarıcı gönderdiysek elit tabaka, "Biz, atalarımızı bir yol üzerinde bulduk ve biz onların öğretilerini izliyoruz," derlerdi.'),
(4349, 43, 24, 'O da, "Size, atalarınızı üzerinde bulduğunuzdan daha doğrusunu getirmiş isem de mi?" derdi. Onlar da, "Sizin getirdiğiniz mesajı inkar ediyoruz," derlerdi.'),
(4350, 43, 25, 'Biz de onlardan öc aldık. Yalanlayanların sonu nasıl olduğuna bir bak.'),
(4351, 43, 26, 'İbrahim, babasına ve halkına demişti ki, "Sizin taptıklarınızdan uzağım."'),
(4352, 43, 27, '"Beni Yaratan bana doğru yolu gösterecektir."'),
(4353, 43, 28, 'Belki doğru yola dönerler diye onun ardından gelecekler için bunu ebedi bir ders kıldı.'),
(4354, 43, 29, 'Doğrusu, kendilerine gerçek ve apaçık bir elçi varıncaya kadar şunlara ve atalarına imkan tanıdım.'),
(4355, 43, 30, 'Kendilerine gerçek geldiği zaman, "Bu bir büyüdür ve biz onu inkar ediyoruz," dediler.'),
(4356, 43, 31, '"Bu Kuran, şu iki kentten ünlü ve büyük bir adama indirilmeli değil miydi?" dediler.'),
(4357, 43, 32, 'Rabbinin rahmetini onlar mı paylaştırıyorlar? Dünya hayatında onların geçimliklerini aralarında biz paylaştırdık; birbirlerine iş gördürebilmeleri için onları birbirlerine derecelerle üstün kıldık. Rabbinin rahmeti onların biriktirdiklerinden daha iyidir.'),
(4358, 43, 33, 'Tüm insanlar (inkarcılıkta) bir tek toplum olacak olmasaydı, Rahman\'ı inkar edenlerin evini gümüş tavanlar ve binip çıkacakları merdivenlerle donatırdık.'),
(4359, 43, 34, 'Evlerine kapılar ve konforlu mobilyalar.'),
(4360, 43, 35, 'Ve nice gösterişli maddeler. Tüm bunlar bu dünya hayatının geçici materyalidir. Ahiret ise Rabbinin katında erdemlilerindir.'),
(4361, 43, 36, 'Kim Rahman\'ın mesajına aldırış etmezse, ona bir şeytanı sardırırız da onun arkadaşı olur.'),
(4362, 43, 37, 'Nitekim onları yoldan çıkarırlar. Buna rağmen onlar doğru yolda olduklarını sanırlar'),
(4363, 43, 38, 'Nihayet bize geldiğinde, "Keşke benimle senin aranda iki doğu arası kadar uzaklık olsaydı; sen ne kötü bir arkadaş mışsın sen!" der.'),
(4364, 43, 39, 'Haksızlık etmiş olduğunuz için, o gün (pişmanlığınız) size bir yarar sağlamaz; siz cezayı paylaşacaksınız.'),
(4365, 43, 40, 'Sen mi sağıra işittireceksin, yahut körü ve apaçık bir sapıklıkta olanı yola getireceksin?'),
(4366, 43, 41, 'Seni alıp götürsek de biz onları cezalandıracağız.'),
(4367, 43, 42, 'Yahut, onlara söz verdiğimizi sana gösteririz; bizim onlara gücümüz yeter.'),
(4368, 43, 43, 'Sana vahyedilene sarıl; çünkü sen doğru yoldasın.'),
(4369, 43, 44, 'Bu, sana ve halkına bir mesajdır; ondan sorulacaksınız.'),
(4370, 43, 45, 'Senden önce gönderdiğimiz elçileri araştır: "Rahman\'ın dışında tapılacak tanrılar kabul etmiş miyiz?"'),
(4371, 43, 46, 'Örneğin; Musa\'yı ayetlerimizle Firavun\'a ve erkanına gönderdik ve "Ben evrenlerin Rabbinin elçisiyim," demişti.'),
(4372, 43, 47, 'Mucizelerimizi kendilerine götürdüğü zaman, o mucizelere gülmüşlerdi.'),
(4373, 43, 48, 'Onlara bir birinden büyük mucizeler gösterdik ve belki dönerler diye başlarına çeşitli felaketler getirdik.'),
(4374, 43, 49, '"Ey büyücü, bizim için Rabbine dua et. Çünkü sen ona daha yakınsın; biz bundan sonra yola geleceğiz," dediler.'),
(4375, 43, 50, 'Fakat, onlardan felaketi kaldırdığımızda, sözlerinden hemen dönüverdiler.'),
(4376, 43, 51, 'Firavun halkına şöyle seslendi: "Ey halkım, Mısır\'ın yönetimi ve şu altımda akıp giden ırmaklar bana ait değil mi? Görmüyor musunuz?"'),
(4377, 43, 52, '"Yahut ben, şu aşağılık ve konuşmaktan aciz olan adamdan daha üstün değil miyim?"'),
(4378, 43, 53, '"Neden ona altınlardan oluşan bir hazine verilmiyor, yahut neden yanında çalışacak melekler gelmiyor?"'),
(4379, 43, 54, 'Böylece halkını yanılttı ve onlar da ona uydular. Onlar bayağı insanlardı.'),
(4380, 43, 55, 'Bizimle savaşmakta israr edince onlardan öc aldık, hepsini boğduk.'),
(4381, 43, 56, 'Onları, sonradan gelecekler için bir ibret ve örnek yaptık.'),
(4382, 43, 57, 'Meryemoğlu bir örnek olarak gösterilince senin halkın hemen reddettiler.'),
(4383, 43, 58, '"Bizim tanrılarımız mı daha iyidir yoksa o mu?" dediler. Sadece seninle tartışmak için bunu söylediler. Onlar gerçekte, kavgacı bir toplumdur.'),
(4384, 43, 59, 'O, kendisine iyilikte bulunduğumuz bir kuldan başka bir şey değildi. Onu İsrailoğullarına bir örnek kıldık.'),
(4385, 43, 60, 'Dileseydik sizi, yeryüzünü koloni haline getiren meleklere çevirirdik.'),
(4386, 43, 61, 'O (İsa), Saat hakkında bir bilgi kaynağı ve işarettir. Öyleyse onun (zamanı) hakkında kuşku beslemeyin ve beni izleyin. Doğru yol budur.'),
(4387, 43, 62, 'Şeytan sizi yoldan çıkarmasın. O size açık bir düşmandır.'),
(4388, 43, 63, 'İsa apaçık mucizelerle gelince demişti ki, "Size bilgelik, ve ayrılığa düştüğünüz konulara açıklama getirdim. ALLAH\'ı dinleyiniz ve bana uyunuz."'),
(4389, 43, 64, '"ALLAH benim Rabbim ve sizin de Rabbinizdir, sadece O\'na kulluk etmelisiniz. Dosdoğru yol budur."'),
(4390, 43, 65, 'Partiler ve mezhepler aralarında tartıştılar. Acı azaptan dolayı vay zalimlerin haline.'),
(4391, 43, 66, 'Onlar, farkında değilken, Saatin kendilerine ansızın gelmesini mi bekliyorlar?'),
(4392, 43, 67, 'Erdemlilerin dışında, o gün yakın dostlar birbirlerine düşman kesilecek.'),
(4393, 43, 68, 'Kullarım, bugün size korku yoktur ve üzülmeyeceksiniz.'),
(4394, 43, 69, 'Ayetlerime inananlar ve teslim olanlar,'),
(4395, 43, 70, 'Siz ve eşleriniz cennete girin; sevinç içinde ağırlanacaksınız.'),
(4396, 43, 71, 'Onlara altın tepsiler ve kadehlerle sunulur. Orada canların çektiği ve gözlerin hoşlandığı herşey vardır. Siz orada ebedi kalacaksınız.'),
(4397, 43, 72, 'Yaptıklarınızın bir karşılığı olarak size miras olarak verilen cennet budur.'),
(4398, 43, 73, 'Orada sizin yemeniz için bol meyveler vardır'),
(4399, 43, 74, 'Suçlular, cehennem azabında ebedi kalıcıdırlar.'),
(4400, 43, 75, 'Onlardan hafifletilmez ve onlar orada ümitsizdirler.'),
(4401, 43, 76, 'Biz onlara haksızlık etmedik, onlar kendi kendilerine haksızlık ettiler.'),
(4402, 43, 77, '"Ey Malik, artık Rabbin bizim işimizi bitirsin," diye seslenirler. O da, "Siz böyle kalacaksınız," der.'),
(4403, 43, 78, 'Size gerçeği getirdik; ancak çoğunuz gerçekten hoşlanmıyorsunuz.'),
(4404, 43, 79, 'Yoksa onlar bir plan mı kurdular? Biz de plan kurarız.'),
(4405, 43, 80, 'Yoksa, sırlarını ve komplolarını işitmediğimizi mi sanıyorlar. Doğrusu, yanlarındaki elçilerimiz kaydetmektedirler.'),
(4406, 43, 81, 'De ki; "Rahman\'ın bir çocuğu olsaydı, ona tapanların ilki ben olurdum."'),
(4407, 43, 82, 'Göklerin ve yerin Rabbi, Yönetimin Rabbi, onların nitelemelerinden çok yücedir.'),
(4408, 43, 83, 'Bırak onları, kendilerine söz verilen günlerine kavuşuncaya kadar dalsınlar, oynasınlar.'),
(4409, 43, 84, 'Gökte de tanrı, yerde de tanrı O\'dur. O Bilgedir, Bilendir.'),
(4410, 43, 85, 'Göklerin, yerin ve aralarındakilerin yönetimine sahip olan çok yücedir. Saat\'in (dünyanın sonunun) bilgisi O\'nun yanındadır ve siz O\'na döndürüleceksiniz.'),
(4411, 43, 86, 'Onların O\'nun dışında çağırdıkları şefaat edemezler. Ancak bilerek gerçeğe tanıklık edenler hariç'),
(4412, 43, 87, 'Onlara, kendilerini kimin yarattığını sorsan, "ALLAH" diyeceklerdir. Öyleyse neden çevriliyorlar?'),
(4413, 43, 88, '"Rabbim bunlar inanmıyan bir topluluktur," denmişti.'),
(4414, 43, 89, 'Onlara aldırma ve "Selam" (barış ve esenlik), de; yakında bilecekler.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'HH. M.'),
(4416, 44, 2, 'Apaçık olan bu kitaba andolsun.'),
(4417, 44, 3, 'Biz uyarmak için onu kutlu bir gecede indirdik.'),
(4418, 44, 4, 'Onda tüm bilgelik işleri belirlenir.'),
(4419, 44, 5, 'Katımızdan bir buyruktur; biz elçiler göndeririz.'),
(4420, 44, 6, 'Rabbinden bir rahmet olarak. O İşitendir, Bilendir.'),
(4421, 44, 7, 'Göklerin, yerin ve aralarındakilerin Rabbidir. Kesin bir inanca sahipseniz...'),
(4422, 44, 8, 'O\'ndan başka tanrı yoktur. Yaşatır ve öldürür. Sizin de, önceki atalarınızın da Rabbidir.'),
(4423, 44, 9, 'Doğrusu, onlar bir kuşku içinde oynamaktadırlar.'),
(4424, 44, 10, 'Göğün apaçık bir dumanı getireceği günü gözetle.'),
(4425, 44, 11, 'İnsanları çepeçevre saracaktır; bu acı bir azaptır.'),
(4426, 44, 12, '"Rabbimiz, bizden bu azabı kaldır; biz inanıyoruz."'),
(4427, 44, 13, 'Mesaja aldırış etmediler. Halbuki kendilerine apaçık bir elçi gelmişti.'),
(4428, 44, 14, 'Sonra ondan yüz çevirdiler ve, "Öğrenim görmüş bir deli!" dediler.'),
(4429, 44, 15, 'Biz azabı birazcık kaldıracağız; ama siz yine döneceksiniz.'),
(4430, 44, 16, 'Asıl o büyük yakalama ile yakaladığımızda öc alırız.'),
(4431, 44, 17, 'Onlardan önce Firavun\'un halkını sınamıştık; onlara şerefli bir elçi gitmişti:'),
(4432, 44, 18, '"Ey ALLAH\'ın kulları, bana kulak verin. Ben size gönderilmiş güvenilir bir elçiyim."'),
(4433, 44, 19, '"ALLAH\'a karşı ululanmayın. Ben size apaçık bir delille gelmiş bulunuyorum."'),
(4434, 44, 20, '"Sizin beni taşlamanızdan, benim Rabbime ve sizin Rabbinize sığınıyorum."'),
(4435, 44, 21, '"Bana inanmıyorsanız, bari beni yalnız bırakın."'),
(4436, 44, 22, 'Ve, "Bunlar suçlu bir topluluk,"diye Rabbine yalvardı.'),
(4437, 44, 23, '"Kullarımla geceleyin yola çık. Siz izleniyorsunuz."'),
(4438, 44, 24, '"Denizi yarılmış olarak terket. Onlar boğulmaya mahkum bir ordudur."'),
(4439, 44, 25, 'Onlar geride neler bırakmışlardı: Bahçeler, pınarlar,'),
(4440, 44, 26, 'Ekinler ve yüksek makamlar,'),
(4441, 44, 27, 'Ve içinde sefa sürdükleri nice nimetler...'),
(4442, 44, 28, 'Böylece hepsini başka bir topluluğa miras bıraktık.'),
(4443, 44, 29, 'Ne gök ve ne de yer onlara ağladı; ertelenmediler de.'),
(4444, 44, 30, 'İsrailoğullarını alçaltıcı zulümden kurtarmıştık;'),
(4445, 44, 31, 'Firavun\'dan. O, azgın bir diktatör idi.'),
(4446, 44, 32, 'Tüm halkın arasından özellikle onları seçtik.'),
(4447, 44, 33, 'Ve onlara, açık bir sınav olarak mucizeler verdik.'),
(4448, 44, 34, 'Şunlar da diyorlar ki:'),
(4449, 44, 35, '"Sadece bir kez ölürüz; dirilecek değiliz."'),
(4450, 44, 36, '"Doğru sözlüler iseniz, atalarımızı geri getirin."'),
(4451, 44, 37, 'Onlar mı, yoksa Tubba\' halkı ve onlardan öncekiler mi daha iyidir? Suçlu oldukları için onları yok etmiştik.'),
(4452, 44, 38, 'Biz, gökler, yer ve aralarındakileri oyun eğlence için yaratmadık.'),
(4453, 44, 39, 'Biz onları ancak belli bir amaca göre yarattık. Ne var ki onların çoğu bilmezler.'),
(4454, 44, 40, 'Hepsi Karar Günü topluca buluşacaklardır.'),
(4455, 44, 41, 'O gün dost, dostunu hiç bir şeyden koruyamaz ve yardım da görmezler.'),
(4456, 44, 42, 'Yalnız ALLAH\'ın merhamet ettikleri hariç. O Üstündür, Rahimdir.'),
(4457, 44, 43, 'Elbette, zakkum ağacı'),
(4458, 44, 44, 'Günahkarın yiyeceğidir.'),
(4459, 44, 45, 'Derişik asit gibi ve midelerde kaynayacaktır'),
(4460, 44, 46, 'Sıcak suyun kaynaması gibi.'),
(4461, 44, 47, 'Onu yakalayın ve cehennemin ortasına sürükleyin.'),
(4462, 44, 48, 'Sonra başına kaynar su azabından dökün.'),
(4463, 44, 49, '"Tad bakalım; sen çok üstün ve şerefliydin!"'),
(4464, 44, 50, '"Bu, işte kuşkulanıp durduğunuz şeydir."'),
(4465, 44, 51, 'Erdemli olanlar ise güvenlikli bir makamdadırlar.'),
(4466, 44, 52, 'Bahçeler ve pınarlar içerisinde.'),
(4467, 44, 53, 'İpek ve atlastan giysiler içinde karşılıklı otururlar.'),
(4468, 44, 54, 'Bu böyledir; onlara güzel eşler vermişizdir.'),
(4469, 44, 55, 'Tam bir güvenlik içinde her meyveyi isterler.'),
(4470, 44, 56, 'Orada, ilk ölümden başka ölüm tatmazlar. Onları cehennem azabından korumuştur.'),
(4471, 44, 57, 'Rabbinin bir lütfu olarak. İşte büyük zafer budur.'),
(4472, 44, 58, 'Öğüt alsınlar diye senin dilinle onu kolaylaştırdık.'),
(4473, 44, 59, 'Öyleyse bekle; onlar da beklemektedirler.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'HH. M.'),
(4475, 45, 2, 'Bu kitabın indirilmesi, Üstün ve Bilge olan ALLAH katındandır.'),
(4476, 45, 3, 'İnananlar için göklerde ve yerde ayetler var.'),
(4477, 45, 4, 'Sizin yaratılışınızda ve yaydığı tüm canlılarda kuşkusuz bir inanca sahip bir toplum için ayetler var.'),
(4478, 45, 5, 'Gecenin ve gündüzün birbirini izlemesinde, ALLAH\'ın gökten bir rızık indirerek onunla ölümünden sonra toprağı diriltmesinde, ve rüzgarları yönetmesinde anlayan bir toplum için ayetler var.'),
(4479, 45, 6, 'Bunlar, sana gerçek olarak okuduğumuz ALLAH\'ın ayetleridir. ALLAH\'tan ve ayetlerinden başka hangi hadise inanıyorlar?'),
(4480, 45, 7, 'Vay haline her uydurukçu günahkarın!'),
(4481, 45, 8, 'Kendisine okunan ALLAH\'ın ayetlerini işittikten sonra, sanki onları hiç işitmemiş gibi büyüklük taslayarak direniyor. Onu acı bir cezayla müjdele.'),
(4482, 45, 9, 'Ayetlerimizden bir şey öğrendiği zaman onu alaya alır.'),
(4483, 45, 10, 'Onları cehennem beklemektedir. Ne işledikleri şeyler, ne de ALLAH\'tan başka edindikleri dostlar onu kurtarabilir. Onlar, acı bir azabı haketmişlerdir.'),
(4484, 45, 11, 'Bu bir rehberdir. Rab\'lerinin ayetlerini inkar edenler için iğrenç ve acı bir azap vardır.'),
(4485, 45, 12, 'Lütfundan payınızı aramanız ve şükretmeniz için gemileri emriyle sürmenize uygun olarak denizi buyruğunuz altına veren ALLAH\'tır'),
(4486, 45, 13, 'Göklerde ve yerde ne varsa hepsini kendi katından buyruğunuza vermiştir. Düşünen bir topluluk için elbette bunda ibretler vardır.'),
(4487, 45, 14, 'İnananlara söyle ki, ALLAH\'ın günlerini ummayanları bağışlasınlar. Zira O, yaptıkları işlere göre bir topluluğa karşılık verir.'),
(4488, 45, 15, 'Kim erdemli davranırsa kendisi içindir, kim de kötülük işlerse kendi aleyhinedir. Sonra, siz Rabbinize döndürüleceksiniz.'),
(4489, 45, 16, 'İsrailoğullarına kitabı, bilgeliği ve peygamberliği vermiştik. Onları iyi nimetlerle rızıklandırmış ve onları tüm halklara üstün kılmıştık.'),
(4490, 45, 17, 'Onlara apaçık emirler verdik. Kendilerine bilgi geldikten sonra, aralarındaki çekememezlikten ötürü ayrılığa düştüler. Diriliş Günü senin Rabbin, ayrılığa düştükleri konularda onların arasında yargı verecektir.'),
(4491, 45, 18, 'Sonra sana da din konusunda bir yasa verdik; sen ona uymalısın, bilmeyenlerin keyfine uyma.'),
(4492, 45, 19, 'Onlar, ALLAH\'tan hiçbir şeyi senden savamazlar. Zalimler birbirlerinin dostlarıdır. ALLAH da erdemlilerin dostudur.'),
(4493, 45, 20, 'Bunlar, halk için aydınlatıcı delillerdir, kesin inanca sahip bir topluma bir rehber ve rahmettir.'),
(4494, 45, 21, 'Kötülükleri işleyen kimseler, dirildiklerinde ve ölümlerinde, kendilerini, inanıp erdemli davrananlarla bir mi tutacağımızı sandılar? Ne kötü hüküm veriyorlar!'),
(4495, 45, 22, 'ALLAH gökleri ve yeri belli bir amaç için yarattı ki her can, kazandığının karşılığını haksızlığa uğramadan görsün.'),
(4496, 45, 23, 'Egosunu tanrı edinen kimseye dikkat ettin mi? Nitekim ALLAH onu bilerek saptırmış, işitme duyusunu ve beynini mühürlemiş ve görüşüne perde koymuştur. ALLAH\'tan başka kim onu doğruya iletebilir? Öğüt almaz mısınız?'),
(4497, 45, 24, 'Onlar derler ki, "Biz sadece dünya hayatında yaşarız. Yaşarız, ölürüz ve bizi zamandan başkası yok etmez." Onların bu konuda bir bilgisi yoktur. Onlar sadece zannediyorlar.'),
(4498, 45, 25, 'Onlara açık açık ayetlerimiz okunduğunda, "Doğru sözlü iseniz atalarımızı geri getirin," demekten başka delilleri yoktur.'),
(4499, 45, 26, 'De ki "Sizi ALLAH diriltir ve öldürür. Sonra, gerçekleşmesinde kuşku bulunmayan Diriliş Gününde sizi bir araya toplar. Fakat insanların çoğu bilmez."'),
(4500, 45, 27, 'Göklerin ve yerin yönetimi ALLAH\'a aittir. Saat (dünyanın sonu) gerçekleştiği gün, işte o gün batılı savunanlar hüsrana uğrayacaktır.'),
(4501, 45, 28, 'Her toplumu diz çökmüş halde görürsün. Her toplum kendi kitabına çağrılır: "Bugün size, yaptıklarınızın karşılığı ödenecektir."'),
(4502, 45, 29, '"Bu kitabımız size karşı gerçeği konuşmaktadır. Çünkü biz, yaptıklarınızı kaydediyorduk."'),
(4503, 45, 30, 'İnanan ve erdemli davrananlara gelince, Rab\'leri onları rahmetine sokacaktır. Apaçık zafer işte budur.'),
(4504, 45, 31, 'İnkar edenlere gelince: "Ayetlerimiz size okunmuş ve siz de büyüklenip suçlu bir topluluk olmamış mıydınız?"'),
(4505, 45, 32, 'Kendilerine ALLAH\'ın sözünün bir gerçek ve Saat\'in kesinleşmesi kaçınılmaz bir olay olduğu söylendiğinde siz, "Saat\'in ne olduğunu bilmeyiz! Biz sadece çeşitli sanılara sahibiz ve bu konuda kesin bir bilgi elde etmiş değiliz," derdiniz.'),
(4506, 45, 33, 'Yaptıkları kötülükler kendilerine görünecek ve alay etmekte oldukları şey onları kuşatıp avlayacaktır.'),
(4507, 45, 34, 'Ve onlara şöyle denir: "Bugünkü karşılaşmanızı unuttuğunuz için biz de bugün sizi unutuyoruz. Yeriniz ateştir ve sizin bir yardımcınız da olmayacaktır."'),
(4508, 45, 35, '"Çünkü siz ALLAH\'ın ayetlerini alaya aldınız ve dünya hayatı sizi kandırdı." Sonuç olarak, onlar oradan çıkamazlar ve özürleri de dinlenmez.'),
(4509, 45, 36, 'Göklerin Rabbi, yerin Rabbi ve evrenlerin Rabbi olan ALLAH\'a övgüler olsun.'),
(4510, 45, 37, 'Göklerde ve yerde tüm büyüklükler O\'na aittir. O Üstündür, Bilgedir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'HH. M.'),
(4512, 46, 2, 'Kitabın indirilmesi, Üstün ve Bilge olan ALLAH\'tandır.'),
(4513, 46, 3, 'Biz gökleri, yeri ve aralarındakileri ancak belli bir amaca göre ve belli bir süre için yarattık. İnkar edenler uyarıya aldırış etmemektedirler.'),
(4514, 46, 4, 'De ki, "ALLAH\'ın dışında yalvardıklarınıza bir bakın. Onların yerden neyi yarattığını bana gösterin. Yoksa göklerde onların bir ortaklığı mı var? Bundan önce bir kitap veya bir bilimsel belge getirin, doğru sözlü iseniz?"'),
(4515, 46, 5, 'ALLAH\'ı bırakıp da Diriliş Gününe kadar kendilerine cevap veremiyen ve hatta onların dualarından bile habersiz olanları çağıranlardan daha sapık kim olabilir?'),
(4516, 46, 6, 'Halk toplandığı zaman onlara düşman olacaklardır ve onların kendilerine tapmalarına karşı çıkacaklardır.'),
(4517, 46, 7, 'Kendilerine apaçık ayetlerimiz okunduğunda, kendilerine gelen gerçeğe karşı çıkanlar, "Bu, açıkça bir büyüdür," dediler.'),
(4518, 46, 8, 'Yoksa "Onu uydurdu" mu diyorlar? De ki, "Onu ben uydurduysam siz beni ALLAH\'tan gelecek hiç bir şeye karşı koruyamazsınız. O, sizin taşkınlığınızı en iyi bilendir. Benimle sizin aranızda tanık olarak O yeter. O Bağışlayandır, Rahimdir.'),
(4519, 46, 9, 'De ki "Ben türedi bir elçi değilim. Bana ve size ne olacağını da bilmem. Ben, ancak bana vahyedilene uyuyorum. Ben apaçık bir uyarıcıdan başka bir şey değilim."'),
(4520, 46, 10, 'De ki, "Düşündünüz mü, ya o ALLAH katından ise ve siz de ona karşı çıkmışsanız ve İsrail oğullarından bir tanık da bunun benzerini görüp inandığı halde, siz kibirlenip yüz çevirmişseniz?! Kuşkusuz ALLAH zalim topluluğu doğru yola iletmez."'),
(4521, 46, 11, 'İnkar edenler, inananlara, "O iyi bir şey olsaydı onlar (İsrail oğulları) bizden öne geçemezlerdi" derler ve ona yol bulamadıkları için, "Bu bir efsanedir" diyeceklerdir.'),
(4522, 46, 12, 'Bundan önce Musa\'nın kitabı rehber ve rahmet idi. Bu, zalimleri uyarmak ve güzel davrananlara bir müjde olmak üzere Arapça indirilmiş doğrulayıcı bir kitaptır.'),
(4523, 46, 13, '"Rabbimiz ALLAH\'tır" deyip dosdoğru davrananlara bir korku yoktur ve onlar üzülmezler de.'),
(4524, 46, 14, 'Onlar cennet halkıdır; yapmış olduklarına karşılık olarak orada ebedi kalacaklardır.'),
(4525, 46, 15, 'Biz insana, ana ve babasına iyilik etmesini öğütledik. Anası onu zahmetle taşır, zahmetle doğurur. Ana karnında taşınması ile sütten kesilmesi otuz ay sürer. Nihayet olgunluk çağına erince ve kırk yaşına varınca: "Rabbim, bana, anama ve babama verdiğin nimete şükretmeğe ve razı olacağın yararlı işler yapmağa beni yönelt. Benim soyumu islah et. Ben tövbe edip, sana teslim olanlardanım," demelidir.'),
(4526, 46, 16, 'İşlediklerinin en iyisini kabul ettiğimiz ve kötülüklerini geçtiğimiz bu kimseler cennet halkı arasındadırlar. Bu, kendilerine verilen doğru sözdür.'),
(4527, 46, 17, 'Öte yanda bir başkası var ki anne babasına, "Öf size, benden önce nice kuşaklar geçmiş olmasına rağmen benim diriltileceğimi mi iddia ediyorsunuz?" O ikisi ise ALLAH\'a sığınarak: "Yazık sana; lütfen inan. ALLAH\'ın sözü gerçektir." Bunun üzerine o, "Bu, eskilerin masallarından başka bir şey değil," der.'),
(4528, 46, 18, 'Bu gibiler, gelip geçmiş cin ve insan toplumları arasında azap sözünü haketmiş kimselerdir. Onlar kaybedenlerdir.'),
(4529, 46, 19, 'Her biri için, yaptıklarına göre dereceler vardır. Yaptıklarının karşılığı, haksızlık edilmeden tam verilir.'),
(4530, 46, 20, 'İnkarcılar cehenneme sunulduğu gün, "Dünya hayatında tüm güzel şeylerinizi boşa harcayıp onların zevkini sürdünüz. Yeryüzünde haksız yere büyüklenmenizin ve yoldan çıkmanızın karşılığında bugün alçaltıcı azabı göreceksiniz."'),
(4531, 46, 21, 'Ad\'ın kardeşinin kum tepeciklerindeki halkını uyarmış olduğunu hatırla. Ondan önce ve sonra "ALLAH\'tan başkasına kulluk etmeyin. Sizin için büyük bir günün azabından korkarım," diyen nice uyarıcılar geçmişti.'),
(4532, 46, 22, '"Bizi tanrılarımızdan saptırmak için mi bize geldin? Doğru sözlü isen bize söz verdiğini getir bakalım," dediler.'),
(4533, 46, 23, '"Onun bilgisi sadece ALLAH\'ın yanındadır. Ben benimle gönderilen şeyi size duyuruyorum; fakat sizin cahil bir toplum olduğunuzu görüyorum."'),
(4534, 46, 24, 'Nihayet onun yayılarak vadilerine yönelmiş olduğunu gördüklerinde, "Bu, bize yağmur yağdıracak bir buluttur," dediler. Hayır, bu sizin gelmesi için meydan okuduğunuz acı bir azaba sahip bir fırtınadır.'),
(4535, 46, 25, 'Rabbinin emriyle herşeyi yerle bir eder. Sabahladıklarında, evlerinden başka herşey görülmez olmuştu. Suçlu topluluğu işte böyle cezalandırırız.'),
(4536, 46, 26, 'Sizi yerleştirmediğimiz yerlere yerleştirmiştik onları. Onlara işitme ve görme duyuları ile beyinler vermiştik. ALLAH\'ın ayet ve mucizelerini bile bile inkar ettikleri için ne işitme, ne görme duyuları ve ne de beyinleri onlara hiç bir yarar sağlamadı. Alaya aldıkları şeyler kendilerini çepeçevre kuşatıverdi.'),
(4537, 46, 27, 'Çevrenizdeki nice kentleri de yok ettik. Dönerler diye onlara ayetleri açıklamıştık.'),
(4538, 46, 28, 'ALLAH\'tan başka, O\'na yakınlık sağlamak amacıyla edindikleri tanrılar kendilerine yardım etmeli değil miydi? Aksine, onları terkettiler. İşte onların yalanları ve uydurmaları böyledir.'),
(4539, 46, 29, 'Cinlerden bir grubu, Kuran\'ı dinlemek üzere sana yöneltmiştik. Ona geldiklerinde, "Susun" dediler. (Okuma) bitirilince topluluklarına birer uyarıcı olarak döndüler.'),
(4540, 46, 30, '"Ey halkımız," dediler, "Biz, Musa\'dan sonra, kendisinden öncekileri doğrulayıcı olarak indirilen bir kitabı dinledik. O gerçeğe ve dosdoğru yola iletmektedir."'),
(4541, 46, 31, '"Ey halkımız, ALLAH\'a çağıranı yanıtlayın. O\'na inanın ki günahlarınızı bağışlasın ve sizi acı azaptan korusun."'),
(4542, 46, 32, 'ALLAH\'a çağıranı yanıtlamıyanlar yeryüzünde kaçamaz. Onların O\'na karşı dostları da olmaz. Onlar apaçık bir sapıklık içindedirler.'),
(4543, 46, 33, 'Gökleri ve yeri yaratan ve bunları yaratmakla yorulmayan ALLAH\'ın ölüleri diriltebileceğini düşünmezler mi? Evet, O her şeye gücü yetendir.'),
(4544, 46, 34, 'İnkarcılar ateşe sunuldukları gün: "Bu gerçek değil mi?" Derler ki, "Evet, Rabbimize andolsun." O da, "İnkar etmiş olduğunuz için azabı tadın," der.'),
(4545, 46, 35, 'Karar ve azim sahibi elçiler nasıl sabrettiyse sen de sabret ve onlar için acele etme. Onlar, kendilerine söz verileni gördüklerinde, (sanki dünyada) gündüzün bir saati kadar kalmışlardır. Bu bir bildiridir: Yoldan çıkmış topluluklardan başkası mı yok edilir?');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'İnkar edip ALLAH\'ın yolundan ayrılanların tüm işlerini O boşa çıkarır.'),
(4547, 47, 2, 'İnanıp erdemli davrananların ve Rab\'lerinden bir gerçek olarak Muhammed\'e indirilene inananların ise günahlarını örter ve durumlarını düzeltir.'),
(4548, 47, 3, 'Çünkü inkar edenler batıla uymakta, inananlar ise Rab\'lerinden gelen gerçeğe uymaktadırlar. ALLAH halkın durumunu böyle sergiler.'),
(4549, 47, 4, 'Savaşta inkar edenlerle karşılaştığınzda boyunlarını vurun. Sonunda üstün geldiğinizde onları esir alın; onları ya karşılıksız veya fidye karşılığında salın. Savaş durumu kalkıncaya kadar bunu uygulayın. ALLAH dileseydi sizi savaş derdinden kurtarırdı; ancak O sizi böylece birbirinizle sınamaktadır. ALLAH yolunda öldürenlere gelince, onların yaptıklarını boşa çıkarmıyacaktır.'),
(4550, 47, 5, 'Onlara yol gösterecek ve işlerini düzeltecektir.'),
(4551, 47, 6, 'Onları kendilerine tanıtmış olduğu cennete sokacaktır.'),
(4552, 47, 7, 'Ey inananlar, ALLAH\'ı desteklerseniz, O da sizi destekler ve ayaklarınızı sağlam tutar.'),
(4553, 47, 8, 'İnkar edenler ise yıkımı haketmişlerdir; onların işlerini boşa çıkarmıştır.'),
(4554, 47, 9, 'Çünkü onlar ALLAH\'ın indirdiğini beğenmediler. O da onların işlerini geçersiz kılmıştır.'),
(4555, 47, 10, 'Kendilerinden öncekilerin sonunun nasıl olduğuna bakmak için yeryüzünü dolaşmazlar mı? ALLAH onları imha etmiştir ve bu inkarcıları da aynı son beklemektedir.'),
(4556, 47, 11, 'Çünkü ALLAH inananların koruyucusudur. İnkarcıların ise bir koruyucusu yoktur.'),
(4557, 47, 12, 'ALLAH inanıp erdemli davrananları içlerinden ırmaklar akan cennetlere sokar. İnkarcılar ise hayvanlar gibi yiyip yaşamaktadırlar ve sonunda ateşi boylarlar.'),
(4558, 47, 13, 'Seni çıkaran ülkenden daha güçlü nice ülkeler vardı ki onları yok ettik, onlara yardım eden olmadı.'),
(4559, 47, 14, 'Rabbinden bir delil üzerinde bulunan, işlediği kötülükler kendisine güzel gözüken gibi olur mu? Onlar arzularına uymaktadırlar.'),
(4560, 47, 15, 'Erdemlilere söz verilen cennetin örneği şudur: İçinde arı sudan ırmaklar, tadı bozulmayan sütten ırmaklar, içenlere lezzet veren sarhoş edici maddelerden oluşan ırmaklar ve süzme baldan ırmaklar vardır. Onlar için orada her türlü ürün ve Rab\'lerinden bir bağışlanma vardır. Bunların durumu, ateşte sürekli kalan ve bağırsaklarını yırtan kaynar bir sudan içirilen kimselerin durumu gibi olur mu?'),
(4561, 47, 16, 'Onlardan bazıları var ki seni dinlerler. Fakat senin yanından çıkınca, kendilerine bilgi verilmiş olanlara, "Bu, demin ne söyledi?" diye sorarlar. İşte bunlar, ALLAH\'ın kalplerini damgaladığı kimselerdir ve onlar heveslerinin ardına düşmüşlerdir.'),
(4562, 47, 17, 'Doğru yolu bulanların ise hidayetini arttırır ve onların erdemli olmalarını sağlar.'),
(4563, 47, 18, 'Saatin kendilerine ansızın gelmesini mi bekliyorlar? Kuşkusuz onun alametleri gelmiş bulunuyor. Onlara gelip çatınca kendilerine gelen mesajın ne yararı olur?'),
(4564, 47, 19, 'Bil ki, ALLAH\'tan başka tanrı yoktur. Kendinin, inanan erkeklerin ve inanan kadınların günahları için bağışlanma dile. ALLAH sizin tüm hareketlerinizi ve duracağınız yeri bilir.'),
(4565, 47, 20, 'İnananlar, "(Savaşmaya izin veren) bir süre indirilse ya?" diyorlardı. Ancak hükmü açık bir sure indirilip de içinde savaştan söz edilince, kalplerinde hastalık bulunanların, ölüm korkusuyla bayılan bir kimsenin bakışı gibi sana baktıklarını görürsün. Onlar için uygun olan,'),
(4566, 47, 21, 'İtaat etmek ve güzel konuşmaktır. İş kararlaştırılınca ALLAH\'a karşı dürüst olsalar kendileri için iyi olurdu.'),
(4567, 47, 22, 'Demek işbaşına gelecek olsanız yeryüzünde bozgunculuk çıkaracak ve akrabalık bağlarını koparacaksınız?'),
(4568, 47, 23, 'İşte, ALLAH\'ın lanetlediği, sağırlaştırıp körleştirdiği kimseler bunlardır.'),
(4569, 47, 24, 'Neden Kuran\'ı araştırıp incelemezler? Yoksa kilitli mi beyinleri?'),
(4570, 47, 25, 'Kendilerine doğru yol belli olduktan sonra geriye dönenleri şeytan ayartmıştır ve onlara ümit vermiştir.'),
(4571, 47, 26, 'Zira onlar, ALLAH\'ın indirdiğini beğenmiyenlere, "Bazı konularda size uyuyoruz," demişlerdi. ALLAH onların komplolarını bilmektedir'),
(4572, 47, 27, 'Melekler canlarını alırken nasıl da (pişmanlık içinde) yüzlerine ve sırtlarına vururlar?'),
(4573, 47, 28, 'Çünkü onlar, ALLAH\'ı kızdıran şeye uydular, O\'nun hoşnut edecek şeyleri beğenmediler ve sonunda yaptıklarını geçersiz hale getirdiler.'),
(4574, 47, 29, 'Kalplerinde hastalık bulunanlar, ALLAH\'ın, onların kinlerini açığa çıkaracağını bilmezler mi?'),
(4575, 47, 30, 'Dileseydik onları sana gösterirdik, sen de onları yüzlerinden tanırdın. Sen onları sözlerinin eğriliğinden tanıyabilirsin. ALLAH tüm işlediklerinizi bilir.'),
(4576, 47, 31, 'Sizden çaba gösterenleri ve güçlüklere karşı direnenleri ortaya çıkarıncaya kadar sizi sınayacağız ve kalitenizi sınayacağız.'),
(4577, 47, 32, 'İnkar edip ALLAH\'ın yolundan sapanlar ve kendilerine doğru yol belli olduktan sonra elçiye karşı gelenler ALLAH\'a hiç bir zarar veremezler. Aksine, onların yaptıklarını geçersiz kılacaktır.'),
(4578, 47, 33, 'Ey inananlar, ALLAH\'a uyunuz, elçiye uyunuz ve işlerinizi boşa çıkarmayınız.'),
(4579, 47, 34, 'İnkar edip ALLAH\'ın yolundan saptıktan sonra inkarcılar olarak ölenleri ALLAH bağışlamıyacaktır.'),
(4580, 47, 35, 'Barışa giden yolda gevşemeyin. Zafer sizindir. ALLAH sizinle beraberdir ve işlediklerinizi ziyan etmeyecektir.'),
(4581, 47, 36, 'Dünya hayatı bir oyun ve eğlenceden ibarettir. İnanıp erdemli davranırsanız, ödüllerinizi verir. O sizden paralarınızı istemiyor.'),
(4582, 47, 37, 'Onları sizden isteseydi ve sizi sıkıştırsaydı cimrilik ederdiniz ve sizin gizli kötülüklerinizi açığa çıkarırdı.'),
(4583, 47, 38, 'Sizler, ALLAH yolunda harcamaya çağrılıyorsunuz, ancak bir kısmınız cimrilik etmektedir. Kim cimrilik ederse kendisine karşı cimrilik etmiş olur. ALLAH zengindir, siz ise yoksulsunuz. Yüz çevirirseniz, yerinize başka bir topluluk getirir de onlar sizin gibi olmazlar.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'Biz sana apaçık bir zafer verdik.'),
(4585, 48, 2, 'Ki ALLAH, geçmiş ve gelecek günahlarını bağışlasın, sana olan nimetini tamamlasın ve seni doğru yola iletsin.'),
(4586, 48, 3, 'Ve ALLAH seni üstün bir zaferle desteklesin.'),
(4587, 48, 4, 'O inançlarına inanç katmak için inananların kalbine huzur indirendir. Göklerin ve yerin orduları ALLAH\'ındır. ALLAH Bilendir, Bilgedir.'),
(4588, 48, 5, 'Ve inanan erkeklerle inanan kadınları, içinden ırmaklar akan cennetlere ebedi kalmak üzere soksun ve onların günahlarını örtsün. Elbette bu, ALLAH\'ın katında büyük bir başarıdır.'),
(4589, 48, 6, 'Ve ALLAH hakkında kötü düşünceler besleyen ikiyüzlü erkeklerle ikiyüzlü kadınları ve putperest erkeklerle putperest kadınları cezalandırsın. Kötülükleri geri tepecektir. ALLAH onlara gazaplanmış, onları lanetlemiş ve onlar için cehennemi hazırlamıştır. Orası ne kötü bir dönüş yeridir.'),
(4590, 48, 7, 'Göklerin ve yerin orduları ALLAH\'a aittir. ALLAH Üstündür, Bilgedir.'),
(4591, 48, 8, 'Biz seni bir tanık, bir müjdeci ve bir uyarıcı olarak gönderdik.'),
(4592, 48, 9, 'Ki siz insanlar, ALLAH\'a ve elçisine inanasınız ve O\'na saygı gösteresiniz, O\'nu dinleyesiniz ve sabah akşam O\'nu yüceltesiniz.'),
(4593, 48, 10, 'Sana bağlılık sözü verenler, ALLAH\'a bağlılık sözü vermişlerdir. ALLAH\'ın eli onların elleri üzerindedir. Kim sözünü bozarsa, kendi aleyhine bozmuş olur. ALLAH\'a verdiği sözü yerine getirene ise büyük bir ödül vereceğiz.'),
(4594, 48, 11, 'Araplardan geride kalanlar, "Paralarımız ve çoluk çocuğumuz bizi alıkoydu, bizim için bağışlanma dile," diyeceklerdir. Onlar gönüllerindekini değil, dilleriyle söylerler. De ki, "Sizin için bir zarar veya yarar dilerse ALLAH\'ın bu dileğine kim engel olabilir?" Oysa ALLAH tüm yaptıklarınızdan haberdardır.'),
(4595, 48, 12, 'Aslında siz, elçinin ve inananların ebedi olarak ailelerine dönmeyeceklerini sandınız ve bu düşünce gönlünüze hoş gelmişti. Kötü hayaller kurup durdunuz ve sonunda bayağı bir topluluk oldunuz.'),
(4596, 48, 13, 'Kim ALLAH\'a ve elçisine inanmazsa, biz inkarcılar için bir ateş hazırlamışızdır.'),
(4597, 48, 14, 'Göklerin ve yerin yönetimi ALLAH\'a aittir. Dileyeni bağışlar ve dileyeni de cezalandırır. ALLAH Bağışlayandır, Rahimdir.'),
(4598, 48, 15, 'Savaştan geri kalmış olanlar, siz ganimetleri almak için gittiğinizde, "Bırakın biz de sizinle gelelim," diyecekler. Onlar ALLAH\'ın sözünü değiştirmek istiyorlar. De ki, "Siz bizi izlemiyeceksiniz. Bu ALLAH\'ın önceden almış olduğu karardır." Bunun üzerine onlar, "Siz aslında bizi çekemiyorsunuz," diyeceklerdir. Doğrusu, onlar pek az söz anlarlar.'),
(4599, 48, 16, 'Araplardan geri kalanlara de ki, "Siz çok güçlü bir topluluğa karşı çağrılacaksınız. Onlar teslim olana kadar onlarla savaşacaksınız. İtaat ederseniz ALLAH size güzel bir ödül verir. Daha önce yüz çevirdiğiniz gibi yüz çevirirseniz sizi acı bir azap ile cezalandırır.'),
(4600, 48, 17, 'Köre bir güçlük yoktur, felçliye bir güçlük yoktur, hastaya da bir güçlük yoktur. Kim ALLAH\'a ve elçisine uyarsa, O onu içinden ırmaklar akan cennetlere sokar. Kim yüz çevirirse onu da acı bir biçimde cezalandırır.'),
(4601, 48, 18, 'ALLAH, ağacın altında sana bağlılık sözü veren inananlardan hoşnut olmuştur. Onların kalplerinde olanı bilmiş, onlara huzur indirmiş ve onları yakın bir zaferle ödüllendirmiştir.'),
(4602, 48, 19, 'Ek olarak, birçok ganimet ele geçirdiler. ALLAH Üstündür, Bilgedir.'),
(4603, 48, 20, 'ALLAH sizin birçok ganimet ele geçireceğinizi size söz vermiş bulunuyor. Size bunu çabucak verdi ve halkın ellerini sizden çekti. Böylece bunu inananlar için bir delil kıldı ve sizi dosdoğru yola iletti.'),
(4604, 48, 21, 'Güç yetirmediğiniz diğer gruba gelince, onları ALLAH kuşatmıştır. ALLAH elbette her şeye gücü yetendir.'),
(4605, 48, 22, 'İnkarcılar sizinle savaşsalardı dönüp kaçarlardı. Sonra ne bir sahip, ne de bir yardımcı bulamazlardı.'),
(4606, 48, 23, 'Öteden beri uygulanan ALLAH\'ın sünneti (sistemi) budur. ALLAH\'ın sünnetinde bir değişme bulamazsın.'),
(4607, 48, 24, 'Sizi onlara karşı üstün getirdikten sonra Mekke\'nin göbeğinde onların ellerini yakanızdan sizin de ellerinizi onların yakasından çeken odur. ALLAH yaptıklarınızı görendir.'),
(4608, 48, 25, 'Onlar inkar edenlerdir, sizi Kutsal Mescitten ve kurbanları yerine ulaşmaktan alıkoyanlardır. Kendilerini tanımadığınız için inciteceğiniz inanan erkekler ve inanan kadınlar bulunmasaydı ve böylece onlardan dolayı bilmeden kendinizi üzmeniz ihtimali olmasaydı... Böylece ALLAH dileyeni rahmetine sokar. Birbirleriyle karışmış halde olmasalardı onlardan inkar edenleri acı bir azapla cezalandırırdık.'),
(4609, 48, 26, 'İnkar edenler, gönüllerindeki öfke ve bağnazlığı, cahiliyye döneminin fanatizmini ateşlediklerinde, ALLAH elçisinin ve inananların üzerine huzurunu indirmiş ve onların erdemlilik sözünü tutmalarını sağlamıştı. Onlar buna tam yaraşan ve hakkeden kimselerdi. ALLAH herşeyi bilendir.'),
(4610, 48, 27, 'ALLAH elçisinin rüyasını gerçekleştirdi: "ALLAH dilerse, güvenlik içinde, başlarınızı (saçlarınızı) traş etmiş ve kısaltmış olarak Kutsal Mescide gireceksiniz. Bir korku duymayacaksınız. Sizin bilmediklerinizi bildiğinden, size bundan önce acil bir zafer hazırlamıştır."'),
(4611, 48, 28, 'O, tüm dinlere üstün kılmak üzere elçisini hidayetle ve gerçek dinle gönderendir. Tanık olarak ALLAH yeterlidir.'),
(4612, 48, 29, 'ALLAH\'ın elçisi Muhammed ve beraberinde bulunanlar inkarcılara karşı sert ve kendi aralarında ise merhametlidirler. Onları, eğilip secde ederek ALLAH\'tan lütuf ve hoşnutluk dilerken görürsün. Yüzlerinde secdelerin izleri vardır. Bu, onların Tevrat\'taki nitelikleridir. İncil\'deki nitelikleri ise, filizini çıkarıp güçlendirmiş ve kalınlaşıp gövdesi üzerine dikilerek ekincileri sevindiren bitki gibidir. O, onlarla inkarcıları öfkelendirir. ALLAH, onlardan inanıp erdemli davrananlara bağışlanma ve büyük bir ödül söz vermiştir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'Ey inananlar, ALLAH\'ın ve elçisinin huzurunda öne geçmeyiniz. ALLAH\'ı dinleyiniz. ALLAH İşitir, Bilir.'),
(4614, 49, 2, 'Ey inananlar, sesinizi peygamberin sesinin üstüne çıkarmayın ve birbirinize bağırdığınız gibi ona bağırmayın. Yoksa siz farketmeden işleriniz boşa gider.'),
(4615, 49, 3, 'Seslerini ALLAH\'ın elçisinin huzurunda kısanlar öyle kimselerdir ki, ALLAH onların kalplerini erdemlilik için uygun hale getirmiştir. Onlar için bağışlanma ve büyük bir ödül vardır.'),
(4616, 49, 4, 'Odaların/duvarların ardından sana seslenenlerin çoğu düşüncesiz kimselerdir.'),
(4617, 49, 5, 'Sen yanlarına çıkıncaya kadar sabretselerdi kendileri için daha iyi olurdu. ALLAH Bağışlayandır, Rahimdir.'),
(4618, 49, 6, 'Ey inananlar, kötü huylara sahip birisi size bir haber getirirse onu araştırınız. Yoksa bilmeden bir topluluğa karşı haksızlık edersiniz ve daha sonra yaptığınızdan pişmanlık duyarsınız.'),
(4619, 49, 7, 'Bilesiniz ki ALLAH\'ın elçisi aranızdadır. İşlerin çoğunda sizlere uysaydı sıkıntıya düşerdiniz. Fakat, ALLAH inancı size sevdirdi ve onu gönüllerinizde süsledi; inkarı, kötülüğü ve isyanı ise size çirkin gösterdi. İşte doğruyu izleyenler bunlardır.'),
(4620, 49, 8, 'ALLAH\'ın lütfu ve nimeti böyledir. ALLAH Bilendir, Bilgedir.'),
(4621, 49, 9, 'İnananlardan iki grup birbirleriyle savaşırlarsa onların arasını bulun. Onlardan biri ötekine saldırırsa, ALLAH\'ın buyruğuna dönünceye kadar saldırgan grupla savaşın. Dönerse onların arasını tarafsızca düzeltin ve adaletli davranın. ALLAH adaletli davrananları sever.'),
(4622, 49, 10, 'İnananlar kardeştirler. Kardeşlerinizin arasını düzeltin. ALLAH\'ı sayın ki merhamet edilesiniz.'),
(4623, 49, 11, 'Ey inananlar, bir topluluk diğer bir topluluk ile alay etmesin, belki onlar kendilerinden daha iyidirler. Kadınlar da kadınlarla alay etmesinler, belki onlar kendilerinden daha iyidirler. Birbirinizi ayıplamayın ve birbirinizi, küçük düşürücü kelimelerle çağırmayın. İnançtan sonra yoldan çıkmak ne kötü bir isimdir. Kim tevbe etmezse onlar zalimlerdir.'),
(4624, 49, 12, 'Ey inananlar, zandan tümüyle sakının, çünkü zannın bir kısmı günahtır. Birbirinizin gizli şeylerini araştırmayın ve birbirinizi arkadan çekiştirmeyin. Ölü kardeşinizin etini yemekten hoşlanır mısınız? Elbette ondan iğrenirsiniz. ALLAH\'ı dinleyin. ALLAH tevbeleri kabul edendir, Rahimdir.'),
(4625, 49, 13, 'Ey halk, sizi bir erkek ile bir dişiden yarattık ve birbirinizle tanışmanız için sizi ırklara ve boylara ayırdık. ALLAH yanında sizin en değerliniz en erdemli olanınızdır. ALLAH Bilendir, Haberdardır.'),
(4626, 49, 14, 'Araplar "İnandık," dediler. De ki, "Siz inanmadınız, fakat inanç kalbinize girinceye kadar, \'teslim olduk\' deyin. ALLAH\'a ve elçisine uyarsanız yaptıklarınızdan hiç bir şeyi eksiltmez. ALLAH Bağışlayandır, Rahimdir.'),
(4627, 49, 15, 'İnananlar onlardır ki, ALLAH\'a ve elçisine inandılar, sonra kuşku beslemediler ve ALLAH yolunda paraları ve canlarıyla savaşım verdiler. Onlar doğru olanlardır.'),
(4628, 49, 16, 'De ki, "ALLAH\'a dininizi mi öğretiyorsunuz? ALLAH göklerde ve yerde olanı bilir. ALLAH her şeyi bilendir."'),
(4629, 49, 17, 'Müslüman (teslim) oldular diye seni minnet altında bırakmak istiyorlar. De ki, "Müslümanlığınızı başıma kakmayın. Aslında, sizi inanca ulaştırdığı için ALLAH sizi kendisine borçlu görür, eğer doğru kimselerseniz."'),
(4630, 49, 18, 'ALLAH göklerin ve yerin gizemlerini bilir. ALLAH yaptıklarınızı görendir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'Q, şanlı Kuran\'a andolsun'),
(4632, 50, 2, 'İçlerinden bir uyarıcının kendilerine gelmesini tuhaf karşıladılar! Kafirler, "Bu tuhaf bir şey" dediler,'),
(4633, 50, 3, '"Ölüp toprak olduktan sonra mı? Bu imkansız bir dönüştür."'),
(4634, 50, 4, 'Yeryüzünün onlardan kimi alıp götürdüğünü bilmişizdir. Yanımızda koruyan bir kayıt vardır.'),
(4635, 50, 5, 'Oysa onlar gerçek kendilerine geldiği zaman onu yalanladılar; kararsızlık içindedirler.'),
(4636, 50, 6, 'Üstlerindeki göğe bakmadılar mı, onu hiçbir çatlağı olmadan nasıl yaptık ve onu nasıl süsledik?'),
(4637, 50, 7, 'Yeri ise yaydık, içine dağlar yerleştirdik ve içinde her çeşit güzel bitkiler yetiştirdik.'),
(4638, 50, 8, 'Her yönelen kul için bu bir aydınlatma ve mesajdır.'),
(4639, 50, 9, 'Ve gökten kutlu bir su indirerek onunla bahçeler ve biçilecek taneler bitirdik.'),
(4640, 50, 10, 'Küme küme tomurcuklara sahip yüksek hurma ağaçları yetiştirdik.'),
(4641, 50, 11, 'Kullara bir besin olarak. Onunla bölgeyi dirilttik. Çıkış (diriliş) de böyledir.'),
(4642, 50, 12, 'Onlardan önce Nuh\'un halkı, Res\'liler ve Semud da yalanlamıştı.'),
(4643, 50, 13, 'Ad, Firavun ve Lut\'un kardeşleri...'),
(4644, 50, 14, 'Eyke\'liler ve Tubba\' halkı da... Hepsi elçileri yalanladılar. Bunun üzerine tehdidim gerçekleşti.'),
(4645, 50, 15, 'İlk yaratılıştan yorulduk mu ki? Doğrusu onlar yeni bir yaratılıştan kuşku içindedirler.'),
(4646, 50, 16, 'İnsanı biz yarattık ve onun kendi kendine neyi fısıldadığını iyi biliriz. Biz ona şahdamarından daha yakınız.'),
(4647, 50, 17, 'Sağında ve solunda iki alıcı melek oturmuş kaydetmektedirler.'),
(4648, 50, 18, 'Yanında gözetleyen tanıklar olmaksızın ağzından bir söz çıkmaz.'),
(4649, 50, 19, 'Sonunda ölüm sarhoşluğu gerçekten gelmiştir; işte senin kendisinden kaçıp durduğun şey budur.'),
(4650, 50, 20, 'Boruya üflenmiştir. Bu, söz verilen gündür.'),
(4651, 50, 21, 'Her can yanında bir sürücü ve bir tanık ile gelir.'),
(4652, 50, 22, 'Sen tümüyle bundan gafildin. Şimdi biz örtünü kaldırdık; bugün gözün çelik gibi keskindir.'),
(4653, 50, 23, 'Yanındaki arkadaşı, "İşte yanımdaki hazır" der.'),
(4654, 50, 24, 'Haydi ikiniz atın cehenneme her inatçı kafiri,'),
(4655, 50, 25, 'İyiliği engelleyen, azgın, kuşkucuyu...'),
(4656, 50, 26, 'O ki ALLAH ile birlikte başka tanrı edindi. Onu o çetin cezanın içine atın.'),
(4657, 50, 27, 'Arkadaşı, "Rabbim, ben onu azdırmadım; fakat o kendisi derin bir sapıklık içindeydi," der.'),
(4658, 50, 28, 'O da der ki, "Huzurumda cekişmeyin. Size daha önceden uyarı göndermiştim."'),
(4659, 50, 29, '"Benim katımda söz değiştirilmez ve ben kullara asla haksızlık etmem."'),
(4660, 50, 30, 'O gün cehenneme, "Doldun mu?" deriz. O, "Daha yok mu?" der.'),
(4661, 50, 31, 'Erdemlilere ise cennet yaklaştırılır, uzak değildir.'),
(4662, 50, 32, 'Bu, size söz verilendir. Her yönelen ve her koruyana,'),
(4663, 50, 33, 'Onlar yalnız başınayken bile Rahman\'ı sayarlar ve içtenlikle gelirler.'),
(4664, 50, 34, 'Oraya esenlikle girin; bu, sonsuz yaşama günüdür.'),
(4665, 50, 35, 'Diledikleri her şeyi elde ederler ve hatta katımızda fazlası da vardır.'),
(4666, 50, 36, 'Onlardan önce, onlardan daha güçlü nice nesilleri yok etmiştik. Ülke ülke dolaşmışlardı; kurtuluşu var mı?'),
(4667, 50, 37, 'Bunda, zeka sahibi olan yahut tanık olarak kulak veren herkes için bir mesaj vardır.'),
(4668, 50, 38, 'Gökleri, yeri ve aralarındakileri altı günde yarattık. Hiç bir yorgunluk da duymadık'),
(4669, 50, 39, 'Onların söylediklerine sabret ve güneşin doğuşundan ve batışından önce Rabbini överek yücelt.'),
(4670, 50, 40, 'Geceleyin O\'nu yücelt ve secdelerin ardından da...'),
(4671, 50, 41, 'Çağırıcının yakın bir yerden çağıracağı güne kulak ver.'),
(4672, 50, 42, 'O gün o çığlığı kesinlikle duyarlar; bu, çıkış günüdür.'),
(4673, 50, 43, 'Biz diriltiriz, öldürürüz ve dönüş de bizedir.'),
(4674, 50, 44, 'O gün yer onlardan dolayı yarılıverir. Bu, bizim için kolay bir toparlamadır'),
(4675, 50, 45, 'Biz onların ne konuştuğunu iyi biliyoruz. Sen onların üzerinde bir zorba değilsin. Uyarılarımdan korkanlara sen bu Kuran ile öğüt ver.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'Esip savuranlara,'),
(4677, 51, 2, 'Yük yüklenenlere,'),
(4678, 51, 3, 'Kolayca akıp gidenlere,'),
(4679, 51, 4, 'Ve işi bölümlere ayıranlara andolsun ki'),
(4680, 51, 5, 'Size söz verilen kuşkusuz bir gerçektir.'),
(4681, 51, 6, 'Yargılama kesinlikle gerçekleşecektir.'),
(4682, 51, 7, 'Mükemmel çizilmiş yörüngelere sahip göğe andolsun ki'),
(4683, 51, 8, 'Siz ihtilaf içindesiniz.'),
(4684, 51, 9, 'Çevrilen, ondan çevrilir.'),
(4685, 51, 10, 'Kahrolsun palavracılar,'),
(4686, 51, 11, 'Ki şaşkınlıkları içinde umursamıyorlar.'),
(4687, 51, 12, 'Yargı Gününün zamanını sorarlar.'),
(4688, 51, 13, 'O gün onlar ateşe sunulacaklardır.'),
(4689, 51, 14, 'Cezanızı tadın, meydan okumakta olduğunuz şey budur işte!'),
(4690, 51, 15, 'Erdemliler cennetlerdedir, pınar başlarındadır.'),
(4691, 51, 16, 'Rab\'lerinin kendilerine verdiğini alırlar. Çünkü onlar bundan önce güzel davranıyorlardı.'),
(4692, 51, 17, 'Geceleri az uyurlardı.'),
(4693, 51, 18, 'Seher vakitlerinde bağışlanma dilerlerdi.'),
(4694, 51, 19, 'Paralarında, isteyenler ve yoksullar için bir pay vardı.'),
(4695, 51, 20, 'Kesin inananlar için yerde ayetler (işaret ve deliller) vardır.'),
(4696, 51, 21, 'Kendi içinizde de... Görmez misiniz?'),
(4697, 51, 22, 'Gökte rızkınız ve size söz verilenler vardır.'),
(4698, 51, 23, 'Göğün ve yerin Rabbine andolsun ki sizin konuşmanız nasıl bir gerçek ise, bu da öylece bir gerçektir.'),
(4699, 51, 24, 'İbrahim\'in ağırlanan konuklarının haberini aldın mı?'),
(4700, 51, 25, 'Onun huzuruna girmişlerdi ve "Selam (barış)" demişlerdi. O da, "Selam size, yabancılar!" demişti.'),
(4701, 51, 26, 'Ailesine yöneldi ve sonra semiz bir buzağı ile geldi.'),
(4702, 51, 27, 'Onu onların önüne sürüp, "Yemez misiniz?" dedi.'),
(4703, 51, 28, 'Onlardan bir korku duydu. Bunun üzerine onlar, "Korkma" dediler ve ona bilgin bir oğul müjdelediler.'),
(4704, 51, 29, 'Karısı hayret içinde, (hayretten) yüzüne vurarak, "Kısır bir yaşlı kadın!" dedi.'),
(4705, 51, 30, 'Dediler ki, "Rabbin böyle söylemiştir. O Bilgedir, Bilendir."'),
(4706, 51, 31, '(İbrahim:) "Ey elçiler asıl göreviniz nedir?" dedi.'),
(4707, 51, 32, 'Dediler ki, "Biz suçlu bir topluluğa gönderildik."'),
(4708, 51, 33, '"Üzerlerine balçıktan taşlar göndermek için..."'),
(4709, 51, 34, '"Rabbin tarafından taşkınlar için işaretlenmiş olarak."'),
(4710, 51, 35, 'Sonra, orada inananlardan kim varsa çıkardık.'),
(4711, 51, 36, 'Zaten orada bir evin dışında hiç bir müslüman bulmadık.'),
(4712, 51, 37, 'Acı azaptan korkacaklar için orada bir ders bıraktık.'),
(4713, 51, 38, 'Musa\'da da (bir ders vardır). Onu Firavun\'a apaçık bir delil ile göndermiştik.'),
(4714, 51, 39, 'Erkanıyla birlikte yüz çevirdi ve "Ya bir büyücüdür, ya da bir deli," dedi.'),
(4715, 51, 40, 'Onu ve askerlerini yakalayıp denize attık. Bu sonucu haketmişti.'),
(4716, 51, 41, 'Ad halkında da (bir ders vardır). Üzerlerine korkunç bir rüzgar gönderdik.'),
(4717, 51, 42, 'Rastgeldiği her şeyi toz toprağa çeviriyordu.'),
(4718, 51, 43, 'Semud\'da da (bir ders vardır). Onlara, "Belli bir süreye kadar keyfinize bakın" denmişti.'),
(4719, 51, 44, 'Rab\'lerinin emrine karşı geldiler. Bunun üzerine bakınırlarken onları bir yıldırım çarptı.'),
(4720, 51, 45, 'Ne kalkabildiler, ne de yardım görebildiler.'),
(4721, 51, 46, 'Daha önce de Nuh halkını... Onlar yoldan çıkmış bir topluluktu.'),
(4722, 51, 47, 'Göğü gücümüzle biz kurduk ve onu biz genişletmekteyiz.'),
(4723, 51, 48, 'Yeri biz döşedik; ne güzel döşeyiciyiz.'),
(4724, 51, 49, 'Öğüt almanız için de herşeyi çiftler halinde yarattık.'),
(4725, 51, 50, 'Öyleyse ALLAH\'a kaçınız. Ben, O\'nun tarafından size gönderilmiş bir uyarıcıyım.'),
(4726, 51, 51, 'ALLAH ile birlikte başka tanrılar edinmeyin. Ben O\'nun tarafından size gönderilmiş bir uyarıcıyım.'),
(4727, 51, 52, 'İşte böyle, onlardan öncekilere her ne zaman bir elçi geldiyse, "Bu, bir büyücüdür," yahut "Bu bir delidir," derlerdi.'),
(4728, 51, 53, 'Bunu (söylemeyi) birbirlerine öğütlediler mi? Doğrusu, onlar sınırı aşan bir topluluktur.'),
(4729, 51, 54, 'Onlardan yüz çevir; sen kınanacak değilsin.'),
(4730, 51, 55, 'Hatırlat, çünkü hatırlatmak inananlara yarar sağlar.'),
(4731, 51, 56, 'Cinleri ve insanları ancak bana kulluk etmeleri için yarattım.'),
(4732, 51, 57, 'Onlardan ne bir rızık istiyorum, ne de beni beslemelerini.'),
(4733, 51, 58, 'Kuşkusuz Rızık veren, Kuvvet sahibi ve Güçlü olan ancak ALLAH\'tır.'),
(4734, 51, 59, 'Elbette, bu zulmedenlerin de (geçmiş) yoldaşlarının payına benzer bir payları vardır.'),
(4735, 51, 60, 'Kendilerine söz verilen günden dolayı vay haline şu inkarcıların!');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'Andolsun Tur\'a,'),
(4737, 52, 2, 'Kaydedilmiş kitaba,'),
(4738, 52, 3, 'Ki parşömen üzerinde yayımlanmış.'),
(4739, 52, 4, 'Sık sık ziyaret edilen Eve (Kabe\'ye),'),
(4740, 52, 5, 'Yükseltilmiş tavana,'),
(4741, 52, 6, 'Ve kaynatılmış denize...'),
(4742, 52, 7, 'Rabbinin cezalandırması kesinlikle gerçekleşecektir.'),
(4743, 52, 8, 'Onu engelleyecek hiç bir güç yoktur.'),
(4744, 52, 9, 'O gün gök sallanıp sarsılacak,'),
(4745, 52, 10, 'Dağlar yürütülüp silinecek,'),
(4746, 52, 11, 'Yalanlayanların vay haline o gün!'),
(4747, 52, 12, 'Onlar ki bir bataklıkta oynamaktadırlar.'),
(4748, 52, 13, 'Cehennem ateşine itildikleri gün:'),
(4749, 52, 14, 'İşte, yalanlamakta olduğunuz Ateş budur.'),
(4750, 52, 15, 'Bu bir büyü müdür, yoksa siz mi görmüyorsunuz?'),
(4751, 52, 16, 'Orda yanın. İster sabredin, ister sabretmeyin sizin için değişmeyecektir. Yaptığınızın karşılığını görmektesiniz.'),
(4752, 52, 17, 'Erdemliler bahçeler ve nimetler içindedir.'),
(4753, 52, 18, 'Rab\'lerinin kendilerine vermiş olduğu şeylerden zevk duyarlar. Rab\'leri onları cehennem azabından korumuştur.'),
(4754, 52, 19, 'Yapmış olduklarınıza karşılık afiyetle yeyin, için.'),
(4755, 52, 20, 'Dizilmiş koltuklara yaslanmışlardır ve onları güzel eşlerle eşlendirmişizdir.'),
(4756, 52, 21, 'Soyları tarafından izlenen inananlara soylarını da katarız ve onların yaptıklarından hiç bir şeyi eksiltmeyiz. Herkes kazanmış olduğu şeylerin bir ipoteğidir.'),
(4757, 52, 22, 'Onlara canlarının istediği meyveden ve etten bol bol veririz.'),
(4758, 52, 23, 'Orada birbirlerinden kadeh kapışırlar, onda ne bir saçmalama, ne de bir günaha girme vardır.'),
(4759, 52, 24, 'Çevrelerinde, inciler gibi korunmuş kendilerine ait hizmetkarlar (servis için) dolaşıp durur.'),
(4760, 52, 25, 'Birbirlerine dönüp geçmişi anarlar:'),
(4761, 52, 26, '"Daha önce halkımızın arasında çekinirdik," derler,'),
(4762, 52, 27, '"ALLAH bize iyilik etti de bizi içe işleyen azaptan korudu."'),
(4763, 52, 28, '"Biz daha önce O\'na yalvarırdık; O, İyilik edendir, Rahimdir."'),
(4764, 52, 29, 'Sen öğüt ver. Rabbinin sana olan iyiliği sayesinde sen ne bir kahinsin, ne de deli.'),
(4765, 52, 30, 'Yoksa, "O bir şairdir, onun ölmesini bekliyoruz." mu diyorlar?'),
(4766, 52, 31, 'De ki, "Bekleyedurun; ben de sizinle birlikte beklemekteyim."'),
(4767, 52, 32, 'Bunları rüyalarının etkisiyle mi söylüyorlar, yoksa onlar haddi aşan bir topluluk mudur?'),
(4768, 52, 33, 'Yoksa, "Onu kendi uydurdu" mu diyorlar? Hayır, onlar inanmazlar.'),
(4769, 52, 34, 'Doğru sözlüler iseler bunun benzeri bir hadis getirsinler.'),
(4770, 52, 35, 'Onlar yokluktan mı yaratıldılar? Yoksa yaratanlar kendileri mi?'),
(4771, 52, 36, 'Gökleri ve yeri onlar mı yarattılar? Doğrusu, onlar kesin bir inanca sahip olmazlar.'),
(4772, 52, 37, 'Yoksa Rabbinin hazineleri onların mı yanındadır? Onlar mı kontrol etmektedirler?'),
(4773, 52, 38, 'Yoksa üzerine çıkıp (vahyi) dinledikleri bir merdivenleri mi var? Öyleyse dinleyicileri açık bir delil getirsin.'),
(4774, 52, 39, 'Yoksa kızlar O\'na, oğullar size mi?'),
(4775, 52, 40, 'Yoksa sen onlardan bir ücret istiyorsun da bu onlara ağır bir borç mu yüklüyor?'),
(4776, 52, 41, 'Yoksa gizemlerin bilgisine mi sahipler ve onu kendileri mi yazıyorlar?'),
(4777, 52, 42, 'Yoksa bir planı mı uygulamak istiyorlar? Halbuki kafirlerin kendileri bir plana mahkum edilmiştir.'),
(4778, 52, 43, 'Yoksa ALLAH\'tan başka tanrıları mı vardır? ALLAH onların ortak koştuklarından yücedir.'),
(4779, 52, 44, 'Gökten bir parçanın düştüğünü görseler, "Bulut kümesidir!" derler.'),
(4780, 52, 45, 'Çarpılacakları günlerine kavuşuncaya kadar onları bırak.'),
(4781, 52, 46, 'O gün, planları kendilerini hiç bir şeyden korumayacak ve yardım da görmeyeceklerdir.'),
(4782, 52, 47, 'Zulmedenlere bunun dışında bir ceza daha vardır, fakat çokları bilmezler.'),
(4783, 52, 48, 'Rabbinin hükmü gerçekleşinceye kadar sabret sen gözlerimiz önündesin ve kalktığın zaman Rabbini överek yücelt.'),
(4784, 52, 49, 'Geceleyin ve yıldızlar kaybolurken O\'nu yücelt.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'Düşerken yıldızlara andolsun.'),
(4786, 53, 2, 'Arkadaşınız (Muhammed) ne sapmıştır, ne de azmıştır.'),
(4787, 53, 3, 'Ne de kendi kişisel arzusundan konuşmaktadır.'),
(4788, 53, 4, 'O (Kuran) ancak ve ancak bildilen bir vahiydir.'),
(4789, 53, 5, 'Onu, büyük güce sahip olan öğretmiştir.'),
(4790, 53, 6, 'Üstün otoritenin sahibi göründü,'),
(4791, 53, 7, 'En yüksek ufukta.'),
(4792, 53, 8, 'Sonra inip yaklaştı.'),
(4793, 53, 9, 'Mesafe iki yay kadar veya daha yakın oldu.'),
(4794, 53, 10, 'Ve sonra kuluna ne bildirilecekse onu vahyetti.'),
(4795, 53, 11, 'Gördüğünü gönlü yalanlamadı.'),
(4796, 53, 12, 'Onun gördüğü hakkında kendisiyle tartışıyor musunuz?'),
(4797, 53, 13, 'Onu bir kez daha görmüştü.'),
(4798, 53, 14, 'En son noktada.'),
(4799, 53, 15, 'Ki yanında barınılacak cennet vardır.'),
(4800, 53, 16, 'Tüm bölge olağanüstü biçimde kuşatılmıştı.'),
(4801, 53, 17, 'Göz şaşmadı, sınırı da aşmadı.'),
(4802, 53, 18, 'Rabbinin büyük ayetlerini gördü.'),
(4803, 53, 19, 'Gördünüz mü (dişi putlarınız olan) Lat ve Uzza\'yı?'),
(4804, 53, 20, 'Ve üçüncüleri olan Menat\'ı?'),
(4805, 53, 21, 'Erkekler sizin de dişiler O\'nun mu?'),
(4806, 53, 22, 'Ne de insafsız bir paylaşma!'),
(4807, 53, 23, 'Onlar, sizin ve atalarınızın taktığı isimlerden başka bir şey değildir ve ALLAH onlar için hiç bir delil indirmemiştir. Kendilerine, Rab\'lerinde bir yol gösterici geldiği halde, sadece kuruntuları ve kişisel arzularını izliyorlar.'),
(4808, 53, 24, 'İnsan her umduğuna kavuşacak mıdır?'),
(4809, 53, 25, 'Son da ilk de ALLAH\'ındır.'),
(4810, 53, 26, 'Göklerdeki meleklerin bile şefaati bir yarar sağlamaz. Ancak ALLAH\'ın dilediği kimse için izin vermesinden ve O\'nun rızasına uygun olduktan sonra...'),
(4811, 53, 27, 'Ahirete inanmıyanlar melekleri dişi olarak nitelendirmektedirler.'),
(4812, 53, 28, 'Onların bu konuda bir bilgileri yoktur, sadece sanıları izlemektedirler ve sanılar gerçeğin yerini tutmaz.'),
(4813, 53, 29, 'Mesajımızdan yüz çeviren ve dünya hayatından başkasını arzulamıyan kimseyi önemseme.'),
(4814, 53, 30, 'İşte onların ulaştıkları bilgilerinin düzeyi budur. Elbette, Rabbin yolundan sapanı iyi bilir, doğru yolda olanı da iyi bilir.'),
(4815, 53, 31, 'Göklerde ve yerde ne varsa ALLAH\'ındır. Kötülük işleyenleri yaptıklarından dolayı cezalandıracaktır ve güzel davrananları da ödüllendirecektir.'),
(4816, 53, 32, 'Onlar, büyük günahlardan ve kötülüklerden kaçınırlar, yalnız küçük suçlar işlerler. Rabbinin bağışlaması engindir. Sizi topraktan yaratırken ve annelerinizin karınlarında cenin (fetus) durumundayken sizi iyi bilmektedir. Öyleyse kendinizi (övüp) temize çıkarmayın. O, erdemlileri iyi bilir.'),
(4817, 53, 33, 'Gördün mü o yüz çevireni?'),
(4818, 53, 34, 'Ender olarak ve pek az verdi.'),
(4819, 53, 35, 'Gizemlerin bilgisine mi sahiptir ki (geleceği) görüyor?'),
(4820, 53, 36, 'Ona Musa\'nın öğretisi hakkında bilgi verilmedi mi?'),
(4821, 53, 37, 'Ve sözünü gerçekleştiren İbrahim\'in?:'),
(4822, 53, 38, 'Ki hiçbir kimse bir başkasının günah yükünü taşımaz,'),
(4823, 53, 39, 'İnsan için ancak kendi çalışması vardır.'),
(4824, 53, 40, 'Ve onun çalışması da yakında görülecektir.'),
(4825, 53, 41, 'Sonra kendisine ödülü tam olarak verilecektir.'),
(4826, 53, 42, 'Son varış Rabbinedir.'),
(4827, 53, 43, 'O\'dur seni güldüren ve ağlatan.'),
(4828, 53, 44, 'O\'dur öldüren ve dirilten.'),
(4829, 53, 45, 'O\'dur iki çifti yaratan; erkeği ve dişiyi...'),
(4830, 53, 46, 'Atıldığı zaman bir damlacık meniden.'),
(4831, 53, 47, 'O\'na aittir tekrar yaratmak.'),
(4832, 53, 48, 'O\'dur Zengin eden, yoksul yapan.'),
(4833, 53, 49, 'O\'dur Şira galaksisinin Rabbi.'),
(4834, 53, 50, 'O\'dur helak eden eski Ad halkını,'),
(4835, 53, 51, 'Semudu da bırakmadı,'),
(4836, 53, 52, 'Ve ondan önce Nuh\'un halkını da; onlar çok zalim ve çok azgın kimselerdi.'),
(4837, 53, 53, 'Yıkılıp batırılmış olanlar (Sodom ve Gomora), en bayağılardı.'),
(4838, 53, 54, 'Onları örten örttü.'),
(4839, 53, 55, 'O halde Rabbinin hangi nimetlerinden kuşkulanırsın?'),
(4840, 53, 56, 'Bu, eski uyarıcılardan bir uyarıcıdır.'),
(4841, 53, 57, 'Kaçınılmaz olan yaklaşmıştır.'),
(4842, 53, 58, 'ALLAH\'tan başkası onu kaldıramaz.'),
(4843, 53, 59, 'Bu sözü mü tuhaf karşılıyorsunuz?'),
(4844, 53, 60, 'Gülüyorsunuz da ağlamıyorsunuz?'),
(4845, 53, 61, 'Ve aylak aylak oyalanıyorsunuz?'),
(4846, 53, 62, 'Haydi ALLAH\'a secde edin, kulluk edin.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'Saat (dünyanın sonu) yaklaştı ve ay yarıldı.'),
(4848, 54, 2, 'Bir mucize görseler yüz çevirirler ve, "Süregelen bir büyüdür" derler.'),
(4849, 54, 3, 'Yalanladılar; arzularına ve tümüyle statükoya uydular.'),
(4850, 54, 4, 'Oysa, kötülüklerini engelleyecek uyarılar dolu haberler kendilerine gelmiş bulunuyor.'),
(4851, 54, 5, 'Bu üstün bir hikmettir; ancak uyarılar yarar sağlamıyor.'),
(4852, 54, 6, 'Onlara aldırma; çağırıcının, görülmemiş ve tanınmamış bir şeye çağıracağı gün,'),
(4853, 54, 7, 'Gözleri zillet içinde mezarlardan çıkarlar; tıpkı saçılmış çekirgeler gibi...'),
(4854, 54, 8, 'Çağırıcıya doğru koşarlarken, inkarcılar, "Bu zorlu bir gündür," derler.'),
(4855, 54, 9, 'Onlardan önce de Nuh\'un halkı yalanlamıştı. Kulumuzu yalanlayıp, "Delidir" dediler. Nitekim o engellendi.'),
(4856, 54, 10, 'Rabbini çağırdı, "Ben yenildim; bana yardım et."'),
(4857, 54, 11, 'Bunun üzerine göğün kapılarını boşanan sularla açtık.'),
(4858, 54, 12, 'Yerden de pınarlar fışkırttık. Nihayet sular, daha önce belirlenmiş seviyeye ulaştılar.'),
(4859, 54, 13, 'Onu ağaç lifleri ile (bağlanmış) kütükler üzerinde taşıdık.'),
(4860, 54, 14, 'Reddedilmiş olan kişiye bir ödül olarak gözetimimiz altında akıp gidiyordu.'),
(4861, 54, 15, 'Bunu bir ders olarak bıraktık. Öğüt alan yok mudur?'),
(4862, 54, 16, 'Cezalandırmam ve uyarılarım nasılmış!'),
(4863, 54, 17, 'Kuran\'ı mesaj için kolaylaştırdık; öğüt alan yok mudur?'),
(4864, 54, 18, 'Ad da yalanladı. Cezalandırmam ve uyarılarım nasılmış!'),
(4865, 54, 19, 'Uğursuzluk üstüne uğursuzluğa sahip bir günde üzerlerine vahşi bir rüzgar gönderdik.'),
(4866, 54, 20, 'İnsanları, sanki köklerinden koparılmış hurma kötükleriymiş gibi yıkıyordu.'),
(4867, 54, 21, 'Cezalandırmam ve uyarılarım nasılmış!'),
(4868, 54, 22, 'Kuran\'ı mesaj için kolaylaştırdık; öğüt alan yok mudur?'),
(4869, 54, 23, 'Semud da uyarıları yalanladı.'),
(4870, 54, 24, 'Dediler ki, "Bizden bir insana mı uyalım? O zaman biz sapar ve cehenneme gireriz."'),
(4871, 54, 25, '"Mesaj aramızdan ona mı verildi? O, yalancı küstahın biridir."'),
(4872, 54, 26, 'Yalancı küstahın kim olduğunu yarın öğreneceklerdir.'),
(4873, 54, 27, 'Deveyi bir sınav olarak göndereceğiz. Onları gözetle, sabırlı ol.'),
(4874, 54, 28, 'Onlara, suyun (deveyle) aralarında paylaşılacağını bildir. Her içim sırayla sunulacaktır.'),
(4875, 54, 29, 'Bir arkadaşlarını çağırdılar, o da çekip (deveyi) kesti.'),
(4876, 54, 30, 'Cezalandırmam ve uyarılarım nasılmış!'),
(4877, 54, 31, 'Üzerlerine bir tek patlama gönderdik ve onlar ağılcının topladığı saman yığınına döndüler.'),
(4878, 54, 32, 'Kuran\'ı mesaj için kolaylaştırdık; öğüt alan yok mudur?'),
(4879, 54, 33, 'Lut halkı da uyarıları yalanlamıştı.'),
(4880, 54, 34, 'Üzerlerine taş yağdıran bir fırtına gönderdik, yalnız Lut\'un ailesini seher vakti kurtardık.'),
(4881, 54, 35, 'Katımızdan bir iyilik olarak. Şükredeni işte böyle ödüllendiririz.'),
(4882, 54, 36, 'Onları bu yakalayışımıza karşı uyarmıştı; ancak onlar uyarıları kuşkuyla karşıladılar.'),
(4883, 54, 37, 'Onun konuklarına göz diktiler, biz de onları kör ettik. Azabımı ve uyarılarımı tadın bakalım.'),
(4884, 54, 38, 'Ertesi gün, yaman bir azap sabahlarını kutladı.'),
(4885, 54, 39, 'Azabımı ve uyarılarımı tadın bakalım.'),
(4886, 54, 40, 'Kuran\'ı mesaj için kolaylaştırdık; öğüt alan yok mudur?'),
(4887, 54, 41, 'Firavun\'un erkanına da uyarıcılar gitmişti.'),
(4888, 54, 42, 'Tüm mucizelerimizi yalanladılar ve biz de onları En üstün ve her şeye gücü yetenin yakalayışı gibi yakaladık.'),
(4889, 54, 43, 'Sizin inkarcılarınız onlarınkinden daha mı iyi? Yoksa kitaplarda kendiniz için bir af ilanına mı rastladınız?'),
(4890, 54, 44, 'Yoksa, "Biz, zafere ulaşacak bir cemaatiz" mi diyorlar?'),
(4891, 54, 45, 'O cemaat bozguna uğratılacak; dönüp kaçacaklar.'),
(4892, 54, 46, 'Saat onları beklemektedir, daha korkunç ve acıdır.'),
(4893, 54, 47, 'Suçlular bir sapıklık ve cehennem içindedir.'),
(4894, 54, 48, 'Yüzükoyun ateşe sürüklenecekleri gün: "Cehennemin dokunuşunu tadın."'),
(4895, 54, 49, 'Biz her şeyi belli bir ölçüyle yaratmışızdır.'),
(4896, 54, 50, 'Buyruğumuz göz kırpması gibi anidir.'),
(4897, 54, 51, 'Sizin benzerlerinizi yok etmiştik. Yok mu öğüt alan?'),
(4898, 54, 52, 'Tüm yaptıkları kitaplarda kayıtlıdır.'),
(4899, 54, 53, 'Küçük ve büyük hepsi yazılmıştır.'),
(4900, 54, 54, 'Erdemliler, cennetler (bahçeler) ve ırmaklar içindedir.'),
(4901, 54, 55, 'Güçlü Kralın yanında onurlu makamlardadırlar.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'Rahman,'),
(4903, 55, 2, 'Kuran\'ı öğretti.'),
(4904, 55, 3, 'İnsanı yarattı.'),
(4905, 55, 4, 'Ona beyanı (açıklama yeteneğini) öğretti.'),
(4906, 55, 5, 'Güneş ve ay bir hesap ile (hareket etmekte) dir.'),
(4907, 55, 6, 'Yıldızlar ve ağaçlar secde etmektedirler.'),
(4908, 55, 7, 'Göğü yükseltti ve ölçüyü/dengeyi koydu.'),
(4909, 55, 8, 'Ki ölçüyü aşmayasınız.'),
(4910, 55, 9, 'Ölçüyü adaletle gözetiniz; ölçüyü kaybetmeyiniz.'),
(4911, 55, 10, 'Yeryüzünü tüm yaratıklar için yarattı.'),
(4912, 55, 11, 'Orada meyvalar, salkımlı hurma ağaçları vardır.'),
(4913, 55, 12, 'Kabuklu taneler ve baharatlar.'),
(4914, 55, 13, '(Ey insanlar ve cinler), Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4915, 55, 14, 'İnsanı, çömlek gibi kuru bir çamurdan yarattı.'),
(4916, 55, 15, 'Cinleri de dumansız ateşten yarattı.'),
(4917, 55, 16, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4918, 55, 17, 'İki doğunun ve iki batının Rabbi.'),
(4919, 55, 18, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4920, 55, 19, 'İki denizi salmıştır; birbirlerine kavuşuyorlar.'),
(4921, 55, 20, 'Aralarında bir engel vardır; birbirinin sınırını aşıp karışmazlar.'),
(4922, 55, 21, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4923, 55, 22, 'İkisinden de inci ve mercan çıkar.'),
(4924, 55, 23, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4925, 55, 24, 'Denizde dağlar gibi akıp giden gemiler O\'nundur.'),
(4926, 55, 25, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4927, 55, 26, 'Üzerinde bulunan herkes ölümlüdür.'),
(4928, 55, 27, 'Sadece Görkemli ve Onurlu Rabbinin varlığı kalıcıdır.'),
(4929, 55, 28, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4930, 55, 29, 'Göklerde ve yerde olan herkes O\'na yalvarır. O her gün kesintisiz kontrol etmektedir.'),
(4931, 55, 30, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4932, 55, 31, 'Ey sorumlu iki topluluk, sizi elbette sorguya çekeceğiz.'),
(4933, 55, 32, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4934, 55, 33, 'Ey insanlar ve cinler topluluğu, göklerin ve yerin sınırlarını aşmaya gücünüz yetiyorsa, aşıp geçin. Bir yetkiye sahip olmadan geçemezsiniz.'),
(4935, 55, 34, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4936, 55, 35, 'Üzerinize ateşten toplar ve ışınlar gönderilir; yardım görmezsiniz.'),
(4937, 55, 36, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4938, 55, 37, 'Gök parçalanıp da, yağ gibi eridiği ve kırmızı bir güle dönüştüğü zaman...'),
(4939, 55, 38, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4940, 55, 39, 'O gün ne insana ne de cine günahından sorulmaz.'),
(4941, 55, 40, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4942, 55, 41, 'Suçlular yüzlerinden tanınır ve yaka paça götürülürler.'),
(4943, 55, 42, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4944, 55, 43, 'İşte bu, suçluların yalanlayıp durduğu cehennemdir.'),
(4945, 55, 44, 'Onunla kaynar su arasında dönüp dolaşırlar.'),
(4946, 55, 45, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4947, 55, 46, 'Rabbinin görkeminden korkan (cin ve insan) lar için iki cennet vardır.'),
(4948, 55, 47, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4949, 55, 48, 'Türlü besinlerle doludur her ikisi.'),
(4950, 55, 49, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4951, 55, 50, 'İçlerinde akan pınarlar vardır.'),
(4952, 55, 51, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4953, 55, 52, 'İkisinde de her meyveden iki çeşit vardır.'),
(4954, 55, 53, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4955, 55, 54, 'Astarları atlastan yataklara yaslanırlar, her iki cennetin meyveleri pek yakındır.'),
(4956, 55, 55, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4957, 55, 56, 'Oralarda, daha önce ne bir insan ne de bir cin tarafından dokunulmamış, bakışlarını dikmiş eşler vardır.'),
(4958, 55, 57, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4959, 55, 58, 'Onlar yakut ve mercan gibidirler.'),
(4960, 55, 59, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4961, 55, 60, 'İyiliğin karşılığı, yalnız iyilik değil midir?'),
(4962, 55, 61, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4963, 55, 62, 'O ikisinin ötesinde iki cennet daha var.'),
(4964, 55, 63, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4965, 55, 64, 'Yemyeşildirler.'),
(4966, 55, 65, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4967, 55, 66, 'İkisinde de fışkıran iki kaynak vardır.'),
(4968, 55, 67, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4969, 55, 68, 'Her ikisinde de meyvalar, hurmalar ve narlar vardır.'),
(4970, 55, 69, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4971, 55, 70, 'Her ikisinde de iyilikler, güzellikler vardır.'),
(4972, 55, 71, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4973, 55, 72, 'Çadırlara kapanmış güzeller'),
(4974, 55, 73, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4975, 55, 74, 'Daha önce onlara ne insan ne de cin dokunmamıştı.'),
(4976, 55, 75, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4977, 55, 76, 'Yeşil yastıklara ve işlemeli halılara yaslanırlar.'),
(4978, 55, 77, 'Rabbinizin hangi nimetlerini yalanlayabilirsiniz?'),
(4979, 55, 78, 'Görkemli ve Onurlu Rabbinin ismi ne yücedir!');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'Kaçınılmaz olay gerçekleştiği zaman,'),
(4981, 56, 2, 'Onun gerçekleşmesini artık yalanlayan çıkmaz.'),
(4982, 56, 3, 'O alçaltıcıdır, yücelticidir.'),
(4983, 56, 4, 'Yerin sallanıp sarsılacağı,'),
(4984, 56, 5, 'Ve dağların paramparça edileceği zaman,'),
(4985, 56, 6, 'Artık o toz duman haline gelmiştir.'),
(4986, 56, 7, 'Sizler de üç bölüme ayrılırsınız.'),
(4987, 56, 8, 'Mutlular ne kadar da mutludurlar!'),
(4988, 56, 9, 'Mutsuzlar ne kadar da mutsuzdurlar!'),
(4989, 56, 10, 'Bir de ileri geçen öncü elitler vardır.'),
(4990, 56, 11, 'Onlar (Tanrı\'ya) yaklaştırılanlardır.'),
(4991, 56, 12, 'Nimet cennetlerinde (bahçelerinde)...'),
(4992, 56, 13, 'Onların büyük bir kısmı önceki nesillerden,'),
(4993, 56, 14, 'Küçük bir kısmı da sonraki nesillerdendir.'),
(4994, 56, 15, 'Lüks mobilyalar üzerinde,'),
(4995, 56, 16, 'Karşılıklı yaslanmışlardır.'),
(4996, 56, 17, 'Onlara ölümsüz gençler servis yaparlar.'),
(4997, 56, 18, 'Kaynaktan doldurulmuş bardaklar, sürahiler ve kadehlerle.'),
(4998, 56, 19, 'Ne ara verirler ne de yorulurlar.'),
(4999, 56, 20, 'Ve beğendikleri meyveler...'),
(5000, 56, 21, 'Canlarının çektiği kuş etleri...'),
(5001, 56, 22, 'Güzel eşler...'),
(5002, 56, 23, 'Korunmuş inciler gibi...'),
(5003, 56, 24, 'Yapmış olduklarına bir karşılık olarak verilir.'),
(5004, 56, 25, 'Orada ne bir saçmalık, ne de günaha sokan bir söz işitmezler.'),
(5005, 56, 26, 'Sadece, "Selam, selam," derler.'),
(5006, 56, 27, 'Sağ tarafta olanlar sağ tarafta olacaklar!'),
(5007, 56, 28, 'Dikensiz meyve ağaçları,'),
(5008, 56, 29, 'Salkımları sarkmış muz ağaçları,'),
(5009, 56, 30, 'Uzamış gölgeler,'),
(5010, 56, 31, 'Fışkıran sular,'),
(5011, 56, 32, 'Ve bol meyveler içindedirler.'),
(5012, 56, 33, 'Bunlar ne tükenirler, ne de yasak edilirler!'),
(5013, 56, 34, 'Ve onlar yükseltilmiş mobilyalar üzerindedirler.'),
(5014, 56, 35, 'Biz kadınları yeniden biçimlendirdik.'),
(5015, 56, 36, 'Onları, gençleştirdik.'),
(5016, 56, 37, 'Mükemmel biçimde eşlenmişlerdir.'),
(5017, 56, 38, 'Sağ tarafta olanlar içindir.'),
(5018, 56, 39, 'Onların bir çoğu önceki nesillerdendir.'),
(5019, 56, 40, 'Onların bir çoğu da sonraki nesillerdendir.'),
(5020, 56, 41, 'Sol tarafta bulunanlar, sol tarafta olacaklardır.'),
(5021, 56, 42, 'İşleyen ve kaynayan bir azap içindedirler.'),
(5022, 56, 43, 'Sıcak gölgeler altındadırlar.'),
(5023, 56, 44, 'Ne soğuktur, ne de yararlı.'),
(5024, 56, 45, 'Bundan önce onlar konfor içinde şımarmışlardı.'),
(5025, 56, 46, 'Büyük günahı işlemekte direniyorlardı.'),
(5026, 56, 47, 'Diyorlardı ki, "Biz öldükten, toz ve kemiğe dönüştükten sonra mı diriltileceğiz?"'),
(5027, 56, 48, '"Önceki atalarımız da mı?"'),
(5028, 56, 49, 'De ki, "Öncekiler de, sonrakiler de."'),
(5029, 56, 50, '"Bilinen günün buluşma anı için toplanacaklardır."'),
(5030, 56, 51, '"Sonra da siz, ey sapıtmışlar, ey yalanlayıcılar,"'),
(5031, 56, 52, '"Zakkum ağacından yiyeceksiniz."'),
(5032, 56, 53, '"Onunla karnınızı dolduracaksınız."'),
(5033, 56, 54, '"Ve üzerine kaynar su içeceksiniz."'),
(5034, 56, 55, '"Susamış devenin içişi gibi içeceksiniz."'),
(5035, 56, 56, 'Yargı gününde işte böyle ağırlanacaklardır.'),
(5036, 56, 57, 'Sizi biz yarattık, doğrulamanız gerekmez miydi?'),
(5037, 56, 58, 'Attığınız meniye dikkat ettiniz mi?'),
(5038, 56, 59, 'Siz mi onu yaratıyorsunuz, yoksa biz mi yaratıyoruz?'),
(5039, 56, 60, 'Aranızda ölümünüzü önceden biz belirledik. Kimse bizi engelleyemez:'),
(5040, 56, 61, 'Sizi başka nesillerle değiştirmekten, yahut sizi bilmediğiniz bir biçimde yaratmaktan....'),
(5041, 56, 62, 'İlk yaratılışı biliyorsunuz. Öğüt almalı değil misiniz?'),
(5042, 56, 63, 'Ektiğinize dikkat ettiniz mi?'),
(5043, 56, 64, 'Siz mi onu yetiştiriyorsunuz, yoksa biz mi?'),
(5044, 56, 65, 'Dileseydik onu samana çevirirdik de siz şaşardınız:'),
(5045, 56, 66, '"Borca girdik."'),
(5046, 56, 67, '"Doğrusu, yoksun bırakıldık."'),
(5047, 56, 68, 'İçmekte olduğunuz suya dikkat ettiniz mi?'),
(5048, 56, 69, 'Onu bulutlardan siz mi indiriyorsunuz, yoksa biz mi?'),
(5049, 56, 70, 'Dileseydik onu tuzlu yapardık. Şükretmez misiniz?'),
(5050, 56, 71, 'Yakmakta olduğunuz ateşe dikkat ettiniz mi?'),
(5051, 56, 72, 'Onun ağacını siz mi başlattınız, yoksa biz mi başlatmaktayız?'),
(5052, 56, 73, 'Kullananlar için biz onu bir ibret ve yararlı yaptık.'),
(5053, 56, 74, 'Öyleyse Büyük Rabbinin ismini yücelt.'),
(5054, 56, 75, 'Yıldızların yerlerine yemin ederim.'),
(5055, 56, 76, 'Onun ne büyük bir yemin olduğunu bir bilseniz!'),
(5056, 56, 77, 'Bu, onurlu bir Kuran\'dır.'),
(5057, 56, 78, 'Gizli bir kitaptadır.'),
(5058, 56, 79, 'Onu ancak temizler kavrayabilir.'),
(5059, 56, 80, 'Evrenlerin Rabbinden indirilmiştir.'),
(5060, 56, 81, 'Siz bu sözü mü küçümsüyorsunuz?'),
(5061, 56, 82, 'İnkar etmeyi iş mi ediniyorsunuz?'),
(5062, 56, 83, 'Ya can boğaza dayandığı zaman?'),
(5063, 56, 84, 'O anda siz bakınmaktasınız.'),
(5064, 56, 85, 'Biz ona (can çekişene) sizden daha yakınız; ancak siz göremezsiniz.'),
(5065, 56, 86, 'Yaptığınızın karşılığını görmeyeceğiniz doğruysa,'),
(5066, 56, 87, 'Onu geri çevirsenize, eğer doğru sözlü iseniz?'),
(5067, 56, 88, 'Ancak o, (bana) yaklaştırılanlardan ise-'),
(5068, 56, 89, 'o zaman neşe, çiçekler ve nimet cennetleri...'),
(5069, 56, 90, 'O, sağda olanlardan ise,'),
(5070, 56, 91, '"Sana sağdakilerden selam olsun!"'),
(5071, 56, 92, 'Ama o yalanlayan sapıklardan ise-'),
(5072, 56, 93, 'kaynar sudan bir ağırlanma-'),
(5073, 56, 94, 've cehennemde yanma...'),
(5074, 56, 95, 'Mutlak gerçek budur.'),
(5075, 56, 96, 'Öyleyse Büyük Rabbinin ismini yücelt');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'Göklerde ve yerde ne varsa ALLAH\'ı yüceltir. O Üstündür, Bilgedir.'),
(5077, 57, 2, 'Göklerin ve yerin yönetimi O\'na aittir. Yaşatır, öldürür. O her şeye gücü yetendir.'),
(5078, 57, 3, 'O ilktir, sondur. O en dıştakidir, en içtekidir. O her şeyi bilir.'),
(5079, 57, 4, 'O\'dur gökleri ve yeri altı günde yaratan ve daha sonra tüm otoriteyi kuran. Yere giren ve ondan çıkanı, gökten inen ve ona yükselen her şeyi bilir. Nerede olursanız olun O sizinle beraberdir. ALLAH tüm yaptıklarınızı görendir.'),
(5080, 57, 5, 'Göklerin ve yerin yönetimi O\'na aittir. Her şey ALLAH \'a havale edilir.'),
(5081, 57, 6, 'Geceyi gündüze sokar, gündüzü de geceye sokar. O, göğüslerin içindekini bilir.'),
(5082, 57, 7, 'ALLAH\'a ve elçisine inanın ve yönetiminize verdiği şeylerden yardım için verin. Sizden inanıp yardım edenler için büyük bir ödül vardır.'),
(5083, 57, 8, 'Rabbinize inanmanız için elçi sizi çağırdığı halde size ne oluyor ki hâlâ ALLAH\'a inanmıyorsunuz? Hem O, sizden söz almıştı, eğer inanıyor iseniz.'),
(5084, 57, 9, 'Sizi karanlıklardan aydınlığa çıkarmak için kuluna apaçık ayetleri indiren O\'dur. Kuşkusuz ALLAH size karşı Şefkatlidir, Rahimdir.'),
(5085, 57, 10, 'Size ne oluyor ki ALLAH yolunda harcamıyorsunuz? Oysa göklerin ve yerin zenginlikleri ALLAH\'ındır. Zaferden önce harcayıp savaşanlarınız, daha sonra harcayıp savaşanlarla bir olmazlar; onlardan daha üstündürler. ALLAH her birine kurtuluşu söz vermiştir. ALLAH tüm yaptıklarınızdan haberdardır.'),
(5086, 57, 11, 'Kim (erdemli davranarak) ALLAH\'a güzel bir borç verirse, karşılığını kat kat alır ve cömert bir ödül kazanır.'),
(5087, 57, 12, 'İnanan erkeklerin ve kadınların ışıklarının önlerinden ve sağ yanlarından yayıldığını gördüğün gün, "Müjde, bugün, sürekli olarak kalacağınız altlarından ırmaklar akan cennetler var. Bu, en büyük zaferdir.'),
(5088, 57, 13, 'O gün, ikiyüzlü erkekler ve kadınlar inananlara, "Bize bakın da sizin ışığınızdan alalım," diyecekler. Onlara, "Geriye dönün de ışık arayın," denir. Aralarına, iç taraftaki merhametle, dış taraftaki azabı ayıran kapılı bir engel konacaktır.'),
(5089, 57, 14, '"Biz sizinle birlikte değil miydik?" diye onlara seslenirler. "Evet," derler, "Ancak siz kendinizi kandırdınız, beklediniz, kuşkular beslediniz ve ALLAH\'ın kararı gelinceye kadar kuruntularla oyalandınız. Kandırıcı, sizi ALLAH hakkında yanılttı.'),
(5090, 57, 15, 'Bugün ne sizden ne de inkar edenlerden bir fidye kabul edilmez. Yeriniz cehennemdir; mevlanız odur. Ne kötü bir duraktır.'),
(5091, 57, 16, 'İnananlar için ALLAH\'ın mesajına ve inen gerçeğe gönüllerini açmanın vakti gelmedi mi? Daha önce kendilerine kitap verilmiş olanlar gibi olmasınlar; onların üzerinden uzun zaman geçince kalpleri katılaşmıştı. Onların çoğunluğu yoldan çıkmış kimselerdir.'),
(5092, 57, 17, 'Biliniz ki ALLAH ölümünden sonra yeri diriltir. Düşünesiniz diye size ayetleri böyle açıklıyoruz.'),
(5093, 57, 18, 'Sadaka veren erkekler ve kadınlar, ALLAH\'a güzel bir borç vermişlerdir. Onlara kat kat ödenir ve onlar cömert bir ödül hakketmişlerdir.'),
(5094, 57, 19, 'ALLAH\'a ve elçisine inananlar, dosdoğru olanlar ve şehidlerdir. Rab\'leri katında onlar için ödülleri ve ışıkları vardır. İnkar edip ayet ve mucizelerimizi yalanlayanlar ise cehennem halkıdır.'),
(5095, 57, 20, 'Bilesiniz ki dünya hayatı bir oyun, eğlence, süs, aranızda övünme, para ve çocuk çoğaltma yarışından ibarettir. Bu, inkarcıların hoşuna giden bir bitkiyi yetiştiren bir yağmura benzer. Ne var ki daha sonra o bitki kurur, sararır ve sonunda çerçöp olur. Ahirette ise ALLAH\'tan çetin bir azap, bir bağışlanma ve hoşnutluk vardır. Dünya hayatı, kandıran, geçici bir zevkten ibarettir.'),
(5096, 57, 21, 'Rabbinizden bir bağışlanmaya ve genişliği gökler ve yer kadar olan bir cennete koşun. ALLAH\'a ve elçisine inananlar için hazırlanmıştır. Bu, ALLAH\'ın dilediğine ve/veya dileyene verdiği lütfudur. ALLAH Büyük Lütuf sahibidir.'),
(5097, 57, 22, 'Yeryüzünde ve aranızda oluşan hiç bir şey yoktur ki biz onu yaratmadan önce bir kitapta kayıtlı olmasın. Bu, kuşkusuz ALLAH için çok kolaydır.'),
(5098, 57, 23, 'Bu, yitirdiğiniz bir şey için üzülmemeniz ve O\'nun size verdiği nimetlerle de şımarmamanız içindir. ALLAH kendini beğenip övünenleri sevmez.'),
(5099, 57, 24, 'Onlar ki cimrilik ederler ve halka cimri olmayı öğütlerler. Kim yüz çevirirse, kuşkusuz ALLAH Zengindir, Övgüye layık olandır.'),
(5100, 57, 25, 'Elçilerimizi apaçık kanıtlarla gönderdik, onlarla birlikte kitabı ve yasayı indirdik ki halk adaleti gözetsin. Büyük bir kuvvete ve halk için yararlara sahip olan demiri de indirdik ki ALLAH kendisini ve elçisini inançla destekleyenleri ayırsın. ALLAH Güçlüdür, Üstündür.'),
(5101, 57, 26, 'Biz Nuh\'u, İbrahim\'i gönderdik, soylarına peygamberlik ve kitap bağışladık. Bir kısmı doğruyu buldu; ama onlardan çoğu yoldan çıkmışlardı.'),
(5102, 57, 27, 'Sonra onların peşinden, ard arda elçilerimizi gönderdik. Meryem oğlu İsa\'yı da gönderdik ve ona İncil\'i verdik. Onu izleyenlerin gönüllerine şefkat ve merhamet koyduk. Fakat, bizim kendileri için onaylamadığımız bir ruhbanlık uydurdular. Halbuki onlardan sadece ALLAH\'ı hoşnut edecek hususlara uymalarını istemiştik. Üstelik ruhbanlığa hakkıyla da uymadılar. Aralarından inananlara ödüllerini verdik; ancak çokları yoldan çıkmışlardı.'),
(5103, 57, 28, 'Ey inananlar, ALLAH\'ı sayıp dinleyin ve elçisine inanın ki rahmetinden size iki pay versin, size, aydınlığında yürüyeceğiniz bir ışık bağışlasın ve sizi bağışlasın. ALLAH Bağışlayandır, Rahimdir.'),
(5104, 57, 29, 'Böylece kitap halkı bilsin ki, onlar ALLAH\'ın lütfunu tekelleştiremezler, tüm lütuf ALLAH\'ın elindedir, onu dilediğine ve/veya dileyene verir. ALLAH Büyük Lütuf Sahibidir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'ALLAH, kocası hakkında seninle tartışan ve ALLAH\'a şikayette bulunan kadının sözlerini işitti. ALLAH ikinizin tüm konuştuklarını işitmiştir. ALLAH İşitendir, Bilendir.'),
(5106, 58, 2, 'İçinizden, kadınlarını annelerine benzeterek yabancılaştıranlar bilirler ki onlar anaları değildir. Anneleri kendilerini doğuranlardır. Söyledikleri kötü ve asılsız bir sözdür. ALLAH Affedendir, Bağışlayandır.'),
(5107, 58, 3, 'Kadınlarını annelerine benzeterek yabancılaştırdıktan sonra sözlerinden dönenler, karılarıyla cinsel ilişkiye girmeden önce bir köleyi özgürlüğe kavuştursunlar. Size öğütlenen budur. ALLAH yaptığınız her şeyi haber alır.'),
(5108, 58, 4, 'Azad edecek bir köle bulamıyan, cinsel ilişkiden önce üst üste iki ay boyunca oruç tutmalı. Buna güç yetiremiyen ise altmış yoksulu doyurmalı. ALLAH\'a ve elçisine inanmalısınız. Bunlar, ALLAH\'ın belirlediği sınırdır. İnkarcılar acı bir azaba mahkum olmuşlardır.'),
(5109, 58, 5, 'ALLAH\'a ve elçisine karşı gelenler, kendilerinden öncekiler nasıl alçaltıldıysa öylece alçaltılacaklardır. Biz, apaçık ayetler indirmiş bulunuyoruz. İnkarcılar için utandırıcı bir azap vardır.'),
(5110, 58, 6, 'Gün gelir ALLAH onları topluca diriltir ve sonra yapmış olduklarını kendilerine haber verir. Onlar, unuttukları halde ALLAH onları saymıştır. ALLAH her şeye tanıktır.'),
(5111, 58, 7, 'ALLAH\'ın göklerde ve yerde olan her şeyi bildiğini anlamaz mısın? Üç kişi gizli konuşsa, mutlaka O onların dördüncüsüdür. Beş kişi olsalar, mutlaka O onların altıncısıdır. İster bundan az veya çok ve nerede olursa olsunlar, O onlarla beraberdir. Sonra diriliş gününde onlara yaptıklarını haber verir. ALLAH her şeyi Bilendir.'),
(5112, 58, 8, 'Gizli toplantılar yapmaktan menedildikleri halde hâlâ menedildikleri işte israr edenler dikkatini çekmedi mi? Onlar günah işlemek, düşmanlıkta bulunmak ve elçiye itaatsizlik etmek amacıyla kulis yapıyorlar. Sana geldiklerinde, ALLAH\'ın seni selamlamadığı bir biçimde selamlıyorlar. Kendi aralarında, "Söylediklerimizden ötürü ALLAH bizi cezalandırsa ya!" derler. Cehennem onlara yeter. Orada yanarlar; ne kötü bir duraktır orası.'),
(5113, 58, 9, 'Ey inananlar, gizli görüştüğünüz vakit günah, düşmanlık ve elçiye isyan için konuşmayın; iyilik ve erdemlilik için konuşun. Huzuruna toplanacağınız ALLAH\'ı dinleyin.'),
(5114, 58, 10, 'Komplo toplantıları şeytandandır. Bu yolla inananları incitmek ister. Oysa ALLAH\'ın izni olmadan onlara hiç bir zarar veremez. İnananlar ALLAH\'a güvensinler.'),
(5115, 58, 11, 'Ey inananlar, toplantılarda size "Yer açınız" dendiğinde yer açınız ki ALLAH\'ta size yer açsın. Size, "Kalkınız" dendiğinde kalkınız ki ALLAH da içinizdeki inananları ve kendilerine bilgi verilenleri derecelerle yükseltsin. ALLAH tüm yaptıklarınızdan haberdardır.'),
(5116, 58, 12, 'Ey inananlar, elçiyle özel görüşme yapacağınız zaman, görüşmenizden önce (yoksullara) bir sadaka verin. Bu sizin için daha iyi ve daha temizdir. Veremiyorsanız, ALLAH Bağışlayandır, Rahimdir.'),
(5117, 58, 13, 'Özel görüşmenizden önce bir sadaka vermekten çekindiniz mi ki onu uygulamadınız? ALLAH tevbenizi kabul eder. Namazı gözetin, zekatı verin, ALLAH\'a ve elçisine uyun. ALLAH yaptıklarınızdan haberdardır.'),
(5118, 58, 14, 'ALLAH\'ın kendilerine kızgın olduğu bir topluluğu dost edinenlere dikkat etmedin mi? Onlar ne sizdendir ne de onlardandır. Onlar bile bile yalan yere yemin ederler!'),
(5119, 58, 15, 'ALLAH onlar için çetin bir ceza hazırlamıştır. Yaptıkları ne kadar da kötüdür.'),
(5120, 58, 16, 'Yeminlerini kalkan edinerek ALLAH\'ın yolundan saptırdılar. Onlar alçaltıcı bir azabı haketmişlerdir.'),
(5121, 58, 17, 'Ne paraları, ne de evlatları, onları ALLAH\'tan kurtaramıyacaktır. Onlar cehennem halkıdırlar ve orada ebedi kalıcıdırlar.'),
(5122, 58, 18, 'Gün gelecek, ALLAH hepsini diriltecek ve şimdi size yemin ettikleri gibi O\'na da yemin edeceklerdir. Kendilerinin (doğru) bir şey üzerinde olduklarını sanıyorlar! Doğrusu, onlar yalancılardır.'),
(5123, 58, 19, 'Şeytan onları sahiplenmiş ve onlara ALLAH\'ın mesajını unutturmuştur. Onlar şeytanın partisidir. Şeytanın partisi kesinlikle kaybedecektir.'),
(5124, 58, 20, 'ALLAH\'a ve elçisine karşı gelenler, alçaklardandır.'),
(5125, 58, 21, 'ALLAH, "Ben ve benim elçilerim kazanacaklardır," diye yazmıştır. ALLAH Güçlüdür, Üstündür.'),
(5126, 58, 22, 'ALLAH\'a, elçisine ve ahiret gününe inanmış bir topluluk göremezsin ki, ALLAH\'a ve elçisine karşı gelenleri dost edinsin. Hatta onlar, kendilerinin anaları, babaları, çocukları, oğulları, kardeşleri ve akrabaları bile olsa... Kalplerine inancı yazmış ve katından bir vahiyle desteklemiştir. Onları içlerinden ırmaklar akan, ebedi kalacakları cennetlere sokar. ALLAH onlardan hoşnut olmuştur, onlar da O\'ndan hoşnut olmuşlardır. Onlar ALLAH\'ın partisidir. Hiç kuşkusuz, ALLAH\'ın partisi kazanacaktır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'Göklerde ve yerde ne varsa ALLAH\'ı yüceltir. O Üstündür, Bilgedir.'),
(5128, 59, 2, 'O ki, kitap halkından inkarcı olanları ilk sürgün için ülkelerinden çıkarmıştır. Onların çıkacağını hiç beklemiyordunuz. Kalelerinin kendilerini ALLAH\'tan koruyacağını sandılar. Ancak ALLAH onlara ummadıkları bir yerden geldi ve kalplerine korku saldı. Böylece evlerini kendi elleriyle ve inananların elleriyle yıkıyorlar. Ey görüş sahipleri ibret alınız.'),
(5129, 59, 3, 'ALLAH onları ayrılmaya zorlamasaydı, bu dünyada cezalandıracaktı. Ahirette de onlara ateş azabı vardır.'),
(5130, 59, 4, 'Çünkü onlar ALLAH\'a ve elçisine karşı geldiler. Kim ALLAH\'a karşı gelse ALLAH\'ın cezalandırması çetindir.'),
(5131, 59, 5, 'Bir ağacı kesseniz de, yahut gövdesi üzerinde bıraksanız da bu ALLAH\'ın arzusuna göredir. O, yoldan çıkanları alçaltacaktır.'),
(5132, 59, 6, 'ALLAH\'ın onlardan alıp elçisine verdikleri için, siz at ve deve sürmediniz (savaşmadınız); ama ALLAH elçisini dilediğinin üstüne gönderir. ALLAH her şeye kadirdir.'),
(5133, 59, 7, 'ALLAH\'ın o ülkelerin halklarından elçisine ganimet bıraktığı şeyler ALLAH\'ın ve elçisinindir. Yani akrabalara, yetimlere, yoksullara ve yolda kalmışlara verilmelidir ki zenginlerinizin arasında tekelleşmesin. Elçinin size verdiğini alın; ancak onun size vermediğinden uzak durun. ALLAH\'ı dinleyin. ALLAH\'ın cezalandırması çetindir.'),
(5134, 59, 8, 'ALLAH\'ın lütuf ve rızasını aradıkları, ALLAH\'ı ve elçisini destekledikleri için yurtlarından ve mallarından edilmiş bulunan göçmenlerin fakirlerine (öncelikle vermelisiniz). Doğru olanlar bunlardır.'),
(5135, 59, 9, 'Onlardan önce yurt ve inanca sahip olanlar, kendilerine göç edenleri severler ve verdiklerinden ötürü içlerinde bir burukluk duymazlar. Hatta kendileri ihtiyaç içinde bile olsalar onları kendilerinden önde tutarlar. Doğrusu, nefislerinin bencilliğinden korunanlar kurtulanlardır.'),
(5136, 59, 10, 'Onlardan sonra gelenler, "Rabbimiz, bizi ve bizden önce inanmış olan kardeşlerimizi bağışla; kalbimizi inananlara karşı kin beslemekten koru. Rabbimiz, sen şefkatlisin, Rahimsin," derler.'),
(5137, 59, 11, 'İkiyüzlü davrananlara dikkat etmedin mi? Kitap halkından inkarcı olan arkadaşlarına, "Çıkartılırsanız biz de sizinle birlikte çıkacağız ve sizin aleyhinize hiç bir kimseye itaat etmiyeceğiz. Sizinle savaşılırsa, kesinlikle sizi destekleyeceğiz," demektedirler. ALLAH onların yalancı olduğuna tanıklık eder.'),
(5138, 59, 12, 'Doğrusu, onlar çıkarılsalar, onlarla birlikte çıkmazlar. Onlarla savaşılırsa, onları desteklemezler. Destekleseler bile arkalarını dönüp kaçarlar, sonra yardım görmezler.'),
(5139, 59, 13, 'Kalplerine, ALLAH\'tan daha çok siz korku salıyorsunuz. Zira onlar anlayışsız bir topluluktur.'),
(5140, 59, 14, 'Onlar, iyi korunmuş kentlerde veya duvarların ardında olmaları haricinde bir araya gelip sizinle savaşamazlar. Aralarındaki çekişmeleri çetindir. Onları birlik sanırsın, halbuki kalpleri darmadağınıktır. Zira onlar, akıllarını kullanmıyan bir topluluktur.'),
(5141, 59, 15, 'Kendilerinden kısa bir süre önce geçmiş olanlara benziyorlar. Onlar, işlediklerinin karşılığını tatmışlar ve acı bir azabı haketmişlerdi.'),
(5142, 59, 16, 'Onlar tıpkı şeytan gibidir: İnsana, "İnkar et" der. Sonra o inkar edince de, "Benim seninle bir ilişkim yok. Ben, evrenlerin Rabbi ALLAH\'tan korkarım," der.'),
(5143, 59, 17, 'Her ikisi de ebedi kalacakları ateşte son buldular. Zalimlerin cezası işte böyledir.'),
(5144, 59, 18, 'Ey inananlar, ALLAH\'ı sayıp dinleyin. Herkes yarın için ne hazırladığına baksın. ALLAH\'ı sayıp dinleyin. ALLAH yaptıklarınızdan haberdardır.'),
(5145, 59, 19, 'ALLAH\'ı unuttukları için O\'nun da kendilerini kendilerine unutturduğu kimseler gibi olmayın. Onlar yoldan çıkmışlardır.'),
(5146, 59, 20, 'Cennet halkıyla cehennem halkı bir olamaz; kazananlar ancak cennet halkıdır.'),
(5147, 59, 21, 'Bu Kuran\'ı bir dağın üzerine indirseydik, ALLAH\'a olan saygıdan ötürü onun titreyip paramparça olduğunu görecektin. Belki düşünürler diye biz insana böyle örnekler vermekteyiz.'),
(5148, 59, 22, 'O ALLAH\'tır ki O\'ndan başka tanrı yoktur. Tüm gizemleri ve açıktakileri Bilendir. O Rahmandır, Rahimdir.'),
(5149, 59, 23, 'O ALLAH\'tır ki O\'ndan başka tanrı yoktur. Egemendir, Kutsaldır, Barıştır, Güvenilendir, Koruyandır, Üstündür, Güçlüdür, Uludur. Onların ortak koştuklarından ALLAH çok yücedir.'),
(5150, 59, 24, 'O, Yaratan, Vareden ve Biçim veren ALLAH\'tır. Tüm güzel isimler O\'nundur. Göklerde ve yerde ne varsa O\'nu yüceltir. O Üstündür, Bilgedir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'Ey inananlar, benim düşmanımı ve sizin düşmanınızı dost edinmeyin. Size gelen gerçeği inkar etmiş ve Rabbiniz olan ALLAH\'a inandığınız için elçiyi ve sizi (ülkenizden) çıkarmış oldukları halde siz onlara sevgi gösteriyorsunuz. Rızamı kazanmak için yolumda bir cihada çıktıysanız, onları nasıl gizlice sevebilirsiniz? Oysa ben, gizlediğinizi de açığa vurduğunuzu da çok iyi bilirim. Sizden kim böyle davranırsa doğru yoldan sapmış olur.'),
(5152, 60, 2, 'Sizi ele geçirseler size düşman kesilirler ve elleriyle, dilleriyle sizi incitirler. İnkar etmenizi isterler.'),
(5153, 60, 3, 'Yakınlarınız ve çocuklarınız size yarar sağlamaz. Diriliş Günü sizi yargılar. ALLAH yaptıklarınızı Görendir.'),
(5154, 60, 4, 'Sizin için İbrahim ve onunla beraber olanlarda güzel bir örnek vardır. Onunla beraber olanlar, halklarına, "Biz, sizden ve sizin ALLAH\'ın dışında taptıklarınızdan uzağız. Sizi tanımıyoruz. YALNIZ ALLAH\'a inanıncaya kadar bizimle sizin aranızda ebedi olarak düşmanlık ve nefret belirmiştir. Ne var ki, İbrahim\'in babasına "Senin bağışlanman için dua edeceğim, fakat ALLAH\'tan gelecek hiçbir şeye karşı seni koruyamam." biçimindeki sözü (bir hata olarak) bunun dışındaydı. "Rabbimiz, sana güvendik, sana yöneldik ve sonunda sana döneceğiz."'),
(5155, 60, 5, '"Rabbimiz, inkarcılar için bizi bir sınanma aracı kılma, bizi bağışla. Rabbimiz sen Üstünsün, Bilgesin."'),
(5156, 60, 6, 'ALLAH\'ı ve Ahiret Gününü arzu edenleriniz için onlarda güzel bir örnek vardır. Yüz çevirenlere gelince, Zengin ve Övgüye layık olan sadece ALLAH\'tır'),
(5157, 60, 7, 'Belki de ALLAH sizinle onlar arasındaki düşmanlığı sevgiye çevirir. ALLAH Kadirdir. ALLAH Bağışlayandır, Rahimdir.'),
(5158, 60, 8, 'ALLAH, din uğrunda sizinle savaşmayan ve sizi yurdunuzdan çıkarmayan kimselere iyilik yapmanızı ve onlara adaletli davranmanızı yasaklamaz. ALLAH adaletli davrananları sever.'),
(5159, 60, 9, 'Ancak ALLAH, sadece, sizinle din uğrunda savaşan, sizi yurdunuzdan çıkaran ve sizi çıkarılmanıza yardım eden kimselerle dost olmanızı yasaklar. Onları dost edinenler zalimlerdir.'),
(5160, 60, 10, 'Ey inananlar, inanan göçmen kadınlar size sığındığında onları sorgulayın. ALLAH onların inançlarını çok iyi bilir. İnançlı olduklarını anlarsanız, onları kafirlere geri göndermeyin. Ne bunlar o inkarcılara helaldir, ne de onlar bunlara helaldir. İnkarcıların harcadığı mehirlerini onlara geri verin. Mehirlerini ödediğiniz taktirde bunlarla evlenmenizde bir sakınca yoktur. İnkarcı kadınları sorumluluğunuzda tutmayın. Onlara harcadığınız mehirlerinizi isteyebilirsiniz, onlar da verdikleri mehirlerini isteyebilirler. Bu, ALLAH\'ın hükmüdür. O, aranızda yargıda bulunur. ALLAH Bilendir, Bilgedir.'),
(5161, 60, 11, 'Kafirlere katılan eşleriniz yoluyla bir şeyler yitirdikten sonra (ganimet veya size katılanlar yoluyla bir şeyler kazanıp) üstün gelirseniz, eşlerini yitirmiş olanlara, onların harcamış oldukları mehir kadar verin. İnandığınız ALLAH\'ı sayıp dinleyin.'),
(5162, 60, 12, 'Ey peygamber, inanan kadınlar sana sığındıklarında, ALLAH\'a hiç bir şeyi ortak koşmamak, hırsızlık yapmamak, zina etmemek, çocuklarını öldürmemek, bir iftira uydurup getirmemek ve doğru işlerde sana karşı gelmemek üzere yemin edip söz verirlerse onların sözünü kabul et ve onlar için ALLAH\'tan bağışlanma dile. ALLAH Bağışlayandır, Rahimdir.'),
(5163, 60, 13, 'Ey inananlar, ALLAH\'ın kendilerine kızgın olduğu bir topluluğu dost edinmeyin. İnkarcılar, mezardakilerden nasıl umut kesmişlerse onlar da ahiretten öylesine umut kesmişlerdir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'Göklerde ve yerde olanlar ALLAH\'ı yüceltir. O, Üstündür, Bilgedir.'),
(5165, 61, 2, 'Ey inananlar, neden yapmadığınız veya yapmayacağınız şeyleri söylüyorsunuz?'),
(5166, 61, 3, 'Yapmadığınız şeyi söylemeniz, ALLAH katında en sevilmeyen davranışlardandır.'),
(5167, 61, 4, 'ALLAH kendi yolunda kenetlenmiş bir duvar gibi düzenli birlikler halinde savaşanları sever.'),
(5168, 61, 5, 'Hani Musa, halkına: "Ey halkım, benim, ALLAH\'ın bir elçisi olarak size geldiğimi bildiğiniz halde neden beni incitiyorsunuz?" demişti. Onlar eğrilince, ALLAH ta kalplerini eğriltti. ALLAH yoldan çıkmış toplulukları doğruya ulaştırmaz.'),
(5169, 61, 6, 'Hani İsa, halkına: "Ey İsrail oğulları, ben, size gönderilmiş ALLAH\'ın bir elçisiyim. Benden önceki Tevrat\'ı onaylayıcı ve benden sonra gelecek ve ismi daha çok övülen bir elçiyi müjdeliyorum." Sonra kendilerine apaçık delilleri gösterdiğinde, "Bu apaçık bir büyüdür," dediler.'),
(5170, 61, 7, 'İslam\'a çağrıldığı halde ALLAH hakkında yalan uydurandan daha zalim kim olabilir? ALLAH zalim topluluğu doğruya iletmez.'),
(5171, 61, 8, 'ALLAH\'ın ışığını ağızlarıyla söndürmek isterler. İnkarcılar hoşlanmasa da ALLAH ışığını tamamlayacaktır.'),
(5172, 61, 9, 'O, elçisini hidayet ve gerçek din ile gönderdi ki onu tüm dinlere üstün kılsın, ortak koşanlar hoşlanmasa da..'),
(5173, 61, 10, 'Ey inananlar, sizi acı azaptan kurtaracak bir ticareti size göstereyim mi?'),
(5174, 61, 11, 'ALLAH\'a ve elçisine inanırsınız, ALLAH\'ın yolunda canınız ve paranızla çaba gösterirsiniz. Bu sizin için en iyisidir, bir bilseniz.'),
(5175, 61, 12, 'Günahlarınızı bağışlar ve sizi içinden ırmaklar akan bahçelere ve Adn bahçelerindeki saraylara sokar. Büyük başarı budur.'),
(5176, 61, 13, 'Bundan başka sevdiğiniz bir şey daha: ALLAH\'tan bir destek ve yakın bir zafer... İnananları müjdele.'),
(5177, 61, 14, 'Ey inananlar, ALLAH\'ı destekleyenler olun. Nasıl ki Meryem oğlu İsa, öğrencilerine, "ALLAH yolunda kim benim destekçilerim olur?" diye sormuştu. Öğrencileri, "Biz ALLAH\'ın destekçileriyiz," demişlerdi. İsrail oğullarından bir grup inandı, bir grup da inkar etti. İnananları düşmanlarına karşı destekledik; onlar üstün geldiler.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'Göklerdekilerin ve yerdekilerin hepsi ALLAH\'ı yüceltir. O Kraldır, En kutsaldır, Üstündür, Bilgedir.'),
(5179, 62, 2, 'O ki, ümmilerin arasından, kendilerinden olan bir elçi göndermiştir ki onlara O\'nun ayetlerini okuyor, onları temizliyor ve onlara kitabı ve bilgeliği öğretiyor. Bundan önce onlar apaçık bir sapıklık içinde bulunuyorlardı.'),
(5180, 62, 3, 'Ve henüz kendilerine katılmamış bulunan başkalarına da... O Üstündür, Bilgedir.'),
(5181, 62, 4, 'Bu, ALLAH\'ın lütfudur; dileyene ve/veya dilediğine verir. ALLAH büyük lütuf sahibidir.'),
(5182, 62, 5, 'Kendilerine Tevrat verilip de onun gereğini yerine getirmeyenlerin örneği, kitaplar taşıyan eşeğin durumuna benzer. ALLAH\'ın ayetlerini yalanlayan topluluğun durumu ne kötüdür. ALLAH zalim toplumu doğruya ulaştırmaz.'),
(5183, 62, 6, 'De ki; "Ey Yahudiler, halkın arasından yalnız kendinizin ALLAH\'ın dostları olduğunuzu iddia ediyorsanız, ölümü dileyin; doğru iseniz."'),
(5184, 62, 7, 'Onlar, yapmış olduklarından ötürü hiç bir vakit onu dileyemiyeceklerdir. ALLAH zalimleri iyi bilir.'),
(5185, 62, 8, 'De ki; "Kaçmakta olduğunuz ölüm, mutlaka size ulaşacak ve daha sonra gizliyi ve açığı Bilenin huzuruna döndürüleceksiniz. Yaptığınız her şeyi size bildirecektir.'),
(5186, 62, 9, 'Ey inananlar, cuma (toplantı) günü namaza çağrıldığınızda ALLAH\'ı anmak için acele edin ve alışverişi bırakın. Bilseniz, bu sizin için daha iyidir.'),
(5187, 62, 10, 'Namaz bitince, yeryüzüne yayılarak ALLAH\'ın lütfunu arayın ve ALLAH\'ı sürekli anmaya devam edin.'),
(5188, 62, 11, 'Bazıları, bir ticaret veya bir eğlence gördükleri zaman, seni ayakta bırakıp oraya akın ettiler. De ki, "ALLAH\'ın yanında bulunanlar eğlenceden ve ticaretten daha iyidir. ALLAH en iyi rızık verendir."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'İkiyüzlüler sana geldiklerinde, "Senin ALLAH elçisi olduğuna şehadet ederiz," derler. ALLAH senin kendi elçisi olduğunu bilir ve ALLAH ikiyüzlülerin yalan söylediğine de şehadet eder.'),
(5190, 63, 2, 'Onlar, yeminlerini kalkan yapıp ALLAH\'ın yolundan saptırmaktadırlar. Yaptıkları ne kötüdür.'),
(5191, 63, 3, 'Çünkü onlar inandıktan sonra inkar ettiler; bu yüzden kalpleri mühürlendi; onlar anlamazlar.'),
(5192, 63, 4, 'Onları gördüğün zaman, görünüşleri hoşuna gider. Konuşurlarsa sözlerini dinlersin. Onlar, dikilip dayanmış kütükler gibidir. Her gürültüyü kendi aleyhlerine sanarlar; onlar gerçek düşmandır; onlardan sakın. ALLAH onları kahretsin; nasıl da çevriliyorlar!'),
(5193, 63, 5, 'Onlara, "Gelin de ALLAH\'ın elçisi sizin için bağışlanma dilesin," denildiğinde, başlarını çevirirler; büyüklük taslayarak saptıklarını görürsün.'),
(5194, 63, 6, 'Onlar için bağışlanma dilesen de dilemesen de birdir; ALLAH onları bağışlamıyacaktır. ALLAH yoldan çıkmış topluluğu doğruya ulaştırmaz.'),
(5195, 63, 7, 'Onlar, "ALLAH\'\'ın elçisinin yanında bulunanlara yardım etmeyin ki dağılıp gitsinler," diyenlerdir. Oysa göklerin ve yerin hazineleri ALLAH\'a aittir; ama ikiyüzlüler kavrayamazlar.'),
(5196, 63, 8, '"Kente dönersek, üstün olanlar alçakları çıkaracaktır," diyorlar. Oysa üstünlük ALLAH\'a, elçisine ve inananlara aittir. Ne var ki ikiyüzlüler bilmezler.'),
(5197, 63, 9, 'Ey inananlar, paralarınız ve çocuklarınız sizi ALLAH\'ı anmaktan alıkoymasın. Böyle davrananlar kaybedenlerdir.'),
(5198, 63, 10, 'Size ölüm gelip de, "Rabbim keşke beni kısa bir süre için ertelesen de, yoksullara yardım ederek erdemlilerden olsam," demeden önce verdiğimiz rızıklardan yardım için veriniz'),
(5199, 63, 11, 'ALLAH süresi geldiği zaman hiçbir canı ertelemez. ALLAH yaptıklarınızdan Haberdardır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'Göklerde ve yerde ne varsa ALLAH\'ı yüceltir. Yönetim O\'nundur, övgü O\'nadır. O her şeye Gücü yetendir.'),
(5201, 64, 2, 'Sizi O yarattı. Sonra kiminiz inkarcı, kiminiz de inançlıdır. ALLAH tüm yaptıklarınızı Görendir.'),
(5202, 64, 3, 'Gökleri ve yeri belli bir amaç için yaratmış, size biçim vermiş ve biçiminizi güzelleştirmiştir. Dönüş O\'nadır.'),
(5203, 64, 4, 'Göklerde ve yerde olanları bilir; sizin gizlediklerinizi ve açığa vurduklarınızı da bilir. ALLAH gizli düşüncelerinizi çok iyi bilir.'),
(5204, 64, 5, 'Daha önce inkar edip de yaptıklarının karşılığını tadanların haberi size ulaşmadı mı? Onlara acı bir azap vardır.'),
(5205, 64, 6, 'Çünkü, elçileri onlara apaçık kanıtlarla gidiyorlar ve onlar ise, "Bize doğru yolu bir insan mı gösterecek?" diyorlardı. Böylece inkar edip yüz çeviriyorlardı. ALLAH hiç bir şeye muhtaç değildir. ALLAH Zengindir, Övülendir.'),
(5206, 64, 7, 'İnkarcılar, diriltilmeyeceklerini ileri sürerler. De ki, "Evet, Rabbime andolsun diriltileceksiniz ve sonra yaptıklarınız size bildirilecektir. Bu, ALLAH\'a kolaydır.'),
(5207, 64, 8, 'ALLAH\'a, elçisine ve indirdiğimiz bu ışığa inanınız. ALLAH yaptıklarınızdan Haberdardır.'),
(5208, 64, 9, 'Gün gelir, sizi toplanma günü için toplarız. Bu, aldanma günüdür. Kim ALLAH\'a inanır ve erdemli davranırsa, kötülüklerini örter ve onu altından ırmaklar akan cennetlere sokar. Orada ebedi kalırlar. Büyük başarı işte budur.'),
(5209, 64, 10, 'İnkar edenler, ayetlerimizi ve mucizelerimizi yalanlayanlar ateşin halkıdır; orada ebedi kalıcıdırlar. Ne kötü bir varış noktasıdır!'),
(5210, 64, 11, 'Başa ne gelirse ALLAH\'ın izniyledir. Kim ALLAH\'a inanırsa, gönlüne yön gösterir. ALLAH her şeyi bilendir.'),
(5211, 64, 12, 'ALLAH\'a uyunuz, elçisine uyunuz. Yüz çevirirseniz, elçimizin görevi açıkça bildirmekten ibarettir.'),
(5212, 64, 13, 'ALLAH: O\'ndan başka tanrı yoktur. İnananlar yalnız ALLAH\'a güvenmelidirler.'),
(5213, 64, 14, 'Ey inananlar, eşleriniz ve çocuklarınız size düşman olabilirler. Öyleyse onlardan sakının. Ama affeder, hoşgörülü davranır ve bağışlarsanız, elbette ALLAH da Bağışlayandır, Rahimdir.'),
(5214, 64, 15, 'Paralarınız ve çocuklarınız bir sınavdır. Büyük ödül ALLAH\'ın yanındadır.'),
(5215, 64, 16, 'Gücünüz yettiği kadar ALLAH\'ı sayın. Dinleyin, itaat edin ve kendi yararınız için yardımda bulununuz. Nefsinin bencillik ve cimriliğinden korunanlar başaranlardır.'),
(5216, 64, 17, 'ALLAH\'a güzel bir ödünç sunarsanız, O da onu sizin için katlar ve sizi bağışlar. ALLAH Değer verir, Şefkatlidir.'),
(5217, 64, 18, 'Gizliyi ve açığı Bilendir; Üstündür, Bilgedir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'Ey peygamber, kadınları boşadığınız zaman boşama sürelerini gözetin ve bu süreyi iyice hesaplayın. Rabbiniz olan ALLAH\'ı dinleyin. Onları evlerinden çıkarmayın, onlar da çıkmasınlar (kötü davranarak onları buna zorlamayın). Ancak kanıtlanmış bir zina işlemeleri bunun dışındadır. ALLAH\'ın yasası budur. Kim ALLAH\'ın yasasını çiğnerse kendisine zulmetmiş olur. Bilmezsin, belki ALLAH bundan sonra (olumlu) bir iş ortaya çıkarır.'),
(5219, 65, 2, 'Sürelerini doldurdukları zaman, onları güzelce tutun, yahut güzelce ayırın. İçinizden adalet sahibi iki kişiyi de bu işleme tanık tutun. Tanıklığı ALLAH için yapınız. İşte bu, ALLAH\'a ve ahiret gününe inananlar için bir öğüttür. Kim ALLAH\'ı sayıp dinlerse ona bir çıkış yolu yaratır.'),
(5220, 65, 3, 'Ve beklemediği yerden ona rızık verir. Kim ALLAH\'a güvenirse O, ona yeter. ALLAH buyruğunu yerine getirendir. ALLAH herşey için bir ölçü koymuştur.'),
(5221, 65, 4, 'Menopoz dönemine girerek aybaşından kesilen kadınlarınıza gelince, kuşkunuz varsa, bekleme süreleri üç aydır. Aybaşı hali görmeyen ve gebe olanların süresi, yüklerini bırakmalarıyla sona erer. Kim ALLAH\'ı dinlerse, onun işini kolaylaştırır.'),
(5222, 65, 5, 'Bu, ALLAH\'ın size indirdiği emridir. Kim ALLAH\'ı dinlerse onun kötülüklerini örter ve ödülünü büyütür.'),
(5223, 65, 6, 'O kadınları, gücünüz oranında, oturduğunuz yerde oturtun. Ayrılmaya zorlamak için onları rahatsız etmeyin. Gebe iseler, yüklerini bırakıncaya kadar onların geçimlerini sağlayın. Sizin için emzirirlerse ücretlerini ödeyin. Aranızda güzel bir ilişki kurun. Güçlükle karşılaşırsanız, onu başka bir kadın emzirecektir.'),
(5224, 65, 7, 'Varlıklı kimse varlığı oranında nafaka yardımında bulunsun. Dar gelirli ise, ALLAH\'ın kendisine verdiğinden versin. ALLAH bir kimseye vermiş olduğundan fazla bir sorumluluk yüklemez. ALLAH zorluktan sonra kolaylık getirecektir.'),
(5225, 65, 8, 'Nice kentler, Rab\'lerinin ve O\'nun gönderdiği elçilerin emirlerine baş kaldırdı. Sonunda biz de onları çetin bir hesaba çektik ve onları görülmemiş biçimde cezalandırdık.'),
(5226, 65, 9, 'Kararlarının sonucunu tattılar. Kararlarının sonucu bir hüsran oldu.'),
(5227, 65, 10, 'ALLAH onlar için çetin bir ceza hazırlamıştır. Öyleyse, ey akıl sahibi inananlar, ALLAH\'ı dinleyiniz. ALLAH size bir mesaj...'),
(5228, 65, 11, 'bir elçi indirmiştir. İnanıp erdemli davrananları karanlıklardan aydınlığa çıkarmak için size ALLAH\'ın apaçık ayetlerini okur. Kim ALLAH\'a inanır ve erdemli davranırsa onu içinde ırmaklar akan cennetlere sokar; orada ebedi kalırlar. ALLAH ona güzel rızık vermiştir.'),
(5229, 65, 12, 'Yedi göğü ve yerden/topraktan da o kadar yaratan ALLAH\'tır. Buyruklar onların arasında akıp durur. Böylece ALLAH\'ın herşeye gücü yettiğini ve ALLAH\'ın herşeyi bilgiyle kuşatmış olduğunu bilesiniz.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'Ey peygamber, sırf eşlerini memnun etmek amacıyla, ALLAH\'ın sana helal (yasal) kıldığını kendine yasaklıyorsun. ALLAH Bağışlayandır, Rahimdir.'),
(5231, 66, 2, 'ALLAH, yeminlerinizin helalleştirmenin yolunu bildirmiştir. Mevlanız (efendiniz) ALLAH\'tır. O Bilendir, Bilgedir.'),
(5232, 66, 3, 'Peygamber, eşlerinden birine gizlice bir hadis söylemişti. Eşi bunu yayınca, ALLAH onu bundan haberdar kıldı. Ona durumun bir kısmını bildirmiş, bir kısmından ise vazgeçmişti. Peygamber konuyu eşine açınca eşi, "Bunu sana kim haber verdi?" diye sordu. "Herşeyi Bilen ve herşeyden Haberdar olan bana bildirdi," diye cevapladı.'),
(5233, 66, 4, 'İkiniz ALLAH\'a tevbe ederseniz kalbiniz yönelmiş olur. Ona (elçiye) karşıt olarak birbinizi destekleseniz, onun mevlası (koruyucusu/efendisi) ALLAH\'tır; Cibril, erdemli inananlar ve melekler de onu destekler.'),
(5234, 66, 5, 'Sizi boşarsa, Rabbi ona, belki sizin yerinize sizden daha iyi, (Tanrı\'ya) teslim olan, inanan, adayan, yönelen, kulluk eden, aktif, dul ve genç eşler verir'),
(5235, 66, 6, 'Ey inananlar, yakıtı halk ve taşlar olan ateşten kendinizi ve ailenizi koruyun. Ateşin üzerinde sert ve güçlü melekler olup, ALLAH\'ın buyruğuna karşı gelmezler ve kendilerine ne emredilmişse onu uygularlar.'),
(5236, 66, 7, 'Ey inkarcılar, bugün özür beyan etmeyin. Siz, ancak yapmış olduklarınızın karşılığını görmektesiniz.'),
(5237, 66, 8, 'Ey inananlar, ALLAH\'a yürekten tevbe edin ki Rabbiniz kötülüklerinizi örtsün ve altından ırmaklar akan cennetlere soksun. ALLAH, o gün peygamberi ve beraberinde bulunanları utandırmaz. Işıkları, önlerinden sağ yanlarına doğru yayılır ve, "Rabbimiz, bizim için ışığımızı tamamla ve bizi bağışla; sen herşeye gücü yetensin," derler.'),
(5238, 66, 9, 'Ey peygamber, kafirlere ve ikiyüzlülere karşı savaşım ver ve onlara karşı sert davran. Yerleri cehennemdir. O ne kötü duraktır.'),
(5239, 66, 10, 'ALLAH inkarcılara, Nuh\'un karısı ile Lut\'un karısını örnek verir. İki erdemli kulumuzun nikahı altında bulunuyorlardı; ancak onlara ihanette bulundular. Kocaları ALLAH\'tan gelen hiç bir şeyi o ikisinden savamadı. İkisine de, "Girenlerle birlikte cehenneme girin," denildi.'),
(5240, 66, 11, 'ALLAH inananlara, Firavun\'un hanımını örnek verir. O, "Rabbim, benim için cennette bir ev kur ve beni Firavun\'dan ve yaptıklarından kurtar; beni bu zalim halktan kurtar," demişti.'),
(5241, 66, 12, 'Ve İmran kızı Meryem de... O, iffetini korudu, biz de ruhumuzdan ona üfledik. O Rabbinin kelimelerine ve kitaplarına inandı; itaat edenlerden oldu.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'Yönetimi elinde bulunduran çok yücedir. O herşeye Kadirdir.'),
(5243, 67, 2, 'Hanginizin daha güzel işler yapacağını sınamak için ölümü ve hayatı yaratandır. O, Üstündür, Bağışlayandır.'),
(5244, 67, 3, 'O yedi göğü kat kat yaratmıştır. Rahman\'ın yaratışında her hangi bir kusur bulamazsın. Bakışlarını yönelt de bak; herhangi bir çatlak görebiliyor musun?'),
(5245, 67, 4, 'Sonra tekrar tekrar bak; bakışların usanmış ve yenilmiş olarak sana döner.'),
(5246, 67, 5, 'En aşağı göğü lambalarla süsledik ve onları şeytanlar için bir taşlama kıldık. Onlara alevli ateş azabını hazırladık.'),
(5247, 67, 6, 'Rab\'lerini inkar edenler cehennem cezasını hakketmişlerdir. Ne kötü bir duraktır.'),
(5248, 67, 7, 'Oraya atıldıkları zaman, onun kaynayıp tüterken uğultusunu işittiler'),
(5249, 67, 8, 'Neredeyse öfkeden patlayacak! İçine her ne zaman bir grup atılsa, oranın gardiyanları, "Size bir uyarıcı gelmedi mi?" diye sodular.'),
(5250, 67, 9, 'Onlar da dediler ki, "Evet, bize bir uyarıcı gelmişti, ama biz yalanladık ve, \'ALLAH hiçbir şey indirmemiştir. Siz tümüyle sapıtmışsınız,\' dedik."'),
(5251, 67, 10, '"Dinleseydik veya aklımızı kullansaydık biz şu ateşin halkı içinde olmazdık," dediler.'),
(5252, 67, 11, 'Böylece günahlarını itiraf ettiler. Ateşin halkı uzak olsunlar.'),
(5253, 67, 12, 'Kendi başlarına iken Rab\'lerine saygı duyanlar için bir bağışlanma ve büyük bir ödül vardır.'),
(5254, 67, 13, 'İsterseniz sözünüzü gizleyin veya onu açıklayın; O gizli düşüncelerinizi çok iyi Bilir.'),
(5255, 67, 14, 'Hiç Yaratan yarattığını bilmez mi? O, Latiftir, Haberdardır.'),
(5256, 67, 15, 'O ki yeri emriniz altına verdi. Yeryüzünü dolaşın ve onun besinlerinden yeyin. Son dönüş O\'nadır.'),
(5257, 67, 16, 'Yoksa, aniden sallanmaya başlayacak olan yerin dibine sizi batırmayacağına dair göktekinden güvence mi aldınız?'),
(5258, 67, 17, 'Aynı şekilde, üstünüze gökten taş yağdıran bir fırtına göndermeyeceğine dair göktekinden güvence mi aldınız? Uyarının nasıl olduğunu öğreneceksiniz.'),
(5259, 67, 18, 'Onlardan öncekiler de inkar etmişti. Beni tanımamak da nasıl mış?'),
(5260, 67, 19, 'Üzerlerinde düzenli sıralar halinde kanat çırpan kuşları görmezler mi? Rahman\'dan başkası onları tutmuyor. O herşeyi Görendir.'),
(5261, 67, 20, 'Rahman\'ın dışında size yardım edecek o ordularınız nerede? Gerçekten, inkarcılar bir aldanma içindedirler.'),
(5262, 67, 21, 'O, verdiği besinleri kesse, sizi besleyecek olan kimdir? Gerçekten azgınlık ve nefret içinde direnmektedirler.'),
(5263, 67, 22, 'Yüzüstü sürünen mi, yoksa dosdoğru yol üzerinde düzgün bir biçimde yürüyen mi daha doğru yoldadır?'),
(5264, 67, 23, 'De ki, "Sizi yaratan, size duyma, görme duyuları ve beyinler veren O\'dur. Ne kadar seyrek şükredersiniz!"'),
(5265, 67, 24, 'De ki, "Sizi yeryüzünde üreten O\'dur ve siz O\'nun huzurunda toplanacaksınız."'),
(5266, 67, 25, '"Doğru iseniz, bu söz ne zaman gerçekleşecek?" derler.'),
(5267, 67, 26, 'De ki, "Bu bilgi ALLAH\'ın katındadır. Ben sadece apaçık bir uyarıcıyım."'),
(5268, 67, 27, 'Onu yaklaşırken gördükleri zaman inkarcıların suratı asılır, ve onlara "İşte isteyip durduğunuz şey budur," denir.'),
(5269, 67, 28, 'De ki, "Baksanıza, ALLAH yanımdakilerle birlikte beni helak etse de, yahut bize acısa da, inkarcıları acı azaptan kim kurtarabilir?"'),
(5270, 67, 29, 'De ki, "O Rahman\'dır; biz O\'na inandık ve O\'na güvendik. Kimin gerçekten tam bir sapıklık içinde olduğunu ileride öğreneceksiniz."'),
(5271, 67, 30, 'De ki, "Hiç düşündünüz mü ki: suyunuz çekilse, size kim temiz bir su getirebilir?');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'NuN, kaleme ve yazdıklarına andolsun.'),
(5273, 68, 2, 'Sen Rabbinin nimetiyle delirmiş değilsin.'),
(5274, 68, 3, 'Senin için kesintisiz bir ödül vardır.'),
(5275, 68, 4, 'Kuşkusuz sen güçlü bir karaktere sahipsin.'),
(5276, 68, 5, 'Sen de göreceksin, onlar da görecekler;'),
(5277, 68, 6, 'Hanginizin şaşkın olduğunu.'),
(5278, 68, 7, 'Rabbin, kimin yolundan sapmış olduğunu da en iyi bilir, doğru yolda olanları da en iyi bilir.'),
(5279, 68, 8, 'Öyleyse yalanlayanlara uyma.'),
(5280, 68, 9, 'Kendilerinin ödün verip uzlaşabilmesi için senin de ödün verip uzlaşmanı istediler.'),
(5281, 68, 10, 'Şunların hiçbirine uyma: yemin edip duran, aşağılık,'),
(5282, 68, 11, 'İftiracı, söz götürüp getiren,'),
(5283, 68, 12, 'İyiliğe ve yardıma engel olan, saldırgan, günahkar.'),
(5284, 68, 13, 'İnsafsız ve sahtekar.'),
(5285, 68, 14, 'Mal ve çocuk sahibidir diye (onlara uyma).'),
(5286, 68, 15, 'Ayetlerimiz kendisine okunduğu zaman, "Efsane" der.'),
(5287, 68, 16, 'Onun burnunu işaretliyeceğiz.'),
(5288, 68, 17, 'Onları sınadık; tıpkı bahçe sahiplerini sınadığımız gibi. Sabahleyin devşireceklerine yemin etmişlerdi.'),
(5289, 68, 18, 'Bundan hiç bir kuşkuları yoktu.'),
(5290, 68, 19, 'Onlar uykudayken Rabbin tarafından gönderilen bir ziyaretçi (fırtına) bahçelerini ziyaret etti.'),
(5291, 68, 20, 'Ve bahçe meyvesiz kalmıştı.'),
(5292, 68, 21, 'Sabahleyin birbirlerine seslendiler:'),
(5293, 68, 22, '"Devşirecekseniz, haydi ekinlerinize erken varın."'),
(5294, 68, 23, 'Derken yola çıktılar, aralarında konuşuyorlardı.'),
(5295, 68, 24, '"Sakın, bugün hiçbir yoksul oraya yanınıza girmesin."'),
(5296, 68, 25, 'Sonuçtan emin bir halde erken vardılar.'),
(5297, 68, 26, 'Fakat bahçeyi görünce, "Biz yolu şaşırdık," dediler.'),
(5298, 68, 27, '"Yok, doğrusu biz yoksun bırakıldık."'),
(5299, 68, 28, 'Ortancaları (erdemlileri), "Ben size demedim mi? Rabbinizi yüceltmeniz gerekmez miydi?" dedi.'),
(5300, 68, 29, 'Dediler ki, "Rabbimiz yücedir. Biz zalimler imişiz."'),
(5301, 68, 30, 'Ve hemen birbirlerini suçlamaya başladılar.'),
(5302, 68, 31, 'Dediler ki, "Yazıklar olsun bize. Azgınmışız."'),
(5303, 68, 32, '"Belki Rabbimiz bize daha iyisini verir. Biz Rabbimize dönüyoruz."'),
(5304, 68, 33, 'İşte azap böyledir. Ahiret azabı ise daha büyüktür. Bir bilselerdi.'),
(5305, 68, 34, 'Erdemliler, Rab\'lerinden nimet bahçeleri (cennetleri) haketmişlerdir.'),
(5306, 68, 35, 'Müslümanlara suçlular gibi mi davranalım?'),
(5307, 68, 36, 'Neyiniz var, ne biçim hüküm veriyorsunuz?'),
(5308, 68, 37, 'Yoksa bir kitabınız var da onu mu okuyup duruyorsunuz?'),
(5309, 68, 38, 'Ve içinde her dilediğinizi bulabiliyorsunuz?'),
(5310, 68, 39, 'Yoksa, dilediğiniz hükmü verebileceğinize dair Diriliş Gününe kadar sürecek bir güvence mi aldınız bizden?'),
(5311, 68, 40, 'Sor onlara, "Bunu kim garanti etmektedir?"'),
(5312, 68, 41, 'Yoksa onların ortakları mı var? Haydi ortaklarını getirsinler, eğer doğru kimseler iseler?'),
(5313, 68, 42, 'Gün gelecek, onların içyüzleri açığa çıkarılacak, secdeye çağrılacaklar; ancak buna güçleri yetmeyecektir.'),
(5314, 68, 43, 'Gözleri düşmüş bir durumda, onları aşağılanma kaplar. Oysa onlar sağlam iken secdeye çağrılmışlardı'),
(5315, 68, 44, 'Bu hadisi (sözü) yalanlayanla Beni başbaşa bırak. Onları, bilmedikleri yerden yavaş yavaş yaklaştıracağız.'),
(5316, 68, 45, 'Onlara mühlet veriyorum. Benim planım sağlamdır.'),
(5317, 68, 46, 'Yoksa sen onlardan bir ücret istiyorsun da onlar ağır bir borç altında mı kalıyorlar?'),
(5318, 68, 47, 'Yoksa geçmişin ve geleceğin bilgisi onların yanında da onlar mı kaydediyorlar?'),
(5319, 68, 48, 'Rabbinin hükmünün gerçekleşmesi için sabret. Balığın arkadaşı (Yunus) gibi olma. Hani o, (balık tarafından) yutulmuş bir durumda iken seslenmişti.'),
(5320, 68, 49, 'Rabbinden ona bir nimet ulaşmasaydı, kınanmış olarak çorak bir sahile atılacaktı.'),
(5321, 68, 50, 'Ancak Rabbi onu seçip erdemlilerden kıldı.'),
(5322, 68, 51, 'Mesajı işittikleri zaman, inkarcılar neredeyse seni gözleriyle yiyeceklerdi. "O, delidir!" diyorlardı.'),
(5323, 68, 52, 'Halbuki o, tüm evrene bir mesajdır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'Gerçekleşen (olay).'),
(5325, 69, 2, 'Nedir o gerçekleşen!'),
(5326, 69, 3, 'Gerçekleşenin ne olduğunu nerden bileceksin?'),
(5327, 69, 4, 'Semud ve Ad (halkı) sarsıcı olayı yalanladı.'),
(5328, 69, 5, 'Ve Semud o azgın (sarsıntı) ile yok edildi.'),
(5329, 69, 6, 'Ad ise sert ve azgın bir kasırga ile yok edildi.'),
(5330, 69, 7, 'Onu, yedi gece ve sekiz gün boyunca üzerlerine bir bela olarak saldı. Halkın, çürümüş hurma gövdeleri gibi yere yıkıldıklarını görürsün.'),
(5331, 69, 8, 'Onların hiç bir kalıntısını görüyor musun?'),
(5332, 69, 9, 'Firavun, ondan öncekiler ve altüst olan (Sodomlu) larda kötülük işlemişti.'),
(5333, 69, 10, 'Rab\'lerinin elçisine isyan ettiler. Bunun sonucu olarak da onları şiddeti gittikçe artan bir biçimde yakalamıştı.'),
(5334, 69, 11, 'Su taşınca sizi akıp giden (sal) üzerinde taşımıştık.'),
(5335, 69, 12, 'Ki o size bir ders olsun ve dinleyen kulaklar anlasın.'),
(5336, 69, 13, 'Boruya bir kez üfürüldüğü zaman,'),
(5337, 69, 14, 'Yer ve dağlar kaldırılıp birbirine çarpılıp darmadağın edildiği zaman,'),
(5338, 69, 15, 'İşte o gün kaçınılmaz olay gerçekleşmiştir.'),
(5339, 69, 16, 'Gök yarılmıştır, parçalanmıştır.'),
(5340, 69, 17, 'Melekler her yandadır. Rabbinin yönetimi o gün sekiz (evren) üzerinde egemen olacaktır.'),
(5341, 69, 18, 'O gün ortaya çıkarılırsınız ve hiçbir şeyiniz gizli kalmaz.'),
(5342, 69, 19, 'Kitabı sağından verilen, "Alın kitabımı okuyun," der,'),
(5343, 69, 20, '"Hesabımla karşılaşacağıma inanıyordum."'),
(5344, 69, 21, 'O mutlu bir yaşantı içindedir,'),
(5345, 69, 22, 'Yüksek bir cennette (bahçede),'),
(5346, 69, 23, 'Meyveleri ulaşılabilecek mesafededir.'),
(5347, 69, 24, 'Geçmiş günlerinizde yaptığınız işlerden ötürü afiyetle yiyiniz, içiniz.'),
(5348, 69, 25, 'Kitabı solundan verilenlere gelince, onlar, "Keşke kitabım bana verilmeseydi," der,'),
(5349, 69, 26, '"Hesabımın ne olduğunu öğrenmeyeydim."'),
(5350, 69, 27, '"Keşke ölümüm sonsuz olsaydı."'),
(5351, 69, 28, '"Param bana yaramadı."'),
(5352, 69, 29, '"Tüm gücümü yitirdim."'),
(5353, 69, 30, 'Yakalayın, bağlayın onu.'),
(5354, 69, 31, 'Ve sonra cehennemde yakın.'),
(5355, 69, 32, 'Sonra, onu yetmiş arşın boyunda bir zincire vurun.'),
(5356, 69, 33, 'Çünkü o, Yüce ALLAH\'a inanmıyordu.'),
(5357, 69, 34, 'Yoksullara yedirmeğe de çalışmıyordu.'),
(5358, 69, 35, 'Bu yüzden onun buralarda bir dostu yoktur.'),
(5359, 69, 36, 'Hiç bir yiyeceği de... İrin hariç,'),
(5360, 69, 37, 'Onu ancak günahkarlar yer.'),
(5361, 69, 38, 'Yemin ederim gördüklerinize,'),
(5362, 69, 39, 'Ve görmediklerinize,'),
(5363, 69, 40, 'Ki bu şerefli bir elçinin (getirdiği) sözdür.'),
(5364, 69, 41, 'O bir şair sözü değildir; ne de az inanıyorsunuz?'),
(5365, 69, 42, 'Kahin sözü de değildir; ne de az düşünüyorsunuz?'),
(5366, 69, 43, 'Evrenlerin Rabbinden indirilmedir.'),
(5367, 69, 44, 'O bize bazı sözler yakıştırsa,'),
(5368, 69, 45, 'Biz onu kuvvetle yakalar,'),
(5369, 69, 46, 'Sonra, ondan vahyi keserdik.'),
(5370, 69, 47, 'Ve sizden kimse de buna engel olamazdı.'),
(5371, 69, 48, 'Kuşkusuz bu, erdemliler için bir hatırlatmadır.'),
(5372, 69, 49, 'İçinizden yalanlayanlar olduğunu iyi biliyoruz.'),
(5373, 69, 50, 'O, inkarcılar için bir üzüntü kaynağıdır.'),
(5374, 69, 51, 'O, kuşkusuz mutlak gerçektir.'),
(5375, 69, 52, 'Öyleyse Büyük olan Rabbinin ismini yücelt.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'Sorgulayan birisi, gerçekleşecek azabı sordu.'),
(5377, 70, 2, 'Onu inkarcılardan savacak kimse yoktur.'),
(5378, 70, 3, 'Yükseliş Yollarının Sahibi olan ALLAH\'tandır.'),
(5379, 70, 4, 'Melekler ve ruh (vahiy/komutlar/Cebrail), elli bin yıla eşit bir gün içinde O\'na yükselir.'),
(5380, 70, 5, 'Şimdi sen güzelce sabret.'),
(5381, 70, 6, 'Onlar onu uzak görüyorlar.'),
(5382, 70, 7, 'Biz ise onu yakın görüyoruz.'),
(5383, 70, 8, 'Gün gelecek, gök erimiş maden gibi.'),
(5384, 70, 9, 'Dağlar ise atılmış yün gibi olur.'),
(5385, 70, 10, 'Dost dostun durumunu sormaz.'),
(5386, 70, 11, 'Birbirlerine gösterilirler. Suçlu, o günün azabından kurtulmak için fidye vermek ister: Oğullarını,'),
(5387, 70, 12, 'Eşini, kardeşini,'),
(5388, 70, 13, 'Kendisini yetiştiren tüm akrabalarını,'),
(5389, 70, 14, 'Ve yeryüzünde bulunan herkesi, ki kurtulsun.'),
(5390, 70, 15, 'Hayır, o alevli ateştir.'),
(5391, 70, 16, 'Yakmak için isteklidir...'),
(5392, 70, 17, 'Çağırır, sırtını dönüp gideni,'),
(5393, 70, 18, 'Toplayıp kasaya saklayanı.'),
(5394, 70, 19, 'Doğrusu insan endişeli bir karaktere sahiptir.'),
(5395, 70, 20, 'Kendisine kötülük dokunduğu zaman ümidini keser.'),
(5396, 70, 21, 'Kendisine iyilik dokunduğu zaman ise cimridir.'),
(5397, 70, 22, 'Ancak namaz kılanlar hariç:'),
(5398, 70, 23, 'Onlar ki namazlarını kaçırmazlar;'),
(5399, 70, 24, 'Paralarında bilinen bir pay (zekat) ayrılmıştır,'),
(5400, 70, 25, 'İsteyen yoksula ve yoksuna...'),
(5401, 70, 26, 'Onlar Din Gününü doğrularlar;'),
(5402, 70, 27, 'Rab\'lerinin azabından çekinirler;'),
(5403, 70, 28, 'Rab\'lerinin azabına güven olmaz.'),
(5404, 70, 29, 'Onlar cinsel ilişkiden sakınırlar;'),
(5405, 70, 30, 'Ancak eşleri, yahut yeminlerinin/anlaşmalarının hak sahibi olduklari hariç; onlardan dolayı yerilmezler.'),
(5406, 70, 31, '- bunun ötesini arayanlar ise aşırı gidenlerdir-'),
(5407, 70, 32, 'Onlar güvenilirdirler, sözlerine bağlıdırlar;'),
(5408, 70, 33, 'Gereği gibi tanıklıkta bulunurlar;'),
(5409, 70, 34, 'Namazlarına özen gösterirler.'),
(5410, 70, 35, 'Onlar cennetlerde ağırlanırlar.'),
(5411, 70, 36, 'Peki şimdi inkarcılara ne oluyor da senin önünde koşuşuyorlar?'),
(5412, 70, 37, 'Sağdan, soldan gruplar halinde...'),
(5413, 70, 38, 'Herbiri, nimet cennetine sokulacağını mı umuyor?'),
(5414, 70, 39, 'Asla; biz onları yarattık, bildikleri şeyden...'),
(5415, 70, 40, 'Doğuların ve batıların Rabbine andolsun; bizim gücümüz yeter...'),
(5416, 70, 41, 'Onları, kendilerinden daha iyilerle değiştirmeye... Bizi kimse yenemez'),
(5417, 70, 42, 'Bırak onları, kendilerine söz verilen gün ile karşı karşıya gelinceye kadar dalsınlar, oynasınlar.'),
(5418, 70, 43, 'O gün mezarlarından hızlı hızlı çıkarlar; kurban taşına sürülüyorlarmış gibi...'),
(5419, 70, 44, 'Gözleri dönmüş, kendilerini utanç sarmış olarak. İşte bu, onlara söz verilmiş olan gündür.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, '"Kendilerine acı azap gelmezden önce halkını uyar," diye Nuh\'u halkına gönderdik.'),
(5421, 71, 2, 'Dedi ki, "Ey halkım, ben size apaçık bir uyarıcıyım."'),
(5422, 71, 3, '"ALLAH\'a kulluk edin, O\'nu sayın ve beni izleyin."'),
(5423, 71, 4, '"Ki günahlarınızı bağışlasın ve sizi belli bir süreye kadar ertelesin. Kuşkusuz, ALLAH\'ın verdiği süre gelince ertelenmez; bir bilseydiniz.'),
(5424, 71, 5, 'Dedi ki, "Rabbim, ben halkımı gece gündüz çağırdım."'),
(5425, 71, 6, '"Ne var ki, çağrım onların kaçışını arttırmaktan başka şeye yaramadı."'),
(5426, 71, 7, '"Her ne zaman senin onları bağışlaman için onları çağırdıysam parmaklarını kulaklarına tıkadılar, elbiselerini başlarına örttüler, direndiler, büyüklendikçe büyüklendiler."'),
(5427, 71, 8, '"Sonra onları açıkça çağırdım."'),
(5428, 71, 9, '" Sonra onlara ilan ettim, gizliden gizliye de konuştum."'),
(5429, 71, 10, '"Dedim ki, \'Rabbinizden bağışlanma dileyin; O çok Bağışlayandır."'),
(5430, 71, 11, '"Size gökten bol yağmur göndersin."'),
(5431, 71, 12, '"Size bol para ve çocuklarla desteklesin, size bahçeler ve ırmaklar versin."'),
(5432, 71, 13, 'Size ne oluyor ki ALLAH\'a saygı göstermek istemiyorsunuz?'),
(5433, 71, 14, 'Oysa sizi evrimler halinde yaratan O\'dur.'),
(5434, 71, 15, 'ALLAH\'ın yedi göğü tabakalar halinde nasıl yarattığını görmez misiniz?'),
(5435, 71, 16, 'Ayı bunların içinde bir ışık, güneşi de bir lamba yaptı.'),
(5436, 71, 17, 'Ve ALLAH sizi topraktan bir bitki olarak bitirdi.'),
(5437, 71, 18, 'Sonra sizi ona döndürecek ve sizi tekrar çıkaracaktır.'),
(5438, 71, 19, 'ALLAH yeryüzünü sizin için bir yerleşim yeri yaptı.'),
(5439, 71, 20, 'Ki orada geniş yollarda gidesiniz.'),
(5440, 71, 21, 'Nuh dedi ki, "Rabbim, onlar bana karşı geldiler ve parası, çocukları kendisine sadece zarar veren bir kimseye uydular."'),
(5441, 71, 22, '"Ve hatta büyük tuzaklar kurdular."'),
(5442, 71, 23, '"Dediler ki, \'Tanrılarınızı terketmeyin. Ne Vedd\'i, ne Suva\'ı, ne Yeğus\'u, Yeuk\'u ve Nesr\'i bırakmayın."'),
(5443, 71, 24, '"Çok kişiyi saptırdılar. Öyleyse, sen de zalimlerin şaşkınlığını arttır."'),
(5444, 71, 25, 'Suçlarından ötürü boğuldular ve ateşe sokuldular. Kendilerine ALLAH\'tan başka yardımcı da bulamadılar.'),
(5445, 71, 26, 'Nuh dedi ki, "Rabbim, yeryüzünde bir tek inkarcı bırakma."'),
(5446, 71, 27, '"Onları bırakırsan kullarını saptırırlar ve ancak bayağı inkarcılar doğururlar."'),
(5447, 71, 28, '"Rabbim, beni, anamı babamı, evime inanan olarak girenleri, inanan erkek ve kadınları bağışla; zalimlerin ise ancak yıkımlarını arttır."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'De ki, "Bana vahyedildiğine göre, cinlerden bir grup dinlediler ve şöyle dediler:" "Biz ilginç bir Kuran işittik."'),
(5449, 72, 2, '"O doğruya iletiyor ve biz ona inandık; bundan böyle Rabbimize hiç kimseyi ortak koşmayacağız."'),
(5450, 72, 3, '"Rabbimiz çok yücedir. O ne bir eş ne de bir çocuk edinmemiştir. "'),
(5451, 72, 4, '"ALLAH hakkında saçma şeyler söyleyip duran bizim o beyinsizdi."'),
(5452, 72, 5, '"İnsanların ve cinlerin ALLAH\'a karşı yalan uyduramıyacaklarını sanmıştık."'),
(5453, 72, 6, '"Ne var ki bazı insanlar, bazı cinlere sığınırlardı ve bu da onların azgınlıklarını arttırırdı."'),
(5454, 72, 7, '"Onlar, tıpkı sizin sandığınız gibi, ALLAH\'ın hiç bir kimseyi göndermeyeceğini sanıyorlardı."'),
(5455, 72, 8, '"Biz göğe dokunduk ve onu güçlü bekçilerle ve ateş toplarıyla korunmuş bulduk."'),
(5456, 72, 9, '"Biz orada casusluk için otururduk. Ancak şimdi her kim dinlemek isterse kendisini gözleyen bir ateş topu bulur."'),
(5457, 72, 10, '"Yerdekiler için bir kötülük mü planlanmıştır, yoksa Rab\'leri onlar için bir iyilik mi dilemiştir, bilmiyoruz."'),
(5458, 72, 11, '"Aramızda erdemliler de var, bunun aşağısında olanlar da var; biz çeşitli tarikatlara(yollara) ayrıldık. "'),
(5459, 72, 12, '"Biz, yeryüzünde ALLAH\'ı aciz bırakamıyacağımızı, kaçmakla da O\'nu atlatamıyacağımızı anladık."'),
(5460, 72, 13, '"Biz, yol gösteren (Kuran)ı işitir işitmez ona inandık. Rabbine inanan hiçbir haksızlığa ve sıkıntıya uğramaz."'),
(5461, 72, 14, '"Aramızdan müslümanlar da var uzlaşmacılar da vardır." Kim teslim olursa, işte gerçeği araştıranlar onlardır.'),
(5462, 72, 15, 'Uzlaşmacılara gelince, cehenneme odun olmuşlardır.'),
(5463, 72, 16, 'Doğru yola girselerdi onlara bol su verirdik.'),
(5464, 72, 17, 'Ki onunla onları sınayalım. Kim Rabbinin mesajından yüz çevirirse onu zorlu ve yokuşlu bir cezaya süreriz.'),
(5465, 72, 18, 'Mescidler sadece ALLAH\'a aittir; öyleyse ALLAH ile birlikte hiç kimseyi çağırmayın.'),
(5466, 72, 19, 'ALLAH\'ın kulu kalkıp sadece O\'na çağırdığında ona karşı nerdeyse hepsi bir bütün oldular.'),
(5467, 72, 20, 'De ki, "Ben sadece Rabbime çağırırım; ve O\'na hiç kimseyi ortak koşmam."'),
(5468, 72, 21, 'De ki, "Ben size ne bir zarar ne de bir yarar verme gücüne sahip değilim."'),
(5469, 72, 22, 'De ki, "Hiç kimse beni ALLAH\'a karşı savunamaz, O\'ndan başka sığınak da bulamam."'),
(5470, 72, 23, '"Ancak ALLAH\'tan bir duyuruyu ve mesajını iletirim." Artık kim ALLAH\'a ve elçisine karşı gelirse içinde ebedi kalacağı cehennem ateşini hakeder.'),
(5471, 72, 24, 'Kendilerine söz verilen şeyi gördüklerinde kimin yardımcısının güçsüz ve sayıca az olduğunu görecekler.'),
(5472, 72, 25, 'De ki, "Size söz verilen yakın mıdır yoksa Rabbim onun için uzun bir süre mi belirlemiştir bilmiyorum."'),
(5473, 72, 26, 'O geleceği bilendir; ve O sırrını hiç kimseye göstermez.'),
(5474, 72, 27, 'Ancak seçtiği bir elçi hariç; nitekim O, o elçiden önceye ve sonraya ait bir gözlem sunar.'),
(5475, 72, 28, 'Böylece, elçilerin Rab\'lerinin mesajını ilettikleri ortaya çıksın. O onların yaptıklarını tümüyle kuşatmıştır ve herşeyi sayı olarak hesaplamıştır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'Ey bürünen,'),
(5477, 73, 2, 'Bazı geceler hariç, geceleyin kalk.'),
(5478, 73, 3, 'Yarısında veya yarısından biraz önce.'),
(5479, 73, 4, 'Yahut yarısından biraz sonra... Kuran\'ı da özenle düşüne düşüne oku.'),
(5480, 73, 5, 'Biz sana ağır bir söz bırakacağız.'),
(5481, 73, 6, 'Kuşkusuz gece kalkıp meditasyonda bulunmak çok daha etkili ve ifade açısından daha uygundur.'),
(5482, 73, 7, 'Seni gündüzün epeyce uğraştıracak işlerin vardır.'),
(5483, 73, 8, 'Rabbinin ismini an ve tüm gönlünle O\'na yönel.'),
(5484, 73, 9, 'Doğunun ve batının Rabbi; O\'ndan başka tanrı yoktur. Kendine avukat olarak O\'nu seç.'),
(5485, 73, 10, 'Onların sözlerine karşı sabırlı ol ve onlardan güzellikle ayrıl.'),
(5486, 73, 11, 'Varlık sahibi olup da seni yalanlayanları bana bırak; onlara biraz süre tanı.'),
(5487, 73, 12, 'Yanımızda zincirler ve cehennem vardır.'),
(5488, 73, 13, 'Yutulması güç bir yiyecek ve acı bir azap...'),
(5489, 73, 14, 'Gün gelir, yer ve dağlar titrer ve dağlar dağılan kum yığınına dönüşür.'),
(5490, 73, 15, 'Biz, size hakkınızda tanıklık edecek bir elçi gönderdik; nasıl ki Firavun\'a da bir elçi göndermiştik.'),
(5491, 73, 16, 'Ancak Firavun, elçiye karşı geldi; biz de onu feci bir biçimde yakaladık.'),
(5492, 73, 17, 'Peki, inkar ederseniz, çocukların saçlarını ağartan bir günden nasıl korunursunuz?'),
(5493, 73, 18, 'Gök bile ondan dolayı yarılır. O\'nun sözü gerçekleşir.'),
(5494, 73, 19, 'Bu bir hatırlatmadır; dileyen, Rabbine doğru yol tutar.'),
(5495, 73, 20, 'Rabbin, senin ve yoldaşlarıdan bir grubun, gecenin üçte ikisinden az, yarısında ve üçte birinde kalktığını bilir. Gecenin ve gündüzün miktarını ALLAH belirler. O, sizin bunu yapamıyacağınızı bildiği için sizi affetmiştir. Öyleyse Kuran\'dan kolayınıza geldiği kadar okuyun. Aranızda hastalar, yeryüzünde ALLAH\'ın lütfundan rızık arayanlar ve ALLAH yolunda savaşanlar olduğunu bilmektedir. Ondan kolayınıza geldiği kadar okuyun. Namazı gözetin, zekatı verin ve güzel davranmak yoluyla ALLAH\'a bir borçsunun. Kendiniz için yaptığınız her iyiliği, ALLAH katında daha iyi ve daha büyük bir ödül olarak bulacaksınız. ALLAH\'tan bağışlanma dileyin; ALLAH Bağışlayandır, Rahimdir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'Ey gizlenen,'),
(5497, 74, 2, 'Kalk ve uyar.'),
(5498, 74, 3, 'Rabbini yücelt.'),
(5499, 74, 4, 'Örtülerini temizle.'),
(5500, 74, 5, 'Kötülükten uzaklaş.'),
(5501, 74, 6, 'İhtiraslı olma.'),
(5502, 74, 7, 'Rabbin için sabret.'),
(5503, 74, 8, 'Duyuru yapıldığı zaman,'),
(5504, 74, 9, 'İşte, zorlu gün o gündür.'),
(5505, 74, 10, 'İnkarcılar için kolay değil.'),
(5506, 74, 11, 'Bir birey olarak yarattığım kişiyi bana bırak.'),
(5507, 74, 12, 'Ona hem zenginlik verdim,'),
(5508, 74, 13, 'Hem de gözü önünde çocuklar...'),
(5509, 74, 14, 'Ona nimetler yağdırdım.'),
(5510, 74, 15, 'Buna rağmen, daha fazlasını istiyor.'),
(5511, 74, 16, 'Asla, çünkü o, ayetlerimize karşı bir inatçı kesildi.'),
(5512, 74, 17, 'Onu sarp bir yokuşa sardıracağım.'),
(5513, 74, 18, 'Nitekim o düşündü; ölçtü biçti.'),
(5514, 74, 19, 'Kahrolası, ne biçim ölçüp biçti.'),
(5515, 74, 20, 'Kahrolası, gene ne biçim ölçüp biçti.'),
(5516, 74, 21, 'Baktı.'),
(5517, 74, 22, 'Sonra surat astı, kaşlarını çattı.'),
(5518, 74, 23, 'Ve arkasını döndü; büyüklük tasladı:'),
(5519, 74, 24, '"Bu," dedi, "etkileyici bir büyüden başka bir şey değil."'),
(5520, 74, 25, '"Bu sadece bir insan sözüdür."'),
(5521, 74, 26, 'Onu Sakar\'a atacağım.'),
(5522, 74, 27, 'Sakar nedir bilir misin?'),
(5523, 74, 28, 'Ne bırakır, ne de yüklenir (tam ve mükemmel),'),
(5524, 74, 29, 'Halklar için (evrensel) bir göstergedir/ekrandır.'),
(5525, 74, 30, 'Üzerinde ondokuz vardır.'),
(5526, 74, 31, 'Biz ateşe bekçi olarak sadece melekleri atadık. Onların sayısını (ondokuz\'u) da, () inkarcılar için bir fitne (sınav/huzursuzluk kaynağı) yaptık, () kitap verilmiş olanları ikna etsin, () inananların inancını güçlendirsin, () kitap verilmiş olanlarla inananların kuşkularını ortadan kaldırsın, ve () kalplerinde hastalık olanlarla inkarcılar da, "ALLAH bu örnekle ne demek istiyor?" desinler. Böylece ALLAH dilediğini/dileyeni saptırır ve dilediğini/dileyeni de doğruya iletir. Rabbinin ordularını kendisinden başkası bilmez. Bu (sayı) halklara bir mesajdır.'),
(5527, 74, 32, 'Hayır, andolsun Ay\'a,'),
(5528, 74, 33, 'Geçtiği vakit geceye,'),
(5529, 74, 34, 'Ağardığı vakit sabaha,'),
(5530, 74, 35, 'Bu büyüklerden birisidir.'),
(5531, 74, 36, 'Halklara bir uyarıdır.'),
(5532, 74, 37, 'İlerlemek yahut geride kalmak dileyenleriniz için.'),
(5533, 74, 38, 'Her kişi kendi günahıyla mahkum olur.'),
(5534, 74, 39, 'Ancak sağ tarafta olanlar hariç;'),
(5535, 74, 40, 'Cennetler içindedirler, sorarlar,'),
(5536, 74, 41, 'Suçlulara:'),
(5537, 74, 42, '"Sizi bu cezaya sokan nedir?"'),
(5538, 74, 43, 'Diyecekler ki, "Desteklemezdik/namaz kılmazdık"'),
(5539, 74, 44, '"Yoksula da yedirmezdik."'),
(5540, 74, 45, '"Biz, boş şeylere dalanlarla birlikte dalardık."'),
(5541, 74, 46, '"Yargı gününü yalanlardık."'),
(5542, 74, 47, '"Nihayet (şimdi) kesin gerçeğe ulaştık."'),
(5543, 74, 48, 'Aracıların şefaati onlara bir yarar sağlamaz.'),
(5544, 74, 49, 'Öyleyse neden bu mesajdan yüz çeviriyorlar.'),
(5545, 74, 50, 'Ürkmüş zebralar gibi,'),
(5546, 74, 51, 'Aslandan kaçan...'),
(5547, 74, 52, 'Hayır, onlardan her biri, kendisine özel olarak açılmış sayfalar verilmesini ister.'),
(5548, 74, 53, 'Doğrusu, onlar ahiretten korkmuyor.'),
(5549, 74, 54, 'Doğrusu, bu bir öğüttür.'),
(5550, 74, 55, 'Dileyen ondan öğüt alır.'),
(5551, 74, 56, 'ALLAH dilemezse onlar öğüt alamazlar. O, erdemli davranmanın kaynağıdır; bağışlamanın kaynağıdır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'Diriliş Gününe and içerim.'),
(5553, 75, 2, 'Sürekli özeleştiride bulunan kişiye and içerim.'),
(5554, 75, 3, 'İnsan, kemiklerini bir araya toplayamayız mı sanıyor?'),
(5555, 75, 4, 'Evet; parmak uçlarını bile düzenlemeye gücümüz yeter.'),
(5556, 75, 5, 'Doğrusu, insan her şeyin önüne sergilenmesini ister.'),
(5557, 75, 6, '"Diriliş Günü ne zaman?" diye sorar.'),
(5558, 75, 7, 'Gözün kamaştığı,'),
(5559, 75, 8, 'Ayın tutulduğu,'),
(5560, 75, 9, 'Ve güneş ile ay bir araya toplandığı zaman,'),
(5561, 75, 10, 'O gün insanoğlu, "Kaçacak yer nerede?" der.'),
(5562, 75, 11, 'Hayır, bir sığınak yok.'),
(5563, 75, 12, 'O gün son durak Rabb\'inin huzurudur.'),
(5564, 75, 13, 'O gün insana, yapıp yapmadığı herşey haber verilir.'),
(5565, 75, 14, 'Doğrusu, insan kendi kendisine tanıktır.'),
(5566, 75, 15, 'Birtakım özürler ortaya atsa da...'),
(5567, 75, 16, 'Onu aceleye getirip dilini oynatma.'),
(5568, 75, 17, 'Onu toplamak da okutmak da bize düşer.'),
(5569, 75, 18, 'Biz onu okuduğumuz zaman, onun okunuşunu izle.'),
(5570, 75, 19, 'Sonra, onu açıklamak da bizim görevimizdir.'),
(5571, 75, 20, 'Ne var ki, siz geçici (dünyayı) seviyorsunuz.'),
(5572, 75, 21, 'Ahireti ise önemsemiyorsunuz.'),
(5573, 75, 22, 'O gün bazı yüzler parlar.'),
(5574, 75, 23, 'Rabb\'ine bakar.'),
(5575, 75, 24, 'O gün bazı yüzler de var ki asıktır.'),
(5576, 75, 25, 'Belkemiğinin kırılacağının endişesi içindedir.'),
(5577, 75, 26, 'Doğrusu, (nefis) boğaza dayandığı,'),
(5578, 75, 27, 'Ve, "Çare bulan var mı?" dendiği zaman.'),
(5579, 75, 28, 'Bunun artık o ayrılık zamanı olduğunu anlar.'),
(5580, 75, 29, 'Bacakları birbirine dolaşmıştır.'),
(5581, 75, 30, 'O gün sevk Rabbine doğrudur.'),
(5582, 75, 31, 'O ne doğruladı, ne de destekledi;'),
(5583, 75, 32, 'Fakat yalanladı ve yüz çevirdi.'),
(5584, 75, 33, 'Sonra çalım satarak ailesine gitti.'),
(5585, 75, 34, 'Sen bunu haketmişsin.'),
(5586, 75, 35, 'Gerçekten sen bunu haketmiş bulunuyorsun.'),
(5587, 75, 36, 'İnsan, başıboş bırakılacağını mı sanıyor?'),
(5588, 75, 37, 'Dökülen meniden bir sperm değil miydi?'),
(5589, 75, 38, 'Ve bir embriyoya dönüştükten sonra O yaratıp biçim verdi?'),
(5590, 75, 39, 'Ve ondan erkek ve dişi olmak üzere iki çift yarattı?'),
(5591, 75, 40, 'Bunları yapanın ölüleri diriltmeye gücü yetmez mi?');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'İnsanın üzerinden, kendisinin anılmaya değer bir şey olmadığı bir zaman periyodu geçmemiş midir?'),
(5593, 76, 2, 'Biz insanı bir sıvı karışımdan yarattık ki onu sınayalım. Bu yüzden onu işiten ve gören yaptık.'),
(5594, 76, 3, 'Ona yolu gösterdik; ya şükredendir, ya da nankör.'),
(5595, 76, 4, 'İnkarcılar için zincirler, prangalar ve alevli bir ateş hazırlamışızdır.'),
(5596, 76, 5, 'İyiler ise, kafur (CHO) karıştırılmış bir kadehten içerler.'),
(5597, 76, 6, 'ALLAH\'ın kullarının taşıra taşıra içtikleri bir kaynak...'),
(5598, 76, 7, 'Onlar sözlerini yerine getirirler ve alabildiğine kötü olan bir günden korkarlar.'),
(5599, 76, 8, 'Yoksula, öksüze ve tutsağa sevdikleri yiyecekleri yedirirler.'),
(5600, 76, 9, '"Biz sizi ALLAH rızası için yediriyoruz. Sizden ne bir karşılık, ne de bir teşekkür beklemiyoruz."'),
(5601, 76, 10, '"Biz, suratsız ve belalı bir günden ötürü Rabbimizden çekiniriz."'),
(5602, 76, 11, 'Nitekim, ALLAH onları o günün kötülüğünden korur ve onlara neşe ve sevinç verir.'),
(5603, 76, 12, 'Direndikleri için onları bahçe ve ipekle ödüllendirir.'),
(5604, 76, 13, 'Orada koltuklara yaslanırlar; orada ne (yakıcı) güneş ne de dondurucu soğuk görürler.'),
(5605, 76, 14, 'Ağaçların gölgesi üzerlerine sarkmış ve meyveler yaklaştırılarak koparılmaları kolaylaştırılmıştır.'),
(5606, 76, 15, 'Onlara gümüş tepsiler ve şeffaf bardaklarda sunulur.'),
(5607, 76, 16, 'Gümüşten yapılmış şeffaf bardaklar... Onları tam olarak haketmişlerdir.'),
(5608, 76, 17, 'Orada zencefil (ginger) karıştırılmış bir kadehten içirilirler.'),
(5609, 76, 18, 'Bir kaynak ki, ona "Selsebil" denir.'),
(5610, 76, 19, 'Onlara ölümsüz gençler servis yapacaktır. Onları görsen, kendilerini saçılmış inci sanırsın.'),
(5611, 76, 20, 'Nereye baksan nimetler ve büyük bir yönetim görürsün.'),
(5612, 76, 21, 'Üstlerinde yeşil kadifeden elbiseler ve ipekler vardır. Gümüş bilezikler takınmışlardır. Rab\'leri onlara temiz bir içecek içirir.'),
(5613, 76, 22, 'Bu, ödülünüzdür ve gayretinize teşekkür edilmiştir.'),
(5614, 76, 23, 'Kuşkusuz, Kuran\'ı sana bir indirişle biz indirdik.'),
(5615, 76, 24, 'Öyleyse Rabbinin hükmünü izlemekte sabret; onlardan hiç bir nankör günahkara uyma.'),
(5616, 76, 25, 'Sabah akşam Rabbinin ismini an.'),
(5617, 76, 26, 'Geceleyin O\'na secde et ve O\'nu uzun gecelerde yücelt.'),
(5618, 76, 27, 'Bunlar şu geçici (dünya hayatını) seviyorlar ve önlerindeki ağır bir günü ise önemsemiyorlar.'),
(5619, 76, 28, 'Onları biz yarattık ve yerleştirdik. Dilediğimiz zaman da onları benzerleriyle değiştiririz.'),
(5620, 76, 29, 'Bu bir hatırlatmadır: Dileyen, Rabbine varan bir yol tutar.'),
(5621, 76, 30, 'ALLAH dilemedikçe siz dileyemezsiniz. ALLAH Bilendir, Bilgedir.'),
(5622, 76, 31, 'O, dilediğini ve/veya dileyeni Rahmetine sokar. Zalimlere ise acı bir ceza hazırlamıştır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'Andolsun ard arda gönderilenlere,'),
(5624, 77, 2, 'Esip savuranlara,'),
(5625, 77, 3, 'Yaydıkça yayanlara,'),
(5626, 77, 4, 'Ayırdıkça ayıranlara,'),
(5627, 77, 5, 'Mesajı verenlere,'),
(5628, 77, 6, 'Bir müjde veya uyarı olarak...'),
(5629, 77, 7, 'Size söz verilenler kesinlikle gerçekleşecektir.'),
(5630, 77, 8, 'Yıldızlar söndürüldüğü,'),
(5631, 77, 9, 'Göğün yarıldığı,'),
(5632, 77, 10, 'Dağların ufalanıp savrulduğu,'),
(5633, 77, 11, 'Ve elçilere randevu verildiği zaman,'),
(5634, 77, 12, 'Belirlenen gün için...'),
(5635, 77, 13, 'Yani Karar Günü için!'),
(5636, 77, 14, 'O nasıl bir yargılama günüdür bilir misin!'),
(5637, 77, 15, 'Yalanlayanların vay haline o gün!'),
(5638, 77, 16, 'Öncekileri helak etmedik mi?'),
(5639, 77, 17, 'Sonra, diğerlerini de onlara katmadık mı?'),
(5640, 77, 18, 'Suçlulara işte böyle davranırız.'),
(5641, 77, 19, 'Yalanlayanların vay haline o gün!'),
(5642, 77, 20, 'Sizi değersiz bir sudan yaratmadık mı?'),
(5643, 77, 21, 'Ve sonra onu iyi korunan bir yere yerleştirmedik mi?'),
(5644, 77, 22, 'Bilinen bir süreye kadar.'),
(5645, 77, 23, 'Biz ölçtük, biçtik. Biz en iyi biçim verenleriz.'),
(5646, 77, 24, 'Yalanlayanların vay haline o gün!'),
(5647, 77, 25, 'Yeryüzünü bir toplanma yeri yapmadık mı?'),
(5648, 77, 26, 'Yaşayanlar için, ölüler için...'),
(5649, 77, 27, 'Üzerine yüksek dağlar yerleştirip size tatlı su içirmedik mi?'),
(5650, 77, 28, 'Yalanlayanların vay haline o gün!'),
(5651, 77, 29, 'Haydi yalanlamakta olduğunuz şeye doğru yürüyün.'),
(5652, 77, 30, 'Yürüyün, (cehennem alevinin) üç kollu gölgesine doğru...'),
(5653, 77, 31, 'O ne gölgelendirir ne de alevden korur.'),
(5654, 77, 32, 'Kıvılcımlar saçar, saraylar gibi...'),
(5655, 77, 33, '(Rengi de) Sarı deve gibi.'),
(5656, 77, 34, 'Yalanlayanların vay haline o gün!'),
(5657, 77, 35, 'Bu, onların konuşamıyacağı bir gündür.'),
(5658, 77, 36, 'Ve özür dilemeleri için onlara izin de verilmez.'),
(5659, 77, 37, 'Yalanlayanların vay haline o gün!'),
(5660, 77, 38, 'Bu, Karar Günüdür. Sizi ve öncekileri toplamışızdır.'),
(5661, 77, 39, 'Bir planınız varsa bana karşı onu uygulayın!'),
(5662, 77, 40, 'Yalanlayanların vay haline o gün!'),
(5663, 77, 41, 'Erdemliler gölgeliklerde ve pınarlar arasında...'),
(5664, 77, 42, 'Ve canlarının çektiği meyvalar...'),
(5665, 77, 43, 'Yaptıklarınıza karşılık afiyetle yiyip için.'),
(5666, 77, 44, 'İyi davrananları işte böyle ödüllendiririz.'),
(5667, 77, 45, 'Yalanlayanların vay haline o gün!'),
(5668, 77, 46, 'Yiyiniz ve geçici olarak eğleniniz; siz suçlularsınız.'),
(5669, 77, 47, 'Yalanlayanların vay haline o gün!'),
(5670, 77, 48, 'Onlara eğilin dendiğinde eğilmezler.'),
(5671, 77, 49, 'Yalanlayanların vay haline o gün!'),
(5672, 77, 50, 'Artık bundan sonra hangi hadise (söze) inanırlar?');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'Neyi birbirlerine sorup duruyorlar?'),
(5674, 78, 2, 'Büyük haberi mı?'),
(5675, 78, 3, 'Ki onlar onda anlaşmazlık halindedirler.'),
(5676, 78, 4, 'Hayır, öğreneceklerdir.'),
(5677, 78, 5, 'Hayır, kesinlikle öğreneceklerdir.'),
(5678, 78, 6, 'Yapmadık mı yeryüzünü bir beşik,'),
(5679, 78, 7, 'Dağları da birer kazık?'),
(5680, 78, 8, 'Sizi çiftler halinde yarattık.'),
(5681, 78, 9, 'Uykunuzu bir dinlenme yaptık.'),
(5682, 78, 10, 'Geceyi bir örtü yaptık.'),
(5683, 78, 11, 'Gündüzü de geçimi sağlama zamanı olarak belirledik.'),
(5684, 78, 12, 'Ve üstünüze yedi sağlam (göğü) kurduk.'),
(5685, 78, 13, 'Parlayan bir lamba yerleştirdik.'),
(5686, 78, 14, 'Bulutlardan ise şarıl şarıl yağmur indirdik.'),
(5687, 78, 15, 'Ki onunla taneler ve bitkiler.'),
(5688, 78, 16, 'Ve çeşit çeşit bahçeler çıkaralım.'),
(5689, 78, 17, 'Yargılama Günü, belirlenmiş bir vakittedir.'),
(5690, 78, 18, 'Sur\'a üfürüldüğü gün, hepiniz bölük bölük gelirsiniz.'),
(5691, 78, 19, 'Gök açılmış; kapı kapı olmuştur.'),
(5692, 78, 20, 'Dağlar yürütülmüş; serap olmuştur.'),
(5693, 78, 21, 'Cehennem ise gözetlemekte:'),
(5694, 78, 22, 'Azgınlar için bir varış yeri olarak...'),
(5695, 78, 23, 'Orada çağlar boyu kalacaklardır.'),
(5696, 78, 24, 'Orada bir serinlik ve bir içecek bulamazlar;'),
(5697, 78, 25, 'Ancak kaynar su ve acı bir yiyecek hariç.'),
(5698, 78, 26, 'Yaptıklarına uygun bir karşılık olarak.'),
(5699, 78, 27, 'Onlar bir hesap ummuyorlardı.'),
(5700, 78, 28, 'Ve ayetlerimizi, mucizelerimizi yalanladılar.'),
(5701, 78, 29, 'Halbuki biz herşeyi sayıp yazmıştık.'),
(5702, 78, 30, 'Öyleyse tadın, sizin sadece cezanızı arttıracağız.'),
(5703, 78, 31, 'Erdemliler için kurtuluş vardır.'),
(5704, 78, 32, 'Bağlar, bahçeler...'),
(5705, 78, 33, 'Genç ve yaşıt eşler...'),
(5706, 78, 34, 'Dolu kadehler...'),
(5707, 78, 35, 'Orada ne bir boş söz ne de bir yalan işitmezler.'),
(5708, 78, 36, 'Rabbinden bir karşılık; hesaplanmış bir armağandır.'),
(5709, 78, 37, 'Göklerin, yerin ve aralarındakilerin Rabbidir, Rahman\'dır. Hiç bir davada O\'na hitap edemezler.'),
(5710, 78, 38, 'Gün gelecek Ruh ve melekler sıra sıra dururlar. Rahman\'ın izin verdikleri hariç hiç kimse konuşamaz. Onlar da doğruyu söylerler.'),
(5711, 78, 39, 'İşte gerçek gün budur. Dileyen, Rabbine doğru yol tutsun.'),
(5712, 78, 40, 'Biz, sizi yakın bir azap ile uyarmış bulunuyoruz. O gün kişi yapmış olduklarına bakar ve inkarcı ise, "Keşke toprak olsaydım!" der.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'Andolsun söküp çıkaranlara,'),
(5714, 79, 2, 'Rahatça çekenlere,'),
(5715, 79, 3, 'Yüzüp akanlara,'),
(5716, 79, 4, 'Yarışıp birbirlerini geçenlere,'),
(5717, 79, 5, 'Ve böylece emirleri uygulayanlara...'),
(5718, 79, 6, 'O gün o sarsıntı sarsar.'),
(5719, 79, 7, 'Ardından bir diğeri izler.'),
(5720, 79, 8, 'O gün yürekler titrer.'),
(5721, 79, 9, 'Gözleri ise alçalır.'),
(5722, 79, 10, 'Derler ki, "Daha önceki halimize mi döndürüldük?"'),
(5723, 79, 11, '"Biz çürümüş kemikler olduktan sonra ha!?"'),
(5724, 79, 12, '"Öyleyse bu zararına bir dönüştür," derler.'),
(5725, 79, 13, 'O bir tek dürtüşten ibarettir.'),
(5726, 79, 14, 'Onlar uyanıvermişlerdir.'),
(5727, 79, 15, 'Sana Musa\'nın tarihi ulaştı mı?'),
(5728, 79, 16, 'Rabbi, kutsal Tuva vadisinde ona seslenmişti:'),
(5729, 79, 17, '"Firavun\'a git; o azdı."'),
(5730, 79, 18, '"Ona de ki: Arınmayacak mısın?"'),
(5731, 79, 19, '"Seni Rabbine ileteyim de saygılı olasın."'),
(5732, 79, 20, 'Ona büyük mucizeyi gösterdi.'),
(5733, 79, 21, 'Fakat o yalanladı ve karşı geldi.'),
(5734, 79, 22, 'Sonra, sırtını döndü, (aleyhte) çaba gösterdi.'),
(5735, 79, 23, 'Toplayıp, ilan etti.'),
(5736, 79, 24, '"Ben sizin en yüce rabbinizim," dedi.'),
(5737, 79, 25, 'Sonunda, ALLAH onu ahiret ve dünya cezasına çarptı.'),
(5738, 79, 26, 'Kuşkusuz, saygı duyanlar için bunda bir ibret vardır.'),
(5739, 79, 27, 'Siz mi, yoksa gök mü yaratılış açısından daha zorludur? Onu O yaptı.'),
(5740, 79, 28, 'Onu alabildiğine yükseltti ve düzenledi.'),
(5741, 79, 29, 'Gecesini kararttı, sabahını ise ortaya çıkardı.'),
(5742, 79, 30, 'Ve yeri de yumurta biçimine soktu'),
(5743, 79, 31, 'Ondan suyunu ve otlağını çıkardı.'),
(5744, 79, 32, 'Dağları da çaktı.'),
(5745, 79, 33, 'Tüm bunlar sizin ve hayvanlarınızın geçimi için.'),
(5746, 79, 34, 'Büyük baskın geldiği zaman,'),
(5747, 79, 35, 'O gün insan, neyin uğrunda çaba harcadığını anlar.'),
(5748, 79, 36, 'Cehennem göz önüne çıkarılacaktır.'),
(5749, 79, 37, 'Azgınlara,'),
(5750, 79, 38, 'Ve dünya hayatını yeğleyenlere gelince.'),
(5751, 79, 39, 'Gidilecek yer cehennem olacaktır.'),
(5752, 79, 40, 'Rabbinin makamına karşı saygı duyan ve kendini kötü arzulardan alıkoyanlara gelince'),
(5753, 79, 41, 'Gidilecek yer cennet olacaktır.'),
(5754, 79, 42, 'Sana Saat (dünyanın sonu) ne zaman gerçekleşecektir diye soruyorlar.'),
(5755, 79, 43, 'Onu bildirmek, (ey Muhammed) senin görevin değildir.'),
(5756, 79, 44, 'Onun kararı Rabbine aittir.'),
(5757, 79, 45, 'Senin görevin, sadece ondan korkanları uyarmaktır.'),
(5758, 79, 46, 'Onu gördükleri gün, sanki (dünyada) bir akşam veya kuşluk vakti kadar kalmışlardır');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'Surat astı ve döndü;'),
(5760, 80, 2, 'O kör adam geldi diye.'),
(5761, 80, 3, 'Ne bilirsin, belki de o arınacak;'),
(5762, 80, 4, 'Yahut ta öğüt alacak ve ona mesajın yararı dokunacaktı.'),
(5763, 80, 5, 'Kendisini zengin görüp önemsemeyene gelince;'),
(5764, 80, 6, 'Sen ona yöneliyorsun.'),
(5765, 80, 7, 'Onun arınmamasından sana ne?'),
(5766, 80, 8, 'Oysa, sana büyük bir hevesle gelen,'),
(5767, 80, 9, 'Saygı gösterdiği halde,'),
(5768, 80, 10, 'İlgi göstermedin ona.'),
(5769, 80, 11, 'Doğrusu, bu bir hatırlatmadır.'),
(5770, 80, 12, 'Dileyen bundan öğüt alır.'),
(5771, 80, 13, 'Onurlu kitaplardadır,'),
(5772, 80, 14, 'Yüce ve temiz.'),
(5773, 80, 15, 'Elçilerin elleriyle (yazılmıştır).'),
(5774, 80, 16, 'Onurlu ve güzel huylu (elçiler).'),
(5775, 80, 17, 'Yazıklar olsun insana; ne kadar da nankördür!'),
(5776, 80, 18, 'Onu hangi şeyden yaratmıştı?'),
(5777, 80, 19, 'Bir spermadan onu yarattı; ölçü ve biçim verdi.'),
(5778, 80, 20, 'Sonra ona yolu kolaylaştırdı.'),
(5779, 80, 21, 'Sonra onu öldürdü, mezara koydu.'),
(5780, 80, 22, 'Dilediği zaman da onu diriltti.'),
(5781, 80, 23, 'Ne var ki O\'nun kendisine emrettiğini yerine getirmedi.'),
(5782, 80, 24, 'İnsan, yiyeceğine bir baksın!'),
(5783, 80, 25, 'Biz suyu döktükçe döktük.'),
(5784, 80, 26, 'Toprağı yardıkça yardık.'),
(5785, 80, 27, 'Ve orada taneler bitirdik,'),
(5786, 80, 28, 'Üzümler, çayırlar,'),
(5787, 80, 29, 'Zeytinler, hurmalar,'),
(5788, 80, 30, 'İri ve sık ağaçlı bahçeler,'),
(5789, 80, 31, 'Meyveler ve sebzeler...'),
(5790, 80, 32, 'Size ve hayvanlarınıza bir geçimlik olarak.'),
(5791, 80, 33, 'Sonra, o müthiş patlama gerçekleşince,'),
(5792, 80, 34, 'O gün kişi kaçar: kardeşinden,'),
(5793, 80, 35, 'Annesinden, babasından,'),
(5794, 80, 36, 'Eşinden ve çocuklarından...'),
(5795, 80, 37, 'O gün herkesin kendisine yetecek işi vardır.'),
(5796, 80, 38, 'O gün bazı yüzler var ki aydınlık;'),
(5797, 80, 39, 'Güleç, neşeli.'),
(5798, 80, 40, 'O gün bazı yüzler de perişan;'),
(5799, 80, 41, 'Karanlık bürümüştür.'),
(5800, 80, 42, 'İşte onlar inkarcılardır, sapanlardır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'Güneş yuvarlandığı zaman,'),
(5802, 81, 2, 'Yıldızlar sönüp düştüğü zaman,'),
(5803, 81, 3, 'Dağlar yürütüldüğü zaman,'),
(5804, 81, 4, 'En değerli mallar terkedildiği zaman,'),
(5805, 81, 5, 'Yabani hayvanlar toplandığı zaman,'),
(5806, 81, 6, 'Denizler kaynatıldığı zaman,'),
(5807, 81, 7, 'Nefisler/kişiler çiftleştirildiği zaman,'),
(5808, 81, 8, 'Canlı canlı gömülen kız çocuğu sorulduğu zaman:'),
(5809, 81, 9, 'Hangi suçtan ötürü öldürüldü diye.'),
(5810, 81, 10, 'Kayıtlar yayımlandığı zaman,'),
(5811, 81, 11, 'Gök yerinden oynatıldığı zaman,'),
(5812, 81, 12, 'Cehennem alevlendirildiği zaman,'),
(5813, 81, 13, 'Cennet yaklaştırıldığı zaman,'),
(5814, 81, 14, 'Her kişi ne yapıp getirdiğini bilir.'),
(5815, 81, 15, 'Andolsun gizlenen yıldızlara,'),
(5816, 81, 16, 'Akıp deliklerine girenlere.'),
(5817, 81, 17, 'Andolsun kararmaya başlayan geceye,'),
(5818, 81, 18, 'Ve nefes almağa başlayan sabaha,'),
(5819, 81, 19, 'Bu, onurlu bir elçinin sözüdür.'),
(5820, 81, 20, 'Güçlüdür; Yönetimin Sahibi katından yetkilidir.'),
(5821, 81, 21, 'Kendisine uyulmalı ve güvenilmeli.'),
(5822, 81, 22, 'Arkadaşınız deli değildir.'),
(5823, 81, 23, 'Onu apaçık bir ufukta görmüştür.'),
(5824, 81, 24, 'O, hiçbir haberi gizlemiyor.'),
(5825, 81, 25, 'O, kovulan şeytanın sözü olamaz.'),
(5826, 81, 26, 'Öyleyse nereye gidiyorsunuz?'),
(5827, 81, 27, 'Bu, tüm halklara bir mesajdır.'),
(5828, 81, 28, 'Sizden doğru davranmak isteyenler için...'),
(5829, 81, 29, 'Ne dilerseniz, ancak evrenlerin Rabbi olan ALLAH\'ın dilediğine göredir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'Gök yarıldığı zaman,'),
(5831, 82, 2, 'Gezegenler saçıldığı zaman,'),
(5832, 82, 3, 'Denizler akıtılıp taşırıldığı zaman,'),
(5833, 82, 4, 'Mezarların içi dışına çevrildiği zaman,'),
(5834, 82, 5, 'Her kişi, yaptığını ve yapmadığını öğrenecektir.'),
(5835, 82, 6, 'Ey insan, seni Onurlu olan Rabbine karşı aldatan nedir?'),
(5836, 82, 7, 'O ki seni yarattı, düzenledi, uyumlu hale soktu;'),
(5837, 82, 8, 'Dilediği bir biçimde seni oluşturdu.'),
(5838, 82, 9, 'Doğrusu, siz, dini yalanlıyorsunuz.'),
(5839, 82, 10, 'Halbuki üstünüzde koruyucular vardır.'),
(5840, 82, 11, 'Onlar onurlu kayıtçılardır.'),
(5841, 82, 12, 'Yaptıklarınızı bilirler.'),
(5842, 82, 13, 'İyiler nimetler içindedir.'),
(5843, 82, 14, 'Kötüler ise cehennemde.'),
(5844, 82, 15, 'Din Günü oraya girerler.'),
(5845, 82, 16, 'Oradan hiç ayrılamazlar.'),
(5846, 82, 17, 'Din Gününün ne olduğunu bilir misin?'),
(5847, 82, 18, 'Evet, Din Gününün ne olduğunu bilir misin?'),
(5848, 82, 19, 'O gün kimsenin kimseye yardımı dokunmaz. O gün tüm kararlar yalnız ALLAH\'a aittir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'Yazıklar olsun kandıranlara.'),
(5850, 83, 2, 'Halktan bir şey aldıklarında ölçüyü tam uygularlar.'),
(5851, 83, 3, 'Verdikleri zaman ise ölçüyü ve tartıyı eksik tutarlar.'),
(5852, 83, 4, 'Onlar hiç dirileceklerini sanmazlar mı?'),
(5853, 83, 5, 'Büyük bir günde?'),
(5854, 83, 6, 'O gün halk Evrenlerin Rabbinin huzurunda dururlar.'),
(5855, 83, 7, 'Doğrusu, kötülerin yazgısı Siccin\'dedir.'),
(5856, 83, 8, 'Siccin nedir bilir misin?'),
(5857, 83, 9, 'Rakamlanmış bir kitaptır.'),
(5858, 83, 10, 'O gün yalanlayanlara yazıklar olsun.'),
(5859, 83, 11, 'Onlar Din Gününü yalanlar.'),
(5860, 83, 12, 'Onu ancak azgın günahkarlar yalanlar.'),
(5861, 83, 13, 'Kendisine ayetlerimiz iletildiğinde, "Efsane" derdi.'),
(5862, 83, 14, 'Doğrusu, işledikleri günahlar kalplerini kaplamış.'),
(5863, 83, 15, 'Doğrusu, o gün onlar Rab\'lerinden perdelenir.'),
(5864, 83, 16, 'Ve böylece onlar cehenneme atılır.'),
(5865, 83, 17, '"Yalanlamakta olduğunuz şey işte budur" denir.'),
(5866, 83, 18, 'Doğrusu, iyilerin kitabı İlliyyun\'dadır.'),
(5867, 83, 19, 'İlliyyun nedir bilir misin?'),
(5868, 83, 20, 'Rakamlanmış bir kitaptır.'),
(5869, 83, 21, '(Tanrı\'ya) yakın olanlar ona tanık olur.'),
(5870, 83, 22, 'İyiler nimetler içindedir.'),
(5871, 83, 23, 'Koltuklar üzerinde çevreyi seyrederler.'),
(5872, 83, 24, 'Yüzlerinden nimetlerin sevinç ve parıltısını okursun.'),
(5873, 83, 25, 'Çeşni katılmış bir nektardan içirilecekler.'),
(5874, 83, 26, 'Çeşnisi misktir. Yarışanlar işte bunun için yarışsınlar.'),
(5875, 83, 27, 'Onun karışımı Tesnimdir'),
(5876, 83, 28, 'Ki o, (Tanrı\'ya) yakın olanların içtiği bir pınardır.'),
(5877, 83, 29, 'Suçlular, inananlara gülerlerdi.'),
(5878, 83, 30, 'Yanlarından geçtikleri zaman da birbirlerine göz kaş edip küçümserlerdi.'),
(5879, 83, 31, 'Taraftarlarının yanına döndükleri zaman da alaya alırlardı.'),
(5880, 83, 32, 'Onları gördüklerinde, "Bunlar sapıklardır!" derlerdi.'),
(5881, 83, 33, 'Oysa onların üzerine gözcü olarak gönderilmemişlerdi.'),
(5882, 83, 34, 'Bugün de inananlar inkarcılara güler.'),
(5883, 83, 35, 'Koltuklar üzerinde bakarlar:'),
(5884, 83, 36, 'İnkarcılar yaptıklarıyla cezalandılar mı diye.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'Gök çatladığı zaman.'),
(5886, 84, 2, 'Ve doğal yapısı gereğince Rabbine kulak verdiği zaman.'),
(5887, 84, 3, 'Yer dümdüz edildiği,'),
(5888, 84, 4, 'İçindekileri atıp boşalttığı,'),
(5889, 84, 5, 'Ve doğal yapısı gereğince Rabbine kulak verdiği zaman.'),
(5890, 84, 6, 'Ey insan, Rabbine doğru çalışıp çabalarsın ve sonunda O\'nunla karşılaşırsın.'),
(5891, 84, 7, 'Kitabı sağ taraftan verilen,'),
(5892, 84, 8, 'Kolay bir hesaba çekilecek,'),
(5893, 84, 9, 'Ve arkadaşlarına sevinç içinde dönecektir.'),
(5894, 84, 10, 'Kitabı arkasından verilen ise,'),
(5895, 84, 11, 'Yok olmayı arzulayacak,'),
(5896, 84, 12, 'Ve bir ateşte yanacaktır.'),
(5897, 84, 13, 'Oysa arkadaşları arasında sevinçliydi;'),
(5898, 84, 14, 'Bir daha (Rabbine) dönmeyeceğini sanmıştı.'),
(5899, 84, 15, 'Doğrusu, Rabbi onu görmektedir.'),
(5900, 84, 16, 'Andolsun akşamın kızıllığına,'),
(5901, 84, 17, 'Gecenin topladığına,'),
(5902, 84, 18, 'Dolunay halindeki aya,'),
(5903, 84, 19, 'Siz evreden evreye binip geçeceksiniz.'),
(5904, 84, 20, 'Peki onlar neden inanmıyorlar?'),
(5905, 84, 21, 'Onlara Kuran okunduğunda neden kabul etmiyorlar?'),
(5906, 84, 22, 'Aksine, inkarcılar yalanlıyorlar.'),
(5907, 84, 23, 'ALLAH onların sakladıklarını çok iyi bilir.'),
(5908, 84, 24, 'Onları acı bir azapla müjdele.'),
(5909, 84, 25, 'İnanıp erdemli davrananlara ise kesilmez bir ödül var.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'Andolsun galaksiler sahibi göğe.'),
(5911, 85, 2, 'Söz verilen güne,'),
(5912, 85, 3, 'Ve tanığa da tanık olunana da andolsun.'),
(5913, 85, 4, 'Kanyon halkına yazıklar olsun.'),
(5914, 85, 5, 'Tutuşturulmuş ateşin-'),
(5915, 85, 6, 'Başında oturmuşlar,'),
(5916, 85, 7, 'Ve inananlara yaptıkları işkenceyi seyrediyorlar.'),
(5917, 85, 8, 'İnananlardan nefret ediyorlardı. Sadece, onlar Üstün ve Övgüye layık olan ALLAH\'a inandıkları için...'),
(5918, 85, 9, 'Göklerin ve yerin yönetimi O\'na aittir. Ve ALLAH herşeye Tanıktır.'),
(5919, 85, 10, 'İnanan erkeklere ve kadınlara zulüm ve işkencede bulunan ve daha sonra tevbe etmeyenler cehennem azabını haketmişlerdir. Onlar için yakıcı bir azap vardır.'),
(5920, 85, 11, 'İnanan ve erdemli davrananlar ise içlerinden ırmaklar akan cennetleri haketmişlerdir. Büyük başarı budur.'),
(5921, 85, 12, 'Doğrusu, Rabbinin yakalaması pek çetindir.'),
(5922, 85, 13, 'Başlatan ve tekrarlayan O\'dur.'),
(5923, 85, 14, 'O Bağışlayandır, Sevendir.'),
(5924, 85, 15, 'Şanlı yönetimin sahibidir.'),
(5925, 85, 16, 'Dilediğini yapandır.'),
(5926, 85, 17, 'Orduların tarihinden haberin var mı?'),
(5927, 85, 18, 'Firavun\'un, Semud\'un?'),
(5928, 85, 19, 'İnkarcılar, kronik yalanlayıcılardır.'),
(5929, 85, 20, 'ALLAH onları arkalarından kuşatmıştır.'),
(5930, 85, 21, 'Gerçekten, o şanlı bir Kuran\'dır.'),
(5931, 85, 22, 'Korunmuş bir ana levhadadır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'Göğe ve Tarık\'a andolsun.'),
(5933, 86, 2, 'Tarık\'ın ne olduğunu bilir misin?'),
(5934, 86, 3, 'Parlak yıldızdır.'),
(5935, 86, 4, 'Her kişinin üzerinde mutlaka bir koruyucu vardır.'),
(5936, 86, 5, 'İnsan neden yaratılmış olduğuna bir baksın.'),
(5937, 86, 6, 'Atılan bir sıvıdan yaratıldı.'),
(5938, 86, 7, 'Omurga ile kaburga kemikleri arasından çıkar.'),
(5939, 86, 8, 'Kuşkusuz O, onu diriltmeğe gücü yetendir.'),
(5940, 86, 9, 'Gizemlerin ortaya çıkarılacağı gün.'),
(5941, 86, 10, 'Onun ne bir gücü ne de bir yardımcısı olmayacaktır.'),
(5942, 86, 11, 'Geri çeviren göğe andolsun,'),
(5943, 86, 12, 'Ve yarılan yeryüzüne andolsun ki,'),
(5944, 86, 13, 'Bu, ayırdedici bir sözdür.'),
(5945, 86, 14, 'Şakaya alınamaz.'),
(5946, 86, 15, 'Onlar bir plan kuruyor.'),
(5947, 86, 16, 'Ben de bir plan kuruyorum.'),
(5948, 86, 17, 'Öyleyse inkarcılara az bir süre daha tanı.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'Yüce Rabbinin ismini eksikliklerden uzak tut.'),
(5950, 87, 2, 'O ki yarattı, düzene koydu.'),
(5951, 87, 3, 'O ki ölçtü, yol gösterdi.'),
(5952, 87, 4, 'O ki yeşillikler bitirdi.'),
(5953, 87, 5, 'Sonra onu kuruyup kararmış bir samana çevirir.'),
(5954, 87, 6, 'Seni okutacağız; sen de unutmayacaksın.'),
(5955, 87, 7, 'ALLAH\'ın dilediği hariç; O açığı da bilir, gizliyi de.'),
(5956, 87, 8, 'Sana kolayı daha da kolaylaştıracağız.'),
(5957, 87, 9, 'Öğüt yararlı olacaksa öğüt ver.'),
(5958, 87, 10, 'Saygı duyanlar öğüt alacaktır.'),
(5959, 87, 11, 'Mutsuz ise ondan yan çizecektir.'),
(5960, 87, 12, 'Ve büyük ateşe girecektir.'),
(5961, 87, 13, 'Nitekim, orada ne ölecek, ne de yaşayacaktır.'),
(5962, 87, 14, 'Kurtulmuştur arınan,'),
(5963, 87, 15, 'Rabbinin ismini anıp namaz kılan.'),
(5964, 87, 16, 'Ne var ki siz dünya hayatını seçiyorsunuz.'),
(5965, 87, 17, 'Oysa ahiret daha iyi ve süreklidir.'),
(5966, 87, 18, 'Bu, ilk sayfalarda/öğretilerde kayıtlıdır.'),
(5967, 87, 19, 'İbrahim\'in ve Musa\'nın sayfalarında.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'Sana o bunaltanın haberi ulaştı mı?'),
(5969, 88, 2, 'O gün yüzler saygı gösterir.'),
(5970, 88, 3, 'Çalışmıştır, yorgun düşmüştür.'),
(5971, 88, 4, 'Kızgın ateşe girerler.'),
(5972, 88, 5, 'Kaynar bir pınardan içirilirler.'),
(5973, 88, 6, 'Değersiz bir yiyeceğin dışında bir şeyleri yoktur;'),
(5974, 88, 7, 'Ne besler, ne de açlığı giderir.'),
(5975, 88, 8, 'O gün başka yüzler de var ki mutludur.'),
(5976, 88, 9, 'Yaptıklarından ötürü sevinçlidir.'),
(5977, 88, 10, 'Yüksek bir cennettedir.'),
(5978, 88, 11, 'Orada bir saçmalık işitmezler.'),
(5979, 88, 12, 'Orada akan bir kaynak var.'),
(5980, 88, 13, 'Orada yüksek mobilyalar var.'),
(5981, 88, 14, 'Kadehler konulmuş,'),
(5982, 88, 15, 'Yastıklar dizilmiş,'),
(5983, 88, 16, 'Ve halılar serilmiştir.'),
(5984, 88, 17, 'Bakmazlar mı develere, nasıl yaratıldı?'),
(5985, 88, 18, 'Ve göğe, nasıl yükseltildi?'),
(5986, 88, 19, 'Ve dağlara, nasıl dikildi?'),
(5987, 88, 20, 'Ve yere, nasıl döşendi?'),
(5988, 88, 21, 'Hatırlat, çünkü sen hatırlatıcısın.'),
(5989, 88, 22, 'Sen onları zorlayacak değilsin.'),
(5990, 88, 23, 'Fakat kim yüz çevirir ve inkar ederse,'),
(5991, 88, 24, 'ALLAH onu en büyük ceza ile cezalandırır.'),
(5992, 88, 25, 'Onların dönüşleri bizedir.'),
(5993, 88, 26, 'Hesaplarını görmek de bize düşer.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'Andolsun tan vaktine,'),
(5995, 89, 2, 'On geceye,'),
(5996, 89, 3, 'Çifte ve teke,'),
(5997, 89, 4, 'Ve geçmekte olan geceye.'),
(5998, 89, 5, 'Zeka sahipleri için bunlar birer yemin değil midir?'),
(5999, 89, 6, 'Görmedin mi Rabbin ne yaptı Ad halkına?'),
(6000, 89, 7, 'Yüksek kulelere sahip İrem\'e ki;'),
(6001, 89, 8, 'Hiç bir ülkede eşi ortaya konmamıştı?'),
(6002, 89, 9, 'Vadideki kayaları oyan Semud\'a?'),
(6003, 89, 10, 'Ve piramitler sahibi Firavun\'a?'),
(6004, 89, 11, 'Tüm bunlar ülkelerinde azmışlardı.'),
(6005, 89, 12, 'Oralarda kötülükleri yaygınlaştırmışlardı.'),
(6006, 89, 13, 'Nitekim, Rabbin de üstlerine türlü felaketler yağdırdı.'),
(6007, 89, 14, 'Rabbin sürekli gözetlemektedir.'),
(6008, 89, 15, 'Rabbi, sınamak için insana bolca verip sevindirdiği zaman, "Rabbim bana cömert davrandı," der.'),
(6009, 89, 16, 'Ancak ne zaman ki rızkını kısarak onu sınarsa, "Rabbim beni küçük düşürdü," der.'),
(6010, 89, 17, 'Hayır! Doğrusu siz öksüze cömert davranmıyorsunuz?'),
(6011, 89, 18, 'Yoksulu yedirmeye birbirinizi teşvik etmiyorsunuz.'),
(6012, 89, 19, 'Mirası da hak gözetmeden yiyorsunuz.'),
(6013, 89, 20, 'Parayı/malı da çok fazla seviyorsunuz.'),
(6014, 89, 21, 'Doğrusu, yer çarpılıp paralandığı zaman,'),
(6015, 89, 22, 'Rabbin, dizi dizi meleklerle birlikte geldiği zaman,'),
(6016, 89, 23, 'Ki cehennem de o gün getirilmiştir. İşte o gün insan anlayacaktır. Artık anlamanın kendisine ne yararı var ki!'),
(6017, 89, 24, '"Keşke bu hayatım için önceden bir şeyler yapsaydım," der.'),
(6018, 89, 25, 'O gün, O\'nun cezası gibi bir cezayı kimse veremez.'),
(6019, 89, 26, 'O\'nun vurduğu bağ gibisini de kimse bağlayamaz.'),
(6020, 89, 27, 'Ey doygunluğa ermiş kişi,'),
(6021, 89, 28, 'Hoşnut olarak ve hoşnut olunarak Rabbine dön.'),
(6022, 89, 29, 'Kullarımın arasına hoşgeldin.'),
(6023, 89, 30, 'Cennetime hoşgeldin.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'And içerim bu kente,'),
(6025, 90, 2, 'Ki sen bu kentte oturmaktasın.'),
(6026, 90, 3, 'Doğurana ve doğurduğuna da andolsun.'),
(6027, 90, 4, 'İnsanı zorluklar arasında (direnmesi için) yarattık.'),
(6028, 90, 5, 'Kendisine güç yetiremiyeceğimizi mi sanıyor?'),
(6029, 90, 6, '(Övünerek) "Çok para harcadım," diyor.'),
(6030, 90, 7, 'Kimsenin kendisini görmediğini mi sanıyor?'),
(6031, 90, 8, 'Ona vermedik mi: İki göz,'),
(6032, 90, 9, 'Bir dil ve iki dudak?'),
(6033, 90, 10, 'Ona iki yolu göstermedik mi?'),
(6034, 90, 11, 'Ne var ki zor yola katlanamadı.'),
(6035, 90, 12, 'Zor yolun ne olduğunu bilir misin?'),
(6036, 90, 13, 'Köleleri özgürlüklerine kavuşturmaktır;'),
(6037, 90, 14, 'Kıtlık anında doyurmaktır:'),
(6038, 90, 15, 'Akraba bir öksüzü,'),
(6039, 90, 16, 'Yahut düşkün bir yoksulu...'),
(6040, 90, 17, 'Dahası, birbirlerine sabır ve sevgiyi öğütleyen inananlardan olmaktır.'),
(6041, 90, 18, 'Nitekim mutlular onlardır.'),
(6042, 90, 19, 'Ayet ve mucizelerimizi inkar edenlerse talihsizlerdir.'),
(6043, 90, 20, 'Onlar ateşe kapatılacaklardır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'Andolsun güneşe ve onun aydınlığına,'),
(6045, 91, 2, 'Onu izleyen aya,'),
(6046, 91, 3, 'Onu açığa çıkaran gündüze,'),
(6047, 91, 4, 'Onu örten geceye,'),
(6048, 91, 5, 'Göğe ve onu kurana,'),
(6049, 91, 6, 'Yere ve onu yuvarlayıp döşeyene,'),
(6050, 91, 7, 'Nefse ve onu düzenleyene,'),
(6051, 91, 8, 'Sonra da ona kötülüğünü ve erdemliliğini bildirene andolsun ki;'),
(6052, 91, 9, 'Onu temizleyen kurtulmuştur.'),
(6053, 91, 10, 'Onu başıboş bırakan ise zarar etmiştir.'),
(6054, 91, 11, 'Semud (halkı), azgınlığı yüzünden yalanladı.'),
(6055, 91, 12, 'En azgınları ayaklanmıştı.'),
(6056, 91, 13, 'ALLAH\'ın elçisi, onlara, "ALLAH\'ın devesine ve onun suyuna dokunmayın," demişti.'),
(6057, 91, 14, 'Onu yalanlayıp deveyi boğazladılar. Bunun üzerine Rab\'leri suçlarından ötürü onları silip yerle bir etti.'),
(6058, 91, 15, 'Ne var ki hâlâ onların sonlarından ders alınmıyor');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'Andolsun bürüdüğü zaman geceye,'),
(6060, 92, 2, 'Ortaya çıktığı zaman gündüze,'),
(6061, 92, 3, 'Erkeği ve dişiyi yaratana andolsun ki,'),
(6062, 92, 4, 'İşleriniz çeşit çeşittir.'),
(6063, 92, 5, 'Kim verir ve erdemli davranır,'),
(6064, 92, 6, 'Ve iyiyi, güzeli doğrularsa,'),
(6065, 92, 7, 'Ona iyice kolaylaştırırız.'),
(6066, 92, 8, 'Fakat, kim cimrilik edip zenginlik taslar,'),
(6067, 92, 9, 'Ve iyiyi, güzeli yalanlarsa,'),
(6068, 92, 10, 'Onu da zora yöneltiriz.'),
(6069, 92, 11, 'Düştüğü vakit kurtaramaz parası/malı onu.'),
(6070, 92, 12, 'Doğruya biz iletiriz;'),
(6071, 92, 13, 'Sonu da ilki de biz kontrol ederiz.'),
(6072, 92, 14, 'Ben sizi alevli bir ateşe karşı uyardım.'),
(6073, 92, 15, 'Oraya talihsiz olandan başkası girmez.'),
(6074, 92, 16, 'O ki yalanladı ve sırtını döndü.'),
(6075, 92, 17, 'Erdemli ise ondan uzak tutulacaktır.'),
(6076, 92, 18, 'O ki malını vererek temizlenir;'),
(6077, 92, 19, 'Hiç kimseden de buna karşılık bir iyilik beklemez.'),
(6078, 92, 20, 'Sadece En Yüce olan Rabbinin rızasını gözetir.'),
(6079, 92, 21, 'Kendisi de yakında mutlu olacaktır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'Andolsun kuşluk vaktine,'),
(6081, 93, 2, 'Ve dingin olduğu zaman geceye,'),
(6082, 93, 3, 'Rabbin seni ne bıraktı ne de sana darıldı.'),
(6083, 93, 4, 'Senin için son (ahiret) ilkten (dünyadan) daha iyidir.'),
(6084, 93, 5, 'Rabbin yakında sana verecek ve sen de beğeneceksin.'),
(6085, 93, 6, 'Seni bir öksüz olarak bulup barındırmadık mı?'),
(6086, 93, 7, 'Seni bir sapık olarak bulup doğruya iletmedik mi?'),
(6087, 93, 8, 'Seni fakir bulup zengin etmedik mi?'),
(6088, 93, 9, 'Öyleyse, öksüzü yüzüstü bırakma.'),
(6089, 93, 10, 'Dilenciyi de azarlama.'),
(6090, 93, 11, 'Ve Rabbinin sana yaptığı iyilikleri de anlat.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'Göğsünü genişletip seni sakinleştirmedik mi?'),
(6092, 94, 2, 'Üzerindeki yükünü indirmedik mi?'),
(6093, 94, 3, 'Ki belini bükmüştü.'),
(6094, 94, 4, 'Senin şanını yükseltmedik mi?'),
(6095, 94, 5, 'Elbette, kolaylık zorlukla birliktedir.'),
(6096, 94, 6, 'Evet, kolaylık zorlukla birliktedir.'),
(6097, 94, 7, 'Ne zaman fırsat bulursan uğraş ver.'),
(6098, 94, 8, 'Ve sadece Rabbini arzula.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'Andolsun incire ve zeytine,'),
(6100, 95, 2, 'Sina Dağına,'),
(6101, 95, 3, 'Ve andolsun bu güvenilir kente (Mekke\'ye) ki;'),
(6102, 95, 4, 'Biz insanı en güzel bir biçimde yarattık.'),
(6103, 95, 5, 'Sonra onu aşağıların aşağısı kıldık.'),
(6104, 95, 6, 'Ancak inanıp erdemli davrananlar hariç; onlara kesintisiz bir ödül vardır.'),
(6105, 95, 7, 'Artık neden hala bu dini yalanlıyorsun.'),
(6106, 95, 8, 'ALLAH bilgelerin En Bilgesi değil midir?');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'Yaratan Rabbinin ismiyle oku.'),
(6108, 96, 2, 'O, insanı bir embriyodan yarattı.'),
(6109, 96, 3, 'Oku, Rabbin En Cömert/Yüce olandır.'),
(6110, 96, 4, 'Kalem yoluyla öğretir.'),
(6111, 96, 5, 'İnsana bilmediklerini öğretti.'),
(6112, 96, 6, 'Doğrusu, insan azar;'),
(6113, 96, 7, 'Zenginlik taslayarak.'),
(6114, 96, 8, 'Elbette, dönüş senin Rabbinedir.'),
(6115, 96, 9, 'Gördün mü, şu engelleyeni:'),
(6116, 96, 10, 'Namaz kılarken bir kulu?'),
(6117, 96, 11, 'Ne dersin, o doğru yolu izleseydi?'),
(6118, 96, 12, 'Yahut erdemliliği öğütleseydi?'),
(6119, 96, 13, 'Ne dersin, o yalanlayıp yüz çevirdiyse?'),
(6120, 96, 14, 'Bilmez mi ki ALLAH herşeyi görmektedir.'),
(6121, 96, 15, 'Doğrusu, buna son vermezse, yakalarız perçeminden,'),
(6122, 96, 16, 'O yalancı ve günahkar perçeminden.'),
(6123, 96, 17, 'O zaman haydi çağırsın kurultayını.'),
(6124, 96, 18, 'Biz de zebanileri çağıracağız..'),
(6125, 96, 19, 'Asla, ona uyma; secde et ve yaklaş!');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'Biz onu Kudret Gecesinde indirdik.'),
(6127, 97, 2, 'Kudret Gecesi ne kadar önemlidir, bilir misin?'),
(6128, 97, 3, 'Kudret Gecesi bin aydan daha hayırlıdır.'),
(6129, 97, 4, 'Melekler ve Ruh (Cebrail) o gece Rab\'lerinin izniyle tüm buyrukları yerine getirmek için inerler.'),
(6130, 97, 5, 'O, tan yeri ağarıncaya kadar esenliktir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'Kitap halkının inkarcıları ve putperestler, kendilerine açık delil/kanıt gelmesine rağmen yollarını terketmezler.'),
(6132, 98, 2, 'ALLAH\'ın bir elçisi kendilerine arınmış sahifeler okuyor.'),
(6133, 98, 3, 'Onda dosdoğru öğretiler vardır.'),
(6134, 98, 4, 'Gerçek şu ki, kendilerine kitap verilmiş olanlar, ancak onlara açık delil geldikten sonra ayrılığa düştüler.'),
(6135, 98, 5, 'Oysa onlardan, dini sadece ALLAH\'a ait kılan tektanrıcılar (monoteist) olarak O\'na kulluk etmeleri, namazı gözetmeleri ve zekatı vermeleri istenmişti. İşte dosdoğru din budur.'),
(6136, 98, 6, 'Kitap halkının inkarcıları ve putperestler, cehennem ateşinin içindedirler ve orada ebedi kalıcıdırlar. Onlar, yaratıkların en kötüsüdür.'),
(6137, 98, 7, 'İnanıp erdemli davrananlar ise yaratıkların en iyisidir.'),
(6138, 98, 8, 'Rableri katındaki ödülleri, içinden ırmaklar akan cennetlerdir. Orada ebedi olarak kalacaklardır. ALLAH onlardan hoşnut olmuş, onlar da O\'ndan hoşnut olmuşlardır. Rabbine saygı gösterenin ödülü işte böyledir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'Yerin korkunç bir depremle sarsıldığı,'),
(6140, 99, 2, 'Yerin ağırlıklarını fışkırttığı,'),
(6141, 99, 3, 'İnsanın da, "Buna ne oluyor?" dediği zaman,'),
(6142, 99, 4, 'İşte o gün (yer), haberlerini anlatacaktır.'),
(6143, 99, 5, 'Çünkü Rabbin ona vahyetmiştir.'),
(6144, 99, 6, 'O gün, insanlar yapmış oldukları kendilerine gösterilsin diye bölükler halinde çıkagelirler.'),
(6145, 99, 7, 'Kim bir atom ağırlığı iyilikte bulunursa onu görür.'),
(6146, 99, 8, 'Ve kim bir atom ağırlığı kötülükte bulunursa onu görür.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'Andolsun soluyarak aşanlara,'),
(6148, 100, 2, 'Ateş saçarak gidenlere,'),
(6149, 100, 3, 'Sabah sabah akın edenlere,'),
(6150, 100, 4, 'Toz koparanlara,'),
(6151, 100, 5, 'Ve topluluğun içine dalanlara ki;'),
(6152, 100, 6, 'İnsan Rabbine karşı çok nankördür.'),
(6153, 100, 7, 'Üstelik buna da tanıktır.'),
(6154, 100, 8, 'O mala çok düşkündür.'),
(6155, 100, 9, 'Bilmez mi ki, mezardakiler ortaya atıldığı,'),
(6156, 100, 10, 'Ve göğüslerde gizlenenler açığa çıkarıldığı zaman,'),
(6157, 100, 11, 'Evet o gün Rab\'leri onların herşeyinden haberlidir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'Şok.'),
(6159, 101, 2, 'Hem de ne şok!'),
(6160, 101, 3, 'Şoke edenin niteliği sana bildirildi mi?'),
(6161, 101, 4, 'O gün halk, yayılmış kelebekler gibi olur.'),
(6162, 101, 5, 'Dağlar da atılmış yün gibi olur.'),
(6163, 101, 6, 'Kimin tartıları ağır gelirse,'),
(6164, 101, 7, 'O, mutlu bir hayat içinde olacaktır.'),
(6165, 101, 8, 'Kimin de tartıları hafif gelirse,'),
(6166, 101, 9, 'Onun da anası uçurumdur.'),
(6167, 101, 10, 'O uçurumun ne olduğunu bilir misin?'),
(6168, 101, 11, 'O, kızgın bir ateştir!');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'Çoğaltma yarışı sizi alabildiğine meşgul etti;'),
(6170, 102, 2, 'Mezarlara varıncaya (ölünceye) kadar...'),
(6171, 102, 3, 'Doğrusu, yakında bileceksiniz.'),
(6172, 102, 4, 'Elbette, yakında bileceksiniz.'),
(6173, 102, 5, 'Doğrusu, kesin olarak bilseydiniz.'),
(6174, 102, 6, 'Cehennemi görürdünüz.'),
(6175, 102, 7, 'Zaten, onu gözünüzle kesin olarak göreceksiniz.'),
(6176, 102, 8, 'Sonra, o gün (size verilen) nimetlerden sorulacaksınız.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'Andolsun akıp giden zamana ki;'),
(6178, 103, 2, 'İnsanlar zarardadır.'),
(6179, 103, 3, 'Ancak inanıp erdemli davrananlar, birbirlerine gerçeği öğütleyenler ve birbirlerine sabretmeyi öğütleyenler hariç.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'Yazıklar olsun her dedikoducuya ve iftiracıya.'),
(6181, 104, 2, 'Ki o para/mal biriktirir ve onu sayar durur.'),
(6182, 104, 3, 'Sanki parası/malı onu ölümsüz yapacakmış gibi.'),
(6183, 104, 4, 'Asla; onu Hutama\'ya atacağım.'),
(6184, 104, 5, 'Hutama\'nın ne olduğunu bilir misin?'),
(6185, 104, 6, 'ALLAH\'ın tutuşturulmuş ateşidir.'),
(6186, 104, 7, 'Ki beyinlere işler.'),
(6187, 104, 8, 'Onların üzerine kapanmıştır.'),
(6188, 104, 9, 'Yükselen direkler arasında.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'Rabbinin fil halkını ne hale soktuğuna dikkat ettin mi?'),
(6190, 105, 2, 'Planlarını boşa çıkarıp onları şaşkına çevirmedi mi?'),
(6191, 105, 3, 'Onların üstüne kuş sürüleri gönderdi.'),
(6192, 105, 4, 'Onlara çamurdan sertleşmiş taşlar atıyorlardı.'),
(6193, 105, 5, 'Ve böylece onları yenmiş ekin gibi perişan etmişti.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'Kureyş (bu mesajı) bağrına basmalı;'),
(6195, 106, 2, 'Kış ve yaz kervanlarını sevdikleri gibi.'),
(6196, 106, 3, 'Bu evin Rabbine kulluk etsinler.'),
(6197, 106, 4, 'O ki onları açlıktan kurtarıp doyurdu ve korkudan güvene kavuşturdu.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'Dini yalanlayanı gördün mü?'),
(6199, 107, 2, 'İşte, öksüze kötü davranan odur.'),
(6200, 107, 3, 'Yoksulları doyurmaya da yanaşmaz.'),
(6201, 107, 4, 'Yazıklar olsun o namaz kılanlara,'),
(6202, 107, 5, 'Onlar ki namazlarından tümüyle habersizdirler.'),
(6203, 107, 6, 'Onlar ki gösteriş yaparlar.'),
(6204, 107, 7, 'Ve yardımı da engellerler.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'Biz sana bolca nimetler verdik.'),
(6206, 108, 2, 'Öyleyse Rabbin için namaz kıl ve kendini ada/yönel.'),
(6207, 108, 3, 'Asıl mahfolacak olanlar sana kin duyanlardır.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'De ki, "Ey inkarcılar."'),
(6209, 109, 2, '"Ben, sizin taptıklarınıza tapmam."'),
(6210, 109, 3, '"Siz de benim taptığıma tapmazsınız."'),
(6211, 109, 4, '"Ben asla sizin taptığınıza tapacak değilim."'),
(6212, 109, 5, '"Siz de benim taptığıma tapacak değilsiniz."'),
(6213, 109, 6, '"Sizin dininiz size, benim dinim bana."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'ALLAH\'ın yardımı ve zafer geldiği zaman,'),
(6215, 110, 2, 'Halkın akın akın ALLAH\'ın dinine girdiğini göreceksin.'),
(6216, 110, 3, 'Rabbini överek yücelt ve ondan bağışlanma dile. O, tevbeleri sürekli kabul edendir.');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'Ateş kürükleyenin elleri kahrolsun, zaten kendisi kahroldu.'),
(6218, 111, 2, 'Ne parası, ne de bir kazancı ona yaramadı.'),
(6219, 111, 3, 'O, alev sahibi bir ateşe girecektir.'),
(6220, 111, 4, 'Odun taşıyan (zulmun ateşine yakıt hazırlayan) karısı da.'),
(6221, 111, 5, 'Boynunda, dikenlerden/liften örülmüş bir ip bulunduğu (kötülükleri boynuna dolanmış) halde...');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'De ki, "O ALLAH bir tektir."'),
(6223, 112, 2, '"ALLAH Sonsuz ve Mutlaktır."'),
(6224, 112, 3, '"Doğurmamıştır, doğurulmamıştır."'),
(6225, 112, 4, '"Hiçbir kimse de O\'na denk değildir."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'De ki, "Şafağın Rabbine sığınırım."'),
(6227, 113, 2, '"Yarattığı şeylerin şerrinden."'),
(6228, 113, 3, '"Çöktüğü zaman karanlığın şerrinden."'),
(6229, 113, 4, '"Arabozucuların, sözleşmeleri bozanların şerrinden."'),
(6230, 113, 5, '"Kıskandığı vakit kıskananın şerrinden."');
INSERT INTO `tr_yuksel` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'De ki, "Sığınırım halkın Rabbine,"'),
(6232, 114, 2, '"Halkın Yöneticisine,"'),
(6233, 114, 3, '"Halkın Tanrısına,"'),
(6234, 114, 4, '"Şerrinden sinsice fısıldayanların."'),
(6235, 114, 5, '"Onlar halkın göğsüne fısıldarlar."'),
(6236, 114, 6, '"Onlar hem cinlerden hem de halktandır."');
