INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'ناھايىتى شەپقەتلىك ۋە مېھرىبان اﷲ نىڭ ئىسمى بىلەن باشلايمەن'),
(2, 1, 2, 'جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر'),
(3, 1, 3, 'اﷲ ناھايىتى شەپقەتلىك ۋە مېھرىباندۇر'),
(4, 1, 4, 'قىيامەت كۈنىنىڭ ئىگىسىدۇر'),
(5, 1, 5, '(رەببىمىز) ساڭىلا ئىبادەت قىلىمىز ۋە سەندىنلا ياردەم تىلەيمىز'),
(6, 1, 6, 'بىزنى توغرا يولغا باشلىغىن'),
(7, 1, 7, 'غەزىپىڭگە يولۇققانلارنىڭ ۋە ئازغانلارنىڭ يولىغا ئەمەس، سەن ئىنئام قىلغانلارنىڭ يولىغا (باشلىغىن)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'ئەلىف، لام، مىم'),
(9, 2, 2, 'بۇ كىتابتا (يەنى قۇرئاندا) ھېچ شەك يوق، (ئۇ) تەقۋادارلارغا يېتەكچىدۇر'),
(10, 2, 3, 'ئۇلار غەيبكە ئىشىنىدۇ، نامازنى ئادا قىلىدۇ، ئۇلار بىز بەرگەن مال - مۈلۈكتىن (خۇدا يولىغا) سەرپ قىلىدۇ'),
(11, 2, 4, 'ئۇلار ساڭا نازىل قىلىنغان كىتابقا، سەندىن ئىلگىرىكى (پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىشىنىدۇ ۋە ئاخىرەتكە شەكسىز ئىشىنىدۇ'),
(12, 2, 5, 'ئەنە شۇلار پەرۋەردىگارىنىڭ توغرا يولىدا بولغۇچىلاردۇر، ئەنە شۇلار بەختكە ئېرىشكۈچىلەردۇر'),
(13, 2, 6, 'شۈبھىسىزكى، كاپىرلارنى ئاگاھلاندۇرامسەن، ئاگاھلاندۇرمامسەن، بەرىبىر، ئۇلار ئىمان ئېيتمايدۇ'),
(14, 2, 7, 'اﷲ ئۇلارنىڭ دىللىرىنى ۋە قۇلاقلىرىنى پېچەتلىۋەتكەن (ئىماننىڭ نۇرى كىرمەيدۇ)، ئۇلارنىڭ كۆزلىرى پەردىلەنگەن (ھەقىقەتنى كۆرمەيدۇ)، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ'),
(15, 2, 8, 'كىشىلەر ئارىسىدا اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشەندۇق دېگۈچىلەر بار، ھەقىقەتتە ئۇلار ئىشەنمەيدۇ (يەنى ئاغزىدا ئىشەندۇق دېگىنى بىلەن، كۆڭلىدە ئىشەنمەيدۇ)'),
(16, 2, 9, 'ئۇلار اﷲ نى ۋە مۆمىنلەرنى ئالدىماقچى بولىدۇ، ھەقىقەتتە ئۇلار تۇيماستىن ئۆزلىرىنىلا ئالدايدۇ'),
(17, 2, 10, 'ئۇلارنىڭ دىللىرىدا كېسەل (يەنى مۇناپىقلىق ۋە شەكلىنىش) بار، اﷲ ئۇلارنىڭ كېسىلىنى كۈچەيتىۋەتتى؛ يالغان سۆزلىگەنلىكلىرى (يەنى يالغاندىن ئىماننى دەۋا قىلغانلىقلىرى ۋە اﷲ نىڭ ئايەتلىرىنى مەسخىرە قىلغانلىقلىرى) ئۈچۈن ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(18, 2, 11, 'ئۇلارغا: «يەر يۈزىدە بۇزغۇنچىلىق قىلماڭلار» دېيىلسە، «بىز ئىسلاھ قىلغۇچىلارمىز» دەيدۇ'),
(19, 2, 12, 'بىلىڭلاركى، ئۇلار ھەقىقەتەن بۇزغۇنچىلاردۇر، لېكىن بۇنى ئۆزلىرى تۇيمايدۇ'),
(20, 2, 13, 'ئۇلارغا (يەنى مۇناپىقلارغا): «ئىمان ئېيتقان كىشىلەردەك (يەنى ساھابىلەردەك چىن كۆڭلۈڭلار بىلەن) ئىمان ئېيتىڭلار» دېيىلسە، «بىز ئىمان ئېيتقان ئەخمەقلەرگە ئوخشاش ئىمان ئېيتامدۇق؟» دەيدۇ. بىلىڭلاركى ئۇلارنىڭ ئۆزلىرىلا ئەخمەقلەر، لېكىن (بۇنى) ئۇلار تۇيمايدۇ'),
(21, 2, 14, 'ئۇلار مۆمىنلەر بىلەن ئۇچراشقىنىدا: «بىز ئىمان ئېيتتۇق» دېيىشىدۇ، شاياتۇنلىرى (يەنى مۇناپىق كاتتىباشلىرى) بىلەن يالغۇز جايدا تېپىشقاندا بولسا «بىز ھەقىقەتەن سىلەر بىلەن بىللىمىز، پەقەت (تىلىمىزنىڭ ئۇچىدىلا ئىمان ئېيتىپ قويۇپ) مۆمىنلەرنى مەسخىرە قىلىمىز» دەيدۇ'),
(22, 2, 15, 'مەسخىرە قىلغانلىقلىرى ئۈچۈن اﷲ ئۇلارنى جازالايدۇ، ئۇلارنى گۇمراھلىقلىرىدا قويۇپ بېرىدۇكى، ئۇلار تېڭىرقىغان ھالدا يۈرۈشىدۇ'),
(23, 2, 16, 'ئەنە شۇلار ھىدايەتنى بېرىپ گۇمراھلىقنى ئالدى. شۇڭا سودىسى پايدا كەلتۈرمىدى، ئۇلار ھىدايەت تاپقۇچى بولمىدى'),
(24, 2, 17, 'ئۇلار گوياكى (كېچىدە ئىسسىنىش ۋە يورۇقلۇق ئېلىش ئۈچۈن) ئوت ياققان كىشىلەرگە ئوخشايدۇ، ئوت ئۇلارنىڭ ئەتراپىنى يورۇتقاندا، اﷲ ئۇلارنىڭ (ئوتىنىڭ) يورۇقىنى ئۆچۈرۈۋەتتى، ئۇلارنى (ئەتراپىدىكى ھېچ نەرسىنى) كۆرەلمەيدىغان قاراڭغۇلۇقتا قالدۇردى'),
(25, 2, 18, 'ئۇلار گاستۇر (يەنى گاس ئاڭلىمىغاندەك، ياخشىلىقنى ئاڭلىمايدۇ)، گاچىدۇر (يەنى گاچا سۆزلىيەلمىگەندەك، ياخشى ئىشنى سۆزلىيەلمەيدۇ)، كوردۇر (يەنى كور بولۇپ قالغاندەك، توغرا يولنى كۆرمەيدۇ)، شۇڭا ئۇلار (گۇمراھلىقتىن) قايتمايدۇ'),
(26, 2, 19, 'ياكى ئۇلار زۇلمەتلىك، گۈلدۈرمامىلىق ۋە چاقماقلىق قاتتىق يامغۇردا قالغان، گويا چاقماق سوقۇۋېتىپ ئۆلۈپ كېتىشتىن قورقۇپ، قۇلاقلىرىنى بارماقلىرى بىلەن ئېتىۋالغان كىشىلەرگە ئوخشايدۇ. اﷲ كاپىرلارنى (يەنى ئۇلارنىڭ ھەممە ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر'),
(27, 2, 20, 'چاقماق چاققاندا ئۇلار كۆز نۇرىدىن ئايرىلىپ قالغىلى تاس قالىدۇ. ئۇلار چاقماق يورۇقىدا مېڭىۋالىدۇ؛ قاراڭغۇلۇق قاپلىغاندا تۇرۇپ قالىدۇ، اﷲ خالىسا ئىدى، ئۇلارنى ئاڭلاش ۋە كۆرۈش قۇۋۋىتىدىن ئەلۋەتتە مەھرۇم قىلاتتى. اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر'),
(28, 2, 21, 'ئى ئىنسانلار! تەقۋادارلاردىن بولۇشۇڭلار ئۈچۈن، سىلەرنى ۋە سىلەردىن بۇرۇنقىلار (يەنى ئۆتكەنكى ئۈممەتلەر) نى ياراتقان پەرۋەردىگارىڭلارغا ئىبادەت قىلىڭلار'),
(29, 2, 22, 'اﷲ سىلەرگە زېمىننى تۆشەك ۋە ئاسماننى بىنا (يەنى يۇلتۇزلارنىڭ زىچلىقىدا بىناغا ئوخشاش) قىلىپ بەردى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، سىلەرگە رىزىق بولۇش ئۈچۈن يامغۇر ئارقىلىق تۈرلۈك مېۋىلەرنى ئۆستۈرۈپ بەردى. ئەمدى اﷲ قا شېرىك كەلتۈرمەڭلار، ھالبۇكى، سىلەر (اﷲ نىڭ شېرىكى يوقلۇقىنى) بىلىپ تۇرىسىلەر'),
(30, 2, 23, 'بەندىمىز (مۇھەممەد) گە بىز نازىل قىلغان قۇرئاندىن شەكلەنسەڭلار، قۇرئانغا ئوخشاش بىرەر سۈرىنى مەيدانغا چىقىرىپ بېقىڭلار (قۇرئانغا تەئەررۇز قىلىشتا) اﷲ تىن باشقا ياردەمچىڭلارنىڭ ھەممىسىنى (ياردەمگە) چاقىرىڭلار، (قۇرئان ئىنساننىڭ سۆزى دېگەن گېپىڭلاردا) راستچىل بولساڭلار'),
(31, 2, 24, 'ئەگەر مۇنداق قىلالمىساڭلار - ھەرگىزغۇ قىلالمايسىلەر - كاپىرلار ئۈچۈن تەييارلانغان، ئىنسان ۋە تاشلار يېقىلغۇ بولغان دوزاختىن ساقلىنىڭلار'),
(32, 2, 25, '(ئى مۇھەممەد!) ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا ئۇلارنىڭ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرىدىغانلىقى بىلەن خۇش خەۋەر بەرگىن. ئۇلار جەننەتنىڭ بىرەر مېۋىسىدىن رىزىقلاندۇرۇلغان چاغدا: «بۇنىڭ بىلەن بۇرۇن (دۇنيادىمۇ) رىزىقلاندۇرۇلغان ئىدۇق» دەيدۇ. ئۇلارغا كۆرۈنۈشى دۇنيانىڭ مېۋىلىرىگە ئوخشايدىغان، تەمى ئوخشىمايدىغان مېۋىلەر بېرىلىدۇ. جەننەتلەردە ئۇلارغا پاك جۈپتىلەر (يەنى ھۆرلەر) بېرىلىدۇ، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ'),
(33, 2, 26, 'اﷲ ھەقىقەتەن پاشا ۋە ئۇنىڭدىنمۇ كىچىك نەرسىلەرنى مىسال قىلىپ كەلتۈرۈشتىن تارتىنىپ قالمايدۇ، مۆمىنلەر بولسا ئۇنى پەرۋەردىگارى تەرىپىدىن نازىل بولغان ھەقىقەت دەپ بىلىدۇ؛ كاپىرلار بولسا: «اﷲ بۇ نەرسىلەرنى مىسال قىلىپ كەلتۈرۈشتىن نېمىنى ئىرادە قىلىدۇ؟» دەيدۇ. اﷲ بۇ مىسال بىلەن نۇرغۇن كىشىلەرنى (ئۇنى ئىنكار قىلغانلىقتىن) ئازدۇرىدۇ ۋە نۇرغۇن كىشىلەرنى (ئۇنى تەستىقلىغانلىقتىن) ھىدايەت قىلىدۇ؛ شۇ ئارقىلىق پەقەت پاسىقلارنىلا ئازدۇرىدۇ'),
(34, 2, 27, 'پاسىقلار اﷲ ۋەدە ئالغاندىن كېيىن بەرگەن ۋەدىسىنى بۇزىدۇ. اﷲ ئۇلاشقا بۇيرۇغان شەيئىنى ئۈزۈپ قويىدۇ (يەنى سىلە - رەھىم قىلمايدۇ)، يەر يۈزىدە بۇزۇقچىلىق قىلىدۇ؛ ئەنە شۇلار زىيان تارتقۇچىلاردۇر'),
(35, 2, 28, 'اﷲ نى قانداقمۇ ئىنكار قىلىسىلەركى، جانسىز ئىدىڭلار، سىلەرگە جان بەردى (يەنى ئاتاڭلارنىڭ پۇشتىدا، ئاناڭلارنىڭ قارنىدا ئابىمەنىي ئىدىڭلار)، كېيىن (ئەجىلىڭلار توشقاندا) اﷲ جېنىڭلارنى ئالىدۇ، ئاندىن كېيىن تىرىلدۈرىدۇ، ئاندىن كېيىن اﷲ نىڭ دەرگاھىغا (ھېساب بېرىش ئۈچۈن) قايتۇرۇلىسىلەر'),
(36, 2, 29, 'اﷲ يەر يۈزىدىكى ھەممە نەرسىنى سىلەر (نىڭ پايدىلىنىشىڭلار) ئۈچۈن ياراتتى، ئاندىن ئاسماننى يارىتىشقا يۈزلىنىپ، ئۇنى قۇسۇرسىز يەتتە ئاسمان قىلىپ تاماملىدى. اﷲ ھەرنەرسىنى بىلگۈچىدۇر'),
(37, 2, 30, 'ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە: «مەن يەر يۈزىدە خەلىپە (يەنى ئورۇنباسار) يارىتىمەن» دېدى. پەرىشتىلەر: «يەر يۈزىدە بۇزۇقچىلىق قىلىدىغان، قان تۆكىدىغان (شەخسنى) خەلىپە قىلامسەن؟ ھالبۇكى بىز سىنى پاك دەپ مەدھىيىلەيمىز، مۇقەددەس دەپ مەدھىيىلەيمىز» دېدى. اﷲ: «مەن ھەقىقەتەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن» دېدى'),
(38, 2, 31, 'اﷲ پۈتۈن شەيئىلەرنىڭ ناملىرىنى ئادەمگە ئۆگەتتى، ئاندىن ئۇلارنى پەرىشتىلەرگە كۆرسىتىپ: «(خەلىپە بولۇشقا ئادەمگە قارىغاندا بىز ھەقلىق دەيدىغان قارشىڭلاردا) راستچىل بولساڭلار، بۇ شەيئىلەرنىڭ ناملىرىنى ماڭا ئېيتىپ بېرىڭلار» دېدى'),
(39, 2, 32, 'پەرىشتىلەر: «سېنى (پۈتۈن كەمچىلىكلەردىن) پاك دەپ تونۇيمىز. بىز سەن بىلدۈرگەندىن باشقىنى بىلمەيمىز، ھەقىقەتەن سەن ھەممىنى بىلگۈچىدۇرسەن، ھېكمەت بىلەن ئىش قىلغۇچىدۇرسەن» دېدى'),
(40, 2, 33, 'اﷲ: «ئى ئادەم! ئۇلارغا بۇ نەرسىلەرنىڭ ناملىرىنى ئېيتىپ بەرگىن» دېدى. ئادەم ئۇلارغا بۇ نەرسىلەرنىڭ ناملىرىنى ئېيتىپ بەرگەن چاغدا، اﷲ: «مەن سىلەرگە، ئاسمانلاردىكى ۋە زېمىندىكى غەيبلەرنى ھەقىقەتەن بىلىپ تۇرىمەن، ئاشكارا ۋە يوشۇرۇن ئىشىڭلارنى بىلىپ تۇرىمەن، دېمىگەنمىدىم» دېدى'),
(41, 2, 34, 'ئۆز ۋاقتىدا پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى، ئىبلىس (سەجدە قىلىشتىن) باش تارتتى، تەكەببۇرلۇق قىلدى، ئۇ كاپىرلاردىن بولۇپ كەتتى'),
(42, 2, 35, 'بىز (ئادەمگە): «ئى ئادەم! سەن خوتۇنۇڭ (يەنى ھەۋۋا) بىلەن جەننەتتە تۇرۇڭلار! جەننەتتىكى نەرسىلەردىن خالىغىنىڭلارچە كەڭتاشا يەپ - ئىچىڭلار، بۇ دەرەخكە يېقىنلاشماڭلار (يەنى مېۋىسىدىن يېمەڭلار)، بولمىسا (ئۆزۈڭلارغا) زۇلۇم قىلغۇچىلاردىن بولۇپ قالىسىلەر» دېدۇق'),
(43, 2, 36, 'شەيتان ئۇ ئىككىسىنى تېيىلدۇردى (يەنى مەنئى قىلىنغان دەرەخنىڭ مېۋىسىنى يېگۈزۈپ خاتالاشتۇردى)، تۇرۇۋاتقان جەننەتتىن چىقاردى. (ئادەمگە، ھەۋۋاغا، ئىبلىسقا) «بىر - بېرىڭلار بىلەن ئۆچەكەشكەن ھالدا (جەننەتتىن يەر يۈزىگە) چۈشۈڭلار، يەر يۈزىدە ۋاقىتلىق (ئەجىلىڭلار توشقۇچە) يەرلىشىڭلار ۋە (نېمەتلەردىن) بەھرىمەن بولۇڭلار» دېدۇق'),
(44, 2, 37, 'ئادەم پەرۋەردىگارىدىن بىرقانچە سۆز تەلىم ئالدى (يەنى گۇناھىنىڭ كەچۈرۈلۈشى ئۈچۈن ئوقۇيدىغان دۇئا ئۇنىڭغا ئىلھام ئارقىلىق بىلدۈرۈلدى)، اﷲ ئۇنىڭ تەۋبىسىنى قوبۇل قىلدى. اﷲ ھەقىقەتەن تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر (بەندىلىرىگە) ناھايىتى مېھرىباندۇر'),
(45, 2, 38, '(ئۇلارغا) «ھەممىڭلار بۇ يەردىن چۈشۈڭلار، سىلەرگە مەن تەرەپتىن بىر يول كۆرسەتكۈچى (يەنى پەيغەمبەر) كېلىدۇ، يولۇمغا ئەگەشكەنلەرگە (ئاخىرەتتە) قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ» دېدۇق'),
(46, 2, 39, 'كاپىرلار ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالىدۇ'),
(47, 2, 40, 'ئى ئسرائىل ئەۋلادى! مېنىڭ سىلەرگە بەرگەن نېمىتىمنى ياد ئېتىڭلار، ماڭا بەرگەن (ئىمان ئېيتىش ۋە تائەت - ئىبادەت قىلىش ھەققىدىكى) ۋەدەڭلارغا ۋاپا قىلىڭلار، مەنمۇ سىلەرگە قىلغان (ساۋاب بېرىش ھەققىدىكى) ۋەدەمگە ۋاپا قىلىمەن، مەندىنلا قورقۇڭلار'),
(48, 2, 41, 'مەن نازىل قىلغان تەۋراتنى تەستىق قىلغۇچى قۇرئانغا ئىمان كەلتۈرۈڭلار، قۇرئاننى بىرىنچى بولۇپ ئىنكار قىلغۇچى بولماڭلار، ئايەتلىرىمنى ئەرزان باھادا سېتىۋەتمەڭلار، ماڭىلا تەقۋادارلىق قىلىڭلار'),
(49, 2, 42, 'ھەقنى باتىلغا (يەنى اﷲ نازىل قىلغان ھەقىقەتنى ئۆزۈڭلار ئويدۇرۇپ چىققان نەرسىگە) ئارىلاشتۇرماڭلار، ھەقنى بىلىپ تۇرۇپ يوشۇرماڭلار'),
(50, 2, 43, 'نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، رۇكۇ قىلغۇچىلار بىلەن بىللە رۇكۇ قىلىڭلار'),
(51, 2, 44, 'كىشىلەرنى ياخشى ئىشقا بۇيرۇپ ئۆزۈڭلارنى ئۇنتۇمسىلەر؟ ھالبۇكى، سىلەر كىتاب (يەنى تەۋرات) نى ئوقۇپ تۇرىسىلەر، چۈشەنمەمسىلەر'),
(52, 2, 45, 'سەۋر قىلىش، ناماز ئوقۇش ئارقىلىق (اﷲ تىن) ياردەم تىلەڭلار. ناماز اﷲ تىن قورققۇچىلار (يەنى كەمتەرلىك بىلەن اﷲ قا بويسۇنغۇچىلار) دىن باشقىلارغا ھەقىقەتەن مۇشەققەتلىك ئىشتۇر'),
(53, 2, 46, 'اﷲ تىن قورققۇچىلار پەرۋەردىگارىغا چوقۇم مۇلاقات بولىدىغانلىقىغا ۋە اﷲ نىڭ دەرگاھىغا چوقۇم قايتىپ بارىدىغانلىقىغا جەزمەن ئىشىنىدۇ'),
(54, 2, 47, 'ئى ئىسرائىل ئەۋلادى! سىلەرگە بەرگەن نېمىتىمنى ۋە سىلەرنى (بىر زامانلاردا) تامام جاھان ئەھلىدىن ئۈستۈن قىلغانلىقىمنى ياد ئېتىڭلار'),
(55, 2, 48, 'كىشى كىشىگە ئەسقاتمايدىغان، ھېچ كىشىنىڭ شاپائىتى قوبۇل قىلىنمايدىغان، ھېچ كىشىدىن فىدىيە ئېلىنمايدىغان، ئۇلار (يەنى كاپىرلار ۋە گۇناھكار بەندىلەر) ياردەمگە ئېرىشەلمەيدىغان كۈندىن قورقۇڭلار'),
(56, 2, 49, 'ئۆز ۋاقتىدا سىلەرنى قاتتىق قىينىغان، ئوغۇللىرىڭلارنى بوغۇزلاپ، قىزلىرىڭلارنى (ئىشقا سېلىش ئۈچۈن) تىرىك قالدۇرغان پىرئەۋن گۇرۇھىدىن سىلەرنى قۇتقۇزدۇق. بۇ، پەرۋەردىگارىڭلارنىڭ (ياخشى ئادەم بىلەن يامان ئادەمنى ئاجرىتىدىغان) چوڭ سىنىقى ئىدى'),
(57, 2, 50, 'ئۆز ۋاقتىدا دېڭىزنى يېرىپ سىلەرنى قۇتقۇزدۇق (يەنى دېڭىزنىڭ سۈيىنى ئوتتۇرىدىن بۆلۈپ، قۇرۇق يول ھاسىل قىلىپ، شۇ يەردىن سىلەرنى سالامەت ئۆتكۈزدۇق) ۋە پىرئەۋن گۇرۇھىنى كۆز ئالدىڭلاردا غەرق قىلدۇق'),
(58, 2, 51, 'ئۆز ۋاقتىدا مۇساغا 40 كېچە (كۈتكەندىن كېيىن تەۋراتنى نازىل قىلىشنى) ۋەدە قىلدۇق. ئۇ سىلەردىن ئايرىلغاندىن كېيىن، ئۆزۈڭلارغا زۇلۇم قىلىپ، موزاينى مەبۇد قىلىۋالدىڭلار'),
(59, 2, 52, 'شۇنىڭدىن كېيىن، شۈكۈر قىلسۇن دەپ، سىلەرنى ئەپۇ قىلدۇق'),
(60, 2, 53, 'ئۆز ۋاقتىدا سىلەرنى ھىدايەت تاپسۇن دەپ، مۇساغا ھەقنى باتىلدىن ئايرىغۇچى كىتابنى (يەنى تەۋراتنى) بەردۇق'),
(61, 2, 54, 'ئۆز ۋاقتىدا مۇسا ئۆز قەۋمىگە: «ئى قەۋمىم! سىلەر موزاينى مەبۇد قىلىپ، ھەقىقەتەن ئۆزۈڭلارغا زۇلۇم قىلدىڭلار، ياراتقۇچىڭلارغا تەۋبە قىلىڭلار، موزايغا چوقۇنمىغانلار چوقۇنغانلارنى ئۆلتۈرسۇن» دېدى. مۇنداق قىلىش ياراتقۇچىڭلارنىڭ دەرگاھىدا سىلەر ئۈچۈن ياخشىدۇر. اﷲ تەۋبەڭلارنى قوبۇل قىلدى. اﷲ تەۋبىنى ھەقىقەتەن بەكمۇ قوبۇل قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(62, 2, 55, 'ئۆز ۋاقتىدا: «ئى مۇسا! اﷲ نى ئوپئوچۇق كۆرمىگىچە ساڭا ھەرگىز ئىشەنمەيمىز» دېدىڭلار - دە، (سىلەرگە نازىل بولغان ئازابنى) كۆرۈپ تۇرغىنىڭلار ھالدا سىلەرنى چاقماق سوقتى'),
(63, 2, 56, 'ئاندىن كېيىن، سىلەرنى شۈكۈر قىلسۇن، دەپ ئۆلگىنىڭلاردىن كېيىن قايتا تىرىلدۈردۇق'),
(64, 2, 57, 'سىلەرگە بۇلۇتنى سايىۋەن قىلىپ بەردۇق، سىلەرگە تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق. (بىز سىلەرگە) «رىزىق قىلىپ بەرگەن پاك، شېرىن نەرسىلەردىن يەپ - ئىچىڭلار» (دېدۇق). ئۇلار (نېمەتلىرىمىزگە نانكورلۇق قىلىش بىلەن) بىزگە ئەمەس، پەقەت ئۆزلىرىگىلا زۇلۇم قىلدى'),
(65, 2, 58, 'ئۆز ۋاقتىدا (مۇسا ئارقىلىق ئەجدادلىرىڭلارغا): «بۇ شەھەرگە (يەنى بەيتۇلمۇقەددەسكە) كىرىڭلار، ئۇ يەردە خالىغىنىڭلارچە كەڭتاشا يەپ - ئىچىڭلار، (بەيتۇلمۇقەددەس) دەرۋازىسىدىن (شۈكۈر قىلىش يۈزىسىدىن) سەجدە قىلغان ھالدا كىرىڭلار، (پەرۋەردىگارىمىز) گۇناھلىرىمىزنى كەچۈرگىن دەڭلار، گۇناھىڭلارنى مەغپىرەت قىلىمىز، ياخشىلارغا تېخىمۇ ئارتۇق ساۋاب بېرىمىز» دېدۇق'),
(66, 2, 59, 'زۇلۇم قىلغۇچىلار ئۆزلىرىگە سۆزلەنگەن سۆزنى باشقا سۆزگە ئۆزگەرتىۋەتتى، زۇلۇم قىلغۇچىلار يولدىن چىققانلىقلىرى (يەنى اﷲ قا ئاسىيلىق قىلغانلىقلىرى) ئۈچۈن، ئۇلارغا ئاسماندىن ئازاب چۈشۈردۇق'),
(67, 2, 60, 'ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمى ئۈچۈن سۇ تەلەپ قىلغان ئىدى، بىز ئۇنىڭغا: «ھاساڭ بىلەن تاشنى ئۇرغىن» دېدۇق. تاشتىن 12 بۇلاق ئېتىلىپ چىقتى، ھەممە ئادەم (يەنى قەبىلە) ئۆزىنىڭ سۇ ئىچىدىغان ئورنىنى بىلدى، (ئۇلارغا) «اﷲ بەرگەن رىزىقتىن يەڭلار ۋە ئىچىڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلماڭلار» (دېدۇق)'),
(68, 2, 61, 'ئۆز ۋاقتىدا (سىنا چۆلىدە تەرەنجىبىن ۋە بۆدۈنە بىلەنلا ئوزۇقلىنىۋاتقىنىڭلاردا) سىلەر: «ئى مۇسا! بىز بىر خىل يېمەكلىككە چىداپ تۇرالمايمىز، بىز ئۈچۈن پەرۋەردىگارىڭغا دۇئا قىلغىن، بىزگە زېمىننىڭ كۆكتاتلىرىدىن تەرخەمەك، سامساق، يېسىمۇق ۋە پىيازلارنى ئۆستۈرۈپ بەرسۇن» دېدىڭلار، مۇسا: «سىلەر ياخشىنى ناچارغا تېگىشەمسىلەر؟ (يەنى پىياز، سامساق، كۆكتاتنى تەرەنجىبىن بىلەن بۆدۈنىدىن ئارتۇق كۆرەمسىلەر؟) بىرەر شەھەرگە كىرىڭلار، (شۇ يەردە) تىلىگىنىڭلار بار» دېدى. ئۇلار خارلىقتا، موھتاجلىقتا قالدى. اﷲ نىڭ غەزىپىگە تېگىشلىك بولدى. بۇ ئۇلارنىڭ اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى، پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى تۈپەيلىدىن بولدى. بۇ اﷲ قا ئاسىيلىق قىلغانلىقلىرى ۋە ھەددىدىن ئاشقانلىقلىرى تۈپەيلىدىن بولدى'),
(69, 2, 62, 'شۈبھىسىزكى، مۆمىنلەر، يەھۇدىيلار، ناسارالار (ئارىسىدىن) ۋە پەرىشتىلەرگە، يۇلتۇزلارغا چوقۇنغۇچىلار ئارىسىدىن اﷲ قا، ئاخىرەت كۈنىگە ئىمان كەلتۈرگەن ۋە ياخشى ئەمەللەرنى قىلغانلار پەرۋەردىگارىنىڭ قېشىدا ئەجرىدىن بەھرىمەن بولىدۇ. ئۇلارغا (ئاخىرەتتە) قورقۇنچ ۋە غەم - قايغۇ يوقتۇر'),
(70, 2, 63, 'ئۆز ۋاقتىدا، بىز سىلەردىن چىن ۋەدە ئالغان ۋە تۇر تېغىنى ئۈستۈڭلارغا تىكلەپ قويغان، سىلەرگە: «(دۇنيادا ھالاك بولۇشتىن، ئاخىرەتتە ئازابقا قېلىشتىن) ساقلىنىشىڭلار ئۈچۈن بىز بەرگەن كىتابنى مەھكەم تۇتۇڭلار (يەنى تەۋراتقا ئەمەل قىلىڭلار)، ئۇنىڭدىكى ئەھكاملارنى ئېسىڭلاردا ساقلاڭلار» (دېگەن ئىدۇق)'),
(71, 2, 64, 'شۇنىڭدىن كېيىن (بەرگەن ۋەدەڭلاردىن) يۈز ئۆرۈدۈڭلار، سىلەرگە اﷲ نىڭ پەزلى - مەرھەمىتى بولمىسا ئىدى، چوقۇم زىيان تارتقۇچىلاردىن بولاتتىڭلار'),
(72, 2, 65, 'ئاراڭلاردىكى شەنبە كۈنى (بېلىق تۇتماسلىق) توغرىسىدىكى شەرىئەت چەكلىمىسىدىن چىقىپ كەتكۈچىلەرنى (قانداق قىلغانلىقىمىزنى)، ئەلۋەتتە بىلىسىلەر، بىز ئۇلارغا: «خار مايمۇن بولۇپ كېتىڭلار» دېدۇق'),
(73, 2, 66, 'ئۇنى (يەنى ئۇلارغا بېرىلگەن بۇ جازانى) زامانداشلىرى ۋە كېيىنكىلەر ئۈچۈن ئىبرەت، تەقۋادارلار ئۈچۈن ۋەز - نەسىھەت قىلدۇق'),
(74, 2, 67, 'ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمىگە: «اﷲ ھەقىقەتەن سىلەرنى بىر كالا بوغۇزلاشقا بۇيرۇيدۇ» دېگەن ئىدى. ئۇلار: «بىزنى مەسخىرە قىلىۋاتامسەن؟» دېدى. مۇسا: «جاھىللاردىن (يەنى مەسخىرە قىلغۇچىلاردىن) بولۇپ قېلىشتىن اﷲ قا سېغىنىپ پاناھ تىلەيمەن» دېدى'),
(75, 2, 68, 'ئۇلار: «بىز ئۈچۈن پەرۋەردىگارىڭغا ئىلتىجا قىلغىنكى، بىزگە قانداق كالا ئىكەنلىكىنى بايان قىلسۇن» دېدى. مۇسا: «اﷲ ئۇنى قېرىمۇ ئەمەس، ياشمۇ ئەمەس، ئوتتۇرا ياش بولسۇن دەپ ئېيتتى، سىلەر بۇيرۇلغاننى قىلىڭلار» دېدى'),
(76, 2, 69, 'ئۇلار: «بىز ئۈچۈن پەرۋەردىگارىڭغا ئىلتىجا قىلغىنكى، بىزگە ئۇنىڭ رەڭگىنى بايان قىلسۇن» دېدى. مۇسا ئېيتتىكى، «اﷲ ھەقىقەتەن ئۇنىڭ رەڭگى قارىغۇچىلارنى زوقلاندۇرىدىغان ساپسېرىق بولسۇن دېدى»'),
(77, 2, 70, 'ئۇلار: «پەرۋەردىگارىڭغا ئىلتىجا قىلغىن، ئۇنىڭ قانداق ئىكەنلىكىنى بىزگە بايان قىلسۇن، (يەنى دېھقانچىلىققا ئىشلىتىلگەن كالىمۇ، ياكى سەمرىتىش ئۈچۈن بېقىلغان كالىمۇ؟) بۇ بىزگە مۈجمەل بولۇپ قالدى (بۇ ئېنىق بولىدىغان بولسا) خۇدا خالىسا (ئۇنى) بىز چوقۇم تاپىمىز» دېدى'),
(78, 2, 71, 'مۇسا: «اﷲ ئۇنىڭ يەر ھەيدەپ، ئېكىن سۇغۇرۇپ كۆندۈرۈلگەن بولماسلىقىنى، بېجىرىم، ئالىسى يوق (يەنى رەڭگىدە سېرىقلىقتىن باشقا رەڭ يوق) بولۇشىنى ئېيتتى» دېدى. ئۇلار: «ئەمدى تولۇق بايان قىلدىڭ» دېدى ئۇلار (كالىنى) تېپىپ كېلىپ بوغۇزلىدى، (ئۇلار سوئال سوراۋەرگەنلىكتىن) بۇنى ئورۇنلىيالمىغىلى تاس قالدى'),
(79, 2, 72, 'ئۆز ۋاقتىدا سىلەر بىر كىشىنى ئۆلتۈرگەن ۋە (قاتىلنىڭ كىملىكى) توغرىسىدا دەتالاش قىلىشقان ئىدىڭلار. سىلەر يوشۇرماقچى بولغان نەرسىنى (يەنى قاتىلنىڭ ئىشىنى) اﷲ ئاشكارىلىغۇچىدۇر'),
(80, 2, 73, '(سىلەرگە مۇسا ئارقىلىق) دېدۇقكى، كالىنىڭ بىر پارچىسى بىلەن ئۆلۈكنى ئۇرۇڭلار (ئۇ تىرىلىپ قاتىلنى ئېيتىپ بېرىدۇ)، اﷲ ئۆلۈكلەرنى شۇنداق (يەنى كۆز ئالدىڭلاردا بۇ ئۆلۈكنى تىرىلدۈرگەندەك) تىرىلدۈرىدۇ، سىلەرنى چۈشەنسۇن دپ قۇدرىتىنىڭ ئالامەتلىرىنى سىلەرگە كۆرسىتىدۇ'),
(81, 2, 74, '(ئى يەھۇدىيلار جامائەسى!) شۇنىڭدىن كېيىن (يەنى شانلىق مۆجىزىلەرنى كۆرگەندىن كېيىن) دىلىڭلار قېتىپ كەتتى، تاشتەك ياكى تاشتىنمۇ قاتتىق قېتىپ كەتتى. تاشلار ئارىسىدا ئىچىدىن (سۇ ئېتىلىپ چىقىپ) ئېرىقلار ھاسىل بولىدىغانلىرى بار، يېرىلىپ ئارىسىدىن سۇ چىقىدىغانلىرىمۇ بار؛ اﷲ تىن قورقۇپ (تاغ چوققىلىرىدىن) دومىلاپ چۈشىدىغانلىرىمۇ بار (دېمەك، تاش يۇمشايدۇ، سىلەرنىڭ دىلىڭلار بولسا يۇمشىمايدۇ ۋە تەسىرلەنمەيدۇ)، اﷲ قىلمىشىڭلاردىن غاپىل ئەمەستۇر'),
(82, 2, 75, '(ئى مۆمىنلەر جامائەسى! دەۋىتىڭلار ئارقىلىق) يەھۇدىيلارنىڭ ئىمان ئېيتىشىنى ئۈمىد قىلامسىلەر؟ ھالبۇكى، ئۇلاردىن بىر گۇرۇھ ئادەم، اﷲ نىڭ كالامىنى ئاڭلاپ چۈشەنگەندىن كېيىن، ئۇنى بىلىپ تۇرۇپ (قەستەن) ئۆزگەرتىۋەتتى'),
(83, 2, 76, 'ئۇلار مۆمىنلەر بىلەن ئۇچراشقانلىرىدا: «ئىمان ئېيتتۇق» دەيتتى. بىر - بىرى بىلەن يالغۇز جايدا تېپىشقانلىرىدا: «پەرۋەردىگارىڭلار قېشىدا مۆمىنلەر سىلەرگە قارشى پاكىت كۆرسەتسۇن دەپ اﷲ سىلەرگە بىلدۈرگەن ۋەھيىنى (يەنى مۇھەممەد ئەلەيھسسالامنىڭ تەۋراتتىكى سۈپەتلىرىنى) ئۇلارغا سۆزلەپ بېرەمسىلەر؟ (بۇنى) چۈشەنمەمسىلەر!» دەيتتى'),
(84, 2, 77, 'يەھۇدىيلار ئۆزلىرىنىڭ يوشۇرۇن ۋە ئاشكارا ئىشلىرىنى اﷲ نىڭ بىلىپ تۇرىدىغانلىقىنى ئۇقمامدۇ؟'),
(85, 2, 78, 'ئۇلارنىڭ ساۋاتسىزلىرى كىتابنى (يەنى تەۋراتنى) ئۇقمايدۇ، (ئەھبارلىرى ئويدۇرۇپ چىققان) ئويدۇرمىلارنىلا بىلىدۇ، گۇمان بىلەنلا ئىش قىلىدۇ'),
(86, 2, 79, 'كىتابنى ئۆز قوللىرى بىلەن يېزىپ ئۇنى ئاز پۇلغا سېتىش (يەنى دۇنيانىڭ ئازغىنا مەنپەئىتى) ئۈچۈن، بۇ اﷲ تەرىپىدىن نازىل بولغان دېگۈچىلەرگە ۋاي! قولى بىلەن يازغانلىرى (يەنى تەۋراتنى ئۆزگەرتكەنلىكلىرى) ئۈچۈن ئۇلارغا ۋاي! (بۇنىڭ بىلەن) ئېرىشكەن (ھارام) نەرسىلىرى ئۈچۈن ئۇلارغا ۋاي'),
(87, 2, 80, 'ئۇلار: «بىزنى دوزاخ ئوتى ساناقلىق كۈنلەرلا كۆيدۈرىدۇ» دەيدۇ. (ئۇلارغا) «سىلەرگە اﷲ شۇنداق قىلىشقا ۋەدە بەرگەنمۇ، (ۋەدە بەرگەن بولسا) اﷲ ۋەدىسىگە ھەرگىزمۇ خىلاپلىق قىلمايدۇ، ياكى ئۆزۈڭلار بىلمەيدىغاننى اﷲ نامىدىن دەۋاتامسىلەر؟» دېگىن'),
(88, 2, 81, 'ئۇنداق ئەمەس (سىلەرنى دوزاخ ئوتى كۆيدۈرىدۇ)، گۇناھ قىلغان ۋە گۇناھقا چۆمۈپ كەتكەنلەر ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالىدۇ'),
(89, 2, 82, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ئەھلى جەننەتتۇر. ئۇلار جەننەتتە مەڭگۈ قالىدۇ'),
(90, 2, 83, 'ئۆز ۋاقتىدا بىز ئىسرائىل ئەۋلادىدىن اﷲ تىن باشقىغا ئىبادەت قىلماسلىققا، ئاتا - ئانىغا، خىش - ئەقرىبالارغا، يېتىملەرگە، مىسكىنلەرگە ياخشىلىق قىلىشقا، كىشىلەرگە ياخشى سۆز قىلىشقا، ناماز ئۆتەشكە، زاكات بېرىشكە ئەھدە ئالدۇق. ئاراڭلاردىكى ئازغىنا كىشىدىن باشقىڭلار ئەھدىدىن يېنىۋالدىڭلار، سىلەر ھەمىشە (ئەھدەڭلاردىن) يېنىۋالىسىلەر'),
(91, 2, 84, 'ئۆز ۋاقتىدا بىر - بىرىڭلارنىڭ قېنىنى تۆكمەسلىككە، بىر - بىرىڭلارنى يۇرتلىرىڭلاردىن ھەيدەپ چىقارماسلىققا، سىلەردىن چىن ئەھدە ئالغان ئىدۇق. كېيىن بۇنى (يەنى ئەھدىنى) ئېتىراپ قىلغان ئىدىڭلار، بۇنىڭغا ئۆزۈڭلار گۇۋاھسىلەر'),
(92, 2, 85, 'كېيىن سىلەر (ئەھدىنى بۇزۇپ) بىر - بىرىڭلارنى ئۆلتۈردۈڭلار، ئىچىڭلاردىن بىر تۈركۈم كىشىلەرنى يۇرتىدىن ھەيدەپ چىقاردىڭلار، گۇناھ ۋە دۈشمەنلىك قىلىش بىلەن، ئۇلارنىڭ قارشى تەرىپىدىكىلەرگە ياردەم بەردىڭلار، ئۇلار ئەسىر چۈشۈپ كەلسە، فىدىيە بېرىپ قۇتقۇزۇۋالىسىلەر، ھالبۇكى، ئۇلارنى يۇرتلىرىدىن ھەيدەپ چىقىرىش سىلەرگە ھارام قىلىنغان ئىدى (يەنى بىر - بىرىڭلارنى ئۆلتۈرۈش ۋە يۇرتىدىن ھەيدەپ چىقىرىشنى راۋا كۆرۈسىلەرۇ، ئۇلارنىڭ ئەسىرگە چۈشۈپ دۈشمەنلىرىڭلارنىڭ قولىدا بولۇپ قېلىشىنى راۋا كۆرمەيسىلەر). سىلەر كىتابنىڭ (يەنى تەۋراتنىڭ) بىر قىسىم ئەھكاملىرىغا ئىشىنىپ، بىر قىسىم ئەھكاملىرىنى ئىنكار قىلامسىلەر؟ سىلەردىن شۇنداق قىلغانلارنىڭ جازاسى پەقەت ھاياتىي دۇنيادا خارلىققا قېلىش، قىيامەت كۈنى قاتتىق ئازابقا دۇچار بولۇشتۇر. اﷲ قىلمىشىڭلاردىن غاپىل ئەمەستۇر'),
(93, 2, 86, 'ئەنە شۇلار ئاخىرەتنى بېرىپ، دۇنيا تىرىكچىلىكىنى سېتىۋالغان كىشىلەردۇر. شۇنىڭ ئۈچۈن، ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ، ئۇلارغا ياردەممۇ قىلىنمايدۇ'),
(94, 2, 87, 'شەك - شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋرات) بەردۇق. ئۇنىڭدىن كېيىن ئارقىمۇئارقا پەيغەمبەرلەر ئەۋەتتۇق، مەريەم ئوغلى ئىساغا مۆجىزىلەر بەردۇق، ھەمدە ئۇنى روھۇلقۇددۇس (يەنى جىبرىئىل) بىلەن يۆلىدۇق. ھەرقاچان بىرەر پەيغەمبەر كۆڭلۈڭلارغا ياقمايدىغان بىرنەرسە ئېلىپ كەلسە، تەكەببۇرلۇق قىلىۋېرەمسىلەر؟ بىر قىسىم پەيغەمبەرلەرنى ئىنكار قىلدىڭلار، يەنە بىر قىسىم پەيغەمبەرلەرنى ئۆلتۈردۈڭلار'),
(95, 2, 88, 'ئۇلار: «دىللىرىمىز پەردىلەنگەن» دېدى. ئۇنداق ئەمەس، ئۇلارنىڭ كۇفرى سەۋەبلىك اﷲ ئۇلارغا لەنەت قىلدى، ئۇلاردىن ئىمان ئېيتىدىغانلار ناھايىتى ئاز'),
(96, 2, 89, 'ئۇلارغا اﷲ تەرىپىدىن ئۇلاردىكى كىتاب (تەۋرات) نى تەستىقلايدىغان كىتاب (قۇرئان) نازىل بولغان چاغدا (ئۇنىڭغا ئىشەنمىدى). ئىلگىرى ئۇلار (مۇھەممەد ئەلەيھىسسالامنى ۋاسىتە قىلىپ) كاپىرلارغا قارشى ئۆزلىرىگە ياردەم كېلىشىنى تىلەيتتى، ئۇلار بىلىدىغان (يەنى تەۋراتتا سۈپىتى بايان قىلىنغان) پەيغەمبەر كەلگەن چاغدا ئۇنى ئىنكار قىلدى. اﷲ كاپىرلارغا (يەنى پەيغەمبەرلەرنىڭ تۈگەنچىسىنى ئىنكار قىلغان يەھۇدىيلارغا) لەنەت قىلدى'),
(97, 2, 90, 'يەھۇدىيلارنىڭ اﷲ نازىل قىلغان قۇرئاننى ئىنكار قىلىشى اﷲ نىڭ ئۆز پەزلىنى (يەنى پەيغەمبەرلىكنى) خالىغان بەندىسىگە چۈشۈرگەنلىكىگە ھەسەت قىلىش يۈزىسىدىندۇر. ئۇلارنىڭ ئۆزلىرىنى (يەنى ئىمانىنى) كۇفرىغا سېتىشى ئەجەب يامان ئىشتۇر. ئۇلار ئۈستى - ئۈستىگە غەزەپكە تېگىشلىك بولدى. كاپىرلار خارلىغۇچى ئازابقا دۇچار بولىدۇ'),
(98, 2, 91, 'ئۇلارغا: «اﷲ نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ئىمان كەلتۈرۈڭلار» دېيىلسە، ئۇلار: «ئۆزىمىزگە نازىل قىلىنغان كىتابقا (يەنى تەۋراتقا) ئىمان كەلتۈرىمىز» دەيدۇ. ئۇنىڭدىن كېيىنكى كىتابقا (يەنى قۇرئانغا) ئىشەنمەيدۇ، ھالبۇكى، ئۇ (يەنى قۇرئان) ھەقتۇر، ئۇلارنىڭ قولىدىكى كىتابنى تەستىق قىلغۇچىدۇر. (ئۇلارغا) «سىلەر (تەۋراتقا) ئىشىنىدىغان بولساڭلار، ئىلگىرى نېمە ئۈچۈن اﷲ نىڭ پەيغەمبەرلىرىنى ئۆلتۈردۈڭلار؟» دېگىن'),
(99, 2, 92, 'مۇسا سىلەرگە ھەقىقەتەن ئېنىق مۆجىزىلەرنى كەلتۈردى، مۇسا (تۇر تېغىغا كەتكەن) دىن كېيىن، ئۆزۈڭلارغا زۇلۇم قىلىپ موزاينى مەبۇد قىلىۋالدىڭلار'),
(100, 2, 93, 'ئۆز ۋاقتىدا بىز سىلەردىن (تەۋراتقا ئەمەل قىلىشقا) چىن ۋەدە ئالغان ئىدۇق، سىلەرگە: «مەن نازىل قىلغان كىتابنى مەھكەم تۇتۇڭلار (بولمىسا ئۈستۈڭلارغا تاغنى تاشلايمىز)» دەپ تۇر تېغىنى ئۈستۈڭلارغا تىكلىدۇق ۋە (تەۋراتقا) قۇلاق سېلىڭلار (دېدۇق). ئۇلار: «سۆزۈڭگە قۇلاق سالدۇق ئەمرىڭگە ئاسىيلىق قىلدۇق» دېيىشتى. ئۇلارنىڭ كۇفرى تۈپەيلىدىن دىللىرىغا موزاينىڭ مۇھەببىتى سىڭىپ كەتتى. ئۇلارغا: «ئەگەر سىلەر مۆمىن بولساڭلار، ئىمانىڭلار سىلەرنى ئەجەب يامان ئىشقا بۇيرۇيدىكىنا؟ (سىلەر مۆمىن ئەمەس، چۈنكى ئىمان موزايغا چوقۇنۇشقا بۇيرۇمايدۇ)» دېگىن'),
(101, 2, 94, 'ئۇلارغا: «ئەگەر ئاخىرەت يۇرتى (يەنى جەننەت) اﷲ نىڭ قېشىدا (سىلەر گۇمان قىلغاندەك) باشقىلارغا ئەمەس، يالغۇز سىلەرگىلا خاس بولسا، (سىلەرنى جەننەتكە ئۇلاشتۇرىدىغان) ئۆلۈمنى ئارزۇ قىلىپ بېقىڭلار! (جەننەت بىزگىلا خاس دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار» دېگىن'),
(102, 2, 95, 'ئۇلار قىلغان يامان ئەمەللىرى سەۋەبلىك، ئۆلۈمنى ھەرگىزمۇ ئارزۇ قىلمايدۇ. اﷲ زالىملارنى ئوبدان بىلگۈچىدۇر'),
(103, 2, 96, 'شۈبھىسىزكى، ئۇلارنى ھاياتقا ھەممە كىشىدىن، ھەتتا مۇشرىكلاردىنمۇ ھېرىس كۆرۈسەن؛ ھەر بىرى مىڭ يىل ئۆمۈر كۆرۈشنى ئارزۇ قىلىدۇ، ئۇزۇن ئۆمۈر كۆرۈش ئۇلارنى ئازابتىن قۇتۇلدۇرۇپ قالالمايدۇ. اﷲ ئۇلار نىڭ قىلمىشلىرىنى ئېنىق كۆرۈپ تۇرغۇچىدۇر'),
(104, 2, 97, 'ئېيتقىنكى، «جىبرىئىلغا دۈشمەن بولغان ئادەم (اﷲ قا دۈشمەندۇر)، چۈنكى ئۇ (يەنى جىبرىئىل) اﷲ نىڭ ئەمرى بىلەن ئۆزىدىن ئىلگىرىكىنى (يەنى ساماۋى كىتابلارنى) تەستىق قىلغۇچى، توغرا يول كۆرسەتكۈچى، مۆمىنلەرگە بېشارەت بەرگۈچى قۇرئاننى سېنىڭ قەلبىڭگە نازىل قىلدى'),
(105, 2, 98, 'كىمكى اﷲ نى، اﷲ نىڭ پەرىشتىلىرىنى، پەيغەمبەرلىرىنى، جىبرىئىلنى، مىكائىلنى دۈشمەن تۇتىدىكەن (اﷲ نى دۈشمەن تۇتقان بولىدۇ)، شۈبھىسىزكى، اﷲ كاپىرلارنى دۈشمەن تۇتىدۇ»'),
(106, 2, 99, 'شەك - شۈبھىسىزكى، ساڭا بىز روشەن ئايەتلەرنى نازىل قىلدۇق، ئۇلارنى پەقەت توغرا يولدىن چىققانلارلا ئىنكار قىلىدۇ'),
(107, 2, 100, 'ھەر قاچان ئۇلار بىرەر ئەھدە تۈزۈشسە، بىر تۈركۈمى ئۇنى بۇزىدۇ، بەلكى ئۇلارنىڭ (يەنى يەھۇدىيلارنىڭ) تولىسى (تەۋراتقا) ئىمان ئېيتمايدۇ'),
(108, 2, 101, 'ئۇلارغا اﷲ تەرىپىدىن قوللىرىدىكى كىتابنى تەستىقلايدىغان بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلسە، (ئۇنىڭ پەيغەمبەرلىك دەلىللىرىنى) بىلمەيدىغاندەك، اﷲ نىڭ كىتابىنى (يەنى تەۋراتنى) ئارقىسىغا چۆرۈۋېتىدۇ (يەنى تەۋراتنىڭ سۆزلىرىگە ئەمەل قىلمايدۇ)'),
(109, 2, 102, 'ئۇلار سۇلايماننىڭ پادىشاھلىق زامانىدىكى شەيتانلارنىڭ (سېھرىگەرلىكىگە ئائىت) سۆزلىرىگە ئەگەشتى. سۇلايمان (سېھرىگەر بولغىنى ۋە سېھىر ئۆگىنىش بىلەن) كاپىر بولغىنى يوق، لېكىن شەيتانلار كىشىلەرگە سېھىر ئۆگىتىپ كاپىر بولدى. ئۇلار بابىلدىكى ھارۇت - مارۇت دېيىلىدىغان ئىككى پەرىشتىگە نازىل قىلىنغان سېھىرنى ئۈگىتەتتى. (بۇ ئىككى پەرىشتە) بىز كىشىلەرنى سىنايمىز كاپىر بولمىغىن، دېمىگىچە ھېچ كىشىگە سېھىر ئۆگەتمەيتتى. ئۇلار بۇ ئىككىسىدىن ئەر - خوتۇننى بىر - بىرىدىن ئايرىۋېتىدىغان نەرسىلەرنى (يەنى سېھىرگەرلىكنى) ئۆگىنەتتى، ئۇلار (سېھىر ئارقىلىق) اﷲ نىڭ رۇخسىتىسىز ھېچ كىشىگە زىيان يەتكۈزەلمەيتتى. ھالبۇكى، ئۇلار ئۆزلىرىگە زىيىنى بار، پايدىسى يوق نەرسىنى ئۆگىنەتتى. يەھۇدىيلار (تەۋراتنى تاشلاپ) سېھىرنى سېتىۋالغۇچىغا ئاخىرەتتە (اﷲ نىڭ رەھمىتىدىن ۋە جەننىتىدىن) ھېچ نېسىۋە يوق ئىكەنلىكىنى ئوبدان بېلەتتى، ئۇلار جانلىرىنىڭ بەدىلىگە سېتىۋالغان نەرسىسىنىڭ (يەنى ئۆزلىرى ئۈچۈن سېھىر ئۆگىنىشنى تاللىشىنىڭ) ئەجەبمۇ يامان ئىش ئىكەنلىكىنى كاشكى بىلسە ئىدى'),
(110, 2, 103, 'ئەگەر ئۇلار ئىمان كەلتۈرسە (سېھىرنى تاشلاپ) اﷲ تىن قورقسا، (ئۇلار ئۈچۈن) اﷲ نىڭ دەرگاھىدىكى ساۋاب ئەلۋەتتە ياخشى ئىدى، كاشكى ئۇلار بۇنى بىلسە ئىدى'),
(111, 2, 104, 'ئى مۆمىنلەر! (پەيغەمبەرگە) رائىنا دەپ خىتاب قىلماي، ئۇنزۇرنا دەپ خىتاب قىلىڭلار، (سۆزىگە) قۇلاق سېلىڭلار، كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(112, 2, 105, 'ئەھلى كىتاب ۋە مۇشرىكلاردىن بولغان كاپىرلار پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە بىرەر ياخشىلىقنىڭ چۈشۈشىنى ياقتۇرمايدۇ، اﷲ ئۆزىنىڭ خالىغان بەندىسىگە پەيغەمبەرلىكنى خاس قىلىدۇ، اﷲ چوڭ پەزل ئىگىسىدۇر'),
(113, 2, 106, 'قانداقلا بىر ئايەتنى ئەمەلدىن قالدۇرساق ياكى ئۇنتۇلدۇرساق، (ئورنىغا) ئۇنىڭدىن ئارتۇق ياكى شۇنىڭغا ئوخشاش بىر ئايەتنى كەلتۈرىمىز. اﷲ نىڭ ھەر نەرسىگە قادىر ئىكەنلىكىنى بىلمەمسەن؟'),
(114, 2, 107, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا مەنسۇپ ئىكەنلىكىنى بىلمەمسەن؟ سىلەرگە اﷲ تىن باشقا ھېچقانداق دوست ۋە مەدەتكار يوقتۇر'),
(115, 2, 108, 'ئۇلار (يەنى يەھۇدىيلار) ئىلگىرى مۇسادىن سوئال سورىغاندەك، سىلەرمۇ پەيغەمبىرىڭلاردىن سوئال سورىماقچى بولامسىلەر؟ كىمكى ئىمانىنى كۇفرىغا تېگىشسە، ئۇ ھەقىقەتەن توغرا يولدىن ئازغان بولىدۇ'),
(116, 2, 109, 'ئەھلى كىتاب ئىچىدىكى نۇرغۇن كىشىلەر ئۆزلىرىگە ھەقىقەت (يەنى دىنىڭلارنىڭ ھەقلىقى) ئاشكارا بولغاندىن كېيىن، ھەسەت قىلىش يۈزىسىدىن سىلەرنى ئىمانىڭلاردىن قايتۇرۇپ كاپىر قىلىشنى ئارزۇ قىلىدۇ. اﷲ نىڭ ئەمرى كەلگەنگە قەدەر (يەنى اﷲ سىلەرگە ئۇلار بىلەن ئۇرۇش قىلىشقا رۇخسەت قىلغۇچە) ئۇلارنى ئەپۇ قىلىڭلار ۋە كەچۈرۈڭلار. اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر'),
(117, 2, 110, 'نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، ئۆزۈڭلار ئۈچۈن ئىشلىگەن ھەرقانداق ياخشى ئەمەل بولسا، اﷲ نىڭ دەرگاھىدا ئۇنىڭ ساۋابىنى تاپىسىلەر، اﷲ ھەقىقەتەن قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(118, 2, 111, 'ئۇلار: «يەھۇدىي ياكى ناسارا بولمىغان ئادەم (يەنى يەھۇدىيلار يەھۇدىي بولمىغان ئادەم، ناسارالار ناسارا بولمىغان ئادەم) ھەرگىزمۇ جەننەتكە كىرمەيدۇ» دېيىشتى. بۇ، ئۇلارنىڭ قۇرۇق ئارزۇسىدۇر. «ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار، دەلىلىڭلارنى كەلتۈرۈڭلار» دېگىن'),
(119, 2, 112, 'ئۇنداق ئەمەس، كىمكى ياخشى ئەمەللەرنى قىلغان ھالدا ئۆزىنى اﷲ قا تاپشۇرىدىكەن (يەنى اﷲ نىڭ ئەمرىگە بويسۇنۇپ، ئەمەلىنى خالىس اﷲ ئۈچۈن قىلىدىكەن)، ئۇ پەرۋەردىگارىنىڭ دەرگاھىدا ساۋابتىن بەھرىمەن بولىدۇ. ئۇلارغا (ئاخىرەتتە) ھېچ قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ'),
(120, 2, 113, 'يەھۇدىيلار: «ناسارالارنىڭ ھېچقانداق ئاساسى يوق (يەنى ئۇلار توغرا دىندا ئەمەس)» دېدى. ناسارالارمۇ: «يەھۇدىيلارنىڭ ھېچقانداق ئاساسى يوق (يەنى ئۇلار توغرا دىندا ئەمەس)» دېدى. ھالبۇكى، ئۇلار كىتابنى (يەنى يەھۇدىيلار تەۋراتنى، ناسارالار ئىنجىلنى) ئوقۇيدۇ، بىلىمسىز ئادەملەر (يەنى ئەرەب مۇشرىكلىرى) مۇ ئۇلار دېگەندەك دېدى، اﷲ قىيامەت كۈنى ئۇلارنىڭ ئۆزئارا ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىدۇ'),
(121, 2, 114, 'اﷲ نىڭ مەسجىدلىرىدە اﷲ نىڭ نامىنىڭ ياد قىلىنىشىنى توسقان ۋە ئۇلارنى ۋەيران قىلىشقا ئورۇنغانلاردىنمۇ زالىم كىشى بارمۇ؟ مەسجىدلەرگە ئۇلارنىڭ پەقەت قورققان ھالدىلا كىرىشى لايىق ئىدى، ئۇلار دۇنيادا رەسۋا بولىدۇ. ئاخىرەتتە چوڭ ئازابقا (يەنى دوزاخ ئازابىغا) دۇچار بولىدۇ'),
(122, 2, 115, 'مەشرىقمۇ، مەغرىبمۇ (يەنى پۈتۈن يەر يۈزى) اﷲ نىڭدۇر، قايسى تەرەپكە يۈزلەنسەڭلەرمۇ، ئۇ اﷲ نىڭ تەرىپى (يەنى سىلەرگە ئىختىيار قىلغان قىبلىسى) دۇر. اﷲ (نىڭ مەغپىرىتى) ھەقىقەتەن كەڭدۇر، (اﷲ) ھەممىنى بىلگۈچىدۇر'),
(123, 2, 116, 'ئۇلار (يەنى يەھۇدىيلار، ناسارالار، مۇشرىكلار): «اﷲ نىڭ بالىسى بار» دەيدۇ. اﷲ (ئۇلارنىڭ گۇمان قىلغان نەرسىسىدىن) پاكتۇر، ئۇنداق ئەمەس، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭدۇر، ھەممىسى ئۇنىڭغا بويسۇنغۇچىدۇر'),
(124, 2, 117, 'اﷲ ئاسمانلار ۋە زېمىننى يوقتىن بار قىلغۇچىدۇر. بىر ئىشنى ۋۇجۇدقا كەلتۈرۈشنى ئىرادە قىلسا، «ۋۇجۇدقا كەل» دەيدۇ - دە، ئۇ ۋۇجۇدقا كېلىدۇ'),
(125, 2, 118, 'بىلمەيدىغانلار (يەنى قۇرەيش كاپىرلىرى): «اﷲ نېمە ئۈچۈن بىز بىلەن سۆزلەشمەيدۇ؟ ياكى نېمە ئۈچۈن بىزگە (راست پەيغەمبەرلىكنىڭ دەلىلى سۈپتىدە) بىرەر مۆجىزە كەلمەيدۇ؟» دەيدۇ. ئۇلاردىن بۇرۇن ئۆتكەنلەرمۇ (پەيغەمبەرلىرىگە) شۇنداق سۆزنى قىلغان ئىدى؛ ئۇلارنىڭ دىللىرى بىر - بىرىگە ئوخشايدۇ. ئايەتلىرىمىزنى جەزمەن ئىشىنىدىغان (يەنى ھەقىقەت ئىزدەيدىغان) قەۋم ئۈچۈن ھەقىقەتەن بايان قىلدۇق'),
(126, 2, 119, 'بىز سېنى ھەقىقەتەن (مۆمىنلەرگە جەننەت بىلەن) خوش خەۋەر بەرگۈچى، (كاپىرلارنى دوزاخ ئازابىدىن) ئاگاھلاندۇرغۇچى قىلىپ ھەق (دىن) بىلەن ئەۋەتتۇق. سەن ئەھلى دوزاخ (يەنى كاپىرلاردىن ئىمان ئېيتمىغانلار) توغرىسىدا جاۋابكار ئەمەسسەن'),
(127, 2, 120, 'سەن يەھۇدىيلار ۋە ناسارالارنىڭ دىنىغا كىرمىگىچە ئۇلار سەندىن ھەرگىزمۇ رازى بولمايدۇ. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «توغرا يول پەقەت اﷲ نىڭ يولىدۇر». ساڭا ئىلىم كەلگەندىن كېيىن (يەنى پاكىتلىق ھەقىقەت ساڭا ئاشكارا بولغاندىن كېيىن)، ئەگەر سەن ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگىشىدىغان بولساڭ، (سېنى اﷲ نىڭ ئازابىدىن قۇتقۇزىدىغان) ھېچقانداق دوستمۇ بولمايدۇ، مەدەتكارمۇ بولمايدۇ'),
(128, 2, 121, 'بىز ئاتا قىلغان كىتابنى تېگىشلىك رەۋىشتە ئوقۇيدىغانلار بار، ئەنە شۇلار ئۇنىڭغا ئىشىنىدۇ. كىملەركى كىتابنى (يەنى قۇرئاننى) ئىنكار قىلىدىكەن، ئۇلار زىيان تارتقۇچىلاردۇر'),
(129, 2, 122, 'ئى ئىسرائىل ئەۋلادى! سىلەرگە بەرگەن نېمىتىمنى ۋە سىلەرنى (بىر زامانلاردا) ھەممە جاھان ئەھلىدىن ئۈستۈن قىلغانلىقىمنى ياد ئېتىڭلار'),
(130, 2, 123, 'بىراۋغا بىراۋ قىلچىلىك ياردەم قىلالمايدىغان، ھېچ كىشىنىڭ فىدىيىسى قوبۇل قىلىنمايدىغان، ھېچ كىشىنىڭ شاپائىتى پايدا قىلمايدىغان، ئۇلارغا (يەنى كاپىرلار ۋە گۇناھكارلارغا) ياردەم قىلىنمايدىغان كۈندىن قورقۇڭلار'),
(131, 2, 124, 'ئۆز ۋاقتىدا ئىبراھىمنى پەرۋەردىگارى بىر قانچە ئەمرلەر (يەنى شەرىئەت تەكلىپلىرى) بىلەن سىنىدى، ئىبراھىم ئۇلارنى بەجا كەلتۈردى. اﷲ ئۇنىڭغا: «سېنى چوقۇم كىشىلەرگە (دىندا) پېشۋا قىلىمەن» دېدى، ئىبراھىم: «مېنىڭ بىر قىسىم ئەۋلادىمنىمۇ پېشۋا قىلساڭ» دېدى. اﷲ: «مېنىڭ (پېشۋا قىلىش) ئەھدەمگە زالىملار (يەنى كاپىرلار) ئېرىشەلمەيدۇ» دېدى'),
(132, 2, 125, 'ئۆز ۋاقتىدا بىز كەبىنى كىشىلەر ئۈچۈن جەم بولىدىغان جاي ۋە ئامان جاي قىلىپ بەردۇق. (كىشىلەرگە) ماقامى ئىبراھىمنى نامازگاھ قىلىڭلار (يەنى شۇ يەردە ناماز ئوقۇڭلار دېدۇق). ئىبراھىم بىلەن ئىسمائىلغا ئۆيۈمنى (يەنى كەبەمنى) تاۋاپ قىلغۇچىلار، ئېتىكاپتا ئولتۇرغۇچىلار، رۇكۇ قىلغۇچىلار، سەجدە قىلغۇچىلار ئۈچۈن پاك تۇتۇشنى بۇيرۇدۇق'),
(133, 2, 126, 'ئۆز ۋاقتىدا ئىبراھىم: «پەرۋەردىگارىم! بۇ يەرنى (يەنى مەككىنى) تېنىچ شەھەر قىلىپ بەرگىن، ئاھالىسىدىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقانلارنى تۈرلۈك مېۋىلەر بىلەن رىزىقلاندۇرغىن» دېدى. اﷲ: «كاپىر بولغان ئادەمنىمۇ (رىزىقتىن) ئازغىنا مۇددەت (يەنى ھاياتلىقىدا) بەھرىمەن قىلىمەن، ئاندىن كېيىن ئۇنى (ئاخىرەتتە) دوزاخقا ھەيدەيمەن» دېدى. (بۇ) نېمىدېگەن يامان ئاقىۋەت!'),
(134, 2, 127, 'ئۆز ۋاقتىدا ئىبراھىم بىلەن ئىسمائىل كەبىنىڭ ئۇلىنى قوپۇرۇۋېتىپ: «پەرۋەردىگارىمىز بىزنىڭ (خىزمىتىمىزنى) قوبۇل قىلغىن، سەن ھەقىقەتەن (دۇئايىمىزنى) ئاڭلاپ تۇرغۇچىسەن، (نىيىتىمىزنى) بىلىپ تۇرغۇچىسەن'),
(135, 2, 128, 'پەرۋەردىگارىمىز! ئىككىمىزنى ئۆزۈڭگە ئىتائەتمەن قىلغىن، بىزنىڭ ئەۋلادلىرىمىزدىنمۇ ئۆزۈڭگە ئىتائەتمەن ئۈممەت چىقارغىن، بىزگە ھەجىمىزنىڭ قائىدىلىرىنى بىلدۈرگىن، تەۋبىمىزنى قوبۇل قىلغىن، چۈنكى سەن تەۋبىنى ناھايىتى قوبۇل قىلغۇچىسەن، ناھايىتى مېھرىبانسەن'),
(136, 2, 129, 'پەرۋەردىگارىمىز! ئۇلارنىڭ ئىچىدىن ئايەتلىرىڭنى ئۇلارغا تىلاۋەت قىلىپ بېرىدىغان، كىتابىڭنى (يەنى قۇرئاننى)، ھېكمەتنى (يەنى پاك سۈننەتنى) ئۇلارغا ئۆگىتىدىغان، ئۇلارنى (مۇشرىكلىك ۋە گۇناھلاردىن) پاك قىلىدىغان بىر پەيغەمبەر ئەۋەتكىن، ھەقىقەتەن سەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن» دېدى'),
(137, 2, 130, 'ئۆزىنى ئەخمەق قىلغانلاردىن باشقا كىم ئىبراھىمنىڭ دىنىدىن يۈز ئۆرۈيدۇ، ھەقىقەتەن بىز ئۇنى بۇ دۇنيادا (پەيغەمبەرلىككە) تاللىدۇق. شەك - شۈبھىسىزكى، ئاخىرەتتە ئۇ ياخشىلار قاتارىدا بولىدۇ'),
(138, 2, 131, 'ئۆز ۋاقتىدا پەرۋەردىگارى ئۇنىڭغا: «(پەرۋەردىگارىڭنىڭ ئەمرىگە) ئىتائەت قىلغىن» دېدى. ئۇ: «ئالەملەرنىڭ پەرۋەردىگارىغا ئىتائەت قىلدىم» دېدى'),
(139, 2, 132, 'ئىبراھىم ۋە يەئقۇب ئۆز ئوغۇللىرىغا ۋەسىيەت قىلىپ: «ئى ئوغۇللىرىم! اﷲ سىلەرگە مۇشۇ دىنىنى (يەنى ئىسلام دىنىنى) تاللىدى، پەقەت مۇسۇلمان پىتىڭلارچە ئۆلۈڭلار (يەنى ئىمانىڭلاردا مەھكەم تۇرۇڭلار، تاكى ئىمان بىلەن كېتىڭلار)» دېدى'),
(140, 2, 133, 'يەئقۇب جان ئۈزۈۋاتقان ۋاقتىدا سىلەر يېنىدا بارمىدىڭلار؟ شۇ چاغدا يەئقۇب ئوغۇللىرىدىن: «مەن ئۆلگەندىن كېيىن سىلەر كىمگە ئىبادەت قىلىسىلەر؟» دەپ سورىدى. ئۇلار: «سېنىڭ ئىلاھىڭ ۋە ئاتا - بوۋىلىرىڭ ئىبراھىم، ئىسمائىل، ئىسھاقلارنىڭ ئىلاھى بولغان بىر اﷲ قا ئىبادەت قىلىمىز، بىز ئۇنىڭغا ئىتائەت قىلغۇچىمىز» دېدى'),
(141, 2, 134, 'ئۇلار ئۆتكەن بىر ئۈممەتتۇر، ئۇلارنىڭ ئەمەللىرى (نىڭ ساۋابى) ئۆزلىرى ئۈچۈندۇر. سىلەرنىڭ ئەمەلىڭلار (نىڭمۇ ساۋابى) ئۆزۈڭلار ئۈچۈندۇر، ئۇلارنىڭ قىلمىشىغا سىلەر جاۋابكار ئەمەس'),
(142, 2, 135, 'ئۇلار: «يەھۇدىي ياكى ناسارا بولۇڭلار، توغرا يول تاپىسىلەر» دەيدۇ. «ئۇنداق ئەمەس، بىز ئىبراھىمنىڭ ئىسلامغا ئۇيغۇن دىنىغا ئەگىشىمىز، ئۇ مۇشرىكلاردىن ئەمەس ئىدى» دېگىن'),
(143, 2, 136, 'ئېيتىڭلاركى، «اﷲ قا ئىمان ئېيتتۇق، بىزگە نازىل قىلىنغان ۋەھيىگە، ئىبراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا ۋە ئۇلارنىڭ ئەۋلادلىرىغا نازىل قىلىنغان ۋەھيىگە، مۇساغا بېرىلگەن (تەۋراتقا)، ئىساغا بېرىلگەن (ئىنجىلغا) ۋە پەيغەمبەرلەرگە پەرۋەردىگارى تەرىپىدىن بېرىلگەن (كىتابلارغا) ئىمان ئېيتتۇق، ئۇلاردىن ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى يەھۇدىيلار ۋە ناسارالارغا ئوخشاش، ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ بەزىسىنى ئىنكار قىلمايمىز)، بىز اﷲ قا بويسۇنغۇچىلارمىز»'),
(144, 2, 137, 'ئەگەر ئۇلار (يەنى ئەھلى كىتاب) سىلەر ئىمان ئېيتقاندەك ئىمان ئېيتسا، ئەلۋەتتە، توغرا يول تاپقان بولىدۇ. ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە، (سەن بىلەن) ئاداۋەتتە بولغان بولىدۇ، ئۇلارغا قارشى اﷲ ساڭا كۇپايىدۇر. اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ھىيلە - مىكرىنى) بىلىپ تۇرغۇچىدۇر'),
(145, 2, 138, 'اﷲ نىڭ دىنىدا چىڭ تۇرۇڭلار، اﷲ نىڭ دىنىدىن ياخشى كىمنىڭ دىنى بار؟ بىز اﷲ قا ئىبادەت قىلغۇچىلارمىز'),
(146, 2, 139, '«بىز بىلەن اﷲ توغرۇلۇق مۇنازىرىلىشەمسىلەر؟ ھالبۇكى، ئۇ ھەم بىزنىڭ پەرۋەردىگارىمىز، ھەم سىلەرنىڭ پەرۋەردىگارىڭلاردۇر. بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈندۇر، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، بىز اﷲ قا سادىقمىز» دېگىن'),
(147, 2, 140, '«ياكى ئىبراھىم، ئىسمائىل، ئىسھاق، يەئقۇب ۋە ئۇنىڭ ئەۋلادى يەھۇدىي ئىدى ياكى ناسارا ئىدى» دەمسىلەر؟ ئېيتقىنكى، «(ئۇلارنىڭ قايسى دىندا ئىكەنلىكىنى) سىلەر ئوبدان بىلەمسىلەر؟ ياكى اﷲ ئوبدان بىلەمدۇ؟ قولىدا اﷲ نازىل قىلغان دەلىل - ئىسپات تۇرۇپ ئۇنى يوشۇرغان ئادەمدىن زالىم كىم بار؟ اﷲ سىلەرنىڭ قىلمىشىڭلاردىن غاپىل ئەمەستۇر»'),
(148, 2, 141, 'ئۇلار ئۆتكەن بىر ئۈممەتتۇر، ئۇلارنىڭ ئەمەللىرى ئۆزلىرى ئۈچۈندۇر. سىلەرنىڭ ئەمەلىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، ئۇلارنىڭ قىلمىشىغا سىلەر جاۋابكار ئەمەس'),
(149, 2, 142, 'بەزى ئەخمەق كىشىلەر: «ئۇلارنى (يەنى پەيغەمبەر ئەلەيھىسسالام بىلەن مۆمىنلەرنى) قاراپ كېلىۋاتقان قىبلىسىدىن (يەنى بەيتۇلمۇقەددەستىن) نېمە يۈز ئۆرۈگۈزگەندۇ؟» دەيدۇ. (ئى مۇھەممەد!) ئېيتقىنكى، «مەشرىق ۋە مەغرىب (يەنى ھەممە تەرەپ) اﷲ نىڭدۇر، اﷲ خالىغان كىشىنى توغرا يولغا باشلايدۇ»'),
(150, 2, 143, 'شۇنىڭدەك (يەنى سىلەرنى ئىسلامغا ھىدايەت قىلغاندەك) كىشىلەرگە (يەنى ئۆتكەنكى ئۈممەتلەرگە) شاھىت بولۇشۇڭلار ئۈچۈن ۋە پەيغەمبەرنىڭ سىلەرگە شاھىت بولۇشى ئۈچۈن، بىز سىلەرنى ياخشى ئۈممەت قىلدۇق. سەن يۈز كەلتۈرۈپ كېلىۋاتقان تەرەپنى (يەنى بەيتۇلمۇقەددەسنى) قىبلە قىلغانلىقىمىز پەيغەمبەرگە ئەگەشكەنلەرنى ئاسىيلىق قىلغانلاردىن (يەنى مۇرتەد بولۇپ كەتكەنلەردىن) ئايرىۋېلىشىمىز ئۈچۈنلا ئىدى. اﷲ ھىدايەت قىلغانلاردىن باشقىلارغا بۇ (يەنى قىبلىنىڭ ئۆزگەرتىلىشى) ھەقىقەتەن ئېغىردۇر. اﷲ سىلەرنىڭ ئىمانىڭلارنى (يەنى ئىلگىرى بەيتۇلمۇقەددەسكە قاراپ ئوقۇغان نامىزىڭلارنى) بىكار قىلىۋەتمەيدۇ. اﷲ كىشىلەرگە ھەقىقەتەن مېھرىباندۇر، ناھايىتى كۆيۈمچاندۇر'),
(151, 2, 144, 'بىز سېنىڭ (كەبە قىبلەڭ بولۇشىنى تىلەپ) قايتا - قايتا ئاسمانغا قارىغانلىقىڭنى كۆرۈپ تۇرۇۋاتىمىز. سېنى چوقۇم سەن ياقتۇرىدىغان قىبلىگە يۈزلەندۈرىمىز. (نامازدا) يۈزۈڭنى مەسجىدى ھەرەم تەرەپكە قىلغىن. (ئى مۆمىنلەر!) قەيەردە بولماڭلار (نامازدا) يۈزۈڭلارنى مەسجىدى ھەرەم تەرەپكە قىلىڭلار. ھەقىقەتەن كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) بۇنىڭ (يەنى قىبلىنىڭ كەبىگە ئۆزگەرتىلىشىنىڭ) پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەت ئىكەنلىكىنى، چوقۇم بىلىدۇ، اﷲ ئۇلارنىڭ قىلمىشىدىن غاپىل ئەمەس'),
(152, 2, 145, 'كىتاب بېرىلگەنلەرگە بارچە دەلىلنى كەلتۈرسەڭمۇ، ئۇلار سېنىڭ قىبلەڭگە بويسۇنمايدۇ، سەنمۇ ئۇلارنىڭ قىبلىسىگە بويسۇنمايسەن؛ ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) ھەم ئۆز ئارا بىر - بىرىنىڭ قىبلىسىگە بويسۇنمايدۇ. ساڭا ئىلىم (يەنى ۋەھيى) كەلگەندىن كېيىن، مۇبادا ئۇلارنىڭ نەپسى خاھىشلىرىغا بويسۇنساڭ، ئۇ چاغدا سەن چوقۇم زۇلۇم قىلغانلاردىن بولىسەن'),
(153, 2, 146, 'بىز كىتاب بەرگەنلەر (يەنى يەھۇدىيلار بىلەن ناسارالار) ئۇنى (يەنى پەيغەمبەرنى) ئۆز ئوغۇللىرىنى تونۇغاندەك تونۇيدۇ. ئۇلارنىڭ بىر پىرقىسى، شۈبھىسىزكى، ھەقىقەتنى بىلىپ تۇرۇپ يوشۇرىدۇ'),
(154, 2, 147, 'ھەقىقەت اﷲ تەرىپىدىن كەلگەندۇر، ھەرگىز شەك كەلتۈرگۈچىلەردىن بولمىغىن'),
(155, 2, 148, 'ھەر (ئۈممەت) نىڭ يۈز كەلتۈرىدىغان تەرىپى (يەنى قىبلىسى) بار، (ئى مۆمىنلەر!) ياخشى ئەمەللەرنى قىلىشقا ئالدىراڭلار، قەيەردە بولماڭلار، (قىيامەت كۈنى) اﷲ ھەممەڭلارنى (ھېساب ئۈچۈن) يىغىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر'),
(156, 2, 149, '(قەيەردىن سەپەرگە چىقمىغىن، نامىزىڭدا) يۈزۈڭنى مەسجىدى ھەرەم (يەنى كەبە) تەرەپكە قىلغىن؛ بۇ، ھەقىقەتەن، پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەتتۇر، اﷲ سىلەرنىڭ قىلمىشىڭلاردىن غاپىل ئەمەستۇر'),
(157, 2, 150, 'كىشىلەر (يەنى يەھۇدىيلار ۋە مۇشرىكلار) گە سىلەرگە قارشى دەلىل - ئىسپات بولماسلىقى ئۈچۈن، (ئى مۇھەممەد! سەپەر قىلىشقا) قەيەردىن چىقمىغىن، (نامىزىڭدا) يۈزۈڭنى مەسجىدى ھەرەم تەرەپكە قىلغىن. (ئى مۆمىنلەر!) سىلەرمۇ قەيەردە بولماڭلار، (نامىزىڭلاردا) يۈزۈڭلارنى مەسجىدى ھەرەم تەرەپكە قىلىڭلار. پەقەت ئۇلارنىڭ ئىچىدىكى زۇلۇم قىلغۇچىلارلا (قىبلىنىڭ ئۆزگەرگەنلىكىنى سىلەرگە قارشى دەلىل قىلىۋالىدۇ)، ئۇلار (يەنى زالىملار) دىن قورقماڭلار، (مېنىڭ ئەمرىمنى تۇتۇش بىلەن) مەندىن قورقۇڭلار. (قىبلىنى ئۆزگەرتىشكە ئەمر قىلىشىم) سىلەرگە بەرگەن نېمىتىمنى كامالەتكە يەتكۈزۈشۈم ئۈچۈن ۋە توغرا يولدا بولۇشۇڭلار ئۈچۈندۇر'),
(158, 2, 151, 'شۇنىڭدەك (يەنى سىلەرگە بەرگەن نېمىتىمنى كامالەتكە يەتكۈزگىنىمدەك) ئۆز ئىچىڭلاردىن سىلەرگە بىزنىڭ ئايەتلىرىمىزنى تىلاۋەت قىلىپ بېرىدىغان، سىلەرنى (مۇشرىكلىك ۋە گۇناھدىن) پاك قىلىدىغان، سىلەرگە كىتابنى (يەنى قۇرئاننى) ۋە ھېكمەتنى (يەنى دىنىي ئەھكاملارنى) ئۆگىتىدىغان، سىلەرگە بىلمىگەنلىرىڭلارنى بىلدۈرىدىغان بىر پەيغەمبەر ئەۋەتتۇق'),
(159, 2, 152, 'مېنى (تائەت - ئىبادەت بىلەن) ياد ئېتىڭلار، (مېنى ياد ئەتسەڭلار) مەنمۇ سىلەرنى (ساۋاب بېرىش بىلەن، مەغپىرەت قىلىش بىلەن) ياد ئېتىمەن. ماڭا شۈكۈر قىلىڭلار، ناشۈكۈرلۈك قىلماڭلار'),
(160, 2, 153, 'ئى مۆمىنلەر! سەۋر ئارقىلىق ۋە ناماز ئارقىلىق ياردەم تىلەڭلار. اﷲ ھەقىقەتەن سەۋر قىلغۇچىلار بىلەن بىللىدۇر'),
(161, 2, 154, 'اﷲ نىڭ يولىدا ئۆلتۈرۈلگەنلەرنى (يەنى شېھىتلەرنى) ئۆلۈك دېمەڭلار، بەلكى ئۇلار تىرىكتۇر، لېكىن سىلەر بۇنى سەزمەيسىلەر'),
(162, 2, 155, 'بىز سىلەرنى بىرئاز قورقۇنچ بىلەن، بىرئاز قەھەتچىلىك بىلەن ۋە ماللىرىڭلارغا، جانلىرىڭلارغا، بالىلىرىڭلارغا، زىرائەتلىرىڭلارغا يېتىدىغان زىيان بىلەن چوقۇم سىنايمىز. (بېشىغا كەلگەن مۇسىبەت، زىيان - زەخمەتلەرگە) سەۋر قىلغۇچىلارغا (جەننەت بىلەن) خۇش خەۋەر بەرگىن'),
(163, 2, 156, 'ئۇلارغا بىرەر مۇسىبەت كەلگەن چاغدا، ئۇلار: «بىز ئەلۋەتتە اﷲ نىڭ ئىگىدارچىلىقىدىمىز (يەنى اﷲ نىڭ بەندىلىرىمىز)، چوقۇم اﷲ نىڭ دەرگاھىغا قايتىمىز» دەيدۇ'),
(164, 2, 157, 'ئەنە شۇلار پەرۋەردىگارىنىڭ مەغپىرىتى ۋە رەھمىتىگە ئېرىشكۈچىلەردۇر، ئەنە شۇلار ھىدايەت تاپقۇچىلاردۇر'),
(165, 2, 158, 'سەفا بىلەن مەرۋە ھەقىقەتەن اﷲ نىڭ (دىنىنىڭ) ئالامەتلىرىدۇر. ھەج قىلغان ياكى ئۆمرە قىلغان ئادەم ئۇلارنىڭ ئارىسىدا سەئيى قىلسا ھېچ باك يوق. كىمكى ئۆز ئىختىيارى بىلەن بىرەر ياخشىلىق قىلىدىكەن (اﷲ ئۇنىڭ مۇكاپاتىنى بېرىدۇ)، اﷲ ئەلۋەتتە شۈكۈرنىڭ مۇكاپاتىنى بەرگۈچىدۇر، اﷲ ھەممىنى بىلگۈچىدۇر'),
(166, 2, 159, 'بۇ كىتابتا (يەنى تەۋراتتا) كىشىلەرگە توغرا يولنى ئېنىق بايان قىلغىنىمىزدىن كېيىن، بىز نازىل قىلغان (مۇھەممەد ئەلەيھىسسالامنىڭ راست پەيغەمبەر ئىكەنلىكىنى ئىسپاتلايدىغان) روشەن دەلىللەرنى ۋە توغرا يولنى يوشۇرىدىغانلارغا اﷲ لەنەت قىلىدۇ (يەنى رەھمىتىدىن يىراق قىلىدۇ)؛ لەنەت قىلغۇچىلار (يەنى پەرىشتىلەر ۋە مۆمىنلەر) مۇ ئۇلارغا لەنەت قىلىدۇ'),
(167, 2, 160, 'پەقەت تەۋبە قىلغانلار، (ئەمەلىنى) تۈزەتكەنلەر، يەھۇدىيلارنىڭ تەۋرات ئەھكاملىرىدىن يوشۇرغانلىرىنى) بايان قىلغانلارلا بۇنىڭدىن مۇستەسنا. ئەنە شۇلارنىڭ تەۋبىسىنى قوبۇل قىلىمەن، مەن تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇرمەن. ناھايىتى مېھرىباندۇرمەن'),
(168, 2, 161, 'شۈبھىسىزكى، كاپىر بولۇپ، كاپىر پېتى ئۆلگەنلەر بار، ئەنە شۇلار چوقۇم اﷲ نىڭ، پەرىشتىلەرنىڭ ۋە ئىنسانلارنىڭ ھەممىسىنىڭ لەنىتىگە ئۇچرايدۇ'),
(169, 2, 162, 'ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ، ئۇلارغا بېرىلىدىغان ئازاب كېچىكتۈرۈلمەيدۇ'),
(170, 2, 163, 'سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر؛ ئۇ ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر'),
(171, 2, 164, 'ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىدا، كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىشىدە، كىشىلەرگە پايدىلىق نەرسىلەرنى ئېلىپ دېڭىزدا ئۈزۈپ يۈرگەن كېمىلەردە، اﷲ بۇلۇتتىن ياغدۇرۇپ بەرگەن، ئۆلگەن زېمىننى تىرىلدۈرىدىغان يامغۇردا، يەر يۈزىگە تارقىتىۋەتكەن ھايۋاناتلاردا، شامال يۆنۈلۈشلىرىنىڭ ئۆزگىرىپ تۇرۇشىدا، ئاسمان - زېمىن ئارىسىدا بويسۇندۇرۇلغان بۇلۇتلاردا، چۈشىنىدىغان كىشىلەر ئۈچۈن، ئەلۋەتتە (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) دەلىللەر باردۇر'),
(172, 2, 165, 'بەزى ئادەملەر اﷲ تىن غەيرىيلەرنى (يەنى بۇتلارنى) اﷲ قا شېرىك قىلىۋالىدۇ، ئۇلارنى (مۆمىنلەرنىڭ) اﷲ نى دوست تۇتقىنىدەك دوست تۇتىدۇ، (يەنى ئۇلۇغلايدۇ ۋە ئۇلارغا بويسۇنىدۇ). مۆمىنلەر اﷲ نى ھەممىدىن بەك دوست تۇتقۇچىلاردۇر، زالىملار (قىيامەت كۈنى ئۆزلىرىگە تەييارلانغان) ئازابنى كۆرگەن چاغدا پۈتۈن كۈچ - قۇۋۋەتنىڭ اﷲ قا مەنسۈپ ئىكەنلىكىنى ۋە اﷲ نىڭ ئازابىنىڭ قاتتىق ئىكەنلىكىنى كاشكى بىلسە ئىدى (دۇنيادا اﷲ قا شېرىك كەلتۈرگەنلىكلىرىگە ھەددىدىن زىيادە پۇشايمان قىلاتتى)'),
(173, 2, 166, 'ئەينى ۋاقىتتا ئەگەشتۈرگۈچىلەر ئازابنى كۆرۈپ، ئەگەشكۈچىلەردىن ئادا - جۇدا بولىدۇ. ئۇلارنىڭ ئۆزئارا مۇناسىۋىتى (يەنى دۇنيادىكى دوستلۇقى) ئۈزۈلىدۇ'),
(174, 2, 167, 'ئەگەشكۈچىلەر: «كاشكى بىزلەرگە (دۇنياغا) قايتىشقا بولسا ئىدى، ئۇلار بىزدىن ئادا - جۇدا بولغاندەك بىزمۇ ئۇلاردىن ئادا - جۇدا بولاتتۇق» دەيدۇ. اﷲ ئۇلارغا (يامان) ئىشلىرىنىڭ ھەسرەت بولغانلىقىنى مۇشۇنداق كۆرسىتىدۇ، ئۇلار دوزاختىن چىقمايدۇ'),
(175, 2, 168, 'ئى ئىنسانلار! يەر يۈزىدىكى ھالال - پاكىز نەرسىلەردىن يەڭلار، شەيتاننىڭ يوللىرىغا ئەگەشمەڭلار، چۈنكى شەيتان سىلەرگە ئوچۇق دۈشمەندۇر'),
(176, 2, 169, 'شەيتان سىلەرنى ھەقىقەتەن يامانلىقلارغا، قەبىھ گۇناھلارغا ۋە اﷲ نامىدىن، يالغاندىن (اﷲ سىلەرگە ھالال قىلغان نەرسىلەرنى ھارام دەپ ۋە ھارام قىلغان نەرسىلەرنى ھالال دەپ)، سىلەر بىلمەيدىغان نەرسىلەرنى ئېيتىشقا بۇيرۇيدۇ'),
(177, 2, 170, 'ھەرقاچان ئۇلارغا (يەنى مۇشرىكلارغا): «اﷲ نازىل قىلغان نەرسىگە (يەنى اﷲ پەيغەمبىرىگە نازىل قىلغان ۋەھيىگە، قۇرئانغا) ئەگىشىڭلار» دېيىلسە، ئۇلار: «ياق، ئاتا - بوۋىلىرىمىزدىن قالغان دىنغا ئەگىشىمىز» دېيىشىدۇ. ئاتا - بوۋىلىرى ھېچ نەرسىنى چۈشەنمىگەن ۋە توغرا يولدا بولمىغان تۇرسا، يەنە ئۇلارغا ئەگىشەمدۇ؟'),
(178, 2, 171, 'كاپىرلەرنى ھىدايەتكە دەۋەت قىلغۇچى خۇددى مەنىسىنى چۈشەنمەي تاۋۇشىنى ۋە نىدانىلا ئاڭلايدىغان چاھارپايلارنى چاقىرغان ئادەمگە ئوخشايدۇ (كاپىرلار ھەقنى ئاڭلاشتىن) گاستۇر، (ھەقنى سۆزلەشتىن) گاچىدۇر، (ھەقنى كۆرۈشتىن) كوردۇر، ئۇلار (ۋەز - نەسىھەتنى چۈشەنمەيدۇ'),
(179, 2, 172, 'ئى مۆمىنلەر! بىز سىلەرنى رىزىقلاندۇرغان ھالال نەرسىلەردىن يەڭلار، ئەگەر اﷲ غىلا ئىبادەت قىلىدىغان بولساڭلار، اﷲ قا (يەنى اﷲ نىڭ ھەددى - ھېسابسىز نېمەتلىرىگە) شۈكۈر قىلىڭلار'),
(180, 2, 173, 'اﷲ سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋاننى، قاننى، چوشقا گۆشىنى، اﷲ تىن غەيرىينىڭ (يەنى بۇتلارنىڭ) نامى ئېيتىلىپ بوغۇزلانغان ھايۋاننى يېيىشنى ھارام قىلدى، كىمكى ئۆز ئىختىيارىچە ئەمەس، ئىلاجىسىزلىقتىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) ھاياتىنى ساقلاپ قالغۇدەك يېسە، ئۇنىڭغا ھېچ گۇناھ بولمايدۇ. ھەقىقەتەن اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(181, 2, 174, 'اﷲ كىتابتا (يەنى تەۋراتتا) نازىل قىلغان نەرسىلەرنى يوشۇرىدىغان ۋە ئۇنى ئازغىنا پۇلغا ساتىدىغانلارنىڭ قارنىغا يېگىنى پەقەت ئوتتۇر (چۈنكى ئۇلارنىڭ يېگەن شۇ ھارام مېلى ئۇلارنى دوزاخقا ئېلىپ بارىدۇ)، قىيامەت كۈنى اﷲ ئۇلارغا (ئۇلارنى خۇشال قىلىدىغان) سۆز قىلمايدۇ، ئۇلارنى (گۇناھلىرىدىن) پاكلىمايدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(182, 2, 175, 'ئەنە شۇلار ھىدايەتنى گۇمراھلىققا، مەغپىرەتنى ئازابقا (يەنى جەننەتنى دوزاخقا) تېگىشتى، ئۇلار دوزاخ ئازابىغا نېمىدېگەن چىداملىق!'),
(183, 2, 176, 'بۇ (قاتتىق ئازاب) شۇنىڭ ئۈچۈنكى، اﷲ ھەقىقەتەن ھەق كىتابنى (تەۋراتنى) نازىل قىلدى (ئۇلار تەۋراتتىكى نەرسىلەرنى يوشۇردى ۋە ئۆزگەرتىۋەتتى)، كىتاب توغرىسىدا ئىختىلاپ قىلغۇچىلار چۇڭقۇر نىزادىدۇر'),
(184, 2, 177, 'سىلەرنىڭ كۈن چىققان ۋە كۈن پاتقان تەرەپكە يۈزكەلتۈرۈشۈڭلارنىڭ ئۆزىلا ياخشى ئەمەلگە ياتمايدۇ. بەلكى اﷲ قا، ئاخىرەت كۈنىگە، پەرىشتىلەرگە، كىتابقا (يەنى اﷲ نازىل قىلغان كىتابلارغا)، پەبغەمبەرلەرگە ئىمان كەلتۈرۈش، اﷲ نى سۆيۈش يۈزىسىدىن خىش - ئەقرىبالارغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە (يەنى پۇل - مېلىدىن ئالاقىسى ئۈزۈلۈپ قالغان مۇساپىرلارغا)، سائىللارغا ۋە قۇللارنىڭ ئازادلىققا ئېرىشىشىگە پۇل - مال ياردەم بېرىش، ناماز ئوقۇش، زاكات بېرىش، ئەھدىگە ۋاپا قىلىش، يوقسۇزلۇققا، كېسەللىككە ۋە (اﷲ نىڭ يولىدا قىلىنغان) ئۇرۇشقا بەرداشلىق بېرىش ياخشى ئەمەلگە كىرىدۇ. ئەنە شۇلار (يەنى يۇقىرىقى سۈپەتلەرگە ئىگە كىشىلەر) (ئىمانىدا) راستچىل ئادەملەردۇر، ئەنە شۇلار تەقۋادار ئادەملەردۇر'),
(185, 2, 178, 'ئى مۆمىنلەر! ئۆلتۈرۈلگەنلەر ئۈچۈن قىساس ئېلىش سىلەرگە پەرز قىلىندى، ھۆر ئادەم ئۈچۈن ھۆر ئادەمدىن، قۇل ئۈچۈن قۇلدىن، ئايال ئۈچۈن ئايالدىن قىساس ئېلىنىدۇ. قاتىل ئۈچۈن (دىنىي) قېرىندىشى تەرىپىدىن بىرنەرسە كەچۈرۈم قىلىنسا (يەنى ئۆلتۈرۈلگۈچىنىڭ ئىگىسى قاتىلدىن دىيەت ئېلىشقا رازى بولۇپ قىساس ئېلىشتىن ۋاز كەچسە، ئۇ، جىنايەتچىدىن دىيەتنى) چىرايلىقچە تەلەپ قىلىشى لازىم، (جىنايەتچىمۇ دىيەتنى) ياخشىلىقچە (يەنى كېچىكتۈرمەستىن، كېمەيتىۋەتمەستىن تولۇق) بېرىشى لازىم. بۇ (ھۆكۈم) پەرۋەردىگارىڭلار تەرىپىدىن (سىلەرگە بېرىلگەن) يېنىكلىتىشتۇر ۋە رەھمەتتۇر. شۇنىڭدىن كېيىن (يەنى دىيەتنى قوبۇل قىلغاندىن كېيىن)، چېكىدىن ئېشىپ كەتكەن (يەنى قاتىلنى ئۆلتۈرگەن) ئادەم قاتتىق ئازابقا دۇچار بولىدۇ'),
(186, 2, 179, 'ئى ئەقىل ئىگىلىرى! سىلەرگە قىساستا ھاياتلىق بار، (يەنى كىشى بىراۋنى ئۆلتۈرگەن تەقدىردە ئۆزىنىڭمۇ ئۆلتۈرۈلىدىغانلىقىنى بىلسە، ئۇ، ئادەم ئۆلتۈرۈشتىن يانىدۇ، شۇنىڭ بىلەن، ئۇنىڭ ئۆزىمۇ، ئۇ ئۆلتۈرمەكچى بولغان ئادەممۇ ئۆلۈشتىن ساقلىنىپ قالىدۇ). (ناھەق قان تۆكۈشتىن) ساقلىنىشىڭلار ئۈچۈن (قىساس يولغا قويۇلدى)'),
(187, 2, 180, 'ئەگەر سىلەردىن بىرەيلەن ئۆلۈش ئالدىدا كۆپ مال قالدۇرسا، ئۇنىڭ ئاتا - ئانىسىغا ۋە خىش - ئەقرىبالىرىغا ئادىللىق بىلەن ۋەسىيەت قىلىشى تەقۋادارلارنىڭ ئۆتەشكە تېگىشلىك بۇرچى سۈپىتىدە پەرز قىلىندى'),
(188, 2, 181, 'ۋەسىيەتنى ئاڭلىغاندىن كىيىن، كىمكى ئۇنى ئۆزگەرتىدىكەن، ئۇنىڭ گۇناھى ئۆزگەرتكەنلەرگە يۈكلىنىدۇ. اﷲ ھەقىقەتەن ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى بىلىپ تۇرغۇچىدۇر'),
(189, 2, 182, 'كىمكى ۋەسىيەت قىلغۇچىنىڭ سەۋەنلىكى ياكى قەستەن ناتوغرا قىلغانلىقىنى بىلىپ، ئۇلارنىڭ (يەنى ۋەسىيەت قىلغۇچىلار بىلەن ۋەسىيەت قىلىنغۇچىلارنىڭ) ئارىسىنى تۈزەپ قويسا، ئۇنىڭغا ھېچ گۇناھ بولمايدۇ، اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(190, 2, 183, 'ئى مۆمىنلەر! (گۇناھلاردىن) ساقلىنىشىڭلار ئۈچۈن، سىلەردىن ئىلگىرىكىلەرگە (يەنى ئىلگىرىكى ئۈممەتلەرگە) روزا پەرز قىلىنغاندەك، سىلەرگىمۇ (رامىزان روزىسى) پەرز قىلىندى'),
(191, 2, 184, '(بۇ پەرز قىلىنغان روزا) ساناقلىق كۈنلەردۇر، سىلەردىن كىمكى كېسەل ياكى سەپەر ئۈستىدە بولۇپ (روزا تۇتمىغان بولسا)، تۇتمىغان كۈنلەرنى (يەنى قازاسىنى) باشقا كۈنلەردە تۇتسۇن، روزىنى (قېرىلىق ياكى ئاجىزلىق تۈپەيلىدىن مۇشەققەت بىلەن) ئاران تۇتىدىغان كىشىلەر تۇتمىسا، (كۈنلۈكى ئۈچۈن) بىر مىسكىن تويغۇدەك تاماق فىدىيە بېرىشى لازىم. كىمكى فىدىيىنى (بەلگىلەنگەن مىقداردىن) ئارتۇق بەرسە، بۇ ئۆزى ئۈچۈن ياخشىدۇر، ئەگەر بىلسەڭلار، روزا تۇتۇش سىلەر ئۈچۈن (ئېغىز ئوچۇق يۈرۈشتىن ۋە فىدىيە بېرىشتىن) ياخشىدۇر'),
(192, 2, 185, 'رامىزان ئېيىدا قۇرئان نازىل بولۇشقا باشلىدى، قۇرئان ئىنسانلارغا يېتەكچىدۇر، ھىدايەت قىلغۇچى ۋە ھەق بىلەن ناھەقنى ئايرىغۇچى روشەن ئايەتلەردۇر، سىلەردىن كىمكى رامىزان ئېيىدا ھازىر بولسا رامىزان روزىسىنى تۇتسۇن؛ كىمكى كېسەل ياكى سەپەر ئۈستىدە (يەنى مۇساپىر) بولۇپ (تۇتمىغان بولسا، تۇتمىغان كۈنلەر ئۈچۈن) باشقا كۈنلەردە تۇتسۇن. اﷲ سىلەرگە ئاسانلىقنى خالايدۇ، تەسلىكنى خالىمايدۇ، (ئاغزىڭلار ئوچۇق يۈرگەن كۈنلەرنىڭ قازاسىنى قىلىش بىلەن رامىزان روزىسىنىڭ) سانىنى تولدۇرشۇڭلارنى، سىلەرنى ھىدايەت قىلغانلىقىغا اﷲ نى ئۇلۇغلىشىڭلارنى، (ئۇنىڭ ئىنئاملىرىغا) شۈكۈر قىلىشىڭلارنى خالايدۇ'),
(193, 2, 186, 'مېنىڭ بەندىلىرىم سەندىن مەن توغرۇلۇق سورىسا (ئۇلارغا ئېيتقىنكى)، مەن ھەقىقەتەن ئۇلارغا يېقىنمەن (يەنى ئۇلارنىڭ ئەھۋالىنى، سۆزلىرىنى بىلىپ تۇرىمەن)، ماڭا دۇئا قىلسا، مەن دۇئا قىلغۇچىنىڭ دۇئاسىنى ئىجابەت قىلىمەن، ئۇلار توغرا يول تېپىش ئۈچۈن مېنىڭ دەۋىتىمنى قوبۇل قىلسۇن ۋە ماڭا ئىمان ئېيتسۇن (يەنى ھەمىشە ئىمان بىلەن بولسۇن)'),
(194, 2, 187, 'روزا كېچىلىرىدە ئاياللىرىڭلارغا يېقىنچىلىق قىلىش سىلەرگە ھالال قىلىندى، ئۇلار سىلەر ئۈچۈن كىيىمدۇر (يەنى ئۇلار سىلەرگە ئارىلىشىپ ياشايدۇ ۋە كىيىم بەدەنگە يېپىشقاندەك يېپىشىدۇ)، سىلەرمۇ ئۇلار ئۈچۈن كىيىمسىلەر (يەنى سىلەرمۇ ئۇلارغا ئارىلىشىپ ياشايسىلەر ۋە كىيىم بەدەنگە يېپىشقاندەك يېپىشىسىلەر). اﷲ ئۆزۈڭلارنىڭ ئۆزۈڭلارنى ئالدىغانلىقىڭلارنى (يەنى روزا كېچىسى يېقىنچىلىق قىلغىنىڭلارنى) بىلدى. اﷲ تەۋبەڭلارنى قوبۇل قىلدى، سىلەرنى ئەپۇ قىلدى. ئەمدى ئۇلارغا (يەنى ئاياللىرىڭلارغا) يېقىنچىلىق قىلىڭلار، اﷲ سىلەرگە تەقدىر قىلغان نەرسىنى (يەنى پەرزەنتنى) تەلەپ قىلىڭلار، تاكى تاڭنىڭ ئاق يىپى قارا يىپىدىن ئايرىلغانغا (يەنى تاڭ يورۇغانغا) قەدەر يەڭلار، ئىچىڭلار، ئاندىن كەچ كىرگىچە روزا تۇتۇڭلار، سىلەر مەسجىدتە ئېتىكاپتا ئولتۇرغان چېغىڭلاردا (كۈندۈز بولسۇن كېچە بولسۇن)، ئۇلارغا يېقىنچىلىق قىلماڭلار، ئەنە شۇلار اﷲ بەلگىلىگەن چېگرىلاردۇر (يەنى مەنئى قىلغان ئىشلاردۇر)، ئۇنىڭغا يېقىنلاشماڭلار. كىشىلەرنىڭ (ھارام قىلىنغان ئىشلاردىن) ساقلىنىشلىرى ئۈچۈن، اﷲ ئايەتلىرىنى ئۇلارغا مۇشۇنداق بايان قىلىدۇ'),
(195, 2, 188, 'بىر - بىرىڭلارنىڭ ماللىرىنى ناھەق يەۋالماڭلار، بىلىپ تۇرۇپ كىشىلەرنىڭ بىر قىسىم ماللىرىنى زۇلۇم بىلەن يەۋېلىش ئۈچۈن ھاكىملارغا پارا بەرمەڭلار'),
(196, 2, 189, '(ئى مۇھەممەد!) سەندىن يېڭى چىققان ئاي (يەنى ئاينىڭ كىچىك چىقىپ چوڭىيىدىغانلىقى، ئاندىن كىچىكلەيدىغانلىقى) توغرۇلۇق سورىشىدۇ، (ئۇلارغا) ئېيتقىنكى، «ئۇ كىشىلەرنىڭ (تىرىكچىلىك، سودا - سېتىق، مۇئامىلە، روزا تۇتۇش، ئېغىز ئېچىش قاتارلىق) ئىشلارنىڭ ۋە ھەجنىڭ ۋاقىتلىرىنى ھېسابلىشى ئۈچۈندۇر». (جاھىلىيەت دەۋرىدە قىلغىنىڭلاردەك ئېھرامدىكى چېغىڭلاردا) ئۆيلەرنىڭ ئارقىسىدىن (تۆشۈك ئېچىپ) كىرىش ياخشىلىققا ياتمايدۇ. (اﷲ نىڭ ئەمرىگە مۇخالىپ ئىش قىلىشتىن) قورققان ئادەم ياخشى ئەمەل (ئىگىسى) دۇر. (ئېھرامدىكى چېغىڭلاردا ئادەتتىكىدەك) ئۆيلەرنىڭ دەرۋازىلىرىدىن كىرىڭلار، بەختكە ئېرىشىشڭلار ئۈچۈن اﷲ قا تەقۋادارلىق قىلىڭلار'),
(197, 2, 190, 'سىلەرگە ئۇرۇش ئاچقان ئادەملەرگە قارشى اﷲ يولىدا جىھاد قىلىڭلار، تاجاۋۇز قىلماڭلار، تاجاۋۇز قىلغۇچىلارنى اﷲ ھەقىقەتەن دوست تۇتمايدۇ'),
(198, 2, 191, 'ئۇلارنى (يەنى مۇشرىكلارنى) (ئۇلار ھەرەمدە بولسۇن، باشقا يەردە بولسۇن، ئۇرۇش ھارام قىلىنغان ئايلاردا بولسۇن) ئۇچراتقان يەردە ئۆلتۈرۈڭلار، سىلەرنى يۇرتۇڭلاردىن (يەنى مەككىدىن) چىقىرىۋەتكەندەك، سىلەرمۇ ئۇلارنى يۇرتىدىن چىقىرىۋېتىڭلار، زىيانكەشلىك قىلىش ئۇرۇشتىنمۇ ياماندۇر. تاكى ئۇلار ئۆزلىرى ئۇرۇش ئاچمىغىچە مەسجىدى ھەرەم ئەتراپىدا ئۇلار بىلەن ئۇرۇش قىلماڭلار، ئەگەر ئۇلار (مەسجىدى ھەرەم ئەتراپىدا) ئۆزلىرى ئۇرۇش ئاچسا، ئۇلارنى ئۆلتۈرۈۋېرىڭلار. كاپىرلارنىڭ جازاسى شۇنداق بولىدۇ'),
(199, 2, 192, 'ئەگەر ئۇلار ئۇرۇشنى توختاتسا (سىلەرمۇ ئۇرۇشتىن قول يىغىڭلار)، اﷲ (تەۋبە قىلغۇچىلارغا) ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(200, 2, 193, 'تاكى زىيانكەشلىك تۈگىگەن ۋە اﷲ نىڭ دىنى يولغا قويۇلغانغا قەدەر ئۇلار بىلەن ئۇرۇشۇڭلار؛ ئەگەر ئۇلار ئۇرۇشنى توختاتسا، زۇلۇم قىلغۇچىلاردىن باشقىلارغا دۈشمەنلىك قىلىشقا بولمايدۇ'),
(201, 2, 194, '(ئۇرۇش) ھارام قىلىنغان ئاي (ئۇرۇش) ھارام قىلىنغان ئايغا تەڭدۇر (يەنى ئۇرۇش ھارام قىلىنغان. ئايلاردا دۈشمىنىڭلار سىلەرگە چېقىلسا، شۇ ئايلاردا سىلەرمۇ ئۇلارغا چېقىلساڭلار بولىدۇ). ھۆرمىتى ساقلىنىشقا تېگىشلىك نەرسىلەر تەڭ - باراۋەردۇر (يەنى ھۆرمىتى ساقلىنىشقا تېگىشلىك نەرسىلەرنى باشقىلار دەپسەندە قىلسا، ئىنتىقام ئېلىش يۈزسىدىن، سىلەرمۇ ئۇلارنى دەپسەندە قىلساڭلار ھېچ يامىنى يوق. (بىراۋ سىلەرگە قانچىلىك چېقىلغان بولسا، سىلەرمۇ ئۇنىڭغا شۇنچىلىك چېقىلىڭلار؛ اﷲ قا تەقۋادارلىق قىلىڭلار، بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر'),
(202, 2, 195, 'اﷲ نىڭ يولىدا (پۇل - مال) سەرپ قىلىڭلار، ئۆزۈڭلارنى ھالاكەتكە تاشلىماڭلار، ئېھسان قىلىڭلار، ئېھسان قىلغۇچىلارنى اﷲ ھەقىقەتەن دوست تۇتىدۇ'),
(203, 2, 196, 'ھەج بىلەن ئۆمرەنى اﷲ ئۈچۈن تولۇق ئورۇنلاڭلار، ئەگەر (دۈشمەن ياكى كېسەللىك سەۋەبىدىن) ھەجنى ياكى ئۆمرەنى ئادا قىلىشتا توسقۇنلۇققا ئۇچرىساڭلار، ئۇ ھالدا نېمە ئوڭاي بولسا (يەنى تۆگە، كالا، قويدىن نېمىنى تاپالىساڭلار)، شۇنى قۇربانلىق قىلىڭلار، قۇربانلىق قىلىنىدىغان مال جايىغا (يەنى بەلگىلەنگەن جايغا) يەتمىگىچە بېشىڭلارنى چۈشۈرمەڭلار، سىلەردىن كىمكى كېسەل (يەنى چېچىنى چۈشۈرسە زىيان قىلىدىغان كېسەل) ياكى بېشىدا ئىللەت (يەنى باش ئاغرىقى، پىتقا ئوخشاشلار) بولۇپ (بېشىنى چۈشۈرمىسە)، فىدىيە قىلىش يۈزىسىدىن (ئۈچ كۈن) روزا تۇتسۇن، ياكى (ئالتە مىسكىنگە ئۈچ سا) سەدىقە بەرسۇن، ياكى (بىرەر مال) قۇربانلىق قىلسۇن، ئەمىنلىكتە بولغان چېغىڭلاردا (ئاراڭلاردىن) ئۆمرە قىلغاندىن كېيىن ھەج قىلىشتىن بەھرىمەن بولغان ئادەم (يەنى ھەجدىن بۇرۇن ئۆمرە قىلىپ تاماملىغان ئادەم) نېمە ئوڭاي بولسا، شۇنى قۇربانلىق قىلسۇن، (پۇلى يوقلۇقتىن ياكى قۇربانلىق مال يوقلۇقىدىن قۇربانلىق مال) تاپالمىغان ئادەم ھەج جەريانىدا ئۈچ كۈن روزا تۇتسۇن، (ھەجدىن) قايتقاندىن كېيىن يەتتە كۈن روزا تۇتسۇن، ئۇنىڭ ھەممىسى ئون كۈن بولىدۇ. مانا بۇ (يەنى ئۆمرە قىلغان ئادەمگە قۇربانلىق قىلىش لازىملىقى ياكى روزا تۇتۇش ھەققىدىكى ھۆكۈم) مەسجىدى ھەرەم دائىرىسىدە (يەنى ھەرەمدە) ئولتۇرۇشلۇق بولمىغان كىشىلەر ئۈچۈندۇر. اﷲ قا تەقۋادارلىق قىلىڭلار، بىلىڭلاركى، اﷲ نىڭ ئازابى قاتتىقتۇر'),
(204, 2, 197, 'ھەج ۋاقتى مەلۇم بىرقانچە ئايدۇر (يەنى شەۋۋال، زۇلقەئدە ئايلىرى ۋە زۇلھەججە ئېيىنىڭ ئون كۈنىدۇر). بۇ ئايلاردا ھەج قىلىشنى نىيەت قىلغان (يەنى ئېھرام باغلىغان) ئادەمنىڭ جىنسىي ئالاقە قىلىشى، گۇناھ قىلىشى ۋە جاڭجال قىلىشى مەنئى قىلىنىدۇ، سىلەر قانداقلىكى ياخشى ئەمەلنى قىلساڭلار، اﷲ ئۇنى بىلىپ تۇرىدۇ (ئاخىرەتلىكىڭلار ئۈچۈن) زاد - راھىلە ئېلىۋېلىڭلار، ئەڭ ياخشى زاد - راھىلە تەقۋادارلىقتۇر. ئى ئەقىل ئىگىلىرى! ماڭا تەقۋادارلىق قىلىڭلار'),
(205, 2, 198, 'پەرۋەردىگارىڭلاردىن (ھەج مەۋسۈمىدە تىجارەت ۋە باشقا ئوقەت ئارقىلىق) رىزىق تەلەپ قىلساڭلار، سىلەرگە ھېچ گۇناھ بولمايدۇ. ئەرەفاتتىن قايتقان چېغىڭلاردا مەشئەرى ھەرەمدا (يەنى مۇزدەلىفەدە) اﷲ نى ياد ئېتىڭلار، اﷲ سىلەرنى ھىدايەت قىلغانلىقى ئۈچۈن، ئۇنى ياد ئېتىڭلار، ئىلگىرى سىلەر ھەقىقەتەن گۇمراھ ئىدىڭلار (يەنى اﷲ سىلەرنى ھىدايەت قىلىشتىن ئىلگىرى گۇمراھلار قاتارىدا ئىدىڭلار)'),
(206, 2, 199, 'ئاندىن كېيىن، سىلەر (ئەرەفاتتىن) كىشىلەر قايتقان جاي بىلەن قايتىڭلار، اﷲ تىن مەغپىرەت تەلەپ قىلىڭلار. اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(207, 2, 200, 'سىلەر ھەجگە ئائىت ئىبادەتلەرنى ئادا قىلغاندىن كېيىن، اﷲ نى بولسا ئاتا - بوۋاڭلارنى (پەخىرلىنىپ) ياد ئەتكەندەك ياكى ئۇنىڭدىنمۇ زىيادە ياد ئېتىڭلار، بەزى كىشىلەر: «ئى پەرۋەردىگارىمىز! بىزگە (نېسىۋىمىزنى) بۇ دۇنيادىلا بەرگىن» دەيدۇ. ھالبۇكى، ئۇنىڭغا ئاخىرەتتە (ياخشىلىقتىن) ھېچ نېسىۋە يوقتۇر'),
(208, 2, 201, 'بەزى كىشىلەر: «پەرۋەردىگارىمىز! بىزگە دۇنيادا ياخشىلىق ئاتا قىلغىن، ئاخىرەتتىمۇ ياخشىلىق ئاتا قىلغىن، بىزنى دوزاخ ئازابىدىن ساقلىغىن» دەيدۇ'),
(209, 2, 202, 'ئەنە شۇلار ئۆزلىرى قىلغان ئەمەللىرىنىڭ مول مېۋىسىنى كۆرىدۇ. اﷲ نىڭ ھېساب ئېلىشى تېزدۇر (يەنى شۇنچە كۆپ خالايىقتىن قىسقا ۋاقىت ئىچىدە ھېساب ئېلىپ بولىدۇ)'),
(210, 2, 203, 'ساناقلىق كۈنلەردە (يەنى تەشرىق كۈنلىرىدە) اﷲ نى ياد ئېتىڭلار (يەنى نامازلىرىڭلارنىڭ ئاخىرىدا تەكبىر ئېيتىڭلار)، (مىنادىن) ئالدىراپ ئىككى كۈندە قايتقان ئادەمگە ھېچ گۇناھ بولمايدۇ، تەخىر قىلىپ قايتقان ئادەمگىمۇ ھېچ گۇناھ بولمايدۇ. (يۇقىرىقى ئەھكاملار) تەقۋادارلىق قىلىپ (ھەجنى تولۇق ئادا قىلماقچى بولغان) ئادەم ئۈچۈندۇر. اﷲ تىن قورقۇڭلار؛ بىلىڭلاركى، سىلەر ھېساب ئۈچۈن اﷲ نىڭ دەرگاھىغا يىغىلىسىلەر'),
(211, 2, 204, '(ئى مۇھەممەد!) كىشىلەر ئارىسىدا شۇنداق ئادەم باركى، ئۇنىڭ دۇنيا تىرىكچىلىكى توغرىسىدىكى سۆزى سېنى قىزىقتۇرىدۇ (لېكىن ئۇ يالغانچى مۇناپىقتۇر)، ئۇ دىلىدىكى نەرسىگە (يەنى دىلى باشقا تىلى باشقا ئەمەسلىكىگە) اﷲ نى گۇۋاھ قىلىدۇ. ھالبۇكى، ئۇ (ساڭا ۋە ساڭا ئەگەشكۈچىلەرگە) ئەشەددىي دۈشمەندۇر (كۆرۈنۈشتە ئۇ شېرىن سۆزى ئارقىلىق دىندار قىياپەتكە كىرىۋالىدۇ)'),
(212, 2, 205, 'ئۇ (ھۇزۇرۇڭدىن) قايتقاندىن كېيىن، زېمىندا بۇزۇقچىلىق قىلىش ئۈچۈن ۋە زىرائەتلەرنى، ھايۋاناتلارنى ھالاك قىلىش ئۈچۈن تىرىشىدۇ (ئۇنىڭ بۇزغۇنچىلىقى ئەمەلدە ئىنسانلارنى ھالاك قىلىش ئۈچۈندۇر، چۈنكى زىرائەتلەرسىز ۋە ھايۋاناتلارسىز ئىنسانلارنىڭ ياشىيالىشى مۇمكىن ئەمەس). اﷲ بۇزۇقچىلىقنى ياقتۇرمايدۇ'),
(213, 2, 206, 'ئۇنىڭغا (ۋەز - نەسىھەت قىلىنىپ يامان سۆز - ھەرىكەتلىرىڭدىن قايتىپ) اﷲ تىن قورققىن دېيىلسە، غۇرۇرى ئۇنىڭغا گۇناھ يۈكلەيدۇ (يەنى ھەقتىن تەكەببۇرلۇق بىلەن باش تارتىپ، پىتنە - پاساتتا تېخىمۇ ئەزۋەيلەيدۇ)، ئۇنىڭغا جەھەننەم يېتەرلىكتۇر، جەھەننەم ناھايىتى يامان جايدۇر'),
(214, 2, 207, 'بەزى كىشىلەر باركى، ئۇلار اﷲ نىڭ رەزاسى ئۈچۈن جېنىنى پىدا قىلىدۇ (قىلغان ئەمەلى بىلەن پەقەت اﷲ نىڭ رازىلىقىنىلا تىلەيدۇ). اﷲ بەندىلىرىگە تولىمۇ مەرھەمەتلىكتۇر'),
(215, 2, 208, 'ئى مۆمىنلەر! ئىسلام دىنىغا پۈتۈنلەي كىرىڭلار (يەنى ئسلام دىنىنىڭ پۈتۈن ئەھكاملىرىغا بويسۇنۇڭلار، بەزى ئەھكاملىرىغا ئەمەل قىلىپ، بەزى ئەھكاملىرىنى تەرك ئەتمەڭلار)، شەيتاننىڭ يوللىرىغا (ۋە ئىغۋاسىغا) ئەگەشمەڭلار، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر'),
(216, 2, 209, 'سىلەرگە (ئىسلام دىنىنىڭ ھەقلىقىنى كۆرسىتىدىغان) روشەن دەلىللەر كەلگەندىن كېيىن (توغرا يولدىن) تېيىپ كەتسەڭلار، بىلىڭلاركى، (سىلەر جازالىنىسىلەر، چۈنكى) اﷲ غالىبتۇر (يەنى سىلەردىن ئىنتىقام ئېلىشتىن ئاجىز ئەمەستۇر)، ھېكمەت بىلەن ئش قىلغۇچىدۇر'),
(217, 2, 210, 'ئۇلار (يەنى ئسلامغا كىرىشنى تەرك ئەتكەنلەر) پەقەت اﷲ نىڭ (قىيامەت كۈنى خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىش ئۈچۈن) بۇلۇت پارچىلىرى ئىچىدە كېلىشىنى، پەرىشتلەرنىڭ كېلىشىنى ۋە (خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىلىپ، بىر گۇرۇھ جەننەتى، بىر گۇرۇھ دوزىخى بولۇپ ئايرىلىش بىلەن خالايىقنىڭ) ئىشىنىڭ پۈتۈشىنى كۈتەمدۇ؟ (بەندىلەرنىڭ) ھەممە ئىشى اﷲ قا قايتۇرۇلىدۇ'),
(218, 2, 211, '(ئى مۇھەممەد!) ئىسرائىل ئەۋلادىدىن سورىغىنكى، بىز ئۇلارغا نەقەدەر كۆپ روشەن دەلىللەرنى ئاتا قىلدۇق. كىمكى اﷲ نىڭ نېمىتىگە ئېرىشكەندىن كېيىن، ئۇنى (كۇفرىلىق قىلىپ) ئۆزگەرتىۋەتسە (اﷲ ئۇنىڭغا ئازاب قىلىدۇ)، اﷲ نىڭ ئازابى ھەقىقەتەن قاتتىقتۇر'),
(219, 2, 212, 'كاپىرلارغا دۇنيا تىرىكچىلىكى چىرايلىق كۆرسىتىلدى (يەنى ئۇلار دۇنيانىڭ شەھۋەتلىرىگە، نېمەتلىرىگە مەپتۇن بولىدۇ)، مۆمىنلەرنى مەسخىرە قىلىدۇ، ھالبۇكى قىيامەت كۈنى تەقۋادارلار ئۇلاردىن (دەرىجە جەھەتتە) ئۈستۈن تۇرىدۇ؛ اﷲ خالىغان كىشىگە ھېسابسىز رىزىق بېرىدۇ'),
(220, 2, 213, 'ئىنسانلار (دەسلەپتە) بىر ئۈممەت (يەنى ھەق دىندا) ئىدى (كېيىن ئۇلارنىڭ بەزىسى ئىمان ئېيتىپ، بەزىسى ئىمان ئېيتماي ئىختىلاپ قىلىشتى)، اﷲ (مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (كۇففارلارنى دوزاختىن) ئاگاھلاندۇرغۇچى پەيغەمبەرلەرنى ئەۋەتتى؛ (اﷲ) كىشىلەرنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىش ئۈچۈن، ئۇلارغا ھەق كىتابنى نازىل قىلدى، پەقەت كىتاب بېرىلگەن كىشىلەرلا ئۆزلىرىگە روشەن دەلىللەر كەلگەندىن كېيىن ئۆزئارا ھەسەت قىلىشىپ (يەنى كاپىرلار مۆمىنلەرگە ھەسەت قىلىپ)، كىتاب توغرىسىدا ئىختىلاپ قىلىشتى، اﷲ ئۆز ئىرادىسى بويىچە مۆمىنلەرنى ئۇلار (يەنى گۇمراھلار) ئىختىلاپ قىلىشقان ھەقىقەتكە ھىدايەت قىلدى. اﷲ خالىغان كىشىنى توغرا يولغا باشلايدۇ'),
(221, 2, 214, 'سىلەر تېخى ئىلگىرىكىلەر ئۇچرىغان كۈلپەتلەرگە ئۇچرىماي تۇرۇپ جەننەتكە كىرىشنى ئويلامسىلەر؟ سىلەردىن ئىلگىرى ئۆتكەن (مۆمىن) لەر ئېغىرچىلىقلارغا، كۈلپەتلەرگە ئۇچرىغان ۋە چۆچۈتۈۋېتىلگەن ئىدى، ھەتتا پەيغەمبەر ۋە مۆمىنلەر: «اﷲ نىڭ (بىزگە ۋەدە قىلغان) ياردىمى قاچان كېلىدۇ؟» دېگەن ئىدى. بىلىڭلاركى، اﷲ نىڭ ياردىمى ھەقىقەتەن يېقىندۇر'),
(222, 2, 215, '(ئى مۇھەممەد!) سەندىن (ماللىرىنى) قانداق سەرپ قىلسا بولدىغانلىقىنى سورايدۇ، ئېيتقىنكى، «مال - مۈلكىڭلاردىن نېمىنى سەرپ قىلماڭلار، ئۇنى ئاتا ئاناڭلارغا، خىش - ئەقرىبالىرىڭلارغا، مىسكىنلەرگە ۋە ئىبن سەبىللەرگە سەرپ قىلساڭلار بولىدۇ. ياخشىلىقتىن نېمىنى قىلماڭلار، اﷲ ئۇنى بىلىپ تۇرىدۇ»'),
(223, 2, 216, 'سىلەرگە جىھاد پەرز قىلىندى، ھالبۇكى، سىلەر ئۇنى ياقتۇرمايسىلەر، سىلەر بىرەر نەرسىنى ياقتۇرماسلىقىڭلار مۇمكىن، ئەمما ئۇ سىلەر ئۈچۈن پايدىلىقتۇر؛ سىلەر بىرەر نەرسىنى ياقتۇرۇشۇڭلار مۇمكىن، ئەمما ئۇ سىلەر ئۈچۈن زىيانلىقتۇر. (سىلەرگە نېمىنىڭ پايدىلىق ئىكەنلىكىنى) اﷲ بىلىدۇ، سىلەر بىلمەيسىلەر، (شۇنىڭ ئۈچۈن) اﷲ بۇيرۇغانغا ئالدىراڭلار'),
(224, 2, 217, '(ئى مۇھەممەد!) ئۇلار سەندىن: «ھارام قىلىنغان ئايدا ئۇرۇش قىلىشقا بولامدۇ؟» دەپ سورايدۇ، ئېيتقىنكى، «بۇ ئايدا ئۇرۇش قىلىش چوڭ گۇناھتۇر؛ اﷲ نىڭ يولىدىن توسۇش، اﷲ قا كۇفرىلىق قىلىش، مەسجىدى ھەرەمدا (ئىبادەت قىلىشتىن) توسۇش ۋە مەسجىدى ھەرەمدىن ئاھالىنى ھەيدەپ چىقىرىش اﷲ نىڭ دەرگاھىدا تېخىمۇ چوڭ گۇناھتۇر، پىتنە (يەنى مۆمىنلەرگە زىيانداشلىق قىلىش) ئادەم ئۆلتۈرۈشتىنمۇ قاتتىق گۇناھتۇر». ئۇلار (يەنى كۇففارلار) قولىدىن كەلسىلا سىلەرنى دىنىڭلاردىن قايتۇرۇۋەتكەنگە قەدەر سىلەر بىلەن داۋاملىق ئۇرۇشىدۇ. سىلەردىن كىمكى ئۆز دىنىدىن قايتىپ كاپىر پېتى ئۆلسە، ئۇنىڭ ئەمەللىرى دۇنيا ۋە ئاخىرەتتە بىكار بولۇپ كېتىدۇ. بۇنداق ئادەملەر ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر'),
(225, 2, 218, 'شۈبھىسىزكى، ئمان ئېيتقانلار، ھىجرەت قىلغانلار ۋە اﷲ نىڭ يولىدا جىھاد قىلغانلار ئەنە شۇنداق كىشىلەر اﷲ نىڭ رەھمىتىنى ئۈمىد قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(226, 2, 219, '(ئى مۇھەممەد!) سەندىن ھاراق ۋە قىمار توغرىسىدا سورىشىدۇ، سەن ئۇلارغا: «بۇنىڭ ھەر ئىككىسىدە چوڭ گۇناھ ۋە كىشىلەرگە (ئازغىنا ماددى) پايدىمۇ بار، لېكىن ئۇلاردىكى گۇناھ پايدىغا قارىغاندا تېخىمۇ چوڭ» دېگىن، ئۇلار سەندىن نېمىنى سەدىقە قىلىش توغرۇلۇق سورايدۇ، «(ئېھتىياجدىن) ئارتۇقىنى (سەدىقە قىلىڭلار)» دېگىن. (دۇنيا ۋە ئاخىرەتنىڭ ئىشلىرىنى) تەپەككۇر قىلىشىڭلار ئۈچۈن اﷲ ئۆز ئايەتلىرىنى سىلەرگە شۇنداق بايان قىلىدۇ'),
(227, 2, 220, '(ئى مۇھەممەد!) ئۇلار سەندىن يېتىملەر توغرۇلۇق سورىشىدۇ، ئېيتقىنكى، «(تەربىيىلەش، ماللىرىنى ئۆستۈرۈپ) ئۇلارنى تۈزەش ياخشىدۇر، ئۇلار بىلەن (ئۇلارغا پايدىلىق بولغان رەۋىشتە) ئارىلىشىپ ياشىساڭلار، ئۇ چاغدا (يەنى ئۇلارغا قېرىنداشلارچە مۇئامىلە قىلىڭلار)، ئۇلار سىلەرنىڭ دىنىي قېرىنداشلىرىڭلاردۇر. اﷲ (يېتىملەرنىڭ ئىشلىرىنى) بۇزغۇچى ۋە تۈزىگۈچىنى بىلىپ تۇرىدۇ. ئەگەر اﷲ خالىسا، سىلەرنى (قىيىن ئىشقا تەكلىپ قىلىش بىلەن) چوقۇم جاپاغا سالاتتى». اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(228, 2, 221, '(ئى مۇسۇلمانلار!) مۇشرىك ئاياللار ئىمان ئېيتمىغىچە ئۇلارنى نىكاھىڭلارغا ئالماڭلار. (ھۆر) مۇشرىك ئايال (ھۆسن - جامالى ۋە مېلى بىلەن) سىلەرنى مەپتۇن قىلغان تەقدىردىمۇ، مۆمىن دېدەك، ئەلۋەتتە، ئۇنىڭدىن ئارتۇقتۇر. مۇشرىك ئەرلەر ئىمان ئېيتمىغىچە مۆمىن ئاياللارنى ئۇلارغا ياتلىق قىلماڭلار، (ھۆر) مۇشرىك ئەر سىلەرنى مەپتۇن قىلغان تەقدىردىمۇ مۆمىن قۇل، ئەلۋەتتە، ئۇنىڭدىن ئارتۇقتۇر. ئەنە شۇلار (يەنى مۇشرىك ئەر ۋە ئاياللار سىلەرنى) دوزاخقا چاقىرىدۇ، اﷲ ئۆز ئىرادىسى بويىچە (سىلەرنى) جەننەتكە، مەغپىرەتكە چاقىرىدۇ، كىشىلەر پەند - نەسىھەت ئالسۇن دەپ اﷲ ئۇلارغا ئۆز ئايەتلىرىنى بايان قىلىدۇ'),
(229, 2, 222, '(ئى مۇھەممەد!) ئۇلار سەندىن ھەيز توغرۇلۇق (يەنى ھەيزدار ئايال بىلەن جىنسىي ئالاقە قىلىشنىڭ دۇرۇسلۇقى ياكى دۇرۇس ئەمەسلىكى توغرۇلۇق) سورايدۇ. ئېيتقىنكى، «ھەيز زىيانلىقتۇر (يەنى ھەيز مەزگىلىدە جىنسىي ئالاقە قىلىش ئەر - خوتۇن ھەر ئىككىسىگە زىيانلىقتۇر)، ھەيز مەزگىلىدە ئايالىڭلاردىن نېرى تۇرۇڭلار، (ھەيزدىن) پاك بولغۇچە ئۇلارغا يېقىنچىلىق قىلماڭلار، پاك بولغاندا، ئۇلارغا اﷲ بۇيرۇغان جايدىن يېقىنچىلىق قىلىڭلار». اﷲ ھەقىقەتەن تەۋبە قىلغۇچىلارنى دوست تۇتىدۇ. (ھارامدىن ۋە نىجاسەتتىن) پاك بولغۇچىلارنى ھەقىقەتەن دوست تۇتىدۇ'),
(230, 2, 223, 'ئاياللىرىڭلار سىلەر ئۈچۈن (خۇددى) ئېكىنزارلىقتۇر (يەنى نەسىل تېرىيدىغان جايدۇر). ئېكىنزارلىقىڭلارغا خالىغان رەۋىشتە كېلىڭلار، ئۆزۈڭلار ئۈچۈن ئالدىنئالا ياخشى ئەمەل تەييارلاڭلار، (گۇناھتىن چەكلىنىش بىلەن) اﷲ تىن قورقۇڭلار، بىلىڭلاركى، سىلەر اﷲ قا مۇلاقات بولۇسىلەر، مۆمىنلەرگە (جەننەت بىلەن) خۇش خەۋەر بەرگىن'),
(231, 2, 224, 'ياخشىلىق قىلماسلىققا، تەقۋادار بولماسلىققا ۋە كىشىلەرنى ئەپلەشتۈرمەسلىككە قەسەم قىلىش شەكلى بىلەن (ئۇلارنى قىلىشقا) اﷲ نى توسالغۇ قىلماڭلار (يەنى اﷲ بىلەن قەسەم قىلىشنى ياخشى ئىشلارنى قىلماسلىقنىڭ سەۋەبى قىلماڭلار)، اﷲ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر'),
(232, 2, 225, 'مەقسەتسىز ئىچكەن قەسىمىڭلار ئۈچۈن اﷲ سىلەرنى جازاغا تارتمايدۇ، قەستەن ئىچكەن قەسىمىڭلار ئۈچۈن اﷲ سىلەرنى جازاغا تارتىدۇ. اﷲ مەغپىرەت قىلغۇچىدۇر، ھەلىمدۇر (يەنى بەندىلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)'),
(233, 2, 226, 'ئاياللىرىغا يېقىنچىلىق قىلماسلىققا قەسەم ئىچكەن كىشىلەر تۆت ئاي كۈتىدۇ؛ ئەگەر ئۇلار (بۇ مۇددەت ئىچىدە ئاياللىرىغا يېقىنچىلىق قىلىشقا) قايتسا، (ئاياللىرىغا يامانلىق قىلغان گۇناھىنى) اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر. اﷲ ناھايىتى مېھرىباندۇر'),
(234, 2, 227, 'ئەگەر ئۇلار تالاق قىلىش نىيىتىگە كەلسە، اﷲ ھەقىقەتەن (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(235, 2, 228, 'تالاق قىلىنغان ئاياللار ئۈچ ھەيز ئۆتكۈچە ئىددەت تۇتىدۇ. ئەگەر ئۇلار اﷲ قا، ئاخىرەت كۈنىگە ئىشىنىدىكەن، بەچچىدانلىرىدىكى اﷲ ياراتقان نەرسىنى (يەنى بالىنى ياكى ھەيزنى) يوشۇرۇشى دۇرۇس ئەمەس، ئۇلارنىڭ ئەرلىرى ئەگەر ئەپلىشىپ قېلىشنى خالىسا، شۇ مۇددەت ئىچىدە ئۇلارنى قايتۇرۇپ كېلىشكە (باشقىلارغا قارىغاندا) ئەڭ ھەقلىقتۇر. ئاياللىرى ئۈستىدە ئەرلەرنىڭ ھەقلىرى بولغىنىدەك، ئەرلىرى ئۈستىدە ئاياللارنىڭمۇ ھەقلىرى بار (يەنى ئاياللار مۇۋاپىق دەرىجىدە ھوقۇقتىن بەھرىمەن بولۇشى كېرەك، مۇۋاپىق دەرىجىدە مەجبۇرىيەتمۇ ئۆتىشى كېرەك). (ئاياللارغا مەھرى بېرىش ۋە ئۇلارنىڭ تۇرمۇشىنى قامداش مەجبۇرىيەتلىرى ئەرلەرگە يۈكلەنگەنلىكتىن) ئەرلەر ئاياللاردىن بىر دەرىجە ئارتۇقلۇققا ئىگە. اﷲ غالىبتۇر. ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(236, 2, 229, '(قايتا يارىشىشقا بولىدىغان) تالاق ئىككى قېتىمدۇر، ئۇنىڭدىن كېيىن (خوتۇنىنى) چىرايلىقچە تۇتۇش، ياكى ياخشىلىق بىلەن (يەنى زۇلۇم قىلماي، يامان گېپىنى قىلماي، كىشىلەرنى ئۇنىڭدىن يىراقلاشتۇرماي) قويۇۋېتىش لازىم، اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالماسلىق خەۋپى (يەنى ياخشى ئۆتەلمەسلىك، اﷲ بەلگىلىگەن ئەر - خوتۇنلۇق ھوقۇقلىرىغا رىئايە قىلالماسلىق خەۋپى) بولمىسىلا، ئۆزۈڭلارنىڭ ئاياللىرىڭلارغا (مەھرى ئۈچۈن) بەرگەن مال - مۈلۈكتىن ھېچقانداق نەرسىنى قايتۇرۇۋېلىش دۇرۇس بولمايدۇ. ئەگەر سىلەر ئۇلار (يەنى ئەر - ئايال) نىڭ اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالماسلىقىدىن قورقساڭلار، خوتۇننىڭ (تالاق خېتىنى ئېلىش مەقسىتىدە) مال بەرگەنلىكى ئۈچۈن ئۇ ئىككىسىگە (يەنى ئەر - خوتۇنغا) گۇناھ بولمايدۇ. ئەنە شۇلار اﷲ نىڭ بەلگىلىمىلىرىدۇر، ئۇنىڭغا خىلاپلىق قىلماڭلار اﷲ نىڭ بەلگىلىمىلىرىگە خىلاپلىق قىلغۇچىلار زالىملاردۇر'),
(237, 2, 230, 'ئەگەر (ئۇ) ئۇنى (يەنى خوتۇنىنى) (ئۈچ قېتىم) تالاق قىلسا، (خوتۇن) باشقا ئەرگە تەگمىگىچە ئۇنىڭغا دۇرۇس بولمايدۇ، كېيىنكى ئەر ئۇنى تالاق قىلغاندىن كېيىن (ئىددىتىنى توشقۇزۇپ)، بۇرۇنقى ئەر بىلەن قايتا ياراشسا، ئۈلارغا ھېچ گۇناھ بولمايدۇ. ئەگەر ئۇلار اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالايدىغانلىقىنى (يەنى قايتا ياراشقاندىن كېيىن ياخشى ئۆتەلەيدىغانلىقىنى) تەسەۋۋۇر قىلسا. بۇ اﷲ نىڭ بەلگىلىمىلىرىدۇركى، اﷲ ئۇنى بىلىدىغان (يەنى ئىشنىڭ ئاقىۋىتىگە پەمى يېتىدىغان) قەۋم ئۈچۈن بايان قىلىدۇ'),
(238, 2, 231, 'سىلەر ئاياللارنى تالاق قىلغان بولساڭلار، ئۇلارنىڭ ئىددىتى توشاي دەپ قالغان بولسا، ئۇلار بىلەن چىرايلىقچە يارىشىپ، (ئۇلارنى نىكاھىڭلاردا) تۇتۇڭلار، ياكى ئۇلارنى ياخشىلىق بىلەن قويۇۋېتىڭلار، زۇلۇم قىلىش مەقسىتىدە زىيان يەتكۈزۈپ ئۇلارنى تۇتۇۋالماڭلار. كىمكى شۇنداق قىلىدىكەن (يەنى كىمكى چىرايلىقچە ئۆي تۇتۇش نىيىتى بىلەن ئەمەس، بەلكى ئىددىتىنى ئۇزارتىپ زۇلۇم قىلىش نىيىتىدە خوتۇنى بىلەن يارىشىدىكەن)، ئۆزىگە ئۆزى زۇلۇم قىلغان بولىدۇ. اﷲ نىڭ ئايەتلىرىنى كۈلكە قىلىۋالماڭلار (يەنى اﷲ نىڭ ئەھكاملىرىنى خىلاپلىق قىلىش يولى بىلەن مەسخىرە قىلماڭلار)، اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ۋە سىلەرگە ۋەز - نەسىھەت قىلىش ئۈچۈن نازىل قىلغان كىتابنى، ھېكمىتىنى (يەنى كىتابدىكى ئەھكاملارنى) ياد ئېتىڭلار، اﷲ قا تەقۋادارلىق قىلىڭلار. بىلىڭلاركى، اﷲ ھەر نەرسىنى بىلگۈچىدۇر'),
(239, 2, 232, 'ئەگەر سىلەر ئاياللارنى تالاق قىلغان بولۇپ، ئۇلارنىڭ ئىددىتى توشقان ۋە قائىدە بويىچە (تەگمەكچى بولغان) ئېرى بىلەن پۈتۈشكەن بولسا، ئۇلارنى نىكاھلىنىشتىن توسماڭلار. بۇنىڭ بىلەن، ئىچىڭلاردىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان كەلتۈرگەنلەرگە نەسىھەت قىلىنىدۇ، بۇ (يەنى ۋەز - نەسىھەت ئېلىش، اﷲ نىڭ ئەمرىلىرىنى تۇتۇش)، سىلەر ئۈچۈن ئەڭ پايدىلىقتۇر ۋە ئەڭ پاكتۇر، (شەرىئەت ئەھكاملىرىدىن سىلەرگە نېمىلەر ئەڭ پايدىلىق ئىكەنلىكىنى) اﷲ بىلىدۇ، سىلەر بىلمەيسىلەر'),
(240, 2, 233, 'ئانىلار (بالىلارنى ئېمىتىش مۇددىتىدە) تولۇق ئېمىتمەكچى بولسا، تولۇق ئىككى يىل ئېمىتىشى لازىم. ئاتىلار ئانىلارنى قائىدە بويىچە يېمەك - ئىچمەك ۋە كىيىم - كېچەك بىلەن تەمىنلەپ تۇرۇشى كېرەك، كىشى پەقەت قولىدىن كېلىدىغان ئىشقىلا تەكلىپ قىلىنىدۇ. ئانىنى بالىسى سەۋەبلىك زىيان تارتقۇزماسلىق لازىم. (ئەگەر ئاتا ئۆلۈپ كەتسە) ئۇنىڭ ۋارىسى ئاتىغا ئوخشاش مەسئۇلىيەتنى (يەنى ئانا بولغۇچىغا نەپىقە بېرىش ۋە ئۇنىڭ ھەقلىرىگە رىئايە قىلىش قاتارلىقلارنى) ئۆز ئۈستىگە ئېلىشى كېرەك. ئەگەر ئاتا - ئانا كېڭىشىپ (بالىنى ئىككى يىل توشماستىنلا) ئايرىۋەتمەكچى بولسا، ئۇلارغا ھېچبىر گۇناھ بولمايدۇ. ئەگەر بالىلىرىڭلارنى ئىنىكئانىلارغا ئېمىتمەكچى بولساڭلار، قائىدە بويىچە ئۇلارنىڭ ھەققىنى بەرسەڭلارلا سىلەرگە ھېچ گۇناھ بولمايدۇ. اﷲ تىن (يەنى اﷲ نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىشتىن) قورقۇڭلار، بىلىڭلاركى، اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(241, 2, 234, 'ئىچىڭلاردا ئۆزى ۋاپات بولۇپ ئاياللىرى قېلىپ قالغان كىشىلەر بولسا، ئاياللىرى تۆت ئاي ئون كۈن ئىددەت تۇتۇشى لازىم، ئىددىتى توشقاندىن كېيىن ئۆزلىرى توغرۇلۇق قائىدە بويىچە ئىش قىلسا، سىلەرگە (يەنى شۇ ئاياللارغا ئىگىدارچىلىق قىلغۇچىلارغا) ھېچ گۇناھ بولمايدۇ. اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلاردىن خەۋەرداردۇر'),
(242, 2, 235, 'ئىددىتى توشمىغان ئاياللارغا ئۇلارنى ئالىدىغانلىقىڭلارنى بۇرىتىپ ئۆتسەڭلار ياكى بۇنى دىلىڭلاردا يوشۇرۇن تۇتساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ. ئۇلارغا ئېغىز ئاچىدىغانلىقىڭلار اﷲ قا مەلۇمدۇر، لېكىن ئۇلار بىلەن يوشۇرۇن ۋەدىلىشىپ قويماڭلار، پەقەت (ئاشكارا ئېيتىلسا كىشى خىجىل بولمايدىغان) مۇۋاپىق سۆزنى قىلساڭلار بولىدۇ، ئىددىتى توشمىغىچە ئۇلارنى نىكاھىڭلارغا ئېلىشقا بەل باغلىماڭلار، بىلىڭلاركى، اﷲ دىلىڭلاردىكىنى بىلىپ تۇرىدۇ، ئۇنىڭدىن (يەنى اﷲ نىڭ ئەمرىگە خىلاپلىق قىلىپ جازالىنىشتىن) ھەزەر قىلىڭلار، بىلىڭلاركى، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، (اﷲ تىن قورقۇپ گۇناھتىن چەكلەنگەنلەرگە) ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)'),
(243, 2, 236, 'ئەگەر سىلەر ئاياللىرىڭلارغا يېقىنچىلىق قىلماي ياكى مەھرى تەيىنلىمەي تۇرۇپ ئۇلارنى تالاق قىلساڭلار (يەنى نىكاھىڭلارغا ئالغان ئايال بىلەن بىر يەردە بولمىغان ياكى مەھرىنى مۇئەييەنلەشتۈرمىگەن شارائىتتا) ئۇنى قويۇۋەتسەڭلار، سىلەرگە ھېچ گۇناھ بولمايدۇ، لېكىن ئۇنىڭغا مۇتئە (يەنى بەلگىلىك مىقداردا پۇل - مال) بېرىشىڭلار كېرەك، ئۇلارغا نەرسە بېرىڭلار، باي ئۆز ھالىغا يارىشا، كەمبەغەلمۇ ئۆز ھالىغا يارىشا قائىدە بويىچە بەرسۇن، (بۇ) ياخشى ئادەملەرنىڭ ئۆتەشكە تېگىشلىك مەجبۇرىيىتىدۇر'),
(244, 2, 237, 'ئەگەر سىلەر ئۇلارغا يېقىنچىلىق قىلىشتىن ئىلگىرى ۋە مەھرى تەيىنلىنىپ بولغاندىن كېيىن تالاق قىلساڭلار، تەيىنلەنگەن مەھرىنىڭ يېرىمىنى بېرىڭلار، ئەمما ئاياللار ئۆتۈنسە (يەنى مەھرىدىن ۋازكېچىپ پۈتۈنلەي ئالمىسا) ياكى نىكاھنىڭ تۈگۈنى ئۆز ئىلكىدە بولغان ئەرلەر ئۆتۈنسە (يەنى تەيىنلەنگەن مەھرىنىڭ ھەممىسىنى بەرسە)، شۇ بويىچە بىر تەرەپ قىلىش لازىم. سىلەر ئۆتۈنسەڭلار، (ئۆتۈنگەن ئادەم) تەقۋادارلىققا ئەڭ يېقىندۇر. ئۆزئارا ئېھسان قىلىشنى ئۇنتۇماڭلار. اﷲ ھەقىقەتەن قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(245, 2, 238, '(بەش) نامازنى بولۇپمۇ نامازدىگەرنى داۋاملاشتۇرۇڭلار (يەنى پۈتۈن شەرتلىرى بىلەن ۋاقتىدا تولۇق ئادا قىلىڭلار). اﷲ نىڭ ھۇزۇرىدا (يەنى نامازدا) ئىتائەتمەنلىك بىلەن تۇرۇڭلار'),
(246, 2, 239, 'قورقۇنچتا قالغىنىڭلاردا، پىيادە ياكى ئۇلاغلىق كېتىپ بېرىپ (ناماز ئوقۇڭلار)، ئامانلىق تاپقىنىڭلاردا اﷲ نىڭ سىلەرگە ئۆگەتكىنى بويىچە اﷲ نى ياد ئېتىڭلار (يەنى قورقۇنچ تۈگىگەندە، اﷲ نامازنى قانداق ئۆتەشكە ئەمر قىلغان بولسا، تولۇق ئەركانلىرى بىلەن شۇنداق ئۆتەڭلار'),
(247, 2, 240, 'سىلەرنىڭ ئىچىڭلاردا ئۆزى ۋاپات بولۇپ ئايالى قېلىپ قالىدىغانلار ئۇلارنىڭ (يەنى ئاياللىرىنىڭ) بىر يىلغىچە يېمەك - ئىچمەك، كىيىم - كېچەك بىلەن تەمىنلىنىشىنى، ئۆيىدىن ھەيدەپ چىقىرىلماسلىقىنى ۋەسىيەت قىلسۇن. ئەگەر (ئاياللىرى) ئۆزلىرى چىقىپ كەتسە، ئۇلارنىڭ ئۆزلىرى توغرىسىدا قىلغان قائىدىلىك ئشلىرى ئۈچۈن سىلەرگە (يەنى مېيىتنىڭ ئىگىلىرىگە) ھېچ گۇناھ بولمايدۇ. اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(248, 2, 241, 'تالاق قىلىنغان ئاياللارغا قائىدە بويىچە نەپىقە بېرىش لازىم. (ئۇ) تەقۋادارلارنىڭ ئۆتەشكە تېگىشلىك مەجبۇرىيىتىدۇر'),
(249, 2, 242, 'سىلەرنىڭ چۈشىنىشىڭلار ئۈچۈن اﷲ (ئۆز ئەھكاملىرىنى كۆرسىتىپ بېرىدىغان) ئايەتلىرىنى سىلەرگە مۇشۇنداق بايان قىلىدۇ'),
(250, 2, 243, 'ئۆزلىرى مىڭلارچە تۇرۇقلۇق، ئۆلۈمدىن قورقۇپ، يۇرتلىرىدىن قېچىپ چىققان كىشىلەردىن خەۋىرىڭ يوقمۇ؟ اﷲ ئۇلارنى «ئۆلۈڭلار» دېدى (ئۇلار ئۆلدى). ئاندىن كېيىن اﷲ ئۇلارنى تىرىلدۈردى، اﷲ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر ۋە لېكىن ئىنسانلارنىڭ كۆپچىلىكى (اﷲ نىڭ نېمىتىگە) شۈكۈر قىلمايدۇ'),
(251, 2, 244, 'اﷲ نىڭ يولىدا جىھاد قىلىڭلار، بىلىڭلاركى، اﷲ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيىتىڭلارنى، ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر'),
(252, 2, 245, 'كىمكى اﷲ قا قەرزىي ھەسەنە بېرىدىكەن (يەنى اﷲ نىڭ يولىدا پۇل - مېلىنى خۇشاللىق بىلەن سەرپ قىلىدىكەن)، اﷲ ئۇنىڭغا نەچچە ھەسسە (يەنى ئون ھەسسىدىن يەتتە يۈز ھەسسىگىچە) كۆپ قايتۇرىدۇ. اﷲ (سىناش ئۈچۈن، بەزى كىشىلەرنىڭ رىزقىنى) تار قىلىدۇ، (بەزى كىشىلەرنىڭ رىزقىنى) كەڭ قىلىدۇ، (قىيامەت كۈنى) اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر'),
(253, 2, 246, 'ساڭا مۇسا (ۋاپاتى) دىن كېيىن ئىسرائىل ئەۋلادىدىدىن بولغان بىر جامائەنىڭ خەۋىرى يەتمىدىمۇ؟ ئۇلار ئۆز ۋاقتىدا ئۆزلىرىنىڭ پەيغەمبىرىگە؛ «بىزگە پادىشاھ تىكلەپ بەرگىن، (ئۇنىڭ بىلەن دۈشمەنلەرگە قارشى) اﷲ نىڭ يولىدا جىھاد قىلايلى» دېيىشتى. پەيغەمبەر: «سىلەرگە جىھاد پەرز قىلىنسا جىھاد قىلماي قالارسىلەرمۇ؟» دېدى. ئۇلار: «يۇرتلىرىمىزدىن ھەيدەپ چىقىرىلغان ۋە ئوغۇللىرىمىزدىن جۇدا قىلىنغان تۇرساق قانداقمۇ اﷲ نىڭ يولىدا جىھاد قىلمايلى؟» دېدى. ئۇلارغا جىھاد پەرز قىلىنغان چاغدا، ئازغىنىسىدىن باشقا ھەممىسى جىھادتىن باش تارتتى. اﷲ زالىملارنى ئوبدان بىلگۈچىدۇر'),
(254, 2, 247, 'پەيغەمبىرى ئۇلارغا: «اﷲ ھەقىقەتەن سىلەرگە تالۇتنى پادىشاھ قىلىپ ئەۋەتتى» دېدى. ئۇلار (پەيغەمبىرىگە ئېتىراز بىلدۈرۈپ): «ئۇ قانداقمۇ بىزگە پادىشاھ بولىدۇ. (ئارىمىزدا پادىشاھ ئەۋلاتلىرى بولغانلىقتىن) پادىشاھلىققا بىز ئۇنىڭدىن ھەقلىقمىز، ئۇنىڭ ئۈستىگە ئۇنىڭ مال - مۈلكى كۆپ بولمىغان تۇرسا» دېدى. پەيغەمبەر: «اﷲ ھەقىقەتەن سىلەرگە (پادىشاھ قىلىشقا) ئۇنى ئىختىيار قىلدى، ئۇنىڭ ئىلمىنى زىيادە ۋە بەدىنىنى قابىل قىلدى، اﷲ سەلتەنەتنى خالىغان ئادەمگە بېرىدۇ. اﷲ (نىڭ پەزلى) كەڭدۇر، (ئۇنىڭغا كىمنىڭ لايىق بولىدىغانلىقىنى) ئوبدان بىلىدۇ» دېدى'),
(255, 2, 248, 'ئۇلارغا پەيغەمبىرى ئېيتتىكى: «ئۇنىڭ پادىشاھلىقىنىڭ ئالامىتى شۇكى، سىلەرگە بىر ساندۇق كېلىدۇ، ئۇنىڭدا رەببىڭلار تەرىپىدىن سىلەرنى تەسكىن تاپقۇزىدىغان نەرسە ۋە مۇسانىڭ ھارۇننىڭ تەۋەلىرى قالدۇرۇپ كەتكەن نەرسىلەر (يەنى مۇسانىڭ ھاسىسى، كىيىمى ۋە تەۋرات يېزىلغان بەزى تاختىلار) قاچىلانغان بولىدۇ، ئۇنى پەرىشتىلەر كۆتۈرۈپ كېلىدۇ. ئەگەر (اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغان) بولساڭلار، بۇنىڭدا (يەنى تابۇتنىڭ نازىل بولۇشىدا اﷲ نىڭ تالۇتنى سىلەرگە پادىشاھلىققا تاللىغانلىقىغا) ئەلۋەتتە سىلەر ئۈچۈن (روشەن) ئالامەت بار»'),
(256, 2, 249, 'تالۇت ئەسكەرلىرى بىلەن (بەيتۇلمۇقەددەستىن) ئايرىلغان چاغدا: «اﷲ سىلەرنى بىر دەريا بىلەن سىنايدۇ، كىمكى ئۇنىڭدىن ئىچىدىكەن، ئۇ ماڭا تەۋە ئەمەس، كىمكى دەريا سۈيىنى تېتىمايدىكەن، ئۇ ھەقىقەتەن ماڭا تەۋەدۇر، ئۇنىڭدىن (ئۇسسۇزلۇقىنى بىرئاز بېسىش ئۈچۈن) ئىچكەن ئادەم (بۇيرۇققا خىلاپلىق قىلغان بولمايدۇ)» دېدى. ئۇلارنىڭ ئىچىدىكى ئازغىنا كىشىدىن باشقا ھەممىسى ئۇنىڭدىن ئىچتى. تالۇت ۋە ئۇنىڭغا ئىمان ئېيتقان كىشىلەر دەريادىن ئۆتكەن چاغدا (دۈشمەننىڭ كۆپلۈكىنى كۆرۈپ قورقۇنچقا چۈشۈپ، ئۇلارنىڭ بىر پىرقىسى): «بۈگۈن بىز جالۇت ۋە ئۇنىڭ ئەسكەرلىرى بىلەن ئۇرۇشۇشقا تاقەت كەلتۈرەلمەيمىز» دېدى. (تالۇتنىڭ تەۋەلىرىدىن) اﷲ قا مۇلاقات بولۇشقا ئېتىقاد قىلىدىغانلار: «اﷲ نىڭ ئىرادىسى بىلەن ئاز جامائە كۆپ جامائە ئۈستىدىن غەلىبە قىلىدۇ» دېدى. اﷲ چىداملىق كۆرسەتكۈچىلەر بىلەن بىللىدۇر'),
(257, 2, 250, 'ئۇلار جالۇت ۋە ئۇنىڭ ئەسكەرلىرى بىلەن ئۇچراشقان چاغدا: «پەرۋەردىگارىمىز! قەلبىمىزنى چىداملىقلىق بىلەن تولدۇرغىن، قەدەملىرىمىزنى (ئۇرۇش مەيدانىدا) ساباتلىق قىلغىن، كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دېدى'),
(258, 2, 251, 'مۆمىنلەر اﷲ نىڭ ئىرادىسى بىلەن كاپىرلارنى مەغلۇپ قىلدى، داۋۇد جالۇتنى ئۆلتۈردى. اﷲ داۋۇدقا سەلتەنەتنى، ھېكمەتنى (يەنى پەيغەمبەرلىكنى) بەردى. ئۇنىڭغا خالىغان نەرسىلىرىنى (يەنى پايدىلىق ئىلىملەرنى) بىلدۈردى. اﷲ ئىنسانلارنىڭ بەزىسىنى بەزىسى بىلەن مۇداپىئە قىلىپ تۇرمىسا (يەنى كۈچلۈك تاجاۋۇزچىغا ئۇنىڭدىنمۇ كۈچلۈك بولغان بىرسىنى ئاپىرىدە قىلمىسا) ئىدى، يەر يۈزى ئەلۋەتتە پاساتقا ئايلىناتتى (يەنى خارابلىققا يۈزلىنەتتى)، لېكىن اﷲ (يامانلىقنى ئۈستۈنلۈككە ئىگە قىلماسلىق بىلەن) پۈتۈن جاھان ئەھلىگە مەرھەمەت قىلغۇچىدۇر'),
(259, 2, 252, 'بۇ اﷲ نىڭ ئايەتلىرىدۇركى، ئۇنى بىز ساڭا توغرا رەۋىشتە ئوقۇپ بېرىمىز. (ئى مۇھەممەد!) سەن ئەلۋەتتە (اﷲ نىڭ دەۋىتىنى يەتكۈزۈش ئۈچۈن ئەۋەتىلگەن) پەيغەمبەرلەرنىڭ بىرىسەن'),
(260, 2, 253, 'ئەنە شۇ پەيغەمبەرلەرنىڭ بەزىسىنى بەزىسىدىن ئۈستۈن قىلدۇق؛ اﷲ ئۇلارنىڭ بەزىسى (يەنى مۇسا ئەلەيھىسسالام) بىلەن سۆزلەشتى ۋە بەزىسىنىڭ (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ) دەرىجىسىنى يۇقىرى كۆتۈردى. مەريەم ئوغلى ئىساغا (ئۆلۈكلەرنى تىرىلدۈرۈش، گاچىلارنى، بەرەس كېسىلىنى ساقايتىشقا ئوخشاش) روشەن مۆجىزىلەرنى ئاتا قىلدۇق. ئۇنى روھۇلقۇدۇس (يەنى جىبرىئىل ئەلەيھىسسالام) بىلەن كۈچلەندۈردۇق، ئەگەر اﷲ خالىغان بولسا ئىدى، ئۇلاردىن كېيىن كەلگەن ئۈممەتلەر ئۇلارغا روشەن دەلىللەر كەلگەندىن كېيىن ئۆزئارا ئۇرۇشماس ئىدى، لېكىن ئۇلار ئىختىلاپ قىلىشتى، بەزىسى ئىمان ئېيتتى، بەزىسى كاپىر بولدى. ئەگەر اﷲ خالىغا بولسا ئىدى، ئۇلار ئۆزئارا ئۇرۇشماس ئىدى، لېكىن اﷲ خالىغىنىنى قىلىدۇ'),
(261, 2, 254, 'ئى مۆمىنلەر! سودا - سېتىق، دوستلۇق ۋە (اﷲ نىڭ ئىزنىسىز) شاپائەت بولمايدىغان كۈنى (يەنى قىيامەت كۈنى) يىتىپ كېلىشتىن بۇرۇن، سىلەرگە بىز رىزىق قىلىپ بەرگەن مال - مۈلۈكلەردىن (ياخشىلىق يوللىرىغا) سەرپ قىلىڭلار. كۇفرانى نېمەت قىلغۇچىلار (ئۆزلىرىگە) زۇلۇم قىلغۇچىلاردۇر'),
(262, 2, 255, 'بىر اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر؛ اﷲ ھەمىشە تىرىكتۇر، ھەممىنى ئىدارە قىلىپ تۇرغۇچىدۇر؛ ئۇ مۈگدەپ قالمايدۇ، ئۇنى ئۇيقۇ باسمايدۇ؛ ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ (مۈلكى) دۇر؛ اﷲ نىڭ رۇخسىتىسىز كىممۇ اﷲ نىڭ ئالدىدا شاپائەت قىلالىسۇن؛ اﷲ ئۇلارنىڭ ئالدىدىكى (يەنى دۇنيادا قىلغان)، كەينىدىكى (يەنى ئۇلار ئۈچۈن ئاخىرەتتە تەييارلىغان) ئىشلارنىڭ ھەممىسىنى بىلىپ تۇرىدۇ؛ ئۇلار اﷲ نىڭ مەلۇماتىدىن (اﷲ) ئۇلارغا بىلدۈرۈشنى خالىغان نەرسىلەردىن (يەنى پەيغەمبەرلەرنىڭ تىلى ئارقىلىق بىلدۈرگەن نەرسىلەردىن) باشقا ھېچ نەرسىنى بىلمەيدۇ، اﷲ نىڭ كۇرسى (مەلۇماتى) ئاسمانلارنى ۋە زېمىننى ئۆز ئىچىگە ئالىدۇ. ئاسمان - زېمىننى ساقلاش ئۇنىڭغا ئېغىر كەلمەيدۇ. ئۇ يۇقىرى مەرتىۋىلىكتۇر، ھەممىدىن ئۇلۇغدۇر'),
(263, 2, 256, 'دىندا (ئۇنىڭغا كىرىشكە) زورلاش يوقتۇر، ھىدايەت گۇمراھلىقتىن ئېنىق ئايرىلدى، كىمكى تاغۇت (يەنى اﷲ تىن باشقا بارچە مەبۇد) نى ئىنكار قىلىپ، اﷲ قا ئىمان ئېيتىدىكەن، ئۇ سۇنماس، مەھكەم تۇتقىنى تۇتقان بولىدۇ، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(264, 2, 257, 'اﷲ مۆمىنلەرنىڭ ئىگىسىدۇ، ئۇلارنى (كۇفرىنىڭ) زۇلمىتىدىن (ئىماننىڭ) يورۇقلۇقىغا چىقىرىدۇ؛ كاپىرلارنىڭ ئىگىسى شەيتاندۇر. ئۇلارنى (ئىماننىڭ) يورۇقلۇقىدىن (گۇمراھلىقنىڭ) قاراڭغۇلۇقىغا چىقىرىدۇ. ئەنە شۇلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر'),
(265, 2, 258, 'پەرۋەردىگارى توغرىسىدا ئىبراھىم بىلەن مۇنازىرىلەشكەن، اﷲ نىڭ ئاتا قىلغان پادىشاھلىقى ئۇنى ئىبراھىم بىلەن مۇنازىرىلىشىشكە ئېلىپ كەلگەن ئادەم (يەنى نەمرۇد) نى كۆرمىدىڭمۇ؟ ئەينى زاماندا ئىبراھىم (اﷲ نىڭ بارلىقىغا دەلىل كۆرسىتىپ): «مېنىڭ پەرۋەردىگارىم (ئۆلۈكنى) تىرىلدۈرەلەيدۇ، (تىرىكنى) ئۆلتۈرەلەيدۇ» دېدى. ئۇ: «مەنمۇ (ئۆلۈكنى) تىرىلدۈرەلەيمەن، (تىرىكنى) ئۆلتۈرەلەيمەن» دېدى (نەمرۇد ئۆلۈمگە ھۆكۈم قىلىنغان ئىككى ئادەمنى چاقىرتىپ كىلىپ بىرىنى ئۆلتۈردى، بىرىنى قويۇپ بەردى). ئىبراھىم (نەمرۇدنىڭ بۇنداق ھاماقەتلىكىنى كۆرۈپ): «اﷲ ھەقىقەتەن كۈننى شەرقتىن چىقىرالايدۇ، (خۇدالىق دەۋاسى قىلىدىغان بولساڭ) سەن ئۇنى غەربتىن چىقىرىپ باققىن» دېدى. (بۇنداق پاكىت ئالدىدا) كاپىر ئېغىز ئاچالماي قالدى. اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ'),
(266, 2, 259, 'ياكى سەن تام، ئۆگزىلىرى يىقىلىپ ۋەيران بولغان (خارابە) شەھەر (يەنى بەيتۇلمۇقەددەس) دىن ئۆتكەن كىشىنى (يەنى ئۇزەيرنى) كۆرمىدىڭمۇ؟ ئۇ (ئەجەبلىنىپ): «اﷲ بۇ شەھەرنىڭ ئۆلگەن ئاھالىسىنى قانداق تىرىلدۈرەر؟» دېدى. اﷲ ئۇنى يۈز يىل ئۆلۈك ھالىتىدە تۇرغۇزۇپ ئاندىن (كامالى قۇدرىتىنى كۆرسىتىش ئۈچۈن) تىرىلدۈردى. اﷲ (پەرىشتە ئارقىلىق) ئۇنىڭدىن: «(بۇ ھالەتتە) قانچىلىك تۇردۇڭ؟» دەپ سورىدى. ئۇ: «بىر كۈن ياكى بىر كۈنگە يەتمەيدىغان ۋاقىت تۇردۇم» دېدى. اﷲ ئېيتتى: «ئۇنداق ئەمەس، (تولۇق) 100 يىل تۇردۇڭ! يېمەك ئىچمىكىڭگە قارىغىنكى، بۇزۇلمىغان؛ ئېشىكىڭگە قارىغىنكى (ئۇنىڭ سۆڭەكلىرى چىرىپ كەتكەن). (ساڭا يۇقىرىقىدەك ئادەتتىن تاشقىرى ئىشلارنى قىلغانلىقىمىز) سېنى بىز كىشىلەرگە (قۇدرىتىمىزنىڭ) دەلىلى قىلىش ئۈچۈندۇر. بۇ سۆڭەكلەرگە قارىغىنكى، ئۇنى قانداق قۇراشتۇرىمىز، ئاندىن ئۇنىڭغا گۆش قوندۇرىمىز. (يۇقىرىقى ئىشلار) ئۇنىڭغا ئېنىق بولغان چاغدا (يەنى ئۇ روشەن مۆجىزىلەرنى كۆرگەن چاغدا)، ئۇ: «مەن اﷲ نىڭ ھەممە نەرسىگە قادىر ئىكەنلىكىنى بىلدىم» دېدى'),
(267, 2, 260, 'ئۆز ۋاقتىدا ئىبراھىم: «پەرۋەردىگارىم، ئۆلۈكلەرنى قانداق تىرىلدۈرىدىغانلىقىڭنى ماڭا كۆرسەتكىن» دېدى. اﷲ: «(ئۆلۈكلەرنى تىرىلدۈرەلەيدىغانلىقىمغا) ئىشەنمىدىڭمۇ؟» دېدى. ئىبراھىم: «ئىشەندىم، لېكىن كۆڭلۈم (تېخىمۇ) قارار تاپسۇن ئۈچۈن (كۆرۈشنى تىلەيمەن)» دېدى. اﷲ ئېيتتى: «قۇشتىن تۆتنى ئالغىن، ئۇلارنى ئۆزۈڭگە توپلىغىن (يەنى ئۇلارنى پارچىلاپ گۆشلىرى بىلەن پەيلىرىنى ئارىلاشتۇرغىن)، ئاندىن ھەربىر تاغقا ئۇلاردىن بىر بۆلۈكىنى قويغىن، ئاندىن ئۇلارنى چاقىرغىن، سېنىڭ ئالدىڭغا ئۇلار چاپسان كېلىدۇ». بىلگىنكى، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(268, 2, 261, 'اﷲ نىڭ يولىدا پۇل - مېلىنى سەرپ قىلغانلارنىڭ (سەرپ قىلغان نەرسىسى يەرگە تېرىلىپ) يەتتە باشاق چىقارغان، ھەر باشىقىدا 100 دان تۇتقان بىر دانغا ئوخشايدۇ. اﷲ خالىغان بەندىسىگە ھەسسىلەپ ساۋاب بېرىدۇ. اﷲ نىڭ مەرھەمىتى (چەكلىك ئەمەس) كەڭدۇر (اﷲ مال سەرپ قىلغۇچىنىڭ نىيىتىنى بىلگۈچىدۇر)'),
(269, 2, 262, 'پۇل - مېلىنى اﷲ يولىدا سەرپ قىلىدىغان، ئارقىسىدىن ئۇنىڭغا مىننەت قىلمايدىغان، ۋە (خەير - ساخاۋىتىگە ئېرىشكەنلەرنى) رەنجىتمەيدىغان كىشىلەر پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب تاپىدۇ، ئۇلارغا (قىيامەت كۈنى) قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ'),
(270, 2, 263, 'ياخشى سۆز ۋە كەچۈرۈش كېيىن ئەزىيەت يەتكۈزىدىغان سەدىقىدىن ئەۋزەلدۇر. اﷲ (بەندىلەرنىڭ سەدىقىسىدىن) بىھاجەتتۇر، ھەلىمدۇر (يەنى ئەمرىگە خىلاپلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ'),
(271, 2, 264, 'ئى مۆمىنلەر! پۇل - مېلىنى كىشىلەرگە كۆرسىتىش ئۈچۈن سەرپ قىلىدىغان، اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان كەلتۈرمەيدىغان كىشى (نىڭ قىلغان ئەمەلىنى بىكار قىلىۋەتكىنىگە) ئوخشاش، بەرگەن سەدىقەڭلارنى مىننەت قىلىش ۋە ئەزىيەت يەتكۈزۈش بىلەن بىكار قىلىۋەتمەڭلار. بۇنداق (پۇل - مېلىنى باشقىلارغا كۆرسىتىش ئۈچۈن سەرپ قىلىدىغان) ئادەم خۇددى ئۈستىگە توپا - چاڭ قونۇپ قالغان، قاتتىق يامغۇردىن كېيىن (يۇيۇلۇپ) بۇرۇنقىدەك بولۇپ قالغان سىلىق تاشقا ئوخشايدۇ. ئۇلار قىلغان ئەمەللىرى ئۈچۈن (ئاخىرەتتە) ھېچقانداق ساۋابقا ئىگە بولالمايدۇ. اﷲ كاپىر قەۋمىنى ھىدايەت قىلمايدۇ'),
(272, 2, 265, 'ماللىرىنى اﷲ نىڭ رازىلىقىنى تىلەش ۋە نەپسىلىرىنى (سەدىقە - ئېھسانغا) ئادەتلەندۈرۈش يۈزىسىدىن سەرپ قىلىدىغانلار ئېگىزلىككە جايلاشقان، قاتتىق يامغۇردىن بەھرىمەن بولسا مېۋىسى ئىككى ھەسسە كۆپ بولىدىغان، قاتتىق يامغۇردىن بەھرىمەن بولمىسا شەبنەم بىلەنمۇ قانىدىغان (يەنى قايسى ھالدا بولسۇن مېۋە بېرىدىغان) بىر باغقا ئوخشايدۇ (دېمەك، بۇنداق خەير - ساخاۋەتلىك ئادەم مېلى كۆپ بولسا كۆپكە يارىشا، ئاز بولسا ئازغا يارىشا بېرىدۇ). اﷲ قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(273, 2, 266, 'ئاراڭلاردا كىممۇ ياقتۇرسۇنكى، ئۇنىڭ خورمىلىق، ئۈزۈملۈك، ئاستىدىن ئېرىقلار ئېقىپ تۇرىدىغان، مېۋىلەرنىڭ ھەممە خىللىرى بولغان بىر بېغى بولسۇن، ئۇنىڭ ئۆزى قېرىپ قالغان، بالىلىرى ئۇششاق بولۇپ، (بۇ باغ) ئوت ئېلىپ كەلگەن بىر قارا بوراندا كۆيۈپ كەتسۇن. سىلەرنىڭ ئويلىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە ئايەتلىرىنى شۇنداق (گۈزەل مىسال بىلەن) بايان قىلىدۇ (يەنى پۇل - مېلىنى رىيا ئۈچۈن سەرپ قىلغان ياكى باشقىلارغا رەنجىتىش، مىننەت قىلىش بىلەن سەرپ قىلغان ئادەم ساۋابىنى ئەڭ موھتاج تۇرۇقلۇق يوقىتىپ قويغان بولىدۇ)'),
(274, 2, 267, 'ئى مۆمىنلەر! سىلەر ئېرىشكەن نەرسىلەرنىڭ ۋە بىز سىلەرگە زېمىندىن چىقىرىپ بەرگەن نەرسىلەر (يەنى ئاشلىقلار، مېۋىلەر) نىڭ ياخشىلىرىدىن سەدىقە قىلىڭلار؛ ئۆزۈڭلارمۇ پەقەت كۆزۈڭلارنى يۇمۇپ تۇرۇپ ئالىدىغان ناچارلىرىنى ئىلغاپ سەدىقە قىلماڭلار. بىلىڭلاركى، اﷲ (بۇنداق سەدىقەڭلاردىن) بىھاجەتتۇر، ھەمدىگە لايىقتۇر'),
(275, 2, 268, 'شەيتان سىلەرنى پېقىرلىقتىن قورقىتىدۇ، يامان ئىشلارغا (يەنى بېخىللىققا، زاكات بەرمەسلىككە) بۇيرۇيدۇ؛ اﷲ سىلەرگە ئۆز مەغپىرىتىنى ۋە پەزلىنى ۋەدە قىلىدۇ. اﷲ نىڭ مەرھەمىتى كەڭدۇر، ئۇ ھەممىنى بىلىپ تۇرغۇچىدۇر'),
(276, 2, 269, 'ھېكمەتنى (يەنى ياخشى ئەمەلگە يېتەكلەيدىغان پايدىلىق ئىلىمنى) خالىغان بەندىسىگە ئاتا قىلىدۇ؛ ھېكمەت ئاتا قىلىنغان ئادەمگە كۆپ ياخشىلىق ئاتا قىلىنغان بولىدۇ. پەقەت ئەقىللىق ئادەملەرلا بۇنىڭدىن پەند - نەسىھەت ئالىدۇ'),
(277, 2, 270, 'نېمىنى خەير - ئېھسان قىلماڭلار ياكى (اﷲ نىڭ يولىدا) نېمىنى ۋەدە قىلماڭلار، اﷲ ئۇنى ھەقىقەتەن بىلىپ تۇرىدۇ. زالىملار (يەنى زاكاتنى مەنئى قىلغۇچىلار ياكى پۇل - مېلىنى گۇناھ ئىشلارغا سەرپ قىلغۇچىلار) غا ھېچقانداق مەدەتكار چىقمايدۇ'),
(278, 2, 271, 'سەدىقىنى ئاشكارا بەرسەڭلار، بۇ ياخشىدۇر، ئەگەر ئۇنى مەخپى بەرسەڭلار ۋە يوقسۇللارغا بەرسەڭلار، تېخىمۇ ياخشىدۇر. بۇ، سىلەرنىڭ بەزى گۇناھلىرىڭلارغا كەففارەت بولىدۇ، اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلاردىن خەۋەرداردۇر'),
(279, 2, 272, '(ئى مۇھەممەد!) ئۇلارنى ھىدايەت قىلىش سېنىڭ مەسئۇلىيىتىڭ ئەمەس (ساڭا تاپشۇرۇلغان ۋەزىپە پەقەت كىشىلەرگە اﷲ نىڭ ۋەھىسىنى يەتكۈزۈشتۇر). لېكىن اﷲ خالىغان بەندىسىنى ھىدايەت قىلىدۇ. مېلىڭلاردىن نېمىنى خەير - ئېھسان قىلماڭلار، ئۇ ئۆزۈڭلارنىڭ پايدىسى ئۈچۈندۇر، سىلەر پەقەت اﷲ نىڭ رازىلىقى ئۈچۈنلا خەير - ئېھسان قىلىسىلەر (يەنى خەير - ئېھسانىڭلار ئۈچۈن اﷲ نىڭ رازىلىقىدىن باشقىنى كۆزدە تۇتماڭلار)، مېلىڭلاردىن نېمىنى خەير - ئېھسان قىلماڭلار، سىلەرگە ئۇنىڭ ساۋابى تولۇق بېرىلىدۇ، سىلەرگە زۇلۇم قىلىنمايدۇ (يەنى قىلغان ياخشى ئەمەلىڭلارنىڭ ساۋابى كېمەيتىلمەيدۇ)'),
(280, 2, 273, 'ئۆزىنى اﷲ يولىغا ئاتىغان، (تىرىكچىلىك قىلىش ئۈچۈن) يىراق جايلارغا بارالمايدىغان يوقسۇللارغا خەير - ئېھسان قىلىنىشى كېرەك. ئەھۋالنى ئۇقمىغانلار، ئۇلارنىڭ نەرسە تىلىمىگەنلىكلىرىگە قاراپ، ئۇلارنى باي دەپ گۇمان قىلىدۇ، ئۇلارنىڭ چىرايىدىن تونۇيسەن، ئۇلار كىشىلەرگە چاپلىشىۋېلىپ تىلەمچىلىك قىلمايدۇ، مېلىڭلاردىن نېمىنى خەير - ئېھسان قىلماڭلار، اﷲ ھەقىقەتەن ئۇنى بىلگۈچىدۇر'),
(281, 2, 274, 'ماللىرىنى كېچە - كۈندۈز (يەنى ھەممە ۋاقىت)، يوشۇرۇن ۋە ئاشكارا يوسۇندا خەير - ئېھسان قىلىدىغانلار پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب تاپىدۇ، ئۇلارغا (ئاخىرەتتە) قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ'),
(282, 2, 275, 'جازانە، ئۆسۈم يېگەن ئادەملەر (قىيامەت كۈنى گۆرلىرىدىن) جىن چېپىلىپ قالغان ساراڭ ئادەملەردەك قوپىدۇ. بۇ شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ ھارام قىلغان ئىشنى ھالال بىلىپ: «سودا - سېتىق، جازانىگە ئوخشاش (جازانە نېمىشقا ھارام بولىدۇ؟)» دېدى. اﷲ سودا - سېتىقنى ھالال قىلدى، جازانىنى، (بەدەلسىز بولغانلىقى، شەخسكە ۋە جەمئىيەتكە زىيانلىق بولغانلىقى ئۈچۈن) ھارام قىلدى. كىمكى پەرۋەردىگارى تەرىپىدىن ۋەز - نەسىھەت كەلگەندىن كېيىن (يەنى جازانە مەنئى قىلىنغاندىن كېيىن ئۇنىڭدىن) يانسا، بۇرۇن ئالغىنى ئۆزىنىڭ بولىدۇ، ئۇنىڭ ئىشى اﷲ قا تاپشۇرۇلىدۇ (يەنى اﷲ خالىسا ئۈنى كەچۈرىدۇ، خالىسا جازالايدۇ). قايتا جازانە قىلغانلار ئەھلى دوزاخ بولۇپ، دوزاختا مەڭگۈ قالىدۇ'),
(283, 2, 276, 'اﷲ جازانىنىڭ (بەرىكىتىنى) ئۆچۈرۈۋېتىدۇ، سەدىقىنىڭ (بەرىكىتىنى) زىيادە قىلىدۇ. اﷲ ھەربىر ناشۈكۈر (يەنى جازانىنى ھالال سانىغۇچى) گۇناھكارنى دوست تۇتمايدۇ'),
(284, 2, 277, 'ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، ناماز ئوقۇغان زاكات بەرگەن كىشىلەر پەرۋەردىگارىنىڭ دەرگاھىدا (چوڭ) ساۋابقا ئېرىشىدۇ، ئۇلاردا (ئاخىرەتتە) قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ'),
(285, 2, 278, 'ئى ئىمان ئېيتقان كىشىلەر! (ھەقىقي) مۆمىن بولساڭلار، اﷲ (نىڭ ئەمرىگە مۇخالىپەتچىلىك) قىلىشتىن ساقلىنىڭلار، (كىشىلەرنىڭ زىممىسىدە) قېلىپ قالغان جازانىنى (يەنى ئۆسۈمنى) ئالماڭلار'),
(286, 2, 279, 'ئەگەر ئۇنداق قىلساڭلار، بىلىڭلاركى، اﷲ ۋە ئۇنىڭ رەسۇلى سىلەرگە ئۇرۇش ئېلان قىلىدۇ، ئەگەر (جازانە قىلىشتىن) تەۋبە قىلساڭلار، (قەرز بەرگەن) سەرمايەڭلار ئۆزۈڭلارغا قايتىدۇ، باشقىلارنى زىيان تارتقۇزمايسىلەر، ئۆزۈڭلارمۇ زىيان تارتمايسىلەر'),
(287, 2, 280, 'ئەگەر قەرزدارنىڭ قولى قىسقا بولسا، ئۇنىڭ ھالى ياخشىلانغۇچە كۈتۈڭلار، ئەگەر (خەيرلىك ئىش ئىكەنلىكىنى بىلسەڭلار، قىيىنچىلىقتا قالغان قەرزداردىن ئالىدىغان قەرزنى ئۇنىڭغا) سەدىقە قىلىپ بېرىۋەتكىنىڭلار سىلەر ئۈچۈن تېخىمۇ ياخشىدۇر'),
(288, 2, 281, 'سىلەر شۇنداق بىر كۈندىن (يەنى قىيامەت كۈنىدىن) قورقۇڭلاركى، ئۇ كۈندە سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، ئاندىن ھەر كىشى قىلغان (ياخشى - يامان) ئەمەلىنىڭ نەتىجىسىنى تولۇق كۆرىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ، (يەنى ياخشى ئەمەلى كېمەيتىلىپ، يامان ئەمەلى كۆپەيتىلمەيدۇ)'),
(289, 2, 282, 'ئى مۆمىنلەر! مۇددەت بەلگىلەپ ئۆزئارا قەرز بېرىشسەڭلار، ئۇنى (ھۆججەت قىلىپ) يېزىپ قويۇڭلار، ئاراڭلاردىكى خەت بىلىدىغان كىشى ئۇنى (كەم - زىيادە قىلماي) ئادىللىق بىلەن يازسۇن، خەت بىلىدىغان كىشى (اﷲ ئۇنىڭغا بىلدۈرگەندەك توغرىلىق بىلەن يېزىشىنى) رەت قىلمىسۇن، ئۇ يازسۇن، زىممىسىدە باشقىلارنىڭ ھەققى بولغان ئادەم (يازىدىغان كىشىگە) ئېيتىپ بەرسۇن، (قەرزدار) پەرۋەردىگارى اﷲ تىن قورقسۇن، قەرز ئالغان نەرسىدىن ھېچنېمىنى كېمەيتىۋەتمىسۇن، ئەگەر قەرز ئالغۇچى ئادەم ئەخمەق (يەنى خۇدبىن، ئەقىلسىز، ئىسراپخور) ياكى ئاجىز (يەنى كىچىك بالا ياكى بەكمۇ قېرى) بولسا، ياكى (گەپ قىلالماسلىقى، گاچىلىقى، كېكەچلىكى تۈپەيلىدىن) ئۆزى ئېيتىپ بېرەلمىسە، ئۇنىڭ ئىشلىرىنى باشقۇرغۇچى ئادەم ئادىللىق بىلەن ئېيتىپ بەرسۇن، سىلەر ئەر كىشىڭلاردىن ئىككى كىشىنى گۇۋاھلىققا تەكلىپ قىلىڭلار؛ ئەگەر ئىككى ئەر كىشى يوق بولسا، سىلەر (ئادالىتىگە، دىيانىتىگە) رازى بولىدىغان كىشىلەردىن بىر ئەر، ئىككى ئايالنى گۇۋاھلىققا تەكلىپ قىلىڭلار، بۇ ئىككى ئايالنىڭ بىرسى ئۇنتۇپ قالسا، ئككىنچىسى ئېسىگە سالىدۇ. گۇۋاھچىلار (گۇۋاھلىققا) چاقىرىلغان ۋاقتىدا (گۇۋاھ بولۇشتىن) باش تارتمىسۇن. قەرز مەيلى ئاز بولسۇن، مەيلى كۆپ بولسۇن، ئۇنى قايتۇرۇش ۋاقتى بىلەن قوشۇپ يېزىشتىن ئېرىنمەڭلار. اﷲ نىڭ نەزىرىدە (يەنى ھۆكمىدە)، بۇ ئەڭ ئادىللىق ۋە ئەڭ ئىسپاتلىقتۇر (يەنى گۇۋاھلىقنىڭ ئۇنتۇلماسلىقىنى ئەڭ ئىسپاتلىغۇچىدۇر)، گۇمانلانماسلىقىڭلارغا ئەڭ يېقىندۇر. لېكىن ئاراڭلاردا قىلىشقان قولمۇقول سودىدا ھۆججەت يازمىساڭلارمۇ ھېچ گۇناھ بولمايدۇ. ئۆزئارا سودا قىلىشقان ۋاقتىڭلاردا گۇۋاھچى تەكلىپ قىلىڭلار، پۈتۈكچىگىمۇ، گۇۋاھچىغىمۇ زىيان يەتكۈزۈلمىسۇن؛ ئەگەر زىيان يەتكۈزسەڭلار گۇناھ ئۆتكۈزگەن بولىسىلەر، اﷲ نىڭ (ئەمرىگە ۋە نەھيىسىگە مۇخالىپەتچىلىك قىلىشتىن) ساقلىنىڭلار، اﷲ سىلەرگە ئۆگىتىدۇ (يەنى سىلەرگە ئىككىلا دۇنيادا پايدىلىق بولغان ئىلىمنى بېرىدۇ)، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر'),
(290, 2, 283, 'ئەگەر سىلەر سەپەر ئۈستىدە بولۇپ، خەت يازالايدىغان ئادەمنى تاپالمىساڭلار، بۇ چاغدا (قەرز ھۆججىتى يېزىپ بېرىشنىڭ ئورنىغا) گۆرۈ قويىدىغان نەرسىنى تاپشۇرساڭلار بولىدۇ؛ ئاراڭلاردا بىرىڭلار بىرىڭلارغا ئىشىنىپ ئامانەت قويسا، ئۇ چاغدا ئامانەتنى ئالغۇچى ئادەم ئامانەتنى تاپشۇرسۇن (يەنى قەرز بەرگۈچى قەرز ئالغۇچىغا ئىشىنىپ ئۇنىڭدىن بىر نەرسىنى گۆرۈ ئالمىسا، قەرزدار قەرزنى دېيىشكەن ۋاقتىدا قايتۇرسۇن)، (قەرزدار ئامانەتكە رىئايە قىلىشتا) پەرۋەردىگارىدىن قورقسۇن. گۇۋاھلىقنى يوشۇرماڭلار، كىمكى ئۇنى يوشۇرىدىكەن، ھەقىقەتەن ئۇنىڭ دىلى گۇناھكار بولىدۇ. اﷲ قىلمىشىڭلارنى تامامەن بىلگۈچىدۇر'),
(291, 2, 284, 'ئاسمانلاردىكى، زېمىندىكى شەيئىلەر (ئىگىدارچىلىق قىلىش جەھەتتىن بولسۇن، تەسەررۇپ قىلىش جەھەتتىن بولسۇن) اﷲ نىڭ ئىلكىدىدۇر. دىلىڭلاردىكى (يامانلىقنى) مەيلى ئاشكارا قىلىڭلار، مەيلى يوشۇرۇڭلار، ئۇنىڭ ئۈچۈن اﷲ سىلەردىن ھېساب ئالىدۇ. اﷲ خالىغان كىشىگە مەغپىرەت قىلىدۇ، خالىغان كىشىگە ئازاب قىلىدۇ. اﷲ ھەممە نەرسىگە قادىردۇر'),
(292, 2, 285, 'پەيغەمبەر پەرۋەردىگارى تەرىپىدىن ئۇنىڭغا نازىل قىلىنغان كىتابغا ئىمان كەلتۈردى، مۆمىنلەرمۇ ئىمان كەلتۈردى، ئۇلارنىڭ ھەممىسى اﷲ قا ۋە اﷲ نىڭ پەرىشتىلىرىگە، كىتابلىرىغا ۋە پەيغەمبەرلىرىگە ئىمان كەلتۈردى. (ئۇلار) «اﷲ نىڭ پەيغەمبەرلىرىنىڭ ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ، بەزىسىگە ئىمان ئېيتماي قالمايمىز)» دەيدۇ. ئۇلار: «بىز (دەۋىتىڭنى) ئاڭلىدۇق ۋە (ئەمرىڭگە) ئىتائەت قىلدۇق، پەرۋەردىگارىمىز، مەغپىرىتىڭنى تىلەيمىز، ئاخىر يانىدىغان جايىمىز سېنىڭ دەرگاھىڭدۇر» دەيدۇ'),
(293, 2, 286, 'اﷲ ھېچكىمنى تاقىتى يەتمەيدىغان ئىشقا تەكلىپ قىلمايدۇ. كىشىنىڭ قىلغان ياخشىلىقى (نىڭ ساۋابى) ئۆزىگىدۇر، يامانلىقى (نىڭ جازاسى) مۇ ئۆزىگىدۇر. (ئۇلار) «پەرۋەردىگارىمىز! ئەگەر بىز ئۇنتۇساق ياكى خاتالاشساق (يەنى بىز ئۇنتۇش ياكى سەۋەنلىك سەۋەبىدىن ئەمرىڭنى تولۇق ئورۇنلىيالمىساق)، بىزنى جازاغا تارتمىغىن. پەرۋەردىگارىمىز! بىزدىن ئىلگىرىكىلەرگە يۈكلىگىنىڭگە ئوخشاش، بىزگە ئېغىر يۈك يۈكلىمىگىن (يەنى بىزنى قىيىن ئىشلارغا تەكلىپ قىلمىغىن)، پەرۋەردىگارىمىز! كۈچىمىز يەتمەيدىغان نەرسىنى بىزگە ئارتمىغىن، بىزنى كەچۈرگىن، بىزگە مەغپىرەت قىلغىن، بىزگە رەھىم قىلغىن، سەن بىزنىڭ ئىگىمىزسەن، كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دەيدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'ئەلىف، لام، مىم'),
(295, 3, 2, 'اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ (ھەمىشە) تىرىكتۇر، مەخلۇقاتنى ئىدارە قىلىپ تۇرغۇچىدۇر'),
(296, 3, 3, '(ئى مۇھەممەد!) ئۆزىدىن ئىلگىرىكى كىتابلارنى تەستىق قىلغۇچى ھەق كىتابنى (يەنى قۇرئاننى) (اﷲ) ساڭا نازىل قىلدى. ئىلگىرى، كىشىلەرگە يول كۆرسەتكۈچى قىلىپ تەۋرات بىلەن ئىنجىلنى نازىل قىلغان ئىدى'),
(297, 3, 4, 'ھەق بىلەن باتىلنى ئايرىغۇچى كىتابنى (يەنى بارلىق ساماۋى كىتابلارنى) نازىل قىلدى. شۈبھىسىزكى، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ. اﷲ غالىبتۇر، (يامانلارنى) جازالىغۇچىدۇر'),
(298, 3, 5, 'شۈبھىسىزكى، ئاسمان ۋە زېمىندىكى ھېچ نەرسە اﷲ قا مەخپىي ئەمەس'),
(299, 3, 6, 'ئۇ سىلەرنى بەچچىدانلاردا (يەنى ئاناڭلارنىڭ قورسىقىدىكى چېغىڭلاردا) ئۆزى خالىغان شەكىلگە كىرگۈزىدۇ. ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(300, 3, 7, '(ئى مۇھەممەد!) ئۇ (اﷲ) ساڭا كىتابنى (يەنى قۇرئاننى) نازىل قىلدى. ئۇنىڭدا مەھكەم (يەنى مەنىسى ئوچۇق) ئايەتلەر باركى، ئۇلار كىتابنىڭ (يەنى پۈتۈن قۇرئاننىڭ) ئاساسىدۇر؛ يەنە باشقا مۇتەشابىھ (يەنى مەنىسى مۇئەييەن ئەمەس) ئايەتلەر باردۇر. دىللىرىدا ئەگرىلىك بار (يەنى گۇمراھلىققا مايىل) كىشىلەر پىتنە قوزغاش ۋە ئۆز رايى بويىچە مەنە بېرىش ئۈچۈن، مۇتەشابىھ ئايەتلەرگە ئەگىشىدۇ (يەنى مۇتەشابىھ ئايەتلەرنى ئۆز نەپسى خاھىشى بويىچە چۈشەندۈرىدۇ). بۇنداق ئايەتلەرنىڭ (ھەقىقىي) مەنىسىنى پەقەت اﷲ بىلىدۇ. ئىلىمدا توشقانلار ئېيتىدۇ: «ئۇنىڭغا ئىشەندۇق، ھەممىسى پەرۋەردىگارىمىز تەرىپىدىن نازىل بولغان. (بۇنى) پەقەت ئەقىل ئىگىلىرىلا چۈشىنىدۇ'),
(301, 3, 8, 'پەرۋەردىگارىمىز! بىزنى ھىدايەت قىلغىنىڭدىن كېيىن دىللىرىمىزنى توغرا يولدىن بۇرىۋەتمىگىن، بىزگە دەرگاھىڭدىن رەھمەت بېغىشلىغىن. شۈبھىسىزكى، سەن (بەندىلىرىڭگە ئاتالارنى) بەكمۇ بېغىشلىغۇچىسەن'),
(302, 3, 9, 'پەرۋەردىگارىمىز! شۈبھىسىزكى، كېلىشى شەكسىز بولغان كۈندە (يەنى ھېساب ئېلىنىدىغان قىيامەت كۈنىدە) كىشىلەرنى توپلىغۇچىسەن. شۈبھىسىزكى، اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ»'),
(303, 3, 10, 'كاپىرلارنىڭ ماللىرى، بالىلىرى اﷲ نىڭ ئازابى ئالدىدا ھەقىقەتەن ھېچ نەرسىگە دال بولالمايدۇ. ئۇلار (يەنى كۇففارلار) دوزاخنىڭ يېقىلغۇسىدۇر'),
(304, 3, 11, '(بۇ كۇففارلارنىڭ ھالى) پىرئەۋن تەۋەلىرىنىڭ ۋە ئۇلاردىن ئىلگىرىكىلەرنىڭ ھالىغا ئوخشايدۇ. ئۇلار بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقاردى، گۇناھى تۈپەيلىدىن اﷲ ئۇلارنى جازالىدى، اﷲ نىڭ جازاسى قاتتىقتۇر'),
(305, 3, 12, '(ئى مۇھەممەد!) كاپىرلارغا ئېيتقىنكى، «سىلەر (دۇنيادا) مەغلۇپ قىلىنىسىلەر ۋە (ئاخىرەتتە) جەھەننەمگە توپلىنىسىلەر، جەھەننەم نېمىدېگەن يامان تۆشەك»'),
(306, 3, 13, '(ئى يەھۇدىيلار جامائەسى! بەدرىدە جەڭ ئۈچۈن) ئۇچراشقان ئىككى گۇرۇھتا سىلەر ئۈچۈن ئىبرەت بار، بىرى اﷲ يولىدا ئۇرۇشۇۋاتقان گۇرۇھتۇر، يەنى بىر مۇسۇلمانلارنى (سانىنى) ئۆزلىرىدەك ئىككى باراۋەر ئوپئوچۇق كۆرگەن كاپىرلار گۇرۇھىدۇر (يەنى اﷲ كاپىرلارغا قورقۇنچ سېلىش بىلەن ئۇلارنى ئۇرۇشتىن قول يىغدۇرۇش ئۈچۈن، كاپىرلارغا مۇسۇلمانلارنىڭ سانىنى جىق كۆرسەتتى). اﷲ خالىغان كىشىگە ياردەم بېرىپ قوللايدۇ. بۇنىڭدا ھەقىقەتەن ئەقىل ئىگىلىرى ئۈچۈن ئىبرەت بار'),
(307, 3, 14, 'ئاياللار، ئوغۇللار، ئالتۇن - كۈمۈشتىن توپلانغان كۆپ ماللار، ئارغىماقلار، چارۋىلار ۋە ئېكىنلەردىن ئىبارەت كۆڭۈل تارتىدىغان نەرسىلەرنىڭ مۇھەببىتى ئىنسانلارغا چىرايلىق كۆرسىتىلدى. ئۇلار دۇنيا تىرىكچىلىكىدە مەنپەئەتلىنىدىغان (باقاسى يوق) شەيئىلەردۇر؛ اﷲ نىڭ دەرگاھىدا بولسا قايتىدىغان گۈزەل جاي (يەنى جەننەت) باردۇر (شۇنىڭ ئۈچۈن باشقىغا ئەمەس، جەننەتكە قىزىقىش كېرەك)'),
(308, 3, 15, '(ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «سىلەرگە ئۇلاردىنمۇ (يەنى دۇنيانىڭ زىبۇزىننەتلىرى ۋە نېمەتلىرىدىنمۇ) ياخشى بولغان نەرسىلەرنى ئېيتىپ بېرەيمۇ؟ تەقۋادارلار ئۈچۈن پەرۋەردىگارى ھۇزۇرىدا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەر بولۇپ، (ئۇلار) جەننەتلەردە مەڭگۈ قالىدۇ، (جەننەتلەردە) پاك جۈپتىلەر بار، (تەقۋادارلار ئۈچۈن) يەنە اﷲ نىڭ رەزاسى بار». اﷲ بەندىلىرىنى (يەنى ئۇلارنىڭ ھەممە ئىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر'),
(309, 3, 16, 'ئۇلار (يەنى تەقۋادارلار): «پەرۋەردىگارىمىز! بىز شۈبھىسىز ئىمان ئېيتتۇق، بىزنىڭ گۇناھلىرىمىزنى مەغپىرەت قىلغىن، بىزنى دوزاخ ئازابىدىن ساقلىغىن» دەيدۇ'),
(310, 3, 17, 'ئۇلار (يەنى تەقۋادارلار) سەۋر قىلغۇچىلاردۇر، راستچىللاردۇر، (اﷲ قا) ئىتائەت قىلغۇچىلاردۇر، (ياخشىلىق يوللىرىغا پۇل - ماللىرىنى) سەرپ قىلغۇچىلاردۇر ۋە سەھەرلەردە ئىستىغپار ئېيتقۇچىلاردۇر'),
(311, 3, 18, 'اﷲ ئادالەتنى بەرپا قىلغان ھالدا گۇۋاھلىق بەردىكى، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. پەرىشتىلەرمۇ، ئىلىم ئەھلىلىرىمۇ شۇنداق گۇۋاھلىق بەردى: ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. ئۇ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(312, 3, 19, 'ھەقىقەتەن اﷲ نىڭ نەزىرىدە مەقبۇل دىن ئىسلام (دىنى) دۇر. كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) پەقەت ئۆزلىرىگە ئىلىم كەلگەندىن كېيىنلا ئۆزئارا ھەسەت قىلىشىپ ئىختىلاپ قىلىشتى (يەنى ئىسلامنىڭ ھەقلىقىنى، مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىنى روشەن پاكىتلار ئارقىلىق بىلىپ تۇرۇپ، كۆرەلمەسلىكتىن ئىنكار قىلىشتى)، كىمكى اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلىدىكەن (اﷲ ئۇنى يېقىندا جازالايدۇ)، اﷲ تېز ھېساب ئالغۇچىدۇر'),
(313, 3, 20, '(ئى مۇھەممەد!) ئەگەر ئۇلار سەن بىلەن (دىن بارىسىدا) مۇنازىرەلەشسە: «ماڭا ئەگەشكەنلەر بىلەن بىرلىكتە، مەن ئۆزۈمنى اﷲ قا تاپشۇردۇم (يەنى اﷲ نىڭ ئەمرلىرىگە بويسۇنغۇچىمىز)» دېگىن. كىتاب بېرىلگەنلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا) ۋە ئۈممىلەرگە (يەنى ئەرەب مۇشرىكلىرىغا): «مۇسۇلمان بولدۇڭلارمۇ؟» دېگىن، ئەگەر ئۇلار مۇسۇلمان بولسا، توغرا يول تاپقان بولىدۇ. ئەگەر ئۇلار يۈز ئۆرۈسە (ساڭا ھېچ زىيىنى يوق)، سېنىڭ پەقەت تەبلىغ قىلىش مەسئۇلىيىتىڭ بار، اﷲ بەندىلىرىنى (يەنى ھەممە ئەھۋالىنى) كۆرۈپ تۇرغۇچىدۇر'),
(314, 3, 21, 'اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلىدىغانلار، پەيغەمبەرلەرنى ناھەق ئۆلتۈرىدىغانلار، كىشىلەرنىڭ ئىچىدىكى ئادالەتكە بۇيرۇيدىغانلار (يەنى اﷲ قا دەۋەت قىلغۇچىلار) نى ئۆلتۈرىدىغانلارغا چوقۇم قاتتىق ئازاب بىلەن خۇش خەۋەر بەرگىن'),
(315, 3, 22, 'ئەنە شۇلارنىڭ دۇنيا ۋە ئاخىرەتتىكى ئەمەللىرى بىكار بولدى، ئۇلارغا ھېچ ياردەم قىلغۇچى يوقتۇر'),
(316, 3, 23, 'كىتابتن نېسىۋە بېرىلگەنلەر (يەنى يەھۇدىيلارنىڭ ئۆلىمالىرى) نى كۆرمىدىڭمۇ؟ ئۇلار ئۆز ئارىسىدا (دەتالاش قىلىشقان ئىشلار توغرىسىدا) اﷲ نىڭ كىتابى بويىچە ھۆكۈم چىقىرىشقا چاقىرىلسا، ئۇلارنىڭ بىر تۈركۈمى بۇنىڭدىن (يەنى اﷲ نىڭ ھۆكىمىنى قوبۇل قىلىشتىن) يۈز ئۆرۈيدۇ، ئۇلار ھەقتىن يۈز ئۆرۈگۈچىلەردۇر (يەنى ھەقتىن يۈز ئۆرۈش ئۇلارنىڭ تەبىئىتىگە سىڭىپ كەتكەن)'),
(317, 3, 24, 'بۇ ئۇلارنىڭ «دوزاخ ئوتى بىزنى ساناقلىق كۈنلەرلا (يەنى موزايغا چوقۇنغان 40 كۈنلۈك ۋاقىتلا) كۆيدۈرىدۇ» دېگەنلىكلىرى ۋە دىنىي جەھەتتە ئويدۇرۇپ چىقارغان نەرسىلىرىنىڭ ئۆزلىرىنى ئالدىغانلىقى ئۈچۈندۇر'),
(318, 3, 25, 'كېلىشى شەكسىز بولغان كۈندە (يەنى قىيامەت كۈنىدە) ئۇلارنى توپلايمىز، ھەر بىر كىشىنىڭ قىلغان ئىشىنىڭ جازاسى ۋە مۇكاپاتى تولۇق بېرىلىدىغان، ئۇلارغا (ئازابنى زىيادە قىلىۋېتىش ياكى ساۋابىنى كېمەيتىۋېتىش بىلەن) زۇلۇم قىلىنمايدىغان شۇ چاغدا (ئۇلارنىڭ ھالى) قانداق بولىدۇ؟'),
(319, 3, 26, 'ئېيتىقىنكى، «پادىشاھلىقنىڭ ئىگىسى بولغان ئى اﷲ! خالىغان ئادەمگە پادىشاھلىقنى بېرىسەن، خالىغان ئادەمدىن پادىشاھلىقنى تارتىپ ئالىسەن؛ خالىغان ئادەمنى ئەزىز قىلىسەن، خالىغان ئادەمنى خار قىلىسەن؛ ھەممە ياخشىلىق (نىڭ خەزىنىسى يالغۇز) سېنىڭ قولۇڭدىدۇر. سەن ھەقىقەتەن ھەممىگە قادىرسەن'),
(320, 3, 27, 'كېچىنى كۈندۈزگە كىرگۈزىسەن، كۈندۈزنى كېچىگە كىرگۈزىسەن (شۇنىڭ بىلەن، كېچە - كۈندۈزنىڭ ئۇزۇن - قىسقا بولۇشى پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)؛ جانلىقنى (يەنى تىرىك بولغان ئىنساننى، ھايۋاننى) جانسىزدىن (يەنى جانلىق بولغان ئابىمەنىيدىن) چىقىرىسەن، جانسىزنى (يەنى جانسىز بولغان ئابىمەنىينى) جانلىقتىن (يەنى جانلىق بولغان ئىنساندىن، ھايۋاندىن) چىقىرىسەن؛ ئۆزۈڭ خالىغان كىشىگە ھېسابسىز رىزىق بېرىسەن»'),
(321, 3, 28, 'مۆمىنلەر مۆمىنلەرنى قويۇپ كاپىرلارنى دوست تۇتمىسۇن، كىمكى شۇنداق قىلىدىكەن، ئۇ اﷲ نىڭ دوستلۇقىغا ئېرىشەلمەيدۇ، ئۇلاردىن قورقۇپ دوستلۇق ئىزھار قىلىشىڭلار بۇنىڭدىن مۇستەسنا. اﷲ سىلەرنى ئۆزىنىڭ ئازابىدىن قورقۇتىدۇ، ئاخىر قايتىدىغان جاي اﷲ نىڭ دەرگاھىدۇر'),
(322, 3, 29, 'ئېيتقىنكى، «دىلىڭلاردىكىنى مەيلى يوشۇرۇڭلار، مەيلى ئاشكارىلاڭلار، اﷲ ئۇنى بىلىپ تۇرىدۇ. اﷲ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىمۇ بىلىپ تۇرىدۇ. اﷲ ھەر نەرسىگە قادىردۇر»'),
(323, 3, 30, 'ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ھەر ئادەمنىڭ قىلغان ياخشى ئىشلىرى، يامان ئىشلىرى ئۆزىنىڭ ئالدىدا ئايان بولىدۇ، (ئۇلار يامان ئىشلىرىنى كۆرمەسلىكنى)، ئۆزى بىلەن يامان ئىشلىرىنىڭ ئارىسىدا (مەشرىق بىلەن مەغرىبنىڭ ئارىسىدەك) ئۇزۇن مۇساپە بولۇشىنى ئارزۇ قىلىدۇ. اﷲ سىلەرنى ئۆزىنىڭ ئازابىدىن قورقۇتىدۇ. اﷲ بەندىلىرىگە مېھرىباندۇر'),
(324, 3, 31, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئەگەر سىلەر اﷲ نى دوست تۇتساڭلار، ماڭا ئەگىشىڭلاركى، اﷲ سىلەرنى دوست تۇتىدۇ، (ئۆتكەنكى) گۇناھىڭلارنى مەغپىرەت قىلىدۇ. اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر»'),
(325, 3, 32, 'ئېيتقىنكى، «اﷲ قا ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار». ئەگەر ئۇلار (ئىتائەت قىلىشتىن) يۈز ئۆرۈسە، اﷲ كاپىرلارنى (يەنى اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلارنى) دوست تۇتمايدۇ'),
(326, 3, 33, 'اﷲ ھەقىقەتەن ئادەمنى، نۇھنى، ئىبراھىم ئەۋلادىنى، ئىمران ئەۋلادىنى (پەيغەمبەرلىككە) تاللاپ، (ئۇلارنى ئۆزلىرىنىڭ زامانىدىكى) جاھان ئەھلىدىن ئارتۇق قىلدى'),
(327, 3, 34, 'ئۇ ئەۋلادلار بىر - بىرى بىلەن قانداشتۇر، اﷲ (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلەرنىڭ نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(328, 3, 35, 'ئۆز ۋاقتىدا ئىمراننىڭ ئايالى ئېيتتى: «پەرۋەردىگارىم! مەن قورسىقىمدىكى پەرزەنتىمنى (دۇنيا ئىشلىرىدىن) ئازاد قىلىنغان ھالدا چوقۇم سېنىڭ خىزمىتىڭگە ئاتىدىم، (بۇ نەزرەمنى) قوبۇل قىلغىن، سەن ھەقىقەتەن (دۇئايىمنى) ئاڭلاپ تۇرغۇچىسەن، (نىيىتىمنى) بىلىپ تۇرغۇچىسەن»'),
(329, 3, 36, 'ئۇ قىز تۇغقان ۋاقتىدا: «ئى پەرۋەردىگارىم! قىز تۇغدۇم» دېدى، ئۇنىڭ نېمە تۇغقانلىقىنى اﷲ ئوبدان بىلىدۇ. (ئۇ تەلەپ قىلغان) ئوغۇل (ئاتا قىلىنغان) قىزغا ئوخشاش ئەمەس (بەلكى بۇ قىز ئارتۇقتۇر). (ئىمراننىڭ ئايالى ئېيتتىكى) «مەن ئۇنى مەريەم ئاتىدىم، مەن ھەقىقەتەن ئۇنىڭ ئۈچۈن، ئۇنىڭ ئەۋلادلىرى ئۈچۈن ساڭا سېغىنىپ، قوغلاندى شەيتاندىن پاناھ تىلەيمەن»'),
(330, 3, 37, 'ئۇنى پەرۋەردىگارى چىرايلىقچە قوبۇل قىلدى، ئۇنى ئوبدان ئۆستۈردى، ئۇنىڭغا زەكەرىيانى كېپىل قىلدى (يەنى ئۇنىڭ خىزمىتىنى قىلىشقا زەكەرىيانى قويدى). زەكەرىيا ھەر قېتىم (ئۇنىڭ) ئىبادەتگاھىغا ئۇنى كۆرگىلى كىرسە، ئۇنىڭ يېنىدا بىرەر يېمەكلىك ئۇچرىتاتتى. ئۇ: «ئى مەريەم! بۇ ساڭا قەيەردىن كەلدى؟» دەيتتى، مەريەم: «ئۇ، اﷲ تەرىپىدىن كەلدى» دەيتتى. اﷲ ھەقىقەتەن خالىغان ئادەمگە (جاپا - مۇشەققەتسىزلا) ھېسابسىز رىزىق بېرىدۇ'),
(331, 3, 38, 'شۇ ۋاقىتتا (يەنى مەريەمنىڭ كارامىتىنى كۆرگەن چاغدا) زەكەرىيا پەرۋەردىگارىغا دۇئا قىلىپ: «ئى پەرۋەردىگارىم! ماڭا ئۆز دەرگاھىڭدىن بىر ياخشى پەرزەنت ئاتا قىلغىن، ھەقىقەتەن دۇئانى ئىشىتكۈچىسەن» دېدى'),
(332, 3, 39, 'ئۇ ئىبادەتگاھىدا ناماز ئوقۇۋاتقاندا پەرىشتىلەر ئۇنىڭغا: «ساڭا اﷲ نىڭ كەلىمىسى (يەنى ئىسا) نى تەستىق قىلغۇچى، سەييىد، پەرھىزكار ۋە ياخشىلاردىن بولغان يەھيا ئىسىملىك بىر پەيغەمبەر بىلەن خۇش خەۋەر بېرىدۇ» دەپ نىدا قىلدى'),
(333, 3, 40, 'ئۇ: «پەرۋەردىگارىم! مەن قېرىپ قالغان تۇرسام، ئايالىم تۇغۇتتىن قالغان تۇرسا قانداقمۇ مېنىڭ ئوغلۇم بولسۇن؟» دېدى. پەرىشتىلەر: «اﷲ شۇنىڭدەك خالىغىنىنى قىلىدۇ (يەنى قۇدرىتى ئىلاھىيە ھېچ نەرسىدىن ئاجىز ئەمەس)» دېدى'),
(334, 3, 41, 'ئۇ: ‹‹پەرۋەردىگارىم! ماڭا (ئايالىمنىڭ ھامىلىدار بولغانلىقىغا) بىر نىشانە ئاتا قىلغىن» دېدى. اﷲ: ‹‹ساڭا بولىدىغان نىشانە شۇكى، ئۈچ كۈنگىچە كىشىلەرگە سۆز قىلالمايسەن (يەنى زىكرى تەسبىھ ئېيتىشتىن باشقا سۆزگە تىلىڭ كەلمەيدۇ). پەقەت ئىشارەتلا قىلىسەن، (نېمىتىمگە شۈكۈر قىلىش يۈزىسىدىن) پەرۋەردىگارىڭنى كۆپ ياد ئەت، كەچتە، ئەتىگەندە تەسبىھ ئېيت» دېدى'),
(335, 3, 42, 'پەرىشتىلەر ئېيتتى: «ئى مەريەم! اﷲ ھەقىقەتەن سېنى (پۈتۈن ئاياللار ئىچىدىن) تاللىدى، سېنى پاك قىلدى، سېنى پۈتۈن جاھان ئاياللىرىدىن ئارتۇق قىلدى'),
(336, 3, 43, 'ئى مەريەم! پەرۋەردىگارىڭغا ئىتائەت قىل، سەجدە قىل ۋە رۇكۇ قىلغۇچىلار بىلەن بىللە رۇكۇ قىل»'),
(337, 3, 44, '(ئى مۇھەممەد!) بۇ - ساڭا ۋەھيى قىلىۋاتقىنىمىز - غەيب خەۋەرلىرىدىندۇر. مەريەمنى ئۇلارنىڭ قايسىسى تەربىيىسىگە ئېلىش (مەسىلىسىدە چەك تاشلاش يۈزىسىدىن) قەلەملىرىنى (سۇغا) تاشلىغان چاغدا، سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ، (مەريەمنىڭ كىمنىڭ تەربىيىسىدە بولۇشىنى) ئۆزئارا جاڭجال قىلىشقانلىرىدا سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ'),
(338, 3, 45, 'ئۆز ۋاقتىدا پەرىشتىلەر ئېيتتى: «ئى مەريەم! اﷲ ساڭا (ئاتىنىڭ ۋاسىتىسىز) اﷲ نىڭ بىر كەلىمىسى (دىن تۆرەلگەن بىر بوۋاق) بىلەن خۇش خەۋەر بېرىدۇكى، ئۇنىڭ ئىسمى، مەسىھ مەريەم ئوغلى ئىسادۇر، ئۇ دۇنيا ۋە ئاخىرەتتە ئابرۇيلۇق ۋە اﷲ قا يېقىنلاردىن بولىدۇ'),
(339, 3, 46, 'ئۇ بۆشۈكتىمۇ (يەنى بوۋاقلىق چېغىدىمۇ)، ئوتتۇرا ياش بولغاندىمۇ كىشىلەرگە (پەيغەمبەرلەرنىڭ سۆزىنى) سۆزلەيدۇ ۋە (تەقۋادارلىقتا كامىل) ياخشى ئادەملەردىن بولىدۇ»'),
(340, 3, 47, 'مەريەم: «پەرۋەردىگارىم! ماڭا ئىنساننىڭ قولى تەگمىگەن تۇرسا (يەنى ئەرلىك بولمىسام) قانداق بالام بولىدۇ؟» دېدى. پەرىشتە: «اﷲ خالىغىنىنى شۇنداق يارىتىدۇ (يەنى ئاتا - ئانا، ئارقىلىقمۇ ۋە ئۇنىڭسىزمۇ يارىتىدۇ). بىرەر ئىشنىڭ بولۇشىنى ئىرادە قىلسا، ۋۇجۇدقا كېلىدۇ» دېدى'),
(341, 3, 48, 'اﷲ ئۇنىڭغا خەتنى، ھېكمەتنى (يەنى سۆز ھەرىكەتتە توغرا بولۇشىنى)، تەۋراتنى، ئىنجىلنى ئۆگىتىدۇ'),
(342, 3, 49, 'ئۇنى بەنى ئىسرائىلغا پەيغەمبەر قىلىپ ئەۋەتىدۇ. (ئۇ دەيدۇ) «شۈبھىسىزكى، سىلەرگە مەن رەببىڭلار تەرىپىدىن بولغان (پەيغەمبەرلىكىمنىڭ راستلىقىنى كۆرسىتىدىغان) بىر مۆجىزە ئېلىپ كەلدىم، مەن سىلەرگە لايدىن قۇشنىڭ شەكلىدەك بىر نەرسە ياسايمەن، ئاندىن ئۇنىڭغا پۈۋلەيمەن - دە، اﷲ نىڭ ئىزنى بىلەن، ئۇ قۇش بولىدۇ. تۇغما كورنى، بەرەس كېسىلىنى ساقايتىمەن، اﷲ نىڭ ئىزنى بىلەن، ئۆلۈكلەرنى تىرىلدۈرىمەن، سىلەر يەيدىغان ۋە ئۆيۈڭلاردا ساقلايدىغان يېمەكلىكلەردىن خەۋەر بېرىمەن (يەنى سىلەرنىڭ يوشۇرۇن ئەھۋالىڭلارنى ئېيتىپ بېرەلەيمەن). شۈبھىسىزكى، ئەگەر سىلەر اﷲ نىڭ مۆجىزىلىرىگە ئىشەنگۈچى بولساڭلار، بۇنىڭدا (يەنى مەن كەلتۈرگەن مۆجىزىلەردە) سىلەر ئۈچۈن ئەلۋەتتە (مېنىڭ راستلىقىمنى كۆرسىتىدىغان روشەن) ئالامەت بار'),
(343, 3, 50, 'مەن ئىلگىرى كەلگەن تەۋراتنى تەستىق قىلغان ھالدا (كەلدىم)، سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن دەلىل ئېلىپ كەلدىم. اﷲ تىن قورقۇڭلار ۋە (مېنىڭ ئەمرىمگە) ئىتائەت قىلىڭلار'),
(344, 3, 51, 'اﷲ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر»'),
(345, 3, 52, 'ئىسا ئۇلارنى (يەنى يەھۇدىيلار) دىن كۇفرىنى (كۇفرىدا چىڭ تۇرۇپ، ئۇنى ئۆلتۈرۈش نىيىتىگە كەلگەنلىكىنى) سەزگەن چاغدا: «اﷲ ئۈچۈن (اﷲ قا دەۋەت قىلىش يولىدا) ماڭا كىملەر ياردەم بېرىدۇ؟» دېدى. ھەۋارىلار (يەنى ئۇنىڭ تەۋەلىرىدىن ھەقىقىي مۆمىنلەر) ئېيتتى: «اﷲ ئۈچۈن ساڭا بىز ياردەم بېرىمىز، اﷲ قا ئىمان ئېيتتۇق. گۇۋاھ بولغىنكى، بىز (پەيغەمبەرلىكىڭگە) بويسۇنغۇچىلارمىز'),
(346, 3, 53, 'پەرۋەردىگارىمىز! سەن نازىل قىلغان كىتابقا ئىشەندۇق، پەيغەمبەرگە ئەگەشتۇق، بىزنى (ھىدايىتىڭگە) شاھىت بولغانلار قاتارىدا قىلغىن»'),
(347, 3, 54, 'ئۇلار مىكىر ئىشلەتتى (يەنى ئىسا ئەلەيھىسسالامنى ئۆلتۈرمەكچى بولدى)، اﷲ ئۇلارنىڭ مىكرىنى بەربات قىلدى (ئىسا ئەلەيھىسسالامنى قۇتقۇزۇپ ئاسمانغا ئېلىپ چىقىپ كەتتى). اﷲ مىكىر قىلغۇچىلارنىڭ جازاسىنى ئوبدان بەرگۈچىدۇر'),
(348, 3, 55, 'ئۆز ۋاقتىدا اﷲ ئېيتتى: «ئى ئىسا! مەن سېنى (ئەجىلىڭ يەتكەندە) قەبزى روھ قىلىمەن، سېنى دەرگاھىمغا كۆتۈرىمەن (يەنى ئاسمانغا ئېلىپ چىقىمەن). سېنى كاپىرلاردىن پاك قىلىمەن (يەنى سېنى ئۆلتۈرمەكچى بولغان يامانلارنىڭ شەررىدىن ساقلايمەن)، ساڭا ئەگەشكەنلەرنى قىيامەتكىچە كاپىرلاردىن ئۈستۈن قىلىمەن. ئاندىن مېنىڭ دەرگاھىمغا قايتىسىلەر، سىلەر ئىختىلاپ قىلىشقان ئىش (يەنى ئىسانىڭ ئىشى) ئۈستىدە ئاراڭلاردا مەن ھۆكۈم چىقىرىمەن'),
(349, 3, 56, 'كاپىرلارغا بولسا دۇنيا ۋە ئاخىرەتتە قاتتىق ئازاب قىلىمەن، ئۇلارغا (ئۇلاردىن اﷲ نىڭ ئازابىنى توسىدىغان) ھېچ مەدەتكار بولمايدۇ»'),
(350, 3, 57, 'ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا كەلسەك، اﷲ ئۇلارنىڭ (قىلغان ياخشى ئەمەللىرىنىڭ) ئەجرىنى تولۇق بېرىدۇ. اﷲ زالىملارنى دوست تۇتمايدۇ (زالىملارنى دوست تۇتمايدىغان زات قانداقمۇ بەندىلىرىگە زۇلۇم قىلسۇن؟)'),
(351, 3, 58, '(ئى مۇھەممەد!) بۇ بولسا ساڭا بىز ئوقۇپ بېرىۋاتقان ئايەتلەردۇر ۋە ھېكمەتلىك قۇرئاندۇر'),
(352, 3, 59, 'شۈبھىسىزكى، اﷲ نىڭ نەزىرىدە ئىسانىڭ مىسالى (ئۇ ئاتىسىز يارىتىلغانلىقتىن) ئادەمنىڭ (يەنى ئادەم ئەلەيھىسسالامنىڭ) مىسالىغا ئوخشايدۇ. ئادەمنى اﷲ (ئاتا - ئانىسىز) تۇپراقتىن ياراتتى، ئاندىن ئۇنىڭغا: «ۋۇجۇدقا كەل» دېدى - دە، ئۇ ۋۇجۇدقا كەلدى (ئىسانىڭ ئىشى ئادەمنىڭ ئىشىدىن ئەجەبلىنەرلىك ئەمەس)'),
(353, 3, 60, '(ئىسا ھەققىدىكى بۇ سۆز) اﷲ تەرىپىدىن نازىل بولغان ھەقىقەتتۇر، سەن شەك قىلغۇچىلاردىن بولمىغىن'),
(354, 3, 61, 'سەن ئىسا توغرۇلۇق ھەقىقىي مەلۇماتقا ئىگە بولغىنىڭدىن كېيىن، كىملەركى سەن بىلەن مۇنازىرىلەشسە، سەن ئۇلارغا: «كېلىڭلار، ئوغۇللىرىمىزنى ۋە ئوغۇللىرىڭلارنى، ئاياللىرىمىزنى ۋە ئاياللىرىڭلارنى، ئۆزلىرىمىزنى ۋە ئۆزلىرىڭلارنى يىغىپ، اﷲ نىڭ لەنىتى يالغانچىلارغا بولسۇن، دەپ اﷲ قا يالۋۇرۇپ دۇئا قىلايلى» دېگىن'),
(355, 3, 62, 'شۈبھىسىزكى، بۇ ئەلۋەتتە راست قىسسىدۇر. بىر اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(356, 3, 63, 'ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە (ئۇلار بۇزغۇنچىلاردۇر)، شۈبھىسىزكى، اﷲ بۇزغۇنچىلارنى بىلىپ تۇرىدۇ'),
(357, 3, 64, '«ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) پەقەت اﷲ غىلا ئىبادەت قىلىش، اﷲ قا ھېچ نەرسىنى شېرىك كەلتۈرمەسلىك، اﷲ نى قويۇپ بىر - بىرىمىزنى خۇدا قىلىۋالماسلىقتەك ھەممىمىزگە ئورتاق بولغان بىر سۆزگە (يەنى بىرخىل ئەقىدىگە) ئەمەل قىلايلى» دېگىن، ئەگەر ئۇلار يۈز ئۆرۈسە (يەنى بۇنى قوبۇل قىلمىسا): «(ئى يەھۇدىيلار ۋە ناسارالار جامائەسى!) بىزنىڭ مۇسۇلمان ئىكەنلىكىمىزگە گۇۋاھ بولۇڭلار'),
(358, 3, 65, 'ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) نېمە ئۈچۈن ئىبراھىم توغرىسىدا مۇنازىرىلىشىسىلەر؟ (ئىبراھىمنى ئۆزۈڭلارنىڭ دىنىدا دەپ گۇمان قىلىسىلەر؟) ھالبۇكى، تەۋرات بىلەن ئىنجىل ئىبراھىمدىن كېيىن نازىل بولدى. (سۆزۈڭلارنىڭ ئاساسسىز ئىكەنلىكىنى) چۈشەنمەمسىلەر؟'),
(359, 3, 66, '(ئى يەھۇدىيلار ۋە ناسارالار جامائەسى!) سىلەر شۇنداق كىشىلەر سىلەركى، بىلىدىغان نەرسەڭلار (ئىسا) ئۈستىدە مۇنازىرىلەشتىڭلار، ئەمدى سىلەر بىلمەيدىغان نەرسەڭلار (ئىبراھىم ۋە ئۇنىڭ دىنى) ئۈستىدم نېمىشقا مۇنازىرىلىشىسىلەر؟ (ئىبراھىمنىڭ ئىشىدىكى ھەقىقەتنى) اﷲ بىلىدۇ، سىلەر بىلمەيسىلەر» دەڭلار'),
(360, 3, 67, 'ئىبراھىم يەھۇدىيمۇ ئەمەس، ناسارامۇ ئەمەس ۋە لېكىن توغرا دىنغا ئېتىقاد قىلغۇچى، اﷲ قا بويسۇنغۇچى ئىدى، مۇشرىكلاردىن ئەمەس ئىدى'),
(361, 3, 68, 'ئىبراھىمغا ئەڭ يېقىن بولغانلار، شۈبھىسىزكى، (ئىبراھىمنىڭ زامانىدا ۋە ئۇنىڭدىن كېيىن) ئۇنىڭغا (يەنى ئۇنىڭ يولىغا) ئەگەشكەنلەردۇر، بۇ پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) دۇر، (بۇ پەيغەمبەرنىڭ ئۈممىتى بولغان) مۆمىنلەردۇر. اﷲ مۆمىنلەرنىڭ مەدەتكارىدۇر'),
(362, 3, 69, 'ئەھلى كىتابتىن بىر تۈركۈم كىشىلەر سىلەرنى ئازدۇرۇشنى ئۈمىد قىلىدۇ، ئۇلار پەقەت ئۆزلىرىنىلا ئازدۇرىدۇ (بۇنىڭ ۋابالى ئۇلارغا ھەسسىلەپ ئازاب قىلىنىش بىلەن ئۆزلىرىگىلا قايتىدۇ). (لېكىن) ئۇلار (بۇنى) تۇيمايدۇ'),
(363, 3, 70, 'ئى ئەھلى كىتاب! اﷲ نىڭ ئايەتلىرىنى (يەنى مۇھەممەد ئەلەيھىسسالامغا نازىل قىلىنغان قۇرئاننىڭ ھەقلىقىنى) بىلىپ تۇرۇپ نېمىشقا ئىنكار قىلىسىلەر؟'),
(364, 3, 71, 'ئى ئەھلى كىتاب! نېمە ئۈچۈن ھەقنى باتىلغا ئارىلاشتۇرىسىلەر ۋە بىلىپ تۇرۇپ ھەقنى (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ كىتابلىرىڭلاردىكى سۈپىتىنى) يوشۇرىسىلەر؟'),
(365, 3, 72, 'ئەھلى كىتابتىن بىر تۈركۈمى: «مۆمىنلەرگە نازىل قىلىنغان ۋەھيىگە ئەتىگەندە ئىمان ئېيتىڭلار، كەچقۇرۇنلۇقى يېنىۋېلىڭلار، (شۇنداق قىلساڭلار) ئۇلار (ئىماندىن) قايتىشى مۇمكىن. پەقەت سىلەرنىڭ دىنىڭلارغا ئەگەشكەنلەرگىلا ئىشىنىڭلار» دېدى. (ئى مۇھەممەد!) ئېيتقىنكى، «توغرا يول اﷲ نىڭ يولىدۇر». (يەھۇدىيلار بى - بىرىگە ئېيتىدۇ) بىرەرسىگە سىلەرگە بېرىلگەنگە ئوخشاش ۋەھىي بېرىلىشتىن ياكى ئۇلارنىڭ (يەنى مۇسۇلمانلارنىڭ) (قىيامەت كۈنى) پەرۋەردىگارىڭلارنىڭ دەرگاھىدا سىلەر بىلەن مۇنازىرىلىشىشىدىن (يەنى مۇنازىرىلىشىپ سىلەرنى يېڭىپ قويۇشىدىن قورقۇپ) پەقەت دىنىڭلارغا ئەگەشكەن ئادەمگىلا ئىشىنىڭلار (دىنىڭلاردا بولمىغان ھېچ ئادەمنىڭ پەيغەمبەرلىكىنى ئېتىراپ قىلماڭلار. ئەگەر سىلەر مۇھەممەدنىڭ پەيغەمبەرلىكىنى ئىقرار قىلىپ ئۇنىڭ دىنىغا كىرمىسەڭلار، بۇ قىيامەت كۈنى سىلەرگە قارشى پاكىت بولىدۇ). (ئى مۇھەممەد!) ئېيتقىنكى، «پەزل - كەرەم (يەنى پەيغەمبەرلىك) اﷲ نىڭ ئىلكىدىدۇر. اﷲ ئۇنى خالىغان كىشىگە بېرىدۇ». اﷲ (نىڭ پەزلى) كەڭدۇر، (اﷲ) ھەممىنى بىلگۈچىدۇر'),
(366, 3, 73, 'ئەھلى كىتابتىن بىر تۈركۈمى: «مۆمىنلەرگە نازىل قىلىنغان ۋەھيىگە ئەتىگەندە ئىمان ئېيتىڭلار، كەچقۇرۇنلۇقى يېنىۋېلىڭلار، (شۇنداق قىلساڭلار) ئۇلار (ئىماندىن) قايتىشى مۇمكىن. پەقەت سىلەرنىڭ دىنىڭلارغا ئەگەشكەنلەرگىلا ئىشىنىڭلار» دېدى. (ئى مۇھەممەد!) ئېيتقىنكى، «توغرا يول اﷲ نىڭ يولىدۇر». (يەھۇدىيلار بى - بىرىگە ئېيتىدۇ) بىرەرسىگە سىلەرگە بېرىلگەنگە ئوخشاش ۋەھىي بېرىلىشتىن ياكى ئۇلارنىڭ (يەنى مۇسۇلمانلارنىڭ) (قىيامەت كۈنى) پەرۋەردىگارىڭلارنىڭ دەرگاھىدا سىلەر بىلەن مۇنازىرىلىشىشىدىن (يەنى مۇنازىرىلىشىپ سىلەرنى يېڭىپ قويۇشىدىن قورقۇپ) پەقەت دىنىڭلارغا ئەگەشكەن ئادەمگىلا ئىشىنىڭلار (دىنىڭلاردا بولمىغان ھېچ ئادەمنىڭ پەيغەمبەرلىكىنى ئېتىراپ قىلماڭلار. ئەگەر سىلەر مۇھەممەدنىڭ پەيغەمبەرلىكىنى ئىقرار قىلىپ ئۇنىڭ دىنىغا كىرمىسەڭلار، بۇ قىيامەت كۈنى سىلەرگە قارشى پاكىت بولىدۇ). (ئى مۇھەممەد!) ئېيتقىنكى، «پەزل - كەرەم (يەنى پەيغەمبەرلىك) اﷲ نىڭ ئىلكىدىدۇر. اﷲ ئۇنى خالىغان كىشىگە بېرىدۇ». اﷲ (نىڭ پەزلى) كەڭدۇر، (اﷲ) ھەممىنى بىلگۈچىدۇر'),
(367, 3, 74, 'اﷲ رەھمىتىنى ئۆزى خالىغان كىشىگە خاس قىلىدۇ، اﷲ ئۇلۇغ پەزل ئىگىسىدۇر'),
(368, 3, 75, 'ئەھلى كىتابتىن بەزى كىشىلەر باركى، ئۇنىڭدا كۆپ مال ئامانەت قويساڭمۇ ساڭا ئۇنى قايتۇرۇپ بېرىدۇ؛ ئۇلاردىن يەنە بەزى كىشىلەرمۇ باركى، ئۇنىڭدا بىر دىنار ئامانەت قويساڭمۇ ئۇنىڭ بېشىدا تۇرمىغىچە (يەنى قايتا - قايتا سۈيلىمىگىچە) ئۇنى ساڭا قايتۇرۇپ بەرمەيدۇ. بۇ شۇنىڭ ئۈچۈنكى، ئۇلار: «ئۈممىلەرنىڭ مېلىغا خىيانەت قىلساق بىزگە گۇناھ بولمايدۇ (يەنى بىزنىڭ دىنىمىزدا بولمىغانلارنىڭ ماللىرى بىزگە ھارام ئەمەس)» دېدى، ئۇلار بىلىپ تۇرۇپ اﷲ نامىدىن يالغاننى توقۇيدۇ'),
(369, 3, 76, 'ئۇنداق ئەمەس (يەنى ئۇلار باشقا دىندىكىلەرگە زۇلۇم قىلسا گۇناھ بولىدۇ). ئەھدىگە ۋاپا قىلغان ۋە گۇناھتىن ساقلانغانلار (اﷲ دوست تۇتقان كىشىلەردۇر)، چۈنكى اﷲ تەقۋادارلارنى ھەقىقەتەن دوست تۇتىدۇ'),
(370, 3, 77, 'اﷲ قا بەرگەن ئەھدىنى ۋە قەسەملىرىنى ئازغىنا بەدەلگە تېگىشىدىغانلار ئاخىرەتتە (اﷲ نىڭ رەھمىتىدىن) ھېچقانداق نېسىۋىگە ئېرىشەلمەيدۇ، قىيامەت كۈنى اﷲ ئۇلارغا (ئۇلارنى خۇش قىلىدىغان) سۆز قىلمايدۇ، ئۇلارغا (رەھمەت كۆزى بىلەن) قارىمايدۇ، ئۇلارنى (گۇناھلىرىدىن) پاكلىمايدۇ، ئۇلار قاتتىق ئازابقا قالىدۇ'),
(371, 3, 78, 'ئۇلاردىن (يەنى يەھۇدىيلاردىن) بىر تۈركۈمى كىتابتا بولمىغان نەرسىلەرنى، سىلەرنىڭ كىتابتا بار ئىكەن دەپ ئويلىشىڭلار ئۈچۈن، تىللىرىنى ئەگرى - بۈگرى قىلىپ ئوقۇيدۇ ۋە ئۇنى اﷲ تەرىپىدىن نازىل بولغان دەيدۇ. ھالبۇكى، ئۇ اﷲ تەرىپىدىن نازىل بولغان ئەمەس، ئۇلار بىلىپ تۇرۇپ اﷲ نامىدىن يالغاننى توقۇيدۇ'),
(372, 3, 79, 'اﷲ بىرەر ئىنسانغا كىتابنى، ھېكمەتنى، پەيغەمبەرلىكنى بەرگەندىن كېيىن، ئۇنىڭ كىشىلەرگە: «اﷲ نى قويۇپ ماڭا بەندە بولۇڭلار» دېيىشى لايىق بولمايدۇ (يەنى پەيغەمبەردىن مۇنداق گەپنىڭ سادىر بولۇشىنى تەسەۋۋۇر قىلغىلى بولمايدۇ) ۋە لېكىن (ئۇ): «كىتابنى ئۆگەتكەنلىكىڭلار، ئوقۇغانلىقىڭلار ئۈچۈن رەببانى بولۇڭلار» (دەيدۇ)'),
(373, 3, 80, 'شۇنىڭدەك ئۇنىڭ پەرىشتىلەرنى، پەيغەمبەرلەرنى رەب قىلىۋېلىشقا بۇيرۇشى لايىق بولمايدۇ. مۇسۇلمان بولغىنىڭلاردىن كېيىن پەيغەمبىرىڭلار سىلەرنى كۇفرىغا بۇيرۇمدۇ؟'),
(374, 3, 81, 'ئۆز ۋاقتىدىلا اﷲ پەيغەمبەرلەردىن چىن ئەھدە ئېلىپ: «سىلەرگە مەن كىتابنى ۋە ھېكمەتنى ئاتا قىلدىم، كېيىن سىلەرگە، سىلەردىكى نەرسىلەرنى (يەنى كىتاب بىلەن ھېكمەتنى) ئېتىراپ قىلغۇچى بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلسە ئۇنىڭغا، ئەلۋەتتە، ئىمان ئېيتىشىڭلار كېرەك ۋە ئۇنىڭغا ئەلۋەتتە ياردەم بېرىشىڭلار كېرەك» (دېدى). اﷲ: «(بۇ ئەھدىنى) ئىقرار (يەنى ئېتىراپ) قىلدىڭلارمۇ؟ شۇنىڭ (يەنە بۇ ئىش) ئۈچۈن مېنىڭ ئەھدىمنى قوبۇل قىلدىڭلارمۇ؟» دېدى. ئۇلار: «ئېتىراپ قىلدۇق» دېدى. اﷲ: «(ئۆزۈڭلارنىڭ ۋە تەۋەلىرىڭلارنىڭ ئېتىراپ قىلغانلىقىغا) گۇۋاھ بولۇڭلار، مەنمۇ سىلەر بىلەن بىللە گۇۋاھ بولغۇچىلاردىندۇرمەن» دېدى'),
(375, 3, 82, 'كىمكى شۇنىڭدىن كېيىن يۈز ئۆرۈسە (يەنى ئەھدىنى بۇزسا)، ئۇلار اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر'),
(376, 3, 83, 'ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) اﷲ نىڭ دىنىدىن (يەنى ئىسلام دىنىدىن) باشقا دىننى تىلەمدۇ؟ ھالبۇكى، ئاسمانلاردىكىلەر ئىختىيارىي ۋە ئىختىيارسىز رەۋىشتە اﷲ قا بويسۇنغان تۇرسا، ئۇلار (قىيامەت كۈنى) اﷲ تەرىپىگە قايتۇرۇلىدۇ'),
(377, 3, 84, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «بىز اﷲ قا ئىمان ئېيتتۇق، بىزگە نازىل قىلىنغانغا (يەنى قۇرئانغا)، ئىبراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا ۋە ئۇنىڭ ئەۋلادلىرىغا نازىل قىلىنغان ۋەھيىگە، مۇساغا بېرىلگەنگە (يەنى ئىنجىلغا) ۋە پەيغەمبەرلەرگە پەرۋەردىگارى تەرىپىدىن بېرىلگەنگە (يەنى كىتابلارغا) ئىمان ئېيتتۇق، ئۇلارنىڭ ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ، بەزىسىنى ئىنكار قىلمايمىز)، بىز اﷲ قا بويسۇنغۇچىلارمىز»'),
(378, 3, 85, 'كىمكى ئىسلام (دىنىدىن) غەيرىي دىننى تىلەيدىكەن، ھەرگىز ئۇ (يەنى ئۇنىڭ دىنى) قوبۇل قىلىنمايدۇ، ئۇ ئاخىرەتتە زىيان تارتقۇچىدۇر'),
(379, 3, 86, 'ئىمان ئېيتقاندىن، پەيغەمبەرنىڭ ھەق ئىكەنلىكىگە شاھىت بولغاندىن ۋە ئۆزلىرىگە روشەن دەلىللەر كەلگەندىن كېيىن كاپىر بولغان قەۋمنى اﷲ قانداقمۇ ھىدايەت قىلسۇن! اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ'),
(380, 3, 87, 'اﷲ نىڭ، پەرىشتىلەرنىڭ ۋە ئىنسانلارنىڭ ھەممىسىنىڭ لەنىتىگە ئۇچراش ئۇلارغا بېرىلگەن جازادۇر'),
(381, 3, 88, 'ئۇلار دوزاختا مەڭگۈ قالىدۇ. ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ. كېچىكتۈرۈلمەيدۇ'),
(382, 3, 89, 'پەقەت تەۋبە قىلغانلار ۋە (بۇزۇق ئەمەللىرىنى) تۈزەتكەنلەر بۇنىڭدىن مۇستەسنا، اﷲ (مۇنداقلارغا) مەغپىرەت قىلغۇچىدۇر ۋە مېھرىباندۇر'),
(383, 3, 90, 'شۈبھىسىزكى، ئىمان ئېيتقاندىن كېيىن كاپىر بولغانلار، ئاندىن كۇفرىنى تېخىمۇ ئاشۇرغانلارنىڭ تەۋبىسى ھەرگىزمۇ قوبۇل قىلىنمايدۇ. ئەنە شۇلار گۇمراھلاردۇر'),
(384, 3, 91, 'شۈبھىسىزكى، كاپىر بولۇپ (تەۋبە قىلماستىن) كۇفرى بىلەن ئۆلگەنلەرنىڭ ھېچبىرىدىن يەر يۈزىگە بىر كېلىدىغان ئالتۇن فىدىيە بەرسىمۇ قوبۇل قىلىنمايدۇ، ئەنە شۇلار قاتتىق ئازابقا قالىدۇ. ئۇلارغا (ئۇلارنى اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچبىر مەدەتكار بولمايدۇ'),
(385, 3, 92, 'ياخشى كۆرگەن نەرسەڭلاردىن سەرپ قىلمىغۇچە (يەنى ماللىرىڭلارنىڭ ياخشىسىنى سەدىقە قىلمىغۇچە) ھەرگىز ياخشىلىققا (يەنى جەننەتكە) ئېرىشەلمەيسىلەر، (اﷲ نىڭ يولىدا) نېمىنى سەرپ قىلماڭلار. اﷲ ئۇنى بىلىپ تۇرغۇچىدۇر'),
(386, 3, 93, 'تەۋرات نازىل قىلىنىشتىن ئىلگىرى، ئىسرائىل (يەنى يەئقۇب ئەلەيھىسسالام) ئۆزىگە ھارام قىلغان يېمەكلىكلەر (يەنى تۆگىنىڭ گۆشى ۋە سۈتى) دىن باشقا يېمەكلىكلەرنىڭ ھەممىسى ئىسرائىل ئەۋلادىغا ھالال ئىدى. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئەگەر راستچىل بولساڭلار، تەۋراتنى ئېلىپ كېلىپ ئوقۇپ بېقىڭلار»'),
(387, 3, 94, 'شۇنىڭدىن كېيىن (يەنى روشەن دەلىل مەيدانغا كەلگەندىن كېيىن)، كىمكى اﷲ نامىدىن يالغاننى توقۇيدىكەن، ئۇلار زالىملاردۇر'),
(388, 3, 95, 'ئېيتقىنكى، «اﷲ راست ئېيتتى، (ئىسلام دىنىدىن ئىبارەت) ئىبراھىمنىڭ توغرا دىنىغا ئەگىشىڭلار، ئۇ (يەنى ئىبراھىم) مۇشرىكلاردىن ئەمەس ئىدى»'),
(389, 3, 96, 'ھەقىقەتەن ئىنسانلارغا (ئىبادەت ئۈچۈن) تۇنجى سېلىنغان ئۆي (يەنى بەيتۇللا) مەككىدىدۇر، مۇبارەكتۇر، جاھان ئەھلىگە ھىدايەتتۇر'),
(390, 3, 97, 'ئۇنىڭدا ئوچۇق ئالامەتلەر باركى، ماقامى ئىبراھىم شۇلارنىڭ بىرى، بەيتۇللاغا كىرگەن ئادەم ئەمىن بولىدۇ. قادىر بولالىغان كىشىلەرنىڭ اﷲ ئۈچۈن كەبىنى زىيارەت قىلىشى ئۇلارغا پەرز قىلىندى. كىمكى ئىنكار قىلىدىكەن (يەنى ھەجنى تەرك ئېتىدىكەن، زىيىنى ئۆزىگە)، شۈبھىسىزكى، اﷲ ئەھلى جاھاندىن (يەنى ئۇلارنىڭ ئىبادىتىدىن) بىھاجەتتۇر'),
(391, 3, 98, 'ئى مۇھەممەد! ئېيتقىنكى: «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار، ناسارالار) نېمە ئۈچۈن اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلىسىلەر؟ ۋەھالەنكى، اﷲ قىلمىشىڭلارغا شاھىتتۇر»'),
(392, 3, 99, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار، ناسارالار) (ئىسلام دىنىنىڭ ھەقلىقىغا) شاھىت بولۇپ تۇرۇپ نېمە ئۈچۈن اﷲ نىڭ يولىنى ئەگرى كۆرسىتىشنى قەستلەپ، مۆمىنلەرنى ئۇنىڭدىن توسىسىلەر؟ اﷲ قىلمىشىڭلاردىن غاپىل ئەمەستۇر»'),
(393, 3, 100, 'ئى مۆمىنلەر! ئەگەر ئەھلى كىتابتىن بىر پىرقىغە ئىتائەت قىلساڭلار، مۆمىن بولغىنىڭلاردىن كېيىن، ئۇلار سىلەرنى كاپىر قىلىپ قويىدۇ'),
(394, 3, 101, 'سىلەرگە اﷲ نىڭ ئايەتلىرى ئوقۇلۇۋاتقان (يەنى ۋەھيى ئۈزۈلۈپ قالماي اﷲ نىڭ ئايەتلىرى نازىل بولۇۋاتقان) ۋە ئاراڭلاردا اﷲ نىڭ پەيغەمبىرى (ھايات) تۇرغان تۇرسا، قانداقمۇ كاپىر بولۇسىلەر؟ كىمكى اﷲ قا (يەنى اﷲ نىڭ دىنىغا) مەھكەم يېپىشىدىكەن، ئۇ توغرا يولغا يېتەكلەنگەن بولىدۇ'),
(395, 3, 102, 'ئى مۆمىنلەر! اﷲ قا لايىق رەۋىشتە تەقۋادارلىق قىلىڭلار، پەقەت مۇسۇلمانلىق ھالىتىڭلار بىلەنلا ۋاپات بولۇڭلار'),
(396, 3, 103, 'ھەممىڭلار اﷲ نىڭ ئاغامچىسىغا (يەنى اﷲ نىڭ دىنىغا) مەھكەم يېپىشىڭلار، ئايرىلماڭلار (يەنى سىلەردىن ئىلگىرى يەھۇدىيلار ۋە ناسارالار ئىختىلاپ قىلىشقاندەك، دىندا ئىختىلاپ قىلىشماڭلار). اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار، ئۆز ۋاقتىدا سىلەر ئۆزئارا دۈشمەن ئىدىڭلار، اﷲ دىلىڭلارنى بىرلەشتۈردى، اﷲ نىڭ نېمىتى بىلەن ئۆزئارا قېرىنداش بولدۇڭلار، سىلەر دوزاخ چۇقۇرىنىڭ گىرۋىكىدە ئىدىڭلار، اﷲ سىلەرنى (ئىسلام ئارقىلىق) ئۇنىڭدىن قۇتقۇزدى. سىلەرنىڭ ھىدايەت تېپىشىڭلار ئۈچۈن، اﷲ ئايەتلىرىنى سىلەرگە شۇنداق بايان قىلىدۇ'),
(397, 3, 104, 'سىلەرنىڭ ئاراڭلاردا خەيرلىك ئىشلارغا دەۋەت قىلىدىغان، ياخشى ئىشلارغا بۇيرۇپ، يامان ئىشلارنى مەنئى قىلىدىغان بىر جامائە بولسۇن؛ ئەنە شۇلار مەقسىتىگە ئېرىشكۈچىلەردۇر'),
(398, 3, 105, 'روشەن دەلىللەر كەلگەندىن كېيىن ئايرىلىپ كېتىشكەن ۋە ئىختىلاپ قىلىشقان كىشىلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دەك بولماڭلار، ئەنە شۇلار چوڭ ئازابقا دۇچار بولىدۇ'),
(399, 3, 106, 'شۇ كۈندە (يەنى قىيامەت كۈنىدە) بەزى يۈزلەر (يەنى مۆمىنلەرنىڭ يۈزلىرى) ئاقىرىدۇ، بەزى يۈزلەر (يەنى كۇففارلارنىڭ يۈزلىرى) قارىيىدۇ. يۈزى قارايغانلارغا (پەرىشتىلەر ئېيتىدۇكى) «ئىمان ئېيتقىنىڭلاردىن كېيىن كاپىر بولدۇڭلارمۇ؟ كاپىر بولغانلىقىڭلار ئۈچۈن ئازابنى تېتىڭلار»'),
(400, 3, 107, 'يۈزى ئاقارغانلار اﷲ نىڭ رەھمىتىدە (يەنى جەننەتتە) بولىدۇ، ئۇلار ئۇنىڭدا (يەنى جەننەتتە) مەڭگۈ قالىدۇ'),
(401, 3, 108, 'بۇ، اﷲ نىڭ ئايەتلىرىدۇر، ئۇلارنى (ئى مۇھەممەد!) ساڭا راست ئوقۇپ بېرىمىز، اﷲ جاھان ئەھلىگە زۇلۇم قىلىشنى خالىمايدۇ'),
(402, 3, 109, 'ئاسمانلار ۋە زېمىندىكى شەيئىلەر اﷲ نىڭ (مۈلكى، مەخلۇقاتى ۋە بەندىلىرى) دۇر، ھەممە ئىشلار اﷲ قا قايتۇرۇلىدۇ'),
(403, 3, 110, '(ئى مۇھەممەد ئۈممىتى!) سىلەر ئىنسانلار مەنپەئىتى ئۈچۈن ئوتتۇرىغا چىقىرىلغان ياخشىلىققا بۇيرۇپ يامانلىقتىن توسىدىغان اﷲ قا ئىمان ئېيتىدىغان ئەڭ ياخشى ئۈممەتىسلەر. ئەگەر ئەھلى كىتاب (يەنى يەھۇدىيلار، ناسارالار) ئىمان ئېيتسا (يەنى مۇھەممەد ئەلەيھىسسالامغا نازىل قىلىنغان ۋەھيىگە ئىشەنسە)، ئۇلار ئۈچۈن (دۇنيا ۋە ئاخىرەتتە) ئەلۋەتتە ياخشى بولاتتى. ئۇلارنىڭ ئىچىدە مۆمىنلەرمۇ بار، ئۇلارنىڭ تولىسى پاسىقلاردۇر'),
(404, 3, 111, 'ئۇلار سىلەرگە ئازار بېرىشتىن باشقا ھېچقانداق زىيان - زەخمەت يەتكۈزەلمەيدۇ؛ ئەگەر سىلەر بىلەن ئۇرۇشسا، سىلەرگە ئارقىسىنى قىلىپ قاچىدۇ، ئاندىن ئۇلارغا ياردەم بېرىلمەيدۇ'),
(405, 3, 112, 'ئۇلارنىڭ قەيەردە بولمىسۇن اﷲ نىڭ ۋە مۇسۇلمانلارنىڭ ھىمايىسىدە بولغانلىرىدىن باشقىلىرى خارلىققا پېتىپ قالدى، اﷲ نىڭ غەزىپىگە ئۇچرىدى. موھتاجلىققا چۆمدى. بۇ ئۇلارنىڭ اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى ۋە پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى ئۈچۈندۇر، ئۇلارنىڭ اﷲ نىڭ پەرمانىدىن چىقىپ كەتكەنلىكلىرى ۋە اﷲ نىڭ چەكلىمىسىدىن ئېشىپ كەتكەنلىكلىرى ئۈچۈندۇر'),
(406, 3, 113, 'ئۇلار بىرخىل ئەمەس، ئەھلى كىتابتىن (يەنى يەھۇدىيلار، ناسارالاردىن) كېچىلىرى اﷲ نىڭ ئايەتلىرىنى تىلاۋەت قىلىپ تۇرىدىغان ۋە سەجدە قىلىپ تۇرىدىغان بىر جامائە كىشىلەر بار'),
(407, 3, 114, 'ئۇلار اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدۇ، ياخشى ئىشلارغا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، خەيرلىك ئىشلارنى قىلىشقا ئالدىرايدۇ؛ ئەنە شۇلار ياخشىلاردىندۇر'),
(408, 3, 115, 'ئۇلار ھەر قانداق ياخشىلىقنى قىلسۇن، ئۇ بىكار كەتمەيدۇ. اﷲ تەقۋادارلارنى ئوبدان بىلىدۇ'),
(409, 3, 116, 'شۈبھىسىزكى، كاپىرلارنىڭ ماللىرى، بالىلىرى اﷲ نىڭ ئازابى ئالدىدا ھەرگىز ھېچ نەرسىگە دال بولالمايدۇ؛ ئۇلار دوزاخ ئەھلىدۇر، دوزاختا مەڭگۈ قالىدۇ'),
(410, 3, 117, 'ئۇلارنىڭ بۇ دۇنيادا (رىيا قىلىپ) بەرگەن سەدىقىلىرى ئۆزلىرىگە زۇلۇم قىلغان بىر قەۋمنىڭ زىرائىتىگە تېگىپ ئۇنى نابۇت قىلىۋەتكەن سوغۇق شامالغا ئوخشايدۇ (ئىگىلىرىنىڭ گۇناھى تۈپەيلىدىن نابۇت قىلىنغان بۇ زىرائەتكە ئوخشاش، اﷲ ئۇلارنىڭ ياخشى ئەمەللىرىنى بەربات قىلىدۇ). ئۇلارغا اﷲ زۇلۇم قىلمىدى ۋە لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى'),
(411, 3, 118, 'ئى مۆمىنلەر! ئۆزۈڭلاردىن بولمىغانلارنى (يەنى مۇناپىقلارنى) سىرداش قىلىۋالماڭلار، ئۇلار سىلەرگە بۇزغۇنچىلىق قىلىشتا بوشاڭلىق قىلىپ قالمايدۇ. ئۇلار سىلەرنىڭ مۇشەققەتتە قېلىشىڭلارنى ئارزۇ قىلىدۇ، ئۇلارنىڭ دۈشمەنلىكى ئېغىزلىرىدىن مەلۇم بولۇپ تۇرىدۇ، ئۇلارنىڭ دىللىرىدا يوشۇرغان دۈشمەنلىكى تېخىمۇ چوڭدۇر. شۈبھىسىزكى، ئەگەر چۈشىنىدىغان بولساڭلار، سىلەرگە نۇرغۇن ئايەتلەرنى بايان قىلدۇق'),
(412, 3, 119, '(ئى مۆمىنلەر جامائەسى!) سىلەر ئۇلارنى دوست تۇتىسىلەر، ئۇلار سىلەرنى دوست تۇتمايدۇ (سىلەرگە بولغان دۈشمەنلىكنى يوشۇرۇن تۇتىدۇ)، سىلەر (ھەممە ساماۋى) كىتابقا ئىشىنىسىلەر (شۇنداق تۇرۇقلۇق ئۇلار سىلەرنى ئۆچ كۆرىدۇ)، ئۇلار سىلەر بىلەن ئۇچراشقاندا، ئىمان ئېيتتۇق، دەيدۇ. ئۆزلىرى يالغۇز قالغاندا سىلەرگە بولغان ئاچچىقىدىن بارماقلىرىنى چىشلەيدۇ. (ئى مۇھەممەد!) ئېيتىقىنكى، «ئاچچىقىڭلار بىلەن ئۆلۈڭلار! (يەنى اﷲ ئۆلگىنىڭلارغا قەدەر ئاچچىقىڭلارنى داۋاملاشتۇرسۇن!)» اﷲ ھەقىقەتەن دىللاردىكىنى بىلگۈچىدۇر'),
(413, 3, 120, 'ئەگەر سىلەرگە (كەڭچىلىك، مولچىلىق، نۇسرەت، غەنىمەت قاتارلىق) بىرەر ياخشىلىق يەتسە، ئۇلار بۇنىڭدىن قايغۇرىدۇ؛ ئەگەر سىلەرگە (ئېغىرچىلىق، قەھەتچىلىك ۋە مەغلۇبىيەت قاتارلىق) بىرەر يامانلىق يەتسە، ئۇلار بۇنىڭدىن خۇشال بولىدۇ. ئەگەر سىلەر (ئۇلارنىڭ ئەزىيىتىگە) سەۋر قىلساڭلار ۋە (سۆزۈڭلاردا، ھەرىكىتىڭلاردا اﷲ تىن) قورقساڭلار، ئۇلارنىڭ ھىيلىسى سىلەرگە قىلچە زىيان يەتكۈزەلمەيدۇ. اﷲ ئۇلارنىڭ قىلمىشلىرىنى ھەقىقەتەن تولۇق بىلگۈچىدۇر'),
(414, 3, 121, '(ئى مۇھەممەد!) ئۆز ۋاقتىدا سەن ئەتىگەندە ئۆيۈڭدىن (ئۇھۇدقا) چىقىپ مۆمىنلەرنى ئۇرۇش سەپلىرىگە ئورۇنلاشتۇردۇڭ، اﷲ (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر'),
(415, 3, 122, 'ئۆز ۋاقتىدا سىلەردىن ئىككى گۇرۇھ (يەنى سەلەمە ئايمىقى بىلەن بەنى ھارىسە ئايمىقى) ئاجىزلىق كۆرسەتمەكچى (يەنى قورقۇپ ئۇرۇش مەيدانىدىن قايتماقچى) بولدى؛ اﷲ ئۇلارنىڭ مەدەتكارى ئىدى. (مۆمىنلەر جىمى ئەھۋالى ۋە جىمى ئىشلىرىدا) اﷲ غىلا تەۋەككۈل قىلسۇن!'),
(416, 3, 123, 'اﷲ سىلەرگە بەدرىدە نۇسرەت ئاتا قىلدى، ھالبۇكى، سىلەر كۈچسىز ئىدىڭلار (سانىڭلار ۋە قورالىڭلار ئاز ئىدى). اﷲ نىڭ سىلەرگە بەرگەن نۇسرىتىگە شۈكۈر قىلىش يۈزىسىدىن اﷲ تىن قورقۇڭلار'),
(417, 3, 124, '(ئى مۇھەممەد!) ئۆز ۋاقتىدا سەن مۆمىنلەرگە: «پەرۋەردىگارىڭلار سىلەرگە ئۈچ مىڭ پەرىشتە چۈشۈرۈپ ياردەم بەرسە يەنە يەتمەمدۇ؟» دەيتتىڭ'),
(418, 3, 125, 'شۇنداق، ئۇ يېتىدۇ. ئەگەر (جەڭدە) سەۋر قىلساڭلار، (اﷲ تىن) قورقۇپ (ئۇنىڭ ئەمرىگە ئىتائەت قىلساڭلار)، دۈشمەن دەرھال ھۇجۇم قىلىپ كەلگەندە پەرۋەردىگارىڭلار سىلەرگە بەش مىڭ نىشانلىق پەرىشتە بىلەن ياردەم بېرىدۇ'),
(419, 3, 126, 'اﷲ ئۇنى (يەنى پەرىشتىلەر ئارقىلىق ياردەم بېرىشنى) پەقەت سىلەرگە خۇش خەۋەر بولسۇن ۋە دىلىڭلار شۇنىڭ بىلەن خاتىرجەم بولسۇن (شۇنىڭ بىلەن دۈشمەننىڭ سانىنىڭ كۆپلۈكىدىن قورقمىسۇن) دەپ قىلدى. ياردەم پەقەت غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ تەرىپىدىندۇر'),
(420, 3, 127, '(اﷲ نىڭ ياردىمى) كاپىرلارنىڭ بىر تۈركۈمىنى ھالاك قىلىش ئۈچۈن ياكى ئۇلارنى رەسۋا قىلىپ، ئۇلارنىڭ ئۈمىدسىز ھالدا مەغلۇپ بولۇشلىرى ئۈچۈندۇر'),
(421, 3, 128, '(ئى مۇھەممەد!) ھېچ ئىش (يەنى بەندىلەرنىڭ ئىشلىرىنىڭ تەدبىرى) سېنىڭ ئىختىيارىڭدا ئەمەس، اﷲ يا ئۇلارنىڭ (مۇسۇلمان بولسا) تەۋبىسىنى قوبۇل قىلىدۇ، يا ئۇلار زالىم بولغانلىقى ئۈچۈن (كۇفرىدا چىڭ تۇرسا) ئۇلارنى ئازابقا دۇچار قىلىدۇ'),
(422, 3, 129, 'ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭ مۈلكىدۇر. اﷲ خالىغان كىشىگە مەغپىرەت قىلىدۇ، خالىغان كىشىگە ئازاب قىلىدۇ. اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(423, 3, 130, 'ئى مۆمىنلەر! جازانىنى قاتمۇقات ئاشۇرۇپ يېمەڭلار، مەقسىدىڭلارغا ئېرىشىش ئۈچۈن اﷲ تىن (نەھيى قىلغان ئىشلارنى تەرك ئېتىپ) قورقۇڭلار'),
(424, 3, 131, 'كاپىرلار ئۈچۈن تەييارلانغان دوزاختىن (يەنى دوزاخقا كىرىشكە سەۋەب بولىدىغان يامان ئىشلاردىن) ساقلىنىڭلار'),
(425, 3, 132, 'سىلەرگە رەھمەت قىلىنىشى ئۈچۈن، اﷲ قا ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار'),
(426, 3, 133, 'پەرۋەردىگارىڭلارنىڭ مەغپىرىتىگە ۋە تەقۋادارلار ئۈچۈن تەييارلانغان، كەڭلىكى ئاسمان - زېمىنچە كېلىدىغان جەننەتكە ئالدىراڭلار'),
(427, 3, 134, 'تەقۋادارلار كەڭچىلىكتىمۇ، قىسىنچىلىقتىمۇ اﷲ يولىدا (پۇل - مال) سەرپ قىلىدىغانلار، (ئۆچ ئېلىشقا قادىر تۇرۇقلۇق) ئاچچىقىنى يۇتىدىغانلار، (يامانلىق قىلغان ياكى بوزەك قىلغان) كىشىلەرنى كەچۈرىدىغانلاردۇر. اﷲ ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ'),
(428, 3, 135, 'تەقۋادارلار يامان بىر گۇناھ قىلىپ قالسا ياكى ئۆزلىرىگە زۇلۇم قىلسا اﷲ نى ياد ئېتىدۇ، گۇناھلىرى ئۈچۈن مەغپىرەت تەلەپ قىلىدۇ، گۇناھنى كەچۈرىدىغان اﷲ تىن باشقا كىم بار؟ ئۇلار قىلمىشلىرىنى بىلىپ تۇرۇپ داۋاملاشتۇرمايدۇ'),
(429, 3, 136, 'ئۇلارنىڭ (يەنى يۇقىرىقى خىسلەتلەرگە ئىگە كىشىلەرنىڭ) مۇكاپاتى پەرۋەردىگارى تەرىپىدىن بولغان مەغپىرەت ۋە ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردۇر. بۇ يەرلەردە ئۇلار مەڭگۈ قالىدۇ. ياخشى ئىش قىلغۇچىلارنىڭ ئېرىشىدىغان ساۋابى نېمىدېگەن ياخشى!'),
(430, 3, 137, 'سىلەردىن ئىلگىرى نۇرغۇن ئۈممەتلەر كېلىپ كەتتى؛ يەر يۈزىدە سەير قىلىڭلار، (ھەقنى) يالغانغا چىقارغانلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار'),
(431, 3, 138, 'بۇ (قۇرئان) (ئومۇمىي) ئىنسانلارغا باياندۇر (يەنى ھالال بىلەن ھارامنى ئوچۇق بايان قىلىدۇ)، تەقۋادارلارغا توغرا يول كۆرسەتكۈچىدۇر ۋە ۋەز - نەسىھەتتۇر'),
(432, 3, 139, '(جىھادتىن) بوشىشىپ قالماڭلار، شېھىت بولغانلار ئۈچۈن قايغۇرماڭلار، ئەگەر مۆمىن بولساڭلار، ئۈستۈنلۈك قازىنىسىلەر'),
(433, 3, 140, 'ئەگەر سىلەرگە شىكەستىلىك يەتكەن بولسا (يەنى بۇ جەڭدە سىلەر يارىلانغان ۋە شېھىت بولغان بولساڭلار)، دۈشمەنلەرگىمۇ (بەدرى جېڭىدە) ئوخشاشلا شىكەستلىك يەتتى (يەنى ئۇلار يارىلاندى ۋە ئۆلتۈردى). بۇ كۈنلەرنى ئىنسانلار ئارىسىدا ئايلاندۇرۇپ تۇرىمىز (يەنى غەلبىنى ھەمىشە بىر پىرقىدە قىلماي، بىر كۈن بۇ پىرقىدە، بىركۈن ئۇ پىرقىدە قىلىمىز)، (بۇ) اﷲ نىڭ (ھەقىقىي) مۆمىنلەرنى (مۇناپىقلاردىن) ئايرىشى ئۈچۈندۇر، سىلەرنى شېھىتلەردىن قىلىشى (يەنى سىلەردىن بەزىلەرنى شېھىتلىك دەرىجىسىگە مۇشەررەپ قىلىشى) ئۈچۈندۇر، - اﷲ زالىملارنى دوست تۇتمايدۇ'),
(434, 3, 141, 'مۆمىنلەرنى (ئۇلارغا يەتكەن مۇسىبەت ئارقىلىق) پاكلىشى ئۈچۈندۇر، كاپىرلارنى يوق قىلىشى ئۈچۈندۇر'),
(435, 3, 142, '(ئى مۆمىنلەر جامائەسى!) اﷲ سىلەردىن (ھەقىقىي) جىھاد قىلغانلارنى ۋە (جىھادنىڭ جاپا - مۇشەققەتلىرىگە) چىدىغۇچىلارنى بىلمەي (يەنى ئايرىماي تۇرۇپ)، (پەقەت ئاغزىڭلاردا ئىمان ئېيتتۇق دېيىش بىلەنلا) جەننەتكە كىرىشنى ئويلامسىلەر؟'),
(436, 3, 143, 'سىلەر ئۆلۈم (يەنى دۈشمەن) بىلەن ئۇچرىشىشتىن ئىلگىرى ئۇنى ئارزۇ قىلاتتىڭلار، مانا ئۇنى ئۆز كۆزۈڭلار بىلەن كۆردۈڭلار (ئەمدى نېمىشقا مەغلۇپ بولىسىلەر؟)'),
(437, 3, 144, 'مۇھەممەد پەقەت بىر پەيغەمبەردۇر، ئۇنىڭدىن بۇرۇن كۆپ پەيغەمبەرلەر ئۆتتى (ئۇلارنىڭ بەزىلىرى ئۆز ئەجىلى بىلەن ئۆلگەن، بەزىلىرى باشقىلار تەرىپىدىن ئۆلتۈرۈلگەن)؛ ئۇ ۋاپات بولسا ياكى ئۆلتۈرۈلسە، ئارقاڭلارغا يېنىۋالامسىلەر؟ (يەنى ئىماندىن يېنىپ مۇرتەد بولامسىلەر؟) كىمكى ئارقىسىغا يېنىۋالىدىكەن (يەنى مۇرتەد بولىدىكەن)، ئۇ اﷲ قا قىلچە زىيان يەتكۈزەلمەيدۇ. اﷲ شۈكۈر قىلغۇچىلارنى مۇكاپاتلايدۇ'),
(438, 3, 145, 'ھېچ كىشى اﷲ نىڭ ئىزنىسىز ئۆلمەيدۇ، اﷲ ھەر ئادەمنىڭ ئەجىلىنى پۈتۈۋەتكەن (ئادەمنىڭ ئۆمرىنى قورقۇنچاقلىق ئۇزارتالمايدۇ، باتۇرلۇق قىسقارتالمايدۇ). كىمكى دۇنيا ساۋابىنى كۆزلىسە، ئۇنىڭغا دۇنيانىڭ ساۋابىنى بېرىمىز (ئۇنىڭ ئاخىرەتلىكتىن نېسىۋىسى بولمايدۇ)؛ كىمكى ئاخىرەتنىڭ ساۋابىنى كۆزلىسە، ئۇنىڭغا ئاخىرەتنىڭ ساۋابىنى بېرىمىز، شۈكۈر قىلغۇچىلارنى مۇكاپاتلايمىز'),
(439, 3, 146, 'نۇرغۇن پەيغەمبەرلەر بىلەن كۆپلىگەن خۇدا جۇي ئۆلىمالار بىرلىكتە جەڭ قىلدى. ئۇلار اﷲ يولىدا يەتكەن كۈلپەتلەردىن روھسىزلانمىدى، بوشاشمىدى، باش ئەگمىدى، اﷲ ئۆزىنىڭ يولىدا دۇچ كەلگەن قىيىنچىلىقلارغا چىداشلىق بەرگۈچىلەرنى دوست تۇتىدۇ'),
(440, 3, 147, 'ئۇلارنىڭ سۆزى پەقەت: «پەرۋەردىگارىمىز! گۇناھلىرىمىزنى، ئىشىمىزدا چەكتىن ئاشقانلىقىمىزنى مەغپىرەت قىلغىن، قەدەملىرىمىزنى (جەڭ مەيدانىدا) مۇستەھكەم قىلغىن ۋە كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دېگەندىن باشقا بولمىدى'),
(441, 3, 148, 'اﷲ ئۇلارغا دۇنيانىڭ ساۋابىنى (يەنى دۈشمەنلەر ئۈستىدىن غەلىبە قىلىشنى) ۋە ئاخىرەتنىڭ ياخشى ساۋابىنى (يەنى جەننەت ۋە ئۇنىڭ نېمەتلىرىنى) ئاتا قىلدى، اﷲ ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ'),
(442, 3, 149, 'ئى مۆمىنلەر! ئەگەر سىلەر كاپىرلارغا ئىتائەت قىلساڭلار، ئۇلار سىلەرنى ئارقاڭلارغا (يەنى كۇفرىغا) ياندۇرىدۇ، شۇنىڭ بىلەن زىيان تارتىپ قالىسىلەر'),
(443, 3, 150, 'ئۇنداق ئەمەس (يەنى ئۇلار سىلەرنىڭ ئىتائەت قىلىدىغان مەدەتكارىڭلار ئەمەس)، اﷲ سىلەرنىڭ مەدەتكارىڭلاردۇر (ئۇنىڭغا ئىتائەت قىلىڭلار)، ئۇ ياردەم قىلغۇچىلارنىڭ ئەڭ ياخشىسىدۇر'),
(444, 3, 151, '(ئىلاھلىقى) ھەققىدە ھېچبىر دەلىل چۈشۈرمىگەن نەرسىلەرنى (يەنى بۇتلارنى) اﷲ قا شېرىك قىلغانلىقلىرى ئۈچۈن، كاپىرلارنىڭ دىللىرىغا قورقۇنچ سالىمىز، ئۇلارنىڭ بارىدىغان جايى دوزاختۇر، زالىملارنىڭ جايى (يەنى دوزاخ) ھەقىقەتەن يامان جايدۇر'),
(445, 3, 152, 'اﷲ سىلەرگە قىلغان (دۈشمىنىڭلارغا قارشى نۇسرەت ئاتا قىلىشتىن ئىبارەت) ۋەدىسىدە تۇردى؛ اﷲ نىڭ ئىزنى بىلەن ئۇلارنى قىرىۋاتاتتىڭلار، اﷲ سىلەر ياقتۇرىدىغان غەلىبىنى كۆرسەتكەندىن كېيىن زەئىپلىشىپ قالدىڭلار، پەيغەمبەرنىڭ ئەمرى توغرىسىدا جاڭجاللاشتىڭلار، (پەيغەمبەرنىڭ ئەمرىگە) بويسۇنمىدىڭلار، بەزىلىرىڭلار دۇنيانى كۆزلىدىڭلار، بەزىلىرىڭلار ئاخىرەتنى كۆزلىدىڭلار، ئاندىن اﷲ سىناش ئۈچۈن سىلەردىن دۈشمەننى قايتۇردى (يەنى سىلەرنى غەلىبىدىن مەغلۇبىيەتكە يۈزلەندۈردى) ۋە سىلەرنى ھەقىقەتەن ئەپۇ قىلدى. اﷲ مۆمىنلەرگە مەرھەمەت قىلغۇچىدۇر'),
(446, 3, 153, 'ئۆز ۋاقتىدا پەيغەمبەر (ئى اﷲ نىڭ بەندىلىرى! مېنىڭ تەرىپىمگە كېلىڭلار، مەن رەسۇلۇللاھتۇرمەن، كىمكى جىھاد مەيدانىغا قايتسا، ئۇ جەننەتكە كىرىدۇ دەپ) ئارقاڭلاردىن سىلەرنى چاقىرىپ تۇراتتى، سىلەر ھېچ كىشىگە قارىماي قاچاتتىڭلار، (بۇنىڭدىن كېيىن) قولۇڭلاردىن كەتكەن نەرسىگە (يەنى غەنىيمەتكە)، بېشىڭلارغا كەلگەن مۇسىبەتكە (يەنى مەغلۇبىيەتكە) قايغۇرماسلىقىڭلار ئۈچۈن، اﷲ سىلەرگە غەم ئۈستىگە غەم بەردى (يەنى سىلەرنى غەنىيمەتتىن مەھرۇم قىلغاننىڭ ئۈستىگە مەغلۇبىيەت بىلەن جازالىدى). اﷲ قىلمىشىڭلاردىن خەۋەرداردۇر'),
(447, 3, 154, 'غەمدىن كېيىن اﷲ سىلەرگە ئەمىنلىك چۈشۈرۈپ بەردى، سىلەرنىڭ بىر قىسمىڭلارنى (يەنى ھەقىقىي مۆمىنلەرنى) مۈگدەك باستى، يەنە بىر قىسمىڭلار (يەنى مۇناپىقلار) ئۆزلىرى بىلەن بولۇپ كەتتى (يەنى كۇففارلار قايتا ھۇجۇم قىلارمۇ، دەپ جان قايغۇسى بىلەن بولۇپ كەتتى)، بۇلار، جاھىلىيەت دەۋرى كىشىلىرىگە ئوخشاش، اﷲ قا قارىتا ناتوغرا گۇمانلاردا بولدى، ئۇلار: «بىزگە اﷲ ۋەدە قىلغان غەلىبىدىن نېسىۋە بارمۇ؟» دەيدۇ. ئېيتىقىنكى، «ھەممە ئىش ھەقىقەتەن اﷲ نىڭ ئىلكىدە». ئۇلار ساڭا ئاشكارا قىلمايدىغان نەرسىلەرنى ئۆز ئىچىدە يوشۇرۇشىدۇ؛ ئۇلار: «ئەگەر بىزگە غەلىبىدىن ئازراق نېسىۋە بولسا ئىدى، بۇ يەردە ئۆلتۈرەلمەيتتۇق» دەيدۇ. (ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر سىلەر ئۆيلىرىڭلاردا بولغان تەقدىردىمۇ (ئاراڭلاردىكى) ئۆلتۈرۈلۈشى پۈتۈۋېتىلگەن كىشىلەر چوقۇم ئۆزلىرىنىڭ ئۆلىدىغان جايلىرىغا بېرىشىدۇ (اﷲ نىڭ قازاسىدىن قېچىپ قۇتۇلغىلى بولمايدۇ). (اﷲ نىڭ مۇنداق قىلىشى) كۆڭلۈڭلاردىكىنى سىناش ۋە قەلبىڭلاردىكى نەرسىلەرنى پاكلاش ئۈچۈندۇر. اﷲ كۆڭۈللەردىكىنى (ياخشىلىق بولسۇن، يامانلىق بولسۇن) بىلگۈچىدۇر»'),
(448, 3, 155, 'ئىككى قوشۇن (يەنى مۇسۇلمانلار بىلەن مۇشرىكلار) بىر - بىرى بىلەن ئۇچراشقان كۈندە، ئىچىڭلاردىكى (جەڭدىن) ھەقىقەتەن يۈز ئۆرۈگەن كىشىلەرنى، بەزى قىلمىشلىرى (يەنى پەيغەمبەرنىڭ ئەمرىگە مۇخالىپەتچىلىك قىلغانلىقى) تۈپەيلىدىن، شەيتان تېيىلدۇرۇشقا ئورۇندى؛ اﷲ ھەقىقەتەن ئۇلارنى ئەپۇ قىلدى. اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)'),
(449, 3, 156, 'ئى مۆمىنلەر! سەپەرگە چىققان ياكى غازاتقا چىققان قېرىنداشلىرى ھەققىدە، ئۇلار يېنىمىزدا بولغاندا ئۆلمەيتتى ۋە ئۆلتۈرۈلمەيتتى، دېگەن كاپىرلارغا ئوخشاش بولماڭلار، اﷲ بۇنى ئۇلارنىڭ دىللىرىدا ھەسرەت قىلسۇن. اﷲ تىرىلدۈرىدۇ ۋە ئۆلتۈرىدۇ (ئۇلارنىڭ غازاتقا چىقماسلىقى ئۇلارنى ئۆلۈمدىن قۇتۇلدۇرالمايدۇ). اﷲ قىلمىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(450, 3, 157, 'شۈبھىسىزكى، ئەگەر سىلەر اﷲ يولىدا (يەنى غازاتتا) ئۆلتۈرۈلسەڭلار ياكى (غازاتقا كېتىپ بېرىپ ئۆز ئەجىلىڭلار بىلەن) ئۆلسەڭلار، سىلەرگە اﷲ تەرىپىدىن بولغان مەغپىرەت ۋە رەھمەت، ئەلۋەتتە، ئۇلارنىڭ توپلايدىغان دۇنياسىدىن ئارتۇقتۇر'),
(451, 3, 158, 'ئەگەر سىلەر (ئۆز ئەجىلىڭلار بىلەن) ئۆلسەڭلار ياكى (جەڭ مەيدانىدا) ئۆلتۈرۈلسەڭلار، (ئاخىرەتتە) چوقۇم اﷲ نىڭ دەرگاھىغا توپلىنىسىلەر (اﷲ سىلەرنى ئەمەلىڭلارغا قاراپ مۇكاپاتلايدۇ)'),
(452, 3, 159, 'اﷲ نىڭ رەھمىتى بىلەن سەن ئۇلارغا مۇلايىم بولدۇڭ؛ ئەگەر قوپال، باغرى قاتتىق بولغان بولساڭ، ئۇلار چۆرەڭدىن تارقاپ كېتەتتى؛ ئۇلارنى ئەپۇ قىل، ئۇلار ئۈچۈن مەغپىرەت تىلىگىن، ئىشتا ئۇلار بىلەن كېڭەشكىن؛ (كېڭەشكەندىن كېيىن) بىر ئىشقا بەل باغلىساڭ، اﷲ قا تەۋەككۈل قىلغىن. اﷲ ھەقىقەتەن تەۋەككۈل قىلغۇچىلارنى دوست تۇتىدۇ'),
(453, 3, 160, 'ئەگەر اﷲ سىلەرگە ياردەم بەرسە، سىلەرنى ھېچ كىشى يېڭەلمەيدۇ. ئەگەر اﷲ سىلەرگە ياردىمىنى تەرك ئەتسە، اﷲ تىن باشقا سىلەرگە كىم ياردەم بېرىدۇ؟ مۆمىنلەر يالغۇز اﷲ قا تەۋەككۈل قىلسۇن! (يەنى ھەممە ئىشنى اﷲ قا تاپشۇرسۇن)'),
(454, 3, 161, 'ھېچقانداق بىر پەيغەمبەر غەنىيمەتكە خىيانەت قىلمايدۇ (يەنى بۇنى تەسەۋۋۇر قىلغىلى بولمايدۇ). كىمكى غەنىيمەتكە خىيانەت قىلىدىكەن، قىيامەت كۈنى ئۇ خىيانەت قىلغان نەرسىسى بىلەن (يەنى خىيانەت قىلغان نەرسىسى گەجگىسىگە ئارتىلغان ھالدا) كېلىدۇ. ھەر كىشنىڭ قىلغان ئەمەلىگە تولۇق مۇكاپات، (ياكى) تولۇق جازا بېرىلىدۇ، ئۇلارغا (ياخشىلارنىڭ ساۋابىنى كېمەيتىۋېتىپ، ئاسىيلارنىڭ جازاسىنى ئاشۇرۇۋېتىپ) زۇلۇم قىلىنمايدۇ'),
(455, 3, 162, 'اﷲ نىڭ رازىلىقىنى ئىزدىگەن كىشى اﷲ نىڭ غەزىپىگە ئۇچراشقا تېگىشلىك بولغان كىشىگە ئوخشامدۇ؟ اﷲ نىڭ غەزىپىگە ئۇچراشقا تېگىشلىك بولغان كىشىنىڭ بارىدىغان جايى جەھەننەمدۇر، جەھەننەم نېمىدېگەن يامان جاي!'),
(456, 3, 163, 'اﷲ نىڭ دەرگاھىدا ئۇلارنىڭ دەرىجىسى تۈرلۈك بولىدۇ. اﷲ ئۇلارنىڭ قىلمىشلىرىنى كۆرۈپ تۇرغۇچىدۇر'),
(457, 3, 164, 'اﷲ مۆمىنلەرگە اﷲ نىڭ ئايەتلىرىنى تىلاۋەت قىلىدىغان، ئۇلارنى (گۇناھلاردىن) پاك قىلىدىغان، ئۇلارغا كىتابنى ۋە ھېكمەتنى (يەنى قۇرئان ۋە سۈننەتنى) ئۆگىتىدىغان، ئۆزلىرىدىن بولغان بىر پەيغەمبەر ئەۋەتىپ، ئۇلارغا چوڭ ئېھسان قىلدى؛ ھالبۇكى، ئۇلار بۇرۇن ئوچۇق گۇمراھلىقتا ئىدى'),
(458, 3, 165, 'سىلەرگە يەتكەن مۇسىبەت سىلەر دۈشمەنگە يەتكۈزگەن مۇسىبەتنىڭ يېرىمىغا تەڭ كېلىدىغان تۇرسا (يەنى ئۇھۇد غازىتىدا سىلەردىن 70 كىشى شېھىت بولغان تۇرسا، بەدرى غازىتىدا بولسا مۇشرىكلاردىن 70 كىشىنى ئۆلتۈرگەن، 70 كىشىنى ئەسىر ئالغان تۇرساڭلار)، سىلەر يەنە: «بۇ مۇسىبەت قەيەردىن كەلدى؟» دېدىڭلار. ئېيتقىنكى، «ئۇ سىلەرنىڭ ئۆزلىرىڭلاردىن (يەنى پەيغەمبەرنىڭ ئەمرىگە خىلاپلىق قىلغانلىقىڭلار ۋە غەنمەتكە ھېرىس بولغانلىقىڭلاردىن) بولدى». اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر'),
(459, 3, 166, 'ئىككى قوشۇن ئۇچراشقان كۈندە (يەنى مۇسۇلمانلار بىلەن مۇشرىكلار توقۇنۇشقان ئۇھۇد جېڭىدە) سىلەرگە كەلگەن مۇسىبەت اﷲ نىڭ ئىرادىسى بىلەن كەلگەندۇر، بۇ ھەقىقىي مۆمىنلەرنى بىلىش (يەنى ئايرىش) ئۈچۈندۇر'),
(460, 3, 167, 'ۋە مۇناپىقلارنى بىلىش (يەنى ئايرىش) ئۈچۈندۇركى، ئۇلارغا: «كېلىڭلار، اﷲ يولىدا ئۇرۇش قىلىڭلار ياكى (ئۆزۈڭلارنى) قوغداڭلار» دېيىلسە، ئۇلار: «ئۇرۇشتىن خەۋەر تاپقان بولساق، ئەلۋەتتە، سىلەرگە ئەگىشەتتۇق (يەنى سىلەر بىلەن بىر سەپتە تۇرۇپ ئۇرۇش قىلاتتۇق)» دېدى. ئۇ كۈندە ئۇلار ئىمانغا قارىغاندا كۇفرىغا يېقىن ئىدى. ئۇلار كۆڭۈللىرىدە يوق نەرسىلەرنى ئېغىزلىرىدا دېدى، ئۇلار يوشۇررغاننى (يەنى نىفاق بىلەن مۇشرىكلىكنى) اﷲ ئوبدان بىلىدۇ'),
(461, 3, 168, '(بۇ يەنە اﷲ نىڭ) ئۇرۇشقا چىقىشنى خالىمىغان (مۇناپىقلارنى ئايرىشى ئۈچۈندۇركى، ئۇلار ئۆيلىرىدە) ئولتۇرۇپ قېرىنداشلىرىغا: «ئەگەر ئۇلار (يەنى مۆمىنلەر) بىزگە بويسۇنغان بولسا (يەنى نەسىھىتىمىزنى ئاڭلاپ ئۇرۇشتىن قايتقان بولسا)، ئۆلتۈرۈلمەيتتى» دېدى. (ئى مۇھەممەد! بۇ مۇناپىقلارغا) ئېيتقىنكى، «(ئەگەر ئۇرۇشقا چىقماسلىق ئۆلۈمدىن قۇتۇلدۇرىدىغان بولسا) ئۆلۈمنى ئۆزۈڭلاردىن دەپئى قىلىپ بېقىڭلار، ئەگەر (دەۋايىڭلاردا) راستچىل بولساڭلار»'),
(462, 3, 169, 'اﷲ نىڭ يولىدا شېھىت بولغانلارنى ئۆلۈك دەپ گۇمان قىلمىغىن، بەلكى ئۇلار تىرىك بولۇپ، اﷲ نىڭ دەرگاھىدىكى رىزىقتىن بەھرىمەن قىلىنىدۇ (يەنى جەننەتنىڭ نېمەتلىرىدىن ئەتىگەن - ئاخشامدا مەڭگۈلۈك رىزىقلاندۇرۇپ تۇرۇلىدۇ)'),
(463, 3, 170, 'ئۇلار اﷲ نىڭ ئۆزلىرىگە بەرگەن پەزلىدىن خۇرسەندۇر، ئۆزلىرىنىڭ ئارقىسىدىن تېخى يېتىپ كەلمىگەن (يەنى شېھىت بولماي تىرىك قالغان) قېرىنداشلىرىغا (ئاخىرەتتە) نە قورقۇنچ، (دۇنيادىن ئايرىلغانلىقىغا) نە قايغۇ يوق ئىكەنلىكى بىلەن خۇش خەۋەر بېرىشنى تىلەيدۇ'),
(464, 3, 171, 'ئۇلار اﷲ تەرىپىدىن بېرىلگەن نېمەت ۋە پەزلنى، مۆمىنلەرنىڭ ئەجرىنى اﷲ نىڭ بىكار قىلىۋەتمەيدىغانلىقى بىلەن خۇش خەۋەر بېرىشنى تىلەيدۇ'),
(465, 3, 172, '(ئۇھۇد غازىتىدا) يارىلانغاندىن كېيىن اﷲ نىڭ ۋە پەيغەمبەرنىڭ چاقىرىقىغا ئاۋاز قوشقانلار، ئۇلار (يەنى مۆمىنلەر) دىن ياخشى ئەمەللەرنى قىلغانلار ۋە تەقۋادارلىق قىلغانلار چوڭ ساۋابقا ئېرىشىدۇ'),
(466, 3, 173, '(مۇشرىكلار تەرەپدارى بولغان) ئادەملەر ئۇلارغا: «شەك - شۈبھىسىزكى، كىشىلەر (يەنى قۇرەيشلەر) سىلەرگە قارشى قوشۇن توپلىدى، ئۇلاردىن قورقۇڭلار» دېدى. بۇ سۆز ئۇلارنىڭ ئىمانىنى كۈچەيتتى. ئۇلار: «بىزگە اﷲ كۇپايە، اﷲ نېمىدېگەن ياخشى ھامىي!» دېدى'),
(467, 3, 174, 'ئۇلار اﷲ نىڭ نېمىتى ۋە پەزلى بىلەن قايتىپ كەلدى، ھېچقانداق زىيان - زەخمەتكە ئۇچرىمىدى، ئۇلار اﷲ نىڭ رازىلىقىنى ئىزدىدى. اﷲ ئۇلۇغ پەزل ئىگىسىدۇر'),
(468, 3, 175, 'ئەنە شۇ شەيتان ئۆز دوستلىرىنى (يەنى كۇففارلارنى) قورقىتىدۇ. ئەگەر مۆمىن بولساڭلار، ئۇلاردىن قورقماڭلار، مەندىن قورقۇڭلار'),
(469, 3, 176, '(ئى مۇھەممەد!) كۇفرىغا ئۆزلىرىنى ئاتىغانلار (يەنى سۆزلىرى ۋە ھەرىكەتلىرى بىلەن كۇفرىغا شاپاشلايدىغان مۇناپىقلار) سېنى غەمكىن قىلمىسۇن، ئۇلار ھەقىقەتەن (كۇفرى بىلەن) اﷲ قا قىلچە زىيان يەتكۈزەلمەيدۇ. اﷲ ئاخىرەتتە ئۇلارغا (ساۋابتىن) ھېچبىر نېسىۋە بەرمەسلىكنى خالايدۇ؛ ئۇلار چوڭ ئازابقا دۇچار بولىدۇ'),
(470, 3, 177, 'ئىماننى كۇفرىغا تېگىشكەنلەر (يەنى مەزكۇر مۇناپىقلار) (كۇفرى بىلەن) اﷲ قا قىلچە زىيان يەتكۈزەلمەيدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(471, 3, 178, 'كاپىرلار ئۆزلىرىگە (جازا بەرمەستىن) مۆھلەت بېرىشىمىزنى (يەنى ئۆمرىنى ئۇزۇن قىلىشىمىزنى) ھەرگىز ئۆزلىرى ئۈچۈن پايدىلىق دەپ گۇمان قىلمىسۇن، ئۇلارغا مۆھلەت بېرىشىمىز (ۋە ئەجىلىنى كېچىكتۈرۈشىمىز) پەقەت ئۇلارنىڭ گۇناھىنىڭ كۆپىيىشى ئۈچۈندۇر. ئۇلار (ئاخىرەتتە) خار قىلغۇچى ئازابقا دۇچار بولىدۇ'),
(472, 3, 179, 'اﷲ ياماننى (يەنى مۇناپىقنى) ياخشىدىن (يەنى مۆمىندىن) ئايرىمىغۇچە مۆمىنلەرنى (يەنى سىلەرنى) ھازىرقى پېتىڭلارچە قويمايدۇ. اﷲ سىلەرنى غەيبتىنمۇ خەۋەردار قىلمايدۇ ۋە لېكىن اﷲ پەيغەمبەرلىرىدىن خالىغىنىنى تاللاپ، ئۇنىڭغا (مۇھەممەد ئەلەيھىسسالامغا مۇناپىقلارنىڭ ھالىنى بىلدۈرگەندەك) غەيبنى بىلدۈرىدۇ؛ اﷲ قا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتىڭلار، ئەگەر سىلەر ئىمان ئېيتساڭلار ۋە (پەرۋەردىگارىڭلاردىن) قورقساڭلار، چوڭ ساۋابقا ئېرىشىسىلەر'),
(473, 3, 180, 'اﷲ ئۆز پەزلىدىن بەرگەن نەرسىلەرگە (يەنى پۇل - مالغا) بېخىللىق قىلىدىغانلار بېخىللىقنى ئۆزلىرى ئۈچۈن پايدىلىق دەپ گۇمان قىلمىسۇن، ئەمەلدە بۇ ئۇلار ئۈچۈن زىيانلىقتۇر؛ ئۇلارنىڭ بېخىللىق قىلغان نەرسىسى قىيامەت كۈنى ئۇلارنىڭ بوينىغا تاقاق قىلىپ سېلىنىدۇ. ئاسمانلارنىڭ ۋە يەرنىڭ مىراسى اﷲ نىڭدۇر (يەنى كائىناتتىكى ھەممە نەرسە اﷲ نىڭ مۈلكى بولۇپ، ئۇلار پانىي بولغاندىن كېيىن اﷲ نىڭ دەرگاھىغا قايتىدۇ). اﷲ قىلغان ئەمەللىرىڭلاردىن خەۋەرداردۇر'),
(474, 3, 181, '«اﷲ بولسا ھەقىقەتەن پېقىر، بىز بولساق باي» دېگەن كىشىلەر (يەنى يەھۇدىيلار) نىڭ سۆزىنى اﷲ ھەقىقەتەن ئاڭلىدى، ئۇلارنىڭ سۆزلىرىنى ۋە پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرىنى خاتىرىلەپ قويىمىز (يەنى نامە - ئەمالىغا مۇئەككەل پەرىشتىلەرگە بۇنى ئۇلارنىڭ نامە - ئەمالىغا يېزىشنى بۇيرۇيمىز) ھەمدە ئاخىرەتتە پەرىشتىلەر تىلى ئارقىلىق، كۆيدۈرگۈچى ئازابنى تېتىڭلار، دەيمىز'),
(475, 3, 182, 'بۇ سىلەرنىڭ قىلمىشىڭلار تۈپەيلىدىندۇر، اﷲ بەندىلىرىگە زۇلۇم قىلغۇچى ئەمەستۇر'),
(476, 3, 183, 'ئۇلار: «(تەۋراتتا) اﷲ بىزنى (ئاسماندىن چۈشكەن) ئوت يەپ كېتىدىغان بىر قۇربانلىق كەلتۈرمىگىچە ھەقىقەتەن ھېچبىر پەيغەمبەرگە ئىمان كەلتۈرمەسلىككە بۇيرۇدى» دېدى (بۇ ئىپتىرا بولۇپ، اﷲ ئۇلارنى بۇيرۇغان ئەمەس). (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرگە مۆجىزىلەر ۋە سىلەر ئېيتقان مۆجىزە بىلەن ھەقىقەتەن نۇرغۇن پەيغەمبەر كەلدى؛ ئەگەر سىلەر (دەۋايىڭلاردا) راستچىل بولساڭلار، ئۇلارنى نېمىشقا ئۆلتۈردۈڭلار؟»'),
(477, 3, 184, '(ئى مۇھەممەد!) ئۇلار سېنى يالغانغا چىقارغان بولسا (قايغۇرۇپ كەتمىگىن)، سەندىن ئىلگىرى ئۆتكەن، مۆجىزىلەرنى، كىتابلارنى (يەنى ئىبراھىم ئەلەيھىسسالامغا نازىل قىلىنغان سەھىپىلەرگە ئوخشاش ساماۋى كىتابلارنى) ۋە نۇرلۇق كىتابنى (يەنى تەۋرات، ئىنجىلغا ئوخشاشلارنى) ئېلىپ كەلگەن نۇرغۇن پەيغەمبەرلەرمۇ يالغانغا چىقىرىلغان ئىدى'),
(478, 3, 185, 'ھەر بىرجان ئىگىسى ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر. سىلەرنىڭ ئەجرىڭلار (يەنى ئەمەللىرىڭلارنىڭ ساۋابى) قىيامەت كۈنى تولۇق بېرىلىدۇ، كىمكى دوزاختىن يىراق قىلىنغان ۋە جەننەتكە كىرگۈزۈلگەن ئىكەن، ئۇ مۇرادىغا يەتكەن بولىدۇ. دۇنيا تىرىكچىلىكى پەقەت ئالدايدىغان مالدىنلا ئىبارەتتۇر (يەنى ئالدايدىغان نەرسە بولۇپ، ئازغىنا بەھرىمەن بولغاندىن كېيىن تۈگەيدۇ)'),
(479, 3, 186, 'سىلەر ماللىرىڭلاردا (سەدىقىغە بۇيرۇلۇش ۋە ئاپەت يېتىشى بىلەن) ۋە جانلىرىڭلاردا (قەتل قىلىنىش، ئەسىر ئېلىنىش، كېسەل بولۇش بىلەن) چوقۇم سىنىلىسىلەر، سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن، مۇشرىكلاردىن چوقۇم نۇرغۇن يامان سۆز ئاڭلايسىلەر، ئەگەر (يۇقىرىقى ئەھۋاللار يۈز بەرگەندە) سەۋر قىلساڭلار (سۆزۈڭلار ۋە ھەرىكىتىڭلاردا اﷲ تىن) قورقساڭلار، ئۇنداقتا بۇ ھەقىقەتەن ئىرادە بىلەن قىلىنىشقا تېگىشلىك ئىشلاردىندۇر'),
(480, 3, 187, 'ئۆز ۋاقتىدا، اﷲ كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار) دىن كىتابنى (يەنى كىتابتىكى اﷲ نىڭ ئەھكاملىرىنى) كىشىلەرگە چوقۇم بايان قىلىپ بېرىشكە ۋە يوشۇرماسلىققا ئەھدە ئالدى، لېكىن بۇنى (يەنى ئەھدىنى) ئۇلار ئارقىسىغا چۆرۈۋەتتى ۋە ئۇنى ئازغىنا بەدەلگە (يەنى دۇنيانىڭ ئەرزىمەس نەرسىسىگە) تېگىشتى. ئۇلارنىڭ تېگىشىپ ئالغىنى نېمىدېگەن يامان!'),
(481, 3, 188, '(ئى مۇھەممەد!) قىلغان ئىشلىرىدىن (يەنى ھەقنى ياپقانلىقىدىن) خۇشال بولىدىغان، قىلمىغان ئىشلىرى بىلەن مەدھىيىلىنىشنى ياخشى كۆرىدىغان كىشىلەرنى ھەرگىز اﷲ نىڭ ئازابىدىن قۇتۇلىدۇ دەپ گۇمان قىلمىغىن، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(482, 3, 189, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ ھەممە نەرسىگە قادىردۇر'),
(483, 3, 190, 'شۈبھىسىزكى، ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىدا، كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا ئەقىل ئىگىلىرى ئۈچۈن، ئەلۋەتتە، روشەن دەلىللەر بار'),
(484, 3, 191, 'ئۇلار ئۆرە تۇرغاندىمۇ، ئولتۇرغاندىمۇ، ياتقاندىمۇ اﷲ نى ئەسلەپ تۇرىدۇ، ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشى توغرىسىدا پىكىر يۈرگۈزىدۇ. (ئۇلار ئېيتىدۇ) «پەرۋەردىگارىمىز! بۇنى بىكار ياراتمىدىڭ، سەن پاكتۇرسەن، بىزنى دوزاخ ئازابىدىن ساقلىغىن'),
(485, 3, 192, 'پەرۋەردىگارىمىز! سەن كىمنىكى دوزاخقا كىرگۈزىدىكەنسەن، ئۇنى ئەلۋەتتە خار قىلغان بولىسەن. زالىملارغا ھېچقانداق ياردەمچى بولمايدۇ'),
(486, 3, 193, 'پەرۋەردىگارىمىز! بىز ھەقىقەتەن بىر چاقىرغۇچىنىڭ (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ) \'رەببىڭلارغا ئىمان ئېيتىڭلار\' دەپ ئىمانغا چاقىرغانلىقىنى ئاڭلىدۇق، ئىمان ئېيتتۇق، پەرۋەردىگارىمىز! بىزنىڭ گۇناھلىرىمىزنى مەغپىرەت قىلغىن، يامانلىقلىرىمىزنى يوققا چىقارغىن، بىزنى ياخشىلارنىڭ قاتارىدا قەبزى روھ قىلغىن'),
(487, 3, 194, 'پەرۋەردىگارىمىز! بىزگە پەيغەمبەرلىرىڭ ئارقىلىق ۋەدە قىلغان نەرسىنى (يەنى ئىتائەت قىلغانلارغا خاس بولغان جەننەتنى) بەرگىن، قىيامەت كۈنى بىزنى رەسۋا قىلمىغىن. سەن ھەقىقەتەن ۋەدەڭگە خىلاپلىق قىلمايسەن»'),
(488, 3, 195, 'ئۇلارنىڭ دۇئاسىنى پەرۋەردىگارى ئىجابەت قىلدى: «مەن سىلەردىن ئەر بولسۇن، ئايال بولسۇن، ھەر قانداق بىر ياخشى ئىش قىلغۇچىنىڭ قىلغان ئەمەلىنى بىكار قىلىۋەتمەيمەن، سىلەر بىر - بىرىڭلاردىن تۆرەلگەن. ھىجرەت قىلغانلار، يۇرتلىرىدىن ھەيدەپ چىقىرىلغانلار، مېنىڭ يولۇمدا (يەنى اﷲ نىڭ دىنى ئۈچۈن) ئەزىيەت تارتقانلار، ئۇرۇشقا قاتناشقانلار، (يەنى مېنىڭ يولۇمدا ئۇرۇشقانلار) ۋە مېنىڭ يولۇمدا ئۆلتۈرۈلگەنلەرنىڭ گۇناھلىرىنى (مەغپىرىتىم ۋە رەھمىتىم بىلەن) ئەلۋەتتە، ئۇلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمەن». بۇ (ئۇلارنىڭ ياخشى ئەمەللىرى ئۈچۈن) اﷲ تەرىپىدىن بېرىلگەن مۇكاپاتتۇر. اﷲ نىڭ دەرگاھىدا ياخشى مۇكاپات (يەنى جەننەت) بار'),
(489, 3, 196, 'كاپىرلارنىڭ (پۇل - مال، ئابرۇي، مەرتىۋە ئىزدەش يۈزىسىدىن) شەھەرلەردە كېزىپ يۈرۈشلىرى سېنى ئالدىمىسۇن'),
(490, 3, 197, 'بۇ ئازغىنا بەھرىمەن بولۇشتۇر، كەلگۈسىدە ئۇلارنىڭ جايى جەھەننەم بولىدۇ. ئۇ نېمىدېگەن يامان تۆشەك!'),
(491, 3, 198, 'پەرۋەردىگارىغا تەقۋادارلىق قىلغان كىشىلەر ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردىن بەھرىمەن بولۇپ، ئۇ يەردە مەڭگۈ قالىدۇ. بۇ، اﷲ تەرىپىدىن بولغان كۈتۈۋېلىشتۇر. اﷲ نىڭ دەرگاھىدىكى نەرسىلەر (يەنى مۇكاپات ۋە ھۆرمەت) ياخشى بەندىلەر ئۈچۈن (كۇففارلار بەھرىمەن بولغان باقاسى يوق ئازغىنا راھەتتىن) ئارتۇقتۇر'),
(492, 3, 199, 'ئەھلى كىتاب ئارىسىدا ھەقىقەتەن اﷲ قا ئىمان كەلتۈرىدىغان، سىلەرگە نازىل بولغان كىتابقا (يەنى قۇرئانغا)، ئۆزلىرىگە نازىل بولغان كىتابقا (يەنى تەۋراتقا ۋە ئىنجىلغا) ئىشىنىدىغانلار بار؛ ئۇلار اﷲ تىن قورقىدۇ، اﷲ نىڭ ئايەتلىرىنى ئازغىنا بەدەلگە تېگىشمەيدۇ (يەنى ئۇلارنىڭ كىتابلىرىدىكى مۇھەممەد ئەلەيھىسسالامنىڭ سۈپەتلىرى ۋە شەرىئەت ئەھكاملىرىنى، راھىبلارغا ئوخشاش، دۇنيانىڭ ئەرزىمەس نەرسىلىرىنى دەپ ئۆزگەرتىۋەتمەيدۇ)، ئۇلارغا پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب بېرىلىدۇ. اﷲ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر'),
(493, 3, 200, 'ئى مۆمىنلەر! تائەت - ئىبادەتنىڭ مۇشەققەتلىرىگە ۋە سىلەرگە يەتكەن ئېغىرچىلىقلارغا سەۋر قىلىڭلار، دۈشمەنلەرگە زىيادە چىداملىق بولۇڭلار، (چېگرالىرىڭلارنى ساقلاپ) جىھادقا تەييار تۇرۇڭلار، مەقسىتىڭلارغا يېتىش ئۈچۈن اﷲ تىن قورقۇڭلار (يەنى ئۇنىڭ ئەمرىگە مۇخالىپەتچىلىك قىلماڭلار)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'ئى ئىنسانلار! سىلەرنى بىر ئىنساندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتقان، شۇ ئىنساندىن (يەنى ئۆز جىنسىدىن) ئۇنىڭ جۈپتىنى (يەنى ھەۋۋانى) ياراتقان ۋە ئۇلاردىن (يەنى ئادەم بىلەن ھەۋۋادىن) نۇرغۇن ئەر - ئاياللارنى ياراتقان پەرۋەردىگارىڭلاردىن قورقۇڭلار، بىر - بىرىڭلاردىن نەرسە سورىغاندا نامى بىلەن سورايدىغان اﷲ تىن قورقۇڭلار، سىلە - رەھىمنى ئۈزۈپ قويۇشتىن ساقلىنىڭلار. اﷲ ھەقىقەتەن سىلەرنى (يەنى پۈتۈن ئەھۋالىڭلارنى) كۆزىتىپ تۇرغۇچىدۇر'),
(495, 4, 2, 'يېتىملەرنىڭ مال - مۈلكىنى (ئۇلار بالاغەتكە يەتكەن چاغدا) بېرىڭلار، ياخشىسىنى يامانغا (يەنى ئۇلارنىڭ ماللىرىنىڭ ياخشىسىنى ئۆزۈڭلارنىڭ ماللىرىڭلارنىڭ يامىنىغا) تېگىشىۋالماڭلار؛ ئۇلارنىڭ مال - مۈلكىنى ئۆزۈڭلارنىڭ مال - مۈلكىگە قوشۇپ يەۋالماڭلار. بۇ ھەقىقەتەن چوڭ گۇناھتۇر'),
(496, 4, 3, 'ئەگەر يېتىم قىزلارغا (ئۆيلىنىپ) ئادىل مۇئامىلىدە بولالماسلىقىڭلاردىن (يەنى تەربىيەڭلاردا بىرەر يېتىم قىز بولۇپ، ئۇنىڭغا شۇ قىزغا باراۋەر تۇرىدىغان قىزغا بېرىلىدىغان مەھرىنى بېرەلمەسلىكىڭلاردىن) قورقساڭلار، ئۇنى تەرك ئېتىپ، ئۆزۈڭلار ياقتۇرىدىغان باشقا ئاياللاردىن ئىككىنى، ئۈچنى ۋە تۆتنى ئېلىشىڭلارغا بولىدۇ، ئەگەر (ئۇلارنىڭ ئارىسىدا) ئادىل بولالماسلىقىڭلاردىن قورقساڭلار، بىر خوتۇن بىلەن ياكى قول ئاستىڭلاردىكى چۆرىلەر بىلەن كۇپايىلەنسەڭلار بولىدۇ. بىر خوتۇن بىلەن كۇپايىلىنىش زۇلۇم قىلماسلىققا ئەڭ يېقىندۇر'),
(497, 4, 4, 'ئاياللارغا ئۇلارنىڭ مەھرىلىرىنى خۇشاللىق بىلەن سوۋغا قىلىپ بېرىڭلار. ئەگەر ئۇلار ئۇنىڭدىن بىر قىسمىنى سىلەرگە ئۆتۈنۈپ بەرسە، ئۇنى مەززىلىك، سىڭىشلىق (يەنى پاك - ھالال بىلىپ) يەڭلار'),
(498, 4, 5, 'اﷲ تىرىكچىلىكىڭلارنىڭ ئاساسى قىلغان ماللىرىڭلارنى ئەخمەقلەرگە تۇتقۇزۇپ قويماڭلار، ئۇلارنى يېمەك - ئىچمەك، كىيىم - كېچەكلەر بىلەن تەمىنلەڭلار. ئۇلارغا چىرايلىق سۆز قىلىڭلار'),
(499, 4, 6, 'يېتىملەرنى بالاغەتكە يەتكەنگە قەدەر سىناپ تۇرۇڭلار، ئۇلاردا ماللىرىنى باشقۇرالايدىغان ھالەتنى بايقىساڭلار، ئۇلارغا مال - مۈلكىنى تاپشۇرۇپ بېرىڭلار؛ ئۇلارنىڭ چوڭ بولۇپ قېلىشىدىن قورقۇپ مال - مۈلكىنى بۇزۇپ - چېچىپ يەۋالماڭلار. (يېتىمگە ۋەسى بولغانلاردىن) كىمكى باي ئىكەن، (ۋەسى بولغانلىق ھەققى ئۈچۈن) يېتىمنىڭ مال - مۈلكىنى يېيىشتىن ئۆزىنى ساقلىسۇن، (سىلەردىن) كىمكى يوقسۇل ئىكەن، ئۇ (ئۆز ئەمگىكىنىڭ ھەققى ئۈچۈن) مۇۋاپىق رەۋىشتە يېسۇن، ئۇلار (يەنى بالاغەتكە يەتكەن يېتىملەر) نىڭ مال - مۈلكىنى تاپشۇرۇپ بېرىدىغان چاغدا باشقىلارنى گۇۋاھ قىلىپ قويۇڭلار. اﷲ ھېساب ئېلىشقا يېتەرلىكتۇر'),
(500, 4, 7, 'ئاتا - ئانىسى ۋە تۇغقانلىرى قالدۇرغان مىراستا (يەنى مېيىتنىڭ تەرەكىسىدە) ئەرلەرنىڭ ھەسسىسى بار. مەيلى ئۇ (يەنى تەرەكە) ئاز بولسۇن ياكى كۆپ بولسۇن، ھەر ئادەم اﷲ نىڭ ئادىل شەرىئىتىدە) بەلگىلەنگەن ھەسسىسىنى ئالىدۇ'),
(501, 4, 8, 'تەقسىمات ۋاقتىدا (ۋارىس ئەمەس) تۇغقانلار، (تۇغقان ئەمەس) يېتىملەر ۋە مىسكىنلەر ھازىر بولسا، ئۇلارغا بۇنىڭدىن (يەنى تەرەكىدىن ئۇلارنىڭ كۆڭلىنى ئېلىش ئۈچۈن بىر ئاز نەرسە) بېرىڭلار، ئۇلارغا چىرايلىق سۆز قىلىڭلار (يەنى بۇ كىچىكلەرنىڭ مېلى، بىزنىڭ ئەمەس دېگەنگە ئوخشاش ئۆزرىنى ئېيتىڭلار)'),
(502, 4, 9, '(كىملەركى) ئۆزلىرىدىن قېلىپ قالىدىغان كىچىك بالىلىرى بولۇپ، ئۇلاردىن قانداق ئەندىشە قىلىدىغان بولسا، باشقىلارنىڭ قېلىپ قالغان كىچىك بالىلىرى ئۈچۈنمۇ شۇنداق ئەندىشە قىلسۇن، (يېتىملەرنىڭ ئىشىدا) اﷲ تىن قورقسۇن ۋە توغرا سۆزنى ئېيتسۇن'),
(503, 4, 10, 'زۇلۇم قىلىپ يېتىملەرنىڭ مال - مۈلكىنى (ناھەق) يەۋالىدىغانلار، شۈبھىسىزكى، قورسىقىغا (قىيامەت كۈنى يېنىپ تۇرىدىغان) ئوتنى يەۋالغان بولىدۇ، ئۇلار يېنىپ تۇرغان ئوتقا (يەنى دوزاخقا) كىرىدۇ'),
(504, 4, 11, 'اﷲ بالىلىرىڭلار (غا تېگىدىغان مىراس) ھەققىدە تەۋسىيە قىلىدۇكى، بىر ئەرگە ئىككى ئايالنىڭ ھەسسىسى تېگىدۇ، ئەگەر مېيىتنىڭ قىزى (ئىككى ياكى) ئىككىدىن كۆپ بولسا، ئۇ چاغدا ئۇلارغا مىراسنىڭ ئۈچتىن ئىككى ھەسسىسى تېگىدۇ، ئەگەر (ۋارىس) بىر قىز بولىدىغان بولسا، ئۇنىڭغا مىراسنىڭ يېرىمى تېگىدۇ، ئەگەر مېيىتنىڭ بالىلىرى بولسا، بۇ چاغدا ئۇنىڭ ئاتا - ئانىسىنىڭ ھەر بىرىگە مىراسنىڭ ئالتىدىن بىرى تېگىدۇ. ئەگەر ئۇنىڭ بالىلىرى بولماي مىراسقا پەقەت ئۇنىڭ ئاتا - ئانىسى ۋارىسلىق قىلىدىغان بولسا، بۇ چاغدا ئۇنىڭ ئانىسىغا مىراسنىڭ ئۈچتىن بىرى تېگىدۇ (قالغىنى ئاتىسىغا تېگىدۇ). ئەگەر مېيىتنىڭ (ئاتا - ئانىسىدىن باشقا يەنە) قېرىنداشلىرى بولسا، ئانىسىغا مىراسنىڭ ئالتىدىن بىرى تېگىدۇ (قالغىنىنى ئاتىسى ئالىدۇ). (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ۋە قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ. ئاتا - ئاناڭلار ۋە بالىلىرىڭلاردىن (يەنى بۇ ئىككىسىدىن) قايسىسىنىڭ مەنپەئەت جەھەتتىن ئۆزۈڭلارغا ئەڭ يېقىن ئىكەنلىكىنى بىلمەيسىلەر، بۇلار اﷲ تەرىپىدىن بېكىتىلگەن بەلگىلىمىدۇر. اﷲ ھەقىقەتەن ھەممىنى بىلگۈچى، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(505, 4, 12, 'ئەگەر ئاياللىرىڭلارنىڭ بالىلىرى بولمىسا، بۇ چاغدا ئۇلار قالدۇرغان مىراسنىڭ يېرىمى (ئى ئەرلەر!) سىلەرگە تېگىدۇ. ئەگەر ئۇلارنىڭ بالىلىرى بولسا، ئۇلارغا قالدۇرغان مىراسنىڭ تۆتتىن بىرى سىلەرگە تېگىدۇ. (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى مېيىتنىڭ قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ. ئەگەر سىلەرنىڭ بالىلىرىڭلار بولمىسا، بۇ چاغدا ئاياللىرىڭلارغا سىلەر قالدۇرغان مىراسنىڭ تۆتتىن بىرى تېگىدۇ، ئەگەر سىلەرنىڭ بالىلىرىڭلار بولسا، بۇ چاغدا ئايالىڭلارغا سىلەر قالدۇرغان مىراستىن سەككىزدىن بىرى تېگىدۇ. (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ. ئەگەر مىراس قالدۇرغۇچى ئاتا - ئانىسىز، بالىسىز ئەر ياكى ئايال بولۇپ، ئۇنىڭ پەقەت ئانا بىر قېرىندىشى ۋە ئانا بىر ھەمشىرىسى بولسا، ئۇلارنىڭ ھەر بىرىگە مىراسنىڭ ئالتىدىن بىرى تېگىدۇ. ئەگەر مىراس قالدۇرغۇچىنىڭ (يەنى مىراس قالدۇرغۇچى مېيىتنىڭ ئانا بىر، ئاتا باشقا) قېرىنداشلىرى ۋە ھەمشىرىلىرى بۇنىڭدىن (يەنى بىردىن) كۆپ بولسا، ئۇ چاغدا مىراسنىڭ ئۈچتىن بىرى ئۇلارنىڭ ئارىسىدا ئورتاق تەقسىم قىلىنىدۇ. (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ. (مىراس قالدۇرغۇچى ئادەمنىڭ) ۋەسىيىتى ۋارىسلارغا زىيان يەتكۈزمەيدىغان بولۇشى كېرەك. ئۇ اﷲ تەرىپىدىن قىلىنغان تەۋسىيىدۇر، اﷲ ھەممىنى بىلگۈچىدۇر، ھەلىمدۇر (يەنى ئەمرىگە خىلاپلىق قىلغۇچىلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)'),
(506, 4, 13, 'ئەنە شۇلار (يەنى اﷲ بەندىلىرىنىڭ ئەمەل قىلىشى ئۈچۈن بەلگىلەپ بەرگەن يۇقىرىدىكى ئەھكاملار) اﷲ نىڭ قانۇنلىرىدۇر، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىدىكەن، اﷲ ئۇنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇ ئۇ يەرلەردە مەڭگۈ قالىدۇ. بۇ چوڭ مۇۋەپپەقىيەت قازىنىشتۇر'),
(507, 4, 14, 'كىمكى اﷲ قا ۋە پەيغەمبىرىگە ئاسىيلىق قىلىپ، اﷲ نىڭ قانۇنلىرىنىڭ سىرتىغا چىقىپ كېتىدىكەن، اﷲ ئۇنى دوزاخقا كىرگۈزىدۇ، ئۇ دوزاختا مەڭگۈ قالىدۇ، خورلىغۇچى ئازابقا دۇچار بولىدۇ'),
(508, 4, 15, 'ئاياللىرىڭلاردىن پاھىشە قىلغانلارنى (ئىسپاتلاش ئۈچۈن) ئاراڭلاردىن تۆت گۇۋاھچى كەلتۈرۈڭلار؛ ئەگەر ئۇلار گۇۋاھلىق بەرسە (يەنى ئۇلار گۇۋاھلىق بېرىپ ئايالنىڭ جىنايىتىنى ئىسپاتلىسا)، تاكى ئاياللىرىڭلار ۋاپات بولغانغا ياكى اﷲ ئۇلارغا بىر چىقىش يولى بەرگەنگە قەدەر ئۇلارنى ئۆيدە تۇتۇڭلار'),
(509, 4, 16, 'ئاراڭلاردا ئىككى كىشى (يەنى ئەر بىلەن ئايال) پاھىشە قىلسا، ئۇلارنى ئەيىبلەڭلار؛ ئەگەر ئۇلار تەۋبە قىلىپ تۈزەلسە، ئۇلارنى كەچۈرۈڭلار، اﷲ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، تولىمۇ مېھرىباندۇر'),
(510, 4, 17, 'اﷲ قوبۇل قىلىشنى ۋەدە قىلغان تەۋبە يامانلىقنى نادانلىقتىن قىلىپ سېلىپ، ئاندىن ئۇزۇنغا قالماي (يەنى ئۆلۈم كېلىشتىن بۇرۇن) تەۋبە قىلغانلارنىڭ تەۋبىسىدۇر؛ اﷲ ئەنە شۇلارنىڭ تەۋبىسىنى قوبۇل قىلىدۇ، اﷲ ھەممىنى بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(511, 4, 18, 'داۋاملىق يامان ئىشلارنى قىلىپ بېشىغا ئۆلۈم كەلگەن چاغدا، ئەمدى تەۋبە قىلدىم، دېگۈچىلەرنىڭ تەۋبىسى ۋە كاپىر پېتى ئۆلگۈچىلەرنىڭ تەۋبىسى (يەنى ئۆلۈم ئالدىدا ئىمان ئېيتتىم دېگۈچىلەرنىڭ ئىمانى) قوبۇل قىلىنمايدۇ. ئەنە شۇلار قاتتىق ئازاب تەييارلىدۇق'),
(512, 4, 19, 'ئى مۆمىنلەر! ئاياللارغا زورلۇق قىلىپ، ئۇلارنى مىراس قىلىپ ئالماق (يەنى بىر ئادەمنىڭ قولىدىن يەنە بىر ئادەمنىڭ قولىغا ئۆتۈپ تۇرىدىغان مال ئورنىدا قىلىۋالماق) سىلەرگە دۇرۇس بولمايدۇ؛ ئۇلار ئوپئوچۇق بىر پاھىشە ئىشىنى قىلمىغان ھالەتتە، سىلەر ئۇلارغا بەرگەن مەھرىدىن بىر قىسمىنى يۇلۇۋېلىش ئۈچۈن، ئۇلارغا بېسىم ئىشلەتمەڭلار، ئۇلار بىلەن چىرايلىقچە تىرىكچىلىك قىلىڭلار، ئەگەر ئۇلارنى ياقتۇرمىساڭلار (سەۋر قىلىڭلار)، چۈنكى سىلەر ياقتۇرمايدىغان بىر ئىشتا اﷲ كۆپ خەيرىيەتلەرنى پەيدا قىلىشى مۇمكىن'),
(513, 4, 20, 'ئەگەر بىر خوتۇننى قويۇۋېتىپ، ئورنىغا يەنە بىر خوتۇننى ئالماقچى بولساڭلار، ئۇلارنىڭ بىرىگە (مەھرى قىلىپ) كۆپ مال بەرگەن بولساڭلارمۇ ئۇنىڭدىن ھېچ نەرسىنى قايتۇرۇۋالماڭلار. ئۇنى (ئايالغا) قارا چاپلاپ ۋە ئوچۇق زۇلۇم قىلىپ ئالامسىلەر؟'),
(514, 4, 21, 'ئۆزئارا خىلۋەتتە بولۇشقان تۇرساڭلار ۋە ئاياللار سىلەردىن (نىكاھ ئەقدىدىن ئىبارەت) مۇستەھكەم ئەھدە ئالغان تۇرسا، ئۇنى قانداقمۇ قايتۇرۇۋالىسىلەر؟'),
(515, 4, 22, 'ئاتىلىرىڭلار ئالغان ئاياللارنى ئالماڭلار، لېكىن بۇنىڭدىن ئىلگىرى ئالغان بولساڭلار (ئەپۇ قىلىنىدۇ)، بۇ ھەقىقەتەن يامان ئىشتۇر، ئۆچ كۆرۈلىدىغان قىلىقتۇر، بۇ نېمىدېگەن يامان ئادەت!'),
(516, 4, 23, 'سىلەرگە ئانىلىرىڭلارنى، قىزلىرىڭلارنى، ھەمشىرىلىرىڭلارنى، ئاتاڭلارنىڭ ھەمشىرىلىرىنى، ئاناڭلارنىڭ ھەمشىرىلىرىنى، قېرىندىشىڭلارنىڭ قىزلىرىنى، ھەمشىرەڭلارنىڭ قىزلىرىنى، سۈت ئانىلىرىڭلارنى، ئېمىلدەش ھەمشىرىڭلارنى، قېيىن ئاناڭلارنى، سىلەر بىر يەردە بولغان ئاياللىرىڭلارنىڭ (باشقا ئەردىن بولغان) ئۆيۈڭلاردا تەربىيىلەنگەن قىزلىرىنى (بۇ شەرت ئەمەس، ئۆيدە تەربىيىلەنمىگەن بولسىمۇ) ئېلىش ھارام قىلىندى. ئەگەر سىلەر ئۇلارنىڭ ئانىلىرى بىلەن بىر تۆشەكتە بولمىغان بولساڭلار، بۇ چاغدا ئۇلارنى ئالساڭلار ھېچ باك يوقتۇر، يەنە ئۆز پۇشتىڭلاردىن بولغان بالىلىرىڭلارنىڭ ئاياللىرىنى ئېلىشىڭلار ۋە ئىككى ھەمشىرىنى بىر ۋاقىتتا ئەمرىڭلاردا ساقلىشىڭلار ھارام قىلىندى. لېكىن ئۆتكەنكىلەر (يەنى جاھىلىيەت دەۋرىدە قىلغىنىڭلار) ئەپۇ قىلىنىدۇ. اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلەرگە) ناھايىتى مېھرىباندۇر'),
(517, 4, 24, 'ئەرلىك ئاياللارنى ئېلىشمۇ سىلەرگە ھارام قىلىندى. پەقەت سىلەرنىڭ باشقۇرۇشۇڭلار ئاستىدىكى چۆرىلەر بۇنىڭدىن مۇستەسنا. بۇ اﷲ نىڭ سىلەرگە پەرز قىلغان ھۆكىملىرىدۇر، بۇلاردىن (يەنى يۇقىرىدىكى ئېلىش دۇرۇس بولمايدىغان ئاياللاردىن) باشقىلىرىنى زىنادىن ساقلىنىپ، ئىپپەتلىك بولغىنىڭلار ھالدا، ماللىرىڭلارنى سەرپ قىلىپ (يەنى مەھرىنى بېرىپ) نىكاھلاپ ئېلىشىڭلار ھالال قىلىندى. قايسىكى ئاياللارنى (نىكاھلاپ ئېلىپ) پايدىلانساڭلار، ئۇلارغا (نىكاھ ۋاقتىدا) بېكىتىلگەن مەھرىلىرىنى بېرىڭلار، مەھرى بېكىتىلگەندىن كېيىن، ئۆزئارا پۈتۈشكەن نەرسەڭلار توغرىسىدا (يەنى ئايالنىڭ رازىلىقى بىلەن ساقىت قىلىنغان مەھرى ئۈچۈن) سىلەرگە ھېچ گۇناھ بولمايدۇ. اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(518, 4, 25, 'سىلەردىن مۆمىن ئاياللارنى ئېلىشقا قۇربى يەتمەيدىغانلار قول ئاستىدىكى مۆمىن چۆرىلەردىن ئالسۇن. اﷲ ئىمانىڭلارنى ئوبدان بىلىدۇ (يەنى ئۇلارنىڭ سىرتقى كۆرۈنۈشىگە قاراپ ئېلىۋېرىڭلار، ئىچكى ئەھۋالىنى اﷲ قا تاپشۇرۇڭلار). سىلەر بىر - بېرىڭلار بىلەن دىنداشسىلەر، ئۇلارنىڭ خوجىلىرىنىڭ قوشۇلۇشى بىلەن ئۇلارنى خوتۇنلۇققا ئالساڭلار بولىدۇ، ئۇلارغا مەھرىلىرىنى كېمەيتىۋەتمەستىن ۋە كېچىكتۈرمەستىن بېرىڭلار. لېكىن ئۇلار ئاشكارا زىنا قىلمايدىغان، يوشۇرۇن ئاشنا تۇتمايدىغان ئەفىفە بولۇشلىرى كېرەك. ئەگەر ئۇلار ئەرگە تەگكەندىن كېيىن پاھىشە قىلسا، ئۇلارغا ھۆر ئاياللارغا بېرىلىدىغان جازانىڭ يېرىمى بېرىلىشى كېرەك. بۇ (يەنى چۆرىلەرنى نىكاھلاپ ئېلىش)، ئاراڭلاردا زىناغا مۇپتىلا بولۇپ قېلىشتىن قورققان كىشى ئۈچۈندۇر. سەۋر قىلالىساڭلار سىلەر ئۈچۈن ياخشىدۇر. اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(519, 4, 26, 'اﷲ سىلەرگە (ھالال ۋە ھارامغا دائىر ئەھكاملارنى) بايان قىلىشنى، سىلەردىن بۇرۇنقىلار (يەنى پەيغەمبەرلەر ۋە ياخشىلار) نىڭ يوللىرىغا سىلەرنى ھىدايەت قىلىشنى، (گۇناھىڭلاردىن قىلغان) تەۋبەڭلارنى قوبۇل قىلىشنى خالايدۇ. اﷲ ھەممىنى (يەنى بەندىلىرىنىڭ ھەممە ئەھۋالىنى) بىلگۈچىدۇر ۋە ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(520, 4, 27, 'اﷲ سىلەرنىڭ تەۋبەڭلارنى قوبۇل قىلىشنى خالايدۇ (بۇ اﷲ نىڭ رەھمىتىنىڭ كەڭلىكىنى كۆرسىتىش ئۈچۈن تەكرارلاندى). شەھۋەتلەرگە ئەگىشىدىغانلار (يەنى شەيتانغا ئەگىشىدىغان فاجىرلار) توغرا يولدىن بۇرۇلۇپ كېتىشىڭلارنى (شۇلارغا ئوخشاش فاجىر بولۇشۇڭلارنى) خالايدۇ'),
(521, 4, 28, 'اﷲ سىلەرنىڭ يۈكۈڭلارنى يېنىكلىتىشنى (يەنى شەرىئەت ئەھكاملىرىنى سىلەرگە ئاسانلاشتۇرۇشنى) خالايدۇ. ئىنسان (نەپسى خاھىشىغا خىلاپلىق قىلىشتىن) ئاجىز يارىتىلدى'),
(522, 4, 29, 'ئى مۆمىنلەر! بىر - بىرىڭلارنىڭ ماللىرىنى (ئوغرىلىق، خىيانەت قىلىش، بۇلاش، جازانىخورلۇق، قىمار ئويناش قاتارلىق) ناھەق يول بىلەن يەۋالماڭلار، ئىككى تەرەپ رازى بولۇشۇپ قىلىشقان سودا - سېتىق ئارقىلىق ئېرىشىلگەن نەرسە بۇنىڭدىن مۇستەسنا. سىلەر ئۆزۈڭلارنى (يەنى بىر - بىرىڭلارنى) ئۆلتۈرمەڭلار، اﷲ ھەقىقەتەن سىلەرگە ناھايىتى مېھرىباندۇر'),
(523, 4, 30, 'كىمكى چەكتىن ئېشىپ ۋە زۇلۇم قىلىپ ئۇنى (يەنە اﷲ چەكلىگەن ئىشلارنى) قىلىدىكەن، ئۇنى دوزاخقا كىرگۈزىمىز، بۇ اﷲ قا ئاساندۇر'),
(524, 4, 31, 'ئەگەر سىلەر مەنئى قىلىنغان چوڭ گۇناھلاردىن ساقلانساڭلار، (كەرىمىمىزگە ئېلىپ) كىچىك گۇناھلىرىڭلارنى يوققا چىقىرىمىز ۋە سىلەرنى ئېسىل ماكانغا (يەنى جەننەتكە) كىرگۈزىمىز'),
(525, 4, 32, 'ئى مۆمىنلەر! اﷲ سىلەردىكى بىر - بىرىڭلاردىن ئارتۇق قىلغان نەرسىلەرنى (ھەسەت قىلىش يۈزىسىدىن) ئارزۇ قىلماڭلار، ئەرلەر قىلغان ئەمەللىرىدىن ھەسسىدار بولىدۇ، ئاياللارمۇ قىلغان ئەمەللىرىدىن ھەسسىدار بولىدۇ. اﷲ تىن ئۇنىڭ پەزلىنى تىلەڭلار (اﷲ بېرىدۇ). اﷲ ھەقىقەتەن ھەممە نەرسىنى بىلگۈچىدۇر'),
(526, 4, 33, 'ھەر بىر ئادەمنىڭ قالدۇرغان تەرەكىسى ئۈچۈن ۋارىسلار تەيىنلىدۇق، ئۇلار ئاتا - ئانىلار ۋە يېقىنلاردۇر. سىلەر بىلەن (ئۆزئارا ياردەم بېرىش ۋە مىراسخور بولۇشقا) ئەھدۇپەيمان قىلىشقان كىشىلەرگە ئۇلارنىڭ (مىراستىن) ھەسسىسىنى بېرىڭلار. اﷲ ھەقىقەتەن ھەممە نەرسىنى بايقاپ تۇرغۇچىدۇر'),
(527, 4, 34, 'ئەرلەر ئاياللارنىڭ ھامىيلىرىدۇر، بۇ اﷲ نىڭ ئۇلارنىڭ بەزىسىنى بەزىسىدىن (يەنى ئەرلەرنى كۈچ - قۇۋۋەت، غازاتقا چىقىش قاتارلىق جەھەتلەردە ئاياللاردىن) ئارتۇق قىلغانلىقىدىندۇر ۋە ئەرلەرنىڭ ئۆز پۇل - ماللىرىنى سەرپ قىلغانلىقىدىندۇر (يەنى ئاياللارنىڭ نەپىقىسى ئەرلەرنىڭ زىممىسىگە يۈكلەنگەنلىكتىندۇر). ياخشى ئاياللار ئىتائەت قىلغۇچىلاردۇر، ئەرلىرى يېنىدا بولمىغان چاغلاردا اﷲ نىڭ پاناھىدا ئەرلىرىنىڭ ھەقلىرىنى ساقلىغۇچىلاردۇر، سىلەر سەركەشلىك قىلىشلىرىدىن قورقىدىغان ئاياللارغا نەسىھەت قىلىڭلار، (بۇ ئۈنۈم بەرمىسە) بىر تۆشەكتە بىللە ياتماڭلار، (بۇمۇ ئۈنۈم بەرمىسە) ئۇلارنى ئەدەبلەش مەقسىتىدە ئاستىراق ئۇرۇڭلار. ئەگەر سىلەرگە ئىتائەت قىلسا، ئۇلارنى بوزەك قىلىش خىيالىدا بولماڭلار. اﷲ ھەقىقەتەن سىلەردىن ئۈستۈندۇر، بۈيۈكتۇر (اﷲ ئاياللارغا زۇلۇم قىلغاننى جازالايدۇ)'),
(528, 4, 35, 'ئەگەر ئەر - خوتۇن ئىككىيلەننىڭ ئىناقسىز بولۇپ قېلىشىدىن قورقساڭلار، ئەرنىڭ تۇغقانلىرىدىن بىر ھەققانىي كىشىنى، خوتۇننىڭ تۇغقانلىرىدىن بىر ھەققانىي كىشىنى ئەۋەتىڭلار، اﷲ ئەگەر بۇ ئىككى كىشىنى ئەپلەشتۈرۈشنى خالىسا، ئەر - خوتۇن ئىككىسىنىڭ ئارىسىغا ئىناقلىق سالىدۇ. اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر'),
(529, 4, 36, 'اﷲ قا ئىبادەت قىلىڭلار، ئۇنىڭغا ھېچ نەرسىنى شېرىك كەلتۈرمەڭلار، ئاتا - ئاناڭلارغا، خىش - ئەقرىبالىرىڭلارغا، يېتىملەرگە، مىسكىنلەرگە، يېقىن قوشنىغا، يىراق قوشنىغا، ياندىكى ھەمراھقا (يەنى سەپەرداشقا، ساۋاقداشقا)، مۇساپىرغا، قول ئاستىڭلاردىكى قۇل - چۆرىلەرگە ياخشىلىق قىلىڭلار، شۈبھىسىزكى، اﷲ مۇتەكەببىر، ماختانچاقنى ياقتۇرمايدۇ'),
(530, 4, 37, 'ئۇلار (يەنى مۇتەكەببىرلەر، ماختانچاقلار) بېخىللىق قىلىدۇ، كىشىلەرنى بېخىللىققا بۇيرۇيدۇ، اﷲ ئۇلارغا ئۆز پەزلىدىن بەرگەن نەرسىلەرنى (يەنى پۇل - مالنى، ۋە مۇھەممەد ئەلەيھىسسالامنىڭ تەۋراتتىكى سۈپىتىنى) يوشۇرىدۇ، (اﷲ نىڭ نېمىتىنى) ئىنكار قىلغۇچىلارغا خورلىغۇچى ئازابنى تەييارلىدۇق'),
(531, 4, 38, 'ئۇلار ماللىرىنى كىشىلەرگە كۆرسىتىش ئۈچۈن سەرپ قىلىدۇ، ئۇلار اﷲ قا ئىشەنمەيدۇ، ئاخىرەت كۈنىگىمۇ ئىشەنمەيدۇ. كىمكى ئۆزىگە شەيتاننى ھەمراھ قىلىدىكەن (ئۇ شەيتاننىڭ بۇيرۇقى بويىچە ئىش قىلىدۇ)، شەيتان نېمىدېگەن يامان ھەمراھ!'),
(532, 4, 39, 'ئەگەر ئۇلار اﷲ قا، ئاخىرەت كۈنىگە ئىشەنسە ۋە اﷲ ئۆزلىرىگە بەرگەن مالدىن سەرپ قىلسا، ئۇلارغا نېمە زىيىنى بولاتتى؟ اﷲ ئۇلارنى ئوبدان بىلىدۇ (قىلمىشىغا قاراپ جازالايدۇ)'),
(533, 4, 40, 'اﷲ ھەقىقەتەن زەررىچە زۇلۇم قىلمايدۇ. ئەگەر كىشىنىڭ زەررىچە ياخشىلىقى بولسا، اﷲ ئۇنى ھەسسىلەپ زىيادە قىلىدۇ، ئۆز دەرگاھىدىن بۈيۈك ئەجىر (يەنى جەننەت) ئاتا قىلىدۇ'),
(534, 4, 41, 'بىز ھەر بىر ئۈممەتتىن بىر گۇۋاھچىنى كەلتۈرگەن ۋە (ئى مۇھەممەد!) سېنى بۇلارغا (يەنى ئۈممىتىڭ ئىچىدىكى ئىمانسىزلارغا ۋە گۇناھكارلارغا) گۇۋاھچى قىلىپ كەلتۈرگەن چېغىمىزدا ئۇلارنىڭ ھالى قانداق بولىدۇ؟'),
(535, 4, 42, 'كاپىرلار ۋە پەيغەمبەرگە ئاسىيلىق قىلغانلار ئۇ كۈندە (ئۇلار قىيامەتنىڭ دەھشىتىنى كۆرگەنلىكتىن) يەر بىلەن تۈپتۈز قىلىۋېتىلىشىنى (يەنى يەرگە كۆمۈلۈپ تۇپراققا ئايلىنىپ كېتىشىنى) ئارزۇ قىلىدۇ، (ئۇلارنىڭ ھەممە ئەزاسى گۇۋاھ بولغانلىقتىن) اﷲ تىن ھېچبىر سۆزنى يوشۇرالمايدۇ'),
(536, 4, 43, 'ئى مۆمىنلەر! سىلەر مەست بولساڭلار، نېمە دەۋاتقىنىڭلارنى بىلگىنىڭلارغىچە، جۇنۇپ بولساڭلار، - يول ئۈستىدە بولغانلار (بۇنىڭدىن) مۇستەسنا، - غۇسلى قىلمىغۇچە نامازغا يېقىنلاشماڭلار، ئەگەر كېسەل (يەنى كېسەلگە سۇ زىيان قىلىدىغان بولسا) ياكى سەپەر ئۈستىدە بولساڭلار، ياكى ھاجەت قىلساڭلار، ياكى ئاياللار بىلەن مۇناسىۋەت ئۆتكۈزسەڭلار، (مۇشۇنداق ئەھۋال ئاستىدا) سۇ تاپالمىساڭلار، پاك تۇپراقنى يۈزۈڭلارغا، قولۇڭلارغا سۈرتۈپ تەيەممۇم قىلىڭلار، اﷲ ھەقىقەتەن ئەپۇ قىلغۇچىدۇر، مەغپىرەت قىلغۇچىدۇر (يەنى اﷲ بەندىلىرىگە ھەرەج بولماسلىقى ئۈچۈن ئىبادەتنى ئاسانلاشتۇرۇپ بەرگۈچىدۇر)'),
(537, 4, 44, '(ئى مۇھەممەد!) كىتابتىن (يەنى تەۋراتتىن) نېسىۋە بېرىلگەن كىشىلەر (يەنى يەھۇدىي موللىلىرى) نىڭ گۇمراھلىقنى سېتىۋالغانلىقىنى ۋە سىلەرنىڭ توغرا يولدىن ئېزىشىڭلارنى خالايدىغانلىقىنى كۆرمىدىڭمۇ؟'),
(538, 4, 45, 'اﷲ سىلەرنىڭ دۈشمەنلىرىڭلارنى ئوبدان بىلىدۇ. اﷲ سىلەرنى ساقلاشقا يېتەرلىكتۇر ۋە سىلەرگە مەدەتكار بولۇشقا يېتەرلىكتۇر (يەنى سىلەرنى يەھۇدىيلارنىڭ مىكرىدىن ساقلاشقا اﷲ كۇپايىدۇر)'),
(539, 4, 46, 'يەھۇدىيلارنىڭ ئىچىدە كىتابنىڭ (يەنى تەۋراتنىڭ) سۆزلىرىنى ئۆزگەرتىۋېتىدىغانلارمۇ بار، ئۇلار: «بىز (سۆزۈڭنى) ئاڭلىدۇق، (ئەمرىڭگە) بويسۇنمىدۇق، بىزگە قۇلاق سال، بىز ساڭا قۇلاق سالمايمىز» دەيدۇ، دىنغا تەنە قىلىش يۈزىسىدىن، رائىنا دېگەن سۆزنى تىللىرىنى ئەگرى قىلىپ ئېيتىدۇ. ئەگەر ئۇلار (يۇقىرىقى سۆزلىرىنىڭ ئورنىغا) ئاڭلىدۇق ۋە بويسۇندۇق، قۇلاق سال، ئۇنزۇرنا دېسە، ئۇلار ئۈچۈن (اﷲ نىڭ دەرگاھىدا) تېخىمۇ ياخشى، تېخىمۇ توغرا بولاتتى، لېكىن كۇفرى سەۋەبلىك اﷲ ئۇلارنى رەھمىتىدىن يىراق قىلدى. ئۇلارنىڭ ئازغىنىسىدىن باشقىسى ئىمان ئېيتمايدۇ'),
(540, 4, 47, 'ئى كىتاب بېرىلگەنلەر! (يەنى يەھۇدىيلار) بىز نۇرغۇن يۈزلەرنى ئۆزگەرتىپ ئارقىسىغا قىلىپ قويۇشىمىزدىن (يەنى يۈزدىكى بۇرۇن، كۆز، قاش قاتارلىق سەزگۈ ئەزالىرىنى ئۆز جايلىرىدىن يوق قىلىۋېتىپ، ئۇلارنى ئارقا تەرەپكە پەيدا قىلىپ قويۇشىمىزدىن، سۈرەتلىرىنى مۇبەددەل قىلىشىمىزدىن)، ئىلگىرى، ياكى ئۇلارغا شەنبە كۈنىنىڭ ھۆرمىتىنى ساقلىمىغانلارغا لەنەت قىلغاندەك لەنەت قىلىشىمىزدىن ئىلگىرى، ئۆزۈڭلاردىكى كىتابنى (يەنى تەۋراتنى) تەستىق قىلىدىغان، بىز نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ئىمان كەلتۈرۈڭلار (بولمىسا يۇقىرىقى ئىككى بالانىڭ بىرىگە ئۇچرايسىلەر)، اﷲ نىڭ ھۆكىمى ئىجرا قىلىنىدۇ (يەنى اﷲ بىرەر ئىشقا ئەمر قىلسا، ئۇ ئىش چوقۇم بولماي قالمايدۇ)'),
(541, 4, 48, 'اﷲ ھەقىقەتەن اﷲ قا شېرىك كەلتۈرۈش گۇناھىنى مەغپىرەت قىلمايدۇ، خالىغان ئادەمنىڭ ئۇنىڭدىن باشقا گۇناھىنى مەغپىرەت قىلىدۇ. كىمكى اﷲ قا شېرىك كەلتۈرىدىكەن، چوڭ گۇناھ قىلغان بولىدۇ'),
(542, 4, 49, 'ئۆزلىرىنى پاك دەپ قارايدىغانلار (يەنى ئۆزلىرىنى تەقۋادارلىق بىلەن مەدھىيىلەيدىغانلارنى) نى كۆرمىدىڭمۇ؟ ئۇنداق ئەمەس (يەنى ئىش ئۇلارنىڭ دېگىنىدەك ئەمەس)، اﷲ خالىغان بەندىسىنى پاك قىلىدۇ، ئۇلارغا قىلچىلىكمۇ زۇلۇم قىلىنمايدۇ'),
(543, 4, 50, '(ئى مۇھەممەد!) ئۇلارنىڭ اﷲ قا تۆھمەت چاپلاۋاتقانلىقىغا قارىغىن! بۇ ئوپئوچۇق گۇناھتۇر'),
(544, 4, 51, 'كىتابتىن نېسىۋە بېرىلگەنلەر (يەنى يەھۇدىيلار) نى كۆرمىدىڭمۇ؟ ئۇلار بۇتقا ۋە شەيتانغا ئىشىنىدۇ، كاپىرلارنى كۆرسىتىپ: «بۇلار (يەنى قۇرەيش كۇففارلىرى) نىڭ يولى ئىمان ئېيتقانلارنىڭكىدىن تېخىمۇ توغرىدۇر» دەيدۇ'),
(545, 4, 52, 'ئەنە شۇلار اﷲ رەھمىتىدىن يىراق قىلغان كىشىلەردۇر؛ اﷲ كىمنى رەھمىتىدىن يىراق قىلىدىكەن، ئۇنىڭغا (اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچبىر مەدەتكار تاپالمايسەن'),
(546, 4, 53, 'ياكى ئۇلارنىڭ پادىشاھلىقتىن نېسىۋىسى بارمۇ؟ (ھېچقانداق نېسىۋىسى يوق) ئەگەر بولىدىغان بولسا، (زىيادە بېخىللىقتىن) ئۇلار كىشىلەرگە قىلچىلىك نەرسە بەرمەيدۇ'),
(547, 4, 54, 'ياكى ئۇلار اﷲ ئۆز پەزلىدىن كىشىلەرگە بەرگەن نەرسىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا بېرىلگەن پەيغەمبەرلىككە) ھەسەت قىلىشامدۇ؟ بىز ھەقىقەتەن ئىبىراھىم ئەۋلادىغا كىتاب، ھېكمەت بەردۇق ۋە ئۇلارغا بۈيۈك پادىشاھلىق بەردۇق'),
(548, 4, 55, 'ئۇلار (يەنى يەھۇدىيلار) نىڭ ئىچىدە ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) ئىمان ئېيتقانلارمۇ بار، ئۇنىڭدىن يۈز ئۆرۈگەنلەرمۇ بار، ئۇلارغا (ئۇلارنىڭ كۇفرىغا جازا بېرىش يۈزىسىدىن) جەھەننەمنىڭ يېنىپ تۇرغان ئوتى يېتەرلىكتۇر'),
(549, 4, 56, 'شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى دوزاخقا كىرگۈزىمىز، ئۇلارنىڭ تېرىلىرى پىشىپ تۈگىگەن چاغدا ئازابنى تېتىتىش ئۈچۈن ئورنىغا باشقا تېرە يەڭگۈشلەيمىز. اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(550, 4, 57, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمىز، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. ئۇ يەردە ئۇلارغا پاك جۈپتىلەر بولىدۇ، ئۇلارنى جەننەتنىڭ مەڭگۈلۈك سايىسىگە داخىل قىلىمىز'),
(551, 4, 58, 'شۈبھىسىزكى، اﷲ سىلەرنى ئامانەتلەرنى ئىگىسىگە قايتۇرۇشقا، كىشىلەر ئارىسىدا ھۆكۈم قىلغاندا ئادىل ھۆكۈم قىلىشقا بۇيرۇيدۇ، اﷲ سىلەرگە نەسىھەت قىلغان ئىشلار نېمىدېگەن ياخشى، اﷲ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر'),
(552, 4, 59, 'ئى مۆمىنلەر! اﷲ قا، پەيغەمبەرگە ۋە ئۆزۈڭلاردىن بولغان ئىش ئۈستىدىكىلەرگە ئىتائەت قىلىڭلار، ئەگەر سىلەر بىر شەيئىدە ئىختىلاپ قىلىشىپ قالساڭلار، بۇ توغرىدا اﷲ قا ۋە پەيغەمبەرگە مۇراجىئەت قىلىڭلار، بۇ (يەنى اﷲ نىڭ كىتابىغا ۋە پەيغەمبىرىنىڭ سۈننىتىگە مۇراجىئەت قىلىش) سىلەر ئۈچۈن پايدىلىقتۇر، نەتىجە ئېتىبارى بىلەن گۈزەلدۇر'),
(553, 4, 60, 'ساڭا نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) ۋە سەندىن بۇرۇن نازىل قىلىنغان كىتابلارغا (يەنى تەۋراتقا ۋە ئىنجىلغا) ئىمان كەلتۈردۇق دەۋالغان كىشىلەر (يەنى مۇناپىقلار) نى كۆرمىدىڭمۇ؟ ئۇلار ئەرزىنى شەيتاننىڭ ئالدىغا ئېلىپ بارماقچى بولۇۋاتىدۇ؛ ھالبۇكى، ئۇلار شەيتاننى ئىنكار قىلىشقا بۇيرۇلغان، شەيتان ئۇلارنى چوڭقۇر ئازدۇرۇشنى خالايدۇ'),
(554, 4, 61, 'ئۇلار (يەنى مۇناپىقلار) غا: «اﷲ نازىل قىلغان كىتاب تەرەپكە ۋە پەيغەمبەر تەرەپكە كېلىڭلار» دېيىلسە، مۇناپىقلارنىڭ سەندىن قاتتىق يۈز ئۆرۈگەنلىكىنى كۆرىسەن'),
(555, 4, 62, 'قىلمىشلىرى تۈپەيلىدىن ئۆز بېشىغا بىرەر مۇسىبەت كەلگەن چاغدا، سېنىڭ ئالدىڭغا كېلىپ اﷲ بىلەن قەسەم قىلغان ھالدا: «بىز پەقەت ياخشىلىق ۋە يارىشىشنىلا ئىرادە قىلغان ئىدۇق» دېگەنلەرنىڭ ھالى قانداق بولىدۇ؟'),
(556, 4, 63, 'ئەنە شۇلار (يەنى مۇناپىقلار) (يالغان ئېيتىدۇ)، اﷲ ئۇلارنىڭ دىللىرىدىكىنى (يەنى مۇناپىقلىقىنى، مىكرىنى) بىلىدۇ، ئۇلاردىن يۈز ئۆرۈگىن، ئۇلارغا نەسىھەت قىلغىن، ئۇلارغا تەسىرلىك سۆزلەرنى قىلغىن'),
(557, 4, 64, 'بىز ھەر قانداق پەيغەمبەرنى پەقەت ئىتائەت قىلىنىش ئۈچۈنلا ئەۋەتتۇق (ئۇنىڭغا ئىتائەت قىلغانلىق اﷲ قا ئىتائەت قىلغانلىق بولىدۇ)، ئۇلار (يەنى مۇناپىقلار) (سېنىڭ ھۆكىمىڭنى قوبۇل قىلماي) ئۆزلىرىگە زۇلۇم قىلغاندا، ساڭا كېلىپ اﷲ تىن مەغپىرەت تىلىسە، پەيغەمبەرمۇ ئۇلار ئۈچۈن مەغپىرەت تىلىسە ئىدى، ئەلۋەتتە، ئۇلار اﷲ نى كەچۈرۈم قىلغۇچى، ناھايىتى مېھرىبان تاپاتتى'),
(558, 4, 65, '(ئى مۇھەممەد!) پەرۋەردىگارىڭ بىلەن قەسەمكى، ئۇلار (يەنى مۇناپىقلار) ئۆز ئارىسىدىكى دەتالاشقا سېنى ھۆكۈم چىقىرىشقا تەكلىپ قىلمىغۇچە، ئاندىن سېنىڭ چىقارغان ھۆكىمىڭگە ئۇلارنىڭ دىللىرىدىكى قىلچە غۇم بولسىمۇ يوقالمىغىچە ۋە ئۇلار پۈتۈنلەي بويسۇنمىغىچە ئىمان ئېيتقان بولمايدۇ'),
(559, 4, 66, 'ئەگەر بىز ئۇلارغا: «ئۆزۈڭلارنى ئۆلتۈرۈڭلار ياكى يۇرتۇڭلاردىن چىقىپ كېتىڭلار» دەپ ئەمر قىلغان بولساق (يەنى ئىلگىرىكىلەرگە يۈكلىگەندەك ئېغىر تەكلىپنى ئۇلارغا يۈكلىسەك) ئىدۇق، ئۇلارنىڭ ئازغىنىسىدىن باشقىسى بۇنى ئىجرا قىلمايتتى، ئەگەر ئۇلار قىلىنغان ۋەز - نەسىھەتكە ئەمەل قىلسا ئىدى، ئەلۋەتتە ئۇلارغا ياخشى بولاتتى ۋە ئىمانى مۇستەھكەم بولغان بولاتتى (گۇمراھلىق ۋە نىفاقتىن ئەڭ يىراق بولغان بولاتتى)'),
(560, 4, 67, 'ئۇ چاغدا دەرگاھىمىزدىن ئۇلارغا ئەلۋەتتە بۈيۈك ئەجىر (يەنى جەننەت) ئاتا قىلاتتۇق'),
(561, 4, 68, 'ئۇلارنى ئەلۋەتتە توغرا يولغا ھىدايەت قىلاتتۇق'),
(562, 4, 69, 'كىملەركى اﷲ قا ۋە پەيغەمبەرگە ئىتائەت قىلىدىكەن، ئۇلار (ئاخىرەتتە) اﷲ نىڭ نېمىتىگە ئېرىشكەن پەيغەمبەرلەر، سىددىقلار (يەنى يۇقىرى دەرىجىلىك پەيغەمبەرلەر ۋە شېھىتلەر)، شېھىتلەر ۋە ياخشىلار بىلەن بىللە بولىدۇ، ئۇلار نېمىدېگەن ياخشى ھەمراھلار'),
(563, 4, 70, 'بۇ پەزل اﷲ تەرىپىدىندۇر، اﷲ (پەزل - ئېھسانغا تېگىشلىك بولغانلارنى) بىلىشتە يېتەرلىكتۇر'),
(564, 4, 71, 'ئى مۆمىنلەر! (دۈشمەنلەرنىڭ شەررىدىن ساقلىنىش ئۈچۈن) تەييارلىق ھالىتىدە تۇرۇڭلار، ئۇرۇشقا تۈركۈم - تۈركۈم بولۇپ ياكى ھەممىڭلار بىر بولۇپ چىقىڭلار'),
(565, 4, 72, 'شۈبھىسىزكى، ئاراڭلاردا ئۇرۇشقا چىقماي قېلىپ قالغانلار (يەنى مۇناپىقلار) بار، ئەگەر سىلەرگە بىرەر مۇسىبەت (يەنى مەغلۇبىيەت ياكى قىرغىنلىق) كەلسە، (ئۇ مۇناپىق) «اﷲ ماڭا ھەقىقەتەن مەرھەمەت قىلدى، چۈنكى مەن ئۇلار بىلەن (ئۇرۇشتا) بىللە بولمىدىم (بولمىسا ئۆلتۈرۈلگەنلەر قاتارىدا ئۆلتۈرۈلگەن بولاتتىم)» دەيدۇ'),
(566, 4, 73, 'ئەگەر سىلەرگە اﷲ نىڭ پەزلى (يەنى غەلىبە ۋە غەنىيمەت) يەتسە، گويا سىلەر بىلەن ئۇ (يەنى مۇناپىق) نىڭ ئارىسىدا ھېچقانداق دوستلۇق يوقتىكىدەك، «كاشكى مەن (ئۇرۇشتا) ئۇلار بىلەن بىللە بولغان بولسام، چوڭ بىر مۇۋەپپەقىيەتكە (يەنى غەنىيمەتتىن زور نېسىۋىگە) ئېرىشكەن بولاتتىم» دەيدۇ'),
(567, 4, 74, 'دۇنيا تىرىكچىلىكىنى ئاخىرەتكە تېگىشىدىغانلار اﷲ يولىدا (جېنىنى، پۇل - مېلىنى پىدا قىلىپ) جىھاد قىلسۇن، كىمكى اﷲ يولىدا جىھاد قىلىپ ئۆلتۈرۈلسە ياكى غەلىبە قىلسا، بىز ئۇنىڭغا كاتتا ئەجىر ئاتا قىلىمىز'),
(568, 4, 75, '(ئى مۆمىنلەر!) سىلەرگە نېمە بولدىكى، اﷲ نىڭ يولىدا جىھاد قىلمايسىلەر ۋە (دۇئا قىلىپ): «ئى پەرۋەردىگارىمىز! بۇ ئاھالىسى زالىم شەھەردىن بىزنى چىقارغىن، ئۆز دەرگاھىڭدىن بىزگە بىر ئىگىدارچىلىق قىلغۇچىنى مۇيەسسەر قىل، ئۆز دەرگاھىڭدىن بىزگە بىر مەدەتكارنى مۇيەسسەر قىل» دەيدىغان ئاجىز ئەرلەر، ئاجىز ئاياللار ۋە بالىلارنى قۇتقۇزۇش ئۈچۈن جىھاد قىلمايسىلەر'),
(569, 4, 76, 'مۆمىنلەر اﷲ يولىدا جىھاد قىلىدۇ، كاپىرلار شەيتاننىڭ يولىدا ئۇرۇش قىلىدۇ؛ شەيتاننىڭ دوستلىرى بىلەن ئۇرۇش قىلىڭلار (سىلەر ئۇلارنى يېڭىسىلەر)، شەيتاننىڭ تەدبىرى ھەقىقەتەن ئاجىزدۇر'),
(570, 4, 77, '(ئى مۇھەممەد!) ئۇلارغا (يەنى مەككىدە تۇرۇپ ئۇرۇشۇشنى تەلەپ قىلغانلارغا): «(ئۇرۇشتىن) قولۇڭلارنى يىغىڭلار، ناماز ئوقۇڭلار، زاكات بېرىڭلار» دېگەن كىشىلەرنى كۆرمىدىڭمۇ؟ (يەنى ئۇلاردىن ئەجەبلەنمەمسەن؟) بۇلارغا جىھاد پەرز قىلىنغان چاغدا، ئۇلارنىڭ ئارىسىدىكى بىر تۈركۈم كىشىلەر دۈشمەندىن گويا اﷲ (نىڭ ئازابى) دىن قورققاندەك بەلكى ئۇنىڭدىنمۇ بەتتەررەك قورقىدۇ. ئۇلار (ئۆلۈمدىن قورقۇپ): «پەرۋەردىگارىمىز! بىزگە نېمە ئۈچۈن جىھادنى پەرز قىلدىڭ؟ نېمە ئۈچۈن بىزنى ئۇزاققا قالماي كېلىدىغان ئەجەلگىچە تەخىر قىلمىدىڭ؟ (يەنى ئەجىلىمىز يەتكۈچە تەخىر قىلساڭ، ئۇرۇشتا ئۆلتۈرۈلمىسەك بولماسمىدى؟)» دېدى. (ئى مۇھەممەد!) ئېيتقىنكى، «دۇنيانىڭ مەنپەئىتى ئازدۇر (يەنى دۇنيانىڭ نېمىتى پانىيدۇر)، (اﷲ تىن) قورققان (ۋە ئۇنىڭ ئەمرىنى تۇتقانلار) ئۈچۈن ئاخىرەت ياخشىدۇر، سىلەرگە قىلچىلىك زۇلۇم قىلىنمايدۇ (يەنى قىلغان ئەمەلىڭلارنىڭ ساۋابى كېمەيتىلمەيدۇ)»'),
(571, 4, 78, 'قەيەردە بولماڭلار، (ئەجەل كەلگەندە) ئۆلۈم سىلەرنى تاپىدۇ، سىلەر مۇستەھكەم قەلئەلەردە بولغان تەقدىردىمۇ. ئەگەر ئۇلار (يەنى مۇناپىقلار) بىرەر ياخشىلىققا ئېرىشسە، «بۇ اﷲ تىن بولدى» دەيدۇ. ئەگەر ئۇلار بىرەر زىيان - زەخمەتكە ئۇچرىسا، «بۇ سەندىن (يەنى سېنىڭ دىنىڭغا كىرگەنلىكىمىزدىن) بولدى» دەيدۇ. (ئى مۇھەممەد! بۇ ئەخمەقلەرگە) ئېيتقىنكى، «(ياخشىلىق ۋە يامانلىقنىڭ) ھەممىسى اﷲ تەرىپىدىندۇر (يەنى ھەممىسىنى اﷲ ياراتقاندۇر)». بۇ قەۋمگە نېمە بولدىكىن، ئۇلار ھېچبىر سۆزنى (يەنى ھەممە شەيئىنىڭ اﷲ نىڭ تەقدىرى بىلەن بولىدىغانلىقىنى) چۈشەنمەيدۇ'),
(572, 4, 79, '(ئى ئىنسان!) ساڭا يەتكەن ياخشىلىق (مەرھەمەت قىلىش يۈزىسىدىن) اﷲ تەرىپىدىندۇر، ساڭا يەتكەن يامانلىق (قىلمىشلىرىڭ تۈپەيلىدىن) ئۆزۈڭدىندۇر. (ئى مۇھەممەد!) سېنى بىز (پۈتۈن) ئىنسانلارغا پەيغەمبەر قىلىپ ئەۋەتتۇق، (سېنىڭ پەيغەمبەرلىكىڭگە) شاھىتىلىققا اﷲ يېتەرلىكتۇر'),
(573, 4, 80, 'كىمكى پەيغەمبەرگە ئىتائەت قىلىدىكەن، ئۇ اﷲ قا ئىتائەت قىلغان بولىدۇ (چۈنكى پەيغەمبەر اﷲ نىڭ ئەمرىنى يەتكۈزىدۇ). كىمكى (ئى مۇھەممەد!) سەندىن يۈز ئۆرۈيدىكەن، (بىلگىنكى) بىز سېنى ئۇلارغا كۆزەتچى (يەنى ئۇلارنىڭ ئەمەللىرىنى كۆزىتىپ، ئەمەللىرىگە قاراپ ھېساب ئالغۇچى) قىلىپ ئەۋەتمىدۇق'),
(574, 4, 81, 'ئۇلار (يەنى مۇناپىقلار): «ئەمرىڭگە ئىتائەت قىلدۇق» دەيدۇ، سېنىڭ يېنىڭدىن چىقىپ كەتكەن چاغدا، ئۇلارنىڭ بىر تۈركۈمى ئۆزلىرىنىڭ ئېيتقان سۆزلىرىنى ئۆزگەرتىۋېتىدۇ (يەنى ئەمرىڭگە خىلاپلىق قىلىدۇ)، اﷲ ئۇلارنىڭ ئۆزگەرتكەنلىرىنى خاتىرىلەپ تۇرىدۇ (يەنى پەرىشتىلەرنى ئۇلارنىڭ ئەمەللىرىنى يېزىشقا بۇيرۇيدۇ). سەن ئۇلاردىن يۈز ئۆرۈگىن (يەنى كەچۈرگىن)، اﷲ قا تەۋەككۈل قىلغىن (تەۋەككۈل قىلغۇچىغا) اﷲ نىڭ ھامىيلىقى يېتەرلىكتۇر'),
(575, 4, 82, 'ئۇلار قۇرئان ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ ئەگەر قۇرئان (مۇشرىكلار بىلەن مۇناپىقلار گۇمان قىلغاندەك) اﷲ تىن غەيرىنىڭ تەرىپىدىن بولغان بولسا، ئەلۋەتتە، ئۇنىڭدىن نۇرغۇن زىددىيەتلەرنى تاپقان بولاتتى'),
(576, 4, 83, '(مۆمىنلەرگە ئائىت) ئامانلىقنىڭ ياكى قورقۇنچنىڭ بىرەر خەۋىرى ئۇلارغا (يەنى مۇناپىقلارغا) يەتسە، ئۇنى تارقىتىدۇ، ئەگەر ئۇنى پەيغەمبەرگە ۋە ئۇلار (يەنى مۆمىنلەر) نىڭ ئىچىدىكى ئىش ئۈستىدىكىلەرگە مەلۇم قىلسا، (شۇ) خەۋەرنى چىقارغۇچىلار ئۇنى (يەنى شۇ خەۋەرنىڭ ھەقىقىي ئەھۋالىنى) ئۇلاردىن ئەلۋەتتە بىلىۋالاتتى. (ئى مۆمىنلەر!) ئەگەر سىلەرگە اﷲ نىڭ پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، سىلەرنىڭ ئازغىنا كىشىدىن باشقىلىرىڭلار، ئەلۋەتتە، شەيتانغا ئەگىشىپ كېتەتتىڭلار'),
(577, 4, 84, '(ئى مۇھەممەد!) اﷲ نىڭ يولىدا جىھاد قىلغىن (يەنى ئۆزۈڭ يالغۇز بولساڭمۇ جىھاد قىلغىن، ساڭا غەلىبە ۋەدە قىلىنغان. مۇناپىقلارنىڭ جىھادتىن قېلىپ قالغانلىقىغا ئەھمىيەت بېرىپ كەتمە.) سەن پەقەت ئۆزۈڭگىلا جاۋابكار سەن، مۆمىنلەرنى (جىھادقا) قىزىقتۇرغىن، اﷲ نىڭ كاپىرلار كۈچىنى توسىدىغانلىقىمۇ ھەقىقەتتۇر، اﷲ نىڭ كۈچى ئەڭ زوردۇر. اﷲ نىڭ جازاسى ئەڭ قاتتىقتۇر'),
(578, 4, 85, 'كىمكى (كىشىلەر ئارىسىدا) ياخشى (ئىش ئۈچۈن) شاپائەت قىلسا، ئۇنىڭدىن (يەنى ياخشى ئىشتىن) ئۇنىڭ نېسىۋىسى بولىدۇ؛ كىمكى بىر يامان (ئىش ئۈچۈن) شاپائەت قىلسا، ئۇنىڭدىن (يەنى يامان ئىشتىن) ئۇنىڭ نېسىۋىسى بولىدۇ. اﷲ ھەممە ئىشقا قادىردۇر'),
(579, 4, 86, 'سىلەرگە بىر كىشى سالام بەرسە، ئۇنىڭغا تېخىمۇ ياخشى سالام بىلەن جاۋاب قايتۇرۇڭلار (يەنى بىر كىشى ئەسسالامۇئەلەيكۇم دەپ سالام بەرسە، ئۇنىڭغا ئەسسالامۇ ئەلەيكۇم ۋە رەھمەتۇللاھى ۋەبەرەكاتۇھۇ دەڭلار)، ياكى ئۇنىڭ سالامىنى ئەينەن قايتۇرۇڭلار (يەنى ۋەئەلەيكۇم ئەسسالام دەڭلار). اﷲ ھەقىقەتەن ھەممە نەرسىدىن (يەنى بەندىلىرىنىڭ چوڭ - كىچىك ئىشلىرىدىن ھېساب ئالغۇچىدۇر'),
(580, 4, 87, 'اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. اﷲ شەكسىز بولىدىغان قىيامەت كۈنىدە سىلەرنى (ھېساب ئېلىش ئۈچۈن مەھشەرگاھقا) جەزمەن يىغىدۇ. اﷲ تىنمۇ توغرا سۆزلۈك كىم بار؟ (يەنى سۆزىدە، ۋەدىسىدە اﷲ تىنمۇ سادىق ھېچكىم يوق)'),
(581, 4, 88, 'ئى مۆمىنلەر! سىلەر نېمىشقا مۇناپىقلار توغرىسىدا ئىككى گۇرۇھقا بۆلۈنۈپ كېتىسىلەر؟ اﷲ ئۇلارنىڭ قىلمىشلىرى تۈپەيلىدىن ئۇلارنى كاپىرلار ھۆكمىدە قىلدى، اﷲ ئازدۇرغان كىشىلەرنى سىلەر ھىدايەت قىلماقچى بولامسىلەر؟ كىمنىكى اﷲ گۇمراھ قىلىدىكەن، سەن ئۇنىڭغا ھەرگىز توغرا يول تېپىپ بېرەلمەيسەن'),
(582, 4, 89, 'ئۇلار سىلەرنىڭ ئۆزلىرىدەك كاپىر بولۇشۇڭلارنى، شۇنىڭ بىلەن ئۆزلىرىگە ئوخشاش بولۇشۇڭلارنى ئۈمىد قىلىدۇ، ئۇلار اﷲ يولىدا ھىجرەت قىلمىغۇچە (يەنى ھىجرەت قىلىش ئارقىلىق ئىمانىنى ئىسپات قىلمىغۇچە) ئۇلارنى دوست تۇتماڭلار، ئەگەر ئۇلار اﷲ يولىدا ھىجرەت قىلىشتىن يۈز ئۆرۈسە، ئۇلارنى قەيەردە تاپساڭلار شۇ يەردە تۇتۇپ ئۆلتۈرۈڭلار، ئۇلارنى دوستمۇ تۇتماڭلار، ياردەمچىمۇ قىلماڭلار'),
(583, 4, 90, 'پەقەت سىلەر بىلەن ئۆزلىرى ئارىسىدا ئەھدە بولغان بىر قەۋمگە سېغىنغانلار ياكى سىلەرگە قارشى ئۇرۇش قىلىشتىن ياكى ئۆز قەۋمگە قارشى ئۇرۇش قىلىشتىن يۈرەكلىرى سىقىلىپ (يەنى سىلەر تەرەپمۇ ئەمەس، ئۆز قەۋمى تەرەپمۇ ئەمەس بولۇپ) سىلەرگە كەلگەنلەر بۇنىڭدىن مۇستەسنا (يەنى مۇنداقلارنى ئۆلتۈرمەڭلار ۋە ئەسىر ئالماڭلار). ئەگەر اﷲ خالىسا، ئۇلارنى سىلەرگە مۇسەللەت قىلاتتى، ئۇلار، ئەلۋەتتە، سىلەر بىلەن ئۇرۇشاتتى (يەنى اﷲ ئۇلارنى كۈچلۈك قىلپ سىلەرگە قارشى ئۇرۇش قىلىشقا جۈرئەتلىك قىلاتتى)، ئەگەر ئۇلار سىلەردىن يىراق تۇرسا (يەنى سىلەرگە چېقىلمىسا)، سىلەر بىلەن ئۇرۇشمىسا ۋە سىلەرگە تەسلىم بولسا، اﷲ سىلەرنىڭ ئۇلارغا ھۇجۇم قىلىشىڭلارنى ھەرگىز رۇخسەت قىلمايدۇ'),
(584, 4, 91, 'ئىمانىنى ئىزھار قىلىش بىلەن سىلەردىنمۇ ئامانلىققا ئېرىشمەكچى ۋە قەۋمىگە قايتقاندا (كۇفرىنى ئىزھار قىلىش بىلەن) ئۆز قەۋمىدىنمۇ ئامانلىققا ئېرىشمەكچى بولغان ئىككىنچى بىر خىل كىشىلەر (يەنى مۇناپىقلار) نى كۆرىسىلەر، ئۇلار ھەر قاچان پىتنە - پاساتقا (يەنى كۇفرىغا ياكى مۇسۇلمانلار بىلەن ئۇرۇش قىلىشقا) چاقىرىلسا ئۆزلىرىنى ئۇنىڭدىن تارتىدۇ، ئەگەر ئۇلار سىلەردىن يىراق تۇرمىسا، سىلەرگە تەسلىم بولمىسا، (سىلەر بىلەن) ئۇرۇشۇشتىن قول يىغمىسا، بۇ چاغدا ئۇلارنى قەيەردە كۆرسەڭلار شۇ يەردە (ئەسىر ئېلىڭلار) تۇتۇپ ئۆلتۈرۈڭلار، (ئۇلارنىڭ خىيانىتى سەۋەبلىك) سىلەرگە ئۇلارغا قارشى (يەنى ئەسىر ئېلىشقا، ئۆلتۈرۈشكە) روشەن پاكىت ئاتا قىلدۇق'),
(585, 4, 92, 'مۆمىن ئادەمنىڭ مۆمىن ئادەمنى سەۋەنلىك بىلەن ئەمەس، قەستەن ئۆلتۈرۈشى ھېچ دۇرۇس ئەمەس، كىمكى بىرەر مۆمىننى سەۋەنلىك بىلەن ئۆلتۈرۈپ قويىدىكەن، ئۇ بىر مۆمىن قۇلنى ئازاد قىلىشى ۋە ئۆلتۈرۈلگۈچىنىڭ ۋارىسلىرىغا دىيەت تۆلىشى كېرەك، ئۇلار (يەنى ئۆلتۈرۈلگۈچىنىڭ ۋارىسلىرى) دىيەتنى كەچۈرۈم قىلسام (شۇنىڭ بىلەن قاتىل ئۇنى) تۆلىمىسە ھېچ باك يوق، ئەگەر ئۆلتۈرۈلگۈچى مۆمىن سىلەرگە دۈشمەن (كۇففار) قەۋمدىن بولسا، بۇ چاغدا ئۆلتۈرۈپ قويغۇچى بىر نەپەر مۆمىن قۇلنى ئازاد قىلىشى لازىم (دىيەت كەلمەيدۇ. چۈنكى ئۆلتۈرۈلگۈچىنىڭ ئىگىسى مۇسۇلمان بولمىغاچقا ئۇنىڭغا ۋارىس بولمايدۇ)، ئەگەر ئۆلتۈرۈلگۈچى ئاراڭلاردا مۇئاھىدە بولغان (كۇففار) قەۋمدىن بولسا، ئۇنىڭ ۋارىسلىرىغا دىيەت تۆلەش ۋە بىر نەپەر قۇلنى ئازاد قىلىش كېرەك، ئازاد قىلىدىغان قۇل تاپالمىغان ئادەم (يەنى قۇل ئازاد قىلىشقا قادىر بولالمىغان ئادەم) (تەۋبىسىنى اﷲ نىڭ قوبۇل قىلىشى ئۈچۈن) ئۈزۈلدۈرمەي ئىككى ئاي روزا تۇتۇشى لازىم. اﷲ ھەممىنى بىلىپ تۇرغۇچى، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(586, 4, 93, 'كىمكى بىر مۆمىننى قەستەن ئۆلتۈرىدىكەن، ئۇنىڭ جازاسى جەھەننەم بولىدۇ، ئۇ جەھەننەمدە مەڭگۈ قالىدۇ، اﷲ نىڭ غەزىپىگە ۋە لەنىتىگە دۇچار بولىدۇ. اﷲ ئۇنىڭغا (ئاخىرەتتە) قاتتىق ئازاب تەييارلايدۇ'),
(587, 4, 94, 'ئى مۆمىنلەر! اﷲ يولىغا (يەنى غازاتقا) ئاتلانغان ۋاقتىڭلاردا ھەقىقەتلەپ ئىش قىلىڭلار (يەنى مۆمىن ياكى كاپىرلىقى ئېنىق بولغۇچە ئۆلتۈرۈشكە ئالدىراپ كەتمەڭلار)، سىلەرگە مۇسۇلمان بولغانلىقىنى بىلدۈرۈپ سالام بەرگەن ئادەمگە - دۇنيانىڭ مال - مۈلكىنى (غەنىيمەتنى) كۆزلەپ - سەن مۆمىن ئەمەسسەن دېمەڭلار (يەنى ئۆلۈشتىن قورقۇپ سالام بەردىڭ دەپ ئۇنى ئۆلتۈرۈۋەتمەڭلار)، اﷲ نىڭ دەرگاھىدا (مۇنداقلارنى ئۆلتۈرۈشتىن سىلەرنى بىھاجەت قىلىدىغان) نۇرغۇن غەنىيمەتلەر (يەنى ساۋاب) بار، ئىلگىرى سىلەرمۇ ئەنە شۇنداق (كاپىر) ئىدىڭلار، كېيىن اﷲ سىلەرگە (ئىماننى) ئىلتىپات قىلدى، (ئۇلارنى ئۆزۈڭلارغا سېلىشتۇرۇپ) ھەقىقەتلەپ ئىش قىلىڭلار. اﷲ سىلەرنىڭ قىلمىشىڭلاردىن خەۋەرداردۇر'),
(588, 4, 95, 'مۆمىنلەردىن ئۆزرىسىز (ئەما، توكۇر، كېسەلگە ئوخشاش ئۆزرىسى بارلار بۇنىڭدىن مۇستەسنا) جىھادقا چىقمىغانلار اﷲ يولىدا ماللىرىنى، جانلىرىنى تىكىپ جىھاد قىلغۇچىلار بىلەن باراۋەر بولمايدۇ. اﷲ ماللىرىنى، جانلىرىنى تىكىپ جىھاد قىلغۇچىلارنى جىھادقا چىقمىغانلاردىن بىر دەرىجە ئۈستۈن قىلدى. بۇ ئىككى خىل كىشىلەر (يەنى ئۆزرىسى بولۇپ جىھادقا چىقالمىغانلار ۋە جىھادقا چىققۇچىلار) نىڭ ھەممىسىگە اﷲ جەننەتنى ۋەدە قىلدى. اﷲ جىھاد قىلغۇچىلارغا بۈيۈك ئەجىر ئاتا قىلىپ، ئۇلارنى (ئۆزرىسىز تۇرۇپ جىھادقا چىقمىغانلاردىن ئارتۇق) قىلدى'),
(589, 4, 96, 'اﷲ (ئۇلارغا) بەلەن مەرتىۋىلەر، مەغپىرەت ۋە رەھمەت ئاتا قىلدى، اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(590, 4, 97, 'ئۆزلىرىگە زۇلۇم قىلغۇچىلار (يەنى ھىجرەتنى تەرك ئېتىپ كۇففارلار بىلەن بىللە تۇرغۇچىلار) نىڭ جانلىرى پەرىشتىلەر تەرىپىدىن ئېلىنىدىغان چاغدا، پەرىشتىلەر ئۇلاردىن: «(دىنىڭلارنىڭ ئىشىدا) قايسى ھالەتتە ئىدىڭلار؟» دەپ سورايدۇ. ئۇلار: «زېمىندا (يەنى مەككى زېمىنىدا) بىز (دىننى بەرپا قىلىشتىن) بوزەك قىلىنغان ئىدۇق» دەيدۇ. پەرىشتىلەر: «ھىجرەت قىلساڭلار اﷲ نىڭ زېمىنى كەڭرى ئەمەسمىدى؟» دەيدۇ. ئەنە شۇ (ھىجرەت قىلمىغان) لارنىڭ بارىدىغان جايى جەھەننەمدۇر. جەھەننەم نېمىدېگەن يامان جاي!'),
(591, 4, 98, 'پەقەت ئەرلەردىن، ئاياللاردىن، بالىلاردىن چارىسىز قالغان (يەنى ھىجرەت قىلىشقا چارە تاپالمىغان)، يول بىلمەيدىغان ئاجىزلار بۇنىڭدىن مۇستەسنا'),
(592, 4, 99, 'ئەنە شۇلارنى اﷲ ئەپۇ قىلغاي. اﷲ ناھايىتى ئەپۇ قىلغۇچىدۇر، ناھايىتى مەغپىرەت قىلغۇچىدۇر'),
(593, 4, 100, 'كىمكى اﷲ يولىدا ھىجرەت قىلىدىكەن، ئۇ زېمىندا چىقىش يولى ۋە كەڭچىلىك تاپىدۇ. كىمكى ئۆيىدىن اﷲ نىڭ ۋە پەيغەمبەرنىڭ تەرىپىگە ھىجرەت قىلىپ چىقسا، ئاندىن يولدا ئۆلسە، ئۇنىڭ ئەجرىنى چوقۇم اﷲ بېرىدۇ. اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر ۋە ناھايىتى مېھرىباندۇر'),
(594, 4, 101, 'سەپەرگە چىققان ۋاقتىڭلاردا كاپىرلارنىڭ ئۆزۈڭلارغا زىيان - زەخمەت يەتكۈزۈپ قويۇشىدىن قورقۇپ، نامازنى قەسرى قىلىپ (يەنى تۆت رەكئەتنى ئىككى رەكئەت قىلىپ) ئوقۇساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ. كاپىرلار سىلەرگە ھەقىقەتەن ئوپئوچۇق دۈشمەندۇر (ناماز ئوقۇۋاتقان پۇرسەتتىن پايدىلىنىپ سىلەرنى ئۆلتۈرۈۋېتىشتىن يانمايدۇ)'),
(595, 4, 102, '(ئى مۇھەممەد!) سەن مۆمىنلەر بىلەن (جىھادقا) بىللە بولۇپ ئۇلار بىلەن ناماز ئوقۇماقچى بولغىنىڭدا، ئۇلارنىڭ بىر پىرقىسى سەن بىلەن بىرلىكتە نامازغا تۇرسۇن، قوراللىرىنى (ئېھتىيات يۈزىسىدىن) يېنىدا تۇتسۇن، سەجدىگە بېرىپ بولغاندىن كېيىن ئۇلار ئارقاڭلارغا ئۆتۈپ تۇرسۇن (يەنى ناماز ئوقۇپ بولغان پىرقە ئارقاڭلاردا كۆزەتچىلىك قىلىپ تۇرسۇن، ئۇلارنىڭ ئورنىغا) ناماز ئوقۇمىغان ئىككىنچى بىر پىرقە كېلىپ سەن بىلەن بىرلىكتە (يەنى ئارقاڭدا) ناماز ئوقۇسۇن، (دۈشمەنلىرىدىن) ئېھتىياجلىق بىلەن ھوشيار تۇرسۇن. (دۈشمەنلىرى بىلەن ئۇرۇشۇشقا تەييار تۇرۇش يۈزىسىدىن) قوراللىرىنى يېنىدا تۇتسۇن، كاپىرلار سىلەرنىڭ قوراللىرىڭلاردىن، ئەشيالىرىڭلاردىن غەپلەتتە قېلىشىڭلارنى، شۇنىڭ بىلەن سىلەرگە بىردىنلا (تۇيۇقسىزدىن) ھۇجۇم قىلىشنى ئارزۇ قىلىدۇ (يەنى دۈشمەنلەرنىڭ سىلەرگە ھۇجۇم قىلىش پۇرسىتىگە ئىگە بولالماسلىقى ئۈچۈن، ھەممەڭلار بىرلا ۋاقىتتا يىغىلىپ ناماز ئوقۇماي، يۇقىرىقى ئۇسۇل بويىچە ئوقۇڭلار). ئەگەر سىلەرگە يامغۇر سەۋەبلىك ھەرەج بولىدىغان بولسا ياكى كېسەل بولساڭلار، (بۇ چاغدا) قوراللىرىڭلارنى قويۇپ قويساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ، ئېھتىياتچان ۋە ھوشيار بولۇڭلار، اﷲ ھەقىقەتەن كاپىرلار ئۈچۈن خور قىلغۇچى ئازاب تەييارلىدى'),
(596, 4, 103, 'نامازدىن فارىغ بولغىنىڭلاردا ئۆرە تۇرغان ۋە ياتقان ھالەتلىرىڭلاردا اﷲ نى ياد ئېتىڭلار (اﷲ سىلەرگە دۈشمىنىڭلارغا قارشى ياردەم بېرىشى مۇمكىن). خاتىرجەم بولغان چېغىڭلاردا نامازنى (پۈتۈن شەرتلىرى بىلەن) مۇكەممەل ئادا قىلىڭلار. شۈبھىسىزكى، ناماز مۆمىنلەرگە ۋاقتى بەلگىلەنگەن پەرز قىلىندى'),
(597, 4, 104, 'دۈشمەننىڭ ئارقىسىدىن قوغلاشقا سۇسلۇق قىلماڭلار؛ ئەگەر سىلەر قىينالساڭلار، ئۇلارمۇ خۇددى سىلەرگە ئوخشاش قىينىلىدۇ، سىلەر ئۇلار ئۈمىد قىلمايدىغان نەرسىنى (يەنى شېھىت بولۇشىنى، ساۋابنى ۋە غەلىبىنى) اﷲ تىن ئۈمىد قىلىسىلەر. اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(598, 4, 105, '(ئى مۇھەممەد!) سېنى كىشىلەر ئارىسىدا اﷲ نىڭ كۆرسەتكىنى بويىچە ھۆكۈم قىلسۇن دەپ، ساڭا ھەقىقەتەن ھەق كىتابنى نازىل قىلدۇق. خائىنلارنىڭ تەرىپىنى ئالمىغىن'),
(599, 4, 106, 'اﷲ تىن مەغپىرەت تىلىگىن. اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(600, 4, 107, '(گۇناھ قىلىش بىلەن) ئۆزلىرىگە خىيانەت قىلغۇچىلارنىڭ تەرىپىنى ئالمىغىن. خىيانەتكە ئادەتلەنگەن، گۇناھقا چۆمگەن كىشىنى اﷲ ھەقىقەتەن دوست تۇتمايدۇ'),
(601, 4, 108, 'ئۇلار (خىيانەتلىرىنى) ئىنسانلاردىن (خىجىل بولۇپ) يوشۇرىدۇ، اﷲ تىن (خىجىل بولۇپ) يوشۇرمايدۇ؛ ئۇلار اﷲ رازى بولمايدىغان سۆزنى (يەنى بوھتان چاپلاش، يالغان گۇۋاھلىق بېرىش ۋە يالغان قەسەمنى) پىلانلىغان چاغدا، اﷲ ئۇلار بىلەن بىللە ئىدى (يەنى ئۇلارنىڭ ئەھۋالىنى بىلىپ تۇراتتى، سۆزىنى ئاڭلاپ تۇراتتى). ئۇلارنىڭ قىلمىشلىرىنى اﷲ تامامەن بىلگۈچىدۇر'),
(602, 4, 109, 'سىلەر شۇنداق كىشىلەر سىلەركى، ھاياتىي دۇنيادا ئۇلارنىڭ (يەنى ئوغرى ۋە خائىنلارنىڭ) تەرىپىنى ئالدىڭلار، قىيامەت كۈنى كىم اﷲ قا قارشى ئۇلارنىڭ تەرىپىنى ئالىدۇ؟ (يەنى اﷲ نىڭ ئازابىنى كىم ئۇلاردىن دەپئى قىلىدۇ؟) ياكى كىم ئۇلارغا ھامىي بولىدۇ؟'),
(603, 4, 110, 'كىمكى بىرەر يامانلىق ياكى ئۆزىگە بىرەر زۇلۇم قىلىپ قويۇپ، ئاندىن اﷲ تىن مەغپىرەت تەلەپ قىلسا، ئۇ اﷲ نىڭ مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان ئىكەنلىكىنى كۆرىدۇ'),
(604, 4, 111, 'كىمكى (قەستەن) بىرەر گۇناھ قىلسا، ئۇنى ئۆزىنىڭ زىيىنىغا قىلىدۇ (يەنى ئۇنىڭ ۋابالىنى ئۆزى تارتىدۇ). اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(605, 4, 112, 'كىمكى بىر كىچىك گۇناھ ياكى بىر چوڭ گۇناھ قىلىپ قويۇپ، ئۇنى بىگۇناھ ئادەمگە چاپلىسا، ئۇ شۇ بوھتاننى ۋە ئوپئوچۇق بىر گۇناھنى ئۆزىگە يۈكلىۋالغان بولىدۇ'),
(606, 4, 113, '(ئى مۇھەممەد!) ئەگەر ساڭا اﷲ نىڭ پەزلى ۋە رەھمىتى بولمىسا ئىدى، ئۇلاردىن بىر گۇرۇھ ئادەم چوقۇم سېنى ئازدۇرۇشنى قەستلەيتتى؛ ئۇلار پەقەت ئۆزلىرىنى ئازدۇرىدۇ، ساڭا قىلچە زىيان يەتكۈزەلمەيدۇ. اﷲ ساڭا كىتابنى (يەنى قۇرئاننى) ۋە ھېكمەتنى (يەنى سۈننەتنى) نازىل قىلدى، ساڭا سەن بىلمىگەن نەرسىلەرنى (يەنى شەرىئەت ئەھكاملىرىنى ۋە غەيب ئىشلارنى) بىلدۈردى. اﷲ نىڭ ساڭا پەزلى چوڭدۇر'),
(607, 4, 114, 'ئۇلارنىڭ يوشۇرۇن سۆھبىتىىنىڭ تولىسىدا خەيرىيەت يوقتۇر. پەقەت سەدىقىغە ياكى ياخشىلىققا ياكى كىشىلەرنى ئەپلەشتۈرۈشكە ئەمر قىلغان كىشىلەر (نىڭ يوشۇرۇن سۆھبىتى) بۇنىڭدىن مۇستەسنا. كىمكى ئۇ (ئىش) لارنى اﷲ نىڭ رازىلىقىنى تىلەش يۈزىسىدىن قىلىدىكەن، ئۇنىڭغا بۈيۈك ئەجىر ئاتا قىلىمىز'),
(608, 4, 115, 'كىمكى ئۆزىگە (مۆجىزىلەر ئارقىلىق) توغرا يول ئېنىق بولغاندىن كېيىن، پەيغەمبەرگە (يەنى ئۇنىڭ ئەمرىگە) مۇخالىپەتچىلىك قىلىدىكەن، مۆمىنلەرنىڭ يولىدىن غەيرىيگە ئەگىشىدىكەن، ئۇنى ئۆز يولىغا قويۇپ بېرىمىز، ئۇنى (ئاخىرەتتە) جەھەننەمگە كىرگۈزىمىز، جەھەننەم نېمىدېگەن يامان جاي!'),
(609, 4, 116, 'اﷲ ئۆزىگە بىرەر نەرسىنىڭ شېرىك كەلتۈرۈلۈشىنى (يەنى مۇشرىكلىك گۇناھىنى) ئەلۋەتتە مەغپىرەت قىلمايدۇ، بۇنىڭدىن باشقىنى (اﷲ ئۆزى) خالىغان ئادەمگە مەغپىرەت قىلىدۇ، كىمكى اﷲ قا شېرىك كەلتۈرىدىكەن، ئۇ ھەقىقەتەن قاتتىق ئازغان بولىدۇ'),
(610, 4, 117, 'ئۇلار (يەنى مۇشرىكلار) اﷲ نى قويۇپ، پەقەت (ئۆزلىرى) چىشى (ناملار بىلەن ئاتىۋالغان) بۇتلارغا ئىبادەت قىلىدۇ، پەقەت اﷲ نىڭ ئىبادىتىدىن باش تارتقان شەيتانغا ئىبادەت قىلىدۇ'),
(611, 4, 118, 'اﷲ شەيتاننى رەھمىتىدىن يىراق قىلدى، شەيتان ئېيتتى: «مەن ئەلۋەتتە سېنىڭ بەندىلىرىڭدىن مۇئەييەن ساندىكىلەرنى ئىگىلەيمەن'),
(612, 4, 119, 'شۇنداقلا چوقۇم ئۇلارنى ئازدۇرىمەن، ئۇلارنى خام خىيال قىلدۇرىمەن (يەنى ھايات ئۇزۇن، قىيامەت، ھېساب ئېلىش يوق دېگەندەك باتىل ئەقىدىلەرنى كۆڭلىگە سالىمەن)، ئۇلارنى چوقۇم چاھار پايلارنىڭ (بۇتلارغا نەزەر قىلىپ بەلگە ئۈچۈن) قۇلاقلىرىنى يېرىشقا بۇيرۇيمەن، ئۇلارنى چوقۇم اﷲ نىڭ مەخلۇقاتىنى ئۆزگەرتىۋېتىشكە (يەنى قۇللارنى ئاختا قىلىش، ئۇلارغا مەڭ ئويدۇرۇشقا ئوخشاش) ئىشلارغا بۇيرۇيمەن». كىمكى اﷲ نى قويۇپ شەيتاننى دوست تۇتىدىكەن، ئۇ (دۇنيا ۋە ئاخىرەتتە) ئوپئوچۇق زىيان تارتقان بولىدۇ'),
(613, 4, 120, 'شەيتان ئۇلارغا (يالغان) ۋەدىلەرنى بېرىدۇ ۋە ئۇلارنى خام خىيالغا سالىدۇ (يەنى ئەمەلدە ئىشقا ئاشمايدىغان ئارزۇلارنى كۆڭلىگە سالىدۇ)، شەيتان ئۇلارغا پەقەت يالغاننىلا ۋەدە قىلىدۇ'),
(614, 4, 121, 'ئەنە شۇلارنىڭ بارىدىغان جايى جەھەننەمدۇر، ئۇلار جەھەننەمدىن قاچىدىغان جاي تاپالمايدۇ'),
(615, 4, 122, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتكە كىرگۈزىمىز. ئۇلار جەننەتتە مەڭگۈ قالىدۇ. اﷲ نىڭ ۋەدىسى ھەقتۇر، اﷲ تىنمۇ راست سۆزلۈك كىم بار؟ (يەنى اﷲ تىنمۇ راست سۆزلۈك ھېچ ئەھەدى بولمايدۇ)'),
(616, 4, 123, '(اﷲ ۋەدە قىلغان ساۋاب) سىلەرنىڭ قۇرۇق ئارزۇيۇڭلار ۋە ئەھلى كىتابنىڭ قۇرۇق ئارزۇسى بىلەن قولغا كەلمەيدۇ، كىمكى بىر يامانلىق قىلىدىكەن، بۇنىڭ ئۈچۈن (دۇنيادا ياكى ئاخىرەتتە) جازالىنىدۇ، ئۇ ئۆزى ئۈچۈن اﷲ تىن باشقا (اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق دوست ۋە ھېچقانداق مەدەتكار تاپالمايدۇ'),
(617, 4, 124, 'ئەر - ئاياللاردىن مۆمىن بولۇپ تۇرۇپ ياخشى ئىشلارنى قىلغانلار جەننەتكە داخىل بولىدۇ، ئۇلارغا قىلچە زۇلۇم قىلىنمايدۇ (يەنى ئۇلارنىڭ قىلغان ئەمەللىرىنىڭ ساۋابى قىلچە كېمەيتىۋېتىلمەيدۇ)'),
(618, 4, 125, 'ياخشى ئەمەللەرنى قىلغان ھالدا ئۆزىنى اﷲ قا تاپشۇرغان (يەنى اﷲ نىڭ ئەمرىگە بويسۇنۇپ ئەمەلىنى خالىس اﷲ ئۈچۈن قىلغان)، باتىل دىنلاردىن بۇرالغان ھالدا ئىبىراھىمنىڭ دىنىغا ئەگەشكەن كىشىدىن (دىنىي جەھەتتە) ياخشىراق ئادەم بارمۇ؟ اﷲ ئىبىراھىمنى دوست تۇتتى'),
(619, 4, 126, 'ئاسمانلاردىكى، زېمىندىكى شەيئىلەرىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، بەندىلىرىدۇر، مەخلۇقاتلىرىدۇر). اﷲ ھەممە نەرسىنى تولۇق بىلگۈچىدۇر'),
(620, 4, 127, '(ئى مۇھەممەد!) سەندىن ئاياللار توغرىسىدا پەتىۋا سورايدۇ، ئېيتقىنكى، ئۇلار توغرىسىدا اﷲ سىلەرگە پەتىۋا بېرىدۇ. قۇرئاندا سىلەرگە تىلاۋەت قىلىنىدىغان (ئاياللارنىڭ مىراسىغا ئائىت) ئايەتلەرمۇ پەتىۋا بېرىدۇ. شۇنداق يېتىملەر توغرىسىدا پەتىۋا بېرىدۇكى، ئۇلارنىڭ تەيىن قىلىنغان مىراسىنى بەرمەيسىلەر (يەنى مۇنداق قىلماسلىقىڭلارغا پەتىۋا بېرىدۇ)، بوزەك قىلىنغان بالىلار (نىڭ ھەققىنى بېرىشىڭلار) ۋە يېتىملەرگە ھەققانىي بولۇشىڭلار ھەققىدە پەتىۋا بېرىدۇ. (ئاياللارغا ۋە يېتىملەرگە) قانداقلا ياخشىلىق قىلساڭلار، شۈبھىسىزكى، اﷲ ئۇنى بىلىپ تۇرىدۇ (يەنى شۇنىڭغا ئاساسەن اﷲ سىلەرنى مۇكاپاتلايدۇ)'),
(621, 4, 128, 'ئەگەر بىرەر ئايال ئېرىنىڭ كۆڭۈلسىز بولۇشىدىن ياكى يۈز ئۆرۈشىدىن ئەندىشە قىلسا، ئۇلارنىڭ ئۆزئارا كېلىشىشى ھېچ گۇناھ ئەمەس، كېلىشىش (ئۈزلۈشۈپ كېتىشتىن) ياخشى. ئىنسانلارنىڭ تەبىئىتىگە بېخىللىق سىڭىپ كەتكەن، ئەگەر (ئاياللىرىڭلارغا) ياخشىلىق قىلساڭلار، (ئۇلارغا زۇلۇم قىلىشتىن) ساقلانساڭلار (اﷲ سىلەرنى مۇكاپاتلايدۇ). اﷲ ھەقىقەتەن قىلمىشىڭلاردىن خەۋەرداردۇر'),
(622, 4, 129, 'قانچە تىرىشساڭلارمۇ ئاياللىرىڭلارغا (مۇھەببەتتە ۋە دىلنىڭ مايىللىقىدا) باراۋەر مۇئامىلىدە بولۇشقا ھەرگىز قادىر بولالمايسىلەر، شۇنىڭ ئۈچۈن بىرسىگە پۈتۈنلەي مايىل بولۇپ، ئىككىنچىسىنى (ئېرى باردەكمۇ ئەمەس، يوقتەكمۇ ئەمەس) ئېسىپ قويۇلغان ئايالدەك تاشلاپ قويماڭلار، ئەگەر (بۇ جەھەتتىكى قۇسۇرلىرىڭلارنى) تۈزەتسەڭلار، (ئۇلارغا زۇلۇم قىلىشتىن) ساقلانساڭلار، اﷲ ئەلۋەتتە (سىلەرگە) مەغپىرەت قىلىدۇ، رەھىم قىلىدۇ'),
(623, 4, 130, 'ئەگەر ئىككىسى ئۈزلۈشۈپ كەتسە، اﷲ ئۆز پەزلى بىلەن ئۇلارنىڭ بىرسىنى ئىككىنچىسىدىن بىھاجەت قىلىدۇ، اﷲ (نىڭ پەزلى) كەڭدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(624, 4, 131, 'ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، مەخلۇقاتىدۇر). سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا) ۋە سىلەرگە اﷲ قا تەقۋادارلىق قىلىشنى تەۋسىيە قىلدۇق، ئەگەر كاپىر بولساڭلار (كۇفرىڭلار اﷲ قا زىيان يەتكۈزەلمەيدۇ)، ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر اﷲ نىڭ (مۈلكىدۇر، مەخلۇقاتىدۇر). اﷲ (مەخلۇقاتتىن) بىھاجەتتۇر، مەدھىيىگە لايىقتۇر'),
(625, 4, 132, 'ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، مەخلۇقاتىدۇر). ھەممە ئىشنى باشقۇرۇشقا اﷲ يېتەرلىكتۇر'),
(626, 4, 133, 'ئى ئىنسانلار! ئەگەر اﷲ خالىسا، سىلەرنى يوق قىلىپ ئورنۇڭلارغا باشقىلارنى كەلتۈرىدۇ، اﷲ بۇنىڭغا قادىردۇر'),
(627, 4, 134, 'كىمكى (قىلغان ئەمەلى بىلەن) دۇنيانىڭ نېمىتىنى تىلەيدىكەن، (ئۇنىڭغا ئېيتىپ قويغىنكى) دۇنيا ۋە ئاخىرەتنىڭ نېمىتى اﷲ نىڭ دەرگاھىدىدۇر (بەندە ئۆز پەرۋەردىگارىدىن دۇنيا ۋە ئاخىرەتنىڭ نېمىتىنى تىلىسۇن). اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلىرىنىڭ ئەمەللىرىنى) كۆرۈپ تۇرغۇچىدۇر'),
(628, 4, 135, 'ئى مۆمىنلەر! خۇدالىق ئۈچۈن گۇۋاھلىق بېرىشتە، ئۆزۈڭلارنىڭ ياكى ئاتا - ئاناڭلارنىڭ ياكى تۇغقانلىرىڭلارنىڭ زىيىنىغا (گۇۋاھلىق بېرىشكە) توغرا كەلگەن تەقدىردىمۇ، ئادالەتنى بەرپا قىلىشقا تىرىشىڭلار، (گۇۋاھلىق بېرىلگۈچى) باي بولسا (ئۇنىڭغا رىئايە قىلماستىن)، ياكى پېقىر بولسا (ئۇنىڭغا ئىچ ئاغرىتماستىن) ھامان ئادىل گۇۋاھ بولۇڭلار، اﷲ سىلەردىن ئۇلارغا يېقىندۇر (يەنى ئۇلارنىڭ مەنپەئىتىنىڭ نېمىدە بولىدىغانلىقىنى ئوبدان بىلىدۇ)، نەپسى خاھىشىڭلارغا ئەگىشىپ (ھەقىقەتتىن) بۇرۇلۇپ كەتمەڭلار. ئەگەر تىلىڭلارنى تولغۇساڭلار (يەنى گۇۋاھلىقتىكى پاكىتنى بۇرمىلىساڭلار)، ياكى گۇۋاھلىقتىن باش تارتساڭلار، مۇنداقتا اﷲ ھەقىقەتەن سىلەرنىڭ قىلمىشىڭلاردىن خەۋەردار بولۇپ تۇرغۇچىدۇر'),
(629, 4, 136, 'ئى مۆمىنلەر! اﷲ قا، اﷲ نىڭ پەيغەمبىرىگە ۋە اﷲ ئۇنىڭغا نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ۋە ئىلگىرى اﷲ نازىل قىلغان كىتابلارغا (يەنى قۇرئاندىن ئىلگىرى نازىل قىلىنغان ساماۋى كىتابلارغا) ئىمان كەلتۈرۈڭلار. كىمكى اﷲ نى، اﷲ نىڭ پەرىشتىلىرىنى، كىتابلىرىنى، پەيغەمبەرلىرىنى ۋە ئاخىرەت كۈنىنى ئىنكار قىلىدىكەن، ئۇ قاتتىق ئازغان بولىدۇ'),
(630, 4, 137, 'ئىمان ئېيتقان، ئاندىن كاپىر بولغان، ئاندىن ئىمان ئېيتقان، ئاندىن كاپىر بولغان، ئاندىن كۇفرىدا ئەزۋەيلىگەن (يەنى شۇ كۇفرى بىلەن ئۆلگەن) كىشىلەرنى اﷲ مەغپىرەت قىلمايدۇ ۋە ئۇلارنى توغرا يولغا (يەنى جەننەتكە) يېتەكلىمەيدۇ'),
(631, 4, 138, '(ئى مۇھەممەد!) مۇناپىقلارغا بېشارەت بەرگىنكى، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(632, 4, 139, 'ئۇلار مۆمىنلەرنى قويۇپ، كاپىرلارنى دوست تۇتىدۇ، ئۇلار كاپىرلارنىڭ قېشىدىن ئىززەت ۋە قۇدرەت تەلەپ قىلامدۇ؟ (يەنى كۇففارلارنى دوستلۇقىدىن ئىززەت ۋە غەلىبە تىلەمدۇ؟) ئىززەت ۋە قۇدرەتنىڭ ھەممىسى اﷲ قا خاستۇر'),
(633, 4, 140, 'اﷲ كىتابتا (يەنى قۇرئاندا) سىلەرگە نازىل قىلدىكى، سىلەر اﷲ نىڭ ئايەتلىرىنىڭ ئىنكار قىلىنىۋاتقان ۋە مەسخىرە قىلىنىۋاتقانلىقىنى ئاڭلىغان چېغىڭلاردا، ئۇلار (يەنى اﷲ نىڭ ئايەتلىرىنى مەسخىرە قىلىۋاتقان كاپىرلار) باشقا پاراڭغا كىرىشمىگىچە ئۇلار بىلەن بىللە ئولتۇرماڭلار، بولمىسا (گۇناھتا) ئۇلارغا ئوخشاش بولۇپ قالىسىلەر، اﷲ ھەقىقەتەن مۇناپىقلارنىڭ ۋە كاپىرلارنىڭ ھەممىسىنى (ئاخىرەتتە) جەھەننەمگە توپلايدۇ'),
(634, 4, 141, 'ئۇلار (يەنى مۇناپىقلار) سىلەرگە ھادىسە كېلىشىنى كۈتىدۇ، ئەگەر سىلەر اﷲ نىڭ نۇسرىتىگە ئېرىشسەڭلار، ئۇلار: «بىز سىلەر بىلەن بىللە ئەمەسمىدۇق؟ (كاپىرلاردىن ئالغان غەنىيمەتلەردىن بىزگىمۇ بېرىڭلار)» دەيدۇ. ئەگەر كاپىرلار زەپەر تاپسا، ئۇلار (كاپىرلارغا): «بىز سىلەردىن غالىب بولۇپ تۇرۇپ (سىلەرنى ئۆلتۈرمىدۇق) ئەمەسمۇ؟ سىلەرنى مۆمىنلەردىن ساقلىمىدىقمۇ؟» دەيدۇ. اﷲ قىيامەت كۈنى ئاراڭلاردا (ھەققانىي) ھۆكۈم چىقىرىدۇ، اﷲ كاپىرلارغا ھەرگىز مۆمىنلەرگە قارشى يول بەرمەيدۇ'),
(635, 4, 142, 'شۈبھىسىزكى، مۇناپىقلار اﷲ نى ئالدىماقچى بولۇشىدۇ، اﷲ ئۇلارنىڭ ئالدامچىلىقىغا يارىشا جازا بېرىدۇ، ئۇلار ناماز ئۈچۈن تۇرغاندا، خۇش ياقماسلىق بىلەن تۇرىدۇ (ساۋاب ئۈمىد قىلمايدۇ)، (نامازنى) كىشىلەرگە كۆرسىتىش ئۈچۈن ئوقۇيدۇ (يەنى رىياكارلىق قىلىدۇ)، اﷲ نى پەقەت ئازغىنا ياد ئېتىدۇ'),
(636, 4, 143, 'مۇناپىقلار كۇفرى بىلەن ئىمان ئارىسىدا ئارىسالدى بولۇپ تۇرىدۇ، نە ئۇلارغا، نە بۇلارغا مەنسۈپ ئەمەس (يەنى مۆمىنلەرگىمۇ، كاپىرلارغىمۇ مەنسۈپ ئەمەس)، كىمنى اﷲ گۇمراھ قىلغان ئىكەن، ئۇنىڭغا ھەرگىزمۇ توغرا يول تاپالمايسەن'),
(637, 4, 144, 'ئى مۆمىنلەر! مۆمىنلەرنى (دوست تۇتۇشنى) قويۇپ، كاپىرلارنى دوست تۇتماڭلار، سىلەرنىڭ زىيىنىڭلارغا (يەنى مۇناپىق ئىكەنلىكىڭلارغا) اﷲ نىڭ روشەن پاكىتى بولۇشىنى خالامسىلەر؟'),
(638, 4, 145, 'مۇناپىقلار چوقۇم دوزاخنىڭ ئەڭ ئاستىنقى قەۋىتىگە (يەنى قەئرىگە) تاشلىنىدۇ، ئۇلارغا ھەرگىزمۇ (ئازابتىن قۇتقۇزىدىغان) مەدەتكار تاپالمايسەن'),
(639, 4, 146, 'پەقەت (مۇناپىقلىقتىن) تەۋبە قىلغان، (ئەمەللىرىنى) تۈزەتكەن، اﷲ (نىڭ كىتابى) غا چىڭ يېپىشقان، دىننى اﷲ ئۈچۈن خالىس قىلغان (يەنى قىلغان ئەمەلىدىن پەقەت اﷲ نىڭ رازىلىقىنى كۆزلىگەن) كىشىلەر بۇنىڭدىن مۇستەسنا. ئەنە شۇلار مۆمىنلەر بىلەن بىللە (يەنى ئۇلارنىڭ قاتارىدا) دۇر. اﷲ مۆمىنلەرگە (ئاخىرەتتە جەننەتتىن ئىبارەت) بۈيۈك ئەجىر ئاتا قىلىدۇ'),
(640, 4, 147, 'ئەگەر (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلساڭلار ۋە ئىمان ئېيتساڭلار، اﷲ سىلەرگە ئازاب قىلىپ نېمە قىلىدۇ؟ اﷲ شۈكۈر قىلغۇچىنى مۇكاپاتلىغۇچىدۇر، ھەممىنى بىلگۈچىدۇر'),
(641, 4, 148, 'زۇلۇمغا ئۇچرىغۇچىدىن باشقا (ھەر قانداق كىشىنىڭ) ئاشكارا يامان سۆز قىلىشىنى اﷲ ياقتۇرمايدۇ (زۇلۇمغا ئۇچرىغۇچىنىڭ زالىمدىن شىكايەت قىلىشىغا ۋە ئۇنىڭغا دۇئايىبەت قىلىشىغا بولىدۇ)، اﷲ (زۇلۇمغا ئۇچرىغۇچىنىڭ دۇئاسىنى) ئاڭلىغۇچىدۇر، (زالىمنى) بىلگۈچىدۇر'),
(642, 4, 149, 'سىلەر ئاشكارا ياكى يوشۇرۇن بىر ياخشىلىقنى قىلساڭلار ياكى بىرەر يامانلىقنى كەچۈرسەڭلار (ساۋاب تاپىسىلەر). شۈبھىسىزكى، اﷲ ئەپۇ قىلغۇچىدۇر، قۇدرەتلىكتۇر'),
(643, 4, 150, 'شۈبھىسىزكى، اﷲ نى ۋە ئۇنىڭ پەيغەمبەرلىرىنى ئىنكار قىلغانلار، اﷲ بىلەن ئۇنىڭ پەيغەمبەرلىرىنىڭ ئارىسىنى (اﷲ قا ئىشىنىپ پەيغەمبەرلىرىگە ئىشەنمەيمىز دېيىش بىلەن) ئاجرىتىۋەتمەكچى بولغانلار، (پەيغەمبەرلەرنىڭ) بەزىسىگە ئىشىنىمىز، بەزىسىگە ئىشەنمەيمىز دېگۈچىلەر - ئۇنىڭ (يەنى ئىمان بىلەن كۇفرىنىڭ) ئارىسىدا (ئوتتۇرا) يول تۇتماقچى بولغانلار'),
(644, 4, 151, 'ئەنە شۇلار (ئىماننى دەۋا قىلغان تەقدىردىمۇ) راستىنلا كاپىرلاردۇر، كاپىرلارغا خورلىغۇچى ئازاب تەييارلىدۇق'),
(645, 4, 152, 'اﷲ قا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتقان، ئۇلاردىن ھېچبىرىنىڭ ئارىسىنى ئاجرىتىۋەتمىگەن (يەنى ھەممىسىگە ئىمان ئېيتقان) كىشىلەرگە اﷲ ئۇلارنىڭ (تېگىشلىك) ئەجىرلىرىنى ئاتا قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(646, 4, 153, 'ئەھلى كىتاب (يەنى يەھۇدىيلار ۋە ناسارالار) سەندىن ئۆزلىرىگە ئاسماندىن بىر كىتاب چۈشۈرۈشنى سورايدۇ، ئۇلار مۇسادىن بۇنىڭدىنمۇ چوڭراقىنى سوراپ: «بىزگە اﷲ نى ئاپئاشكارا كۆرسەتكىن» دېگەن ئىدى. شۇنىڭ بىلەن، ئۇلارنىڭ زۇلمى سەۋەبلىك ئۇلارنى چاقماق سوقتى (يەنى ئاسماندىن بىر ئوت كېلىپ ئۇلارنى ھالاك قىلدى). ئۇلار روشەن مۆجىزىلەر كەلگەندىن كېيىن موزاينى (مەبۇد) قىلىۋالدى. كېيىن بىز ئۇلارنى ئەپۇ قىلدۇق. بىز مۇساغا (ئۇنىڭ راست پەيغەمبەرلىكىنى ئىسپاتلايدىغان مۆجىزىلەردىن) روشەن پاكىت ئاتا قىلدۇق'),
(647, 4, 154, '(تەۋرات شەرىئىتىنى قوبۇل قىلمىغانلىقلىرى سەۋەبلىك) ئۇلاردىن ئەھدە ئالماق ئۈچۈن ئۈستىگە تۇر (تېغى) نى تىكلەپ قويدۇق، ئۇلارغا: «(بەيتۇلمۇقەددەس) دەرۋازىسىدىن سەجدە قىلغان ھالدا كىرىڭلار» دېدۇق ھەمدە ئۇلارغا: «شەنبىنىڭ ھۆرمىتىنى ساقلاڭلار (يەنى شەنبىدە بېلىق تۇتۇش بىلەن ھەددىڭلاردىن ئاشماڭلار)» دېدۇق. ئۇلاردىن (بۇ ھەقتە) مەھكەم ۋەدە ئالدۇق'),
(648, 4, 155, 'ئۇلار ئەھدىنى بۇزغانلىقلىرى، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى، پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى ۋە (مۇھەممەد ئەلەيھىسسالامغا) دىللىرىمىز پەردىلەنگەن (يەنى سۆزۈڭنى چۈشەنمەيدۇ) دېگەنلىكلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق)، بەلكى اﷲ ئۇلارنىڭ كۇفرى سەۋەبىدىن دىللىرىنى پېچەتلىۋەتتى، شۇڭا ئۇلارنىڭ ئازغىنىسىدىن باشقىنى ئىمان ئېيتمايدۇ'),
(649, 4, 156, 'يەنە ئۇلارنىڭ كۇفرى (يەنى ئىسا ئەلەيھىسسالامنى ئىنكار قىلغانلىقلىرى) ئۈچۈن ۋە مەريەمگە چوڭ بوھتان چاپلىغانلىقلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق)'),
(650, 4, 157, 'يەنە اﷲ نىڭ رەسۇلى ئىسا ئىبن مەريەمنى ھەقىقەتەن ئۆلتۈردۇق دېگەنلىكلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق). ۋاھالەنكى، ئۇلارنىڭ ئىسانى ئۆلتۈرگىنىمۇ يوق، دارغا ئاسقىنىمۇ يوق ۋە لېكىن ئۇلارغا شۈبھە سېلىندى (ئىسانى ئاستۇق دەپ گۇمان قىلىپ، ئىسا ئەلەيھىسسالامغا ئوخشاپ قالغان باشقا بىرسىنى ئاستى)، ئىسا توغرىسىدا ئىختىلاپ قىلىشقانلار ھەقىقەتەن ئۇنىڭ ئۆلتۈرۈلگەنلىكى (مەسلىسى) دە شۈبھىدىدۇر. ئۇلار بۇ (ئىشنىڭ ھەقىقىتى) نى بىلمەيدۇ، گۇمانغىلا ئاساسلىنىدۇ، ئۇلار ئىسانى جەزمەن ئۆلتۈرمىدى'),
(651, 4, 158, 'بەلكى اﷲ ئۇنى ئۆز تەرىپىگە كۆتۈردى (يەنى ئىسا ئەلەيھىسسالامنى اﷲ ئۇلارنىڭ شەررىدىن قۇتۇلدۇرۇپ تىرىك ھالدا ئاسمانغا ئېلىپ چىقىپ كەتتى). اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(652, 4, 159, 'ئەھلى كىتابتىن (يەنى يەھۇدىيلار ۋە ناسارالاردىن) ھەر قاندىقى جان ئۈزۈش ئالدىدا ئۇنىڭغا (يەنى ئىسانىڭ ھەق پەيغەمبەرلىكى ۋە اﷲ نىڭ بەندىسى ئىكەنلىكىگە) ئىمان ئېيتىدۇ. ئىسا قىيامەت كۈنى ئۇلار (يەھۇدىيلارنىڭ ئىسانى دارغا ئاستۇق دېگەن دەۋاسىنىڭ ۋە ناسارالارنىڭ ئىسا اﷲ نىڭ ئوغلى دېگەن دەۋاسىنىڭ يالغانلىقى) غا گۇۋاھ بولىدۇ'),
(653, 4, 160, 'يەھۇدىيلارنىڭ قىلغان ھەقسىزلىقلىرى (يەنى زۇلمى ۋە قىلغان گۇناھلىرى)، نۇرغۇن كىشىلەرنى اﷲ نىڭ يولىدىن (يەنى اﷲ نىڭ دىنىغا كىرىشتىن) توسقانلىقلىرى، چەكلەنگەن جازانىنى ئالغانلىقلىرى ۋە كىشىلەرنىڭ پۇل - ماللىرىنى ناھەق يېگەنلىكلىرى ئۈچۈن، ئۇلارغا (ئىلگىرى) ھالال قىلىنغان پاكىز نەرسىلەرنى ھارام قىلدۇق، ئۇلارنىڭ ئىچىدىن كاپىر بولغانلارغا قاتتىق ئازاب ھازىرلىدۇق'),
(654, 4, 161, 'يەھۇدىيلارنىڭ قىلغان ھەقسىزلىقلىرى (يەنى زۇلمى ۋە قىلغان گۇناھلىرى)، نۇرغۇن كىشىلەرنى اﷲ نىڭ يولىدىن (يەنى اﷲ نىڭ دىنىغا كىرىشتىن) توسقانلىقلىرى، چەكلەنگەن جازانىنى ئالغانلىقلىرى ۋە كىشىلەرنىڭ پۇل - ماللىرىنى ناھەق يېگەنلىكلىرى ئۈچۈن، ئۇلارغا (ئىلگىرى) ھالال قىلىنغان پاكىز نەرسىلەرنى ھارام قىلدۇق، ئۇلارنىڭ ئىچىدىن كاپىر بولغانلارغا قاتتىق ئازاب ھازىرلىدۇق'),
(655, 4, 162, 'ئۇلارنىڭ ئىچىدىكى بىلىمى چوڭقۇر بولغانلارغا، ساڭا نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا)، سەندىن ئىلگىرى (ئۆتكەن پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىمان ئېيتىدىغان مۆمىنلەرگە، نامازنى ئۆتىگۈچىلەرگە، زاكات بەرگۈچىلەرگە، اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقۇچىلارغا (جەننەتتە مەڭگۈ قېلىشتىن ئىبارەت) كاتتا ئەجىر ئاتا قىلىمىز'),
(656, 4, 163, '(ئى مۇھەممەد!) بىز نۇھقا ۋە ئۇنىڭدىن كېيىنكى پەيغەمبەرلەرگە ۋەھيى قىلغاندەك، ھەقىقەتەن ساڭا ۋەھيى قىلدۇق ھەمدە ئىبىراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا، يەئقۇبنىڭ ئەۋلادلىرىغا، ئىساغا، ئەييۇبقا، يۇنۇسقا، ھارۇنغا، سۇلايمانغا ۋەھيى قىلدۇق، داۋۇدقا زەبۇرنى ئاتا قىلدۇق'),
(657, 4, 164, 'بىز نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدا ئىلگىرى (ئى مۇھەممەد!) ساڭا بايان قىلغانلىرىمىزمۇ بار، ساڭا بايان قىلمىغانلىرىمىزمۇ بار، اﷲ مۇساغا (بىۋاسىتە) سۆز قىلدى'),
(658, 4, 165, 'ئەۋەتىلگەن پەيغەمبەرلەردىن كېيىن اﷲ نىڭ ئالدىدا كىشىلەر (يەنى پەيغەمبەر ئەۋەتىلگەن بولسا ئەلۋەتتە ئىمان ئېيتاتتىم ۋە ئىتائەت قىلاتتىم دېگۈچىلەر) گە باھانە بولۇپ قالماسلىقى ئۈچۈن، (ئىتائەت قىلغۇچىلارغا جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (ئاسىيلىق قىلغۇچىلارنى دوزاختىن) ئاگاھلاندۇرغۇچى پەيغەمبەرلەرنى ئەۋەتتۇق، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(659, 4, 166, '(يەھۇدىيلار ۋە ناسارالار سېنىڭ پەيغەمبەرلىكىڭگە گۇۋاھلىق بەرمىسە گۇناھى ئۆزىگە) لېكىن اﷲ ساڭا نازىل قىلغان قۇرئان ئارقىلىق (پەيغەمبەرلىكىڭگە) گۇۋاھلىق بېرىدۇ، اﷲ قۇرئاننى (باشقىلار بىلمەيدىغان ئۆزىگە خاس) ئىلمى بىلەن نازىل قىلدى. پەرىشتىلەرمۇ (اﷲ ساڭا نازىل قىلغان قۇرئانغا ۋە پەيغەمبەرلىكىڭگە) گۇۋاھلىق بېرىدۇ، (باشقىلار گۇۋاھلىق بەرمىگەندىمۇ) اﷲ نىڭ گۇۋاھلىقى يېتەرلىكتۇر'),
(660, 4, 167, 'شۈبھىسىزكى، كاپىر بولغانلار ۋە (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلار ھەقىقەتەن قاتتىق ئازدى'),
(661, 4, 168, 'شۈبھىسىزكى، كاپىر بولغانلار ۋە زۇلۇم قىلغانلارنى اﷲ مەغپىرەت قىلمايدۇ، توغرا يولغىمۇ (يەنى جەننەتنىڭ يولىغىمۇ) باشلىمايدۇ'),
(662, 4, 169, '(ئۇلارنى) پەقەت جەھەننەمنىڭ يولىغا باشلايدۇ، ئۇلار جەھەننەمدە مەڭگۈ قالىدۇ، بۇ (يەنى ئۇلارنى جەھەننەمدە مەڭگۈ قالدۇرۇش) اﷲ قا ئاسان'),
(663, 4, 170, 'ئى ئىنسانلار! پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) سىلەرگە پەرۋەردىگارىڭلاردىن ھەق (دىن) ئېلىپ كەلدى، ئىمان ئېيتىڭلاركى، (بۇ) سىلەر ئۈچۈن پايدىلىق، ئەگەر ئىنكار قىلساڭلار (يەنى كۇفرىنى داۋاملاشتۇرساڭلار) (اﷲ سىلەردىن بىھاجەت). چۈنكى ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مەخلۇقاتىدۇر، مۈلكىدۇر). اﷲ (بەندىلىرىنىڭ ئەھۋالىنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(664, 4, 171, 'ئى ئەھلى كىتاب! (يەنى ناسارالار جامائەسى) دىنىڭلاردا ھەددىدىن ئاشماڭلار، اﷲ نىڭ شەنىگە ھەق (سۆز) دىن باشقىنى ئېيتماڭلار (يەنى اﷲ قا شېرىكى ۋە بالىسى بولۇشتىن پاك دەپ ئېتىقاد قىلىڭلار). مەسىھ ئىسا - مەريەمنىڭ ئوغلى، پەقەت اﷲ نىڭ رەسۇلىدۇر (سىلەر گۇمان قىلغاندەك اﷲ نىڭ ئوغلى ئەمەستۇر)، مەريەمگە اﷲ نىڭ ئىلقا قىلغان كەلىمىسىدۇر (يەنى ئاتىنىڭ ۋاسىتىسىز، اﷲ نىڭ «ۋۇجۇدقا كەل» دېگەن سۆزىدىن يارىتىلغاندۇر)، اﷲ تەرىپىدىن كەلگەن بىر روھتۇر، اﷲ قا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتىڭلار، (اﷲ، ئىسا، مەريەمدىن ئىبارەت) ئۈچتۇر دېمەڭلار، (اﷲ ئۈچ دەيدىغان ئېتىقادتىن) قايتىڭلار، (بۇ) سىلەرگە پايدىلىقتۇر، اﷲ پەقەت بىر ئىلاھتۇر، بالىسى بولۇشتىن اﷲ پاكتۇر، ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، مەخلۇقاتىدۇر)، اﷲ يېتەرلىك ھامىيدۇر'),
(665, 4, 172, 'مەسىھمۇ، (اﷲ قا) يېقىن پەرىشتىلەرمۇ اﷲ قا بەندە بولۇشتىن ھەرگىز باش تارتمايدۇ، كىملەركى اﷲ قا بەندىچىلىك قىلىشتىن باش تارتىدىكەن ۋە كۆرەڭلەپ كېتىدىكەن، (بىلسۇنكى) اﷲ ئۇلارنىڭ ھەممىسىنى (قىيامەت كۈنى ھېساب ئېلىش ۋە جازالاش ئۈچۈن) ئۆز دەرگاھىغا توپلايدۇ'),
(666, 4, 173, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كەلسەك، اﷲ ئۇلارنىڭ (ئەمەللىرىنىڭ) ئەجرىنى تولۇق بېرىدۇ، نېمىتىنى ئۇلارغا بېرىدۇ، (اﷲ قا قۇلچىلىق قىلىشتىن) باش تارتقان ۋە كۆرەڭلەپ كەتكەنلەرگە كەلسەك، اﷲ ئۇلارغا قاتتىق ئازاب قىلىدۇ. ئۇلار ئۆزلىرى ئۈچۈن اﷲ تىن باشقا (اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق دوست ۋە ھېچقانداق مەدەتكار تاپالمايدۇ'),
(667, 4, 174, 'ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن (شانلىق مۆجىزىلەر بىلەن كۈچلەندۈرۈلگەن مۇھەممەد ئەلەيھىسسالامدىن ئىبارەت) ئوچۇق دەلىل كەلدى، سىلەرگە روشەن نۇرنى (يەنى قۇرئاننى) نازىل قىلدۇق'),
(668, 4, 175, 'اﷲ قا ئىمان ئېيتقان ۋە ئۇنىڭغا (يەنى قۇرئانغا) چىڭ يېپىشقانلارغا كەلسەك، اﷲ ئۇلارنى ئۆز رەھمىتىگە ۋە نېمىتىگە (يەنى جەننىتىگە) داخىل قىلىدۇ ۋە ئۇلارنى توغرا يولغا باشلايدۇ'),
(669, 4, 176, '(ئى مۇھەممەد!) سەندىن (ئاتا - ئانىسى، بالىسى يوق مېيىتنىڭ مىراسى توغرۇلۇق) پەتىۋا سورىشىدۇ، ئېيتقىنكى، «اﷲ ئاتا - ئانىسى، بالىسى يوق كىشى (قالدۇرغان مىراس) توغرىسىدا پەتىۋا بېرىدۇ، ئەگەر بىر كىشى ۋاپات بولسا، بالىسى بولمىسا، پەقەت بىر ھەمشىرىسى بولسا، ئۇ تەرەكىنىڭ يېرىمىنى ئالىدۇ، ئەگەر ئۇ ئايالنىڭ بالىسى بولمىسا، ئەر بىر تۇغقىنى ئۇنىڭ ۋارىسى بولىدۇ (يەنى مىراسنىڭ ھەممىسىنى ئالىدۇ)، ئەگەر ئىككى ھەمشىرىسى بولسا، ئۇلار تەرەكىنىڭ ئۈچتىن ئىككى ھەسسىسىنى ئالىدۇ، ئەگەر ۋارىسلار ھەم ئەر، ھەم ئايال بىر تۇغقانلار بولسا (ئۇلارنىڭ ئىچىدىكى) بىر ئەرگە ئىككى ئايالنىڭ ھەسسىسى بېرىلىدۇ، ئاداشماسلىقىڭلار ئۈچۈن اﷲ سىلەرگە (شەرىئەت ئەھكاملىرىنى) بايان قىلىپ بېرىدۇ. اﷲ ھەممە نەرسىنى بىلگۈچىدۇر»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ئى مۆمىنلەر! ئەھدىلەرگە (يەنى اﷲ بىلەن سىلەرنىڭ ئاراڭلاردىكى ۋە سىلەرنىڭ ئۆز ئاراڭلاردىكى ئەھدىلەرگە) ۋاپا قىلىڭلار. سىلەرگە (بۇ سۈرىدە تۆۋەندە) ئوقۇپ بېرىلىدىغانلاردىن باشقا، ھايۋانلار، (يەنى تۆگە، كالا، قويلار) نىڭ ھەممىسىنى (زەبھى قىلىنغاندىن كېيىن يېيىش) ھالال قىلىندى. لېكىن سىلەر ئېھرام (ياكى ھەرەم) دە بولغىنىڭلاردا شىكارنى ھالال سانىماڭلار، شۈبھىسىزكى، اﷲ (ھالال - ھارام توغرۇلۇق) خالىغان نەرسىنى ھۆكۈم قىلىدۇ'),
(671, 5, 2, 'ئى مۆمىنلەر! اﷲ نىڭ دىنىنىڭ ئالامەتلىرىنى (يەنى ھەجدە تائەتنىڭ بەلگىسى ھېسابلانغان ئىشلارنى) (تەرك ئېتىش بىلەن) شەھرى ھەرەم (دا ئۇرۇش قىلىش) نى، ھەدىيىنى (يەنى كەبىگە ئاتالغان قۇربانلىققا دەخلى - تەرۇز قىلىشنى)، قەلادە (يەنى ھەجدە قۇربانلىق قىلىنىدىغان مال ئىكەنلىكىنى بىلدۈرۈش ئۈچۈن ھايۋانغا ئېسىپ قويۇلغان نەرسە) لەرگە (چېقىلىشنى)، پەرۋەردىگارىنىڭ پەزلىنى ۋە رازىلىقىنى تىلەپ بەيتۇللاھقا (ھەج ئۈچۈن ياكى ئۆمرە ئۈچۈن) كەلگۈچىلەرگە (چېقىلىشنى) ھالال سانىماڭلار، ئىھرامدىن چىققان چېغىڭلاردا شىكار قىلساڭلار بولىدۇ، بىرەر قەۋم (يەنى مۇشرىكلار) نىڭ سىلەرنى مەسجىدى ھەرەم (زىيارىتى) دىن توسقانلىقتىن ئىبارەت دۈشمەنلىكى ھەرگىزمۇ سىلەرنىڭ ئۇلارغا چېقىلىشىڭلارغا سەۋەبچى بولمىسۇن، ياخشى ئىشقا ۋە تەقۋادارلىققا ياردەملىشىڭلار، گۇناھقا ۋە زۇلۇمغا ياردەملەشمەڭلار، اﷲ (نىڭ ئازابى) دىن قورقۇڭلار، اﷲ نىڭ ئازابى ھەقىقەتەن قاتتىق'),
(672, 5, 3, 'سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋان، قان، چوشقا گۆشى، اﷲ تىن غەيرىينىڭ (يەنى بۇتلارنىڭ) نامى تىلغا ئېلىنىپ بوغۇزلانغان ھايۋان، بوغۇپ ئۆلتۈرۈلگەن ھايۋان، ئۇرۇپ ئۆلتۈرۈلگەن ھايۋان، (ئېگىزدىن) يىقىلىپ ئۆلگەن ھايۋان، (ھايۋانلار تەرىپىدىن) ئۈسۈپ ئۆلتۈرۈلگەن ھايۋان، يىرتقۇچ ھايۋانلار يېرىپ ئۆلتۈرۈپ يېگەن ھايۋان (نىڭ گۆشىنى يېيىش) ھارام قىلىندى. لېكىن (يۇقىرىقى بەش تۈرلۈك ھايۋاندىن جېنى چىقمىغان چاغدا) بوغۇزلىغانلىرىڭلار ھالال بولىدۇ ھەمدە بۇتلارغا ئېلىپ بېرىلىپ ئۇنىڭ يېنىدا بوغۇزلانغان ھايۋانلار ھارام قىلىندى. ئەزلام (يەنى جاھىلىيەت دەۋرىدە پال ئۈچۈن ئىشلىتىدىغان ئۈچ پارچە ياغاچ) بىلەن پال سېلىشىڭلار ھارام قىلىندى، بۇ گۇناھتۇر، كاپىرلار بۈگۈن سىلەرنىڭ دىنىڭلار (نى يوقىتىش) دىن ئۈمىدىنى ئۈزدى، ئۇلاردىن قورقماڭلار، مەندىن قورقۇڭلار. بۈگۈن سىلەرنىڭ دىنىڭلارنى پۈتۈن قىلدىم، سىلەرگە نېمىتىمنى تاماملىدىم، ئىسلام دىنىنى سىلەرنىڭ دىنىڭلار بولۇشقا تاللىدىم، كىمكى ئاچلىقتا ئىلاجىسىزلىقتىن، گۇناھنى مەقسەت قىلماستىن (ھارام قىلىنغان نەرسىلەرنى يېسە گۇناھ بولمايدۇ)، اﷲ مەغپىرەت قىلغۇچىدۇر، بەندىلىرىگە ناھايىتى مېھرىباندۇر'),
(673, 5, 4, '(ئى مۇھەممەد!) ئۇلار سەندىن ئۆزلىرىگە (يېمەك - ئىچمەكتىن) نېمىلەرنىڭ ھالال قىلىنغانلىقىنى سورايدۇ، ئېيتقىنكى، «سىلەرگە پاك نەرسىلەر ۋە (ئىت، قارچىغا، قۇشقا ئوخشاش) شىكارغا ئۆگىتىلگەن، سىلەر اﷲ نىڭ ئۆزۈڭلارغا ئۆگەتكىنى بويىچە ئۆگەتكەن ئوۋچى جانىۋارلارنىڭ شىكار قىلغان نەرسىلىرى ھالال قىلىندى، ئۇلارنىڭ سىلەر ئۈچۈن ئوۋلىغان نەرسىلىرىدىن يەڭلار (ئوۋچى جانىۋارلارنى شىكارغا قويۇپ بەرگەن چېغىڭلاردا) اﷲ نىڭ نامىنى ياد قىلىڭلار (يەنى بىسمىللا دەڭلار)، اﷲ (نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىش) تىن ساقلىنىڭلار، اﷲ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر»'),
(674, 5, 5, 'بۈگۈن سىلەرگە پاك نەرسىلەر ھالال قىلىندى، كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) نىڭ تامىقى سىلەرگە ھالالدۇر ۋە سىلەرنىڭ تامىقىڭلار ئۇلارغا ھالالدۇر، مۆمىنلەردىن بولغان ئەفىفە ئاياللارنىڭ، سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن بولغان ئەفىفە ئاياللارنىڭ مەھرىنى بەرسەڭلار، ئىپپەتلىك بولغىنىڭلار ۋە ئاشكارا - يوشۇرۇن پاھىشىنى كۆزلىمىگىنىڭلار ھالدا (ئۇلارنى ئالساڭلار)، ئۇلار سىلەرگە ھالالدۇر، كىمكى ئىماندىن يېنىۋالىدىكەن (ياكى شەرىئەت ئەھكاملىرىنى ئىنكار قىلىدىكەن)، ئۇنىڭ ئەمەلى (نىڭ ساۋابى) يوققا چىقىرىلىدۇ، ئاخىرەتتە ئۇ زىيان تارتقۇچىلاردىن بولىدۇ'),
(675, 5, 6, 'ئى مۆمىنلەر! (سىلەر تاھارەتسىز بولۇپ) ناماز (ئوقۇماقچى بولۇپ) تۇرغىنىڭلاردا، يۈزۈڭلارنى يۇيۇڭلار، قولۇڭلارنى جەينىكىڭلار بىلەن قوشۇپ يۇيۇڭلار، بېشىڭلارغا مەسھى قىلىڭلار، پۇتۇڭلارنى ئوشۇقۇڭلار بىلەن قوشۇپ يۇيۇڭلار، ئەگەر جۇنۇپ بولساڭلار پاكلىنىڭلار (يەنى غۇسلى قىلىڭلار)، ئەگەر كېسەل بولساڭلار (سۇ زىيان قىلىدىغان بولسا)، ياكى سەپەر ئۈستىدە بولۇپ (سۇ تاپالمىساڭلار)، ياكى سىلەرنىڭ بىرسىڭلار ھاجەتخانىدىن كەلگەن (يەنى تاھارەت سۇندۇرغان) بولساڭلار، ياكى ئاياللىرىڭلار بىلەن يېقىنچىلىق قىلغاندىن كېيىن (غۇسلى تاھارەت ئۈچۈن) سۇ تاپالمىساڭلار، پاك تۇپراقتا تەيەممۇم قىلىڭلار، ئۇنىڭ بىلەن يۈزۈڭلارغا، قولۇڭلارغا مەسھى قىلىڭلار، اﷲ سىلەرگە سىلەرگە مۇشەققەتنى خالىمايدۇ، لېكىن اﷲ شۈكۈر قىلىشىڭلار ئۈچۈن سىلەرنى پاك قىلىشنى، (ئىسلام شەرىئىتىنى بايان قىلىش بىلەن) نېمىتىنى سىلەرگە تاماملاشنى خالايدۇ'),
(676, 5, 7, '(ئى مۆمىنلەر!) اﷲ نىڭ سىلەرگە بەرگەن (ئۇلۇغ) نېمىتىنى ۋە سىلەر بىلەن قىلغان مۇئاھىدىسىنى ئەسلەڭلار، ئەينى زاماندا سىلەر: «ئاڭلىدۇق ۋە ئىتائەت قىلدۇق» دېگەن ئىدىڭلار، اﷲ تىن قورقۇڭلار (يەنى اﷲ قا بەرگەن ئەھدىنى بۇزۇشتىن ساقلىنىڭلار). اﷲ ھەقىقەتەن (بەندىلىرىنىڭ) دىللىرىدىكى سىرلىرىنى بىلگۈچىدۇر'),
(677, 5, 8, 'ئى مۆمىنلەر! اﷲ نىڭ (ھەقلىرىنى) ئادا قىلىشقا تىرىشىڭلار، ئادىللىق بىلەن گۇۋاھلىق بېرىڭلار، بىرەر قەۋمگە بولغان ئۆچمەنلىكىڭلار (ئۇلارغا) ئادىل بولماسلىقىڭلارغا سەۋەب بولمىسۇن، (دۈشمىنىڭلارغا) ئادىل بولۇڭلار، بۇ (يەنى ئۇلارغا ئۆچمەن تۇرۇپ ئادىل بولۇشۇڭلار) تەقۋادارلىققا ئەڭ يېقىندۇر، اﷲ تىن قورقۇڭلار، اﷲ ھەقىقەتەن قىلمىشىڭلاردىن خەۋەرداردۇر'),
(678, 5, 9, 'اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا مەغپىرەت ۋە كاتتا ساۋاب ۋەدە قىلدى'),
(679, 5, 10, 'كاپىر بولغان ۋە بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقارغان كىشىلەر ئەھلى دوزاختۇر'),
(680, 5, 11, 'ئى مۆمىنلەر! اﷲ نىڭ سىلەرگە بەرگەن (يەنى سىلەرنى دۈشمىنىڭلاردىن ساقلىغانلىق) نېمىتىنى ئەسلەڭلار، ئەينى زاماندا بىر جامائە سىلەرگە قوللىرىنى ئۇزاتماقچى (يەنى سىلەرنى ئۆلتۈرمەكچى) بولغان ئىدى، اﷲ ئۇلارنىڭ قوللىرىنى سىلەردىن توستى (يەنى سىلەرنىڭ ئۇلارنى زىيانكەشلىك قىلىشىدىن ساقلىدى). اﷲ تىن (اﷲ نىڭ ئەمرىنى ئورۇنلاش، مەنئى قىلغان ئىشلىرىدىن چەكلىنىش بىلەن) قورقۇڭلار، مۆمىنلەر اﷲ قا تەۋەككۈل قىلسۇن'),
(681, 5, 12, 'شۈبھىسىزكى، اﷲ ئىسرائىل ئەۋلادىدىن مەھكەم ئەھدە ئالدى، ئۇلارنىڭ ئارىسىدىن (يەنى ئون ئىككى ئايماقنىڭ ھەر بىرسىدىن بىردىن) ئون ئىككى باشلىقنى (كېپىللىك ئۈچۈن جەببارلارغا يەنى ئەمالىقەلەرگە) ئەۋەتتۇق ھەمدە اﷲ: «مەن سىلەرگە ياردەم بېرىمەن، (ئى ئىسرائىل ئەۋلادى! اﷲ بىلەن قەسەمكى) ئەگەر سىلەر نامازنى (تەئدىل ئەركان بىلەن) ئۆتىسەڭلار، زاكات بەرسەڭلار، پەيغەمبەرلىرىمگە ئىمان ئېيتساڭلار ۋە ئۇلارغا ياردەم بەرسەڭلار، اﷲ قا قەرزى ھەسەنە بەرسەڭلار (يەنى اﷲ نىڭ يولىدا پۇل - مېلىڭلارنى سەرپ قىلساڭلار)، ئەلۋەتتە سىلەرنىڭ گۇناھىڭلارنى يوققا چىقىرىمەن، سىلەرنى ئەلۋەتتە ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمەن. شۇنىڭدىن (يەنى مەھكەم ئەھدىدىن) كېيىن سىلەردىن كىمكى (ئەمر قىلغان نەرسىلىرىمنى) ئىنكار قىلىدىكەن، ئۇ ئەلۋەتتە توغرا يولدىن ئازغان بولىدۇ» دېدى'),
(682, 5, 13, 'ئەھدىنى بۇزغانلىقلىرى ئۈچۈن ئۇلارنى رەھمىتىمىزدىن يىراق قىلدۇق، ئۇلارنىڭ دىللىرىنى (ئىماننى قوبۇل قىلىشقا يۇمشىمايدىغان دەرىجىدە) قاتتىق قىلدۇق، ئۇلار (تەۋراتنىڭ) سۆزلىرىنى ئۆزگەرتىۋېتىدۇ، ئۇلار (تەۋراتتا) ئۆزلىرى ئۈچۈن قىلىنغان نەسىھەتنىڭ بىر قىسمىنى ئۇنتۇدى. (ئى مۇھەممەد!) ئۇلارنىڭ ئازغىنىسىدىن باشقىسىنىڭ خىيانەت قىلغانلىقىنى بايقاپ تۇرىسەن، (ئەگەر تەۋبە قىلسا) ئۇلارنى ئەپۇ قىلغىن، (ئەگەر ئىمان ئېيتسا) ئۇلارنى كەچۈرگىن، اﷲ ھەقىقەتەن ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ'),
(683, 5, 14, 'بىز ناسارامىز دېگەنلەردىنمۇ (اﷲ نى بىر دەپ تونۇشقا ۋە مۇھەممەد ئەلەيھىسسالامغا ئىمان ئىېيتىشقا) مەھكەم ئەھدە ئالدۇق، ئۇلار (ئىنجىلدا) ئۆزلىرىگە قىلىنغان نەسىھەتنىڭ بىر قىسمىنى ئۇنتۇدى، (بۇنىڭ جازاسى ئۈچۈن) ئۇلارنىڭ ئارىسىغا قىيامەتكىچە ئاداۋەت ۋە دۈشمەنلىك سالدۇق، (شۇ چاغدا) اﷲ ئۇلارغا قىلمىشلىرىنى ئېيتىپ بېرىدۇ (يەنى ئۇلار ئۆزلىرىنىڭ قەبىھ ئەمەللىرىنىڭ جازاسىنى تارتىدۇ)'),
(684, 5, 15, 'ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) سىلەرگە سىلەر كىتابتا (ئىنجىلدا ۋە تەۋراتتا) يوشۇرغان نۇرغۇن نەرسىلەرنى بايان قىلىپ بېرىدىغان ۋە (يوشۇرغان نەرسىلەردىن) نۇرغۇنىنى كەچۈرىدىغان (ئاشكارىلاپ سىلەرنى پاش قىلمايدىغان) رەسۇلىمىز كەلدى، سىلەرگە اﷲ تەرىپىدىن نۇر (يەنى مۇھەممەد ئەلەيھىسسالام) ۋە روشەن كىتاب (يەنى قۇرئان) كەلدى'),
(685, 5, 16, 'اﷲ شۇ كىتاب (يەنى قۇرئان) ئارقىلىق رازىلىقىنى تىلىگەنلەرنى سالامەتلىك يوللىرى (اﷲ نىڭ شەرىئىتى ۋە ئۇنىڭ ئەھكاملىرى) غا يېتەكلەيدۇ، ئىرادىسى بويىچە ئۇلارنى (كۇفرى) قاراڭغۇلۇقىدىن (ئىماننىڭ) نۇرىغا چىقىرىدۇ ۋە ئۇلارنى توغرا يولغا ھىدايەت قىلىدۇ'),
(686, 5, 17, 'مەريەم ئوغلى مەسىھ (يەنى ئىسا) اﷲ دۇر دېگەن كىشىلەر (يەنى ناسارالارنىڭ بىر پىرقىسى) ھەقىقەتەن كاپىر بولدى. (ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر اﷲ مەريەم ئوغلى مەسىھنى، مەسىھنىڭ ئانىسىنى (يەنى مەريەمنى) ۋە يەر يۈزىدىكى بارلىق كىشىلەرنى ھالاك قىلىشنى ئىرادە قىلسا، اﷲ (نىڭ ئىرادىسى) دىن بىرەر نەرسىنى توسۇشقا كىم قادىر بولالايدۇ؟» ئاسمانلارنىڭ ۋە زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ خالىغىنىنى خەلق ئېتىدۇ، اﷲ ھەر نەرسىگە قادىردۇر'),
(687, 5, 18, 'يەھۇدىيلار ۋە ناسارالار: «بىز اﷲ نىڭ ئوغۇللىرىدۇرمىز (يەنى ئاتا - بالىدەك يېقىنمىز) ۋە (بىز اﷲ نىڭ دىنىدا بولغاچقا) دوستلىرىدۇرمىز» دېدى. ئېيتقىنكى، «(ئۇنداق بولسا) اﷲ نېمە ئۈچۈن سىلەرگە گۇناھىڭلار تۈپەيلىدىن ئازاب قىلىدۇ؟» بەلكى سىلەر باشقا كىشىلەرگە ئوخشاش اﷲ ياراتقان ئىنسانسىلەر، اﷲ خالىغان ئادەمگە مەغپىرەت قىلىدۇ؛ خالىغان ئادەمگە ئازاب قىلىدۇ. ئاسمانلارنىڭ، يەر يۈزىنىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقى اﷲ قا خاستۇر، ئەڭ ئاخىر بارىدىغان جاي اﷲ نىڭ دەرگاھىدۇر'),
(688, 5, 19, 'ئى ئەھلى كىتاب! (يەنى يەھۇدىي ۋە ناسارالار) بىزگە (ساۋاب بىلەن) خۇش خەۋەر بەرگۈچى، (ئازابتىن) ئاگاھلاندۇرغۇچى كەلمىدى دېمەسلىكىڭلار ئۈچۈن، پەيغەمبەرلەر ئۈزۈلۈپ قالغان بىر زاماندا سىلەرگە (شەرىئەتنى ۋە ئۇنىڭ ئەھكاملىرىنى) بايان قىلىپ بېرىدىغان رەسۇلىمىز كەلدى، ھەقىقەتەن سىلەرگە خۇش خەۋەر بەرگۈچى ۋە ئاگاھلاندۇرغۇچى (يەنى مۇھەممەد ئەلەيھىسسالام) كەلدى (ئەمدى سىلەرگە ئۆزرە قالمىدى). اﷲ ھەر نەرسىگە قادىردۇر'),
(689, 5, 20, 'ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمگە ئېيتتى: «ئى قەۋمىم! اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ياد قىلىڭلار، اﷲ ئۆز ۋاقتىدا ئىچىڭلاردا (سىلەرنى توغرا دىنغا يېتەكلەيدىغان) نۇرغۇن پەيغەمبەرلەرنى بارلىققا كەلتۈردى. سىلەردىن پادىشاھلار قىلدى (يەنى ئۆزۈڭلارنى ئۆزۈڭلارغا خوجا قىلدى). سىلەرگە جاھان ئەھلىدىن ھېچ كىشىگە بەرمىگەن (دەريانى يېرىپ ئوتتۇرىسىدىن يول ئېچىپ بېرىش، بۇلۇتلارنى سايىۋەن قىلىپ بېرىش، ئاسماندىن يېمەكلىك چۈشۈرۈپ بېرىش قاتارلىق) نېمەتلەرنى بەردى'),
(690, 5, 21, 'ئى قەۋمىم! اﷲ سىلەرگە تەقدىر قىلغان (يەنى سىلەرنى كىرىشكە ئەمر قىلغان) مۇقەددەس زېمىنغا (يەنى بەيتۇلمۇقەددەسكە) كىرىڭلار، ئارقىڭلارغا چېكىنمەڭلار، بولمىسا (ئىككىلا دۇنيادا) زىيان تارتقۇچىلاردىن بولۇپ قالىسىلەر»'),
(691, 5, 22, 'ئۇلار: «ئى مۇسا! مۇقەددەس زېمىندا كۈچلۈك (يەنى جاسامەتلىك، قامەتلىك) بىر قەۋم بار (ئۇلار ئاد قەۋمىنىڭ قالدۇقى بولغان ئەمالىقەلەر)، ئۇلار چىقىپ كەتمىگىچە بىز ئۇ يەرگە ھەرگىز كىرمەيمىز، ئۇلار ئۇيەردىن چىقىپ كەتسە چوقۇم كىرىمىز» دېدى'),
(692, 5, 23, 'اﷲ تىن قورققۇچىلاردىن بولغان، اﷲ نىڭ (ئىمان تائەتتىن ئىبارەت) نېمىتىگە ئېرىشكەن ئىككى ئادەم (يەنى كالب ئىبن يەفۇننە بىلەن يۇشە ئىبن نۇن) ئۇلارغا: «(شەھەرنىڭ) دەرۋازىسىدىن ھۇجۇم قىلىپ كىرىڭلار، (شەھەرنىڭ) دەرۋازىسىدىن ھۇجۇم قىلىپ كىرسەڭلار، چوقۇم غەلىبە قىلىسىلەر، ئەگەر (اﷲ نىڭ ۋەدە قىلغان ياردىمىگە) ئىشەنسەڭلار، اﷲ قا تەۋەككۈل قىلىڭلار» دېدى'),
(693, 5, 24, 'ئۇلار: «ئى مۇسا! مادامىكى، ئۇلار مۇقەددەس يەردە ئىكەن، بىز ھەرگىز ئۇ يەرگە كىرمەيمىز، سەن پەرۋەردىگارىڭ بىلەن بىللە بېرىپ ئىككىڭلار ئۇرۇشۇڭلار، بىز بۇ يەردە ئولتۇرۇپ تۇرايلى» دېدى'),
(694, 5, 25, 'مۇسا دېدى: «پەرۋەردىگارىم! مەن پەقەت ئۆزۈمگە ۋە قېرىنداشلىرىمغىلا ئىگىمەن، سەن بىز بىلەن (ئىتائىتىڭدىن باش تارتقان) قەۋمنىڭ ئارىسىنى ئايرىۋەتكىن’'),
(695, 5, 26, 'اﷲ دېدى: «ئۇلارنىڭ مۇقەددەس يەرگە كىرىشى 40 يىلغىچە ھارام قىلىندى، (بۇ جەرياندا) ئۇلار زېمىندا ئادىشىپ يۈرىدۇ. پاسىق قەۋم ئۈچۈن قايغۇرمىغىن»'),
(696, 5, 27, '(ئى مۇھەممەد!) ئۇلارغا (يەنى ئەھلى كىتابقا ۋە قەۋمڭگە) ئادەمنىڭ ئىككى ئوغلى (يەنى ھابىل بىلەن قابىل) نىڭ ھېكايىسىنى راستلىق بىلەن ئوقۇپ بەرگىن، ئۆز ۋاقتىدا ئۇلار (يەنى ھابىل بىلەن قابىل) ئىككىيلەن قۇربانلىق قىلغان ئىدى. بىرسى (يەنى ھابىل) نىڭ قوبۇل بولدى، يەنە بىرسى (يەنى قابىل) نىڭ قوبۇل بولمىدى. (قابىل ھابىلغا) «مەن سېنى چوقۇم ئۆلتۈرىمەن» دېدى، ھابىل نېمىشقا دېۋىدى، قابىل: «اﷲ سېنىڭ قۇربانلىقىڭنى قوبۇل قىلىپ، مېنىڭكىنى قوبۇل قىلمىدى» دېدى. (ھابىل) ئېيتتى: «اﷲ پەقەت تەقۋادارلارنىڭ (قۇربانلىقىنى) قوبۇل قىلىدۇ»'),
(697, 5, 28, 'ئەگەر سەن مېنى ئۆلتۈرۈشكە قولۇڭنى سوزىدىغان بولساڭ، مەن سېنى ئۆلتۈرۈشكە قولۇمنى سوزمايمەن، مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى اﷲ تىن قورقىمەن'),
(698, 5, 29, 'مەن ھەقىقەتەن سەن بىلەن بولغان گۇناھنى (يەنى مېنى ئۆلتۈرگەنلىك گۇناھىڭنى) ۋە سېنىڭ (ئىلگىرىكى) گۇناھىڭنى (يەنى ئاتاڭ ئادەمنىڭ ئەمرىگە ئاسىيلىق قىلغانلىق گۇناھىڭنى) ئۈستۈڭگە ئېلىپ ئەھلى دوزاخلاردىن بولۇشۇڭلارنى تىلەيمەن، (كىشىلەرگە) زۇلۇم قىلغۇچىلارنىڭ جازاسى شۇدۇر»'),
(699, 5, 30, 'ئۇ (يەنى قابىل) نىڭ نەپسى ئۇنىڭغا قېرىندىشىنى (يەنى ھابىلنى) ئۆلتۈرۈشنى چىرايلىق كۆرسەتتى، شۇنىڭ بىلەن، ئۇنى ئۆلتۈرۈپ (دۇنيا ۋە ئاخىرەتتە) زىيان تارتقۇچىلاردىن بولدى'),
(700, 5, 31, 'اﷲ ئۇنىڭغا قېرىندىشىنىڭ جەسىتىنى قانداق كۆمۈشنى كۆرسىتىش ئۈچۈن بىر قاغا ئەۋەتتى. ئۇ (تۇمشۇقى ۋە ئىككى پۇتى بىلەن) يەرنى كولىدى. ئۇ (بۇنى كۆرۈپ): «ۋاي مەن قېرىندىشىمنىڭ جەسىتىنى كۆمۈشتە مۇشۇ قاغىچىلىك بولۇشتىن ئاجىز كەلدىممۇ؟» دېدى. شۇنىڭ بىلەن ئۇ نادامەت قىلغۇچىلاردىن بولدى'),
(701, 5, 32, 'شۇ (يەنى قابىلنىڭ ھابىلنى ناھەق ئۆلتۈرگەنلىكى) سەۋەبتىن (تەۋراتتا) ئىسرائىل ئەۋلادىغا ھۆكۈم قىلدۇقكى، كىمكى ناھەق ئادەم ئۆلتۈرمىگەن ياكى يەر يۈزىدە بۇزغۇنچىلىق قىلمىغان بىر ئادەمنى ئۆلتۈرسە، ئۇ پۈتۈن ئىنسانلارنى ئۆلتۈرگەندەك بولىدۇ، كىمكى بىر ئادەمنى تىرىلدۈرسە (يەنى قۇتقۇزسا ياكى ھايات قېلىشىغا سەۋەبچى بولسا)، ئۇ پۈتۈن ئىنسانلارنى تىرىلدۈرگەندەك بولىدۇ، پەيغەمبەرلىرىمىز بەنى ئىسرائىلغا راستلا (شانلىق) مۆجىزىلەرنى ئېلىپ كەلدى، شۇنىڭدىن كېيىن ئۇلارنىڭ (يەنى ئىسرائىل ئەۋلادىنىڭ) تولىسى (كۇفرىدا، ئادەم ئۆلتۈرۈشتە) زېمىندا ھەددىدىن ئاشقۇچىلاردىن بولدى'),
(702, 5, 33, 'اﷲ ۋە ئۇنىڭ رەسۇلى بىلەن ئۇرۇش قىلىدىغانلارنىڭ، يەر يۈزىدە بۇزغۇنچىلىق قىلىدىغانلارنىڭ جازاسى شۇكى، ئۇلار ئۆلتۈرۈلۈشى ياكى دارغا ئېسىلىشى ياكى ئوڭ قوللىرى ۋە سول پۇتلىرى كېسىلىشى ياكى سۈرگۈن قىلىنىشى كېرەك. بۇ (يەنى جازا) ئۇلار ئۈچۈن بۇ دۇنيادا رەسۋالىق (ئېلىپ كەلگۈچىدۇر)، ئاخىرەتتە ئۇلار چوڭ ئازابقا دۇچار بولىدۇ'),
(703, 5, 34, 'سىلەر تۇتۇۋېلىشتىن ئىلگىرى تەۋبە قىلغانلار بۇنىڭدىن مۇستەسنا، بىلىڭلاركى، اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(704, 5, 35, 'ئى مۆمىنلەر! مەقسەتكە ئېرىشىشىڭلار ئۈچۈن اﷲ (نىڭ ئازابى) دىن قورقۇڭلار، اﷲ قا (تائەت - ئىبادەت قىلىش، گۇناھتىن ساقلىنىش بىلەن) يېقىنچىلىقنى تىلەڭلار، اﷲ يولىدا جىھاد قىلىڭلار'),
(705, 5, 36, 'يەر يۈزىدىكى بارلىق نەرسىلەر، يەنە شۇنىڭدەك بىر ھەسسە كېلىدىغان نەرسىلەر كاپىرلارنىڭ بولۇپ، ئۇلار قىيامەت كۈنى (اﷲ) نىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن ئۇنى فىدىيە قىلىپ بەرگەن تەقدىردىمۇ، ئۇلارنىڭ (فىدىيىسى) قوبۇل قىلىنمايدۇ، ئۇلار قىينىغۇچى ئازابقا دۇچار بولىدۇ'),
(706, 5, 37, 'ئۇلار دوزاختىن چىقماقچى بولىدۇيۇ، ئەمما ھەرگىز چىقالمايدۇ، ئۇلار (ئۈزۈلۈپ قالمايدىغان) دائىملىق ئازابقا قالىدۇ'),
(707, 5, 38, 'ئوغرىلىق قىلغۇچى ئەرنىڭ ۋە ئوغرىلىق قىلغۇچى ئايالنىڭ قىلمىشىنى جازالاپ، اﷲ تەرىپىدىن ئىبرەت قىلىش يۈزىسىدىن قوللىرىنى كېسىڭلار، اﷲ (ئۆز ئەمرىنى ئىشقا ئاشۇرۇشتا) غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(708, 5, 39, 'كىمكى (ئوغرىلىق قىلىپ كىشىلەرگە) زۇلۇم قىلغاندىن كېيىن تەۋبە قىلسا، (ئەمەلىنى) تۈزىسە (يەنى ئوغرىلىق قىلىشتىن يانسا)، اﷲ ئۇنىڭ تەۋبىسىنى قوبۇل قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(709, 5, 40, 'بىلەمسەنكى، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ خالىغان ئادەمگە ئازاب قىلىدۇ، خالىغان ئادەمگە مەغپىرەت قىلىدۇ، اﷲ ھەممە نەرسىگە قادىردۇر'),
(710, 5, 41, 'ئى پەيغەمبەر! دىللىرىدا ئىشەنمەي، ئېغىزلىرىدا: «ئىمان ئېيتتۇق» دەپ قويىدىغانلار (يەنى مۇناپىقلار) دىن ۋە يەھۇدىيلاردىن كۇفرىغا (پۇرسەت تاپسىلا كۇفرىنى ئىزھار قىلىشقا) ئالدىرايدىغان كىشىلەر سېنى غەمكىن قىلمىسۇن، ئۇلار (ئەھبارلىرى ئوقۇغان) يالغانغا قۇلاق سالىدۇ، ساڭا كەلمىگەن (يەنى ئۆچلۈك قىلىپ سېنىڭ سۆھبىتىڭگە ھازىر بولمىغان) باشقا بىر قەۋمگە (يەنى خەيبەر يەھۇدىيلىرىنىڭ سۆزىگە) قۇلاق سالىدۇ، ئۇلار (تەۋراتنىڭ) سۆزلىرىنى جايلىرىدىن ئۆزگەرتىۋېتىدۇ (يەنى اﷲ نىڭ ئەھكاملىرىنى باشقىلارغا ئالماشتۇرۇۋېتىدۇ). ئۇلار: «ئەگەر سىلەرگە بۇ (ھۆكۈم) بېرىلسە، ئۇنى قوبۇل قىلىڭلار، ئەگەر ئۇ بېرىلمىسە قوبۇل قىلماڭلار» دەيدۇ، اﷲ بىركىمنىڭ گۇمراھلىقىنى ئىرادە قىلىدىكەن، ئۇنىڭ ئۈچۈن اﷲ قا قارشى قولۇڭدىن ھېچ ئىش كەلمەيدۇ (يەنى ھېچ ئادەم ئۇنىڭدىن گۇمراھلىقنى دەپئى قىلىشقا قادىر بولالمايدۇ). دىللىرىنى (قىلمىشى قەبىھ بولغانلىقتىن كۇفرىدىن) پاك قىلىشنى اﷲ خالىمىغان كىشىلەر ئەنە شۇلاردۇر، ئۇلار دۇنيادا رەسۋالىققا، ئاخىرەتتە چوڭ ئازابقا قالىدۇ'),
(711, 5, 42, 'ئۇلار يالغانغا قۇلاق سالغۇچىلاردۇر، (پارا ۋە جازانىغا ئوخشاش) ھارامنى يېگۈچىلەردۇر. (ئى مۇھەممەد!) ئەگەر ئۇلار سېنىڭ ئالدىڭغا (دەۋالىشىپ كەلسە)، ئۇلارنىڭ ئارىسىدا ھۆكۈم قىلىپ قويساڭ، ياكى ئۇنى رەت قىلساڭ بولىدۇ (يەنى ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىش ياكى ئۇلاردىن يۈز ئۆرۈش سېنىڭ ئىختىيارىڭ)، ئەگەر ئۇلارنى رەت قىلساڭ، ئۇلار ساڭا قىلچە زىيان يەتكۈزەلمەيدۇ (چۈنكى اﷲ سېنى كىشىلەرنىڭ شەررىدىن ساقلايدۇ)، ئەگەر ئۇلارنىڭ ئارىسىدا ھۆكۈم قىلساڭ، ئادىللىق بىلەن ھۆكۈم قىلغىن، اﷲ ھەقىقەتەن ئادىللارنى دوست تۇتىدۇ'),
(712, 5, 43, 'ئۇلارنىڭ يېنىدا اﷲ نىڭ ھۆكمىنى ئۆز ئىچىگە ئالغان تەۋرات تۇرسا (يەنى تەۋراتتىكى ھۆكۈملەرنى كۆرۈپ تۇرۇپ ئەمەل قىلمايۋاتسا)، (ئى مۇھەممەد) قانداقچە سېنى ھۆكۈم چىقىرىشقا تەكلىپ قىلىدۇ؟ ئاندىن كېيىن (يەنى ھەقىقەت ئاشكارا بولغاندىن كېيىن) سېنىڭ ھۆكمىڭدىن يۈز ئۆرۈيدۇ، ئەنە شۇلار (تەۋراتقىمۇ، ساڭىمۇ) ئىشەنمىگۈچىلەردۇر'),
(713, 5, 44, 'بىز ھەقىقەتەن (مۇساغا) تەۋراتنى نازىل قىلدۇق، تەۋراتتا (توغرا يولغا يېتەكلەيدىغان) ھىدايەت ۋە نۇر بار، (اﷲ نىڭ ھۆكمىگە) بويسۇنغان پەيغەمبەرلەر يەھۇدىيلار ئارىسىدا (تەۋرات بىلەن) ھۆكۈم قىلىدۇ، (يەھۇدىيلارنىڭ) زاھىتلىرى ۋە ئۆلىمالىرىمۇ (ئۆزگەرتىشتىن) ساقلاشقا بۇيرۇلغان كىتابۇللاھ (يەنى تەۋرات) بويىچە ھۆكۈم قىلىدۇ، ئۇلار تەۋراتنى (ئۆزگەرتىشتىن ساقلاشقا) نازارەتچى ئىدى. (ئى يەھۇدىيلارنىڭ ئۆلىمالىرى!) كىشىلەردىن قورقماڭلار، مەندىن قورقۇڭلار. مېنىڭ ئايەتلىرىمنى ئاز پۇلغا تېگىشمەڭلار (يەنى پۇل - مال، مەنسەپ ۋە پارا ئۈچۈن ئايەتلىرىمنىڭ ھۆكمىنى ئۆزگەرتىۋەتمەڭلار)، كىملەركى اﷲ نازىل قىلغان ئايەتلەر بويىچە ھۆكۈم قىلمايدىكەن، ئۇلار كاپىرلاردۇر'),
(714, 5, 45, 'ئۇلار (يەنى ئىسرائىل ئەۋلادى) غا تەۋراتتا شۇنداق بەلگىلىدۇقكى، جانغا جان (يەنى ناھەق ئادەم ئۆلتۈرگەن ئۆلتۈرۈلىدۇ)، كۆزگە كۆز (يەنى كىشىنىڭ كۆزىنى ناھەق قۇيۇۋەتكەن ئادەمنىڭ كۆزىنى قۇيۇۋېتىلىدۇ)، بۇرۇنغا بۇرۇن (يەنى كىشىنىڭ بۇرنىنى ناھەق كەسكەننىڭ بۇرنى كېسىلىدۇ)، قۇلاققا قۇلاق (يەنى كىشىنىڭ قۇلىقىنى ناھەق كەسكەننىڭ قۇلىقى كېسىلىدۇ)، چىشقا چىش (يەنى كىشىنىڭ چىشىنى ناھەق تۆككەننىڭ چىشى تۆكۈلىدۇ) ۋە (كىشىنى) قانداق يارىدار قىلغان بولسا شۇنداق يارىدار قىلىنىش بىلەن قىساس ئېلىنىدۇ، كىمكى (جىنايەتچىنى) ئەپۇ قىلسا (يەنى ئۇنىڭدىن قىساس ئالمىسا)، بۇ ئۇنىڭ (گۇناھى) غا كەففارەت بولىدۇ، اﷲ نازىل قىلغان بويىچە ھۆكۈم قىلمايدىغان كىشىلەر زالىملاردۇر'),
(715, 5, 46, 'ئۇلارنىڭ (يەنى بەنى ئىسرائىل پەيغەمبەرلىرىنىڭ) ئارقىسىدىن ئۆزىدىن ئىلگىرى نازىل قىلىنغان تەۋراتنى (يەنى ئۇنىڭ اﷲ تەرىپىدىن نازىل قىلىنغانلىقىنى) ئېتىراپ قىلغۇچى ئىسا ئىبن مەريەمنى ئەۋەتتۇق، ئۇنىڭغا ھىدايەت بىلەن نۇرنى ئۆز ئىچىگە ئالغان ئىنجىلنى ئاتا قىلدۇق، (ئىنجىل) ئۆزىدىن ئىلگىرى نازىل قىلىنغان تەۋراتنى ئېتىراپ قىلغۇچىدۇر (يەنى ئۇنىڭغا مۇۋاپىقتۇر)، تەقۋادارلارغا ھىدايەت ۋە پەند - نەسىھەتتۇر'),
(716, 5, 47, 'ئەھلى ئىنجىللار (يەنى ناسارالار) اﷲ ئىنجىلدا نازىل قىلغان ئەھكاملار بويىچە ھۆكۈم قىلسۇن، اﷲ نازىل قىلغان ئايەتلەر بويىچە ھۆكۈم قىلمىغانلار پاسىقلاردۇر'),
(717, 5, 48, '(ئى مۇھەممەد!) بىز ساڭا ئۆزىدىن ئىلگىرى (ساماۋى) كىتابلارنى ئېتىراپ قىلغۇچى ۋە ئۇلارغا شاھىت بولغۇچى ھەق كىتابنى (يەنى قۇرئاننى) نازى قىلدۇق. (ئى مۇھەممەد!) ئۇلارنىڭ ئارىسىدا اﷲ ساڭا نازىل قىلغان قۇرئان (ئەھكامى) بويىچە ھۆكۈم قىلغىن، ساڭا كەلگەن ھەقتىن بۇرۇلۇپ، ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن. (ئى ئۈممەتلەر!) سىلەرنىڭ ھەر بىرىڭلارغا بىرخىل شەرىئەت ۋە ئوچۇق يول تەيىن قىلدۇق. ئەگەر اﷲ خالىسا، ئەلۋەتتە، سىلەرنى بىر ئۈممەت قىلاتتى (يەنى پۈتۈن ئىنسانلارنى بىر دىندا قىلاتتى). لېكىن اﷲ سىلەرگە بەرگەن شەرىئەتلەر ئارىسىدا سىلەرنى سىناش ئۈچۈن (كۆپ ئۈممەت قىلىپ ئايرىدى). ياخشى ئىشلارغا ئالدىراڭلار. ھەممىڭلار اﷲ نىڭ دەرگاھىغا قايتىسىلەر، سىلەر ئىختىلاپ قىلىشقان نەرسىلەرنى (ئۇنىڭ قايسى ھەق، قايسى ناھەق ئىكەنلىكىنى) اﷲ سىلەرگە ئېيتىپ بېرىدۇ'),
(718, 5, 49, '(ئۇلار يەنى يەھۇدىيلار ۋە ناسارالار) نىڭ ئارىسىدا اﷲ ساڭا نازىل قىلغان قۇرئاننىڭ (ئەھكامى) بويىچە ھۆكۈم قىلغىن، ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن، ئۇلارنىڭ اﷲ ساڭا نازىل قىلغان قۇرئاننىڭ بىر قىسمىدىن سېنى ۋاز كەچۈرۈشىدىن ساقلانغىن. ئەگەر ئۇلار (اﷲ ساڭا نازىل قىلغان ھۆكۈمدىن) يۈز ئۆرۈسە، (ئى مۇھەممەد!) بىلگىنكى، ئۇلارنىڭ بىر قىسىم گۇناھلىرى تۈپەيلىدىن اﷲ ئۇلارنى جازالاشنى ئىرادە قىلىدۇ. شەك - شۈبھىسىزكى، كىشىلەردىن نۇرغۇنى پاسىقتۇر (يەنى پەرۋەردىگارىنىڭ تائىتىدىن باش تارتىپ، ھەققە خىلاپلىق قىلىپ گۇناھقا چۆمگۈچىلەردۇر)'),
(719, 5, 50, 'ئۇلار جاھىلىيەت دەۋرىنىڭ ھۆكمىنى تەلەپ قىلامدۇ؟ (اﷲ قا) چىن ئىشىنىدىغان قەۋمنىڭ نەزىرىدە ھۆكۈمدە اﷲ تىنمۇ ئادىل كىم بار'),
(720, 5, 51, 'ئى مۆمىنلەر! يەھۇدىي ۋە ناسارالارنى دوست تۇتماڭلار. ئۇلار بىر - بىرىنى دوست تۇتىدۇ. سىلەردىن كىمكى ئۇلارنى دوست تۇتىدىكەن، ئۇمۇ ئەنە شۇلاردىن سانىلىدۇ، اﷲ زالىم قەۋمنى ھەقىقەتەن ھىدايەت قىلمايدۇ'),
(721, 5, 52, 'دىللىرىدا كېسەللىك (يەنى مۇناپىقلىق) بارلارنىڭ (زامان ئۆزگىرىپ كاپىرلار زەپەر تاپسا) ئۆزىمىزگە بىر پالاكەتچىلىك كېلىشىدىن قورقىمىز دەپ، ئۇلار بىلەن دوست بولۇشقا ئالدىرىغانلىقىنى كۆرىسەن، اﷲ (رەسۇلۇللاھقا، مۇسۇلمانلارغا) غەلىبە (يەنى مەككىنى پەتھى قىلىشنى) ئاتا قىلىدۇ، ياكى بىر ئىشنى (يەنى ئۇلارنىڭ مۇناپىقلىقىنى پاش قىلىشنى) مەيدانغا كەلتۈرىدۇ - دە، كۆڭلىدە يوشۇرغىنى (يەنى مۆمىنلەرنىڭ دۈشمەنلىرىنى دوست تۇتقانلىقى) ئۈچۈن ئۇلار نادامەت چېكىدۇ'),
(722, 5, 53, '(بۇ مۇناپىقلارنىڭ مۇسۇلمانلارغا قىلغان ھىيلە - مىكرى پاش قىلىنغاندا) مۆمىنلەر (بىر - بىرىگە): «سىلەر بىلەن چوقۇم بىللە ئىكەنلىكلىرىگە اﷲ نامى بىلەن قاتتىق قەسەم ئىچىشكەنلەرمۇ شۇ كىشىلەرمۇ؟» دەيدۇ. ئۇلارنىڭ (يەنى مۇناپىقلارنىڭ) قىلغان ئەمەللىرى بىكار بولدى، ئۇلار (دۇنيا ۋە ئاخىرەتتە) زىيان تارتقۇچى بولدى'),
(723, 5, 54, 'ئى مۆمىنلەر! سىلەردىن كىمكى مۇرتەد بولىدىكەن، اﷲ (ئۇنىڭ ئورنىغا) اﷲ ئۇلارنى دوست تۇتىدىغان، ئۇلارمۇ اﷲ نى دوست تۇتىدىغان، مۆمىنلەرگە كۆيۈنىدىغان، كاپىرلارغا شەپقەتسىز، اﷲ نىڭ يولىدا جىھاد قىلىدىغان ۋە مالامەت قىلغۇچىنىڭ مالامىتىدىن قورقمايدىغان بىر قەۋمنى كەلتۈرىدۇ. بۇ (يەنى يۇقىرىدىكى سۈپەتلەر بىلەن سۈپەتلىنىش) اﷲ نىڭ پەزلىدۇركى، (اﷲ) ئۇنى (بەندىلىرىدىن) خالىغان كىشىگە بېرىدۇ، اﷲ (نىڭ پەزلى) كەڭدۇر، (اﷲ) ھەممىنى بىلگۈچىدۇر'),
(724, 5, 55, 'شۈبھىسىزكى، سىلەرنىڭ دوستۇڭلار اﷲ دۇر، اﷲ نىڭ رەسۇلىدۇر ۋە مۆمىنلەردۇركى، مۆمىنلەر تەئدىل ئەركان بىلەن ناماز ئۆتەيدۇ، زاكات بېرىدۇ، ئۇلار (اﷲ نىڭ ئەمرىگە) كەمتەرلىك بىلەن بويسۇنغۇچىلاردۇر'),
(725, 5, 56, 'كىمكى اﷲ نى، اﷲ نىڭ پەيغەمبىرىنى ۋە مۆمىنلەرنى دوست تۇتىدىكەن، (ئۇ اﷲ نىڭ جامائەسىدىندۇر). اﷲ نىڭ جامائەسى (ئەلۋەتتە) غالىبتۇر'),
(726, 5, 57, 'ئى مۆمىنلەر! سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن دىنىڭلارنى مەسخىرە ۋە ئويۇنچۇق قىلغانلارنى دوست قىلىۋالماڭلار. ئەگەر (ھەقىقىي) مۆمىن بولساڭلار، اﷲ تىن قورقۇڭلار'),
(727, 5, 58, 'سىلەر نامازغا چاقىرغان (يەنى ئەزان ئېيتقان) چېغىڭلاردا، ئۇلار نامازنى (ھەم سىلەرنى) مەسخىرە ۋە ئويۇنچۇق قىلىدۇ، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار (نامازنىڭ ھېكمىتىنى ۋە نامازنىڭ نەپسىنى پاكلاشتىكى غايىسىنى) چۈشەنمەس قەۋمدۇر'),
(728, 5, 59, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىي ۋە ناسارالار جامائەسى) پەقەت بىزنىڭ اﷲ قا، بىزگە نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا)، بۇنىڭدىن ئىلگىرى (پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىمان ئېيتقانلىقىمىز ئۈچۈن ۋە سىلەرنىڭ كۆپچىلىكىڭلار پاسىق بولغانلىقىڭلار ئۈچۈن، بىزنى ئەيىبلەمسىلەر؟»'),
(729, 5, 60, '(ئى مۇھەممەد!) ئېيتقىنكى، «سىلەرگە اﷲ نىڭ نەزىرىدىكى بۇنىڭدىنمۇ يامان جازانى ئېيتىپ بېرەيمۇ؟ (ئۇ شۇنداق) كىشىلەرنىڭ (جازاسىكى)، اﷲ ئۇلارنى رەھمىتىدىن يىراق قىلدى ۋە ئۇلارغا غەزەپ قىلدى، اﷲ ئۇلارنىڭ بىر قىسمىنى مايمۇن ۋە چوشقا قىلدى، بىر قىسمىنى شەيتانغا ئىبادەت قىلىدىغان قىلدى، ئەنە شۇلارنىڭ ئورنى ئەڭ ياماندۇر (يەنى دوزاختۇر)، (ئۇلار) توغرا يولدىن ئەڭ ئاداشقاندۇر»'),
(730, 5, 61, '(يەھۇدىيلارنىڭ مۇناپىقلىرى) يېنىڭلارغا كەلگەن چاغدا، بىز ئىمان ئېيتتۇق، دەيدۇ، ۋاھالەنكى، ئۇلار (ئى مۇھەممەد! يېنىڭغا) كۇفرى بىلەن كىرىپ كۇفرى بىلەن چىقىپ كەتتى (يەنى سەندىن ئاڭلىغان ئىلىمدىن پايدىلانمىدى). اﷲ ئۇلارنىڭ يوشۇرغان نەرسىسىنى (يەنى نىفاقىنى ۋە كۇفرىنى) ئوبدان بىلىدۇ'),
(731, 5, 62, 'ئۇلار (يەنى يەھۇدىيلار) نىڭ ئارىسىدىكى نۇرغۇن كىشىلەرنىڭ گۇناھقا، زۇلۇم سېلىشقا ۋە ھارام يېيىشكە ئالدىرايدىغانلىقىنى كۆرىسەن، ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!'),
(732, 5, 63, 'ئۇلارنىڭ زاھىتلىرى، ئەھبارلىرى نېمىشقا ئۇلارنى يالغان سۆزلەشتىن، ھارام يېيىشتىن توسمايدۇ؟ ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!'),
(733, 5, 64, 'يەھۇدىيلار: «اﷲ نىڭ قولى باغلاقلىقتۇر (يەنى اﷲ بېخىلدۇر ياكى پېقىردۇر)» دېدى. يەھۇدىيلارنىڭ قوللىرى باغلانسۇن! ئۇلار بۇ سۆزلىرى سەۋەبلىك اﷲ نىڭ رەھمىتىدىن يىراق قىلىندى، ئەمەلدە اﷲ نىڭ ئىككى قولى ئوچۇقتۇر، قانداق خالىسا شۇنداق رىزىق بېرىدۇ، پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان قۇرئان ئۇلارنىڭ كۆپچىلىكىدە يامانلىق بىلەن كۇفرىنى ئاشۇرىدۇ (يەنى ئۇلارنىڭ ساڭا دۈشمەنلىكىنى ۋە قۇرئاننى ئىنكار قىلىشىنى زىيادە قىلىدۇ). بىز ئۇلارنىڭ ئارىسىغا قىيامەتكىچە داۋاملىشىدىغان ئۆچمەنلىك ۋە دۈشمەنلىكنى سالدۇق، ئۇلار ھەر قاچان (رەسۇلۇللاھقا قارشى) ئۇرۇش ئوتىنى ياقماقچى بولسا، اﷲ ئۇنى ئۆچۈرىدۇ، ئۇلار يەر يۈزىدە (ئىسلامغا سۇيىقەست قىلىش ۋە مۇسۇلمانلار ئارىسىدا پىتنە قوزغاش بىلەن) بۇزغۇنچىلىق قىلغۇچىلارنى ياقتۇرمايدۇ'),
(734, 5, 65, 'ئەگەر ئەھلى كىتاب (يەنى يەھۇدىيلار ۋە ناسارالار) (اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ھەقىقىي) ئىمان ئېيتسا ۋە (اﷲ چەكلىگەن ئىشلاردىن) پەرھىز قىلسا ئىدى، ئۇلارنىڭ گۇناھلىرىنى بىز چوقۇم كەچۈرەتتۇق، ئۇلارنى چوقۇم نازۇنېمەتلىك جەننەتكە كىرگۈزەتتۇق'),
(735, 5, 66, 'ئەگەر ئۇلار تەۋراتقا، ئىنجىلغا ۋە پەرۋەردىگارى تەرىپىدىن ئۇلارغا نازىل قىلىنغان باشقا كىتابلارغا ئەمەل قىلسا ئىدى، ئۇلار چوقۇم ئۈستىلىرىدىن ۋە ئاياغلىرى ئاستىدىن يەر ئىدى (يەنى ئۇلارغا ئاسمان - زېمىندىن كەڭ رىزىق بېرىلەتتى)، ئۇلاردىن بىر جامائە (يەنى مۇھەممەد ئەلەيھىسسالامغا ئىمان ئېيتقانلار) توغرا يولدىدۇر. ئۇلارنىڭ نۇرغۇنلىرى نېمىدېگەن يامان ئىش قىلغۇچىلار!'),
(736, 5, 67, 'ئى پەيغەمبەر! پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان ئەھكاملارغا ھەممىسىنى يەتكۈزگىن، ئەگەر تولۇق يەتكۈزمىسەڭ، اﷲ تاپشۇرغان ۋەزىپىنى ئادا قىلمىغان بولىسەن. اﷲ سېنى كىشىلەرنىڭ زىيانكەشلىكىدىن ساقلايدۇ. اﷲ ھەقىقەتەن كاپىر قەۋمنى ھىدايەت قىلمايدۇ'),
(737, 5, 68, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) سىلەر تەۋراتقا، ئىنجىلغا ۋە سىلەرگە پەرۋەردىگارىڭلاردىن نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) تولۇق ئەمەل قىلمىغۇچە، سىلەر ئېتىبارغا ئالغۇدەك دىندا بولغان بولمايسىلەر». پەرۋەردىگارىڭدىن نازىل قىلىنغان ئەھكاملار ئۇلارنىڭ كۆپچىلىكىدە يامانلىق ۋە كۇفرىنى ئاشۇرىدۇ، كاپىر قەۋم ئۈچۈن قايغۇرمىغىن (چۈنكى پەيغەمبەرلەرنى يالغانغا چىقىرىش ئۇلارنىڭ ئادىتىدۇر)'),
(738, 5, 69, 'مۆمىنلەردىن، يەھۇدىيلاردىن، پەرىشتىلەرگە ۋە يۇلتۇزلارغا چوقۇنغۇچىلاردىن، ناسارالاردىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر'),
(739, 5, 70, 'بىز بەنى ئىسرائىلدىن ھەقىقەتەن چىن ئەھدە ئالدۇق. ئۇلارغا (دىننىڭ ئىشلىرىنى بايان قىلىپ بېرىش ئۈچۈن) پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارغا ھەر قاچان بىرەر پەيغەمبەر ئۇلار ياقتۇرمايدىغان ئەھكامنى ئېلىپ كەلسە، (ئۇلار پەيغەمبەرلەرنىڭ) بىر بۆلۈكىنى يالغانچى قىلدى، بىر بۆلۈكىنى ئۆلتۈردى'),
(740, 5, 71, 'ئۇلار، (بىز ئىسرائىل ئەۋلادى پەيغەمبەرلەرنى ئۆلتۈرۈش بىلەن) ئازابقا دۇچار بولمايمىز، دەپ ئويلىدى. ئۇلار كور بولدى (يەنى ھەقىقەتنى كۆرمىدى)، ئۇلار گاس بولدى (يەنى ھەقىقەتنى ئاڭلىمىدى)، ئاندىن كېيىن (ئۇلار تەۋبە قىلدى)، اﷲ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلدى، ئاندىن ئۇلارنىڭ نۇرغۇنى يەنە (ھەقنى كۆرۈشتىن) كور بولدى، يەنە (پەند - نەسىھەت ئاڭلاشتىن) گاس بولى، اﷲ ئۇلارنىڭ قىلىۋاتقان ئىشلىرىنى كۆرۈپ تۇرغۇچىدۇر'),
(741, 5, 72, 'شۈبھىسىزكى، اﷲ مەريەمنىڭ ئوغلى ئىسادۇر، دېگۈچىلەر كاپىر بولدى، ئىسا: «ئى بەنى ئىسرائىل! مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان اﷲ قا ئىبادەت قىلىڭلار، كىمكى اﷲ قا شېرىك كەلتۈرىدىكەن (يەنى اﷲ تىن غەيرىيگە ئىلاھ دەپ ئېتىقاد قىلىدىكەن)، اﷲ ئۇنىڭغا جەننەتنى ھارام قىلىدۇ، ئۇنىڭ جايى دوزاخ بولىدۇ، زالىملارغا ھېچبىر مەدەتكار (يەنى اﷲ نىڭ ئازابىدىن قۇتۇلدۇرغۇچى) بولمايدۇ» دېدى'),
(742, 5, 73, 'اﷲ ئۈچ ئىلاھنىڭ بىرىدۇر، دېگۈچىلەر شەكسىز كاپىر بولدى. بىر ئىلاھتىن باشقا ھېچ ئىلاھ يوقتۇر. ئەگەر ئۇلار ئېيتىۋاتقان سۆزلىرىدىن قايتمىسا، ئۇلارنىڭ ئىچىدىكى كاپىر بولغانلار (دۇنيا ۋە ئاخىرەتتە) قاتتىق ئازابقا قالىدۇ'),
(743, 5, 74, 'ئۇلار اﷲ قا تەۋبە قىلمامدۇ؟ اﷲ تىن مەغپىرەت تەلەپ قىلمامدۇ؟ اﷲ (تەۋبە قىلغۇچىلارغا) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(744, 5, 75, 'مەريەم ئوغلى ئىسا پەقەت (ئۆتكەن پەيغەمبەرلەرگە ئوخشاش) پەيغەمبەردۇر، ئۇنىڭدىن ئىلگىرى نۇرغۇن پەيغەمبەرلەر ئۆتكەن، ئۇنىڭ ئانىسى ناھايىتى راستچىل خوتۇندۇر، ئۇ ئىككىسى تاماق يەيتتى (يەنە ئۇلارمۇ باشقا مەخلۇقلارغا ئوخشاشلا گۆش، سۆڭەك، تومۇر ۋە پەيلەردىن تەركىب تاپقان ئىدى). ئۇلارغا (يەنى ئۇلارنىڭ ئېتىقادىنىڭ باتىللىقىغا) ئايەتلىرىمىزنى قانداق بايان قىلىدىغانلىقىمىزغا قارىغىن؛ ئاندىن ئۇلارنىڭ ھەقتىن قانداق باش تارتقانلىقىغا قارىغىن'),
(745, 5, 76, '(ئى مۇھەممەد!) «سىلەر اﷲ تىن باشقا، سىلەرگە پايدا - زىيان يەتكۈزۈشكە قادىر بولالمايدىغان نەرسىلەرگە چوقۇنامسىلەر؟» دېگىن. اﷲ بولسا (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر'),
(746, 5, 77, '(ئى مۇھەممەد!)» ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) دىنىڭلاردا ھەقسىز رەۋىشتە چەكتىن ئېشىپ كەتمەڭلار، ئىلگىرى ئۆزلىرى ئازغان، نۇرغۇن كىشىلەرنى ئازدۇرغان ۋە توغرا يولدىن ئاداشقان قەۋمنىڭ نەپسى خاھىشلىرىغا ئەگەشمەڭلار» دېگىن'),
(747, 5, 78, 'بەنى ئىسرائىلدىن كاپىر بولغانلارغا داۋۇدنىڭ ۋە مەريەم ئوغلى ئىسانىڭ تىلى بىلەن (يەنى زەبۇردا ۋە ئىنجىلدا) لەنەت قىلىندى. بۇ (يەنى ئۇلارنىڭ لەنەتكە ئۇچرىشى) ئۇلارنىڭ ئاسىيلىق قىلغانلىقلىرى ۋە ھەددىدىن ئېشىپ كەتكەنلىكلىرىدىن بولدى'),
(748, 5, 79, 'ئۇلار ئۆزلىرى قىلغان يامان ئىشلاردىن بىر - بىرىنى توسمايتتى؛ ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!'),
(749, 5, 80, 'ئۇلار (يەنى يەھۇدىيلار) نىڭ ئىچىدە نۇرغۇنلىرىنىڭ كاپىرلارنى (يەنى رەسۇلۇللاھنى ۋە مۇسۇلمانلارنى ئۆچ كۆرۈش يۈزىسىدىن، مۇشرىكلارنى) دوست تۇتقانلىقىنى كۆرىسەن. ئۇلارنىڭ ئۆزلىرى ئۈچۈن ئالدىن تەييارلىغان ئەمەللىرى نېمىدېگەن يامان! (بۇ ئەمەللەر) ئۇلارغا اﷲ نىڭ غەزىپىنى ئېلىپ كەلدى. ئۇلار مەڭگۈ ئازابقا قالغۇچىلاردۇر'),
(750, 5, 81, 'ئەگەر ئۇلار اﷲ قا، پەيغەمبەرگە ۋە پەيغەمبەرگە نازىل قىلىنغان كىتابقا ئىمان ئېيتسا ئىدى، كاپىرلارنى دوست تۇتمايتتى، لېكىن ئۇلارنىڭ تولىسى پاسىقلاردۇر'),
(751, 5, 82, '(ئى مۇھەممەد!) يەھۇدىيلار ۋە مۇشرىكلارنىڭ مۆمىنلەرگە ھەممىدىن قاتتىق دۈشمەن ئىكەنلىكىنى چوقۇم بايقايسەن، بىز ناسارا دېگەن كىشىلەرنىڭ دوستلۇق جەھەتتە مۆمىنلەرگە ھەممىدىن يېقىن ئىكەنلىكىنىمۇ چوقۇم بايقايسەن، بۇ، ناسارالارنىڭ ئىچىدە ئۆلىمالار، راھىبلار بولغانلىقى ۋە (ھەقنى قوبۇل قىلىشتا) تەكەببۇرلۇق قىلمايدىغانلىقلىرى ئۈچۈندۇر'),
(752, 5, 83, 'ئۇلار پەيغەمبەرگە نازىل قىلىنغان قۇرئاننى ئاڭلىغان چاغلىرىدا ھەقىقەتنى تونۇغانلىقلىرىدىن، ئۇلارنىڭ كۆزلىرىدىن ياش قويۇلغانلىقىنى كۆرىسەن، ئۇلار ئېيتىدۇ: «رەببىمىز، بىز ئىمان ئېيتتۇق، بىزنى (پەيغەمبىرىڭنى، كىتابىڭنى) ئېتىراپ قىلغۇچىلار قاتارىدا قىلغىن'),
(753, 5, 84, 'بىز نېمىشقا اﷲ قا، بىزگە كەلگەن ھەقىقەتكە ئىشەنمەيلى؟ ھالبۇكى، بىز پەرۋەردىگارىمىزنىڭ بىزنى ياخشى كىشىلەر قاتارىدا (جەننەتكە) كىرگۈزۈشنى ئۈمىد قىلىمىز»'),
(754, 5, 85, 'بۇ سۆزلىرى ئۈچۈن اﷲ ئۇلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەر بىلەن مۇكاپاتلايدۇ. ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. ياخشىلىق قىلغۇچىلارغا بېرىلىدىغان مۇكاپات ئەنە شۇ'),
(755, 5, 86, 'كاپىر بولغانلار، بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقارغانلار دوزاخ ئەھلىدۇر'),
(756, 5, 87, 'ئى مۆمىنلەر! اﷲ سىلەرگە ھالال قىلغان پاك نەرسىلەرنى (تەركى دۇنيا بولۇش يۈزىسىدىن ئۆزۈڭلارغا) ھارام قىلماڭلار، (اﷲ بەلگىلەپ بەرگەن) چەكتىن ئاشماڭلار، اﷲ چەكتىن ئاشقۇچىلارنى ھەقىقەتەن دوست تۇتمايدۇ'),
(757, 5, 88, 'اﷲ سىلەرگە رىزىق قىلىپ بەرگەن ھالال، پاك نەرسىلەردىن يەڭلار، سىلەر ئىمان ئېيتقان اﷲ قا تەقۋادارلىق قىلىڭلار'),
(758, 5, 89, 'اﷲ سىلەرنى سەۋەنلىك قەسىمىڭلار ئۈچۈن جاۋابكارلىققا تارتىدۇ (مۇنداق قەسىمىڭلارنى بۇزساڭلار) ئۇنىڭ كەففارىتى ئائىلەڭلارغا بېرىدىغان ئوتتۇرا دەرىجىلىك تاماق بىلەن ئون مىسكىننى بىر ۋاخ غىزالاندۇرۇشتۇر، ياكى ئۇلارغا (يەنى ئون مىسكىنگە بەدىنىنى يېپىپ تۇرىدىغان) بىر قۇر كىيىم بېرىشتۇر، ياكى بىر قۇل ياكى بىر چۆرىنى ئازاد قىلىشتۇر، كىمكى بۇنداق قىلىشقا كۈچى يەتمىسە، ئۈچ كۈن روزا تۇتۇشى لازىم. بۇ ئەنە شۇ ئىچكەن قەسىمىڭلارنى بۇزغانلىقىڭلارنىڭ كەففارىتىدۇر، قەسىمىڭلارغا رىئايە قىلىڭلار (يەنى كەلسە - كەلمەس قەسەم ئىچمەڭلار). سىلەرنىڭ شۈكۈر قىلىشىڭلار ئۈچۈن اﷲ ئايەتلىرىنى سىلەرگە ئەنە شۇنداق بايان قىلىدۇ'),
(759, 5, 90, 'ئى مۆمىنلەر! ھاراق ئىچىش، قىمار ئويناش، بۇتلار (يەنى چوقۇنۇش ئۈچۈن تىكلەنگەن تاشلار) غا چوقۇنۇش، پال ئوقلىرى بىلەن پال سېلىش شەيتاننىڭ ئىشى، پاسكىنا قىلىقلاردۇر، بەختكە ئېرىشىشىڭلار ئۈچۈن شەيتاننىڭ ئىشىدىن يىراق بولۇڭلار'),
(760, 5, 91, 'شەيتان ھاراق، قىمار ئارقىلىق ئاراڭلاردا دۈشمەنلىك، ئاداۋەت تۇغدۇرماقچى، سىلەرنى نامازدىن ۋە اﷲ نى ياد ئېتىشتىن توسماقچى بولىدۇ، سىلەر ئەمدى (ھاراقتىن، قىماردىن) يانمامسىلەر؟'),
(761, 5, 92, 'اﷲ ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار، (ئۇلارغا مۇخالىپەتچىلىك قىلىشتىن) ھەزەر قىلىڭلار، ئەگەر (ئىتائەت قىلىشتىن) باش تارتساڭلار، بىلىڭلاركى، رەسۇلىمىزنىڭ مەسئۇلىيىتى پەقەت ئوچۇق تەبلىغ قىلىشتۇر'),
(762, 5, 93, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەر (ھارام قىلىنمىغان نەرسىلەرنى) يېسە، ئىچسە ھېچ گۇناھ بولمايدۇ، قاچانكى ئۇلار (ھارام نەرسىلەردىن) ساقلانسا، ئىمانىدا ۋە ياخشى ئەمەللىرىدە ئىزچىل بولسا، ئاندىن (ھارام قىلىنغان نەرسىلەردىن) يەنە ساقلانسا ۋە ھارام دەپ ئېتىقاد قىلسا، ئاندىن (ھارام قىلىنغان نەرسىلەردىن داۋاملىق) ساقلانسا ۋە ياخشى ئىشلارنى قىلسا، اﷲ ياخشى ئىش قىلغۇچىلارنى دوست تۇتىدۇ'),
(763, 5, 94, 'ئى مۆمىنلەر! كۆرمەي تۇرۇپ اﷲ تىن قورقىدىغانلارنى بىلىش ئۈچۈن، قولۇڭلار ۋە نەيزەڭلار بىلەن ئوۋلىنىدىغان بىر ئاز ئوۋ بىلەن اﷲ سىلەرنى چوقۇم سىنايدۇ، كىمكى بۇنىڭدىن كېيىن ھەددىدىن ئاشسا (يەنى ئىھرامدا تۇرۇپ ئوۋ ئوۋلىسا)، ئۇ قىينىغۇچى ئازابقا دۇچار بولىدۇ'),
(764, 5, 95, 'ئى مۆمىنلەر! سىلەر ئوۋلىغان ھايۋانلارنى ئىھرامدا ياكى ھەرەمدە تۇرۇپ ئۆلتۈرمەڭلار. سىلەردىن كىمكى ئۇنى قەستەن ئۆلتۈرىدىكەن، ئىشنىڭ ۋابالىنى تېتىشى ئۈچۈن، ئۇنىڭ جازاسى ئىككى ئادىل كىشىنىڭ باھالىشى بويىچە ھېلىقى ئۆلتۈرۈلگەن ئوۋغا ئوخشاش ئۆي ھايۋانلىرىدىن بىرنى كەبىگە ئېلىپ بېرىپ قۇربانلىق قىلىشتۇر، ياكى كەففارەت بېرىش يۈزىسىدىن مىسكىنلەرگە ئاش بېرىشتۇر، ياكى ھەر مىسكىنگە بېرىدىغان ئاشنىڭ باراۋىرىگە بىر كۈن روزا تۇتۇشتۇر. ئۆتكەنكى گۇناھنى (يەنى بۇ ئايەت چۈشۈشتىن بۇرۇن ئىھرامدا تۇرۇپ ئوۋ ئۆلتۈرگەن بولساڭلار، بۇنى) اﷲ ئەپۇ قىلدى، كىمكى قايتا گۇناھ ئۆتكۈزسە (يەنى قايتىدىن ئوۋ ئۆلتۈرسە)، اﷲ ئۇنى جازالايدۇ، اﷲ غالىبتۇر، (ئاسىيلىق قىلغۇچىلارنى) جازالىغۇچىدۇر'),
(765, 5, 96, 'سىلەرنىڭ ۋە يولۇچىلارنىڭ مەنپەئەتلىنىشى ئۈچۈن سىلەرگە دېڭىز ھايۋانلىرى، دېڭىزدىن چىقىدىغان يېمەكلىكلەر ھالال قىلىندى، مادامىكى سىلەر ئېھرامدا ياكى ھەرەمدە ئىكەنسىلەر، قۇرۇقلۇق ھايۋانلىرىنى ئوۋلاش سىلەرگە ھارام قىلىندى، سىلەر (قىيامەت كۈنى) دەرگاھىغا توپلىنىدىغان اﷲ تىن قورقۇڭلار'),
(766, 5, 97, 'اﷲ بەيتۇلھەرەم بولغان كەبىنى، ھەج ئېيىنى، ھەجدە قىلىنىدىغان قۇربانلىقنى، (قۇربانلىقنىڭ ئالامىتى سۈپىتىدە) بويۇنلىرىغا بەلگە ئېسىلغان تۆگىلەرنى ئىنسانلارنىڭ دۇنيا ۋە ئاخىرەتلىك ئىشلىرىنىڭ تۇتقۇسى قىلدى، بۇ، اﷲ نىڭ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى بىلىپ تۇرىدىغانلىقىنى ۋە اﷲ نىڭ ھەر شەيئىنى بىلگۈچى ئىكەنلىكىنى سىلەرنىڭ بىلىشىڭلار ئۈچۈندۇر'),
(767, 5, 98, 'بىلىڭلاركى، اﷲ نىڭ ئازابى قاتتىقتۇر، (ئىتائەت قىلغۇچىلارغا) اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(768, 5, 99, 'پەيغەمبەرنىڭ مەسئۇلىيىتى پەقەت تەبلىغ قىلىشتۇر. اﷲ سىلەرنىڭ ئاشكارا ۋە يوشۇرۇن قىلىپ يۈرگەن ئىشلىرىڭلارنى بىلىپ تۇرىدۇ'),
(769, 5, 100, '(ئى مۇھەممەد!) ئېيتقىنكى، گەرچە ھارامنىڭ كۆپلۈكى (ئى تىڭشىغۇچى) سېنى ئەجەبلەندۈرسىمۇ، ھارام بىلەن ھالال باراۋەر ئەمەس. ئى ئەقىل ئىگىلىرى! نىجات تېپىشىڭلار ئۈچۈن اﷲ تىن قورقۇڭلار'),
(770, 5, 101, 'ئى مۆمىنلەر! ئەگەر سىلەرگە ئاشكارا قىلىنسا سىلەرنى بىئارام قىلىپ قويىدىغان نەرسىلەر توغرۇلۇق (پەيغەمبەردىن) سورىماڭلار. ئەگەر قۇرئان نازىل قىلىنىۋاتقان چاغدا ئۇلار توغرۇلۇق سورىساڭلار، سىلەرگە بىلدۈرۈلىدۇ، ئۆتكەندە سورىغانلىرىڭلارنى اﷲ ئەپۇ قىلدى. اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ھەلىمدۇر (يەنى ئاسىيلىق قىلغۇچىلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)'),
(771, 5, 102, 'سىلەردىن بۇرۇن ئۆتكەن بىر قەۋم (پەيغەمبەرلىرىدىن) شۇنداق مەسىلىلەرنى سورىغان ئىدى، كېيىن بۇنىڭ سەۋەبىدىن (يەنى سورىغان ئىشلىرى بايان قىلىنىپ ئۇلار ئەمەل قىلمىغانلىقتىن) كاپىر بولۇپ كەتتى'),
(772, 5, 103, 'بەھىرە (سەككىزنى تۇغقان، بەشىنچىسىنى ئەركەك تۇغقان تۆگە بولۇپ، جاھىلىيەت ئەرەبلىرى بۇ تۆگىنىڭ قۇلىقىنى يېرىپ قويۇپ ئۆز مەيلىگە قويۇپ بېرەتتى، مىنمەيتتى، يۈك ئارتمايتتى)، سائىبە (چىشىدىن ئوننى تۇغقان تۆگە بولۇپ، مىنىلمەيتتى، قىرقىلمايتتى، سۈتى مۇساپىرلارغىلا بېرىلەتتى)، ۋەسىلە (بىرىنچىنى ۋە ئىككىنچىنى چىشى تۇغقان، بۇتلار ئۈچۈن ئۆز مەيلىگە قويۇۋېتىلگەن تۆگە)، ھام (ئون تۆگىگە ئاتا بولغان بۇغرا تۆگە بولۇپ، ئۇلار ئۇنى مىنمەي، يېمەك - ئىچمەكتە ئۆز مەيلىگە قويۇپ بېرەتتى) لارغا اﷲ (يۇقىرىقىدەك قىلىشنى) بۇيرۇغىنى يوق، (لېكىن كاپىرلار اﷲ قا يالغاننى چاپلايدۇ، ئۇلارنىڭ تولىسى بۇنىڭ يالغانلىقىنى) چۈشەنمەيدۇ'),
(773, 5, 104, 'ئۇلارغا اﷲ نازىل قىلغان ھۆكۈمگە، پەيغەمبەر بايان قىلغان ھۆكۈمگە ئەمەل قىلىڭلار دېيىلسە، ئۇلار: «ئاتا - بوۋىلىرىمىزنىڭ دىنى بىزگە كۇپايە» دەيدۇ. ئۇلارنىڭ ئاتا - بوۋىلىرى ھېچ نەرسە بىلمەيدىغان ۋە ھىدايەت تاپمىغان تۇرسىمۇ (يەنىلا ئۇلارنىڭ دىنىغا ئەمەل قىلامدۇ؟)'),
(774, 5, 105, 'ئى مۆمىنلەر! ئۆزۈڭلارنى گۇناھتىن ساقلاڭلار، سىلەر قاچانكى توغرا يولدا بولساڭلار، باشقىلارنىڭ ئاداشقىنى سىلەرگە زىيان يەتكۈزەلمەيدۇ. سىلەر ھەممىڭلار اﷲ نىڭ دەرگاھىغا قايتىسىلەر، اﷲ قىلغان ئەمەلىڭلارنى سىلەرگە ئېيتىپ بېرىدۇ'),
(775, 5, 106, 'ئى مۆمىنلەر! ئاراڭلاردىن بىركىم ئۆلىدىغان چېغىدا ۋەسىيەت قىلماقچى بولسا، ئۇنىڭغا ئىچىڭلاردىن ئىككى ئادىل كىشى گۇۋاھ بولسۇن، ياكى (ئۆز دىنىڭلاردىكىلەردىن گۇۋاھ بولىدىغان ئىككى كىشى تېپىلمىسا) غەيرىي دىندىكىلەردىن ئىككى كىشى گۇۋاھ بولسۇن، ئەگەر سىلەر سەپەردە بولۇپ، بېشىڭلارغا ئۆلۈم دەھشىتى كەلگەندە، سىلەر (بۇ ئىككى گۇۋاھنىڭ راستچىللىقىدىن) گۇمانلانساڭلار، نامازدىن كېيىن ئۇلارنى ئېلىپ قېلىڭلار، ئۇلار: «بىز پايدىسىغا قەسەم قىلىدىغان كىشىلەرمىز، بىزنىڭ يېقىنىمىز بولغان تەقدىردىمۇ قەسىمىمىزنى ھېچ نەرسىگە ساتمايمىز، خۇدالىق ئۈچۈن بولغان گۇۋاھلىقنى يوشۇرمايمىز. ئەگەر ئۇنى يوشۇرساق، بىز ئەلۋەتتە گۇناھكارلادىن بولىمىز» دەپ اﷲ نىڭ نامى بىلەن قەسەم قىلسۇن'),
(776, 5, 107, 'ئەگەر ئۇ ئىككى گۇۋاھچىنىڭ (يالغان گۇۋاھلىق بەرگەنلىك ياكى مىراسقا خىيانەت قىلغانلىق) گۇناھى بىلىنسە، ئۇلارنىڭ ئورنىغا مىراس ئېلىشقا ئەڭ ھەقلىق بولغان ۋارىسلاردىن ئىككى كىشى گۇۋاھ بولۇپ: «بىزنىڭ گۇۋاھلىقىمىز ئەلۋەتتە ئۇلارنىڭ گۇۋاھلىقىدىنمۇ توغرىدۇر، بىز شەرىئەتتىن چىقىپ كەتمىدۇق، بولمىسا بىز چوقۇم زالىملاردىن بولىمىز» دەپ اﷲ نىڭ نامى بىلەن قەسەم قىلسۇن'),
(777, 5, 108, 'بۇ (ئۇسۇل) گۇۋاھچىلارنى ئەڭ گۇۋاھلىق قىلىدىغان قىلىشقا، قىلغان قەسىمىنى رەت قىلىشتىن (يەنى ئۇلاردىن كېيىن باشقىلار قەسەم قىلىشى بىلەن ئۆزلىرىنىڭ رەسۋا بولۇشىدىن) قورقىدىغان قىلىشقا ئەڭ يېقىن (ئۇسۇل) دۇر. اﷲ تىن قورقۇڭلار، (اﷲ نىڭ ئەمرىنى) ئاڭلاڭلار. اﷲ پاسىق قەۋمنى ھىدايەت قىلمايدۇ'),
(778, 5, 109, 'اﷲ قىيامەت كۈنى پەيغەمبەرلەرنى يىغىپ: «(قەۋمىڭلارنى ئىمانغا دەۋەت قىلغىنىڭلاردا) قانداق جاۋابقا ئىگە بولدۇڭلار؟» دەپ سورايدۇ، ئۇلار: «(سېنىڭ بىلگىنىڭگە سېلىشتۇرغاندا) بىز ھېچ نەرسە بىلمەيمىز، سەن غەيبلەرنى ناھايىتى ئوبدان بىلىسەن» دەيدۇ'),
(779, 5, 110, 'ئۆز ۋاقتىدا اﷲ ئېيتتى: ئى مەريەم ئوغلى ئىسا! ساڭا ۋە سېنىڭ ئاناڭغا بەرگەن نېمىتىمنى ئەسلىگىن، ئەينى زاماندا ساڭا روھۇلقۇدۇس (يەنى جىبرىئىل) بىلەن مەدەت بەردىم، (كىچىكىڭدە) بۆشۈكتە ۋە (پەيغەمبەر بولغان) ئوتتۇرا ياش ۋاقتىڭدا كىشىلەرگە سۆزلەيتتىڭ، ئەينى زاماندا ساڭا كىتابنى، ھېكمەتنى، تەۋراتنى ۋە ئىنجىلنى ئۆگەتتىم، ئەينى زاماندا مېنىڭ ئىزنىم بىلەن لايدىن قۇشنىڭ شەكلىدە بىر نەرسە ياسايتتىڭ - دە، ئۇنى پۈۋلىسەڭ ئىزنىم بىلەن ئۇچىدىغان قۇش بولاتتى، مېنىڭ ئىزنىم بىلەن تۇغما كورنى، بەرەس كېسىلىنى ساقايتاتتىڭ، ئەينى زاماندا، مېنىڭ ئىزنىم بىلەن ئۆلۈكلەرنى (تىرىلدۈرۈپ قەبرىلەردىن) چىقىراتتىڭ، ئەينى زاماندا بەنى ئىسرائىلنى ساڭا چېقىلىشتىن توستۇم، سەن ئۇلارغا مۆجىزىلەر بىلەن كەلگەن چېغىڭدا، ئۇلارنىڭ ئىچىدىكى كاپىرلار: «بۇ پەقەت روشەن سېھىردۇر» دېدى'),
(780, 5, 111, 'ئەينى زاماندا مەن ھەۋارىلارغا: «ماڭا ۋە مېنىڭ پەيغەمبىرىمگە ئىمان ئېيتىڭلار» دەپ ۋەھيى قىلدىم. ئۇلار: «بىز ئىمان ئېيتتۇق، گۇۋاھ بولغىنكى، بىز (ئەمرىڭگە) بويسۇنغۇچىلارمىز» دېدى'),
(781, 5, 112, 'ئۆز ۋاقتىدا ھەۋارىلار: «ئى مەريەم ئوغلى ئىسا! رەببىڭ بىزگە ئاسماندىن ئۈستىدە تاماق بار داستىخان چۈشۈرۈپ بېرەلەمدۇ؟» دېدى. ئىسا: «ئەگەر (اﷲ نىڭ قۇدرىتىگە) ئىشەنسەڭلار (مۇنداق سوئاللارنى بېرىشتە) اﷲ تىن قورقۇڭلار» دېدى'),
(782, 5, 113, 'ئۇلار: «بىز ئۇنىڭدىن يېيىشنى، كۆڭلىمىزنىڭ تىنچلىنىشىنى، سۆزۈڭنىڭ راستلىقىنى بىلىشنى ۋە پەيغەمبەرلىكىڭگە گۇۋاھچىلاردىن بولۇشنى ئىرادە قىلىمىز» دېدى'),
(783, 5, 114, 'مەريەم ئوغلى ئىسا: «ئى پەرۋەردىگارىمىز اﷲ! بىزگە ئۈستىدە تاماق بار داستىخان چۈشۈرگىن، بۇ كۈن بىزلەرگە ۋە بىزدىن كېيىنكىلەرگە بايرام بولۇپ قالسۇن، ئۇ داستىخان سەندىن بىزگە (قۇدرىتىڭنى ۋە پەيغەمبەرلىكىمنى كۆرسىتىدىغان) بىر مۆجىزە بولۇپ قالسۇن. بىزگە رىزىق بەرگىن، سەن رىزىق بەرگۈچىلەرنىڭ ياخشىسىسەن» دېدى'),
(784, 5, 115, 'اﷲ (ئىسانىڭ دۇئاسىنى ئىجابەت قىلىپ): «ئۇنى مەن سىلەرگە چوقۇم (ئاسماندىن) چۈشۈرىمەن، سىلەردىن كىمكى شۇنىڭدىن كېيىن كاپىر بولسا، ئۇنى شۇنداق بىر ئازاب بىلەن ئازابلايمەنكى، ئەھلى جاھاندىن ھېچ كىشىنى مۇنداق ئازابلىمايمەن» دېدى'),
(785, 5, 116, 'ئۆز ۋاقتىدا اﷲ ئېيتتى: «ئى مەريەم ئوغلى ئىسا! سەن كىشىلەرگە، اﷲ نى قويۇپ مەن بىلەن ئانامنى ئىككى ئىلاھ قىلىۋېلىڭلار، دېدىڭمۇ؟» ئىسا ئېيتتى: «(رەببىم!) شەنىڭگە لايىق ئەمەس نەرسىلەردىن سېنى پاك دەپ ئېتىقاد قىلىمەنكى، ماڭا ئېيتىشقا تېگىشلىك بولمىغان سۆزلەرنى مەن ئېيتمايمەن، ئەگەر مەن بۇ سۆزنى ئېيتقان بولسام، ئۇنى سەن چوقۇم بىلىسەن (يەنى مېنىڭ ئۇنداق دېمىگەنلىكىم ساڭا مەلۇملۇق). سەن مېنىڭ زاتىمدىكىنى بىلىسەن، مەن سېنىڭ زاتىڭدىكىنى بىلمەيمەن، سەن غەيبلەرنى ناھايىتى ئوبدان بىلىسەن'),
(786, 5, 117, 'مەن ئۇلارغا پەقەت سەن مېنى ئېيتىشقا بۇيرۇغان سۆزنى، يەنى، مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان اﷲ قا ئىبادەت قىلىڭلار، دېدىم. مەن ئۇلارنىڭ ئارىسىدا بولغان مۇددەتتە، ئۇلارنىڭ ئەمەللىرىنى كۆزىتىپ تۇرغان ئىدىم، مېنى قەبزى روھ قىلغىنىڭدىن كېيىن، ئۇلارنىڭ ئەمەللىرىنى سەن كۆزىتىپ تۇرغان ئىدىڭ، سەن ھەممە نەرسىدىن خەۋەردارسەن'),
(787, 5, 118, 'ئەگەر ئۇلارغا ئازاب قىلساڭ، ئۇلار سېنىڭ بەندىلىرىڭدۇر (ئۇلارنى خالىغىنىڭچە تەسەررۇپ قىلىسەن)، (ساڭا ھېچ ئەھەدى تەئەررۇز قىلالمايدۇ)، ئەگەر ئۇلارغا (يەنى ئۇلارنىڭ ئىچىدىكى تەۋبە قىلغانلارغا) مەغپىرەت قىلساڭ، سەن (ئىشىڭدا) غالىب، ھېكمەت بىلەن ئىش قىلغۇچىدۇرسەن»'),
(788, 5, 119, 'اﷲ ئېيتىدۇ: «بۇ كۈن (يەنى قىيامەت كۈنى) راستچىللارنىڭ راستچىللىقى ئۆزىگە پايدا قىلىدىغان كۈندۇر، ئۇلار ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردە مەڭگۈ قالىدۇ، اﷲ ئۇلاردىن رازى بولىدۇ، ئۇلار اﷲ تىن مەمنۇن بولىدۇ، بۇ زور بەختتۇر»'),
(789, 5, 120, 'ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلاردىكى پۈتۈن مەۋجۇداتنىڭ پادىشاھلىقى اﷲ قا مەنسۇپتۇر، اﷲ ھەر نەرسىگە قادىردۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'جىمى ھەمدۇسانا ئاسمانلارنى ۋە زېمىننى ياراتقان، زۇلمەتلەرنى ۋە نۇرنى پەيدا قىلغان اﷲ قا خاستۇر! ئاندىن (يەنى مۇشۇنداق دەلىللەرنى كۆرۈپ تۇرۇپ) كاپىرلار پەرۋەردىگارىغا باشقىلارنى شېرىك كەلتۈرىدۇ'),
(791, 6, 2, 'اﷲ سىلەرنى (يەنى سىلەرنىڭ ئاتاڭلار ئادەمنى) لايدىن ياراتتى، ئاندىن ئەجىلىڭلارنى بېكىتتى، (سىلەرنىڭ تىرىلىدىغان) ۋاقتىڭلار اﷲ نىڭ دەرگاھىدا مەلۇمدۇر، ئاندىن سىلەر - ئى كۇففارلار! (ئۆلگەندىن كېيىن تىرىلىشكە) شەك كەلتۈرىسىلەر'),
(792, 6, 3, 'ئاسمانلاردا ۋە زېمىندا (ئىبادەت قىلىنىشقا تېگىشلىك) اﷲ ئەنە شۇدۇر، ئۇ سىلەرنىڭ يوشۇرۇن ۋە ئاشكارا ئىشىڭلارنى، (ياخشى - يامان) قىلمىشىڭلارنى بىلىپ تۇرىدۇ'),
(793, 6, 4, 'ئۇلار پەرۋەردىگارىنىڭ ئايەتلىرىدىن بىرەر ئايەت كەلسىلا ئۇنىڭدىن باش تارتىدۇ'),
(794, 6, 5, 'ئۇلارغا (اﷲ تەرىپىدىن) كىتاب (يەنى قۇرئان) كېلىۋېدى، ئۇلار ئۇنىڭغا چىنپۈتمىدى، ئۇزاققا قالماي ئۇلارغا ئۆزلىرى مەسخىرە قىلغان نەرسىنىڭ خەۋەرلىرى كېلىدۇ (يەنى ئۇلارغا ئازاب نازىل بولۇپ، ئۆزلىرى مەسخىرە قىلغان نەرسىنىڭ خەۋىرى ئېنىق بولىدۇ)'),
(795, 6, 6, 'ئۇلاردىن بۇرۇن ئۆتكەن نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار كۆرمىدىمۇ؟ (يەنى ئۇنىڭدىن ئىبرەت ئالمىدىمۇ؟) ئۇلارنى زېمىندا سىلەرنى يەرلەشتۈرمىگەن بىر شەكىلدە يەرلەشتۈرگەن ئىدۇق، ئۇلارغا مول يامغۇرلار ياغدۇرۇپ بەرگەن ئىدۇق، ئۆستەڭلارنى ئۇلارنىڭ ئاستىدىن ئېقىپ تۇرىدىغان قىلغان ئىدۇق، ئاندىن كېيىن ئۇلارنى قىلغان گۇناھلىرى تۈپەيلىدىن ھالاك قىلدۇق، ئۇلاردىن كېيىن باشقا خەلقنى پەيدا قىلدۇق'),
(796, 6, 7, '(ئى مۇھەممەد!) بىز ساڭا (ئۇلار تەلەپ قىلغاندەك) قەغەزگە يېزىلغان بىر كىتابنى نازىل قىلغان، كاپىرلار ئۇنى قوللىرى بىلەن تۇتۇپ كۆرگەن تەقدىردىمۇ، ئۇلار، شەكسىزكى، بۇ پەقەت روشەن سېھىردۇر، دەيتتى، چۈنكى ئۇلارنىڭ غەرىزى ئەڭ روشەن دەلىل كەلگەندىمۇ ئىمان ئېيتماسلىقتۇر'),
(797, 6, 8, 'ئۇلار: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىگە گۇۋاھ بولۇپ) نېمىشقا بىر پەرىشتە چۈشمىدى؟» دەيدۇ، ئەگەر ئۇنىڭغا بىرەر پەرىشتە چۈشۈرگەن بولساق، ئۇلارنىڭ ئىشى تۈگىگەن بولاتتى (يەنى بۇ چاغدا ھەقنى ئىنكار قىلسا، ھالاك قىلىناتتى)، ئۇلارغا مۆھلەت بېرىلمەيتتى'),
(798, 6, 9, 'بىز پەيغەمبەرنى پەرىشتىدىن قىلغان تەقدىردىمۇ، ئەلۋەتتە، ئۇنى ئىنسان شەكلىدە قىلاتتۇق، ئۇلارنى يەنە شۈبھىگە چۈشۈرەتتۇق (يەنى ئۇلار پەرىشتىنى ئىنسان شەكلىدە كۆرگەندە: «بۇ ئىنسان، ئەمما پەرىشتە ئەمەس» دەيتتى)'),
(799, 6, 10, 'سەندىن ئىلگىرى ئۆتكەن پەيغەمبەرلەر (كاپىرلار تەرىپىدىن) مەسخىرە قىلىندى، مەسخىرە قىلغۇچىلار مەسخىرە قىلغانلىقلىرىنىڭ ئاقىۋېتىنى كۆردى'),
(800, 6, 11, '(ئى مۇھەممەد! بۇ مەسخىرە قىلغۇچىلارغا) ئېيتقىنكى، «زېمىندا سەير قىلىڭلار، ئاندىن پەيغەمبەرلىرىنى ئىنكار قىلغانلارنىڭ ئاقىۋېتىنىڭ قانداق بولغانلىقىنى كۆزدىن كەچۈرۈڭلار»'),
(801, 6, 12, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئاسمانلاردىكى، زېمىندىكى مەۋجۇداتلار كىمنىڭ؟» (ئۇلارغا) ئېيتقىنكى، «اﷲ نىڭ»، اﷲ (ئېھسان قىلىش يۈزىسىدىن بەندىلىرىگە) رەھمەت قىلىشنى ئۆز ئۈستىگە ئالدى، ھېچقانداق گۇمان بولمىغان قىيامەت كۈنى اﷲ سىلەرنى ئەلۋەتتە يىغىدۇ. (كۇفرىنى ئىختىيار قىلىشى بىلەن) ئۆزىگە ئۆزى زىيان سالغانلار ئىمان ئېيتمايدۇ'),
(802, 6, 13, 'كېچە ۋە كۈندۈزدە جىم تۇرغان (ياكى ھەرىكەتلىنىپ تۇرغان) شەيئىلەرنىڭ ھەممىسى اﷲ نىڭ مۈلكىدۇر، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر'),
(803, 6, 14, '(ئى مۇھەممەد!) بۇ مۇشرىكلارغا ئېيتقىنكى، «ئاسمانلارنى، زېمىننى يوقتىن بار قىلغۇچى اﷲ تىن غەيرىينى مەبۇد تۇتامدىم؟ اﷲ رىزىق بېرىدۇ، ئۆزى رىزىققا موھتاج ئەمەس». (ئى مۇھەممەد!) ئېيتقىنكى، «مەن (بۇ ئۈممەت ئىچىدە خۇدانىڭ ئەمرىگە) بويسۇنغۇچىلارنىڭ ئەۋۋىلى بولۇشقا بۇيرۇلدۇم. (ماڭا دېيىلدىكى) سەن ھەرگىز مۇشرىكلاردىن بولمىغىن»'),
(804, 6, 15, '(ئۇلارغا يەنە) ئېيتقىنكى، «ئەگەر مەن رەببىمگە ئاسىيلىق قىلسام (يەنى پەرۋەردىگارىمدىن غەيرىيگە ئىبادەت قىلسام)، بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن ئەلۋەتتە قورقىمەن»'),
(805, 6, 16, 'مۇشۇ كۈندە كىمكى ئازابتىن قۇتۇلىدىكەن، اﷲ ئۇنىڭغا رەھمەت قىلغان بولىدۇ، ئەنە شۇ روشەن نىجات تاپقانلىقتۇر'),
(806, 6, 17, 'ئەگەر اﷲ ساڭا بىرەر ياخشىلىق يەتكۈزسە (ئۇ چاغدا ئۇنىڭغا ھېچكىم ئارىلىشالمايدۇ)، چۈنكى اﷲ ھەر نەرسىگە قادىردۇر'),
(807, 6, 18, 'اﷲ بەندىلىرىنىڭ ئۈستىدە قاھىردۇر (يەنى اﷲ بەندىلىرىنى تىزگىنلەپ تۇرغۇچىدۇر)، ئۇ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىدىن خەۋەرداردۇر'),
(808, 6, 19, '(ئى مۇھەممەد!) ئېيتقىنكى، «(مېنىڭ پەيغەمبەرلىكىمنىڭ راستلىقى ئۈچۈن) قايسى نەرسە ئەڭ چوڭ گۇناھ؟» ئېيتقىنكى، «اﷲ مەن بىلەن سىلەرنىڭ ئاراڭلاردا گۇۋاھتۇر (ماڭا اﷲ نىڭ گۇۋاھلىقى كۇپايىدۇر). بۇ قۇرئان ماڭا سىلەرنى ۋە (قىيامەتكىچە) قۇرئان يەتكەن كىشىلەرنى ئاگاھلاندۇرۇش ئۈچۈن ۋەھيى قىلىندى. (ئى مۇشرىكلار!) سىلەر اﷲ بىلەن باراۋەر باشقا ئىلاھ بار دەپ چوقۇم گۇۋاھلىق بېرەمسىلەر؟» (ئۇلارغا) ئېيتقىنكى، «مەن (مۇنداق دەپ) گۇۋاھلىق بەرمەيمەن». (ئى مۇھەممەد!) ئېيتقىنكى، «ئۇ ھەقىقەتەن بىر ئىلاھتۇر، سىلەر شېرىك كەلتۈرگەن بۇتلاردىن مەن ھەقىقەتەن ئادا - جۇدامەن»'),
(809, 6, 20, 'بىز كىتاب بەرگەن كىشىلەر (يەنى يەھۇدىيلار ۋە ناسارالار) ئۇنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) (تەۋرات ۋە ئىنجىلدىكى سۈپىتى بويىچە) خۇددى ئۆزلىرىنىڭ ئوغۇللىرىنى تونۇغاندەك تونۇيدۇ، ئۆزلىرىگە ئۆزلىرى زىيان سالغانلار (ئېنىق پاكىتلار تۇرسىمۇ مۇھەممەد ئەلەيھىسسالامغا) ئىمان ئېيتمايدۇ'),
(810, 6, 21, 'اﷲ قا يالغان چاپلىغان ياكى اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلاردىنمۇ زالىم ئادەملەر بارمۇ؟ زالىملار ھەقىقەتەن مەقسىتىگە ئېرىشەلمەيدۇ'),
(811, 6, 22, 'قىيامەت كۈنى ئۇلارنىڭ ھەممىسىنى (ھېساب ئېلىش ئۈچۈن) يىغىمىز، ئاندىن مۇشرىكلارغا: «اﷲ نىڭ شېرىكلىرى دەپ ئېتىقاد قىلغان مەبۇدلىرىڭلار قەيەردە؟» دەيمىز'),
(812, 6, 23, 'ئاندىن ئۇلار پەقەت ئۆزرە ئېيتىپ (دۇنيادىكى چاغلىرىدىكىگە ئوخشاش يالغانغا ئىلتىجا قىلىپ): «پەرۋەردىگارىمىز! اﷲ بىلەن قەسەم قىلىمىزكى، بىز مۇشرىك بولمىغان ئىدۇق» دەيدۇ'),
(813, 6, 24, 'ئۇلارنىڭ (بىز مۇشرىك بولمىغان دەپ) ئۆزلىرىگە قارشى قانداق يالغان سۆزلىگەنلىكىگە قارىغىن، ئۇلارنىڭ (اﷲ نىڭ شېرىكلىرى دەپ) بوھتان چاپلىغان بۇتلىرى ئۇلاردىن قاچىدۇ'),
(814, 6, 25, '(ئى مۇھەممەد!) ئۇلارنىڭ ئىچىدە (قۇرئان ئوقۇغان چېغىڭدا) ساڭا قۇلاق سالىدىغانلار بار، قۇرئاننى چۈشەنمەسلىكلىرى ئۈچۈن ئۇلارنىڭ دىللىرىنى پەردىلىدۇق، قۇلاقلىرىنى ئېغىر قىلدۇق. ئۇلار ھەممە مۆجىزىلەرنى كۆرگەن تەقدىردىمۇ ئۇنىڭغا ئىمان ئېيتمايدۇ. ھەتتا كاپىرلار سېنىڭ يېنىڭغا مۇنازىرىلىشىپ كەلگەندىمۇ: «بۇ پەقەت بۇرۇنقىلاردىن قالغان ئەپسانىلاردۇر» دەيدۇ'),
(815, 6, 26, 'ئۇلار كىشىلەرنى قۇرئاندىن (ۋە مۇھەممەد ئەلەيھىسسالامغا ئەگىشىشىتىن) توسىدۇ، ئۆزلىرىمۇ ئۇنىڭدىن يىراق قاچىدۇ، ئۇلار پەقەت ئۆزلىرىنىلا ھالاك قىلىدۇ، ھالبۇكى، ئۇلار (بۇنى) تۇيمايدۇ'),
(816, 6, 27, 'ئەگەر ئۇلارنى دوزاخ ئۈستىدە توختىتىلغان چاغدا كۆرسەڭ (دەھشەتلىك بىر ھالىنى كۆرەتتىڭ). ئۇلار: «كاشكى دۇنياغا قايتۇرۇلساق ئىدۇق، پەرۋەردىگارىمىزنىڭ ئايەتلىرىنى ئىنكار قىلمايتتۇق ۋە مۆمىنلەردىن بولاتتۇق» دەيدۇ'),
(817, 6, 28, 'ئۇنداق ئەمەس، ئۇلارنىڭ ئىلگىرى يوشۇرغان ئىشلىرى (قەبىھ قىلمىشلىرى تۈپەيلىدىن شۇنداق ئارزۇ قىلىشىدۇ) ئۇلارغا ئاشكارا بولدى، ئۇلار دۇنياغا قايتۇرۇلغان تەقدىردىمۇ مەنئى قىلىنغان ئىشلارنى ئەلۋەتتە يەنە قىلاتتى. شۈبھىسىزكى، ئۇلار (ئىمان ئېيتتۇق دېگەن ۋەدىسىدە) يالغانچىلاردۇر'),
(818, 6, 29, 'ئۇلار: «ھاياتلىق پەقەت مۇشۇ دۇنيادىكى ھاياتىمىزدۇر، ئۆلگەندىن كېيىن تىرىلدۈرۈلمەيمىز» دەيدۇ'),
(819, 6, 30, 'ئەگەر ئۇلارنى پەرۋەردىگارىنىڭ ئالدىدا توختىتىلغان چاغدا كۆرسەڭ (غايەت چوڭ ئىشنى كۆرۈسەن). اﷲ ئۇلارغا: «بۇ (ئۆلگەندىن كېيىن تىرىلىش) راست ئەمەسمىكەن؟» دەيدۇ. ئۇلار: «پەرۋەردىگارىمىز بىلەن قەسەمكى، راست ئىكەن» دەيدۇ. اﷲ: «كاپىر بولغانلىقىڭلار ئۈچۈن ئازابنى تېتىڭلار» دەيدۇ'),
(820, 6, 31, 'اﷲ قا مۇلاقات بولۇشنى (يەنى قايتا تىرىلىشنى) ئىنكار قىلغانلار ھەقىقەتەن زىيان تارتتى. قىيامەت تۇيۇقسىز كەلگەن چاغدا، ئۇلار: «ئۇ دۇنيادىكى بىپەرۋالىقىمىزدىن نادامەت» دەيدۇ. گۇناھلىرىنى ئۈستىلىرىگە يۈكلىۋالىدۇ، ئۇلارنىڭ (گۇناھلارنى) يۈكلىگىنى نېمىدېگەن يامان!'),
(821, 6, 32, 'دۇنيا تىرىكچىلىكى پەقەت ئويۇن، غەپلەتتىنلا ئىبارەت، تەقۋادارلىق قىلىدىغانلار ئۈچۈن ئەلۋەتتە ئۇ دۇنيا ياخشىدۇر، (بۇنى) چۈشەنمەمسىلەر؟'),
(822, 6, 33, 'ئۇلارنىڭ سۆزى سېنى قايغۇغا سالىدىغانلىقىنى بىز ئوبدان بىلىمىز، ئۇلار سېنى (ئىچىدە) ئىنكار قىلمايدۇ (لېكىن ئۇلار تەرسالىقتىن ئىنكار قىلىدۇ)، (ھەقىقەتتە) زالىملار اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلىدۇ'),
(823, 6, 34, 'سەندىن بۇرۇن ئۆتكەن پەيغەمبەرلەرمۇ ئىنكار قىلىندى. ئۇلار ئىنكار قىلىنغانلىقىغا ۋە ئۆزلىرىگە يەتكەن ئەزىيەتلەرگە سەۋر قىلدى، ئاخىر ئۇلار بىزنىڭ ياردىمىمىزگە ئېرىشتى، اﷲ نىڭ سۆزلىرىنى ھەر قانداق ئادەم ئۆزگەرتەلمەيدۇ، شۈبھىسىزكى، پەيغەمبەرلەرنىڭ قىسسىلىرى ساڭا نازىل بولدى'),
(824, 6, 35, '(ئى مۇھەممەد!) ئۇلارنىڭ (ئىسلامدىن) يۈز ئۆرۈگەنلىكى ساڭا ئېغىر كەلگەن بولسا، ئۇنداقتا يەر ئاستىغا كىرىشكە يول ياكى ئاسمانغا چىقىشقا شوتا تېپىپ بىر مۆجىزە كەلتۈرەلىسەڭ (كەلتۈرگىن)، ئەگەر اﷲ خالىسا، ئەلۋەتتە ئۇلارنى ھىدايەتكە بىرلەشتۈرەتتى، سەن ھەرگىز (اﷲ نىڭ ھېكمىتىنى) چۈشەنمەيدىغانلاردىن بولمىغىن'),
(825, 6, 36, '(سۆزۈڭگە) ھەقىقىي قۇلاق سالىدىغانلار (سېنىڭ دەۋىتىڭنى) قوبۇل قىلىدۇ (سۆزۈڭگە قۇلاق سالمىغانلار ئۆلۈكلەرنىڭ ئورنىدا)، ئۆلۈكلەرنى اﷲ تىرىلدۈرىدۇ، ئاندىن ئۇلار اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ'),
(826, 6, 37, 'ئۇلار (يەنى مەككە كۇففارلىرى): «نېمىشقا مۇھەممەدكە پەرۋەردىگارىدىن بىر مۆجىزە چۈشۈرۈلمىدى؟» دەيدۇ، ئېيتقىنكى، «اﷲ مۆجىزە چۈشۈرۈشكە ھەقىقەتەن قادىر». لېكىن ئۇلارنىڭ تولىسى (مۆجىزە نازىل بولۇپ ئىمان ئېيتمىسا بالاغا دۇچار بولىدىغانلىقىنى) بىلمەيدۇ'),
(827, 6, 38, 'مەيلى يەر يۈزىدە ماڭىدىغان ھايۋان بولسۇن، مەيلى ئىككى قانىتى بىلەن ئۇچىدىغان ئۇچار قۇش بولسۇن، ھەممىسى سىلەرگە ئوخشاش ئۈممەتلەردۇر (يەنى اﷲ تەرىپىدىن يارىتىلغان مەخلۇقلاردۇر). لەۋھۇلمەھپۇزدا ھېچ نەرسىنى چۈشۈرۈپ قويمىدۇق (يەنى ھەممىنى تولۇق پۈتتۇق)، كېيىن ئۇلار پەرۋەردىگارىنىڭ دەرگاھىغا يىغىلىدۇ (اﷲ ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىدۇ)'),
(828, 6, 39, 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار گاسلاردۇر (يەنى قوبۇل قىلىش يۈزىسىدىن ئاڭلىمايدۇ)، گاچىلاردۇر (يەنى ھەقنى سۆزلىمەيدۇ). ئۇلار (كۇفرىنىڭ) قاراڭغۇلۇقى ئىچىدە قالغانلاردۇر، اﷲ خالىغان ئادەمنى ئازدۇرىدۇ، خالىغان ئادەمنى توغرا يولغا سالىدۇ'),
(829, 6, 40, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر سىلەر راستچىل بولساڭلار ئېيتىپ بېقىڭلار، ئەگەر سىلەرگە اﷲ نىڭ ئازابى ياكى قىيامەت كەلسە، اﷲ تىن غەيرىيگە ئىلتىجا قىلامسىلەر؟'),
(830, 6, 41, 'ئۇنداق ئەمەس، سىلەر پەقەت اﷲ قا ئىلتىجا قىلىسىلەر، ئەگەر اﷲ خالىسا، سىلەر اﷲ نىڭ كۆتۈرۈۋېتىشىنى ئىلتىجا قىلغان بالانى اﷲ كۆتۈرۈۋېتىدۇ، سىلەر اﷲ نىڭ شېرىكلىرى قىلىۋالغان بۇتلارنى ئۇنتۇيسىلەر (يەنى بۇتلارغا ئىلتىجا قىلمايسىلەر)»'),
(831, 6, 42, 'شەك - شۈبھىسىزكى، سەندىن ئىلگىرىكى ئۈممەتلەرگە (نۇرغۇن پەيغەمبەرلەرنى) ئەۋەتتۇق (ئۇلار پەيغەمبەرلەرنى ئىنكار قىلدى). ئۇلارنى (اﷲ قا تەۋبە قىلىپ) يېلىنسۇن دەپ نامراتلىق ۋە ئاغرىق - سىلاق بىلەن جازالىدۇق'),
(832, 6, 43, 'ئۇلارغا بىزنىڭ ئازابىمىز چۈشكەن چاغدا نېمىشقا يېلىنمىدى؟ لېكىن ئۇلارنىڭ دىللىرى قەساۋەتلەشتى (ئىمانغا يۇمشىمىدى)، شەيتان ئۇلارغا قىلمىشلىرىنى چىرايلىق كۆرسەتتى'),
(833, 6, 44, 'ئۇلار ئۆزلىرىگە قىلىنغان نەسىھەتنى ئۇنتۇغان چاغدا، ئۇلارغا (سىناش ئۈچۈن) پاراۋانلىقنىڭ ھەممە ئىشىكلىرىنى ئېچىۋەتتۇق، ئۇلار تاكى ئۆزلىرىگە بېرىلگەن نېمەتلەردىن خۇشال - خۇرام تۇرغاندا (ئۇلارنى ئۇشتۇمتۇت جازالىدۇق)، ئۇلار ھەسرەتتە قالدى'),
(834, 6, 45, 'زۇلۇم قىلغان قەۋمنىڭ يىلتىزى قۇرۇتۇلدى (يەنى ئۇلار پۈتۈنلەي ھالاك قىلىندى). جىمى ھەمدۇسانا (پەيغەمبەرلىرىگە ياردەم بېرىپ، كۇففارلارنى ھالاك قىلغانلىقى ئۈچۈن) ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!'),
(835, 6, 46, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ، ئەگەر اﷲ سىلەرنىڭ قۇلىقىڭلارنى گاس، كۆزۈڭلارنى كور قىلىپ قويسا، دىللىرىڭلارنى پېچەتلىۋەتسە (يەنى سىلەرنى ھېچ نەرسىنى چۈشەنمەيدىغان قىلىپ قويسا)، ئۇلارنى اﷲ تىن باشقا يەنە قايسى ئىلاھ ئەسلىگە كەلتۈرەلەيدۇ؟» قارىغىنكى، بىز (قۇدرىتىمىزنى كۆرسىتىپ بېرىدىغان) ئايەتلىرىمىزنى قانداق بايان قىلغانمىز، ئەمما ئۇلار (شۇ ئايەتلەردىن) يۈز ئۆرۈيدۇ (يەنى ئىمان ئېيتمايدۇ)'),
(836, 6, 47, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ، ئەگەر سىلەرگە اﷲ نىڭ ئازابى ئۇشتۇمتۇت ياكى ئاشكارا كەلسە، زالىم قەۋمدىن باشقا يەنە كىملەر ھالاك قىلىنىدۇ؟»'),
(837, 6, 48, 'بىز پەيغەمبەرلەرنى پەقەت (ساۋاب بىلەن) خۇش خەۋەر بەرگۈچى، (ئازابتىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتىمىز، ئىمان ئېيتقان ۋە (ئەمەللىرىنى) تۈزىگەن كىشىلەرگە (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر'),
(838, 6, 49, 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار، (اﷲ قا) ئىتائەتسىزلىكلىرى تۈپەيلىدىن، ئازابقا دۇچار بولىدۇ'),
(839, 6, 50, '(ئى مۇھەممەد! سەندىن ئادەتتىن تاشقىرى ئىشلارنى تەلەپ قىلغۇچى كۇففارلارغا) ئېيتقىنكى، «مەن سىلەرگە، مەندە اﷲ نىڭ خەزىنىلىرى بار، دېمەيمەن، مەن غەيبنىمۇ بىلمەيمەن، سىلەرگە، مەن بولسام ھەقىقەتەن بىر پەرىشتە، دەپمۇ ئېيتمايمەن، مەن پەقەت ماڭا قىلىنغان ۋەھيىگىلا ئەمەل قىلىمەن». ئېيتقىنكى، «كور ئادەم بىلەن كۆزى ساق ئادەم باراۋەرمۇ؟ (يەنى كاپىر بىلەن مۆمىن باراۋەرمۇ؟) ئويلاپ باقمامسىلەر؟»'),
(840, 6, 51, 'پەرۋەردىگارىنىڭ دەرگاھىغا يىغىلىشتىن قورقىدىغانلارنىڭ (كۇفرىدىن، گۇناھتىن) ساقلىنىشلىرى ئۈچۈن، ئۇلارنى قۇرئان بىلەن ئاگاھلاندۇرغىن، ئۇلارغا اﷲ تىن غەيرىي دوست ۋە شاپائەت قىلغۇچى يوقتۇر'),
(841, 6, 52, 'اﷲ نىڭ رازىلىقىنى دەپ ئەتىگەندە، ئاخشامدا پەرۋەردىگارىغا ئىبادەت قىلىدىغانلارنى (يېنىڭدىن) قوغلىۋەتمىگىن، ئۇلارنىڭ ھېسابىدىن ساڭا ھېچ نەرسە يۈكلەنمەيدۇ (يەنى ئۇلارنىڭ قىلمىشلىرى ئۈچۈن سەن جاۋابكارلىققا تارتىلمايسەن). سېنىڭ ھېسابىڭدىنمۇ ئۇلارغا ھېچ نەرسە يۈكلەنمەيدۇ، ئۇلارنى قوغلىۋەتسەڭ، زالىملاردىن بولۇپ قالىسەن'),
(842, 6, 53, 'ئۇلارنىڭ: «اﷲ ئارىمىزدىن (ھىدايەت قىلىش بىلەن) ئىنئام قىلغان كىشىلەر مۇشۇلارمۇ؟» دېيىشلىرى ئۈچۈن، ئۇلارنى بىر - بىرى بىلەن مۇشۇنداق سىنىدۇق (يەنى پېقىر، ئاجىز كىشىلەرنى ئىمان ئېيتقۇزۇش بىلەن، باي، چوڭ كىشىلەرنى سىنىدۇق). اﷲ شۈكۈر قىلغۇچىلارنى ئوبدان بىلىدۇ ئەمەسمۇ؟ (يەنى شۈكۈر قىلغۇچىلارنى اﷲ ھىدايەت قىلىدۇ)'),
(843, 6, 54, 'بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتقانلار يېنىڭغا كەلگەن چاغدا: «سىلەرگە ئامانلىق بولسۇن، پەرۋەردىگارىڭلار (بەندىلىرىگە) رەھمەت قىلىشنى (ئېھسان قىلىش يۈزىسىدىن) ئۆز ئۈستىگە ئالدى، سىلەردىن كىمكى بىلمەستىن بىرەر يامانلىق قىلىپ قويۇپ، ئاندىن كېيىن تەۋبە قىلسا ۋە (ئەمەلىنى) تۈزىسە، اﷲ ئەلۋەتتە ئۇنىڭغا مەغپىرەت قىلىدۇ ۋە رەھىم قىلىدۇ» دېگىن'),
(844, 6, 55, '(بۇ سۈرىدە مۇشرىكلارنىڭ گۇمراھلىقىنى كۆرسىتىدىغان دەلىللەرنى بايان قىلغىنىمىزدەك) گۇناھكارلارنىڭ يولى ئاشكارا بولسۇن ئۈچۈن، ئايەتلىرىمىزنى روشەن بايان قىلىمىز'),
(845, 6, 56, '(ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر اﷲ نى قايرىپ قويۇپ ئىبادەت قىلغان بۇتلارغا ئىبادەت قىلىشتىن مەن ھەقىقەتەن مەنئى قىلىندىم». ئېيتقىنكى، «مەن سىلەرنىڭ نەپسى خاھىشىڭلارغا ئەگەشمەيمەن، بولمىسا مەن ئازغان بولىمەن ۋە ھىدايەت تاپقۇچىلاردىن بولالماي قالىمەن»'),
(846, 6, 57, 'ئېيتقىنكى، «ھەقىقەتەن مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان روشەن دەلىلگە (يەنى قۇرئانغا ۋە ۋەھىگە) ئاساسلىنىمەن، ھالبۇكى، سىلەر ئۇنى ئىنكار قىلدىڭلار، سىلەر بالدۇر يۈز بېرىشىنى تەلەپ قىلغان ئازاب مېنىڭ ئىگىدارچىلىقىدا ئەمەس، بارچە ھۆكۈم اﷲ قا خاس، اﷲ ھەق ھۆكۈم قىلىدۇ، اﷲ ھۆكۈم قىلغۇچىلارنىڭ ياخشىسىدۇر»'),
(847, 6, 58, 'ئېيتقىنكى، «سىلەر بالدۇر يۈز بېرىشىنى تەلەپ قىلغان ئازاب مېنىڭ قولۇمدىن كەلسە ئىدى، ئۇ چاغدا مەن بىلەن سىلەرنىڭ ئاراڭلاردىكى ئىش چوقۇم ھۆكۈم قىلىنىپ بولغان بولاتتى (يەنى سىلەردىن ئارام تېپىش ئۈچۈن، سىلەرنى ھالاك قىلغان بولاتتىم)». اﷲ زالىملارنى ئوبدان بىلىدۇ'),
(848, 6, 59, 'غەيبنىڭ خەزىنىلىرى اﷲ نىڭ دەرگاھىدىدۇر، ئۇنى پەقەتلا اﷲ بىلىدۇ، قۇرۇقلۇقتىكى، دېڭىزدىكى نەرسىلەرنىڭ ھەممىسىنى اﷲ بىلىدۇ، (دەرەختىن) تۆكۈلگەن ياپراقتىن اﷲ بىلمەيدىغان بىرەرسىمۇ يوق، مەيلى قاراڭغۇ يەر ئاستىدىكى بىرەر دانە ئۇرۇق بولسۇن، مەيلى ھۆل ياكى قۇرۇق نەرسىلەر بولسۇن، ھەممىسى (اﷲ قا مەلۇم بولۇپ) لەۋھۇلمەھپۇزدا يېزىقلىقتۇر'),
(849, 6, 60, 'اﷲ سىلەرنى كېچىسى ئۇخلىتىدۇ، كۈندۈزدىكى قىلغان ئىشلىرىڭلارنى بىلىپ تۇرىدۇ، ئاندىن بەلگىلەنگەن مۇددەتكىچە ياشىشىڭلار ئۈچۈن، كۈندۈزدە سىلەرنى ئويغىتىدۇ، ئاندىن اﷲ نىڭ دەرگاھىغا قايتىسىلەر، ئاندىن قىلغان ئەمەللىرىڭلارنى اﷲ سىلەرگە ئېيتىپ بېرىدۇ'),
(850, 6, 61, 'اﷲ بەندىلىرىنىڭ ئۈستىدە قاھىردۇر (يەنى اﷲ بەندىلىرىنى تىزگىنلەپ تۇرغۇچىدۇر)، ئۇ سىلەرگە ساقلىغۇچى پەرىشتىلەرنى (يەنى قىلغان - ئەتكەننى خاتىرىلەپ تۇرىدىغان پەرىشتىلەرنى) ئەۋەتىدۇ، بېرىڭلارغا ئۆلۈم كەلسە، ئۇنى بىزنىڭ پەرىشتىلىرىمىز قەبزى روھ قىلىدۇ. ئۇلار (اﷲ نىڭ ئەمرىنى ئورۇنلاشتا) بىپەرۋالىق قىلمايدۇ'),
(851, 6, 62, 'ئاندىن ئۇلار (يەنى بەندىلەر) ھەق ھۆكۈم قىلغۇچى، ئىگىسى اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ (قىيامەت كۈنى) ھۆكۈم قىلىش يالغۇز اﷲ غىلا خاستۇر، اﷲ ئەڭ تېز ھېساب ئالغۇچىدۇر'),
(852, 6, 63, '(ئى مۇھەممەد! بۇ كۇففارلارغا) ئېيتقىنكى، «قۇرۇقلۇقنىڭ ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرى (يەنى بالايى - ئاپەتلىرى) دىن سىلەرنى كىم قۇتقۇزىدۇ؟ (بۇ قاراڭغۇلۇقلارنى كۆرگەن چېغىڭلاردا) ئەگەر اﷲ بىزنى بۇنىڭدىن قۇتقۇزسا چوقۇم شۈكۈر قىلغۇچىلاردىن بولىمىز، دەپ اﷲ قا ئاشكارا ۋە يوشۇرۇن ئىلتىجا قىلىسىلەر»'),
(853, 6, 64, 'ئېيتقىنكى، «(يالغۇز) اﷲ سىلەرنى ئۇلاردىن ۋە بارلىق غەمدىن قۇتقۇزىدۇ، ئاندىن سىلەر (بۇنى) تونۇغۇنۇڭلاردىن كېيىن، ئىمان ئېيتماي اﷲ قا شېرىك كەلتۈرىسىلەر»'),
(854, 6, 65, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «اﷲ سىلەرنىڭ ئۈستۈڭلاردىن (نۇھ ۋە لۇت ئەلەيھىسسالاملارنىڭ قەۋمىگە، ئەسھابۇلپىلگە ئەۋەتىلگەن ئازابقا ئوخشاش) ياكى ئايىغىڭلار ئاستىدىن (پىرئەۋن ۋە قارۇنلارغا ئەۋەتىلگەن ئازابقا ئوخشاش) ئازاب ئەۋەتىشكە، ياكى سىلەرنى پىرقىلەر قىلىپ، ئارىلاشتۇرۇپ (يەنى ئۇرۇشقا سېلىپ) بېرىڭلارغا بېرىڭلارنىڭ ئازابىنى تېتىتىشقا قادىردۇر» ئۇلارنى چۈشەنسۇن دەپ، ئايەتلىرىمىزنى تۈرلۈك شەكىللەردە قانداق بايان قىلىدىغانلىقىمىزغا قارىغىن'),
(855, 6, 66, 'قۇرئان ھەق تۇرسا، ئۇنى قەۋمىڭ ئىنكار قىلدى. «مەن سىلەرگە ھامىي ئەمەس» دېگىن'),
(856, 6, 67, '(اﷲ تەرىپىدىن) ئالدىنئالا بېرىلگەن ھەر بىر خەۋەرنىڭ مەيدانغا كېلىدىغان ۋاقتى بار، ئۇزاققا قالماي بىلىسىلەر'),
(857, 6, 68, 'بىزنىڭ ئايەتلىرىمىز توغرىسىدا (تەنە ۋە مەسخىرە قىلىپ) پاراڭ سېلىشىۋاتقان كىشىلەرنى كۆرگىنىڭدە، ئۇلار بۇنىڭدىن باشقا پاراڭغا چۈشمىگۈچە ئۇلار بىلەن بىللە ئولتۇرمىغىن، ئۇلار بىلەن بىللە ئولتۇرماسلىقنى شەيتان ساڭا ئۇنتۇلدۇرغان بولسا، ئېسىڭگە كەلگەندىن كېيىن، زالىم قەۋم بىلەن بىللە ئولتۇرمىغىن'),
(858, 6, 69, 'تەقۋادارلارغا (قۇرئاننى مەسخىرە قىلغۇچى) كاپىرلارنىڭ ھېسابىدىن ھېچ نەرسە يۈكلەنمەيدۇ، لېكىن تەقۋادارلار ئۇلارغا ۋەز - نەسىھەت قىلىشلىرى كېرەك. (شۇنداق قىلغاندا) ئۇلار (قۇرئاننى مەسخىرە قىلىشتىن) ساقلىنىشلىرى مۇمكىن'),
(859, 6, 70, 'دىننى ئويۇن - كۈلكە قىلىۋالغان، دۇنيا تىرىكچىلىكىگە ئالدانغانلارنى تەرك ئەت. ھەر قانداق ئادەمنىڭ ئۆز قىلمىشى تۈپەيلى ھالاك بولۇشتىن ساقلىنىشى ئۈچۈن، (كىشىلەرگە) قۇرئان بىلەن نەسىھەت قىلغىن، ئۇنىڭغا اﷲ تىن باشقا دوست ۋە شاپائەت قىلغۇچى بولمايدۇ، ئۇ ھەر قانداق فىدىيە بەرسىمۇ قوبۇل قىلىنمايدۇ. ئەنە شۇلار قىلمىشلىرى تۈپەيلىدىن ھالاك بولىدىغانلاردۇر، ئۇلار كۇفرى تۈپەيلىدىن (ئۈچەيلىرىنى ئۆرتىۋېتىدىغان) قايناقسۇدىن بولغان شارابقا ۋە قاتتىق ئازابقا دۇچار بولىدۇ'),
(860, 6, 71, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، بىز اﷲ نى قويۇپ، بىزگە پايدا - زىيان يەتكۈزەلمەيدىغان بۇتلارغا ئىبادەت قىلامدۇق؟ بىزنى اﷲ ھىدايەت قىلغاندىن كېيىن، ئارقىمىزغا چېكىنەمدۇق؟ (يەنى مۇرتەد بولامدۇق؟) (مۇبادا بىز شۇنداق قىلىدىغان بولساق، مۇنداق) بىر ئادەمگە: شەيتانلار ئۇنى بىر چۆلگە باشلاپ بارغان، (چۆلدە ئۇ) تېڭىرقاپ قالغان، دوستلىرى ئۇنى بېرى كەل دەپ توغرا يولغا چاقىرىۋاتقان (ئۇ چاقىرىقنى قوبۇل قىلماي ئاخىر ھالاك بولغان ئادەمگە) ئوخشاپ قالىمىز. (بۇ كۇففارلارغا) ئېيتقىنكى، اﷲ نىڭ ھىدايىتى ھەقىقەتەن ھەقىقىي ھىدايەتتۇر، بىز ئالەملەرنىڭ پەرۋەردىگارىغا بويسۇنۇشقا بۇيرۇلدۇق'),
(861, 6, 72, 'ناماز ئوقۇشقا ۋە اﷲ تىن قورقۇشقا بۇيرۇلدۇق، (قىيامەت كۈنى) اﷲ نىڭ دەرگاھىغا (ھېساب ئۈچۈن) يىغىلىسىلەر'),
(862, 6, 73, 'اﷲ ئاسمانلارنى، زېمىننى ھېكمەت بىلەن ياراتتى، اﷲ (بىرەر شەيئىگە) «ۋۇجۇدقا كەل» دېسە (دەرھال) ۋۇجۇدقا كېلىدىغان كۈندىكى (يەنى قىيامەت كۈنىدىكى) ئازابتىن قورقۇڭلار. اﷲ نىڭ سۆزى ھەقتۇر، سۇر چېلىنىدىغان كۈندىكى (يەنى قىيامەت كۈنىدىكى) پادىشاھلىق اﷲ قا خاستۇر، اﷲ يوشۇرۇن ۋە ئاشكارا نەرسىلەرنى بىلگۈچىدۇر، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، (بەندىلىرىنىڭ ئەھۋالىدىن) خەۋەرداردۇر'),
(863, 6, 74, 'ئۆز ۋاقتىدا، ئىبراھىم ئۆز ئاتىسى ئازەرگە: «(سەن ئۆزۈڭنى ياراتقان اﷲ نى قويۇپ) بۇتلارنى مەبۇد قىلىۋالامسەن؟ مەن سېنى ۋە قەۋمىڭنى ھەقىقەتەن ئېنىق گۇمراھلىقتا كۆرۈمەن» دېدى'),
(864, 6, 75, 'ئىبراھىمنىڭ (تەۋھىد بارىسىدا) قەتئىي ئىشەنگۈچىلەردىن بولۇشى ئۈچۈن، (ئۇنىڭغا ئاتىسى بىلەن قەۋمنىڭ گۇمراھلىقىنى كۆرسەتكىنىمىزدەك) بىز ئىبراھىمغا ئاسمانلارنىڭ، زېمىننىڭ ئاجايىپلىرىنى كۆرسەتتۇق'),
(865, 6, 76, 'ئىبراھىم كېچىنىڭ قاراڭغۇلۇقى باسقاندا بىر يۇلتۇزنى كۆرۈپ (قەۋمىنىڭ سۆزىنى دەلىل كەلتۈرۈپ، ئۇلارنى مات قىلىش ئۈچۈن): «بۇ مېنىڭ پەرۋەردىگارىمدۇر» دېدى. يۇلتۇز پېتىپ كېتىۋىدى، «مەن پېتىپ كەتكۈچىلەرنى (يەنى يۇلتۇزلارغا ئىبادەت قىلىشنى) ياقتۇرمايمەن» دېدى'),
(866, 6, 77, 'ئۇ ئاينىڭ تۇغقانلىقىنى كۆرۈپ (يۇقىرىقى ئۇسۇل بويىچە): «بۇ مېنىڭ پەرۋەردىگارىمدۇر» دېدى. ئاي پېتىپ كېتىۋىدى، «ئەگەر پەرۋەردىگارىم مېنى ھىدايەت قىلمىغان بولسا، مەن چوقۇم ئازغۇچى قەۋمنىڭ قاتارىدا بولىدىكەنمەن» دېدى'),
(867, 6, 78, 'ئۇ قۇياشنىڭ چىقىۋاتقانلىقىنى كۆرۈپ (يۇقىرىقى ئۇسۇل بويىچە): «بۇ مېنىڭ پەرۋەردىگارىمدۇر، بۇ ھەممىدىن (يەنى يۇلتۇز بىلەن ئايدىن) چوڭدۇر» دېدى. قۇياش پېتىپ كېتىۋىدى، ئۇ ئېيتتىكى: «ئى قەۋمىم! مەن ھەقىقەتەن سىلەر شېرىك كەلتۈرگەن مەبۇدلاردىن ئادا - جۇدامەن'),
(868, 6, 79, 'مەن ھەقىقەتەن، باتىل دىنلاردىن ھەق دىنغا بۇرۇلۇپ، ئاسمانلارنى، زېمىننى ياراتقان زاتقا يۈزلەندىم، مەن مۇشرىكلاردىن ئەمەسمەن»'),
(869, 6, 80, 'ئىبراھىمنىڭ قەۋمى ئۇنىڭ بىلەن مۇنازىرىلەشتى، ئىبراھىم ئېيتتىكى، «مېنى اﷲ ھىدايەت قىلغان تۇرسا، مەن بىلەن اﷲ (نىڭ مەۋجۇتلۇقى ۋە بىرلىكى) توغرۇلۇق مۇنازىرىلىشەمسىلەر؟ مەن سىلەرنىڭ اﷲ قا شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن قورقمايمەن، پەقەت پەرۋەردىگارىمنىڭلا خالىغان نەرسىسى بولىدۇ، پەرۋەردىگارىمنىڭ بىلىمى ھەممە نەرسىنى ئۆز ئىچىگە ئالغان، (مەبۇدلىرىڭلارنىڭ ماڭا ھېچبىر زىيان يەتكۈزەلمەيدىغانلىقىنى) ئويلىمامسىلەر!'),
(870, 6, 81, 'اﷲ نىڭ شېرىكى بولۇش ھەققىدە اﷲ سىلەرگە ھېچقانداق دەلىل چۈشۈرمىگەن بۇتلارنى اﷲ قا شېرىك كەلتۈرۈشتىن قورقمايۋاتساڭلار، سىلەرنىڭ شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن مەن قانداقمۇ قورقاتتىم! ئەگەر سىلەر بىلسەڭلار (ئېيتىڭلارچۇ)، بىز بۇ ئىككى گۇرۇھ ئادەمدىن زادى قايسىمىز ئەڭ قورقماسلىققا تېگىشلىك»'),
(871, 6, 82, 'ئازابتىن ئەمىن بولۇش ئىمان ئېيتقان، ئىمانغا مۇشرىكلىكنى ئارىلاشتۇرمىغان ئادەملەرگە مەنسۇپتۇر، ئۇلار ھىدايەت تاپقۇچىلاردۇر'),
(872, 6, 83, 'ئۆز قەۋمىگە رەددىيە بېرىش ئۈچۈن ئىبراھىمغا بەرگەن دەلىلىمىز ئەنە شۇدۇر، خالىغان كىشىمىزنى بىر قانچە دەرىجە كۆتۈرىمىز، پەرۋەردىگارىڭ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر'),
(873, 6, 84, 'بىز ئىبراھىمغا ئىسھاقنى، يەئقۇبنى ئاتا قىلدۇق. ئۇلارنىڭ ھەر بىرىنى ھىدايەت قىلدۇق. (ئىبراھىمدىن) ئىلگىرى نۇھنى ھىدايەت قىلدۇق، ئىبراھىمنىڭ ئەۋلادىدىن داۋۇد، سۇلايمان، ئەييۇب، يۇسۇف، مۇسا، ھارۇنلارنى ھىدايەت قىلدۇق، ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز'),
(874, 6, 85, 'زەكەرىيا، يەھيا، ئىسا ۋە ئىلياسلارنى ھىدايەت قىلدۇق، ئۇلارنىڭ ھەممىسى ياخشىلاردىندۇر'),
(875, 6, 86, 'ئىسمائىل، ئىليەسە، يۇنۇس ۋە لۇتلارنى ھىدايەت قىلدۇق، ئۇلارنى (ئۆز زامانىدىكى) تامامى جاھان ئەھلىدىن ئۈستۈن قىلدۇق'),
(876, 6, 87, 'ئۇلارنىڭ ئاتا - بوۋىلىرىدىن، ئەۋلادلىرىدىن، قېرىنداشلىرىدىن بەزىلىرىنى ھىدايەت قىلدۇق، ئۇلارنى تاللىدۇق، ئۇلارنى توغرا يولغا يېتەكلىدۇق'),
(877, 6, 88, 'اﷲ نىڭ يولى ئەنە شۇدۇر، اﷲ بەندىلىرىدىن خالىغان كىشىلەرنى شۇ يولغا باشلايدۇ. ئەگەر ئۇلار (يەنى شۇ پەيغەمبەرلەر) شېرىك كەلتۈرسە ئىدى، ئۇلارنىڭ قىلغان ياخشى ئەمەللىرى، ئەلۋەتتە، بىكار بولۇپ كېتەتتى'),
(878, 6, 89, 'ئۇلارغا كىتابنى، ھېكمەتنى ۋە پەيغەمبەرلىكنى ئاتا قىلدۇق، ئەگەر بۇ مۇشرىكلار ئۇلارنى (يەنى كىتاب، ھېكمەت ۋە پەيغەمبەرلىكنى) ئىنكار قىلسا، ئۇلارنى ئىنكار قىلمايدىغان باشقا بىر قەۋمگە تاپشۇرىمىز'),
(879, 6, 90, 'ئەنە شۇلار (يەنى مەزكۇر پەيغەمبەرلەر) اﷲ ھىدايەت قىلغان كىشىلەردۇر، ئۇلارنىڭ يولىغا ئەگەشكىن، (ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «قۇرئاننى تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، قۇرئان پەقەت پۈتۈن جاھان ئەھلى ئۈچۈن ۋەز - نەسىھەتتۇر»'),
(880, 6, 91, 'ئۇلار اﷲ نى ھەقىقىي رەۋىشتە تونۇمىدى. ئۇلار (يەنى يەھۇدىيلار) ئۆز ۋاقتىدا: «اﷲ ھېچ كىشىگە ھېچ نەرسە نازىل قىلمىدى» دېدى (يەنى ۋەھيىنى ۋە پەيغەمبەرلەرنى ئىنكار قىلدى). (ئى مۇھەممەد!) ئېيتقىنكى، «مۇسا ئېلىپ كەلگەن ۋە كىشىلەرگە نۇر، ھىدايەت بولغان كىتابنى كىم نازىل قىلدى؟ ئۇ كىتابنى پارچە - پارچە قەغەزلەرگە كۆچۈرۈپ، بىر قىسىمىنى ئاشكارىلاپ، كۆپ قىسىمىنى يوشۇرىسىلەر. (ئى يەھۇدىيلار!) سىلەرگە ئۆزۈڭلار ۋە ئاتا - بوۋاڭلار بىلمىگەن نەرسىلەر بىلدۈرۈلدى، (بۇ قۇرئاننى) اﷲ (نازىل قىلدى)». ئاندىن، ئۇلارنى تەرك ئەتكىنكى، ئۇلار باتىللىرىدا (يەنى قالايمىقان سۆزلىرىدە) ئويناپ يۈرسۇن'),
(881, 6, 92, 'بۇ (قۇرئان) مەككە ئاھالىسىنى ۋە ئۇنىڭ ئەتراپىدىكىلەرنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن بىز نازىل قىلغان، ئۇنىڭدىن ئىلگىرى نازىل قىلىنغان (تەۋرات ۋە ئىنجىلغا ئوخشاش) كىتابلارنى ئېتىراپ قىلغۇچى مۇبارەك كىتابتۇر، ئاخىرەتكە ئىمان ئېيتىدىغانلار ئۇنىڭغا ئىمان ئېيتىدۇ، ئۇلار نامازلىرىنى ئۆز ۋاقتىدا تولۇق ئادا قىلىدۇ'),
(882, 6, 93, 'اﷲ قا يالغان چاپلىغان ياكى ئۆزىگە ھېچ نەرسە ۋەھىيى قىلىنمىغان تۇرۇقلۇق، ماڭا ۋەھىيى قىلىندى دېگەن، ياكى اﷲ نازىل قىلغاندەك مەنمۇ كىتاب نازىل قىلالايمەن دېگەن كىشىلەردىنمۇ زالىم كىشىلەر بارمۇ؟ (ئى مۇھەممەد!) زالىملارنى سەكراتقا چۈشكەن ۋاقىتلىرىدا كۆرسەڭ (ئەلۋەتتە، قورقۇنچلۇق ھالىنى كۆرۈسەن)، پەرىشتىلەر (يەنى ئازاب پەرىشتىلىرى) قوللىرىنى سوزۇپ: «سىلەر جانلىرىڭلارنى چىقىرىڭلار! سىلەر اﷲ توغرىسىدا ھەقسىز سۆزلەرنى قىلغانلىقىڭلار (يەنى ئىختىرا قىلغانلىقىڭلار، شېرىك كەلتۈرگەنلىكىڭلار، بالا نىسبەتەن بەرگەنلىكىڭلار) ۋە اﷲ نىڭ ئايەتلىرىنى مەنسىتمىگەنلىكىڭلار ئۈچۈن، بۈگۈن خورلىغۇچى ئازاب بىلەن جازالىنىسىلەر» دەيدۇ'),
(883, 6, 94, 'بىز دەسلەپتە سىلەرنى قانداق خەلق ئەتكەن بولساق دەرگاھىمىزغا شۇنداق تەنھا كەلدىڭلار، سىلەرگە بىز ئاتا قىلغان نەرسىلەرنى ئارقاڭلاردا (يەنى دۇنيادا) قويۇپ كەلدىڭلار، (ئىبادەت قىلىنىشقا تېگىشلىك بولۇشتا) اﷲ نىڭ شېرىكلىرى دەپ گۇمان قىلغان شاپائەتچىڭلارنى سىلەر بىلەن بىللە كۆرمەيمىزمۇ؟ ئۇلار بىلەن سىلەرنىڭ ئاراڭلاردىكى مۇناسىۋەت ھەقىقەتەن ئۈزۈلدى، سىلەر (شاپائەت قىلىدۇ) دەپ گۇمان قىلغان بۇتلار بەربات بولدى'),
(884, 6, 95, 'دانلارنى ۋە ئۇرۇقچىلارنى بىخ ئۇرغۇزغۇچى ھەقىقەتەن اﷲ دۇر، تىرىكنى ئۆلۈكتىن ۋە ئۆلۈكنى تىرىكتىن چىقارغۇچى ئەنە شۇ اﷲ دۇر، (اﷲ نىڭ ئىبادىتىدىن) قانداقمۇ باش تارتىسىلەر؟'),
(885, 6, 96, 'اﷲ سۈبھىنى (قاراڭغۇدىن) يېرىپ چىقارغۇچىدۇر (يەنى تاڭنى يورۇتقۇچىدۇر)، اﷲ كېچىنى (سىلەر ئۈچۈن) ئىستىراھەت قىلىپ ياراتتى، (سىلەرنىڭ) ۋاقىت ھېسابلىشىڭلار ئۈچۈن قۇياش بىلەن ئاينى ياراتتى، بۇ، غالىب، ھەممىنى بىلگۈچى اﷲ نىڭ ئالدىنئالا بەلگىلىگەن ئىشىدۇر'),
(886, 6, 97, 'قۇرۇقلۇق ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرىدا (يەنە كېچىلىك يول يۈرگىنىڭلاردا) يول تېپىشىڭلار ئۈچۈن اﷲ سىلەرگە يۇلتۇزلارنى يارىتىپ بەردى. (اﷲ نىڭ ئۇلۇغلۇقىنى) بىلىدىغان قەۋم ئۈچۈن، (اﷲ نىڭ قۇدرىتىنىڭ) دەلىللىرىنى ھەقىقەتەن ئوچۇق بايان قىلدۇق'),
(887, 6, 98, 'اﷲ سىلەرنى بىر ئىنساندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتتى، سىلەرنى ئاتاڭلارنىڭ پۇشتىدا، ئاناڭلارنىڭ بەچچىدانىدا قارارلاشتۇردى، چۈشىنىدىغان قەۋم ئۈچۈن ئايەتلەرنى ھەقىقەتەن ئوچۇق بايان قىلدۇق'),
(888, 6, 99, 'اﷲ بۇلۇتتىن يامغۇر سۈيىنى چۈشۈردى، شۇنىڭ بىلەن بارلىق ئۆسۈملۈكلەرنى ئۈندۈردى، ئۆسۈملۈكلەردىن يېشىل ياپراقلارنى چىقاردى، (بوغداي ۋە ئارپا باشاقلىرىغا ئوخشاش) بىر - بىرىگە مىنگىشىپ كەتكەن دانلارنى يېتىشتۈردى، خورما دەرىخىنىڭ چېچەكلىرىدىن يەرگە يېقىن ساڭگىلاپ تۇرىدىغان خورما ساپاقلىرىنى يېتىشتۈردى، (يامغۇر سۈيى بىلەن) ئۈزۈملۈك باغلارنى يېتىشتۈردى، (يامغۇر سۈيى بىلەن) شەكىلدە، ھەجىمدە، تەمدە بىر - بىرىگە ئوخشايدىغان ۋە ئوخشىمايدىغان زەيتۇن، ئانار دەرەخلىرىنى يېتىشتۈردى، (ئۇلارنىڭ ھەر بىرىنىڭ) يېڭى تۇتقان مېۋىسىگە ۋە پىشقان مېۋىسىگە قاراڭلار، ئۇلاردا اﷲ نىڭ بارلىقىغا ئىشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ئوچۇق دەلىللەر بار'),
(889, 6, 100, 'جىنلارنى اﷲ ياراتقان تۇرسا، (مۇشرىكلار) جىنلارنى اﷲ قا (مۇشرىكلار ئىختىرا قىلغان نەرسىلەردىن) پاكتۇر، ئۇلارنىڭ سۈپەتلىگەنلىرىدىن ئۈستۈندۇر'),
(890, 6, 101, 'اﷲ ئاسمانلارنى ۋە يەرنى ئۆرنەكسىز ياراتقۇچىدۇر، اﷲ نىڭ خوتۇنى يوق تۇرسا، قانداقمۇ بالىسى بولسۇن؟ ھەممە نەرسىنى اﷲ ياراتقان، ھەممە نەرسىنى اﷲ ئوبدان بىلگۈچىدۇر'),
(891, 6, 102, 'ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، اﷲ تىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ ھەممە نەرسىنى ياراتقۇچىدۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، ئۇ ھەر نەرسىگە ھامىيدۇر'),
(892, 6, 103, 'كۆزلەر اﷲ نى كۆرمەيدۇ، اﷲ كۆزلەرنى كۆرۈپ تۇرىدۇ، اﷲ (بەندىلىرىگە) مېھرىباندۇر، ھەممىدىن خەۋەرداردۇر'),
(893, 6, 104, '(ئى پەيغەمبەر ئېيتقىنكى) سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن (ھىدايەتنى ئازغۇنلۇقتىن ئايرىپ كۆرسىتىدىغان) دەلىللەر كەلدى، كىمكى ھەقنى كۆرۈپ ئىمان ئېيتسا ئۆزىگە پايدا، كىمكى ئۇنىڭدىن كۆز يۇمىسا (يەنى باش تارتسا) ئۆزىگە زىيان. مەن سىلەرگە (ئەمەلىڭلارنى كۆزىتىپ تۇرىدىغان) كۆزەتچى ئەمەسمەن (پەقەت سىلەرنى ئاگاھلاندۇرغۇچى پەيغەمبەرمەن)'),
(894, 6, 105, 'ئۇلار (يەنى مۇشرىكلار): (ئى مۇھەممەد!) سەن (قۇرئاننى باشقىلاردىن) ئۆگەندىڭ دەيدۇ دەپ، ھەقنى بىلىدىغان قەۋمگە بايان قىلىمىز دەپ، ئايەتلەرنى مۇشۇنداق شەكىللەردە بايان قىلدۇق'),
(895, 6, 106, '(ئى مۇھەممەد!) ساڭا پەرۋەردىگارىڭ تەرىپىدىن ۋەھيى قىلىنغان قۇرئانغا ئەمەل قىلغىن؛ اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر، مۇشرىكلار بىلەن تەڭ بولمىغىن'),
(896, 6, 107, 'ئەگەر اﷲ (ئۇلارنىڭ ھىدايەت تېپىشنى) خالىسا، ئۇلار شېرىك كەلتۈرمەيتتى، سېنى ئۇلارغا كۆزەتچى قىلغىنىمىز يوق، سەن ئۇلارغا ھامىيمۇ ئەمەسسەن'),
(897, 6, 108, 'مۇشرىكلارنىڭ خۇدانى قويۇپ ئىبادەت قىلىدىغان بۇتلىرىنى تىللىماڭلار، (ئۇنداق قىلساڭلار) ئۇلار بىلمىگەنلىكلىرىدىن ھەددىدىن ئېشىپ اﷲ نى تىللايدۇ. شۇنىڭدەك بىز ھەر ئۈممەتكە ئۆزلىرىنىڭ ئەمەلىنى چىرايلىق كۆرسەتتۇق، ئاندىن ئۇلارنىڭ قايتىپ بارىدىغان جايى اﷲ نىڭ دەرگاھىدۇر، اﷲ ئۇلارنىڭ قىلمىشلىرىنى ئۇلارغا ئېيتىپ بېرىدۇ (يەنى ياخشىغا مۇكاپات بېرىدۇ، ياماننى جازالايدۇ)'),
(898, 6, 109, 'ئەگەر ئۇلارغا بىرەر مۆجىزە كەلسە، ئۇنىڭغا چوقۇم ئىشىنىدىغانلىقلىرىنى (بىلدۈرۈپ) اﷲ نىڭ نامى بىلەن قاتتىق قەسەم ئىچىشىدۇ. (ئى مۇھەممەد!) مۆجىزىلەر (مېنىڭ ئەمەس)، اﷲ نىڭ دەرگاھىدىكى ئىش، دېگىن. (ئى مۆمىنلەر!) سىلەر نېمىنى بىلىسىلەر؟ مۆجىزىلەر كەلسە، ئۇلار، ئېھتىمال، ئۇنىڭغا ئىشەنمەسلىكلىرى مۇمكىن'),
(899, 6, 110, 'ئۇلارنىڭ دىللىرىنى، كۆزلىرىنى ھەق (نى چۈشىنىش ۋە كۆرۈش) تىن بۇرىۋېتىمىز، ئۇلار دەسلەپتە مۆجىزىلەرگە ئىشەنمىگىنىدەك (يەنى ئىشەنمەيدۇ)، ئۇلارنى گۇمراھلىقلىرىدا تېڭىرقاپ يۈرۈشكە قويۇۋېتىمىز'),
(900, 6, 111, 'بىز ئۇلارغا (يەنى كۇففارلارغا) پەرىشتىلەرنى چۈشۈرگەن، ئۆلۈكلەرنى ئۇلار بىلەن سۆزلەشتۈرگەن، بىز ھەممە نەرسىنى ئۇلارنىڭ ئالدىغا يىغىپ بەرگەن (يەنى ئۇلارنىڭ يۇقىرىقى تەلەپلىرىنى ئورۇنلىغان) تەقدىردىمۇ، اﷲ خالىمىغۇچە ئۇلار ئىمان ئېيتمايدۇ، لېكىن ئۇلارنىڭ تولىسى (بۇنى) ئۇقمايدۇ'),
(901, 6, 112, 'شۇنىڭدەك (يەنى بۇ مۇشرىكلارنى ساڭا دۈشمەن قىلغاندەك) ئىنسانلار ۋە جىنلارنىڭ شەيتانلىرىنى ھەر بىر پەيغەمبەرگە دۈشمەن قىلدۇق، ئۇلار ئالداش ئۈچۈن شېرىن سۆزلەر بىلەن بىر - بىرىگە ۋەسۋەسە قىلىدۇ، ئەگەر پەرۋەردىگارىڭ خالىسا، ئۇلار بۇ ئىشنى (يەنى پەيغەمبەرلىرىگە دۈشمەنلىكنى) قىلمايتتى، ئۇلارنى ئۆزلىرى تۈزگەن ھىيلە - مىكىرلىرى بىلەن قويۇپ بەرگىن (اﷲ ساڭا ئۇلارغا قارشى ياردەم بېرىدۇ)'),
(902, 6, 113, 'ئاخىرەتكە ئىمان ئېيتمايدىغانلارنىڭ دىللىرى ئۇلارنىڭ شېرىن سۆزلىرىگە مايىل بولسۇن، ئۇنىڭدىن خۇرسەن بولسۇن ۋە ئۇلار قىلىۋاتقان گۇناھلارنى بۇلارمۇ قىلسۇن، دەپ ۋەسۋەسە قىلىدۇ'),
(903, 6, 114, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «اﷲ سىلەرگە روشەن كىتاب نازىل قىلغان تۇرسا، (ئارىمىزدىكى ئىختىلاپنى ھەل قىلىش ئۈچۈن) اﷲ تىن غەيرىي ھۆكۈم قىلغۇچىنى تەلەپ قىلامدىم؟» بىز كىتاب ئاتا قىلغانلار (يەنى يەھۇدىيلار ۋە ناسارالارنىڭ ئۆلىمالىرى) ئۇنىڭ (يەنى قۇرئاننىڭ) پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان ھەق كىتاب ئىكەنلىكىنى بىلىدۇ، سەن ھەرگىزمۇ شەك قىلغۇچىلاردىن بولمىغىن'),
(904, 6, 115, 'پەرۋەردىگارىڭنىڭ سۆزى ناھايىتى راستتۇر، ناھايىتى توغرىدۇر، ئۇنىڭ سۆزىنى ئۆزگەرتەلەيدىغان ھېچ كىشى يوقتۇر. اﷲ (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر'),
(905, 6, 116, 'ئەگەر سەن يەر يۈزىدىكى كۆپچىلىك كىشىلەر (يەنى كۇففارلار) گە ئىتائەت قىلساڭ، ئۇلار سېنى اﷲ نىڭ يولىدىن ئازدۇرىدۇ، ئۇلار پەقەت گۇمانغىلا ئەگىشىدۇ، ئۇلار پەقەت يالغاننىلا سۆزلەيدۇ'),
(906, 6, 117, '(ئى مۇھەممەد!) سېنىڭ پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغانلارنى ھەقىقەتەن ئوبدان بىلىدۇ، توغرا يول تاپقانلارنىمۇ ئوبدان بىلىدۇ'),
(907, 6, 118, 'اﷲ نىڭ ئايەتلىرىگە ئىمان كەلتۈرگۈچى بولساڭلار، اﷲ نىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان ھايۋانلارنى يەڭلار'),
(908, 6, 119, 'زۆرۈرىيەت بولمىغۇچە يېيىش ھارام قىلىنغان نەرسىلەرنى اﷲ سىلەرگە ئوچۇق بايان قىلغان تۇرسا، اﷲ نىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان ھايۋانلارنى نېمىشقا يېمەيسىلەر؟ نۇرغۇن كىشىلەر نەپسى خاھىشىغا ئەگىشىپ، بىلمەستىن، (باشقىلارنى) ھەقىقەتەن ئازدۇرىدۇ، شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ (شەرئى دەلىلسىز ھالال، ھارام دەپ) ھەددىدىن ئاشقۇچىلارنى ئوبدان بىلىدۇ'),
(909, 6, 120, 'ئاشكارا ۋە يوشۇرۇن گۇناھلارنى تەرك ئېتىڭلار، شۈبھىسىزكى، گۇناھ قىلغۇچىلار ئۆز قىلمىشلىرى تۈپەيلىدىن (ئاخىرەتتە) جازاغا تارتىلىدۇ'),
(910, 6, 121, '(ئى مۆمىنلەر!) اﷲ نىڭ ئىسمى ئېيتىلماي بوغۇزلانغان (يەنى بىسمىللاە دەپ زەبھى قىلىنمىغان) ھايۋانلارنى يېمەڭلار، ئۇنى يېيىش ئەلۋەتتە گۇناھتۇر. شۈبھىسىزكى، شەيتانلار ئۆز دوستلىرىنى (يەنى مۇشرىكلارنى) سىلەر بىلەن دەتالاش قىلىشقا ۋەسۋەسە قىلىدۇ، ئەگەر ئۇلارغا ئىتائەت قىلساڭلار، سىلەر چوقۇم مۇشرىك بولۇپ قالىسىلەر'),
(911, 6, 122, 'ئەسلىدە ئۆلۈك (يەنى كاپىر) بولۇپ، بىز ئۇنى (يەنى ئۇنىڭ قەلبىنى ئىمان بىلەن) تىرىلدۈرگەن، بىز ئۇنىڭغا كىشىلەر ئارىسىدا ھىدايەت تېپىشقا نۇر بەرگەن بىر ئادەم زۇلمەتتە (يەنى كۇفرىنىڭ قاراڭغۇلۇقىدا) قالغان ۋە ئۇنىڭدىن قۇتۇلالمىغان بىر ئادەم بىلەن ئوخشاشمۇ؟ كاپىرلارغا ئۇلارنىڭ قىلمىشلىرى ئەنە شۇنداق چىرايلىق كۆرسىتىلدى'),
(912, 6, 123, 'ھىيلە - مىكىر ئىشلىتىشلىرى ئۈچۈن (مەككىدە قۇرەيشنىڭ چوڭلىرىنى باش گۇناھكار قىلغىنىمىزدەك)، ھەر بىر شەھەرنىڭ چوڭلىرىنى باش گۇناھكار قىلدۇق، ئۇلارنىڭ ئىشلەتكەن ھىيلە - مىكىرلىرى ئۆزلىرىگە يانىدۇ (يەنى ئۇلار شۇ ھىيلە - مىكىرلەرنىڭ ۋابالىغا قالىدۇ)، ئۇلار (بۇنى) سەزمەيدۇ'),
(913, 6, 124, 'ئۇلارغا (مۇھەممەد ئەلەيھىسسالامنىڭ راست پەيغەمبەرلىكىنى كۆرسىتىدىغان) بىرەر مۆجىزە كەلگەن چاغدا، (ئۇلار) بىزگە پەيغەمبەرلەرگە بېرىلگەن مۆجىزە بېرىلمىگۈچە (ئۇنىڭغا) ھەرگىز ئىشەنمەيمىز، دەيدۇ. اﷲ پەيغەمبەرلىك ۋەزىپىسىنى قەيەرگە ئورنىتىشنى (يەنى كىمنىڭ ئۇنىڭ ئەھلى بولىدىغانلىقىنى) ئوبدان بىلىدۇ، (بۇ) گۇناھكارلار ئۆزلىرى ئىشلەتكەن ھىيلە - مىكىرلەر تۈپەيلىدىن اﷲ نىڭ دەرگاھىدا خورلۇققا ۋە قاتتىق ئازابقا دۇچار بولىدۇ'),
(914, 6, 125, 'اﷲ كىمنى ھىدايەت قىلماقچى بولسا، ئۇنىڭ كۆكسىنى ئىسلام ئۈچۈن ئاچىدۇ (يەنى ئۇنىڭ قەلبىنى ئىمانغا كېڭەيتىپ بېرىدۇ)، اﷲ كىمنى ئازدۇرماقچى بولسا، ئۇنىڭ كۆكسىنى شۇنچە تارايتىۋېتىدۇكى، (ئىمان ئېيتىش) ئۇنىڭغا گويا ئاسمانغا چىقىشتىنمۇ قىيىن تۇيۇلىدۇ، ئىمان ئېيتمىغانلارنى اﷲ مۇشۇنداق ئازابلايدۇ'),
(915, 6, 126, 'بۇ (يەنى ئىسلام دىنى) پەرۋەردىگارىڭنىڭ توغرا يولىدۇر، ئىبرەت ئالىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلدۇق'),
(916, 6, 127, 'پەرۋەردىگارىنىڭ دەرگاھىدا ئۇلارغا دارى سالام (يەنى جەننەت) بار، ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرى ئۈچۈن اﷲ ئۇلارغا مەدەتكاردۇر'),
(917, 6, 128, 'اﷲ (ئىنسانلارنى، جىنلارنى ھېساب ئۈچۈن) تولۇق يىغىدىغان كۈننى ئەسلىگىن، (بۇ كۈندە اﷲ ئېيتىدۇكى) «ئى جىنلار جامائەسى! سىلەر نۇرغۇن ئادەملەرنى ئازدۇردۇڭلار». ئۇلارنىڭ ئىنسانلاردىن بولغان دوستلىرى: «ئى پەرۋەردىگارىمىز! بىز بىر - بىرىمىزدىن پايدىلاندۇق، بىز ئەمدى سەن بەلگىلىگەن ۋاقىتقا (يەنى قىيامەت كۈنىگە) يەتتۇق» دەيدۇ، اﷲ: «دوزاخ سىلەرنىڭ جايىڭلاردۇر، سىلەر اﷲ نىڭ خاھىشىغا باغلىق ھالدا ئۇ يەردە مەڭگۈ قالىسىلەر» دەيدۇ. شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر'),
(918, 6, 129, 'شۇنىڭغا (يەنى ئىنسانلار بىلەن جىنلارنى ئۆزئارا پايدىلاندۇرغىنىمىزغا) ئوخشاش، قىلمىشلىرى تۈپەيلىدىن زالىملارنىڭ بەزىسىنى بەزىسىگە مۇسەللەت قىلىمىز'),
(919, 6, 130, '«ئى جىنلار ۋە ئىنسانلار جامائەسى! سىلەرگە ئۆز ئىچىڭلاردىن مېنىڭ ئايەتلىرىمنى بايان قىلىپ بېرىدىغان، بۇ كۈنۈڭلاردىكى ئۇچرىشىشتىن سىلەرنى ئاگاھلاندۇرىدىغان پەيغەمبەرلەر كەلمىدىمۇ؟» ئۇلار: «گۇناھلىرىمىزنى ئېتىراپ قىلدۇق» دەيدۇ. ئۇلارنى دۇنيا تىرىكچىلىكى ئالدىغان، ئۇلار ئۆزلىرىنىڭ ھەقىقەتەن كاپىر بولغانلىقلىرىنى ئېتىراپ قىلىدۇ'),
(920, 6, 131, 'بۇ شۇنىڭ ئۈچۈنكى، پەرۋەردىگارىڭ شەھەرلەرنىڭ ئاھالىسىنى (ئۇلارغا پەيغەمبەرلەر ئەۋەتىپ ئاگاھلاندۇرماستىن) غەپلەتتىكى ۋاقىتلىرىدا زۇلۇم قىلغانلىقلىرى تۈپەيلىدىن ھالاك قىلغىنى يوق'),
(921, 6, 132, '(ئاخىرەتتە) ھەر ئادەمنىڭ قىلغان ئەمەلىگە يارىشا دەرىجىسى بولىدۇ، پەرۋەردىگارىڭ ئۇلارنىڭ قىلغان ئىشلىرىدىن غاپىل ئەمەس'),
(922, 6, 133, 'پەرۋەردىگارىڭ (خالايىقتىن ۋە ئۇلارنىڭ ئىبادەتلىرىدىن) بىھاجەتتۇر، رەھمەت ئىگىسىدۇر. ئەگەر ئۇ خالىسا، سىلەرنى ھالاك قىلىپ، سىلەرنى باشقا بىر قەۋمنىڭ ئەۋلادىدىن پەيدا قىلغاندەك، سىلەرنىڭ ئورنۇڭلارغا ئۆزى خالىغان (ئىتائەتمەن) باشقا بىر قەۋمنى قويىدۇ'),
(923, 6, 134, 'سىلەرگە ۋەدە قىلىنغان (قىيامەت، ئۆلگەندىن كېيىن تىرىلىش قاتارلىق) ئىشلار چوقۇم مەيدانغا كېلىدۇ، سىلەر قېچىپ قۇتۇلالمايسىلەر'),
(924, 6, 135, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئى قەۋمىم! سىلەر كۈچۈڭلارنىڭ يېتىشىچە ئىشلەڭلار (يەنى كۇفرىڭلاردا چىڭ تۇرۇڭلار)، مەنمۇ چوقۇم كۈچۈمنىڭ يېتىشىچە ئىشلەيمەن (يەنى مەنمۇ ئۆز دەۋىتىمدە چىڭ تۇرىمەن). ئاخىرەتتە كىمنىڭ ئاقىۋېتى ياخشى بولىدىغانلىقىنى ئۇزۇنغا قالماي بىلىسىلەر، زالىملار ئەلۋەتتە مەقسىتىگە ئېرىشەلمەيدۇ»'),
(925, 6, 136, 'مۇشرىكلار اﷲ ياراتقان ئېكىنلەردىن ۋە چاھار پايلاردىن (يەنى تۆگە، كالا، قويلاردىن) اﷲ ئۈچۈن بىر ھەسسە (بۇتلىرى ئۈچۈن بىر ھەسسە) ئايرىپ قويۇپ، ئۆز گۇمانلىرىچە (ھېچقانداق دەلىلسىزلا): «بۇ اﷲ ئۈچۈندۇر، بۇ مەبۇدلىرىمىز ئۈچۈندۇر» دېدى. بۇتلىرىنىڭ نېسىۋېسىدىن اﷲ نىڭكىگە قوشۇشقا بولمايتتى، اﷲ نىڭ نېسىۋېسىدىن بۇتلىرىنىڭكىگە قوشۇشقا بولاتتى (مۇشرىكلار اﷲ قا ئاتىغان ھەسسىسىدىن بۇتلارغا ئاتىغان ھەسسىسىگە بىرەر نەرسە قوشۇلۇپ قالسا، ئۇنى بۇتلىرىنىڭ ھەسسىسى ئىچىدە قالدۇراتتى ۋە اﷲ باي، بۇنىڭغا موھتاج ئەمەس، دەيتتى. بۇتلىرىغا ئاتىغان ھەسسىسىدىن اﷲ قا ئاتىغان ھەسسىسىگە بىرەر نەرسە قوشۇلۇپ قالسا، ئۇنى بۇتلىرىنىڭ ھەسسىسىگە قايتۇرۇۋېتەتتى). ئۇلارنىڭ قارارى نېمىدېگەن قەبىھ!'),
(926, 6, 137, 'مۇشرىكلارنىڭ شېرىكلىرى (يەنى شەيتانلار) مۇشرىكلارنى ھالاك قىلىش ۋە ئۇلارنىڭ دىنىنى قالايمىقانلاشتۇرۇش ئۈچۈن، ئۇلارنىڭ نۇرغۇنلىرىغا بالىلىرىنى ئۆلتۈرۈشنى، شۇنىڭدەك (يەنى اﷲ قا ۋە بۇتلارغا ھەسسە ئايرىپ قويۇشتەك تەقسىماتنى شەيتانلارغا ۋە مۇشرىكلارغا چىرايلىق كۆرسەتكەندەك) چىرايلىق كۆرسەتتى. ئەگەر اﷲ خالىغان بولسا ئىدى، ئۇلار بۇ (قەبىھ) ئىشنى قىلمايتتى، ئۇلارنى اﷲ قا چاپلىغان يالغانلىرى بىلەن بىللە تەرك ئەت'),
(927, 6, 138, 'ئۇلار ئۆزلىرىنىڭ گۇمانىچە: «بۇ ھايۋانلار ۋە ئېكىنلەر (مەبۇدلىرىمىزغا خاس بولۇپ، باشقىلارغا) ھارامدۇر، ئۇلارنى پەقەت بىز خالىغان ئادەملەرلا يەيدۇ، بۇ ھايۋانلارنى مىنىش ھارامدۇر، بۇ ھايۋانلارنى بوغۇزلىغاندا اﷲ نىڭ ئىسمىنى ئېيتىشقا بولمايدۇ» دېدى. ئۇلار اﷲ قا يالغان چاپلىدى، اﷲ ئۇلارنى يالغان چاپلىغانلىقلىرى ئۈچۈن جازالايدۇ'),
(928, 6, 139, 'ئۇلار: «بۇ ھايۋانلارنىڭ قورساقلىرىدىكى بالىلىرى بىزنىڭ ئەر كىشىلىرىمىزگە خاستۇر، بىزنىڭ خوتۇن كىشىلىرىمىزگە ھارامدۇر» دېدى. ئەگەر تۇغۇلغان ھايۋان ئۆلۈك بولسا، بۇ ئەر كىشىلەر بىلەن خوتۇن كىشىلەر ئارىسىدا ئورتاقتۇر (يەنى ئۇنى ھەممەيلەن تەڭ يەيدۇ) دەپ (ھالال - ھارامنى تەيىنلەشتى). اﷲ قا يالغان چاپلىغانلىقلىرى ئۈچۈن اﷲ ئۇلارنى جازالايدۇ. شۈبھىسىزكى، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر'),
(929, 6, 140, 'ھاماقەتلىك ۋە نادانلىقلىرىدىن بالىلىرىنى ئۆلتۈرگەن ۋە اﷲ نامىدىن يالغان ئېيتىپ، اﷲ رىزىق قىلىپ بەرگەن نەرسىلەرنى ھارام بىلگەن كىشىلەر ھەقىقەتەن زىيان تارتتى، ئۇلار ھەقىقەتەن (توغرا يولدىن) ئاداشتى، ھىدايەت تاپمىدى'),
(930, 6, 141, 'اﷲ بىدىشلىك ۋە بىدىشسىز باغلارنى بەرپا قىلدى، مېۋىلىرى، دانلىرى (رەڭگى، تەمى، ھەجمى ۋە پۇرىقى) بىر - بىرىگە ئوخشىمايدىغان خورما دەرەخلىرىنى، زىرائەتلەرنى، (رەڭگى ۋە شەكلى) بىر - بىرىگە ئوخشايدىغان، (تەمى) ئوخشىمايدىغان زەيتۇن ۋە ئانارنى ياراتتى، مېۋىسى پىشقاندا، ئۇلاردىن يەڭلار، مېۋە (نىڭ ھوسۇلى) نى يىغقان كۈندە (يەنى يىغقان ۋاقىتتا)، ئۇنىڭ ئۆشرىسىنى ئادا قىلىڭلار، ئىسراپ قىلماڭلار، اﷲ ھەقىقەتەن ئىسراپ قىلغۇچىلارنى ياقتۇرمايدۇ'),
(931, 6, 142, 'اﷲ چاھارپايلارنى (يەنى تۆگە، كالا، قويلارنى)، يۈك كۆتۈرىدىغانلارنى، بوغۇزلىنىدىغانلارنى ياراتتى. اﷲ سىلەرگە رىزىق قىلىپ بەرگەن نەرسىلەردىن يەڭلار، شەيتاننىڭ ئارقىسىغا كىرمەڭلار، شەيتان سىلەرگە ھەقىقەتەن ئوچۇق دۈشمەندۇر'),
(932, 6, 143, '(اﷲ سىلەرگە يېيىش ھالال قىلىنغان) سەككىز خىل چاھارپاينى ياراتتى: قويدىن ئىككى (قوچقار ۋە ساغلىق)، ئۆچكىدىن ئىككى (تېكە ۋە ئۇرغاچى). (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «اﷲ (قوي بىلەن ئۆچكىدىن) ئىككى ئەركەكنى ھارام قىلدىمۇ؟ ياكى ئىككى چىشىنىمۇ ۋە ياكى ئىككى چىشىنىڭ قورسىقىدىكى قوزا بىلەن ئوغلاقنىمۇ؟ ئەگەر (سىلەر اﷲ بۇلارنى بىزگە ھارام قىلدى دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، قېنى ماڭا بىلىپ ئېيتىپ بېرىڭلار!»'),
(933, 6, 144, '(اﷲ سىلەرگە) تۆگىدىن ئىككىنى (يەنى ئەركەك بىلەن چىشىنى)، كالىدىن ئىككىنى (يەنى ئەركەك بىلەن چىشىنى) ياراتتى، (مۇشرىكلارغا) ئېيتقىنكى، «اﷲ ئىككى ئەركەكنى ھارام قىلدىمۇ؟ ياكى ئىككى چىشىنىمۇ ۋە ياكى ئىككى چىشىنىڭ قورسىقىدىكى بوتىلاق بىلەن موزايلىنىمۇ؟ ياكى اﷲ بۇنى سىلەرگە تەۋسىيە قىلغان چاغدا سىلەر ھازىرمىدىڭلار؟ كىشىلەرنى ئازدۇرۇش ئۈچۈن، بىلمەي تۇرۇپ اﷲ نامىدىن يالغان ئېيتقان ئادەمدىنمۇ زالىم كىشى بارمۇ؟ شۈبھىسىزكى، اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ»'),
(934, 6, 145, '(ئى مۇھەممەد! مەككە كۇففارلىرىغا) ئېيتقىنكى، ساڭا ۋەھيى قىلىنغان ئەھكاملار ئىچىدە، ئۆزى ئۆلۈپ قالغان نەرسە ياكى ئېقىپ چىققان قان ۋە ياكى چوشقا گۆشى - چوشقا نىجىس يېمەكلىكلەرگە ئادەتلەنگەنلىكى ئۈچۈن چوشقا گۆشى پاسكىنىدۇر - ۋە اﷲ تىن غەيرىينىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان گۇناھ مالدىن غەيرىينى ھەر قانداق ئادەم ئۈچۈن ھارام كۆرمەيمەن. كىمكى ئۇلاردىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) نائىلاج ۋە ئىختىيارسىز ھالدا ھاياتىنى ساقلاپ قالغۇدەك يېسە (ھېچ گۇناھ بولمايدۇ)، چۈنكى پەرۋەردىگارىڭ مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر'),
(935, 6, 146, 'يەھۇدىيلارغا توم تۇياقلىق (يەنى تۆگە، تۆگىقۇشقا ئوخشاش ئاچا تۇياقلىق بولماي توم تۇياقلىق بولغان) ھايۋانلارنىڭ ھەممىسىنى ھارام قىلدۇق، ئۇلارغا كالا، قويلارنىڭ دۈمبىسىدىكى ياكى ئۈچەيلىرىدىكى ياكى سۆڭىكىگە چاپلىشىقلىق ياغلىرىدىن باشقا ياغلىرىنى ھارام قىلدۇق، بۇ ئۇلارنىڭ (پەيغەمبەرلەرنى ئۆلتۈرۈش، جازانە قىلىش، كىشىلەرنىڭ مال - مۈلكىنى ناھەق يەۋېلىش قاتارلىق) زۇلۇملىرى تۈپەيلىدىن، ئۇلارغا بەرگەن جازايىمىزدۇر، بىز ئەلۋەتتە (بەرگەن خەۋەرلىرىمىزدە) راستچىلمىز'),
(936, 6, 147, '(ئى مۇھەممەد!) ئەگەر ئۇلار (يەنى يەھۇدىيلار) سېنى ئىنكار قىلسا، سەن: «پەرۋەردىگارىڭلار (قىلمىشىڭلار ئۈچۈن، سىلەرنى ئالدىراپ جازالىمىغانلىقتىن) كەڭ رەھمەت ئىگىسىدۇر، (اﷲ نىڭ رەھمىتىگە مەغرۇر بولماڭلاركى) اﷲ نىڭ ئازابى گۇناھكار قەۋمدىن قايتۇرۇلمايدۇ» دېگىن'),
(937, 6, 148, 'مۇشرىكلار: «ئەگەر اﷲ خالىسا ئىدى، بىز ۋە ئاتا - بوۋىلىرىمىز شېرىك كەلتۈرمەيتتۇق ۋە ھېچ نەرسىنى ھارام قىلمايتتۇق» دەيدۇ. ئۇلاردىن بۇرۇنقى كىشىلەر تاكى (بىزنىڭ ئازابىمىز نازىل بولۇپ) ئازابىمىزنى تېتىغانغا قەدەر (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) مۇشۇنداق (يەنى مۇشرىكلار سېنى ئىنكار قىلغاندەك) ئىنكار قىلغان ئىدى. (ئۇلارغا) ئېيتقىنكى، «سۆزۈڭلارنىڭ راستلىقىغا پاكىتىڭلار بولسا، بىزگە چىقىرىپ كۆرسىتىڭلار، سىلەر پەقەت گۇمانغىلا ئاساسلىنىسىلەر، اﷲ قا پەقەت يالغاننى چاپلايسىلەر»'),
(938, 6, 149, '(ئى مۇھەممەد!) ئېيتقىنكى، «(سىلەرنىڭ پاكىتىڭلار بولمىسا) اﷲ نىڭ ئېنىق پاكىتى بار، ئەگەر ئۇ خالىسا ھەممىڭلارنى، ئەلۋەتتە، ھىدايەت قىلاتتى»'),
(939, 6, 150, '(ئى مۇھەممەد! ئۇلارغا) «اﷲ بۇنى ھارام قىلدى، دەپ گۇۋاھلىق بېرىدىغان گۇۋاھچىلىرىڭلارنى چاقىرىپ كېلىڭلار» دېگىن، ئەگەر ئۇلار (ھازىر بولۇپ يالغاندىن) گۇۋاھلىق بەرسە، ئۇلارغا قوشۇلۇپ گۇۋاھلىق بەرمىگىن (چۈنكى ئۇ پۈتۈنلەي يالغاندۇر). بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن، ئۇلار پەرۋەردىگارىڭغا شېرىك كەلتۈرىدۇ'),
(940, 6, 151, 'ئېيتقىنكى، «سىلەر كېلىپ (ئاڭلاڭلار)، اﷲ ھارام قىلغان نەرسىلەرنى سىلەرگە ئوقۇپ بېرەي: سىلەر اﷲ قا ھېچ نەرسىنى شېرىك كەلتۈرمەڭلار، ئاتا - ئاناڭلارغا ياخشىلىق قىلىڭلار، نامراتلىقتىن قورقۇپ بالاڭلارنى ئۆلتۈرمەڭلار، بىز ئۇلارغا ۋە سىلەرگە رىزىق بېرىمىز، ئاشكارا ۋە يوشۇرۇن يامان ئىشلارغا يېقىن كەلمەڭلار، الله نىڭ ئەمرىگە خىلاپلىق قىلىپ (ناھەق) ئادەم ئۆلتۈرمەڭلار، ھەقلىق رەۋىشتە ئۆلتۈرۈش بۇنىڭدىن مۇستەسنا. سىلەرنىڭ چۈشىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ'),
(941, 6, 152, 'يېتىمنىڭ مېلىنى تاكى ئۇ بالاغەتكە يەتكەنگە قەدەر ئۇنىڭغا ئەڭ پايدىلىق ئۇسۇلدا تەسەررۇپ قىلىڭلار، ئۆلچەمنى ۋە تارازىنى توغرا قىلىڭلار، ھېچقانداق ئادەمنى كۈچى يەتمەيدىغان ئىشقا تەكلىپ قىلمايمىز. سۆز قىلغان (ھۆكۈم چىقارغان ياكى گۇۋاھلىق بەرگەن) چېغىڭلاردا ئادىل بولۇڭلار، (ھۆكۈم چىقىرىلغان ياكى گۇۋاھلىق بېرىلگەن ئادەم) تۇققىنىڭلار بولغان تەقدىردىمۇ، اﷲ قا بېرىلگەن ئەھدىگە ۋاپا قىلىڭلار، ئىبرەت ئېلىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ»'),
(942, 6, 153, 'بۇ مېنىڭ توغرا يولۇمدۇر، شۇ يولدا مېڭىڭلار، ناتوغرا يوللاردا ماڭماڭلار، ئۇلار سىلەرنى اﷲ نىڭ يولىدىن ئايرىۋېتىدۇ، (اﷲ نىڭ ئەمرىنى تۇتۇپ دوزاختىن) ساقلىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ'),
(943, 6, 154, 'ئاندىن ئۇلار (يەنى ئىسرائىل ئەۋلادى) ئۆز پەرۋەردىگارىغا مۇلاقات بولىدىغانلىقىغا ئىشەنسۇن دەپ، ياخشىلىق قىلغان ئادەمگە بېرىدىغان نېمىتىمىزنى مۇكەممەللەشتۈرۈش، (ئىسرائىل ئەۋلادىنىڭ دىنىي ئىشلىرىدا ئېھتىياجلىق بولغان) ھەر بىر نەرسىنى تەپسىلىي بايان قىلىش، (ئىسرائىل ئەۋلادىنى) ھىدايەت قىلىش ۋە (ئۇلارغا) رەھمەت قىلىش يۈزىسىدىن، مۇساغا كىتاب (يەنى تەۋرات) ئاتا قىلدۇق'),
(944, 6, 155, 'بۇ بىز نازىل قىلغان (قۇرئان) مۇبارەك كىتابتۇر. اﷲ نىڭ رەھمىتىنى ئۈمىد قىلغۇچىلاردىن بولۇش ئۈچۈن، ئۇنىڭغا ئەگىشىڭلار، (ئۇنىڭغا مۇخالىپەتچىلىك قىلىشتىن) ساقلىنىڭلار'),
(945, 6, 156, '(مېنىڭ قۇرئان نازىل قىلىشىم) سىلەرنىڭ، پەقەت بىزدىن ئىلگىرىكى تائىپىگىلا (يەنى يەھۇدىيلار ۋە ناسارالارغىلا) كىتاب نازىل قىلىنغان، ئۇلارنىڭ ئوقۇغان كىتابلىرىدىن بىزنىڭ زادىلا خەۋىرىمىز يوق، دېمەسلىكىڭلار ئۈچۈندۇر'),
(946, 6, 157, 'ياكى سىلەرنىڭ، ئەگەر بىزگە كىتاب نازىل قىلىنغان بولسا، بىز ئەلۋەتتە ئۇلاردىن ئارتۇق ھىدايەت تاپقان بولاتتۇق، دېمەسلىكىڭلار ئۈچۈندۇر. سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن ھەقىقەتەن پاكىت، ھىدايەت ۋە رەھمەت نازىل بولدى، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغان ۋە ئۇلاردىن يۈز ئۆرۈگەن كىشىدىنمۇ زالىم ئادەم بارمۇ؟ بىزنىڭ ئايەتلىرىمىزدىن يۈز ئۆرۈگەنلەرنى ئۇلارنىڭ يۈز ئۆرۈگەنلىكلىرى ئۈچۈن قاتتىق ئازاب بىلەن جازالايمىز'),
(947, 6, 158, 'ئۇلار پەقەت (ئۆلۈم) پەرىشتىلىرىنىڭ ياكى پەرۋەردىگارىڭنىڭ ياكى پەرۋەردىگارىڭنىڭ (قۇياشنىڭ مەغرىبتىن چىقىشىدىن ئىبارەت) بەزى ئالامەتلىرىنىڭ كېلىشىنىلا كۈتىدۇ. پەرۋەردىگارىڭنىڭ بەزى ئالامەتلىرى كەلگەن كۈندە ئىلگىرى ئىمان ئېيتمىغانلارنىڭ ياكى ئىمان ئېيتىپ ياخشى ئىش قىلمىغانلارنىڭ ئېيتقان ئىمانى پايدىسىز بولىدۇ. «(سىلەر ئۆزۈڭلارغا كېلىدىغان ئىشلارنى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە كۈتىمىز» دېگىن'),
(948, 6, 159, 'دىنىدا بۆلگۈنچىلىك قىلىپ تۈرلۈك پىرقىلەرگە ئايرىلغانلاردىن سەن ئادا - جۇداسەن؛ ئۇلارنىڭ ئىشى پەقەت اﷲ غىلا خاستۇر (يەنى اﷲ نىڭ باشقۇرۇشىدىدۇر)، كېيىن (يەنى ئاخىرەتتە) اﷲ ئۇلارنىڭ قىلمىشلىرىنى ئۆزىگە ئېيتىپ بېرىدۇ'),
(949, 6, 160, 'بىرەر ياخشىلىق قىلغان ئادەم ئون ھەسسە ئارتۇق ساۋابقا ئىگە بولىدۇ، بىرەر يامانلىق قىلغان ئادەمگە پەقەت ئۇنىڭ يامانلىقىغا باراۋەرلا جازا بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ'),
(950, 6, 161, '(ئى مۇھەممەد! بۇ مۇشرىكلارغا) ئېيتقىنكى، «پەرۋەردىگارىم مېنى توغرا يولغا يەنى توغرا دىنغا، باتىل دىنلاردىن توغرا دىنغا بۇرالغان ئىبراھىمنىڭ دىنىغا باشلىدى، ئىبراھىم مۇشرىكلاردىن ئەمەس ئىدى»'),
(951, 6, 162, '(ئى مۇھەممەد!) ئېيتقىنكى، «مېنىڭ نامىزىم، قۇربانلىقىم، ھاياتىم ۋە ماماتىم (دۇنيادا قىلغان ياخشىلىقلىرىم ۋە تائەت - ئىبادەتلىرىم) ئالەملەرنىڭ پەرۋەردىگارى اﷲ ئۈچۈندۇر'),
(952, 6, 163, 'اﷲ نىڭ شېرىكى يوقتۇر، مەن مۇشۇنىڭغا (يەنى يالغۇز اﷲ غىلا خالىس ئىبادەت قىلىشقا) بۇيرۇلدۇم، مەن مۇسۇلمانلارنىڭ ئەۋۋىلىمەن»'),
(953, 6, 164, '(ئى مۇھەممەد!) ئېيتقىنكى، «اﷲ ھەممە نەرسىنىڭ پەرۋەردىگارى تۇرسا، ئۇنىڭدىن باشقا پەرۋەردىگار تەلەپ قىلامدىمەن؟» ھەر ئادەم ئۆزى قىلغان گۇناھقا ئۆزى جاۋابكار، بىر گۇناھكار ئادەم باشقا ئادەمنىڭ گۇناھىغا جاۋابكار ئەمەس (يەنى بىر ئادەمنىڭ گۇناھى بىلەن باشقا بىر ئادەم جازاغا تارتىلمايدۇ). كېيىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتىسىلەر، سىلەر دەتالاش قىلىشقان ھەق بىلەن ناھەقنى ئۇ سىلەرگە ئېيتىپ بېرىدۇ'),
(954, 6, 165, 'اﷲ سىلەرنى (ئۆتكەنكى ئۈممەتلەرنىڭ) ئورۇنباسارلىرى قىلدى، اﷲ سىلەرنى بەرگەن نېمىتىگە شۈكۈر قىلسۇن دەپ، بەزىڭلارنى بەزىڭلاردىن بىر قانچە دەرىجە يۇقىرى قىلدى، پەرۋەردىگارىڭ (ئاسىيلىق قىلغۇچىلاردىن) ئەلۋەتتە تېز ھېساب ئالغۇچىدۇر، (ئىتائەت قىلغۇچىلارنى) ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'ئەلىف، لام، مىم، صاد'),
(956, 7, 2, '(ئى مۇھەممەد! كاپىرلارنى) ئاگاھلاندۇرۇشۇڭ ئۈچۈن، مۆمىنلەرگە ۋەز - نەسىھەت قىلىشىڭ ئۈچۈن، بۇ كىتاب (يەنى قۇرئان) ساڭا نازىل قىلىندى، ئۇنىڭدىن (يەنى ئۇنى تەبلىغ قىلىشتا قەۋمىڭنىڭ ئۇنى ئىنكار قىلىشىدىن ئەنسىرەپ) يۈرىكىڭ سىقىلمىسۇن'),
(957, 7, 3, '(ئى ئىنسانلار!) سىلەر پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) ئەگىشىڭلار، اﷲ نى قويۇپ، (جىنلاردىن ۋە ئىنسانلاردىن بولغان، سىلەرنى ئازدۇرىدىغان) دوستلارغا ئەگەشمەڭلار، سىلەر ۋەز - نەسىھەتنى ئاز قوبۇل قىلىسىلەر'),
(958, 7, 4, 'بىز نۇرغۇن شەھەر (ئاھالىسىنى) نى ھالاك قىلدۇق، بىزنىڭ ئازابىمىز ئۇلارغا كېچىسى ياكى چۈشلۈكى ئۇخلاۋاتقان چاغلىرىدا كەلدى'),
(959, 7, 5, 'بىزنىڭ ئازابىمىز كەلگەن چاغدا، ئۇلارنىڭ چەككەن پەريادى: بىز ھەقىقەتەن زالىم ئىدۇق، دېيىشتىنلا ئىبارەت بولدى'),
(960, 7, 6, 'بىز ئەلۋەتتە پەيغەمبەر ئەۋەتىلگەن ئۈممەتلەردىن (پەيغەمبەرلەر تەبلىغ قىلدىمۇ؟ سىلەر قانداق جاۋاب بەردىڭلار؟ دەپ) سورايمىز، پەيغەمبەرلەردىنمۇ ئەلۋەتتە (اﷲ تاپشۇرغان ۋەزىپىنى ئادا قىلدىڭلارمۇ؟ دەپ) سورايمىز'),
(961, 7, 7, '(قىيامەت كۈنى ئۇلارنىڭ ئەھۋالىنى) ئۇلارغا ھەقىقىي بىلىش ئاساسىدا ئېيتىپ بېرىمىز، بىز ئۇلاردىن ئايرىلمىغان ئىدۇق (يەنى ئۇلارنىڭ ھەممە ئەھۋالىدىن خەۋەرداردۇرمىز)'),
(962, 7, 8, 'بۇ كۈندە بەندىلەرنىڭ (ياخشى يامان ئەمەللىرىنىڭ) ئۆلچىنىدىغانلىقى ھەقتۇر. ياخشى ئەمەللىرى ئېغىر چىققانلار نىجات تاپقۇچىلاردۇر'),
(963, 7, 9, 'ياخشى ئەمەللىرى يېنىك چىققانلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى تۈپەيلىدىن ئۆزلىرىگە زىيان سالغۇچىلاردۇر'),
(964, 7, 10, 'شۈبھىسىزكى، سىلەرنى زېمىندا يەرلەشتۈردۇق، زېمىندا سىلەرگە تۇرمۇش لازېمەتلىكلىرىنى پەيدا قىلدۇق، (سىلەرگە پەرۋەردىگارىڭلارنىڭ مۇشۇنداق نېمەتلىرى بار تۇرۇقلۇق ئۇنىڭغا) ناھايىتى ئاز شۈكۈر قىلىسىلەر'),
(965, 7, 11, 'شۈبھىسىزكى بىز سىلەرنى ياراتتۇق، ئاندىن سىلەرنى شەكىلگە كىرگۈزدۇق، ئاندىن پەرىشتىلەرگە: “سىلەر ئادەمگە (ھۆرمەت بىلدۈرۈش يۈزىسىدىن) سەجدە قىلىڭلار›› دېدۇق، ئىبلىستىن باشقىسى سەجدە قىلدى. ئۇ سەجدە قىلغۇچىلاردىن بولمىدى'),
(966, 7, 12, 'اﷲ (ئىبلىسقا) ‹‹سېنى سەجدە قىلىشقا بۇيرۇغان چېغىمدا نېمىشقا سەجدە قىلمىدىڭ؟ ›› دېدى. ئىبلىس: ‹‹مەن ئۇنىڭدىن ئارتۇق. مېنى ئوتتىن، ئۇنى لايدىن ياراتتىڭ›› دېدى'),
(967, 7, 13, 'اﷲ (ئىبلىسقا) ئېيتتى: ‹‹سەن بۇيەردىن (يەنى جەننەتتىن) چۈشۈپ كەت، بۇ يەردىن ئۆزۈڭنى چوڭ تۇتۇشۇڭغا بولمايدۇ، يوقال، سەن ھەقىقەتەن پەسكەشلەردىنسەن››'),
(968, 7, 14, 'ئىبلىس: ‹‹ماڭا كىشىلەر قايتا تىرىلدۈرۈلىدىغان كۈنگىچە (يەنى قىيامەت كۈنىگىچە) مۆھلەت بەرگىن›› دېدى'),
(969, 7, 15, 'اﷲ: ‹‹ساڭا مۆھلەت بېرىلىدۇ›› دېدى'),
(970, 7, 16, 'ئىبلىس ئېيتتى: ‹‹سېنىڭ مېنى ئازدۇرغانلىقىڭدىن ئۇلار (يەنى ئادەم ئەلەيھىسسالام ۋە ئۇنىڭ ئەۋلادىغا ۋەسۋەسە قىلىش) ئۈچۈن چوقۇم سېنىڭ توغرا يولۇڭ ئۈستىدە ئولتۇرىمەن'),
(971, 7, 17, 'ئاندىن ئۇلارغا چوقۇم ئالدىدىن، ئارقىسىدىن، ئوڭدىن، سولىدىن ھۇجۇم قىلىمەن، ئۇلارنىڭ كۆپچىلىكىنىڭ (نېمىتىڭگە) شۈكۈر قىلغۇچىلار ئەمەسلىكىنى كۆرىسەن››'),
(972, 7, 18, 'اﷲ ئېيتتى: ‹‹جەننەتتىن ئەيىبلەنگەن ۋە (رەھمىتىمدىن) مەھرۇم قىلىنغان ھالدا چىق، ئۇلارنىڭ ئىچىدىن ساڭا ئەگەشكەنلەرنى قوشۇپ، ھەممىڭلار بىلەن چوقۇم جەھەننەمنى تولدۇرىمەن››'),
(973, 7, 19, '‹‹ئى ئادەم! سەن ئايالىڭ بىلەن ئىككىڭلار بىللە جەننەتتە تۇرۇڭلار، جەننەتنىڭ مېۋىلىرىدىن خالىغىنىڭلارچە يەڭلار، بۇ دەرەخكە يېقىنلاشماڭلار، (ئۇنىڭغا يېقىنلاشساڭلار) زالىملاردىن بولۇپ قالىسىلەر››'),
(974, 7, 20, 'لېكىن شەيتان ئۇلارنىڭ يېپىقلىق ئەۋرەتلىرىنى ئېچىۋېتىش ئۈچۈن، ئۇلارغا ۋەسۋەسە قىلدى، شەيتان ئېيتتى: ‹‹پەرۋەردىگارىڭلارنىڭ سىلەرگە بۇ دەرەخنىڭ مېۋىسىنى يېيىشنى مەنئى قىلغانلىقى پەقەت سىلەرنىڭ پەرىشتە بولۇپ كەتمەسلىكىڭلار ياكى (جەننەتتە) مەڭگۈ تۇرۇپ قالماسلىقىڭلار ئۈچۈندۇر››'),
(975, 7, 21, 'شۇنىڭدەك ئۇلارغا: ‹‹مەن چوقۇم سىلەرگە سادىقمەن›› دەپ قەسەم ئىچىپ بەردى'),
(976, 7, 22, 'ئالدامچىلىق ئۇسۇللىرى ئارقىلىق ئۇلارنى (يۇقىرى مەرتىۋىدىن تۆۋەن مەرتىۋىگە) چۈشۈردى. دەرەخنىڭ مېۋىسىنى يېگەندىن كېيىنلا ئۇلارنىڭ ئەۋرەتلىرى ئېچىلىپ قالدى، ئۇلار جەننەتتىكى دەرەخلەرنىڭ ياپراقلىرى بىلەن ئەۋرەتلىرىنى يېپىشقا كىرىشتى، ئۇلارنىڭ پەرۋەردىگارى (ئۇلارغا كايىپ): ‹‹مەن سىلەرگە بۇ دەرەخنى (يەنى مېۋىسىنى) يېيىشنى مەنئى قىلمىغانمىدىم؟ شۈبھىسىزكى، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر، دېمىگەنمىدىم؟ ›› دەپ نىدا قىلدى'),
(977, 7, 23, 'ئۇلار: پەرۋەردىگارىمىز! بىز ئۆزىمىزگە ئۆزىمىز زۇلۇم قىلدۇق، ئەگەر سەن بىزگە مەغپىرەت قىلمىساڭ، بىزگە رەھىم قىلمىساڭ، بىز چوقۇم زىيان تارتقۇچىلاردىن بولىمىز›› دېدى'),
(978, 7, 24, 'اﷲ ئېيتتى: ‹‹سىلەر بىر - بىرىڭلارغا (يەنى شەيتان ئىنسانغا، ئىنسان شەيتانغا) دۈشمەن بولغان ھالدا چۈشۈڭلار، ئەجىلىڭلار پۈتكەنگە قەدەر يەر يۈزىدە تۇرىسىلەر ۋە ئۇنىڭدىن پايدىلىنىسىلەر››'),
(979, 7, 25, 'اﷲ ئېيتتى: ‹‹يەر يۈزىدە ياشايسىلەر، يەر يۈزىدە ئۆلۈسىلەر، (قىيامەت كۈنى) يەنە يەر ئاستىدىن چىقىرىلىسىلەر››'),
(980, 7, 26, 'ئى ئادەم بالىلىرى! سىلەرگە بىز ھەقىقەتەن ئەۋرىتىڭلارنى ياپىدىغان لىباسنى ۋە زىننەتلىنىدىغان لىباسنى چۈشۈردۇق، تەقۋادارلىق لىباسى ئەڭ ياخشىدۇر، ئەنە شۇلار (يەنى ئىنسان بالىلىرىغا سەترى ئەۋرەت ئۈچۈن لىباس ياراتماق) ئۇلارنىڭ ئىبرەت ئېلىشى ئۈچۈن اﷲ نىڭ (بەندىلىرىگە بولغان پەزلى - مەرھەمىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر'),
(981, 7, 27, 'ئى ئادەم بالىلىرى! شەيتان سىلەرنىڭ ئاتا - ئاناڭلارنى (ئازدۇرۇپ) جەننەتتىن چىقىرىۋەتكەندەك، سىلەرنىمۇ ئازدۇرمىسۇن، شەيتان ئۇلارنىڭ ئەۋرەتلىرىنى ئۆزلىرىگە كۆرسىتىش ئۈچۈن كىيىملىرىنى سالغۇزۇۋەتكەن ئىدى (يەنى ئۇلارنىڭ يالىڭاچ بولۇپ قېلىشىغا شەيتان سەۋەبچى بولغان ئىدى). شەيتان ۋە ئۇنىڭ قول ئاستىدىكىلىرى سىلەرنى كۆرەلەيدۇ، سىلەر ئۇلارنى كۆرەلمەيسىلەر، شۈبھىسىزكى، بىز شەيتانلارنى ئىمان ئېيتمايدىغانلارنىڭ دوستى قىلدۇق'),
(982, 7, 28, 'ئۇلار (يەنى مۇشرىكلار) بىرەر يامان ئىشنى قىلغان چاغلىرىرىدا: ‹‹ئاتا - بوۋىلىرىمىزنىڭ مۇشۇنداق قىلغانلىقىنى كۆرگەن ئىدۇق، اﷲ بىزنىمۇ شۇنداق قىلىشقا بۇيرۇغان›› دەيدۇ. (ئى مۇھەممەد! ئۇلارغا) ئېيىتقىنكى، ‹‹اﷲ (بەندىلىرىنى) ھەقىقەتەن يامان ئىشقا بۇيرۇمايدۇ، ئۆزلىرىڭلار بىلمەيدىغان نەرسىلەرنى اﷲ نامىدىن يالغان ئېيتامسىلەر؟ ››'),
(983, 7, 29, '(ئى مۇھەممەد!) ئېيىتقىنكى، «پەرۋەردىگارىم مېنى ئادىل بولۇشقا بۇيرۇدى. ھەربىر سەجدە قىلغاندا پۈتۈنلەي اﷲ قا يۈزلىنىڭلار، اﷲ قا ئىخلاس بىلەن ئىبادەت قىلىڭلار، سىلەر اﷲ دەسلەپتە پەيدا قىلغان ھالىتىڭلاردا (ئەسلىگە) قايتىسىلەر»'),
(984, 7, 30, 'اﷲ (سىلەردىن) بىر گۇرۇھنى ھىدايەت قىلدى؛ يەنە بىر گۇرۇھقا گۇمراھلىق تېگىشلىك بولدى. شۈبھىسىزكى، ئۇلار اﷲ نى قويۇپ، شەيتانلارنى دوست تۇتقان تۇرۇقلۇق ئۆزلىرىنى توغرا يولدا گۇمان قىلىدۇ'),
(985, 7, 31, 'ئى ئادەم بالىلىرى! ھەر ناماز ۋاقتىدا (ياكى تاۋاپ ۋاقتىدا ئەۋرىتىڭلارنى سەترى قىلىپ تۇرىدىغان) كىيىمىڭلارنى كىيىڭلار، يەڭلار، ئىچىڭلار، ئىسراپ قىلماڭلار، اﷲ ئىسراپ قىلغۇچىلارنى ھەقىقەتەن ياقتۇرمايدۇ'),
(986, 7, 32, 'ئېيىتقىنكى، «اﷲ بەندىلىرى ئۈچۈن ياراتقان لىباسلارنى، شېرىن، پاك رىزىقلارنى كىم ھارام قىلدى؟» ئېيىتقىنكى، ‹‹ئۇلار بۇ دۇنيادا مۆمىنلەر ئۈچۈن يارىتىلغان (گەرچە ئۇلارغا كۇففارلار شېرىك بولسىمۇ)، ئاخىرەتتە بولسا مۆمىنلەرگىلا خاستۇر». (اﷲ نىڭ بىر ۋە شېرىكى يوق ئىكەنلىكىنى بىلىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلى بايان قىلىمىز'),
(987, 7, 33, '(ئى مۇھەممەد! ئۇلارغا) ئېيىتقىنكى، «پەرۋەردىگارىم ئاشكارا ۋە يوشۇرۇن يامان ئىشلارنىڭ ھەممىسىنى، گۇناھلارنى، (كىشىلەرگە) ناھەق چېقىلىشنى، (اﷲ نىڭ شېرىكى بولۇشقا) اﷲ ھېچقانداق دەلىل چۈشۈرمىگەن نەرسىلەرنى اﷲ قا شېرىك كەلتۈرۈشنى، ئۆزۈڭلار بىلمەيدىغان نەرسىلەرنى اﷲ نامىدىن قالايمىقان سۆزلەشنى ھارام قىلدى››'),
(988, 7, 34, '(پەيغەمبەرلىرىنى ئىنكار قىلغان) ھەر بىر ئۈممەتنىڭ (ھالاك بولىدىغان) مۇئەييەن ۋاقتى بولىدۇ، (ھالاك بولىدىغان) ۋاقتى يەتكەندە، ئۇلار بىردەممۇ ئىلگىرى - كېيىن بولمايدۇ (يەنى دەرھال ھالاك بولىدۇ)'),
(989, 7, 35, 'ئى ئادەم بالىلىرى! ئىچىڭلاردىن مېنىڭ ئايەتلىرىمنى سىلەرگە بايان بېرىدىغان پەيغەمبەرلىرىم كەلگەندە، تەقۋادارلىق قىلغان ۋە (ئەمەلىنى) تۈزەتكەنلەرگە (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر'),
(990, 7, 36, 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار، ئۇنىڭغا ئىمان ئېيتىشقا گەدەنكەشلىك قىلغانلار دوزاخ ئەھلىدۇر، ئۇلار ھەمىشە دوزاختا قالغۇسىدۇ'),
(991, 7, 37, 'اﷲ قا يالغان چاپلىغان ياكى ئۇنىڭ ئايەتلىرىنى ئىنكار قىلغانلاردىنمۇ زالىم كىشى بارمۇ؟ ئۇلار لەۋھۇلمەھپۇزدا يېزىلغان (رىزىقتىن ۋە ئەجەلدىن) ئۆز نېسىۋىسىنى كۆرىدۇ، ھەتتا بىزنىڭ پەرىشتىلىرىمىز ئۇلارنىڭ جانلىرىنى ئېلىش ئۈچۈن كەلگەندىمۇ، ئۇلارغا: ‹‹اﷲ نى قويۇپ ئىبادەت قىلغان مەبۇدلىرىڭلار قەيەردە؟ ›› دەيدۇ، ئۇلار: ‹‹ئۇلار بىزدىن غايىب بولۇپ كەتتى›› دەيدۇ. ئۇلار كاپىر بولغانلىقلىرىغا ئۆزلىرىنڭ زىيىنىغا گۇۋاھلىق بېرىدۇ (يەنى كاپىر بولغانلىقلىرىغا ئىقرار قىلىدۇ)'),
(992, 7, 38, 'اﷲ (ئايەتلىرىنى ئىنكار قىلغانلارغا) ئېيتىدۇ: ‹‹سىلەردىن ئىلگىرى ئۆتكەن، جىنلاردىن ۋە ئىنسانلاردىن بولغان (كاپىر) ئۈممەتلەر بىلەن بىرلىكتە دوزاخقا كىرىڭلار››. ھەرقاچان بىر ئۈممەت دوزاخقا كىرىدىكەن، ئۇ ئۆزىدىن ئىلگىرىكى ئۈممەتكە (ئۇنىڭغا ئەگىشىپ گۇمراھ بولغانلىقى ئۈچۈن) لەنەت ئوقۇيدۇ. ئۇلارنىڭ ھەممىسى دوزاخقا كىرىپ بولۇپ جەملەشكەندە، ئۇلارنىڭ ئىچىدىكى كېيىن كىرگەنلەر (يەنى ئەگەشكۈچىلەر) ئىلگىرى كىرگەنلەرگە (يەنى باشلىقلىرىغا) قارىتىپ: ‹‹ئى پەرۋەردىگارىمىز! بۇلار بىزنى ئازدۇرغان. ئۇلارغا ئىككى ھەسسە دوزاخ ئازابى بەرگىن›› دەيدۇ اﷲ: ‹‹ھەممىگە ئىككى ھەسسە ئازاب بېرىلىدۇ، لېكىن سىلەر (ئازابنىڭ دەھشىتىنى) بىلمەيسىلەر›› دەيدۇ'),
(993, 7, 39, 'ئۇلارنىڭ ئارىسىدىكى ئىلگىرىكىلەر كېيىنكىلەرگە: ‹‹سىلەرنىڭ بىزدىن ھېچقانداق ئارتۇقچىلىقىڭلار يوق (يەنى گۇمراھلىقتا ۋەئازابقا تېگىشلىك بولۇشتا بىر - بىرىمىزگە ئوخشاشمىز)، قىلمىشلىرىڭلار تۈپەيلىدىن ئازابنى تېتىڭلار›› دەيدۇ'),
(994, 7, 40, 'شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ئۇلارغا ئىمان ئېيتىشقا گەدەنكەشلىك قىلغانلارغا ئاسماننىڭ دەرۋازىسى ئېچىلمايدۇ (يەنى ئۇلارنىڭ ياخشى ئەمەلى ۋە دۇئاسى قوبۇل بولمايدۇ)، تاكى تۆگە يىڭنىنىڭ تۆشۈكىدىن ئۆتكۈچە ئۇلار جەننەتكە كىرمەيدۇ (يەنى ئۇلار ھەرگىزمۇ جەننەتكە كىرمەيدۇ)، گۇناھكارلارغا مۇشۇنداق جازا بېرىمىز'),
(995, 7, 41, 'دوزاختا ئۇلارنىڭ ئاستىغا سالىدىغان كۆرپىسىمۇ ئوتتىن، ئۈستىگە يېپىنىدىغان يوتقىنىمۇ ئوتتىن بولىدۇ، زالىملارغا مۇشۇنداق جازا بېرىمىز'),
(996, 7, 42, 'ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار - كىشىنى پەقەت كۈچى يېتىدىغان ئىشقىلا تەكلىپ قىلىمىز ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە ھەمىشە قالىدۇ'),
(997, 7, 43, 'ئۇلارنىڭ كۆڭۈللىرىدىكى ئۆچمەنلىكلىرىنى ئېلىپ تاشلىدۇق (يەنى جەننەتكە كىرگەنلەر ئارىسىدا ئۆزئارا ئاغرىق - ئاداۋەت بولمايدۇ). ئۇلارنىڭ (سارايلىرى) ئاستىدىن (جەننەتنىڭ) ئۆستەڭلىرى ئېقىپ تۇرىدۇ، ئۇلار: «جىمى ھەمدۇسانا بىزنى بۇ (نېمەتلەرنى ھاسىل قىلىشقا) يېتەكلىگەن اﷲ قا بولسۇنكى، اﷲ بىزنى يېتەكلىمىگەن بولسا، توغرا يول تاپمىغان بولاتتۇق، پەرۋەردىگارىمىزنىڭ پەيغەمبەرلىرى بىزگە ھەقنى ئېلىپ كەلدى›› دەيدۇ، ئۇلارغا: «قىلغان ئەمەلىڭلار ئۈچۈن سىلەرگە بېرىلگەن جەننەت مانا مۇشۇ›› دەپ نىدا قىلىنىدۇ (يەنى پەرىشتىلەر شۇنداق دەپ نىدا قىلىدۇ)'),
(998, 7, 44, 'ئەھلى دوزاخقا: ‹‹بىز پەرۋەردىگارىمىز بىزگە ۋەدە قىلغان نەرسىنى ھەق تاپتۇق، سىلەرمۇ پەرۋەردىگارىڭلار ۋەدە قىلغان نەرسىنى ھەق تاپتىڭلارمۇ؟ ›› دەپ توۋلايدۇ. ئۇلار: ‹‹ھەئە›› دەيدۇ، شۇنىڭ بىلەن بىر جاكارچى ئۇلارنىڭ ئارىسىدا (مۇنداق دەپ) جاكارلايدۇ: ‹‹زالىملارغا اﷲ نىڭ لەنىتى بولسۇن'),
(999, 7, 45, 'زالىملار كىشىلەرنى اﷲ نىڭ داغدام يولىدىن توسىدۇ، ئۇنىڭ ئەگرى بولۇشىنى تىلەيدۇ ھەمدە ئۇلار ئاخىرەتكە ئىشەنمەيدۇ'),
(1000, 7, 46, 'ئۇلارنىڭ ئارىسىدا بىر توسما بولىدۇ. توسمىنىڭ ئۈستىدە (ئەئرافتا) ئەھلى جەننەت ۋە ئەھلى دوزاخنى ئۇلارنىڭ سىماسىدىن تونۇيدىغان ئادەملەر (يەنى ئەھلى ئەئراف) بولىدۇ، ئۇلار ئەھلى جەننەتكە: «سىلەرگە ئامانلىق تىلەيمىز›› دەپ توۋلايدۇ. ئۇلار جەننەتكە كىرمىگەن، (ئەمما) كىرىش ئۈمىدىدە تۇرغانلاردۇر'),
(1001, 7, 47, 'ئۇلارنىڭ كۆزلىرى ئەھلى دوزاخ تەرەپكە بۇرۇلغاندا: ‹‹ئى پەرۋەردىگارىمىز! بىزنى زالىم قەۋم بىلەن بىللە قىلمىغىن›› دەيدۇ'),
(1002, 7, 48, 'ئەئرافتىكىلەر (يەنى ياخشىلىقلىرى بىلەن يامانلىقلىرى تەڭ بولۇپ قالغان، جەننەتتىمۇ ئەمەس، دوزاختىمۇ ئەمەس كىشىلەر) سىماسىدىن تونۇيدىغان (دوزىخى) ئادەملەرگە: ‹‹توپلىغان پۇل - ماللىرىڭلار ۋە تەكەببۇرلۇقۇڭلار سىلەرگە نېمىگە ئەسقاتتى؟ ›› دەپ توۋلايدۇ'),
(1003, 7, 49, '(ئەئرافتىكىلەر دوزىخىلار مۆمىنلەرنىڭ پېقىرلىرىنى كۆرسىتىپ تۇرۇپ) سىلەر اﷲ نىڭ رەھمىتىگە ئېرىشەلمەيدۇ، دەپ قەسەم قىلغان كىشىلەر مۇشۇلارمۇ؟ (مانا بۇلارغا) جەننەتكە كىرىڭلار، سىلەرگە (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر (دېيىلدى دەيدۇ)'),
(1004, 7, 50, 'ئەھلى دوزاخ ئەھلى جەننەتكە: ‹‹سۇدىن ياكى اﷲ سىلەرگە بەرگەن ئىچىدىغان نەرسىلەردىن بىزگە بىر ئاز قۇيۇپ بەرسەڭلار! ›› دەپ توۋلايدۇ، ئەھلى جەننەت ئۇلارغا (جاۋابەن): ‹‹اﷲ ئۇلارنى كاپىرلارغا ھارام قىلغان›› دەيدۇ'),
(1005, 7, 51, 'كاپىرلار دىنلىرىنى مەسخىرە ۋە ئويۇنچۇق قىلىۋالدى، ئۇلارنى دۇنيا تىرىكچىلىكى ئالدىدى، ئۇلارنىڭ بۈگۈنكى كۈنگە مۇلاقات بولۇشنى ئۇنتۇغانلىقلىرى ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرىغا ئوخشاش، بىز بۈگۈن ئۇلارنى ئۇنتۇيمىز (يەنى ئۇلارن دوزاختا قالدۇرىمىز)'),
(1006, 7, 52, 'ئۇلارغا كىتاب (يەنى قۇرئان) بەردۇق، ئۇنى (يەنى قۇرئاننىڭ مەنىللىرىنى) بىلش ئاساسىدا بايان ۋە رەھمەت قىلدۇق'),
(1007, 7, 53, 'ئۇلار (يەنى كۇففارلار) پەقەت (ئۆزلىرىگە ۋەدە قىلىنغان ئازابنىڭ) نەتىجىسىنىلا كۈتىدۇ، ئۇنىڭ نەتىجىسى كەلگەن كۈنى (يەنى قىيامەت كۈنى)، ئلگىرى كىتابقا ئەمەل قىلمىغانلار: ‹‹پەرۋەردىگارىمىزنىڭ ئەلچىللىرى ھەقىقەتنى ئېلىپ كەلگەن ئىدى (بىز ئۇلارغا ئىمان ئېيتمىدۇق، بۈگۈن) بىزگە شاپائەت قىلىدىغان شاپائەتچىلەر بارمىدۇ؟ ياكى گۇناھ قىلىشتىن قول ئۈزۈپ، ياخشى ئەمەللەرنى قىلىش ئۈچۈن دۇنياغا قايتۇرۇلۇشىمىزغا بولامدىغاندۇ›› دەيدۇ. ئۇلار ئۆزلىرىگە زىيان سالدى، ئۇلارنىڭ اﷲ نىڭ شېرىكلىرى دەپ ئىپتىرا قىلغان مەبۇدلىرى ئۇلارنىڭ كۆزىدىن غايىب بولدى'),
(1008, 7, 54, 'شۈبھىسىزكى، پەرۋەردىگارىڭلار اﷲ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە (يەنى ئالتە دەۋردە) ياراتتى، ئاندىن (ئۆزىنىڭ ئۇلۇغلۇقىغا لايىق رەۋىشتە) ئەرش ئۈستىدە قارار ئالدى، اﷲ كېچە بىلەن (كېچىنىڭ قاراڭغۇلۇقى بىلەن) كۈندۈزنى ياپىدۇ، كېچە بىلەن كۈندۈز بىر - بىرىنى قوغلىشىدۇ (يەنى داۋاملىق ئورۇن ئالمىشىپ تۇرىدۇ): اﷲ قۇياش، ئاي ۋە يۇلتۇزلارنى اﷲ نىڭ ئەمرىگە بويسۇندۇرۇلغۇچى قىلىپ ياراتتى، يارىتىش ۋە ئەمر قىلىش (يەنى كائىناتنى تەسەررۇپ قىلىش) راستىنلا اﷲ نىڭ ئىلكىدىدۇر. ئالەملەرنىڭ پەرۋەردىگارى اﷲ نىڭ دەرىجىسى كاتتىدۇر'),
(1009, 7, 55, 'پەرۋەردىگارىڭلارغا تۆۋەنچىلىك بىلەن يوشۇرۇن دۇئا قىلىڭلار، (دۇئا قىلغاندا كەلسە - كەلمەس سۆزلەپ، توۋلاپ) ھەددىدىن ئاشۇرۋەتكەنلەرنى اﷲ ياقتۇرمايدۇ'),
(1010, 7, 56, 'اﷲ (پەيغەمبەرلەرنى ئەۋەتىپ) يەر يۈزىنى تۈزىگەندىن كېيىن، يەر يۈزىدە بۇزۇقچىلىق قىلماڭلار (اﷲ نىڭ ئازابىدىن) قورققان ۋە (رەھمىتىنى) ئۇمىد قىلغان ھالدا دۇئا قىلىڭلار، شۈبھىسىزكى اﷲ نىڭ رەھمىتى ياخشىلىق قىلغۇچىلارغا يېقىندۇر'),
(1011, 7, 57, 'اﷲ نىڭ رەھمىتى (يەنى يامغۇر) مەيدانغا كېلىش ئالدىدا، اﷲ بېشارەتچى قىلىپ شاماللارنى ئەۋەتىدۇ، شاماللار ئېغىر بۇلۇتلارنى كۆتۈرگەن چاغدا، ئۇلارنى ئۆلۈك (يەنى قاغجىراپ كەتكەن گىياھسىز) جايلارغا سۈرىمىز، ئۇنىڭدىن يامغۇر ياغدۇرۇپ تۈرلۈك - تۈرلۈك مېۋىلەرنى چىقىرىمىز، ئىبرەت ئېلىشىڭلار ئۈچۈن (زېمىندىن مېۋىلەرنى چىقارغاندەك، قىيامەتتە) ئۆلۈكلەرنى قەبرىلىرىدىن چىقىرىمىز'),
(1012, 7, 58, 'مۇنبەت يەرنىڭ گىياھسى اﷲ نىڭ ئىزنى بىلەن تولۇق ئۈنىدۇ (بۇ ۋەز - نەسىھەتكە قۇلاق سېلىپ، ئۇنىڭدىن پايدىلىنىدىغان مۆمىننىڭ مىسالىدۇر)، ناچار يەرنىڭ گىياھسى ئاز ئۆسىدۇ (بۇ ۋەز - نەسىھەتتىن پايدىلانمايدىغان كاپىرنىڭ مىسالىدۇر)، شۈكۈر قىلىدىغان قەۋم ئۈچۈن ئايەتلىرىمىزنى مۇشۇنداق شەكىللەردە بايان قىلىمىز'),
(1013, 7, 59, 'شەك شۈبھىسىزكى، نۇھنى ئۆز قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، ئۇ: ‹‹ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر، مەن ھەقىقەتەن سىلەرنىڭ بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىغا قېلىشىڭلاردىن قورقىمەن›› دېدى'),
(1014, 7, 60, 'قەۋمىنىڭ چوڭلىرى: ‹‹بىز سېنى ھەقىقەتەن روشەن گۇمراھلىقتا كۆرۈمىز›› دېدى'),
(1015, 7, 61, 'نۇھ ئېيتتى: ‹‹ئى قەۋمىم! مەن قىلچىمۇ گۇمراھ ئەمەسمەن، لېكىن مەن ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەرمەن'),
(1016, 7, 62, 'سىلەرگە پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزىمەن، سىلەرگە نەسىھەت قىلىمەن، مەن اﷲ نىڭ ۋەھيى قىلىشى بىلەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن'),
(1017, 7, 63, 'سىلەرنى ئاگاھلاندۇرۇش ئۈچۈن، (كۇفرىدىن) ساقلىنىشىڭلار ۋە اﷲ نىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن، سىلەرگە ئاراڭلاردىكى بىر كىشى ئارقىلىق پەرۋەردىگارىڭلارنىڭ ۋەھيىسى كېلىشىدىن ئەجەبلەنمەمسىلەر؟ ››'),
(1018, 7, 64, 'ئۇلار نۇھنى ئىنكار قىلدى، نۇھنى ئۇنىڭ بىلەن كېمىدە بىللە بولغانلارنى قۇتقۇزدۇق، ئايەتلىرىمىزنى ئىنكار قىلغانلارنى پۈتۈنلەي (سۇغا) غەرق قىلدۇق. شۈبھىسىزكى، ئۇلار (ھەقنى كۆرۈشتىن دىللىرى) كور قەۋم ئىدى'),
(1019, 7, 65, 'ئاد (قەۋمى) گە ئۇلارنىڭ قېرىندىشى ھۇدنى ئەۋەتتۇق، ھۇد: ‹‹ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر. (ئۇنىڭ ئازابىدىن) قورقمامسىلەر؟ ›› دېدى'),
(1020, 7, 66, 'ئۇنىڭ قەۋمىنىڭ ئىمان ئېيتمىغان چوڭلىرى: ‹‹بىز سېنى ھەقىقەتەن ئەخمەق ھېسابلايمىز، بىز سېنى، ئەلۋەتتە، (پەيغەمبەرلىك دەۋايىڭدا) يالغانچىلاردىن دەپ گۇمان قىلىمىز›› دېدى'),
(1021, 7, 67, 'ھۇد ئېيتتى: ‹‹ئى قەۋمىم! مەن ئەخمەق ئەمەسمەن، لېكىن مەن ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەرمەن'),
(1022, 7, 68, 'سىلەرگە پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزىمەن، مەن سىلەرگە ئىشەنچىلىك نەسىھەتچىمەن'),
(1023, 7, 69, 'پەرۋەردىگارىڭلارنىڭ ۋەھيىسى سىلەرنى ئاگاھلاندۇرۇش ئۈچۈن ئۆز ئىچىڭلاردىكى بىر كىشى ئارقىلىق كەلگەنلىكىدىن ئەجەبلىنەمسىلەر؟ ›› ئۆز ۋاقتىدا نۇھنىڭ قەۋمى ھالاك بولغاندىن كېيىن، اﷲ نىڭ سىلەرنى ئۇلارنىڭ ئورۇنباسارلىرى قىلغانلىقىنى ۋۇجۇدۇڭلارنى، قامەتلىك، بەستلىك قىلغانلىقىنى ياد ئېتىڭلار، سىلەر بەختكە ئېرىشىشىڭلار ئۈچۈن اﷲ نىڭ نېمەتلىرىنى ياد ئېتىڭلار'),
(1024, 7, 70, 'ئۇلار: ‹‹ (ئى ھۇد) ئاتا - بوۋىللىرىمىز ئىبادەت قىلىپ كېلىۋاتقان مەبۇدلارنى تاشلاپ، يالغۇز بىر اﷲ قا ئىبادەت قىلىشىمىزغا دەۋەت قىلغىلى كەلدىڭمۇ؟ ئەگەر راست سۆزلىگۈچىلەردىن بولساڭ، بىزگە ۋەدە قىلغان ئازابنى كەلتۈرگىن›› دېدى'),
(1025, 7, 71, 'ھۇد: ‹‹سىلەرگە چوقۇم پەرۋەردىگارىڭلارنىڭ ئازابى ۋە غەزىپى نازىل بولىدۇ، سىلەر ئۆزۈڭلار ۋە ئاتا - بوۋاڭلار ئات قويۇۋالغان، بۇ ھەقتە (ئۇلارغا ئىبادەت قىلىشقا) اﷲ ھېچقانداق دەلىل نازىل قىلمىغان (بۇتلىرىڭلارنىڭ) ئىسىملىرى ئۈستىدە مەن بىلەن مۇنازىرىلىشەمسىلەر؟ (اﷲ نىڭ ئازابىنى) كۈتۈڭلار، مەنمۇ ھەقىقەتەن سىلەر بىلەن بىرلىكتە (سىلەرگە نازىل بولىدىغان ئازابنى) كۈتىمەن›› دېدى'),
(1026, 7, 72, 'بىز ھۇدنى ۋە ئۇنىڭ بىلەن بىللە بولغۇچىلارنى (يەنى مۆمىنلەرنى) رەھمىتىمىز بىلەن قۇتقۇزدۇق، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى تەلتۆكۈس ھالاك قىلدۇق، ئۇلار ئىمان ئېيتمىغان ئىدى'),
(1027, 7, 73, 'سەمۇدقا ئۇلارنىڭ قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ ئەۋەتتۇق). ئۇ: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. سىلەرگە ھەقىقەتەن پەرۋەردىگارىڭلار تەرىپىدىن (مېنىڭ پەيغەمبەرلىكىمنى ئىسپاتلايدىغان) روشەن مۆجىزە كەلدى، مانا بۇ - اﷲ (بىۋاسىتە) ياراتقان چېشى تۆگىمۇ سىلەرگە مېنىڭ مۆجىزەمدۇر، ئۇنى اﷲ نىڭ زېمىنىغا قويۇۋېتىڭلار، ئىختىيارىي ئوتلىسۇن، ئۇنىڭغا زىيان - زەخمەت يەتكۈزمەڭلار، بولمىسا، سىلەرنى قاتتىق ئازاب ھالاك قىلىدۇ'),
(1028, 7, 74, 'ياد ئېتىڭلاركى، ئۆز ۋاقتىدا اﷲ سىلەرنى ئاد قەۋمىدىن كېيىن ئۇلارنىڭ ئورۇنباسارلىرى قىلدى، سىلەرنى (ھىجر) زېمىنىغا ئورۇنلاشتۇردى، ئۇنىڭ تۈزلەڭلىرىگە ئىمارەتلەر سالدىڭلار، تاغلارنى تېشىپ ئۆيلەر ياسىدىڭلار. اﷲ نىڭ نېمەتلىرىنى ياد ئېتىڭلار، زېمىندا بۇزغۇنچىلىق قىلماڭلار» دېدى'),
(1029, 7, 75, 'ئۇنىڭ قەۋمىنىڭ تەكەببۇر چوڭلىرى بوزەك قىلىنغانلارغا يەنى ئۇلارنىڭ ئىچىدىكى مۆمىنلەرگە: «سىلەر سالىھنى ئۇنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەر دەپ بىلەمسىلەر؟» دېدى. ئۇلار: «بىز ئۇنىڭ پەيغەمبەرلىكىگە ئەلۋەتتە ئىشىنىمىز» دېدى'),
(1030, 7, 76, 'ھېلىقى تەكەببۇرلار: «سىلەر ئىشەنگەنگە بىز ھەرگىز ئىشەنمەيمىز» دېدى'),
(1031, 7, 77, 'ئۇلار چىشى تۆگىنى بوغۇزلىدى، پەرۋەردىگارىنىڭ ئەمرىنى تۇتۇشتىن باش تارتتى ۋە ئى سالىھ: «راستتىنلا پەيغەمبەرلەردىن بولساڭ، بىزگە ۋەدە قىلغان ئازابنى كەلتۈرۈپ باق» دېدى'),
(1032, 7, 78, 'ئۇلارغا قاتتىق ئازاب يۈزلەندى - دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى'),
(1033, 7, 79, 'ئۇ (ئۇلار ھالاك بولغاندىن كېيىن) ئۇلارغا ئارقىسىنى قىلدى ۋە: «ئى قەۋمىم! سىلەرگە ھەقىقەتەن پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم، سىلەرگە نەسىھەت قىلدىم، لېكىن سىلەر نەسىھەت قىلغۇچىلارنى ياقتۇرمىدىڭلار» دېدى'),
(1034, 7, 80, 'لۇتنى (ئەھلى سەدومغا پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۆز ۋاقتىدا ئۇ ئۆز قەۋمىگە (ئۇلارنى ئەيىبلەش يۈزىسىدىن) مۇنداق دېگەن ئىدى: «سىلەر شۇنداق قەبىھ ئىشنى قىلامسىلەر؟ مۇنداق قەبىھ ئىشنى سىلەردىن ئىلگىرى جاھان ئەھلىدىن ھېچقانداق ئادەم قىلمىغان ئىدى'),
(1035, 7, 81, 'سىلەر ھەقىقەتەن ئاياللارنى تاشلاپ قويۇپ، ئەرلەر بىلەن جىنسىي تەلىپىڭلارنى قاندۇرىسىلەر، سىلەر ھەقىقەتەن ھەددىدىن ئاشقۇچى قەۋمسىلەر»'),
(1036, 7, 82, 'لۇتنىڭ قەۋمىنىڭ بىردىنبىر جاۋابى: «لۇتنى ئۇنىڭ ئەگەشكۈچىلىرى بىلەن قوشۇپ شەھرىڭلاردىن چىقىرىۋېتىڭلار، چۈنكى ئۇلار پاك كىشىلەردۇر» دېيىشتىن ئىبارەت بولدى'),
(1037, 7, 83, 'لۇتنى ئۇنىڭ خوتۇنىدىن باشقا، تەۋە كىشىلىرى بىلەن بىللە (ئۇنىڭ قەۋمىگە نازىل بولغان ئازابتىن) قۇتقۇزدۇق، پەقەت خوتۇنىلا (شەھەردە) قىلىپ ھالاك بولغۇچىلاردىن بولدى'),
(1038, 7, 84, 'ئۇلارغا بىر تۈرلۈك يامغۇر (يەنى تاش) ياغدۇردۇق، گۇناھلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن'),
(1039, 7, 85, 'مەديەن (خەلقىگە) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۇ (ئۆز قەۋمىگە) ئېيتتى: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. سىلەرگە پەرۋەردىگارىڭلاردىن ھەقىقەتەن (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) مۆجىزە كەلدى، ئۆلچەمنى ۋە تارازىنى توغرىلاڭلار، كىشىلەرنىڭ نەرسىلىرىنى كەم بەرمەڭلار. (اﷲ پەيغەمبەرلەرنى ئەۋەتىپ) زېمىننى تۈزىگەندىن كېيىن، زېمىندا (گۇناھلار قىلىپ) بۇزغۇنچىلىق قىلماڭلار، ئەگەر مۆمىن بولساڭلار، ئەنە شۇ سىلەر ئۈچۈن ياخشىدۇر'),
(1040, 7, 86, 'سىلەر اﷲ قا ئىمان ئېيتقانلارنى قورقۇتقان، اﷲ نىڭ يولىدىن توسقان ۋە ئۇ يولنىڭ ئەگرىلىكىنى تىلىگەن ھالدا يوللاردا ئولتۇرماڭلار، ئەسلىدە سىلەر ئاز ئىدىڭلار، اﷲ نىڭ سىلەرنىڭ سانىڭلارنى كۆپەيتكەنلىكىنى ياد ئېتىڭلار، بۇزۇقچىلىق قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قاراڭلار'),
(1041, 7, 87, 'ئەگەر سىلەردىن بىر گۇرۇھ ئادەم مېنىڭ پەيغەمبەرلىكىمگە ئىشەنگەن، يەنە بىر گۇرۇھ ئادەم ئىشەنمىگەن بولسا، اﷲ بىزنىڭ ئارىمىزدا ھۆكۈم چىقارغۇچە سەۋر قىلىپ تۇرۇڭلار. اﷲ ھۆكۈم چىقارغۇچىلارنىڭ ئەڭ ياخشىسىدۇر»'),
(1042, 7, 88, 'شۇئەيب قەۋمىنىڭ (ئىماندىن باش تارتقان) تەكەببۇر چوڭلىرى: «ئى شۇئەيب! سېنى چوقۇم ئەگەشكۈچىلىرىڭ (يەنى ساڭا ئىمان ئېيتقانلار) بىلەن قوشۇپ شەھرىمىزدىن ھەيدەپ چىقىرىمىز، ياكى چوقۇم بىزنىڭ دىنىمىزغا قايتىشىڭلار كېرەك» دېدى. شۇئەيب: «بىز (بۇنىڭ ھەر ئىككىلىسىنى يامان كۆرۈدىغان تۇرساق (بىزنى يەنىلا شۇنىڭغا مەجبۇر قىلامسىلەر؟)» دېدى'),
(1043, 7, 89, '(شۇئەيب يەنى ئېيتتى) «اﷲ بىزنى سىلەرنىڭ دىنىڭلاردىن قۇتۇلدۇرغاندىن كېيىن ئۇنىڭغا قايتساق، ئەلۋەتتە، اﷲ قا يالغان چاپلىغان بولىمىز. پەرۋەردىگارىمىز اﷲ خالىسىلا، بىز سىلەرنىڭ دىنىڭلارغا قايتمايمىز. پەرۋەردىگارىمىزنىڭ ئىلمى ھەممىنى ئۆز ئىچىگە ئالىدۇ، اﷲ قا تەۋۋەككۈل قىلدۇق (يەنى ھەممە ئىشىمىزنى اﷲ قا تاپشۇرىمىز). پەرۋەردىگارىمىز! بىز بىلەن قەۋمىمىز ئارىسىدا ھەق ھۆكۈم چىقارغىن، سەن ھۆكۈم چىقارغۇچىلارنىڭ ئەڭ ياخشىسىدۇرسەن»'),
(1044, 7, 90, 'ئۇنىڭ قەۋمىنىڭ ئىمانسىز چوڭلىرى: «ئەگەر سىلەر شۇئەيبكە ئەگەشسەڭلار، شۈبھىسىزكى، چوقۇم زىيان تارتىسىلەر» دېدى'),
(1045, 7, 91, 'ئۇلارغا قاتتىق زىلزىلە يۈزلەندى، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى'),
(1046, 7, 92, 'شۇئەيبنى ئىنكار قىلغانلار (اﷲ نىڭ ھالاك قىلىشى بىلەن) گويا بۇ يەردە تۇرمىغاندەك (يەنى ياشىمىغاندەك) بولۇپ قالدى. شۇئەيبنى ئىنكار قىلغانلار زىيان تارتقۇچىلاردىن بولدى'),
(1047, 7, 93, 'شۇئەيب ئۇلارغا ئارقىسىنى قىلىپ: «ئى قەۋمىم! مەن سىلەرگە ھەقىقەتەن پەرۋەدىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم ۋە نەسىھەت قىلدىم، مەن قانداقمۇ كاپىر قەۋمگە قايغۇراي!» دېدى'),
(1048, 7, 94, 'بىز مەيلى قايسى شەھەرگە بولسۇن، بىرەر پەيغەمبەر ئەۋەتكەن بولساق (شەھەر ئەھلى ئۇنى ئىنكار قىلغان بولسا)، ئۇلارنىڭ اﷲ قا يالۋۇرۇشلىرى (تەۋبە قىلىشلىرى) ئۈچۈن، ئۇلارنى نامراتلىققا، كېسەللىككە گىرىپتار قىلدۇق'),
(1049, 7, 95, 'ئاندىن (ئۇلارنىڭ بېشىغا كەلگەن) كۈلپەتنى نېمەتكە ئايلاندۇردۇق، شۇنىڭ بىلەن ئۇلار كۆپەيدى، ئۇلار: «بىزنىڭ ئاتا - بوۋىلىرىمىزنىڭ بېشىغا ھەقىقەتەن كۈلپەتلەر، نېمەتلەر كەلگەن ئىدى (بۇ - زاماننىڭ ئادىتى؛ اﷲ تەرىپىدىن كەلگەن ئوقۇبەت ئەمەس)» دېدى. ئۇلار خەۋەرسىز تۇرغاندا، ئۇلارنى ئۇشتۇمتۇت ھالاك قىلدۇق'),
(1050, 7, 96, '(پەيغەمبەرلىرىنى ئىنكار قىلغانلىقتىن ھالاك قىلىنغان) شەھەرلەرنىڭ ئاھالىسى ئىمان ئېيتقان ۋە (كۇفرىدىن، گۇناھلاردىن) ساقلانغان بولسا ئىدى، ئەلۋەتتە، ئۇلارنى ئاسمان - زېمىننىڭ پاراۋانلىقلىرىغا مۇيەسسەر قىلاتتۇق، لېكىن ئۇلار (پەيغەمبەرلەرنى) ئىنكار قىلدى، شۇ ئۇلارنى ئۆز قىلمىشلىرى تۈپەيلىدىن ھالاك قىلدۇق'),
(1051, 7, 97, '(پەيغەمبەرلەرنى ئىنكار قىلىدىغان قىلىدىغان) شەھەرلەرنىڭ ئاھالىسى بىزنىڭ ئازابىمىزنىڭ ئۆزلىرىگە كېچىسى (غەپلەتتە) ئۇخلاۋاتقانلىرىدا كېلىشىدىن قورقمامدۇ'),
(1052, 7, 98, 'شەھەرلەر ئاھالىسى بىزنىڭ ئازابىمىزنىڭ چۈش ۋاقتىدا - ئۇلار ئويناپ تۇرغانلىرىدا كېلىشىدىن قورقمامدۇ'),
(1053, 7, 99, 'ئۇلار اﷲ نىڭ مەكرىدىن (يەنى ئۇلار تۇيۇقسىز تۇرغاندا اﷲ نىڭ ئازاب نازىل قىلىشىدىن) قورقمامدۇ؟ پەقەت زىيان تارتقۇچى قەۋملا اﷲ نىڭ مەكرىدىن قورقمايدۇ'),
(1054, 7, 100, 'زېمىننىڭ ئىلگىرىكى ئىگىلىرى (ھالاك بولغان) دىن كېيىن (زېمىنغا) ۋارىس بولغۇچىلارنى، خالىساق، گۇناھى تۈپەيلىدىن ھالاك قىلىدىغانلىقىمىز، دىللىرىنى پىچەتلىۋېتىدىغانلىقىمىز ئۇلارغا (يەنى زېمىنغا ۋارىسلىق قىلغۇچىلارغا) ئايان بولمىدىمۇ؟ ئۇلار (ھەق سۆزنى) ئاڭلىمايدۇ'),
(1055, 7, 101, 'ئاشۇ شەھەرلەر (ئاھالىسى) نىڭ بەزى خەۋەرلىرىنى (ئى مۇھەممەد!) ساڭا ھېكايە قىلىپ بېرىمىز، شەك - شۈبھىسىزكى، ئۇلارنىڭ پەيغەمبەرلىرى ئۇلارغا مۆجىزىلەر ئېلىپ كەلگەن ئىدى، ئۇلار ئىلگىرى (ھەقنى) ئىنكار قىلغانلىقلىرى ئۈچۈن (ئۇ مۆجىزىلەرگە) ئىمان ئېيتمىدى. اﷲ كاپىرلارنىڭ دىللىرىنى ئەنە شۇنداق پېچەتلەيدۇ'),
(1056, 7, 102, 'ئۇلارنىڭ تولىسىنىڭ (روزىمىساقتا) بەرگەن ۋەدىسىگە ۋاپا قىلىدىغانلىقىنى بايقىمىدۇق، ئۇلارنىڭ تولىسىنىڭ شەكسىز پاسىقلار (يەنى اﷲ نىڭ ئەمر - پەرمانىدىن باش تارتقۇچىلار) ئىكەنلىكىنى بايقىدۇق'),
(1057, 7, 103, 'مەزكۇز پەيغەمبەرلەردىن كېيىن، مۇسانى مۆجىزىلىرىمىز بىلەن، (مىسىرنىڭ مۇسا زامانىدىكى پادىشاھى) پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار بۇ مۆجىزىلەرنى ئىنكار قىلدى، بۇزغۇنچىلىق قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن'),
(1058, 7, 104, 'مۇسا ئېيتتى: «ئى پىرئەۋن! مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى ئەۋەتكەن پەيغەمبەرمەن'),
(1059, 7, 105, 'اﷲ نامىدىن پەقەت ھەقىقەتتىن باشقىنى ئېيتماسلىققا لايىقمەن، راستتىنلا پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) بىر مۆجىزە ئېلىپ كەلدىم، شۇنىڭ ئۈچۈن، ئىسرائىل ئەۋلادىنى مەن بىلەن قويۇپ بەرگىن (مەن بىلەن بىللە ئاتا - بوۋىلىرىنىڭ ۋەتىنى مۇقەددەس زېمىنغا بارسۇن)»'),
(1060, 7, 106, 'پىرئەۋن (مۇساغا): «ئەگەر سەن پەرۋەردىگارىڭ تەرىپىدىن) بىر مۆجىزە ئېلىپ كەلگەن بولساڭ، (بۇ دەۋايىڭدا) راستچىللاردىن بولساڭ، ئۇنى بىزگە كۆرسەتكىن» دېدى'),
(1061, 7, 107, 'مۇسا ھاسىسىنى تاشلىۋېدى، ئۇ ناگاھان ئاشكارا بىر ئەجدىھاغا ئايلاندى'),
(1062, 7, 108, 'قولىنى (ياقىسىدىن) چىقىرىۋىدى، ئۇ ناگاھان قارىغۇچىلارغا (نۇر چاقنىتىپ تۇرىدىغان) ئاپئاق بولۇپ كۆرۈندى'),
(1063, 7, 109, 'پىرئەۋن قەۋمنىڭ چوڭلىرى ئېيتتى: «بۇ ھەقىقەتەن ناھايىتى ئۇستا سېھىرگەر ئىكەن'),
(1064, 7, 110, 'ئۇ سىلەرنى زېمىنىڭلار (يەنى مىسىر زېمىنى) دىن ھەيدەپ چىقىرىۋەتمەكچى. نېمە مەسلىھەت بېرىسىلەر؟»'),
(1065, 7, 111, 'ئۇلار ئېيتتى: «(ئۇلارنىڭ ئىشىنى ئۆزۈڭ بىر پىكىرگە كەلگۈچە) تەخىر قىلغىن، شەھەرلەرگە (سېھىرگەرلەرنى) يىغقۇچى كىشىلەرنى ئەۋەتكىن'),
(1066, 7, 112, 'ئۇلار بارلىق ئۇستا سېھىرگەرلەرنى سېنىڭ ھۇزۇرۇڭغا ئېلىپ كەلسۇن»'),
(1067, 7, 113, '(پىرئەۋن سېھىرگەرلىرىنى يىغىشقا ئادەملىرىنى ئەۋەتتى) سېھىرگەرلەر پىرئەۋننىڭ قېشىغا كەلدى، ئۇلار: «ئەگەر بىز غەلىبە قىلساق، بىزگە چوقۇم مۇكاپات بېرىلەمدۇ؟» دېدى'),
(1068, 7, 114, 'پىرئەۋن: «ھەئە، (مۇكاپات بېرىلىدۇ، ئۇنىڭ ئۈستىگە) سىلەر چوقۇم مېنىڭ يېقىن كىشىلىرىم بولۇپ قالىسىلەر» دېدى'),
(1069, 7, 115, 'ئۇلار (يەنى سېھىرگەرلەر): «ئى مۇسا! (ھاساڭنى) سەن ئاۋۋال تاشلامسەن، ياكى بىز (ئۆزىمىزنىڭكىنى) ئاۋۋال تاشلامدۇق؟» دېدى'),
(1070, 7, 116, 'مۇسا: «سىلەر تاشلاڭلار!» دېدى. ئۇلار ھاسا، ئارغامچىلارنى تاشلاپ كىشىلەرنىڭ كۆزلىرىنى باغلىدى، ئۇلارنى (يەنى كىشىلەرنى) قاتتىق چۆچۈتۈۋەتتى، ئۇلار (كىشىلەرنىڭ كۆزلىرىگە) چوڭ (كۆرۈنىدىغان) سېھىرنى كۆرسەتتى'),
(1071, 7, 117, 'مۇساغا: «ھاساڭنى تاشلىغىن» دەپ ۋەھىي قىلدۇق، (مۇسا ھاسىسىنى تاشلىۋىدى، ئۇ ئەجدىھاغا ئايلىنىپ) ئۇلارنىڭ ئويدۇرما نەرسىلىرىنى دەرھال يۇتۇۋەتتى'),
(1072, 7, 118, 'ھەقىقەت ئاشكارا بولدى، ئۇلارنىڭ كۆرسەتكەن سېھىرلىرى بەربات بولدى'),
(1073, 7, 119, 'بۇ يەردە ئۇلار (يەنى پىرئەۋن بىلەن قەۋمى) مەغلۇپ بولدى، خار بولغان ھالدا (شەھەرگە قايتتى)'),
(1074, 7, 120, 'سېھىرگەرلەر سەجدىگە باردى'),
(1075, 7, 121, 'ئۇلار دېدى: «ئالەملەرنىڭ پەرۋەردىگارىغا ئىمان ئېيتتۇق'),
(1076, 7, 122, 'مۇسا ۋە ھارۇننىڭ پەرۋەردىگارىغا (ئىمان ئېيتتۇق)»'),
(1077, 7, 123, 'پىرئەۋن ئېيتتى: «سىلەر مەن رۇخسەت قىلماي تۇرۇپ مۇساغا ئىمان ئېيتتىڭلار، بۇ چوقۇم سىلەرنىڭ شەھەر (يەنى مىسىر) دىكى چېغىڭلاردا ئاھالىنى شەھەردىن ھەيدەپ چىقىرىش ئۈچۈن (مۇسا بىلەن بىرلىشىپ) ئالدىن پىلانلىغان ھىيلەڭلاردۇر (سىلەرنى قانداق جازالايدىغانلىقىمنى) ئۇزاققا قالماي بىلىسىلەر'),
(1078, 7, 124, 'سىلەرنىڭ قولۇڭلارنى، پۇتۇڭلارنى چوقۇم ئوڭ - چەپ قىلىپ (ئوڭ قولۇڭلار بىلەن سول پۇتۇڭلارنى ياكى سول پۇتۇڭلار بىلەن ئوڭ قولۇڭلارنى) چوقۇم كېسىمەن، ئاندىن ھەممىڭلارنى چوقۇم دارغا ئېسىپ ئۆلتۈرىمەن»'),
(1079, 7, 125, 'سېھىرگەرلەر ئېيتتى: «بىز، ئەلۋەتتە، پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز'),
(1080, 7, 126, 'سەن بىزنى پەقەت پەرۋەردىگارىمىزنىڭ بىزگە نازىل قىلغان ئايەتلىرىگە ئىمان ئېيتقانلىقىمىز ئۈچۈنلا ئەيىبلەۋاتىسەن، پەرۋەردىگارىمىز! بىزگە سەۋر ئاتا قىلغىن، بىزنى مۇسۇلمان پېتىمىزچە قەبزى روھ قىلغىن»'),
(1081, 7, 127, 'پىرئەۋن قەۋمىنىڭ چوڭلىرى (پىرئەۋنگە): «مۇسا بىلەن ئۇنىڭ قەۋمىنى زېمنىدا پىتنە - پاسات تۇغدۇرۇشقا، سېنى ۋە سېنىڭ ئىلاھلىرىڭنى تاشلاشقا قويۇۋېتەمسەن؟» دېدى. پىرئەۋن: «ئۇلارنىڭ باللىلىرىنى ئۆلتۈرىمىز، ئاياللىرىنى بولسا (خىزمەتكە سېلىشقا) قالدۇرۇپ قويىمىز، بىز ئەلۋەتتە ئۇلارنىڭ ئۈستىدىن ھۆكۈمرانلىق قىلىمىز» دېدى'),
(1082, 7, 128, 'مۇسا ئۆز قەۋمىگە: «اﷲ تىن مەدەت تىلەڭلار، سەۋر قىلىڭلار، يەر يۈزى ھەقىقەتەن اﷲ نىڭ مۈلكىدۇر، (اﷲ) ئۇنىڭغا بەندىلىرىدىن ئۆزى خالىغان كىشىلەرنى ۋارىس قىلىدۇ؛ ياخشى ئاقىۋەت تەقۋادارلارغا مەنسۇپ» دېدى'),
(1083, 7, 129, 'ئۇلار: «سەن بىزگە كېلىشتىن بۇرۇنمۇ، كەلگەندىن كېيىنمۇ ھامان خارلىنىپ كەلدۇق» دېدى. مۇسا: «پەرۋاردىگارىڭلارنىڭ دۈشمىنىڭلارنى ھالاك قېلىشى، (مىسىر) زېمىنىدا ئۇلارنىڭ ئورنىغا سىلەرنى دەسسىتىشى، سىلەرنىڭ قانداق ئىش قىلىدىغانلىقىڭلارغا قارىشىمۇ ھەقىقەقتۇر» دېدى'),
(1084, 7, 130, 'اﷲ قا قەسەمكى، بىز ھەقىقەتەن پىرئەۋن ۋە ئۇنىڭ تەۋەلىرىنىڭ ئىبرەت ئېلىشلىرى ئۈچۈن، ئۇلارنى قەھەتچىلىك بىلەن، مېۋىلىرىنىڭ ھوسۇلىنى كېمەيتىۋېتىش بىلەن سىنىدۇق'),
(1085, 7, 131, 'ئۇلار ئەگەر (پاراۋانلىق - مولچىلىقتەك) بىرەر ياخشىلىققا ئېرىشسە، بىز بۇنىڭغا ھەقلىقمىز، دەيدۇ. ئەگەر ئۇلارغا بىرەر يامانلىق كەلسە، ئۇلار: بۇ مۇسا ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنىڭ شۇملۇقىدىن كەلدى، دەيدۇ. ئۇلارغا يەتكەن ياخشىلىق بىلەن يامانلىق (مۇسانىڭ شۇملۇقىدىن ئەمەس) اﷲ نىڭ تەقدىرىدۇر، لېكىن ئۇلارنىڭ تولىسى بۇنى (يەنى ئۇلارغا كەلگەن بالا - قازانىڭ ئۆز گۇناھلىرى تۈپەيلى اﷲ تەرىپىدىن كەلگەنلىكىنى ئۇقمايدۇ'),
(1086, 7, 132, 'ئۇلار (يەنى پىرئەۋن قەۋمى): «كۆزىمىزنى باغلاش ئۈچۈن ھەرقانداق مۆجىزە كەلتۈرسەڭمۇ بىز ساڭا ھەرگىز ئىشەنمەيمىز» دېدى'),
(1087, 7, 133, 'ئۇلارغا سۇ ئاپىتى، چېكەتكە، پىت، پاقا، قان (بالالىرىنى) روشەن مۆجىزىلەر قىلىپ ئەۋەتتۇق، (اﷲ قا ئىمان ئېيتىشقا) ئۇلارنىڭ گەدەنكەشلىكى يول قويمىدى، ئۇلار گۇناھكار قەۋم بولدى'),
(1088, 7, 134, 'ئۇلارغا بالا نازىل بولغاندا، ئۇلار: «ئى مۇسا! پەرۋەردىگارىڭغا ئۆزىنىڭ ساڭا ئاتا قىلغان ئەھدى بىلەن (يەنى ساڭا بەرگەن پەيغەمنبەرلىكنىڭ ھەقىقىي ھۆرمىتى بىلەن بىزدىن بالانى كۆتۈرۈۋېتىشكە) دۇئا قىلساڭ، اﷲ قا قەسەمكى، ئەگەر بىزدىن بالانى كۆتۈرۈۋەتسەڭ، ساڭا چوقۇم ئىمان ئېيتىمىز، ئىسرائىل ئەۋلادىنى چوقۇم سەن بىلەن بىللە قويۇپ بېرىمىز» دېگەن ئىدى'),
(1089, 7, 135, 'ئۇلاردىن بالانى ۋاقىتلىق كۆتۈرۈۋەتكەن چېغىمىزدا، ئۇلار توساتتىن ئەھدىلىرىنى بۇزدى'),
(1090, 7, 136, 'ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ۋە ئايەتلىرىمىزدىن غاپىل بولغانلىقلىرى ئۈچۈن، ئۇلارنى دېڭىزغا غەرق قىلىپ جازالىدۇق'),
(1091, 7, 137, 'بوزەك قىلىنغان قەۋمنى (يەنى بەنى ئىسرائىلنى) بىز بەرىكەتلىك قىلغان (شام) زېمىننىڭ شەرق تەرەپلىرىگە ۋە غەرب تەرەپلىرىگە (يەنى ھەممە تەرەپكە) ۋارىس قىلدۇق. ئىسرائىل ئەۋلادى سەۋر قىلغانلىقلىرى ئۈچۈن، پەرۋەردىگارىڭنىڭ ئۇلارغا قىلغان چىرايلىق ۋەدىسى تولۇق ئىشقا ئاشتى؛ پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ سالغانلىرى (يەنى ئىمارەتلىرى) نى ۋە ياسىغانلىرى (يەنى باغلىرى ۋە ئېكىنزارلىقلىرى) نى ۋەيران قىلدۇق'),
(1092, 7, 138, 'ئىسرائىل ئەۋلادىنى بىز (قۇلزۇم) دېڭىزىدىن ئۆتكۈزۈۋەتتۇق، ئۇلار بۇتلىرىغا چوقۇنۇۋاتقان بىر قەۋمنىڭ يېنىدىن ئۆتكەندە: «ئى مۇسا! بىزگىمۇ ئۇلارنىڭ بۇتلىرىغا ئوخشاش بۇت ئورنىتىپ بەرگىن» دېدى. مۇسا: «سىلەر ھەقىقەتەن نادان قەۋم ئىكەنسىلەر» دېدى'),
(1093, 7, 139, 'شۈبھىسىزكى، ئاشۇ كىشىلەرنىڭ باتىل دىنلىرى گۇمران بولغۇسىدۇر، ئۇلارنىڭ قىلغان ئەمەللىرى بىكاردۇر'),
(1094, 7, 140, 'مۇسا: «اﷲ سىلەرنى (زامانىڭلاردىكى) جاھان ئەھلىدىن ئارتۇق قىلغان تۇرسا، سىلەرگە ئۇنىڭدىن باشقا ئىلاھ ئىزدەمدىم؟» دېدى'),
(1095, 7, 141, 'ئۆز ۋاقتىدا سىلەرنى پىرئەۋننىڭ قەۋمىدىن قۇتقۇزدۇق، ئۇلار سىلەرگە قاتتىق ئازابنى تېتىتاتتى، ئوغۇللىرىڭلارنى ئۆلتۈرەتتى، ئاياللىرىڭلارنى (خورلاپ ئىشلىتىشكە) ئېلىپ قالاتتى، بۇنىڭدا پەرۋەردىگارىڭلاردىن سىلەرگە زور سىناق بار ئىدى'),
(1096, 7, 142, 'مۇسا (بىزگە مۇناجات قىلىشقا) ئوتتۇز كېچىنى ۋەدە قىلدۇق. ئۇنىڭغا يەنە ئون كېچىنى قوشتۇق، شۇنىڭ بىلەن، پەرۋەردىگارىڭنىڭ ۋەدە قىلغان ۋاقتى 40 كېچە بولدى. مۇسا قېرىندىشى ھارۇنغا: «(مەن قايتىپ كەلگەنگە قەدەر) قەۋمىمگە مېنىڭ ئورۇنباسارىم بولۇپ تۇرغىن، (ئۇلارنىڭ ئىشىنى) تۈزىگىن، بۇزۇقچىلىق قىلغۇچىلارنىڭ يولىدا ماڭمىغىن» دېدى'),
(1097, 7, 143, 'مۇسا بىز ۋەدە قىلغان ۋاقىتتا كەلگەن ۋە پەرۋەردىگارى ئۇنىڭغا (بىۋاسىتە) سۆ قىلغان چاغدا: «پەرۋەردىگارىم! ماڭا ئۆزۈڭنى كۆرسەتكىن، سېنى بىر كۆرۈپ ئالاي» دېدى. اﷲ: «مېنى (بۇ دۇنيادا) ھەرگىز كۆرەلمەيسەن (چۈنكى ئىنساننىڭ بۇ ئاجىز تېنى بۇنىڭغا تاقەت قىلالمايدۇ). لېكىن تاغقا قارىغىن، ئەگەر تاغ ئورنىدا مەھكەم تۇرالىسا، مېنى كۆرەلەيسەن» دېدى. پەرۋەردىگارى تاغقا تەجەللى قىلىش بىلەن، تاغنى تۈپتۈز قىلىۋەتتى، مۇسا بىھوش بولۇپ يىقىلدى. ئۇ ھوشىغا كېلىپ: «(پەرۋەرىگارىم!) سەن پاكتۇرسەن، ساڭا تەۋبە قىلدىم، مەن (سېنىڭ ئۇلۇغلۇقۇڭغا) ئىشەنگۈچىلەرنىڭ ئەۋۋىلىمەن» دېدى'),
(1098, 7, 144, 'اﷲ ئېيتتى: «ئى مۇسا! مەن ھەقىقەتەن سېنى (زامانىڭدىكى) كىشىلەر ئارىسىدىن پەيغەمبەرلىككە ۋە مەن بىلەن (بىۋاسىتە) سۆزلىشىشكە تاللىدىم، ساڭا مەن ئاتا قىلغن پەيغەمبەرلىكنى قوبۇل قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن»'),
(1099, 7, 145, 'بىز ئۇنىڭ ئۈچۈن (تەۋرات) تاختىلىرىغا (ئىسرائىل ئەۋلادى دىنىدا موھتاج بولغان) ۋەز - نەسىھەت، (دىنىي) ئەھكاملارنىڭ ھەممىسىنى تەپسىلىي يازدۇق. (ئى مۇسا) ئۇنى (يەنى تەۋراتنى) مەھكەم تۇتقىن ھەمدە قەۋمىڭنى ئۇنىڭدىكى ئەڭ گۈزەل ئەھكاملارغا ئەمەل قىلىشقا بۇيرۇغىن، سىلەرگە پاسىقلار (يەنى پىرئەۋن ۋە ئۇنىڭ قەۋمى) نىڭ دىيارىنى كۆرسىتىمەن'),
(1100, 7, 146, 'يەر يۈزىدە ناھەق تەكەببۇرلۇق قىلىدىغانلارغا مېنىڭ ئايەتلىرىمنى پەھىم قىلدۇرمايمەن، ئۇلار بارلىق مۆجىزىلەرنى كۆگەندىمۇ ئۇنىڭغا ئىشەنمەيدۇ، ئۇلار ئەگەر توغرا يولنى كۆرسە، ئۇنىڭدا ماڭمايدۇ، گۇمراھلىق يولىنى كۆرسە ئۇنىڭدا ماڭىدۇ، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلدى ۋە ئۇنىڭدىن غاپىل بولدى'),
(1101, 7, 147, 'بىزنىڭ ئايەتلىرىمىزنى ۋە ئاخىرەتتە اﷲ قا مۇلاقات بولۇشنى ئىنكار قىلغانلارنىڭ قىلغان (ياخشى) ئەمەللىرى (ئىمانى بولمىغانلىقى ئۈچۈن) بىكار بولۇپ كېتىدۇ، ئۇلارغا پەقەت قىلغان ئەمەللىرىگە يارىشا جازا بېرىلىدۇ'),
(1102, 7, 148, 'مۇسانىڭ قەۋمى مۇسادىن كېيىن (يەنى مۇسا پەرۋەردىگارىغا مۇناجات قىلىش ئۈچۈن تۇر تېغىغا كەتكەندىن كېيىن)، زىننەت بۇيۇملرىدىن موزايدەك ئاۋاز چىقىرىدىغان بىر جانسىز موزاي ياسىدى. ئۇلار موزاينىڭ ئۆزلىرىگە سۆزلىيەلمەيدىغانلىقىنى، يول كۆرسىتەلمەيدىغانلىقىنى كۆرمىدىمۇ؟ ئۇلار ئۇنى مەبۇد قىلىۋالدى، ئۇلار (ئۆزلىرىگە) زۇلۇم قىلغۇچى بولدى'),
(1103, 7, 149, 'ئۇلار (موزايغا چوقۇنغانلىقلىرىغا) پۇشايمان قىلغان ۋە راست ئازغانلىقلىرىنى چۈشەنگەن چاغدا: «ئەگەر پەرۋەردىگارىمىز بىزگە رەھىم قىلمىسا ۋە مەغپىرەت قىلمىسا، بىز ئەلۋەتتە زىيان تارتقۇچىلاردىن بولىمىز» دېدى'),
(1104, 7, 150, 'مۇسا غەزەپلەنگەن، غەمكىن ھالدا قايتىپ كېلىپ، قەۋمىگە: «مەن يوق چاغدا (موزايغا چوقۇنۇپ) نەقەدەر يامان ئىش قىلدىڭلار - ھە! (كۈتۈپ تۇرماي) پەرۋەردىگارىڭلار نىڭ ئەمرىگە ئالدىراپ كەتتىڭلارمۇ؟» دېدى ۋە (غەزەپلەنگەنلىكتىن تەۋرات) تاختىلىرىنى (يەرگە) تاشلىدى، قېرىندىشىنىڭ چېچىدىن تۇتۇپ ئۆز تەرىپىگە تارتتى. (ھارۇن) ئېيتتى: «ئى قېرىندىشىم! (بۇ) قەۋم مېنى بوزەك تاپتى، مېنى ئۆلتۈرۈۋەتكىلى تاس قالدى. مېنى دۈشمەنلەرگە تەپەررۇج قىلىپ بەرمىگىن؟ مېنى زالىم قەۋم (يەنى موزايغا چوقۇنغانلار) قاتارىدا سانىمىغىن»'),
(1105, 7, 151, 'مۇسا ئېيتتى: «پەرۋەردىگارىم! ماڭا ۋە مېنىڭ قېرىندىشىمغا مەغپىرەت قىلغىن، بىزنى رەھمىتىڭ دائىرىسىگە كىرگۈزگىن، سەن ئەڭ رەھىم قىلغۇچى زاتتۇرسەن»'),
(1106, 7, 152, 'شۈبھىسىزكى موزاينى مەبۇد قىلىۋالغانلار پەرۋەردىگارىنىڭ غەزىپىگە ئۇچرايدۇ، بۇ دۇنيادا خارلىققا قالىدۇ، (اﷲ قا) بوھتان قىلغۇچىلارغا بىز مۇشۇنداق جازا بېرىمىز'),
(1107, 7, 153, 'يامان ئىشلارنى قىلىپ قويۇپ، كېيىن تەۋبە قىلغان ۋە ئىمانىدا سەمىمىي بولغانلارنى تەۋبىسىدىن كېيىن پەرۋەردىگارىڭ ئەلۋەتتە مەغپىرەت قىلىدۇ، (ئۇلارغا) رەھىم قىلىدۇ'),
(1108, 7, 154, 'مۇسا غەزىپى بېسىلغاندىن كېيىن (يەردە ياتقان تەۋرات) تاختىلىرىنى ئالدى. ئۇنىڭدا پەرۋەردىگارىدىن قورققۇچىلارغا ھىدايەت قىلىنىدۇ ۋە رەھمەت قىلىنىدۇ دەپ يېزىلغان ئىدى'),
(1109, 7, 155, 'مۇسا، بىز بەلگىلەپ بەرگەن ۋاقىتتا ئېلىپ كېلىش ئۈچۈن، ئۆز قەۋمىدىن 70 كىشىنى تاللىدى؛ ئۇلارغا زىلزىلە يۈزلەنگەندە، مۇسا: «پەرۋەردىگارىم! خالىغان بولساڭ ئۇلارنى ۋە مېنى بۇرۇنلا ھالاك قىلغان بولاتتىڭ. ئارىمىزدىكى ئەخمەقلەرنىڭ قىلمىشى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟ بۇ پەقەت سېنىڭ سىنىقىڭدۇر، شۇ ئارقىلىق خالىغان بەندەڭنى ئازدۇرىسەن، خالىغان بەندەڭنى ھىدايەت قىلىسەن، سەن بىزنىڭ ئىگىمىزسەن، بىزگە مەغپىرەت قىلغىن، رەھىم قىلغىن. سەن ئەڭ ياخشى مەغپىرەت قىلغۇچىسەن'),
(1110, 7, 156, 'بىزگە بۇ دۇنيادا ۋە ئاخىرەتتە تېگىشلىك ياخشىلىق قىلغىن، بىز ھەقىقەتەن ساڭا تەۋبە قىلدۇق» دېدى. اﷲ ئېيتتى: «ئازابىم بىلەن (بەندىلىرىمدىن) خالىغان كىشىنى ئازابلايمەن، مېنىڭ رەھمىتىم مەخلۇقاتنىڭ ھەممىسىگە ئورتاقتۇر. رەھمىتىمنى (كۇفرىدىن ۋە گۇناھتىن) ساقلانغۇچىلارغا، (ماللىرىنىڭ) زاكىتىنى بېرىدىغانلارغا ۋە بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتىدىغانلارغا تېگىشلىك قىلىمەن»'),
(1111, 7, 157, 'ئۇلار ئەلچىگە - ئۈممى پەيغەمبەرگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) ئەگىشىدۇ، ئۇلار ئۆز ئىلكىدىكى تەۋرات، ئىنجىللاردا ئۇنىڭ (سۈپىتىنىڭ) يېزىلغانلىقىنى كۆرىدۇ. ئۇ ئۇلارنى ياخشى ئىش قىلىشقا بۇيرۇيدۇ، يامان ئىش قىلىشتىن توسىدۇ، ئۇلارغا پاك نەرسىلەرنى ھالال قىلىدۇ، ناپاك نەرسىلەرنى ھارام قىلىدۇ، ئۇلارنىڭ ئېغىر يۈكىنى يېنىكلىتىدۇ، ئۇلارنى سېلىنغان تاقاق، كويزا - كىشەنلەردىن بوشىتىدۇ (يەنى ئۇلارغا يۈكلەنگەن ئېغىر ۋەزىپىلەرنى ئېلىپ تاشلايدۇ)، ئۇنىڭغا ئىمان ئېيتقانلار، ئۇنى ھۆرمەتلىگەنلەر، ئۇنىڭغا ياردەم بەرگەنلەر، ئۇنىڭغا نازىل قىلىنغان نۇر (يەنى قۇرئان) غا ئەگەشكۈچىلەر بەختكە ئېرىشكۈچىلەردۇر'),
(1112, 7, 158, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئىنسانلار! مەن ھەقىقەتەن سىلەرنىڭ ھەممىڭلارغا اﷲ تەرىپىدىن ئەۋەتىلگەن ئەلچىمەن، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ تىرىلدۈرىدۇ ۋە ئۆلتۈرىدۇ، اﷲ قا ۋە اﷲ نىڭ سۆزلىرىگە ئىمان كەلتۈرىدىغان ئەلچىسى ئۈممى پەيغەمبەرگە ئىمان كەلتۈرۈڭلار، ھىدايەت تېپىشىڭلار ئۈچۈن ئۇنىڭغا ئەگىشىڭلار»'),
(1113, 7, 159, 'مۇسانىڭ قەۋمىنىڭ (يەنى بەنى ئىسرائىلنىڭ) ئىچىدە (كىشىلەرنى) ھەق بىلەن توغرا يولغا باشلايدىغان، ھەق بىلەن توغرا ھۆكۈم قىلىدىغان بىر جامائە بار'),
(1114, 7, 160, 'ئۇلار (يەنى ئىسرائىل) نى 12 تارماققا (يەنى قەبىلىگە) جامائەلەر قىلىپ بۆلۈۋەتتۇق. مۇسانىڭ قەۋمى (باياۋاندا) ئۇنىڭدىن سۇ تەلەپ قىلغاندا، مۇساغا: «ھاساڭ بىلەن تاشنى ئۇرغىن» دەپ ۋەھىي قىلدۇق. (مۇسا ھاسىسى بىلەن تاشنى ئۇرۇۋىدى) ئۇنىڭدىن (قەبىلىلەرنىڭ سانى بويىچە) 12 بۇلاق ئېتىلىپ چىقتى، ھەر قەبىلە ئۆزىنىڭ سۇ ئىچىدىغان ئورنىنى بىلدى. ئۇلارغا بۇلۇتنى سايىۋەن قىلىپ بەردۇق، ئۇلارغا تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق. (ئۇلارغا ئېيتتۇقكى): «سىلەر بىز رىزىقلاندۇرغان لەززەتلىك نەرسىلەردىن يەڭلار». (ئۇلار بۇ ئۇلۇغ نېمەتلەرگە نانكورلۇق قىلىش بىلەن) بىزگە زىيان سالغىنى يوق، (ئۆزلىرىنى اﷲ نىڭ ئازابىغا دۇچار قىلىشلىرى بىلەن) ئۆزلىرىگە ئۆزلىرى زۇلۇم سالدى'),
(1115, 7, 161, 'ئۆز ۋاقتىدا (بىز ئۇلارغا) (يەنى بەنى ئىسرائىلنىڭ ئاتا - بوۋىلىرىغا): «سىلەر بۇ شەھەر (يەنى بەيتۇلمۇقەددەس) دە تۇرۇڭلار، ئۇ يەردىكى يېمەكلىكلەردىن خالىغىنىڭلارچە يەڭلار، شەھەر دەرۋازىسىدىن سەجدە قىلغان ھالدا كىرىڭلار، (كىرىۋاتقان چېغىڭلاردا) ئى اﷲ! گۇناھلىرىمىزنى كەچۈرگىن، دەڭلار، جىمى گۇناھلىرىڭلارنى مەغپىرەت قىلىمىز، ياخشىلىق قىلغۇچىلارغا (ساۋابىنى) زىيادە بېرىمىز» دېدۇق'),
(1116, 7, 162, 'ئۇلارنىڭ ئىچىدىكى زالىملار ئۇلارغا ئېيتىلغان سۆز (اﷲ نىڭ ئەمرىنى) باشقا سۆزلەرگە ئۆزگەرتىۋەتتى، ئۇلار زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارغا ئاسماندىن ئازاب ئەۋەتتۇق'),
(1117, 7, 163, 'ئۇلار (يەنى يەھۇدىيلار) دىن دېڭىز (يەنى قۇلزۇم دېڭىزى) بويىدىكى (ئەيلە) شەھرىنىڭ ئەھۋالىنى سورىغىن. (ئۇنىڭ ئاھالىسى بېلىق تۇتۇش چەكلەنگەن كۈن) شەنبىدە اﷲ نىڭ چەكلىمىسىدىن چىقاتتى. چۈنكى شەنبە كۈنى بېلىقلار سۇ ئۈستىدىن ئۇلارنىڭ ئالدىغا كېلەتتى، شەنبىدىن غەيرىي كۈنلەردە ئۇلارنىڭ ئالدىغا كەلمەيتتى، پاسىق بولغانلىقلىرى ئۈچۈن. ئۇلارنى مۇشۇنداق سىنايمىز'),
(1118, 7, 164, 'ئۆز ۋاقتىدا ئۇلارنىڭ ئىچىدىكى بىر جامائە ئادەملەر: اﷲ ھالاك قىلىدىغان ياكى قاتتىق ئازابلايدىغان قەۋمگە نېمە ئۈچۈن ۋەز - نەسىھەت قىلىسىلەر؟» دېدى. ئۇلار (يەنى ۋەز - نەسىھەت قىلغۇچىلار): «پەرۋەردىگارىڭلارنىڭ ئالدىدا (گۇناھقا سۈكۈت قىلىپ تۇردۇق دەپ) ئۆزرە ئېيتماسلىقىمىز ۋە ئۇلارنىڭ اﷲ تىن قورقۇپ (گۇناھتىن چەكلىنىشلىرىنى ئۈمىد قىلغانلىقىمىز ئۈچۈن شۇنداق قىلدۇق)» دېدى'),
(1119, 7, 165, 'ئۇلار ئۆزلىرىگە قىلىنغان ۋەز - نەسىھەتنى قوبۇل قىلمىغان چاغدا، يامان ئىشتىن مەنئى قىلغۇچىلارنى قۇتقۇزدۇق، اﷲ نىڭ ئەمرىدىن چىققانلىقلىرى ئۈچۈن، زالىملارنى قاتتىق ئازابقا دۇچار قىلدۇق'),
(1120, 7, 166, 'ئۇلار نەھيى قىلىنغان نەرسىنى تەرك ئېتىشتىن باش تارتقانلىقلىرى ئۈچۈن، ئۇلارغا: «خار مايمۇن بولۇپ كېتىڭلار» دېدۇق'),
(1121, 7, 167, '(ئى مۇھەممەد!) ئۆز ۋاقتىدا، پەرۋەردىگارىڭ ئۇلارغا (يەنى يەھۇدىيلارغا) قىيامەتكىچە چوقۇم قاتتىق ئازابلايدىغان كىشىلەرنى ئەۋەتىپ تۇرىدىغانلىقىنى خەۋەر قىلدى. شۈبھىسىزكى، پەرۋەردىگارىڭ (ئاسىيلىق قىلغۇچىلارنى) ئەلۋەتتە تېز جازالىغۇچىدۇر، (ئىتائەت قىلغۇچىلارغا) ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، مەرھەمەت قىلغۇچىدۇر'),
(1122, 7, 168, 'ئۇلارنى (يەنى يەھۇدىيلارنى) يەر يۈزىدە تۈرلۈك پىرقىلەرگە بۆلۈۋەتتۇق، ئۇلارنىڭ ئىچىدە ياخشىلىرىمۇ بار، ياخشىلىرىدىن تۆۋەنلىرىمۇ بار، ئۇلارنىڭ (كۇفرىدىن ۋە گۇناھتىن) قايتىشلىرى ئۈچۈن، ئۇلارنى قاتتىقچىلىق ۋە كەڭچىلىك بىلەن سىنىدۇق'),
(1123, 7, 169, 'ئۇلاردىن كېيىن، (يامان) ئەۋلادلار تەۋراتقا ۋارىسلىق قىلدى، ئۇلار بۇ دۇنيانىڭ ئەرزىمەس پۇل - مېلىنى (ھالال بىلەن ھارامنى ئايرىماي، قارىقويۇق) ئالىدۇ، ۋە بىزنى اﷲ ئەپۇ قىلىدۇ، دەيدۇ. شۇنداق پۇل - مال ئۇلارنىڭ قولىغا كەلسە، ئۇلار يەنە ئالىدۇ. كىتابتا (يەنى تەۋراتتا) اﷲ قا (يالغان چاپلىماي) پەقەت ھەقنىلا ئېيتىشلىرى توغرۇلۇق ئۇلاردىن چىڭ ۋەدە ئېلىنمىدىمۇ؟ ھالبۇكى، ئۇلار تەۋراتتىكى نەرسىلەرنى ئوقۇغان ئىدى. ھارامنى تەرك ئېتىپ، اﷲ تىن قورقىدىغانلارغا ئاخىرەت ياخشىدۇر، بۇنى چۈشەنمەمسىلەر؟'),
(1124, 7, 170, 'كىتابنى مەھكەم تۇتىدىغانلار، ناماز ئوقۇيدىغانلار (ساۋابقا ئېرىشىدۇ)، بىز ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى ھەقىقەتەن زايا قىلىۋەتمەيمىز'),
(1125, 7, 171, 'ئۆز ۋاقتىدا (تۇر) تاغنى قومۇرۇپ ئۇلارنىڭ ئۈستىگە سايىۋەندەك تىكلىدۇق، ئۇلار تاغنى ئۈستىلىرىگە چۈشۈپ كېتىدۇ دەپ ئويلىدى. (ئۇلارغا ئېيتتۇقكى) «تەقۋادارلار قاتارىدا بولۇشۇڭلار ئۈچۈن ئۆزۈڭلارغا بېرىلگەن كىتابنى (يەنى تەۋراتنى) مەھكەم تۇتۇڭلار، ئۇنىڭدىكى ئەھكاملارغا ئەمەل قىلىڭلار»'),
(1126, 7, 172, 'ئۆز ۋاقتىدا پەرۋەردىگارىڭ ئادەم باللىرىنى (يەنى نەسلىنى) ئۇلارنىڭ (ئاتىلىرىنىڭ) پۇشتىدىن چىقاردى ۋە ئۇلارنى ئۆزلىرىگە گۇۋاھ قىلىپ: «مەن سىلەرنىڭ پەرۋەدىگارىڭلار ئەمەسمۇ؟» دېدى (يەنى اﷲ ئۆزىنىڭ ئۇلارنىڭ پەرۋەردىگارى ئىكەنلىكىگە ۋە بىرلىكىگە ئادەم بالىلىرىنى ئىقرار قىلدۇردى، ئۇلار ئىقرار قىلىپ بۇنى ئۆز ئۈستىگە ئالدى). ئۇلار: «ھەئە، سەن بىزنىڭ پەرۋەردىگارىمىزدۇرسەن، گۇۋاھلىق بەردۇق» دېدى. (ئۇلارنى گۇۋاھلىق بەرگۈزگەنلىكىمىز) قىيامەت كۈنى ئۇلارنىڭ: «بىز بۇنىڭدىن غەپلەتتە قالغان ئىكەنمىز» دېمەسلىكلىرى، ياكى «ئاتا - بوۋىلىرىمىز ئىلگىرى (ساڭا بۇتلارنى) شېرىك كەلتۈرگەن ئىدى، بىزنىڭ پەقەت ئۇلارنىڭ ئەۋلادى بولۇپ قالغان يېرىمىز بار (يەنى بىزنىڭ ئۆزرىمىز بار) گۇمراھلارنىڭ قىلمىشلىرى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟» دېمەسلىكلىرى ئۈچۈندۇر'),
(1127, 7, 173, 'ئۆز ۋاقتىدا پەرۋەردىگارىڭ ئادەم باللىرىنى (يەنى نەسلىنى) ئۇلارنىڭ (ئاتىلىرىنىڭ) پۇشتىدىن چىقاردى ۋە ئۇلارنى ئۆزلىرىگە گۇۋاھ قىلىپ: «مەن سىلەرنىڭ پەرۋەدىگارىڭلار ئەمەسمۇ؟» دېدى (يەنى اﷲ ئۆزىنىڭ ئۇلارنىڭ پەرۋەردىگارى ئىكەنلىكىگە ۋە بىرلىكىگە ئادەم بالىلىرىنى ئىقرار قىلدۇردى، ئۇلار ئىقرار قىلىپ بۇنى ئۆز ئۈستىگە ئالدى). ئۇلار: «ھەئە، سەن بىزنىڭ پەرۋەردىگارىمىزدۇرسەن، گۇۋاھلىق بەردۇق» دېدى. (ئۇلارنى گۇۋاھلىق بەرگۈزگەنلىكىمىز) قىيامەت كۈنى ئۇلارنىڭ: «بىز بۇنىڭدىن غەپلەتتە قالغان ئىكەنمىز» دېمەسلىكلىرى، ياكى «ئاتا - بوۋىلىرىمىز ئىلگىرى (ساڭا بۇتلارنى) شېرىك كەلتۈرگەن ئىدى، بىزنىڭ پەقەت ئۇلارنىڭ ئەۋلادى بولۇپ قالغان يېرىمىز بار (يەنى بىزنىڭ ئۆزرىمىز بار) گۇمراھلارنىڭ قىلمىشلىرى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟» دېمەسلىكلىرى ئۈچۈندۇر'),
(1128, 7, 174, 'ئۇلارنىڭ (گۇمراھلىقتىن، ئاتا - بوۋىلىرىغا ئەگىشىشتىن) قايتىشلىرى ئۈچۈن، ئايەتلىرىمىزنى مۇشۇنداق تەپسىلىي بايان قىلىمىز'),
(1129, 7, 175, '(ئى مۇھەممەد!) ئۇلار (يەنى يەھۇدىيلار) غا شۇنداق بىر ئادەمنىڭ قىسسىسىنى ئوقۇپ بەرگىنكى، ئۇنىڭغا ئايەتلىرىمىزنى بەردۇق (يەنى كىتابۇللاھنىڭ بەزى ئىلىملىرىنى ئۆگەتتۇق). ئۇ ئايەتلىرىمىزدىن ئۆزىنى تارتتى، ئۇنىڭغا شەيتان ئاپىرىدە بولۇشى بىلەنلا گۇمراھلاردىن بولۇپ كەتتى'),
(1130, 7, 176, 'ئەگەر بىز خالىساق، ئۇنى (تەقۋادار ئۆلىمالار دەرىجىسىگە) كۆتۈرەتتۇق، لېكىن ئۇ دۇنياغا بېرىلىپ كەتتى، نەپسى خاھىشىغا بويسۇندى، ئۇ گويا بىر ئىتقا ئوخشايدۇكى، ئۇنى قوغلىۋەتسەڭمۇ تىلىنى چىقىرىپ ھەسىرەيدۇ، قوغلىۋەتسەڭمۇ تىلىنى چىقىرىپ ھەسىرەيدۇ، بۇ بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ئەنە شۇ قەۋمنىڭ مىسالىدۇر، ئويلاپ بېقىشلىرى ئۈچۈن، بۇ قىسسىنى (قەۋمىڭگە) سۆزلەپ بەرگىن'),
(1131, 7, 177, 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلىپ ئۆزلىرىگە زىيان سالغان قەۋمنىڭ مىسالى نېمىدېگەن يامان!'),
(1132, 7, 178, 'اﷲ ھىدايەت قىلغان ئادەم ھىدايەت تاپقۇچىدۇر، اﷲ گۇمران قىلغان ئادەم زىيان تارتقۇچىدۇر'),
(1133, 7, 179, 'شەك - شۈبھىسىزكى، جىن ۋە ئىنسانلاردىن نۇرغۇنلىرىنى دوزاخقا (يېقىلغۇ بولۇش ئۈچۈن) ياراتتۇق، ئۇلار دىللىرى بولغىنى بىلەنمۇ، ئۇ ئارقىلىق ھەقنى چۈشەنمەيدۇ. ئۇلار كۆزلىرى بولغان بىلەنمۇ، ئۇ ئارقىلىق (اﷲ نىڭ قۇدرىتىنىڭ دەلىللىرىنى) كۆرمەيدۇ. ئۇلار قۇلاقلىرى بولغىنى بىلەنمۇ، ئۇ ئارقىلىق (اﷲ نىڭ ئايەتلىرىنى ئىبرەت ئىلىپ) تىڭشىمايدۇ. ئۇلار گويا ھايۋانغا ئوخشايدۇ، ھايۋاندىنمۇ بەتتەر گۇمراھتۇر، ئەنە شۇلار غاپىلدۇر'),
(1134, 7, 180, 'اﷲ نىڭ گۈزەل ئىسىملىرى بار، اﷲ نى شۇ (گۈزەل ئىسىملىرى) بىلەن ئاتاڭلار، اﷲ نىڭ ئىسىملىرىنى كەلسە - كەلمەس قوللىنىدىغانلارنى تەرك ئېتىڭلار، ئۇلار (ئاخىرەتتە) قىلمىشلىرىنىڭ جازاسىنى تارتىدۇ'),
(1135, 7, 181, 'بىز ياراتقان ئۈممەتلەر ئىچىدە ھەق يولغا دەۋەت قىلىدىغان ۋە ھەقتە چىڭ تۇرىدىغان بىر ئۈممەت بار'),
(1136, 7, 182, 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى تۇيدۇرماستىن، ئاستا - ئاستا ھالاكەتكە يېقىنلاشتۇرىمىز'),
(1137, 7, 183, 'ئۇلارغا مۆھلەت بېرىمەن مېنىڭ ئازابىم ھەقىقەتەن قاتتىقتۇر'),
(1138, 7, 184, 'ئۇلار ئۆزىنىڭ ھەمراھى (يەنى مۇھەممەد ئەلەيھىسسالام) نىڭ مەجنۇن ئەمەسلىكىنى ئويلىمامدۇ؟ ئۇ پەقەت ئاشكارا ئاگاھلاندۇرغۇچىدۇر'),
(1139, 7, 185, 'ئۇلار ئاسمانلارنىڭ ۋە زېمىننىڭ سەلتەنىتى ئۈستىدە، اﷲ نىڭ ياراتقان مەخلۇقاتلىرى، ئۆزلىرىنىڭ ئەجىلى يېقىنلىشىدىغانلىقىنىڭ مۇمكىنلىكى ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ ئۇلار (كالامۇللاھ ئىكەنلىكى شۇنچە روشەن بولغان) قۇرئاندىن باشقا يەنە قايسى سۆزگە ئىشىنىدۇ؟'),
(1140, 7, 186, 'اﷲ ئازدۇرغان كىشىنى ھىدايەت قىلغۇچى بولمايدۇ، اﷲ ئۇلارنى گۇمراھلىقلىرىدا قويۇپ بېرىدۇكى، ئۇلار تېڭىرقاپ يۈرىدۇ'),
(1141, 7, 187, '(ئى مۇھەممەد!) ئۇلار سەندىن قىيامەتنىڭ قاچان بولىدىغانلىقىنى سورايدۇ، ئېيتقىنكى، «ئۇنىڭ قاچان بولىدىغانلىقىنى پەقەت پەرۋەردىگارىم (اﷲ) بىلىدۇ، ئۇنى پەقەت ئۆزى بەلگىلىگەن ۋاقىتتا مەيدانغا چىقىرىدۇ، قىيامەت ۋە زېمىننىڭ (ئەھلىگە) ئېغىر (ۋەقەدۇر). ئۇ سىلەرگە تۇيۇقسىز كېلىدۇ». گويا سەن قىيامەت بىلەن ناھايىتى تونۇشتەك، ئۇنى سەندىن سورىشىدۇ. ئېيتقىنكى، «ئۇنىڭ ۋاقتىنى پەقەت اﷲ بىلىدۇ، لېكىن كىشىلەرنىڭ تولىسى ئۇنىڭ ۋاقتىنىڭ مەخپىي بولۇشىدىكى سەۋەبنى) بىلمەيدۇ'),
(1142, 7, 188, 'اﷲ خالىمىغان ئىكەن، ئۆزۈمگە پايدا يەتكۈزۈش، زىياننى ئۆزۈمدىن دەپئى قىلىش قولۇمدىن كەلمەيدۇ، (قىيامەتنىڭ قاچان بولۇشىنى قانداقمۇ بىلەي؟) ئەگەر مەن غەيبنى بىلىدىغان بولسام (دۇنيا مەنپەئەتلىرىدىن) نۇرغۇن مەنپەئەت ھاسىل قىلغان بولاتتىم، زىيان - زەخمەتكىمۇ ئۇچرىمىغان بولاتتىم (لېكىن مەن غەيبنى بىلمەيمەن، شۇڭا ماڭا تەقدىر قىلىنغان ياخشى - يامانلىق يېتىپ تۇرىدۇ). مەن پەقەت ئىمان ئېيتىدىغان قەۋم ئۈچۈن ئاگاھلاندۇرغۇچى ۋە خۇش خەۋەر بەرگۈچى قىلىپ ئەۋەتىلگەن پەيغەمبەرمەن»'),
(1143, 7, 189, 'ئۇ سىلەرنى بىر جاندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتتى، (ئادەم ئەلەيھىسسالامنىڭ) ئۇنسى - ئۈلپەت ئېلىشى ئۈچۈن، خوتۇنىنى (يەنى ھەۋۋانى) ئۇنىڭ ئۆز جىنسىدىن ياراتتى، خوتۇنى بىلەن يېقىنچىلىق قىلغاندىن كېيىن، خوتۇنى يېنىككىنە قورساق كۆتۈرۈپ ئېغىر بوي يۈردى، ئۇ (بالىنىڭ قورساقتا ئۆسۈپ چوڭىيىشى بىلەن) ئېغىرلاشقاندىن كېيىن، (ئەر - خوتۇن) ئىككىيلەن پەرۋەردىگارى اﷲ قا: «ئەگەر بىزگە (ئەزالىرى) بېجىرىم پەرزەنت ئاتا قىلساڭ، نېمىتىڭگە ئەلۋەتتە شۈكۈر قىلغۇچىلاردىن بولىمىز» دەپ دۇئا قىلدى'),
(1144, 7, 190, 'اﷲ ئۇلارغا بېجىرىم ئوغۇل پەرزەنت ئاتا قىلغان ئىدى، ئۇلارنىڭ ئەۋلادى اﷲ قا شېرىك كەلتۈرۈپ (بۇتلارغا چوقۇندى)، اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەن مەبۇدلىرىدىن پاكتۇر'),
(1145, 7, 191, 'ئۆزلىرى يارىتىلغان، ھېچ نەرسىنى يارىتىلمايدىغان نەرسىلەرنى (يەنى بۇتلارنى) اﷲ قا شېرىك كەلتۈرەمدۇ!'),
(1146, 7, 192, 'ئۇ نەرسىلەر چوقۇنغۇچىلىرىغا ياردەم بېرەلمەيدۇ، ئۆزلىرىگىمۇ ياردەم بېرەلمەيدۇ'),
(1147, 7, 193, 'ئۇلارنى توغرا يولغا چاقىرساڭلار، ئۇلار سىلەرگە ئەگەشمەيدۇ. مەيلى ئۇلارنى دەۋەت قىلىڭلار، ياكى جىم تۇرۇڭلار، بۇ ئۇلار ئۈچۈن بەرىبىر ئوخشاش (يەنى سىلەر قايسى ھالەتتە بولماڭلار، ئۇلار سىلەرنىڭ دەۋىتىڭلارغا جاۋاب قايتۇرۇشقا قادىر ئەمەستۇر)'),
(1148, 7, 194, 'سىلەر اﷲ نى قويۇپ ئىبادەت قىلغان بۇتلار سىلەرگە ئوخشاش مەخلۇقلاردۇر، (ئۇلارنى ئىلاھ دېگەن دەۋايىڭلاردا) راستچىل بولساڭلار، ئۇلارغا دۇئا قىلىپ بېقىڭلار، دۇئايىڭلارنى ئىجابەت قىلىپ باقسۇن'),
(1149, 7, 195, 'ئۇلار (يەنى بۇتلار) نىڭ ماڭىدىغان پۇتلىرى بارمۇ؟ ياكى تۇتىدىغان قوللىرى بارمۇ؟ ياكى كۆرىدىغان كۆزلىرى بارمۇ ۋە ئاڭلايدىغان قۇلاقلىرى بارمۇ؟ (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «شېرىكلىرىڭلارنى (يەنى بۇتلىرىڭلارنى ماڭا قارشى) چاقىرىڭلار، ئاندىن (قولۇڭلاردىن كېلىشىچە) ماڭا زىيانكەشلىك قىلىڭلار، ئازراقمۇ مۆھلەت بەرمەڭلار (مەن اﷲ قا يۆلەنگەنلىكىم ئۈچۈن سىلەرگە پەرۋا قىلمايمەن)'),
(1150, 7, 196, 'ھەقىقەتەن كىتابنى (يەنى قۇرئاننى) نازىل قىلغان اﷲ مېنىڭ ئىگەمدۇر، ئۇ ياخشىلارغا ئىگىدارچىلىق قىلىدۇ'),
(1151, 7, 197, 'سىلەرنىڭ اﷲ نى قويۇپ ئىبادەت قىلىدىغان بۇتلىرىڭلار سىلەرگە ياردەم بېرەلمەيدۇ، ئۆزلىرىگىمۇ ياردەم بېرەلمەيدۇ'),
(1152, 7, 198, 'ئەگەر ئۇلارنى ھىدايەتكە دەۋەت قىلساڭلار، ئۇلار (ھىدايەت تاپماق تۈگۈل دەۋىتىڭنى) ئاڭلىمايدۇ، ئۇلار ساڭا قاراپ تۇرغاندەك كۆرۈنسىمۇ، لېكىن ئۇلار كۆرمەيدۇ'),
(1153, 7, 199, 'ئەپۇنى دوست تۇتقىن، ياخشىلىققا (يەنى ياخشى سۆز قىلىشقا، ياخشى ئىش قىلىشقا) بۇيرۇغىن، نادانلار بىلەن تەڭ بولمىغىن (يەنى نادانلارنىڭ قىلغىنىنى قىلماي، ئۇلارغا مۇلايىم بولغىن)'),
(1154, 7, 200, 'ئەگەر شەيتان ساڭا ۋەسۋەسە قىلسا، (شەيتاننىڭ ۋەسۋەسىسىدىن) اﷲ قا سېغىنىپ پاناھ تىلىگىن. اﷲ ھەقىقەتەن (سۆزۈڭنى) ئاڭلاپ تۇرغۇچىدۇر (ئىشىڭنى بىلىپ تۇرغۇچىدۇ'),
(1155, 7, 201, 'تەقۋادار كىشىلەر شەيتاننىڭ ۋەسۋەسىسىگە ئۇچرىسا، اﷲ نى ئەسلەيدۇ - دە، ھەقىقەتنى كۆرۈۋېلىپ، (شەيتاننىڭ ۋەسۋەسىسىدىن) خالاس تاپىدۇ'),
(1156, 7, 202, 'شەيتانلار (كۇففار) بۇرادەرلىرىنى بولۇشىچە ئازدۇرىدۇ، ئۇلارنى ئازدۇرۇشتىن بوشىشىپ قالمايدۇ،'),
(1157, 7, 203, '(ئۇلار تەلەپ) قىلغان مۆجىزىلەردىن) بىرەر مۆجىزىنى كەلتۈرمىسەڭ، ئۇنى نېمىشقا (ئۆزۈڭ) ئىجاد قىلمىدىڭ؟ دېيىشىدۇ، (ئى مۇھەممەد!) ئۇلارغا ئېيتقىنكى، «(ئىش مېنىڭ ئىلكىمدە ئەمەس) مەن پەقەت پەرۋەردىگارىم تەرىپىدىن ماڭا ۋەھيى قىلىنغانغىلا ئەمەل قىلىمەن (ئۆزلىكىمدىن مۆجىزە يارىتالمايمەن)، بۇ (قۇرئاندا سىلەرگە) پەرۋەردىگارىڭلار تەرىپىدىن كەلتۈرۈلگەن دەلىللەردۇر (قۇرئان باشقا مۆجىزىلەرگە ئېھتىياج قالدۇرمايدۇ)، ئىمان ئېيتقان قەۋم ئۈچۈن ھىدايەتتۇر ۋە رەھمەتتۇر»'),
(1158, 7, 204, 'اﷲ نىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن، قۇرئان ئوقۇلغان چاغدا، ئۇنى دىققەت بىلەن ئاڭلاڭلار ۋە جىم تۇرۇڭلار (يەنى سۆز قىلماڭلار)'),
(1159, 7, 205, 'پەرۋەردىگارىڭنى يېلىنغان ۋە ئۇنىڭدىن قورققان ھالدا ئىچىڭدە ياد ئەتكىن، ئەتىگەندە - ئاخشامدا ئۇنى پەس ئاۋازدا زىكرى قىلغىن، غاپىللاردىن بولمىغىن'),
(1160, 7, 206, 'پەرۋەردىگارىڭنىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) اﷲ قا ئىبادەت قىلىشتىن باش تارتمايدۇ، اﷲ قا تەسبىھ ئېيتىدۇ ۋە ئۇنىڭغا سەجدە قىلىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, '(ئى مۇھەممەد! ساھابىلىرىڭ) غەنىيمەتلەر (نى قانداق تەقسىم قىلىشىڭ) توغرۇلۇق سەندىن سورىشىدۇ، (ئۇلارغا) ئېيتقىنكى، «غەنىيمەتلەر (توغرىسىدا ھۆكۈم چىقىرىش) اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە خاستۇر، اﷲ تىن قورقۇڭلار، (ئىختىلاپ قىلىشماي ئىتتىپاق بولۇپ) ئاراڭلارنى تۈزەڭلار، ئەگەر سىلەر مۆمىن بولساڭلار، اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار»'),
(1162, 8, 2, 'پەقەت اﷲ ياد ئېتىلسە دىللىرىدا قورقۇنچ پەيدا بولىدىغان، اﷲ نىڭ ئايەتلىرى تىلاۋەت قىلىنسا ئىمانى كۈچىيىدىغان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرلا (كامىل) مۆمىنلەردۇر'),
(1163, 8, 3, 'ئۇلار (مۇكەممەل رەۋىشتە) ناماز ئوقۇيدۇ، بىز ئۇلارغا رىزىق قىلىپ بەرگەن پۇل - مالدىن (خۇدا يولىدا) سەرپ قىلىدۇ'),
(1164, 8, 4, 'ئەنە شۇلار ھەقىقىي مۆمىنلەردۇر، ئۇلار پەرۋەردىگارىنىڭ دەرگاھىدا يۇقىرى مەرتىۋىلەرگە، مەغپىرەتكە ۋە ئېسىل رىزىققا (يەنى جەننەتنىڭ تۈگىمەس نېمەتلىرىگە) ئېرىشىدۇ'),
(1165, 8, 5, '(غەنىيمەت تەقسىماتى توغرىسىدىكى ھۆكۈمنىڭ اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە خاس قىلىنغانلىقىنى ئۇلارنىڭ ياقتۇرمىغانلىقى) پەرۋەردىگارىڭنىڭ سېنى ھەقلىق رەۋىشتە ئۆيۈڭدىن (يەنى ئۆيۈڭ جايلاشقان مەدىنىدىن ئۇرۇشقا) چىقارغانلىقىنى (ئۇلارنىڭ ياقتۇرمىغانلىقىغا) ئوخشايدۇ. مۆمىنلەردىن بىر گۇرۇھ ئادەم (دۈشمەنگە قارشى ئۇرۇشقا چىقىشنى) ئەلۋەتتە يامان كۆرىدۇ'),
(1166, 8, 6, 'ئۇلار گويا (ئالامەتلىرى) كۆرۈنۈپ تۇرۇۋاتقان ئۆلۈمگە ھەيدىلىۋاتقاندەك، ھەقىقەت ئايدىڭلاشقاندىن كېيىن، ھەقىقەت (يەنى ئۇرۇشقا چىقىش مەسىلىسى) ئۈستىدە سەن بىلەن مۇنازىرىلىشىدۇ'),
(1167, 8, 7, 'ئۆز ۋاقتىدا اﷲ ئىككى گۇرۇھ (بىرى مۇشرىكلار كارۋىنى، يەنى بىرى مۇشرىكلار قوشۇنى) دىن بىرىنىڭ قولۇڭلارغا كەلتۈرۈلۈشىنى ۋەدە قىلدى، سىلەر قورالسىز گۇرۇھنىڭ (يەنى كارۋاننىڭ) قولۇڭلارغا كەلتۈرۈلۈشىنى ياقتۇردۇڭلار. اﷲ ئۆز سۆزلىرى ئارقىلىق ھەقنى ھەق قىلىشنى (يەنى ئىسلام دىنىنى ئۈستۈن قىلىشنى)، كاپىرلارنىڭ يىلتىزىنى قۇرۇتۇشنى خالايدۇ'),
(1168, 8, 8, 'اﷲ گۇناھكار ئادەملەرنىڭ ياقتۇرمىغىنىغا قارىماي، ھەقنى ھەق (يەنى ئىسلامنى ئاشكارا) قىلىدۇ، باتىل (يەنى كۇفرى) نى بەربات قىلىدۇ'),
(1169, 8, 9, 'ئۆز ۋاقتىدا (دۇئا قىلىپ) پەرۋەردىگارىڭلاردىن ياردەم تىلىدىڭلار، اﷲ: «سىلەرگە ئاقىمۇ - ئارقا (چۈشىدىغان) مىڭ پەرىشتە بىلەن ياردەم بېرىمەن» دەپ دۇئايىڭلارنى ئىجابەت قىلدى'),
(1170, 8, 10, 'اﷲ سىلەرگە پەقەت (غەلىبە قىلىدىغانلىقىڭلارغا) بېشارەت بېرىش ۋە كۆڭلۈڭلارنى ئارام تاپقۇزۇش ئۈچۈنلا ياردەم بەردى. ياردەم پەقەت اﷲ تەرىپىدىنلا كېلىدۇ. اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1171, 8, 11, 'ئۆز ۋاقتىدا اﷲ تىنچلاندۇرۇش يۈزىسىدىن سىلەرگە ئۇيقۇ بېغىشلىدى؛ (تاھارەت ئېلىپ، غۇسلى قىلىپ) پاڭ بولۇشۇڭلار ئۈچۈن، سىلەردىن شەيتاننىڭ ۋەسۋەسىسىنى كەتكۈزۈش ئۈچۈن، (اﷲ نىڭ ياردىمىگە ئىشەنچ قىلىپ) كۆڭلۈڭلارنىڭ توق تۇرۇشى ئۈچۈن، قەدىمىڭلارنىڭ (قۇمغا پېتىپ كەتمەي) مەزمۇت تۇرۇشى ئۈچۈن، اﷲ سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى'),
(1172, 8, 12, 'ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە: «مەن سىلەر بىلەن بىللە، مۆمىنلەرنى (ئۇرۇش مەيدانىدا) ساباتلىق قىلىڭلار، كاپىرلارنىڭ دىللىرىغا قورقۇنچ سالىمەن» دەپ ۋەھيى قىلدى. (قىلىچ بىلەن كاپىرلارنىڭ) گەدەنلىرىگە چېپىڭلار (يەنى باشلىرىنى كېسىڭلار)، ئۇلارنىڭ بارماقلىرىغا (ھەممە ئەزاسىغا چېپىڭلار'),
(1173, 8, 13, 'بۇ شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە قارشىلىق قىلدى. كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە قارشىلىق قىلىدىكەن، اﷲ ئۇنى قاتتىق جازالايدۇ'),
(1174, 8, 14, '(ئى كاپىرلار جامائەسى! بۇ دۇنيادا) مۇشۇنداق جازانى تېتىڭلار. كاپىرلار (ئاخىرەتتە) دوزاخ ئازابىغا دۇچار بولىدۇ'),
(1175, 8, 15, 'ئى مۆمىنلەر! كاپىرلارنىڭ ھۇجۇمىغا دۇچ كەلگەن چېغىڭلاردا، ئۇلارغا ئارقاڭلارنى قىلماڭلار (يەنى قاچماڭلار)'),
(1176, 8, 16, 'كىمكى قايتا ئۇرۇشۇش ياكى (ياردەم تىلەش) ئۈچۈن مۇسۇلمانلار جامائەسىگە قوشۇلۇش مەقسىتىدە بىر تەرەپكە يۆتكىلىش قىلماستىن، بەلكى دۈشمەنگە ئارقىسىنى قىلىدىكەن (يەنى قاچىدىكەن)، ئۇ ھەقىقەتەن اﷲ نىڭ غەزىپىگە ئۇچرايدۇ، ئۇنىڭ جايى دوزاخ بولىدۇ. دوزاخ نېمىدېگەن يامان جاي!'),
(1177, 8, 17, '(ئى مۇسۇلمانلار! بەدرىدە) ئۇلارنى (يەنى مۇشرىكلارنى) سىلەر (ئۆز كۈچۈڭلار بىلەن) ئۆلتۈرگىنىڭلار يوق، بەلكى ئەمەلدە ئۇلارنى (سىلەرگە ياردەم بېرىپ، ئۇلارنىڭ دىللىرىدا قورقۇنچ پەيدا قىلىش بىلەن) اﷲ ئۆلتۈردى، (ئى مۇھەممەد بىر سىقىم توپىنى مۇشرىكلارغا) ئاتقىنىڭدا سەن ئاتمىدىڭ، بەلكى ئەمەلدە ئۇنى (مۇشرىكلارنىڭ كۆزلىرىگە) اﷲ ئاتتى. (اﷲ نىڭ مۇنداق قىلىشى) مۆمىنلەرگە (ساۋاب، غەلىبە، غەنىيمەتلەردىن ئىبارەت) چىرايلىق ئىنئاملارنى ئاتا قىلىش ئۈچۈن ئىدى. اﷲ ھەقىقەتەن (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى، ئەھۋاللىرىنى) بىلىپ تۇرغۇچىدۇر'),
(1178, 8, 18, 'بۇ (اﷲ نىڭ مۆمىنلەرگە بەرگەن ئىنئامىدۇر). اﷲ كاپىرلارنىڭ ھىيلىسىنى چوقۇم مەغلۇپ قىلغۇچىدۇر'),
(1179, 8, 19, '(ئى كۇففارلار جامائەسى!) ئەگەر سىلەر غەلىبىنى تىلىسەڭلار، غەلىبە ئاللىقاچان سىلەرگە كەلدى (يەنى ئۇ سىلەرنىڭ زىيىنىڭلارغا كەلدى). ئەگەر (پەيغەمبەر بىلەن دۈشمەنلىشىشتىن) يانساڭلار، بۇ سىلەر ئۈچۈن ياخشىدۇر، ئەگەر ئۇنىڭ بىلەن يەنە ئۇرۇشساڭلار، بىز ئۇنىڭغا يەنە ياردەم بېرىمىز، قوشۇنۇڭلار كۆپ بولغان تەقدىردىمۇ، ئۇ سىلەردىن ھېچ نەرسىنى دەپئى قىلىپ بېرەلمەيدۇ، اﷲ ھەقىقەتەن مۆمىنلەر بىلەن بىللىدۇر'),
(1180, 8, 20, 'ئى مۆمىنلەر! اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، سىلەر (قۇرئاننى) ئاڭلاپ تۇرۇپ ئۇنىڭدىن يۈز ئۆرۈمەڭلار'),
(1181, 8, 21, 'ئاڭلىماي تۇرۇپ ئاڭلىدۇق دېگەن كىشىلەرنى (يەنى كاپىرلارنى) دورىماڭلار'),
(1182, 8, 22, 'شۈبھىسىزكى، اﷲ نىڭ نەزىرىدە ھايۋانلارنىڭ ئەڭ يامىنى (ھەقنى ئاڭلىماي) گاس بولۇۋالغان، (ھەقنى سۆزلىمەي) گاچا بولۇۋالغان، (ياخشى - ياماننى پەرق ئەتمەي) ئەقلىنى يوقاتقان كىشىلەردۇر'),
(1183, 8, 23, 'ئەگەر اﷲ ئۇلاردا بىرەر ياخشىلىق بار دەپ بىلسە ئىدى، ئەلۋەتتە ئۇلارغا (ھەقنى) ئاڭلىتاتتى؛ ئۇلارغا ئاڭلاتقان تەقدىردىمۇ، ئۇلار ھەقتىن ئەلۋەتتە يۈز ئۆرۈگەن ھالدا باش تارتاتتى'),
(1184, 8, 24, 'ئى مۆمىنلەر! اﷲ ۋە ئۇنىڭ پەيغەمبىرى سىلەرنى تىرىلدۈرىدىغان (يەنى ئەبەدىي ھاياتقا ئېرىشتۈرىدىغان) ئىمانغا دەۋەت قىلسا، ئۇنى قوبۇل قىلىڭلار. بىلىڭلاركى، اﷲ كىشى بىلەن ئۇنىڭ قەلبى ئارىسىدا توسالغۇ بولالايدۇ (يەنى كىشىنىڭ دىلىنى ئىگىسىنىڭ خاھىشى بويىچە ئەمەس، بەلكى ئۆز خاھىشى بويىچە تەسەررۇپ قىلىدۇ). (قىيامەت كۈنى) سىلەر اﷲ نىڭ دەرگاھىغا يىغىلىسىلەر'),
(1185, 8, 25, 'سىلەرنىڭ ئاراڭلاردىكى زۇلۇم قىلغانلارنىڭ بېشىغا كېلىش بىلەنلا چەكلىنىپ قالمايدىغان بالا - قازادىن ساقلىنىڭلار، بىلىڭلاركى، اﷲ نىڭ ئازابى قاتتىقتۇر'),
(1186, 8, 26, 'ياد ئېيتىڭلاركى، ئۆز ۋاقتىدا سىلەر (مەككە) زېمىنىدا ئاز سانلىق بولۇپ، بوزەك قىلىنغان ئىدىڭلار، كىشىلەر (يەنى مۇشرىكلار) نىڭ تالان - تاراج قىلىشىدىن قورقاتتىڭلار، شۈكۈر قىلىشىڭلار ئۈچۈن اﷲ سىلەرنى (مەدىنىدە) يەرلەشتۈردى، (بەدرى ئۇرۇشىدا) ئۆز ياردىمى بىلەن سىلەرنى كۈچلەندۈردى. سىلەرگە ھالال نەرسىلەر (يەنى غەنىيمەتلەر) نى رىزىق قىلىپ بەردى'),
(1187, 8, 27, 'ئى مۆمىنلەر! اﷲ قا، پەيغەمبەرگە خىيانەت قىلماڭلار، سىلەرگە قويۇلغان ئامانەتلەرگە بىلىپ تۇرۇپ خىيانەت قىلماڭلار'),
(1188, 8, 28, 'بىلىڭلاركى، سىلەرنىڭ ماللىرىڭلار، بالىلىرىڭلار سىلەر ئۈچۈن بىر تۈرلۈك سىناقتۇر، اﷲ نىڭ دەرگاھىدا چوڭ ساۋاب بار'),
(1189, 8, 29, 'ئى مۆمىنلەر! ئەگەر اﷲ تىن قورقساڭلار، اﷲ سىلەرگە ھەق بىلەن باتىلنى ئايرىيدىغان ھىدايەت ئاتا قىلىدۇ، گۇناھىڭلارنى كەچۈرىدۇ، سىلەرگە مەغپىرەت قىلىدۇ. اﷲ كاتتا پەزل ئىگىسىدۇر'),
(1190, 8, 30, '(ئى مۇھەممەد!) ئۆز ۋاقتىدا كاپىرلار سېنى قاماققا ئېلىش، ياكى ئۆلتۈرۈش ۋە ياكى (مەككىدىن) ھەيدەپ چىقىرىش ئۈچۈن مىكىر ئىشلەتتى، ئۇلار مىكىر ئىشلىتىدۇ، اﷲ ئۇلارنىڭ مىكرىنى بەربات قىلىدۇ، اﷲ نىڭ تەدبىرى ئۇلارنىڭ مىكرىدىن ئۈنۈملۈكتۇر'),
(1191, 8, 31, 'ئۇلارغا بىزنىڭ ئايەتلىرىمىز تىلاۋەت قىلىنسا، ئۇلار: «بىز (بۇ سۆزلەرنى) ئاللىقاچان ئاڭلىغان ئىدۇق، ئەگەر خالىساق ئۇنىڭغا ئوخشاش سۆزلەرنى بىزمۇ ئەلۋەتتە قىلالايتتۇق، بۇ پەقەت بۇرۇنقىلاردىن قالغان ئەپسانىلاردۇر» دېدى'),
(1192, 8, 32, 'ئۆز ۋاقتىدا ئۇلار: «ئەگەر بۇ (يەنى قۇرئان) سېنىڭ تەرىپىڭدىن نازىل بولغان ھەقىقەت بولىدىغان بولسا، ئاسماندىن ئۈستىمىزگە تاش ياغدۇرغىن، ياكى بىزنى قاتتىق ئازاب بىلەن جازالىغىن!» دېدى'),
(1193, 8, 33, 'سەن ئۇلارنىڭ ئىچىدە تۇرغان چېغىڭدا (سېنى ھۆرمەتلەش يۈزىسىدىن) اﷲ ئۇلارغا ئازاب قىلمايدۇ، ئۇلار ئىستىغپار ئېيتىپ تۇرغان چاغدا اﷲ ئۇلارغا ئازاب قىلمايدۇ'),
(1194, 8, 34, 'ئۇلار (باشقىلارنىڭ) مەسجىدى ھەرەمغا كىرىشىنى توسۇۋاتسا، اﷲ ئۇلارغا نېمىشقا ئازاب قىلمىسۇن؟ ئۇلار مەسجىدى ھەرەمنىڭ ئىگىلىرى ئەمەس، مەسجىدى ھەرەمنىڭ ئىگىلىرى تەقۋادارلاردىن باشقىلار ئەمەستۇر، لېكىن (بۇنى) ئۇلارنىڭ تولىسى بىلمەيدۇ'),
(1195, 8, 35, 'ئۇلارنىڭ مەسجىدى ھەرەمنىڭ يېنىدىكى ئىبادىتى پەقەت ئىسقىرتماق، چاۋاك چالماقتىنلا ئىبارەت بولدى. (ئى كاپىرلار!) كاپىر بولغانلىقىڭلار تۈپەيلىدىن (دۇنيا ۋە ئاخىرەتنىڭ) ئازابىنى تېتىڭلار'),
(1196, 8, 36, 'شۈبھىسىزكى، كاپىرلار مال - مۈلۈكلىرىنى (باشقىلارنى) اﷲ نىڭ يولىدىن توسۇش ئۈچۈن سەرپ قىلىدۇ، سەرپ قىلىنغاندىن كېيىن ئۇلارنىڭ مال - مۈلۈكلىرى ئۇلارغا ھەسرەت ئېلىپ كېلىدۇ، ئاخىر ئۇلار يېڭىلىدۇ. كاپىرلار جەھەننەمگىلا ھەيدەپ كىرگۈزۈلىدۇ'),
(1197, 8, 37, 'اﷲ نىڭ (ئۇلارنى دۇنيادا مەغلۇپ قىلىشى، ئاخىرەتتە جەھەننەمگە ھەيدىشى) كاپىرنى مۆمىندىن پەرقلەندۈرۈش ئۈچۈندۇر، اﷲ ھەممە كاپىرلارنى ئۈستى - ئۈستىگە دۆۋىلەپ توپلىغاندىن كېيىن، دوزاخقا تاشلايدۇ، ئەنە شۇلار زىيان تارتقۇچىلاردۇر'),
(1198, 8, 38, '(ئى مۇھەممەد!) كاپىرلارغا ئېيتقىنكى، ئەگەر ئۇلار (كۇفرىدىن، پەيغەمبەر بىلەن دۈشمەنلىشىشتىن) يانسا، ئۇلارنىڭ ئۆتكەنكى پەيغەمبەرلىرىمنى ئىنكار قىلغانلارنى ھالاك قىلىش مېنىڭ تۇتقان يولۇم بولۇپ كەلدى (ئۇلارنىمۇ شۇنداق قىلىمەن)'),
(1199, 8, 39, 'پىتنە تۈگىگەن، دىن پۈتۈنلەي اﷲ ئۈچۈن بولغانغا قەدەر ئۇلار بىلەن ئۇرۇشۇڭلار؛ ئەگەر ئۇلار (كۇفرىدىن) يانسا، اﷲ ئۇلارنىڭ قىلمىشلىرىنى كۆرىدۇ'),
(1200, 8, 40, 'ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە، (ئى مۆمىنلەر!) بىلىڭلاركى، اﷲ سىلەرنىڭ مەدەتكارىڭلاردۇر، اﷲ نېمىدېگەن ياخشى مەدەتكار، نېمىدېگەن ياخشى ياردەمچى!'),
(1201, 8, 41, '(ئى مۆمىنلەر!) بىلىڭلاركى، سىلەر ئالغان غەنىيمەتنىڭ بەشتىن بىرى اﷲ قا، پەيغەمبەرگە، پەيغەمبەرنىڭ خىش - ئەقرىبالىرىغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە خاستۇر. ئەگەر سىلەر اﷲ قا، ئىككى گۇرۇھ (يەنى مۇسۇلمانلار بىلەن كاپىرلار) ئۇچرىشىپ ھەق بىلەن ناھەق ئايرىلغان كۈندە (يەنى بەدرى ئۇرۇشى بولغان كۈندە) بەندىمىزگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىز نازىل قىلغان نەرسىلەر (يەنى ئايەتلەر ۋە پەرىشتىلەر) گە ئىشەنسەڭلار (اﷲ نىڭ غەنىيمەت توغرىسىدىكى ھۆكمىنىڭ ئەنە شۇ ئىكەنلىكىنى بىلىڭلار). اﷲ ھەر نەرسىگە قادىردۇر'),
(1202, 8, 42, 'ئۆز ۋاقتىدا سىلەر ۋادىنىڭ (مەدىنىگە) يېقىن تەرىپىدە ئىدىڭلار، ئۇلار ۋادىنىڭ (مەدىنىگە) يىراق تەرىپىدە ئىدى. (قۇرەيشنىڭ) سودا كارۋىنى بولسا سىلەرنىڭ تۆۋىنىڭلاردا ئىدى. (مۇشرىكلار بىلەن ئۇچرىشىشنى) ۋەدىلەشكەن بولساڭلار، سىلەر (ئۆزۈڭلارنىڭ ئازلىقىنى، مۇشرىكلارنىڭ كۆپلۈكىنى كۆرۈپ) ئەلۋەتتە بۇنىڭغا خىلاپلىق قىلغان بولاتتىڭلار، لېكىن اﷲ بولۇشقا تېگىشلىك ئىشنى (يەنى مۇسۇلمانلارنى ئەزىز، مۇشرىكلارنى خار قىلىشنى) ئەمەلگە ئاشۇرۇش ئۈچۈن (سىلەرنى بەدرىدە مۇشرىكلار بىلەن ۋەدىسىز ئۇچراشتۇردى). اﷲ نىڭ مۇنداق قىلىشى ھالاك بولىدىغانلارنىڭ روشەن دەلىلنى كۆرۈپ ئاندىن ھالاك بولۇشى، ياشايدىغانلارنىڭ روشەن دەلىلنى كۆرۈپ ئاندىن ياشىشى ئۈچۈن ئىدى. اﷲ (بەندىلىرىنىڭ سۆزلىرىنى)، ئەلۋەتتە، ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(1203, 8, 43, '(ئى مۇھەممەد!) ئۆز ۋاقتىدا، اﷲ چۈشۈڭدە ساڭا ئۇلارنى (يەنى دۈشمەنلىرىڭنى) ئاز كۆرسەتتى، ئەگەر ساڭا ئۇلارنى كۆپ كۆرسەتكەن بولسا، سىلەرگە چوقۇم قورقۇنچ چۈشەتتى ۋە جەڭ ئىشىدا چوقۇم دەتالاش قىلاتتىڭلار، لېكىن اﷲ (سىلەرنى مۇنداق قىلىشتىن) ساقلىدى. اﷲ دىللاردىكىنى ئەلۋەتتە بىلىپ تۇرغۇچىدۇر'),
(1204, 8, 44, 'اﷲ بولۇشقا تېگىشلىك ئىشنى ئىشقا ئاشۇرۇش ئۈچۈن، دۈشمەن بىلەن ئۇچراشقان چېغىڭلاردا، (دۈشمەنلەرگە قارشى تۇرۇشقا جۈرئەت قىلسۇن دەپ) ئۇلارنى سىلەرنىڭ كۆزۈڭلارغا ئاز كۆرسەتتى ۋە سىلەرنى ئۇلارنىڭ كۆزلىرىگە (سىلەرگە قارشى تەييارلىقتا بولمىسۇن دەپ) ئاز كۆرسەتتى ھەممە ئىش اﷲ قا قايتۇرۇلىدۇ (اﷲ ئۆزى خالىغانچە ھۆكۈم قىلىدۇ)'),
(1205, 8, 45, 'ئى مۆمىنلەر! (مۇشرىكلاردىن) بىر جامائەگە (يەنى دۈشمەن قوشۇنىغا) ئۇچراشقان چېغىڭلاردا ساباتلىق كۆرسىتىڭلار، مۇۋەپپەقىيەت قازىنىشىڭلار ئۈچۈن اﷲ نى كۆپ ياد ئېتىڭلار'),
(1206, 8, 46, '(پۈتۈن سۆز ھەرىكەتلىرىڭلاردا) اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، ئىختىلاپ قىلىشماڭلار، بولمىسا، (دۈشمەن بىلەن ئۇچرىشىشتىن) قورقۇپ قالىسىلەر، كۈچ - قۇۋۋىتىڭلار كېتىپ قالىدۇ؛ سەۋر قىلىڭلار، اﷲ ھەقىقەتەن سەۋر قىلغۇچىلار بىلەن بىللىدۇر'),
(1207, 8, 47, 'يۇرتلىرىدىن چوڭچىلىق ۋە رىيا بىلەن چىققان كىشىلەر (يەنى قۇرەيش مۇشرىكلىرى) گە ئوخشاش بولماڭلار، ئۇلار (كىشىلەرنى) اﷲ نىڭ يولىدىن توسىدۇ، اﷲ ئۇلارنىڭ ھەممە ئىشلىرىنى بىلگۈچىدۇر'),
(1208, 8, 48, 'ئۆز ۋاقتىدا شەيتان ئۇلارغا (قەبىھ) ئەمەللىرىنى چىرايلىق كۆرسىتىپ: «ھېچقانداق كىشى سىلەرنى يېڭەلمەيدۇ، مەن ھەقىقەتەن سىلەرگە مەدەتكار» دېگەن ئىدى. ئىككى قوشۇن ئۇچراشقان چاغدا شەيتان ئارقىسىغا چېكىندى ۋە: «مەن سىلەردىن ئادا - جۇدامەن، سىلەر كۆرمەيۋاتقاننى ھەقىقەتەن كۆرۈپ تۇرۇۋاتىمەن، مەن راستلا اﷲ تىن قورقىمەن، اﷲ نىڭ ئازابى قاتتىقتۇر» دېدى'),
(1209, 8, 49, 'ئۆز ۋاقتىدا مۇناپىقلار ۋە دىللىرىدا كېسىلى (يەنى شەك) بارلار: «بۇلارنى (يەنى مۇسۇلمانلارنى) ئۇلارنىڭ دىنى ئالدىدى (يەنى مۇسۇلمانلار ئۆز دىنىغا مەغرۇر بولۇپ، ئۆزلىرىنى يېڭىلمەس دەپ گۇمان قىلىپ، ئاز سانلىق تۇرۇقلۇق كۆپ سانلىقلار بىلەن ئۇرۇشۇشقا چىقتى)» دېدى. كىمكى اﷲ قا يۆلىنىدىكەن (اﷲ ئۇنىڭغا ئەلۋەتتە ياردەم بېرىدۇ). چۈنكى اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1210, 8, 50, 'پەرىشتىلەر (بەدرى ئۇرۇشىدا) كاپىرلارنىڭ جانلىرىنى ئېلىۋاتقاندا، ئۇلارنىڭ يۈزلىرىگە ۋە ئارقىلىرىغا ئۇرۇۋاتقانلىقىنى كۆرسەڭ ئىدىڭ (ئەلۋەتتە قورقۇنچلۇق ھالنى كۆرگەن بولاتتىڭ). (پەرىشتىلەر ئۇلارغا) «دوزاخنىڭ كۆيدۈرگۈچى ئازابىنى تېتىڭلار!» (دەيتتى)'),
(1211, 8, 51, 'بۇ (ئازاب) سىلەرنىڭ قىلمىش جىنايىتىڭلار تۈپەيلىدىندۇر، اﷲ بەندىلىرىگە زۇلۇم قىلغۇچى ئەمەستۇر'),
(1212, 8, 52, '(بۇ كاپىرلارنىڭ گۇناھ قىلىش ئادىتى) پىرئەۋن جامائەسى ۋە ئۇلاردىن بۇرۇنقىلارنىڭ ئادىتىگە ئوخشايدۇ، ئۇلار اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلدى، گۇناھلىرى تۈپەيلىدىن، اﷲ ئۇلارنى ھالاك قىلدى. اﷲ ھەقىقەتەن كۈچلۈكتۇر، اﷲ نىڭ ئازابى قاتتىقتۇر'),
(1213, 8, 53, 'بۇ (يەنى ئۇلارغا نازىل بولغان ئازاب) شۇنىڭ ئۈچۈنكى، بىرەر قەۋم ئۆزلىرىنىڭ ھالىتىنى (كۇفرى ۋە گۇناھ بىلەن) ئۆزگەرتمىگۈچە اﷲ ئۇلارغا بەرگەن نېمىتىنى ئۆزگەرتىۋەتمەيدۇ، اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (قىلمىشلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(1214, 8, 54, '(ئۇلارنىڭ ئادىتى) پىرئەۋن جامائەسىنىڭ ۋە ئۇلاردىن بۇرۇنقىلارنىڭ ئادىتىگە ئوخشايدۇكى، ئۇلار پەرۋەردىگارىنىڭ ئايەتلىرىنى ئىنكار قىلغان ئىدى. گۇناھلىرى تۈپەيلىدىن ئۇلارنى ھالاك قىلدۇق، پىرئەۋن جامائەسىنى (دېڭىزغا) غەرق قىلدۇق، ئۇلارنىڭ ھەممىسى (كۇفرىلىق ۋە گۇناھ قىلىپ، ئۆزلىرىنى ئازابقا دۇچار قىلىش بىلەن) ئۆزلىرىگە زۇلۇم قىلغۇچى (قەۋم) بولدى'),
(1215, 8, 55, 'اﷲ نىڭ نەزىرىدە ھايۋانلارنىڭ ئەڭ يامىنى كۇفرىدا چىڭ تۇرغانلاردۇركى، ئۇلار ھەقىقەتەن ئىمان ئېيتمايدۇ'),
(1216, 8, 56, '(ئى مۇھەممەد!) سەن ئۇلارنىڭ ئارىسىدىكى مەلۇم كىشىلەر بىلەن (مۇشرىكلارغا ياردەم بەرمەسلىكلىرى ئۈستىدە) مۇئاھىدە تۈزدۈڭ، ئۇلار بولسا ھەر قېتىمدا مۇئاھىدىسىنى بۇزىدۇ، ئۇلار اﷲ تىن قورقمايدۇ'),
(1217, 8, 57, 'ئەگەر ئۇرۇشتا ئۇلار ئۈستىدىن غەلىبە قىلساڭ، ئارقىدىكىلىرىنىڭ قېچىشى ۋە ئىبرەت ئېلىشى ئۈچۈن ئۇلارنى قاتتىق جازالىغىن (يەنى ئۇلارنى باشقىلارغا ئىبرەت قىلغىنكى، ئۇلارنىڭ سەن بىلەن ئۇرۇش قىلىشقا دەرمانى قالمىسۇن)'),
(1218, 8, 58, 'ئەگەر سەن (مۇئاھىدە تۈزگەن) قەۋمدىن خىيانەت (ئالامەتلىرىنى) سەزسەڭ، مۇئاھىدىسىنى ئۇلارغا ئوچۇق - يورۇقلۇق بىلەن تاشلاپ بەرگىن (يەنى سەن بىلەن مۇئاھىدە تۈزۈشكەن قەۋمدىن خىيانەت شەپىسى كۆرۈلسە، ئۇلارغا تۇيۇقسىز ھۇجۇم قىلماستىن، مۇئاھىدىنىڭ ئەمەلدىن قالغانلىقىنى ئالدى بىلەن ئۇلارغا ئۇقتۇرۇپ قويغىن). اﷲ ھەقىقەتەن خائىنلارنى دوست تۇتمايدۇ'),
(1219, 8, 59, '(بەدرى ئۇرۇشىدا ئۆلتۈرۈلمەي قالغان) كاپىرلار قۇتۇلدۇق دەپ ئويلىمىسۇن، ئۇلار (پەرۋەردىگارىنىڭ جازاسىدىن) ھەقىقەتەن قېچىپ قۇتۇلالمايدۇ'),
(1220, 8, 60, 'دۈشمەنلىرىڭلار (بىلەن ئۇرۇش قىلىش) ئۈچۈن، قولۇڭلاردىن كېلىشىچە قورال كۈچى، جەڭ ئېتى تەييارلاڭلار، بۇنىڭ بىلەن، اﷲ نىڭ دۈشمىنىنى، ئۆزۈڭلارنىڭ دۈشمىنىڭلارنى ۋە ئۇلاردىن باشقا دۈشمەنلەرنى قورقۇتىسىلەر، ئۇلارنى سىلەر تونۇمايسىلەر، اﷲ تونۇيدۇ، سىلەرنىڭ اﷲ يولىدا سەرپ قىلغىنىڭلار مەيلى نېمە بولسا بولسۇن، سىلەرگە ئۇنىڭ ساۋابى تولۇق بېرىلىدۇ، سىلەرگە زۇلۇم قىلىنمايدۇ (يەنى بۇ ساۋابتىن ھېچ نەرسە كېمەيتىلمەيدۇ)'),
(1221, 8, 61, 'ئەگەر ئۇلار تىنچلىققا مايىل بولسا، سەنمۇ تىنچلىققا مايىل بولغىن، اﷲ قا تەۋەككۈل قىلغىن. اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(1222, 8, 62, 'ئەگەر ئۇلار (تەييارلىق قىلىۋېلىش ئۈچۈن سۈلھى بىلەن) سېنى ئالدىماقچى بولسا، اﷲ (ئۇلارنىڭ شەررىدىن) شەكسىز ساڭا كۇپايە قىلغۇچىدۇر. اﷲ سېنى ئۆزنىڭ ياردىمى بىلەن ۋە مۆمىنلەر بىلەن كۈچلەندۈرىدۇ'),
(1223, 8, 63, 'اﷲ مۆمىنلەرنىڭ دىللىرىنى بىرلەشتۈردى. سەن يەر يۈزىدىكى پۈتۈن بايلىقنى سەرپ قىلىپمۇ ئۇلارنىڭ دىللىرىنى بىرلەشتۈرەلمەيتىڭ؛ لېكىن اﷲ (ئۆزىنىڭ قۇدرەت كامىلەسى بىلەن) ئۇلارنى ئىناق قىلدى. شۈبھىسىزكى، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1224, 8, 64, 'ئى پەيغەمبەر! (يالغۇز) اﷲ ساڭا ۋە ساڭا ئەگەشكەن مۆمىنلەرگە كۇپايە قىلغۇچىدۇر'),
(1225, 8, 65, 'ئى پەيغەمبەر! مۆمىنلەرنى (مۇشرىكلار بىلەن) ئۇرۇش قىلىشقا رىغبەتلەندۈرگىن، ئەگەر سىلەردە چىداملىق يىگىرمە ئادەم بولىدىغان بولسا، (دۈشمەندىن) 200 نى يېڭەلەيدۇ؛ ئەگەر سىلەردە (چىداملىق) 100 ئادەم بولىدىغان بولسا، كاپىرلاردىن 1000 نى يېڭەلەيدۇ؛ چۈنكى ئۇلار (جاھىل) قەۋم بولۇپ، (اﷲ نىڭ ھېكمىتىنى) چۈشەنمەيدۇ'),
(1226, 8, 66, 'اﷲ ئەمدى سىلەرنىڭ (يۈكۈڭلارنى) يىنىكلەتتى، سىلەرنىڭ ئاجىزلىقىڭلارنى بىلدى، ئەگەر سىلەردە چىداملىق 100 ئادەم بولىدىغان بولسا، 200 (دۈشمەن) نى يېڭەلەيدۇ، ئەگەر سىلەردە (چىداملىق) 1000 ئادەم بولىدىغان بولسا، اﷲ نىڭ ئىزنى بىلەن 2000 (دۈشمەن) نى يېڭەلەيدۇ. اﷲ چىداملىقلار بىلەن بىللىدۇر'),
(1227, 8, 67, 'پەيغەمبەرگە زېمىندا دۈشمەننى كۆپرەك ئۆلتۈرمەي تۇرۇپ (يەنى مۇشرىكلىكنىڭ ھەيۋىسىنى يوقىتىپ، ئىسلامغا قۇۋۋەت بەرمەي تۇرۇپ)، ئەسىرلەردىن فىدىيە ئېلىش لايىق ئەمەس ئىدى. (ئى مۆمىنلەر! سىلەر فىدىيە ئېلىش بىلەن) دۇنيا مەنپەئىتىنى كۆزلەيسىلەر، اﷲ سىلەرگە ئاخىرەتنى (يەنى ئاخىرەتنىڭ ساۋابىنى) تىلەيدۇ. اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1228, 8, 68, 'اﷲ نىڭ (ئوچۇق چەكلەنمىگەن ئىشنى قىلغانلارنى جازالىماسلىق دېگەن) ھۆكمى ئەزىلىسى بولمىسا ئىدى، (ئەسىرلەردىن) فىدىيە ئالغانلىقىڭلار ئۈچۈن ئەلۋەتتە زور ئازابقا دۇچار بولاتتىڭلار'),
(1229, 8, 69, '(ئى جىھاد قىلغۇچىلار جامائەسى!) غەنىيمەت ئالغان نەرسەڭلارنى ھالال ۋە پاك بىلىپ يەڭلار، اﷲ (نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىش) دىن قورقۇڭلار. اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر'),
(1230, 8, 70, 'ئى پەيغەمبەر! قولۇڭلاردىكى ئەسىرلەرگە ئېيتقىنكى، ئەگەر اﷲ دىلىڭلاردا ئىمان بارلىقىنى بىلسە (يەنى دىلىڭلاردا ئىمان بولسا، اﷲ ئۇنى بىلىدۇ)، سىلەرگە ئۆزۈڭلاردىن ئېلىنغان فىدىيىدىن ياخشىراق نەرسىنى بېرىدۇ، (ئىلگىرىكى گۇناھلىرىڭلارنى) مەغپىرەت قىلىدۇ. اﷲ (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر'),
(1231, 8, 71, '(ئى مۇھەممەد!) ئەگەر ئۇلار (يەنى مۇشۇ ئەسەرلەر ئىماننى دەۋا قىلىش ئارقىلىق) ساڭا خىيانەت قىلماقچى بولغان بولسا، ئۇنداقتا ئىلگىرى ئۇلار (كۇفرى بىلەن، ئەھدىنى بۇزۇش بىلەن) اﷲ قا خىيانەت قىلغان ئىدى، اﷲ سېنى ئۇلارنىڭ ئۈستىدىن غالىب قىلدى. اﷲ (بولغۇسى ھەممە ئىشنى) بىلگۈچى، ئۆز ھېكمىتى بويىچە ئىش قىلغۇچىدۇر'),
(1232, 8, 72, 'ئىمان ئېيتقانلار، (اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى سۆيۈش يۈزىسىدىن يۇرتىنى تاشلاپ) ھىجرەت قىلغان، پۇل - ماللىرى ۋە جانلىرى بىلەن اﷲ يولىدا جىھاد قىلغانلار (يەنى مۇھاجىرلار)، (يۇرتىدا مۇھاجىرلارغا) جاي بەرگەنلەر ۋە ياردەم كۆرسەتكەنلەر (يەنى ئەنسارلار) - ئەنە شۇلار ئەلۋەتتە بىر - بىرىگە ئىگىدۇر (يەنى بىر - بىرىگە ياردەمچىدۇر، بىر - بىرىگە مىراسخوردۇر). ئىمان ئېيتقان ئەمما (مەدىنىگە) ھىجرەت قىلمىغانلار بىلەن سىلەرنىڭ ئوتتۇراڭلاردا تاكى ئۇلار ھىجرەت قىلغانغا قەدەر ئۆزئارا ئىگە بولۇش (يەنى ياردەملىشىش، مىراسخور بولۇش) بولمايدۇ؛ ئەگەر دىن ئىشىدا ئۇلار سىلەردىن ياردەم تەلەپ قىلسا، ئۇلارنىڭ دۈشمىنى بىلەن سىلەرنىڭ ئاراڭلاردا كېلىشىم بولمىسىلا، ئۇلارغا ياردەم بېرىشىڭلار كېرەك، اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(1233, 8, 73, 'كاپىرلار (ياردەمدە ۋە مىراستا) بىر - بىرىگە ئىگىدۇر، ئەگەر بۇ ھۆكۈمگە ئەمەل قىلمىساڭلار، يەر يۈزىدە پىتنە ۋە چوڭ قالايمىقانچىلىق تۇغۇلىدۇ'),
(1234, 8, 74, 'ئىمان ئېيتقانلار، (مەدىنىگە) ھىجرەت قىلغانلار، اﷲ نىڭ يولىدا جىھاد قىلغانلار، (يۇرتىدا مۇھاجىرلارغا) جاي بەرگەنلەر ۋە ياردەم كۆرسەتكەنلەر (يەنى ئەنسارلار) - ئەنە شۇلار ھەقىقىي مۆمىنلەردۇر، ئۇلار (نىڭ گۇناھى) مەغپىرەت قىلىنىدۇ، ئۇلار (جەننەتتە) ئېسىل رىزىققا ئىگە بولىدۇ'),
(1235, 8, 75, 'كېيىن ئىمان ئېيتىپ (مەدىنىگە) ھىجرەت قىلغانلار ۋە سىلەر بىلەن بىللە جىھاد قىلغانلار سىلەردىن سانىلىدۇ. اﷲ نىڭ ھۆكمىدە، ئۇرۇق - تۇغقانلار بىر - بىرىگە مىراسخور بولۇشقا (ياتلاردىن) ئەڭ ھەقلىقتۇر، شۈبھىسىزكى، اﷲ ھەر شەيئىنى بىلىپ تۇرغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, '(بۇ) اﷲ ۋە ئۇنىڭ پەيغەمبىرىنىڭ سىلەر بىلەن مۇئاھىدە تۈزۈشكەن مۇشرىكلارغا قاراتقان ئادا - جۇدا بولۇش (يەنى مۇئاھىدىنى بىكار قىلىش) ئېلانىدۇر'),
(1237, 9, 2, '(ئى مۇشرىكلار!) يەر يۈزىدە (خالىغىنىڭلارچە) تۆت ئاي يۈرۈڭلار، بىلىڭلاركى، سىلەر اﷲ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر، (بىلىڭلاركى) اﷲ كاپىرلارنى خار قىلغۇچىدۇر'),
(1238, 9, 3, '(بۇ) چوڭ ھەج كۈنىدە اﷲ ۋە ئۇنىڭ پەيغەمبىرى تەرىپىدىن كىشىلەرگە چىقىرىلغان ئېلاندۇركى، اﷲ ۋە ئۇنىڭ پەيغەمبىرى مۇشرىكلاردىن ئادا - جۇدادۇر. ئەگەر (كۇفرىدىن) تەۋبە قىلساڭلار، بۇ سىلەرگە (گۇمراھلىقتا كېتىۋېرىشتىن) ياخشىدۇر، ئەگەر (ئىسلامدىن) يۈز ئۆرۈسەڭلار، بىلىڭلاركى، اﷲ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر. كاپىرلارغا قاتتىق ئازاب بىلەن بېشارەت بەرگىن'),
(1239, 9, 4, 'ئەمما مۇشرىكلار ئىچىدىكى سىلەر بىلەن مۇئاھىدە تۈزگەن، مۇئاھىدىگە ھېچقانداق خىلاپلىق قىلمىغان، سىلەرگە قارشى ھېچقانداق ئادەمگە ياردەم بەرمىگەنلەرگە كەلسەك، ئۇلار بىلەن تۈزگەن بۇ مۇئاھىدىگە ئۇنىڭ ۋاقتى توشقانغا قەدەر رىئايە قىلىڭلار، شۈبھىسىزكى،، اﷲ تەقۋادارلارنى دوست تۇتىدۇ'),
(1240, 9, 5, '(ئۇرۇش قىلىش) ھارام قىلىنغان ئايلار ئۆتۈپ كەتكەندە، مۇشرىكلارنى قەيەردە ئۇچراتساڭلار، شۇ يەردە ئۆلتۈرۈڭلار، ئەسىرگە ئېلىڭلار، قورشاڭلار، ئۇلارنىڭ ئۆتىدىغان يوللىرىنى كۆزىتىپ تۇرۇڭلار، ئەگەر ئۇلار تەۋبە قىلسا، ناماز ئوقۇسا، زاكات بەرسە، ئۇلارنى قويۇپ بېرىڭلار، شۈبھىسىزكى، اﷲ (تەۋبە قىلغۇچىلارغا) مەغپىرەت قىلغۇچىدۇر، رەھىم قىلغۇچىدۇر'),
(1241, 9, 6, 'ئەگەر مۇشرىكلاردىن بىرەر كىشى سەندىن ئامانلىق تىلىسە، تاكى ئۇ اﷲ نىڭ كالامىنى (يەنى قۇرئاننى) ئاڭلىغانغا (يەنى پىكىر قىلىپ، ئۇنىڭ ھەقىقىتىگە يەتكەنگە) قەدەر، ئۇنىڭغا ئامانلىق بەرگىن، ئاندىن (ئۇ ئىمان ئېيتمىسا)، ئۇنى (جېنى، مېلى) ئامان تاپىدىغان جايغا يەتكۈزۈپ قويغىن، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار (ئىسلام دىنىنىڭ ھەقىقىتىنى) ئۇقمايدىغان قەۋمدۇر'),
(1242, 9, 7, 'اﷲ نىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ قارىشىچە، (ئەھدىسىگە ۋاپا قىلمايدىغان) مۇشرىكلاردا قانداقمۇ مۇئاھىدە دېگەن نەرسە بولسۇن؟ ئەمما مەسجىدى ھەرەم قېشىدا سىلەر بىلەن ئەھدە تۈزۈشكەن مۇشرىكلار ئەگەر ئەھدىسىگە ۋاپا قىلىدىكەن، سىلەرمۇ ۋاپا قىلىڭلار، شۈبھىسىزكى، اﷲ تەقۋادارلارنى (يەنى پەرۋەردىگاردىن قورققان، ئەھدىسىگە ۋاپا قىلغان، خىيانەت قىلمىغانلارنى) دوست تۇتىدۇ'),
(1243, 9, 8, 'مۇشرىكلار ئەھدىسىگە قانداقمۇ ۋاپا قىلسۇن؟ ئەگەر ئۇلار سىلەرنىڭ ئۈستۈڭلاردىن غەلىبە قىلسا، سىلەرنىڭ توغراڭلاردا نە تۇغقانچىلىققا، نە ئەھدىگە رىئايە قىلمايدۇ. سىلەرنى ئېغىزلىرىدا خۇش قىلىدۇ، دىللىرىدا (ۋەدىسىدە تۇرۇشنى) خالىمايدۇ (يەنى ئاغزىدا چىرايلىق سۆزلىگىنى بىلەن، كۆڭۈللىرى ئاداۋەت، نىفاق بىلەن تولغان). ئۇلارنىڭ تولىسى پاسىقلاردۇر'),
(1244, 9, 9, 'ئۇلار اﷲ نىڭ ئايەتلىرىنى (دۇنيانىڭ مال - مۈلۈكلىرىدىن) ئەرزىمەس نەرسىلەرگە تېگىشتى، (كىشىلەرنى) اﷲ نىڭ يولىدىن توستى. ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!'),
(1245, 9, 10, 'ئۇلار مۆمىنلەر ھەققىدە نە تۇغقانچىلىققا، نە ئەھدىگە رىئايە قىلمايدۇ. ئەنە شۇلار (سىلەرگە زۇلۇم قىلىشتا) ھەددىدىن ئاشقۇچىلاردۇر'),
(1246, 9, 11, 'ئەگەر ئۇلار (كۇفرىدىن) تەۋبە قىلسا، ناماز ئۆتىسە، زاكات بەرسە، دىنىي جەھەتتە سىلەرنىڭ قېرىندىشىڭلار بولىدۇ (يەنى ھوقۇق ۋە مەجبۇرىيەتتە ئۇلار سىلەر بىلەن ئوخشاشتۇر). ئۇقىدىغان قەۋم ئۈچۈن، ئايەتلەرنى تەپسىلىي بايان قىلىمىز'),
(1247, 9, 12, 'ئەگەر ئۇلار ئەھدە بەرگەندىن كېيىن، قەسەملىرىنى بۇزسا ۋە دىنىڭلارنى ئەيىبلىسە، ئۇلارنىڭ (بۇنىڭدىن) چەكلىنىشلىرى ئۈچۈن، كۇفرىنىڭ كاتتىباشلىرىغا ئۇرۇش ئېچىڭلار. ئۇلارنىڭ قەسىمىنىڭ ھەقىقەتەن ئېتىبارى يوق'),
(1248, 9, 13, '(ئى مۆمىنلەر جامائەسى! ئەھدە بەرگەندە) ئىچكەن قەسىمىنى بۇزغان، پەيغەمبەرنى (مەككە) دىن ھەيدەپ چىقىرىشنى قەستلىگەن ۋە سىلەرگە ئالدى بىلەن ھۇجۇم قىلغان قەۋمگە ئۇرۇش ئاچمايسىلەر؟ ئۇلاردىن قورقامسىلەر؟ ئەگەر مۆمىن بولساڭلار، قورقۇشۇڭلارغا اﷲ ئەڭ لايىقتۇر'),
(1249, 9, 14, 'ئۇلارغا ئۇرۇش ئېچىڭلار، اﷲ ئۇلارنى سىلەرنىڭ قولۇڭلار بىلەن جازالايدۇ. اﷲ ئۇلارنى خار قىلىدۇ، اﷲ سىلەرنى ئۇلار ئۈستىدىن غالىب قىلىدۇ. (ئىسلام دىنىنى ئۈستۈن قىلىپ، كاپىرلارنى جازالاپ) مۆمىن قەۋمنىڭ كۆڭلىگە شىپالىق (يەنى تەسەللى) بېرىدۇ'),
(1250, 9, 15, 'ھەم ئۇلارنى دىللىرىدىكى ئاچچىقتىن خالاس قىلىدۇ. اﷲ خالىغان ئادەمنى تەۋبىگە مۇۋەپپەق قىلىدۇ. اﷲ ھەممىنى بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1251, 9, 16, 'اﷲ سىلەرنىڭ ئاراڭلاردىكى جىھاد قىلغانلارنى، اﷲ تىن، اﷲ نىڭ پەيغەمبىرىدىن ۋە مۆمىنلەردىن باشقىنى سىرداش قىلىۋالمىغانلارنى بىلمەي (يەنى ئوتتۇرىغا چىقارماي) تۇرۇپ سىلەر ئۆز ھالىمىزغا قويۇپ بېرىلىمىز، (يەنى سىنالمايمىز) دەپ ئويلامسىلەر؟ اﷲ سىلەرنىڭ قىلمىشىڭلاردىن خەۋەرداردۇر'),
(1252, 9, 17, 'مۇشرىكلار ئۆزلىرىنىڭ كۇفرىنى ئىقرار قىلىپ تۇرۇقلۇق (يەنى كاپىرلىق بىلەن اﷲ نىڭ مەسجىدلىرىنى ئاۋات قىلىش بىر - بىرى بىلەن سىغىشالمايدۇ)، (ئۇلارنىڭ) اﷲ نىڭ مەسجىدلىرىنى ئاۋات قىلىشى توغرا ئەمەس، ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرىنىڭ نەتىجىسى بولمايدۇ، ئۇلار دوزاختا قالىدۇ'),
(1253, 9, 18, 'اﷲ نىڭ مەسجىدلىرىنى پەقەت اﷲ قا، ئاخىرەت كۈنىگە ئىمان ئېيتقان، ناماز ئۆتىگەن، زاكات بەرگەن، اﷲ تىن باشقىدىن قورقمىغان كىشىلەرلا ئاۋات قىلىدۇ (يەنى مەسجىدنى رېمونت قىلىش، سەرەمجانلاشتۇرۇش، تازىلاش، مەسجىدتە داۋاملىق ناماز ئۆتەش، قۇرئان تىلاۋەت قىلىش قاتارلىق ئىشلارنى قىلىدۇ)، ئەنە شۇلارنىڭ ھىدايەت تاپقۇچىلاردىن بولۇشى كۆزدە تۇتۇلىدۇ'),
(1254, 9, 19, 'سىلەر ھاجىلارنى سۇ بىلەن تەمىنلەشنى، مەسجىدى ھەرەمنى ئاۋات قىلىشنى اﷲ قا، ئاخىرەت كۈنىگە ئىمان ئېيتقان ۋە اﷲ نىڭ يولىدا جىھاد قىلغانلارنىڭ (ئىمانىغا) ئوخشاش ھېسابلامسىلەر؟ اﷲ نىڭ نەزىرىدە ئۇلار ئوخشاش ئەمەس. اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ'),
(1255, 9, 20, 'ئىمان ئېيتقانلارنىڭ، ھىجرەت قىلغانلارنىڭ ۋە ماللىرى ھەم جانلىرى بىلەن اﷲ نىڭ يولىدا جىھاد قىلغانلارنىڭ دەرىجىسى اﷲ نىڭ دەرگاھىدا ئەڭ كاتتىدۇر؛ ئەنە شۇلار (ساۋاب تېپىش بىلەن) مەقسىتىگە ئېرىشكۈچىلەردۇر'),
(1256, 9, 21, 'پەرۋەردىگارى ئۇلارغا ئۆزىنىڭ رەھمىتى، رازىلىقى ۋە جەننەتلىرى بىلەن خۇش خەۋەر بېرىدۇ، ئۇلار جەننەتلەردە مەڭگۈلۈك نېمەتكە ئېرىشىدۇ'),
(1257, 9, 22, 'ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. شۈبھىسىزكى، اﷲ نىڭ دەرگاھىدا (ئۇلارغا) كاتتا ساۋاب بار'),
(1258, 9, 23, 'ئى مۆمىنلەر! ئەگەر ئاتىلىرىڭلار، قېرىندىشىڭلار ئىماندىن كۇفرىنى ئارتۇق كۆرسە (كۇفرىدا مەھكەم تۇرسا)، ئۇلارنى دوست تۇتماڭلار. ئىچىڭلاردىن كىملەركى، ئۇلارنى دوست تۇتىدىكەن، ئۇلار زالىملاردۇر (يەنى ئۇلارغا ئوخشاش مۇشرىكتۇر، چۈنكى مۇشرىكلىككە رازى بولغان ئادەممۇ مۇشرىك ھېسابلىنىدۇ)'),
(1259, 9, 24, 'ئېيتقىنكى، «ئەگەر سىلەرنىڭ ئاتاڭلار، ئوغۇللىرىڭلار، قېرىندىشىڭلار، خوتۇنلىرىڭلار، ئۇرۇق - تۇغقانلىرىڭلار (ھەمدە ئۇلاردىن باشقىلار)، تاپقان پۇل - ماللىرىڭلار، ئاقماي قېلىشىدىن قورققان تىجارىتىڭلار، ياخشى كۆرۈدىغان ئۆيلىرىڭلار سىلەر ئۈچۈن اﷲ تىن، اﷲ نىڭ پەيغەمبىرىدىن ۋە اﷲ نىڭ يولىدا جىھاد قىلىشتىنمۇ سۆيۈملۈك بولسا (يەنى شۇلار بىلەن بولۇپ كېتىپ، اﷲ نىڭ پەيغەمبىرىگە ياردەم بەرمىسەڭلار)، ئۇ ھالدا سىلەر تاكى اﷲ نىڭ ئەمر (يەنى مەككىنى پەتھى قىلىش ئىزنى) كەلگۈچە كۈتۈڭلار، اﷲ پاسىق قەۋمنى (يەنى اﷲ نىڭ دىنىنىڭ چەك - چېگرىسىدىن چىقىپ كەتكۈچىلەرنى) ھىدايەت قىلمايدۇ»'),
(1260, 9, 25, 'اﷲ سىلەرگە نۇرغۇن جەڭ مەيدانلىرىدا ۋە ھۈنەين كۈنىدە (يەنى جېڭىدە) ھەقىقەتەن ياردەم بەردى. ئەينى ۋاقىتتا سانىڭلارنىڭ كۆپلۈكىدىن خۇشاللىنىپ كەتتىڭلار (يەنى بۈگۈن بىزنىڭ سانىمىز كۆپ، مەغلۇپ بولمايمىز دېدىڭلار، بۇ چاغدا سىلەرنىڭ سانىڭلار 12 مىڭ، دۈشمىنىڭلارنىڭ سانى 4000 ئىدى)، سانىڭلارنىڭ كۆپلۈكى سىلەرگە قىلچە ئەسقاتمىدى. (قاتتىق قورققىنىڭلاردىن) كەڭ زېمىن سىلەرگە تار تۇيۇلدى. ئاندىن (مەغلۇپ بولۇپ، رەسۇلۇللاھنى ئازغىنا مۆمىنلەر بىلەن تاشلاپ) ئارقاڭلارغا قاراپ قاچتىڭلار'),
(1261, 9, 26, 'ئاندىن پەيغەمبىرىگە ۋە مۆمىنلەرگە (مەرھەمەت قىلىپ) خاتىرجەملىك بېغىشلىدى، سىلەرگە قوشۇنلارنى (يەنى سىلەرگە ياردەمگە پەرىشتىلەرنى) چۈشۈردى، ئۇلارنى سىلەر كۆرمىدىڭلار (شۇنىڭ بىلەن سىلەر غەلىبە قىلدىڭلار)، اﷲ كاپىرلارنى (ئۆلتۈرۈش ۋە ئەسىر ئېلىنىش بىلەن) ئازابلىدى، كاپىرلارنىڭ جازاسى ئەنە شۇ'),
(1262, 9, 27, 'شۇنىڭدىن كېيىن، اﷲ خالىغان ئادەمگە تەۋبىنى نېسىپ قىلىدۇ (يەنى ئىسلام دىنىغا كىرىشكە مۇۋەپپەق قىلىدۇ). اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(1263, 9, 28, 'ئى مۆمىنلەر! مۇشرىكلار (اﷲ نى ئىنكار قىلغانلىقلىرى ئۈچۈن) پەقەت نىجىستۇر، مۇشۇ يىلدىن (يەنى ھىجرىيىنىڭ 9 - يىلىدىن) كېيىن ئۇلار مەسجىدى ھەرەمغا (ھەرەمگە) يېقىنلاشمىسۇن (يەنى ھەج قىلمىسۇن، ئۆمرە قىلمىسۇن). ئەگەر سىلەر (ئى مۆمىنلەر! مۇشرىكلارنىڭ ھەرەمگە كىرىشتىن مەنئى قىلىنىشى بىلەن) پېقىرلىقتىن قورقساڭلار، اﷲ خالىسا ئۆز پەزلى بىلەن سىلەرنى باي قىلىدۇ، شۈبھىسىزكى، اﷲ (سىلەرگە پايدىلىق ئىشلارنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1264, 9, 29, 'سىلەر ئەھلى كىتابتىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشەنمەيدىغانلار (يەھۇدىيلار ئۇزەيرىنى اﷲ نىڭ ئوغلى، ناسارالار ئىسانى اﷲ نىڭ ئوغلى دەپ ئېتىقاد قىلغانلىقلىرى ئۈچۈن، ئۇلار اﷲ قا ۋە ئاخىرەت كۈنىگە ئېتىقاد قىلىمىز دېگەن بىلەنمۇ ئېتىقاد قىلمىغاننىڭ ئورنىدىدۇر) بىلەن، اﷲ (ئۆزىنىڭ كىتابىدا) ۋە ئۇنىڭ پەيغەمبىرى (سۈننىتىدە) ھارام قىلغان نەرسىلەرنى ھارام بىلمەيدىغانلار ۋە ھەق دىن (يەنى ئىسلام دىنى) غا ئېتىقاد قىلمايدىغانلار بىلەن، تاكى ئۇلار سىلەرگە بويسۇنۇپ خار ھالدا جىزىيە تۆلىگەنگە قەدەر، ئۇرۇش قىلىڭلار'),
(1265, 9, 30, 'يەھۇدىيلار: «ئۈزەير اﷲ نىڭ ئوغلىدۇر» دېدى، ناسارالار: «مەسىھ (يەنى ئىسا) اﷲ نىڭ ئوغلىدۇر» دېدى، بۇ، ئۇلارنىڭ ئاغزىدىكى (دەلىلسىز) سۆزىدۇر، (ئۇلارنىڭ سۆزلىرى) ئىلگىرىكى كاپىرلارنىڭ (يەنى مۇشرىكلارنىڭ: «پەرىشتىلەر اﷲ نىڭ قىزلىرىدۇر» دېگەن) سۆزلىرىگە ئوخشايدۇ. اﷲ ئۇلارغا لەنەت قىلسۇنكى، (اﷲ نىڭ بالىسى يوقلۇقىغا روشەن دەلىل تۇرسا، ھەقىقەتتىن باتىلغا بۇرۇلۇپ) ئۇلار قانداقمۇ (اﷲ نىڭ بالىسى بار دەپ) اﷲ قا يالغان چاپلايدۇ'),
(1266, 9, 31, 'ئۇلار ئۆزلىرىنىڭ ھىبرىلىرىنى، راھىبلىرىنى (يەھۇدىيلار بىلەن ناسارالارنىڭ دىنىي باشلىقلىرىنى، ئۆلىمالىرىنى) ۋە مەريەم ئوغلى مەسىھنى مەبۇد قىلىۋالدى. (ھالبۇكى) ئۇلار (پەيغەمبەرلەر ئارقىلىق) پەقەت بىر ئىلاھقا ئىبادەت قىلىشقا بۇيرۇلغان ئىدى، ئۇنىڭدىن باشقا (يەنى ئالەملەرنىڭ پەرۋەردىگارى اﷲ تىن باشقا) ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەنلىكلىرىدىن پاكتۇر'),
(1267, 9, 32, 'ئۇلار (يەنى مۇشرىكلار، يەھۇدىيلار ۋە ناسارالار) اﷲ نىڭ نۇرىنى (يەنى ئىسلام دىنىنى) ئېغىزلىرى (يەنى يالغان - ياۋىداق بوھتانلىرى) بىلەن ئۆچۈرمەكچى بولىدۇ، اﷲ ئۆز نۇرىنى ئۈستۈن قىلماي قالمايدۇ، كاپىرلار (بۇنى) يامان كۆرگەن تەقدىردىمۇ'),
(1268, 9, 33, 'اﷲ ھەق دىن (ئىسلام) نى بارلىق دىنلاردىن ئۈستۈن قىلىش ئۈچۈن، ئۆزىنىڭ پەيغەمبىرىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى تولۇق) ھىدايەت ۋە ھەق دىن بىلەن ئەۋەتتى، مۇشرىكلار ئۇنىڭ (ئۈستۈن بولۇشىنى) يامان كۆرگەن تەقدىردىمۇ'),
(1269, 9, 34, 'ئى مۆمىنلەر! ھىبرىلەر ۋە راھىبلەردىن (يەھۇدىي ۋە ناسارا ئۆلىمالىرىدىن) نۇرغۇنلىرى كىشىلەرنىڭ پۇل - ماللىرىنى ھەقىقەتەن ھارام يەيدۇ، (كىشىلەرنى) اﷲ نىڭ دىنىغا كىرىشتىن توسىدۇ، ئالتۇن - كۈمۈش يىغىپ، ئۇنى اﷲ نىڭ يولىدا سەرپ قىلمايدىغانلارغا (دوزاختا بولىدىغان) قاتتىق ئازاب بىلەن بېشارەت بەرگىن'),
(1270, 9, 35, 'ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ئۇ ئالتۇن - كۈمۈشلەر جەھەننەمنىڭ ئوتىدا قىزىتىلىپ، ئۇنىڭ بىلەن ئۇلارنىڭ پېشانىلىرى، يانلىرى ۋە دۈمبىلىرى داغلىنىدۇ. ئۇلارغا: «بۇ سىلەرنىڭ ئۆزۈڭلار ئۈچۈن يىغقان ئالتۇن - كۈمۈشۈڭلار (سىلەر بۇ دۇنيايىڭلاردىكى اﷲ نىڭ ھەققىنى ئادا قىلمىدىڭلار). يىغقان ئالتۇن - كۈمۈشۈڭلارنىڭ ۋابالىنى تېتىڭلار» دېيىلىدۇ'),
(1271, 9, 36, 'شۈبھىسىزكى، ئاسمان ۋە زېمىن يارىتىلغاندىن تارتىپ (قەمەرى) ئايلارنىڭ سانى اﷲ نىڭ دەرگاھىدا (يەنى لەۋھۇلمەھپۇزدا) 12 دۇر، ئۇلاردىن تۆتى (يەنى زۇلقەئدە، زۇلھەججە، مۇھەررەم، رەجەپ) ئۇرۇش قىلىش ھارام قىلىنغان ئايلاردۇر، بۇ توغرا دىندۇر (يەنى مەزكۇر تۆت ئايدا ئۇرۇشنىڭ ھارام قىلىنىشى اﷲ نىڭ توغرا دىنىدۇر)، بۇ ئايلاردا (ئۇلارنىڭ ھۆرمىتىنى ساقلىماسلىق بىلەن، اﷲ چەكلىگەن گۇناھ ئىشلارنى قىلىش بىلەن) ئۆزۈڭلارغا زىيان سالماڭلار، مۇشرىكلار سىلەرگە بىرلىكتە ھۇجۇم قىلغاندەك، سىلەرمۇ ئۇلارغا قارشى بىرلىكتە ئۇرۇش قىلىڭلار. بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر (يەنى اﷲ نىڭ ئەمرىنى بەجا كەلتۈرۈش، مەنئى قىلغان ئىشلىرىدىن چەكلىنىش بىلەن اﷲ تىن قورققۇچىلارغا اﷲ نىڭ ياردەم بېرىدىغانلىقىغا ئىشەنچىدە بولۇڭلار)'),
(1272, 9, 37, 'ئاينى ئارقىغا سۈرۈش (يەنى ئۇرۇش ھارام قىلىنغان ئاينى باشقا بىر ئايغا ئۆزگەرتىپ، يىلنىڭ 12 ئېيىنى 13 ئايغا كۆپەيتىش) كۇفرىنى زىيادە قىلىشتۇر، بۇنىڭ بىلەن كاپىرلار ئازدۇرۇلىدۇ؛ ئۇلار اﷲ ھارام قىلغان ئايلارنىڭ سانىغا مۇۋاپىقلىشىش ئۈچۈن، ئۇنى بىر يىل ھالال قىلسا، ئىككىنچى يىل ھارام قىلىدۇ، شۇنىڭ بىلەن ئۇلار اﷲ ھارام قىلغان ئاينى ھالال قىلىدۇ، ئۇلارغا ئۆزلىرىنىڭ يامان ئەمەللىرى چىرايلىق كۆرسىتىلدى. اﷲ كاپىر قەۋمنى ھىدايەت قىلمايدۇ'),
(1273, 9, 38, 'ئى مۆمىنلەر! سىلەرگە اﷲ نىڭ يولىدا جىھاد قىلىشقا چىقىڭلار دېيىلسە، نېمىشقا يۇرتۇڭلاردىن ئايرىلغىڭلار كەلمەيدۇ؟ دۇنيا ھاياتىنى ئاخىرەتتىكى (بەخت) بىلەن تېگىشىشكە رازى بولدۇڭلارمۇ؟ دۇنيا ھاياتىدىن بەھرىمەن بولۇش ئاخىرەتنىڭ نېمەتلىرى بىلەن سېلىشتۇرغاندا، ئەرزىمەس نەرسىدۇر'),
(1274, 9, 39, 'ئەگەر سىلەر جىھادقا چىقمىساڭلار، اﷲ سىلەرگە قاتتىق ئازاب قىلىدۇ. (سىلەرنى ھالاك قىلىپ) ئورنۇڭلارغا سىلەردىن باشقا قەۋمنى كەلتۈرىدۇ، سىلەر اﷲ قا قىلچە زىيان يەتكۈزەلمەيسىلەر، اﷲ ھەر نەرسىگە قادىردۇر'),
(1275, 9, 40, 'ئەگەر سىلەر اﷲ نىڭ پەيغەمبىرىگە ياردەم قىلمىساڭلار (اﷲ ياردەم قىلىدۇ)، اﷲ ھەقىقەتەن ئۇنىڭغا ياردەم قىلغان ئىدى. ئۆز ۋاقتىدا كاپىرلار ئۇنى (مەككىدىن) ھەيدەپ چىقارغان ئىدى. ئۇنىڭغا پەقەت بىر كىشى (يەنى ئەبۇبەكرى سىددىق) ھەمراھ ئىدى. ئەينى زاماندا ئۇ ئىككىسى غاردا ئىدى. (رەسۇلۇللاھ) ھەمراھىغا: «زەم قىلمىغىن، اﷲ ھەقىقەتەن بىز بىلەن بىللە» دەيتتى، اﷲ ئۇنىڭغا (يەنى پەيغەمبىرىگە) خاتىرجەملىك بېغىشلىدى، ئۇنىڭغا (پەرىشتىلەردىن بولغان) قوشۇنلار بىلەن مەدەت بەردى، ئۇلارنى سىلەر كۆرمىدىڭلار، كاپىرلارنىڭ سۆزىنى (يەنى شېرىك كەلىمىسىنى) پەس قىلدى، اﷲ نىڭ سۆزى (يەنى كەلىمە تەۋھىد) ئۈستۈندۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1276, 9, 41, '(ئى مۆمىنلەر جامائەسى!) سىلەر يىنىك بولغان ياكى ئېغىر بولغان ھالەتتە (يەنى مەيلى ياش - قېرى، پىيادە، ئۇلاغلىق بولۇڭلار، ئوڭۇشلۇق ۋە قىيىن شارائىتتا بولۇڭلار، ئىختىيارىي ۋە ئىختىيارسىز بولۇڭلار، ھەممە ئەھۋالدا) جىھادقا چىقىڭلار، اﷲ نىڭ يولىدا مېلىڭلار بىلەن، جېنىڭلار بىلەن جىھاد قىلىڭلار، ئەگەر بىلسەڭلار مۇنداق قىلىش سىلەر ئۈچۈن ياخشىدۇر'),
(1277, 9, 42, 'ئەگەر (ئۇلارنىڭ دەۋەت قىلىنغىنى) ئاسان قولغا كېلىدىغان غەنىيمەت ۋە (يىراق ئەمەس) ئوتتۇراھال سەپەر بولىدىغان بولسا، ئۇلار چوقۇم ساڭا ئەگىشىپ (غەنىيمەت ئېلىش مەقسىتىدە) چىقاتتى. لېكىن، بۇ ئارىلىق ئۇلارغا يىراق بىلىندى. ئۇلار بىلەن قەسەم ئىچىپ: «ئەگەر چىقىشقا قادىر بولالىغان بولساق، ئەلۋەتتە سىلەر بىلەن بىللە چىقاتتۇق» دەيدۇ، ئۇلار (يالغان قەسەملىرى تۈپەيلىدىن) ئۆزلىرىنى ئۆزلىرى ھالاك قىلىدۇ، اﷲ بىلىدۇكى، ئۇلار شەك - شۈبھىسىز يالغانچىلاردۇر'),
(1278, 9, 43, '(ئى مۇھەممەد!) اﷲ سېنى كەچۈردى، (ئۆزرىسىدە) راستچىللار بىلەن يالغانچىلار (ساڭا ئېنىق بولمىغۇچە نېمىشقا ئۇلارغا چىقماسلىققا) رۇخسەت بەردىڭ'),
(1279, 9, 44, '(ئى مۇھەممەد!) اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغانلار سەندىن ماللىرى بىلەن ۋە جانلىرى بىلەن جىھاد قىلىشقا رۇخسەت سورىمايدۇ. اﷲ تەقۋادارلارنى ئوبدان بىلىدۇ'),
(1280, 9, 45, '(ئى مۇھەممەد) سەندىن پەقەت اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشەنمەيدىغانلار، دىللىرىدا (اﷲ نىڭ بىرلىكىگە ۋە سېنىڭ پەيغەمبەرلىكىڭگە) گۇمانى بارلار رۇخسەت سورايدۇ، ئۇلار ئۆز گۇمانىدا تېڭىرقاپ يۈرىدۇ'),
(1281, 9, 46, 'ئەگەر ئۇلارنىڭ جىھادقا چىقىش نىيىتى بولسا، ئەلۋەتتە ئۇنىڭغا تەييارلىق قىلاتتى، لېكىن اﷲ ئۇلارنىڭ چىقىشىنى ياقتۇرمىدى، (ئۇلارنىڭ دىللىرىغا ھۇرۇنلۇقنى سېلىپ) ئۇلارنىڭ ئىرادىسىنى سۇندۇردى. (ئۇلارغا) «ئۆيلىرىدە قېلىپ قالغۇچىلار (يەنى ئاياللار، كىچىك بالىلار، ئاجىزلار) بىلەن قېلىڭلار» دېيىلدى'),
(1282, 9, 47, 'ئەگەر ئۇلار سىلەر بىلەن بىرلىكتە چىققان بولسا، ئاراڭلاردا پەقەت پىتنە - پاساتنى كۆپەيتەتتى، ئاراڭلارغا بۆلگۈنچىلىك سېلىش ئۈچۈن چوقۇم سۇخەنچىلىك بىلەن شۇغۇللىناتتى،. ئاراڭلاردا ئۇلار ئۈچۈن تىڭ - تىڭلايدىغانلار بار، اﷲ زالىملارنى (يەنى مۇناپىقلارنىڭ ئىچى - تېشىنى) ئوبدان بىلگۈچىدۇر'),
(1283, 9, 48, 'ئىلگىرى ئۇلار تاكى اﷲ نىڭ ياردىمى كەلگەنگە، اﷲ نىڭ دىنى ئۈستۈنلۈك قازانغانغا قەدەر، ھەقىقەتەن، بۆلگۈنچىلىك سېلىشقا ئورۇنغان، ساڭا ھىيلە - مىكىرلەرنى ئىشلەتكەن ئىدى، ھالبۇكى، ئۇلار بۇنداق بولۇشىنى (يەنى دىننىڭ ئۈستۈنلۈك قازىنىشىنى) يامان كۆرگۈچىلەردۇر'),
(1284, 9, 49, 'ئۇلارنىڭ ئارىسىدا: «ماڭا (يەنى مېنىڭ قېلىشىمغا) رۇخسەت قىلغىن، مېنى بالاغا گىرىپتار قىلمىغىن (يەنى رۇمنىڭ ئاياللىرى بىلەن گۇناھ قىلىپ سېلىشىمغا سەۋەبچى بولمىغىن)» دېگۈچىلەرمۇ بار. ئەمەلدە ئۇلار (مۇناپىقلىقتىن ئىبارەت چوڭ) بالاغا گىرىپتار بولدى. شۈبھىسىزكى، جەھەننەم كاپىرلارنى ئوراپ تۇرغۇچىدۇر'),
(1285, 9, 50, 'ئەگەر سەن بىرەر ياخشىلىققا ئېرىشسەڭ، ئۇلارنىڭ كۆڭلى يېرىم بولىدۇ؛ ئەگەر ساڭا بىرەر مۇسىبەت كەلسە: «ئىشنىڭ ئالدىنى ئالغان ئىكەنمىز» دېيىشىپ خۇشال قايتىشىدۇ'),
(1286, 9, 51, 'ئېيتقىنكى، «بىزگە پەقەت اﷲ نىڭ تەقدىر قىلغان نەرسىسى يېتىدۇ، ئۇ بىزنىڭ مەدەتكارىمىزدۇر. مۆمىنلەر اﷲ قا تەۋەككۈل قىلسۇن (يەنى مۆمىنلەر ھەممە ئىشنى اﷲ قا تاپشۇرسۇن، ئۇندىن باشقا ھېچ ئەھەدىگە يۆلەنمىسۇن)»'),
(1287, 9, 52, 'ئېيتقىنكى، «سىلەر پەقەت بىزنىڭ (غەلىبە قىلىش ياكى شېھىت بولۇشتىن ئىبارەت) ئىككى خىل ياخشى ئاقىۋەتنىڭ بىرىگە ئېرىشىشىمىزنى كۈتۈۋاتىسىلەر، بىزمۇ اﷲ نىڭ سىلەرگە ئۆز دەرگاھىدىن ئازاب چۈشۈرۈشنى ياكى بىزنىڭ قولىمىز ئارقىلىق سىلەرنى جازالىشىنى كۈتۈۋاتىمىز، سىلەرمۇ (ئۆزۈڭلارغا كەلگەننى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە (ئۆزىمىزگە كەلگەننى) كۈتەيلى»'),
(1288, 9, 53, 'ئېيتقىنكى، «(ئى مۇناپىقلار جامائەسى!) سىلەر ئىختىيارىي ياكى مەجبۇرىي يوسۇندا (قانچىلىك پۇل - مال سەرپ قىلساڭلارمۇ) ھەرگىز قوبۇل قىلىنمايدۇ، چۈنكى سىلەر اﷲ نىڭ ئىتائىتىدىن چىققان قەۋم بولدۇڭلار»'),
(1289, 9, 54, 'ئۇلارنىڭ بەرگەن نەپىقىلىرى شۇنىڭ ئۈچۈن قوبۇل بولمايدۇكى، ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلىدۇ، نامازنى خۇش ياقماسلىق بىلەن ئوقۇيدۇ، (پۇل - مېلىنى) رازىلىق بىلەن بەرمەيدۇ'),
(1290, 9, 55, 'ئۇلارنىڭ مال - مۈلكى ۋە ئەۋلادى سېنى ئەجەبلەندۈرمىسۇن، اﷲ ئۇلارنى بۇ دۇنيادا شۇ نەرسىلەر بىلەن ئازابلاشنى ۋە ئۇلارنىڭ كاپىر پېتى جان ئۈزۈشنى ئىرادە قىلىدۇ'),
(1291, 9, 56, 'ئۇلار: «بىز چوقۇم سىلەر (يەنى سىلەرگە ئوخشاش مۇسۇلمانلار) دىن» دەپ، اﷲ بىلەن قەسەم ئىچىدۇ. ئۇلار سىلەردىن ئەمەس، لېكىن ئۇلار (زىيان - زەخمەتكە ئۇچراشتىن قورقۇپ، ئاغزىدا مۇسۇلمان بولغان) قورقۇنچاق قەۋمدۇر'),
(1292, 9, 57, 'ئەگەر ئۇلار بىرەر قورغان ياكى غار ۋە ياكى گەمىنى تاپسا ئىدى، ئۇلار شۇ تەرەپكە قاراپ ئەلۋەتتە يۈگۈرۈشكەن بولاتتى'),
(1293, 9, 58, '(ئى مۇھەممەد!) ئۇلارنىڭ بەزىسى سېنىڭ سەدىقە (ۋە غەنىيمەت) لەرنى تەقسىم قىلىشىڭنى ئەيىبلەيدۇ، ئۇلارغا ئۇنىڭدىن بېرىلسە خۇش بولىدۇ، بېرىلمىسە خاپا بولۇشىدۇ'),
(1294, 9, 59, 'ئەگەر ئۇلار اﷲ نىڭ ۋە اﷲ نىڭ پەيغەمبىرىنىڭ ئۇلارغا بەرگىنىگە رازى بولسا ۋە: «اﷲ بىزگە. كۆرىدۇ قىلىدۇ، اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىزگە ئۆز پەزلىدىن ئاتا قىلىدۇ، بىز ھەقىقەتەن اﷲ تىن (اﷲ نىڭ پەزلىنى، ئېھسانىنى) ئۆتۈنۈپ سورىغۇچىلارمىز» دېسە، ئەلۋەتتە، ئۇلار ئۈچۈن ياخشى بولاتتى'),
(1295, 9, 60, 'زاكات پەقەت پېقىرلارغا، مىسكىنلەرگە، زاكات خادىملىرىغا، دىللىرىنى ئىسلامغا مايىل قىلىش كۆزدە تۇتۇلغانلارغا، قۇللارنى ئازاد قىلىشقا، قەرزدارلارغا، اﷲ نىڭ يولىغا، ئىبن سەبىللەرگە بېرىلىدۇ، بۇ اﷲ نىڭ بەلگىلىمىسىدۇر، اﷲ (بەندىلىرىنىڭ مەنپەئەتنى) ئوبدان بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1296, 9, 61, 'ئۇلارنىڭ بەزىلىرى پەيغەمبەرگە (سۆزى ۋە ھەرىكىتى ئارقىلىق) ئەزىيەت يەتكۈزىدۇ ۋە (پەيغەمبەرنى) «ئاڭلىغان نەرسىسىنىڭ ھەممىسىگە ئىشىنىدۇ» دەيدۇ. ئېيتقىنكى، «(دۇرۇس، ئۇ) سىلەرگە پايدىلىق سۆزلەرنى ئاڭلاپ ئىشىنىدۇ، اﷲ نىڭ سۆزلىرىگە ئىشىنىدۇ، مۆمىنلەرنىڭ سۆزىگە (ئۇلارنىڭ ئىخلاسىنى بىلگەنلىكتىن) ئىشىنىدۇ، ئۇ سىلەردىن ئىمان ئېيتقانلار ئۈچۈن رەھمەتتۇر» اﷲ نىڭ پەيغەمبىرىگە ئەزىيەت يەتكۈزىدىغانلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(1297, 9, 62, 'ئۇلار سىلەرنى رازى قىلىش ئۈچۈن (پەيغەمبەرگە تېگىدىغان گەپنى قىلمىدۇق دەپ) اﷲ بىلەن قەسەم قىلىدۇ، ئەگەر ئۇلار راستلا مۆمىن بولىدىغان بولسا، اﷲ نى ۋە ئۇنىڭ پەيغەمبىرىنى رازى قىلىشلىرى كېرەك ئىدى'),
(1298, 9, 63, '(بۇ مۇناپىقلار) اﷲ قا ۋە ئۇنى پەيغەمبىرىگە كىمكى قارشىلىق كۆرسىتىدىكەن، ئۇنىڭ دوزاخقا كىرىدىغانلىقىنى، دوزاختا مەڭگۈ قالىدىغانلىقىنى بىلمەمدۇ؟ ئەنە شۇ چوڭ رەسۋالىقتۇر'),
(1299, 9, 64, 'مۇناپىقلار ئۆزلىرى توغرىسىدا بىرەر سۈرە نازىل بولۇپ، ئۇلارنىڭ دىللىرىدىكىنى (يەنى نىفاقنى) مۆمىنلەرگە خەۋەر بېرىشتىن قورقىدۇ. ئېيتقىنكى، «سىلەر (اﷲ نىڭ دىنىنى خالىغىنىڭلارچە) مەسخىرە قىلىڭلار، سىلەر (پاش بولۇشتىن) قورقىدىغان نەرسىنى (يەنى مۇناپىقلىقنى) اﷲ چوقۇم ئاشكارا قىلىدۇ»'),
(1300, 9, 65, '(ئى مۇھەممەد!) ئەگەر سەن ئۇلارنىڭ (مەسخىرە قىلغانلىقىنى) سورىساڭ، ئۇلار: «بىز (راست ئەمەس)، پەقەت ئىچ پۇشۇقى قىلىپ ئوينىشىپ دەپ قويدۇق» دەيدۇ. (بۇ مۇناپىقلارغا) «سىلەر اﷲ نىڭ دىنىنى، اﷲ نىڭ ئايەتلىرىنى (يەنى كىتابىنى) ۋە اﷲ نىڭ پەيغەمبىرىنى مەسخىرە قىلدىڭلارمۇ؟» دېگىن'),
(1301, 9, 66, 'سىلەر (يالغان قەسەم ئىچىپ) ئۆزرە ئېيتماڭلار، سىلەر ئىمان ئېيتقىنىڭلاردىن كېيىن، (پەيغەمبەرنى مەسخىرە قىلىش بىلەن) كاپىر بولدۇڭلار، سىلەردىن بىر گۇرۇھنى (راست تەۋبە قىلغانلىقلىرى ئۈچۈن) ئەپۇ قىلساق، يەنە بىر گۇرۇھنى گۇناھكار بولغانلىقلىرى (يەنى مۇناپىقلىق بىلەن گۇناھنى داۋاملاشتۇرغانلىقلىرى) ئۈچۈن جازالايمىز'),
(1302, 9, 67, 'مۇناپىق ئەرلەر بىلەن مۇناپىق ئاياللار (مۇناپىقلىقتا ۋە ئىماندىن يىراق بولۇشتا) بىر - بىرىگە ئوخشايدۇ، ئۇلار يامانلىققا بۇيرۇيدۇ، ياخشىلىقتىن توسىدۇ، ئۇلار قوللىرىنى يۇمۇۋالىدۇ (يەنى بېخىللىق قىلىپ سەدىقە بەرمەيدۇ). ئۇلار اﷲ نى ئۇنتۇدى (يەنى اﷲ نىڭ تائىتىنى تەرك ئەتتى)، اﷲ مۇ ئۇلارنى ئۇنتۇدى (يەنى اﷲ ئۇلارنى ئۆزىنىڭ پەزلىدىن ۋە رەھمىتىدىن مەھرۇم قىلدى). شۈبھىسىزكى، مۇناپىقلار پاسىقلاردۇر'),
(1303, 9, 68, 'اﷲ مۇناپىق ئەرلەرگە، مۇناپىق ئاياللار ۋە كۇففارلارغا دوزاخنى ۋەدە قىلدى، ئۇلار دوزاختا مەڭگۈ قالىدۇ. دوزاخ ئۇلارنى (ئازابلاشقا) يېتەرلىكتۇر، اﷲ ئۇلارغا لەنەت قىلدى، ئۇلار مەڭگۈلۈك ئازابقا دۇچار بولىدۇ'),
(1304, 9, 69, '(ئى مۇناپىقلار، سىلەرنىڭ ھالىڭلار) سىلەردىن بۇرۇن ئۆتكەن (ھالاك بولغان ئۈممەتلەرنىڭ ھالىغا) ئوخشايدۇ، ئۇلار كۈچ - قۇۋۋەتتە سىلەردىن كۈچلۈك، مال - مۈلكى ۋە ئەۋلادى سىلەرنىڭكىگە قارىغاندا كۆپ ئىدى. سىلەردىن بۇرۇن ئۆتكەنلەر (دۇنيانىڭ لەززەتلىرى جەھەتتىكى) نېسىۋىسىدىن بەھرىمەن بولغاندەك، سىلەرمۇ (دۇنيانىڭ لەززەتلىرى جەھەتتىكى) نېسىۋەڭلاردىن بەھرىمەن بولدۇڭلار؛ ئۇلار باتىلغا چۆمگەندەك، سىلەرمۇ باتىلغا چۆمدۈڭلار؛ ئەنە شۇلارنىڭ ياخشى ئەمەللىرى دۇنيا ۋە ئاخىرەتتە ئېتىبارسىزدۇر، ئەنە شۇلار زىيان تارتقۇچىلاردۇر'),
(1305, 9, 70, 'ئۇلارغا ئۇلاردىن بۇرۇن ئۆتكەنلەرنىڭ، نۇھ، ئاد ۋە سەمۇد قەۋملىرىنىڭ، ئىبراھىم قەۋمىنىڭ، مەديەن ئاھالىسىنىڭ، كۆمتۈرۈۋېتىلگەن قىشلاقلارنىڭ (يەنى لۇت ئەلەيھىسسالام قەۋمى قىشلاقلىرىنىڭ) خەۋەرلىرى كەلمىدىمۇ؟ ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەر ئېلىپ كەلدى (بۇ مۆجىزىلەرنى ئۇلار ئىنكار قىلدى). اﷲ ئۇلارغا زۇلۇم قىلغىنى يوق، لېكىن ئۇلار (كاپىر بولۇش ۋە گۇناھ قىلىش بىلەن) ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى'),
(1306, 9, 71, 'مۆمىن ئەرلەر، مۆمىن ئاياللار بىر - بىرى بىلەن دوستتۇر، ئۇلار (كىشىلەرنى) ياخشى ئىشلارنى قىلىشقا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، نامازنى (تولۇق) ئادا قىلىدۇ، زاكات بېرىدۇ، اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە ئىتائەت قىلىدۇ، ئەنە شۇلارغا اﷲ رەھىم قىلىدۇ، اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1307, 9, 72, 'اﷲ مۆمىن ئەرلەر ۋە مۆمىن ئاياللارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى ۋەدە قىلدى. ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ، ھەمىشە تۇرىدىغان جەننەتلەردە گۈزەل جايلارنى ۋەدە قىلدى. (ئۇلار ئېرىشىدىغان) اﷲ نىڭ رازىلىقى (جەننەت نېمەتلىرىنىڭ ھەممىسىدىن) كاتتىدۇر، بۇ چوڭ بەختتۇر'),
(1308, 9, 73, 'ئى پەيغەمبەر! كاپىرلارغا ۋە مۇناپىقلارغا قارشى جىھاد قىلغىن، ئۇلارغا (قارشى غازات قىلىش ۋە قورقۇنچ سېلىش بىلەن) قاتتىق مۇئامىلە قىلغىن، ئۇلارنىڭ جايى جەھەننەمدۇر، ئۇ نېمىدېگەن يامان جاي!'),
(1309, 9, 74, 'ئۇلار (يەنى مۇناپىقلار) ھېچنېمە دېمىگەنلىكى توغرىسىدا اﷲ بىلەن قەسەم قىلىدۇ، ئۇلار ھەقىقەتەن كۇفرى سۆزىنى ئېيتتى، ئىسلام دىنىغا كىرگەنلىكىنى ئىپادىلىگەندىن كېيىن كاپىر بولدى، ئۇلار ئىشقا ئاشمايدىغان ئىشنى ئىشقا ئاشۇرۇشقا ئورۇندى (يەنى پەيغەمبەر ئەلەيھىسسالامنى ئۆلتۈرۈشنى قەستلىدى)، ئۇلارنىڭ (پەيغەمبەر ئەلەيھىسسالامنى) يامان كۆرۈشنى پەقەت اﷲ نىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئۇلارنى ئۆز پەزلىدىن باي قىلغانلىقى ئۈچۈندۇر، ئەگەر ئۇلار (مۇناپىقلىقتىن) تەۋبە قىلسا، ئۆزلىرىگە ياخشىدۇر، ئەگەر ئۇلار (ئىماندىن داۋاملىق) يۈز ئۆرۈسە، اﷲ ئۇلارنى دۇنيا ۋە ئاخىرەتتە قاتتىق ئازاب بىلەن جازالايدۇ، يەر يۈزىدە ئۇلارغا (ئۇلارنى ئازابتىن قۇتۇلدۇرىدىغان) بىرەر دوست ۋە بىرەر مەدەتكار تېپىلمايدۇ'),
(1310, 9, 75, 'ئۇلارنىڭ ئىچىدىكى بەزىلەر: «ئەگەر اﷲ بىزگە ئۆز پەزلىدىن ئاتا قىلسا (يەنى رىزقىمىزنى كەڭ قىلسا) ئەلۋەتتە سەدىقە بېرەتتۇق، ئەلۋەتتە ياخشىلاردىن بولاتتۇق» دەپ اﷲ قا ئەھدە قىلدى'),
(1311, 9, 76, 'اﷲ ئۇلارغا ئۆز پەزلىدىن ئاتا قىلغان (يەنى باي قىلغان) چاغدا، بېخللىق قىلىشتى (يەنى ئەھدىنى بۇزۇپ، اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە ئىتائەت قىلىشتىن يۈز ئۆرۈشتى)'),
(1312, 9, 77, 'ئۇلار (سەدىقە قىلىمىز، ياخشىلاردىن بولىمىز دەپ) اﷲ قا بەرگەن ئەھدىسىگە خىلاپلىق قىلغانلىقلىرى، (ئىمانىنى، ئېھسانىنى، دەۋا قىلىشتىكى) يالغانچىلىقلىرى ئۈچۈن، اﷲ قا مۇلاقات بولىدىغان كۈن (يەنى قىيامەت كۈنى) گە قەدەر مۇناپىقلىقنىڭ دىللىرىدا (يىلتىز تارتىپ) تۇرۇشىنى اﷲ ئۇلارنىڭ (ئىشىنىڭ) ئاقىۋىتى قىلدى'),
(1313, 9, 78, 'اﷲ نىڭ ئۇلارنىڭ ئىچكى سىرلىرىنى، (ئۆزئارا قىلىشقان) يوشۇرۇن سۆزلىرىنى بىلىپ تۇرىدىغانلىقىنى، پۈتۈن غەيبلەرنى چوڭقۇر بىلىدىغانلىقىنى ئۇلار ئۇقمامدۇ؟'),
(1314, 9, 79, '(مۇناپىقلار) مۆمىنلەرنىڭ ئىچىدىكى مەردلىك بىلەن (كۆپ) سەدىقە قىلغۇچىلارنى، تاقىتىنىڭ يېتىشىچە (يەنى كەمبەغەل بولغانلىقتىن ئاز) سەدىقە قىلغۇچىلارنى ئەيىبلەيدۇ، ئۇلارنى مەسخىرە قىلىدۇ، اﷲ ئۇلارنى مەسخىرە قىلغانلىقلىرى ئۈچۈن جازالايدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(1315, 9, 80, '(ئى مۇھەممەد!) ئۇلار (يەنى مۇناپىقلار) ئۈچۈن مەيلى مەغپىرەت تەلەپ قىل، مەيلى مەغپىرەت تەلەپ قىلما (بەرىبىر ئوخشاش)، ئۇلار ئۈچۈن 70 قېتىم مەغپىرەت قىلمايدۇ. چۈنكى ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلدى، اﷲ پاسىق قەۋمنى ھىدايەت قىلمايدۇ'),
(1316, 9, 81, 'رەسۇلۇللاھقا خىلاپلىق قىلىپ (تەبۈك غازىتىغا چىقماي) قېلىپ قالغانلار (يەنى مۇناپىقلار) ئۆيلىرىدە بەخىرامان ئولتۇرغانلىقلىرى بىلەن خۇشال بولۇشتى، ئۇلار اﷲ نىڭ يولىدا ماللىرى بىلەن، جانلىرى بىلەن جىھاد قىلىشنى ياقتۇرمىدى، ئۇلار (بىر - بىرىگە): «ئىسسىقتا چىقماڭلار» دېيىشتى. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «جەھەننەمنىڭ ئوتى تېخىمۇ قىزىقتۇر. ئەگەر ئۇلار چۈشىنىدىغان بولسا، (ئەلۋەتتە ئىسسىقتا رەسۇلۇللاھ بىلەن بىرگە چىقاتتى)»'),
(1317, 9, 82, 'ئۇلار قىلمىش (گۇناھ) لىرىنىڭ جازاسى ئۈچۈن، ئاز كۈلسۇن، كۆپ يىغلىسۇن'),
(1318, 9, 83, 'ئەگەر اﷲ سېنى (تەبۈك غازىتىدىن) قايتۇرۇپ (ئۆزرىسىز قېلىپ قالغان مۇناپىقلاردىن) بىر تۈركۈمنىڭ يېنىغا ئېلىپ بارسا، ئۇلار (ئىككىنچى بىر غازاتقا) چىقىشقا سەندىن ئىزنى سورىسا، (ئۇلارغا) ئېيتقىنكى، «سىلەر مەن بىلەن بىللە مەڭگۈ (غازاتقا) چىقماڭلار، ھەرگىز مەن بىلەن بىللە چىقىپ ئۇرۇش قىلماڭلار، شۈبھىسىزكى، سىلەر دەسلەپكى قېتىمدا (چىقماي، ئۆيۈڭلاردا بەخىرامان) ئولتۇرۇشقا رازى بولدۇڭلار، ئەمدى (غازاتقا چىقماي) قېلىپ قالغۇچىلار (يەنى ئاياللار، كىچىك بالىلار) بىلەن ئولتۇرۇڭلار»'),
(1319, 9, 84, '(ئى مۇھەممەد!) سەن ئۇلارنىڭ ئارىسىدىن ئۆلگەن ھېچبىر ئادەمنىڭ نامىزىنى چۈشۈرمىگىن، (دەپنە قىلىش، زىيارەت ياكى دۇئا ئۈچۈن) ئۇنىڭ قەبرىسى ئۈستىدىمۇ تۇرمىغىن، چۈنكى ئۇلار اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلدى. ئۇلار پاسىق ھالىتى بىلەن ئۆلدى'),
(1320, 9, 85, 'ئۇلارنىڭ ماللىرى ۋە ئەۋلادلىرى سېنى ئەجەبلەندۈرمىسۇن. اﷲ دۇنيادا بۇلار ئارقىلىق ئۇلارنى جازالاشنى، ئۇلارنىڭ كاپىر پېتى جان ئۈزۈشنى ئىرادە قىلىدۇ'),
(1321, 9, 86, 'بىرەر سۈرە نازىل قىلىنىپ: «اﷲ قا ئىمان ئېيتىڭلار ۋە اﷲ نىڭ پەيغەمبىرى بىلەن بىللە جىھاد قىلىڭلار» دېيىلسە، ئۇلارنىڭ ئارىسىدىكى مالدارلار سەندىن (غازاتتىن قېلىپ قېلىشقا) رۇخسەت سورايدۇ، ئۇلار: «بىزنى قوي، (غازاتقا چىقماي، ئۆيلىرىدە بەخىرامان) ئولتۇرغۇچىلار بىلەن بىللە بولايلى» دەيدۇ'),
(1322, 9, 87, 'ئۇلار ئۆيلىرىدە قالغۇچىلار (يەنى ئاياللار، كېسەللەر ۋە ئاجىزلار) بىلەن بىللە بولۇشقا رازى بولدى، ئۇلارنىڭ دىللىرى پېچەتلەندى. شۇنىڭ ئۈچۈن، ئۇلار (جىھادتىكى پەيغەمبەرگە ئىتائەت قىلىشتىن كېلىدىغان بەختنى) چۈشەنمەيدۇ'),
(1323, 9, 88, 'لېكىن، پەيغەمبەر ۋە پەيغەمبەر بىلەن بىللە ئىمان ئېيتقانلار ماللىرى بىلەن، جانلىرى بىلەن جىھاد قىلدى، ئەنە شۇلار (ئىككىلا دۇنيانىڭ) ياخشىلىقلىرىغا نائىل بولىدۇ، ئەنە شۇلار مەقسەتكە يەتكۈچىلەردۇر'),
(1324, 9, 89, 'اﷲ ئۇلارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى تەييارلىدى. ئۇلار ئۇنىڭدا مەڭگۈ قالىدۇ، بۇ چوڭ بەختتۇر'),
(1325, 9, 90, 'ئەئرابىلاردىن ئۆزرە بايان قىلغۇچىلار (جىھادقا چىقماسلىققا) رۇخسەت سوراپ كەلدى. (ئىماننى دەۋا قىلىپ) اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە يالغانچىلىق قىلغانلار جىھادقا چىقمىدى (يەنى ئەئرابىلاردىن يەنە بىر تۈركۈمى جىھادقىمۇ چىقمىدى، ئۆزرە بايان قىلىپمۇ كەلمىدى). ئۇلاردىن كاپىر بولغانلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(1326, 9, 91, 'ئاجىزلار (يەنى ياشانغان بوۋايلار)، كېسەللەر، (جىھادقا چىقىشقا) خىراجەت تاپالمىغانلار اﷲ قا ۋە اﷲ نىڭ پەيغەمبىرىگە سادىق بولسىلا، جىھادقد چىقمىسا گۇناھ بولمايدۇ، ياخشى ئىش قىلغۇچىلارنى ئەيىبلەشكە يول يوقتۇر، اﷲ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(1327, 9, 92, 'يەنە مۇنداق كىشىلەرنىمۇ ئەيىبلەشكە يول يوقتۇركى، ئۇلار (جىھادقا چىقىش ئۈچۈن) سەندىن ئۇلاغ سوراپ كەلگەندە، (ئۇلارغا سەن) «مېنىڭ سىلەرنى تەمىنلەيدىغان ئۇلاغلىرىم يوق» دېدىڭ؟ ئۇلار (جىھادقا چىقىشقا) سەرپ قىلىدىغان نەرسە تاپالمىغانلىقلىرى ئۈچۈن غەمكىن، كۆزلىرىدىن ياش تۆككەن ھالدا قايتىشتى'),
(1328, 9, 93, 'ئۆزلىرى باي تۇرۇپ، (جىھادقا چىقماسلىققا) رۇخسەت سورىغانلار ئەيىبلىنىشى كېرەك، ئۇلار (جىھادقا چىقمىغان ئاياللار، كېسەللەر، ئاجىزلار) بىلەن بىللە بولۇشقا رازى بولدى، اﷲ ئۇلارنىڭ دىللىرىنى پېچەتلىۋەتتى، (شۇنىڭ ئۈچۈن) ئۇلار (اﷲ نىڭ ئەمرىنى) بىلمەيدۇ'),
(1329, 9, 94, 'ئۇلارنىڭ يېنىغا (يەنى مەدىنىگە تەبۈك غازىتىدىن) قايتىپ كەلگەن چېغىڭلاردا، ئۇلار سىلەرگە (غازاتتىن قېلىپ قالغانلىقلىرى توغرىسىدا) ئۆزرە بايان قىلىدۇ، (ئى مۇھەممەد!) ئۇلارغا: «ئۆزرە بايان قىلماڭلار، سىلەرنىڭ (سۆزۈڭلارغا) ئىشەنمەيمىز، اﷲ سىلەرنىڭ ئەھۋالىڭلارنى (ۋە دىلىڭلاردىكى مۇناپىقلىقنى) بىزگە خەۋەر قىلدى، اﷲ ۋە ئۇنىڭ پەيغەمبىرى سىلەرنىڭ (بۇنىڭدىن كېيىنكى) ئەمەلىڭلارنى كۆرۈپ تۇرىدۇ (مۇناپىقلىقتىن تەۋبە قىلامسىلەر؟ ياكى ئۇنىڭدا چىڭ تۇرامسىلەر؟)، ئاندىن كېيىن (يەنى ئۆلگىنىڭلاردىن كېيىن)، يوشۇرۇن ۋە ئاشكارا ئىشلارنى بىلگۈچى (اﷲ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، اﷲ سىلەرگە پۈتۈن قىلمىشىڭلارنى ئېيتىپ بېرىدۇ» دېگىن'),
(1330, 9, 95, 'ئۇلار (يەنى مۇناپىقلار) نىڭ يېنىغا قايتىپ بارغىنىڭلاردا، ئۇلار سىلەرنىڭ ئۇلارنى ئەيىبلىمەسلىكىڭلار ئۈچۈن (يالغان ئۆزرىلەر بايان قىلىپ)، اﷲ بىلەن قەسەم قىلىدۇ، ئۇلاردىن يۈز ئۆرۈڭلار (يەنى ئۇلار بىلەن سۆزلەشمەڭلار ۋە سالاملاشماڭلار)، ئۇلار ھەقىقەتەن نىجىستۇر، قىلمىشلىرىنىڭ جازاسى ئۈچۈن ئۇلارنىڭ بارىدىغان جايى جەھەننەمدۇر'),
(1331, 9, 96, 'سىلەرنىڭ رازىلىقىڭلارغا ئېرىشىش ئۈچۈن، ئۇلار سىلەرگە قەسەم ئىچىپ بېرىدۇ، سىلەر ئۇلاردىن رازى بولساڭلار (بۇ ئۇلارغا پايدا بەرمەيدۇ)، چۈنكى اﷲ پاسىق قەۋمدىن رازى بولمايدۇ'),
(1332, 9, 97, 'ئەئرابىلارنىڭ كۇفرى ۋە مۇناپىقلىقى تېخىمۇ زىيادىدۇر، اﷲ پەيغەمبىرىگە نازىل قىلغان شەرىئەت ئەھكاملىرىنى بىلمەسلىككە ئۇلار ئەڭ لايىقتۇر، اﷲ (مەخلۇقاتنىڭ ئەھۋالىنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1333, 9, 98, 'ئەئرابىلاردىن بەزىلىرى (اﷲ يولىدا) بەرگەن نەرسىلىرىنى جەرىمانە ھېسابلايدۇ، سىلەرنىڭ ھادىسىگە ئۇچرىشىڭلارنى كۈتىدۇ، ئۇلارنىڭ ئۆزلىرى ھالاكەتكە ئۇچرىسۇن، اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچى، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(1334, 9, 99, 'ئەئرابىلارنىڭ بەزىلىرى اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتىدۇ، (اﷲ يولىدا) بەرگەن نەرسىلىرىنى اﷲ قا يېقىن بولۇشنىڭ ۋە پەيغەمبەرنىڭ دۇئاسىغا ئېرىشىشنىڭ ۋاسىتىسى قىلىدۇ، ئۇلار بۇ ئارقىلىق راستلا اﷲ قا بولغان يېقىنچىلىققا ئېرىشىدۇ، اﷲ قا ئۇلارنى ئۆزنىڭ رەھمىتى دائىرىسىگە كىرگۈزىدۇ، اﷲ ھەقىقەتەن (مۆمىن بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) تولىمۇ مېھرىباندۇر'),
(1335, 9, 100, 'ھەممىدىن ئىلگىرى ئىمان ئېيتقان مۇھاجىرلار ۋە ئەنسارلاردىن، ياخشىلىق قىلىش بىلەن ئۇلارغا ئەگەشكەنلەر (يەنى تابىئىنلار ۋە قىيامەتكىچە ئۇلارنىڭ يولىدا ماڭغانلار) دىن اﷲ رازى بولدى، ئۇلارمۇ اﷲ تىن مەمنۇن بولدى. اﷲ ئۇلارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى تەييارلىدى، ئۇلار جەننەتتە مەڭگۈ قالىدۇ، بۇ چوڭ بەختتۇر'),
(1336, 9, 101, 'ئەتراپىڭلاردا ئەئرابىلاردىن مۇناپىقلار بار، مەدىنە ئاھالىسىدىنمۇ مۇناپىقلىقتا چىڭ تۇرغانلار بار، ئۇلارنى سەن بىلمەيسەن، بىز بىلىمىز، ئۇلارنى ئىككى قېتىم (يەنى دۇنيادا قەتل قىلىش ۋە ئەسىر ئېلىش بىلەن، ئۆلگەندە گۆر ئازابى بىلەن) جازالايمىز. ئاندىن ئۇلار (ئاخىرەتتە) چوڭ ئازابقا (يەنى دوزاخ ئازابىغا) قايتۇرۇلىدۇ'),
(1337, 9, 102, 'يەنە بىر قىسىم كىشىلەر گۇناھلىرىنى ئېتىراپ قىلدى (يەنى باشقىلارغا ئوخشاش جىھادقا چىقمىغانلىقىغا يالغان ئۆزرە ئېيتمىدى). ئۇلار ياخشى ئىشقا يامان ئىشنى ئارىلاشتۇرۇپ قويدى، اﷲ نىڭ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلىشى ئۈمىدلىكتۇر، اﷲ ھەقىقەتەن (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(1338, 9, 103, '(ئى مۇھەممەد!) ئۇلارنىڭ ماللىرىنىڭ بىر قىسىمىنى سەدىقە ھېسابىدا ئالغىنكى، ئۇنىڭ بىلەن ئۇلارنى گۇناھلىرىدىن پاكلىغايسەن ۋە (ياخشىلىقلىرىنى) كۆپەيتكەيسەن، ئۇلارغا دۇئا قىلغىن، شۈبھىسىزكى، سېنىڭ دۇئايىڭ ئۇلارغا خاتىرجەملىك ئېلىپ كېلىدۇ، اﷲ ئۇلارنىڭ (سۆزىنى) ئاڭلاپ تۇرغۇچى، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(1339, 9, 104, 'ئۇلار (يەنى تەۋبە قىلغۇچىلار) اﷲ نىڭ ئۆز بەندىلىرىنىڭ تەۋبىسىنى قوبۇل قىلىدىغانلىقىنى، (خالىس نىيەتلىك كىشىلەرنىڭ) سەدىقىسىنى قوبۇل قىلىدىغانلىقىنى ۋە اﷲ نىڭ تەۋبىنى بەكمۇ قوبۇل قىلىدىغانلىقىنى، ناھايىتى مېھرىبان ئىكەنلىكىنى بىلمەمدۇ؟'),
(1340, 9, 105, '(ئۇلارغا) ئېيتقىنكى، «(خالىغان) ئەمەللەرنى قىلىڭلار، قىلغان ئەمەلىڭلارنى اﷲ، اﷲ نىڭ پەيغەمبىرى ۋە مۆمىنلەر كۆرۈپ تۇرىدۇ، يوشۇرۇن ۋە ئاشكارا (نەرسىلەرنى) بىلگۈچى (اﷲ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، اﷲ قىلمىشىڭلارنى سىلەرگە خەۋەر قىلىدۇ»'),
(1341, 9, 106, 'يەنە بىر قىسىم (جىھادقا چىقمىغان) كىشىلەر (نىڭ ئىشى) اﷲ نىڭ ئەمرىگە قالدۇرۇلدى، اﷲ يا ئۇلارنى (تەۋبە قىلمىسا) جازالايدۇ، يا ئۇلارنى تەۋبىگە مۇۋەپپەق قىلىدۇ (ۋە مەغپىرەت قىلىدۇ). اﷲ ئۇلارنى (يەنى ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1342, 9, 107, 'بەزى كىشىلەر مۆمىنلەرگە زىيان يەتكۈزۈش، كۇفرىنى كۈچەيتىش، مۆمىنلەرنىڭ ئارىسىنى پارچىلاش، اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە قارشى تۇرغان ئادەم (يەنى ئەبۇئامر راھىب) نىڭ كېلىشىنى كۈتۈش مەقسىتىدە مەسجىد بىنا قىلدى. ئۇلار: «بىز پەقەت ياخشىلىقنىلا ئىرادە قىلدۇق» دەپ چوقۇم قەسەم قىلىدۇ، اﷲ گۇۋاھلىق بېرىدۇكى، ئۇلار شەك - شۈبھىسىز يالغانچىلاردۇر'),
(1343, 9, 108, 'ئۇ مەسجىدتە مەڭگۈ ناماز ئوقۇمىغىن، بىرىنچى كۈنىدىن تارتىپلا تەقۋا ئاساسىغا قۇرۇلغان مەسجىد ھەقىقەتەن سېنىڭ ناماز ئوقۇشۇڭغا ئەڭ لايىقتۇر، ئۇنىڭدا (گۇناھلاردىن) پاك بولۇشنى سۆيىدىغان كىشىلەر بار، اﷲ (ئىچكى ۋە تاشقى جەھەتتىن) پاك بولغۇچىلارنى دوست تۇتىدۇ'),
(1344, 9, 109, '(دىننىڭ) بىناسىنى اﷲ نىڭ تەقۋادارلىقى ۋە رازىلىقى ئاساسىغا قۇرغان ئادەم ياخشىمۇ ياكى يىقىلاي دەپ قالغان يارنىڭ گىرۋىكىگە قۇرۇپ، ئۆزىمۇ ئۇنىڭ بىلەن بىللە دوزاخ ئوتىغا ئۆرۈلۈپ چۈشكەن ئادەم ياخشىمۇ؟ اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ'),
(1345, 9, 110, 'تاكى ئۇلارنىڭ يۈرەكلىرى پارە - پارە بولۇپ ئۆلمىگىچە، ئۇلارنىڭ سالغان مەسجىدى (يەنى مەسجىدى زىرار) دىللىرىدىكى گۇمان (مۇناپىقلىق) نىڭ يىلتىزى بولۇپ قىلىۋېرىدۇ، اﷲ (مۇناپىقلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، (ئۇلارنىڭ يامان نىيىتىگە ئاساسەن، ئۇلارنى جازالاشتا) ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1346, 9, 111, 'شۈبھىسىزكى، اﷲ مۆمىنلەردىن ئۇلارنىڭ جانلىرىنى، ماللىرىنى ئۇلارغا جەننەتنى بېرىپ سېتىۋالدى. ئۇلار اﷲ نىڭ يولىدا ئۇرۇش قىلىپ (دۈشمەنلەرنى) ئۆلتۈرىدۇ ۋە ئۆلتۈرۈلىدۇ (يەنى دۈشمەنلەر بىلەن جىھاد قىلىپ شېھىت بولىدۇ)، (جىھاد قىلغۇچىلارغا جەننەتنى ۋەدە قىلىش) تەۋراتتا، ئىنجىلدا ۋە قۇرئاندا زىكرى قىلىنغان (اﷲ نىڭ) راست ۋەدىسىدۇر، ۋەدىسىگە اﷲ تىنمۇ بەك ۋاپا قىلغۇچى كىم بار؟ (يەنى اﷲ تىنمۇ ۋاپادار ئەھەدى يوق) قىلغان بۇ سوداڭلاردىن خۇشال بولۇڭلار، بۇ زور مۇۋەپپەقىيەتتۇر'),
(1347, 9, 112, '(گۇناھلىرىدىن) تەۋبە قىلغۇچىلار، (ئىخلاس بىلەن) ئىبادەت قىلغۇچىلار، (اﷲ قا) ھەمدۇسانا ئېيتقۇچىلار، روزا تۇتقۇچىلار، رۇكۇ قىلغۇچىلار، سەجدە قىلغۇچىلار، ياخشى ئىشلارغا دەۋەت قىلىپ، يامان ئىشلاردىن توسقۇچىلار، اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلغۇچىلار (يەنى اﷲ نىڭ بېكىتكەن پەرزلىرىنى ئادا قىلىپ، نەھيى قىلغان ئىشلىرىدىن يانغۇچىلار ھەم ئەھلى جەننەتتۇر)؛ مۆمىنلەرگە (جەننەت بىلەن) خۇش خەۋەر بەرگىن'),
(1348, 9, 113, 'پەيغەمبەرگە ۋە مۆمىنلەرگە مۇشرىكلارنىڭ ئەھلى دوزاخ ئىكەنلىكى ئېنىق مەلۇم بولغاندىن كېيىن، مۇشرىكلار ئۇلارنىڭ تۇغقىنى بولغان تەقدىردىمۇ، ئۇلارغا مەغپىرەت تەلەپ قىلىشى دۇرۇس ئەمەس'),
(1349, 9, 114, 'ئىبراھىمنىڭ ئاتىسىغا مەغپىرەت تەلەپ قىلىشى ئۇنىڭغا ئىلگىرى قىلغان ۋەدىسىنى ئورۇنلاش ئۈچۈن ئىدى. ئۇنىڭغا ئاتىسىنىڭ اﷲ نىڭ دۈشمىنى ئىكەنلىكى ئېنىق مەلۇم بولغاندىن كېيىن، ئۇ ئاتىسى بىلەن ئادا - جۇدا بولدى. ئىبراھىم بولسا ھەقىقەتەن كۆپ ئاھ چەككۈچى (يەنى كۆڭلى يۇمشاق)، (يەتكەن ئەزىيەتلەرگە) چىداملىق ئىدى'),
(1350, 9, 115, 'اﷲ بىر قەۋمنى ھىدايەت قىلغاندىن كېيىن، ئۇلار ساقلىنىشقا تېگىشلىك ئىشلارنى بايان قىلماي تۇرۇپ، ئۇلارنى گۇمراھ قىلمايدۇ (يەنى گۇمراھلىققا ھۆكۈم قىلمايدۇ)، اﷲ ھەقىقەتەن ھەر نەرسىنى بىلىپ تۇرغۇچىدۇر'),
(1351, 9, 116, 'شۈبھىسىزكى، ئاسماننىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنى) ئۆلتۈرىدۇ، سىلەرگە اﷲ تىن باشقا ھېچ دوست ۋە مەدەتكار يوق'),
(1352, 9, 117, 'اﷲ ھەقىقەتەن پەيغەمبەرنىڭ ۋە ئۇنىڭغا قىيىنچىلىق پەيتىدە ئەگەشكەن مۇھاجىرلار ۋە ئەنسارلارنىڭ تەۋبىسىنى قوبۇل قىلدى. (ئەينى زاماندا) ئۇلارنىڭ ئارىسىدىكى بىر تۈركۈم كىشىلەرنىڭ دىللىرى ھەقتىن بۇرۇلۇپ كېتىشىگە تاس قالغاندىن كېيىن، اﷲ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلدى. اﷲ ئۇلارغا تولىمۇ مەرھەمەتلىك، ناھايىتى مېھرىباندۇر'),
(1353, 9, 118, 'جىھادقا چىقمىغان ئۈچ كىشىنىڭ تەۋبىسىنىمۇ اﷲ قوبۇل قىلدى. شۇنچە كەڭ زېمىن ئۇلارغا تار تۇيۇلغان، ئۇلارنىڭ جانلىرى سىقىلغان، ئۇلار اﷲ قا تەۋبە قىلماي تۇرۇپ اﷲ نىڭ غەزىپىدىن قۇتۇلۇش مۇمكىن ئەمەسلىكىگە ئىشەنگەن ئىدى. ئاندىن ئۇلارنىڭ تەۋبە قىلغۇچىلاردىن بولۇشى ئۈچۈن، اﷲ ئۇلارنى تەۋبىگە مۇۋەپپەق قىلدى. اﷲ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، (بەندىلىرىگە) ناھايىتى مېھرىباندۇر'),
(1354, 9, 119, 'ئى مۆمىنلەر! اﷲ تىن قورقۇڭلار، راستچىللار بىلەن بىللە بۇلۇڭلار'),
(1355, 9, 120, 'مەدىنە ئاھالىسى ۋە ئۇلارنىڭ ئەتراپىدىكى ئەئرابىلارنىڭ رەسۇلۇللا بىلەن جىھادقا بىللە چىقماي قېلىپ قېلىشى، ئۆزلىرىنىڭ ئارامى بىلەنلا بولۇپ، جاپادا رەسۇلۇللاھ بىلەن بىللە بولماسلىقى توغرا ئەمەس ئىدى. بۇ (يەنى ئۇلارنى جىھادقا چىقماي قېلىپ قېلىشتىن مەنئى قىلىش) شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ نىڭ يولىدا ئۇچرىغان ئۇسسۇزلۇق، تارتقان جاپا - مۇشەققەت ۋە ئاچلىق، ئۇلارنىڭ كاپىرلارنى خاپىلىققا سالىدىغان ھەر بىر قەدىمى، دۈشمەنلەر ئۈستىدىن ئېرىشكەن (ئۇلارنى ئۆلتۈرۈش، ئەسىر ئېلىش، مەغلۇپ قىلىش قاتارلىق) ھەر بىر نەرسىسى ئۈچۈن (اﷲ نىڭ دەرگاھىدا) ئۇلارغا ياخشىلىق (يەنى ساۋاب) يېزىلىدۇ، اﷲ ئەلۋەتتە ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ'),
(1356, 9, 121, 'ئۇلارنىڭ (اﷲ نىڭ يولىدا) مەيلى كىچىك بولسۇن ياكى چوڭ بولسۇن، سەرپ قىلغان نەرسىسى ۋە جىھاد ئۈچۈن باسقان ھەر بىر مۇساپىسى ئۇلارنىڭ قىلغان ئەمەللىرىگە ئەڭ ياخشى مۇكاپات بېرىش يۈزىسىدىن ئۇلارغا يېزىلىدۇ'),
(1357, 9, 122, 'مۆمىنلەرنىڭ ھەممىسىنىڭ جىھادقا چىقىشى لايىق ئەمەس (ئۇلارنىڭ بىر تۈركۈمى جىھادقا چىقتى)، ئۇلارنىڭ ئىچىدىكى ھەر بىر جامائەدىن يەنە بىر تۈركۈمى دىنىي ئالىم بولۇپ، ئۇلار قەۋمىنىڭ قېشىغا قايتقاندىن كېيىن، قەۋمىنىڭ اﷲ تىن قورقۇشى ئۈچۈن، ئۇلارنى ئاگاھلاندۇرۇش مەقسىتىدە نېمىشقا (ئىلىم تەلەپ قىلىشقا) چىقمىدى؟'),
(1358, 9, 123, 'ئى مۆمىنلەر! سىلەر ئۆزۈڭلارغا يېقىن جايدىكى كاپىرلار بىلەن ئۇرۇش قىلىڭلار (يەنى سىلەرگە خەتەر بولماسلىق ئۈچۈن، ئالدى بىلەن ئەتراپىڭلاردىكى كۇففارلارنى ئۇجۇقتۇرۇڭلار)، سىلەرنى ئۇلار ھېس قىلسۇن (يەنى ئۇرۇش ئۈستىدە ئۇلارغا قاتتىق قول بۇلۇڭلار، ئۇلارغا ئىچىڭلار ئاغرىمىسۇن)، بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر (يەنى اﷲ تەقۋادارلارغا مەدەتكاردۇر)'),
(1359, 9, 124, '(قۇرئاندىن) بىرەر سۈرە نازىل قىلىنسا، ئۇلار (يەنى مۇناپىقلار) نىڭ ئارىسىدا (مەسخىرە قىلىش يۈزىسىدىن): «بۇ (يەنى سۈرە) قايسىڭلارنىڭ ئىمانىنى كۈچەيتتى» دەيدىغانلار بار، مۆمىنلەرگە كېلىدىغان بولساق، (ھەر سۈرىدە يېڭى دەلىل - ئىسپاتلار بولغانلىقى ئۈچۈن) بۇ (يەنى سۈرە) ئۇلارنىڭ ئىمانىنى كۈچەيتتى، ئۇلار (سۈرىنىڭ نازىل بولۇشىدىن) خۇشال بولىدۇ'),
(1360, 9, 125, 'دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار) غا كېلىدىغان بولساق، بۇ (سۈرە) ئۇلارنىڭ كۇفرىغا كۇفرى قوشتى (يەنى ئۇلار مۇناپىقلىقتا تېخىمۇ ئەزۋەيلىدى، تېخىمۇ گۇمراھلاشتى)، ئۇلار كاپىر پېتى ئۆلدى'),
(1361, 9, 126, 'ئۇلار ئۆزلىرىنىڭ ھەر يىلى بىر قېتىم ياكى ئىككى قېتىم سىنىلىدىغانلىقىنى (يەنى ئۇلار توغرىسىدا ۋەھيى نازىل بولۇپ، سىرلىرىنىڭ پاش قىلىنىدىغانلىقىنى) بىلمەمدۇ؟ ئاندىن ئۇلار تەۋبە قىلمايدۇ (يەنى نىفاقتىن قايتمايدۇ). ئىبرەتمۇ ئالمايدۇ'),
(1362, 9, 127, '(ئۇلار پەيغەمبەر ئەلەيھىسسالامنىڭ ھۇزۇرىدىكى چاغدا، ئۇلارنىڭ ئەيىبىنى ئاچىدىغان قۇرئاندىن) بىرەر سۈرە نازىل قىلىنسا، ئۇلار بىر - بىرىگە قارىشىپ: «سىلەرنى بىرەرسى كۆرۈپ قالارمۇ» دېيىشىپ تىكىۋېتىدۇ، ئۇلار (ھەقىقەتنى) چۈشەنمەس قەۋم بولغانلىقى ئۈچۈن، اﷲ قا ئۇلارنىڭ دىللىرىنى ئۆزگەرتىۋەتتى'),
(1363, 9, 128, '(ئى ئىنسانلار!) شۈبھىسىزكى، سىلەرگە ئۆزئارا ئاراڭلاردىن پەيغەمبەر كەلدى. سىلەرنىڭ كۈلپەت چېكىشىڭلار ئۇنىڭغا ئېغىر تۇيۇلىدۇ؛ ئۇ سىلەرنىڭ (ھىدايەت تېپىشىڭلارغا) ھېرىستۇر، مۆمىنلەرگە ئامراقتۇر، ناھايىتى كۆيۈمچاندۇر'),
(1364, 9, 129, '(ئى پەيغەمبەر!) ئەگەر ئۇلار (ساڭا ئىمان ئېيتىشتىن) يۈز ئۆرۈسە، سەن: «اﷲ ماڭا. كۆرىدۇ قىلىدۇ، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇنىڭغا تەۋەككۈل قىلدىم (يەنى اﷲ قا يۆلەندىم، اﷲ تىن باشقا ھېچ ئەھەدىدىن ئۈمىد كۈتمەيمەن ۋە ھېچ ئەھەدىدىنمۇ قورقمايمەن)، ئۇ بۈيۈك ئەرشنىڭ پەرۋەردىگارىدۇر» دېگىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'ئەلىف، لام، را. بۇ، ھېكمەتلىك كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر'),
(1366, 10, 2, 'ئىنسانلارنىڭ بىرىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا): ئىنسانلار (يەنى كاپىرلار) نى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرغىن، مۆمىنلەرگە پەرۋەردىگارىنىڭ دەرگاھىدا (قىلغان ياخشى ئەمەللىرى ئۈچۈن) يۇقىرى ئورۇنغا ئېرىشىدىغانلىقى بىلەن خۇش خەۋەر بەرگىن، دەپ ۋەھيى قىلغانلىقىمىزدىن ئۇلار ئەجەبلىنەمدۇ؟ كاپىرلار: «بۇ ھەقىقەتەن ئاشكارا سېھىرگەردۇر» دېدى'),
(1367, 10, 3, 'شۈبھىسىزكى، سىلەرنىڭ پەرۋەردىگارىڭلار اﷲ دۇر، ئۇ ئىنسانلارنى، زېمىننى ئالتە كۈندە ياراتتى. ئاندىن ئەرش ئۈستىدە ئۆزىگە لايىق رەۋىشتە قارار ئالدى. اﷲ خالايىقنىڭ ئىشلىرىنى (ئۆز ھېكمىتىگە مۇۋاپىق) ئىدارە قىلىپ تۇرىدۇ، (قىيامەت كۈنى) ھەر قانداق شاپائەت قىلغۇچى ئالدى بىلەن اﷲ نىڭ ئىزنىنى ئالىدۇ، ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، ۋەز - نەسىھەت ئالمامسىلەر؟'),
(1368, 10, 4, '(قىيامەت كۈنى) ھەممىڭلار اﷲ نىڭ دەرگاھىغا قايتىسىلەر، اﷲ نىڭ ۋەدىسى ھەقتۇر، شۈبھىسىزكى، مەخلۇقاتنى دەسلەپتە اﷲ ياراتقان، (ئۇلارنى ھالاك قىلغاندىن كېيىن، قىيامەت كۈنى) ئىمان ئېيتقانلارنى ۋە ياخشى ئەمەل قىلغانلارنى ئادىللىق بىلەن مۇكاپاتلاش ئۈچۈن قايتا تىرىلدۈرىدۇ، كاپىرلارنىڭ بولسا، ئۆزلىرىنىڭ كۇفرى تۈپەيلىدىن، (دوزاختىكى) ئىچىملىكى قاتتىق قاينىغان سۇ بولىدۇ ھەمدە ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(1369, 10, 5, 'اﷲ قۇياشنى نۇر چاچقۇچى ۋە ئاينى نۇرلۇق قىلىپ ياراتتى، يىللارنىڭ سانىنى ۋە (ۋاقىتلارنىڭ) ھېسابىنى بىلىشىڭلار ئۈچۈن، ئايغا مەنزىللەرنى (يەنى بۇرۇجلارنى) تەيىن قىلدى. اﷲ ئۇلارنى بىكار ياراتقىنى يوق (بەلكى بۈيۈك ھېكمەت ۋە زور پايدىلىق ئۈچۈن ياراتتى)، اﷲ ئايەتلىرىنى (قۇدرىتى ئىلاھىيىنى) بىلىدىغان (ۋە ئۇنىڭ ھېكمىتىنى پىكىر قىلىدىغان) قەۋم ئۈچۈن تەپسىلىي بايان قىلىدۇ'),
(1370, 10, 6, 'كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا، اﷲ ئاسماندا ۋە زېمىندا ياراتقان شەيئىلەردە (اﷲ تىن) قورقىدىغان قەۋم ئۈچۈن، ھەقىقەتەن، (اﷲ نىڭ بارلىقىنى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار'),
(1371, 10, 7, 'شۈبھىسىزكى، بىز بىلەن مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار (يەنى اﷲ قا مۇلاقات بولۇشنى كۆزلىمەيدىغانلار)، دۇنيا تىرىكچىلىكىدىن بەھرىمەن بولۇش بىلەنلا رازى بولغانلار ۋە ئۇنىڭ بىلەن ئارام تاپقانلار، بىزنىڭ ئايەتلىرىمىزدىن غاپىل بولغانلار،'),
(1372, 10, 8, '- ئەنە شۇلارنىڭ جايى، قىلمىشلىرى تۈپەيلىدىن، دوزاخ بولىدۇ'),
(1373, 10, 9, 'شۈبھىسىزكى، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلارنى پەرۋەردىگارى، ئۇلارنىڭ ئىمانى سەۋەبىدىن، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان نازۇنېمەتلىك جەننەتلەرگە يېتەكلەيدۇ'),
(1374, 10, 10, 'ئۇلارنىڭ جەننەتتىكى دۇئاسى: «ئى رەببىمىز، بىز سېنى پاك دەپ ئېتىقاد قىلىمىز» دېيىشتىن ئىبارەت، ئۇلارنىڭ جەننەتتە (بىر - بىرىگە) بېرىدىغان سالامى: «(ئەھلى دوزاخ چېكىۋاتقان ئازابتىن) ئامان بولغايسەن (يەنى ئەسسالامۇئەلەيكۇم)» دېيىشتىن ئىبارەتتۇر، ئۇلارنىڭ دۇئاسىنىڭ ئاخىرى: «جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!» دېيىشتىن ئىبارەتتۇر'),
(1375, 10, 11, 'ئەگەر اﷲ كىشىلەر تەلەپ قىلغان يامانلىقنى ئىشقا ئاشۇرۇشقا ئۇلار تەلەپ قىلغان ياخشىلىقىنى ئىشقا ئاشۇرۇشقا ئالدىرىغاندەك ئالدىرىسا ئىدى، ئۇلارنىڭ ئەجىلى چوقۇم يەتكەن بولاتتى (يەنى ھالاك بولاتتى). بىز بىلەن مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلارنى گۇمراھلىقتا تېڭىرقاپ يۈرۈشكە قويۇپ بېرىمىز'),
(1376, 10, 12, 'ئىنسانغا بىرەر زىيان - زەخمەت يەتسە، ياتسىدۇ، ئولتۇرسىدۇ، تۇرسىدۇ، (ئۇنى دەپئى قىلىشىمىزنى تىلەپ) بىزگە دۇئا قىلىدۇ؛ ئۇلارغا يەتكەن زىيان - زەخمەتنى كۆتۈرۈۋەتسەك، گويا ئۇنى كۆتۈرۈۋېتىشىمىزنى تىلەپ بىزگە دۇئا قىلمىغاندەك، يامان ئىشنى يەنە داۋاملاشتۇرىدۇ، ھەددىدىن ئاشقۇچىلارنىڭ قىلمىشلىرى ئۇلارغا مۇشۇنداق چىرايلىق كۆرسىتىلدى'),
(1377, 10, 13, 'سىلەردىن ئىلگىرىكى ئۈممەتلەر زۇلۇم قىلغاندا (يەنى كاپىر بولغاندا، گۇمراھلىقتا مۆجىزىلەرنى ئېلىپ كەلگەن ئىدى. ئۇلار (پەيغەمبەرلەر ئېلىپ كەلگەن مۆجىزىلەرگە) ئىمان كەلتۈرمىدى. گۇناھكار قەۋمنى (يەنى مەككە مۇشرىكلىرىنى) شۇنىڭغا ئوخشاش جازالايمىز'),
(1378, 10, 14, 'سىلەرنىڭ قانداق قىلىدىغانلىقىڭلارنى كۆرۈش ئۈچۈن، ئۇلارنى (يەنى ئۆتكەن ئۈممەتلەرنى) (ھالاك قىلغان) دىن كېيىن، ئۇلارنىڭ ئورنىغا سىلەرنى ئورۇنباسار قىلدۇق'),
(1379, 10, 15, 'ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنسا، بىزگە مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار (يەنى قىيامەتكە ئىشەنمەيدىغانلار): «بۇنىڭدىن باشقا بىر قۇرئاننى كەلتۈرگىن ياكى ئۇنى ئۆزگەرتكىن (يەنى ئازابقا دائىر ئايەتنى رەھمەت ئايىتىگە ئۆزگەرت، ئىلاھلىرىمىزنى سۆككەننىڭ ئورنىغا مەدھىيلە، ھارامنى ھالال قىل)» دەيدۇ. «مەن ئۇنى ئۆزۈمچە ئۆزگەرتەلمەيمەن، مەن پەقەت ماڭا قىلىنغان ۋەھيىگىلا ئەمەل قىلىمەن؛ ئەگەر مەن پەرۋەردىگارىمغا ئاسىيلىق قىلسام، ئەلۋەتتە، بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن قورقىمەن» دېگىن'),
(1380, 10, 16, '«ئەگەر اﷲ ئىرادە قىلسا، سىلەرگە ئۇنى (يەنى قۇرئاننى) تىلاۋەت قىلماس ئىدىم، اﷲ مۇ ئۇنى (مەن ئارقىلىق) سىلەرگە بىلدۈرمەس ئىدى. ئاراڭلاردا ئۇنىڭدىن (يەنى قۇرئان نازىل بولۇشتىن) ئىلگىرى ئۇزۇن زامان تۇردۇم، (مۆجىزە بولغان بۇ قۇرئاننىڭ پەقەت اﷲ تەرىپىدىن نازىل بولغانلىقىنى بىلىشكە) ئەقلىڭلارنى ئىشلەتمەمسىلەر؟» دېگىن'),
(1381, 10, 17, 'اﷲ قا يالغاننى چاپلىغان ياكى اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغان ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ گۇناھكارلار ھەرگىز بەختكە ئېرىشەلمەيدۇ'),
(1382, 10, 18, 'ئۇلار اﷲ نى قويۇپ، ئۆزلىرىگە پايدا - زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنىدۇ، «بۇلار (يەنى بۇتلار) اﷲ نىڭ دەرگاھىدا بىزگە شاپائەت قىلغۇچىلار» دېيىشىدۇ، ئەجەبا، ئاسمانلاردا ۋە زېمىندا بۇنداق نەرسە (يەنى اﷲ نىڭ شېرىكى ياكى شاپائەت قىلغۇچى) بولۇپ، ئۇنى اﷲ بىلمەي قېلىپ، سىلەر اﷲ قا ئۇنى ئېيتىپ بېرەمسىلەر؟ اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە يۈكسەكتۇر'),
(1383, 10, 19, 'ئىنسانلار (ئەسلىدە) بىر دىن (يەنى ئىسلام دىنى) دا ئىدى. كېيىن ئۇلار ئىختىلاپ قىلىشتى (يەنى دىنلار كۆپەيدى، بۇتلارغا چوقۇنۇشتى). پەرۋەردىگارىڭنىڭ ئالدىنئالا ئېيتقان سۆزى (يەنى جازالاشنى قىيامەتكە قالدۇرۇشتىن ئىبارەت تەقدىرى) بولمىسا ئىدى، ئۇلارنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىلاتتى (يەنى دىندا ئىختىلاپ قىلىشقانلىرى ئۈچۈن، ئۇلارغا بۇ دۇنيادىلا جازا بېرىلەتتى)'),
(1384, 10, 20, 'ئۇلار (يەنى ئەھلى مەككە): «نېمىشقا ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) پەرۋەردىگارى تەرىپىدىن (بىز تەلەپ قىلغان مۆجىزىلەردىن) بىر مۆجىزە چۈشۈرۈلمىدى» دەيدۇ. ئېيتقىنكى، «غەيبنى بىلىش پەقەت اﷲ قا خاس (مۆجىزىلەرنى اﷲ كەلتۈرىدۇ، مەن پەقەت تەبلىغ قىلغۇچىمەن)، سىلەر (اﷲ نىڭ ئارىمىزدا ھۆكۈم چىقىرىشنى) كۈتۈڭلار، شۈبھىسىزكى، مەنمۇ سىلەر بىلەن (شۇنى) كۈتكۈچىلەردىنمەن»'),
(1385, 10, 21, 'كىشىلەرنىڭ بېشىغا كەلگەن زىيان - زەخمەتنى كۆتۈرۈۋەتكەندىن كېيىن، ئۇلارغا رەھمىتىمىزنى تېتىتساق، ئۇلار بىزنىڭ ئايەتلىرىمىز ئۈستىدە مىكىر ئىشلىتىشكە (يەنى ئىنكار قىلىشقا) ئالدىرايدۇ. ئېيتقىنكى، «اﷲ ئۇلارنى قىلغان مىكرى ئۈچۈن ئەڭ تېز جازالايدۇ». شۈبھىسىزكى، بىزنىڭ پەرىشتىلىرىمىز مىكرىڭلارنى يېزىپ تۇرىدۇ'),
(1386, 10, 22, 'اﷲ سىلەرنى قۇرۇقلۇقتا (يەنى ئۇلاغلار ئۈستىدە)، دېڭىزدا (يەنى كېمىلەر ئۈستىدە) سەپەر قىلدۇرىدۇ، سىلەر ئولتۇرغان كېمە (كىشىلەرنى ئېلىپ) مەيىن شامالدا مېڭىۋاتقان ۋە (بۇ) شامالدىن ئۇلار خۇشاللىنىۋاتقان چاغدا، بىردىنلا بوران چىقىپ (كېمە ئۆرۈلىدۇ)، ئۇلار تەرەپ - تەرەپتىن كۆتۈرۈلۈۋاتقان دېڭىز دولقۇنلىرى ئىچىدە قالىدۇ، كېمىدىكىلەر قورشىۋېلىنغانلىقىغا (يەنى ھالاك بولىدىغانلىقىغا) جەزم قىلىدۇ، (چوقۇنۇۋاتقان بۇتلىرىنى تاشلاپ قويۇپ) «ئى خۇدا! ئەگەر سەن بىزنى بۇنىڭدىن (يەنى بالادىن) قۇتۇلدۇرساڭ، بىز چوقۇم شۈكۈر قىلغۇچىلاردىن بولىمىز» دەپ چىن كۆڭلى بىلەن دۇئا قىلىدۇ'),
(1387, 10, 23, 'اﷲ ئۇلارنى (ئۇ بالادىن) قۇتقۇزغاندىن كېيىن، ئۇلار يەر يۈزىدە يولسىزلىق بىلەن بۇزۇقچىلىق قىلىدۇ. ئى ئىنسانلار! بۇزۇقچىلىقىڭلارنىڭ گۇناھى ئۆزۈڭلارغا بولىدۇ، (سىلەر) بۇ دۇنيا (نىڭ باقاسى يوق شەھۋەتلىرى) دىن بەھرىمەن بولىسىلەر (بۇنىڭ ھەسرىتى ئاخىرەتكە قالىدۇ)، ئاندىن سىلەر بىزنىڭ دەرگاھىمىزغا قايتىسىلەر، سىلەرگە قىلمىشىڭلارنى خەۋەر قىلىمىز'),
(1388, 10, 24, 'ھاياتىي دۇنيانىڭ مىسالى (تېز ئۆزگۈرۈشىدە، نېمەتلىرىنىڭ باقاسىزلىقىدا، كىشىلەرنىڭ ئۇنىڭ بىلەن مەغرۇر بولۇشىدا) بىز ئاسماندىن چۈشۈرگەن (يامغۇر) سۈيىگە ئوخشايدۇكى، ئۇنىڭ بىلەن زېمىننىڭ ئىنسانلار، ھايۋانلار يەيدىغان تۈرلۈك ئۆسۈملۈكلىرى (ئۈنۈپ چىقىپ) بىر - بىرى بىلەن گىرەلىشىپ كەتكەن، ھەتتا زېمىن گۈزەل تۈسكە كىرگەن، چىرايلىق ياسانغان، زېمىن ئىگىلىرى ئۇنىڭدىن مول ھوسۇل ئېلىشقا جەزم قىلىپ تۇرغان چاغدا، ناگاھان كېچىسى ياكى كۈندۈزى ئۇنىڭغا قازايىمىز يەتتى - دە، (زىرائەتلەرنى) تۈنۈگۈن (يەنى ئىلگىرى) يوقتەك، (پۈتۈنلەي) ئۇرۇپ تاشلانغاندەك قىلىۋەتتۇق (يەنى نابۇت قىلىپ، يوق قىلدۇق)، پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلىمىز'),
(1389, 10, 25, 'اﷲ (كىشىلەرنى) ئامان جايغا (يەنى جەننەتكە) دەۋەت قىلىدۇ ۋە خالىغان ئادەمنى توغرا يولغا باشلايدۇ'),
(1390, 10, 26, 'ياخشى ئىش قىلغانلار جەننەتكە ۋە اﷲ نىڭ جامالىنى كۆرۈشكە نائىل بولىدۇ، ئۇلارنىڭ يۈزلىرىنى قارىلىق ۋە خارلىق قاپلىمايدۇ (يەنى ئۇلارغا غەم - قايغۇ ۋە پەرىشانلىق يۈزلەنمەيدۇ). ئەنە شۇلار ئەھلى جەننەت بولۇپ، جەننەتتە مەڭگۈ قالغۇچىلاردۇر'),
(1391, 10, 27, '(دۇنيادا) يامان ئىشلارنى قىلغۇچىلار (بىر يامانلىقنى قىلسا، قىلغان يامانلىقىغا) ئوخشاش (يەنى شۇنىڭغا لايىق) جازاغا ئۇچرايدۇ، ئۇلار خارلىققا دۇچار بولىدۇ، ئۇلارنى اﷲ نىڭ ئازابىدىن قۇتۇلدۇرغۇچى زادى بولمايدۇ، ئۇلارنىڭ يۈزلىرى گويا قاراڭغۇ كېچىنىڭ پارچىسى بىلەن ئورالغاندەك قارىيىپ كېتىدۇ، ئۇلار ئەھلى دوزاخ بولۇپ، دوزاختا مەڭگۈ قالغۇچىلاردۇر'),
(1392, 10, 28, 'شۇ كۈن (يەنى قىيامەت كۈنى) دە (مۆمىنلەرنى، كاپىرلارنى) ھەممىسىنى يىغىمىز، ئاندىن مۇشرىكلارغا: «سىلەر (اﷲ قا شېرىك كەلتۈرگەن) بۇتلىرىڭلار بىلەن بىللە، ئورنۇڭلاردا مىدىرلىماي تۇرۇڭلار» دەيمىز، ئۇلارنىڭ ئارىسىنى ئايرىيمىز، ئۇلارنىڭ شېرىك كەلتۈرگەن بۇتلىرى ئېيتىدۇ: «سىلەر بىزگە ئىبادەت قىلمىغان ئىدىڭلار (بەلكى نەپسى خاھىشىڭلارغا ئىبادەت قىلغان ئىدىڭلار)'),
(1393, 10, 29, 'سىلەر بىلەن بىزنىڭ ئارىمىزدا گۇۋاھ بولۇشقا اﷲ كۇپايىكى، سىلەرنىڭ بىزگە چوقۇنۇشۇڭلاردىن بىز غاپىل ئىدۇق (يەنى بىز كۆرمەيدىغان، ئاڭلىمايدىغان، ھېس قىلمايدىغان جانسىز ئىدۇق)»'),
(1394, 10, 30, 'بۇ چاغدا ھەر ئادەم ئىلگىرى قىلغان ئەمەللىرى ئۈستىدە سىنىلىدۇ (يەنى ياخشىمۇ - يامانمۇ، ئۇنىڭ نەتىجىسىنى. كۆرىدۇ)، ئۇلار ھەقىقىي ئىگىسى - اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ، ئۇلارنىڭ شاپائەت قىلىدۇ دەپ دەۋا قىلغان بۇتلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالىدۇ'),
(1395, 10, 31, '(ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەرگە ئاسماندىن (يامغۇر ياغدۇرۇپ) زېمىندىن (گىياھ ئۈندۈرۈپ) كىم رىزىق بېرىدۇ؟ سىلەرنىڭ ئاڭلاش ۋە كۆرۈش قابىلىيىتىڭلارنى كىم باشقۇرىدۇ؟ تىرىك شەيئىلەردىن كىم پەيدا قىلىدۇ؟ (خالايىقنىڭ) ئىشلىرىنى كىم ئىدارە قىلىدۇ؟» ئۇلار (بۇلارغا جاۋابەن): «اﷲ» دەيدۇ. ئېيتقىنكى، «اﷲ تىن غەيرىيگە چوقۇنۇش بىلەن اﷲ نىڭ ئازابىدىن) قورقمامسىلەر؟'),
(1396, 10, 32, '(يۇقىرىقىدەك چوڭ ئىشلارنى قىلغۇچى) سىلەرنىڭ ھەقىقىي پەرۋەردىگارىڭلار، ئەنە شۇ اﷲ دۇر. ھەقىقەتتىن قالسا، گۇمراھلىقتىن غەيرىي نەرسە مەۋجۇت ئەمەس، نېمىشقا (ئىماندىن يۈز ئۆرۈپ گۇمراھلىققا) بۇرۇلۇپ كېتىسىلەر؟»'),
(1397, 10, 33, 'پەرۋەردىگارىڭنىڭ اﷲ قا ئىتائەت قىلىشتىن باش تارتقانلار توغرىسىدىكى ھۆكمى ھەقلىق بولدى. ئۇلار (اﷲ نىڭ بىرلىكىگە، رەسۇلۇللانىڭ پەيغەمبەرلىكىگە) ئىمان ئېيتمايدۇ'),
(1398, 10, 34, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرنىڭ بۇتلىرىڭلارنىڭ ئىچىدە مەۋجۇداتنى يوقلۇقتىن بار قىلىپ (ئاندىن يوق قىلىپ)، ئاندىن ئۇنى ئەسلىگە كەلتۈرەلەيدىغانلار بارمۇ؟» ئېيتقىنكى، «اﷲ مەۋجۇداتنى يوقلۇقتىن بار قىلىپ (ئاندىن يوق قىلىپ)، ئاندىن ئۇنى ئەسلىگە كەلتۈرەلەيدۇ. نېمىشقا (ھەقتىن باتىل تەرەپكە) بۇرۇلۇپ كېتىسىلەر؟»'),
(1399, 10, 35, 'ئېيتقىنكى، «سىلەرنىڭ بۇتلىرىڭلارنىڭ ئارىسىدا (گۇمراھنى) ھەق يولغا يېتەكلىيەلەيدىغانلار بارمۇ؟» ئېيتقىنكى، «اﷲ گۇمراھنى ھەق يولغا يېتەكلىيەلەيدىغان زاتقا بويسۇنۇشقا تېگىشلىكمۇ؟ ياكى (گۇمراھنى) ھەق يولغا يېتەكلىيەلمەيدىغان، پەقەت ئۆزى باشقىلارنىڭ يېتەكلىشىگە موھتاج بولغان (بۇتلاغا) بويسۇنۇشقا تېگىشلىكمۇ؟ سىلەرگە نېمە بولدى؟ نېمىشقا مۇنداق (باتىل) ھۆكۈم چىقىرىسىلەر؟»'),
(1400, 10, 36, 'ئۇلارنىڭ تولىسى (بۇتلارغا ئېتىقاد قىلىشتا) پەقەت پەرەزگىلا تايىنىدۇ، پەرەز دېگەن ھەقىقەتنى ئىسپاتلاشتا ھەقىقەتەن ھېچ نەرسىگە يارىمايدۇ. شۈبھىسىزكى، اﷲ ئۇلارنىڭ قىلمىشلىرىنى ئوبدان بىلگۈچىدۇر'),
(1401, 10, 37, 'بۇ قۇرئاننى بىراۋنىڭ اﷲ قا ئىپتىرا قىلىشى ئەقلىغە سىغمايدۇ (چۈنكى قۇرئاننى ھېچ ئىنسان ئىجاد قىلالمايدۇ)، لېكىن قۇرئان ئىلگىرى كەلگەن (تەۋرات، ئىنجىل قاتارلىق ساماۋى) كىتابلارنى تەستىق قىلغۇچىدۇر، اﷲ بەلگىلىگەن ئەھكاملارنى بايان قىلغۇچىدۇر، ئۇنىڭدا ھېچ شەك يوقتۇر، (ئۇ) ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر'),
(1402, 10, 38, 'ئۇلار: «قۇرئاننى مۇھەممەد ئۆزى توقۇپ چىققان» دېيىشەمدۇ؟ ئېيتقىنكى، «ئەگەر (بۇ سۆزۈڭلاردا) راستچىل بولساڭلار، اﷲ تىن باشقا (ياردەمگە) چاقىرالايدىغانلىكى كىشىڭلارنى چاقىرىپ، قۇرئانغا ئوخشاش بىرەر سۈرىنى كەلتۈرۈپ بېقىڭلار»'),
(1403, 10, 39, 'ئۇنداق ئەمەس، ئۇلار قۇرئاننىڭ مەنىسىنى تولۇق چۈشەنمەي ۋە ھەقىقىتىگە تېخى ئەقلى يەتمەي تۇرۇپ ئۇنى ئىنكار قىلىشتى. ئۇلاردىن بۇرۇن ئۆتكەن ئۈممەتلەرمۇ (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) مۇشۇلارغا ئوخشاش يالغانغا چىقارغان ئىدى. زالىملارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراپ باققىن'),
(1404, 10, 40, 'ئۇلارنىڭ ئىچىدە قۇرئانغا ئىشىنىدىغانلار بار، ئىشەنمەيدىغانلارمۇ بار، پەرۋەردىگارىڭ بۇزغۇنچىلارنى ھەممىدىن ئوبدان بىلىدۇ'),
(1405, 10, 41, 'ئەگەر ئۇلار سېنى يالغانچى قىلىشتا چىڭ تۇرسا: «مېنىڭ ئەمەلىم ئۆزۈم ئۈچۈندۇر، سىلەرنىڭ ئەمەلىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، مېنىڭ ئەمەلىمدىن سىلەر ئادا - جۇداسىلەر، سىلەرنىڭ قىلمىشىڭلاردىن مەنمۇ ئادا - جۇدامەن (يەنى بىراۋ يەنە بىراۋنىڭ قىلغان گۇناھىغا جاۋابكار ئەمەس)» دېگىن'),
(1406, 10, 42, 'ئۇلارنىڭ ئىچىدە (قۇرئان ئوقۇغىنىڭدا) ساڭا قۇلاق سالىدىغانلار بار، سەن گاسلارغا (يەنى اﷲ ئاڭلاش ئىقتىدارىنى ئېلىپ تاشلىغانلارغا) ئۇلار ھېچ نەرسىنى چۈشەنمىسىمۇ ئاڭلىتالامسەن؟'),
(1407, 10, 43, 'ئۇلارنىڭ ئىچىدە ساڭا قارايدىغانلار بار (يەنى پەيغەمبەرلىكىڭنىڭ ئېنىق ئالامەتلىرىنى بايقايدىغانلار بار، لېكىن ئۇلار بايقىغانلىرىدىن پايدىلانمىغانلىقلىرى ئۈچۈن كۆردۇر). (ھەقىقەتنى) كۆرەلمىسىمۇ كورلارنى ھىدايەت قىلالامسەن؟'),
(1408, 10, 44, 'شۈبھىسىزكى، اﷲ ئىنسانلارغا قىلچە زۇلۇم قىلمايدۇ، لېكىن ئىنسانلار (كۇفرى ۋە گۇناھ ئارقىلىق) ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلىدۇ'),
(1409, 10, 45, 'قىيامەت كۈنى اﷲ ئۇلارنى (ھېساب ئېلىش ئۈچۈن) يىغىدۇ، (ئۇلارغا دۇنيادا تۇرغان مۇددىتى) كۈندۈزدە ئازغىنا ۋاقىت تۇرغاندەك قىسقا بىلىنىدۇ، ئۇلار ئۆزئارا تونۇشىدۇ، ئۆزلىرىنىڭ اﷲ قا مۇلاقات بولۇشىنى (يەنى ئۆلگەندىن كېيىن تىرىلىشىنى) ئىنكار قىلغانلار چوقۇم زىيان تارتىدۇ، ئۇلار توغرا يول تاپمىدى'),
(1410, 10, 46, 'ئۇلارغا بىز ۋەدە قىلغان ئازابنىڭ بىر قىسمىنى ساڭا كۆرسىتىمىز (يەنى بۇ دۇنيادىلا، سەن ھايات ۋاقىتتىلا كۆرسىتىمىز)، ياكى ئۇنىڭدىن ئىلگىرى سېنى قەبزى روھ قىلىمىز، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتىدۇ (مەيلى بالدۇر بولسۇن، مەيلى كېيىن بولسۇن، ئۇلارنى چوقۇم جازالايمىز)، ئاندىن اﷲ ئۇلارنىڭ قىلمىشلىرىغا گۇۋاھ بولىدۇ'),
(1411, 10, 47, '(ئۆتكەن ئۈممەتلەرنىڭ) ھەر بىرىگە پەيغەمبەر ئەۋەتىلگەن. (قىيامەت كۈنى) ئۇلارنىڭ پەيغەمبىرى كەلگەندە، ئۇلارنىڭ ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ'),
(1412, 10, 48, 'ئۇلار: «ئەگەر سىلەر راستچىل بولساڭلار (بىزگە ئېيتىپ بېرىڭلاركى) بۇ ۋەدە قاچان ئىشقا ئاشۇرۇلىدۇ؟» دەيدۇ'),
(1413, 10, 49, 'ئېيتقىنكى: «اﷲ خالىمىسىلا، مەن ئۆزۈمدىن زىياننى دەپئى قىلىشقا، پايدا قازىنىشقا قادىر ئەمەسمەن، ھەر ئۈممەتنىڭ (ھالاك بولىدىغان) مەلۇم ۋاقتى بولىدۇ، شۇ ۋاقىت يېتىپ كەلگەندە، ئۇلار بىردەممۇ كېچىكتۈرۈلمەيدۇ، بىردەممۇ ئىلگىرى سۈرۈلمەيدۇ»'),
(1414, 10, 50, 'ئېيتقىنكى: «ئېيتىڭلارچۇ! ئەگەر اﷲ نىڭ ئازابى سىلەرگە كېچىسى ياكى كۈندۈزى كەلسە (بۇنىڭدىن سىلەرگە نېمە پايدا؟) گۇناھكارلار (يەنى مۇشرىكلار) ئازابتىن قايسى نەرسىگە ئالدىرايدۇ؟'),
(1415, 10, 51, '(اﷲ نىڭ) ئازابى يۈز بەرگەندە ئاندىن ئۇنىڭغا ئىشەندىڭلارمۇ؟ ئەمدى (ئېيتقان ئىمانىڭلار پايدىسىزدۇر، ئۇنىڭدىن ئىلگىرى مەسخىرە قىلىش بىلەن) ئازابنىڭ تېزرەك چۈشۈشىنى تەلەپ قىلاتتىڭلار»'),
(1416, 10, 52, 'ئاندىن كېيىن (ئۆزلىرىگە) زۇلۇم قىلغانلارغا: «مەڭگۈلۈك ئازابنى تېتىڭلار، سىلەرگە پەقەت قىلمىشىڭلارنىڭ جازاسى بېرىلىدۇ» دېيىلىدۇ'),
(1417, 10, 53, 'ئۇلار سەندىن: «ئۇ (يەنى ۋەدە قىلىنغان ئازاب، ئۆلگەندىن كېيىن تىرىلىش) راستمۇ؟» دەپ سورايدۇ، سەن: «ھەئە، پەرۋەردىگارىم بىلەن قەسەمكى، ئۇ ئەلۋەتتە راستتۇر، سىلەر قېچىپ قۇتۇلالمايسىلەر» دېگىن'),
(1418, 10, 54, 'ئەگەر ھەر بىر زۇلۇم قىلغان ئادەم (يەنى كاپىر) يەر يۈزىدىكى ھەممە نەرسىگە مالىك بولىدىغان بولسا (اﷲ نىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن)، ئەلۋەتتە (ئۇنىڭ ھەممىسىنى) فىدىيە قىلىپ بىرىۋېتەتتى، ئۇلار ئازابنى كۆرگەن چاغدا نادامىتىنى يوشۇرۇن تۇتىدۇ (يەنى دۇنيادىكى قىلمىشلىرىغا ئىچىدە پۇشايمان قىلىدۇ). ئۇلارنىڭ (يەنى خالايىقنىڭ) ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ'),
(1419, 10, 55, 'راستلا ئاسمانلاردىكى ۋە زېمىندىكى بارلىق نەرسىلەر اﷲ نىڭ مۈلكىدۇر، راستلا اﷲ نىڭ ۋەدىسى ھەقتۇر، لېكىن ئۇلار (يەنى ئىنسانلار) نىڭ تولىسى (بۇنى) بىلمەيدۇ'),
(1420, 10, 56, 'اﷲ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنى) ئۆلتۈرىدۇ، سىلەر اﷲ نىڭ دەرگاھىغا قايتىسىلەر'),
(1421, 10, 57, 'ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن نەسىھەت بولغان، دىللاردىكى دەردكە (يەنى شەك ۋە نادانلىققا) شىپا بولغان، مۆمىنلەرگە ھىدايەت ۋە رەھمەت بولغان (قۇرئان) كەلدى'),
(1422, 10, 58, 'ئېيتقىنكى، «ئۇلار اﷲ نىڭ پەزلى ۋە رەھمىتىدىن خۇشال بولسۇن، بۇ ئۇلارنىڭ يىغقان (دۇنيا - ماللىرىدىن) ياخشىدۇر»'),
(1423, 10, 59, 'ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ! سىلەرگە اﷲ چۈشۈرگەن رىزىقتىن بەزىسىنى ھارام، بەزىسىنى ھالال قىلدىڭلار، اﷲ سىلەرگە مۇنداق قىلىشقا ئىزنى بەردىمۇ؟ ياكى اﷲ قا يالغاننى چاپلامسىلەر؟»'),
(1424, 10, 60, 'اﷲ قا يالغاننى چاپلايدىغانلار قىيامەت كۈنى قانداق ئويلايدۇ؟ (يەنى جازاغا تارتىلمايمىز، دەپ ئويلامدۇ؟) شۈبھىسىزكى، اﷲ ئىنسانلارغا مەرھەمەتلىكتۇر (چۈنكى ئۇلارغا بولىدىغان ئازابنى كېچىكتۈردى)، لېكىن كىشىلەرنىڭ تولىسى (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلمايدۇ'),
(1425, 10, 61, 'سەن قايسى ھالەتتە بولمىغىن، قۇرئاندىن قايسى نەرسىنى ئوقۇمىغىن، سىلەر قايسىبىر ئىشنى قىلماڭلار، ئۇنىڭ بىلەن بولۇۋاتقان ۋاقتىڭلاردا ھامان بىز سىلەرنى كۆزىتىپ تۇرىمىز، ئاسمان - زېمىندىكى زەررە چاغلىق نەرسە ۋە ئۇنىڭدىن كىچىك ياكى چوڭ نەرسە بولسۇن، ھېچقايسىسى اﷲ نىڭ بىلىشىدىن چەتتە قالمايدۇ، ئۇلارنىڭ ھەممىسى لەۋھۇلمەھپۇزدا خاتىرىلەنگەندۇر'),
(1426, 10, 62, 'راستلا اﷲ نىڭ دوستلىرىغا (ئاخىرەتتە اﷲ نىڭ ئازابىدىن) قورقۇش، (دۇنيادا قولدىن كەتكۈزۈپ قويغانغا) قايغۇرۇش يوقتۇر'),
(1427, 10, 63, 'ئۇلار ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلاردۇر'),
(1428, 10, 64, 'ئۇلارغا دۇنيا ۋە ئاخىرەتتە خۇش خەۋەر بېرىلىدۇ (يەنى دۇنيادا جان ئۈزۈش ۋاقتىدا، اﷲ نىڭ رازىلىقى ۋە رەھمىتىگە ئېرىشىدىغانلىقى بىلەن، ئاخىرەتتە نازۇنېمەتلىك جەننەتكە كىرىدىغانلىقى بىلەن خۇش خەۋەر بېرىلىدۇ)، اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ، ئەنە شۇ كاتتا بەختتۇر'),
(1429, 10, 65, '(ئى مۇھەممەد! ئۇلارنىڭ (ساڭا سەن پەيغەمبەر ئەمەسسەن دېگەن) سۆزى سېنى قايغۇغا سالمىسۇن، پۈتۈن كۈچ - قۇۋۋەت اﷲ قا مەنسۈپ (اﷲ ساڭا مەدەتكاردۇر). اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەمەللىرىنى) بىلىپ تۇرغۇچىدۇر'),
(1430, 10, 66, 'راستلا ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، بەندىسىدۇر، مەخلۇقاتىدۇر). اﷲ نى قويۇپ، بۇتلارغا چوقۇنىدىغانلار پەقەت يوقىلاڭ پەرەزگىلا تايىنىدۇ، ئۇلار پەقەت جۆيلۈيدۇ'),
(1431, 10, 67, 'ئى ئىنسانلار! ئارام ئېلىشىڭلار ئۈچۈن اﷲ سىلەرگە كېچىنى ياراتتى، نەرسىلەرنى كۆرۈشۈڭلار ئۈچۈن كۈندۈزنى ياراتتى، (ئىبرەت قۇلىقى بىلەن) ئاڭلايدىغان قەۋم ئۈچۈن بۇنىڭدا نۇرغۇن ئالامەتلەر بار'),
(1432, 10, 68, 'ئۇلار (يەنى كاپىرلار): «اﷲ نىڭ بالىسى بار» دېيىشتى، اﷲ (بۇنىڭدىن) پاكتۇر، بىھاجەتتۇر (يەنى بالا قىلىۋېلىش ئېھتىياج ئۈچۈن بولىدۇ، اﷲ بولسا ھېچ نەرسىگە موھتاج ئەمەستۇر)، ئاسمانلاردىكى، زېمىندىكى ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، (اﷲ نىڭ بالىسى بار دېگەن) بۇ سۆزۈڭلارغا ھېچقانداق پاكىتىڭلار يوق، سىلەر بىلمەيدىغان ئىشلارنى يالغاندىن اﷲ قا چاپلامسىلەر؟'),
(1433, 10, 69, '(ئى مۇھەممەد!) ئېيتقىنكى، «اﷲ قا يالغاننى چاپلايدىغانلار ھەقىقەتەن (دوزاخ ئازابىدىن) قۇتۇلالمايدۇ»'),
(1434, 10, 70, 'ئۇلار بۇ دۇنيادىن (ئازغىنا ۋاقىتلا) بەھرىمەن بولىدۇ، ئاندىن ئۇلار (ھېساب بېرىش ئۈچۈن) بىزنىڭ دەرگاھىمىزغا قايتىدۇ، ئاندىن كاپىر بولغانلىقلىرى سەۋەبىدىن ئۇلارغا قاتتىق ئازابنى تېتىتىمىز'),
(1435, 10, 71, 'ئۇلارغا نۇھنىڭ قىسسىسىنى ئوقۇپ بەرگىن، ئۆز ۋاقتىدا نۇھ قەۋمىگە: «ئى قەۋمىم! ئەگەر سىلەرگە مېنىڭ ئىچىڭلاردا تۇرۇشۇم ۋە سىلەرنى اﷲ نىڭ ئايەتلىرى بىلەن قورقۇتۇشۇم ئېغىر كەلگەن بولسا، مەن اﷲ قا تەۋەككۈل قىلدىم (يەنى اﷲ قا يۆلەندىم، سىلەرگە پەرۋا قىلمايمەن). سىلەر بۇتلىرىڭلار بىلەن بىرلىكتە (مېنى قەستلەيدىغان) ئىشىڭلارنى قارار قىلىۋېرىڭلار، ئىشىڭلار سىلەرگە تۇتۇق بولۇپ قالمىسۇن (يەنى مېنى قەستلەيدىغان ئىشىڭلار ئاشكارا بولسۇن)، ئاندىن مېنى بىردەممۇ كېچىكتۈرمەي، مېنىڭ ھەققىمدە (خالىغىنىڭلارنى) ئىجرا قىلىۋېرىڭلار'),
(1436, 10, 72, 'ئەگەر سىلەر (مېنىڭ نەسىھىتىمدىن) يۈز ئۆرۈسەڭلار، سىلەردىن (يۈز ئۆرۈشۈڭلارغا سەۋەبچى بولىدىغان) ھەق سورىغىنىم يوق، مەن ئەجرىمنى پەقەت اﷲ تىن سورايمەن، مەن (اﷲ قا) بويسۇنغۇچىلاردىن بولۇشقا بۇيرۇلدۇم» دېدى'),
(1437, 10, 73, 'لېكىن ئۇلار نۇھنى يالغانچى قىلدى. ئۇنى ۋە ئۇنىڭ بىلەن كېمىدە بىللە بولغۇچى (مۆمىن) لەرنى قۇتقۇزدۇق، ئۇلارنى (يەنى بىز قۇتقۇزغان كىشىلەرنى) (غەرق بولغانلارنىڭ) ئورۇنباسارلىرى قىلدۇق، بىزنىڭ مۆجىزىلىرىمىزنى ئىنكار قىلغانلارنى (توپان بالاسى بىلەن) غەرق قىلدۇق، (ئى مۇھەممەد!) ئاگاھلاندۇرۇلغۇچىلار (يەنى پەيغەمبەرلىرىنى يالغانچى قىلغانلار) نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىن'),
(1438, 10, 74, 'نۇھتىن كېيىن نۇرغۇن پەيغەمبەرلەرنى ئۆزلىرىنىڭ قەۋمىگە ئەۋەتتۇق، (قەۋمىگە) ئۇلار روشەن مۆجىزىلەر بىلەن كەلدى، ئۇلار (يەنى پەيغەمبەرلەر كېلىشتىن) ئىلگىرى ئىنكار قىلىشقان نەرسىگە ئىمان ئېيتىشمىدى. (كۇفرىدا) ھەددىدىن ئاشقۇچىلارنىڭ دىللىرىنى ئەشۇنداق پېچەتلەيمىز'),
(1439, 10, 75, 'ئۇلاردىن كېيىن مۇسا بىلەن ھارۇننى پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا نۇرغۇن مۆجىزىلەر بىلەن ئەۋەتتۇق. ئۇلار (ئىمان ئېيتىشتىن) بويۇنتاۋلىق قىلدى. ئۇلار گۇناھكار قەۋم ئىدى'),
(1440, 10, 76, 'ئۇلارغا بىز تەرەپتىن ھەقىقەت نازىل بولغان چاغدا ئۇلار: «بۇ ھەقىقەتەن روشەن سېھىردۇر» دېدى'),
(1441, 10, 77, 'مۇسا: «سىلەرگە ھەقىقەت كەلسە ئۇنى سېھىر دەمسىلەر؟ ئەجەبا بۇ سېھىرمۇ؟ سېھىرگەرلەر مۇۋەپپەقىيەت قازىنالمايدۇ» دېدى'),
(1442, 10, 78, 'ئۇلار: «سەن بىزگە ئاتا - بوۋىلىرىمىزنىڭ دىنىدىن بىزنى ۋاز كەچتۈرۈش ۋە زېمىن (يەنى مىسىر زېمىنى) نىڭ سەلتەنىتىنى ئىككىڭلارنىڭ قىلىۋېلىش ئۈچۈن كەلدىڭمۇ؟ بىز ئىككىڭلارغا ئىشەنمەيمىز» دېدى'),
(1443, 10, 79, 'پىرئەۋن: «مېنىڭ ئالدىمغا بارلىق ئۇستا سېھىرگەرلەرنى ئېلىپ كېلىڭلار» دېدى'),
(1444, 10, 80, 'سېھىرگەرلەر يېتىپ كەلگەندە، مۇسا ئۇلارغا: «(ئارغامچا ۋە ھاسىلىرىڭلاردىن) تاشلىماقچى بولغىنىڭلارنى تاشلاڭلار» دېدى'),
(1445, 10, 81, 'ئۇلار تاشلىغاندا، مۇسا: «سىلەرنىڭ كۆرسەتكىنىڭلار سېھىردۇر، اﷲ چوقۇم ئۇنى بەربات قىلىدۇ، اﷲ بۇزغۇنچىلارنىڭ ئىشىنى ئەلۋەتتە تۈزىمەيدۇ» دېدى'),
(1446, 10, 82, 'گۇناھكارلار ياقتۇرمىغان تەقدىردىمۇ، اﷲ پاكىتلىرى ئارقىلىق ھەقىقەتنى ئىسپاتلايدۇ'),
(1447, 10, 83, 'مۇساغا پەقەت ئۆز قەۋمىدىن (يەنى بەنى ئىسرائىلنىڭ ئەۋلادىدىن) ئازغىنىسى پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىنىڭ زىيانكەشلىك قىلىشىدىن قورققان ھالدا ئىمان ئېيتتى. پىرئەۋن زېمىندا (يەنى مىسىر زېمىنىدا) شەك - شۈبھىسىز مۇتەكەببىردۇر، ئۇ ئەلۋەتتە ھەددىدىن ئاشقۇچىلاردىندۇر'),
(1448, 10, 84, 'مۇسا: «ئى قەۋمىم! ئەگەر اﷲ قا ئىمان ئېيتقان بولساڭلار، اﷲ قا بويسۇنغان بولساڭلار، ئۇنىڭغا تەۋەككۈل قىلىڭلار (يەنى ھەممە ئىشىڭلارنى اﷲ قا تاپشۇرۇڭلار، ھەرقانداق ئەھۋالدا اﷲ قا يۆلىنىڭلار)» دېدى'),
(1449, 10, 85, 'ئۇلار ئېيتتى: «اﷲ قا تەۋەككۈل قىلدۇق، پەرۋەردىگارىمىز، زالىم قەۋمنى بىزگە زىيانكەشلىك قىلىش ئىمكانىيىتىگە ئىگە قىلمىغىن'),
(1450, 10, 86, 'بىزنى رەھمىتىنىڭ بىلەن زالىم قەۋمدىن قۇتۇلدۇرغىن»'),
(1451, 10, 87, 'بىز مۇسا بىلەن ئۇنىڭ قېرىندىشىغا: «قەۋمىڭلار ئۈچۈن مىسىردا ئۆيلەر ياساڭلار، ئۆيلىرىڭلارنى ناماز ئوقۇيدىغان جاي قىلىڭلار، ناماز ئوقۇڭلار، مۆمىنلەرگە (دۈشمەنلىرى ئۈستىدىن غەلىبە قىلىدىغانلىقى بىلەن) خۇش خەۋەر بېرىڭلار» دەپ ۋەھىي قىلدۇق'),
(1452, 10, 88, 'مۇسا ئېيتتى: «ئى پەرۋەردىگارىمىز! كىشىلەرنى يولۇڭدىن ئازدۇرۇشى ئۈچۈن - ئى پەرۋەردىگارىمىز! - پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا ھاياتىي دۇنيادا زىننەت بۇيۇملىرىنى، تۈرلۈك مال - مۈلۈكلەرنى بەردىڭ، پەرۋەردىگارىمىز! ئۇلارنىڭ مال - مۈلۈكلىرىنى يوق قىلغىن، ئۇلارنىڭ دىللىرىنى قاتتىق قىلغىن، شۇنىڭ بىلەن ئۇلار ئىمان ئېيتماي قاتتىق ئازابنى كۆرسۇن»'),
(1453, 10, 89, 'اﷲ ئېيتتى: «ئىككىڭلارنىڭ دۇئاسى ئىجابەت قىلىندى، دەۋىتىڭلارنى داۋاملاشتۇرۇڭلار، نادانلارنىڭ يولىغا ئەگەشمەڭلار»'),
(1454, 10, 90, 'بىز ئىسرائىل ئەۋلادىنى دېڭىزدىن ئۆتكۈزۈۋەتتۇق، ئۇلارنى پىرئەۋن ۋە ئۇنىڭ ئەسكەرلىرى زۇلۇم ۋە زوراۋانلىق قىلىش يۈزىسىدىن قوغلىدى. پىرئەۋن غەرق بولىدىغان ۋاقىتتا «ئىمان ئېيتتىمكى، ئىسرائىل ئەۋلادى ئىمان ئېيتقان ئىلاھتىن غەيرىي ئىلاھ يوقتۇر، مەن مۇسۇلمانلاردىنمەن» دېدى'),
(1455, 10, 91, '(ئۇنىڭغا دېيىلدىكى) «(ھاياتتىن ئۈمىد ئۈزگىنىڭدە) ئەمدى (ئىمان ئېيتامسەن؟) ئىلگىرى اﷲ قا ئاسىيلىق قىلغان ۋە بۇزغۇنچىلاردىن بولغان ئىدىڭ'),
(1456, 10, 92, 'سەندىن كېيىنكىلەرگە ئىبرەت بولۇشۇڭ ئۈچۈن، بۈگۈن سېنىڭ جەسىتىڭنى قۇتقۇزىمىز (يەنى سېنىڭ جەسىتىڭنى دېڭىزدىن چىقىرىپ قويىمىز)». نۇرغۇن كىشىلەر، شەك - شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزدىن غاپىلدۇر'),
(1457, 10, 93, 'شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىنى (ئۇلارنىڭ دۈشمەنلىرىنى ھالاك قىلغاندىن كېيىن) ياخشى جايغا ئورۇنلاشتۇردۇق، ئۇلارنى شېرىن نەرسىلەر بىلەن رىزىقلاندۇردۇق، ئۇلارغا ئىلىم (يەنى اﷲ نىڭ ھۆكمىنى ئىچىگە ئالغان تەۋرات) كەلگەندىن كېيىن، ئۇلار (دىن ئىشىدا) ئاندىن ئىختىلاپ قىلىشتى. پەرۋەردىگارىڭ قىيامەت كۈنى، ئۇلار ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىدۇ'),
(1458, 10, 94, 'مۇبادا سەن ساڭا بىز نازىل قىلغان كىتابتىن شەكلىنىدىغان بولساڭ، سەندىن ئىلگىرى كىتاب ئوقۇغانلار (يەنى تەۋرات بىلەن ئىنجىل نازىل قىلىنغان يەھۇدىيلار ۋە ناسارالار) دىن سوراپ باققىن، شۈبھىسىزكى، ساڭا پەرۋەردىگارىڭ تەرىپىدىن ھەق (قۇرئان) كەلدى. سەن ھەرگىز شەك قىلغۇچىلاردىن بولمىغىن'),
(1459, 10, 95, 'سەن ھەرگىز اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغۇچىلاردىن بولمىغىن، (ئۇنداق بولساڭ) زىيان تارتقۇچىلاردىن بولۇپ قالىسەن'),
(1460, 10, 96, 'شۈبھىسىزكى، پەرۋەردىگارىڭنىڭ لەنىتىگە تېگىشلىك بولغانلار ئىمان ئېيتمايدۇ'),
(1461, 10, 97, 'ئۇلارغا پۈتۈن مۆجىزىلەر كۆرسىتىلگەن تەقدىردىمۇ، قاتتىق ئازابنى كۆرمىگىچە (ئىمان ئېيتمايدۇ)'),
(1462, 10, 98, '(ھالاك بولغان شەھەرلەر ئاھالىلىرى ئىچىدە) يۇنۇس قەۋمىدىن باشقىسىغا (ئازابنىڭ ئالامىتىنى كۆرۈپ) ئېيتقان ئىماننىڭ پايدىسى بولغىنى يوق، يۇنۇس قەۋمى ئىمان ئېيتقاندا، دۇنيا ھاياتىدا رەسۋا قىلىدىغان ئازابنى ئۇلارنىڭ ئۈستىدىن كۆتۈرۈۋەتتۇق، ئۇلارنى مەلۇم ۋاقىتقىچە (يەنى ئەجىلى يەتكەنگە قەدەر دۇنيادىكى شەيئىلەردىن) بەھرىمەن قىلدۇق'),
(1463, 10, 99, 'ئەگەر پەرۋەردىگارىڭ خالىسا ئىدى، ئەلۋەتتە، يەر يۈزىدىكى كىشىلەرنىڭ ھەممىسى ئىمان ئېيتاتتى، سەن كىشىلەرنى مۇسۇلمان بولۇشقا مەجبۇرلامسەن؟'),
(1464, 10, 100, 'ھەر قانداق كىشى پەقەت اﷲ نىڭ ئىرادىسى بولغاندىلا، ئاندىن ئىمان ئېيتىدۇ، (اﷲ نىڭ ئايەتلىرى ئۈستىدە) پىكىر يۈرگۈزمەيدىغانلارنى اﷲ ئازابقا گىرىپتار قىلىدۇ'),
(1465, 10, 101, 'ئېيتقىنكى، «ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى كۆزىتىڭلار. مۆجىزىلەر ۋە ئاگاھلاندۇرغۇچىلارنىڭ (يەنى پەيغەمبەرلەرنىڭ) ئىمان ئېيتمايدىغان قەۋمگە پايدىسى بولمايدۇ'),
(1466, 10, 102, 'ئۇلار پەقەت ئۆزلىرىدىن بۇرۇن ئۆتكەنلەرگە كەلگەن كۈنلەرنىڭ (يەنى ئازابنىڭ) (ئۆزلىرىگە كېلىشىنى) كۈتىدۇ». (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەر (زۇلۇمنىڭ ئاقىۋىتىنى) كۈتۈڭلار، مەنمۇ سىلەر بىلەن بىرلىكتە ھەقىقەتەن (سىلەرنىڭ ھالاك بولۇشۇڭلارنى) كۈتكۈچىمەن»'),
(1467, 10, 103, 'ئاندىن كېيىن پەيغەمبەرلىرىمىزنى، مۆمىنلەرنى (ئازابتىن) قۇتقۇزدۇق، مۆمىنلەرنى قۇتقۇزۇشقا مۇشۇنداق مەسئۇل بولىمىز'),
(1468, 10, 104, '(ئى مۇھەممەد! قەۋمىڭنىڭ مۇشرىكلىرىغا) ئېيتقىنكى، «ئى ئىنسانلار! ئەگەر سىلەر مېنىڭ دىنىمدىن گۇمانلىنىدىغان بولساڭلار، سىلەرنىڭ اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلارغا مەن چوقۇنمايمەن، لېكىن مەن سىلەرنى قەبزى روھ قىلىدىغان اﷲ قا ئىبادەت قىلىمەن، مەن مۆمىنلەردىن بولۇشقا بۇيرۇلدۇم»'),
(1469, 10, 105, 'يەنە (ماڭا): «باتىل دىنلاردىن بۇرۇلۇپ، توغرا دىنغا يۈزلەنگىن، مۇشرىكلاردىن بولمىغىن'),
(1470, 10, 106, 'اﷲ نى قويۇپ، ساڭا پايدا - زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە ئىبادەت قىلمىغىن، ئەگەر ئۇلارغا ئىبادەت قىلساڭ، چوقۇم (ئۆزۈڭگە) زۇلۇم سالغۇچىلاردىن بولىسەن'),
(1471, 10, 107, 'ئەگەر اﷲ ساڭا بىرەر زىيان - زەخمەت يەتكۈزۈشنى ئىرادە قىلسا، يالغۇز اﷲ تىن باشقا ئۇنى دەپئى قىلغۇچى بولمايدۇ، ئەگەر اﷲ ساڭا بىرەر ياخشىلىقنى ئىرادە قىلسا، اﷲ نىڭ پەزلىنى قايتۇرغۇچى بولمايدۇ، اﷲ پەزلىنى بەندىلىرىدىن خالىغان ئادەمگە يەتكۈزىدۇ، اﷲ (بەندىلىرىنىڭ گۇناھىنى) مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) مېھرىباندۇر» (دېيىلدى)'),
(1472, 10, 108, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن ھەق دىن كەلدى، كىمكى ھىدايەت تاپىدىكەن، ئۆزىنىڭ پايدىسى ئۈچۈن ھىدايەت تاپقان بولىدۇ، كىمكى ئازىدىكەن، ئۆزىنىڭ زىيىنى ئۈچۈن ئازغان بولىدۇ، مەن سىلەرگە ھامىي ئەمەسمەن»'),
(1473, 10, 109, '(ئى مۇھەممەد!) ساڭا نازىل قىلىنغان ۋەھيىگە ئەگەشكىن (يەنى ئۇنىڭغا ئۆزۈڭ ئەمەل قىلىپ، باشقىلارغا ئۇنى يەتكۈزگىن). اﷲ (سەن بىلەن ئۇلارنىڭ ئارىسىدا) ھۆكۈم چىقارغۇچە (دىنغا دەۋەت قىلىش يولىدىكى مۇشەققەتلەرگە) چىدىغىن، اﷲ ئەڭ ھەققانىي ھۆكۈم چىقارغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'ئەلىف، لام، را. بۇ، ھېكمەت بىلەن ئىش قىلغۇچى، ھەممىدىن خەۋەردار اﷲ تەرىپىدىن نازىل بولغان ئايەتلەرنىڭ تۈزۈلۈشى پۇختا، (ئەقىدە، ئەھكام ۋەز ۋە قىسسىلەر) تەپسىلىي بايان قىلىنغان كىتابتۇر'),
(1475, 11, 2, '(ئېيتقىنكى) «سىلەر پەقەت اﷲ قا ئىبادەت قىلىڭلار، شۈبھىسىزكى، مەن سىلەرگە اﷲ تەرىپىدىن ئەۋەتىلگەن، (كاپىر بولساڭلار سىلەرنى اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرغۇچىمەن، (ئىمان ئېيتساڭلار اﷲ نىڭ ساۋابى بىلەن سىلەرگە) خۇش خەۋەر بەرگۈچىمەن'),
(1476, 11, 3, 'سىلەر پەرۋەردىگارىڭلاردىن مەغپىرەت تەلەپ قىلىڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، اﷲ سىلەرنى مۇئەييەن مۇددەتكىچە (يەنى ئۆمرۈڭلار ئاخىرلىشىپ، ئەجىلىڭلار يەتكۈچە ھاياتىي دۇنيادىن) ئوبدان بەھرىمەن قىلىدۇ، ياخشى ئىش قىلغۇچىغا قىلغان ياخشىلىقنىڭ ساۋابىنى بېرىدۇ، ئەگەر (ئىماندىن) يۈز ئۆرۈسەڭلار، (ئازابقا دۇچار بولىسىلەر) سىلەرنىڭ قىيامەت كۈنىنىڭ چوڭ ئازابىغا قېلىشىڭلاردىن ئەنسىرەيمەن'),
(1477, 11, 4, '(ئۆلگەندىن كېيىن) اﷲ نىڭ دەرگاھىغا قايتىسىلەر، اﷲ ھەر نەرسىگە قادىردۇر»'),
(1478, 11, 5, 'راستتىنلا ئۇلار (يەنى كاپىرلار) (رەسۇلۇللاھ، قۇرئان ئوقۇۋاتقاندا ئۇنىڭ يېنىدىن ئۆتكەنلىرىدە) ئۇنىڭدىن يوشۇرۇش ئۈچۈن يۈز ئۆرۈيدۇ، راستلا ئۇلار كىيىملىرى بىلەن چۈمكىنىۋالغانلىرىدا، اﷲ ئۇلارنىڭ يوشۇرۇن ۋە ئاشكارا ئىشلىرىنى بىلىپ تۇرىدۇ، اﷲ ھەقىقەتەن دىللاردىكىنى بىلگۈچىدۇر'),
(1479, 11, 6, 'يەر يۈزىدىكى ھايۋانلارنىڭ ھەممىسىگە رىزىق بېرىشنى اﷲ (مەرھەمەت يۈزىسىدىن) ئۈستىگە ئالغان، اﷲ ئۇلارنىڭ تۇرار جايىنى ۋە (ئۆلگەندىن كېيىن) كۆمۈلىدىغان جاينى بىلىدۇ، ئۇلارنىڭ ھەممىسى روشەن كىتابقا (يەنى لەۋھۇلمەھپۇزغا يېزىلغاندۇر'),
(1480, 11, 7, 'اﷲ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە ياراتتى. (ئاسمان - زېمىننى يارتىشتىن ئىلگىرى) اﷲ نىڭ ئەرشى سۇ ئۈستىدە ئىدى. سىلەرنىڭ قايسىڭلارنىڭ ئەمەلى ياخشى ئىكەنلىكىنى سىناش ئۈچۈن اﷲ (ئۇلارنى ياراتتى). (ئى مۇھەممەد!) ئەگەر سەن (مەككە كۇففارلىرىغا): «سىلەر ئۆلگەندىن كېيىن چوقۇم تىرىلىسىلەر!» دېسەڭ، كاپىرلار چوقۇم ئېيتىدۇ: «بۇ (سۆزنى ئىچىگە ئالغان قۇرئان) پەقەت روشەن سېھىردۇر»'),
(1481, 11, 8, 'ئەگەر بىز ئۇلارغا نازىل بولىدىغان ئازابنى چەكلىك ۋاقىت كېچىكتۈرسەك، ئۇلار چوقۇم (مەسخىرە قىلىش يۈزىسىدىن): «ئازاب نېمىشقا چۈشمەيدۇ؟» دەيدۇ، راستلا ئۇلارغا اﷲ نىڭ ئازابى چۈشكەن كۈندە، ئۇلار قاچىدىغان يەر تاپالمايدۇ ئۇلار مەسخىرە قىلغان ئازاب ئۇلارنى قورشىۋالىدۇ'),
(1482, 11, 9, 'ئەگەر بىز بىرەر ئىنسانغا رەھمىتىمىزنى تېتىتساق (يەنى نېمىتىمىزنى بەرسەك)، ئاندىن ئۇنى ئۇنىڭدىن تارتىۋالساق، ئۇ چوقۇم (اﷲ نىڭ رەھمىتىدىن) ئۈمىدسىزلىنىپ كېتىدۇ، كۇفرانى نېمەت قىلىدۇ'),
(1483, 11, 10, 'ئەگەر بىز (پېقىرلىق، كېسەللىك، قاتتىقچىلىق قاتارلىق) بالالارغا مۇپتىلا قىلغاندىن كېيىن، ئۇنىڭغا نېمىتىمىزنى تېتىتساق، ئۇ چوقۇم: «مەندىن بالالار كۆتۈرۈلۈپ كەتتى (ئەمدى كەلمەيدۇ)» دەپ شەك شۈبھىسىز خۇشاللىنىپ، مەغرۇرلىنىپ كېتىدۇ'),
(1484, 11, 11, 'پەقەت (بالاغا ئۇچرىغاندا) سەۋرى قىلغان، (نېمەتكە يولۇققاندا) ياخشى ئىشلارنى قىلغان كىشىلەر (ياخشىلاردۇر). ئۇلارنىڭ گۇناھى مەغپىرەت قىلىنىدۇ، ئۇلار (ئاخىرەتتە) چوڭ مۇكاپاتقا ئېرىشىدۇ'),
(1485, 11, 12, '(ئى مۇھەممەد!) ئۇلارنىڭ (يەنى مۇشرىكلارنىڭ) «نېمىشقا ئۇنىڭغا بىر خەزىنە چۈشمىدى؟ ياكى ئۇنىڭ بىلەن بىللە (ئۇنى تەستىقلايدىغان) بىرەر پەرىشتە كەلمىدى؟» دەيدىغانلىقى ئۈچۈن، ساڭا نازىل قىلىنغان ۋەھيىنىڭ بىر قىسىمىنى يەتكۈزگۈڭ كەلمەيدىغاندۇ، يەتكۈزۈشتىن يۈرىكىڭ سىقىلىدىغاندۇ، سەن پەقەت (ئۇلارنى اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرغۇچىسىمەن، اﷲ ھەممە نەرسىنى تەسەررۇپ قىلغۇچىدۇر'),
(1486, 11, 13, 'ئۇلار قۇرئاننى (مۇھەممەد) ئۆزى ئىجاد قىلغان دېيىشەمدۇ؟ ئېيتقىنكى، «(بۇ سۆزۈڭلاردا) راستچىل بولساڭلار، اﷲ تىن غەيرىي چاقىرالايدىغانلىكى كىشىلىرىڭلارنى (ياردەمگە) چاقىرىپ، (پاساھەت ۋە بالاغەت جەھەتتىن) قۇرئاندىكىگە ئوخشايدىغان ئون سۆز ئىجاد قىلىپ بېقىڭلار»'),
(1487, 11, 14, 'ئەگەر ئۇلار (بۇ چاقىرىقىڭلارغا) ئاۋاز قوشمىسا، بىلىڭلاركى، قۇرئان اﷲ تىڭ ۋەھيى قىلىشى بىلەن نازىل قىلىنغان. اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر، سىلەر (اﷲ قا) بويسۇنامسىلەر؟'),
(1488, 11, 15, '(قىلغان ياخشى ئەمەللىرى بىلەن پەقەت) ھاياتىي دۇنيانى ۋە ئۇنىڭ زىبۇزىننىتىنى (يەنى دۇنيانىڭ نېمەتلىرىنى) كۆزلەيدىغان كىشىلەرگە ئۇلارنىڭ ئەمەللىرىنىڭ ئەجرىنى بۇ دۇنيادىلا تولۇق بېرىمىز، دۇنيادا ئۇلارنىڭ ئەجرىدىن ھېچ نەرسە كېمەيتىلمەيدۇ'),
(1489, 11, 16, 'بۇنداق كىشىلەرگە ئاخىرەتتە دوزاختىن باشقا نەرسە يوقتۇر، ئۇلارنىڭ قىلغان ئىشلىرى يوققا چىقىرىلىدۇ، قىلغان (ياخشى) ئەمەللىرى ھېسابقا ئېلىنمايدۇ'),
(1490, 11, 17, 'پەرۋەردىگارى تەرىپىدىن كەلگەن روشەن دەلىل (يەنى قۇرئان) غا ئاساسلانغان ئادەم (يەنى مۇھەممەد ئەلەيھىسسالام) (ھاياتىي دۇنيانى كۆزلەيدىغان كىشىلەر بىلەن ئوخشاشمۇ؟) ئۇنىڭغا اﷲ تەرىپىدىن كەلگەن گۇۋاھلىق بەرگۈچى (يەنى جىبرىئىل ئەلەيھىسسالام) قۇرئاننى تىلاۋەت قىلىدۇ، قۇرئاندىن ئىلگىرى مۇساغا نازىل قىلىنغان كىتاب (يەنى تەۋرات) (ئىسرائىل ئەۋلادىغا) يېتەكچى ۋە رەھمەت قىلىپ (نازىل قىلىندى)، ئەنە شۇنداق كىشىلەر قۇرئانغا (ھەقىقىي ئىشىنىدۇ، (كۇففار) جامائەلىرىدىن كىمكى قۇرئاننى ئىنكار قىلىدىكەن، ئۇنىڭغا ۋەدە قىلىنغان جاي دوزاختۇر، قۇرئانغا شەك كەلتۈرمىگىن، ئۇ ھەقىقەتەن پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان ھەقىقەتتۇر، لېكىن ئىنسانلارنىڭ تولىسى (قۇرئاننىڭ ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغانلىقىغا) ئىشەنمەيدۇ'),
(1491, 11, 18, '(اﷲ نىڭ شېرىكى بار، بالىسى بار دەپ) اﷲ قا يالغاننى چاپلىغان كىشىدىنمۇ زالىم كىشى بارمۇ؟ ئەنە شۇلار (قىيامەت كۈنى) پەرۋەردىگارىغا توغرىلىنىدۇ، گۇۋاھچىلار: «بۇلار پەرۋەردىگارى ھەققىدە يالغان سۆزلىگەنلەردۇر» دەيدۇ. راستلا اﷲ نىڭ لەنىتى زالىملارغا بولىدۇ (يەنى اﷲ زالىملارنى رەھمىتىدىن يىراق قىلىدۇ)'),
(1492, 11, 19, 'ئۇلار (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى ئىسلام يولىدىن) توسىدۇ، اﷲ نىڭ يولىنىڭ ئەگرى بولىشىنى تىلەيدۇ، ئۇلار ئاخىرەتنى ئىنكار قىلغۇچىلاردۇر'),
(1493, 11, 20, 'ئەنە شۇلار زېمىندا اﷲ نىڭ ئازابىدىن قېچىپ قۇتۇلالمايدۇ، ئۇلارغا اﷲ تىن باشقا (ئىگە بولىدىغان) ياردەمچىلەرمۇ بولمايدۇ، ئۇلار قاتمۇ قات ئازابقا دۇچار بولىدۇ، ئۇلار (ھەقنى) ئاڭلاشقا ۋە (ھەقنى) كۆرۈشكە قادىر بولالمايدۇ'),
(1494, 11, 21, 'ئەنە شۇلار ئۆزلىرىگە زىيان سالغۇچىلاردۇر. ئۇلارنىڭ ئويدۇرۇپ چىقارغان بۇتلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالدى'),
(1495, 11, 22, 'شەك يوقكى، ئۇلار ئاخىرەتتە ئەڭ زىيان تارتقۇچىلاردۇر'),
(1496, 11, 23, 'شۈبھىسىزكى، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار، پەرۋەردىگارىنىڭ ھۇزۇرىدا ئاجىزلىق بىلدۈرگەنلەر - ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە مەڭگۈ قالغۇچىلاردۇر'),
(1497, 11, 24, '(كاپىرلار ۋە مۆمىنلەردىن ئىبارەت) ئىككى پىرقە) ئەماغا ۋە گاسقا ئوخشايدۇ، (يەنە بىر پىرقە بولسا) كۆزى كۆرۈدىغان، قۇلىقى كۆرۈدىغان، قۇلىقى ئاڭلايدىغان ئادەمگە ئوخشايدۇ، ئۇلارنىڭ ئەھۋالى بىر - بىرىگە ئوخشامدۇ؟ ئىبرەت ئامامسىلەر؟'),
(1498, 11, 25, 'شۈبھىسىزكى، بىز نۇھنى قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، ئۇ: «مەن ھەقىقەتەن سىلەرگە ئوچۇق ئاگاھلاندۇرغۇچىمەن'),
(1499, 11, 26, 'بىر اﷲ تىن باشقىغا ئىبادەت قىلماڭلار، سىلەرنىڭ قاتتىق كۈننىڭ ئازابىغا ئۇچرىشىڭلاردىن قورقىمەن» دېدى'),
(1500, 11, 27, 'ئۇنىڭ قەۋمىنىڭ كاپىر كاتتىلىرى: «بىزنىڭچە، سەن پەقەت بىزگە ئوخشاش بىر ئادەمسەن، بىزنىڭچە، ئارىمىزدىكى پەس كىشىلەر يەڭگىللىك بىلەن ساڭا ئەگەشكەن، بىزنىڭچە، سىلەرنىڭ ھېچقانداق ئارتۇقچىلىقىڭلار يوق، بەلكى بىز سىلەرنى يالغانچى دەپ ئويلايمىز» دېدى'),
(1501, 11, 28, 'نۇھ ئېيتتى: «ئى قەۋمىم، ئېيتىڭلارچۇ، مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان (دەۋىتىمنىڭ توغرىلىقىنى ئىسپاتلايدىغان) ئېنىق دەلىلگە ئاساسلانسام ۋە ئۇنىڭ رەھمىتىگە (يەنى پەيغەمبەرلىككە) سازاۋەر بولغان بولسام، ئۇ دەلىل سىلەر ئۈچۈن مەخپىي بولسا، ئۇنى يامان كۆرسەڭلار، ئۇنى قوبۇل قىلىشقا سىلەرنى مەجبۇرلامدۇق؟'),
(1502, 11, 29, 'ئى قەۋمىم! سىلەرگە (دىننى) تەبلىغ قىلغانلىقىم ئۈچۈن سىلەردىن پۇل - مال تەلەپ قىلمايمەن، ماڭا ئەجىر بېرىشنى پەقەت اﷲ ئۆز ئۈستىگە ئالغان، مەن ئىمان ئېيتقان كىشىلەرنى (يېنىمدىن) قوغلىۋەتمەيمەن، (چۈنكى) ئۇلار، شۈبھىسىزكى، پەرۋەردىگارىغا مۇلاقات بولىدۇ، لېكىن مەن سىلەرنى نادان قەۋم كۆرۈمەن'),
(1503, 11, 30, 'ئى قەۋمىم! ئەگەر مەن ئۇلارنى قوغلىۋەتسەم، اﷲ نىڭ ئازابىدىن مېنى كىم قۇتقۇزالايدۇ؟ ئويلىنىپ باقمامسىلەر؟'),
(1504, 11, 31, 'مەن سىلەرگە، مەندە اﷲ نىڭ خەزىنىلىرى بار، دېمەيمەن، غەيبنى بىلىمەن دېمەيمەن، مەن ئەلۋەتتە (ئۆزۈمنى) پەرىشتە دەپمۇ ئېيتمايمەن ھەمدە سىلەر كۆزگە ئىلمايدىغان كىشىلەرنى اﷲ ئۇلارغا ياخشىلىق بەرمەيدۇ دېمەيمەن، اﷲ ئۇلارنىڭ دىللىرىدىكىنى ئوبدان بىلىدۇ، بولمىسا مەن چوقۇم زالىملاردىن بولۇپ قالىمەن»'),
(1505, 11, 32, 'ئۇلار (يەنى نۇھنىڭ قەۋمى): «ئى نۇھ! بىز بىلەن مۇنازىرلەشتىڭ، ناھايىتى كۆپ مۇنازىرلەشتىڭ. ئەگەر راست سۆزلىگۈچىلەردىن بولساڭ، بىزنى قورقۇتقان ئازابنى چۈشۈرۈپ باققىن» دېدى'),
(1506, 11, 33, 'نۇھ ئېيتتى: «ئۇنى (يەنى ئازابنى) ئەگەر خالىسا اﷲ لا چۈشۈرەلەيدۇ، سىلەر ھەرگىز قېچىپ قۇتۇلالمايسىلەر'),
(1507, 11, 34, 'مەن سىلەرگە قىلغان نەسىھىتىمنىڭ سىلەرگە پايدىسى بولمايدۇ، اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، سىلەر اﷲ نىڭ دەرگاھىغا قايتىسىلەر»'),
(1508, 11, 35, 'ياكى ئۇلار (يەنى قۇرەيشنىڭ كاپىرلىرى) قۇرئاننى ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئۆزى ئىجاد قىلدى دېيىشەمدۇ؟ «ئەگەر ئۇنى مەن ئىجاد قىلغان بولسام، ئۇ گۇناھىمغا ئۆزۈم مەسئۇل، مەن سىلەرنىڭ گۇناھىڭلاردىن ئادا - جۇدا مەن» دېگىن'),
(1509, 11, 36, 'نۇھقا (مۇنداق) ۋەھيى قىلىندى: «قەۋمىڭدىن ئىلگىرى ساڭا ئىمان ئېيتقانلاردىن باشقا يەنە ئىمان ئېيتقۇچىلار بولمايدۇ، ئۇلارنىڭ قىلمىشىدىن قايغۇرمىغىن'),
(1510, 11, 37, 'بىزنىڭ كۆز ئوڭىمىزدا ۋەھيىمىز بويىچە كېمە ياسىغىن، (ئۆزلىرىگە) زۇلۇم قىلغانلار توغرىسىدا ماڭا سۆز ئاچمىغىن (يەنى ئۇلارغا شاپائەت قىلمىغىن)، ئۇلار چوقۇم (توپان بالاسىدا) غەرق قىلىنىدۇ»'),
(1511, 11, 38, 'نۇھ كېمە ياسىدى، قەۋمنىڭ چوڭلىرى ئۇنىڭ ئالدىدىن ئۆتكەندە ئۇنى مەسخىرە قىلىشتى، نۇھ ئېيتتى: «ئەگەر سىلەر (بۈگۈن) بىزنى مەسخىرە قىلساڭلار (كېلەچەكتە سىلەر غەرق قىلىنغاندا)، سىلەر بىزنى مەسخىرە قىلغاندەك بىزمۇ سىلەرنى چوقۇم مەسخىرە قىلىمىز'),
(1512, 11, 39, 'خار قىلغۇچى ئازاب (يەنى توپان بالاسى بىلەن غەرق بولۇش) نىڭ كىمنىڭ ئۈستىگە كېلىدىغانلىقىنى، دائىمىي ئازاب (يەنى جەھەننەم ئازابى) نىڭ كىمگە چۈشىدىغانلىقىنى كېلەچەكتە بىلىسىلەر»'),
(1513, 11, 40, '(توپان بالاسى توغرىسىدىكى) بۇيرۇقىمىز يېتىپ كەلگەن ۋە يەر يۈزىدىن سۇ ئېتىلىپ چىققان چاغدا: «كېمىگە ھايۋاناتلاردىن (ئەركەك چىشى بولۇپ) بىر جۇپتىن ئېلىۋالغىن، ئائىلەڭدىكىلەر بىلەن ھالاك بولۇشى ھۆكۈم قىلىنغانلار (يەنى نۇھ ئەلەيھىسسالامنىڭ ئايالى ۋائىلە بىلەن ئوغلى كەنئان) بۇنىڭ سىرتىدا - ۋە ئىمان ئېيتقانلار بىلەن كېمىگە چىققىن» دېدۇق، ئۇنىڭ بىلەن پەقەت ئازغىنا كىشىلەر ئىمان ئېيتتى'),
(1514, 11, 41, 'نۇھ (ئىمان ئېيتقان تەۋەلىرىگە) ئېيتتى: «كېمىگە چىقىڭلار، كېمىنىڭ مېڭىشى ۋە توختىشى اﷲ نىڭ ئىسمى بىلەندۇر، شۈبھىسىزكى، مېنىڭ پەرۋەردىگارىم (تەۋبە قىلغۇچىلارنىڭ گۇناھىنى) ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر»'),
(1515, 11, 42, 'كېمە ئۇلارنى ئېلىپ تاغدەك دولقۇنلار ئىچىدە ئۈزۈپ باراتتى، نۇھ كېمە مېڭىشتىن بۇرۇن ئۆزىدىن يىراقتا تۇرغان ئوغلىنى: «ئى ئوغلۇم، بىز بىلەن بىللە كېمىگە چىققىن، كاپىرلار بىلەن بىللە بولمىغىن» دەپ توۋلىدى'),
(1516, 11, 43, 'ئۇ: «مېنى سۇدا غەرق بولۇشتىن ساقلاپ قالىدىغان بىر تاغنىڭ ئۈستىگە چىقىۋالىمەن» دېدى. نۇھ: «بۈگۈن اﷲ رەھىم قىلغان ئادەمدىن باشقا ھېچ ئادەم ئۇنىڭ جازالىشىدىن قۇتۇلۇپ قالالمايدۇ» دېدى. ئۇلارنىڭ ئارىسىنى دولقۇن ئايرىۋەتتى - دە، ئۇ غەرق بولۇپ كەتتى'),
(1517, 11, 44, '«ئى زېمىن سۈيۈڭنى يۇتقىن، ئى بۇلۇت تارقىغىن» دېيىلدى، سۇ سوغۇلدى، اﷲ نىڭ (كاپىرلارنى غەرق قىلىشتىن ئىبارەت) ھۆكمى ئىجرا قىلىندى، كېمە جۇدى تېغىنىڭ ئۈستىدە توختىدى، «زالىم قەۋم ھالاك بولسۇن» دېيىلدى'),
(1518, 11, 45, 'نۇھ پەرۋەردىگارىغا دۇئا قىلىپ: «پەرۋەردىگارىم! ئوغلۇم مېنىڭ ئائىلەمدىكىلەردىن ئىدى (سەن ماڭا ئۇلارنىڭ نىجات تېپىشىنى ۋەدە قىلغان ئىدىڭ)، سېنىڭ ۋەدەڭ ئەلۋەتتە ھەقتۇر، سەن ھەقىقەتەن ئەڭ توغرا ھۆكۈم قىلغۇچىسەن» دېدى'),
(1519, 11, 46, 'اﷲ ئېيتتى: «ئى نۇھ! ئۇ (مەن نىجات تېپىشىنى ۋەدە قىلغان) ئائىلەڭدىكىلەردىن ئەمەس، ئۇنىڭ ئەمەلى ياماندۇر، سەن ئېنىق بىلمىگەن نەرسەڭنى مەندىن سورىمىغىن، مەن سېنىڭ جاھىللاردىن بولماسلىقىڭنى نەسىھەت قىلىمەن»'),
(1520, 11, 47, 'نۇھ ئېيتتى: «پەرۋەردىگارىم! مەن بىلمىگەن نەرسىنى سوراشتىن سېنىڭ پاناھىڭغا سېغىنىمەن، ئەگەر ماڭا مەغپىرەت قىلمىساڭ، زىيان تارتقۇچىلاردىن بولىمەن»'),
(1521, 11, 48, 'ئېيتىلدى (يەنى اﷲ ئېيتتى): «ئى نۇھ! ساڭا، سەن بىلەن (كېمىدە) بىللە بولغانلارنىڭ بىر قىسىم ئەۋلادىغا بىز تەرەپتىن نازىل بولغان ئامانلىق ۋە بەرىكەتلەر بىلەن (كېمىدىن) چۈشكىن، ئۇلارنىڭ يەنە بىر قىسىم ئەۋلادىنى (ھاياتىي دۇنيادىن) بەھرىمەن قىلىمىز، ئاندىن ئۇلار بىزنىڭ قاتتىق ئازابىمىزغا دۇچار بولىدۇ»'),
(1522, 11, 49, '(ئى مۇھەممەد!) ئەنە شۇ (قىسسە) غەيبكە ئائىت خەۋەرلەردىندۇر، ساڭا ئۇنى ۋەھيى قىلدۇق، سەن ۋە سېنىڭ قەۋمىڭ بۇنىڭدىن ئىلگىرى ئۇنى بىلمەيتتىڭلار، سەۋر قىلغىن، شۈبھىسىزكى، ياخشى ئاقىۋەت تەقۋادارلارغا مەنسۇپتۇر'),
(1523, 11, 50, 'ئاد قەۋمىگە ئۇلارنىڭ قېرىندىشى ھۇدنى ئەۋەتتۇق، ھۇد ئېيتتى: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا مەبۇد (بەرھەق) يوقتۇر، سىلەر (اﷲ تىن غەيرىيگە چوقۇنۇش بىلەن) پەقەت (اﷲ قا) يالغاننى توقۇغۇچىلارسىلەر»'),
(1524, 11, 51, '«ئى قەۋمىم! مەن تەبلىغ ئۈچۈن سىلەردىن ئەجىر سورىمايمەن، ماڭا ئەجىر بېرىشنى پەقەت مېنى ياراتقان اﷲ ئۈستىگە ئالغان، سىلەر چۈشەنمەمسىلەر؟»'),
(1525, 11, 52, '«ئى قەۋمىم! پەرۋەردىگارىڭلاردىن مەغپىرەت تىلەڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، سىلەرگە ئۇ كۆپ يېغىن ياغدۇرۇپ بېرىدۇ، سىلەرگە تېخىمۇ كۈچ - قۇۋۋەت بېرىدۇ، سىلەر گۇناھقا چۆمگەن ھالدا (مېنىڭ دەۋىتىمدىن) يۈز ئۆرۈمەڭلار»'),
(1526, 11, 53, 'ئۇلار ئېيتتى: «ئى ھۇد! سەن بىزگە (ئۆزۈڭنىڭ راستلىقىڭنى كۆرسىتىدىغان) ھېچقانداق روشەن دەلىل كەلتۈرمىدىڭ، سېنىڭ سۆزۈڭ بىلەن بىز ئىلاھلىرىمىزنى تاشلىۋەتمەيمىز ۋە سېنىڭ (پەيغەمبەرلىكىڭگە) ئىشەنمەيمىز'),
(1527, 11, 54, 'بىز پەقەت سېنى بەزى ئىلاھلىرىمىز ساراڭ قىلىپ قويۇپتۇ دەيمىز» ھۇد ئېيتتى: «مەن ھەقىقەتەن اﷲ نى گۇۋاھ قىلىمەن، سىلەرمۇ گۇۋاھ بولۇڭلاركى، مەن سىلەرنىڭ اﷲ نى قويۇپ شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن ئادا - جۇدامەن، ھەممىڭلار ماڭا سۇيىقەست قىلىڭلار، ماڭا مۆھلەت بەرمەڭلار'),
(1528, 11, 55, 'بىز پەقەت سېنى بەزى ئىلاھلىرىمىز ساراڭ قىلىپ قويۇپتۇ دەيمىز» ھۇد ئېيتتى: «مەن ھەقىقەتەن اﷲ نى گۇۋاھ قىلىمەن، سىلەرمۇ گۇۋاھ بولۇڭلاركى، مەن سىلەرنىڭ اﷲ نى قويۇپ شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن ئادا - جۇدامەن، ھەممىڭلار ماڭا سۇيىقەست قىلىڭلار، ماڭا مۆھلەت بەرمەڭلار'),
(1529, 11, 56, 'شۈبھىسىزكى، مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان اﷲ قا مەن تەۋەككۈل قىلدىم، اﷲ نىڭ باشقۇرۇشىدا بولمىغان بىرمۇ مەخلۇق يوقتۇر؛ پەرۋەردىگارىم ھەقىقەتەن توغرا يولدىدۇر (يەنى ئادىل بولۇپ، ھېچ كىشىگە زۇلۇم قىلمايدۇ)'),
(1530, 11, 57, 'ئەگەر سىلەر (مېنىڭ دەۋىتىمنى قوبۇل قىلىشتىن) يۈز ئۆرۈسەڭلار (يۈز ئۆرۈگىنىڭلارنىڭ ماڭا زىيىنى يوق)، سىلەرگە مەن پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم (پەيغەمبەرنىڭ ۋەزىپىسى پەقەت تەبلىغ قىلىشتۇر)، پەرۋەردىگارىم (سىلەرنى ھالاك قىلىپ) ئورنۇڭلارغا باشقا بىر قەۋمنى كەلتۈرىدۇ، (شېرىك كەلتۈرۈش بىلەن) اﷲ قا قىلچە زىيان يەتكۈزەلمەيسىلەر، پەرۋەردىگارىم ھەقىقەتەن ھەربىر نەرسىنى كۈزىتىپ تۇرغۇچىدۇر»'),
(1531, 11, 58, 'بىزنىڭ (ئازابلاش) بۇيرۇقىمىز كەلگەندە، ھۇدنى ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنى مەرھەمەت قىلىپ قۇتقۇزدۇق، ئۇلارنى قاتتىق ئازابتىن خالاس قىلدۇق'),
(1532, 11, 59, 'ئەنە شۇ ئاد قەۋمى پەرۋەردىگارىنىڭ مۆجىزىلىرىنى ئىنكار قىلدى، ئۇنىڭ پەيغەمبەرلىكىگە ئاسىيلىق قىلدى، ھەربىر ئۇچىغا چىققان تەرسا، شەپقەتسىز (يەنى ھەققە قارشىلىق قىلىپ جېدەللەشكۈچى) نىڭ بۇيرۇقىغا ئەگەشتى'),
(1533, 11, 60, 'ئۇلارغا بۇ دۇنيادا لەنەت قىلىندى، قىيامەت كۈنىمۇ ئۇلارغا لەنەت قىلىنىدۇ، بىلىڭلاركى، ئاد قەۋمى ھەقىقەتەن پەرۋەردىگارىنى ئىنكار قىلدى. ئاگاھ بولۇڭلاركى، ھۇد قەۋمى بولغان ئاد ھالاك بولسۇن'),
(1534, 11, 61, 'سەمۇدقا ئۇلارنىڭ قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇ ئېيتتى: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ سىلەرنى زېمىندىن (يەنى تۇپراقتىن) ياراتتى، سىلەرنى زېمىندا تۇرغۇزدى، اﷲ تىن مەغپىرەت تىلەڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، پەرۋەردىگارىم ھەقىقەتەن يېقىندۇر، (دۇئانى) ئىجابەت قىلغۇچىدۇر»'),
(1535, 11, 62, 'ئۇلار: «ئى سالىھ! بۇنىڭدىن بۇرۇن سەن ئارىمىزدا ئۈمىد قىلىنغان كىشى ئىدىڭ (يەنى بۇ سۆزنى ئېيتىشىڭدىن بۇرۇن ئارىمىزدا چوڭ ئادەم بولۇپ قېلىشىڭنى ئۈمىد قىلاتتۇق)، بىزنى ئاتا - بوۋىلىرىمىز ئىبادەت قىلىپ كەلگەن بۇتلارغا ئىبادەت قىلىشتىن توسۇمسەن؟ شەك شۈبھىسىزكى، ھەقىقەتەن بىز سەن دەۋەت قىلغان ئىشقا زور گۇماندىمىز» دېيىشتى'),
(1536, 11, 63, 'سالىھ ئېيتتى: «ئى قەۋمىم! ئېيتىڭلارچۇ، ئەگەر مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان ئېنىق دەلىلگە ئاساسلانسام ۋە ئۇنىڭ رەھمىتى (يەنى پەيغەمبەرلىك) گە سازاۋەر بولسام، شۇنداق تۇرۇقلۇق اﷲ قا قارشىلىق قىلسام، اﷲ نىڭ ئازابىدىن مېنى كىم قۇتقۇزىدۇ؟ (مېنى ئۆزۈڭلارغا ئەگىشىشكە دەۋەت قىلىش بىلەن) ماڭا پەقەت زىياندىن باشقىسىنى زىيادە قىلالمايسىلەر'),
(1537, 11, 64, 'ئى قەۋمىم! بۇ اﷲ (بىۋاسىتە) ياراتقان چىشى تۆگە سىلەرگە مېنىڭ مۆجىزەمدۇر، ئۇنى اﷲ نىڭ زېمىنىغا قويۇۋېتىڭلار، ئىختىيارى ئوتلىسۇن، ئۇنىڭغا زىيان زەخمەت يەتكۈزمەڭلار، بولمىسا سىلەرنى پات كېلىدىغان ئازاب ھالاك قىلىدۇ»'),
(1538, 11, 65, 'ئۇلار چىشى تۆگىنى بوغۇزلىدى، سالىھ ئېيتتى: «ئۆيۈڭلاردا (ھاياتلىقتىن) ئۈچ كۈن بەھرىمەن بولۇۋېلىڭلار. بۇ ۋەدە يالغان ئەمەس»'),
(1539, 11, 66, 'بىزنىڭ (ئازاب قىلىش) ئەمرىمىز كەلگەندە، سالىھنى ۋە ئۇنىڭ بىلەن بىللە ئىمان ئېيتقانلارنى رەھمىتىمىز بىلەن شۇ كۈننىڭ شەرمەندىچىلىكىدىن قۇتقۇزدۇق، پەرۋەردىگارىڭ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر'),
(1540, 11, 67, 'زالىملارغا قاتتىق ئاۋاز يۈزلەندى - دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى'),
(1541, 11, 68, 'گويا ئۇلار ئۆيلىرىدە تۇرمىغاندەك (يەنى ئۇلارنىڭ تۇرغان جايلىرى ئادەمزات تەرىپىدىن ئاۋات قىلىنمىغاندەك) بولۇپ قالدى، بىلىڭلاركى، سەمۇد پەرۋەردىگارىنى ئىنكار قىلدى، سەمۇد ھالاك بولسۇن'),
(1542, 11, 69, 'شۈبھىسىزكى، بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلەر) ئىبراھىمغا خۇش خەۋەر ئېلىپ كەلدى. ئۇلار: «ساڭا ئامانلىق تىلەيمىز» دېدى. ئىبراھىم: «سىلەرگە ئامانلىق تىلەيمەن» دېدى. ئۇ ئۇزاققا قالماي بىر موزاينى كاۋاپ قىلىپ ئېلىپ كەلدى'),
(1543, 11, 70, 'ئۇلارنىڭ (يەنى پەرىشتىلەرنىڭ) ئۇنىڭغا قول ئۇزاتمايۋاتقانلىقىنى كۆرۈپ، ئۇلارنى خوش كۆرمىدى، ئۇلاردىن قورقۇنچ ھېس قىلدى، ئۇلار: «قورقمىغىن (بىز رەببىڭنىڭ پەرىشتىلىرى، تاماق يېمەيمىز)، بىز ھەقىقەتەن لۇتنىڭ قەۋمىنى (ھالاك قىلىش ئۈچۈن) ئەۋەتىلدۇق» دېدى'),
(1544, 11, 71, 'ئۇنىڭ ئايالى (يەنى سارە) بىر تەرەپتە تۇرۇپ (لۇت قەۋمىنىڭ ھالاك بولىدىغانلىقىدىن خۇشاللىنىپ) كۈلدى، بىز ئۇنىڭغا (يەنى سارەگە) ئىسھاق (ئاتلىق بالىسى بولىدىغانلىقى) بىلەن ۋە ئىسھاقتىن كېيىن كېلىدىغان يەئقۇب (ئاتلىق نەۋرىسى بولىدىغانلىقى) بىلەن خۇش خەۋەر بەردۇق'),
(1545, 11, 72, 'ئۇ (يەنى سارە): «ۋىيەي! مەن بىر موماي تۇرسام، ئېرىم بىر بوۋاي تۇرسا، تۇغامدىمەن؟ بۇ ھەقىقەتەن قىزىق ئىشقۇ!» دېدى'),
(1546, 11, 73, 'ئۇلار: «اﷲ نىڭ ئەمرىدىن (يەنى قۇدرىتىدىن) ئەجەبلىنەمسە؟ اﷲ نىڭ رەھمىتى ۋە بەرىكەتلىرى سىلەرنىڭ ئائىلەڭلارغا (يەنى ئىبراھىمنىڭ ئائىلىسىدىكىلەرگە) نازىل بولسۇن. اﷲ ھەقىقەتەن مەدھىيىلەشكە لايىقتۇر، ئۇلۇغلاشقا لايىقتۇر» دېدى'),
(1547, 11, 74, 'ئىبراھىمدىن قورقۇنچ يوقالغان ھەمدە خۇش خەۋەر ئۇنىڭغا يەتكەن چاغدا، ئۇ بىز بىلەن لۇتنىڭ قەۋمى توغرىسىدا مۇنازىرىلىشىشىگە كىرىشتى'),
(1548, 11, 75, 'ئىبراھىم بولسا ھەقىقەتەن كۆڭلى كۆكسى كەڭ، كۆپ ئاھ چەككۈچى (يەنى دىلى يۇمشاق)، (ھەممە ئىشتا) اﷲ قا مۇراجىئەت قىلغۇچى زاتتۇر'),
(1549, 11, 76, '(پەرىشتىلەر ئېيتتى) «ئى ئىبراھىم! بۇنى (يەنى لۇت قەۋمى توغرىسىدا مۇنازىرىلىشىشنى) قويغىن، شۈبھىسىزكى، پەرۋەردىگارىڭنىڭ (ئۇلارنى ھالاك قىلىش توغرىسىدىكى) ئەمرى ئاللىقاچان چۈشتى، ئۇلارغا چوقۇم (مۇنازىرىلىشىش ياكى شاپائەت قىلىش بىلەن) قايتۇرغىلى بولمايدىغان ئازاب چۈشىدۇ»'),
(1550, 11, 77, 'بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلەر) لۇتنىڭ يېنىغا كەلگەن چاغدا (لۇت ئۆز قەۋمىنىڭ ئۇلارغا چېقىلىپ قويۇشىدىن قورقۇپ) قىيىن ئەھۋالدا قالدى، ئۇلار توغرىسىدا يۈرىكى سىقىلدى ۋە: «بۇ بىر دىشۋار كۈندۇر» دېدى'),
(1551, 11, 78, 'لۇتنىڭ قەۋمى ئۇنىڭ قېشىغا ئالدىراپ كېلىشتى، ئۇلار بۇرۇنلا يامان ئىشلارنى قىلاتتى (يەنى لىۋاتەت قىلاتتى). لۇت ئېيتتى: «ئى قەۋمىم! مېنىڭ بۇ قىزلىرىم سىلەرگە ئەڭ پاكتۇر، اﷲ تىن قورقۇڭلار، مېھمانلىرىم ئۈستىدە مېنى رەسۋا قىلماڭلار، ئاراڭلاردا قەبىھ ئىشتىن توسىدىغان بىرەر كاللىسى جايىدا ئادەم يوقمۇ؟»'),
(1552, 11, 79, 'ئۇلار (لۇتقا) ئېيتتى: «قىزلىرىڭغا ھاجىتىمىز يوق ئىكەنلىكىنى سەن ئوبدان بىلىسەن، شۇنىڭدەك بىزنىڭ مەقسىتىمىزنىمۇ ئوبدان بىلىسەن»'),
(1553, 11, 80, 'لۇت ئېيتتى: «كاشكى مېنىڭ (سىلەرگە قارشى تۇرىدىغان) كۈچ قۇۋۋىتىم بولسا ئىدى، ياكى ئىلتىجا قىلىدىغان كۈچلۈك جەمەتىم بولسا ئىدى (چوقۇم سىلەرگە زەربە بەرگەن بولاتتىم)»'),
(1554, 11, 81, 'ئۇلار (يەنى پەرىشتىلەر): «ئى لۇت! شۈبھىسىزكى، بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىدۇرمىز، ئۇلار ھەرگىزمۇ ساڭا زىيان يەتكۈزەلمەيدۇ، ئائىلەڭدىكىلەرنى ئېلىپ كېچىنىڭ ئاخىرىدا (ئۇلارنىڭ ئىچىدىن) چىقىپ كەتكىن، ئايالىڭدىن باشقا، سىلەردىن ھېچ كىشى ئارقىسىغا قارىمىسۇن، ئۇ (يەنى ئايالىڭ) ئۇلار بىلەن بىرگە ھالاك بولغۇچىدۇر، شۈبھىسىزكى، ئۇلارغا ئازاب چۈشىدىغان چاغ تاڭ ۋاقتىدۇر، تاڭ يېقىن ئەمەسمۇ؟» دېدى'),
(1555, 11, 82, 'بىزنىڭ (جازالاش) ئەمرىمىز چۈشكەندە، ئۇلارنىڭ يۇرتلىرىنى ئاستىن - ئۈستۈن قىلىۋەتتۇق (يەنى كۆمتۈرۈۋەتتۇق)، ئۇ يۇرتلارغا بىز ئۈستى - ئۈستىلەپ ساپال تاشلارنى ياغدۇردۇق'),
(1556, 11, 83, 'ئۇ ساپال تاشلارغا پەرۋەردىگارىڭنىڭ دەرگاھىدا بەلگە سېلىنغان ئىدى، ئۇ يۇرتلار زالىملار (يەنى قۇرەيش كاپىرلىرى) دىن يىراق ئەمەس'),
(1557, 11, 84, 'مەديەن (ئەھلى) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇ ئېيتتى: «ئى قەۋمىم! اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۆلچەمدە ۋە تارازىدا كەم بەرمەڭلار، مەن سىلەرنى ھەقىقەتەن باي ھېسابلايمەن، مەن ھەقىقەتەن سىلەرنىڭ ھەممىنى قورشاپ تۇرغۇچى كۈن (يەنى قىيامەت كۈنى) نىڭ ئازابىغا دۇچار بولۇشۇڭلاردىن قورقىمەن'),
(1558, 11, 85, 'ئى قەۋمىم! سىلەر ئۆلچەمنى توغرا ئۆلچەڭلار، تارازىنى توغرا تارتىڭلار، كىشىلەرگە نەرسىلەرنى كەم بەرمەڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە - پاسات تېرىماڭلار'),
(1559, 11, 86, 'اﷲ سىلەرگە قالدۇرغان ھالال رىزىق (سىلەر يىغقان ھارامدىن) ياخشىدۇر، ئەگەر سىلەر مۆمىن بولساڭلار. مەن سىلەرگە مۇھاپىزەتچى ئەمەسمەن»'),
(1560, 11, 87, 'ئۇلار: «ئى شۇئەيب! سېنىڭ نامىزىڭ (يەنى دىنىڭ) سېنى ئاتا - بوۋىلىرىمىز ئىبادەت قىلىپ كېلىۋاتقان نەرسىلەرنى (يەنى بۇتلارنى) بىزنى تاشلاتقۇزۇشقا ياكى ماللىرىمىزنى خالىغىنىڭچە تەسەررۇپ قىلىشىمىزنى تەرك ئەتكۈزۈشكە بۇيرۇمدۇ؟ سەن تازىمۇ كۆڭلى - كۆكسى كەڭ، تازىمۇ توغرا يول تاپقان ئادەمسەن - دە» دېدى'),
(1561, 11, 88, 'شۇئەيب ئېيتتى: «ئى قەۋمىم! ئېيتىپ بېقىڭلارچۇ، پەرۋەردىگارىم ماڭا روشەن پاكىت (يەنى پەيغەمبەرلىك) ۋە كەڭتاشا ھالال رىزىق ئاتا قىلغان تۇرسا، (مەن شۇنداق نېمەتلەرنى ئاتا قىلغان پەرۋەردىگارىمنىڭ ئەمرىگە خىلاپلىق قىلامدىم؟) سىلەرگە قىلىش مەنئى قىلىنغان نەرسىنى ئۆزۈممۇ قىلىشنى خالىمايمەن، مەن پەقەت (سىلەرنى) قولۇمدىن كېلىشىچە تۈزەشنى خالايمەن، مەن پەقەت اﷲ نىڭ ياردىمى بولغاندىلا مۇۋەپپەقىيەت قازىنالايمەن، (ھەممە ئىشتا) اﷲ قا تايىنىمەن ۋە ئۇنىڭغا يۈزلىنىمەن'),
(1562, 11, 89, 'ئى قەۋمىم! سىلەرنىڭ ماڭا قارشى تۇرغانلىقىڭلار سىلەرنى نۇھنىڭ قەۋمى ياكى ھۇدنىڭ قەۋمى ۋە ياكى سالىھنىڭ قەۋمى دۇچار بولغان ئازابقا دۇچار قىلمىسۇن. لۇت قەۋمى (نىڭ دىيارى) سىلەر (نىڭ جايىڭلار) دىن يىراق ئەمەس'),
(1563, 11, 90, 'پەرۋەردىگارىڭلاردىن مەغپىرەت تەلەپ قىلىڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، مېنىڭ پەرۋەردىگارىم (تەۋبە قىلغۇچىلارغا) ھەقىقەتەن مەرھەمەتلىكتۇر، (ئۇلارنى) دوست تۇتقۇچىدۇر»'),
(1564, 11, 91, 'ئۇلار: «ئى شۇئەيب! بىز سېنىڭ ئېيتقانلىرىڭنىڭ نۇرغۇنىنى چۈشەنمەيمىز. بىز ئەلۋەتتە سېنى ئارىمىزدا كۈچسىز ئادەم دەپ قارايمىز، سېنىڭ قەۋم - قېرىنداشلىرىڭ بولمىغاندا ئىدى، بىز سېنى چوقۇم تاش - كېسەك قىلىپ ئۆلتۈرەتتۇق، سەن بىزگە ئەتىۋارلىق ئەمەسسەن» دېدى'),
(1565, 11, 92, 'شۇئەيب ئېيتتى: «ئى قەۋمىم! مېنىڭ قەۋم قېرىنداشلىرىم سىلەرگە اﷲ تىنمۇ ئەتىۋارلىقمۇ؟ سىلەر اﷲ نى ئارقاڭلارغا چۆرىۋەتتىڭلار (يەنى اﷲ قا ئىتائەت قىلمىدىڭلار ۋە اﷲ نى ئۇلۇغلىمىدىڭلار)، پەرۋەردىگارىم سىلەرنىڭ قىلمىشلىرىڭلاردىن ئەلۋەتتە خەۋەرداردۇر'),
(1566, 11, 93, 'ئى قەۋمىم! سىلەر ئۆز يولۇڭلار بويىچە ھەرىكەت قىلىڭلار، مەنمۇ ئەلۋەتتە ئۆز يولۇم بويىچە ھەرىكەت قىلاي! ئۇزاققا قالماي كىمنىڭ رەسۋا قىلغۇچى ئازابقا دۇچار بولىدىغانلىقى ۋە كىمنىڭ يالغانچى ئىكەنلىكىنى بىلىسىلەر، (ئىشىڭلارنىڭ ئاقىۋىتىنى) كۈتۈڭلار! شۈبھىسىزكى. مەنمۇ سىلەر بىلەن بىللە كۈتىمەن»'),
(1567, 11, 94, 'بىزنىڭ (ئۇلارنى ھالاك قىلىش) پەرمانىمىز چۈشكەن چاغدا، شۇئەيبنى ۋە ئۇنىڭ بىلەن بىللە ئىمان ئېيتقانلارنى مەرھەمەت قىلىپ قۇتۇلدۇردۇق، (ئۆزلىرىگە) زۇلۇم قىلغانلارغا قاتتىق ئاۋاز (دىن پەيدا بولغان زىلزىلە) يۈزلەندى، ئۇلار ئۆز ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى'),
(1568, 11, 95, 'گويا ئۇلار ئۆيلىرىدە تۇرمىغاندەك (يەنى ياشىمىغاندەك) بولۇپ قالدى. مەديەن ئاھالىسى خۇددى سەمۇد قەۋمى ھالاك بولغاندەك ھالاك بولسۇن!'),
(1569, 11, 96, 'بىز مۇسانى ھەقىقەتەن ئايەتلىرىمىز (يەنى اﷲ تىن نازىل بولغان دىنىي ئەھكاملار) ۋە روشەن دەلىللەر (يەنى مۆجىزىلەر) بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار پىرئەۋننىڭ بۇيرۇقىغا بويسۇندى، پىرئەۋننىڭ بۇيرۇقى توغرا ئەمەس ئىدى'),
(1570, 11, 97, 'بىز مۇسانى ھەقىقەتەن ئايەتلىرىمىز (يەنى اﷲ تىن نازىل بولغان دىنىي ئەھكاملار) ۋە روشەن دەلىللەر (يەنى مۆجىزىلەر) بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار پىرئەۋننىڭ بۇيرۇقىغا بويسۇندى، پىرئەۋننىڭ بۇيرۇقى توغرا ئەمەس ئىدى'),
(1571, 11, 98, 'پىرئەۋن قىيامەت كۈنى باشلامچىلىق بىلەن ئۆز قەۋمىنى دوزاخقا باشلاپ كىرىدۇ، ئۇ جاي نېمىدېگەن يامان!'),
(1572, 11, 99, 'ئۇلار بۇ دۇنيادىمۇ، قىيامەت كۈنىدىمۇ لەنەتكە ئۇچرايدۇ، ئۇلارغا بېرىلگەن (ئىككىلا دۇنيادا لەنەتكە ئۇچراشتىن ئىبارەت) بۇ سوغا نېمىدېگەن يامان!'),
(1573, 11, 100, 'مانا بۇلار (ئاھالىسىنى بىز ھالاك قىلغان) يۇرتلارنىڭ خەۋەرلىرىدۇر، ئۇنى ساڭا (ۋەھىيى ئارقىلىق) بايان قىلىمىز. ئۇ يۇرتلاردىن ئىزى قالغانلىرىمۇ بار (يەنى ئاھالىسى ھالاك قىلىنىپ، يۇرتلىرىنىڭ ئورنى قالدۇرۇلغانلىرىمۇ بار)، ئىزى قالمىغانلىرىمۇ بار (يەنى ئاھالىسى بىلەن قوشۇپ ئۆرۈۋېتىلگەن زىرائەتتەك ۋەيران قىلىنغانلىرىمۇ بار)'),
(1574, 11, 101, 'بىز ئۇلارغا زۇلۇم قىلمىدۇق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى. پەرۋەردىگارىڭنىڭ (ئۇلارنى ھالاك قىلىش) پەرمانى كەلگەندە، ئۇلارنىڭ اﷲ نى قويۇپ ئىبادەت قىلغان مەبۇدلىرى ھېچ نەرسىگە ئەسقاتمىدى، ئۇلارغا پەقەت زىيان سالدى'),
(1575, 11, 102, 'پەرۋەردىگارىڭ زالىم يۇرتلار (ئاھالىسى) نى ھالاك قىلسا، ئەنە شۇنداق ھالاك قىلىدۇ، اﷲ نىڭ ئازابى ھەقىقەتەن تولىمۇ قاتتىقتۇر'),
(1576, 11, 103, 'ئاخىرەت ئازابىدىن قورقىدىغان ئادەم بۇ (قىسسە) دىن، ئەلۋەتتە ئىبرەت ئالىدۇ، ئەنە شۇ كۈن پۈتۈن خالايىق (ھېساب بېرىش ئۈچۈن) يىغىلىدىغان كۈندۇر، ئەنە شۇ كۈن ھەممە ھازىر بولىدىغان كۈندۇر'),
(1577, 11, 104, 'ئۇنى (يەنى قىيامەت كۈنىنى) بىز مۇئەييەن مۇددەتكىچە تەخىر قىلىمىز'),
(1578, 11, 105, 'قىيامەت كۈنى بولغاندا، ھەرقانداق ئادەم اﷲ تىن ئىجازەت بولغاندىلا سۆزلەيدۇ، ئۇلارنىڭ بەزىلىرى بەتبەخت بولىدۇ، بەزىلىرى سائادەتمەن بولىدۇ'),
(1579, 11, 106, 'بەتبەختلەرگە كەلسەك، ئۇلار دوزاخقا كىرىدۇ، ئۇلار دوزاختا (ئېشەك ھاڭرىغاندەك) توۋلاپ نالە - پەرياد چېكىدۇ'),
(1580, 11, 107, '(دوزاخنىڭ) ئاسمان - زېمىننى يوقالمايلا تۇرسا، ئۇلار دوزاختا پەرۋەردىگارىڭ خالىغان زامانغىچە داۋاملىق تۇرىۋېرىدۇ، پەرۋەردىگارىڭ ئەلۋەتتە خالىغىنىنى قىلغۇچىدۇر'),
(1581, 11, 108, 'سائادەتمەنلەرگە كەلسەك، ئۇلار جەننەتكە كىرىدۇ، (جەننەتنىڭ) ئاسمان - زېمىنى يوقالمايلا تۇرسا، ئۇلار جەننەتتە پەرۋەردىگارىڭ خالىغان زامانغىچە داۋاملىق تۇرىۋېرىدۇ (بۇ ئۇلارغا قىلىنغان) ئۈزۈلۈپ قالمايدىغان ئىنئامدۇر'),
(1582, 11, 109, 'ئۇلار (يەنى مۇشرىكلار) نىڭ بۇتلارغا چوقۇنۇىشىنىڭ (گۇمراھلىق) ئىكەنلىكىدىن شەكلەنمە، ئۇلار پەقەت ئۆزلىرىنىڭ بىز ساڭا قىسسىسىنى بايان قىلغان ئاتا - بوۋىلىرىغا ئوخشاشلا چوقۇنىدۇ، بىز ئۇلارنىڭ (ئازابتىن) تېگىشلىك نېسىۋىسىنى چوقۇم كېمەيتمەي بېرىمىز'),
(1583, 11, 110, 'شۈبھىسىزكى، بىز مۇساغا (يەنى تەۋراتنى) بەردۇق، ئۇنىڭ توغرىسىدا ئىختىلاپ قىلىندى (يەنى ئۇنىڭ قەۋمى بۇ كىتاب ئۈستىدە ئىختىلاپ قىلىشتى). پەرۋەردىگارىڭنىڭ ئالدىن چىقارغان (ئۇلارنى جازالاشنى قىيامەتكە قالدۇرۇش ھەققىدىكى) ھۆكمى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا (مۇشۇ دۇنيادىلا ياخشىغا ياخشىچە، يامانغا يامانچە) چوقۇم ھۆكۈم چىقىرىلاتتى. ھەقىقەتەن ئۇلار ئۇنىڭدىن (يەنى قۇرئاندىن) زور گۇماندىدۇر'),
(1584, 11, 111, 'پەرۋەردىگارىڭ ھەر بىر ئادەمگە ئۇنىڭ قىلغان ئەمەللىرىڭنىڭ مۇكاپاتىنى تولۇق بېرىدۇ، اﷲ، ئەلۋەتتە، ئۇلارنىڭ ئەمەللىرىدىن تولۇق خەۋەرداردۇر'),
(1585, 11, 112, '(ئى مۇھەممەد!) اﷲ سېنى بۇيرۇغاندەك توغرا يولدا بولغىن، ساڭا ئىمان ئېيتقانلارمۇ توغرا يولدا بولسۇن. (مەنئى قىلىنغان ئىشلارنى قىلىپ) اﷲ نىڭ چەكلىرىدىن چىقىپ كەتمەڭلار، شۈبھىسىزكى، اﷲ قىلمىشىڭلارنى بىلىپ تۇرغۇچىدۇر'),
(1586, 11, 113, 'زالىملارغا مايىل بولماڭلار، (مايىل بولساڭلار) دوزاخ ئازابىغا قالىسىلەر، سىلەرگە اﷲ تىن باشقا (دوزاختىن قۇتۇلدۇرىدىغان) ھېچ ياردەمچى يوقتۇر، ئاندىن كېيىن ھېچ ياردەمگە ئېرىشەلمەيسىلەر'),
(1587, 11, 114, 'كۈندۈزنىڭ ئىككى تەرىپىدە (يەنى ئەتىگەندە ۋە كەچقۇرۇن ۋاقىتلىرىدا) ۋە كېچىنىڭ دەسلەپكى ۋاقىتلىرىدا ناماز ئۆتىگىن. شۈبھىسىزكى، ياخشى ئىشلار ئارقىلىق يامان ئىشلار يۇيۇلىدۇ. بۇ چۈشەنگۈچىلەر ئۈچۈن ۋەز - نەسىھەتتۇر'),
(1588, 11, 115, '(ئى مۇھەممەد! مۇشرىكلاردىن يەتكەن ئەزىيەتلەرگە) سەۋر قىلغىن، اﷲ ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ'),
(1589, 11, 116, 'سىلەردىن بۇرۇن ئۆتكەن ئۈممەتلەرنىڭ ئارىسىدا نېمىشقا (يامانلارنى) يەر يۈزىدە بۇزۇقچىلىق قىلىشتىن توسىدىغان ئەقىل ئىلگىرى بولمىدى؟ ئۇلار ئارىسىدىن بۇزۇقچىلىقنى توسۇپ بىزنىڭ نىجاتلىقىمىزغا ئېرىشكەن ئازغىنا كىشىلەر بۇنىڭدىن مۇستەسنا. ئۆزلىرىگە زۇلۇم قىلغۇچىلار ئەيش - ئىشرەتلىك تۇرمۇشنى قوغلىشىدۇ. ئۇلار گۇناھكار ئادەملەردۇر'),
(1590, 11, 117, 'پەرۋەردىگارىڭ ئاھالىسى ياخشى بولغان يۇرتلارنى زۇلۇم سېلىپ ھالاك قىلمايدۇ'),
(1591, 11, 118, 'ئەگەر پەرۋەردىگارىڭ خالىسا ئىدى، پۈتۈن ئادەملەرنى ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، ئۇلار (دىن توغرىسىدا) داۋاملىق ئىختىلاپ قىلىشقۇچىلاردۇر، پەقەت پەرۋەردىگارىڭنىڭ رەھمىتىگە ئېرىشكەنلەر بۇنىڭدىن مۇستەسنا. اﷲ ئۇلارنى شۇنىڭ ئۈچۈن (يەنى ئىختىلاپ ئۈچۈن) ياراتتى. پەرۋەردىگارىڭنىڭ: «مەن چوقۇم پۈتۈن جىنلار ۋە ئىنسانلار بىلەن دوزاخنى توشقۇزىمەن» دېگەن سۆزى ئېنىق بەلگىلەندى'),
(1592, 11, 119, 'ئەگەر پەرۋەردىگارىڭ خالىسا ئىدى، پۈتۈن ئادەملەرنى ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، ئۇلار (دىن توغرىسىدا) داۋاملىق ئىختىلاپ قىلىشقۇچىلاردۇر، پەقەت پەرۋەردىگارىڭنىڭ رەھمىتىگە ئېرىشكەنلەر بۇنىڭدىن مۇستەسنا. اﷲ ئۇلارنى شۇنىڭ ئۈچۈن (يەنى ئىختىلاپ ئۈچۈن) ياراتتى. پەرۋەردىگارىڭنىڭ: «مەن چوقۇم پۈتۈن جىنلار ۋە ئىنسانلار بىلەن دوزاخنى توشقۇزىمەن» دېگەن سۆزى ئېنىق بەلگىلەندى'),
(1593, 11, 120, 'كۆڭلۈڭنى خاتىرجەم قىلىش ئۈچۈن، ساڭا پەيغەمبەرلەرنىڭ قىسسىلىرىنى بايان قىلىپ بېرىمىز، بۇ قىسسىلەر ھەقىقەتنى، مۆمىنلەر ئۈچۈن ۋەز - نەسىھەت، ئىبرەتلەرنى ئۆز ئىچىگە ئالغان'),
(1594, 11, 121, 'ئىمان ئېيتمايدىغانلارغا ئېيتقىنكى، «سىلەر ئۆز يولۇڭلار بويىچە ھەرىكەت قىلىڭلار، بىزمۇ ئۆز يولىمىز بويىچە ھەرىكەت قىلايلى'),
(1595, 11, 122, 'سىلەر (ئىشىڭلارنىڭ ئاقىۋىتىنى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە (شۇنى) كۈتىمىز»'),
(1596, 11, 123, 'ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلىش پەقەت اﷲ قا خاستۇر، ھەممە ئىش اﷲ قا قايتۇرۇلىدۇ، اﷲ قا ئىبادەت قىلغىن ۋە اﷲ قا تەۋەككۈل قىلغىن (يەنى ھەممە ئىشىڭنى اﷲ قا تاپشۇرغىن). پەرۋەردىگارىڭ قىلىۋاتقان ئىشلىرىڭلاردىن غاپىل ئەمەستۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'ئەلىف، لام، را. بۇ روشەن كىتاب ئايەتلىرىدۇر'),
(1598, 12, 2, 'شۈبھىسىزكى، سىلەرنى چۈشەنسۇن دەپ، ئۇنى ئەرەبچە قۇرئان قىلىپ نازىل قىلدۇق'),
(1599, 12, 3, 'بۇ قۇرئاننى ساڭا ۋەھيى قىلىش ئارقىلىق ئەڭ چىرايلىق قىسسىنى ساڭا بايان قىلىپ بېرىمىز، بۇنىڭدىن بۇرۇن بۇ قىسسىدىن ھەقىقەتەن بىخەۋەر ئىدىڭ'),
(1600, 12, 4, 'ئۆز ۋاقتىدا يۇسۇف ئۆز ئاتىسىغا: «ئى ئاتا! مەن ھەقىقەتەن چۈشۈمدە 11 يۇلتۇزنىڭ ۋە ئاي بىلەن كۈننىڭ ماڭا سەجدە قىلغانلىقىنى كۆردۈم» دېدى'),
(1601, 12, 5, 'ئاتىسى (يەنى يەئقۇب ئەلەيھىسسالام): «ئى ئوغلۇم! چۈشۈڭنى قېرىنداشلىرىڭغا ئېيتمىغىن، ساڭا سۇيىقەست قىلىپ قالمىسۇن، شەيتان ئىنسانغا ھەقىقەتەن ئاشكارا دۈشمەندۇر» دېدى'),
(1602, 12, 6, 'شۇنىڭغا (يەنى ساڭا ئۇلۇغ چۈش كۆرسەتكىنىگە) ئوخشاش، پەرۋەردىگارىڭ سېنى (پەيغەمبەرلىككە) تاللايدۇ، ساڭا چۈش تەبىرىنى بىلدۈرىدۇ، ئەجدادلىرىڭ ئىبراھىم، ئىسھاقلارغا ئىلگىرى اﷲ ئۆزىنىڭ ئۇلۇغ نېمىتىنى بەرگىنىدەك، ساڭىمۇ، يەئقۇب ئەۋلادىغىمۇ ئۆزىنىڭ ئۇلۇغ نېمىتىنى بېرىدۇ. شۈبھىسىزكى، پەرۋەردىگارىڭ (ئۆز مەرھەمىتىگە كىمنىڭ لايىق ئىكەنلىكىنى) ئوبدان بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1603, 12, 7, 'يۇسۇفنىڭ ۋە قېرىنداشلىرىنىڭ (قىسسىسىدە) سورىغۇچىلار ئۈچۈن اﷲ نىڭ قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ھەقىقەتەن نۇرغۇن ئالامەتلەر بار'),
(1604, 12, 8, 'ئۆز ۋاقتىدا ئۇلار: «يۇسۇف ۋە ئۇنىڭ قېرىندىشى (يەنى بۇنيامىن) ئاتىمىزغا بىزدىنمۇ سۆيۈملۈكتۇر، ھالبۇكى، بىز (كۈچلۈك) جامائەدۇرمىز، ئاتىمىز (نىڭ ئۇ ئىككىسىنى بىزدىن ئارتۇق كۆرۈشى) ئەلۋەتتە روشەن خاتادۇر» دېيىشتى'),
(1605, 12, 9, '(ئۇلار) «يۇسۇفنى ئۆلتۈرۋېتىڭلار، ياكى ئۇنى يىراق بىر چەت جايغا تاشلىۋېتىڭلار، (شۇ چاغدا) ئاتاڭلارنىڭ مۇھەببىتى سىلەرگە قالىدۇ، بۇ (گۇناھ) تىن كېيىن (تەۋبە قىلىپ) ياخشى ئادەم بولۇپ كېتىسىلەر» دېيىشتى'),
(1606, 12, 10, 'ئۇلاردىن بىرى سۆز قىلىپ: «يۇسۇفنى ئۆلتۈرمەڭلار، (ئەگەر ئۇنى ئاتىسىدىن يىراق قىلماقچى بولساڭلار، ئۇنى قۇدۇققا تاشلىۋېتىڭلار، ئۇنى يولدىن ئۆتكەن كارۋان ئېلىۋالسۇن» دېدى'),
(1607, 12, 11, 'ئۇلار ئېيتتى: «ئى ئاتىمىز! نېمىشقا يۇسۇف تورىسىدا بىزگە ئىشەنمەيسەن؟ ھالبۇكى، بىز ئۇنىڭغا ھەقىقەتەن ياخشى نىيەتتىمىز'),
(1608, 12, 12, 'ئەتە ئۇنى بىزگە قوشۇپ قويغىن، يەپ - ئىچىپ، ئويناپ - كۈلۈپ كىرسۇن، ئۇنى بىز چوقۇم ياخشى مۇھاپىزەت قىلىمىز»'),
(1609, 12, 13, 'يەئقۇب: «ئۇنى (يەنى يۇسۇفنى) ئېلىپ كەتسەڭلار راستلا خاتىرجەم بولالمايمەن، سىلەر غەپلەتتە قېلىپ، ئۇنى بۆرە يەپ قويۇشتىن ئەنسىرەيمەن» دېدى'),
(1610, 12, 14, 'ئۇلار: «بىز (كۈچلۈك) بىر جامائە تۇرۇقلۇق، ئۇنى بۆرىگە يېگۈزۈپ قويساق، ئۇ چاغدا بىز ئەلۋەتتە - زىيان تارتقۇچىلاردىن بولىمىز» دېدى'),
(1611, 12, 15, 'ئۇلار يۇسۇفنى ئېلىپ چىقىپ كەتكەن ۋە ئۇنى قۇدۇققا تاشلاشنى بىردەك قارار قىلىشقان چاغدا، بىز يۇسۇفكە: «كەلگۈسىدە ئۇلارغا بۇ ئىشنى (سېنىڭ يۇسۇف ئىكەنلىكىڭنى) ئۇلار ئۇقمايدىغان پەيتتە ئېيتقىن» دەپ ۋەھيى قىلدۇق'),
(1612, 12, 16, 'ئۇلار كەچتە ئاتىسىنىڭ ئالدىغا يىغلاپ كېلىشتى'),
(1613, 12, 17, 'ئۇلار: «ئى ئاتىمىز! يۇسۇفنى نەرسىلىرىمىزنىڭ يېنىدا قويۇپ يۈگۈرۈشكىلى كېتىپ قېلىپتۇق، ئۇنى بۆرە يەپ كېتىپتۇ، راست گەپ قىلساقمۇ سەن بىزگە ئىشەنمەيسەن» دېيىشتى'),
(1614, 12, 18, 'ئۇلار يۇسۇفنىڭ كۆڭلىكىنى يالغاندىن قانغا بوياپ ئېلىپ كېلىشكەن ئىدى. يەئقۇب: «بۇ ئىشنى نەپسىڭلار سىلەرگە چىرايلىق كۆرسىتىپ قىلغۇزۇپتۇ. مەن پەقەت چىرايلىقچە سەۋرە قىلىمەن، سىلەرنىڭ (مۇشۇ يالغان) سۆزۈڭلارنى كۆتۈرۈشۈمگە اﷲ مەدەتكاردۇر» دېدى'),
(1615, 12, 19, 'بۇ يەردىن بىر كارۋان ئۆتتى، ئۇلار سۇچىسىنى (سۇ ئېلىپ كېلىشكە) ئەۋەتتى، ئۇ سوغىسىنى قۇدۇققا تاشلىدى ۋە: «خۇش خەۋەر! مانا بىر ئوغۇل بالا» دېدى. ئۇلار ئۇنى تىجارەت مېلى سۈپتىدە مەخپىي تۇتۇشتى، ئۇلارنىڭ قىلمىشلىرىنى اﷲ بىلىپ تۇرغۇچىدۇر'),
(1616, 12, 20, 'ئۇلار يۇسۇفنى ساناقلىق بىر قانچە تەڭگىگە ئەرزان باھادا سېتىۋەتتى، چۈنكى ئۇلار يۇسۇفكە قىزىقمىغان ئىدى'),
(1617, 12, 21, 'ئۇنى سېتىۋالغان مىسىرلىق ئادەم ئايالىغا: «ئۇنى ياخشى كۈتكىن، بىزگە ئەسقاتار ياكى بالا قىلىۋالارمىز» دېدى. شۇنىڭدەك (يەنى يۇسۇفنى قۇدۇقتىن قۇتقۇزغىنىمىزدەك) يۇسۇفنى (مىسىر) زېمىنىغا يەرلەشتۈردۈق، ئۇنىڭغا چۈش تەبىرىنى ئۈگەتتۇق، اﷲ (خالىغان) ئىشىنى ئاشۇرۇشقا قادىردۇر، لېكىن ئىنسانلارنىڭ تولىسى (بۇنى) ئۇقمايدۇ'),
(1618, 12, 22, 'يۇسۇف قىرانىغا يەتكەندە، ئۇنىڭغا ھېكمەت بىلەن ئىلىم ئاتا قىلدۇق، ياخشى ئىش قىلغۇچىلارنى شۇنداق مۇكاپاتلايمىز'),
(1619, 12, 23, 'يۇسۇف تۇرۇۋاتقان ئۆيدىكى ئايال (يەنى زۈلەيخا) يۇسۇفتىن ئۆزى بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدى، ئۇ ئىشىكلەرنى تاقىۋېتىپ: «بېرى كەل» دېدى. يۇسۇف: «خۇدا پاناھ بەرسۇن! ئۇ (يەنى ئېرىڭ) ھەقىقەتەن مېنىڭ خوجايىنىمدۇر، ئۇ مېنى ياخشى كۈتتى. ئۆزىگە زۇلۇم قىلغۇچىلار ھەقىقەتەن نىجات تاپالمايدۇ» دېدى'),
(1620, 12, 24, 'ئۇ (يەنى زۈلەيخا) يۇسۇفكە (يېقىنچىلىق قىلىشقا) بەل باغلىدى، پەرۋەردىگارىنىڭ روشەن دەلىلىنى كۆرمىگەن بولسا، يۇسۇفمۇ ئۇنىڭغا مايىل بولغان بولاتتى. بىز يۇسۇفنى گۇناھتىن ۋە سەت ئىشتىن ئەنە شۇنداق ساقلىدۇق، شۈبھىسىزكى، يۇسۇف بىزنىڭ سادىق بەندىلىرىمىزدىن ئىدى'),
(1621, 12, 25, 'ئۇلارنىڭ ھەر ئىككىسى (يۇسۇف چىقىپ كېتىش ئۈچۈن، زۈلەيخا ئۇنى قايتۇرۇپ كېلىش ئۈچۈن) ئىشىككە قاراپ يۈگۈرۈشتى. (بۇ چاغدا) ئۇ (يەنى زۈلەيخا) يۇسۇفنىڭ كۆڭلىكىنى ئارقىسىدىن (تارتىپ) يىرتىۋەتتى، ئىشىك ئالدىدا ئۇنىڭ (يەنى زۈلەيخانىڭ) ئېرى بىلەن دوقۇرۇشۇپ قېلىشتى. ئۇ (يەنى زۈلەيخا): «سېنىڭ ئەھلىيەڭنى دەپسەندە قىلماقچى بولغان ئادەمنى پەقەت زىندانغا تاشلاش ياكى قاتتىق قىيناش بىلەن جازالاش كېرەك» دېدى'),
(1622, 12, 26, 'يۇسۇف: «ئۇ (يەنى زۈلەيخا) مەندىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدى» دېدى. ئۇ (يەنى زۈلەيخا) نىڭ ئائىلىسىدىكىلەردىن بىرسى گۇۋاھلىق بېرىپ: «يۇسۇفنىڭ كۆڭلىكى ئالدىدىن يىرتىلغان بولسا، ئۇنىڭ (يەنى زۈلەيخا) نىڭ سۆزى راست، يۇسۇفنىڭ سۆزى يالغان'),
(1623, 12, 27, 'يۇسۇفنىڭ كۆڭلىكى ئارقىسىدىن يىرتىلغان بولسا، ئۇ (يەنى زۈلەيخانىڭ) سۆزى يالغان يۇسۇفنىڭ سۆزى راست» دېدى'),
(1624, 12, 28, 'ئۇ (يەنى زۈلەيخانىڭ ئېرى) يۇسۇفنىڭ كۆڭلىكىنىڭ ئارقىسىدىن يىرتىلغانلىقىنى كۆرۈپ: «ئەي ئاياللار جامائەسى! بۇ چوقۇم سىلەرنىڭ ھىيلەڭلاردۇر، سىلەرنىڭ ھىيلەڭلار ھەقىقەتەن چوڭدۇر» دېدى'),
(1625, 12, 29, '(يەنى ئېيتتى) «يۇسۇف! بۇنى سۆزلەپ يۈرمىگىن. (ئايالىم) سەن ئۆز گۇناھىڭغا كەچۈرۈم سورىغىن، سەن ھەقىقەتەن خاتالاشتىڭ»'),
(1626, 12, 30, 'شەھەردىكى بەزى ئاياللار: «ئەزىز (يەنى مىسىرنىڭ پادىشاھسى) نىڭ خوتۇنى قۇلىغا كۆيۈپ قېلىپ، ئۇنىڭ بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلىپتۇ، بىز ئۇنى، ئەلۋەتتە، ئاشكارا ئازغان دەپ قارايمىز» دېيىشتى'),
(1627, 12, 31, 'ئۇ (يەنى زۈلەيخا) ئۇلارنىڭ گەپ - سۆزلىرىنى ئاڭلاپ، ئۇلارغا (مېھماندارچىلىققا چاقىرىپ) ئادەم ئەۋەتتى. ئۇلارغا (يۆلىنىدىغان) تەكىيە ياستۇقلارنى تەييارلىدى، ئۇلارنىڭ ھەربىرىگە بىردىن پىچاق بەردى. (زۈلەيخا يۇسۇفكە) «سەن چىقىپ ئۇلار بىلەن كۆرۈشكىن دېدى، ئۇلار يۇسۇفنى كۆرۈپ ھاڭ تاڭ بولۇپ قېلىشتى ۋە ئۆزلىرىنىڭ قوللىرىنى كېسىۋېلىشتى. ئۇلار: «(بۇنداق چىرايلىق ئىنساننى ياراتقان) اﷲ پاكتۇر، بۇ ئادەمزات ئەمەس، پەقەت بىر ئېسىل پەرىشتىدۇر» دېيىشتى'),
(1628, 12, 32, 'ئۇ (يەنى زۈلەيخا): «مانا بۇ سىلەر ئۇنىڭ ئۈچۈن مېنى ئەيىبلىگەن ھېلىقى ئادەم، مەن ئۇنىڭدىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدىم، لېكىن ئۇ ئۆزىنى چىڭ ساقلىدى. ئەگەر ئۇ يەنىلا بۇيرۇقۇمنى قىلمىسا، چوقۇم زىندانغا تاشلىنىدۇ، چوقۇم خار ئادەم بولۇپ قالىدۇ» دېدى'),
(1629, 12, 33, 'يۇسۇف: «ئى پەرۋەردىگارىم! ماڭا ئۇلار ئۈندىگەن نەرسىدىن كۆرە زىندان سۆيۈملۈكتۇر، ئەگەر ئۇلارنىڭ ھىيلىسىنى مەندىن دەپئى قىلمىساڭ، (ئىنسانچىلىقتا) ئۇلارغا مايىل بولۇپ قالىمەن، نادانلاردىن بولۇپ قالىمەن» دېدى'),
(1630, 12, 34, 'پەرۋەردىگارى ئۇنىڭ دۇئاسىنى ئىجابەت قىلىپ ئۇلارنىڭ ھىيلىسىنى يۇسۇفتىن دەپئى قىلدى. اﷲ (ئىلتىجا قىلغۇچىلارنىڭ دۇئاسىنى) ھەقىقەتەن ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر'),
(1631, 12, 35, 'ئاندىن ئۇلار (يۇسۇفنىڭ بىگۇناھ ئىكەنلىكىگە دائىر) پاكىتلارنى كۆرگەندىن كېيىن، ئۇنى بىرمەھەل قاماپ قويۇشنى لايىق تاپتى'),
(1632, 12, 36, 'زىندانغا يۇسۇف بىلەن ئىككى يىگىت بىللە كىرگەن ئىدى. ئۇلارنىڭ بىرى: «مەن چۈشۈمدە ئۆزۈمنى ھەقىقەتەن (ئۈزۈمدىن) ھاراق سىقىۋاتقان كۆرۈپتىمەن» دېدى. ئىككىنچىسى: «مەن چۈشۈمدە ئۆزۈمنى ھەقىقەتەن بېشىمدا نان كۆتۈرۈپ تۇرغان، ناننى قۇشلار يەۋاتقان كۆرۈپتىمەن، تەبىرىنى بىزگە ئېيتىپ بەرسەڭ، بىز سېنى ھەقىقەتەن ياخشىلىق قىلغۇچىلاردىن (يەنى چۈشكە تەبىر بېرەلەيدىغانلاردىن) دەپ قارايمىز» دېدى'),
(1633, 12, 37, 'يۇسۇف ئېيتتى: «ئىككىڭلارغا سىرتتىن تاماق كىرىشتىن بۇرۇن، ئۇنىڭ نېمە ئىكەنلىكىنى سىلەرگە ئېيتىپ بېرەلەيمەن، بۇ پەرۋەردىگارىم ماڭا بىلدۈرگەن نەرسىلەردىندۇر. شۈبھىسىزكى، مەن اﷲ قا ئىشەنمەيدىغان، ئاخىرەتنى ئىنكار قىلىدىغان قەۋمنىڭ دىنىنى تاشلىدىم'),
(1634, 12, 38, 'مەن ئەجدادلىرىم - ئىبراھىم، ئىسھاق، يەئقۇبلارنىڭ دىنىغا ئەگەشتىم، ھېچقانداق نەرسىنى اﷲ قا شېرىك كەلتۈرۈش بىزگە لايىق ئەمەس، بۇ اﷲ نىڭ بىزگە ۋە ئىنسانلارغا قىلغان مەرھەمىتىدۇر، لېكىن ئىنسانلارنىڭ تولىسى شۈكۈر قىلمايدۇ'),
(1635, 12, 39, 'تۈرمىداش ئاغىنىلەر! تارقاق كۆپ مەبۇدلار ياخشىمۇ؟ ياكى قۇدرەتلىك بىر اﷲ ياخشىمۇ؟'),
(1636, 12, 40, 'سىلەرنىڭ اﷲ نى قويۇپ ئىبادەت قىلىۋاتقىنىڭلار پەقەت سىلەر ئۆزۈڭلار ۋە ئاتا - بوۋاڭلار ئاتىۋالغان (بۇتلارنىڭ ۋە غەيرىينىڭ) ناملىرىدىنلا ئىبارەت، اﷲ (ئۇلارنىڭ اﷲ نىڭ شېرىكى بولۇشى توغرىسىدا) ھېچقانداق دەلىل چۈشۈرگىنى يوق، ئىبادەت ۋە دىن بارىسىدىكى بارچە ھۆكۈملەر پەقەت اﷲ غىلا مەنسۇپ، ئۇ سىلەرنى پەقەت ئۆزىگىلا ئىبادەت قىلىشقا بۇيرۇغان، بۇ توغرا دىندۇر. لېكىن كىشىلەرنىڭ تولىسى (بۇنى) بىلمەيدۇ'),
(1637, 12, 41, 'تۈرمىداش ئاغىنىلەر! سىلەرنىڭ بېرىڭلار (زىنداندىن چىقىپ) خوجايىنغا ھاراق قۇيۇپ بېرىدۇ (يەنى ساقىيلىق ۋەزىپىسىنى ئۆتەيدۇ)، يەنە بېرىڭلار دارغا ئېسىلىدۇ، كاللىسىنى قۇشلار يەيدۇ، سىلەر سورىغان ئىشلار ھۆكۈم قىلىنىپ بولدى»'),
(1638, 12, 42, 'يۇسۇف ئۇلارنىڭ ئىچىدىكى ئۆزىنىڭ قۇتۇلۇشىغا ئىشەنگەن بىرسىگە (يەنى ساقىيغا): «مېنى خۇجايىنىڭنىڭ ئالدىدا ئەسلەپ قويغىن» دېگەن ئىدى. خوجايىنىنىڭ ئالدىدا يۇسۇفنى ئەسلەپ قويۇشنى شەيتان ئۇنىڭغا ئۇنتۇدۇردى، يۇسۇف زىنداندا بىر نەچچە يىل يېتىپ قالدى'),
(1639, 12, 43, 'پادىشاھ ئېيتتى: «مەن ھەقىقەتەن چۈشۈمدە يەتتە ئورۇق كالىنىڭ يەتتە سېمىز كالىنى يەپكەتكەنلىكىنى ۋە يەتتە يېشىل باشاق بىلەن يەتتە قۇرۇق باشاقنى كۆرۈپتىمەن، ئى ئەربابلار! سىلەر ئەگەر چۈش تەبىرىنى بىلسەڭلار، بۇ چۈشۈمگە تەبىر بېرىپ بېقىڭلار»'),
(1640, 12, 44, 'ئۇلار ئېيتتى: «بۇ بىر قالايمىقان چۈش ئىكەن، بىز (مۇنداق) قالايمىقان چۈشكە تەبىر بېرىشنى ئۇقمايمىز»'),
(1641, 12, 45, 'ھېلىقى ئىككىسىدىن (تۈرمىدىن) قۇتۇلغان ۋە ئۇزاق مۇددەتتىن كېيىن (يۇسۇفنى) ئەسلىگەن بىرسى: «مەن سىلەرگە بۇ چۈشنىڭ تەبىرىنى ئېيتىپ بېرەلەيمەن، مېنى (يۇسۇفنىڭ يېنىغا) ئەۋەتىڭلار» دېدى'),
(1642, 12, 46, '(ئۇنى ئەۋەتىشتى، ئۇ تۈرمىگە بېرىپ يۇسۇفنىڭ يېنىغا كىردى - دە) «ئى راستچىل يۇسۇف! يەتتە ئورۇق كالىنىڭ يەتتە سېمىز كالىنى يەپ كەتكەنلىكىنى ۋە يەتتە يېشىل باشاق بىلەن يەتتە قۇرۇق باشاقنى (كۆرۈپ چۈشىگەن چۈشنىڭ تەبىرىنى) بىزگە ئېيتىپ بەرسەڭ، مەن ئۇنى كىشىلەرگە (يەنى پادىشاھقا ۋە ئۇنىڭ دۆلەت ئەربابلىرىغا) خەۋەر قىلسام، ئۇلار (سېنىڭ پەزلىڭنى) بىلسۇن» دېدى'),
(1643, 12, 47, 'يۇسۇف ئېيتتى: «يەتتە يىل ئۈزۈلدۈرمەي تېرىقچىلىق قىلىڭلار، ئالغا ھوسۇلۇڭلاردىن يېيىش ئۈچۈن ئازغىنىسىنى خامان تەپكەندىن تاشقىرى، قالغىنىنى (مىتە چۈشۈپ كەتمەسلىكى ئۈچۈن) باشىقى بىلەن قويۇڭلار'),
(1644, 12, 48, 'شۇنىڭدىن كېيىن قەھەتچىلىك بولىدىغان يەتتە يىل كېلىدۇ، (بۇ يىللاردا) ئىلگىرى توپلىۋالغان ئاشلىقىڭلاردىن (ئۇرۇقلۇق ئۈچۈن) ساقلاپ قويۇلىدىغان ئازغىنىسىدىن باشقا ھەممىسىنى يەپ تۈگىتىسىلەر'),
(1645, 12, 49, 'شۇنىڭدىن كېيىن (مولچىلىق) يىللار كېلىدۇ، ئۇنىڭدا ھۆل - يېغىن كۆپ بولىدۇ، كىشىلەر مېۋىلەردىن تۈرلۈك ئىچىملىكلەرنى سىقىپ چىقىرىدۇ»'),
(1646, 12, 50, '(ساقىي پادىشاھقا بېرىپ يۇسۇفنىڭ ئۇنىڭ چۈشىگە بەرگەن تەبىرىنى ئېيتقاندىن كېيىن) پادىشاھ: ئۇنى ئالدىمغا ئېلىپ كېلىڭلار» دېدى. (پادىشاھنىڭ) ئەلچىسى يۇسۇفنىڭ ئالدىغا بارغاندا، يۇسۇف ئەلچىگە: «خوجايىنىڭغا قايتىپ بېرىپ، ئۇنىڭدىن ھېلىقى قوللىرىنى كېسىۋالغان خوتۇنلارنىڭ ئىشىنى، (بىلەمدۇ؟) سوراپ باققىن، شۈبھىسىزكى مېنىڭ پەرۋەردىگارىم ئۇلارنىڭ ھىيلىسىنى ئوبدان بىلىدۇ» دېدى'),
(1647, 12, 51, 'پادىشاھ ھېلىقى خوتۇنلارنى يىغىپ ۋە ئۆزىنىڭ خوتۇنىنى چاقىرىپ): «سىلەر يۇسۇفتىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلغىنىڭلاردا، ھەقىقىي ئەھۋالىڭلار قانداق بولغان؟) دېدى. ئۇلار: «اﷲ پاكتۇر؛ يۇسۇفتە ئازراقمۇ گۇناھ بار دەپ بىلمەيمىز» دېدى. ئەزىزنىڭ خوتۇنى: «ئەمدى ھەقىقىي ئەھۋال ئايدىڭلاشتى، مەن ئۇنىڭ بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلغان ئىدىم، يۇسۇف ھەقىقەتەن راستچىللاردىن ئىدى» دېدى'),
(1648, 12, 52, '(يۇسۇف ئېيتتى) «بۇ (يەنى پادىشاھنىڭ ئەلچىسىنى قايتۇرۇۋەتكەنلىكىم) ئەزىز يوق چاغدا خوتۇنىغا خىيانەت قىلمىغانلىقىمنى ۋە خائىنلارنىڭ ھىيلىسىنى اﷲ نىڭ مۇۋەپپەقىيەتكە ئېرىشتۈرمەيدىغانلىقىنى ئەزىزگە بىلدۈرۈش ئۈچۈندۇر»'),
(1649, 12, 53, '«مەن ئۆزۈمنى ئاقلىمايمەن: نەپسى دېگەن نەرسە ھەقىقەتەن يامان ئىشلارغا كۆپ بۇيرۇيدۇ، پەرۋەردىگارىم رەھمەت قىلىپ (ساقلىغان) ئادەم بۇنىڭدىن مۇستەسنا. مېنىڭ پەرۋەردىگارىم ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر؛ ناھايىتى مېھرىباندۇر»'),
(1650, 12, 54, 'پادىشاھ: «يۇسۇفنى ئالدىمغا ئېلىپ كېلىڭلار، ئۇنى ئۆزۈمنىڭ خاس ئادىمىم قىلىمەن» دېدى. يۇسۇف ئۇنىڭ بىلەن سۆزلىشىۋاتقاندا، ئۇ: «سەن بۈگۈن بىزنىڭ ئالدىمىزدا ھەقىقەتەن مەرتىۋىلىك، ئىشەنچلىك ئادەمسەن» دېدى'),
(1651, 12, 55, 'يۇسۇف: «مېنى (مىسىر) زېمىنىنىڭ خەزىنىلىرىنى باشقۇرۇشقا قويغىن، مەن ھەقىقەتەن (ئۇنى باشقۇرۇش يوللىرىنى) بىلىدىغان، ئوبدان ساقلىيالايدىغان ئادەممەن» دېدى'),
(1652, 12, 56, 'بىز يۇسۇفنى (مىسىر زېمىنىدا خالىغان جايدا تۇرىدىغان (مەملىكەتنى خالىغانچە تەسەررۇپ قىلىدىغان) قىلىپ يەرلەشتۈردۇق، رەھمىتىمىزنى خالىغان بەندىلىرىمىزگە ئېرىشتۈرىمىز، ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيمىز'),
(1653, 12, 57, 'ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلارغا ئاخىرەتنىڭ ساۋابى ئەلۋەتتە، (بۇ دۇنيانىڭ نېمەتلىرىدىن) ئارتۇقتۇر'),
(1654, 12, 58, 'يۇسۇفنىڭ قېرىنداشلىرى كېلىپ يۇسۇفنىڭ قېشىغا كىردى. يۇسۇف ئۇلارنى تونۇدى، ئۇلار بولسا يۇسۇفنى تونۇمىدى'),
(1655, 12, 59, 'يۇسۇف ئۇلارغا ئېھتىياجلىق ئاشلىقنى تەييارلاپ بەرگەندىن كېيىن ئېيتتى: «سىلەر مېنىڭ ئالدىمغا ئاتا بىر ئۇكاڭلارنى (يەنى بۇنيامىننى) ئېلىپ كېلىڭلار، مېنىڭ (ئاشلىقنى) تولۇق ئۆلچەپ بېرىۋاتقانلىقىمنى ۋە ناھايىتى مېھماندوست ئىكەنلىكىمنى كۆرمىدىڭلارمۇ؟'),
(1656, 12, 60, 'ئەگەر ئۇنى مېنىڭ ئالدىمغا ئېلىپ كەلمىسەڭلار، سىلەرگە بېرىدىغان ئاشلىقىم يوق، مېنىڭ يېنىمغا يېقىن يولىماڭلار»'),
(1657, 12, 61, 'ئۇلار: «بىز ئۇنى ئاتىسىدىن سوراپ (سېنىڭ ئالدىڭغا ئېلىپ كېلىشكە) تىرىشىمىز، بىز چوقۇم مۇشۇنداق قىلىمىز» دېدى'),
(1658, 12, 62, 'يۇسۇف خىزمەتچىلىرىگە: «ئۇلارنىڭ (ئاشلىق سېتىۋالغان) مال - مۈلۈكلىرىنى يۈك - تاقىلىرىنىڭ ئارىسىغا سېلىپ قويۇڭلار، ئۇلار ئائىلىسىگە قايتىپ بارغاندا، ئۇنى تونۇپ قايتىپ كېلىشى مۇمكىن» دېدى'),
(1659, 12, 63, 'ئۇلار ئاتىسىنىڭ ئالدىغا قايتىپ بارغىنىدا: «ئى ئاتىمىز! (مىسىرنىڭ پادىشاھى) بىزگە قايتا ئاشلىق بەرمەس بولدى، ئۇكىمىزنى (يەنى بۇنيامىننى) بىز بىلەن بىللە ئەۋەتكىن، (شۇنداق قىلساڭ) ئاشلىق ئالالايمىز، ئۇنى بىز چوقۇم ئوبدان مۇھاپىزەت قىلىمىز» دېدى'),
(1660, 12, 64, '(يەئقۇب) ئۇلارغا ئېيتتى: «ئۇنىڭ توغرىسىدا ئىلگىرى سىلەرگە ئۇنىڭ قېرىندىشى (يەنى يۇسۇف) توغرىسىدا ئىشەنگەندەك ئىشىنەمدىم؟ (مەن سىلەرگە ۋە سىلەرنىڭ ئۇنى ساقلىشىڭلارغلا ئىشەنمەيمەن) اﷲ ياخشى ساقلىغۇچىدۇر، ھەممىدىن مېھرىباندۇر»'),
(1661, 12, 65, 'ئۇلار يۈكلىرىنى ئاچقاندا (ئاشلىق سېتىۋالغان) مال مۈلۈكلىرىنىڭ ئۆزلىرىگە قايتۇرۇپ بېرىلگەنلىكىنى كۆردى، ئۇلار: «ئاتىمىز! بىز (بۇنىڭدىن ئارتۇق) يەنە نېمە تەلەپ قىلىمىز؟ بۇ مال - مۈلۈكلىرىمىز بىزگە قايتۇرۇپ بېرىلىپتۇ، (ئۇكىمىزنى ئېلىپ يەنە بارساق) ئائىلىمىزگە ئاشلىق ئېلىپ كېلىمىز، ئۇكىمىزنى مۇھاپىزەت قىلىمىز، بىر تۆگە ئاشلىقىنى ئارتۇق ئالىمىز، بۇ (يەنى بىر تۆگە ئاشلىق پادىشاھ ئۈچۈن) ئازغىنا ئاشلىقتۇر» دېدى'),
(1662, 12, 66, 'يەئقۇب ئېيتتى: «بۇنيامىننى قوغداشتا ھەممىڭلار ھالاك بولمىساڭلارلا چوقۇم ماڭا قايتۇرۇپ ئېلىپ كېلىشكە اﷲ نىڭ نامى بىلەن قەسەم قىلىپ چىڭ ۋەدە بەرمىگۈچە ئۇنى سىلەر بىلەن بىللە ئەۋەتمەيمەن». ئۇلار (يەنى ئوغۇللىرى) ئۇنىڭغا قەسەم قىلىپ چىن ۋەدە بەرگەندىن كېيىن، يەئقۇب: «بىزنىڭ ئېيتقان سۆزلىرىمىزگە اﷲ گۇۋاھتۇر» دېدى'),
(1663, 12, 67, '(ئۇ يەنە) «ئى ئوغۇللىرىم! (مىسىرغا) ھەممىڭلار بىر دەرۋازىدىن كىرمەي، باشقا باشقا دەرۋازىلاردىن كىرىڭلار. اﷲ نىڭ قازاسى ئالدىدا مەن سىلەردىن ھېچ نەرسىنى دەپئى قىلالمايمەن، ھۆكۈم پەقەت اﷲ غىلا خاس، اﷲ قا تەۋەككۈل قىلدىم، تەۋەككۈل قىلغۇچىلار اﷲ غىلا تەۋەككۈل قىلسۇن!» دېدى'),
(1664, 12, 68, 'ئۇلار ئاتىسىنىڭ بۇيرۇقى بويىچە باشقا - باشقا دەرۋازىلاردىن كىرگەندە، (بۇ) اﷲ نىڭ قازاسى ئالدىدا ئۇلار ئۈچۈن ھېچ نەرسىگە دال بولالمىدى، بۇ پەقەت يەئقۇبنىڭ كۆڭلىدىكى ئۈمىدىنىلا ئىپادىلىدى، خالاس. بىز يەئقۇبقا (ۋەھيى ئارقىلىق بىلدۈرگەن ئىدۇق، شۇڭا ئۇ (كەڭ بىلىم ئىگىسىدۇر، لېكىن كىشىلەرنىڭ تولىسى (تەقدىرنىڭ سىرىنى) بىلمەيدۇ'),
(1665, 12, 69, 'ئۇلار (يەنى يەئقۇبنىڭ بالىلىرى) يۇسۇفنىڭ ئالدىغا كىرگەندە، يۇسۇف ئۇكىسىنى قۇچاقلىدى ۋە: «مەن ھەقىقەتەن سېنىڭ قېرىندىشىڭ بولىمەن، ئۇلارنىڭ بىزگە قىلغان ئىشلىرىدىن قايغۇرمىغىن» دېدى'),
(1666, 12, 70, 'يۇسۇف ئۇلارنى ئېھتىياجلىق ئاشلىق بىلەن تەمىنلىگەندىن كېيىن، قەدەھنى ئۇكىسىنىڭ يۈكى ئىچىگە سېلىپ قويدى. ئاندىن بىر جاكاچى چىقىپ: «ئى كارۋانلار! سىلەر چوقۇم ئوغرى ئىكەنسىلەر» دەپ توۋلىدى.'),
(1667, 12, 71, '(كارۋاندىكىلەر) ئۇلارغا قاراپ: «نېمەڭلارنى يوقىتىپ قويدۇڭلار» دېدى'),
(1668, 12, 72, 'ئۇلار: «پادىشاھنىڭ (تامغىسى بېسىلغان) قەدەھنى يوقىتىپ قويدۇق، ئۇنى تېپىپ كەلگەن كىشىگە (مۇكاپات ئۈچۈن) بىر تۆگە ئاشلىق بېرىلىدۇ، مەن بۇنىڭغا كېپىل» دېدى'),
(1669, 12, 73, 'ئۇلار: اﷲ نىڭ نامى بىلەن قەسەمكى، سىلەر بىلىسىلەر، بىز بۇ يەرگە بۇزغۇنچىلىق قىلىش ئۈچۈن كەلمىدۇق، بىز ئوغرى ئەمەسمىز» دېدى'),
(1670, 12, 74, 'ئۇلار (يەنى يۇسۇفنىڭ خىزمەتچىلىرى): «سىلەرنىڭ شەرىئىتىڭلاردا ئوغرىنىڭ جازاسى نېمە؟ (ئەگەر قەدەھ يۈكۈڭلاردىن چىقىپ قېلىپ) يالغانچى بولۇپ قالساڭلارچۇ؟) دېدى'),
(1671, 12, 75, 'ئۇلار: «ئۇنىڭ جازاسى قەدەھ بىلەن تېپىلغان ئادەمنى قۇل قىلىش، زالىملارغا بىز مۇشۇنداق جازا بېرىمىز» دېدى'),
(1672, 12, 76, 'ئۇلار يۇسۇفنىڭ ئۇكىسىنىڭ يۈكىنى ئاختۇرۇشتىن ئىلگىرى، ئۇلارنى يۈكىنى ئاختۇرۇشقا باشلىدى، ئاندىن يۇسۇفنىڭ ئۇكىسىنىڭ يۈكىدىن قەدەھنى تېپىپ چىقتى. بىز يۇسۇفكە مۇشۇ تەدبىرنى كۆرسەتتۇق، (مىسىر) پادىشاھىنىڭ قانۇنى بويىچە يۇسۇف ئۇكىسىنى ئېلىپ قالالمايتى، لېكىن اﷲ ئۇنىڭ شۇنداق قىلىشىنى خالىدى، بىز خالىغان ئادەمنى (يۇسۇفنى كۆتۈرگەندەك) يۇقىرى دەرىجىلەرگە كۆتۈرىمىز، ھەربىر بىلىمدار ئۈستىدە ئۇنىڭدىنمۇ بىلىمدار زات بار'),
(1673, 12, 77, 'ئۇلار: «ئەگەر ئۇ، ئوغرىلىق قىلغان بولسا، ئۇنىڭ قېرىندىشىمۇ (يەنى يۇسۇفمۇ) ئىلگىرى ئوغرىلىق قىلغان» دېيىشتى. يۇسۇف (ئۇلارنىڭ) بۇ سۆزىنى ئىچىدە بىلدى، لېكىن ئۇلارغا مەلۇم قىلمىدى، يۇسۇف (ئىچىدە): «سىلەرنىڭ مەۋقەيىڭلار ئەڭ ياماندۇر، سىلەرنىڭ (يۇسۇف ۋە ئۇنىڭ ئۇكىسى توغرىسىدا) قىلغان سۆزۈڭلارنىڭ يالغانلىقىنى اﷲ ئوبدان بىلىدۇ» دېدى'),
(1674, 12, 78, 'ئۇلار: «ئى ئالىيجاناب زات! ئۇنىڭ (ئۇنىڭدىن ئايرىلالمايدىغان) بىر قېرى ئاتىسى بار، ئۇنىڭ ئورنىغا بىزنىڭ بىرىمىزنى ئېلىپ قالغىن، بىز سېنى ھەقىقەتەن ياخشىلىق قىلغۇچىلاردىن ھېسابلايمىز» دېدى'),
(1675, 12, 79, 'يۇسۇف: «خۇدا ساقلىسۇن، نەرسىمىزنى كىمنىڭ يۈكىدىن تاپقان بولساق، شۇنى ئېلىپ قالىمىز، بولمىسا بىز چوقۇم زالىملاردىن بولۇپ قالىمىز» دېدى'),
(1676, 12, 80, 'ئۇلار (بۇنيامىننىڭ ئورنىغا ئۆز ئارىسىدىن بىرسىنى ئېلىپ قېلىشقا يۇسۇفنىڭ ماقۇل بولۇشىدىن) ئۈمىدسىزلەنگەندىن كېيىن، چەترەك يەرگە بېرىپ مەخپىي مەسلىھەتلەشتى، ئۇلارنىڭ چوڭى ئېيتتى: «ئاتاڭلارنىڭ سىلەردىن قەسەم قىلدۇرۇپ ۋەدە ئالغانلىقىنى ۋە ئىلگىرى يۇسۇف توغرىسىدىكى خاتالىقىڭلارنى بىلەمسىلەر؟ ئاتام ماڭا رۇخسەت قىلمىغىچە ياكى اﷲ مەن ئۈچۈن ھۆكۈم چىقارمىغۇچە بۇ يەردىن ھەرگىز ئايرىلمايمەن، اﷲ ئەڭ ئادىل ھۆكۈم قىلغۇچىدۇر'),
(1677, 12, 81, 'سىلەر قايتىپ كېتىپ ئاتاڭلار بىلەن كۆرۈشۈڭلار، ئاندىن ئېيتىڭلاركى، ئى ئاتىمىز! سېنىڭ ئوغلۇڭ راستلا ئوغرىلىق قىلدى، بىز پەقەت بىلگەن نەرسىمىز ئۈستىدىلا گۇۋاھلىق بېرىۋاتىمىز، بىز غەيبنى بىلمەيمىز (يەنى ساڭا ۋەدە بەرگەن چېغىمىزدا ئۇنىڭ ئوغرىلىق قىلىدىغانلىقىنى بىلمىدۇق)'),
(1678, 12, 82, 'بىز تۇرغان شەھەردىن (يەنى مىسىردىن) ۋە بىز بىلەن بىللە ماڭغان كارۋاندىن سوراپ باققىن، بىزنىڭ سۆزىمىز چوقۇم راست»'),
(1679, 12, 83, 'يەئقۇب ئېيتتى: «ئۇنداق ئەمەس، نەپسىڭلار سىلەرگە بۇ ئىشنى چىرايلىق كۆرسەتتى، مەن پەقەت چىرايلىقچە سەۋرە قىلىمەن، ئۇلارنىڭ (يەنى ئۈچ ئوغلۇمنىڭ) ھەممىسىنى ماڭا دەرقەمتە قىلىپ بېرىشنى اﷲ تىن تىلەيمەن. اﷲ ھەقىقەتەن (مېنىڭ ھالىمنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر»'),
(1680, 12, 84, 'يەئقۇب ئۇلاردىن يۈز ئۆرۈپ: «ئىسىت يۇسۇف!» دېدى. قايغۇدىن (كۆپ يىغلاپ) ئىككى كۆزى ئاقىرىپ كەتتى (يەنى ئىككى كۆزى كۆرمەس بولۇپ قالدى)، ئۇ تولۇپ تاشقان ئاچچىقىنى ئىچىگە يۇتقان ئىدى'),
(1681, 12, 85, 'ئۇلار: «اﷲ نىڭ نامى بىلەن قەسەمكى، سەن يۇسۇفنى ياد ئېتىۋېرىپ (ھەسرەتتىن) ھالاك بولۇشقا تاس قالىدىغان ياكى ھالاك بولىدىغان بولدۇڭ» دېدى'),
(1682, 12, 86, 'يەئقۇب ئېيتتى: «مەن قايغۇ - ھەسرىتىمنى پەقەت اﷲ غىلا ئېيتىمەن، اﷲ نىڭ بىلدۈرۈشى بىلەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن'),
(1683, 12, 87, 'ئى ئوغۇللىرىم! بېرىڭلار، يۇسۇفنى ۋە ئۇنىڭ ئۇكىسىنى ئىزدەڭلار، اﷲ نىڭ رەھمىتىدىن نائۈمىد بولماڭلار، شۈبھىسىزكى، پەقەت كاپىر قەۋملا اﷲ نىڭ رەھمىتىدىن ئۈمىدسىزلىنىدۇ»'),
(1684, 12, 88, 'ئۇلار يۇسۇفنىڭ قېشىغا كىرگىنىدە: «ئى ئالىيجاناب زات! بىزگە ۋە ئائىلىمىزگە ئاچارچىلىق يەتتى، (ئالدىڭغا) ناچار ئۆتمەس ماللار بىلەن كېلىپ قالدۇق، بىزگە يېتەرلىك ئاشلىق بەرگىن، بىزگە سەدىقە قىلغىن، اﷲ سەدىقە قىلغۇچىلارنى ھەقىقەتەن مۇكاپاتلايدۇ» دېدى'),
(1685, 12, 89, 'يۇسۇف: «سىلەر نادان ۋاقتىڭلاردا يۇسۇف ۋە ئۇنىڭ ئۇكىسىغا نېمىلەر قىلغانلىقىڭلارنى بىلەمسىلەر؟» دېدى'),
(1686, 12, 90, 'ئۇلار: «سەن راستلا يۇسۇفمۇ؟» دېدى. ئۇ: «مەن يۇسۇف بۇ مېنىڭ ئىنىم. اﷲ بىزگە مەرھەمەت قىلدى. كىمكى ھەقىقەتەن تەقۋادارلىق قىلىدىكەن، سەۋرى قىلىدىكەن (ياخشى مۇكاپاتقا ئېرىشىدۇ)، چۈنكى اﷲ ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ» دېدى'),
(1687, 12, 91, 'ئۇلار «اﷲ نىڭ نامى بىلەن قەسەمكى، اﷲ سېنى ھەقىقەتەن بىزدىن ئارتۇق قىلىپتۇ، بىز جەزمەن خاتا قىلىپتۇق» دېيىشتى'),
(1688, 12, 92, 'يۇسۇف ئېيتتى: «بۈگۈن سىلەر ئەيىبلەشكە ئۇچرىمايسىلەر، اﷲ سىلەرنى كەچۈرسۇن، اﷲ ئەڭ مەرھەمەتلىك زاتتۇر'),
(1689, 12, 93, 'سىلەر مېنىڭ بۇ كۆڭلىكىمنى ئېلىپ بېرىپ ئاتامنىڭ يۈزىگە تاشلاڭلار، كۆزى ئېچىلىدۇ، پۈتۈن ئائىلەڭلاردىكىنى ئىلىپ مېنىڭ يېنىمغا كېلىڭلار» دېدى'),
(1690, 12, 94, 'كارۋان (مىسىردىن شانغا قاراپ) قوزغالغان چاغدا، ئۇلارنىڭ ئاتىسى (يەنى يەئقۇب ئەلەيھىسسالام): «مەن چوقۇم يۇسۇفنىڭ ھىدىنى ئېلىۋاتىمەن، سىلەر مېنى ئالجىپ قالغان دېمەيدىغان بولساڭلار (يۇسۇفنى ئەلۋەتتە ھايات دەيتتىم)» دېدى'),
(1691, 12, 95, 'ئۇلار (يەنى يەئقۇبنىڭ نەۋرىلىرى ۋە يېنىدىكى كىشىلەر): «اﷲ نىڭ نامى بىلەن قەسەمكى، ھەقىقەتەن سەن تېخى يەنىلا بۇرۇنقى قايمۇقۇشۇڭدا ئىكەنسەن» دېدى'),
(1692, 12, 96, 'خۇش خەۋەرچى كېلىپ كۆڭلەكنى يەئقۇبنىڭ يۈزىگە تاشلىدى، ئۇنىڭ كۆزى ئېچىلدى، يەئقۇب: «مەن سىلەرگە، اﷲ نىڭ بىلدۈرىشى بىلەن سىلەر بىلمىگەن نەرسىلەرنى بىلىمەن، دېمىگەنمىدىم» دېدى'),
(1693, 12, 97, 'ئۇلار: «ئى ئاتىمىز! گۇناھلىرىمىز ئۈچۈن بىزگە مەغپىرەت تىلىگىن، بىز ھەقىقەتەن خاتالاشتۇق» دېيىشتى'),
(1694, 12, 98, 'يەئقۇب: «پەرۋەردىگارىمدىن سىلەرگە مەغپىرەت تىلەيمەن. اﷲ گۇناھلارنى ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر» دېدى'),
(1695, 12, 99, 'ئۇلار يۇسۇفنىڭ ئالدىغا كىرگەندە، يۇسۇف ئاتا - ئانىسىنى قۇچاقلىدى ۋە: «خۇدا خالىسا، مىسىرغا ئاما - ئېسەن كىرىڭلار» دېدى'),
(1696, 12, 100, 'ئاتا - ئانىسىنى تەختىدە (يېنىدا) ئولتۇرغۇزدى، ئۇلار (شاھلارغا تازىم قىلىش ئادىتى بويىچە) يۇسۇفكە سەجدە قىلىشتى. ئۇ: «ئى ئاتا! مانا بۇ، بۇرۇن (كىچىك ۋاقتىمدا) كۆرگەن چۈشۈمنىڭ تەبىرىدۇر، ئۇ چۈشۈمنى پەرۋەردىگارىم ھەقىقەتەن راستىغا چىقاردى. پەرۋەردىگارىم مېنى زىنداندىن چىقىرىش بىلەن، قېرىنداشلىرىم بىلەن مېنىڭ ئارامنى شەيتان بۇزغاندىن كېيىن، سىلەرنى سەھرادىن بۇ يەرگە ئېلىپ كېلىش بىلەن ماڭا ئېھسان قىلدى، پەرۋەردىگارىم خالىغىنىنى (ئىشقا ئاشۇرۇشقا) ھەقىقەتەن تەدبىرلىكتۇر، ئۇ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1697, 12, 101, 'پەرۋەردىگارىم! ماڭا ھەقىقەتەن پادىشاھلىق ئاتا قىلدىڭ، چۈش تەبىرىنى بىلدۈردۈڭ، ئى ئاسمانلارنى ۋە زېمىننى ئۆرنەكسىز ياراتقۇچى زات! دۇنيا ۋە ئاخىرەتتە مېنىڭ ئىگەمسەن، مېنى مۇسۇلمان پېتىمچە قەبزى روھ قىلغىن، مېنى ياخشى بەندىلەر قاتارىدا قىلغىن» دېدى'),
(1698, 12, 102, 'مانا بۇلار (يەنى يۇسۇف قىسسىسى) بىر قىسىم غەيب خەۋەرلىرى بولۇپ، بىز ئۇنى ساڭا ۋەھيى قىلدۇق. ئۇلار (يەنى يۇسۇفنىڭ قېرىنداشلىرى) (ئۇنىڭغا قارشى) چارە تەدبىر تۈزۈپ بىر قارارغا كېلىشكەندە سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ'),
(1699, 12, 103, 'سەن گەرچە كىشىلەرنىڭ ئىمان ئېيتىشىغا ھېرىس بولساڭمۇ (لېكىن) ئۇلارنىڭ تولىسى ئىمان ئېيتمايدۇ'),
(1700, 12, 104, 'قۇرئاننى تەبلىغ قىلغانلىقىڭغا ئۇلاردىن ھېچقانداق ئەجىر تەلەپ قىلمايسەن، قۇرئان پەقەت جاھان ئەھلى ئۈچۈن ۋەز - نەسىھەتتۇر'),
(1701, 12, 105, 'ئاسمانلاردا ۋە زېمىندا (اﷲ نىڭ قۇدرىتىنىڭ) نۇرغۇن ئالامەتلىرى باركى، ئۇلا ر ئۇنىڭ يېنىدىن دىققەت قىلماستىن ئۆتۈپ كېتىدۇ'),
(1702, 12, 106, 'ئۇلارنىڭ تولىسى اﷲ قا شېرىك كەلتۈرۈپ تۇرۇپ ئىشىنىدۇ (يەنى اﷲ نى ياراتقۇچى، رىزىق بەرگۈچى دەپ ئىقرار قىلىش بىلەن بىللە، بۇتلارغىمۇ چوقۇنىدۇ)'),
(1703, 12, 107, 'ئۇلار اﷲ نىڭ ئازابىدىن، بىرەر جازانىڭ ئۆزلىرىگە چۈشۈشىدىن ياكى ئۇلار سەزمەستىن قىيامەتنىڭ تۇيۇقسىز يېتىپ كېلىشىدىن ئەمىن بولدىمۇ؟'),
(1704, 12, 108, 'ئېيتقىنكى، «بۇ مېنىڭ يولۇمدۇر، (كىشىلەرنى) اﷲ قا دەۋەت قىلىمەن، مەن ۋە ماڭا ئەگەشكەنلەر روشەن دەلىلگە ئاساسلىنىمىز. اﷲ پاكتۇر، مەن مۇشرىكلاردىن ئەمەسمەن»'),
(1705, 12, 109, 'بىز سەندىن ئىلگىرى پەقەت شەھەر ئاھالىسىدىن بولغان ئەرلەرنى پەيغەمبەر قىلىپ، ئۇلارغا ۋەھيى قىلدۇق، ئۇلار (يەنى پەيغەمبەرگە چىنپۈتمىگۈچىلەر) يەر يۈزىدە سەير قىلىپ يۈرۈپ ئۆزلىرىدىن ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ ئاخىرەت يۇرتى تەقۋادارلىق قىلغانلار ئۈچۈن ئەلۋەتتە ياخشىدۇر، سىلەر چۈشەنمەمسىلەر'),
(1706, 12, 110, 'ھەتتا پەيغەمبەرلەر (قەۋمىنىڭ ئىمان ئېيتىشىدىن) ئۈمىدسىزلەنگەن ۋە قەۋمى تەرىپىدىن) يالغانغا چىقىرىلغانلىقىغا جەزم قىلغان چاغدا، ئۇلارغا بىزنىڭ ياردىمىمىز يېتىپ كەلدى، بىز خالىغان ئادەملەرنى قۇتقۇزدۇق، بىزنىڭ ئازابىمىز گۇناھكار قەۋمدىن قايتۇرۇلمايدۇ'),
(1707, 12, 111, 'ئۇلارنىڭ قىسسىسىدىن ئەقىل ئىگىلىرى ئەلۋەتتە ئىبرەت ئالىدۇ. (قۇرئان) يالغاندىن توقۇلغان سۆز ئەمەس، لېكىن، ئۇ ئۆزىدىن ئىلگىرى نازىل بولغان (ساماۋى) كىتابلارنى تەستىق قىلغۇچىدۇر، ھەممە نەرسىنى ئىنچىكە بايان قىلغۇچىدۇر، ئىمان ئېيتىدىغان قەۋم ئۈچۈن ھىدايەتتۇر ۋە رەھمەتتۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'ئەلىف، لام، مىم، را. بۇ، كىتاب (يەنى قۇرئان) نىڭ ئايەتلىرىدۇر. (قۇرئاندا) ساڭا پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان نەرسىلەر ھەقتۇر، لېكىن ئىنسانلارنىڭ تولىسى ئۇنىڭغا (يەنى قۇرئاننىڭ اﷲ تەرىپىدىن نازىل بولغانلىقىغا) ئىشەنمەيدۇ'),
(1709, 13, 2, 'اﷲ ئاسمانلارنى تۈرۈكسىز بەرپا قىلدى، سىلەر ئۇنى كۆرۈپ تۇرۇۋاتىسىلەر، ئاندىن ئۇ ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى. كۈن بىلەن ئاينى (بەندىلەرنىڭ مەنپەئىتى ئۈچۈن) بويسۇندۇردى، (ئۇلارنىڭ) ھەربىرى مۇئەييەن مۇددەتكىچە (يەنى دۇنيانىڭ تۈگەيدىغان ۋاقتىقىچە) سەير قىلىدۇ، اﷲ (ئۆز ھېكمىتى ۋە قۇدرىتى بىلەن) مەخلۇقاتنىڭ ئىشلىرىنى باشقۇرۇپ تۇرىدۇ، سىلەرنى پەرۋەردىگارىڭلارغا مۇلاقات بولۇشقا ئىشەنسۇن دەپ، (قۇدرىتىنىڭ) دەلىللىرىنى تەپسىلىي بايان قىلىدۇ'),
(1710, 13, 3, 'اﷲ يەر يۈزىنى (ئۇزۇنسىغا ۋە توغرىسىغا) سوزۇپ كەڭ ياراتتى، يەر يۈزىدە تاغلارنى ۋە دەريالارنى ياراتتى، مېۋىلەرنىڭ ھەربىر تۈرىنى ئەركەك - چىشى - ئىككى جىنسلىق قىلىپ ياراتتى. كېچىنى كۈندۈزگە كىرىشتۈردى (يەنى كېچىنىڭ قاراڭغۇلۇقى بىلەن كۈندۈزنىڭ يورۇقلۇقىنى ياپتى). بۇلاردا تەپەككۇر قىلىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) دەلىللەر بار'),
(1711, 13, 4, 'يەر يۈزىدە بىر بىرىگە تۇتىشاڭغۇ قىتئەلەر بار ۋە تۈرلۈك زىرائەتلەر بار، كۆپ شاخلىق بولغان ۋە كۆپ شاخلىق بولمىغان (بىر ئۇرۇقتىن بىر قانچە تال ئۈنۈپ چىققان، بىر ئۇرۇقتىن پەقەت بىر تال ئۈنۈپ چىققان) خورما دەرەخلىرى بار، (ئۇلارنىڭ ھەممىسى) بىر خىل سۇ بىلەن سۇغىرىلىدۇ، ئۇلارنىڭ بەزىسىنى بەزىسىدىن تەمىدە ئارتۇق قىلىمىز، بۇلاردا چۈشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار'),
(1712, 13, 5, '(ئى مۇھەممەد!) ئەگەر (بىرەر نەرسىدىن) ئەجەبلىنىدىغان بولساڭ، ئۇلارنىڭ (يەنى كۇففارلارنىڭ): «بىز (ئۆلۈپ) توپا بولۇپ كەتكەندىن كېيىن قايتا تىرىلەمدۇق؟» دېگەن سۆزى ئەجەبلىنىشكە لايىقتۇر. ئەنە شۇلار پەرۋەردىگارىنى ئىنكار قىلغانلاردۇر، (قىيامەت كۈنى) ئەنە شۇلارنىڭ بويۇنلىرىغا تاقاقلار سېلىنىدۇ، ئەنە شۇلار دوزاخ ئەھلى بولۇپ، دوزاختا مەڭگۈ قالغۇچىلاردۇر'),
(1713, 13, 6, 'ئۇلار سەندىن (اﷲ نىڭ) ئېھسانىدىن ئىلگىرى ئازابنىڭ چاپسانراق چۈشۈشىنى تەلەپ قىلىدۇ، ئۇلاردىن بۇرۇن اﷲ نىڭ ئازابىغا دۇچار بولغان ئۈممەتلەر ئۆتكەن، ئىنسانلار زۇلۇم قىلسىمۇ، پەرۋەردىگارىڭ ھەقىقەتەن ئۇلارنى مەغپىرەت قىلىدۇ. پەرۋەردىگارىڭ (گۇناھلارغا چۆمۈپ تەۋبە قىلمىغانلارنى) شەك شۈبھىسىز قاتتىق جازالايدۇ'),
(1714, 13, 7, 'كۇففارلار نېمىشقا ئۇنىڭغا پەرۋەردىگارى تەرىپىدىن بىرەر مۆجىزە نازىل بولمىدى؟ دېيىشىدۇ، سەن پەقەت بىر ئاگاھلاندۇرغۇچى. ھەر قەۋمنىڭ بىر ھىدايەت قىلغۇچىسى (يەنى پەيغەمبىرى) بولىدۇ (مۆجىزىلەرگە كەلسەك. ئۇ اﷲ نىڭ ئىلكىدىكى ئىش)'),
(1715, 13, 8, 'ھەر بىر (ھامىلىدار) ئايالنىڭ قورسىقىدىكىنى اﷲ بىلىدۇ (يەنى ئوغۇلمۇ - قىزمۇ، بىرمۇ - كۆپمۇ، چوڭمۇ - كىچىكمۇ، چىرايلىقمۇ - سەتمۇ، بەختلىكمۇ - بەختسىزمۇ، ئۆمرى ئۇزۇنمۇ - قىسقىمۇ - ھەممىسىنى اﷲ بىلىدۇ). بەچچىدانلارنىڭ كىچىكلەپ كەتكەنلىكى ياكى يوغىناپ كەتكەنلىكىنىمۇ (يەنى بالىنىڭ مۇددىتى توشماي تۇغۇلىدىغانلىقىنى ياكى مۇددىتى ئېشىپ كېتىپ تۇغۇلىدىغانلىقىنى) (اﷲ) بىلىدۇ. اﷲ نىڭ دەرگاھىدا ھەممە نەرسە ئۆلچەملىكتۇر'),
(1716, 13, 9, 'اﷲ مەخپىي ۋە ئاشكارا ئىشلارنىڭ ھەممىسىنى بىلگۈچىدۇر، ھەممىدىن بۈيۈكتۇر، ھەممىدىن ئۈستۈندۇر'),
(1717, 13, 10, 'سىلەرنىڭ ئىچىڭلاردىن يوشۇرۇن سۆز قىلغان، ئاشكارا سۆز قىلغان، كېچىسى يوشۇرۇنغان، كۈندۈزى ئاشكارا يۈرگەنلەرنىڭ ھەممىسى (اﷲ ئۈچۈن ئېيتقاندا) ئوخشاشتۇر (يەنى اﷲ ھەممىنى بىلىپ تۇرىدۇ)'),
(1718, 13, 11, 'ھەربىر ئادەمنىڭ ئالدى - كەينىدە اﷲ نىڭ ئەمرى بويىچە ئۇنى قوغدايدىغان نۆۋەتچى پەرىشتىلەر بار. ھەر قانداق بىر قەۋم ئۆزىنىڭ ئەھۋالىنى ئۆزگەرتمىگۈچە (يەنى اﷲ نىڭ بەرگەن نېمەتلىرىگە تۇزكورلۇق قىلىپ گۇناھلارغا چۈممىگىچە) اﷲ ئۇلارنىڭ ئەھۋالىنى ئۆزگەرتمەيدۇ (يەنى اﷲ ئۇلارغا بەرگەن نېمەت، خاتىرجەملىك ۋە ئىززەت - ھۆرمەتنى ئېلىپ تاشلىمايدۇ)، اﷲ بىرەر قەۋمنى ھالاك قىلماقچى (ياكى ئازابلىماقچى) بولسا، ئۇنىڭغا قارشى تۇرغىلى بولمايدۇ، ئۇلارغا ئازابنى دەپئى قىلىدىغان اﷲ تىن باشقا ئىگىمۇ بولمايدۇ'),
(1719, 13, 12, 'اﷲ (چاقماقتىن) قورقىتىش، (يامغۇرغا) ئۈمىد تۇغدۇرۇش ئۈچۈن، سىلەرگە چاقماقنى كۆرسىتىدۇ، (قۇدرىتى بىلەن) قويۇق بۇلۇتلارنى پەيدا قىلىدۇ'),
(1720, 13, 13, 'گۈلۈرماما اﷲ نى مەدھىيىلەش بىلەن تەسبىھ ئېيتىدۇ، پەرىشتىلەرمۇ اﷲ تىن قورقۇپ تەسبىھ ئېيتىدۇ، اﷲ چاقماق چۈشۈرۈپ، ئۇنىڭ بىلەن ئۆزى خالىغان ئادەمنى ھالاك قىلىدۇ. ئۇلار اﷲ توغرىسىدا دەتالاش قىلىشىدۇ، اﷲ نىڭ تەدبىرى كۈچلۈكتۇر'),
(1721, 13, 14, 'ھەقىقىي قىلىنغان دۇئا اﷲ قا قارىتىلىدۇ، (يەنى ئىخلاس بىلەن قىلىنغان دۇئانى اﷲ ئىجابەت قىلىدۇ). كۇففارلارنىڭ اﷲ نى قويۇپ، دۇئالىرىنى قاراتقان مەبۇدلىرى كۇففارلارنىڭ ھېچقانداق دۇئاسىنى ئىجابەت قىلمايدۇ. ئۇلار (يەنى كاپىرلار) شۇنداق بىر ئادەمگە ئوخشايدۇكى، ئۇ ئاغزىمىزغا سۇ چۈشسۇن دەپ (نېرىدىن) ئىككى ئالىقىنىنى سۇغا قارىتىپ ئېچىپ تۇرىدۇ، (ئەمەلىيەتتە سۇ ئاڭلىمايدىغان، ھېس - تۇيغۇسىز نەرسە بولغاچقا) سۇ ھەرگىز ئۇنىڭ ئاغزىغا چۈشمەيدۇ، كاپىرلارنىڭ مەبۇدلىرىغا قىلغان دۇئاسى پۈتۈنلەي بىكاردۇر'),
(1722, 13, 15, 'ئاسمانلاردىكىلەر ۋە زېمىندىكىلەر (يەنى پەرىشتىلەر، ئىنسانلار ۋە جىنلار) ئىختىيارى ۋە مەجبۇرى يوسۇندا اﷲ قا بويسۇنىدۇ، ئەتىگەن – ئاخشامدا (يەنى داۋاملىق تۈردە) ئۇلارنىڭ سايىلىرىمۇ بويسۇنىدۇ'),
(1723, 13, 16, '(ئى مۇھەممەد! مۇشرىكلارنى مات قىلىش ئۈچۈن ئۇلارغا) «ئاسمانلارنىڭ، زېمىننىڭ پەرۋەردىگارى كىم؟» دېگىن، «(ئۇلارنىڭ پەرۋەردىگارى) اﷲ» دېگىن. (ئۇلارغا) «سىلەر اﷲ نى قويۇپ ئۆزۈڭلارغا پايدا - زىيان يەتكۈزەلمەيدىغان مەبۇدلارنى (اﷲ قا شېرىك) قىلىۋالدىڭلارمۇ؟» دېگىن؟ «كۆر (يەنى كاپىر) بىلەن كۆزى كۆرۈدىغان ئادەم (يەنى مۆمىن) باراۋەر بولامدۇ؟ ياكى قاراڭغۇلۇق (يەنى گۇمراھلىق) بىلەن نۇر (يەنى ھىدايەت) باراۋەر بولامدۇ؟» دېگىن، ئۇلار اﷲ ياراتقانغا ئوخشاش قانداقتۇر مەخلۇقاتلارنى ياراتقان مەبۇدلارنى پۈتۈن مەخلۇقاتنى ياراتقان اﷲ قا شېرىك قىلىۋېلىپ اﷲ نىڭ ياراتقىنى بىلەن مەبۇدلارنى پۈتۈن مەخلۇقاتنى ياراتقان اﷲ قا شېرىك قىلىۋېلىپ اﷲ نىڭ ياراتقىنى بىلەن مەبۇدلىرىنىڭ ياراتقىنىنى ئايرىيالماي قالدىمۇ؟ اﷲ ھەممە نەرسىنى ياراتقۇچىدۇر، اﷲ بىر دۇر، ھەممىگە غالىبتۇر'),
(1724, 13, 17, 'اﷲ ئاسماندىن يامغۇر ياغدۇردى، (يامغۇر سۈيى) جىلغىلاردا لىپمۇ لىق ئاقتى، كەلكۈن سۇ ئۆزىنىڭ ئۈستىدە كۆپۈكلەرنى لەيلىتىپ ئاقتى، كىشىلەرنىڭ زىننەت بۇيۇملىرىنىڭ، ياكى ئەسۋاب ياساش ئۈچۈن ئوتقا سېلىپ ئېرىتكەن مەدەنلەرنىڭمۇ (يەنى كۆپۈكمۇ خۇددى كەلكۈن سۇنىڭ كۆپۈكىگە ئوخشاش پايدىسىزدۇر). اﷲ ھەق بىلەن باتىلنى بۇ مىسال بىلەن ئەنە شۇنداق بايان قىلىدۇ، كۆپۈك بولسا ئېقىپ تۈگەيدۇ، ئىنسانلارغا پايدىلىق نەرسە زېمىندا قېلىپ قالىدۇ، اﷲ مىساللارنى ئەنە شۇنداق بايان قىلىدۇ'),
(1725, 13, 18, 'اﷲ نىڭ (ئىمان غا قىلغان دەۋىتىنى) قوبۇل قىلغانلارغا ئەڭ ئوبدان مۇكاپات بېرىلىدۇ، قوبۇل قىلمىغانلارنىڭ ئىگىدارچىلىقىدا يەر يۈزىدىكى پۈتۈن مال - مۈلۈك، يەنە شۇنىڭدەك بىر ھەسسە كېلىدىغان مال - مۈلۈك بولغان تەقدىردىمۇ، ئۇلار (ئاخىرەتتە اﷲ نىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن)، ئەلۋەتتە، ئۇنى پىدا قىلغان بولاتتى. ئۇلاردىن قاتتىق ھېساب ئېلىنىدۇ، ئۇلارنىڭ بارىدىغان جايى دوزاخ بولىدۇ، (دوزاخ) نېمىدېگەن يامان جاي!'),
(1726, 13, 19, '(ئى مۇھەممەد!) پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان نەرسىنىڭ ھەق ئىكەنلىكىنى بىلىدىغان ئادەم (ھەقنى كۆرمەيدىغان دىلى) كور ئادەم بىلەن ئوخشاش بولامدۇ؟ (اﷲ نىڭ ئايەتلىرىدىن) پەقەت ئەقىل ئىگىلىرىلا پەند - نەسىھەت ئالىدۇ'),
(1727, 13, 20, 'ئۇلار اﷲ نىڭ ئەھدىگە (يەنى اﷲ تاپشۇرغان ئىشلارغا) ۋاپا قىلىدۇ، بەرگەن ۋەدىسىنى بۇزمايدۇ'),
(1728, 13, 21, 'ئۇلار اﷲ نىڭ (خىش - ئەقرىبالارغا) سىلە - رەھىم قىلىشتىن ئىبارەت ئەمرىنى بەجا كەلتۈرىدۇ، اﷲ تىن قورقىدۇ، قاتتىق ھېساب ئېلىنىشىدىن قورقىدۇ'),
(1729, 13, 22, 'پەرۋەردىگارىنىڭ رازىلىقىنى كۆزلەپ (يەتكەن كۈلپەتلەرگە) سەۋر قىلغانلار، (پەرز) نامازنى ئادا قىلغانلار، بىز رىزىق قىلىپ بەرگەن مال - مۈلۈكتىن (اﷲ يولىدا) يوشۇرۇن ۋە ئاشكارا يوسۇندا سەرپ قىلغانلار ۋە ياخشىلىقى ئارقىلىق يامانلىقىنى دەپئى قىلىدىغانلار (يەنى بىلمەستىن يامان ئىش قىلىپ قالسا، ئارقىدىن ياخشى ئىش قىلىدىغانلار، يامانلىق قىلغۇچىلارغا كەڭ قورساق بولۇپ، ياخشى مۇناسىۋەتتە بولىدىغانلار) - ئەنە شۇلارنىڭ ئاخىرەتلىكى ياخشى بولىدۇ'),
(1730, 13, 23, 'ئۇلار مەڭگۈ تۇرىدىغان جەننەتكە كىرىدۇ، ئۇلارنىڭ ئاتىلىرى، خوتۇنلىرى ۋە ئەۋلادىنىڭ ئىچىدىكى ياخشىلىقلارمۇ جەننەتكە كىرىدۇ. پەرىشتىلەر ئۇلارنى (مۇبارەكلەش ئۈچۈن جەننەتنىڭ) ھەربىر دەرۋازىسىدىن كىرىدۇ'),
(1731, 13, 24, '(پەرىشتىلەر ئۇ دۇنيادا) «سەۋرىنى دوست تۇتقانلىقىڭلار ئۈچۈن سىلەرگە ئامانلىق بولسۇن! ئاخىرەتلىكنىڭ ياخشى بولۇشى نېمىدېگەن ئوبدان!» دەيدۇ'),
(1732, 13, 25, 'اﷲ قا بەرگەن ۋەدىسىنى مۇستەھكەملىگەندىن كېيىن بۇزغانلار، اﷲ نىڭ سىلە - رەھىم قىلىشتىن ئىبارەت ئەمرىنى تۇتمىغانلار، يەر يۈزىدە بۇزغۇنچىلىق قىلغانلار - ئەنە شۇلار لەنەتكە دۇچار بولىدۇ ۋە (ئۇلارنىڭ) ئاخىرەتلىكى يامان بولىدۇ'),
(1733, 13, 26, 'اﷲ (ئۆزىنىڭ ھېكمىتىگە ئاساسەن) خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ ۋە (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ. (مۇشرىكلار) دۇنيا تىرىكچىلىكىگە خۇش بولۇپ كەتتى، دۇنيا تىرىكچىلىكى ئاخىرەتكە قارىغاندا ئازغىنا نەرسىدۇر'),
(1734, 13, 27, 'كۇففارلار: «نېمىشقا ئۇنىڭغا پەرۋەردىگارىدىن (مۇسانىڭ ۋە ئىسانىڭ مۆجىزىسىدەك) بىرەر مۆجىزە كەلمىدى؟» دەيدۇ. (ئى مۇھەممەد!) «(ئىش اﷲ نىڭ ئىلكىدە، مېنىڭ ئىلكىمدە ئەمەس) اﷲ ھەقىقەتەن خالىغان ئادەمنى گۇمراھ قىلىدۇ» دېگىن'),
(1735, 13, 28, '(تەۋبە قىلغانلار) ئىمان ئېيتقانلار بولۇپ، ئۇلارنىڭ دىللىرى اﷲ نى ياد ئېتىش بىلەن ئارام تاپىدۇ، بىلىڭلاركى، دىللار اﷲ نى ياد ئېتىش بىلەن ئارام تاپىدۇ'),
(1736, 13, 29, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار خۇشاللىققا، گۈزەل قارارگاھقا (يەنى جەننەتكە) ئېرىشىدۇ'),
(1737, 13, 30, '(ئى مۇھەممەد!) بىز ساڭا ۋەھىي قىلغان كىتابنى ئۇلارغا ئوقۇپ بېرىشىڭ ئۈچۈن، سەندىن ئىلگىرى پەيغەمبەرلەرنى ئەۋەتكەندەك، سېنى شۇنداق بىر ئۈممەتكە ئەۋەتتۇقكى، ئۇنىڭدىن بۇرۇن نۇرغۇن ئۈممەتلەر ئۆتكەن، ھالبۇكى، ئۇلار (يەنى مۇشرىكلار) مەرھەمەتلىك اﷲ نى ئىنكار قىلماقتا، (ئى مۇھەممەد! بۇ مۇشرىكلارغا) ئېيتقىنكى، «ئۇ مېنىڭ پەرۋەردىگارىمدۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يۇقتۇر، ئۇنىڭغا تەۋەككۈل قىلدىم ۋە ئۇنىڭ دەرگاھىغا قايتىمەن»'),
(1738, 13, 31, 'مۇبادا بىرەر قۇرئان (يەنى كىتاب) بولۇپ، ئۇ (تىلاۋەت قىلىش) بىلەن تاغلار يۆتكىلىدىغان ياكى يەر يېرىلىدىغان ۋە ياكى ئۆلۈكلەر سۆزلەيدىغان بولغان تەقدىردىمۇ (ئۇلار چوقۇم ئىمان ئېيتمايتتى). ھەممە ئىش اﷲ نىڭ ئىلكىدىدۇر، ئەگەر اﷲ خالىسا، ئىنسانلارنىڭ ھەممىسىنى جەزمەن ھىدايەت قىلىدىغانلىقىنى مۆمىنلەر بىلمىدىمۇ؟ (مەككە پەتھى قىلىنىپ ئىسلام دىنىنىڭ غەلىبە قىلىشىدىن ئىبارەت) اﷲ نىڭ ۋەدىسى ئەمەلگە ئاشقانغا قەدەر، كاپىرلار ئۆز قىلمىشلىرى تۈپەيلىدىن ھامان تۈرلۈك بالالارغا دۇچار بولىدۇ، ياكى بالا ئۇلارنىڭ يۇرتىغا يېقىن بىر جايغا چۈشىدۇ، اﷲ ۋەدىسىگە ئەلۋەتتە خىلاپلىق قىلمايدۇ'),
(1739, 13, 32, 'سەندىن ئىلگىرى ئۆتكەن نۇرغۇن پەيغەمبەرلەر ھەقىقەتەن مەسخىرە قىلىندى. كاپىرلارغا بېرىلىدىغان ئازابنى بىر مۇددەت كېچىكتۈردۈم، ئاندىن ئۇلارنى جازالىدىم، ئۇلارغا بەرگەن جازايىم قانداق ئىكەن؟'),
(1740, 13, 33, 'ھەر بىر كىشىنىڭ قىلغان ئەمەلىنى كۈزىتىپ تۇرغۇچى اﷲ (بۇتلارغا ئوخشامدۇ؟) ئۇلار بۇتلارنى اﷲ قا شېرىك قىلدى. ئۇلارغا: «ئۇلارغا بۇتلارنىڭ ناملىرىنى ئاتاپ بېقىڭلار، يەر يۈزىدە اﷲ بىلمەيدىغان نەرسىلەر باردەك، سىلەر ئۇلارنى اﷲ قا ئېيتىپ بېرەلەمسىلەر؟ ياكى قۇرۇق گۇمان بىلەن (ئۇلارنى اﷲ نىڭ شېرىكلىرى دەۋاتامسىلەر؟)» دېگىن. بەلكى كاپىرلارغا ئۇلارنىڭ كۇفرى چىرايلىق كۆرسىتىلدى، ئۇلار توغرا يولدىن مەنئى قىلىندى. اﷲ گۇمراھ قىلغان ئادەمگە ھېچقانداق ھىدايەت قىلغۇچى بولمايدۇ'),
(1741, 13, 34, 'ئۇلار بۇ دۇنيادا ئازابقا دۇچار بولىدۇ، ئۇلارنى اﷲ نىڭ ئازابىدىن ساقلىغۇچى بولمايدۇ'),
(1742, 13, 35, 'تەقۋادارلارغا ۋەدە قىلىنغان جەننەتنىڭ سۈپىتى شۇكى، ئۇنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدۇ، ئۇنىڭ مېۋىلىرى تۈگىمەيدۇ، ھەمىشە سايە چۈشۈپ تۇرىدۇ، ئەنە شۇ تەقۋادارلارنىڭ ئاقىۋەت بارىدىغان جايىدۇر؛ كاپىرلارنىڭ ئاقىۋەت بارىدىغان جايى دوزاختۇر'),
(1743, 13, 36, '(ئى مۇھەممەد!) بىز كىتاب (يەنى تەۋرات ۋە ئىنجىل) نازىل قىلغانلار (يەنى ئابدۇللا ئىبن سالام ۋە نەجاشىغا ئوخشاشلار) ساڭا نازىل قىلىنغان (قۇرئان) دىن شادلىنىدۇ. (ساڭا قارشى) ئىتتىپاق تۈزگەنلەرنىڭ ئارىسىدا (ھەقلىقىنى بىلىپ تۇرۇپ گەدەنكەشلىكتىن) قۇرئاننىڭ بەزى (جايلىرىنى) ئىنكار قىلىدىغانلارمۇ بار. (ئى مۇھەممەد!) «مەن پەقەت (يالغۇز) اﷲ غىلا قىلىشقا، اﷲ قا (ھېچ نەرسىنى) شېرىك كەلتۈرمەسلىككە بۇيرۇلدۇم، (ئىنسانلارنى) اﷲ قا ئىبادەت قىلىشقا دەۋەت قىلىمەن، اﷲ نىڭلا دەرگاھىغا قايتىمەن» دېگىن'),
(1744, 13, 37, 'شۇنىڭدەك (يەنى ئۆتكەنكى پەيغەمبەرلەرگە ئۆز تىلىدا كىتاب نازىل قىلغىنىمىزدەك)، قۇرئاننى ئەرەب تىلىدا (كىشىلەرنىڭ ئارىسىدا چىقىرىشقا) ھۆكۈم قىلىپ نازىل قىلدۇق، سەن ئىلىمگە ئىگە بولۇپ تۇرۇپ، ئۇلارنىڭ (يەنى ئەھلى كىتابنىڭ) نەپسى خاھىشلىرىغا ئەگىشىپ كېتىدىغان بولساڭ، ساڭا ھېچقانداق ياردەم بەرگۈچى ۋە سېنى اﷲ نىڭ ئازابىدىن ساقلىغۇچى بولمايدۇ'),
(1745, 13, 38, 'بىز سەندىن ئىلگىرى ھەقىقەتەن نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارغا خوتۇنلار ۋە بالىلار بەردۇق، ھەرقانداق پەيغەمبەر اﷲ نىڭ ئىزنىسىز مۆجىزە كەلتۈرەلمەيدۇ، ھەربىر ۋاقىتنىڭ (ئۆزىگە مۇناسىپ) ھۆكمى بولىدۇ'),
(1746, 13, 39, 'اﷲ (ئەھكاملاردىن) خالىغىنىنى بىكار قىلىدۇ، خالىغىنىنى ئۆز جايىدا قالدۇرىدۇ. لەۋھۇلمەھپۇز اﷲ نىڭ دەرگاھىدىدۇر'),
(1747, 13, 40, 'ئۇلارغا بىز ۋەدە قىلغان ئازابنىڭ بەزىسىنى ساڭا كۆرسەتسەكمۇ (يەنى سېنىڭ ھايات ۋاقتىڭدا نازىل قىلساقمۇ) ياكى سېنى (مۇشرىكلارغا ئازاب كېلىشتىن بۇرۇن) قەبزى روھ قىلساقمۇ، سېنىڭ مەسئۇلىيىتىڭ پەقەت تەبلىغ قىلىشتۇر. بىزنىڭ مەسئۇلىيىتىمىز ھېساب ئېلىشتۇر'),
(1748, 13, 41, 'ئۇلارنىڭ يېرىنى چۆرىسىدىن تارايتىپ بېرىۋاتقانلىقىمىز (يەنى كۇففارلارنىڭ زېمىنىنى قەدەممۇ قەدەم پەتھى قىلىپ، ئىسلام يۇرتىغا قوشۇۋاتقانلىقىمىز) نى ئۇلار (يەنى مۇشرىكلار) كۆرمەيۋاتامدۇ؟ اﷲ (خالىغانچە) ھۆكۈم قىلىدۇ، اﷲ نىڭ ھۆكمىگە ھېچ كىشى قارشى تۇرالمايدۇ. اﷲ تېز ھېساب ئالغۇچىدۇر'),
(1749, 13, 42, 'ئۇلار (يەنى قۇرەيش كاپىرلىرى) دىن ئىلگىرى ئۆتكەنلەرمۇ (پەيغەمبەرلىرىگە) ھىيلە - مىكىر ئىشلەتكەن ئىدى. (ئۇلارنىڭ ھىيلە –مىكرىنى بەربات قىلىدىغان) يوشۇرۇن تەدبىرلەرنىڭ ھەممىسى اﷲ نىڭ ئىلگىدىدۇر، اﷲ ھەربىر كىشىنىڭ قىلىۋاتقان (ياخشى - يامان) ئەمەللىرىنى بىلىپ تۇرىدۇ، كاپىرلار (ئازابنى كۆرگەندە) كىمنىڭ ئاخىرەتلىكىنىڭ ياخشى بولغانلىقىنى بىلىدۇ'),
(1750, 13, 43, '(ئى مۇھەممەد!) مەككە كۇففارلىرى: «سەن پەيغەمبەر ئەمەسسەن» دەيدۇ. ئېيتقىنكى، «راستلىقىمغا سىلەر بىلەن مېنىڭ ئوتتۇرامدا اﷲ نىڭ گۇۋاھلىقى ۋە اﷲ نىڭ كىتابىنى بىلىدىغانلار (يەنى ئەھلى كىتاب ئۆلىمالىرىدىن ئىمان ئېيتقانلار) نىڭ گۇۋاھلىقى يېتەرلىكتۇر»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'ئەلىق. لام. را. (بۇ قۇرئان شۇنداق) كىتابتۇركى، (ئى مۇھەممەد!) ئۇنى ساڭا كىشىلەرنى پەرۋەردىگارىنىڭ ئىزنى بىلەن قاراڭغۇلۇقتىن (يەنى كۇفرىدىن) يورۇقلۇققا (يەنى ئىمانغا) چىقىرىشىڭ ئۈچۈن، غالىب، (ھەممە تىللاردا) مەدھىيىلەنگەن اﷲ نىڭ يولىغا (باشلىشىڭ ئۈچۈن) نازىل قىلدۇق'),
(1752, 14, 2, 'ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، قاتتىق ئازابتىن كاپىرلارغا ۋاي!'),
(1753, 14, 3, 'ئۇلار بۇ دۇنيانى ئاخىرەتتىن ئارتۇق. كۆرىدۇ (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى ئىسلام دىنىدىن) توسىدۇ، اﷲ نىڭ يولىنىڭ ئەگرى بولۇشىنى تىلەيدۇ، ئۇلار (ھەقتىن) يىراق بولغان گۇمراھلىقتىدۇر'),
(1754, 14, 4, 'قانداقلا بىر پەيغەمبەرنى ئەۋەتمەيلى، ئۇنى بىز ئۆز قەۋمىگە (اﷲ نىڭ شەرىئىتىنى) بايان قىلىش ئۈچۈن، پەقەت ئۆز قەۋمىنىڭ تىلى بىلەن (سۆزلەيدىغان) قىلىپ ئەۋەتتۇق، اﷲ خالىغان ئادەمنى گۇمراھ قىلىدۇ، خالىغان ئادەمنى ھىدايەت قىلىدۇ (يەنى پەيغەمبەرلەرنىڭ ۋەزىپىسى پەقەت تەبلىغ قىلىش بولۇپ، ھىدايەت قىلىش ئىشى اﷲ نىڭ ئىلگىدىدۇر). اﷲ غالىبتۇر ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1755, 14, 5, 'بىز مۇسانى مۆجىزىلىرىمىز بىلەن ھەقىقەتەن (پەيغەمبەر قىلىپ) ئەۋەتتۇق، (ئۇنىڭغا دېدۇقكى) «قەۋمىڭنى قاراڭغۇلۇقتىن (يەنى كۇفرىدىن) يورۇقلۇققا. (يەنى ئىمانغا) چىقارغىن، ئۇلارغا اﷲ نىڭ نېمەتلىرىنى ئەسلەتكىن». بۇنىڭدا (بالاغا) سەۋر قىلغۇچى، (نېمەتكە) شۈكۈر قىلغۇچى ھەربىر (بەندە) ئۈچۈن ئەلۋەتتە نۇرغۇن ئىبرەتلەر بار'),
(1756, 14, 6, 'مۇسا ئۆز قەۋمىگە ئۆز ۋاقتىدا: «اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار. اﷲ ئۆز ۋاقتىدا سىلەرنى پىرئەۋن قەۋمىدىن قۇتقۇزدى، ئۇلار سىلەرنى قاتتىق قىينايتتى، ئوغۇللىرىڭلارنى بوغۇزلايتتى، خوتۇن كىشىلىرىڭلارنى (خىزمەت قىلدۇرۇشقا) قالدۇراتتى. بۇنىڭدا پەرۋەردىگارىڭلار تەرىپىدىن (سىلەرگە) چوڭ سىناق بار ئىدى» دېدى'),
(1757, 14, 7, 'ئۆز ۋاقتىدا پەرۋەردىگارىڭلار: «نېمىتىگە شۈكۈر قىلساڭلار، (ئۇنى) تېخىمۇ زىيادە قىلىمەن. ئەگەر (كۇفرانى نېمەت) قىلساڭلار، مېنىڭ ئازابىم، ئەلۋەتتە، بەكمۇ قاتتىق بولىدۇ» دەپ جاكالىدى'),
(1758, 14, 8, 'مۇسا (ئۆز قەۋمىگە): «ئەگەر سىلەر ۋە پۈتۈن يەر يۈزىدىكى كىشىلەر كاپىر بولۇپ كەتسەڭلارمۇ (اﷲ قا قىلچە زىيان يەتكۈزەلمەيسىلەر)، چۈنكى اﷲ ئەلۋەتتە (ھەممىدىن) بىھاجەتتۇر، مەدھىيىلەشكە لايىقتۇر» دېدى'),
(1759, 14, 9, 'سىلەردىن ئىلگىرى ئۆتكەن نۇھ، ئاد ۋە سەمۇد قەۋملىرىنىڭ ۋە ئۇلاردىن كېيىن كەلگەن، (سانىنى) پەقەت اﷲ بىلىدىغان ئۈممەتلەرنىڭ خەۋىرى سىلەرگە يەتمىدىمۇ؟ ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەر بىلەن كەلدى، ئۇلار قوللىرى بىلەن پەيغەمبەرلەرنىڭ ئېغىزلىرىغا ئىشارەت قىلىپ (يەنى ئۇلارنىڭ سۈكۈت قىلىشىنى كۆرسىتىپ): «بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ھەقىقەتەن ئىنكار قىلىمىز، بىز سىلە دەۋەت قىلىۋاتقان ئىشقا ھەقىقەتەن زور گۇماندىمىز» دېدى'),
(1760, 14, 10, 'پەيغەمبەرلىرى: «ئاسمانلارنى ۋە زېمىننى ياراتقۇچى اﷲ نىڭ (بىرلىكىدە) شەك بارمۇ؟ اﷲ گۇناھلىرىڭلارنى مەغپىرەت قىلىش ئۈچۈن، سىلەرنى (ئىمانغا) دەۋەت قىلىدۇ، سىلەرنى (جازالاشنى) مەلۇم مۇددەتكىچە (يەنى ئەجىلىڭلار يەتكۈچە) تەخىر قىلىدۇ» دېدى. ئۇلار: «سىلەر پەقەت بىزگە ئوخشاش ئىنسان، ئاتا - بوۋىلىرىمىز ئىبادەت قىلىپ كېلىۋاتقان (بۇتلار) غا ئىبادەت قىلىشىمىزدىن توسماقچى بولىسىلەر، (راستلىقىڭلار ئۈچۈن) بىزگە ئېنىق پاكىت كۆرسىتىڭلار» دېدى'),
(1761, 14, 11, 'پەيغەمبەرلىرى ئۇلارغا: «بىز پەقەت سىلەرگە ئوخشاش ئىنسانمىز، لېكىن اﷲ بەندىلىرىدىن خالىغان كىشىلەرگە (پەيغەمبەرلىكىنى) مەرھەمەت قىلىدۇ، اﷲ نىڭ ئىزنىسىز سىلەرگە ھېچقانداق پاكىت كۆرسىتەلمەيمىز، مۆمىنلەر (ھەممە ئىشتا) يالغۇز بىر اﷲ غىلا تەۋەككۈل قىلسۇن (يەنى ھەممە ئىشلىرىنى اﷲ قا تاپشۇرسۇن)'),
(1762, 14, 12, 'اﷲ بىزنى (بىزنى تونۇيدىغان) يوللىرىمىزغا يېتەكلىگەن تۇرسا، نېمىشقا اﷲ قا تەۋەككۈل قىلمايلى؟ بىزگە يەتكۈزگەن ئەزىيەتلىرىڭلارغا، ئەلۋەتتە، سەۋر قىلىمىز، تەۋەككۈل قىلغۇچىلار پەقەت اﷲ قا تەۋەككۈل قىلسۇن» دېدى'),
(1763, 14, 13, 'كاپىرلار پەيغەمبەرلىرىگە: «سىلەرنى زېمىندىن چوقۇم ھەيدەپ چىقىرىمىز، ياكى چوقۇم بىزنىڭ دىنىمىزغا قايتىشىڭلار كېرەك» دېدى. ئۇلارغا پەرۋەردىگارى (مۇنداق) ۋەھيى قىلدى: «زالىملارنى چوقۇم ھالاك قىلىمىز'),
(1764, 14, 14, 'ئۇلارنى ھالاك قىلغاندىن كېيىن زېمىندا چوقۇم سىلەرنى تۇرغۇزىمىز، بۇ (يەنى غەلىبە) مېنىڭ ئالدىمدا تۇرۇشتىن (يەنى سوراققا تارتىلىشتىن) ۋە ئازابىمدىن قورققانلار ئۈچۈندۇر»'),
(1765, 14, 15, 'پەيغەمبەرلەر اﷲ تىن مەدەت تىلىدى، ھەر بىر ئۇچىغا چىققان تەرسا، شەپقەتسىز (يەنى اﷲ قا ئىتائەت قىلىشقا گەدەنكەشلىك قىلىپ ھەقىقەتتىن باش تارتقۇچى) ھالاك بولدى'),
(1766, 14, 16, 'ئۇنىڭ ئالدىدا جەھەننەم تۇرىدۇ، جەھەننەمدە ئۇ يىرىڭ ئارىلاش سۇ بىلەن سۇغۇرۇلىدۇ'),
(1767, 14, 17, '(ئۇنىڭ ئاچچىقلىقىدىن) ئۇنى يۇتۇملاپ ئېچىپ تەسلىكتە يۇتىدۇ، ئۆلۈم ئۇنى قورىشىۋالىدۇ. لېكىن ئۇ ھەرگىز ئۆلمەيدۇ، شۇنىڭدىن كېيىن تېخىمۇ قاتتىق ئازابقا دۇچار بولىدۇ'),
(1768, 14, 18, 'پەرۋەردىگارنى ئىنكار قىلغانلارنىڭ قىلغان (ياخشى) ئەمەللىرى بورانلىق كۈندە شامال ئۇچۇرۇپ كەتكەن بىر دۆۋە كۈلگە ئوخشىمايدۇ، كاپىرلار (بۇ دۇنيادا) قىلغان ياخشى ئەمەللىرى ئۈچۈن ئازراقمۇ شاۋابقا ئېرىشەلمەيدۇ، بۇ چۇڭقۇر ئېزىشتۇر (يەنى چوڭ زىياندۇر)'),
(1769, 14, 19, 'ئاسمانلارنى ۋە زېمىننى اﷲ نىڭ ھەق ئاساستا ياراتقانلىقىنى (يەنى بىكار ياراتمىغانلىقىنى) كۆرمىدىڭمۇ؟ ئەگەر اﷲ خالىسا، (ئى ئىنسانلار!) سىلەرنى يوق قىلىۋېتىپ (ئورنۇڭلارغا سىلەردىن ياخشى) يېڭى ئادەملەرنى يارىتىدۇ'),
(1770, 14, 20, 'بۇ اﷲ قا قىيىن ئەمەس'),
(1771, 14, 21, 'ئۇلار (يەنى خالايىقلار) نىڭ ھەممىسى (قىيامەت كۈنى قەبرىلىرىدىن) چىقىپ، اﷲ قا مۇلاقات بولىدۇ، ئاجىزلىرى (يەنى ئەگەشكۈچىلىرى) مۇتەكەببىرلىرىگە (يەنى ئەگەشتۈرگۈچىلىرىگە): «شۈبھىسىزكى، بىز سىلەرنىڭ ئەگەشكۈچىلىرىڭلار ئىدۇق، سىلەر بىزگە بولۇۋاتقان اﷲ نىڭ ئازابىدىن ئازراق دەپئى قىلالامسىلەر؟» دەيدۇ. ئۇلار: «اﷲ بىزنى ھىدايەت قىلغان بولسا ئىدى، بىز ئەلۋەتتە سىلەرنى ھىدايەت قىلاتتۇق، بىز مەيلى نالە - پەرياد قىلايلى، مەيلى سەۋر قىلايلى، بەربىر ئوخشاش، بىزگە ھېچقانداق قاچىدىغان جاي يوق» دەيدۇ'),
(1772, 14, 22, 'ئىش پۈتكەندە (يەنى ھېساب تۈگەپ، جەننەتىلەر بىلەن دوزىخىلار ئايرىلىپ بولغاندا)، شەيتان: «اﷲ ھەقىقەتەن سىلەرگە (ئىتائەت قىلغۇچىنى مۇكاپاتلاش، ئاسىيلىق قىلغۇچىنى جازالاشتىن ئىبارەت) راست ۋەدىنى قىلغان ئىدى، (ۋەدىسىگە ۋاپا قىلدى). مەن سىلەرگە (ئۆلگەندىن كېيىن تىرىلىش ساۋاب، جازا دېگەنلەر يوق، دەپ يالغان) ۋەدە قىلغان ئىدىم، (ۋەدەمگە) خىلاپلىق قىلدىم، سىلەرگە مېنىڭ (كۇفرىغا، گۇناھقا زورلىغۇدەك) ھۆكۈمرانلىقىم بولغىنى يوق، مەن سىلەرنى پەقەت (گۇمراھلىققا) دەۋەت قىلدىم، سىلەر (دەۋىتىمنى) قوبۇل قىلدىڭلار، شۇنىڭ ئۈچۈن مېنى ئەيىبلىمەڭلار، ئۆزۈڭلارنى ئەيىبلەڭلار، مەن سىلەرگە ياردەم بېرىپ سىلەرنى قۇتقۇزالمايمەن، سىلەرمۇ ماڭا ياردەم بېرىپ مېنى قۇتقۇزالمايسىلەر، مەن سىلەرنىڭ بۇرۇن مېنى اﷲ قا (ئىبادەتتە) شېرىك قىلغانلىقىڭلارنى ئېتىراپ قىلمايمەن، شۈبھىسىزكى، زالىملار چوقۇم قاتتىق ئازابقا دۇچار بولىدۇ» دەيدۇ'),
(1773, 14, 23, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار، پەرۋەردىگارىنىڭ ئىزنى بىلەن، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرىدۇ، ئۇ جەننەتلەردە مەڭگۈ قالىدۇ، (پەرىشتىلەر ئۇلارنى ھۆرمەتلەپ) سىلەرگە ئامانلىق بولسۇن، دەپ سالام بېرىدۇ'),
(1774, 14, 24, 'اﷲ نىڭ مۇنداق بىر تەمسىل كۆرسەتكەنلىكىنى كۆرمىدىڭمۇ. كەلىمە تەييىبە (يەنى ياخشى سۆز، ئىمان كەلىمىسى) يىلتىزى يەرنىڭ ئاستىدا بولغان، شېخى ئاسمانغا تاقاشقان'),
(1775, 14, 25, 'پەرۋەردىگارىنىڭ ئىزنى بىلەن ۋاقتى - ۋاقتىدا مېۋە بېرىپ تۇرىدىغان ئېسىل دەرەخقە ئوخشايدۇ. اﷲ كىشىلەرگە ۋەز - نەسىھەت ئالسۇن دەپ، ئۇلارغا نۇرغۇن تەمسىللەرنى كەلتۈرىدۇ'),
(1776, 14, 26, 'يامان سۆز (يەنى كۇفرى كەلىمىسى) زېمىندىن قومۇرۇپ تاشلانغان، ھېچقانداق قارارى يوق ناچار دەرەخقە ئوخشايدۇ'),
(1777, 14, 27, 'اﷲ مۆمىنلەرنى مۇستەھكەم ئىمان بىلەن دۇنيادا ۋە ئاخىرەتتە مەھكەم تۇرغۇزىدۇ. اﷲ زالىملا - نى گۇمراھ قىلىدۇ، اﷲ خالىغىنىنى قىلىدۇ'),
(1778, 14, 28, 'اﷲ نىڭ بەرگەن نېمىتىگە كۇفرىلىق قىلغان ۋە ئۆز قەۋمىنى (ئازدۇرۇش بىلەن) ھالاكەت مەۋقەسىگە چۈشۈرۈپ قويغانلارنى كۆرمىدىڭمۇ؟'),
(1779, 14, 29, '(ھالاكەت مەۋقەسى) جەھەننەمدۇركى، ئۇلار جەھەننەمگە كىرىدۇ، ئۇ نېمىدېگەن يامان جاي!'),
(1780, 14, 30, 'ئۇلار (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى ئىسلام دىنىدىن) ئازدۇرۇش ئۈچۈن، اﷲ قا نۇرغۇن بۇتلارنى شېرىك قىلىۋالدى. ئۇلارغا: «(دۇنيانىڭ نېمەتلىرىدىن) بەھرىمەن بولدۇڭلار، شۈبھىسىزكى، سىلەرنىڭ بارىدىغان جايىڭلار دوزاختۇر» دېگىن'),
(1781, 14, 31, 'مۆمىن بەندىلىرىمگە ئېيتقىنكى، ئۇلار نامازنى ئادا قىلسۇن، ئالدى - ساتتىمۇ، دوستلۇقمۇ بولمايدىغان كۈن (يەنى قىيامەت كۈنى) كېلىشتىن بۇرۇن، بىز ئۇلارغا بەرگەن مال - مۈلۈكتىن يوشۇرۇن ۋە ئاشكارا يوسۇندا سەدىقە قىلسۇن'),
(1782, 14, 32, 'اﷲ ئاسمانلارنى ۋە زېمىننى ياراتتى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، يامغۇر سۈيى بىلەن سىلەرگە رىزىق قىلىپ نۇرغۇن مېۋىلەرنى ئۆستۈرۈپ بەردى، اﷲ سىلەرگە ئۆز ئەمرى بويىچە دېڭىزدا قاتنايدىغان كېمىلەرنى بويسۇندۇرۇپ بەردى، سىلەرگە دەريالارنى بويسۇندۇرۇپ بەردى'),
(1783, 14, 33, 'سىلەرگە ئاي بىلەن كۈننى تەرتىپلىك دەۋر قىلىپ تۇرىدىغان قىلىپ بويسۇندۇرۇپ بەردى، سىلەرگە كېچە بىلەن كۈندۈزنى بويسۇندۇرۇپ بەردى'),
(1784, 14, 34, 'اﷲ سىلەرگە سورىغان نەرسەڭلارنىڭ ھەممىسىنى بەردى، سىلەر اﷲ نىڭ نېمەتلىرىنى ساناپ تۈگىتەلمەيسىلەر، كاپىر ئادەم، شەك - شۈبھىسىزكى، زۇلۇم قىلغۇچىدۇر، (اﷲ نىڭ نېمەتلىرىگە) كۇفرىلىق قىلغۇچىدۇر'),
(1785, 14, 35, 'ئۆز ۋاقتىدا ئىبراھىم ئېيتتى: «پەرۋەردىگارىم! بۇ جاينى (يەنى مەككىنى) تىنچ (جاي) قىلىپ بەرگىن، مېنى ۋە مېنىڭ ئەۋلادلىرىمنى بۇتلارغا چوقۇنۇشتىن يىراق قىلغىن'),
(1786, 14, 36, 'پەرۋەردىگارىم! ئۇلار (يەنى بۇتلار) نۇرغۇن كىشىلەرنى ئازدۇردى، كىمكى ماڭا ئەگەشكەن ئىكەن، ئۇ مېنىڭ دىنىمدىدۇر، كىمكى ماڭا ئاسىيلىق قىلغان ئىكەن، سەن ئەلۋەتتە (ئۇنىڭغا) مەغپىرەت قىلىشقا ۋە مەرھەمەت قىلىشقا (قادىرسەن)'),
(1787, 14, 37, 'پەرۋەردىگارىمىز! ئەۋلادىمنىڭ بىر قىسىمىنى (يەنى بالام ئىسمائىل بىلەن ئايالىم ھاجەرنى) ناماز ئوقۇسۇن (يەنى ساڭا ئىبادەت قىلسۇن دەپ) سېنىڭ ھۆرمەتلىك ئۆيۈڭنىڭ قېشىدىكى ئېكىنسىز بىر ۋادىغا (يەنى مەككىگە) ئورۇنلاشتۇردۇم، پەرۋەردىگارىمىز! بىر قىسىم كىشىلەرنىڭ دىللىرىنى ئۇلارغا مايىل قىلغىن، ئۇلارنى شۈكۈر قىلسۇن دەپ تۈرلۈك مېۋىلەرنى ئۇلارغا رىزىق قىلىپ بەرگىن'),
(1788, 14, 38, 'پەرۋەردىگارىمىز! سەن بىزنىڭ يوشۇرۇن ۋە ئاشكارا قىلغانلىرىمىزنى بىلىپ تۇرىسەن، ئاسمان - زېمىندىكى ھېچ نەرسە اﷲ قا مەخپىي ئەمەس'),
(1789, 14, 39, 'جىمى ھەمدۇسانا ياشانغىنىمدا ماڭا ئىسمائىل بىلەن ئىسھاقنى بەرگەن اﷲ قا خاستۇر! پەرۋەردىگارىم! ھەقىقەتەن دۇئانى ئاڭلاپ تۇرغۇچىدۇر (يەنى دۇئا قىلغۇچىنىڭ دۇئاسىنى ئىجابەت قىلغۇچىدۇر)'),
(1790, 14, 40, 'پەرۋەردىگارىم! مېنى ۋە بىر قىسىم ئەۋلادىمنى نامازنى ئادا قىلغۇچى قىلغىن، پەرۋەردىگارىمىز! دۇئايىمنى قوبۇل قىلغىن'),
(1791, 14, 41, 'پەرۋەردىگارىمىز! ھېساب ئالىدىغان كۈندە (يەنى قىيامەت كۈنىدە) ماڭا، ئاتا - ئانامغا ۋە مۆمىنلەرگە مەغپىرەت قىلغىن»'),
(1792, 14, 42, 'زالىملارنىڭ قىلمىشلىرىدىن اﷲ نى بىخەۋەر دەپ ئويلىمىغىن، اﷲ ئۇلارنى جازالاشنى (شۇ كۈنىنىڭ دەھشىتىدىن) كۆزلەر چەكچىيىپ قالىدىغان كۈنىگىچە (يەنى قىيامەت كۈنىگىچە) كېچىكتۈرىدۇ'),
(1793, 14, 43, '(بۇ كۈندە) ئۇلار باشلىرىنى كۆتۈرۈپ، ئالغا قاراپ چاپىدۇ، كۆزلىرى ئېچىلىپ قالىدۇ، (تېڭىرقىغانلىقتىن) دىللىرى (ئەقىل - ئىدراكتىن) خالىي بولۇپ قالىدۇ'),
(1794, 14, 44, '(ئى مۇھەممەد!) كىشىلەرنى (يەنى كۇففارلارنى) ئازاب كېلىدىغان كۈن (يەنى قىيامەت كۈنى) دىن ئاگاھلاندۇرغىن، (بۇ كۈندە) زالىملار: «پەرۋەردىگارىمىز! بىزگە قىسقىغىنا ۋاقىت مۆھلەت بەرگىن (يەنى دۇنياغا قايتۇرغىن)، دەۋىتىڭنى قوبۇل قىلايلى، پەيغەمبەرلەرگە ئەگىشەيلى» دەيدۇ. (ئۇلارغا) سىلەر ئىلگىرى، بىز ھەرگىز يوقالمايمىز، دەپ قەسەم ئىچمىگەنمىدىڭلار؟ (دېيىلىدۇ)'),
(1795, 14, 45, 'ئۆزلىرىگە زۇلۇم قىلغانلارنى (ھالاك قىلغىنىمىزدىن كېيىن) سىلەر ئۇلارنىڭ جايلىرىدا تۇردۇڭلار (ئۇلارنىڭ جايلىرىدىن ئىبرەت ئالساڭلارچۇ)، ئۇلارنى قانداق جازالىغانلىقىمىز سىلەرگە ئېنىق مەلۇم بولدى. سىلەرگە نۇرغۇن مىساللارنى كەلتۈردۇق (يەنى پەيغەمبەرلەرگە چىنپۈتمىگەنلەرنىڭ ئەھۋالىنى بايان قىلدۇق)'),
(1796, 14, 46, 'ئۇلار ھەقىقەتەن (پەيغەمبەرلەرگە ۋە مۆمىنلەرگە) مىكىر ئىشلەتتى. ئۇلارنىڭ مىكرى تاغنى ئۆرۈۋەتكۈدەك (چوڭ) بولسىمۇ، اﷲ ئۇلارنىڭ مىكرىنى بەربات قىلىدۇ'),
(1797, 14, 47, 'اﷲ نى پەيغەمبەرلىرىگە قىلغان ۋەدىسىگە خىلاپلىق قىلىدۇ دەپ ئويلىمىغىن، اﷲ ھەقىقەتەن غالىبتۇر، (دوستلىرى ئۈچۈن دۈشمەنلىرىدىن) ئىنتىقام ئالغۇچىدۇر'),
(1798, 14, 48, 'ئۇ كۈندە زېمىنمۇ باشقا بىر زېمىنغا، ئاسمانلارمۇ باشقا بىر ئاسمانلارغا ئايلىنىدۇ، ئۇلار (يەنى پۈتۈن خالايىق قەبرىلىرىدىن چىقىپ) يېگانە، قۇدرەتلىك اﷲ نىڭ دەرگاھىدا (يەنى مەھشەرگاھدا) ھازىر بولىدۇ'),
(1799, 14, 49, '(ئى مۇھەممەد!) ئۇ كۈندە گۇناھكارلار (يەنى كۇففارلار) نى زەنجىرلەر بىلەن بىر - بىرىگە چېتىلىپ باغلانغان ھالدا كۆرۈسەن'),
(1800, 14, 50, 'ئۇلارنىڭ كۆڭلىكى قارامايدىن بولىدۇ، ئوت ئۇلارنىڭ يۈزلىرىنى ئورىۋالىدۇ'),
(1801, 14, 51, 'اﷲ ھەر كىشىنىڭ قىلمىشىغا قاراپ شۇنداق جازا بېرىدۇ، اﷲ ئەلۋەتتە تېز ھېساب ئالغۇچىدۇر'),
(1802, 14, 52, 'كىشىلەرنىڭ ئاگاھلاندۇرۇلۇشى، اﷲ نىڭ بىر ئىكەنلىكىنى بىلىشلىرى، ئەقىل ئىگىلىرىنىڭ ۋەز - نەسىھەت ئېلىشلىرى ئۈچۈن، بۇ (قۇرئان) تەبلىغدۇر (يەنى كىشىلەرگە تەبلىغ قىلىش ئۈچۈن نازىل قىلىندى)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'ئەلىف، لام، را. بۇ، كىتابنىڭ - روشەن قۇرئاننىڭ ئايەتلىرىدۇر'),
(1804, 15, 2, 'كاپىرلار (قىيامەتنىڭ دەھشىتىنى كۆرگەندە، دۇنيادىكى چېغىمىزدا) مۇسۇلمان بولغان بولساقچۇ، دەپ ئارمان قىلىدۇ'),
(1805, 15, 3, '(ئى مۇھەممەد!) ئۇلارنى (يەنى كۇففارلارنى مەيلىگە) قويۇۋەت، يەپ - ئىچىپ، ئويناپ - كۈلۈپ يۈرسۇن، ئۇلار بىھۇدە ئارزۇلار بىلەن بولىۋەرمىسۇن، ئۇلار ئۇزاققا قالماي (ئۆز قىلمىشلىرىنىڭ يامانلىقىنى) بىلىدۇ'),
(1806, 15, 4, 'بىز قانداق بىر شەھەرنى ھالاك قىلمايلى، پەقەت ئۇنىڭ (ھالاك بولىدىغان) مۇئەييەن ۋاقتى بولىدۇ'),
(1807, 15, 5, 'ھەرقانداق ئۈممەتنىڭ (ھالاك بولىدىغان ۋاقتى) ئىلگىرى سۈرۈلمەيدۇ، ھەم كېچىكتۈرۈلمەيدۇ'),
(1808, 15, 6, 'ئۇلار ئېيتتى: «ئى قۇرئان نازىل قىلىنغان ئادەم! سەن ھەقىقەتەن مەجنۇنسەن'),
(1809, 15, 7, 'ئەگەر سەن (مەن رەسۇلۇللاھ دېگەن سۆزۈڭدە) راستچىللاردىن بولساڭ، (پەيغەمبەرلىكىڭگە گۇۋاھلىق بېرىدىغان) پەرىشتىلەرنى ئالدىمىزغا ئېلىپ كەلسەڭچۇ؟»'),
(1810, 15, 8, 'پەرىشتىلەرنى پەقەت ھەق ئاساسىدا (يەنى ئازاب نازىل قىلىشىمىز مۇقەررەر بولغان چاغدىلا) نازىل قىلىمىز، بۇ چاغدا ئۇلارغا مۆھلەت بېرىلمەيدۇ (يەنى ئۇلارنىڭ ئازابى كېچىكتۈرۈلمەيدۇ)'),
(1811, 15, 9, 'قۇرئاننى ھەقىقەتەن بىز نازىل قىلدۇق ۋە چوقۇم ئۇنى قوغدايمىز'),
(1812, 15, 10, 'شۈبھىسىزكى، سېنىڭدىن ئىلگىرىكى بۇرۇنقى ھەرقايسى تائىپىلەرگە نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق'),
(1813, 15, 11, 'ئۇلار ئۆزلىرىگە پەيغەمبەر كەلسىلا ئۇنى مەسخىرە قىلىشتى'),
(1814, 15, 12, 'شۇنداق مەسخىرە قىلىشنى بىز گۇناھكارلارنىڭ دىللىرىغا سالىمىز'),
(1815, 15, 13, 'ئۇلار قۇرئانغا ئىشەنمەيدۇ، بۇ بۇرۇندىن ئادەت بولۇپ قالغان (يەنى بۇرۇندىن تارتىپ پەيغەمبەرلىرنى ئىنكار قىلغان قەۋملەرنى اﷲ نىڭ ھالاك قىلىشى ئادەت بولۇپ كېلىۋاتقان) ئىش'),
(1816, 15, 14, 'بىز ئۇلارغا ئاسماندىن بىر ئىشىك ئېچىپ بەرگىن، ئۇلار ئۇنىڭدىن ئاسمانغا ئۆرلەپ (ئالەمى ئەرۋاھنى، پەرىشتىلەرنى كۆرگەن تەقدىردىمۇ)'),
(1817, 15, 15, 'ئۇلار چوقۇم: «كۆزلىرىمىز باغلىنىپ قالدى، بەلكىم سېھىرلەنگەن قەۋم بولۇپ قالساق كېرەك» دېيىشىدۇ'),
(1818, 15, 16, 'بىز ئاسماندا بۇرۇجلارنى ياراتتۇق ۋە كۆزەتكۈچىلەر ئۈچۈن ئاسماننى (يۇلتۇزلار بىلەن) بېزىدۇق'),
(1819, 15, 17, 'ھەر بىر قوغلاندى شەيتاندىن ئاسماننى قوغدىدۇق'),
(1820, 15, 18, 'لېكىن (ئاسمان خەۋەرلىرىگە) ئوغرىلىقچە قۇلاق سالماقچى بولغان شەيتاننى روشەن ئاقار يۇلتۇز قوغلاپ يېتىدۇ (يەنى ھالاك قىلىدۇ)'),
(1821, 15, 19, 'يەر يۈزىنى يېيىپ كەڭ قىلدۇق، ئۇنىڭغا تاغلارنى ئورناتتۇق، يەر يۈزىدە ھەرخىل تەكشى ئۆلچەملىك ئۆسۈملۈكلەرنى ئۆستۈردۇق'),
(1822, 15, 20, 'يەر يۈزىدە سىلەر ئۈچۈن ۋە سىلەر رىزقىنى بېرەلمەيدىغانلار (يەنى خادىملار ۋە ھايۋانلار) ئۈچۈن تۇرمۇش لازىمەتلىكلىرىنى ياراتتۇق (ئۇلارنىڭ يېمەك - ئىچمەكلىرىنى سىلەر ئەمەس، بىز يارىتىمىز)'),
(1823, 15, 21, 'قانداق نەرسە بولمىسۇن، ئۇنىڭ خەزىنىسى (يەنى خەزىنىنىڭ ئاچقۇچى) بىزنىڭ دەرگاھىمىزدىدۇر، بىز ئۇنى پەقەت مەلۇم مىقداردىلا چۈشۈرىمىز'),
(1824, 15, 22, 'بىز يامغۇرنى ھەيدەيدىغان شاماللارنى ئەۋەتتۇق، بىز بۇلۇتتىن يامغۇر ياغدۇرۇپ سىلەرنى سۇغاردۇق، سىلەر ھەرگىز ئۇنى ساقلاشقا قادىر ئەمەسسىلەر'),
(1825, 15, 23, 'بىز (ھەممىنى) تىرىلدۈرىمىز، (ھەممىنى) ئۆلتۈرىمىز ۋە (ھەممىگە) ۋارىسلىق قىلىمىز'),
(1826, 15, 24, 'بىز سىلەردىن ئىلگىرى ئۆتكەنلەرنى ھەقىقەتەن بىلىمىز ۋە كېيىن كېلىدىغانلارنىمۇ ھەقىقەتەن بىلىمىز'),
(1827, 15, 25, 'پەرۋەردىگارىڭ ھەقىقەتەن (ھېساب ئېلىش ۋە جازالاش ئۈچۈن) ئۇلارنى يىغىدۇ. اﷲ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر'),
(1828, 15, 26, 'شەك - شۈبھىسىزكى، بىز ئىنسان (يەنى ئادەم ئەلەيھىسسالامنى) نى قارا لايدىن ياسىلىپ شەكىلگە كىرگۈزۈلگەن (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن ياراتتۇق'),
(1829, 15, 27, 'جىنلار (يەنى شەيتانلار ۋە ئۇلارنىڭ باشلىقى ئىبلىس) نى ئىلگىرى تۈتۈنسىز ئوت يالقۇنىدىن ياراتقان ئىدۇق'),
(1830, 15, 28, 'پەرۋەردىگارىڭ ئۆز ۋاقتىدا پەرىشتىلەرگە ئېيتتى: «مەن ھەقىقەتەن قارا لايدىن ياسىلىپ شەكىلگە كىرگۈزۈلگەن (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن ئىنسان (يەنى ئادەم ئەلەيھىسسالام) نى يارىتىمەن'),
(1831, 15, 29, 'مەن ئۇنى تولۇق ياراتقان (يەنى ئۇنى يارىتىپ سۈرەتكە كىرگۈزۈپ، ئەزالىرى تولۇق ۋە مۇكەممەل ئىنسان ھالىتىگە كەلتۈرگەن) ۋە ئۇنىڭغا جان كىرگۈزگەن چېغىمدا، سىلەر ئۇنىڭغا سەجدە قىلىڭلار)'),
(1832, 15, 30, 'پەرىشتىلەرنىڭ بىرىمۇ ئايرىلىپ قالماي ھەممىسى (ئادەم ئەلەيھىسسالامغا) سەجدە قىلدى'),
(1833, 15, 31, 'پەقەت ئىبلىسلا سەجدە قىلىشتىن باش تارتتى'),
(1834, 15, 32, 'اﷲ: «ئى ئىبلىس! سەن نېمىشقا سەجدە قىلمايسەن؟» دېدى'),
(1835, 15, 33, 'ئىبلىس: «قارا لايدىن ياساپ شەكىلگە كىرگۈزۈپ (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن يارىتىلغان ئىنسان مېنىڭ سەجدە قىلىشىمغا لايىق ئەمەس» دېدى'),
(1836, 15, 34, 'اﷲ ئېيتتى: «سەن جەننەتتىن يوقال، ھەقىقەتەن قوغلاندى بولدۇڭ'),
(1837, 15, 35, 'ساڭا قىيامەت كۈنىگىچە ھەقىقەتەن لەنەت بولسۇن»'),
(1838, 15, 36, 'ئىبلىس: «پەرۋەردىگارىم! ماڭا خالايىقلار تىرىلدۈرۈلىدىغان كۈن (يەنى قىيامەت كۈنى) گىچە مۆھلەت بەرگىن (مېنى ھايات قالدۇرغىن)» دېدى'),
(1839, 15, 37, 'اﷲ: «ساڭا ھەقىقەتەن مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى'),
(1840, 15, 38, 'اﷲ: «ساڭا ھەقىقەتەن مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى'),
(1841, 15, 39, 'ئىبلىس ئېيتتى: «پەرۋەردىگارىم! سەن مېنى گۇمراھ قىغانلىقىڭ ئۈچۈن، يەر يۈزىدە ئادەم بالىلىرىغا (گۇناھلارنى) ئەلۋەتتە چىرايلىق كۆرسىتىمەن، ئۇلارنىڭ ھەممىسىنى ئەلۋەتتە ئازدۇرىمەن'),
(1842, 15, 40, 'ئۇلاردىن پەقەت تاللانغان بەندىلىرىڭلا بۇنىڭدىن مۇستەسنا (يەنى ئۇلارنى ئازدۇرالمايمەن)»'),
(1843, 15, 41, 'اﷲ ئېيتتى: «بۇ مەن ساقلاشقا تېگىشلىك توغرا يولدۇر'),
(1844, 15, 42, 'مېنىڭ بەندىلىرىمگە (يەنى ئۇلارنى ئازدۇرۇشقا) سېنىڭ كۈچۈڭ يەتمەيدۇ، پەقەت گۇمراھلاردىن ساڭا ئەگەشكەنلەرگىلا (سېنىڭ كۈچۈڭ يېتىدۇ)»'),
(1845, 15, 43, 'شۈبھىسىزكى، دوزاخ ئۇلار (يەنى ئىبلىس ۋە ئۇنىڭ تەۋەلىرى) نىڭ ھەممىسىگە ۋەدە قىلىنغان جايدۇر'),
(1846, 15, 44, 'جەھەننەمنىڭ يەتتە دەرۋازىسى بولۇپ، ئۇلارنىڭ ھەربىرىدىن كىرىدىغان مۇئەييەن بىر بۆلۈك (ئادەم) بار'),
(1847, 15, 45, 'تەقۋادارلار ھەقىقەتەن (ئاخىرەتتە ياپيېشىل) باغ - بوستانلاردىن، سۇ، ھەسەل ۋە مەي ئېقىپ تۇرىدىغان) بۇلاقلاردىن بەھرىمەن بولىدۇ'),
(1848, 15, 46, '(ئۇلارغا) جەننەتكە ئامان - ئېسەن، تىنچ كىرىڭلار! دېيىلىدۇ'),
(1849, 15, 47, 'ئۇلارنىڭ دىللىرىدىكى دۈشمەنلىكنى چىقىرىپ تاشلايمىز، ئۇلار ئۆزئارا قېرىنداش بولغان ھالدا تەختلەر ئۈستىدە بىر - بىرىگە قارىشىپ ئولتۇرىدۇ'),
(1850, 15, 48, 'جەننەتتە ئۇلارغا چارچاش بولمايدۇ، ئۇلار جەننەتتىن چىقىرىۋېتىلمەيدۇ'),
(1851, 15, 49, '(ئى مۇھەممەد!) مېنىڭ (مۆمىن) بەندىلىرىمگە خەۋەر قىلغىنكى، مەن تولىمۇ مەغپىرەت قىلغۇچىمەن، ناھايىتى رەھىم قىلغۇچىمەن'),
(1852, 15, 50, 'مېنىڭ ئازابىم قاتتىقتۇر'),
(1853, 15, 51, 'ئۇلارغا ئىبراھىمنىڭ مېھمانلىرى ھەققىدىكى (ھېكايىنى) ئېيتىپ بەرگىن'),
(1854, 15, 52, 'ئۆز ۋاقتىدا پەرىشتىلەر ئىبراھىمنىڭ يېنىغا كىرىپ سالام بەردى، ئىبراھىم: «بىز ھەقىقەتەن سىلەردىن قورقۇۋاتىمىز» دېدى'),
(1855, 15, 53, 'ئۇلار: «قورقمىغىن، (بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىمىز)، ھەقىقەتەن ساڭا (ئۆسۈپ چوڭ بولغىنىدا) ناھايىتى بىلىملىك (بولىدىغان) بىر ئوغۇل (يەنى ئىسھاق) بىلەن خۇش خەۋەر بېرىمىز» دېدى'),
(1856, 15, 54, 'ئىبراھىم: «مەن قېرىغان تۇرسام، ماڭا (بالا بىلەن) خۇش خەۋەر بەردىڭلارمۇ؟ ماڭا نېمە بىلەن خۇش خەۋەر بېرىۋاتىسىلەر؟» دېدى'),
(1857, 15, 55, 'ئۇلار: «بىز ساڭا راست خۇش خەۋەر بەردۇق، نائۈمىد بولمىغىن» دېدى'),
(1858, 15, 56, 'ئىبراھىم: «پەرۋەردىگارىڭنىڭ رەھمىتىدىن گۇمراھلاردىن باشقا كىممۇ نائۈمىد بولار؟» دېدى'),
(1859, 15, 57, 'ئىبراھىم: «ئى ئەلچىلەر! نېمە ئىش بىلەن كەلدىڭلار؟» دېدى'),
(1860, 15, 58, 'ئۇلار ئېيتتى: «بىز ھەقىقەتەن گۇناھكار بىز قەۋم (نى جازالاش) ئۈچۈن ئەۋەتىلدۇق'),
(1861, 15, 59, 'پەقەت لۇتنىڭ تەۋەلىرىلا بۇنىڭدىن مۇستەسنا، بىز ئۇلار (يەنى لۇتنىڭ تەۋەلىرى ۋە ئۇنىڭ ئائىلىسىدىكىلەر) نىڭ ھەممىسىنى (ئازابتىن) چوقۇم قۇتۇلدۇرىمىز'),
(1862, 15, 60, 'پەقەت لۇتنىڭ خوتۇنىلا بۇنىڭدىن مۇستەسنا، بىز ئۇنىڭ چوقۇم باشقىلار بىلەن بىللە ئازابلىنىدىغانلىقىنى ئالدىن بېكىتتۇق»'),
(1863, 15, 61, 'ئەلچىلەر لۇتنىڭ ئائىلىسىگە كەلگەن چاغدا'),
(1864, 15, 62, 'لۇت: «سىلەر ھەقىقەتەن ناتونۇش ئادەملەرغۇ؟» دېدى'),
(1865, 15, 63, 'ئۇلار ئېيتتى: «ئۇنداق ئەمەس (بىز اﷲ نىڭ ئەلچىلىرىمىز)، بىز سېنىڭ ئالدىڭغا ئۇلار (يەنى سېنىڭ قەۋمىڭ) شەكلىنىۋاتقان ئازابنى ئېلىپ كەلدۇق'),
(1866, 15, 64, 'بىز ساڭا ھەقىقەتنى ئېلىپ كەلدۇق، بىز ئەلۋەتتە (سۆزىمىزدە) راستچىلمىز'),
(1867, 15, 65, 'كېچىنىڭ ئاخىرىدا ئائىلەڭدىكىلەرنى ئېلىپ يولغا چىققىن، ئۆزۈڭ ئۇلارنىڭ ئارقىسىدا ماڭغىن، سىلەردىن ھېچبىر ئادەم ئارقىسىغا قارىمىسۇن، سىلەر بۇيرۇلغان يەرگە يەتكۈچە ئالغا قاراپ مېڭىۋېرىڭلار»،'),
(1868, 15, 66, 'سەھەردە بۇلار (يەنى لۇتنىڭ قەۋم) نىڭ يىلتىزى قۇرۇتۇلىدۇ (يەنى تەلتۆكۈس ھالاك قىلىنىدۇ) دېگەن ھۆكۈمنى لۇتقا ۋەھىي قىلدۇق'),
(1869, 15, 67, '(سەدۇم) شەھرىنىڭ ئاھالىسى (يەنى لۇتنىڭ قەۋمى) خۇشال بولۇشۇپ يېتىپ كېلىشتى'),
(1870, 15, 68, 'لۇت ئېيتتى: «بۇلار مېنىڭ مېھمىنىم، (ئۇلارغا چېقىلىپ قويۇپ ئۇلارنىڭ ئالدىدا) مېنى رەسۋا قىلماڭلار'),
(1871, 15, 69, 'اﷲ تىن قورقۇڭلار (مېھمىنىمنى خارلاش ئارقىلىق) مېنى خارلىماڭلار»'),
(1872, 15, 70, 'ئۇلار: «بىز سېنى كەلگەن مۇساپىرلارنى ھىمايە قىلىشتىن توسمىغانمىدۇق؟» دېدى'),
(1873, 15, 71, 'لۇت: «بۇلار مېنىڭ قىزلىرىم (يەنى قەۋمىمنىڭ قىزلىرى) دۇر، ئەگەر (قازائى شەھۋەت) قىلماقچى بولساڭلار (ئۇلارنى نىكاھلاپ ئېلىڭلار)» دېدى'),
(1874, 15, 72, '(ئى مۇھەممەد!) سېنىڭ ھاياتىڭ بىلەن قەسەمكى، ئۇلار ئەلۋەتتە گۇمراھلىقلىرىدا تېڭىرقاپ يۈرۈشىدۇ'),
(1875, 15, 73, 'كۈن چىقار ۋاقتىدا ئۇلارغا قاتتىق چۇقان يۈزلەندى'),
(1876, 15, 74, 'بىز ئۇ شەھەرنى ئاستىن - ئۈستۈن قىلىۋەتتۇق (يەنى كۆمتۈرۈۋەتتۇق) ۋە ئۇلارنىڭ ئۈستىىگە (يامغۇردەك) ساپال تاش ياغدۇردۇق'),
(1877, 15, 75, 'ئۇنىڭدا (يەنى ئۇلارغا نازىل بولغان ئازابتا) كۆزەتكۈچىلەر ئۈچۈن ھەقىقەتەن نۇرغۇن ئىبرەتلەر بار'),
(1878, 15, 76, 'ئۇ شەھەر (قۇرەيشلەر شامغا بارىدىغان) يول ئۈستىدە ھەقىقەتەن ھېلىمۇ مەۋجۇت تۇرماقتا'),
(1879, 15, 77, 'بۇنىڭدا مۆمىنلەر ئۈچۈن ھەقىقەتەن ئىبرەت بار'),
(1880, 15, 78, 'ئەيكەلىكلەر، شۈبھىسىزكى، زالىم ئىدى (يەنى كۇفرىدا ھەددىدىن ئاشقانلار ئىدى)'),
(1881, 15, 79, '(شۇئەيبنىڭ پەيغەمبەرلىكىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئۇلارنى ھالاك قىلدۇق، ئۇلار (يەنى بۇ ئىككى شەھەر) نىڭ ھەر ئىككىلىسى، شۈبھىسىزكى، ئاشكارا يول ئۈستىدىدۇر (ئى مەككە ئاھالىسى! ئۇلاردىن ئىبرەت ئالمامسىلەر؟)'),
(1882, 15, 80, 'ھىجرىلىكلەر ھەقىقەتەن پەيغەمبەرلەرنى ئىنكار قىلدى (يەنى ئۇلارنىڭ سالىھ ئەلەيھىسسالامنى ئىنكار قىلغانلىقى باشقا پەيغەمبەرلەرنىمۇ ئىنكار قىلغانلىقىدۇر)'),
(1883, 15, 81, 'ئۇلارغا مۆجىزىلىرىمىزنى كەلتۈردۇق (يەنى تاشتىن چىشى تۆگە چىقىرىشتەك قۇدرىتىمىزگە دالالەت قىلىدىغان مۆجىزىلەرنى ئۇلارغا كۆرسەتتۇق)، ئۇلار بۇ (مۆجىزە) لەردىن يۈز ئۆرۈدى'),
(1884, 15, 82, 'ئۇلار (ئازابتىن) ئەمىن بولغان ھالدا تاغلارنى تېشىپ (بۇ بىزنى اﷲ نىڭ ئازابىدىن ساقلايدۇ دەپ) ئۆي سالاتتى'),
(1885, 15, 83, 'لېكىن قاتتىق چۇقان سەھەردىلا ئۇلارنى ھالاك قىلدى'),
(1886, 15, 84, 'ئۇلارنىڭ قىلغان ئەمگەكلىرى ئۆزلىرىگە ئەسقاتمىدى (يەنى ئۇلارنىڭ سالغان بىنالىرى ئۇلارنى اﷲ نىڭ ئازابىدىن ساقلاپ قالالمىدى)'),
(1887, 15, 85, 'بىز ئاسمانلارنى، زېمىننى ۋە ئۇلاردىكى نەرسىلەرنى پەقەت ھەق ئاساسىدىلا ياراتتۇق، قىيامەتنىڭ بولۇشى چوقۇمدۇر، (ئى مۇھەممەد! نادانلار بىلەن تەڭ بولمىغىن، ئۇلارغا) چىرايلىق مۇئامىلىدە بولغىن'),
(1888, 15, 86, 'پەرۋەردىگارىڭ ھەقىقەتەن ھەممىنى ياراتقۇچىدۇر، (بەندىلەرنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر'),
(1889, 15, 87, '(نامازدا) تەكرارلىنىپ تۇرىدىغان يەتتە ئايەتنى (يەنى سۈرە فاتىھەنى) ۋە ئۇلۇغ قۇرئاننى ھەقىقەتەن ساڭا ئاتا قىلدۇق'),
(1890, 15, 88, 'بەزى كاپىرلارنىڭ بىز بەھرىمەن قىلغان نەرسىلىرىگە كۆز سالمىغىن، ئۇلار (نىڭ ئىمان ئېيتمىغانلىقىدىن) قايغۇرمىغىن، مۆمىنلەرگە كەمتەر بولغىن'),
(1891, 15, 89, '«مەن ھەقىقەتەن (سىلەرنى اﷲ نىڭ ئازابىنىڭ نازىل بولۇشىدىن) ئاشكارا ئاگاھلاندۇرغۇچىمەن» دېگىن'),
(1892, 15, 90, 'پارچىلىۋەتكۈچىلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا تەۋرات بىلەن ئىنجىلنى) نازىل قىلغاندەك (ساڭا سۈرە فاتىھە بىلەن ئۇلۇغ قۇرئاننى نازىل قىلدۇق)'),
(1893, 15, 91, 'ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) قۇرئاننى پارچە - پەرچە قىلىۋەتتى (يەنى قۇرئاننىڭ بەزىسىگە ئىشىنىپ، بەزىسىنى ئىنكار قىلدى)'),
(1894, 15, 92, 'پەرۋەردىگارىڭنىڭ نامى بىلەن قەسەمكى، ئۇلار (يەنى خالايىق) نىڭ ھەممىسىنىڭ (بۇ دۇنيادا) قىلغان ئىشلىرىنى چوقۇم سورايمىز'),
(1895, 15, 93, 'پەرۋەردىگارىڭنىڭ نامى بىلەن قەسەمكى، ئۇلار (يەنى خالايىق) نىڭ ھەممىسىنىڭ (بۇ دۇنيادا) قىلغان ئىشلىرىنى چوقۇم سورايمىز'),
(1896, 15, 94, 'ساڭا بۇيرۇلغاننى (يەنى پەرۋەردىگارىڭنىڭ ئەمرىنى) ئاشكارا ئوتتۇرىغا قويغىن، مۇشرىكلار (نىڭ مەسخىرەلىرىگە) پەرۋا قىلمىغىن'),
(1897, 15, 95, 'سېنى مەسخىرە قىلغۇچىلارغا بىز چوقۇم تېتىيمىز'),
(1898, 15, 96, 'ئۇلار باشقا مەبۇدلارنى اﷲ قا شېرىك قىلىدۇ، ئۇلار ئۇزاققا قالماي (ئىشنىڭ ئاقىۋىتىنى) بىلىدۇ'),
(1899, 15, 97, 'بىز ئۇلارنىڭ سۆزلىرىدىن يۈرىكىڭنىڭ سېقىلىۋاتقانلىقىنى ئوبدان بىلىمىز'),
(1900, 15, 98, 'پەرۋەردىگارىڭغا ھەمدۇ ۋە تەسبىھ ئېيتقىن، ھەم سەجدە قىلغۇچىلاردىن بولغىن'),
(1901, 15, 99, 'سەن ئۆزۈڭگە ئۆلۈم كەلگەنگە (يەنى ئەجىلىڭ يەتكەنگە) قەدەر پەرۋەردىگارىڭغا ئىبادەت قىلغىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'اﷲ نىڭ پەرمانى (يەنى قىيامەت) چوقۇم كېلىدۇ، ئۇنىڭغا ئالدىراپ كەتمەڭلار، اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە يۈكسەكتۇر'),
(1903, 16, 2, 'اﷲ ئۆز ئىرادىسى بويىچە خالىغان بەندىلىرىگە پەرىشتىلەرنى ۋەھيى بىلەن نازىل قىلىپ (مۇنداق دەيدۇ): «(ئىنسانلارنى) ئاگاھلاندۇرۇڭلاركى، مەندىن باشقا ھېچ ئىلاھ يوقتۇر، مەندىن (يەنى ئازابىمدىن) قورقۇڭلار»'),
(1904, 16, 3, 'اﷲ ئاسمانلارنى ۋە زېمىننى ھەق ياراتتى، اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن يۈكسەكتۇر'),
(1905, 16, 4, 'ئۇ ئىنساننى ئابىمەنىيدىن ياراتتى، ناگاھان ئۇ (اﷲ قا) ئاشكارا خۇسۇمەت قىلغۇچىدۇر'),
(1906, 16, 5, 'اﷲ چارۋا ماللارنى سىلەرنىڭ مەنپەئەتلىنىشىڭلار ئۈچۈن ياراتتى، ئۇلار (نىڭ يۇڭى ۋە تېرىسى) بىلەن ئىسسىنىسىلەر، ئۇلار (نىڭ نەسلى، سۈتى، سۆڭىكى ۋە قىغى) دىن پايدىلىنىسىلەر ۋە ئۇلار (نىڭ گۆشلىرى) نى يەيسىلەر'),
(1907, 16, 6, 'ئۇلارنى كەچتە ئوتلاقتىن قايتۇرۇپ كەلگەن ۋە ئەتىگەندە ئوتلاققا ئېلىپ چىققان ۋاقتىڭلاردا، چارۋا ماللار سىلەرگە زىننەت بولۇپ (خۇشاللىق بېغىشلايدۇ)'),
(1908, 16, 7, 'ئۇلار يۈك - تاقلىرىڭلارنى سىلەر جاپا - مۇشەققەت بىلەن ئاران يېتىپ بارالايدىغان جايلارغا كۆتۈرۈپ بارىدۇ، سىلەرنىڭ پەرۋەردىگارىڭلار سىلەرگە ھەقىقەتەن شەپقەتلىكتۇر ۋە مېھرىباندۇر'),
(1909, 16, 8, 'اﷲ ئاتنى، خېچىرنى ۋە ئېشەكنى مىنىشىڭلار ئۈچۈن ۋە زىننەت ئۈچۈن ياراتتى، اﷲ يەنە سىلەر بىلمەيدىغان نەرسىلەرنىمۇ يارىتىدۇ'),
(1910, 16, 9, 'توغرا يول كۆرسىتىشنى اﷲ ئۆز ئۈستىگە ئالغان. بەزى يوللار ئەگرى بولىدۇ، مۇبادا اﷲ خالىسا ئىدى، سىلەرنىڭ ھەممىڭلارنى ئەلۋەتتە توغرا يولغا سالغان بولاتتى'),
(1911, 16, 10, 'اﷲ سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، ئۇنىڭدىن ئىچىسىلەر ۋە شۇ يامغۇر ئاقىلىق ئۆسكەن دەل - دەرەخلەر بىلەن چارۋاڭلارنى باقىسىلەر'),
(1912, 16, 11, 'اﷲ سىلەرگە شۇ يامغۇر بىلەن زىرائەتلەرنى ئۆستۈرۈپ بېرىدۇ، زەيتۇن، خورما، ئۈزۈم ۋە تۈرلۈك مېۋىلەرنى ئۆستۈرۈپ بېرىدۇ، چوڭقۇر پىكىر قىلىدىغان قەۋم ئۈچۈن بۇنىڭدا ھەقىقەتەن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) دەلىللەر بار'),
(1913, 16, 12, 'اﷲ سىلەرگە كېچىنى، كۈندۈزنى، كۈننى، ئاينى بويسۇندۇرۇپ بەردى، يۇلتۇزلارمۇ اﷲ نىڭ ئەمرى بىلەن بويسۇندۇرۇلغاندۇر؛ چۈشىنىدىغان قەۋم ئۈچۈن بۇنىڭدا نۇرغۇن دەلىللەر بار'),
(1914, 16, 13, 'اﷲ يەنە يەر يۈزىدە سىلەر ئۈچۈن ياراتقان رەڭگارەڭ نەرسىلەرنى (سىلەرگە بويسۇندۇرۇپ بەردى)، ۋەز - نەسىھەت ئالىدىغان قەۋم ئۈچۈن بۇنىڭدا ئەلۋەتتە ئىبرەت بار'),
(1915, 16, 14, 'اﷲ سىلەرنى دېڭىزنىڭ يېڭى گوشلىرى (يەنى بېلىقلىرى) نى يېسۇن، تاقايدىغان زىننەت بۇيۇملىرى (يەنى ئۈنچە - مارجانلار) نى چىقارسۇن دەپ، سىلەرگە دېڭىزنى بويسۇندۇرۇپ بەردى. اﷲ نىڭ نېمەتلىرىنى تەلەپ قىلىشىڭلار ۋە ئۇنىڭغا شۈكۈر قىلىشىڭلار ئۈچۈن، (اﷲ نىڭ بويسۇندۇرۇشى بىلەن يېمەك - ئىچمەكلەر ۋە يۈك - تاقلار قاچىلانغان) كېمىلەرنىڭ دېڭىزدا دولقۇن يېرىپ كېتىۋاتقانلىقىنى كۆرۈسەن'),
(1916, 16, 15, 'زېمىننىڭ سىلەرنى تەۋرىتىۋەتمەسلىكى ئۈچۈن، اﷲ ئۇنىڭدا تاغلارنى بەرپا قىلدى، (زىرائەتلىرىڭلارنى، چارۋا ماللىرىڭلارنى سۇغىرىشىڭلار ئۈچۈن) ئېرىق - ئۆستەڭلەرنى، ئېزىپ قالماسلىقىڭلار ئۈچۈن يوللارنى پەيدا قىلدى'),
(1917, 16, 16, '(يوللارغا) بەلگىلەرنى قىلدى. ئۇلار (كېچىسى قۇرۇقلۇقتا ۋە دېڭىزلاردا) يۇلتۇزلار بىلەن يول تاپىدۇ'),
(1918, 16, 17, '(پۇتۇن كائىناتنى) ياراتقان اﷲ بىلەن (ھېچ نەرسىنى) يارىتالمايدىغان (بۇتلار) ئوخشاش بولامدۇ؟ ئويلىمامسىلەر'),
(1919, 16, 18, 'ئەگەر اﷲ نىڭ (سىلەرگە بەرگەن) نېمىتىنى سانىساڭلار، ئۇنىڭ ھېسابىنى ئېلىپ بولالمايسىلەر. شۈبھىسىزكى، اﷲ سىلەر (نىڭ بۇ جەھەتتىكى يېتەرسىزلىكىڭلار) نى ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر'),
(1920, 16, 19, 'اﷲ سىلەر يوشۇرغان ۋە ئاشكارا قىلغاننىڭ ھەممىسىنى بىلىپ تۇرىدۇ'),
(1921, 16, 20, 'ئۇلارنىڭ اﷲ نى قويۇپ ئىبادەت قىلغانلىرى (يەنى بۇتلىرى) ھېچ شەيئىنى يارىتالمايدۇ، بەلكى ئۇلارنىڭ ئۆزلىرى يارىتىلغاندۇر (يەنى ئىنسانلار تەرىپىدىن ياسالغاندۇر)'),
(1922, 16, 21, 'ئۇلار (يەنى بۇتلار) تىرىك ئە مەس، ئۆلۈكتۈر، ئۇلار (چوقۇنغۇچىلىرىنىڭ) قاچان قايتا تىرىلدۈرۈلىدىغانلىقىنى ئۇقمايدۇ'),
(1923, 16, 22, 'سىلەرنى (ئىبادەت قىلىشقا لايىق) ئىلاھىڭلار بىردۇر، ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ دىللىرى (اﷲ نىڭ بىرلىكىنى) ئىنكار قىلىدۇ، ئۇلار چوڭچىلىق قىلىپ، (ھەقنى قوبۇل قىلىشتىن) باش تارتقۇچىلاردۇر'),
(1924, 16, 23, 'اﷲ ئۇلارنىڭ يوشۇرغانلىرىنى ۋە ئاشكارا قىلغانلىرىنى راستلا بىلىپ تۇرىدۇ، اﷲ تەكەببۇرلۇق قىلغۇچىلارنى ھەقىقەتەن دوست تۇتمايدۇ'),
(1925, 16, 24, 'ئۇلارغا (يەنى مۇشرىكلارغا): «پەرۋەردىگارىڭلار (پەيغەمبىرىگە) نېمىلەرنى نازىل قىلدى؟» دېيىلسە، ئۇلار (مەسخىرە قىلىپ): «قەدىمكىلەرنىڭ ئەپسانىلىرىنى (نازىل قىلىدى)» دېيىشىدۇ'),
(1926, 16, 25, 'ئۇلار قىيامەت كۈنى ئۆزلىرىنىڭ گۇناھلىرىغا تولۇق جاۋابكار بولۇشلىرى ۋە ئۆزلىرى تەرىپىدىن بىلمەستىن ئازدۇرۇلغانلارنىڭ بىر قىسىم گۇناھلىرىغا ئۆزلىرى جاۋابكار بولۇشلىرى ئۈچۈن (يۇقىرىقى بوھتان سوزلەرنى قىلدى)، ئۇلارنىڭ جاۋابكار بولىدىغىنى نېمىدېگەن يامان!'),
(1927, 16, 26, 'ئۇلار (يەنى مەككە كاپىرلىرى) دىن ئىلگىرى ئۆتكەن كاپىرلار (ئۆز پەيغەمبەرلىرىگە) ھىيلە - مىكىر ئىشلىتىپ (اﷲ نىڭ نۇرىنى ئۆچۈرمەكچى بولدى). اﷲ ئۇلارنىڭ (ھىيلە - مىكىر) بىناسىنى تەلتۆكۈس گۇمران قىلدى، ئۇلارنىڭ ئۈستىگە (بۇ بىنانىڭ) ئۆگزىسى ئۆرۈلۈپ چۈشتى، ئۇلارغا ئۆزلىرى ئويلىمىغان يەردىن ئازاب كەلدى'),
(1928, 16, 27, 'ئاندىن كېيىن اﷲ ئۇلارنى قىيامەت كۈنى رەسۋا قىلىدۇ، (ئۇلارغا كايىپ) «مېنىڭ شېرىكلىرىم قېنى قەيەردە؟ سىلەر ئۇلار توغرىسىدا (پەيغەمبەرلەر بىلەن) مۇنازىرىلىشەتتىڭلارغۇ؟» دەيدۇ. ئىلىم بېرىگەنلەر (يەنى ئۇلارنى ئىمانغا دەۋەت قىلغان پەيغەمبەرلەر ۋە ئۆلىمالار): «بۈگۈن رەسۋالىق ۋە ئازاب ھەقىقەتەن كاپىرلارغا تېگىشلىكتۇر» دەيدۇ'),
(1929, 16, 28, 'ئۇلار (كۇفرىنى ئىختىيار قىلىپ) ئۆزلىرىگە زىيان سالغان شارائىتتا، پەرىشتىلەر ئۇلارنىڭ جانلىرىنى ئالىدۇ. ئۇلار بويسۇنغانلىقلىرىنى بىلدۈرۈپ: «بىز ھېچقانداق يامان ئىش قىلمىدۇق» (دەيدۇ). ئۇنداق ئەمەس، اﷲ سىلەنىڭ قىلمىشىڭلارنى ھەقىقەتەن تولۇق بىلگۈچىدۇر'),
(1930, 16, 29, 'سىلەر (ئىچىدە) مەڭگۈ قالىدىغان جەھەننەمنىڭ دەرۋازىلىرىدىن كىرىڭلار، چوڭچىلىق قىلىپ، (اﷲ قا ئىتائەت قىلىشتىن باش تارتقۇچىلارنىڭ بارىدىغان جايى نېمىدېگەن يامان!'),
(1931, 16, 30, 'تەقۋادارلارغا: «پەرۋەردىگارىڭلار (پەيغەمبىرىگە) نېمىلەرنى نازىل قىلدى؟» دېيىلسە، ئۇلار: «ياخشى سۆزلەرنى نازىل قىلدى» دېيىشىدۇ. ياخشى ئىش قىلغۇچىلار بۇ دۇنيادا مۇكاپاتلىنىدۇ، ئاخىرەت يۇرتى (ئۇلار ئۈچۈن تېخىمۇ) ياخشىدۇر. تەقۋادارلارنىڭ يۇرتى راستلا نېمىدېگەن گۈزەل!'),
(1932, 16, 31, 'ئۇلار ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان ئەدنى جەننەتلىرىگە كىرىدۇ، ئۇ يەردە ئۇلارنىڭ كۆڭلى خالىغان نەرسىلەر تېپىلىدۇ، اﷲ تەقۋادارلارنى ئەنە شۇنداق مۇكاپاتلايدۇ'),
(1933, 16, 32, 'ئۇلار (مۇشرىكلىك ۋە گۇناھلاردىن) پاك بولغان شارائىتتا پەرىشتىلەر ئۇلارنى قەبزى روھ قىلىدۇ، پەرىشتىلەر ئۇلارغا: «سالام سىلەرگە! قىلغان ئەمەلىڭلارنى خاسىيىتىدىن جەننەتكە كىرىڭلار» دەيدۇ'),
(1934, 16, 33, 'كاپىرلار پەقەت (ئۆزلىرىنىڭ جېنىنى ئېلىش ئۈچۈن) پەرىشتىلەرنىڭ چۈشۈشىنى ياكى پەرۋەردىگارىڭنىڭ ئەمرىنىڭ (يەنى ئازابىنىڭ) چۈشۈشىنىلا كۈتىدۇ، ئۇلاردىن بۇرۇن ئۆتكەن (كاپىر) لارمۇ ئەنە شۇنداق قىلغان ئىدى. اﷲ ئۇلارغا زۇلۇم قىلغىنى يوق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى'),
(1935, 16, 34, 'ئۇلار قىلمىشلىرىنىڭ جازاسىنى تارتتى، ئۇلارغا مەسخىرە قىلغانلىقلىرىنىڭ جازاسى (يەنى قاتتىق ئازابى) چۈشتى'),
(1936, 16, 35, 'مۇشرىكلار: «مۇبادا اﷲ خالىغان بولسا، بىز ۋە بىزنىڭ ئاتا بوۋىلىرىمىز اﷲ نى قويۇپ ھىچ نەرسىگە ئىبادەت قىلمىغان بولاتتۇق، اﷲ ھارام قىلمىغان (يەنى اﷲ نىڭ ھۆكمىسىز) ھىچ نەرسىنى ھارام قىلمىغان بولاتتۇق» دېدى. ئۇلاردىن ئىلگىرىكىلەرمۇ ئەنە شۇنداق قىلغان ئىدى. پەيغەمبەرلەرنىڭ مەسئۇلىيىتى پەقەت روشەن تەبلىغ قىلىشتىنلا ئىبارەت (ھىدايەت قىلىش بولسا اﷲ نىڭ ئىشىدۇر)'),
(1937, 16, 36, 'بىز ھەقىقەتەن ھەر بىر ئۈممەتكە: «اﷲ قا ئىبادەت قىلىڭلار، شەيتاندىن (يەنى شەيتانغا، بۇتلارغا، كاھىنلارغا ئوخشاش ھەرقانداق مەبۇدقا ئىبادەت قىلىشتىن) يىراق بولۇڭلار» دەپ پەيغەمبەر ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدا اﷲ ھىدايەت قىلغانلىرىمۇ بار، گۇمراھلىققا تېگىشلىك بولغانلىرىمۇ بار. يەر يۈزىدە سەير قىلىپ يۈرۈپ (پەيغەمبەرلەرنى) ئىنكار قىلغانلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزىتىڭلار'),
(1938, 16, 37, 'سەن ئۇلارنىڭ ھىدايەت تىپىشىغا ھېرىس بولساڭمۇ، اﷲ ئۆزى گۇمراھ قىلغان كىشىنى ھىدايەت قىلمايدۇ، ئۇلارنى (اﷲ نىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق مەدەتكار يوق'),
(1939, 16, 38, 'ئۇلار: «اﷲ ئۆلۈكنى تىرىلدۈرمەيدۇ» دەپ قاتتىق قەسەم ئىچىشتى، ئۇنداق ئەمەس (يەنى ئۆلۈكنى تىرىلدۈرىدۇ)، اﷲ ۋەدىسىنى چوقۇم ئىشقا ئاشۇرىدۇ، لېكىن ئىنسانلارنىڭ تولىسى (اﷲ نىڭ قۇدرىتىنى) بىلمەيدۇ (شۇڭلاشقا ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلىدۇ'),
(1940, 16, 39, 'اﷲ ئۇلار ئىختىلاپ قىلىشقان ھەق بىلەن ناھەقنى ئۇلارغا ئايرىپ بېرىش ۋە كاپىرلارغا ئۇلارنىڭ يالغانچىلىقلىرىنى بىلدۈرۈپ قويۇش ئۈچۈن (ئۇلارنى قايتىدىن تىرىلدۈرىدۇ)'),
(1941, 16, 40, 'بىز بىرەر شەيئىنى ۋۇجۇدقا كەلتۈرمەكچى بولساق، ئۇنىڭغا: «ۋۇجۇدقا كەل» دەيمىز - دە، ئۇ ۋۇجۇدقا كېلىدۇ'),
(1942, 16, 41, 'زۇلۇمغا ئۇچرىغاندىن كېيىن اﷲ يولىدا ھىجرەت قىلغانلارنى دۇنيادا چوقۇم ياخشى جايغا ئورۇنلاشتۇرىمىز، ئۇلار بىلىدىغان بولسا، ئۇلارغا ئاخىرەتنىڭ ساۋابى تېخىمۇ چوڭدۇر'),
(1943, 16, 42, 'ئۇلار (ئېغىرچىلىقلارغا) سەۋر قىلدى، ئۇلار (ھەممە ئىشتا) پەرۋەردىگارىغا تەۋەككۈل قىلىدۇ (يەنى يۆلىنىدۇ)'),
(1944, 16, 43, 'سەندىن ئىلگىرى بىز پەقەت ئەرلەرگىلا ۋەھيى نازىل قىلىپ، ئۇلارنى پەيغەمبەر قىلىپ ئەۋەتتۇق، (ئى قۇرەيش جامائەسى!) بۇنى بىلمىسەڭلار، ئىلىم ئەھلىلىرى (يەنى تەۋرات ۋە ئىنجىلنى بىلگۈچىلەر) دىن سوراڭلار'),
(1945, 16, 44, '(ئۇلارنى ئۇلارنىڭ راستلىقىنى كۆرسىتىپ بېرىدىغان) دەلىللەر ۋە مۇقەددەس كىتابلار بىلەن (ئەۋەتتۇق)، بىز ساڭا قۇرئاننى ئىنسانلارغا چۈشۈرۈلگەن شەرىئەتنى بايان قىلىپ بەرسۇن، ئۇلار (بۇ قۇرئاننى) پىكىر قىلىپ (ئۇنىڭدىن ۋەز - نەسىھەت ئالسۇن) دەپ نازىل قىلدۇق'),
(1946, 16, 45, 'ھىيلە - مىكىر ئىشلىتىپ يامان ئىش قىلغانلار اﷲ نىڭ ئۆزلىرىنى يەرگە يۇتقۇزۇۋىېتىشىدىن ياكى ئۆزلىرىگە ئويلىمىغان يەردىن ئازاب كېلىدىن قورقمامدۇ؟'),
(1947, 16, 46, 'ياكى ئۆزلىرىنى سەپەر ئۇستىدىكى چاغلىرىدا جازاغا يۇلۇقتۇرۇشىدىن قورقمامدۇ؟ (بۇ چاغدا) ئۇلار قېچىپ قۇتۇلالمايدۇ'),
(1948, 16, 47, 'ياكى (اﷲ نىڭ) ئۆزلىرىنى قەدەممۇقەدەم جازالىشىدىن قورقمامدۇ؟ سىلەرنىڭ پەرۋەردىگارىڭلار ئەلۋەتتە مەرھەمەتلىكتۇر، مېھرىباندۇر (شۇڭا سىلەرنى ئالدىراپ جازالىمىدى'),
(1949, 16, 48, 'ئۇلار اﷲ ياراتقان شەيئىلەرنىڭ سايىلىرىنىڭ بويسۇنغان ھالدا اﷲ قا سەجدە قىلىش يۈزىسىدىن ئوڭ - سولغا مايىل بولۇپ تۇرغانلىقىنى كۆرمىدىمۇ؟'),
(1950, 16, 49, 'ئاسمانلاردىكى ۋە زېمىندىكى ھايۋاناتلار، پەرىشتىلەر اﷲ قا سەجدە قىلىدۇ، ئۇلار (اﷲ قا سەجدە قىلىشتىن) چوڭچىلىق قىلىپ باش تارتمايدۇ'),
(1951, 16, 50, 'ئۇلار ئۆز ئۈستىدىكى پەرۋەردىگارىدىن قورقىدۇ، ئۆزلىرىگە بۇيرۇلغاننى قىلىدۇ'),
(1952, 16, 51, 'اﷲ: «ئىككى ئىلاھقا ئىبادەت قىلماڭلار، ئۇ پەقەت بىرلا ئىلاھتۇر، مەندىنلا قورقۇڭلار» دېدى'),
(1953, 16, 52, 'ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، ئىبادەت ھەمىشە اﷲ قا خاستۇر، اﷲ تىن بۆلەك غەيرىدىن قورقامسىلەر؟'),
(1954, 16, 53, 'سىلەر بەھرىمەن بولۇۋاتقان نېمەتلەرنىڭ ھەممىسىنى اﷲ بەرگەن، سىلەرگە بىر ئېغىرچىلىق يەتسە، اﷲ قا يالۋۇرۇپ دۇئا قىلىسىلەر'),
(1955, 16, 54, 'اﷲ سىلەردىن ئېغىرچىلىقنى كۆتۈرۈۋەتسە، ئاراڭلاردىن بىر بۆلۈك ئادەم دەرھال ئۆز پەرۋەردىگارىغا شېرىك كەلتۈرىدۇ'),
(1956, 16, 55, 'ئۇلار ئۆزلىرىگە بەرگەن (بېشىدىكى ئېغىرچىلىقنى كۆتۈرۈۋەتكەنلىكىمىزدىن ئىبارەت) نېمىتىمىزگە ناشۈكۈرلۈك قىلىدۇ، (سىلەر ئەجىلىڭلار يەتكۈچە دۇنيادىن) بەھرىمەن بولۇڭلار، (ئىشىڭلارنىڭ ئاقىۋىتىنى) ئۇزاققا قالماي بىلىسىلەر'),
(1957, 16, 56, 'ئۇلار بىز ئۆزلىرىگە بەرگەن مال - مۈلۈكتىن بىلىمسىز (بۇتلارغا) نېسىۋە قالدۇرىدۇ، (ئى مۇشرىكلار!) اﷲ نىڭ نامى بىلەن قەسەمكى، سىلەر ئۆزۈڭلار ئويدۇرۇپ چىقارغان نەرسەڭلار ئۈچۈن چوقۇم سوراققا تارتىلىسىلەر'),
(1958, 16, 57, 'ئۇلار اﷲ قا قىزلارنى نىسبەت بېرىدۇ، اﷲ (بۇنداق بوھتاندىن) پاكتۇر، ئۇلار ئۆزى ئارزۇ قىلىدىغانلىرىنى ئۆزلىرىگە نىسبەت بېرىدۇ'),
(1959, 16, 58, 'ئۇلارنىڭ بىرەرسىگە (خوتۇنىنىڭ) قىز تۇغقانلىق خوش خەۋىرى يەتكۈزۈلسە، چىرايى ئۆزگىرىپ، غەزەپناك بولۇپ كېتىدۇ'),
(1960, 16, 59, 'يەتكۈزۈلگەن يامان خەۋەردىن قورقۇپ، ئۆز قەۋمىگە كۆرۈنمەي يوشۇرۇنۇۋالىدۇ. ئاندىن ئۇ نومۇسقا چىداپ قىزنى ساقلاپ قالامدۇ؟ ياكى ئۇنى توپا ئاستىغا (تىرىك) كۆمەمدۇ؟ (شۇ ھەقتە ئويلىنىدۇ) ئۇلارنىڭ ھۆكمى (يەنى ئوغۇللارنى ئۆزلىرىگە نىسبەت بېرىپ، قىزلارنى اﷲ قا نىسبەت بېرىشى) ھەقىقەتەن نېمىدېگەن قەبىھ!'),
(1961, 16, 60, 'ئاخىرەتكە ئىشەنمەيدىغانلار (ئوغۇلنى ياخشى كۆرۈپ، قىزنى يامان كۆرۈش، خوتۇنى ئوغۇل تۇغسا خۇشال بولۇپ، قىز تۇغسا ئاچچىقى كېلىش، ئار - نومۇستىن ۋە كەمبەغەللىكتىن قورقۇپ، قىزلارنى تىرىك كۈمۈشكە ئوخشاش) يامان سۈپەتلەرگە ئىگە، اﷲ بولسا ئەڭ ئالىي خىسلەتلەرگە ئىگە، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(1962, 16, 61, 'ئەگەر اﷲ ئىنسانلارنى ئۇلارنىڭ زۇلمى تۈپەيلىدىن (چاپسان) جازالايدىغان بولسا، يەر يۈزىدە قىمىرلاپ تۇرىدىغان ھېچبىر جان ئىگىسىنى قويمىغان بولاتتى، لېكىن اﷲ ئۇلارنى مۇئەييەن ۋاقىتقىچە تەخىر قىلىدۇ، ئۇلارنىڭ (ھالاك بولۇش) ۋاقتى كەلگەندە قىلچە ۋاقىتمۇ كېچىكتۈرۈلمەيدۇ، (ۋاقتى كەلمىسە) قىلچە ۋاقىتمۇ ئىلگىرى سۈرۈلمەيدۇ'),
(1963, 16, 62, 'ئۇلار ئۆزلىرى يامان كۆرۈدىغاننى اﷲ قا مەنسۇپ قىلىدۇ، ئۇلار ئاقىۋىتىنىڭ ياخشى بولىدىغانلىقى بىلەن جۆيلۈيدۇ، شۈبھىسىزكى، ئۇلار دوزاخ ئازابىغا دۇچار بولىدۇ، ئۇلار (دوزاختا) تاشلىنىپ قالغۇچىلاردۇر'),
(1964, 16, 63, '(ئى مۇھەممەد!) اﷲ نامى بىلەن قەسەمكى، سەندىن ئىلگىرى ئۆتكەن ئۈممەتلەرگە ھەقىقەتەن (پەيغەمبەرلەر) ئەۋەتتۇق، شەيتان ئۇلارنىڭ ئەمەللىرىنى ئۆزلىرىگە چىرايلىق كۆرسەتتى، شەيتان بۈگۈن ئۇلارنىڭ ياردەمچىسىدۇر. ئۇلار ئاخىرەتتە قاتتىق ئازابقا دۇچار بولىدۇ'),
(1965, 16, 64, '(ئى مۇھەممەد!) بىز كىتابنى (يەنى قۇرئاننى) پەقەت سېنىڭ كىشىلەرگە ئۇلار (دىنىدىكى) دەتالاش قىلىشقان نەرسىلەرنى بايان قىلىپ بېرىشىڭ ئۈچۈن، ئىمان ئېيتىدىغان قەۋمگە ھىدايەت ۋە رەھمەت بولسۇن ئۈچۈنلا نازىل قىلدۇق'),
(1966, 16, 65, 'اﷲ بۇلۇتتىن يامغۇر ياغدۇرۇپ، شۇنىڭ بىلەن قاغجىراپ قالغان زېمىندا (تۈرلۈك زىرائەتلەرنى) ئۈندۈردى، بۇنىڭدا (ۋەز - نەسىھەت) ئاڭلايدىغان قەۋم ئۈچۈن، ئەلۋەتتە، (اﷲ نىڭ قۇدرىتىنىڭ چوڭلۇقىنى كۆرسىتىدىغان) دەلىللەر بار'),
(1967, 16, 66, 'چاھار پايلاردا سىلەر ئۈچۈن ھەقىقەتەن بىر ئىبرەت بار. سىلەرنى چاھارپايلارنىڭ قارنىدىكى ماياق - تېزەكتىن ۋە قاندىن ئايرىلىپ چىققان پاكىز ۋە تەملىك سۈت بىلەن سۇغىرىمىز'),
(1968, 16, 67, '(سىلەر ئۈچۈن يەنە بۇنداق ئىبرەتمۇ باركى) خورمىدىن، ئۈزۈمدىن شارابلار ۋە پاكىز يېمەكلىكلەر ياسايسىلەر، ئەقىلنى ئىشلىتىدىغان قەۋم ئۈچۈن بۇنىڭدا ھەقىقەتەن اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان دەلىل بار'),
(1969, 16, 68, 'پەرۋەردىگارىڭ ھەسەل ھەرىسىگە ئىلھام بىلەن بىلدۈردى: «تاغلارغا، دەرەخلەرگە ۋە (كىشىلەرنىڭ ھەسەل ھەرىلىرى ئۈچۈن) ياسىغان ئۆيلىرىگە ئۇۋا تۈزگىن'),
(1970, 16, 69, 'ئاندىن كېيىن، تۈرلۈك مېۋىلەر (يەنى گۈل - چېچەكلەر) دىن يېگىن، پەرۋەردىگارىڭنىڭ يوللىرىغا ئىتائەتمەنلىك بىلەن كىرگىن»، ئۇنىڭ (يەنى ھەسەل ھەرىسىنىڭ) قارنىدىن ئىنسانلارغا شىپا بولىدىغان خىلمۇ - خىل رەڭلىك ئىچىملىك (يەنى ھەسەل) چىقىدۇ. بۇنىڭدا (اﷲ نىڭ قۇدرىتىنىڭ چوڭلۇقىنى) پىكىر قىلىدىغان قەۋم ئۈچۈن ئىبرەت بار'),
(1971, 16, 70, 'اﷲ سىلەرنى ياراتتى، ئاندىن (ئەجىلىڭلار پۈتكەندە) سىلەرنى قەبزى روھ قىلىدۇ، ئاراڭلاردا ئۆمۈرنىڭ يامان (يەنى ئەڭ قېرىلىق) باسقۇچىغا ئۇلىشىدىغانلارمۇ بار، ئۇلار ھەتتا (بىر نەرسىلەرنى) بىلگەندىن كېيىن، ھېچ نەرسىنى بىلمەس بولۇپ قالىدۇ (يەنى ئىلگىرى بىلىدىغانلىرىنى ئۇنتۇپ، كۈچ - قۇۋۋىتى تۈگەپ، ئەقلى كېمىيىپ كىچىك بالىغا ئوخشاپ قالىدۇ). اﷲ ھەقىقەتەن (مەخلۇقاتنى باشقۇرۇشنى) بىلگۈچىدۇر، (ئۆزى ئىرادە قىلغان نەرسىنى ئىشقا ئاشۇرۇشقا) قادىردۇر'),
(1972, 16, 71, 'اﷲ سىلەرنىڭ بەزىڭلارنى بەزىڭلاردىن رىزىقتا ئۈستۈن قىلدى، رىزىقتا ئۈستۈن قىلىنغانلار ئۆزلىرىنىڭ رىزقىنى باراۋەر بولۇش ئۈچۈن قول ئاستىدىكى قۇللارغا بەرمەيدۇ، ئۇلار اﷲ نىڭ نېمىتىنى ئىنكار قىلىشامدۇ؟'),
(1973, 16, 72, 'اﷲ سىلەر (نىڭ ئۇنى - ئۈلپەت ئېلىشىڭلار) ئۈچۈن خوتۇنۇڭلارنى ئۆز تىپىڭلاردىن ياراتتى، سىلەر ئۈچۈن ئۇلاردىن ئوغۇللارنى ۋە نەۋرىلەرنى ياراتتى. سىلەرگە تۈرلۈك شېرىن نەرسىلەرنى رىزىق قىلىپ بەردى. ئۇلار باتىلغا (يەنى بۇتلارغا) ئىشىنىپ اﷲ نىڭ نېمىتىگە كۇفرىلىق قىلامدۇ؟'),
(1974, 16, 73, 'ئۇلار اﷲ نى قويۇپ، ئۆزلىرىگە ئاسمانلاردىن ھېچقانداق رىزىق چۈشۈرۈپ ۋە يەردىن ھېچقانداق رىزىق چىقىرىپ (يەنى ئاسماندىن يامغۇر ياغدۇرۇپ ۋە يەردىن زىرائەت ۋە دەرەخلەرنى ئۆستۈرۈپ) بېرەلمەيدىغان، (شۇنداق قىلىشقا) قۇدرىتى يەتمەيدىغان بۇتلارغا چوقۇنامدۇ؟'),
(1975, 16, 74, 'سىلەر اﷲ قا مىسال كەلتۇرمەڭلار (يەنى اﷲ قا بۇتلارنى ئوخشاتماڭلار، اﷲ قا شېرىك كەلتۈرمەڭلار). اﷲ ھەقىقەتەن (ھەممە ھەقىقەتنى) بىلىدۇ، سىلەر (اﷲ نىڭ بۈيۈكلۈكىنى) بىلمەيسىلەر'),
(1976, 16, 75, 'اﷲ بۇنداق بىر مىسال كەلتۈرىدۇ: ھېچقانداق ئىشنى ئۆز ئالدىغا بىر تەرەپ قىلالمايدىغان بىر قۇل بىز ئوبدان رىزىق (يەنى مال - مۈلۈك) ئاتا قىلغان ۋە ئۇنى يوشۇرۇن ياكى ئاشكارا يوسۇندا سەدىقە قىلىپ تۇرغان (ھۆر - ئەركىن) ئادەم بىلەن ئوخشاشمۇ؟ (يەنى ھېچ نەرسىگە ئىگە بولمىغان قۇلغا ئوخشايدىغان بۇتلارنى ھەممىگە ئىگە بولغان ۋە كائىناتنى خالىغانچە تەسەررۇپ قىلىدىغان اﷲ بىلەن قانداقمۇ تەڭلەشتۈرگىلى بولسۇن؟) جىمى ھەمدۇسانا اﷲ قا خاستۇر! ئۇلار (يەنى كاپىرلار) نىڭ تولىسى (ھەمدۇساناغا اﷲ لايىق ئىكەنلىكىنى، بۇتلارنىڭ ھەمدۇساناغا ۋە ئىبادەتكە لايىق ئەمەسلىكىنى) ئۇقمايدۇ'),
(1977, 16, 76, 'اﷲ يەنە مۇنداق مىسال كەلتۈرىدۇ؛ مۇنداق ئىككى ئادەم بار بولۇپ، ئۇلارنىڭ بىرى ھېچ ئىش قىلالمايدىغان ۋە ئىگىسىگە يۈك بولۇپ قالغان بىر گاچادۇركى، ئىگىسى بۇ گاچىنى مەيلى قەيەرگە ئەۋەتسۇن، ئۇ ھېچقانداق پايدا ئېلىپ كېلەلمەيدۇ، يەنە بىرى بولسا ئۆزى توغرا يولدا ماڭغان ۋە (كىشىلەرنى) ھەققانىي بولۇشقا بۇيرۇيدىغان ئادەمدۇر، بۇ ئىككى ئادەم ئوخشاشمۇ؟ (يەنى بۇ گاچا بىلەن ئۆزى توغرا يولدا تۇرۇپ پاساھەتلىك سۆزلەيدىغان، ئۆزى قۇرئان نۇرى بىلەن نۇرلانغان ئادەم بىر - بىرىگە ئوخشامدۇ؟ ئەقىللىق ئادەمنىڭ ئۇلارنى ئوخشاش ئورۇنغا قويمىغىنىدەك، بۇت ۋە تاشنى ھەممىنى بىلگۈچى قۇدرەتلىك اﷲ بىلەن ئوخشاش ئورۇنغا قويغىلى بولمايدۇ)'),
(1978, 16, 77, 'ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلىش اﷲ قا خاستۇر، قىيامەتنىڭ قايىم بولۇشى كۆزنى يۇتۇپ ئاچقۇچىلىق ياكى ئۇنىڭدىنمۇ تېزدۇر. اﷲ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر'),
(1979, 16, 78, 'اﷲ سىلەرنى ئاناڭلارنىڭ قارنىدىن ھېچ نەرسىنى بىلمەيدىغان ھالىتىڭلار بىلەن چىقاردى، اﷲ سىلەرگە شۈكۈر قىلسۇن دەپ، قۇلاق، كۆز ۋە يۈرەكلەرنى ئاتا قىلدى'),
(1980, 16, 79, 'ئۇلار قۇشلارنىڭ ھاۋادا ئۇچۇشقا بويسۇندۇرۇلغانلىقىنى كۆرمىدىمۇ؟ ئۇلارنى (ھاۋادىن چۈشۈپ كېتىشتىن) پەقەت اﷲ تۇتۇپ تۇرىدۇ، بۇنىڭدا ئىمان ئېيتىدىغان قەۋم ئۈچۈن (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار'),
(1981, 16, 80, 'اﷲ سىلەرگە ئۆيۈڭلارنى تۇرالغۇ جاي قىلىپ بەردى، سىلەرگە ھايۋانلارنىڭ تېرىلىرىدىن ئۆيلەرنى (چېدىرلەرنى) قىلىپ بەردى، سىلەر كۆچكەن كۈنۈڭلاردىمۇ، تۇرغان كۈنۈڭلاردىمۇ ئۇنىڭ يىنىكلىكىنى ھېس قىلىسىلەر، سىلەرگە قويلارنىڭ يۈكلىرىدىن، تۆگىلەرنىڭ يۇڭلىرىدىن، ئۆچكىلەرنىڭ يۇڭلىرىدىن ئۆي جاھازلىرىنى (يەنى سىلەر كىيىدىغان ۋە ئۆيۈڭلارنى سەرەمجانلاشتۇرىدىغان) ھەم مەلۇم مۇددەتكىچە (ئۆلۈم كەلگۈچە ياكى ئۇلار كونىراپ تۈگىگىچە) پايدىلىنىدىغان نەرسىلەرنى قىلىپ بەردى'),
(1982, 16, 81, 'اﷲ سىلەرگە ئۆزى ياراتقان نەرسىلەر (يەنى دەرەخلەر، تاغلار ۋە بىنالار) دىن (كۈننىڭ ھارارىتىدىن ساقلىنىدىغان) سايىلەرنى قىلىپ بەردى. تاغلاردىن سىلەرگە (غار، ئۆڭكۈرگە ئوخشاش) تۇرالغۇ قىلىدىغان جايلارنى قىلىپ بەردى. سىلەرگە ئىسسىقتىن (ۋە سوغۇقتىن) ساقلىنىدىغان كىيىملەرنى، ئۇرۇشتا سىلەرنى (زىيان - زەخمەتتىن) ساقلايدىغان تۆمۈر كىيىملەرنى تەييار قىلدى. اﷲ سىلەرنى ئىتائەت قىلسۇن دەپ، سىلەرگە ئەنە شۇنداق مۇكەممەل نېمەتلەرنى بېرىدۇ'),
(1983, 16, 82, 'ئەگەر ئۇلار (يەنى مۇشرىكلار ئىماندىن) يۈز ئۆرۈسە (ساڭا ھېچ زىيىنى يوق)، سېنىڭ ۋەزىپەڭ پەقەت روشەن تەبلىغ قىلىشتۇر'),
(1984, 16, 83, 'ئۇلار اﷲ نىڭ نېمىتىنى تونۇيدۇ، ئاندىن (اﷲ تىن بۆلەك غەيرىيگە ئىبادەت قىلىش بىلەن) ئۇ نېمەتلەرنى ئىنكار قىلىدۇ، ئۇلارنىڭ تولىسى كاپىردۇر (يەنى كاپىر پېتىچە ئۆلىدۇ)'),
(1985, 16, 84, 'شۇ كۈندە (يەنى قىيامەت كۈنىدە) ھەر ئۈممەتتىن (ئۇنىڭغا ئىمان بىلەن ياكى كۇفرى بىلەن گۇۋاھلىق بېرىدىغان) بىر شاھىت (يەنى پەيغەمبەر) كەلتۈرىمىز، ئاندىن كاپىرلارغا (ئۆزرە ئېيتىشقا) رۇخسەت قىلىنمايدۇ ھەمدە ئۇلاردىن پەرۋەردىگارىنى رازى قىلىش (يەنى پەرۋەردىگارىنى رازى قىلىدىغان ئەمەللەرنى قىلىش) تەلەپ قىلىنمايدۇ'),
(1986, 16, 85, 'زالىملار (يەنى مۇشرىكلار) ئازابنى كۆرگەن چاغدا، ئۇلاردىن (ئازاب) يىنىكلىتىلمەيدۇ ۋە كېچىكتۈرۈلمەيدۇ'),
(1987, 16, 86, 'مۇشرىكلار (دۇنيادا چوقۇنغان) بۇتلىرىنى كۆرگەن چاغدا: «ئى پەرۋەردىگارىمىز! بۇلار بولسا بىزنىڭ سېنى قويۇپ ئىبادەت قىلغان بۇتلىرىمىزدۇر» دەيدۇ، بۇتلىرى ئۇلارغا جاۋاب بېرىپ؛ «شۈبھىسىزكى، سىلەر ھەقىقەتەن يالغانچىسىلەر» دەيدۇ'),
(1988, 16, 87, 'شۇ كۈندە ئۇلار ئۆزلىرىنىڭ اﷲ قا باش ئەگكەنلىكىنى بىلدۈرىدۇ، ئەمما ئۇلارنىڭ (دۇنيادىكى چاغلىرىدا: «بۇتلار بىزگە شاپائەت قىلىدۇ» دېگەن) يالغان - ياۋىداقلىرى يوققا چىقىدۇ'),
(1989, 16, 88, 'كاپىر بولغانلارغا ۋە (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى دىنىدىن) توسقانلارغا ئۇلارنىڭ قىلغان بۇزغۇنچىلىقلىرى ئۈچۈن ئازاب قىلىمىز'),
(1990, 16, 89, 'شۇ كۈندە بىز ھەر بىر ئۈممەتكە ئۇلارنىڭ ئىچىدىن ئۇلارغا قارشى (گۇۋاھلىق بېرىدىغان) بىر شاھىت (يەنى پەيغەمبەر) كەلتۈرىمىز ۋە (شۇ كۈندە) بىز سېنى ئۇلارغا (يەنى ئۈممىتىڭگە) شاھىت قىلىپ كەلتۈرىمىز. ساڭا بىز كىتابنى (يەنى قۇرئاننى) ھەممە نەرسىنى (يەنى كىشىلەر موھتاج بولىدىغان دىنىي ئىشلارنىڭ ھەممىسىنى) چۈشەندۈرۈپ بېرىدىغان، (دىللارغا) ھىدايەت، (بەندىلەرگە) رەھمەت، مۇسۇلمانلارغا مەڭگۈلۈك سائادەت بىلەن خوش خەۋەر يەتكۈزىدىغان قىلىپ نازىل قىلدۇق'),
(1991, 16, 90, 'اﷲ ھەقىقەتەن (كىشىلەر ئارىسىدا) ئادىل بولۇشقا، (جىمى خەلققە) ياخشىلىق قىلىشقا، خىش - ئەقرىبالارغا سىلە - رەھىم قىلىشقا بۇيرۇيدۇ، قەبىھ (سۆز - ھەرىكەتلەر) دىن، يامان ئىشلاردىن ۋە زۇلۇم قىلىشتىن توسىدۇ. نەسىھەتنى قوبۇل قىلسۇن دەپ، اﷲ سىلەرگە پەند - نەسىھەت قىلىدۇ'),
(1992, 16, 91, 'سىلەر ئەھدە تۈزۈشكەنلىرىڭلاردا، اﷲ نىڭ ئەھدىگە ۋاپا قىلىڭلار، قەسىمىڭلارنى (اﷲ نىڭ نامىنى تىلغا ئېلىپ) پۇختىلىغاندىن كېيىن بۇزماڭلار، چۈنكى سىلەر اﷲ نى گۇۋاھچى قىلدىڭلار. شۈبھىسىزكى، اﷲ قىلمىشىڭلارنى بىلىپ تۇرىدۇ'),
(1993, 16, 92, 'سىلەر پىششىق ئىگىرگەن يىپنى چۇۋۇپ پارچە - پارچە قىلىۋەتكەن خوتۇندەك بولماڭلار. بىر جامائە (سان ۋە مال - مۈلۈك جەھەتتە) يەنە بىر جامائەدىن كۆپ بولغانلىقى ئۈچۈن، قەسىمىڭلارنى ئالدامچىلىقنىڭ ۋاسىتىسى قىلىۋالىسىلەر (يەنى ئۆز ئارا ياردەملىشىش ئۈستىدە بىر جامائە بىلەن ئىتتىپاق تۈزۈشكەندىن كېيىن، سانى، پۇل - مېلى كۆپرەك يەنە بىر جامائەنى كۆرۈپلا ئىلگىرىكى جامائە بىلەن تۈزۈشكەن ئەھدىنى بۇزۇپ، كېيىنكى جامائە بىلەن ئەھدى تۈزىسلەر)، اﷲ سىلەرنى ھەقىقەتەن بۇنىڭ (يەنى ۋەدىگە ۋاپا قىلىش ئەمرى) بىلەن سىنايدۇ، سىلەر دەتالاش قىلىشقان ھەق - ناھەقنى قىيامەت كۈنى سىلەرگە چوقۇم ئايرىپ بېرىدۇ'),
(1994, 16, 93, 'مۇبادا اﷲ خالىغان بولسا ئىدى، سىلەرنى (يەنى پۇتۇن ئىنسانلارنى) ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، لېكىن (اﷲ نىڭ ھېكمىتى ئۇلارنى ئۆز ئىختىيارىغا قويۇۋېتىشنى تەقەززا قىلدى)، اﷲ خالىغان ئادەمنى گۇمراھ قىلىدۇ، خالىغان ئادەمنى ھىدايەت قىلىدۇ، (قىيامەت كۈنى) پۈتۈن قىلمىشلار ئۈستىدە سوراق قىلىنىسىلەر'),
(1995, 16, 94, 'قەدىمىڭلار (ئىسلام دىنىدا) مۇستەھكەم، ئىزچىل بولغاندىن كېيىن تېيىلىپ كەتمەسلىك ئۈچۈن، قەسىمىڭلارنى ئۆز ئاراڭلاردا ئالدامچىلىقنىڭ ۋاسىتىسى قىلىۋالماڭلار، (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلىقىڭلار (يەنى ئەھدىگە ۋاپا قىلىشتىن يۈز ئۆرۈگەنلىكىڭلار) ئۈچۈن ئازابنى تېتىيسىلەر، (سىلەر ئاخىرەتتە دوزاختا) چوڭ ئازابقا قالىسىلەر'),
(1996, 16, 95, 'سىلەر اﷲ نىڭ ئەھدىنى ئەزىمەس نەرسىگە سېتىۋەتمەڭلار: سىلەر ئەگەر بىلسەڭلار، اﷲ نىڭ دەرگاھىدىكى ساۋاب سىلەر ئۈچۈن (پانى دۇنيانىڭ نەرسىلىرىدىن) ئارتۇقتۇر'),
(1997, 16, 96, '(ئى ئىنسانلار!) سىلەرنىڭ ئىلكىڭلاردىكى نەرسىلەر تۈگەيدۇ، اﷲ نىڭ دەرگاھىدىكى نەرسىلەر تۈگىمەستۇر. سەۋر قىلغۇچىلارغا، ئەلۋەتتە، ئۇلارنىڭ قىلغان ئەمەلىدىنمۇ ياخشىراق ساۋاب بېرىمىز'),
(1998, 16, 97, 'ئەر - ئايال مۆمىنلەردىن كىمكى ياخشى ئەمەل قىلىدىكەن، بىز ئۇنى ئەلۋەتتە (دۇنيانىڭ قانائەتچانلىق، ھالال رىزىق ۋە ياخشى ئەمەللەرگە مۇۋەپپەق قىلىپ) ئوبدان ياشىتىمىز، ئۇلارغا، ئەلۋەتتە، قىلغان ئەمەلىدىنمۇ ياخشىراق ساۋاب بېرىمىز'),
(1999, 16, 98, 'سەن قۇرئان ئوقۇماقچى بولغىنىڭدا، قوغلاندى شەيتاننىڭ (ۋەسۋەسىسىدىن) اﷲ قا سېغىنىپ پاناھ تىلىگىن'),
(2000, 16, 99, 'شۈبھىسىزكى، مۆمىنلەرنىڭ ۋە پەرۋەردىگارىغا تەۋەككۈل قىلغۇچىلار (يەنى يۆلەنگۈچىلەر) نىڭ ئۈستىدىن شەيتان ھۆكۈمرانلىق قىلالمايدۇ (يەنى پەرۋەردىگارىغا چىن يۆلەنگەن مۆمىنلەرگە شەيتاننىڭ ۋەسۋەسىسى تەسىر قىلالمايدۇ)'),
(2001, 16, 100, 'شەيتان پەقەت ئۆزىنى دوست تۇتىدىغانلار ۋە ئۆزىنىڭ (ئىغۋا قىلىشى) بىلەن مۇشرىك بولۇپ كەتكەنلەر ئۈستىدىنلا ھۆكۈمرانلىق قىلىدۇ'),
(2002, 16, 101, 'بىز بىر ئايەتنى يەنە بىر ئايەتنىڭ ئورنىغا ئالماشتۇرغىنىمىزدا (يەنى بىر ھۆكۈمنى بىكار قىلىپ، ئۇنىڭ ئورنىغا باشقا بىر ھۆكۈمنى چۈشۈرگىنىمىزدە)، - اﷲ ئۆزى نازىل قىلغاننى (يەنى شۇ ھۆكۈمدە بەندىگە نېمە مەنپەئەت ۋە ھېكمەت بارلىقىنى) ئۆزى ئوبدان بىلىدۇ - ئۇلار: «(ئى مۇھەممەد!) سەن (اﷲ قا) يالغان چاپلىدىڭ» دەيدۇ، ئۇنداق ئەمەس، ئۇلارنىڭ تولىسى (اﷲ نىڭ ھېكمىتىنى) ئۇقمايدۇ'),
(2003, 16, 102, 'ئېيتقىنكى، قۇرئاننى جىبرىئىل مۆمىنلەرنىڭ (ئىمانىنى) مۇستەھكەم قىلىش ئۈچۈن، مۇسۇلمانلارغا ھىدايەت ۋە خۇش خەۋەر قىلىپ، پەرۋەردىگارىڭ ھۇزۇرىدىن راستچىللىق بىلەن نازىل قىلدى'),
(2004, 16, 103, 'ئۇلار (يەنى مۇشرىكلار) نىڭ: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) قۇرئاننى بىر ئىنسان تەلىم بېرىدۇ» دېگەن سۆزىنى ئوبدان بىلىمىز، ئۇلار كۆرسەتكەن ئادەمنىڭ تىلى ئەرەب تىلى ئەمەس، بۇ (قۇرئان) بولسا روشەن ئەرەب تىلىدۇر'),
(2005, 16, 104, 'شۈبھىسىزكى، اﷲ نىڭ ئايەتلىرىگە (يەنى قۇرئانغا) ئىشەنمەيدىغانلارنى اﷲ ھىدايەت قىلمايدۇ، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ'),
(2006, 16, 105, 'پەقەت اﷲ نىڭ ئايەتلىرىگە ئىشەنمەيدىغانلارلا (اﷲ قا) يالغان چاپلايدۇ، (مۇھەممەد ئەلەيھىسسالام) يالغانچى ئەمەس) دەل ئۇلارنىڭ ئۆزلىرى يالغانچىدۇر'),
(2007, 16, 106, 'كىمكى اﷲ قا ئىمان ئېيتقاندىن كېيىن ئىمانىدىن يېنىۋالسا، - قەلبى ئىمان بىلەن مۇستەھكەم تۇرسىمۇ مەجبۇرلاش ئاستىدا (ئاغزىدىلا) ئىماندىن يانغانلىقىنى بىلدۈرگەنلەر بۇنىڭدىن مۇستەسنا - كۇفرى بىلەن كۆڭلى ئازادە بولسا (يەنى ئىختىيارىي يوسۇندا مۇرتەد بولغان بولسا)، ئۇ اﷲ نىڭ غەزىپىگە دۇچار بولىدۇ ۋە چوڭ ئازابقا قالىدۇ'),
(2008, 16, 107, 'بۇ (يەنى ئازاب) ئۇلارنىڭ دۇنيا تىرىكچىلىكىنى ئاخىرەتتىن ئارتۇق كۆرگەنلىكلىرى ۋە اﷲ نىڭ كاپىر قەۋمنى ھىدايەت قىلمايدىغانلىقى ئۈچۈندۇر'),
(2009, 16, 108, 'ئەنە شۇ كىشىلەر اﷲ تەرىپىدىن دىللىرى، قۇلاقلىرى ۋە كۆزلىرى پېچەتلىۋېتىلگەن كىشىلەردۇر، ئەنە شۇلار غاپىللاردۇر'),
(2010, 16, 109, 'ئۇلار راستلا ئاخىرەتتە زىيان تارتقۇچىلاردۇر'),
(2011, 16, 110, 'ئاندىن (ئى مۇھەممەد!) زىيانكەشلىككە ئۇچرىغاندىن كېيىن، ھەقىقەتەن (مەدىنىگە) ھىجرەت قىلغان. ئاندىن (اﷲ نىڭ يولىدا) جىھاد قىلغان ۋە (جىھادنىڭ مۇشەققەتلىرىگە) چىدىغانلارغا پەرۋەردىگارىڭ (مەغپىرەت قىلىدۇ)، شۈبھىسىزكى، پەرۋەردىگارىڭ شۇنىڭدىن (يەنى ئۇلارنىڭ ھىجرەت، جىھاد، سەۋر قىلغىنىدىن) كېيىن ئۇلارغا ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، رەھمەت قىلغۇچىدۇر'),
(2012, 16, 111, 'ھەر ئادەم ئۆزى بىلەن بولۇپ كېتىدىغان، ھەر ئادەم قىلغان ئەمەلىنىڭ مۇكاپاتى تولۇق بېرىلىدىغان، ئۇلارغا زۇلۇم قىلىنمايدىغان كۈننى (يەنى قىيامەت كۈنىنى) ئەسلىگىن'),
(2013, 16, 112, 'اﷲ مۇنداق بىر مىسالنى كەلتۈرىدۇ: بىر شەھەر (يەنى مەككە ئاھالىسى) بولۇپ، (ئەسلىدە) تىنچ - خاتىرجەم ئىدى. ئۇنىڭ رىزقى تەرەپ - تەرەپتىن كەڭتاشا كېلىپ تۇراتتى، (ئۇنىڭ ئاھالىسى) اﷲ نىڭ نېمەتلىرىگە كۇفرىلىق قىلدى، اﷲ ئۇلارغا قىلمىشلىرى تۈپەيلىدن ئاچلىقنىڭ ۋە قورقۇنچنىڭ ئەلىمىنى تېتىتتى'),
(2014, 16, 113, 'ئۇلارغا ئۆز ئىچىدىن بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلدى، ئۇنى ئۇلار ئىنكار قىلدى. ئۇلارغا ئازاب (يەنى قاتتىقچىلىق ۋە ئوقۇبەت) يۈزلەندى، ئۇلار (گۇناھلارغا چۆمگەنلىك تۈپەيلىدىن) زالىم ئىدى'),
(2015, 16, 114, 'اﷲ سىلەرگە رىزىق قىلىپ بەرگەن ھالال، شېرىن يېمەكلىكلەردىن يەڭلار، ئەگەر اﷲ غىلا ئىبادەت قىلىدىغان بولساڭلار، اﷲ نىڭ ھىممىتىگە شۈكۈر قىلىڭلار'),
(2016, 16, 115, 'اﷲ سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋاننى، قاننى، چوشقا گۆشىنى، اﷲ تىن غەيرىنىڭ (يەنى بۇتلارنىڭ) نامى ئېيتىلىپ بوغۇزلانغان ھايۋاننى يېيىشنى ھارام قىلدى. كىمكى ئختىيارىچە ئەمەس، ئىلاجىسىزلىقتىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) ھاياتىنى ساقلاپ قالغۇدەك مىقداردا (يېسە) اﷲ (ئۇنى) مەغپىرەت قىلغۇچىدۇر، (ئۇنىڭغا) كۆيۈنگۈچىدۇر'),
(2017, 16, 116, 'ئاغزىڭلارغا كەلگەن يالغاننى سۆزلەش ئۈچۈن (ھېچقانداق دەلىلسىز) «بۇ ھالال، بۇ ھارام» دېمەڭلار، چۈنكى (مۇنداقتا) اﷲ نامىدىن يالغاننى ئويدۇرغان بولىسىلەر، اﷲ نامىدىن يالغاننى ئويدورغۇچىلار ھەقىقەتەن (دۇنيا ئاخىرەتتە) مەقسىتىگە ئېرىشەلمەيدۇ'),
(2018, 16, 117, '(ئۇلار دۇنيادىن پەقەت) ئازغىنا بەھرىمەن بولىدۇ، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ'),
(2019, 16, 118, 'ئىلگىرى ساڭا بايان قىلغان نەرسىلەرنى يالغۇز يەھۇدىيلارغا ھارام قىلدۇق، (بۇنىڭ بىلەن) بىز ئۇلارغا زۇلۇم قىلمىدۇق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى'),
(2020, 16, 119, 'شۈبھىسىزكى، بىلمەستىن يامان ئىشلارنى قىلغاندىن كېيىن تەۋبە قىلغان ۋە (ئەمەلىنى) تۈزەتكەنلەرگە پەرۋەردىگارىڭ ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، مەرھەمەت قىلغۇچىدۇر'),
(2021, 16, 120, 'ئىبراھىم ھەقىقەتەن (ياخشى خىسلەتلەرنى ئۆزلەشتۈرگۈچى) پېشىۋا، اﷲ قا ئىتائەتمەن (باتىل دىنلاردىن ھەق دىنغا بۇرالغۇچى يەنى ھەق دىنغا ئېتىقاد قىلغۇچى) ئىدى. ئۇ اﷲ قا شېرىك كەلتۈرگۈچىلەردىن ئەمەس ئىدى'),
(2022, 16, 121, 'ئىبراھىم اﷲ نىڭ نېمەتلىرىگە شۈكۈر قىلغۇچى ئىدى. اﷲ ئۇنى (پەيغەمبەرلىككە) تاللىدى ۋە ئۇنى توغرا يولغا باشلىدى'),
(2023, 16, 122, 'ئۇنىڭغا دۇنيادا ياخشىلىق ئاتا قىلدۇق، شەك - شۈبھىسىزكى، ئاخىرەتتە ئۇ ياخشىلار قاتارىدا بولىدۇ'),
(2024, 16, 123, 'ئاندىن ساڭا: «باتىل دىنلاردىن ھەق دىنغا بۇرالغۇچى (يەنى ھەق دىنغا ئېتىقاد قىلغۇچى) ئىبراھىمنىڭ دىنىغا ئەگەشكىن، ئىبراھىم مۇشرىكلاردىن ئەمەس ئىدى» دەپ ۋەھيى قىلدۇق'),
(2025, 16, 124, 'شەنبە كۈنى ئۈستىدە ئىختىلاپ قىلغۇچىلار ئۈچۈنلا ئۇنى ئۇلۇغلاش بەلگىلەندى (يەنى شەنبە كۈنىنى ئۇلۇغلاش ۋە بۇ كۈندە ئىشلىمەسلىك ئىبراھىمنىڭ شەرىئىتىدە يوق ئىدى. يەھۇدىيلار اﷲ نىڭ ئەمرىگە ئاسىيلىق قىلىپ دىندا ئىختىلاپ قىلىشقانلىقلىرى سەۋەبىدىن ئۇلارغا ئېغىرچىلىق تۇغدۇرۇش ئۈچۈن شۇنداق بەلگىلىمىنى چىقاردۇق). شۈبھىسىزكى، پەرۋەردىگارىڭ قىيامەت كۈنى ئۇلار ئىختىلاپ قىلىشقان ھەق - ناھەق ئۈستىدە چوقۇم ھۆكۈم چىقىرىدۇ'),
(2026, 16, 125, 'پەرۋەردىگارىڭنىڭ يولىغا (يەنى ئىسلام دىنىغا) ھېكمەتلىك ئۇسلۇبتا ياخشى ۋەز - نەسىھەت بىلەن دەۋەت قىلغىن، ئۇلار (يەنى مۇخالىپەتچىلىك قىلغۇچىلار) بىلەن چىرايلىق رەۋىشتە مۇنازىرىلەشكەن. پەرۋەردىگارىڭ ھەقىقەتەن ئۇنىڭ يولىدىن ئازغانلارنى ئوبدان بىلىدۇ، ھىدايەت تاپقۇچىلارنىمۇ ئوبدان بىلىدۇ'),
(2027, 16, 126, 'ئەگەر (ئۆزۈڭلارغا يەتكەن زىيان - زەخمەت ئۈچۈن) ئىنتىقام ئالماقچى بولساڭلار، ئۆزۈڭلارغا يەتكەن زىيان - زەخمەت قانچىلىك بولسا، شۇنچىلىك ئىنتىقام ئېلىڭلار (يەنى ئاشۇرۇۋەتمەڭلار)، ئەگەر سەۋر قىلساڭلار (يەنى ئىنتىقام ئالماي كەچۈرسەڭلار)، بۇ سەۋر قىلغۇچىلار (يەنى كەچۈرگۈچىلەر) ئۈچۈن (ئەلۋەتتە) ياخشىدۇر'),
(2028, 16, 127, '(ئى مۇھەممەد!) (اﷲ نىڭ يولىدا ساڭا يەتكەن ئەرزىيەتلەرگە) سەۋر قىلغىن، سەۋرەڭ پەقەت اﷲ نىڭ ياردىمى بىلەنلا قولغا كېلىدۇ. ئۇلارنىڭ (ئىمان ئېيتمىغانلىقى) غا قايغۇرمىغىن. ئۇلارنىڭ ھىيلە - مىكرىسىدىن يۈرىكىڭ سىقىلمىسۇن'),
(2029, 16, 128, 'اﷲ ھەقىقەتەن تەقۋادارلىق قىلغۇچىلار ۋە ياخشى ئىش قىلغۇچىلار بىلەن بىللىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'اﷲ (بارچە نۇقساندىن) پاكتۇر، ئۇ (مۇھەممەد ئەلەيھىسسالامغا) قۇدرىتىمىزنىڭ دەلىللىرىنى كۆرسىتىش ئۈچۈن، بەندىسىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) بىر كېچىدە مەسجىدى ھەرەمدىن ئەتراپىنى بەرىكەتلىك قىلغان مەسجىدى ئەقساغا ئېلىپ كەلدى. ھەقىقەتەن اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر'),
(2031, 17, 2, 'مۇساغا كىتابنى (يەنى تەۋراتنى) ئاتا قىلدۇق، كىتابنى ئىسرائىل ئەۋلادىغا يېتەكچى قىلدۇق، (ئۇلارغا) مەندىن غەيرىنى ھامىي (يەنى پەرۋەردىگار) قىلىۋالماڭلار!» (دېدۇق)'),
(2032, 17, 3, 'ئى بىز نۇھ بىلەن بىللە كېمىگە چۈشۈرگەنلەرنىڭ ئەۋلادلىرى! (ئەجدادىڭلارنى غەرق بولۇپ كېتىشتىن قۇتقۇزدۇق، اﷲ نىڭ نېمىتىگە شۈكۈر قىلىڭلار) نۇھ ھەقىقەتەن كۆپ شۈكۈر قىلغۇچى بەندە ئىدى'),
(2033, 17, 4, 'ئىسرائىل ئەۋلادىغا كىتابتا (يەنى تەۋراتتا): «پەلەستىن زېمىنىدا چوقۇم ئىككى قېتىم بۇزغۇنچىلىق قىلىسىلەر ۋە زۇلۇم قىلىش بىلەن، اﷲ چەكلىگەن ئىشلارنى قىلىش بىلەن تولىمۇ ھەددىڭلاردىن ئېشىپ كېتىسىلەر» دەپ ۋەھيى قىلدۇق'),
(2034, 17, 5, 'بىرىنچى قېتىملىق بۇزغۇنچىلىقنىڭ (جازاسى) ۋەدە قىلىنغان ۋاقىت كەلگەندە، (سىلەرنى جازالاش ئۈچۈن) سىلەرگە كۈچلۈك بەندىلىرىمىزنى ئەۋەتتۇق، (ئۇلار سىلەرنى ئۆلتۈرۈش، بۇلاڭ - تالاڭ قىلىش ئۈچۈن) ئۆيلەرنى ئاختۇردى. (سىلەرگە دۈشمەننى مۇسەللەت قىلىش) چوقۇم ئىشقا ئاشىدىغان ۋەدە ئىدى'),
(2035, 17, 6, 'ئاندىن (يەنى تەۋبە قىلغىنىڭلاردىن) كېيىن سىلەرنىڭ ئۇلاردىن ئۈستۈنلۈكۈڭلارنى ئەسلىگە كەلتۈردۇق (يەنى دۈشمىنىڭلارنى ھالاك قىلدۇق، سىلەرنى ئۇنىڭ ئۈستىدىن غالىب قىلدۇق)، سىلەرگە نۇرغۇن مال ۋە ئەۋلادلار ئاتا قىلدۇق، سانىڭلارنى كۆپ قىلدۇق'),
(2036, 17, 7, '(ئى ئسرائىل ئەۋلادى!) ئەگەر ياخشىلىق قىلساڭلار، ياخشىلىقىڭلار ئۆزۈڭلار ئۈچۈندۇر، يامانلىق قىلساڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، ئىككىنچى قېتىملىق بۇزغۇنچىلىق (جازاسىنىڭ) ۋەدە قىلىنغان ۋاقتى كەلگەندە، (دۈشمەنلەرنىڭ خار قىلىشى بىلەن) يۈزۈڭلاردا قايغۇنىڭ ئالامەتلىرى كۆرۈلۈشى ئۈچۈن، ئۇلارنىڭ بەيتۇلمۇقەددەسكە دەسلەپكى قېتىمدا كىرگەندەك كىرىپ (ئۇنى خاراب قىلىشى) ئۈچۈن، ئىشغال قىلغانلىكى يەرنى ۋەيران قىلىشى ئۈچۈن (ئۇلارنى بىز ئەۋەتتۇق)'),
(2037, 17, 8, '(ئەگەر تەۋبە قىلساڭلار) پەرۋەردىگارىڭلارنىڭ سىلەرگە رەھىم قىلىشى مۇھەققەق يېقىندۇر، ئەگەر سىلەر قايتساڭلار (يەنى قايتا بۇزغۇنچىلىق قىلساڭلار)، بىز قايتىمىز (يەنى بىز سىلەرنى قايتا جازالايمىز). جەھەننەمنى كاپىرلار ئۈچۈن زىندان قىلدۇق'),
(2038, 17, 9, 'بۇ قۇرئان ھەقىقەتەن ئەڭ توغرا يولغا باشلايدۇ، ياخشى ئىشلارنى قىلىدىغان مۆمىنلەرگە ئۇلارنىڭ چوڭ مۇكاپاتقا ئېرىشىدىغانلىقى بىلەن خۇش خەۋەر بېرىدۇ'),
(2039, 17, 10, '(يەنە مۆمىنلەرگە خۇش خەۋەر بېرىدۇكى) ئاخىرەتكە ئىشەنمەيدىغانلارغا قاتتىق ئازاب تەييارلىدۇق'),
(2040, 17, 11, 'ئىنسان (ئۆزىگە ۋە بالا - چاقىلىرىغا) خەيرىلىك دۇئا قىلغاندەك، (ئاچچىقى كەلگەندە) بەتدۇئا قىلىدۇ، ئىنسان ئالدىراڭغۇدۇر'),
(2041, 17, 12, 'كېچە بىلەن كۈندۈزنى (كامالى قۇدرىتىمىزنى كۆرسىتىدىغان) ئىككى ئالامەت قىلدۇق، كېچىنىڭ ئالامىتىنى كۆتۈرۈۋەتتۇق، پەرۋەردىگارىڭلارنىڭ پەزلىنى تەلەپ قىلىشىڭلار (يەنى تۇرمۇش لازىمەتلىكلىرىنى تېپىش يولىدا سەئيى ھەرىكەتتە بولۇشۇڭلار) ئۈچۈن، (كېچە بىلەن كۈندۈزنىڭ ئالمىشىشى ئارقىلىق كۈنلەرنىڭ، ئايلارنىڭ) يىللارنىڭ سانىنى ۋە ھېسابىنى بىلىشىڭلار ئۈچۈن، كۈندۈزنىڭ ئالامىتىنى يورۇقلۇق قىلدۇق، (دىنىي ۋە دۇنياۋى) ئىشلارنىڭ ھەممىسىنى تەپسىلىي بايان قىلدۇق'),
(2042, 17, 13, 'ھەر بىر ئىنساننىڭ ئەمەلىنى ئۇنىڭ بوينىغا ئېسىپ قويىمىز (يەنى ئىنساننىڭ قىلغان ھەرقانداق ئەمەلى خۇددى بويۇنچاق بويۇندىن ئايرىلمىغىنىدەك ئۇنىڭدىن ھەرگىز ئايرىلمايدۇ، شۇنىڭغا يارىشا جازا بېرىلىدۇ)، قىيامەت كۈنى ئۇنىڭ نامە - ئەمالىنى كۆرسىتىمىز، ئۇ ئۇنى ئوچۇق. كۆرىدۇ'),
(2043, 17, 14, '(ئۇنىڭغا) «نامە - ئەمالىڭنى ئوقۇغىن! بۈگۈن ئۆزۈڭگە (يەنى بۇ قىلمىشلىرىڭغا) ئۆزۈڭ گۇۋاھ بولۇشۇڭ. كۆرىدۇ» دېيىلىدۇ'),
(2044, 17, 15, 'كىمكى ھىدايەت تاپىدىكەن، ھىدايەت تاپقانلىقىنىڭ پايدىسى ئۇنىڭ ئۆزى ئۈچۈندۇر. كىمكى ئازىدىكەن، ئازغانلىقنىڭ زىيىنى ئۇنىڭ ئۆزى ئۈچۈندۇر، بىر ئادەم يەنە بىر ئادەمنىڭ گۇناھىنى ئۈستىگە ئالمايدۇ. پەيغەمبەر ئەۋەتمەي تۇرۇپ (ھېچ ئادەمنى جازالىغىنىمىز يوق'),
(2045, 17, 16, 'بىرەر شەھەر (ئاھالىسى) نى ھالاك قىلماقچى بولساق، ئۇنىڭدىكى دۆلەتمەن ئادەملەرنى (بىزگە ئىتائەت قىلىشقا بۇيرۇيمىز، ئۇلار ئىتائەت قىلماي، پىسقى - پاسات قىلىدۇ - دە، ئۇلارغا ئازابىمىز تېگىشلىك بولىدۇ، شۇنىڭ بىلەن ئۇلارنى قورقۇنچلۇق رەۋىشتە ھالاك قىلىمىز'),
(2046, 17, 17, 'نۇھتىن كېيىن (پەيغەمبەرلىرىنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، پەرۋەردىگارىڭ بەندىلىرىنىڭ گۇناھلىرىنى تولۇق بىلىپ تۇرۇشتا، كۆرۈپ تۇرۇشتا يېتەرلىكتۇر'),
(2047, 17, 18, 'بىز دۇنيانى كۆزلىگەنلەر ئىچىدىن خالىغان ئادەمگە (ئۇنىڭ خالىغىنىنى ئەمەس) بىزنىڭ خالىغىنىمىزنى بېرىمىز، ئاندىن ئۇنى (ئاخىرەتتە) جەھەننەم بىلەن جازالايمىز، جەھەننەمگە ئۇ خارلانغان، (اﷲ نىڭ رەھمىتىدىن) قوغلانغان ھالدا كىرىدۇ'),
(2048, 17, 19, 'كىمكى ئاخىرەتنى كۆزلەيدىكەن ۋە مۆمىن بولۇپ ئاخىرەتكە لايىق ئەمەللەرنى قىلىدىكەن، مۇنداق ئادەملەرنىڭ قىلغان ئەمەلى (اﷲ نىڭ دەرگاھىدا) قوبۇل بولىدۇ'),
(2049, 17, 20, 'بۇلارنىڭ ۋە ئۇلارنىڭ ھەر ئىككىسىگە پەرۋەردىگارىڭنىڭ ئاتا قىلغىنىدىن بېرىمىز. پەرۋەردىگارىڭنىڭ ئاتا قىلغىنى (ھېچ ئادەمدىن) مەنئى قىلىنمايدۇ'),
(2050, 17, 21, 'ئۇلارنىڭ بەزىسىنى بەزىسىدىن قانداق ئۈستۈن قىلغانلىقىمىزغا قارىغىن، ئاخىرەت دەرىجىلىرىدىكى (پەرق) ئەلۋەتتە تېخىمۇ چوڭدۇر ۋە ئۈستۈنلۈك (جەھەتتىكى پەرق) زىيادە چوڭدۇر'),
(2051, 17, 22, 'اﷲ قا باشقا مەبۇدنى (شېرىك) قىلمىغىن، (ئۇنداق قىلساڭ اﷲ نىڭ دەرگاھىدا) ئەيىبلەنگەن، (اﷲ نىڭ ياردىمىدىن) مەھرۇم بولغان ھالدا (دوزاختا ھەمىشە) قالىسەن'),
(2052, 17, 23, 'پەرۋەردىگارىڭ پەقەت ئۇنىڭ ئۆزىگىلا ئىبادەت قىلىشىڭلارنى ۋە ئاتا - ئاناڭلارغا ياخشىلىق قىلىشىڭلارنى تەۋسىيە قىلدى، ئۇلارنىڭ بىرى، يا ئىككىلىسى سېنىڭ قول ئاستىڭدا بولۇپ ياشىنىپ قالسا، ئۇلارغا ئوھوي دېمىگىن (يەنى مالاللىقنى بىلدۈرىدىغان شۇنچىلىك سۆزنىمۇ قىلمىغىن)، ئۇلارنى دۈشكەلىمىگىن، ئۇلارغا ھۆرمەت بىلەن يۇمشاق سۆز قىلغىن'),
(2053, 17, 24, 'ئۇلارغا كامالىي مېھرىبانلىقتىن ناھايىتى كەمتەر مۇئامىلىدە بولغىن ۋە: «ئى پەرۋەردىگارىم! ئۇلار مېنى كىچىكلىكىمدە تەربىيىلىگىنىدەك ئۇلارغا مەرھەمەت قىلغىن» دېگىن'),
(2054, 17, 25, 'پەرۋەردىگارىڭلار دىلىڭلاردىكىنى ئوبدان بىلگۈچىدۇر، ئەگەر سىلەر ياخشى بولساڭلار (يەنى ئاتا - ئاناڭلارنى قاخشاتقۇچى بولمىساڭلار، اﷲ سىلەرنىڭ خاتالىغىڭلارنى كەچۈرىدۇ). اﷲ ھەقىقەتەن تەۋبە قىلىپ تۇرغۇچىلارنى مەغفىرەت قىلغۇچىدۇر'),
(2055, 17, 26, 'تۇغقانغا، مىسكىنگە، ئىبىن - سەبىلگە (خەيرى - ساخاۋەتتىن) ھەققىنى بەرگىن، (پۇل - مېلىڭنى ناتوغرا يوللارغا) ئىسراپ قىلمىغىن'),
(2056, 17, 27, 'ئىسراپ قىلغۇچىلار ھەقىقەتەن شەيتانلارنىڭ قېرىنداشلىرىدۇر، شەيتان پەرۋەردىگارىغا تولىمۇ كۇفرانى نېمەت قىلغان ئىدى'),
(2057, 17, 28, 'ئەگەر سەن پەرۋەردىگارىڭدىن كۈتۈۋاتقان مەرھەمەت نازىل بولغاندىن كېيىن ئاندىن ئۇلارغا خەيرى - ساخاۋەت قىلماقچى بولساڭ (يەنى قولۇڭدا ئۇلارغا بېرىدىغان بىر نەرسە بولمىسا)، ئۇلارغا چىرايلىق گەپ قىلغىن'),
(2058, 17, 29, 'قولۇڭنى بوينۇڭغا باغلىۋالمىغىن (يەنى بېخىللىق قىلمىغىن) قولۇڭنى تولىمۇ ئېچىپمۇ ئەتمىگىن (يەنى ئىسراپ قىلمىغىن)، (ئۇنداق قىلساڭ پۇلسىز قېلىپ) مالامەتكە، پۇشايمانغا قالىسەن'),
(2059, 17, 30, 'شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، خالىغان ئادەمنىڭ رىزقىنى تار قىلىدۇ، اﷲ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىنى، مەنپەئىتىنى) بىلىپ تۇرغۇچىدۇر، كۆرۈپ تۇرغۇچىدۇر'),
(2060, 17, 31, 'كەمبەغەللىكتىن قورقۇپ بالىلىرىڭلارنى ئۆلتۈرمەڭلار. ئۇلارنىڭ ۋە سىلەرنىڭ رىزقىڭلارنى بىز بېرىمىز، ئۇلارنى ئۆلتۈرۈش ھەقىقەتەن چوڭ گۇناھتۇر'),
(2061, 17, 32, 'زىناغا يېقىنلاشماڭلار، چۈنكى ئۇ قەبىھ ئىشتۇر، يامان يولدۇر'),
(2062, 17, 33, 'ناھەق ئادەم ئۆلتۈرمەڭلاركى، اﷲ (ناھەق ئادەم ئۆلتۈرۈشنى (ھارام قىلدى، كىمكى ناھەق ئۆلتۈرۈلىدىكەن، (قاتىلدىن قىساس ئېلىش، يا دىيەت ئېلىش، يا كەچۈرۈم قىلىش) ھوقۇقىنى ئۆلتۈرۈلگۈچىنىڭ ئىگىسىنىڭ قولىدا قىلدۇق، ئىگىسى قىساس ئېلىشتا چەكتىن چىقىپ كەتمىسۇن (قاتىلدىن غەيرىنى ئۆلتۈرۈش، يا ئۇنىڭ ئەزالىرىنى كېسىش، بىر ئادەم ئۈچۈن ئىككى ئادەمنى ئۆلتۈرۈش قاتارلىق ئىشلارنى قىلمىسۇن)، (ناھەق ئۆلتۈرۈلگۈچىنىڭ) ئىگىسىگە اﷲ ھەقىقەتەن مەدەتكاردۇر'),
(2063, 17, 34, 'يېتىمنىڭ مېلىنى تاكى ئۇ بالاغەتكە يەتكەنگە قەدەر (يېتىمگە) ئەڭ پايدىلىق ئۇسۇلدا تەسەررۇپ قىلىڭلار، ئەھدىگە ۋاپا قىلىڭلار، (قىيامەت كۈنى) ئەھدە ئۈستىدە (يەنى ئەھدىگە ۋاپا قىلغان - قىلمىغانلىق ئۈستىدە) ئەلۋەتتە سوئال - سوراق قىلىنىسىلەر'),
(2064, 17, 35, 'سىلەر (باشقىلارغا ئاشلىق قاتارلىقلارنى) ئۆلچەپ بەرگەندە، تولۇق ئۆلچەڭلار ۋە تارازىدا تارتىپ بېرىڭلار، بۇنداق قىلىش (سىلەرگە دۇنيادا) ياخشىدۇر، ئاخىرەتلىكىڭلار ئۈچۈن تېخىمۇ ئوبداندۇر'),
(2065, 17, 36, 'بىلمەيدىغان نەرسەڭگە ئەگەشمە (يەنى بىلمىگەننى بىلدىم، كۆرمىگەننى كۆردۈم، ئاڭلىمىغاننى ئاڭلىدىم دېمە)، (ئىنسان قىيامەت كۈنى) قۇلاق، كۆز، دىل (يەنى سەزگۈ ئەزالىرى) نىڭ قىلمىشلىرى ئۈستىدە ھەقىقەتەن سوئال - سوراق قىلىنىدۇ'),
(2066, 17, 37, 'سەن زېمىندا مەغرۇرانە يۈرمىگىن، (ھاكاۋۇرلۇقتىن ئايىغىڭ بىلەن) يەرنى تېشىۋېتەلمەيسەن، (ھەرقانچە گىدەيگىنىڭ بىلەن) ئېگىزلىكتە تاغلارغا تەڭلىشەلمەيسەن'),
(2067, 17, 38, 'يۇقىرىقى (ئىش) لارنىڭ ھەممىسى قەبىھ بولۇپ، اﷲ نىڭ دەرگاھىدا ياماندۇر'),
(2068, 17, 39, 'بۇلار پەرۋەردىگارىڭ ساڭا ۋەھيى قىلغان ھېكمەتلەرنىڭ بىر قىسمىدۇر، اﷲ قا باشقا مەبۇدنى شېرىك قىلمىغىن، بولمىسا مالامەت قىلىنغان، (اﷲ نىڭ رەھمىتىدىن) قوغلانغان ھالدا جەھەننەمگە تاشلىنىسەن'),
(2069, 17, 40, 'ئەجەبا اﷲ سىلەرگە ئوغۇللارنى خاس قىلىپ، پەرىشتىلەرنى ئۆزىنىڭ قىزلىرى قىلىۋالغانمۇ؟ ھەقىقەتەن سىلەر چوڭ سۆزنى قىلىۋاتىسىلەر'),
(2070, 17, 41, 'ئۇلارنى ئىبرەت ئالسۇن دەپ (ۋەز - نەسىھەتنى) قۇرئاندا تۈرلۈك شەكىلدە بايان قىلدۇق، (بۇنىڭ بىلەن) ئۇلار (ھەقىقەتەن) تېخىمۇ يىراقلاشماقتا'),
(2071, 17, 42, 'مۇشرىكلار ئېيتقاندەك، اﷲ بىلەن بىرگە يەنە باشقا ئىلاھلار بولغان تەقدىردە ئەرش ئىگىسى (اﷲ ئۈستىدىن) غەلىبە قىلىش ئۈچۈن ئەلۋەتتە يول ئىزدىگەن بولاتتى'),
(2072, 17, 43, 'اﷲ پاكتۇر، ئۇلارنىڭ بوھتان سۆزلىرىدىن يۈكسەك دەرىجىدە ئالىيدۇر'),
(2073, 17, 44, 'يەتتە ئاسمان - زېمىن ۋە ئۇلاردىكى مەخلۇقاتلار اﷲ نى پاك دەپ بىلىدۇ، (كائىناتتىكى) قانداقلىكى نەرسە بولمىسۇن، اﷲ نى پاك دەپ مەدھىيىلەيدۇ (يەنى اﷲ نىڭ ئۇلۇغلۇقىنى سۆزلەيدۇ)، لېكىن سىلەر (تىلىڭلار ئوخشاش بولمىغانلىقى ئۈچۈن) ئۇلارنىڭ مەدھىيىسىنى سەزمەيسىلەر، اﷲ ھەقىقەتەن (بەندىلىرىگە) ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)، (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر'),
(2074, 17, 45, '(ئى مۇھەممەد!) سەن ئاخىرەتكە ئىشەنمەيدىغان (مۇشرىكلارغا) قۇرئاننى ئوقۇغان چېغىڭدا ئۇلار بىلەن سېنىڭ ئاراڭدا يوشۇرۇن پەردە پەيدا قىلدۇق'),
(2075, 17, 46, 'قۇرئاننى چۈشەنمىسۇن دەپ ئۇلارنىڭ دىللىرىنى پەردىلىدۇق، ئۇلارنىڭ قۇلاقلىرىنى ئېغىر قىلدۇق، قۇرئاندا يالغۇز پەرۋەردىگارىڭنى تىلغا ئالساڭ، ئۇلار ئۈركۈپ قېچىپ كېتىدۇ'),
(2076, 17, 47, 'تىلاۋىتىڭگە ئۇلارنىڭ نېمە ئۈچۈن (يەنى مەسخىرە ئۈچۈن) قۇلاق سالىدىغانلىقىنى بىز ئوبدان بىلىمىز، ئۇلار قىرائىتىڭنى تىڭشىغان چاغلىرىدا (سېنىڭ توغراڭدا) پىچىرلىشىدۇ، ئەينى ۋاقىتتا زالىملار (مۆمىنلەرگە): «سىلەر پەقەت سېھىرلەنگەن ئادەمگىلا ئەگىشىۋاتىسىلەر» دەيدۇ'),
(2077, 17, 48, 'ئۇلارنىڭ سېنىڭ توغراڭدا (بىردەم سېھرىگەر، بىردەم شائىر ۋە بىردەم مەجنۇن دېگەندەك) نۇرغۇن مىساللارنى كەلتۈرگەنلىكىگە قارىغىن، ئۇلار (مۇشۇنداق بوھتانلارنى چاپلاش بىلەن) ئازدى، ئۇلار (ھىدايەتكە) يول تاپالمايدۇ'),
(2078, 17, 49, 'مۇشرىكلار: ‹‹بىز قۇرۇق سۆڭەك ۋە چىرىپ توپا بولۇپ كەتكەندىن كېيىن چوقۇم قايتا تىرىلىپ يېڭىدىن ئادەم بولامدۇق؟ ›› دېيىشىدۇ'),
(2079, 17, 50, 'سىلەر تاش ياكى تۆمۈر ياكى ھاياتلىقنى تەسەۋۋۇر قىلىش تېخىمۇ قىيىن بولغان بىر نەرسە بولۇپ كەتسەڭلارمۇ (اﷲ سىلەرنى تىرىلدۈرىدۇ) ئۇلار: ‹‹بىزنى كىم تىرىلدۈرىدۇ؟» دېگىن، ئۇلار ساڭا باشلىرىنى لىڭشىتىپ: ‹‹ئۇ قاچان بولىدۇ؟ ›› دېيىشىدۇ، (سەن) ‹‹ئۈمىدكى، ئۇ يېقىندا بولىدۇ›› دېگىن'),
(2080, 17, 51, 'سىلەر تاش ياكى تۆمۈر ياكى ھاياتلىقنى تەسەۋۋۇر قىلىش تېخىمۇ قىيىن بولغان بىر نەرسە بولۇپ كەتسەڭلارمۇ (اﷲ سىلەرنى تىرىلدۈرىدۇ) ئۇلار: ‹‹بىزنى كىم تىرىلدۈرىدۇ؟» دېگىن، ئۇلار ساڭا باشلىرىنى لىڭشىتىپ: ‹‹ئۇ قاچان بولىدۇ؟ ›› دېيىشىدۇ، (سەن) ‹‹ئۈمىدكى، ئۇ يېقىندا بولىدۇ›› دېگىن'),
(2081, 17, 52, 'شۇ كۈندە اﷲ سىلەرنى چاقىرىدۇ، سىلەر ئۇنىڭغا ھەمدى ئېيتىپ ئىجابەت قىلىسىلەر ۋە (دۇنيادا) پەقەت ئازغىنا تۇرغاندەك ئويلايسىلەر'),
(2082, 17, 53, 'بەندىلىرىمگە ئېيتقىنكى، ئۇلار ياخشى سۆزلەرنى قىلسۇن، شۈبھىسىزكى، شەيتان ئۇلارنىڭ ئارىسىدا پىتنە قوزغايدۇ، شەيتان ئىنسانغا ھەقىقەتەن ئاشكارا دۈشمەندۇر'),
(2083, 17, 54, 'اﷲ سىلەرنى ئوبدان بىلىدۇ، اﷲ سىلەرگە رەھىم قىلىدۇ، خالىسا سىلەرنى ئازابلايدۇ، سېنى ئۇلار (يەنى كاپىرلار) غا ھامىي قىلىپ ئەۋەتكىنىمىز يوق'),
(2084, 17, 55, 'پەرۋەردىگارىڭ ئاسمانلاردىكى، زېمىندىكى بەندىلىرىنىڭ ئەھۋالىنى ھەممىدىن ئوبدان بىلىدۇ، بىز ھەقىقەتەن پەيغەمبەرلەرنىڭ بەزىسىنى بەزىسىدىن ئۈستۈن قىلدۇق'),
(2085, 17, 56, 'داۋۇتقا زەبۇرنى ئاتا قىلدۇق، سىلەر اﷲ نى قويۇپ خۇدا دەپ ئويلىغانلىرىڭلارنى چاقىرىڭلار. ئۇلارنىڭ قولىدىن سىلەردىن بالانى كۆتۈرۈۋېتىشمۇ ۋە ئۇنى (باشقىلارغا) يۆتكەپ قويۇشمۇ كەلمەيدۇ'),
(2086, 17, 57, 'ئۇلار (اﷲ نى قويۇپ ئىلاھ دەپ) ئىبادەت قىلىدىغانلارنىڭ (ئۆزلىرى ئىبادەت ئارقىلىق) پەرۋەردىگارىغا يېقىن بولۇشنى تىلەيدۇ، ئۇلارنىڭ ئارىسىدا (پەرۋەردىگارىغا) يېقىنراق بولغىنى (ھەم ئىبادەت ئارقىلىق اﷲ قا يېقىن بولۇشنى تىلەيدۇ)، پەرۋەردىگارىنىڭ رەھمىتىنى ئۈمىد قىلىپ (ئۇنىڭ) ئازابىدىن قورقىدۇ، پەرۋەردىگارىنىڭ ئازابى ھەقىقەتەن ساقلىنىشقا تېگىشلىكتۇر'),
(2087, 17, 58, '(اﷲ قا ئاسىيلىق قىلغان) ھەرقانداق بىر شەھەر بولمىسۇن، ئۇنى بىز قىيامەت كۈنىدىن بۇرۇن ھالاك قىلىمىز، ياكى ئۇنى قاتتىق ئازابلايمىز، بۇ لەۋھۇلمەھپۇزدا پۈتۈلۈپ كەتكەن (ھۆكۈم) دۇر'),
(2088, 17, 59, '(قۇرەيش مۇشرىكلىرى تەلەپ قىلغان) مۆجىزىلەرنى مەيدانغا كەلتۈرمەسلىكىمىز پەقەت بۇرۇنقىلار (يەنى ئىلگىرىكى ئۈممەتلەرنىڭ) مۆجىزىلەرنى يالغانغا چىقارغانلىقى ئۈچۈندۇر، بىز سەمۇدقا چىشى تۆگىنى روشەن (مۆجىزە) قىلىپ بەردۇق، ئۇلار ئۇنى ئىنكار قىلدى. بىز مۆجىزىلەرنى پەقەت (بەندىلەرنى) قورقۇتۇش ئۈچۈنلا ئەۋەتىمىز'),
(2089, 17, 60, 'ئۆز ۋاقتىدا بىز ساڭا پەرۋەردىگارىڭنىڭ ھەقىقەتەن كىشىلەرنىڭ (ئەھۋالى) نى تولۇق بىلىدىغانلىقىنى ئېيتتۇق، بىز ساڭا (مىراج كېچىسى ئاسمان - زېمىندىكى ئاجايىپلاردىن) كۆرسەتكەن كۆرۈنۈشنى، قۇرئاندىكى لەنەت قىلىنغان دەرەخنى (يەنى قۇرئاندا زىكرى قىلىنغان زەققۇم دەرىخىنى) پەقەت كىشىلەر ئۈچۈن سىناق قىلدۇق، بىز ئۇلارنى (يەنى مۇشرىكلارنى) قورقۇتىمىز. بىزنىڭ قورقۇتىشىمىز ئۇلارنىڭ يولسىزلىقىنى تېخىمۇ كۈچەيتىدۇ'),
(2090, 17, 61, 'ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار‍‌‍!» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى. ئىبلىس: «سەن لايدىن ياراتقانغا سەجدە قىلامدىمەن؟» دېدى'),
(2091, 17, 62, 'ئىبلىس: «ماڭا ئېيتىپ بەرگىنە، سەن مەندىن ئۈستۈن قىلغان ئادەم مۇشۇمۇ؟ ئەگەر ماڭا قىيامەتكىچە مۆھلەت بېرىدىغان بولساڭ، ئۇنىڭ ئەۋلادىنىڭ ئازغىنىسىدىن باشقىسىنى (ئازدۇرۇپ) تۈپ يىلتىزىدىن قۇرۇتۇۋېتىمەن (يەنى ئۇلارنى خالىغانچە يېتىلەيمەن)» دېدى'),
(2092, 17, 63, 'اﷲ (ئىبلىسقا) ئېيتتى؛ «بارغىن (ساڭا مۆھلەت بەردىم)، ئۇلاردىن (يەنى ئادەم بالىلىرىدىن) كىمكى ساڭا ئەگىشىدىكەن، (جەھەننەم ساڭا ۋە ئۇلارغا) بېرىلگەن تولۇق جازادۇر'),
(2093, 17, 64, 'ئۇلارنىڭ ئىچىدىن قوزغىتالايدىغانلىكى ئادىمىڭنى ئاۋازىڭ بىلەن (يەنى گۇناھقا چاقىرىشىڭ بىلەن) قوزغاتقىن، ئاتلىق ۋە پىيادە قوشۇنىڭ بىلەن ئۇلارغا ھۇجۇم قىلغىن، ئۇلارنىڭ ماللىرىغا ۋە بالىلىرىغا شېرىك بولغىن، ئۇلارغا (يالغان) ۋەدىلەرنى بەرگىن - شەيتاننىڭ ئۇلارغا بەرگەن ۋەدىسى پەقەت ئالدامچىلىقتۇر'),
(2094, 17, 65, 'شۈبھىسىزكى، مېنىڭ (ئىخلاسمەن) بەندىلىرىم ئۈستىدىن سەن ھۆكۈمرانلىق قىلالمايسەن. پەرۋەردىگارىڭ ھەقىقەتەن ھامىي بولۇشقا يېتەرلىكتۇر»'),
(2095, 17, 66, 'پەرۋەردىگارىڭلار، سىلەرنى پەزلىنى (يەنى پەزلىدىن بولغان رىزقىڭلارنى) تەلەپ قىلسۇن دەپ، كېمىلەرنى سىلەر ئۈچۈن دېڭىزدا ماڭدۇرۇپ بېرىدۇ، ئۇلار ھەقىقەتەن سىلەرگە ناھايىتى مېھرىباندۇر'),
(2096, 17, 67, 'سىلەر دېڭىزدا بىرەر ئاپەتكە يولۇققان چېغىڭلاردا، ئېسىڭلارغا سىلەر چوقۇنۇۋاتقان مەبۇدلار كەلمەي، پەقەت اﷲ لا كېلىدۇ (يەنى ئىنسان مۇنداق چاغدا اﷲ تىن باشقىغا ئىلتىجا قىلمايدۇ)، اﷲ سىلەرنى (غەرق بولۇشتىن) قۇتقۇزۇپ، (ئامان - ئېسەن) قۇرۇقلۇققا چىقارغاندا يۈز ئۆرۈيسىلەر (يەنى ئىخلاسىڭلار تۈگەپ ئاۋۋالقى ھالىتىڭلارغا قايتىۋالىسىلەر). ئىنسان كۇفرىلىق قىلغۇچىدۇر (يەنى اﷲ نىڭ نېمەتلىرىنى ئىنكار قىلىش ئىنساننىڭ تەبىئىتىدۇر)'),
(2097, 17, 68, '(دېڭىزدا غەرق بولۇپ كېتىشتىن قۇتۇلغىنىڭلار بىلەن) اﷲ نىڭ سىلەرنى يەرگە يۇتقۇزۇۋېتىشىدىن، ياكى سىلەرگە ئاسماندىن تاش ياغدۇرۇپ ھالاك قىلىشىدىن، ئاندىن سىلەرنى (اﷲ نىڭ ئازابىدىن ساقلايدىغان) ھېچ ھامىي تاپالماسلىقىڭلاردىن قورقمامسىلەر؟'),
(2098, 17, 69, 'ياكى اﷲ نىڭ سىلەرنى دېڭىزدا قايتا قاتناتقۇزۇپ (دېڭىزدىكى چېغىڭلاردا) قارا بوران چىقىرىپ، كۈفرىلىق قىلغانلىقىڭلار تۈپەيلىدىن سىلەرنى غەرق قىلىۋېتىشىدىن، ئاندىن سىلەرگە بىزدىن ئۆچ ئېلىپ بېرىدىغان ھېچ ئەھدى تاپالماسلىقىڭلاردىن قورقمامسىلەر؟'),
(2099, 17, 70, 'شەك - شۈبھىسىزكى، بىز ئادەم بالىلىرىنى ھۆرمەتلىك قىلدۇق، ئۇلارنى قۇرۇقلۇقتا (ئۇلۇغلارغا) مىندۇردۇق، دېڭىزدا (كېمىلەرگە) چىقاردۇق، ئۇلارنى شېرىن يېمەكلىكلەر بىلەن رىزىقلاندۇردۇق، ئۇلارنى مەخلۇقاتلىرىمىزنىڭ نۇرغۇنىدىن ئۈستۈن قىلدۇق'),
(2100, 17, 71, 'بىز ھەممە ئادەمنى ئۇلارنىڭ نامە - ئەمالى بىلەن چاقىرىدىغان كۈننى (ئېسىڭدا تۇتقىن)، نامە - ئەمالى ئوڭ قولىدىن بېرىلگەنلەر ئۆزلىرىنىڭ نامە - ئەمالىنى ئوقۇيدۇ، ئۇلارغا قىلچە زورلۇق قىلىنمايدۇ'),
(2101, 17, 72, 'بۇ دۇنيادا (دىلى) كۆر بولغان ئادەم ئاخىرەتتىمۇ كۆر ۋە تېخىمۇ گۇمراھ بولىدۇ'),
(2102, 17, 73, 'ئۇلار سېنى بىز ساڭا ۋەھىي قىلغان قۇرئانغا خىلاپلىق قىلدۇرۇپ، بىزگە قۇرئاندىن غەيرىنى ئىپتىرا قىلدۇرۇشقا تاس قالدى، (ئۇلارنىڭ كۆڭلىدىكىدەك قىلغىنىڭدا) ئەلۋەتتە ئۇلار سېنى (ئۆزلىرىگە) دوست قىلىۋالاتتى'),
(2103, 17, 74, 'سېنى بىز (ھەقتە تۇرۇشتا) مۇستەھكەم قىلمىغان بولساق، ئۇلارغا مايىل بولۇپ كەتكىلى ھەقىقەتەن تاس قالغان ئىدىڭ'),
(2104, 17, 75, 'ئەگەر شۇنداق قىلىدىغان بولساڭ، ئەلۋەتتە ساڭا دۇنيا ۋە ئاخىرەتتە ھەسسىلەپ ئازاب بەرگەن بولاتتۇق، ئاندىن سەن بىزگە قارشى ياردەم بېرىدىغان ھېچ ئادەم تاپالمايتتىڭ'),
(2105, 17, 76, '(يەھۇدىيلار) سېنى (مەدىنە) زېمىنىدىن چىقىرىۋېتىش ئۈچۈن، ئۇ يەردىن سېنى خاتىرجەم تۇرغۇزماسلىققا تاس قالدى، شۇنداق بولغاندا، سەن چىقىپ كەتكەندىن كېيىن، ئۇلار ئازغىنا ۋاقىت تۇرالايتتى (يەنى ئازغىنا ۋاقىتتىن كېيىن ھالاك بولاتتى)'),
(2106, 17, 77, '(بۇ) سەندىن ئىلگىرى بىز ئەۋەتكەن پەيغەمبەرلىرىمىزنىڭ تۇتقان يولىدۇر (يەنى پەيغەمبەرلىرىنى ئارىسىدىن ھەيدەپ چىقارغان ھەرقانداق ئۈممەتنى ھالاك قىلىش اﷲ نىڭ پەيغەمبەرلىرى ھەققىدە تۇتقان يولىدۇر)، تۇتقان يولىمىزدا ئۆزگىرىش تاپالمايسەن'),
(2107, 17, 78, '(ئى مۇھەممەد!) كۈن قايرىلغان ۋاقىتتىن تارتىپ قاراڭغۇ چۈشكەن ۋاقىتقا قەدەر ناماز ئوقۇغىن. بامدات نامىزىنىمۇ ئوقۇغىن، (كۈندۈزنىڭ ۋە كېچىنىڭ پەرىشتىلىرى) چوقۇم بامدات نامىزىدا ھازىر بولىدۇ'),
(2108, 17, 79, 'ساڭا نەپلە (ئىبادەت) بولۇشى ئۈچۈن، كېچىنىڭ بىر قىسىمىدا (ناماز ئوقۇشقا، قۇرئان ئوقۇشقا) ئويغانغىن، (ئى مۇھممەد‍! (پەرۋەردىگارىڭنىڭ سېنى مەدھىيىلىنىدىغان ئورۇنغا (يەنى بۈيۈك شاپائەت ماقامىغا) تۇرغۇزۇشى ھەقىقەتتۇر'),
(2109, 17, 80, '«پەرۋەردىگارىم! مېنى (قەبرەمگە) ئوڭۇشلۇق كىرگۈزگىن، (قەبرەمدىن) ئوڭۇشلۇق چىقارغىن، ماڭا دەرگاھىڭدىن ھەققە ياردەم بېرىدىغان قۇۋۋەت ئاتا قىلغىن» دېگىن'),
(2110, 17, 81, '«ھەقىقەت (يەنى ئىسلام) كەلدى، باتىل (يەنى كۈفرى) يوقالدى. باتىل ھەقىقەتەن ئوڭاي يوقىلىدۇ» دېگىن'),
(2111, 17, 82, 'بىز مۆمىنلەرگە (يەنى ئۇلارنىڭ دىللىرىغا) شىپا ۋە رەھمەت بولىدىغان قۇرئان ئايەتلىرىنى نازىل قىلىمىز، قۇرئان كاپىرلارغا زىياندىن باشقىنى زىيادە قىلمايدۇ (يەنى ئۇلار قۇرئاننى تەستىق قىلمىغانلىقتىن، ئۇلارنىڭ كۇفرى تېخىمۇ ئاشىدۇ)'),
(2112, 17, 83, 'ئىنسانغا (تۈرلۈك نېمەتلەرنى) بەرسەك (پەرۋەردىگارىنىڭ ئىبادىتىدىن) يۈز ئۆرۈيدۇ، (چوڭچىلىق قىلىپ) پەرۋەردىگارىدىن يىراقلىشىدۇ، ئۇنىڭغا بىرەر كۆڭۈلسىزلىك يەتسە، (اﷲ نىڭ رەھمىتىدىن) مەيۈسلىنىپ كېتىدۇ'),
(2113, 17, 84, 'ھەر ئادەم (ھىدايەت ۋە گۇمراھلىقتا) ئۆز يولى بويىچە ئىش قىلىدۇ، پەرۋەردىگارىڭلار كىمنىڭ توغرا يولدا ئىكەنلىكىنى ئوبدان بىلىدۇ'),
(2114, 17, 85, 'ئۇلار سەندىن روھنىڭ ماھىيىتى ھەققىدە سورىشىدۇ. ‹‹روھنىڭ ماھىيىتىنى پەرۋەردىگارىم بىلىدۇ›› دېگىن، سىلەرگە پەقەت ئازغىنا ئىلىم بېرىلگەن'),
(2115, 17, 86, 'ئەگەر بىز خالىساق ساڭا ۋەھيى قىلغان قۇرئاننى ئەلۋەتتە (دىللاردىن ۋە مۇسھەپلەردىن) كۆتۈرۈۋەتتۇق، ئاندىن ئۇنى ئەسلىگە كەلتۈرۈشكە بىزگە قارشى ھېچقانداق ھامىي تاپالمايتتىڭ'),
(2116, 17, 87, 'پەقەت پەرۋەردىگارىڭ رەھمەت قىلىش يۈزىسىدىن ئۇنى كۆتۈرۈۋەتمىدى. اﷲ نىڭ رەھمىتى ساڭا ھەقىقەتەن چوڭ بولدى'),
(2117, 17, 88, 'ئېيتقىنكى، «ئەگەر ئىنسانلار، جىنلار بۇ قۇرئاننىڭ ئوخشىشىنى مەيدانغا كەلتۈرەلمەيدۇ»'),
(2118, 17, 89, 'بۇ قۇرئاندا ئىنسانلار ئۈچۈن تۈرلۈك مەسىللەرنى خىلمۇ - خىل شەكىلدە بايان قىلدۇق، (روشەن پاكىتلارنىڭ بولىشىغا قارىماي) ئىنسانلارنىڭ تولىسى پەقەت (ھەقىقەتنى) ئىنكار قىلىدۇ'),
(2119, 17, 90, 'ئۇلار ئېيتتى: «تاكى سەن بىزگە يەر ئاستىدىن بىر بۇلاقنى ئېتىلدۇرۇپ چىقارمىغىچە ساڭا ھەرگىز ئىشەنمەيمىز'),
(2120, 17, 91, 'ياكى سېنىڭ خورمىلىق، تاللىق ۋە ئوتتۇرىسىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان بىر بېغىڭ بولسۇن'),
(2121, 17, 92, 'ياكى سەن ئېيتقاندەك ئۈستىمىزگە ئاسماننى پارچە - پارچە قىلىپ چۈشۈرگىن، ياكى (پەيغەمبەرلىكىڭگە) گۇۋاھچى قىلىپ اﷲ نى ۋە پەرىشتىلەرنى ئالدىمىزغا كەلتۈرگىن'),
(2122, 17, 93, 'ياكى سېنىڭ ئالتۇندىن ئۆيۈڭ بولسۇن، ياكى سەن (شوتا بىلەن) ئاسمانغا چىققىن، تاكى (اﷲ تەرىپىدىن سېنىڭ ئۇنىڭ بەندىسى ۋە پەيغەمبەرلىكى ئىكەنلىكىڭ يېزىلغان) بىز ئوقۇيالايدىغان بىر كىتابنى ئېلىپ چۈشمىگىچە سېنىڭ ئاسمانغا چىققانلىقىڭغا ھەرگىز ئىشەنمەيمىز». (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «اﷲ پاكتۇر، مەن پەقەت بىر پەيغەمبەر ئىنسانمەن (اﷲ نىڭ ئىزنى بولمىسا ھېچ ئادەم بىرەر مۆجىزە كەلتۈرەلمەيدۇ)»'),
(2123, 17, 94, 'ئۇلار (يەنى مۇشرىكلار) نىڭ ئۆزلىرىگە ھىدايەت (يەنى پەيغەمبەر بىلەن قۇرئان) كەلگەن چاغدا ئىمان ئېيتماسلىقلىرى «اﷲ ئىنساننى پەيغەمبەر قىلىپ ئەۋەتەمدۇ؟» دېگەنلىكلىرى ئۈچۈندۇر'),
(2124, 17, 95, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «مۇبادا زېمىندا (يەنى زېمىن ئەھلىدەك) مېڭىپ يۈرۈيدىغان، ئولتۇراقلاشقان پەرىشتىلەر بولسا ئىدى، ئەلۋەتتە ئۇلارغا ئاسماندىن پەرىشتىنى پەيغەمبەر قىلىپ چۈشۈرەتتۇق»'),
(2125, 17, 96, '(ئى مۇھەممەد‍! (ئېيتقىنكى، «اﷲ مەن بىلەن سىلەرنىڭ ئاراڭلاردا مېنىڭ راستلىقىمغا گۇۋاھ بولۇشقا يېتەرلىكتۇر. اﷲ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر. كۆرۈپ تۇرغۇچىدۇر»'),
(2126, 17, 97, 'كىمكى اﷲ ھىدايەت قىلغان ئىكەن ئۇ ھىدايەت تاپقۇچىدۇر، اﷲ گۇمراھ قىلغان ئادەمگە اﷲ تىن باشقا (ئازابتىن قۇتۇلدۇرىدىغان) ھېچ ياردەمچىلەرنى تاپالمايسەن، قىيامەت كۈنى ئۇلارنى دۈم ياتقۇزۇپ (سۆرەلگەن) كۆر، گاچا، گاس ھالدا يىغىمىز، ئۇلارنىڭ جايى جەھەننەم بولىدۇ، (جەھەننەمنىڭ) ئوتى پەسلەپ قالسا، ئۇلارغا (ئوتنى) تېخىمۇ يالقۇنجىتىمىز'),
(2127, 17, 98, 'ئۇلارنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ۋە «چىرىگەن سۆڭەك ۋە توپىغا ئايلانغىنىمىزدىن كېيىن راستلا قايتىدىن تېرىلىپ يېڭىدىن ئادەم بولامدۇق؟» دېگەنلىكلىرى ئۈچۈن، ئۇلارغا بېرىلگەن جازا ئەنە شۇدۇر'),
(2128, 17, 99, 'ئاسمانلارنى، زېمىننى ياراتقات اﷲ نىڭ ئۇلارغا ئوخشاش ئىنسانلارنى يارىتالايدىغانلىقىنى ئۇلار بىلمەمدۇ؟ اﷲ ئۇلارنىڭ (ئۆلۈشى ۋە تىرىلىشى ئۈچۈن) شەك - شۈبھىسىز مۇئەييەن ۋاقىت بەلگىلىدى، كاپىرلار پەقەت كۇفرىغىلا ئۇنايدۇ'),
(2129, 17, 100, '«ئەگەر پەرۋەردىگارىمنىڭ رەھمەت خەزىنىلىرى سىلەرنىڭ قولۇڭلاردا بولسا، چىقىمدىن (يەنى تۈگەپ كېتىشىدىن) قورقۇپ، چوقۇم بېخىللىق قىلاتتىڭلار، ئىنسان بېخىل كېلىدۇ» دېگىن'),
(2130, 17, 101, 'بىز مۇساغا ھەقىقەتەن توققۇز روشەن مۆجىزە ئاتا قىلدۇق، مۇسا پىرئەۋنگە ۋە ئۇنىڭ قەۋمىگە كەلگەن چاغدا بىز مۇساغا: «(ئۇلاردىن) ئىسرائىل ئەۋلادىنى (قويۇۋېتىشنى) سۆرىغىن» (دېدۇق). پىرئەۋن ئۇنىڭغا: «ئى مۇسا! سېنى مەن چوقۇم جادۇ قىلىنغان دەپ ئويلايمەن» دېدى'),
(2131, 17, 102, 'مۇسا: «(ئى پىرئەۋن!) بۇلار (يەنى توققۇز مۆجىزە) نى پەقەت ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارى (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) دەلىل قىلىپ نازىل قىغانلىقىنى سەن (گەرچە تېلىڭدا ئىقرار قىلمىساڭمۇ دىلىڭدا ئەلۋەتتە) ئوبدان بىلىسەن، مەن سېنى، ئى پىرئەۋن، ھەقىقەتەن، ھالاك بولىدۇ دەپ ئويلايمەن» دېدى'),
(2132, 17, 103, 'پىرئەۋن ئۇلارنى (مىسىر) زېمىنىدىن چىقىرىۋەتمەكچى بولدى، بىز پىرئەۋننى ۋە ئۇنىڭ بىلەن بىللە بولغانلارنى - ھەممىسىنى (دېڭىز) غا غەرق قىلىۋەتتۇق'),
(2133, 17, 104, 'پىرئەۋننى غەرق قىلىۋەتكەندىن كېيىن، بىز ئىسرائىل ئەۋلادىغا: «(مىسىر) زېمىنىدا تۇرۇڭلار، ئاخىرەت ۋەدىسى (يەنى قىيامەت) كەلگەن چاغدا سىلەرنى (قەبرەڭلاردىن مەھشەرگاھقا) (كاپىرلار بىلەن مۆمىنلەرنى) ئارىلاش ئېلىپ كېلىمىز» دېدۇق'),
(2134, 17, 105, 'قۇرئاننى ھەقىقەت ئاساسىدا نازىل قىلدۇق، قۇرئانمۇ ھەقىقەتنى ئۆز ئىچىگە ئالغان ھالدا نازىل بولدى، سېنى بىز پەقەت خوش خەۋەر بەرگۈچى، ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق'),
(2135, 17, 106, 'قۇرئاننى كىشىلەرگە دانە - دانە ئوقۇپ بېرىشىڭ ئۈچۈن ئۇنى بۆلۈپ - بۆلۈپ نازىل قىلدۇق، ئۇنى تەدرىجىي نازىل قىلدۇق'),
(2136, 17, 107, '«قۇرئانغا ئىشىنىڭلار ياكى ئىشەنمەڭلار (مەيلى، چۈنكى ئىشىنىشىڭلار ئۇنىڭغا كامالەت، ئىشەنمەسلىكىڭلار ئۇنىڭغا نۇقسان ئېلىپ كەلمەيدۇ)» دېگىن. شۈبھىسىزكى، قۇرئان نازىل بولۇشتىن ئىلگىرى كىتاب بېرىلگەنلەر قۇرئاننى ئاڭلىغان چاغلىرىدا (تەسىرلىنىپ) دەرھال سەجدىگە بارىدۇ'),
(2137, 17, 108, 'ئۇلار: «پەرۋەردىگارىمىز پاكتۇر، پەرۋەردىگارىمىزنىڭ ۋەدىسى چوقۇم ئورۇنلىنىدۇ» دەيدۇ'),
(2138, 17, 109, 'ئۇلار يىغلىغان ھالدا سەجدە قىلىشقا يىقىلىدۇ (قۇرئاننى ئاڭلاش بىلەن ئۇلار اﷲ قا) تېخىمۇ تەزەررۇ قىلىدۇ'),
(2139, 17, 110, '«اﷲ دەپ ئاتىساڭلارمۇ بولىدۇ، رەھمان دەپ ئاتىساڭلارمۇ بولىدۇ، قايسىنى ئاتىساڭلار (ھەممىسى ياخشىدۇر). چۈنكى اﷲ نىڭ نۇرغۇن گۈزەل ئىسىملىرى بار» دېگىن، نامىزىڭدا (قىرائەتنى) (مۇشرىكلار ئاڭلاپ ساڭا ئەزىيەت يەتكۈزمەسلىگى ئۈچۈن) يۇقىرى ئاۋاز بىلەنمۇ ئوقۇمىغىن، (مۆمىنلەر ئاڭلىماي قالماسلىقى ئۈچۈن) پەس ئاۋاز بىلەنمۇ ئوقۇمىغىن. ئۇلارنىڭ ئارىسىدا ئوتتۇرا يول تۇتقىن'),
(2140, 17, 111, 'ئېيتقىنكى، «جىمى ھەمدۇسانا بالىسى بولۇشتىن پاك بولغان، سەلتەنىتىدە شېرىكى بولمىغان، خارلىقتىن قۇتۇلۇش ئۈچۈن دوستقا موھتاج بولمىغان (يەنى خار بولمىغان، ياردەمچىگە موھتاج بولمىغان) اﷲ قا خاستۇر! اﷲ نى مۇكەممەل رەۋىشتە ئۇلۇغلىغىن»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'جىمى ھەمدۇسانا اﷲ قا خاستۇركى، (اﷲ ئىنسانلارنى) ئۆز تەرىپىدىن بولغان قاتتىق ئازابتىن ئاگاھلاندۇرۇش، ياخشى ئەمەللەرنى قىلغان مۆمىنلەرگە ئوبدان مۇكاپات (يەنى جەننەت) بېرىدىغانلىقى بىلەن خۇش خەۋەر بېرىش ئۈچۈن، بەندىسى (يەنى پەيغەمبىرى مۇھەممەد) گە توغرا، ھېچقانداق قىڭغىرلىق (يەنى ئىختىلاپ، زىددىيەت) بولمىغان قۇرئاننى نازىل قىلدى'),
(2142, 18, 2, 'جىمى ھەمدۇسانا اﷲ قا خاستۇركى، (اﷲ ئىنسانلارنى) ئۆز تەرىپىدىن بولغان قاتتىق ئازابتىن ئاگاھلاندۇرۇش، ياخشى ئەمەللەرنى قىلغان مۆمىنلەرگە ئوبدان مۇكاپات (يەنى جەننەت) بېرىدىغانلىقى بىلەن خۇش خەۋەر بېرىش ئۈچۈن، بەندىسى (يەنى پەيغەمبىرى مۇھەممەد) گە توغرا، ھېچقانداق قىڭغىرلىق (يەنى ئىختىلاپ، زىددىيەت) بولمىغان قۇرئاننى نازىل قىلدى'),
(2143, 18, 3, 'ئۇلار (يەنى ياخشى ئەمەللەرنى قىلغان مۆمىنلەر) جەننەتتە مەڭگۈ قالىدۇ'),
(2144, 18, 4, 'اﷲ نىڭ بالىسى بار دېگۈچىلەرنى ئاگاھلاندۇرىدۇ'),
(2145, 18, 5, 'ئۇلار ۋە ئۇلارنىڭ ئاتا - بوۋىلىرى بۇ بوھتانلىرىدا ھېچقانداق مەلۇماتقا ئىگە ئەمەس، ئۇلارنىڭ ئېغىزلىرىدىن چىققان سۆز نېمىدېگەن چوڭ! ئۇلار پەقەت يالغاننىلا ئېيتىدۇ'),
(2146, 18, 6, 'ئەگەر ئۇلار قۇرئانغا ئىمان ئېيتمىسا، ئەپسۇسلىنىپ، ئۇلارنىڭ (ئىماندىن) يۈز ئۆرۈگەنلىكىدىن ئۆزۈڭنى ھالاك قىلىۋېتىشىڭ مۇمكىن'),
(2147, 18, 7, 'ئىنسانلارنىڭ قايسىلىرىنىڭ ئەمەلى ئەڭ ياخشى ئىكەنلىكىنى سىناش ئۈچۈن، بىز ھەقىقەتەن يەر يۈزىدىكى شەيئىلەرنىڭ ھەممىسىنى يەر يۈزىنىڭ زىننىتى قىلدۇق'),
(2148, 18, 8, 'بىز چوقۇم يەر يۈزىدىكى (دەل - دەرەخ، تاغ ۋە بىنا قاتارلىق) نەرسىلەرنىڭ ھەممىسىنى يوق قىلىپ، يەر يۈزىنى تۈپتۈز باياۋانغا ئايلاندۇرىمىز'),
(2149, 18, 9, 'ئەسھابۇلكەھفنى ۋە رەقىم (يەنى ئەسھابۇلكەھفنىڭ ئىسىملىرى يېزىلغان تاختا) نى (بىزنىڭ قۇدرىتىمىزنى كۆرسىتىپ بېرىدىغان) ئالامەتلىرىمىز ئىچىدىكى ئەڭ قىزىقى دەپ قارىدىڭمۇ؟'),
(2150, 18, 10, 'ئۆز ۋاقتىدا بىر قانچە يىگىت غارنى پاناھ جاي قىلىۋالدى، ئۇلار: «پەرۋەردىگارىمىز! بىزگە رەھمەت خەزىنىلىرىڭدىن ئاتا قىلغىن، بىزنىڭ ئىشلىرىمىزنى تۈزەپ، بىزنى ھىدايەت تاپقۇچىلاردىن قىلغىن» دېدى'),
(2151, 18, 11, 'ئۇلارنى بىز غاردا ئۇزۇن يىللار ئۇخلىتىپ قويدۇق'),
(2152, 18, 12, 'ئاندىن ئىككى گۇرۇھ (يەنى ئەسھابۇلكەھف ئىچىدىكى قانچىلىك ئۇخلىغانلىقلىرى ھەققىدە ئىختىلاپ قىلىشقان ئىككى گۇرۇھ) تىن قايسىسىنىڭ غاردا قانچىلىك ئۇزاق تۇرۇشقانلىرىنى ئىنچىكە ھېسابلايدىغانلىقىنى سىناش ئۈچۈن ئۇلارنى ئويغاتتۇق'),
(2153, 18, 13, 'بىز ساڭا ئۇلارنىڭ ھېكايىسىنى راستلىقى بىلەن ئېيتىپ بېرىمىز، ئۇلار ھەقىقەتەن پەرۋەردىگارىغا ئىمان ئېيتقان، بىز ھىدايەتنى زىيادە قىلغان يىگىتلەردۇر'),
(2154, 18, 14, 'ئۇلارنىڭ دىللىرىنى (يەنى ئىرادىسىنى) مۇستەھكەم قىلدۇق، ئۆز ۋاقتىدا ئۇلار (ئىمانسىز زالىم پادىشاھ ئالدىدا) دەس تۇرۇپ ئېيتتى: «بىزنىڭ پەرۋەردىگارىمىز ئاسمانلارنىڭ ۋە زېمىنىڭ پەرۋەردىگارىدۇر، بىز ئۇنى قويۇپ ھەرگىزمۇ باشقا ئىلاھقا ئىبادەت قىلمايمىز، ئەگەر بىز) اﷲ نىڭ شېرىكى بار) دەيدىغان بولساق، بۇ چاغدا ھەقىقەتەنمۇ تولىمۇ يىراقلاشقان بولىمىز'),
(2155, 18, 15, 'بىزنىڭ قەۋمىمىز اﷲ نى قويۇپ (بۇتلارنى) ئىلاھ قىلىۋالدى، ئۇلار ئۆزلىرىنىڭ ئىلاھلارغا ئىبادەت قىلىشقا تېگىشلىك ئىكەنلىكىگە نېمىشقا ئېنىق دەلىل كەلتۈرمەيدۇ، اﷲ قا يالغاننى چاپلىغان ئادەمدىنمۇ زالىم كىم بار؟»'),
(2156, 18, 16, '(ئى يىگىتلەر!) سىلەر ئۇلاردىن ۋە ئۇلارنىڭ اﷲ تىن باشقا ئىبادەت قىلىدىغان بۇتلىرىدىن ئايرىلغان ئىكەنسىلەر، غارنى پاناھ جاي قىلىڭلار، پەرۋەردىگارىڭلار سىلەرگە كەڭ رەھمەت قىلىدۇ، اﷲ سىلەرنىڭ ئىشلىرىڭلارنى ئاسانلاشتۇرۇپ بېرىدۇ'),
(2157, 18, 17, 'كۈن چىققان چاغدا ئۇلارنىڭ غارنىڭ ئوڭ تەرىپىگە ئۆتۈپ كەتكەنلىكىنى، كۈن پاتقان چاغدا ئۇلارنىڭ غارنىڭ شىمال تەرىپىگە ئۆتۈپ كەتكەنلىكىنى كۆرۈسەن (يەنى غارنىڭ ئىشكى شىمال تەرەپتە بولغانلىقتىن كۈننىڭ نۇرى غارنىڭ ئىككى تەرىپىگە چۈشۈپ تۇرىدۇ، اﷲ ئۇلارنى ھۆرمەتلىگەنلىكتىن، كۈن چىققاندىمۇ، پاتقاندىمۇ ئۇنىڭ نۇرى ئۇلارنىڭ ئۈستىگە چۈشمەيدۇ)، ئۇلار بولسا غارنىڭ (ئوتتۇرىسىدىكى) بوشلۇقتۇر، بۇ اﷲ نىڭ (قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ئالامەتلەردىندۇر، كىمنىكى اﷲ ھىدايەت قىلىدىكەن، ئۇ (ھەقىقىي) ھىدايەت تاپقۇچىدۇر، كىمنىكى (قىلغان يامان ئەمەلى تۈپەيلىدىن) اﷲ ئازدۇرىدىكەن، ئۇنىڭغا يول كۆرسەتكۈچى دوستنى ھەرگىز تاپالمايسەن'),
(2158, 18, 18, '(مۇبادا ئۇلارنى كۆرىدىغان بولساڭ، كۆزلىرىنىڭ ئوچۇقلۇقى ۋە ئۆرۈلۈپ تۇرىدىغانلىقتىن) ئۇلارنى ئويغاق دەپ ئويلايسەن، ھالبۇكى، ئۇلار ئۇيقۇدىدۇر، ئۇلارنى بىز ئوڭ ۋە سول تەرەپكە ئۆرۈپ تۇرىمىز. ئۇلارنىڭ ئىتى (غار) نىڭ بوسۇغىسىغا ئىككى پۇتنى سوزۇپ ياتىدۇ، ئەگەر ئۇلارنى كۆرىدىغان بولساڭ، ئەلۋەتتە ئۇلاردىن قاتتىق قورقۇپ، يۈز ئۆرۈپ چوقۇم قاچقان بولاتتىڭ'),
(2159, 18, 19, 'ئۇلارنى ئۇخلاتقىنىمىزدەك، (غاردا قانچىلىك تۇرغانلىقلىرىنى) ئۆز - ئارا سوراشسۇن دەپ (ئۆلۈمگە ئوخشاش ئۇزاق ئويقۇدىن) ئۇلارنى ئويغاتتۇق، ئۇلارنىڭ بىرى «(بۇ غاردا) قانچە ئۇزۇن تۇردۇڭلار؟» دېدى. ئۇلار: «بىر كۈن ياكى بىر كۈندىنمۇ ئاز تۇردۇق» دېيىشتى. ئۇلارنىڭ (بەزىسى) دېدىكى، «قانچە ئۇزاق تۇرغانلىقىڭلارنى پەرۋەردىگارىڭلار ئوبدان بىلىدۇ (بۇنى سۈرۈشتۈرۈشنىڭ پايدىسى يوق، بىز ھازىر ئاچ)، بىرىڭلارنى بۇيرۇڭلار، بۇ تەڭگىلەرنى شەھەرگە ئېلىپ بېرىپ، كىمنىڭ تامىقى ئەڭ پاكىز بولسا، ئۇنىڭدىن سىلەرگە سېتىۋېلىپ ئېلىپ كەلسۇن، ئۇ (شەھەرگە كىرىشتە ۋە تاماق سېتىۋېلىشتا) ئېھتىيات قىلسۇن سىلەرنى ھېچ كىشىگە تۇيدۇرمىسۇن'),
(2160, 18, 20, 'ئەگەر شەھەردىكى كىشلەر سىلەرنى تۇتۇۋالسا، سىلەرنى چالما كېسەك قىلىپ ئۆلتۈرىدۇ، ياكى سىلەرنى ئۆزلىرىنىڭ دىنىغا كىرىشكە مەجبۇرلايدۇ، مۇنداقتا ھەرگىزمۇ مەقسىتىڭلارغا ئېرىشەلمەيسىلەر»'),
(2161, 18, 21, 'ئۇلارنى ئۇخلىتىپ ئويغاتقىنىمىزدەك، كىشىلەرگە اﷲ نىڭ (قايتا تىرىلدۈرۈش توغرىسىدىكى) ۋەدىسىنىڭ ھەقلىقىنى ۋە قىيامەتنىڭ بولىدىغانلىقىدا شەك - شۈبھە يوق ئىكەنلىكىنى بىلدۈرۈش ئۈچۈن، كىشىلەرنى ئۇلارنىڭ ئەھۋالىدىن خەۋەردار قىلدۇق. ئۆز ۋاقتىدا ئۇلار (ئەسھابۇلكەھف قەبزى روھ قىلىنغاندىن كېيىن) ئەسھابۇلكەھفنىڭ ئىشى ئۈستىدە دەتالاش قىلىشتى، بەزى كىشلەر: «(ئۇلارنىڭ يېنىغا ھېچ كىشىنىڭ كىرمەسلىكى ئۈچۈن) ئەسھابۇلكەھفنىڭ ئەتراپىغا (يەنى غارنىڭ ئىشىكىگە) بىر تام ئېتىڭلار» دېدى، اﷲ ئەسھابۇلكەھفنىڭ ئەھۋالىنى ھەممىدىن ئوبدان بىلىدۇ، گېپى ئۆتىدىغان باشلىقلار (يەنى پادىشاھ ۋە شەھەر كاتتىباشلىرى): «غارنىڭ ئەتراپىغا (يەنى غارنىڭ ئىشكى ئالدىغا) ئەلۋەتتە بىر مەسجىد سالايلى» دېدى'),
(2162, 18, 22, 'ئۇلار (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ زامانىدىكى ناسارالار): «ئۇلار (يەنى ئەسھابۇلكەھف) ئۈچ بولۇپ، تۆتىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ، بەزىلەر: «ئۇلار بەش بولۇپ، ئالتىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ، بۇ پۈتۈنلەي ئاساسسىز، قارىسىغا (ئېيتىلغان سۆزدۇر)، يەنە بەزىلەر: «ئۇلار يەتتە بولۇپ، سەككىزىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ، ئېيىتقىنكى، «ئۇلارنىڭ سانىنى پەرۋەردىگارىم ئوبدان بىلىدۇ، ئۇندىن باشقا ئازغىنا كىشىلەر بىلىدۇ.» ئۇلار توغرىسىدا (ناسارالار بىلەن) پەقەت يۈزەگىنە مۇنازىرىلەشكىن (يەنى بۇ ھەقتە مۇنازىرىلەشكۈچىلەرنى تەستىقمۇ قىلمايدىغان، يالغانغىمۇ چىقارمايدىغان دەرىجىدە مۇنازىرىلەشكىن). ئۇلارنىڭ ھېچبىرىدىن ئەسھابۇلكەھف قىسسىسىنى سورىمىغىن'),
(2163, 18, 23, 'سەن بىرەر ئىشنى قىلماقچى بولساڭ، «ئەتە شۇ ئىشنى چوقۇم قىلىمەن» دېمەي،'),
(2164, 18, 24, '«خۇدا خالىسا» دېگەن سۆزنى قوشۇپ دېگىن (يەنى خۇدا خالىسا قىلىمەن دېگىن)، (خۇدا خالىسا دېگەن سۆزنى ئۇنتۇپ قالساڭ، پەرۋەردىگارىڭنى تىلغا ئالغىن (يەنى «خۇدا خالىسا» نى ئېسىڭگە كەلگەندە ئېيتقىن) ھەمدە: «پەرۋەردىگارىمنىڭ مېنى بۇنىڭدىنمۇ يېقىن توغرا يولغا باشلىشىنى (يەنى دىنىم، دۇنيايىمدا مېنى ئەڭ تۈزۈك ئىشلارغا مۇۋەپپەق قىلىشىنى) ئۈمىد قىلىمەن» دېگىن'),
(2165, 18, 25, 'ئۇلار ئۆز غارىدا ئۈچيۈز توققۇز يىل تۇردى'),
(2166, 18, 26, 'ئېيىتقىنكى، «ئۇلارنىڭ تۇرغان ۋاقتىنى اﷲ ئوبدان بىلىدۇ، ئاسمانلاردىكى، زېمىندىكى غەيبنى (بىلىش) اﷲ قا خاستۇر. اﷲ نىڭ كۆرۈشى نېمىدېگەن مۇكەممەل! اﷲ نىڭ ئاڭلىشى نېمىدېگەن مۇكەممەل! ئۇلارغا اﷲ تىن بۆلەك ھېچقانداق ياردەمچى بولمايدۇ، اﷲ ئۆز ھۆكمىدە ھېچ كىشىنى شېرىك قىلمايدۇ»'),
(2167, 18, 27, '(ئى مۇھەممەد!) سەن پەرۋەردىگارىڭنىڭ كىتابىدىن ساڭا ۋەھيى قىلىنغاننى ئوقۇغىن، اﷲ نىڭ سۆزلىرىنى ھېچ كىشى ئۆزگەرتەلمەيدۇ، سەن ھەرگىزمۇ اﷲ تىن بۆلەك ئىلتىجاگاھ تاپالمايسەن'),
(2168, 18, 28, 'سەن پەرۋەردىگارىڭنىڭ رازىلىقىنى تىلەپ، ئەتىگەن - ئاخشامدا ئۇنىڭغا ئىبادەت قىلىدىغانلار (يەنى ئاجىز، كەمبەغەل مۇسۇلمانلار) بىلەن سەۋرچان بىللە بولغىن، ھاياتىي دۇنيانىڭ زىبۇزىننىتىنى (يەنى مۇشرىكلارنىڭ چوڭلىرىنىڭ سۆھبىتىنى) دەپ، ئۇلارنى كۆزگە ئىلماي قالمىغىن، بىز دىلىنى زىكرىمىزدىن غەپلەتتە قالدۇرغان، نەپسى خاھىشىغا ئەگەشكەن، ئىشى ھەددىدىن ئاشقان ئادەمنىڭ (سۆزى) گە ئەگەشمىگىن'),
(2169, 18, 29, '(ئى مۇھەممەد!) «(بۇ) ھەق (قۇرئان) پەرۋەردىگارىڭلار تەرىپىدىن نازىل بولىدۇ، خالىغان ئادەم ئىمان ئېيتسۇن، خالىغان ئادەم كاپىر بولسۇن» دېگىن، بىز ھەقىقەتەن كاپىرلار ئۈچۈن تۈتۈن پەردىلىرى ئۇلارنى ئورىۋالىدىغان ئوتنى تەييارلىدۇق، ئۇلار (تەشنالىقتىن) سۇ تەلەپ قىلسا، ئۇلارغا مەدەن ئېرىتمىسىگە ئوخشاش، يۈزلەرنى كۆيدۈرۈۋېتىدىغان (ناھايىتى قىزىق) سۇ بېرىلىدۇ، بۇ نېمىدېگەن يامان شاراب! جەھەننەم نېمىدېگەن يامان جاي!'),
(2170, 18, 30, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كېلىدىغان بولساق، بىز ئۇلارنىڭ قىلغان ياخشى ئەمەللىرىنىڭ ساۋابىنى ھەقىقەتەن يوققا چىقىرىۋەتمەيمىز'),
(2171, 18, 31, 'ئەنە شۇلار ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان، ئۇلار دائىم تۇرىدىغان جەننەتلەردىن بەھرىمەن بولىدۇ، جەننەتتە ئۇلار ئالتۇن بىلەيزۈكلەرنى سالىدۇ، نېپىز ۋە قېلىن يىپەكتىن ئىشلەنگەن يېشىل كىيىملەرنى كىيىدۇ. (ئالتۇن ۋە ئېسىل رەختلەر بىلەن قاپلانغان) تەختلەرگە يۆلىنىپ ئولتۇرىدۇ. بۇ نېمىدېگەن ياخشى مۇكاپات! جەننەت نېمىدېگەن گۈزەل جاي'),
(2172, 18, 32, 'ئۇلار (يەنى كەمبەغەللەرنى يېنىڭدىن قوغلىۋېتىشنى تەلەپ قىلغان كاپىرلار) غا مۇنداق (بىرى مۆمىن، بىرى كاپىر بولغان) ئىككى ئادەمنى مىسال قىلىپ كەلتۈرگىن: ئۇلارنىڭ بىرىگە (يەنى كاپىرغا) بىز ئەتراپى خورمىزارلىق، (ئىككى باغنىڭ) ئوتتۇرىسى ئېكىنزارلىق بولغان تاللىق ئىككى باغ ئاتا قىلدۇق'),
(2173, 18, 33, 'ھەر ئىككىلىسى كەم - كۇتىسىز، تولۇق مېۋە بېرىدىغان باغلار بولۇپ، ئۇلارنىڭ ئارىسىدىن ئېقىپ تۇرىدىغان ئۆستەڭنى بەرپا قىلدۇق'),
(2174, 18, 34, 'ئۇ (يەنى باغ ئىگىسى) نۇرغۇن مال - مۈلۈككە ئىگە ئىدى، ئۇ بۇرادىرىگە پەخىرلىنىپ: «مەن سەندىن باي، ئادەملىرىم (يەنى خىزمەتچىلىرىم، بالىلىرىم، ياردەمچىلىرىم) سېنىڭدىن كۆپ» دېدى'),
(2175, 18, 35, 'ئۇ (مۆمىن بۇرادىرىنى يېتىلەپ) بېغىغا كىرىپ (كۇفرىلىق قىلىش بىلەن) ئۆزىگە زۇلۇم قىلغان ھالدا ئېيتتى: «بۇ باغ مەڭگۈ يوقالمايدۇ دەپ ئويلايمەن'),
(2176, 18, 36, 'شۇنىڭدەك قىيامەت بولمايدۇ دەپ ئويلايمەن، ئەگەر مەن پەرۋەردىگارىمغا قايتۇرۇلغاندىمۇ (يەنى قىيامەت بولغاندىمۇ) ئەلۋەتتە بۇ باغدىن ياخشىراق جاينى تاپىمەن»'),
(2177, 18, 37, 'ئۇنىڭغا (مۆمىن) بۇرادىرى مۇنازىرىلەشكەن ھالدا ئېيتتى: «سېنى (ئەسلىدە) تۇپراقتىن، ئاندىن ئابىمەنىيدىن يارىتىپ، ئاندىن سېنى راۋۇرۇس ئىنسان قىلغان زاتنى ئىنكار قىلامسەن؟'),
(2178, 18, 38, 'لېكىن مەن پەرۋەردىگارىم بولغان ئۇ اﷲ نى (ئېتىراپ قىلىمەن)، پەرۋەردىگارىمغا ھېچ ئەھەدىنى شېرىك كەلتۈرمەيمەن'),
(2179, 18, 39, 'سەن بېغىڭغا كىرگەن چېغىڭدا: اﷲ نىڭ خالىغىنى بولىدۇ، اﷲ نىڭ ياردىمى بولمىسا قولۇمدىن ھېچ ئىش كەلمەيدۇ. دېسەڭچۇ! سېنىڭ قارىشىڭچە، مېنىڭ مېلىم ۋە ئەۋلادىم سېنىڭدىن ئاز بولسىمۇ،'),
(2180, 18, 40, 'ئۈمىدكى، پەرۋەردىگارىم ماڭا سېنىڭ بېغىڭدىن ياخشىراق نەرسە ئاتا قىلغاي. بېغىڭغا ئاسماندىن بىر ئاپەت يۈزلەندۈرۈش بىلەن بېغىڭ تۈپتۈز قاقاس يەرگە ئايلىنىپ قالغاي،'),
(2181, 18, 41, 'ياكى ئۇنىڭ سۈيى سىڭىپ كېتىپ ئۇنى ئىزدەش (يەنى ئەسلىگە كەلتۈرۈش) سېنىڭ قولۇڭدىن كەلمەيدۇ»'),
(2182, 18, 42, 'ئۇنىڭ مېۋىلىرى (يەنى بېغى) ۋەيران بولدى، تېلىنىڭ بىدىشلىرى يەرگە يىقىلدى، ئۇ باغقا سەرپ قىلغان چىقىملىرىغا (ھەسرەت چېكىپ) ئىككى ئالقىنىنى ئۇۋۇلىغىلى تۇردى ھەمدە ئۇ: «مەن پەرۋەردىگارىمغا ھېچ ئەھەدىنى شېرىك كەلتۈرمىسەمچۇ» دېدى'),
(2183, 18, 43, 'ئۇنىڭغا اﷲ تىن باشقا ياردەم قىلىدىغان (يەنى ئۇنىڭدىن بۇ بالانى دەپئى قىلىدىغان) ھېچ جامائە بولمىدى، ئۆزىگىمۇ ئۆزى ياردەم قىلالمىدى (يەنى بالانى ئۆزىدىن دەپئى قىلالمىدى)'),
(2184, 18, 44, 'بۇ يەردە (يەنى مۇنداق ھالەتتە) مەدەتكار بولۇش پەقەت بەرھەق اﷲ قا خاستۇر، اﷲ (ئۆزىگە ئىمان ئېيتقۇچىلارغا دۇنيا ۋە ئاخىرەتتە) ياخشى مۇكاپات بەرگۈچىدۇر، (ئۇنىڭغا يۆلەنگەن ۋە ئۇنى ئۈمىد قىلغانلارنىڭ) ئاقىۋىتىنى ياخشى قىلغۇچىدۇر'),
(2185, 18, 45, '(ئى مۇھەممەد!) ئۇلارغا (يەنى كىشىلەرگە) دۇنيا تىرىكچىلىكى ھەققىدە مۇنداق مىسالنى كەلتۈرگىن: (ئۇ شۇنىڭغا) ئوخشايدۇكى، بىز ئاسماندىن يامغۇر ياغدۇردۇق، شۇ سۇ بىلەن زېمىننىڭ ئۆسۈملۈكلىرى بىر - بىرىگە چىرمىشىپ بولۇق ئۆستى، كېيىن ئۇ قۇرۇپ تۆكۈلدى، شاماللار ئۇنى (تەرەپ - تەرەپكە) ئۇچۇرۇپ كەتتى، اﷲ ھەر نەرسىگە قادىردۇر'),
(2186, 18, 46, 'ماللار ۋە بالىلار دۇنيا تىرىكچىلىكىنىڭ زىننىتىدۇر، باقى قالىدىغان ياخشى ئەمەللەرنىڭ ساۋابى پەرۋەردىگارىڭنىڭ نەزىرىدە تېخىمۇ ياخشىدۇر. ئۈمىد تېخىمۇ چوڭدۇر'),
(2187, 18, 47, 'ئۇ كۈندە (يەنى قىيامەت كۈنىدە) بىز تاغلارنى يوق قىلىمىز، زېمىننى ئوچۇق، تۈپتۈز كۆرىسەن (يەنى ئۇنى توسۇپ تۇرىدىغان تاغ، دەرەخ، بىنالار بولمايدۇ)، ئۇلارنى (يەنى ئىنسانلارنى) (مەھشەرگاھقا) يىغىمىز، ئۇلاردىن بىر كىشىنىمۇ قالدۇرۇپ قويمايمىز'),
(2188, 18, 48, 'ئۇلار (يەنى جىمى خالايىق) سەپ - سەپ بولۇشۇپ پەرۋەردىگارىڭغا توغرىلىنىدۇ، (كاپىرلارغا) «سىلەر بىزنىڭ ئالدىمىزغا سىلەرنى دەسلەپتە قانداق ياراتقان بولساق، شۇ ھالەتتە (مالسىز، بالىسىز، يالىڭاچ) كەلدىڭلار، بەلكى سىلەر، سىلەرگە ۋەدە قىلىنغان چاغنى بېكىتمەيدۇ، دەپ ئويلىغانسىلەر (يەنى ئۆلگەندىن كېيىن تىرىلىش، ھېساپ بېرىش، جازاغا تارتىلىش يوق دەپ ئويلىغانسىلەر)» دەيمىز'),
(2189, 18, 49, 'كىشىلەرنىڭ نامە - ئەمالى ئوتتۇرىغا قويۇلىدۇ، گۇناھكارلارنىڭ ئۇنىڭدىكى خاتىرىلەردىن قورققانلىقىنى كۆرىسەن، ئۇلار: «ۋاي بىزگە! بۇ نامە - ئەمالغا چوڭ - كىچىك گۇناھنىڭ ھەممىسى خاتىرىلىنىپتىغۇ؟» دەيدۇ، ئۇلار قىلغان - ئەتكەنلىرىنىڭ ھەممىسىنىڭ نامە - ئەمالىغا خاتىرىلەنگەنلىكىنى كۆرىدۇ، پەرۋەردىگارىڭ ھېچ ئادەمگە زۇلۇم قىلمايدۇ'),
(2190, 18, 50, 'ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى، ئىبلىس جىنلاردىن ئىدى. شۇڭا ئۈ پەرۋەردىگارىنىڭ ئىتائىتىدىن چىقتى. مېنى قويۇپ ئىبلىسنى ۋە ئۇنىڭ ئەۋلادىنى دوست قىلىۋالامسىلەر؟ ھالبۇكى، ئۇلار سىلەرگە دۈشمەندۇر، ئىبلىس زالىملار ئۈچۈن نېمىدېگەن يامان بەدەل! (يەنى اﷲ قا ئىبادەت قىلىشنىڭ ئورنىغا شەيتانغا چوقۇنۇش نېمىدېگەن يامان!)'),
(2191, 18, 51, 'ئۇلار (يەنى شەيتانلار) غا ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىنىمۇ، ئۇلارنىڭ ئۆزلىرىنىڭ يارىتىلىشىنىمۇ كۆرسەتكىنىم يوق (يەنى مەزكۇرلارنى خەلق ئەتكەن چېغىمدا ئۇلارنى ھازىر قىلغىنىم يوق)، (كىشىلەرنى) ئازدۇرغۇچىلارنى ياردەمچى قىلىۋالغىنىممۇ يوق'),
(2192, 18, 52, 'شۇ كۈندە اﷲ (ئۇلارغا) ئېيتىدۇ: «سىلەر مېنىڭ شېرىكلىرىم دەپ ئويلىغىنىڭلارنى چاقىرىڭلار». ئۇلار چاقىرىدۇ، لېكىن ئۇلار (نىڭ شېرىكلىرى) ئىجابەت قىلمايدۇ، بىز ئۇلارنىڭ ئارىسىدا (ئۇلار ئۆتەلمەيدىغان) ھالاكەتلىك جاينى پەيدا قىلدۇق'),
(2193, 18, 53, 'گۇناھكارلار دوزاخنى كۆرۈپ، ئۇنىڭغا كىرىدىغانلىقىغا جەزم قىلىدۇ، دوزاختىن قاچىدىغان جاي تاپالمايدۇ'),
(2194, 18, 54, 'بۇ قۇرئاندا ئىنسانلارغا تۈرلۈك مىساللارنى بايان قىلدۇق. ئىنسان كۆپ دەتالاش قىلىدۇ (يەنى كۆپ جېدەللىشىش ئىنساننىڭ تەبىئىتىدۇر)'),
(2195, 18, 55, 'ئىنسانلارغا ھىدايەت كەلگەن چاغدا، ئۇلارنىڭ ئىمان ئېيتماسلىقلىرى ۋە پەرۋەردىگارىدىن مەغپىرەت تەلەپ قىلماسلىقلىرى پەقەت (اﷲ نىڭ) بۇرۇنقىلارغا تۇتقان يولىنى (يەنى ھالاك قىلىشنى كۈتىدىغانلىقلىرى) ياكى ئۆزلىرىگە كېلىدىغان ئازابنى كۆزلىرى بىلەن كۆرۈشنى (كۈتىدىغانلىقلىرى) ئۈچۈندۇر'),
(2196, 18, 56, 'بىز پەيغەمبەرلەرنى پەقەت (ئىمان ئېيتقۇچىلارغا) خوش خەۋەر بەرگۈچى، (ئاسىيلىق قىلغۇچىلارنى) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتىمىز. كاپىرلار (پەيغەمبەرلەر ئېلىپ كەلگەن) ھەقىقەتنى يوققا چىقىرىش ئۈچۈن بىھۇدە سۆزلەرنى قىلىپ مۇنازىرىلىشىدۇ، ئۇلار مېنىڭ ئايەتلىرىمنى ۋە ئۇلارغا بېرىلگەن ئاگاھلاندۇرۇشلارنى مەسخىرە (يەنى ئويۇنچۇق) قىلىۋالدى'),
(2197, 18, 57, 'پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن پەند - نەسىھەت قىلىنغان، ئۇ ئايەتلەردىن يۈز ئۆرۈگەن ۋە ئۆزىنىڭ قىلغان گۇناھلىرىنى ئۇنتۇغان كىشىدىنمۇ زالىم كىشى بارمۇ؟ شۈبھىسىزكى، بىز ئۇلارنىڭ دىللىرىغا قۇرئاننى چۈشىنىشكە توسقۇنلۇق قىلىدىغان پەردىلەرنى تارتتۇق، قۇلاقلىرىنى ئېغىر قىلدۇق، ئەگەر ئۇلارنى ھىدايەتكە دەۋەت قىلساڭ، ئۇلار ھەرگىز ھىدايەت تاپمايدۇ (يەنى ئۇلارنى ئىمانغا، قۇرئانغا دەۋەت قىلساڭ، ئۇلار دەۋىتىڭنى ھەرگىز قوبۇل قىلمايدۇ)'),
(2198, 18, 58, 'پەرۋەردىگارىڭ ناھايىتى مەغپىرەت قىلغۇچىدۇر ۋە رەھىم قىلغۇچىدۇر. ئەگەر اﷲ ئۇلارنى قىلمىشىغا قاراپ جازالايدىغان بولسا، ئەلۋەتتە ئۇلارغا قىلىنىدىغان ئازابنى تېزلەتكەن بولاتتى، لېكىن ئۇلارغا بەلگىلەنگەن بىر ۋاقىت بار، (ئۇ ۋاقىت كەلگەندە) ئۇلار اﷲ تىن بۆلەك ھېچ پاناگاھ تاپالمايدۇ'),
(2199, 18, 59, 'شۇ شەھەرلەرنىڭ ئاھالىلىرى (يەنى ھۇد، سالىھ، لۇت، شۇئەيب پەيغەمبەرلەرنىڭ قەۋملىرىگە ئوخشاش ئۆتكەنكى ئۈممەتلەر) زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارنى ھالاك قىلدۇق، ئۇلارنىڭ ھالاك بولۇشىغا مۇئەييەن ۋاقىتنى بەلگىلىدۇق'),
(2200, 18, 60, 'ئۆز ۋاقتىدا مۇسا ياش خادىمىغا (يەنى يۇشەئ ئىبىن نۇنغا): «ئىككى دەريانىڭ قوشۇلىدىغان جايىغا يەتمىگىچە مېڭىشىمنى توختاتمايمەن، (شۇ جايغا يەتكەنگە قەدەر) ئۇزاققىچە مېڭىۋېرىمەن» دېدى'),
(2201, 18, 61, 'ئۇلار ئىككى دەريانىڭ قوشۇلىدىغان جايىغا يېتىپ بارغاندا، بېلىقىنى ئۇنتۇپ قالدى، بېلىق دەرياغا يول ئېلىپ كەتتى'),
(2202, 18, 62, 'ئۇلار (ئىككى دەريانىڭ قوشۇلىدىغان جايىدىن) ئۆتكەندە مۇسا ياش خادىمىغا: «ئەتىگەنلىك تامىقىمىزنى ئېلىپ كەلگىن، بۇ سەپىرىمىزدە ھەقىقەتەن چارچاپ كەتتۇق» دېدى'),
(2203, 18, 63, 'ياش خادىم: «بىز چوڭ تاش ئاستىدا ئارام ئېلىۋاتقاندا (يۈز بەرگەن ئىشتىن يەنى پىشۇرۇلغان بېلىقنىڭ تىرىلىپ، سېۋەتتىن سەكرەپ چىقىپ دەرياغا كىرىپ كەتكەنلىكىدىن ئىبارەت قىزىق ئىشتىن) خەۋىرىڭ بارمۇ؟ مەن راستلا بېلىقنى ئۇنتۇپتىمەن. بېلىقنىڭ (ئاجايىپ ۋەقەسى) نى ساڭا ئېيتىشنى پەقەت شەيتان ماڭا ئۇنتۇلدۇرۇپتۇ، بېلىق دەرياغا ئاجايىپ يول ئالغان ئىدى» دېدى'),
(2204, 18, 64, 'مۇسا: «بىزنىڭ ئىزدەيدىغىنىمىز دەل مۇشۇ ئىدى» دېدى - دە، ئىككىسى كەلگەن ئىزى بويىچە كەينىگە يېنىشتى'),
(2205, 18, 65, 'ئۇ ئىككىسى (بېلىقنى ئۇنتۇغان چوڭ تاشنىڭ يېنىدا) بىزنىڭ بەندىلىرىمىزدىن چوڭ مەرھەمىتىمىزگە ئېرىشكەن ۋە بىزنىڭ ئۆزىمىزگە خاس ئىلمىمىزنى (يەنى ئىلمىي غەيبنى) بىز بىلدۈرگەن بىر بەندىنى (يەنى خىزىر ئەلەيھىسسالامنى) ئۇچراتتى'),
(2206, 18, 66, 'مۇسا ئۇنىڭغا: «اﷲ ساڭا بىلدۈرگەن توغرا ئىلىمنى ماڭا ئۆگىتىشىڭ ئۈچۈن ساڭا ئەگىشەيمۇ؟ (يەنى سۆھبىتىڭدە بولايمۇ؟)» دېدى'),
(2207, 18, 67, 'ئۇ ئېيتتى: «سەن مەن بىلەن بىللە بولۇشقا سەۋر - تاقەت قىلىپ تۇرالمايسەن'),
(2208, 18, 68, 'سەن چوڭقۇر تونۇپ يەتمىگەن (يەنى قارىماققا يامان، ئەمما ماھىيىتىنى سەن چۈشەنمىگەن) ئىشقا قانداقمۇ سەۋر - تاقەت قىلىپ تۇرالايسەن؟»'),
(2209, 18, 69, 'مۇسا ئېيتتى: «اﷲ خالىسا مېنى سەۋر قىلغۇچى كۆرىسەن، سېنىڭ بۇيرۇقۇڭغا خىلاپلىق قىلمايمەن»'),
(2210, 18, 70, '(خىزىر) ئېيىتتى: «ئەگەر سەن ماڭا ئەگەشسەڭ، (يولۇققان ئىش توغرىسىدا) مەن ئۇنى ساڭا سۆزلەپ بەرگىچە ئۇنىڭدىن سورىما (يەنى ئۆزۈم سۆزلەپ بەرمىگىچە قىلغان ئىشلىرىمدىن سورىما)»'),
(2211, 18, 71, 'شۇنىڭ بىلەن ئۇ ئىككىسى بىرلىكتە مېڭىپ كېلىپ بىر كېمىگە چىقىشتى، (خىزىر) كېمىنى تېشىۋەتتى (يەنى كېمىنىڭ بىر تاختىسىنى سۇغۇرۇۋەتتى)، مۇسا (ئۇنىڭغا): «كېمىدىكىلەرنى غەرق بولۇپ كەتسۇن دەپ كېمىنى تەشتىڭمۇ؟ سەن ھەقىقەتەن (قورقۇنچلۇق) چوڭ بىر ئىشنى قىلدىڭ» دېدى'),
(2212, 18, 72, '(خىزىر) سەن ھەقىقەتەن مەن بىلەن بىللە بولۇشتا سەۋر - تاقەت قىلىپ تۇرالمايسەن دېمىدىممۇ؟» دېدى'),
(2213, 18, 73, 'مۇسا ئېيتتى: «ئەھدىنى ئۇنتۇپ قالغانلىقىمغا مېنى ئەيىبلىمە، سەن بىلەن بىللە بولۇشتا مېنى قىيىن ھالغا چۈشۈرۈپ قويما»'),
(2214, 18, 74, 'ئۇ ئىككىسى يەنى بىرلىكتە ماڭدى، ئۇلار بىر بالىنى ئۇچراتتى، (خىزىر) ئۇنى ئۆلتۈرۈپ قويدى. مۇسا: «سەن بىر بىگۇناھ جاننى ناھەق ئۆلتۈردۈڭ، سەن ھەقىقەتەن ياۋۇز ئىشنى قىلدىڭ»'),
(2215, 18, 75, '(خىزىر) ئېيتتى: «ساڭا مەن ھەقىقەتەن مەن بىلەن بىللە بولۇشقا سەۋر - تاقەت قىلىپ تۇرالمايسەن دېمىدىممۇ؟»'),
(2216, 18, 76, 'مۇسا ئېيتتى: «بۇنىڭدىن كېيىن يەنە سەندىن بىرەر ئىش توغرۇلۇق سورىسام (يەنى قىلغان ئىشىڭغا ئېتىراز بىلدۈرسەم)، مېنى ئۆزۈڭگە ھەمراھ قىلمىغىن، (ساڭا بەرگەن ۋەدەمگە ئۈچ قېتىم خىلاپلىق قىلغانلىقىم ئۈچۈن مېنىڭ ئالدىمدا (ماڭا ھەمراھ بولماسلىقتا) مەزۇرسەن»'),
(2217, 18, 77, 'ئۇ ئىككىسى يەنە بىللە مېڭىپ بىر شەھەرگە يېتىپ كەلدى، شەھەر ئاھالىسىدىن تاماق سورىدى، شەھەر ئاھالىسى ئۇلارنى مېھمان قىلىشتىن باش تارتتى، ئۇلار بۇ شەھەردە ئۆرۈلۈپ چۈشەي دەپ قالغان بىر تامنى ئۇچراتتى، (ئۇنى خىزىر قولى بىلەن سىناپ) تۈزلەپ قويدى. مۇسا ئېيتتى: «ئەگەر خالىساڭ بۇ ئىش ئۈچۈن ئەلۋەتتە ئىش ھەققى ئالغان بولاتتىڭ»'),
(2218, 18, 78, '(خىزىر) ئېيتتى: «بۇ، سەن بىلەن مېنىڭ ئايرىلىش (ۋاقتىمىز)، سەن سەۋر - تاقەت قىلىپ تۇرالمىغان (يۇقىرىقى ئۈچ ئىشنىڭ) ھەقىقىتىنى ساڭا چۈشەندۈرۈپ بېرەي'),
(2219, 18, 79, 'كېمىگە كەلسەك، ئۇ دېڭىزدا ئىشلەيدىغان بىرقانچە نەپەر كەمبەغەلنىڭ كېمىسى ئىدى، مەن ئۇنى تېشىپ ئەيبناك قىلماقچى بولدۇم، (چۈنكى) ئۇلارنىڭ ئالدىدا (يەنى بارىدىغان يوللىرىدا) ھەرقانداق ساق كېمىنى ئىگىسىدىن (زورلۇق بىلەن) تارتىۋالىدىغان بىر پادىشاھ بار ئىدى'),
(2220, 18, 80, '(ئۆلتۈرۈلگەن) بالىغا كەلسەك، ئۇنىڭ ئاتا - ئانىسى مۆمىن ئىدى. (ئۇ ئەسلىدە كاپىر يارىتىلغانلىقتىن، چوڭ بولغاندا) ئۇنىڭ گۇمراھلىق بىلەن كۇفرىنى ئاتا - ئانىسىغا تېڭىشىدىن قورقتۇق'),
(2221, 18, 81, 'شۇنىڭ ئۈچۈن بىز ئۇلارنىڭ پەرۋەردىگارىنىڭ ئۇلارغا ئۇ بالىغا قارىغاندا پاك ۋە كۆيۈملۈك پەرزەنت ئاتا قىلىشىنى ئىرادە قىلدۇق'),
(2222, 18, 82, 'تامغا كەلسەك، ئۇ شەھەردىكى ئىككى يېتىم بالىنىڭ ئىدى، تام ئاستىدا ئۇلارنىڭ (ھەققى بولغان) خەزىنىسى بار ئىدى (تام ئۆرۈلۈپ كەتسە، خەزىنىسى ئېچىلىپ قېلىپ كىشىلەر ئۇنى ئېلىپ كېتەتتى)، ئۇلارنىڭ ئاتا - ئانىسى ياخشى ئادەم ئىدى. پەرۋەردىگارىڭ ئۇلارنىڭ چوڭ بولغاندا تام ئاستىدىكى خەزىنىسىنى چىقىرىۋېلىشىنى ئىرادە قىلدى، بۇ پەرۋەردىگارىڭنىڭ (ئۇلارغا قىلغان) رەھمىتىدۇر، ئۇنى (يەنى يۇقىرىقى ئۈچ ئىشنى) مەن ئۆز خاھىشىم بىلەن قىلغىنىم يوق (بەلكى ئۇنى اﷲ نىڭ ئەمرى ۋە ئىلھامى بىلەن قىلدىم)، سەن سەۋر - تاقەت قىلىپ تۇرالمىغان نەرسىلەرنىڭ ھەقىقىتى ئەنە شۇ»'),
(2223, 18, 83, 'ئۇلار (يەنى يەھۇدىيلار) سەندىن زۇلقەرنەين توغرۇلۇق سورىشىدۇ. «سىلەرگە مەن ئۇنىڭ قىسسىسىنى (ۋەھيى ئاساسىدا) بايان قىلىمەن» دېگىن'),
(2224, 18, 84, 'ئۇنىڭغا بىز زېمىندا ھەقىقەتەن كۈچ - قۇدرەت ئاتا قىلدۇق، ئۇنىڭغا ئۆز مەقسىتىگە يېتىشنىڭ پۈتۈن يوللىرىنى ئاسانلاشتۇرۇپ بەردۇق'),
(2225, 18, 85, 'زۇلقەرنەين (اﷲ ئۇنىڭغا ئاسانلاشتۇرۇپ بەرگەن) يولغا ماڭدى'),
(2226, 18, 86, 'ئۇ كۈنپېتىش تەرەپكە يېتىپ بارغاندا، كۈننىڭ قارا لايلىق بۇلاققا پېتىپ كېتىۋاتقانلىقىنى ھېس قىلدى. ئۇ بۇلاقنىڭ يېنىدا بىر تۈرلۈك قەۋمنى ئۇچراتتى. بىز: «ئى زۇلقەرنەين! سەن ئۇلارنى جازالىغىن ياكى ئۇلارغا ياخشى مۇئامىلىدە بولغىن» دېدۇق'),
(2227, 18, 87, 'زۇلقەرنەين ئېيتتى: «كىمكى زۇلۇم قىلغان بولسا، بىز ئۇنى جازالايمىز، ئاندىن ئۇ پەرۋەردىگارىڭنىڭ دەرگاھىغا قايتۇرۇلىدۇ، پەرۋەردىگارى ئۇنى قاتتىق جازالايدۇ'),
(2228, 18, 88, 'كىمكى ئىمان ئېيتسا ۋە ياخشى ئەمەل قىلسا، ئۇنىڭغا ئەڭ ياخشى مۇكاپات بېرىلىدۇ، ئۇنى قولاي ئىشقا بۇيرۇيمىز»'),
(2229, 18, 89, 'ئاندىن زۇلقەرنەين (اﷲ ئۇنىڭغا قولايلىق قىلىپ بەرگەن) يولغا ماڭدى'),
(2230, 18, 90, 'زۇلقەرنەين كۈنچىقىش تەرەپكە بارغاندا، كۈننىڭ شۇنداق بىر قەۋمنىڭ ئۈستىگە چۈشۈۋاتقانلىقىنى كۆردىكى، ئۇ قەۋمگە بىز (كۈننىڭ ھارارىتىدىن) ساقلىنىدىغان (كىيىم، ئىمارەت قاتارلىق) نەرسىلەرنى ئاتا قىلمىدۇق'),
(2231, 18, 91, 'ئەھۋال شۇنداق بولدى (يەنى زۇلقەرنەين كۈنچىقىش تەرەپتىكى كىشىلەرگىمۇ كۈنپېتىش تەرەپتىكى كىشىلەرگە قىلغان مۇئامىلىنى قىلدى). بىز ئۇنىڭ قولىدىكى نەرسىلەردىن تولۇق خەۋەردار ئىدۇق'),
(2232, 18, 92, 'ئاندىن ئۇ (اﷲ ئۇنىڭغا قولايلىق قىلىپ بەرگەن) يولغا ماڭدى'),
(2233, 18, 93, 'زۇلقەرنەين ئىككى تاغ ئارىسىغا يېتىپ بارغاندا، ئىككى تاغ ئالدىدا ھېچقانداق سۆزنى چۈشەنمەيدىغان (يەنى ئۆزىنىڭ تىلىدىن غەيرى تىلنى بىلمەيدىغان) بىر قەۋمنى ئۇچراتتى'),
(2234, 18, 94, 'ئۇلار: «ئى زۇلقەرنەين! يەجۇج بىلەن مەجۇج يەر يۈزىدە ھەقىقەتەن بۇزغۇنچىلىق قىلغۇچىلاردۇر، بىز بىلەن ئۇلارنىڭ ئارىسىغا بىر توسما سېلىپ بېرىشىڭ ئۈچۈن، ساڭا بىر قىسىم مېلىمىزنى (باجغا ئوخشاش تۆلىسەك بولامدۇ؟» دېدى'),
(2235, 18, 95, 'زۇلقەرنەين ئېيتتى: «اﷲ نىڭ ماڭا بەرگەن نەرسىلىرى (يەنى كۈچ - قۇۋۋەت ۋە پادىشاھلىق سىلەرنىڭ ماڭا بېرىدىغان مېلىڭلاردىن) ئارتۇقتۇر، ماڭا ئادەم كۈچى ياردەم قىلىڭلار، ئۇلار بىلەن سىلەرنىڭ ئاراڭلارغا مۇستەھكەم بىر توسما سېلىپ بېرەي'),
(2236, 18, 96, 'ماڭا تۆمۈر پارچىلىرىنى ئېلىپ كېلىڭلار، (تۆمۈر پارچىلىرى دۆۋىلىنىپ) ئىككى تاغنىڭ ئارىسى تەكشى بولغاندا، كۆيۈكلەرنى بېسىڭلار». تۆمۈر پارچىلىرى (قىزىپ) ئوتتەك بولغاندا، ئۇ: «ماڭا (ئېرىتىلگەن مىسنى) بېرىڭلار، ئۈستىگە تۆكەي» دېدى'),
(2237, 18, 97, 'ئۇلار (يەنى يەجۇج - مەجۇج) ئۇنىڭ ئۈستىگە چىقىشقىمۇ، ئۇنى تېشىشكىمۇ قادىر بولالمىدى'),
(2238, 18, 98, 'زۇلقەرنەين ئېيتتى: «بۇ (يەنى توسمىنىڭ بەرپا قىلىنىشى) پەرۋەردىگارىمنىڭ نېمىتىدۇر، پەرۋەردىگارىمنىڭ ۋەدىسى ئىشقا ئاشقاندا، ئۇ ئۇنى تۈپتۈز قىلىۋېتىدۇ، پەرۋەردىگارىمنىڭ ۋەدىسى ھەقتۇر»'),
(2239, 18, 99, 'ئۇ كۈندە (يەنى قىيامەت كۈنىدە، قىستاڭچىلىقتىن) كىشىلەرنى بىر - بىرىگە گىرەلەشتۈرۈۋېتىمىز، سۇر چېلىنىدۇ، ئۇلارنى (ھېساب ئۈچۈن) يېغىمىز'),
(2240, 18, 100, 'ئۇ كۈندە كاپىرلارغا جەھەننەمنى ئاشكارا كۆرسىتىمىز'),
(2241, 18, 101, 'ئۇلارنىڭ (بۇ دۇنيادىكى چاغلىرىدا دىللىرىنىڭ) كۆزلىرى مېنى ياد قىلىشتىن پەردىلەنگەن ئىدى، (ئۇلارنىڭ دىللىرىنى زۇلمەت باسقانلىقى ئۈچۈن كالامۇللانى) ئاڭلاشقا قادىر ئەمەس ئىدى'),
(2242, 18, 102, 'كاپىرلار مېنى قويۇپ بەندىلىرىمنى مەبۇد قىلىۋېلىشقا بولىدۇ دەپ ئويلامدۇ؟ بىز جەھەننەمنى كاپىرلارغا ھەقىقەتەن مەنزىلگاھ قىلىپ تەييارلىدۇق'),
(2243, 18, 103, 'سىلەرگە ئەمەللىرى جەھەتتىن ئەڭ زىيان تارتقۇچىلارنى ئېيتىپ بېرەيلىمۇ؟'),
(2244, 18, 104, 'ئۇلار ھاياتىي دۇنيادا قىلغان ئەمەللىرى يوققا چىققان ئەمما ئۆزلىرى ئوبدان ئىش قىلدۇق دەپ ئويلىغان ئادەملەردۇر'),
(2245, 18, 105, 'ئەنە شۇلار پەرۋەردىگارىنىڭ ئايەتلىرىنى ۋە ئۇنىڭغا مۇلاقات بولۇشنى ئىنكار قىلغان، نەتىجىدە قىلغان ئەمەللىرى بىكار بولۇپ كەتكەن كىشىلەردۇر، قىيامەت كۈنى ئۇلارنى (يەنى مۇنداق كىشىلەرنى) قىلچە ئېتىبارغا ئالمايمىز'),
(2246, 18, 106, 'كاپىر بولغانلىقلىرى، مېنىڭ ئايەتلىرىمنى ۋە پەيغەمبەرلىرىمنى مەسخىرە قىلغانلىقلىرى ئۈچۈن، ئۇلارنىڭ جازاسى جەھەننەمدۇر'),
(2247, 18, 107, 'شۈبھىسىزكى، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ مەنزىلگاھى فىردەۋس جەننەتلىرى بولىدۇ'),
(2248, 18, 108, 'ئۇلار فىردەۋس جەننەتلىرىدە مەڭگۈ قالىدۇ، ئۇنىڭدىن يۆتكىلىشنى خالىمايدۇ'),
(2249, 18, 109, 'ئېيتقىنكى، «پەرۋەردىگارىمنىڭ سۆزلىرىنى يېزىش ئۈچۈن، ئەگەر دېڭىز (سۈيى) سىيا بولۇپ كەتسە، پەرۋەردىگارىمنىڭ سۆزلىرى تۈگىمەي تۇرۇپ، دېڭىز (سۈيى) چوقۇم تۈگەپ كەتكەن بولاتتى، مۇبادا يەنە شۇنچىلىك دېڭىز (سۈيىنى) كەلتۈرسەكمۇ»'),
(2250, 18, 110, '(ئى مۇھەممەد‍! (ئېيتقىنكى، مەن سىلەرگە ئوخشاش بىر ئىنسانمەن، ماڭا ۋەھىي قىلىنىدۇكى، سىلەرنىڭ ئىلاھىڭلار يالغۇز بىر ئىلاھتۇر، كىمكى، پەرۋەردىگارىغا مۇلاقات بولۇشنى ئۈمىد قىلىدىكەن (ساۋابىنى ئۈمىد قىلىپ ئازابىدىن قورقىدىكەن)، ياخشى ئىش قىلسۇن. پەرۋەردىگارىغا قىلىدىغان ئىبادەتكە ھېچكىمنى شېرىك كەلتۈرمىسۇن»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'كاف، ھا، يا، عين، صاد'),
(2252, 19, 2, 'بۇ، پەرۋەردىگارىڭنىڭ بەندىسى زەكەرىياغا قىلغان مەرھىمىتىنىڭ بايانىدۇر'),
(2253, 19, 3, 'ئۇ ئۆز ۋاقتىدا پەرۋەردىگارىغا پەس ئاۋازدا مۇناجات قىلدى'),
(2254, 19, 4, 'ئۇ ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن كۈچ - قۇۋۋىتىمدىن كەتتىم، چېچىم ئاقاردى. پەرۋەردىگارىم! ساڭا دۇئا قىلىپ ئۈمىدسىز بولۇپ قالغىنىم يوق'),
(2255, 19, 5, 'مەن تاغىلىرىمنىڭ بالىلىرىنىڭ ئورنۇمنى باسالماسلىقلىرىدىن (يەنى دىننى قولدىن بېرىپ قويۇشلىرىدىن) ھەقىقەتەن ئەنسىرەيمەن، مېنىڭ ئايالىم تۇغماستۇر، (پەزلىڭدىن) ماڭا بىر (ياخشى) پەرزەنت ئاتا قىلغىن'),
(2256, 19, 6, 'ئۇ ماڭا ۋارىس بولغاي ۋە يەئقۇب ئەۋلادىغا ۋارىس بولغاي، پەرۋەردىگارىم! ئۇنى سېنىڭ رازىلىقىڭغا ئېرىشىدىغان قىلغىن»'),
(2257, 19, 7, '(اﷲ ئېيتتىكى) «ئى زەكەرىيا! بىز ھەقىقەتەن ساڭا (پەرىشتىلەر ئارقىلىق) يەھيا ئىسىملىك بىر ئوغۇل بىلەن خۇش خەۋەر بېرىمىز، ئىلگىرى ھېچ ئادەمنى ئۇنىڭغا ئىسىمداش قىلمىدۇق»'),
(2258, 19, 8, 'زەكەرىيا ئېيتتى: «ئايالىم تۇغماس تۇرسا، مەن قېرىپ مۈكچىيىپ كەتكەن تۇرسام، مېنىڭ قانداقمۇ ئوغلۇم بولسۇن؟»'),
(2259, 19, 9, '(نىدا قىلغان پەرىشتە) ئېيتتى: «ئىش ئەنە شۇنداقتۇر، پەرۋەردىگارىڭ ئېيتتى: بۇ مەن ئۈچۈن ئاساندۇر، سېنى مەن ياراتتىم، ھالبۇكى سەن بۇرۇن يوق ئىدىڭ (يەنى سېنى يوقتىن بار قىلغاندەك، يەھيانى ئىككىڭلاردىن تۆرەلدۈرۈشكە قادىرمەن)»'),
(2260, 19, 10, 'زەكەرىيا ئېيتتى: «پەرۋەردىگارىم! (ئايالىمنىڭ ھامىلدار بولغانلىقىغا) ماڭا بىر ئالامەت قىلىپ بەرسەڭ». اﷲ ئېيتتى: «سېنىڭ ئالامىتىڭ (شۇكى) ساق تۇرۇپ ئۈچ كېچە - كۈندۈز كىشىلەرگە سۆز قىلالمايسەن»'),
(2261, 19, 11, 'زەكەرىيا نامازگاھىدىن قەۋمىنىڭ ئالدىغا چىقىپ، ئۇلارنى ئەتىگەن - ئاخشامدا تەسبىھ ئېيتىشقا ئىشارەت قىلدى'),
(2262, 19, 12, '(يەھيا تۇغۇلۇپ چوڭ بولغاندا ئۇنىڭغا) «ئى يەھيا! كىتابنى (يەنى تەۋراتنى) تىرىشىپ ئوقۇغىن» (دېدۇق). ئۇنىڭغا بالىلىق چېغىدىلا ئەقىل - پاراسەت ئاتا قىلدۇق'),
(2263, 19, 13, 'بىز ئۇنىڭغا مەرھەمەت قىلىش ۋە ئۇنى (ناچار خىسلەتلەردىن) پاك قىلىش يۈزىسىدىن (شۇنداق قىلدۇق)، يەھيا تەقۋادار ئىدى'),
(2264, 19, 14, 'ئۇنى ئاتا - ئانىسىغا كۆيۈمچان (قىلدۇق)، ئۇ مۇتەكەببىر (يەنى ئاتا - ئانىسىنى قاخشاتقۇچى)، (پەرۋەردىگارىغا) ئاسىيلىق قىلغۇچى بولمىدى'),
(2265, 19, 15, 'ئۇ تۇغۇلغان كۈنىدە، ۋاپات بولغان كۈنىدە، تىرىلىپ (قەبرىدىن) تۇرغۇزۇلغان كۈنىدە (اﷲ تائالا تەرىپىدىن بولغان) ئامانلىققا ئېرىشىدۇ'),
(2266, 19, 16, '(ئۇلارغا) قۇرئاندا مەريەم (قىسسىسى) نى بايان قىلغىن، ئەينى زاماندا ئۇ ئائىلىسىدىن ئايرىلىپ شەرق تەرەپتىكى بىر جايغا باردى'),
(2267, 19, 17, 'ئۇ پەردە تارتىپ كىشىلەردىن يۇشۇرۇندى، ئۇنىڭغا بىزنىڭ روھىمىزنى (يەنى جىبرىئىل ئەلەيھىسسالامنى) ئەۋەتتۇق، ئۇ مەريەمگە بېجىرىم ئادەم سۈرىتىدە كۆرۈندى'),
(2268, 19, 18, 'مەريەم ئېيتتى: «مەن ھەقىقەتەن مەرھەمەتلىك اﷲ قا سېغىنىپ سېنىڭ چېقىلىشىڭدىن پاناھ تىلەيمەن، ئەگەر سەن تەقۋادار بولساڭ (ماڭا چېقىلمىغىن)»'),
(2269, 19, 19, 'جىبرىئىل ئېيتتى: «مەن ساڭا بىر پاك ئوغۇل بېرىش ئۈچۈن ئەۋەتىلگەن پەرۋەردىگارىڭنىڭ ئەلچىسىمەن»'),
(2270, 19, 20, 'مەريەم ئېيتتى: «ماڭا كىشى يېقىنلاشمىغان تۇرسا، مەن پاھىشە قىلمىغان تۇرسام، قانداقمۇ مېنىڭ ئوغۇل بالام بولسۇن؟»'),
(2271, 19, 21, 'جىبرىئىل ئېيتتى: «ئەمەلىيەت سەن دېگەندەكتۇر، (لېكىن پەرۋەردىگارىڭ ئېيتتى: ئۇ ماڭا ئاساندۇر. ئۇنى كىشىلەرگە قۇدرىتىمىزنى كۆرسىتىدىغان) دەلىل ۋە بىز تەرەپتىن بولغان مەرھەمەت قىلدۇق، بۇ تەقدىر قىلىنىپ بولغان ئىشتۇر»'),
(2272, 19, 22, 'مەريەم ھامىلدار بولدى، (ئائىلىسىدىن) يىراق بىر جايغا كەتتى'),
(2273, 19, 23, 'تولغاقنىڭ قاتتىقلىقى ئۇنى (يۆلىنىۋېلىش ئۈچۈن) بىر خورما دەرىخىنىڭ يېنىغا كېلىۋېلىشقا مەجبۇر قىلدى. مەريەم ئېيتتى: «كاشكى مەن بۇنىڭدىن ئىلگىرى ئۆلۈپ كەتكەن بولسامچۇ! كىشىلەر تەرىپىدىن ئۇنتۇلۇپ كەتكەن (ئەرزىمەس) نەرسىگە ئايلىنىپ كەتسەمچۇ!»'),
(2274, 19, 24, 'جىبرىئىل ئۇنىڭغا خورما دەرىخىنىڭ ئاستىدىن نىدا قىلدى: «غەم قىلمىغىن، پەرۋەردىگارىڭ سېنىڭ ئاستىڭدىن (ئېقىپ تۇرىدىغان) بىر ئېرىقنى پەيدا قىلدى'),
(2275, 19, 25, 'خورما دەرىخىنى ئۆز تەرىپىڭگە ئىرغىتقىن، ئالدىڭغا پىشقان يېڭى خورمىلار تۆكۈلىدۇ'),
(2276, 19, 26, '(بۇ شېرىن خورمىدىن) يېگىن، (بۇ تاتلىق سۇدىن ئىچكىن ۋە خۇرسەن بولغىن، بىرەر ئادەمنى كۆرسەڭ (ئۇ بالا توغرۇلۇق سورىسا): مەن ھەقىقەتەن مەرھەمەتلىك اﷲ قا ۋەدە بەردىم، بۈگۈن ھېچ ئادەمگە سۆز قىلمايمەن دېگىن»'),
(2277, 19, 27, 'مەريەم بوۋاقنى (يەنى ئىسا ئەلەيھىسسالامنى) كۆتۈرۈپ قەۋمىنىڭ ئالدىغا كەلدى، ئۇلار ئېيتتى: «ئى مەريەم! سەن ھەقىقەتەن غەلىتە ئىش قىلدىڭ'),
(2278, 19, 28, 'ئى ھارۇننىڭ ھەمشىرىسى! سېنىڭ ئاتاڭ يامان ئادەم ئەمەس ئىدى، ئاناڭمۇ يولدىن چىققان خوتۇن ئەمەس ئىدى»'),
(2279, 19, 29, 'مەريەم بوۋاقنى كۆرسەتتى، ئۇلار: «بۆشۈكتىكى بوۋاققا قانداق سۆزلەيمىز» دېدى'),
(2280, 19, 30, 'بوۋاق ئېيتتى: «مەن ھەقىقەتەن اﷲ نىڭ بەندىسىمەن، ماڭا كىتابنى بەردى (يەنى ئىنجىلنى بېرىدۇ) ۋە مېنى پەيغەمبەر قىلدى (يەنى قىلىدۇ)'),
(2281, 19, 31, 'قەيەردە بولاي مېنى بەرىكەتلىك قىلدى، ھاياتلا بولىدىكەنمەن، ماڭا نامازنى، زاكاتنى ئادا قىلىشنى تەۋسىيە قىلدى'),
(2282, 19, 32, 'مېنى ئانامغا كۆيۈمچان قىلدى، مېنى مۇتەكەببىر، شەقى قىلمىدى'),
(2283, 19, 33, 'مەن تۇغۇلغان كۈنۈمدە، ۋاپات بولغان كۈنۈمدە، تىرىلىپ قەبرەمدىن تۇرغۇزۇلغان كۈنۈمدە (اﷲ تائالا تەرىپىدىن بولغان) ئامانلىققا ئېرىشىمەن»'),
(2284, 19, 34, 'ئەنە شۇ ئىسا ئىبىن مەريەمنىڭ (قىسسىسىدۇر). ئىسا ئۇلار دەتالاش قىلىۋاتقان ئۆزىنڭ ئىشىنىڭ (ھەقىقىتى) ئۈستىدە ھەق گەپنى قىلدى'),
(2285, 19, 35, '«اﷲ نىڭ شەنىگە بالىسى بولۇش لايىق ئەمەس، اﷲ (بالىدىن، شېرىكتىن) پاكتۇر، اﷲ بىرەر ئىش قىلىشنى ئىرادە قىلسا، ئۇنىڭغا ۋۇجۇتقا كەل دەيدۇ - دە ئۇ ۋۇجۇتقا كېلىدۇ'),
(2286, 19, 36, 'اﷲ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، شۇنىڭ ئۈچۈن ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر»'),
(2287, 19, 37, 'ئىسانىڭ ئىشى توغرىسىدا ئۇلار (يەنى ئەھلى كىتاب) ئارىسىدىكى پىرقىلەر ئىختىلاپ قىلىشتى، بۈيۈك كۈن (يەنى قىيامەت كۈنى) كەلگەن چاغدا، كاپىرلارغا ۋاي!'),
(2288, 19, 38, 'ئۇلار بىزنىڭ دەرگاھىمىزغا كەلگەن كۈندە (يەنى قىيامەت كۈنىدە) قۇلاقلىرى تازا ئاڭلايدىغان كۆزلىرى تازا كۆرىدىغان بولىدۇ، لېكىن زالىملار بۈگۈن (يەنى بۇ دۇنيادا) روشەن گۇمراھلىقتىدۇر'),
(2289, 19, 39, 'ئۇلارنى (يەنى خالايىقنى) ھەسرەت كۈنى (يەنى قىيامەت كۈنىدىن ئاگاھلاندۇرغىن، ئۇ چاغدا ھەممە ئىش بىر تەرەپ قىلىنغان بولىدۇ. ھالبۇكى، ئۇلار غەپلەتتىدۇر، (قىيامەتنى) ئىنكار قىلماقتىدۇر'),
(2290, 19, 40, 'زېمىنغا ۋە زېمىن ئۈستىدىكى بارچىگە چوقۇم بىز ۋارىسلىق قىلىمىز. ئۇلار (ھېساب بېرىش ئۈچۈن) بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىدۇ'),
(2291, 19, 41, '(ئى مۇھەممەد!) كىتابتا (يەنى قۇرئاندا) ئىبراھىم (قىسسىسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن تولىمۇ راستچىل پەيغەمبەر ئىدى'),
(2292, 19, 42, 'ئۆز ۋاقتىدا ئىبراھىم ئاتىسىغا ئېيتتى: «ئى ئاتا نېمىشقا ئاڭلىمايدىغان، كۆرمەيدىغان ۋە ساڭا ھېچ نەرسىگە ئەسقاتمايدىغان نەرسىلەرگە چوقۇنىسەن'),
(2293, 19, 43, 'ئى ئاتا! ماڭا سەن بىلمەيدىغان ئىلىملەر نازىل بولدى، ماڭا ئەگەشكىن، سېنى توغرا يولغا باشلايمەن'),
(2294, 19, 44, 'ئى ئاتا! شەيتانغا چوقۇنمىغىن، شەيتان مەرھەمەتلىك اﷲ قا ھەقىقەتەن ئاسىيلىق قىلدى'),
(2295, 19, 45, 'ئى ئاتا! سېنىڭ مەرھەمەتلىك اﷲ نىڭ ئازابىغا قېلىشىڭدىن، شەيتاننىڭ ھەمراھىغا ئايلىنىپ قېلىشىڭدىن قورقىمەن»'),
(2296, 19, 46, 'ئۇ (يەنى ئازەر ئېيتتى: «ئى ئىبراھىم! سەن مېنىڭ ئىلاھلىرىمدىن يۈز ئۆرۈمەكچىمۇسەن، ئەگەر سەن (مۇنداق قىلىشتىن) يانمىساڭ، چوقۇم سېنى تاش - كېسەك قىلىپ ئۆلتۈرىمەن، ئۇزاق زامان مەندىن يىراق تۇرغىن»'),
(2297, 19, 47, 'ئىبراھىم ئېيتتى: «ساڭا ئامانلىق بولسۇن، پەرۋەردىگارىمدىن ساڭا مەغپىرەت تىلەيمەن، اﷲ ماڭا ھەقىقەتەن مېھرىباندۇر'),
(2298, 19, 48, 'سىلەردىن ۋە سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان نەرسىلەردىن يىراق بولىمەن، پەرۋەردىگارىمغا ئىبادەت قىلىمەن، پەرۋەردىگارىمنىڭ ئىبادىتىدىن مەھرۇم بولۇپ قالماسلىقنى ئۈمىد قىلىمەن»'),
(2299, 19, 49, 'ئۇ ئۇلاردىن ۋە ئۇلارنىڭ اﷲ نى قويۇپ چوقۇنۇۋاتقان نەرسىلىرىدىن يىراقلاشتى، ئۇنىڭغا بىز ئىسھاق ۋە يەئقۇبنى ئاتا قىلدۇق، ئۇ ئىككىسىنىڭ ھەر بىرسىنى پەيغەمبەر قىلدۇق'),
(2300, 19, 50, 'ئۇلارغا بىزنىڭ رەھمىتىمىزنى ئاتا قىلدۇق، ئۇلارنىڭ نامىنى بەلەن ۋە يۇقىرى قىلدۇق'),
(2301, 19, 51, 'كىتابتا (يەنى قۇرئاندا) مۇسا (قىسىسى) نى بايان قىلغىن، شۈبھىسىزكى، ئۇ اﷲ ئىبادىتىگە خاس قىلغان ھەم رەسۇل (يەنى ئەلچى)، ھەم پەيغەمبەر ئىدى'),
(2302, 19, 52, 'ئۇنىڭغا بىز تۇر تېغىنىڭ ئوڭ تەرىپىدىن نىدا قىلدۇق، ئۇنى بىز مۇناجات ئۈچۈن (دەرگاھىمىزغا) يېقىنلاشتۇردۇق'),
(2303, 19, 53, 'ئۇنىڭغا بىز مەرھەمەت قىلىپ قېرىندىشى ھارۇننى پەيغەمبەر قىلىپ بەردۇق'),
(2304, 19, 54, '(ئى مۇھەممەد!) كىتابتا (يەنى قۇرئاندا) ئىسمائىل (نىڭ قىسىسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن ۋەدىسىدە راستچىل، ھەم رەسۇل، ھەم پەيغەمبەر ئىدى'),
(2305, 19, 55, 'ئۇ ئائىلىسىدىكىلەرنى ناماز ئوقۇشقا بۇيرۇيتتى، پەرۋەردىگارىنىڭ دەرگاھىدا ئۇنىڭ رازىلىقىغا ئېرىشكەن ئىدى'),
(2306, 19, 56, 'كىتابتا (يەنى قۇرئاندا) ئىدرىس (قىسىسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن راستچىل پەيغەمبەر ئىدى'),
(2307, 19, 57, 'ئۇنى بىز يۇقىرى ئورۇنغا كۆتۈردۇق'),
(2308, 19, 58, 'اﷲ نىڭ نېمىتىگە ئېرىشكەن ئەنە شۇ پەيغەمبەرلەر ئادەم (ئەلەيھىسسالام) نىڭ نەسلىدىندۇر، بىز نۇھ بىلەن بىللە كېمىگە سالغانلارنىڭ نەسلىدىندۇر، ئىبراھىمنىڭ نەسلىدىندۇر، ئىسرائىل (يەنى يەئقۇب ئەلەيھىسسالام) نىڭ نەسلىدىندۇر، بىز (ئىمانغا) ھىدايەت قىلغان، (پەيغەمبەرلىككە) تاللىغانلاردىندۇر، ئۇلارغا مەرھەمەتلىك اﷲ نىڭ ئايەتلىرى تىلاۋەت قىلىنغان چاغدا سەجدە قىلغان ۋە يىغلىغان ھالدا (زېمىنغا) يىقىلىدۇ'),
(2309, 19, 59, 'ئۇلار كەتكەندىن كېيىن (ئۇلارنىڭ) ئورنىنى باسقان ئورۇنباسارلار نامازنى تەرك ئەتتى، نەپسى خاھىشلىرىغا ئەگەشتى، ئۇلار (قىيامەتتە) گۇمراھلىقنىڭ جازاسىنى تارتىدۇ'),
(2310, 19, 60, 'تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار بۇنىڭدىن مۇستەسنا، ئەنە شۇلارلا جەننەتكە كىرىدۇ، ئۇلارنىڭ (ئەمەللىرىنىڭ ساۋابىدىن) ھېچ نەرسە كېمەيتىلمەيدۇ'),
(2311, 19, 61, 'ئۇ دائىمىي تۇرالغۇ بولغان، مەرھەمەتلىك اﷲ بەندىلىرىگە ۋەدە قىلغان، ئۇلار كۆرمەي تۇرۇپ (ئىشەنگەن) جەننەتلەردۇر، اﷲ نىڭ (جەننەت بىلەن قىلغان) ۋەدىسى چوقۇم ئىشقا ئاشىدۇ'),
(2312, 19, 62, 'ئۇلار جەننەتتە بىھۇدە سۆز ئاڭلىمايدۇ، پەقەت (پەرىشتىلەرنىڭ ئۇلارغا بەرگەن) سالامىنىلا ئاڭلايدۇ، ئۇلار جەننەتتە ئەتىگەن - ئاخشامدا (كۆڭلى تارتقان يىمەك - ئىچمەكلەردىن) رىزىققا ئىگە بولۇپ تۇرىدۇ'),
(2313, 19, 63, 'بەندىلىرىمىز ئىچىدىكى تەقۋادارلارغا بىز مىراس قىلىپ بېرىدىغان جەننەت ئەنە شۇ'),
(2314, 19, 64, 'بىز (دۇنياغا) پەرۋەردىگارىڭنىڭ ئەمرى بىلەن چۈشىمىز، بىزنىڭ ئالدىمىزدىكى، ئارقىمىزدىكى ۋە ئالدى - كەينىمىز ئارىسىدىكى ئىشلارنىڭ ھەممىسى اﷲ نىڭ ئىلكىدىدۇر، پەرۋەردىگارىڭ (بەندىلىرىنىڭ ئەمەللىرىدىن ھېچ نەرسىنى) ئۇنتۇمايدۇ'),
(2315, 19, 65, 'ئۇ ئاسمانلارنىڭ، زېمىن ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر، ئۇنىڭغا ئىبادەت قىلغىن، اﷲ نىڭ ئىبادىتىگە چىداملىق بولغىن، سەن ئۇنىڭ شېرىكى بارلىقىنى بىلەمسەن؟'),
(2316, 19, 66, 'ئىنسان (يەنى ئۆلگەندىن كېيىن تىرىلىشىگە ئېتىقاد قىلمايدىغان كاپىر): «مەن چوقۇم تىرىلەمدىم؟» دەيدۇ'),
(2317, 19, 67, 'ئىنسان بىز ئۇنى ئىلگىرى يوقتىن بار قىلغانلىقىمىزنى ئەسلىمەمدۇ؟'),
(2318, 19, 68, 'پەرۋەردىگارىڭ بىلەن قەسەمكى، ئۇلارنى ئەلۋەتتە (ئۇلارنى ئازدۇرغان) شەيتانلار بىلەن قوشۇپ يىغىمىز، ئاندىن ئۇلارنى جەھەننەمنىڭ چۆرىسىدە تىزلاندۇرۇپ ئولتۇرغۇزغان ھالدا ئەلۋەتتە ھازىر قىلىمىز'),
(2319, 19, 69, 'ئاندىن ھەربىر گۇرۇھنىڭ ئىچىدىن مەرھەمەتلىك اﷲ قا ئەڭ ئاسىي بولغانلىرىنى تارتىپ چىقىرىمىز'),
(2320, 19, 70, 'ئاندىن بىز دوزاخ ئازابىغا ئەڭ لايىق بولغانلارنى ئەلۋەتتە ئوبدان بىلىمىز'),
(2321, 19, 71, 'سىلەرنىڭ ئىچىڭلاردىن دوزاخقا بارمايدىغان بىرەر كىشىمۇ قالمايدۇ، بۇ پەرۋەردىگارىڭنىڭ ئۆزگەرمەس ھۆكمىدۇر'),
(2322, 19, 72, 'ئاندىن ئەڭ تەقۋادارلارنى (جەھەننەمدىن) قۇتقۇزىمىز، زالىملارنى جەھەننەمدە تىزلىنىپ ئولتۇرغان ھالدا قويىمىز'),
(2323, 19, 73, 'ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنغان چاغدا، كاپىرلار مۆمىنلەرگە: «ئىككى گۇرۇھتىن قايسىنىڭ ئورنى ياخشى، سورۇنى گۈزەل؟» دېدى'),
(2324, 19, 74, 'ئۇلاردىن بۇرۇن (بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، بۇلار مەيلى مال - مۈلۈك جەھەتتىن بولسۇن، مەيلى تاشقى كۆرۈنۈش جەھەتتىن بولسۇن، ئۇلاردىن ئارتۇق ئىدى'),
(2325, 19, 75, '(ئى مۇھەممەد!) ئېيتقىنكى، «كىمكى گۇمراھلىقتا بولىدىكەن، مەرھەمەتلىك اﷲ ئۇنىڭغا مۆھلەت بىرىدۇ، تاكى ئۇلار ئازاب ۋە قىيامەتتىن ئىبارەت اﷲ ئۇلارغا ۋەدە قىلغان نەرسىلەرنى كۆرگەندە ئاندىن كىمنىڭ ئورنى يامان ۋە كىمنىڭ لەشكىرى ئاجىز ئىكەنلىكىنى بىلىدۇ»'),
(2326, 19, 76, 'اﷲ ھىدايەت تاپقۇچىلارنىڭ ھىدايىتىنى زىيادە قىلىدۇ، باقى قالىدىغان ياخشى ئەمەللەر ساۋاب جەھەتتىن ۋە ئاقىۋەت جەھەتتىن پەرۋەردىگارىڭنىڭ دەرگاھىدا ئارتۇقتۇر'),
(2327, 19, 77, 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ماڭا (ئاخىرەتتە) ئەلۋەتتە مال - مۈلۈك، ئەۋلاد بېرىلىدۇ دېگەن ئادەمنى كۆردۈڭمۇ؟'),
(2328, 19, 78, 'ئۇ غەيبنى بىلگەنمۇ؟ ياكى مەرھەمەتلىك اﷲ نىڭ ئەھدىگە ئېرىشكەنمۇ؟'),
(2329, 19, 79, 'ھەرگىز ئۇنداق بولغان ئەمەس، ئۇنىڭ دېگەنلىرىنى خاتىرىلەپ قويىمىز، ئۇنىڭغا بېرىدىغان ئازابنى كۈچەيتىمىز'),
(2330, 19, 80, 'ئۇنىڭ دېگەنلىرىنى (يەنى مېلىنى ۋە ئەۋلادىنى) باشقىلارغا مىراس قىلىپ قالدۇرىمىز، ئۇ ئالدىمىزغا تەنھا كېلىدۇ'),
(2331, 19, 81, 'ئۇلار (يەنى مۇشرىكلار) ئىززەتكە ئېرىشىش ئۈچۈن (يەنى بۇتلىرىنىڭ شاپائىتى بىلەن اﷲ نىڭ ئالدىدا ئىززەتلىك بولۇش ئۈچۈن)، اﷲ نى قويۇپ ئىلاھلار (يەنى بۇتلار) غا چوقۇندى'),
(2332, 19, 82, 'ئىش ئۇلارنىڭ گۇمان قىلغىنىدەك ئەمەس (يەنى بۇتلىرى ئۇلارنىڭ اﷲ ئالدىدا ئىززەت تېپىشلىرىنىڭ سەۋەبى بولالمايدۇ)، ئۇلارنىڭ (يەنى مۇشرىكلارنىڭ) چوقۇنۇشىنى ئىنكار قىلىدۇ، ئۇلار (يەنى مۇشرىكلار) نىڭ دۈشمىنىگە ئايلىنىدۇ'),
(2333, 19, 83, 'بىزنىڭ شەيتانلارنى كاپىرلارغا ئەۋەتكەنلىكىمىزنى (يەنى مۇسەللەت قىلغانلىقىمىزنى) بىلمەمسەن؟ شەيتانلار ئۇلارنى (گۇناھلارغا) قىزىقتۇرىدۇ'),
(2334, 19, 84, 'ئۇلارنىڭ ھالاك بولۇشىغا ئالدىراپ كەتمىگىن، (ئۇلارنىڭ كۈنى ئاز قالغان بولۇپ) ئۇنى بىز ساناپ تۇرىمىز'),
(2335, 19, 85, 'شۇ كۈندە (يەنى قىيامەت كۈنىدە) تەقۋادارلارنى مەرھەمەتلىك پەرۋەردىگارنىڭ دەرگاھىغا ئىززەت - ئىكرام بىلەن يىغىمىز'),
(2336, 19, 86, 'گۇناھكارلارنى ئۇلار چاڭقىغان ھالدا جەھەننەمگە ھەيدەيمىز'),
(2337, 19, 87, 'مەرھەمەتلىك اﷲ نىڭ ئەھدىگە ئېرىشكەنلەردىن باشقىلار شاپائەت قىلىشقا قادىر بولالمايدۇ'),
(2338, 19, 88, 'ئۇلار: «اﷲ نىڭ بالىسى بار» دېيىشتى'),
(2339, 19, 89, 'سىلەر شەك - شۈبھىسىز قەبىھ بىر سۆزنى قىلدىڭلار'),
(2340, 19, 90, 'بۇ سۆزنىڭ (يامانلىقىدىن) ئاسمانلار پارچىلىنىپ كەتكىلى، يەر يېرىلىپ كەتكىلى، تاغلار گۇمران بولۇپ كەتكىلى تاس قالدى'),
(2341, 19, 91, 'بۇ شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ نىڭ بالىسى بار دەپ دەۋا قىلدى'),
(2342, 19, 92, 'بالىسى بولۇش اﷲ قا لايىق ئەمەستۇر'),
(2343, 19, 93, 'ئاسمانلاردا ۋە زېمىندا اﷲ قا بەندە بولۇپ بويسۇنمايدىغان ھېچ مەخلۇق يوقتۇر'),
(2344, 19, 94, 'شەك - شۈبھىسىزكى، اﷲ ئۇلارنى تولۇق ئىگەللىدى ۋە سانىنى مۇكەممەل بىلدى'),
(2345, 19, 95, 'قىيامەت كۈنى ئۇلارنىڭ ھەممىسى اﷲ نىڭ دەرگاھىغا تەنھا كېلىدۇ'),
(2346, 19, 96, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ئۈچۈن اﷲ ھەقىقەتەن (ئۇلارنىڭ دىللىرىدا) مۇھەببەت پەيدا قىلىدۇ'),
(2347, 19, 97, '(ئى مۇھەممەد!) تەقۋادارلارغا خۇش - خەۋەر بېرىشىڭ ۋە سەركەش قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، بىز قۇرئاننى سېنىڭ تىلىڭ بىلەن چۈشىنىشلىك قىلىپ نازىل قىلدۇق'),
(2348, 19, 98, 'ئۇلاردىن ئىلگىرى نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلاردىن بىرەر ئەھەدىنى كۆرەلەمسەن؟ ياكى ئۇلاردىن بىرەر پەس ئاۋاز ئاڭلىيالامسەن؟ (يەنى ئۇلار شۇنداق تەلتۆكۈس ھالاك قىلىندىكى، ئۇلاردىن كۆرگىلى ۋە ئاۋازىنى ئاڭلىغىلى ھېچ ئەھەدى قالمىدى)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طاھا (يەنى ئى مۇھەممەد!)'),
(2350, 20, 2, 'قۇرئاننى ساڭا سېنى جاپاغا سېلىش ئۈچۈن ئەمەس،'),
(2351, 20, 3, 'پەقەت (اﷲ تىن) قورقىدىغانلارغا ۋەز - نەسىھەت قىلىش ئۈچۈن نازىل قىلدۇق'),
(2352, 20, 4, 'ئۇ زېمىننى ۋە (كەڭ) ئېگىز ئاسمانلارنى ياراتقان زات تەرىپىدىن نازىل قىلىنغان'),
(2353, 20, 5, 'مەرھەمەتلىك اﷲ ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى'),
(2354, 20, 6, 'ئاسمانلاردىكى، زېمىندىكى، ئۇلارنىڭ ئارىسىدىكى ۋە يەر ئاستىدىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدۇر)'),
(2355, 20, 7, 'ئەگەر سەن ئاشكارا سۆزلىسەڭ (ياكى يوشۇرۇن سۆزلىسەڭ، اﷲ نىڭ نەزىرىدە ئوخشاشتۇر)، چۈنكى ئۇ سىرنى ۋە ئۇنىڭدىنمۇ مەخپىيرەك ئىشلارنى بىلىپ تۇرىدۇ'),
(2356, 20, 8, 'اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، اﷲ نىڭ گۈزەل ئىسىملىرى بار'),
(2357, 20, 9, 'مۇسانىڭ قىسسىسىدىن خەۋىرىڭ بارمۇ؟'),
(2358, 20, 10, 'ئۆز ۋاقتىدا مۇسا (بىر جايدا) ئوتنىڭ يورۇقىنى كۆردى. ئۇ ئائىلىسىدىكىلەرگە: «تۇرۇپ تۇرۇڭلار، مەن ئوتنىڭ يورۇقىنى كۆردۈم، مەن بېرىپ (ئۇنىڭدىن) بىرەر پارچە چوغ ئېلىپ كېلىشىم ياكى ئوت بار يەردە بىرەر يول باشلىغۇچىنى ئۇچرىتىشىم مۇمكىن» دېدى'),
(2359, 20, 11, 'مۇسا ئوتنىڭ يېنىغا كەلگەندە نىدا قىلىندىكى، ئى مۇسا!'),
(2360, 20, 12, 'مەن ھەقىقەتەن سېنىڭ پەرۋەردىگارىڭدۇرمەن، كەشىڭنى سالغىن، سەن ھەقىقەتەن مۇقەددەس ۋادى بولغان تۇۋادا سەن'),
(2361, 20, 13, 'مەن سېنى (پەيغەمبەرلىككە) تاللىدىم، سەن ۋەھيىگە قۇلاق سالغىن'),
(2362, 20, 14, 'مەن ھەقىقەتەن (ئىبادەتكە لايىق) اﷲ دۇرمەن، مەندىن باشقا ھېچ مەبۇد (بەرھەق) يوق، (يالغۇز) ماڭا ئىبادەت قىلغىن، مېنى زىكىر قىلىش ئۈچۈن ناماز ئوقۇغىن'),
(2363, 20, 15, 'قىيامەتنىڭ بولۇشى ھەقىقەتتۇر، ھەر ئادەم ئۆزىنىڭ ئەمەلىگە يارىشا مۇكاپاتلىنىشى ئۈچۈن، ئۇنى (يەنى ئۇنىڭ قاچان بولۇشىنى) مەخپىي تۇتتۇم'),
(2364, 20, 16, 'قىيامەتكە ئىشەنمەيدىغان ۋە نەپسى خاھىشلىرىغا ئەگەشكەن ئادەم سېنى ئۇنىڭدىن (يەنى قىيامەتكە تەييارلىق قىلىشتىن ۋە ئۇنى تەستىق قىلىشتىن) توسمىسۇن، ئۇنداقتا ئۆزۈڭ ھالاك بولىسەن (چۈنكى ئاخىرەتتىن غاپىل بولۇش، ھالاكەتكە ئېلىپ بارىدۇ'),
(2365, 20, 17, 'ئى مۇسا! ئوڭ قولۇڭدىكى نېمە ئۇ؟»'),
(2366, 20, 18, 'مۇسا ئېيتتى: «ئۇ مېنىڭ ھاسام، ئۇنىڭغا تايىنىمەن، ئۇنىڭ بىلەن قويلىرىمغا غازاڭ قېقىپ بېرىمەن، مېنىڭ يەنە باشقا ئېھتىياجلىرىممۇ بار»'),
(2367, 20, 19, 'اﷲ: «ئى مۇسا! ئۇنى (يەرگە) تاشلىغىن» دېدى'),
(2368, 20, 20, 'مۇسا ئۇنى تاشلىۋىدى، ئۇ ناگاھان مېڭىۋاتقان بىر يىلانغا ئايلاندى'),
(2369, 20, 21, 'اﷲ ئېيتتى: «ئۇنى تۇتۇۋالغىن، قورقمىغىن، ئۇنى دەسلەپكى (يەنى ھاسىلىق) ھالىتىگە قايتۇرىمىز'),
(2370, 20, 22, 'قولۇڭنى قولتۇقۇڭنىڭ ئاستىغا سالغىن (ئاندىن چىقارغىن)، ئۇ ھېچقانداق ئەيىب - نۇقسانسىز ھالدا ئاپئاق (نۇرلۇق بولۇپ چىقىدۇ، (بۇ) ئىككىنچى مۆجىزىدۇر'),
(2371, 20, 23, 'ساڭا چوڭ مۆجىزىلىرىمىزدىن بەزىسىنى كۆرسىتىمىز'),
(2372, 20, 24, 'پىرئەۋنگە بارغىن، ئۇ ھەقىقەتەن ھەددىدىن ئاشتى»'),
(2373, 20, 25, 'مۇسا ئېيتتى: «پەرۋەردىگارىم! مېنىڭ قەلبىمنى كەڭ قىلغىن، (يەنى ئىمان بىلەن ۋە پەيغەمبەرلىك بىلەن نۇرلاندۇرغىن)'),
(2374, 20, 26, 'مېنىڭ ئىشىمنى ئاسانلاشتۇرغىن'),
(2375, 20, 27, 'تىلىمدىن كېكەچلىكنى كۆتۈرۈۋەتكىن'),
(2376, 20, 28, 'ئۇلار مېنىڭ سۆزۇمنى چۈشەنسۇن'),
(2377, 20, 29, 'ماڭا ئائىلەمدىن قېرىندىشىم ھارۇننى ياردەمچى قىلىپ بەرگىن'),
(2378, 20, 30, 'ماڭا ئائىلەمدىن قېرىندىشىم ھارۇننى ياردەمچى قىلىپ بەرگىن'),
(2379, 20, 31, 'ئۇنىڭ بىلەن مېنى تېخىمۇ كۈچەيتكىن'),
(2380, 20, 32, 'مېنىڭ ئىشىمغا ئۇنى شېرىك قىلغىن'),
(2381, 20, 33, 'بۇنىڭ بىلەن بىز ساڭا كۆپ تەسبىھ ئېيتقايمىز'),
(2382, 20, 34, 'ساڭا كۆپ زىكرى ئېيتقايمىز'),
(2383, 20, 35, 'سەن ھەقىقەتەن بىزنى كۆرۈپ تۇرغۇچىسەن»'),
(2384, 20, 36, 'اﷲ ئېيتتى: «ئى مۇسا! سېنىڭ سورىغان نەرسىلىرىڭ ساڭا بېرىلدى.'),
(2385, 20, 37, '(بۇنىڭدىن باشقا) ساڭا يەنە بىر قېتىم ئىنئام قىلغان ئىدۇق'),
(2386, 20, 38, 'ئۆز ۋاقتىدا ئاناڭنىڭ كۆڭلىگە پەقەت ۋەھيى ئارقىلىق بىلىنىدىغان نەرسىلەرنى سالدۇق»'),
(2387, 20, 39, '(ئېيتتۇقكى) «ئۇنى (يەنى بالىنى) ساندۇققا سالغىن، ئاندىن ئۇنى (ساندۇق بىلەن) دەرياغا تاشلىغىن، دەريا ئۇنى قىرغاققا تاشلىسۇن، ئۇنى مېنىڭ دۈشمىنىم ۋە ئۇنىڭ دۈشمىنى بولغان (پىرئەۋن) ئېلىپ (باقسۇن)». مېنىڭ قوغدىشىم ئاستىدا تەربىيىلىنىشىڭ ئۈچۈن، ساڭا مەن تەرەپتىن بولغان مۇھەببەتنى سالدىم'),
(2388, 20, 40, 'ئۆز ۋاقتىدا سېنىڭ ھەمشىرەڭ (پىر ئەۋىننىڭ ئائىلىسىگە) بېرىپ: «سىلەرگە ئۇنى باقىدىغان بىر كىشىنى كۆرسىتىپ قويايمۇ؟» دېدى. بىز سېنىڭ ئاناڭنى خۇش بولسۇن، قايغۇرمىسۇن دەپ سېنى ئۇنىڭغا قايتۇرۇپ بەردۇق، سەن بىر ئادەمنى ئۆلتۈرۈپ قويدۇڭ، سېنى بىز غەمدىن خالاس قىلدۇق، سېنى تۈرلۈك بالالار بىلەن سىنىدۇق، سەن مەديەنلىكلەر ئارىسىدا كۆپ يىللار تۇردۇڭ، ئاندىن، ئى مۇسا! تەقدىر بىلەن (بۇ يەرگە) كەلدىڭ'),
(2389, 20, 41, 'سېنى مەن مېنىڭ مۇھەببىتىمگە (يەنى پەيغەمبەر قىلىپ ۋەھيى نازىل قىلىشىمغا) تاللىدىم'),
(2390, 20, 42, '(ئى مۇسا!) سەن ۋە سېنىڭ قېرىندىشىڭ (ھارۇن) مېنىڭ مۆجىزىلىرىمنى ئېلىپ بېرىڭلار، مېنىڭ زىكرىمگە سۇسلۇق قىلىپ قالماڭلار'),
(2391, 20, 43, 'پىرئەۋننىڭ يېنىغا بېرىڭلار، ئۇ ھەقىقەتەن ھەددىدىن ئاشتى'),
(2392, 20, 44, 'ئۇنىڭغا يۇمشاق سۆز قىلىڭلار، ئۇ ۋەز - نەسىھەتنى قوبۇل قىلىشى، ياكى (ھەددىدىن ئاشقانلىقنىڭ ئاقىۋىتىدىن) قورقۇشى مۇمكىن'),
(2393, 20, 45, 'ئۇلار: «پەرۋەردىگارىمىز! بىز ھەقىقەتەن ئۇنىڭ بىزنى ئالدىراپ جازالىشىدىن ياكى تېخىمۇ ھەددىدىن ئېشىشىدىن قورقىمىز» دېيىشتى'),
(2394, 20, 46, 'اﷲ ئېيتتى: «سىلەر قورقماڭلار، مەن ھەقىقەتەن سىلەر بىلەن بىللە (ئۇنىڭ سىلەرگە بەرگەن جاۋابىنى) ئاڭلاپ تۇرىمەن، (ئۇنىڭ سىلەرگە قىلىدىغانلىرىنى) كۆرۈپ تۇرىمەن'),
(2395, 20, 47, 'سىلەر پىرئەۋننىڭ يېنىغا بېرىپ، بىز ئىككىمىز پەرۋەردىگارىڭنىڭ ئەلچىسى بولىمىز، ئىسرائىل ئەۋلادىنى قويۇۋەتكىن، بىز بىلەن كەتسۇن، ئۇلارنى قىينىمىغىن. ساڭا بىز ھەقىقەتەن پەرۋەردىگارىڭنىڭ مۆجىزىسىنى ئېلىپ كەلدۇق، توغرا يولغا ئەگەشكەن ئادەم (اﷲ نىڭ ئازابىدىن) ئامان قالىدۇ'),
(2396, 20, 48, 'بىزگە شەك - شۈبھىسىز ۋەھيى قىلىندىكى، (اﷲ نىڭ پەيغەمبەرلىرىنى) ئىنكار قىلغان، (ئىماندىن) يۈز ئۆرىگەن ئادەم (قاتتىق) ئازابقا دۇچار بولىدۇ دەڭلار»'),
(2397, 20, 49, '(ئۇلار پىرئەۋنگە كېلىپ اﷲ تائالا تەرىپىدىن ۋەھيى قىلىنغان نەرسىلەرنى يەتكۈزگەندىن كېيىن) پىرئەۋن: «ئى مۇسا، سىلەرنىڭ پەرۋەردىگارىڭلار كىم؟» دېدى'),
(2398, 20, 50, 'مۇسا ئېيتتى: «پەرۋەردىگارىمىز شۇنداق زاتتۇركى، ھەممە نەرسىگە (ئۆزىگە مۇناسىپ) شەكىل ئاتا قىلدى، (ئاندىن ئۇلارغا ياشاش يوللىرىنى، پايدىلىنىدىغان نەرسىلەرنى) كۆرسەتتى»'),
(2399, 20, 51, 'پىرئەۋن ئېيتتى: «ئۆتۈپ كەتكەن ئۈممەتلەرنى ھالى قانداق؟ (يەنى قانداق بولغان؟)»'),
(2400, 20, 52, 'مۇسا ئېيتتى: «ئۇلار توغرىسىدىكى مەلۇمات پەرۋەردىگارىمنىڭ دەرگاھىدا لەۋھۇلمەھپۇزدا (خاتىرىلەكلىكتۇر)، پەرۋەردىگارىم ھەم خاتالاشمايدۇ ھەم ئۇنتۇمايدۇ'),
(2401, 20, 53, 'ئۇ زېمىننى سىلەرگە بىسات قىلىپ بەردى، سىلەر ئۈچۈن نۇرغۇن يوللارنى ئېچىپ بەردى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، ئۇنىڭ بىلەن نۇرغۇن ئۆسۈملۈكلەرنى ئۆستۈرۈپ بەردى»'),
(2402, 20, 54, '(ئۇلاردىن) يەڭلار ۋە مال چارۋىلىرىڭلارنى بېقىڭلار، ئۇنىڭدا ئەقىل ئىگىلىرى ئۈچۈن (اﷲ نىڭ بارلىقى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار'),
(2403, 20, 55, 'سىلەرنى زېمىندىن خەلق ئەتتۇق، (ئۆلگۈنۈڭلاردىن كېيىن) سىلەرنى يەنە زېمىنغا قايتۇرىمىز (توپىغا ئايلىنىسىلەر)، سىلەرنى (ھېساب ئېلىش ئۈچۈن) يەنە بىر قېتىم زېمىندىن چىقىرىمىز'),
(2404, 20, 56, 'شەك - شۈبھىسىزكى، ئۇنىڭغا بىزنىڭ ھەممە مۆجىزىلىرىمىزنى كۆرسەتتۇق. ئۇ ئىنكار قىلدى ۋە (ئىمان ئېيتىشتىن) باش تارتتى'),
(2405, 20, 57, 'پىرئەۋن ئېيتتى: «ئى مۇسا! سېھرىڭ بىلەن بىزنى زېمىنىمىز (مىسىر) دىن چىقىرىۋېتىش ئۈچۈن كەلدىڭمۇ؟'),
(2406, 20, 58, 'ساڭا بىز شۇنىڭغا ئوخشاش سېھىر بىلەن تاقابىل تۇرىمىز، سەن بىزگە ئۆز ئارا يىغىلىدىغان ۋاقىتنى مۇئەييەن جاينى بەلگىلەپ بەرگىن، سەنمۇ، بىزمۇ ئۇنىڭغا خىلاپلىق قىلمايلى»'),
(2407, 20, 59, 'مۇسا ئېيتتى: «سىلەرگە ۋەدە قىلىنغان چاغ بايرام كۈنى بولۇپ، كىشىلەر چاشكا ۋاقتىدا يىغىلسۇن»'),
(2408, 20, 60, 'پىرئەۋن قايتىپ كېتىپ ھىيلە - مىكرنى (يەنى سېھىرگەرلەرنى) توپلىدى. ئاندىن ئۇ (سېھىرگەرلەرنى ۋە سېھرىدە ئىشلىنىدىغان نەرسىلەرنى ئېلىپ ۋەدىلەشكەن ۋاقىتتا) كەلدى'),
(2409, 20, 61, 'مۇسا ئۇلار غا ئېيتتى: «سىلەرگە ۋاي! اﷲ قا يالغاننى چاپلىماڭلار (يەنى سېھرىڭلار ئارقىلىق اﷲ نىڭ مۆجىزىلىرىگە تاقابىل تۇرۇشنى دەۋا قىلماڭلار)، اﷲ سىلەرنى قاتتىق ئازاب بىلەن ھالاك قىلىدۇ، اﷲ قا بوھتان چاپلىغان ئادەم مەغلۇب بولىدۇ»'),
(2410, 20, 62, 'ئۇلار ئۆز ئىشى (يەنى مۇسا ۋە ھارۇننىڭ ئىشى) ئۈستىدە دەتالاش قىلىشتى، قىلغان دەتالاشنى يوشۇرۇشتى'),
(2411, 20, 63, 'ئۇلار ئېيتتى: «بۇ ئىككىسى راستلا سېھرىگەر بولۇپ، سېھىر بىلەن سىلەرنى زېمىنىڭلاردىن چىقىرىۋەتمەكچى ۋە توغرا دىنىڭلارنى يوقاتماقچى بولىدۇ'),
(2412, 20, 64, 'ھىيلەڭلارنىڭ ھەممىسىنى جەملەڭلار (يەنى پۈتۈن چارە - تەدبىرلىرىڭلارنى بىر نۇقتىغا يىغىپ بىر ياقىدىن باش چىقىرىڭلار)، ئاندىن (قارىغۇچىلارغا ھەيۋەتلىك كۆرۈنۈش ئۈچۈن مەيدانغا) سەپ تارتىپ كېلىڭلار، بۈگۈن ئۈستۈنلۈك قازانغان ئادەم راستلا مەقسىتىگە ئېرىشىدۇ»'),
(2413, 20, 65, 'ئۇلار ئېيتتى: «ئى مۇسا! (ھاساڭنى) سەن ئاۋۋال تاشلامسەن؟ ياكى (ئارغامچا ھاسىلىرىمىزنى) بىز ئاۋۋال تاشلامدۇق؟»'),
(2414, 20, 66, 'مۇسا: «بەلكى سىلەر ئاۋۋال تاشلاڭلار!» دېدى. (ئۇلار تاشلىغان ئىدى) ناگاھان ئۇلارنىڭ ئاغامچىلىرى، ھاسىلىرى ئۇلارنىڭ سېھىرىدىن (يەنى سېھىرنىڭ تەسىرىدىن) ئۇنىڭغا ھەرىكەتلىنىپ مېڭىۋاتقاندەك تۇيۇلدى'),
(2415, 20, 67, '(بۇنىڭدىن) مۇسا ئۆزىدە قورقۇنچ ھېس قىلدى'),
(2416, 20, 68, 'بىز (ئۇنىڭغا) ئېيتتۇقكى، «قورقمىغىن سەن چوقۇم ئۈستۈنلۈك قازىنىسەن،'),
(2417, 20, 69, 'قولۇڭدىكىنى تاشلىغىن، ئۇ ئۇلارنىڭ كۆرسەتكەن سېھىرلىرىنى دەم تارتىپ يۇتۇۋېتىدۇ، ئۇلارنىڭ كۆرسەتكەنلىكىنى سېھىرگەرلەرنىڭ ھىيلىسىدۇر، سېھىرگەرلەر قەيەرگە بارسا مۇۋەپپەقىيەت قازىنالمايدۇ»'),
(2418, 20, 70, '(مۇسا ھاسىسىنى تاشلىۋىدى، ئۇلارنىڭ كۆرسەتكەن سېھىرلىرىنى دەم تارتىپ يۇتۇۋەتتى) شۇنىڭ بىلەن سېھىرگەرلەر (سەجدە قىلغان ھالدا يىقىلىپ): «ھارۇن بىلەن مۇسانىڭ پەرۋەردىگارىغا ئىمان ئېيتتۇق» دېيىشتى'),
(2419, 20, 71, 'پىرئەۋن ئېيتتى: «سىلەر مەن رۇخسەت بېرىشتىن بۇرۇن ئۇنىڭغا ئىمان ئېيتتىڭلار، شۈبھىسىزكى، ئۇ چوقۇم سىلەرگە سېھىرنى ئۆگەتكەن چوڭۇڭلار ئىكەن، مەن چوقۇم سىلەرنىڭ قوللىرىڭلارنى، پۇتلىرىڭلارنى ئوڭ - چەپ قىلىپ (يەنى ئوڭ قول بىلەن سول پۇتنى، ئوڭ پۇت بىلەن سول قولنى) كېسىپ تاشلايمەن، سىلەرنى چوقۇم خورما دەرەخلىرىگە ئاسىمەن، سىلەر قايسىمىزنىڭ (يەنى مېنىڭ ياكى مۇسانىڭ پەرۋەردىگارىنىڭ) ئازابىنىڭ ئەڭ قاتتىق ۋە ئەڭ ئۇزۇن ئىكەنلىكىنى چوقۇم بىلىسىلەر»'),
(2420, 20, 72, 'ئۇلار ئېيتتى: «سېنى (يەنى ساڭا ئەگىشىشنى) ئىختىيار قىلىپ، بىزگە كەلگەن (مۇسانىڭ راستلىقىنى كۆرسىتىدىغان) روشەن دەلىللەرنى ۋە بىزنى خەلق ئەتكەن اﷲ نى ھەرگىز تاشلىمايمىز. (بىزنىڭ ھەققىمىزدە) نېمىنى ھۆكۈم قىلساڭ، شۇنى ھۆكۈم قىلغىن، سېنىڭ ھۆكمىڭ پەقەت بۇ دۇنيادىلا ئۆتىدۇ'),
(2421, 20, 73, 'بىزنىڭ خاتالىقىمىزنى، سېنىڭ مەجبۇرلىشىڭ ئارقىسىدا بىزنىڭ سېھىر كۆرسەتكەنلىكىمىزدىن ئىبارەت گۇناھىمىزنى مەغپىرەت قىلسۇن دەپ بىز ھەقىقەتەن پەرۋەردىگارىمىزغا ئىمان ئېيتتۇق، اﷲ نىڭ (ساۋابىنى سېنىڭ مۇكاپاتىڭدىن) ئارتۇقتۇر؛ اﷲ نىڭ (ئازابى سېنىڭ جازايىڭدىن) دائىمىيدۇر»'),
(2422, 20, 74, 'كىمكى گۇناھكار ھالدا اﷲ قا مۇلاقات بولىدىكەن، ئۇ دوزاخقا كىرىدۇ، دوزاختا ئۇ ئۆلمەيدۇ ۋە (ئوبدان) ياشىمايدۇ'),
(2423, 20, 75, 'كىمكى مۆمىن بولۇپ، ياخشى ئەمەللەرنى قىلغان ھالدا اﷲ قا مۇلاقات بولىدىكەن، ئۇ (اﷲ نىڭ دەرگاھىدا) يۇقىرى دەرىجىگە ئېرىشىدۇ'),
(2424, 20, 76, 'ئۇ (دەرىجىلەر) دائىمىي تۇرالغۇ بولغان جەننەتلەر بولۇپ، ئۇلارنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدۇ، ئۇ يەرلەردە ئۇ مەڭگۈ قالىدۇ، ئەنە شۇ (كۇفرىدىن ۋە گۇناھلاردىن) پاك بولغان ئادەمگە بېرىلىدىغان مۇكاپاتتۇر'),
(2425, 20, 77, 'بىز شەك - شۈبھىسىز مۇساغا ۋەھيى قىلدۇقكى، «سەن مېنىڭ بەندىلىرىمنى كېچىدە (مىسىردىن) ئېلىپ چىقىپ كەت، سەن (ھاساڭنى) دېڭىزغا ئۇرۇپ ئۇلارغا بىر قۇرۇق يول ئېچىپ بەرگىن، (پىرئەۋننىڭ ئارقاڭدىن قوغلاپ) يېتىۋېلىشىدىن قورقمىغىن، (دېڭىزدا غەرق بولۇپ كېتىشتىنمۇ) قورقمىغىن»'),
(2426, 20, 78, 'پىرئەۋن لەشكەرلىرى بىلەن قوغلىدى، ئۇلارنى (يەنى پىرئەۋن بىلەن ئۇنىڭ لەشكەرلىرىنى) دېڭىزدا دەھشەتلىك ھالەت ئورىۋالدى (يەنى ئۇلار دېڭىزدا غەرق بولدى)'),
(2427, 20, 79, 'پىر ئەۋن ئۆز قەۋمىنى ئازدۇردى، (ئۇلارنى) توغرا يولغا باشلىمىدى'),
(2428, 20, 80, 'ئى ئىسرائىل ئەۋلادى! سىلەرنى ھەقىقەتەن دۈشمىنىڭلاردىن قۇتقۇزدۇق، (مۇناجات ئۈچۈن ۋە مۇساغا تەۋراتنى نازىل قىلىش ئۈچۈن) سىلەرگە (تۇرسىنا) نىڭ ئوڭ تەرىپىنى ۋەدە قىلدۇق، سىلەرگە تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق'),
(2429, 20, 81, '(ئېيتتۇقكى) «بىز رىزىق قىلىپ بەرگەن پاك - ھالال نەرسىلەردىن يەڭلار، بۇ ھەقتە چەكتىن ئاشماڭلار، (ئۇنداق قىلساڭلار) سىلەرگە غەزىپىم نازىل بولىدۇ، كىمگە مېنىڭ غەزىپىم نازىل بولىدىكەن، ئۇ چوقۇم ھالاك بولىدۇ'),
(2430, 20, 82, 'تەۋبە قىلغان، ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، ئاندىن توغرا يولدا ماڭغان ئادەمنى مەن ئەلۋەتتە ناھايىتى مەغپىرەت قىلغۇچىمەن»'),
(2431, 20, 83, '«ئى مۇسا! ئالدىراپ (تۇرسىناغا) قەۋمىڭدىن ئىلگىرى كېلىشىڭنىڭ سەۋەبى نېمە؟» دېدۇق'),
(2432, 20, 84, 'مۇسا ئېيتتى: «ئۇلار ئىزىمنى قوغلىشىپ كېلىۋاتىدۇ، پەرۋەردىگارىم! سېنى رازى بولسۇن دەپ بۇ يەرگە ئالدىراپ كەلدىم»'),
(2433, 20, 85, 'اﷲ ئېيتتى: «سەندىن (يەنى سەن كەتكەندىن) كېيىن قەۋمىڭنى ھەقىقەتەن سىنىدۇق، ئۇلارنى سامىرى ئازدۇردى»'),
(2434, 20, 86, 'مۇسا قەۋمىگە غەزەپلەنگەن ھالدا خاپا قايتتى، ئۇ ئېيتتى: «سىلەرگە پەرۋەردىگارىڭلار چىرايلىق ۋەدە قىلمىغانمىدى؟ (سىلەر ئەھدىنى ئۇنتۇغۇدەك) ۋاقىت شۇنچە ئۇزۇن بولۇپ كەتتىمۇ؟ ياكى سىلەر ئۆزۈڭلار پەرۋەردىگارىڭلارنىڭ غەزىپى چۈشۈشىنى ئىرادە قىلدىڭلارمۇ؟ شۇنىڭ ئۈچۈن سىلەرنىڭ ماڭا بەرگەن ۋەدەڭلارغا خىلاپلىق قىلدىڭلارمۇ؟»'),
(2435, 20, 87, 'ئۇلار ئېيتتى: «ساڭا بەرگەن ۋەدىگە ئۆزلۈكىمىزدىن خىلاپلىق قىلغىنىمىز يوق، لېكىن بىز (پىرئەۋن) قەۋمىنىڭ زىننەتلىرىدىن نۇرغۇن ئېلىپ چىققان ئىدۇق، (سامىرىنىڭ بۇيرۇقى بويىچە) ئۇلارنى (ئوتقا) تاشلىدۇق، سامىرىمۇ (ئۆزى ئېلىپ چىققان زىبۇزىننەتلىرىنى) تاشلىدى»'),
(2436, 20, 88, 'سامىرى (ئېرىتىلگەن زىبۇزىننەتلەردىن) ئۇلار ئۈچۈن بىر موزاي ھەيكەل ياسىدى، ئۇنىڭدىن موزاينىڭ ئاۋازىغا ئوخشاش ئاۋاز چىقاتتى، ئۇلار (يەنى سامىرى ۋە ئۇنىڭ تەۋەلىرى) ئېيتتى: «بۇ سىلەرنىڭ ئىلاھىڭلاردۇر ۋە مۇسانىڭ ئىلاھىدۇر، مۇسا (ئىلاھنى بۇ يەردە) ئۇنتۇپ قېلىپ (ئۇنى ئىزدەپ تۇرسىناغا كەتتى)»'),
(2437, 20, 89, 'ئۇ موزاينىڭ ئۇلارنىڭ سۆزىگە جاۋاب بېرەلمەيدىغانلىقىنى، ئۇلارغا پايدا - زىيان يەتكۈزەلمەيدىغانلىقىنى ئۇلار ئۇقمامدۇ؟'),
(2438, 20, 90, 'شەك - شۈبھىسىزكى، ھارۇن ئۇلارغا (مۇسا قايتىپ كېلىشتىن) بۇرۇن: «ئى قەۋمىم! سىلەر موزاي بىلەن قايمۇقتۇرۇلدۇڭلار، سىلەرنىڭ پەرۋەردىگارىڭلار مەرھەمەتلىك اﷲ دۇر، ماڭا بوي سۇنۇڭلار، (موزايغا چوقۇنۇشنى تەرك ئېتىش بىلەن) بۇيرىقىمغا ئىتائەت قىلىڭلار!» دېدى'),
(2439, 20, 91, 'ئۇلار: «مۇسا قايتىپ كەلگەنگە قەدەر ئۇنىڭغا چوقۇنىۋېرىمىز» دېيىشتى'),
(2440, 20, 92, 'مۇسا ئېيتتى: «ئى ھارۇن! ئۇلارنىڭ ئازغانلىقىنى كۆرگەن چېغىڭدا (خۇدالىق ئۈچۈن غەزەپلىنىشتە) ماڭا ئەگىشىشتىن نېمە توسالغۇ بولدى؟ مېنىڭ بۇيرىقىمغا خىلاپلىق قىلدىڭمۇ؟»'),
(2441, 20, 93, 'مۇسا ئېيتتى: «ئى ھارۇن! ئۇلارنىڭ ئازغانلىقىنى كۆرگەن چېغىڭدا (خۇدالىق ئۈچۈن غەزەپلىنىشتە) ماڭا ئەگىشىشتىن نېمە توسالغۇ بولدى؟ مېنىڭ بۇيرىقىمغا خىلاپلىق قىلدىڭمۇ؟»'),
(2442, 20, 94, 'ھارۇن ئېيتتى: «ئى قېرىندىشىم! مېنىڭ ساقىلىمنى ۋە چېچىمنى تارتمىغىن، مەن ھەقىقەتەن سېنىڭ، ئىسرائىل ئەۋلادىنى بۆلۈۋېتىپسەن، سۆزۈمگە دىققەت قىلماپسەن، دېيىشىڭدىن قورقتۇم»'),
(2443, 20, 95, 'مۇسا ئېيتتى: «ئى سامىرى! سەن نېمە بولدۇڭ؟ (يەنى مۇنداق يامان ئىشنى قىلىشىڭدىن غەرىزىڭ نېمە؟)»'),
(2444, 20, 96, 'سامىرى ئېيتتى: «مەن ئۇلار كۆرمىگەننى كۆردۈم، ئەلچى (يەنى جىبرىئىل) نىڭ ئىزىدىن بىر چاڭگال توپىنى ئالدىم - دە، ئۇنى (موزايغا) چاچتىم، شۇنىڭ بىلەن ئۇنىڭدىن موزاينىڭ ئاۋازىغا ئوخشاش ئاۋاز چىقتى)، شۇنىڭدەك (بۇنى) ماڭا نەپسىم چىرايلىق كۆرسەتتى»'),
(2445, 20, 97, 'مۇسا (سامىرىغا) ئېيتتى: «سەن بارغىن! سەن ئۆمۈرۋايەت (كۆرگەنلا كىشىگە) ماڭا يېقىنلاشماڭلار! دېگەيسەن (يەنى سېنىڭ كىشىگە يېقىنلاشماسلىقىڭ، كىشىنىڭ ساڭا يېقىنلاشماسلىقى بۇ دۇنيادا ساڭا بېرىلگەن جازادۇر)، شۈبھىسىزكى (ئاخىرەتتە جازالىنىدىغان) مۇئەييەن بىر ۋاقتىڭ بار، اﷲ ساڭا قىلغان بۇ ۋەدىسىگە خىلاپلىق قىلمايدۇ، سەن ھامان چوقۇنۇپ كېلىۋاتقان ئىلاھىڭغا قارىغىن، بىز ئۇنى چوقۇم (ئوتتا) كۆيدۈرىمىز، ئاندىن ئۇنىڭ (كۈلىنى) دېڭىزغا چېچىۋېتىمىز»'),
(2446, 20, 98, 'سىلەرنىڭ ئىلاھىڭلار بىر اﷲ دۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ ھەر نەرسىنى تولۇق بىلگۈچىدۇر'),
(2447, 20, 99, '(ئى مۇھەممەد!) شۇنىڭدەك، ساڭا ئۆتكەنكىلەرنىڭ بەزى خەۋەرلىرىنى بايان قىلىمىز، ساڭا ئۆز تەرىپىمىزدىن قۇرئاننى ئاتا قىلدۇق'),
(2448, 20, 100, 'كىمكى قۇرئاندىن يۈز ئۆرۈيدىكەن، ئۇنىڭغا قىيامەت كۈنى چوڭ گۇناھتىن ئېغىر يۈك يۈكلىنىدۇ'),
(2449, 20, 101, 'ئۇ گۇناھى ئۈچۈن بېرىلگەن ئازابنى مەڭگۈ تارتىدۇ) قىيامەت كۈنى ئۇلارغا يۈكلەنگەن بۇ يۈك نېمىدېگەن يامان!'),
(2450, 20, 102, 'بۇ سۇر چىلىنىدىغان كۈندۇر، بۇ كۈندە (شەكلى ئۆزگەرگەن) كۆك كۆزلۈك (قارا يۈزلۈك) گۇناھكارلارنى يىغىمىز'),
(2451, 20, 103, '(قىيامەتنىڭ دەھشىتىنى كۆرۈپ دۇنيادا تۇرغان ۋاقتى قىسقا بىلىنگەنلىكتىن) (ئۇلار) «(دۇنيادا) پەقەت ئون كۈنلا تۇردۇڭلار» دېيىشىپ پىچىرلىشىدۇ'),
(2452, 20, 104, 'ئۇلارنىڭ دەيدىغانلىرىنى بىز ئوبدان بىلىمىز، ئەينى زاماندا ئۇلارنىڭ ئەڭ توغرا سۆزلەيدىغىنى: «سىلەر پەقەت بىر كۈن تۇردۇڭلار» دەيدۇ'),
(2453, 20, 105, 'ئۇلار سەندىن تاغلار توغرۇلۇق (يەنى تاغنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقى توغرۇلۇق) سورايدۇ، ئېيتقىنكى، «پەرۋەردىگارىم ئۇلارنى كۈكۈم تالقان قىلىپ (ئاندىن شامالنى ئەۋەتىپ ئۇنى سورۇيدۇ)'),
(2454, 20, 106, 'ئۇلارنى تۈزلەڭلىككە ئايلاندۇرىدۇ'),
(2455, 20, 107, 'ئۇ يەرلەردە نە چۇڭقۇرلۇقنى، نە ئېگىزلىكنى كۆرمەيسەن»'),
(2456, 20, 108, 'بۇ كۈندە (كىشىلەر اﷲ نىڭ مەھشەرگاھقا) دەۋەت قىلغۇچىسىغا ئەگىشىدۇ، قىڭغىر كەتمەيدۇ، مەرھەمەتلىك اﷲ نىڭ ئۇلۇغلۇقى ۋە ھەيۋىتىدىن قورقۇپ، بارلىق ئاۋازلار بېسىقىدۇ، پىچىرلاشقاندىن باشقىنى ئاڭلىمايسەن'),
(2457, 20, 109, 'بۇ كۈندە مەرھەمەتلىك اﷲ ئىزىن بەرگەن ۋە سۆزىدىن اﷲ رازى بولغان ئادەمنىڭ شاپائىتىدىن باشقا ھېچقانداق شاپائەت پايدا بەرمەيدۇ'),
(2458, 20, 110, 'اﷲ ئۇلارنىڭ ئالدىدىكىنى ۋە كەينىدىكىنى بىلىدۇ، ئۇلار بولسا ئۇنى بىلمەيدۇ'),
(2459, 20, 111, 'گۇناھكارلار مەخلۇقاتنى ئىدارە قىلىپ تۇرغۇچى (مەڭگۈ) ھايات اﷲ قا ئېگىلىدۇ، اﷲ قا شېرىك كەلتۈرگەنلەر شەكسىز زىيان تارتىدۇ'),
(2460, 20, 112, 'مۆمىن تۇرۇپ ياخشى ئەمەللەرنى قىلغانلار يامانلىقلىرىنىڭ ئېشىپ كېتىشىدىن ۋە ياخشىلىقلىرىنىڭ كېمىيىپ كېتىشىدىن قورقمايدۇ'),
(2461, 20, 113, 'ئۇلارنىڭ تەقۋادار بولۇشلىرى ئۈچۈن ياكى ئۇلارنى ۋەز - نەسىھەتكە ئىگە قىلىش ئۈچۈن، ساڭا ئەرەبچە قۇرئاننى نازىل قىلدۇق ۋە ئۇنىڭدا تۈرلۈك ئاگاھلاندۇرۇشلارنى بايان قىلدۇق'),
(2462, 20, 114, 'اﷲ (زاتى، سۈپىتى جەھەتتە پەتەن مەخلۇقاتتىن) ئۈستۈندۇر، ھەق پادىشاھتۇر، (ئى مۇھەممەد!) ساڭا قۇرئاننىڭ ۋەھيىسى تاماملىنىشتىن بۇرۇن ئوقۇشقا ئالدىراپ كەتمىگىن، (يەنى جىبرىئىل ساڭا قۇرئاننى ئوقۇپ بېرىۋاتقاندا، بىللە ئوقۇشقا ئالدىراپ كەتمەي، ئۇ تىلاۋىتىدىن فارىغ بولغۇچە تىڭشاپ تۇرغىن). «ئى پەرۋەردىگارىم! ئىلمىمنى زىيادە قىلغىن!» دېگىن'),
(2463, 20, 115, 'بىز ئىلگىرى ئادەمگە (دەرەخنىڭ مېۋىسىنى يېمەسلىكنى) تەۋسىيە قىلدۇق، ئۇ (بۇ تەۋسىيەنى) يادىدىن چىقاردى، ئۇنىڭدا ھېچقانداق چىڭ ئىرادە كۆرمىدۇق'),
(2464, 20, 116, 'ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە (يەنى ئادەم ئەلەيھىسسالامغا) سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقىسى سەجدە قىلدى، ئۇ (سەجدە قىلىشتىن) باش تارتتى'),
(2465, 20, 117, 'بىز دېدۇق: «ئى ئادەم! بۇ ھەقىقەتەن سېنىڭ دۈشمىنىڭدۇر، خوتۇنۇڭنىڭ دۈشمىنىدۇر ئۇ سىلەر ئىككىڭلارنىڭ جەننەتتىن چىقىپ كېتىشىڭلارغا سەۋەبچى بولۇپ قالمىسۇن، (ئۇ چاغدا) سىلەر جاپا مۇشەققەتتە قالىسىلەر'),
(2466, 20, 118, 'جەننەتتە سەن ھەقىقەتەن ئاچ قالمايسەن ۋە يالىڭاچ قالمايسەن'),
(2467, 20, 119, 'ھەمدە ئۇنىڭدا ھەقىقەتەن ئۇسساپ كەتمەيسەن، ئىسسىپ كەتمەيسەن»'),
(2468, 20, 120, 'شەيتان ئۇنىڭغا ۋەسۋەسە قىلىپ: «ئى ئادەم! ساڭا (مەن يېگەن ئادەم) ئۆلمەيدىغان دەرەخنى ۋە زاۋال تاپماس پادىشاھلىقنى كۆرسىتىپ قويايمۇ؟» دېدى'),
(2469, 20, 121, 'ئۇلار ئۇ دەرەخنىڭ مېۋىسىنى يېدى، شۇنىڭ بىلەن ئۇلارنىڭ ئەۋرەتلىرى ئېچىلىپ قالدى، ئۇلار جەننەت (دەرەخلىرىنىڭ) يوپۇرماقلىرى بىلەن سەترى ئەۋرەت قىلىشقا كىرىشتى، ئادەم پەرۋەردىگارىنىڭ (ئەمرىگە) خىلاپلىق قىلدى، شۇنىڭ بىلەن ئۇ ئازدى'),
(2470, 20, 122, 'ئاندىن پەرۋەردىگارى ئۇنى تاللىدى، ئۇنىڭ تەۋبىسىنى قوبۇل قىلدى ۋە ئۇنى ھىدايەت قىلدى'),
(2471, 20, 123, 'اﷲ (ئادەم بىلەن ھەۋۋاغا) ئېيتتى: «سىلەر ھەممىڭلار جەننەتتىن بەزىڭلار بەزىڭلارغا دۈشمەن بولغان ھالدا چۈشۈڭلار، ئەگەر سىلەرگە مېنىڭ تەرىپىمدىن ھىدايەت كەلسە، كىمكى مېنىڭ ھىدايىتىمگە ئەگەشسە، ئۇ ئازمايدۇ ۋە ئاخىرەتتە شەقىي بولمايدۇ'),
(2472, 20, 124, 'كىمكى مېنىڭ زىكرىمدىن يۈز ئۆرۈيدىكەن، ئۇنىڭ ھاياتى تار (يەنى خاتىرجەمسىز) بولىدۇ، قىيامەت كۈنى ئۇنى بىز كور قوپۇرىمىز»'),
(2473, 20, 125, 'ئۇ: «ئى پەرۋەردىگارىم! نېمىشقا مېنى كور قوپۇردۇڭ، ۋاھالەنكى، (دۇنيادىكى چاغدا) مېنىڭ كۆزۈم كۆرەتتىغۇ؟» دەيدۇ'),
(2474, 20, 126, 'اﷲ ئېيتىدۇ: «شۇنىڭدەك ساڭا (روشەن) ئايەتلىرىمىز كەلدى، سەن ئۇلارنى تەرك ئەتتىڭ، سەن بۈگۈن شۇنىڭغا ئوخشاش تەرك ئېتىلىسەن»'),
(2475, 20, 127, 'ھەددىدىن ئاشقان ۋە پەرۋەردىگارىنى ئايەتلىرىگە ئىمان ئېيتمىغانلارغا شۇنىڭغا ئوخشاش جازا بېرىمىز، ئاخىرەتنىڭ ئازابى تېخىمۇ قاتتىقتۇر، تېخىمۇ باقىيدۇر'),
(2476, 20, 128, 'ئۇلاردىن ئىلگىرى ئۆتكەن (پەيغەمبەرلىرىنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار ئۇقمامدۇ؟ ئۇلار شۇ ئۈممەتلەرنىڭ ماكانلىرىدا مېڭىپ يۈرىدۇ، ئەقىل ئىگىلىرى ئۈچۈن ئۇنىڭدا ھەقىقەتەن نۇرغۇن ئىبرەتلەر بار'),
(2477, 20, 129, 'ئەگەر پەرۋەردىگارىڭنىڭ (ئازابنى ئۇلاردىن كېچىكتۈرۈشكە) قىلىۋەتكەن ھۆكمى ۋە ئالدىنئالا بەلگىلەپ قويغان ۋاقتى بولمىسا ئىدى، ئۇ چاغدا (ئۇلارنىڭ ھالاك بولۇشى) چوقۇم ئىدى'),
(2478, 20, 130, 'ئۇلارنىڭ سۆزلىرىگە سەۋرى قىلغىن، مەمنۇن بولۇشۇڭ ئۈچۈن، كۈن چىقىشتىن ئىلگىرى، كۈن پېتىشتىن بۇرۇن، كېچە ۋاقىتلىرىدا ۋە كۈندۈزنىڭ دەسلەپكى ۋە ئاخىرقى ۋاقىتلىرىدا پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن (يەنى ناماز ئوقۇغىن) ۋە ھەمدە ئېيتقىن'),
(2479, 20, 131, 'بىز (كۇففارلاردىن) تۈرلۈك جامائەنى بەھرىمەن قىلغان دۇنيانىڭ نېمەتلىرى ۋە زىبۇزىننەتلىرىگە كۆز سالمىغىن، بۇنىڭ بىلەن ئۇلارنى سىنايمىز، پەرۋەردىگارىڭنىڭ رىزقى (يەنى ساۋابى بۇ پانىي نېمەتتىن) ياخشىدۇر ۋە ئەڭ باقىيدۇر'),
(2480, 20, 132, '(ئى مۇھەممەد!) ئائىلەڭدىكىلەرنى (ۋە ئۈممىتىڭنى) نامازغا بۇيرۇغىن، ئۆزۈڭمۇ ئۇنى ئادا قىلىشقا چىداملىق بولغىن، سەندىن بىز رىزىق تەلەپ قىلمايمىز. ساڭا بىز رىزىق بېرىمىز، ياخشى ئاقىۋەت پەقەت تەقۋادارلارغا خاستۇر'),
(2481, 20, 133, 'ئۇلار (يەنى مۇشرىكلار): «(مۇھەممەد) پەرۋەردىگارى تەرىپىدىن بىزگە بىرەر مۆجىزە كەلتۈرسىچۇ!» دەيدۇ، ئۇلارغا بۇرۇنقى ئۈممەتلەرنىڭ خەۋەرلىرىنى ئۆز ئىچىگە ئالغان قۇرئان كەلمىدىمۇ؟'),
(2482, 20, 134, 'ئەگەر بىز ئۇلارنى ئۇنىڭدىن (يەنى مۇھەممەد ئەلەيھىسسالامنى ئەۋەتىشتىن) ئىلگىرى ھالاك قىلغان بولساق، ئۇلار چوقۇم: «نېمىشقا بىزگە بىرەر پەيغەمبەر ئەۋەتمىدىڭ؟ بىز ۋە رەسۋا بولۇشتىن ئىلگىرى سېنىڭ ئايەتلىرىڭگە ئەگەشتۇق» دەيتتى'),
(2483, 20, 135, 'ئېيتقىنكى، ھەممە (سىلەرنىڭ ۋە بىزنىڭ ئىشلىرىمىزنى ئاقىۋىتىنى) كۈتمەكتە. سىلەرمۇ كۈتۈڭلار، كىملەرنىڭ توغرا يول ئىگىلىرى ئىكەنلىكىنى ۋە كىملەرنىڭ ھىدايەت تاپقانلىقىنى (ئۇزاققا قالماي) بىلىسىلەر»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'كىشىلەرگە ئۇلارنىڭ (ئەمەللىرىدىن) ھېساب ئېلىنىدىغان ۋاقىت (يەنى قىيامەت) توغرۇلۇق ئويلىنىشتىن) يۈز ئۆرۈمەكتىدۇر'),
(2485, 21, 2, 'ئۇلارغا پەرۋەردىگارى تەرىپىدىن (قۇرئاندا) يېڭى ۋەز - نەسىھەت نازىل بولسىلا، ئۇلار ئۇنىڭغا مەسخىرە قىلغان ھالدا قۇلاق سېلىشىدۇ'),
(2486, 21, 3, 'ئۇلارنىڭ دىللىرى (اﷲ نىڭ كالامىدىن) غەپلەتتىدۇر، كاپىرلار ئۆز ئارا: «بۇ (يەنى مۇھەممەد ئەلەيھىسسالام) پەقەت سىلەرگە ئوخشاش (ئاددىي) بىر ئىنسان، سىلەر سېھىرنى كۆرۈپ تۇرۇپ ئۇنى قوبۇل قىلامسىلەر؟» دەپ يوشۇرۇن سۆزلىشىدۇ'),
(2487, 21, 4, '(پەيغەمبەر) ئېيتتى: «پەرۋەردىگارىم ئاسمان، زېمىندا ئېيتىلغان سۆزلەرنى بىلىپ تۇرىدۇر، اﷲ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر»'),
(2488, 21, 5, '(كاپىرلار قۇرئان سېھرىدۇر دەپلا قالماستىن) بەلكى ئۇ شائىردۇر، ئىلگىرىكى پەيغەمبەرلەرگە ئوخشاش بىزگە بىرەر مۆجىزە كەلتۈرسۇن» دېيىشتى'),
(2489, 21, 6, 'ئۇلاردىن ئىلگىرى، بىز ھالاك قىلغان شەھەرلەردىن (يەنى ئاھالىسىدىن) ھېچقايسىسى ئىمان ئېيتمىغان تۇرسا، (مۆجىزىلەرنى كۆرسەتسەڭ) ئۇلار ئىمان ئېيتامدۇ؟'),
(2490, 21, 7, '(ئى مۇھەممەد!) سەندىن ئىلگىرى - (پەرىشتىلەرنى ئەمەس) پەقەت ئىنسانلارنىلا پەيغەمبەر قىلىپ ئەۋەتتۇق، ئەگەر (بۇنى) بىلمىسەڭلار، ئەھلى ئىلىمدىن (يەنى تەۋرات، ئىنجىللارنى بىلىدىغانلاردىن) سوراڭلار'),
(2491, 21, 8, 'پەيغەمبەرلەرنى تاماق يېمەيدىغان جەسەت قىلغىنىمىز يوق (يەنى ئۇلار بارلىق كىشىلەرگە ئوخشاش يەيدۇ، ئىچىدۇ) ھەمدە ئۇلار (دۇنيادا ئۆلمەي) مەڭگۈ قالىدىغان بولغىنى يوق (يەنى ئۇلارمۇ بارلىق كىشىلەرگە ئوخشاش ۋاپات بولىدۇ)'),
(2492, 21, 9, 'ئاندىن ئۇلارغا (ئۇلارغا ياردەم بېرىش، ئۇلارنى ئىنكار قىلغانلارنى ھالاك قىلىشتىن ئىبارەت ۋەدىمىزنى راست قىلىپ بەردۇق، ئۇلارنى ۋە بىز خالىغانلارنى قۇتقۇزدۇق، (كۇفرىدا ۋە گۇمراھلىقتا) ھەددىدىن ئاشقۇچىلارنى ھالاك قىلدۇق'),
(2493, 21, 10, 'سىلەرنىڭ شان - شەرىپىڭلارنى ئۆز ئىچىگە ئالغان كىتابنى (يەنى قۇرئاننى) سىلەرگە ھەقىقەتەن نازىل قىلىپ بەردۇق. (بۇنى) چۈشەنمەمسىلەر؟'),
(2494, 21, 11, 'بىز (ئاھالىسى) كۇففار بولغان نۇرغۇن شەھەرلەرنى ھالاك قىلدۇق. ئۇلاردىن كېيىن (ئورنىغا) باشقا قەۋمنى پەيدا قىلدۇق'),
(2495, 21, 12, 'ئۇلار بىزنىڭ ئازابىمىزنىڭ (كەلگەنلىكىنى) ھېس قىلغان چاغدا ئۇشتۇمتۇت قاچىدۇ'),
(2496, 21, 13, '(پەرىشتىلەر ئۇلارغا مەسخىرە قىلىپ ئېيتتى) قاچماڭلار، باياشات تۇرمۇشۇڭلارغا، تۇرالغۇ جايلىرىڭلارغا قايتىڭلار، سىلەردىن (مۇھىم ئىشلاردا مەسلىھەت) سورىلىشى مۇمكىن»'),
(2497, 21, 14, 'ئۇلار: «ۋاي ئىسىت! ھەقىقەتەن بىز زالىم بولۇپتۇق» دېيىشتى'),
(2498, 21, 15, 'ئۇلارنى ئورۇلغان زىرائەتتەك ھالاك قىلىپ، جانلىرىنى ئالغىنىمىزغا قەدەر، ئۇلار يۇقىرىقى پەريادىنى تەكرارلاپ تۇردى'),
(2499, 21, 16, 'بىز ئاسماننى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئويناپ ياراتقىنىمىز يوق (يەنى ئۇلارنى بىكار ياراتماستىن، قۇدرىتىمىزنىڭ ئالامەتلىرى سۈپىتىدە ياراتتۇق)'),
(2500, 21, 17, 'ئەگەر بىز ئىچ پۇشۇقى قىلماقچى بولساق، قېشىمىزدىكى نەرسىلەر بىلەن قىلاتتۇق، ئەگەر (شۇنى) ئىرادە قىلغان بولساق (لېكىن بۇ ئۇلۇغ زاتىمىزغا مۇھال بولغانلىقتىن بۇنداق قىلىشنى ئىرادە قىلمىدۇق)'),
(2501, 21, 18, 'بىز ھەق ئارقىلىق باتىلغا ھۇجۇم قىلىمىز، ھەق باتىلنى يوقىتىدۇ، باتىل ناگاھان يوقىلىدۇ، (ئى كۇففارلار جامائەسى! اﷲ نى بالىسى بار، خوتۇنى بار دەپ) سۈپەتلىگەن سۆزۈڭلاردىن سىلەرگە ۋاي!'),
(2502, 21, 19, 'ئاسمانلاردىكى ۋە زېمىندىكىلەرنىڭ ھەممىسى اﷲ نىڭ ئىلكىدىدۇر، اﷲ نىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) اﷲ نىڭ ئىبادىتىدىن تەكەببۇرلۇق قىلىپ باش تارتمايدۇ ۋە ھېرىپمۇ قالمايدۇ'),
(2503, 21, 20, 'ئۇلار كېچە - كۈندۈز تەسبىھ ئېيتىدۇ، بوشاشمايدۇ'),
(2504, 21, 21, 'ئۇلار (يەنى مۇشرىكلار) زېمىندىكى نەرسىلەرنى ئىلاھ قىلىۋالدى، ئۇلار (يەنى بۇ نەرسىلەر) ئۆلۈكلەرنى تىرىلدۈرەلەمدۇ؟'),
(2505, 21, 22, 'ئەگەر ئاسمان - زېمىندا اﷲ تىن باشقا ئىلاھلار بولسا ئىدى، (كائىناتنىڭ تەرتىپى) ئەلۋەتتە بۇزۇلاتتى، ئەرشنىڭ پەرۋەردىگارى اﷲ ئۇلارنىڭ سۈپەتلىگەن نەرسىلىرىدىن پاكتۇر'),
(2506, 21, 23, 'اﷲ نىڭ قىلغانلىرىدىن سوئال - سوراق قىلىنمايدۇ، ئۇلاردىن سوئال - سوراق قىلىنىدۇ'),
(2507, 21, 24, 'ئۇلار اﷲ نى قويۇپ، باشقا مەبۇدلارنى ئىلاھ قىلىۋالدىمۇ؟ ئېيتقىنكى: «(بۇنىڭغا) دەلىلىڭلارنى كۆرسىتىڭلار، مانا بۇ مەن بىلەن بىللە بولغانلارنىڭ كىتابى (يەنى قۇرئان) دۇر ۋە مەندىن ئىلگىرىكىلەرنىڭ (تەۋرات، ئىنجىل قاتارلىق) كىتابىدۇر، ئۇلارنىڭ تولىسى ھەقنى ئۇقمايدۇ، ئۇلار (تەۋھىد) تىن يۈز ئۆرۈگۈچىلەردۇر»'),
(2508, 21, 25, '(ئى مۇھەممەد!) سەندىن ئىلگىرى ئەۋەتىلگەن پەيغەمبەرلەرنىڭ ھەممىسىگە: «مەندىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ماڭىلا ئىبادەت قىلىڭلار» دەپ ۋەھيى قىلدۇق'),
(2509, 21, 26, 'ئۇلار (يەنى مۇشرىكلار): «اﷲ نىڭ (پەرىشتىلەردىن) بالىسى بار» دېيىشتى، ئۇنداق ئەمەس. اﷲ (بۇنىڭدىن) پاكتۇر، (پەرىشتىلەر اﷲ نىڭ) ھۆرمەتلىك بەندىلىرىدۇر'),
(2510, 21, 27, 'ئۇلار اﷲ قا ئالدى بىلەن سۆز قىلىشقا پېتىنىلمايدۇ، ئۇلار اﷲ نىڭ ئەمرى بويىچە ئىش قىلىدۇ'),
(2511, 21, 28, 'اﷲ ئۇلارنىڭ قىلغانلىرىنى ۋە قىلماقچى بولغانلىرىنى بىلىپ تۇرىدۇ، ئۇلار اﷲ رازى بولغانلارغىلا شاپائەت قىلىدۇ، اﷲ نىڭ ھەيۋىسىدىن تىترەپ تۇرىدۇ'),
(2512, 21, 29, 'ئۇلاردىن كىمكى، مەن اﷲ تىن باشقا ئىلاھمەن، دەيدىكەن، ئۇنى دوزاخ بىلەن جازالايمىز، زالىملارنى شۇنداق جازالايمىز'),
(2513, 21, 30, 'كاپىرلار بىلەمدۇكى، ئاسمانلار بىلەن زېمىن بىر - بېرىگە تۇتاش ئىدى، ئۇلارنى ئايرىۋەتتۇق، ھەممە جانلىق مەۋجۇداتنى سۇدىن ياراتتۇق، ئۇلار (اﷲ نىڭ قۇدرىتىگە) ئىشىنەمدۇ؟'),
(2514, 21, 31, 'يەر تەۋرەپ ئۇلارنىڭ خاتىرجەمسىز بولماسلىقى ئۈچۈن، بىز زېمىندا تاغلارنى ياراتتۇق، ئۇلارنى (سەپەردە) كۆزلىگەن مەقسەتلەرگە يېتىۋالسۇن دەپ، تاغلاردا كەڭ يوللارنى ياراتتۇق'),
(2515, 21, 32, 'ئاسماننى بىز بىخەتەر ئۆگزە قىلدۇق، ھالبۇكى، ئۇلار (يەنى مۇشرىكلار) ئاسماندىكى (كۈن، ئاي، يۇلتۇزلارغا ئوخشاش اﷲ نىڭ بارلىقىنى ۋە ئۇنىڭ قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلەردىن غەپلەتتىدۇر'),
(2516, 21, 33, 'اﷲ كېچىنى ۋە كۈندۈزنى، كۈننى ۋە ئاينى ياراتتى. ئۇلارنىڭ ھەر بىرى (ئۆزىگە خاس) پەلەكتە سەير قىلىدۇ'),
(2517, 21, 34, '(ئى مۇھەممەد!) سەندىن ئىلگىرى (ئىنسانلاردىن) ھېچ كىشىنى مەڭگۈ ياشاتقىنىمىز يوق، سەن ئۆلسەڭ، ئۇلار مەڭگۈ ياشامدۇ؟ (بۇنداق بولۇش ھەرگىزمۇ مەڭگۈ ياشاتقىنىمىز يوق، سەن ئۆلسەك، ئۇلار مەڭگۈ ياشامدۇ؟ (بۇنداق بولۇش ھەرگىزمۇ ئۇلارغا مۇيەسسەر بولمايدۇ، ئۇلارنىڭ ھەممىسى ئۆلىدۇ)'),
(2518, 21, 35, 'ھەر بىر جان ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر. سىلەرنى بىز يامانلىققا مۇپتىلا قىلىش، ياخشىلىق بېرىش ئارقىلىق سىنايمىز سىلەر بىزنىڭ دەرگاھىمىزغا قايتىسىلەر (ئەمەلىڭلارغا يارىشا جازالايمىز)'),
(2519, 21, 36, 'كاپىرلار سىنى كۆرگەن چاغدا: «سىلەرنىڭ مەبۇدلىرىڭلارنى ئەيىبلەيدىغان ئادەم مۇشۇمۇ؟» (دېيىشىپ) سېنى مەسخىرە قىلىشىدۇ، ھالبۇكى، ئۇلار مەرھەمەتلىك اﷲ تەرىپىدىن نازىل بولغان قۇرئاننى ئىنكار قىلىشىدۇ'),
(2520, 21, 37, 'ئىنسان ئالدىراڭغۇ يارىتىلغان، سىلەرگە ئالامەتلىرىمنى (يەنى ماڭا ئاسىيلىق قىلغانلارنى جازالايدىغانلىقىمنى) كۆرسىتىمەن. (ئۇنىڭ ۋاقتى سائىتىدىن بۇرۇن) مېنى ئالدىرىتىپ كەتمەڭلار'),
(2521, 21, 38, 'ئۇلار: «(سۆزۈڭلاردا) راستچىل بولساڭلار، بۇ ۋەدە (يەنى بىزگە قىلىنغان ئازابنىڭ يۈز بېرىدىغان ۋاقتى) قاچان؟» دېيىشىدۇ'),
(2522, 21, 39, 'ئەگەر كاپىرلار بۇ چاغدا يۈزلىرىدىن، دۈمبىلىرىدىن ئازابنى دەپئى قىلالمايدىغانلىقلىرىنى ۋە ھېچقانداق ياردەمگە ئېرىشەلمەيدىغانلىقلىرىنى بىلسە ئىدى (ئازابنى بالدۇر يۈز بېرىشىنى تەلەپ قىلمايتتى)'),
(2523, 21, 40, 'لېكىن قىيامەت ئۇلارغا تۇيۇقسىز كېلىدۇ - دە، ئۇلارنى ھەيرەتتە قالدۇرىدۇ، ئۇلار قىيامەتكە قارشىلىق كۆرسىتەلمەيدۇ، ئۇلارغا (تەۋبە قىلىۋېلىش ۋە ئۆزرە بايان قىلىش ئۈچۈن) مۆھلەتمۇ بېرىلمەيدۇ'),
(2524, 21, 41, 'سەندىن ئىلگىرى ھەقىقەتەن نۇرغۇن پەيغەمبەرلەر مەسخىرە قىلىندى، ئۇلارنى مەسخىرە قىلغۇچىلارغا مەسخىرە قىلغاننىڭ جازاسى بولغان ئازاب نازىل بولدى'),
(2525, 21, 42, '(ئى مۇھەممەد! مەسخىرە قىلغۇچىلارغا) «سىلەرنى كېچىدە ۋە كۈندۈزدە مەرھەمەتلىك اﷲ نىڭ ئازابىدىن كىم ساقلايدۇ؟» دېگىن بەلكى ئۇلار پەرۋەردىگارىنىڭ كالامىدىن يۈز ئۆرۈگۈچىلەردۇر'),
(2526, 21, 43, 'ئۇلاردىن بىزدىن باشقا ئۆزلىرىنى (قوغدايدىغان) مەبۇدلىرى بارمۇ ئۇلارنىڭ (چوقۇنۇۋاتقان) مەبۇدلىرى ئۆزلىرىنى قوغداشقىمۇ قادىر بولالمايدۇ، بىزنىڭ ياردىمىمىزگىمۇ ئېرىشەلمەيدۇ'),
(2527, 21, 44, 'بۇلارنى (يەنى مۇشرىكلارنى) ۋە ئۇلارنىڭ ئەجداتلىرىنى (دۇنيانىڭ مال - مۈلكىدىن) بەھرىمەن قىلدۇق، ھەتتا (شۇ نېمەت ئىچىدە) ئۇلارنىڭ ئۆمرى ئۇزۇن بولدى. ئۇلارنىڭ زېمىننىڭ ئەتراپىنى (مۇسۇلمانلارغا ئىگەللىتىش بىلەن) قەدەممۇقەدەم تارايتىپ بېرىۋاتقانلىقىمىزنى ئۇلارمۇ بىلەمدۇ؟ (ئەھۋال مۇشۇنداق ئىكەن) ئۇلار غالىب ھېسابلىنامدۇ؟ (مەغلۇپ ھېسابلىنامدۇ؟)'),
(2528, 21, 45, '(ئى مۇھەممەد!) «مەن سىلەرنى پەقەت ۋەھيى ئارقىلىق ئاگاھلاندۇرىمەن» دېگىن. گاسلار ئاگاھلاندۇرۇلغان چاغدا دەۋەتنى ئاڭلىمايدۇ (ئى مۇشرىكلار! سىلەر سەركەشلىكىڭلارنىڭ قاتتىقلىقىدىن خۇددى گاسقا ئوخشاش دەۋەتنى ئاڭلىمايسىلەر، ۋەز - نەسىھەتنى قوبۇل قىلمايسىلەر)'),
(2529, 21, 46, 'ئەگەر ئۇلار پەرۋەردىگارىڭدىن ئازغىنا ئازاب يەتسىلا، ئۇلار چوقۇم: «ۋاي ئىسىت! (اﷲ نىڭ پەيغەمبەرلىرىنى ئىنكار قىلىپ) ھەقىقەتەن (ئۆزىمىزگە) زۇلۇم قىلىپتۇق» دەيدۇ'),
(2530, 21, 47, 'قىيامەت كۈنى بىز (ئەمەللەر تارتىلىدىغان) ئادالەت تارازىسىنى ئورنىتىمىز، ھېچ ئادەمگە قىلچە ئۇۋال قىلىنمايدۇ (يەنى ياخشى ئادەمنىڭ ياخشىلىقى كېمەيتىۋېتىلمەيدۇ، يامان ئادەمنىڭ يامانلىقى ئاشۇرۇۋېتىلمەيدۇ) ئەگەر ئۇنىڭ قىچا چاغلىق ئەمەلى بولسىمۇ، ئۇنى ھازىر قىلىمىز، (بەندىلەرنىڭ ئەمەللىرىدىن) ھېساب ئېلىشقا بىز يېتەرلىكمىز'),
(2531, 21, 48, 'بىز ھەقىقەتەن موسا بىلەن ھارۇنغا فۇرقاننى (يەنى ھەق بىلەن باتىلنى، ھالال بىلەن ھارامنى، ھىدايەت بىلەن گۇمراھلىقنى ئايرىغۇچى تەۋراتنى)، نۇرنى، تەقۋادارلارغا (پايدىلىنىدىغان) ۋەز - نەسىھەتنى بەردۇق'),
(2532, 21, 49, 'تەقۋادارلار پەرۋەردىگارىنى كۆرمەي تۇرۇپ، ئۇنىڭدىن قورقىدۇ ھەمدە ئۇلار قىيامەتنىڭ (دەھشىتىدىن) تىترىگۈچىلەردۇر'),
(2533, 21, 50, 'بۇ بىز (سىلەرنىڭ تىلىڭلاردا) نازىل قىلغان مۇبارەك قۇرئاندۇر. (ئى ئەرەب جامائەسى! ئۇ شۇنچە روشەن تۇرسىمۇ) سىلەر ئۇنى ئىنكار قىلامسىلەر؟'),
(2534, 21, 51, '(مۇسا، ھارۇنلاردىن بۇرۇن) بىز ئىبراھىمغا ھەقىقەتەن (دۇنيا ۋە ئاخىرەتتە ياخشىلىق يوللىرىنى كۆرسىتىپ بېرىدىغان) ھىدايەت ئاتا قىلغان ئىدۇق، بىز ئۇنىڭ (پەيغەمبەرلىكنىڭ ئەھلى) ئىكەنلىكىنى بىلەتتۇق'),
(2535, 21, 52, 'ئۆز ۋاقتىدا ئىبراھىم (كۇففارلار چوقۇنۇۋاتقان بۇتلارنى مەنسىتمەي) ئاتىسىغا ۋە قەۋمىگە: سىلەر چوقۇنۇۋاتقان بۇ بۇتلار نېمە؟ (يەنى نېمە ئۈچۈن بۇ بۇتلارغا چوقۇنىسىلەر؟)» دېدى'),
(2536, 21, 53, 'ئۇلار: «ئاتا - بوۋىلىرىمىزنىڭ ئۇلارغا ئىبادەت قىلىپ كېلىۋاتقانلىقىنى بىلىمىز (يەنى ئۇلارغا ئاتا - بوۋىلىرىمىزغا تەقلىد قىلىپ چوقۇنىمىز)» دېدى'),
(2537, 21, 54, 'ئىبراھىم: «سىلەرمۇ، ئاتا - بوۋاڭلارمۇ (پايدا - زىيان يەتكۈزەلمەيدىغان، ئاڭلىمايدىغان جانسىز بۇتلارغا چوقۇنغانلىقىڭلار ئۈچۈن) ھەقىقەتەن روشەن گۇمراھلىقتا سىلەر» دېدى'),
(2538, 21, 55, 'ئۇلار: «سەن راست دەۋاتامسەن؟ ياكى چاقچاق دەۋاتامسەن؟» دېدى'),
(2539, 21, 56, 'ئىبراھىم ئېيتتى: «ئۇنداق ئەمەس، سىلەرنىڭ (ئىبادەت قىلىشىڭلارغا لايىق) پەرۋەردىگارىڭلار ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارىدۇر، ئۇلارنى ئۇ ياراتقان، مەنمۇ ئۇنىڭغا (قەتئىي دەلىللەر بىلەن) گۇۋاھلىق قىلغۇچىلاردىنمەن'),
(2540, 21, 57, 'اﷲ بىلەن قەسەمكى، سىلەر قايتىپ كەتكەندىن كېيىن چوقۇم بۇتلىرىڭلارنى (چېقىۋېتىشنىڭ) ئامالىنى قىلىمەن»'),
(2541, 21, 58, 'ئىبراھىم بۇتلارنى چېقىپ پارچە - پارچە قىلىۋەتتى، ئۇلارنىڭ قايتىپ كېلىپ (بۇتلارنى كىمنىڭ چاققانلىقىنى سورىشى ئۈچۈن) پەقەت چوڭ بۇتنى (چاقماي) قالدۇردى'),
(2542, 21, 59, '(ئۇلار بايرىمىدىن قايتىپ كېلىپ بۇتلىرىنىڭ چېقىۋېتىلگەنلىكىنى كۆرۈپ) «بۇتلىرىمىزنى مۇنداق قىلغان كىم؟ ئۇ ھەقىقەتەن زالىم ئىكەن» دېدى'),
(2543, 21, 60, '(ئۇلارنىڭ بەزىسى) ئىبراھىم ئاتلىق بىر يىگىتنىڭ ئۇلارنى ئەيىبلىگەنلىكىنى ئاڭلىغان ئىدۇق (يەنى شۇ قىلغان بولۇشى مۇمكىن)» دېدى'),
(2544, 21, 61, 'ئۇلار (يەنى نەمرۇد ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرى): «ئىبراھىمنى كىشىلەرنىڭ ئالدىغا ئېلىپ كېلىڭلار، (ئىبراھىمنىڭ بۇتلارنى ئەيىبلىگەنلىكىگە) ئۇلار گۇۋاھلىق بەرسۇن» دېدى)'),
(2545, 21, 62, 'ئۇلار: «ئى ئىبراھىم! بۇتلىرىمىزنى مۇشۇنداق قىلغان سەنمۇ؟» دېدى.'),
(2546, 21, 63, '(ئىبراھىم: «(ياق) بەلكى ئۇلارنىڭ مۇنۇ چوڭى شۇنداق قىلدى، ئەگەر ئۇلار سۆزلىيەلىسە، (كىمنىڭ چاققانلىقىنى) ئۇلارنىڭ ئۆزلىرىدىن سوراپ بېقىڭلار!» دېدى'),
(2547, 21, 64, 'ئۇلار بىر - بىرىنى ئەيىبلەپ: «(سىلەر بۇتلارغا چوقۇنۇشتا) ھەقىقەتەن زالىمسىلەر» دېدى'),
(2548, 21, 65, 'ئاندىن ئۇلار (خىجالەتچىلىكتىن) باشلىرىنى تۆۋەن سېلىپ: «ئۇلارنىڭ گەپ قىلالمايدىغانلىقىنى سەن ئوبدان بىلىسەنغۇ؟» دېدى'),
(2549, 21, 66, 'ئىبراھىم ئېيتتى: «سىلەر اﷲ نى قويۇپ سىلەرگە ھېچ پايدا يەتكۈزەلمەيدىغان ۋە زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنامسىلەر»'),
(2550, 21, 67, 'سىلەرگە ۋە اﷲ نى قويۇپ سىلەر چوقۇنۇۋاتقان بۇتلارغا تۈفى! سىلەر چۈشەنمەمسىلەر؟»'),
(2551, 21, 68, 'ئۇلار ئېيتتى: «ئىبراھىمنى كۆيدۈرۈڭلار، ئىلاھلىرىڭلارغا ياردەم بېرىڭلار، ئەگەر ئۇلارغا ھەقىقىي ياردەم بەرمەكچى بولساڭلار»'),
(2552, 21, 69, 'بىز ئوتقا: «ئى ئوت! ئىبراھىمغا سالقىن ۋە ئامانلىق (بەخش ئېتىدىغان) بولۇپ بەرگىن» دېدۇق'),
(2553, 21, 70, 'ئۇلار ئىبراھىمغا زىيانكەشلىك قىلماقچى بولدى، بىز ئۇلارنى ئەڭ زىيان تارتقۇچى قىلدۇق'),
(2554, 21, 71, 'بىز ئىبراھىم بىلەن لۇتنى قۇتقۇزدۇق، ئۇلارنى بىز ئەھلى جاھان ئۈچۈن بەرىكەتلىك زېمىنغا كۆچۈردۇق'),
(2555, 21, 72, 'بىز ئۇنىڭغا (يەنى ئىبراھىمغا) ئىسھاقنى بەردۇق، (ئۇ پەرۋەردىگارىدىن سورىمىسىمۇ) يەئقۇبنى ئوشۇق بەردۇق (يەنى يەئقۇب ئەلەيھىسسالامنى نەۋرە قىلىپ ياكى ئىبراھى ئەلەيھىسسالامنىڭ تەلىۋىدىن زىيادە قىلىپ بەردۇق)، ئۇلارنىڭ ھەممىسىنىڭ ياخشى ئادەملەر قىلدۇق'),
(2556, 21, 73, 'ئۇلارنى بىزنىڭ ئەمرىمىز بويىچە (كىشىلەرنى بىزنىڭ دىنىمىزغا) يېتەكلەيدىغان پېشۋالار (يەنى پەيغەمبەرلەر) قىلدۇق، ئۇلارغا بىز ياخشى ئىشلارنى قىلىشنى، ناماز ئوقۇشنى، زاكات بېرىشنى ۋەھيى قىلىدۇ، ئۇلار بىزگە خالىس ئىبادەت قىلاتتى'),
(2557, 21, 74, 'لۇتقا پەيغەمبەرلىكنى ۋە ئىلىمنى ئاتا قىلدۇق (لۇت ئىبراھىم ئەلەيھىسسالامغا ئىمان ئېيتقان، ئەگەشكەن ۋە ئۇنىڭ بىلەن ھىجرەت قىلغان ئىدى)، ئۇنىڭ (ئائىلىسى بەچچىۋازلىق، قاراقچىلىق قاتارلىق) يامان قىلىقلارنى قىلىدىغان شەھەردىن قۇتقۇزدۇق، ھەقىقەتەن ئۇلار يامان پاسىق قەۋم ئىدى'),
(2558, 21, 75, 'بىز ئۇنى رەھمىتىمىز دائىرىسىگە كىرگۈزدۇق، ئۇ ھەقىقەتەن ياخشىلاردىن ئىدى'),
(2559, 21, 76, 'نۇھنىڭ (قىسسىسىنى بايان قىلغىن). ئۆز ۋاقتىدا ئۇ (مەزكۇر پەيغەمبەرلەردىن) بۇرۇن (قەۋمىگە، پەرۋەردىگارىم يەر يۈزىدە كۇففارلاردىن بىرنىمۇ قويمىغىن، دەپ بەتدۇئا قىلغان ئىدى)، بىز ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنى ۋە ئۇنىڭ ئائىلىسىدىكىلەرنى (ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنى) بۈيۈك بالادىن (يەنى توپان بالاسىدا غەرق بولۇشتىن) قۇتقۇزدۇق (يەنى نۇھنى قەۋمىنىڭ شەررىدىن قۇتقۇزۇپ، ئۇلارنى ھالاك قىلدۇق)'),
(2560, 21, 77, 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان قەۋمگە قارشى ئۇنىڭغا ياردەم بەردۇق، ئۇلار ھەقىقەتەن يامان قەۋم ئىدى. ئۇلارنىڭ (بىرنىمۇ قويماي) ھەممىسىنى غەرق قىلدۇق'),
(2561, 21, 78, 'داۋۇت بىلەن سۇلايماننىڭ (قىسسىسىنى بايان قىلغىن). ئۆز ۋاقتىدا بىر قەۋمنىڭ قويى كېچىسى زىرائەتنى يەپ (بۇزۇۋەتكەندە)، ئۇ ئىككىسى زىرائەت توغرىسىدا ھۆكۈم چىقارغان ئىدى. ئۇلارنىڭ ھۆكمىگە بىز شاھىت ئىدۇق'),
(2562, 21, 79, 'قانداق ھۆكۈم قىلىشنى بىز سۇلايمانغا بىلدۈردۇق، تەسبىھ ئېيتىشقا مۇسەخخەر قىلدۇق، (شۇنداق قىلىشقا) بىز قادىر ئىدۇق'),
(2563, 21, 80, 'جەڭلەردە سىلەرنى (يارىدار بولۇشتىن) ساقلاش مەقسىتىدە، داۋۇدقا سىلەر ئۈچۈن ساۋۇت ياساشنى ئۆگەتتۇق، سىلەر (بۇنىڭغا) شۈكۈر قىلامسىلەر؟ (يەنى شۈكۈر قىلىڭلار ۋە نېمەتنىڭ قەدرىنى بىلىڭلار)'),
(2564, 21, 81, 'سۇلايمانغا قاتتىق چىقىدىغان شامالنى مۇسەخخەر قىلىپ بەردۇق، شامال سۇلايماننىڭ ئەمرى بويىچە، بىز بەرىكەتلىك قىلغان زېمىنغا (يەنى شامغا) باراتتى، بىز ھەممىنى بىلگۈچىمىز'),
(2565, 21, 82, 'سۇلايمانغا يەنە بىر قىسىم شەيتانلارنى مۇسەخخەر قىلىپ بەردۇق، ئۇلار دېڭىزلاردا چۆكۈپ ئۇنىڭغا ئۈنچە - مارجان (گۆھەرلەرنى سۈزۈپ چىقاتتى)، ئۇلار يەنە (شەھەرلەرنى، ئېگىز سارايلارنى سېلىشتەك) باشقا ئىشلارنىمۇ قىلاتتى، بىز ئۇلارنى (سۇلايماننىڭ ئىتائىتىدىن چىقىشتىن) ساقلايتتۇق'),
(2566, 21, 83, '(ئەييۇبنىڭ قسسسىنى بايان قىلغىن). ئۆز ۋاقتىدا ئۇ پەرۋەردىگارىغا: «ھەقىقەتەن مېنى بال (يەنى قاتتىق كېسەل) ئورىۋالدى، سەن ئەڭ مەرھەمەتلىكسەن» دەپ دۇئا قىلدى'),
(2567, 21, 84, 'ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنىڭ بېشىغا كەلگەن بالانى كۆتۈرۈۋەتتۇق، ئۇنىڭغا رەھىم قىلغانلىقىمىز، ئىبادەت قىلغۇچىلارغا ئىبرەت قىلغانلىقىمىز ئۈچۈن، ئۇنىڭغا ئائىلىسىنى ۋە ئۇنىڭغا ئوخشاش بىر باراۋەرنى زىيادە ئاتا قىلدۇق (يەنى ئۇنىڭ ئۆلگەن بالىلىرىنىڭ ۋە يوقالغان ماللىرىنىڭ ئورنىغا بىر ھەسسە كۆپ بالا، بىر ھەسسە كۆپ مال بەردۇق)'),
(2568, 21, 85, 'ئىسمائىل، ئىدرىس ۋە زۇلكىفللارنىڭ (قىسسىسىنى باياق قىلغىن). (ئۇلارنىڭ) ھەممىسى سەۋرلىك ئىدى (يەنى اﷲ قا ئىبادەت قىلىشتا چىداملىق ئىدى ۋە گۇناھلار ئالدىدا ئۆزلىرىنى تۇتۇۋالاتتى)'),
(2569, 21, 86, 'ئۇلارنى رەھمىتىمىز دائىرىسىگە كىرگۈزدۇق، ئۇلار ھەقىقەتەن ياخشىلاردىن ئىدى'),
(2570, 21, 87, 'يۇنۇسنىڭ (قىسسىنى بايان قىلغىن) ئۆز ۋاقتىدا ئۇ (قەۋمنىڭ ئىمان ئېيتمىغانلىقىدىن) خاپا بولۇپ (شەھىرىدىن) چىقىپ كەتكەن ئىدى. ئۇ بىزنى (بېلىقنىڭ قارنىدا) ئۆزىنى قىسمايدۇ دەپ ئويلىدى، ئۇ قاراڭغۇلۇقتا (يەنى بېلىقنى قارنىدا): «(پەرۋەردىگارىم) سەندىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، سەن (جىمى كەمچىلىكلەردىن) پاكتۇرسەن، مەن ھەقىقەتەن (ئۆز نەپسىمگە) زۇلۇم قىلغۇچىلاردىن بولدۇم» دەپ نىدا قىلدى'),
(2571, 21, 88, 'ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنى (بېلىق يۇتقان چاغدىكى) غەمدىن خالاس قىلدۇق، شۇنىڭدەك مۆمىنلەرنى (ئېغىرچىلىقلاردىن) خالاس تاپقۇزىمىز'),
(2572, 21, 89, 'زەكەرىيانىڭ (قىسسىنى بايان قىلغىن). ئۆز ۋاقتىدا ئۇ: «ئى پەرۋەردىگارىم! مىنى (بالىسىز، ۋارىسسىز) يالغۇز قويمىساڭ، سەن ئەڭ ياخشى ۋارىستۇرسەن (يەنى ھەممە ئادەم ئۆلىدۇ، سەنلا باقىي قالىسەن)» دەپ نىدا قىلدى'),
(2573, 21, 90, 'بىز ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنىڭغا يەھيانى ئاتا قىلدۇق، ئۇنىڭغا خوتۇنىنى ئۆزگەرتىپ بەردۇق (يەنى تۇغىدىغان قىلىپ بەردۇق). (يۇقىرىقى پەيغەمبەرلەرنىڭ دۇئاسىنى ئىجابەت قىلىشىمىز) شۇنىڭ ئۈچۈن ئىدىكى، ئۇلار ياخشى ئىشلارنى قىلىشقا ئالدىرايتتى، (رەھمىتىمىزنى) ئۈمىد قىلىپ، (ئازابىمىزدىن) قورقۇپ بىزگە دۇئا قىلاتتى، بىزگە كەمتەرلىك بىلەن ئىبادەت قىلاتتى'),
(2574, 21, 91, 'نومۇسنى ساقلىغان ئايالنى (يەنى مەريەمنىڭ قىسسىنى بايان قىلغىن) ئۇنىڭغا (يەنى كىيىمنىڭ ئىچىگە) بىزنىڭ تەرىپىمىزدىن بولغان روھنى پۈۋلىدۇق (پۈۋلەنگەن روھ ئۇنىڭ ئىچىگە كىرىپ، ئۇ ئىساغا ھامىلدار بولدى)، ئۇنى ۋە ئوغلىنى (يەنى ئىسا بىلەن مەريەمنى) ئەھلى جاھان ئۈچۈن (بىزنىڭ قۇدرىتىمىزنى كۆرسىتىدىغان) دەلىل قىلدۇق'),
(2575, 21, 92, '(ئى ئىنسانلار!) سىلەرنىڭ دىنىڭلار ھەقىقەتەن بىر دىندۇر. مەن سىلەرنىڭ پەرۋەردىگارىڭلاردۇرمەن، ماڭىلا ئىبادەت قىلىڭلار'),
(2576, 21, 93, 'ئۇلار (دىندا ئىختىلاپ قىلىشىپ) پىرقە - پىرقىلەرگە بۆلۈنۈپ كەتتى، ھەممىسى بىزنىڭ دەرگاھىمىزغا قايتقۇچىلاردۇر'),
(2577, 21, 94, 'كىمكى مۆمىن بولۇپ ياخشى ئەمەللەرنى قىلىدىكەن، ئۇنىڭ ئەمەلىنىڭ (ساۋابى) زايا كەتمەيدۇ. ئۇنىڭ ئەمەلىنى (يەنى نامە - ئەمالنى) چوقۇم خاتىرىلەپ قويىمىز'),
(2578, 21, 95, 'بىز ھالاك قىلغان شەھەر (ئاھالىسى) نىڭ دۇنياغا قايتىشى مۇمكىن ئەمەس'),
(2579, 21, 96, 'يەجۇج - مەجۈج (توسمىسى) ئېچىۋېتىلگەندە، ئۇلار زېمىننىڭ ھەر بىر تۆپىلىكلىرىدىن ئالدىراپ چىقىپ كېلىدۇ'),
(2580, 21, 97, 'راست ۋەدە (يەنى قىيامەتنىڭ ۋاقتى) يېقىنلاشتى، ئۇ كۈندە كاپىرلارنىڭ كۆزلىرى چەكچىيىپ قالىدۇ، (ئۇلار) «ۋاي ئىسىت! بۇنىڭدىن بىز غەپلەتتە قېلىپتۇق، بەلكى بىز ئۆزىمىزگە زۇلۇم قىلىپتۇق» (دېيىشىدۇ)'),
(2581, 21, 98, '(ئى مۇشرىكلار!) ھەقىقەتەن سىلەر اﷲ نى قويۇپ ئىبادەت قىلغان نەرسەڭلار بىلەن دوزاخقا يېقىلغۇ بولىسىلەر، سىلەر دوزاخقا كىرىسىلەر'),
(2582, 21, 99, 'ئەگەر سىلەر ئىبادەت قىلىۋاتقان بۇتلار ئىلاھلار بولىدىغان بولسا، دوزاخقا كىرمىگەن بولاتتى (ئىبادەت قىلغۇچى ۋە ئىبادەت قىلىنغۇچىلارنىڭ) ھەممىسى دوزاختا مەڭگۈ قالىدۇ'),
(2583, 21, 100, 'ئۇلار دوزاختا ھەمىشە پەرياد چېكىدۇ، (ئازابنىڭ قاتتىقلىقىدىن) ئۇلار دوزاختا ھېچ نەرسىنى ئاڭلىمايدۇ'),
(2584, 21, 101, 'بىز بىر تەرەپتىن بېرىلگەن بەخت - سائادەتكە تېگىشلىك بولغانلار (يەنى تائەت - ئىبادەتكە تەۋپىق بېرىلىپ توغرا يولدا ئىبادەت قىلغان سائادەتمەن بەندىلەر) دوزاختىن يىراق قىلىنىدۇ'),
(2585, 21, 102, 'ئۇلار دوزاخنىڭ شەپىسىنىڭ ئاڭلىمايدۇ، كۆڭلى تارتقان نازۇنېمەتلەر ئىچىدە مەڭگۈ قالىدۇ'),
(2586, 21, 103, 'چوڭ قورقۇنچ (يەنى قىيامەتنىڭ دەھشىتى) ئۇلارنى قايغۇغا سالمايدۇ، پەرىشتىلەر: «اﷲ سىلەرگە ۋەدە قىلغان كۈن مۇشۇ» (دەپ)، ئۇلارنى (جەننەتنىڭ دەرۋازىلىرى ئالدىدا) قارشى ئالىدۇ.'),
(2587, 21, 104, 'ئۇ كۈندە ئاسماننى (پۈتۈكچى) نەرسە يېزىلغان قەغەزنى يۆگىگەندەك يۆگەيمىز. مەخلۇقاتلارنى دەسلەپتە قانداق ياراتقان بولساق، شۇ ھالىتىدە ئەسلىگە قايتۇرىمىز. (بۇ) بىز ئۈستىمىزگە ئالغان ۋەدىدۇر، (ئۇنى) چوقۇم ئىشقا ئاشۇرىمىز.'),
(2588, 21, 105, 'بىز لەۋھۇلمەھپۇزدا (ئەزەلدە) يازغاندىن كېيىن، (داۋۇدقا نازىل قىلىنغان) زەبۇردا زېمىنغا (يەنى جەننەت زېمىنغا) ھەقىقەتەن مېنىڭ ياخشى بەندىلىرىم ۋارىسلىق قىلىدۇ دەپ يازدۇق'),
(2589, 21, 106, 'بۇ (قۇرئان) دا اﷲ قا ئىبادەت قىلغۇچى قەۋم ئۈچۈن (خەۋەرلەر، ۋەز - نەسىھەتلەردىن) ئەلۋەتتە يېتەرلىك بار'),
(2590, 21, 107, '(ئى مۇھەممەد!) سېنى بىز پۈتۈن ئەھلى جاھان ئۈچۈن پەقەت رەھمەت قىلىپلا ئەۋەتتۇق'),
(2591, 21, 108, '(ئى مۇھەممەد! مۇشرىكلارغا) «ماڭا ۋەھيى قىلىندىكى، سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، سىلەر بويسۇنامسىلەر؟» دېگىن'),
(2592, 21, 109, 'ئەگەر ئۇلار (ئىسلامدىن) يۈز ئۆرۈسە، ئېيتقىنكى، مەن سىلەرگە (ھەقىقەتنى ھېچ كىشىنى ئايرىماستىن) ئوخشاش ئۇقتۇردۇم، سىلەرگە ۋەدە قىلىنغان (ئازاب) يېقىنمۇ، يىراقمۇ؟ ئۇقمايمەن'),
(2593, 21, 110, 'اﷲ ھەقىقەتەن ئاشكارا سۆزنىمۇ، سىلەرنىڭ يوشۇرغانلىرىڭلارنىمۇ بىلىپ تۇرىدۇ'),
(2594, 21, 111, 'بىلەمسەنكى، ئۇ (يەنى سىلەرگە بېرىلىدىغان جازانىڭ كېچىكىشى) سىلەر ئۈچۈن سىناق ۋە ۋاقىتلىق پايدىلىنىش بولۇشى مۇمكىن»'),
(2595, 21, 112, '(پەيغەمبەر ئېيتتى) «پەرۋەردىگارىم! (مېنىڭ بىلەن مەككە كۇففارلىرىنىڭ ئارىسىدا) ئادىللىق بىلەن ھۆكۈم چىقارغىن، بىزنىڭ پەرۋەردىگارىمىز مەرھەمەتلىك اﷲ دۇر، سىلەرنىڭ بوھتان سۆزلىرىڭلارغا قارشى اﷲ تىن ياردەم تىلەيمەن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'ئى ئىنسانلار! پەرۋەردىگارىڭلاردىن (يەنى ئازابىدىن) قورقۇڭلار، قىيامەتنىڭ زىلزىلىسى ھەقىقەتەن چوڭ ئىشتۇر'),
(2597, 22, 2, 'ئۇ كۈندە ھەر بىر سۈت ئېمىتىۋاتقان ئايال (قاتتىق قورقۇنچتىن تېڭىرقاپ قېلىش بىلەن) ئېمىۋاتقان بوۋىقىنى ئۇنتۇيدۇ، ھەر بىر ھامىلىدار ئايالنىڭ بويىدىن ئاجراپ كېتىدۇ، ئىنسانلارنى مەست كۆرۈسەن، ھالبۇكى، ئۇلار (ھەقىقەتتە) مەست ئەمەس (دېمەك، قىيامەتنىڭ دەششىتى ئۇلارنى ئەس - ھوشىدىن كەتكۈزۈۋەتكەن)، لېكىن اﷲ نىڭ ئازابى قاتتىقتۇر'),
(2598, 22, 3, 'بەزى كىشىلەر اﷲ (نىڭ قۇدرىتى ۋە سۈپەتلىرى) ئۈستىدە ھېچقانداق دەلىلسىز مۇنازىرىلىشىدۇ، ھەر بىر سەركەش شەيتانغا (يەنى ھەقتى توسقۇچى كۇففارلارنىڭ كاتتىباشلىرىغا) ئەگىشىدۇ'),
(2599, 22, 4, 'اﷲ ھۆكۈم قىلىدۇكى، كىمكى شەيتانغا ئەگىشىدىكەن شەيتان ئۇنى ئازدۇرىدۇ، ۋە جەھەننەمنىڭ ئازابىغا سۆرەيدۇ'),
(2600, 22, 5, 'ئى ئىنسانلار! ئەگەر سىلەر قايتا تىرىلىشتىن شەكلەنسەڭلار (گۇمانىڭلارنىڭ تۈگىشى ئۈچۈن ئەسلىدىكى يارىتىلىشىڭلارغا قاراڭلار)، بىز سىلەرگە (اﷲ نىڭ قۇدرىتىنى بايان قىلىش ئۈچۈن ئاتاڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن، ئۇنىڭ ئەۋلادىنى (ئالدى بىلەن) ئابىمەنىيدىن، ئاندىن لەختە قاندىن، ئاندىن شەكىلگە كىرگۈزۈلگەن ۋە كىرگۈزۈلمىگەن پارچە گۆشتىن ياراتتۇق. بىز خالىغان ئادەمنى بەچچىداندان مۇئەييەن مۇددەتكىچە قالدۇرىمىز، ئاندىن سىلەرنى (ئاناڭلارنىڭ قارنىدىن) بوۋاقلىق ھالىتىڭلاردا چىقىرىمىز (ئاندىن سىلەرنى ئاستا - ئاستا كۈچ - قۇۋۋەتكە تولدۇرىمىز)، سىلەر (كامالەت يېشى) قىران ۋاقتىڭلارغا يېتىسىلەر، بەزىڭلار ياشلىقىڭلاردا ۋاپات بولۇپ كېتىسىلەر، بەزىڭلار، (ئىلگىرى) بىلگەندىن كېيىن ھېچ نەرسىنى بىلمەس ھالىتىڭلارغا قايتۇرۇلۇشىڭلار ئۈچۈن، ناھايىتى قېرىپ كەتكۈچە (ھايات) قالدۇرۇلىسىلەر، (اﷲ تائالانىڭ ئۆلگەنلەرنىڭ قايتا تىرىلدۈرۈشكە قادىر ئىكەنلىكىنى كۆرسىتىدىغان يەنە بىر دەلىل شۇكى) سەن زېمىننى قاقاس كۆرۈسەن، ئۇنىڭغا بىز يامغۇر ياغدۇرساق ئۇ جانلىنىدۇ ۋە كۆپۈشىدۇ، تۈرلۈك چىرايلىق ئۆسۈملۈكلەرنى ئۈندۈرۈپ بېرىدۇ'),
(2601, 22, 6, 'بۇ (يەنى اﷲ تائالانىڭ ئىنساننى ۋە ئۆسۈملۈكلەرنى يۇقىرىقىدەك يارىتىشى)، اﷲ نىڭ ھەق ئىكەنلىكىنى، ئۆلۈكلەرنى تىرىلدۈرەلەيدىغانلىقىنى ۋە ھەر نەرسىگە قادىر ئىكەنلىكىنى سىلەرگە بىلدۈرۈش ئۈچۈندۇر'),
(2602, 22, 7, 'شۇنداقلا قىيامەتنىڭ شەكسىز بولىدىغانلىقىنى، قەبرىلەردىكى ئۆلۈكلەرنى (ئۆلۈك زېمىننى ئۆسۈملۈكلەرنى ئۈندۈرۈش بىلەن تىرىلدۈرگەندەك) اﷲ نىڭ تىرىلدۈرىدىغانلىقىنى بىلدۈرۈش ئۈچۈندۇر'),
(2603, 22, 8, 'بەزىلەر (توغرا تونۇشقا ئېلىپ بارىدىغان) ھېچ قانداق ھەقىقى ئىلىمسىز، ھىدايەتسىز ۋە نۇرلۇق كىتابسىز ھالدا اﷲ توغرىسىدا مۇنازىرىلىشىدۇ، (ئۇنىڭ مۇنازىرىلىشىشى نوقۇل نەپسى خاھىشى ۋە تەرسالىقىدىندۇر)'),
(2604, 22, 9, 'ئۇ (كىشىلەرنى) اﷲ نىڭ يولىدىن ئازدۇرۇش ئۈچۈن مەغرۇرانە يۈرىدۇ، ئۇ دۇنيادا خارلىققا دۇچار بولىدۇ، ئاخىرەتتە ئۇنىڭغا كۆيدۈرگۈچى ئوتنىڭ ئازابىنى تېتىتىمىز'),
(2605, 22, 10, '(ئۇنىڭغا): «(خارلىق بىلەن ئازاب) سېنىڭ قىلمىشلىرىڭ يەنى كۇفرىڭ بىلەن گۇمراھلىقىڭ) تۈپەيلىدىندۇر، اﷲ بەندىلىرىگە ھەرگىز زۇلۇم قىلغۇچى ئەمەستۇر» (دېيىلىدۇ)'),
(2606, 22, 11, 'بەزىلەر اﷲ قا شەك - شۈبھە بىلەن (يەنى تۇراقسىز ھالدا) ئىبادەت قىلىدۇ، ئەگەر ئۇنىڭغا ياخشىلىق يەتسە دىنىدا تۇرىدۇ (بۇنداق ئادەم مۇناپىق بولۇپ، اﷲ قا دىلى بىلەن ئەمەس پەقەت تىلى بىلەن ئىبادەت قىلىدۇ)، ئەگەر ئۇنىڭغا بەختسىزلىك يەتسە دىنىدىن يېنىۋالىدۇ، ئۇ دۇنيا ۋە ئاخىرەتتە زىيان تارتىدۇ، بۇ روشەن زىياندۇر'),
(2607, 22, 12, 'ئۇ اﷲ نى قويۇپ پايدا - زىيان يەتكۈزەلمەيدىغان بۇتقا چوقۇنىدۇ، بۇ چوڭقۇر گۇمراھلىقتۇر'),
(2608, 22, 13, 'ئۇ (مۇشرىكلارنىڭ قارىشى بويىچە بۇت قىيامەت كۈنى شاپائەت قىلىدۇ دېگەندىمۇ) ئەلۋەتتە زىيىنى پايدىسىدىن يېقىن بولغان نەرسىگە چوقۇنىدۇ، (ئۇ بۇت) راستتىنلا يامان مەدەتكاردۇر، راستتىنلا يامان ھەمراھتۇر'),
(2609, 22, 14, 'شۈبھىسىزكى، اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، اﷲ ھەقىقەتەن خالىغىنىنى قىلىدۇ (يەنى خالىغان ئادەمگە ساۋاب بېرىدۇ، خالىغان ئادەمگە ئازاب قىلىدۇ)'),
(2610, 22, 15, 'كىمكى اﷲ پەيغەمبەرگە دۇنيا ۋە ئاخىرەتتە ھەرگىز ياردەم بەرمەيدۇ دەپ گۇمان قىلىدىكەن، ئۇ تورۇسقا ئارغامچا سېلىپ بوغۇلۇپ ئالسۇن، قارىسۇنكى، ئۇنىڭ (بۇ) تەدبىرى غەزىپىنى باسالامدۇ؟ (يەنى اﷲ پەيغەمبىرى مۇھەممەد ئەلەيھىسسالامغا ياردەم بەرمەيدۇ دەپ ئويلايدىكەن ئۇ ئېسىلىپ ئۆلۈۋالسۇن، ئەگەر بۇ ئۇنىڭ خاپىلىقىنى باسالايدىغان بولسا، چۈنكى اﷲ پەيغەمبەرگە چوقۇم ياردەم بېرىدۇ)'),
(2611, 22, 16, 'شۇنىڭغا ئوخشاش (يەنى ئىلگىرىكى كىتابلارنى نازىل قىلغىنىمىزغا ئوخشاش). قۇرئاننى (مۇھەممەد ئەلەيھىسسالامغا) روشەن ئايەتلەر قىلىپ نازىل قىلدۇق، اﷲ خالىغان ئادەمنى ھىدايەت قىلىدۇ (يەنى اﷲ تىن باشقا ھېچ ھىدايەت قىلغۇچى يوق، اﷲ خالىغان ئادەمنى توغرا يولغا باشلايدۇ'),
(2612, 22, 17, 'مۆمىنلەر، يەھۇدىيلار، يۇلتۇزلارغا چوقۇنغۇچىلار، ناسارالار، مەجۇسىيلەر (يەنى ئاتەشپەرەسلەر) ۋە مۇشرىكلار ئۈچۈن قىيامەت كۈنى اﷲ ھەقىقەتەن ھۆكۈم چىقىرىدۇ، (يەنى اﷲ تائالا مۆمىنلەر بىلەن مەزكۇر بەش پىرقە ئارىسىدا توغرا ھۆكۈم چىقىرىپ، مۆمىنلەرنى جەننەتكە، كۇففارلارنى دوزاخقا كىرگۈزىدۇ)، اﷲ ھەقىقەتەن ھەممە نەرسىنى كۆرۈپ تۇرغۇچىدۇر'),
(2613, 22, 18, 'بىلمەمسەنكى، ئاسماندىكىلەر (يەنى پەرىشتىلەر)، زېمىندىكىلەر (يەنى ئىنسانلار، جىنلار ۋە زېمىندا ياشايدىغان بارلىق مەخلۇقاتلار)، كۈن، ئاي، يۇلتۇزلار، تاغلار، دەرەخلەر، ھايۋاناتلار اﷲ قا بويسۇنۇپ، اﷲ نىڭ تەسەررۇپ قىلىشى بويىچە ھەرىكەت قىلىدۇ) ۋە نۇرغۇن كىشىلەر اﷲ قا سەجدە قىلىدۇ، نۇرغۇن كىشىلەر (كۇفرى سەۋەبلىك) ئازابقا دۇچار بولدى، اﷲ نىڭ خارلىشىغا ئۇچرىغان ئادەمگە ھۆرمەتلىگۈچى چىقمايدۇ، اﷲ ھەقىقەتەن خالىغىنىنى قىلىدۇ (يەنى ئازاب قىلىش، رەھىم قىلىش، ئەزىز قىلىش، خار قىلىش، باي قىلىش، كەمبەغەل قىلىش اﷲ نىڭ خاھىشىدىكى ئىش بولۇپ، اﷲ قا ھېچ كىشى تەئەررۇز قىلالمايدۇ)'),
(2614, 22, 19, '(مۆمىنلەر ۋە كۇففارلاردىن ئىبارەت) بۇ ئىككى (پىرقە) پەرۋەردىگارى توغرىسىدا مۇنازىرىلەشتى (يەنى مۆمىنلەر بىلەن كۇففارلار اﷲ نىڭ دىنى ئۈستىدە بەس - مۇنازىرە قىلىشىپ، مۆمىنلەر اﷲ نىڭ دىنىغا ياردەم بەرمەكچى بولدى، كۇففارلار اﷲ نىڭ نۇرىنى ئۆچۈرمەكچى بولدى)، كاپىرلارغا ئوتتىن كىيىملەر پىچىلىدۇ، ئۇلارنىڭ باشلىرى ئۈستىدىن يۇقىرى ھارارەتلىك قايناقسۇ قويۇلىدۇ'),
(2615, 22, 20, 'ئۇنىڭ بىلەن ئۇلارنىڭ ئىچ –باغرى ۋە تېرىلىرى ئېرىتىلىدۇ'),
(2616, 22, 21, 'ئۇلار تۆمۈر - توقماقلار بىلەن ئۇرۇلىدۇ'),
(2617, 22, 22, 'ھەر قاچان ئۇلار (يېتىۋاتقان) غەم - قايغۇنىڭ قاتتىقلىقىدىن دوزاختىن چىقماقچى بولسا، ئۇلار دوزاخقا قايتۇرۇلىدۇ، (ئۇلارغا) كۆيدۈرگۈچى (دوزاخ) ئازابىنى تېتىڭلار (دېيىلىدۇ)'),
(2618, 22, 23, 'اﷲ ھەقىقەتەن مۆمىنلەرنى ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇلار جەننەتلەردە ئالتۇن بىلەيزۈكلەرنى ۋە مەرۋايىتلارنى زىننەت بۇيۇملىرى قىلىدۇ، ئۇلارنىڭ كىيىمى يىپەكتىن بولىدۇ'),
(2619, 22, 24, 'ئۇلار ياخشى سۆزگە (يەنى كەلىمە تەۋھىدكە) ھىدايەت قىلىندى. اﷲ نىڭ يولىغا ھىدايەت قىلىندى'),
(2620, 22, 25, 'كاپىرلارغا، (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقۇچىلارغا، - ئۇ ئولتۇرۇشلۇق بولسۇن ياكى سىرتتىن كەلگەن بولسۇن - بىز كىشىلەرگە ئىبادەتگاھ قىلغان مەسجىدى ھەرەمدىن توسقۇچىلارغا (يەنى ھەج پەرزنى ئادا قىلىش ئۈچۈن مەسجىدى ھەرەمغا كەلگەن مۆمىنلەرنى توسقۇچىلارغا) (قاتتىق ئازابنى تېتىتىمىز)، كىمكى مەسجىدى ھەرەمدا زۇلۇم بىلەن گۇناھ قىلماقچى بولىدىكەن، ئۇنىڭغا قاتتىق ئازابنى تېتىتىمىز'),
(2621, 22, 26, 'ئۆز ۋاقتىدا ئىبراھىمغا بەيتۇللاھنىڭ ئورنىنى تەيىنلەپ بەردۇق، (ئۇنىڭغا ئېيتتۇقكى) «ماڭا ھېچ نەرسىنى شېرىك كەلتۈرمىگىن، تاۋاپ قىلغۇچىلارغا، قىيام قىلغۇچىلارغا، رۇكۇ قىلغۇچىلارغا، سەجدە قىلغۇچىلارغا مىنىڭ ئۆيۈمنى پاك قىلغىن'),
(2622, 22, 27, 'كىشىلەرنىڭ ئارىسىدا (ئۇلارنى) ھەجىگە چاقىرىپ نىدا قىلغىن، ئۇلار پىيادە ۋە ئۇرۇق تۆگىلەرگە مىنىپ كېلىدۇ، ئۇرۇق تۆگىلەر يىراق يوللارنى بېسىپ كېلىدۇ'),
(2623, 22, 28, 'كىشىلەر ئۆزلىرىگە تېگىشلىك بولغان (دىنى ۋە دۇنياۋى) مەنپەئەتلەرنى كۆرسۇن، بەلگىلەنگەن كۈنلەردە (يەنى قۇربانلىق كۈنلىرىدە) اﷲ ئۇلارغا رىزىق قىلىپ بەرگەن چارۋا ماللارنى (يەنى تۆگە، قوي، ئۆچكىلەرنى اﷲ نىڭ نېمەتلىرىگە شۈكۈر قىلىش يۈزىسىدىن) اﷲ نىڭ ئىسمىنى ئېيتىپ قۇربانلىق قىلسۇن». سىلەر قۇربانلىقلارنى گۆشىدىن يەڭلار، موھتاجقا، پېقىرغا بېرىڭلار'),
(2624, 22, 29, 'ئاندىن ئۇلار كىرلىرىنى تازىلىسۇن (يەنى ئېھرامدىن چىققاندىن كېيىن چاچلىرىنى، تىرناقلىرىنى ئالسۇن)، ئۆز ئۈستىگە ئالغان ئىبادەتلىرىنى ئادا قىلسۇن، قەدىمىي بەيت (يەنى بەيتۇللاھنى) تاۋاپ قىلسۇن'),
(2625, 22, 30, '(ھەج ئىبادىتى) ئەنە شۇدۇر، كىمكى اﷲ نىڭ دىنىنىڭ ئەھكاملىرىنى ئۇلۇغلىسا (يەنى ھەج جەريانىدا اﷲ نىڭ بۇيرۇغانلىرىنى بەجا كەلتۈرۈپ، مەنئى قىلغان ئىشلىرىدىن چەكلەنسە)، بۇ، پەرۋەردىگارنىڭ دەرگاھىدا ئۇنىڭ (دۇنيا ۋە ئاخىرەتلىكى) ئۈچۈن ياخشىدۇر، سىلەرگە چاھارپايلاردىن قۇرئاندا (ھاراملىقى) بايان قىلىنغانلىرىدىن باشقىلىرى ھالال قىلىندى. سىلەر بۇتلاردىن ئىبارەت نىجىستىن ساقلىنىڭلار، يالغان گۇۋاھلىق بېرىشتىن ساقلىنىڭلار'),
(2626, 22, 31, 'اﷲ قا خالىس ئىبادەت قىلىڭلار، ئۇنىڭغا شېرىك كەلتۈرمەڭلار، كىمكى اﷲ قا شېرىك كەلتۈرىدىكەن، ئۇ گويا ئاسماندىن تاشلىنىپ، قۇشلار ئۇنى ئېلىپ قاچقاندەك، ياكى بوران ئۇنى ئېلىپ بېرىپ يىراق جايغا تاشلىۋەتكەندەك بولۇپ قالىدۇ'),
(2627, 22, 32, 'ئىش مانا شۇنداق، كىمكى دىنىي ئىشلارنى (جۈملىدىن ھەجنىڭ ئەمەللىرىنى قۇربانلىقلارنى) ئۇلۇغلايدىكەن، بۇ، دىللارنىڭ تەقۋادارلىقىدىندۇر'),
(2628, 22, 33, 'مۇئەييەن مۇددەتكىچە (يەنى بوغۇزلايدىغان ۋاقىتقىچە قۇربانلىق ماللىرىدىن (سېغىپ، نەسىللەندۈرۈپ، مىنىپ) پايدىلىنىسىلەر، ئاندىن ئۇنى بوغۇزلاشقا تېگىشلىك جاي بەيتۇللاھنىڭ يېنىدۇر (يەنى (يەنى ھەرەمدۇر)'),
(2629, 22, 34, 'ھەر ئۈممەت. اﷲ ئۇلارغا رىزىق قىلىپ بەرگەن چارۋىلارنى (شۈكۈر قىلىش يۈزىسىدىن قۇربانلىق قىلغانلىرىدا) اﷲ نىڭ ئىسمىنى ئېيتسۇن دەپ، ئۇلارغا قۇربانلىقنى بەلگىلىدۇق، سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، ئۇنىڭغا بويسۇنۇڭلار، ئىتائەتمەنلەرگە خۇش خەۋەر بەرگىن'),
(2630, 22, 35, 'ئۇلار شۇنداق كىشىلەركى اﷲ ياد ئېتىلسە، ئۇلارنىڭ دىللىرى قورقۇپ (تىترەيدۇ)، ئۆزلىرىگە يەتكەن ئەزىيەتلەرگە سەۋر قىلىدۇ، ناماز ئۆتەيدۇ، ئۇلارغا بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن (ياخشىلىق يوللىرىغا) بېرىدۇ'),
(2631, 22, 36, '(كەبىگە ئېلىپ بېرىلىدىغان) تۆگىنى اﷲ نىڭ (دىننىڭ) ئالامەتلىرىدىن (يەنى ھەجنىڭ ئەھكاملىرىدىن) قىلدۇق، ئۇلاردا سىلەرگە نۇرغۇن پايدا بار، ئۇلار قاتار قىلىپ تۇرغۇزۇپ (يەنى ئۇلارنىڭ ئالدى سول پۇتىنى باغلاپ، ئۈچ پۇت بىلەن تۇرغۇزۇپ بوغۇزلىغىنىڭلاردا) اﷲ نىڭ ئىسمىنى تىلغا ئېلىڭلار (يەنى بىسمىللا دەڭلار)، ئۇلار بوغۇزلىنىپ جېنى چىققاندا، ئۇلارنى يەڭلار، قانائەتچان موھتاجلارغا ۋە سائىللارغا بېرىڭلار، سىلەرنى شۈكۈر قىلسۇن دەپ ئۇ تۆگىلەرنى سىلەرگە بويسۇندۇرۇپ بەردۇق'),
(2632, 22, 37, 'اﷲ قا ئۇلارنىڭ گۆشلىرى ۋە قانلىرى يېتىپ بارمايدۇ، اﷲ قا يېتىدىغىنى پەقەت سىلەرنىڭ تەقۋادارلىقىڭلاردۇر، اﷲ نىڭ سىلەرنىڭ ئۆزىنىڭ ئەھكاملىرىغا يېتەكلىگەنلىكىنى ئۇلۇغلىشىڭلار ئۈچۈن، اﷲ ئۇلارنى سىلەرگە ئاشۇنداق بويسۇندۇرۇپ بەردى. ياخشى ئىش قىلغۇچىلارغا (ئاخىرەتتە بەختكە ئېرىشىدىغانلىقى بىلەن) خۇش خەۋەر بەرگىن'),
(2633, 22, 38, 'اﷲ مۆمىنلەرنى چوقۇم قوغدايدۇ، اﷲ ھەر قانداق خىيانەتچى، ناشۈكۈر بەندىنى دوست تۇتمايدۇ'),
(2634, 22, 39, 'ھۇجۇم قىلىنغۇچىلارغا، زۇلۇمغا ئۇچرىغانلىقلىرى ئۈچۈن، (قارشىلىق كۆرسىتىشكە) رۇخسەت قىلىنىدى، اﷲ ئۇلارغا ياردەم بېرىشكە ئەلۋەتتە قادىر'),
(2635, 22, 40, '(ئۇلار) پەقەت پەرۋەردىگارىمىز اﷲ دېگەنلىكلىرى ئۈچۈنلا ئۆز يۇرتلىرىدىن ناھەق ھەيدەپ چىقىرىلدى، ئەگەر اﷲ ئىنسانلارنى بىر - بىرىگە قارشىلىق كۆرسەتكۈزمىگەن بولسا، راھىبلارنىڭ ئىبادەتخانىلىرى، چېركاۋلار، يەھۇدىيلارنىڭ ئىبادەتخانىلىرى ۋە اﷲ نىڭ نامى كۆپ يادلىنىدىغان مەسجىدلەر ئەلۋەتتە ۋەيران قىلىناتتى، كىمكى اﷲ نىڭ دىنىغا ياردەم بېرىدىكەن، ئەلۋەتتە اﷲ ئۇنىڭغا ياردەم بېرىدۇ، اﷲ ئەلۋەتتە كۈچلۈكتۇر، غالىبتۇر'),
(2636, 22, 41, 'اﷲ نىڭ ياردىمىگە ئېرىشىشكە ھەقلىق بولغانلار) شۇنداق كىشىلەردۇركى، ئەگەر ئۇلارنى يەر يۈزىدە ئۈستۈنلۈككە ئىگە قىلساق، نامازنى ئادا قىلىدۇ، زاكات بېرىدۇ، ياخشى ئىشلارغا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، ئىشلارنىڭ ئاقىۋىتى اﷲ قا مەنسۇپتۇر (يەنى ھەممە ئىشلار ئاخىرەتتە اﷲ قا قايتىدۇ)'),
(2637, 22, 42, 'ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى، ئىبراھىمنىڭ قەۋمى، لۇتنى (قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن'),
(2638, 22, 43, 'ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى، ئىبراھىمنىڭ قەۋمى، لۇتنى (قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن'),
(2639, 22, 44, 'ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى، ئىبراھىمنىڭ قەۋمى، لۇتنى (قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن'),
(2640, 22, 45, 'نۇرغۇن شەھەر ئاھالىلىرى زالىم بولغانلىقلىرى ئۈچۈن (شەھەرنىڭ ئۆيلىرىنىڭ) ئۆگزىلىرى ئۆرۈلۈپ، ئۈستىگە تاملىرى يىقىلغان نۇرغۇن تاشلاندۇق قۇدۇقلار ۋە ئېگىز ئىمارەتلەر (ئاھالىسىز) قالغان'),
(2641, 22, 46, 'ئۇلار زېمىندا سەير قىلمىدىمۇ؟ شۇنىڭ بىلەن ئۇلار (يەنى سەير قىلىپ) چۈشىنىدىغان دىللارغا، ياكى ئاڭلايدىغان قۇلاقلارغا ئىگە بولمىدىمۇ؟ ھەقىقەتەن كۆزلەرلا كور بولمايدۇ، لېكىن كۆكرەكلەردىكى قەلبلەر كور بولىدۇ (يەنى ھەقىقىي كورلۇق كۆزنىڭ كورلۇقى ئەمەس، دىلنىڭ كورلۇقىدۇر، كور ئادەم ئىبرەت ئالمايدۇ ۋە چۈشەنمەيدۇ)'),
(2642, 22, 47, '(ئى مۇھەممەد!) ئۇلار (يەنى مۇشرىكلار) (مەسخىرە قىلىش يۈزىسىدىن سەندىن ئازابنىڭ تېز نازىل بولۇشنى تەلەپ قىلىدۇ (ئازاب چوقۇم نازىل بولىدۇ، لېكىن ئۇنىڭ نازىل بولۇشنىڭ مۇئەييەن ۋاقتى - سائىتى بولىدۇ)، اﷲ ۋەدىسىگە ھەرگىز خىلاپلىق قىلمايدۇ اﷲ بەندىلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)، ھەقىقەتەن پەرۋەردىگارىنىڭ دەرگاھىدىكى بىر كۈن سىلەر سانايدىغان مىڭ يىلچىلىكتۇر'),
(2643, 22, 48, 'نۇرغۇن شەھەر ئاھالىلىرى زالىم تۇرۇقلۇق، ئۇلارغا مەن مۆھلەت بەردىم (ئۇلار بۇ مۆھلەتكە مەغرۇر بولۇپ كەتتى)، ئاندىن (يەنى ئۇلارغا ئۇزاق مۇددەت مۆھلەت بەرگىنىمدىن كېيىن) ئۇلارنى جازالىدىم، ئاخىر قايتىدىغان جاي مېنىڭ دەرگاھىمدۇر (يەنى ئاخىر ھەممىسى ماڭا كېلىدۇ'),
(2644, 22, 49, '(ئى مۇھەممەد! اﷲ نىڭ ئازابىنىڭ تېز نازىل بولۇشىنى تەلەپ قىلغۇچىلارغا) ئېيتقىنكى، «ئى ئىنسانلار! سىلەرگە مەن ھەقىقەتەن ئاشكارا ئاگاھلاندۇرغۇچىمەن»'),
(2645, 22, 50, 'ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار مەغپىرەتكە ۋە ئېسىل رىزىققا (يەنى جەننەتكە) ئېرىشىدۇ'),
(2646, 22, 51, 'بىزنى ئاجىز بىلىپ ئايەتلىرىمىزنى يوققا چىقىرىش ئۈچۈن تىرىشقۇچىلار - ئەنە شۇلار ئەھلى دوزاختۇر'),
(2647, 22, 52, 'بىز سەندىن ئەۋەتكەن قايسىبىر رەسۇل، قايسىبىر پەيغەمبەر بولمىسۇن، ئۇ (اﷲ نازىل قىلغان ئايەتلەرنى) ئوقۇغان چاغدا، شەيتان ھامان ئۇنىڭ قىرائىتىگە (شۈبھە) سالىدۇ. اﷲ شەيتاننىڭ سالغان شۈبھىسىنى بەربات قىلغاندىن كېيىن ئۆزىنىڭ ئايەتلىرىنى مۇستەھكەم قىلىدۇ. اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(2648, 22, 53, '(اﷲ تائالانىڭ مۇنداق قىلىشى) دىللىرىدا (مۇناپىقلىق) ئىللىتى بارلارنى، دىللىرى قەساۋەتلىشىپ كەتكەنلەرنى (يەنى ئەبۇجەھل، نەزر، ئۆتبەگە ئوخشاش اﷲ نىڭ زىكرىگە دىلى ئېرىمەس كۇففارلارنى) شەيتاننىڭ سالغان شۈبھىسى بىلەن سىناش ئۈچۈندۇر، زالىملار (يەنى يۇقىرىدىكى مۇناپىقلار، مۇشرىكلار) ھەقىقەتەن (اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە) قاتتىق ئاداۋەتتىدۇر'),
(2649, 22, 54, '(اﷲ تائالانىڭ مۇنداق قىلىشى) ئىلىم ئەھلىلىرىنىڭ قۇرئاننىڭ پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەت ئىكەنلىكىنى بىلىشلىرى، شۇنىڭ ئۈچۈندۇر. اﷲ مۆمىنلەرنى ھەقىقەتەن توغرا يولغا باشلىغۇچىدۇر'),
(2650, 22, 55, 'كاپىرلار تاكى ئۇلارغا قىيامەت ئۇشتۇمتۇت كەلگۈچە ياكى ئۇلارغا قىيامەت كۈنىنىڭ ئازابى يەتكۈچە قۇرئاندىن ھامان شەكلىنىدۇ'),
(2651, 22, 56, 'بۇ كۈندە پادىشاھلىق (يالغۇز) اﷲ قا خاستۇر، اﷲ بەندىلەر ئارىسىدا (ئادىل) ھۆكۈم چىقىرىدۇ، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار (تۈرلۈك) نازۇنېمەتلەر بار جەننەتلەردە بولىدۇ'),
(2652, 22, 57, 'كاپىرلار ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار (دوزاختا) خار قىلغۇچى ئازابقا دۇچار بولىدۇ'),
(2653, 22, 58, 'اﷲ نىڭ يولىدا ھىجرەت قىلغانلارغا، ئاندىن (جىھاد قىلىپ) ئۆلتۈرۈلگەنلەرگە ياكى (كېسەل بىلەن) ئۆلگەنلەرگە اﷲ چوقۇم ياخشى رىزىق (يەنى جەننەتنىڭ مەڭگۈلۈك نېمەتلىرىنى) ئاتا قىلىدۇ. رىزىق بەرگۈچىلەرنىڭ ئەڭ ياخشىسى ھەقىقەتەن يالغۇز اﷲ دۇر'),
(2654, 22, 59, 'اﷲ ئۇلارنى ئەلۋەتتە ئۇلار مەمنۇن بولىدىغان جايغا (يەنى جەننەتكە) كىرگۈزىدۇ، اﷲ ھەقىقەتەن (ئۇلارنى مەمنۇن قىلىدىغان نەرسىلەرنى) بىلگۈچىدۇر، ھەلىمدۇر (يەنى ئۇلارنىڭ دۈشمەنلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)'),
(2655, 22, 60, 'ئىش مانا شۇنداق، كىمكى قانچىلىك زۇلۇمغا ئۇچراپ شۇنچىلىك ئىنتىقامىنى ئالغان بولسا، ئاندىن يەنە زۇلۇمغا ئۇچرىسا، اﷲ ئۇنىڭغا چوقۇم ياردەم بېرىدۇ، اﷲ ھەقىقەتەن ناھايىتى ئەپۇ قىلغۇچىدۇر، ناھايىتى مەغپىرەت قىلغۇچىدۇر'),
(2656, 22, 61, '(شۇ نەرسە اﷲ نىڭ قۇدرىتىنىڭ ئالامەتلىرىدىندۇركى) اﷲ كېچىنى كۈندۈزگە، كۈندۈزنى كېچىگە كىرگۈزىدۇ (شۇنىڭ بىلەن كېچە - كۈندۈزنىڭ ئۇزۇن - قىسقا بولۇشى پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىنى) كۆرۈپ تۇرغۇچىدۇر'),
(2657, 22, 62, 'بۇ (بۇ) (قۇدرەت) شۇنىڭ بۇنىڭ ئۈچۈندۇركى، اﷲ ھەقتۇر، (مۇشرىكلارنىڭ) اﷲ نى قويۇپ ئىبادەت قىلىدىغان نەرسىلىرى (يەنى بۇتلىرى) باتىلدۇر، اﷲ (ھەممە نەرسىدىن) ئۈستۈندۇر، بۈيۈكتۇر'),
(2658, 22, 63, 'اﷲ نىڭ بۇلۇتتىن يامغۇر ياغدۇرۇپ بېرىدىغانلىقىنى، شۇنىڭ بىلەن زېمىننى يېشىلزارلىققا ئايلاندۇرىدىغانلىقىنى كۆرمىدىڭمۇ؟ اﷲ ھەقىقەتەن ناھايىتى مېھرىباندۇر، ھەممىدىن خەۋەرداردۇر'),
(2659, 22, 64, 'ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، اﷲ ھەقىقەتەن ھەممىدىن بىھاجەتتۇر، ھەمدۇساناغا لايىقتۇر'),
(2660, 22, 65, 'اﷲ نىڭ يەر يۈزىدىكى نەرسىلەرنى، ئۆزىنىڭ ئەمرى بويىچە دېڭىزدا يۈرۈۋاتقان كېمىلەرنى سىلەرگە تويۇندۇرۇپ بەرگەنلىكىنى كۆرمىدىڭمۇ؟ (ئاسماننىڭ زېمىنغا چۈشۈپ كەتمەسلىكى ئۈچۈن، اﷲ ئاسماننى توختىتىپ تۇرىدۇ، پەقەت اﷲ نىڭ ئىزنى بىلەن (ئاسماننىڭ قىيامەت بولغان چاغدا) چۈشۈپ كېتىشى بۇنىڭدىن مۇستەسنا. اﷲ ئىنسانلارغا ھەقىقەتەن ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر'),
(2661, 22, 66, 'اﷲ سىلەرگە ھاياتلىق بېرىدۇ، ئاندىن سىلەرنىڭ جېنىڭلارنى ئالىدۇ، ئاندىن سىلەرنى تىرىلدۈرىدۇ، ئىنسان ھەقىقەتەن (اﷲ نىڭ نېمەتلىرىگە) ناشۈكۈرلۈك قىلغۇچىدۇر'),
(2662, 22, 67, 'ھەر ئۈممەت ئۈچۈن بىر شەرىئەت بېكىتتۇق، ئۇلار شۇ شەرىئەتكە ئەمەل قىلىدۇ، (مۇشرىكلار) شەرىئەت ئىشىدا سەن بىلەن جېدەللەشمىسۇن، كىشىلەرنى پەرۋەردىگارىڭنىڭ (ئىبادىتىگە) دەۋەت قىلغىن، سەن ھەقىقەتەن شەكسىز توغرا يولدىدۇرسەن'),
(2663, 22, 68, 'ئەگەر ئۇلار سەن بىلەن جېدەللەشسە، ئېيتقىنكى، ئېيتقىنكى «اﷲ سىلەرنىڭ قىلمىشىڭلارنى ئوبدان بىلىدۇ'),
(2664, 22, 69, 'اﷲ قىيامەت كۈنى سىلەر ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە ھۆكۈم چىقىرىدۇ»'),
(2665, 22, 70, 'بىلمەمسەنكى، اﷲ ئاسمان، زېمىندىكى شەيئىلەرنى بىلىپ تۇرىدۇ، بۇ ھەقىقەتەن لەۋھۇلمەھپۇزدا (يېزىلغان)، بۇ (يەنى بىلىش) ھەقىقەتەن اﷲ قا ئاسان'),
(2666, 22, 71, 'ئۇلار (يەنى قۇرەيش كۇففارلىرى) اﷲ نى قويۇپ، (ئىبادەت قىلىشنىڭ دۇرۇسلۇقىغا) اﷲ ھېچقانداق دەلىل چۈشۈرمىگەن نەرسىلەرگە ۋە ئۆزلىرى بىلمەيدىغان نەرسىلەرگە (يەنى بوتلارغا) چوقۇنىدۇ، زالىملارغا ھېچقانداق مەدەتكار يوق'),
(2667, 22, 72, 'كاپىرلارغا بىزنىڭ روشەن ئايەتلىرىمىز ئوقۇپ بېرىلسە، ئۇلارنىڭ چېھرىلىرىدە (قوشۇمىسى تۈرۈلگەنلىكتىن) ئىنكار قىلغانلىقىنى كۆرۈسەن، ئۇلارنىڭ بىزنىڭ ئايەتلىرىمىزنى ئوقۇپ بەرگۈچىلەرگە ھۇجۇم قىلغىلى تاس قىلغانلىقىنى كۆرۈسەن، ئېيتقىنكى، «سىلەرگە مەن بۇنىڭدىنمۇ يامانراقىنى ئېيتىپ بېرەيمۇ؟ ئۇ دوزاختۇر. اﷲ ئۇنىڭ كاپىرلارغا ۋەدە قىلدى، ئۇلار قايتىپ بارىدىغان جاي نېمىدېگەن يامان!»'),
(2668, 22, 73, 'ئى ئىنسانلار! (يەنى مۇشرىكلار جامائەسى!) بىر مىسال كەلتۈرۈلىدۇ، ئۇنىڭغا قۇلاق سېلىڭلار، شۈبھىسىزكى، سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلارنىڭ ھەممىسى يىغىلغان تەقدىردىمۇ بىر چىۋىننى يارىتالمايدۇ (ئەقلى بار ئادەم قانداقمۇ مۇشۇنداق بۇتلارنى مەبۇد قىلىۋېلىپ ئۇلارغا چوقۇنىدۇ!)، چىۋىن ئۇلارنىڭ بەدىنىدىن بىر نەرسىنى (يەنى بۇتقا سۈركەپ قويۇلغان خۇش پۇراق بىر نەرسىنى) ئېلىپ قاچسا، ئۇنى چىۋىندىن تارتىپ ئالالمايدۇ، بۇتمۇ ۋە (ئۇنىڭغا) چوقۇنغۇچىمۇ ئاجىزدۇر'),
(2669, 22, 74, 'ئۇلار (ئەرزىمەس بۇتلارنى اﷲ قا شېرىك قىلىۋېلىپ) اﷲ نى تېگىشلىك رەۋىشتە ئۇلۇغلاشمىدى، شۈبھىسىزكى، اﷲ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر'),
(2670, 22, 75, 'اﷲ پەرىشتىلەردىنمۇ (پەيغەمبەرلىرىگە ۋەھيى يەتكۈزۈش ئۈچۈن)، ئەلچىلەرنى تاللايدۇ، اﷲ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (قىلمىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر'),
(2671, 22, 76, 'اﷲ ئۇلارنىڭ ئالدىدىكى نەرسىلەرنى (يەنى ئۇلارنىڭ ھازىرقى ئەھۋالىنى ۋە كەلگۈسىدىكى ئىشلىرىنى) بىلىدۇ. (بەندىلەرنىڭ) ئىشلىرىنىڭ ھەممىسى اﷲ قا قايتۇرۇلىدۇ (اﷲ ئۇلارغا يا مۇكاپات، يا جازا بېرىدۇ)'),
(2672, 22, 77, 'ئى مۆمىنلەر! بەختكە ئېرىشىشىڭلار ئۈچۈن رۇكۇ قىلىڭلار، سەجدە قىلىڭلار (يەنى تەزەررۇ بىلەن ناماز ئوقۇڭلار)، (يالغۇز) پەرۋەردىگارىڭلارغا ئىبادەت قىلىڭلار، ياخشى ئىشلارنى (يەنى خىش - ئەقرىبالارغا سىلەر - رەھىم قىلىش، يېتىم - يېسىرلەرنىڭ بېشىنى سىلاش، كېچىسى تەھەججۇد نامىزى ئۆتەش قاتارلىق ئىشلارنى) قىلىڭلار'),
(2673, 22, 78, 'اﷲ نىڭ يولىدا (پۇل - مېلىڭلار بىلەن، جېنىڭلار بىلەن) تاقىتىڭلارنىڭ يېتىشىچە جىھاد قىلىڭلار، اﷲ (دىنغا پەيغەمبەرنى سىلەرگە خاس قىلدى). سىلەرگە دىندا ھېچقانداق مۈشكۈللۈكنى قىلمىدى (سىلەرنى سىلەر تاقەت قىلالايدىغان ئىشلارنى قىلىشقا تەكلىپ قىلمىدى). (بۇ) ئاتاڭلار ئىبراھىمنىڭ دىنىدۇر (بۇ توغرا دىن بولغانلىقى ئۈچۈن، ئۇنى مەھكەم ئۇچلاڭلار)، اﷲ سىلەرنى ئىلگىرى (يەنى قۇرئاندىن ئىلگىرىكى كىتابلاردا) مۇسۇلمان دەپ ئاتىدۇ. قۇرئاندىمۇ شۇنداق ئاتىدۇ. (اﷲ) پەيغەمبەرنى سىلەرگە گۇۋاھ بولۇشقا ۋە سىلەرنى كىشىلەرگە گۇۋاھ بولۇشقا (تاللىدى)، ناماز ئوقۇڭلار، زاكات بېرىڭلار، اﷲ قا يېپىشىڭلار، اﷲ سىلەرنىڭ ئىگەڭلاردۇر، اﷲ نېمىدېگەن ياخشى ئىگە! نېمىدېگەن ياخشى مەدەتكار!');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'مۆمىنلەر ھەقىقەتەن بەختكە ئېرىشتى'),
(2675, 23, 2, '(شۇنداق مۆمىنلەركى) ئۇلار نامازلىرىدا (اﷲ نىڭ ئۇلۇغلۇقىدىن سۈر بېسىپ كەتكەنلىكتىن) ئەيمىنىپ تۇرغۇچىلاردۇر'),
(2676, 23, 3, 'ئۇلار بىھۇدە سۆز، بىھۇدە ئىشتىن يىراق بولغۇچىلاردۇر'),
(2677, 23, 4, 'ئۇلار زاكات بەرگۈچىلەردۇر'),
(2678, 23, 5, 'ئۇلار ئەۋرەتلىرىنى (ھارامدىن) ساقلىغۇچىلاردۇر'),
(2679, 23, 6, '(يەنى ئەۋرەتلىرىنى) پەقەت خوتۇنلىرىدىن، چۆرىلىرىدىن باشقىلاردىن ساقلىغۇچىلاردۇر (بۇلار بىلەن يېقىنچىلىق قىلغۇچىلار مالامەت قىلىنمايدۇ)'),
(2680, 23, 7, 'بۇنىڭ سىرتىدىن (جىنسىي تەلەپنى قاندۇرۇشنى) تەلەپ قىلغۇچىلار ھەددىدىن ئاشقۇچىلاردۇر'),
(2681, 23, 8, 'ئۇلار (يەنى مۆمىنلەر) ئۆزلىرىگە تاپشۇرۇلغان ئامانەتلەرگە ۋە بەرگەن ئەھدىگە رىئايە قىلغۇچىلاردۇر'),
(2682, 23, 9, 'ئۇلار نامازلىرىنى (ۋاقتىدا تەئدىل ئەركان بىلەن) ئادا قىلغۇچىلاردۇر'),
(2683, 23, 10, 'ئەنە شۇلار (يەنى يۇقىرى سۈپەتلەرگە ئىگە مۆمىنلەر نازۇنېمەتلىك جەننەتنىڭ) ۋارىسلىرىدۇر'),
(2684, 23, 11, 'ئۇلار (يۇقىرى دەرىجىلىك جەننەت) فىردەۋسكە ۋارىسلىق قىلىدۇ، فىردەۋستە مەڭگۈ قالىدۇ'),
(2685, 23, 12, 'بىز ئىنساننى ھەقىقەتەن لاينىڭ جەۋھىرىدىن ياراتتۇق'),
(2686, 23, 13, 'ئاندىن ئۇنى بىر پۇختا قارارگاھ (يەنى بەچچىدان) دا (جايلاشقان) ئابىمەنىي قىلدۇق'),
(2687, 23, 14, 'ئاندىن ئابىمەنىينى لەختە قانغا ئايلاندۇردۇق، ئاندىن لەختە قاننى پارچە گۆشكە ئايلاندۇردۇق، ئاندىن پارچە گۆشنى سۆڭەككە ئايلاندۇردۇق، ئاندىن سۆڭەككە گۆش قوندۇردۇق، ئاندىن ئۇنى (جان كىرگۈزۈپ) باشقا مەخلۇققا ئايلاندۇردۇق، ئاڭ ماھىر ياراتقۇچى اﷲ (ھېكمەت ۋە قۇدرەت جەھەتتىن) ئۇلۇغدۇر'),
(2688, 23, 15, 'ئاندىن سىلەر چوقۇم ئۆلۈسىلەر'),
(2689, 23, 16, 'ئاندىن سىلەر ھەقىقەتەن قىيامەت كۈنى (قەبرەڭلاردىن) تۇرغۇزۇلىسىلەر'),
(2690, 23, 17, 'بىز ھەقىقەتەن سىلەرنىڭ ئۈستۈڭلاردا يەتتە قەۋەت ئاسماننى ياراتتۇق، بىز مەخلۇقاتتىن غاپىل ئەمەسمىز'),
(2691, 23, 18, 'بىز بۇلۇتتىن ھاجەتكە قاراپ يامغۇر ياغدۇردۇق، ئۇنى زېمىندا توختاتتۇق، ئۇنى قۇرۇتۇۋېتىشقا ئەلۋەتتە قادىرمىز'),
(2692, 23, 19, 'شۇ يامغۇر بىلەن سىلەرگە خورما باغچىلىرى ۋە ئۈزۈم باغچىلىرىنى يېتىشتۈرۈپ بەردۇق، سىلەر ئۈچۈن ئۇ باغلاردا نۇرغۇن مېۋىلەر بار، ئۇلاردىن يەيسىلەر'),
(2693, 23, 20, 'شۇ يامغۇر بىلەن تۇرسىنادىن چىقىدىغان (زەيتۇن) دەرىخىنى (ئۆستۈرۈپ بەردۇق)، ئۇنىڭدىن ماي چىقىدۇ، يېگۈچىلەر (نانلىرىنى شۇ ماي بىلەن) مايلايدۇ'),
(2694, 23, 21, 'چارۋىلاردا ھەقىقەتەن سىلەرگە ئىبرەت بار، ئۇلارنىڭ قورسىقىدىكى نەرسىلەر (يەنى سۈتلەر) بىلەن سىلەرنى سۇغىرىمىز، ئۇلاردا سىلەر ئۈچۈن نۇرغۇن مەنپەئەتلەر بار، ئۇلارنىڭ گۆشىنى يەيسىلەر'),
(2695, 23, 22, 'ئۇلار (يەنى تۆگە ۋە ئۆكۈزلەر) بىلەن (قۇرۇقلۇقتا)، كېمە بىلەن (دېڭىزدا) يۈك توشۇيسىلەر'),
(2696, 23, 23, 'بىز ھەقىقەتەن نۇھنى قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، نۇھ ئېيتتى: «ئى قەۋمىم! (يالغۇز اﷲ قا ئىبادەت قىلىڭلار، سىلەرگە ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوق، اﷲ نىڭ ئازابىدىن) قورقمامسىلەر؟»'),
(2697, 23, 24, 'ئۇنىڭ قەۋمىدىكى ئىمانسىز كاتتىلار ئېيتتى: «بۇ پەقەت سىلەرگە ئوخشاش بىر ئىنساندۇر، ئۇ سىلەردىن ئۈستۈن بولۇۋالماقچى، ئەگەر اﷲ (پەيغەمبەر ئەۋەتىشنى) خالىسا ئىدى، ئەلۋەتتە، پەرىشتىلەرنى ئەۋەتتى، بۇنداق سۆزنى بۇرۇنقى ئاتا - بوۋىلىرىمىزدىن ئاڭلىغان ئەمەسمىز'),
(2698, 23, 25, 'ئۇ پەقەت بىر مەجنۇندۇر، سىلەر ئۇنى بىر مەزگىل كۈتۈڭلار»'),
(2699, 23, 26, 'نۇھ (ئۇلارنىڭ ئىمان ئېيتىشىدىن ئۈمىدىنى ئۈزگەندىن كېيىن): «پەرۋەردىگارىم! ئۇلار مېنى يالغانغا چىقارغانلىقلىرى ئۈچۈن ماڭا ياردەم بەرگىن» دېدى'),
(2700, 23, 27, 'بىز نۇھقا: «بىزنىڭ كۆز ئالدىمىزدا ۋەھيىمىز بويىچە كېمە ياسىغىن، بىزنىڭ بۇيرۇقىمىز يېتىپ كەلگەن ۋە تونۇردىن سۇ ئېتىلىپ چىققان چاغدا، ھايۋانلاردىن (ئەركەك - چىشى بولۇپ) بىر جۈپتىن ئېلىۋالغىن، ئائىلەڭدىكىلەرنىمۇ ئېلىۋالغىن، ئۇلارنىڭ ئىچىدىكى ھالاك بولۇشقا ھۆكۈم قىلىنغانلار بۇنىڭدىن مۇستەسنا. (ئۆزلىرىگە) زۇلۇم قىلغانلار توغرىسىدا ماڭا سۆز ئاچمىغىن (يەنى ئۇلارغا شاپائەت قىلمىغىن)، ئۇلار چوقۇم (توپان بالاسىدا) غەرق قىلىنىدۇ» دەپ ۋەھيى قىلدۇق'),
(2701, 23, 28, 'سەن بىلەن بىللە بولغان مۆمىنلەر بىلەن كېمىگە چىققان چېغىڭدا: «جىمى ھەمدۇسانا بىزنى زالىم قەۋمدىن قۇتقۇزغان اﷲ قا خاستۇر» دېگىن'),
(2702, 23, 29, 'ھەمدە: «ئى پەرۋەردىگارىم! مېنى مۇبارەك مەنزىلگە چۈشۈرگىن، سەن (دوستلىرىڭنى) ئەڭ ياخشى ئورۇنلاشتۇرغۇچىسەن» دېگىن'),
(2703, 23, 30, 'ھەقىقەتەن بۇنىڭدا (يەنى نۇھنىڭ ئۈممىتىنىڭ ئىشلىرىدا ئەقىل ئىگىلىرى ئۈچۈن) نۇرغۇن ئىبرەتلەر بار، بىز بەندىلەرنى (پەيغەمبەرلەرنى ئەۋەتىش بىلەن) ئەلۋەتتە سىنايمىز'),
(2704, 23, 31, 'ئۇلارنى ھالاك قىلغاندىن كېيىن باشقا بىر قەۋم پەيدا قىلدۇق'),
(2705, 23, 32, 'بىز ئۇلارغا ئۆزلىرىنىڭ ئىچىدىن بىر پەيغەمبەرنى (يەنى ھۇدنى) ئەۋەتتۇق. (ئۇ ئېيتتى) اﷲ غىلا ئىبادەت قىلىڭلار، سىلەرگە ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر ئاللانىڭ (ئازابىدىن) قورقمامسىلەر؟'),
(2706, 23, 33, 'ئۇنىڭ قەۋمىنىڭ كاپىر بولغان، ئاخىرەت مۇكاپاتىنى يالغان چىقارغان ۋە بۇ دۇنيادا بىز تەرەپتىن باياشات تۇرمۇشقا ئىگە قىلىنغان كاتتىلىرى ئېيتتى: «بۇ پەقەت سىلەرگە ئوخشاش بىر ئىنساندۇر، ئۇ سىلەر يېگەننى يەيدۇ، سىلەر ئىچكەننى ئىچىدۇ.'),
(2707, 23, 34, 'ئەگەر سىلەر ئۆزۈڭلارغا ئوخشاش بىر ئىنسانغا ئىتائەت قىلساڭلار، شۈبھىسىزكى، ئۇ چاغدا سىلەر چوقۇم زىيان تارتقۇچىلارسىلەر'),
(2708, 23, 35, 'ئۇ سىلەرنى سىلەر ئۆلۈپ توپىغا ۋە قۇرۇق ئۇستىخانغا ئايلانغاندىن كېيىن تىرىلىسىلەر دەپ ئاگاھلاندۇرامدۇ؟'),
(2709, 23, 36, 'سىلەرگە ئاگاھلاندۇرۇلغان ئىش تولىمۇ يىراقتۇر'),
(2710, 23, 37, 'بىزنىڭ مۇشۇ دۇنيادىكى ھاياتىمىزدىن باشقا ھايات يوقتۇر، بىز ئۆلىمىز ۋە تىرىلىمىز (يەنى بىر تەرەپتىن ئۆلۈپ تۇرىمىز ۋە بىر تەرەپتىن تۇغۇلۇپ تۇرىمىز)، بىز قايتا تىرىلمەيمىز'),
(2711, 23, 38, 'ئۇ پەقەت اﷲ نامىدىن يالغاننى توقۇغان ئادەملەردۇر، بىز ئۇنىڭغا ئىشەنمەيمىز»'),
(2712, 23, 39, 'ھۇد ئېيتتى: «پەرۋەردىگارىم! ئۇلار مېنى يالغانغا چىقارغانلىقلىرى ئۈچۈن ماڭا ياردەم بەرگىن»'),
(2713, 23, 40, 'اﷲ ئېيتتى: «ئۇلار ئۇزۇنغا قالماي (كاپىر بولغانلىقلىرىغا) نادامەت چېكىدۇ»'),
(2714, 23, 41, 'ئۇلار ھەقلىق رەۋىشتە قاتتىق ئاۋاز بىلەن ھالاك بولدى. ئۇلارنى بىز لۆجە قىلىۋەتتۇق، زالىم قەۋم اﷲ نىڭ رەھمىتىدىن) يىراق بولسۇن'),
(2715, 23, 42, 'ئۇلارنى ھالاك قىلغاندىن كېيىن، باشقا بىر قەۋملەرنى پەيدا قىلدۇق'),
(2716, 23, 43, 'ھەر قانداق ئۈممەت ھالاك بولۇش ۋاقتى كەلمەي تۇرۇپ ھالاك بولمايدۇ، ھالاك بولۇش ۋاقتىدىن ئۆتۈپمۇ كەتمەيدۇ'),
(2717, 23, 44, 'ئاندىن بىزنىڭ پەيغەمبەرلىرىمىزنى ئارقىمۇ ئارقا ئەۋەتتۇق، ھەر ئۈممەتكە ئۇنىڭ پەيغەمبىرى كەلگەن چاغدا، ئۇلار ئۇنى ئىنكار قىلغانلىقتىن، ئۇلارنى ئارقىمۇ ئارقا ھالاك قىلدۇق، ئۇلارنى (كىشىلەرگە) ھېكايە قىلىپ قالدۇردۇق، ئىمان ئېيتمايدىغان قەۋم اﷲ نىڭ رەھمىتىدىن) يىراق بولسۇن'),
(2718, 23, 45, 'ئاندىن مۇسا ۋە ئۇنىڭ قېرىندىشى ھارۇننى بىزنىڭ ئايەتلىرىمىز بىلەن ۋە روشەن پاكىت بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار ھاكاۋۇرلۇق قىلىپ (ئىماندىن) باش تارتتى، ئۇلارمۇ تەكەببۇر قەۋم ئىدى'),
(2719, 23, 46, 'ئاندىن مۇسا ۋە ئۇنىڭ قېرىندىشى ھارۇننى بىزنىڭ ئايەتلىرىمىز بىلەن ۋە روشەن پاكىت بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار ھاكاۋۇرلۇق قىلىپ (ئىماندىن) باش تارتتى، ئۇلارمۇ تەكەببۇر قەۋم ئىدى'),
(2720, 23, 47, 'ئۇلار: «بىزگە ئوخشاش ئىككى ئاددى ئىنسانغا ئىمان ئېيتامدۇق، ھالبۇكى، ئۇلارنىڭ قەۋمى بىزنىڭ (قۇلغا ئوخشاش) خىزمەتچىلىرىمىزدۇر» دېيىشتى'),
(2721, 23, 48, 'ئۇلار بۇ ئىككىسىنى (يەنى مۇسا بىلەن ھارۇننى) ئىنكار قىلىشتى. شۇنىڭ بىلەن ئۇلار ھالاك بولدى'),
(2722, 23, 49, 'ئۇلار (يەنى بەنى ئىسرائىل) نى ھىدايەت تاپسۇن دەپ، بىز ھەقىقەتەن مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق'),
(2723, 23, 50, 'بىز مەريەمنىڭ ئوغلىنى (يەنى ئىسانى) ۋە ئۇنىڭ ئانىسىنى (يەنى مەريەمنى) (كامال قۇدرىتىمىزنىڭ) دەلىلى قىلدۇق، ئۇلارنى تۈپتۈز، ئېقىن سۇلۇق بىر ئېگىز جايغا ئورۇنلاشتۇردۇق'),
(2724, 23, 51, 'ئى پەيغەمبەرلەر! ھالال نەرسىلەرنى يەڭلار، ياخشى ئەمەللەرنى قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ قىلغان ئەمەللىرىڭلارنى ئوبدان بىلىمەن'),
(2725, 23, 52, '(ئى پەيغەمبەرلەر جامائەسى!) سىلەرنىڭ دىنىڭلار ھەقىقەتەن بىر دىندۇر، مەن سىلەرنىڭ پەرۋەردىگارىڭلاردۇرمەن، (مېنىڭ ئازابىمدىن) قورقۇڭلار'),
(2726, 23, 53, 'ئۇلار (يەنى ئۈممەتلەر) دىنىي جەھەتتە نۇرغۇن پىرقىلەرگە بۆلۈندى، ھەر پىرقە ئۆز دىنى بىلەن خۇشالدۇر'),
(2727, 23, 54, 'ئۇلارنى ئۆلگەنلىرىگە قەدەر گۇمراھلىقتا تەرك ئەتكىن'),
(2728, 23, 55, '(ئۇلار) بىزنىڭ ئۇلارغا مال - مۈلۈك ۋە ئەۋلادلارنى بەرگەنلىكىمىزنى ئۆزلىرىگە تېز ياخشىلىق قىلغانلىق دەپ ئويلامدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلار ئۇقمايدۇ'),
(2729, 23, 56, '(ئۇلار) بىزنىڭ ئۇلارغا مال - مۈلۈك ۋە ئەۋلادلارنى بەرگەنلىكىمىزنى ئۆزلىرىگە تېز ياخشىلىق قىلغانلىق دەپ ئويلامدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلار ئۇقمايدۇ'),
(2730, 23, 57, 'ھەقىقەتەن پەرۋەردىگارىنىڭ ھەيۋىسىدىن قورققۇچى كىشىلەر،'),
(2731, 23, 58, 'پەرۋەردىگارىنىڭ ئايەتلىرىگە ئىشەنگۈچى كىشىلەر،'),
(2732, 23, 59, 'پەرۋەردىگارىغا شېرىك كەلتۈرمەيدىغان كىشىلەر،'),
(2733, 23, 60, 'سەدىقە بېرىدىغان ئەمما پەرۋەردىگارىنىڭ دەرگاھىغا (ھېساب بېرىش ئۈچۈن) قايتىپ بارىدىغانلىقىدىن دىللىرى قورقۇپ تۇرىدىغان كىشىلەر-'),
(2734, 23, 61, 'ئەنە شۇلار ياخشى ئىشلارنى (باشقىلارنىڭ) ئالدىدا قىلىشقا تىرىشقۇچىلاردۇر'),
(2735, 23, 62, 'بىز ئىنساننى پەقەت تاقىتى يېتىدىغان (ئىش) قىلا تەكلىپ قىلىمىز، بىزنىڭ دەرگاھىمىزدا ھەقنى سۆزلەيدىغان كىتاب (يەنى بەندىلەرنىڭ ياخشى - يامان ئىشلىرى خاتىرىلەنگەن نامە - ئەمال بار، ئۇلارغا (ساۋابىنى كېمەيتىۋېتىش ياكى ئازابنى ئاشۇرۇۋېتىش بىلەن) قىلچە زۇلۇم قىلىنمايدۇ'),
(2736, 23, 63, 'ئۇلار (يەنى گۇناھكار كۇففارلار) نىڭ دىللىرى بۇنىڭدىن (يەنى قۇرئاندىن) غەپلەتتىدۇر، ئۇنىڭدىن يەنى كۇفرىدىن) باشقا ئۇلار كېلەچەكتە نۇرغۇن (يامان) ئىشلارنى قىلىدۇ'),
(2737, 23, 64, 'ئۇلارنىڭ بايلىرىنى ئازاب بىلەن جازالىغان ۋاقتىمىزدا ناگاھ ئۇلار پەرياد قىلىدۇ'),
(2738, 23, 65, '(ئۇلارغا ئېيتىلىدۇ) «بۈگۈن پەرياد قىلماڭلار، ھەقىقەتەن سىلەر بىزنىڭ ياردىمىمىزگە ئېرىشەلمەيسىلەر»'),
(2739, 23, 66, 'مېنىڭ ئايەتلىرىم سىلەرگە ئوقۇپ بېرىلەتتى، (ئۇنى ئاڭلاشتىن يۈز ئۆرۈپ) ئارقاڭلارغا چېكىنىۋالاتتىڭلار'),
(2740, 23, 67, 'ئۇلار ھەرەم ئەھلى بولغانلىقى بىلەن پەخىرلىنىپ ئىماندىن يۈز ئۆرۈيدۇ، كەچلىك پاراڭلىرىدا يامان سۆزلەرنى قىلىدۇ، (يەنى قۇرئانغا تەنە قىلىدۇ، پەيغەمبەر ئەلەيھىسسالامنى سۆكىدۇ)'),
(2741, 23, 68, 'ئۇلار (ھەق) كالام (يەنى ئۇلۇغ قۇرئان) ئۈستىدە چوڭقۇر پىكىر قىلمىدىمۇ؟ ياكى ئۇلارغا (اﷲ تەرىپىدىن) ئاتا - بوۋىلىرىغا كەلمىگەن (يېڭى) نەرسە كەلدىمۇ؟'),
(2742, 23, 69, 'ياكى ئۇلار ئۆزلىرىگە كەلگەن ئەلچىنى تونۇماي ئۇنى ئىنكار قىلىۋاتامدۇ'),
(2743, 23, 70, 'ياكى ئۇلار ئۇنى مەجنۇن دېيىشەمدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلارغا ئۇ ھەقىقەتنى ئېلىپ كەلدى. ئۇلارنىڭ تولىسى ھەقىقەتنى يامان كۆرىدۇ'),
(2744, 23, 71, 'ئەگەر ھەقىقەت (يەنى قۇرئان) ئۇلارنىڭ نەپسى خاھىشلىرىغا بويسۇنىدىغان بولسا، ئەلۋەتتە ئاسمانلار، زېمىن ۋە ئۇلاردىكى مەخلۇقاتلار خاراب بولغان بولاتتى، ئۇنداق بولغىنى يوق، ئۇلارغا (ۋەز - نەسىھەتنى ئۆز ئىچىگە ئالغان) قۇرئاننى ئاتا قىلدۇق، ئۇلار ئۆزلىرىگە (ۋەز - نەسىھەت بولغان) قۇرئاندىن يۈز ئۆرۈيدۇ'),
(2745, 23, 72, 'ياكى سەن ئۇلاردىن (ئەلچىلىكنى يەتكۈزگەنلىكىڭگە) ھەق تەلەپ قىلامسەن؟ (يەنى ئۇلار شۇنداق گۇمان قىلامدۇ؟) پەرۋەردىگارىڭنىڭ (ساڭا بەرگەن) ئەجرى ياخشىدۇر، اﷲ رىزىق بەرگۈچىلەرنىڭ ياخشىسىدۇر'),
(2746, 23, 73, 'سەن ئەلۋەتتە ئۇلارنى توغرا يولغا دەۋەت قىلىسەن'),
(2747, 23, 74, 'ئاخىرەتكە ئىشەنمەيدىغانلار ھەقىقەتەن توغرا يولدىن چەتنىگۈچىلەردۇر'),
(2748, 23, 75, 'ئەگەر ئۇلار (يەنى مۇشرىكلار) غا رەھىم قىلساق، ئۇلارغا كەلگەن كۈلپەتنى كۆتۈرۈۋەتسەك، چوقۇم گۇمراھلىقلىرىدا داۋاملىق تېڭىرقاپ يۈرۈشەتتى'),
(2749, 23, 76, 'ئۇلارنى بىز ھەقىقەتەن ئازاب (يەنى قەھەتچىلىك) بىلەن جازالىدۇق، ئۇلار پەرۋەردىگارىغا بويسۇنمىدى، ئىلتىجا قىلىپ دۇئامۇ قىلمىدى'),
(2750, 23, 77, 'تاكى ئۇلارغا قاتتىق ئازابنىڭ دەرۋازىسىنى ئاچقان چېغىمىزدا ناگاھان ئۇلار ئۈمىدسىزلىنىپ كېتىدۇ'),
(2751, 23, 78, 'اﷲ سىلەر ئۈچۈن قۇلاقلارنى، كۆزلەرنى ۋە دىللارنى ياراتتى، سىلەر ناھايىتى ئاز شۈكۈر قىلىسىلەر'),
(2752, 23, 79, 'اﷲ سىلەرنى زېمىندا (نەسىللەندۈرۈش يولى بىلەن) ياراتتى، (ھېساب بېرىش ئۈچۈن) ئۇنىڭ دەرگاھىغا يىغىلىسىلەر'),
(2753, 23, 80, 'اﷲ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنىڭ) جېنىنى ئالىدۇ، كېچە - كۈندۈزنىڭ ئۆزگىرىپ تۇرۇشى اﷲ نىڭ باشقۇرۇشىدىدۇر، چۈشەنمەمسىلەر؟'),
(2754, 23, 81, '(ئۇلار اﷲ نىڭ قۇدرىتىنى ئويلاپ كۆرمىدى) بەلكى ئۇلار بۇرۇنقىلار نېمىدېگەن بولسا شۇنى دېدى'),
(2755, 23, 82, 'ئۇلار ئېيتتى: «بىز ئۆلۈپ توپىغا ۋە (ئۇۋۇلۇپ كەتكەن) سۆڭەككە ئايلانغاندىن كېيىن، چوقۇم قايتا تىرىلەمدۇق؟'),
(2756, 23, 83, 'ھەقىقەتەن بىز ۋە بىزنىڭ ئاتا - بوۋىلىرىمىز ئىلگىرى مۇشۇنداق ئاگاھلاندۇرۇلغان ئىدۇق، بۇ پەقەت قەدىمكىلەردىن قالغان ئەپسانىلاردۇر»'),
(2757, 23, 84, 'ئېيىتقىنكى، «زېمىن ۋە ئۇنىڭدىكى مەخلۇقاتلار كىمنىڭ؟ ئەگەر سىلەر بىلسەڭلار (ماڭا بۇنى ئېيتىپ بېرىڭلار)»'),
(2758, 23, 85, 'ئۇلار: «اﷲ نىڭ» دەيدۇ. (بۇنىڭدىن) پەند - نەسىھەت ئالمامسىلەر؟'),
(2759, 23, 86, 'ئېيىتقىنكى، «يەتتە ئاسماننىڭ ۋە ئۇلۇغ ئەرشنىڭ پەرۋەردىگارى كىم؟»'),
(2760, 23, 87, 'ئۇلار «اﷲ» دەيدۇ. ئېيىتقىنكى، «ئۇنىڭ (ئازابىدىن) قورقمامسىلەر؟»'),
(2761, 23, 88, 'ئېيىتقىنكى، «ھەممە شەيئىنىڭ پادىشاھلىقى كىمنىڭ قولىدا؟ (ئىلتىجا قىلغانلارغا) پاناھ بولالايدىغان ۋە ئۇنىڭغا قارشى ھېچ نەرسە پاناھ بولالمايدىغان كىم؟ ئەگەر سىلەر بىلسەڭلار (ماڭا بۇنى ئېيتىپ بېرىڭلار)»'),
(2762, 23, 89, 'ئۇلار: «اﷲ» دەيدۇ. ئېيىتقىنكى، «سىلەر قانداقمۇ قايمۇقتۇرۇلىسىلەر؟»'),
(2763, 23, 90, 'ئۇنداق ئەمەس، ئۇلارغا بىز ھەق سۆزنى ئېلىپ كەلدۇق، ئۇلار چوقۇم يالغانچىلاردۇر'),
(2764, 23, 91, 'اﷲ نىڭ بالىسى يوق، ئۇنىڭغا باراۋەر باشقا بىر ئىلاھمۇ يوق، ئەگەر بۇنداق بولسا ئىدى، ئۇ چاغدا ھەر ئىلاھ ئۆزى ياراتقان مەخلۇقنى يالغۇز ئىگىلەيتتى، بەزىسى بەزىسىدىن غالىب كېلەتتى، اﷲ ئۇلارنىڭ اﷲ قا نىسبەت بەرگەن نەرسىلىرىدىن پاكتۇر'),
(2765, 23, 92, 'اﷲ غەيبنى ۋە ئاشكارىنى بىلگۈچىدۇر، مۇشرىكلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن ئۈستۈندۇر'),
(2766, 23, 93, 'ئېيتقىنكى، «پەرۋەردىگارىم! ئەگەر ماڭا ئۇلارغا (دۇنيادا) ۋەدە قىلغان ئازابنى كۆرسىتىدىغان بولساڭ'),
(2767, 23, 94, 'ئېيتقىنكى: «پەرۋەردىگارىم! مېنى زالىم قەۋملەر ئىچىدە قىلمىغىن»'),
(2768, 23, 95, 'بىز ئۇلارغا ۋەدە قىلغان ئازابنى ساڭا كۆرسىتىشكە ئەلۋەتتە قادىرمىز'),
(2769, 23, 96, '(ئۇلارنىڭ قىلغان) يامانلىقىغا (ئەپۇ قىلىش بىلەن) ياخشىلىق قىلغىن، ئۇلارنىڭ (سېنى) سۈپەتلەشلىرىنى (يەنى ئۇلارنىڭ ساڭا قىلغان مەسخىرىسىنى) بىز ئوبدان بىلىمىز'),
(2770, 23, 97, 'ئېيتقىنكى، «پەرۋەردىگارىم! ساڭا سېغىنىپ شەيتانلارنىڭ ۋەسۋەسىلىرىدىن پاناھ تىلەيمەن'),
(2771, 23, 98, 'پەرۋەردىگارىم! ساڭا سېغىنىپ شەيتانلارنىڭ ماڭا ھەمراھ بولۇشىدىن پاناھ تىلەيمەن»'),
(2772, 23, 99, 'ئۇلارنىڭ (يەنى كاپىرلارنىڭ) بىرىگە ئۆلۈم كەلسە ئېيتىدۇركى، «پەرۋەردىگارىم! مېنى (دۇنياغا) قايتۇرغىن'),
(2773, 23, 100, 'مەن قويۇپ كەلگەن مال - مۈلكۈم بىلەن ياخشىلىق قىلىشىم مۇمكىن». (ئۇنىڭ دۇنياغا قايتىشى) مۇمكىن ئەمەس، ئۇ چوقۇم شۇ سۆزنى قىلغۇچىدۇر، ئۇلار قەبرىلىرىدىن تۇرغۇزۇلغان كۈنگە قەدەر ئۇلارنىڭ ئالدىدا (ئۇلارنى دۇنياغا قايتىشتىن توسىدىغان) بىر توسما بولىدۇ'),
(2774, 23, 101, 'سۇر چېلىنغان كۈندە ئۇلارنىڭ ئارىسىدىكى تۇغقانچىلىق مەۋجۇت بولماي قالىدۇ. بىر - بىرىنىڭ (ئەھۋالىنىمۇ) سوراشمايدۇ'),
(2775, 23, 102, 'تارازىسى ئېغىر كەلگەنلەر (يەنى ياخشىلىقلىرى يامانلىقلىرىنى بېسىپ كەتكەنلەر) بەختكە ئېرىشكۈچىلەردۇر'),
(2776, 23, 103, 'تارازىسى يېنىك كەلگەنلەر (يەنى يامانلىقلىرى ياخشىلىقلىرىنى بېسىپ كەتكەنلەر) زىيان تارتقۇچىلاردۇر، جەھەننەمدە مەڭگۈ قالغۇچىلاردۇر'),
(2777, 23, 104, 'ئۇلارنىڭ يۈزلىرىنى ئوت كۆيدۈرىدۇ، كالپۇكلىرى قورۇلۇپ، چىشلىرى ئېچىلىپ (بەت چىراي) بولۇپ قالىدۇ'),
(2778, 23, 105, '(ئۇلارغا) «سىلەرگە (دۇنيادا) مېنىڭ ئايەتلىرىم ئوقۇپ بېرىلگەن، سىلەر ئۇنى (شۇنچە روشەن تۇرۇقلۇق) ئىنكار قىلغان ئەمەسمىدىڭلار» دېيىلىدۇ'),
(2779, 23, 106, 'ئۇلار ئېيتىدۇ: «پەرۋەردىگارىمىز! بەتبەختلىكىمىز ئۈستىمىزدىن غالىب كېلىپ گۇمراھ قەۋم بولۇپ قالغان ئىدۇق'),
(2780, 23, 107, 'پەرۋەردىگارىمىز! بىزنى دوزاختىن چىقارغىن (ئاندىن دۇنياغا قايتۇرغىن)، ئەگەر بىز (شۇنىڭدىن كېيىن) يەنە گۇناھ قىلساق، بىز ھەقىقەتەن زالىم بولۇپ قالىمىز»'),
(2781, 23, 108, 'اﷲ ئېيتىدۇ: «جەھەننەمدە خار ھالدا قىلىڭلار، (ئۈستۈڭلاردىن ئازابنىڭ كۆتۈرۈلۈپ كېتىشى ھەققىدە) ماڭا سۆز ئاچماڭلار»'),
(2782, 23, 109, 'شۈبھىسىزكى، بەندىلىرىمدىن بىر تۈركۈم كىشىلەر: «پەرۋەردىگارىمىز! بىز ئىمان ئېيتتۇق، بىزگە مەغپىرەت قىلغىن، بىزگە رەھىم قىلغىن، سەن بولساڭ ئەڭ رەھىم قىلغۇچىسەن» دەيتتى'),
(2783, 23, 110, 'ئۇلارنى مەسخىرە قىلدىڭلار، ھەتتا ئۇلار سىلەرگە مېنىڭ زىكرىمنى ئۇنتۇلدۇردى (يەنى ئۇلارنى مەسخىرە قىلىش بىلەن بولۇپ كېتىپ ماڭا ئىتائەت قىلىشنى ئۇنتۇدۇڭلار)، سىلەر ئۇلاردىن كۈلەتتىڭلار'),
(2784, 23, 111, '(سىلەردىن يەتكەن ئەرزىيەتلەرگە) سەۋر قىلغانلىقلىرى ئۈچۈن، مەن ئۇلارنى بۈگۈن مۇكاپاتلىدىم، ئۇلار ھەقىقەتەن مەقسەتكە ئېرىشكۇچىلەردۇر'),
(2785, 23, 112, 'اﷲ (ئۇلارغا): «زېمىندا قانچىلىك تۇردۇڭلار؟» دەيدۇ'),
(2786, 23, 113, 'ئۇلار: «بىر كۈن ياكى بىر كۈندىنمۇ ئاز تۇردۇق، (ئاي - كۈنلەرنى) ھېسابلىغۇچى (پەرىشتە) دىن سورىغىن» دەيدۇ (ئۇلار دوزاختا چېكىۋاتقان ئازاب تۈپەيلىدىن، زېمىندا قانچىلىك تۇرغانلىقىنى ئۇنتۇيدۇ)'),
(2787, 23, 114, 'اﷲ: «ئەگەر سىلەر بىلسەڭلار (دۇنيادا) پەقەت ئازغىنا تۇردۇڭلار» دەيدۇ'),
(2788, 23, 115, 'ئى ئىنسانلار! بىزنى سىلەر ئۆزۈڭلارنى بىكار ياراتتى (يەنى سىلەر بىزنى ھايۋاناتلارنى ياراتقىنىمىزدەك ھېچقانداق ساۋابمۇ بەرمەيدىغان، جازامۇ بەرمەيدىغان قىلىپ ياراتتى). ئۆزۈڭلارنى بىزنىڭ دەرگاھىمىزغا قايتۇرۇلمايدۇ دەپ ئويلامسىلەر؟'),
(2789, 23, 116, 'ھەق - پادىشاھ اﷲ ئۈستۈندۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ ئۇلۇغ ئەرشنىڭ پەرۋەردىگارىدۇر'),
(2790, 23, 117, 'كىمكى ھېچقانداق دەلىلى بولمىغان ھالدا اﷲ قا قوشۇپ يەنە بىر مەبۇدقا ئىبادەت قىلىدىكەن، پەرۋەردىگارىنىڭ دەرگاھىدا ھېساب بېرىدۇ، كاپىرلار ھەقىقەتەن نىجات تاپمايدۇ'),
(2791, 23, 118, '«پەرۋەردىگارىم! مەغپىرەت قىلغىن، رەھىم قىلغىن، سەن رەھىم قىلغۇچىلارنىڭ ياخشىسىسەن» دېگىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, '(ئى مۇھەممەد!) بۇ بىر سۈرىدۇركى، ئۇنى بىز (ساڭا) نازىل قىلدۇق، ئۇنىڭدىكى (ئەھكاملارنى) پەرز قىلىپ بېكىتتۇق، (ئى مۆمىنلەر!) سىلەرنىڭ ۋەز - نەسىھەت ئېلىشىڭلار ئۈچۈن، ئۇنىڭدا (سىلەرنى ئەمەل قىلسۇن دەپ شەرىئەت ئەھكاملىرىنى ئۆز ئىچىگە ئالغان) روشەن ئايەتلەرنى نازىل قىلدۇق'),
(2793, 24, 2, 'زىنا قىلغۇچى ئايال ۋە زىنا قىلغۇچى ئەرنىڭ ھەر بىرىنى يۈز دەررىدىن ئۇرۇڭلار، ئەگەر سىلەر اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتىدىغان بولساڭلار، اﷲ نىڭ دىنىنىڭ (ئەھكامىنى ئىجرا قىلىشتا) ئۇلارغا رەھىم قىلماڭلار، ئۇلارنى جازالىغان چاغدا مۆمىنلەردىن بىر تۈركۈم كىشى ھازىر بولسۇن'),
(2794, 24, 3, 'زىنا قىلغۇچى ئەر پەقەت زىنا قىلغۇچى ئايالنى ياكى مۇشرىك ئايالنىلا ئالىدۇ، زىنا قىلغۇچى ئايال پەقەت زىنا قىلغۇچى ئەرگە ياكى مۇشرىككىلا تېگىدۇ، ئۇ (يەنى زىنا) مۆمىنلەرگە ھارام قىلىندى'),
(2795, 24, 4, 'ئىپپەتلىك ئاياللارنى زىنا بىلەن قارىلىغان، (بۇنىڭ راستلىقىغا ئادىل) تۆت گۇۋاھچىنى كەلتۈرەلمىگەن كىشىلەرنى 80 دەررە ئۇرۇڭلار (يەنى بىراۋغا زىنا بىلەن تۆھمەت چاپلىغان ھەربىر ئادەمنى قامچا ۋە شۇنىڭغا ئوخشاش نەرسىلەر بىلەن 80 نى ئۇرۇڭلار، چۈنكى ئۇلار ئەفىفە ئاياللارغا يالغاندىن تۆھمەت چاپلىغان ۋە كىشىلەرنىڭ ئابرۇيىنى تۆككەن ئادەملەردۇر)، ئۇلارنىڭ گۇۋاھلىقىنى (ئۇلار يالغانچىلىق ۋە تۆھمەت چاپلاش مەيدانىدا چىڭ تۇرىدىغانلا بولسا) ھەرگىز قوبۇل قىلماڭلار، ئۇلار پاسىقلاردۇر (يەنى ئۇلار بوھتان چاپلاشتىن ئىبارەت چوڭ گۇناھنى ئىشلىگەنلىكلىرى ئۈچۈن، اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر)'),
(2796, 24, 5, 'كېيىن تەۋبە قىلغان ۋە (ئەمەللىرىنى) تۈزەتكەنلەر (يەنى ئەفىفە ئاياللارغا قايتا تۆھمەت چاپلىمىغانلار) بۇنىڭدىن مۇستەسنا (ئۇلارنى كەچۈرۈڭلار، گۇۋاھلىقىنى قوبۇل قىلىڭلار)، چۈنكى اﷲ مەغپىرەت قىلغۇچىدۇر (يەنى بەندە تەۋبە قىلىپ ئۆزىنى تۈزەتسە، ئۇنى اﷲ مەغپىرەت قىلىدۇ)، تولىمۇ مېھرىباندۇر'),
(2797, 24, 6, 'خوتۇنلىرىنى زىنا بىلەن قارىلىغان ۋە ئۆزلىرىدىن باشقا گۇۋاھچىسى بولمىغانلار گۇۋاھلىق سۆزىنىڭ راست ئىكەنلىكىنى دەلىللەش ئۈچۈن، اﷲ نىڭ نامى بىلەن تۆت قېتىم قەسەم قىلسۇن'),
(2798, 24, 7, 'بەشىنچى قېتىمىدا: «ئەگەر يالغانچى بولسام ماڭا اﷲ نىڭ لەنىتى بولسۇن» دېسۇن'),
(2799, 24, 8, 'خوتۇن كىشى ئېرىنىڭ يالغانچى ئىكەنلىكىگە اﷲ نىڭ نامى بىلەن تۆت قېتىم قەسەم ئىچسە، جازاغا تارتىلىشتىن ساقلاپ قېلىنىدۇ'),
(2800, 24, 9, 'بەشىنچى قېتىمىدا، ئۇ ئەگەر راستچىللاردىن بولسا (يەنى ئېرىمنىڭ گېپى راست بولىدىغان بولسا) ماڭا اﷲ نىڭ غەزىپى بولسۇن دەپ قەسەم قىلىدۇ'),
(2801, 24, 10, 'ئەگەر سىلەرگە اﷲ نىڭ پەزلى ۋە رەھمىتى بولمىسا ئىدى (چوقۇم رەسۋا بولاتتىڭلار)، اﷲ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(2802, 24, 11, 'شۈبھىسىزكى، سىلەردىن بىر گۇرۇھ ئادەم (ئائىشەگە) بوھتان چاپلىدى. بۇ سىلەر ئۈچۈن يامان ئەمەستۇر، (سەۋر قىلغانلىقىڭلار بىلەن ساۋابقا ئېرىشىدىغانلىقىڭلار ئۈچۈن ۋە ئائىشە، سەفۋانلارنىڭ پاكلىقى ئاشكارا بولىدىغانلىقى ئۈچۈن) بەلكى ياخشىدۇر، بوھتان چاپلىغۇچىلاردىن كىمنىڭ قانچىلىك گۇناھى بولسا، ئۇنىڭغا شۇنچىلىك جازا بېرىلىدۇ، بوھتاننىڭ چوڭ قىسمىنى تارقاتقان ئادەم (يەنى ئابدۇللاھ ئىبن ئۇبەي) قاتتىق ئازابقا دۇچار بولىدۇ'),
(2803, 24, 12, 'بوھتاننى ئاڭلىغان چاغلىرىڭلاردا ئەر - ئايال مۆمىنلەر نېمىشقا ئۆزلىرىنى ياخشى دەپ قاراپ، بۇ ئېنىق بوھتان دېمىدى؟'),
(2804, 24, 13, 'ئۇلار نېمىشقا تۆت نەپەر گۇۋاھچىنى كەلتۈرمىدى؟ گۇۋاھچىلارنى كەلتۈرەلمىگەن ئىكەن، ئۇلار اﷲ نىڭ نەزىرىدە يالغانچى ھېسابلىنىدۇ'),
(2805, 24, 14, 'سىلەرگە دۇنيا ۋە ئاخىرەتتە اﷲ نىڭ پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، بوھتان چاپلىغىنىڭلار ئۈچۈن ئەلۋەتتە چوڭ ئازابقا دۇچار بولاتتىڭلار'),
(2806, 24, 15, 'ئۆز ۋاقتىدا بوھتاننى تىلىڭلار بىلەن تارقاتتىڭلار، بىلمەيدىغان نەرسەڭلارنى ئېغىزىڭلارغا ئالدىڭلار، ئۇنى سەل چاغلىدىڭلار، ھالبۇكى، اﷲ نىڭ نەزىرىدە، ئۇ چوڭ گۇناھتۇر'),
(2807, 24, 16, 'ئۇنى ئاڭلىغان چېغىڭلاردا، نېمىشقا مۇنداق سۆزلەرنى قىلىش بىزگە مۇناسىپ ئەمەس، (ئى اﷲ!) سەن پاكتۇرسەنكى، بۇ چوڭ بوھتاندۇر دېمىدىڭلار'),
(2808, 24, 17, 'اﷲ سىلەرگە نەسىھەت قىلىدۇكى، ئەگەر مۆمىن بولساڭلار، ھەرگىز قايتا مۇنداق سۆزلەرنى قىلماڭلار'),
(2809, 24, 18, 'اﷲ سىلەرگە (شەرىئەت ئەھكاملىرىنى ۋە گۈزەل ئەدەب - ئەخلاقلارنى كۆرسىتىپ بېرىدىغان) ئايەتلەرنى بايان قىلىدۇ، اﷲ (بەندىلەرگە پايدىلىق ئىشلارنى) ئوبدان بىلگۈچىدۇر، (تەدبىر قوللىنىشتا ۋە شەرىئىتىنى بەرپا قىلىشتا) ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(2810, 24, 19, 'مۆمىنلەر ئۈستىدە يامان سۆزلەرنىڭ تارىلىشىنى ياقتۇرىدىغان ئادەملەر، شۈبھىسىزكى، دۇنيا ۋە ئاخىرەتتە قاتتىق ئازابقا قالىدۇ، اﷲ (سىرلارنى ۋە نىيەتلەرنى) بىلىپ تۇرىدۇ، سىلەر بىلمەيسىلەر'),
(2811, 24, 20, 'اﷲ نىڭ سىلەرگە پەزلى ۋە مەرھەمىتى بولمىسا ئىدى (سىلەرنى قاتتىق جازالايتتى)، اﷲ شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر'),
(2812, 24, 21, 'ئى مۆمىنلەر! شەيتاننىڭ كەينىگە كىرمەڭلار، كىمكى شەيتاننىڭ كەينىگە كىرىدىكەن، شەيتان ئۇنى قەبىھ (سۆز - ھەرىكەتلەر) گە، يامان ئىشلارغا بۇيرۇيدۇ، اﷲ نىڭ سىلەرگە پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، سىلەردىن ھېچ ئادەم ئەبەدىي پاك بولماس ئىدى ۋە لېكىن اﷲ خالىغان بەندىسىنى پاك قىلىدۇ، اﷲ سىلەرنىڭ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىڭلارنى) بىلىپ تۇرغۇچىدۇر'),
(2813, 24, 22, 'ئاراڭلاردا ئەھلى كەرەم ۋە دۆلەتمەن بولغانلار خىش - ئەقرىبالارغا، مىسكىنلەرگە ۋە دىن يولىدا ھىجرەت قىلغانلارغا (بىر نەرسە) بەرمەسلىككە قەسەم قىلمىسۇن، (ئۇلارنىڭ گۇناھىنى) ئەپۇ قىلسۇن، كەچۈرسۇن، اﷲ نىڭ سىلەرگە مەغپىرەت قىلىشىنى ياقتۇرمامسىلەر؟ اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(2814, 24, 23, 'يامان ئىشتىن بىخەۋەر ئىپپەتلىك مۆمىن ئاياللارغا قارا چاپلايدىغانلار دۇنيا ۋە ئاخىرەتتە چوقۇم لەنەتكە ئۇچرايدۇ (يەنى اﷲ نىڭ رەھمىتىدىن يىراق قىلىنىدۇ)، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(2815, 24, 24, 'شۇ كۈندە (يەنى قىيامەت كۈنىدە) ئۇلارنىڭ تىللىرى، قوللىرى ۋە پۇتلىرى ئۇلارنىڭ قىلمىشلىرىدىن ئۇلارنىڭ زىيىنىغا گۇۋاھلىق بېرىدۇ'),
(2816, 24, 25, 'شۇ كۈندە اﷲ ئۇلارغا تولۇق تېگىشلىك جازاسىنى بېرىدۇ، ئۇلار اﷲ نىڭ ئاشكارا ھەق ئىكەنلىكىنى بىلىدۇ'),
(2817, 24, 26, 'يامان ئاياللار يامان ئەرلەرگە لايىقتۇر، يامان ئەرلەر يامان ئاياللارغا لايىقتۇر، ياخشى ئاياللار ياخشى ئەرلەرگە لايىقتۇر، ياخشى ئەرلەر ياخشى ئاياللارغا لايىقتۇر، ئەنە شۇلار ئۇلارنىڭ چاپلىغان بوھتانلىرىدىن پاكتۇر، ئۇلار مەغپىرەتتىن، (نازۇنېمەتلىك جەننەتلەردىكى) ئېسىل رىزىقتىن بەھرىمەن بولىدۇ'),
(2818, 24, 27, 'ئى مۆمىنلەر! باشقىلارنىڭ ئۆيلىرىگە (كىرىشكە) ئىجازەت سورىمىغىچە ۋە ئۆي ئىگىسىگە سالام بەرمىگىچە كىرمەڭلار، ۋەز - نەسىھەت ئېلىشىڭلار (يەنى بۇ گۈزەل ئەخلاق - ئەدەبكە ئەمەل قىلىشىڭلار) ئۈچۈن بۇ (يەنى ئىجازەت سوراپ ۋە سالام بېرىپ كىرىش ئۇشتۇمتۇت كىرىشتىن) سىلەرگە ياخشىدۇر'),
(2819, 24, 28, 'ئەگەر باشقىلارنىڭ ئۆيلىرىدە ھېچ ئادەم بولمىسا سىلەرگە ئىجازەت بېرىلمىگىچە كىرمەڭلار، ئەگەر سىلەرگە قايتىڭلار دېيىلسە قايتىپ كېتىڭلار، قايتىپ كېتىش سىلەر ئۈچۈن ئەڭ ياخشىدۇر، اﷲ سىلەرنىڭ قىلىدىغان ئەمەللىرىڭلارنى بىلىپ تۇرغۇچىدۇر'),
(2820, 24, 29, 'شەخسىي تۇرالغۇ بولمىغان (يەنى دۇكان - سارايلارغا ئوخشاش) ئۆيلەرگە ھاجىتىڭلار چۈشۈپ كىرسەڭلار، سىلەرگە ھېچ گۇناھ يوقتۇر اﷲ سىلەرنىڭ ئاشكارىلىغىنىڭلارنىمۇ، يوشۇرغىنىڭلارنىمۇ بىلىپ تۇرىدۇ'),
(2821, 24, 30, 'مۆمىن ئەرلەرگە ئېيتقىنكى، (نا مەھرەملەرگە) تىكىلىپ قارىمىسۇن، ئەۋرەتلىرىنى (زىنادىن) ساقلىسۇن، مۇنداق قىلىش ئۇلار ئۈچۈن ئەڭ ياخشىدۇر، اﷲ ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرىدىن تولۇق خەۋەردار'),
(2822, 24, 31, 'مۆمىنەلەرگە ئېيتقىنكى، نامەھرەملەرگە تىكىلىپ قارىمىسۇن، ئەۋرەتلىرىنى ياپسۇن، تاشقى زىننەتلىرىدىن باشقا زىننەتلىرىنى ئاشكارىلىمىسۇن، لېچەكلىرى بىلەن كۆكرەكلىرىنى ياپسۇن، (تاشقى زىننەتلىرىدىن باشقا) زىننەتلىرىنى ئەرلىرىدىن، ئاتىلىرىدىن، يا قېيىن ئاتىلىرىدىن، يا ئوغۇللىرىدىن، يا ئەرلىرىنىڭ ئوغۇللىرىدىن، يا ئۆز قېرىنداشلىرىدىن، يا قېرىنداشلىرىنىڭ ئوغۇللىرىدىن، يا ھەمشىرىلىرىنىڭ ئوغۇللىرىدىن، يا دىنداش ئاياللاردىن، يا قول ئاستىدىكى چۆرىلەردىن، يا خوتۇنلارغا ئېھتىياجى يوق خىزمەتچىلەر (يەنى قېرى، دەلدۈش بولغانلىقتىن جىنسىي شەھۋىتى يوقلار) دىن، يا ئاياللارنىڭ ئۇياتلىق جايلىرىنى ئۇقمايدىغان (يەنى بالاغەتكە يەتمىگەن) بالىلاردىن باشقا كىشىلەرگە كۆرسەتمىسۇن، زىننەتلىرىنى كىشىلەرگە بىلدۈرۈش ئۈچۈن ئاياغلىرىنى يەرگە ئۇرمىسۇن، ئى مۆمىنلەر! بەختكە ئېرىشىشىڭلار ئۈچۈن ھەممىڭلار اﷲ قا تەۋبە قىلىڭلار'),
(2823, 24, 32, 'ئاراڭلاردىكى بويتاق ئەر، ئاياللارنىڭ ۋە ياخشى قۇل ۋە ياخشى چۆرىلىرىڭلارنىڭ بېشىنى ئوڭلاپ قويۇڭلار؛ ئەگەر ئۇلار يوقسۇز بولىدىغان بولسا (ئۇلارنىڭ يوقسۇزلۇقى سىلەرنىڭ ئۇلارنىڭ بېشىنى ئوڭلاپ قويۇشۇڭلارغا توسۇق بولمىسۇن)، اﷲ ئۇلارنى ئۆز كەرىمى بىلەن باي قىلىدۇ، اﷲ نىڭ (كەرىمى) كەڭدۇر، اﷲ بەندىلەرنىڭ مەنپەئىتىنى بىلگۈچىدۇر'),
(2824, 24, 33, 'ئۆيلىنەلمەيدىغانلار اﷲ ئۇلارنى ئۆز كەرىمى بىلەن باي قىلغۇچە، ئۆزلىرىنى ئىپپەتلىك تۇتسۇن، قۇللىرىڭلاردىن توختام تۈزۈشنى تىلەيدىغانلار (يەنى خوجىسىغا مۇئەييەن پۇل - مال تۆلەش شەرتى بىلەن ئۆزىنىڭ ئازاد قىلىنىشىنى تىلەيدىغانلار) بىلەن، ئەگەر ئۇلارنىڭ ساداقىتىنى بايقىساڭلار، توختام تۈزۈڭلار، سىلەرگە اﷲ ئاتا قىلغان مال - مۈلۈكنىڭ بىر قىسمىنى (ئۆزلىرىنى ئازاد قىلىشقا ياردەم تەرىقىسىدە) ئۇلارغا بېرىڭلار، ئەگەر سىلەرنىڭ چۆرىلىرىڭلار ئىپپەتلىك بولۇشنى خالىسا، بۇ دۇنيانىڭ ئازغىنا مېلىنى دەپ ئۇلارنى پاھىشىغا مەجبۇرلىماڭلار، كىمكى ئۇلارنى پاھىشىغا مەجبۇرلايدىكەن، مەجبۇرلانغاندىن كېيىن اﷲ ئۇلارنى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) رەھىم قىلغۇچىدۇر (يەنى پاھىشىغا مەجبۇرلانغانلىقى ئۈچۈن، اﷲ ئۇلارنى جاۋابكارلىققا تارتمايدۇ، مەجبۇرلىغۇچىلارنى قاتتىق جازالايدۇ)'),
(2825, 24, 34, '(ئى مۆمىنلەر!) بىز سىلەرگە روشەن ئايەتلەرنى، سىلەردىن ئىلگىرى ئۆتكەن ئۈممەتلەردىن مىساللارنى ۋە تەقۋادارلار ئۈچۈن ۋەز – نەسىھەتلەرنى شەك – شۈبھىسىز نازىل قىلدۇق'),
(2826, 24, 35, 'اﷲ ئاسمانلارنىڭ ۋە زېمىننىڭ نۇرىدۇر، اﷲ نىڭ (مۆمىن بەندىسىنىڭ قەلبىدىكى) نۇرى خۇددى (چىراق قويىدىغان) تەكچىگە ئوخشايدۇ، ئۇنىڭدا چىراق باردۇر، چىراق شىشىنىڭ ئىچىدىدۇر، شىشە گويا نۇرلۇق يۇلتۇزدۇر، چىراق مۇبارەك زەيتۇن دەرىخىنىڭ (يېغى) بىلەن يورۇتۇلغان، ئۇ (يەنى زەيتۇن دەرىخى) شەرق تەرەپتىمۇ ئەمەس، غەرب تەرەپتىمۇ ئەمەس (سەھرادا ئوچۇقچىلىقتا بولۇپ، كۈن بويى ئۇنىڭغا كۈن نۇرى چۈشۈپ تۇرىدۇ، شۇنىڭ بىلەن ئۇنىڭ مېۋىسى پىشىپ يېتىلگەن بولىدۇ)، يېغى (ئەڭ سۈزۈك بولىدۇ، سۈزۈكلۈكىدىن) ئوت تەگمىسىمۇ يورۇپ كېتەيلا دەپ قالىدۇ، (ئوت بىلەن يورۇتۇلسا) نۇر ئۈستىگە نۇر قوشۇلىدۇ، اﷲ خالىغان كىشىنى ئۇنىڭ نۇرىغا (يەنى قۇرئانغا) (ئەگىشىشكە) مۇۋەپپەق قىلىدۇ، اﷲ (ئىبرەت ئالسۇن دەپ) كىشىلەرگە مىساللارنى (ئۇلارنىڭ پەمىگە يېقىنلاشتۇرۇپ) بايان قىلىدۇ، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر'),
(2827, 24, 36, 'اﷲ نىڭ ئۇلۇغلىنىشى ۋە ئىسمىنىڭ ياد ئېتىلىشى بۇيرۇلغان مەسجىدلەردە (مۆمىنلەر) ئەتىگەن – ئاخشامدا تەسبىھ ئېيتىپ تۇرىدۇ'),
(2828, 24, 37, 'ئۇلار شۇنداق ئەرلەركى، سودا - سېتىق ئۇلارنى اﷲ نى زىكىر قىلىشتىن، ناماز ئۆتەشتىن، زاكات بېرىشتىن غەپلەتتە قالدۇرمايدۇ، ئۇلار دىللار ۋە كۆزلەر قالايمىقانلىشىپ كېتىدىغان كۈن (يەنى قىيامەت كۈنى) دىن قورقىدۇ'),
(2829, 24, 38, 'اﷲ نى ئۆزلىرىنىڭ قىلغان ياخشى ئەمەللىرىگە مۇكاپات بەرسۇن ۋە ئۆزىنىڭ پەزلىدىن ئاشۇرۇپ بەرسۇن دەپ (ئۇلار شۇنداق ئىبادەتلەرنى قىلىدۇ) اﷲ خالىغان ئادەمگە ھېسابسىز رىزىق بېرىدۇ'),
(2830, 24, 39, 'كاپىرلارنىڭ (ياخشى ئەمەل دەپ ئويلىغان) ئەمەللىرى باياۋاندىكى سەرابقا ئوخشايدۇ، تەشنا ئادەم ئۇنى سۇ دەپ گۇمان قىلىدۇ، ئۇنىڭ يېنىغا كەلسە ھېچ نەرسە كۆرۈنمەيدۇ (يەنى سۇ كۆرمەيدۇ)، ئۇ اﷲ نىڭ ئۆز (ئەمەلىنىڭ) يېنىدا (كۆزىتىپ تۇرغۇچى) ئىكەنلىكىنى بايقايدۇ، اﷲ ئۇنىڭ قىلمىشىغا تېگىشلىك جازا بېرىدۇ، اﷲ تېز ھېساب ئالغۇچىدۇر'),
(2831, 24, 40, 'ياكى ئۇلارنىڭ (ياخشى دەپ گۇمان قىلغان) ئەمەللىرى چوڭقۇر دېڭىزدىكى قاراڭغۇلۇققا ئوخشايدۇ، ئۇنى (يەنى دېڭىزنى) قاتمۇقات دولقۇنلار ۋە دولقۇنلارنىڭ ئۈستىدىكى قارا بۇلۇتلار قاپلاپ تۇرىدۇ. (مانا بۇ) قاتمۇقات قاراڭغۇلۇقلاردۇر، (بۇ قاراڭغۇلۇققا گىرىپتار بولغۇچى) قولىنى ئۇزاتسا بارماقلىرىنى كۆرەلمەيدۇ، اﷲ نۇر ئاتا قىلمىسا، كىم بولمىسۇن، نۇرنى كۆرەلمەيدۇ (يەنى اﷲ ھىدايەت قىلمىغان ئادەم ھەرگىز ھىدايەت تاپمايدۇ)'),
(2832, 24, 41, 'اﷲ قا ئاسمانلاردىكى ۋە زېمىندىكىلەرنىڭ ۋە قاناتلىرىنى كەرگەن ھالدا (ئۇچۇپ كېتىۋاتقان) قۇشلارنىڭ تەسبىھ ئېيتىدىغانلىقىنى سەن بىلمەمسەن؟ اﷲ ھەر (مەخلۇق) نىڭ دۇئاسىنى ۋە تەسبىھىنى بىلىدۇ، اﷲ ئۇلارنىڭ قىلىدىغان ئىشلىرىنى تولۇق بىلگۈچىدۇر'),
(2833, 24, 42, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، (خالايىقنىڭ) ئاخىر قايتىدىغان جايى اﷲ نىڭ دەرگاھىدۇر'),
(2834, 24, 43, 'كۆرمەمسەنكى، اﷲ بۇلۇتلارنى ھەيدەيدۇ، ئاندىن ئۇلارنى بىر – بىرىگە قوشىدۇ، ئاندىن ئۇلارنى توپلايدۇ، شۇنىڭ بىلەن بۇلۇتنىڭ ئارىسىدىن يامغۇر ياغقانلىقىنى كۆرىسەن، اﷲ ئاسماندىكى تاغدەك بۇلۇتلاردىن مۆلدۈر ياغدۇرىدۇ، ئۇنى اﷲ خالىغان ئادەمگە يەتكۈزىدۇ (يەنى ئۇنىڭ بىلەن خالىغان ئادەمنىڭ زىرائىتى، مېۋىسى ۋە چارۋىلىرىغا زىيان يەتكۈزىدۇ) ۋە ئۇنىڭ (زىيىنىدىن) خالىغان ئادەمنى ساقلايدۇ، چاقماقنىڭ يورۇقى (قارىغۇچىلارنى) كۆزلىرىدىن ئايرىۋەتكىلى تاس قالىدۇ'),
(2835, 24, 44, 'اﷲ كېچە بىلەن كۈندۈزنى ئالماشتۇرۇپ تۇرىدۇ، بۇنىڭدا ئەقىل ئىگىلىرى ئۈچۈن ئەلۋەتتە ئىبرەت بار'),
(2836, 24, 45, 'اﷲ ھەر بىر جاندارنى سۇدىن ياراتتى، ئۇلارنىڭ بەزىسى قورسىقى بىلەن ماڭىدۇ، بەزىسى ئىككى پۇت بىلەن ماڭىدۇ، بەزىسى تۆت پۇت بىلەن ماڭىدۇ، اﷲ خالىغان مەخلۇقاتلارنى يارىتىدۇ، اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر'),
(2837, 24, 46, 'شۈبھىسىزكى، بىز روشەن ئايەتلەرنى نازىل قىلدۇق، اﷲ ئۆزى خالىغان كىشىلەرنى توغرا يولغا باشلايدۇ'),
(2838, 24, 47, '(مۇناپىقلار) «اﷲ قا ۋە پەيغەمبەرگە ئىمان ئېيتتۇق ۋە ئىتائەت قىلدۇق» دېيىشىدۇ، ئاندىن ئۇلاردىن بىر جامائە شۇنىڭدىن (يەنى ئىماننى دەۋا قىلغاندىن) كېيىن، (شەرىئەت ھۆكمىدىن) يۈز ئۆرۈيدۇ، ئۇلار (ھەقىقەتتە) مۆمىن ئەمەس'),
(2839, 24, 48, 'ئۇلارنىڭ ئارىسىدا (پەيغەمبەر) ھۆكۈم چىقىرىش ئۈچۈن، ئۇلار اﷲ نىڭ ۋە پەيغەمبەرنىڭ (ھۆكمىگە) چاقىرىلسا، ئۇلاردىن بىر جامائە (پەيغەمبەرنىڭ ئالدىغا ھازىر بولۇشتىن) باش تارتىدۇ'),
(2840, 24, 49, 'ئەگەر ھەقىقەت ئۇلارنىڭ تەرىپىدە بولىدىغان بولسا (پەيغەمبەر ئەلەيھىسسالامنىڭ ھەق ھۆكۈم چىقىرىدىغانلىقنى بىلىدىغانلىقلىرى ئۈچۈن)، پەيغەمبەرگە بويسۇنغان ھالدا كېلىشىدۇ'),
(2841, 24, 50, 'ئۇلارنىڭ دىللىرىدا (مۇناپىقلىق) ئىللىتى بارمۇ؟ يا) مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىدىن) گۇمانلىنامدۇ؟ يا اﷲ نىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئۇلارغا ئادىل بولماسلىقىدىن قورقامدۇ؟ ياق، ئۇلار (رەسۇلۇللاھنىڭ ھۆكمىدىن باش تارتقانلىقلىرى ئۈچۈن) زالىمدۇر'),
(2842, 24, 51, '(پەيغەمبەر) مۆمىنلەرنىڭ ئارىسىدا ھۆكۈم چىقىرىش ئۈچۈن، ئۇلار اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە چاقىرىلغان چاغدا ئۇلار: «ئاڭلىدۇق ۋە ئىتائەت قىلدۇق دېيىشلىرى كېرەك، مانا شۇنداق كىشىلەر مەقسەتكە ئېرىشكۈچىلەردۇر'),
(2843, 24, 52, 'اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلغان اﷲ تىن قورققان ۋە ئۇنىڭغا تەقۋادارلىق قىلغان كىشىلەر بەختكە ئېرىشكۈچىلەردۇر'),
(2844, 24, 53, '(مۇناپىقلار) ئەگەر سەن ئۇلارنى (جىھادقا) چىقىشقا ئەمر قىلساڭ، چوقۇم چىقىدىغانلىقىغا اﷲ بىلەن كۈچلۈك قەسەم قىلىدۇ. ئېيتقىنكى، «قەسەم قىلماڭلار، (قەسىمىڭلار يالغان، سىلەردىن تەلەپ قىلىنىدىغىنى) ساداقەتلىك بىلەن ئىتائەت قىلىشتۇر، اﷲ سىلەرنىڭ قىلمىشلىرىڭلاردىن ھەقىقەتەن خەۋەرداردۇر»'),
(2845, 24, 54, 'ئېيتقىنكى: «اﷲ قا ئىتائەت قىلىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار، ئەگەر ئۇلار (اﷲ قا ئىتائەت قىلىشتىن) باش تارتسا، پەيغەمبەرنىڭ ئەلچىلىكنى يەتكۈزۈش مەسئۇلىيىتى بار، سىلەرنىڭ ئىتائەت قىلىش مەسئۇلىيىتىڭلار بار، ئەگەر پەيغەمبەرنىڭ (ئەمرىگە) ئىتائەت قىلساڭلار، ھىدايەت تاپىسىلەر، پەيغەمبەرنىڭ پەقەت چۈشىنىشلىك قىلىپ تەبلىغ قىلىش مەسئۇلىيىتى بار»'),
(2846, 24, 55, 'اﷲ ئىچىڭلاردىكى ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەرگە، ئۇلاردىن بۇرۇن ئۆتكەنلەرنى زېمىندا ھۆكۈمران قىلغاندەك، ئۇلارنىمۇ چوقۇم ھۆكۈمران قىلىشىنى، ئۇلار ئۈچۈن تاللىغان دىنىنى چوقۇم مۇستەھكەم قىلىپ بېرىشنى ۋە ئۇلارنىڭ قورقۇنچىسىنى ئامانلىققا ئايلاندۇرۇپ بېرىشنى ۋەدە قىلدى، ئۇلار ماڭا ئىبادەت قىلىدۇ، ماڭا ھېچ نەرسىنى شېرىك كەلتۈرمەيدۇ، شۇ ۋەدىدىن كېيىن كاپىر بولغانلار اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر'),
(2847, 24, 56, 'اﷲ نىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن ناماز ئوقۇڭلار، زاكات بېرىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار'),
(2848, 24, 57, 'كاپىرلارنى زېمىنىدا اﷲ نىڭ ئازابىدىن قېچىپ قۇتۇلالايدۇ دەپ ئويلىمىغىن، ئۇلارنىڭ جايى دوزاخ بولىدۇ، دوزاخ نېمىدېگەن يامان جاي!'),
(2849, 24, 58, 'ئى مۆمىنلەر! سىلەرنىڭ قۇللىرىڭلار، چۆرىلىرىڭلار ۋە ئىچىڭلاردىكى بالاغەتكە يەتمىگەن بالىلار ناماز بامداتتىن بۇرۇن، چۈشتە (ئۇخلاش ئۈچۈن) كىيىمىڭلارنى سالغان چېغىڭلاردا ۋە خۇپتەندىن كېيىن (مۇشۇنداق) ئۈچ ۋاقىتتا (يېنىڭلارغا كىرسە) سىلەردىن ئىجازەت سورىسۇن، (بۇ) ئۈچ ۋاقىت سىلەرنىڭ ئەۋرىتىڭلار (ئېچىلىپ قالىدىغان ۋاقىتتۇر)، بۇ ئۈچ ۋاقىتتىن باشقا ۋاقىتلاردا (ئۇلار سىلەرگە كىرسە) سىلەرگىمۇ، ئۇلارغىمۇ ھېچ گۇناھ بولمايدۇ، سىلەر بىر - بىرىڭلار بىلەن بېرىش - كېلىش قىلىپ تۇرىسىلەر، اﷲ سىلەرگە ئايەتلەرنى (يەنى شەرىئەت ئەھكاملىرىنى) ئەنە شۇنداق بايان قىلىدۇ، اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(2850, 24, 59, 'كىچىك بالىلىرىڭلار بالاغەتكە يەتكەندە، ئۇلار ئىلگىرىكىلەر (يەنى چوڭلار كىرىشتە) ئىجازەت سورىغاندەك، (ئۆيگە كىرىشتە ھەممە ۋاقىت) ئىجازەت سورىسۇن، اﷲ ئايەتلىرىنى (دىننىڭ ئىشلىرىنى) سىلەرگە شۇنداق بايان قىلىدۇ، اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(2851, 24, 60, 'ياتلىق بولۇشنى خالىمايدىغان ياشانغان ئاياللار زىننەتلىرىنى ئاشكارىلىمىغان ھالدا (رىدا، نىمچىغا ئوخشاش) تاشقى كىيىملىرىنى سېلىۋەتسە (ئەرلەرنىڭ ئالدىدا ئادەتتىكى كىيىملىرى بىلەن يۈرسە)، ھېچ گۇناھ يوقتۇر، تاشقى كىيىملىرىنى سېلىۋېتىشتىن ساقلانسا ئۇلار ئۈچۈن ياخشىدۇر اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، بىلىپ تۇرغۇچىدۇر'),
(2852, 24, 61, '(جىھادقا چىقالمىسا) ئەماغا گۇناھ يوقتۇر، توكۇرغا گۇناھ يوقتۇر، كېسەلگە گۇناھ يوقتۇر. (ئى ئىنسانلار!) سىلەر ئۆز ئۆيلىرىڭلاردا (يەنى ئەرلىرىڭلارنىڭ، ئاياللىرىڭلارنىڭ ئۆيلىرىدە) ئاتىلىرىڭلارنىڭ ئۆيلىرىدە، ئانىلىرىڭلارنىڭ ئۆيلىرىدە، قېرىنداشلىرىڭلارنىڭ ئۆيلىرىدە، ھەمشىرەڭلارنىڭ ئۆيلىرىدە، يا ئاتاڭلارنىڭ قېرىنداىشلىرىنىڭ ئۆيلىرىدە، ئاتاڭلارنىڭ ھەمشىرىلىرىنىڭ ئۆيلىرىدە، يا ئاناڭلارنىڭ قېرىنداشلىرىنىڭ ئۆيلىرىدە، يا ئاناڭلارنىڭ ھەمشىرىلىرىنىڭ ئۆيلىرىدە، يا سىلەر ئاچقۇچلىرىنى باشقۇرىدىغانلارنىڭ ئۆيلىرىدە (يەنى ئۆي ئىگىسى سىرتقا چىقىپ كېتىپ، ئۆي بىلەن ئاچقۇچنى سىلەرگە تاپشۇرغانلارنىڭ ئۆيلىرىدە)، يا دوستلىرىڭلارنىڭ ئۆيلىرىدە (ئىجازەتسىز) بىرەر نېمە يېسەڭلار گۇناھ يوقتۇر، سىلەر توپلىشىپ يېسەڭلارمۇ، يا يەككە يېسەڭلارمۇ گۇناھ يوقتۇر، سىلەر ئۆيلەرگە كىرگەن چېغىڭلاردا ئۆزۈڭلارغا (يەنى ئۆيدىكى كىشىلەرگە) اﷲ بەلگىلىگەن مۇبارەك، پاك سالامنى بېرىڭلار، (يەنى ئەسسالامۇ ئەلەيكۇم دەڭلار) اﷲ سىلەرنى چۈشەنسۇن دەپ ئايەتلەرنى مۇشۇنداق بايان قىلىدۇ'),
(2853, 24, 62, 'اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتقان كىشىلەر كامىل مۆمىنلەردۇر، ئۇلار (مۇسۇلمانلارغا پايدىلىق) بىر ئىش ئۈستىدە (مەسلىھەتلىشىش ئۈچۈن) پەيغەمبەر بىلەن جەم بولغان چاغلىرىدا، پەيغەمبەردىن ئىجازەت سورىمىغىچە كېتىپ قالمايدۇ، سەندىن ئىجازەت سورايدىغانلار اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتىدىغان كىشىلەردۇر، بەزى شەخسىي ئىش ئۈچۈن ئۇلار سەندىن ئىجازەت سورىسا، ئۇلارنىڭ ئىچىدىن سەن خالىغان ئادەملەرگە ئىجازەت بەرگىن، ئۇلارغا سەن اﷲ تىن مەغپىرەت تىلىگىن، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇ، ناھايىتى مېھرىباندۇر'),
(2854, 24, 63, '(ئى مۆمىنلەر!) پەيغەمبەرنى سىلەر بىر - بىرىڭلارنى چاقىرغاندەك چاقىرماڭلار، سىلەردىن (پەيغەمبەرنىڭ سۆھبىتىدىن) ئاستا سۇغۇرۇلۇپ غىپپىدە چىقىپ كەتكەنلەرنى اﷲ ئەلۋەتتە بىلىدۇ، پەيغەمبەرنىڭ ئەمرىگە خىلاپلىق قىلغۇچىلار (دۇنيادا چوڭ بىر) پىتنىگە يولۇقۇشتىن، يا (ئاخىرەتتە) قاتتىق بىر ئازابقا دۇچار بولۇشتىن قورقسۇن'),
(2855, 24, 64, 'بىلىڭلاركى، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، اﷲ سىلەرنىڭ ئەمەلىي ئەھۋالىڭلارنى بىلىپ تۇرىدۇ، بەندىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدىغان كۈندە (يەنى قىيامەت كۈنىدە) اﷲ ئۇلارنىڭ قىلغان ئىشلىرىنى ئۇلارغا ئېيتىپ بېرىدۇ، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'پۈتۈن جاھان ئەھلىنى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرغۇچى بولۇشى ئۈچۈن، بەندىسىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) ھەق بىلەن باتىلنى ئايرىغۇچى قۇرئاننى نازىل قىلغان اﷲ نىڭ بەرىكىتى بۈيۈكتۇر'),
(2857, 25, 2, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ نىڭ بالىسى يوقتۇر، پادىشاھلىقتا شېرىكىمۇ يوقتۇر، اﷲ ھەممە نەرسىنى ياراتتى، ئاندىن (ئۆزىنىڭ خاھىشى بويىچە) ئۇنىڭغا مۇناسىپ ئۆلچەمنى بەلگىلىدى'),
(2858, 25, 3, 'مۇشرىكلار اﷲ نى قويۇپ ھېچ نەرسىنى يارىتالمايدىغان ۋە ئۆزلىرى يارىتىلغان ئىلاھلار (يەنى بۇتلار) غا ئىبادەت قىلدى. ئۇلار (يەنى بۇتلار) ئۆزلىرىدىن بىرەر زىياننى دەپئى قىلالمايدۇ. ئۆزلىرىگە بىرەر پايدىنى كەلتۈرەلمەيدۇ، (باشقىلارنىڭ) جېنىنى ئېلىشقا، (باشقىلارغا) ھايات بېغىشلاشقا ياكى (ئۆلۈكلەرنى) تىرىلدۈرۈشكە قادىر بولالمايدۇ'),
(2859, 25, 4, 'كاپىرلار: «بۇ (يەنى قۇرئان) پەقەت ئۇ (يەنى مۇھەممەد) ئۆزى توقۇغان يالغاندۇر، ئۇنىڭغا ئۇنى توقۇشقا باشقا بىر قەۋم (يەنى ئەھلى كىتاب) ياردەم بەرگەن» دەيدۇ، ئۇلار (پەيغەمبەر ئەلەيھىسسالامغا بوھتان چاپلاش بىلەن ئۇنىڭغا) زۇلۇم قىلدى ۋە يالغاننى چاپلىدى'),
(2860, 25, 5, 'ئۇلار ئېيتتى: «(بۇ) بۇرۇنقىلارنىڭ ئەپسانىلىرىدۇركى، ئۇلارنى ئۇ يازدۇرۇۋالغان، (ھىپزى قىلىۋېلىش ئۈچۈن) ئۇنىڭغا ئەتىگەن – ئاخشامدا ئوقۇپ بېرىلىۋاتىدۇ»'),
(2861, 25, 6, 'ئېيتقىنكى، «ئۇنى ئاسمانلاردىكى ۋە زېمىندىكى سىرنى بىلىپ تۇرىدىغان اﷲ نازىل قىلدى، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، (بەندىلەرگە) ناھايىتى مېھرىباندۇر»'),
(2862, 25, 7, 'مۇشرىكلار (مۇھەممەد ئەلەيھىسسالامنى مەسخىرە قىلىپ) ئېيتتى: «بازارلاردا (بىز تاماق يېگەندەك) تاماق يەيدىغان، (بىز ماڭغاندەك) مېڭىپ يۈرىدىغان بۇ قانداق پەيغەمبەر؟ ئۇنىڭ بىلەن بىللە ئاگاھلاندۇرغۇچى بولۇش ئۈچۈن اﷲ ئۇنىڭغا نېمىشقا بىر پەرىشتە ئەۋەتمىدى'),
(2863, 25, 8, 'يا ئۇنىڭغا بىر خەزىنە بېرىلمىدى، يا ئۇنىڭغا مېۋىلىرىنى يەيدىغان بىر باغ بېرىلمىدى؟» زالىملار (يەنى كاپىرلار): «سىلەر پەقەت سېھىرلەنگەن بىر ئادەمگە ئەگىشىۋاتىسىلەر» دېدى'),
(2864, 25, 9, 'مۇشرىكلارنىڭ سېنىڭ ئۈچۈن نۇرغۇن مىساللارنى كەلتۈرگەنلىكىگە قارىغىن، ئۇلار ئازدى، توغرا يول تاپالمايدۇ'),
(2865, 25, 10, 'اﷲ نىڭ بەرىكىتى بۈيۈكتۇركى، ئەگەر ئۇ خالىسا ساڭا ئۇلارنىڭ ئېيتقانلىرىدىنمۇ ياخشى، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان باغلارنى ۋە چوڭ سارايلارنى ئاتا قىلاتتى'),
(2866, 25, 11, 'ياق، ئۇلار قىيامەتنى ئىنكار قىلدى، قىيامەتنى ئىنكار قىلغانلارغا بىز دوزاخنى تەييارلىدۇق'),
(2867, 25, 12, 'دوزاخ ئۇلارنى يىراقتىن كۆرگەن چاغدا، ئۇلار دوزاخنىڭ غەزەپتىن قاينىغان ۋە (ئېشەكتەك) ھاڭرىغان ئاۋازنى ئاڭلايدۇ'),
(2868, 25, 13, 'ئۇلار زەنجىر بىلەن باغلانغان ھالدا دوزاخنىڭ تار يېرىگە تاشلانغان چاغدا، بۇ يەردە ئۇلار (ئۆزلىرىنىڭ) ئۆلۈمىنى تىلەيدۇ'),
(2869, 25, 14, '(ئۇلارغا) بۈگۈن سىلەر بىر ئۆلۈمنى ئەمەس، تالاي ئۆلۈملەرنى تىلەڭلار (دېيىلىدۇ)'),
(2870, 25, 15, 'ئۇلارغا ئېيتقىنكى، «شۇ (دوزاخ) ياخشىمۇ يا تەقۋادارلارغا ۋەدە قىلىنغان، (كىرگەن كىشى) مەڭگۈ قالىدىغان جەننەتتە ياخشىمۇ؟» جەننەت تەقۋادارلارغا بېرىلگەن مۇكاپاتتۇر ۋە ئۇلارنىڭ ئاخىرى بارىدىغان جايىدۇر'),
(2871, 25, 16, 'ئۇلار جەننەتتە خالىغان نېمەتلەردىن بەھرىمەن بولىدۇ، جەننەتتە مەڭگۈ قالىدۇ، بۇ پەرۋەردىگارىڭدىن ئىشقا ئاشۇرۇش تىلىنىدىغان ۋەدىدۇر'),
(2872, 25, 17, 'شۇ كۈندە اﷲ ئۇلارغا قوشۇپ، ئۇلار اﷲ نى قويۇپ، چوقۇنغان نەرسىلىرىنى يىغىدۇ. ئاندىن اﷲ (ئۇلارغا): «مېنىڭ بۇ بەندىلىرىمنى سىلەر ئازدۇردۇڭلارمۇ؟ يا (توغرا) يولدىن ئۇلار ئۆزلىرى ئازدىمۇ؟» دەيدۇ'),
(2873, 25, 18, '(ئىبادەت قىلىنغۇچىلار) ئېتىدۇ: «ئى اﷲ! سەن پاكتۇرسەن، سېنى قويۇپ باشقىلارنى دوست تۇتۇش (يەنى سەندىن باشقىغا چوقۇنۇش) بىزگە (ۋە سېنىڭ مەخلۇقاتىڭدىن ھېچ ئەھەدىگە) لايىق ئەمەس، لېكىن سەن ئۇلارنى ۋە ئۇلارنىڭ ئاتا – بوۋىلىرىنى شۇ قەدەر نېمەتكە چۈمدۈردۇڭكى، ئۇلار سېنى ياد ئېتىشىنى ئۇنتۇدى. ئۇلار ھالاك بولغۇچى قەۋم بولدى»'),
(2874, 25, 19, 'شۈبھىسىزكى، ئىبادەت قىلىنغۇچىلار سىلەرنىڭ (ئۇلارنى مەبۇد دېگەن) سۆزۈڭلارنى ئىنكار قىلدى، (ئى كۇففارلار!) سىلەر (ئۆزۈڭلاردىن ئازابنى) دەپئى قىلىشقىمۇ ۋە (بۇ بالادىن ئۆزۈڭلارنى قۇتۇلدۇرۇشقا) ياردەم بېرىشكىمۇ قادىر بولالمايسىلەر، سىلەردىن كىمكى (اﷲ قا شېرىك كەلتۈرۈش بىلەن ئۆزىگە) زۇلۇم قىلىدىكەن، ئۇنىڭغا بىز (ئاخىرەتتە) قاتتىق ئازابنى تېتىتىمىز'),
(2875, 25, 20, 'بىز سەندىن ئىلگىرى ئەۋەتكەن پەيغەمبەرلەرنىڭ ھەممىسىلا ئەلۋەتتە تاماق يەيتتى، بازارلاردا مېڭىپ يۈرەتتى، بەزىڭلارنى بەزىڭلار بىلەن سىنىدۇق، سەۋر قىلامسىلەر؟ (يەنى سەۋر قىلىڭلار) پەرۋەردىگارىڭ (سەۋر قىلغۇچىلار بىلەن سەۋر قىلمىغۇچىلارنى) كۆرۇپ تۇرغۇچىدۇر'),
(2876, 25, 21, 'بىزگە مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار (يەنى مۇشرىكلار): «نېمىشقا بىزگە پەرىشتىلەر چۈشۈرۈلمەيدۇ، ياكى (بىز نېمىشقا) پەرۋەردىگارىمىزنى كۆرمەيمىز» دېيىشتى. شۈبھىسىزكى ئۇلار ئۆزلىرىنى چوڭ تۇتتى ۋە تولىمۇ ھەددىدىن ئاشتى'),
(2877, 25, 22, 'ئۇلار (يەنى مۇشرىكلار) (ئۆزلىرىنىڭ جېنىنى ئېلىش ئۈچۈن چۈشكەن) پەرىشتىلەرنى كۆرگەن كۈنى گۇناھكارلارغا خۇش خەۋەر بولمايدۇ، پەرىشتىلەر ئۇلارغا: «(سىلەرگە جەننەت) ھارامدۇر» دەيدۇ'),
(2878, 25, 23, 'بىز ئۇلارنىڭ قىلغان ياخشى ئەمەللىرىنى بىر تەرەپ قىلىپ، ئۇنى ئۇچۇپ يۈرگەن توزاندەك قىلىۋېتىمىز، (يەنى ئۇلار ئىمانسىىز بولغانلىقتىن، قىلغان ياخشى ئەمەللىرىنى يوققا چىقىرىمىز)'),
(2879, 25, 24, 'ئەھلى جەننەت بۇ كۈندە ئەڭ ياخشى جايدا ۋە ئەڭ ياخشى ئارامگاھتا بولىدۇ'),
(2880, 25, 25, 'شۇ كۈندە ئاسمان بۇلۇت بىلەن بىرلىكتە يېرىلىدۇ، پەرىشتىلەر (بەندىلەرنىڭ نامە - ئەمالىنى ئېلىپ) ئارقىمۇئارقا چۈشىدۇ'),
(2881, 25, 26, 'پادىشاھلىق بۇ كۈندە مەرھەمەتلىك اﷲ قا خاستۇر، بۇ كۈن كاپىرلارغا قېيىندۇر'),
(2882, 25, 27, 'شۇ كۈنى زالىم (يەنى كاپىر) ئىككى قولىنى چىشلەپ: «ئىسىت! پەيغەمبەر بىلەن (نىجاتلىق) يولىنى تۇتسامچۇ، ئىسىت! پالانىنى دوست تۇتمىغان بولسامچۇ؟ قۇرئان ماڭا يەتكەندىن كېيىن، ئۇ (يەنى پالانى) مېنى قۇرئاندىن، شەك - شۈبھىسىزكى، ئازدۇردى» دەيدۇ، شەيتان ئىنساننى (ئازدۇرۇپ بولۇپ) تاشلىۋېتىدۇ'),
(2883, 25, 28, 'شۇ كۈنى زالىم (يەنى كاپىر) ئىككى قولىنى چىشلەپ: «ئىسىت! پەيغەمبەر بىلەن (نىجاتلىق) يولىنى تۇتسامچۇ، ئىسىت! پالانىنى دوست تۇتمىغان بولسامچۇ؟ قۇرئان ماڭا يەتكەندىن كېيىن، ئۇ (يەنى پالانى) مېنى قۇرئاندىن، شەك - شۈبھىسىزكى، ئازدۇردى» دەيدۇ، شەيتان ئىنساننى (ئازدۇرۇپ بولۇپ) تاشلىۋېتىدۇ'),
(2884, 25, 29, 'شۇ كۈنى زالىم (يەنى كاپىر) ئىككى قولىنى چىشلەپ: «ئىسىت! پەيغەمبەر بىلەن (نىجاتلىق) يولىنى تۇتسامچۇ، ئىسىت! پالانىنى دوست تۇتمىغان بولسامچۇ؟ قۇرئان ماڭا يەتكەندىن كېيىن، ئۇ (يەنى پالانى) مېنى قۇرئاندىن، شەك - شۈبھىسىزكى، ئازدۇردى» دەيدۇ، شەيتان ئىنساننى (ئازدۇرۇپ بولۇپ) تاشلىۋېتىدۇ'),
(2885, 25, 30, 'پەيغەمبەر ئېيتتى: «ئى پەرۋەردىگارىم! شۈبھىسىزكى، مېنىڭ قەۋمىم بۇ قۇرئاننى تاشلاندۇق قىلىپ قويدى»'),
(2886, 25, 31, 'شۇنىڭدەك (يەنى ساڭا قەۋمىڭنىڭ مۇشرىكلىرىدىن دۈشمەنلەرنى قىلغاندەك) ھەربىر پەيغەمبەرگە بىر قىسىم گۇناھكارلارنى ئۇنىڭ دۈشمىنى قىلدۇق، پەرۋەردىگارىڭ يول كۆرسەتكۈچى ۋە ياردەمچى بولۇشقا ساڭا يېتەرلىكتۇر'),
(2887, 25, 32, 'كاپىرلار: «قۇرئان نېمىشقا ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىر قېتىمدىلا نازىل قىلىنمىدى؟» دېدى. سېنىڭ دىلىڭنى مۇستەھكەم قىلىش ئۈچۈن، ئۇنى پارچە - پارچە نازىل قىلدۇق، ئۇنى ئايرىم – ئايرىم نازىل قىلدۇق'),
(2888, 25, 33, 'ئۇلار (يەنى مۇشرىكلار) قانداق بىر سوئالنى تاشلىمىسۇن، (ئۇنىڭغا قارىتا) بىز ساڭا ھەق جاۋابنى ۋە ھەممىدىن گۈزەل چۈشەندۇرۇشنى نازىل قىلدۇق'),
(2889, 25, 34, 'ئۇلار جەھەننەمگە يۈزلىرى بىلەن سۈرۈلىدۇ، ئۇلارنىڭ جايى ئەڭ يامان، يولى ئەڭ ئازغۇندۇر'),
(2890, 25, 35, 'شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق ھەمدە ئۇنىڭ قېرىندىشى ھارۇننى ئۇنىڭغا ياردەمچى قىلدۇق'),
(2891, 25, 36, 'ئۇلارغا: «بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان قەۋمگە بېرىڭلار» دېدۇق، (ئۇ قەۋم كۇفرىدا چىڭ تۇرغانلىقتىن) ئۇلارنى دەھشەتلىك تۈردە ھالاك قىلدۇق'),
(2892, 25, 37, 'نۇھنىڭ قەۋمى پەيغەمبەرلەرنى ئىنكار قىلغان چاغدا ئۇلارنى غەرق قىلدۇق ھەمدە ئۇلارنى كىشىلەرگە ئىبرەت قىلدۇق، زالىملارغا (ئاخىرەتتە) قاتتىق ئازاب تەييارلىدۇق'),
(2893, 25, 38, 'ئاد قەۋمىنى، سەمۇد قەۋمىنى، رەس ئاھالىسىنى ۋە ئۇلارنىڭ ئارىسىدىكى نۇرغۇن ئۈممەتلەرنى ھەم (ھالاك قىلدۇق)'),
(2894, 25, 39, 'ھەربىر قەۋمگە نۇرغۇن مىساللارنى بايان قىلدۇق، (ۋەز - نەسىھەت ئۈنۈم بەرمىگەنلىكتىن) ھەربىر قەۋمنى ھالاك قىلدۇق'),
(2895, 25, 40, 'ئۇلار ھەقىقەتەن (شامغا بېرىپ كېلىشتە) يامان يامغۇر (يەنى تاش) ياغدۇرۇلغان شەھەردىن ئۆتىدۇ، ئەجەبا ئۇلار ئۇنى كۆرۈپ (ئىبرەت ئالمامدۇ؟) ياق (يەنى ئىبرەت ئالمايدۇ)، ئۇلار (قىيامەت كۈنى) قايتا تىرىلىشنى ئۈمىد قىلمايدۇ'),
(2896, 25, 41, 'ئۇلار سېنى كۆرسە پەقەت مەسخىرە قىلىۋېلىپ: «اﷲ پەيغەمبەرقىلىپ ئەۋەتكەن مۇشۇمۇ؟ ئەگەر دىنىمىزدا چىڭ تۇرمىساق (مۇھەممەد) بىزنى ئىلاھلىرىمىزندىن ئازدۇرۇۋەتكىلى تاس قالاتتى» (دېدى). كىمنىڭ يولىنىڭ ئەڭ خاتا ئىكەنلىكىنى ئۇلار (ئاخىرەتتە) ئازابنى كۆرگەن چاغدا بىلىدۇ'),
(2897, 25, 42, 'ئۇلار سېنى كۆرسە پەقەت مەسخىرە قىلىۋېلىپ: «اﷲ پەيغەمبەرقىلىپ ئەۋەتكەن مۇشۇمۇ؟ ئەگەر دىنىمىزدا چىڭ تۇرمىساق (مۇھەممەد) بىزنى ئىلاھلىرىمىزندىن ئازدۇرۇۋەتكىلى تاس قالاتتى» (دېدى). كىمنىڭ يولىنىڭ ئەڭ خاتا ئىكەنلىكىنى ئۇلار (ئاخىرەتتە) ئازابنى كۆرگەن چاغدا بىلىدۇ'),
(2898, 25, 43, 'ئېيتىپ باقساڭچۇ؟ نەپسى خاھىشىنى ئىلاھ قىلىۋالغان ئادەمگە (نەپسى خاھىشىغا ئەگىشىشتىن ئۇنى ساقلاش ئۈچۈن) ھامىي بولالامسەن'),
(2899, 25, 44, 'ئۇلارنىڭ تولىسىنى (گېپىڭنى) ئاڭلايدىغان ياكى چۈشىنىدىغان ئادەملەر دەپ گۇمان قىلامسەن؟ ئۇلار (چۈشەنمەسلىكتە) پەقەت چاھارپايلارغا ئوخشاشتۇر، بەلكى ئۇلاردىنمۇ بەتتەر گۇمراھتۇر'),
(2900, 25, 45, 'پەرۋەردىگارىڭنىڭ سايىنى قانداق سوزغانلىقىنى كۆرمىدىڭمۇ؟ ئەگەر خالىسا ئۇنى ئەلۋەتتە مۇقىم قىلاتتى، ئاندىن قۇياشنى سايىگە دەلىل قىلدۇق (يەنى كۈننىڭ چىققانلىقىنى سايىنىڭ بارلىقىغا دەلىل قىلدۇق)'),
(2901, 25, 46, 'ئاندىن ئۇنى ئاستا – ئاستا يوقاتتۇق'),
(2902, 25, 47, 'اﷲ سىلەرگە كېچىنى لىباس، ئۇيقۇنى (بەدىنىڭلار ئۈچۈن) راھەت قىلدى، كۈندۈزنى (تىرىكچىلىك ئۈچۈن زېمىندا) يېيىلىدىغان ۋاقىت قىلدى'),
(2903, 25, 48, 'اﷲ رەھمىتىنى (يەنى يامغۇرنى) ياغدۇرۇش ئالدىدا شامالنى خۇش خەۋەر قىلىپ ئەۋەتتى، يامغۇر بىلەن ئۆلۈك زېمىننى تىرىلدۈرۈش ۋە بىز ياراتقان ھايۋانلارنى، نۇرغۇن ئىنسانلارنى سۇ بىلەن تەمىنلەش ئۈچۈن، بۇلۇتتىن پاك سۇنى چۈشۈرۈپ بەردۇق'),
(2904, 25, 49, 'اﷲ رەھمىتىنى (يەنى يامغۇرنى) ياغدۇرۇش ئالدىدا شامالنى خۇش خەۋەر قىلىپ ئەۋەتتى، يامغۇر بىلەن ئۆلۈك زېمىننى تىرىلدۈرۈش ۋە بىز ياراتقان ھايۋانلارنى، نۇرغۇن ئىنسانلارنى سۇ بىلەن تەمىنلەش ئۈچۈن، بۇلۇتتىن پاك سۇنى چۈشۈرۈپ بەردۇق'),
(2905, 25, 50, 'ئۇلارنى ۋەز - نەسىھەت ئالسۇن دەپ، يامغۇرنى ئۇلارنىڭ ئارىسىدا تەقسىم قىلدۇق (يەنى گاھ ئۇ يەرگە، گاھ بۇ يەرگە ياغدۇردۇق)، ئىنسانلارنىڭ تولىسى (اﷲ قا) كۇفرىلىق قىلىدۇ'),
(2906, 25, 51, 'ئەگەر خالىساق ئەلۋەتتە ھەر شەھەرگە بىر ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) ئەۋەتەتتۇق'),
(2907, 25, 52, 'شۇنىڭ ئۈچۈن كاپىرلارغا ئىتائەت قىلمىغىن، قۇرئان ئارقىلىق ئۇلارغا قارشى (پاكىتلارنى ئوتتۇرىغا قويۇپ) بارلىق كۈچۈڭ بىلەن كۈرەش قىلغىن'),
(2908, 25, 53, 'اﷲ (قۇدرىتى بىلەن) بىرى تاتلىق بولغان دەريانى، بىرى تۇزلۇق بولغان دېڭىزنى (ئۇلار بىر - بىرىگە تۇتىشاڭغۇ بولغان ھالدا (قويۇۋەتتى) ئارلىشىپ كەتمەسلىكى ئۈچۈن (ئۇلارنىڭ ئارىسىدا پەردە، توسما بەرپا قىلدى'),
(2909, 25, 54, 'اﷲ ئىنساننى ئابىمەنىدىن ياراتتى، ئۇنى بالىلار نىسبەت بېرىلىدىغان ئەر ۋە قۇدا - باجىلىق پەيدا قىلىدىغان ئايال (دىن ئىبارەت ئىككى خىل قىلىپ) ياراتتى، پەرۋەردىگارىڭ ھەممىگە قادىردۇر'),
(2910, 25, 55, 'ئۇلار (يەنى مۇشرىكلار) اﷲ نى قويۇپ ئۇلارغا پايدىمۇ يەتكۈزەلمەيدىغان، زىيانمۇ يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنىدۇ، كاپىر پەرۋەردىگارىغا ئاسىيلىق قىلىش بىلەن (شەيتانغا) ياردەم بەرگۈچىدۇر'),
(2911, 25, 56, 'سېنى پەقەت (جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (دوزاختىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق'),
(2912, 25, 57, 'ئېيتقىنكى، «مەن (اﷲ نىڭ ئەمرىنى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچ ھەق تەلەپ قىلمايمەن، پەقەت (مېنىڭ تىلەيدىغىنىم شۇكى، مېنىڭ دىنىمغا ئەگىشىش بىلەن (پەرۋەردىگارى تەرەپكە يول ئېلىشنى خالايدىغانلار) يول ئالسۇن)»'),
(2913, 25, 58, '(ھەممە ئىشىڭدا) ئۆلمەيدىغان مەڭگۈ ھايات اﷲ قا يۆلەنگىن، ئۇنىڭغا ھەمد ئېيتىش بىلەن اﷲ نى پاك دەپ ئېتىقاد قىلغىن، ئۇ بەندىلەرنىڭ گۇناھلىرىنى تولۇق بىلىشتە يېتەرلىكتۇر'),
(2914, 25, 59, 'اﷲ ئاسمانلارنى زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتى، ئاندىن ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى، اﷲ ناھايىتى مەرھەمەتلىكتۇر، بۇنى (يەنى اﷲ نىڭ بۈيۈكلۈكىنى) بىلىدىغان ئادەمدىن سورىغىن (ساڭا اﷲ نىڭ سۈپەتلىرىنى ئېيتىپ بېرىدۇ)'),
(2915, 25, 60, 'ئۇلارغا (يەنى مۇشرىكلارغا): «رەھمانغا (يەنى مەرھەمەتلىك اﷲ قا) سەجدە قىلىڭلار!» دېيىلسە، ئۇلار: «رەھمان دېگەن نېمە؟ سەن بىزنى بۇيرۇغان نەرسىگە سەجدە قىلامدۇق؟» دەيدۇ. بۇ سۆز ئۇلارنى (ئىماندىن) تېخىمۇ يىراقلاشتۇرىدۇ'),
(2916, 25, 61, 'ئاسماندا بۇرۇجلەرنى، يېنىپ تۇرىدىغان چىراقنى (يەنى كۈننى) ۋە نۇرلۇق ئاينى ياراتقان اﷲ نىڭ بەرىكىتى بۈيۈكتۇر'),
(2917, 25, 62, 'اﷲ (ئۆزىنىڭ قۇدرىتىنى) ئەسلىمەكچى بولغانلار ياكى (ئۆزىنىڭ نېمىتىگە) شۈكۈر قىلماقچى بولغانلار ئۈچۈن كېچە بىلەن كۈندۈزنى ئالمىشىپ تۇرىدىغان قىلىپ بەردى'),
(2918, 25, 63, 'مەرھەمەتلىك اﷲ نىڭ (ياخشى كۆرىدىغان) بەندىلىرى زېمىندا ئۆزلىرىنى تۆۋەن تۇتۇپ تەمكىنلىك بىلەن ماڭىدۇ، نادانلار ئۇلارغا (ياقتۇرمايدىغان) سۆز قىلسا، ئۇلار: «سىلەرگە ئامانلىق تىلەيمىز» دەيدۇ (يەنى گۇناھ بولمايدىغان سۆزلەرنى قىلىدۇ)'),
(2919, 25, 64, 'ئۇلار كېچىلەرنى پەرۋەردىگارىغا سەجدە قىلىش ۋە قىيامدا تۇرۇش بىلەن) يەنى ناماز ئوقۇش بىلەن) ئۆتكۈزىدۇ'),
(2920, 25, 65, 'ئۇلار (يەنى اﷲ ياخشى كۆرىدىغان بەندىلەر) ئېيتىدۇ: «پەرۋەردىگارىمىز! بىزدىن جەھەننەم ئازابىنى دەپئى قىلغىن، جەھەننەمنىڭ ئازابى ھەقىقەتەن (سېنىڭ دۈشمەنلىرىڭدىن) ئايرىلمايدۇ'),
(2921, 25, 66, 'جەھەننەم ھەقىقەتەن يامان قارارگاھتۇر، يامان جايدۇر»'),
(2922, 25, 67, 'ئۇلار (يەنى اﷲ ياخشى كۆرىدىغان بەندىلەر) خىراجەت قىلغاندا، ئىسراپچىلىقمۇ قىلمايدۇ، بېخىللىقمۇ قىلمايدۇ، ئوتتۇراھال خىراجەت قىلىدۇ'),
(2923, 25, 68, 'ئۇلار اﷲ قا ئىككىنچى بىر مەبۇدنى شېرىك قىلمايدۇ، اﷲ ھارام قىلغان ناھەق ئادەم ئۆلتۈرۈش ئىشىنى قىلمايدۇ، زىنا قىلمايدۇ، كىمكى بۇ (گۇناھلار) نى قىلىدىكەن، (ئاخىرەتتە) ئۇ جازاغا ئۇچرايدۇ'),
(2924, 25, 69, 'قىيامەت كۈنى ئۇنىڭغا ھەسسىلەپ ئازاب قىلىنىدۇ، ئۇ مەڭگۈ ئازاب ئىچىدە خارلانغان ھالدا قالىدۇ'),
(2925, 25, 70, 'پەقەت (ئۇلارنىڭ ئىچىدىن) (بۇ دۇنيادىكى چېغىدا) تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارلا بۇ ھالدا قالمايدۇ، اﷲ ئۇلارنىڭ گۇناھلىرىنى ياخشىلىققا ئالماشتۇرىدۇ، اﷲ تولىمۇ مەغپىرەت قىلغۇچىدۇر ۋە مەرھەمەت قىلغۇچىدۇر'),
(2926, 25, 71, 'كىمكى (گۇناھلىرىغا) تەۋبە قىلىدىكەن ۋە ئەمەلىنى تۈزەيدىكەن، ئۇ اﷲ قا يۈزلەنگەن بولىدۇ (يەنى اﷲ ئۇ نىڭ تەۋبىسىنى قوبۇل قىلىپ، ئۇنىڭدىن رازى بولىدۇ)'),
(2927, 25, 72, 'ئۇلار (يەنى اﷲ ياخشى كۆرىدىغان بەندىلەر) يالغان گۇۋاھلىق بەرمەيدۇ، يامان سۆزنى ئاڭلاپ قالغان چاغدا ئالىيجانابلىق بىلەن ئۆتۈپ كېتىدۇ'),
(2928, 25, 73, 'ئۇلارغا پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن ۋەز - نەسىھەت قىلىنسا، گاس، كور بولۇۋالمايدۇ (يەنى ئۇنى بېرىلىپ ئىخلاس بىلەن ئاڭلايدۇ)'),
(2929, 25, 74, 'ئۇلار: «ئى پەرۋەردىگارىمىز! بىزگە ئاياللىرىمىز ۋە ئەۋلاتلىرىمىز ئارقىلىق شادلىق بېغىشلىشىڭنى (يەنى بىزگە ساڭا ئىتائەتمەن پەرزەنت ئاتا قىلىشىڭنى) تىلەيمىز، بىزنى تەقۋادارلارنىڭ پېشىۋاسى (يەنى تەقۋادارلارنىڭ نەمۇنىسى، ياخشىلىققا دەۋەت قىلغۇچى) قىلغىن» دەيدۇ'),
(2930, 25, 75, 'ئەنە شۇلار سەۋرلىك بولغانلىقى ئۈچۈن جەننەت بىلەن مۇكاپاتلىنىدۇ، ئۇلار جەننەتتە (پەرىشتىلەر تەرىپىدىن قىلىنغان) دۇئا ۋە سالام بىلەن قارشى ئېلىنىدۇ'),
(2931, 25, 76, 'ئۇلار جەننەتتە مەڭگۈ قالىدۇ، جەننەت نېمىدېگەن گۈزەل قارارگاھ! نېمىدېگەن گۈزەل جاي!'),
(2932, 25, 77, 'ئېيتقىنكى، «ئەگەر سىلەرنىڭ دۇئايىڭلار بولمىسا، پەرۋەردىگارىم سىلەرگە پەرۋا قىلمايدۇ، (ئى كاپىرلار! پەيغەمبەرنى) ئىنكار قىلدىڭلار، (ئاخىرەتتە) سىلەر قۇتۇلالمايدىغان ئازابقا دۇچار بولۇسىلەر»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طا، سىم، مىم'),
(2934, 26, 2, 'بۇ، روشەن كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر'),
(2935, 26, 3, '(ئى مۇھەممەد!) ئۇلارنىڭ ئىمان ئېيتمىغانلىقىدىن ئۆزۈڭنى ھالاك قىلىۋېتىشىڭ مۇمكىن'),
(2936, 26, 4, 'ئەگەر بىز ئۇلارنىڭ (ئىمان ئېيتىشىنى) خالايدىغان بولساق، ئۇلارغا ئاسماندىن (ئۇلارنى ئىمانغا مەجبۇرلايدىغان) بىر ئايەتنى (ئالامەتنى) نازىل قىلاتتۇقتە، ئۇنىڭغا ئۇلار باش ئەگكەن بولاتتى'),
(2937, 26, 5, 'ئۇلارغا مەرھەمەتلىك اﷲ تەرىپىدىن قۇرئاندىن يېڭىدىن بىرنەرسە نازىل بولسىلا، ئۇلار ئۇنىڭدىن يۈز ئۆرۈيدۇ'),
(2938, 26, 6, 'ئۇلار راستلا (قۇرئاننى) ئىنكار قىلدى، مەسخىرە قىلغان نەرسىنىڭ خەۋەرلىرى (يەنى ئاقىۋىتى) ئۇلارغا كېلىدۇ'),
(2939, 26, 7, 'ئۇلار زېمىننى (يەنى زېمىننىڭ ئاجايىباتلىرىنى) كۈزەتمىدىمۇ؟ زېمىندا تۈرلۈك پايدىلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق'),
(2940, 26, 8, 'بۇنىڭدا ئەلۋەتتە (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ئالامەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى'),
(2941, 26, 9, 'سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(2942, 26, 10, 'ئۆز ۋاقتىدا پەرۋەردىگارىڭ مۇساغا نىدا قىلدى: «سەن زالىم قەۋمگە بارغىن'),
(2943, 26, 11, '(ئۇلار) پىرئەۋننىڭ قەۋمىدۇر، ئۇلار (اﷲ نىڭ جازالىشىدىن) قورقمامدۇ؟»'),
(2944, 26, 12, 'مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن قورقىمەن'),
(2945, 26, 13, '(ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن) يۈرىكىم سىقىلىدۇ، تىلىم كېكەچ، شۇڭا (ماڭا ياردەمدە بولۇش ئۈچۈن) ھارۇننى (پەيغەمبەر) قىلىپ ئەۋەتكىن'),
(2946, 26, 14, '(ئۇلارنىڭ دەۋاسىچە) ئۇلارنىڭ ئالدىدا مېنىڭ گۇناھىم بار، ئۇلارنىڭ مېنى ئۆلتۈرۈشىدىن قورقىمەن»'),
(2947, 26, 15, 'اﷲ ئېيتتى: «ھەرگىز ئۇنداق ئەمەس (يەنى سېنى ئۇلار ھەرگىز ئۆلتۈرەلمەيدۇ)، سىلەر مېنىڭ مۆجىزىلىرىمنى ئېلىپ بېرىڭلار، بىز ھەقىقەتەن سىلەر بىلەن بىللە (مۇنازىرەڭلارنى) ئاڭلاپ تۇرىمىز'),
(2948, 26, 16, 'سىلەر پىرئەۋنگە بېرىپ: بىز ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارىنىڭ ئەلچىسىمىز'),
(2949, 26, 17, 'ئىسرائىل ئەۋلادىنى (قۇللۇقۇڭدىن) بوشاتقىن، ئۇلار بىز بىلەن (شامغا) كەتسۇن دەڭلار»'),
(2950, 26, 18, '(ئۇلار پىرئەۋنگە كېلىپ ئەلچىلىكنى يەتكۈزدى) پىرئەۋن (مۇساغا) ئېيتتى: «سېنى بىز ئۆيىمىزدە كىچىكىڭدىن تەربىيلەپ چوڭ قىلمىدۇقمۇ؟ ئارىمىزدا كۆپ يىللار تۇرمىدىڭمۇ؟'),
(2951, 26, 19, 'سەن ھېلىقى قىلغان ئىشىڭنى قىلمىدىڭمۇ؟ (يەنى قىبتىنى ئۆلتۈرمىدىڭمۇ؟) سەن تۇزكورلاردىنسەن»'),
(2952, 26, 20, 'مۇسا ئېيتتى: «مەن ئۇ ئىشنى قىلغان چېغىمدا نادانلاردىن ئىدىم'),
(2953, 26, 21, 'سىلەردىن قورقۇپ، سىلەردىن قاچتىم، پەرۋەردىگارىم ماڭا ھېكمەت ئاتا قىلدى، مېنى پەيغەمبەر قىلدى'),
(2954, 26, 22, 'سەن ئىسرائىل ئەۋلادىنى قۇل قىلدىڭ، سېنىڭ ماڭا مىننەت قىلغان نېمىتىڭ ئەنە شۇدۇر»'),
(2955, 26, 23, 'پىرئەۋن ئېيتتى: «ئالەملەرنىڭ پەرۋەردىگارى دېگەن نېمە؟»'),
(2956, 26, 24, 'مۇسا ئېيتتى: «ئۇ ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى پۈتۈن مەخلۇقاتلارنىڭ پەرۋەردىگارىدۇر. ئەگەر سىلەر ھەقىقىي ئىشىنىدىغان بولساڭلار»'),
(2957, 26, 25, 'پىرئەۋن چۆرىسىدىكىلەرگە: «(ئۇنىڭ جاۋابىنى) ئاڭلاۋاتامسىلەر؟» دېدى'),
(2958, 26, 26, 'مۇسا ئيتتى: «(ئۇ) سىلەرنىڭ پەرۋەردىگارىڭلاردۇر ۋە سىلەرنىڭ ئاتا - بوۋاڭلارنىڭ پەرۋەردىگارىدۇر»'),
(2959, 26, 27, 'پىرئەۋن ئېيتتى: «سىلەرگە ئەۋەتىلگەن (بۇ) ئەلچى ئەلۋەتتە مەجنۇندۇر»'),
(2960, 26, 28, 'مۇسا ئېيتتى: «(ئۇ) مەشرىقنىڭ، مەغرىبنىڭ ۋە ئۇلارنىڭ ئارىسىدىكى مەخلۇقاتنىڭ پەرۋەردىگارىدۇر. ئەگەر سىلەر چۈشىنىدىغان بولساڭلار»'),
(2961, 26, 29, 'پىرئەۋن ئېيتتى: «ئەگەر مەندىن غەيرىنى ئىلاھ قىلىۋالىدىغان بولساڭ، سېنى چوقۇم زىندانغا تاشلايمەن»'),
(2962, 26, 30, 'مۇسا ئېيتتى: «(مېنىڭ راست پەيغەمبەر ئىكەنلىكىمنى ئىسپاتلايدىغان) روشەن دەلىل كەلتۈرسەممۇ (زىندانغا تاشلامسەن؟)»'),
(2963, 26, 31, 'پىرئەۋن ئېيتتى: «ئەگەر سۆزۈڭ راست بولسا روشەن دەلىلىڭنى كەلتۈرگىن»'),
(2964, 26, 32, 'مۇسا ھاسىسىنى تاشلىۋىدى، ناگاھان ئوپئوچۇق ئەجدىھاغا ئايلاندى'),
(2965, 26, 33, 'مۇسا (قوينىدىن) قولىنى چىقىرىۋىدى، ناگاھان ئۇ قارىغۇچىلارغا (نۇر چاقناپ تۇرىدىغان) ئاپئاق بولۇپ كۆرۈندى'),
(2966, 26, 34, 'پىرئەۋن چۆرىسىدىكى (قەۋمنىڭ) چوڭلىرىغا ئېيتتى: «بۇ ھەقىقەتەن ئۇستا سېھىرگەر ئىكەن، ئۇ سېھىر ئارقىلىق سىلەرنى زېمىنىڭلاردىن ھەيدەپ چىقارماقچى، (بۇ ھەقتە) نېمە مەسلىھەت بېرىسىلەر؟»'),
(2967, 26, 35, 'پىرئەۋن چۆرىسىدىكى (قەۋمنىڭ) چوڭلىرىغا ئېيتتى: «بۇ ھەقىقەتەن ئۇستا سېھىرگەر ئىكەن، ئۇ سېھىر ئارقىلىق سىلەرنى زېمىنىڭلاردىن ھەيدەپ چىقارماقچى، (بۇ ھەقتە) نېمە مەسلىھەت بېرىسىلەر؟»'),
(2968, 26, 36, 'ئۇلار ئېيتتى: «(ئۇلارنىڭ ئىشىنى) تەخىر قىلغىن، شەھەرلەرگە (سېھىرگەرلەرنى) يىققۇچى كىشىلەرنى ئەۋەتكىن'),
(2969, 26, 37, 'ئۇلار سېنىڭ ھوزۇرۇڭغا ناھايىتى ئۇستا سېھىرگەرلەرنىڭ ھەممىسىنى ئېلىپ كەلسۇن»'),
(2970, 26, 38, 'مۇئەييەن كۈندىكى بەلگىلەنگەن ۋاقىتتا سېھىرگەرلەر توپلاندى'),
(2971, 26, 39, 'كىشىلەرگە: «سىلەر يىغىلىپ بولدۇڭلارمۇ؟ ئەگەر سېھىرگەرلەر غالىب چىقسا ئۇلارغا بويسۇنۇشىمىز مۇمكىن» دېيىلدى'),
(2972, 26, 40, 'كىشىلەرگە: «سىلەر يىغىلىپ بولدۇڭلارمۇ؟ ئەگەر سېھىرگەرلەر غالىب چىقسا ئۇلارغا بويسۇنۇشىمىز مۇمكىن» دېيىلدى'),
(2973, 26, 41, 'سېھىرگەرلەر پىرئەۋننىڭ قېشىغا يېتىپ كەلگەندە، ئۇلار پىرئەۋنگە: «ئەگەر غەلىبە قىلساق بىزگە چوقۇم مۇكاپات بېرىلەمدۇ؟» دېدى'),
(2974, 26, 42, 'پىرئەۋن: «ھەئە، ئۇ چاغدا (سىلەرگە مۇكاپات بېرىلىدۇ، ئۇنىڭ ئۈستىگە) سىلەر چوقۇم مېنىڭ يېقىن ئادەملىرىمدىن بولۇپ قالىسىلەر» دېدى'),
(2975, 26, 43, 'مۇسا ئۇلارغا: «تاشلايدىغان نەرسەڭلارنى تاشلاڭلار!» دېدى'),
(2976, 26, 44, 'ئۇلار ئارغامچىلىرىنى، ھاسسىلىرىنى تاشلىدى. ئۇلار: «پىرئەۋننىڭ كاتتىلىقى بىلەن قەسەمكى، بىز شەك شۈبھىسىز غەلىبە قىلىمىز» دېدى'),
(2977, 26, 45, 'مۇسا ھاسىسىنى تاشلىدى، (ئۇ ئەجدىھاغا ئايلىىنىپ) ئۇلارنىڭ ئويدۇرما نەرسىلىرىنى يۈتۈۋەتتى'),
(2978, 26, 46, 'سىھىرگەرلەر سەجدىگە باردى'),
(2979, 26, 47, 'ئۇلار: «ئالەملەرنىڭ پەرۋەردىگارىغا - مۇسا ۋە ھارۇننىڭ پەرۋەردىگارىغا - ئىمان ئېيتتۇق» دېدى'),
(2980, 26, 48, 'ئۇلار: «ئالەملەرنىڭ پەرۋەردىگارىغا - مۇسا ۋە ھارۇننىڭ پەرۋەردىگارىغا - ئىمان ئېيتتۇق» دېدى'),
(2981, 26, 49, 'پىرئەۋن: «مەن رۇخسەت قىلماي تۇرۇپ سىلەر مۇساغا ئىمان ئېيتتىڭلار، ئۇ چوقۇم سىلەرگە سېھىرنى ئۈگەتكەن باشلىقىڭلار ئىكەن، (سىلەرنى قانداق جازالايدىغانلىقىمنى) ئۇزۇنغا قالماي بىلىسىلەر، قولۇڭلارنى، پۇتۇڭلارنى چوقۇم ئوڭ - چەپ قىلىپ (يەنى ئوڭ قولۇڭلار بىلەن سول پۇتۇڭلارنى ياكى سول قولۇڭلار بىلەن ئوڭ پۇتۇڭلارنى) كېسىمەن، ھەممىڭلارنى چوقۇم دارغا ئاسىمەن» دېدى'),
(2982, 26, 50, 'سېھىرگەرلەر: «(بۇنىڭدىمۇ بىزگە) ھېچ زىيان يوق، بىز ئەلۋەتتە پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز، بىز مۇساغا ھەممىدىن بۇرۇن ئىمان ئېيتقۇچىلار بولغانلىقىمىز ئۈچۈن، پەرۋەردىگارىمىزنىڭ خاتالىقلىرىمىزنى مەغپىرەت قىلىشىنى ئەلۋەتتە ئۈمىد قىلىمىز» دېدى'),
(2983, 26, 51, 'سېھىرگەرلەر: «(بۇنىڭدىمۇ بىزگە) ھېچ زىيان يوق، بىز ئەلۋەتتە پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز، بىز مۇساغا ھەممىدىن بۇرۇن ئىمان ئېيتقۇچىلار بولغانلىقىمىز ئۈچۈن، پەرۋەردىگارىمىزنىڭ خاتالىقلىرىمىزنى مەغپىرەت قىلىشىنى ئەلۋەتتە ئۈمىد قىلىمىز» دېدى'),
(2984, 26, 52, 'بىز مۇساغا: «كېچىدە بەندىلىرىمنى (يەنى بەنى ئىسرائىلنى) ئېلىپ ئاتلانغىن. سىلەر ئەلۋەتتە قوغلىنىسىلەر، (يەنى پىرئەۋن ئۆز قەۋمى بىلەن سىلەرنى قوغلاپ چىقىدۇ)» دەپ ۋەھيى قىلدۇق'),
(2985, 26, 53, 'پىرئەۋن شەھەرلەرگە (ئەسكەر) توپلىغۇچىلارنى ئەۋەتتى'),
(2986, 26, 54, '(پىرئەۋن ئېيتتىكى) «بۇ كىشىلەر ھەقىقەتەن بىر ئوچۇم ئادەملەردۇر'),
(2987, 26, 55, 'ئۇلار ھەقىقەتەن بىزنىڭ ئاچچىقىمىزنى كەلتۈرۈپ قويدى'),
(2988, 26, 56, 'بىز ھەقىقەتەن ئېھتىياتچان جامائەمىز»'),
(2989, 26, 57, 'بىز ئولارنى (يەنى پىرئەۋن بىلەن ئۇنىڭ قەۋمىنى) باغلاردىن، بۇلاقلاردىن، خەزىنىلەردىن ۋە ئېسىل تۇرالغۇدىن ئايرىۋەتتۇق'),
(2990, 26, 58, 'بىز ئولارنى (يەنى پىرئەۋن بىلەن ئۇنىڭ قەۋمىنى) باغلاردىن، بۇلاقلاردىن، خەزىنىلەردىن ۋە ئېسىل تۇرالغۇدىن ئايرىۋەتتۇق'),
(2991, 26, 59, 'شۇنداق قىلىپ ئۇلارنى بەنى ئىسرائىلغا مىراس قىلىپ بەردۇق'),
(2992, 26, 60, 'ئۇلار (يەنى پىرئەۋن بىلەن ئۇنىڭ قوشۇنى) ئۇلارنى كۈن چىققان چاغدا قوغلاپ چىقتى'),
(2993, 26, 61, 'ئىككى توپ (يەنى پىرئەۋن توپى بىلەن مۇسا ئەلەيھىسسالامنىڭ توپى) بىر - بىرىنى كۆرۈپ تۇرۇشقان چاغدا، مۇسانىڭ ئادەملىرى: «ئۇلار: (يەنى پىرئەۋن بىلەن قوشۇنى) بىزگە چوقۇم يېتىشىۋالىدىغان بولدى» دېدى'),
(2994, 26, 62, 'مۇسا ئېيتتى: «ئۇنداق بولمايدۇ (يەنى ھەرگىز يېتىشەلمەيدۇ) پەرۋەردىگارىم ھەقىقەتەن مەن بىلەن بىللە، مېنى (قۇتۇلۇش يولىغا) باشلايدۇ»'),
(2995, 26, 63, 'بىز مۇساغا: «ھاساڭ بىلەن دەرياغا ئۇرغىن» دەپ ۋەھيى قىلدۇق، (مۇسا ھاسىسى بىلەن ئۇرۇۋېدى) دەريا يېرىلدى، (ھەر بىر يېرىلغان) قىسمى چوڭ تاغدەك بولۇپ قالدى'),
(2996, 26, 64, 'ئىككىنچى بىر گۇرۇھنى بۇ يەرگە يېقىنلاشتۇردۇق (يەنى پىرئەۋن بىلەن قوشۇنىنى بەنى ئىسرائىلنىڭ ئارقىسىدىن دەرياغا كىرگۈزدۇق)'),
(2997, 26, 65, 'مۇسا بىلەن ئۇنىڭ ھەمراھلىرىنى پۈتۈنلەي قۇتقۇزدۇق'),
(2998, 26, 66, 'ئاندىن ئىككىنچى گۇرۇھنى (يەنى پىرئەۋن بىلەن قەۋمىنى) غەرق قىلدۇق'),
(2999, 26, 67, 'بۇنىڭدا (يەنى پىرئەۋن بىلەن ئۇنىڭ قەۋمىنىڭ غەرق بولۇشىدا) ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى'),
(3000, 26, 68, 'سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(3001, 26, 69, 'ئۇلارغا ئىبراھىمنىڭ قىسسىسىنى ئوقۇپ بەرگىن'),
(3002, 26, 70, 'ئۆز ۋاقتىدا ئۇ ئاتىسىغا ۋە قەۋمىگە: «نېمىگە ئىبادەت قىلىسىلەر؟» دېدى'),
(3003, 26, 71, 'ئۇلار: «بۇتلارغا ئىبادەت قىلىمىز، بۇ ئىبادەتنى داۋالاشتۇرىمىز» دېيىشتى'),
(3004, 26, 72, 'ئىبراھىم ئېيتتى: «دۇئا قىلغان چېغىڭلاردا ئۇلار (دۇئايىڭلارنى ئاڭلامدۇ؟)'),
(3005, 26, 73, 'يا سىلەرگە پايدا يەتكۈزەلەمدۇ؟ يا زىيان يەتكۈزەلەمدۇ؟»'),
(3006, 26, 74, 'ئۇلار ئېيتتى: «ئۇنداق ئەمەس، ئاتا - بوۋىلىرىمىزنىڭ شۇنداق قىلغانلىقىنى بايقىدۇق»'),
(3007, 26, 75, 'ئىبراھىم ئېيتتى: «ئېيتىپ بېقىڭلارچۇ! سىلەرنىڭ ئىبادەت قىلغىنىڭلار نېمە؟'),
(3008, 26, 76, 'سىلەرنىڭ ئەڭ قەدىمكى ئەجدادلىرىڭلارنىڭ ئىبادەت قىلغىنى نېمە؟'),
(3009, 26, 77, 'ئۇلار (يەنى بۇتلار) مېنىڭ دۈشمىنىمدۇر، پەقەت ئالەملەرنىڭ پەرۋەردىگارى (مېنىڭ پەرۋەردىگارىمدۇر)'),
(3010, 26, 78, 'ئۇ مېنى ياراتقان، ئۇ مېنى ھىدايەت قىلىدۇ'),
(3011, 26, 79, 'ئۇ مېنى تاماق بىلەن تەمىنلەيدۇ، ئۇسسۇلۇق بىلەن تەمىنلەيدۇ'),
(3012, 26, 80, 'ئاغرىپ قالسام ئۇ مېنى ساقايتىدۇ'),
(3013, 26, 81, 'ئۇ مېنى قەبزى روھ قىلىدۇ، كېيىن يەنە تىرىلدۈرىدۇ'),
(3014, 26, 82, 'قىيامەت كۈنى ئۇنىڭ مېنىڭ خاتالىقلىرىمنى مەغپىرەت قىلىشىنى ئۈمىد قىلىمەن'),
(3015, 26, 83, 'پەرۋەردىگارىم! ماڭا ئىلىم ھېكمەت ئاتا قىلغىن، مېنى ياخشىلارغا قوشقىن'),
(3016, 26, 84, 'كېيىنكىلەر ئارىسىدا ياخشى نامىمنى قالدۇرغىن'),
(3017, 26, 85, 'مېنى نازۇنېمەتلىك جەننەتنىڭ ۋارىسلىرىدىن قىلغىن'),
(3018, 26, 86, 'ئاتامغا مەغپىرەت قىلغىن، ئۇ ھەقىقەتەن گۇمراھلاردىن بولدى'),
(3019, 26, 87, 'ئۇلار (يەنى خالايىقلار) (ھېساب بېرىش ئۈچۈن) تىرىلدۈرىلىدىغان كۈندە مېنى رەسۋا قىلمىغىن'),
(3020, 26, 88, 'ئۇ كۈنى (ھېچ كىشىگە) مال ۋە ئوغۇللار پايدا يەتكۈزەلمەيدۇ'),
(3021, 26, 89, 'پەقەت (اﷲ نىڭ دەرگاھى) غا پاك قەلب بىلەن كەلگەن ئادەمگىلا پايدا يەتكۈزۈلىدۇ»'),
(3022, 26, 90, 'جەننەت تەقۋادارلارغا يېقىنلاشتۇرىلىدۇ'),
(3023, 26, 91, 'دوزاخ گۇمراھلارغا ئوچۇق كۆرسىتىلىدۇ'),
(3024, 26, 92, 'ئۇلارغا: «سىلەر ئىلگىرى اﷲ نى قويۇپ چوقۇنغان نەرسەڭلار قەيەردە؟ ئۇلار سىلەرگە ياردەم بېرەلەمدۇ؟ يا ئۆزلىرىگە ياردەم بېرەلەمدۇ؟» دېيىلىدۇ'),
(3025, 26, 93, 'ئۇلارغا: «سىلەر ئىلگىرى اﷲ نى قويۇپ چوقۇنغان نەرسەڭلار قەيەردە؟ ئۇلار سىلەرگە ياردەم بېرەلەمدۇ؟ يا ئۆزلىرىگە ياردەم بېرەلەمدۇ؟» دېيىلىدۇ'),
(3026, 26, 94, 'بۇتلار، گۇمراھلار ۋە ئىبلىسنىڭ قوشۇنلىرىنىڭ ھەممىسى دوزاخقا ئۈستى - ئۈستىلەپ تاشلىنىدۇ'),
(3027, 26, 95, 'بۇتلار، گۇمراھلار ۋە ئىبلىسنىڭ قوشۇنلىرىنىڭ ھەممىسى دوزاخقا ئۈستى - ئۈستىلەپ تاشلىنىدۇ'),
(3028, 26, 96, 'ئۇلار دوزاختا جېدەللىشىپ ئېيتىدۇ: «اﷲ بىلەن قەسەمكى، بىز ئوپئوچۇق گۇمراھلىقتا ئىدۇق'),
(3029, 26, 97, 'ئۇلار دوزاختا جېدەللىشىپ ئېيتىدۇ: «اﷲ بىلەن قەسەمكى، بىز ئوپئوچۇق گۇمراھلىقتا ئىدۇق'),
(3030, 26, 98, 'ئۆز ۋاقتىدا بىز سىلەرنى (ئىبادەتتە) ئالەملەرنىڭ پەرۋەردىگارى بىلەن ئوخشاش ئورۇندا قوياتتۇق'),
(3031, 26, 99, 'بىزنى پەقەت گۇناھكارلارلا ئازدۇردى'),
(3032, 26, 100, 'شۇنىڭ ئۈچۈن بىزگە شاپائەت قىلغۇچىلار يوق'),
(3033, 26, 101, 'ۋە يېقىن دوستىمۇ يوق'),
(3034, 26, 102, 'ئەگەر بىزگە (دۇنياغا) قايتىشقا بولسا ئىدى، بىز مۆمىنلەردىن بولاتتۇق»'),
(3035, 26, 103, 'شۈبھىسىزكى، بۇنىڭدا (يەنى ئىبراھىم ۋە ئۇنىڭ قەۋمىنىڭ قىسسىسىدە) (ئەقىل ئىگىلىرى ئۈچۈن) ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى'),
(3036, 26, 104, 'سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(3037, 26, 105, 'نۇھنىڭ قەۋمى پەيغەمبەرلەرنى ئىنكار قىلدى'),
(3038, 26, 106, 'ئۆز ۋاقتىدا ئۇلارغا قېرىندىشى نۇھ ئېيتتى: «سىلەر (اﷲ تىن) قورقمامسىلەر؟'),
(3039, 26, 107, 'مەن ھەقىقەتەن سىلەرگە سادىق بىر پەيغەمبەرمەن'),
(3040, 26, 108, 'سىلەر اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار'),
(3041, 26, 109, 'تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن'),
(3042, 26, 110, 'اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار»'),
(3043, 26, 111, 'ئۇلار: «(ئى نۇھ!) ساڭا تۆۋەن تەبىقىدىكى ئادەملەر ئەگەشكەن تۇرسا، ساڭا بىز ئىمان ئېيتامدۇق؟» دېدى'),
(3044, 26, 112, 'نۇھ ئېيتتى: «مەن ئۇلارنىڭ نېمە قىلغانلىقىنى ئۇقمايمەن'),
(3045, 26, 113, 'ئەگەر بىلسەڭلار ئۇلاردىن ھېساب ئېلىشنى پەقەت پەرۋەردىگارىم ئۈستىگە ئالغان'),
(3046, 26, 114, 'مەن مۆمىنلەرنى قوغلىۋەتمەيمەن'),
(3047, 26, 115, 'مەن پەقەت ئوچۇق - ئاشكارا ئاگاھلاندۇرغۇچىمەن»'),
(3048, 26, 116, 'ئۇلار ئېيتتى: «ئى نۇھ! ئەگەر سەن (پەيغەمبەرلىك دەۋەتىڭدىن) يانمىساڭ، سەن چوقۇم تاش - كېسەك قىلىنىپ ئۆلتۈرۈلگۈچىلەردىن بولىسەن»'),
(3049, 26, 117, 'نۇھ ئېيتتى: «پەرۋەردىگارىم! قەۋمىم مېنى ھەقىقەتەن ئىنكار قىلدى'),
(3050, 26, 118, 'مەن بىلەن ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقارغىن، مېنى ۋە مەن بىلەن بىللە بولغان مۆمىنلەرنى قۇتقۇزغىن»'),
(3051, 26, 119, 'بىز ئۇنى ۋە (ئادەملەر، ھايۋانلار بىلەن) لىق تولغان كېمىدە ئۇنىڭ بىلەن بىللە بولغانلارنى قۇتقۇزدۇق'),
(3052, 26, 120, '(ئۇلارنى قۇتقۇزغاندىن) كېيىن ئاندىن قالغانلارنى غەرق قىلدۇق'),
(3053, 26, 121, 'بۇنىڭدا (تەپەككۇر قىلغۇچىلار ئۈچۈن) ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلار (يەنى كىشىلەر) نىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى'),
(3054, 26, 122, 'سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(3055, 26, 123, 'ئاد (خەلقى) پەيغەمبەرلەرنى ئىنكار قىلدى'),
(3056, 26, 124, 'ئۆز ۋاقتىدا ئۇلارغا قېرىندىشى ھۇد ئېيتتى: «سىلەر (اﷲ تىن) قورقمامسىلەر؟'),
(3057, 26, 125, 'مەن ھەقىقەتەن سىلەرگە سادىق پەيغەمبەرمەن'),
(3058, 26, 126, 'سىلەر اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار'),
(3059, 26, 127, 'تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن'),
(3060, 26, 128, 'سىلەر ھەر بىر ئېگىز جايغا ئويۇن - كۈلكە ئۈچۈن بىر ئالامەت (يەنى ئېگىز بىنا) سالامسىلەر'),
(3061, 26, 129, '(گويا سىلەر ئۆلمەيدىغاندەك) دۇنيادا مەڭگۈ قېلىشنى ئۈمىد قىلىپ پۇختا سارايلارنى سالامسىلەر'),
(3062, 26, 130, 'ئەگەر (بىراۋنى) جازالىساڭلار، زالىملاردەك جازالايسىلەر'),
(3063, 26, 131, 'سىلەر اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار'),
(3064, 26, 132, 'سىلەرگە سىلەر بىلىدىغان نېمەتلەرنى ئاتا قىلغان اﷲ تىن قورقۇڭلار'),
(3065, 26, 133, 'ئۇ سىلەرگە چارۋىلارنى، ئوغۇللارنى، باغلارنى، بۇلاقلارنى ئاتا قىلدى'),
(3066, 26, 134, 'ئۇ سىلەرگە چارۋىلارنى، ئوغۇللارنى، باغلارنى، بۇلاقلارنى ئاتا قىلدى'),
(3067, 26, 135, 'مەن ھەقىقەتەن سىلەرنىڭ بۈيۈك كۈننىڭ ئازابىغا قېلىشىڭلاردىن قورقىمەن»'),
(3068, 26, 136, 'ئۇلار ئېيتتى: «نەسىھەت قىلامسەن، قىلمامسەن، بەرىبىر بىزگە ئوخشاش'),
(3069, 26, 137, 'بۇ (يەنى بۇتلارغا چوقۇنۇش) پەقەت بۇرۇنقىلاردىن قالغان ئادەت'),
(3070, 26, 138, 'بىز ھەرگىز جازالانمايمىز»'),
(3071, 26, 139, 'ئۇلار ئۇنى (يەنى ھۇدنى) ئىنكار قىلدى، ئۇلارنى بىز ھالاك قىلدۇق، بۇنىڭدا ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى'),
(3072, 26, 140, 'سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(3073, 26, 141, 'سەمۇد (خەلقى) پەيغەمبەرلەرنى ئىنكار قىلدى'),
(3074, 26, 142, 'ئۆز ۋاقتىدا ئۇلارغا قېرىندىشى سالىھ ئېيتتى: «(اﷲ تىن) قورقمامسىلەر؟'),
(3075, 26, 143, 'مەن ھەقىقەتەن سىلەرگە سادىق پەيغەمبەرمەن'),
(3076, 26, 144, 'سىلەر اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار'),
(3077, 26, 145, 'تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن'),
(3078, 26, 146, 'سىلەر بۇ يەرلەردە، باغچىلاردىن، بۇلاقلاردىن، زىرائەتلەردىن، يۇمشاق پىشقان خورمىلاردىن بەھرىمەن بولۇپ خاتىرجەم ھالدا داۋاملىق قالىمىز (دەپ ئويلامسىلەر)'),
(3079, 26, 147, 'سىلەر بۇ يەرلەردە، باغچىلاردىن، بۇلاقلاردىن، زىرائەتلەردىن، يۇمشاق پىشقان خورمىلاردىن بەھرىمەن بولۇپ خاتىرجەم ھالدا داۋاملىق قالىمىز (دەپ ئويلامسىلەر)'),
(3080, 26, 148, 'سىلەر بۇ يەرلەردە، باغچىلاردىن، بۇلاقلاردىن، زىرائەتلەردىن، يۇمشاق پىشقان خورمىلاردىن بەھرىمەن بولۇپ خاتىرجەم ھالدا داۋاملىق قالىمىز (دەپ ئويلامسىلەر)'),
(3081, 26, 149, 'خۇشال ھالدا تاغلارنى تېشىپ ئۆي ياسامسىلەر'),
(3082, 26, 150, 'اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار'),
(3083, 26, 151, 'زېمىندا بۇزغۇنچىلىق قىلىدىغان، ئىسلاھ قىلمايدىغان ھەددىدىن ئاشقۇچىلارنىڭ ئەمرىگە ئىتائەت قىلماڭلار»'),
(3084, 26, 152, 'زېمىندا بۇزغۇنچىلىق قىلىدىغان، ئىسلاھ قىلمايدىغان ھەددىدىن ئاشقۇچىلارنىڭ ئەمرىگە ئىتائەت قىلماڭلار»'),
(3085, 26, 153, 'ئۇلار ئېيتتى: «سەن ھەقىقەتەن سېھىر قىلىنغانلاردىنسەن'),
(3086, 26, 154, 'سەن پەقەت بىزگە ئوخشاش بىر ئىنسانسەن، ئەگەر سەن راستچىللاردىن بولساڭ، بىرەر مۆجىزە كەلتۈرۈپ باققىن»'),
(3087, 26, 155, 'سالىھ ئېيتتى: «بۇ چىشى تۆگە (سۈيۈڭلاردىن بىر كۈن) ئىچىدۇ، سىلەرمۇ مۇئەييەن بىر كۈن ئىچىسىلەر'),
(3088, 26, 156, 'ئۇنىڭغا يامانلىق قىلماڭلار، بولمىسا سىلەر بۈيۈك كۈننىڭ ئازابىغا دۇچار بولۇسىلەر»'),
(3089, 26, 157, 'ئۇلار چىشى تۆگىنى بوغۇزلىدى، شۇنىڭ بىلەن ئۇلار نادامەتكە قالدى'),
(3090, 26, 158, 'ئۇلارغا ئازاب چۈشتى، بۇنىڭدا ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى'),
(3091, 26, 159, 'سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(3092, 26, 160, 'لۇتنىڭ قەۋمى پەيغەمبەرلەرنى ئىنكار قىلدى'),
(3093, 26, 161, 'ئۆز ۋاقتىدا ئۇلارغا قېرىندىشى لۇت ئېيتتى: «سىلەر (اﷲ تىن) قورقمامسىلەر؟'),
(3094, 26, 162, 'مەن ھەقىقەتەن سىلەرگە سادىق پەيغەمبەرمەن'),
(3095, 26, 163, 'سىلەر اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار'),
(3096, 26, 164, 'تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن'),
(3097, 26, 165, 'سىلەر ئەھلى جاھان ئىچىدىن لىۋاتە قىلىپ، پەرۋەردىگارىڭلار سىلەر ئۈچۈن ياراتقان ئاياللىرىڭلارنى تاشلاپ قويامسىلەر؟ سىلەر ھەقىقەتەن (بۇزۇقچىلىقتا) ھەددىدىن ئاشقۇچى قەۋمسىلەر»'),
(3098, 26, 166, 'سىلەر ئەھلى جاھان ئىچىدىن لىۋاتە قىلىپ، پەرۋەردىگارىڭلار سىلەر ئۈچۈن ياراتقان ئاياللىرىڭلارنى تاشلاپ قويامسىلەر؟ سىلەر ھەقىقەتەن (بۇزۇقچىلىقتا) ھەددىدىن ئاشقۇچى قەۋمسىلەر»'),
(3099, 26, 167, 'ئۇلار ئېيتتى: «ئى لۇت! ئەگەر (دەۋىتىڭدىن) قايتمىساڭ، چوقۇم سۈرگۈن قىلىنىسەن»'),
(3100, 26, 168, 'لۇت ئېيتتى: «مەن سىلەرنىڭ قىلىقىڭلاردىن قاتتىق يىرگىنىمەن'),
(3101, 26, 169, 'پەرۋەردىگارىم! قىلمىشلىرى تۈپەيلىدىن (ئۇلارغا كېلىدىغان ئازابتىن) مېنى ۋە تەۋەلىرىمنى قۇتقۇزغىن»'),
(3102, 26, 170, 'ئۇنى ۋە ئۇنىڭ تەۋەلىرىنىڭ ھەممىسىنى قۇتقۇزدۇق'),
(3103, 26, 171, 'پەقەت موماينى (يەنى لۇتنىڭ ئايالىنى قالدۇرۇپ قويۇپ) ھالاك قىلدۇق'),
(3104, 26, 172, 'ئاندىن قالغانلارنى ھالاك قىلدۇق'),
(3105, 26, 173, 'ئۇلارنىڭ ئۈستىگە بىز يامغۇر (يەنى تاش) ياغدۇردۇق، ئاگاھلاندۇرۇلغۇچىلارغا ياغدۇرۇلغان يامغۇر نېمىدېگەن يامان!'),
(3106, 26, 174, 'بۇنىڭدا ئەلۋەتتە (چوڭ) بىر ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى'),
(3107, 26, 175, 'سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(3108, 26, 176, 'ئەيكىلىقلار پەيغەمبەرلىرىنى ئىنكار قىلدى'),
(3109, 26, 177, 'ئۆز ۋاقتىدا شۇئەيب ئۇلارغا ئېيتتى: «سىلەر (اﷲ تىن) قورقمامسىلەر؟'),
(3110, 26, 178, 'مەن ھەقىقەتەن سىلەرگە سادىق پەيغەمبەرمەن'),
(3111, 26, 179, 'اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار'),
(3112, 26, 180, 'تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن'),
(3113, 26, 181, 'ئۆلچەمنى تولدۇرۇپ بېرىڭلار، كەم بەرگۈچىلەردىن بولماڭلار'),
(3114, 26, 182, '(نەرسىلەرنى) توغرا تارازىدا تارتىڭلار'),
(3115, 26, 183, 'كىشىلەرگە نەرسىلىرىنى (يەنى قايسى يول بىلەن بولمىسۇن، كىشىلەرنىڭ ھەقلىرىنى) كەم بەرمەڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە - پاسات تېرىماڭلار'),
(3116, 26, 184, 'سىلەرنى ۋە ئىلگىرىكى ئۈممەتلەرنى ياراتقان اﷲ تىن قورقۇڭلار»'),
(3117, 26, 185, 'ئۇلار ئېيتتى: «سەن سېھىر قىلىنغۇچىلاردىنسەن'),
(3118, 26, 186, 'سەن پەقەت بىزگە ئوخشاش (ئاددى) ئادەمسەن، سېنى بىز ھەقىقەتەن يالغانچى دەپ گۇمان قىلىمىز'),
(3119, 26, 187, 'ئەگەر (سۆزۈڭدە) راستچىللاردىن بولساڭ، بىزگە ئاسماندىن ئازاب چۈشۈرگىن»'),
(3120, 26, 188, 'شۇئەيب ئېيتتى: «پەرۋەردىگارىم! سىلەرنىڭ قىلمىشىڭلارنى ئوبدان بىلىدۇ»'),
(3121, 26, 189, 'ئۇلار شۇئەيبنى ئىنكار قىلدى، ئۇلارنى سايە كۈنىنىڭ ئازابى ھالاك قىلدى. ئۇ ھەقىقەتەن بۈيۈك كۈننىڭ ئازابى ئىدى'),
(3122, 26, 190, 'بۇنىڭدا ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچىلاردىن بولمىدى'),
(3123, 26, 191, 'سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(3124, 26, 192, 'شۈبھىسىزكى، قۇرئان ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر'),
(3125, 26, 193, 'ئاگاھلاندۇرغۇچىلاردىن بولۇشۇڭ ئۈچۈن، ئىشەنچلىك جىبرىئىل ئۇنى سېنىڭ قەلبىڭگە ئېلىپ چۈشتى'),
(3126, 26, 194, 'ئاگاھلاندۇرغۇچىلاردىن بولۇشۇڭ ئۈچۈن، ئىشەنچلىك جىبرىئىل ئۇنى سېنىڭ قەلبىڭگە ئېلىپ چۈشتى'),
(3127, 26, 195, '(قۇرئان) ئوچۇق ئەرەبى تىلىدا (نازىل بولدى)'),
(3128, 26, 196, 'ھەقىقەتەن قۇرئان ئىلگىرىكى (پەيغەمبەرلەرنىڭ) كىتابلىرىدا تىلغا ئېلىنغان'),
(3129, 26, 197, '(ئابدۇللا ئىبن سالام ۋە ئۇنىڭ ئىمان ئېيتقان ھەمراھلىرىغا ئوخشاش) بەنى ئىسرائىل ئۆلىمالىرىنىڭ قۇرئاننى بىلىشى مۇشرىكلارغا (قۇرئاننىڭ توغرىلىقىنى كۆرسىتىدىغان) دەلىل بولمامدۇ؟'),
(3130, 26, 198, 'ئەگەر بىز قۇرئاننى ئەرەب تىلىدا بولمىغان بىر ئادەمگە نازىل قىلغان بولساق، ئۇ قۇرئاننى ئۇلارغا ئوقۇپ بەرسە، ئۇلار ئۇنىڭغا ئىشەنمەيتتى'),
(3131, 26, 199, 'ئەگەر بىز قۇرئاننى ئەرەب تىلىدا بولمىغان بىر ئادەمگە نازىل قىلغان بولساق، ئۇ قۇرئاننى ئۇلارغا ئوقۇپ بەرسە، ئۇلار ئۇنىڭغا ئىشەنمەيتتى'),
(3132, 26, 200, 'شۇنىڭدەك گۇناھكارلارنىڭ دىللىرىغا قۇرئاننى سالدۇق (ئۇلار قۇرئاننى چۈشىنىپ، ئۇنىڭ پاساھەت - بالاغىتىنى ۋە مۆجىزە ئىكەنلىكىنى بىلىپ تۇرۇپ ئىمان ئېيتمىدى'),
(3133, 26, 201, 'ئۇلار (اﷲ نىڭ) قاتتىق ئازابىنى كۆرمىگىچە قۇرئانغا ئىشەنمەيدۇ'),
(3134, 26, 202, 'ئۇ ئازاب ئۇلارغا ئۇشتۇمتۇت كېلىدۇ، ئۇلار ئۇنىڭ (كەلگەنلىكىنى) تۇيماي قالىدۇ'),
(3135, 26, 203, '(ئۇلارغا تۇيۇقسىز ئازاب كەلگەندە) ئۇلار: «بىزگە (ئىمان ئېيتىۋېلىش ئۈچۈن) مۆھلەت بېرىلەمدۇ؟» دەيدۇ'),
(3136, 26, 204, 'ئۇلار بىزنىڭ ئازابىمىزنىڭ بۇرۇنراق كېلىشىنى تىلەمدۇ؟'),
(3137, 26, 205, 'ئېيتىپ باققىنا! ئەگەر ئۇلارنى (نۇرغۇن) يىللار (نېمەتلىرىمىزدىن) بەھرىمەن قىلساق، ئاندىن ئۇلارغا ئاگاھلاندۇرۇلغان ئازاب كەلسە، ئۇلارنىڭ بەھرىمەن بولغان نېمەتلىرى (ئۇلاردىن ئازابنى دەپئى قىلىشتا) ھېچ نەرسىگە ئەسقاتمايدۇ'),
(3138, 26, 206, 'ئېيتىپ باققىنا! ئەگەر ئۇلارنى (نۇرغۇن) يىللار (نېمەتلىرىمىزدىن) بەھرىمەن قىلساق، ئاندىن ئۇلارغا ئاگاھلاندۇرۇلغان ئازاب كەلسە، ئۇلارنىڭ بەھرىمەن بولغان نېمەتلىرى (ئۇلاردىن ئازابنى دەپئى قىلىشتا) ھېچ نەرسىگە ئەسقاتمايدۇ'),
(3139, 26, 207, 'ئېيتىپ باققىنا! ئەگەر ئۇلارنى (نۇرغۇن) يىللار (نېمەتلىرىمىزدىن) بەھرىمەن قىلساق، ئاندىن ئۇلارغا ئاگاھلاندۇرۇلغان ئازاب كەلسە، ئۇلارنىڭ بەھرىمەن بولغان نېمەتلىرى (ئۇلاردىن ئازابنى دەپئى قىلىشتا) ھېچ نەرسىگە ئەسقاتمايدۇ'),
(3140, 26, 208, 'قانداقلىكى شەھەر بولمىسۇن، ۋەز - نەسىھەت قىلىش ئۈچۈن پەيغەمبەرلەرنى ئەۋەتمىگىچە، ئۇنى ھالاك قىلمىدۇق، (ئۇلارنى جازالاشتا) بىز زالىم بولمىدۇق'),
(3141, 26, 209, 'قانداقلىكى شەھەر بولمىسۇن، ۋەز - نەسىھەت قىلىش ئۈچۈن پەيغەمبەرلەرنى ئەۋەتمىگىچە، ئۇنى ھالاك قىلمىدۇق، (ئۇلارنى جازالاشتا) بىز زالىم بولمىدۇق'),
(3142, 26, 210, 'قۇرئاننى شەيتانلار ئېلىپ چۈشكىنى يوق'),
(3143, 26, 211, 'بۇ ئۇلارغا لايىق ئەمەس. ئۇلارمۇ (ئۇنىڭغا) قادىر بولالمايدۇ'),
(3144, 26, 212, 'شۈبھىسىزكى، ئۇلار (يەنى شەيتانلار) (ئوغرىلىقچە) تىڭشاشتىن مەنئى قىلىنغاندۇر'),
(3145, 26, 213, 'اﷲ تىن باشقا ھېچ ئىلاھقا ئىبادەت قىلمىغىن، ئۇنداق قىلساڭ، ئازاب قىلىنغۇچىلاردىن بولۇپ قالىسەن'),
(3146, 26, 214, 'يېقىن خىش – ئەقرىبالىرىڭنى ئاگاھلاندۇغىن'),
(3147, 26, 215, 'ساڭا ئەگەشكەن مۆمىنلەرگە مۇلايىم بولغىن'),
(3148, 26, 216, 'ئەگەر خىش - ئەقرىبالىرىڭ ساڭا ئاسىيلىق قىلسا: «مەن سىلەرنىڭ قىلمىشلىرىڭلاردىن ھەقىقەتەن ئادا - جۇدا مەن» دېگىن'),
(3149, 26, 217, 'ناھايىتى غالىب، مېھرىبان اﷲ قا يۆلەنگىن'),
(3150, 26, 218, 'اﷲ سېنى (نامازغا) قوپقىنىڭدا كۆرۈپ تۇرىدۇ'),
(3151, 26, 219, 'ناماز ئوقۇغۇچىلار ئارىسىدىكى (سەجدىگە بارغانلىق، رۇكۇغا تۇرغانلىق ۋە قىيامدا تۇرغانلىق) ھەرىكىتىڭنى كۆرۈپ تۇرىدۇ'),
(3152, 26, 220, 'شۈبھىسىزكى، اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى بىلىپ تۇرغۇچىدۇر'),
(3153, 26, 221, '(ئى مۇھەممەد! مەككە كۇففارلىرىغا ئېيتقىنكى) سىلەرگە مەن شەيتانلارنىڭ كىمگە چۈشىدىغانلىقىنى ئېيتىپ بېرەيمۇ؟'),
(3154, 26, 222, 'ئۇلار ھەربىر ئىغۋا توقۇغۇچى، گۇناھكارغا چۈشىدۇ'),
(3155, 26, 223, 'ئۇلار (پەرىشتىلەرنىڭ سۆزلىرىنى ئوغرىلىقچە) ئاڭلايدۇ، ئۇلار (يەنى شەيتانلار) نىڭ تولىسى يالغانچىلاردۇر'),
(3156, 26, 224, 'شائىرلارغا گۇمراھلار ئەگىشىدۇ'),
(3157, 26, 225, 'ئۇلارنىڭ (سۆز) ۋادىلىرىدا تېڭىرقاپ يۈرگەنلىكىنى كۆرمەمسەن؟'),
(3158, 26, 226, 'ئۇلار قىلمايدىغان نەرسىلىرىنى قىلدۇق دەپ سۆزلەيدۇ'),
(3159, 26, 227, 'پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان، اﷲ نى كۆپ زىكرى قىلغان، زۇلۇمغا ئۇچرىغاندىن كېيىن ئۆزىنى قوغدىغان شائىرلار بۇنىڭدىن مۇستەسنا، زۇلۇم قىلغۇچىلار ئۇزاققا قالماي قايسى جايغا قايتىدىغانلىقىنى بىلىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طا، سىن. بۇ، قۇرئاننىڭ ۋە روشەن كىتابنىڭ ئايەتلىرىدۇر'),
(3161, 27, 2, 'مۆمىنلەر ئۈچۈن ھىدايەتتۇر ۋە خۇش خەۋەردۇر'),
(3162, 27, 3, '(شۇنداق مۆمىنلەركى) ئۇلار نامازنى ئادا قىلىدۇ، زاكات بېرىدۇ ۋە ئاخىرەتكە جەزمەن ئىشىنىدۇ'),
(3163, 27, 4, 'ئاخىرەتكە ئىشەنمەيدىغانلارغا ئۇلارنىڭ ئەمەللىرىنى ھەقىقەتەن چىرايلىق كۆرسەتتۇق، ئۇلار (گۇمراھلىقلىرىدا) تېڭىرقاپ يۈرۈشىدۇ'),
(3164, 27, 5, 'ئۇلار (دۇنيادا) قاتتىق ئازابقا دۇچار بولىدۇ. ئاخىرەتتە ئۇلار ئەڭ زىيان تارتقۇچىلاردۇر'),
(3165, 27, 6, 'ساڭا قۇرئان ئەلۋەتتە ھېكمەت بىلەن ئىش قىلغۇچى، ھەممىنى بىلگۈچى اﷲ تەرىپىدىن بېرىلىدۇ'),
(3166, 27, 7, 'ئۆز ۋاقتىدا مۇسا ئەھلىيىسىگە: «مەن ھەقىقەتەن ئوت كۆردۈم، مەن ئوتنىڭ يېنىغا بېرىپ (يول توغرۇلۇق) بىرەر خەۋەر ئۇقۇپ كېلەي، ياكى سىلەرنىڭ ئىسسىنىشىڭلار ئۈچۈن ئوتتىن بىر چوغ ئېلىپ كېلەي» دېدى'),
(3167, 27, 8, 'مۇسا ئوتنىڭ يېنىغا كەلگەندە (مۇنداق) نىدا ئاڭلاندى: «اﷲ ئوتنىڭ يېنىدىكى كىشىنى ۋە ئوتنىڭ ئەتراپىدىكىلەرنى مۇبارەك قىلدى. ئالەملەرنىڭ پەرۋەردىگارى اﷲ پاكتۇر'),
(3168, 27, 9, 'ئى مۇسا! شۈبھىسىزكى، مەن غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ دۇرمەن'),
(3169, 27, 10, 'ھاساڭنى تاشلىغىن!» مۇسا ھاسىسىنىڭ گويا ئەجدىھادەك تېز ھەرىكەتلىنىۋاتقانلىقىنى كۆرگەندە، ئارقىسىغا بۇرۇلۇپ چېكىندى، كەينىگە (يەنى قورققىنىدىن ئەجدىھاغا) قارىيالمىدى. (اﷲ ئېيتتى) «ئى مۇسا! قورقمىغىن، مېنىڭ ھۇزۇرۇمدا پەيغەمبەرلەر ئەلۋەتتە قورقمايدۇ'),
(3170, 27, 11, 'لېكىن كىمكى (ئۆزىگە) زۇلۇم قىلسا، ئاندىن يامان ئەمەللىرىنى ياخشى ئەمەلگە ئۆزگەرتسە، مەن (ئۇنىڭغا) ناھايىتى مەغپىرەت قىلغۇچىدۇرمەن، ناھايىتى مېھرىباندۇر'),
(3171, 27, 12, 'قولۇڭنى قوينۇڭغا سالغىن، ئۇ ھېچقانداق ئىللەتسىز ئاپئاق بولۇپ چىقىدۇ، (بۇ) مېنىڭ پىرئەۋن ۋە ئۇنىڭ قەۋمىگە ئېلىپ بېرىشىڭ ئۈچۈن بەرگەن توققۇز مۆجىزەمنىڭ ئىچىدىدۇر، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى»'),
(3172, 27, 13, 'ئۇلارغا بىزنىڭ نۇرغۇن روشەن ئايەتلىرىمىز نازىل بولغان چاغدا، ئۇلار: «بۇ روشەن سېھىردۇر» دېدى'),
(3173, 27, 14, 'ئۇلار ئۇ ئايەتلەرنى ئىچىدە ئېتىراپ قىلدى، لېكىن ئۇلار ئۇنى زۇلۇم ۋە تەكەببۇرلۇق قىلىش يۈزىسىدىن ئىنكار قىلدى. بۇزغۇنچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن'),
(3174, 27, 15, 'بىز ھەقىقەتەن داۋۇدقا، سۇلايمانغا (دۇنيا ۋە دىن ئىلىملىرىدىن كەڭ) ئىلىم ئاتا قىلدۇق، ئۇلار ئېيتتى: «جىمى ھەمدۇسانا بىزنى نۇرغۇن بەندىلىرىدىن ئارتۇق قىلغان اﷲ قا خاستۇر!»'),
(3175, 27, 16, 'سۇلايمان (پەيغەمبەرلىكتە، ئىلىمدە، پادىشاھلىقتا ئاتىسى) داۋۇدقا ۋارىسلىق قىلدى. ئۇ: «ئى ئىنسانلار! بىزگە قۇشلارنىڭ تىلى تەلىم بېرىلدى. (دۇنيانىڭ نېمەتلىرىدىن) ھەممە نەرسە ئاتا قىلىندى، بۇ ئەلۋەتتە (اﷲ نىڭ) روشەن ئېھسانىدۇر» دېدى'),
(3176, 27, 17, 'سۇلايماننىڭ جىنلاردىن، ئىنسانلاردىن ۋە قۇشلاردىن بولغان قوشۇنلىرى توپلاندى (سۇلايمان ئەلەيھىسسالام ئۇلارنىڭ ئالدىدا دەبدەبە بىلەن ماڭاتتى). ئۇلار تەرتىپلىك ئورۇنلاشتۇرۇلغان ئىدى'),
(3177, 27, 18, 'ئۇلار (شامدىكى بىر) چۈمۈلىلەر ۋادىسىغا يېتىپ كەلگەندە، بىر چۈمۈلە ئېيتتى: «ئى چۈمۈلىلەر! ئۇۋىلىرىڭلارغا كىرىپ كېتىڭلار، سۇلايمان ۋە ئۇنىڭ قوشۇنى ئۇقماستىن سىلەرنى يەنچىۋەتمىسۇن»'),
(3178, 27, 19, 'سۇلايمان چۈمۈلىنىڭ سۆزىدىن تەبەسسۇم قىلىپ كۈلدى ۋە ئېيتتى: «پەرۋەردىگارىم! سەن مېنى ماڭا ۋە ئاتا - ئانامغا بەرگەن نېمىتىڭگە شۈكۈر قىلىشقا، سەن رازى بولىدىغان ياخشى ئەمەلنى قىلىشقا مۇۋەپپەق قىلغىن، رەھمىتىڭ بىلەن مېنى ياخشى بەندىلىرىڭ قاتارىغا كىرگۈزگىن»'),
(3179, 27, 20, 'ئۇ قۇشلارنى كۆزدىن كەچۈرگەندىن كېيىن ئېيتتى: «ماڭا نېمە بولدى؟ ھۆپۆپنى كۆرمەيمەنغۇ! يا ئۇ يوقاپ كەتتىمۇ'),
(3180, 27, 21, 'ئۇنى چوقۇم قاتتىق جازالايمەن، يا ئۇنى چوقۇم بوغۇزلايمەن، يا چوقۇم (يوقاپ كەتكەنلىكىنى ئاقلايدىغان) بىر روشەن دەلىل كەلتۈرىدۇ»'),
(3181, 27, 22, 'ئۇزاق ئۆتمەي ھۆپۆپ كەلدى، ئۇ ئېيتتى: «مەن سەن بىلمىگەن ئىشنى بىلىپ (كەلدىم)، ساڭا مەن سەبەدىن (يەنى يەمەندىكى سەبە شەھىرىدىن) بىر مۇھىم خەۋەر ئېلىپ كەلدىم'),
(3182, 27, 23, 'مەن ھەقىقەتەن ئۇلارغا (يەنى سەبە ئاھالىسىگە) بىر ئايالنىڭ (يەنى بىلقىسنىڭ) پادىشاھلىق قىلىۋاتقانلىقىنى كۆردۈم، ئۇنىڭغا (ئۆزىگە ۋە سەلتەنەتىگە كېرەكلىك) ھەممە نەرسە بېرىلگەن ئىكەن، ئۇ چوڭ ئەرشكە ئىگە ئىكەن'),
(3183, 27, 24, 'ئۇنىڭ ۋە قەۋمىنىڭ اﷲ نى قويۇپ قۇياشقا چوقۇنىدىغانلىقىنى بايقىدىم، شەيتان ئۇلارغا قىلمىشلىرىنى (يەنى اﷲ نى قويۇپ، قۇياشقا چوقۇنغانلىقلىرىنى) چىرايلىق كۆرسەتتى، ئۇلارنى توغرا يولدىن توستى، ئۇلار ھىدايەت تاپمايدۇ'),
(3184, 27, 25, 'ئۇلار ئاسمانلاردىكى ۋە زېمىندىكى سىرلەرنى ئاشكارىلىغۇچى، سىلەرنىڭ يوشۇرۇن ۋە ئاشكارا ئىشىڭلارنى بىلىپ تۇرغۇچى اﷲ قا سەجدە قىلمايدۇ'),
(3185, 27, 26, 'اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇ بۈيۈك ئەرشنىڭ پەرۋەردىگارىدۇر»'),
(3186, 27, 27, 'سۇلايمان ئېيتتى: «سەن راست دەۋاتامسەن، يا يالغانمۇ؟ قارايمىز'),
(3187, 27, 28, 'بۇ خېتىمنى ئېلىپ بېرىپ ئۇلارغا تاشلىغىن، ئاندىن ئۇلاردىن نېرىراق (جايدا يوشۇرۇنۇپ) تۇر، ئۇلارنىڭ قانداق جاۋاپ قايتۇرىدىغانلىقىغا قارىغىن»'),
(3188, 27, 29, 'ئۇ (يەنى بىلقىس) ئېيتتى: «ئى ئۇلۇغلار! ماڭا ھەقىقەتەن سۇلايماندىن بىر پارچە قىممەتلىك خەت كەلدى. (ئۇنىڭ مەزمۇنى شۇكى) ناھايىتى شەپقەتلىك ۋە مېھرىبان اﷲ نىڭ ئىسمى بىلەن باشلايمەن'),
(3189, 27, 30, 'ئۇ (يەنى بىلقىس) ئېيتتى: «ئى ئۇلۇغلار! ماڭا ھەقىقەتەن سۇلايماندىن بىر پارچە قىممەتلىك خەت كەلدى. (ئۇنىڭ مەزمۇنى شۇكى) ناھايىتى شەپقەتلىك ۋە مېھرىبان اﷲ نىڭ ئىسمى بىلەن باشلايمەن'),
(3190, 27, 31, 'سىلەر ماڭا ھاكاۋۇرلۇق قىلماڭلار، مېنىڭ ئالدىمغا مۇسۇلمان بولغان ھالدا كېلىڭلار»'),
(3191, 27, 32, 'ئۇ (يەنى بىلقىس) ئېيتتى: «ئى ئۇلۇغلار! مېنىڭ (بۇ) ئىشىمدا مەسلىھەت بېرىڭلار، سىلەرنى ئۈستىدە قويماي تۇرۇپ ھېچ ئىشنى بېكىتكىنىم يوق»'),
(3192, 27, 33, 'ئۇلار ئېيتتى: «بىز كۈچلۈك ۋە جەڭگىۋارمىز، ئىش سېنىڭ ئىختىيارىڭدىدۇر، (بىزنى) نېمىگە بۇيرۇيدىغانلىقىڭنى (ئويلاپ) كۆرگىن»'),
(3193, 27, 34, 'ئۇ (يەنى بىلقىس) ئېيتتى: «شۈبھىسىزكى، پادىشاھلار بىرەر شەھەرگە ھۇجۇم قىلىپ كىرسە، ئۇنى خاراب قىلىدۇ، شەھەرنىڭ مۆتىۋەر ئادەملىرىنى (ئۆلتۈرۈش، ئەسىرگە ئېلىش ۋە سۈرگۈن قىلىش بىلەن) خار قىلىدۇ، ئۇلارمۇ شۇنداق قىلىدۇ'),
(3194, 27, 35, 'مەن چوقۇم ئۇلارغا سوۋغا ئەۋەتىمەن، ئەلچىلەرنىڭ نېمە خەۋەر ئېلىپ كېلىدىغانلىقىغا قارايمەن»'),
(3195, 27, 36, 'ئەلچى سۇلايماننىڭ ئالدىغا يېتىپ كەلگەندە، سۇلايمان ئېيتتى: «سىلەر ماڭا مال بىلەن ياردەم قىلماقچىمۇ؟ اﷲ نىڭ ماڭا بەرگەنلىرى سىلەرنىڭ بەرگەنلىرىڭلاردىن ياخشىدۇر، بەلكى سىلەر سوغاڭلار بىلەن كۆرەڭلەپ كېتىسىلەر'),
(3196, 27, 37, 'سەن قايتىپ كەتكىن، ئۇلارنىڭ ئۈستىگە ئۇلار تاقابىل تۇرالمايدىغان بىر قوشۇن بىلەن چوقۇم بارىمىز، ئۇلارنى يۇرتىدىن خار، كەمسىتىلگەن ھالدا چوقۇم چىقىرىۋېتىمىز»'),
(3197, 27, 38, 'سۇلايمان ئېيتتى: «ئى ئۇلۇغلار! ئۇلار مېنىڭ قېشىمغا مۇسۇلمان بولۇپ كېلىشتىن بۇرۇن، (سىلەردىن) كىم ئۇنىڭ تەختىنى ئېلىپ كېلىدۇ»'),
(3198, 27, 39, 'جىندىن بولغان ئىفرىت ئېيتتى: «مەن ئۇنى ئورنۇڭدىن تۇرۇشتىن بۇرۇن ساڭا ئېلىپ كېلىمەن، مەن ئۇنى ئېلىپ كېلىشكە ئەلۋەتتە قادىرمەن، ئىشەنچلىكمەن»'),
(3199, 27, 40, '(نازىل بولغان) كىتابنى چوڭقۇر بىلىدىغان زات (يەنى ئاسەف ئىبن بۇرخىيا): «ئۇنى مەن ساڭا كۆزۈڭنى يۇمۇپ ئاچقۇچە ئېلىپ كېلىمەن» دېدى (ئۇ دۇئا قىلىۋېدى، تەخت دەرھال ئالدىدا ھازىر بولدى). سۇلايمان تەختنىڭ يېنىدا تۇرغانلىقىنى كۆرگەندە: «بۇ پەرۋەردىگارىمنىڭ (ماڭا قىلغان) ئېھسانىدۇر، ئۇ شۈكۈر قىلامدىم، يا تۇز كورلۇق قىلامدىم، بۇنىڭ بىلەن مېنى سىنىدى، كىمكى شۈكۈر قىلىدىكەن، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈن شۈكۈر قىلىدۇ، كىمكى تۇزكورلۇق قىلىدىكەن، (بىلىش كېرەككى) ھەقىقەتەن پەرۋەردىگارىم (ئۇنىڭ شۈكۈر قىلىشىدىن بىھاجەتتۇر)، (اﷲ نىڭ) كەرىمى كەڭدۇر» دېدى'),
(3200, 27, 41, 'سۇلايمان (بىلقىسنىڭ يېتىپ كېلىشى يېقىنلاشقان ۋاقىتتا ئۇنىڭ ئەقلىنى سىناش يۈزىسىدىن) ئېيتتى: «ئۇنىڭ (يەنى بىلقىسنىڭ) تەختىنىڭ (شەكلىنى) ئۆزگەرتىڭلار، ئۇ تەختىنى تونۇمدۇ، تونىمامدۇ، قارايمىز»'),
(3201, 27, 42, 'بىلقىس كەلگەندە (ئۇنىڭغا): «سېنىڭ تەختىڭ مۇشۇنداقمۇ؟» دېيىلدى. ئۇ: «شۇدەك تۇرىدۇ» دېدى. (سۇلايمان اﷲ نىڭ نېمىتىنى سۆزلەش يۈزىسىدىن ئېيتتى) ئۇنىڭدىن (يەنى بىلقىستىن) بۇرۇن بىزگە (اﷲ نى ۋە اﷲ نىڭ قۇدرىتىنى بىلدۈرىدىغان) ئىلىم بېرىلدى، بىز مۇسۇلمان بولدۇق'),
(3202, 27, 43, 'ئۇنى اﷲ نى قويۇپ چوقۇنغان نەرسىلىرى (اﷲ قا ئىبادەت قىلىشتىن) توستى. چۈنكى ئۇ كاپىر قەۋمدىن ئىدى'),
(3203, 27, 44, 'ئۇنىڭغا (يەنى بىلقىسقا): «سارايغا كىرگىن» دېيىلدى، ئۇ (يەنى بىلقىس) ساراينى چوڭ سۇ دەپ گۇمانلىنىپ (كىيىمىنى كۆتۈرۈپ) ئىككى پاچىقىنى ئاچتى، سۇلايمان ئېيتتى: «شۈبھىسىزكى، ئۇ ئەينەكتىن ياسالغان سارايدۇر». ئۇ (يەنى بىلقىس) ئېيتتى: «پەرۋەردىگارىم، مەن ھەقىقەتەن (اﷲ قا شېرىك كەلتۈرۈپ، قۇياشقا چوقۇنۇش بىلەن) ئۆزۈمگە زۇلۇم قىلدىم، سۇلايمان بىلەن بىللە ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا بويسۇندۇم (يەنى سۇلايماننىڭ دىنىغا ئەگىشىپ ئىسلامغا كىردىم)»'),
(3204, 27, 45, 'بىز ھەقىقەتەن سەمۇدقا (يەنى سەمۇد قەبىلىسىگە نەسەب جەھەتتىن) قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، (سالىھ) «اﷲ قا ئىبادەت قىلىڭلار» (دېدى)، ناگاھان ئۇلار جېدەللىشىدىغان (يەنى دىن بارىسىدا جىدەللىشىدىغان مۆمىنلەر ۋە كاپىرلاردىن ئىبارەت) ئىككى گۇرۇھقا بۆلۈنۈپ كەتتى'),
(3205, 27, 46, 'سالىھ ئېيتتى: «ئى قەۋمىم، سىلەر نېمىشقا رەھمەتتىن بۇرۇن ئازابنىڭ كېلىشىنى تىلەيسىلەر؟ سىلەر رەھمەتكە ئېرىشىشىڭلار ئۈچۈن نېمىشقا اﷲ تىن مەغپىرەت تەلەپ قىلمايسىلەر؟»'),
(3206, 27, 47, 'ئۇلار ئېيتتى: «(بىزگە كەلگەن قەھەتچىلىك) سېنىڭ ۋە سەن بىلەن بولغان كىشىلەرنىڭ شۇملۇقىدىن كەلدى». سالىھ ئېيتتى: «سىلەرگە كېلىدىغان ياخشى يامانلىق اﷲ تەرىپىدىن كېلىدۇ، بەلكى سىلەرنى اﷲ سىنايدۇ»'),
(3207, 27, 48, 'شەھەردە (يەنى ھىجرىدە) يەر يۈزىدە بۇزغۇنچىلىق قىلىدىغان، ئىسلاھ قىلمايدىغان توققۇز نەپەر كىشى بار ئىدى'),
(3208, 27, 49, 'ئۇلار: «ئۆز ئارا اﷲ بىلەن قەسەم قىلىڭلار» دېدى. ئۇلار ئېيتتى: «بىر كېچىدە سالىھنى ۋە ئۇنىڭ تەۋەلىرىنى چوقۇم ئۆلتۈرەيلى، ئاندىن چوقۇم ئۇنىڭ (يەنى ئۇلارنىڭ) ئىگىسىگە، ئۇ ئۆلتۈرۈلگەن چاغدا بىز ئۈستىدە ئەمەس ئىدۇق، بىز ھەقىقەتەن راستچىلمىز، دەيلى»'),
(3209, 27, 50, 'ئۇلار (سالىھقا قارشى) سۇيىقەست پىلانلىدى. ئۇلارنى (ئۇلارنىڭ ھالاك بولۇشىنى تېزلىتىش ئۈچۈن) سۇيىقەستى ئۈچۈن تۇيۇقسىز جازالىدۇق'),
(3210, 27, 51, 'ئۇلارنىڭ سۇيىقەستىنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىنكى، ئۇلارنى ۋە ئۇلارنىڭ قەۋمىنى پۈتۈنلەي ھالاك قىلدۇق'),
(3211, 27, 52, 'ئۇلار زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئەنە ئۇلارنىڭ ئۆيلىرى (ئادەمزاتتىن) خالى بولۇپ قالدى. اﷲ نىڭ (قۇدرىتىنى) بىلىدىغان قەۋم ئۈچۈن بۇنىڭدا (چوڭ) ئىبرەت بار'),
(3212, 27, 53, '(سالىھ بىلەن) ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلارنى (ئازابتىن) قۇتقۇزدۇق'),
(3213, 27, 54, '(لۇتنىڭ قىسسىسىنى بايان قىلغىن) ئەينى زاماندا لۇت ئۆزىنىڭ قەۋمىگە ئېيتتى: «سىلەر بىلىپ تۇرۇپ قەبىھ ئىشلارنى قىلامسىلەر'),
(3214, 27, 55, 'سىلەر ئاياللارنى قويۇپ، جىنسىي تەلىۋىڭلارنى ئەرلەر بىلەن قاندۇرامسىلەر؟ بەلكى سىلەر نادان قەۋمسىلەر»'),
(3215, 27, 56, 'ئۇلارنىڭ بىردىن - بىر جاۋابى: «لۇتنىڭ تەۋەلىرىنى شەھىرىڭلاردىن ھەيدەپ چىقىرىڭلار، ئۇلار پاك كىشىلەردۇر» دېيىشتىن ئىبارەت بولدى'),
(3216, 27, 57, 'بىز ئۇنى ۋە خوتۇنىدىن باشقا تەۋەلىرىنى قۇتقۇزدۇق، تەقدىرىمىز بىلەن ئۇنى (يەنى خوتۇنىنى ئازابقا) قالغۇچىلاردىن قىلدۇق'),
(3217, 27, 58, 'ئۇلارنىڭ ئۈستىگە بىز يامغۇر (يەنى تاش) ياغدۇردۇق. ئاگاھلاندۇرۇلغۇچىلارغا ياغدۇرۇلغان يامغۇر نېمىدېگەن يامان!'),
(3218, 27, 59, '«جىمى ھەمدۇسانا اﷲ قا خاستۇر! ئۇنىڭ (پەيغەمبەرلىككە) تاللىغان بەندىلىرىگە ئامانلىق تىلەيمەن!» دېگىن. اﷲ ياخشىمۇ؟ ياكى ئۇلارنىڭ شېرىك كەلتۈرگەن بۇتلىرى ياخشىمۇ؟'),
(3219, 27, 60, 'ئاسمانلارنى ۋە زېمىننى ياراتقان، سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەرگەن، ئۇنىڭ بىلەن گۈزەل باغلارنى يېتىشتۈرۈپ بەرگەن كىم؟ ئۇ باغلارنىڭ دەرەخلىرىنى ئۆستۈرۈش سىلەرنىڭ قولۇڭلاردىن كەلمەيدۇ، اﷲ تىن باشقا ئىلاھ بارمۇ؟ باشقا ئىلاھ يوقتۇر، ئۇلار (ھەقىقەتتىن) بۇرۇلۇپ كەتكەن قەۋمدۇر'),
(3220, 27, 61, 'زېمىننى) ئىنسانلارغا ۋە ھايۋانلارغا) تۇرالغۇ قىلغان، ئۇنىڭ تۈرلۈك تەرەپلىرىدە دەريالارنى ئاققۇزغان، ئۇنىڭ ئۈستىدە (تەۋرەپ كەتمەسلىكى ئۈچۈن) تاغلارنى ئورناتقان، ئىككى دېڭىز ئارىسىدا (يەنى تاتلىق سۇ بىلەن شورلۇق سۇ ئارىسىدا ئارىلىشىپ كەتمەسلىكى ئۈچۈن) توساقلارنى قىلغان كىم؟ اﷲ تىن باشقا ئىلاھ بارمۇ؟ (باشقا ئىلاھ يوق) ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى بىلمەيدۇ'),
(3221, 27, 62, 'بېشىغا كۈن چۈشكەن ئادەم دۇئا قىلسا (ئۇنىڭ دۇئاسىنى) ئىجابەت قىلىدىغان، ئۇنىڭ بېشىغا كەلگەن ئېغىرچىلىقنى كۆتۈرۈۋېتىدىغان ۋە سىلەرنى زېمىننىڭ ئورۇنباسارلىرى قىلغان كىم؟ اﷲ تىن باشقا ئىلاھ بارمۇ؟ سىلەر ئازغىنا ۋەز - نەسىھەت ئالىسىلەر'),
(3222, 27, 63, 'قۇرۇقلۇقنىڭ ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرىدا سىلەرگە يول كۆرسىتىپ بېرىدىغان، رەھمىتىدىن (يەنى يامغۇر ياغدۇرۇپ بېرىشتىن) ئىلگىرى شاماللارنى بېشارەت قىلىپ ئەۋەتىدىغان كىم؟ اﷲ تىن باشقا ئىلاھ بارمۇ؟ (ھېچ ئىلاھ يوقتۇر) اﷲ ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر'),
(3223, 27, 64, '(ئىنساننى) دەسلەپتە خەلق ئەتكەن، ئاندىن ئۇنى تىرىلدۈرىدىغان كىم؟ سىلەرگە ئاسماندىن ۋە زېمىندىن رىزىق بېرىدىغان كىم؟ اﷲ تىن باشقا ئىلاھ بارمۇ؟ (ھېچ ئىلاھ يوقتۇر) ئېيتقىنكى، «) ئەگەر دەۋايىڭلاردا) راستچىل بولىدىغان بولساڭلار، دەلىلىرىڭلارنى كەلتۈرۈڭلار»'),
(3224, 27, 65, 'اﷲ تىن بۆلەك ئاسمانلاردىكى ۋە زېمىندىكىلەر غەيبنى بىلمەيدۇ، ئۇلار (يەنى خالايىق) قاچان تىرىلىدىغانلىقلىرىنى بىلمەيدۇ'),
(3225, 27, 66, 'ئۇلارنىڭ ئاخىرەت توغرىسىدىكى ئىلمى پىشىپ يېتىلدىمۇ؟ ئۇنداق ئەمەس، ئۇلار ئاخىرەت توغرۇلۇق گۇماندىدۇر، ئۇلار ئاخىرەت توغرۇلۇق كوردۇر'),
(3226, 27, 67, 'كاپىرلار (يەنى قايتا تىرىلىشىنى ئىنكار قىلغۇچى مەككە مۇشرىكلىرى) ئېيتتى: «بىز ۋە بىزنىڭ ئاتا - بوۋىلىرىمىز توپا بولۇپ كەتكەندىن كېيىن (قەبرىمىزدىن تىرىلىپ) چوقۇم چىقىرىلامدۇق'),
(3227, 27, 68, 'ھەقىقەتەن بىزگە ۋە ئاتا - بوۋىلىرىمىزغا ئىلگىرى (قايتا تىرىلىش) ۋەدە قىلىنغان ئىدى. بۇ پەقەت بۇرۇنقىلارنىڭ قىسسىلىرىدۇر»'),
(3228, 27, 69, 'سەن (بۇ كۇففارلارغا) ئېيتقىن: «زېمىندا سەير قىلىڭلار، گۇناھكارلار (يەنى پەيغەمبەرلەرنى ئىنكار قىلغۇچىلار) نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار») ئۇلارنى اﷲ ھالاك قىلمىدىمۇ؟ ئىلگىرىكى كۇففارلارنىڭ بېشىغا كەلگەن كۈن كېيىنكى كۇففارلارنىڭ بېشىغىمۇ كېلىدۇ)'),
(3229, 27, 70, '(ئى مۇھەمممەد!) سەن ئۇلارنىڭ (ئىمان ئېيتمىغانلىقىدىن) قايغۇرمىغىن، ئۇلارنىڭ ھىيلە - مىكرىدىن ئىچىڭ پۇشمىسۇن (اﷲ سېنى ئۇلارنىڭ شەررىدىن ساقلايدۇ)'),
(3230, 27, 71, 'ئۇلار: «ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار، (سىلەر بىزگە ۋەدە قىلغان) ئازاب قاچان چۈشىدۇ» دېيىشىدۇ'),
(3231, 27, 72, '«سىلەر ئالدىراپ كەتكەن ئازابنىڭ بىر قىسمى سىلەرگە يېقىنلاشقان بولۇشى مۇمكىن» دېگىن (بۇ بەدرى سوقۇشىدا ئۇلارنىڭ ئۆلتۈرۈلۈشى ۋە ئەسىرگە ئېلىنىشىدۇر)'),
(3232, 27, 73, 'پەرۋەردىگارىڭ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر، لېكىن ئۇلارنىڭ تولىسى (پەرۋەردىگارىغا) شۈكۈر قىلمايدۇ'),
(3233, 27, 74, 'پەرۋەردىگارىڭ ئۇلارنىڭ دىللىرىدا يوشۇرغانلىرىنى ۋە ئاشكارا قىلغانلىرىنى (يەنى ئۇلارنىڭ پەيغەمبەر ئەلەيھىسسالامغا قارىتا ئاداۋىتىنى ۋە سۈيىقەستىنى) شەك - شۈبھىسىز بىلىدۇ'),
(3234, 27, 75, 'ئاسمان زېمىندا لەۋھۇلمەھپۇزغا يېزىلمىغان ھېچبىر سىر يوق'),
(3235, 27, 76, '(پەيغەمبەرلەرنڭ تۈگەنچىسى مۇھەممەد ئەلەيھىسسالامغا نازىل بولغان) بۇ قۇرئان ھەقىقەتەن ئىسرائىل ئەۋلادىغا ئۇلارنىڭ ئىختىلاپ قىلغان نەرسىلىرىنىڭ كۆپىنى بايان قىلىپ بېرىدۇ'),
(3236, 27, 77, 'قۇرئان مۆمىنلەر ئۈچۈن ھەقىقەتەن ھىدايەتتۇر ۋە رەھمەتتۇر'),
(3237, 27, 78, 'شۈبھىسىزكى، پەرۋەردىگارىڭ ئۇلار (يەنى بەنى ئىسرائىل ئەۋلادى) نىڭ ئارىسىدا (قىيامەت كۈنى) ئادىل ھۆكۈم چىقىرىدۇ، اﷲ غالىبتۇر، (بەندىلىرىنىڭ ئىشلىرىنى) بىلگۈچىدۇر'),
(3238, 27, 79, 'اﷲ قا تەۋەككۈل قىلغىن (يەنى ئىشىڭنى اﷲ قا تاپشۇرغىن، ھەممە ئىشىڭدا اﷲ قا يۆلەنگىن، اﷲ ساڭا مەدەتكاردۇر)، (ئى مۇھەممەد) شۈبھىسىزكى، سەن روشەن ھەق (دىن) دىسەن'),
(3239, 27, 80, 'سەن ئۆلۈكلەرگە ۋە يۈز ئۆرۈگەن گاسلارغا (يەنى دىللىرىنىڭ ئۆلۈكلىكىدە ئۆلۈكلەرگە ۋە ھەقنى ئاڭلىماسلىقتا گاسلارغا ئوخشايدىغان كۇففارلارغا) دەۋەتنى ئاڭلىتالمايسەن'),
(3240, 27, 81, 'سەن (دىلى) كورلارنى گۇمراھلىقتىن ئايرىپ، ھىدايەت قىلالمايسەن، سەن (دەۋىتىڭنى) پەقەت بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتىپ مۇسۇلمان بولغانلارغىلا ئاڭلىتالايسەن'),
(3241, 27, 82, 'ئۇلار (يەنى كۇففارلار) غا ئالدىنئالا ئېيتىلغان سۆز ئىشقا ئاشقاندا (يەنى ئازاپ ۋە قىيامەت يېقىنلاشقاندا)، ئۇلارغا زېمىندىن بىر تۈرلۈك ھايۋاننى چىقىرىمىزكى، ئۇ ئۇلارغا ئىنسانلارنىڭ بىزنىڭ ئايەتلىرىمىزگە ئىشەنمىگەنلىكىنى سۆزلەيدۇ'),
(3242, 27, 83, 'ئۇ كۈندە ھەر ئۈممەتنىڭ ئىچىدىن بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلاردىن بىر توپ ئادەمنى) ھېساب ئېلىش ۋە جازالاش ئۈچۈن) يىغىمىز (ئالدى - كەينى يىغىلغۇچە) ئۇلار توختىتىپ تۇرۇلىدۇ'),
(3243, 27, 84, 'ئۇلار) اﷲ نىڭ دەرگاھىغا) كەلگەن چاغلىرىدا، اﷲ ئۇلارغا: «سىلەر تولۇق بىلمەي تۇرۇپ مېنىڭ ئايەتلىرىمنى ئىنكار قىلدىڭلارمۇ؟ سىلەر (ئەمرى قىلىنغان نەرسىلەردىن) زادى نېمىلەرنى قىلدىڭلار؟» دەيدۇ'),
(3244, 27, 85, 'ئۇلار) ئۆزلىرىگە) زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارغا ئازاب ھەقلىق بولدى. ئۇلار (ئۆزرىلىرى بولمىغانلىقى ئۈچۈن) سۆز قىلالمايدۇ'),
(3245, 27, 86, 'ئۇلارغا كېچىنى ئارام ئالىدىغان (ۋاقىت) قىلغانلىقىمىزنى، كۈندۈزنى (ھاياتلىق يولىدا ھەرىكەتلىنىش ئۈچۈن) يورۇق قىلغانلىقىمىزنى ئۇلار ئۇقمامدۇ؟ بۇنىڭدا ئىمان ئېيتىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار'),
(3246, 27, 87, 'ئۇ كۈندە سۇر چىلىنىدۇ، اﷲ خالىغانلاردىن (يەنى پەرىشتىلەر، پەيغەمبەرلەر ۋە شېھىتلاردىن) باشقا ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى قورقۇنچ باسىدۇ، ھەممە اﷲ قا بويسۇنغان ھالدا كېلىدۇ'),
(3247, 27, 88, 'تاغلارنى تۇرغۇن ھالەتتە گۇمان قىلىسەن، ھالبۇكى، ئۇلار بۇلۇتتەك چۆگىلەپ تۇرىدۇ، (بۇ) ھەممە نەرسىنى پۇختا ياراتقان اﷲ نىڭ ھۈنىرىدۇر، اﷲ ھەقىقەتەن سىلەرنىڭ قىلمىشلىرىڭلاردىن تولۇق خەۋەرداردۇر'),
(3248, 27, 89, 'ياخشى ئىش قىلغانلار قىلغان ياخشىلىقىدىن ئوبدانراق مۇكاپاتقا ئېرىشىدۇ، ئۇلار بۇ كۈندە قورقۇنجىدىن ئەمىن بولىدۇ'),
(3249, 27, 90, 'يامان ئىش قىلغانلار دوزاخقا يۈزى بىلەن تاشلىنىدۇ، سىلەر پەقەت قىلمىشىڭلارغا يارىشا جازالىنىسىلەر'),
(3250, 27, 91, '(ئى مۇھەممەد! ئېيتقىنكى) «مەن پەقەت اﷲ ھۈرمەتلىك قىلغان بۇ شەھەر (يەنى مەككە مۇكەررەمە) نىڭ پەرۋەردىگارىغا ئىبادەت قىلىشقا بۇيرۇلدۇم، ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، مەن مۇسۇلمانلاردىن بولۇشقا، قۇرئان ئوقۇشقا بۇيرۇلدۇم». كىمكى ھىدايەت تاپىدىكەن، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈندۇر، كىمكى ئازىدىكەن (ئۇنىڭ ۋابالى ئۆزىگە بولىدۇ) سەن (ئۇنىڭغا) ئېيتقىنكى، «مەن پەقەت ئاگاھلاندۇرغۇچىلاردىنمەن»'),
(3251, 27, 92, '(ئى مۇھەممەد! ئېيتقىنكى) «مەن پەقەت اﷲ ھۈرمەتلىك قىلغان بۇ شەھەر (يەنى مەككە مۇكەررەمە) نىڭ پەرۋەردىگارىغا ئىبادەت قىلىشقا بۇيرۇلدۇم، ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، مەن مۇسۇلمانلاردىن بولۇشقا، قۇرئان ئوقۇشقا بۇيرۇلدۇم». كىمكى ھىدايەت تاپىدىكەن، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈندۇر، كىمكى ئازىدىكەن (ئۇنىڭ ۋابالى ئۆزىگە بولىدۇ) سەن (ئۇنىڭغا) ئېيتقىنكى، «مەن پەقەت ئاگاھلاندۇرغۇچىلاردىنمەن»'),
(3252, 27, 93, '(ئى مۇھەممەد!) ئېيتقىنكى، «جىمى ھەمدۇسانا اﷲ قا خاستۇر، اﷲ سىلەرگە (قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەر ئۇلارنى (تونۇش پايدا بەرمەيدىغان چاغدا) تونۇيسىلەر، پەرۋەردىگارىڭ سىلەرنىڭ قىلغان ئەمەللىرىڭلاردىن غاپىل ئەمەستۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طاسن، مىم'),
(3254, 28, 2, 'بۇ، روشەن كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر'),
(3255, 28, 3, 'ئىمان ئېيتقان قەۋم ئۈچۈن ساڭا مۇسا ۋە پىرئەۋن قىسسىسىنى ھەقلىق ئاساستا ئوقۇپ بېرىمىز'),
(3256, 28, 4, 'شۈبھىسىزكى، پىرئەۋن (مىسىر) زېمىنىدا (زومىگەرلىكتە) ھەددىدىن ئاشتى، ئاھالىسىنى بۆلەكلەرگە بۆلۈپ، ئۇلاردىن بىر تائىپە (يەنى بەنى ئىسرائىل) نى بوزەك قىلدى. ئۇلاردىن ئوغۇللىرىنى ئۆلتۈرۈپ، قىزلىرىنى) خىزمەتكە سېلىش ئۈچۈن) تىرىك قالدۇردى، پىرئەۋن ھەقىقەتەن بۇزغۇنچىلاردىن ئىدى'),
(3257, 28, 5, 'بىز (مىسىر) زېمىنىدا بوزەك قىلىنغانلارغا مەرھەمەت قىلىشنى ئىرادە قىلىمىز، ئۇلارنى يولباشچىلاردىن قىلىشنى، ئۇلارنى پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ (مۈلكىگە) ۋارىس قىلىشنى ئىرادە قىلىمىز'),
(3258, 28, 6, 'ئۇلارنى مىسىر زېمىنىد اكۈچ - قۇۋۋەتكە ئىگە قىلماقچىمىز، بىز پىرئەۋنگە، (ۋەزىرى) ھامانغا ۋە ئۇلارنىڭ قوشۇنىغا ئۇلار (يەنى بوزەك قىلىنغانلار) دىن قورقىدىغان نەرسىنى (يەنى بەنى ئىسرائىلنى مىسىرغا ھۆكۈمران قىلىشنى) كۆرسىتىمىز'),
(3259, 28, 7, 'مۇسانىڭ ئانىسىغا ئىلھام بىلەن بىلدۈردۇقكى: «مۇسانى ئېمىتكىن، ئۇنىڭ زىيانكەشلىككە ئۇچرىشىدىن قورقساڭ، ئۇنى (ساندۇققا سېلىپ) دەرياغا (يەنى نىل دەرياسىغا) تاشلىغىن، ئۇنى (ھالاك بولارمىكىن دەپ) قورقمىغىن، (ئۇنىڭ پىراقىدىن) قايغۇرمىغىن، ئۇنى ساڭا چوقۇم قايتۇرىمىز ۋە ئۇنى پەيغەمبەرلەردىن قىلىمىز»'),
(3260, 28, 8, 'پىرئەۋن نىڭ ئائىلىسىدىكىلەر مۇسانى ئاقىۋەت ئۆزلىرىگە دۈشمەن، ۋە خاپىلىق (نىڭ مەنبەسى) قىلىش ئۈچۈن (نىل دەرياسىدىن) سۈزۈۋالدى. شۈبھىسىزكى، پىرئەۋن، ھامان ۋە ئۇلارنىڭ قوشۇنلىرى خاتالاشقان ئىدى'),
(3261, 28, 9, 'پىرئەۋن نىڭ ئايالى: «(بۇ بالا) ماڭا ۋە ساڭا كۆزنۇرى، (يەنى خۇشاللىق) بولسۇن، ئۇنى ئۆلتۈرمەڭلار، بەلكى ئۇ بىزگە پايدا يەتكۈزەر ياكى ئۇنى ئوغۇل قىلىۋالارمىز» دېدى. ھالبۇكى، ئۇلار (پىرئەۋن نىڭ ۋە ئۇنىڭ ياردەمچىلىرىنىڭ ھالاكىتىنىڭ مۇسانىڭ قولىدا بولىدىغانلىقىنى) ئۇقمايتتى'),
(3262, 28, 10, 'مۇسانىڭ ئانىسى (مۇسانىڭ پىرئەۋتننىڭ قولىغا چۈشۈپ قالغانلىقىنى ئاڭلاپ) ئەس - ھوشىنى يوقاتتى، (ئۇنىڭ اﷲ نىڭ بالىنى قايتۇرۇش ۋەدىسىگە) ئىشەنگۈچىلەردىن بولۇشى ئۈچۈن، ئۇنىڭ كۆڭلىنى خاتىرجەم قىلمىغان بولساق، ئۇ بالىنى ئاشكارىلاپ قويغىلى تاس قالغان ئىدى'),
(3263, 28, 11, 'ئانىسى مۇسانىڭ ھەمشىرىسىگە: «(خەۋەرنى ئۇقۇش ئۈچۈن) مۇساغا ئەگەشكىن» دېدى. ھەمشىرىسى مۇسانى يىراقتىن كۆرۈپ تۇردى. ھالبۇكى، ئۇلار (ئۇنى) تونۇمايتتى'),
(3264, 28, 12, '(ئۆز ئانىسى كېلىشتىن) ئىلگىرى مۇسانى سۈت ئەمگۈزگۈچى ئاياللارنىڭ ئېمىتىشىدىن توستۇق، مۇسانىڭ ھەمشىرىسى: «سىلەرگە مۇسانى باقىدىغان ئوبدان بىر ئائىلىنى كۆرسىتىپ قويايمۇ؟ ئۇلار مۇسانى ئىخلاس بىلەن باقىدۇ» دېدى'),
(3265, 28, 13, 'ئانىسىنىڭ خۇشال بولۇشى، قايغۇرماسلىقى ۋە اﷲ نىڭ ۋەدىسىنىڭ ھەق ئىكەنلىكىنى بىلىشى ئۈچۈن بىز مۇسانى ئۇنىڭغا قايتۇردۇق، لېكىن ئىنسالارنىڭ تولىسى (اﷲ نىڭ ۋەدىسىنىڭ ھەق ئىكەنلىكىنى) بىلمەيدۇ'),
(3266, 28, 14, 'مۇسا ئۆسۈپ يېتىلگەندە، ئەقلى توشقاندا ئۇنىڭغا پەيغەمبەرلىكنى ۋە ئىلىمنى ئاتا قىلدۇق، بىز ياخشىلارغا مۇشۇنداق مۇكاپات بېرىمىز'),
(3267, 28, 15, 'مۇسا شەھەر ئاھالىسىدىن ھېچ كىشىنى كۆرگىلى بولمايدىغان بىر ۋاقىتتا (يەنى چۈشلۈك ئۇيقىدىكى چاغدا) شەھەرگە كىردى، ئۇ شەھەردە ئىككى كىشىنىڭ ئۇرۇشۇۋاتقانلىقىنى كۆردى. (ئۇرۇشۇۋاتقانلاردىن) بىرى ئۆز قەۋمىدىن بولسا، يەنە بىرى ئۇنىڭ دۈشمىنى ئىدى. ئۆز قەۋمىدىن بولغان ئادەم دۈشمىنىگە قارشى ئۇنىڭدىن ياردەم تىلىدى. مۇسا ئۇنى مۇشت بىلەن بىرنى ئۇرۇپ ئۆلتۈرۈپ قويدى. مۇسا ئېيتتى: «بۇ شەيتاننىڭ ئىشىدۇر، شۈبھىسىزكى، شەيتان ئازدۇرغۇچى ئاشكارا دۈشمەندۇر»'),
(3268, 28, 16, 'مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۆزۈمگە زۇلۇم قىلدىم. ماڭا مەغپىرەت قىلغىن». اﷲ ئۇنىڭغا مەغپىرەت قىلدى، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(3269, 28, 17, 'مۇسا ئېيتتى: «پەرۋەردىگارىم! (ماڭا مەغپىرەت قىلغانلىق) نېمىتىڭ سەۋەبى بىلەن مەن ھەرگىز گۇناھكارلارغا ياردەمچى بولمايمەن»'),
(3270, 28, 18, 'مۇسا ئەتىسى ئەتىگەندە قورقۇنچ ئىچىدەئەتراپىغا قاراپ شەھەر ئارىلاپ يۈرەتتى، ناگاھان تۈنۈگۈن ئۇنىڭدىن ياردەم تىلىگەن ئادەم (بىر قىبتى بىلەن ئۇرۇشۇپ تۇرغان ھالدا) مۇسادىن يەنە ياردەم تىلىدى، مۇسا ئۇنىڭغا: «سەن ھەقىقەتەن ئاشكارا گۇمراھ ئىكەنسەن» دېدى'),
(3271, 28, 19, 'مۇسا ئىككىسىنىڭ (ئورتاق) دۈشمىنى بولغان ئادەم (يەنى قىبتى) گە قول ئۇزاتماقچى بولغاندا، ياردەم تىلىگەن ئادەم: «ئى مۇسا! سەن تۈنۈگۈن بىر ئادەمنى ئۆلتۈرگەندەك مېنى ئۆلتۈرمەكچىمۇسەن، سەن زېمىندا تۈزىگۈچىلەردىن بولۇشنى خالىماي، پەقەت زومىگەر بولۇشنىلا خالايسەن» دېدى'),
(3272, 28, 20, 'بىر ئادەم شەھەرنىڭ يىراق يېرىدىن ئالدىراپ كېلىپ: «ئى مۇسا! شۈبھىسىزكى، كاتتىلار سېنى ئۆلتۈرۈشكە مەسلىھەت قىلىشىۋاتىدۇ، (شەھەردىن دەرھال) چىقىپ كەتكىن، مەن ئەلۋەتتە ساڭا سادىق كىشىلەردىنمەن» دېدى'),
(3273, 28, 21, 'مۇسا قورقۇنچ ئىچىدە ئەتراپقا باققان ھالدا شەھەردىن چىقتى، ئۇ: «ئى پەرۋەردىگارىم! مېنى زالىم قەۋمدىن قۇتقۇزغىن» دېدى'),
(3274, 28, 22, 'ئۇ مەديەن تەرەپكە يۈزلەنگەن چاغدا: «پەرۋەردىگارىم مېنى توغرا يولغا يېتەكلىشى مۇمكىن» دېدى'),
(3275, 28, 23, 'مۇسا مەديەندىكى بىر بۇلاقنىڭ يېنىغا كەلگەندە (ماللىرىنى) سۇغىرىۋاتقان بىر توپ كىشىلەرنى كۆردى. ئۇلاردىن باشقا يەنە (قويلىرىنى سۇدىن) توسۇپ تۇرغان ئىككى ئايالنى كۆردى. مۇسا ئۇلارغا: «سىلەرگە نېمە بولدى؟ (يەنى قويلىرىڭلارنى سۇدىن توسۇپ تۇرۇپ تۇرۇپسىلەرغۇ؟)» دېدى، ئۇلار: «پادىچىلار قويلىرىنى سۇغۇرۇپ بولغاندىن كېيىن، ئاندىن بىز سۇغىرىمىز، ئاتىمىز بولسا ياشىنىپ قالغان بوۋايدۇر» دېدى'),
(3276, 28, 24, 'مۇسا ئۇلارنىڭ قويلىرىنى سۇغۇرۇپ بەردى، ئاندىن ئارقىسىغا بۇرۇلۇپ (بىر دەرەخنىڭ) سايىسىگە بېرىپ (ئولتۇرۇپ): «پەرۋەردىگارىم! ماڭا رىزىقتىن نېمىنىلا بەرسەڭ، مەن ھەقىقەتەن ئۇنىڭغا موھتاج» دېدى'),
(3277, 28, 25, 'ئۇلارنىڭ (يەنى ئۇ ئىككى ئايالنىڭ) بىرى مۇسانىڭ يېنىغا ئۇياتچانلىق بىلەن مېڭىپ كېلىپ: «(قويلىرىمىزنى) سۇغىرىپ بەرگەنلىكىڭنىڭ ھەققىنى بېرىش ئۈچۈن ئاتام سېنى راستلا چاقىرىدۇ» دېدى. مۇسا ئۇنىڭ (يەنى شۇئەيبنىڭ) قېشىغا كېلىپ ئەھۋالنى ھېكايە قىلىپ بەردى، شۇئەيب: «قورقمىغىن، زالىم قەۋمدىن قۇتۇلدۇڭ» دېدى'),
(3278, 28, 26, 'ئۇلارنىڭ (يەنى ئاياللارنىڭ) بىرى ئېيتتى: «ئى ئاتا، ئۇنى سەن ئىشلەتكىن، بۇ سەن ئىشلەتكەنلەرنىڭ ئەڭ ياخشىسىدۇر، كۈچلۈك، ئىشەنچلىكتۇر»'),
(3279, 28, 27, 'ئۇ (يەنى شۇئەيب) ئېيتتى: «ماڭا سەككىز يىل ئىشلەپ بېرىشىڭ بەدىلىگە ساڭا بۇ ئىككى قىزىمنىڭ بىرىنى بېرىشنى خالايمەن، ئەگەر ئون يىلنى توشقۇزۇۋەتسەڭ (ئۇمۇ) ئىختىيارىڭ، (ئون يىلنى شەرت قىلىپ) سېنى مۇشەققەتكە سېلىپ قويۇشنى خالىمايمەن، خۇدا خالىسا، مېنىڭ ياخشى ئادەم ئىكەنلىكىمنى بايقايسەن»'),
(3280, 28, 28, 'مۇسا ئېيتتى: «بۇ ئىككىمىزنىڭ ئارىمىزدىكى توختامدۇر، ئىككى مۇددەت (يەنى سەككىز يىل بىلەن ئون يىل) نىڭ قايسىسىنى توشقۇزسام توشقۇزاي، ماڭا ئارتۇقچە تەلەپ قويۇشقا بولمايدۇ، اﷲ بىزنىڭ سۆزىمىزگە گۇۋاھتۇر»'),
(3281, 28, 29, 'مۇسا (ئۆز ئارا كېلىشكەن) مۇددەتنى (يەنى كېلىشكەن مۇددەتنىڭ تولۇقراقى بولغان ئون يىلنى) توشقۇزغاندىن كېيىن ئايالىنى ئېلىپ (مىسىرغا قاراپ) يولغا چىقتى، ئۇ تۇر تېغى تەرىپىدە ئوت كۆردى، ئۇ ئايالىغا: «شۈبھىسىزكى، مەن (يىراقتىن) ئوت كۆردۈم، ئۇ يەردىن مەن سىلەرگە بىر خەۋەر ئېلىپ كېلەي، يا سىلەرنىڭ ئىسسىنىشىڭلار ئۈچۈن ئوتتىن بىر پارچە چوغ ئېلىپ كېلەي» دېدى (مۇسا ئەلەيھىسسالام ئوتنىڭ يېنىغا كېلىپ، ئۇنىڭ ئوت ئەمەس نۇر ئىكەنلىكىنى بىلدى)'),
(3282, 28, 30, 'مۇسا ئوتنىڭ يېنىغا كەلگەندە ۋادىنىڭ ئوڭ تەرىپىنىڭ گىرۋىكىگە توغرا كېلىدىغان مۇبارەك جايدىكى دەرەخ تەرەپتىن (مۇنداق) نىدا ئاڭلاندى: «ئى مۇسا، (ساڭا سۆز قىلىۋاتقان) مەن، ھەقىقەتەن، ئالەملەرنىڭ پەرۋەردىگارى اﷲ دۇرمەن'),
(3283, 28, 31, 'سەن ھاساڭنى تاشلىغىن». مۇسا ھاسىسىنىڭ گويا ئەجدىھادەك تېز ھەرىكەتلىنىۋاتقانلىقىنى كۆرگەندە ئارقىسىغا بۇرۇلۇپ قاچتى، كەينىگە قارىمىدى، (نىدا قىلىندىكى) «ئى مۇسا! ئالدىڭغا ماڭغىن، قورقمىغىن، سەن ھەقىقەتەن (قۇرقۇنچتىن) ئەمىن بولغۇچىلاردىنسەن'),
(3284, 28, 32, 'قولۇڭنى قوينۇڭغا سالغىن، ئۇ ھېچقانداق ئىللەتسىز (بىر پارچە ئايدەك پارلاپ) ئاپئاق بولۇپ چىقىدۇ، قورقۇنچنى دەپئى قىلىش ئۈچۈن، قولۇڭنى يىغىۋالغىن (يەنى قوينۇڭغا سېلىۋالغىن)، بۇ ئىككىسى پىرئەۋنگە (ۋە ئۇنىڭ قەۋمىنىڭ) چوڭلىرىغا پەرۋەردىگارىڭ تەرىپىدىن كەلگەن پاكىتتۇر، ئۇلار ھەقىقەتەن (بىزنىڭ ئىتائىتىمىزدىن چىققۇچى) پاسىق قەۋم ئىدى»'),
(3285, 28, 33, 'مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۇلاردىن بىر ئادەمنى ئۆلتۈرۈپ قويدۇم، شۇنىڭ ئۈچۈن ئۇلارنىڭ مېنى ئۆلتۈرۈشىدىن قورقىمەن'),
(3286, 28, 34, 'قېرىندىشىم ھارۇننىڭ تىلى مېنىڭدىن راۋاندۇر، ئۇنى مەن بىلەن (پەيغەمبەرلىكىمنى) تەستىقلايدىغان ياردەمچى قىلىپ ئەۋەتكىن، مەن ھەقىقەتەن ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن قورقىمەن»'),
(3287, 28, 35, 'اﷲ ئېيتتى: «قېرىندىشىڭ ئارقىلىق سېنى كۈچەيتىمىز، ئىككىڭلارنى غالىب قىلىمىز، شۇنىڭ ئۈچۈن ئۇلار ئىككىڭلارغا زىيانكەشلىك قىلالمايدۇ، سىلەر ئىككىڭلارۋە ئىككىڭلارغا ئەگەشكەنلەر بىزنىڭ ئايەتلىرىمىز ئارقىلىق غەلىبە قىلىسىلەر»'),
(3288, 28, 36, 'مۇسا ئۇلارغا بىزنىڭ روشەن مۆجىزىلىرىمىزنى ئېلىپ كەلگەندە، ئۇلار: «بۇ ئويدۇرۇپ چىقىرىلغان سېھىردۇر، بىزنىڭ ئاتا - بوۋىلىرىمىزنىڭ (زامانىدا) بۇنداق نەرسىلەرنىڭ بارلىقىنى ئاڭلىمىغان ئىدۇق» دېدى'),
(3289, 28, 37, 'مۇسا ئېيتتى: «پەرۋەردىگارىم! ئۆزىنىڭ دەرگاھىدىن كىمنىڭ ھىدايەت ئېلىپ كەلگەنلىكىنى، ئاخىرەتنىڭ ياخشى ئاقىۋىتىگە كىمنىڭ ئىگە بولىدىغانلىقىنى ئوبدان بىلىدۇ، زالىملار (يەنى اﷲ قا يالغاننى چاپلىغۇچى فاجىرلار) چوقۇم مۇۋەپپەقىيەت قازىنالمايدۇ»'),
(3290, 28, 38, 'پىرئەۋن ئېيتتى: «ئى كاتتىلار! مەن ئۆزۈمدىن باشقا يەنە بىر ئىلاھنىڭ بارلىقىنى بىلمەيمەن. ئى ھامان، مېنىڭ ئۈچۈن پىششىق خىش پىشۇرۇپ ئېگىز بىر بىنا سالغىن، مەن مۇسانىڭ ئىلاھىنى كۆرۈشۈم مۇمكىن، مەن ھەقىقەتەن ئۇنى (ئاسماندا بىر پەرۋەردىگار بار دېگەن دەۋاسىدا) يالغانچىلاردىن گۇمان قىلىمەن»'),
(3291, 28, 39, 'پىرئەۋن ۋە ئۇنىڭ قوشۇنى (مىسىر) زېمىنىدا زۇلۇم قىلىپ) ئىمان ئېيتىشقا) بويۇنتاۋلىق قىلدى. ئۇلار ئۆزلىرىنى بىزتەرەپكە قايتۇرۇلمايمىز) يەنى قىيامەت ۋە قايتا تىرىلىش يوق، ھېساب ئېلىنىشمۇ، جازاغا تارتىلىشمۇيوق) دەپ گۇمان قىلدى'),
(3292, 28, 40, 'بىز پىرئەۋننى ۋە ئۇنىڭ قوشۇنلىرىنى جازالىدۇق، ئۇلارنى دېڭىزغا تاشلىۋەتتۇق (يەنى ئۇلارنى دېڭىزدا غەرق قىلىۋەتتۇق، ئۇلاردىن بىر ئادەممۇ ساق قالغىنى يوق)، (ئى مۇھەممەد!) زالىملارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا (ئىبرەت كۆزى بىلەن) قارىغىن'),
(3293, 28, 41, 'ئۇلارنى. بىز دوزاخقا ئۈندەيدىغان پېشۋالار قىلدۇق (يەنى ئۇلارنى بىز دۇنيادا گۇمراھلار ئەگىشىدىغان كۇفرىنىڭ باشلىقلىرى قىلدۇق)، قىيامەت كۈنى ئۇلار ياردەمگە ئېرىشەلمەيدۇ (يەنى قىيامەت كۈنى ئۇلاردىن ئازابنى دەپئى قىلىدىغان مەدەتكار بولمايدۇ)'),
(3294, 28, 42, 'ئۇلارغا بۇ دۇنيادا لەنەتنى ئەگەشتۈردۇق (يەنى ئۇلارغا بۇ دۇنيادا اﷲ، پەرىشتىلەر ۋە مۆمىنلەر لەنەت ئوقۇيدۇ)، قىيامەت كۈنى ئۇلار اﷲ نىڭ رەھمىتىدىن يىراق قىلىنغۇچىلاردۇر'),
(3295, 28, 43, 'شۈبھىسىزكى، بىز (نۇھنىڭ قەۋمى ئاد، سەمۇد ۋە لۇتنىڭ قەۋمى قاتارلىق) ئىلگىرىكى ئۈممەتلەرنى ھالاك قىلغاندىن كېيىن، كىشىلەر (يەنى بەنى ئىسرائىل) نىڭ دىللىرىغا (ھەقىقەتنى كۆرىدىغان) نۇر بېرىپ، ئۇلارغا ھىدايەت ۋە رەھمەت قىلدۇق، ئۇلار ۋەز - نەسىھەت ئالسۇن دەپ، مۇساغا كىتابنى (يەنى تەۋراتنى) بەردۇق'),
(3296, 28, 44, '(ئى مۇھەممەد!) بىز غەربىي تاغنىڭ بىر تەرىپىدە مۇساغا ۋەھيى نازىل قىلغىنىمىزدا سەن يوق ئىدىڭ، (بۇنى) ئۆز كۆزۈڭ بىلەنمۇ كۆرمىگەن ئىدىڭ'),
(3297, 28, 45, 'لېكىن بىز نۇرغۇن ئۈممەتلەرنى ياراتتۇق، ئۇلارنىڭ ئۆمرى ئۇزۇن بولدى، سەن مەديەن ئاھالىسىنىڭ ئىچىدە ئۇلارغا بىزنىڭ ئايەتلىرىمىزنى تىلاۋەت قىلىپ تۇرغۇچى بولغىنىڭ يوق، لېكىن بىز (سېنى ئۇلاردىن باشقا بىر قەۋمگە پەيغەمبەر قىلىپ) ئەۋەتتۇق'),
(3298, 28, 46, 'بىز تۇر تېغىنىڭ بىر تەرىپىدە (مۇساغا) سۆز قىلغىنىمىزدا سەن يوق ئىدىڭ، لېكىن بۇ سەندىن ئىلگىرى ھېچبىر ئاگاھلاندۇرغۇچى كەلمىگەن قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، ئۇلارنىڭ ۋەز - نەسىھەت ئېلىشى ئۈچۈن، پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان رەھمەتتۇر'),
(3299, 28, 47, 'ئۇلارنىڭ قىلغان ئىشلىرى (يەنى كۇفرى ۋە گۇناھلىرى) تۈپەيلىدىن، ئۇلارغا بىرەر ئازاب كەلسە، «پەرۋەردىگارىمىز! بىزگە بىرەر پەيغەمبەر ئەۋەتكەن بولساڭ ئىدىڭ، سېنىڭ ئايەتلىرىڭگە ئەگىشەتتۇق ۋە مۆمىنلەردىن بولاتتۇق» دېمەسلىكلىرى ئۈچۈن (سېنى ئۇلارغا پەيغەمبەر قىلىپ ئەۋەتتۇق)'),
(3300, 28, 48, 'ئۇلارغا بىز ۋەھى قىلغان ھەقىقەت نازىل بولغان چاغدا، ئۇلار: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) نېمىشقا مۇساغا بېرىلگەن مۆجىزە بېرىلمىدى» دېدى. ئۇلار ئىلگىرى مۇساغا بېرىلگەن مۆجىزىنى ئىنكار قىلمىدىمۇ؟ ئۇلار: «(تەۋرات بىلەن قۇرئان) بىر - بىرىنى تەستىق قىلىدىغان سېھىردۇر» دېدى. ئۇلار: «بىز ھەقىقەتەن (ئىككى كىتابنىڭ) ھەربىرىنى ئىنكار قىلغۇچىمىز» دېدى'),
(3301, 28, 49, 'ئېيتقىنكى، «ئەگەر (ئۇ ئىككى كىتاب سېھىردۇر دېگەن سۆزۈڭلاردا) راستچىل بولىدىغان بولساڭلار، ئۇ ئىككى كىتابقا قارىغاندا، اﷲ تەرىپىدىن نازىل بولغان تېخىمۇ توغرا بىر كىتاب كەلتۈرۈپ بېقىڭلار، مەن ئۇنىڭغا ئەگىشەي»'),
(3302, 28, 50, 'ئەگەر ئۇلار ساڭا جاۋاپ بەرمىسە، بىلگىنكى، ئۇلار پەقەت ئۆزلىرىنىڭ نەپسى خاھىشىغىلا ئەگىشىدۇ، اﷲ ۋەھىي قىلغان توغرا يولنى قويۇپ نەپسى خاھىشىغا ئەگەشكەن كىشىدىنمۇ ئازغۇن ئادەم بارمۇ؟ اﷲ ھەقىقەتەن زالىم قەۋمنى ھىدايەت قىلمايدۇ'),
(3303, 28, 51, 'ۋەز - نەسىھەت ئالسۇن دەپ، شۈبھىسىزكى، ئۇلارغا قۇرئاننى ئۈزۈلدۈرمەي چۈشۈردۇق'),
(3304, 28, 52, 'قۇرئاندىن ئىلگىرى بىز كىتاب (يەنى ئىنجىل، تەۋرات) نازىل قىلغانلار (يەنى ناسارا ۋە يەھۇدىيلارنىڭ مۆمىنلىرى) قۇرئانغا ئىشىنىدۇ'),
(3305, 28, 53, 'ئۇلارغا قۇرئان تىلاۋەت قىلىنغان چاغدا، ئۇلار: «بىز قۇرئانغا ئىشەندۇق. شۈبھىسىزكى، ئۇ پەرۋەردىگارىمىز تەرىپىدىن نازىل بولغان ھەقىقەتتۇر. بىز ھەقىقەتەن بۇنىڭدىن بۇرۇن مۇسۇلمان ئىدۇق» دەيدۇ'),
(3306, 28, 54, 'ئۇلارنىڭ قىلغان سەۋر - تاقىتى ئۈچۈن ئۇلارغا قوش ساۋاب بېرىلىدۇ، ئۇلار ياخشىلىق ئارقىلىق يامانلىقنى دەپئى قىلىدۇ، بىز ئۇلارغا رىزىق قىلىپ بەرگەن نەرسىلەردىن ياخشىلىق يوللىرىغا سەرپ قىلىدۇ'),
(3307, 28, 55, 'ئۇلار بىھۇدە سۆزلەرنى ئاڭلىغاندا ئۇنىڭدىن يۈز ئۆرۈپ (يەنى قۇلاق سالماي): «بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈن، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزۈڭلار ئۈچۈن، سىلەرگە ئامانلىق بولسۇن، بىز نادانلاردىن دوستلۇق تىلىمەيمىز» دەيدۇ'),
(3308, 28, 56, 'شۈبھىسىزكى، سەن خالىغان ئادەمىڭنى ھىدايەت قىلالمايسەن، لېكىن اﷲ ئۆزى خالىغان ئادەمنى ھىدايەت قىلىدۇ، اﷲ ھىدايەت تاپقۇچىلارنى ئوبدان بىلىدۇ'),
(3309, 28, 57, 'ئۇلار: «ئەگەر بىز سەن بىلەن توغرا يولغا ئەگىشىدىغان بولساق، زېمىنىمىزدىن ھەيدەپ چىقىرىلىمىز» دېدى. ئۇلارنى بىز تېچ ھەرەمگە يەرلەشتۈرمىدۇقمۇ؟ ھەرەمگە تۈرلۈك مېۋىلەرنىڭ ھەممىسى كەلتۈرۈلىدۇ، (بۇ) بىزنىڭ دەرگاھىمىزدىن چۈشۈرۈلگەن رىزىقتۇر، لېكىن ئۇلارنىڭ تولىسى بىلمەيدۇ'),
(3310, 28, 58, 'بىز نۇرغۇن شەھەرنى ھالاك قىلدۇق، ئۇلارنىڭ ئاھالىسى كەيىپ - ساپالىق تۇرمۇش كۆچۈرەتتى، بۇ ئۇلارنىڭ تۇرالغۇ جايلىرىدۇر، ئۇلار ھالاك بولغاندىن كېيىن (ئۇ جايلاردا) ئازغىنا ۋاقىتتىن تاشقىرى ئادەم تۇرغان ئەمەس، ئۇلار (نىڭ مال - مۈلكى ۋە يۇرتىغا) بىز ۋارىسلىق قىلدۇق'),
(3311, 28, 59, 'پەرۋەردىگارىڭ شەھەرلەرنىڭ مەركىزىگە، ئۇلارنىڭ ئاھالىسىگە بىزنىڭ ئايەتلىرىمىزنى ئوقۇپ بېرىدىغان بىر پەيغەمبەر ئەۋەتمىگىچە شەھەرلەرنى ھالاك قىلغۇچى بولمىدۇق، شەھەرلەرنىڭ ئاھالىسى زالىم بولمىغىچە شەھەرلەرنى ھالاك قىلغۇچىمۇ بولمىدۇق'),
(3312, 28, 60, 'سىلەرگە قانداق نەرسە بېرىلمىسۇن، (ئۇ) دۇنيا تىرىكچىلىكىدىكى پايدىلىنىدىغان نەرسىدۇر ۋە دۇنيا تىرىكچىلىكىنىڭ زىننىتىدۇر، اﷲ نىڭ دەرگاھىدىكى ساۋاب ئەڭ ياخشىدۇر، ئەڭ باقىيدۇر، چۈشەنمەمسىلەر؟'),
(3313, 28, 61, 'بىز ياخشى ۋەدىنى قىلغان (يەنى جەننەتنى ۋەدە قىلغان) ئادەم ئۇنى تاپىدۇ، (ئۇ) دۇنيا تىرىكچىلىكىدىكى پايدىلىنىدىغان نەرسىلەر بىلەن بىز پايدىلاندۇرغان، ئاندىن قىيامەت كۈنى ئازابقا دۇچار بولغان ئادەم بىلەن ئوخشاشمۇ'),
(3314, 28, 62, 'شۇ كۈنى اﷲ ئۇلارغا: «سىلەر گۇمان قىلغان مېنىڭ شېرىكلىرىم قەيەردە؟» دەپ نىدا قىلىدۇ'),
(3315, 28, 63, 'ئۆزلىرىگە ئازاب تېگىشلىك بولغانلار (يەنى مۇشرىكلارنىڭ چوڭلىرى): «پەرۋەردىگارىمىز! بىز (سېنىڭ يولۇڭدىن) ئازدۇرغانلارنى (يەنى ئەگەشكۈچىلىرىمىزنى) ئۆزىمىز ئازغاندەك ئازدۇردۇق، (پەرۋەردىگارىمىز!) ئۇلاردىن يىراقلىشىپ ساڭا يېقىنلاشتۇق، ئۇلار بىزگە ئىبادەت قىلمايتتى (پەقەت نەپسى خاھىشلىرىغا ئىبادەت قىلاتتى)» دېيىشىدۇ'),
(3316, 28, 64, 'ئۇلارغا: «شېرىك كەلتۈرگەن مەبۇدلىرىڭلارنى چاقىرىڭلار» دېيىلگەن ھامان، ئۇلار شېرىك كەلتۈرگەن مەبۇدلىرىنى چاقىرىدۇ، ئۇلاردىن جاۋاب كەلمەيدۇ، ئۇلار ئازابنى كۆرگەن چاغدا، (كاپىر بولغانلىقلىرىغا نادامەت قىلىپ) (دۇنيادىكى ۋاقتىدا) ھىدايەت تاپقان بولۇشلىرىنى ئارزۇ قىلىدۇ'),
(3317, 28, 65, 'ئۇ كۈندە اﷲ ئۇلارغا نىدا قىلىپ ئېيتىدۇ: «پەيغەمبەرلەرگەنېمە بىلەن جاۋاپ بەردىڭلار؟»'),
(3318, 28, 66, 'ئۇ كۈندە ئۇلارغا بارلىق خەۋەرلەر قاراڭغۇ بولىدۇ، ئۇلار ئۆز ئارا سوراشمايدۇ'),
(3319, 28, 67, 'تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغان ئادەمگە كەلسەك، ئۇنىڭ مەقسەتكە ئېرىشكۈچىلەردىن بولۇشى ئۈمىدلىكتۇر'),
(3320, 28, 68, 'اﷲ (مەخلۇقاتىدىن) خالىغىنىنى يارىتىدۇ، خالىغان ئادەمنى (پەيغەمبەرلىككە) تاللايدۇ، تاللاش ھوقوقى ئۇلاردا ئەمەس، اﷲ پاكتۇر، ئۇلارنىڭ شېرىك كەلتۈرگەنلىرىدىن ئۈستۈندۇر'),
(3321, 28, 69, 'پەرۋەردىگارىڭ ئۇلارنىڭ دىللىرىدا يوشۇرۇن تۇتقانلىرىنى ۋە (ئېغىزلىرىدا) دېگەنلىرىنى بىلىپ تۇرىدۇ'),
(3322, 28, 70, 'ئۇ اﷲ دۇر، ئۇنىڭدىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، دۇنيا ۋە ئاخىرەتتە جىمى ھەمدۇسانا اﷲ قا مەنسۇپتۇر، ھۆكۈم چىقىرىش اﷲ قا مەنسۇپتۇر، سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر'),
(3323, 28, 71, '«ئېيتىپ بېقىڭلارچۇ! ئەگەر اﷲ كېچىنى سىلەر ئۈچۈن قىيامەت كۈنىگىچە ئۇزۇن سوزىدىغان بولسا، اﷲ تىن بۆلەك قايسى ئىلاھ سىلەرگە يورۇقلۇق ئېلىپ كېلەلەيدۇ، سىلەر ئاڭلىمامسىلەر؟» دېگىن'),
(3324, 28, 72, '«ئېيتىپ بېقىڭلارچۇ؟ ئەگەر اﷲ كۈندۈزنى سىلەر ئۈچۈن قىيامەت كۈنىگىچە سوزىدىغان بولسا، اﷲ تىن بۆلەك قايسى ئىلاھ سىلەرگە ئارام ئېلىشىڭلار ئۈچۈن كېچىنى ئېلىپ كېلەلەيدۇ؟ سىلەر كۆرمەمسىلەر؟» دېگىن'),
(3325, 28, 73, 'سىلەرنى كېچىدە ئارام ئالسۇن، كۈندۈزدە (ھاياتلىق يولىدا ھەرىكەتلىنىپ) اﷲ نىڭ پەزلىنى تەلەپ قىلسۇن ۋە (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلسۇن دەپ، سىلەر ئۈچۈن كېچە بىلەن كۈندۈزنى يارىتىشى اﷲ نىڭ رەھمىتىدىندۇر'),
(3326, 28, 74, 'ئۇ كۈندە اﷲ ئۇلارغا نىدا قىلىپ: «سىلەر گۇمان قىلغان مېنىڭ شېرىكلىرىم قەيەردە؟» دەيدۇ'),
(3327, 28, 75, 'بىز ھەر ئۈممەتتىن) ئۇلارنىڭ ئەمەللىرىگە گۇۋاھلىق بېرىدىغان) بىر گۇۋاھچى (يەنى پەيغەمبەر) نى چىقىرىمىز، پاكىتىڭلارنى كەلتۈرۈڭلار دەيمىز، ئۇلار ھەقىقەتنىڭ اﷲ قا مەنسۇپ ئىكەنلىكىنى ئاندىن بىلىدۇ، ئۇلارنىڭ ئويدۇرۇپ چىققان نەرسىلىرى (يەنى مەبۇدلىرى) ئۆزلىرىنى ئۇلاردىن چەتكە ئالىدۇ'),
(3328, 28, 76, 'قارۇن ھەقىقەتەن مۇسانىڭ قەۋمىدىن ئىدى. قارۇن ئۇلارغا يوغانلىق قىلدى، قارۇنغا خەزىنىلەردىن شۇ قەدەر بەرگەن ئىدۇقكى، ئۇلارنىڭ ئاچقۇچلىرىنى (كۆتۈرۈش) كۈچلۈك بىر جامائەگىمۇ ھەقىقەتەن ئېغىرلىق قىلاتتى، ئەينى ۋاقىتتا قارۇنغا قەۋمى ئېيتتى: «كۆرەڭلەپ كەتمە، اﷲ ھەقىقەتەن كۆرەڭلەپ كەتكۈچىلەرنى دوست تۇتمايدۇ'),
(3329, 28, 77, 'اﷲ ساڭا بەرگەن بايلىق بىلەن ئاخىرەت يۇرتىنى تىلىگىن، دۇنيادىكى نېسىۋەڭنىمۇ ئۇنتۇمىغىن، اﷲ ساڭا ياخشىلىق قىلغاندەك، سەن (اﷲ نىڭ بەندىلىرىگىمۇ) ياخشىلىق قىلغىن، يەر يۈزىدە بۇزغۇنچىلىقنى تىلىمىگىن، اﷲ ھەقىقەتەن بۇزغۇنچىلىق قىلغۇچىلارنى دوست تۇتمايدۇ»'),
(3330, 28, 78, 'قارۇن: «مېنىڭ ئالاھىدە بىلىمىم بولغانلىقتىن بۇ بايلىققا ئېرىشتىم» دېدى. قارۇندىن ئىلگىرى ئۆتكەن ئۈممەتلەردىن ئۇنىڭغا قارىغاندا تېخىمۇ كۈچلۈك، توپلىغان (مېلى) تېخىمۇ كۆپ بولغان كىشىلەرنى اﷲ نىڭ ھالاك قىلغانلىقىنى ئۇ بىلمىدىمۇ؟ گۇناھكارلارنىڭ گۇناھلىرىنىڭ سورىلىشى (اﷲ بۇنى بىلگەنلىكى ئۈچۈن) ھاجەتسىزدۇر'),
(3331, 28, 79, 'قارۇن ئۆز قەۋمىنىڭ ئالدىغا بارلىق زىننىتى بىلەن ھەشەمەتلىك ھالدا چىقتى. دۇنيا تىرىكچىلىكىنى كۆزلەيدىغانلار: «كاشكى بىزگە قارۇنغا بېرىلگەن بايلىق بېرىلسىچۇ، ئۇ ھەقىقەتەن (دۇنيالىقتىن) چوڭ نېسىۋىگە ئىگە ئىكەن» دېدى'),
(3332, 28, 80, 'ئىلىملىك كىشىلەر: «ۋاي سىلەرگە! ئىمان ئېيتقان ۋە ياخشى ئەمەلنى قىلغان كىشىگە اﷲ نىڭ ساۋابى ياخشىدۇر. ئۇ ساۋاب پەقەت سەۋر قىلغۇچىلارغىلا بېرىلىدۇ» دېدى'),
(3333, 28, 81, 'قارۇننى ئۇنىڭ ئۆيى بىلەن قوشۇپ يەرگە يۇتقۇزدۇق، ئۇنىڭدىن اﷲ نىڭ ئازابىنى دەپئى قىلىدىغان بىر جامائە بولمىدى. قارۇن ئۆزىنى قوغدىيالمىدى'),
(3334, 28, 82, 'تۈنۈگۈن تېخى قارۇننىڭ دەرىجىسىنى ئارزۇ قىلغانلار دېيىشتىكى، «پاھ كۆردۈڭمۇ؟ اﷲ بەندىلىرىدىن خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدىكەن، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدىكەن، اﷲ بىزگە مەرھەمەت قىلمىغان (يەنى ئازۇ قىلغىنىمىزنى بەرگەن) بولسا ئىدى، بىزنى ئەلۋەتتە يەر يۇتاتتى، ۋاي كۆردۈڭمۇ؟ كاپىرلار نىجات تاپمايدۇ»'),
(3335, 28, 83, 'ئەنە شۇ ئاخىرەت يۇرتىنى يەر يۈزىدە چوڭچىلىق قىلىشنى ۋە بۇزغۇنچىلىق قىلىشنى كۆزلىمەيدىغانلارغا خاس قىلدۇق، (ياخشى) ئاقىۋەت تەقۋادارلارغا مەنسۇپتۇر'),
(3336, 28, 84, 'كىمكى ياخشى ئىش قىلغان ئىكەن، ئۇ قىلغان ئىشلىرىدىنمۇ ياخشى مۇكاپاتقا (يەنى قىلغان ياخشىلىقىدىن نەچچە ھەسسە ئارتۇق ساۋابقا) ئېرىشىدۇ، كىمكى يامان ئىش قىلىدىكەن، يامان ئىش قىلغانلارغا پەقەت قىلغان ئىشلىرىغا يارىشا جازا بېرىلىدۇ'),
(3337, 28, 85, 'ساڭا قۇرئاننى (يەنى ئۇنى تەبلىغ قىلىشنى ۋە ئۇنىڭ ئەھكامىغا ئەمەل قىلىشنى) پەرز قىلغان زات سېنى ئەلۋەتتە قايتىدىغان يەرگە (يەنى مەككىگە) قايتۇرىدۇ، ئېيتقىنكى، «كىمنىڭ ھىدايەت ئېلىپ كەلگەنلىكىنى، كىمنىڭ ئوپئوچۇق گۇمراھلىقتا ئىكەنلىكىنى پەرۋەردىگارىم ھەممىدىن ئوبدان بىلىدۇ»'),
(3338, 28, 86, 'سەن (پەيغەمبەر بولۇشتىن ئىلگىرى) قۇرئاننىڭ ساڭا نازىل قىلىنىشىنى ئۈمىد قىلمىغان ئىدىڭ، پەقەت پەرۋەردىگارىڭ رەھمەت قىلىپ (ساڭا ئۇنى نازىل قىلدى)، كاپىرلارغا ياردەمچى بولمىغىن'),
(3339, 28, 87, 'ساڭا اﷲ نىڭ ئايەتلىرى نازىل قىلىنغاندىن كېيىن، ئۇ ئايەتلەرگە (ئەمەل قىلىش) تىن كاپىرلار سېنى توسمىسۇن، پەرۋەردىگارىڭغا (يەنى ئۇنى بىردەپ بىلىشكە ۋە ئۇنىڭغا ئىبادەت قىلىشقا) دەۋەت قىلغىن، ھەرگىزمۇ مۇشرىكلاردىن بولمىغىن (يەنى ئۇلارنىڭ نەپسى خاھىشلىرىغا ماسلاشمىغىن)'),
(3340, 28, 88, 'اﷲ قا قوشۇپ باشقا بىر ئىلاھقا ئىبادەت قىلما، اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ نىڭ زاتىدىن باشقا بارلىق نەرسە يوقىلىدۇ، (مەخلۇقاتتا ئىجرا بولىدىغان) ھۆكۈم پەقەت اﷲ قا خاستۇر، سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'ئەلىف، لام، مىم'),
(3342, 29, 2, 'ئىنسانلار «ئىمان ئېيتتۇق» دەپ قويۇش بىلەنلا سىنالماي تەرك ئېتىلىمىز، دەپ ئويلامدۇ؟'),
(3343, 29, 3, 'ئۇلاردىن بۇرۇن ئۆتكەنلەرنى بىز ھەقىقەتەن سىنىدۇق، اﷲ (ئىمانىدا) راستچىللارنى چوقۇم بىلىدۇ، (ئىمانىدا) يالغانچىلارنىمۇ چوقۇم بىلىدۇ'),
(3344, 29, 4, 'يامان ئىش قىلىدىغانلار بىزدىن (يەنى ئازابىمىزدىن) قېچىپ قۇتۇلالايمىز دەپ ئويلايدۇ. ئۇلارنىڭ چىقارغان ھۆكمى نېمىدېگەن يامان!'),
(3345, 29, 5, 'كىمكى اﷲ قا مۇلاقات بولۇشنى ئۈمىد قىلىدىكەن، (بىلسۇنكى) اﷲ نىڭ (بۇنىڭغا بەلگىلىگەن) ۋاقتى چوقۇم يېتىپ كېلىدۇ، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلىرىنىڭ تاشقى ۋە ئىچكى ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر'),
(3346, 29, 6, 'كىمكى جىھاد قىلىدىكەن، ئۇنىڭ قىلغان جىھادى ئۆزىنىڭ پايدىسى ئۈچۈندۇر، اﷲ ھەقىقەتەن تامامى ئەھلى جاھاندىن بىھاجەتتۇر'),
(3347, 29, 7, 'شۈبھىسىزكى، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ گۇناھلىرىنى يوققا چىقىرىمىز، ئۇلارنى ئەلۋەتتە قىلغان ئەمەللىرىنىڭ ئەڭ ياخشىسى بىىلەن مۇكاپاتلايمىز'),
(3348, 29, 8, 'ئىنساننى ئاتا - ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق، ئەگەر ئاتا - ئاناڭ سېنى سەن بىلمەيدىغان نەرسىنى ماڭا شېرىك كەلتۈرۈشكە زورلىسا، ئۇلارغا ئىتائەت قىلمىغىن، سىلەر مېنىڭ دەرگاھىمغا قايتىپ بارىسىلەر، سىلەرگە قىمىشلىرىڭلارنى خەۋەر قىلىمەن'),
(3349, 29, 9, 'ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارنى چوقۇم ياخشىلار قاتارىغا كىرگۈزىمىز'),
(3350, 29, 10, 'بەزى كىشىلەر: «اﷲ قا ئىمان ئېيتتۇق» دەيدۇ، اﷲ نىڭ يولىدا بىرەر ئەزىيەت تارتسا، كىشىلەر تەرىپىدىن يەتكەن كۈلپەتنى اﷲ نىڭ ئازابى بىلەن ئوخشاش بىلىدۇ، ئەگەر پەرۋەردىگارىڭدىن مەدەت كەلسە: «بىز ھەقىقەتەن سىلەر بىلەن بىللە ئىدۇق» دەيدۇ. اﷲ جاھان ئەھلىنىڭ دىللىرىدىكىنى ھەممىدىن ئوبدان بىلمەمدۇ؟'),
(3351, 29, 11, 'اﷲ مۆمىنلەرنى چوقۇم بىلىدۇ، مۇناپىقلارنىمۇ چوقۇم بىلىدۇ'),
(3352, 29, 12, 'كاپىرلار مۆمىنلەرگە: «بىزنىڭ يولىمىز (يەنى دىنىمىز) غا ئەگىشىڭلار، سىلەرنىڭ گۇناھىڭلارنى بىز ئۈستىمىزگە ئالىمىز» دېدى. كاپىرلار مۆمىنلەرنىڭ گۇناھلىرىدىن ئازراقمۇ ئۈستىگە ئالالمايدۇ، ئۇلار ھەقىقەتەن يالغانچىلاردۇر'),
(3353, 29, 13, 'ئۇلار ئۆزلىرىنىڭ يۈكلىرىنى ۋە ئۇنىڭغا قوشۇپ باشقا يۈكلەرنى (يەنى ئۆزلىرىنىڭ گۇناھلىرىنى ۋە ئازدۇرغانلىرىنىڭ گۇناھلىرىنى) ئۈستىگە ئالىدۇ، قىيامەت كۈنى ئۇلار ئۆزلىرى ئويدۇرۇپ چىققان يالغان سۆزلىرى ئۈچۈن سوراققا تارتىلىدۇ'),
(3354, 29, 14, 'بىز ھەقىقەتەن نۇھنى قەۋمىگە (پەيغەمبەر قىلىپ) ئەۋەتتۇق، نۇھ (قەۋمىنى تەۋھىدكە دەۋەت قىلىپ) ئۇلارنىڭ ئىچىدە 50 يىل كەم 1000 يىل تۇردى (نۇھ ئەلەيھىسسالامنىڭ قەۋمى بۇتپەرەس بولۇپ، ئۇنىڭ پەيغەمبەرلىكىگە چىنپۈتمىدى). ئۇلار زالىم بولغانلىقلىرى (يەنى كۇفرىدا ۋە گۇمراھلىقتا چىڭ تۇرۇپ ئۆزلىرىگە زۇلۇم قىلغانلىقلىرى) ئۈچۈن، اﷲ ئۇلارنى توپان (بالاسى) بىلەن ھالاك قىلدى'),
(3355, 29, 15, 'بىز نۇھنى ۋە كېمىدىكىلەرنى (غەرق بولۇپ كېتىشتىن) قۇتقۇزدۇق، توپاننى پۈتۈن ئەھلى جاھان ئۈچۈن ئىبرەت قىلدۇق'),
(3356, 29, 16, 'ئىبراھىمنىمۇ (قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۆز ۋاقتىدا ئۇ قەۋمىگە ئېيتتى: «بىر اﷲ قا ئىبادەت قىلىڭلارۋە ئۇنىڭدىن قورقۇڭلار، ئەگەر بىلسەڭلار مۇنداق قىلىش سىلەر ئۈچۈن ياخشىدۇر'),
(3357, 29, 17, 'سىلەر اﷲ نى قويۇپ بۇتلارغىلا ئىبادەت قىلىسىلەر (بۇتلارنى ئۆز قولۇڭلار بىلەن ياسىۋالغانسىلەر، بۇتلارنىڭ قولىدىن پايدا - زىيان كەلمەيدۇ) ۋە يالغاننىلا ئويدۇرىسىلەر، سىلەرنىڭ اﷲ نى قويۇپ چوقۇنۇۋاتقىنىڭلار (يەنى بۇتلار) سىلەرگە رىزىق بېرىشكە قادىر ئەمەس، رىزىقنى اﷲ نىڭ دەرگاھىدىن تەلەپ قىلىڭلار (رىزىق بېرىشكە پەقەت اﷲ نىڭ ئۆزى قادىردۇر)، اﷲ قا ئىبادەت قىلىڭلار ۋە ئۇنىڭغا شۈكۈر قىلىڭلار، اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر'),
(3358, 29, 18, 'سىلەر (مېنى) ئىنكار قىلساڭلار (ئىنكار قىلغانلىقىڭلار بىلەن ماڭا قىلچەزىيان يەتكۈزەلمەيسىلەر، پەقەت ئۆزۈڭلارغا زىيان يەتكۈزىسىلەر)، سىلەردىن ئىلگىرىكى نۇرغۇن ئۈممەتلەرمۇ) پەيغەمبەرلىرىنى (ئىنكار قىلغان) شۇنىڭ بىلەن ئۇلارغا اﷲ ئازابى نازىل بولغان، سىلەرگىمۇ شۇنداق ئازاب نازىل بولىدۇ)، پەيغەمبەرنىڭ مەسئۇلىيىتى پەقەت (اﷲ نىڭ ئەمرلىرىنى) چۈشىنىشلىك قىلىپ يەتكۈزۈشتۇر»'),
(3359, 29, 19, 'ئۇلار (يەنى ئىنكار قىلغۇچىلار) اﷲ نىڭ مەخلۇقاتىنى دەسلەپتە يوقتىن قانداق بارلىققا كەلتۈرگەنلىكىنى، ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلدۈرىدىغانلىقىنى كۆرمىدىمۇ؟ بۇ اﷲ قا ھەقىقەتەن ئاساندۇر (ئۇلار بۇنى قانداقمۇ ئىنكار قىلىدۇ، دەسلەپتە يارىتىشقا قادىر بولغان زات قايتا ئەسلىگە كەلتۈرۈشكە ئەلۋەتتە قادىردۇر)'),
(3360, 29, 20, 'ئېيتقىنكى، «زېمىندا سەير قىلىڭلار، اﷲ نىڭ مەخلۇقاتىنى قانداق قىلىپ (يوقتىن) بار قىلغانلىقىغا قاراڭلار، ئاندىن اﷲ ئۇلارنى (قىيامەتتە) قايتا پەيدا قىلىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر'),
(3361, 29, 21, 'اﷲ خالىغان ئادەمنى جازالايدۇ، خالىغان ئادەمگە رەھمەت قىلىدۇ، (قىيامەتتە) اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر'),
(3362, 29, 22, 'سىلەر مەيلى زېمىندا بولسۇن، مەيلى ئاسماندا بولسۇن، اﷲ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر، سىلەرگە اﷲ تىن بۆلەك دوستمۇ يوق، مەدەتكارمۇ يوق»'),
(3363, 29, 23, 'اﷲ نىڭ ئايەتلىرىنى ۋە ئۇنىڭغا مۇلاقات بولۇشنى (يەنى ئاخىرەتنى) ئىنكار قىلغانلار ئەنە شۇلار مېنىڭ رەھمىتىمدىن نائۈمىد بولدى. ئەنە شۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(3364, 29, 24, 'ئىبراھىمنىڭ قەۋمىنىڭ جاۋابى: «ئىبراھىمنى ئۆلتۈرۈڭلار ياكى كۆيدۈرۈڭلار!» دېيىشتىن ئىبارەت بولدى. اﷲ ئۇنى ئوتتىن قۇتۇلدۇردى (ئۇلار ئىبراھىم ئەلەيھىسسالامنى ئوتقا تاشلىغاندا، اﷲ ئۇنىڭغا ئوتنى سورۇن ۋە ئامان جاي قىلىپ بەردى). ھەقىقەتەن بۇنىڭدا ئىمان ئېيتقان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار'),
(3365, 29, 25, 'ئىبراھىم ئېيتتى: «بۇ دۇنيادا (بۇتلارغا چوقۇنۇش مەقسىتىدە يىغىلىشىڭلارنىڭ) ئاراڭلاردىكى دوستلۇققا سەۋەب بولۇشى ئۈچۈن، اﷲ نى قويۇپ بۇتلارنى مەبۇد قىلىۋالدىڭلار، ئاندىن قىيامەت كۈنى بەزىڭلارنى بەزىڭلار ئىنكار قىلىسىلەر، بەزىڭلارغا بەزىڭلار لەنەت ئوقۇيسىلەر (يەنى قىيامەت كۈنى مەزكۇر دوستلۇق دۈشمەنلىككە ئايلىنىدۇ، ئەگەشتۈرگۈچىلەر ئەگەشكۈچىلەردىن ئادا - جۇدا بولىدۇ، ئەگەشكۈچىلەر ئەگەشتۈرگۈچىلەرگە لەنەت ئوقۇيدۇ، چۈنكى ئۇلارنىڭ دۇنيادىكى دوستلۇقى اﷲ ئۈچۈن بولغان ئەمەس)، سىلەرنىڭ جايىڭلار دوزاخ بولىدۇ، سىلەرنى (دوزاختىن قۇتۇلدۇرىدىغان) ھېچقانداق ياردەمچى بولمايدۇ»'),
(3366, 29, 26, 'لۇت ئىبراھىمغا ئىمان ئېيتتى. ئىبراھىم ئېيتتى: «مەن چوقۇم پەرۋەردىگارىم تەرىپىگە ھىجرەت قىلىمەن (يەنى اﷲ نىڭ رازىلىقىنى ئىزدەش يۈزىسىدىن ۋەتىنىمنى تەرك ئېتىپ، اﷲ ئەمر قىلغان جايغا ھىجرەت قىلىپ بارىمەن). اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر»'),
(3367, 29, 27, 'ئىبراھىمغا ئىسھاقنى، يەئقۇبنى ئاتا قىلدۇق (يەنى ئىبراھىم ئەلەيھىسسالام خۇدالىق ئۈچۈن قەۋمىدىن ئايرىلغاندىن كېيىن، ياخشى پەرزەنت ئىسھاقنى بەردۇق ۋە ئىسھاقنىڭ ئوغلى بولغان نەۋرىسى يەئقۇبنى بەردۇق)، (ئۇنىڭدىن كېيىن) پەيغەمبەرلىكنى، كىتابنى (يەنى ساماۋى كىتابلارنى) ئۇنىڭ ئەۋلادىغا خاس قىلدۇق، دۇنيادىكى مۇكاپاتنى ئۇنىڭغا ئاتا قىلدۇق (يەنى جەمئى دىنلاردا ئۇنى ياخشى نامغا ئىگە قىلدۇق)، ئۇ ھەقىقەتەن ئاخىرەتتە ياخشى بەندىلەردىندۇر'),
(3368, 29, 28, 'لۇتنى (قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئەينى ۋاقىتتا ئۇ قەۋمىگە ئېيتتى: «سىلەر ھەقىقەتەن قەبىھ ئىش قىلىۋاتىسىلەر، سىلەردىن ئىلگىرى جاھان ئەھلىدىن بىرىمۇ مۇنداق قەبىھ ئىشنى قىلغان ئەمەس'),
(3369, 29, 29, 'سىلەر ھەقىقەتەن لىۋاتە قىلامسىلەر؟ يوللارنى توسۇپ بۇلاڭچىلىق قىلامسىلەر؟ سورۇنلىرىڭلاردا ئوپئوچۇق يامان ئىشلارنى قىلىۋېرەمسىلەر؟» ئۇنىڭ قەۋمىنىڭ جاۋابى: «ئەگەر سەن راستچىللاردىن بولساڭ، بىزگە اﷲ نىڭ ئازابىنى كەلتۈرگىن» دېيىشتىنلا ئىبارەت بولدى'),
(3370, 29, 30, 'لۇت: «پەرۋەردىگارىم! بۇزغۇنچى قەۋمگە قارشى ماڭا ياردەم بەرگىن (يەنى ئۇلارنى ھالاك قىلىپ ماڭا ياردەم بەرگىن، ئۇلار بۇزغۇنچى ئەخمەقلەر بولۇپ، ئۇلارنىڭ تۈزىلىشى ئۈمىد قىلىنمايدۇ، ئۇلار گۇمراھلىققا، بۇزۇقچىلىققا چۆمۈپ كەتكەن)» دېدى'),
(3371, 29, 31, 'ئەلچىلىرىمىز (يەنى پەرىشتىلەر) ئىبراھىمغا (پەرزەنت بېرىلىشتىن ئىبارەت) خۇش خەۋەر ئېلىپ كەلگەن چاغدا، ئۇلار: «بىز بۇ شەھەر ئاھالىسىنى چوقۇم ھالاك قىلىمىز، ئۇنىڭ ئاھالىسى ھەقىقەتەن زالىم ئىدى» دېدى'),
(3372, 29, 32, 'ئىبراھىم: «ئۇ شەھەردە لۇت بارغۇ (يەنى لۇتقا ئوخشاش بىر ياخشى پەيغەمبەر تۇرۇۋاتقان شەھەر ئاھالىسىنى قانداقمۇ ھالاك قىىلىسىلەر؟)» دېدى. پەرىشتىلەر ئېيتتى: «بۇ شەھەردە بار كىشىلەرنى بىز (يەنى لۇتنى ۋە ئۇنىڭغا ئەگەشكەن مۆمىنلەرنى) ئوبدان بىلىمىز، ئۇنى ۋە ئۇنىڭ ئايالىدىن باشقا كىشىلىرىنى ئەلۋەتتە قۇتۇلدۇرىمىز، پەقەت ئايالى (كۇفرىدا قەۋمىگە ھەمنەپەس بولغانلىقى ئۈچۈن) قېلىپ ھالاك بولغۇچىلاردىن بولىدۇ»'),
(3373, 29, 33, 'بىزنىڭ ئەلچىلىرىمىز لۇتقا كەلگەن چاغدا، لۇت ئۇلارنى قوغداپ قالالمايدىغانلىقىدىن ئەنسىرەپ قايغۇردى ۋە يۈرىكى سىقىلدى، ئۇلار ئېيتتى: «سەن قورقمىغىن ۋە قايغۇرمىغىن، بىز چوقۇم سېنى ۋە ئايالىڭدىن بۆلەك كىشىلىرىڭنى چوقۇم قۇتۇلدۇرىمىز، پەقەت ئۇ قېلىپ ھالاك بولغۇچىلاردىن بولىدۇ»'),
(3374, 29, 34, 'بۇ شەھەرنىڭ ئاھالىسى اﷲ نىڭ ئىتائىتىدىن چىققانلىقلىرى ئۈچۈن، ئۇلارغا بىز چوقۇم ئاسماندىن ئازاب چۈشۈرىمىز'),
(3375, 29, 35, 'چۈشۈنىدىغان قەۋم ئۈچۈن (ئىبرەت قىلىپ) ئۇنىڭدىن (يەنى شەھەر خارابىسىنىڭ ئىزلىرىدىن) روشەن نىشاننى ھەقىقەتەن قالدۇردۇق'),
(3376, 29, 36, 'مەديەن (ئاھالىسىگە) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ ئەۋەتتۇق)، شۇئەيب: «ئى قەۋمىم! بىر اﷲ قا ئىبادەت قىلىڭلار، ئاخىرەت كۈنىدىن قورقۇڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە - پاسات تېرىماڭلار» دېدى'),
(3377, 29, 37, 'ئۇلار شۇئەيبنى ئىنكار قىلدى، شۇنىڭ بىلەن ئۇلارغا قاتتىق زىلزىلە يۈزلەندى - دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قېلىشتى'),
(3378, 29, 38, 'ئادنى ۋە سەمۇدنى (ھالاك قىلدۇق)، (ئى ئەھلى مەككە!) سىلەرگە ھەقىقەتەن ئۇلارنىڭ (ھىجازدىكى، يەمەندىكى) تۇرالغۇ جايلىرىدىن ئۇلارنىڭ ھالاك بولغانلىقى مەلۇم بولدى، شەيتان ئۇلارغا (كۇقرىدىن ۋە گۇناھلاردىن ئىبارەت) ئەمەللىرىنى چىرايلىق كۆرسەتتى، ئۇلارنى توغرا يولدىن توستى، ئۇلار ئەقىل ئىگىلىرى ئىدى) لېكىن ئۇلار تەكەببۇرلۇق يۈزىسىدىن ئەقلىنى جايىدا ئىشلەتمىدى)'),
(3379, 29, 39, '(دۇنيا - دەپنىسى نۇرغۇن) قارۇننى، پىرئەۋننى ۋە (ئۇنىڭ زۇلۇمدا ياردەمچى بولغان ۋەزىرى) ھاماننى (ھالاك قىلدۇق)، شۈبھىسىزكى، مۇسا ئۇلارغا روشەن مۆجىزىلەرنى ئېلىپ كەلدى، ئۇلار زېمىندا چوڭچىلىق قىلدى (يەنى اﷲ قا ئىبادەت قىلىشتىن، پەيغەمبەرگە ئىتائەت قىلىشتىن بويۇنتاۋلىق قىلدى)، ئۇلار (ئازابىمىزدىن) قېچىپ كېتەلمىدى'),
(3380, 29, 40, '(بۇ گۇناھكارلارنىڭ) ھەربىرىنى گۇناھى تۈپەيلىدىن جازالىدۇق، ئۇلارنىڭ بەزىسىگە تاش ياغدۇردۇق، ئۇلارنىڭ بەزىسىنى قاتتىق تاۋۇش ھالاك قىلدى، ئۇلارنىڭ بەزىسىنى (مال - مۈلكى بىلەن قوشۇپ) يەرگە يۇتقۇزدۇق، ئۇلارنىڭ بەزىسىنى (سۇدا) غەرق قىلدۇق، اﷲ ئۇلارغا زۇلۇم قىلمىدى، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى'),
(3381, 29, 41, 'اﷲ نى قويۇپ بۇتلارنى مەبۇد قىلىۋالغانلارنىڭ مىسالى ئۆي ياسىغان ئۆمۈچۈكنىڭ مىسالىغا ئوخشايدۇ (يەنى ئۆمۈچۈكنىڭ ئۆيى ئۇنى ئىسسىق - سوغۇقتىن قوغدىيالمىغاندەك، ئۇلارنىڭ چوقۇنۇۋاتقان بۇتلىرىمۇ ئۇلارغا ھېچقانداق پايدا - زىيان يەتكۈزەلمەيدۇ)، ئۆيلەرنىڭ ئەڭ ئاجىزى ئۆمۈچۈكنىڭ ئۆيىدۇر. ئەگەر ئۇلار (بۇ ھەقىقەتنى) بىلسە ئىدى (بۇتلارغا چوقۇنمايتتى)'),
(3382, 29, 42, 'اﷲ ئۇلار اﷲ نى تاشلاپ ئىبادەت قىلىۋاتقان نەرسىنى ئوبدان بىلىدۇ، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(3383, 29, 43, 'بىز ئىنسانلارغا (قۇرئاندا ئۇلارنىڭ زېھنىغا يېقىنلاشتۇرۇش ئۈچۈن) بايان قىلغان بۇ تەمسىللەرنى پەقەت ئالىملارلا چۈشىنەلەيدۇ'),
(3384, 29, 44, 'اﷲ ئاسمانلارنى ۋە زېمىننى ھەق ياراتتى، ھەقىقەتەن بۇنىڭدا مۆمىنلەر ئۈچۈن (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) دەلىل بار'),
(3385, 29, 45, '(ئى مۇھەممەد!) ساڭا ۋەھىي قىلىنغان كىتابنى (يەنى قۇرئاننى) تىلاۋەت قىلغىن، نامازنى (تەئدىل ئەركان بىلەن) ئوقۇغىن، ناماز ھەقىقەتەن قەبىھ ئىشلاردىن ۋە گۇناھلاردىن توسىدۇ، اﷲ نى ياد ئېتىش ھەممىدىن (يەنى ئۇنىڭدىن باشقا ھەممە ئىبادەتتىن) ئۇلۇغدۇر، اﷲ قىلىۋاتقان (ھەممە) ئىشىڭلارنى بىلىپ تۇرىدۇ'),
(3386, 29, 46, 'ئەھلى كىتاب بىلەن پەقەت ئەڭ چىرايلىق رەۋىشتە مۇنازىرىلىشىڭلار، ئۇلارنىڭ ئىچىدىكى زۇلۇم قىلغۇچىلار (يەنى سىلەرگە قارشى ئۇرۇش قىلغۇچىلار ۋە فىدىيە بېرىشتىن باش تارتقۇچىلار) بۇنىڭدىن مۇستەسنا، ئېيتىڭلاركى، «بىزگە نازىل قىلىنغان كىتابقا ۋە سىلەرگە نازىل قىلىنغان كىتابقا ئىمان ئېيتتۇق، سىلەرنىڭ ئىلاھىڭلار ۋە بىزنىڭ ئىلاھىمىز بىردۇر، بىز اﷲ قا بويسۇنغۇچىلارمىز»'),
(3387, 29, 47, '(ئى مۇھەممەد!) شۇنىڭدەك (يەنى سەندىن ئىلگىرىكىلەرگە كىتاب نازىل قىلغاندەك) ساڭىمۇ كىتاب (يەنى قۇرئان) نازىل قىلدۇق، بىز كىتاب بەرگەنلەر (يەنى يەھۇدىيلار ناسارالار) دىن قۇرئانغا ئىشىنىدىغانلار بار، بۇلاردىن (يەنى مەككە ئاھالىسىدىن) مۇ قۇرئانغا ئىشىنىدىغانلار بار، بىزنىڭ ئايەتلىرىمىزنى پەقەت كاپىرلارلا ئىنكار قىلىدۇ'),
(3388, 29, 48, 'ئىلگىرى سەن كىتاب ئوقۇشنى بىلمەيتتىڭ، خەت يېزىشنىمۇ بىلمەيتتىڭ، (مۇبادا سەن كىتاب ئوقۇغان، خەت يازىدىغان بولساڭ) ئۇ چاغدا ھەققە قارشى تۇرغۇچىلار چوقۇم گۇمانلىناتتى'),
(3389, 29, 49, 'ئۇنداق ئەمەس (يەنى ئىش زالىملار گۇمان قىلغاندەك ئەمەس)، قۇرئان ئىلىم بېرىلگەنلەرنىڭ كۆڭۈللىرىدە ساقلانغان روشەن ئايەتلەردۇر، بىزنىڭ ئايەتلىرىمىزنى پەقەت زالىملارلا ئىنكار قىلىدۇ'),
(3390, 29, 50, 'ئۇلار: «نېمىشقا ئۇنىڭغا پەرۋەردىگارىدىن مۆجىزىلەر نازىل قىلىنمايدۇ» دېدى. ئېيتقىنكى، «مۆجىزىلەر ھەقىقەتەن اﷲ نىڭ دەرگاھىدىدۇر (مېنىڭ قولۇمدا ئەمەستۇر)، مەن پەقەت بىر ئاشكارا ئاگاھلاندۇرغۇچىمەن»'),
(3391, 29, 51, 'بىزنىڭ ساڭا ئۇلارغا تىلاۋەت قىلىنىپ تۇرىدىغان كىتابنى نازىل قىلغانلىقىمىز ئۇلارغا (مۆجىزە بولۇشقا) كۇپايە قىلمىدىمۇ؟ بۇ كىتابتا ئىمان ئېيتقان قەۋم ئۈچۈن شەك - شۈبھىسىز رەھمەت ۋە ۋەز - نەسىھەت بار'),
(3392, 29, 52, 'ئېيتقىنكى، «مەن بىلەن سىلەرنىڭ ئاراڭلاردا (مېنىڭ راست پەيغەمبەر ئىكەنلىكىمگە) گۇۋاھ بولۇشقا اﷲ كۇپايىدۇر، اﷲ ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى بىلىپ تۇرىدۇ، بۇتقا ئىشەنگەن، اﷲ نى ئىنكار قىلغان ئادەملەر زىيان تارتقۇچىلاردۇر»'),
(3393, 29, 53, 'ئۇلار سەندىن ئازابنىڭ چاپسان كېلىشىنى تەلەپ قىلىدۇ، اﷲ (ئۇلارنىڭ ھالاك بولۇشىنىڭ) مۇئەييەن ۋاقتىنى بەلگىلىمىگەن بولسا ئىدى، ئۇلارغا ئازاب ئەلۋەتتە (ئۇلار تەلەپ قىلغان ۋاقىتتىلا) كېلەتتى، ئۇلارغا ئازاب چوقۇم ئۇلار خەۋەرسىز ھالەتتە تۇرغاندا ئۇشتۇمتۇت كېلىدۇ'),
(3394, 29, 54, 'ئۇلار سەندىن ئازابنىڭ تېز كېلىشىنى تەلەپ قىلىدۇ، شەك - شۈبھىسىزكى، جەھەننەم (قىيامەت كۈنى) كاپىرلارنى قورشاپ تۇرغۇچىدۇر'),
(3395, 29, 55, 'ئۇ كۈندە ئازاب ئۇلارنى ئۈستىلىرىدىن، ئاياغلىرىنىڭ ئاستىدىن ئورىۋالىدۇ. اﷲ ئۇلارغا: «(دۇنيادا قىلغان ئەمەللىرىڭلارنىڭ) جازاسىنى تېتىڭلار» دەيدۇ'),
(3396, 29, 56, 'ئى مۆمىن بەندىلىرىم! مېنىڭ زېمىنىم ھەقىقەتەن كەڭدۇر، ماڭىلا ئىبادەت قىلىڭلار'),
(3397, 29, 57, 'ھەربىر جاندار ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر، ئاندىن بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىسىلەر'),
(3398, 29, 58, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەرنى جەننەتنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان ئالىي جايلىرىغا ئورۇنلاشتۇرىمىز، ئۇ يەرلەردە ئۇلار مەڭگۈ قالىدۇ، ياخشى ئەمەللەرنى قىلغۇچىلارغا بېرىلگەن مۇكاپات نېمىدېگەن ياخشى!'),
(3399, 29, 59, 'ئۇلار (ھىجرەت قىلىشنىڭ جاپا - مۇشەققەتلىرىگە، اﷲ نىڭ يولىدا يەتكەن ئەزىيەتلەرگە) سەۋر قىلىدۇ، (ھەممە ئىشلىرىدا) پەرۋەردىگارىغا يۆلىنىدۇ'),
(3400, 29, 60, 'نۇرغۇن ھايۋانلار ئۆز رىزقىنى ئۈستىگە ئالالمايدۇ (يەنى ئۆز رىزقىنى تېپىپ يېيىشتىن ئاجىزدۇر)، ئۇلارنى ۋە سىلەرنى اﷲ رىزىقلاندۇرىدۇ، اﷲ (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر'),
(3401, 29, 61, 'ئەگەر سەن ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم ياراتتى، كۈننى ۋە ئاينى كىم (بەندىلەرنىڭ مەنپەئىتى ئۈچۈن) بويسۇندۇردى؟» دەپ سورىساڭ، ئۇلار چوقۇم: «اﷲ» دەپ جاۋاب بېرىدۇ، ئۇلار قانداقمۇ (اﷲ نىڭ ئىبادىتىدىن) باش تارتىدۇ!'),
(3402, 29, 62, 'اﷲ بەندىلىرىدىن قايسى ئادەمنىڭ رىزقىنى كەڭ قىلماقچى بولىدىكەن، ئۇنى كەڭ قىلىدۇ، (قايسى بەندىسىنىڭ رىزقىنى تار قىلماقچى بولىدىكەن) ئۇنى تار قىلىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىنى بىلىپ تۇرغۇچىدۇر'),
(3403, 29, 63, 'ئەگەر سەن ئۇلاردىن: «بۇلۇتتىن يامغۇر سۈيىنى چۈشۈرۈپ ئۇنىڭ بىلەن ئۆلگەن زېمىننى تېرىلدۈرگەن كىم؟» دەپ سورىساڭ، ئۇلار چوقۇم «اﷲ» دەپ جاۋاپ بېرىدۇ. ئېيتقىنكى، «(سىلەرگە قارشى پاكىتلار بار بولغانلىقتىن) جىمى ھەمدۇسانا اﷲ قا خاستۇر»، بەلكى ئۇلارنىڭ تولىسى (بۇ سۆزلىرىدىكى زىددىيەتنى) چۈشەنمەيدۇ'),
(3404, 29, 64, 'دۇنيا تىرىكچىلىكى پەقەت ئويۇن - كۈلكە ۋە غەپلەتتىن ئىبارەتتۇر، شۈبھىسىزكى، ئاخىرەت يۇرتى ھەقىيقى ھايات (يۇرتىدۇر)، ئەگەر ئۇلار بىلسە ئىدى (بۇ دۇنيانى ئۇ دۇنيادىن ئارتۇق كۆرمەيتتى)'),
(3405, 29, 65, 'ئۇلار كېمىگە چىقىپ (غەرق بولۇشتىن قورققان) چاغلىرىدا، اﷲ قا كامالى ئىخلاس بىلەن ئىلتىجا قىلىدۇ، اﷲ ئۇلارنى ئامان - ئېسەن قۇرۇقلۇققا چىقارغان چاغدا (بالا - قازادىن قۇتۇلدۇرغان اﷲ نى ئۇنتۇپ)، ناگاھان (اﷲ قا) شېرىك كەلتۈرىدۇ'),
(3406, 29, 66, 'ئۇلار بەرگەن نېمىتىمىز (يەنى دېڭىزدىن قۇتۇلدۇرغانلىقىمىزغا ناشۈكۈرلۈك قىلىپ باقسۇن، قالغان ئۆمۈرلىرىدىن) بەھرىمەن بولسۇن، بۇلار (ئىشنىڭ ئاقىۋىتىنى) كەلگۈسىدە بىلىدۇ'),
(3407, 29, 67, 'ئۇلار (يەنى مۇشرىكلار) كۆرمەمدۇكى، بىز ھەرەمنى ئامان (جاي) قىلدۇق، ھالبۇكى، ئۇلارنىڭ ئەتراپىدىكى كىشىلەر ئەسىر ئېلىنىۋاتىدۇ ۋە ئۆلتۈرۈلۈۋاتىدۇ، ئۇلار باتىلغا ئىشىنىپ، اﷲ نىڭ نېمىتىنى ئىنكار قىلامدۇ؟'),
(3408, 29, 68, 'اﷲ قا يالغاننى چاپلىغان ياكى ھەق كەلگەندە ئۇنى ئىنكار قىلغان ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ جەھەننەمدە كاپىرلارغا جاي يوقمىدۇ؟ (ئەلۋەتتە بار)'),
(3409, 29, 69, 'بىز ئۈچۈن كۈرەش قىلغانلارنى ئەلۋەتتە يولىمىزغا يېتەكلەيمىز، اﷲ ھەقىقەتەن ياخشى ئىش قىلغۇچىلار بىلەن بىللىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'ئەلىف، لام، مىم'),
(3411, 30, 2, 'رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش اﷲ نىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر اﷲ نىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، اﷲ خالىغان كىشىگە ياردەم بېرىدۇ، اﷲ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر'),
(3412, 30, 3, 'رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش اﷲ نىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر اﷲ نىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، اﷲ خالىغان كىشىگە ياردەم بېرىدۇ، اﷲ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر'),
(3413, 30, 4, 'رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش اﷲ نىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر اﷲ نىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، اﷲ خالىغان كىشىگە ياردەم بېرىدۇ، اﷲ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر'),
(3414, 30, 5, 'رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش اﷲ نىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر اﷲ نىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، اﷲ خالىغان كىشىگە ياردەم بېرىدۇ، اﷲ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر'),
(3415, 30, 6, 'اﷲ (ئۇلارنى غالىب قىلىدىغانلىقىنى) ۋەدە قىلدى. اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ) چۈنكى اﷲ نىڭ ۋەدىسى ھەقتۇر، سۆزى راستتۇر)، لېكىن كىشىلەرنىڭ تولىسى (بۇنى) بىلمەيدۇ'),
(3416, 30, 7, 'ئۇلار ھاياتىي دۇنيانىڭ تاشقى كۆرۈنۈشىنىلا بىلىدۇ، ئۇلار ئاخىرەتتىن (يەنى ئاخىرەت ئىشىدا ئويلىنىشتىن ۋە ئاخىرەت ئۈچۈن ئىشلەشتىن) غەپلەتتىدۇر'),
(3417, 30, 8, 'ئۇلار (ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىنى بىلىشلىرى ئۈچۈن) ئۆزلىرى ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ اﷲ ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى (يۇلتۇز قاتارلىق) نەرسىلەرنى پەقەت ھەق ۋە مۇئەييەن ۋاقىتلىق قىلىپ ياراتتى، شەك – شۈبھىسىزكى، نۇرغۇن كىشىلەر پەرۋەردىگارىغا مۇلاقات بولۇشنى (قايتا تىرىلىدىغانلىقىنى، قىلغان ئەمەلىگە يارىشا مۇكاپاتلىنىدىغانلىقىنى ياكى جازالىنىدىغانلىقىنى) ئىنكار قىلغۇچىدۇر'),
(3418, 30, 9, 'ئۇلار يەر يۈزىدە كېزىپ ئۆزلىرىدىن ئىلگىرى ئۆتكەنلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزەتمىدىمۇ؟ ئۇلار (يەنى ئۆتكەنلەر) قۇۋۋەتتە بۇلاردىن كۈچلۈك ئىدى. ئۇلار زىرائەت تېرىشتا، ئىمارەت سېلىشتا بۇلار (يەنى قۇرەيشلەر) دىن ئۈستۈن ئىدى. ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەرنى ئېلىپ كەلدى (ئۇلار مۆجىزىلەرنى ئىنكار قىلدى). اﷲ ئۇلارغا زۇلۇم قىلمىدى (يەنى اﷲ ئۇلارنى گۇناھسىز ھالاك قىلغىنى يوق)، لېكىن ئۇلار ئۆزلىرى (كۇفرى ئۈچۈن ۋە پەيغەمبەرلەرنىڭ مۆجىزىلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئۆزلىرىگە زۇلۇم قىلدى (شۇنىڭ بىلەن ئۇلار ھالاك بولۇشقا تېگىشلىك بولدى)'),
(3419, 30, 10, 'ئاندىن يامان ئىش قىلغانلارنىڭ ئاقىۋىتى ئەڭ يامان بولىدۇ، چۈنكى ئۇلار اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغان ۋە مەسخىرە قىلغان كىشىلەردۇر'),
(3420, 30, 11, 'اﷲ مەخلۇقاتنى دەسلەپتە (يوقتىن) بار قىلىدۇ. ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلدۈرىدۇ، ئاندىن اﷲ نىڭ دەرگاھىغا (ھېساب ئۈچۈن) قايتۇرۇلىسىلەر'),
(3421, 30, 12, 'قىيامەت قايىم بولغان كۈندە گۇناھكارلار ئۈمىدسىزلىنىپ كېتىدۇ'),
(3422, 30, 13, 'ئۇلارغا شېرىك كەلتۈرگەن بۇتلىرىدىن شاپائەت قىلغۇچىلار بولمايدۇ، ئۇلار شېرىك كەلتۈرگەن بۇتلىرىدىن تانىدۇ'),
(3423, 30, 14, 'قىيامەت قايىم بولغان كۈندە (مۆمىنلەر بىلەن كاپىرلار) بىر - بىرىدىن ئايرىلىدۇ'),
(3424, 30, 15, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار باغچىدا (يەنى جەننەتنىڭ باغچىلىرىدا) يايرىتىلىدۇ'),
(3425, 30, 16, 'بىزنىڭ ئايەتلىرىمىزنى ۋە ئاخىرەتتىكى مۇلاقاتنى ئىنكار قىلغان ۋە يالغانغا چىقارغانلارغا كەلسەك، ئۇلار داۋاملىق ئازابتا قالىدۇ'),
(3426, 30, 17, 'سىلەر ئاخشامدا ۋە ئەتىگەندە اﷲ قا تەسبىھ ئېيتىڭلار'),
(3427, 30, 18, 'ئاسمانلاردىكى، زېمىندىكى ھەمدۇسانا، كەچ ۋە پېشىن ۋاقىتلىرىدىكى ھەمدۇسانانىڭ ھەممىسى اﷲ قا خاستۇر'),
(3428, 30, 19, 'اﷲ جانسىز نەرسىلەردىن جانلىق نەرسىلەرنى چىقىرىدۇ، جانلىق نەرسىلەردىن جانسىز نەرسىلەرنى چىقىرىدۇ، ئۆلگەن زېمىننى تىرىلدۈرىدۇ (يەنى قۇرغاق زېمىننى ئۆسۈملۈكلەر بىلەن كۆكەرتىدۇ)، سىلەرمۇ مۇشۇنداق تىرىلدۈرۈلىسىلەر'),
(3429, 30, 20, 'اﷲ نىڭ سىلەرنى تۇپراقتىن ياراتقانلىقى، ئاندىن سىلەرنىڭ ئىنسان بولۇپ زېمىندا تارىلىپ يۈرۈشۈڭلار اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر'),
(3430, 30, 21, 'ئاياللار بىلەن ئۇنسى – ئۈلپەت ئېلىشىڭلار ئۈچۈن (اﷲ نىڭ) ئۇلارنى سىلەرنىڭ ئۆز تىپىڭلاردىن ياراتقانلىقى، ئاراڭلاردا (يەنى ئەر – خوتۇن ئارىسىدا) مېھر - مۇھەببەت ئورناتقانلىقى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن، شەك - شۈبھىسىزكى، بۇنىڭدا نۇرغۇن ئىبرەتلەر بار'),
(3431, 30, 22, 'اﷲ نىڭ ئاسمانلارنى، زېمىننى ياراتقانلىقى، تىللىرىڭلارنىڭ، رەڭگىلىرىڭلارنىڭ خىلمۇخىل بولۇشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، بۇنىڭدا بىلىملىك كىشىلەر ئۈچۈن ھەقىقەتەن نۇرغۇن ئالامەتلەر بار'),
(3432, 30, 23, 'سىلەرنىڭ كېچىسى ۋە كۈندۈزى ئۇخلىشىڭلار، (كۈندۈزى) اﷲ نىڭ مەرھەمىتىدىن (رىزىق) تەلەپ قىلىپ (ھەرىكەتلىنىشىڭلار) اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، شەك - شۈبھىسىزكى، بۇنىڭدا ئاڭلايدىغان قەۋم ئۈچۈن نۇرغۇن ئىبرەتلەر بار'),
(3433, 30, 24, 'اﷲ نىڭ سىلەرگە چاقماقنى قورقۇنچ ۋە ئۈمىد قىلىپ كۆرسىتىشى، بۇلۇتتىن يامغۇر ياغدۇرۇپ ئۇنىڭ بىلەن ئۆلگەن زېمىننى تىرىلدۈرۈشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، چۈشىنەلەيدىغان قەۋم ئۈچۈن بۇنىڭدا، شەك - شۈبھىسىزكى، نۇرغۇن ئىبرەتلەر بار'),
(3434, 30, 25, 'اﷲ نىڭ ئاسمان زېمىننى ئۆز ئەمرى بىلەن (مۇئەللەق) تۇرغۇزۇشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، ئاندىن اﷲ (قەبرىلەردە كۆمۈلۈپ ياتقان) سىلەرنى يەر ئاستىدىن چاقىرسا، دەرھال چىقىسىلەر'),
(3435, 30, 26, 'ئاسمانلاردىكى ۋە زېمىندىكى ھەممە اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ مەخلۇقاتىدۇر ۋە اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، ھەممە اﷲ قا بويسۇنغۇچىدۇر'),
(3436, 30, 27, 'مەخلۇقاتنى دەسلەپتە (يوقتىن) بار قىلىدىغان، ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلىدۈرىدىغان اﷲ ئەنە شۇدۇر، ئۇ (يەنى مەخلۇقاتنى تىرىلدۈرۈش) اﷲ قا (ئۇنى دەسلەپتە يوقتىن بار قىلغانغا قارىغاندا) ئوڭايدۇر، ئاسمانلاردا ۋە زېمىندا ئەڭ ئالىي سۈپەت اﷲ قا خاستۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(3437, 30, 28, 'اﷲ سىلەرگە ئۆزۈڭلاردىن بىر مىسال كەلتۈرىدۇ، بىز سىلەرگە رىزىق قىلىپ بەرگەن مال – مۈلۈككە قۇللىرىڭلارنىڭ شېرىك بولۇشىغا، ئۇ بارىدا ئۇلارنىڭ سىلەر بىلەن باراۋەر بولۇشىغا، ئۇلاردىن ئۆزلىرىڭلار (يەنى ئازاد كىشىلەر) دىن قورققاندەك قورقۇشقا رازىمۇسىلەر؟ (بۇنىڭغا رازى بولمىغان ئىكەنسىلەر، مەخلۇقاتنىڭ اﷲ قا شېرىك بولۇشىغا قانداق رازى بولدۇڭلار؟) چۈشىنىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلىمىز'),
(3438, 30, 29, 'ئۇنداق ئەمەس (يەنى اﷲ قا شېرىك كەلتۈرۈشتە ئۇلارنىڭ ھېچقانداق ئۆزرىسى يوق)، (ئۆزلىرىگە) زۇلۇم قىلغانلار بىلىمسىزلىكتىن نەپسى خاھىشىغا ئەگەشتى. اﷲ گۇمراھ قىلغان ئادەمنى كىم ھىدايەت قىلالايدۇ، ئۇلارغا ھەرگىز ياردەم قىلغۇچى بولمايدۇ'),
(3439, 30, 30, 'باتىل دىنلاردىن بۇرۇلۇپ ئىسلام دىنىغا يۈزلەنگىن، اﷲ نىڭ دىنىغا (ئەگەشكىنكى) اﷲ ئىنسانلارنى شۇ دىن بىلەن ياراتقان، اﷲ نىڭ ياراتقىنىدا ئۆزگىرىش بولمايدۇ، بۇ توغرا دىندۇر، لېكىن ئىنسانلارنىڭ تولىسى بىلمەيدۇ'),
(3440, 30, 31, 'اﷲ قا تەۋبە بىلەن قايتىڭلار، اﷲ تىن قورقۇڭلار، نامازنى ئادا قىلىڭلار، مۇشرىكلاردىن بولماڭلار'),
(3441, 30, 32, 'سىلەر دىندا ئىختىلاپ قىلىشىپ، پىرقە - پىرقە بولۇپ، ھەرپىرقە ئۆز دىنى بىلەن خوشاللىنىدىغانلاردىن (بولماڭلار)'),
(3442, 30, 33, 'ئىنسانلارغا بىرەر زىيان - زەخمەت يەتكەن چاغدا، ئۇلار پەرۋەردىگارىغا ئىخلاس بىلەن ئىلتىجا قىلىدۇ، ئاندىن اﷲ ئۇلارغا رەھمىتىنى تېتىتقان چاغدا، ئۇلاردىن بىر پىرقە دەرھال پەرۋەردىگارىغا شېرىك كەلتۈرىدۇ'),
(3443, 30, 34, 'ئۇلار بىزنىڭ بەرگەن نېمەتلىرىمىزگە نا شۈكۈرلۈك قىلىپ باقسۇن، (ئى مۇشرىكلار!) (دۇنيا تىرىكچىلىكىدە سىلەرگە بەرگەن نېمەتلىرىمىزدىن) بەھرىمەن بولۇڭلار، سىلەر (پانىي دۇنيانىڭ نېمىتىدىن بەھرىمەن بولغانلىقىڭلارنىڭ ئاقىۋىتىنى) كەلگۈسىدە بىلىسىلەر'),
(3444, 30, 35, 'ياكى ئۇلارغا ئۇلارنىڭ بۇتلارغا چوقۇنىشىنىڭ توغرىلىقىنى سۆزلەيدىغان بىرەر كىتاب نازىل قىلدۇقمۇ؟ (مۇنداق بولغىنى يوق)'),
(3445, 30, 36, 'ئىنسانلارغا رەھمەتنى تېتىتساق، ئۇلار بۇنىڭدىن خۇشاللىنىپ كېتىدۇ، ئەگەر ئۇلارغا قىلمىشلىرى تۈپەيلىدىن بىرەر كۆڭۈلسىزلىك يەتسە، دەرھال ئۈمىدسىزلىنىپ كېتىدۇ'),
(3446, 30, 37, 'ئۇلار بىلمەمدۇكى، اﷲ خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ، ھەقىقەتەن بۇنىڭدا (رىزىق بەرگەن اﷲ نىڭ ھېكمىتىگە) ئىشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار'),
(3447, 30, 38, 'خىش - ئەقرىبالارغا، مىسكىنلەرگە، ئىبن سەبىللەرگە (سىلە - رەھىم ۋە ياخشىلىقتىن تېگىشلىك) ھەققىنى بەرگىن، اﷲ نىڭ رازىلىقىنى كۆزلەيدىغانلار ئۈچۈن مۇنداق قىلىش ياخشىدۇر، ئەنە شۇلار مەقسەتكە ئېرىشكۈچىلەردۇر'),
(3448, 30, 39, 'سىلەر كىشىلەرنىڭ پۇل - مېلى ئىچىدە ئۆستۈرۈش ئۈچۈن بىرەر پۇل - مال بەرسەڭلار، اﷲ نىڭ دەرگاھىدا ئۇ ئۆسمەيدۇ (يەنى كىشىلەر بەرگىنىمدىن جىقراق قايتۇرسۇن دېگەن نىيەت بىلەن بېرىلگەن سوغىنىڭ اﷲ نىڭ دەرگاھىدا ساۋابى بولمايدۇ)، اﷲ نىڭ رازىلىقىنى كۆزلەپ بەرگەن سەدىقەڭلار (ياكى ئېھسانىڭلار) غا ھەسسىلەپ ساۋاب بېرىلىدۇ'),
(3449, 30, 40, 'اﷲ سىلەرنى خەلق ئەتتى، ئاندىن سىلەرگە رىزىق بەردى. ئاندىن سىلەرنى قەبزى روھ قىلىدۇ، ئاندىن سىلەرنى تىرىلدۈرىدۇ، شېرىك كەلتۈرگەنلىرىڭلاردىن قايسىسى بۇ ئىشلارنىڭ قايسىبىرىنى قىلالايدۇ، اﷲ پاكتۇر، ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن ئالىيدۇر'),
(3450, 30, 41, 'ئىنسانلارنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن، قۇرۇقلۇقتا ۋە دېڭىزدا ئاپەت يۈزبەردى، اﷲ ئۇلارنى تەۋبە قىلسۇن دەپ قىلمىشلىرىنىڭ بىر قىسمىنىڭ (جازاسىنى) ئۇلارغا تېتىتتى'),
(3451, 30, 42, '(ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، يەر يۈزىدە سەير قىلىڭلار، ئۆتكەنكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار (ئۆزلىرىگە زۇلۇم قىلغانلارنىڭ جايلىرىغا قاراڭلاركى، ئۇلارنىڭ پەيغەمبەرلەرنى ئىنكار قىلغانلىقلىرىنىڭ ئاقىۋىتى قانداق بولغان، اﷲ تائالا ئۇلارنىڭ دىيارلىرىنى خاراپ قىلىپ، ئۇلارنى ئىبرەت ئالغۇچىلار ئۈچۈن ئىبرەت قىلدى)، ئۇلارنىڭ تولىسى مۇشرىك ئىدى'),
(3452, 30, 43, 'اﷲ تەرىپىدىن كېلىدىغان، قارشى تۇرغىلى بولمايدىغان كۈن (يەنى قىيامەت) كېلىشتىن ئىلگىرى توغرا دىنغا يۈزلەنگىن، بۇ كۈندە ئۇلار (نىڭ بىر پىرقىسى جەننەتكە، بىر پىرقىسى جەھەننەمگە) ئايرىلىدۇ'),
(3453, 30, 44, 'كىمكى كاپىر بولسا (دوزاختا ئەبەدىي قېلىپ) كۇفرىنىڭ جازاسىنى ئۆزى تارتىدۇ، كىملەركى ياخشى ئەمەل قىلسا، ئۆزلىرى ئۈچۈن (ئاخىرەتتە) جاي راستلايدۇ'),
(3454, 30, 45, 'اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى مەرھەمىتىدىن مۇكاپاتلايدۇ، اﷲ ھەقىقەتەن كاپىرلارنى دوست تۇتمايدۇ'),
(3455, 30, 46, 'اﷲ نىڭ سىلەرگە ئۆزىنىڭ رەھمىتىنى تېتىتىش ئۈچۈن، ئەمرى بويىچە كېمىلەرنى دېڭىزلاردا ماڭدۇرۇش ئۈچۈن، ئۆزىنىڭ مەرھەمىتىدىن تىلىشىڭلار ئۈچۈن، شۈكۈر قىلىشىڭلار ئۈچۈن، شاماللارنى يامغۇر بىلەن خۇش خەۋەر بەرگۈچى قىلىپ ئەۋەتىشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر'),
(3456, 30, 47, '(ئى مۇھەممەد!) شۈبھىسىزكى، سەندىن ئىلگىرى نۇرغۇن پەيغەمبەرلەرنى قەۋمىگە (پەيغەمبەر قىلپ) ئەۋەتتۇق، ئۇ پەيغەمبەرلەر ئۇلارغا (ئۆزلىرىنىڭ راست پەيغەمبەرلىكلىرىنى ئىسپاتلايدىغان) نۇرغۇن روشەن مۆجىزىلەرنى ئېلىپ كەلدى (ئۇلار مۆجىزىلەرنى ئىنكار قىلىپ ئاسىيلىق قىلدى)، ئاسىيلىق قىلغانلارنى جازالىدۇق، مۆمىنلەرگە ياردەم قىلىش بىزگە تېگىشلىك بولدى'),
(3457, 30, 48, 'اﷲ شاماللارنى ئەۋەتىپ بۇلۇتنى قوزغايدۇ، ئاندىن ئۇنى ئاسماندا خالىغىنى بويىچە تارقىتىدۇ، (گاھى) ئۇنى تارقاق پارچىلارغا بۆلىدۇ، شۇنىڭ بىلەن يامغۇرنىڭ بۇلۇتنىڭ ئارىسىدىن چۈشۈۋاتقانلىقىنى كۆرىسەن، اﷲ يامغۇرنى خالىغان بەندىلىرىنىڭ ئۈستىگە ياغدۇرغان چاغدا، ئۇلار دەرھال خۇشال بولۇپ كېتىدۇ'),
(3458, 30, 49, 'ھالبۇكى، ئۇلار يامغۇر يېغىشتىن بۇرۇن ھەقىقەتەن ئۈمىدسىزلىنىپ كەتكەن ئىدى'),
(3459, 30, 50, 'اﷲ نىڭ رەھمىتىنىڭ نەتىجىلىرىگە قارىغىنكى، ئۇ زېمىننى ئۆلگەندىن كېيىن قانداق تىرىلدۈرىدۇ، شۈبھىسىزكى، اﷲ ھەقىقەتەن ئۆلۈكلەرنى تىرىلدۈرگۈچىدۇر، اﷲ ھەممە نەرسىگە قادىردۇر'),
(3460, 30, 51, 'ئەگەر بىز (زىرائەتكە زىيانلىق) شامالنى ئەۋەتسەك، ئۇلار (شامالنىڭ تەسىرىدىن) زىرائەتنىڭ سارغىيىپ كەتكەنلىكىنى كۆرسە، شۇنىڭدىن كېيىن ئۇلار (اﷲ نىڭ ئىلگىرىكى نېمەتلىرىگە) كۇفرىلىق قىلىدۇ'),
(3461, 30, 52, 'شۈبھىسىزكى، سەن ئۆلۈكلەرگە (سۆزۈڭنى) ئاڭلىتالمايسەن، ئارقىسىغا قاراپ يۈز ئۆرۈگەن گاسلارغىمۇ چاقىرىقىڭنى ئاڭلىتالمايسەن'),
(3462, 30, 53, 'كورلارنىمۇ گۇمراھلىقتىن قۇتقۇزۇپ توغرا يولغا سالالمايسەن، سەن پەقەت بىزنىڭ ئايەتلىرىمىزگە ئىشىنىدىغانلارغىلا (سۆزۈڭنى) ئاڭلىتالايسەن، ئۇلار مۇسۇلمانلاردۇر'),
(3463, 30, 54, 'اﷲ سىلەرنى ئاجىز ياراتتى، ئاندىن سىلەرنى ئاجىزلىقتىن كۈچلۈك قىلدى، ئاندىن (سىلەرنى) كۈچلۈكلۈكتىن ئاجىز قىلدى ۋە (سىلەرگە) قېرىلىقنى يەتكۈزدى، اﷲ نېمىنى خالىسا شۇنى يارىتىدۇ، ئۇ ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر'),
(3464, 30, 55, 'قىيامەت قايىم بولغان كۈندە مۇشرىكلار (دۇنيادا) پەقەت ئازغىنا ۋاقىت تۇرغانلىقىغا قەسەم قىلىدۇ، ئۇلار دۇنيادىكى چاغلىرىدىمۇ مۇشۇنداق يالغانچىلىق قىلاتتى'),
(3465, 30, 56, 'ئىلمى بار، ئىمانى بار ئادەملەر ئېيتتى: «سىلەر اﷲ نىڭ تەقدىرى بويىچە (دۇنيادا ۋە قەبرىلەردە) قىيامەت كۈنىگىچە قالدىڭلار، مانا بۇ قىيامەت كۈنىدۇر، لېكىن سىلەر بىلمەيتتىڭلار (يەنى قىيامەتنىڭ ھەقلىقىغا ئىشەنمەيتتىڭلار)»'),
(3466, 30, 57, 'بۇ كۈندە زالىملارنىڭ (ئېيتقان) ئۆزرىسىنىڭ پايدىسى بولمايدۇ، ئۇلاردىن ئۇلارنىڭ (تەۋبە قىلىشى يا ئىتائەت قىلىشى بىلەن) اﷲ نى رازى قىلىشمۇ تەلەپ قىلىنمايدۇ'),
(3467, 30, 58, 'شەك - شۈبھىسىزكى، بۇ قۇرئاندا كىشىلەرگە تۈرلۈك مىساللارنى بايان قىلدۇق، ئەگەر سەن ئۇلارغا (قۇرئان ئايەتلىرىدىن) بىرەر ئايەتنى كەلتۈرسەڭ، كاپىرلار (دىللىرى قەساۋەتلىشىپ كەتكەنلىكتىن) چوقۇم ئېيتىدۇكى، «سىلەر پەقەت يالغانچىلار سىلەر»'),
(3468, 30, 59, '(اﷲ نىڭ بىرلىكىنى) بىلمەيدىغان (كۇففار) لارنىڭ دىللىرىنى مۇشۇنداق پېچەتلەيمىز'),
(3469, 30, 60, '(ئى مۇھەممەد!) (مۇشرىكلارنىڭ ئەزىيەتلىرىگە) سەۋر قىلغىن، اﷲ نىڭ (ساڭا نۇسرەت ئاتا قىلىش ۋە دىنىڭنى ئۈستۈنلۈككە ئىگە قىلىش) ۋەدىسى ھەقىقەتەن ھەقتۇر، (ئاخىرەتكە) ئىشەنمەيدىغانلار سېنى دىققەت قىلىپ قويمىسۇن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'ئەلىف، لام، مىم'),
(3471, 31, 2, 'بۇ، ھېكمەتلىك كىتابنىڭ ئايەتلىرىدۇر'),
(3472, 31, 3, '(ئۇ) (تەئدىل ئەركان بىلەن) ناماز ئوقۇيدىغان، (اﷲ نىڭ رەزاسىنى تىلەش يۈزىسىدىن كۆڭۈل ئازادىلىكى بىلەن) زاكات بېرىدىغان ۋە ئاخىرەتكە جەزمەن ئىشىنىدىغان ياخشى ئىش قىلغۇچى كىشىلەرگە ھىدايەتتۇر ۋە رەھمەتتۇر'),
(3473, 31, 4, '(ئۇ) (تەئدىل ئەركان بىلەن) ناماز ئوقۇيدىغان، (اﷲ نىڭ رەزاسىنى تىلەش يۈزىسىدىن كۆڭۈل ئازادىلىكى بىلەن) زاكات بېرىدىغان ۋە ئاخىرەتكە جەزمەن ئىشىنىدىغان ياخشى ئىش قىلغۇچى كىشىلەرگە ھىدايەتتۇر ۋە رەھمەتتۇر'),
(3474, 31, 5, 'ئاشۇلار (يەنى يۇقىرىقى سۈپەتلەرگە ئىگە كىشىلەر) پەرۋەردىگارىنىڭ توغرا يولىدا بولغۇچىلاردۇر، ئەنە شۇلار مەقسەتكە ئېرىشكۈچىلەردۇر'),
(3475, 31, 6, 'بەزى كىشىلەر بىلىمسىزلىكتىن، (كىشىلەرنى) اﷲ نىڭ يولىدىن ئازدۇرۇش ئۈچۈن بىھۇدە گەپلەرنى سېتىۋالىدۇ ۋە اﷲ نىڭ يولىنى مەسخىرە قىلىدۇ، ئەنە شۇلار خور قىلغۇچى ئازابقا دۇچار بولىدۇ'),
(3476, 31, 7, 'ئەگەر ئۇنىڭغا بىزنىڭ ئايەتلىرىمىز (يەنى قۇرئان ئايەتلىرى) ئوقۇپ بېرىلسە، گويا ئۇنى ئاڭلىمىغاندەك، ئىككى قۇلىقى ئېغىرلىشىپ قالغاندەك (اﷲ نىڭ ئايەتلىرىگە قۇلاق سېلىشتىن) تەكەببۇرلۇق بىلەن يۈز ئۆرۈيدۇ، (ئى مۇھەممەد!) ئۇنىڭغا قاتتىق ئازاب بىلەن خۇش خەۋەر بەرگىن'),
(3477, 31, 8, 'شۈبھىسىزكى، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار نازۇنېمەتلىك جەننەتلەرگە كىرىدۇ'),
(3478, 31, 9, 'ئۇ جەننەتلەردە ئۇلار مەڭگۈ قالىدۇ. بۇ اﷲ نىڭ راست ۋەدىسىدۇر (اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ)، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(3479, 31, 10, 'اﷲ ئاسمانلارنى تۈۋرۈكسىز ياراتتىكى، سىلەر ئۇنى كۆرۈپ تۇرۇۋاتىسىلەر، يەر تەۋرەپ سىلەرنى خاتىرجەمسىز قىلمىسۇن دەپ، يەردە تاغلارنى ئورناتتى، زېمىندا تۈرلۈك جاندارلارنى تاراتتى، بۇلۇتتىن يامغۇر سۈيىنى چۈشۈرۈپ، ئۇنىڭ بىلەن زېمىندا تۈرلۈك پايدىلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق'),
(3480, 31, 11, 'مانا بۇلار اﷲ نىڭ (ياراتقان) مەخلۇقاتلىرىدۇر، ماڭا كۆرسىتىپ بېرىڭلاركى، اﷲ تىن باشقا مەبۇدلىرىڭلار زادى نېمىلەرنى ياراتتى؟ بەلكى زالىملار ئوپئوچۇق گۇمراھلىقتىدۇر'),
(3481, 31, 12, 'بىز لوقمانغا ھەقىقەتەن ھېكمەت ئاتا قىلدۇق، (ئۇنىڭغا ئېيتتۇقكى) «اﷲ قا شۈكۈر قىلغىن، كىمكى شۈكۈر قىلىدىكەن، ئۆزى ئۈچۈن قىلغان بولىدۇ، كىمكى كۇفرىلىق قىلىدىكەن، شۈبھىسىزكى، اﷲ (بەندىلەردىن) بىھاجەتتۇر، مەدھىيىگە لايىقتۇر»'),
(3482, 31, 13, 'ئۆز ۋاقتىدا لوقمان (ھەكىم) ئوغلىغا نەسىھەت قىلىپ: «ئى ئوغۇلچىقىم، اﷲ قا شېرىك كەلتۈرمىگىن، شېرىك كەلتۈرۈش ھەقىقەتەن زور گۇناھتۇر» دېدى'),
(3483, 31, 14, 'ئىنساننى ئاتا - ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق. ئانىسى ئۇنى (قورسىقىدا) ئۈستى - ئۈستىگە ئاجىزلىق بىلەن كۆتۈردى. ئىككى يىلدا ئۇنى ئەمچەكتىن ئايرىدى. (ئى ئىنسان!) ماڭا ۋە ئاتا - ئاناڭغا شۈكۈر قىلغىن، ئاخىر قايتىدىغان جاي مېنىڭ دەرگاھىمدۇر'),
(3484, 31, 15, 'ئەگەر ئاتا - ئاناڭ سېنى سەن بىلمەيدىغان نەرسىنى ماڭا شېرىك كەلتۈرۈشكە زورلىسا، ئۇلارغا ئىتائەت قىلمىغىن، ئۇلارغا دۇنيادا ياخشى مۇئامىلىدە بولغىن (يەنى دىنىڭغا زىيان يەتمەيدىغان ئاساستا ياخشىلىق قىلغىن)، ماڭا تائەت بىلەن قايتقان ئادەمنىڭ يولىغا ئەگەشكىن، ئاندىن مېنىڭ دەرگاھىمغا قايتىسىلەر، سىلەرگە قىلمىشىڭلارنى ئېيتىپ بېرىمەن'),
(3485, 31, 16, '(لوقمان ئېيتتى) «ئى ئوغۇلچىقىم! قىلمىشىڭ قىچا چاغلىق نەرسە بولۇپ، ئۇ ئۇيۇل تاشنىڭ ئىچىدە (يەنى ئەڭ مەخپىي جايدا) يا ئاسمانلارنىڭ قېتىدا ياكى زېمىننىڭ ئاستىدا بولسىمۇ، اﷲ ئۇنى ھازىر قىلىدۇ (ئۇنىڭ ھېسابىنى ئالىدۇ). اﷲ ھەقىقەتەن ئىنچىكە كۈزەتكۈچىدۇر، ھەممىدىن خەۋەرداردۇر'),
(3486, 31, 17, 'ئى ئوغۇلچىقىم! نامازنى (ۋاقتىدا تەئدىل ئەركان بىلەن) ئوقۇغىن، (كىشىلەرنى) ياخشىلىققا بۇيرۇغىن، يامانلىقتىن توسقىن، ساڭا يەتكەن كۈلپەتلەرگە سەۋر قىلغىن (چۈنكى ھەقىقەتكە دەۋەت قىلغۇچى ئەزىيەتلەرگە ئۇچرايدۇ)، بۇ ھەقىقەتەن قىلىشقا ئىرادە تىكلەشكە تېگىشلىك ئىشلاردىندۇر'),
(3487, 31, 18, 'كىشىلەردىن مەنسىتمەسلىك بىلەن يۈز ئۆرۈمىگىن، زېمىندا غادىيىپ ماڭمىغىن، اﷲ ھەقىقەتەن ھاكاۋۇر، ئۆزىنى چوڭ تۇتقۇچىلارنى دوست تۇتمايدۇ'),
(3488, 31, 19, 'ئوتتۇراھال ماڭغىن، ئاۋازىڭنى پەسلەتكىن، ئاۋازلارنىڭ ئەڭ زېرىكەرلىكى ھەقىقەتەن ئېشەكلەرنىڭ ئاۋازىدۇر»'),
(3489, 31, 20, 'بىلمەمسىلەركى، اﷲ ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى سىلەرگە بويسۇندۇرۇپ بەردى (يەنى سىلەرنىڭ پايدىلىنىشىڭلارغا مۇۋاپىقلاشتۇرۇپ بەردى). سىلەرگە ئاشكارا ۋە يوشۇرۇن (يەنى ماددىي ۋە مەنىۋى) نېمەتلەرنى كامالەتكە يەتكۈزۈپ بەردى. بەزى كىشىلەر ھېچقانداق ئىلىمسىز، ھىدايەتسىز ۋە نۇرلۇق كىتابسىز ھالدا اﷲ بارىسىدا جېدەللىشىدۇ'),
(3490, 31, 21, 'ئەگەر ئۇلارغا: «اﷲ نازىل قىلغان نەرسە (يەنى قۇرئان) غا ئەگىشىڭلار» دېيىلسە، ئۇلار: «ياق، بىز ئاتا - بوۋىلىرىمىزنىڭ دىنىغا ئەگىشىمىز» دەيدۇ، شەيتان ئۇلارنى دوزاخ ئازابىغا چاقىرغان تۇرسا (ئۇلار يەنىلا ئاتا - بوۋىلىرىنىڭ دىنىغا ئەگىشەمدۇ؟)'),
(3491, 31, 22, 'ياخشى ئىش قىلغۇچى بولغان ھالدا ئىخلاس بىلەن اﷲ قا بويسۇنغان ئادەم مەھكەم تۇتقىغا ئېسىلغان بولىدۇ، ئىشلارنىڭ ئاقىۋىتى اﷲ قا مەنسۇپتۇر'),
(3492, 31, 23, 'كىمكى كاپىر بولىدىكەن، ئۇنىڭ كۇفرى سېنى قايغۇغا سالمىسۇن، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتىدۇ، ئۇلارغا (دۇنيادىكى) قىلمىشلىرىنى ئېيتىپ بېرىمىز، اﷲ ھەقىقەتەن ئۇلارنىڭ دىللىرىدىكىنى بىلىپ تۇرغۇچىدۇر'),
(3493, 31, 24, 'ئۇلارنى (دۇنيادىن) ئازغىنا (مۇددەت) بەھرىمەن قىلىمىز، ئاندىن ئۇلارنى قاتتىق ئازابنى (تېتىشقا) مەجبۇرلايمىز'),
(3494, 31, 25, 'ئەگەر سەن ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم خەلق ئەتتى؟» دەپ سورىساڭ، ئۇلار چوقۇم «اﷲ» دەپ جاۋاپ بېرىدۇ، ئېيتقىنكى، «جىمى ھەمدۇسانا اﷲ قا خاستۇر!» بەلكى ئۇلارنىڭ تولىسى ئۇقمايدۇ'),
(3495, 31, 26, 'ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر اﷲ نىڭ مۈلكىدۇر، اﷲ ھەقىقەتەن (مەخلۇقاتىدىن ۋە ئۇلارنىڭ ئىبادىتىدىن) بىھاجەتتۇر، مەدھىيىگە لايىقتۇر'),
(3496, 31, 27, 'ئەگەر يەر يۈزىدىكى دەرەخلەرنىڭ ھەممىسى قەلەم بولغان، دېڭىز (سىيا) بولغان، ئۇنىڭغا يەنە يەتتە دېڭىز (نىڭ سىياسى) قوشۇلغان تەقدىردىمۇ اﷲ نىڭ سۆزلىرىنى (يېزىپ) تۈگەتكىلى بولمايدۇ، اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(3497, 31, 28, '(ئى ئىنسانلار!) سىلەرنىڭ (دەسلەپتە) يارىتىلىشىڭلار، (ئۆلگەندىن كېيىن) تىرىلدۈرۈلۈشىڭلار پەقەت بىر ئادەمنىڭ يارىتىلىشى، بىر ئادەمنىڭ تىرىلدۈرۈلۈشىگە ئوخشاشتۇر. اﷲ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەمەللىرىنى) كۆرۈپ تۇرغۇچىدۇر'),
(3498, 31, 29, 'اﷲ نىڭ كېچىنى كۈندۈزگە ۋە كۈندۈزنى كېچىگە كىرگۈزىلىدىغانلىقىنى، كۈن بىلەن ئاينى بويسۇندۇرىدىغانلىقىنى كۆرمەمسەن؟ (كۈن بىلەن ئاينىڭ) ھەر بىرى مۇئەييەن مۇددەت (يەنى قىيامەت) كىچە (ئۆز پەلىكىدە) سەير قىلىدۇ. اﷲ سىلەرنىڭ قىلمىشىڭلاردىن تولۇق خەۋەرداردۇر'),
(3499, 31, 30, 'بۇ شۇنىڭ ئۈچۈنكى، اﷲ ھەق (ئىلاھ) دۇر، ئۇلارنىڭ اﷲ نى قويۇپ ئىبادەت قىلغانلىرى (يەنى بۇتلىرى) باتىلدۇر، اﷲ ئالىيدۇر، بۈيۈكتۇر'),
(3500, 31, 31, 'كۆرمەمسەنكى، كېمىلەرنىڭ اﷲ نىڭ مەرھەمىتى بىلەن دېڭىزدا يۈرۈشى اﷲ نىڭ سىلەرگە (ئۆز قۇدرىتىنىڭ) بىر قىسىم دەلىللىرىنى كۆرسىتىشى ئۈچۈندۇر. بۇنىڭدا ھەر بىر سەۋر قىلغۇچى، شۈكۈر قىلغۇچى (بەندە ئۈچۈن ھەقىقەتەن) (چوڭ) ئىبرەت بار'),
(3501, 31, 32, '(دېڭىزدا) ئۇلارنى تاغلاردەك دولقۇنلار ئورىۋالغان چاغدا، ئۇلار كامالى ئىخلاس بىلەن اﷲ قا ئىلتىجا قىلىدۇ، اﷲ ئۇلارنى (دېڭىز خەتەرلىرىدىن) قۇتۇلدۇرۇپ قۇرۇقلۇققا چىقارغان چاغدا، ئۇلارنىڭ بەزىسى توغرا يولدا بولىدۇ. بىزنىڭ ئايەتلىرىمىزنى پەقەت خىيانەتكار (اﷲ نىڭ نېمەتلىرىدىن تانغان) ئادەملا ئىنكار قىلىدۇ'),
(3502, 31, 33, 'ئى ئىنسانلار! پەرۋەردىگارىڭلارغا (بۇيرۇقلىرىنى ئورۇنلاش، مەنئى قىلغان ئىشلاردىن چەكلىنىش بىلەن) تەقۋادارلىق قىلىڭلار، شۇنداق بىر كۈندىن قورقۇڭلاركى، (ئۇ كۈندە) ئاتا بالىسىغا ئەسقاتمايدۇ، بالىمۇ ئاتىسىغا ئەسقاتمايدۇ، اﷲ نىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، سىلەرنى ھەرگىز دۇنيا تىرىكچىلىكى مەغرۇر قىلمىسۇن، شەيتاننىڭ سىلەرنى اﷲ نىڭ ئەپۇسىنىڭ كەڭلىكى بىلەن مەغرۇر قىلىشىغا يول قويماڭلار'),
(3503, 31, 34, 'قىيامەت (نىڭ بولىدىغان ۋاقتى) توغرىسىدىكى بىلىم ھەقىقەتەن اﷲ نىڭ دەرگاھىدىدۇر. اﷲ يامغۇرنى (ئۆزى بەلگىلىگەن ۋاقىتتا ۋە ئۆزى بەلگىلىگەن جايغا) ياغدۇرىدۇ، بەچچىداندىكىلەرنىڭ نېمە ئىكەنلىكىنى (يەنى ئوغۇلمۇ، قىزمۇ، بېجىرىممۇ، كەمتۈكمۇ، بەختلىكمۇ، بەختسىزمۇ) اﷲ بىلىدۇ، ھېچ ئادەم ئەتە نېمىلەرنى قىلىدىغانلىقىنى (يەنى ئۇنىڭغا نېمە ئىش بولىدىغانلىقىنى، ياخشى - يامان ئىشلاردىن نېمىلەرنى قىلىدىغانلىقىنى) بىلمەيدۇ، ھېچ ئادەم ئۆزىنىڭ قەيەردە ئۆلىدىغانلىقىنى بىلمەيدۇ، اﷲ ھەقىقەتەن (پۈتۈن ئىشلارنى) بىلىپ تۇرغۇچىدۇر، (شەيئىلەرنىڭ ئىچكى ۋە تاشقى تەرەپلىرىدىن) تولۇق خەۋەرداردۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'ئەلىف، لام، مىم'),
(3505, 32, 2, '(بۇ) كىتاب (يەنى قۇرئان) نىڭ ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغانلىقىدا ھېچ شەك يوقتۇر'),
(3506, 32, 3, 'مۇشرىكلار: «مۇھەممەد ئۇنى ئۆزى توقۇدى» دەيدۇ. ھەرگىز ئۇنداق ئەمەس، بەلكى ئۇ سەندىن ئىلگىرى ھېچقانداق بىر پەيغەمبەر كەلمىگەن بىر قەۋمنى ھىدايەت تاپسۇن دەپ، ئۇلارنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەتتۇر'),
(3507, 32, 4, 'اﷲ ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتى، ئاندىن ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى. سىلەرگە اﷲ تىن باشقا ھېچقانداق ئىگە يوق، شاپائەت قىلغۇچىمۇ يوق (بۇنى) پىكىر قىلىپ (ئىمان ئېيتمامسىلەر؟)'),
(3508, 32, 5, 'اﷲ ئاسماندىن زېمىنغىچە بولغان (مەخلۇقاتىنىڭ) ئىشلىرىنى ئىدارە قىلىپ تۇرىدۇ، ئاندىن ئۇ ئىشلار ئۇزۇنلۇقى سىلەر ساناۋاتقان مىڭ يىلدەك كېلىدىغان بىر كۈندە اﷲ نىڭ دەرگاھىغا ئۆرلەيدۇ'),
(3509, 32, 6, 'ئۇ (اﷲ) يوشۇرۇننى ۋە ئاشكارىنى بىلگۈچىدۇر، غالىبتۇر. (بەندىلىرىگە) ناھايىتى مېھرىباندۇر'),
(3510, 32, 7, 'ئۇ ھەممە نەرسىنى چىرايلىق (يەنى ھېكمىتىگە مۇۋاپىق، جايىدا) ياراتتى. دەسلەپتە ئىنساننى (يەنى ئىنسانلارنىڭ ئاتىسى ئادەم ئەلەيھىسسالامنى) لايدىن ياراتتى'),
(3511, 32, 8, 'ئاندىن ئۇنىڭ نەسلىنى ئەرزىمەس سۇنىڭ جەۋھىرى (يەنى مەنىي) دىن ياراتتى'),
(3512, 32, 9, 'ئاندىن ئۇنى راۋۇرۇس يارىتىپ، ئۇنىڭغا ئۆزىگە مەنسۇپ روھنى كىرگۈزدى (يەنى جاننى كىرگۈزدى). يەنە سىلەر ئۈچۈن قۇلاقلارنى، كۆزلەرنى ۋە يۈرەكلەرنى ياراتتى، سىلەر ئاز شۈكۈر قىلىسىلەر'),
(3513, 32, 10, 'ئۇلار: «يەر ئاستىدا يوقالغاندىن كېيىن (يەنى گۆردە توپىغا ئايلىنىپ كەتكەندىن كېيىن) راستلا قايتا يارىتىلامدۇق؟» دەيدۇ، بەلكى ئۇلار پەرۋەردىگارىغا مۇلاقات بولۇشنى ئىنكار قىلغۇچىلاردۇر'),
(3514, 32, 11, 'ئېيتقىنكى، «سىلەرنىڭ (جېنىڭلارنى ئېلىشقا) مۇئەككەل قىلىنغان پەرىشتە سىلەرنىڭ جېنىڭلارنى ئالىدۇ، ئاندىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتۇرۇلىسىلەر»'),
(3515, 32, 12, 'ئەگەر سەن گۇناھكارلارنىڭ پەرۋەردىگارىنىڭ دەرگاھىدا باشلىرىنى ساڭگىلىتىپ تۇرغانلىقىنى كۆرىدىغان بولساڭ (ئەلۋەتتە قورقۇنچلۇق ھالەتنى كۆرگەن بولاتتىڭ)، ئۇلار ئېيتىدۇ: «پەرۋەردىگارىمىز! (ئىشنىڭ ھەقىقىتىنى) كۆردۇق، ئاڭلىدۇق. بىزنى ياخشى ئەمەل قىلىش ئۈچۈن (دۇنياغا) قايتۇرغىن، بىز (ئەمدى) ھەقىقەتەن (سېنىڭ ۋەدەڭنىڭ ھەق ئىكەنلىكىگە) ئىشەنگۈچىلەرمىز»'),
(3516, 32, 13, 'ئەگەر بىز خالىساق، ھەر بىر كىشىگە ئەلۋەتتە ھىدايەت بېرەتتۇق، لېكىن مېنىڭ (گۇناھكارلارنى ئازاب قىلىشتىن ئىبارەت) ھۆكمىم بېكىپ كەتتى. مەن چوقۇم جىنلار ۋە ئىنسانلار (يەنى ئۇلارنىڭ گۇناھكارلىرى) - ھەممىسى بىلەن جەھەننەمنى تولدۇرىمەن'),
(3517, 32, 14, '(ئەھلى دوزاخقا ئېيتىلىدۇ) «مۇشۇ كۈندىكى ئۇچرىشىشىڭلارنى ئۇنتۇپ قالغانلىقىڭلار ئۈچۈن (بۇ خورلىغۇچى ئازابنى) تېتىڭلار، بىز ھەقىقەتەن سىلەرنى ئۇنتۇدۇق، قىلمىشىڭلار تۈپەيلىدىن مەڭگۈلۈك ئازابنى تېتىڭلار»'),
(3518, 32, 15, 'بىزنىڭ ئايەتلىرىمىزگە ھەقىقەتەن شۇنداق كىشىلەر ئىمان ئېيتىدۇكى، ئۇلارغا شۇ ئايەتلەر بىلەن ۋەز - نەسىھەت قىلىنسا (اﷲ نىڭ ئايەتلىرىنى ئۇلۇغلاش يۈزىسىدىن اﷲ قا) سەجدە قىلغان ھالدا يىقىلىدۇ، پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمدۇ ئېيتىدۇ. ئۇلار (پەرۋەردىگارىنىڭ تائەت - ئىبادىتىدىن) چوڭچىلىق قىلىپ باش تارتمايدۇ'),
(3519, 32, 16, 'ئۇلارنىڭ يانلىرى ئورۇن - كۆرپىدىن يىراق بولىدۇ (يەنى ئۇلار كېچىسى ئىبادەت قىلىپ ئاز ئۇخلايدۇ)، ئۇلار پەرۋەردىگارىنىڭ (ئازابىدىن) قورقۇپ، (رەھمىتىنى) ئۈمىد قىلىپ ئۇنىڭغا دۇئا قىلىدۇ، ئۇلارغا بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن (ياخشىلىق يوللىرىغا) سەرپ قىلىدۇ'),
(3520, 32, 17, 'ئۇلارنىڭ قىلغان ئەمەللىرىگە مۇكاپات يۈزىسىدىن اﷲ نىڭ ھۇزۇرىدا ساقلانغان ۋە ئۇلارنى خۇشال قىلىدىغان كاتتا نېمەتنى ھېچكىم بىلمەيدۇ'),
(3521, 32, 18, 'مۆمىن ئادەم پاسىق ئادەمگە ئوخشامدۇ؟ ئۇلار (ئاخىرەتلىك ساۋابتا) باراۋەر ئەمەس'),
(3522, 32, 19, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كەلسەك، ئۇلارنىڭ قارارگاھى جەننەت بولىدۇ، (ئۇ) ئۇلارنىڭ قىلغان ئەمەللىرىنى مۇكاپاتلاش يۈزىسىدىن ئۇلا رئۈچۈن تەييارلانغان جايدۇر'),
(3523, 32, 20, 'اﷲ نىڭ ئىتائىتىدىن چىققانلارغا كەلسەك، ئۇلارنىڭ جايى دوزاخ بولىدۇ، ھەر قاچان ئۇلار دوزاختىن چىقماقچى بولسا، ئۇلار دوزاخقا قايتۇرۇلىدۇ. ئۇلارغا: «سىلەر ئىنكار قىلغان دوزاخنىڭ ئازابىنى تېتىڭلار» دېيىلىدۇ'),
(3524, 32, 21, 'ئۇلارنىڭ تەۋبە قىلىشى ئۈچۈن بىز ئۇلارغا چوقۇم ئەڭ چوڭ ئازابتىن (يەنى ئاخىرەت ئازابىدىن) ئىلگىرى ئەڭ يېقىنقى ئازاب (يەنى دۇنيا ئازابى) نى تېتىتىمىز'),
(3525, 32, 22, 'پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن ۋەز - نەسىھەت قىلىنغان، ئاندىن ئۇنىڭدىن يۈز ئۆرۈگەن ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ بىز گۇناھكارلارنى چوقۇم جازالايمىز'),
(3526, 32, 23, 'شەك - شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق. (ئى مۇھەممەد!) سەن (مىراج كېچىسىدە، يا قىيامەت كۈنىدە) مۇساغا مۇلاقات بولۇشتىن شەكلەنمىگىن، بىز تەۋراتنى ئىسرائىل ئەۋلادىغا يېتەكچى قىلدۇق'),
(3527, 32, 24, 'ئۇلار (كۈلپەتلەرگە) سەۋر قىلغان، بىزنىڭ ئايەتلىرىمىزگە جەزمەن ئىشەنگەن چاغدا، ئۇلاردىن بىر قىسىم كىشىلەرنى بىزنىڭ ئەمرىمىز بىلەن توغرا يول كۆرسىتىدىغان پېشۋالار قىلدۇق'),
(3528, 32, 25, 'سېنىڭ پەرۋەردىگارىڭ قىيامەت كۈنى ئۇلارنىڭ ئارىسىدا ئۇلار ئىختىلاپ قىلىشقان (دىنىي) ئىشلار ئۈستىدە ئەلۋەتتە ھۆكۈم چىقىرىدۇ'),
(3529, 32, 26, 'ئۇلاردىن ئىلگىرى ئۆتكەن قانچىلىغان ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار بىلمەمدۇ؟ ئۇلار (يەنى مەككىلىكلەر) ئۇلار (يەنى ھالاك بولغانلار) نىڭ يۇرتىدىن ئۆتۈپ تۇرىدۇ، بۇنىڭدا (بىزنىڭ قۇدرىتىمىزنىڭ چوڭلۇقىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار، ئۇلار ئاڭلىمامدۇ؟'),
(3530, 32, 27, 'ئۇلار كۆرمەمدۇكى، بىز گىياھسىز زېمىنغا يامغۇر سۈيىنى سۈرۈپ ئېلىپ بارىمىز، ئۇنىڭ بىلەن ئۇلارنىڭ ھايۋاناتلىرى ۋە ئۆزلىرى يەيدىغان زىرائەتلەرنى ئۈندۈرۈپ چىقىرىمىز، ئۇلار (بۇنى) كۆرمەمدۇ؟'),
(3531, 32, 28, 'ئۇلار (يەنى مەككە كۇففارلىرى مەسخىرە قىلىش يۈزىسىدىن): «(بىز بىلەن سىلەرنىڭ ئاراڭلاردا چىقىرىلىدىغان) بۇ ھۆكۈم قاچان ئەمەلگە ئاشىدۇ؟ ئەگەر سىلەر (دەۋايىڭلاردا) راستچىل بولساڭلار (ئېيتىپ بېرىڭلار)» دەيدۇ'),
(3532, 32, 29, 'ئېيتقىنكى، «ھۆكۈم چىقىرىلىدىغان كۈندە كاپىرلارنىڭ ئېيتقان ئىمانىنىڭ پايدىسى بولمايدۇ، ئۇلارغا مۆھلەتمۇ بېرىلمەيدۇ»'),
(3533, 32, 30, 'سەن ئۇلاردىن يۈز ئۆرۈگىن (يەنى سۆزلىرىگە پەرۋا قىلىپ كەتمىگىن). سەن (اﷲ نىڭ ئۇلارغا چۈشىدىغان ئازابىنى) كۈتكىن، ئۇلارمۇ ھەقىقەتەن (سىلەرگە چۈشىدىغان نەرسىلەرنى) كۈتكۈچىلەردۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'ئى پەيغەمبەر! اﷲ قا تەقۋادارلىق قىلىشنى (داۋاملاشتۇرغىن)، كاپىرلارغا ۋە مۇناپىقلارغا ئىتائەت قىلمىغىن، اﷲ ھەقىقەتەن (بەندىلەرنىڭ ئەمەللىرىنى) بىلىپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئىشلىرىنى) ھېكمەت بىلەن باشقۇرغۇچىدۇر'),
(3535, 33, 2, 'ساڭا پەرۋەردىگارىڭ تەرىپىدىن ۋەھيى قىلىنغان نەرسىگە (يەنى قۇرئانغا) ئەگەشكىن. اﷲ ھەقىقەتەن قىلغان ئەمەلىڭلاردىن تولۇق خەۋەرداردۇر'),
(3536, 33, 3, '(ھەممە ئىشىڭدا) اﷲ قا يۆلەنگىن، اﷲ (ساڭا) ھامىي بولۇشقا يېتەرلىكتۇر'),
(3537, 33, 4, 'اﷲ ھېچ ئادەمنىڭ ئىچىدە ئىككى يۈرەك ياراتقىنى يوق، سىلەر زىھار قىلغان ئاياللىرىڭلارنى اﷲ سىلەرگە ئانا قىلغىنى يوق، سىلەر بالا قىلىۋالغانلارنىمۇ اﷲ (ھەممە ھۆكۈمدە ئۆز پۇشتۇڭلاردىن بولغان) بالىلىرىڭلارنىڭ ھۆكمىدە قىلمىدى، بۇ (يەنى ئۆز پۇشتۇڭلاردىن بولمىغانلارنى بالا دېيىش) پەقەت ئاغزىڭلاردا ئېيتىلغان سۆزدۇر، اﷲ ھەق (سۆز) نى ئېيتىدۇ، توغرا يولغا باشلايدۇ'),
(3538, 33, 5, 'ئۇلارنى ئاتىلىرىنىڭ ئىسىملىرى بىلەن چاقىرىڭلار، بۇ اﷲ نىڭ دەرگاھىدا توغرىدۇر، ئەگەر ئۇلارنىڭ ئاتىلىرىنى بىلمىسەڭلار، ئۇ چاغدا ئۇلار سىلەرنىڭ دىنىي قېرىنداشلىرىڭلاردۇر، دوستلىرىڭلاردۇر، سىلەر سەۋەنلىكتىن قىلىپ سالغان ئىشلاردا سىلەرگە ھېچ گۇناھ بولمايدۇ، لېكىن قەستەن قىلغان ئىشىڭلاردا (سىلەرگە گۇناھ بولىدۇ)، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(3539, 33, 6, 'پەيغەمبەر مۆمىنلەرگە ئۇلارنىڭ ئۆز نەپسىلىرىدىنمۇ چارىدۇر، پەيغەمبەرنىڭ ئاياللىرى ئۇلارنىڭ ئانىلىرىدۇر، كىتابۇللاھتا ئۇرۇق - تۇغقانلارنىڭ بەزىسى بەزىسىگە (مىراس ئېلىشتا) مۆمىنلەردىن ۋە مۇھاجىرلاردىن يېقىندۇر، لېكىن (مۆمىنلەردىن ۋە مۇھاجىرلاردىن بولغان) دوستلىرىڭلارغا ۋەسىيەت قىلساڭلار دۇرۇس بولىدۇ، بۇ (يەنى تۇغقانلارنىڭ ئۆز ئارا مىراسقا ۋارىسلىق قىلىشى) كىتابتا (يەنى اﷲ نىڭ كىتابىدا) يېزىلغاندۇر'),
(3540, 33, 7, 'ئۆز ۋاقتىدا بىز پەيغەمبەرلەردىن ئەھدە ئالدۇق، سەندىن، نۇھتىن، ئىبراھىمدىن، مۇسادىن، مەريەم ئوغلى ئىسادىن مەھكەم ئەھدە ئالدۇق'),
(3541, 33, 8, 'اﷲ (قىيامەت كۈنى) راستچىللاردىن راستچىللىقى توغرۇلۇق سورايدۇ (يەنى پەيغەمبەرلەردىن پەيغەمبەرلىكىنى يەتكۈزگەنلىكىنى سورايدۇ)، اﷲ كاپىرلارغا قاتتىق ئازاب تەييارلىدى'),
(3542, 33, 9, 'ئى مۆمىنلەر! اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار، ئۆز ۋاقتىدا ئۈستۈڭلارغا (كۇففارلاردىن ئۇيۇشقان ئۇرۇشقۇچى) قوشۇن كەلگەن ئىدى. بىز ئۇلارغا قارشى بوران ۋە سىلەرگە كۆرۈنمەيدىغان قوشۇن (يەنى پەرىشتىلەرنى) ئەۋەتتۇق، اﷲ سىلەرنىڭ قىلغان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(3543, 33, 10, 'ئەينى ۋاقىتتا ئۇلار يۇقىرى تەرىپىڭلاردىنمۇ، تۆۋەن تەرىپىڭلاردىنمۇ (ھۇجۇم قىلىپ) كەلگەن ئىدى. بۇ چاغدا كۆزۈڭلار چەكچىيىپ قالغان، يۈرىكىڭلار ئاغزىڭلارغا قاپلىشىپ قالغان، اﷲ ھەققىدە تۈرلۈك گۇمانلاردا بولغان ئىدىڭلار'),
(3544, 33, 11, 'بۇ يەردە مۆمىنلەر سىنالغان ۋە (بېشىغا كەلگەن كۈننىڭ قاتتىقلىقىدىن گويا ئۇلارنى يەر سىلكىگەندەك) قاتتىق تەۋرىنىشكە ئۇچرىغان ئىدى'),
(3545, 33, 12, 'ئەينى ۋاقىتتا مۇناپىقلار ۋە دىللىرىدا (مۇناپىقلىق) كېسىلى بارلار: «اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىزلەرگە قۇرۇق ۋەدىلەرنى قىلىپتىكەن» دېيىشەتتى'),
(3546, 33, 13, 'ئەينى ۋاقىتتا ئۇلاردىن بىر گۇرۇھ ئادەم: «ئى يەسرىبلىكلەر! بۇ يەردە تۇرماي قايتىڭلار!» دېيىشەتتى، ئۇلاردىن يەنە بىر گۇرۇھ ئادەم: «بىزنىڭ ئۆيلىرىمىز ھەقىقەتەن ئوچۇقچىلىقتا» دېيىشىپ، پەيغەمبەردىن رۇخسەت سورىشاتتى، بۇلارنىڭ ئۆيلىرى ئوچۇقچىلىقتا ئەمەس، ئۇلار پەقەت قېچىشنىلا ئويلايدۇ'),
(3547, 33, 14, 'ئەگەر شەھەرگە تۆت تەرەپتىن ھۇجۇم قىلىپ كىرىلسە، ئاندىن ئۇلاردىن ئىمانىدىن قايتىش، مۇسۇلمانلارغا قارشى ئۇرۇش قىلىش تەلەپ قىلىنسا، ئۇلار بۇ تەلەپنى ئۈزلۈكسىز رەۋىشتە ئاز ۋاقىت ئىچىدىلا قوبۇل قىلاتتى'),
(3548, 33, 15, 'ئۇلار (يەنى مۇناپىقلار) ئىلگىرى اﷲ قا (جەڭدە) چېكىنمەسلىككە ۋەدە بەرگەن ئىدى. اﷲ قا بەرگەن ۋەدىسى سورىلىدۇ'),
(3549, 33, 16, 'ئېيتقىنكى، «ئەگەر سىلەر ئۆلۈمدىن يا ئۆلتۈرۈلۈشتىن قاچساڭلار، قېچىشنىڭ سىلەرگە ھېچقانداق پايدىسى يوق، قاچقاندىمۇ سىلەر (دۇنيادىن) ئازغىنا (ۋاقىت) بەھرىمەن بولالايسىلەر»'),
(3550, 33, 17, 'ئېيتقىنكى: «ئەگەر اﷲ سىلەرگە بىرەر يامانلىقنى ئىرادە قىلسا، كىم اﷲ قا قارشى تۇرۇپ سىلەرنى قوغدىيالايدۇ، ئەگەر اﷲ سىلەرگە رەھمەت قىلىشنى ئىرادە قىلسا (كىم ئۇنىڭغا ئارىلىشالايدۇ؟)» ئۇلار ئۆزلىرى ئۈچۈن اﷲ تىن باشقا ھېچ ئىگە ۋە ھېچ ياردەمچى تاپالمايدۇ'),
(3551, 33, 18, 'اﷲ ئىچىڭلاردىكى باشقىلارنى (جىھادتىن) توسقۇچىلارنى ۋە قېرىنداشلىرىغا (مۇھەممەدنى ۋە ساھابىلىرىنى تەرك ئېتىپ) بىزنىڭ قېشىمىزغا كېلىڭلار دېگۈچىلەرنى ئوبدان بىلىدۇ، ئۇلار جەڭگە (رىيا قىلىپ) ئاندا - ساندا قاتنىشىدۇ'),
(3552, 33, 19, 'ئۇلار سىلەرگە (ياردەمدە بولۇشقا) بېخىلدۇر، ئەگەر (ئۇلارغا دۈشمەن تەرەپتىن) خەۋپ كېلىدىغان بولسا، ئۇلارنىڭ ساڭا سەكراتقا چۈشۈپ قالغان ئادەمدەك (قورقۇنچتىن) كۆزلىرىنى پىلدىر لىتىپ قاراۋاتقانلىقىنى كۆرىسەن. قورقۇنچ كەتكەندە، ئۇلار پۇل - مالغا ئاچكۆز بولغان ھالدا سىلەرنى كەسكىن تىللار بىلەن رەنجىتىدۇ. ئۇلار (چىن دىللىرى بىلەن) ئىمان ئېيتقان ئەمەس. (ئۇلارنىڭ كۇفرى سەۋەبلىك) ئەمەللىرىنى اﷲ بىكار قىلدى، بۇ اﷲ ئۈچۈن ئاساندۇر'),
(3553, 33, 20, 'ئۇلار (يەنى مۇناپىقلار) (قاتتىق قورققانلىقتىن) ئىتتىپاقداش قوشۇننى چېكىنمىدى دەپ ئويلايدۇ، ئەگەر ئىتتىپاقداش قوشۇن قايتا كەلسە، ئۇلار (قىردا) ئەئرابىلارنىڭ (يەنى قىر ئەرەبلىرىنىڭ) يېنىدا تۇرۇپ سىلەرنىڭ خەۋەرلىرىڭلارنى ئۇققاچ (تىنچ يېتىشىنى) ئارزۇ قىلىدۇ، ئۇلار (ئۇرۇش ۋاقتىدا) ئاراڭلاردا بولغان تەقدىردىمۇ، (قورققانلىقتىن) جەڭگە (رىيا قىلىپ) ئاندا - ساندا قاتناشقان بولاتتى'),
(3554, 33, 21, 'سىلەرگە - اﷲ نى، ئاخىرەت كۈنىنى ئۈمىد قىلغان ۋە اﷲ نى كۆپ ياد ئەتكەنلەرگە - رەسۇلۇللاھ ئەلۋەتتە ياخشى ئۈلگىدۇر'),
(3555, 33, 22, 'مۆمىنلەر ئىتتىپاقداش قوشۇننى كۆرگەن چاغدا: «بۇ (يەنى قېيىن ئەھۋالدا قالغاندا دۈشمەن ئۈستىدىن غەلىبە قىلىش) اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىزگە ۋەدە قىلغان ئىشتۇر. اﷲ ۋە ئۇنىڭ پەيغەمبىرى راست ئېيتتى» دېيىشتى. (بۇ ئىش) ئۇلارنىڭ (اﷲ قا بولغان) ئىمانىنى ۋە (اﷲ نىڭ بۇيرۇقلىرىغا بولغان) بويسۇنۇشىنى تېخىمۇ كۈچەيتتى'),
(3556, 33, 23, 'مۆمىنلەرنىڭ ئىچىدە (رەسۇلۇللاھ بىلەن غازاتقا چىققاندا ساباتلىق بولۇپ، شېھىت بولغانغا قەدەر دادىللىق بىلەن جەڭ قىلىش توغرۇلۇق) اﷲ قا بەرگەن ئەھدىنى ئىشقا ئاشۇرغان نۇرغۇن كىشىلەر بار. ئۇلارنىڭ بەزىسى (ئەھدىگە ۋاپا قىلىپ) شېھىت بولدى، بەزىسى (شېھىت بولۇشنى) كۈتكمەكتە، ئۇلار (پەرۋەردىگارىغا بەرگەن ئەھدىسىنى) ھەرگىز ئۆزگەرتكىنى يوق'),
(3557, 33, 24, 'اﷲ راستچىللارغا راستچىللىقى ئۈچۈن (ئاخىرەتتە ئەڭ ياخشى) مۇكاپات بېرىدۇ، مۇناپىقلارنى (ئەھدىنى بۇزغۇچى مۇناپىقلارنى مۇناپىق پېتى بۇ دۇنيادىن كەتكۈزۈپ) ئەگەر خالىسا ئازابلايدۇ، يا ئۇلارنى تەۋبىگە مۇۋەپپەق قىلىدۇ، اﷲ ھەقىقەتەن (بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(3558, 33, 25, 'اﷲ كاپىرلار (يەنى مەدىنىگە ھۇجۇم قىلغان ئىتتىپاقداش قوشۇن) نى خاپا قايتۇردى، ئۇلار مەقسىتىگە يېتەلمىدى، اﷲ (دۈشمەنگە بوران ۋە پەرىشتىلەرنى ئاپىرىدە قىلىپ) مۆمىنلەرنى ئۇرۇشسىز غەلىبىگە ئېرىشتۈردى، اﷲ كۈچلۈكتۇر، غالىبتۇر'),
(3559, 33, 26, 'اﷲ ئەھلى كىتابتىن مۇشرىكلارغا ياردەم بەرگەنلەرنى (يەنى رەسۇلۇللاھ بىلەن قىلغان ئەھدىنى بۇزغان بەنى قۇرەيزە يەھۇدىيلىرىنى) قۇرغانلىرىدىن چۈشۈردى، ئۇلارنىڭ دىللىرىغا قورقۇنچ سالدى. (ئۇلارنىڭ) بىر بۆلۈكىنى ئۆلتۈردۇڭلار ۋە بىر بۆلۈكىنى ئەسىر ئالدىڭلار'),
(3560, 33, 27, 'اﷲ سىلەرگە ئۇلارنىڭ زېمىنىنى، قورو - جايلىرىنى، مال - مۈلۈكلىرىنى ۋە (تېخى) سىلەرنىڭ قەدىمىڭلار دەسسەپ باقمىغان زېمىننى مىراس قىلىپ بەردى، اﷲ ھەر نەرسىگە قادىردۇر'),
(3561, 33, 28, 'ئى پەيغەمبەر! (ئارتۇق خىراجەت سوراپ سېنى رەنجىتكەن) ئاياللىرىڭغا ئېيتقىنكى، «ئەگەر سىلەر دۇنيا تېرىكچىلىكىنى (يەنى پاراۋان تۇرمۇشنى) ۋە دۇنيانىڭ زىبۇ زىننىتىنى كۆزلىسەڭلار، كېلىڭلار، سىلەرگە بىر ئاز نەرسە بېرەي، سىلەرنى چىرايلىقچە قويۇپ بېرەي'),
(3562, 33, 29, 'ئەگەر سىلەر اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى، ئاخىرەت يۇرتىنى ئىختىيار قىلساڭلار، اﷲ ھەقىقەتەن ئىچىڭلاردىكى ياخشى ئىش قىلغۇچىلارغا چوڭ ساۋابنى (يەنى كۆز كۆرمىگەن، قۇلاق ئاڭلىمىغان ۋە ئىنساننىڭ كۆڭلىگە كەلمىگەن نازۇنېمەتلەر بار جەننەتنى) تەييار قىلدى»'),
(3563, 33, 30, 'ئى پەيغەمبەرنىڭ ئاياللىرى! ئىچىڭلاردىن كىمكى ئاشكارا قەبىھ ئىشنى قىلىدىكەن، ئۇنىڭغا ھەسسىلەپ ئازاب قىلىنىدۇ، بۇ اﷲ قا ئاساندۇر'),
(3564, 33, 31, 'سىلەردىن كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلسا ۋە ياخشى ئەمەل قىلسا، ئۇنىڭغا ساۋابنى ئىككى قاتلاپ بېرىمىز، ئۇنىڭغا (جەننەتتە) ئېسىل رىزىق تەييارلىدۇق'),
(3565, 33, 32, 'ئى پەيغەمبەرنىڭ ئاياللىرى! سىلەر باشقا ئاياللارنىڭ ھېچبىرىگە ئوخشىمايسىلەر، سىلەر (يات ئەرلەرگە سۆز قىلغاندا) نازاكەت بىلەن سۆز قىلماڭلار، (نازاكەت بىلەن سۆز قىلساڭلار) دىلىدا نىفاق بار ئادەم (سىلەرگە قارىتا) تەمەدە بولۇپ قالىدۇ، (گۇماندىن خالى) ياخشى سۆزنى سۆزلەڭلار'),
(3566, 33, 33, 'ئۆيلىرىڭلاردا ئولتۇرۇڭلار، ئىلگىرىكى جاھىلىيەت دەۋرىدىكى ئاياللارنىڭ ياسىنىپ چىققىنىدەك ياسىنىپ چىقماڭلار، ناماز ئوقۇڭلار، زاكات بېرىڭلار، اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، ئى پەيغەمبەرنىڭ ئائىلىسىدىكىلەر! اﷲ سىلەردىن گۇناھنى ساقىت قىلىشنى ۋە سىلەرنى تامامەن پاك قىلىشنى خالايدۇ'),
(3567, 33, 34, 'ئۆيلىرىڭلاردا اﷲ نىڭ ئايەتلىرىدىن ۋە ھېكمەت (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ ھەدىسلىرى) تىن ئوقۇلۇۋاتقان نەرسىلەرنى ياد ئېتىڭلار، اﷲ ھەقىقەتەن سىرلەرنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر'),
(3568, 33, 35, 'مۇسۇلمان ئەرلەر ۋە مۇسۇلمان ئاياللارغا، مۆمىن ئەرلەر ۋە مۆمىن ئاياللارغا، تائەت - ئىبادەت قىلغۇچى ئەرلەر ۋە تائەت - ئىبادەت قىلغۇچى ئاياللارغا، راستچىل ئەرلەر ۋە راستچىل ئاياللارغا، سەۋر قىلغۇچى ئەرلەر ۋە سەۋر قىلغۇچى ئاياللارغا، خۇدادىن قورققۇچى ئەرلەر ۋە خۇدادىن قورققۇچى ئاياللارغا، سەدىقە بەرگۈچى ئەرلەر ۋە سەدىقە بەرگۈچى ئاياللارغا، روزا تۇتقۇچى ئەرلەر ۋە روزا تۇتقۇچى ئاياللارغا، نەپسىلىرىنى ھارامدىن ساقلىغۇچى ئەرلەر ۋە نەپسىلىرىنى ھارامدىن ساقلىغۇچى ئاياللارغا، اﷲ نى كۆپ زىكرى قىلغۇچى ئەرلەر ۋە اﷲ نى كۆپ زىكرى قىلغۇچى ئاياللارغا اﷲ مەغپىرەت ۋە كاتتا ساۋاب تەييارلىدى'),
(3569, 33, 36, 'اﷲ ۋە ئۇنىڭ پەيغەمبىرى بىرەر ئىشتا ھۆكۈم چىقارغان چاغدا، ئەر - ئايال مۆمىنلەرنىڭ ئۆز ئىشىدا ئىختىيارلىقى بولمايدۇ (يەنى اﷲ ۋە اﷲ نىڭ پەيغەمبىرى بىرەر ئىشتا ھۆكۈم چىقارغان ئىكەن، ھېچ ئادەمنىڭ ئۇنىڭغا مۇخالىپەتچىلىك قىلىشىغا بولمايدۇ)، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئاسىيلىق قىلسا، ھەقىقەتەن ئۇ ئوپئوچۇق ئازغان بولىدۇ'),
(3570, 33, 37, 'ئۆز ۋاقتىدا سەن اﷲ نېمەت بەرگەن، سەنمۇ ئىنئام قىلغان كىشىگە: «خوتۇنۇڭنى نىكاھىڭدا تۇتقىن، اﷲ تىن قورققىن!» دېدىڭ، اﷲ ئاشكارىلىماقچى بولغان نەرسىنى كۆڭلۈڭدە يوشۇردۇڭ، كىشىلەرنىڭ تەنە قىلىشىدىن قورقتۇڭ، اﷲ تىن قۇرقۇشۇڭ ئەڭ ھەقلىق ئىدى. مۆمىنلەرگە ئۇلارنىڭ بالا قىلىۋالغان ئوغۇللىرىنىڭ قويۇپ بەرگەن خوتۇنلىرىنى نىكاھلاپ ئالسا گۇناھ بولماسلىقى ئۈچۈن، زەينەبنى زەيد قويۇۋەتكەندىن كېيىن ساڭا نىكاھلاپ بەردۇق، اﷲ نىڭ (سېنىڭ زەينەبنى ئېلىشىڭ توغرىسىدىكى) ئەمرى چوقۇم ئورۇنلىنىدۇ'),
(3571, 33, 38, 'اﷲ پەيغەمبەرگە ھالال قىلغان ئىشقا ھېچ گۇناھ بولمايدۇ. (بۇ) اﷲ نىڭ ئۆتكەنكى (پەيغەمبەرلەرگە) تۇتقان يولىدۇر. اﷲ نىڭ ئەمرى ئەزەلدىنلا بېكىپ كەتكەن (ئۆزگەرمەس) ھۆكۈمدۇر'),
(3572, 33, 39, 'ئۇلار (يەنى پەيغەمبەرلەر) اﷲ نىڭ ئەمر - پەرمانلىرىنى يەتكۈزىدۇ، اﷲ تىن قورقىدۇ، اﷲ تىن باشقا ھېچ كىشىدىن قورقمايدۇ (ئى مۇھەممەد! سەنمۇ اﷲ تىن غەيرىيدىن قورقماسلىقتا شۇ پەيغەمبەرلەرگە ئەگەشكىن)، اﷲ (ھەممە ئىشلاردىن) ھېساب ئېلىشقا يېتەرلىكتۇر'),
(3573, 33, 40, 'مۇھەممەد ئاراڭلاردىكى ئەرلەردىن ھېچبىرىنىڭ ئاتىسى ئەمەس، لېكىن (ئۇ) اﷲ نىڭ پەيغەمبىر ى ۋە پەيغەمبەرلەرنىڭ ئاخىرقىسىدۇر (اﷲ ئۇنىڭ بىلەن پەيغەمبەرلىكنى ئاخىرلاشتۇرغان، ئۇنىڭدىن كېيىن ھېچقانداق پەيغەمبەر كەلمەيدۇ)، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر (سىلەرنىڭ سۆز - ھەرىكىتىڭلاردىن ھېچ نەرسە اﷲ قا مەخپىي ئەمەس)'),
(3574, 33, 41, 'ئى مۆمىنلەر! اﷲ نى كۆپ ياد قىلىڭلار'),
(3575, 33, 42, 'ئۇنىڭغا ئەتىگەن - ئاخشامدا تەسبىھ ئېيتىڭلار (چۈنكى بۇ ئىككى ۋاقىتتا پەرىشتىلەر چۈشۈپ تۇرغانلىقتىن، ئۇ ۋاقىتلارنىڭ ئەۋزىلى ھېسابلىنىدۇ)'),
(3576, 33, 43, 'اﷲ سىلەرنى قاراڭغۇلۇقتىن نۇرغا (يەنى گۇمراھلىقتىن ھىدايەتكە) چىقىرىش ئۈچۈن، سىلەرگە رەھمەت قىلىپ تۇرىدۇ، ئۇنىڭ پەرىشتىلىرى سىلەرگە مەغپىرەت تەلەپ قىلىپ تۇرىدۇ، اﷲ مۆمىنلەرگە ناھايىتى مېھرىباندۇر'),
(3577, 33, 44, 'ئۇلار اﷲ قا مۇلاقات بولغان كۈندە ئۇلارغا اﷲ تەرىپىدىن يوللىنىدىغان سالام (سىلەرگە) ئامانلىق بولسۇن (دېگەن سۆزدىن ئىبارەت)، اﷲ ئۇلارغا ئېسىل مۇكاپات تەييارلىدى'),
(3578, 33, 45, 'ئى پەيغەمبەر! سېنى بىز ھەقىقەتەن گۇۋاھچى، بېشارەتچى، ئاگاھلاندۇرغۇچى، اﷲ نىڭ ئىزنى بىلەن ئۇنىڭ (بىر لىكىگە، تائەت - ئىبادىتىگە) دەۋەت قىلغۇچى ۋە نۇرلۇق چىراق قىلىپ ئەۋەتتۇق'),
(3579, 33, 46, 'ئى پەيغەمبەر! سېنى بىز ھەقىقەتەن گۇۋاھچى، بېشارەتچى، ئاگاھلاندۇرغۇچى، اﷲ نىڭ ئىزنى بىلەن ئۇنىڭ (بىر لىكىگە، تائەت - ئىبادىتىگە) دەۋەت قىلغۇچى ۋە نۇرلۇق چىراق قىلىپ ئەۋەتتۇق'),
(3580, 33, 47, 'مۆمىنلەرگە ئۇلارنىڭ اﷲ نىڭ بۈيۈك ئېھسانىغا ئېرىشىدىغانلىقىدىن خۇش خەۋەر بەرگىن'),
(3581, 33, 48, 'كاپىرلارغا ۋە مۇناپىقلارغا ئىتائەت قىلمىغىن، ئۇلارنىڭ (ساڭا قىلغان) ئازار - كۈلپەتلىرىگە پەرۋا قىلمىغىن (اﷲ قا تەۋەككۈل قىلغىن)، اﷲ (ئۇنىڭغا) تەۋەككۈل قىلغانلارغا يېتەرلىكتۇر'),
(3582, 33, 49, 'ئى مۆمىنلەر! سىلەر مۆمىن ئاياللارنى ئالساڭلار، ئاندىن ئۇلارغا يېقىنچىلىق قىلىشتىن بۇرۇن ئۇلارنى قويۇۋەتسەڭلار، بۇ چاغدا ئۇلار سىلەر ئۈچۈن ئىددەت تۈتماسلىقى كېرەك، ئۇلارغا ئاز - تولا نەرسە بېرىڭلار، ئۇلارنى چىرايلىقچە قويۇپ بېرىڭلار'),
(3583, 33, 50, 'ئى پەيغەمبەر! ساڭا بىز سەن مەھرىلىرىنى بەرگەن ئاياللىرىڭنى، اﷲ ساڭا غەنىمەت قىلىپ بەرگەن، ئەسىر چۈشكەن ئاياللاردىن ئۆزۈڭ مالىك بولغانلارنى ھالال قىلدۇق، ئاتاڭنىڭ ئەر قېرىندىشى ۋە ھەمشىرە قېرىندىشىنىڭ قىزلىرىنى، ئاناڭنىڭ ئەر قېرىندىشى ۋە ھەمشىرە قېرىندىشىنىڭ قىزلىرىنى (ساڭا ھالال قىلدۇق). بۇلار سەن بىلەن بىرلىكتە ھىجرەت قىلدى. ئەگەر بىر مۆمىن خوتۇن ئۆزىنى پەيغەمبەرگە بەخش قىلسا، پەيغەمبەر ئۇنى (مەھرىسىز) ئېلىشىنى خالىسا، بۇنى مۆمىنلەرگە ئەمەس، يالغۇز ساڭىلا (ھالال قىلدۇق)، ئۇلارنىڭ ئاياللىرى ۋە چۆرىلىرى توغرۇلۇق بىزنىڭ چىقارغان بەلگىلىمىلىرىمىز بىزگە مەلۇمدۇر، ساڭا ھەرەج بولۇپ قالماسلىقى ئۈچۈن (سېنى ئالاھىدىلىكلەرگە ئىگە قىلدۇق)، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(3584, 33, 51, 'ئاياللىرىڭدىن خالىغىنىڭنىڭ نۆۋىتىنى ئارقىغا سۈرسەڭ، خالىغىنىڭ بىلەن (نۆۋىتىدىن باشقا ۋاقىتتا) بىر يەردە بولساڭ بولىۋېرىدۇ، (ۋاقىتلىق) ئايرىلىپ تۇرغان ئاياللىرىڭدىن بىرى بىلەن ھەمتۆشەك بولماقچى بولساڭ، ساڭا ھېچ گۇناھ يوقتۇر، (مانا) بۇ ئۇلارنىڭ خۇشال بولۇشىغا، قايغۇرماسلىقىغا، بەرگەنلىرىڭگە ئۇلارنىڭ ھەممىسىنىڭ رازى بولۇشىغا ئەڭ يېقىندۇر، اﷲ دىلىڭلاردىكىنى بىلىپ تۇرىدۇ، اﷲ ھەممىنى بىلگۈچىدۇر، ھەلىمدۇر (يەنى جازالاشقا ئالدىراپ كەتمەيدۇ)'),
(3585, 33, 52, '(ئى پەيغەمبەر! ئىلكىڭدىكى ئاياللىرىڭدىن باشقا) يەنە خوتۇن ئالساڭ، ساڭا ھالال بولمايدۇ، باشقا ئاياللارنىڭ جامالى سېنى ئەجەبلەندۈرگەن تەقدىردىمۇ، ئۇلارنى يەڭگۈشلەش (ساڭا ھالال بولمايدۇ). پەقەت سېنىڭ ئىلكىڭدىكى چۆرىلەر بۇنىڭدىن مۇستەسنا، اﷲ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر'),
(3586, 33, 53, 'ئى مۆمىنلەر! پەيغەمبەرنىڭ ئۆيلىرىگە كىرمەڭلار، پەقەت تاماققا چاقىرىلغان ۋاقتىڭلاردىلا كىرىڭلار، (باشقا ۋاقىتتا رۇخسەت بىلەن كىرگەندە) تاماقنىڭ پىشىشىنى كۈتۈپ ئولتۇرماڭلار، لېكىن چاقىرىلغاندا كىرىڭلار يۇ، تاماق يەپ بولغاندىن كېيىن تارقاپ كېتىڭلار، تاماقتىن كېيىن) پاراڭ سېلىشىپ ئولتۇرماڭلار، بۇ (يەنى تاماقتىن كېيىن پاراڭ سېلىشىپ ئولتۇرۇش) پەيغەمبەرنى رەنجىتىدۇ، پەيغەمبەر (بۇنى ئېيتىشقا) سىلەردىن خىجىل بولىدۇ، اﷲ ھەق (نى ئېيتىش) تىن خىجىل بولمايدۇ، سىلەر پەيغەمبەرنىڭ ئاياللىرىدىن بىر نەرسە سوراپ (ئالماقچى) بولساڭلار، پەردە ئارقىسىدىن سوراپ ئېلىڭلار، مۇنداق قىلىش سىلەرنىڭ دىللىرىڭلارنىمۇ، ئۇلارنىڭ دىللىرىنىمۇ ئەڭ پاك تۇتىدۇ، سىلەرنىڭ رەسۇلۇللاھنى رەنجىتىپ قويۇشۇڭلار دۇرۇس ئەمەس، رەسۇلۇللاھتىن كېيىن ئۇنىڭ ئاياللىرىنى ئېلىشىڭلارمۇ مەڭگۈ دۇرۇس ئەمەس، بۇنداق قىلىش (يەنى رەسۇلۇللاھنى رەنجىتىش ۋە ئۇنىڭ ئاياللىرىنى ئېلىش) ھەقىقەتەن اﷲ نىڭ دەرگاھىدا چوڭ گۇناھتۇر'),
(3587, 33, 54, 'بىرەر نەرسىنى ئاشكارىلىساڭلارمۇ، يا (دىلىڭلاردا) يوشۇرساڭلارمۇ (اﷲ ھامان بىلىدۇ)، چۈنكى اﷲ ھەقىقەتەن ھەممە نەرسىنى بىلگۈچىدۇر'),
(3588, 33, 55, 'ئۇلارغا (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ ئاياللىرىغا) ئاتىلىرى بىلەن، ئوغۇللىرى بىلەن، قېرىنداشلىرى بىلەن، قېرىنداشلىرىنىڭ ئوغۇللىرى بىلەن، ھەمشىرىلىرىنىڭ ئوغۇللىرى بىلەن، مۆمىن ئاياللار بىلەن، قۇل، چۆرىلىرى بىلەن كۆرۈشۈش ھېچ گۇناھ ئەمەس، (ئى پەيغەمبەرنىڭ ئاياللىرى!) اﷲ تىن قورقۇڭلار، اﷲ ھەقىقەتەن ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر'),
(3589, 33, 56, 'اﷲ ھەقىقەتەن پەيغەمبەرگە رەھمەت يوللايدۇ (يەنى پەيغەمبەر ئەلەيھىسسالامغا رەھمەت قىلىدۇ، شەنىنى ئۇلۇغ قىلىدۇ، دەرىجىسىنى ئۈستۈن قىلىدۇ)، پەرىشتىلەرمۇ ھەقىقەتەن (پەيغەمبەرگە) مەغپىرەت تەلەپ قىلىدۇ، ئى مۆمىنلەر! سىلەر پەيغەمبەرگە دۇرۇد ئېيتىڭلار ۋە ئامانلىق تىلەڭلار (چۈنكى رەسۇلۇللاھنىڭ سىلەرنىڭ ئۈستۈڭلاردىكى ھەققى چوڭدۇر، ئۇ سىلەرنى گۇمراھلىقتىن ھىدايەتكە، زۇلمەتتىن يورۇقلۇققا چىقارغۇچىدۇر)'),
(3590, 33, 57, 'اﷲ نى ۋە ئۇنىڭ پەيغەمبىرىنى رەنجىتىدىغانلار (يەنى اﷲ ۋە رەسۇلۇللاھ يامان كۆرىدىغان ئىشلارنى قىلىدىغانلار) نى اﷲ دۇنيا ۋە ئاخىرەتتە رەھمىتىدىن يىراق قىلىدۇ ۋە ئۇلارغا خار قىلغۇچى ئازابنى تەييار لايدۇ'),
(3591, 33, 58, 'مۆمىنلەر ۋە مۆمىنەلەرگە قىلمىغان ئىشلارنى (چاپلاپ) ئۇلارنى رەنجىتىدىغانلار (شۇ) بۇھتاننى ۋە روشەن گۇناھنى ئۈستىگە ئارتىۋالغان بولىدۇ'),
(3592, 33, 59, 'ئى پەيغەمبەر! ئاياللىرىڭغا، قىزلىرىڭغا ۋە مۆمىنلەرنىڭ ئاياللىرىغا ئېيتقىنكى، پۈركەنجى بىلەن بەدىنىنى ئورىۋالسۇن، بۇنداق قىلغاندا ئۇلارنىڭ (ھۈر ئاياللار ئىكەنلىكى) ئەڭ ئوڭاي تونۇلىدۇ - دە، باشقىلار ئۇلارغا چېقىلمايدۇ. اﷲ (بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(3593, 33, 60, 'ئەگەر مۇناپىقلار، (نىفاقتىن) دىللىرىدا كېسەل بارلار، مەدىنىدە يالغان خەۋەر تارقاتقۇچىلار (قىلمىشلىرىدىن) يانمىسا، (ئى مۇھەممەد) ئەلۋەتتە بىز سېنى ئۇلارغا مۇسەللەت قىلىمىز، ئاندىن ئۇلار مەدىنىدە لەنەتكە ئۇچرىغان ھالدا سەن بىلەن ئازغىنا ۋاقىت تۇرالايدۇ (يەنى ئۇلار مەدىنىدىن سۈرگۈن قىلىنىپ ھەيدەپ چىقىرىلىدۇ، چىقىپ كېتىشكە تەييارلىق قىلىۋېلىش ئۈچۈن مەدىنىدە پەقەت ئازغىنا ۋاقىت تۇرالايدۇ). ئۇلار قەيەردە بايقالسا، شۇ يەردە تۇتۇلىدۇ ۋە ئۆلتۈرۈلىدۇ'),
(3594, 33, 61, 'ئەگەر مۇناپىقلار، (نىفاقتىن) دىللىرىدا كېسەل بارلار، مەدىنىدە يالغان خەۋەر تارقاتقۇچىلار (قىلمىشلىرىدىن) يانمىسا، (ئى مۇھەممەد) ئەلۋەتتە بىز سېنى ئۇلارغا مۇسەللەت قىلىمىز، ئاندىن ئۇلار مەدىنىدە لەنەتكە ئۇچرىغان ھالدا سەن بىلەن ئازغىنا ۋاقىت تۇرالايدۇ (يەنى ئۇلار مەدىنىدىن سۈرگۈن قىلىنىپ ھەيدەپ چىقىرىلىدۇ، چىقىپ كېتىشكە تەييارلىق قىلىۋېلىش ئۈچۈن مەدىنىدە پەقەت ئازغىنا ۋاقىت تۇرالايدۇ). ئۇلار قەيەردە بايقالسا، شۇ يەردە تۇتۇلىدۇ ۋە ئۆلتۈرۈلىدۇ'),
(3595, 33, 62, 'بۇ (يەنى مۇناپىقلارغا اﷲ نىڭ تۇتقان يولى) اﷲ نىڭ ئۆتكەنكى ئۈممەتلەرگە تۇتقان يولىدۇر، اﷲ نىڭ تۇتقان يولىدا ھېچقانداق ئۆزگىرىش تاپالمايسەن'),
(3596, 33, 63, 'كىشىلەر سەندىن قىيامەتنىڭ ۋاقتىنى سورايدۇ. «ئۇنى (مەن بىلمەيمەن) پەقەت اﷲ بىلىدۇ» دېگىن. سەن نېمە بىلىسەن، قىيامەت يېقىن بولۇشى مۇمكىن'),
(3597, 33, 64, 'اﷲ ھەقىقەتەن كاپىرلارنى رەھمىتىدىن يىراق قىلدى ۋە ئۇلارغا دوزاخنى تەييارلىدى'),
(3598, 33, 65, 'ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇلار ھېچقانداق (ئۇلارنى قوغدايدىغان) دوست ۋە (ئۇلاردىن ئازابنى دەپئى قىلىدىغان) ياردەمچى تاپالمايدۇ'),
(3599, 33, 66, 'دوزاختا ئۇلارنىڭ يۈزلىرى تۈرۈلۈپ كېتىدىغان كۈندە، ئۇلار: «كاشكى بىز اﷲ قا ئىتائەت قىلغان بولساقچۇ! پەيغەمبەرگە ئىتائەت قىلغان بولساقچۇ!» دەيدۇ'),
(3600, 33, 67, 'ئۇلار: «پەرۋەردىگارىمىز! بىز ھەقىقەتەن باشلىقلىرىمىزغا، كاتتىلىرىمىزغا ئىتائەت قىلدۇق، ئۇلار بىزنى توغرا يولدىن ئازدۇردى، پەرۋەردىگارىمىز! ئۇلارغا ئازابنى ئىككى ھەسسە بەرگىن ۋە ئۇلارغا قاتتىق لەنەت قىلغىن» دەيدۇ'),
(3601, 33, 68, 'ئۇلار: «پەرۋەردىگارىمىز! بىز ھەقىقەتەن باشلىقلىرىمىزغا، كاتتىلىرىمىزغا ئىتائەت قىلدۇق، ئۇلار بىزنى توغرا يولدىن ئازدۇردى، پەرۋەردىگارىمىز! ئۇلارغا ئازابنى ئىككى ھەسسە بەرگىن ۋە ئۇلارغا قاتتىق لەنەت قىلغىن» دەيدۇ'),
(3602, 33, 69, 'ئى مۆمىنلەر! سىلەر مۇساغا ئازار قىلغان كىشىلەر (يەنى بەنى ئىسرائىل) دەك بولماڭلار، اﷲ مۇسانى ئۇلارنىڭ ئېيتقانلىرىدىن (يەنى تۆھمەتلىرىدىن) ئاقلىدى، مۇسا اﷲ نىڭ دەرگاھىدا ئابرۇيلۇق ئىدى'),
(3603, 33, 70, 'ئى مۆمىنلەر! اﷲ تىن قورقۇڭلار، توغرا سۆزنى قىلىڭلار'),
(3604, 33, 71, 'اﷲ سىلەرنىڭ ئەمەللىرىڭلارنى تۈزەيدۇ (يەنى سىلەرنى ياخشى ئەمەللەرگە مۇۋەپپەق قىلىدۇ)، گۇناھلىرىڭلارنى مەغپىرەت قىلىدۇ، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلسا زور مۇۋەپپەقىيەت قازانغان بولىدۇ'),
(3605, 33, 72, 'شۈبھىسىزكى، بىز ئامانەتنى (يەنى پەرزلەرنى ۋە شەرىئەت تەكلىپلىرىنى) ئاسمانلارغا، زېمىنغا ۋە تاغلارغا تەڭلىدۇق، ئۇلار ئۇنى ئۈستىگە ئالمىدى، ئۇنىڭ (ئېغىرلىقى) دىن قورقتى، ئۇنى ئىنسان ئۈستىگە ئالدى، ئىنسان ھەقىقەتەن (ئۆزىگە) زۇلۇم قىلغۇچىدۇر، ھەقىقەتەن ناداندۇر'),
(3606, 33, 73, '(مەزكۇر ئامانەتكە خىيانەت قىلغانلىقلىرى ئۈچۈن) مۇناپىق ئەرلەرنى، مۇناپىق ئاياللارنى، مۇشرىك ئەرلەرنى، مۇشرىك ئاياللارنى اﷲ ئازابلايدۇ، (مەزكۇر ئامانەتكە رىئايە قىلغانلىقلىرى ئۈچۈن) مۆمىن ئەرلەر ۋە مۆمىن ئاياللارنى اﷲ ئەپۇ قىلىدۇ، (اﷲ مۆمىنلەرگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'جىمى ھەمدۇسانا اﷲ قا خاستۇر، ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ مۈلكىدۇر، ئاخىرەتتىكى ھەمدۇسانامۇ اﷲ قا خاستۇر، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىدىن خەۋەرداردۇر'),
(3608, 34, 2, 'اﷲ زېمىننىڭ قوينىغا كىرىپ كېتىدىغان (يەنى يامغۇر، كان ۋە ئۆلۈكلەرگە ئوخشاش) نەرسىلەرنى، زېمىندىن چىقىدىغان (ئۆسۈملۈك، بۇلاق ۋە قۇدۇقلارنىڭ سۇلىرى قاتارلىق) نەرسىلەرنى، ئاسماندىن چۈشىدىغان (پەرىشتە، يامغۇر، ئەجەل، رىزىق قاتارلىق) نەرسىلەرنى، ئاسمانغا ئۆرلەيدىغان (ياخشى ئەمەللەر، مەقبۇل دۇئالار قاتارلىق) نەرسىلەرنى بىلىپ تۇرىدۇ، اﷲ بەندىلىرىگە ناھايىتى مېھرىباندۇر، (تەۋبە قىلغۇچىلارنى) ناھايىتى مەغپىرەت قىلغۇچىدۇر'),
(3609, 34, 3, 'كاپىرلار: «بىزگە قىيامەت كەلمەيدۇ» دەيدۇ، ئېيتقىنكى، «ئۇنداق ئەمەس، پەرۋەردىگارىم بىلەن قەسەمكى، قىيامەت سىلەرگە چوقۇم كېلىدۇ، اﷲ غەيىبنى بىلگۈچىدۇر، ئاسمانلاردىكى ۋە زېمىندىكى زەررە چاغلىق نەرسە اﷲ تىن يىراق ئەمەستۇر، ئۇنىڭ (يەنى زەررە) دىن كىچىكرەك نەرسە بولسۇن، چوڭراق نەرسە بولسۇن، لەۋھۇلمەھپۇزدا خاتىرىلەنمىگىنى يوق»'),
(3610, 34, 4, '(ئۇلارنىڭ لەۋھۇلمەھپۇزدا خاتىرىلىنىشى) ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى مۇكاپاتلاش ئۈچۈندۇر، ئەنە شۇلار مەغپىرەتكە ۋە ئېسىل رىزىققا ئېرىشىدۇ'),
(3611, 34, 5, 'بىزنىڭ ئايەتلىرىمىزگە كۈچىنىڭ بېرىچە قارشى تۇرغانلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(3612, 34, 6, 'ئىلىم بېرىلگەنلەر (يەنى ساھابىلەر ۋە كېيىنكى ئەمەل قىلغۇچى ئۆلىمالار) ساڭا پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان قۇرئاننىڭ شۈبھىسىز ھەق ئىكەنلىكىنى، (ئىنسانلارنى) غالىب، ھەمدۇساناغا لايىق اﷲ نىڭ يولىغا باشلايدىغانلىقىنى بىلىدۇ'),
(3613, 34, 7, 'كاپىرلار (بىر - بىرىگە) ئېيتىدۇ: «سىلەرگە سىلەر (قەبرىلەردە چىرىپ) تىتما - تالاڭ بولۇپ كەتكەندىن كېيىن چوقۇم يېڭىدىن يارىتىلىسىلەر دەپ خەۋەر بېرىدىغان بىر ئادەمنى كۆرسىتىپ قويايلىمۇ؟'),
(3614, 34, 8, 'ئۇ اﷲ قا يالغاننى توقۇدىمۇ؟ يا ئۇ ئېلىشىپ قالدىمۇ؟» ھەرگىز ئۇنداق ئەمەس، ئاخىرەتكە ئىمان كەلتۈرمەيدىغانلار ئازابتا ۋە چوڭقۇر گۇمراھلىقتىدۇر'),
(3615, 34, 9, 'ئۇلار ئالدىدىكى ۋە ئارقىسىدىكى (ئۇلارنى ئوراپ تۇرغان) ئاسمان بىلەن زېمىنغا قارىمامدۇ؟ ئەگەر خالىساق ئۇلارنى يەرگە يۇتقۇزىمىز، ياكى ئاسماننى ئۇلارنىڭ ئۈستىگە پارچە - پارچە قىلىپ چۈشۈرىمىز. بۇنىڭدا اﷲ قا قايتقۇچى ھەر بەندە ئۈچۈن ئەلۋەتتە (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) دەلىل بار'),
(3616, 34, 10, 'بىز داۋۇدقا دەرگاھىمىزدىن ھەقىقەتەن پەزل (يەنى پەيغەمبەرلىك، زەبۇر، تاغلارنىڭ ۋە قۇشلارنىڭ بويسۇنۇشى، تۆمۈرنىڭ يۇمشاق بولۇپ بېرىشى، ساۋۇت ياساشنى بىلدۈرۈشلەر) ئاتا قىلدۇق. «ئى تاغلار! ئى قۇشلار! داۋۇت بىلەن بىرلىكتە تەسبىھ ئېيتىڭلار» (دېدۇق). تۆمۈرنى داۋۇدقا يۇمشاق قىلىپ بەردۇق'),
(3617, 34, 11, '(بىز ئۇنىڭغا ئېيتتۇق) «مۇكەممەل ساۋۇتلارنى ياسىغىن، ساۋۇتلارنى ياساشتا (ھالقىلىرىنى بىر - بىرىگە) تەكشى قىلغىن، (ئى داۋۇد ئائىلىسىدىكىلەر!) ياخشى ئىش قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ ئەمەللىرىڭلارنى كۆرۈپ تۇرغۇچىمەن»'),
(3618, 34, 12, 'سۇلەيمانغا شامالنى (بويسۇندۇرۇپ بەردۇق)، شامال چۈشتىن ئىلگىرى بىر ئايلىق مۇساپىنى، چۈشتىن كېيىن بىر ئايلىق مۇساپىنى باساتتى، ئۇنىڭغا مىس (چىقىدىغان) بۇلاقنى ئاققۇزۇپ بەردۇق، بەزى جىنلار پەرۋەردىگارىنىڭ ئەمرى بويىچە سۇلەيماننىڭ ئالدىدا ئىشلەيتتى، ئۇلاردىن كىمكى بىزنىڭ ئەمرىمىزگە خىلاپلىق قىلىدىكەن، ئۇنىڭغا بىز دوزاخ ئازابىنى تېتىتىمىز'),
(3619, 34, 13, 'ئۇلار سۇلەيمانغا ئۇ خالىغان كاتتا سارايلارنى، (مىستىن - ئەينەكتىن ئاجايىپ) ھەيكەللەرنى، كۆلدەك چوڭ لېگەنلەرنى، (چوڭلۇقتىن) مىدىرلىمايدىغان قازانلارنى ياسايتتى، (ئېيتتۇقكى) «ئى داۋۇد ئائىلىسىدىكىلەر! (اﷲ نىڭ بۇ چوڭ نېمەتلىرىگە) شۈكۈر قىلىڭلار»، مېنىڭ بەندىلىرىمدىن (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلغۇچى بەك ئاز'),
(3620, 34, 14, 'سۇلەيماننىڭ ۋاپاتىنى ھۆكۈم قىلغىنىمىزدا (يەنى ئۇنى ۋاپات قىلدۇرغىنىمىزدا) سۇلەيماننىڭ ئۆلگەنلىكىدىن جىنلارنى پەقەت ئۇنىڭ ھاسىسىنى يېگەن قۇرۇتلار خەۋەردار قىلدى. سۇلەيمان (ھاسىسىنى قۇرۇت يەپ) يىقىلغان چاغدا جىنلارغا ئېنىق بولدىكى، ئەگەر ئۇلار غەيبنى بىلىدىغان بولسا ئىدى، خار قىلغۇچى ئازابتا (يەنى شۇنچە ئۇزاق ۋاقىت ئېغىر ئەمگەكتە) قالمىغان بولاتتى'),
(3621, 34, 15, 'شۈبھىسىزكى، سەبەئ قەۋمىگە ئۇلارنىڭ تۇرىدىغان جايىدا (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان بۈيۈك) دەلىل بار ئىدى، ئۇنىڭ ئوڭ تەرىپىدە بىر تۈركۈم باغلار، سول تەرىپىدە بىر تۈركۈم باغلار بار ئىدى. (ئۇلارغا ئېيتتۇقكى) «پەرۋەردىگارىڭلار بەرگەن رىزىقتىن يەڭلار ۋە ئۇنىڭغا شۈكۈر قىلىڭلار، (سىلەرنىڭ جايىڭلار) بەلەن جايدۇر، (سىلەرگە رىزىق بەرگەن پەرۋەردىگارىڭلار شۈكۈر قىلغۇچىنى) مەغپىرەت قىلغۇچى پەرۋەردىگاردۇر'),
(3622, 34, 16, 'ئۇلار (شۈكۈردىن) باش تارتتى، شۇنىڭ بىلەن ئۇلارغا قاتتىق سەلنى ئەۋەتتۇق. ئۇلارنىڭ باغلىرىنى ئاچچىق مېۋىلىك، يۇلغۇنلۇق، ئاز - تولا سىدرىلىق (يەنى ياۋا چىلانلىق) باغلارغا ئايلاندۇردۇق'),
(3623, 34, 17, 'ئۇلار كۇفرىلىق قىلغانلىقلىرى ئۈچۈن مۇشۇنداق جازالىدۇق، بىز پەقەت كۇفرىلىق قىلغۇچىلارنىلا جازالايمىز'),
(3624, 34, 18, 'ئۇلار بىلەن بىز بەرىكەتلىك قىلغان قىشلاقلار ئارىسىدا، كۆرۈنۈپ تۇرىدىغان (تۇتىشىپ كەتكەن) نۇرغۇن قىشلاقلارنى بەرپا قىلدۇق، ئۇلار ئارسىدىكى مېڭىشنى ئۆلچەملىك قىلدۇق (يەنى سەپەردە يېمەك - ئىچمەك ئېلىۋالمىسىمۇ بولىدىغان، ئەتىگەن چىققان يولۇچى چۈشتە بىر ئۆتەڭگە يېتىپ بارسا، كەچتە بىر ئۆتەڭگە يېتىپ بارىدىغان قىلدۇق)، (ئۇلارغا ئېيتتۇقكى) «ئۇلارنىڭ ئارىسىدا كېچىلەردە ۋە كۈندۈزلەردە تىنچ - ئامان مېڭىڭلار!»'),
(3625, 34, 19, 'ئۇلار: «پەرۋەردىگارىمىز! سەپەرلىرىمىز ئارىسىنى (يەنى ئۆتەڭلار ئارىسىدىكى مۇساپىنى) يىراق قىلغىن» دېدى. ئۇلار ئۆزلىرىگە زۇلۇم قىلدى. ئۇلارنى بىز (كېيىنكى كىشىلەرگە) چۆچەك قىلىپ قالدۇردۇق. ئۇلارنى تىرىپىسىن قىلىۋەتتۇق، بۇنىڭغا ھەر بىر (بالاغا) سەۋر قىلغۇچى، (نېمەتكە) شۈكۈر قىلغۇچى ئۈچۈن نۇرغۇن ئىبرەتلەر بار'),
(3626, 34, 20, 'ئىبلىس ئۇلارنى (ئازدۇرۇشتىن ئىبارەت) گۇمانىنى ھەقىقەتەن ئىشقا ئاشۇردى. چۈنكى بىر تۈركۈم مۆمىنلەردىن باشقا ئۇلارنىڭ ھەممىسى ئىبلىسقا ئەگەشتى'),
(3627, 34, 21, 'ئىبلىسنىڭ ئۇلار (يەنى ئەگەشكۈچىلەر) ئۈستىدىن (ۋەسۋەسە قىلىش بىلەن) ھۆكۈمرانلىق قىلىش پەقەت بىزنىڭ ئاخىرەتكە ئىمان ئېيتقۇچىلار بىلەن ئۇنىڭغا شەك كەلتۈرگۈچىلەرنى بىلىۋېلىشىمىز ئۈچۈندۇر، پەرۋەردىگارىڭ ھەممە نەرسىنى كۆزەتكۈچىدۇر'),
(3628, 34, 22, 'ئېيتقىنكى، «سىلەر اﷲ نى قويۇپ مەبۇد دەپ قارىغىنىڭلارنى چاقىرىڭلار، ئۇلار ئاسمانلاردا ۋە زېمىندا زەررە چاغلىق نەرسىگە ئىگە بولالمايدۇ، ئۇلارنىڭ ئاسمان - زېمىننىڭ (يارىتىلىشى ۋە باشقۇرۇلۇشىدا اﷲ قا) ھېچ شېرىكچىلىگى يوق، ئۇلارنىڭ ئىچىدە اﷲ قا (ئاسمان - زېمىننى ئىدارە قىلىشتا ياردەملىشىدىغان) ھېچ ياردەمچىمۇ يوق»'),
(3629, 34, 23, 'اﷲ ئىزنى بەرگەن ئادەمدىن باشقىغا اﷲ نىڭ دەرگاھىدا شاپائەت پايدا بەرمەيدۇ. شاپائەت قىلغۇچىلارنىڭ دىللىرىدىن قورقۇنچ كۆتۈرۈلگەندە، ئۇلار ئاندىن (بەزىسى بەزىسىگە): «پەرۋەردىگارىڭلار (شاپائەت توغرولۇق) نېمە دېدى؟» دەيدۇ. ئۇلار (جاۋابەن): «ھەقنى (دېدى) (يەنى مۆمىنلەرگە شاپائەت قىلىشقا رۇخسەت قىلدى)» دەيدۇ. اﷲ يۈكسەكتۇر، كاتتىدۇر'),
(3630, 34, 24, 'ئېيتقىنكى، «سىلەرگە ئاسمانلاردىن ۋە زېمىندىن كىم رىزىق بېرىپ تۇرىدۇ؟» ئېيتقىنكى، «اﷲ (رىزىق بېرىپ تۇرىدۇ)، شۈبھىسىزكى، بىز ياكى سىلەر چوقۇم ھىدايەتتىدۇرمىز، يا روشەن گۇمراھلىقتىدۇرمىز»'),
(3631, 34, 25, 'ئېيتقىنكى، «بىزنىڭ قىلغان گۇناھىمىز ئۈچۈن سىلەر سوراققا تارتىلمايسىلەر، سىلەرنىڭ قىلمىشىڭلار ئۈچۈن بىز سوراققا تارتىلمايمىز»'),
(3632, 34, 26, 'ئېيتقىنكى، «پەرۋەردىگارىمىز بىزنى (قىيامەت كۈنى) يىغىدۇ. ئاندىن بىزنىڭ ئارىمىزدا ھەقلىق بىلەن ھۆكۈم چىقىرىدۇ، اﷲ ئادىل ھۆكۈم قىلغۇچىدۇر، (خالايىقنىڭ ئەھۋالىنى) بىلگۈچىدۇر»'),
(3633, 34, 27, 'ئېيتقىنكى، «ماڭا ئېيتىپ بېقىڭلارچۇ، سىلەرنىڭ اﷲ نىڭ شېرىكى دەپ ئاتىۋالغىنىڭلار ھەرگىزمۇ ئۇنداق ئەمەس، بەلكى ئۇ - اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر»'),
(3634, 34, 28, 'سېنى بىز پەقەت پۈتۈن ئىنسانلار ئۈچۈن (مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچى ۋە (كاپىرلارنى ئازاب بىلەن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق. ۋە لېكىن ئىنسانلارنىڭ تولىسى (بۇنى) بىلمەيدۇ'),
(3635, 34, 29, 'ئۇلار: «ئەگەر سىلەر راستچىل بولساڭلار، بۇ ۋەدە (يەنى سىلەر بىزنى قورقۇتۇۋاتقان ئازاب) قاچان ئىشقا ئاشىدۇ؟» دەيدۇ'),
(3636, 34, 30, 'ئېيتقىنكى، «سىلەرگە (ئازاب ئۈچۈن) ۋەدە قىلىنغان بىر كۈن باركى، ئۇنىڭدىن بىردەممۇ ئىلگىرى - كېيىن بولمايسىلەر»'),
(3637, 34, 31, 'كاپىرلار: «بۇ قۇرئانغا ۋە ئۇنىڭدىن ئىلگىرىكى كىتابلارغا ھەرگىز ئىشەنمەيمىز» دەيدۇ. ئەگەر زالىملارنى پەرۋەردىگارىڭنىڭ دەرگاھىدا توختىتىپ قويۇلغان چاغدا بەزىسى بەزىسى بىلەن جېدەللىشىۋاتقاندا كۆرسەڭ ئىدىڭ (ئەلۋەتتە قورقۇنچلۇق ھالنى كۆرەتتىڭ)، بوزەك قىلىنغانلار چوڭچىلىق قىلغانلارغا: «ئەگەر سىلەر بولمىساڭلار بىز چوقۇم ئىمان ئېيتاتتۇق» دەيدۇ'),
(3638, 34, 32, 'چوڭچىلىق قىلغانلار بوزەك قىلىنغانلارغا: «سىلەرگە ھىدايەت كەلگەندىن كېيىن بىز سىلەرنى ئۇنىڭدىن توستۇقمۇ؟ ھەرگىز توسمىدۇق، سىلەر ئۆزۈڭلار گۇناھ قىلغۇچى بولدۇڭلار» دەيدۇ'),
(3639, 34, 33, 'بوزەك قىلىنغانلار چوڭچىلىق قىلغانلارغا: «ھەرگىز ئۇنداق ئەمەس، (سىلەرنىڭ بىزگە) كېچە - كۈندۈز قىلغان ھىيلە - مىكرىڭلار (بىزنى ئىماندىن توستى)، ئۆز ۋاقتىدا سىلەر بىزنى اﷲ نى ئىنكار قىلىشقا ۋە ئۇنىڭغا شېرىك كەلتۈرۈشكە بۇيرۇيتتۇڭلار» دەيدۇ. ئازابنى كۆرگەن چاغدا، ئۇلار (ھەر ئىككى گۇرۇھ ئىمان ئېيتمىغانلىقلىرىغا) ئىچىدە پۇشايمان قىلىدۇ، كاپىرلارنىڭ بويۇنلىرىغا تاقاقلارنى سالىمىز، ئۇلارغا پەقەت قىلمىشلىرىنىڭ جازاسى بېرىلىدۇ'),
(3640, 34, 34, 'قايسى شەھەرگە بولمىسۇن، بىرەر ئاگاھلاندۇرغۇچى ئەۋەتسەكلا ئۇنىڭ دۆلەتمەن ئادەملىرى: «شۈبھىسىزكى، بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ئىنكار قىلىمىز» دېدى'),
(3641, 34, 35, 'ئۇلار يەنە: «بىزنىڭ ماللىرىمىز ۋە بالىلىرىمىز ئەڭ كۆپ، بىز ئازابلانمايمىز» دېدى'),
(3642, 34, 36, 'ئېيتقىنكى، «ھەقىقەتەن، پەرۋەردىگارىم كىمنىڭ رىزقىنى كەڭ قىلىشنى خالىسا ئۇنى كەڭ قىلىدۇ، (كىمنىڭ رىزقىنى تار قىلىشنى خالىسا ئۇنى) تار قىلىدۇ، لېكىن كىشىلەرنىڭ تولىسى (بۇنى) ئۇقمايدۇ»'),
(3643, 34, 37, 'ماللىرىڭلار ۋە بالىلىرىڭلار سىلەرنى بىزگە يېقىنلاشتۇرالمايدۇ، پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا قىلغان ئەمەللىرى ئۈچۈن ھەسسىلەپ ساۋاب بېرىلىدۇ، ئۇلار (جەننەتنىڭ) ئېسىل ئۆيلىرىدە (ھەر قانداق كۆڭۈلسىزلىكتىن) ئەمىن بولغان ھالدا تۇرىدۇ'),
(3644, 34, 38, 'بىزنىڭ ئايەتلىرىمىزگە كۈچىنىڭ بېرىچە قارشى تۇرغانلار ئازابقا دۇچار قىلىنغۇچىلاردۇر'),
(3645, 34, 39, 'ئېيتقىنكى، «ھەقىقەتەن، پەرۋەردىگارىم قايسى بەندىسىنىڭ رىزقىنى كەڭ قىلىشنى خالىسا ئۇنى كەڭ قىلىدۇ، (قايسى بەندىسىنىڭ رىزقىنى تار قىلىشنى خالىسا ئۇنى) تار قىلىدۇ، (اﷲ نىڭ يولىدا) بەرگەن نەرسەڭلارنىڭ ئورنىنى اﷲ تولدۇرۇپ بېرىدۇ، ئۇ رىزىق بەرگۈچىلەرنىڭ ياخشىسىدۇر»'),
(3646, 34, 40, 'ئۇ كۈندە اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ ھەممىسىنى (ھېساب ئېلىش ئۈچۈن) يىغىدۇ، ئاندىن پەرىشتىلەرگە: «بۇ كىشىلەر سىلەرگە چوقۇنغانمىدى؟» دەيدۇ'),
(3647, 34, 41, 'ئۇلار ئېيتىدۇ: «سەن پاكتۇرسەن، بىزنىڭ دوستىمىز ئۇلار ئەمەس، سەن. (ئۇلار بىزگە چوقۇنمايتتى) بەلكى جىنغا چوقۇناتتى. ئۇلارنىڭ تولىسى جىنغا ئىمان كەلتۈرەتتى»'),
(3648, 34, 42, 'بۈگۈن سىلەر بىر - بىرىڭلارغا پايدا - زىيان يەتكۈزەلمەيسىلەر، بىز (اﷲ تىن غەيرىيگە چوقۇنغۇچى) زالىملارغا: «سىلەر چىنپۈتمىگەن دوزاخ ئازابىنى تېتىڭلار» دەيمىز'),
(3649, 34, 43, 'ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز ئوقۇپ بېرىلگەندە، ئۇلار: «بۇ پەقەت سىلەرنى ئاتا - بوۋاڭلار چوقۇنغان بۇتلارغا چوقۇنۇشتىن توسماقچى بولغان ئادەمدۇر» دەيدۇ ھەمدە ئۇلار: «بۇ پەقەت توقۇلغان يالغان سۆزدۇر» دەيدۇ. كاپىرلارغا ھەقىقەت يېتىپ كەلگەندە، ئۇلار: «بۇ پەقەت ئوپئوچۇق سېھىردۇر» دېدى'),
(3650, 34, 44, 'ئۇلارغا ئوقۇيدىغان ھېچقانداق كىتابلارنى ئاتا قىلغىنىمىز يوق. سەندىن ئىلگىرى ئۇلارغا ھېچقانداق پەيغەمبەرمۇ ئەۋەتكىنىمىز يوق'),
(3651, 34, 45, 'ئۇلاردىن ئىلگىرى ئۆتكەنلەر (ھەقنى) ئىنكار قىلغان، ئۇلار (يەنى مەككە كۇففارلىرى) ئۇلار (يەنى ئىلگىرىكىلەر) ئېرىشكەن كۈچ - قۇۋۋەتنىڭ ئوندىن بىرىگىمۇ ئېرىشمىگەن، ئۇلار مېنىڭ پەيغەمبەرلىرىمنى ئىنكار قىلدى. ئۇلارغا مېنىڭ جازايىم قانداق بولدى؟'),
(3652, 34, 46, 'ئېيتقىنكى، «مەن سىلەرگە بىر ئىشنى تەۋسىيە قىلىمەن: سىلەر خۇدالىق ئۈچۈن ئىككىدىن، يا بىردىن تۇرۇڭلار (يەنى اﷲ نىڭ رازىلىقى ئۈچۈن يىغىلىپ ياكى يەككە ھالدا ھەقنى تىلەڭلار)، ئاندىن پىكىر يۈرگۈزۈڭلار». ھەمراھىڭلاردا (يەنى مۇھەممەدتە) ھېچ مەجنۇنلۇق يوق، پەقەت ئۇ قاتتىق ئازاب نازىل بولۇشتىن ئىلگىرى سىلەرگە ئەۋەتىلگەن بىر ئاگاھلاندۇرغۇچىدۇر'),
(3653, 34, 47, 'ئېيتقىنكى، «سىلەردىن ھېچقانداق ھەق تەلەپ قىلغىنىم يوق، ئۇ سىلەرگە بولسۇن؛ ئەجرىمنى پەقەت اﷲ تىن تىلەيمەن. اﷲ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر»'),
(3654, 34, 48, 'ئېيتقىنكى، «پەرۋەردىگارىم، ھەقىقەتەن ھەق ئارقىلىق (باتىلنى) ئاتىدۇ (يەنى پاكىتنى روشەن بايان قىلىدۇ)، اﷲ غەيبنى تامامەن بىلگۈچىدۇر»'),
(3655, 34, 49, 'ئېيتقىنكى، «ھەق (يەنى ئىسلام) كەلدى (باتىل يوقالدى)، باتىل (بىرەر مەخلۇقنى) پەيدا قىلالمايدۇ، (يوقالغان بىرەر مەخلۇقنى) ئەسلىگە كەلتۈرەلمەيدۇ»'),
(3656, 34, 50, 'ئېيتقىنكى، «ئەگەر ئازسام ئازغانلىقىمنىڭ زىيىنى ئۆزۈمگىدۇر، ئەگەر ھىدايەت تاپسام (بۇ) پەرۋەردىگارىمنىڭ ماڭا ۋەھيى قىلغان قۇرئانىنىڭ سەۋەبىدىندۇر، اﷲ ھەقىقەتەن ئاڭلاپ تۇرغۇچىدۇر، يېقىندۇر»'),
(3657, 34, 51, 'ئەگەر سەن ئەينى ۋاقىتتا ئۇلارنىڭ قورقۇپ قاچىدىغان يەر تاپالمايۋاتقانلىقىنى، دوزاخقا (ئېلىپ بېرىلىش ئۈچۈن مەھشەرگاھقا) يېقىن جايدا تۇتۇلۇۋاتقانلىقىنى كۆرسەڭ (ئەلۋەتتە قورقۇنچلۇق ھالىنى كۆرەتتىڭ)'),
(3658, 34, 52, 'ئۇلار (ئازابنى كۆرگەندە): «ئىمان ئېيتتۇق» دەيدۇ، ئۇلار قانداقمۇ يىراق جايدىن ئىماننى قولغا كەلتۈرەلەيدۇ'),
(3659, 34, 53, 'ئۇلار ئىلگىرى ئۇنى (يەنى پەيغەمبەرنى) ئىنكار قىلغان ئىدى. ئۇلار غەيب (ئىشلىرى) ئۈستىدە يىراق جايدىن قارىسىغا سۆزلەيدۇ'),
(3660, 34, 54, 'ئۇلار بىلەن ئۇلارنىڭ ئارزۇ قىلغان نەرسىلىرى (يەنى ئىمان ئېيتىش ۋە جەننەتكە كىرىش) ئارىسىدا توسالغۇ پەيدا قىلىندى. ئۇلارغا ئىلگىرى ئۇلارغا ئوخشاشلارغا قىلىنغاندەك قىلىندى. چۈنكى ئۇلار ھازىر ئىمان كەلتۈرگەن نەرسىلىرى ئۈستىدە (دۇنيادىكى چاغلىرىدا) چوڭقۇر شەكتە ئىدى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'جىمى ھەمدۇسانا ئاسمانلارنى ۋە زېمىننى ئۆرنەكسىز ياراتقۇچى، پەرىشتىلەرنى ئىككى قاناتلىق، ئۈچ قاناتلىق، تۆت قاناتلىق ئەلچىلەر قىلغۇچى اﷲ قا خاستۇر! اﷲ يارىتىشتا خالىغىنىنى زىيادە قىلىدۇ، اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر'),
(3662, 35, 2, 'اﷲ رەھمىتىدىن كىشىلەرگە ئاچقان نەرسىنى ھەرگىزمۇ توسۇپ قالالىغۇچى بولمايدۇ، اﷲ رەھمىتىدىن توسقان نەرسىنى، اﷲ توسقاندىن كېيىن، ئۇنى ھېچ قويۇپ بەرگۈچى بولمايدۇ، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(3663, 35, 3, 'ئى ئىنسانلار! اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ياد ئېتىڭلار، اﷲ تىن باشقا سىلەرگە ئاسماندىن ۋە زېمىندىن رىزىق بېرىپ تۇرىدىغان ياراتقۇچى بارمۇ؟ اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، (اﷲ نىڭ ئىبادىتىدىن) قانداقمۇ باش تارتىسىلەر؟'),
(3664, 35, 4, '(ئى مۇھەممەد!) ئەگەر ئۇلار سېنى ئىنكار قىلسا (قايغۇرۇپ كەتمە)، ھەقىقەتەن سەندىن ئىلگىرىكى پەيغەمبەرلەرمۇ ئىنكار قىلىنغان، ھەممە ئىش اﷲ نىڭ دەرگاھىغا قايتۇرۇلىدۇ'),
(3665, 35, 5, 'ئى ئىنسانلار! اﷲ نىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، سىلەرنى ھەرگىز دۇنيا تىرىكچىلىكى مەغرۇر قىلمىسۇن، شەيتاننىڭ سىلەرنى اﷲ نىڭ ئەپۇسىنىڭ كەڭلىكى بىلەن مەغرۇر قىلىشىغا يول قويماڭلار'),
(3666, 35, 6, 'شەيتان ھەقىقەتەن سىلەرگە دۈشمەندۇر، ئۇنى دۈشمەن تۇتۇڭلار، شەيتان ئۆزىنىڭ تەۋەلىرىنى ئەھلى دوزاختىن بولۇشقا چاقىرىدۇ'),
(3667, 35, 7, 'كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار مەغپىرەتكە ۋە كاتتا ساۋابقا ئېرىشىدۇ'),
(3668, 35, 8, 'ئۆزىنىڭ يامان ئەمەلى چىرايلىق كۆرسىتىلگەن، شۇنىڭ بىلەن ئۇنى چىرايلىق ھېسابلىغان كىشى (ھىدايەت تاپقان كىشى بىلەن ئوخشاشمۇ؟) اﷲ ھەقىقەتەن خالىغان كىشىنى گۇمراھ قىلىدۇ (خالىغان كىشىنى ھىدايەت قىلىدۇ). شۇڭا سەن ئۇلار ئۈچۈن (يەنى ئىمان ئېيتمىغانلىقلىرى ئۈچۈن) قايغۇرۇپ ئۆزۈڭنى ھالاك قىلىۋالمىغىن، اﷲ ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرىنى بىلىپ تۇرغۇچىدۇر'),
(3669, 35, 9, 'اﷲ شاماللارنى ئەۋەتىپ (ئۇنىڭ بىلەن) بۇلۇتنى قوزغايدۇ، بىز بۇلۇتلارنى ئۆلۈك (يەنى قاغجىراق) بىر يەرگە ھەيدەيمىز (بۇلۇتتىن يامغۇر ياغدۇرىمىز)، شۇ يامغۇر بىلەن ئۆلگەن زېمىننى تىرىلدۈرىمىز. ئۆلۈكلەرنىڭ تىرىلدۈرۈلۈشى ئەنە شۇنداقتۇر'),
(3670, 35, 10, 'كىمكى (دۇنيادا) شەرەپ ئىزدەيدىكەن، شەرەپنىڭ ھەممىسى اﷲ قا مەنسۇپ (ئۇنى اﷲ تىن تىلىسۇن). ياخشى سۆز اﷲ نىڭ دەرگاھىغا ئۆرلەيدۇ، ياخشى ئەمەل ئۇنى كۆتۈرىدۇ، (پەيغەمبەرگە قارشى) ھىيلە - مىكىرلەرنى تۈزىدىغانلار قاتتىق ئازابقا دۇچار بولىدۇ، ئۇلارنىڭ ھىيلە - مىكرى ئىشقا ئاشمايدۇ'),
(3671, 35, 11, 'اﷲ سىلەرنى (يەنى ئەسلىڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن ياراتتى. ئاندىن ئابىمەنىيدىن پەيدا قىلدى. ئاندىن سىلەرنى (ئەر - ئايالدىن ئىبارەت) بىر جۈپ قىلدى، ھەر قانداق ئايالنىڭ ھامىلدار بولۇشى ۋە تۇغۇشى اﷲ نىڭ ئىلمىنىڭ سىرتىدا ئەمەس، ھەرقانداق ئادەمنىڭ ئۆمرىنىڭ ئۇزۇن ياكى قىسقا بولۇشى لەۋھۇلمەھپۇزدا يېزىلغان، بۇ ھەقىقەتەن اﷲ قا ئاسان'),
(3672, 35, 12, 'ئىككى دېڭىز (يەنى دېڭىز بىلەن دەريا) ئوخشاش ئەمەس. بۇنىڭ (يەنى دەريانىڭ) سۈيى تاتلىق، تەملىك بولۇپ (گالدىن) سىلىق ئۆتىدۇ، بۇ (يەنى دېڭىز) ناھايىتى ئاچچىق، تۇزلۇق، ئۇلارنىڭ ھەر ئىككىسىدىن يېڭى گۆشلەرنى (يەنى بېلىقلارنى) يەيسىلەر، (دېڭىزدىن) سىلەر تاقايدىغان زىننەت بۇيۇملىرى (يەنى ئۈنچە - مارجانلار) نى چىقىرىسىلەر، اﷲ نىڭ نېمىتىنى تەلەپ قىلىشڭلار ئۈچۈن، شۈكۈر قىلىشىڭلار ئۈچۈن (اﷲ نىڭ ئەمرى بىلەن يۈك - تاق، يېمەك - ئىچمەكلەر قاچىلانغان) كېمىلەرنىڭ دېڭىزدا دولقۇن يېرىپ كېتىۋاتقانلىقىنى كۆرىسەن'),
(3673, 35, 13, 'اﷲ كېچىنى كۈندۈزگە كىرگۈزىدۇ، كۈندۈزنى كېچىگە كىرگۈزىدۇ (شۇنىڭ بىلەن كېچە - كۈندۈزنىڭ ئۇزۇن - قىسقا بولۇشى جايلار، پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)، اﷲ كۈن بىلەن ئاينى (بەندىلىرىنىڭ مەنپەئىتىگە) بويسۇندۇرۇپ بەردى. ھەر ئىككىلىسى مۇئەييەن مۇددەتكىچە (يەنى قىيامەتكىچە ئۆز ئوقىدا) سەير قىلىدۇ، ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، پادىشاھلىق ئۇنىڭغا خاستۇر، اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلار قىلچىلىك نەرسىگە ئىگە ئەمەس'),
(3674, 35, 14, 'ئەگەر ئۇلارنى چاقىرساڭلار، سىلەرنىڭ چاقىرغىنىڭلارنى ئاڭلىمايدۇ، ئاڭلىغان تەقدىردىمۇ سىلەرگە جاۋاپ قايتۇرالمايدۇ، قىيامەت كۈنى ئۇلار (اﷲ قا ئۇلارنى) شېرىك قىلغانلىقىڭلارنى ئىنكار قىلىدۇ، (مۇشرىكلارنىڭ ۋە ئۇلارنىڭ بۇتلىرىنىڭ قىيامەت كۈنىدىكى ئەھۋالىنى ھېچ ئەھەدى) ساڭا ھەممىدىن خەۋەردار زاتتەك (يەنى اﷲ دەك) ئېيتىپ بېرەلمەيدۇ'),
(3675, 35, 15, 'ئى ئىنسانلار! سىلەر اﷲ قا موھتاجسىلەر، اﷲ (ھەممىدىن) بىھاجەتتۇر، مەدھىيىگە لايىقتۇر'),
(3676, 35, 16, 'ئەگەر اﷲ خالىسا، سىلەرنى ھالاك قىلىپ (ئورنۇڭلارغا) يېڭى بىر خەلقنى پەيدا قىلىدۇ'),
(3677, 35, 17, 'بۇ اﷲ قا قىيىن ئەمەس'),
(3678, 35, 18, 'ھېچبىر گۇناھكار ئادەم بىراۋنىڭ گۇناھىنى ئۈستىگە ئالالمايدۇ، گۇناھى ئېغىر بىر ئادەم باشقا بىراۋنى ئۆز گۇناھىنى ئۈستىگە ئېلىۋېلىشقا چاقىرسا، ئۇ يېقىن تۇغقىنى بولغان تەقدىردىمۇ، ئۇنىڭ گۇناھىدىن ئازراق نەرسىنى ئۈستىگە ئېلىۋالالمايدۇ، سەن پەقەت پەرۋەردىگارىنى كۆرمەي تۇرۇپ ئۇنىڭدىن قورقىدىغانلارنى، نامازنى ئادا قىلىدىغانلارنى ئاگاھلاندۇرىسەن، كىمكى (گۇناھلاردىن) پاكلىنىدىكەن، ئۇ ئۆزى ئۈچۈن پاكلانغان بولىدۇ. ئاخىر قايتىدىغان جاي اﷲ نىڭ دەرگاھىدۇر'),
(3679, 35, 19, 'كور ئادەم بىلەن كۆزى ساق ئادەم (يەنى كاپىر بىلەن مۆمىن) باراۋەر بولمايدۇ'),
(3680, 35, 20, 'زۇلمەت بىلەن نۇر (يەنى باتىل بىلەن ھەق) باراۋەر بولمايدۇ'),
(3681, 35, 21, 'سايە بىلەن ئىسسىق (يەنى جەننەت بىلەن دوزاخ) باراۋەر بولمايدۇ'),
(3682, 35, 22, 'تىرىكلەر بىلەن ئۆلۈكلەر (يەنى مۆمىنلەر بىلەن كاپىرلار) باراۋەر بولمايدۇ، شۈبھىسىزكى، اﷲ (ھەق دەۋەتنى) خالىغان كىشىلەرگە ئاڭلىتىدۇ. سەن قەبرىلەردىكىلەرگە (يەنى كاپىرلارغا) ئاڭلىتالمايسەن'),
(3683, 35, 23, 'سەن پەقەت بىر ئاگاھلاندۇرغۇچىسەن'),
(3684, 35, 24, 'ھەقىقەتەن بىز سېنى ھەق (دىن) بىلەن (مۆمىنلەرگە) خۇش خەۋەر بەرگۈچى، (كاپىرلارغا) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق، قانداقلا بىر ئۈممەت بولمىسۇن، ئۇنىڭغا پەيغەمبەر كەلگەن'),
(3685, 35, 25, 'ئۇلار سېنى ئىنكار قىلسا، ئۇلاردىن بۇرۇن ئۆتكەن ئۈممەتلەرمۇ (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) ئىنكار قىلغان، پەيغەمبەرلىرى ئۇلارغا روشەن مۆجىزىلەرنى، (ئىبراھىم ئەلەيھىسسالامغا نازىل قىلىنغان سەھىپىلەرگە ئوخشاش) سەھىپىلەرنى، (تەۋرات، ئىنجىلدەك) نۇرلۇق كىتابلارنى ئېلىپ كەلدى'),
(3686, 35, 26, 'ئاندىن كاپىرلارنى جازالىدىم، (ئۇلارغا بەرگەن) ئازابىم قانداق ئىكەن؟ (يەنى ئۇلارغا قانداق قاتتىق ئازاب قىلدىم؟)'),
(3687, 35, 27, 'كۆرمەمسەنكى، اﷲ بۇلۇتتىن يامغۇر ياغدۇردى. ئۇنىڭ بىلەن تۈرلۈك، رەڭگارەڭ مېۋىلەرنى چىقاردى، (شۇنىڭدەك اﷲ تاغلارنى ياراتتى) تاغلارنىڭ ئاق، قىزىللىرى، ھەر خىل رەڭدىكى يوللۇقلىرى ۋە قاپقارىلىرىمۇ بار'),
(3688, 35, 28, 'شۇنىڭدەك ئىنسانلارنى، ھايۋانلارنى، چاھارپايلارنىمۇ خىلمۇخىل رەڭلىك قىلىپ ياراتتى، اﷲ نىڭ بەندىلىرى ئىچىدە اﷲ تىن پەقەت ئالىملارلا قورقىدۇ، اﷲ ھەقىقەتەن غالىبتۇر، (بەندىلىرى ئىچىدە تەۋبە قىلغانلارنى) مەغپىرەت قىلغۇچىدۇر'),
(3689, 35, 29, 'شۈبھىسىزكى، اﷲ نىڭ كىتابىنى تىلاۋەت قىلىپ تۇرىدىغانلار، نامازنى ئادا قىلىدىغانلار كاسات بولمايدىغان تىجارەتنى ئۈمىد قىلىدۇ، بىز رىزىق قىلىپ بەرگەن نەرسىلەرنى (اﷲ نىڭ رازىلىقى ئۈچۈن) يوشۇرۇن ۋە ئاشكارا سەرپ قىلىدىغان كىشىلەرگە'),
(3690, 35, 30, 'اﷲ ئەجىرلىرىنى تولۇق بېرىدۇ ۋە مەرھەمىتىدىن ئۇلارغا ئاشۇرۇپ بېرىدۇ، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر'),
(3691, 35, 31, 'ساڭا بىز ۋەھيى قىلغان كىتاب (يەنى قۇرئان) ھەقتۇر. ئىلگىرىكى كىتابلارنىڭ (راستلىقىنى) ئىسپاتلىغۇچىدۇر، اﷲ ھەقىقەتەن بەندىلىرىنى تولۇق بىلگۈچىدۇر، كۆرۈپ تۇرغۇچىدۇر'),
(3692, 35, 32, 'ئاندىن بىز كىتاب (يەنى قۇرئان) نى بەندىلىرىمىزدىن بىز تاللىغان كىشىلەرگە مىراس قىلىپ بەردۇق. ئۇلارنىڭ بەزىسى ئۆزىگە زۇلۇم قىلغۇچىدۇر (يەنى قۇرئاننى تىلاۋەت قىلغان بىلەن ئەمەل قىلمايدۇ)، بەزىسى ئوتتۇراھالدۇر (يەنى كۆپ چاغلاردا قۇرئانغا ئەمەل قىلىپ، قىسمەن چاغلاردا ئەمەل قىلمايدۇ) ۋە بەزىسى اﷲ نىڭ ئىزنى بويىچە ياخشى ئىشلارنى قىلىشقا ئالدىرىغۇچىدۇر. ئەنە شۇ كاتتا مەرھەمەتتۇر'),
(3693, 35, 33, 'ئۇلار ھەمىشە تۇرىدىغان جەننەتلەرگە كىرىدۇ. ئۇلار جەننەتلەردە ئالتۇن بىلەيزۈكلەرنى، مەرۋايىتلارنى تاقايدۇ، ئۇلارنىڭ كىيىمى يىپەكتىن بولىدۇ'),
(3694, 35, 34, 'ئۇلار ئېيتتى: «جىمى ھەمدۇسانا بىزدىن غەم - قايغۇنى كەتكۈزۈۋەتكەن اﷲ قا مەنسۇپتۇر! بىزنىڭ پەرۋەردىگارىمىز ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر'),
(3695, 35, 35, 'اﷲ مەرھەمىتىدىن بىزنى جەننەتتە تۇرغۇزدى. جەننەتتە بىز ھەرگىز جاپا تارتمايمىز، ھەرگىز چارچىمايمىز»'),
(3696, 35, 36, 'كاپىرلار دوزاخ ئازابىغا دۇچار بولىدۇ، ئۇلارنىڭ ئۆلۈپ (ئارام تېپىپ قالماسلىقى ئۈچۈن) جانلىرى ئېلىنمايدۇ، ئۇلاردىن ئازابمۇ يېنىكلىتىلمەيدۇ، كۇفرىدا ھەددىدىن ئاشقان ھەر ئىنساننى ئەنە شۇنداق جازالايمىز'),
(3697, 35, 37, 'ئۇلار دوزاختا: «پەرۋەردىگارىمىز! بىزنى چىقىرىۋەتسەڭ (دۇنيادىكى ۋاقىتتا) قىلغان ئەمەللىرىمىزدىن باشقا ئەمەللەرنى قىلساق» دەپ يالۋۇرۇپ توۋلايدۇ. (اﷲ ئېيتىدۇ) «سىلەرگە ئۆمرۈڭلارنى ئۇزۇن قىلىپ، ۋەز - نەسىھەت ئالىدىغان ئادەم ۋەز - نەسىھەت ئالالىغۇدەك ۋاقىت بەرمىدىممۇ؟ سىلەرگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلدىغۇ، ئەمدى ئازابنى تېتىڭلار، زالىملارغا ھېچقانداق ياردەم بەرگۈچى يوق»'),
(3698, 35, 38, 'اﷲ ھەقىقەتەن ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلگۈچىدۇر. اﷲ ھەقىقەتەن دىللاردىكى سىرلارنى بىلگۈچىدۇر'),
(3699, 35, 39, 'اﷲ سىلەرنى زېمىندا ئورۇنباسارلار قىلدى، كىمكى كاپىر بولىدىكەن، ئۇنىڭ كۇفرىنىڭ زىيىنى ئۆزىگە بولىدۇ، كاپىرلارنىڭ كۇفرى پەرۋەردىگارىنىڭ دەرگاھىدا پەقەت غەزەپنىلا زىيادە قىلىدۇ (يەنى ئۇلارنىڭ كۇفرى ئۇلارنى اﷲ نىڭ رەھمىتىدىن تېخىمۇ يىراقلاشتۇرىدۇ)، كاپىرلارنىڭ كۇفرى ئۇلارغا پەقەت ھالاكەتنىلا زىيادە قىلىدۇ'),
(3700, 35, 40, 'ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ؟ سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلار (نېمە بىلەن چوقۇنۇشقا لايىق بولدى؟) ماڭا ئېيتىپ بېرىڭلاركى، ئۇلار زېمىندىن نېمىلەرنى ياراتتى؟ يا ئۇلار ئاسمانلارنى اﷲ بىلەن بىرلىكتە ياراتتىمۇ؟ يا ئۇلارغا بىز بىر كىتاب بەرگەن بولۇپ، ئۇلار شۇ كىتابتىكى روشەن دەلىللەرگە ئاساسلىنامدۇ؟» ھەرگىز ئۇنداق ئەمەس، زالىملار بىر - بىرىگە پەقەت يالغاننىلا ۋەدە قىلىشىدۇ'),
(3701, 35, 41, 'اﷲ ھەقىقەتەن ئاسمانلارنى ۋە زېمىننى (قۇدرىتى ۋە گۈزەل ھېكمىتى بىلەن) چۈشۈپ كېتىشتىن توختىتىپ تۇرىدۇ. ئەگەر ئۇلار چۈشۈپ كەتسە، ئۇلارنى ھېچ ئەھەدى توختىتىپ تۇرالمايدۇ. اﷲ ھەقىقەتەن ھەلىمدۇر (يەنى كۇففارلار ئازابقا تېگىشلىك بولسىمۇ، ئۇلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)، ناھايىتى مەغپىرەت قىلغۇچىدۇر'),
(3702, 35, 42, 'ئۇلار ئۆزلىرىگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلسە، ھەر قانداق ئۈممەتكە قارىغاندا ئەڭ ھىدايەت تاپقۇچى بولىدىغانلىقلىرى بىلەن قاتتىق قەسەم ئىچىشتى. ئۇلارغا ئاگاھلاندۇرغۇچى كەلگەن چاغدا (ھىدايەتتىن) تېخىمۇ يىراق بولۇشتى'),
(3703, 35, 43, '(بۇنداق بولۇشى) ئۇلارنىڭ زېمىندا تەكەببۇرلۇق قىلغانلىقلىرى ۋە ھىيلە - مىكىر ئىشلەتكەنلىكلىرىدىندۇر. ھىيلە - مىكىرنىڭ ۋابالى پەقەت ھىيلە - مىكىر ئىشلەتكەن ئادەمنىڭ ئۆزىگە بولىدۇ، ئۇلار پەقەت ئىلگىرىكى ئۈممەتلەرگە قوللىنىلغان (اﷲ نىڭ ئۇلارنى ھالاك قىلىش ۋە جازالاشتىن ئىبارەت) يولىنىلا كۈتىدۇ، اﷲ نىڭ (مەخلۇقاتلار ئۈستىدە قوللانغان) يولىدا ھەرگىزمۇ ھېچقانداق ئۆزگىرىشنى كۆرمەيسەن، اﷲ يولىدا ھەرگىز يۆتكىلىشنىمۇ (يەنى ئازابنىڭ ئازابقا تېگىشلىك بولغانلار ئۈستىدىن باشقىلارنىڭ ئۈستىگە يۆتكىلىشىنى) كۆرمەيسەن'),
(3704, 35, 44, 'ئۇلار زېمىندا سەير قىلىپ، ئۇلاردىن ئىلگىرىكىلەر (يەنى ھالاك بولغان ئۈممەتلەر) نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆرمىدىمۇ؟ ئۇلار (يەنى ئۆتكەنكى ئۈممەتلەر) بۇلاردىن تېخىمۇ كۈچلۈك ئىدى. ئاسمانلاردىكى ۋە زېمىندىكى ھېچ نەرسە اﷲ نى ئىلاجسىز قالدۇرالمايدۇ. اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر'),
(3705, 35, 45, 'ئەگەر اﷲ ئىنسانلارنى ئۇلارنىڭ قىلمىشلىرى تۈپەيلىدىن جازالايدىغان بولسا، يەر يۈزىدە ھېچبىر جان ئىگىسىنى قويمىغان بولاتتى ۋە لېكىن اﷲ ئۇلار (دىن ھېساب ئېلىش) نى مۇئەييەن ۋاقىتقىچە تەخىر قىلىدۇ، ئۇلار (دىن ھېساب ئېلىش) نىڭ ۋاقتى يېتىپ كەلگەندە (ئۇلارنىڭ ئەمەللىرىگە قاراپ جازا بېرىدۇ)، اﷲ ھەقىقەتەن بەندىلىرىنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'ياسىن'),
(3707, 36, 2, 'ھېكمەتلىك قۇرئان بىلەن قەسەمكى، (ئى مۇھەممەد!) شەك - شۈبھىسىزكى، سەن پەيغەمبەرلەردىنسەن'),
(3708, 36, 3, 'ھېكمەتلىك قۇرئان بىلەن قەسەمكى، (ئى مۇھەممەد!) شەك - شۈبھىسىزكى، سەن پەيغەمبەرلەردىنسەن'),
(3709, 36, 4, 'توغرا يولدىسەن'),
(3710, 36, 5, 'قۇرئان ئاتا - بوۋىلىرى ئاگاھلاندۇرۇلمىغان (يەنى ئۇلارغا پەيغەمبەر كەلمىگەن)، غاپىل قالغان بىر قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، غالىب، ناھايىتى مېھرىبان اﷲ تەرىپىدىن نازىل قىلىنغاندۇر'),
(3711, 36, 6, 'قۇرئان ئاتا - بوۋىلىرى ئاگاھلاندۇرۇلمىغان (يەنى ئۇلارغا پەيغەمبەر كەلمىگەن)، غاپىل قالغان بىر قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، غالىب، ناھايىتى مېھرىبان اﷲ تەرىپىدىن نازىل قىلىنغاندۇر'),
(3712, 36, 7, 'ئۇلارنىڭ كۆپچىلىكىگە (ئازاب توغرىسىدىكى) ھۆكۈم ھەقىقەتەن تېگىشلىك بولدى. شۇنىڭ ئۈچۈن ئۇلار ئىمان ئېيتمايدۇ'),
(3713, 36, 8, 'بىز ھەقىقەتەن ئۇلارنىڭ بويۇنلىرىغا ئېڭەكلىرىگىچە تاقاقلارنى تاقىدۇق، شۇنىڭ ئۈچۈن ئۇلار باشلىرىنى ئىگەلمەيدۇ (يەنى ئۇلار ئىمانغا بويسۇنمايدۇ ۋە ئىمانغا باش ئەگمەيدۇ)'),
(3714, 36, 9, 'ئۇلارنىڭ ئالدىدا بىر توسۇق، كەينىدە بىر توسۇق قىلدۇق، ئۇلارنىڭ كۆزلىرىنى پەردىلىدۇق، شۇنىڭ ئۈچۈن ئۇلار (ئىماننىڭ يوللىرى توسۇلۇپ كەتكەنلىكى ئۈچۈن) ھېچ نەرسىنى كۆرەلمەيدۇ'),
(3715, 36, 10, 'ئۇلارنى ئاگاھلاندۇرامسەن، ئاگاھلاندۇرمامسەن، ئۇلارغا بەرىبىر ئوخشاش، ئىمان ئېيتمايدۇ'),
(3716, 36, 11, 'سەن پەقەت قۇرئانغا ئەگەشكەن، مەرھەمەتلىك اﷲ نى كۆرمەي تۇرۇپ قورققان كىشىنى ئاگاھلاندۇرالايسەن (يەنى ئاگاھلاندۇرۇشۇڭنىڭ پايدىسى بولىدۇ)، ئۇنىڭغا (اﷲ نىڭ ئۇنىڭ گۇناھلىرىنى) مەغپىرەت قىلىدىغانلىقى ۋە ئېسىل ساۋاب (يەنى جەننەت ئاتا قىلىدىغانلىقى) بىلەن خۇش خەۋەر بەرگىن'),
(3717, 36, 12, 'بىز، شۈبھىسىزكى، ئۆلۈكلەرنى تىرىلدۈرىمىز، ئۇلارنىڭ (دۇنيادا) قىلغان (ياخشى - يامان) ئەمەللىرىنى ۋە ئىشلىرىنى خاتىرىلەپ قويىمىز، ھەممە شەيئىنى روشەن دەپتەردە (يەنى لەۋھۇلمەھپۇزدا) تولۇق خاتىرىلەپ قويغانمىز'),
(3718, 36, 13, 'ئۇلارغا (يەنى كۇففارلارغا) شەھەر (يەنى ئەنتاكىيە) ئاھالىسىنى مىسال قىلىپ كەلتۈرگىن، ئۆز ۋاقتىدا ئۇلارغا پەيغەمبەرلەر كەلگەن ئىدى'),
(3719, 36, 14, 'ئەينى زاماندا ئۇلارغا ئىككى پەيغەمبەر ئەۋەتتۇق. ئۇلار ئۇ ئىككى پەيغەمبەرنى ئىنكار قىلدى. ئۈچىنچى پەيغەمبەرنى ئەۋەتىش بىلەن (ئۇلارغا) ياردەم قىلدۇق، پەيغەمبەرلەر (ئۇلارغا): «بىز ھەقىقەتەن (اﷲ نىڭ) سىلەرگە (يەنى سىلەرنى ھىدايەت قىلىشقا) ئەۋەتىلگەن ئەلچىلىرىمىز» دېدى'),
(3720, 36, 15, 'ئۇلار: «سىلەر پەقەت بىزگە ئوخشاش ئىنسان، مېھرىبان اﷲ ھېچ نەرسە نازىل قىلغىنى يوق، سىلەر پەقەت يالغانچىسىلەر» دېدى'),
(3721, 36, 16, 'ئەلچىلەر: «پەرۋەردىگارىمىز بىلىدۇكى، بىز شەك - شۈبھىسىز سىلەرگە ئەۋەتىلگەن ئەلچىلەرمىز'),
(3722, 36, 17, 'بىزنىڭ مەسئۇلىيىتىمىز پەقەت (روشەن دەلىللەر بىلەن) چۈشىنىشلىك قىلىپ تەبلىغ قىلىشتۇر» دېدى'),
(3723, 36, 18, 'ئۇلار (ئەلچىلەرگە): «بىز سىلەردىن شۇم پال ئالدۇق. ئەگەر سىلەر (دەۋىتىڭلارنى) توختاتمىساڭلار، سىلەرنى چوقۇم تاش - كېسەك قىلىپ ئۆلتۈرىمىز ۋە بىزنىڭ قاتتىق جازايىمىزغا ئۇچرايسىلەر» دېدى'),
(3724, 36, 19, 'ئەلچىلەر: «(كۇفرىڭلار سەۋەبىدىن) شۇملۇقۇڭلار ئۆزۈڭلار بىلەن بىللىدۇر، سىلەرگە ۋەز - نەسىھەت قىلىنسا (شۇم پال ئالامسىلەر؟) ھەرگىز ئۇنداق ئەمەس (يەنى ئىش سىلەر گۇمان قىلغاندەك ئەمەس)، سىلەر (اﷲ قا شېرىك كەلتۈرۈش بىلەن) ھەددىدىن ئاشقۇچى قەۋمسىلەر» دېدى'),
(3725, 36, 20, 'شەھەرنىڭ يىراق جايىدىن بىر كىشى (يەنى ھەبىب نەجار) يۈگۈرۈپ كېلىپ ئېيتتى: «ئى قەۋمىم! پەيغەمبەرلەرگە ئەگىشىڭلار'),
(3726, 36, 21, 'توغرا يولدا بولغان، (ئىمانغا دەۋەت قىلغانلىقىغا) سىلەردىن ھەق سورىمايدىغان كىشىلەرگە ئەگىشىڭلار'),
(3727, 36, 22, 'مەن نېمىشقا مېنى ياراتقان ۋە سىلەر (ئۆلگەندىن كېيىن) دەرگاھىغا قايتىدىغان اﷲ قا ئىبادەت قىلماي؟'),
(3728, 36, 23, 'مەن اﷲ تىن غەيرىينى ئىلاھ قىلىۋالىدىغان بولسام، ئەگەر مېھرىبان اﷲ ماڭا بىرەر زىيان - زەخمەت يەتكۈزمەكچى بولسا، ئۇلارنىڭ شاپائىتى مەندىن ھېچ نەرسىنى دەپئى قىلالمايدۇ ھەمدە مېنى قۇتقۇزالمايدۇ'),
(3729, 36, 24, 'ئەگەر ئۇنداق بولسا (يەنى ئاللادىن غەيرىينى ئىلاھ قىلىۋالىدىغان بولسام)، مەن ھەقىقەتەن ئوپئوچۇق گۇمراھلىقتا بولىمەن'),
(3730, 36, 25, 'مەن ھەقىقەتەن (سىلەرنى ياراتقان) پەرۋەردىگارىڭلارغا ئىمان ئېيتتىم (ئىمانىمغا) قۇلاق سېلىڭلار (ۋە ئۇنىڭغا گۇۋاھ بولۇڭلار)»'),
(3731, 36, 26, '(قەۋمى ئۇنى ئۆلتۈرۈۋەتكەندىن كېيىن، ئۇنىڭغا) «جەننەتكە كىرگىن» دېيىلدى، ئۇ: «كاشكى قەۋمىم پەرۋەردىگارىمنىڭ ماڭا مەغپىرەت قىلغانلىقنى ۋە مېنى ھۈرمەتلىكلەردىن قىلغانلىقىنى بىلسە ئىدى» دېدى'),
(3732, 36, 27, '(قەۋمى ئۇنى ئۆلتۈرۈۋەتكەندىن كېيىن، ئۇنىڭغا) «جەننەتكە كىرگىن» دېيىلدى، ئۇ: «كاشكى قەۋمىم پەرۋەردىگارىمنىڭ ماڭا مەغپىرەت قىلغانلىقنى ۋە مېنى ھۈرمەتلىكلەردىن قىلغانلىقىنى بىلسە ئىدى» دېدى'),
(3733, 36, 28, 'ئۇنىڭدىن كېيىن ئۇنىڭ قەۋمىنى (جازالاش ئۈچۈن) ئاسماندىن قوشۇن (يەنى پەرىشتىلەر) چۈشۈرمىدۇق (ھەر قەۋمنى ھالاك قىلىش ئۈچۈن پەرىشتىلەرنى) چۈشۈرۈۋەرگۈچى بولمىدۇق'),
(3734, 36, 29, 'پەقەت بىر ئاۋاز بىلەنلا ناگاھان ئۇلار قېتىپ قالدى'),
(3735, 36, 30, '(اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغۇچى) بەندىلەرگە ئەپسۇسكى، ئۇلارغا بىرەر پەيغەمبەر كەلسىلا ئۇنى مەسخىرە قىلىشتى'),
(3736, 36, 31, 'ئۇلار (يەنى مەككە ئاھالىسى) ئۇلاردىن ئىلگىرى ئۆتكەن نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى بىلمەمدۇ؟ ئۇلار بۇلارنىڭ يېنىغا (يەنى دۇنياغا ئەبەدىي) قايتمايدۇ'),
(3737, 36, 32, 'ئۇلارنىڭ ھەممىسى (قىيامەت كۈنى ھېساب بېرىش ئۈچۈن) ھۇزۇرىمىزغا ھازىر قىلىنىدۇ'),
(3738, 36, 33, 'ئۆلۈك (يەنى قۇرغاق) زېمىننى (يامغۇر بىلەن) تىرىلدۈرۈپ، ئۇنىڭدىن ئۇلار يەيدىغان ئاشلىقنى ئۆستۈرگەنلىكىمىز ئۇلارغا (اﷲ نىڭ قۇدرىتى كامىلەسىنى كۆرسىتىدىغان) ئالامەتتۇر'),
(3739, 36, 34, 'بىز زېمىندا نۇرغۇنلىغان خورما باغلىرىنى، ئۈزۈم باغلىرىنى بەرپا قىلدۇق، شۇ زېمىندا بۇلاقلارنى ئېقىتتۇق'),
(3740, 36, 35, 'ئۇلار ئۇنىڭ (يەنى باغلارنىڭ) مېۋىسىنى يېسۇن، (بۇ مېۋىلەرنى) ئۇلارنىڭ قوللىرى ياراتقان ئەمەس، ئۇلار (اﷲ نىڭ نېمەتلىرىگە) شۈكۈر قىلمامدۇ؟'),
(3741, 36, 36, 'اﷲ (پۈتۈن ئەيىب - نۇقسانلاردىن) پاكتۇر، ئۇ پۈتۈن شەيئىلەرنى جۈپ ياراتتى. زېمىندىن ئۈنۈپ چىقىدىغان نەرسىلەرنىڭ، ئۇلارنىڭ ئۆزلىرىنىڭ ۋە ئۇلار بىلمەيدىغان نەرسىلەر (يەنى ئاجايىپ مەخلۇقاتلار) نىڭ ھەممىسىنىڭ جۈپتى بار'),
(3742, 36, 37, 'ئۇلارغا (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) بىر ئالامەت كېچىدىن ئىبارەتكى، ئۇنىڭدىن كۈندۈزنى ئايرىۋېتىمىز - دە (يەنى كۈندۈزنىڭ نۇرىنى يوق قىلىۋېتىمىز - دە)، ئۇلار ناگاھان قاراڭغۇدا قالىدۇ'),
(3743, 36, 38, 'كۈن بەلگىلەنگەن جايىغا قاراپ سەير قىلىدۇ، بۇ، غالىب، ھەممىنى بىلگۈچى اﷲ نىڭ ئالدىنئالا بەلگىلىگەن ئىشىدۇر'),
(3744, 36, 39, 'ئايغا سەير قىلىدىغان مەنزىللەرنى بەلگىلىدۇق، ئۇ (ئاخىرقى مەنزىلگە يېتىپ بارغاندا) خورمىنىڭ قۇرۇپ قالغان شېخىغا ئوخشاش بولۇپ قالىدۇ'),
(3745, 36, 40, 'كۈننىڭ ئايغا يېتىۋېلىشى (يەنى ئىككىسىنىڭ جەم بولۇپ قېلىشى)، كېچىنىڭ كۈندۈزدىن ئېشىپ كېتىشى (يەنى ۋاقتى كەلمەستىن كۈندۈزنىڭ ئورنىنى ئېلىشى) مۇمكىن ئەمەس، ھەر بىرى پەلەكتە ئۈزۈپ تۇرىدۇ'),
(3746, 36, 41, 'ئۇلارغا (بىزنىڭ كامالى قۇدرىتىمىزنى كۆرسىتىدىغان) ئالامەت شۇكى، بىز ئۇلارنىڭ ئاتا - بوۋىلىرىنى (نۇھ ئەلەيھىسسالامنىڭ نەرسە - كېرەك ۋە ھايۋاناتلار بىلەن) توشقۇزۇلغان كېمىسىگە چۈشۈردۇق'),
(3747, 36, 42, 'بىز ئۇلار ئۈچۈن شۇ كېمىگە ئوخشاش ئۇلار چۈشىدىغان نەرسىلەرنى ياراتتۇق'),
(3748, 36, 43, 'ئەگەر بىز خالىساق ئۇلارنى غەرق قىلىۋېتىمىز، ئۇلارغا ياردەم بەرگۈچىمۇ بولمايدۇ ۋە ئۇلارنى قۇتۇلدۇرغۇچىمۇ بولمايدۇ'),
(3749, 36, 44, 'پەقەت بىز رەھىم قىلغانلىقىمىز ۋە ئۇلارنى مۇئەييەن مۇددەتكىچە (ھاياتتىن) بەھرىمەن قىلىدىغانلىقىمىز ئۈچۈن (ئۇلار قۇتۇلدۇرۇلىدۇ)'),
(3750, 36, 45, 'ئۇلارغا: «رەھمەتكە ئېرىشىشىڭلار ئۈچۈن ئالدىڭلاردىكىدىن (يەنى دۇنيا ئازابىدىن) ۋە ئارقاڭلاردىكىدىن (يەنى ئاخىرەت ئازابىدىن) قورقۇڭلار» دېيىلسە (ئۇلار بۇنىڭدىن يۈز ئۆرۈيدۇ)'),
(3751, 36, 46, 'پەرۋەردىگارىنىڭ (پەيغەمبەرنىڭ راستلىقىنى ئىسپاتلايدىغان) ئايەتلىرىدىن بىر ئايەت كەلسىلا، ئۇلار بۇنىڭدىن يۈز ئۆرۈيدۇ'),
(3752, 36, 47, 'ئەگەر ئۇلارغا: «اﷲ سىلەرگە رىزىق قىلىپ بەرگەن نەرسىلەردىن (پېقىرلارغا) سەدىقە قىلىپ بېرىڭلار» دېيىلسە، كاپىرلار مۆمىنلەرگە (مەسخىرە قىلىش يۈزىسىدىن): «ئەگەر اﷲ خالىسا تەمىنلەيدىغان ئادەمنى بىز تەمىنلەمدۇق؟ سىلەر پەقەت روشەن گۇمراھلىقتاسىلەر» دەيدۇ'),
(3753, 36, 48, 'ئۇلار: «ئەگەر راستچىل بولساڭلار، بۇ ۋەدە قاچان ئىشقا ئاشىدۇ؟» دەيدۇ'),
(3754, 36, 49, 'ئۇلار پەقەت بىر ئاۋازنىلا (يەنى ئىسراپىل ئەلەيھىسسالامنىڭ بىرىنچى قېتىملىق چالغان سۇرىنىلا) كۈتىدۇ، ئۇلار (دۇنيادا سودا - سېتىق ۋە شۇنىڭغا ئوخشاش ئىشلار ئۈستىدە) جاڭجاللىشىپ تۇرغان ھالەتلىرىدە، ئۇ ئاۋاز ئۇلارنى ھالاك قىلىدۇ'),
(3755, 36, 50, 'ئۇلار بىر - بىرىگە ۋەسىيەت قالدۇرۇشقىمۇ، ئائىلىسىگە قايتىشقىمۇ قادىر بولالمايدۇ'),
(3756, 36, 51, 'سۇر چېلىنغان ھامان ئۇلار قەبرىلىرىدىن چىقىپ پەرۋەردىگارى تەرەپكە يۈگۈرىدۇ'),
(3757, 36, 52, 'ئۇلار: «ۋاي ئىسىت! بىزنى ئۇخلاۋاتقان يېرىمىزدىن (يەنى قەبرىمىزدىن) كىم ئويغاتتى؟» دەيدۇ (چۈنكى ئۇلار ئىككى سۇر ئارىسىدا ئۇيقۇدا بولۇپ ئازابتىن خالى بولىدۇ)، (پەرىشتىلەر يا مۆمىنلەر ئۇنىڭغا جاۋابەن) «مېھرىبان اﷲ نىڭ ۋەدە قىلغىنى مۇشۇ (كۈن)، پەيغەمبەرلەر راست ئېيتقان» دەيدۇ'),
(3758, 36, 53, 'پەقەت بىر ئاۋاز بىلەنلا ئۇلارنىڭ ھەممىسى ھۇزۇرىمىزغا ھازىر قىلىنىدۇ'),
(3759, 36, 54, 'ئۇ كۈندە ھېچبىر ئىنسانغا قىلچە زۇلۇم قىلىنمايدۇ، سىلەرگە پەقەت قىلمىشىڭلارغا يارىشا جازا بېرىلىدۇ'),
(3760, 36, 55, 'شۈبھىسىزكى، ئۇ كۈندە جەننەت ئەھلىلىرى نېمەت ئىچىدىدۇر'),
(3761, 36, 56, 'ئۇلار ۋە ئۇلارنىڭ جۈپتىلىرى (جەننەتلەرنىڭ) سايىلىرى ئاستىدا تەختلەرگە يۆلەنگەن ھالدا تۇرىدۇ'),
(3762, 36, 57, 'ئۇلار ئۈچۈن جەننەتتە (تۈرلۈك) مېۋىلەر بار، ئۇلار ئۈچۈن جەننەتتە (ئۇلارنىڭ) كۆڭلى تارتقان نەرسىلەر بولىدۇ'),
(3763, 36, 58, 'مېھرىبان پەرۋەردىگارى تەرىپىدىن (ئۇلارغا) سالام دېيىلىدۇ'),
(3764, 36, 59, '(گۇناھكارلارغا) «ئى گۇناھكارلار! بۈگۈن (بىر تەرەپكە) ئايرىلىڭلار» (دېيىلىدۇ)'),
(3765, 36, 60, 'سىلەرگە (پەيغەمبەرلىرىم ئارقىلىق): «ئى ئادەم بالىلىرى! شەيتانغا چوقۇنماڭلار، ئۇ ھەقىقەتەن سىلەرگە ئاشكارا دۈشمەندۇر، ماڭىلا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر» دەپ تەۋسىيە قىلمىدىممۇ؟'),
(3766, 36, 61, 'سىلەرگە (پەيغەمبەرلىرىم ئارقىلىق): «ئى ئادەم بالىلىرى! شەيتانغا چوقۇنماڭلار، ئۇ ھەقىقەتەن سىلەرگە ئاشكارا دۈشمەندۇر، ماڭىلا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر» دەپ تەۋسىيە قىلمىدىممۇ؟'),
(3767, 36, 62, 'شۈبھىسىزكى، شەيتان سىلەردىن نۇرغۇن كىشىلەرنى ئازدۇردى، (بۇنى) چۈشەنمەمسىلەر؟'),
(3768, 36, 63, 'سىلەرگە ۋەدە قىلىنغان جەھەننەم مانا شۇ'),
(3769, 36, 64, 'سىلەر (دۇنيادىكى چېغىڭلاردا) كاپىر بولغانلىقىڭلار ئۈچۈن جەھەننەمگە كىرىڭلار'),
(3770, 36, 65, 'شۇ كۈندە ئۇلارنىڭ ئېغىزلىرىنى پېچەتلىۋېتىمىز، ئۇلارنىڭ قىلمىشلىرىنى قوللىرى بىزگە سۆزلەپ بېرىدۇ، پۇتلىرى گۇۋاھلىق بېرىدۇ (يەنى ھەر ئەزا ئۇنىڭدىن سادىر بولغان ئىشنى سۆزلەپ بېرىدۇ)'),
(3771, 36, 66, 'ئەگەر بىز خالىساق، ئۇلارنىڭ كۆزلىرىنى ئەلۋەتتە يوق قىلىۋېتەتتۇق، ئاندىن ئۇلار يول مېڭىشقا ئالدىرايتتى، لېكىن ئۇلار قانداقمۇ كۆرەلىسۇن؟'),
(3772, 36, 67, 'ئەگەر بىز خالىساق، ئۇلارنى ئۆيلىرىدىلا سۈرىتىنى مۇبەددەل (يەنى مايمۇن، چوشقا ياكى تاش) قىلىۋېتەتتۇق، شۇنىڭ بىلەن ئۇلار ئالدىغىمۇ ماڭالمايتتى، ئارقىغىمۇ قايتالمايتتى'),
(3773, 36, 68, 'بىز ئۆمرىنى ئۇزۇن قىلغان ئادەمنىڭ تېنىنى ئاجىزلىتىۋېتىمىز (يەنى ياشلىق، قىرانلىق ۋاقتىدىكىدىن ئاجىزلايدۇ، قېرىيدۇ)، ئۇلار (بۇنى) چۈشەنمەمدۇ؟'),
(3774, 36, 69, 'ئۇنىڭغا (يەنى پەيغەمبەرگە) بىز شېئىر تەلىم بەرمىدۇق، ئۇنىڭغا شېئىر مۇناسىپمۇ ئەمەس. ئۇ پەقەت تىرىكلەرنى ئاگاھلاندۇرۇش، كاپىرلارغا ئازابنىڭ تېگىشلىك ئىكەنلىكىنى (بىلدۈرۈش ئۈچۈن نازىل بولغان) ۋەز - نەسىھەتتۇر ۋە روشەن قۇرئاندۇر'),
(3775, 36, 70, 'ئۇنىڭغا (يەنى پەيغەمبەرگە) بىز شېئىر تەلىم بەرمىدۇق، ئۇنىڭغا شېئىر مۇناسىپمۇ ئەمەس. ئۇ پەقەت تىرىكلەرنى ئاگاھلاندۇرۇش، كاپىرلارغا ئازابنىڭ تېگىشلىك ئىكەنلىكىنى (بىلدۈرۈش ئۈچۈن نازىل بولغان) ۋەز - نەسىھەتتۇر ۋە روشەن قۇرئاندۇر'),
(3776, 36, 71, 'ئۇلار بىلمەمدۇكى، ئۇلار ئۈچۈن ھايۋانلارنى (قۇدرەت) قولىمىز بىلەن خەلق ئەتتۇق، ئۇلار ھايۋانلارنى باشقۇرۇپ تۇرغۇچىلاردۇر'),
(3777, 36, 72, 'ھايۋانلارنى ئۇلارغا بويسۇندۇرۇپ بەردۇق، ئۇلار ئۇ ھايۋانلارنىڭ بەزىسىنى مىنىدۇ، بەزىسىنى يەيدۇ'),
(3778, 36, 73, 'ئۇلار شۇ ھايۋانلاردىن پايدىلىنىدۇ ۋە سۈتلىرىنى ئىچىدۇ، ئۇلار (پەرۋەردىگارىنىڭ بۇ نېمەتلىرىگە) شۈكۈر قىلمامدۇ؟'),
(3779, 36, 74, 'ئۇلار ياردەمگە ئېرىشىشنى ئۈمىد قىلىپ، اﷲ نى قويۇپ باشقا مەبۇدلارنى ئىلاھ قىلىۋالدى'),
(3780, 36, 75, 'ئۇ مەبۇدلار ئۇلارغا ياردەم بېرەلمەيدۇ. بۇلار شۇ مەبۇدلار ئۈچۈن (يەنى ئۇلارنىڭ خىزمىتى ئۈچۈن) ھازىرلانغان قوشۇندۇر'),
(3781, 36, 76, '(ئى، مۇھەممەد!) ئۇلار (يەنى مۇشرىكلار) نىڭ سۆزى (يەنى سېنى ئىنكار قىلغانلىقى) سېنى غەمكىن قىلمىسۇن. بىز ھەقىقەتەن ئۇلارنىڭ دىللىرىدىكىنى ۋە ئاشكارا قىلغان (سۆز ۋە ھەرىكەتلىرى) نى بىلىپ تۇرىمىز'),
(3782, 36, 77, 'ئىنسان بىلمەمدۇكى، بىز ھەقىقەتەن ئۇنى ئابىمەنىيدىن ياراتتۇق. ئەمدى ئۇ (پەرۋەردىگارىغا) ئاشكارا خۇسۇمەتچى بولۇپ قالدى'),
(3783, 36, 78, 'ئۇ بىزگە (ئىنساننىڭ قايتا تىرىلىشىنى يىراق ساناپ، چىرىگەن سۆڭەكلەرنى) مىسال قىلىپ كۆرسەتتى. ئۆزىنىڭ يارىتىلغانلىقىنى بولسا ئۇنتۇدى، ئۇ: «چىرىپ كەتكەن سۆڭەكلەرنى كىم تىرىلدۈرەلەيدۇ؟» دېدى'),
(3784, 36, 79, 'ئېيتقىنكى، «ئۇنى ئەڭ دەسلەپتە ياراتقان زات تىرىلدۈرىدۇ، ئۇ ھەر بىر مەخلۇقنى بىلگۈچىدۇر»'),
(3785, 36, 80, 'اﷲ سىلەر ئۈچۈن يېشىل دەرەختىن ئوت پەيدا قىلدى. سىلەر ئۇنىڭ بىلەن ئوت ياقىسىلەر'),
(3786, 36, 81, 'ئاسمانلارنى ۋە زېمىننى ياراتقان زات ئۇلارنىڭ ئوخشىشىنى يارىتىشقا قادىر ئەمەسمۇ؟ ئۇ بۇنىڭغا قادىر، اﷲ ماھىر ياراتقۇچىدۇر، ھەممىنى بىلگۈچىدۇر'),
(3787, 36, 82, 'اﷲ بىرەر شەيئىنى (يارىتىشنى) ئىرادە قىلسا، ئۇنىڭغا «ۋۇجۇتقا كەل» دەيدۇ - دە، ئۇ ۋۇجۇتقا كېلىدۇ'),
(3788, 36, 83, 'اﷲ پاكتۇركى، ھەر بىر نەرسىنىڭ ئىگىلىك ھوقوقى ئۇنىڭ قولىدىدۇر، (ئۆلگەندىن كېيىن) ئۇنىڭ دەرگاھىغا قايتۇرۇلىسىلەر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'سەپ - سەپ بولۇپ تۇرغان، دۈشمەنلەرگە قارشى ئات سېلىپ تۇرغان، (اﷲ تىن مەدەت تىلەپ) كالامۇاﷲ نى تىلاۋەت قىلىپ تۇرغان (غازات قىلغۇچى) قوشۇن بىلەن قەسەمكى'),
(3790, 37, 2, 'سەپ - سەپ بولۇپ تۇرغان، دۈشمەنلەرگە قارشى ئات سېلىپ تۇرغان، (اﷲ تىن مەدەت تىلەپ) كالامۇاﷲ نى تىلاۋەت قىلىپ تۇرغان (غازات قىلغۇچى) قوشۇن بىلەن قەسەمكى'),
(3791, 37, 3, 'سەپ - سەپ بولۇپ تۇرغان، دۈشمەنلەرگە قارشى ئات سېلىپ تۇرغان، (اﷲ تىن مەدەت تىلەپ) كالامۇاﷲ نى تىلاۋەت قىلىپ تۇرغان (غازات قىلغۇچى) قوشۇن بىلەن قەسەمكى'),
(3792, 37, 4, 'سىلەرنىڭ ئىلاھىڭلار شەك - شۈبھىسىز بىر ئىلاھتۇر'),
(3793, 37, 5, '(ئۇ) ئاسمانلارنىڭ، زېمىننىڭ، ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ ۋە مەشرىقلەرنىڭ پەرۋەردىگارىدۇر'),
(3794, 37, 6, 'بىز ھەقىقەتەن يېقىن ئاسماننى يۇلتۇزلار (نۇرى) بىلەن زىننەتلىدۇق'),
(3795, 37, 7, 'اﷲ نىڭ ئىتائىتىدىن چىققۇچى ھەر بىر شەيتاندىن ئۇنى قوغدىدۇق'),
(3796, 37, 8, 'ئۇلار پەرىشتىلەرنىڭ (سۆزلىرىدىن ھېچ نەرسىنى) ئاڭلىيالمايدۇ، ئۇلار، قوغلىنىشى ئۈچۈن، ھەر تەرەپتىن، ئاققۇچى يۇلتۇزلار بىلەن ئېتىلىدۇ، ئۇلار (ئاخىرەتتە) دائىملىق ئازابقا قالىدۇ'),
(3797, 37, 9, 'ئۇلار پەرىشتىلەرنىڭ (سۆزلىرىدىن ھېچ نەرسىنى) ئاڭلىيالمايدۇ، ئۇلار، قوغلىنىشى ئۈچۈن، ھەر تەرەپتىن، ئاققۇچى يۇلتۇزلار بىلەن ئېتىلىدۇ، ئۇلار (ئاخىرەتتە) دائىملىق ئازابقا قالىدۇ'),
(3798, 37, 10, 'لېكىن (ئاسمان خەۋەرلىرىدىن) ئوغرىلىقچە بىر نەرسە ئاڭلىغان شەيتاننى يورۇق يۇلتۇز قوغلاپ (كۆيدۈرۈپ تاشلايدۇ)'),
(3799, 37, 11, 'ئۇلاردىن (يەنى ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغۇچىلاردىن) سوراپ باققىن! ئۇلارنى يارىتىش قىيىنمۇ؟ يا بىزنىڭ ياراتقانلىرىمىز (يەنى ئاسمان - زېمىن ۋە ئۇلارنىڭ ئارىسىدىكى پەرىشتىلەر ۋە چوڭ مەخلۇقاتلار) نى يارىتىش قىيىنمۇ؟ بىز ھەقىقەتەن ئۇلارنى (يەنى ئۇلارنىڭ ئەسلى بولغان ئادەمنى) يېپىشقاق لايدىن ياراتتۇق'),
(3800, 37, 12, '(ئى مۇھەممەد!) سەن ئۇلارنىڭ اﷲ نىڭ قۇدرىتىنى كۆرۈپ تۇرۇپ، ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغانلىقىدىن بەلكى ئەجەبلىنەرسەن. ئۇلار بولسا سېنى مەسخىرە قىلىشىدۇ'),
(3801, 37, 13, 'ئۇلارغا (قۇرئان بىلەن) ۋەز - نەسىھەت قىلىنسا (ۋەز - نەسىھەتنى) قوبۇل قىلمايدۇ'),
(3802, 37, 14, 'ئۇلار بىرەر مۆجىزىنى كۆرسە مەسخىرە قىلىشىدۇ'),
(3803, 37, 15, 'ئۇلار: «بۇ روشەن سېھىردۇر، بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغاندىن كېيىن راستلا تىرىلەمدۇق؟ بىزنىڭ بۇرۇنقى ئاتىلىرىمىزمۇ تىرىلەمدۇ؟» دەيدۇ'),
(3804, 37, 16, 'ئۇلار: «بۇ روشەن سېھىردۇر، بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغاندىن كېيىن راستلا تىرىلەمدۇق؟ بىزنىڭ بۇرۇنقى ئاتىلىرىمىزمۇ تىرىلەمدۇ؟» دەيدۇ'),
(3805, 37, 17, 'ئۇلار: «بۇ روشەن سېھىردۇر، بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغاندىن كېيىن راستلا تىرىلەمدۇق؟ بىزنىڭ بۇرۇنقى ئاتىلىرىمىزمۇ تىرىلەمدۇ؟» دەيدۇ'),
(3806, 37, 18, 'ئېيتقىنكى، «ھەئە، سىلەر خار ھالدا تىرىلىسىلەر»'),
(3807, 37, 19, 'بىر ئاۋازنى (يەنى ئىسراپىلنىڭ چالغان سۇرىنى) ئاڭلاش بىلەنلا (مەھشەرگاھقا) ھازىر بولۇپ (ئۆزلىرىگە نېمە قىلىنىدىغانلىقىغا) قارىشىپ تۇرىدۇ'),
(3808, 37, 20, 'ئۇلار: «ۋاي ئىسىت! بىزگە بۇ قىيامەت كۈنىدۇر» دەيدۇ'),
(3809, 37, 21, 'بۇ سىلەر ئىنكار قىلغان (ھەق بىلەن باتىل) ئايرىلىدىغان كۈندۇر'),
(3810, 37, 22, '(اﷲ پەرىشتىلىرىگە ئېيتىدۇ) زۇلۇم قىلغۇچىلارنى، ئۇلارنىڭ (مۇشرىكلىكتىكى، كۇفرىدىكى) قاياشلىرىنى ۋە ئۇلارنىڭ اﷲ نى قويۇپ چوقۇنغان بۇتلىرىنى (مەھشەرگاھقا) توپلاڭلار، ئاندىن ئۇلارنى دوزاخنىڭ يولىغا باشلاڭلار'),
(3811, 37, 23, '(اﷲ پەرىشتىلىرىگە ئېيتىدۇ) زۇلۇم قىلغۇچىلارنى، ئۇلارنىڭ (مۇشرىكلىكتىكى، كۇفرىدىكى) قاياشلىرىنى ۋە ئۇلارنىڭ اﷲ نى قويۇپ چوقۇنغان بۇتلىرىنى (مەھشەرگاھقا) توپلاڭلار، ئاندىن ئۇلارنى دوزاخنىڭ يولىغا باشلاڭلار'),
(3812, 37, 24, 'ئۇلارنى توختىتىپ تۇرۇڭلار، چۈنكى ئۇلار (جىمى سۆزلىرى ۋە ئىشلىرىدىن) سوئال - سوراق قىلىنىدۇ'),
(3813, 37, 25, '(ئاندىن) ئۇلارغا: «نېمىشقا (دۇنيادىكى ھالىتىڭلارغا ئوخشاش) بىر - بىرىڭلارغا ياردەم قىلمايسىلەر؟» (دېيىلىدۇ)'),
(3814, 37, 26, 'بەلكى ئۇلار بۈگۈن بويسۇنغۇچىلاردۇر'),
(3815, 37, 27, 'ئۇلار بىر - بىرىگە قاراپ مۇنازىرىلىشىدۇ'),
(3816, 37, 28, 'ئەگەشكۈچىلەر ئەگەشتۈرگۈچىلەرگە: «سىلەر (سۆزۈڭلارنىڭ راستلىقىغا) قەسەم ئىچىپ بىزنى ئىماندىن توسۇيتتۇڭلار» دەيدۇ'),
(3817, 37, 29, '(ئەگەشتۈرگۈچىلەر ئەگەشكۈچىلەرگە) ئېيتىدۇ: «سىلەر (ئۆز ئىختىيارىڭلار بىلەن) مۆمىن بولمىدىڭلار'),
(3818, 37, 30, 'بىزنىڭ سىلەرنى (بىزگە ئەگىشىشكە) زورلايدىغان كۈچىمىز يوق ئىدى، بەلكى سىلەر (بىزگە ئوخشاش) ئازغان قەۋم ئىدىڭلار'),
(3819, 37, 31, 'پەرۋەردىگارىمىزنىڭ بىزنى (جازالاش) سۆزى بىزگە تېگىشلىك بولدى. بىز ھەقىقەتەن (شۇ سۆز بويىچە ئازابنى) چوقۇم تېتىغۇچىمىز'),
(3820, 37, 32, 'سىلەرنى بىز ئازدۇردۇق، چۈنكى بىز ھەقىقەتەن ئازغان ئىدۇق»'),
(3821, 37, 33, 'ئۇلار (يەنى ئەگەشتۈرگۈچىلەر بىلەن ئەگەشكۈچىلەر) بۇ كۈندە (يەنى قىيامەت كۈنىدە) ئازابتا ئورتاقتۇر'),
(3822, 37, 34, 'بىز ھەقىقەتەن گۇناھكارلارغا مۇشۇنداق قىلىمىز، (يەنى ئەگەشتۈرگۈچىلەر بىلەن ئەگەشكۈچىلەرنىڭ ھەممىسىگە ئازاب قىلىمىز)'),
(3823, 37, 35, 'ئۇلار ھەقىقەتەن مۇشۇنداق ئىدىكى، ئۇلارغا: «بىر اﷲ تىن باشقا ھېچ ئىلاھ يوق» (دەڭلار) دېيىلسە، (كەلىمە تەۋھىدتىن) تەكەببۇرلۇق قىلىپ باش تارتاتتى'),
(3824, 37, 36, 'ئۇلار: «ئىلاھلىرىمىزنى ھەقىقەتەن بىر مەجنۇن شائىر ئۈچۈن (يەنى ئۇنىڭ سۆزى ئۈچۈن) تاشلىۋېتەمدۇق؟» دەيتتى'),
(3825, 37, 37, 'ھەرگىز ئۇنداق ئەمەس (يەنى ئىش ئۇلارنىڭ بوھتان قىلغىنىدەك ئەمەس)، ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ھەق (دىن) نى ئېلىپ كەلدى ۋە پەيغەمبەرلەرنى تەستىق قىلدى'),
(3826, 37, 38, '(ئى گۇناھكارلار!) سىلەر (چوقۇم) قاتتىق ئازابنى تېتىغۇچىسىلەر'),
(3827, 37, 39, 'سىلەر پەقەت قىلمىشىڭلارغا يارىشا جازالىنىسىلەر'),
(3828, 37, 40, 'پەقەت اﷲ نىڭ سادىق بەندىلىرىلا جازاغا تارتىلمايدۇ'),
(3829, 37, 41, 'ئۇلار مەلۇم رىزىقتىن (يەنى تۈرلۈك مېۋىلەردىن) بەھرىمەن بولىدۇ، ئۇلار ھۈرمەتلىنىدۇ'),
(3830, 37, 42, 'ئۇلار مەلۇم رىزىقتىن (يەنى تۈرلۈك مېۋىلەردىن) بەھرىمەن بولىدۇ، ئۇلار ھۈرمەتلىنىدۇ'),
(3831, 37, 43, 'ئۇلار نازۇنېمەتلىك باغلاردا، تەختلەر ئۈستىدە بىر - بىرىگە قارىشىپ ئولتۇرىدۇ'),
(3832, 37, 44, 'ئۇلار نازۇنېمەتلىك باغلاردا، تەختلەر ئۈستىدە بىر - بىرىگە قارىشىپ ئولتۇرىدۇ'),
(3833, 37, 45, 'ئۇلارغا ئېقىپ تۇرغان، ئاپئاق، ئىچكۈچىلەرگە لەززەت بېغىشلايدىغان شارابلاردىن تولدۇرۇلغان جاملار ئايلاندۇرۇپ تۇرۇلىدۇ'),
(3834, 37, 46, 'ئۇلارغا ئېقىپ تۇرغان، ئاپئاق، ئىچكۈچىلەرگە لەززەت بېغىشلايدىغان شارابلاردىن تولدۇرۇلغان جاملار ئايلاندۇرۇپ تۇرۇلىدۇ'),
(3835, 37, 47, 'ئۇنىڭدا مەست قىلىدىغان نەرسە يوق، ئۇلار ئۇنى ئىچىش بىلەن مەست بولمايدۇ'),
(3836, 37, 48, 'ئۇلارنىڭ يېنىدا ئەرلىرىدىن غەيرىيگە كۆز سالمايدىغان شەھلا كۆزلۈك (جۈپتىلەر) بولىدۇ'),
(3837, 37, 49, 'ئۇلار (ئاقلىقتا) گويا قول تەگمەي ساقلانغان تۆگىقۇشنىڭ تۇخۇملىرىغا ئوخشايدۇ'),
(3838, 37, 50, 'ئۇلار (يەنى ئەھلى جەننەت) بىر - بىرىگە قارىشىپ پاراڭ سېلىشىدۇ'),
(3839, 37, 51, 'ئۇلاردىن بىرى ئېيتىدۇ: «مېنىڭ بىر دوستۇم بولۇپ، ئۇ (ماڭا) \'سەن (ئۆلگەندىن كېيىن تىرىلىشكە) ھەقىقىي ئىشىنەمسەن'),
(3840, 37, 52, 'ئۇلاردىن بىرى ئېيتىدۇ: «مېنىڭ بىر دوستۇم بولۇپ، ئۇ (ماڭا) \'سەن (ئۆلگەندىن كېيىن تىرىلىشكە) ھەقىقىي ئىشىنەمسەن'),
(3841, 37, 53, 'بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغان چاغدا بىز چوقۇم (قىلمىشلىرىمىزدىن) ھېساب بېرەمدۇق؟\' دەيتتى»'),
(3842, 37, 54, 'ئۇ (بۇرادەرلىرىگە): «سىلەر ئۇنى (يەنى دوستۇمنى) كۆرەمسىلەر؟» دەيدۇ'),
(3843, 37, 55, 'ئۇ قاراپلا ئۇنى (يەنى كاپىر دوستىنى) دوزاخنىڭ ئوتتۇرىسىدا كۆرىدۇ'),
(3844, 37, 56, 'ئۇ (يەنى مۆمىن) ئېيتىدۇ: «اﷲ بىلەن قەسەمكى، مېنى (ئازدۇرۇپ) ھالاك قىلغىلى تاس قېلىپتىكەنسەن'),
(3845, 37, 57, 'ئەگەر پەرۋەردىگارىمنىڭ (مېنى ئىمانىمدا مۇستەھكەم قىلىشتىن ئىبارەت) مەرھەمىتى بولمىغان بولسا ئەلۋەتتە مەن (سەن بىلەن بىللە دوزاخقا) ھازىر قىلىنغانلاردىن بولاتتىم'),
(3846, 37, 58, 'تۇنجى ئۆلگىنىمىزدىن باشقا ئۆلمەمدۇق؟ ئازابقا دۇچار بولمامدۇق'),
(3847, 37, 59, 'تۇنجى ئۆلگىنىمىزدىن باشقا ئۆلمەمدۇق؟ ئازابقا دۇچار بولمامدۇق'),
(3848, 37, 60, 'بۇ (يەنى ئەھلى جەننەت ئېرىشكەن نېمەت) ھەقىقەتەن زور بەختتۇر'),
(3849, 37, 61, 'ئىشلىگۈچىلەر مۇشۇنداق زور بەختكە ئېرىشىش ئۈچۈن ئىشلىسۇن»'),
(3850, 37, 62, 'بۇ (يەنى جەننەتنىڭ نېمەتلىرى) ياخشى زىياپەتمۇ؟ ياكى زەققۇم دەرىخىمۇ؟'),
(3851, 37, 63, 'زەققۇمنى بىز زالىملار (يەنى گۇمراھلار) ئۈچۈن (ئاخىرەتتە) ئازاب قىلدۇق'),
(3852, 37, 64, 'شۈبھىسىزكى، ئۇ جەھەننەمنىڭ قەئرىدە ئۆسىدىغان دەرەختۇر'),
(3853, 37, 65, 'ئۇنىڭ مېۋىسى گويا شەيتانلارنىڭ باشلىرىغا ئوخشايدۇ'),
(3854, 37, 66, 'ئۇلار شۈبھىسىز شۇ مېۋىلەردىن يەيدۇ، ئۇنىڭ بىلەن قورساقلىرىنى تويغۇزىدۇ'),
(3855, 37, 67, 'ئاندىن ئۇلار ئۇنىڭ ئۈستىگە (يىرىڭ بىلەن) قايناقسۇنىڭ ئارىلاشتۇرمىسىنى ئىچىدۇ'),
(3856, 37, 68, 'ئاندىن ئۇلارنىڭ قايتىدىغان جايى چوقۇم دوزاخ بولىدۇ'),
(3857, 37, 69, 'ئۇلار ھەقىقەتەن ئاتا - بوۋىلىرىنى گۇمراھ بىلدى'),
(3858, 37, 70, 'ئۇلار ئاتا - بوۋىلىرىنىڭ ئىزلىرىدىن يۈگۈردى'),
(3859, 37, 71, 'ئۇلاردىن ئىلگىرى بۇرۇنقىلار (يەنى ئۆتكەنكى ئۈممەتلەر) نىڭ تولىسى ھەقىقەتەن ئازدى'),
(3860, 37, 72, 'بىز ئۇلارغا ھەقىقەتەن ئاگاھلاندۇرغۇچىلارنى (يەنى پەيغەمبەرلەرنى) ئەۋەتتۇق'),
(3861, 37, 73, 'ئاگاھلاندۇرۇلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن'),
(3862, 37, 74, 'لېكىن اﷲ نىڭ سادىق بەندىلىرى (ئازابتىن قۇتۇلدى)'),
(3863, 37, 75, 'نۇھ ھەقىقەتەن بىزگە ئىلتىجا قىلدى. بىز (ئۇنىڭ ئىلتىجاسىنى) نېمىدېگەن ياخشى قوبۇل قىلغۇچىدۇرمىز!'),
(3864, 37, 76, 'بىز ئۇنى ۋە ئۇنىڭ تەۋەلىرىنى چوڭ غەمدىن (يەنى سۇدا غەرق بولۇپ كېتىشتىن) قۇتقۇزدۇق'),
(3865, 37, 77, 'ئۇنىڭ نەسلىنى باقىي قالدۇردۇق'),
(3866, 37, 78, 'كېيىنكىلەر (يەنى كېيىنكى ئەۋلادلار) ئىچىدە ئۇنىڭغا ياخشى نام قالدۇردۇق'),
(3867, 37, 79, 'خالايىقلار نۇھقا سالام يوللايدۇ'),
(3868, 37, 80, 'بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز'),
(3869, 37, 81, 'ئۇ ھەقىقەتەن بىزنىڭ مۆمىن بەندىلىرىمىزدىن ئىدى'),
(3870, 37, 82, 'ئاندىن بىز باشقىلارنى (يەنى نۇھ قەۋمىنىڭ كاپىرلىرىنى سۇدا) غەرق قىلدۇق'),
(3871, 37, 83, 'ئىبراھىممۇ ھەقىقەتەن نۇھنىڭ دىنىغا تەۋەلەردىن ئىدى'),
(3872, 37, 84, 'ئۆز ۋاقتىدا ئىبراھىم پەرۋەردىگارىغا ساغلام (يەنى مۇشرىكلىكتىن، شەكتىن پاك) دىل بىلەن كەلگەن ئىدى'),
(3873, 37, 85, 'ئۆز ۋاقتىدا ئۇ ئاتىسى (ئازەر) گە ۋە قەۋمىگە (ئۇلارنى ئەيىبلەپ) ئېيتتى: «سىلەر نېمىگە ئىبادەت قىلىۋاتىسىلەر؟'),
(3874, 37, 86, 'سىلەر اﷲ نى قويۇپ يالغان ئىلاھلارغا چوقۇنامسىلەر؟'),
(3875, 37, 87, 'ئالەملەرنىڭ پەرۋەردىگارى بولغان (اﷲ قا) نېمىشقا گۇمان قىلىسىلەر؟»'),
(3876, 37, 88, 'ئۇ يۇلتۇزلارغا قارىدى، ئاندىن: «مەن ھەقىقەتەن كېسەل بولۇپ قالىمەن» دېدى'),
(3877, 37, 89, 'ئۇ يۇلتۇزلارغا قارىدى، ئاندىن: «مەن ھەقىقەتەن كېسەل بولۇپ قالىمەن» دېدى'),
(3878, 37, 90, 'ئۇلار ئۇنىڭدىن يۈز ئۆرۈپ ئايرىلىشتى (يەنى ئۇنى تاشلاپ كېتىشتى)'),
(3879, 37, 91, 'ئىبراھىم ئاستا ئۇلارنىڭ بۇتلىرىنىڭ يېنىغا بېرىپ: «(مەسخىرە قىلىش يۈزىسىدىن، بۇ تاماقتىن) يېمەمسىلەر؟ نېمىشقا گەپ قىلمايسىلەر؟» دېدى'),
(3880, 37, 92, 'ئىبراھىم ئاستا ئۇلارنىڭ بۇتلىرىنىڭ يېنىغا بېرىپ: «(مەسخىرە قىلىش يۈزىسىدىن، بۇ تاماقتىن) يېمەمسىلەر؟ نېمىشقا گەپ قىلمايسىلەر؟» دېدى'),
(3881, 37, 93, 'ئاندىن ئۇلارغا يۈزلىنىپ ئوڭ قولى بىلەن ئۇرۇپ پاچاقلاشقا كىرىشتى'),
(3882, 37, 94, 'ئۇلار (يەنى قەۋمى) ئىبراھىمنىڭ يېنىغا ئالدىراپ - سالدىراپ كېلىپ: «بىز ئۇلارغا چوقۇنۇۋاتساق، سەن ئۇلارنى چېقىپ تاشلامسەن؟» دېدى'),
(3883, 37, 95, 'ئىبراھىم ئېيتتى: «(ئۆزۈڭلار) ئويۇپ ياسىغان بۇتلارغا چوقۇنامسىلەر؟'),
(3884, 37, 96, 'ھالبۇكى، سىلەرنى ۋە ياسىغان نەرسەڭلارنى اﷲ ياراتقاندۇر»'),
(3885, 37, 97, 'ئۇلار: «ئىبراھىم ئۈچۈن بىر بىنا سېلىڭلار، (ئۇنى ئوتۇن بىلەن تولدۇرۇپ ئوت يېقىڭلار، ئوت يانغاندا) ئۇنى ئوتقا تاشلاڭلار» دېدى. (ئۇلار ئىبراھىمنى ئوتقا تاشلاش بىلەن) ئۇنىڭغا سۇيىقەست قىلماقچى بولدى'),
(3886, 37, 98, 'بىز (ئىبراھىمنى ئوتتىن سالامەت قۇتقۇزۇش بىلەن) ئۇلارنى مەغلۇپ قىلدۇق'),
(3887, 37, 99, 'ئىبراھىم ئېيتتى: «مەن ھەقىقەتەن پەرۋەردىگارىم مېنى بۇيرۇغان جايغا ھىجرەت قىلىمەن، ئۇ مېنى يېتەكلەيدۇ'),
(3888, 37, 100, 'پەرۋەردىگارىم! ماڭا بىر ياخشى پەرزەنت ئاتا قىلغىن»'),
(3889, 37, 101, 'بىز ئىبراھىمغا ناھايىتى مۇلايىم بىر ئوغۇل بىلەن خۇش خەۋەر بەردۇق'),
(3890, 37, 102, 'ئۇ ئىبراھىمنىڭ ئىش - كۈشلىرىگە يارىغۇدەك بولغان چاغدا، ئىبراھىم: «ئى ئوغلۇم! مەن سېنى بوغۇزلاپ قۇربانلىق قىلىشقا (ئەمر قىلىنىپ) چۈشەپتىمەن (پەيغەمبەرلەرنىڭ چۈشى ھەقىقەتتۇر ۋە ئۇلارنىڭ ئىشلىرى اﷲ نىڭ ئەمرى بويىچە بولىدۇ)، ئويلاپ باققىنا! سېنىڭ قانداق پىكرىڭ بار؟» دېدى. ئۇ ئېيتتى: «ئى ئاتا! نېمىگە بۇيرۇلغان بولساڭ، شۇنى ئىجرا قىلغىن، خۇدا خالىسا (ئۇنىڭغا) مېنى سەۋر قىلغۇچى تاپىسەن»'),
(3891, 37, 103, 'ئىككىسى (اﷲ نىڭ ئەمرىگە) بويسۇندى، ئىبراھىم ئۇنى (يەنى ئوغلىنى بوغۇزلاش ئۈچۈن) يېنى ياتقۇزدى'),
(3892, 37, 104, 'بىز ئۇنىڭغا: «ئى ئىبراھىم! (ھېلىقى) چۈشنى ئىشقا ئاشۇردۇڭ (يەنى چۈشۈڭدە بۇيرۇلغاننى بەجا كەلتۈردۈڭ)» دەپ نىدا قىلدۇق. بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز'),
(3893, 37, 105, 'بىز ئۇنىڭغا: «ئى ئىبراھىم! (ھېلىقى) چۈشنى ئىشقا ئاشۇردۇڭ (يەنى چۈشۈڭدە بۇيرۇلغاننى بەجا كەلتۈردۈڭ)» دەپ نىدا قىلدۇق. بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز'),
(3894, 37, 106, 'بۇ ھەقىقەتەن روشەن سىناقتۇر'),
(3895, 37, 107, 'بىز ئۇنىڭ ئورنىغا چوڭ بىر قۇربانلىقنى (يەنى جەننەتتىن چىققان قوچقارنى) بەردۇق'),
(3896, 37, 108, 'كېيىنكىلەر ئىچىدە ئۇنىڭغا ياخشى نام قالدۇردۇق'),
(3897, 37, 109, 'ئىبراھىمغا سالام بولسۇن!'),
(3898, 37, 110, 'بىز ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز'),
(3899, 37, 111, 'ئۇ ھەقىقەتەن بىزنىڭ مۆمىن بەندىلىرىمىزدىندۇر'),
(3900, 37, 112, 'ئۇنىڭغا بىز پەيغەمبەر بولىدىغان ۋە ياخشىلاردىن بولىدىغان ئىسھاق بىلەن خۇش خەۋەر بەردۇق'),
(3901, 37, 113, 'ئىبراھىمغا ۋە ئىسھاققا (دىننىڭ ۋە دۇنيانىڭ) بەرىكەتلىرىنى ئاتا قىلدۇق، ئۇلارنىڭ نەسلىدىن ياخشى ئىش قىلغۇچىلارمۇ، (گۇناھ ۋە كۇفرى بىلەن) ئۆزىگە ئاشكارا زۇلۇم قىلغۇچىلارمۇ چىقىدۇ'),
(3902, 37, 114, 'بىز ھەقىقەتەن مۇسا بىلەن ھارۇنغا (پەيغەمبەرلىك بىلەن) ئېھسان قىلدۇق'),
(3903, 37, 115, 'ئۇلارنى ۋە ئۇلارنىڭ قەۋمىنى (يەنى بەنى ئىسرائىلنى) چوڭ غەمدىن (يەنى پىرئەۋننىڭ ئۇلارنى قۇل قىلىشىدىن) خالاس تاپقۇزدۇق'),
(3904, 37, 116, 'بىز ئۇلارغا ياردەم بەردۇق، شۇنىڭ ئۈچۈن ئۇلار غەلىبە قىلدى'),
(3905, 37, 117, 'ئۇلارغا روشەن بىر كىتابنى (يەنى تەۋراتنى) بەردۇق'),
(3906, 37, 118, 'ئۇلارنى توغرا يولغا باشلىدۇق'),
(3907, 37, 119, 'كېيىنكىلەر ئىچىدە ئۇلارغا ياخشى نام قالدۇردۇق'),
(3908, 37, 120, 'مۇساغا ۋە ھارۇنغا (بىزدىن) سالام بولسۇن!'),
(3909, 37, 121, 'بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز'),
(3910, 37, 122, 'ئۇلار ھەقىقەتەن بىزنىڭ مۆمىن بەندىلىرىمىزدىندۇر'),
(3911, 37, 123, 'ئىلياسمۇ ھەقىقەتەن پەيغەمبەرلەردىندۇر'),
(3912, 37, 124, 'ئۆز ۋاقتىدا ئۇ قەۋمىگە ئېيتتى: «(اﷲ تىن) قورقمامسىلەر؟'),
(3913, 37, 125, 'سىلەر ياراتقۇچىلارنىڭ ئەڭ ئەۋزىلى (اﷲ نى) تەرك ئېتىپ، بەئلى (ناملىق بۇت) گە چوقۇنامسىلەر؟'),
(3914, 37, 126, 'اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر ۋە بۇرۇنقى ئاتا - بوۋاڭلارنىڭ پەرۋەردىگارىدۇر»'),
(3915, 37, 127, 'ئۇلار ئۇنى ئىنكار قىلدى. شەك - شۈبھىسىزكى، ئۇلار (ئازابقا) ھازىر قىلىنىدۇ'),
(3916, 37, 128, 'پەقەت اﷲ نىڭ ئىخلاسمەن بەندىلىرى بۇنىڭدىن مۇستەسنا'),
(3917, 37, 129, 'كېيىنكىلەر ئىچىدە ئۇنىڭغا (يەنى ئىلياسقا) ياخشى نام قالدۇردۇق'),
(3918, 37, 130, 'ئىلياسقا (بىزدىن) سالام بولسۇن!'),
(3919, 37, 131, 'بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز'),
(3920, 37, 132, 'ھەقىقەتەن ئۇ بىزنىڭ مۆمىن بەندىلىرىمىزدىندۇر'),
(3921, 37, 133, 'لۇت ھەقىقەتەن پەيغەمبەرلەردىندۇر'),
(3922, 37, 134, 'ئۆز ۋاقتىدا لۇتنى ۋە ئۇنىڭ تەۋەلىرىنىڭ ھەممىسىنى قۇتقۇزدۇق'),
(3923, 37, 135, 'پەقەت موماي (يەنى لۇتنىڭ ئايالى) نى قالدۇرۇپ ھالاك قىلدۇق'),
(3924, 37, 136, 'ئاندىن قالغانلارنى (يەنى ئۇنىڭ قەۋمىدىن بولغان كۇففارلارنى) ھالاك قىلدۇق'),
(3925, 37, 137, '(ئى ئەھلى مەككە!) سىلەر ئۇلارنىڭ يەرلىرىدىن ئەتىگەن - ئاخشامدا ئۆتۈپ تۇرىسىلەر، سىلەر چۈشەنمەمسىلەر؟'),
(3926, 37, 138, '(ئى ئەھلى مەككە!) سىلەر ئۇلارنىڭ يەرلىرىدىن ئەتىگەن - ئاخشامدا ئۆتۈپ تۇرىسىلەر، سىلەر چۈشەنمەمسىلەر؟'),
(3927, 37, 139, 'يۇنۇس ھەقىقەتەن پەيغەمبەرلەردىندۇر'),
(3928, 37, 140, 'ئۆز ۋاقتىدا ئۇ قەۋمىدىن قېچىپ (كىشىلەر بىلەن) توشقان بىر كېمىگە چىقىۋالدى'),
(3929, 37, 141, '(كېمىدىكىلەر چەك چىققان ئادەمنى دېڭىزغا تاشلاپ كېمىنىڭ يۈكىنى يېنىكلىتىش ئۈچۈن) چەك تاشلاشتى، يۇنۇس (چەكتە) مەغلۇب بولغانلاردىن بولۇپ (دېڭىزغا تاشلاندى)'),
(3930, 37, 142, 'ئۇنى چوڭ بىر بېلىق يۇتۇۋەتتى. ئۇ (قەۋمىنى تاشلاپ، پەرۋەردىگارىنىڭ ئىزنىسىز چىققانلىقى ئۈچۈن) ئەيىبلىنىشكە تېگىشلىك ئىدى'),
(3931, 37, 143, 'ئەگەر ئۇ تەسبىھ ئېيتقۇچىلاردىن بولمىسا ئىدى، بېلىقنىڭ قارنىدا ئەلۋەتتە قىيامەتكىچە قالاتتى'),
(3932, 37, 144, 'ئەگەر ئۇ تەسبىھ ئېيتقۇچىلاردىن بولمىسا ئىدى، بېلىقنىڭ قارنىدا ئەلۋەتتە قىيامەتكىچە قالاتتى'),
(3933, 37, 145, 'ئۇنى بىز (دېڭىز ساھىلىدىكى) بىر قۇرغاق يەرگە (بېلىقنىڭ قارنىدىن) ئاتتۇق، ئەينى ۋاقىتتا ئۇ كېسەل ئىدى'),
(3934, 37, 146, 'ئۇنىڭ ئۈستىگە (سايە تاشلاپ تۇرۇش ئۈچۈن) بىر تۈپ كاۋىنى ئۆستۈرۈپ بەردۇق'),
(3935, 37, 147, 'ئۇنى بىز 100 مىڭ، بەلكى (بۇنىڭدىنمۇ) كۆپ (بىر قەۋمگە پەيغەمبەر قىلىپ) ئەۋەتتۇق'),
(3936, 37, 148, '(ئۇلار ۋەدە قىلىنغان ئازابنى كۆرگەن چاغدا) ئىمان ئېيتتى. مۇئەييەن مۇددەتكىچە (يەنى ئەجىلى يەتكەنگە قەدەر) ئۇلارنى (ھاياتتىن) بەھرىمەن قىلدۇق'),
(3937, 37, 149, '(ئى مۇھەممەد!) ئۇلار (يەنى ئەھلى مەككە) دىن سوراپ باققىنكى، قىزلار پەرۋەردىگارىڭغا، ئوغۇللار ئۇلارغا مەنسۇپمۇ؟'),
(3938, 37, 150, 'ياكى بىز پەرىشتىلەرنى چىشى ياراتقان بولۇپ، ئۇلار (بىزنىڭ ياراتقانلىقىمىزنى) كۆرۈپ تۇرغانمۇ؟ (شۇنىڭ ئۈچۈن شۇنداق دەمدۇ؟)'),
(3939, 37, 151, 'بىلىڭلاركى، ئۇلار ھەقىقەتەن يالغاننى توقۇپ: «اﷲ نىڭ بالىسى بار» دەيدۇ. ئۇلار ئەلۋەتتە يالغانچىلاردۇر'),
(3940, 37, 152, 'بىلىڭلاركى، ئۇلار ھەقىقەتەن يالغاننى توقۇپ: «اﷲ نىڭ بالىسى بار» دەيدۇ. ئۇلار ئەلۋەتتە يالغانچىلاردۇر'),
(3941, 37, 153, 'اﷲ ئوغۇللارنى تاللىماي، قىزلارنى تاللىغانمۇ؟'),
(3942, 37, 154, 'سىلەرگە نېمە بولدى؟ قانداقچە مۇنداق ھۆكۈم چىقىرىسىلەر؟'),
(3943, 37, 155, 'پىكىر قىلمامسىلەر؟'),
(3944, 37, 156, 'ياكى (اﷲ نىڭ بالىسى بارلىقىغا) ئېنىق پاكىتىڭلار بارمۇ؟'),
(3945, 37, 157, '(ئەگەر سۆزۈڭلاردا) راستچىل بولساڭلار كىتابىڭلار (يەنى تەۋرات) نى ئېلىپ كېلىپ (ماڭا كۆرسىتىڭلار)'),
(3946, 37, 158, 'ئۇلار: «اﷲ بىلەن جىنلارنىڭ ئارىسىدا قۇدا - باجىلىق بار» دەپ جۆيلۈدى. جىنلار شەك - شۈبھىسىز بىلىدۇكى، ئۇلار (يەنى شۇ سۆزنى قىلغۇچىلار) (دوزاخقا) چوقۇم ھازىر قىلىنىپ (شۇ يەردە ئازاب چېكىدۇ)'),
(3947, 37, 159, 'اﷲ ئۇلارنىڭ (اﷲ نىڭ بالىسى بار دەپ) سۈپەتلىگەنلىرىدىن پاكتۇر'),
(3948, 37, 160, 'لېكىن اﷲ نىڭ ئىخلاسمەن بەندىلىرى بۇنىڭدىن مۇستەسنا (يەنى اﷲ نى ئۇلارنىڭ سۈپەتلىگەنلىرىدىن پاك ئېتىقاد قىلىدۇ)'),
(3949, 37, 161, '(ئى كاپىرلار!) شۈبھىسىزكى، سىلەر (اﷲ) دوزاخقا كىرىشنى (پۈتۈۋەتكەن) كىشىدىن باشقا ھېچ ئەھەدىنى ئازدۇرالمايسىلەر، سىلەر چوقۇنۇۋاتقان بۇتلارمۇ ئازدۇرالمايدۇ'),
(3950, 37, 162, '(ئى كاپىرلار!) شۈبھىسىزكى، سىلەر (اﷲ) دوزاخقا كىرىشنى (پۈتۈۋەتكەن) كىشىدىن باشقا ھېچ ئەھەدىنى ئازدۇرالمايسىلەر، سىلەر چوقۇنۇۋاتقان بۇتلارمۇ ئازدۇرالمايدۇ'),
(3951, 37, 163, '(ئى كاپىرلار!) شۈبھىسىزكى، سىلەر (اﷲ) دوزاخقا كىرىشنى (پۈتۈۋەتكەن) كىشىدىن باشقا ھېچ ئەھەدىنى ئازدۇرالمايسىلەر، سىلەر چوقۇنۇۋاتقان بۇتلارمۇ ئازدۇرالمايدۇ'),
(3952, 37, 164, '(پەرىشتىلەر) ئېيتىدۇ: «بىزنىڭ ھەر بىرىمىزنىڭ مۇئەييەن ئورنى بار'),
(3953, 37, 165, 'بىز ھەقىقەتەن (ئىبادەتتە) سەپ تارتىپ تۇرغۇچىلارمىز'),
(3954, 37, 166, 'بىز ھەقىقەتەن (اﷲ قا) تەسبىھ ئېيتقۇچىلارمىز»'),
(3955, 37, 167, 'ئۇلار (يەنى قۇرەيش كۇففارلىرى) ھەمىشە: «ئەگەر بىزدە بۇرۇنقىلارنىڭكىدەك (يەنى ئۆتكەنكى ئۈممەتلەرنىڭ كىتابلىرىدەك) بىر كىتاب بولسا ئىدى. ئەلۋەتتە، اﷲ نىڭ ئىخلاسمەن بەندىلىرى بولاتتۇق» دەيدۇ'),
(3956, 37, 168, 'ئۇلار (يەنى قۇرەيش كۇففارلىرى) ھەمىشە: «ئەگەر بىزدە بۇرۇنقىلارنىڭكىدەك (يەنى ئۆتكەنكى ئۈممەتلەرنىڭ كىتابلىرىدەك) بىر كىتاب بولسا ئىدى. ئەلۋەتتە، اﷲ نىڭ ئىخلاسمەن بەندىلىرى بولاتتۇق» دەيدۇ'),
(3957, 37, 169, 'ئۇلار (يەنى قۇرەيش كۇففارلىرى) ھەمىشە: «ئەگەر بىزدە بۇرۇنقىلارنىڭكىدەك (يەنى ئۆتكەنكى ئۈممەتلەرنىڭ كىتابلىرىدەك) بىر كىتاب بولسا ئىدى. ئەلۋەتتە، اﷲ نىڭ ئىخلاسمەن بەندىلىرى بولاتتۇق» دەيدۇ'),
(3958, 37, 170, 'ئۇلار ئۇنى (يەنى قۇرئاننى) ئىنكار قىلىشتى، ئۇلار ئۇزاققا قالماي (كۇفرىنىڭ ئاقىۋىتىنى) بىلىدۇ'),
(3959, 37, 171, 'بىزنىڭ پەيغەمبەر بەندىلىرىمىز ھەققىدىكى سۆزلىرىمىز ئالدىنئالا ئېيتىلغان'),
(3960, 37, 172, 'ئۇلار چوقۇم نۇسرەت تاپقۇچىلاردۇر'),
(3961, 37, 173, 'بىزنىڭ قوشۇنىمىز چوقۇم غەلىبە قىلغۇچىلاردۇر'),
(3962, 37, 174, 'ئۇلار (يەنى مەككە كۇففارلىرى) دىن ۋاقىتلىق يۈز ئۆرۈپ تۇرغىن'),
(3963, 37, 175, 'ئۇلارنى (ئۇلارغا ئازاب نازىل بولغان چاغدا) كۆرگىن، ئۇلارمۇ ئۇزۇنغا قالماي (كۇفرىنىڭ ئاقىۋىتىنى) كۆرىدۇ'),
(3964, 37, 176, 'ئۇلار ئازابىمىزنىڭ بالدۇر نازىل بولۇشىنى تەلەپ قىلامدۇ؟'),
(3965, 37, 177, 'ئازاب ئۇلارنىڭ ھويلىسىغا چۈشكەن چاغدا، ئاگاھلاندۇرۇلغۇچىلارنىڭ ئەتىگىنى نېمىدېگەن يامان!'),
(3966, 37, 178, 'ئۇلاردىن ۋاقىتلىق يۈز ئۆرۈپ تۇرغىن'),
(3967, 37, 179, 'ئۇلارنى (ئۇلارغا ئازاب نازىل بولغان چاغدا) كۆرگىن، ئۇلارمۇ ئۇزۇنغا قالماي (كۇفرىنىڭ ئاقىۋىتىنى) كۆرىدۇ'),
(3968, 37, 180, 'قۇدرەت ئىگىسى بولغان پەرۋەردىگارىڭ ئۇلارنىڭ سۈپەتلىگەنلىرىدىن پاكتۇر'),
(3969, 37, 181, 'پەيغەمبەرلەرگە سالام بولسۇن!'),
(3970, 37, 182, 'جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'صاد. شانلىق قۇرئان بىلەن قەسەمكى، (بۇ قۇرئان ھەقىقەتەن مۆجىزىدۇر)'),
(3972, 38, 2, 'بەلكى كاپىرلار ئىماندىن باش تارتماقتا ۋە (رەسۇلۇللاھقا) مۇخالىپەتچىلىك قىلماقتا'),
(3973, 38, 3, 'ئۇلاردىن ئىلگىرى (كۇفرى تۈپەيلىدىن) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلار (ئازابنى كۆرگەندە) پەرياد قىلدى، ھالبۇكى، (بۇ) قاچىدىغان ۋاقىت ئەمەس'),
(3974, 38, 4, 'ئۇلارغا ئۆزلىرىنىڭ ئىچىدىن بىر ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلگەنلىكىگە ئۇلار ھەيران قېلىشتى، كاپىرلار ئېيتتى: «بۇ بىر يالغانچى سېھىرگەردۇر'),
(3975, 38, 5, 'ئۇ نۇرغۇن ئىلاھلارنى بىر ئىلاھ قىلماقچىمۇ؟ بۇ ھەقىقەتەن غەلىتە ئىش»'),
(3976, 38, 6, 'ئۇلارنىڭ (يەنى قۇرەيشنىڭ) كاتتىلىرى (سورۇنلىرىدىن) قوزغىلىپ چىقىپ ئېيتتى: «سىلەر مېڭىڭلار، مەبۇدلىرىڭلارغا ئىبادەت قىلىشتا چىڭ تۇرۇڭلار، بۇ ھەقىقەتەن (بىزدىن) ئىرادە قىلىنىدىغان بىر ئىش (يەنى مۇھەممەد سىلەرنى دىنىڭلاردىن تاندۇرۇپ، سىلەرگە ئۆزىنىڭ ئۈستۈنلۈك قازىنىشىنى ئىرادە قىلىدۇ)'),
(3977, 38, 7, 'بۇنى بىز باشقا بىر دىندىن (يەنى ناسارا دىنىدىكىلەردىن) ئاڭلىمىدۇق، بۇ پەقەت ئويدۇرمىدۇر'),
(3978, 38, 8, 'قۇرئان بىزگە نازىل بولماي ئۇنىڭغا نازىل بولدىمۇ؟» بەلكى ئۇلار مېنىڭ زىكرىم (يەنى قۇرئان) دىن شەكتىدۇر، بەلكى ئۇلار تېخى مېنىڭ ئازابىمنى تېتىغىنى يوق'),
(3979, 38, 9, 'ياكى ئۇلاردا غالىب، ناھايىتى كەرەملىك پەرۋەردىگارىڭنىڭ رەھمىتىنىڭ خەزىنىلىرى بارمۇ؟'),
(3980, 38, 10, 'ياكى ئۇلار ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقىغا ئىگىمۇ؟ (ئەگەر ئۇلار ئىگە بولسا، ئۇلار ئۆزلىرىنى ئاسمانغا ئېلىپ چىقىدىغان) شوتىلارغا چىقسۇن'),
(3981, 38, 11, 'ئۇلار (رەسۇلۇللاھقا قارشى) جامائەلەردىن ئۇيۇشقان مەغلۇب قىلىنىدىغان قوشۇندۇر'),
(3982, 38, 12, 'ئۇلاردىن (يەنى قۇرەيش مۇشرىكلىرىدىن) ئىلگىرى نۇھ قەۋمى، ئاد (قەۋمى) ۋە قوزۇقلار ئىگىسى پىرئەۋن (قەۋمى) (پەيغەمبەرلىرىنى) ئىنكار قىلدى'),
(3983, 38, 13, 'سەمۇد قەۋمى، لۇت قەۋمى ۋە ئەيكىلىكلەر (يەنى شۇئەيب ئەلەيھىسسالام قەۋمى) مۇ (پەيغەمبەرلىرىنى ئىنكار قىلدى)، ئەنە شۇلار (پەيغەمبەرلەرگە قارشى ئۇيۇشقان) جامائەلەر ئىدى'),
(3984, 38, 14, 'ئۇلار (يەنى جامائەلەر) دىن پەيغەمبەرلەرنى ئىنكار قىلمىغان بىرىمۇ يوق، شۇنىڭ ئۈچۈن ئۇلارغا مېنىڭ ئازابىم تېگىشلىك بولدى'),
(3985, 38, 15, 'بۇلار (يەنى مەككە مۇشرىكلىرى) پەقەت بىر ئاۋاز (يەنى بىرىنچى قېتىملىق سۇر) نىلا كۈتىدۇ، ئۇ قايتىلانمايدۇ'),
(3986, 38, 16, 'ئۇلار (مەسخىرە قىلىش يولى بىلەن): «پەرۋەردىگارىمىز! (سەن بىزگە ئازابتىن ۋەدە قىلغان) نېسىۋىمىزنى بىزگە بالدۇرراق – قىيامەت كۈنىدىن ئىلگىرى بەرگىن» دېيىشتى'),
(3987, 38, 17, 'ئۇلارنىڭ سۆزلىرىگە سەۋر قىلغىن، (دىندا) كۈچلۈك بەندىمىز داۋۇدنى ئەسلىگىن، ئۇ ھەقىقەتەن اﷲ قا ئىتائەت قىلغۇچى ئىدى'),
(3988, 38, 18, 'بىز ھەقىقەتەن تاغلارنى (داۋۇدقا) بويسۇندۇرۇپ بەردۇق، ئۇلار داۋۇد بىلەن ئەتىگىنى - ئاخشىمى تەسبىھ ئېيتاتتى'),
(3989, 38, 19, 'قۇشلارنىمۇ ئۇنىڭغا بويسۇندۇرۇپ بەردۇق، ئۇلارمۇ (تەرەپ - تەرەپتىن) توپلىنىپ، داۋۇد بىلەن بىللە تەسبىھ ئېيتاتتى، (تاغلارنىڭ، قۇشلارنىڭ) ھەممىسى اﷲ قا ئىتائەت قىلغۇچىدۇر'),
(3990, 38, 20, 'داۋۇدنىڭ سەلتەنىتىنى كۈچەيتتۇق، ئۇنىڭغا ھېكمەت ۋە (ھەممە ئادەم چۈشىنىدىغان) روشەن سۆز - ئىبارىلەرنى ئاتا قىلدۇق'),
(3991, 38, 21, '(ئى مۇھەممەد!) دەۋا قىلغۇچىلارنىڭ قىسسىسى ساڭا يەتتىمۇ؟ ئۆز ۋاقتىدا ئۇلار (داۋۇد ئىبادەت قىلىۋاتقان) مەسجىدنىڭ تېمىدىن ئارتىلىپ چۈشۈشتى'),
(3992, 38, 22, 'ئەينى ۋاقىتتا ئۇلار داۋۇدنىڭ يېنىغا كىردى. داۋۇد ئۇلاردىن قورقتى، ئۇلار (داۋۇدقا): «قورقمىغىن، بىز بىرىمىز - بىرىمىزگە چېقىلغان ئىككى دەۋاگەرمىز، بىزنىڭ ئارىمىزدا ئادىللىق بىلەن ھۆكۈم قىلغىن، ناھەق ھۆكۈم چىقارمىغىن، بىزنى توغرا يولغا باشلىغىن» دېدى'),
(3993, 38, 23, '(ئۇلارنىڭ بىرى) «شۈبھىسىزكى، مېنىڭ بۇ قېرىندىشىمنىڭ 99 ساغلىقى بار، مېنىڭ بىر ساغلىقىم بار، قېرىندىشىم ئۇ بىر ساغلىقنىمۇ ماڭا بەرگىن دەيدۇ، (ئارىمىزدا بۇ توغرۇلۇق مۇنازىرە بولۇپ) ئۇ مېنى سۆزدە يېڭىپ قويدى» دېدى'),
(3994, 38, 24, 'داۋۇد ئېيتتى: «ئۇ سېنىڭ ساغلىقىڭنى ئۆزىنىڭ ساغلىقلىرىغا قوشۇۋېلىشنى تەلەپ قىلىپ راستلا سېنى بوزەك قىلىپتۇ، نۇرغۇن شېرىكلەر (يەنى دوستلار)، شۈبھىسىزكى، بىر - بىرىگە چېقىلىدۇ، پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارلا (بىر - بىرىگە چېقىلمايدۇ) بۇلار ئازدۇر». داۋۇد بىزنىڭ ئۇنى سىنىغانلىقىمىزنى بىلدى. پەرۋەردىگارىدىن مەغپىرەت تەلەپ قىلدى، سەجدىگە باردى. (اﷲ قا) تەۋبە قىلدى'),
(3995, 38, 25, 'بىز ئۇنىڭ خاتالىقىنى ئەپۇ قىلدۇق، ئۇ بىزنىڭ دەرگاھىمىزدا ئەلۋەتتە يېقىنلىققا ۋە ياخشى ئاقىۋەتكە ئىگە بولىدۇ'),
(3996, 38, 26, 'ئى داۋۇد! سېنى بىز ھەقىقەتەن يەر يۈزىدە خەلىپە قىلدۇق، كىشىلەرنىڭ ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقارغىن، نەپسى خاھىشقا ئەگەشمىگىنكى، ئۇ سېنى اﷲ نىڭ يولىدىن ئازدۇرىدۇ، اﷲ نىڭ يولىدىن ئازغانلار ھېساب كۈنى (يەنى قىيامەت كۈنى) نى ئۇنتۇغانلىقلىرى ئۈچۈن ھەقىقەتەن قاتتىق ئازابقا دۇچار بولىدۇ'),
(3997, 38, 27, 'ئاسماننى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنى بىكار ياراتمىدۇق، ئۇ (يەنى ئۇلارنى بىكار يارىتىلغان دەپ قاراش) كاپىرلارنىڭ گۇمانىدۇر، كاپىرلارغا دوزاختىن ۋاي!'),
(3998, 38, 28, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى يەر يۈزىدە بۇزغۇنچىلىق قىلغانلار بىلەن ئوخشاش قىلامدۇق؟ ياكى تەقۋادارلارنى فاجىرلارغا ئوخشاش قىلامدۇق؟'),
(3999, 38, 29, '(بۇ) ئۇلارنىڭ ئايەتلەرنى تەپەككۈر قىلىشلىرى ئۈچۈن، ئەقىل ئىگىلىرىنىڭ ۋەز - نەسىھەت ئېلىشلىرى ئۈچۈن بىز ساڭا نازىل قىلغان مۇبارەك كىتابتۇر'),
(4000, 38, 30, 'بىز داۋۇدقا سۇلەيماننى ئاتا قىلدۇق، سۇلەيمان نېمىدېگەن ياخشى بەندە! ئۇ (اﷲ قا) ھەقىقەتەن ئىتائەت قىلغۇچى ئىدى'),
(4001, 38, 31, 'ئۆز ۋاقتىدا كەچقۇرۇنلىقى ئۇنىڭغا ياخشى يۈگۈرۈك ئاتلار توغرىلاندى'),
(4002, 38, 32, 'سۇلەيمان ئېيتتى: «مەن ھەقىقەتەن ئاتلارنى پەرۋەردىگارىمنى ئەسلەش ئۈچۈن ياخشى كۆردۈم، ئۇلار (كۆزۈمدىن) غايىب بولغۇچە (ئۇلارنى كۆزدىن كۆچۈرۈش بىلەن بولدۇم)»'),
(4003, 38, 33, 'سۇلەيمان ئېيتتى: «ئاتلارنى مېنىڭ ئالدىمغا ئېلىپ كېلىڭلار». (ئاتلار ئېلىپ كېلىنگەندىن كېيىن) سۇلەيمان ئۇلارنىڭ پاچاقلىرىنى، بويۇنلىرىنى سىلاشقا باشلىدى'),
(4004, 38, 34, 'بىز ھەقىقەتەن سۇلەيماننى سىنىدۇق، ئۇنىڭ تەختى ئۈستىگە بىر جەسەتنى تاشلىدۇق، ئاندىن ئۇ (بۇنىڭ سىناق ئىكەنلىكىنى بىلىپ) تەۋبە قىلدى'),
(4005, 38, 35, 'سۇلەيمان ئېيىتتى: «پەرۋەردىگارىم! ماڭا مەغپىرەت قىلغىن، ماڭا مەندىن كېيىن (مەندىن باشقا) ھېچ ئادەمگە مۇيەسسەر بولمايدىغان پادىشاھلىقنى ئاتا قىلغىن، سەن ھەقىقەتەن كۆپ ئاتا قىلغۇچىسەن»'),
(4006, 38, 36, 'بىز سۇلەيمانغا شامالنى بويسۇندۇرۇپ بەردۇق. شامال سۇلەيماننىڭ بۇيرۇقى بويىچە ئۇ خالىغان تەرەپكە لەرزان ماڭاتتى'),
(4007, 38, 37, 'ھەمدە ئۇنىڭغا (كاتتا بىنالارنى سالالايدىغان) قۇرغۇچى ۋە (دېڭىزلارغا چۆكۈپ ئۈنچە - مارجانلارنى ئېلىپ چىقالايدىغان) غەۋۋاس شەيتانلارنى بويسۇندۇرۇپ بەردۇق'),
(4008, 38, 38, 'يەنە نۇرغۇن شەيتانلارغا ئىشكەل سېلىنغاندۇر'),
(4009, 38, 39, '(بىز سۇلەيمانغا ئېيتتۇقكى) «بۇ بىزنىڭ (ساڭا بەرگەن كەڭ) ئاتايىمىز، (ئۇنىڭدىن سەن خالىغان كىشىگە) بەرگىن، ياكى بەرمىگىن، (بۇ ھەقتە سەندىن) ھېساب ئېلىنمايدۇ»'),
(4010, 38, 40, 'ئۇ بىزنىڭ دەرگاھىمىزدا ھەقىقەتەن يېقىنلىققا ۋە ياخشى ئاقىۋەتكە ئىگە بولىدۇ'),
(4011, 38, 41, 'بەندىمىز ئەييۇبنى ئەسلىگىن، ئۇ ئۆز ۋاقتىدا پەرۋەردىگارىغا (ئىلتىجا قىلىپ): «شەيتان ماڭا ھەقىقەتەن جاپا - مۇشەققەت ۋە ئازاب يەتكۈزدى» دەپ نىدا قىلدى'),
(4012, 38, 42, 'ئۇنىڭغا: «پۇتۇڭ بىلەن يەرنى تەپكىن» دېدۇق، ئۇ پۇتى بىلەن يەرنى تېپىۋىدى، سۈزۈك بىر بۇلاق ئېتىلىپ چىقتى. (بىز ئۇنىڭغا) «بۇ يۇيۇنسىمۇ بولىدىغان، ئىچسىمۇ بولىدىغان سوغۇق سۇدۇر» (دېدۇق)'),
(4013, 38, 43, 'بىز ئۇنىڭ (كېسەل ۋاقتىدا ھادىسىگە ئۇچراپ يوق بولغان) ئەھلى - ئەۋلادىنى (يېڭىدىن) بەردۇق، ئۇلار بىلەن بىللە بىر باراۋەر ئارتۇق بەردۇق، (بۇ) بىزنىڭ نازىل قىلغان رەھمىتىمىزدۇر، ئەقىل ئىگىلىرى ئۈچۈن ۋەز - نەسىھەتتۇر'),
(4014, 38, 44, '(ئۇنىڭغا) «قولۇڭ بىلەن بىر باغلام چۆپنى ئېلىپ، ئۇنىڭ بىلەن (ئايالىڭنى) ئۇرغىن. قەسىمىڭنى بۇزمىغىن» (دېدۇق). بىز ئەييۇبنى ھەقىقەتەن سەۋرچان بايقىدۇق، ئۇ نېمىدېگەن ئوبدان بەندە! ھەقىقەتەن ئۇ (اﷲ قا) تەۋبە بىلەن يۈزلەنگۈچىدۇر'),
(4015, 38, 45, 'ئىبادەتتە كۈچلۈك، (دىندا) بەسىرەتلىك بەندىلىرىمىز ئىبراھىم، ئىسھاق ۋە يەئقۇبنى ئەسلىگىن'),
(4016, 38, 46, 'ئۇلارنى بىز پاك - خىسلەتلىك، ئاخىرەتنى ئەسلەپ تۇرىدىغان سەمىمىي كىشلەر قىلدۇق'),
(4017, 38, 47, 'ئۇلار بىزنىڭ دەرگاھىمىزدا ھەقىقەتەن ئالاھىدە تاللانغان كىشىلەردۇر، ھەقىقەتەن ياخشى كىشىلەردۇر'),
(4018, 38, 48, 'ئىسمائىلنى، ئىليەسەنى ۋە زۇلكىفلنى ئەسلىگىن، (ئۇلارنىڭ) ھەممىسى ياخشىلاردىندۇر'),
(4019, 38, 49, 'بۇ ۋەز - نەسىھەتتۇر، تەقۋادارلارغا ئەلۋەتتە ياخشى ئاقىۋەت بار'),
(4020, 38, 50, '(ئۇ) تۇرالغۇ بولغان جەننەتلەردىن ئىبارەت بولۇپ، ئۇلارغا ئۇنىڭ دەرۋازىلىرى ئوچۇقتۇر'),
(4021, 38, 51, 'ئۇلار جەننەتلەردە تەختلەرگە يۆلىنىپ ئولتۇرىدۇ، ئۇلار جەننەتلەردە نۇرغۇن مېۋىلەرنى، نۇرغۇن شارابلارنى تەلەپ قىلىپ (ئەكەلدۈرۈپ) تۇرىدۇ'),
(4022, 38, 52, 'ئۇلارنىڭ يانلىرىدا ئەرلىرىدىن غەيرىيگە كۆز سالمايدىغان، ياش قۇرامى ئوخشاش جۈپتىلىرى بولىدۇ'),
(4023, 38, 53, 'بۇ سىلەرگە قىيامەت كۈندە ۋەدە قىلىنغان نېمەتتۇر'),
(4024, 38, 54, 'بۇ ھەقىقەتەن بىزنىڭ ئاتايىمىزدۇركى، ئۇ ھەرگىز تۈگىمەيدۇ'),
(4025, 38, 55, 'بۇ (ھەقىقەتتۇر)، كاپىرلارنىڭ (ئاخىرەتتە بولىدىغان جايى) ھەقىقەتەن ئەڭ يامان جايدۇر'),
(4026, 38, 56, 'ئۇ جەھەننەمدۇر، ئۇلار جەھەننەمگە كىرىدۇ، ئۇ نېمىدېگەن يامان جاي!'),
(4027, 38, 57, 'بۇ (كۆيدۈرگۈچى) قىزىق سۇدۇر، قان - يىرىڭدۇر، ئۇلار بۇنى تېتىسۇن'),
(4028, 38, 58, 'يەنە (ئۇلارغا) مۇشۇ خىلدىكى باشقا تۈرلۈك ئازابلار بار'),
(4029, 38, 59, '(ئەگەشكۈچىلىرى بىلەن دوزاخقا كىرگەن چاغدا، ئەگەشتۈرگۈچىلەرگە ئېيتىلىدۇ) «بۇ جامائە سىلەر بىلەن بىرگە كىرگۈچىدۇر». (ئەگەشتۈرگۈچىلەر ئېيتىدۇ) «ئۇلار قارشى ئېلىنمايدۇ، ئۇلار چوقۇم (بىزگە ئوخشاش) ئازابنى تېتىغۇچىدۇر»'),
(4030, 38, 60, 'ئەگەشكۈچىلەر ئېيتىدۇ: «بەلكى سىلەر قارشى ئېلىنمايسىلەر، سىلەر ئازابنى بىزگە ئېلىپ كەلدىڭلار (يەنى بىزنى ئازدۇرۇپ دوزاخقا كىرىشىمىزگە سەۋەبچى بولدۇڭلار)، بۇ نېمىدېگەن يامان جاي!»'),
(4031, 38, 61, 'ئۇلار (يەنى ئەگەشكۈچىلەر): «پەرۋەردىگارىمىز! بىزگە بۇ ئازابنى ئېلىپ كەلگەن ئادەمگە دوزاخ ئازابىنى ئىككى ھەسسە زىيادە قىلغىن» دەيدۇ'),
(4032, 38, 62, 'ئۇلار (يەنى كاپىرلارنىڭ كاتتىباشلىرى) ئېيتىدۇ: «بىز (دۇنيادىكى چاغدا) يامانلاردىن ھېسابلايدىغان ئادەملەرنى (يەنى مۆمىنلەرنى) (دوزاختا) كۆرمەيمىزغۇ؟'),
(4033, 38, 63, 'ئۇلارنى بىز (دۇنيادىكى چاغدا) مەسخىرە قىلاتتۇقمۇ؟ ياكى ئۇلاردىن كۆزلەر ئېغىپ كەتتىمۇ؟ (يەنى ياكى ئۇلار دوزاختا بىز بىلەن بىللە تۇرسىمۇ كۆرمەيۋاتامدۇق؟)»'),
(4034, 38, 64, 'ئەھلى دوزاخنىڭ بۇ رەۋىشتە ئۆزئارا جاڭجاللىشىشى چوقۇمدۇر'),
(4035, 38, 65, '(ئى مۇھەممەد! مەككە كاپىرلىرىغا) ئېيتقىنكى، «مەن پەقەت بىر ئاگاھلاندۇرغۇچىمەن، غالىب بىر اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر'),
(4036, 38, 66, '(ئۇ اﷲ) ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر، غالىبتۇر. (ئۆزى خالىغان بەندىلەرنى) ناھايىتى مەغپىرەت قىلغۇچىدۇر»'),
(4037, 38, 67, '(ئۇلارغا) ئېيتقىنكى، «ئۇ (يەنى اﷲ نىڭ بىرلىكىگە دەۋەت قىلغۇچى پەيغەمبەر ئىكەنلىكىم) چوڭ خەۋەردۇر'),
(4038, 38, 68, 'سىلەر ئۇنىڭدىن يۈز ئۆرۈيسىلەر'),
(4039, 38, 69, 'پەرىشتىلەر (ئادەم ئەلەيھىسسالام توغرىسىدا) مۇنازىرىلىشىۋاتقان چاغدا، مەن ئۇلارنىڭ ئەھۋالىنى بىلمەيتتىم'),
(4040, 38, 70, 'ماڭا پەقەت ۋەھيى قىلىنىدۇكى، مەن روشەن بىر ئاگاھلاندۇرغۇچىمەن»'),
(4041, 38, 71, 'ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە ئېيتتى: «مەن ھەقىقەتەن لايدىن بىر ئادەم يارىتىمەن'),
(4042, 38, 72, 'ئۇنى مەن راۋۇرۇس ياراتقان (يەنى ئۇنى يارىتىپ سۈرەتكە كىرگۈزۈپ، ئەزالىرىنى تولۇق، مۇكەممەل ئىنسان ھالىتىگە كەلتۈرگەن) ۋە ئۇنىڭغا جان كىرگۈزگەن ۋاقتىمدا ئۇنىڭغا سەجدە قىلىڭلار»'),
(4043, 38, 73, 'پەرىشتىلەرنىڭ بىرىمۇ قالماي، ھەممىسى (ئادەم ئەلەيھىسسالامغا) سەجدە قىلدى'),
(4044, 38, 74, 'پەقەت ئىبلىسلا بويۇنتاۋلىق قىلدى، ئۇ كاپىرلاردىن بولۇپ كەتتى'),
(4045, 38, 75, 'اﷲ ئېيتتى: «ئى ئىبلىس! مەن ئۆز قولۇم بىلەن ياراتقان ئىنسانغا سەجدە قىلىشتىن ساڭا نېمە توسقۇنلۇق قىلدى (ئۇنىڭغا سەجدە قىلماسلىقىڭ تەكەببۇرلۇق قىلغانلىقىڭدىنمۇ؟ ياكى يۇقىرى مەرتىۋىلىكلەردىن بولغانلىقىڭدىنمۇ؟»'),
(4046, 38, 76, 'ئىبلىس: «مەن ئۇنىڭدىن ئارتۇق، مېنى ئوتتىن ياراتتىڭ، ئۇنى لايدىن ياراتتىڭ» دېدى'),
(4047, 38, 77, 'اﷲ: «سەن جەننەتتىن يوقال! سەن ھەقىقەتەن قوغلاندى بولدۇڭ، ساڭا ھەقىقەتەن قىيامەتكىچە مېنىڭ لەنىتىم بولسۇن» دېدى'),
(4048, 38, 78, 'اﷲ: «سەن جەننەتتىن يوقال! سەن ھەقىقەتەن قوغلاندى بولدۇڭ، ساڭا ھەقىقەتەن قىيامەتكىچە مېنىڭ لەنىتىم بولسۇن» دېدى'),
(4049, 38, 79, 'ئىبلىس: «پەرۋەردىگارىم! ماڭا (خالايىق) تىرىلدۈرۈلىدىغان كۈن (يەنى قىيامەت) گىچە مۆھلەت بەرسەڭ (يەنى مېنى ھايات قالدۇرساڭ)» دېدى'),
(4050, 38, 80, 'اﷲ: «جەزمەن ساڭا مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى'),
(4051, 38, 81, 'اﷲ: «جەزمەن ساڭا مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى'),
(4052, 38, 82, 'ئىبلىس ئېيتتى: «ئىززىتىڭ بىلەن قەسەمكى، ئۇلارنىڭ ھەممىسىنى چوقۇم ئازدۇرىمەن'),
(4053, 38, 83, 'ئۇلاردىن پەقەت تاللانغان بەندىلىرىڭلا بۇنىڭدىن مۇستەسنا (يەنى ئۇلارنى ئازدۇرالمايمەن)»'),
(4054, 38, 84, 'اﷲ ئېيتتى: «مېنىڭ سۆزۈم ھەقتۇر، مەن ھەقنى سۆزلەيمەن'),
(4055, 38, 85, 'سەن بىلەن ۋە ئۇلاردىن ساڭا ئەگەشكەنلەرنىڭ ھەممىسى بىلەن چوقۇم جەھەننەمنى توشقۇزىمەن»'),
(4056, 38, 86, 'ئېيتقىنكى، «(اﷲ تەرىپىدىن كەلگەن ۋەھيىنى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچ ھەق تەلەپ قىلمايمەن، مەن (قۇرئاننى) ئىپتىرا قىلغۇچىلاردىن (يەنى يالغاندىن توقۇغۇچىلاردىن) ئەمەسمەن'),
(4057, 38, 87, 'قۇرئان پەقەت جاھان ئەھلى ئۈچۈن ۋەز - نەسىھەتتۇر'),
(4058, 38, 88, 'بىر مۇددەتتىن كېيىن ئۇنىڭ راستلىقىنى چوقۇم بىلىسىلەر»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'بۇ كىتاب غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ تەرىپىدىن نازىل قىلىنغاندۇر'),
(4060, 39, 2, 'ساڭا بىز (بۇ) كىتابنى ھەقىقەتەن ھەق بىلەن (يەنى ھەقىقەتنى ئۆز ئىچىگە ئالغان ھالدا) نازىل قىلدۇق، دىنىڭنى شېرىكتىن (ۋە رىيادىن) ساپ قىلغىنىڭ ھالدا اﷲ قا ئىبادەت قىلغىن'),
(4061, 39, 3, 'بىلىڭلاركى، (شېرىكتىن ۋە رىيادىن) ساپ دىن اﷲ قا خاستۇر، اﷲ نى قويۇپ بۇتلارنى ھىمايىچى قىلىۋالغانلار: «بىزنىڭ ئۇلارغا چوقۇنۇشىمىز پەقەت ئۇلارنىڭ بىزنى اﷲ قا يېقىنلاشتۇرۇشى ئۈچۈندۇر» (دەيدۇ). اﷲ ھەقىقەتەن (قىيامەت كۈنى) ئۇلارنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىدۇ، اﷲ ھەقىقەتەن يالغانچىنى، (اﷲ نىڭ نېمەتلىرىگە) كۇفرىلىق قىلغۇچىنى ھىدايەت قىلمايدۇ'),
(4062, 39, 4, 'ئەگەر اﷲ بالا تۇتۇشنى خالىسا ئىدى، ئەلۋەتتە، مەخلۇقات ئىچىدىن خالىغاننى ئىختىيار قىلاتتى، اﷲ (بالا تۇتۇشتىن) پاكتۇر، ئۇ غالىب بىر اﷲ دۇر'),
(4063, 39, 5, 'اﷲ ئاسمانلارنى ۋە زېمىننى ھەق ئاساسىدا ياراتتى. اﷲ كېچىنى كۈندۈزگە كىرگۈزىدۇ ۋە كۈندۈزنى كېچىگە كىرگۈزىدۇ، اﷲ كۈن بىلەن ئاينى (بەندىلەرنىڭ مەنپەئەتىگە) بويسۇندۇرۇپ بەردى، ئۇلارنىڭ ھەر بىرى مۇئەييەن ۋاقىتقىچە (يەنى قىيامەتكىچە ئۆز ئوقىدا) سەير قىلىدۇ، بىلىڭلاركى، ئۇ (يەنى اﷲ ئۆز ئىشىدا) غالىبتۇر. (بەندىلىرىنىڭ گۇناھلىرىنى تولىمۇ مەغپىرەت قىلغۇچىدۇر'),
(4064, 39, 6, 'اﷲ سىلەرنى بىر ئادەمدىن ياراتتى، ئاندىن ئۇنىڭدىن ئۇنىڭ جۈپتىنى (يەنى ھەۋۋانى) ياراتتى ۋە ھايۋانلاردىن سەككىزنى سىلەر ئۈچۈن ياراتتى، اﷲ سىلەرنى ئاناڭلارنىڭ قارنىدا بىر ھالەتتىن يەنە بىر ھالەتكە تەرەققىي قىلدۇرۇپ (يەنى ئابىمەنىي، ئاندىن لەختە قان، ئاندىن پارچە گۆش باسقۇچلىرىغا بۆلۈپ)، ئۈچ قاراڭغۇلۇق (يەنى بالاھەمراھىنىڭ پەردىسى، بەچچىدان ۋە ئانىنىڭ قورسىقى) ئىچىدە يارىتىدۇ، ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، پادىشاھلىق اﷲ قا خاستۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، قانداقمۇ (ئۇنىڭغا ئىبادەت قىلىشتىن ئۇنىڭدىن غەيرىيگە چوقۇنۇشقا) بۇرۇلۇپ كېتىسىلەر'),
(4065, 39, 7, 'ئەگەر كاپىر بولساڭلار اﷲ سىلەردىن بىھاجەتتۇر، اﷲ بەندىلىرىنىڭ كاپىر بولۇشىغا رازى بولمايدۇ. ئەگەر سىلەر شۈكۈر قىلساڭلار، اﷲ سىلەرنىڭ قىلغان شۈكۈرۈڭلاردىن رازى بولىدۇ، بىر گۇناھكار ئادەم باشقا بىراۋنىڭ گۇناھىنى ئۈستىگە ئالمايدۇ (يەنى ھەر ئادەم قىلغان گۇناھىغا ئۆزى جاۋابكار بولىدۇ)، ئاندىن سىلەر پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتىسىلەر، اﷲ قىلمىشىڭلارنى سىلەرگە ئېيتىپ بېرىدۇ، اﷲ ھەقىقەتەن دىللاردىكى سىرلارنى تولۇق بىلگۈچىدۇر'),
(4066, 39, 8, 'ئىنسانغا بىرەر زىيان - زەخمەت يەتسە، پەرۋەردىگارىغا يۈزلەنگەن ھالدا ئۇنىڭغا دۇئا قىلىدۇ، ئاندىن اﷲ ئۆز تەرىپىدىن ئۇنىڭغا نېمەت ئاتا قىلغان (ئۇنىڭ بېشىغا كەلگەن كۈلپەتنى كۆتۈرۈۋەتكەن) چاغدا، اﷲ قا زىيان - زەخمەتنى كۆتۈرۈۋېتىش توغرۇلۇق ئىلگىرى قىلغان دۇئاسىنى ئۇنتۇپ كېتىدۇ، (باشقىلارنى) اﷲ نىڭ يولىدىن ئازدۇرۇش ئۈچۈن اﷲ قا شېرىكلەرنى كەلتۈرىدۇ، ئېيتقىنكى، «سەن كۇفرىڭ بىلەن تۇرۇپ، (بۇ پانىي دۇنيادىن) ئازغىنا (ۋاقىت) بەھرىمەن بولغىن، سەن چوقۇم دوزىخىلاردىندۇرسەن'),
(4067, 39, 9, '(اﷲ قا شېرىك كەلتۈرگەن ئادەم ياخشىمۇ؟) ياكى ئاخىرەت (ئازابىدىن) قورقۇپ، پەرۋەردىگارىنىڭ رەھمىتىنى ئۈمىد قىلىپ كېچىنىڭ سائەتلىرىنى سەجدە قىلغان ۋە قىيامدا تۇرغان ھالدا ئىبادەت قىلىپ ئۆتكۈزگەن ئادەم (ياخشىمۇ؟)». ئېيتقىنكى، «بىلىدىغانلار بىلەن بىلمەيدىغانلار باراۋەر بولامدۇ؟ پەقەت (ساغلام) ئەقىل ئىگىلىرى ئىبرەت ئالىدۇ»'),
(4068, 39, 10, 'ئېيتقىنكى، «ئى (اﷲ نىڭ) مۆمىن بەندىلىرى! پەرۋەردىگارىڭلارغا تەقۋادارلىق قىلىڭلار، بۇ دۇنيادا ياخشى ئىش قىلغانلار (ئاخىرەتتە) ياخشىلىققا (يەنى جەننەتكە) ئېرىشىدۇ. اﷲ نىڭ يېرى كەڭدۇر (كۇففارلار ئارىسىدىن شۇ يەرگە ھىجرەت قىلىڭلار)، پەقەت سەۋر قىلغۇچىلارغا ئۇلارنىڭ ئەجرى ھېسابسىز بېرىلىدۇ»'),
(4069, 39, 11, 'ئېيتقىنكى، «مەن دىننى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا اﷲ قا ئىبادەت قىلىشقا بۇيرۇلدۇم'),
(4070, 39, 12, 'ھەمدە (بۇ ئۈممەتنىڭ ئىچىدە) مۇسۇلمانلارنىڭ ئەۋۋىلى بولۇشقا بۇيرۇلدۇم»'),
(4071, 39, 13, 'ئېيتقىنكى، «ئەگەر مەن پەرۋەردىگارىمغا ئاسىيلىق قىلسام، شۈبھىسىزكى، بۈيۈك كۈننىڭ ئازابىدىن قورقىمەن (يەنى اﷲ نىڭ ئەمرىگە ئاسىيلىق قىلسام، قىيامەت كۈنى ئۇنىڭ مېنى جەھەننەم ئوتى بىلەن ئازابلىشىدىن قورقىمەن)»'),
(4072, 39, 14, 'ئېيتقىنكى، «دىنىمنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا اﷲ قا ئىبادەت قىلىمەن'),
(4073, 39, 15, 'سىلەر اﷲ تىن باشقا خالىغىنىڭلارغا ئىبادەت قىلىڭلار!» ئېيتقىنكى، «قىيامەت كۈنى ئۆزلىرىنى ۋە ئائىلىسىدىكىلەرنى زىيان تارتقۇزغۇچىلار قاتتىق زىيان تارتقۇچىلاردۇر، بىلىڭلاركى، ئەنە شۇ روشەن زىياندۇر'),
(4074, 39, 16, 'ئۇلارنى ئۈستىدىنمۇ قاتمۇقات ئوت، ئاستىدىنمۇ قاتمۇقات ئوت ئورىۋالىدۇ، شۇ (قاتتىق ئازاب) بىلەن اﷲ بەندىلىرىنى قورقىتىدۇ». ئى بەندىلىرىم! (ئازابىمدىن) قورقۇڭلار'),
(4075, 39, 17, 'شەيتانغا (ۋە بۇتلارغا) چوقۇنۇشتىن يىراق بولغانلارغا، اﷲ قا (ۋە ئۇنىڭ ئىبادىتىگە) قايتقانلارغا (جەننەت بىلەن) خۇش خەۋەر بېرىلىدۇ، سۆزگە قۇلاق سېلىپ ئۇنىڭ ئەڭ ياخشىسىغا ئەگىشىدىغانلارغا (يەنى تەقۋادار بەندىلىرىمگە) خۇش خەۋەر بەرگىن، ئەنە شۇلار اﷲ ھىدايەت قىلغان كىشىلەردۇر، ئەنە شۇلار (ساغلام) ئەقىل ئىگىلىرىدۇر'),
(4076, 39, 18, 'شەيتانغا (ۋە بۇتلارغا) چوقۇنۇشتىن يىراق بولغانلارغا، اﷲ قا (ۋە ئۇنىڭ ئىبادىتىگە) قايتقانلارغا (جەننەت بىلەن) خۇش خەۋەر بېرىلىدۇ، سۆزگە قۇلاق سېلىپ ئۇنىڭ ئەڭ ياخشىسىغا ئەگىشىدىغانلارغا (يەنى تەقۋادار بەندىلىرىمگە) خۇش خەۋەر بەرگىن، ئەنە شۇلار اﷲ ھىدايەت قىلغان كىشىلەردۇر، ئەنە شۇلار (ساغلام) ئەقىل ئىگىلىرىدۇر'),
(4077, 39, 19, 'كىمگە ئازاب ۋەدىسى تېگىشلىك بولسا (ئۇ چوقۇم دوزاخقا كىرىدۇ)، سەن دوزاختىكى ئادەمنى قۇتقۇزالامسەن؟'),
(4078, 39, 20, 'لېكىن (دۇنيادا) پەرۋەردىگارىدىن قورققانلار (جەننەتتە) قەۋەت - قەۋەت ئالىي ئىمارەتلەردىن بەھرىمەن بولىدۇ، ئۇلارنىڭ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدۇ، (بۇ) اﷲ نىڭ ۋەدىسىدۇر، اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ'),
(4079, 39, 21, 'سەن بىلمەمسەنكى، اﷲ بۇلۇتتىن يامغۇر ياغدۇرىدۇ. ئاندىن ئۇنى يەرگە سىڭدۈرۈپ بۇلاقنى پەيدا قىلىدۇ. ئاندىن اﷲ ئۇنىڭ بىلەن رەڭگى خىلمۇخىل زىرائەتلەرنى ئۈندۈرىدۇ. ئاندىن ئۇ قۇرۇيدۇ، ئۇنىڭ سارغىيىپ كەتكەنلىكىنى كۆرىسەن. ئاندىن اﷲ ئۇنى شاخ - شۇمبىغا ئايلاندۇرىدۇ، ئۇنىڭدا ھەقىقەتەن ئەقىل ئىگىلىرى ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ئىبرەت بار'),
(4080, 39, 22, 'اﷲ كۆكسىنى ئىسلام ئۈچۈن كەڭ قىلغان، پەرۋەردىگارىنىڭ نۇرى ئۈستىدە بولغان ئادەم (دىلى كور ئادەم بىلەن) ئوخشاشمۇ؟ اﷲ نىڭ زىكرىنى تەرك ئەتكەنلىكتىن دىللىرى قېتىپ كەتكەنلەرگە ۋاي! ئەنە شۇلار روشەن گۇمراھلىقتىدۇر'),
(4081, 39, 23, 'اﷲ سۆزلەرنىڭ ئەڭ چىرايلىقى بولغان قۇرئاننى نازىل قىلدى. (پاساھەتتە، بالاغەتتە) ئۇنىڭ بەزىسى بەزىسىگە ئوخشاپ كېتىدۇ، (ۋەز - نەسىھەتلەر، ئەھكاملار، قىسسىلەر ئۇنىڭدا) تەكرارلىنىدۇ، (قۇرئاندىكى ئازاب ئايەتلىرى تىلاۋەت قىلىنغان چاغدا) پەرۋەردىگارىدىن قورقىدىغان كىشىلەرنىڭ بەدەنلىرى تىترەيدۇ، ئاندىن اﷲ نىڭ زىكرى ئۈچۈن (يەنى اﷲ نىڭ رەھمىتى، مەغپىرىتىگە دائىر ئايەتلەر تىلاۋەت قىلىنغان چاغدا)، ئۇلارنىڭ بەدەنلىرى ۋە دىللىرى يۇمشاپ (ئارام تاپىدۇ)، ئەنە شۇ (يەنى قۇرئان) اﷲ نىڭ ھىدايىتىدۇركى، ئۇنىڭ بىلەن (اﷲ) خالىغان ئادەمنى ھىدايەت قىلىدۇ، اﷲ ئازدۇرغان ئادەمگە ھېچ ھىدايەت قىلغۇچى بولمايدۇ'),
(4082, 39, 24, 'قىيامەت كۈنى قاتتىق ئازابنى يۈزى بىلەن مۇداپىئە قىلىدىغان ئادەم (ئازابقا دۇچار بولمايدىغان ئادەم بىلەن ئوخشاشمۇ؟) زالىملارغا (يەنى كاپىرلارغا): «قىلغان گۇناھىڭلارنىڭ جازاسىنى تېتىڭلار» (دېيىلىدۇ)'),
(4083, 39, 25, 'ئۇلاردىن ئىلگىرى ئۆتكەن (ئۈممەت) لەر (پەيغەمبەرلىرىنى) ئىنكار قىلدى، ئويلىمىغان يەردىن ئۇلارغا ئازاب كەلدى'),
(4084, 39, 26, 'اﷲ ئۇلارغا بۇ دۇنيادا خارلىقنىڭ (تەمىنى) تېتىتتى، ئاخىرەتنىڭ ئازابى تېخىمۇ بۈيۈكتۇر، ئۇلار (ئاخىرەتنىڭ ئازابىنى) بىلسە ئىدى (پەيغەمبەرلىرىنى ئىنكار قىلمايتتى)'),
(4085, 39, 27, 'بىز كىشىلەرنى ۋەز - نەسىھەت ئالسۇن دەپ، بۇ قۇرئاندا ئۇلارغا تۈرلۈك مىساللارنى بايان قىلدۇق'),
(4086, 39, 28, 'قۇرئان ئەرەبچىدۇر، ئەگرىلىكتىن خالىدۇر، ئۈمىدكى، ئۇلار (قۇرئاننىڭ مەنىسىنى چۈشىنىپ كۇفرىدىن) ساقلانغاي'),
(4087, 39, 29, 'اﷲ (سىلەرگە مۇنداق) بىر مىسالنى كەلتۈرىدۇ: بىر قۇل بولۇپ، ئۇنىڭغا (ئىشقا سېلىشنى) تالىشىدىغان نۇرغۇن خوجايىنلار ئىگىدارچىلىق قىلىدۇ (بىرسى بۇ ئىشقا، بىرسى ئۇ ئىشقا بۇيرۇيدۇ، ئۇ كىمنى رازى قىلىشنى بىلەلمەي قالىدۇ)، يەنە بىر قۇل بولۇپ، ئۇ بىر ئادەمگىلا خاستۇر، بۇ ئىككى قۇل (يەنى ئۇنىڭ ئەھۋالى) باراۋەرمۇ؟ جىمى ھەمدۇسانا اﷲ قا خاستۇر! بەلكى ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى بىلمەيدۇ'),
(4088, 39, 30, 'سەن ھەقىقەتەن ئۆلىسەن، ئۇلارمۇ ھەقىقەتەن ئۆلىدۇ'),
(4089, 39, 31, 'ئاندىن كېيىن سىلەر قىيامەت كۈنى پەرۋەردىگارىڭلارنىڭ دەرگاھىدا بىر - بىرىڭلار بىلەن دەۋا قىلىشىسىلەر'),
(4090, 39, 32, 'اﷲ قا يالغاننى چاپلىغان، نازىل بولغان راست سۆزنى (يەنى قۇرئاننى) ئىنكار قىلغان ئادەمدىنمۇ زالىم كىشى بارمۇ؟ جەھەننەمدە كاپىرلارغا ئورۇن يوقمۇ؟'),
(4091, 39, 33, 'راست سۆز (يەنى قۇرئان) نى ئېلىپ كەلگەن كىشى ۋە ئۇنى ئېتىراپ قىلغان كىشىلەر - ئەنە شۇلار تەقۋادارلاردۇر'),
(4092, 39, 34, 'ئۇلار پەرۋەردىگارىنىڭ دەرگاھىدا ئۆزلىرى خالىغان نەرسىلەردىن بەھرىمەن بولىدۇ، ئەنە شۇ ياخشى ئىش قىلغۇچىلارنىڭ مۇكاپاتىدۇر'),
(4093, 39, 35, 'اﷲ ئۇلارنىڭ ئەڭ يامان ئەمەللىرىنى يوققا چىقىرىدۇ، ئۇلارغا قىلغان ئەڭ ياخشى ئەمەللىرى بويىچە ساۋاب بېرىدۇ'),
(4094, 39, 36, 'اﷲ بەندىسىگە (يەنى مۇھەممەد ئەلەيھىسسالامنى قوغداشقا ۋە ئۇنىڭغا ياردەم بېرىشكە) يېتەرلىك ئەمەسمۇ؟ ئۇلار سېنى اﷲ تىن باشقا مەبۇدلار بىلەن قورقىتىدۇ، اﷲ گۇمراھ قىلغان ئادەمنى ھېچ ھىدايەت قىلغۇچى بولمايدۇ'),
(4095, 39, 37, 'اﷲ ھىدايەت قىلغان ئادەمنى ھېچ ئازدۇرغۇچى بولمايدۇ، اﷲ غالىب، (دۈشمەنلىرىنى) جازالىغۇچى ئەمەسمۇ؟'),
(4096, 39, 38, 'ئەگەر ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم ياراتقان» دەپ سورىساڭ، شۈبھىسىزكى، ئۇلار: «اﷲ ياراتقان» دەيدۇ. ئېيتقىنكى، «ئەگەر اﷲ ماڭا بىرەر زىيان - زەخمەت يەتكۈزۈشنى ئىرادە قىلسا، ئۇنى دەپئى قىلىشقا، يا اﷲ ماڭا رەھمەت قىلىشنى ئىرادە قىلسا، ئۇنى توسۇۋېلىشقا، اﷲ تىن باشقا سىلەر ئىبادەت قىلىۋاتقان مەبۇدلار قادىر بولالامدۇ؟ ماڭا ئېيتىپ بېرىڭلارچۇ؟» ئېيتقىنكى، «ماڭا اﷲ كۇپايىدۇر، تەۋەككۈل قىلغۇچىلار ئۇنىڭغا تەۋەككۇل قىلسۇن (يەنى ھەممە ئىشنى اﷲ قا تاپشۇرسۇن)»'),
(4097, 39, 39, 'ئېيتقىنكى، «ئى قەۋمىم! سىلەر ئۆز ھالىڭلار بويىچە ئىشلەڭلار، مەنمۇ ئۆز ھالىم بويىچە ئىشلەيمەن، سىلەر ئۇزۇنغا قالماي خارلىغۇچى ئازابنىڭ كىمگە كېلىدىغانلىقىنى، مەڭگۈلۈك ئازابنىڭ كىمگە چۈشىدىغانلىقىنى بىلىسىلەر»'),
(4098, 39, 40, 'ئېيتقىنكى، «ئى قەۋمىم! سىلەر ئۆز ھالىڭلار بويىچە ئىشلەڭلار، مەنمۇ ئۆز ھالىم بويىچە ئىشلەيمەن، سىلەر ئۇزۇنغا قالماي خارلىغۇچى ئازابنىڭ كىمگە كېلىدىغانلىقىنى، مەڭگۈلۈك ئازابنىڭ كىمگە چۈشىدىغانلىقىنى بىلىسىلەر»'),
(4099, 39, 41, 'شۈبھىسىزكى، بىز ساڭا كىتابنى ئىنسانلار ئۈچۈن ھەق بىلەن نازىل قىلدۇق، كىمكى ھىدايەت تاپىدىكەن، ئۆزى ئۈچۈن ھىدايەت تاپقان بولىدۇ، كىمكى ئازىدىكەن، ئۆزىنىڭ زىيىنى ئۈچۈن ئازغان بولىدۇ، سەن ئۇلارغا ھامىي ئەمەسسەن (يەنى ئۇلارنى ئىمانغا زورلاشقا مۇئەككەل ئەمەسسەن)'),
(4100, 39, 42, 'ئىنسانلار ئۆلىدىغان چاغلىرىدا، اﷲ ئۇلارنىڭ جانلىرىنى ئالىدۇ، ئۆلمىگەنلەرنىڭ جانلىرىنى ئۇخلىغان چاغلىرىدا ئالىدۇ، ئۆلۈمگە ھۆكۈم قىلىنغان (يەنى ئەجىلى يەتكەنلەر) نىڭ جانلىرىنى تۇتۇپ قېلىپ، قالغانلارنىڭ جانلىرىنى مۇئەييەن ۋاقىتقىچە (يەنى ئەجىلى يەتكۈچە) قويۇپ بېرىدۇ، بۇنىڭدا پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار'),
(4101, 39, 43, '(ئۇلار پىكىر يۈرگۈزمىدى) بەلكى اﷲ نى قويۇپ، (بۇتلاردىن) شاپائەتچىلەرنى تۇتتى. ئېيتقىنكى، «ئۇلار (يەنى شاپائەتچى قىلىنغان بۇتلار) ھېچ نەرسىگە قادىر بولالمايدىغان ۋە (ھېچ نەرسىنى) سەزمەيدىغان تۇرسىمۇ (ئۇلارنى شاپائەتچى تۇتامدۇ؟)'),
(4102, 39, 44, 'شاپائەتنىڭ ھەممىسى اﷲ نىڭ ئىلكىدىدۇر (ھېچ ئادەم اﷲ نىڭ ئىزنىسىز شاپائەت قىلالمايدۇ)، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر. ئاندىن سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر»'),
(4103, 39, 45, 'يالغۇز اﷲ تىلغا ئېلىنسا، ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ دىللىرى سىقىلىپ كېتىدۇ. اﷲ تىن باشقا مەبۇدلار تىلغا ئېلىنسا، ئۇلار خۇش بولۇپ كېتىدۇ'),
(4104, 39, 46, 'ئېيتقىنكى، «ئى ئاسمانلارنى ۋە زېمىننى ياراتقۇچى، يوشۇرۇننى ۋە ئاشكارىنى بىلگۈچى اﷲ! سەن بەندىلىرىڭنىڭ ئارىسىدا ئۇلار ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە ھۆكۈم چىقىرىسەن»'),
(4105, 39, 47, 'ئەگەر (ئۆزلىرىگە) زۇلۇم قىلغانلارنىڭ ئىگىدارچىلىقىدا يەر يۈزىدىكى نەرسىلەرنىڭ ھەممىسى يەنە شۇنىڭدەك بىر ھەسسە كۆپ نەرسە بولىدىغان بولسا، قىيامەت كۈنىدىكى قاتتىق ئازاب ئۈچۈن ئۇنى فىدىيە بەرگەن بولاتتى، ئۇلارغا اﷲ تەرىپىدىن (بۇ دۇنيادىكى چاغلىرىدا) ئويلاپ باقمىغان ئازابلار ئاشكارا بولىدۇ'),
(4106, 39, 48, 'ئۇلارغا ئۇلارنىڭ (دۇنيادا) قىلغان يامان ئىشىلىرى ئاشكارا بولىدۇ، ئۇلارنىڭ (دۇنيادىكى چاغدا پەيغەمبەرنىڭ دەۋىتىنى) مەسخىرە قىلغانلىقلىرىنىڭ جازاسى ئۈچۈن ئۇلارنى (قاتتىق) ئازاب ئورىۋالىدۇ'),
(4107, 39, 49, 'ئىنسانغا بىرەر مۇسىبەت يەتسە، بىزگە دۇئا قىلىپ (ئىلتىجا قىلىدۇ)، ئاندىن ئۇنىڭغا مەرھەمەت قىلىپ بىرەر نېمىتىمىزنى ئاتا قىلساق، ئۇ: «بۇ نېمەت ماڭا ئۆز ئىلمىمدىن (يەنى پەزلىمدىن، ئەمگىكىمدىن، تىرىشچانلىقىمدىن، رىزىق تېپىشنىڭ يوللىرىنى بىلگەنلىكىمدىن) كەلدى» دەيدۇ. (ئىش ئۇ ئويلىغاندەك ئەمەس) بەلكى ئۇ سىناقتۇر (يەنى نېمەت ئاتا قىلىش بىلەن، ئۇنى ئىتائەت قىلامدۇ؟ ئاسىيلىق قىلامدۇ؟ دەپ سىنايمىز). لېكىن ئۇلارنىڭ تولىسى (بۇنى) بىلمەيدۇ'),
(4108, 39, 50, 'مۇنداق سۆزنى ئۇلاردىن ئىلگىرىكىلەرمۇ قىلغان، ئۇلارنىڭ ئېرىشكەن مال - مۈلۈكلىرى ئۇلارغا ئەسقاتمىدى'),
(4109, 39, 51, 'ئۇلار (دۇنيادا) قىلغان يامانلىقلىرىنىڭ جازاسىنى تارتتى. بۇلارنىڭ ئىچىدىكى زۇلۇم قىلغانلارمۇ قىلغان يامانلىقلىرىنىڭ جازاسىنى تارتىدۇ، ئۇلار ئازابىمىزدىن قۇتۇلالمايدۇ'),
(4110, 39, 52, 'ئۇلار بىلمەمدۇكى، اﷲ (سىناش يۈزىسىدىن) خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ، شەك - شۈبھىسىزكى، بۇنىڭدا ئىمان ئېيتقان قەۋم ئۈچۈن نۇرغۇن ئالامەتلەر بار'),
(4111, 39, 53, '(ئى مۇھەممەد! مېنىڭ تىلىمدىن) ئېيتقىنكى، «(گۇناھلارنى قىلىۋېرىپ) ئۆزلىرىگە جىنايەت قىلغان بەندىلىرىم! اﷲ نىڭ رەھمىتىدىن ئۈمىدسىزلەنمەڭلار. اﷲ ھەقىقەتەن (خالىغان ئادەمنىڭ) جىمى گۇناھلىرىنى مەغپىرەت قىلىدۇ، شۈبھىسىزكى، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(4112, 39, 54, 'سىلەرگە ئازاب كېلىپ، ياردەمگە ئېرىشەلمىگىنىڭلاردىن بۇرۇن پەرۋەردىگارىڭلار تەرەپكە قايتىڭلار ۋە ئۇنىڭغا ئىتائەت قىلىڭلار'),
(4113, 39, 55, 'سىلەر تۇيمىغان ھالەتتە سىلەرگە ئازابنىڭ ئۇشتۇمتۇت كېلىشىدىن ئىلگىرى سىلەرگە نازىل قىلىنغان ئەڭ گۈزەل قۇرئانغا ئەگىشىڭلار»'),
(4114, 39, 56, 'ھەر قانداق ئادەم: «اﷲ نىڭ تائىتىدە كەتكۈزۈپ قويغانلىرىمغا ھەسرەت! مەن (اﷲ نىڭ شەرىئىتىنى ۋە دىنىنى) مەسخىرە قىلغۇچىلاردىن ئىدىم» دەپ قالماسلىق ئۈچۈن'),
(4115, 39, 57, 'ياكى «اﷲ مېنى ھىدايەت قىلغان بولسا (ھىدايەت تېپىپ) ئەلۋەتتە تەقۋادارلاردىن بولاتتىم» دەپ قالماسلىق ئۈچۈن'),
(4116, 39, 58, 'ياكى ئازابنى كۆرگەن چاغدا: «كاشكى دۇنياغا قايتىشىمغا بولسا ئىدى، (ئۇ چاغدا اﷲ قا ئىتائەت قىلىپ) ياخشىلاردىن بولار ئىدىم» دەپ قالماسلىق ئۈچۈن (پەرۋەردىگارىغا بويسۇنسۇن)'),
(4117, 39, 59, 'دۇرۇس، ھەقىقەتەن ساڭا مېنىڭ نۇرغۇن ئايەتلىرىم (يەنى ھىدايەتكە سەۋەب بولىدىغان قۇرئان) كەلدى. سەن ئۇلارنى ئىنكار قىلدىڭ، بويۇنتاۋلىق قىلدىڭ، سەن كاپىرلادىن بولدۇڭ'),
(4118, 39, 60, 'قىيامەت كۈنى اﷲ قا يالغاننى چاپلىغانلارنىڭ يۈزلىرىنى قاپقارا كۆرىسەن، جەھەننەمدە مۇتەكەببىرلەرگە ئورۇن يوقمۇ؟'),
(4119, 39, 61, 'اﷲ (شېرىكتىن ۋە گۇناھتىن) ساقلانغۇچىلارنى بەختكە ئېرىشتۈرىدىغان يوللار ئارقىلىق (ئازابتىن) قۇتۇلدۇرىدۇ، ئۇلارغا كۈلپەت يەتمەيدۇ، ئۇلار قايغۇرمايدۇ'),
(4120, 39, 62, 'اﷲ ھەممە نەرسىنى ياراتقۇچىدۇر، ھەممە نەرسىگە ھامىيدۇر (ئۇلارنى خالىغانچە تەسەررۇپ قىلىدۇ)'),
(4121, 39, 63, 'ئاسمانلارنىڭ ۋە زېمىننىڭ (خەزىنىلىرىنىڭ) ئاچقۇچلىرى اﷲ نىڭ ئىلكىدىدۇر، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلار – ئەنە شۇلار زىيان تارتقۇچىلاردۇر'),
(4122, 39, 64, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئى نادانلار! مېنى اﷲ تىن غەيرىيگە ئىبادەت قىلىشىقا بۇيرۇمسىلەر؟»'),
(4123, 39, 65, 'ساڭا ۋە سەندىن ئىلگىرىكى (پەيغەمبەر) لەرگە: «ئەگەر سەن اﷲ قا شېرىك كەلتۈرسەڭ، سېنىڭ ئەمەلىڭ ئەلۋەتتە بىكار بولۇپ كېتىدۇ، سەن ئەلۋەتتە زىيان تارتقۇچىلاردىن بولۇپ قالىسەن، بەلكى يالغۇز اﷲ قا ئىبادەت قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن» دەپ ۋەھيى قىلىندى'),
(4124, 39, 66, 'ساڭا ۋە سەندىن ئىلگىرىكى (پەيغەمبەر) لەرگە: «ئەگەر سەن اﷲ قا شېرىك كەلتۈرسەڭ، سېنىڭ ئەمەلىڭ ئەلۋەتتە بىكار بولۇپ كېتىدۇ، سەن ئەلۋەتتە زىيان تارتقۇچىلاردىن بولۇپ قالىسەن، بەلكى يالغۇز اﷲ قا ئىبادەت قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن» دەپ ۋەھيى قىلىندى'),
(4125, 39, 67, 'ئۇلار اﷲ نى ھەقىقىي رەۋىشتە تونۇمىدى. قىيامەت كۈنى زېمىن پۈتۈنلەي اﷲ نىڭ چاڭگىلىدا بولىدۇ، ئاسمانلار اﷲ نىڭ ئوڭ قولىدا قاتلىنىپ تۇرىدۇ، اﷲ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە ئۈستۈندۇر'),
(4126, 39, 68, 'سۇر چېلىنغاندا ئاسمانلاردىكى ۋە زېمىندىكى اﷲ خالىغاندىن باشقا مەخلۇقاتلارنىڭ ھەممىسى ئۆلىدۇ، ئاندىن سۇر ئىككىنچى قېتىم چېلىنغاندا ناگاھان ئۇلار تۇرۇپ (نېمىگە بۇيرۇلىدىغانلىقلىرىغا) قاراپ تۇرىدۇ'),
(4127, 39, 69, 'مەھشەرگاھ پەرۋەردىگارىنىڭ نۇرى بىلەن يورۇيدۇ، نامە - ئەماللار ھازىر قىلىنىدۇ، پەيغەمبەرلەر ۋە گۇۋاھچىلار كەلتۈرۈلىدۇ، ئۇلارنىڭ ئارىسىدا ئادىل ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ'),
(4128, 39, 70, 'ھەر ئادەمگە قىلغان ئەمەلىنىڭ ساۋابى تولۇق بېرىلىدۇ، اﷲ ئۇلارنىڭ ئەمەللىرىنى ئوبدان بىلىدۇ'),
(4129, 39, 71, 'كاپىرلار جەھەننەمگە توپ - توپ بولۇپ ھەيدىلىدۇ، ئۇلار جەھەننەمگە يېتىپ كەلگەندە، جەھەننەمنىڭ دەرۋازىلىرى ئېچىلىدۇ، جەھەننەمنى باشقۇرغۇچى پەرىشتىلەر ئۇلارغا: «ئاراڭلاردىن سىلەرگە پەرۋەردىگارىڭلارنىڭ ئايەتلىرىنى سىلەرگە ئوقۇپ بېرىدىغان، بۈگۈنكى ئۇچرىشىشنىڭ بارلىقىدىن ئاگاھلاندۇرىدىغان پەيغەمبەرلەر كەلمىگەنمۇ؟» دەيدۇ. ئۇلار: «شۇنداق (پەيغەمبەرلەر كەلگەن، بىزنى ئاگاھلاندۇرغان)، لېكىن ئازابقا دۇچار بولۇش ھۆكمى (كۇفرى ۋە قىلمىشلىرى تۈپەيلىدىن) كاپىرلارغا تېگىشلىك بولدى» دەيدۇ'),
(4130, 39, 72, '(ئۇلارغا) «جەھەننەمنىڭ دەرۋازىلىرىدىن كىرىڭلار، جەھەننەمدە مەڭگۈ قېلىڭلار، مۇتەكەببىرلارنىڭ جايى نېمىدېگەن يامان!» دېيىلىدۇ'),
(4131, 39, 73, 'پەرۋەردىگارىغا تەقۋادارلىق قىلغانلار جەننەتكە توپ - توپ بولغان ھالدا ماڭدۇرۇلىدۇ، ھالبۇكى، ئۇلار جەننەتكە يېتىپ كەلگەن چاغدا ئۇنىڭ دەرۋازىلىرى ئېچىلىپ بولغان بولىدۇ، جەننەتكە مۇئەككەل پەرىشتىلەر ئۇلارغا: «سىلەرگە ئامانلىق بولسۇن، سىلەر (گۇناھلارنىڭ كىرلىرىدىن) پاك بولدۇڭلار، جەننەتكە كىرىڭلار، (ئۇنىڭدا) مەڭگۈ قېلىڭلار» دەيدۇ'),
(4132, 39, 74, 'ئۇلار (جەننەتكە كىرىپ ئورۇنلاشقان چاغدا) ئېيتىدۇ: «جىمى ھەمدۇسانا اﷲ قا خاستۇركى، ئۇ بىزگە قىلغان ۋەدىسىنى ئىشقا ئاشۇردى، جەننەتنىڭ زېمىنىغا بىزنى ۋارىس قىلدى. جەننەتتە ئۆزىمىز خالىغان جايدا تۇرىمىز، ئىشلىگۈچىلەرنىڭ ئەجرى (يەنى جەننەت) نېمىدېگەن ياخشى!»'),
(4133, 39, 75, 'پەرىشتىلەرنىڭ پەرۋەردىگارىغا تەسبىھ ئېيتقان، ھەمدى ئېيتقان ھالدا ئەرشنىڭ چۆرىسىنى ئوراپ تۇرغانلىقىنى كۆرىسەن، ئۇلارنىڭ ئارىسىدا ھەققانىي ھۆكۈم چىقىرىلىدۇ (مۆمىنلەر جەننەتكە كىرىدۇ، كاپىرلار دوزاخقا كىرىدۇ)، «جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!» دېيىلىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حامىم'),
(4135, 40, 2, '(بۇ) كىتاب (يەنى قۇرئان) غالىب، ھەممىنى بىلگۈچى اﷲ تەرىپىدىن نازىل قىلىنغاندۇر'),
(4136, 40, 3, '(اﷲ) گۇناھنى مەغپىرەت قىلغۇچىدۇر، تەۋبىنى قوبۇل قىلغۇچىدۇر. (اﷲ نىڭ) ئازابى قاتتىقتۇر، (اﷲ) ئىنئام ئىگىسىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئاقىۋەت قايتىدىغان جاي ئۇنىڭ دەرگاھىدۇر'),
(4137, 40, 4, 'اﷲ نىڭ ئايەتلىرى (يەنى قۇرئان) توغرىسىدا پەقەت كاپىرلار مۇجادىلە قىلىشىدۇ، ئۇلارنىڭ شەھەرلەردە بېرىپ - كېلىپ يۈرۈشلىرىگە مەغرۇر بولۇپ كەتمىگىن'),
(4138, 40, 5, 'ئۇلار (يەنى مەككە كاپىرلىرى) دىن بۇرۇن، نۇھنىڭ قەۋمى ۋە ئۇلاردىن كېيىنكى (پەيغەمبەرلىرىگە قارشى ئۇيۇشقان ئاد ۋە سەمۇدقا ئوخشاش) جامائەلار (پەيغەمبەرلىرىنى) ئىنكار قىلدى. (پەيغەمبەرلىرىنى ئىنكار قىلغۇچى ئۈممەتلەردىن) ھەر ئۈممەت ئۆزلىرىنىڭ پەيغەمبىرىنى ھالاك قىلىشنى قەستلىدى. ھەقنى بىكار قىلىش ئۈچۈن باتىل (سۆزلەر ئارقىلىق پەيغەمبەرلىرى بىلەن) مۇنازىرە قىلىشتى، شۇنىڭ بىلەن ئۇلارنى ھالاك قىلدىم، مېنىڭ ئازابىم قانداق ئىكەن؟'),
(4139, 40, 6, 'شۇنىڭدەك (يەنى ئىلگىرىكى ئۈممەتلەرگە تېگىشلىك بولغاندەك) پەرۋەردىگارىڭنىڭ ئازابى كاپىرلارغا تېگىشلىك بولدى. ئۇلار دوزاخ ئەھلىدۇر'),
(4140, 40, 7, 'ئەرشنى كۆتۈرۈپ تۇرغان ۋە ئەرشنىڭ چۆرىسىدىكى پەرىشتىلەر پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمدى ئېيتىدۇ، ئۇنىڭغا ئىمان ئېيتىدۇ، مۆمىنلەرگە مەغپىرەت تىلەيدۇ. (ئۇلار ئېيتىدۇ) «پەرۋەردىگارىمىز! سېنىڭ رەھمىتىڭ ۋە ئىلمىڭ ھەممە نەرسىنى ئۆز ئىچىگە ئالىدۇ، تەۋبە قىلغانلارغا ۋە سېنىڭ يولۇڭغا ئەگەشكەنلەرگە مەغپىرەت قىلغىن، ئۇلارنى دوزاخ ئازابىدىن ساقلىغىن'),
(4141, 40, 8, 'پەرۋەردىگارىمىز! سەن ئۇلارنى، ئۇلارنىڭ ئاتا - بوۋىلىرىدىن، ئاياللىرىدىن، ئەۋلادلىرىدىن ياخشى بولغانلارنى ئۇلارغا ۋەدە قىلغان مەڭگۈلۈك جەننەتكە كىرگۈزگىن، سەن ھەقىقەتەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن'),
(4142, 40, 9, 'پەرۋەردىگارىمىز! سەن ئۇلارنى يامان ئىشلاردىن ساقلىغىن، سەن كىمنى يامان ئىشلاردىن ساقلايدىكەنسەن، شۈبھىسىزكى، بۇ كۈندە سەن ئۇنىڭغا رەھمەت قىلغان بولىسەن، بۇ چوڭ بەختتۇر»'),
(4143, 40, 10, 'شۈبھىسىزكى، كاپىرلارغا: «اﷲ نىڭ (دۇنيادا) سىلەرگە بولغان غەزىپى سىلەرنىڭ (ئاخىرەتتە) بىر - بىرىڭلارغا بولغان غەزىپىڭلاردىن ئېشىپ چۈشىدۇ، چۈنكى سىلەر ئىمانغا دەۋەت قىلىناتتىڭلار، لېكىن ئىمان ئېيتمايتتىڭلار» دەپ نىدا قىلىنىدۇ'),
(4144, 40, 11, 'ئۇلار: «پەرۋەردىگارىمىز! بىزنى ئىككى قېتىم ئۆلتۈرۈپ، ئىككى قېتىم تىرىلدۈردۈڭ، بىز گۇناھىمىزغا ئىقرار قىلدۇق، ئەمدى چىقىش يولى تېپىلارمۇ؟» دەيدۇ'),
(4145, 40, 12, 'بۇ شۇنىڭ ئۈچۈندۇركى، سىلەر اﷲ نى بىر دەپ بىلىشكە چاقىرىلغاندا (بۇنى) ئىنكار قىلدىڭلار، ئەگەر اﷲ قا شېرىك كەلتۈرۈلسە (يەنى لات، ئۇززا قاتارلىق بۇتلارغا چوقۇنۇشقا چاقىرىلساڭلار) ئۇنى تەستىق قىلىسىلەر (يەنى ئۇلارنىڭ ئىلاھلىقىنى ئېتىراپ قىلىسىلەر)، ھۆكۈم يۈكسەك، بۈيۈك اﷲ قا خاستۇر'),
(4146, 40, 13, 'اﷲ سىلەرگە (قۇدرىتىنىڭ) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەرگە كۆكتىن يامغۇر بىلەن رىزىق چۈشۈرۈپ بېرىدۇ، پەقەت اﷲ نىڭ تائىتىگە قايتقان كىشىلەرلا (اﷲ نىڭ ئايەتلىرىدىن) ۋەز - نەسىھەت ئالىدۇ'),
(4147, 40, 14, 'دىنىڭلارنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا اﷲ قا ئىبادەت قىلىڭلار، كاپىرلار، ياقتۇرمىغان تەقدىردىمۇ'),
(4148, 40, 15, 'اﷲ نىڭ مەرتىۋىسى يۇقىرىدۇر، (اﷲ) ئەرشنىڭ ئىگىسىدۇر، (خالايىق) مۇلاقات بولۇشىدىغان كۈن (يەنى قىيامەت كۈنى) دىن ئاگاھلاندۇرۇش ئۈچۈن، اﷲ ئۆز ھۆكمى بويىچە بەندىلىرىدىن خالىغان ئادەمگە ۋەھيى چۈشۈرىدۇ'),
(4149, 40, 16, 'ئۇلار قەبرىلەردىن چىققان كۈندە اﷲ قا ئۇلاردىن (يەنى ئۇلارنىڭ ئەھۋالىدىن، ئەمەللىرىدىن، سىرلىرىدىن) ھېچ نەرسە مەخپىي قالمايدۇ، بۈگۈن پادىشاھلىق كىمگە خاس؟ غالىب بىر اﷲ قا خاستۇر'),
(4150, 40, 17, 'بۈگۈن ھەر ئادەمگە قىلغان ئەمەلىگە يارىشا جازا بېرىلىدۇ، بۈگۈن زۇلۇم يوقتۇر، اﷲ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر'),
(4151, 40, 18, 'ئۇلارنى قىيامەت كۈنىدىن ئاگاھلاندۇرغىن، ئۇ چاغدا دىللار (قورقۇنچنىڭ قاتتىقلىقىدىن) بوغۇزلارغا قاپلىشىۋالىدۇ، ئۇلار قايغۇ - ھەسرەتكە تولىدۇ، زالىملارغا يېقىن دوست ۋە شاپائىتى قوبۇل قىلىنىدىغان شاپائەتچى بولمايدۇ'),
(4152, 40, 19, 'اﷲ كۆزلەرنىڭ خىيانىتىنى ۋە دىللاردىكى يوشۇرۇن نەرسىلەرنى بىلىپ تۇرىدۇ'),
(4153, 40, 20, 'اﷲ ئادىللىق بىلەن ھۆكۈم چىقىرىدۇ، ئۇلار اﷲ نى قويۇپ ئىبادەت قىلىۋاتقان بۇتلار ھېچ نەرسىگە ھۆكۈم چىقىرىشقا قادىر ئەمەس، اﷲ ھەقىقەتەن (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر'),
(4154, 40, 21, 'ئۇلار زېمىندا سەير قىلىپ، ئۇلاردىن ئىلگىرى ئۆتكەنلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ ئۇلار بولسا زېمىندا كۈچ - قۇۋۋەت ۋە يادىكارلىقلار (يەنى ئۆي - ئىمارەت قۇرۇش) جەھەتتە بۇلار (يەنى سېنىڭ قەۋمىڭنىڭ كاپىرلىرى) دىن ئۈستۈن ئىدى، گۇناھى تۈپەيلىدىن اﷲ ئۇلارنى جازالىدى، ئۇلارنى ھېچ ئەھەدى اﷲ نىڭ ئازابىدىن ساقلاپ قالالمىدى'),
(4155, 40, 22, 'بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەرنى ئېلىپ كەلگەن ئىدى، ئۇلار ئىشەنمىدى، شۇنىڭ بىلەن اﷲ ئۇلارنى جازالىدى. اﷲ ھەقىقەتەن كۈچلۈكتۇر، (اﷲ نىڭ) ئازابى قاتتىقتۇر'),
(4156, 40, 23, 'بىز مۇسانى مۆجىزىلىرىمىز بىلەن، روشەن دەلىل بىلەن ھەقىقەتەن پىرئەۋنگە، ھامانغا ۋە قارۇنغا ئەۋەتتۇق، ئۇلار (مۇسانى) يالغانچى، سېھىرگەر دېيىشتى'),
(4157, 40, 24, 'بىز مۇسانى مۆجىزىلىرىمىز بىلەن، روشەن دەلىل بىلەن ھەقىقەتەن پىرئەۋنگە، ھامانغا ۋە قارۇنغا ئەۋەتتۇق، ئۇلار (مۇسانى) يالغانچى، سېھىرگەر دېيىشتى'),
(4158, 40, 25, 'مۇسا ئۇلارغا دەرگاھىمىزدىن ھەق (يەنى پەيغەمبەرلىك) بىلەن كەلگەندە، ئۇلار: «مۇسا بىلەن ئىمان ئېيتقانلارنىڭ ئوغۇللىرىنى (نەسلىنى قۇرۇتۇش ئۈچۈن) ئۆلتۈرۈڭلار، ئاياللىرىنى (خىزمەتكە سېلىش ئۈچۈن) قالدۇرۇڭلار» دېدى. كاپىرلارنىڭ ھىيلە - مىكرى پەقەت بەربات بولىدۇ'),
(4159, 40, 26, 'پىرئەۋن ئېيتتى: «مېنى قويۇۋېتىڭلار، مۇسانى ئۆلتۈرىمەن، مۇسا (ئۆزىنى مەندىن قۇتۇلدۇرۇش ئۈچۈن) پەرۋەردىگارىنى چاقىرسۇن، مەن ھەقىقەتەن مۇسانىڭ دىنىڭلارنى ئۆزگەرتىۋېتىشىدىن ياكى زېمىندا (يەنى دۆلەتتە) قالايمىقانچىلىق تۇغدۇرۇشىدىن قورقىمەن»'),
(4160, 40, 27, 'مۇسا ئېيتتى: «مەن ھەقىقەتەن مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار (بولغان اﷲ) غا سېغىنىپ، ھېساب كۈنى (يەنى قىيامەت كۈنى) گە ئىشەنمەيدىغان ھەر بىر مۇتەكەببىر (نىڭ زىيانكەشلىك قىلىشى) دىن پاناھ تىلەيمەن»'),
(4161, 40, 28, 'پىرئەۋن خانىدانىغا مەنسۇپ، ئىمانىنى يوشۇرىدىغان بىر مۆمىن ئادەم ئېيتتى: «سىلەر \'پەرۋەردىگارىم اﷲ\' دېگەن كىشىنى ئۆلتۈرەمسىلەر؟ ھالبۇكى، ئۇ سىلەرگە ھەقىقەتەن پەرۋەردىگارىڭلارنىڭ دەرگاھىدىن روشەن دەلىللەرنى ئېلىپ كەلدى. ئەگەر ئۇ (پەيغەمبەرلىك دەۋاسىدا) يالغانچى بولسا، يالغانچىلىقنىڭ زىيىنى ئۇنىڭ ئۆزىگىدۇر، ئەگەر ئۇ راستچىل بولسا، ئۇ سىلەرنى قورقۇتقان ئازابنىڭ بىر قىسمى سىلەرگە كېلىدۇ، اﷲ ھەقىقەتەن ھەددىدىن ئاشقان يالغانچىنى ھىدايەت قىلمايدۇ'),
(4162, 40, 29, 'ئى قەۋمىم! بۈگۈن سەلتەنەت سىلەرنىڭ ئىلگىڭلاردىدۇر، (مىسىر) زېمىنىدا سىلەر غالىبسىلەر، ئەگەر بىزگە اﷲ نىڭ ئازابى كەلسە، ئۇنىڭدىن قۇتۇلۇشقا بىزگە كىم ياردەم بېرىدۇ؟» پىرئەۋن ئېيتتى: «مەن سىلەرگە پەقەت نېمىنى توغرا دەپ قارىسام، شۇنى مەسلىھەت بېرىمەن. سىلەرنى مەن پەقەت توغرا يولغا باشلايمەن»'),
(4163, 40, 30, 'مۆمىن ئادەم ئېيتتى: «ئى قەۋمىم! سىلەرنىڭ نۇھ، ئاد، سەمۇد قەۋملىرىگە ئوخشاش ۋە ئۇلاردىن كېيىنكىلەرگە ئوخشاش جامائەلەر ئۇچرىغان جازاغا ئۇچرىشىڭلاردىن قورقىمەن، اﷲ بەندىلىرىگە زۇلۇم قىلىشنى خالىمايدۇ'),
(4164, 40, 31, 'مۆمىن ئادەم ئېيتتى: «ئى قەۋمىم! سىلەرنىڭ نۇھ، ئاد، سەمۇد قەۋملىرىگە ئوخشاش ۋە ئۇلاردىن كېيىنكىلەرگە ئوخشاش جامائەلەر ئۇچرىغان جازاغا ئۇچرىشىڭلاردىن قورقىمەن، اﷲ بەندىلىرىگە زۇلۇم قىلىشنى خالىمايدۇ'),
(4165, 40, 32, 'ئى قەۋمىم! مەن سىلەر ئۈچۈن ھەقىقەتەن قىيامەت كۈنىدىن ئەنسىرەيمەن'),
(4166, 40, 33, 'ئۇ كۈندە (دوزاخ ئازابىنىڭ دەھشىتىدىن قورقۇپ) ئارقاڭلارغا چېكىنىسىلەر، سىلەرگە ھېچ اﷲ نىڭ ئازابىدىن قۇتۇلدۇرغۇچى بولمايدۇ. كىمكى اﷲ ئۇنى گۇمراھ قىلىدىكەن، ئۇنى ھىدايەت قىلغۇچى بولمايدۇ'),
(4167, 40, 34, 'شەك - شۈبھىسىزكى، ئىلگىرى سىلەرگە يۇسۇف (اﷲ نىڭ دەرگاھىدىن) روشەن مۆجىزىلەرنى ئېلىپ كەلدى. ئۇ ئېلىپ كەلگەن دەلىلدىن سىلەر ھامان شەكلەندىڭلار، تاكى ئۇ ۋاپات بولغاندا، (دەلىل - پاكىتسىز) ھالدا، اﷲ ئۇنىڭدىن كېيىن ھەرگىز پەيغەمبەر ئەۋەتمەيدۇ\' دېدىڭلار، اﷲ (گۇناھتا) ھەددىدىن ئاشقۇچىنى، (دىندا) شەكلەنگۈچىنى مۇشۇنداق ئازدۇرىدۇ»'),
(4168, 40, 35, 'اﷲ نىڭ ئايەتلىرى ئۈستىدە ئۆزلىرىگە (اﷲ تەرىپىدىن) كەلگەن ھېچقانداق دەلىل بولماستىن (قارىسىغا) جاڭجاللىشىدىغانلار اﷲ نىڭ دەرگاھىدا ۋە مۆمىنلەرنىڭ نەزىرىدە قاتتىق نەپرەتكە قالىدۇ، بۇزغۇنچىلىق قىلىپ (ئىماندىن باش تارتقان)، (بەندىلەرگە) زومىگەرلىك قىلغان ھەر قانداق ئادەمنىڭ دىلىنى اﷲ مۇشۇنداق پېچەتلىۋېتىدۇ'),
(4169, 40, 36, 'پىرئەۋن (ۋەزىرى ھامانغا): «ئى ھامان! ماڭا بىر ئېگىز بىنا سالغىن، ئۇنىڭ بىلەن مەن دەرۋازىلارغا–ئاسماننىڭ دەرۋازىلىرىغا يېتىشىم مۇمكىن، شۇنىڭ بىلەن مەن مۇسانىڭ ئىلاھىنى كۆرەي، مەن ھەقىقەتەن مۇسانى يالغانچى دەپ گۇمان قىلىمەن» دېدى. پىرئەۋنگە ئۇنىڭ يامان ئەمەلى شۇنداق چىرايلىق كۆرسىتىلدى، پىرئەۋن (گۇمراھلىقى تۈپەيلىدىن ھىدايەت) يولىدىن مەنئى قىلىندى، پىرئەۋننىڭ ھىيلە - مىكرى پەقەت بىكاردۇر'),
(4170, 40, 37, 'پىرئەۋن (ۋەزىرى ھامانغا): «ئى ھامان! ماڭا بىر ئېگىز بىنا سالغىن، ئۇنىڭ بىلەن مەن دەرۋازىلارغا–ئاسماننىڭ دەرۋازىلىرىغا يېتىشىم مۇمكىن، شۇنىڭ بىلەن مەن مۇسانىڭ ئىلاھىنى كۆرەي، مەن ھەقىقەتەن مۇسانى يالغانچى دەپ گۇمان قىلىمەن» دېدى. پىرئەۋنگە ئۇنىڭ يامان ئەمەلى شۇنداق چىرايلىق كۆرسىتىلدى، پىرئەۋن (گۇمراھلىقى تۈپەيلىدىن ھىدايەت) يولىدىن مەنئى قىلىندى، پىرئەۋننىڭ ھىيلە - مىكرى پەقەت بىكاردۇر'),
(4171, 40, 38, '(پىرئەۋن خاندانىدىن) ئىمان ئېيتقان كىشى ئېيتتى: «ئى قەۋمىم! سىلەر ماڭا ئەگىشىڭلار، مەن سىلەرنى توغرا يولغا باشلايمەن'),
(4172, 40, 39, 'ئى قەۋمىم! بۇ دۇنيا تىرىكچىلىكى پەقەت (ۋاقىتلىق) پايدىلىنىشتىن ئىبارەتتۇر، ئاخىرەت ھەقىقەتەن (مەڭگۈلۈك) قارارگاھتۇر'),
(4173, 40, 40, 'كىمكى (بۇ دۇنيادا) بىرەر يامانلىقنى قىلىدىكەن، ئۇنىڭغا (ئاخىرەتتە) شۇنىڭغا لايىق جازا بېرىلىدۇ، مەيلى ئەر بولسۇن، مەيلى ئايال بولسۇن، كىمكى ئۆزى مۆمىن تۇرۇپ بىرەر ياخشى ئىش قىلىدىكەن، ئەنە شۇلار جەننەتكە كىرىدۇ، جەننەتتە ئۇلارغا ھېسابسىز رىزىق بېرىلىدۇ'),
(4174, 40, 41, 'ئى قەۋمىم! ماڭا نېمىدۇركى، سىلەرنى مەن (دوزاختىن) قۇتۇلۇشقا دەۋەت قىلىمەن، سىلەر بولساڭلار مېنى دوزاخقا دەۋەت قىلىسىلەر'),
(4175, 40, 42, 'سىلەر مېنى اﷲ نى ئىنكار قىلىشقا، مەن بىلمەيدىغان نەرسىلەرنى ئۇنىڭغا شېرىك كەلتۈرۈشكە دەۋەت قىلىسىلەر، ھالبۇكى، مەن سىلەرنى غالىب، (بەندىلەرنىڭ گۇناھلىرىنى) ناھايىتى مەغپىرەت قىلغۇچى (اﷲ) غا دەۋەت قىلىمەن'),
(4176, 40, 43, 'ھەقىقەتەن سىلەر مېنى ئۇنىڭغا (يەنى بۇتقا) ئىبادەت قىلشىقا چاقىرىسىلەر، ئۇنىڭ دۇنيا ۋە ئاخىرەتتە (ئۆزىگە ئىبادەت قىلىشقا) چاقىرغىنى يوق، بىزنىڭ ئاخىر بارىدىغان جايىمىز اﷲ نىڭ دەرگاھىدۇر، (گۇمراھلىقتا) ھەددىدىن ئاشقۇچىلار ئەھلى دوزاختۇر'),
(4177, 40, 44, '(سىلەرگە ئازاب نازىل بولغان چاغدا) سىلەرگە ئېيتقان سۆزۈمنىڭ (راستلىقىنى) ئەسلەيسىلەر، مەن ئىشىمنى اﷲ قا تاپشۇرىمەن، اﷲ ھەقىقەتەن بەندىلىرىنى كۆرۈپ تۇرغۇچىدۇر»'),
(4178, 40, 45, 'اﷲ ئۇنى پىرئەۋن جامائەسىنىڭ سۇيىقەستلىرىدىن ساقلىدى. پىرئەۋن جامائەسىگە ئەڭ يامان ئازاب نازىل بولدى'),
(4179, 40, 46, 'ئۇلار ئەتىگىنى - ئاخشىمى ئوتقا توغرىلىنىپ تۇرىدۇ، قىيامەت قايىم بولغان كۈندە: «ئى پىرئەۋن جامائەسى! ئازابنىڭ ئەڭ قاتتىقىغا (دوزاخ ئوتىغا) كىرىڭلار!» (دېيىلىدۇ)'),
(4180, 40, 47, 'ئۇ چاغدا ئۇلار دوزاختا ئۆزئارا مۇنازىرىلىشىدۇ، ئاجىز (ئەگەشكۈچىلەر) ھاكاۋۇر (ئەگەشتۈرگۈچىلىرىگە): «شۈبھىسىزكى، بىز سىلەرگە ئەگەشكەن ئىدۇق، بىزدىن دوزاخ ئازابىدىن بىر قىسىمىنى ئېلىپ تاشلىيالامسىلەر؟» دەيدۇ'),
(4181, 40, 48, 'ھاكاۋۇر (ئەگەشتۈرگۈچىلىرى): «شۈبھىسىزكى، بىز ھەممىمىز دوزاختادۇرمىز، اﷲ ھەقىقەتەن بەندىلەرنىڭ ئارىسىدا ھۆكۈم چىقاردى» دەيدۇ'),
(4182, 40, 49, 'دوزاختىكىلەر دوزاخقا مۇئەككەل پەرىشتىلەرگە: «سىلەر پەرۋەردىگارىڭلارغا دۇئا قىلىڭلار، ئۇ بىزدىن بىر كۈنلۈك ئازابنى يېنىكلەتسە ئىكەن» دەيدۇ'),
(4183, 40, 50, 'ئۇلار (يەنى دوزاخقا مۇئەككەل پەرىشتىلەر): «پەيغەمبىرىڭلار سىلەرگە روشەن مۆجىزىلەرنى ئېلىپ كەلمىگەنمىدى؟» دەيدۇ. ئۇلار (يەنى كۇففارلار): «ھەئە، ئېلىپ كەلگەن ئىدى» دەيدۇ. ئۇلار (يەنى پەرىشتىلەر): «سىلەر دۇئا قىلىڭلار، (لېكىن) كاپىرلارنىڭ دۇئاسى پايدىسىزدۇر» دەيدۇ'),
(4184, 40, 51, 'شەك - شۈبھىسىزكى، بىز پەيغەمبىرىمىزگە، مۆمىنلەرگە ھاياتىي دۇنيادا ۋە (پەرىشتە، پەيغەمبەر ۋە مۆمىنلەردىن بەندىلەرنىڭ ئەمەللىرىگە گۇۋاھ بولىدىغان) گۇۋاھچىلار ھازىر بولىدىغان كۈندە ئەلۋەتتە ياردەم بېرىمىز'),
(4185, 40, 52, 'ئۇ كۈندە كاپىرلارنىڭ ئۆزرىلىرى پايدا بەرمەيدۇ، ئۇلار لەنەتكە ئۇچرايدۇ، ئۇلار ئاخىرەتنىڭ ئازابىغا دۇچار بولىدۇ'),
(4186, 40, 53, 'شۈبھىسىزكى، بىز مۇساغا ھىدايەت ئاتا قىلدۇق، ئىسرائىل ئەۋلادىغا كىتاب (يەنى تەۋرات) نى مىراس قىلىپ بەردۇق'),
(4187, 40, 54, '(ئۇ) ئەقىل ئىگىلىرى ئۈچۈن ھىدايەتتۇر ۋە نەسىھەتتۇر'),
(4188, 40, 55, '(ئى مۇھەممەد! مۇشرىكلارنىڭ يەتكۈزگەن ئەزىيەتلىرىگە) سەۋر قىلغىن، اﷲ نىڭ (ساڭا ۋە سېنىڭ تەۋەلىرىڭگە ياردەم بېرىش) ۋەدىسى ھەقىقەتەن ھەقتۇر، گۇناھىڭغا ئىستىغپار ئېيتقىن، ئاخشىمى - ئەتىگىنى پەرۋەردىگارىڭغا ھەمدى بىلەن تەسبىھ ئېيتقىن'),
(4189, 40, 56, 'شۈبھىسىزكى، اﷲ نىڭ ئايەتلىرى ئۈستىدە ئۆزلىرىگە (اﷲ تەرىپىدىن) كەلگەن ھېچقانداق دەلىل بولماستىن (قارىسىغا) جاڭجاللىشىدىغانلارنىڭ كۆكرەكلىرىدە پەقەت كىبرى بار، ئۇلار (اﷲ نىڭ نۇرىنى ئۆچۈرۈشتىن ئىبارەت) مەقسىتىگە يېتەلمەيدۇ، اﷲ قا سېغىنىپ (ئۇلارنىڭ شەررىدىن پاناھ تىلىگىن)، ھەقىقەتەن اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر'),
(4190, 40, 57, 'ئاسمانلارنى ۋە زېمىننى يارىتىش ئەلۋەتتە ئىنسانلارنى يارىتىشتىن كۆپ قىيىندۇر ۋە لېكىن ئىنسانلارنىڭ كۆپچىلىكى (بۇنى) ئۇقمايدۇ'),
(4191, 40, 58, 'ئەما بىلەن كۆزى كۆرۈدىغان ئادەم (يەنى مۆمىن بىلەن كاپىر) باراۋەر بولمايدۇ، ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان ئادەملەر بىلەن يامان ئىش قىلغۇچى ئادەملەر باراۋەر بولمايدۇ. سىلەر ئاز ۋەز - نەسىھەت ئالىسىلەر'),
(4192, 40, 59, 'قىيامەت چوقۇم كېلىدۇ، ئۇنىڭدا شەك يوق ۋە لېكىن ئىنسانلارنىڭ تولىسى (ئۇنىڭغا) ئىشەنمەيدۇ (شۇنىڭ ئۈچۈن ئۇلار ئۆلگەندىن كېيىن تىرىلىشنى، قىيامەت كۈنى بېرىلىدىغان مۇكاپات بىلەن جازانى ئىنكار قىلىدۇ)'),
(4193, 40, 60, 'پەرۋەردىگارىڭلار ئېيتىدۇ: «ماڭا دۇئا قىلىڭلار، مەن (دۇئايىڭلارنى) قوبۇل قىلىمەن (تىلىگىنىڭلارنى بېرىمەن)، شۈبھىسىزكى، مېنىڭ ئىبادىتىمدىن چوڭچىلىق قىلىپ باش تارتىدىغانلار خار ھالدا جەھەننەمگە كىرىدۇ»'),
(4194, 40, 61, '(كۈندۈزى ئىشلەپ يەتكەن ھارغىنلىقتىن) ئارام ئېلىشىڭلار ئۈچۈن، اﷲ سىلەرگە كېچىنى (قاراڭغۇ قىلىپ) ياراتتى، (تىرىكچىلىك يولىدا ھەرىكەتلىنىشىڭلار ئۈچۈن) كۈندۈزنى يورۇق ياراتتى. اﷲ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر ۋە لېكىن ئىنسانلارنىڭ تولىسى (اﷲ نىڭ ئېھسانىغا شۈكۈر قىلمايدۇ، اﷲ نىڭ مەرھەمىتى ۋە نېمەتلىرىگە نانكورلۇق قىلىدۇ)'),
(4195, 40, 62, 'ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ھەممە نەرسىنى ياراتقۇچىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، قانداقمۇ (اﷲ قا ئىبادەت قىلىشتىن بۇتلارغا ئىبادەت قىلىشقا) بۇرۇلۇپ كېتىسىلەر؟'),
(4196, 40, 63, 'اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلار (ھىدايەتتىن ۋە ھەقىقەتتىن) شۇنداق بۇرۇلۇپ كېتىدۇ (ئى مۇھەممەد! قەۋمىڭنىڭ اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقىغا قايغۇرۇپ كەتمىگىن، ئۇلاردىن ئىلگىرى ئۆتكەنلەر مۇشۇنداق قىلغان)'),
(4197, 40, 64, 'اﷲ سىلەرگە زېمىننى تۇرالغۇ، ئاسماننى بىنا قىلدى. سىلەرنى سۈرەتكە كىرگۈزدى. سۈرىتىڭلارنى چىرايلىق قىلدى. سىلەرگە (تۈرلۈك) پاك نەرسىلەرنى رىزىق قىلىپ بەردى. ئەنە شۇ اﷲ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئالەملەرنىڭ پەرۋەردىگارى اﷲ نىڭ بەرىكىتى بۈيۈكتۇر'),
(4198, 40, 65, 'اﷲ ھاياتتۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، دىنىڭلارنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا ئۇنىڭغا ئىبادەت قىلىڭلار، جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!'),
(4199, 40, 66, '(ئى مۇھەممەد!) ئېيتقىنكى: «پەرۋەردىگارىمدىن ماڭا روشەن دەلىللەر كەلگەن چاغدا، سىلەر اﷲ نى قويۇپ ئىبادەت قىلىۋاتقان بۇتلىرىڭلارغا ئىبادەت قىلىشتىن مەنئى قىلىندىم، ئالەملەرنىڭ پەرۋەردىگارىغا بويسۇنۇشقا بۇيرۇلدۇم»'),
(4200, 40, 67, 'اﷲ سىلەرنى (يەنى ئاتاڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن، (ئۇنىڭ ئەۋلادىنى ئالدى بىلەن) ئابىمەنىيدىن، ئاندىن لەختە قاندىن ياراتتى، ئاندىن ئۇ سىلەرنى (ئاناڭلارنىڭ قارنىدىن) بوۋاق ھالىتىڭلاردا چىقىرىدۇ، ئاندىن (كۈچ - قۇۋۋەت ۋە ئەقىلدە كامالەت يېشى) قىران ۋاقتىڭلارغا يېتىسىلەر، ئاندىن قېرىيسىلەر– بەزىڭلار بۇرۇن ۋاپات بولۇپ كېتىسىلەر–ئاندىن سىلەر مۇئەييەن ۋاقىتقىچە ياشايسىلەر، سىلەر (اﷲ نىڭ قۇدرىتىنىڭ دەلىللىرىنى) چۈشەنگەيسىلەر'),
(4201, 40, 68, 'اﷲ تىرىلدۈرۈشكە ۋە ئۆلتۈرۈشكە قادىر، ئۇ بىرەر ئىشنى (قىلىشنى) ئىرادە قىلسا، ئۇنىڭغا «ۋۇجۇدقا كەل» دەيدۇ - دە، ئۇ ۋۇجۇدقا كېلىدۇ'),
(4202, 40, 69, 'اﷲ نىڭ ئايەتلىرى ئۈستىدە جاڭجاللىشىۋاتقانلارنى كۆرمىدىڭمۇ؟ ئۇلار قانداقمۇ (ھىدايەتتىن گۇمراھلىققا) بۇرۇلۇپ كېتىدۇ؟'),
(4203, 40, 70, 'ئۇلار كىتابنى (يەنى قۇرئاننى) ئىنكار قىلدى، پەيغەمبەرلىرىمىزگە بىز نازىل قىلغان مۆجىزىلەرنى ئىنكار قىلدى. ئۇلار ئۇزاققا قالماي (ئىنكار قىلغانلىقلىرىنىڭ ئاقىۋىتىنى) بىلىدۇ'),
(4204, 40, 71, 'ئۇ چاغدا (يەنى دوزاخقا كىرگەندە) ئۇلارنىڭ بويۇنلىرىدا تاقاقلار ۋە زەنجىرلەر بولىدۇ، ئۇلار قايناقسۇغا سۆرەپ كىرىلىدۇ، ئاندىن ئوتتا كۆيدۈرۈلىدۇ'),
(4205, 40, 72, 'ئۇ چاغدا (يەنى دوزاخقا كىرگەندە) ئۇلارنىڭ بويۇنلىرىدا تاقاقلار ۋە زەنجىرلەر بولىدۇ، ئۇلار قايناقسۇغا سۆرەپ كىرىلىدۇ، ئاندىن ئوتتا كۆيدۈرۈلىدۇ'),
(4206, 40, 73, 'ئاندىن ئۇلارغا: «اﷲ نى قويۇپ شېرىك كەلتۈرگەن بۇتلىرىڭلار قەيەردە؟» دېيىلىدۇ، ئۇلار: «ئۇ بۇتلار ئۆزلىرىنى بىزدىن چەتكە ئالدى، بەلكى بىز ئىلگىرى ھېچنەرسىگە ئىبادەت قىلمايتتۇق» دەيدۇ. اﷲ كاپىرلارنى مۇشۇنداق گۇمراھ قىلىدۇ'),
(4207, 40, 74, 'ئاندىن ئۇلارغا: «اﷲ نى قويۇپ شېرىك كەلتۈرگەن بۇتلىرىڭلار قەيەردە؟» دېيىلىدۇ، ئۇلار: «ئۇ بۇتلار ئۆزلىرىنى بىزدىن چەتكە ئالدى، بەلكى بىز ئىلگىرى ھېچنەرسىگە ئىبادەت قىلمايتتۇق» دەيدۇ. اﷲ كاپىرلارنى مۇشۇنداق گۇمراھ قىلىدۇ'),
(4208, 40, 75, 'بۇ (ئازاب) سىلەرنىڭ زېمىندا ھەقسىز رەۋىشتە (گۇناھ قىلىپ پۇل - مالنى ھارام يوللارغا سەرپ قىلغانلىقىڭلار ئۈچۈن) خۇشال بولغانلىقىڭلار ۋە ھاكاۋۇر بولۇپ كەتكەنلىكىڭلار ئۈچۈندۇر'),
(4209, 40, 76, 'سىلەر دوزاخنىڭ دەرۋازىلىرىدىن كىرىپ ئۇ يەردە مەڭگۈ قېلىڭلار، مۇتەكەببۇرلارنىڭ جايى نېمىدېگەن يامان؟'),
(4210, 40, 77, '(ئى مۇھەممەد! قەۋمىڭنىڭ سېنى ئىنكار قىلغانلىقىغا) سەۋر قىلغىن، اﷲ نىڭ (ئۇلارنى ئازابلاش توغرىسىدىكى) ۋەدىسى ھەقىقەتەن ھەقتۇر، ساڭا بىز ئۇلارغا ۋەدە قىلغان ئازابنىڭ بىر قىسمىنى كۆرسەتسەك (بۇ اﷲ نىڭ ۋەدىسىگە ۋاپا قىلغانلىقىدۇر) ياكى سېنى (ئۇنى كۆرسىتىشتىن بۇرۇن) ۋاپات قىلدۇرساق، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىدۇ، (قىيامەتتە ئۇلار ئازابقا دۇچار بولىدۇ)'),
(4211, 40, 78, 'سەندىن ئىلگىرى بىز ھەقىقەتەن نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدىن ساڭا بايان قىلىپ بەرگەنلىرىمىزمۇ، بايان قىلىپ بەرمىگەنلىرىمىزمۇ بار، ھېچقانداق پەيغەمبەر اﷲ نىڭ ئىزنىسىز بىرەر مۆجىزە كەلتۈرەلمەيدۇ، اﷲ نىڭ ئەمرى (يەنى قىيامەت) كەلگەن چاغدا (كىشىلەرنىڭ ئارىسىدا) ئادالەت بىلەن ھۆكۈم چىقىرىلىدۇ، بۇ چاغدا ھەقكە قارشى تۇرغۇچىلار زىيان تارتىدۇ'),
(4212, 40, 79, 'مىنىشىڭلار ۋە گۆشلىرىنى يېيىشىڭلار ئۈچۈن اﷲ سىلەرگە ھايۋانلارنى ياراتتى'),
(4213, 40, 80, 'ئۇلار سىلەرگە نۇرغۇن پايدا كەلتۈرىدۇ، ئۇلارغا مىنىپ دىلىڭلارغا پۈككەن ھاجىتىڭلاردىن چىقىسىلەر، (قۇرۇقلۇقتا يۈك - تاقلىرىڭلارنى) ئۇلارغا ئارتىسىلەر، (دېڭىزدا) كېمىلەرگە قاچىلايسىلەر'),
(4214, 40, 81, 'اﷲ سىلەرگە (قۇدرىتىنىڭ) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەر اﷲ نىڭ (قۇدرىتىنىڭ) قايسى ئالامەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4215, 40, 82, 'ئۇلار زېمىندا سەير قىلىپ ئۇلاردىن ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزەتمىدىمۇ؟ بۇلاردىن ئۇلارنىڭ سانى كۆپ ئىدى، زېمىندا كۈچ - قۇۋۋەت ۋە يادىكارلىقلار (يەنى ئۆي - ئىمارەت) جەھەتتە بۇلاردىن ئۈستۈن ئىدى. ئۇلارنىڭ قولغا كەلتۈرگەن نەرسىلىرى ئۇلارغا ئەسقاتمىدى'),
(4216, 40, 83, 'ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەر بىلەن كەلگەندە، ئۆزلىرىدە بار ئىلىم بىلەن پەخىرلىنىپ (پەيغەمبەرلەرنى ئىنكار قىلدى ۋە مەسخىرە قىلدى)، ئۇلارغا ئۆزلىرى مەسخىرە قىلغان (ۋە كەلسە دەپ ئالدىرىغان) ئازاب نازىل بولدى'),
(4217, 40, 84, 'ئۇلار بىزنىڭ ئازابىمىزنى كۆرگەن چاغدا: «بىز بىر اﷲ قا ئىمان ئېيتتۇق، بىز بۇرۇن (اﷲ قا) شېرىك قىلغان بۇتلارنى ئىنكار قىلدۇق» دېدى'),
(4218, 40, 85, 'بىزنىڭ ئازابىمىزنى كۆرگەن چاغدا ئېيتقان ئىماننىڭ ئۇلارغا پايدىسى بولمىدى، بۇ (يەنى ئازابنى كۆرگەندە ئېيتقان ئىماننىڭ پايدىلىق بولماسلىقى) اﷲ نىڭ بەندىلىرى ئارىسىدا تۇتقان يولىدۇر، بۇ چاغدا كاپىرلار زىيان تارتقۇچى بولدى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حامىم'),
(4220, 41, 2, '(بۇ) ناھايىتى شەپقەتلىك، مېھرىبان اﷲ تەرىپىدىن نازىل قىلىنغان (ۋەھيىدۇر)'),
(4221, 41, 3, 'ئايەتلىرى ئوچۇق بايان قىلىنغان كىتابتۇر، بىلىدىغان قەۋم ئۈچۈن نازىل بولغان ئەرەبچە قۇرئاندۇر'),
(4222, 41, 4, '(مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچىدۇر، (كاپىرلارنى دوزاخ بىلەن) ئاگاھلاندۇرغۇچىدۇر. ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى (ئۇنىڭ ئايەتلىرىنى پىكىر قىلىشتىن) يۈز ئۆرۈدى. ئۇلار (ئۇنىڭغا) قۇلاق سالمايدۇ'),
(4223, 41, 5, 'ئۇلار (پەيغەمبەرگە): «بىزنىڭ دىللىرىمىز سەن بىزنى دەۋەت قىلغان نەرسىلەردىن پەردىلەنگەن، قۇلاقلىرىمىز ئېغىر، سەن بىلەن بىزنىڭ ئارىمىزدا پەردە (يەنى دىنىي جەھەتتە ئوخشىماسلىق) بار، سەن ئۆز ئىشىڭنى قىل، بىزمۇ ھەقىقەتەن ئۆز ئىشىمىزنى قىلايلى (يەنى سەنمۇ ئۆز دىنىڭ بىلەن بول، بىزمۇ ئۆز دىنىمىز بىلەن بولايلى)» دەيدۇ'),
(4224, 41, 6, 'سەن (ئۇ مۇشرىكلارغا): «مەن سىلەرگە ئوخشاش ئىنسانمەن، ماڭا، ئىلاھىڭلار بىر ئىلاھتۇر، دەپ ۋەھيى قىلىنىدۇ، ئۇنىڭغا (ئىمان ۋە تائەت - ئىبادەت بىلەن) يۈزلىنىڭلار ۋە ئۇنىڭدىن مەغپىرەت تىلەڭلار!» دېگىن، مۇشرىكلارغا ۋاي!'),
(4225, 41, 7, 'ئۇلار زاكات بەرمەيدۇ، ئاخىرەتكە ئىشەنمەيدۇ'),
(4226, 41, 8, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا ئەلۋەتتە ئۈزلۈكسىز ساۋاب بېرىلىدۇ'),
(4227, 41, 9, '(سەن ئۇلارغا) «سىلەر زېمىننى ئىككى كۈندە ياراتقان اﷲ نى ئىنكار قىلامسىلەر ۋە ئۇنىڭغا شېرىكلەر كەلتۈرەمسىلەر؟ ئۇ ئالەملەرنىڭ پەرۋەردىگارىدۇر» دېگىن'),
(4228, 41, 10, 'ئۇ زېمىننىڭ ئۈستىدە نۇرغۇن تاغلارنى ياراتتى، زېمىننى بەرىكەتلىك قىلدى، ئەھلى زېمىننىڭ رىزقىنى بەلگىلىدى، (زېمىننى يارىتىش بىلەن ئاھالىسىنىڭ رىزقىنى بەلگىلەشنى كەم - زىيادىسىز) باراۋەر تۆت كۈندە (تاماملىدى)، (زېمىننىڭ ۋە ئۇنىڭدىكى نەرسىلەرنىڭ يارىتىلىش مۇددىتىنى) سورىغۇچىلار ئۈچۈن (بۇ جاۋابتۇر)'),
(4229, 41, 11, 'ئاندىن ئۇ ئاسماننى (يارىتىشقا) يۈزلەندى، ھالبۇكى، ئۇ تۇمان ھالىتىدە ئىدى، ئاسمان بىلەن زېمىنغا: «ئىختىيارىي ياكى ئىختىيارسىز ھالدا كېلىڭلار (يەنى ئەمرىمنى قوبۇل قىلىڭلار)» دېدى. ئىككىلىسى «ئىختىيارىي كەلدۇق» دېدى'),
(4230, 41, 12, 'اﷲ يەتتە ئاسماننى ئىككى كۈندە ياراتتى. ھەر ئاسماننىڭ ئىشىنى ئۆزىگە بىلدۈردى (يەنى ھەر ئاسماننىڭ ئېھتىياجلىق ئىشلىرىنى ئورۇنلاشتۇردى). ئەڭ تۆۋەنكى ئاسماننى يۇلتۇزلار بىلەن زىننەتلىدۇق ۋە قوغدىدۇق، بۇ ئەنە شۇ غالىب، ھەممىنى بىلگۈچى اﷲ نىڭ تەقدىرىدۇر'),
(4231, 41, 13, 'ئەگەر ئۇلار (بۇ بايانلاردىن كېيىن ئىماندىن) يۈز ئۆرۈسە (سەن ئۇلارغا): «سىلەرنى ئاد ۋە سەمۇد قەۋمىلىرىنىڭ بېشىغا كەلگەن ئازابقا ئوخشاش ئازابتىن ئاگاھلاندۇرىمەن» دېگىن'),
(4232, 41, 14, 'ئۆز ۋاقتىدا ئۇلارغا ئالدى - كەينىدىن (يەنى ھەممە تەرەپتىن) پەيغەمبەرلەر كېلىپ: «بىر اﷲ تىن باشقىغا ئىبادەت قىلماڭلار!» دېسە، ئۇلار: «ئەگەر پەرۋەردىگارىمىز خالىسا، چوقۇم پەرىشتىلەرنى چۈشۈرگەن بولاتتى، بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ھەقىقەتەن ئىنكار قىلىمىز» دېدى'),
(4233, 41, 15, 'ئاد بولسا زېمىندا (اﷲ نىڭ بەندىلىرى ھۇد ۋە ئۇنىڭ بىلەن ئىمان ئېيتقانلارغا) ھەقسىز رەۋىشتە يوغانچىلىق قىلدى. ئۇلار: «كۈچ - قۇۋۋەتتە بىزدىن كىم ئارتۇق؟» دېدى. ئۇلار ئۆزلىرىنى ياراتقان اﷲ نىڭ كۈچ - قۇۋۋەتتە ئۆزلىرىدىن ئۈستۈن ئىكەنلىكىنى بىلمىدىمۇ؟ ئۇلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلدى'),
(4234, 41, 16, 'ئۇلارغا بۇ دۇنيادىلا خار قىلغۇچى ئازابنى تېتىتىشىمىز ئۈچۈن، شۇم كۈنلەردە قاتتىق سوغۇق شامالنى ئەۋەتتۇق، ئاخىرەتنىڭ ئازابى تېخىمۇ خار قىلغۇچىدۇر، ئۇلار (ئازابنى دەپئى قىلىدىغان) ياردەمگە ئېرىشەلمەيدۇ'),
(4235, 41, 17, 'سەمۇدقا بولسا توغرا يولنى كۆرسەتتۇق، ئۇلار ھىدايەتتىن كورلۇق (يەنى گۇمراھلىق) نى ئارتۇق بىلدى. قىلمىشلىرى تۈپەيلىدىن، ئۇلارنى خار قىلغۇچى چاقماق ئازابى ھالاك قىلدى'),
(4236, 41, 18, 'ئىمان ئېيتقانلارنى ۋە تەقۋادارلارنى (يەنى سالىھ ئەلەيھىسسالام بىلەن ئۇنىڭغا ئەگەشكەنلەرنى ئازابتىن) قۇتقۇزدۇق'),
(4237, 41, 19, 'ئۇ كۈندە اﷲ نىڭ دۈشمەنلىرى دوزاخقا توپلىنىدۇ، ئالدى - ئارقىسى يىغىلىپ بولغۇچە توختىتىپ قويۇلىدۇ'),
(4238, 41, 20, 'ئۇلار دوزاخقا كەلگەندە، ئۇلارنىڭ قۇلاقلىرى، كۆزلىرى ۋە تېرىلىرى ئۇلارنىڭ قىلمىشلىرى ھەققىدە گۇۋاھلىق بېرىدۇ'),
(4239, 41, 21, 'ئۇلار ئۆزلىرىنىڭ تېرىلىرىگە: «نېمىشقا بىزنىڭ زىيىنىمىزغا گۇۋاھلىق بەردىڭلار؟» دەيدۇ، تېرىلىرى: «ھەر نەرسىنى سۆزلەتكەن اﷲ بىزنى سۆزلەتتى، ئۇ دەسلەپتە سىلەرنى ياراتتى. ئۇنىڭ دەرگاھىغا قايتۇرۇلىسىلەر» دەيدۇ'),
(4240, 41, 22, 'سىلەر (دۇنيادىكى چېغىڭلاردا) قۇلىقىڭلارنىڭ، كۆزۈڭلارنىڭ، تېرىلىرىڭلارنىڭ سىلەرنىڭ زىيىنىڭلارغا گۇۋاھلىق بېرىشىدىن ساقلانمىغان ئىدىڭلار (يەنى يامان ئىشلارنى قىلغان چېغىڭلاردا، رەسۋا بولۇشتىن قورقۇپ، كىشىلەردىن يوشۇرغان ئىدىڭلار، ۋەھالەنكى، ئەزالىرىڭلارنىڭ سىلەرنىڭ زىيىنىڭلارغا گۇۋاھلىق بېرىدىغانلىقىنى گۇمان قىلمىغان ئىدىڭلار)، لېكىن سىلەر قىلمىشىڭلاردىن نۇرغۇن ئىشلارنى «اﷲ بىلمەيدۇ» دەپ گۇمان قىلدىڭلار'),
(4241, 41, 23, 'پەرۋەردىگارىڭلارغا قىلغان ئەنە شۇ (قەبىھ) گۇمانىڭلار سىلەرنى ھالاك قىلدى. شۇنىڭ بىلەن زىيان تارتقۇچىلاردىن بولدۇڭلار'),
(4242, 41, 24, 'ئەگەر ئۇلار (ئازابقا) چىدىسا، (چىداملىقنىڭ پايدىسى يوق) دوزاخ ئۇلارنىڭ جايىدۇر، ئەگەر ئۇلار (اﷲ تىن) رازىلىق تىلىسە، ھەرگىز رازىلىققا ئېرىشەلمەيدۇ'),
(4243, 41, 25, 'ئۇلارغا بىز (شەيتانلاردىن) دوستلارنى مۇسەللەت قىلدۇق، ئۇلار (يەنى شەيتانلار) ئۇلارغا ئالدى - كەينىدىكى نەرسىلەرنى (يەنى ئۇلارنىڭ ھازىرقى ۋە كەلگۈسىدىكى قەبىھ ئەمەللىرىنى) چىرايلىق كۆرسەتتى، ئۇلارغا ئۇلاردىن ئىلگىرى ئۆتكەن ئىنسانلاردىن ۋە جىنلاردىن بولغان (گۇناھكار شەقى) ئۈممەتلەر قاتارىدا ئازاب سۆزى تېگىشلىك بولدى، چۈنكى ئۇلار زىيان تارتقۇچىلار ئىدى'),
(4244, 41, 26, 'كاپىرلار: «بۇ قۇرئانغا قۇلاق سالماڭلار، ئۇنى ئېلىشتۇرۇۋېتىڭلار، سىلەر غەلىبە قىلىشىڭلار مۇمكىن» دەيدۇ'),
(4245, 41, 27, 'بىز كاپىرلارغا چوقۇم قاتتىق ئازابنى تېتىتىمىز، بىز ئۇلارنىڭ يامان قىلمىشلىرىغا ئەڭ قاتتىق جازا بېرىمىز'),
(4246, 41, 28, 'اﷲ نىڭ دۈشمەنلىرىگە بېرىلىدىغان جازا ئەنە شۇ دوزاختۇر، ئۇلارنىڭ مەڭگۈلۈك تۇرىدىغان جايى دوزاختۇر، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ئۈچۈن (ئۇلارغا) شۇنداق جازا بېرىلىدۇ'),
(4247, 41, 29, 'كاپىرلار: «پەرۋەردىگارىمىز! ئىنسانلاردىن ۋە جىنلاردىن بىزنى ئازدۇرغانلارنى بىزگە كۆرسەتكىن، (ئۇلارنىڭ) ئەڭ خارلاردىن بولۇشى ئۈچۈن ئۇلارنى ئاياغلىرىمىزنىڭ ئاستىدا چەيلەيمىز» دېدى'),
(4248, 41, 30, 'شۈبھىسىزكى، «پەرۋەردىگارىمىز اﷲ دۇر» دېگەنلەر، ئاندىن توغرا يولدا بولغانلارغا پەرىشتىلەر چۈشۈپ: «قورقماڭلار، غەم قىلماڭلار، سىلەرگە ۋەدە قىلىنغان جەننەت ئۈچۈن خۇشال بولۇڭلار، بىز دۇنيادا، ئاخىرەتتە سىلەرنىڭ دوستلىرىڭلارمىز، جەننەتتە سىلەر ئۈچۈن كۆڭلۈڭلار تارتقان نەرسىلەرنىڭ ھەممىسى ۋە تىلىگەن نەرسەڭلارنىڭ ھەممىسى بار، (ئۇلار) ناھايىتى مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان (اﷲ) تەرىپىدىن بېرىلگەن زىياپەتتۇر» دەيدۇ'),
(4249, 41, 31, 'شۈبھىسىزكى، «پەرۋەردىگارىمىز اﷲ دۇر» دېگەنلەر، ئاندىن توغرا يولدا بولغانلارغا پەرىشتىلەر چۈشۈپ: «قورقماڭلار، غەم قىلماڭلار، سىلەرگە ۋەدە قىلىنغان جەننەت ئۈچۈن خۇشال بولۇڭلار، بىز دۇنيادا، ئاخىرەتتە سىلەرنىڭ دوستلىرىڭلارمىز، جەننەتتە سىلەر ئۈچۈن كۆڭلۈڭلار تارتقان نەرسىلەرنىڭ ھەممىسى ۋە تىلىگەن نەرسەڭلارنىڭ ھەممىسى بار، (ئۇلار) ناھايىتى مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان (اﷲ) تەرىپىدىن بېرىلگەن زىياپەتتۇر» دەيدۇ'),
(4250, 41, 32, 'شۈبھىسىزكى، «پەرۋەردىگارىمىز اﷲ دۇر» دېگەنلەر، ئاندىن توغرا يولدا بولغانلارغا پەرىشتىلەر چۈشۈپ: «قورقماڭلار، غەم قىلماڭلار، سىلەرگە ۋەدە قىلىنغان جەننەت ئۈچۈن خۇشال بولۇڭلار، بىز دۇنيادا، ئاخىرەتتە سىلەرنىڭ دوستلىرىڭلارمىز، جەننەتتە سىلەر ئۈچۈن كۆڭلۈڭلار تارتقان نەرسىلەرنىڭ ھەممىسى ۋە تىلىگەن نەرسەڭلارنىڭ ھەممىسى بار، (ئۇلار) ناھايىتى مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان (اﷲ) تەرىپىدىن بېرىلگەن زىياپەتتۇر» دەيدۇ'),
(4251, 41, 33, 'اﷲ قا (يەنى اﷲ نىڭ تەۋھىدىگە ۋە تائىتىگە) دەۋەت قىلغان، ياخشى ئەمەللەرنى قىلغان ۋە «مەن ھەقىقەتەن مۇسۇلمانلاردىنمەن» دېگەن كىشىدىنمۇ ياخشى سۆزلۈك ئادەم بارمۇ؟'),
(4252, 41, 34, 'ياخشى ئىش بىلەن يامان ئىش باراۋەر بولمايدۇ، ياخشى خىسلەت ئارقىلىق (يامان خىسلەتكە) تاقابىل تۇرغىن، (شۇنداق قىلساڭ) سەن بىلەن ئۆزىنىڭ ئارىسىدا ئاداۋەت بار ئادەم گويا سىرداش دوستۇڭدەك بولۇپ قالىدۇ'),
(4253, 41, 35, 'بۇ خىسلەتكە پەقەت سەۋرچان ئادەملەرلا ئېرىشەلەيدۇ، بۇ خىسلەتكە پەقەت بۈيۈك نېسىۋە ئىگىسىلا ئېرىشەلەيدۇ'),
(4254, 41, 36, 'ئەگەر شەيتان سېنى ۋەسۋەسە قىلىپ (يۇقىرىقى خىسلەتنى ئۆزلەشتۈرمەسلىككە كۈشكۈرتسە) اﷲ قا سېغىنىپ (شەيتاننىڭ شەررىدىن) پاناھ تىلىگىن، اﷲ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر'),
(4255, 41, 37, 'كېچە بىلەن كۈندۈز، كۈن بىلەن ئاي اﷲ نىڭ (بىرلىكىنى ۋە قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، قۇياشقا سەجدە قىلماڭلار، ئايغىمۇ سەجدە قىلماڭلار، ئۇلارنى ياراتقان اﷲ قا سەجدە قىلىڭلار، ئەگەر پەقەت اﷲ غىلا ئىبادەت قىلىدىغان بولساڭلار'),
(4256, 41, 38, 'ئەگەر كۇففارلار چوڭچىلىق قىلىپ (اﷲ قا سەجدە قىلىشتىن) باش تارتسا، پەرۋەردىگارىڭنىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) كېچە - كۈندۈز اﷲ قا تەسبىھ ئېيتىپ تۇرىدۇ، ئۇلار مالال بولۇپ قالمايدۇ'),
(4257, 41, 39, '(بۇمۇ) اﷲ نىڭ (بىرلىكىنى ۋە قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇركى، سەن زېمىننى قاقاس كۆرىسەن، ئۇنىڭغا يامغۇر ياغدۇرغان ۋاقتىمىزدا، زېمىن كۆتۈرۈلىدۇ ۋە كۆپۈشىدۇ، شۈبھىسىزكى، زېمىننى تىرىلدۈرگەن زات ئۆلۈكلەرنى ئەلۋەتتە تىرىلدۈرگۈچىدۇر، ئۇ ھەقىقەتەن ھەر نەرسىگە قادىردۇر'),
(4258, 41, 40, 'شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى بۇرمىلاپ چۈشەندۈرىدىغانلار بىزگە مەخپىي قالمايدۇ، قىيامەت كۈنى دوزاخقا تاشلىنىدىغان ئادەم ياخشىمۇ؟ ياكى قىيامەت كۈنى (اﷲ نىڭ ئازابىدىن) ئەمىن بولغان ھالدا كېلىدىغان ئادەم ياخشىمۇ؟ خالىغىنىڭلارنى قىلىڭلار، شۈبھىسىزكى، اﷲ قىلمىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(4259, 41, 41, 'شۈبھىسىزكى، قۇرئان كەلگەندە ئۇنى ئىنكار قىلغانلار (قاتتىق جازاغا ئۇچرايدۇ)، شۈبھىسىزكى، قۇرئان غالىب كىتابتۇر'),
(4260, 41, 42, 'ئۇنىڭغا ئالدىدىنمۇ، ئارقىسىدىنمۇ (يەنى ھېچقايسى تەرىپىدىن) باتىل يۈزلەنمەيدۇ، ئۇ ھېكمەت بىلەن ئىش قىلغۇچى، مەدھىيىگە لايىق اﷲ تەرىپىدىن نازىل قىلىنغاندۇر'),
(4261, 41, 43, 'ساڭا (مۇشرىكلار تەرىپىدىن) ئېيتىلىدىغان سۆزلەر پەقەت ئىلگىرىكى پەيغەمبەرلەرگە (قەۋملىرى تەرىپىدىن) ئېيتىلغان سۆزلەردۇر، شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ مەغپىرەت ئىگىسىدۇر ۋە قاتتىق ئازاب ئىگىسىدۇر'),
(4262, 41, 44, 'مۇبادا بىز ئۇنى ئەجەم تىلىدىكى قۇرئان قىلىپ نازىل قىلغان بولساق، ئۇلار ئەلۋەتتە: «نېمىشقا ئۇنىڭ ئايەتلىرى (بىز چۈشىنىدىغان تىلدا) روشەن بايان قىلىنمىدى؟ پەيغەمبەر ئەرەب، كىتاب ئەجەمچىمۇ؟» دەيتتى. ئۇ ئىمان كەلتۈرگەنلەرگە ھىدايەتتۇر ۋە (دىللاردىكى شەك - شۈبھىگە) شىپادۇر، ئىمان ئېيتمايدىغانلارنىڭ قۇلاقلىرى (قۇرئاننى ئاڭلاشتىن) ئېغىردۇر (يەنى قۇرئاننىڭ دەۋىتىگە قۇلاق سالمىغانلارنىڭ ۋە ئۇنىڭدىن يۈز ئۆرۈگەنلەرنىڭ گويا قۇلاقلىرى ئېغىردۇر)، قۇرئان ئۇلارنىڭ (دىللىرىغا) كورلۇقتۇر (يەنى ئۇلار ھىدايەت نۇرىنى كۆرمەيدۇ)، ئەنە شۇلار يىراقتىن نىدا قىلىنغۇچىلاردۇر (يەنى ئۇلار ھەقنى قوبۇل قىلماسلىقتا ئىمانغا يىراقتىن چاقىرىلغان، مۇساپىنىڭ يىراقلىقىدىن چاقىرىقنى ئاڭلىمىغان كىشىلەرگە ئوخشايدۇ)'),
(4263, 41, 45, 'بىز ھەقىقەتەن مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق، ئۇ ھەقتە ئىختىلاپ بولدى. ئەگەر پەرۋەردىگارىڭنىڭ (ئۇلارنى جازالاشنى قىيامەتكىچە كېچىكتۈرۈش) ۋەدىسى بولمىسا ئىدى، ئەلۋەتتە ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىلاتتى، ئۇلار ھەقىقەتەن قۇرئاندىن زىيادە شەكتىدۇر'),
(4264, 41, 46, 'كىمكى ياخشى ئىش قىلىدىكەن، پايدىسى ئۆزىگىدۇر، كىمكى يامان ئىش قىلىدىكەن زىيىنىمۇ ئۆزىگىدۇر. پەرۋەردىگارىڭ بەندىلەرگە زۇلۇم قىلغۇچى ئەمەستۇر'),
(4265, 41, 47, 'قىيامەتنىڭ ۋاقتىنى بىلىش پەقەت اﷲ قا خاستۇر (يەنى قىيامەتنىڭ قاچان بولىدىغانلىقىنى اﷲ تىن باشقا كىشى بىلمەيدۇ). مېۋىلەرنىڭ بوغۇنلىرىدىن چىقىشى، ھەر قانداق چىشىنىڭ ھامىلدار بولۇشى ۋە تۇغۇشى اﷲ نىڭ ئىلمىنىڭ سىرتىدا ئەمەستۇر، ئۇ كۈندە اﷲ ئۇلارغا: «مېنىڭ شېرىكلىرىم (يەنى سىلەر ماڭا شېرىك كەلتۈرگەن نەرسىلەر) قەيەردە؟» دەپ نىدا قىلىدۇ، ئۇلار: «ساڭا مەلۇم قىلىمىزكى، ئارىمىزدا ھېچ كىشى (سېنىڭ شېرىكىڭ بار دەپ) گۇۋاھلىق بېرەلمەيدۇ» دەيدۇ'),
(4266, 41, 48, 'ئۇلار ئىلگىرى (يەنى دۇنيادىكى چاغدا) چوقۇنغان بۇتلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالىدۇ، ئۇلار (اﷲ نىڭ ئازابىدىن) قاچىدىغان جاي يوق ئىكەنلىكىنى ئېنىق بىلىدۇ'),
(4267, 41, 49, 'ئىنسان (ئۆزىگە) ياخشىلىق تىلەشتىن زېرىكمەيدۇ، ئەگەر ئۇنىڭغا بىرەر يامانلىق يېتىپ قالسا (اﷲ نىڭ رەھمىتىدىن) تولىمۇ ئۈمىدسىزلىنىپ كېتىدۇ'),
(4268, 41, 50, 'ئەگەر بىز ئۇنىڭغا كۈلپەتتىن كېيىن رەھمىتىمىزنى تېتىتساق، ئۇ چوقۇم: «بۇ ئۆزۈمنىڭ سەئيى - ئىجتىھاتىمدىن كەلگەن، قىيامەتنىڭ بولىدىغانلىقىغا ئىشەنمەيمەن، مۇبادا (قىيامەت بولۇپ) مەن پەرۋەردىگارىمغا قايتۇرۇلغاندىمۇ ئۇنىڭ دەرگاھىدا چوقۇم جەننەتكە ئېرىشىمەن» دەيدۇ. ئەلۋەتتە كاپىرلارغا قىلمىشلىرىنى خەۋەر قىلىمىز ۋە ئۇلارغا قاتتىق ئازابنى تېتىتىمىز'),
(4269, 41, 51, 'ئىنسانغا نېمەت ئاتا قىلساق (پەرۋەردىگارىغا شۈكۈر قىلىشتىن) يۈز ئۆرۈيدۇ، (اﷲ نىڭ ئەمرىگە بويسۇنۇشتىن باش تارتىپ) ھاكاۋۇرلىشىپ كېتىدۇ، ئەگەر ئۇنىڭغا يامانلىق يەتسە (اﷲ قا يۈزلىنىپ) ئۈزلۈكسىز دۇئا قىلىدۇ'),
(4270, 41, 52, '(ئى مۇھەممەد! ئېيتقىنكى) «ئېيتىپ بېقىڭلارچۇ (قۇرئان مەن ئېيتقاندەك) اﷲ تەرىپىدىن نازىل بولغان بولسا، سىلەر (ئويلىماستىنلا ئۇنى ئىنكار قىلساڭلار، ھالىڭلار قانداق بولىدۇ). (ھەقتىن) يىراق ئىختىلاپتا بولغان كىشىدىنمۇ گۇمراھ ئادەم بارمۇ؟ (يەنى سىلەردىنمۇ گۇمراھ ئادەم يوق)»'),
(4271, 41, 53, 'ئۇلارغا بىز تاكى ئۇلارغا قۇرئاننىڭ ھەق ئىكەنلىكى ئېنىق بولغۇچە، (ئاسمان - زېمىن) ئەتراپىدىكى ۋە ئۆزلىرىدىكى (قۇدرىتىمىزگە دالالەت قىلىدىغان) ئالامەتلەرنى كۆرسىتىمىز، پەرۋەردىگارىڭنىڭ ھەممە نەرسىنى كۆرۈپ تۇرىدىغانلىقى (سېنىڭ راستلىقىڭنىڭ دەلىلى بولۇشقا) ئۇلارغا كۇپايە ئەمەسمۇ'),
(4272, 41, 54, 'بىلىڭلاركى، ئۇلار ھەقىقەتەن پەرۋەردىگارىغا مۇلاقات بولۇشتىن شەكلىنىدۇ، بىلىڭلاركى، اﷲ ھەممە نەرسىنى ھەقىقەتەن تولۇق بىلىپ تۇرغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حامىم،'),
(4274, 42, 2, 'عين، سىين، قاف'),
(4275, 42, 3, 'غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ ساڭا ۋە سەندىن ئىلگىرىكى (پەيغەمبەر) لەرگە مۇشۇنداق ۋەھىي قىلىدۇ'),
(4276, 42, 4, 'ئاسمانلاردىكى شەيئىلەر ۋە زېمىندىكى شەيئىلەر اﷲ نىڭدۇر (يەنى اﷲ نىڭ مۈلكىدۇر، اﷲ نىڭ تەسەررۇپى ئاستىدىدۇر)، اﷲ ئۈستۈندۇر، كاتتىدۇر'),
(4277, 42, 5, '(اﷲ نىڭ كاتتىلىقىدىن) ئاسمانلار يۇقىرى تەرىپىدىن يېرىلىپ كېتىشكە تاس قالىدۇ، پەرىشتىلەر پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمدى ئېيتىدۇ، يەر يۈزىدىكىلەرگە مەغپىرەت تىلەيدۇ. بىلىڭلاركى، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(4278, 42, 6, 'اﷲ نى قويۇپ (بۇتلارنى) ئىگە قىلىۋالغانلارنىڭ (ئەمەللىرىنى) اﷲ كۈزىتىپ تۇرغۇچىدۇر، سەن ئۇلارغا ھامىي ئەمەسسەن'),
(4279, 42, 7, 'مەككە ئاھالىسىنى ۋە ئۇنىڭ ئەتراپىدىكىلەرنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، (كىشىلەرنى) قىيامەت كۈنىدىن ئاگاھلاندۇرۇشۇڭ ئۈچۈن، ساڭا بىز مۇشۇنداق ئەرەبچە قۇرئاننى نازىل قىلدۇق، ئۇ كۈندە ھېچ شەك يوقتۇر، (ئۇ كۈندە) بىر پىرقە (كىشىلەر) جەننەتتە ۋە بىر پىرقە كىشىلەر جەھەننەمدە بولىدۇ'),
(4280, 42, 8, 'ئەگەر اﷲ خالىسا، ئۇلارنى (يەنى پۈتۈن ئىنسانلارنى) ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، لېكىن اﷲ خالىغان ئادەمنى رەھمىتىگە داخىل قىلىدۇ، زالىملارغا ھېچقانداق ئىگە، ھېچقانداق مەدەتكار بولمايدۇ'),
(4281, 42, 9, 'بەلكى ئۇلار اﷲ تىن باشقىلارنى ئىگە قىلىۋالدى، پەقەت اﷲ ئىگىدۇر، اﷲ ئۆلۈكلەرنى تىرىلدۈرىدۇ، اﷲ ھەر نەرسىگە قادىر'),
(4282, 42, 10, 'سىلەر (دىن ئۈستىدە ۋە ئۇنىڭدىن باشقا) ھەر قانداق نەرسە ئۈستىدە ئىختىلاپ قىلىشماڭلار، ئۇنىڭغا اﷲ ھۆكۈم چىقىرىدۇ. (ئى مۇھەممەد! ئۇلارغا ئېيتقىنكى) ئەنە شۇ اﷲ مېنىڭ پەرۋەردىگارىمدۇر، ئۇنىڭغا تەۋەككۈل قىلدىم، (ھەممە ئىشتا) اﷲ قا مۇراجىئەت قىلىمەن'),
(4283, 42, 11, 'اﷲ ئاسمانلارنى ۋە زېمىننى ياراتقۇچىدۇر، اﷲ سىلەرگە ئۆز جىنسىڭلاردىن جۈپتىلەرنى (يەنى ئاياللارنى) ياراتتى. ھايۋانلارغىمۇ ئۆز تىپىدىن جۈپتىلەرنى ياراتتى. بۇ ئارقىلىق اﷲ سىلەرنى كۆپەيتىدۇ، ھېچ شەيئى اﷲ قا ئوخشاش ئەمەستۇر، اﷲ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر'),
(4284, 42, 12, 'ئاسمانلارنىڭ ۋە زېمىننىڭ (خەزىنىلىرىنىڭ) ئاچقۇچلىرى اﷲ نىڭ ئىلكىدىدۇر، اﷲ خالىغان كىشىنىڭ رىزقىنى (سىناش يۈزىسىدىن) كەڭ قىلىدۇ، خالىغان كىشىنىڭ رىزقىنى (بالاغا مۇپتىلا قىلىپ سىناش يۈزىسىدىن) تار قىلىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىنى بىلىپ تۇرغۇچىدۇر'),
(4285, 42, 13, 'اﷲ سىلەرگە دىندىن نۇھقا تەۋسىيە قىلغان نەرسىنى، ساڭا (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىز ۋەھىي قىلغان نەرسىنى، ئىبراھىمغا، مۇساغا ۋە ئىساغا بىز تەۋسىيە قىلغان نەرسىنى بايان قىلدى. سىلەر دىننى بەرپا قىلىڭلار، دىندا تەپرىقىچىلىك قىلماڭلار، مۇشرىكلارغا سەن ئۇلارنى دەۋەت قىلغان نەرسە (يەنى تەۋھىد) ئېغىر كەلدى، اﷲ تەۋھىدكە خالىغان ئادەمنى تاللايدۇ، اﷲ نىڭ (تائىتىگە) قايتىدىغان ئادەمنى تەۋھىدكە باشلايدۇ'),
(4286, 42, 14, '(يەھۇدىي، ناسارا قاتارلىقلار) ئۇلارغا ئىلىم كەلگەندىن كېيىن (يەنى ئۇلار رەسۇلۇللاھنىڭ ھەق پەيغەمبەرلىكىنى بىلگەندىن كېيىن)، ئاندىن ئۆزئارا ھەسەت قىلىشىپ پىرقىلەرگە بۆلۈندى. ئەگەر پەرۋەردىگارىڭنىڭ (ئۇلارنى جازالاشنى) قىيامەتكىچە تەخىر قىلىشتىن ئىبارەت سۆزى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىلىپ بولغان بولاتتى (يەنى ئۇلار تېز ھالاك قىلىنغان بولاتتى). ئۇلاردىن كېيىن كىتابقا ۋارىسلىق قىلغانلار (يەنى رەسۇلۇللاھ بىلەن زامانداش بولغان ئەھلى كىتاب) (كىتاب ھەققىدە) قاتتىق دەرگۇماندىدۇر'),
(4287, 42, 15, 'شۇنىڭ ئۈچۈن (يەنى دىندا ئىختىلاپ قىلىشقانلىقى ئۈچۈن) (ئى مۇھەممەد! بارلىق ئىنسانلارنى توغرا دىن ئاساسىدا ئىتتىپاق بولۇشقا) چاقىرغىن، بۇيرۇلغىنىڭ بويىچە توغرا يولدا چىڭ تۇرغىن، ئۇلار (يەنى مۇشرىكلار) نىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن ۋە ئېيتقىن: «اﷲ نازىل قىلغان (بارلىق) كىتابلارغا ئىمان ئېيتتىم، ئاراڭلاردا ئادىل بولۇشقا بۇيرۇلدۇم، اﷲ بىزنىڭ پەرۋەردىگارىمىزدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈندۇر، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزلىرىڭلار ئۈچۈندۇر، سىلەر بىلەن بىزنىڭ ئارىمىزدا جېدەللىشىشكە (ئورۇن) يوق (چۈنكى ھەقىقەت ئاشكارىدۇر)، اﷲ (قىيامەت كۈنى) بىزنى (ھۆكۈم چىقىرىش ئۈچۈن) يىغىدۇ، ئاخىر قايتىدىغان جاي اﷲ نىڭ دەرگاھىدۇر»'),
(4288, 42, 16, 'كىشىلەر اﷲ نىڭ دىنىنى قوبۇل قىلغاندىن كېيىن، (كىشىلەرنى ئىماندىن توسۇش ئۈچۈن) اﷲ نىڭ دىنىدا خۇسۇمەت قىلىشقانلارنىڭ دەلىللىرى اﷲ نىڭ دەرگاھىدا باتىلدۇر، ئۇلار (دۇنيادا) غەزەپكە ئۇچرايدۇ، (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ'),
(4289, 42, 17, 'اﷲ كىتاب (يەنى قۇرئان) نى ھەق بىلەن نازىل قىلدى ۋە ئادالەتنى نازىل قىلدى. سەن نېمە بىلىسەن؟ قىيامەت يېقىن بولۇشى مۇمكىن'),
(4290, 42, 18, 'قىيامەتكە ئىشەنمەيدىغانلار ئۇنىڭ بولۇشىنى ئالدىرىتىدۇ، ئۇنىڭغا ئىشىنىدىغانلار بولسا قىيامەتتىن قورقىدۇ، قىيامەتنى ھەق دەپ بىلىدۇ، بىلىڭلاركى، قىيامەت توغرۇلۇق مۇجادىلە قىلىدىغانلار ئەلۋەتتە چوڭقۇر گۇمراھلىقتىدۇر'),
(4291, 42, 19, 'اﷲ بەندىلىرىگە كۆيۈنگۈچىدۇر، ئۇ خالىغان ئادەمگە (كەڭ) رىزىق بېرىدۇ، اﷲ كۈچلۈكتۇر، غالىبتۇر'),
(4292, 42, 20, 'كىمكى (ئۆزىنىڭ ئەمەلى بىلەن) ئاخىرەتنىڭ ساۋابىنى كۆزلىسە، ئۇنىڭ ساۋابىنى زىيادە بېرىمىز، كىمكى (ئەمەلى بىلەن) دۇنيانىڭ مەنپەئىتىنى كۆزلىسە، (ئۇنىڭ تىلىگەن) مەنپەئىتىنىڭ بەزىسىنى بېرىمىز، ئۇنىڭغا ئاخىرەتتە (ساۋابتىن) ھېچ نېسىۋە يوق'),
(4293, 42, 21, 'ئۇلارنىڭ اﷲ رۇخسەت قىلمىغان نەرسىلەرنى دىن قىلىپ بېكەتكەن مەبۇدلىرى بارمۇ؟ (اﷲ نىڭ ئۇلارغا بېرىلىدىغان ئازابنى قىيامەتكىچە تەخىر قىلىش توغرىسىدىكى) ھۆكمى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە (دۇنيادىلا) ھۆكۈم چىقىرىلغان بولاتتى، (ئۆزلىرىگە) زۇلۇم قىلغۇچىلار (يەنى كاپىرلار) قاتتىق ئازابقا دۇچار بولىدۇ'),
(4294, 42, 22, 'كاپىرلارنىڭ (قىيامەت كۈنى دۇنيادا) قىلغان گۇناھلىرىنىڭ جازاسىدىن قورقۇپ تۇرغانلىقىنى كۆرىسەن، (ئۇلار مەيلى قورقسۇن، قورقمىسۇن) جازا ئۇلارغا (چوقۇم) نازىل بولغۇسىدۇر، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار جەننەتلەرنىڭ باغچىلىرىدا بولىدۇ، ئۇلار پەرۋەردىگارىنىڭ ھۇزۇرىدا خالىغان نەرسىلەردىن بەھرىمەن بولىدۇ، بۇ كاتتا ئىنئامدۇر'),
(4295, 42, 23, 'اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان بەندىلىرىگە ئەنە شۇ ئىنئام بىلەن خۇش - خەۋەر بېرىدۇ. «(پەيغەمبەرلىكنى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھەق تەلەپ قىلمايمەن، پەقەت تۇغقانچىلىق ھەققى ئۈچۈن مېنى دوست تۇتۇشۇڭلارنى (يەنى پەرۋەردىگارىمنىڭ دەۋىتىنى يەتكۈزۈشۈمگە چېقىلماسلىقىڭلارنى) تىلەيمەن» دېگىن، كىمكى بىرەر ياخشى ئىش قىلسا، ئۇنىڭغا بىز ساۋابنى زىيادە بېرىمىز، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر'),
(4296, 42, 24, 'ئۇلار (يەنى قۇرەيش كاپىرلىرى): «ئۇ (يەنى مۇھەممەد) اﷲ قا يالغاننى توقۇدى» دەمدۇ؟ ئەگەر اﷲ خالىسا (يەنى ئۇلار گۇمان قىلغاندەك، اﷲ قا يالغاننى توقۇغان بولساڭ) سېنىڭ قەلبىڭنى پېچەتلىۋەتكەن بولاتتى، اﷲ باتىلنى يوق قىلىدۇ، (پەيغەمبىرىگە نازىل قىلغان) سۆزلىرى ئارقىلىق ھەقنى ھەق قىلىدۇ، اﷲ ھەقىقەتەن دىللاردىكى سىرلارنى بىلىپ تۇرغۇچىدۇر'),
(4297, 42, 25, 'اﷲ بەندىلىرىدىن تەۋبىنى قوبۇل قىلىدۇ، يامان ئىشلارنى ئەپۇ قىلىدۇ، قىلىۋاتقان ئىشىڭلارنى بىلىپ تۇرىدۇ'),
(4298, 42, 26, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ دۇئاسىنى اﷲ ئىجابەت قىلىدۇ، (كامالى) پەزلىدىن ئۇلارغا ساۋابنى زىيادە بېرىدۇ، كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(4299, 42, 27, 'ئەگەر اﷲ بەندىلىرىنىڭ رىزقىنى كەڭ قىلسا، ئۇلار ئەلۋەتتە زېمىندا پىتنە - پاسات تېرىيتتى. لېكىن اﷲ خالىغان نەرسىنى ئۆلچەم بىلەن چۈشۈرىدۇ، اﷲ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىدىن) تولۇق خەۋەرداردۇر، (ئۇنى) كۆرۈپ تۇرغۇچىدۇر'),
(4300, 42, 28, 'ئۇلار (يامغۇرنىڭ يېغىشىدىن ئۈمىدسىزلەنگەندىن كېيىن) اﷲ يامغۇر ياغدۇرۇپ بېرىدۇ، رەھمىتىنى يايىدۇ، اﷲ (ھەممىنىڭ) ئىگىسىدۇر، مەدھىيىگە لايىقتۇر'),
(4301, 42, 29, 'ئاسمانلارنىڭ، زېمىننىڭ يارىتىلىشى ۋە ئۇ ئىككىسىدە جاندارلارنىڭ يېيىلىشى اﷲ نىڭ (قۇدرىتىنىڭ) دەلىللىرىدىندۇر، اﷲ ئەگەر خالىسا، ئۇلارنى جەمئى قىلىشقا قادىردۇر'),
(4302, 42, 30, 'سىلەرگە (يەنى جېنىڭلار ياكى مېلىڭلارغا) ھەرقانداق بىر مۇسىبەت يەتسە، ئۇ سىلەرنىڭ قىلغان گۇناھىڭلار تۈپەيلىدىن كەلگەن بولىدۇ، اﷲ نۇرغۇن گۇناھىڭلارنى ئەپۇ قىلىدۇ (ئەپۇ قىلمايدىغان بولسا، بالا ۋە مۇسىبەتتىن ھېچ قۇتۇلماس ئىدىڭلار)'),
(4303, 42, 31, '(ئى مۇشرىكلار!) سىلەر زېمىندا اﷲ نىڭ ئازابىدىن قېچىپ قۇتۇلالمايسىلەر، سىلەرگە اﷲ تىن باشقا ھېچ ئىگە ۋە مەدەتكار يوق'),
(4304, 42, 32, 'دېڭىزدا قاتنىغۇچى تاغدەك كېمىلەر اﷲ نىڭ قۇدرىتىنىڭ دەلىللىرىدىندۇر'),
(4305, 42, 33, 'ئەگەر اﷲ خالىسا، شامالنى توختىتىۋېتىدۇ - دە، كېمىلەر دېڭىزنىڭ ئۈستىدە توختاپ قالىدۇ، بۇنىڭدا (قاتتىقلىقتا) سەۋر قىلغۇچى، (كەڭچىلىكتە) شۈكۈر قىلغۇچى ھەر مۆمىن ئۈچۈن نۇرغۇن ئىبرەتلەر بار'),
(4306, 42, 34, 'ياكى اﷲ خالىسا، ئىچىدىكى كىشىلەرنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن كېمىلەرنى (بوران بىلەن) ھالاك قىلىدۇ، اﷲ نۇرغۇن كىشلەرنىڭ گۇناھلىرىنى ئەپۇ قىلىدۇ'),
(4307, 42, 35, 'بىزنىڭ ئايەتلىرىمىز ئۈستىدە مۇجادىلە قىلىدىغانلار ئۆزلىرىگە اﷲ نىڭ ئازابىدىن قېچىپ قۇتۇلىدىغان جاي يوق ئىكەنلىكىنى بىلىدۇ'),
(4308, 42, 36, '(دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبرىلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا اﷲ نىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىدۇر'),
(4309, 42, 37, '(دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبرىلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا اﷲ نىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىدۇر'),
(4310, 42, 38, '(دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبرىلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا اﷲ نىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىدۇر'),
(4311, 42, 39, '(دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبرىلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا اﷲ نىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىدۇر'),
(4312, 42, 40, 'بىر يامانلىقنىڭ جازاسى شۇنىڭغا ئوخشاش بىر يامانلىقتۇر (يەنى ساڭا بىر كىشى قانچىلىك چېقىلسا، شۇنچىلىك چېقىلماي ئاشۇرۇۋەتسەڭ بولمايدۇ)، كىمكى (ئىنتىقام ئېلىشقا قادىر تۇرۇقلۇق) ئەپۇ قىلسا ۋە (ئۆزى بىلەن يامانلىق قىلغۇچىنىڭ ئارىسىنى) تۈزىسە، ئۇنىڭ ئەجرىنى اﷲ بېرىدۇ، اﷲ ھەقىقەتەن زۇلۇم قىلغۇچىلارنى دوست تۇتمايدۇ'),
(4313, 42, 41, 'زۇلۇمغا ئۇچرىغۇچى ئادەم ئىنتىقامنى ئالسا ئۇنى ئەيىبلەشكە بولمايدۇ'),
(4314, 42, 42, 'ئەيىبلىنىدىغانلار پەقەت كىشىلەرگە زۇلۇم قىلىدىغان، زېمىندا ناھەق رەۋىشتە پىتنە - پاسات تېرىيدىغان ئادەملەردۇر، ئەنە شۇلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(4315, 42, 43, 'كىمكى (ئەزىيەتكە) سەۋر قىلسا، (اﷲ نىڭ رازىلىقى ئۈچۈن) ئىنتىقام ئالمىسا، بۇ ئەلۋەتتە مەرغۇپ ئىشلاردىندۇر'),
(4316, 42, 44, 'اﷲ گۇمراھ قىلغان ئادەمگە شۇنىڭدىن كېيىن ھېچ ئىگە يوقتۇر، زالىملار ئازابنى كۆرگەن چاغدا، ئۇلارنىڭ «(دۇنياغا) قايتىشقا يول بارمىدۇ؟» دېگەنلىكىنى كۆرىسەن'),
(4317, 42, 45, 'ئۇلارنىڭ قورققان، خارلانغان ھالدا، (دوزاخقا) كۆزىنىڭ قۇيرۇقى بىلەن قارىغان ھالدا دوزاخقا توغرىلىنىدىغانلىقىنى كۆرىسەن، مۆمىنلەر: «شۈبھىسىزكى، زىيان تارتقۇچىلار قىيامەت كۈنى ئۆزلىرىگە ۋە ئائىلىسىدىكىلەرگە زىيان سالغۇچىلاردۇر» دەيدۇ، بىلىڭلاركى، زالىملار ھەقىقەتەن دائىملىق ئازابتىدۇر'),
(4318, 42, 46, 'ئۇلارغا اﷲ تىن باشقا ياردەم بېرىدىغان دوستلار بولمايدۇ، اﷲ كىمنى گۇمراھ قىلسا، ئۇنىڭغا (ئۇنى دۇنيادا ھەقكە، ئاخىرەتتە جەننەتكە ئېرىشتۈرگۈچى) ھېچ يول بولمايدۇ'),
(4319, 42, 47, 'اﷲ تەرىپىدىن كەلگەن، قارشى تۇرغىلى بولمايدىغان بىر كۈن كېلىشتىن بۇرۇن پەرۋەردىگارىڭلارنىڭ دەۋىتىنى قوبۇل قىلىڭلار، بۇ كۈندە سىلەرگە قاچىدىغان جاي تېپىلماي قالىدۇ. (قىلمىشىڭلار نامە - ئەمالىڭلارغا يېزىقلىق بولغانلىقى، ئەزايىڭلار ئۇنىڭغا گۇۋاھ بولغانلىقى ئۈچۈن) سىلەر (ئۇنى) ئىنكار قىلالمايسىلەر'),
(4320, 42, 48, 'ئەگەر ئۇلار (يەنى مۇشرىكلار) (ئىماندىن) يۈز ئۆرۈسە، بىز سېنى ئۇلارغا كۆزەتچى قىلىپ ئەۋەتكىنىمىز يوق، سېنىڭ ۋەزىپەڭ پەقەت تەبلىغ قىلىشتۇر، ئەگەر ئىنسانغا بىزنىڭ رەھمىتىمىزنى تېتىتساق خۇش بولۇپ كېتىدۇ، ئۇلارنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن (بېشىغا) بىرەر ھادىسە كەلسە، ئىنسان تولىمۇ كۇفرانە نېمەت قىلغۇچىدۇر'),
(4321, 42, 49, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ نېمىنى خالىسا شۇنى يارىتىدۇ، خالىغان ئادەمگە قىز پەرزەنت ئاتا قىلىدۇ، خالىغان ئادەمگە ئوغۇل پەرزەنت ئاتا قىلىدۇ'),
(4322, 42, 50, 'ياكى ئۇلارغا ئوغۇل، قىزنى ئارىلاش بېرىدۇ، خالىغان ئادەمنى تۇغماس قىلىدۇ، اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر'),
(4323, 42, 51, 'اﷲ ھەر قانداق ئادەمگە پەقەت ۋەھيى ئارقىلىق ياكى پەردە ئارقىسىدىنلا ياكى بىر پەيغەمبەرنى ئەۋەتىپ ئۆز ئىزنى بىلەن ئۇنىڭغا خالىغان ۋەھيىنى قىلىش ئارقىلىقلا سۆز قىلغان. اﷲ ھەقىقەتەن (مەخلۇقلارنىڭ سۈپەتلىرىدىن) ئۈستۈندۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(4324, 42, 52, 'شۇنىڭدەك (يەنى باشقا پەيغەمبەرلەرگە ۋەھيى قىلغاندەك) ئەمرىمىز بويىچە ساڭا قۇرئاننى ۋەھيى قىلدۇق، سەن (ۋەھيىدىن ئىلگىرى) قۇرئاننىڭ ۋە ئىماننىڭ نېمە ئىكەنلىكىنى ئۇقمايتتىڭ، لېكىن بىز قۇرئاننى بىر نۇر قىلدۇقكى، ئۇنىڭ بىلەن بەندىلىرىمىزدىن خالىغان كىشىلەرنى ھىدايەت قىلىمىز. شەك - شۈبھىسىزكى سەن توغرا يولغا باشلايسەن'),
(4325, 42, 53, '(ئۇ) ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئۆزىگە خاس بولغان اﷲ نىڭ يولىدۇر. بىلىڭلاركى، ھەممە ئىش اﷲ قا قايتىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حا، مىم'),
(4327, 43, 2, 'روشەن كىتاب بىلەن قەسەمكى، سىلەرنى (ئۇنىڭ مەنىسىنى) چۈشەنسۇن دەپ، بىز ئۇنى ھەقىقەتەن ئەرەبچە قۇرئان قىلدۇق'),
(4328, 43, 3, 'روشەن كىتاب بىلەن قەسەمكى، سىلەرنى (ئۇنىڭ مەنىسىنى) چۈشەنسۇن دەپ، بىز ئۇنى ھەقىقەتەن ئەرەبچە قۇرئان قىلدۇق'),
(4329, 43, 4, 'ھەقىقەتەن ئۇ لەۋھۇلمەھپۇزدا بىزنىڭ دەرگاھىمىزدا (مۇقىملاشقان) يۇقىرى مەرتىۋىلىك ھېكمەتلىك (كىتاب) دۇر'),
(4330, 43, 5, 'سىلەر (گۇمراھلىقتا) ھەددىدىن ئاشقان قەۋم بولغانلىقىڭلار ئۈچۈن سىلەرگە ۋەز - نەسىھەت قىلىشنى تەرك قىلامدۇق؟'),
(4331, 43, 6, 'ئىلگىرىكى ئۈممەتلەرگە نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق'),
(4332, 43, 7, 'ئۇلارغا قانداق بىر پەيغەمبەر كەلمىسۇن، ئۇلار ئۇنى (قەۋمىڭ سېنى مەسخىرە قىلغاندەك) مەسخىرە قىلدى'),
(4333, 43, 8, 'ئۇلار (يەنى مەككە كاپىرلىرى) دىن كۈچ - قۇۋۋەتتە ئۈستۈن بولغانلارنى ھالاك قىلدۇق، ئىلگىرىكىلەرنىڭ قىسسىلىرى قۇرئاندا ئۆتتى'),
(4334, 43, 9, 'ئەگەر سەن ئۇلاردىن: «ئاسمانلار ۋە زېمىننى كىم ياراتتى» دەپ سورىساڭ، ئۇلار: «ئەلۋەتتە ئاسمانلارنى ۋە زېمىننى غالىب، ھەممىنى بىلگۈچى (اﷲ) ياراتتى» دەيدۇ'),
(4335, 43, 10, 'اﷲ سىلەر ئۈچۈن زېمىننى قارارگاھ قىلدى، (سەپەردە كۆزلىگەن يېرىڭلارغا) بېرىۋېلىشىڭلار ئۈچۈن سىلەرگە زېمىندا نۇرغۇن يوللارنى ياراتتى'),
(4336, 43, 11, 'اﷲ بۇلۇتتىن (سىلەرگە زىيان يەتكۈزمەي، پايدا يەتكۈزىدىغان) ئۆلچەمدە يامغۇر ياغدۇردى، ئۇنىڭ بىلەن بىز (گىيا ئۈنمەس قاقاس) ئۆلۈك زېمىننى تىرىلدۈردۇق، سىلەر مۇشۇنداق (يەنى ئۆلۈك يەردىن ئۆسۈملۈك چىقىرىلغاندەك) (قەبرەڭلاردىن) چىقىرىلىسىلەر'),
(4337, 43, 12, 'اﷲ پۈتۈن مەخلۇقاتلارنىڭ تۈرلىرىنى ياراتتى، سىلەرگە ئۈستىگە چىقىشىڭلار ئۈچۈن كېمە ۋە مىنىشىڭلار ئۈچۈن ھايۋان قاتارلىقلارنى ياراتتى. ئاندىن سىلەر ئۇلارنىڭ ئۈستىگە چىققانلىرىڭلاردا پەرەۋردىگارىڭلارنىڭ نېمىتىنى ئەسلەپ: «بىزگە بۇنى بويسۇندۇرۇپ بەرگەن زات پاكتۇر، بىز ئۇنىڭغا (مىنىشكە) قادىر ئەمەس ئىدۇق، بىز ھەقىقەتەن پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز» دېگەيسىلەر'),
(4338, 43, 13, 'اﷲ پۈتۈن مەخلۇقاتلارنىڭ تۈرلىرىنى ياراتتى، سىلەرگە ئۈستىگە چىقىشىڭلار ئۈچۈن كېمە ۋە مىنىشىڭلار ئۈچۈن ھايۋان قاتارلىقلارنى ياراتتى. ئاندىن سىلەر ئۇلارنىڭ ئۈستىگە چىققانلىرىڭلاردا پەرەۋردىگارىڭلارنىڭ نېمىتىنى ئەسلەپ: «بىزگە بۇنى بويسۇندۇرۇپ بەرگەن زات پاكتۇر، بىز ئۇنىڭغا (مىنىشكە) قادىر ئەمەس ئىدۇق، بىز ھەقىقەتەن پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز» دېگەيسىلەر'),
(4339, 43, 14, 'اﷲ پۈتۈن مەخلۇقاتلارنىڭ تۈرلىرىنى ياراتتى، سىلەرگە ئۈستىگە چىقىشىڭلار ئۈچۈن كېمە ۋە مىنىشىڭلار ئۈچۈن ھايۋان قاتارلىقلارنى ياراتتى. ئاندىن سىلەر ئۇلارنىڭ ئۈستىگە چىققانلىرىڭلاردا پەرەۋردىگارىڭلارنىڭ نېمىتىنى ئەسلەپ: «بىزگە بۇنى بويسۇندۇرۇپ بەرگەن زات پاكتۇر، بىز ئۇنىڭغا (مىنىشكە) قادىر ئەمەس ئىدۇق، بىز ھەقىقەتەن پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز» دېگەيسىلەر'),
(4340, 43, 15, 'ئۇلار (پەرىشتىلەرنى اﷲ نىڭ قىزلىرى دېيىش بىلەن) اﷲ نىڭ بىر قىسىم بەندىلىرىنى اﷲ نىڭ بىر جۈزئىييى قىلىۋالدى. (مۇنداق دېگۈچى) ئىنسان ھەقىقەتەن اﷲ قا ئاشكارا ناشۈكۈرلۈك قىلغۇچىدۇر'),
(4341, 43, 16, 'اﷲ مەخلۇقاتتىن ئۆزى ئۈچۈن قىزلارنى (بالا) تۇتۇپ، سىلەرنى ئوغۇللارغا خاس قىلدىمۇ؟'),
(4342, 43, 17, 'ئەگەر ئۇلارنىڭ بىرىگە مەرھەمەتلىك اﷲ قا مەسەل قىلغان نەرسە (يەنى قىز بالا) بىلەن بېشارەت بېرىلسە (يەنى خوتۇنىنىڭ قىز تۇغقانلىقى خەۋەر قىلىنسا)، قايغۇغا چۆمگەن ھالدا ئۇنىڭ يۈزى قارىداپ كېتىدۇ'),
(4343, 43, 18, '(ئۇلار اﷲ قا) زىبۇزىننەت ئىچىدە چوڭ بولىدىغان، مۇنازىرىدە مەقسىتىنى ئوچۇق بايان قىلالمايدىغان (قىزلارنى مەنسۇپ قىلامدۇ؟)'),
(4344, 43, 19, 'ئۇلار مەرھەمەتلىك اﷲ نىڭ بەندىلىرى بولغان پەرىشتىلەرگە قىز دەپ ئېتىقاد قىلدى، اﷲ پەرىشتىلەرنى ياراتقاندا، ئۇلار ئۈستىدە بارمىدى؟ (يەنى ئۇلار پەرىشتىلەرنىڭ قىز ئىكەنلىكىنى قەيەردىن بىلىدۇ؟) ئۇلارنىڭ گۇۋاھلىقى (ئۇلارنىڭ نامە - ئەمالىغا) يېزىلىدۇ، (قىيامەت كۈنى) ئۇلار سوراققا تارتىلىدۇ'),
(4345, 43, 20, 'ئۇلار: «مەرھەمەتلىك اﷲ خالىسا، بىز ئۇلارغا (يەنى پەرىشتىلەرگە) چوقۇنمايتتۇق» دېدى. بۇ توغرۇلۇق ئۇلارنىڭ بىلىمى (يەنى ئاساسى) يوق، ئۇلار پەقەت جۆيلۈيدۇ'),
(4346, 43, 21, 'ئۇلارغا بىز قۇرئاندىن ئىلگىرى (قانداقتۇر) بىر كىتاب بەرگەن بولۇپ، ئۇلار شۇ كىتابقا مەھكەم ئىسىلامدۇ؟'),
(4347, 43, 22, 'ئۇنداق ئەمەس، ئۇلار: «ھەقىقەتەن ئاتا - بوۋىلىرىمىزنىڭ بىر خىل دىنغا ئېتىقاد قىلغانلىقىنى بىلىمىز، بىز ئۇلارنىڭ ئىزىدىن مېڭىپ ھىدايەت تاپقۇچىدۇرمىز» دەيدۇ'),
(4348, 43, 23, 'شۇنىڭدەك سەندىن ئىلگىرى ھەر قاچان بىرەر شەھەرگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) ئەۋەتسەكلا، ئۇنىڭ دۆلەتمەن ئادەملىرى: «بىز ھەقىقەتەن ئاتا - بوۋىلىرىمىزنىڭ بىر خىل دىنغا ئېتىقاد قىلغانلىقىنى بىلىمىز، شەك - شۈبھىسىزكى، بىز ئۇلارنىڭ ئىزلىرىدىن ماڭىمىز» دېيىشتى'),
(4349, 43, 24, '(ھەر پەيغەمبەر ئۆز قەۋمىگە) «ئاتا - بوۋاڭلار ئېتىقاد قىلىپ كەلگەن دىنغا قارىغاندا ئەڭ توغرا بىر دىننى كەلتۈرسەم يەنىلا ئاتا - بوۋاڭلارغا ئەگىشەمسىلەر؟» دېدى. ئۇلار: «سىلەر ئېلىپ كەلگەن دىنغا ئىشەنمەيمىز» دېيىشتى'),
(4350, 43, 25, 'بىز ئۇلارنى جازالىدۇق، (توغرا يولنى) ئىنكار قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىن'),
(4351, 43, 26, 'ئۆز ۋاقتىدا ئىبراھىم ئاتىسىغا ۋە قەۋمىگە ئېيتتى: «سىلەر چوقۇنۇۋاتقان بۇتلاردىن مەن راستلا ئادا - جۇدامەن'),
(4352, 43, 27, 'پەقەت مېنى ياراتقان اﷲ مېنى (توغرا يولغا) باشلايدۇ»'),
(4353, 43, 28, 'ئىبراھىم كەلىمە تەۋھىدنى ئەۋلادىدىن (شېرىك كەلتۈرگەنلەر) ئىمانغا قايتسۇن دەپ ئەۋلادى ئىچىدە قالدۇرۇپ كەتتى'),
(4354, 43, 29, 'بەلكى مەن بۇلار (يەنى مەككە ئاھالىسى) نى ۋە بۇلارنىڭ ئاتا - بوۋىلىرىنى (ئۇزۇن ئۆمۈر ۋە نېمەتتىن) بەھرىمەن قىلدىم، تاكى ئۇلارغا ھەق (يەنى قۇرئان) ۋە روشەن پەيغەمبەر (مۇھەممەد ئەلەيھىسسالام) كەلگۈچە'),
(4355, 43, 30, 'ئۇلارغا ھەق (يەنى قۇرئان) كەلگەندە، ئۇلار: «بۇ سېھىردۇر، بىز ھەقىقەتەن ئۇنى ئىنكار قىلغۇچىمىز» دېيىشتى'),
(4356, 43, 31, 'ئۇلار: «بۇ قۇرئان نېمىشقا ئىككى شەھەر (يەنى مەككە بىلەن تائىف ئادەملىرىدىن) بىر كاتتا ئادەمگە نازىل قىلىنمىدى؟» دېيىشتى'),
(4357, 43, 32, 'پەرەۋردىگارىڭنىڭ رەھمىتىنى ئۇلار تەقسىم قىلىپ بېرەمدۇ؟ بىز ئۇلارنىڭ ھاياتىي دۇنيادىكى رىزقىنى ئۇلارنىڭ ئارىسىدا تەقسىم قىلدۇق، ئۇلارنىڭ بەزىسى بەزىسىنى (ھەق بېرىپ) ئىشقا سالسۇن دەپ، ئۇلارنىڭ بەزىسىنىڭ دەرىجىسىنى بەزىسىدىن ئۈستۈن قىلدۇق، پەرەۋردىگارىڭنىڭ رەھمىتى ئۇلارنىڭ توپلىغان نەرسىلىرى (يەنى پۇل - ماللىرى) دىن ياخشىدۇر'),
(4358, 43, 33, 'ئىنسانلار (كۇففارلارنىڭ باياشات تۇرمۇشىنى كۆرۈپ كۇفرىغا قىزىقىپ ئۇلارغا قوشۇلۇپ) ھەممىسى (كۇفرىدا) بىر ئۈممەت بولۇپ قالمايدىغان بولسا ئىدى، مەرھەمەتلىك اﷲ نى ئىنكار قىلغان كىشىلەرنىڭ ئۆيلىرىنىڭ ئۆگزىلىرىنى ۋە ئۇنىڭغا چىقىدىغان شوتىلىرىنى كۈمۈشتىن قىلىپ بېرەتتۇق'),
(4359, 43, 34, 'ئۇلارنىڭ ئۆيلىرىنىڭ ئىشىكلىرىنى ۋە تەختلىرىنىمۇ كۈمۈشتىن قىلىپ بېرەتتۇق، ئۇلار تەختلەرگە يۆلىنىپ ئولتۇراتتى'),
(4360, 43, 35, '(يۇقىرىقىلارنىڭ بەزىسىنى) ئالتۇندىن قىلىپ بېرەتتۇق، ئۇلارنىڭ ھەممىسى پەقەت دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر. پەرەۋردىگارىڭنىڭ ھۇزۇرىدىكى ئاخىرەت (يەنى جەننەت ۋە ئۇنىڭدىكى نازۇنېمەتلەر) تەقۋادارلارغا خاستۇر'),
(4361, 43, 36, 'كىمكى مېھرىبان اﷲ نى ياد ئېتىشتىن (يەنى قۇرئاندىن) يۈز ئۆرۈيدىكەن، بىز ئۇنىڭغا شەيتاننى مۇسەللەت قىلىمىز (شەيتان ئۇنى ۋەسۋەسە قىلىدۇ)، شەيتان ئۇنىڭغا ھەمىشە ھەمراھ بولىدۇ'),
(4362, 43, 37, 'شەك - شۈبھىسىزكى، شەيتانلار ئۇلارنى (يەنى ئازغۇن كۇففارلارنى) توغرا يولدىن توسىدۇ، ئۇلار بولسا ئۆزلىرىنى توغرا يولدا دەپ گۇمان قىلىدۇ'),
(4363, 43, 38, 'كاپىر ئادەم (ھەمراھى بىلەن قىيامەت كۈنى) دەرگاھىمىزغا كەلگەن چاغدا (ھەمراھىغا): «كاشكى سەن بىلەن مېنىڭ ئارامدا شەرق بىلەن غەربنىڭ ئارىسىدەك ئارىلىق بولسا ئىكەن. (سەن) نېمىدېگەن يامان ھەمراھ! (چۈنكى سەن مېنىڭ بەختسىزلىكىمگە سەۋەبچى بولدۇڭ)» دەيدۇ'),
(4364, 43, 39, '(ئۇلارغا) «(دۇنيادىكى چاغدا) زۇلۇم قىلغانلىقىڭلار (يەنى شېرىك كەلتۈرگەنلىكىڭلار) ئۈچۈن بۈگۈن سىلەرنىڭ ئازابتا ئورتاق بولغانلىقىڭلار سىلەرگە پايدا بەرمەيدۇ» دېيىلىدۇ'),
(4365, 43, 40, '(ئى مۇھەممەد!) سەن گاسلارغا ئاڭلىتالامسەن؟ يا كورلارنى توغرا يولغا سالالامسەن؟ (يەنى كۇففارلار گاسلارغا، كورلارغا ئوخشايدۇ) ئاشكارا گۇمراھلىقتا بولغان كىشىنى ھىدايەت قىلالامسەن؟'),
(4366, 43, 41, '(ئۇلارنى جازالاشتىن ئىلگىرى) سېنى ئېلىپ كەتسەك (يەنى ۋاپات قىلدۇرساق ۋاپاتىڭدىن كېيىن) ئۇلارنى چوقۇم جازالايمىز'),
(4367, 43, 42, 'ياكى بىز ساڭا (ھاياتىڭدا) ئۇلارغا ۋەدە قىلغان ئازابنى كۆرسىتىمىز، بىز، شۈبھىسىزكى، ئۇلارغا (يەنى ئۇلارغا ئازاب قىلىشقا) قادىرمىز'),
(4368, 43, 43, '(ئى مۇھەممەد!) ساڭا ۋەھيى قىلىنغان قۇرئانغا چىڭ ئېسىلغىن، سەن ھەقىقەتەن توغرا يولدىدۇرسەن'),
(4369, 43, 44, 'قۇرئان ساڭا ۋە سېنىڭ قەۋمىڭگە ئەلۋەتتە (ئۇلۇغ) شەرەپتۇر، سىلەر كەلگۈسىدە (بۇ نېمەت ئۈستىدە) سورىلىسىلەر'),
(4370, 43, 45, 'سەندىن بۇرۇن بىز ئەۋەتكەن پەيغەمبەرلىرىمىزدىن سوراپ باققىنكى، مېھرىبان اﷲ تىن باشقا ئىبادەت قىلىنىدىغان ئىلاھلارنى بەلگىلىدۇقمۇ؟ (پەيغەمبەرلەرنىڭ ئىچىدە اﷲ تىن غەيرىيگە ئىبادەت قىلىشقا دەۋەت قىلغىنى بارمۇ؟)'),
(4371, 43, 46, 'بىز ھەقىقەتەن مۇسانى پىرئەۋنگە ۋە ئۇنىڭ تەۋەلىرىگە (ئۇنىڭ راست پەيغەمبەر ئىكەنلىكىنى ئىسپاتلايدىغان شانلىق) مۆجىزىلىرىمىز بىلەن ئەۋەتتۇق، مۇسا (پىرئەۋنگە): «مەن ھەقىقەتەن جىمى ئالەملەرنىڭ پەرۋەردىگارىنىڭ (سېنى ۋە قەۋمىڭنى بىر اﷲ قا ئىبادەت قىلىشقا دەۋەت قىلىش ئۈچۈن ئەۋەتكەن) ئەلچىسىمەن» دېدى'),
(4372, 43, 47, 'مۇسا ئۇلارغا بىزنىڭ مۆجىزىلىرىمىزنى ئېلىپ كەلگەندە، ئۇلار ناگاھان مۆجىزىلەرنى (مەسخىرە قىلىپ) كۈلدى'),
(4373, 43, 48, 'ئۇلارغا بىر - بىرىدىن كاتتا مۆجىزىلەرنى كۆرسەتتۇق، ئۇلارنى (كۇفرىدىن) قايتسۇن دەپ (تۈرلۈك) ئازابلار بىلەن جازالىدۇق'),
(4374, 43, 49, '(ئۇلار ئازابنى كۆرگەندە) «ئى سېھىرگەر! پەرەۋردىگارىڭغا ئۇنىڭ ساڭا بەرگەن (دۇئايىڭنى ئىجابەت قىلىشتىن ئىبارەت) ئەھدى بويىچە بىز ئۈچۈن دۇئا قىلساڭ (ئەگەر ئۇ دۇئايىڭ بىلەن بىزدىن ئازابنى كۆتۈرۈۋەتسە)، بىز چوقۇم ئىمان ئېيتىمىز» دېدى'),
(4375, 43, 50, '(مۇسانىڭ دۇئاسى بىلەن) ئۇلاردىن ئازابنى كۆتۈرۈۋەتكىنىمىزدە، ناگاھان ئۇلار (ئەھدىنى) بۇزۇپ (كۇفرىدا) چىڭ تۇردى'),
(4376, 43, 51, 'پىرئەۋن ئۆز قەۋمى ئىچىدە (پەخىرلەنگەن ھالدا) نىدا قىلىپ ئېيتتى: «ئى قەۋمىم! مىسىرنىڭ پادىشاھلىقى، ئاستىمدىن ئېقىپ تۇرغان بۇ دەريالار مېنىڭ ئەمەسمۇ؟ (مېنىڭ بۇ بۈيۈكلۈكۈمنى) كۆرمەمسىلەر؟ بەلكى مەن ئوچۇق گەپ قىلالمايدىغان بۇ ئەرزىمەس ئادەمدىن (يەنى مۇسا ئەلەيھىسسالامدىن) ياخشىمەن»'),
(4377, 43, 52, 'پىرئەۋن ئۆز قەۋمى ئىچىدە (پەخىرلەنگەن ھالدا) نىدا قىلىپ ئېيتتى: «ئى قەۋمىم! مىسىرنىڭ پادىشاھلىقى، ئاستىمدىن ئېقىپ تۇرغان بۇ دەريالار مېنىڭ ئەمەسمۇ؟ (مېنىڭ بۇ بۈيۈكلۈكۈمنى) كۆرمەمسىلەر؟ بەلكى مەن ئوچۇق گەپ قىلالمايدىغان بۇ ئەرزىمەس ئادەمدىن (يەنى مۇسا ئەلەيھىسسالامدىن) ياخشىمەن»'),
(4378, 43, 53, 'نېمە ئۈچۈن ئۇنىڭغا (ئاسماندىن) ئالتۇن بىلەيزۈكلەر تاشلانمىدى؟ ياكى نېمىشقا (ئۇنىڭ راستلىقىغا گۇۋاھلىق بېرىش ئۈچۈن) ئۇنىڭ بىلەن بىللە پەرىشتىلەر ھەمراھ بولۇپ كەلمىدى؟»'),
(4379, 43, 54, 'پىرئەۋن قەۋمىنى گوللىغانلىقتىن، قەۋمى ئۇنىڭغا ئىتائەت قىلدى، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى'),
(4380, 43, 55, 'ئۇلار بىزنى دەرغەزەپ قىلغاندا، ئۇلارنى بىز (ئەڭ قاتتىق جازالار بىلەن) جازالىدۇق، ئۇلارنىڭ ھەممىسىنى (دېڭىزدا) غەرق قىلدۇق'),
(4381, 43, 56, 'ئۇلارنى بىز (كۇففارلارنىڭ ئازابقا تېگىشلىك بولۇشىدا) كېيىنكىلەرگە نەمۇنە ۋە ئىبرەت قىلدۇق'),
(4382, 43, 57, '(قۇرئاندا) مەريەمنىڭ ئوغلى (يەنى ئىسا ئەلەيھىسسالام) مىسال كەلتۈرۈلسە، ناگاھان سېنىڭ قەۋمىڭ (خۇشاللىقتىن) سۈرەن سېلىشىپ كېتىدۇ'),
(4383, 43, 58, 'ئۇلار: «بىزنىڭ ئىلاھلىرىمىز ياخشىمۇ ياكى ئىسامۇ؟» دېدى. ئۇلار بۇ مىسالنى پەقەت خۇسۇمەت قىلىش ئۈچۈنلا كەلتۈردى، بەلكى ئۇلار جېدەلخور قەۋمدۇر'),
(4384, 43, 59, 'ئۇ (يەنى ئىسا) (ناسارالار گۇمان قىلغاندەك ئىلاھ ياكى ئىلاھنىڭ ئوغلى ئەمەس) پەقەت پەيغەمبەرلىك نېمىتىمىزگە ئېرىشكەن بىر بەندىدۇر، ئۇنى بىز ئىسرائىل ئەۋلادىغا (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) بىر دەلىل قىلدۇق'),
(4385, 43, 60, 'ئەگەر بىز خالىساق، ئەلۋەتتە سىلەرگە زېمىندا ئورۇنباسار بولىدىغان پەرىشتىلەرنى ۋۇجۇتقا كەلتۈرەتتۇق'),
(4386, 43, 61, 'ئۇ (يەنى ئىسا) ئەلۋەتتە قىيامەت (يېقىنلاشقانلىقنىڭ) ئالامىتىدۇر، ئۇنىڭغا (يەنى قىيامەتكە) شەك كەلتۈرمەڭلار. (ئى مۇھەممەد! ئۇلارغا ئېيتقىنكى) «ماڭا ئەگىشىڭلار، بۇ (مېنىڭ دەۋەت قىلغىنىم) توغرا يولدۇر'),
(4387, 43, 62, 'سىلەرنى شەيتان (توغرا يولدا) مېڭىشتىن توسمىسۇن (يەنى شەيتاننىڭ ۋەسۋەسىسىگە ئالدانماڭلار)، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر»'),
(4388, 43, 63, 'ئىسا روشەن مۆجىزىلەر بىلەن كېلىپ ئېيتتى: «سىلەرگە مەن ھېكمەت ئېلىپ كەلدىم. سىلەرگە ئىختىلاپ قىلىۋاتقان نەرسەڭلارنىڭ (يەنى دىنىي ئىشلارنىڭ) بەزىسىنى بايان قىلىپ بېرىمەن، اﷲ تىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار'),
(4389, 43, 64, 'اﷲ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر ۋە سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر»'),
(4390, 43, 65, '(ناسارالار) نىڭ پىرقىلىرى (ئىسا توغرىسىدا) ئۆز - ئارا ئىختىلاپ قىلىشتى، زالىملارغا قاتتىق كۈن (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن ۋاي!'),
(4391, 43, 66, 'ئۇلار پەقەت قىيامەتنىڭ ئۆزلىرى تۇيمىغان ھالدا ئۇشتۇمتۇت كېلىشىنىلا كۈتىدۇ'),
(4392, 43, 67, 'بۇ كۈندە دوستلار بىر - بىرىگە دۈشمەن بولىدۇ، پەقەت (خۇدالىق ئۈچۈن دوستلاشقان) تەقۋادارلارلا (ئۇنداق ئەمەستۇر)'),
(4393, 43, 68, '(ئۇلارغا) «بەندىلىرىم! بۈگۈن سىلەرگە قورقۇنچ يوق، غەم - قايغۇمۇ يوق» (دېيىلىدۇ)'),
(4394, 43, 69, 'ئۇلار بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتقان ۋە مۇسۇلمان بولغانلار ئىدى'),
(4395, 43, 70, '(ئۇلارغا) «سىلەر ئاياللىرىڭلار بىلەن بىللە خۇشال - خۇرام ھالدا جەننەتكە كىرىڭلار» (دېيىلىدۇ)'),
(4396, 43, 71, 'ئۇلارغا ئالتۇن لېگەنلەردە (تائام)، ئالتۇن جاملاردا (شاراب) تۇتۇلىدۇ. جەننەتتە كۆڭۈللەر تارتىدىغان، كۆزلەر لەززەتلىنىدىغان نەرسىلەر بار، سىلەر جەننەتتە مەڭگۈ قالىسىلەر'),
(4397, 43, 72, 'سىلەر (دۇنيادا) قىلغان (ياخشى) ئەمەلىڭلار بىلەن ۋارىس بولغان جەننەت ئەنە شۇدۇر'),
(4398, 43, 73, 'جەننەتتە نۇرغۇن (تۈرلۈك) مېۋىلەر بار، ئۇنىڭدىن يەيسىلەر'),
(4399, 43, 74, 'گۇناھكارلار (يەنى كۇففارلار) ھەقىقەتەن دوزاخ ئازابىدا مەڭگۈ قالغۇچىلاردۇر'),
(4400, 43, 75, 'ئۇلاردىن ئازاب (بىردەممۇ) يېنىكلىتىلمەيدۇ، ئۇلار دوزاختا (ھەر قانداق ياخشىلىقتىن) ئۈمىدسىزلەردۇر'),
(4401, 43, 76, 'ئۇلارغا بىز زۇلۇم قىلمىدۇق ۋە لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى'),
(4402, 43, 77, 'ئۇلار (يەنى كۇففارلار) (دوزاخقا مۇئەككەل پەرىشتىگە): «ئى مالىك! پەرۋەردىگارىڭ بىزگە ئۆلۈم ھۆكۈم قىلسۇن» دەپ توۋلايدۇ. مالىك: «سىلەر ئازابتا چوقۇم قالىسىلەر» دەيدۇ'),
(4403, 43, 78, 'بىز سىلەرگە ھەقىقەتەن ھەق (دىن) نى كەلتۈردۇق ۋە لېكىن سىلەرنىڭ كۆپچىلىكىڭلار ھەق (دىن) نى يامان كۆرگۈچىلەردۇر'),
(4404, 43, 79, 'ئۇلار (مۇھەممەد ئەلەيھىسسالامغا سۇيىقەست قىلىشتا) بىر ئىشنى قارار قىلغان بولسا، بىز (مۇھەممەد ئەلەيھىسسالامغا) ياردەم بېرىشتە بىر ئىشنى قارار قىلىمىز'),
(4405, 43, 80, 'ئۇلار گۇمان قىلامدۇكى، بىز ئۇلارنىڭ سىرلىرىنى ۋە پىچىرلاشقانلىقلىرىنى ئاڭلىمايمىز، ھەرگىز ئۇنداق ئەمەس (ئۇنى ئاڭلاپ تۇرىمىز)، بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلىرىمىز) ئۇلارنىڭ ئالدىدا (ئۇلارنىڭ ئەمەللىرىنى) يېزىپ تۇرىدۇ'),
(4406, 43, 81, '«ئەگەر مېھرىبان اﷲ نىڭ بالىسى بولىدىغان بولسا، مەن (ئۇ بالىغا) ئىبادەت قىلغۇچىلارنىڭ ئەۋۋىلى بولاتتىم» دېگىن'),
(4407, 43, 82, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارى، ئەرشنىڭ پەرۋەردىگارى ئۇلارنىڭ سۆزلىرىدىن پاكتۇر'),
(4408, 43, 83, 'ئۇلارنى (يەنى مەككە كۇففارلىرىنى) (گۇمراھلىقلىرىدا ۋە نادانلىقلىرىدا) قويۇپ بەرگىن، ئۇلار ۋەدە قىلىنغان كۈنىگە مۇلاقات بولغانغا قەدەر (باتىللىرىغا) چۆمسۇن ۋە (دۇنياسى بىلەن) ئوينىسۇن'),
(4409, 43, 84, 'اﷲ ئاسماندىمۇ ئىلاھتۇر، زېمىندىمۇ ئىلاھتۇر، اﷲ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر'),
(4410, 43, 85, 'ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقى ئىلكىدە بولغان زات كاتتىدۇر، قىيامەتنىڭ (بولىدىغان ۋاقتى) توغرىسىدىكى بىلىم ھەقىقەتەن (يالغۇر) اﷲ نىڭ دەرگاھىدىدۇر. سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر'),
(4411, 43, 86, 'ئۇلارنىڭ اﷲ نى قويۇپ چوقۇنغان نەرسىلىرى شاپائەت قىلالمايدۇ. پەقەت ھەق بىلەن گۇۋاھلىق بەرگەن (يەنى لا الە الا اﷲ دېگەن، تىلى بىلەن ئېيتقاننى دىلى بىلەن) ھەقىقىي بىلگەنلەر بۇنىڭدىن مۇستەسنا'),
(4412, 43, 87, 'ئەگەر سەن ئۇلاردىن ئۇلارنى كىم خەلق ئەتكەنلىكلىرىنى سورىساڭ، ئۇلار چوقۇم «اﷲ» دەيدۇ، ئۇلار قانداقمۇ (اﷲ قا ئىبادەت قىلىشتىن بۇتلارغا ئىبادەت قىلىشقا) بۇرۇلۇپ كېتىدۇ'),
(4413, 43, 88, '(اﷲ تائالا مۇھەممەد ئەلەيھىسسالامنىڭ) «ئى پەرۋەردىگارىم! بۇلار ھەقىقەتەن ئىمان ئېيتمايدىغان قەۋمدۇر» دېگەن سۆزىنى (بىلىدۇ)'),
(4414, 43, 89, 'سەن ئۇلاردىن يۈز ئۆرۈگىن، سالام دېگىن (يەنى ئۇلارغا بەتدۇئامۇ قىلمىغىن، دىنغا دەۋەتمۇ قىلمىغىن)، ئۇلار كەلگۈسىدە (كۇفرىنىڭ ئاقىۋىتىنى) بىلىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حا، مىم'),
(4416, 44, 2, 'روشەن قۇرئان بىلەن قەسەم قىلىمەن'),
(4417, 44, 3, 'بىز قۇرئاننى ھەقىقەتەن مۇبارەك كېچىدە (يەنى شەبى قەدرى كېچىسىدە) نازىل قىلدۇق، بىز ھەقىقەتەن ئىنسانلارنى (قۇرئان بىلەن) ئاگاھلاندۇرغۇچى بولدۇق'),
(4418, 44, 4, 'ئۇ كېچىدە ھەر بىر ھېكمەتلىك (يەنى ھېكمەتنىڭ تەقەززاسى بويىچە قىلىنغان) ئىش (يەنى بەندىلەرنىڭ رىزقى، ئەجىلى ۋە باشقا ئەھۋالى) ئايرىلىدۇ'),
(4419, 44, 5, '(شۇ كېچە تەقدىر قىلىنغان ئىشلارنىڭ ھەممىسى) بىزنىڭ دەرگاھىمىزدىن بولغان ئىشتۇر، بىز ھەقىقەتەن (ئىنسانلارغا پەيغەمبەرلەرنى) ئەۋەتكۈچى بولدۇق'),
(4420, 44, 6, '(شۇ) پەرۋەردىگارىڭنىڭ رەھمىتىدىندۇر. ئۇ ھەقىقەتەن (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر. (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر'),
(4421, 44, 7, 'ئەگەر سىلەر ھەقىقىي ئىشەنگۈچى بولساڭلار، (ئۇ) ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پەرۋەردىگارىدۇر'),
(4422, 44, 8, 'ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر. ئۇ (ئۆلۈكنى) تىرىلدۈرەلەيدۇ، (تىرىكنىڭ) جېنىنى ئالالايدۇ، ئۇ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئىلگىرىكى ئاتا - بوۋاڭلارنىڭ پەرۋەردىگارىدۇر'),
(4423, 44, 9, 'ئۇنداق ئەمەس (يەنى ئۇلار ھەقىقىي ئىشەنگۈچى ئەمەس)، بەلكى ئۇلار (قىيامەتتىن) شەكتە بولۇپ (ھەقنى ئىستىھزا قىلىپ) ئوينايدۇ'),
(4424, 44, 10, '(ئى مۇھەممەد!) سەن ئاسمان (ھەممە ئادەم) ئوچۇق (كۆرىدىغان) تۈتۈننى كەلتۈرىدىغان كۈندە (ئۇلارغا بولىدىغان ئازابنى) كۈتكىن'),
(4425, 44, 11, '(ئۇ تۈتۈن) كىشىلەرنى ئورىۋالىدۇ، بۇ قاتتىق ئازابتۇر'),
(4426, 44, 12, '(ئۇلار) «پەرۋەردىگارىمىز! بىزدىن ئازابنى كۆتۈرۈۋەتكىن، بىز ھەقىقەتەن ئىمان كەلتۈرگۈچىدۇرمىز» (دەيدۇ)'),
(4427, 44, 13, '(ئازاب كۆتۈرۈلۈپ كەتكەندە) ئۇلار قانداقمۇ ئىبرەت ئالسۇن، ھالبۇكى، ئۇلارغا روشەن پەيغەمبەر كەلدى (شۇنداق تۇرۇقلۇق ئۇلار ئىمان ئېيتمىدى)'),
(4428, 44, 14, 'ئاندىن ئۇلار ئۇنىڭدىن يۈز ئۆرۈدى ۋە: «ئۇ ئۆگىتىلگەن (يەنى قۇرئاننى كىشىلەردىن ئۆگەنگەن) دۇر، مەجنۇندۇر» دېدى'),
(4429, 44, 15, 'بىز ھەقىقەتەن ئازابنى (سىلەردىن) ئازغىنا ۋاقىت كۆتۈرۈۋېتىمىز، (ئاندىن) سىلەر ھەقىقەتەن (ئىلگىرىكى كاپىرلىق ھالىتىڭلارغا) قايتىۋالىسىلەر'),
(4430, 44, 16, 'ئۇلارنى قاتتىق تۇتقان كۈنىمىزدە چوقۇم جازالايمىز'),
(4431, 44, 17, 'ئۇلاردىن ئىلگىرى بىز ھەقىقەتەن پىرئەۋننىڭ قەۋمىنى سىنىدۇق، ئۇلارغا ئېسىل بىر پەيغەمبەر كەلدى'),
(4432, 44, 18, '(مۇسا ئۇلارغا ئېيتتى) «ماڭا اﷲ نىڭ بەندىلىرىنى (يەنى بەنى ئىسرائىلنى) تاپشۇرۇپ بېرىڭلار، مەن سىلەرگە ھەقىقەتەن ئىشەنچلىك پەيغەمبەر'),
(4433, 44, 19, 'اﷲ قا تەكەببۇرلۇق قىلماڭلار، مەن سىلەرگە ھەقىقەتەن روشەن مۆجىزە بىلەن كەلدىم'),
(4434, 44, 20, 'مەن ھەقىقەتەن مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلارغا سېغىنىپ، سىلەرنىڭ مېنى ئۆلتۈرۈشۈڭلاردىن پاناھ تىلەيمەن'),
(4435, 44, 21, 'ئەگەر ماڭا ئىمان كەلتۈرمىسەڭلار مېنى (ئۆز ھالىمغا) قويۇۋېتىڭلار»'),
(4436, 44, 22, 'مۇسا پەرۋەردىگارىغا: «بۇلار گۇناھكار قەۋمدۇر» دەپ دۇئا قىلدى'),
(4437, 44, 23, '(اﷲ ئېيتتى) «مېنىڭ بەندىلىرىمنى كېچىدە ئېلىپ چىقىپ كەتكىن، سىلەر ھەقىقەتەن قوغلىنىسىلەر'),
(4438, 44, 24, 'دېڭىزنى تىپتىنچ (يەنى سەن ئۆتۈپ بولغاندىن كېيىن قۇرۇق ھالىتى بويىچە) قويغىن، ئۇلار ھەقىقەتەن غەرق قىلىنغۇچى قوشۇندۇر»'),
(4439, 44, 25, 'ئۇلار (غەرق بولۇپ) نۇرغۇن باغلارنى، بۇلاقلارنى، زىرائەتلەرنى، چىرايلىق جايلارنى قالدۇردى'),
(4440, 44, 26, 'ئۇلار (غەرق بولۇپ) نۇرغۇن باغلارنى، بۇلاقلارنى، زىرائەتلەرنى، چىرايلىق جايلارنى قالدۇردى'),
(4441, 44, 27, 'ئۇلار بەھرىمەن بولۇۋاتقان نېمەتلەرنى قالدۇردى'),
(4442, 44, 28, 'شۇنداق قىلىپ، ئۇلارنى باشقىلارغا (يەنى بەنى ئىسرائىلغا) مىراس قىلىپ بەردۇق'),
(4443, 44, 29, 'ئۇلارغا ئاسمانمۇ، زېمىنمۇ يىغلىمىدى، ئۇلارغا مۆھلەتمۇ بېرىلمىدى'),
(4444, 44, 30, 'شەك - شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىنى خار قىلغۇچى ئازابتىن – پىرئەۋن (ئازابى) دىن قۇتقۇزدۇق، پىرئەۋن ھەقىقەتەن مۇتەكەببىر ئىدى، ھەددىدىن ئاشقۇچىلاردىن ئىدى'),
(4445, 44, 31, 'شەك - شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىنى خار قىلغۇچى ئازابتىن – پىرئەۋن (ئازابى) دىن قۇتقۇزدۇق، پىرئەۋن ھەقىقەتەن مۇتەكەببىر ئىدى، ھەددىدىن ئاشقۇچىلاردىن ئىدى'),
(4446, 44, 32, 'ئۇلارنى (يەنى ئۇلارنىڭ ئەينى زاماندا مۇشۇنداق شەرەپكە لايىق ئىكەنلىكىنى) بىلىپ تۇرۇپ (ئۆز زامانىدىكى) جاھان ئەھلىدىن ئارتۇق قىلدۇق'),
(4447, 44, 33, 'ئۇلارغا (يەنى تەپەككۇر قىلغۇچىلارغا) روشەن سىناق بار نۇرغۇن مۆجىزىلەرنى ئاتا قىلدۇق'),
(4448, 44, 34, 'بۇلار (يەنى قۇرەيش كۇففارلىرى) چوقۇم ئېيتىدۇ:'),
(4449, 44, 35, '«پەقەت بىرلا قېتىم ئۆلىمىز، قايتا تىرىلمەيمىز، ئەگەر (ئاخىرەت بار دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، بىزنىڭ ئاتا - بوۋىلىرىمىزنى (تىرىلدۈرۈپ) ئەكېلىپ بېقىڭلار»'),
(4450, 44, 36, '«پەقەت بىرلا قېتىم ئۆلىمىز، قايتا تىرىلمەيمىز، ئەگەر (ئاخىرەت بار دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، بىزنىڭ ئاتا - بوۋىلىرىمىزنى (تىرىلدۈرۈپ) ئەكېلىپ بېقىڭلار»'),
(4451, 44, 37, 'ئۇلار (يەنى مۇشرىكلار) كۈچلۈكمۇ؟ ياكى تۈببە قەۋمىمۇ؟ ئۇلاردىن ئىلگىرى ئۆتكەن (نۇرغۇن) قەۋملەرنى ھالاك قىلدۇق. چۈنكى ئۇلار گۇناھكار ئىدى'),
(4452, 44, 38, 'ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئويناپ (يەنى بىكارغا) ياراتقىنىمىز يوق'),
(4453, 44, 39, 'ئۇلارنى بىز پەقەت ھەق ياراتتۇق ۋە لېكىن ئىنسانلارنىڭ تولىسى (بۇنى) بىلمەيدۇ'),
(4454, 44, 40, 'باتىل بىلەن ھەقنى ئايرىيدىغان كۈن (يەنى قىيامەت كۈنى) ئىنسانلارنىڭ ھەممىسىنىڭ (ھېسابى ئۈچۈن) تەيىنلەنگەن ۋاقىتتۇر'),
(4455, 44, 41, 'ئۇ كۈندە دوست دوستقا ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلار ياردەمگىمۇ ئېرىشەلمەيدۇ'),
(4456, 44, 42, 'پەقەت اﷲ رەھمەت قىلغان كىشى بۇنىڭدىن مۇستەسنا. اﷲ ھەقىقەتەن غالىبتۇر، ناھايىتى مېھرىباندۇر'),
(4457, 44, 43, 'زەققۇم دەرىخى ھەقىقەتەن گۇناھكارلارنىڭ تامىقىدۇر'),
(4458, 44, 44, 'زەققۇم دەرىخى ھەقىقەتەن گۇناھكارلارنىڭ تامىقىدۇر'),
(4459, 44, 45, 'ئۇ ئېرىتىلگەن مىستەك (قىزىق) دۇر، ئۇ قورساقلاردا قايناقسۇدەك قاينايدۇ'),
(4460, 44, 46, 'ئۇ ئېرىتىلگەن مىستەك (قىزىق) دۇر، ئۇ قورساقلاردا قايناقسۇدەك قاينايدۇ'),
(4461, 44, 47, '(دوزاخقا مۇئەككەل پەرىشتىلەرگە) «ئۇنى تۇتۇپ سۆرەپ دوزاخنىڭ ئوتتۇرىسىغا ئېلىپ بېرىڭلار، ئاندىن ئۇنىڭ بېشىغا قايناقسۇ قۇيۇپ ئازابلاڭلار» دېيىلىدۇ'),
(4462, 44, 48, '(دوزاخقا مۇئەككەل پەرىشتىلەرگە) «ئۇنى تۇتۇپ سۆرەپ دوزاخنىڭ ئوتتۇرىسىغا ئېلىپ بېرىڭلار، ئاندىن ئۇنىڭ بېشىغا قايناقسۇ قۇيۇپ ئازابلاڭلار» دېيىلىدۇ'),
(4463, 44, 49, '(ئۇنىڭغا خارلاش ۋە مەسخىرە قىلىش يۈزىسىدىن) «(بۇ ئازابنى) تېتىغىن، سەن ھەقىقەتەن ئىززەتلىك ئۇلۇغ زات ئىدىڭ'),
(4464, 44, 50, 'بۇ (ئازاب) ھەقىقەتەن (ھايات ۋاقتىڭلاردا) سىلەر شەكلەنگەن نەرسىدۇر» دېيىلىدۇ'),
(4465, 44, 51, 'تەقۋادارلار ھەقىقەتەن بىخەتەر جايدا بولىدۇ'),
(4466, 44, 52, 'باغلاردا، بۇلاقلارنىڭ ئارىسىدا بولىدۇ'),
(4467, 44, 53, 'ئۇلار قېلىن، يۇپقا يىپەك كىيىملەرنى كىيىپ بىر - بىرىگە قاراپ ئولتۇرۇشىدۇ'),
(4468, 44, 54, '(ئۇلارنى تۈرلۈك ھۆرمەتلەر بىلەن) مۇشۇنداق ئىكرام قىلدۇق، شەھلا كۆزلۈك ھۆرلەرنى ئۇلارغا جۈپ قىلىمىز'),
(4469, 44, 55, 'ئۇلار جەننەتتە (مەيدە ئېغىرلىشىش ۋە كېسەللىكلەردىن) ئەمىن بولغان ھالدا (خىزمەتچىلەردىن) ھەممە مېۋىلەرنى (كەلتۈرۈشنى) تەلەپ قىلىدۇ'),
(4470, 44, 56, 'ئۇلار ئىلگىرىكى (يەنى دۇنيادىكى) ئۆلۈمدىن باشقا (جەننەتتە) ھېچقانداق ئۆلۈمنى تېتىمايدۇ، اﷲ ئۇلارنى دوزاخ ئازابىدىن ساقلايدۇ'),
(4471, 44, 57, '(بۇ) پەرۋەردىگارىڭنىڭ مەرھەمىتىدىندۇر، بۇ زور مۇۋەپپەقىيەتتۇر'),
(4472, 44, 58, 'ئۇلارنىڭ ۋەز - نەسىھەت ئېلىشى ئۈچۈن قۇرئاننى سېنىڭ تىلىڭ بىلەن ئاسانلاشتۇرۇپ بەردۇق'),
(4473, 44, 59, '(ئى مۇھەممەد! ئۇلارغا چۈشىدىغان ئازابنى) كۈتكىن، شۈبھىسىزكى، ئۇلار (سېنىڭ ھالاك بولۇشۇڭنى) كۈتكۈچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حا، مىم'),
(4475, 45, 2, '(بۇ) كىتاب غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ تەرىپىدىن نازىل قىلىنغاندۇر'),
(4476, 45, 3, 'ئاسمانلاردا ۋە زېمىندا مۆمىنلەر ئۈچۈن (اﷲ نىڭ قۇدرىتىنى كۆرسىتىدىغان) ھەقىقەتەن نۇرغۇن دەلىللەر بار'),
(4477, 45, 4, 'سىلەرنىڭ يارىتىلىشىڭلاردا ۋە (اﷲ زېمىندا) تارقىتىۋەتكەن جانىۋارلاردا (اﷲ نىڭ قۇدرىتىگە) جەزمەن ئىشىنىدىغان قەۋم ئۈچۈن نۇرغۇن دەلىللەر بار'),
(4478, 45, 5, 'كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا، اﷲ نىڭ بۇلۇتتىن (كۆكتىن) يامغۇر ياغدۇرۇپ ئۇنىڭ بىلەن رىزىقلەندۈرۈپ ئۆلگەن زېمىننى تىرىلدۈرۈشىدە ۋە شاماللارنىڭ يۈزلىنىشىنى ئۆزگەرتىپ تۇرۇشىدا، چۈشىنىدىغان قەۋم ئۈچۈن (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار'),
(4479, 45, 6, 'ئەنە شۇلار اﷲ نىڭ ئايەتلىرىدۇر. (ئى مۇھەممەد!) ئۇنى ساڭا ھەقلىق بىلەن تىلاۋەت قىلىپ بېرىمىز. ئۇلار (يەنى مەككە كۇففارلىرى) اﷲ نى ۋە ئۇنىڭ ئايەتلىرىنى قويۇپ قايسى سۆزگە ئىشىنىدۇ؟'),
(4480, 45, 7, 'ھەر بىر گۇناھكار يالغانچىغا ۋاي!'),
(4481, 45, 8, 'ئۇ اﷲ نىڭ ئايەتلىرىنىڭ ئۆزىگە ئوقۇپ بېرىلىۋاتقانلىقىنى ئاڭلاپ تۇرىدۇ، ئاندىن تەكەببۇرلۇق قىلغان ھالدا گويا ئۇنى ئاڭلىمىغاندەك (كۇفرىدا) چىڭ تۇرىدۇ (گۇمراھلىقتا تېخىمۇ ئەزۋەيلەيدۇ)، ئۇنىڭغا قاتتىق ئازاب بىلەن خۇش خەۋەر بەرگىن'),
(4482, 45, 9, 'ئۇ بىزنىڭ ئايەتلىرىمىزدىن بىرەر نەرسىنى بىلگەن چاغدا (يەنى ئۇنىڭغا قۇرئان ئايەتلىرى يەتكەن چاغدا)، ئۇنى مەسخىرە قىلىدىغان نەرسە قىلىۋالىدۇ، ئەنە شۇلار خار قىلغۇچى ئازابقا دۇچار بولىدۇ'),
(4483, 45, 10, 'ئۇلارنى ئالدىدا جەھەننەم (كۈتۈپ تۇرىدۇ)، ئۇلارنىڭ ئېرىشكەن نەرسىلىرى (يەنى دۇنيادا تاپقان ماللىرى ۋە بالىلىرى) ۋە اﷲ نى قويۇپ دوست تۇتقان نەرسىلىرى (يەنى بۇتلىرى) ئۇلارغا ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلار چوڭ ئازابقا دۇچار بولىدۇ'),
(4484, 45, 11, 'بۇ (قۇرئان ئۇنىڭغا ئىشەنگەن ۋە ئۇنىڭغا ئەگەشكەن كىشىگە كامىل) ھىدايەتتۇر، پەرۋەردىگارىنىڭ ئايەتلىرىنى ئىنكار قىلغانلار ئەڭ قاتتىق ئازابقا دۇچار بولىدۇ'),
(4485, 45, 12, 'اﷲ نىڭ ئەمرى بىلەن دېڭىزدا كېمىلەرنىڭ يۈرۈشى ئۈچۈن، سىلەرنىڭ اﷲ نىڭ پەزلىدىن تەلەپ قىلىشىڭلار ئۈچۈن (يەنى تىجارەت قىلىشىڭلار، بېلىق تۇتۇشۇڭلار، دېڭىز ئاستىدىن ئۈنچە - مەرۋايىتلارنى سۈزۈۋېلىشىڭلار ئۈچۈن) ۋە (اﷲ قا) شۈكۈر قىلىشىڭلار ئۈچۈن، اﷲ سىلەرگە دېڭىزنى بويسۇندۇرۇپ بەردى'),
(4486, 45, 13, 'اﷲ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسىنى (كامالى) پەزلىدىن سىلەرگە بويسۇندۇرۇپ بەردى، بۇنىڭدا ھەقىقەتەن (قۇدرىتى ئىلاھىيىنى) تەپەككۈر قىلىدىغان قەۋم ئۈچۈن (اﷲ نىڭ قۇدرىتى ۋە بىرلىكىنى كۆرسىتىدىغان) (روشەن) دەلىللەر بار'),
(4487, 45, 14, 'مۆمىنلەرگە ئېيتقىنكى، اﷲ ھەر قەۋمنىڭ قىلمىشلىرىغا يارىشا جازا بېرىدىغانلىقى ئۈچۈن، ئۇلار اﷲ نىڭ كۈنلىرىنى ئۈمىد قىلمايدىغانلارنى (يەنى اﷲ نىڭ ئازابىدىن قورقمايدىغان كۇففارلارنىڭ يەتكۈزگەن ئەزىيەتلىرىنى) كەچۈرسۇن'),
(4488, 45, 15, 'كىمكى (دۇنيادا) ياخشى ئىش قىلسا، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈن قىلىدۇ، كىمكى يامان ئىش قىلسا، ئۇ ئۆزىنىڭ زىيىنى ئۈچۈن قىلىدۇ، ئاندىن كېيىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتۇرۇلىسىلەر (اﷲ ياخشىلىققا مۇكاپات، يامانلىققا جازا بېرىدۇ)'),
(4489, 45, 16, 'شەك - شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىغا كىتابنى (يەنى تەۋراتنى)، ھېكمەتنى ۋە پەيغەمبەرلىكنى ئاتا قىلدۇق، ئۇلارغا پاك نەرسىلەرنى رىزىق قىلىپ بەردۇق، ئۇلارنى (زامانىدىكى) جاھان ئەھلىدىن ئۈستۈن قىلدۇق'),
(4490, 45, 17, 'ئۇلارغا دىن ئىشىدا روشەن دەلىللەرنى بەردۇق، پەقەت ئۇلارغا ئىلىم (يەنى دىننىڭ راستلىقىغا قەتئى دەلىللەر) كەلگەندىن كېيىنلا، ئاندىن ئۇلار ئۆزئارا ھەسەت قىلىشىپ ئىختىلاپ قىلىشتى. پەرۋەردىگارىڭ ھەقىقەتەن قىيامەت كۈنى ئۇلار ئىختىلاپ قىلىشقان ئىشلار ئۈستىدە ھۆكۈم چىقىرىدۇ'),
(4491, 45, 18, 'ئاندىن سېنى بىز (ئى مۇھەممەد!) دىن ئىشىدا (روشەن) بىر يولدا قىلدۇق، شۇ يولغا ئەگەشكىن، بىلمەيدىغانلار (يەنى مۇشرىكلار) نىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن'),
(4492, 45, 19, 'شۈبھىسىزكى، (ئۇلارنىڭ گۇمراھلىقىغا ماسلىشىدىغان بولساڭ) ئۇلار سەندىن اﷲ نىڭ (ئازابى) دىن ھېچ نەرسىنى دەپئى قىلالمايدۇ، شۈبھىسىزكى، (دۇنيادا) زالىملارنىڭ بەزىسى بەزىسىگە دوستتۇر، اﷲ تەقۋادارلارنىڭ دوستىدۇر'),
(4493, 45, 20, 'بۇ (قۇرئان) ئىنسانلار ئۈچۈن روشەن پاكىتلاردۇر، ھىدايەتتۇر، (ئۆلگەندىن كېيىن تىرىلىشكە) جەزمەن ئىشەنگەن قەۋم ئۈچۈن رەھمەتتۇر'),
(4494, 45, 21, 'يامان ئىشلارنى قىلغانلار (يەنى گۇناھكار كۇففارلار) ئۆزلىرىنى ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلاردەك قىلىشىمىزنى، ھاياتلىقتا، ماماتلىقتا ئۇلار بىلەن ئوخشاش قىلىشىمىزنى ئويلامدۇ؟ (يەنى ئۇلارنى ئوخشاش قىلىشىمىز مۇمكىن ئەمەس) ئۇلارنىڭ چىقارغان ھۆكمى نېمىدېگەن يامان!'),
(4495, 45, 22, 'اﷲ ئاسمانلارنى ۋە زېمىننى ھەقلىق بىلەن ياراتتى، ھەر ئىنساننىڭ قىلمىشىغا يارىشا جازا ياكى مۇكاپات بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ'),
(4496, 45, 23, '(ئى مۇھەممەد!) ماڭا نەپسى خاھىشنى ئىلاھ قىلىۋالغان، اﷲ ئازدۇرغان، (ھەقنى) بىلىپ تۇرغان، اﷲ قۇلىقىنى ۋە قەلبىنى پېچەتلىۋەتكەن ۋە كۆزىنى پەردىلىگەن ئادەمنى ئېيتىپ بەرگىن، اﷲ ئازدۇرغاندىن كېيىن، ئۇنى كىم ھىدايەت قىلالايدۇ؟ سىلەر (بۇلاردىن) ۋەز - نەسىھەت ئالمامسىلەر؟'),
(4497, 45, 24, 'ئۇلار (يەنى قىيامەتنى ئىنكار قىلغۇچىلار): «ھايات دېگەن پەقەت دۇنيادىكى ھاياتىمىزدۇر، ئۆلىمىز ۋە تىرىلىمىز (يەنى تۇغۇلىمىز). پەقەت زاماننىڭ ئۆتۈشى بىلەن يوق بولىمىز» (دەيدۇ). ئۇلار بۇ خۇسۇستا ھېچقانداق مەلۇماتقا ئىگە ئەمەس، ئۇلار پەقەت گۇمان بىلەن سۆزلەيدۇ'),
(4498, 45, 25, 'ئۇلارغا بىزنىڭ ئايەتلىرىمىز ئوچۇق ئوقۇپ بېرىلسە، ئۇلارنىڭ پاكىتى پەقەت «ئەگەر راستچىل بولساڭلار، ئاتا - بوۋىلىرىمىزنى كەلتۈرۈپ بېقىڭلار (يەنى تىرىلدۈرۈڭلار)» دېيىشتىن ئىبارەت بولدى'),
(4499, 45, 26, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «اﷲ سىلەرنى تىرىلدۈرەلەيدۇ، ئاندىن (ئەجىلىڭلار پۈتكەندە) ۋاپات قىلدۇرالايدۇ، ئاندىن شەك - شۈبھىسىز قىيامەت كۈنىدە سىلەرنى يىغىدۇ، لېكىن كىشىلەرنىڭ تولىسى (اﷲ نىڭ قۇدرىتىنى) بىلمەي (قايتا تىرىلىشنى ئىنكار قىلدى)»'),
(4500, 45, 27, 'ئاسمانلار بىلەن زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، قىيامەت قايىم بولغان كۈندە كاپىرلار زىيان تارتىدۇ'),
(4501, 45, 28, 'ھەر ئۈممەتنى (قورقۇنچنىڭ قاتتىقلىقىدىن) تىزلانغان ھالدا كۆرىسەن، ھەر ئۈممەت ئۆزىنىڭ نامە - ئەمالىغا چاقىرىلىدۇ، (ئۇلارغا) «بۈگۈن قىلمىشىڭلارغا يارىشا مۇكاپاتقا ئېرىشىسىلەر ياكى جازاغا ئۇچرايسىلەر» (دېيىلىدۇ)'),
(4502, 45, 29, 'بۇ بىزنىڭ كىتابىمىز (يەنى نامە - ئەمال) سىلەرگە ھەق بىلەن سۆزلەيدۇ، بىز قىلمىشىڭلارنى يازدۇرۇپ تۇرغان ئىدۇق (يەنى پەرىشتىلەرنى ئەمەللىرىڭلارنى يېزىپ تۇرۇشقا بۇيرۇيتتۇق)'),
(4503, 45, 30, 'ئىمان ئېيتقان ۋە ياخشى ئىشلارنى قىلغانلارغا كەلسەك، پەرۋەردىگارى ئۇلارنى رەھمىتى دائىرىسىگە كىرگۈزىدۇ، بۇ روشەن مۇرادقا يېتىشتۇر'),
(4504, 45, 31, 'كاپىرلارغا كەلسەك، (ئۇلارنى ئەيىبلەش يۈزىسىدىن) ئۇلارغا: «مېنىڭ ئايەتلىرىم سىلەرگە ئوقۇپ بېرىلمىدىمۇ؟ سىلەر چوڭچىلىق قىلىپ (ئۇنىڭغا ئىمان ئېيتىشتىن) باش تارتتىڭلار، سىلەر گۇناھقا چۆمگەن قەۋم ئىدىڭلار» (دېيىلىدۇ)'),
(4505, 45, 32, '(سىلەرگە) «اﷲ نىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، قىيامەتتە (يەنى قىيامەتنىڭ بولۇشىدا) ھېچ شەك يوق» دېيىلسە، سىلەر (ھەددىدىن زىيادە سەركەشلىكىڭلاردىن): «قىيامەتنىڭ نېمە ئىكەنلىكىنى بىلمەيمىز، پەقەت ئۇنى بولۇشى مۇمكىن دەپ گۇمان قىلىمىز، (ئۇنىڭغا) بىز ھەقىقىي ئىشەنمەيمىز» دېدىڭلار'),
(4506, 45, 33, '(ئاخىرەتتە) ئۇلارنىڭ قىلغان يامان ئىشلىرى ئاشكارا بولىدۇ، ئۇلار (دۇنيادىكى چاغلىرىدا) مەسخىرە قىلغان ئازاب ئۇلارنى ئورىۋالىدۇ'),
(4507, 45, 34, '(ئۇلارغا) دېيىلىدۇ: «سىلەرنىڭ مۇشۇ كۈندىكى مۇلاقاتنى ئۇنتۇغانلىقىڭلارغا ئوخشاش، بۈگۈن سىلەرنى ئۇنتۇيمىز (يەنى سىلەرنى ئازابقا تاشلاپ قويۇپ ئۇنتۇپ قالغاندەك مۇئامىلە قىلىمىز)، سىلەرنىڭ جايىڭلار دوزاخ بولىدۇ، سىلەرگە (سىلەرنى ئازابتىن قۇتقۇزىدىغان) ھېچقانداق ياردەمچىمۇ بولمايدۇ'),
(4508, 45, 35, 'بۇ شۇنىڭ ئۈچۈنكى، سىلەر اﷲ نىڭ ئايەتلىرىنى مەسخىرە قىلىدىغان نەرسە قىلىۋالدىڭلار ۋە سىلەرنى دۇنيا تىرىكچىلىكى ئالدىدى». بۈگۈن ئۇلار دوزاختىن چىقىرىلمايدۇ، ئۇلاردىن (تەۋبە قىلىش ۋە ئىبادەت قىلىش بىلەن) اﷲ نى رازى قىلىشمۇ تەلەپ قىلىنمايدۇ'),
(4509, 45, 36, 'جىمى ھەمدۇسانا ئاسمانلارنىڭ پەرۋەردىگارى، زېمىننىڭ پەرۋەردىگارى ۋە ئالەملەرنىڭ پەرۋەردىگارى اﷲ قا خاستۇر!'),
(4510, 45, 37, 'ئاسمانلاردىكى ۋە زېمىندىكى ئۇلۇغلۇق اﷲ قا خاستۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حا، مىم'),
(4512, 46, 2, '(بۇ قۇرئان) غالىب، ھېكمەت بىلەن ئىش قىلغۇچى اﷲ تەرىپىدىن نازىل قىلىنغاندۇر'),
(4513, 46, 3, 'ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ھەق ئاساستا مۇئەييەن مۇددەتكىچە (يەنى قىيامەتكىچە قالىدىغان) قىلىپ ياراتتۇق. كاپىرلار ئاگاھلاندۇرۇلغان نەرسىلەردىن يۈز ئۆرۈگۈچىدۇر'),
(4514, 46, 4, '(ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر اﷲ نى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلارنىڭ (ئەھۋالىنى) ئېيتىپ بېرىڭلار، ئۇلار زېمىننىڭ قايسى قىسمىنى ياراتقانلىقىنى ماڭا كۆرسىتىڭلار، ياكى ئاسمانلارنى يارىتىشتا ئۇلارنىڭ اﷲ بىلەن ئورتاقچىلىقى بارمۇ؟ بۇ (قۇرئان) دىن ئىلگىرى (سىلەرگە نازىل قىلىنغان) كىتاب ياكى (ئىلگىرىكىلەردىن) قالغان ئىلىم بولسا ماڭا ئېلىپ كېلىڭلار، ئەگەر سىلەر راستچىل بولساڭلار»'),
(4515, 46, 5, 'اﷲ نى قويۇپ قىيامەتكىچە دۇئانى ئىجابەت قىلىشقا قادىر بولالمايدىغان بۇتلارغا چوقۇنىدىغان ئادەمدىنمۇ گۇمراھ ئادەم بارمۇ؟ بۇتلار ئۇلارنىڭ دۇئاسىدىن غاپىلدۇر'),
(4516, 46, 6, '(قىيامەت كۈنى) ئىنسانلار (ھېساب بېرىش ئۈچۈن) توپلانغان چاغدا، بۇتلار ئۇلارغا (يەنى بۇتپەرەسلەرگە) دۈشمەن بولىدۇ، ئۇلار تەرىپىدىن ئىبادەت قىلىنغانلىقىنى ئىنكار قىلىدۇ'),
(4517, 46, 7, 'ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنسا، كاپىرلار ئۆزلىرىگە كەلگەن ھەقىقەتنى (يەنى قۇرئاننى) باھالاپ: «بۇ روشەن سېھىردۇر» دەيدۇ'),
(4518, 46, 8, 'ياكى ئۇلار قۇرئاننى ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئۆزى توقۇغان دىېيىشەمدۇ؟ ئېيتقىنكى، «ئەگەر ئۇنى مەن توقۇغان بولسام، سىلەر مەندىن اﷲ نىڭ ئازابىدىن ھېچ نەرسىنى دەپئى قىلالمايسىلەر، سىلەرنىڭ قۇرئان توغرىسىدىكى تۆھمەتلىرىڭلارنى اﷲ ئوبدان بىلىدۇ، اﷲ مەن بىلەن سىلەرنىڭ ئاراڭلاردا گۇۋاھ بولۇشقا يېتەرلىكتۇر (يەنى مېنىڭ راستچىللىقىمغا ۋە سىلەرنىڭ يالغانچىلىقىڭلارغا گۇۋاھ بولىدۇ)، اﷲ (تەۋبە قىلغۇچىنى) مەغپىرەت قىلغۇچىدۇر، (مۆمىن بەندىلىرىگە) ناھايىتى مېھرىباندۇر»'),
(4519, 46, 9, 'ئېيتقىنكى، «مەن (اﷲ ئىنسانلارنى تەۋھىدكە دەۋەت قىلىش ئۈچۈن ئەۋەتكەن) تۇنجى پەيغەمبەر ئەمەسمەن، (مەندىن ئىلگىرىكى نۇرغۇن پەيغەمبەرلەر ئېلىپ كەلگەن ۋەھيى بىلەن كەلدىم، نېمىشقا مېنى ئىنكار قىلىسىلەر؟) ماڭا ۋە سىلەرگە نېمە قىلىنىدىغانلىقىنى ئۇقمايمەن (چۈنكى اﷲ نىڭ تەقدىرىنى ئالدىن ئۇققىلى بولمايدۇ)، مەن پەقەت ماڭا ۋەھيى قىلىنغان نەرسىگىلا ئەگىشىمەن، مەن پەقەت (سىلەرنى اﷲ نىڭ ئازابىدىن) ئوچۇق ئاگاھلاندۇرغۇچىمەن»'),
(4520, 46, 10, '(ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر ئېيتىپ بېقىڭلارچۇ! قۇرئان اﷲ تەرىپىدىن نازىل بولغان تۇرسا، سىلەر ئۇنىڭغا ئىشەنمىسەڭلار (ھالىڭلار قانداق بولىدۇ؟) ئىسرائىل ئەۋلادىدىن بىر گۇۋاھچى (يەنى ئابدۇللاھ ئىبن سالام) قۇرئاننىڭ اﷲ تەرىپىدىن نازىل بولغانلىقىغا گۇۋالىق بېرىپ ئىمان كەلتۈرسە، سىلەر تەكەببۇرلۇق قىلىپ (ئىماندىن) باش تارتساڭلار (ھالىڭلار قانداق بولىدۇ؟) شۈبھىسىزكى، اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ»'),
(4521, 46, 11, 'كاپىرلار مۆمىنلەرگە: «ئەگەر (قۇرئان بىلەن ئىسلام دىنى) ياخشى بولىدىغان بولسا ئىدى، ئۇلار (يەنى ئاجىز مۇسۇلمانلار) ئۇنىڭغا بىزدىن بۇرۇن ئىشەنمىگەن بولاتتى» دېدى، ئۇلار قۇرئان بىلەن ھىدايەت تاپمىغانلىقلىرى ئۈچۈن، «بۇ قەدىمقى ئويدۇرمىدۇر» دەيدۇ'),
(4522, 46, 12, 'قۇرئاندىن ئىلگىرى مۇسانىڭ كىتابى (يەنى تەۋرات) ئەھلى جاھانغا پېشۋا ۋە رەھمەت ئىدى. بۇ ئەرەب تىلىدىكى كىتابتۇر، (ئىلگىرىكى كىتابلارنى) تەستىق قىلغۇچىدۇر، (اﷲ ئۇنى) زالىملارنى ئاگاھلاندۇرۇش ئۈچۈن، ياخشى ئىش قىلغۇچى مۆمىنلەرگە (جەننەت بىلەن) خۇش خەۋەر بېرىش ئۈچۈن نازىل قىلدى'),
(4523, 46, 13, '«پەرۋەردىگارىمىز اﷲ دۇر» دېگەن، ئاندىن توغرا يولدا بولغانلارغا، شۈبھىسىزكى، (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر'),
(4524, 46, 14, 'ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە مەڭگۈ قالىدۇ، (بۇ) ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرىنى مۇكاپاتلاش ئۈچۈندۇر'),
(4525, 46, 15, 'بىز ئىنساننى ئاتا - ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق. ئىنساننى ئانىسى مۇشەققەت بىلەن قورساق كۆتۈرۈپ، مۇشەققەت بىلەن تۇغدى. ئۇنىڭغا قورساق كۆتۈرۈش مۇددىتى ۋە ئۇنى سۈتتىن ئايرىش مۇددىتى 30 ئايدۇر. تاكى ئۇ (بوۋاق ئۆسۈپ) كۈچ - قۇۋۋەتكە تولۇپ، 40 ياشقا يەتكەندە، (ئۇ): «پەرۋەردىگارىم! سېنىڭ ماڭا ۋە ئاتا - ئانامغا بەرگەن نېمىتىڭگە شۈكۈر قىلىشنى ۋە سەن رازى بولىدىغان ياخشى ئىشنى قىلىشىمنى ماڭا ئىلھام قىلغىن، مەن ئۈچۈن مېنىڭ ئەۋلادىمنى تۈزىگىن (يەنى مېنىڭ ئەۋلادىمنى ياخشى ئادەملەر قىلغىن)، مەن ھەقىقەتەن ساڭا (جىمى گۇناھلاردىن) تەۋبە قىلدىم، مەن ھەقىقەتەن مۇسۇلمانلاردىندۇرمەن» دەيدۇ'),
(4526, 46, 16, 'ئەنە شۇنداق كىشىلەرنىڭ ياخشى ئىشلىرىنى قوبۇل قىلىپ، يامان ئىشلىرىنى كەچۈرۈم قىلىمىز، ئۇلار (اﷲ يامان ئىشلىرىنى كەچۈرگەنلەر بىلەن) جەننەتتە بولىدۇ، (بۇ) ئۇلارغا قىلىنغان راست ۋەدىدۇر'),
(4527, 46, 17, 'بەزى ئادەم (يەنى ئاتا - ئانىسى ئىمانغا دەۋەت قىلغان فاجىر بالا): ۋاي - ۋۇي! سىلەر مېنى قايتا تىرىلىشتىن قورقۇتۇۋاتامسىلەر، (مەندىن ئىلگىرى) نۇرغۇن ئۈممەتلەر ئۆتتى (ئۇلاردىن ھېچ ئادەم تىرىلدۈرۈلگىنى يوق)» دەيدۇ، ئۇ ئىككىسى اﷲ قا پەرياد قىلىپ: «ۋاي ساڭا! ئىمان ئېيتقىن، اﷲ نىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر» دەيدۇ. ئۇ: «بۇ پەقەت ئاۋالقىلاردىن قالغان ئەپسانىلەردۇر» دەيدۇ'),
(4528, 46, 18, 'ئەنە شۇنداق كىشىلەرگە (اﷲ نىڭ ئازاب قىلىش) سۆزى ھەقلىق بولدى، ئۇلار بولسا ئۇلاردىن ئىلگىرى ئۆتكەن ئىنسانلاردىن ۋە جىنلاردىن بولغان ئۈممەتلەر قاتارىغا كىرگۈزۈلدى، ئۇلار ھەقىقەتەن زىيان تارتقۇچى بولدى'),
(4529, 46, 19, '(مۆمىنلەرنىڭ ۋە كاپىلارنىڭ) ھەممىسىنىڭ قىلغان ئەمەللىرىگە يارىشا دەرىجىلىرى بولىدۇ، ئۇلارنىڭ ئەمەللىرىنىڭ ساۋابى ۋە جازاسى (كەم - زىيادە قىلىنماي) تولۇق بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ'),
(4530, 46, 20, 'كاپىلار دوزاخقا توغرىلىنىدىغان كۈندە (ئۇلارغا ئەيىبلەش يۈزىسىدىن ئېيتىلىدۇ): «سىلەر لەززاتلىرىڭلارنى ھاياتىي دۇنيادا بەھرىمەن بولۇپ تۈگەتتىڭلار، سىلەر زېمىندا ھەقسىز رەۋىشتە چوڭچىلىق قىلىپ، ئىماندىن باش تارتقانلىقىڭلار ۋە اﷲ نىڭ ئىتائىتىدىن چىققانلىقىڭلار تۈپەيلىدىن، بۈگۈن خار قىلغۇچى ئازاب بىلەن جازالىنىسىلەر»'),
(4531, 46, 21, '(ئى مۇھەممەد!) ئادنىڭ قېرىندىشى (يەنى ھۇد ئەلەيھىسسالام) نىڭ ئۆز قەۋمى بىلەن بولغان قىسسىسىنى بايان قىلغىن، ئۆز ۋاقتىدا ئۇ ئەھقاف (دېگەن يەر) دىكى قەۋمىنى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇردى، ئۇنىڭدىن (يەنى ھۇدتىن) ئىلگىرى ۋە ئۇنىڭدىن كېيىن نۇرغۇن پەيغەمبەرلەر ئۆتتى. ئۇ (يەنى ھۇد) (قەۋمىنى ئاگاھلاندۇرۇپ) ئېيتتى: «سىلەر پەقەت اﷲ غىلا ئىبادەت قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ (اﷲ تىن غەيرىيگە ئىبادەت قىلساڭلار) بۈيۈك كۈننىڭ ئازابىغا قېلىشىڭلاردىن قورقىمەن»'),
(4532, 46, 22, 'ئۇلار: «(ئى ھۇد!) سەن بىزنى ئىلاھلىرىمىزدىن توسقىلى كەلدىڭمۇ؟ ئەگەر سەن (سۆزۈڭدە) راستچىللاردىن بولساڭ، بىزگە بىزنى قورقۇتقان ئازابنى كەلتۈرگىن» دېدى'),
(4533, 46, 23, 'ئۇ: «(سىلەرگە ئازابنىڭ قاچان كېلىشىنى) پەقەت اﷲ بىلىدۇ، مەن سىلەرگە ئەۋەتىلگەن نەرسىنى تەبلىغ قىلىمەن ۋە لېكىن مەن سىلەرنى نادان قەۋم كۆرىمەن» دېدى'),
(4534, 46, 24, 'ئۇلار ئۆزلىرىنىڭ ۋادىسىغا سۈرۈلۈۋاتقان بۇلۇتنى كۆرۈپ: «مانا بۇ بىزگە يامغۇر ياغدۇرغۇچى بۇلۇتتۇر» دېيىشتى. (ھۇد ئېيتتى) «ئۇنداق ئەمەس، ئۇ سىلەر بالدۇرراق ئىشقا ئېشىشىنى تەلەپ قىلغان نەرسىدۇر، ئۇ بىر بوران بولۇپ، ئۇنىڭ ئىچىدە قاتتىق ئازاب بار'),
(4535, 46, 25, 'ئۇ پەرۋردىگارىنىڭ بىر ئەمرى بىلەن ھەممە نەرسىنى ھالاك قىلىدۇ» (يەنى بوران كېلىپ ئۇلارنى ھالاك قىلدى)، شۇنىڭ بىلەن ئۇلارنىڭ جايلىرىنىڭ (يەنى تۇرغان ماكانلىرىنىڭ ئىزىدىن) باشقا نەرسە كۆرۈنمەيدىغان بولۇپ قالدى، گۇناھكار قەۋمنى مۇشۇنداق جازالايمىز'),
(4536, 46, 26, 'بىز ئۇلارنى (يەنى ئاد قەۋمىنى) (پۇل - مال، كۈچ - قۇۋۋەت جەھەتتە) سىلەرنى قادىر قىلمىغان نەرسىلەرگە قادىر قىلدۇق. (ئۇلارنىڭ شۇ نېمەتلەرگە شۈكۈر قىلىپ، بۇ ئارقىلىق نېمەتلەرنى بەرگۈچى اﷲ نى تونۇشى ئۈچۈن) ئۇلارغا قۇلاق، كۆز، قەلبلەرنى بەردۇق، ئۇلار اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن، ئۇلارنىڭ قۇلاقلىرى، كۆزلىرى، دىللىرى ئۇلاردىن (اﷲ نىڭ ئازابىدىن) ھېچ نەرسىنى دەپئى قىلمىدى، ئۇلار مەسخىرە قىلغان ئازاب ئۇلارغا نازىل بولدى'),
(4537, 46, 27, '(ئى مەككە ئاھالىسى!) بىز ھەقىقەتەن سىلەرنىڭ ئەتراپىڭلاردىكى شەھەرلەرنى ھالاك قىلدۇق، ئۇلارنى (كۇفرىدىن) يانسۇن دەپ ئايەتلەرنى تەكرار بايان قىلدۇق'),
(4538, 46, 28, '(ئۇلارغا ئازاب كەلگەندە) ئۇلارنىڭ خۇداغا يېقىن قىلىدۇ دەپ اﷲ تىن باشقا تۇتقان مەبۇدلىرى ئۇلارغا نېمىشقا ياردەم قىلمىدى؟ بەلكى ئۇلارنىڭ مەبۇدلىرى غايىب بولدى، بۇ (يەنى بۇتلار بىزنى خۇداغا يېقىن قىلىدۇ دېگەن سۆزى) ئۇلارنىڭ ئويدۇرمىسىدۇر ۋە (اﷲ قا) چاپلىغان بوھتانلىرىدۇر'),
(4539, 46, 29, '(ئى مۇھەممەد!) ئۆز ۋاقتىدا بىر تۈركۈم جىنلارنى ساڭا قۇرئان تىڭشاشقا ئەۋەتتۇق، ئۇلار قۇرئان تىلاۋىتىگە ھازىر بولغاندا (بىر - بىرىگە): «جىم تۇرۇڭلار» دېيىشتى، قۇرئان تىلاۋەت قىلىنىپ بولغاندىن كېيىن ئۇلار ئۆز قەۋمىگە ئاگاھلاندۇرغۇچى بولۇپ قايتىشتى'),
(4540, 46, 30, 'ئۇلار ئېيتتى: «ئى قەۋمىمىز! ھەقىقەتەن بىز مۇسادىن كېيىن نازىل بولغان، ئۇنىڭدىن ئىلگىرىكى كىتابلارغا ئۇيغۇن كېلىدىغان، ھەق دىنغا ۋە توغرا يولغا باشلايدىغان كىتابنى (يەنى قۇرئاننى) تىڭشىدۇق»'),
(4541, 46, 31, '«ئى قەۋمىمىز! اﷲ قا دەۋەت قىلغۇچى (يەنى مۇھەممەد ئەلەيھىسسالام) نىڭ دەۋىتىنى قوبۇل قىلىڭلار ۋە اﷲ قا ئىمان ئېيتىڭلار، اﷲ بەزى گۇناھلارنى مەغپىرەت قىلىدۇ، سىلەرنى قاتتىق ئازابتىن ساقلايدۇ»'),
(4542, 46, 32, 'كىمكى اﷲ قا (ئىمان ئېيتىشقا) دەۋەت قىلغۇچىنىڭ (يەنى رەسۇلۇللاھنىڭ) دەۋىتىنى قوبۇل قىلمىسا، ئازابتىن قېچىپ قۇتۇلالمايدۇ، اﷲ تىن باشقا (ئۇنى اﷲ نىڭ ئازابىدىن ساقلىغۇچى) ياردەمچىلەر بولمايدۇ، ئۇلار (يەنى رەسۇلۇللاھنىڭ دەۋىتىنى قوبۇل قىلمىغۇچىلار) ئوپئوچۇق گۇمراھلىقتىدۇر'),
(4543, 46, 33, 'ئۇلار (يەنى ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغۇچى كۇففارلار) بىلمەمدۇكى، ئاسمانلارنى ۋە زېمىننى ياراتقان ۋە ئۇلارنى يارىتىشتا چارچاپ قالمىغان اﷲ ئۆلۈكلەرنى تىرىلدۈرۈشكە قادىر ئەمەسمۇ؟ دۇرۇس، ئۇ ھەقىقەتەن ھەر نەرسىگە قادىردۇر'),
(4544, 46, 34, 'كاپىرلار دوزاخقا توغرىلىنىدىغان كۈندە (ئۇلارغا): «بۇ (يەنى سىلەر تېتىۋاتقان ئازاب) راست ئەمەسمۇ؟» (دېيىلىدۇ) ئۇلار: «پەرۋەردىگارىمىز بىلەن قەسەمكى، ئەلۋەتتە راستتۇر» دەيدۇ، اﷲ (ئۇلارغا): «كاپىر بولغانلىقىڭلار تۈپەيلىدىن ئازابنى تېتىڭلار» دەيدۇ'),
(4545, 46, 35, '(ئى مۇھەممەد!) سەن (مۇشرىكلارنىڭ سالغان جاپاسىغا) ئىرادىلىك پەيغەمبەرلەر سەۋر قىلغاندەك سەۋر قىلغىن، ئۇلارغا بولىدىغان ئازابقا ئالدىراپ كەتمىگىن. ئۇلار (ئاخىرەتتە) ۋەدە قىلىنغان ئازابنى كۆرگەندە، (ئۇلار دۇنيادا) گويا كۈندۈزدە بىر دەملا تۇرغاندەك ھېس قىلىدۇ، (بۇ قۇرئان سىلەرگە) تەبلىغدۇر، پەقەت پاسىق قەۋملا ھالاك قىلىنىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'كاپىر بولغان ۋە (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلارنىڭ (ياخشى) ئەمەللىرىنى اﷲ بىكار قىلىۋېتىدۇ'),
(4547, 47, 2, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ، مۇھەممەدكە نازىل قىلىنغان قۇرئانغا - قۇرئان ئۇلارنىڭ پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەتتۇر – ئىمان ئېيتقانلارنىڭ گۇناھلىرىنى اﷲ ئۆچۈرۈپ تاشلايدۇ، ئۇلارنىڭ ھالىنى ياخشىلايدۇ (شۇنىڭ بىلەن ئۈلار گۇناھ قىلمايدۇ)'),
(4548, 47, 3, 'بۇ شۇنىڭ ئۈچۈندۇركى، كاپىرلار باتىلغا (يەنى شەيتانغا) ئەگەشتى، مۆمىنلەر بولسا، پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەتكە (يەنى قۇرئانغا) ئەگەشتى، اﷲ ئىنسانلارغا ئۇلارنىڭ ئەھۋالىنى شۇنداق بايان قىلىدۇ'),
(4549, 47, 4, 'سىلەر كۇففارلارغا (جەڭ مەيدانىدا) ئۇچراشقان چېغىڭلاردا، ئۇلارنى ئۆلتۈرۈڭلار، سىلەر ئۇلارنى قىرىپ (مەغلۇپ قىلىپ، ئۇلاردا سىلەرگە قارشىلىق كۆرسىتىدىغان كۈچ قالمىغان چاغدا ئۇلارنى ئۆلتۈرۈشتىن توختاپ)، ئۇلارنى ئەسىر ئېلىڭلار، ئاندىن ئۇلارنى ئېھسان قىلىش يۈزىسىدىن قويۇپ بېرىڭلار، ياكى فىدىيە ئېلىپ قويۇپ بېرىڭلار، تاكى ئۇرۇش ئۆزىنىڭ ئېغىر يۈكىنى تاشلىغانغا قەدەر (يەنى ئۇرۇش توختىغانغا قەدەر) ئىش مانا شۇنداقتۇر. ئەگەر اﷲ خالىسا ئەلۋەتتە (سىلەرنى ئۇرۇشقا تەكلىپ قىلماستىنلا) ئۇلارنى جازالىغان بولاتتى، لېكىن اﷲ بەزىڭلارنى بەزىڭلار بىلەن سىناش ئۈچۈن (يەنى ئىمانىڭلارنى سىناش ئۈچۈن جىھادقا ئەمر قىلدى)، اﷲ نىڭ يولىدا ئۆلتۈرۈلگەنلەرنىڭ (يەنى شېھىتلەرنىڭ) ئەمەللىرىنى اﷲ بىكار قىلىۋەتمەيدۇ'),
(4550, 47, 5, 'اﷲ ئۇلارنى ھىدايەت قىلىدۇ ۋە ھالىنى ياخشىلايدۇ'),
(4551, 47, 6, 'اﷲ ئۈلارنى جەننەتكە كىرگۈزىدۇ، اﷲ ئۇنى ئۇلارغا تونۇتتى (يەنى جەننەتكە كىرگەن ئادەم ئۆزىنىڭ ئۇ يەردىكى جايىنى ئىلگىرى كۆرگەندەكلا بىلىدۇ)'),
(4552, 47, 7, 'ئى مۆمىنلەر! سىلەر اﷲ قا (يەنى اﷲ نىڭ دىنىغا) ياردەم بەرسەڭلار، اﷲ سىلەرگە (دۈشمىنىڭلارغا قارشى ياردەم بېرىدۇ، قەدىمىڭلارنى (ئۇرۇش مەيدانلىرىدا) بەرقارار قىلىدۇ'),
(4553, 47, 8, 'كاپىرلارنى اﷲ ھالاك قىلىدۇ، ئۇلارنىڭ (ياخشى) ئەمەللىرىنى بىكار قىلىدۇ'),
(4554, 47, 9, 'بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار اﷲ نازىل قىلغان كىتابنى (يەنى قۇرئاننى) يامان كۆرۈش بىلەن، ئۆزلىرىنىڭ ئەمەللىرىنى بەربات قىلدى'),
(4555, 47, 10, 'ئۇلار زېمىندا سەير قىلىپ ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ اﷲ ئۇلارنى ھالاك قىلدى. (مەككە) كاپىرلىرىمۇ شۇنداق ئاقىۋەتكە قالىدۇ'),
(4556, 47, 11, 'بۇ شۇنىڭ ئۈچۈندۇركى، اﷲ مۆمىنلەرنىڭ مەدەتكارىدۇر، كاپىرلارغا مەدەتكار يوقتۇر'),
(4557, 47, 12, 'شۈبھىسىزكى، اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، كاپىرلار (ھاياتى دۇنيادا ئۇنىڭ لەززەتلىرىدىن) بەھرىمەن بولىدۇ ۋە چاھارپايلاردەك يەپ - ئىچىدۇ، (ئاخىرەتتە) ئۇلارنىڭ جايى دوزاخ بولىدۇ'),
(4558, 47, 13, 'سېنى ھەيدەپ چىقارغان شەھەرنىڭ (يەنى مەككىنىڭ) ئاھالىسىدىن كۈچلۈك بولغان نۇرغۇن شەھەرلەرنىڭ ئاھالىسىنى ھالاك قىلدۇق، ئۇلارغا ھېچ قانداق ياردەم قىلغۇچى بولمىدى'),
(4559, 47, 14, 'پەرۋەردىگارى تەرىپىدىن بولغان روشەن دەلىللەرگە ئاساسلانغان ئادەم (يەنى مۆمىن) ئۆزىنىڭ يامان ئەمەلى چىرايلىق كۆرسىتىلگەن ۋە نەپسى خاھىشلىرىغا ئەگەشكەن ئادەمگە (يەنى كاپىرغا) ئوخشامدۇ؟'),
(4560, 47, 15, 'تەقۋادارلارغا ۋەدە قىلىنغان جەننەتنىڭ سۈپىتى شۇكى، ئۇ يەردە رەڭگى ئۆزگەرمىگەن سۇدىن ئۆستەڭلار، تەمى ئۆزگەرمىگەن سۈتتىن ئۆستەڭلار، ئىچكۈچىلەرگە لەززەت بېغىشلايدىغان مەيدىن ئۆستەڭلار ۋە ساپ ھەسەلدىن ئۆستەڭلار بولىدۇ، ئۇلارغا جەننەتتە بەھرىمەن بولىدىغان تۈرلۈك مېۋىلەر بولىدۇ ۋە پەرۋەردىگارى تەرىپىدىن مەغپىرەت بولىدۇ، (مۇنداق تەقۋادارلار) دوزاختا مەڭگۈ قالىدىغان، قايناقسۇ بىلەن سۇغىرىلىپ (قىزىقلىقىدىن) ئۈچەيلىرى پارە - پارە قىلىنىدىغانلار بىلەن ئوخشاشمۇ؟'),
(4561, 47, 16, 'ئۇلاردىن (يەنى مۇناپىقلاردىن) بەزى كىشىلەر سېنىڭ سۆزۈڭگە قۇلاق سالىدۇ، ئۇلار سېنىڭ يېنىڭدىن چىققاندا، ئىلىم بېرىلگەن كىشىلەرگە (يەنى ساھابىلەرنىڭ ئۆلىمالىرىغا مەسخىرە قىلىش يۈزىسىدىن): «بايا ئۇ (يەنى مۇھەممەد) نېمە دېدى؟» دەيدۇ، ئەنە شۇلار اﷲ دىللىرىنى پېچەتلىۋەتكەنلەردۇر، نەپسى خاھىشلىرىغا ئەگەشكەنلەردۇر'),
(4562, 47, 17, 'ھىدايەت تاپقانلارنى اﷲ تېخىمۇ ھىدايەت قىلىدۇ، ئۇلارغا تەقۋادارلىقنىڭ مۇكاپاتىنى بېرىدۇ'),
(4563, 47, 18, 'ئۇلار (يەنى مەككە كۇففارلىرى) پەقەت قىيامەتنىڭ ئۇلارغا تۇيۇقسىز كېلىشىنىلا كۈتىدۇ، شۈبھىسىزكى، قىيامەتنىڭ ئالامەتلىرى كەلدى، ئۇلارغا قىيامەت كەلگەندە ئۇلار قانداقمۇ ۋەز - نەسىھەت ئالىدۇ (يەنى بۇ چاغدا ۋەز - نەسىھەت ئالغانلىقىنىڭ پايدىسى بولمايدۇ'),
(4564, 47, 19, 'بىلگىنكى، اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، گۇناھىڭ ئۈچۈن، ئەر - ئايال مۆمىنلەر ئۈچۈن مەغپىرەت تىلىگىن، اﷲ سىلەرنىڭ (بۇ دۇنيادىكى) ھەرىكىتىڭلارنى ۋە (ئاخىرەتتىكى) جايىڭلارنى بىلىدۇ (شۇڭا ئاخىرەتلىك ئۈچۈن تەييارلىنىڭلار)'),
(4565, 47, 20, 'مۆمىنلەر (جىھادنى تەلەپ قىلىش يۈزىسىدىن): «نېمىشقا (جىھاد ئەمرى قىلىنغان) بىرەر سۈرە نازىل قىلىنمىدى؟» دەيدۇ، جىھاد زىكرى قىلىنغان ئېنىق سۈرە نازىل قىلىنغاندا، دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلارنىڭ) ساڭا ئۆلۈم ئالدىدا (ئۆلۈمدىن قورقۇپ) ئايلىنىپ كەتكەن ئادەمدەك قاراۋاتقانلىقىنى كۆرىسەن، (ئى مۇھەممەد!) (ئۇلارنىڭ ساڭا) ئىتائەت قىلىشى ۋە چىرايلىق سۆز قىلىشى ئۇلار ئۈچۈن ئەلۋەتتە ياخشى ئىدى، ئۇرۇش قارار قىلىنغان چاغدا، ئۇلار اﷲ قا سادىق بولۇشسا ئەلۋەتتە ئۇلار ئۈچۈن ياخشى ئىدى'),
(4566, 47, 21, 'مۆمىنلەر (جىھادنى تەلەپ قىلىش يۈزىسىدىن): «نېمىشقا (جىھاد ئەمرى قىلىنغان) بىرەر سۈرە نازىل قىلىنمىدى؟» دەيدۇ، جىھاد زىكرى قىلىنغان ئېنىق سۈرە نازىل قىلىنغاندا، دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلارنىڭ) ساڭا ئۆلۈم ئالدىدا (ئۆلۈمدىن قورقۇپ) ئايلىنىپ كەتكەن ئادەمدەك قاراۋاتقانلىقىنى كۆرىسەن، (ئى مۇھەممەد!) (ئۇلارنىڭ ساڭا) ئىتائەت قىلىشى ۋە چىرايلىق سۆز قىلىشى ئۇلار ئۈچۈن ئەلۋەتتە ياخشى ئىدى، ئۇرۇش قارار قىلىنغان چاغدا، ئۇلار اﷲ قا سادىق بولۇشسا ئەلۋەتتە ئۇلار ئۈچۈن ياخشى ئىدى'),
(4567, 47, 22, 'سىلەر (ئىسلام) دىن يۈز ئۆرۈسەڭلار زېمىندا بۇزغۇنچىلىق قىلارسىلەرمۇ ۋە سىلە - رەھىمنى ئۈزۈپ قويارسىلەرمۇ؟'),
(4568, 47, 23, 'ئەنە شۇنداق كىشىلەرنى اﷲ رەھمىتىدىن يىراق قىلدى، ئۇلارنى (ھەقنى ئاڭلاشتىن) گاس قىلدى، (ھىدايەت يولىنى كۆرۈشتىن) كور قىلدى'),
(4569, 47, 24, 'ئۇلار (ھەقنى تونۇش ئۈچۈن) قۇرئاننى پىكىر قىلمامدۇ؟ بەلكى ئۇلارنىڭ دىللىرىدا قۇلۇپ بار (شۇڭا ئۇلار چۈشەنمەيدۇ)'),
(4570, 47, 25, 'توغرا يول ئېنىق بولغاندىن كېيىن ئارقىسىغا قايتىۋالغانلار (يەنى ئىماندىن كۇفرىغا قايتقانلار) غا (ئۇلارنىڭ بۇ ئىشىنى) شەيتان چىرايلىق كۆرسەتتى، (شەيتان ئارزۇ ۋە ئەجەلنىڭ ئۇزۇنلۇقى بىلەن) ئۇلارنىڭ كۆڭلىنى تىندۇردى'),
(4571, 47, 26, 'بۇ (يەنى شەيتاننىڭ ئۇلارنى ئازدۇرۇشى) شۇنىڭ ئۈچۈندۇركى، ئۇلار اﷲ نازىل قىلغان نەرسىنى يامان كۆرگەن كاپىرلارغا: «بىز بەزى ئىشلاردا سىلەرگە ئىتائەت قىلىمىز» دېدى، اﷲ ئۇلارنىڭ سىرلىرىنى بىلىدۇ'),
(4572, 47, 27, '(ئازاب) پەرىشتىلىرى ئۇلارنىڭ يۈزلىرىگە ۋە ئارقىلىرىغا ئۇرغان ھالدا ئۇلارنىڭ جانلىرىنى ئېلىۋاتقاندا (ئۇلارنىڭ ھالى) قانداق بولىدۇ؟'),
(4573, 47, 28, 'بۇ (ئازاب) شۇنىڭ ئۈچۈندۇركى، ئۇلار اﷲ نى دەرغەزەپ قىلىدىغان نەرسىلەرگە ئەگەشتى، اﷲ نىڭ رازىلىقىنى (يەنى اﷲ نى رازى قىلىدىغان ئىشلارنى) ياقتۇرمىدى، شۇنىڭ بىلەن اﷲ ئۇلارنىڭ ئەمەللىرىنى بىكار قىلدى'),
(4574, 47, 29, 'دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار) اﷲ ئۆزلىرىنىڭ (ئىسلامغا بولغان) دۈشمەنلىكىنى ئاشكارىلىمايدۇ دەپ ئويلامدۇ؟'),
(4575, 47, 30, 'ئەگەر بىز خالىساق، ئۇلارنى چوقۇم ساڭا بىلدۈرەتتۇق، چوقۇم ئۇلارنىڭ سىماسىدىن تونۇيتتۇڭ، سەن ئەلۋەتتە سۆزىنىڭ ئۇسلۇبىدىن ئۇلارنى تونۇيسەن، اﷲ سىلەرنىڭ ئەمەللىرىڭلارنى بىلىپ تۇرىدۇ'),
(4576, 47, 31, 'بىز سىلەرنى ئەلۋەتتە (جىھادقا ئەمر قىلىش ۋە مۇشەققەتلىك ئىشلارغا تەكلىپ قىلىش بىلەن) سىنايمىز، تاكى سىلەرنىڭ ئىچىڭلاردىن (اﷲ نىڭ يولىدا) جىھاد قىلغۇچىلارنى ۋە (جىھادنىڭ مۇشەققەتلىرىگە) چىداشلىق بەرگۈچىلەرنى بىلگەنگە، سىرلىرىڭلاردىن ۋاقىپ بولغانغا قەدەر'),
(4577, 47, 32, 'شۈبھىسىزكى، كاپىر بولغانلار، (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلار، توغرا يول (يەنى رەسۇلۇللاھنىڭ ھەق ئىكەنلىكى) ئېنىق بولغاندىن كېيىن پەيغەمبەر بىلەن دۈشمەنلەشكەنلەر اﷲ قا قىلچە زىيان يەتكۈزەلمەيدۇ، اﷲ ئۇلارنىڭ (سەدىقە بەرگەنگە ئوخشاش) ئەمەللىرىنى بىكار قىلىدۇ (شۇنىڭ بىلەن ئاخىرەتتە ئۇنىڭ ساۋابىغا ئېرىشەلمەيدۇ)'),
(4578, 47, 33, 'ئى مۆمىنلەر! اﷲ قا ئىتائەت قىلىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار، ئەمەللىرىڭلارنى (كۇفرى، نىفاق ۋە رىيا بىلەن) بىكار قىلىۋەتمەڭلار'),
(4579, 47, 34, 'شۈبھىسىزكى، كاپىر بولغان ۋە (كىشىلەرنى) اﷲ نىڭ يولىدىن توسقانلار، ئاندىن كاپىر پىتى ئۆلگەنلەرنى اﷲ ھەرگىز مەغپىرەت قىلمايدۇ'),
(4580, 47, 35, '(كاپىرلار بىلەن جەڭ قىلغان چېغىڭلاردا) بوشاپ قالماڭلار، (كاپىرلارنى) سۈلھكە چاقىرماڭلار، ھالبۇكى، سىلەر غالىپتۇرسىلەر، اﷲ سىلەر بىلەن بىرگىدۇر، اﷲ ھەرگىز ئەمەلىڭلارنىڭ (ساۋابىنى) كېمەيتىۋەتمەيدۇ'),
(4581, 47, 36, 'دۇنيا تىرىكچىلىكى پەقەت ئويۇن ۋە تاماشادىن ئىبارەتتۇر، ئەگەر سىلەر ئىمان ئېيتساڭلار ۋە تەقۋادارلىق قىلساڭلار (اﷲ) سىلەرگە ئەجرىڭلارنى بېرىدۇ، مال - مۈلكۈڭلارنىڭ ھەممىسىنى (بېرىشنى) سورىمايدۇ (پەقەت زاكاتنى بېرىشىڭلارنى سورايدۇ)'),
(4582, 47, 37, 'ئەگەر ئۇ سىلەردىن مال - مۈلكۈڭلارنى جىددى سورىسا، بېخىللىق قىلىسىلەر، بۇ بېخىللىق سىلەرنىڭ (ئىسلامغا بولغان) ئاداۋىتىڭلارنى ئاشكارىلايدۇ'),
(4583, 47, 38, 'سىلەر شۇنداق كىشىلەرسلەركى، اﷲ نىڭ يولىدا (پۇل - مال) سەرپ قىلىشقا چاقىرىلساڭلار، سىلەرنىڭ ئاراڭلاردا بېخىللىق قىلىدىغانلار بار، كىمكى بېخىللىق قىلىدىكەن، ئۇ ئۆزىنىڭ زىيىنى ئۈچۈن بېخىللىق قىلىدۇ، اﷲ (سىلەرنىڭ مال - مۈلكۈڭلاردىن) بىھاجەتتۇر، سىلەر بولساڭلار (اﷲ قا) موھتاجسىلەر، ئەگەر سىلەر (اﷲ نىڭ تائىتىدىن) يۈز ئۆرۈسەڭلار، اﷲ سىلەرنىڭ ئورنۇڭلارغا باشقا بىر قەۋمنى ئالماشتۇرىدۇ، ئۇلار سىلەرگە ئوخشاش بولمايدۇ (بەلكى اﷲ قا ئىتائەت قىلىدۇ)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'اﷲ نىڭ سېنىڭ ئىلگىرىكى ۋە كېيىنكى گۇناھلىرىڭنى كەچۈرۈشى ئۈچۈن، ساڭا بەرگەن نېمىتىنى مۇكەممەللەشتۈرۈشى ئۈچۈن، سېنى توغرا يولغا باشلىشى ئۈچۈن، ساڭا كۈچلۈك ياردەم بېرىشى ئۈچۈن، ساڭا بىز ھەقىقەتەن روشەن غەلىبە ئاتا قىلدۇق'),
(4585, 48, 2, 'اﷲ نىڭ سېنىڭ ئىلگىرىكى ۋە كېيىنكى گۇناھلىرىڭنى كەچۈرۈشى ئۈچۈن، ساڭا بەرگەن نېمىتىنى مۇكەممەللەشتۈرۈشى ئۈچۈن، سېنى توغرا يولغا باشلىشى ئۈچۈن، ساڭا كۈچلۈك ياردەم بېرىشى ئۈچۈن، ساڭا بىز ھەقىقەتەن روشەن غەلىبە ئاتا قىلدۇق'),
(4586, 48, 3, 'اﷲ نىڭ سېنىڭ ئىلگىرىكى ۋە كېيىنكى گۇناھلىرىڭنى كەچۈرۈشى ئۈچۈن، ساڭا بەرگەن نېمىتىنى مۇكەممەللەشتۈرۈشى ئۈچۈن، سېنى توغرا يولغا باشلىشى ئۈچۈن، ساڭا كۈچلۈك ياردەم بېرىشى ئۈچۈن، ساڭا بىز ھەقىقەتەن روشەن غەلىبە ئاتا قىلدۇق'),
(4587, 48, 4, 'مۆمىنلەرنىڭ ئىمانىغا ئىمان قوشۇلۇشى ئۈچۈن، اﷲ ئۇلارنىڭ دىللىرىغا تەمكىنلىكنى چۈشۈردى، ئاسمانلارنىڭ ۋە زېمىننىڭ (پەرىشتىلەردىن، جىنلاردىن، ھايۋانلاردىن ۋە تەبىئەت ھادىسىلىرىدىن بولغان) قوشۇنلىرى اﷲ نىڭدۇر، اﷲ (خەلقنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(4588, 48, 5, '(مۆمىنلەرنىڭ دىللىرىغا اﷲ نىڭ تەمكىنلىك چۈشۈرۈشى) ئەر - ئايال مۆمىنلەرنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان، ئۇلار مەڭگۈ قالىدىغان جەننەتلەرگە كىرگۈزۈشى ئۈچۈندۇر، اﷲ نىڭ ئۇلارنىڭ گۇناھلىرىنى كەچۈرۈشى ئۈچۈندۇر، اﷲ نىڭ دەرگاھىدا، بۇ زور مۇۋەپپەقىيەتتۇر'),
(4589, 48, 6, '(مۆمىنلەرنىڭ دىللىرىغا اﷲ نىڭ تەمكىنلىك چۈشۈرۈشى) مۇناپىق ۋە مۇناپىقەلەرگە، مۇشرىك ۋە مۇشرىكەلەرگە ئازاب قىلىش ئۈچۈندۇر، ئۇلار اﷲ قا قارىتا (اﷲ پەيغەمبىرىگە ۋە مۆمىنلەرگە ياردەم) بەرمەيدۇ دەيدىغان يامان ئويلاردا بولدى، (ئۇلار مۆمىنلەرگە يەتسۇن دېگەن) ھالاكەت ئۇلارنىڭ ئۆزلىرىگە يېتىدۇ. اﷲ ئۇلارغا غەزەپ قىلدى، ئۇلارنى رەھمىتىدىن يىراق قىلدى، ئۇلارغا جەھەننەمنى تەييارلىدى، جەھەننەم نېمىدېگەن يامان جاي!'),
(4590, 48, 7, 'ئاسمانلارنىڭ ۋە زېمىننىڭ قوشۇنلىرى اﷲ نىڭدۇر، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(4591, 48, 8, 'بىز ھەقىقەتەن سېنى (قىيامەت كۈنى خەلققە) گۇۋاھلىق بەرگۈچى، (مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (كاپىرلارنى ئازابتىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق'),
(4592, 48, 9, 'سىلەرنىڭ اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتىشىڭلار ئۈچۈن، ئۇنى (يەنى پەيغەمبەرنى) ئۇلۇغلىشىڭلار ۋە ھۆرمەتلىشىڭلار ئۈچۈن، ئەتىگەندە ۋە ئاخشامدا تەسبىھ ئېيتىشىڭلار ئۈچۈن (بىز پەيغەمبەرنى ئەۋەتتۇق)'),
(4593, 48, 10, 'شۈبھىسىزكى، (ئى مۇھەممەد! ھۇدەيبىيىدە) ساڭا (رىزۋان) بەيئىتىنى قىلغانلار (ھەقىقەتتە) اﷲ قا بەيئەت قىلغان بولىدۇ، اﷲ نىڭ قولى ئۇلارنىڭ قولىنىڭ ئۈستىدىدۇر (يەنى اﷲ ئۇلارنىڭ قىلغان بەيئىتىنى كۆرۈپ تۇرغۇچىدۇر، اﷲ ئۇلارنى بەيئەت قىلغانلىقى ئۈچۈن مۇكاپاتلايدۇ)، كىمكى ئەھدىنى بۇزىدىكەن، ئەھدىنى بۇزغانلىقىنىڭ زېيىنى ئۇنىڭ ئۆزىگە بولىدۇ، اﷲ بىلەن قىلغان ئەھدىسىگە ۋاپا قىلغانلارغا اﷲ بۈيۈك ئەجىر ئاتا قىلىدۇ'),
(4594, 48, 11, '(ھۇدەيبىيىگە چىقماي) قېلىپ قالغان (مۇناپىق) ئەئرابىلار ساڭا: «بىز ماللىرىمىز، بالا - چاقىلىرىمىز بىلەن بولۇپ قېلىپ (سەن بىلەن چىقالمىدۇق)، بىز ئۈچۈن مەغپىرەت تەلەپ قىلغىن» دەيدۇ، ئۇلارنىڭ تىلى بىلەن ئېيتقىنى دىلىدىكى سۆز ئەمەس (يەنى ئۇلار يالغان ئۆزرە ئېيتىدۇ). ئېيتقىنكى، «ئەگەر اﷲ سىلەرگە بىرەر زىياننى ياكى پايدىنى ئىرادە قىلسا، كىم سىلەر ئۈچۈن اﷲ نىڭ ئالدىدا بىرەر نەرسىگە دال بولالايدۇ؟ (يەنى كىم سىلەردىن اﷲ نىڭ خاھىشى ۋە قازاسىنى توسىيالايدۇ؟) ئۇنداق ئەمەس (يەنى ئىش سىلەر ئويلىغاندەك ئەمەس)، اﷲ سىلەرنىڭ قىلمىشىڭلاردىن تولۇق خەۋەرداردۇر'),
(4595, 48, 12, 'بەلكى سىلەر، پەيغەمبەر ۋە مۆمىنلەر ئائىلىسىگە مەڭگۈ قايتمايدۇ، دەپ گۇمان قىلدىڭلار، بۇ (گۇمراھلىق) دىللىرىڭلاردا چىرايلىق تۇيۇلدى، سىلەر يامان گۇماندا (يەنى ئۇلار ئۆلتۈرۈلۈپ بىرىمۇ ساق قايتمايدۇ دەيدىغان ئويدا) بولدۇڭلار، سىلەر ھالاك بولغۇچى قەۋم بولدۇڭلار»'),
(4596, 48, 13, 'كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىشەنمەيدىكەن (ئۇ كاپىردۇر)، شۈبھىسىزكى، بىز كاپىرلارغا دوزاخنى تەييارلىدۇق'),
(4597, 48, 14, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ خالىغان ئادەمگە مەغپىرەت قىلىدۇ، خالىغان ئادەمگە ئازاب قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(4598, 48, 15, '(ھۇدەيبىيىگە چىقماي) قېلىپ قالغانلار غەنىمەت ئېلىش ئۈچۈن چىققان ۋاقىتلىرىدا: «بىزمۇ سىلەر بىلەن چىقايلى» دەيدۇ، ئۇلار اﷲ نىڭ (خەيبەر غەنىمىتىنى ھۇدەيبىيىگە چىققانلارغا خاس قىلغانلىقتىن) ئىبارەت سۆزىنى ئۆزگەرتمەكچى بولىدۇ. (ئۇلارغا) ئېيتقىنكى، «بىز بىلەن چىقىشىڭلارغا ھەرگىز بولمايدۇ، اﷲ ئىلگىرى مۇشۇنداق دېگەن». ئۇلار: «ئۇنداق ئەمەس (يەنى اﷲ ئۇنداق دېمىگەن)، سىلەر بىزگە ھەسەت قىلىۋاتىسىلەر» دەيدۇ. ئۇنداق ئەمەس، ئۇلار (اﷲ نىڭ كالامىنى) پەقەت يۈزەگىنە چۈشىنىدۇ'),
(4599, 48, 16, '(ھۇدەيبىيىگە چىقماي) قېلىپ قالغان ئەئرابىلارغا: «سىلەر جەڭگىۋار بىر قەۋم بىلەن (ئۇرۇشۇشقا) چاقىرىلىسىلەر، ياكى ئۇلار (ئۇرۇشسىزلا) مۇسۇلمان بولىدۇ (ئۇلار بىلەن ئۇرۇشمايسىلەر)، ئەگەر سىلەر (بۇيرۇققا) بويسۇنساڭلار، اﷲ سىلەرگە چىرايلىق ئەجىر ئاتا قىلدۇ، ئەگەر سىلەر ئىلگىرى قېلىپ قالغاندەك، يەنە قېلىپ قالساڭلار، اﷲ سىلەرگە قاتتىق ئازاب قىلىدۇ» دېگىن'),
(4600, 48, 17, '(جىھادقا چىقمىسا) ئەماغا گۇناھ بولمايدۇ، توكۇرغىمۇ گۇناھ بولمايدۇ، كېسەلگىمۇ گۇناھ بولمايدۇ، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىدىكەن، ئۇنى اﷲ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، كىمكى (ئۆزرىسىز جىھادتىن) باش تارتىدىكەن، اﷲ ئۇنى (دۇنيادا خورلۇق بىلەن، ئاخرەتتە ئوت بىلەن) قاتتىق ئازابلايدۇ'),
(4601, 48, 18, 'اﷲ مۆمىنلەردىن ھەقىقەتەن رازى بولدى، (ئى مۇھەممەد!) ئۆز ۋاقتىدا ئۇلار (ھۇدەيبىيىدە) دەرەخ (سايىسى) ئاستىدا ساڭا بەيئەت قىلدى. اﷲ ئۇلارنىڭ دىلىدىكىنى (يەنى راستلىق بىلەن ۋاپانى) بىلىدۇ، اﷲ ئۇلارغا (ئۇلار بەيئەت قىلىۋاتقاندا) تەمكىنلىك چۈشۈرۈپ بەردى ۋە ئۇلارنى يېقىن غەلىبە (يەنى خەيبەرنىڭ پەتھى قىلىنىشى) بىلەن ۋە ئۇلار ئالىدىغان نۇرغۇن غەنىمەتلەر بىلەن مۇكاپاتلىدى. اﷲ غالىپتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(4602, 48, 19, 'اﷲ مۆمىنلەردىن ھەقىقەتەن رازى بولدى، (ئى مۇھەممەد!) ئۆز ۋاقتىدا ئۇلار (ھۇدەيبىيىدە) دەرەخ (سايىسى) ئاستىدا ساڭا بەيئەت قىلدى. اﷲ ئۇلارنىڭ دىلىدىكىنى (يەنى راستلىق بىلەن ۋاپانى) بىلىدۇ، اﷲ ئۇلارغا (ئۇلار بەيئەت قىلىۋاتقاندا) تەمكىنلىك چۈشۈرۈپ بەردى ۋە ئۇلارنى يېقىن غەلىبە (يەنى خەيبەرنىڭ پەتھى قىلىنىشى) بىلەن ۋە ئۇلار ئالىدىغان نۇرغۇن غەنىمەتلەر بىلەن مۇكاپاتلىدى. اﷲ غالىپتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(4603, 48, 20, 'اﷲ سىلەر ئالىدىغان نۇرغۇن غەنىمەتلەرنى سىلەرگە ۋەدە قىلدى، بۇنى (يەنى خەيبەرنىڭ غەنىمىتىنى) ھەممىدىن بۇرۇن ئاتا قىلدى، سىلەرنى دۈشمەنلەرنىڭ قول سېلىشىدىن ساقلىدى، (غەنىمەتلەر، مەككىنىڭ پەتھى قىلىنىشى، مەسجىدى ھەرەمغا كىرىش) مۆمىنلەرگە نىشان بولۇپ قېلىشى ئۈچۈن ۋە سىلەرنى توغرا يولغا باشلاش ئۈچۈن (اﷲ شۇنداق قىلدى)'),
(4604, 48, 21, 'اﷲ يەنە ئۆز كۈچۈڭلار بىلەن قولغا كەلتۈرەلمەيدىغان، اﷲ (ئۇنىڭ سىلەرگە مۇيەسسەر بولىدىغانلىقىنى) تولۇق بىلگەن بىر غەنىمەت (يەنى مەككىنىڭ پەتھى قىلىنىشى) نى (سىلەرگە مۇيەسسەر قىلدى). اﷲ ھەر نەرسىگە قادىردۇر'),
(4605, 48, 22, 'ئەگەر كاپىرلار سىلەر بىلەن (ھۇدەيبىيىدە) ئۇرۇشسا، ئەلۋەتتە، ئارقىسىغا قاراپ قاچاتتى، ئاندىن (ئۆزلىرىگە) ھېچ ئىگە ۋە ياردەمچى تاپالمايتتى'),
(4606, 48, 23, 'بۇ (يەنى كاپىرلارنى مەغلۇپ قىلىش، مۆمىنلەرنى غەلىبە قىلدۇرۇش) اﷲ نىڭ ئىلگىرىدىن تارتىپ تۇتۇپ كېلىۋاتقان يولىدۇر، اﷲ نىڭ يولىدا ھېچقانداق ئۆزگىرىش تاپالمايسەن (يەنى اﷲ نىڭ تۇتقان يولى ئۆزگەرمەيدۇ)'),
(4607, 48, 24, 'اﷲ ئۇلارنى سىلەرگە قول سېلىشتىن توستى ھەمدە ئۇلار ئۈستىدىن غەلىبە قىلغىنىڭلاردىن كېيىن، مەككىنىڭ ئىچىدە (يەنى ھۇدەيبىيىدە) سىلەرنى ئۇلارغا قول سېلىشىڭلاردىن توستى، اﷲ قىلدىغان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(4608, 48, 25, 'ئۇلار كاپىر بولدى، سىلەر مەسجىدى ھەرەمدىن ۋە تۇتۇپ تۇرۇلغان قۇربانلىقنىڭ ئۆز جايىغا يېتىشىدىن توستى، (مەككىدە) سىلەر بىلمەيدىغان ئەر - ئايال مۆمىنلەر بولۇپ، سىلەر بىلمەستىن ئۇلارنى ئۆلتۈرۈپ قويۇش بىلەن گۇناھقا گىرىپتار بولۇشۇڭلارنىڭ خەۋپى بولمىسا ئىدى (ئەلۋەتتە اﷲ مەككىگە كىرىشىڭلارغا رۇخسەت قىلاتتى ۋە سىلەرنى كاپىرلارغا مۇسەللەت قىلاتتى)، اﷲ خالىغان ئادەمنى رەھمىتى (دائىرىسىگە) كىرگۈزۈش ئۈچۈن (سىلەرنى مەككىنى پەتھى قىلىشتىن توستى)، ئەگەر ئۇلار ئايرىلسا (يەنى مۆمىنلەر كاپىرلاردىن ئايرىلسا)، ئۇلارنىڭ ئارىسىدىكى كاپىرلارنى قاتتىق ئازاب بىلەن ئازابلايتتۇق'),
(4609, 48, 26, 'كاپىرلار قىزىققانلىقىنى – جاھىلىيەت دەۋرىنىڭ قىزىققانلىقىنى – دىللىرىغا پۈككەن چاغدا، اﷲ پەيغەمبىرىگە ۋە مۆمىنلەرگە تەمكىنلىكنى چۈشۈردى. ئۇلارغا تەقۋا كەلىمىسىنى (يەنى كەلىمە تەۋھىدنى) ئىختىيار قىلدى. مۆمىنلەر تەقۋا كەلىمىسىگە ئەڭ لايىق ئىدى ۋە ئۇنىڭ ئەھلى ئىدى، اﷲ ھەر نەرسىنى بىلگۈچىدۇر'),
(4610, 48, 27, 'اﷲ پەيغەمبىرىگە، ھەق رەۋىشتە، ھەقىقىي راست چۈش كۆرسەتتى. (ئى مۇھەممەد! سەن ۋە ساھابىلىرىڭ) خۇدا خالىسا (دۈشمەندىن) ئەمىن بولغان، (بەزىلىرىڭلار) باشلىرىڭلارنى چۈشۈرگەن ۋە (بەزىلىرىڭلار) قىرقىغان ھالدا، قورقماي چوقۇم مەسجىدى ھەرەمغا كىرىسىلەر، اﷲ (سۈلھتىكى) سىلەر بىلمەيدىغان (پايدىنى) بىلىدۇ. اﷲ بۇنىڭدىن بۇرۇن (سىلەرگە) يېقىن بىر غەلىبىنى (يەنى خەيبەرنىڭ پەتھىسىنى مۇيەسسەر) قىلدى'),
(4611, 48, 28, 'اﷲ ھەق دىننى پۈتۈن دىنلاردىن ئۈستۈن قىلىش ئۈچۈن، پەيغەمبىرىنى ھىدايەت بىلەن ۋە ھەق دىن بىلەن ئەۋەتتى (مۇھەممەد ئەلەيھىسسالامنىڭ اﷲ نىڭ پەيغەمبىرى ئىكەنلىكىگە) گۇۋاھلىق بېرىشكە اﷲ يېتەرلىكتۇر'),
(4612, 48, 29, 'مۇھەممەد اﷲ نىڭ رەسۇلىدۇر، ئۇنىڭ بىلەن بىرگە بولغان مۆمىنلەر كۇففارلارغا قاتتىقتۇر، ئۆزئارا كۆيۈمچاندۇر، ئۇلارنى رۇكۇ قىلغان، سەجدە قىلغان ھالدا كۆرىسەن. ئۇلار اﷲ نىڭ پەزلىنى ۋە رازىلىقىنى تىلەيدۇ، ئۇلارنىڭ يۈزلىرىدە سەجدىنىڭ ئەسىرىدىن نىشانلار بار، ئەنە شۇ ئۇلارنىڭ تەۋراتتىكى سۈپىتىدۇر، ئۇلارنىڭ ئىنجىلدىكى سۈپىتى بولسا (ئۇلار) شاخ چىقارغان، كۈچلىنىپ چوڭايغان، ئاندىن ئۆز غولى بىلەن ئۆرە تۇرغان، بولۇقلۇقى ۋە كۆركەم كۆرۈنۈشى بىلەن، دېھقانلارنى مەمنۇن قىلغان بىر زىرائەتكە ئوخشايدۇ، (مۆمىنلەرنى مۇنداق زىرائەتكە ئوخشىتىش) كۇففارلارنى خاپا قىلىش ئۈچۈندۇر، اﷲ ئۇلارنىڭ ئىچىدىن ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا مەغپىرەت ۋە كاتتا ئەجىر ۋەدە قىلدى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'ئى مۆمىنلەر! سىلەر اﷲ نىڭ ۋە اﷲ نىڭ پەيغەمبىرىنىڭ ئالدىدا (ھېچقانداق بىر ئىشنى ۋە سۆزنى) ئالدى بىلەن قىلماڭلار، اﷲ تىن قورقۇڭلار، اﷲ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيىتىڭلارنى ۋە ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر'),
(4614, 49, 2, 'ئى مۆمىنلەر! (رەسۇلۇللاھقا سۆز قىلغان چېغىڭلاردا) ئاۋازىڭلارنى پەيغەمبەرنىڭ ئاۋازىدىن يۇقىرى قىلماڭلار، سىلەر بىر - بىرىڭلار بىلەن (توۋلاپ) يۇقىرى ئاۋازدا سۆزلەشكەندەك، پەيغەمبەرگە (توۋلاپ) يۇقىرى ئاۋازدا سۆز قىلماڭلار، (ئۇنداق قىلساڭلار) ئۆزلىرىڭلار تۇيماستىن، (قىلغان ياخشى) ئەمەللىرىڭلار بىكار بولۇپ كېتىدۇ'),
(4615, 49, 3, 'شۈبھىسىزكى، رەسۇلۇللاھنىڭ يېنىدا پەس ئاۋازدا گەپ قىلغانلارنىڭ دىللىرىنى اﷲ تەقۋا ئۈچۈن سىنىغان، ئۇلار مەغپىرەتكە ۋە كاتتا ساۋابقا (يەنى جەننەتكە) ئېرىشىدۇ'),
(4616, 49, 4, 'شۈبھىسىزكى، سېنى ھۇجرىلار (يەنى پاك ئاياللىرىڭنىڭ تۇرالغۇ جايلىرى) ئارقىسىدىن توۋلايدىغانلارنىڭ تولىسى ئەقىلسىز كىشىلەردۇر'),
(4617, 49, 5, 'سەن ئۇلارنىڭ ئالدىغا چىققىنىڭغا قەدەر ئۇلار سەۋر قىلىپ تۇرۇشسا، ئۇلار ئۈچۈن ئەلۋەتتە ياخشى بولاتتى، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(4618, 49, 6, 'ئى مۆمىنلەر! ئەگەر سىلەرگە بىر پاسىق ئادەم بىرەر خەۋەر ئېلىپ كەلسە، (ئىشنىڭ ھەقىقىتىنى) بىلمەستىن بىرەر قەۋمنى رەنجىتىپ قويۇپ، قىلمىشىڭلارغا پۇشايمان قىلىپ قالماسلىقىڭلار ئۈچۈن، (ئۇ خەۋەرنى) ئېنىقلاپ كۆرۈڭلار'),
(4619, 49, 7, 'بىلىڭلاركى، ئىچىڭلاردا رەسۇلۇللاھ (يەنى اﷲ نىڭ پەيغەمبىرى) بار، ئەگەر ئۇ نۇرغۇن ئىشلاردا سىلەرگە ئىتائەت قىلىدىغان بولسا، چوقۇم قىيىن ئەھۋالدا قالاتتىڭلار، لېكىن اﷲ سىلەرگە ئىماننى قىزغىن سۆيگۈزدى ۋە ئۇنى دىلىڭلاردا كۆركەم قىلدى، سىلەرگە پىسقىنى، كۇفرىنى ۋە گۇناھنى يامان كۆرسەتتى، ئەنە شۇلار اﷲ نىڭ پەزلى ۋە نېمىتى بىلەن توغرا يولدا بولغۇچىلاردۇر. اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(4620, 49, 8, 'بىلىڭلاركى، ئىچىڭلاردا رەسۇلۇللاھ (يەنى اﷲ نىڭ پەيغەمبىرى) بار، ئەگەر ئۇ نۇرغۇن ئىشلاردا سىلەرگە ئىتائەت قىلىدىغان بولسا، چوقۇم قىيىن ئەھۋالدا قالاتتىڭلار، لېكىن اﷲ سىلەرگە ئىماننى قىزغىن سۆيگۈزدى ۋە ئۇنى دىلىڭلاردا كۆركەم قىلدى، سىلەرگە پىسقىنى، كۇفرىنى ۋە گۇناھنى يامان كۆرسەتتى، ئەنە شۇلار اﷲ نىڭ پەزلى ۋە نېمىتى بىلەن توغرا يولدا بولغۇچىلاردۇر. اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(4621, 49, 9, 'ئەگەر مۆمىنلەردىن ئىككى گۇرۇھ ئۇرۇشۇپ قالسا، ئۇلارنىڭ ئارىسىنى تۈزەپ قويۇڭلار، ئەگەر ئۇلارنىڭ بىرى ئىككىنچىسىگە تاجاۋۇز قىلسا، تاجاۋۇز قىلغۇچى تاكى اﷲ نىڭ ھۆكمىگە قايتقانغا قەدەر (يەنى تاجاۋۇزىنى توختاتقانغا قەدەر) ئۇنىڭ بىلەن ئۇرۇشۇڭلار، ئەگەر ئۇلار (اﷲ نىڭ ئەمرىگە) قايتسا، ئۇلارنىڭ ئارىسىنى (ھېچبىر تەرەپكە يان باسماستىن) ئادىللىق بىلەن تۈزەپ قويۇڭلار، (ھەممە ئىشتا) ئادىل بولۇڭلار، اﷲ ھەقىقەتەن ئادىللارنى دوست تۇتىدۇ'),
(4622, 49, 10, 'مۆمىنلەر ھەقىقەتەن (دىندا) قېرىنداشلاردۇر، (ئۇرۇشۇپ قالسا) قېرىنداشلىرىڭلارنىڭ ئارىسىنى تۈزەڭلار، رەھمەتكە ئېرىشىشىڭلار ئۈچۈن، اﷲ تىن قورقۇڭلار'),
(4623, 49, 11, 'ئى مۆمىنلەر! بىر قەۋم يەنە بىر قەۋمنى (يەنى بىر جامائە يەنە بىر جامائەنى، بىر ئادەم يەنە بىر ئادەمنى) مەسخىرە قىلمىسۇن، مەسخىرە قىلىنغان قەۋم (اﷲ نىڭ نەزىرىدە) مەسخىرە قىلغۇچى قەۋمدىن ياخشىراق بولۇشى مۇمكىن، سىلەرنىڭ ئاراڭلاردىكى ئاياللارمۇ ئۆزئارا مەسخىرە قىلىشمىسۇن، مەسخىرە قىلىنغۇچى ئاياللار (اﷲ نىڭ دەرگاھىدا) مەسخىرە قىلغۇچى ئاياللاردىن ياخشىراق بولۇشى مۈمكىن، بىر - بىرىڭلارنى ئەيىبلىمەڭلار، بىر - بىرىڭلارنى يامان لەقەم بىلەن چاقىرماڭلار، ئىماندىن كېيىن پىسقى بىلەن ئاتاش (يەنى مۆمىننى پاسىق) دەپ ئاتاش نېمىدېگەن يامان! (مۇنداق نەھيى قىلىنغان ئىشلاردىن) تەۋبە قىلمىغانلار زالىملاردۇر'),
(4624, 49, 12, 'ئى مۆمىنلەر! نۇرغۇن گۇمانلاردىن ساقلىنىڭلار (يەنى ئائىلىدىكىلىرىڭلارغا ۋە كىشىلەرگە گۇمانخورلۇق قىلماڭلار)، بەزى گۇمانلار ھەقىقەتەن گۇناھتۇر، (مۆمىنلەرنىڭ ئەيىبىنى) ئىزدىمەڭلار، بىر - بىرىڭلارنىڭ غەيۋىتىنى قىلماڭلار، سىلەرنىڭ بىرىڭلار ئۆلگەن قېرىندىشىڭلارنىڭ گۆشىنى يېيىشنى ياقتۇرامسىلەر؟ ئۇنى ياقتۇرمايسىلەر اﷲ تىن قورقۇڭلار اﷲ (تەۋبەقىلغۇچىلارنىڭ قىلغان) تەۋبىسىنى بەكمۇ قۇبۇل قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر'),
(4625, 49, 13, 'ئى ئىنسانلار! سىلەرنى بىز ھەقىقەتەن بىر ئەر، بىر ئايالدىن، (ئادەم بىلەن ھەۋۋادىن ئىبارەت) بىر ئاتا بىر ئانىدىن ياراتتۇق ئۆز ئارا تونۇشۇشۇڭلار ئۈچۈن سىلەرنى نۇرغۇن مىللەت ۋە ئۇرۇق قىلدۇق، ھەقىقەتەن ئەڭ تەقۋادار بولغانلىرىڭلار اﷲ نىڭ دەرگاھىدا ئەڭ ھۆرمەتلىك ھېسابلىنىسىلەر (يەنى كىشىلەرنىڭ بىر - بىرىدىن ئارتۇق بولۇشى نەسەب بىلەن ئەمەس، تەقۋادارلىق بىلەن بولىدۇ)، اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر'),
(4626, 49, 14, 'ئەئرابىلار: «ئىمان ئېيتتۇق» دەيدۇ، (ئۇلارغا) ئېيتقىنكى، «(تېخى) ئىمان ئېيتمىدىڭلار ۋە لېكىن سىلەر بويسۇندۇق دەڭلار، ئىمان تېخى دىلىڭلارغا كىرمىدى، ئەگەر سىلەر اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلساڭلار، اﷲ سىلەرنىڭ ئەمەلىڭلاردىن ھېچ نەرسىنى كېمەيتىۋەتمەيدۇ، اﷲ ھەقىقەتەن (مۆمىنلەرگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر'),
(4627, 49, 15, 'شۈبھىسىزكى، (ھەقىقي) مۆمىنلەر اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان كەلتۈرگەن، ئاندىن (ئىمانىدا) شەك كەلتۈرمىگەن، ماللىرى بىلەن، جانلىرى بىلەن اﷲ نىڭ يولىدا جىھاد قىلغانلاردۇر، ئەنە شۇلار (ئىمان دەۋاسىدا) راستچىللاردۇر»'),
(4628, 49, 16, '(ئى مۇھەممەد!) ئېيتقىنكى، «اﷲ قا دىنىڭلارنى (يەنى دىنىڭلارنى دىلىڭلارنىڭ تەستىق قىلغانلىقىنى) خەۋەر قىلامسىلەر؟ ھالبۇكى، اﷲ ئاسمانلاردىكىنى ۋە زېمىندىكىنى بىلىپ تۇرىدۇ، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر»'),
(4629, 49, 17, 'ئۇلار ساڭا مۇسۇلمان بولغانلىقىنى مىننەت قىلىدۇ، (ئۇلارغا) «سىلەر ماڭا مۇسۇلمان بولغانلىقىڭلارنى مىننەت قىلماڭلار، بەلكى اﷲ سىلەرنى ئىمانغا ھىدايەت قىلغانلىقىنى مىننەت قىلىدۇ، ئەگەر سىلەر راست (مۆمىن) بولساڭلار» دېگىن'),
(4630, 49, 18, 'اﷲ ھەقىقەتەن ئاسمانلاردىكى ۋە زېمىندىكى غەيىبنى بىلىپ تۇرىدۇ، اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قاف. شەرەپلىك قۇرئان بىلەن قەسەم قىلىمەن'),
(4632, 50, 2, 'بەلكى ئۇلار (يەنى مۇشرىكلار) ئۆز ئىچىدىن ئۇلارغا بىر ئاگاھلاندۇرغۇچى كەلگەنلىكىدىن ئەجەبلەندى، كاپىرلار ئېيتتى: «بۇ قىزىق ئىشتۇر'),
(4633, 50, 3, 'بىز ئۆلۈپ توپا بولۇپ كەتكەندىن كېيىن (تىرىلىپ ئىلگىرىكى ھالىتىمىزگە قايتامدۇق)، بۇنداق قايتىش (ئەقىلدىن) يىراقتۇر»'),
(4634, 50, 4, 'ئۇلارنى زېمىننىڭ كېمەيتكىنىدىن (يەنى ئۇلارنىڭ جەسەتلىرنى يەر يەپ كەتكىنىدىن) بىز ھەقىقەتەن خەۋەردارمىز، بىزنىڭ دەرگاھىمىزدا ھەممىنى ئۆز ئىچىگە ئالغان كىتاب (يەنى لەۋھۇلمەھپۇز) بار'),
(4635, 50, 5, 'بەلكى ئۇلار بولسا ئۆزلىرىگە نازىل قىلىنغان ھەقىقەتنى (قۇرئاننى) ئىنكار قىلدى، ئۇلار قالايمىقان ھالەتتىدۇر'),
(4636, 50, 6, 'ئۇلار ئاسماننى قانداق (تۈۋرۈكسىز) بەرپا قىلغانلىقىمىزغا، ئۇنى (يۇلتۇزلار بىلەن) بېزىگەنلىكىمىزگە، ئۇنىڭدا ھېچقانداق يوچۇق يوقلۇقىغا قارىمامدۇ؟'),
(4637, 50, 7, 'يەر يۈزىنى (توغرىسىغا ۋە ئۇزۇنىسىغا سوزۇپ) كەڭ ياراتتۇق، يەر يۈزىدە (ئۇنىڭ تەۋرەپ كەتمەسلىكى ئۈچۈن) تاغلارنى قىلدۇق ۋە ئۇنىڭدا تۈرلۈك چىرايلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق'),
(4638, 50, 8, 'اﷲ قا قايتقۇچى ھەر بىر بەندىگە (اﷲ نىڭ قۇدرىتىنى) كۆرسىتىش ئۈچۈن ۋە (اﷲ نىڭ ئۇلۇغلۇقىنى) ئەسلىتىش ئۈچۈن (شۇنداق قىلدۇق)'),
(4639, 50, 9, 'كۆكتىن (بۇلۇتتىن) مۇبارەك سۇنى چۈشۈردۇق، ئۇنىڭ بىلەن باغلارنى ۋە ئاشلىقلارنى، ئېگىز ئۆسكەن، مېۋىلىرى سانجاق - سانجاق بولۇپ كەتكەن خورمىلارنى ئۆستۈردۇق'),
(4640, 50, 10, 'كۆكتىن (بۇلۇتتىن) مۇبارەك سۇنى چۈشۈردۇق، ئۇنىڭ بىلەن باغلارنى ۋە ئاشلىقلارنى، ئېگىز ئۆسكەن، مېۋىلىرى سانجاق - سانجاق بولۇپ كەتكەن خورمىلارنى ئۆستۈردۇق'),
(4641, 50, 11, '(ئۇلارنىڭ ھەممىسىنى) بەندىلەرنىڭ رىزقى ئۈچۈن (ئۆستۈردۇق)، ئۇ سۇ بىلەن ئۆلۈك (يەنى قاقاس) زېمىننى تىرىلدۈردۇق، ئۆلۈكلەرنىڭ تىرىلىشىمۇ شۇنىڭغا ئوخشاشتۇر'),
(4642, 50, 12, 'ئۇلاردىن ئىلگىرى (پەيغەمبەرلىرىنى) ئىنكار قىلغانلار نۇھنىڭ قەۋمى رەس ئاھالىسى، سەمۇد (قەۋمى)، ئاد (قەۋمى)، پىرئەۋن، لۇتنىڭ قىرىنداشلىرى، ئەيكە ئاھالىسى (يەنى شۇئەيب قەۋمى) تۈببە قەۋمى ئىدى، ئۇلارنىڭ ھەممىسى پەيغەمبەرلىرنى ئىنكار قىلدى، شۇنىڭ بىلەن مېنىڭ ئازابىم (ئۇلارغا) تېگىشلىك بولدى'),
(4643, 50, 13, 'ئۇلاردىن ئىلگىرى (پەيغەمبەرلىرىنى) ئىنكار قىلغانلار نۇھنىڭ قەۋمى رەس ئاھالىسى، سەمۇد (قەۋمى)، ئاد (قەۋمى)، پىرئەۋن، لۇتنىڭ قىرىنداشلىرى، ئەيكە ئاھالىسى (يەنى شۇئەيب قەۋمى) تۈببە قەۋمى ئىدى، ئۇلارنىڭ ھەممىسى پەيغەمبەرلىرنى ئىنكار قىلدى، شۇنىڭ بىلەن مېنىڭ ئازابىم (ئۇلارغا) تېگىشلىك بولدى'),
(4644, 50, 14, 'ئۇلاردىن ئىلگىرى (پەيغەمبەرلىرىنى) ئىنكار قىلغانلار نۇھنىڭ قەۋمى رەس ئاھالىسى، سەمۇد (قەۋمى)، ئاد (قەۋمى)، پىرئەۋن، لۇتنىڭ قىرىنداشلىرى، ئەيكە ئاھالىسى (يەنى شۇئەيب قەۋمى) تۈببە قەۋمى ئىدى، ئۇلارنىڭ ھەممىسى پەيغەمبەرلىرنى ئىنكار قىلدى، شۇنىڭ بىلەن مېنىڭ ئازابىم (ئۇلارغا) تېگىشلىك بولدى'),
(4645, 50, 15, '(ئىنسانلارنى) دسلەپتە يارىتىشتىن ئاجىز كەلدۇقمۇ؟ ھەرگىز ئۇنداق ئەمەس) بىز بۇنىڭدا ئاجىزلىق قىلمىغاچقا، ئۆلگەندىن كېيىن تىرىلدۈرۈشتىمۇ ئاجىزلىق قىلمايمىز) ئۇلار يېڭىدىن يارىتىلشقا (يەنى قىيامەتتە تىرىلدۈرۈشكە) قارىتا گۇماندىدۇر'),
(4646, 50, 16, 'بىز ھەقىقەتەن ئىنساننى ياراتتۇق، بىز ئۇنىڭ كۆڭلىگە كەلگەننى بىلىمىز، بىز ئۇنىڭ جان تومۇرىدىنمۇ يېقىنمىز'),
(4647, 50, 17, 'ئىنساننىڭ ئوڭ تەرىپىدە ۋە سول تەرىپىدە ئولتۇرۇپ خاتىرىلەيدىغان ئىككى پەرىشتە بار، (ئىنساننىڭ سۆز - ھەرىكىتى خاتىرىلىنىۋاتقان) ۋاقىتتا، ئۇ قانداق بىر سۆزنى قىلمىسۇن، ئۇنىڭ ئالدىدا ھامان پەرىشتە ھازىر بولۇپ، كۈزىتىپ تۇرىدۇ'),
(4648, 50, 18, 'ئىنساننىڭ ئوڭ تەرىپىدە ۋە سول تەرىپىدە ئولتۇرۇپ خاتىرىلەيدىغان ئىككى پەرىشتە بار، (ئىنساننىڭ سۆز - ھەرىكىتى خاتىرىلىنىۋاتقان) ۋاقىتتا، ئۇ قانداق بىر سۆزنى قىلمىسۇن، ئۇنىڭ ئالدىدا ھامان پەرىشتە ھازىر بولۇپ، كۈزىتىپ تۇرىدۇ'),
(4649, 50, 19, 'ئۆلۈمنىڭ سەكراتى ھەقىقەتنى ئېلىپ كەلدى (يەنى ئاخىرەتنى ئىنكار قىلغۇچى ئاخىرەتنىڭ دەھشىتىنى ئېنىق كۆرىدۇ)، سەن ئۇنىڭدىن (يەنى ئۆلۈمدىن) قاچاتتىڭ'),
(4650, 50, 20, 'سۇر چېلىنىدۇ، ئەنە شۇ (كۇففارلارغا) ئازاب ۋەدە قىلىنغان كۈندۇر'),
(4651, 50, 21, 'ھەر ئىنسان (مەھشەرگاھقا) كېلىدۇ، ئۇنىڭ بىلەن (ئۇنى مەھشەرگاھقا) ھەيدىگۈچى ۋە (ئۇنىڭ ئەمەلىگە) گۇۋاھ بولغۇچى (ئىككى پەرىشتە) بىللە كېلىدۇ'),
(4652, 50, 22, 'ئۇنىڭغا: «ئى ئىنسان! (دۇنيادىكى چېغىڭدا) بۇ (ئېغىر كۈن) دىن غەپلەتتە ئىدىڭ، ئەمدى پەردەڭنى سەندىن كۆتۈرۋەتتۇق، بۈگۈن كۆزۈڭ ئۆتكۈردۇر» دېيىلىدۇ'),
(4653, 50, 23, 'ئۇنىڭغا مۇئەككەل پەرىشتە: «مانا بۇ (نامە - ئەمال) ئالدىمدا تەيياردۇر» دەيدۇ'),
(4654, 50, 24, '(اﷲ تائالا ئىككى پەرىشتىگە خىتاب قىلىپ) «ھەققە قارشى ياخشىلىقتىن توسقۇچى، ھەددىدىن ئاشقۇچى، (دىنغا) شەك كەلتۈرگۈچى، اﷲ قا باشقا مەبۇدنى شېرىك قىلغۇچى ھەر بىر كاپىرنى دوزاخقا تاشلاڭلار، ئۇنى قاتتىق ئازابقا تاشلاڭلار» (دەيدۇ)'),
(4655, 50, 25, '(اﷲ تائالا ئىككى پەرىشتىگە خىتاب قىلىپ) «ھەققە قارشى ياخشىلىقتىن توسقۇچى، ھەددىدىن ئاشقۇچى، (دىنغا) شەك كەلتۈرگۈچى، اﷲ قا باشقا مەبۇدنى شېرىك قىلغۇچى ھەر بىر كاپىرنى دوزاخقا تاشلاڭلار، ئۇنى قاتتىق ئازابقا تاشلاڭلار» (دەيدۇ)'),
(4656, 50, 26, '(اﷲ تائالا ئىككى پەرىشتىگە خىتاب قىلىپ) «ھەققە قارشى ياخشىلىقتىن توسقۇچى، ھەددىدىن ئاشقۇچى، (دىنغا) شەك كەلتۈرگۈچى، اﷲ قا باشقا مەبۇدنى شېرىك قىلغۇچى ھەر بىر كاپىرنى دوزاخقا تاشلاڭلار، ئۇنى قاتتىق ئازابقا تاشلاڭلار» (دەيدۇ)'),
(4657, 50, 27, 'ئۇنىڭ ھەمراھى (بولغان شەيتان): «پەرۋەردىگارىمىز! مەن ئۇنى ئازدۇرمىدىم، لېكىن (ئۇ ئۆز ئىختىيارى بىلەن) چوڭقۇر گۇمراھلىقتا بولدى» (دەيدۇ)'),
(4658, 50, 28, 'اﷲ: «مېنىڭ ئالدىمدا مۇنازىرىلەشمەڭلار (بۇ يەردە مۇنازىرە پايدا بەرمەيدۇ)، سىلەرنى مەن ئالدىنئالا (ئازابىمدىن) ئاگاھلاندۇرغان ئىدىم، مېنىڭ سۆزۈم ئۆزگەرمەيدۇ، مەن بەندىلەرگە زۇلۇم قىلغۇچى ئەمەسمەن» دەيدۇ'),
(4659, 50, 29, 'اﷲ: «مېنىڭ ئالدىمدا مۇنازىرىلەشمەڭلار (بۇ يەردە مۇنازىرە پايدا بەرمەيدۇ)، سىلەرنى مەن ئالدىنئالا (ئازابىمدىن) ئاگاھلاندۇرغان ئىدىم، مېنىڭ سۆزۈم ئۆزگەرمەيدۇ، مەن بەندىلەرگە زۇلۇم قىلغۇچى ئەمەسمەن» دەيدۇ'),
(4660, 50, 30, 'ئۇ كۈندە بىز جەھەننەمگە: «توشتۇڭمۇ؟» دەيمىز، جەھەننەم: «يەنە بارمۇ» دەيدۇ'),
(4661, 50, 31, 'جەننەت تەقۋادارلارغا يىراق بولمىغان جايغا يېقىنلاشتۇرۇلىدۇ'),
(4662, 50, 32, 'مانا بۇ (نېمەتتىن) سىلەرگە ۋەدە قىلىنغان نەرسىلەر اﷲ نىڭ (تائىتىگە) قايتقۇچى، (ئەمرىنى) ساقلىغۇچى ھەر بىر (بەندە) ئۈچۈندۇر'),
(4663, 50, 33, 'كىمكى مېھرىبان اﷲ نى كۆرمەي تۇرۇپ، ئۇنىڭدىن قورقسا ۋە تەۋبە قىلغۇچى قەلب بىلەن كەلسە (ئۇلارغا) «جەننەتكە ئامانلىق بىلەن كىرىڭلار، بۇ مەڭگۈ قېلىش كۈنىدۇر» (دېيىلىدۇ)'),
(4664, 50, 34, 'كىمكى مېھرىبان اﷲ نى كۆرمەي تۇرۇپ، ئۇنىڭدىن قورقسا ۋە تەۋبە قىلغۇچى قەلب بىلەن كەلسە (ئۇلارغا) «جەننەتكە ئامانلىق بىلەن كىرىڭلار، بۇ مەڭگۈ قېلىش كۈنىدۇر» (دېيىلىدۇ)'),
(4665, 50, 35, 'جەننەتتە ئۇلارغا خالىغان نەرسىلەر بار ۋە بىزنىڭ ھۇزۇرىمىزدا (ئۇلارغا) زىيادە (نېمەتلەر) بار'),
(4666, 50, 36, 'بىز ئۇلاردىن (يەنى قۇرەيش كۇففارلىرىدىن) ئىلگىرى ئۇلارغا قارىغاندا كۈچلۈك بولغان نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلار (يەنى شۇ ئۈممەتلەر) ھەر قايسى جايلارنى كەزدى، (ئۇلارغا) ئۆلۈمدىن قاچىدىغان جاي تېپىلدىمۇ؟'),
(4667, 50, 37, 'بۇنىڭدا (ھەقىقەت ئۈستىدە پىكىر يۈرگۈزىدىغان قەلبكە ئىگە ئادەم ياكى (ۋەز - نەسىھەتكە) ھۇزۇرىي قەلب بىلەن قۇلاق سالىدىغان ئادەم ئۈچۈن ئەلۋەتتە ۋەز - نەسىھەت بار'),
(4668, 50, 38, 'بىز ھەقىقەتەن ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتۇق، بىز ھېچقانداق چارچاپ قالغىنىمىز يوق'),
(4669, 50, 39, '(ئى مۇھەممەد!) كاپىرلارنىڭ سۆزلىرىگە سەۋر قىلغىن، كۈن چىقىشتىن ئىلگىرى ۋە كۈن پېتىشتىن. ئىلگىرى پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن، ھەمدى ئېيتقىن'),
(4670, 50, 40, 'كېچىدە ۋە سەجدىدىن كېيىن (يەنى پەرز نامازلارنىڭ ئارقىسىدا) تەسبىھ ئېيتقىن'),
(4671, 50, 41, 'نىدا قىلغۇچى (يەنى ئىسراپىل) يېقىن جايدىن نىدا قىلىدىغان (يەنى ئۇنىڭ ئاۋازى ھەممىگە ئوخشاش يېتىدىغان) كۈندە (نىداغا) قۇلاق سالغىن'),
(4672, 50, 42, 'ئۇلار ھەقنى ئېلىپ كېلىدىغان ئاۋازنى (يەنى ئىككىنچى قېتىملىق سۇرنى) ئاڭلىغان كۈن - ئەنە شۇ (كۈن قەبرىلەردىن) چىقىش كۈنىدۇر'),
(4673, 50, 43, 'بىز ھەقىقەتەن كىشىلەرنى ۋاپات قىلدۇرىمىز ۋە تىرىلدۈرىمىز، ئاخىر بارىدىغان جاي بىزنىڭ دەرگاھىمىزدۇر'),
(4674, 50, 44, 'ئۇ كۈندە يەر يېرىلىپ ئۇلار (قەبرىلىرىدىن) تېز چىقىپ كېلىدۇ، ئەنە شۇ يىغىش بىزگە ئاساندۇر'),
(4675, 50, 45, 'بىز ئۇلارنىڭ (يەنى قۇرەيش كۇففارلىرىنىڭ) ئېيتىدىغان سۆزلىرىنى ئوبدان بىلىمىز، سەن ئۇلارنى (ئىسلامغا) زورلىغۇچى ئەمەسسەن، مېنىڭ ئاگاھلاندۇرۇشۇمدىن قورقىدىغانلارغا (قۇرئان بىلەن) ۋەز - نەسىھەت قىلغىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, '(تۇپراقنى) سورىغۇچى شاماللار بىلەن قەسەمكى،'),
(4677, 51, 2, '(يامغۇرلارنىڭ) ئېغىرلىقىنى كۆتۈرگۈچى بۇلۇتلار بىلەن قەسەمكى،'),
(4678, 51, 3, '(سۇ ئۈستىدە) يىنىك ماڭغۇچى كېمىلەر بىلەن قەسەمكى،'),
(4679, 51, 4, 'ئىشنى (يەنى بەندىلەر ئارىسىدا رىزىقلارنى، يۇرتلار ئارىسىدا يامغۇرلارنى) تەقسىم قىلغۇچى پەرىشتىلەر قەسەمكى،'),
(4680, 51, 5, 'سىلەرگە ۋەدە قىلىنغان (ساۋاب، ئازاب، قىيامەت قاتارلىق) لار ئەلۋەتتە راستتۇر'),
(4681, 51, 6, '(ياخشى ئەمەللەرنى) مۇكاپاتلاش، (يامان ئەمەللەرنى) جازالاش چوقۇم بولىدىغان ئىشتۇر'),
(4682, 51, 7, 'يوللىرى بار ئاسمان بىلەن قەسەمكى، (پەيغەمبەر ھەققىدە) سىلەرنىڭ سۆزۈڭلار ھەقىقەتەن خىلمۇخىلدۇر'),
(4683, 51, 8, 'يوللىرى بار ئاسمان بىلەن قەسەمكى، (پەيغەمبەر ھەققىدە) سىلەرنىڭ سۆزۈڭلار ھەقىقەتەن خىلمۇخىلدۇر'),
(4684, 51, 9, '(اﷲ تائالانىڭ ئىلمىي ئەزەلىسىدە ھىدايەتتىن) بۇرۇۋېتىلگەن ئادەم (قۇرئانغا ۋە مۇھەممەد ئەلەيھىسسالامغا ئىمان كەلتۈرۈشتىن) بۇرۇۋېتىلىدۇ'),
(4685, 51, 10, 'كاززاپلارغا لەنەت بولسۇنكى، ئۇلار جاھالەتكە چۆمگەن بولۇپ (ئاخىرەت ئىشىدىن) غەپلەتتىدۇر'),
(4686, 51, 11, 'كاززاپلارغا لەنەت بولسۇنكى، ئۇلار جاھالەتكە چۆمگەن بولۇپ (ئاخىرەت ئىشىدىن) غەپلەتتىدۇر'),
(4687, 51, 12, 'ئۇلار رەسۇلۇللاھتىن: «قىيامەت قاچان بولىدۇ؟» دەپ سورايدۇ'),
(4688, 51, 13, 'ئۇ بولسا ئۇ كۈندە دوزاختا ئازابلىنىدىغان كۈندۇر'),
(4689, 51, 14, '(دوزاخقا مۇئەككەل پەرىشتىلەر ئۇلارغا) «سىلەرگە قىلىنغان ئازابنى تېتىڭلار، مانا بۇ سىلەر (دۇنيادىكى چېغىڭلاردا) ئالدىراتقان نەرسىدۇر» (دەيدۇ)'),
(4690, 51, 15, 'شۈبھىسىزكى، تەقۋادارلار جەننەتلەردە ۋە (ئېقىپ تۇرغان) بۇلاقلارنىڭ ئارىسىدا بولىدۇ'),
(4691, 51, 16, 'ئۇلار پەرۋەردىگارى ئاتا قىلغان نەرسىلەرنى قوبۇل قىلىدۇ، ئۇلار بۇنىڭدىن ئىلگىرى (يەنى دۇنيادىكى چاغدا) ياخشى ئىش قىلغۇچىلار ئىدى'),
(4692, 51, 17, 'ئۇلار كېچىسى ئاز ئۇخلايتتى'),
(4693, 51, 18, 'ئۇلار سەھەرلەردە (پەرۋەردىگارىدىن) مەغپىرەت تىلەيتتى'),
(4694, 51, 19, 'ئۇلارنىڭ پۇل - ماللىرىدا سائىللار ۋە (ئىپپەتلىكىدىن سائىللىق قىلمايدىغان) موھتاجلار ھوقۇققا ئىگە ئىدى (يەنى ئۇلار سائىللارغا ۋە موھتاجلارغا سەدىقە قىلاتتى)'),
(4695, 51, 20, 'زېمىندا ۋە ئۆزۈڭلاردا اﷲ قا چىن ئېتىقاد قىلىدىغانلار ئۈچۈن) اﷲ نىڭ قۇدرىتىنى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار، (بۇنى) كۆرمەمسىلەر'),
(4696, 51, 21, 'زېمىندا ۋە ئۆزۈڭلاردا اﷲ قا چىن ئېتىقاد قىلىدىغانلار ئۈچۈن) اﷲ نىڭ قۇدرىتىنى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار، (بۇنى) كۆرمەمسىلەر'),
(4697, 51, 22, 'ئاسماندا سىلەرنىڭ رىزقىڭلار بار، سىلەرگە ۋەدە قىلىنغان ساۋاب بار'),
(4698, 51, 23, 'ئاسماننىڭ ۋە زېمىننىڭ پەرۋەردىگارى بىلەن قەسەمكى، سىلەرگە ۋەدە قىلىنغان نەرسە ئۆزۈڭلارنىڭ سۆزلەۋاتقىنىڭلاردەك (يەنى سۆزلىشىڭلارنىڭ ئۆزۈڭلارغا ئېنىق بولغىنىدەك) ھەقتۇر'),
(4699, 51, 24, '(ئى مۇھەممەد!) ساڭا ئىبراھىمنىڭ ھۆرمەتلىك مېھمانلىرىنىڭ خەۋىرى يەتتىمۇ؟'),
(4700, 51, 25, 'ئۆز ۋاقتىدا ئۇلار ئىبراھىمنىڭ يېنىغا كىرىپ سالام دېدى، ئىبراھىم سالامنى ئىلىك ئالدى. (ئىچىدە) «ناتونۇش ئادەملەرغۇ» دېدى'),
(4701, 51, 26, 'شۇنىڭ بىلەن ئۇ ئاستا ئائىلىسىگە چىقىپ (پىشۇرۇلغان) بىر سېمىز موزاينى ئېلىپ كىرىپ ئۇلارنىڭ ئالدىغا قويۇپ: «يېمەمسىلەر» دېدى'),
(4702, 51, 27, 'شۇنىڭ بىلەن ئۇ ئاستا ئائىلىسىگە چىقىپ (پىشۇرۇلغان) بىر سېمىز موزاينى ئېلىپ كىرىپ ئۇلارنىڭ ئالدىغا قويۇپ: «يېمەمسىلەر» دېدى'),
(4703, 51, 28, '(ئۇلارنىڭ تاماق يېمىگەنلىكىنى كۆرۈپ) دىلىدا ئۇلاردىن قورقتى، ئۇلار «قورقمىغىن (بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىمىز)» دېدى، ئۇلار ئىبراھىمغا بىلىملىك بىر ئوغۇل بىلەن خۇش خەۋەر بەردى'),
(4704, 51, 29, 'ئىبراھىمنىڭ ئايالى سۈرەن سېلىپ كېلىپ (ئەجەبلەنگەنلكىدىن ئۆزىنىڭ) يۈزىنى كاچاتلاپ: «مەن تۇغماس موماي تۇرسام (قانداق تۇغىمەن؟)» دېدى'),
(4705, 51, 30, 'ئۇلار: «پەرۋەردىگارىڭ شۇنداق دېدى. ئۇ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر» دېدى'),
(4706, 51, 31, 'ئىبراھىم: «ئى ئەلچىلەر! سىلەر نېمە ئىش بىلەن ئەۋەتىلدىڭلار» دېدى'),
(4707, 51, 32, 'ئۇلار: «بىز ھەقىقەتەن بىر گۇناھكار قەۋمنى (ھالاك قىلىش ئۈچۈن) ئەۋەتىلدۇق (يەنى لۇت قەۋمىنىڭ ئۈستىگە لايدىن پىشۇرۇلغان تاش ياغدۇرۇش ئۈچۈن ئەۋەتىلدۇق)'),
(4708, 51, 33, 'ئۇلارنىڭ ئۈستىگە پەرۋەردىگارىڭنىڭ دەرگاھىدا (گۇناھى) ھەددىدىن ئاشقۇچىلار ئۈچۈن بەلگە قويۇلغان (يەنى ھالاك قىلىنغۇچىنىڭ ئىسمى يېزىلغان)، لايدىن پىشۇرۇلغان تاش ياغدۇرىمىز»'),
(4709, 51, 34, 'ئۇلارنىڭ ئۈستىگە پەرۋەردىگارىڭنىڭ دەرگاھىدا (گۇناھى) ھەددىدىن ئاشقۇچىلار ئۈچۈن بەلگە قويۇلغان (يەنى ھالاك قىلىنغۇچىنىڭ ئىسمى يېزىلغان)، لايدىن پىشۇرۇلغان تاش ياغدۇرىمىز»'),
(4710, 51, 35, '(ھالاك بولمىسۇن دەپ لۇتنىڭ) شەھىرىدىكى مۆمىنلەرنى (سىرتقا) چىقىرىۋەتتۇق'),
(4711, 51, 36, 'بىز ئۇ شەھەردىن مۇسۇلمانلاردىن پەقەت بىر ئائىلىنىلا تاپتۇق'),
(4712, 51, 37, 'ئۇ شەھەردە قاتتىق ئازابتىن قورقىدىغانلار ئۈچۈن نىشان قالدۇردۇق'),
(4713, 51, 38, 'مۇسانىڭ (قىسسىسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇنى بىز پىرئەۋنگە روشەن دەلىل بىلەن ئەۋەتتۇق'),
(4714, 51, 39, 'پىرئەۋن ئۆز قوشۇنى بىلەن (ئىماندىن) يۈز ئۆرىدى، ئۇ: «(مۇسا) سېھىرگەردۇر ياكى مەجنۇندۇر» دېدى'),
(4715, 51, 40, 'پىرئەۋننى ۋە ئۇنىڭ قوشۇنىنى جازالىدۇق، ئۇلارنى دېڭىزغا تاشلىدۇق، پىرئەۋن ئەيىبلەنگۈچىدۇر'),
(4716, 51, 41, 'ئادنىڭ (قىسسىسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇلارغا يامان بوراننى ئەۋەتتۇق'),
(4717, 51, 42, 'ئۇ (يەنى بوران) ھەر قانداق نەرسىنىڭ يېنىدىن ئۆتسە، ئۇنى چىرىگەن سۆڭەكتەك (تىتما - تالاڭ) قىلىۋېتەتتى'),
(4718, 51, 43, 'سەمۇدنىڭ (قىسسىسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇلارغا: «سىلەر ۋاقىتلىق پايدىلىنىڭلار» دېدۇق'),
(4719, 51, 44, 'ئۇلار پەرۋەردىگارىنىڭ ئەمرىگە قارشى چىقتى، ئۇلارنى چاقماق ھالاك قىلدى، ھالبۇكى، ئۇلار (ئازابنىڭ چۈشۈۋاتقانلىقىنى) كۆزلىرى بىلەن كۆرۈپ تۇراتتى'),
(4720, 51, 45, 'ئۇلار (ئازاب چۈشكەن ۋاقىتتا) ئورۇنلىرىدىن تۇرۇشقىمۇ، ئۆزلىرىنى قوغداشقىمۇ قادىر بولالمىدى'),
(4721, 51, 46, 'ئىلگىرى نۇھنىڭ قەۋمىنى ھالاك قىلدۇق، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى'),
(4722, 51, 47, 'ئاسماننى قۇدرەت بىلەن بەرپا قىلدۇق، بىز ھەقىقەتەن قۇدرەتلىكمىز'),
(4723, 51, 48, 'زېمىننى يايدۇق، بىز نېمىدېگەن ياخشى يايغۇچىمىز؟'),
(4724, 51, 49, 'سىلەر ئىبرەت ئېلىشىڭلار ئۈچۈن ھەر بىر نەرسىنى جۈپ ياراتتۇق'),
(4725, 51, 50, '(ئى مۇھەممەد!) ئېيتقىنكى، «اﷲ تەرەپكە قېچىڭلار (يەنى اﷲ قا ئىلتىجا قىلىڭلار)، مەن ھەقىقەتەن سىلەرگە اﷲ تەرىپىدىن (كەلگەن) ئوچۇق ئاگاھلاندۇرغۇچىمەن'),
(4726, 51, 51, 'سىلەر باشقا مەبۇدنى اﷲ قا شېرىك قىلماڭلار، مەن ھەقىقەتەن ئۇنىڭ تەرىپىدىن (كەلگەن) ئوچۇق ئاگاھلاندۇرغۇچىمەن»'),
(4727, 51, 52, 'ئۇلاردىن ئىلگىرىكى ئۈممەتلەرمۇ شۇنداق ئىدى، ئۇلارغا ھەر پەيغەمبەر كەلسىلا ئۇلار (ئۇنى): «بىر سېھرىگەر ياكى بىر مەجنۇن» دېدى'),
(4728, 51, 53, 'ئۇلار (پەيغەمبەرلەرنى ئىنكار قىلىشنى) بىر - بىرىگە تەۋسىيە قىلىشقانمۇ؟ ھەرگىز ئۇنداق ئەمەس (يەنى ئۇلار بىر - بىرىگە ئۇنداق تەۋسىيە قىلىشقان ئەمەس) ئۇلار ھەددىدىن ئاشقۇچى قەۋمدۇر'),
(4729, 51, 54, 'ئۇلاردىن يۈز ئۆرۈگىن، سەن مالامەت قىلىنغۇچى ئەمەسسەن'),
(4730, 51, 55, 'ۋەز - نەسىھەت قىلغىن، ۋەز - نەسىھەت مۆمىنلەرگە پايدىلىق'),
(4731, 51, 56, 'جىنلارنى، ئىنسانلارنى پەقەت ماڭا ئىبادەت قىلىش ئۈچۈنلا ياراتتىم'),
(4732, 51, 57, 'ئۇلاردىن مەن رىزىق تىلىمەيمەن ۋە ئۇلارنىڭ مېنى ئوزۇقلاندۇرۇشىنى تىلىمەيمەن'),
(4733, 51, 58, 'اﷲ ھەقىقەتەن ھەممىگە رىزىق بەرگۈچىدۇر، قۇدرەتلىكتۇر، (اﷲ نىڭ) قۇۋۋىتى ئارتۇقتۇر'),
(4734, 51, 59, '(ئۆزلىرىگە) زۇلۇم قىلغانلاردىن (ئازابتىن) ئۆتكەنكى (ھالاك بولغان) دوستلىرىنىڭ نېسىۋىسىدەك نېسىۋىسى بولىدۇ، بۇلار (ئازابىمغا) ئالدىراتمىسۇن'),
(4735, 51, 60, 'كاپىرلارغا ۋەدە قىلىنغان كۈندىن كاپىرلارغا ۋاي!');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'تۇر تېغى بىلەن قەسەمكى،'),
(4737, 52, 2, 'ئوچۇق قەغەزگە يېزىلغان كىتاب (يەنى قۇرئان) بىلەن قەسەمكى،'),
(4738, 52, 3, 'ئوچۇق قەغەزگە يېزىلغان كىتاب (يەنى قۇرئان) بىلەن قەسەمكى،'),
(4739, 52, 4, 'بەيتۇلمەئمۇر بىلەن قەسەمكى،'),
(4740, 52, 5, 'ئاسمان بىلەن قەسەمكى،'),
(4741, 52, 6, '(قىيامەت كۈنى) ئوت بولۇپ ياندۇرۇلغان دېڭىز بىلەن قەسەمكى،'),
(4742, 52, 7, 'پەرۋەردىگارىڭنىڭ ئازابى چوقۇم يۈز بەرگۈسىدۇر'),
(4743, 52, 8, 'ئۇنىڭغا ھېچ ئادەم قارشى تۇرالمايدۇ'),
(4744, 52, 9, 'ئۇ كۈندە ئاسمان قاتتىق تەۋرەيدۇ'),
(4745, 52, 10, 'تاغلار قاتتىق سەير قىلىدۇ (يەنى زېمىننىڭ ئۈستىدىن توزۇپ يوقىلىپ كېتىدۇ)'),
(4746, 52, 11, 'ئۇ كۈندە (پەيغەمبەرلەرنى) ئىنكار قىلغانلارغا ۋاي!'),
(4747, 52, 12, 'ئۇلار (دۇنيادا) غەپلەتكە چۆمگەنلەردۇر'),
(4748, 52, 13, 'ئۇ كۈندە ئۇلار دوزاخقا قاتتىق ئىتتىرىلىدۇ'),
(4749, 52, 14, '(ئۇلارغا) «مانا بۇ سىلەر يالغانغا چىقارغان دوزاختۇر» (دېيىلىدۇ)'),
(4750, 52, 15, '(ئۇلارغا) «(كۆزلىرىڭلار بىلەن كۆرۈپ تۇرۇۋاتقان بۇ ئازاب) سېھىرمۇ؟ ياكى سىلەر كۆرمەيۋاتامسىلەر'),
(4751, 52, 16, 'دوزاخقا كىرىڭلار، (ئازابقا) مەيلى سەۋر قىلىڭلار، مەيلى سەۋر قىلماڭلار، بەرىبىر سىلەرگە ئوخشاش (چۈنكى سىلەر دوزاختا مەڭگۈ قالىسىلەر)، سىلەر پەقەت قىلمىشىڭلارنىڭ جازاسىنى تارتىسىلەر» (دېيىلىدۇ)'),
(4752, 52, 17, 'تەقۋادارلار ھەقىقەتەن جەننەتلەردە ۋە نېمەت ئىچىدە بولىدۇ'),
(4753, 52, 18, 'ئۇلار پەرۋەردىگارى بەرگەن نەرسىلەر بىلەن ھۇزۇرلىنىدۇ، پەرۋەردىگارى ئۇلارنى دوزاخ ئازابىدىن ساقلايدۇ'),
(4754, 52, 19, '(ئۇلارغا) «قىلغان ئەمەللىرىڭلارنىڭ (مۇكاپاتى) ئۈچۈن كۆڭۈللۈك يەڭلار ۋە ئىچىڭلار» (دېيىلىدۇ)'),
(4755, 52, 20, 'ئۇلار قاتار تىزىلغان تەختلەر ئۈستىدە يۆلەنگەن ھالدا ئولتۇرىدۇ، شەھلا كۆزلۈك ھۈرلەرنى ئۇلارغا جۈپ قىلىپ بېرىمىز'),
(4756, 52, 21, 'ئۆزلىرى ئىمان ئېيتقان، ئەۋلادلىرىمۇ ئەگىشىپ ئىمان ئېيتقانلارنىڭ ئەۋلادلىرىنى ئۇلار بىلەن تەڭ دەرىجىدە قىلىمىز، ئۇلارنىڭ (ياخشى) ئەمەللىرىدىن قىلچىمۇ كېمەيتىۋەتمەيمىز، ھەر ئادەم ئۆزىنىڭ قىلمىشىغا مەسئۇلدۇر'),
(4757, 52, 22, 'ئۇلار كۆرۈۋاتقان نېمەتنىڭ ئۈستىگە يەنە (ئۇلارنى) كۆڭلى تارتقان مېۋە ۋە كۆڭلى تارتقان گۆش بىلەن تەمىنلەيمىز'),
(4758, 52, 23, 'ئۇلار جەننەتتە ئۆزئارا (مەي قاچىلانغان) جاملارنى تۇتىشىدۇ، (مەي ئىچىش بىلەن) ئۇلاردىن (بۇ دۇنيادىكىدەك) بىھۇدە سۆز ۋە گۇناھ سادىر بولمايدۇ'),
(4759, 52, 24, 'گويا سەدەپنىڭ ئىچىدىكى گۆھەردەك (چىرايلىق) غىلمانلار ئۇلارنى ئايلىنىپ يۈرۈپ (ئۇلارنىڭ) خىزمىتىنى قىلىدۇ'),
(4760, 52, 25, 'ئۇلار (يەنى ئەھلى جەننەت) بىر - بىرىگە قارىشىپ پاراڭ سېلىشىدۇ'),
(4761, 52, 26, 'ئۇلار ئېيتىدۇ: «بىز ھەقىقەتەن (دۇنيادىكى چاغدا) ئائىلىمىزدە (اﷲ نىڭ ئازابىدىن) قورقاتتۇق'),
(4762, 52, 27, 'اﷲ بىزگە مەرھەمەت قىلدى، بىزنى زەھەرلىك ئازابتىن ساقلىدى'),
(4763, 52, 28, 'بىز ھەقىقەتەن ئىلگىرى اﷲ قا ئىبادەت قىلاتتۇق، اﷲ ھەقىقەتەن ئېھسان قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر»'),
(4764, 52, 29, '(ئى مۇھەممەد! سەن قۇرئان بىلەن قەۋمىڭگە) ۋەز - نەسىھەت قىلغىن، سەن پەرۋەردىگارىڭنىڭ نېمىتى بىلەن كاھىنمۇ ئەمەسسەن ۋە مەجنۇنمۇ ئەمەسسەن'),
(4765, 52, 30, 'ئۇلار ئېيتىدۇ: «ئۇ شائىردۇر، بىز ئۇنىڭ زاماننىڭ ھادىسىلىرىگە (يولۇقۇشىنى) كۈتىمىز». (ئى مۇھەممەد!) ئېيتقىنكى، «سىلەر كۈتۈڭلار، ھەقىقەتەن مەن سىلەر بىلەن بىرگە كۈتكۈچىلەردىنمەن (يەنى سىلەر مېنىڭ ھالاك بولۇشۇمنى كۈتسەڭلار، مەنمۇ سىلەرنىڭ ھالاك بولۇشۇڭلارنى كۈتىمەن)»'),
(4766, 52, 31, 'ئۇلار ئېيتىدۇ: «ئۇ شائىردۇر، بىز ئۇنىڭ زاماننىڭ ھادىسىلىرىگە (يولۇقۇشىنى) كۈتىمىز». (ئى مۇھەممەد!) ئېيتقىنكى، «سىلەر كۈتۈڭلار، ھەقىقەتەن مەن سىلەر بىلەن بىرگە كۈتكۈچىلەردىنمەن (يەنى سىلەر مېنىڭ ھالاك بولۇشۇمنى كۈتسەڭلار، مەنمۇ سىلەرنىڭ ھالاك بولۇشۇڭلارنى كۈتىمەن)»'),
(4767, 52, 32, 'ئۇلارنىڭ ئەقلى ئۇلارنى مۇشۇنداق (سۆز) قىلىشقا بۇيرۇمدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلار (كۇفرىدا) ھەددىدىن ئاشقان قەۋمدۇر'),
(4768, 52, 33, 'ياكى ئۇلار: «(مۇھەممەد قۇرئاننى ئۆزى توقۇدى دېيىشەمدۇ؟ ئۇنداق ئەمەس، ئۇلار (قۇرئانغا) ئىشەنمەيدۇ'),
(4769, 52, 34, 'ئەگەر (مۇھەممەد ئۇنى توقۇدى دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، (تۈزۈلۈشتە، گۈزەللىكتە، باياندا) قۇرئانغا ئوخشايدىغان بىر سۆزنى كەلتۈرۈپ بېقىڭلار'),
(4770, 52, 35, 'ياكى ئۇلار ياراتقۇچىسىز يارىتىلغانمۇ؟ ياكى ئۇلار ئۆزلىرى ياراتقۇچىمۇ؟'),
(4771, 52, 36, 'ياكى ئۇلار ئاسمانلارنى ۋە زېمىننى ياراتقانمۇ؟ بەلكى ئۇلار (اﷲ نىڭ بىرلىكىگە) ئىشەنمەيدۇ'),
(4772, 52, 37, 'ئۇلارنىڭ يېنىدا پەرۋەردىگارىنىڭ خەزىنىلىرى بارمۇ؟ ياكى ئۇلار (شەيئىلەرنى خالىغانچە تەسەررۇپ قىلىدىغان) (كائىناتنى) بويسۇندۇرغۇچىمۇ؟'),
(4773, 52, 38, 'ياكى ئۇلارنىڭ (ئاسمانغا چىقىپ) سۆز تىڭشايدىغان شوتىسى بارمۇ؟ ئەگەر بولسا، ئۇلارنىڭ سۆز ئاڭلىغۇچىسى ئوچۇق بىر پاكىتىنى كەلتۈرسۇن'),
(4774, 52, 39, 'ياكى اﷲ نىڭ قىزلىرى، سىلەرنىڭ ئوغۇللىرىڭلار بارمۇ؟'),
(4775, 52, 40, 'ياكى سەن ئۇلاردىن (دەۋىتىڭ ئۈچۈن) ھەق سوراپ، شۇ زىياندىن (ئۇلارغا) ئېغىر يۈك ئارتىلىپ قېلىپ (ئىمان ئېيتمامدۇ؟)'),
(4776, 52, 41, 'ياكى ئۇلارنىڭ يېنىدا ئىلمى غەيب بولۇپ (ئۇنىڭدىن غەيب نەرسىلەرنى) يېزىۋالامدۇ؟'),
(4777, 52, 42, 'ياكى ئۇلار ساڭا ھىيلە - مىكىر ئىشلىتەمدۇ؟ كاپىرلارنىڭ ھىيلە - مىكىرلىرى مەغلۇپ بولىدۇ'),
(4778, 52, 43, 'ياكى ئۇلارنىڭ اﷲ تىن باشقا مەبۇدى بارمۇ؟ ئۇلارنىڭ شېرىك كەلتۈرگەن (بۇتلىرىدىن) اﷲ پاكتۇر'),
(4779, 52, 44, 'ئەگەر ئۇلار ئاسماننىڭ بىر پارچىسى چۈشۈپ كەتكەنلىكىنى كۆرسە: «بۇ توپلاشقان بۇلۇتتۇر» دەيدۇ'),
(4780, 52, 45, 'ئۇلارنى ھالاك قىلىدىغان كۈنگىچە تەرك ئەتكىن'),
(4781, 52, 46, 'ئۇ كۈندە ئۇلارنىڭ ھىيلە - مىكرى ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلارغا ياردەممۇ قىلىنمايدۇ'),
(4782, 52, 47, 'ئۇلار ئۇنىڭدىن (يەنى ئاخىرەتتىكى ئازابتىن) بۇرۇن (دۇنيادىمۇ بىر تۈرلۈك ئازابقا دۇچار بولىدۇ)، لېكىن ئۇلارنىڭ تولىسى (بۇنى) بىلمەيدۇ'),
(4783, 52, 48, 'پەرۋەردىگارىڭنىڭ (ئۇلارغا بولىدىغان ئازابنى تەخىر قىلىش (ھۆكمىگە سەۋر قىلغىن، سەن ھەقىقەتەن بىزنىڭ ھىمايىمىزدىدۇرسەن، (ئۇيقۇدىن) تۇرغان ۋاقتىڭدا پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن، ھەمدى ئېيتقىن'),
(4784, 52, 49, 'كېچىدىمۇ، يۇلتۇزلار پاتقاندىن كېيىنمۇ ئۇنىڭغا تەسبىھ ئېيتقىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'ساقىغان يۇلتۇزلار بىلەن قەسەمكى،'),
(4786, 53, 2, 'سىلەرنىڭ ھەمراھىڭلار (يەنى مۇھەممەد ئەلەيھىسسالام) گۇمراھ بولمىدى ۋە يولدىن ئاداشمىدى'),
(4787, 53, 3, '(ئۇ) ئۆز نەپسى خاھىشى بويىچە سۆزلىمەيدۇ'),
(4788, 53, 4, 'پەقەت ئۇنىڭغا نازىل قىلىنغان ۋەھيىنىلا سۆزلەيدۇ'),
(4789, 53, 5, 'ۋەھىينى ئۇنىڭغا كۈچلۈك پەرىشتە (جىبرىئىل) تەلىم بەردى'),
(4790, 53, 6, 'ئۇنىڭ (يەنى جىبرىئىلنىڭ) كۆرۈنۈشى چىرايلىق بولۇپ، يۇقىرى ئۇپۇقتا (ئۆز شەكلىدە) تۇردى'),
(4791, 53, 7, 'ئۇنىڭ (يەنى جىبرىئىلنىڭ) كۆرۈنۈشى چىرايلىق بولۇپ، يۇقىرى ئۇپۇقتا (ئۆز شەكلىدە) تۇردى'),
(4792, 53, 8, 'ئاندىن ئۇ ئاستا - ئاستا (مۇھەممەد ئەلەيھىسسالامغا) يېقىنلىشىپ تۆۋەنگە ساڭگىلىدى'),
(4793, 53, 9, 'ئۇ (پەيغەمبەر ئەلەيھىسسالامغا) ئىككى ياچاق مىقدارى ياكى ئۇنىڭدىنمۇ يېقىنراق يېقىنلاشتى'),
(4794, 53, 10, 'اﷲ بەندىسىگە (جىبرىئىل ئارقىلىق تېگىشلىك) ۋەھيىلەرنى ۋەھىي قىلدى'),
(4795, 53, 11, 'ئۇنىڭ كۆرگىنىنى دىلى ئىنكار قىلمىدى (يەنى راست كۆردى)'),
(4796, 53, 12, '(ئى مۇشرىكلار جامائەسى!) ئۇنىڭ كۆرگەنلىرى ئۈستىدە ئۇنىڭ بىلەن مۇنازىرە قىلىشامسىلەر؟'),
(4797, 53, 13, 'ئۇ جىبرىئىلنى ھەقىقەتەن ئىككىنچى قېتىم سىدرەتۇلمۇنتەھا (دەرىخىنىڭ) يېنىدا كۆردى'),
(4798, 53, 14, 'ئۇ جىبرىئىلنى ھەقىقەتەن ئىككىنچى قېتىم سىدرەتۇلمۇنتەھا (دەرىخىنىڭ) يېنىدا كۆردى'),
(4799, 53, 15, 'جەننەتۇلمەئۋا بولسا سىدرەتۇلمۇنتەھانىڭ يېنىدىدۇر'),
(4800, 53, 16, '(ئۇ) سىدرەتۇلمۇنتەھانى (ئالەملەرنىڭ پەرۋەردىگارىنىڭ نۇرى) قاپلىغان چاغدا (كۆردى)'),
(4801, 53, 17, '(پەيغەمبەر) نىڭ كۆزى (ئوڭ - سولغا) بۇرالمىدى، (كۆزلەنگەن نەرسىدىن) ئۆتۈپ كەتمىدى'),
(4802, 53, 18, 'شۈبھىسىزكى، ئۇ پەرۋەردىگارىڭنىڭ (قۇدرىتىنى كۆرسىتىدىغان بۈيۈك ئالامەتلەرنى) كۆردى'),
(4803, 53, 19, 'ئېيتىپ بېقىڭلارچۇ! لات، ئۇززا ۋە ئۈچىنچىسى بولغان ماناتلار (اﷲ تائالادەك كۈچ - قۇۋۋەتكە ئىگىمۇ؟)'),
(4804, 53, 20, 'ئېيتىپ بېقىڭلارچۇ! لات، ئۇززا ۋە ئۈچىنچىسى بولغان ماناتلار (اﷲ تائالادەك كۈچ - قۇۋۋەتكە ئىگىمۇ؟)'),
(4805, 53, 21, '(ئى مۇشرىكلار جامائەسى!) (سىلەرچە ياخشى ھېسابلانغان) ئوغۇل بالا سىلەرگە خاس بولۇپ، (سىلەرچە يامان ھېسابلانغان) قىز بالا اﷲ قا خاسمۇ؟'),
(4806, 53, 22, 'ئۇنداقتا بۇ ئادالەتسىز تەقسىماتتۇر'),
(4807, 53, 23, 'بۇ بۇتلار پەقەت سىلەر ۋە ئاتا - بوۋاڭلار ئاتىۋالغان قۇرۇق ناملاردىنلا ئىبارەت، اﷲ بۇنى ئىسپاتلايدىغان ھېچبىر دەلىل - پاكىت چۈشۈرگىنى يوق، ئۇلارغا پەرۋەردىگارى تەرىپىدىن شەك - شۈبھىسىز ھىدايەت (يەنى پەيغەمبەر ۋە قۇرئان) كەلگەن تۇرۇقلۇق، ئۇلار پەقەت گۇمانغىلا ۋە نەپسىلىرىنىڭ خاھىشىغىلا ئەگىشىدۇ'),
(4808, 53, 24, 'ئىنسان نېمىنى ئارزۇ قىلسا شۇ بولامدۇ'),
(4809, 53, 25, 'ئاخىرەت ۋە دۇنيا اﷲ نىڭ ئىلكىدىدۇر (يەنى دۇنيا ۋە ئاخىرەتتىكى ئىشلارنىڭ ھەممىسى اﷲ نىڭ خاھىشى بويىچە بولىدۇ)'),
(4810, 53, 26, 'ئاسمانلاردا نۇرغۇن پەرىشتىلەر بولۇپ، ئۇلارنىڭ شاپائىتى ھېچ نەرسىگە ئەسقاتمايدۇ، پەقەت اﷲ ئۆزى خالىغان ۋە رازى بولغان ئادەمگە شاپائەت قىلىشقا رۇخسەت قىلسىلا (ئاندىن) ئۇلارنىڭ شاپائىتى ئەسقاتىدۇ'),
(4811, 53, 27, 'شۈبھىسىزكى، ئاخىرەتكە ئىمان ئېيتمايدىغانلار پەرىشتىلەرنى (اﷲ نىڭ قىزلىرى دەپ) چىشى جىنس نامى بىلەن ئاتايدۇ'),
(4812, 53, 28, 'ئۇلار مۇنداق ئاتاشتا ھېچقانداق ئىلىمگە ئىگە ئەمەس، ئۇلار پەقەت گۇمانغىلا ئاساسلىنىدۇ، گۇمان دېگەن ھەقنى ئىسپاتلاشتا ھېچ نەرسىگە يارىمايدۇ'),
(4813, 53, 29, 'بىزنىڭ زىكرىمىز (يەنى ئىمان بىلەن قۇرئان) دىن باش تارتقان، پەقەت دۇنيا تىرىكچىلىكنىلا كۆزلىگەنلەردىن يۈز ئۆرۈگىن'),
(4814, 53, 30, 'ئۇلارنىڭ ئىلىمدىن يەتكەن يېرى ئەنە شۇ، شۈبھىسىزكى، پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغان ئادەمنى ئوبدان بىلىدۇ، ھىدايەت تاپقان ئادەمنىمۇ ئوبدان بىلىدۇ'),
(4815, 53, 31, 'ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭ مۈلكىدۇر، (اﷲ) يامانلىق قىلغانلارنى قىلمىشلىرىغا قاراپ جازالايدۇ، ياخشىلىق قىلغانلار چىرايلىق مۇكاپات بېرىدۇ'),
(4816, 53, 32, 'ئۇلار چوڭ گۇناھلاردىن ۋە قەبىھ ئىشلاردىن يىراق بولىدۇ. كىچىك گۇناھلار بۇنىڭدىن مۇستەسنا، ھەقىقەتەن پەرۋەردىگارىڭنىڭ مەغپىرىتى كەڭدۇر، اﷲ سىلەرنى زېمىندىن ياراتقان چاغدىكى (ۋاقتىڭلاردىن تارتىپ، ئاناڭلارنىڭ قورساقلىرىدىكى بالا) ۋاقتىڭلاردىن تارتىپ ئوبدان بىلىدۇ، شۇنىڭ ئۈچۈن سىلەر ئۆزۈڭلارنى پاك ھېسابلىماڭلار، اﷲ تەقۋادار بولغان ئادەمنى ئوبدان بىلىدۇ'),
(4817, 53, 33, '(ئى مۇھەممەد!) ئىماندىن يۈز ئۆرۈگەن ئادەمدىن خەۋەر بەرگىن'),
(4818, 53, 34, 'ئۇ (شەرت قىلىنغان مالدىن) ئازغىنا بەردى، قالغىنىنى بەرمىدى'),
(4819, 53, 35, 'ئۇنىڭ دەرگاھىدا ئىلمىي غەيب بولۇپ، ئۇ غەيبنى بىلەمدۇ؟'),
(4820, 53, 36, 'ئۇ مۇسانىڭ ۋە ئىبراھىمنىڭ سەھىپىلىرىدىكى سۆزدىن خەۋەردار بولمىدىمۇ؟'),
(4821, 53, 37, 'ئىبراھىم ۋاپادار كىشى ئىدى، بىر گۇناھكار ئادەم يەنە بىراۋنىڭ گۇناھىنى كۆتەرمەيدۇ (يەنى بىراۋ باشقا بىراۋنىڭ گۇناھى تۈپەيلىدىن جازاغا تارتىلمايدۇ)'),
(4822, 53, 38, 'ئىبراھىم ۋاپادار كىشى ئىدى، بىر گۇناھكار ئادەم يەنە بىراۋنىڭ گۇناھىنى كۆتەرمەيدۇ (يەنى بىراۋ باشقا بىراۋنىڭ گۇناھى تۈپەيلىدىن جازاغا تارتىلمايدۇ)'),
(4823, 53, 39, 'ئىنسان پەقەت ئۆزىنىڭ ئىشلىگەن ئىشىنىڭ نەتىجىسىنى كۆرىدۇ'),
(4824, 53, 40, 'ئۇنىڭ قىلغان ئىشى كەلگۈسىدە كۆرۈلىدۇ (يەنى ئۇنىڭ ئەمەلى قىيامەت كۈنى ئۇنىڭغا توغرىلىنىدۇ)'),
(4825, 53, 41, 'ئاندىن ئۇنىڭغا تولۇق مۇكاپات (ياكى) تولۇق جازا بېرىلىدۇ'),
(4826, 53, 42, 'ھەممىنىڭ ئاخىر بارىدىغان جايى پەرۋەردىگارىڭنىڭ دەرگاھىدۇر'),
(4827, 53, 43, 'اﷲ ئىنساننى كۈلدۈرەلەيدۇ ۋە يىغلىتالايدۇ'),
(4828, 53, 44, 'اﷲ ئۆلتۈرۈشكە ۋە تىرىلدۈرۈشكە قادىر'),
(4829, 53, 45, 'اﷲ بىر جۈپنى - ئەركەك بىلەن چىشىنى ياراتتى'),
(4830, 53, 46, 'ئېتىلىپ چىققان مەنىدىن (ياراتتى)'),
(4831, 53, 47, '(ھېساب ئۈچۈن ئىنسانلارنى) ئىككىنچى قېتىم يارىتىشنى ئۈستىگە ئالدى'),
(4832, 53, 48, 'اﷲ ئىنساننى باي قىلالايدۇ ۋە مەمنۇن قىلالايدۇ'),
(4833, 53, 49, 'اﷲ شىئرا يۇلتۇزىنىڭ پەرۋەردىگارىدۇر'),
(4834, 53, 50, 'ئۇ (يەنى اﷲ) قەدىمكى ئادنى ھالاك قىلدى'),
(4835, 53, 51, 'سەمۇدنىمۇ (ھالاك قىلدى، ئۇلاردىن بىرىنىمۇ) قويمىدى'),
(4836, 53, 52, 'ئىلگىرى نۇھنىڭ قەۋمىنى (ھالاك قىلغان ئىدى)، شۈبھىسىزكى، ئۇلار تېخىمۇ زالىم، تېخىمۇ سەركەش ئىدى'),
(4837, 53, 53, 'لۇد قەۋمىنىڭ شەھەرلىرىنى دۈم كۆمتۈرىۋەتتى، ئۇنى (دەھشەتلىك تاشلار) قاپلىۋالدى'),
(4838, 53, 54, 'لۇد قەۋمىنىڭ شەھەرلىرىنى دۈم كۆمتۈرىۋەتتى، ئۇنى (دەھشەتلىك تاشلار) قاپلىۋالدى'),
(4839, 53, 55, '(ئى ئىنسان!) پەرۋەردىگارىڭنىڭ قايسىبىر نېمىتىدىن گۇمانلىنىسەن؟'),
(4840, 53, 56, 'بۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئىلگىرىكى ئاگاھلاندۇرغۇچىلارنىڭ جۈملىسىدىن بولغان بىر ئاگاھلاندۇرغۇچىدۇر'),
(4841, 53, 57, 'قىيامەت يېقىنلاشتى'),
(4842, 53, 58, 'ئۇنىڭغا اﷲ تىن باشقا كەشىپ قىلغۇچى (يەنى ئۇنىڭ ۋاقتىنى بىلگۈچى ياكى ئۇنىڭ ئازابىنى دەپئى قىلغۇچى يوقتۇر'),
(4843, 53, 59, '(ئى مۇشرىكلار جامائەسى!) مەسخىرە قىلىپ بۇ قۇرئاندىن ئەجەبلىنەمسىلەر؟'),
(4844, 53, 60, '(ئۇنى ئاڭلىغان چاغدا) كۆلەمسىلەر؟ يىغلامسىلەر؟'),
(4845, 53, 61, 'سىلەر غاپىلدۇرسىلەر'),
(4846, 53, 62, 'اﷲ قا سەجدە قىلىڭلار ۋە (ئۇنىڭغا) ئىبادەت قىلىڭلار');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قىيامەت يېقىنلاشتى، ئاي يېرىلدى'),
(4848, 54, 2, '(قۇرەيش كۇففارلىرى) بىرەر مۆجىزىنى كۆرسىلا (ئىماندىن) يۈز ئۆرۈپ، بۇ «داۋاملاشقۇچى سېھىردۇر» دېيىشىدۇ'),
(4849, 54, 3, 'ئۇلار (پەيغەمبەر ئەلەيھىسسالامنى) ئىنكار قىلدى، نەپسى خاھىشلىرىغا ئەگەشتى، (ياخشىلىقتىن يا يامانلىقتىن بولسۇن) ھەر ئىش (شۇ ئىشنىڭ ئەھلىنى جەننەتكە يا دوزاخقا) ئورۇنلاشتۇرغۇچىدۇر'),
(4850, 54, 4, 'ئۇلار ھەقىقەتەن (قۇرئان) خەۋەرلىرىدىن ئىبرەت ئېلىنىدىغان مىقداردا نەرسە كەلدى'),
(4851, 54, 5, 'مۇكەممەل ھېكمەت كەلدى، (اﷲ ئۇلارنى شەقى قىلغانلىقى ئۈچۈن) ئۇلارغا ئاگاھلاندۇرۇشلارنىڭ پايدىسى بولمايدۇ'),
(4852, 54, 6, 'ئۇلاردىن يۈز ئۆرۈگىن، ئۇ كۈندە چاقىرغۇچى (يەنى ئىسراپىل) قىيىن ئىشقا (يەنى ھېسابقا) چاقىرىدۇ (يەنى سۇر چالىدۇ)'),
(4853, 54, 7, 'ئۇلار (قورقۇنچتىن) تىكىلىپ قارىيالمىغان ھالدا گويا تارىلىپ كەتكەن چېكەتكىدەك، قەبرىلىرىدىن چىقىپ كېلىدۇ'),
(4854, 54, 8, 'ئۇلار چاقىرغۇچىنىڭ ئالدىغا بويۇنلىرىنى سوزغان ھالدا يۈگرەيدۇ، كاپىرلار: «بۇ قىيىن كۈندۇر» دەيدۇ'),
(4855, 54, 9, 'ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئىنكار قىلدى، ئۇلار بەندىمىزنى (يەنى نۇھنى) ئىنكار قىلىپ، مەجنۇن دېدى. ئۇ (دىنغا دەۋەت قىلىشتىن) ھەيۋە بىلەن مەنئى قىلىندى'),
(4856, 54, 10, 'ئۇ پەرۋەردىگارىڭغا: «ھەقىقەتەن مەن بوزەك قىلىندىم، ياردەم قىلغىن» دەپ دۇئا قىلدى'),
(4857, 54, 11, 'ئاسماننىڭ دەرۋازىلىرىنى قويۇلۇپ ياغقۇچى يامغۇر بىلەن ئېچىۋەتتۇق'),
(4858, 54, 12, 'زېمىندىن بۇلاقلارنى ئېتىلدۇرۇپ چىقاردۇق، (اﷲ تەقدىر قىلغان ئۇلارنى غەرق قىلىپ ھالاك قىلىش) ئىشىغا بىنائەن، يامغۇر سۈيى بىلەن بۇلاق سۈيى (بىر - بىرىگە) قوشۇلدى'),
(4859, 54, 13, 'نۇھنى تاختا ۋە مىخلار بىلەن ياسالغان كېمىگە سالدۇق'),
(4860, 54, 14, 'كېمە بىزنىڭ ھىمايىمىز ئاستىدا ماڭاتتى، ئىنكار قىلىنغاننى (يەنى نۇھنى) مۇكاپاتلاش ئۈچۈن (نۇھنىڭ قەۋمىنى غەرق قىلدۇق)'),
(4861, 54, 15, 'ئۇنى (يەنى توپان بالاسىنى) ئىبرەت قىلىپ قالدۇردۇق، ئىبرەت ئالغۇچى بارمۇ؟'),
(4862, 54, 16, 'مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!'),
(4863, 54, 17, 'ھەقىقەتەن بىز قۇرئاننى ھىپزى ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟'),
(4864, 54, 18, 'ئاد قەۋمى (پەيغەمبىرى ھۇدنى) ئىنكار قىلدى، مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!'),
(4865, 54, 19, 'شۈبھىسىزكى، ئۇلارنى بىز شۇملۇقى ئۈزۈلمەيدىغان شۇم كۈندە سوغۇق بوران ئەۋەتىپ (ھالاك قىلدۇق)، بوران كىشىلەرنى (ئورۇنلىرىدىن يۇلۇپ كېتەتتى، گويا ئۇلار قومۇرىۋېتىلگەن خورما كۆتەكلىرىدەك ياتاتتى'),
(4866, 54, 20, 'شۈبھىسىزكى، ئۇلارنى بىز شۇملۇقى ئۈزۈلمەيدىغان شۇم كۈندە سوغۇق بوران ئەۋەتىپ (ھالاك قىلدۇق)، بوران كىشىلەرنى (ئورۇنلىرىدىن يۇلۇپ كېتەتتى، گويا ئۇلار قومۇرىۋېتىلگەن خورما كۆتەكلىرىدەك ياتاتتى'),
(4867, 54, 21, 'مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!'),
(4868, 54, 22, 'بىز ھەقىقەتەن قۇرئاننى ھىپزى ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟'),
(4869, 54, 23, 'سەمۇد ئاگاھلاندۇرغۇچىلارنى (يەنى پەيغەمبەرلەرنى) يالغانغا چىقاردى'),
(4870, 54, 24, 'ئۇلار ئېيتتى: «ئارىمىزدىكى (بىزگە ئوخشاش) بىر ئادەمگە ئەگىشەمدۇق؟ (ئۇنداق قىلساق) ئۇ چاغدا بىز ھەقىقەتەن گۇمراھلىققا ۋە ساراڭلىققا مۇپتىلا بولىمىز'),
(4871, 54, 25, 'ئارىمىزدىن ئۇنىڭغا ۋەھيى نازىل قىلىندىمۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇ يالغانچىدۇر، مۇتەكەببىردۇر»'),
(4872, 54, 26, 'ئۇلار ئەتە (يەنى ئاخىرەتتە) كىمنىڭ يالغانچى، مۇتەكەببىر ئىكەنلىكىنى بىلىدۇ'),
(4873, 54, 27, 'بىز ھەقىقەتەن سىناش ئۈچۈن ئۇلارغا چىشى تۆگىنى ئەۋەتىمىز، (ئى سالىھ) ئۇلارنى (ئۇلارنىڭ نېمە قىلارىنى) كۈتكىن، (ئۇلارنىڭ ئەزىيەتلىرىگە) سەۋر قىلغىن'),
(4874, 54, 28, 'سۇنىڭ ئۇلار بىلەن تۆگىنىڭ ئارىسىدا بۆلۈنگەنلىكىنى، قايسىسىنىڭ نۆۋىتى كەلسە شۇنىڭ ھازىر بولىدىغانلىقىنى ئۇلارغا ئۇقتۇرۇپ قويغىن'),
(4875, 54, 29, 'ئۇلار بۇرادىرىنى (تۆگىنى ئۆلتۈرۈشكە) چاقىردى. ئۇ قىلىچنى ئېلىپ تۆگىنى بوغۇزلىدى'),
(4876, 54, 30, 'مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!'),
(4877, 54, 31, 'شۈبھىسىزكى، بىز ئۇلارغا قاتتىق بىر ئاۋازنى ئەۋەتىپ (ھالاك قىلىش بىلەن) ئۇلارنى قوتان ياسىغۇچىنىڭ تاشلاندۇق شاخ - شۇمبىلىرىدەك قىلىۋەتتۇق'),
(4878, 54, 32, 'بىز ھەقىقەتەن قۇرئاننى ھىپزى ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟'),
(4879, 54, 33, 'لۇتنىڭ قەۋمى ئاگاھلاندۇرۇشلارغا چىنپۈتمىدى'),
(4880, 54, 34, 'بىز ھەقىقەتەن ئۇلارنى تاش ياغدۇرۇپ (ھالاك قىلدۇق)، پەقەت لۇتنىڭ تەۋەلىرى بۇنىڭ سىرتىدىدۇر. ئۇلارنى نېمىتىمىز يۈزىسىدىن سەھەردە قۇتۇلدۇردۇق، شۈكۈر قىلغۇچىنى بىز مۇشۇنداق مۇكاپاتلايمىز'),
(4881, 54, 35, 'بىز ھەقىقەتەن ئۇلارنى تاش ياغدۇرۇپ (ھالاك قىلدۇق)، پەقەت لۇتنىڭ تەۋەلىرى بۇنىڭ سىرتىدىدۇر. ئۇلارنى نېمىتىمىز يۈزىسىدىن سەھەردە قۇتۇلدۇردۇق، شۈكۈر قىلغۇچىنى بىز مۇشۇنداق مۇكاپاتلايمىز'),
(4882, 54, 36, 'شۈبھىسىزكى، لۇت ئۇلارنى بىزنىڭ جازالىشىمىزدىن ئاگاھلاندۇردى، ئۇلار ئاگاھلاندۇرۇشلاردىن شەكلەندى'),
(4883, 54, 37, 'ئۇلار لۇتتىن مېھمانلىرىنى (يەنى ئادەمزات شەكلىدە كەلگەن پەرىشتىلەرنى) (ئۇلار بىلەن لىۋاتە قىلىش ئۈچۈن) قوغدىماسلىقنى سورىدى. ئۇلارنىڭ كۆزلىرىنى كور قىلىۋەتتۇق، (ئۇلارغا) «ئازابىمنى ۋە ئاگاھلاندۇرۇشلىرىمنى تېتىڭلار» (دېدۇق)'),
(4884, 54, 38, 'ھەقىقەتەن ئۇلارغا ئەتىگەندە دائىمىي (يەنى ئاخىرەتنىڭ ئازابىغا ئۇلىنىپ كېتىدىغان) ئازاب نازىل بولدى'),
(4885, 54, 39, '(ئۇلارغا): «مېنىڭ ئازابىمنى ۋە ئاگاھلاندۇرۇشلىرىمنى تېتىڭلار» (دېيىلدى)'),
(4886, 54, 40, 'بىز ھەقىقەتەن قۇرئاننى ھىپزى ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟'),
(4887, 54, 41, 'شەك - شۈبھىسىزكى، پىرئەۋن جامائەسىگە ئاگاھلاندۇرۇشلار كەلدى'),
(4888, 54, 42, 'ئۇلار بىزنىڭ پۈتۈن ئايەتلىرىمىزنى يالغانغا چىقاردى، ئۇلارنى غالىب، قۇدرەتلىك ھالەتتە تۇرۇپ ھالاك قىلدۇق'),
(4889, 54, 43, '(ئى ئەرەب جامائەسى!) ئۇلار (يەنى مەن ھالاك قىلغان ئۆتكەنكى ئۈممەتلەر) دىن سىلەرنىڭ كۇففارلىرىڭلار ئارتۇقمۇ؟ ياكى سىلەرگە ساماۋى كىتابلاردا (اﷲ نىڭ ئازابىدىن) كەچۈرۈم قىلىنىش بارمۇ؟'),
(4890, 54, 44, 'ياكى ئۇلار «بىز (مۇھەممەد ئۈستىدىن) غالىب قەۋممىز» دەمدۇ؟'),
(4891, 54, 45, '(مۇشرىكلار) توپى مەغلۇپ قىلىنىدۇ، ئارقىغا قاراپ قاچىدۇ'),
(4892, 54, 46, 'ھەرگىز ئۇنداق ئەمەس (يەنى ئۇلارغا بېرىلىدىغان ئازاب بۇلا ئەمەس)، قىيامەت ئۇلارغا (ئازاب قىلىنىش) ۋەدە قىلىنغان ۋاقىتتۇر، قىيامەت تېخىمۇ قىيىندۇر، تېخىمۇ جاپالىقتۇر'),
(4893, 54, 47, 'گۇناھكارلار ھەقىقەتەن (دۇنيادا) گۇمراھلىقتىدۇر، (ئاخىرەتتە) دوزاختىدۇر'),
(4894, 54, 48, 'ئۇلار دوزاختا دۈم ياتقۇزۇلۇپ سورىلىدىغان كۈندە، (ئۇلارغا) «دوزاخنىڭ ئازابىنى تېتىڭلار» (دېيىلىدۇ)'),
(4895, 54, 49, 'بىز ھەقىقەتەن ھەممە نەرسىنى ئۆلچەملىك ياراتتۇق'),
(4896, 54, 50, '(بىرەر شەيئىنى ياراتماقچى بولساق) بىزنىڭ ئەمرىمىز پەقەت بىر سۆزدۇر، (ئۇ) كۆزنى يۇمۇپ ئاچقاننىڭ ئارىلىقىدا (ئورۇنلىنىدۇ)'),
(4897, 54, 51, 'بىز ھەقىقەتەن (ئۆتكەنكى ئۈممەتلەردىن كۇفرىدا) سىلەرگە ئوخشاش بولغانلارنى ھالاك قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟'),
(4898, 54, 52, 'ئۇلارنىڭ قىلغان (ياخشى ۋە يامان) ئىشلىرىنىڭ ھەممىسى نامە - ئەماللىرىدا مەۋجۇتتۇر'),
(4899, 54, 53, 'كىچىك ئىش، چوڭ ئىشنىڭ ھەممىسى (لەۋھۇلمەھپۇزدا) خاتىرىلەنگەندۇر'),
(4900, 54, 54, 'شۈبھىسىزكى، تەقۋادارلار جەننەتلەردە بولىدۇ، (سۇ، مەي، ھەسەل، سۈت) ئۆستەڭلىرىدىن بەھرىمەن بولىدۇ'),
(4901, 54, 55, '(ئۇلار) قۇدرەتلىك اﷲ نىڭ دەرگاھىدا كۆڭۈلدىكىدەك جايدا بولىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'مېھرىبان اﷲ'),
(4903, 55, 2, 'قۇرئاننى تەلىم بەردى (ئۇنى ھىپزى قىلىشنى ۋە چۈشىنىشنى ئاسانلاشتۇرۇپ بەردى)،'),
(4904, 55, 3, 'ئىنساننى ياراتتى'),
(4905, 55, 4, 'ئۇنىڭغا (مەقسىتىنى ئۇقتۇرۇش ئۈچۈن) سۆزلەشنى ئۆگەتتى'),
(4906, 55, 5, 'كۈن بىلەن ئاي (ئۆز بۇرجلىرىدا) مۇئەييەن ھېساب بويىچە سەير قىلىدۇ'),
(4907, 55, 6, 'ئوت - چۆپلەر، دەل - دەرەخلەر سەجدە قىلىدۇ (يەنى اﷲ نىڭ ئىرادىسىگە بويسۇنىدۇ)'),
(4908, 55, 7, 'اﷲ ئاسماننى ئېگىز ياراتتى. ئۆلچەمدە زۇلۇم قىلماسلىقىڭلار ئۈچۈن، اﷲ تارازىنى بېكىتتى'),
(4909, 55, 8, 'اﷲ ئاسماننى ئېگىز ياراتتى. ئۆلچەمدە زۇلۇم قىلماسلىقىڭلار ئۈچۈن، اﷲ تارازىنى بېكىتتى،'),
(4910, 55, 9, 'ئۆلچەمدە ئادىل بۇلۇڭلار، تارازىدا كەم بەرمەڭلار'),
(4911, 55, 10, 'زېمىننى كىشىلەرنىڭ (پايدىلىنىشى) ئۈچۈن (يايپاڭ) قىلدى'),
(4912, 55, 11, 'زېمىندا مېۋىلەر، بوغۇنلۇق خورمىلار بار'),
(4913, 55, 12, 'سامىنى بار دانلىق زىرائەتلەر، خۇش بۇي ئۆسۈملۈكلەر بار'),
(4914, 55, 13, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4915, 55, 14, '(اﷲ) ئىنساننى (يەنى ئاتاڭلار ئادەمنى) (چەكسە جاراڭلايدىغان) ساپالدەك قۇرۇق لايدىن ياراتتى'),
(4916, 55, 15, 'جىنلارنى ئوتنىڭ يالقۇنىدىن ياراتتى'),
(4917, 55, 16, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4918, 55, 17, 'ئۇ ئىككى مەشرىق (يەنى كۈن بىلەن ئاينىڭ پاتىدىغان ئىككى جايى) نىڭ پەرۋەردىگارىدۇر'),
(4919, 55, 18, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4920, 55, 19, 'ئۇ ئىككى دېڭىزنى (بىرى تاتلىق، بىرى ئاچچىق) ئاققۇزدى، ئۇلار ئۇچرىشىدۇ'),
(4921, 55, 20, 'ئۇلارنىڭ ئارىسىدا توسما بولۇپ، بىر - بىرىگە قوشۇلۇپ كەتمەيدۇ'),
(4922, 55, 21, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4923, 55, 22, 'ئۇلاردىن مەرۋايىت بىلەن مارجان چىقىدۇ'),
(4924, 55, 23, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4925, 55, 24, 'دېڭىزلاردا قاتناۋاتقان تاغدەك كېمىلەر اﷲ نىڭ ئىلكىدىدۇر'),
(4926, 55, 25, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4927, 55, 26, 'زېمىننىڭ ئۈستىدىكى ھەممە يوقىلىدۇ'),
(4928, 55, 27, 'ئەزىمەتلىك ۋە كەرەملىك پەرۋەردىگارىڭنىڭ زاتى مەڭگۈ قالىدۇ'),
(4929, 55, 28, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4930, 55, 29, 'ئاسمانلاردىكى ۋە زېمىندىكى ھەممە اﷲ تىن (مەدەت ۋە رىزىق) تىلەيدۇ (يەنى ھەممە اﷲ قا موھتاجدۇر)، اﷲ ھەر كۈنى بىر ھالەتتىدۇر (يەنى اﷲ ھەر ۋاقىت ۋۇجۇدقا چىقىرىدىغان ئىشلار بىلەن بولۇپ، ئۇلارنى يارىتىپ تۇرىدۇ، ئەھۋاللارنى يېڭىلاپ تۇرىدۇ، مەخلۇقاتلارنى پەيدا قىلىپ تۇرىدۇ)'),
(4931, 55, 30, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4932, 55, 31, 'ئى ئىنسانلار ۋە جىنلار جامائەسى! سىلەردىن (يەنى سىلەرنىڭ ئەمەللىرىڭلاردىن) ھېساب ئېلىشقا يۈزلىنىمىز'),
(4933, 55, 32, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4934, 55, 33, 'ئى جىنلار ۋە ئىنسانلار جامائەسى! ئەگەر سىلەر (اﷲ نىڭ قازاسىدىن قېچىپ) ئاسمانلارنىڭ ۋە زېمىننىڭ چېگرىلىرىدىن ئۆتۈپ كېتەلىسەڭلار، ئۆتۈپ كېتىڭلار، سىلەر پەقەت قۇۋۋەت بىلەن ئۆتۈپ كېتەلەيسىلەر (لېكىن سىلەرگە مۇنداق قۇۋۋەت قەيەردىن كەلسۇن)'),
(4935, 55, 34, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4936, 55, 35, '(قىيامەت كۈنى) سىلەرگە قىزىق ئوتنىڭ يالقۇنى ۋە تۈتۈنى ئەۋەتىلىدۇ، سىلەر ئۆزۈڭلارنى قوغدىيالمايسىلەر'),
(4937, 55, 36, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4938, 55, 37, '(قىيامەت كۈنى پەرىشتىلەرنىڭ چۈشۈشى ئۈچۈن) ئاسمان يېرىلغان چاغدا ئاسمان (دوزاخنىڭ قىزىقلىقىدىن) قىزىل گۈلدەك، قىزىل چەمدەك بولۇپ قالىدۇ'),
(4939, 55, 38, '(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4940, 55, 39, 'بۇ كۈندە ھېچبىر ئىنسان ۋە جىن ئۆزىنىڭ گۇناھىدىن سورالمايدۇ (يەنى گۇناھلىرىنىڭ ئالاھىدە ئالامىتى بولغانلىقتىن مۇنداق سوراشقا ھاجەت بولمايدۇ'),
(4941, 55, 40, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4942, 55, 41, 'گۇناھكارلار ئۆزلىرىنىڭ (يۈزلىرى قارا، كۆزلىرى كۆك بولۇشتىن ئىبارەت) بەلگىلىرىدىن تونۇلىدۇ، ئۇلار كوكۇلىلىرىدىن ۋە پۇتلىرىدىن تۇتۇلۇپ (دوزاخقا تاشلىنىدۇ)'),
(4943, 55, 42, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4944, 55, 43, 'بۇ، گۇناھكارلار ئىنكار قىلىدىغان جەھەننەمدۇر'),
(4945, 55, 44, 'ئۇلار دوزاخ بىلەن قايناقسۇ ئارىسىدا بېرىپ - كېلىپ تۇرىدۇ (يەنى گاھىدا ئوت بىلەن، گاھىدا قايناقسۇ بىلەن ئازابلىنىدۇ'),
(4946, 55, 45, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4947, 55, 46, 'پەرۋەردىگارىڭنىڭ ئالدىدا تۇرۇپ (ھېساب بېرىشتىن) قورققان ئادەمگە ئىككى جەننەت بار'),
(4948, 55, 47, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4949, 55, 48, 'ئۇ ئىككى جەننەتتە تۈرلۈك مېۋىلىك دەرەخلەر بار'),
(4950, 55, 49, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4951, 55, 50, 'ئۇ ئىككى جەننەتتە ئېقىپ تۇرغان ئىككى بۇلاق بار'),
(4952, 55, 51, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4953, 55, 52, 'ئۇ ئىككى جەننەتتە ھەر مېۋىدىن ئىككى خىلدىن بار'),
(4954, 55, 53, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4955, 55, 54, 'ئۇلار ئەستەرلىرى قېلىن تاۋاردىن بولغان تۆشەكلەرگە يۆلىنىدۇ، ئىككى جەننەتنىڭ مېۋىسى (تۇرغان، ئولتۇرغان، ياتقان ئادەممۇ ئۈزەلەيدىغان دەرىجىدە) يېقىندۇر'),
(4956, 55, 55, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4957, 55, 56, 'ئۇ جەننەتلەردە ئەرلىرىدىن باشقىلارغا قارىمايدىغان، ئەرلىرىدىن بۇرۇن ئۇلار بىلەن ھېچ ئىنسان ۋە جىن يېقىنچىلىق قىلمىغان خوتۇنلار بار'),
(4958, 55, 57, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4959, 55, 58, 'گويا ئۇلار (سۈزۈكلۈكتە) ياقۇتتۇر، (ئاقلىقتا) مەرۋايىتتۇر'),
(4960, 55, 59, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4961, 55, 60, 'ياخشى ئىش قىلغۇچى پەقەت ياخشى مۇكاپاتقا ئېرىشىدۇ'),
(4962, 55, 61, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4963, 55, 62, 'ئۇ ئىككى جەننەتتىن تۆۋەنرەك ئىككى جەننەت بار'),
(4964, 55, 63, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4965, 55, 64, 'ئۇ ئىككى جەننەت ياپيېشىلدۇر'),
(4966, 55, 65, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4967, 55, 66, 'ئۇ ئىككى جەننەتتە قايناپ چىقىپ تۇرغان ئىككى بۇلاق بار'),
(4968, 55, 67, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4969, 55, 68, 'ئۇ ئىككى جەننەتتە مېۋىلەر، خورمىلار ۋە ئانارلار بار'),
(4970, 55, 69, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4971, 55, 70, 'ئۇ جەننەتلەردە چىرايلىق خوتۇنلار بار'),
(4972, 55, 71, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4973, 55, 72, 'ئۇلار (جەننەتنىڭ) چېدىرلىرىدا مەستۇرە ھۆرلەردۇر'),
(4974, 55, 73, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4975, 55, 74, 'ئەرلىرىدىن ئىلگىرى ئۇلارغا ھېچ ئىنسان يېقىنچىلىق قىلمىغان'),
(4976, 55, 75, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4977, 55, 76, 'ئۇلار يېشىل ياستۇقلارغا، چىرايلىق بىساتلارغا يۆلەنگەن ھالدا ئىستىراھەت قىلىدۇ'),
(4978, 55, 77, '(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟'),
(4979, 55, 78, 'ئەزىمەتلىك ۋە كەرەملىك پەرۋەردىگارىڭنىڭ نامى مۇبارەكتۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'قىيامەت قايىم بولغان چاغدا،'),
(4981, 56, 2, 'ھېچ ئادەم ئۇنى ئىنكار قىلالمايدۇ'),
(4982, 56, 3, 'زېمىن قاتتىق تەۋرىتىلگەن، تاغلار پارچىلىنىپ توزاندەك توزۇپ كەتكەن چاغدا، قىيامەت (بەزىلەرنى دوزاخقا كىرگۈزۈش بىلەن دەرىجىسىنى) چۈشۈرىدۇ (بەزىلەرنى جەننەتكە كىرگۈزۈش بىلەن دەرىجىسىنى) كۆتۈرىدۇ'),
(4983, 56, 4, 'زېمىن قاتتىق تەۋرىتىلگەن، تاغلار پارچىلىنىپ توزاندەك توزۇپ كەتكەن چاغدا، قىيامەت (بەزىلەرنى دوزاخقا كىرگۈزۈش بىلەن دەرىجىسىنى) چۈشۈرىدۇ (بەزىلەرنى جەننەتكە كىرگۈزۈش بىلەن دەرىجىسىنى) كۆتۈرىدۇ'),
(4984, 56, 5, 'زېمىن قاتتىق تەۋرىتىلگەن، تاغلار پارچىلىنىپ توزاندەك توزۇپ كەتكەن چاغدا، قىيامەت (بەزىلەرنى دوزاخقا كىرگۈزۈش بىلەن دەرىجىسىنى) چۈشۈرىدۇ (بەزىلەرنى جەننەتكە كىرگۈزۈش بىلەن دەرىجىسىنى) كۆتۈرىدۇ'),
(4985, 56, 6, 'زېمىن قاتتىق تەۋرىتىلگەن، تاغلار پارچىلىنىپ توزاندەك توزۇپ كەتكەن چاغدا، قىيامەت (بەزىلەرنى دوزاخقا كىرگۈزۈش بىلەن دەرىجىسىنى) چۈشۈرىدۇ (بەزىلەرنى جەننەتكە كىرگۈزۈش بىلەن دەرىجىسىنى) كۆتۈرىدۇ'),
(4986, 56, 7, 'سىلەر (قىيامەتتە) ئۈچ پىرقىغە بۆلۈنىسىلەر'),
(4987, 56, 8, '(بىرىنچى پىرقە) سائادەتمەنلەردۇر، سائادەتمەنلەر قانداق ئادەملەر؟ (ئۇلار نامە - ئەمالى ئوڭ تەرىپىدىن بېرىلىدىغان) ئادەملەردۇر'),
(4988, 56, 9, '(ئىككىنچى پىرقە) بەختسىز ئادەملەردۇر، بەختسىز ئادەملەر قانداق ئادەملەر؟ (ئۇلار نامە - ئەمالى سول تەرىپىدىن بېرىلىدىغان ئادەملەردۇر'),
(4989, 56, 10, '(ئۈچىنچى پىرقە ياخشى ئىشلارنى) ئەڭ ئالدىدا قىلغۇچىلار بولۇپ، (ئۇلار جەننەتكە) ئەڭ ئالدىدا كىرگۈچىلەردۇر'),
(4990, 56, 11, 'ئۇلار نازۇنېمەتلىك جەننەتلەردە اﷲ قا يېقىن بولغۇچىلاردۇر'),
(4991, 56, 12, 'ئۇلار نازۇنېمەتلىك جەننەتلەردە اﷲ قا يېقىن بولغۇچىلاردۇر'),
(4992, 56, 13, 'بۇلار ئۆتكەنكىلەرنىڭ ئىچىدە كۆپتۇر، كېيىنكىلەرنىڭ ئىچىدە ئازدۇر'),
(4993, 56, 14, 'بۇلار ئۆتكەنكىلەرنىڭ ئىچىدە كۆپتۇر، كېيىنكىلەرنىڭ ئىچىدە ئازدۇر'),
(4994, 56, 15, 'ئۇلار (ئالتۇندىن) توقۇلغان تەختلەر ئۈستىگە يۆلەنگەن ھالدا بىر - بىرىگە قارىشىپ ئولتۇرۇىشىدۇ'),
(4995, 56, 16, 'ئۇلار (ئالتۇندىن) توقۇلغان تەختلەر ئۈستىگە يۆلەنگەن ھالدا بىر - بىرىگە قارىشىپ ئولتۇرۇىشىدۇ'),
(4996, 56, 17, '(قېرىماي) ھەمىشە بىر خىل تۇرىدىغان غىلمانلار ئېقىپ تۇرغان شارابتىن تولدۇرۇلغان پىيالە، چەينەك، جاملارنى ئۇلارغا ئايلىنىپ سۇنۇپ تۇرىدۇ'),
(4997, 56, 18, '(قېرىماي) ھەمىشە بىر خىل تۇرىدىغان غىلمانلار ئېقىپ تۇرغان شارابتىن تولدۇرۇلغان پىيالە، چەينەك، جاملارنى ئۇلارغا ئايلىنىپ سۇنۇپ تۇرىدۇ'),
(4998, 56, 19, 'ئۇ شارابنى ئىچىش بىلەن ئۇلارنىڭ بېشى ئاغرىمايدۇ، مەست بولمايدۇ'),
(4999, 56, 20, 'ئۇلارغا ئىختىيار قىلغان مېۋىلەر ۋە كۆڭۈللىرى تارتقان قۇش گۆشلىرى بېرىلىدۇ'),
(5000, 56, 21, 'ئۇلارغا ئىختىيار قىلغان مېۋىلەر ۋە كۆڭۈللىرى تارتقان قۇش گۆشلىرى بېرىلىدۇ'),
(5001, 56, 22, 'ئۇلارغا سەدەپنىڭ ئىچىدىكى گۆھەرگە ئوخشايدىغان شەھلا كۆزلۈك ھۆرلەر بېرىلىدۇ'),
(5002, 56, 23, 'بۇ ئۇلارنىڭ قىلغان ياخشى ئەمەللىرىنى مۇكاپاتلاش ئۈچۈندۇر'),
(5003, 56, 24, 'ئۇلار جەننەتتە بىھۇدە ۋە يالغان سۆزلەرنى ئاڭلىمايدۇ، پەقەت «سالام! سالام!» سۆزىنىلا ئاڭلايدۇ'),
(5004, 56, 25, 'ئۇلار جەننەتتە بىھۇدە ۋە يالغان سۆزلەرنى ئاڭلىمايدۇ، پەقەت «سالام! سالام!» سۆزىنىلا ئاڭلايدۇ'),
(5005, 56, 26, 'سائادەتمەنلەرنىڭ (يەنى نامە - ئەمالى ئوڭ تەرەپتىن بېرىلگەنلەرنىڭ) ئەھۋالى ناھايىتى ياخشى بولىدۇ'),
(5006, 56, 27, 'سائادەتمەنلەر قانداق ئادەملەر؟'),
(5007, 56, 28, 'ئۇلار سىدرى دەرەخلىرىدىن، سانجاق - سانجاق بولۇپ كەتكەن مەۋز دەرەخلىرىدىن، ھەمىشە تۇرىدىغان سايىدىن، ئېقىپ تۇرغان سۇدىن، تۈگىمەيدىغان ۋە چەكلەنمەيدىغان مېۋىلەردىن، ئېگىز (يۇمشاق) تۆشەكلەردىن بەھرىمەن بولىدۇ'),
(5008, 56, 29, 'ئۇلار سىدرى دەرەخلىرىدىن، سانجاق - سانجاق بولۇپ كەتكەن مەۋز دەرەخلىرىدىن، ھەمىشە تۇرىدىغان سايىدىن، ئېقىپ تۇرغان سۇدىن، تۈگىمەيدىغان ۋە چەكلەنمەيدىغان مېۋىلەردىن، ئېگىز (يۇمشاق) تۆشەكلەردىن بەھرىمەن بولىدۇ'),
(5009, 56, 30, 'ئۇلار سىدرى دەرەخلىرىدىن، سانجاق - سانجاق بولۇپ كەتكەن مەۋز دەرەخلىرىدىن، ھەمىشە تۇرىدىغان سايىدىن، ئېقىپ تۇرغان سۇدىن، تۈگىمەيدىغان ۋە چەكلەنمەيدىغان مېۋىلەردىن، ئېگىز (يۇمشاق) تۆشەكلەردىن بەھرىمەن بولىدۇ'),
(5010, 56, 31, 'ئۇلار سىدرى دەرەخلىرىدىن، سانجاق - سانجاق بولۇپ كەتكەن مەۋز دەرەخلىرىدىن، ھەمىشە تۇرىدىغان سايىدىن، ئېقىپ تۇرغان سۇدىن، تۈگىمەيدىغان ۋە چەكلەنمەيدىغان مېۋىلەردىن، ئېگىز (يۇمشاق) تۆشەكلەردىن بەھرىمەن بولىدۇ'),
(5011, 56, 32, 'ئۇلار سىدرى دەرەخلىرىدىن، سانجاق - سانجاق بولۇپ كەتكەن مەۋز دەرەخلىرىدىن، ھەمىشە تۇرىدىغان سايىدىن، ئېقىپ تۇرغان سۇدىن، تۈگىمەيدىغان ۋە چەكلەنمەيدىغان مېۋىلەردىن، ئېگىز (يۇمشاق) تۆشەكلەردىن بەھرىمەن بولىدۇ'),
(5012, 56, 33, 'ئۇلار سىدرى دەرەخلىرىدىن، سانجاق - سانجاق بولۇپ كەتكەن مەۋز دەرەخلىرىدىن، ھەمىشە تۇرىدىغان سايىدىن، ئېقىپ تۇرغان سۇدىن، تۈگىمەيدىغان ۋە چەكلەنمەيدىغان مېۋىلەردىن، ئېگىز (يۇمشاق) تۆشەكلەردىن بەھرىمەن بولىدۇ'),
(5013, 56, 34, 'ئۇلار سىدرى دەرەخلىرىدىن، سانجاق - سانجاق بولۇپ كەتكەن مەۋز دەرەخلىرىدىن، ھەمىشە تۇرىدىغان سايىدىن، ئېقىپ تۇرغان سۇدىن، تۈگىمەيدىغان ۋە چەكلەنمەيدىغان مېۋىلەردىن، ئېگىز (يۇمشاق) تۆشەكلەردىن بەھرىمەن بولىدۇ'),
(5014, 56, 35, 'شۈبھىسىزكى، بىز ھۆرلەرنى يېڭىدىن ياراتتۇق، ئۇلارنى پاكىز، ئەرلىرىگە ئامراق، تەڭتۇش قىلدۇق'),
(5015, 56, 36, 'شۈبھىسىزكى، بىز ھۆرلەرنى يېڭىدىن ياراتتۇق، ئۇلارنى پاكىز، ئەرلىرىگە ئامراق، تەڭتۇش قىلدۇق'),
(5016, 56, 37, 'شۈبھىسىزكى، بىز ھۆرلەرنى يېڭىدىن ياراتتۇق، ئۇلارنى پاكىز، ئەرلىرىگە ئامراق، تەڭتۇش قىلدۇق'),
(5017, 56, 38, 'بۇ ھۆرلەر سائادەتمەنلەر ئۈچۈندۇر'),
(5018, 56, 39, 'بۇلار بولسا ئىلگىرىكىلەرنىڭ ئىچىدىن بىر جامائەدۇر'),
(5019, 56, 40, 'كېيىنكىلەرنىڭ ئىچىدىن بىر جامائەدۇر'),
(5020, 56, 41, 'بەختسىزلەر (يەنى نامە - ئەمالى سول تەرىپىدىن بېرىلگەنلەر) (دوزىخىلاردۇر). بەختسىزلەر قانداق ئادەملەر؟'),
(5021, 56, 42, 'ئۇلار (بەدەننىڭ تۆشۈكلىرىگە كىرىپ كېتىدىغان ئاتەشلىك شامالنىڭ، زىيادە ھارارەتلىك قايناقسۇنىڭ ۋە قارا تۈتۈندىن بولغان سالقىنمۇ ئەمەس، كۆركەممۇ ئەمەس سايىنىڭ ئىچىدە بولىدۇ'),
(5022, 56, 43, 'ئۇلار (بەدەننىڭ تۆشۈكلىرىگە كىرىپ كېتىدىغان ئاتەشلىك شامالنىڭ، زىيادە ھارارەتلىك قايناقسۇنىڭ ۋە قارا تۈتۈندىن بولغان سالقىنمۇ ئەمەس، كۆركەممۇ ئەمەس سايىنىڭ ئىچىدە بولىدۇ'),
(5023, 56, 44, 'ئۇلار (بەدەننىڭ تۆشۈكلىرىگە كىرىپ كېتىدىغان ئاتەشلىك شامالنىڭ، زىيادە ھارارەتلىك قايناقسۇنىڭ ۋە قارا تۈتۈندىن بولغان سالقىنمۇ ئەمەس، كۆركەممۇ ئەمەس سايىنىڭ ئىچىدە بولىدۇ'),
(5024, 56, 45, 'چۈنكى ئۇلار بۇنىڭدىن ئىلگىرى (يەنى دۇنيادا) شەھۋەتكە چۆمگەن ئىدى'),
(5025, 56, 46, 'ئۇلار چوڭ گۇناھتا (يەنى كۇفرىدا) چىڭ تۇرغان ئىدى'),
(5026, 56, 47, 'ئۇلار: «بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغان چاغدا بىز چوقۇم تىرىلدۈرۈلەمدۇق؟ ئاتا - بوۋىلىرىمىزمۇ تىرىلدۈرۈلەمدۇ؟» دەيتتى'),
(5027, 56, 48, 'ئۇلار: «بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغان چاغدا بىز چوقۇم تىرىلدۈرۈلەمدۇق؟ ئاتا - بوۋىلىرىمىزمۇ تىرىلدۈرۈلەمدۇ؟» دەيتتى'),
(5028, 56, 49, 'ئېيتقىنكى، «ئىلگىرىكىلەر ۋە كېيىنكىلەر مەلۇم كۈننىڭ مۇئەييەن ۋاقتىدا (يەنى قىيامەتتە) توپلىنىدۇ'),
(5029, 56, 50, 'ئېيتقىنكى، «ئىلگىرىكىلەر ۋە كېيىنكىلەر مەلۇم كۈننىڭ مۇئەييەن ۋاقتىدا (يەنى قىيامەتتە) توپلىنىدۇ'),
(5030, 56, 51, 'ئاندىن سىلەر، ئى قايتا تىرىلىشنى ئىنكار قىلغۇچى گۇمراھلار!'),
(5031, 56, 52, 'چوقۇم زەققۇم دەرىخىدىن يەيسىلەر'),
(5032, 56, 53, 'ئۇنىڭدىن قورساقلىرىڭلارنى تولدۇرىسىلەر'),
(5033, 56, 54, 'ئۇنىڭ ئۈستىگە ھارارىتى يۇقىرى بولغان قايناقسۇنى تەشنا بولغان تۆگىلەردەك ئىچىسىلەر'),
(5034, 56, 55, 'ئۇنىڭ ئۈستىگە ھارارىتى يۇقىرى بولغان قايناقسۇنى تەشنا بولغان تۆگىلەردەك ئىچىسىلەر'),
(5035, 56, 56, 'مانا بۇ، ئۇلارنىڭ قىيامەت كۈنىدىكى زىياپىتىدۇر'),
(5036, 56, 57, '(ئىنسانلار!) سىلەرنى بىز ياراتتۇق، سىلەر قايتا تىرىلىشكە ئىشەنمەمسىلەر؟'),
(5037, 56, 58, 'سىلەر (ئاياللارنىڭ بەچىدانىغا) تۆكۈلگەن مەنىنىي دەپ بېقىڭلارچۇ'),
(5038, 56, 59, 'ئۇنى سىلەر (ئىنسان قىلىپ) يارىتامسىلەر؟ ياكى بىز (ئىنسان قىلىپ) يارىتامدۇق'),
(5039, 56, 60, 'بىز سىلەرنىڭ ئاراڭلاردا ئۆلۈمنى بەلگىلىدۇق (يەنى ھەر بېرىڭلارنىڭ ئۆلۈش ۋاقتىنى بېكىتتۇق)، سىلەرنى باشقا بىر قەۋمگە ئالماشتۇرۇشتىن ۋە سىلەرنى سىلەر بىلمەيدىغان بىر شەكىلدە يارىتىشتىن ئاجىز ئەمەسمىز'),
(5040, 56, 61, 'بىز سىلەرنىڭ ئاراڭلاردا ئۆلۈمنى بەلگىلىدۇق (يەنى ھەر بېرىڭلارنىڭ ئۆلۈش ۋاقتىنى بېكىتتۇق)، سىلەرنى باشقا بىر قەۋمگە ئالماشتۇرۇشتىن ۋە سىلەرنى سىلەر بىلمەيدىغان بىر شەكىلدە يارىتىشتىن ئاجىز ئەمەسمىز'),
(5041, 56, 62, 'شۈبھىسىزكى، سىلەر دەسلەپكى يارىتىشنى بىلدىڭلار (يەنى دەسلەپتە اﷲ نىڭ سىلەرنى يوقتىن بار قىلغانلىقىنى تونۇدۇڭلار). سىلەر (اﷲ نىڭ سىلەرنى دەسلەپ ياراتقاندەك قايتا يارىتىشىنى) ئويلىمامسىلەر؟'),
(5042, 56, 63, 'سىلەر تېرىغان زىرائەتنى دەپ بېقىڭلارچۇ'),
(5043, 56, 64, 'ئۇنى سىلەر ئۈندۈردۈڭلارمۇ ياكى بىز ئۈندۈرۈپ (دان چىقىدىغان زىرائەت قىلىپ ئۆستۈردۇقمۇ؟)'),
(5044, 56, 65, 'ئەگەر بىز خالىساق ئەلۋەتتە ئۇنى دانسىز قۇرۇق چۆپكە ئايلاندۇراتتۇق - دە، سىلەر ئەجەبلىنىپ قايغۇراتتىڭلار'),
(5045, 56, 66, 'سىلەر: «بىز ھەقىقەتەن (ئەمگەك بىلەن ئۇرۇقنى) زىيان تارتتۇق، بەلكى بىز (رىزىقتىن) مەھرۇم قالدۇق» (دەيتتىڭلار)'),
(5046, 56, 67, 'سىلەر: «بىز ھەقىقەتەن (ئەمگەك بىلەن ئۇرۇقنى) زىيان تارتتۇق، بەلكى بىز (رىزىقتىن) مەھرۇم قالدۇق» (دەيتتىڭلار)'),
(5047, 56, 68, 'سىلەر ئىچىۋاتقان سۇنى دەپ بېقىڭلارچۇ'),
(5048, 56, 69, 'ئۇنى بۇلۇتتىن سىلەر چۈشۈردۈڭلارمۇ ياكى بىز چۈشۈردۇقمۇ؟'),
(5049, 56, 70, 'ئەگەر بىز خالىساق ئۇنى تۇزلۇق قىلىپ قوياتتۇق (پەرۋەردىگارىڭلارنىڭ زور نېمەتلىرىگە) نېمىشقا شۈكۈر قىلمايسىلەر؟'),
(5050, 56, 71, 'سىلەر ياندۇرۇۋاتقان ئوتنى دەپ بېقىڭلارچۇ'),
(5051, 56, 72, 'ئۇنىڭ دەرىخىنى (يەنى مۇرخۇ، ئۇبار ناملىق بىرىنىڭ شېخىنى بىرىگە سۈركىسە ھۆل تۇرۇپلا ئوت ئالىدىغان دەرەخنى سىلەر ئۆستۈردۈڭلارمۇ ياكى بىز ئۆستۈردۇقمۇ؟'),
(5052, 56, 73, 'ئۇنى بىز ئىبرەت ۋە يولۇچىلار پايدىلىنىدىغان نەرسە قىلدۇق'),
(5053, 56, 74, 'ئۇلۇغ پەرۋەردىگارىڭنى (مۇشرىكلارنىڭ ئىپتىرالىرىدىن) پاك ئېتىقاد قىلغىن'),
(5054, 56, 75, 'يۇلتۇزلارنىڭ جايلىرى بىلەن قەسەم قىلىمەن قىلىمەن'),
(5055, 56, 76, 'شۈبھىسىزكى، ئەگەر بىلسەڭلار، ئۇ (يۇلتۇزلار اﷲ نىڭ ئۇلۇغ قۇدرىتىنى كۆرسىتىدىغانلىقى ئۈچۈن) ئەلۋەتتە كاتتا قەسەمدۇر'),
(5056, 56, 77, 'شەك - شۈبھىسىزكى، ئۇ ئۇلۇغ قۇرئاندۇر'),
(5057, 56, 78, 'كىتابتا (يەنى لەۋھۇلمەھپۇزدا) ساقلانغاندۇر'),
(5058, 56, 79, 'ئۇنى پەقەت پاك بولغانلارلا تۇتىدۇ'),
(5059, 56, 80, 'ئۇ ئالەملەرنىڭ پەرۋەردىگار تەرىپىدىن نازىل قىلىنغاندۇر'),
(5060, 56, 81, '(ئى كۇففارلار جامائەسى!) بۇ قۇرئاننى ئىنكار قىلامسىلەر؟'),
(5061, 56, 82, '(سىلەر اﷲ نىڭ رىزىق بەرگەنلىكىگە) شۈكۈر قىلىشنىڭ ئورنىغا، رىزىق بەرگۈچىنى) ئىنكار قىلامسىلەر؟'),
(5062, 56, 83, 'جان ھەلقۇمغا يەتكەن چاغدا (سەكراتتىكى كىشىگە) قاراپ تۇرىسىلەر'),
(5063, 56, 84, 'جان ھەلقۇمغا يەتكەن چاغدا (سەكراتتىكى كىشىگە) قاراپ تۇرىسىلەر'),
(5064, 56, 85, 'بىز (ئىلمىمىز ۋە قۇدرىتىمىز بىلەن) ئۇنىڭغا سىلەردىن يېقىنمىز، لېكىن سىلەر (ئۇنى) كۆرمەيسىلەر (يەنى بىلمەيسىلەر)'),
(5065, 56, 86, 'ئەگەر (سىلەر گۇمان قىلغاندەك ئەمەلىڭلارغا قاراپ) جازاغا تارتىلمايدىغان بولساڭلار نېمىشقا ئۇنى (يەنى ئۇ مېيىتنىڭ جېنىنى بەدىنىگە قايتۇرمايسىلەر؟ ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار'),
(5066, 56, 87, 'ئەگەر (سىلەر گۇمان قىلغاندەك ئەمەلىڭلارغا قاراپ) جازاغا تارتىلمايدىغان بولساڭلار نېمىشقا ئۇنى (يەنى ئۇ مېيىتنىڭ جېنىنى بەدىنىگە قايتۇرمايسىلەر؟ ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار'),
(5067, 56, 88, 'ئەگەر ئۇ مېيىت مۇقەررەبلەردىن (يەنى تائەت - ئىبادەت ۋە ياخشى ئىشلارنى ئەڭ ئالدىدا قىلغۇچىلاردىن) بولىدىغان بولسا، (ئۇنىڭ مۇكاپاتى) راھەت - پاراغەت، ياخشى رىزىق ۋە نازۇنېمەتلىك جەننەت بولىدۇ'),
(5068, 56, 89, 'ئەگەر ئۇ مېيىت مۇقەررەبلەردىن (يەنى تائەت - ئىبادەت ۋە ياخشى ئىشلارنى ئەڭ ئالدىدا قىلغۇچىلاردىن) بولىدىغان بولسا، (ئۇنىڭ مۇكاپاتى) راھەت - پاراغەت، ياخشى رىزىق ۋە نازۇنېمەتلىك جەننەت بولىدۇ'),
(5069, 56, 90, 'ئەگەر ئۇ سائادەتمەنلەردىن بولىدىغان بولسا، (ئۇلار راھەت - پاراغەتتە بولغانلىقى ئۈچۈن) (ئى مۇھەممەد!) ئۇلاردىن ساڭا سالام!'),
(5070, 56, 91, 'ئەگەر ئۇ سائادەتمەنلەردىن بولىدىغان بولسا، (ئۇلار راھەت - پاراغەتتە بولغانلىقى ئۈچۈن) (ئى مۇھەممەد!) ئۇلاردىن ساڭا سالام!'),
(5071, 56, 92, 'ئەگەر ئۇ تىرىلىشنى ئىنكار قىلغۇچى گۇمراھلاردىن بولىدىغان بولسا، يۇقىرى ھارارەتلىك قايناقسۇ بىلەن كۈتۈۋېلىنىدۇ'),
(5072, 56, 93, 'ئەگەر ئۇ تىرىلىشنى ئىنكار قىلغۇچى گۇمراھلاردىن بولىدىغان بولسا، يۇقىرى ھارارەتلىك قايناقسۇ بىلەن كۈتۈۋېلىنىدۇ'),
(5073, 56, 94, 'جەھەننەمدە كۆيدۈرۈلىدۇ'),
(5074, 56, 95, 'شەك - شۈبھىسىزكى، بۇ ئېنىق ھەقىقەتتۇر'),
(5075, 56, 96, 'ئۇلۇغ پەرۋەردىگارىڭنى (مۇشرىكلارنىڭ ئېتىرازلىرىدىن) پاك ئېتىقاد قىلغىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ قا تەسبىھ ئېيتتى، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(5077, 57, 2, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، اﷲ ئۆلتۈرەلەيدۇ، تىرىلدۈرەلەيدۇ، اﷲ ھەر نەرسىگە قادىردۇر'),
(5078, 57, 3, 'اﷲ نىڭ ئىپتىداسى يوقتۇر، ئىنتىھاسى يوقتۇر، (اﷲ) ئاشكارىدۇر (يەنى مەۋجۇتلۇقىنى كۆرسەتكۈچى دەلىللەر بىلەن ئەقىللەرگە ئاشكارىدۇ)، مەخپىيدۇر (يەنى ئۇنى كۆز بىلەن كۆرگىلى بولمايدۇ، ئۇنىڭ زاتىنىڭ ماھىيىتىنى تونۇشقا ئەقىللەر ئېرىشەلمەيدۇ)، اﷲ ھەر نەرسىنى بىلگۈچىدۇر'),
(5079, 57, 4, 'اﷲ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە ياراتتى، (ئاندىن ئۆزىگە لايىق رەۋىشتە) ئەرش ئۈستىدە قارار ئالدى، اﷲ زېمىنغا كىرىپ كېتىدىغان نەرسىلەرنى، زېمىندىن چىقىدىغان نەرسىلەرنى، ئاسماندىن چۈشىدىغان نەرسىلەرنى، ئاسمانغا ئۆرلەيدىغان نەرسىلەرنى بىلىپ تۇرىدۇ، سىلەر قەيەردە بولساڭلار، اﷲ سىلەر بىلەن بىرگە، اﷲ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(5080, 57, 5, 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر، ھەممە ئىش اﷲ قا قايتۇرۇلىدۇ'),
(5081, 57, 6, 'اﷲ كېچىنى كۈندۈزگە كىرگۈزىدۇ، كۈندۈزنى كېچىگە كىرگۈزىدۇ، اﷲ دىللاردىكى سىرلارنى بىلگۈچىدۇر'),
(5082, 57, 7, 'اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان كەلتۈرۈڭلار، اﷲ سىلەرنى ئىگە قىلغان ماللاردىن سەدىقە قىلىڭلار، سىلەردىن ئىمان ئېيتقانلار ۋە سەدىقە قىلغانلار چوڭ ساۋابقا ئىگە بولىدۇ'),
(5083, 57, 8, '(ئى كۇففارلار!) سىلەر نېمىشقا اﷲ قا ئىمان كەلتۈرمەيسىلەر؟ ھالبۇكى، پەيغەمبەر سىلەرنى پەرۋەردىگارىڭلارغا ئىمان كەلتۈرۈشكە چاقىرىدۇ، اﷲ سىلەردىن چىن ئەھدە ئالدى، ئەگەر ئىمان كەلتۈرۈشنى ئىرادە قىلساڭلار (ئىمانغا ئالدىراڭلار)'),
(5084, 57, 9, 'سىلەرنى كۇفرىنىڭ قاراڭغۇلۇقىدىن (ئىماننىڭ) نۇرىغا چىقىرىش ئۈچۈن، اﷲ بەندىسىگە روشەن ئايەتلەرنى نازىل قىلدى، اﷲ شەك - شۈبھىسىز سىلەرگە مەرھەمەتلىكتۇر، ناھايىتى مېھرىباندۇر'),
(5085, 57, 10, 'نېمىشقا سىلەر اﷲ نىڭ يولىدا (پۇل - مال) سەرپ قىلمايسىلەر، ئاسمانلارنىڭ ۋە زېمىننىڭ مىراسى اﷲ قا خاستۇر، سىلەردىن مەككە پەتھى قىلىنىشتىن ئىلگىرى پۇل - مال سەرپ قىلغانلار ۋە (رەسۇلۇللاھ بىلەن بىرلىكتە تۇرۇپ دۈشمەنلەر بىلەن) ئۇرۇشقانلار (مەككە پەتھى قىلنغاندىن كېيىن پۇل - مال) نەپىقە قىلغانلار ۋە ئۇرۇشقانلار (بىر - بىرى بىلەن) باراۋەر ئەمەس، ئەنە شۇلارنىڭ دەرىجىسى (مەككە پەتھى قىلىنغاندىن) كېيىن نەپىقە قىلىنغان ۋە ئۇرۇشقانلارنىڭ (دەرىجىسىدىن) چوڭدۇر، (ئۇلارنىڭ) ھەر بىرىگە اﷲ جەننەتنى ۋەدە قىلدى. اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلاردىن خەۋەرداردۇر'),
(5086, 57, 11, 'كىمكى اﷲ قا قەرزىي ھەسەنە بېرىدىكەن (يەنى اﷲ نىڭ رازىلىقىنى تىلەپ، ئۇنىڭ يولىدا پۇل - مال سەرپ قىلىدىكەن)، اﷲ ئۇنىڭغا ھەسسىلەپ قايتۇرىدۇ، ئۇنىڭغا چوڭ ساۋاب بېرىدۇ'),
(5087, 57, 12, 'ئۇ كۈندە مۆمىن ئەرلەرنى، مۆمىن ئاياللارنى كۆرۈسەنكى، ئۇلارنىڭ نۇرى ئۇلارنىڭ ئالدىدا ۋە ئۇلارنىڭ ئوڭ تەرىپىدە ماڭىدۇ، (ئۇلارغا) «بۈگۈن سىلەرگە ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەر بىلەن خۇش خەۋەر بېرىلىدۇ، ئۇ يەرلەردە مەڭگۈ قالىسىلەر، بۇ ئۇلۇغ مۇۋاپپەقىيەتتۇر» (دېيىلىدۇ)'),
(5088, 57, 13, 'ئۇ كۈندە مۇناپىق ئەرلەر، مۇناپىق ئاياللار مۆمىنلەرگە: «بىزنى كۈتۈپ تۇرۇڭلار، سىلەرنىڭ نۇرۇڭلاردىن ئازراق ئالايلى» دەيدۇ. ئۇلارغا (مەسخىرە يۈزىسىدىن): «ئارقاڭلارغا قايتىپ نۇر تىلەڭلار» دېيىلىدۇ، شۇنىڭ بىلەن ئۇلارنىڭ ئارىسىغا بىر سېپىل سوقۇلىدۇ، سېپىلنىڭ دەرۋازىسى بولىدۇ، دەرۋازىنىڭ ئىچى تەرىپىدە رەھمەت، تېشىدا ئازاب بولىدۇ'),
(5089, 57, 14, 'ئۇلار مۆمىنلەرنى: «بىز سىلەر بىلەن بىللە ئەمەسمىدۇق» دەپ توۋلايدۇ، مۆمىنلەر: «شۇنداق، لېكىن سىلەر ئۆزۈڭلارنى پىتنىگە سالدىڭلار (يەنى كۆرۈنۈشتە بىز بىلەن بىللە بولساڭلارمۇ، لېكىن ئۆزۈڭلارنى مۇناپىقلىق بىلەن ھالاك قىلدىڭلار، مۆمىنلەرگە بالايى - ئاپەتلەر كېلىشىنى) كۈتتۈڭلار، (ئىسلام دىنىدىن شەكلەندىڭلار، (اﷲ نىڭ رەھمىتىنىڭ كەڭلىكىگە بولغان قۇرۇق) ئارزۇلار سىلەرنى ئالدىدى، تاكى سىلەرگە ئۆلۈم كەلدى، شەيتان سىلەرنى اﷲ نىڭ (ئەپۇ قىلىدىغانلىقى ۋە ئازاب قىلمايدىغانلىقى بىلەن ئالدىدى» دەيدۇ'),
(5090, 57, 15, 'بۈگۈن سىلەردىن ۋە كاپىرلاردىن فىدىيە ئېلىنمايدۇ، جايىڭلار دوزاختۇر، دوزاخ سىلەرگە ئەڭ لايىقتۇر، دوزاخ نېمىدېگەن يامان جاي!'),
(5091, 57, 16, 'مۆمىنلەرگە ئۇلارنىڭ دىللىرى اﷲ نىڭ زىكرىگە ۋە نازىل بولغان ھەقىقەتكە (يەنى قۇرئان ئايەتلىرىگە) ئېرىيدىغان ۋاقىت يەتمىدىمۇ؟ ئۇلار ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دەك بولمىسۇن، (ئۇلار بىلەن پەيغەمبەرلىرىنىڭ ئارىلىقىدىكى) زاماننىڭ ئۇزىرىشى بىلەن ئۇلارنىڭ دىللىرى قېتىپ كەتتى، ئۇلارنىڭ نۇرغۇنى اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر'),
(5092, 57, 17, 'بىلىڭلاركى، اﷲ زېمىننى ئۆلگەندىن كېيىن تىرىلدۈرىدۇ (يەنى قاقاس، قۇرغاق زېمىننى يامغۇر بىلەن كۆكەرتىدۇ)، بىز ھەقىقەتەن سىلەرنى چۈشەنسۇن دەپ سىلەرگە (كامالى قۇدرىتىمىزنى كۆرسىتىدىغان) دەلىللەرنى بايان قىلدۇق'),
(5093, 57, 18, 'سەدىقە بەرگۈچى ئەرلەرگە، سەدىقە بەرگۈچى ئاياللارغا ۋە اﷲ قا قەرزىي ھەسەنە بەرگۈچىلەرگە (يەنى اﷲ نىڭ رازىلىقىنى تىلەپ، كەمبەغەللەرگە سەدىقە بەرگۈچىلەر، ياخشىلىق يوللىرىغا خۇشاللىق بىلەن پۇل - مال سەرپ قىلغۇچىلارغا) ھەسسىلەپ (ساۋاب) بېرىلىدۇ، ئۇلارغا زور مۇكاپات (يەنى جەننەت) بېرىلىدۇ'),
(5094, 57, 19, 'اﷲ قا ۋ ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتقانلار - ئەنە شۇلار پەرۋەردىگارىنىڭ دەرگاھىدا سىددىقلاردۇر، شېھىتلەردۇر، ئۇلار (ئاخىرەتتە) ساۋابقا ۋە نۇرغا ئىگە بولىدۇ، بىرنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار ۋە يالغانغا چىقارغانلار - ئەنە شۇلار ئەھلى دوزاختۇر'),
(5095, 57, 20, 'بىلىڭلاركى، دۇنيا تىرىكچىلىكى ئويۇندىن، (كىشىنى ئاخىرەتتە غەپلەتتە قالدۇرىدىغان) مەشغۇلاتتىن، زىبۇزىننەتتىن، ئۆزئارا پەخىرلىنىشتىن، پۇل - مال، پەرزەنت كۆپەيتىپ پەخىرلىنىشتىن ئىبارەتتۇر، (بىرەر يېغىش بىلەن ئۆستۈرگەن) ئۆسۈملۈكى دېھقانلارنى خۇرسەن قىلغان يامغۇرغا ئوخشايدۇ، ئاندىن ئۇ ئۆسۈملۈك قۇرۇپ قالىدۇ، ئۇنىڭ سارغىيىپ قالغانلىقىنى كۆرۈسەن، ئاندىن ئۇ يەنچىلگەن چۆپكە ئايلىنىدۇ. ئاخىرەتتە (كۇففارلار ئۈچۈن) قاتتىق ئازاب بار، (ياخشى بەندىلەر ئۈچۈن) اﷲ نىڭ مەغپىرىتى ۋە رازىلىقى بار، دۇنيا تىرىكچىلىكى پەقەت (غاپىل ئادەم) ئالدىنىدىغان بەھرىمەن بولۇشتۇر'),
(5096, 57, 21, 'پەرۋەردىگارىڭلارنىڭ مەغپىرىتىنى، كەڭلىكى ئاسمان - زېمىندەك كېلىدىغان جەننەتنى قولغا كەلتۈرۈڭلار، (ئۇ) جەننەت اﷲ قا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتقانلارغا تەييارلانغان، بۇ اﷲ نىڭ پەزلىدۇر، (اﷲ) ئۇنى خالىغان ئادەمگە ئاتا قىلىدۇ، اﷲ ئۇلۇغ پەزل ئىگىسىدۇر'),
(5097, 57, 22, 'يەر يۈزىدىكى بارلىق ھادىسىلەر ۋە ئۆزۈڭلار ئۇچرىغان مۇسىبەتلەر بىز ئۇلارنى يارىتىشتىن بۇرۇن لەۋھۇلمەھپۇزغا يېزىلغان، بۇ (ئىش)، شۈبھىسىزكى، اﷲ قا ئاساندۇر'),
(5098, 57, 23, '(اﷲ تائالانىڭ ئۇلارنى لەۋھۇلمەھپۇزغا يېزىشى) قولۇڭلاردىن كەتكەن نەرسىگە قايغۇرۇپ كەتمەسلىكىڭلار ۋە اﷲ بەرگەن نەرسىلەرگە خۇش بولۇپ كەتمەسلىكىڭلار ئۈچۈندۇر، اﷲ مۇتەكەببىرلەر ۋە ئۆزلىرىنى چوڭ تۇتقۇچىلارنى دوست تۇتمايدۇ'),
(5099, 57, 24, 'ئۇلار بېخىللىق قىلىدىغان ۋە كىشىلەرنى بېخىللىق قىلىشقا بۇيرۇيدىغانلاردۇر، كىمكى (اﷲ نىڭ يولىدا نەپىقە قىلىشتىن) باش تارتىدىكەن (اﷲ قا ھېچ زىيان يەتكۈزەلمەيدۇ)، چۈنكى اﷲ بىھاجەتتۇر، مەدھىيىگە لايىقتۇر'),
(5100, 57, 25, 'بىز ھەقىقەتەن پەيغەمبەرلىرىمىزنى روشەن مۆجىزىلەر بىلەن ئەۋەتتۇق ۋە ئۇلار بىلەن بىللە، ئىنسانلار ئادالەتنى بەرپا قىلسۇن دەپ، كىتابنى، قانۇننى چۈشۈردۇق. تۆمۈرنى ياراتتۇق، (تۆمۈردىن ئۇرۇش قوراللىرى ياسىلىدىغانلىقى ئۈچۈن) تۆمۈر كۈچ - قۇۋۋەتنى ئۆز ئىچىگە ئالغان، تۆمۈردە ئىنسانلار ئۈچۈن نۇرغۇن مەنپەئەتلەر بار، اﷲ اﷲ نى كۆرمەي تۇرۇپ (قوراللارنى ئىشلىتىپ) اﷲ قا ۋە پەيغەمبەرلىرىگە ياردەم بەرگەنلەرنى بىلىش (مەلۇم قىلىش) ئۈچۈن (تۆمۈرنى ياراتتى)، اﷲ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر'),
(5101, 57, 26, 'بىز ھەقىقەتەن نۇھنى، ئىبراھىمنى پەيغەمبەر قىلىپ ئەۋەتتۇق، پەيغەمبەرلىكنى ۋە كىتابنى ئۇلارنىڭ ئەۋلادىغا (ئاتا) قىلدۇق، ئۇلاردىن ھىدايەت تاپقانلىرى بار، ئۇلارنىڭ نۇرغۇنى اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر'),
(5102, 57, 27, 'ئۇلاردىن كېيىن پەيغەمبەرلىرىمىزنى داۋاملىق ئەۋەتتۇق، ئارقىدىن مەريەم ئوغلى ئىسانى ئەۋەتتۇق، ئۇنىڭغا ئىنجىلنى ئاتا قىلدۇق، ئۇنىڭغا ئەگەشكەنلەرنىڭ دىللىرىغا شەپقەت ۋە مېھرىبانلىقنى سالدۇق، رەھبانىيەتنى ئۇلار ئۆزلىرى پەيدا قىلدى، ئۇلارغا ئۇنى بىز بېكىتمىدۇق، ئۇلار ئۇنى پەقەت اﷲ نىڭ رازىلىقىنى تىلەپ (پەيدا قىلدى)، لېكىن ئۇلار ئۇنىڭغا لايىق دەرىجىدە ئەمەل قىلمىدى، ئۇلاردىن ئىمان ئېيتقانلارنىڭ ئەجرىنى ھەسسىلەپ بەردۇق، ئۇلاردىن نۇرغۇنى اﷲ نىڭ ئىتائىتىدىن چىققۇچىلاردۇر'),
(5103, 57, 28, 'ئى (ئىساغا) ئىمان ئېيتقان كىشىلەر! اﷲ تىن قورقۇڭلار، اﷲ قا ۋە ئۇنىڭ پەيغەمبىرى (مۇھەممەد ئەلەيھىسسالامغا) ئىمان ئېيتىڭلار، اﷲ سىلەرگە رەھمىتىدىن ئىككى ھەسسە بېرىدۇ، سىلەرگە سىلەر (ئاخىرەتتە پىلسىراتتا) ماڭىدىغان نۇرنى پەيدا قىلىدۇ، سىلەرگە مەغپىرەت قىلىدۇ، اﷲ بەكمۇ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(5104, 57, 29, 'ئەھلى كىتابنىڭ اﷲ نىڭ پەزلىدىن ھېچ نەرسىگە قادىر ئەمەسلىكلىرىنى، پەزلنىڭ ھەممىسى اﷲ نىڭ قولىدا بولۇپ، (اﷲ نىڭ) ئۇنى خالىغان ئادەمگە بېرىدىغانلىقىنى، اﷲ نىڭ ئۇلۇغ پەزل ئىگىسى ئىكەنلىكىنى بىلىش ئۈچۈن (اﷲ شۇ سۆزنى قىلدى)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'اﷲ ھەقىقەتەن ئېرى توغرىسىدا سەن بىلەن مۇنازىرىلەشكەن ۋە اﷲ قا شىكايەت قىلغان ئايالنىڭ سۆزىنى ئاڭلىدى، اﷲ ئىككىڭلارنىڭ سۆزۈڭلارنى ئاڭلاپ تۇرىدۇ، اﷲ ھەقىقەتەن ئاڭلىغۇچىدۇر كۆرگۈچىدۇر'),
(5106, 58, 2, 'سىلەردىن ئاياللىرىنى زىھار قىلغانلارنىڭ (يەنى ئاياللىرىنى ئانىلىرىمىزنىڭ ئۇچىلىرىغا ئوخشاش دېگۈچىلەرنىڭ) ئاياللىرى ئۇلارنىڭ ئانىلىرى ئەمەستۇر، پەقەت ئۇلارنى تۇغقان ئاياللارلا ئۇلارنىڭ ئانىلىرىدۇر، ھالبۇكى، ئۇلار (يەنى زىھار قىلغۇچىلار) ئەلۋەتتە يامان سۆزنى، يالغان سۆزنى قىلىدۇ، اﷲ، شەك - شۈبھىسىزكى، (تەۋبە قىلغۇچىنى) ئەپۇ قىلغۇچىدۇر، مەغپىرەت قىلغۇچىدۇر'),
(5107, 58, 3, 'ئاياللىرىنى زىھار قىلىپ، ئاندىن قىلغان سۆزىگە (يەنى زىھار قىلغانلىقىغا) پۇشايمان قىلغانلار (ئەر - ئايال ئىككىسى) يېقىنچىلىق قىلىشتىن ئىلگىرى بىر قۇلنى ئازاد قىلىشى لازىم. بۇنىڭ بىلەن سىلەرگە ۋەز - نەسىھەت قىلىنىدۇ، اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەرداردۇر'),
(5108, 58, 4, '(ئازاد قىلىشقا) قۇلى يوق ئادەم، ئايالى بىلەن يېقىنچىلىق قىلىشتىن ئىلگىرى، ئۇدا ئىككى ئاي روزا تۇتۇشى لازىم، (قېرىلىق ياكى كېسەللىك سەۋەبى بىلەن روزا تۇتۇشقا) قادىر بولالمىغان ئادەم (60 مىسكىنگە بىر ۋاخ تاماق بېرىشى لازىم)، بۇ (ئەھكام) اﷲ ۋە ئۇنىڭ پەيغەمبىرى (ئەمر قىلغان ئىشلارغا) تەستىق قىلىشىڭلار ئۈچۈن (بايان قىلىندى). بۇلار اﷲ نىڭ قانۇنىدۇر، (بۇ قانۇننى) ئىنكار قىلغۇچىلار قاتتىق ئازابقا دۇچار بولىدۇ'),
(5109, 58, 5, 'شۈبھىسىزكى، اﷲ بىلەن ۋە ئۇنىڭ پەيغەمبىرى بىلەن قارشىلاشقانلار ئۇلاردىن بۇرۇنقىلار ھالاك قىلىنغاندەك ھالاك قىلىنىدۇ، بىز ھەقىقەتەن (ھالال، ھارام، پەرز ۋە ئەھكاملارنى ئۆز ئىچىگە ئالغان) روشەن ئايەتلەرنى نازىل قىلدۇق، كاپىرلار خار قىلغۇچى ئازابقا دۇچار بولىدۇ'),
(5110, 58, 6, 'ئۇ كۈندە اﷲ ئۇلارنىڭ ھەممىسىنى تىرىلدۈرىدۇ، ئۇلارنىڭ (دۇنيادىكى چاغدا قىلغان) قىلمىشلىرىنى ئېيتىپ بېرىدۇ، اﷲ ئۇلارنىڭ قىلمىشلىرىنى (نامە - ئەمالىغا) خاتىرىلەپ قويغان، ئۇلار بولسا ئۇنتۇپ قالغان، اﷲ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر'),
(5111, 58, 7, 'اﷲ نىڭ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى بىلىپ تۇرىدىغانلىقىنى بىلمەمسەن؟ ئۈچ ئادەم پىچىرلىشىدىكەن، اﷲ ئۇلارنىڭ تۆتىنچىسىدۇر، بەش ئادەم پىچىرلىشىدىكەن، اﷲ ئۇلارنىڭ ئالتىنچىسىدۇر، مەيلى ئۇنىڭدىن ئاز ياكى كۆپ ئادەم پىچىرلاشسۇن، ئۇلار قەيەردە بولمىسۇن، اﷲ ھامان ئۇلار بىلەن بىللىدۇر، ئاندىن قىيامەت كۈنى ئۇلارنىڭ قىلمىشلىرىنى اﷲ ئۇلارغا ئېيتىپ بېرىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىنى تولۇق بىلگۈچىدۇر'),
(5112, 58, 8, 'پىچىرلىشىشتىن مەنئى قىلىنغان، ئاندىن مەنئى قىلىنغان نەرسىگە قايتقان كىشىلەرنى كۆرمىدىڭمۇ؟ ئۇلار گۇناھ بىلەن، زۇلۇم بىلەن ۋە پەيغەمبەرگە قارشى تۇرۇش بىلەن پىچىرلىشىدۇ، ئۇلار سېنىڭ يېنىڭغا كەلگەندە، اﷲ ساڭا سالام قىلىشتا قوللانمىغان سۆزلەر بىلەن سالام قىلىدۇ، ئۇلار ئۆز ئىچىدە: «اﷲ نېمىشقا بۇ سۆزىمىز بىلەن بىزنى جازالىمايدۇ!» دەيدۇ، دوزاخ ئۇلارغا كۇپايىدۇر، ئۇلار دوزاخقا كىرىدۇ، دوزاخ نېمىدېگەن يامان جاي!'),
(5113, 58, 9, 'ئى مۆمىنلەر! سىلەر پىچىرلاشساڭلار گۇناھ، زۇلۇم ۋە پەيغەمبەرنىڭ ئەمرىگە خىلاپلىق قىلىش توغرىسىدا پىچىرلاشماڭلار، ياخشىلىق ۋە تەقۋادارلىق توغرىسىدا پىچىرلىشىڭلار، سىلەر اﷲ تىن قورقۇڭلار، سىلەر ئۇنىڭ دەرگاھىغا يىغىلىسىلەر'),
(5114, 58, 10, 'گۇناھ توغرىسىدا پىچىرلىشىش مۆمىنلەرنى قايغۇغا سېلىش ئۈچۈن شەيتان تەرىپىدىن بولغاندۇر، (بۇنداق پىچىرلىشىش) اﷲ نىڭ ئىرادىسى بولمىسىلا مۆمىنلەرگە ھېچقانداق زىيان يەتكۈزەلمەيدۇ، مۆمىنلەر اﷲ قا تەۋەككۈل قىلسۇن'),
(5115, 58, 11, 'ئى مۆمىنلەر! ئەگەر (سورۇندا) بەزىلەر سىلەرگە: «ئورۇن چىقىرىپ بېرىڭلار» دېسە، ئورۇن چىقىرىپ بېرىڭلار، اﷲ سىلەرگە (رەھمىتىنى ۋە جەننىتىنى) كېڭەيتىپ بېرىدۇ، ئەگەر سىلەرگە (باشقىلارغا ئورۇن بوشىتىپ بېرىش ئۈچۈن): «ئورنۇڭلاردىن تۇرۇپ بېرىڭلار» دېيىلسە، ئورنۇڭلاردىن تۇرۇپ بېرىڭلار، اﷲ سىلەردىن ئىمان ئېيتقانلار ۋە ئىلىم بېرىلگەنلەرنى بىر قانچە دەرىجە يۇقىرى كۆتۈرىدۇ، اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەردار'),
(5116, 58, 12, 'ئى مۆمىنلەر! پەيغەمبەر بىلەن مەخپىي سۆزلەشمەكچى بولساڭلار، ئالدى بىلەن (يوقسۇللارغا) سەدىقە بېرىپ، ئاندىن سۆزلىشىڭلار، بۇ سىلەر ئۈچۈن ياخشىراقتۇر ۋە پاكراقتۇر، ئەگەر (سەدىقە قىلىدىغان نەرسە) تاپالمىساڭلار (سىلەرگە گۇناھ بولمايدۇ) چۈنكى اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(5117, 58, 13, 'سىلەر رەسۇلۇللاھ بىلەن مەخپىي سۆزلىشىشتىن ئىلگىرى سەدىقىلەر بېرىشتىن (كەمبەغەل بولۇپ قالىمىز) دەپ قورقتۇڭلارمۇ؟ سىلەر سەدىقە بەرمىدىڭلار، اﷲ سىلەرنى ئەپۇ قىلدى، ناماز ئوقۇڭلار، زاكات بېرىڭلار، اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، قىلىۋاتقان ئىشىڭلاردىن اﷲ تولۇق خەۋەرداردۇر'),
(5118, 58, 14, 'اﷲ نىڭ غەزىپىگە ئۇچرىغان قەۋمنى ئۇلارنىڭ دوست تۇتقانلىقىنى كۆرمىدىڭمۇ؟ ئۇلار سىلەرگىمۇ ۋە ئۇ قەۋمگىمۇ (يەنى يەھۇدىيلارغىمۇ) مەنسۇپ ئەمەس، ئۇلار بىلىپ تۇرۇپ يالغاندىن قەسەم ئىچىدۇ'),
(5119, 58, 15, 'ئۇلارغا اﷲ قاتتىق ئازاب تەييارلىدى، ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!'),
(5120, 58, 16, 'ئۇلار (يالغان) قەسەملىرىنى قالقان قىلىۋالدى، كىشىلەرنى اﷲ نىڭ يولىدىن توستى، ئۇلار خار قىلغۇچى ئازابقا دۇچار بولىدۇ'),
(5121, 58, 17, 'ئۇلارنىڭ ماللىرى ۋە بالىلىرى (ئۇلارغا ئاخىرەتتە بولىدىغان) اﷲ نىڭ ئازابىدىن ھېچ نەرسىنى دەپئى قىلالمايدۇ، ئەنە شۇلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر'),
(5122, 58, 18, 'ئۇ كۈندە اﷲ ئۆلگەنلەرنىڭ ھەممىسىنى تىرىلدۈرىدۇ، ئۇلار (دۇنيادىكى چاغلىرىدا «بىز مۇسۇلمانمىز» دەپ يالغاندىن) سىلەرگە قەسەم ئىچكەندەك اﷲ قا قەسەم ئىچىدۇ، ئۇلار بۇ قەسەمنىڭ ئۆزلىرىگە پايدىسى بولىدۇ دەپ ئويلايدۇ، ئاگاھ بۇلۇڭلاركى، ئۇلار ھەقىقەتەن يالغانچىلاردۇر'),
(5123, 58, 19, 'ئۇلارنىڭ ئۈستىدىن شەيتان غەلىبە قىلدى، (شەيتان) ئۇلارغا اﷲ نىڭ زىكرىنى ئۇنتۇلدۇردى، ئۇلار شەيتاننىڭ قوشۇنىدۇر، بىلىڭلاركى، شەيتاننىڭ قوشۇنى زىيان تارتقۇچىلاردۇر'),
(5124, 58, 20, 'شۈبھىسىزكى، اﷲ بىلەن ۋە ئۇنىڭ پەيغەمبىرى بىلەن قارشىلاشقۇچىلار ئەڭ خار ئىنسانلار قاتارىدىدۇر'),
(5125, 58, 21, 'اﷲ (لەۋھۇلمەھپۇزغا): «مەن ۋە مېنىڭ پەيغەمبەرلىرىم چوقۇم غەلىبە قىلىمىز» دەپ يازدى، اﷲ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر'),
(5126, 58, 22, 'اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغان بىر قەۋمنىڭ اﷲ بىلەن ۋە ئۇنىڭ پەيغەمبىرى بىلەن قارشىلاشقانلارنى - ئۇلارنىڭ ئانىلىرى، ئوغۇللىرى، يا قېرىنداشلىرى، يا ئۇرۇق - تۇغقانلىرى بولغان تەقدىردىمۇ - دوست تۇتقانلىقىنى كۆرمەيسەن (يەنى ئۇلارنى دوست تۇتۇشى مۇمكىن ئەمەس)، ئەنە شۇلارنىڭ دىللىرىدا اﷲ ئىماننى مەھكەم قىلدى ۋە ئۆز دەرگاھىدىن بولغان روھ بىلەن ئۇلارنى كۈچەيتتى، اﷲ ئۇلارنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ، اﷲ ئۇلاردىن رازى بولىدۇ، اﷲ تىن ئۇلارمۇ مەمنۇن بولىدۇ، ئەنە شۇلار اﷲ نىڭ قوشۇنىدۇر، بىلىڭلاركى، اﷲ نىڭ قوشۇنى مەقسەتكە ئېرىشكۈچىلەردۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ قا تەسبىھ ئېيتتى، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(5128, 59, 2, 'اﷲ ئەھلى كىتابتىن بولغان كاپىرلارنى (يەنى بەنى نەزىر يەھۇدىيلىرىنى) تۇنجى قېتىملىق سۈرگۈندە ئۆيلىرىدىن ھەيدەپ چىقاردى، سىلەر ئۇلارنى (كۈچلۈك تۇرۇپ مۇنداق خار ھالدا) چىقىپ كېتىدۇ دەپ گۇمان قىلمىغان ئىدىڭلار، ئۇلار ئۆزلىرىنىڭ قورغانلىرىنى ئۆزلىرىنى اﷲ نىڭ (ئازابىدىن) توسىيالايدۇ دەپ ئويلىغان ئىدى، ئۇلارغا اﷲ نىڭ ئازابى ئۇلار ئويلىمىغان يەردىن كەلدى. اﷲ ئۇلارنىڭ دىللىرىغا قورقۇنچ سالدى، ئۇلار ئۆيلىرىنى ئۆزلىرىنىڭ قوللىرى ۋە مۆمىنلەرنىڭ قوللىرى بىلەن بۇزدى، ئى ئەقىل ئىگىلىرى! (ئۇلارنىڭ ھالىدىن) ئىبرەت ئېلىڭلار'),
(5129, 59, 3, 'ئەگەر اﷲ ئۇلارنى سۈرگۈن بولۇشقا ھۆكۈم قىلمىغان بولسا ئىدى، ئەلۋەتتە ئۇلارنى دۇنيادا (ئۆلتۈرۈش) بىلەن ئازابلايتتى، ئۇلار ئاخىرەتتە دوزاخ ئازابىغا دۇچار بولىدۇ'),
(5130, 59, 4, 'بۇ (يەنى ئۇلارنىڭ سۈرگۈن قىلىنىشى) شۇنىڭ ئۈچۈندۇركى، ئۇلار اﷲ بىلەن ۋە ئۇنىڭ پەيغەمبىرى بىلەن قارشىلاشتى، كىمكى اﷲ بىلەن قارشىلىشىدىكەن، شۈبھىسىز، اﷲ ئۇنى قاتتىق جازالايدۇ'),
(5131, 59, 5, '(ئى مۇسۇلمانلار!) سىلەر مەيلى (بەنى نەزىرنىڭ) خورمىلىرىنى كېسىڭلار، مەيلى ئۇنى بۇرۇنقى پېتى ئۆرە قالدۇرۇڭلار، ھەممىسىگە اﷲ رۇخسەت قىلىدۇ، (اﷲ نىڭ رۇخسەت قىلىشى) اﷲ نىڭ ئىتائىتىدىن چىققۇچىلارنى (يەنى يەھۇدىيلارنى) رەسۋا قىلىش ئۈچۈندۇر'),
(5132, 59, 6, 'اﷲ پەيغەمبىرىگە ئۇلاردىن (يەنى بەنى نەزىرنىڭ ماللىرىدىن) قايتۇرغان غەنىمەتلەرنى سىلەر ئات - تۆگىلەرنى چاپتۇرۇپ (يەنى ئەجىر قىلىپ، جاپا - مۇشەققەت تارتىپ) قولغا كەلتۈرگەن ئەمەس، لېكىن اﷲ پەيغەمبەرلىرىنى خالىغان ئادەمگە مۇسەللەت قىلىدۇ، اﷲ ھەر نەرسىگە قادىردۇر'),
(5133, 59, 7, 'اﷲ پەيغەمبىرىگە غەنىمەت قىلىپ بەرگەن ئەلى قۇرا (يەنى قۇرەيزە، نەزىر، پەدەك ۋە خەيبەر كۇففارلىرى) نىڭ ماللىرىنى، ئىچىڭلاردىكى بايلار ئارىسىدا قولدىن قولغا ئۆتۈپ يۈرمەسلىكى ئۈچۈن، اﷲ قا، پەيغەمبەرگە، پەيغەمبەرنىڭ خىش - ئەقرىبالىرىغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە خاس قىلدى، پەيغەمبەر سىلەرگە بەرگەننى ئېلىڭلار، پەيغەمبەر چەكلىگەن نەرسىدىن چەكلىنىڭلار، اﷲ تىن قورقۇڭلار، ھەقىقەتەن اﷲ نىڭ ئازابى قاتتىقتۇر'),
(5134, 59, 8, '(ئۇ غەنىمەتنىڭ بىر قىسمى) دىيارىدىن ھەيدەپ چىقىرىلغان، مال - مۈلكىدىن ئايرىلغان پېقىر مۇھاجىرلارغا خاستۇر، ئۇلار اﷲ نىڭ پەزلىنى ۋە رازىلىقىنى تىلەيدۇ، اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ياردەم بېرىدۇ، ئەنە شۇلار (ئىماندا) سادىق ئادەملەردۇر'),
(5135, 59, 9, 'ئۇلاردىن (يەنى مۇھاجىرلاردىن) مەدىنىدە يەرلىك بولغان، ئىمانى كۈچلۈك بولغانلار (يەنى ئەنسارلار) يېنىغا ھىجرەت قىلىپ كەلگەنلەرنى (يەنى مۇھاجىرلارنى) دوست تۇتىدۇ، ئۇلارغا بېرىلگەن نەرسىلەر ئۈچۈن ئىچى تارلىق قىلمايدۇ، ئۇلار موھتاج تۇرۇقلۇق (مۇھاجىرلارنىڭ مەنپەئىتىنى) ئۆزلىرىنىڭ (مەنپەئىتى) دىن ئەلا بىلىدۇ، ئۆز نەپسىنىڭ بېخىللىقىدىن ساقلانغانلار مەقسەتكە ئېرىشكۈچىلەردۇر'),
(5136, 59, 10, 'ئۇلاردىن كېيىن كەلگەنلەر: «پەرۋەردىگارىمىز! بىزگە ۋە بىزدىن ئىلگىرى ئىمان ئېيتقان قېرىنداشلىرىمىزغا مەغپىرەت قىلغىن، دىللىرىمىزدا مۆمىنلەرگە قارشى دۈشمەنلىك پەيدا قىلمىغىن، پەرۋەردىگارىمىز! سەن ناھايىتى مەغپىرەت قىلغۇچىسەن، مېھرىبانسەن» دەيدۇ'),
(5137, 59, 11, 'مۇناپىقلارنىڭ ئۆزلىرىنىڭ (مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىنى) ئىنكار قىلغان ئەھلى كىتاب بۇرادەرلىرىگە: «يۇرتۇڭلاردىن چىقىرىۋېتىلسەڭلار، بىز چوقۇم سىلەر بىلەن بىرگە چىقىپ كېتىمىز، ھەرگىز سىلەرنىڭ زىيىنىڭلار ھېسابىغا باشقا ئادەمگە ئىتائەت قىلمايمىز، سىلەرگە ئۇرۇش ئېچىلسا چوقۇم سىلەرگە ياردەم بېرىمىز» دېيىشكەنلىكىنى كۆرمىدىڭمۇ؟ اﷲ گۇۋاھلىق بېرىدۇكى، مۇناپىقلار چوقۇم يالغانچىلاردۇر'),
(5138, 59, 12, 'ئەگەر ئۇلار ھەيدەپ چىقىرىلسا، مۇناپىقلار ئۇلار بىلەن بىللە چىقمايدۇ، ئەگەر ئۇلارغا ئۇرۇش ئېچىلسا، مۇناپىقلار ئۇلارغا ياردەم بەرمەيدۇ، بەرگەن تەقدىردىمۇ يۈز ئۆرۈپ قاچىدۇ، ئەھلى كىتاب ياردەمگە ئېرىشەلمەيدۇ'),
(5139, 59, 13, 'شۈبھىسىزكى، سىلەر ئۇلارنىڭ دىللىرىدا اﷲ قا قارىغاندىمۇ قورقۇنچلۇقسىلەر (يەنى ئۇلار سىلەردىن اﷲ تىن قورققاندىنمۇ بەك قورقىدۇ)، بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار اﷲ نىڭ (ئۇلۇغلۇقىنى) چۈشەنمەيدىغان قەۋمدۇر'),
(5140, 59, 14, 'ئۇلار پەقەت مۇستەھكەم شەھەر ئىچىدە ياكى تاملارنىڭ ئارقىسىدا تۇرغاندىلا، ئاندىن سىلەرگە قارشى بىرلىشىپ ئۇرۇش قىلالايدۇ، ئۇلارنىڭ ئۇرۇشى ئۆز ئىچىدە قاتتىقتۇر، ئۇلارنى ئۆم دەپ ئويلايسەن، ھالبۇكى، ئۇلارنىڭ دىللىرى تارقاقتۇر، بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار چۈشەنمەيدىغان قەۋمدۇر'),
(5141, 59, 15, 'ئۇلار (يەنى يەھۇدىيلار) ئۇلاردىن ئىلگىرى يېقىندىلا ئۆتكەن، قىلمىشلىرىنىڭ ۋابالىنى تېتىغانلارغا (يەنى مۇشرىكلارغا) ئوخشايدۇ، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ'),
(5142, 59, 16, 'ئۇلار (يەنى مۇناپىقلار يەھۇدىيلارنى ئۇرۇشقا قىزىقتۇرۇشقا) شەيتانغا ئوخشايدۇ، ئۆز ۋاقتىدا شەيتان ئىنسانغا: «كاپىر بولغىن» دېدى. ئىنسان كاپىر بولغان چاغدا، شەيتان: «مەن سەندىن ئادا - جۇدامەن، مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى اﷲ تىن قورقىمەن» دېدى'),
(5143, 59, 17, 'ئۇلارنىڭ (يەنى شەيتان بىلەن ئىنساننىڭ) ئاقىۋىتى بىرلىكتە دوزاخقا كىرىش، دوزاختا مەڭگۈ قىلىش بولدى، ئەنە شۇ زالىملارنىڭ جازاسىدۇر'),
(5144, 59, 18, 'ئى مۆمىنلەر! اﷲ تىن قورقۇڭلار، ھەر ئادەم ئەتە (يەنى قىيامەت كۈنى) ئۈچۈن (ياخشى ئەمەللەردىن) نېمىلەرنى تەييارلىغانلىقىغا قارىسۇن، اﷲ تىن قورقۇڭلار، اﷲ ھەقىقەتەن قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەرداردۇر'),
(5145, 59, 19, 'سىلەر اﷲ نى ئۇنتۇغان، شۇنىڭ بىلەن اﷲ ئۇلارغا ئۆزلىرىنى ئۇنتۇلدۇرغان كىشىلەردەك بولماڭلار، ئەنە شۇلار پاسىقلاردۇر'),
(5146, 59, 20, 'ئەھلى دوزاخ بىلەن ئەھلى جەننەت (قىيامەت كۈنى) باراۋەر بولمايدۇ، ئەھلى جەننەت بولسا مەقسەتكە ئېرىشكۈچىلەردۇر'),
(5147, 59, 21, 'ئەگەر بىز قۇرئاننى بىرەر تاغقا نازىل قىلساق، چوقۇم سەن ئۇنىڭ اﷲ تىن قورققانلىقتىن باش ئەگكەنلىكىنى، يېرىلىپ كەتكەنلىكىنى كۆرەتتىڭ، بىز بۇ مىساللارنى كىشىلەرگە ئۇلار (اﷲ نىڭ قۇدرىتىنى) پىكىر قىلسۇن دەپ بايان قىلىمىز'),
(5148, 59, 22, 'اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ يوشۇرۇننى، ئاشكارىنى بىلگۈچىدۇر، ئۇ ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر (يەنى دۇنيا ۋە ئاخىرەتتە) كەڭ رەھمەت ئىگىسىدۇر'),
(5149, 59, 23, 'اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ (مۇتلەق) پادىشاھتۇر (يەنى پۈتۈن مەخلۇقاتنىڭ مالىكىدۇر)، پاكتۇر (يەنى ھەممە نۇقساندىن پاكتۇر)، (مۆمىنلەرگە) سالامەتلىك بېغىشلىغۇچىدۇر، (دوستلارنى قورقۇنچتىن، ئازابتىن) ئەمىن قىلغۇچىدۇر، ھەممىنى كۆزىتىپ تۇرغۇچىدۇر، غالىبتۇر، قەھر قىلغۇچىدۇر، ئۇلۇغلۇق ئىگىسىدۇر، (مۇشرىكلارنىڭ) شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر'),
(5150, 59, 24, 'اﷲ (ھەممىنى) ياراتقۇچىدۇر، (يوقلۇقتىن) پەيدا قىلغۇچىدۇر، (ھەممە نەرسىگە) سۈرەت (شەكىل) بېغىشلىغۇچىدۇر، ئەڭ چىرايلىق ئىسىملارغا ئىگىدۇر، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر ئۇنىڭغا تەسبىھ ئېيتىپ تۇرىدۇ، ئۇ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'ئى مۆمىنلەر، مېنىڭ دۈشمىنىمنى ۋە سىلەرنىڭ دۈشمىنىڭلارنى دوست تۇتماڭلار، ئۇلار سىلەرگە كەلگەن ھەقىقەتنى (يەنى ئىسلامنى، قۇرئاننى) ئىنكار قىلغان تۇرسا، ئۇلارغا دوستلۇق يەتكۈزىسىلەر، ئۇلار پەرۋەردىگارىڭلار اﷲ قا ئىمان ئېيتقىنىڭلار ئۈچۈن پەيغەمبەرنى ۋە سىلەرنى يۇرتۇڭلاردىن ھەيدەپ چىقاردى، ئەگەر سىلەر مېنىڭ يولۇمدا جىھاد قىلىپ ۋە رازىلىقىمنى تىلەپ چىققان بولساڭلار (ئۇ چاغدا مېنىڭ دۈشمىنىمنى ۋە سىلەرنىڭ دۈشمىنىڭلارنى دوست تۇتماڭلار)، سىلەر ئۇلارغا يوشۇرۇن دوستلۇق يەتكۈزىسىلەر، مەن سىلەر يوشۇرغان ۋە ئاشكارىلىغان نەرسىلەرنى بىلىپ تۇرىمەن، كىمكى شۇنداق قىلىدىكەن (يەنى ئۇلارنى دوست تۇتۇپ، ئۇلارغا پەيغەمبەرنىڭ خەۋەرلىرىنى يەتكۈزىدىكەن)، ئۇ ھەقىقەتەن توغرا يولدىن ئازغان بولىدۇ'),
(5152, 60, 2, 'ئەگەر ئۇلار سىلەرنى يەڭسە سىلەرگە دۈشمەن بولىدۇ، سىلەرگە زىيانكەشلىك قىلىدۇ، سىلەرنى تىللايدۇ، سىلەرنىڭ مۇرتەد بولۇشۇڭلارنى ئارزۇ قىلىدۇ'),
(5153, 60, 3, 'قىيامەت كۈنى سىلەرنىڭ تۇغقانلىرىڭلار ۋە ئەۋلادلىرىڭلار سىلەرگە ھېچ پايدا يەتكۈزەلمەيدۇ، اﷲ ئاراڭلارنى ئايرىۋېتىدۇ (يەنى سىلەر جەننەتتە بولساڭلار، ئۇلار كۇففارلارنىڭ قاتارىدا دوزاختا بولىدۇ)، اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(5154, 60, 4, 'ئىبراھىم ۋە ئۇنىڭ بىللە بولغان (مۆمىنلەر) سىلەرگە ھەقىقەتەن ئوبدان نەمۇنىدۇر، ئۆز ۋاقتىدا ئۇلار قەۋمىگە: «ھەقىقەتەن بىز سىلەردىن ۋە سىلەر اﷲ نى قويۇپ ئىبادەت قىلىۋاتقان بۇتلىرىڭلاردىن ئادا - جۇدامىز، بىزنىڭ ئارىمىزدىكى ئاداۋەت ۋە ئۆچمەنلىك تاكى سىلەر يالغۇز بىر اﷲ قا ئىمان ئېيتقىنىڭلارغا قەدەر ئەبەدىي ساقلىنىپ قالىدۇ» دېدى. پەقەت ئىبراھىمنىڭ ئاتىسىغا ئېيتقان «ئەلۋەتتە ساڭا مەغپىرەت تىلەيمەن، اﷲ نىڭ ئازابىدىن ھېچ نەرسىنى سەندىن دەپئى قىلالمايمەن» دېگەن سۆزىنى سىلەرنىڭ نەمۇنە قىلىشىڭلارغا بولمايدۇ. (ئىبراھىم ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەر ئېيتتى) «پەرۋەردىگارىمىز ساڭا تەۋەككۈل قىلدۇق، ساڭا قايتتۇق، ئاخىر قايتىدىغان جاي سېنىڭ دەرگاھىڭدۇر'),
(5155, 60, 5, 'پەرۋەردىگارىمىز! كاپىرلارنى بىزگە زىيانكەشلىك قىلىش ئىمكانىيىتىگە ئىگە قىلمىغىن (ئۇلار بىزگە مۇسەللەت بولۇپ، بىزنى كۆتۈرگىلى بولمايدىغان ئازاب بىلەن دىنىمىزدىن چىقارمىسۇن)، پەرۋەردىگارىمىز، بىزگە مەغپىرەت قىلغىن، سەن ھەقىقەتەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن»'),
(5156, 60, 6, '(ئى مۇھەممەدنىڭ ئۈممىتى!) ئىبراھىم ۋە ئۇنىڭغا ئەگەشكەن مۆمىنلەر سىلەرگە، اﷲ نى ۋە ئاخىرەت كۈنىنى ئۈمىد قىلىدىغانلارغا ياخشى نەمۇنىدۇر، كىمكى (ئىمانىدىن) يۈز ئۆرۈيدىكەن، (بىلسۇنكى) اﷲ (ئۇنىڭدىن ۋە ئۇنداقلاردىن) بىھاجەتتۇر، (اﷲ) مەدھىيىگە لايىقتۇر'),
(5157, 60, 7, 'بەلكىم اﷲ سىلەر بىلەن سىلەر ئۆچ كۆرۈدىغان ئادەملەر ئارىسىدا دوستلۇق پەيدا قىلىدۇ، اﷲ ھەر نەرسىگە قادىردۇر، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(5158, 60, 8, '(كۇففارلاردىن) سىلەر بىلەن ئۇرۇش قىلمىغان ۋە سىلەرنى يۇرتۇڭلاردىن ھەيدەپ چىقارمىغانلارغا كەلسەك، اﷲ ئۇلارغا ياخشىلىق قىلىشىڭلاردىن، ئۇلارغا ئادىل بولۇشۇڭلاردىن سىلەرنى توسمايدۇ، شۈبھىسىزكى، اﷲ ئادىللارنى دوست تۇتىدۇ'),
(5159, 60, 9, 'اﷲ دىن ئۈچۈن سىلەر بىلەن ئۇرۇشقان، سىلەرنى يۇرتۇڭلاردىن ھەيدەپ چىقارغان ۋە سىلەرنى ھەيدەپ چىقىرىشقا ياردەملەشكەنلەرنى دوست تۇتۇشۇڭلاردىن سىلەرنى توسىدۇ، كىمكى ئۇلارنى دوست تۇتىدىكەن، ئۇلار زالىملاردۇر'),
(5160, 60, 10, 'ئى مۆمىنلەر! سىلەرگە مۆمىن ئاياللار ھىجرەت قىلىپ كەلسە، ئۇلارنى سىناپ كۆرۈڭلار، ئۇلارنىڭ ئىمانىنى اﷲ ئوبدان بىلىدۇ، ئەگەر ئۇلارنى (سىنىغاندىن كېيىن ھەقىقىي) مۆمىنە دەپ تونۇساڭلار، ئۇلارنى كاپىرلارغا قايتۇرۇپ بەرمەڭلار، ئۇلار كاپىرلارغا ھالال ئەمەس، كاپىرلارمۇ ئۇلارغا ھالال ئەمەس، كاپىر ئەرلىرىنىڭ ئۇلارغا بەرگەن مەھرىنى قايتۇرۇپ بېرىڭلار، ئۇلارنىڭ مەھرىلىرىنى بېرىپ نىكاھىڭلارغا ئالساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ، كاپىر ئاياللارنى ئەمرىڭلاردا ساقلىماڭلار، ئۇلارغا بەرگەن مەھرىڭلارنى تەلەپ قىلىڭلار، كاپىرلارمۇ (ھىجرەت قىلىپ كەلگەن مۆمىن ئاياللىرىغا بەرگەن) مەھرىلىرىنى تەلەپ قىلسۇن، اﷲ نىڭ ھۆكمى ئەنە شۇ. اﷲ ئاراڭلاردا ھۆكۈم چىقىرىدۇ، اﷲ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(5161, 60, 11, 'ئەگەر سىلەرنىڭ بەزى ئاياللىرىڭلار كاپىرلارغا كىرىپ كەتسە، (كاپىرلار بىلەن) ئۇرۇشۇپ غەنىمەت ئالغان ۋاقتىڭلاردا، ئاياللىرى كاپىرلارغا كىرىپ كەتكەن كىشىلەرگە (غەنىمەتتىن) ئۇلارنىڭ بەرگەن مەھرىنىڭ مىقدارىنى بېرىڭلار (يەنى مۇسۇلمانلاردىن بىرىنىڭ ئايالى كۇففارلار تەرەپكە قېچىپ كەتسە، سىلەر كۇففارلار بىلەن ئۇرۇشۇپ ئالغان غەنىمەت مالدىن مەزكۇر ئايالنىڭ ئېرىگە مەھرى مىقدارى مال بېرىڭلار)، سىلەر ئۆزۈڭلار ئىشەنگەن اﷲ تىن قورقۇڭلار'),
(5162, 60, 12, 'ئى پەيغەمبەر! مۆمىن ئاياللار ساڭا كېلىپ، اﷲ قا ھېچ نەرسىنى شېرىك كەلتۈرمەسلىككە، ئوغرىلىق قىلماسلىققا، زىنا قىلماسلىققا، بالىلىرىنى (جاھىلىيەت دەۋرىدىكىدەك قىز بالىلىرىنى نومۇستىن ياكى كەمبەغەللىكتىن قورقۇپ) ئۆلتۈرمەسلىككە، باشقىلارنىڭ بالىسىنى يالغاندىن ئەرلىرىنىڭ بالىسى قىلىۋالماسلىققا، سەن بۇيرۇغان ياخشى ئىشلاردىن باش تارتماسلىققا بەيئەت قىلسا (يۇقىرىقى شەرتلەر ئاساسىدا) ئۇلارغا بەيئەت قىلغىن، ئۇلار ئۈچۈن اﷲ تىن مەغپىرەت تىلىگىن، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(5163, 60, 13, 'ئى مۆمىنلەر! اﷲ غەزەپ قىلغان قەۋمنى دوست تۇتماڭلار، ئۇلار ئاخىرەتنىڭ (ساۋابىدىن) قەبرىلىرىدە ياتقان كۇففارلار ئۈمىدىنى ئۈزگەندەك ھەقىقەتەن ئۈمىدىنى ئۈزدى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'ئاسمانلاردىكى نەرسىلەر (يەنى پەرىشتىلەر) ۋە زېمىندىكى نەرسىلەر (يەنى ئىنسانلار، ئۆسۈملۈكلەر، جانسىز شەيئىلەر) اﷲ قا تەسبىھ ئېيتتى، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(5165, 61, 2, 'ئى مۆمىنلەر! سىلەر نېمە ئۈچۈن قىلمايدىغان ئىشنى قىلىمىز دەيسىلەر؟ (يەنى ئەمەلدە سىلەر قىلمايدىغان ياخشى ئىشلارنى نېمە ئۈچۈن ئاغزىڭلاردا قىلىمىز دەيسىلەر؟)'),
(5166, 61, 3, 'سىلەرنىڭ قىلمايدىغان ئىشنى (قىلىمىز) دېيىشىڭلار اﷲ نىڭ دەرگاھىدا ئەڭ ئۆچ (كۆرۈلىدىغان نەرسىدۇر)'),
(5167, 61, 4, 'شۈبھىسىزكى، اﷲ ئۆزىنىڭ يولىدا مۇستەھكەم بىنادەك سەپ بولۇپ جىھاد قىلغانلارنى (يەنى ئۇرۇش مەيدانىدا دۈشمەنلەرگە ئۇچراشقان چاغدا مەۋقەسىدە ساباتلىق تۇرۇپ غازات قىلغانلارنى) دوست تۇتىدۇ'),
(5168, 61, 5, 'ئۆز ۋاقتىدا مۇسا قەۋمىگە: «ئى قەۋمىم! مېنىڭ اﷲ نىڭ سىلەرگە ئەۋەتكەن پەيغەمبەر ئىكەنلىكىمنى بىلىپ تۇرۇپ سىلەر ماڭا نېمىشقا ئازار بېرىسىلەر؟» دېدى، ئۇلار (ھەقتىن) بۇرۇلۇپ كەتكەندىن كېيىن، اﷲ ئۇلارنىڭ دىللىرىنى ھىدايەتتىن بۇرىۋەتتى، اﷲ پاسىق قەۋمنى ھىدايەت قىلمايدۇ'),
(5169, 61, 6, 'ئۆز ۋاقتىدا مەريەمنىڭ ئوغلى ئىسا: «ئى ئىسرائىل ئەۋلادى! مەن سىلەرگە ھەقىقەتەن اﷲ ئەۋەتكەن، مەندىن بۇرۇن كەلگەن تەۋراتنى تەستىق قىلغۇچى، مەندىن كېيىن كېلىدىغان ئەھمەد ئىسىملىك پەيغەمبەر بىلەن خۇش خەۋەر بەرگۈچى پەيغەمبەرمەن» دېدى. ئىسا ئۇلارغا روشەن مۆجىزىلەرنى ئېلىپ كەلگەن چاغدا، ئۇلار: «بۇ ئوپئوچۇق سېھىردۇر» دېدى'),
(5170, 61, 7, 'ئىسلامغا دەۋەت قىلىنىۋېتىپ، اﷲ قا يالغاننى (يەنى اﷲ نىڭ شېرىكى بار، بالىسى بار دېگەندەك سۆزلەرنى) چاپلىغان كىشىدىنمۇ زالىم كىشى بارمۇ؟ اﷲ زالىم (يەنى كاپىر) قەۋمنى ھىدايەت قىلمايدۇ'),
(5171, 61, 8, 'ئۇلار اﷲ نىڭ نۇرىنى (يەنى اﷲ نىڭ دىنىنى ۋە نۇرلۇق شەرىئىتىنى) ئېغىزلىرى بىلەن ئۆچۈرۈۋەتمەكچى بولىدۇ، كاپىرلار يامان كۆرگەن تەقدىردىمۇ، اﷲ ئۆزىنىڭ نۇرىنى مۇكەممەل قىلغۇچىدۇر (يەنى ئۆزىنىڭ دىنىنى ئاشكارىلىغۇچىدۇر'),
(5172, 61, 9, 'مۇشرىكلار يامان كۆرگەن تەقدىردىمۇ، اﷲ ھەق دىننى بارلىق دىنلار ئۈستىدىن غالىب قىلىش ئۈچۈن پەيغەمبىرىنى ھىدايەت بىلەن ۋە ھەق دىن بىلەن ئەۋەتتى'),
(5173, 61, 10, 'ئى مۆمىنلەر! سىلەرگە سىلەرنى قاتتىق ئازابتىن قۇتۇلدۇرىدىغان بىر تىجارەتنى كۆرسىتىپ قويايمۇ؟'),
(5174, 61, 11, 'اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە (چىن) ئىمان ئېيتقايسىلەر، اﷲ نىڭ يولىدا مېلىڭلار بىلەن، جېنىڭلار بىلەن جىھاد قىلغايسىلەر، ئەگەر بىلسەڭلار، سىلەر ئۈچۈن بۇ ياخشىدۇر'),
(5175, 61, 12, '(شۇنداق قىلساڭلار) اﷲ سىلەرنىڭ گۇناھلىرىڭلارنى مەغپىرەت قىلىدۇ، سىلەرنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، دائىمىي تۇرالغۇ بولغان جەننەتلەردىكى گۈزەل سارايلاردا (تۇرغۇزىدۇ)، بۇ چوڭ مۇۋەپپەقىيەتتۇر'),
(5176, 61, 13, 'سىلەرگە (مەرھەمەت قىلىنىدىغان) سىلەر ياخشى كۆرۈدىغان ئىككىنچى بىر نېمەت اﷲ تىن كېلىدىغان نۇسرەت ۋە يېقىن غەلىبە (يەنى مەككىنىڭ پەتھى قىلىنىشى) دۇر، (ئى مۇھەممەد!) مۆمىنلەرگە (بۇ روشەن مەرھەمەت بىلەن) خۇش خەۋەر بەرگىن'),
(5177, 61, 14, 'ئى مۆمىنلەر! ھەۋارىيۇنلار (يەنى ئىسا ئەلەيھىسسالامغا دەسلەپتە ئىمان ئېيتقان مۆمىن قەۋم) دەك اﷲ نىڭ ياردەمچىلىرى بۇلۇڭلار، مەريەمنىڭ ئوغلى ئىسا ھەۋارىيۇنلارغا: «(اﷲ نىڭ دەۋىتىنى تەبلىغ قىلىشتا) اﷲ قا كىملەر ياردەمچىلىرىم بولىدۇ؟» دېدى، ھەۋارىيۇنلار: «بىز اﷲ نىڭ ياردەمچىلىرىمىز» دېدى، ئىسرائىل ئەۋلادىدىن بىر تائىپە ئىمان ئېيتتى، يەنە بىر تائىپە كاپىر بولدى، مۆمىنلەرگە ئۇلارنىڭ ئۆز دۈشمەنلىرىگە قارشى تۇرۇشىدا يار - يۆلەكتە بولدۇق، شۇنىڭ بىلەن مۆمىنلەر غالىب بولدى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'اﷲ قا ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر تەسبىھ ئېيتىدۇ. اﷲ پادىشاھى (مۇتلەقتۇر)، (ھەممە نۇقسانلاردىن) پاكتۇر، غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(5179, 62, 2, 'اﷲ ئۈممىلەرگە (يەنى يېزىشنى ۋە ئوقۇشنى ئۇقمايدىغان ئەرەبلەرگە) ئۇلارنىڭ ئىچىدىن بىر پەيغەمبەرنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) ئەۋەتتى، ئۇ (يەنى پەيغەمبەر) ئۇلارغا قۇرئان ئايەتلىرىنى ئوقۇپ بېرىدۇ، ئۇلارنى (گۇناھلاردىن) پاك قىلىدۇ، ئۇلارغا كىتابنى (يەنى قۇرئاننى) ۋە ھېكمەتنى (يەنى يەنى پەيغەمبەرنىڭ سۈننىتىنى) ئۆگىتىدۇ، گەرچە ئۇلار ئىلگىرى ئوچۇق گۇمراھلىقتا بولسىمۇ'),
(5180, 62, 3, 'ھەمدە (پەيغەمبەرنى) تېخى ئۇلارنىڭ زامانىدا بولمىغان (كېيىن كېلىدىغان) ئىككىنچى بىر قەۋمگە (يەنى قىيامەتكىچە كېلىدىغان مۇسۇلمانلارغا) ئەۋەتتى (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ پەيغەمبەرلىكى زامانداشلىرىغىلا ئەمەس، بەلكى قىيامەتكىچە كېلىدىغان كىشىلەرگىمۇ ئومۇمىيدۇر)، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(5181, 62, 4, 'مانا بۇ اﷲ نىڭ پەزلىدۇركى، ئۇنى خالىغان ئادەمگە بېرىدۇ، اﷲ ئۇلۇغ پەزل ئىگىسىدۇر'),
(5182, 62, 5, 'تەۋراتنى كۆتۈرۈشكە (يەنى ئەمەل قىلىشقا) تەكلىپ قىلىنغان، ئاندىن ئۇنى كۆتۈرمىگەنلەر (يەنى تەۋرات بېرىلگەن ۋە ئۇنىڭغا ئەمەل قىلىشقا تەكلىپ بولسىمۇ، ئۇنىڭغا ئەمەل قىلمىغان ۋە ھىدايەتتىن پايدىلانمىغان يەھۇدىيلار) خۇددى كىتاب يۈكلەنگەن ئېشەككە ئوخشايدۇ (يەنى پايدىلىق چوڭ - چوڭ كىتابلارنى ئۈستىگە ئارتىۋالغان، چارچاشتىن باشقا، ئۇلاردىن ھېچقانداق پايدىلىنالمايدىغان ئېشەككە ئوخشايدۇ)، اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغان قەۋمنىڭ مىسالى نېمىدېگەن يامان! اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ'),
(5183, 62, 6, '(ئى مۇھەممەد!) ئېيتقىنكى، «ئى يەھۇدىيلار! ئەگەر سىلەر باشقىلارنى ئەمەس، پەقەت ئۆزۈڭلارنىلا اﷲ نىڭ دوستلىرى دەپ دەۋا قىلىدىغان بولساڭلار، (ئاخىرەتنىڭ راھىتىگە تېزرەك يېتىش ئۈچۈن) ئۆلۈمنى ئارزۇ قىلىپ بېقىڭلار، ئەگەر (بۇ دەۋايىڭلاردا) راستچىل بولساڭلار»'),
(5184, 62, 7, 'ئۇلار قىلغان گۇناھلىرى تۈپەيلىدىن، ئۆلۈمنى ھەرگىز ئارزۇ قىلمايدۇ، اﷲ زالىملارنى بىلگۈچىدۇر'),
(5185, 62, 8, '(ئى مۇھەممەد!) «سىلەر قاچىدىغان ئۆلۈم چوقۇم سىلەرگە يولۇقىدۇ، ئاندىن سىلەر غەيبنى ۋە ئاشكارىنى بىلگۈچى (اﷲ) نىڭ دەرگاھىغا قايتۇرىسىلەر، اﷲ سىلەرگە قىلمىشىڭلارنى ئېيتىپ بېرىدۇ» دېگىن'),
(5186, 62, 9, 'ئى مۆمىنلەر جۈمە كۈنى جۈمە نامىزىغا ئەزان ئېيتىلسا، اﷲ نى ياد ئېتىشكە (يەنى جۈمە خۇتبىسىنى ئاڭلاشقا ۋە جۈمە نامىزىنى ئادا قىلىشقا) ئالدىراپ بېرىڭلار، ئېلىم - سېتىمنى قويۇپ تۇرۇڭلار، ئەگەر بىلسەڭلار بۇ سىلەر ئۈچۈن ياخشىدۇر'),
(5187, 62, 10, 'ناماز ئوقۇلۇپ بولغاندا (يەنى نامازدىن فارىغ بولغىنىڭلاردىن كېيىن) زېمىنغا تارىلىپ (يەنى ئۆز مەشغۇلاتىڭلار بىلەن بولۇپ)، اﷲ نىڭ پەزلىدىن تەلەپ قىلىڭلار، مەقسىتىڭلارغا ئېرىشىش ئۈچۈن اﷲ نى كۆپ ياد ئېتىڭلار'),
(5188, 62, 11, '(ئى مۇھەممەد!) ئۇلار بىرەر تىجارەتنى ياكى تاماشانى كۆرسە، سېنى ئۆرە تۇرۇپ (خۇتبە ئوقۇۋاتقان) پېتىڭ تاشلاپ، ئۇنىڭغا (يەنى تىجارەتكە) يۈگۈرىدۇ، (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، اﷲ نىڭ دەرگاھىدىكى نەرسە (يەنى ساۋاب ۋە نېمەت) تاماشادىن ۋە تىجارەتتىن ياخشىدۇر، اﷲ رىزىق بەرگۈچىلەرنىڭ ئەڭ ياخشىسىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'مۇناپىقلار سېنىڭ ئالدىڭغا كەلگەن چاغدا: «گۇۋاھلىق بېرىمىزكى سەن ھەقىقەتەن اﷲ نىڭ پەيغەمبىرىدۇرسەن» دەيدۇ، سېنىڭ ھەقىقەتەن اﷲ نىڭ پەيغەمبىرى ئىكەنلىكىڭنى اﷲ بىلىدۇ، اﷲ گۇۋاھلىق بېرىدۇكى، مۇناپىقلار چوقۇم يالغانچىلاردۇر'),
(5190, 63, 2, 'ئۇلار ئۆزلىرىنىڭ يالغان قەسەملىرىنى قالقان قىلىۋالدى، (كىشىلەرنى) اﷲ نىڭ يولىدىن (يەنى اﷲ نىڭ دىنىدىن) توستى، ئۇلارنىڭ قىلمىشى نېمىدېگەن يامان!'),
(5191, 63, 3, 'بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار (ئېغىزلىرىدا) ئىشىنىپ، ئاندىن (دىللىرىدا) ئىنكار قىلدى، ئۇلارنىڭ دىللىرى پېچەتلەندى (يەنى دىللىرىغا ھىدايەت يېتىپ بارالمايدۇ)، ئۇلار (ئىماننى) چۈشەنمەيدۇ'),
(5192, 63, 4, 'ئۇلارغا قارايدىغان بولساڭ، بەدەن قۇرۇلۇشى (سالاپەتلىكى) سېنى ھەيران قالدۇرىدۇ، ئەگەر ئۇلار سۆزلىسە، (سۆزىنىڭ پاساھەتلىكىدىن) سۆزىگە قۇلاق سالىسەن، (ئىلىمسىز، ئەقىلسىز، پاراسەتسىزلىكتە) گويا ئۇلار تامغا يۆلەپ قويۇلغان (چىرىپ قالغان) ياغاچلاردۇر، ئۇلار (قورقۇنچتىن) ھەرقانداق ئاۋازنى ئۆزلىرىگە قارىتىلغان دەپ گۇمانلىنىدۇ، ئۇلار (ساڭا ۋە مۆمىنلەرگە) دۈشمەندۇر، ئۇلاردىن ھەزەر ئەيلىگىن، اﷲ ئۇلارنى ھالاك قىلسۇن! ئۇلار قانداقمۇ (ھىدايەتتىن گۇمراھلىققا) بۇرۇلۇپ كېتىدۇ!'),
(5193, 63, 5, 'ئۇلارغا ئۆزرە ئېيتىپ: «كېلىڭلار، رەسۇلۇللاھ سىلەرگە مەغپىرەت تىلەيدۇ» دېيىلسە، (مەسخىرە قىلىش يۈزىسىدىن) باشلىرىنى سىلكىيدۇ، ئۇلارنىڭ تەكەببۇرلۇق قىلغان ھالدا (دەۋەت قىلىنغان نەرسىدىن) يۈز ئۆرۈگەنلىكىنى كۆرۈسەن'),
(5194, 63, 6, 'ئۇلارغا (مەيلى) مەغپىرەت تەلەپ قىلغىن، (مەيلى) مەغپىرەت تەلەپ قىلمىغىن، بەرىبىر ئوخشاش، اﷲ ئۇلارغا ھەرگىز مەغپىرەت قىلمايدۇ، شۈبھىسىزكى پاسىق قەۋمنى اﷲ ھىدايەت قىلمايدۇ'),
(5195, 63, 7, 'ئۇلار: «رەسۇلۇللاھ نىڭ يېنىدىكى كىشىلەرنى تەمىنلىمەڭلار، ئۇلار تاراپ كەتسۇن» دەيدۇ، ئاسمانلارنىڭ ۋە زېمىننىڭ خەزىنىلىرى اﷲ نىڭ ئىلكىدىدۇر. ۋە لېكىن مۇناپىقلار (اﷲ نىڭ ھېكمىتىنى) چۈشەنمەيدۇ'),
(5196, 63, 8, 'ئۇلار (يەنى مۇناپىقلار): «مەدىنىگە قايتساق، ئەڭ ئەزىز ئادەم (يەنى ئابدۇللاھ ئىبن ئۇبەي ۋە مۇناپىقلارنىڭ ئۆزلىرىنى دېمەكچى) ئەڭ خار ئادەمنى (يەنى رەسۇلۇللاھنى، مۆمىنلەرنى دېمەكچى) مەدىنىدىن چىقىرىۋېتىدۇ» دېيىشىدۇ. غەلىبە اﷲ قا، اﷲ نىڭ پەيغەمبىرىگە ۋە مۆمىنلەرگە مەنسۇپ، لېكىن مۇناپىقلار (غەلىبىنىڭ اﷲ نىڭ دۈشمەنلىرىگە ئەمەس، دوستلىرىغا مەنسۇپ ئىكەنلىكىنى) بىلمەيدۇ'),
(5197, 63, 9, 'ئى مۆمىنلەر! ماللىرىڭلار ۋە بالىلىرىڭلار سىلەرنى اﷲ نىڭ زىكرىدىن (يەنى اﷲ نىڭ تائەت - ئىبادىتىدىن) غەپلەتتە قالدۇرمىسۇن، كىملەركى شۇنداق قىلىدىكەن، ئۇلار زىيان تارتقۇچىلاردۇر'),
(5198, 63, 10, 'بېرىڭلارغا ئۆلۈم كېلىپ: «پەرۋەردىگارىم! نېمىشقا مېنى (يەنى ئەجىلىمنى) بىر ئاز كېچىكتۈرمىدىڭ، سەدىقە قىلىپ ياخشىلاردىن بولار ئىدىم» دېيىشتىن بۇرۇن، ئۆزۈڭلارغا رىزىق قىلىپ بېرىلگەن نەرسىلەرنى سەدىقە قىلىڭلار'),
(5199, 63, 11, 'اﷲ ھېچ ئادەمنى ئەجىلى كەلگەندە ھەرگىز كېچىكتۈرمەيدۇ، اﷲ قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەرداردۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر اﷲ قا تەسبىھ ئېيتىدۇ، پادىشاھلىق اﷲ قا مەنسۇپتۇر، جىمى ھەمدۇسانا اﷲ قا خاستۇر، اﷲ ھەر نەرسىگە قادىردۇر'),
(5201, 64, 2, 'اﷲ سىلەرنى خەلق ئەتتى، ئاراڭلاردا مۆمىنلەرمۇ بار، كاپىرلارمۇ بار، اﷲ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر'),
(5202, 64, 3, 'اﷲ ئاسمانلارنى ۋە زېمىننى ھەق ياراتتى، سىلەرنى سۈرەتكە كىرگۈزدى، سۈرىتىڭلارنى چىرايلىق قىلدى، ئاخىر قايتىدىغان جاي ئۇنىڭ دەرگاھىدۇر'),
(5203, 64, 4, 'اﷲ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى بىلىپ تۇرىدۇ، اﷲ (نىيەتلىرىڭلاردىن، ئەمەللىرىڭلاردىن) يوشۇرغانلىرىڭلارنى ۋە ئاشكارىلىغانلىرىڭلارنى بىلىپ تۇرىدۇ، اﷲ دىللاردىكى سىرلارنى بىلگۈچىدۇر'),
(5204, 64, 5, 'سىلەرگە ئىلگىرى كاپىر بولۇپ ئەمەللىرىنىڭ ۋابالىنى تېتىغانلارنىڭ خەۋىرى كەلمىدىمۇ؟ ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ'),
(5205, 64, 6, 'بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەر بىلەن كەلگەن ئىدى، ئۇلار: «ئىنسان بىزنى ھىدايەت قىلالامدۇ؟» دېدى. (پەيغەمبەرنى) ئىنكار قىلدى، (ئىماندىن) يۈز ئۆرۈدى. اﷲ (ئۇلارنىڭ ئىمانىغا) موھتاج ئەمەستۇر، اﷲ مەخلۇقاتتىن بىھاجەتتۇر، مەدھىيىگە لايىقتۇر'),
(5206, 64, 7, 'كاپىرلار ھەرگىز تىرىلدۈرۈلمەيمىز دەپ گۇمان قىلىشتى، ئېيتقىنكى: «ھەرگىز ئۇنداق ئەمەس، پەرۋەردىگارىم بىلەن قەسەمكى، سىلەر چوقۇم تىرىلدۈرىسىلەر، ئاندىن سىلەرگە قىلمىشىڭلاردىن خەۋەر بېرىلىدۇ، بۇ اﷲ قا ئاساندۇر»'),
(5207, 64, 8, 'اﷲ قا، اﷲ نىڭ پەيغەمبىرىگە ۋە بىز نازىل قىلغان نۇرغا (يەنى قۇرئانغا) ئىمان ئېيتىڭلار، اﷲ قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەرداردۇر'),
(5208, 64, 9, 'ئۇ كۈندە اﷲ سىلەرنى قىيامەت ئۈچۈن توپلايدۇ، ئۇ زىيان تارتىدىغان كۈندۇر (يەنى ئۇ كۈندە ئىماننى تەرك ئەتكەنلىكى بىلەن كاپىرنىڭ زىيان تارتقانلىقى، ياخشى ئەمەللەرنى دېگەندەك قىلالمىغانلىقى بىلەن مۆمىننىڭ زىيان تارتقانلىقى ئاشكارا بولىدۇ). كىمكى اﷲ قا ئىمان ئېيتىپ ياخشى ئىش قىلىدىكەن، اﷲ ئۇنىڭ يامان ئىشلىرىنى يوققا چىقىرىدۇ، ئۇنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇلار ئۇ جەننەتلەردە مەڭگۈ قالىدۇ، بۇ كاتتا مۇۋەپپەقىيەتتۇر'),
(5209, 64, 10, 'اﷲ نىڭ (بىرلىكىنى ۋە قۇدرىتىنى) ئىنكار قىلغانلار، ئايەتلىرىمىزنى يالغانغا چىقارغانلار ئەھلى دوزاخ بولۇپ، ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇ نېمىدېگەن يامان جاي!'),
(5210, 64, 11, 'كىشىگە يەتكەن ھەر قانداق مۇسىبەت پەقەت اﷲ نىڭ ئىزنى (يەنى قازاسى) بىلەنلا (يېتىدۇ)، كىمكى اﷲ قا (يەنى ھەر قانداق مۇسىبەت اﷲ نىڭ قازاسى بىلەن يېتىدۇ دېگەن سۆزگە) ئىشىنىدىكەن، اﷲ ئۇنىڭ قەلبىنى (سەۋرگە) يېتەكلەيدۇ، اﷲ ھەر نەرسىنى بىلگۈچىدۇر'),
(5211, 64, 12, 'اﷲ قا ئىتائەت قىلىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار، ئەگەر (پەيغەمبەرنىڭ دەۋىتىدىن) يۈز ئۆرۈسەڭلار (پەيغەمبەرگە زىيان يوق، ئۆزۈڭلارغا زىيان)، پەيغەمبىرىمىزنىڭ ۋەزىپىسى پەقەت (دەۋەتنى) روشەن تەبلىغ قىلىشتۇر'),
(5212, 64, 13, 'اﷲ تىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، مۆمىنلەر اﷲ قا تەۋەككۈل قىلسۇن'),
(5213, 64, 14, 'ئى مۆمىنلەر! سىلەرنىڭ ئاياللىرڭلاردىن، بالىلىرىڭلاردىن سىلەرگە دۈشمەن بولىدىغانلىرىمۇ بار، ئۇلاردىن ئېھتىيات قىلىڭلار، ئەگەر (ئۇلارنى) ئەپۇ قىلساڭلار، كەچۈرسەڭلار، مەغپىرەت قىلساڭلار (اﷲ سىلەرنىمۇ مەغپىرەت قىلىدۇ). اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(5214, 64, 15, 'سىلەرنىڭ ماللىرىڭلار، بالىلىرىڭلار (سىلەرگە اﷲ تەرىپىدىن) بىر تۈرلۈك سىناقتۇر، اﷲ نىڭ دەرگاھىدا كاتتا ساۋاب بار'),
(5215, 64, 16, 'تاقىتىڭلارنىڭ يېتىشىچە اﷲ قا تەقۋادارلىق قىلىڭلار، (ۋەزىنى) ئاڭلاڭلار، (ئەمرىگە) ئىتائەت قىلىڭلار، (اﷲ نىڭ يولىدا ماللىرىڭلارنى) سەرپ قىلىڭلار، (بۇ) ئۆزۈڭلار ئۈچۈن پايدىلىقتۇر، كىمكى نەپسىنىڭ بېخىللىقىدىن ساقلىنىدىكەن، ئۇ مەقسىتىگە ئېرىشكۈچىدۇر'),
(5216, 64, 17, 'اﷲ قا قەرزىي ھەسەنە بەرسەڭلار (يەنى مەمنۇنىيەت بىلەن سەدىقە بەرسەڭلار) اﷲ سىلەرگە ئۇنىڭ (ساۋابىنى) ھەسسىلەپ بېرىدۇ، سىلەرگە مەغپىرەت قىلىدۇ، اﷲ ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر، ھەلىمدۇر (يەنى بەندىلىرىنى ئازابلاشقا ئالدىراپ كەتمەيدۇ)'),
(5217, 64, 18, '(اﷲ) غەيبنى ۋە ئاشكارىنى بىلگۈچىدۇر، غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'ئى پەيغەمبەر! (ئۈممىتىڭگە ئېيتقىنكى) سىلەر ئاياللارنى تالاق قىلساڭلار، ئۇلارنىڭ ئىددىتىگە (لايىق پەيتتە يەنى پاكلىق ھالىتىدە) تالاق قىلىڭلار. (نەسەب ئارىلىشىپ كەتمەسلىكى ئۈچۈن) ئىددەتنى ساناڭلار، پەرۋەردىگارىڭلاردىن قورقۇڭلار، (ئۇلارنىڭ ئىددىتى توشمىغۇچە) ئۇلارنى ئۆيلىرىدىن چىقىرىۋەتمەڭلار، ئۇلارمۇ (ئۆيىدىن) چىقمىسۇن، ئۇلار پەقەت ئوچۇق پاھىشە قىلسىلا ئاندىن (ھەدنى ئىجرا قىلىش ئۈچۈن) چىقىدۇ، ئەنە شۇ اﷲ نىڭ قانۇنىدۇر، كىمكى اﷲ نىڭ قانۇنىدىن ھالقىپ كېتىدىكەن، ئۇ ئۆزىگە زۇلۇم قىلغان بولىدۇ، سەن بىلمەيسەنكى، اﷲ شۇنىڭدىن كېيىن (يەنى تالاقتىن كېيىن) بىرەر ئىشنى مەيدانغا كەلتۈرۈشى مۇمكىن'),
(5219, 65, 2, 'ئۇلارنىڭ ئىددىتى توشقان چاغدا، ئۇلار بىلەن چىرايلىقچە يېنىشىڭلار ياكى ئۇلار بىلەن چىرايلىقچە ئۈزلىشىپ كېتىڭلار، (يېنىشقان، ئاجراشقان چېغىڭلاردا) ئاراڭلاردىن ئىككى ئادىل گۇۋاھچىنى گۇۋاھ قىلىڭلار، خۇدالىق ئۈچۈن (توغرا) گۇۋاھ بولۇڭلار، بۇنىڭ بىلەن (يەنى بۇ ھۆكۈم بىلەن) اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقان كىشىگە ۋەز - نەسىھەت قىلىنىدۇ، كىمكى اﷲ تىن قورقىدىكەن، اﷲ ئۇنىڭغا چىقىش يولى بېرىدۇ'),
(5220, 65, 3, 'اﷲ ئۇنىڭغا ئويلىمىغان يەردىن رىزىق بېرىدۇ، كىمكى اﷲ قا تەۋەككۈل قىلسا، اﷲ ئۇنىڭغا كۇپايە قىلىدۇ، اﷲ ھەقىقەتەن مەقسىتىگە يېتەلەيدۇ، اﷲ ھەقىقەتەن ھەر بىر نەرسە ئۈچۈن مۇئەييەن مىقدار، مۇئەييەن ۋاقىت بەلگىلىدى'),
(5221, 65, 4, 'ئاياللىرىڭلارنىڭ ئىچىدىكى ھەيزدىن ئۈمىدىنى ئۈزگەنلەردىن (يەنى يېشى چوڭ بولغانلىقتىن ھەيز كۆرمەيدىغانلارنىڭ ئىددىتىنىڭ قانچىلىك بولىدىغانلىقىدىن) شۈبھىدە بولساڭلار، ئۇلارنىڭ ئىددىتى ئۈچ ئايدۇر، (كىچىكلىكتىن) ھەيز كۆرمىگەنلەرنىڭ ئىددىتى ھەم ئۈچ ئايدۇر، ھامىلىدار ئاياللارنىڭ ئىددىتى (مەيلى قويۇۋېتىلگەن بولسۇن، مەيلى ئېرى ئۆلۈپ كەتكەن بولسۇن) بوشىنىش بىلەنلا تۈگەيدۇ، كىمكى اﷲ تىن قورقىدىكەن، اﷲ ئۇنىڭ ئىشىنى ئاسانلاشتۇرۇپ بېرىدۇ'),
(5222, 65, 5, 'ئەنە شۇ اﷲ نىڭ سىلەرگە نازىل قىلغان ھۆكمىدۇر، كىمكى اﷲ تىن قورقىدىكەن، اﷲ ئۇنىڭ گۇناھلىرىنى يوققا چىقىرىدۇ، ئۇنىڭغا كاتتا ساۋاب بېرىدۇ'),
(5223, 65, 6, 'ئۇلارنى (يەنى قويۇۋېتىلگەن ئاياللىرىڭلارنى) قۇدرىتىڭلارنىڭ يېتىشىچە ئۆزۈڭلار ئولتۇرۇۋاتقان جايىڭلارنىڭ بىر قىسمىدا ئولتۇرغۇزۇڭلار، ئۇلارنى تەڭلىكتە قالدۇرۇش ئۈچۈن زىيان يەتكۈزمەڭلار (يەنى ئۇلارنى چىقىپ كېتىشكە مەجبۇرلاش ئۈچۈن تۇرالغۇ جەھەتتىن، نەپىقە جەھەتتىن قىسماڭلار)، ئەگەر ئۇلار ھامىلىدار بولسا، بوشانغانغا قەدەر ئۇلارنى تەمىنلەڭلار، ئەگەر ئۇلار سىلەر ئۈچۈن (بالىلىرىڭلارنى) ئېمىتىپ بەرسە، ئۇلارنىڭ (ئېمىتىش) ھەققىنى بېرىڭلار، ھەققانىي ئاساستا ئۆزئارا مەسلىھەتلىشىڭلار، ئەگەر پىكىردە كېلىشەلمىسەڭلار، ئۇنى (يەنى بالىنى) باشقا بىر ئايال ئېمىتسۇن'),
(5224, 65, 7, 'باي ئادەم (ئايالىنى ۋە بالىسىنى) ئۆزىنىڭ بايلىقىغا يارىشا تەمىنلىسۇن، رىزقى تار قىلىنغان ئادەم اﷲ نىڭ ئۇنىڭغا بەرگىنىگە يارىشا تەمىنلىسۇن (يەنى ھەر ئادەم ئۆزىنىڭ ئىقتىسادىي ئەھۋالىغا يارىشا خىراجەت قىلسۇن). اﷲ ئىنساننى پەقەت تاقىتى يېتىدىغان ئىشقىلا تەكلىپ قىلىدۇ (يەنى پېقىر ئادەمنى باينى تەكلىپ قىلغان ئىشقا تەكلىپ قىلمايدۇ)، اﷲ قىيىنچىلىقتىن كېيىن ئاسانلىقنى بېرىدۇ (يەنى پېقىرلىقتىن كېيىن بايلىقنى بېرىدۇ)'),
(5225, 65, 8, 'نۇرغۇن شەھەرنىڭ ئاھالىلىرى پەرۋەردىگارىنىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئەمرىدىن باش تارتتى، ئۇلاردىن قاتتىق ھېساب ئالدۇق ۋە ئۇلارنى قاتتىق جازالىدۇق'),
(5226, 65, 9, 'ئۇلار قىلمىشلىرىنىڭ ۋابالىنى تېتىدى، ئۇلارنىڭ قىلمىشلىرىنىڭ ئاقىۋىتى زىيان تارتىشتىن ئىبارەت بولدى'),
(5227, 65, 10, 'اﷲ ئۇلارغا (ئاخىرەتتە) قاتتىق ئازاب تەييارلىدى، ئى ئىمان ئېيتقان ئەقىل ئىگىلىرى! اﷲ تىن قورقۇڭلار، اﷲ سىلەرگە قۇرئاننى نازىل قىلدى'),
(5228, 65, 11, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى قاراڭغۇلۇقتىن نۇرغا چىقىرىش ئۈچۈن سىلەرگە اﷲ نىڭ روشەن ئايەتلىرىنى تىلاۋەت قىلىپ بېرىدىغان پەيغەمبەرنى ئەۋەتتى، كىمكى اﷲ قا ئىمان ئېيتىدىكەن ۋە ياخشى ئەمەل قىلىدىكەن، (اﷲ) ئۇنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ، اﷲ ئۇلارغا ئېسىل رىزىق (يەنى جەننەتتىكى رىزىق ۋە نېمەتلەرنى) بېرىدۇ'),
(5229, 65, 12, 'اﷲ يەتتە ئاسماننى ياراتتى، زېمىننىمۇ ئاسمانلارغا ئوخشاش (يەنى يەتتە قىلىپ) ياراتتى، اﷲ نىڭ ئەمرى ئۇلارنىڭ ئارىسىدا جارى بولۇپ تۇرىدۇ، (سىلەرگە بۇنى بىلدۈرۈشۈم) اﷲ نىڭ ھەممىگە قادىر ئىكەنلىكىنى ۋە اﷲ نىڭ ھەممە نەرسىنى تولۇق بىلىدىغانلىقىنى بىلىشىڭلار ئۈچۈندۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'ئى پەيغەمبەر! نېمە ئۈچۈن اﷲ ساڭا ھالال قىلغان نەرسىنى ئاياللىرىڭنىڭ رازىلىقىنى تىلەش يۈزىسىدىن ھارام قىلىسەن، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر'),
(5231, 66, 2, 'اﷲ سىلەرگە (كەففارەت) بىلەن قەسىمىڭلاردىن چىقىشنى بەلگىلىدى، اﷲ سىلەرنىڭ ئىگەڭلاردۇر، اﷲ ھەر نەرسىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(5232, 66, 3, 'ئۆز ۋاقتىدا پەيغەمبەر ئاياللىرىدىن بىرىگە (يەنى ھەفسەگە) بىر سۆزنى يوشۇرۇن ئېيتقان ئىدى، ئۇ (يەنى ھەفسە) ئۇنى (ئائىشەگە) ئېيتىپ قويغان، اﷲ بولسا سىرنىڭ ئاشكارىلانغانلىقىنى (پەيغەمبەرگە) بىلدۈرگەن چاغدا، ئۇ (يەنى پەيغەمبەر) ئۇنىڭغا (ھەفسەگە) ئۇ (يەنى ھەفسە) ئاشكارىلاپ قويغان سۆزلەرنىڭ بەزىسىنى ئېيتتى (يەنى يۈزىگە سالدى). بەزىسىنى ئېيتمىدى (يەنى يۈزىگە سالمىدى). پەيغەمبەر ئۇنىڭغا سىرنى ئاشكارىلاپ قويغانلىقىنى ئېيتقان چاغدا، ئۇ (يەنى ھەفسە): «بۇنى ساڭا كىم خەۋەر قىلدى؟» دېدى، پەيغەمبەر: «ماڭا (بۇنى) ھەممىنى بىلىپ تۇرغۇچى، ھەممىدىن تولۇق خەۋەردار اﷲ خەۋەر قىلدى» دېدى'),
(5233, 66, 4, 'ئەگەر (ئائىشە، ھەفسە) ئىككىڭلار اﷲ قا تەۋبە قىلساڭلار (ئۆزۈڭلار ئۈچۈن ياخشىدۇر). سىلەرنىڭ دىلىڭلار (رەسۇلۇللاھ قا ئىخلاس قىلىشتىن) بۇرۇلۇپ كەتتى، ئەگەر سىلەر (رەسۇلۇ اﷲ خوپ كۆرمەيدىغان ئىش ئۈستىدە) ئۇنىڭغا تاقابىل تۇرساڭلار، ئۇ چاغدا اﷲ ئۇنىڭ مەدەتكارىدۇ، ئۇلاردىن باشقا پەرىشتىلەرمۇ (ئۇنىڭغا) يۆلەنچۈكتۇر'),
(5234, 66, 5, 'ئەگەر ئۇ سىلەرنى قويۇۋەتسە، پەرۋەردىگارى سىلەرنىڭ ئورنۇڭلارغا، سىلەردىن ياخشى، (اﷲ نىڭ ۋە پەيغەمبەرنىڭ ئەمرىگە) بويسۇنغۇچى، (اﷲ قا ۋە پەيغەمبەرگە) ئىمان ئېيتقۇچى، ئىتائەت قىلغۇچى، گۇناھلاردىن تۆۋبە قىلغۇچى، ئىبادەت قىلغۇچى، روزا تۇتقۇچى چوكانلارنى ۋە قىزلارنى ئۇنىڭغا بېرىشى مۇمكىن'),
(5235, 66, 6, 'ئى مۆمىنلەر! ئۆزۈڭلارنى ۋە بالا - چاقاڭلارنى ئىنسان ۋە تاشلار يېقىلغۇ بولىدىغان، رەھىم قىلمايدىغان قاتتىق قول پەرىشتىلەر مۇئەككەل بولغان دوزاختىن ساقلاڭلار، ئۇ پەرىشتىلەر اﷲ نىڭ بۇيرۇقىدىن چىقمايدۇ، نېمىگە بۇيرۇلسا شۇنى ئىجرا قىلىدۇ'),
(5236, 66, 7, 'ئى كاپىرلار! بۈگۈن ئۆزرە ئېيتماڭلار، سىلەر پەقەت قىلمىشىڭلارنىڭ جازاسىنى تارتىسىلەر'),
(5237, 66, 8, 'ئى مۆمىنلەر اﷲ قا سەمىمىي تەۋبە قىلىڭلار، ئۈمىدكى، پەرۋەردىگارىڭلار سىلەرنىڭ گۇناھىڭلارنى يوققا چىقىرىدۇ، سىلەرنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇ كۈندە (يەنى قىيامەت كۈنىدە) اﷲ پەيغەمبەرنى ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنى رەسۋا قىلمايدۇ، ئۇلارنىڭ نۇرى ئۇلارنىڭ ئالدىدا ۋە ئوڭ تەرىپىدە يۈرىدۇ، ئۇلار: «ئى پەرۋەردىگارىمىز! بىزگە نۇرىمىزنى مۇكەممەللەشتۈرۈپ بەرگىن، بىزگە مەغپىرەت قىلغىن، سەن بولساڭ ھەقىقەتەن ھەر نەرسىگە قادىرسەن» دەيدۇ'),
(5238, 66, 9, 'ئى پەيغەمبەر! سەن كۇففارلار بىلەن ۋە مۇناپىقلار بىلەن كۈرەش قىلغىن، ئۇلارغا قاتتىق مۇئامىلە قىلغىن، ئۇلارنىڭ جايى جەھەننەمدۇر، جەھەننەم نېمىدېگەن يامان جاي!'),
(5239, 66, 10, 'اﷲ كۇففارلارغا (ئۇلارنىڭ مۆمىنلەر بىلەن تۇغقانچىلىقىنىڭ پايدا بەرمەسلىكىدە) نۇھنىڭ ئايالىنى ۋە لۇتنىڭ ئايالىنى مىسال قىلىپ كۆرسەتتى، ئۇلار بولسا بىزنىڭ بەندىلىرىمىزدىن ئىككى ياخشى بەندىمىزنىڭ ئەمرىدە ئىدى، ئۇلارنىڭ ئىككىلىسى ئەرلىرىگە (ئىمان ئېيتماسلىق بىلەن) خىيانەت قىلدى، ئۇلارنىڭ ئەرلىرى (يەنى نۇھ بىلەن لۇت) (پەيغەمبەر تۇرۇقلۇق) ئۇلاردىن اﷲ نىڭ ئازابىدىن ھېچ نەرسىنى دەپئى قىلالمىدى. (ئۇ ئىككىسىگە) «دوزاخقا كىرگۈچىلەر بىلەن بىللە كىرىڭلار» دېيىلدى'),
(5240, 66, 11, 'اﷲ ئىمان ئېيتقانلارغا پىرئەۋننىڭ ئايالىنى مىسال قىلىپ كۆرسەتتى، ئۆز ۋاقتىدا ئۇ: «ئى پەرۋەردىگارىم! دەرگاھىڭدا ماڭا جەننەتتىن بىر ئۆي بىنا قىلغىن، مېنى پىرئەۋندىن ۋە ئۇنىڭ يامان ئەمەلىدىن قۇتۇلدۇرغىن، مېنى زالىم قەۋمدىن قۇتۇلدۇرغىن» دېدى'),
(5241, 66, 12, 'ھەمدە ئىمراننىڭ قىزى مەريەمنى مىسال قىلىپ كۆرسەتتى، ئۇ نومۇسىنى ساقلىدى، ئۇنىڭغا بىزنىڭ تەرىپىمىزدىن بولغان روھنى پۈۋلىدۇق (دەم ئۇنىڭ ئىچىگە كىرىپ، ئىساغا ھامىلىدار بولدى)، ئۇ پەرۋەردىگارىنىڭ سۆزلىرىنى ۋە (نازىل قىلغان) كىتابلىرىنى تەستىق قىلدى ۋە اﷲ قا ئىتائەت قىلغۇچىلاردىن بولدى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, '(پۈتۈن ئاسمان - زېمىننىڭ) پادىشاھلىقى ئىلكىدە بولغان اﷲ نىڭ بەرىكىتى بۈيۈكتۇر. اﷲ ھەر نەرسىگە قادىردۇر'),
(5243, 67, 2, 'سىلەردىن قايسىڭلارنىڭ ئەمەلى ئەڭ ياخشى ئىكەنلىكىنى سىناش ئۈچۈن، اﷲ ئۆلۈمنى ۋە تىرىكلىكنى ياراتتى، اﷲ غالىبتۇر، (تەۋبە قىلغۇچىنى) ناھايىتى مەغپىرەت قىلغۇچىدۇر'),
(5244, 67, 3, 'اﷲ يەتتە ئاسماننى بىرىنى بىرىنىڭ ئۈستىدە قىلىپ ياراتتى، مېھرىبان اﷲ نىڭ يارىتىشىدا ھېچ نۇقساننى كۆرمەيسەن، سەن (ئاسمانلارغا) تەكرار قاراپ باققىنكى، بىرەر يوچۇقنى كۆرەمسەن'),
(5245, 67, 4, 'ئاندىن سەن يەنە ئىككى قېتىم قارىغىن. كۆزۈڭ (ئاسمانلاردا بىرەر نۇقساننى تېپىشتىن) ئۈمىد ئۈزگەن ۋە تالغان ھالدا قايتىدۇ'),
(5246, 67, 5, '(بىزگە بارلىق ئاسمانلاردىن) ئەڭ يېقىن ئاسماننى چىراغلار (يەنى يورۇق يۇلتۇزلار) بىلەن بېزىدۇق ۋە ئۇلارنى (پەرىشتىلەرنىڭ سۆزلىرىنى ئوغرىلىقچە تىڭشاشقا ئورۇنغان) شەيتانلارنى ئاتىدىغان نەرسە قىلدۇق (يەنى شەيتانلار يۇلتۇزلاردىن چىققان شولا بىلەن ئېتىلىدۇ)، شەيتانلارغا (ئاخىرەتتە) دوزاخ ئازابىنى تەييارلىدۇق'),
(5247, 67, 6, 'پەرۋەردىگارىنى ئىنكار قىلغانلارمۇ دوزاخ ئازابىغا دۇچار بولىدۇ. دوزاخ نېمىدېگەن يامان جاي!'),
(5248, 67, 7, 'ئۇلار دوزاخقا تاشلانغان چاغدا، قايناپ تۇرغان دوزاخنىڭ (ئېشەك ھاڭرىغاندەك) سەت ئاۋازىنى ئاڭلايدۇ'),
(5249, 67, 8, 'دوزاخ غەزەپتىن پارچىلىنىپ كېتىشكە تاس قالىدۇ. دوزاخقا ھەر قاچان بىر توپ ئادەم تاشلانغان چاغدا، دوزاخقا مۇئەككەل پەرىشتىلەر ئۇلاردىن: «سىلەرگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلمىگەنمىدى؟» دەپ سورايدۇ'),
(5250, 67, 9, 'ئۇلار: «ئۇنداق ئەمەس، بىزگە ھەقىقەتەن ئاگاھلاندۇرغۇچى كەلگەن، بىز (ئۇنى) ئىنكار قىلدۇق ھەمدە بىز: ‹اﷲ (ھېچ ئادەمگە ۋەھيىدىن) بىرەر نەرسە نازىل قىلىنغىنى يوق، سىلەر پەقەت چوڭقۇر ئازغۇنلۇقتا› دېدۇق» دەيدۇ'),
(5251, 67, 10, 'ئۇلار: «ئەگەر بىز (پەيغەمبەرنىڭ سۆزلىرىنى) ئاڭلىغان، ياكى چۈشەنگەن بولساق، ئەھلى دوزاخ قاتارىدا بولماس ئىدۇق» دەيدۇ'),
(5252, 67, 11, 'ئۇلار گۇناھىنى تونۇيدۇ، ئەھلى دوزاخ اﷲ نىڭ رەھمىتىدىن يىراق بولسۇن!'),
(5253, 67, 12, 'پەرۋەردىگارىدىن كۆرمەي تۇرۇپ قورققانلار مەغپىرەتكە ۋە كاتتا ساۋابقا ئېرىشىدۇ'),
(5254, 67, 13, 'سىلەر سۆزۈڭلارنى مەخپىي قىلىڭلار ياكى ئاشكارا قىلىڭلار (اﷲ ھامان ئۇنى بىلىپ تۇرىدۇ). اﷲ ھەقىقەتەن دىللاردىكى سىرلارنى بىلگۈچىدۇر'),
(5255, 67, 14, '(مەخلۇقاتنى) ياراتقان زات بىلمەمدۇ؟ ئۇ شەيئىلەرنىڭ نازۇك تەرەپلىرىنى بىلگۈچىدۇر، ھەممىدىن تولۇق خەۋەرداردۇر'),
(5256, 67, 15, 'اﷲ سىلەرگە زېمىننى مېڭىشقا ئاسان قىلدى، زېمىننىڭ ئەتراپىدا مېڭىڭلار. اﷲ نىڭ (بەرگەن) رىزقىدىن يەڭلار، سىلەر تىرىلگەندىن كېيىن اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر'),
(5257, 67, 16, '(ئى كۇففارلار!) ئاسماندىكى زاتنىڭ يەر تەۋرىگەن چاغدا سىلەرنى يەرگە يۇتقۇزۇۋېتىشىدىن قورقمامسىلەر؟'),
(5258, 67, 17, 'ياكى ئاسماندىكى زاتنىڭ سىلەرگە تاش ياغدۇرۇشىدىن قورقمامسىلەر؟ (سىلەر ئازابنى كۆرگەن چاغدا) مېنىڭ ئاگاھلاندۇرۇشۇمنىڭ قانداق ئىكەنلىكىنى (يەنى ھەق ئىكەنلىكىنى) بىلىسىلەر'),
(5259, 67, 18, 'ئۇلاردىن ئىلگىرى ئۆتكەنلەر (پەيغەمبەرلىرىنى) يالغانغا چىقاردى، (ئۇلارنى ئازاب چۈشۈرۈش بىلەن) ئەيىبلىگەنلىكىم قانداق ئىكەن؟'),
(5260, 67, 19, 'ئۇلار قاناتلىرىنى يايغان ۋە يىغقان ھالدا ئۈستىدە ئۇچۇپ كېتىۋاتقان قۇشلارغا (ئىبرەت كۆزى بىلەن) قارىمامدۇ؟ (قاناتلىرىنى كەرگەن ۋە يىغقان ھالدا) ئۈستىلىرىدە ئۇچۇۋاتقان قۇشلارنى (قاناتلىرىنى ئاچقان ۋە يىغقان چاغدا يەرگە چۈشۈپ كېتىشتىن) پەقەت اﷲ (قۇدرىتى بىلەن) تۇتۇپ تۇرىدۇ، اﷲ ھەقىقەتەن ھەر نەرسىنى كۆرۈپ تۇرغۇچىدۇر'),
(5261, 67, 20, 'سىلەرگە اﷲ ياردەم بەرگەندىن تاشقىرى، يەنە كىم سىلەرگە ياردەمچى قوشۇن بولالايدۇ، كاپىرلار پەقەت ئالدىنىش ئىچىدىدۇر (يەنى شەيتان ئۇلارنى ئازاب نازىل بولمايدۇ دەپ ئالدايدۇ)'),
(5262, 67, 21, 'ئەگەر اﷲ رىزقىنى تۇتۇپ قالسا، سىلەرگە كىم رىزىق بېرىدۇ، ئۇلار تەكەببۇرلۇقتا، ھەقتىن يىراقلىشىشتا ئەزۋەيلەپ كەتكەندۇر'),
(5263, 67, 22, 'زادى قايسى ئادەم يول تاپالايدۇ؟ بېشىنى ساڭگىلىتىپ ماڭغان ئادەممۇ؟ ياكى قەددىنى رۇس تۇتۇپ تۈز يولدا ماڭغان ئادەممۇ؟'),
(5264, 67, 23, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «اﷲ سىلەرنى ياراتتى، سىلەر ئۈچۈن قۇلاقنى، كۆزلەرنى، دىللارنى ياراتتى، سىلەر ئاز شۈكۈر قىلسىلەر»'),
(5265, 67, 24, 'ئېيتقىنكى، «اﷲ سىلەرنى يەر يۈزىدە يارىتىپ كۆپەيتتى، (ھېساب بېرىش ئۈچۈن) اﷲ نىڭ دەرگاھىغا توپلىنىسىلەر»'),
(5266, 67, 25, 'ئۇلار: «ئەگەر سىلەر راستچىل بولساڭلار، بۇ ۋەدە قاچان ئىشقا ئاشۇرۇلىدۇ» دەيدۇ'),
(5267, 67, 26, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «(بۇ ھەقتىكى) ئىلىم (يەنى قىيامەتنىڭ قاچان بولۇشى) اﷲ نىڭ دەرگاھىدىدۇر، مەن پەقەت ئوچۇق ئاگاھلاندۇرغۇچىمەن»'),
(5268, 67, 27, 'ئۇلار ئازابنىڭ ئۆزلىرىگە يېقىنلىقىنى كۆرگەن چاغدا يۈزلىرى قارىداپ كېتىدۇ، ئۇلارغا: «مانا بۇ سىلەر (دۇنيادىكى چېغىڭلاردا) تەلەپ قىلغان نەرسىدۇر» دېيىلىدۇ'),
(5269, 67, 28, '(ئى مۇھەممەد! سېنىڭ ئۆلۈمىڭنى ئارزۇ قىلىدىغان مۇشرىكلارغا) ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ! ئەگەر اﷲ (سىلەر ئارزۇ قىلغاندەك) مېنى ۋە مەن بىلەن بولغان (مۆمىنلەرنى) ھالاك قىلىدىغان بولسا، ياكى (بىزنىڭ ئەجىلىمىزنى تەخىر قىلىپ) بىزگە رەھىم قىلىدىغان بولسا (سىلەرگە نېمە پايدا بار)، كاپىرلارنى قاتتىق ئازابتىن كىم قۇتۇلدۇرىدۇ؟»'),
(5270, 67, 29, 'ئېيتقىنكى، «ئۇ ناھايىتى مېھرىبان اﷲ دۇر، ئۇنىڭغا ئىمان ئېيتتۇق ۋە ئۇنىڭغا تەۋەككۈل قىلدۇق، سىلەر كىمنىڭ روشەن گۇمراھلىقتا ئىكەنلىكىنى كەلگۈسىدە بىلىسىلەر»'),
(5271, 67, 30, '(ئۇلارغا) «ئېيتىپ بېقىڭلارچۇ؟ (سۈيىڭلار يەر ئاستىغا) سىڭىپ كەتسە، سىلەرگە كىم ئېقىن سۇ كەلتۈرۈپ بېرىدۇ» دېگىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نۇن. قەلەم بىلەن ۋە ئۇلار يازغان نەرسىلەر بىلەن قەسەمكى،'),
(5273, 68, 2, '(ئى مۇھەممەد!) سەن پەرۋەردىگارىڭنىڭ نېمىتى بىلەن، (مۇشرىكلار ئېيتقاندەك) مەجنۇن ئەمەسسەن'),
(5274, 68, 3, 'مۇقەررەركى، سەن ئەلۋەتتە ئۆكسىمەس ساۋابقا ئېرىشىسەن'),
(5275, 68, 4, '(ئى مۇھەممەد!) سەن ھەقىقەتەن بۈيۈك ئەخلاققا ئىگىسەن'),
(5276, 68, 5, 'كەلگۈسىدە قايسىڭلارنىڭ مەجنۇن ئىكەنلىكىنى سەن كۆرۈسەن، ئۇلارمۇ كۆرىدۇ'),
(5277, 68, 6, 'كەلگۈسىدە قايسىڭلارنىڭ مەجنۇن ئىكەنلىكىنى سەن كۆرۈسەن، ئۇلارمۇ كۆرىدۇ'),
(5278, 68, 7, 'شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغانلارنى ئوبدان بىلىدۇ، ھىدايەت تاپقۇچىلارنىمۇ ئوبدان بىلىدۇ'),
(5279, 68, 8, 'ئىنكار قىلغۇچىلارغا ئىتائەت قىلمىغىن'),
(5280, 68, 9, 'ئۇلار سېنىڭ ئۆزلىرى بىلەن مادارا قىلىشىڭنى، شۇنىڭ بىلەن ئۆزلىرىنىڭ (سەن بىلەن) مادارا قىلىشىنى ئارزۇ قىلىدۇ'),
(5281, 68, 10, 'قەسەمخور، پەس، غەيۋەتخور، سۇخەنچى، بېخىل، ھەددىدىن ئاشقۇچى، گۇناھكار، قوپال، ئۇنىڭ ئۈستىگە ھارامدىن بولغان ئادەم (يەنى ۋەلىد ئىبن مۇغىرە) گە ئىتائەت قىلمىغىن'),
(5282, 68, 11, 'قەسەمخور، پەس، غەيۋەتخور، سۇخەنچى، بېخىل، ھەددىدىن ئاشقۇچى، گۇناھكار، قوپال، ئۇنىڭ ئۈستىگە ھارامدىن بولغان ئادەم (يەنى ۋەلىد ئىبن مۇغىرە) گە ئىتائەت قىلمىغىن'),
(5283, 68, 12, 'قەسەمخور، پەس، غەيۋەتخور، سۇخەنچى، بېخىل، ھەددىدىن ئاشقۇچى، گۇناھكار، قوپال، ئۇنىڭ ئۈستىگە ھارامدىن بولغان ئادەم (يەنى ۋەلىد ئىبن مۇغىرە) گە ئىتائەت قىلمىغىن'),
(5284, 68, 13, 'قەسەمخور، پەس، غەيۋەتخور، سۇخەنچى، بېخىل، ھەددىدىن ئاشقۇچى، گۇناھكار، قوپال، ئۇنىڭ ئۈستىگە ھارامدىن بولغان ئادەم (يەنى ۋەلىد ئىبن مۇغىرە) گە ئىتائەت قىلمىغىن'),
(5285, 68, 14, 'بىزنىڭ ئايەتلىرىمىز ئۇنىڭغا ئوقۇپ بېرىلسە، ئۇنىڭ مال - مۈلكى ۋە ئوغۇللىرى بولغانلىقى ئۈچۈن، «(بۇ) بۇرۇنقىلارنىڭ ھېكايىلىرىدۇر»، دەيدۇ'),
(5286, 68, 15, 'بىزنىڭ ئايەتلىرىمىز ئۇنىڭغا ئوقۇپ بېرىلسە، ئۇنىڭ مال - مۈلكى ۋە ئوغۇللىرى بولغانلىقى ئۈچۈن، «(بۇ) بۇرۇنقىلارنىڭ ھېكايىلىرىدۇر»، دەيدۇ'),
(5287, 68, 16, 'ئۇنىڭ بۇرنىنى داغلاپ بەلگە سالىمىز'),
(5288, 68, 17, 'شۈبھىسىزكى، بىز ئۇلارنى (يەنى مەككە ئاھالىسىنى) مەلۇم باغنىڭ ئىگىلىرىنى سىنىغاندەك (قەھەتچىلىك، ئاچارچىلىققا دۇچار قىلىش بىلەن) سىنىدۇق. ئۇلار (يەنى باغنىڭ ئىگىلىرى) ئۇنىڭ مېۋىلىرىنى (كەمبەغەللەر ئۇقمىسۇن، ئۇلارغا ھېچ نەرسە بەرمەيلى دېيىشىپ) سەھەردىلا ئۈزۈۋېلىشقا قەسەم ئىچىشكەن ئىدى'),
(5289, 68, 18, 'ئۇلار خۇدا خالىسا دېمىگەن ئىدى'),
(5290, 68, 19, 'ئۇلار ئۇخلاۋاتقاندا، باغقا پەرۋەردىگارىڭ تەرىپىدىن ئازاب (يەنى يانغىن) نازىل بولۇپ، باغ (كۆيۈپ) قاپقارا كۈلدەك بولۇپ قالدى'),
(5291, 68, 20, 'ئۇلار ئۇخلاۋاتقاندا، باغقا پەرۋەردىگارىڭ تەرىپىدىن ئازاب (يەنى يانغىن) نازىل بولۇپ، باغ (كۆيۈپ) قاپقارا كۈلدەك بولۇپ قالدى'),
(5292, 68, 21, 'ئۇلار: «ئەگەر (مېۋىلەرنى) ئۈزمەكچى بولساڭلار باغقا بېرىڭلار» دەپ، ئەتىگەندە بىر - بىرىنى توۋلاشتى'),
(5293, 68, 22, 'ئۇلار: «ئەگەر (مېۋىلەرنى) ئۈزمەكچى بولساڭلار باغقا بېرىڭلار» دەپ، ئەتىگەندە بىر - بىرىنى توۋلاشتى'),
(5294, 68, 23, 'ئۇلار ماڭدى، يولدا كېتىۋېتىپ: «بۈگۈن باغقا ھەرگىز بىر مىسكىنمۇ كىرمىسۇن» دېيىشىپ پىچىرلاشتى'),
(5295, 68, 24, 'ئۇلار ماڭدى، يولدا كېتىۋېتىپ: «بۈگۈن باغقا ھەرگىز بىر مىسكىنمۇ كىرمىسۇن» دېيىشىپ پىچىرلاشتى'),
(5296, 68, 25, 'ئۇلار: «(مىسكىنلەرنى باغقا كىرىشتىن) توسىيالايمىز دېگەن ئوي بىلەن، ئەتىگەندە تۇرۇشتى'),
(5297, 68, 26, 'ئۇلار باغنىڭ (كۆيۈپ قاپقارا بولۇپ كەتكەنلىكىنى) كۆرۈپ: «بىز ھەقىقەتەن ئېزىپ قېلىپتۇق، ياق بىز (باغنىڭ مېۋىسىدىن) قۇرۇق قېلىپتۇق» دېدى'),
(5298, 68, 27, 'ئۇلار باغنىڭ (كۆيۈپ قاپقارا بولۇپ كەتكەنلىكىنى) كۆرۈپ: «بىز ھەقىقەتەن ئېزىپ قېلىپتۇق، ياق بىز (باغنىڭ مېۋىسىدىن) قۇرۇق قېلىپتۇق» دېدى'),
(5299, 68, 28, 'ئۇلارنىڭ ياخشىسى (يەنى ئەڭ ئەقىللىقى): «سىلەر نېمىشقا اﷲ قا تەۋبە قىلىپ تەسبىھ ئېيتمايسىلەر دېمىگەنمىدىم؟ دېدى'),
(5300, 68, 29, 'ئۇلار: «پەرۋەردىگارىمىز! سېنى پاك ئېتىقاد قىلىمىزكى، بىز (مىسكىنلەرنىڭ ھەققىنى مەنئى قىلىش بىلەن) ھەقىقەتەن زۇلۇم قىلغۇچىلاردىن بولدۇق» دېدى'),
(5301, 68, 30, 'شۇنىڭ بىلەن ئۇلار بىر - بىرگە قارىشىپ ئۆز ئارا مالامەت قىلىشتى'),
(5302, 68, 31, 'ئۇلار دېيىشتى: «ۋاي بىزگە! بىز ھەقىقەتەن ھەددىمىزدىن ئېشىپتۇق'),
(5303, 68, 32, 'ئۇمىدكى، پەرۋەردىگارىمىز بىزگە بۇنىڭدىن ياخشىراق باغنى بەرگەي، بىز ئەلۋەتتە پەرۋەردىگارىمىزدىن ئۈمىد قىلغۇچىلارمىز»'),
(5304, 68, 33, 'ئازاب مانا مۇشۇنداقتۇر (يەنى قۇرەيشكىمۇ باغ ئىگىلىرىگە چۈشكەن ئازاب چۈشىدۇ). ئەگەر ئۇلار بىلسە، ئاخىرەتنىڭ ئازابى تېخىمۇ قاتتىقتۇر'),
(5305, 68, 34, 'تەقۋادارلار پەرۋەردىگارىڭنىڭ دەرگاھىدا ھەقىقەتەن نازۇنېمەتلىك جەننەتلەردىن بەھرىمەن بولىدۇ'),
(5306, 68, 35, 'بىز مۇسۇلمانلارنى كاپىرلار بىلەن باراۋەر قىلامدۇق؟'),
(5307, 68, 36, 'سىلەرگە نېمە بولدى؟ قانداقچە مۇنداق ھۆكۈم چىقىرىسىلەر!'),
(5308, 68, 37, 'ياكى سىلەردە (ئاسماندىن نازىل بولغان) بىر كىتاب بولۇپ، ئۇنىڭدىن سىلەرگە (قىيامەت كۈنى) خالىغىنىڭلار بار، دېگەننى ئوقۇدۇڭلارمۇ؟'),
(5309, 68, 38, 'ياكى سىلەردە (ئاسماندىن نازىل بولغان) بىر كىتاب بولۇپ، ئۇنىڭدىن سىلەرگە (قىيامەت كۈنى) خالىغىنىڭلار بار، دېگەننى ئوقۇدۇڭلارمۇ؟'),
(5310, 68, 39, 'ياكى سىلەر ھۆكۈم قىلغان نەرسەڭلارنىڭ بولۇشىغا بىز سىلەر بىلەن قىيامەت كۈنى ئاندىن ۋاقتى توشىدىغان مەھكەم بىر ئەھدى تۈزۈشكەنمۇ؟'),
(5311, 68, 40, 'ئۇلاردىن سوراپ باققىنكى، ئۇلارنىڭ ئارىسىدا قايسىسى يۇقىرىقى ئىشلارغا كېپىللىك قىلالايدۇ؟'),
(5312, 68, 41, 'ياكى ئۇلارنىڭ (يۇقىرىقى ئىشلارغا كېپىللىك قىلىدىغان شېرىكلىرى) بارمۇ؟ ئەگەر ئۇلار (دەۋاسىدا) راستچىل بولسا، شېرىكلىرىنى كەلتۈرۈپ باقسۇن'),
(5313, 68, 42, 'ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ئىش قىيىنلىشىدۇ. ئۇلار (ئىمانىنى سىناش ئۈچۈن) سەجدە قىلىشقا چاقىرىلىدۇ، ئۇلار سەجدە قىلالمايدۇ'),
(5314, 68, 43, 'ئۇلار (قورقۇنچتىن) تىكىلىپ قارىيالمايدۇ، ئۇلارغا خارلىق يۈزلىنىدۇ، ئۇلار (دۇنيادا) ساغلام چاغلىرىدا سەجدىگە دەۋەت قىلىنغان ئىدى (ئۇلار سەجدىدىن باش تارتاتتى)'),
(5315, 68, 44, 'بۇ قۇرئاننى ئىنكار قىلغانلارنى ماڭا قويۇۋەتكىن، ئۇلارنى تۇيدۇرماستىن، ئاستا - ئاستا ھالاكەتكە يۈزلەندۈرىمىز'),
(5316, 68, 45, 'ئۇلارغا مۆھلەت بېرىمەن، شۈبھىسىزكى، مېنىڭ تەدبىرىم پۇختىدۇر'),
(5317, 68, 46, 'ياكى ئۇلاردىن (دىنغا دەۋەت قىلغانلىقىڭغا) ھەق سوراپ، ئۇلارنىڭ (ئىقتىسادىي) يۈكىنى ئېغىرلاشتۇرۇۋەتتىڭمۇ؟'),
(5318, 68, 47, 'ياكى ئۇلارنىڭ يېنىدا غەيب (يەنى غەيبنى ئىچىگە ئالغان لەۋھۇلمەھپۇز) بار بولۇپ، ئۇلار (ئېيتىدىغانلىرىنى شۇنىڭغا) يازامدۇ؟'),
(5319, 68, 48, 'پەرۋەردىگارىڭنىڭ ھۆكمىگە سەۋر قىلغىن، (ئالدىراڭغۇلۇقتا، زېرىككەكلىكتە) بېلىقنىڭ ساھىبىگە (يەنى يۇنۇسقا) ئوخشاش بولمىغىن، ئۆز ۋاقتىدا ئۇ (بېلىقنىڭ قارنىدا تۇرۇپ) غەمكىن ھالدا پەرۋەردىگارىغا ئىلتىجا قىلدى'),
(5320, 68, 49, 'ئەگەر ئۇ پەرۋەردىگارىنىڭ نېمىتىگە ئېرىشمىسە ئىدى، ئەلۋەتتە، ئۇ (قىلمىشىغا) مالامەت قىلىنغان ھالدا (دەرەخمۇ يوق، تاغمۇ يوق) بىر دالىغا تاشلانغان بولاتتى'),
(5321, 68, 50, 'پەرۋەردىگارى ئۇنى (پەيغەمبەرلىككە) تاللىدى، ئۇنى ياخشىلاردىن قىلدى'),
(5322, 68, 51, 'كاپىرلار قۇرئاننى ئاڭلىغان چاغلىرىدا (ساڭا بولغان دۈشمەنلىكىنىڭ قاتتىقلىقىدىن يامان) كۆزلىرى بىلەن سېنى يىقىتىۋېتىشكە تاس قالىدۇ. سېنى: «شۈبھىسىز بىر مەجنۇن» دېيىشىدۇ'),
(5323, 68, 52, 'قۇرئان پەقەت تامام ئەھلى جاھان ئۈچۈن ۋەز - نەسىھەتتۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, '(بولۇشى ئېنىق) قىيامەت'),
(5325, 69, 2, 'قىيامەت نېمىدۇر؟'),
(5326, 69, 3, 'قىيامەتنىڭ نېمە ئىكەنلىكىنى قانداق بىلەلەيسەن؟'),
(5327, 69, 4, 'سەمۇد ۋە ئاد (قەبىلىلىرى) قىيامەتنى ئىنكار قىلدى'),
(5328, 69, 5, 'سەمۇد بولسا ئادەتتىن تاشقىرى قاتتىق ئاۋاز بىلەن ھالاك قىلىندى'),
(5329, 69, 6, 'ئاد بولسا ھەددىدىن زىيادە قاتتىق ۋە سوغۇق بوران بىلەن ھالاك قىلىندى'),
(5330, 69, 7, 'اﷲ ئۇلارغا بوراننى يەتتە كېچە، سەككىز كۈندۈز ئۈزۈلدۈرمەي ئاپىرىدە قىلدى، (ئاد) قەۋمىنىڭ (ئۆز ماكانلىرىدا ھالاك بولۇپ) خورما دەرىخىنىڭ پور كۆتەكلىرىدەك ياتقانلىقىنى كۆرۈسەن'),
(5331, 69, 8, 'ئۇلاردىن بىرەر ئادەمنىڭ قالغانلىقىنى كۆرەلەمسەن؟'),
(5332, 69, 9, 'پىرئەۋن ھەم ئۇنىڭدىن ئىلگىرىكىلەر ۋە شەھەرلىرى دۈم كۆمتۈرۈىۋېتىلگەنلەر (يەنى لۇت قەۋمى) خاتا ئىشلارنى قىلدى'),
(5333, 69, 10, 'ئۇلار پەرۋەردىگارىنىڭ پەيغەمبىرىگە ئاسىيلىق قىلدى، شۇنىڭ بىلەن ئۇلارنى اﷲ قاتتىق جازالىدى'),
(5334, 69, 11, 'شۈبھىسىزكى، (نۇھنىڭ زامانىدا) چوڭ سۇ يامراپ كەتكەن چاغدا، سىلەرنى (يەنى ئەجدادىڭلارنى) بىز كېمىگە سالدۇق، سىلەرگە ئۇنى ئىبرەت قىلىش ئۈچۈن، تۇتۇۋالىدىغان قۇلاقلارنىڭ ئۇنى تۇتۇۋېلىشى ئۈچۈن (بىز شۇنداق قىلدۇق)'),
(5335, 69, 12, 'شۈبھىسىزكى، (نۇھنىڭ زامانىدا) چوڭ سۇ يامراپ كەتكەن چاغدا، سىلەرنى (يەنى ئەجدادىڭلارنى) بىز كېمىگە سالدۇق، سىلەرگە ئۇنى ئىبرەت قىلىش ئۈچۈن، تۇتۇۋالىدىغان قۇلاقلارنىڭ ئۇنى تۇتۇۋېلىشى ئۈچۈن (بىز شۇنداق قىلدۇق)'),
(5336, 69, 13, 'بىرىنچى قېتىملىق سۇر چېلىنغان چاغدا،'),
(5337, 69, 14, 'زېمىن ۋە تاغلار (ئورۇنلىرىدىن) كۆتىرىلىپ (بىر - بىرىگە) ئۇرۇلۇپ (ئۇۋىلىپ كەتكەن) چاغدا،'),
(5338, 69, 15, 'بۇ كۈندە چوڭ ئىش (يەنى قىيامەت) يۈز بېرىدۇ'),
(5339, 69, 16, 'ئاسمان يېرىلىدۇ، بۇ كۈندە ئاسمان (قالايمىقانلىشىپ) ئاجىزلىشىپ كېتىدۇ'),
(5340, 69, 17, 'پەرىشتىلەر ئاسماننىڭ ئەتراپىدا تۇرىدۇ، بۇ كۈندە پەرۋەردىگارىڭنىڭ ئەرشىنى سەككىز پەرىشتە (باشلىرى) ئۈستىدە كۆتۈرۈپ تۇرىدۇ'),
(5341, 69, 18, 'بۇ كۈندە سىلەر (اﷲ قا) (ھېساب بېرىش ئۈچۈن) توغرىلىنىسىلەر، سىلەر ھېچقانداق بىر سىرنى يوشۇرۇپ قالالمايسىلەر'),
(5342, 69, 19, 'نامە - ئەمالى ئوڭ قولىغا بېرىلگەن ئادەم (خۇشاللىقتىن): بۇ مېنىڭ نامە - ئەمالىمنى ئېلىپ ئوقۇپ بېقىڭلار، مەن ھېساباتىمغا مۇلاقات بولىدىغانلىقىمغا مۇقەررەر ئىشەنگەن ئىدىم» دەيدۇ'),
(5343, 69, 20, 'نامە - ئەمالى ئوڭ قولىغا بېرىلگەن ئادەم (خۇشاللىقتىن): بۇ مېنىڭ نامە - ئەمالىمنى ئېلىپ ئوقۇپ بېقىڭلار، مەن ھېساباتىمغا مۇلاقات بولىدىغانلىقىمغا مۇقەررەر ئىشەنگەن ئىدىم» دەيدۇ'),
(5344, 69, 21, 'ئۇ كۆڭۈللۈك تۇرمۇشتا بولىدۇ'),
(5345, 69, 22, 'ئېسىل جەننەتتە بولىدۇ'),
(5346, 69, 23, 'ئۇنىڭ مېۋىلىرى (ئۆرە تۇرغاندىمۇ، ئولتۇرغاندىمۇ، ياتقاندىمۇ قولىنى سوزۇپلا ئالالايدىغان دەرىجىدە) يېقىن بولىدۇ'),
(5347, 69, 24, '(ئۇلارغا) «ئۆتكەنكى كۈنلەردە (يەنى دۇنيادىكى چاغلاردا) ياخشى ئەمەللەرنى قىلغانلىقىڭلار ئۈچۈن، خۇشال - خۇرام يەڭلار، ئىچىڭلار» دېيىلىدۇ'),
(5348, 69, 25, 'نامە - ئەمالى سول قولىغا بېرىلگەن ئادەم ئېيتىدۇكى، «ماڭا نامە - ئەمالىم بېرىلمىگەن بولسىچۇ!'),
(5349, 69, 26, 'ھېسابىمنىڭ نېمە بولغانلىقىنى بىلمىسەم ئىدىم!'),
(5350, 69, 27, 'دۇنيادا ئۆلۈمىم ھاياتىمنى (ئاخىرەتتە قايتا تىرىلمەس قىلىپ) ئاخىرلاشتۇرسىچۇ!'),
(5351, 69, 28, 'مېنىڭ مال - مۈلكۈم ماڭا ھېچ نەرسىگە ئەسقاتمىدى'),
(5352, 69, 29, 'مېنىڭ ھوقۇقۇم قولۇمدىن كەتتى»'),
(5353, 69, 30, '(اﷲ دوزاخقا مۇئەككەل پەرىشتىلەرگە) ئېيتىدۇ: «ئۇنى تۇتۇڭلار، ئۇنىڭغا تاقاق سېلىڭلار،'),
(5354, 69, 31, 'ئاندىن ئۇنى دوزاخقا تاشلاڭلار،'),
(5355, 69, 32, 'ئاندىن ئۇنى ئۇزۇنلۇقى 70 گەز كېلىدىغان زەنجىر بىلەن باغلاڭلار»'),
(5356, 69, 33, '(ئۇنىڭ بۇنداق ئازابلىنىشى شۇنىڭ ئۈچۈندۇركى) ئۇ ھەقىقەتەن ئۇلۇغ اﷲ قا ئىمان ئېيتمىغان ئىدى'),
(5357, 69, 34, 'مىسكىنگە تاماق بېرىشنى تەرغىب قىلمايتتى'),
(5358, 69, 35, 'بۈگۈن ئۇنىڭغا بۇ يەردە (ئۇنىڭدىن ئازابنى كۆتۈرۈۋېتىدىغان) دوست يوقتۇر'),
(5359, 69, 36, 'ئۇنىڭغا يىرىڭدىن باشقا تاماق يوقتۇر'),
(5360, 69, 37, 'ئۇنى پەقەت ئازغان ئادەملەرلا (يەنى كاپىرلارلا) يەيدۇ'),
(5361, 69, 38, 'مەن سىلەر كۆرۈپ تۇرىدىغان نەرسىلەر بىلەن قەسەم قىلىمەن، سىلەر كۆرمەيدىغان نەرسىلەر بىلەنمۇ قەسەم قىلىمەن (يەنى بارلىق مەخلۇقات بىلەن قەسەم قىلىمەن)'),
(5362, 69, 39, 'مەن سىلەر كۆرۈپ تۇرىدىغان نەرسىلەر بىلەن قەسەم قىلىمەن، سىلەر كۆرمەيدىغان نەرسىلەر بىلەنمۇ قەسەم قىلىمەن (يەنى بارلىق مەخلۇقات بىلەن قەسەم قىلىمەن)'),
(5363, 69, 40, 'بۇ قۇرئان ئەلۋەتتە ھۈرمەتلىك پەيغەمبەرنىڭ (اﷲ تەرىپىدىن يەتكۈزگەن) سۆزىدۇر'),
(5364, 69, 41, 'ئۇ شائىرنىڭ سۆزى ئەمەستۇر، سىلەر ناھايىتى ئاز ئىشىنىسىلەر'),
(5365, 69, 42, 'ئۇ كاھىننىڭ سۆزىمۇ ئەمەس، سىلەر ئاز ۋەز - نەسىھەت ئالىسىلەر'),
(5366, 69, 43, '(ئۇ) ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر'),
(5367, 69, 44, 'ئەگەر ئۇ بىزنىڭ نامىمىزدىن يالغاننى توقۇيدىغان بولسا،'),
(5368, 69, 45, 'ئەلۋەتتە ئۇنى قۇدرىتىمىز بىلەن جازالايتتۇق'),
(5369, 69, 46, 'ئاندىن ئۇنىڭ ئاساسى قىزىل تومۇرىنى كېسىپ تاشلايتتۇق'),
(5370, 69, 47, 'سىلەردىن ھېچبىر ئادەم ئۇنى قوغداپ قالالمايتتى'),
(5371, 69, 48, 'شەك - شۈبھىسىزكى، بۇ قۇرئان تەقۋادارلار ئۈچۈن ۋەز - نەسىھەتتۇر'),
(5372, 69, 49, 'بىز شەك - شۈبھىسىز بىلىمىزكى، سىلەرنىڭ ئاراڭلاردا (قۇرئان ئايەتلىرى شۇنچە روشەن تۇرۇقلۇق) ئۇنى ئىنكار قىلغۇچىلار بار'),
(5373, 69, 50, 'قۇرئان كاپىرلارغا (ئۇلار قۇرئاننى تەستىق قىلغۇچىلارنىڭ ساۋابىنى ۋە ئىنكار قىلغۇچىلارنىڭ ئازابىنى كۆرگەن چاغدا) ئەلۋەتتە ھەسرەت بولىدۇ'),
(5374, 69, 51, 'قۇرئان، مۇقەررەركى شەكسىز ھەقىقەتتۇر'),
(5375, 69, 52, 'ئۇلۇغ پەرۋەردىگارىڭنى (مۇشرىكلارنىڭ ئىپتىرالىرىدىن) پاك ئېتىقاد قىلغىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'بىر تىلىگۈچى (يەنى نەزر ئىبن ھارىس) كاپىرلارغا چوقۇم بولىدىغان ئازابنىڭ (چۈشۈشىنى) تىلىدى'),
(5377, 70, 2, 'ئۇ (ئازاب) نى ھېچ ئادەم قايتۇرالمايدۇ'),
(5378, 70, 3, 'ئۇ (ئازاب) ئاسمان شوتىلىرىنىڭ ئىگىسى اﷲ تەرىپىدىن بولغان'),
(5379, 70, 4, 'پەرىشتىلەر ۋە روھ (يەنى جىبرىئىل) اﷲ قا شۇنداق بىر كۈندە ئۆرلەيدۇكى، ئۇنىڭ ئۇزۇنلۇقى 50 مىڭ يىل كېلىدۇ'),
(5380, 70, 5, '(ئى مۇھەممەد! قەۋمىڭنىڭ يەتكۈزگەن ئەزىيەتلىرىگە) چىرايلىقچە سەۋر قىلغىن'),
(5381, 70, 6, 'ئۇلار ئۇ ئازابنى يىراق دەپ قارايدۇ'),
(5382, 70, 7, 'بىز ئۇنى يېقىن دەپ قارايمىز'),
(5383, 70, 8, 'ئۇ كۈندە ئاسمان ئېرىتىلگەن مىستەك بولۇپ قالىدۇ'),
(5384, 70, 9, 'تاغلار ئېرىتىلگەن رەڭلىك يۇڭدەك بولۇپ قالىدۇ'),
(5385, 70, 10, '(ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم - قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ'),
(5386, 70, 11, '(ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم - قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ'),
(5387, 70, 12, '(ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم - قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ'),
(5388, 70, 13, '(ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم - قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ'),
(5389, 70, 14, '(ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم - قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ'),
(5390, 70, 15, 'ياق، ئۇ مۇقەررەر دوزاختۇر'),
(5391, 70, 16, '(يەنى بۇ گۇناھكار كاپىر مۇنداق ئارزۇدىن يانسۇن، پىدا قىلىنغان ھېچقانداق نەرسە ئۇنى ئازابتىن قۇتقۇزالمايدۇ، ئۇنىڭ ئالدىدا دوزاخ يېنىپ تۇرىدۇ). (ئۇ ھارارىتىنىڭ قاتتىقلىقىدىن) باشنىڭ تېرىسىنى شىلىدۇ'),
(5392, 70, 17, 'ئۇ (ھەقتىن) يۈز ئۆرۈپ بۇرالغان ۋە پۇل - مال يىغىپ ساقلىغان (ئۇنىڭدىن اﷲ نىڭ ۋە مىسكىنلەرنىڭ ھەققىنى ئادا قىلمىغان) لارنى چاقىرىدۇ'),
(5393, 70, 18, 'ئۇ (ھەقتىن) يۈز ئۆرۈپ بۇرالغان ۋە پۇل - مال يىغىپ ساقلىغان (ئۇنىڭدىن اﷲ نىڭ ۋە مىسكىنلەرنىڭ ھەققىنى ئادا قىلمىغان) لارنى چاقىرىدۇ'),
(5394, 70, 19, 'ئىنسان ھەقىقەتەن چىدامسىز يارىتىلدى'),
(5395, 70, 20, 'ئۇنىڭغا (يوقسۇزلۇق، ياكى كېسەللىك، يا قورقۇنچتەك) بىرەر كۆڭۈلسىزلىك يەتكەن چاغدا، زارلانغۇچىدۇر'),
(5396, 70, 21, 'بىرەر ياخشىلىق يەتكەن چاغدا، بېخىللىق قىلغۇچىدۇر'),
(5397, 70, 22, 'پەقەت (تۆۋەندىكىلەر بۇنىڭدىن) مۇستەسنا: ئۇلار نامازغا ھەمىشە رىئايە قىلغۇچىلاردۇر'),
(5398, 70, 23, 'پەقەت (تۆۋەندىكىلەر بۇنىڭدىن) مۇستەسنا: ئۇلار نامازغا ھەمىشە رىئايە قىلغۇچىلاردۇر'),
(5399, 70, 24, 'ئۇلار ماللىرىدىن تىلەيدىغان پېقىرغا ۋە تىلىمەيدىغان پېقىرغا مۇئەييەن ھەق (يەنى زاكات) بېرىدۇ'),
(5400, 70, 25, 'ئۇلار ماللىرىدىن تىلەيدىغان پېقىرغا ۋە تىلىمەيدىغان پېقىرغا مۇئەييەن ھەق (يەنى زاكات) بېرىدۇ'),
(5401, 70, 26, 'ئۇلار قىيامەت كۈنىگە تەستىق قىلىدىغانلاردۇر'),
(5402, 70, 27, 'ئۇلار پەرۋەردىگارىنىڭ ئازابىدىن قورققۇچىلاردۇر'),
(5403, 70, 28, 'شۈبھىسىزكى، ئۇلارنىڭ پەرۋەردىگارىنىڭ ئازابى (اﷲ ئەمىن قىلغاندىن باشقا ھېچبىر ئادەم ئۈچۈن) ئەمىن بولغىلى بولمايدىغان نەرسىدۇر'),
(5404, 70, 29, 'ئۇلار ئەۋرەتلىرىنى ھارامدىن ساقلىغۇچىلاردۇر'),
(5405, 70, 30, 'پەقەت خوتۇنلىرى ۋە چۆرىلىرى بۇنىڭدىن مۇستەسنا، (ئۆزلىرىنىڭ خوتۇنلىرى ۋە چۆرىلىرى بىلەن يېقىنچىلىق قىلغانلار) مالامەت قىلىنمايدۇ،'),
(5406, 70, 31, '(جىنسىي تەلىپىنى قاندۇرۇشنى) بۇنىڭ سىرتىدىن تىلىگۈچىلەر ھەددىدىن ئاشقۇچىلاردۇر'),
(5407, 70, 32, 'ئۇلار ئۆزلىرىگە تاپشۇرۇلغان ئامانەتلەرگە ۋە بەرگەن ئەھدىگە رىئايە قىلغۇچىلاردۇر'),
(5408, 70, 33, 'ئۇلار توغرا گۇۋاھلىق بەرگۈچىلەردۇر'),
(5409, 70, 34, 'ئۇلار نامىزىنى ئادا قىلغۇچىلاردۇر'),
(5410, 70, 35, 'ئەنە شۇلار جەننەتلەردە ھۆرمەت قىلىنغۇچىلاردۇر'),
(5411, 70, 36, '(ئى مۇھەممەد) كاپىرلارغا نېمە بولدىكىن، ئۇلار سەن تەرەپكە تېز كېلىشىپ، ئوڭ - سول تەرىپىڭدە توپ - توپ بولۇپ ئولتۇرۇشىدۇ'),
(5412, 70, 37, '(ئى مۇھەممەد) كاپىرلارغا نېمە بولدىكىن، ئۇلار سەن تەرەپكە تېز كېلىشىپ، ئوڭ - سول تەرىپىڭدە توپ - توپ بولۇپ ئولتۇرۇشىدۇ'),
(5413, 70, 38, 'ئۇلاردىن ھەر بىر ئادەم نازۇنېمەتلىك جەننەتكە كىرگۈزۈلۈشنى تەمە قىلامدۇ؟'),
(5414, 70, 39, 'ياق (يەنى ئۇلار جەننەتكە مەڭگۈ كىرەلمەيدۇ). شۈبھىسىزكى، بىز ئۇلارنى ئۇلار بىلىدىغان نەرسىلەردىن ياراتتۇق'),
(5415, 70, 40, 'مەشرىقلارنىڭ ۋە مەغرىبلەرنىڭ پەرۋەردىگارى بىلەن قەسەمكى، ئۇلارنى ئۇلاردىن ياخشىراق ئادەملەر بىلەن ئالماشتۇرۇشقا (يەنى ئۇلارنى ھالاك قىلىپ ئورنىغا ئۇلاردىن ئارتۇق ۋە اﷲ قا ئىتائەتچان بىر قەۋمنى كەلتۈرۈشكە) بىز ئەلۋەتتە قادىرمىز، بىز (بۇنىڭدىن) ئاجىز ئەمەسمىز'),
(5416, 70, 41, 'مەشرىقلارنىڭ ۋە مەغرىبلەرنىڭ پەرۋەردىگارى بىلەن قەسەمكى، ئۇلارنى ئۇلاردىن ياخشىراق ئادەملەر بىلەن ئالماشتۇرۇشقا (يەنى ئۇلارنى ھالاك قىلىپ ئورنىغا ئۇلاردىن ئارتۇق ۋە اﷲ قا ئىتائەتچان بىر قەۋمنى كەلتۈرۈشكە) بىز ئەلۋەتتە قادىرمىز، بىز (بۇنىڭدىن) ئاجىز ئەمەسمىز'),
(5417, 70, 42, '(ئى مۇھەممەد) ئۇلارنى قويۇۋەتكىن، تاكى ئۇلار ۋەدە قىلىنغان كۈنگە مۇلاقات بولغانغا قەدەر (باتىلغا) چۆمسۇن، (دۇنياسىدا) ئوينىسۇن'),
(5418, 70, 43, 'ئۇ كۈندە ئۇلار قەبرىلىرىدىن چىقىپ، خۇددى تىكلەپ قويغان بۇتلىرىغا قاراپ يۈگۈرگەندەك (مەھشەرگاھقا) يۈگۈرىدۇ'),
(5419, 70, 44, 'ئۇلار (اﷲ تىن ئۇيىلىپ) يۇقىرى قارىيالمايدۇ، ئۇلارنى خورلۇق ئورىۋالىدۇ، ئەنە شۇ ئۇلارغا ۋەدە قىلىنغان كۈندۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'بىز ھەقىقەتەن نۇھنى قەۋمىگە: «قەۋمىڭگە قاتتىق ئازاب (يەنى دۇنيادىكى چاغلىرىدا توپان بالاسى، ئاخىرەتتە دوزاخ ئازابى) كېلىشتىن بۇرۇن ئۇلارنى ئاگاھلاندۇرغىن» دەپ ئەۋەتتۇق'),
(5421, 71, 2, 'نۇھ ئېيتتى: «ئى قەۋمىم! مەن ھەقىقەتەن كىشىلەرگە (ئەۋەتىلگەن) ئاشكارا ئاگاھلاندۇرغۇچىمەن'),
(5422, 71, 3, 'اﷲ قا ئىبادەت قىلىڭلار، ئۇنىڭدىن قورقۇڭلار ۋە اﷲ قا ئىتائەت قىلىڭلار'),
(5423, 71, 4, 'ئۇ بەزى گۇناھىڭلارنى مەغپىرەت قىلىدۇ، سىلەرنى مۇئەييەن مۇددەتكىچە كېچىكتۈرىدۇ (يەنى ئۆمرۈڭلارنى ئۇزۇن قىلىدۇ)، شۈبھىسىزكى، ئەگەر بىلسەڭلار، اﷲ نىڭ (ئىنسانغا بېكىتكەن) ئەجىلى كەلگەن چاغدا (ئەجەل) كېچىكتۈرۈلمەيدۇ'),
(5424, 71, 5, 'نۇھ ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن قەۋمىمنى كېچە - كۈندۈز (ئىمانغا) دەۋەت قىلدىم'),
(5425, 71, 6, 'مېنىڭ دەۋىتىم ئۇلارنى تېخىمۇ قاچۇرۇۋەتتى'),
(5426, 71, 7, 'مەن ئۇلارنى سېنىڭ مەغپىرىتىڭگە ئېرىشسۇن دەپ، ھەر قاچان دەۋەت قىلسام، (دەۋىتىمنى ئاڭلىماسلىق ئۈچۈن) بارماقلىرىنى قۇلاقلىرىغا تىقىۋالدى، (سۆزۈمنى ئاڭلىماسلىق ياكى مېنى كۆرمەسلىك ئۈچۈن) كىيىملىرى بىلەن باشلىرىنى چۈمكىۋالدى، (كۇفرىدا) چىڭ تۇردى، چوڭچىلىق قىلىپ (ئىماندىن) تېخىمۇ باش تارتتى'),
(5427, 71, 8, 'ئاندىن مەن ئۇلارنى توۋلاپ دەۋەت قىلدىم'),
(5428, 71, 9, 'ئاندىن ئۇلارنى ئاشكارا دەۋەت قىلدىم ۋە يوشۇرۇن دەۋەت قىلدىم'),
(5429, 71, 10, '(ئۇلارغا) ئېيتتىم: پەرۋەردىگارىڭلاردىن مەغپىرەت تىلەڭلار، ئۇ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر'),
(5430, 71, 11, 'ئۇ سىلەرگە مول يامغۇر ياغدۇرۇپ بېرىدۇ،'),
(5431, 71, 12, 'سىلەرنىڭ ماللىرىڭلارنى ۋە ئوغۇللىرىڭلارنى كۆپەيتىپ بىرىدۇ، سىلەرگە باغلارنى، ئۆستەڭلارنى ئاتا قىلىدۇ،'),
(5432, 71, 13, 'نېمە ئۈچۈن سىلەر اﷲ نىڭ بۈيۈكلۈكىدىن قورقمايسىلەر؟'),
(5433, 71, 14, 'ھالبۇكى اﷲ سىلەرنى بىر قانچە باسقۇچلارغا بۆلۈپ ياراتتى،'),
(5434, 71, 15, 'سىلەر كۆرمىدىڭلارمىكىن، اﷲ قانداق قىلىپ يەتتە ئاسماننى بىرىنىڭ ئۈستىدە بىرىنى قىلىپ ياراتتى،'),
(5435, 71, 16, 'ئاينى ئۇلاردا (يەنى دۇنيانىڭ ئاسمىنىدا) نۇر قىلدى، كۈننى (نۇر چاچقۇچى) چىراغ قىلدى'),
(5436, 71, 17, 'اﷲ سىلەرنى زېمىندىن ياراتتى (چۈنكى ئاتاڭلار ئادەمنى تۇپراقتىن ياراتقان ئىدى)'),
(5437, 71, 18, 'ئاندىن اﷲ سىلەرنى (ئۆلگەندىن كېيىن) زېمىنغا قايتۇرىدۇ (يەنى زېمىندا دەپنە قىلىنىسىلەر)، ئاندىن سىلەرنى (قىيامەت كۈنى) زېمىندىن چىقىرىدۇ'),
(5438, 71, 19, 'زېمىندا كەڭ يوللاردا مېڭىشىڭلار ئۈچۈن، اﷲ سىلەرگە زېمىننى بىسات قىلىپ بەردى»'),
(5439, 71, 20, 'زېمىندا كەڭ يوللاردا مېڭىشىڭلار ئۈچۈن، اﷲ سىلەرگە زېمىننى بىسات قىلىپ بەردى»'),
(5440, 71, 21, 'نۇھ ئېيتتى: «پەرۋەردىگارىم! ئۇلار ھەقىقەتەن ماڭا ئاسىيلىق قىلدى، مېلىغا، بالىسىغا ۋە ئۆزلىرىگە زىياندىن باشقىنى زىيادە قىلمىغان كىشىلەرگە (يەنى بايلىرىغا، كاتتىباشلىرىغا) ئەگەشتى'),
(5441, 71, 22, 'ئۇلار (يەنى كاتتىباشلار) (ماڭا چىنپۈتمىدى، ماڭا ۋە ماڭا ئەگەشكەنلەرگە ئەزىيەت يەتكۈزۈپ) چوڭ ھىيلە تۈزدى'),
(5442, 71, 23, 'ئۇلار: ‹مەبۇدلىرىڭلارنى ھەرگىز تەرك ئەتمەڭلار، ۋەدنى، سۇۋائنى، يەغۇسنى، يەئۇقنى ۋە نەسرىنى ھەرگىز تەرك ئەتمەڭلار› دېدى'),
(5443, 71, 24, 'ئۇلار ھەقىقەتەن نۇرغۇن كىشىلەرنى ئازدۇردى، زالىملارنى تېخىمۇ گۇمراھ قىلغىن»'),
(5444, 71, 25, 'ئۇلار گۇناھلىرى تۈپەيلىدىن (توپان بىلەن) غەرق قىلىندى، دوزاخقا كىرگۈزۈلدى، ئۇلار ئۆزلىرى ئۈچۈن اﷲ تىن باشقا ھېچقانداق ياردەمچى تاپالمىدى'),
(5445, 71, 26, 'نۇھ ئېيتتى: «پەرۋەردىگارىم يەر يۈزىدە كاپىرلارنىڭ بىرىنىمۇ قويمىغىن'),
(5446, 71, 27, 'ئەگەر ئۇلارنى قويساڭ، شۈبھىسىزكى، بەندىلىرىڭنى ئازدۇرىدۇ، ئۇلارنىڭ تاپقان بالىلىرىمۇ پەقەت فاجىر، كاپىر بولىدۇ'),
(5447, 71, 28, 'پەرۋەردىگارىم! ماڭا، ئاتا - ئانامغا، مېنىڭ ئۆيۈمگە مۆمىن بولۇپ كىرگەن كىشىگە ۋە مۆمىن ئەرلەرگە، مۆمىن ئاياللارغا مەغپىرەت قىلغىن، زالىملارغا پەقەت ھالاكەتنى زىيادە قىلغىن»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, '(ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «بىر قانچە نەپەر جىننىڭ (قۇرئان) تىڭشاپ (ئىمان ئېيتقانلىقى) ماڭا ۋەھيى قىلىندى، ئۇلار (قەۋمىگە قايتىپ بارغاندىن كېيىن) ئېيتتى: بىز توغرا يولغا باشلايدىغان ئاجايىپ قۇرئاننى ھەقىقەتەن ئاڭلىدۇق'),
(5449, 72, 2, 'ئۇنىڭغا ئىمان ئېيتتۇق، پەرۋەردىگارىمىزغا ھېچكىمنى شېرىك كەلتۈرمەيمىز'),
(5450, 72, 3, 'پەرۋەردىگارىمىزنىڭ ئەزىمىتى كاتتىدۇر، خوتۇنى ۋە بالىسى يوقتۇر'),
(5451, 72, 4, 'بىزنىڭ ئارىمىزدىكى ئەخمەق (يەنى ئىبلىس) اﷲ قا ۋە ئۇنىڭ كاتتىلىقىغا نالايىق سۆزلەرنى قىلدى'),
(5452, 72, 5, 'بىز، ئىنسانلار ۋە جىنلار اﷲ قا يالغاننى چاپلىمايدۇ، دەپ ئويلىغان ئىدۇق'),
(5453, 72, 6, 'ئىنسانلاردىن بەزى كىشىلەر جىنلاردىن بولغان بەزى كىشىلەرگە سېغىناتتى، شۇنىڭ بىلەن جىنلار تېخىمۇ تەكەببۇرلىشىپ كەتتى'),
(5454, 72, 7, 'ئۇلار (يەنى ئىنساننىڭ كۇففارلىرى)، سىلەرگە ئوخشاش، اﷲ ھېچ ئادەمنى (ئۆلگەندىن كېيىن) تىرىلدۈرمەيدۇ، دەپ گۇمان قىلدى»'),
(5455, 72, 8, '(جىنلار ئېيتتى) «بىز (ئاسمان ئەھلىنىڭ سۆزلىرىنى تىڭشاش ئۈچۈن) ئاسمانغا (چىقىشنى) تىلىدۇق، ئاسماننىڭ كۈچلۈك مۇھاپىزەتچىلەر (يەنى پەرىشتىلەر) بىلەن ۋە (كۆيدۈرگۈچى) يۇلتۇزلار بىلەن تولۇپ كەتكەنلىكىنى بايقىدۇق'),
(5456, 72, 9, 'بىز ھەمىشە (ئوغرىلىقچە تىڭشاش ئۈچۈن) ئاسماننىڭ ئولتۇرۇشقا بولىدىغان جايلىرىدا ئولتۇراتتۇق، كىمكى ئەمدى (ئوغرىلىقچە) ئاڭلاشقا ئورۇنىدىكەن، ئۇنى كۆزىتىپ تۇرغان كۆيدۈرگۈچى يۇلتۇزلارنى بايقايدۇ'),
(5457, 72, 10, 'بىز (جىنلار جامائەسى) زېمىندىكىلەرگە يامانلىق ئىرادە قىلىندىمۇ ياكى پەرۋەردىگارى ئۇلارغا ياخشىلىق ئىرادە قىلدىمۇ؟ بىلمەيمىز'),
(5458, 72, 11, 'بىزنىڭ ئىچىمىزدە ياخشىلارمۇ ۋە ئۇنىڭدىن تۆۋەن تۇرىدىغانلارمۇ بار، بىز تۈرلۈك يوللارغا بۆلۈندۇق'),
(5459, 72, 12, 'بىز جەزمەن ئىشەندۇقكى، اﷲ نى (ئۆز ئىرادىسىنى ئىشقا ئاشۇرۇشتىن) ھەرگىز ئاجىز قىلالمايمىز، ئۇنىڭ (ئازابى) دىنمۇ قېچىپ قۇتۇلالمايمىز'),
(5460, 72, 13, 'بىز ھىدايەتنى (يەنى قۇرئاننى) ئاڭلىغان چېغىمىزدا ئۇنىڭغا ئىمان كەلتۈردۇق، كىمكى پەرۋەردىگارىغا ئىمان ئېيتىدىكەن، (ياخشىلىقلىرىنىڭ) كېمىيىپ كېتىشىدىن ۋە (يامانلىقلىرىنىڭ ئارتتۇرۇلۇپ) زۇلۇم قىلىنىشتىن قورقمايدۇ'),
(5461, 72, 14, 'بىزدىن مۇسۇلمانلار ۋە ھەق يولدىن چىققۇچىلارمۇ بار، مۇسۇلمان بولغانلار توغرا يولنى ئىزدىگەن بولىدۇ'),
(5462, 72, 15, 'توغرا يولدىن چىققۇچىلار جەھەننەمگە ئوتۇن بولىدۇ»'),
(5463, 72, 16, 'ئەگەر ئۇلار (يەنى مەككە كۇففارلىرى) توغرا يولدا بولسا ئىدى، ئۇلارنى سىناش ئۈچۈن مول يامغۇر ياغدۇرۇپ بېرەتتۇق (يەنى رىزقىنى كەڭ قىلىپ بېرەتتۇق)، كىمكى پەرۋەردىگارىنىڭ زىكرىدىن (يەنى اﷲ نىڭ تائەت - ئىبادىتىدىن) يۈز ئۆرۈيدىكەن، اﷲ ئۇنى قاتتىق ئازابقا دۇچار قىلىدۇ'),
(5464, 72, 17, 'ئەگەر ئۇلار (يەنى مەككە كۇففارلىرى) توغرا يولدا بولسا ئىدى، ئۇلارنى سىناش ئۈچۈن مول يامغۇر ياغدۇرۇپ بېرەتتۇق (يەنى رىزقىنى كەڭ قىلىپ بېرەتتۇق)، كىمكى پەرۋەردىگارىنىڭ زىكرىدىن (يەنى اﷲ نىڭ تائەت - ئىبادىتىدىن) يۈز ئۆرۈيدىكەن، اﷲ ئۇنى قاتتىق ئازابقا دۇچار قىلىدۇ'),
(5465, 72, 18, 'مەسجىدلەر اﷲ قا خاستۇر، اﷲ قا قوشۇپ ھېچكىمگە ئىبادەت قىلماڭلار'),
(5466, 72, 19, 'اﷲ نىڭ بەندىسى (يەنى مۇھەممەد ئەلەيھىسسالام) اﷲ قا ئىبادەت قىلىشقا تۇرغاندا، (قۇرئان تىڭشاش ئۈچۈن) جىنلار (ئولىشىپ) ئۇنى بېسىۋىلىشقا تاس قالىدۇ'),
(5467, 72, 20, '(ئى مۇھەممەد! دىنىڭدىن يېنىپ كېتىشىڭنى تەلەپ قىلغان بۇ كۇففارلارغا) ئېيتقىنكى، «پەرۋەردىگارىمغا ئىبادەت قىلىمەن، ئۇنىڭغا ھېچ ئەھەدىنى شېرىك كەلتۈرمەيمەن»'),
(5468, 72, 21, 'ئېيتقىنكى، «مەن سىلەرگە پايدا - زىيان يەتكۈزۈشكە قادىر بولالمايمەن (بۇنىڭغا پەقەت اﷲ قادىر بولالايدۇ)»'),
(5469, 72, 22, 'ئېيتقىنكى، «(ئەگەر مەن اﷲ قا ئاسىي بولسام) مېنى اﷲ نىڭ ئازابىدىن ھېچ كىشى قۇتقۇزالمايدۇ، اﷲ تىن باشقا ھېچ پاناھگاھمۇ تاپالمايمەن (شۇنداق تۇرۇقلۇق، مەن سىلەرنىڭ تەلىپىڭلارنى قانداق قوبۇل قىلالايمەن)'),
(5470, 72, 23, 'مەن پەقەت (سىلەرگە) اﷲ نىڭ ئەلچىلىكىنى يەتكۈزۈشكە قادىرمەن، كىمكى اﷲ قا ۋە ئۇنىڭ پەيغەمبىرىگە ئاسىيلىق قىلىدىكەن (يەنى كىمكى اﷲ نى ۋە اﷲ نىڭ پەيغەمبىرىنى ئىنكار قىلىدىكەن، اﷲ قا مۇلاقات بولۇشقا ئىشەنمەيدىكەن، اﷲ نىڭ ئايەتلىرىگە قۇلاق سېلىشتىن يۈز ئۆرۈيدىكەن)، ئۇ دوزاخ ئازابىغا دۇچار بولىدۇ، دوزاختا مەڭگۈ قالىدۇ'),
(5471, 72, 24, 'ئۇلار (يەنى مۇشرىكلار) ئاگاھلاندۇرۇلغان ئازابنى كۆرگەن چاغدا كىمنىڭ ياردەمچىسى ئاجىز ۋە سانى ئاز ئىكەنلىكىنى بىلىدۇ»'),
(5472, 72, 25, '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرگە ئاگاھلاندۇرۇلغان ئازاب يېقىنمۇ؟ ياكى پەرۋەردىگارىم ئۇنىڭغا (ئۇزاق) ۋاقىت تەيىن قىلامدۇ؟ ئۇقمايمەن'),
(5473, 72, 26, 'اﷲ غەيبنى بىلگۈچىدۇر، ئۆزىنىڭ غەيبىدىن ھېچبىر ئادەمنى خەۋەردار قىلمايدۇ'),
(5474, 72, 27, 'پەقەت اﷲ ئۆزى مەمنۇن بولغان پەيغەمبىرىگىلا (بەزى غەيبنى بىلدۈرىدۇ)، ئۇنىڭ ئالدىغا ۋە ئارقىسىغا كۆزەتچى (پەرىشتە) سېلىپ قويىدۇ'),
(5475, 72, 28, '(بۇ اﷲ نىڭ) ئۇنىڭ (يەنى پەيغەمبەرنىڭ) اﷲ نىڭ ئەلچىلىكىنى يەتكۈزگەنلىكىنى بىلىپ تۇرۇشى ئۈچۈندۇر، اﷲ ئۇلارنىڭ سۆز - ھەرىكەتلىرىنى تولۇق بىلگۈچىدۇر، ھەر شەيئىنىڭ سانىنى تولۇق ئىگەللىگۈچىدۇر»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'ئى كىيىمگە يۆگىنىۋالغۇچى (پەيغەمبەر!)'),
(5477, 73, 2, 'كېچىنىڭ ئازغىنىسىدىن باشقىسىدا، يېرىمىدا ياكى يېرىمىدىنمۇ ئازراقىدا ياكى يېرىمىدىن كۆپرەكىدە ناماز ئوقۇغىن، قۇرئاننى تەرتىل بىلەن (يەنى دانە - دانە، ئوچۇق) ئوقۇغىن'),
(5478, 73, 3, 'كېچىنىڭ ئازغىنىسىدىن باشقىسىدا، يېرىمىدا ياكى يېرىمىدىنمۇ ئازراقىدا ياكى يېرىمىدىن كۆپرەكىدە ناماز ئوقۇغىن، قۇرئاننى تەرتىل بىلەن (يەنى دانە - دانە، ئوچۇق) ئوقۇغىن'),
(5479, 73, 4, 'كېچىنىڭ ئازغىنىسىدىن باشقىسىدا، يېرىمىدا ياكى يېرىمىدىنمۇ ئازراقىدا ياكى يېرىمىدىن كۆپرەكىدە ناماز ئوقۇغىن، قۇرئاننى تەرتىل بىلەن (يەنى دانە - دانە، ئوچۇق) ئوقۇغىن'),
(5480, 73, 5, 'ساڭا ھەقىقەتەن تەنتەنىلىك قۇرئاننى نازىل قىلىمىز'),
(5481, 73, 6, 'شۈبھىسىزكى، كېچىنىڭ قىيامى (يەنى كېچىدە كىشىنىڭ ئىبادەت قىلىشى ئۈچۈن ئۇيقۇدىن ئويغىنىپ تۇرۇشى) ئەڭ مۇۋاپىقتۇر، (تىنچ بولغانلىقى ئۈچۈن) كېچىنىڭ قىرائىتى ئەڭ توغرا بولىدۇ'),
(5482, 73, 7, 'ھەقىقەتەن كۈندۈزى ئىش بىلەن ئالدىراش بولىسەن، (شۇڭا ئىبادەتكە) كېچىسى بېرىلگىن'),
(5483, 73, 8, 'پەرۋەردىگارىڭنىڭ نامىنى ياد ئەتكىن، (ئىبادىتىڭدا) ئۇنىڭغا تولۇق يۈزلەنگىن'),
(5484, 73, 9, 'ئۇ مەشرىقنىڭ ۋە مەغرىبنىڭ پەرۋەردىگارىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇنى ھامىي قىلىۋالغىن (يەنى ھەممە ئىشىڭنى ئۇنىڭغا تاپشۇرغىن)'),
(5485, 73, 10, 'ئۇلارنىڭ (يەنى مەككە كۇففارلىرىنىڭ) (سېنى سېھىرگەر، شائىر، مەجنۇن دېگەن) سۆزلىرىگە سەۋر قىلغىن، ئۇلارنى چىرايلىقچە تەرك ئەتكىن'),
(5486, 73, 11, '(ئى مۇھەممەد! مېنىڭ ئايەتلىرىمنى) ئىنكار قىلغۇچى دۆلەتمەنلەرنى ماڭا قويۇۋەتكىن (يەنى ئۇلارنىڭ جازاسىنى ئۆزۈم بېرىمەن)، ئۇلارغا ئازغىنا مۆھلەت بەرگىن'),
(5487, 73, 12, 'شۈبھىسىزكى، بىزنىڭ دەرگاھىمىزدا (ئۇلارغا) ئېغىر ئىشكەللەر ۋە (كۆيدۈرگۈچى) دوزاخ بار'),
(5488, 73, 13, '(زەققۇم، زەرىدىن ئىبارەت) گالدىن ئۆتمەيدىغان تاماق ۋە قاتتىق ئازاب بار'),
(5489, 73, 14, 'ئۇ كۈندە زېمىن ۋە تاغلار قاتتىق تەۋرەيدۇ، تاغلار بىر دۆۋە تارقاق قۇمغا ئايلىنىدۇ'),
(5490, 73, 15, 'شۈبھىسىزكى، بىز پىرئەۋنگە بىر پەيغەمبەرنى ئەۋەتكەندەك، سىلەرگە (ئەمەلىڭلارغا) گۇۋاھلىق بەرگۈچى بىر پەيغەمبەرنى ئەۋەتتۇق'),
(5491, 73, 16, 'پىرئەۋن پەيغەمبەرگە (يەنى مۇسا ئەلەيھىسسالامغا) ئاسىيلىق قىلدى (يەنى پىرئەۋن مۇسا ئەلەيھىسسالامغا ئىمان ئېيتماي، ئۇنى يالغانغا چىقاردى، ئى قۇرەيش جامائەسى! سىلەر مۇھەممەد ئەلەيھىسسالامغا ئاسىيلىق قىلغاندەك، پىرئەۋنمۇ مۇسا ئەلەيھىسسالامغا ئاسىيلىق قىلغان ئىدى)، شۇنىڭ بىلەن ئۇنى قاتتىق جازالىدۇق'),
(5492, 73, 17, '(ئى قۇرەيش جامائەسى!) ئەگەر ئىمان ئېيتمىساڭلار، (دەھشىتىدىن) بالىلارنىڭ بېشىنى ئاقارتىۋېتىدىغان كۈندىن قانداق ساقلىنىسىلەر'),
(5493, 73, 18, 'ئۇ كۈننىڭ (دەھشىتىدىن) ئاسمان يېرىلىدۇ، اﷲ نىڭ ۋەدىسى چوقۇم ئەمەلگە ئاشىدۇ (چۈنكى اﷲ ۋەدىسىگە خىلاپلىق قىلمايدۇ)'),
(5494, 73, 19, 'بۇ ئايەتلەر ھەقىقەتەن ۋەز - نەسىھەتتۇر، (بۇ ۋەز - نەسىھەتتىن پايدىلىنىشنى) خالىغان ئادەم پەرۋەردىگارىغا يەتكۈزىدىغان يولنى تۇتسۇن'),
(5495, 73, 20, '(ئى مۇھەممەد!) شۈبھىسىزكى، پەرۋەردىگارىڭ سېنىڭ ۋە سەن بىلەن بولغانلارنىڭ (يەنى ساھابىلەر) دىن بىر توپ ئادەمنىڭ (تەھەججۇد نامىزى ئۈچۈن) كېچىنىڭ ئۈچتىن ئىككى ھەسسىسىدە، يېرىمىدا ۋە ئۈچتىن بىر ھەسسىسىدە تۇرىدىغانلىقىڭلارنى بىلىدۇ، كېچە بىلەن كۈندۈزنىڭ (ئۇزۇنلۇقى) نى اﷲ ئالدىنئالا بەلگىلەيدۇ، سىلەرنىڭ ئۇنى ھېسابلاپ بولالمايدىغانلىقىڭلارنى اﷲ بىلىدۇ، اﷲ سىلەرگە رەھىم قىلدى (يەنى كېچىدە تەھەججۇد نامىزى ئوقۇشنىڭ پەرزلىكىنى) ئەمەلدىن قالدۇردى، تەھەججۇد نامىزىدىن سىلەرگە قۇلاي بولغاننى ئوقۇڭلار، اﷲ بىلىدۇكى، بەزىڭلار كېسەل بولۇپ قالىسىلەر، بەزىلەر اﷲ نىڭ پەزلىنى تىلەپ (يەنى تىجارەت قىلىپ) يەر يۈزىدە سەپەر قىلىدۇ، يەنە بەزىلەر اﷲ نىڭ يولىدا جىھاد قىلىدۇ، شۇنىڭ ئۈچۈن ئۇنىڭدىن (يەنى تەھەججۇد نامىزىدىن) قۇلاي بولغاننى ئوقۇڭلار، (پەرز) نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، اﷲ قا قەرزىي ھەسەنە بېرىڭلار (يەنى اﷲ نىڭ رازىلىقى ئۈچۈن ياخشىلىق يوللىرىغا پۇل - مال سەرپ قىلىڭلار)، ئۆزۈڭلار ئۈچۈن (دۇنيادا) قايسىبىر ياخشى ئىشنى قىلساڭلار، اﷲ نىڭ دەرگاھىدا تېخىمۇ ياخشى، تېخىمۇ كاتتا ساۋابقا ئېرىشىسىلەر (دۇنيا بولسا پانىيدۇر، ئاخىرەت بولسا باقىيدۇر ياخشى بەندىلەر ئۈچۈن اﷲ نىڭ دەرگاھىدىكى ساۋاب ھەممىدىن ئارتۇقتۇر)، اﷲ تىن مەغپىرەت تىلەڭلار، اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'ئى (ۋەھيى نازىل بولغان چاغدا) كىيىمگە چۈمكىلىۋالغۇچى (پەيغەمبەر!)'),
(5497, 74, 2, 'تۇر! (ئىنسانلارنى) اﷲ ىڭ ئازابىدىن ئاگاھلاندۇر'),
(5498, 74, 3, 'پەرۋەردىگارىڭنى ئۇلۇغلا'),
(5499, 74, 4, 'كىيىمىڭنى پاك تۇت'),
(5500, 74, 5, 'ئازابتىن (يەنى ئازابقا قالدۇرىدىغان ئىشلاردىن) يىراق بول'),
(5501, 74, 6, 'كۆپ ئېلىش نىيىتىدە بەرمە (يەنى بىر كىشىگە بىرەر سوۋغا بەرسەڭ، ئۇنىڭدىن بەرگەن نەرسەڭدىن كۆپرەك نەرسە ئېلىشنى تەمە قىلما)،'),
(5502, 74, 7, 'پەرۋەردىگارىڭنىڭ (رازىلىقى) ئۈچۈن (قەۋمىڭدىن يەتكەن ئەزىيەتكە) سەۋر قىل'),
(5503, 74, 8, 'سۇر چېلىنغان ۋاقىت - ئۇ كۈن قىيىن كۈندۇر'),
(5504, 74, 9, 'سۇر چېلىنغان ۋاقىت - ئۇ كۈن قىيىن كۈندۇر'),
(5505, 74, 10, 'كاپىرلارغا ئاسان ئەمەستۇر'),
(5506, 74, 11, '(ئى مۇھەممەد!) مەن تەنھا ياراتقان كىشىنى (يەنى ۋەلىد ئىبن مۇغىرەنى) ماڭا قويۇۋەت'),
(5507, 74, 12, 'ئۇنىڭغا نۇرغۇن مال بەردىم'),
(5508, 74, 13, 'ئۇنىڭغا كۆز ئالدىدا تۇرىدىغان ئوغۇللارنى بەردىم'),
(5509, 74, 14, 'ئۇنىڭ ئابرۇيىنى ئۆستۈردۈم'),
(5510, 74, 15, 'ئاندىن ئۇ يەنە كۆپ بېرىشىمنى تەمە قىلىدۇ'),
(5511, 74, 16, 'ياق، (يەنى ئۇنىڭ خالىغىنى بولمايدۇ)، چۈنكى ئۇ بىزنىڭ ئايەتلىرىمىزگە قارشى چىقتى'),
(5512, 74, 17, 'ئۇنىڭغا قاتتىق ئازابىمنى تېتىتىمەن'),
(5513, 74, 18, 'ئۇ (پەيغەمبەر ۋە قۇرئان ھەققىدە) ئويلاندى، (قۇرئان توغرۇلۇق نېمە دېيىشنى ۋە قانداق بوھتان قىلىشنى) پىلانلىدى'),
(5514, 74, 19, 'لەنەت بولسۇنكى، ئۇ قانداق پىلانلىدى'),
(5515, 74, 20, 'يەنە لەنەت بولسۇنكى، ئۇ قانداق پىلانلىدى'),
(5516, 74, 21, 'ئاندىن ئۇ قارىدى،'),
(5517, 74, 22, 'ئاندىن قوشۇمىسىنى تۈردى ۋە چىرايىنى پۈرۈشتۈردى،'),
(5518, 74, 23, 'ئاندىن (ئىماندىن) يۈز ئۆرۈدى (پەيغەمبەرگە ئەگىشىشتىن) تەكەببۇرلۇق بىلەن باش تارتتى،'),
(5519, 74, 24, '«بۇ (كالامۇللاھ ئەمەس) پەقەت ئۆگىنىلگەن سېھىردۇر، بۇ پەقەت ئىنسان سۆزىدۇر» دېدى،'),
(5520, 74, 25, '«بۇ (كالامۇللاھ ئەمەس) پەقەت ئۆگىنىلگەن سېھىردۇر، بۇ پەقەت ئىنسان سۆزىدۇر» دېدى'),
(5521, 74, 26, 'ئۇنى مەن سەقەرگە (يەنى دوزاخقا) سالىمەن'),
(5522, 74, 27, 'سەقەرنىڭ نېمىلىكىنى قانداق بىلەلەيسەن؟'),
(5523, 74, 28, 'ئۇ ھېچ نەرسىنى قالدۇرمايدۇ، قويمايدۇ (بەلكى كۆيدۈرۈپ تاشلايدۇ)'),
(5524, 74, 29, 'ئۇ (چوڭلۇقىدىن) ئىنسانلارنىڭ (كۆزلىرىگە يىراق مۇساپىلەردىن) كۆرۈنۈپ تۇرىدۇ'),
(5525, 74, 30, 'ئۇنىڭغا 19 پەرىشتە مۇئەككەلدۇر'),
(5526, 74, 31, 'بىز دوزاخ مۇئەككەللىرىنى پەقەت پەرىشتىلەردىن قىلدۇق، بىز ئۇلارنىڭ سانى بىلەن پەقەت كاپىرلارنى سىنىدۇق، تاكى ئەھلى كىتاب (ئۆزلىرىنىڭ ساماۋى كىتابلىرىدىكى بۇ ساننى كۆرۈپ قۇرئاننىڭ ھەق ئىكەنلىكىگە) ئىمان ئېيتماي، مۆمىنلەرنىڭ (پەيغەمبەر ئەلەيھىسسالامنىڭ بەرگەن خەۋىرىگە ئىشەنگەنلىكلىرىدىن ۋە ئەھلى كىتابنىڭ قۇرئان خەۋەرلىرىنىڭ تەۋرات، ئىنجىلدىكى خەۋەرلەرگە ئۇيغۇن ئىكەنلىكىنى ئېتىراپ قىلغانلىقىدىن) ئىمانى كۈچەيگەي، ئەھلى كىتاب ۋە مۆمىنلەر (بۇنىڭدىن) شەكلەنمەيدۇ، دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار)، كاپىرلار: «بۇ مىسالدىن اﷲ نېمىنى ئىرادە قىلىدۇ» دەيدۇ، اﷲ مۇشۇنداق خالىغان ئادەمنى گۇمراھ قىلىدۇ، خالىغان ئادەمنى ھىدايەت قىلىدۇ، پەرۋەردىگارىڭنىڭ قوشۇنىنى (يەنى پەرىشتىلەرنىڭ سانىنىڭ كۆپلۈكىنى، ئۇلارنىڭ كۈچ - قۇۋۋىتىنى ۋە ئۇلارنىڭ يارىتىلىشتىكى چوڭلۇقىنى) پەقەت ئۇ ئۆزى بىلىدۇ، بۇ، (يەنى اﷲ بايان قىلغان دوزاخ) پەقەت ئىنسانلار ئۈچۈن ۋەز - نەسىھەتتۇر'),
(5527, 74, 32, 'ياق (يەنى ئۇ مەسخىرە قىلغۇچىلار قەبىھ قىلمىشىدىن يانسۇن)، ئاي بىلەن قەسەمكى،'),
(5528, 74, 33, 'ئۆتۈپ كەتكەن كېچە بىلەن قەسەمكى،'),
(5529, 74, 34, 'يورۇغان سۈبھى بىلەن قەسەمكى،'),
(5530, 74, 35, 'شۈبھىسىزكى، دوزاخ بالالاردىن بىرىدۇر (ئۇنى قانداقمۇ مەسخىرە قىلىسىلەر ۋە ئۇنىڭغا چىنپۈتمەيسىلەر؟)'),
(5531, 74, 36, 'ئۇ ئىنسانلارنى ئاگاھلاندۇرغۇچىدۇر'),
(5532, 74, 37, 'سىلەردىن (جەننەتكە) ئىلگىرىلەشنى خالىغان ياكى (دوزاختىن) چېكىنىشنى خالىغان ئادەمنى ئاگاھلاندۇرغۇچىدۇر'),
(5533, 74, 38, 'ھەر ئىنسان قىلمىشلىرى تۈپەيلىدىن (دوزاختا) مەھبۇستۇر'),
(5534, 74, 39, 'پەقەت ئەسھابى يەمىن (يەنى سائادەتمەن مۆمىنلەر) بۇنىڭدىن مۇستەسنا'),
(5535, 74, 40, 'ئۇلار جەننەتلەردە بولۇپ، گۇناھكارلاردىن: «سىلەرنى دوزاخقا كىرگۈزگەن نېمە؟» دەپ سورايدۇ'),
(5536, 74, 41, 'ئۇلار جەننەتلەردە بولۇپ، گۇناھكارلاردىن: «سىلەرنى دوزاخقا كىرگۈزگەن نېمە؟» دەپ سورايدۇ'),
(5537, 74, 42, 'ئۇلار جەننەتلەردە بولۇپ، گۇناھكارلاردىن: «سىلەرنى دوزاخقا كىرگۈزگەن نېمە؟» دەپ سورايدۇ'),
(5538, 74, 43, 'ئۇلار (جاۋابەن) ئېيتىدۇ: «بىز ناماز ئوقۇمىدۇق'),
(5539, 74, 44, 'مىسكىنلەرگە تائام بەرمىدۇق'),
(5540, 74, 45, 'بىز بىھۇدە سۆز قىلغۇچىلار بىلەن بىللە بىھودە سۆز قىلاتتۇق'),
(5541, 74, 46, 'قىيامەت كۈنىنى ئىنكار قىلاتتۇق،'),
(5542, 74, 47, 'تاكى بىز ئۆلگۈچە (شۇنداق قىلدۇق)»'),
(5543, 74, 48, 'شاپائەت قىلغۇچىلارنىڭ شاپائىتى ئۇلارغا پايدا بەرمەيدۇ (يەنى ئۇلارنى اﷲ نىڭ ئازابىدىن قۇتقۇزىدىغان شاپائەتچى بولمايدۇ، ئۇلارغا پۈتۈن ئەھلى زېمىن شاپائەت قىلغان تەقدىردىمۇ، ئۇلارنىڭ شاپائىتى قوبۇل قىلىنمايدۇ)'),
(5544, 74, 49, 'ئۇلار نېمىشقا ۋەز (يەنى قۇرئان) دىن يۈز ئۆرۈيدۇ'),
(5545, 74, 50, 'گويا ئۇلار يولۋاستىن قاچقان ياۋايى ئېشەكلەردۇر (يەنى بۇ مۇشرىكلار مۇھەممەد ئەلەيھىسسالامنى كۆرگەندە، ئۇنىڭدىن خۇددى يولۋاسنى كۆرۈپ قاچقان ياۋا ئېشەكلەردەك قاچىدۇ)'),
(5546, 74, 51, 'گويا ئۇلار يولۋاستىن قاچقان ياۋايى ئېشەكلەردۇر (يەنى بۇ مۇشرىكلار مۇھەممەد ئەلەيھىسسالامنى كۆرگەندە، ئۇنىڭدىن خۇددى يولۋاسنى كۆرۈپ قاچقان ياۋا ئېشەكلەردەك قاچىدۇ)'),
(5547, 74, 52, 'بەلكى ئۇلاردىن ھەر كىشى ئۆزىگە ئوچۇق سەھىپىلەر بېرىلىشىنى خالايدۇ (يەنى ئۇلار اﷲ نىڭ مۇھەممەد ئەلەيھىسسالامغا نازىل قىلىنغان كىتابىدەك كىتاب نازىل قىلىنىشىنى تەمە قىلىدۇ)'),
(5548, 74, 53, 'ياق (يەنى ئۇلار مۇنداق تەمەدىن يانسۇن). بەلكى ئۇلار (ئاخىرەتتىن) قورقمايدۇ'),
(5549, 74, 54, 'ياق، مۇقەررەركى، ئۇ (يەنى قۇرئان) ۋەز - نەسىھەتتۇر (ئۇلار ئۆزلىرىنىڭ بەختىيار بولۇشىنى كۆزلەيدىغان بولسا، قۇرئان ئۇلارنىڭ ۋەز - نەسىھەت ئېلىشىغا يېتەرلىكتۇر)'),
(5550, 74, 55, 'خالىغان ئادەم ئۇنىڭدىن ۋەز - نەسىھەت ئالىدۇ'),
(5551, 74, 56, 'ئۇلار پەقەت خالىسا، ئاندىن ۋەز - نەسىھەت ئالىدۇ، اﷲ (بەندىلىرى) قورقۇشقا تېگىشلىك زاتتۇر، (بەندىلىرىنى) مەغپىرەت قىلىشقا لايىق زاتتۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'قىيامەت كۈنى بىلەن قەسەمكى،'),
(5553, 75, 2, 'ئۆزىنى مالامەت قىلغۇچى نەپسى بىلەن قەسەمكى، (سىلەر چوقۇم تىرىلدۈرۈلىسىلەر، سىلەردىن چوقۇم ھېساب ئېلىنىدۇ)'),
(5554, 75, 3, 'ئىنسان (يەنى كاپىر) بىزنى ئۆزىنىڭ (چىرىپ تارقىلىپ كەتكەن) سۆڭەكلىرىنى جەمئى قىلالمايدۇ دەپ گۇمان قىلامدۇ؟'),
(5555, 75, 4, 'ئۇنداق ئەمەس (يەنى ئۇنىڭ سۆڭەكلىرىنى جەمئى قىلىمىز)، ئۇنىڭ (ئەڭ ئىنچىكە، ئەڭ نازۇك بولغان) بارماقلىرىنى ئەسلىگە كەلتۈرەلەيمىز'),
(5556, 75, 5, 'بەلكى ئىنسان كەلگۈسى ھاياتىدا گۇناھتا ئەزۋەيلىمەكچى بولىدۇ'),
(5557, 75, 6, 'ئۇ (مەسخىرە قىلىش يۈزىسىدىن): «قىيامەت كۈنى قاچان بولىدۇ» دەپ سورايدۇ'),
(5558, 75, 7, 'كۆزلەر تورلاشقان، ئاينىڭ نۇرى ئۆچكەن، كۈن بىلەن ئاي بىرلەشتۈرۈلگەن چاغدا، ئىنسان بۇ كۈندە: «قاچىدىغان جاي قەيەردە؟» دەيدۇ'),
(5559, 75, 8, 'كۆزلەر تورلاشقان، ئاينىڭ نۇرى ئۆچكەن، كۈن بىلەن ئاي بىرلەشتۈرۈلگەن چاغدا، ئىنسان بۇ كۈندە: «قاچىدىغان جاي قەيەردە؟» دەيدۇ'),
(5560, 75, 9, 'كۆزلەر تورلاشقان، ئاينىڭ نۇرى ئۆچكەن، كۈن بىلەن ئاي بىرلەشتۈرۈلگەن چاغدا، ئىنسان بۇ كۈندە: «قاچىدىغان جاي قەيەردە؟» دەيدۇ'),
(5561, 75, 10, 'كۆزلەر تورلاشقان، ئاينىڭ نۇرى ئۆچكەن، كۈن بىلەن ئاي بىرلەشتۈرۈلگەن چاغدا، ئىنسان بۇ كۈندە: «قاچىدىغان جاي قەيەردە؟» دەيدۇ'),
(5562, 75, 11, 'ياق، (اﷲ نىڭ ئازابىدىن) قاچىدىغان پاناھگاھ يوقتۇر'),
(5563, 75, 12, 'بۇ كۈندە قارارگاھ پەرۋەردىگارىڭنىڭ تەرىپىدۇر'),
(5564, 75, 13, 'بۇ كۈندە ئىنسانغا (چوڭ بولسۇن، كىچىك بولسۇن، ياخشىلىق بولسۇن، يامانلىق بولسۇن) ئىلگىرى ئاخىرى قىلغان ئەمەللىرى ئۇقتۇرۇلىدۇ'),
(5565, 75, 14, 'بەلكى ئىنسان ئۆزىگە (يەنى قىلغان يامان ئىشلىرىغا) ئۆزى گۇۋاھتۇر'),
(5566, 75, 15, 'ئۇ ئۆزىنى ئاقلاش ئۈچۈن قانچە ئۆزرە ئېيتقان تەقدىردىمۇ (ئۆزرىسى قوبۇل قىلىنمايدۇ)'),
(5567, 75, 16, '(ساڭا جىبرىئىل ئارقىلىق ۋەھيى نازىل بولۇۋاتقاندا، ئۇنى ئېسىڭغا ئېلىۋېلىش ئۈچۈن) ئالدىراپ تىلىڭنى مىدىرلاتما'),
(5568, 75, 17, 'ئۇنى توپلاش ۋە ئوقۇپ بېرىش بىزنىڭ مەسئۇلىيىتىمىزدۇر'),
(5569, 75, 18, 'ساڭا ئۇنى ئوقۇپ بەرگىنىمىزدە (يەنى جىبرىئىل ساڭا ئوقۇپ بەرگەندە)، ئۇنىڭ ئوقۇشىغا ئەگەشكىن'),
(5570, 75, 19, 'ئاندىن ئۇنى چۈشەندۈرۈپ بېرىش بىزنىڭ مەسئۇلىيىتىمىزدۇر'),
(5571, 75, 20, '(ئى مۇشرىكلار جامائەسى!) ھەرگىز ئۇنداق ئەمەس (يەنى ئۆلگەندىن كېيىن تىرىلىش، ھېساب بېرىش سىلەر گۇمان قىلغاندەك يوق ئىش ئەمەس)، بەلكى سىلەر بۇ دۇنيانى دوست تۇتىسىلەر'),
(5572, 75, 21, 'ئاخىرەتكە كۆڭۈل بۆلمەيسىلەر'),
(5573, 75, 22, 'بۇ كۈندە، (سائادەتمەنلەرنىڭ) يۈزلىرى نۇرلۇق بولىدۇ'),
(5574, 75, 23, '(ئۇلار) پەرۋەردىگارىغا قاراپ تۇرىدۇ'),
(5575, 75, 24, 'بۇ كۈندە، نۇرغۇن يۈزلەر تۇتۇق بولىدۇ'),
(5576, 75, 25, 'ئۆزلىرىنىڭ چوڭ مۈشكۈللۈككە دۇچار بولىدىغانلىقىغا ئۇلار جەزمەن ئىشىنىدۇ'),
(5577, 75, 26, 'راستلا جان ھەلقۇمىغا يەتكەندە، «(بۇ كېسەلگە) كىم ئىلاج قىلالايدۇ» دېيىلىدۇ'),
(5578, 75, 27, 'راستلا جان ھەلقۇمىغا يەتكەندە، «(بۇ كېسەلگە) كىم ئىلاج قىلالايدۇ» دېيىلىدۇ'),
(5579, 75, 28, '(سەكراتتا ياتقان ئادەم) بۇنىڭ (دۇنيادىن) ئايرىلىش ئىكەنلىكىنى جەزمەن بىلىدۇ'),
(5580, 75, 29, '(سەكراتنىڭ قاتتىقلىقىدىن) پاچاق پاچاققا كىرىشىپ كېتىدۇ'),
(5581, 75, 30, 'بۇ كۈندە ھەيدەپ بېرىلىدىغان جاي پەرۋەردىگارىڭنىڭ دەرگاھىدۇر'),
(5582, 75, 31, '(كاپىر قۇرئانغا) ئىشەنمىدى، ناماز ئوقۇمىدى'),
(5583, 75, 32, 'لېكىن ئۇ (قۇرئاننى) ئىنكار قىلدى ۋە (ئىماندىن) يۈز ئۆرۈدى'),
(5584, 75, 33, 'ئاندىن كېرىلگەن ھالدا ئۆيىگە قايتتى'),
(5585, 75, 34, 'ۋاي سېنىڭ ھالىڭغا! ۋاي سېنىڭ ھالىڭغا!'),
(5586, 75, 35, 'يەنە ۋاي سېنىڭ ھالىڭغا! ۋاي سېنىڭ ھالىڭغا!'),
(5587, 75, 36, 'ئىنسان ئۆزىنى بىكار قويۇپ بېرىلىدۇ (يەنى شەرىئەت ئىشلىرىغا تەكلىپ قىلىنماي، مەيلىگە قويۇۋېتىلگەن ھايۋانغا ئوخشاش) دەپ ئويلامدۇ؟'),
(5588, 75, 37, 'ئۇ (بەچچىدانلارغا) تۆكىلىدىغان (ئاجىز مەنىي) ئەمەسمىدى؟'),
(5589, 75, 38, 'ئاندىن ئۇ لەختە قان بولدى، ئاندىن اﷲ ئۇنى چىرايلىق شەكىلدە ياراتتى'),
(5590, 75, 39, 'ئۇ مەنىيدىن ئەر - ئايال (ئىككى تىپنى) ياراتتى'),
(5591, 75, 40, 'ئۇ (يەنى يۇقىرىقىدەك ئىشلارنى قىلالىغان اﷲ) ئۆلۈكلەرنى تىرىلدۈرۈشكە قادىر ئەمەسمۇ؟');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'ئىنسان (ئانىنىڭ قورسىقىدا) بىر مۇددەتنى ئۆتكۈزدى، ئۇ (ئەرزىمگەنلىكتىن) تىلغا ئېلىنىدىغان نەرسە بولمىدى'),
(5593, 76, 2, 'شۈبھىسىزكى، بىز ئىنساننى ئارىلاشما مەنىي (يەنى ئەر بىلەن ئاياللارنىڭ مەنىيسى) دىن ياراتتۇق، ئۇنى بىز (شەرىئەت تەكلىپلىرى بىلەن) سىنايمىز، شۇنىڭ ئۈچۈن ئۇنى بىز (ئۇنى نازىل قىلغان ئايەتلىرىمىزنى ئاڭلىسۇن، كائىناتنى ياراتقۇچىنىڭ بىرلىكىگە دالالەت قىلىدىغان دەلىللەرنى كۆرسۇن دەپ) ئاڭلايدىغان، كۆرۈدىغان قىلىپ ياراتتۇق'),
(5594, 76, 3, 'شۈبھىسىزكى، ئۇنىڭغا بىز (پەيغەمبەر ئەۋەتىش بىلەن ياخشى - يامان) يولنى كۆرسەتتۇق، ئۇ مۆمىن بولۇپ ياخشى يولدا مېڭىپ) اﷲ نىڭ نېمىتىگە) شۈكۈر قىلغۇچىدۇر، ياكى (فاجىر بولۇپ يامان يولدا مېڭىپ) كۇفرىلىق قىلغۇچىدۇر'),
(5595, 76, 4, 'شۈبھىسىزكى، بىز كاپىرلارغا (پۇتلىرىغا سېلىنىدىغان) زەنجىرلەرنى، (بويۇنلىرىغا سېلىنىدىغان) تاقاقلارنى ۋە يېنىپ تۇرغان دوزاخنى تەييارلىدۇق'),
(5596, 76, 5, 'ھەقىقەتەن ياخشىلار كافۇر ئارىلاشتۇرۇلغان (مەي بىلەن تولدۇرۇلغان) جاملاردىن ئىچىدۇ'),
(5597, 76, 6, '(ئۇ كافۇر جەننەتتىكى بىر) بۇلاقتىن ئېتىلىپ چىققان بولۇپ، ئۇنىڭدىن اﷲ نىڭ (ياخشى) بەندىلىرى ئىچىدۇ، ئۇ بۇلاقنى ئۇلار (خالىغان جايلىرىغا ئېقىتىپ بارالايدۇ'),
(5598, 76, 7, 'ئۇلار ئۆز ئۈستىگە قەسەم ئىچىپ ئالغان ئىشنى ئورۇنلايدۇ ۋە دەھشىتى كەڭ دائىرىلىك بولغان كۈندىن (يەنى قىيامەت كۈنىدىن) قورقىدۇ'),
(5599, 76, 8, 'ئۆزى موھتاج تۇرۇقلۇق، مىسكىنگە، يېتىمگە ۋە ئەسىرگە تائام بېرىدۇ'),
(5600, 76, 9, '(ئۇلار ئېيتىدۇ) «سىلەرگە بىز اﷲ نىڭ رازىلىقى ئۈچۈن تائام بېرىمىز، سىلەردىن (بۇنىڭ بەدىلىگە) ھېچقانداق مۇكاپات ۋە تەشەككۈر تەلەپ قىلمايمىز'),
(5601, 76, 10, 'شۈبھىسىزكى، بىز پەرۋەردىگارىمىز تەرىپىدىن مەيدانغا كەلتۈرۈلگەن ئېغىر، قاتتىق كۈندىن قورقىمىز»'),
(5602, 76, 11, 'اﷲ ئۇلارنى شۇ كۈننىڭ شەررىدىن ساقلايدۇ، ئۇلارنىڭ (يۈزلىرىگە) نۇر، (دىللىرىغا) خۇشاللىق ئاتا قىلىدۇ'),
(5603, 76, 12, 'ئۇلارنىڭ سەۋر - تاقەتلىرى ئۈچۈن اﷲ ئۇلارنى جەننەت بىلەن ۋە (ئۇ يەردىكى) يىپەك (لىباسلار) بىلەن مۇكاپاتلايدۇ'),
(5604, 76, 13, 'ئۇلار جەننەتتە تەختلەرگە يۆلىنىپ ئولتۇرىدۇ، ئۇلار جەننەتتە قاتتىق ئىسسىقنىمۇ، قاتتىق سوغۇقنىمۇ كۆرمەيدۇ'),
(5605, 76, 14, 'جەننەتتىكى (دەرەخلەرنىڭ) سايىلىرى ئۇلارغا يېقىندۇر، جەننەتنىڭ مېۋىلىرىنى ئۈزۈش ئۇلارغا ئاسان قىلىنىدۇ'),
(5606, 76, 15, 'ئۇلارغا (تاماق قاچىلانغان) كۈمۈش تەخسىلەر، (مەي تولدۇرۇلغان) كۈمۈشتىن ياسالغان (شىشىدەك سۈزۈك) جاملار ئايلاندۇرۇپ سۇنۇلۇپ تۇرىدۇ، (ساقىيلار، ئۇلارنىڭ ئېھتىياجىغا قاراپ) لايىق ئۆلچەيدۇ (يەنى ئىچكۈچىلەرنىڭ ئېھتىياجىدىن ئېشىپمۇ قالمايدۇ، كېمىيىپمۇ قالمايدۇ'),
(5607, 76, 16, 'ئۇلارغا (تاماق قاچىلانغان) كۈمۈش تەخسىلەر، (مەي تولدۇرۇلغان) كۈمۈشتىن ياسالغان (شىشىدەك سۈزۈك) جاملار ئايلاندۇرۇپ سۇنۇلۇپ تۇرىدۇ، (ساقىيلار، ئۇلارنىڭ ئېھتىياجىغا قاراپ) لايىق ئۆلچەيدۇ (يەنى ئىچكۈچىلەرنىڭ ئېھتىياجىدىن ئېشىپمۇ قالمايدۇ، كېمىيىپمۇ قالمايدۇ'),
(5608, 76, 17, 'ئۇلار يەنە جەننەتتە زەنجىبىل ئارىلاشتۇرۇلغان جام بىلەن (يەنى جامدىكى مەي بىلەن) سۇغىرىلىدۇ'),
(5609, 76, 18, 'جەننەتتە سەلسەبىل دەپ ئاتىلىدىغان بىر بۇلاقمۇ بار'),
(5610, 76, 19, 'قېرىماي ھەمىشە ياش تۇرىدىغان غىلمانلار نۆۋەت بىلەن ئۇلارنىڭ خىزمىتىنى قىلىپ تۇرىدۇ، ئۇلارنى كۆرگەن چېغىڭدا (ئۇلارنىڭ گۈزەللىكى، سۈزۈكلۈكى ۋە نۇرلۇقلۇقىغا قاراپ) ئۇلارنى تېرىلىپ كەتكەن مەرۋايىتمىكىن دەپ قالىسەن'),
(5611, 76, 20, 'قاچانكى سەن قارايدىغان بولساڭ، بۇ يەردە (تەسۋىرلەپ تۈگەتكىلى بولمايدىغان) نېمەتلەرنى ۋە كاتتا پادىشاھلىقنى كۆرۈسەن'),
(5612, 76, 21, 'ئۇلارنىڭ ئۇچىسىدا يۇپقا يىپەك ۋە قېلىن يىپەكتىن (تەييارلانغان) يېشىل كىيىملەر بولىدۇ، (ئۇلار) كۈمۈش ئۈزۈكلەرنى تاقايدۇ، پەرۋەردىگارى ئۇلارنى پاك شاراب بىلەن سۇغىرىدۇ'),
(5613, 76, 22, 'شۈبھىسىزكى، بۇ سىلەرگە بېرىلگەن مۇكاپاتتۇر، سىلەرنىڭ مېھنىتىڭلار قوبۇل بولدى'),
(5614, 76, 23, '(ئى مۇھەممەد!) ھەقىقەتەن بىز ساڭا قۇرئاننى بۆلۈپ - بۆلۈپ نازىل قىلدۇق'),
(5615, 76, 24, 'سەن پەرۋەردىگارىڭنىڭ ھۆكمىگە سەۋر قىلغىن، سەن ئۇلارنىڭ ئىچىدىن ھېچبىر گۇناھكارغا ياكى كۇفرىلىق قىلغۇچىغا بويسۇنمىغىن'),
(5616, 76, 25, 'ئەتىگەن - ئاخشامدا پەرۋەردىگارىڭنىڭ نامىنى ياد قىلغىن (يەنى ناماز ئوقۇغىن، پەرۋەردىگارىڭغا كۆپ تائەت - ئىبادەت قىلغىن)'),
(5617, 76, 26, 'كېچىنىڭ ئاز قىسمىدا ئۇنىڭغا سەجدە قىل، كۆپ قىسمىدا ئۇنىڭغا تەسبىھ ئېيت'),
(5618, 76, 27, 'شۈبھىسىزكى، بۇلار (يەنى مۇشرىكلار) دۇنيانى (ئاخىرەتتىن) ئارتۇق بىلىدۇ، كەلگۈسىدىكى قىيىن بىر كۈنگە (يەنى قىيامەتكە) سەل قارايدۇ'),
(5619, 76, 28, 'بىز ئۇلارنى ياراتتۇق، ئۇلارنىڭ بەدىنىنى مەزمۇت قىلدۇق، ئەگەر بىز خالىساق (ئۇلارنى ھالاك قىلىپ) ئورنىغا ئۇلارغا ئوخشاش باشقا ئادەمنى دەسسىتەتتۇق'),
(5620, 76, 29, 'شۈبھىسىزكى، بۇ (ئايەتلەر) ۋەز - نەسىھەتتۇر (بۇ ۋەز - نەسىھەتتىن پايدىلىنىشنى) خالىغان ئادەم پەرۋەردىگارىغا يەتكۈزىدىغان يولنى تۇتسۇن'),
(5621, 76, 30, 'پەقەت اﷲ خالىغاندىلا، ئاندىن سىلەر خالايسىلەر (يەنى ھەممە ئىش اﷲ نىڭ خاھىشىغا باغلىقتۇر). اﷲ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر'),
(5622, 76, 31, 'اﷲ خالىغان ئادەمنى رەھمىتىنىڭ دائىرىسىگە كىرگۈزىدۇ. اﷲ زالىملارغا قاتتىق ئازابنى تەييارلىدى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'ئارقىمۇئارقا چىققۇچى (لەرزان) شاماللار بىلەن قەسەمكى،'),
(5624, 77, 2, 'قاتتىق چىققۇچى بورانلار بىلەن قەسەمكى،'),
(5625, 77, 3, '(بۇلۇتلارنى اﷲ خالىغان تەرەپلەرگە) تارقاتقۇچى شاماللار بىلەن قەسەمكى،'),
(5626, 77, 4, '(ھەق بىلەن باتىلنىڭ، ھارام بىلەن ھالالنىڭ) ئارىسىنى ئايرىغۇچى پەرىشتىلەر بىلەن قەسەمكى،'),
(5627, 77, 5, '(بەندىلەرگە) ئۆزرە (قالدۇرماسلىق) ياكى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرۇش ئۈچۈن ۋەھىينى (پەيغەمبەرلەرگە) ئېلىپ چۈشكۈچى پەرىشتىلەر بىلەن قەسەمكى،'),
(5628, 77, 6, '(بەندىلەرگە) ئۆزرە (قالدۇرماسلىق) ياكى (اﷲ نىڭ ئازابىدىن) ئاگاھلاندۇرۇش ئۈچۈن ۋەھىينى (پەيغەمبەرلەرگە) ئېلىپ چۈشكۈچى پەرىشتىلەر بىلەن قەسەمكى،'),
(5629, 77, 7, '(ئى مەككە كۇففارلىرى!) سىلەر ئاگاھلاندۇرۇلغان ئىش (يەنى قىيامەت، ھېساب ۋە جازا) چوقۇم مەيدانغا كەلگۈچىدۇر'),
(5630, 77, 8, 'يۇلتۇزلارنىڭ (نۇرى) ئۆچۈرۈلگەن چاغدا'),
(5631, 77, 9, 'ئاسمان يېرىلغان چاغدا'),
(5632, 77, 10, 'تاغلار تىتىلىپ (شامالدا ئۇچۇپ يۈرگەن زەررىلەردەك) ئۇچۇپ يۈرگەن چاغدا'),
(5633, 77, 11, 'پەيغەمبەرلەرگە (ئۇلار بىلەن ئۈممەتلىرى ئارىسىدا ھۆكۈم چىقىرىلىشقا) ۋاقىت بەلگىلەنگەن چاغدا (ۋەدە قىلىنغان قىيامەت مەيدانغا كېلىدۇ)'),
(5634, 77, 12, '(پەيغەمبەرلەرنىڭ قەۋملىرىگە ئائىت ئىشلار) قايسى بۈيۈك كۈنگە كېچىكتۈرۈلدى؟'),
(5635, 77, 13, 'خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىلىدىغان كۈن ئۈچۈن (تەخىر قىلىندى)'),
(5636, 77, 14, 'ھۆكۈم چىقىرىلىدىغان كۈننىڭ نېمىلىكىنى (يەنى ئۇنىڭ دەھشىتىنى) قانداق بىلەلەيسەن؟'),
(5637, 77, 15, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5638, 77, 16, '(پەيغەمبەرلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئىلگىرىكىلەرنى ھالاك قىلمىدۇقمۇ؟'),
(5639, 77, 17, 'ئاندىن ئۇلارغا ئەگەشتۈرۈپ كېيىنكىلەرنى ھالاك قىلدۇق'),
(5640, 77, 18, 'گۇناھكارلارنى مۇشۇنداق قىلىمىز (يەنى جازالايمىز)'),
(5641, 77, 19, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5642, 77, 20, 'سىلەرنى بىز ئاجىز مەنىدىن ياراتمىدۇقمۇ؟'),
(5643, 77, 21, 'ئۇنى بىز مەلۇم ۋاقىتقىچە (يەنى تۇغۇلغۇچە) پۇختا ئارامگاھ (يەنى بەچچىدان) دا قىلدۇق'),
(5644, 77, 22, 'ئۇنى بىز مەلۇم ۋاقىتقىچە (يەنى تۇغۇلغۇچە) پۇختا ئارامگاھ (يەنى بەچچىدان) دا قىلدۇق'),
(5645, 77, 23, 'بىز (ئۇنى ئابىمەنىيدىن يارىتىشقا) قادىر بولدۇق، بىز (ئۇنى ئەڭ چىرايلىق شەكىلدە يارىتىشقا) نېمىدېگەن ياخشى قادىر بولغۇچىمىز!'),
(5646, 77, 24, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5647, 77, 25, 'زېمىننى بىز ئۆز ئىچىگە ئالىدىغان قىلمىدۇقمۇ؟'),
(5648, 77, 26, '(يەنى) تىرىكلەرنى ۋە ئۆلۈكلەرنى (ئۆز ئىچىگە ئالىدىغان قىلمىدۇقمۇ؟)'),
(5649, 77, 27, 'بىز زېمىنغا ئېگىز تاغلارنى ئورناتتۇق، سىلەرنى تاتلىق سۇ بىلەن سۇغاردۇق'),
(5650, 77, 28, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5651, 77, 29, '(ئۇلارغا دوزاخ مۇئەككەللىرى) «سىلەر (دۇنيادىكى چېغىڭلاردا) ئىنكار قىلغان (دوزاخ) ئازابىغا بېرىڭلار، ئۈچ شاخلىق سايىگە بېرىڭلار» (دەيدۇ)'),
(5652, 77, 30, '(ئۇلارغا دوزاخ مۇئەككەللىرى) «سىلەر (دۇنيادىكى چېغىڭلاردا) ئىنكار قىلغان (دوزاخ) ئازابىغا بېرىڭلار، ئۈچ شاخلىق سايىگە بېرىڭلار» (دەيدۇ)'),
(5653, 77, 31, 'ئۇ سايە (جەھەننەمنىڭ تۈتۈنى بولۇپ كىشىنى) سەگىدەتمەيدۇ، (جەھەننەمنىڭ) يالقۇنىنىمۇ توسمايدۇ'),
(5654, 77, 32, 'ئۇ يالقۇن قەسىردەك (چوڭ) ئۇچقۇنلارنى چىقىرىدۇ'),
(5655, 77, 33, 'ئۇ ئۇچقۇنلار قارا - سېرىق تۆگىلەرگە ئوخشايدۇ'),
(5656, 77, 34, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5657, 77, 35, 'بۇ كۈندە ئۇلار سۆز قىلالمايدۇ'),
(5658, 77, 36, 'ئۇلارنىڭ ئۆزرە ئېيتىشىغا ئىزنى بېرىلمەيدۇ'),
(5659, 77, 37, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5660, 77, 38, 'بۇ (خالايىقنىڭ ئارىسىدا) ھۆكۈم چىقىرىلىدىغان كۈندۇر، (ئۇ كۈندە) سىلەرنى ۋە ئاۋۋالقىلارنى (يەنى ئۆتكەنكى ئۈممەتلەرنى) (ئاراڭلاردا ھۆكۈم چىقىرىش ئۈچۈن) يىغىمىز'),
(5661, 77, 39, 'ئەگەر (ئازابتىن قۇتۇلۇشقا) بىرەر چارەڭلار بولسا، چارە قىلىپ بېقىڭلار'),
(5662, 77, 40, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5663, 77, 41, 'شۈبھىسىزكى، تەقۋادارلار سايىلەردىن، بۇلاقلاردىن ۋە كۆڭلى تارتقان مېۋىلەردىن بەھرىمەن بولىدۇ'),
(5664, 77, 42, 'شۈبھىسىزكى، تەقۋادارلار سايىلەردىن، بۇلاقلاردىن ۋە كۆڭلى تارتقان مېۋىلەردىن بەھرىمەن بولىدۇ'),
(5665, 77, 43, '(ئۇلارغا) «دۇنيادا قىلغان (ياخشى) ئەمەللىرىڭلار ئۈچۈن خۇشال - خۇرام يەڭلار ۋە ئىچىڭلار» (دېيىلىدۇ)'),
(5666, 77, 44, 'شۈبھىسىزكى، بىز ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز'),
(5667, 77, 45, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5668, 77, 46, '(كۇففارلارغا) «(دۇنيانىڭ لەززەتلىرىدىن) ۋاقىتلىق يەڭلار ۋە بەھرىمەن بۇلۇڭلار، شۈبھىسىزكى، سىلەر گۇناھكارسىلەر» (دېيىلىدۇ)'),
(5669, 77, 47, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5670, 77, 48, 'ئۇلارغا: «رۇكۇ قىلىڭلار» دېيىلسە، ئۇلار رۇكۇ قىلمايدۇ'),
(5671, 77, 49, 'بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!'),
(5672, 77, 50, 'ئۇلار (شانلىق دەلىللەرنى ئۆز ئىچىگە ئالغان، پاساھەت - بالاغەتتە ئەڭ يۇقىرى بولغان بۇ قۇرئانغا ئىشەنمىسە) باشقا قايسى سۆزگە ئىشىنىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, '(مۇشرىكلار ئۆزئارا نېمە توغرۇلۇق) سورىشىدۇ؟'),
(5674, 78, 2, 'ئۇلار چوڭ بىر خەۋەر (يەنى قىيامەت توغرۇلۇق) سورىشىدۇ'),
(5675, 78, 3, 'ئۇلار شۇ (يەنى قىيامەت) توغرىسىدا ئىختىلاپ قىلىشقۇچىلاردۇر'),
(5676, 78, 4, 'ھەرگىز ئۇنداق ئەمەس (قىيامەت ئۇلارنىڭ ئويلىغىنىدەك يالغان ئەمەس)، ئۇلار (ھەقىقىي ئەھۋالنى) كەلگۈسىدە بىلىدۇ'),
(5677, 78, 5, 'ھەرگىز ئۇنداق ئەمەس، ئۇلار (بېشىغا كەلگەن ئازابنى) كەلگۈسىدە بىلىدۇ'),
(5678, 78, 6, '(زېمىننىڭ ئۈستىدە مۇقىملىشىشىڭلار ۋە كەڭ تۈزلەڭلىكلەرگە زىرائەت تېرىپ پايدىلىنىشىڭلار ئۈچۈن) زېمىننى بىسات قىلمىدۇقمۇ؟'),
(5679, 78, 7, '(زېمىننىڭ تەۋرەپ كەتمەسلىكى ئۈچۈن) تاغلارنى قوزۇق قىلمىدۇقمۇ؟'),
(5680, 78, 8, 'سىلەرنى ئەر - ئايال قىلىپ ياراتتۇق'),
(5681, 78, 9, 'ئۇيقۇنى سىلەرنىڭ (بەدىنىڭلار ئۈچۈن) راھەت قىلدۇق'),
(5682, 78, 10, 'كېچىنى لىباس قىلدۇق'),
(5683, 78, 11, 'كۈندۈزنى (سىلەر) تىرىكچىلىك قىلىدىغان (ۋاقىت) قىلدۇق'),
(5684, 78, 12, 'ئۈستۈڭلاردا مۇستەھكەم يەتتە ئاسماننى بىنا قىلدۇق'),
(5685, 78, 13, '(سىلەر ئۈچۈن) يېنىپ تۇرغان چىراغنى (كۈننى) ياراتتۇق'),
(5686, 78, 14, 'ئاشلىقلارنى، ئوت - چۆپلەرنى، دەرەخلىرى قويۇق باغچىلارنى ئۆستۈرۈش ئۈچۈن بۇلۇتلاردىن مول ياغدۇرۇپ بەردۇق'),
(5687, 78, 15, 'ئاشلىقلارنى، ئوت - چۆپلەرنى، دەرەخلىرى قويۇق باغچىلارنى ئۆستۈرۈش ئۈچۈن بۇلۇتلاردىن مول ياغدۇرۇپ بەردۇق'),
(5688, 78, 16, 'ئاشلىقلارنى، ئوت - چۆپلەرنى، دەرەخلىرى قويۇق باغچىلارنى ئۆستۈرۈش ئۈچۈن بۇلۇتلاردىن مول ياغدۇرۇپ بەردۇق'),
(5689, 78, 17, 'قىيامەت كۈنى ھەقىقەتەن (مۇكاپاتلاش ۋە جازالاش) ۋاقتىدۇر'),
(5690, 78, 18, 'ئۇ كۈنى سۇر چېلىنىدۇ، سىلەر توپ - توپ بولۇپ كېلىسىلەر'),
(5691, 78, 19, 'ئاسمان ئېچىلىپ نۇرغۇن ئىشىكلەر پەيدا بولىدۇ'),
(5692, 78, 20, 'تاغلار ھەرىكەتلەندۈرۈلۈپ سەررابتەك بولۇپ قالىدۇ (يەنى تاغلار توزۇپ يىراقتىن قارىغۇچىغا سۇدەك تۇيۇلىدۇ)'),
(5693, 78, 21, 'جەھەننەم ھەقىقەتەن (كۇففارلارنى) كۈتۈپ تۇرىدۇ'),
(5694, 78, 22, 'جەھەننەم، شۈبھىسىزكى، گۇناھكارلارنىڭ جايىدۇر'),
(5695, 78, 23, 'ئۇلار جەھەننەمدە ئۇزاق مۇددەت قالىدۇ'),
(5696, 78, 24, 'ئۇلار جەھەننەمدە (جەھەننەمنىڭ ھارارىتىنى يىنىكلىتىلگەن) بىرەر سۆرۈنلۈككە ۋە (تەشنالىقنى قاندۇرىدىغان) بىرەر ئىچىملىككە ئېرىشەلمەيدۇ'),
(5697, 78, 25, 'ئۇلارنىڭ ئېرىشىدىغىنى پەقەت يۇقىرى ھارارەتلىك قايناقسۇ ۋە يىرىڭدىنلا ئىبارەت بولىدۇ'),
(5698, 78, 26, 'بۇ (ئۇلارغا بېرىلىدىغان) مۇۋاپىق جازادۇر'),
(5699, 78, 27, 'چۈنكى ئۇلار (قىلمىشلىرىدىن) ھېساب ئېلىنىشتىن قورقمايتتى'),
(5700, 78, 28, 'ھەمدە بىزنىڭ ئايەتلىرىمىزنى قاتتىق ئىنكار قىلاتتى'),
(5701, 78, 29, 'ھەر نەرسىنى تولۇق خاتىرىلەپ قويىمىز'),
(5702, 78, 30, '(ئۇلارغا ئېيتىمىزكى) «تېتىڭلار! سىلەرگە پەقەت ئازابنىلا زىيادە قىلىمىز»'),
(5703, 78, 31, 'شۈبھىسىزكى، تەقۋادارلار نېمەتكە ئېرىشىدۇ'),
(5704, 78, 32, '(ئۇ نېمەت) باغلار، ئۈزۈملەردۇر'),
(5705, 78, 33, 'تەڭتۇش قىزلاردۇر'),
(5706, 78, 34, '(شاراب بىلەن) تولدۇرۇلغان جاملاردۇر'),
(5707, 78, 35, 'ئۇلار جەننەتتە يالغان سۆز، بىھۇدە سۆز ئاڭلىمايدۇ'),
(5708, 78, 36, '(ئەنە شۇلار) پەرۋەردىگارىڭنىڭ بەرگەن مۇكاپاتى، يېتەرلىك ئاتاسىدۇر'),
(5709, 78, 37, 'ئۇ ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر. ناھايىتى شەپقەتلىكتۇر. (ئۇ كۈندە ھېچ كىشى) ئۇنىڭغا سۆز قىلىشقا قادىر بولالمايدۇ'),
(5710, 78, 38, 'جىبرىئىل ۋە پەرىشتىلەر سەپ بولۇپ تۇرۇپ كېتىدىغان كۈندە (يەنى قىيامەتتە)، ئۇلار (نىڭ ئارىسىدا) مېھرىبان اﷲ نىڭ ئىزنىگە ئېرىشكەن ۋە توغرا سۆزنى قىلغانلاردىن باشقا ھېچ ئەھەدىنىڭ سۆز قىلىشىغا بولمايدۇ'),
(5711, 78, 39, 'ئەنە شۇ چوقۇم بولىدىغان كۈندۇر. كىمكى (ئىمان بىلەن، ياخشى ئەمەل بىلەن) پەرۋەردىگارىغا قايتىدىغان يولنى تۇتۇشنى خالىسا (تۇتسۇن)'),
(5712, 78, 40, 'بىز سىلەرنى ھەقىقەتەن (يۈز بېرىشى) يېقىن ئازابتىن (يەنى ئاخىرەت ئازابىدىن) ئاگاھلاندۇردۇق، ئۇ كۈندە كىشى ئىلگىرى قىلغان ئەمەللىرىنى كۆرىدۇ. كاپىر: «كاشكى مەن تۇپراق بولۇپ كەتسەمچۇ!» دەيدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, '(كۇففارلارنىڭ جانلىرىنى) قاتتىق تارتىپ ئالغۇچى پەرىشتىلەر بىلەن قەسەمكى،'),
(5714, 79, 2, '(مۆمىنلەرنىڭ جانلىرىنى) سىلىق ئالغۇچى پەرىشتىلەر بىلەن قەسەمكى،'),
(5715, 79, 3, '(اﷲ نىڭ ئەمرىنى ئاسماندىن) تېز ئېلىپ چۈشكۈچى پەرىشتىلەر بىلەن قەسەمكى،'),
(5716, 79, 4, '(مۆمىنلەرنىڭ جانلىرىنى ئېلىپ جەننەتكە) ئىلگىرلىگۈچى پەرىشتىلەر بىلەن قەسەمكى،'),
(5717, 79, 5, '(اﷲ نىڭ ئەمرى بىلەن) كائىناتنىڭ ئىشلىرىنى باشقۇرغۇچى پەرىشتىلەر بىلەن قەسەمكى، (سىلەر چوقۇم تىرىلدۈرۈلىسىلەر، سىلەردىن چوقۇم ھېساب ئېلىنىدۇ)'),
(5718, 79, 6, 'بۇ كۈن (يەنى قىيامەت كۈنى) دە زېمىن (ۋە ئۇنىڭدىكى شەيئىلەر قوشۇلۇپ) تەۋرەيدۇ'),
(5719, 79, 7, 'ئۇنىڭ ئارقىسىدىنلا ئاسمان (ۋە ئۇنىڭدىكى شەيئىلەر قوشۇلۇپ) تەۋرەيدۇ'),
(5720, 79, 8, 'بۇ كۈندە دىللارنى (يەنى كۇففارلارنىڭ دىللىرىنى) قورقۇنچ باسىدۇ'),
(5721, 79, 9, 'شۇ (دىللارنىڭ ئىگىلىرىنىڭ) كۆزلىرى (قورقۇنچتىن) تىكىلىپ قارىيالمايدۇ'),
(5722, 79, 10, 'ئۇلار ئېيتىدۇ: «راستلا بىز ئاۋۋالقى ھالىمىزغا قايتۇرۇلامدۇق؟ (يەنى ئۆلگەندىن كېيىن تىرىلەمدۇق؟)'),
(5723, 79, 11, 'ئۇ بىزنىڭ چىرىگەن سۆڭەك بولۇپ قالغان ۋاقتىمىز ئەمەسمۇ؟»'),
(5724, 79, 12, 'ئۇلار ئېيتتى: «ئۇنداق بولىدىغان بولسا، بۇ (يەنى ئۆلگەندىن كېيىن تىرىلىش) زىيانلىق قايتىشتۇر (يەنى بىز ئۆلگەندىن كېيىن تىرىلىدىغان بولساق، دوزاخ ئەھلى بولغانلىقىمىز ئۈچۈن زىيان تارتقۇچىلارمىز)»'),
(5725, 79, 13, 'بىر سەيھە (يەنى ئىسراپىلنىڭ سۇر چېلىشى) بىلەنلا ئۇلار (يەنى جىمى خالايىق) (يەر ئاستىدىن) زېمىننىڭ ئۈستىگە چىقىپ قالىدۇ'),
(5726, 79, 14, 'بىر سەيھە (يەنى ئىسراپىلنىڭ سۇر چېلىشى) بىلەنلا ئۇلار (يەنى جىمى خالايىق) (يەر ئاستىدىن) زېمىننىڭ ئۈستىگە چىقىپ قالىدۇ'),
(5727, 79, 15, 'ساڭا مۇسانىڭ خەۋىرى كەلدىمۇ؟'),
(5728, 79, 16, 'ئۆز ۋاقتىدا ئۇنى پەرۋەردىگارى تۇۋا ناملىق مۇقەددەس ۋادىدا (مۇنداق دەپ) چاقىردى:'),
(5729, 79, 17, '«سەن پىرئەۋننىڭ قېشىغا بارغىن، ئۇ ھەقىقەتەن ھەددىدىن ئاشتى'),
(5730, 79, 18, 'ئۇنىڭغا: (كۇفرىدىن ۋە گۇناھلاردىن) پاك بولۇشنى خالامسەن'),
(5731, 79, 19, 'سېنى پەرۋەردىگارىمنى تونۇشقا يېتەكلىشىمنى خالامسەن؟ (ئۇنى تونۇساڭ پەرۋەردىگارىڭدىن) قورقىسەن - دە، (ياخشى ئەمەللەرنى قىلىسەن)، دېگىن»'),
(5732, 79, 20, 'مۇسا پىرئەۋنگە چوڭ مۆجىزىنى كۆرسەتتى'),
(5733, 79, 21, 'پىرئەۋن (اﷲ نىڭ پەيغەمبىرى مۇسانى) ئىنكار قىلدى ۋە (اﷲ نىڭ ئەمرىگە) ئاسىيلىق قىلدى'),
(5734, 79, 22, 'ئاندىن پىرئەۋن (مۇسادىن) يۈز ئۆرۈپ (ھىيلە - مىكىر ئىشلىتىشكە) كىرىشتى'),
(5735, 79, 23, '(ئۇ سېھرىگەرلەرنى، ئەسكەرلىرىنى، تەۋەلىرىنى) توپلىدى ھەم نىدا قىلىپ'),
(5736, 79, 24, 'دېدى: «مەن سىلەرنىڭ بۈيۈك پەرۋەردىگارىڭلار بولىمەن»'),
(5737, 79, 25, 'اﷲ ئۇنى دۇنيا ۋە ئاخىرەتنىڭ ئازابى بىلەن جازالىدى'),
(5738, 79, 26, 'شەك - شۈبھىسىزكى، اﷲ تىن قورقىدىغانلار بۇنىڭدىن ئىبرەت ئالىدۇ'),
(5739, 79, 27, 'سىلەرنى يارىتىش مۈشكۈلمۇ؟ ياكى ئاسماننى يارىتىش مۈشكۈلمۇ؟ اﷲ ئاسماننى ياراتتى'),
(5740, 79, 28, 'ئۇنىڭ ئېگىزلىكىنى يۈكسەك قىلدى ۋە كەم - كۈتىسىز قىلدى'),
(5741, 79, 29, 'ئۇنىڭ كېچىسىنى قاپقاراڭغۇ، كۈندۈزىنى يوپيورۇق قىلدى'),
(5742, 79, 30, 'شۇنىڭدىن كېيىن زېمىننى (ئەھلى زېمىننىڭ تۇرۇشىغا لايىق قىلىپ) يايدى'),
(5743, 79, 31, 'زېمىندىن بۇلاقلارنى ئاقتۇردى، ئۆسۈملۈكلەرنى ئۆستۈردى'),
(5744, 79, 32, 'تاغلارنى (زېمىندا) مۇقىم قىلدى'),
(5745, 79, 33, 'اﷲ تائالا ئۇلارنىڭ ھەممىسىنى سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ پايدىلىنىشى ئۈچۈن (ياراتتى)'),
(5746, 79, 34, 'قىيامەت كەلگەندە،'),
(5747, 79, 35, '(شۇ) كۈندە ئىنسان (ياخشى - يامان) قىلمىشلىرىنى ئەسلەيدۇ'),
(5748, 79, 36, 'قارىغۇچىلارغا جەھەننەم ئوچۇق كۆرسىتىلىدۇ'),
(5749, 79, 37, 'كىمكى (كۇفرىدا، گۇناھتا) چېكىدىن ئاشىدىكەن،'),
(5750, 79, 38, 'دۇنيا تىرىكچىلىكىنى (ئاخىرەتتىن) ئارتۇق كۆرىدىكەن،'),
(5751, 79, 39, 'ئۇنىڭ جايى ھەقىقەتەن جەھەننەم بولىدۇ'),
(5752, 79, 40, 'پەرۋەردىگارىنىڭ ئالدىدا (سوراققا تارتىلىشتىن) قورقىدىغان، ئۆزىنى نەپسى خاھىشىغا بېرىلىشتىن (يەنى ھارام قىلىنغان نەرسىلەردىن) چەكلىگەن ئادەمگە كەلسەك،'),
(5753, 79, 41, 'ھەقىقەتەن ئۇنىڭ جايى جەننەت بولىدۇ'),
(5754, 79, 42, 'ئۇلار سەندىن قىيامەتنىڭ قاچان بولىدىغانلىقىنى سورايدۇ'),
(5755, 79, 43, 'سەن ئۇنى قانداقمۇ بايان قىلىپ بېرەلەيسەن؟'),
(5756, 79, 44, 'ئۇنى بىلىش پەرۋەردىگارىڭغا خاستۇر'),
(5757, 79, 45, 'سېنىڭ ۋەزىپەڭ پەقەت قىيامەتتىن قورقىدىغان ئادەمنى ئاگاھلاندۇرۇشتۇر'),
(5758, 79, 46, 'ئۇلار قىيامەتنى كۆرگەن كۈندە، گويا ئۆزلىرىنى (دۇنيادا) پەقەت بىر كەچقۇرۇن ياكى بىر ئەتىگەن تۇرغاندەك (گۇمان قىلىدۇ)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'يېنىغا ئەما كەلگەنلىكى ئۈچۈن (پەيغەمبەر ئەلەيھىسسالام) تەرىنى تۈردى ۋە يۈزىنى ئۆرىۋالدى'),
(5760, 80, 2, 'يېنىغا ئەما كەلگەنلىكى ئۈچۈن (پەيغەمبەر ئەلەيھىسسالام) تەرىنى تۈردى ۋە يۈزىنى ئۆرىۋالدى'),
(5761, 80, 3, '(ئى مۇھەممەد!) نېمە بىلىسەن؟ (بەلكى) ئۇ (سەندىن ئالغان مەرىپەت بىلەن گۇناھلىرىدىن) پاكلىنىشى مۇمكىن'),
(5762, 80, 4, 'ياكى ۋەز - نەسىھەت ئاڭلاپ ئۇنىڭدىن پايدىلىنىشى مۇمكىن'),
(5763, 80, 5, 'ئۆزىنى (ئىماندىن) بىھاجەت ھېسابلايدىغان ئادەمگە كەلسەك، سەن ئۇنىڭغا يۈزلىنىسەن (يەنى ئۇنىڭغا قۇلاق سېلىپ تەبلىغ قىلىشقا كۆڭۈل بۆلىسەن)'),
(5764, 80, 6, 'ئۆزىنى (ئىماندىن) بىھاجەت ھېسابلايدىغان ئادەمگە كەلسەك، سەن ئۇنىڭغا يۈزلىنىسەن (يەنى ئۇنىڭغا قۇلاق سېلىپ تەبلىغ قىلىشقا كۆڭۈل بۆلىسەن)'),
(5765, 80, 7, 'ئۇ (كۇفرىدىن، گۇناھلاردىن) پاكلانمىسا، ساڭا نېمە زىيىنىدى'),
(5766, 80, 8, 'سەندىن مەرىپەت تىلەپ، اﷲ تىن قورققان ھالدا يۈگۈرۈپ كەلگەن كىشىگە كەلسەك'),
(5767, 80, 9, 'سەندىن مەرىپەت تىلەپ، اﷲ تىن قورققان ھالدا يۈگۈرۈپ كەلگەن كىشىگە كەلسەك،'),
(5768, 80, 10, 'سەن ئۇنىڭغا قارىماي قويىسەن'),
(5769, 80, 11, '(بۇنىڭدىن كېيىن) ئۇنداق قىلمىغىن. بۇ ئايەتلەر ۋەزدۇر'),
(5770, 80, 12, 'خالىغان ئادەم ئۇنىڭدىن ۋەز - نەسىھەت ئالىدۇ'),
(5771, 80, 13, 'ئۇ (يەنى قۇرئان ئايەتلىرى) قىممەتلىك، (قەدرى) يۇقىرى پاك سەھىپىلەرگە يېزىلغاندۇر'),
(5772, 80, 14, 'ئۇ (يەنى قۇرئان ئايەتلىرى) قىممەتلىك، (قەدرى) يۇقىرى پاك سەھىپىلەرگە يېزىلغاندۇر'),
(5773, 80, 15, '(ئۇ) ھۆرمەتلىك ياخشى (پەرىشتىلەردىن بولغان) پۈتۈكچىلەرنىڭ قولىدىدۇر'),
(5774, 80, 16, '(ئۇ) ھۆرمەتلىك ياخشى (پەرىشتىلەردىن بولغان) پۈتۈكچىلەرنىڭ قولىدىدۇر'),
(5775, 80, 17, 'ئىنسانغا لەنەت بولسۇنكى، ئۇ ئەجەب كاپىر بولدى!'),
(5776, 80, 18, 'اﷲ ئۇنى نېمىدىن ياراتتى؟'),
(5777, 80, 19, 'اﷲ ئۇنى ئابىمەنىيدىن ياراتتى، (ئۇ تولۇق يارىتىلىپ بولغۇچە) اﷲ ئۇنى بىر قانچە باسقۇچلارغا بۆلۈپ پەيدىن - پەي ياراتتى'),
(5778, 80, 20, 'ئاندىن ئۇنىڭغا (ماڭار) يولىنى ئاسانلاشتۇرۇپ بەردى'),
(5779, 80, 21, 'ئاندىن ئۇنى ۋاپات قىلدۇردى، ئۇنىڭغا (كۆمۈلىدىغان) قەبرىنى بەرپا قىلدى'),
(5780, 80, 22, 'ئاندىن اﷲ ئۇنى خالىغان ۋاقىتتا تىرىلدۈرىدۇ'),
(5781, 80, 23, 'ھەرگىز ئۇنداق ئەمەس (يەنى بۇ كاپىر ئىنسان تەكەببۇرلۇقىدىن يانسۇن)، ئۇ اﷲ نىڭ بۇيرۇغانلىرىنى بەجا كەلتۈرمىدى'),
(5782, 80, 24, '(بۇ كاپىر) ئىنسان ئۆزىنىڭ يېمەكلىكلىرىگە (ئىبرەت نەزىرى بىلەن) قارىسۇن'),
(5783, 80, 25, '(بىز بۇلۇتتىن) زور مىقداردا يامغۇر ياغدۇردۇق'),
(5784, 80, 26, 'ئاندىن زېمىننى (ئۇنىڭدىن ئۆسۈملۈكلەرنى چىقىرىش بىلەن) ياردۇق'),
(5785, 80, 27, 'سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ مەنپەئەتلىنىشى ئۈچۈن، زېمىندا ئاشلىقلارنى، ئۈزۈمنى، ئوتياشلارنى، زەيتۇننى، خورمىنى، دەرەخلىرى قويۇق باغچىلارنى، تۈرلۈك مېۋىلەرنى، ئوت - چۆپلەرنى ئۆستۈردۇق'),
(5786, 80, 28, 'سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ مەنپەئەتلىنىشى ئۈچۈن، زېمىندا ئاشلىقلارنى، ئۈزۈمنى، ئوتياشلارنى، زەيتۇننى، خورمىنى، دەرەخلىرى قويۇق باغچىلارنى، تۈرلۈك مېۋىلەرنى، ئوت - چۆپلەرنى ئۆستۈردۇق'),
(5787, 80, 29, 'سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ مەنپەئەتلىنىشى ئۈچۈن، زېمىندا ئاشلىقلارنى، ئۈزۈمنى، ئوتياشلارنى، زەيتۇننى، خورمىنى، دەرەخلىرى قويۇق باغچىلارنى، تۈرلۈك مېۋىلەرنى، ئوت - چۆپلەرنى ئۆستۈردۇق'),
(5788, 80, 30, 'سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ مەنپەئەتلىنىشى ئۈچۈن، زېمىندا ئاشلىقلارنى، ئۈزۈمنى، ئوتياشلارنى، زەيتۇننى، خورمىنى، دەرەخلىرى قويۇق باغچىلارنى، تۈرلۈك مېۋىلەرنى، ئوت - چۆپلەرنى ئۆستۈردۇق'),
(5789, 80, 31, 'سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ مەنپەئەتلىنىشى ئۈچۈن، زېمىندا ئاشلىقلارنى، ئۈزۈمنى، ئوتياشلارنى، زەيتۇننى، خورمىنى، دەرەخلىرى قويۇق باغچىلارنى، تۈرلۈك مېۋىلەرنى، ئوت - چۆپلەرنى ئۆستۈردۇق'),
(5790, 80, 32, 'سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ مەنپەئەتلىنىشى ئۈچۈن، زېمىندا ئاشلىقلارنى، ئۈزۈمنى، ئوتياشلارنى، زەيتۇننى، خورمىنى، دەرەخلىرى قويۇق باغچىلارنى، تۈرلۈك مېۋىلەرنى، ئوت - چۆپلەرنى ئۆستۈردۇق'),
(5791, 80, 33, '(قىيامەتنىڭ) قۇلاقنى گاس قىلغۇدەك قاتتىق ئاۋازى كەلگەن چاغدا (ھەممە ئادەم ئۆز ھالى بىلەن بولۇپ قالىدۇ)'),
(5792, 80, 34, 'ئۇ كۈندە كىشى ئۆزىنىڭ قېرىندىشىدىن، ئانىسىدىن، ئاتىسىدىن، خوتۇنىدىن ۋە بالىلىرىدىن قاچىدۇ'),
(5793, 80, 35, 'ئۇ كۈندە كىشى ئۆزىنىڭ قېرىندىشىدىن، ئانىسىدىن، ئاتىسىدىن، خوتۇنىدىن ۋە بالىلىرىدىن قاچىدۇ'),
(5794, 80, 36, 'ئۇ كۈندە كىشى ئۆزىنىڭ قېرىندىشىدىن، ئانىسىدىن، ئاتىسىدىن، خوتۇنىدىن ۋە بالىلىرىدىن قاچىدۇ'),
(5795, 80, 37, 'ئۇ كۈندە ھەر ئادەمنىڭ بىر ھالى بولىدۇكى، ئۇنىڭ باشقىلارغا قارىشىغا (شۇ ھالى) يار بەرمەيدۇ'),
(5796, 80, 38, 'ئۇ كۈندە نۇرغۇن يۈزلەردىن نۇر، كۈلكە ۋە خۇشال - خۇراملىق يېغىپ تۇرىدۇ'),
(5797, 80, 39, 'ئۇ كۈندە نۇرغۇن يۈزلەردىن نۇر، كۈلكە ۋە خۇشال - خۇراملىق يېغىپ تۇرىدۇ'),
(5798, 80, 40, 'يەنە بۇ كۈندە نۇرغۇن يۈزلەرنى چاڭ - توزان بېسىپ كەتكەن، قارىداپ كەتكەن بولىدۇ'),
(5799, 80, 41, 'يەنە بۇ كۈندە نۇرغۇن يۈزلەرنى چاڭ - توزان بېسىپ كەتكەن، قارىداپ كەتكەن بولىدۇ'),
(5800, 80, 42, 'بۇلار بولسا كاپىرلاردۇر، فاجىرلاردۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'كۈننىڭ نۇرى ئۆچكەن چاغدا،'),
(5802, 81, 2, 'يۇلتۇزلار تۆكۈلگەن چاغدا،'),
(5803, 81, 3, 'تاغلار گۇمران بولغان چاغدا،'),
(5804, 81, 4, 'بوغاز تۆگىلەر تاشلىۋېتىلگەن چاغدا،'),
(5805, 81, 5, 'ياۋايى ھايۋانلار توپلانغان چاغدا،'),
(5806, 81, 6, 'دېڭىزلار ئوت بولۇپ لاۋۇلدىغان چاغدا،'),
(5807, 81, 7, 'جانلار (ئۆز قاياشلىرىغا) قوشۇلغان چاغدا،'),
(5808, 81, 8, 'تىرىك كۆمۈۋېتىلگەن قىزدىن سەن قايسى گۇناھ بىلەن ئۆلتۈرۈلدۈڭ؟ دەپ سورالغان چاغدا،'),
(5809, 81, 9, 'تىرىك كۆمۈۋېتىلگەن قىزدىن سەن قايسى گۇناھ بىلەن ئۆلتۈرۈلدۈڭ؟ دەپ سورالغان چاغدا،'),
(5810, 81, 10, 'نامە - ئەماللار ئېچىلغان چاغدا،'),
(5811, 81, 11, 'ئاسمان ئېچىپ تاشلانغان چاغدا،'),
(5812, 81, 12, 'دوزاخ قىزىتىلغان چاغدا،'),
(5813, 81, 13, 'جەننەت (تەقۋادارلارغا) يېقىنلاشتۇرۇلغان چاغدا،'),
(5814, 81, 14, 'ھەر ئادەم ئۆزىنىڭ قىلغان (ياخشى - يامان) ئىشلىرىنى بىلىدۇ'),
(5815, 81, 15, 'كۈندۈزى يوشۇرۇنغۇچى يۇلتۇزلار بىلەن، پاتىدىغان ۋاقتىدا يوشۇرۇنىدىغان يۇلتۇزلار بىلەن قەسەم قىلىمەن'),
(5816, 81, 16, 'كۈندۈزى يوشۇرۇنغۇچى يۇلتۇزلار بىلەن، پاتىدىغان ۋاقتىدا يوشۇرۇنىدىغان يۇلتۇزلار بىلەن قەسەم قىلىمەن،'),
(5817, 81, 17, 'قاراڭغۇلۇققا چۆمگەن كېچە بىلەن قەسەم قىلىمەن،'),
(5818, 81, 18, 'يورۇشقا باشلىغان سۈبھى بىلەن قەسەم قىلىمەن'),
(5819, 81, 19, 'قۇرئان ھەقىقەتەن (اﷲ نىڭ) ئىززەتلىك بىر ئەلچى (ئارقىلىق نازىل قىلىنغان) سۆزىدۇر'),
(5820, 81, 20, 'ئۇ (ئەلچى) كۈچلۈكتۇر، اﷲ نىڭ دەرگاھىدا مەرتىۋىلىكتۇر'),
(5821, 81, 21, 'پەرىشتىلەر ئۇنىڭغا ئىتائەت قىلغۇچىدۇر. بۇ يەردە (يەنى ئاسماندا) ئۇ ئىشەنچلىكتۇر'),
(5822, 81, 22, 'سىلەرنىڭ ھەمراھىڭلار (يەنى مۇھەممەد ئەلەيھىسسالام) مەجنۇن ئەمەستۇر'),
(5823, 81, 23, 'ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) جىبرىئىلنى ھەقىقەتەن روشەن ئۇپۇقنىڭ شەرقىدە كۆردى'),
(5824, 81, 24, 'ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) (غەيبنى) تەبلىغ قىلىشتا بېخىل ئەمەستۇر'),
(5825, 81, 25, 'ئۇ (يەنى قۇرئان) قوغلاندى شەيتاننىڭ سۆزى ئەمەستۇر'),
(5826, 81, 26, 'قەيەرگە بارىسىلەر؟'),
(5827, 81, 27, 'ئۇ (يەنى قۇرئان) پۈتۈن ئەھلى جاھانغا نەسىھەتتۇر'),
(5828, 81, 28, 'سىلەرنىڭ ئاراڭلاردىن (دىن بارىسىدا) راۋۇرۇس بولۇشنى خالايدىغانلارغا (قۇرئان) نەسىھەتتۇر'),
(5829, 81, 29, 'پەقەت ئالەملەرنىڭ پەرۋەردىگارى اﷲ خالىغاندىلا، ئاندىن سىلەر خالايسىلەر (يەنى اﷲ خالىمىغۇچە سىلەرنىڭ خالىغىنىڭلارنىڭ ھېچ پايدىسى يوق)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'ئاسمان يېرىلغان چاغدا،'),
(5831, 82, 2, 'يۇلتۇزلار تۆكۈلگەن چاغدا،'),
(5832, 82, 3, 'دېڭىزلار بىر - بىرىگە قوشۇلغان چاغدا،'),
(5833, 82, 4, 'قەبرىلەر ئاستى - ئۈستى قىلىنغان (يەنى ئۆلۈكلەر چىقىرىلغان) چاغدا،'),
(5834, 82, 5, 'ھەر ئادەم ئىلگىرى - كېيىن قىلغان ئىشلىرىنىڭ ھەممىسىنى بىلىدۇ'),
(5835, 82, 6, 'ئى ئىنسان! سېنى مەرھەمەتلىك پەرۋەردىگارىڭغا ئاسىيلىق قىلىشقا نېمە ئالدىدى؟'),
(5836, 82, 7, 'ئۇ سېنى ياراتتى، (ئەزالىرىڭنى) بېجىرىم، قامىتىڭنى تۈز قىلدى'),
(5837, 82, 8, 'سېنى ئۇ خالىغان شەكىلدە قۇراشتۇردى'),
(5838, 82, 9, 'ھەرگىز ئۇنداق قىلماڭلار (يەنى اﷲ نىڭ كەرەمىگە مەغرۇر بولماڭلار). (ئى مەككە كۇففارلىرى!) بەلكى سىلەر دىننى ئىنكار قىلىسىلەر'),
(5839, 82, 10, 'ھالبۇكى سىلەرنىڭ ئۈستۈڭلاردا سىلەرنى كۈزىتىپ تۇرغۇچى پەرىشتىلەر بار'),
(5840, 82, 11, 'ئۇلار (اﷲ نىڭ دەرگاھىدا) ھۆرمەتلىك پەرىشتىلەر بولۇپ، (سىلەرنىڭ سۆزلىرىڭلارنى ۋە ئەمەللىرىڭلار) نى يېزىپ تۇرىدۇ'),
(5841, 82, 12, 'ئۇلار قىلمىشىڭلارنى بىلىپ تۇرىدۇ'),
(5842, 82, 13, 'مۆمىنلەر شەك - شۈبھىسىز جەننەتتە بولىدۇ،'),
(5843, 82, 14, 'كاپىرلار شەك - شۈبھىسىز دوزاختا بولىدۇ'),
(5844, 82, 15, 'ئۇلار قىيامەت كۈنى دوزاخقا كىرىدۇ'),
(5845, 82, 16, 'ئۇلار ھەرگىز دوزاختىن ئايرىلمايدۇ'),
(5846, 82, 17, 'قىيامەت كۈنىنىڭ (دەھشەتلىكلىكىنىڭ) قانداقلىقىنى قانداق بىلەلەيسەن؟'),
(5847, 82, 18, 'قىيامەت كۈنىنىڭ (دەھشەتلىكلىكىنىڭ) قانداقلىقىنى قانداق بىلەلەيسەن؟'),
(5848, 82, 19, 'ئۇ كۈندە بىر ئادەمنىڭ يەنە بىر ئادەمگە ياردەم بېرىشى قولىدىن كەلمەيدۇ، بۇ كۈندە ھۆكۈم اﷲ قا خاستۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, '(ئۆلچەمدە ۋە تارازىدا) كەم بەرگۈچىلەرگە ۋاي!'),
(5850, 83, 2, 'ئۇلار كىشىلەردىن ئۆلچەپ ئالغان چاغدا، تولۇق ئالىدۇ'),
(5851, 83, 3, 'كىشىلەرگە ئۆلچەپ ياكى تارتىپ بەرگەن چاغدا، كەم بېرىدۇ'),
(5852, 83, 4, 'ئۇلار بۈيۈك بىر كۈندە تىرىلىدىغانلىقىغا ئىشەنمەمدۇ'),
(5853, 83, 5, 'ئۇلار بۈيۈك بىر كۈندە تىرىلىدىغانلىقىغا ئىشەنمەمدۇ'),
(5854, 83, 6, 'ئۇ كۈندە ئىنسانلار ئالەملەرنىڭ پەرۋەردىگارىنىڭ ھۇزۇرىدا تىك تۇرىدۇ،'),
(5855, 83, 7, 'ئۇلار (تارازىدا، ئۆلچەمدە) كەم بېرىشتىن يانسۇن، يامان ئادەملەرنىڭ نامە - ئەمالى چوقۇم سىججىندا بولىدۇ'),
(5856, 83, 8, 'سىججىن دېگەن نېمە؟ ئۇنى قانداق بىلەلەيسەن؟'),
(5857, 83, 9, 'ئۇ (يامان ئادەملەرنىڭ قىلمىشلىرى) خاتىرىلەنگەن بىر دەپتەردۇر'),
(5858, 83, 10, 'بۇ كۈندە ئىنكار قىلغۇچىلارغا ۋاي!'),
(5859, 83, 11, 'ئۇلار قىيامەت كۈنىنى ئىنكار قىلىدۇ'),
(5860, 83, 12, 'ئۇنى پەقەت (كۇفرىدا) ھەددىدىن ئاشقان، ئىنتايىن گۇناھكار ئادەملا ئىنكار قىلىدۇ'),
(5861, 83, 13, 'ئۇنىڭغا بىزنىڭ ئايەتلىرىمىز تىلاۋەت قىلىنسا، ئۇ: «(بۇ) قەدىمكىلەرنىڭ ھېكايىلىرىدۇر» دەيدۇ'),
(5862, 83, 14, 'ھەرگىز ئۇنداق ئەمەس، بەلكى ئۇلارنىڭ گۇناھلىرى تۈپەيلىدىن دىللىرى قارىيىپ كەتكەن'),
(5863, 83, 15, 'ھەرگىز ئۇنداق ئەمەس، ھەقىقەتەن ئۇلار بۇ كۈندە پەرۋەردىگارىنى كۆرۈشتىن مەنئى قىلىنغان'),
(5864, 83, 16, 'ئاندىن ئۇلار ھەقىقەتەن دوزاخقا داخىل بولغۇچىلاردۇر'),
(5865, 83, 17, 'ئاندىن (ئۇلارغا): «مانا بۇ سىلەر ئىنكار قىلغان نەرسە (يەنى ئازاب) دۇر» دېيىلىدۇ'),
(5866, 83, 18, 'راستلا ياخشى بەندىلەرنىڭ نامە - ئەمالى شەك - شۈبھىسىز ئىللىيوندا بولىدۇ'),
(5867, 83, 19, 'ئىللىيوننىڭ نېمە ئىكەنلىكىنى سەن قانداق بىلەلەيسەن؟'),
(5868, 83, 20, '(ئۇ) ياخشىلارنىڭ ئەمەللىرى خاتىرىلەنگەن دەپتەردۇر'),
(5869, 83, 21, 'ئۇنىڭغا اﷲ نىڭ يېقىن پەرىشتىلىرى گۇۋاھ بولىدۇ'),
(5870, 83, 22, 'ياخشىلار نېمەت ئىچىدە (يەنى جەننەتتە) بولىدۇ'),
(5871, 83, 23, 'ئۇلار تەختلەر ئۈستىدە تۇرۇپ (اﷲ نىڭ جەننەتتە ئۇلارغا بەرگەن تۈرلۈك نېمەتلىرىگە) قارايدۇ'),
(5872, 83, 24, 'ئۇلارغا قارايدىغان بولساڭ، نېمەتنىڭ (ئۇلارنىڭ چىرايلىرىدىن چىقىپ تۇرغان) ئەسىرىنى تونۇيسەن'),
(5873, 83, 25, 'ئۇلار ئاغزى پېچەتلەنگەن ساپ شاراب بىلەن سۇغىرىلىدۇ، ئىچىشنىڭ ئاخىرىدا ئۇنىڭدىن ئىپارنىڭ ھىدى كېلىپ تۇرىدۇ. قىزىققۇچىلار شۇنىڭغا قىزىقسۇن!'),
(5874, 83, 26, 'ئۇلار ئاغزى پېچەتلەنگەن ساپ شاراب بىلەن سۇغىرىلىدۇ، ئىچىشنىڭ ئاخىرىدا ئۇنىڭدىن ئىپارنىڭ ھىدى كېلىپ تۇرىدۇ. قىزىققۇچىلار شۇنىڭغا قىزىقسۇن!'),
(5875, 83, 27, 'ئۇنىڭغا (يەنى ساپ شارابقا) تەسنىمنىڭ (سۈيى) ئارىلاشتۇرۇلغان'),
(5876, 83, 28, 'تەسنىم جەننەتتىكى بىر بۇلاق بولۇپ، ئۇنىڭدىن اﷲ نىڭ يېقىن بەندىلىرى ئىچىدۇ'),
(5877, 83, 29, 'كاپىرلار ھەقىقەتەن مۆمىنلەرنى (مەسخىرە قىلىپ) كۈلەتتى،'),
(5878, 83, 30, '(مۆمىنلەر) ئۇلارنىڭ ئالدىدىن ئۆتكەندە، ئۇلار ئۆزئارا كۆز قىسىشاتتى،'),
(5879, 83, 31, 'ئۆيلىرىگە قايتقاندا (مۆمىنلەرنىڭ غەيۋىتىنى قىلىپ) مەزە قىلغان ھالدا قايتىشاتتى'),
(5880, 83, 32, 'مۆمىنلەرنى كۆرگەندە: «بۇلار شەكسىز ئاداشقانلار» دېيىشەتتى'),
(5881, 83, 33, 'ئۇلار مۆمىنلەرنى كۈزىتىشكە ئەۋەتىلگىنى يوق'),
(5882, 83, 34, 'بۈگۈن (يەنى قىيامەت كۈنى) مۆمىنلەر كاپىرلاردىن كۈلىدۇ،'),
(5883, 83, 35, '(كاپىرلار) نىڭ ھالىغا (ئېسىل) تەختلەر ئۈستىدە ئولتۇرۇپ نەزەر تاشلايدۇ'),
(5884, 83, 36, '(كاپىرلار مۆمىنلەرنى) مەسخىرە قىلغان قىلمىشلىرىنىڭ جازاسىنى تارتتىمۇ؟');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'ئاسمان يېرىلغان،'),
(5886, 84, 2, 'پەرۋەردىگارنىڭ ئەمرىنى ئاڭلاپ، ئىتائەت قىلغان، پەرۋەردىگارنىڭ ئەمرىنى ئاڭلاشقا، ئىتائەت قىلىشقا لايىق بولغان چاغدا،'),
(5887, 84, 3, 'زېمىن سوزۇلغان،'),
(5888, 84, 4, 'قوينىدىكى نەرسىلەرنى (يەنى ئۆلۈكلەرنى، مەدەنلەرنى) سىرتقا چىقىرىپ قۇرۇقدىنىپ قالغان،'),
(5889, 84, 5, 'پەرۋەردىگارىنىڭ ئەمرىنى ئاڭلاپ ئىتائەت قىلغان، پەرۋەردىگارىنىڭ ئەمرىنى ئاڭلاشقا، ئىتائەت قىلىشقا لايىق بولغان چاغدا (ئىنسان خىيالىغا كەلمىگەن دەھشەتلىك ئەھۋاللارغا يولۇقىدۇ)'),
(5890, 84, 6, 'ئى ئىنسان! سەن ھەقىقەتەن پەرۋەردىگارىڭغا مۇلاقات بولغىنىڭغا قەدەر (ئۆز ئىشىڭدا) سەئيى - ئىجتىھات قىلىسەن (يەنى ئەمەلىڭنىڭ نەتىجىسىنى كۆرىسەن)'),
(5891, 84, 7, 'نامە - ئەمالى ئوڭ تەرىپىدىن بېرىلگەن ئادەمدىن ئاسان ھېساب ئېلىنىدۇ'),
(5892, 84, 8, 'نامە - ئەمالى ئوڭ تەرىپىدىن بېرىلگەن ئادەمدىن ئاسان ھېساب ئېلىنىدۇ'),
(5893, 84, 9, '(ئۇ جەننەتتىكى) ئائىلىسىگە خۇشال - خۇرام قايتىدۇ'),
(5894, 84, 10, 'نامە - ئەمالى ئارقا تەرىپىدىن بېرىلگەن ئادەم'),
(5895, 84, 11, '«ۋاي دەپ توۋلايدۇ (يەنى ئۆلۈمنى ئارزۇ قىلىدۇ)،'),
(5896, 84, 12, 'دوزاخقا كىرىدۇ'),
(5897, 84, 13, 'چۈنكى ئۇ (دۇنيادىكى چېغىدا) ئائىلىسىدە شاد - خۇرام ئىدى.'),
(5898, 84, 14, 'ئۇ پەرۋەردىگارىنىڭ دەرگاھىغا قايتىمەن دەپ گۇمان قىلدى'),
(5899, 84, 15, 'ئۇنداق ئەمەس، (يەنى ئۇ پەرۋەردىگارىنىڭ دەرگاھىغا قايتىدۇ). پەرۋەردىگارى ھەقىقەتەن ئۇنى كۆرۈپ تورغۇچىدۇر'),
(5900, 84, 16, 'شەپەق (يەنى كۈن پاتقاندىن كېيىنكى ئۇپۇقتىكى قىزىللىق) بىلەن، كېچە ۋە ئۇنىڭ قاراڭغۇلۇقى باسقان نەرسىلەر بىلەن، نۇرى كامالەتكە يەتكەن ئاي (يەنى تولۇن ئاي) بىلەن قەسەمكى'),
(5901, 84, 17, 'شەپەق (يەنى كۈن پاتقاندىن كېيىنكى ئۇپۇقتىكى قىزىللىق) بىلەن، كېچە ۋە ئۇنىڭ قاراڭغۇلۇقى باسقان نەرسىلەر بىلەن، نۇرى كامالەتكە يەتكەن ئاي (يەنى تولۇن ئاي) بىلەن قەسەمكى'),
(5902, 84, 18, 'شەپەق (يەنى كۈن پاتقاندىن كېيىنكى ئۇپۇقتىكى قىزىللىق) بىلەن، كېچە ۋە ئۇنىڭ قاراڭغۇلۇقى باسقان نەرسىلەر بىلەن، نۇرى كامالەتكە يەتكەن ئاي (يەنى تولۇن ئاي) بىلەن قەسەمكى،'),
(5903, 84, 19, 'سىلەر جەزمەن بىر ھالدىن يەنە بىر ھالغا يۆتكىلىپ تورىسىلەر'),
(5904, 84, 20, 'ئۇلار (يەنى مۇشرىكلار) نېمىشقا ئىمان ئېيتمايدۇ؟'),
(5905, 84, 21, 'ئۇلارنىڭ يېنىدا قۇرئان ئوقۇلغان چاغدا نېمىشقا سەجدە قىلمايدۇ؟'),
(5906, 84, 22, 'بەلكى كاپىرلار (ھەقىقەتنى) ئىنكار قىلىدۇ'),
(5907, 84, 23, 'ئۇلارنىڭ دىللىرىدىكى يوشۇرۇن نەرسىلەرنى اﷲ ئوبدان بىلىدۇ'),
(5908, 84, 24, 'ئۇلارغا قاتتىق ئازاب بىلەن خۇش خەۋەر بەرگىن'),
(5909, 84, 25, 'پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەر ئۈزۈلمەس ساۋابقا ئېرىشىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'بۇرۇجلارنىڭ ئىگىسى بولغان ئاسمان بىلەن، قىيامەت كۈنى بىلەن، جۈمە كۈنى بىلەن، ھارپا كۈنى بىلەن قەسەمكى'),
(5911, 85, 2, 'بۇرۇجلارنىڭ ئىگىسى بولغان ئاسمان بىلەن، قىيامەت كۈنى بىلەن، جۈمە كۈنى بىلەن، ھارپا كۈنى بىلەن قەسەمكى'),
(5912, 85, 3, 'بۇرۇجلارنىڭ ئىگىسى بولغان ئاسمان بىلەن، قىيامەت كۈنى بىلەن، جۈمە كۈنى بىلەن، ھارپا كۈنى بىلەن قەسەمكى،'),
(5913, 85, 4, 'ئورەكلەرنىڭ ئىگىلىرىگە لەنەت بولسۇن'),
(5914, 85, 5, 'ئۇ ئورەكلەر لاۋۇلداپ تۇرغان ئوتلار بىلەن تولدۇرۇلغان ئىدى'),
(5915, 85, 6, 'ئەينى ۋاقىتتا ئۇلار ئورەكلەر ئۈستىدە ئولتۇرۇپ ئۆزلىرىنىڭ مۆمىنلەرگە قىلىۋاتقان ئىشلىرىنى كۆرۈپ تۇراتتى'),
(5916, 85, 7, 'ئەينى ۋاقىتتا ئۇلار ئورەكلەر ئۈستىدە ئولتۇرۇپ ئۆزلىرىنىڭ مۆمىنلەرگە قىلىۋاتقان ئىشلىرىنى كۆرۈپ تۇراتتى،'),
(5917, 85, 8, 'ئۇلار مۆمىنلەرنى پەقەت غالىب، مەدھىيىگە لايىق اﷲ قا ئىمان ئېيتقانلىقلىرى ئۈچۈنلا يامان كۆردى. ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر. اﷲ ھەممە نەرسىنى كۆرۈپ تۇرغۇچىدۇر،'),
(5918, 85, 9, 'ئۇلار مۆمىنلەرنى پەقەت غالىب، مەدھىيىگە لايىق اﷲ قا ئىمان ئېيتقانلىقلىرى ئۈچۈنلا يامان كۆردى. ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا خاستۇر. اﷲ ھەممە نەرسىنى كۆرۈپ تۇرغۇچىدۇر'),
(5919, 85, 10, 'مۆمىن ئەر ۋە مۆمىن ئاياللارغا زىيانكەشلىك قىلغاندىن كېيىن كۇفرىدىن قايتمىغانلار، شۈبھىسىزكى، جەھەننەمنىڭ ئازابىغا دۇچار بولىدۇ، ئۇلارغا ئوت بىلەن ئازاب قىلىنىدۇ'),
(5920, 85, 11, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار، شۈبھىسىزكى، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە سازاۋەر بولىدۇ. بۇ چوڭ بەختتۇر'),
(5921, 85, 12, 'پەرۋەردىگارىڭنىڭ جازاسى شەك - شۈبھىسىز قاتتىقتۇر'),
(5922, 85, 13, 'اﷲ (خالايىقنى) ھەقىقەتەن يوقتىن بار قىلىدۇ (يەنى ئۆلگەندىن كېيىن ئۇلارنى) تىرىلدۈرىدۇ'),
(5923, 85, 14, 'اﷲ (مۆمىن بەندىلەرنىڭ گۇناھىنى) مەغپىرەت قىلغۇچىدۇر. (ئۇلارنى) دوست تۇتقۇچىدۇر،'),
(5924, 85, 15, 'ئەرشنىڭ ئىگىسىدۇر، ناھايىتى ئۇلۇغدۇر،'),
(5925, 85, 16, 'خالىغىنىنى قىلغۇچىدۇر'),
(5926, 85, 17, '(ئى مۇھەممەد!) سەن لەشكەرلەرنىڭ - پىرئەۋن ۋە سەمۇدنىڭ قىسسىسىدىن خەۋەر تاپتىڭمۇ؟'),
(5927, 85, 18, '(ئى مۇھەممەد!) سەن لەشكەرلەرنىڭ - پىرئەۋن ۋە سەمۇدنىڭ قىسسىسىدىن خەۋەر تاپتىڭمۇ؟'),
(5928, 85, 19, '(قۇرەيش مۇشرىكلىرى ئاشۇ كاپىرلارغا نازىل بولغان ئازابتىن ئىبرەت ئالماي) داۋاملىق ئىنكار قىلىشىۋاتىدۇ'),
(5929, 85, 20, 'اﷲ كاپىرلارنىڭ ئەتراپىنى قورشاپ تۇرغۇچىدۇر'),
(5930, 85, 21, 'بەلكى (ئۇلار ئىنكار قىلغان نەرسە) لەۋھۇلمەھپۇزدا ساقلانغان ئۇلۇغ قۇرئاندۇر'),
(5931, 85, 22, 'بەلكى (ئۇلار ئىنكار قىلغان نەرسە) لەۋھۇلمەھپۇزدا ساقلانغان ئۇلۇغ قۇرئاندۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'ئاسمان بىلەن، تارىق (يەنى كېچىدە ئاشكارا بولغۇچى يۇلتۇز) بىلەن قەسەم قىلىمەن'),
(5933, 86, 2, 'تارىقنىڭ نېمىلىكىنى بىلەمسەن؟'),
(5934, 86, 3, 'ئۇ يورۇق يۇلتۇزدۇر'),
(5935, 86, 4, 'ھەر بىر ئادەم بىلەن (شۇ ئادەمنى) ساقلىغۇچى (پەرىشتە) بار'),
(5936, 86, 5, 'ئىنسان نېمىدىن يارىتىلغانلىقىغا قارىسۇن'),
(5937, 86, 6, 'ئۇ ئېتىلىپ چىققۇچى مەنىدىن يارىتىلدى'),
(5938, 86, 7, 'ئۇ مەنى ئۇمۇرتقا بىلەن قوۋۇرغا سۆڭىكىنىڭ ئارىسىدىن چىقىدۇ'),
(5939, 86, 8, 'شەك - شۈبھىسىزكى، اﷲ ئۇنى ئەسلىگە كەلتۈرۈشكە (يەنى تىرىلدۈرۈشكە قادىردۇر'),
(5940, 86, 9, 'سىرلار ئاشكارا قىلىنىدىغان كۈندە (يەنى قىيامەتتە)،'),
(5941, 86, 10, 'ئۇنىڭدىن (يەنى ئىنساندىن) (ئازابنى دەپئى قىلىدىغان ھېچ كۈچ ۋە ياردەمچى يوقتۇر'),
(5942, 86, 11, 'يامغۇرلۇق بۇلۇت بىلەن قەسەمكى،'),
(5943, 86, 12, 'يېرىلىپ (گىياھلارنى ئۈندۈرۈپ بېرىدىغان) زېمىن بىلەن قەسەمكى،'),
(5944, 86, 13, 'قۇرئان شەك - شۈبھىسىز (ھەق بىلەن باتىلنى ئايرىغۇچى) سۆزدۇر،'),
(5945, 86, 14, 'ئۇ چاقچاق ئەمەستۇر'),
(5946, 86, 15, 'ئۇلار (يەنى مۇشرىكلار) (قۇرئاننى يوققا چىقىرىش ئۈچۈن) ھەقىقەتەن ھىيلە - مىكىر ئىشلىتىدۇ'),
(5947, 86, 16, 'مەن ئۇلارنىڭ ھىيلە - مىكرىگە تاقابىل تۇرىمەن'),
(5948, 86, 17, '(ئى مۇھەممەد!) كاپىرلارنىڭ ھالاك بولۇشىغا ئالدىرىمىغىن، ئۇلارغا ئازراق مۆھلەت بەرگىن (ئۇزاققا قالماي ئۇلارغا قانداق قىلغانلىقىمنى كۆرىسەن)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'ھەممىدىن ئۈستۈن پەرۋەردىگارىڭنىڭ نامىنى پاك ئېتىقاد قىلغىن'),
(5950, 87, 2, 'ئۇ (پۈتۈن مەخلۇقاتنى) ياراتتى، (ئۇلارنى) بېجىرىم (يەنى چىرايلىق ۋە مۇناسىپ شەكىلدە) قىلدى'),
(5951, 87, 3, 'مەخلۇقاتقا پايدىلىق ئىشلارنى (تەقدىر قىلدى، ئۇلارغا پايدىلىنىش يوللىرىنى) كۆرسەتتى'),
(5952, 87, 4, 'ئوت - چۆپلەرنى ئۆستۈرۈپ بەردى'),
(5953, 87, 5, 'ئاندىن ئۇلارنى قارامتۇل قۇرۇق ئوت - خەسكە ئايلاندۇردى'),
(5954, 87, 6, '(ئى مۇھەممەد!) ساڭا (قۇرئاننى) ئوقۇتىمىز، اﷲ (نەسخى) قىلماقچى بولغان نەرسىدىن باشقىنى ئۇنتۇمايسەن. اﷲ (بەندىلەرنىڭ) ئاشكارا ۋە يوشۇرۇن (سۆز - ھەرىكەتلىرىنى) بىلىپ تۇرىدۇ'),
(5955, 87, 7, '(ئى مۇھەممەد!) ساڭا (قۇرئاننى) ئوقۇتىمىز، اﷲ (نەسخى) قىلماقچى بولغان نەرسىدىن باشقىنى ئۇنتۇمايسەن. اﷲ (بەندىلەرنىڭ) ئاشكارا ۋە يوشۇرۇن (سۆز - ھەرىكەتلىرىنى) بىلىپ تۇرىدۇ'),
(5956, 87, 8, 'سېنى بىز ئاسان يولغا (يەنى ئىسلام شەرىئىتىگە) مۇۋەپپەق قىلىمىز'),
(5957, 87, 9, 'ۋەز - نەسىھەت پايدىلىق بولىدىغان بولسا، (كىشىلەرگە قۇرئان بىلەن) ۋەز - نەسىھەت قىلغىن'),
(5958, 87, 10, 'اﷲ تىن (قورققان) ئادەم ۋەز - نەسىھەتنى قوبۇل قىلىدۇ'),
(5959, 87, 11, 'ئەڭ شەقى ئادەم ۋەز - نەسىھەتتىن قاچىدۇ'),
(5960, 87, 12, 'ئۇ كاتتا ئوتقا (يەنى دوزاخقا) كىرىدۇ'),
(5961, 87, 13, 'ئاندىن ئۇ ئوتتا ئۆلمەيدۇ ۋە (ئوبدان) ياشىمايدۇ'),
(5962, 87, 14, '(كۇفرىدىن ۋە گۇناھتىن) پاك بولغان ئادەم مەقسىتىگە يەتتى'),
(5963, 87, 15, 'ئۇ پەرۋەردىگارىنىڭ نامىنى ياد ئەتتى، ئاندىن ناماز ئوقۇدى'),
(5964, 87, 16, 'سىلەر دۇنيا تىرىكچىلىكىنى ئارتۇق كۆرىسىلەر'),
(5965, 87, 17, 'ئاخىرەت ياخشىدۇر ۋە باقىيدۇر'),
(5966, 87, 18, 'بۇ (يەنى بۇ سۈرىدىكى ۋەزلەر) شەك - شۈبھىسىز بۇرۇنقى كىتابلاردا - ئىبراھىم ۋە مۇسالارنىڭ كىتابلىرىدا باردۇر'),
(5967, 87, 19, 'بۇ (يەنى بۇ سۈرىدىكى ۋەزلەر) شەك - شۈبھىسىز بۇرۇنقى كىتابلاردا - ئىبراھىم ۋە مۇسالارنىڭ كىتابلىرىدا باردۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, '(ئى مۇھەممەد!) ساڭا ھەقىقەتەن قىيامەتنىڭ خەۋىرى كەلدى'),
(5969, 88, 2, 'بۇ كۈندە نۇرغۇن ئادەملەر خار بولغۇچىدۇر'),
(5970, 88, 3, 'ئۇلار دۇنيادا گۇناھ قىلغۇچىلاردۇر، (ئاخىرەتتە) جاپا تارتقۇچىلاردۇر'),
(5971, 88, 4, 'ئۇلار ھارارىتى قاتتىق دوزاخقا كىرىدۇ'),
(5972, 88, 5, '(سۇلىرى) قايناپ تۇرغان بۇلاقتىن سۇغىرىلىدۇ'),
(5973, 88, 6, 'ئۇلار ئۈچۈن زەرىدىن (يەنى بەتبۇي، ئاچچىق تىكەندىن) باشقا يېمەكلىك بولمايدۇ'),
(5974, 88, 7, 'زەرى كىشىنى سەمرىتمەيدۇ، ئاچلىقنى پەسەيتمەيدۇ'),
(5975, 88, 8, 'بۇ كۈندە نۇرغۇن كىشىلەر باركى، ئۇلار نېمەت بىلەن خۇشالدۇر'),
(5976, 88, 9, 'بۇ دۇنيادا قىلغان (سەئيى ئەمەللىرىدىن) مەمنۇندۇر'),
(5977, 88, 10, 'ئۇلار ئالىي جەننەتتىدۇر'),
(5978, 88, 11, 'ئۇ يەردە يامان سۆز ئاڭلىمايدۇ'),
(5979, 88, 12, 'ئۇ يەردە بۇلاقلار ئېقىپ تۇرىدۇ'),
(5980, 88, 13, 'ئۇ يەردە ئېگىز تەختلەر، قاتار تىزىلغان قەدەھلەر، رەت - رەت قويۇلغان ياستۇقلار، سېلىنغان ئېسىل بىساتلار بار'),
(5981, 88, 14, 'ئۇ يەردە ئېگىز تەختلەر، قاتار تىزىلغان قەدەھلەر، رەت - رەت قويۇلغان ياستۇقلار، سېلىنغان ئېسىل بىساتلار بار'),
(5982, 88, 15, 'ئۇ يەردە ئېگىز تەختلەر، قاتار تىزىلغان قەدەھلەر، رەت - رەت قويۇلغان ياستۇقلار، سېلىنغان ئېسىل بىساتلار بار'),
(5983, 88, 16, 'ئۇ يەردە ئېگىز تەختلەر، قاتار تىزىلغان قەدەھلەر، رەت - رەت قويۇلغان ياستۇقلار، سېلىنغان ئېسىل بىساتلار بار'),
(5984, 88, 17, 'ئۇلار قارىمامدۇكى، تۆگىلەر قانداق يارىتىلدى،'),
(5985, 88, 18, 'ئاسمان قانداق ئېگىز قىلىندى،'),
(5986, 88, 19, 'تاغلار قانداق تۇرغۇزۇلدى،'),
(5987, 88, 20, 'زېمىن قانداق يېيىتىلدى'),
(5988, 88, 21, 'سەن (ئۇلارغا) ۋەز - نەسىھەت قىلغىن، سەن پەقەت (ئۇلارغا) ۋەز - نەسىھەت قىلغۇچىسەن'),
(5989, 88, 22, 'سەن ئۇلارغا مۇسەللەت (يەنى ئۇلارنى ئىمانغا مەجبۇرلىغۇچى) ئەمەسسەن'),
(5990, 88, 23, 'لېكىن كىمكى (ۋەز - نەسىھەتتىن) يۈز ئۆرۈپ (اﷲ نى) ئىنكار قىلىدىكەن،'),
(5991, 88, 24, 'اﷲ ئۇنى قاتتىق ئازابلايدۇ'),
(5992, 88, 25, 'ئۇلار (ئۆلگەندىن كېيىن) ھەقىقەتەن بىزنىڭ دەرگاھىمىزغا قايتىدۇ'),
(5993, 88, 26, 'ئاندىن ئۇلاردىن ھېساب ئېلىشقا بىز مەسئۇلمىز');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'سۈبھى بىلەن، (زۇلھەججىنىڭ دەسلەپكى) ئون كېچىسى، بىلەن قۇربانلىق كۈنى بىلەن، قەسەمكى، (كاپىرلارغا ئازاب قىلىمىز)'),
(5995, 89, 2, 'سۈبھى بىلەن، (زۇلھەججىنىڭ دەسلەپكى) ئون كېچىسى، بىلەن قۇربانلىق كۈنى بىلەن، قەسەمكى، (كاپىرلارغا ئازاب قىلىمىز)'),
(5996, 89, 3, 'سۈبھى بىلەن، (زۇلھەججىنىڭ دەسلەپكى) ئون كېچىسى، بىلەن قۇربانلىق كۈنى بىلەن، قەسەمكى، (كاپىرلارغا ئازاب قىلىمىز)'),
(5997, 89, 4, 'سۈبھى بىلەن، (زۇلھەججىنىڭ دەسلەپكى) ئون كېچىسى، بىلەن قۇربانلىق كۈنى بىلەن، قەسەمكى، (كاپىرلارغا ئازاب قىلىمىز)'),
(5998, 89, 5, 'ئەقىل ئىگىسىگە بۇنىڭدا (يەنى يۇقىرىقى نەرسىلەردە قانائەتلىنەرلىك) قەسەم بارمۇ؟'),
(5999, 89, 6, 'پەرۋەردىگارىڭنىڭ ئاد - كۈچلۈك ئىرەمنى قانداق جازالىغانلىقىنى كۆرمىدىڭمۇ؟'),
(6000, 89, 7, 'پەرۋەردىگارىڭنىڭ ئاد - كۈچلۈك ئىرەمنى قانداق جازالىغانلىقىنى كۆرمىدىڭمۇ؟'),
(6001, 89, 8, 'بۇنداق ئادەملەر باشقا شەھەرلەردە يارىتىلمىغان ئىدى'),
(6002, 89, 9, 'شۇنىڭدەك ۋادىقورادا تاشلارنى كېسىپ (ئۆيلەرنى بىنا قىلغان) سەمۇدنى اﷲ قانداق جازالىدى'),
(6003, 89, 10, 'شۇنىڭدەك قۇزۇقلار ئىگىسى پىرئەۋننى قانداق جازالىدى'),
(6004, 89, 11, 'ئۇلار شەھەرلەردە (زۇلۇم قىلىشتا) ھەددىدىن ئاشتى'),
(6005, 89, 12, 'ئۇ يەرلەردە بۇزغۇنچىلىقنى كۆپ قىلدى'),
(6006, 89, 13, 'پەرۋەردىگارىڭ ئۇلارغا قاتتىق ئازابنى نازىل قىلدى'),
(6007, 89, 14, 'پەرۋەردىگارىڭ (بەندىلەرنى) ئەلۋەتتە كۆزىتىپ تۇرغۇچىدۇر'),
(6008, 89, 15, 'پەرۋەردىگارى ئىنساننى سىناپ ئەزىز قىلسا ۋە ئۇنىڭغا نېمەت بەرسە، ئۇ پەرۋەردىگارىم مېنى ئەزىز قىلدى» دەيدۇ'),
(6009, 89, 16, 'پەرۋەردىگارى ئىنساننى سىناپ رىزقنى تار قىلسا، ئۇ: «پەرۋەردىگارىم مېنى خار قىلدى» دەيدۇ'),
(6010, 89, 17, 'ھەرگىز ئۇنداق ئەمەس، سىلەر يېتىمنى ھۆرمەت قىلمايسىلەر'),
(6011, 89, 18, 'بىر - بىرىڭلارنى مىسكىنگە ئاش، نان بېرىشكە تەرغىب قىلمايسىلەر'),
(6012, 89, 19, 'مىراسنى (ھالال–ھارام دېمەستىن) ھە دەپ يەيسىلەر'),
(6013, 89, 20, 'مالنى (ھېرىسلىق بىلەن) ناھايىتى دوست تۇتىسىلەر'),
(6014, 89, 21, '(ئى غاپىللار!) بۇنىڭدىن يېنىڭلار، زېمىن ئارقىمۇئارقا تەۋرىتىلگەن چاغدا'),
(6015, 89, 22, 'پەرۋەردىگارىڭ كەلگەن ۋە پەرىشتىلەر سەپ - سەپ بولۇپ كەلگەن چاغدا'),
(6016, 89, 23, '(گۇناھكارلارنى كۆرۈش ئۈچۈن) جەھەننەم كەلتۈرۈلگەن چاغدا، شۇ كۈندە ئىنسان (يەنى كاپىر) (دۇنيادىكى قىلمىشلىرىنى) ئەسلەيدۇ، (بۇ چاغدا) ئەسلىگەننىڭ ئۇنىڭغا نېمە پايدىسى بولسۇن'),
(6017, 89, 24, 'ئۇ ئېيتىدۇكى: «كاشكى مەن (دۇنيادىكى) ھايات ۋاقتىمدا ياخشى ئىشلارنى قىلغان بولسامچۇ!»'),
(6018, 89, 25, 'شۇ كۈندە ھەر ئەھەدى (اﷲ قا ئاسىيلىق قىلغانلارنى) اﷲ جازالىغاندەك جازالىمايدۇ'),
(6019, 89, 26, 'ھەمدە ھېچ ئەھەدى اﷲ باغلىغاندەك باغلىمايدۇ'),
(6020, 89, 27, 'ئى ئارام تاپقۇچى جان!'),
(6021, 89, 28, 'سەن پەرۋەردىگارىڭدىن مەمنۇن، پەرۋەردىگارىڭ سەندىن رازى بولغان ھالدا، ئۇنىڭ دەرگاھىغا قايتقىن'),
(6022, 89, 29, 'سەن مېنىڭ (ياخشى) قاتارىغا قوشۇلغىن'),
(6023, 89, 30, 'مېنىڭ جەننىتىمگە كىرگىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'بۇ شەھەر (يەنى مەككە) بىلەن قەسەم قىلىمەن'),
(6025, 90, 2, '(ئى مۇھەممەد!) بۇ شەھەردە (ئۇرۇش قىلىش) ساڭا ھالالدۇر'),
(6026, 90, 3, 'ئادەم (ئەلەيھىسسالام) ۋە (ئۇنىڭ ياخشى) پەرزەنتلىرى بىلەن قەسەم قىلىمەن'),
(6027, 90, 4, 'بىز ئىنساننى ھەقىقەتەن جاپا - مۇشەققەت ئىچىدە قىلىپ ياراتتۇق'),
(6028, 90, 5, 'ئۇ (يەنى فاجىر، شەقى ئادەم) ئۆزىگە ھېچ كىشىنىڭ كۈچى يەتمەيدۇ دەپ ئويلامدۇ؟'),
(6029, 90, 6, 'ئۇ: «كۆپ مال سەرپ قىلدىم» دەيدۇ'),
(6030, 90, 7, 'ئۇ ئۆزىنىڭ (مال) سەرپ قىلغانلىقىنى ھېچ كىشى كۆرمىدى دەپ ئويلامدۇ؟'),
(6031, 90, 8, 'ئىنسان ئۈچۈن ئىككى كۆز، بىر تىل، ئىككى كالپۇك ياراتمىدۇقمۇ؟ ئۇنىڭغا ياخشى يول بىلەن يامان يولنى كۆرسەتمىدۇقمۇ؟'),
(6032, 90, 9, 'ئىنسان ئۈچۈن ئىككى كۆز، بىر تىل، ئىككى كالپۇك ياراتمىدۇقمۇ؟ ئۇنىڭغا ياخشى يول بىلەن يامان يولنى كۆرسەتمىدۇقمۇ؟'),
(6033, 90, 10, 'ئىنسان ئۈچۈن ئىككى كۆز، بىر تىل، ئىككى كالپۇك ياراتمىدۇقمۇ؟ ئۇنىڭغا ياخشى يول بىلەن يامان يولنى كۆرسەتمىدۇقمۇ؟'),
(6034, 90, 11, 'ئۇ داۋان ئاشمىدى'),
(6035, 90, 12, 'داۋان ئېشىشىنىڭ نېمىلىكىنى قانداق بىلەتتىڭ؟'),
(6036, 90, 13, '(داۋان ئېشىش دېگەن) قۇل ئازاد قىلماقتۇر،'),
(6037, 90, 14, 'ياكى ئاچارچىلىق كۈندە تۇغقانچىلىقى بولغان يېتىمگە ياكى توپىدا ياتقان مىسكىن (يەنى ھالى ناچار كەمبەغەل) گە تاماق بەرمەكتۇر،'),
(6038, 90, 15, 'ياكى ئاچارچىلىق كۈندە تۇغقانچىلىقى بولغان يېتىمگە ياكى توپىدا ياتقان مىسكىن (يەنى ھالى ناچار كەمبەغەل) گە تاماق بەرمەكتۇر،'),
(6039, 90, 16, 'ياكى ئاچارچىلىق كۈندە تۇغقانچىلىقى بولغان يېتىمگە ياكى توپىدا ياتقان مىسكىن (يەنى ھالى ناچار كەمبەغەل) گە تاماق بەرمەكتۇر'),
(6040, 90, 17, 'شۇنىڭ بىلەن بىللە (يەنى يۇقىرىقىلارنى قىلىش بىلەن بىللە) ئىمان ئېيتقانلاردىن، ئۆزئارا سەۋر قىلىشقا تەۋسىيە قىلىشقانلاردىن، ئۆزئارا مەرھەمەت قىلىشقا تەۋسىيە قىلىشقانلاردىن بولماقتۇر'),
(6041, 90, 18, 'ئەنە شۇلار (يەنى يۇقىرىدىكى ئىشلارنى قىلغان كىشىلەر) سائادەتمەن كىشىلەردۇر'),
(6042, 90, 19, 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار بەتبەختلەردۇر'),
(6043, 90, 20, 'ئۇلار دوزاخقا سولىنىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'قۇياش ۋە ئۇنىڭ نۇرى بىلەن قەسەمكى،'),
(6045, 91, 2, 'قۇياشقا ئەگەشكەن چاغدىكى ئاي بىلەن قەسەمكى،'),
(6046, 91, 3, 'قۇياشنى ئاشكارا قىلغان كۈندۈز بىلەن قەسەمكى،'),
(6047, 91, 4, 'قۇياشنى يوشۇرغان چاغدىكى كېچە بىلەن قەسەمكى،'),
(6048, 91, 5, 'ئاسمان بىلەن ۋە ئۇنى بىنا قىلغان زات بىلەن قەسەمكى،'),
(6049, 91, 6, 'زېمىن بىلەن ۋە ئۇنى بىنا قىلغان زات بىلەن قەسەمكى،'),
(6050, 91, 7, 'روھ بىلەن ۋە ئۇنى چىرايلىق قىلىپ ياراتقان، ئۇنىڭغا ياخشى - يامانلىقنى بىلدۈرگەن زات بىلەن قەسەمكى،'),
(6051, 91, 8, 'روھ بىلەن ۋە ئۇنى چىرايلىق قىلىپ ياراتقان، ئۇنىڭغا ياخشى - يامانلىقنى بىلدۈرگەن زات بىلەن قەسەمكى،'),
(6052, 91, 9, 'روھىنى پاك قىلغان ئادەم چوقۇم مۇرادىغا يېتىدۇ'),
(6053, 91, 10, 'نەپسىنى (كۇفرى ۋە پىسقى - فۇجۇر بىلەن) كەمسىتكەن ئادەم چوقۇم نائۈمىد بولىدۇ'),
(6054, 91, 11, 'سەمۇد ھەددىدىن ئاشقانلىقى ئۈچۈن (پەيغەمبەرنى) ئىنكار قىلدى'),
(6055, 91, 12, 'ئەينى زاماندا سەمۇدنىڭ ئەڭ بەتبەخت ئادىمى (مۆجىزە بولۇپ كەلگەن تۆگىنى) ئۆلتۈرۈشكە ئالدىرىدى'),
(6056, 91, 13, 'ئۇلارغا اﷲ نىڭ پەيغەمبىرى (يەنى سالىھ ئەلەيھىسسالام): «اﷲ نىڭ تۆگىسىگە چېقىلماڭلار، ئۇنى سۇ ئىچىشتىن توسماڭلار» دېدى'),
(6057, 91, 14, 'ئۇلار پەيغەمبەرنى (يەنى سالىھ ئەلەيھىسسالامنى) يالغانچى قىلدى، تۆگىنى ئۆلتۈردى، گۇناھى تۈپەيلىدىن اﷲ ئۇلارنى تەلتۆكۈس ھالاك قىلدى، ئومۇميۈزلۈك ھالاك قىلدى'),
(6058, 91, 15, 'اﷲ ئۇنىڭ (يەنى ئۇلارنى ھالاك قىلغانلىقنىڭ) ئاقىۋىتىدىن قورقمايدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'قاراڭغۇلۇقى ئالەمنى قاپلىغان كېچە بىلەن قەسەمكى،'),
(6060, 92, 2, 'يورۇغان چاغدىكى كۈندۈز بىلەن قەسەمكى،'),
(6061, 92, 3, 'ئەر - ئايالنى ياراتقان زات اﷲ بىلەن قەسەمكى،'),
(6062, 92, 4, 'سىلەرنىڭ ئەمەلىڭلار شەك - شۈبھىسىز خىلمۇخىلدۇر'),
(6063, 92, 5, '(پۇل - مېلىنى اﷲ نىڭ رازىلىقى ئۈچۈن) سەرپ قىلغان، تەقۋادارلىق قىلغان، (كەلىمە تەۋھىدنى) تەستىق قىلغان ئادەمگە كەلسەك'),
(6064, 92, 6, '(پۇل - مېلىنى اﷲ نىڭ رازىلىقى ئۈچۈن) سەرپ قىلغان، تەقۋادارلىق قىلغان، (كەلىمە تەۋھىدنى) تەستىق قىلغان ئادەمگە كەلسەك،'),
(6065, 92, 7, 'ئۇنىڭغا بىز ياخشى يولنى مۇيەسسەر قىلىپ بېرىمىز'),
(6066, 92, 8, 'بېخىللىق قىلىپ (اﷲ نىڭ ساۋابىدىن) ئۆزىنى بىھاجەت ھېسابلىغان، كەلىمە تەۋھىدنى ئىنكار قىلغان ئادەمگە كەلسەك'),
(6067, 92, 9, 'بېخىللىق قىلىپ (اﷲ نىڭ ساۋابىدىن) ئۆزىنى بىھاجەت ھېسابلىغان، كەلىمە تەۋھىدنى ئىنكار قىلغان ئادەمگە كەلسەك،'),
(6068, 92, 10, 'بىز ئۇنىڭغا يامان يولنى مۇيەسسەر قىلىپ بېرىمىز'),
(6069, 92, 11, 'ئۇ (دوزاخقا) تاشلانغان چاغدا، ئۇنىڭ مېلى ھېچ نەرسىگە ئەسقاتمايدۇ'),
(6070, 92, 12, 'توغرا يولنى كۆرسىتىشكە شەك - شۈبھىسىز بىز مەسئۇلمىز'),
(6071, 92, 13, 'دۇنيا ۋە ئاخىرەت شەك - شۈبھىسىز بىزنىڭ ئىلكىمىزدىدۇر'),
(6072, 92, 14, 'سىلەرنى لاۋۇلداپ تۇرغان ئوتتىن ئاگاھلاندۇردۇم'),
(6073, 92, 15, 'ئۇنىڭغا پەقەت (ھەقنى) ئىنكار قىلغان، (ئىماندىن) يۈز ئۆرۈگەن ئادەملا كىرىدۇ'),
(6074, 92, 16, 'ئۇنىڭغا پەقەت (ھەقنى) ئىنكار قىلغان، (ئىماندىن) يۈز ئۆرۈگەن ئادەملا كىرىدۇ'),
(6075, 92, 17, 'پاكلىنىش ئۈچۈن مال - مۈلكىنى (ياخشىلىق يوللىرىغا) سەرپ قىلىدىغان ئادەم ئۇنىڭدىن (يەنى ئوتتىن) يىراق قىلىنىدۇ'),
(6076, 92, 18, 'پاكلىنىش ئۈچۈن مال - مۈلكىنى (ياخشىلىق يوللىرىغا) سەرپ قىلىدىغان ئادەم ئۇنىڭدىن (يەنى ئوتتىن) يىراق قىلىنىدۇ'),
(6077, 92, 19, 'ئۇنىڭ قىلغان ياخشىلىقى بىراۋنىڭ ياخشىلىقىنى ياندۇرۇش ئۈچۈن ئەمەس، پەقەت ئۇلۇغ پەرۋەردىگارىنىڭ رازىلىقى ئۈچۈندۇر'),
(6078, 92, 20, 'ئۇنىڭ قىلغان ياخشىلىقى بىراۋنىڭ ياخشىلىقىنى ياندۇرۇش ئۈچۈن ئەمەس، پەقەت ئۇلۇغ پەرۋەردىگارىنىڭ رازىلىقى ئۈچۈندۇر'),
(6079, 92, 21, 'ئۇ (يەنى ئېھسان قىلغۇچى ئادەم) (پەرۋەردىگارىنىڭ بەرگەن مۇكاپاتىدىن) چوقۇم رازى بولىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'چاشگاھ ۋاقتى بىلەن قەسەمكى،'),
(6081, 93, 2, 'قاراڭغۇلۇقى (ئەتراپنى قاپلىغان) چاغدىكى كېچە بىلەن قەسەمكى،'),
(6082, 93, 3, '(ئى مۇھەممەد!) پەرۋەردىگارىڭ سېنى تاشلىۋەتكىنىمۇ يوق، يامان كۆرۈپ قالغىنىمۇ يوق'),
(6083, 93, 4, 'ساڭا ئاخىرەت ئەلۋەتتە دۇنيادىن ياخشىدۇر،'),
(6084, 93, 5, 'پەرۋەردىگارىڭ ساڭا (ئاخىرەتتە ساۋاب، ھۆرمەت ۋە شاپائەت قاتارلىقلارنى) ئاتا قىلىدۇ، سەن مەمنۇن بولىسەن'),
(6085, 93, 6, 'ئۇ سېنى يېتىم بىلىپ پاناھ بەرمىدىمۇ؟'),
(6086, 93, 7, 'قايمۇققان بىلىپ ھىدايەت قىلمىدىمۇ؟'),
(6087, 93, 8, 'سېنى يوقسىز بىلىپ (كىشىلەردىن) بىھاجەت قىلمىدىمۇ؟'),
(6088, 93, 9, 'يېتىمنى خارلىما'),
(6089, 93, 10, 'سائىلغا قوپاللىق قىلما'),
(6090, 93, 11, 'پەرۋەردىگارىڭنىڭ (ساڭا بەرگەن) نېمىتىنى (كىشىلەرگە) سۆزلەپ بەرگىن (چۈنكى نېمەتنى سۆزلەپ بەرگەنلىك نېمەتكە شۈكۈر قىلغانلىق بولىدۇ)');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, '(ئى مۇھەممەد!) سېنىڭ كۆكسىڭنى (ھىدايەت، ھېكمەت ۋە قۇرئاننىڭ نۇرى بىلەن) كەڭ قىلمىدۇقمۇ؟'),
(6092, 94, 2, 'سېنىڭ بېلىڭنى ئېگىۋەتكەن ئېغىر يۈكنى ئۈستۈڭدىن ئېلىپ تاشلىدۇق'),
(6093, 94, 3, 'سېنىڭ بېلىڭنى ئېگىۋەتكەن ئېغىر يۈكنى ئۈستۈڭدىن ئېلىپ تاشلىدۇق'),
(6094, 94, 4, 'سېنىڭ شۆھرىتىڭنى ئۈستۈن قىلدۇق'),
(6095, 94, 5, 'ئەلۋەتتە ھەر بىر مۈشكۈللۈك بىلەن بىر ئاسانلىق بار'),
(6096, 94, 6, 'ئەلۋەتتە ھەر بىر مۈشكۈللۈك بىلەن بىر ئاسانلىق بار'),
(6097, 94, 7, '(ئى مۇھەممەد! دەۋەتتىن) بوشىغان چېغىڭدا (اﷲ قا) ئىبادەت قىلىشقا تىرىشقىن'),
(6098, 94, 8, 'پەرۋەردىگارىڭغا يۈزلەنگىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'ئەنجۈر بىلەن ۋە زەيتۇن بىلەن قەسەمكى،'),
(6100, 95, 2, 'سىنا تېغى بىلەن قەسەمكى،'),
(6101, 95, 3, 'بۇ تىنچ شەھەر (يەنى مەككە مۇكەررەمە) بىلەن قەسەمكى،'),
(6102, 95, 4, 'بىز ئىنساننى شەك - شۈبھىسىز ئەڭ چىرايلىق شەكىلدە ياراتتۇق'),
(6103, 95, 5, 'ئاندىن ئۇنى (چىرايلىق ياراتقانلىق نېمىتىمىزگە شۈكۈر قىلماي ئاسىيلىق قىلغانلىقتىن) دوزاخقا قايتۇردۇق'),
(6104, 95, 6, 'پەقەت ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغانلار بۇنىڭدىن مۇستەسنا. بۇلارغا ئۈزۈلمەس ساۋاب بېرىلىدۇ'),
(6105, 95, 7, '(ئى ئىنسان!) بۇنىڭدىن كېيىن (يەنى روشەن دەلىللەر بايان قىلىنغاندىن كېيىن) يەنە نېمىشقا قىيامەتنى ئىنكار قىلىسەن؟'),
(6106, 95, 8, 'اﷲ ئەڭ ئادىل ھۆكۈم قىلغۇچى ئەمەسمۇ؟');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'ياراتقان پەرۋەردىگارىڭنىڭ ئىسمى بىلەن ئوقۇغىن'),
(6108, 96, 2, 'ئۇ ئىنساننى لەختە قاندىن ياراتتى'),
(6109, 96, 3, 'ئوقۇغىن، پەرۋەردىگارىڭ ئەڭ كەرەملىكتۇر'),
(6110, 96, 4, 'ئۇ قەلەم بىلەن (خەت يېزىشنى) ئۆگەتتى'),
(6111, 96, 5, 'ئىنسانغا بىلمىگەن نەرسىلەرنى بىلدۈردى'),
(6112, 96, 6, 'شەك - شۈبھىسىز ئىنسان ئۆزىنى باي ساناپ (اﷲ قا بويسۇنۇشتىن باش تارتىپ)، راستلا ھەددىدىن ئاشىدۇ'),
(6113, 96, 7, 'شەك - شۈبھىسىز ئىنسان ئۆزىنى باي ساناپ (اﷲ قا بويسۇنۇشتىن باش تارتىپ)، راستلا ھەددىدىن ئاشىدۇ'),
(6114, 96, 8, '(ئى ئىنسان!) بارىدىغان يېرىڭ پەرۋەردىگارىڭنىڭ دەرگاھىدۇر (قىلمىشىڭغا قاراپ جازاغا ياكى مۇكاپاتقا ئېرىشىسەن)'),
(6115, 96, 9, 'ناماز ئوقۇۋاتقان بەندىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) توسقان ئادەمنىڭ (ھالىنى) ماڭا ئېيتىپ بەرسەڭچۇ؟'),
(6116, 96, 10, 'ناماز ئوقۇۋاتقان بەندىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) توسقان ئادەمنىڭ (ھالىنى) ماڭا ئېيتىپ بەرسەڭچۇ؟'),
(6117, 96, 11, 'ماڭا (ئۇنىڭ ھالىنى) ئېيتىپ بەرسەڭچۇ؟ ئەگەر ئۇ (ھەددىدىن ئاشقۇچى) (نامازنى توسۇشنىڭ ئورنىغا) توغرا يولدا بولغان بولسا،'),
(6118, 96, 12, 'ياكى تەقۋادارلىققا ئەمر قىلغان بولسا (بۇ ئۇنىڭ ئۆزى ئۈچۈن ياخشى بولماسمىدى؟)'),
(6119, 96, 13, 'ماڭا ئېيتىپ بەرسەڭچۇ؟ ئەگەر ئۇ (قۇرئاننى) ئىنكار قىلسا، ئىماندىن (يۈز ئۆرۈسە)،'),
(6120, 96, 14, 'اﷲ نىڭ (ئۇنى) كۆرۈپ تۇرىدىغانلىقىنى بىلمىدىمۇ؟'),
(6121, 96, 15, 'ئۇ گۇمراھلىقتىن يانسۇن، ئەگەر ئۇ بۇنىڭدىن يانمىسا، چوقۇم ئۇنىڭ كوكۇلىسىدىن - يالغانچى گۇناھكارنىڭ كوكۇلىسىدىن تۇتۇپ (جەھەننەمگە) سۆرەيمىز'),
(6122, 96, 16, 'ئۇ گۇمراھلىقتىن يانسۇن، ئەگەر ئۇ بۇنىڭدىن يانمىسا، چوقۇم ئۇنىڭ كوكۇلىسىدىن - يالغانچى گۇناھكارنىڭ كوكۇلىسىدىن تۇتۇپ (جەھەننەمگە) سۆرەيمىز'),
(6123, 96, 17, 'ئۇ ئۆزىنىڭ سورۇنداشلىرىنى ياردەمگە چاقىرسۇن!'),
(6124, 96, 18, 'بىز زابانىيىلەر (يەنى جەھەننەمنىڭ كۈچلۈك - قاتتىق قول پەرىشتىلىرى) نى چاقىرىمىز'),
(6125, 96, 19, '(بۇ فاجىر گۇمراھلىقتىن) يانسۇن! ئۇنىڭغا ئىتائەت قىلمىغىن، (اﷲ قا) سەجدە قىلغىن (شۇنىڭ بىلەن اﷲ قا) يېقىنلىق ھاسىل قىلغىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'بىز قۇرئاننى ھەقىقەتەن شەبى قەدرىدە نازىل قىلدۇق'),
(6127, 97, 2, 'شەبى قەدرىنىڭ نېمە ئىكەنلىكىنى بىلەمسەن؟'),
(6128, 97, 3, 'شەبى قەدرى (شەرەپ ۋە پەزىلەتتە) مىڭ ئايدىن ئارتۇقتۇر'),
(6129, 97, 4, 'پەرىشتىلەر ۋە جىبرىئىل شۇ كېچىدە پەرۋەردىگارىنىڭ ئەمرى بىلەن (زېمىنغا اﷲ تەقدىر قىلغان) بارلىق ئىش ئۈچۈن چۈشىدۇ'),
(6130, 97, 5, 'شۇ كېچە تاڭ يورۇغانغا قەدەر پۈتۈنلەي تىنچ - ئامانلىقتىن ئىبارەتتۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'ئەھلى كىتابتىن (يەنى يەھۇدىيلار ۋە ناسارالاردىن) كاپىر بولغانلار ۋە مۇشرىكلار ئۆزلىرىگە روشەن دەلىل كەلگەنگە قەدەر كۇفرىدىن ئايرىلغۇچى بولمىدى'),
(6132, 98, 2, '(ئۇ روشەن دەلىل) پاك سەھىپىلەرنى تىلاۋەت قىلىپ تۇرىدىغان، اﷲ تەرىپىدىن كەلگەن پەيغەمبەردۇر'),
(6133, 98, 3, 'ئۇ سەھىپىلەر توغرا ئەھكاملارنى ئۆز ئىچىگە ئالغان'),
(6134, 98, 4, 'كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) پەقەت ئۆزلىرىگە روشەن دەلىل كەلگەندىن كېيىنلا ئىختىلاپ قىلىشتى'),
(6135, 98, 5, 'ئۇلار پەقەت ئىبادەتنى اﷲ قا خالىس قىلغان، ھەق دىنغا ئېتىقاد قىلغان ھالدا (يالغۇز) اﷲ غىلا ئىبادەت قىلىشقا بۇيرۇلدى. نامازنى ئادا قىلىشقا، زاكاتنى بېرىشكە (بۇيرۇلدى)، ئەنە شۇلار (يەنى ئىبادەت، ئىخلاس ناماز، زاكاتلار) توغرا دىندۇر'),
(6136, 98, 6, 'ئەھلى كىتابتىن ۋە مۇشرىكلاردىن كاپىر بولغانلار ھەقىقەتەن دوزاخقا كىرىدۇ. دوزاختا مەڭگۈ قالىدۇ. ئەنە شۇلار مەخلۇقاتلارنىڭ يامىنىدۇر'),
(6137, 98, 7, 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار - ئەنە شۇلار مەخلۇقاتلارنىڭ ياخشىسىدۇر'),
(6138, 98, 8, 'ئۇلارغا پەرۋەردىگارىنىڭ دەرگاھىدا بېرىلىدىغان مۇكاپات (ئۇلارنىڭ) تۇرار جايى بولغان، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردۇركى، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. اﷲ ئۇلاردىن رازى بولىدۇ، ئۇلارمۇ اﷲ تىن مەمنۇن بولىدۇ. بۇنىڭغا پەرۋەردىگارىدىن قورققان ئادەم ئېرىشىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'زېمىن قاتتىق تەۋرىتىلگەندە،'),
(6140, 99, 2, 'زېمىن (قوينىدىكى) يۈكلىرىنى (يەنى مەدەنلەر، ئوتلار، ئۆلۈكلەرنى) چىقارغاندا،'),
(6141, 99, 3, 'ئىنسان (يەنى كاپىر) ئۇنىڭغا (يەنى زېمىنغا): «نېمە بولدى؟» دېگەندە،'),
(6142, 99, 4, 'بۇ كۈندە زېمىن ئۆزىنىڭ خەۋەرلىرىنى (يەنى ئۇنىڭ ئۈستىدە ئىشلەنگەن ياخشى - يامان ئىشلارنى) مەلۇم قىلىدۇ'),
(6143, 99, 5, '(ئۇنىڭ مەلۇم قىلىشى) پەرۋەردىگارىنىڭ ئۇنى شۇنداق قىلىشقا ئەمر قىلغانلىقىدىندۇر'),
(6144, 99, 6, 'بۇ كۈندە كىشىلەر قىلغان ئەمەللىرىنىڭ نەتىجىسىنى كۆرۈش (يەنى نەتىجىسىگە ئېرىشىش) ئۈچۈن توپ - توپ بولۇپ تارىلىپ كېتىدۇ'),
(6145, 99, 7, 'كىمكى زەررىچىلىك ياخشى ئىش قىلىدىكەن، ئۇنىڭ مۇكاپاتىنى كۆرىدۇ'),
(6146, 99, 8, 'كىمكى زەررىچىلىك يامان ئىش قىلىدىكەن، ئۇنىڭ جازاسىنى تارتىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, '(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا - چاڭ چىقارغۇچى، شۇ توپا - چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى'),
(6148, 100, 2, '(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا - چاڭ چىقارغۇچى، شۇ توپا - چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى'),
(6149, 100, 3, '(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا - چاڭ چىقارغۇچى، شۇ توپا - چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى'),
(6150, 100, 4, '(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا - چاڭ چىقارغۇچى، شۇ توپا - چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى'),
(6151, 100, 5, '(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا - چاڭ چىقارغۇچى، شۇ توپا - چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى،'),
(6152, 100, 6, 'ئىنسان ھەقىقەتەن پەرۋەردىگارىنىڭ (نېمەتلىرىنى) شەكسىز ئىنكار قىلغۇچىدۇر'),
(6153, 100, 7, 'ئىنسان ھەقىقەتەن ئۇنىڭغا (يەنى اﷲ نىڭ نېمىتىنى ئىنكار قىلغانلىقىغا) ئەلۋەتتە ئۆزى گۇۋاھتۇر'),
(6154, 100, 8, 'ئىنسان پۇل - مالغا ئىنتايىن ھېرىستۇر'),
(6155, 100, 9, 'قەبرىلەردىكى ئۆلۈكلەر (سىرتقا) چىقىرىلغان، دىللاردىكى سىرلار ئاشكارا قىلىنغان چاغدا، ئۇلارنىڭ ھەممە ئەھۋالىدىن پەرۋەردىگارىنىڭ ھەقىقەتەن خەۋەردار ئىكەنلىكىنى (بۇ نادان ئىنسان) بىلمەمدۇ؟'),
(6156, 100, 10, 'قەبرىلەردىكى ئۆلۈكلەر (سىرتقا) چىقىرىلغان، دىللاردىكى سىرلار ئاشكارا قىلىنغان چاغدا، ئۇلارنىڭ ھەممە ئەھۋالىدىن پەرۋەردىگارىنىڭ ھەقىقەتەن خەۋەردار ئىكەنلىكىنى (بۇ نادان ئىنسان) بىلمەمدۇ؟'),
(6157, 100, 11, 'قەبرىلەردىكى ئۆلۈكلەر (سىرتقا) چىقىرىلغان، دىللاردىكى سىرلار ئاشكارا قىلىنغان چاغدا، ئۇلارنىڭ ھەممە ئەھۋالىدىن پەرۋەردىگارىنىڭ ھەقىقەتەن خەۋەردار ئىكەنلىكىنى (بۇ نادان ئىنسان) بىلمەمدۇ؟');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'قىيامەت،'),
(6159, 101, 2, 'قىيامەت نېمىدۇر'),
(6160, 101, 3, 'قىيامەتنىڭ نېمە ئىكەنلىكىنى قانداق بىلەلەيسەن؟'),
(6161, 101, 4, 'شۇ كۈندە ئىنسانلار تارىلىپ كەتكەن پەرۋانىدەك بولۇپ كېتىدۇ'),
(6162, 101, 5, 'تاغلار ئېتىلغان يۇڭدەك بولۇپ كېتىدۇ'),
(6163, 101, 6, 'تارازىسى ئېغىر كەلگەن (يەنى ياخشىلىقلىرى يامانلىقلىرىنى بېسىپ چۈشكەن) ئادەمگە كەلسەك، ئۇ كۆڭۈللۈك تۇرمۇشتا (يەنى نازۇنېمەتلىك جەننەتتە) بولىدۇ'),
(6164, 101, 7, 'تارازىسى ئېغىر كەلگەن (يەنى ياخشىلىقلىرى يامانلىقلىرىنى بېسىپ چۈشكەن) ئادەمگە كەلسەك، ئۇ كۆڭۈللۈك تۇرمۇشتا (يەنى نازۇنېمەتلىك جەننەتتە) بولىدۇ'),
(6165, 101, 8, 'تارازىسى يېنىك كەلگەن (يەنى يامانلىقلىرى ياخشىلىقلىرىنى بېسىپ كەتكەن، يا ياخشىلىقى بولمىغان) ئادەمگە كەلسەك، ئۇنىڭ جايى ھاۋىيە بولىدۇ'),
(6166, 101, 9, 'تارازىسى يېنىك كەلگەن (يەنى يامانلىقلىرى ياخشىلىقلىرىنى بېسىپ كەتكەن، يا ياخشىلىقى بولمىغان) ئادەمگە كەلسەك، ئۇنىڭ جايى ھاۋىيە بولىدۇ'),
(6167, 101, 10, 'ھاۋىيەنىڭ نېمە ئىكەنلىكىنى سەن قانداق بىلەلەيسەن؟'),
(6168, 101, 11, 'ھاۋىيە قىزىق ئوتتۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'تاكى قەبرىلەرنى زىيارەت قىلغىنىڭلارغا (يەنى قەبرىلەرگە كۆمۈلگىنىڭلارغا) قەدەر، (پۇل - مال، بالىلار بىلەن) پەخىرلەنمەك سىلەرنى غەپلەتتە قالدۇردى'),
(6170, 102, 2, 'تاكى قەبرىلەرنى زىيارەت قىلغىنىڭلارغا (يەنى قەبرىلەرگە كۆمۈلگىنىڭلارغا) قەدەر، (پۇل - مال، بالىلار بىلەن) پەخىرلەنمەك سىلەرنى غەپلەتتە قالدۇردى'),
(6171, 102, 3, 'راستلا كەلگۈسىدە بىلىسىلەر'),
(6172, 102, 4, 'ئاندىن راستلا كەلگۈسىدە بىلىسىلەر'),
(6173, 102, 5, 'ئەگەر سىلەر راستلا ھەقىقىي بىلسەڭلار (غەپلەتتە قالماڭلار)'),
(6174, 102, 6, 'سىلەر چوقۇم جەھەننەمنى كۆرىسىلەر'),
(6175, 102, 7, 'ئاندىن ئۇنى چوقۇم ئۆز كۆزۈڭلار بىلەن كۆرىسىلەر'),
(6176, 102, 8, 'ئاندىن (دۇنيادا سىلەرگە بېرىلگەن) نېمەتلەردىن سوئال - سوراق قىلىنىسىلەر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'زامان بىلەن قەسەمكى،'),
(6178, 103, 2, 'ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، بىر - بىرىگە ھەقنى تەۋسىيە قىلىشقان، بىر - بىرىگە سەۋرىنى تەۋسىيە قىلىشقان كىشىلەردىن باشقا ھەممە ئادەم چوقۇم زىيان ئىچىدىدۇر،'),
(6179, 103, 3, 'ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، بىر - بىرىگە ھەقنى تەۋسىيە قىلىشقان، بىر - بىرىگە سەۋرىنى تەۋسىيە قىلىشقان كىشىلەردىن باشقا ھەممە ئادەم چوقۇم زىيان ئىچىدىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, '(كىشىلەرنى) ئەيىبلىگۈچى سۇخەنچىگە ۋاي!'),
(6181, 104, 2, 'ئۇ مال توپلىدى ۋە ئۇنى (ياخشىلىق يوللىرىغا سەرپ قىلماي ساقلاش ئۈچۈن) سانىدى'),
(6182, 104, 3, 'ئۇ مېلى ئۆزىنى (دۇنيادا مەڭگۈ قالدۇرىدۇ دەپ ئويلايدۇ)'),
(6183, 104, 4, 'ھەرگىز ئۇنداق ئەمەس، ئۇ چوقۇم ھۆتەمەگە تاشلىنىدۇ'),
(6184, 104, 5, 'ھۆتەمەنىڭ نېمىلىكىنى قانداق بىلەلەيسەن؟'),
(6185, 104, 6, 'ئۇ، اﷲ نىڭ ياندۇرغان ئوتىدۇركى، (ئۇنىڭ ئەلىمى) يۈرەكلەرگە يېتىپ بارىدۇ'),
(6186, 104, 7, 'ئۇ، اﷲ نىڭ ياندۇرغان ئوتىدۇركى، (ئۇنىڭ ئەلىمى) يۈرەكلەرگە يېتىپ بارىدۇ'),
(6187, 104, 8, 'ئۇلار چوقۇم دوزاخقا سولىنىدۇ'),
(6188, 104, 9, 'ئېگىز تۈۋرۈكلەرگە باغلىنىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'پەرۋەردىگارىڭنىڭ فىل ئىگىلىرىنى قانداق قىلغانلىقىنى كۆرمىدىڭمۇ؟'),
(6190, 105, 2, 'ئۇ ئۇلارنىڭ ھىيلە - مىكرىنى بەربات قىلمىدىمۇ؟'),
(6191, 105, 3, 'ئۇ ئۇلارنىڭ ئۈستىگە توپ - توپ قۇشلارنى ئەۋەتتى'),
(6192, 105, 4, 'قۇشلار ئۇلارغا ساپال تاشلارنى ئېتىپ،'),
(6193, 105, 5, 'ئۇلارنى چاينىۋېتىلگەن ساماندەك قىلىۋەتتى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'قۇرەيش قوغدالغانلىقلىرى ئۈچۈن،'),
(6195, 106, 2, 'ئۇلار قىشلىق ۋە يازلىق سەپىرىدە قوغدالغانلىقلىرى ئۈچۈن،'),
(6196, 106, 3, 'بۇ ئۆي (يەنى بەيتۇللاھ) نىڭ پەرۋەردىگارىغا ئىبادەت قىلسۇنكى، ئۇ ئۇلارنى ئاچلىقتا ئوزۇقلاندۇردى، ئۇلارنى قورقۇنچتىن ئەمىن قىلدى،'),
(6197, 106, 4, 'بۇ ئۆي (يەنى بەيتۇللاھ) نىڭ پەرۋەردىگارىغا ئىبادەت قىلسۇنكى، ئۇ ئۇلارنى ئاچلىقتا ئوزۇقلاندۇردى، ئۇلارنى قورقۇنچتىن ئەمىن قىلدى');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'دىننى ئىنكار قىلغان ئادەمنى كۆردۈڭمۇ؟'),
(6199, 107, 2, 'ئۇ يېتىمنى دۆشكەلەيدىغان، مىسكىنگە تاماق بېرىشنى تەرغىب قىلمايدىغان ئادەمدۇر'),
(6200, 107, 3, 'ئۇ يېتىمنى دۆشكەلەيدىغان، مىسكىنگە تاماق بېرىشنى تەرغىب قىلمايدىغان ئادەمدۇر،'),
(6201, 107, 4, 'شۇنداق ناماز ئوقۇغۇچىلارغا ۋايكى، ئۇلار نامازنى غەپلەت بىلەن ئوقۇيدۇ،'),
(6202, 107, 5, 'شۇنداق ناماز ئوقۇغۇچىلارغا ۋايكى، ئۇلار نامازنى غەپلەت بىلەن ئوقۇيدۇ'),
(6203, 107, 6, 'نامازنى رىيا بىلەن ئوقۇيدۇ'),
(6204, 107, 7, 'ئۇلار قولقا بەرمەيدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'بىز ساڭا ھەقىقەتەن نۇرغۇن ياخشىلىقلارنى ئاتا قىلدۇق'),
(6206, 108, 2, 'پەرۋەردىگارىڭ ئۈچۈن ناماز ئوقۇغىن ۋە قۇربانلىق قىلغىن'),
(6207, 108, 3, 'ھەقىقەتەن سېنىڭ دۈشمىنىڭنىڭ نام - نىشانى قالمايدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, '(ئى مۇھەممەد! سېنى بۇتلارغا چوقۇنۇشقا ئۈندىگۈچى كۇففارلارغا) ئېيتقىنكى، «ئى كاپىرلار!'),
(6209, 109, 2, 'سىلەر چوقۇنۇۋاتقان بۇتلارغا مەن چوقۇنمايمەن'),
(6210, 109, 3, 'سىلەرمۇ مەن ئىبادەت قىلىۋاتقان اﷲ قا ئىبادەت قىلمايسىلەر'),
(6211, 109, 4, 'سىلەر چوقۇنغان بۇتلارغا مېنىڭ چوقۇنغىنىم يوق'),
(6212, 109, 5, 'سىلەرنىڭمۇ مەن ئىبادەت قىلغان اﷲ قا ئىبادەت قىلغىنىڭلار يوق'),
(6213, 109, 6, 'سىلەرنىڭ دىنىڭلار ئۆزۈڭلار ئۈچۈن، مېنىڭ دىنىممۇ ئۆزۈم ئۈچۈن»');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'اﷲ نىڭ ياردىمى ۋە غەلىبىسى كەلگەن ۋە اﷲ نىڭ دىنىغا كىشىلەرنىڭ توپ - توپ بولۇپ كىرگەنلىكىنى كۆرگىنىڭدە'),
(6215, 110, 2, 'اﷲ نىڭ ياردىمى ۋە غەلىبىسى كەلگەن ۋە اﷲ نىڭ دىنىغا كىشىلەرنىڭ توپ - توپ بولۇپ كىرگەنلىكىنى كۆرگىنىڭدە،'),
(6216, 110, 3, 'رەببىڭغا تەسبىھ ئېيتقىن، ھەمدى ئېيتقىن ۋە ئۇنىڭدىن مەغپىرەت تىلىگىن. اﷲ ھەقىقەتەن تەۋبىنى بەك قوبۇل قىلغۇچىدۇر');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'ئەبۇ لەھەبنىڭ ئىككى قولى قۇرۇپ كەتسۇن! (ئەمەلدە) قۇرۇپ كەتتى'),
(6218, 111, 2, 'ئۇنىڭغا مال - مۈلكى ۋە ئېرىشكەن نەرسىلىرى ئەسقاتمىدى'),
(6219, 111, 3, 'ئۇ لاۋۇلداپ تۇرغان ئوتقا (يەنى دوزاخقا) كىرىدۇ'),
(6220, 111, 4, 'ئۇنىڭ ئوتۇن توشۇغۇچى (يەنى سۇخەنچى) خوتۇنىمۇ لاۋۇلداپ تۇرغان ئوتقا كىرىدۇ'),
(6221, 111, 5, 'ئۇنىڭ بوينىدا مەھكەم ئېشىلگەن ئارغامچا بولىدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, '(ئى مۇھەممەد!) ئېيتقىنكى، ئۇ - اﷲ بىردۇر'),
(6223, 112, 2, 'ھەممە اﷲ قا موھتاجدۇر'),
(6224, 112, 3, 'اﷲ بالا تاپقانمۇ ئەمەس، تۇغۇلغانمۇ ئەمەس'),
(6225, 112, 4, 'ھېچ كىشى ئۇنىڭغا تەڭداش بولالمايدۇ');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, '«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (اﷲ) غا سېغىنىپ پاناھ تىلەيمەن» دېگىن'),
(6227, 113, 2, '«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (اﷲ) غا سېغىنىپ پاناھ تىلەيمەن» دېگىن'),
(6228, 113, 3, '«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (اﷲ) غا سېغىنىپ پاناھ تىلەيمەن» دېگىن'),
(6229, 113, 4, '«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (اﷲ) غا سېغىنىپ پاناھ تىلەيمەن» دېگىن'),
(6230, 113, 5, '«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (اﷲ) غا سېغىنىپ پاناھ تىلەيمەن» دېگىن');
INSERT INTO `ug_saleh` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, '«ئىنسانلارنىڭ پەرۋەردىگارى، ئىنسانلارنىڭ پادىشاھى، ئىنسانلارنىڭ ئىلاھى (اﷲ) غا سېغىنىپ، كىشىلەرنىڭ دىللىرىدا ۋەسۋەسە قىلغۇچى جىنلاردىن ۋە ئىنسانلاردىن بولغان يوشۇرۇن شەيتاننىڭ ۋەسۋەسىسىنىڭ شەررىدىن پاناھ تىلەيمەن» دېگىن'),
(6232, 114, 2, '«ئىنسانلارنىڭ پەرۋەردىگارى، ئىنسانلارنىڭ پادىشاھى، ئىنسانلارنىڭ ئىلاھى (اﷲ) غا سېغىنىپ، كىشىلەرنىڭ دىللىرىدا ۋەسۋەسە قىلغۇچى جىنلاردىن ۋە ئىنسانلاردىن بولغان يوشۇرۇن شەيتاننىڭ ۋەسۋەسىسىنىڭ شەررىدىن پاناھ تىلەيمەن» دېگىن'),
(6233, 114, 3, '«ئىنسانلارنىڭ پەرۋەردىگارى، ئىنسانلارنىڭ پادىشاھى، ئىنسانلارنىڭ ئىلاھى (اﷲ) غا سېغىنىپ، كىشىلەرنىڭ دىللىرىدا ۋەسۋەسە قىلغۇچى جىنلاردىن ۋە ئىنسانلاردىن بولغان يوشۇرۇن شەيتاننىڭ ۋەسۋەسىسىنىڭ شەررىدىن پاناھ تىلەيمەن» دېگىن'),
(6234, 114, 4, '«ئىنسانلارنىڭ پەرۋەردىگارى، ئىنسانلارنىڭ پادىشاھى، ئىنسانلارنىڭ ئىلاھى (اﷲ) غا سېغىنىپ، كىشىلەرنىڭ دىللىرىدا ۋەسۋەسە قىلغۇچى جىنلاردىن ۋە ئىنسانلاردىن بولغان يوشۇرۇن شەيتاننىڭ ۋەسۋەسىسىنىڭ شەررىدىن پاناھ تىلەيمەن» دېگىن'),
(6235, 114, 5, '«ئىنسانلارنىڭ پەرۋەردىگارى، ئىنسانلارنىڭ پادىشاھى، ئىنسانلارنىڭ ئىلاھى (اﷲ) غا سېغىنىپ، كىشىلەرنىڭ دىللىرىدا ۋەسۋەسە قىلغۇچى جىنلاردىن ۋە ئىنسانلاردىن بولغان يوشۇرۇن شەيتاننىڭ ۋەسۋەسىسىنىڭ شەررىدىن پاناھ تىلەيمەن» دېگىن'),
(6236, 114, 6, '«ئىنسانلارنىڭ پەرۋەردىگارى، ئىنسانلارنىڭ پادىشاھى، ئىنسانلارنىڭ ئىلاھى (اﷲ) غا سېغىنىپ، كىشىلەرنىڭ دىللىرىدا ۋەسۋەسە قىلغۇچى جىنلاردىن ۋە ئىنسانلاردىن بولغان يوشۇرۇن شەيتاننىڭ ۋەسۋەسىسىنىڭ شەررىدىن پاناھ تىلەيمەن» دېگىن');
