INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'شروع الله کا نام لے کر جو بڑا مہربان نہایت رحم والا ہے'),
(2, 1, 2, 'سب تعریفیں الله کے لیے ہیں جو سب جہانوں کا پالنے والا ہے'),
(3, 1, 3, 'بڑا مہربان نہایت رحم والا'),
(4, 1, 4, 'جزا کے دن کا مالک'),
(5, 1, 5, 'ہم تیری ہی عبادت کرتے ہیں اور تجھ ہی سے مدد مانگتے ہیں'),
(6, 1, 6, 'ہمیں سیدھا راستہ دکھا'),
(7, 1, 7, 'ان لوگوں کا راستہ جن پر تو نے انعام کیا نہ جن پر تیرا غضب نازل ہوا اور نہ وہ گمراہ ہوئے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'المۤ'),
(9, 2, 2, 'یہ وہ کتاب ہے جس میں کوئی بھی شک نہیں پرہیز گارو ں کے لیے ہدایت ہے'),
(10, 2, 3, 'جو بن دیکھے ایمان لاتے ہیں اور نماز قائم کرتے ہیں ا ور جو کچھ ہم نے انہیں دیا ہے اس میں خرچ کرتے ہیں'),
(11, 2, 4, 'اور جو ایمان لاتے ہیں اس پر جو اتارا گیا آپ پراور جو آپ سے پہلے اتارا گیا اور آخرت پر بھی وہ یقین رکھتے ہیں'),
(12, 2, 5, 'وہی لوگ اپنے رب کے راستہ پر ہیں اور وہی نجات پانے والے ہیں'),
(13, 2, 6, 'بے شک جو لوگ انکار کر چکے ہیں برابر ہے انہیں تو ڈرائے یا نہ ڈرائے وہ ایمان نہیں لائیں گے'),
(14, 2, 7, 'الله نے ان کے دلوں اورکانوں پرمہر لگا دی ہے اور ان کی آنکھوں پر پردہ ہے اوران کے لیے بڑا عذا ب ہے'),
(15, 2, 8, 'اور کچھ ایسے بھی لوگ ہیں جو کہتے ہیں کہ ہم الله اور قیامت کے دن پر ایمان لائے حالانکہ وہ ایمان دار نہیں ہیں'),
(16, 2, 9, 'الله اور ایمان داروں کو دھوکا دیتے ہیں حالانکہ وہ اپنے آپ ہی کو دھوکہ دے رہے ہیں اور نہیں سمجھتے'),
(17, 2, 10, 'انکے دلوں میں بیماری ہے پھر الله نے ان کی بیماری بڑھا دی اور ان کے لیے دردناک عذاب ہے اس لیے کہ وہ جھوٹ بولتے تھے'),
(18, 2, 11, 'اور جب انہیں کہا جاتا ہے کہ ملک میں فساد نہ ڈالو تو کہتے ہیں کہ ہم ہی تو اصلاح کرنے والے ہیں'),
(19, 2, 12, 'خبردار بے شک وہی لوگ فسادی ہیں لیکن نہیں سمجھتے'),
(20, 2, 13, 'اور جب انہیں کہا جاتا ہے ایمان لاؤ جس طرح اور لوگ ایمان لائے ہیں تو کہتے ہیں کیا ہم ایمان لائیں جس طرح بے وقوف ایمان لائے ہیں خبردار وہی بے وقوف ہیں لیکن نہیں جانتے'),
(21, 2, 14, 'اور جب ایمانداروں سے ملتے ہیں تو کہتے ہیں کہ ہم ایمان لائے اور جب اپنے شیطانوں کے پاس اکیلے ہوتے ہیں تو کہتے ہیں ہم توتمہارے ساتھ ہیں ہم تو صرف ہنسی کرنے والے ہیں'),
(22, 2, 15, 'الله ان سے ہنسی کرتا ہے اور انہیں مہلت دیتا ہے کہ وہ اپنی گمراہی میں حیران رہیں'),
(23, 2, 16, 'یہ وہ لوگ ہیں جنہوں نے ہدایت کے بدلے گمراہی خریدی سو ان کی تجارت نے نفع نہ دیا اور ہدایت پانے والے نہ ہوئے'),
(24, 2, 17, 'ان کی مثال اس شخص کی سی ہے جس نے آگ جلائی پھر جب آگ نے اس کے آس پاس کو روشن کر دیا تو الله نے ان کی روشنی بجھا دی اور انہیں اندھیروں میں چھوڑا کہ کچھ نہیں دیکھتے'),
(25, 2, 18, 'بہرے گونگے اندھے ہیں سو وہ نہیں لوٹیں گے'),
(26, 2, 19, 'یا جیسا کہ آسمان سے بارش ہو جس میں اندھیرے اور گرج اور بجلی ہو اپنی انگلیاں اپنے کانوں میں کڑک کے سبب سے موت کے ڈر سے دیتے ہوں اور الله کافروں کو گھیرے ہوئے ہے'),
(27, 2, 20, 'قریب ہے کہ بجلی ان کے آنکھیں اچک لے جب ان پر چمکتی ہے تو اس کی روشنی میں چلتے ہیں اور جب ان پر اندھیرا ہوتا ہے تو ٹھر جاتے ہیں اور اگر الله چاہے تو ان کے کان اور آنکھیں لے جائے بے شک الله ہر چیز پر قادر ہے'),
(28, 2, 21, 'اے لوگو اپنے رب کی عبادت کرو جس نے تمہیں پیدا کیا اور انہیں جو تم سے پہلے تھے تاکہ تم پرہیزگار ہو جاؤ'),
(29, 2, 22, 'جس نے تمہارے لیے زمین کو بچھونا اور آسمان کو چھت بنایا اور آسمان سے پانی اتارا پھر اس سے تمہارے کھانے کے لیے پھل نکالے سو کسی کو الله کا شریک نہ بناؤ حالانکہ تم جانتے بھی ہو'),
(30, 2, 23, 'اور اگر تمہیں اس چیز میں شک ہے جو ہم نے اپنے بندے پر نازل کی ہے تو ایک سورت اس جیسی لے آؤ اور الله کے سوا جس قدر تمہارے حمایتی ہوں بلا لو اگر تم سچے ہو'),
(31, 2, 24, 'بھلا اگر ایسا نہ کر سکو اور ہرگز نہ کر سکو گے تو اس آگ سے بچو جس کا ایندھن آدمی اور پتھر ہیں جو کافرو ں کے لیے تیار کی گئی ہے'),
(32, 2, 25, 'اوران لوگو ں کو خوشخبری دے جو ایمان لائے اور اچھے کام کیے کہ ان کے لیے باغ ہیں ان کے نیچے نہریں بہتی ہیں جب انہیں وہاں کا کوئي پھل کھانے کو ملے گا تو کہیں گے یہ تو وہی ہے جو ہمیں اس سے پہلے ملا تھا اور انہیں ہم شکل پھل دیئے جائیں گے اور ان کے لیے وہاں پاکیزہ عورتیں ہوں گی اوروہ وہیں ہمیشہ رہیں گے'),
(33, 2, 26, 'بے شک الله نہیں شرماتا اس بات سے کہ کوئی مثال بیان کرے مچھر کی یا اس چیز کی جو اس سے بڑھ کر ہے سو جو لوگ مومن ہیں وہ اسے اپنے رب کی طرف سے صحیح جانتے ہیں اور جو کافر ہیں سو کہتے ہیں الله کا اس مثال سے کیا مطلب ہے الله اس مثال سے بہتوں کو گمراہ کرتا ہے اور بہتوں کو اس سے ہدایت کرتا ہے اوراس سے گمراہ تو بدکاروں ہی کو کیا کرتا ہے'),
(34, 2, 27, 'جو الله کے عہد کو پختہ کرنے کے بعد توڑتے ہیں اور جس کے جوڑنے کا الله نے حکم دیا ہے اسے توڑتے ہیں اور ملک میں فساد کرتے ہیں وہی لوگ نقصان اٹھانے والے ہیں'),
(35, 2, 28, 'تم الله کا کیونکر انکار کرسکتے ہو حالانکہ تم بے جان تھے پھر تمہیں زندہ کیا پھر تمہیں مارے گا پھر تمہیں زندہ کرے گا پھر تم اسی کے پاس لوٹ کر جاؤ گے'),
(36, 2, 29, 'الله وہ ہے جس نے جو کچھ زمین میں ہے سب تمہارے لیے پیدا کیا ہےپھر آسمان کی طرف متوجہ ہوا تو انہیں سات آسمان بنایا اور وہ ہر چیز جانتا ہے'),
(37, 2, 30, 'اور جب تیرے رب نے فرشتوں سے کہا میں زمین میں ایک نائب بنانے والا ہوں فرشتوں نے کہا کیا تو زمین میں ایسے شخص کو نائب بنانا چاہتا ہے جو فساد پھیلائے اور خون بہائے حالانکہ ہم تیری حمد کے ساتھ تسبیح بیان کرتے اور تیری پاکی بیان کرتے ہیں فرمایا میں جو کچھ جانتا ہوں وہ تم نہیں جانتے'),
(38, 2, 31, 'اور الله نے آدم کو سب چیزوں کے نام سکھائے پھر ان سب چیزوں کو فرشتوں کے سامنے پیش کیا پھر فرمایا مجھے ان کے نام بتاؤ اگر تم سچے ہو'),
(39, 2, 32, 'انہوں نے کہا تو پاک ہے ہم تو اتنا ہی جانتے ہیں جتنا تو نے ہمیں بتایاہے بے شک تو بڑے علم والا حکمت والا ہے'),
(40, 2, 33, 'فرمایا اے آدم ان چیزو ں کے نام بتا دو پھر جب آدم نے انہیں ان کے نام بتا دیئے فرمایا کیا میں نے تمہیں نہیں کہا تھا کہ میں آسمانوں اور زمین کی چھپی ہوئی چیزیں جانتا ہوں اور جو تم ظاہر کرتے ہو اور جو چھپاتے ہو اسے بھی جانتا ہوں'),
(41, 2, 34, 'اورجب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو انہوں نے سجدہ کیا مگر ابلیس کہ اس نے انکار کیا اورتکبر کیا اورکافروں میں سے ہو گیا'),
(42, 2, 35, 'اور ہم نے کہا اے آدم تم اور تمہاری بیوی جنت میں جا کر رہو اور اس میں جو چاہو اور جہاں سےچاہو کھاؤ اور اس درخت کے نزدیک نہ جاؤ پھر ظالموں میں سے ہو جاؤ گے'),
(43, 2, 36, 'پھر شیطان نے ان کو وہاں سے ڈگمگایا پھر انہیں اس عزت و راحت سے نکالا کہ جس میں تھے اور ہم نے کہا تم سب اترو کہ تم ایک دوسرے کے دشمن ہو اور تمہارے لیے زمین میں ٹھکانا ہے اور سامان ایک وقت معین تک'),
(44, 2, 37, 'پھر آدم نے اپنے رب سے چند کلمات حاصل کیے پھر اس کی توبہ قبول فرمائی بے شک وہ توبہ قبول کرنے والا مہربان ہے'),
(45, 2, 38, 'ہم نے کہا کہ تم سب یہاں سے نیچے اتر جاؤ پھر اگرتمہارے پاس میری طرف سے کوئی ہدایت آئے پس جو میری ہدایت پر چلیں گے ان پرنہ کچھ خوف ہوگا اور نہ وہ غمگین ہوں گے'),
(46, 2, 39, 'اور جو انکار کریں گے اور ہماری آیتوں کو جھٹلائیں گے وہی دوزخی ہو ں گے جو اس میں ہمیشہ رہی گے'),
(47, 2, 40, 'اے بنی اسرائل میرے احسان یاد کرو جو میں نے تم پر کئے اور تم میرا عہد پورا کرو میں تمہارا عہد پورا کروں گا اور مجھ ہی سے ڈرا کرو'),
(48, 2, 41, 'اور اس کتاب پر ایمان لاؤ جو میں نے نازل کی تصدیق کرتی ہے اس کی جو تمہارے پاس ہے اور تم ہی سب سے پہلے اس کےمنکر نہ بنو اور میری آیتو ں کو تھوڑی قیمت پر نہ بیچو اور مجھ ہی سے ڈرو'),
(49, 2, 42, 'اور سچ میں جھوٹ نہ ملاؤ اور جان بوجھ کر حق کو نہ چھپاؤ'),
(50, 2, 43, 'اورنماز قائم کرو اوزکوةٰ دو اور رکوع کرنے والوں کے ساتھ رکوع کرو'),
(51, 2, 44, 'کیا لوگوں کو تم نیکی کا حکم کرتے ہو اور اپنے آپ کو بھول جاتے ہو حالانکہ تم کتاب پڑھتے ہو پھر کیوں نہیں سمجھتے'),
(52, 2, 45, 'اور صبر کرنے اور نماز پڑھنے سےمدد لیا کرو اوربے شک نماز مشکل ہے مگر ان پر جو عاجزی کرنے والے ہیں'),
(53, 2, 46, 'جو یہ سمجھتے ہیں کہ ہمیں ضرور اپنے رب سے ملنا ہے اور ہمیں اس کے پاس لوٹ کر جانا ہے'),
(54, 2, 47, 'اے بنی اسرائیل میری ان نعمتوں کو یاد کرو جو میں نے تمہیں دی تھیں اور میں نے تمہیں جہان پر فضیلت دی تھی'),
(55, 2, 48, 'اوراس دن سے ڈرو جس دن کوئی شخص کسی کے کچھ بھی کام نہ آئے گا اور نہ ان کے لیے کوئی سفارش قبول ہو گی اورنہ اس کی طرف سے بدلہ لیا جائے گا اور نہ ان کی مدد کی جائے گی'),
(56, 2, 49, 'اور جب ہم نے تمہیں فرعونیوں سے نجات دی و ہ تمہیں بری طرح عذاب دیا کرتے تھے تمہارے بیٹوں کو ذبح کرتے تھے اور تمہاری بیٹیوں کو زندہ رکھتے تھے اور اس میں تمہارے رب کی طرف سے تمہاری بڑی آزمائش تھی'),
(57, 2, 50, 'اور جب ہم نے تمہارے لیے سمندر کو پھاڑ دیا پھر تمہیں تو بچا لیا اور تمہارے دیکھتے دیکھتے فرعوینوں کو ڈبو دیا'),
(58, 2, 51, 'اورجب ہم نے موسیٰ سے چالیس رات کا وعدہ کیا پھر اس کے بعد تم نے بچھڑا بنا لیا حالانکہ تم ظالم تھے'),
(59, 2, 52, 'پھر اس کے بعدبھی ہم نے تمہیں معاف کر دیا تاکہ تم شکر کرو'),
(60, 2, 53, 'اورجب ہم نے موسیٰ کو کتاب اورقانون فیصل دیا تاکہ تم ہدایت پاؤ'),
(61, 2, 54, 'اور جب موسیٰ نے اپنی قوم سے کہا اے میری قوم بے شک تم نے بچھڑا بنا کر اپنی جانوں پر ظلم کیا سو اپنے پیدا کرنے والے کے آگے توبہ کرو پھر اپنے آپ کو قتل کرو تمہارے لیے تمہارے خالق کے نزدیک یہی بہتر ہے پھر اس نے تمہاری توبہ قبول کر لی بے شک وہی بڑا توبہ قبول کرنے والا نہایت رحم والا ہے'),
(62, 2, 55, 'اور جب تم نے کہا اے موسیٰ ہم ہرگز تیرا یقین نہیں کریں گے جب تک کہ روبرو الله کو دیکھ نہ لیں تب تمہیں بجلی نے دیکھتے ہی دیکھتے آ لیا'),
(63, 2, 56, 'پھر ہم نے تمہیں تمہاری موت کے بعد زندہ کر اٹھایا تاکہ تم شکر کرو'),
(64, 2, 57, 'اور ہم نے تم پر ابر کا سایہ کیا اور تم پر من اور سلویٰ اتارا جو کچھ ہم نے تمہیں پاکیزہ چیزیں عطا کی ہیں ان میں سے کھاؤ اور انہوں نے ہمارا کچھ نقصان نہ کیا بلکہ اپنا ہی نقصان کرتے رہے'),
(65, 2, 58, 'اور جب ہم نے کہا اس شہر میں داخل ہو جاؤ پھر اس میں جہاں سے چاہو بے تکلفی سے کھاؤ اور دروازہ میں سجدہ کرتے ہوئے داخل ہو اور کہتے جاؤ بخش دے تو ہم تمہارے قصور معاف کر دیں گے اور نیکی کرنے والوں کو زیادہ بھی دیں گے'),
(66, 2, 59, 'پھر ظالموں نے بدل ڈالا کلمہ سوائے اس کے جو انہیں کہا گیا تھا سو ہم نے ان ظالموں پر ان کی نافرمانی کی وجہ سے آسمان سے عذاب نازل کیا'),
(67, 2, 60, 'پھر جب موسیٰ نے اپنی قوم کے لیے پانی مانگا تو ہم نے کہا اپنے عصا کو پتھر پر مار سو اس سے بارہ چشمے بہہ نکلے ہر قوم نے اپنا گھاٹ پہچان لیا الله کے دیئے ہوئے رزق میں سے کھاؤ پیو اور زمین میں فساد مچاتےنہ پھرو'),
(68, 2, 61, 'اور جب تم نے کہا اے موسیٰ ہم ایک ہی طرح کے کھانے پر ہرگز صبر نہ کریں گے سو ہمارے لیے اپنے رب سے دعا مانگ کہ وہ ہمارے لیے زمین کی پیداوار میں سے ساگ اور ککڑی اور گیہوں اور مسور اور پیاز پیدا کر دے کہا کیا تم اس چیز کو لینا چاہتے ہو جو ادنیٰ ہے بدلہ اس کے جو بہتر ہے کسی شہر میں اُترو بے شک جو تم مانگتے ہو تمہیں ملے گا اور ان پر ذلت اور محتاجی ڈال دی گئی اور انہوں نے غضب الہیٰ کمایا یہ اس لیے کہ وہ الله کی نشانیوں کا انکار کرتے تھے اور نبیوں کو ناحق قتل کرتے تھے یہ اس لیے کہ نافرمان تھے اور حد سے بڑھ جاتے تھے'),
(69, 2, 62, 'جو کوئی مسلمان اور یہودی اور نصرانی اورصابئی الله اور قیامت کے دن پر ایمان لائے اور اچھے کام بھی کرے تو ان کا اجر ان کے رب کے ہاں موجود ہے اور ان پر نہ کچھ خوف ہو گا اور نہ وہ غمگین ہوں گے'),
(70, 2, 63, 'اور جب ہم نے تم سے عہد لیا اور تم پر کوہِ طور بلند کیا جو کچھ ہم نے تمہیں دیا ہے اسے مضبوط پکڑو اور جو کچھ اس میں ہے اسے یاد رکھو تاکہ تم پرہیزگار ہو جاؤ'),
(71, 2, 64, 'پھر تم اس کے بعد پھر گئے سو اگر تم پر الله کا فضل اور اس کی رحمت نہ ہوتی تو تم تباہ ہوجاتے'),
(72, 2, 65, 'اور بے شک تمہیں وہ لوگ بھی معلوم ہیں جنہوں نے تم میں سے ہفتہ کے دن زیادتی کی تھی پھر ہم نے ان سے کہا تم ذلیل بندر ہو جاؤ'),
(73, 2, 66, 'پھر ہم نے اس واقعہ کو اس زمانہ کے لوگوں کے لیے اور ان سے پچھلوں کے لیے عبرت اور پرہیز گاروں کے لیے نصیحت بنا دیا'),
(74, 2, 67, 'اور جب موسیٰ نے اپنی قوم سے کہا کہ الله تمہیں حکم دیتا ہے کہ ایک گائے ذبح کرو انہوں نے کہا کیا تم ہم سے ہنسی کرتا ہے کہا میں الله کی پناہ مانگتا ہوں اس سے کہ جاہلوں میں سے ہوں'),
(75, 2, 68, 'انہوں نے کہا ہمارے لیے اپنے رب سے دعا کر ہمیں بتائے کہ وہ گائے کیسی ہے کہا وہ فرماتا ہے کہ وہ ایک ایسی گائے ہے نہ بوڑھی اور نہ بچہ اس کے درمیان ہے پس کر ڈالو جو تمہیں حکم دیا جاتا ہے'),
(76, 2, 69, 'انہوں نے کہا ہمارے لیے اپنے رب سے دعا کر کہ ہمیں بتائے اس کا رنگ کیسا ہےکہاوہ فرماتا ہے کہ وہ ایک زرد گائے ہے اس کا رنگ خوب گہراہے دیکھنے والوں کو بھلی معلوم ہوتی ہے'),
(77, 2, 70, 'انہوں نے کہا ہمارے لیے اپنے رب سے دعا کر ہمیں بتائے کہ وہ کس قسم کی ہے کیوں کہ وہ گائے ہم پر مشتبہ ہو گئی ہے اور ہم اگر الله نے چاہا تو ضرور پتہ لگا لیں گے'),
(78, 2, 71, 'کہا و ہ فرماتا ہے کہ وہ ایک ایسی گائے ہے محنت کرنے والی نہیں جو زمین کو جوتتی ہو یا کھیتی کو پانی دیتی ہو بے عیب ہے اس میں کوئي داغ نہیں انہوں نے کہا اب تو نے ٹھیک بات بتائی پھر انہوں نے اسے ذبح کر دیا اور وہ کرنے والے تو نہیں تھے'),
(79, 2, 72, 'اور جب تم ایک شخص قتل کر کے اس میں جھگڑنے لگے اور الله ظاہر کرنے والا تھا اس چیز کو جسے تم چھپاتے تھے'),
(80, 2, 73, 'پھر ہم نے کہا اس مردہ پر اس گائے کا ایک ٹکڑا مارو اسی طرح الله مردوں کو زندہ کرے گا اور تمہیں اپنی قدرت کی نشانیاں دکھاتا ہے تاکہ تم سمجھو'),
(81, 2, 74, 'پھر ا سکے بعد تمہارے دل سخت ہو گئے گویا کہ وہ پتھر ہیں یا ان سے بھی زیادہ سخت اور بعض پتھر تو ایسے بھی ہیں جن سے نہریں پھوٹ کر نکلتی ہیں اور بعض ایسے بھی ہیں جو پھٹتے ہیں پھر ان سے پانی نکلتا ہے اور بعض ایسے بھی ہیں جو الله کے ڈر سے گر پڑتے ہیں اور الله تمہارے کاموں سے بے خبر نہیں'),
(82, 2, 75, 'کیا تمہیں امید ہے کہ یہود تمہارے کہنے پر ایمان لے آئیں گے حالانکہ ان میں ایک ایسا گروہ بھی گزرا ہے جو الله کا کلام سنتا تھا پھر اسے سمجھنے کے بعد جان بوجھ کر بدل ڈالتا تھا'),
(83, 2, 76, 'اور جب وہ ان لوگوں سے ملتے ہیں جو ایمان لاچکے ہیں تو کہتے ہیں ہم بھی ایمان لے آئےہیں اور جب وہ ایک دوسرے کے پاس علیحدٰہ ہوتے ہیں تو کہتے ہیں کیا تم انہیں وہ راز بتا دیتےہو جو الله نے تم پر کھولے ہیں تاکہ وہ اس سے تمہیں تمہارے رب کے روبرو الزام دیں کیا تم نہیں سمجھتے'),
(84, 2, 77, 'کیا وہ نہیں جانتے کہ الله جانتا ہے جو وہ چھپاتے ہیں اور جو وہ ظاہر کرتے ہیں'),
(85, 2, 78, 'اور بعض ان میں سے ان پڑھ ہیں جو کتاب نہیں جانتے سوائے جھوٹی آرزوؤں کے اور وہ محض اٹکل پچو باتیں بناتے ہیں'),
(86, 2, 79, 'سو افسوس ہے ان لوگوں پر جو اپنے ہاتھوں سے لکھتے ہیں پھر کہتے ہیں کہ یہ الله کی طرف سے ہے تاکہ اس سے کچھ روپیہ کمائیں پھر افسوس ہے ان کے ہاتھوں کے لکھنے پر اور افسوس ہے ان کی کمائی ہر'),
(87, 2, 80, 'اور کہتے ہیں ہمیں سوائے چند گنتی کے دنوں کے آگ نہیں چھوٹے گی کہہ دو کیا تم نے الله سے کوئی عہدلےلیا ہے کہ ہر گز الله اپنے عہد کا خلاف نہیں کرے گا یا تم الله پر وہ باتیں کہتے ہو جو تم نہیں جانتے'),
(88, 2, 81, 'ہاں جس نے کوئی گناہ کیا اور اسے اس کے گناہ نے گھیر لیا سو وہی دوزخی ہیں وہ اس میں ہمیشہ رہیں گے'),
(89, 2, 82, 'اور جو لوگ ایمان لائے اور نیک کام کئے وہی بہشتی ہیں وہ اس میں ہمیشہ رہیں گے'),
(90, 2, 83, 'اور جب ہم نے بنی اسرائیل سے عہد لیا کہ الله کے سوا کسی کی عبادت نہ کرنا اور ماں باپ اور رشتہ داروں اور یتیموں اور محتاجوں سے اچھا سلوک کرنا اور لوگوں سے اچھی بات کہنا اور نماز قائم کرنا اور زکوةٰ دینا پھر سوائے چند آدمیوں کے تم میں سے سب منہ موڑ کر پھر گئے'),
(91, 2, 84, 'اور جب ہم نے تم سے عہد لیا کہ آپس میں خونریزی نہ کرنا اور نہ اپنے لوگوں کو جلا وطن کرنا پھر تم نے اقرار کیا اور تم خود گواہ ہو'),
(92, 2, 85, 'پھر تم ہی وہ ہو کہ اپنے لوگوں کو قتل کرتے ہو اور ایک جماعت کو اپنے میں سے ان کے گھروں میں سے نکالتے ہو ان پر گناہ اور ظلم سے چڑھائی کرتے ہو اور اگر وہ تمہارے پاس قیدی ہو کر آئیں تو ان کا تاوان دیتے ہو حالانکہ تم پر ان کا نکالنا بھی حرام تھا کیا تم کتاب کے ایک حصہ پرایمان رکھتے ہو اور دوسرے حصہ کا انکار کرتے ہو پھرجو تم میں سے ایسا کرے اس کی یہی سزا ہے کہ دنیا میں ذلیل ہو اور قیامت کے دن بھی سخت عذاب میں دھکیلے جائیں اور الله اس سے بے خبر نہیں جو تم کرتے ہو'),
(93, 2, 86, 'یہی وہ لوگ ہیں جنہوں نے دنیا کی زندگی کو آخرت کے بدلہ خریدا سو ان سے عذاب ہلکانہ کیا جائے گا اور نہ انہیں کوئی مدد مل سکے گی'),
(94, 2, 87, 'اور بے شک ہم نے موسیٰ کو کتاب دی اور اس کے بعد بھی پے در پے رسول بھیجتے رہے اور ہم نے عیسیٰ مریم کے بیٹے کو نشانیاں دیں اور روح لقدس سے اس کی تائید کی کیا جب تمہارے پاس کوئی وہ حکم لایا جسے تمہارے دل نہیں چاہتے تھے تو تم اکڑ بیٹھے پھر ایک جماعت کو تم نے جھٹلایا اور ایک جماعت کو قتل کیا'),
(95, 2, 88, 'اور کہتے ہیں ہمارے دلوں پر غلاف ہیں بلکہ الله نے ان کے کفر کے سبب سے لعنت کی ہے سو بہت ہی کم ایمان لاتے ہیں'),
(96, 2, 89, 'اور جب ان کے پاس اللہ کی طرف سے کتاب آئی جو تصدیق کرتی ہے اس کی جو ان کے پاس ہے اور اس سے پہلے وہ کفار پر فتح مانگا کرتے تھے پھر جب ان کے پاس وہ چیز آئی جسے انہوں نے پہچان لیا تو اس کا انکارکیاسوکافروں پر الله کی لعنت ہے'),
(97, 2, 90, 'انہوں نے اپنی جانوں کو بہت ہی بری چیز کے لیے بیچ ڈالا یہ کہ الله کی نازل کی ہوئی چیزوں کا اس ضد میں آ کر انکار کرنے لگے کہ وہ پنے فضل کو اپنے بندوں میں سے جس پر چاہتا ہے کیوں نازل کر دیتا ہے سوغضب پر غضب میں آ گئے اور کافروں کے لیے ذلت کا عذاب ہے'),
(98, 2, 91, 'اورجب ان سے کہا جاتا ہے کہ اس پر ایمان لاؤ جو الله نے نازل کیا ہے تو کہتے ہیں ہم تو اسی کو مانتے ہیں جو ہم پر اترا ہے اور اسے نہیں مانتے ہیں جو اس کے سوا ہے حالانکہ وہ حق ہے اور تصدیق کرنے والی ہے جو ان کے پاس ہے کہہ دو پھر تم کیوں اس سے پہلے الله کے نبیوں کو قتل کرتے رہے اگر تم مومن تھے'),
(99, 2, 92, 'اور تمہارے پاس موسیٰ صریح معجزے لے کر آیا پھر تم نے اس کے بعد بچھڑے کو بنالیا اور تم ظالم تھے'),
(100, 2, 93, 'اور جب ہم نے تم سے عہد لیا اور تم پر کوہِ طورکو اٹھایا کہ جو ہم نے تمہیں دیا ہے اسے مضبوطی سے پکڑو اور سنو انہوں نے کہا ہم نے سن لیا اور مانیں گے نہیں اور ان کے دلوں میں کفر کی وجہ سے بچھڑے کی محبت رچ گئی تھی کہہ دو اگر تم ایمان دار ہو تو تمہارا ایمان تمہیں بہت ہی برا حکم دے رہا ہے'),
(101, 2, 94, 'کہہ دو اگر الله کے نزدیک آخرت کا گھر خصوصیت کے ساتھ سوائے اور لوگوں کے تمہارے ہی لئے ہے تو تم موت کی آرزو کرو اگر تم سچے ہو'),
(102, 2, 95, 'وہ کبھی بھی اس کی ہر گز آرزو نہیں کریں گے ان گناہوں کی وجہ سے جو ان کے ہاتھ آگے بھیج چکے ہیں اور الله ظالموں کو خوب جانتا ہے'),
(103, 2, 96, 'اور آپ انہیں زندگی پر سب لوگوں سے زيادہ حریص پائیں گے اور ان سے بھی جو مشرک ہیں ہرایک ان میں سے چاہتا ہے کہ کاش اسے ہزار برس عمرملے اور اسے عمر کا ملنا عذاب سے بچانے والا نہیں اور الله دیکھتا ہے جو وہ کرتے ہیں'),
(104, 2, 97, 'کہہ دوجو کوئی جبرائیل کا دشمن ہو سواسی نے اتاراہے وہ قرآن اللہ کے حکم سے آپ کے دل پر ان کی تصدیق کرتا ہے جو اس سے پہلے ہیں اور ایمان والوں کے لئے ہدایت اور خوشخبری ہے'),
(105, 2, 98, 'جو شخص اللہ اور اس کے فرشتوں اور اس کے رسولوں اور جبرائیل اور میکائیل کا دشمن ہو تو بیشک اللہ بھی ان کافروں کا دشمن ہے'),
(106, 2, 99, 'اور ہم نے آپ کی طرف روشن آیتیں اتاری ہیں اور ان سے انکاری نہیں مگر فاسق'),
(107, 2, 100, 'کیا جب کبھی انہوں نے کوئی عہد باندھا تو اسے ان میں سے ایک جماعت نے پھینک دیا بلکہ ان میں سے اکثرایمان ہی نہیں رکھتے'),
(108, 2, 101, 'اور جب ان کے پاس الله کی طرف سے وہ رسول آیا جو اس کی تصدیق کرتا ہے جو ان کے پا س ہے تو اہلِ کتاب کی ایک جماعت نے الله کی کتاب کو اپنی پیٹھ کے پیچھے ایسا پھینکا کہ گویا اسے جانتے ہی نہیں'),
(109, 2, 102, 'اور انہوں نے اس چیز کی پیروی کی جو شیطان سلیمان کی بادشاہت کے وقت پڑھتے تھے اور سلیمان نے کفر نہیں کیا تھا لیکن شیطانوں نے ہی کفر کیا لوگوں کو جادو سکھاتے تھے اور اس کی بھی جو شہر بابل میں ہاروت و ماروت دوفرشتوں پر اتارا گیا تھا اور وہ کسی کو نہ سکھاتے تھے جب تک یہ نہ کہہ دیتے ہم تو صرف آزمائش کے لیے ہیں تو کافر نہ بن پس ان سے وہ بات سیکھتے تھے جس سے خاوند اور بیوی میں جدائی ڈالیں حالانکہ وہ اس سے کسی کو الله کے حکم کے سوا کچھ بھی نقصان نہیں پہنچا سکتے تھے اور سیکھتے تھے وہ و ان کو نقصان دیتی تھی اورنہ نفع اور وہ یہ بھی جانتے تھے کہ جس نے جادو کو خریدا اس کے لیے آخرت میں کچھ حصہ نہیں اور وہ چیز بہت بری ہے جس کے بدلہ میں انہوں نے اپنے آپ کو بیچا کاش وہ جانتے'),
(110, 2, 103, 'اور اگر وہ ایمان لاتے اور پرہیز گاری کرتے تو البتہ الله کے ہاں کا اجر ان کے لیے بہتر تھا کاش وہ جانتے'),
(111, 2, 104, 'اے ایمان والو راعنا نہ کہو اور انظرنا کہو اور سنا کرو اور کافروں کے لیے دردناک عذاب ہے'),
(112, 2, 105, 'اہل کتاب کے کافر اور مشرک نہیں چاہتے کہ تمہارے رب کی طرف سے تم پر کوئی بھی اچھی بات نازل ہو اور الله اپنی رحمت کے ساتھ خاص کر لیتا ہے جسے چاہے اور الله بڑے فضل والا ہے'),
(113, 2, 106, 'ہم جو کسی آیت کو منسوخ کرتے ہیں یا بھلا دیتے ہیں تو اس سے بہتر یا اس کے برابر لاتے ہیں کیا تم نہیں جانتے کہ الله ہر چیز پر قاد رہے'),
(114, 2, 107, 'کیا تم نہیں جانتے الله ہی کے لیے آسمانوں اور زمین کی بادشاہت ہے اور تمہارے لیے الله کے سوا نہ کوئی دوست ہے نہ مددگار'),
(115, 2, 108, 'کیا تم چاہتے ہو کہ اپنے رسول سے سوال کرو جیسے اس سے پہلے موسیٰ سے سوال کیے گئے تھے اور جو کوئی ایمان کے عوض کفر کوبدل لے سو وہ سیدھے راستہ سے گمراہ ہوا'),
(116, 2, 109, 'اکثر اہلِ کتاب تو اپنے حسد سے حق ظاہر ہونے کے بعد بھی یہ چاہتے ہیں کہ کسی طرح سے تمہیں ایمان لانے کے بعد پھر کفر کی طرف لوٹا کر لے جائیں سو معاف کرو اور درگزر کرو جب تک کہ الله اپنا حکم بھیجے بے شک الله ہر چیز پر قادر ہے'),
(117, 2, 110, 'اور نماز قائم کرو اور زکوةٰ دو اور جو کچھ نیکی سے اپنےواسطے آگے بھیجو گے اسے الله کے ہاں پاؤ گے بے شک الله جو کچھ تم کرتے ہو سب دیکھتا ہے'),
(118, 2, 111, 'اور کہتے ہیں کہ سوائے یہود یا نصاریٰ کے اور کوئی جنت میں ہرگز داخل نہ ہوگا یہ ان کے ڈھکوسلے ہیں کہہ دو اپنی دلیل لاؤ اگر تم سچے ہو'),
(119, 2, 112, 'ہاں جس نے اپنا منہ الله کے سامنے جھکا دیا اور وہ نیکو کار بھی ہو تو اس کے لیے اس کا بدلہ اس کے رب کے ہاں ہے اور ان پر نہ کوئی خوف ہوگا اور نہ وہ غمگین ہوں گے'),
(120, 2, 113, 'اور یہود کہتے ہیں کہ نصاریٰ ٹھیک راہ پر نہیں اور نصاریٰ کہتے ہیں کہ یہودی راہے حق پر نہیں ہیں حالانکہ وہ سب کتاب پڑھتے ہیں ایسی ہی باتیں وہ لوگ بھی کہتے ہیں جو بے علم ہیں پھر الله قیامت کے دن ان باتوں کا کہ جس میں وہ جھگڑ رہے ہیں خودفیصلہ کرے گا'),
(121, 2, 114, 'اوراس سے بڑھ کر کون ظالم ہوگا جس نے الله کی مسجدوں میں اس کا نام لینے کی ممانعت کردی اور ان کے ویران کرنے کی کوشش کی ایسے لوگوں کا حق نہیں ہے کہ ان میں داخل ہوں مگر ڈرتے ہوئے ان کے لیے دنیا میں بھی ذلت ہے اوران کے لیے آخرت میں بہت بڑا عذاب ہے'),
(122, 2, 115, 'اورمشرق اور مغرب الله ہی کا ہے سو تم جدھر بھی رخ کرو ادھر ہی الله کا رخ ہے بے شک الله وسعت ولا جاننے والا ہے'),
(123, 2, 116, 'اور کہتے ہیں الله نے بیٹا بنایاہے حالانکہ وہ پاک ہے بلکہ جو کچھ آسمانوں اور زمین میں ہے سب اسی کا ہے سب اسی کے فرمانبردار ہیں'),
(124, 2, 117, 'آسمانوں اور زمین کا پیدا کرنے والا ہے اور جب کوئی چیز کرنا چاہتا ہے تو صرف یہی کہہ دیتا ہے کہ ہو جا سو وہ ہو جاتی ہے'),
(125, 2, 118, 'اوربے علم کہتے ہیں کہ الله ہم سے کیوں کلام نہیں کرتا یا ہمارے اس کوئی نشانی کیوں نہیں آتی ان سے پہلے لوگ بھی ایسی ہی باتیں کہہ چکے ہیں ان کے دل ایک جیسے ہیں یقین کرنے والوں کے لیے تو ہم نشانیاں بیان کر چکے ہیں'),
(126, 2, 119, 'بے شک ہم نے تمہیں سچائ کے ساتھ بھیجا ہے خوشخبری سنانے کے لیے اور ڈرانے کے لیے اور تم سے دوزخیوں کے متعلق باز پرس نہ ہو گی'),
(127, 2, 120, 'اورتم سے یہود اور نصاریٰ ہرگز راضی نہ ہوں گے جب تک کہ تم ان کے دین کی پیروی نہیں کرو گے کہہ دو بے شک ہدایت الله ہی کی ہدایت ہے اور اگر تم نے ان کی خواہشوں کی پیروی کی اس کے بعد جو تمہارے پاس علم آ چکا تو تمہارے لیے الله کے ہاں کوئی دوست اور مددگار نہیں ہوگا'),
(128, 2, 121, 'وہ لوگ جنہیں ہم نے کتاب دی ہے وہ اسے پڑھتے ہیں جیسا اس کے پڑھنے کا حق ہے وہی لوگ اس پر ایمان لاتے ہیں جور اس سے انکار کرتے ہیں وہی نقصان اٹھانے والے ہیں'),
(129, 2, 122, 'اے بنی اسرائل میرے احسان کو یاد کرو جو میں نے تم پر کیے اور بے شک میں نے تمہیں سارے جہاں پر بزرگی دی تھی'),
(130, 2, 123, 'اوراس دن سے ڈرو جس دن کوئی بھی کسی کے کام نہ آئے گا اور نہ اس سے بدلہ قبول کیا جائے گا اور نہ اسے کوئی سفارش نفع دے گی اور نہ وہ مدد دیے جائیں گے'),
(131, 2, 124, 'اور جب ابراھیم کو اس کے رب نے کئی باتوں میں آزمایا تو اس نےانہیں پورا کر دیا فرمایا بے شک میں تمہیں سب لوگوں کا پیشوا بنادوں گا کہا اور میری اولاد میں سے بھی فرمایا میرا عہد ظالموں کو نہیں پہنچے گا'),
(132, 2, 125, 'اور جب ہم نے کعبہ لوگوں کے لیے عبادت گاہ اور امن کی جگہ بنایا (اور فرمایا) مقام ابراہیم کو نماز کی جگہ بناؤ اور ہم نے ابراھیم اور اسماعیل سے عہد لیا کہ میرے گھر کو طواف کرنے والوں اور اعتکاف کرنے والوں اور رکوع کرنے والوں اور سجدہ کرنے والوں کے لیے پاک رکھو'),
(133, 2, 126, 'اورجب ابراھیم نے کہا اے میرے رب اسے امن کا شہر بنا دے اور اس کے رہنےو الوں کو پھلوں سے رزق دے جوکوئی ان میں سے الله اور قیامت کے دن پر ایمان لائے فرمایا اور جو کافر ہوگا سو اسے بھی تھوڑاسا فائدہ پہنچاؤں گا پھر اسے دوزخ کے عذاب میں دھکیل دوں گا اوروہ برا ٹھکانہ ہے'),
(134, 2, 127, 'اور جب ابراھیم اور اسماعیل کعبہ کی بنیادیں اٹھا رہے تھے اے ہمارے رب ہم سے قبول کر بے شک تو ہی سننے والا جاننے والا ہے'),
(135, 2, 128, 'اے ہمارے رب ہمیں اپنا فرمانبردار بنا دے اور ہماری اولاد میں سے بھی ایک جماعت کو اپنافرمانبردار بنا اور ہمیں ہمارے حج کے طریقے بتا دے اور ہماری توبہ قبول فرما بے شک تو بڑا توبہ قبول کرنے والا نہایت رحم والا ہے'),
(136, 2, 129, 'اے ہمارے رب اور ان میں ایک رسول انہیں میں سے بھیج جو ان پر تیری آیتیں پڑھیں اور انہیں کتاب اور دانائی سکھائے اور انہیں پاک کرے بے شک تو ہی غالب حکمت والا ہے'),
(137, 2, 130, 'اور کون ہے جو ملت ابراھیمی سے روگردانی کرے سوائے اس کے جو خود ہی احمق ہو اور ہم نے تو اسے دنیا میں بھی بزرگی دی تھی اور بے شک وہ آخرت میں بھی اچھے لوگوں میں سے ہوگا'),
(138, 2, 131, 'جب اسے اس کے رب نے کہا فرمانبردار ہو جا تو کہا میں جہانوں کا پروردگار کا فرمانبردار ہوں'),
(139, 2, 132, 'اور اسی بات کی ابراھیم اور یعقوب نے بھی اپنے بیٹوں کو وصیت کی کہ اے میرے بیٹو بے شک الله نےتمہارے لیے یہ دین چن لیا سو تم ہر گز نہ مرنا مگر درآنحالیکہ تم مسلمان ہو'),
(140, 2, 133, 'کیا تم حاضر تھے جب یعقوب کو موت آئی تب اس نے اپنے بیٹوں سے کہا تم میرے بعد کس کی عبادت کرو گے انہوں نے کہا ہم آپ کے اور آپ کے باپ دادا ابراھیم اور اسماعیل اور اسحاق کے معبود کی عبادت کریں گے جو ایک معبود ہے اور ہم اسی کے فرمانبردار ہیں'),
(141, 2, 134, 'یہ ایک جماعت تھی جو گزرچکی ان کے لیے ان کے اعمال میں اور تمہارے لیے اور تمہارے اعمال ہیں اور تم سے نہیں پوچھا جائے گا کہ وہ کیا کرتے تھے'),
(142, 2, 135, 'اور کہتے ہیں کہ یہودی یا نصرانی ہو جاؤ تاکہ ہدایت پاؤ کہہ دو بلکہ ہم تو ملت ابراھیمی پر رہیں گے جو موحد تھا اور مشرکوں میں سے نہیں تھا'),
(143, 2, 136, 'کہہ دو ہم الله پر ایمان لائے اور اس پر جو ہم پر اتارا گیا اور جو ابراھیم اور اسماعیل اور اسحاق اور یعقوب اور اس کی اولاد پر اتارا گیا اور جو موسیٰ اور عیسیٰ کو دیا گیا اور جو دوسرے نبیوں کو ان کے رب کی طرف سے دیا گیا ہم کسی ایک میں ان میں سے فرق نہیں کرتے اور ہم اسی کے فرمانبردار ہیں'),
(144, 2, 137, 'پس اگر وہ بھی ایمان لے آئيں جس طرح تم ایمان لائے ہو تو وہ بھی ہدایت پا گئے اور اگر وہ نہ مانیں تو وہی ضد میں پڑے ہوئے ہیں سو تمہیں ان سے الله کافی ہے اور وہی سننے والا جاننے والا ہے'),
(145, 2, 138, 'الله کا رنگ اللہ کے رنگ سے اور کس کا رنگ بہتر ہے اور ہم تو اسی کی عبادت کرتے ہیں'),
(146, 2, 139, 'کہہ دو کیا تم ہم سے الله کی نسبت جھگڑا کرتے ہو حالانکہ وہی ہمارا اور تمہارا رب ہے اور ہمارے لیے ہمارے عمل ہیں اور تمہاری لئے تمہارے عمل او رہم تو خا لص اسی کی عبادت کرتے ہیں'),
(147, 2, 140, 'یا تم کہتے ہو کہ ابراھیم اور اسماعیل اور اسحاق اور یعقوب اور اس کی اولاد یہودی یا نصرانی تھے کہہ دو کیا تم زیادہ جانتے ہو یا الله اور اس سے بڑھ کر کون ظالم ہے جو گواہی چھپائے جو اس کے پاس الله کی طرف سے ہےاور الله بے خبر نہیں اس سے جو تم کرتے ہو'),
(148, 2, 141, 'وہ ایک جماعت تھی جو گزر چکی ان کے لیے ان کے عمل ہیں اور تمہارے لیے تمہارے عمل ہیں اور تم سے ان کے اعمال کی نسبت نہیں پوچھا جائے گا'),
(149, 2, 142, 'بے وقوف لوگ کہیں گے کہ کس چیز نے مسلمانوں کو ان کے قبلہ سے پھیر دیا جس پر وہ تھے کہہ دو مشرق اور مغرب الله ہی کا ہے وہ جسے چاہتا ہے سیدھا راستہ دکھاتا ہے'),
(150, 2, 143, 'اور اسی طرح ہم نے تمہیں برگزیدہ امت بنایا تاکہ تم اور لوگوں پر گواہ ہو اور رسول تم پر گواہ ہو اور ہم نے وہ قبلہ نہیں بنایا تھا جس پر آپ پہلے تھے مگر اس لیے کہ ہم معلوم کریں اس کو جو رسول کی پیروی کرتا ہے اس سے جو الٹے پاؤں پھر جاتا ہےاور بے شک یہ بات بھاری ہے سوائے ان کے جنہیں الله نے ہدایت دی اور الله تمہارے ایمان کو ضائع نہیں کرے گا بے شک الله لوگوں پر بڑا مہربان نہایت رحم والا ہے'),
(151, 2, 144, 'بے شک ہم آپ کے منہ کا آسمان کی طرف پھرنا دیکھ رہے ہیں سو ہم آپ کو اس قبلہ کی طرف پھیر دیں گے جسے آپ پسند کرتے ہیں پس اب اپنا منہ مسجد حرام کی طرف پھیر لیجیئے اور جہاں کہیں تم ہوا کرو اپنے مونہوں کو اسی کی طرف پھیر لیا کرو اور بے شک وہ لوگ جنہیں کتاب دی گئی ہے یقیناً جانتے ہیں کہ وہی حق ہے ان کے رب کی طرف سے اور الله اس سے بے خبر نہیں جو وہ کر رہے ہیں'),
(152, 2, 145, 'اور اگر آپ ان کے سامنے تمام دلیلیں لے آئيں جنہیں کتاب دی گئی تو بھی وہ آپ کے قبلے کو نہیں مانیں گے اور نہ آپ ہی ان کے قبلہ کو ماننے والے ہیں اور نہ ان میں کوئی دوسرے قبلہ کو ماننے والا ہے اور اگر آپ ان کی خواہشوں کی پیروی کریں گے بعد اس کے کہ آپ کے پاس علم آ چکا تو بے شک آپ بھی تب ظالموں میں سے ہوں گے'),
(153, 2, 146, 'وہ لوگ جنہیں ہم نے کتاب دی تھی وہ اسے پہچانتے ہیں جیسے اپنے بیٹوں کو پہچانتے ہیں اور بےشک کچھ لوگ ان میں سے حق کوچھپاتے ہیں اور وہ جانتے ہیں'),
(154, 2, 147, 'آپ کے رب کی طرف سے حق وہی ہے پس شک کرنے والوں میں سے نہ ہو'),
(155, 2, 148, 'اور ہر ایک کے لیے ایک طرف ہے جس طرف وہ منہ کرتا ہے پس تم نیکیوں کی طرف دوڑو تم جہاں کہیں بھی ہو گے تم سب کو الله سمیٹ کر لے آئے گا بے شک الله ہر چیز پر قادر ہے'),
(156, 2, 149, 'اور جہاں سے آپ نکلیں تو اپنا منہ مسجد حرام کی طرف کیا کریں اور آپ کے رب کی طرف سےیہی حق بھی ہے اور الله تمہارے کام سے غافل نہیں'),
(157, 2, 150, 'اور آپ جہاں کہیں سے نکلیں تو اپنا منہ مسجد حرام کی طرف کیا کریں اور تم بھی جہاں کہیں ہو تو اپنا منہ ا س کی طرف کیاکر و تاکہ لوگوں کو تم پر کوئی الزام نہ رہے مگر ان میں سے جو ہٹ دھرم ہیں تم بھی ان سے نہ ڈرو اور ہم سے ڈرتے رہا کرو اور تاکہ میں اپنی نعمت تم پر پوری کروں اورتاکہ تم راہ پاؤ'),
(158, 2, 151, 'جیسا کہ ہم نے تم میں ایک رسول تم ہی میں سے بھیجا جو تم پر ہماری آیتیں پڑھتا ہے اور تمہیں پاک کرتا ہے اور تمہیں کتاب اور دانائی سکھاتا ہے اور تمہیں سکھاتا ہے جو تم نہیں جانتے تھے'),
(159, 2, 152, 'پس مجھے یاد کرو میں تمہیں یاد کروں گا اور میرا شکر کرو اور ناشکری نہ کرو'),
(160, 2, 153, 'اے ایمان والو صبر اور نماز سے مدد لیا کرو بے شک اللہ صبر کرنے والوں کے ساتھ ہے'),
(161, 2, 154, 'اور جو الله کی راہ میں مارے جائیں انہیں مرا ہوا نہ کہا کرو بلکہ وہ تو زندہ ہیں لیکن تم نہیں سمجھتے'),
(162, 2, 155, 'اور ہم تمہیں کچھ خوف اور بھوک اور مالوں اور جانوں اور پھلوں کے نقصان سے ضرور آز مائيں گے اور صبر کرنے والوں کو خوشخبری دے دو'),
(163, 2, 156, 'وہ لوگ کہ جب انہیں کوئی مصیبت پہنچتی ہے تو کہتے ہیں ہم تو الله کے ہیں اور ہم اسی کی طرف لوٹ کر جانے والے ہیں'),
(164, 2, 157, 'یہ لوگ ہیں جن پر ان کے رب کی طرف سے مہربانیاں ہیں اور رحمت اور یہی ہدایت پانے والے ہیں'),
(165, 2, 158, 'بے شک صفا اور مروہ الله کی نشانیوں میں سے ہیں پس جو کعبہ کا حج یا عمرہ کرے تو اس پر کوئی گناہ نہیں کہ ان کے درمیان طواف کرے اورجو کوئی اپنی خوشی سے نیکی کرے تو بے شک الله قدردان جاننے والا ہے'),
(166, 2, 159, 'بے شک جو لوگ ان کھلی کھلی باتوں اور ہدایت کو جسے ہم نے نازل کر دیا ہے اس کے بعد بھی چھپاتے ہیں کہ ہم نے ان کو لوگوں کے لیے کتاب میں بیان کر دیا یہی لوگ ہیں کہ ان پر الله لعنت کرتا ہے اور لعنت کرنے والے لعنت کرتے ہیں'),
(167, 2, 160, 'مگر وہ لوگ جنہوں نے توبہ کی اور اصلاح کر لی اور ظاہر کر دیا پس یہی لوگ ہیں کہ میں ان کی توبہ قبول کرتا ہوں اور میں بڑاتوبہ قبول کرنے والا نہایت رحم والا ہوں'),
(168, 2, 161, 'بے شک جنہوں نے انکار کیا اور انکار ہی کی حالت میں مر بھی گئے تو ان پر الله کی لعنت ہے اور فرشتوں اور سب لوگوں کی بھی'),
(169, 2, 162, 'وہ ہمیشہ اسی میں رہیں گے ان سے عذاب ہلکا نہ کیا جائے گا اور نہ وہ مہلت دیئے جائیں گے'),
(170, 2, 163, 'اور تمہارا معبود ایک ہی معبود ہے جس کے سوا کوئی معبود نہیں بڑا مہربان نہایت رحم والا ہے'),
(171, 2, 164, 'بے شک آسمانوں اور زمین کے پیدا کرنے میں اور رات اور دن کے بدلنے میں اور جہازوں میں جو دریا میں لوگوں کی نفع دینے والی چیزیں لے کر چلتے ہیں اور اس پانی میں جسسے الله نے آسمان سے نازل کیا ہے پھر اس سے مردہ زمین کو زندہ کرتا ہے اور اس میں ہر قسم کے چلنے والے جانور پھیلاتا ہے اور ہواؤں کے بدلنے میں اور بادل میں جو آسمان اور زمین کے درمیان حکم کا تابع ہے البتہ عقلمندوں کے لیے نشانیاں ہیں'),
(172, 2, 165, 'اورایسے لوگ بھی ہیں جنہوں نے الله کے سوا اور شریک بنا رکھے ہیں جن سے ایسی محبت رکھتے ہیں جیسی کہ الله سے رکھنی چاہیئے اور ایمان والوں کو تو الله ہی سے زیادہ محبت ہوتی ہے اور کاش دیکھتے وہ لوگ جو ظالم ہیں جب عذاب دیکھیں گے کہ سب قوت الله ہی کے لیے ہے اور الله سخت عذاب دینے والا ہے'),
(173, 2, 166, 'جب وہ لوگ بیزار ہو جائیں گے جن کی پیروی کی گئی تھی ان لوگوں سے جنہوں نے پیروی کی تھی اور وہ عذاب دیکھ لیں گے اور ان کے تعلقات ٹوٹ جائیں گے'),
(174, 2, 167, 'اور کہیں گے وہ لوگ جنہوں نے پیروی کی تھی کاش ہمیں دوبارہ جانا ہوتا تو ہم بھی ان سے بیزار ہوجاتے جیسے یہ ہم سے بیزار ہو جاتے جیسے یہ ہم سے بیزار ہوئے ہیں اسی طرح الله انہیں ان کے اعمال حسرت دلانے کے لیے دکھائے گا اور وہ دوزخ سے نکلنے والے نہیں'),
(175, 2, 168, 'اے لوگو ان چیزوں میں سے کھاؤ جو زمین میں حلال پاکیزہ ہیں اور شیطان کے قدموں کی پیروی نہ کرو بے شک وہ تمہارا صریح دشمن ہے'),
(176, 2, 169, 'وہ تو تمہیں برائی اور بے حیائی ہی کا حکم دے گا اور یہ کہ الله کے ذمے تم وہ باتیں لگاؤ جنہیں تم نہیں جانتے'),
(177, 2, 170, 'اور جب انہیں کہا جاتا ہے کہ اس کی پیروی کرو جو الله نے نازل کیا ہے تو کہتے ہیں بلکہ ہم تو اس کی پیروی کریں گے جس پر ہم نے اپنے باپ دادا کو پایا کیا اگرچہ ان کے باپ دادا کچھ بھی نہ سمجھتے ہوں اور نہ سیدھی راہ پائی ہو'),
(178, 2, 171, 'اوران کی مثال جو کافر ہیں اس شخص کی سی ہے جو اس چیز کو پکارتا ہے جو سوائے پکار اور آواز کے نہیں سنتی وہ بہرے ہیں گونگے ہیں اندھے ہیں پس وہ نہیں سمجھتے'),
(179, 2, 172, 'اے ایمان والو پاکیزہ چیزوں میں سے کھاؤ جو ہم نے تمہیں عطا کی اور الله کا شکر کرو اگر تم اس کی عبادت کرتے ہو'),
(180, 2, 173, 'سوائے اس کے نہیں کہ تم پر مردار اور خون اورسؤر کا گوشت اور اور اس چیز کو کہ الله کے سوا اور کے نام سے پکاری گئی ہو حرام کیا ہے پس جو لاچار ہو جائے نہ سرکشی کرنے والا ہو اور نے حد سے بڑھنے والا تو اس پر کوئی گناہ نہیں بے شک الله بخشنے والا نہایت رحم والا ہے'),
(181, 2, 174, 'بے شک جو لوگ الله کی نازل کی ہوئی کتاب کو چھپاتے اور اس کے بدلے میں تھوڑا سا مول لیتے ہیں یہ لوگ اپنے پیٹوں میں نہیں کھاتے مگر آگ اور الله ان سے قیامت کے دن کلام نہیں کرے گا اور نہ انہیں پاک کرے گا اور ان کے لیے دردناک عذاب ہے'),
(182, 2, 175, 'یہی وہ لوگ ہیں جنہوں نے گمراہی کو بدلے ہدایت کے خریدا اور عذاب کو بدلے بخشش کے پس دوزخ کی آگ پر ان کا کتنا بڑا صبر ہے'),
(183, 2, 176, 'یہ اس لیے کہ الله نے کتاب سچائی کے ساتھ اتاری اور بے شک جنہوں نے کتاب میں اختلاف کیا البتہ ضد میں بہت دور جا پڑے'),
(184, 2, 177, 'یہی نیکی نہیں کہ تم اپنے منہ مشرق اور مغرب کی طرف پھیرو بلکہ نیکی تو یہ ہے جو الله اور قیامت کے دن پر ایمان لائے اورفرشتوں اور کتابوں او رنبیوں پر اور ا سکی محبت میں رشتہ دارو ں اور یتیموں اور مسکینوں اور مسافروں اور سوال کرنے والوں کو اور گردنوں کے چھڑانے میں مال دے اور نماز پڑھے اور زکوةٰ دے اور جو اپنے عہدوں کو پورا کرنے والے ہیں جب وہ عہد کر لیں اورتنگدستی میں اور بیماری میں اور لڑائی کےوقت صبر کرنے والے ہیں یہی سچے لوگ ہیں اوریہی پرہیزگار ہیں'),
(185, 2, 178, 'اے ایمان والو مقتولوں میں برابری کرنا تم پر فرض کیا گیا ہے آزاد بدلے آزاد کے اور غلام بدلے غلام کے اور عورت بدلے عورت کے پس جسے اس کے بھائی کی طرف سے کچھ بھی معاف کیا جائے تو دستور کے موافق مطالبہ کرنا چاہیئے اور اسے نیکی کے ساتھ ادا کرنا چاہیئے یہ تمہارے رب کی طرف سے آسانی اور مہربانی ہے پس جو اس کے بعد زیادتی کرے تو اس کے لیے دردناک عذاب ہے'),
(186, 2, 179, 'اور اے عقلمندو تمہارے لیے قصاص میں زندگی ہے تاکہ تم (خونریزی سے) بچو'),
(187, 2, 180, 'تم پر فرض کیا گیا ہے کہ جب تم میں سے کسی کو موت آ پہنچے اگر وہ مال چھوڑے تو ماں باپ اور رشتہ داروں کے لیے مناسب طور پر وصیت کرے یہ پرہیزگاروں پرحق ہے'),
(188, 2, 181, 'پس جواسے اس کے سننے کےبعد بدل دے اس کا گناہ ان ہی پر ہے جو اسے بدلتے ہیں بے شک اللہ سننے والا جاننے والا ہے'),
(189, 2, 182, 'پس جو وصیت کرنے والے سے طرف داری یا گناہ کا خوف کرے پھر ان کے درمیان اصلاح کر دے تو اس پر کوئی گناہ نہیں بے شک الله بڑا بخشنے والا نہایت رحم والا ہے'),
(190, 2, 183, 'اے ایمان والو تم پر روزے فرض کیے گئے ہیں جو تم سے پہلے تھے تاکہ تم پرہیز گار ہو جاؤ'),
(191, 2, 184, 'گنتی کے چند روز پھر جو کوئی تم میں سے بیمار یا سفر پر ہو تو دوسرے دنو ں سے گنتی پوری کر لےاور ان پر جو اس کی طاقت رکھتے ہیں فدیہ ہے ایک مسکین کا کھانا پھر جو کوئی خوشی سے نیکی کرے تو وہ اس کے لیےبہتر ہے اورروزہ رکھنا تمہارے لیے بہتر ہے اگرتم جانتے ہو'),
(192, 2, 185, 'رمضان کا وہ مہینہ ہے جس میں قرآن اتارا گیا جو لوگوں کے واسطے ہدایت ہے اور ہدایت کی روشن دلیلیں اور حق و باطل میں فرق کرنے والا ہے سو جو کوئی تم میں سے اس مہینے کو پالے تو اس کے روزے رکھے اور جو کوئی بیمار یا سفر پر ہو تو دوسرے دنو ں سے گنتی پوری کر ے اللہ تم پر آسانی چاہتا ہے اور تم پر تنگی نہیں چاہتا اور تاکہ تم گنتی پوری کر لو اور تاکہ تم الله کی بڑائی بیان کرو اس پر کہ اس نے تمہیں ہدایت دی اور تاکہ تم شکر کرو'),
(193, 2, 186, 'اور جب آپ سےمیرے بندے میرے متعلق سوال کریں تو میں نزدیک ہوں دعاا کرنے والے کی دعا قبول کرتا ہوں جب وہ مجھے پکارتا ہے پھر چاہیئے کہ میرا حکم مانیں اور مجھ پر ایمان لائیں تاکہ وہ ہدایت پائیں'),
(194, 2, 187, 'تمہارے لیے روزوں کی راتو ں میں اپنی عورتوں سے مباثرت کرنا حلال کیا گیا ہے وہ تمہارے لیے پردہ ہیں اورتم ان کے لیے پردہ ہو الله کو معلوم ہے تم اپنے نفسوں سے خیانت کرتے تھے پس تمہاری توبہ قبول کر لی اور تمہیں معاف کر دیا سو اب ان سے مباثرت کرو اور طلب کرو وہ چیز جو الله نے تمہارے لیے لکھدی ہے اور کھاؤ اور پیو جب تک کہ تمہارے لیے سفید دھاری سیادہ دھاری سے فجر کے وقت صاف ظاہر ہو جاوے پھر روزوں کو رات پورا کرو اور ان سے مباثرت نہ کرو جب کہ تم مسجدوں میں معتکف ہو یہ الله کی حدیں ہیں سو ان کے قریب نہ جاؤ اسی طرح الله اپنی آیتیں لوگوں کے لیے بیان کرتا ہے تاکہ وہ پرہیز گار ہو جائیں'),
(195, 2, 188, 'اور ایک دوسرے کے مال آپس میں ناجائز طور پر نہ کھاؤ اور انہیں حاکموں تک نہ پہنچاؤ تاکہ لوگوں کے مال کا کچھ حصہ گناہ سے کھا جاؤ حالانکہ تم جانتے ہو'),
(196, 2, 189, 'آپ سے چاندوں کے متعلق پوچھتے ہیں کہہ دو یہ لوگوں کے لیے اور حج کے لیے وقت کے اندازے ہیں اور نیکی یہ نہیں ہے کہ تم گھروں میں ان کی پشت کی طرف سے آؤ اور لیکن نیکی یہ ہے کہ جو کوئی الله سے ڈرے اور تم گھروں میں ان کے دروازوں سے آؤ اور الله سے ڈرتے رہو تاکہ تم کامیاب ہو جاؤ'),
(197, 2, 190, 'اور الله کی راہ میں ان سے لڑو جوتم سے لڑیں اور زیادتی نہ کرو بے شک الله زیادتی کرنے والوں کو پسند نہیں کرتا'),
(198, 2, 191, 'اور انہیں قتل کرو جہاں پاؤ اور انہیں نکال دو جہاں سے انہوں نےتمہیں نکالا ہے اور غلبہ شرک قتل سے زیادہ سخت ہے اور مسجد حرام کے پاس ان سے نہ لڑو جب تک کہ وہ تم سے یہاں نہ لڑیں پھراگروہ تم سےلڑیں تم بھی انہیں قتل کرو کافروں کی یہی سزا ہے'),
(199, 2, 192, 'پھر اگر وہ باز آجائیں تو الله بڑا بخشنے والا نہایت رحم والا ہے'),
(200, 2, 193, 'اور ان سے لڑو یہاں تک کہ فساد باقی نہ رہے اور الله کا دین قائم ہو جائے پھر اگر وہ باز آجائیں تو سوائے ظالموں کے کسی پر سختی جائز نہیں'),
(201, 2, 194, 'حرمت واے مہینے کا بدلہ حرمت والا مہینہ ہے اور سب قابلِ تعظیم باتوں کا بدلہ ہے پھر جو تم پر زیادتی کرےتم بھی اس پر زیادتی کرو جیسی کہ اس نے تم پر زیادتی کی اور الله سے ڈرو اور جان لو کہ الله پرہیزگاروں کے ساتھ ہے'),
(202, 2, 195, 'اورالله کی راہ میں خرچ کرو اور اپنے آپ کوپنے ہاتھوں ہلاکت میں نہ ڈالو او رنیکی کرو بے شک الله نیکی کرنے والوں کو دوست رکھتا ہے'),
(203, 2, 196, 'اور الله کے لیے حج اور عمرہ پورا کرو پس اگر روکے جاؤ تو جو قربانی سے میسر ہو اور اپنے سر نہ منڈواؤ جب تک کہ قربانی اپنی جگہ پر نہ پہنچ جائے پھر جو کوئی تم میں سے بیمار ہو یا اسے سر میں تکلیف ہو تو روزوں سے یا صدقہ سے یا قربانی سے فدیہ دے پھر جب تم امن میں ہو تو عمرہ سے حج تک فائدہ اٹھائے تو قربانی سے جو میسر ہو (دے) پھر جو نہ پائے تو تین روزے حج کے دنوں میں رکھےاور سات جب تم لوٹو یہ دس پورے ہو گئے یہ ا س کے لیے ہے جس کا گھر بار مکہ میں نہ ہو اور الله سے ڈرتے رہو اور جان لو کہ الله سخت عذاب دینے والا ہے'),
(204, 2, 197, 'حج کے چند مہینے معلوم ہیں سو جو کوئي ان میں حج کا قصد کرے تو مباثرت جائز نہیں اور نہ گناہ کرنا اور نہ حج میں لڑائي جھگڑا کرنا اور تم جو نیکی کرتے ہو الله اس کو جانتا ہے اور زادِ راہ لے لیا کرو اور بہترین زادِ راہ پرہیزگاری ہے اور اے عقلمندوں مجھ سے ڈرو'),
(205, 2, 198, 'تم پر کوئی گناہ نہیں ہے کہ اپنے رب کا فضل تلاش کرو پھر جب تم عرفات سے پھرو تو مشعر الحرام کے پاس الله کو یاد کرو اور اس کی یاد اس طرح کرو کہ جس طرح اس نے تمیں بتائی ہے اور اس سے پہلے تو تم گمراہوں میں سے تھے'),
(206, 2, 199, 'پھر تم لوٹ کر آؤ جہاں سے لوٹ کر آتے ہیں اور الله سے بخشش مانگو بے شک الله بڑا بخشنے والا نہایت رحم والا ہے'),
(207, 2, 200, 'پھر جب حج کے ارکان ادا کر چکو تو الله کو یاد کرو جیسے تم اپنے باپ دادا کو یاد کیا کرتے تھے یا اس سے بھی بڑھ کر یاد کرنا پھر بعض تو یہ کہتے ہیں اےہمارے رب ہمیں دنیا میں دے اور اس کے لیے آخرت میں کوئی حصہ نہیں ہے'),
(208, 2, 201, 'اوربعض یہ کہتے ہیں کہ اے ہمارے رب ہمیں دنیا میں نیکی اور آخرت میں بھی نیکی دے اور ہمیں دوزخ کے عذاب سے بچا'),
(209, 2, 202, 'یہی وہ لوگ ہیں جنہیں ان کی کمائی کا حصہ ملتا ہے اور الله جلد حساب لینے والا ہے'),
(210, 2, 203, 'اور الله کو چند گنتی کے دنوں میں یاد کرو پھر جس نے دو دن کے اندر کوچ کرنے میں جلدی کی تو اس پر کوئی گناہ نہیں اور جو تاخیر کرے تو اس پر بھی کوئی گناہ نہیں جو (الله سے) ڈرتا ہے اور الله سے ڈرو اور جان لو کہ تم اسی کی طرف جمع کیے جاؤ گے'),
(211, 2, 204, 'اور بعض ایسے بھی ہیں جن کی بات دنیا کی زندگی میں آپ کو بھلی معلوم ہوتی ہے اور وہ اپنی دل کی باتوں پر الله کو گواہ کرتا ہے حالانکہ وہ سخت جھگڑالو ہے'),
(212, 2, 205, 'اور جب پیٹھ پھیر کر جاتا ہے تو ملک میں فساد ڈالتا اور کھیتی اور مویشی کو برباد کرنے کی کوشش کرتا ہے اور الله فساد کو پسندنہیں کرتا'),
(213, 2, 206, 'اور جب اسسے کہا جاتا ہے کہ الله سے ڈر تو شیخی میں آ کر اور بھی گناہ کرتا ہے سو اس کے لیے دوزخ کافی ہے اور البتہ وہ برا ٹھکانہ ہے'),
(214, 2, 207, 'اوربعض ایسے بھی ہیں جو الله کی رضا جوئی کے لیے اپنی جان بھی بیچ دیتے ہیں اور الله کے بندوں پر بڑا مہربان ہے'),
(215, 2, 208, 'اے ایمان والو اسلام میں سارے کے سارے داخل ہو جاؤ اور شیطان کے قدموں کی پیروی نہ کرو کیوں کہ وہ تمہارا صریح دشمن ہے'),
(216, 2, 209, 'پھر اگر تم کھلی کھلی نشانیاں آجانے کے بعد بھی پھسل گئے تو جان لو کہ الله غالب حکمت والا ہے'),
(217, 2, 210, 'کیا وہ انتظار کرتے ہیں کہ الله ان کے سامنے بادلوں کے سایہ میں آ موجود ہو اور فرشتے بھی آجائیں اور کام پورا ہو جائے اور سب باتیں الله ہی کے اختیار میں ہیں'),
(218, 2, 211, 'بنی اسرائیل سے پوچھیئے کہ ہم نے انہیں کتنی روشن دلیلیں دیں اور جو الله کی نعمت کو بدل دیتا ہے بعد اس کے کہ وہ اس کے پاس آ چکی ہو تو بے شک الله سخت عذاب دینے والا ہے'),
(219, 2, 212, 'کافروں کو دنیا کی زندگی بھلی لگتی ہے اور وہ ان لوگوں کا مذاق اڑاتے ہیں جو ایمان لائے حالانکہ جولوگ پرہیزگار ہیں وہ قیامت کے دن ان سے بالاتر ہوں گے اور الله جسے چاہے بے حساب رزق دیتا ہے'),
(220, 2, 213, 'سب لوگ ایک دین پر تھے پھر الله نے انبیاء خوشخبری دینے والے اور ڈرانے والے بھیجے اور ان کے ساتھ سچی کتابیں نازل کیں تاکہ لوگوں میں اس بات میں فیصلہ کرے جس میں اختلاف کرتے تھےاور اس میں اختلاف نہیں کیا مگر انہیں لوگوں نے جنھیں وہ (کتاب) دی گئی تھی اس کے بعد کہ ا ن کے پاس روشن دلیلیں آ چکی تھیں آپس کی ضد کی وجہ سے پھر الله نے اپنے حکم سے ہدایت کی ان کو جو ایمان والے ہیں اس حق بات کی جس میں وہ اختلاف کر رہے تھے اور الله جسے چاہے سیدھے راستے کی ہدایت کرتا ہے'),
(221, 2, 214, 'کیا تم خیال کرتے ہو کہ جنت میں داخل ہو جاؤ گے حالانکہ تمہیں وہ (حالات) پیش نہیں آئے جو ان لوگو ں کو پیش آئے جو تم سے پہلے ہو گزرے ہیں انہیں سختی اور تکلیف پہنچی اور ہلا دیئے گئے یہاں تک کہ رسول اور جو اس کے ساتھ ایمان لائے تھے بول اٹھے کہ الله کی مدد کب ہوگی سنو بے شک الله کی مدد قریب ہے'),
(222, 2, 215, 'آپ سے پوچھتے ہیں کیا خرچ کریں کہہ دو جو مال بھی تم خرچ کرو وہ ماں باپ اور رشتہ داروں اور یتیموں اور محتاجوں اور مسافروں کا حق ہے اور جو نیکی تم کرتے ہو سو بے شک الله خوب جانتا ہے'),
(223, 2, 216, 'تم پر جہاد فرض کیا گیا ہے اور وہ تمہیں ناگوار ہے اور ممکن ہے تم کسی چیز کو ناگوار سمجھو اور وہ تمہارے لیے بہتر ہو اور ممکن ہے کہ تم کسی چیز کو پسند کرو اور وہ تمہارے لیے مضر ہو اور الله ہی جانتا ہے اور تم نہیں جانتے'),
(224, 2, 217, 'آپ سے حرمت والے مہینے میں لڑائی کے متعلق پوچھتے ہیں کہہ دو اس میں لڑنا بڑا (گناہ) ہے اور الله کے راستہ سے روکنا اور اس کا انکار کرنا اور مسجد حرام سے روکنا اور اس کے رہنے والوں کو اس میں سے نکالنا الله کے نزدیک اس سے بڑا گناہ ہے اور فتنہ انگیزی تو قتل سے بھی بڑا جرم ہے اور وہ تم سے ہمیشہ لڑتے رہیں گے یہاں تک کہ تمہیں تمہارے دین سے پھیر دیں اگر ان کا بس چلےاور جو تم میں سے اپنے دین سے پھر جائے پھر کافر ہی مرجائے پس یہی وہ لوگ ہیں کہ ان کے عمل دنیا اور آخرت میں ضائع ہو گئے اور وہی دوزخی ہیں جو اسی میں ہمیشہ رہیں گے'),
(225, 2, 218, 'بے شک جو لوگ ایمان لائے اور جنہوں نے ہجرت کی اور الله کی راہ میں جہاد کیا وہی الله کی رحمت کے امیدوار ہیں اور الله بڑا بخشنے والا نہایت رحم والا ہے'),
(226, 2, 219, 'آپ سے شراب اور جوئے کے متعلق پوچھتے ہیں کہہ دو ان میں بڑا گنا ہے اور لوگوں کے لیے کچھ فائدے بھی ہیں اور ان کا گناہ ان کے نفع سے بہت بڑا ہے اور آپ سے پوچھتے ہیں کہ کیا خرچ کریں کہہ دو جو زاید ہو ایسے ہی الله تمہارے لیے آیتیں کھول کر بیان کرتا ہے تاکہ تم غور کرو'),
(227, 2, 220, 'دنیا اور آخرت کے بارے میں اور یتیموں کے متعلق آپ سے پوچھتے ہیں کہہ دو ان کی اصلاح کرنا بہتر ہے اور اگر تم انہیں ملا لو تو وہ تمہارے بھائی ہیں اور الله بگاڑنے والے کو اصلاح کرنےوالے سے جانتا ہے اور اگر الله چاہتا تو تمہیں تکلیف میں ڈالتا بے شک الله غالب حکمت والا ہے'),
(228, 2, 221, 'اور مشرک عورتیں جب تک ایمان نہ لائیں ان سے نکاح نہ کرو اورمشرک عورتوں سے ایمان دار لونڈی بہتر ہے گو وہ تمہیں بھلی معلوم ہو اور مشرک مردوں سے نکاح نہ کرو یہاں تک کہ وہ ایمان لائیں اور البتہ مومن غلام مشرک سے بہتر ہے اگرچہ وہ تمہیں اچھا ہی لگے یہ لوگ دوزخ کی طرف بلاتے ہیں اور الله جنت اور بخشش کی طرف اپنے حکم سے بلاتا ہے اور لوگوں کے لیے اپنی آیتیں کھول کر بیان کرتا ہے تاکہ وہ نصیحت حاصل کریں'),
(229, 2, 222, 'اور آپ سے حیض کے بارے میں پوچھتے ہیں کہہ دو وہ نجاست ہے پس حیض میں عورتوں سے علیحدٰہ رہو اور ان کے پاس نہ جاؤ یہاں تک کہ وہ پاک ہو لیں پھر جب وہ پاک ہو جائیں تو ان کے پاس جاؤ جہاں سے اللہ نےتمہیں حکم دیا ہے بے شک الله توبہ کرنے والوں کو دوست رکھتا ہے اوربہت پاک رہنے والوں کو دوست رکھتا ہے'),
(230, 2, 223, 'تمہاری بیویاں تمہاری کھیتیاں ہیں پس تم اپنی کھیتیوں میں جیسے چاہو آؤ اور اپنے لیے آئندہ کی بھی تیاری کرو اور الله سے ڈرتے رہو اور جان لو کہ تم ضرور اسے ملو گے اور ایمان والوں کو خوشخبری سنا دو'),
(231, 2, 224, 'اور الله کو اپنی قسموں کا نشانہ نہ بناؤ نیکی اور پرہیز گاری اور لوگو ں کے درمیان اصلاح کرنے سے اور الله سننے والا جاننے والا ہے'),
(232, 2, 225, 'الله تمہیں تمہاری قسموں میں بے ہودہ گوئی پر نہیں پکڑتا لیکن تم سے ان قسموں پر مواخذہ کرتا ہے جن کا تمہارے دلوں نے ارادہ کیا ہو اور الله بڑا بحشنے والا بردبار ہے'),
(233, 2, 226, 'جو لوگ اپنی بیویوں کے پاس جانے سے قسم کھا لیتے ہیں ان کے لیے چار مہینے کی مہلت ہے پھر اگر وہ رجوع کر لیں تو الله بڑا بخشنے والا نہایت رحم والا ہے'),
(234, 2, 227, 'اور اگر انہوں نے طلاق کا پختہ ارداہ کر لیا تو بے شک الله سننے والا جاننے والا ہے'),
(235, 2, 228, 'اور طلاق دی ہوئی عورتیں تین حیض تک اپنے آپ کو روکے رکھیں اور ان کے لیے جائز نہیں کہ چھپائیں جو الله نے ان کے پیٹوں میں پیدا کیاہے اگر وہ الله اور قیامت کے دن پر ایمان رکھتی ہیں اوران کے خاوند اس مدت میں ان کو لوٹالینے کے زیادہ حق دار ہیں اگر وہ اصلاح کا اردہ رکھتے ہیں اور دستور کے مطابق ان کا ویسا ہی حق ہےجیسا ان پر ہے اور مردوں کو ان پر فضیلت دی ہے اور الله غالب حکمت والا ہے'),
(236, 2, 229, 'طلاق دو مرتبہ ہے پھر بھلائی کے ساتھ روک لینا ہے یا نیکی کے ساتھ چھوڑ دنیا ہے اور تمہارے یے اس میں سے کچھ بھی لینا جائز نہیں جو تم نے انہیں دیا ہے مگر یہ کہ دونوں ڈریں کہ الله کی حدیں قائم نہیں رکھ سکیں گے پھر اگرتمہیں خوف ہو کہ دونوں الله کی حدیں قائم نہیں رکھ سکیں گے تو ان دونوں پر اس میں کوئی گناہ نہیں کہ عورت معاوضہ دے کر پیچھا چھڑالے یہ الله کی حدیں ہیں سو ان سے تجاوز نہ کرو اورجو الله کی حدوں سے تجاوز کرے گا سو وہی ظالم ہیں'),
(237, 2, 230, 'پھر اگر اسے طلاق دے دی تو اس کے بعد اس کے لیے وہ حلال نہ ہوگی یہاں تک کہ وہ کسی اور خاوند سے نکاح کرے پھر اگر وہ اسے طلاق دے دے تو ان دونوں پر کوئی گناہ نہیں کہ آپس میں رجوع کر لیں اگر ان کا گمان غالب ہو کہ وہ الله کی حدیں قائم سکیں گے اور یہ الله کی حدیں ہیں وہ انہیں کھول کر بیان کرتا ہے ان لوگوں کے لیے جو علم رکھتے ہیں'),
(238, 2, 231, 'اور جب عورتوں کو طلاق دے دوپھر وہ اپنی عدت کو پہنچ جائیں تو انہیں حسن سلوک سے روک لو یا انہیں دستور کے مطابق چھوڑ دو اور انہیں تکلیف دینے کے یے نہ روکو تاکہ تم سختی کرو اور جو ایسا کرے گا تو وہ اپنے اوپر ظلم کرے گا اور الله کی آیتوں کا تمسخر نہ اڑاؤ اورالله کے احسان کو یاد کرو جو اس نے تم پر کیا ہے اور جو اس نے تم پر کتاب اور حکمت اتاری ہے کہ تمہیں اس سے نصیحت کرے اور الله سے ڈرو اور جان لو کہ الله ہر چیز کو خوب جاننے والا ہے'),
(239, 2, 232, 'اورجب تم عورتوں کو طلاق دے دو پس وہ اپنی عدت تمام کر چکیں تو اب انہیں اپنے خاوندوں سے نکاح کرنے سے نہ روکو جب کہ وہ آپس میں دستور کے مطابق راضی ہو جائیں تم میں سے یہ نصیحت اسے کی جاتی ہے جو الله اور قیامت کے دن پر ایمان رکھتا ہے یہ تمہارے لیے بڑی پاکیزی اوربڑی صفائی کی بات ہے اور الله ہی جانتا ہے اور تم نہیں جانتے'),
(240, 2, 233, 'اور مائیں اپنے بچوں کو پورے دو برس دودھ پلائیں یہ اس کے لیے ہے جو دودھ کی مدت کو پورا کرنا چاہے اور باپ پر دودھ پلانے والیوں کا کھانا اور کپڑا دستور کے مطابق ہے کسی کو تکلیف نے دی جائے مگر اسی قدر کہ اس کی طاقت ہو نہ ماں کو اس کے بچہ کی وجہ سے تکلیف دی جائے اور نہ باپ ہی کو اس کی اولاد کی وجہ سے اور وارث پر بھی ویسا ہی نان نفقہ ہے پھر اگر دونوں اپنی رضا مندی اور مشورہ سے دودھ چھڑانا چاہیں تو ان پر کوئی گناہ نہیں ہے اور اگر کسی اور سے اپنی اولاد کو دودھ پلوانا چاہو تو اس میں بھی تم پر کوئی گناہ نہیں بشرطیکہ تم دے دو جو دستور کے مطابق تم نے دینا ٹھرایا ہے اور الله سے ڈرو اور جان لو کہ الله اسے جو تم کرتے ہو خوب دیکھتا ہے'),
(241, 2, 234, 'اور جو تم میں سے مر جائیں اور بیویاں چھوڑ جائيں تو ان بیویوں کو چار مہینے دس دن تک اپنے نفس کو روکنا چاہیئے پھر جب وہ اپنی مدت پوری کر لیں تو تم پر اس میں کوئی گناہ نہیں جو وہ دستور کے مطابق اپنے حق میں کریں اور الله اس سے جو تم کرتے ہو خبردار ہے'),
(242, 2, 235, 'اورتم پر اس میں گناہ نہیں ہے کہ ان عورتوں کو اشارہ سے پیغام نکاح دو اور یا تم اسے اپنے دل میں چھپاؤ الله جانتاہے کہ تمہیں ان عورتوں کا خیال پیدا ہو گا لیکن مخفی طور پر ان سے نکاح کا وعدہ نہ کرو مگر یہ کہ قاعدہ کے مطابق کوئی بات کہو اورجب تک معیاد نوشتہ پوری نہ ہو اس وقت تک نکاح کا قصد بھی نہ کرو اور جان لو کہ الله جانتا ہے جو کچھ تمہارے دلو ں میں ہے پس اس سے ڈرتے رہو اور جان لو الله بڑا بخشنے والا بردبار ہے'),
(243, 2, 236, 'تم پر کوئی گناہ نہیں اگر تم عورتوں کو طلاق دے دو جب کہ انہیں ہاتھ بھی نہ لگایا ہو اور ان کے لیے کچھ مہر بھی مقرر نہ کیا ہو اور انہیں کچھ سامان دے دو وسعت والے پر اپنے قدر کے مطابق اور مفلس پر اپنے قدر کے مطابق سامان حسب دستور ہے نیکو کاروں پر یہ حق ہے'),
(244, 2, 237, 'اور اگر تمہیں انہیں طلاق دو اس سے پہلےکہ انہیں ہاتھ لگاؤ حالانکہ تم ان کے لیے مہر مقرر کر چکے ہو تو نصف اس کا جو تم نے مقرر کیا تھا مگر یہ کہ وہ معاف کر دیں یا وہ شخص معاف کر دے جس کے ہاتھ میں نکاح کی گرہ ہے اور تمہارا معاف کر دینا پرہیزگاری کے زیادہ قریب ہے اور آپس میں احسان کرنا نہ بھولو کیوں کہ جو کچھ بھی تم کر رہے ہو الله اسے دیکھ رہا ہے'),
(245, 2, 238, 'سب نمازوں کی حفاظت کیا کرو اور (خاص کر) درمیانی نماز کی اور الله کے لیے ادب سے کھڑے رہا کرو'),
(246, 2, 239, 'پھر اگر تمہیں خوف ہو تو پیادہ یا سوار ہی (پڑھ لیا کرو) پھر جب امن پاؤ تو الله کو یادکیا کرو جیسا اس نے تمہیں سکھایا ہے جو تم نہ جانتے تھے'),
(247, 2, 240, 'اور جو لوگ تم میں سے مر جائیں اوربیویاں چھوڑ جائیں تو انہیں اپنی بیویوں کے لیے سال بھر کے لیے گزارہ کے واسطے وصیت کرنی چاہیئے گھر سے باہر گئے بغیر پھر اگر وہ خود نکل جائیں توتم پر اس میں کوئي گناہ نہیں جو وہ عورتیں اپنے حق میں دستور کے موافق کریں اور الله زبردست حکمت والا ہے'),
(248, 2, 241, 'اور طلاق دی ہوئی عورتوں کے واسطے دستور کے موافق خرچ دینا پرہیزگارو ں پر یہ لازم ہے'),
(249, 2, 242, 'اسی طرح الله تمہارے واسطے اپنے احکام بیان فرماتا ہے تاکہ تم سمجھ لو'),
(250, 2, 243, 'کیا تم نے ان لوگو ں کو نہیں دیکھا جو موت کے ڈر سے اپنے گھروں سے نکلے حالانکہ وہ ہزاروں تھے پھر الله نےان کو فرمایا کہ مرجاؤ پھر انہیں زندہ کر دیا بے شک الله لوگوں پر فضل کرنے والا ہے لیکن اکثر لوگ شکر نہیں کرتے'),
(251, 2, 244, 'اور الله کی راہ میں لڑو اور سمجھ لو کہ بے شک الله خوب سننے والا جاننے والا ہے'),
(252, 2, 245, 'ایسا کون شخص ہے جو الله کو اچھا قرض دے پھر الله اس کو کئی گناہ بڑھا کردے اور الله ہی تنگی کرتا ہے اور کشائش کرتا ہے اورتم سب اسی کی طرف لوٹائے جاؤ گے'),
(253, 2, 246, 'کیاتم نے بنی اسرائیل کی ایک جماعت کو موسیٰ کے بعد نہیں دیکھا جب انہوں نے اپنے نبی سے کہا کہ ہمارے لیے ایک بادشاہ مقرر کر دو تاکہ ہم الله کی راہ میں لڑیں پیغمبر نے کہاکیا یہ بھی ممکن ہے اگر تمہیں لڑائی کا حکم ہو تو تم اس وقت نہ لڑو انہوں نے کہا ہم الله کی راہ میں کیوں نہیں لڑیں گے حالانکہ ہمیں اپنے گھروں اور اپنےبیٹوں سے نکال دیا گیا ہے پھر جب انہیں لڑائی کا حکم ہوا تو سوائے چند آدمیوں کے سب پھر گئے اور الله ظالموں کو خوب جانتا ہے'),
(254, 2, 247, 'ان کے نبی نے ان سے کہا بے شک الله نے طالوت کو تمہارا بادشاہ مقرر فرمایا ہے انہوں نے کہا اس کی حکومت ہم پر کیوں کر ہو سکتی ہے اس سے تو ہم ہی سلطنت کے زیادہ مستحق ہیں اور اسے مال میں بھی کشائش نہیں دی گئی پیغمبر نے کہا بے شک الله نے اسے تم پر پسند فرمایا ہے اور اسے علم اور جسم میں زیادہ فراخ دی ہے اور الله اپنا ملک جسے چاہے دیتا ہے اور الله کشائش والا جاننے والا ہے'),
(255, 2, 248, 'اوربنی اسرائیل سے ان کے نبی نے کہاکہ طالوت کی بادشاہی کی یہ نشانی ہے کہ تمہارے پاس وہ صندوق واپس آئے گا جس میں تمہارے رب کی طرف سے اطمینان ہے اور کچھ بچی ہوئی چیزیں ہیں ان میں سے جو موسیٰ اور ہارون کی اولاد چھوڑ گئی تھی اس صندوق کو فرشتے اٹھا لائیں گے بے شک اس میں تمہارے لیے پوری نشانی ہے اگرتم ایمان والے ہو'),
(256, 2, 249, 'پھر جب طالوت فوجیں لے کر نکلا کہا بے شک الله ایک نہر سے تمہاری آزمائش کرنے والا ہے جس نے اس نہر کا پانی پیا تو وہ میرا نہیں ہے اور جس نے اسے نہ چکھا تو وہ بےشک میرا ہے مگر جو کوئی اپنے ہاتھ سے ایک چلو بھر لے (تو اسے معاف ہے) پھر ان میں سے سوائے چند آدمیوں کے سب نے اس کا پانی پی لیا پھر جب طالوت اور ایمان والے ا س کے ساتھ پار ہوئے تو کہنے لگے آج ہمیں جالوت اور اس کے لشکروں سے لڑنے کی طاقت نہیں جن لوگو ں کو خیال تھا کہ انہیں الله سے ملنا ہے وہ کہنے لگے بار ہا بڑی جماعت پر چھوٹی جماعت الله کے حکم سے غالب ہوئی ہے اور الله صبر کرنے والو ں کے ساتھ ہے'),
(257, 2, 250, 'اورجب جالوت اور اس کی فوجوں کے سامنے ہوئے تو کہا اے رب ہمارے دلوں میں صبر ڈال دے اور ہمارے پاؤں جمائے رکھ اور اس کافر قوم پر ہماری مدد کر'),
(258, 2, 251, 'پھر الله کے حکم سے مومنو ں نے جالوت کے لشکروں کو شکست دی اور داؤد نے جالوت کو مار ڈالا اور الله نے سلطنت اور حکمت داؤد کو دی اور جو چاہا اسے سکھایا اور اگر الله کابعض کو بعض کے ذریعے سے دفع کرا دینا نہ ہوتا تو زمین فساد سے پُر ہو جاتی لیکن الله جہان والوں پر بہت مہربان ہے'),
(259, 2, 252, 'یہ الله کی آیتیں ہیں ہم تمہیں ٹھیک طور پر پڑھ کر سناتے ہیں اوربے شک تو ہمارے رسولوں میں سے ہے'),
(260, 2, 253, 'یہ سب رسول ہیں ہم نے ان میں سے بعض کو بعض پر فضیلت دی ہے بعض وہ ہیں جن سے الله نے کلام فرمائی اور بعضوں کے درجے بلند کیے اور ہم نے عیسیٰ مریم کے بیٹے کو صریح معجزے دیے تھے اور اسے روح القدس کے ساتھ قوت دی تھی اور اگر الله چاہتا تو وہ لوگ جو ان پیغمبروں کے بعد آئے وہ آپس میں نہ لڑتے بعد اس کے کہ ان کے پاس صاف حکم پہنچ چکے تھے لیکن ان میں اختلاف پیدا ہو گیا پھر کوئی ان میں سے ایمان لایا اور کوئی کافر ہوا اور اگر الله چاہتا تو وہ آپس میں نہ لڑتے لیکن الله جو چاہتا ہے کرتا ہے'),
(261, 2, 254, 'اے ایمان والو! جو ہم نے تمہیں رزق دیا ہے اس میں سے خرچ کرو اس دن کے آنے سے پہلے جس میں نہ کوئی خرید و فروخت ہو گی اور نہ کوئی دوستی اور نہ کوئی سفارش اور کافر وہی ظالم ہیں'),
(262, 2, 255, 'الله کے سوا کوئی معبود نہیں زندہ ہےسب کا تھامنے والا نہ اس کی اونگھ دبا سکتی ہے نہ نیند آسمانوں اور زمین میں جو کچھ بھی ہے سب اسی کا ہے ایسا کون ہے جو اس کی اجازت کے سوا اس کے ہاں سفارش کر سکے مخلوقات کے تمام حاضر اور غائب حالات کو جانتا ہے اور وہ سب اس کی معلومات میں سےکسی چیز کا احاطہ نہیں کر سکتے مگر جتنا کہ وہ چاہے اس کی کرسی نے سب آسمانوں اور زمین کو اپنے اندر لے رکھا ہے اور الله کو ان دونوں کی حفاظت کچھ گراں نہیں گزرتی اور وہی سب سےبرتر عظمت والا ہے'),
(263, 2, 256, 'دین کے معاملے میں زبردستی نہیں ہے بے شک ہدایت یقیناً گمراہی سے ممتاز ہو چکی ہے پھر جو شخص شیطان کو نہ مانے اور الله پر ایمان لائے تو اس نے مضبوط حلقہ پکڑ لیاجو ٹوٹنے والا نہیں اور الله سننے والا جاننے والا ہے'),
(264, 2, 257, 'الله ایمان والوں کا مددگار ہے اور انہیں اندھیروں سے روشنی کی طرف نکالتا ہے اور جو لوگ کافر ہیں ان کے دوست شیطان ہیں انہیں روشنی سے اندھیروں کی طرف نکالتے ہیں یہی لوگ دوزخ میں رہنے والے ہیں وہ اس میں ہمیشہ رہیں گے'),
(265, 2, 258, 'کیا تو نے اس شخص کو نہیں دیکھا جس نے ابراھیم سے اس کے رب کی بابت جھگڑا کیا اس لیے کہ الله نے اسے سلطنت دی تھی جب ابراھیم نے کہا کہ میرا رب وہ ہے جو زندہ کرتا ہے اور مارتا ہے اس نے کہا میں بھی زندہ کرتا ہوں اور مارتا ہوں کہا ابراھیم نے بے شک الله سورج مشرق سے لاتا ہے تو اسے مغرب سے لے آ تب وہ کافر حیران رہ گیا اور الله بے انصافوں کی سیدھی راہ نہیں دکھاتا'),
(266, 2, 259, 'یا تو نے اس شخص کو نہیں دیکھا جو ایک شہر پر گزرا اور وہ اپنی چھتوں پر گرا ہوا تھاکہا اسے الله مرنے کے بعد کیوں کر زندہ کرے گا پھر الله نے اسے سو برس تک مار ڈالا پھر اسے اٹھایا کہا تو یہاں کتنی دیر رہا کہا ایک دن یا اس سے کچھ کم رہا فرمایا بلکہ تو سو برس رہا ہے اب تو اپنا کھانا اور پینا دیکھ وہ تو سڑا نہیں اور اپنے گدھے کو دیکھ اور ہم نے تجھے لوگوں کے واسطے نمونہ چاہا ہے اور ہڈیوں کیطرف دیکھ کہ ہم انہیں کس طرح ابھار کر جوڑدیتے ہیں پھر ان پر گوشت پہناتے ہیں پھر اس پر یہ حال ظاہر ہوا تو کہا میں یقین کرتا ہو ں کہ بے شک الله ہر چیز پر قادر ہے'),
(267, 2, 260, 'اور یاد کر جب ابراھیم نے کہا اے میرے پروردگار! مجھ کو دکھا کہ تو مردے کو کس طرح زندہ کرے گا فرمایا کہ کیا تم یقین نہیں لاتے کہا کیوں نہیں لیکن اس واسطے چاہتاہوں کہ میرے دل کو تسکین ہو جائے فرمایا تو چار جانور اڑنے والے پکڑے پھر انہیں اپنے ساتھ ہلا لے پھر ہر پہاڑ پر ان کے بدن کا ایک ایک ٹکڑا رکھ دے پھر ان کو بلا تیرے پاس دوڑتے ہوئے آئیں گے اور جان لے کہ بے شک الله زبردست حکمت والا ہے'),
(268, 2, 261, 'ان لوگوں کی مثال جو الله کی راہ میں مال خرچ کرتے ہیں ایسی ہے کہ جیسے ایک دانہ کہ اگائے سات بالیں ہر بال میں سو سو دانے اور الله جس کے واسطے چاہے بڑھاتا ہے اور الله بڑی وسعت جاننے والا ہے'),
(269, 2, 262, 'جو لوگ اپنے مال الله کی راہ میں خرچ کرتے ہیں پھر خرچ کرنے کے بعد نہ احسان رکھتے ہیں اور نہ ستاتے ہیں انہیں کے لیے اپنے رب کے ہاں ثواب ہے اوران پر نہ کوئی ڈر ہے اور نہ وہ غمگین ہوں گے'),
(270, 2, 263, 'مناسب بات کہہ دینا اور درگزر کرنا اس خیرات سے بہتر ہے جس کے بعد ستانا ہو اور الله بے پرواا نہایت تحمل والا ہے'),
(271, 2, 264, 'اے ایمان والو! احسان رکھ کر اور ایذا دے کے اپنی خیرات کو ضائع نہ کرو اس شخص کی طرح جو اپنا مال لوگوں کے دکھانے کو خرچ کرتا ہے اور الله پر اور قیامت کے دن پر یقین نہیں رکھتا سو اس کی مثال ایسی ہے جیسے صاف پتھر کہ اس پر کچھ مٹی پڑی ہو پھر اس پر زور کا مینہ برساپھر اسی کو بالکل صاف کر دیا ایسے لوگوں کو اپنی کمائی ذرا ہاتھ بھی نہ لگے گی اور الله کافروں کو سیدھی راہ نہیں دکھاتا'),
(272, 2, 265, 'اور ان لوگوں کی مثال جو اپنے مال الله کی رضا حاصل کرنے کے لیے اور اپنے دلوں کو مضبوط کر کے خرچ کرتے ہیں ایسی ہے جس طرح بلند زمین پر ایک باغ ہو اس پر زور کا مینہ برسا تو وہ باغ اپنا پھل دوگنا لایا اور اگر اس پر مینہ نہ برسایا تو شبنم ہی کافی ہے اور الله تمہارے کاموں کو خوب دیکھنے والا ہے'),
(273, 2, 266, 'کیا تم میں کسی کو یہ بات پسند آتی ہے کہ اس کا ایک باغ کھجور اور انگور کا ہو جس کے نیچے نہریں بہتی ہوں اسے اس باغ میں اوربھی ہر طرح کا میوہ حاصل ہو اور اس پر بڑھاپا آ گیا ہو اور اس کی اولاد ضعیف ہو تب اس باغ پرایک بگولہ آ پڑا جس میں آگ تھی جس سے وہ باغ جل گیا الله تمہیں اس طرح نشانیاں سمجھاتا ہے تاکہ تم سوچا کرو'),
(274, 2, 267, 'اے ایمان والو! اپنی کمائی میں سے ستھری چیزیں خرچ کرو اور اس چیز میں سے بھی جو ہم نے تمہارے لیے زمین سے پیدا کی ہے اور اس میں سے ردی چیز کا ارادہ نہ کرو کہ اس کو خرچ کرو حالانکہ تم اسے کبھی نہ لو مگر یہ کہ چشم پوشی کر جاؤ اور سمجھ لو کہ بے شک الله بے پرواہ تعریف کیا ہوا ہے'),
(275, 2, 268, 'شیطان تمہیں تنگدستی کا وعدہ دیتا ہے اور بے حیائی کا حکم کرتا ہے اور الله تمہیں اپنی بخشش اور فضل کا وعدہ دیتا ہے اور الله بہت کشائش کرنے والا سب کچھ جاننے والا ہے'),
(276, 2, 269, 'جس کو چاہتا ہے سمجھ دے دیتا ہے اور جسے سمجھ دی گئی تو اسے بڑی خوبی ملی اور نصیحت وہی قبول کرتے ہیں جو عقل والے ہیں'),
(277, 2, 270, 'اورجو تم خیرات کے طور پر خرچ کرو گے یا تم کوئی منت مانگوگے تو بے شک الله کو سب معلوم ہے اور ظالموں کا کوئی مددگار نہیں ہے'),
(278, 2, 271, 'اگر تم خیرات ظاہر کرکے دو تو بھی اچھی بات ہے اور اگر اسے چھپا کردو اور فقیروں کو پہنچادو تو تمہارے حق میں وہ بہتر ہے اور الله تمہارے کچھ گناہ دور کر دے گا اور الله تمہارے کاموں سے خوب خبر رکھنے والا ہے'),
(279, 2, 272, 'انہیں راہ پر لانا تیرے ذمہ نہیں اور لیکن الله جسے چاہے راہ پر لاتا ہے اور جو مال تم خرچ کرو گے اس کا نفع تمہاری جان کے لیے ہےاور الله ہی کی رضا مندی کے لیے خرچ کرو اور جو اچھی چیز تم خرچ کرو گے اس کا پورا اجر تمہیں دیا جائے گا ااور تم پر ظلم نہیں کیا جائے گا'),
(280, 2, 273, 'خیرات ان حاجت مندوں کے لیے ہے جو الله کی راہ میں رکےہوئے ہیں ملک میں چل پھر نہیں سکتے ناواقف ان کے سوال نہ کرنے سے انہیں مال دار سمجھتا ہے تو ان کے چہرے سے پہچان سکتا ہے لوگوں سے لپٹ کر سوال نہیں کرتے اور جو کام کی چیز تم خرچ کرو گے بے شک وہ الله کو معلوم ہے'),
(281, 2, 274, 'جو لوگ اپنے مال الله کی راہ میں رات اوردن چھپا کر اور ظاہر خرچ کرتے ہیں تو ان کے لیےاپنے رب کے ہاں ثواب ہے ان پر نہ کوئي ڈر ہے اور نہ وہ غمگین ہوں گے'),
(282, 2, 275, 'جو لوگ سود کھاتے ہیں قیامت کے دن وہ نہیں اٹھیں گے مگر جس طرح کہ وہ شخص اٹھتا ہے جس کے حواس جن نے لپٹ کر کھو دیئے ہیں یہ حالت ان کی اس لیے ہوگی کہ انہوں نے کہا تھا کہ سوداگری بھی تو ایسی ہی ہے جیسےسود لینا حالانکہ الله نے سوداگری کو حلال کیا ہے اور سود کو حرام کیا ہے پھر جسے اپنے رب کی طرف سے نصیحت پہنچی اوروہ باز آ گیا تو جو پہلے لے چکا ہے وہ اسی کا رہا اور اس کا معاملہ الله کے حوالہ ہے اور جو کوئی پھر سود لے وہی لوگ دوزخ والے ہیں وہ اس میں ہمیشہ رہیں گے'),
(283, 2, 276, 'الله سود کو مٹاتا ہے اور صدقات کو بڑھاتا ہے اور الله کسی ناشکرے گناہگار کو پسندنہیں کرتا'),
(284, 2, 277, 'جو لوگ ایمان لائے اور نیک کام کیے اور نماز کو قائم رکھا اور زکواةٰ دیتے رہے تو ان کے رب کے ہاں ان کااجر ہے اور ان پر کوئی خوف نہ ہوگا اور نہ وہ غمگین ہوں گے'),
(285, 2, 278, 'اے ایمان والو! الله سے ڈرو اور جو کچھ باقی سود رہ گیا ہے اسے چھوڑ دو اگر تم ایمان والے ہو'),
(286, 2, 279, 'اگر تم نے نہ چھوڑا تو الله اور اس کے رسول کی طرف سے تمہارےخلاف اعلان جنگ ہے اور اگرتوبہ کرلوتو اصل مال تمھارا تمہارے واسطے ہے نہ تم کسی پر ظلم کرو اور نہ تم پر ظلم کیا جائے گا'),
(287, 2, 280, 'اور اگر وہ تنگ دست ہے تو آسودہ حالی تک مہلت دینی چاہیئے اور بخش دو تو تمہارے لیے بہت ہی بہتر ہے اگر تم جانتے ہو'),
(288, 2, 281, 'اور اس دن سے ڈرو جس دن الله کی طرف لوٹائے جاؤ گے پھر پھر ہر شخص کو اس کی کمائی کا پورا پورا بدلہ دے دیا جائے گا اور ان پر ظلم نہ ہوگا'),
(289, 2, 282, 'اے ایمان والو! جب تم کسی وقت مقرر تک آپس میں ادھار کا معاملہ کرو تو اسے لکھ لیا کرو اور چاہیئے کہ تمہارے درمیان لکھنے والے انصاف سے لکھے اور لکھنے والا لکھنے سے انکار نہ کرے جیسا کہ اس کو الله نے سکھایا ہے سو اسے چاہیئے کہ لکھ دے اور وہ شخص بتلاتا جائے کہ جس پر قرض ہے اور الله سے ڈرے جو اس کا رب ہے اور اس میں کچھ کم کر کے نہ لکھائے پھر اگر وہ شخص کہ جس پر قرض ہے بے وقوف ہے یا کمزور ہے یا وہ بتلا نہیں سکتا تو اس کا کارکن ٹھیک طور پر لکھوا دے اور اپنے مردوں میں سے دو گواہ کر لیا کرو پھر اگر دو مرد نہ ہوں تو ایک مرد اور دوعورتیں ان لوگوں میں سے جنہیں تم گواہو ں میں سے پسند کرتے ہوتاکہ اگر ایک ان میں سے بھول جائے تو دوسری اسے یاد دلا دے اور جب گواہوں کو بلایا جائے تو انکار نہ کریں اور معاملہ چھوٹا ہو یا بڑا اس کی معیاد تک لکھنے میں سستی نہ کرو یہ لکھ لینا الله کے نزدیک انصاف کو زیادہ قائم رکھنے والا ہے اور شہادت کا زیادہ درست رکھنے والا ہے اور زیادہ قریب ہے اس بات کے کہ تم کسی شبہ میں نہ پڑو مگر یہ کہ سوداگری ہاتھوں ہاتھ ہو جسے آپس میں لیتے دیتے ہو پھر تم پر کوئی گناہ نہیں کہ اسے نہ لکھو اور جب آپس میں سودا کرو تو گواہ بنا لو اور لکھنے والے اور گواہ بنانے والے کو تکلیف نہ دی جائے اور اگر تم نے تکلیف دی تو تمہیں گناہ ہوگا اور الله سے ڈرو اور الله تمہیں سکھاتا ہے اور الله ہر چیز کا جاننے والا ہے'),
(290, 2, 283, 'اور اگر تم سفر میں ہو اور کوئی لکھنے والا نہ پاؤ تو گروی پر قبضہ کیا جائے اور اگر ایک تم میں سے دوسرے پر اعتبار کرے تو چاہیئے کہ کہ وہ شخص امانت ادا کردے جس پر اعتبار کیا گیا اور اپنے الله سے ڈرے جو اس کا رب ہے اور گواہی کو نہ چھپاؤ اور جو شخص اسے چھپائے گا تو بے شک اس کا دل گناہگار ہے اور جو کچھ تم کرتے ہو الله خوب جانتا ہے'),
(291, 2, 284, 'جو کچھ آسمانوں اور زمین میں ہے اللہ ہی کا ہے اور اگر تم اپنے دل کی بات ظاہر کرو گے یا چھپاؤ گے الله تم سے اس کا حساب لے گا پھر جس کو چاہے بخشے گا اور جسے چاہے عذاب کرے گا اور الله ہر چیز پر قادر ہے'),
(292, 2, 285, 'رسول نے مان لیا جو کچھ اس پر اس کے رب کی طرف سے اترا ہے اور مسلمانوں نے بھی مان لیا سب نے الله کو اور اس کے فرشتوں کو اور اس کی کتابوں کو اور اس کے رسولوں کو مان لیا ہے کہتے ہیں کہ ہم الله کے رسولوں کو ایک دوسرے سے الگ نہیں کرتے اور کہتے ہیں ہم نے سنا اور مان لیا اے ہمارے رب تیری بخشش چاہتے ہیں اور تیری ہی طرف لوٹ کر جانا ہے'),
(293, 2, 286, 'الله کسی کو اس کی طاقت کے سوا تکلیف نہیں دیتا نیکی کا فائدہ بھی اسی کو ہو گا اور برائی کی زد بھی اسی پر پڑے گی اے رب ہمارے! اگر ہم بھول جائیں یا غلطی کریں تو ہمیں نہ پکڑ اے رب ہمارے! اور ہم پر بھاری بوجھ نہ رکھ جیسا تو نے ہم سے پہلے لوگوں پر رکھا تھا اے رب ہمارے! اور ہم سے وہ بوجھ نہ اٹھوا جس کی ہمیں طاقت نہیں اور ہمیں معاف کر دے اور ہمیں بخش دے اور ہم پر رحم کر تو ہی ہمارا کارساز ہے کافروں کے مقابلہ میں تو ہماری مدد کر');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'المۤ'),
(295, 3, 2, 'الله اس کے سوا کوئی معبود نہیں زندہ ہے نظام کائنات کا سنبھالنے والا ہے'),
(296, 3, 3, 'اس نے تجھ پر یہ سچی کتاب نازل فرمائی جو پہلی کتابوں کی تصدیق کرتی ہے اور اسی نے اس کتاب سے پہلے تورات اور انجیل نازل فرمائی'),
(297, 3, 4, 'وہ کتابیں لوگوں کے لیے راہ نما ہیں اور اسی نے فیصلہ کن چیزیں نازل فرمائیں بے شک جو لوگ الله کی آیتوں سے منکر ہوئے ان کے لیے سخت عذاب ہے اور الله تعالیٰ زبردست بدلہ دینے والا ہے'),
(298, 3, 5, 'الله پر زمین اور آسمان میں کوئی چیز چھپی ہوئی نہیں'),
(299, 3, 6, 'وہی جس طرح چاہے ماں کے پیٹ میں تمہارا نقشہ بناتا ہے اس کے سوا اور کوئی معبود نہیں زبردست حکمت والا ہے'),
(300, 3, 7, 'وہی ہے جس نے تجھ پر کتاب اتاری اس میں بعض آیتیں محکم ہیں (جن کے معنیٰ واضح ہیں) وہ کتاب کی اصل ہیں اور دوسری مشابہ ہیں (جن کے معنیٰ معلوم یا معین نہیں) سو جن لوگو ں کے دل ٹیڑھے ہیں وہ گمراہی پھیلانے کی غرض سے اور مطلب معلوم کرنے کی غرض سے متشابہات کے پیچھے لگتے ہیں اور حالانکہ ان کا مطلب سوائے الله کے اور کوئی نہیں جانتا اور مضبوط علم والے کہتے ہیں ہمارا ان چیزوں پر ایمان ہے یہ سب ہمارے رب کی طرف سے ہیں اور نصیحت وہی لوگ مانتے ہیں جو عقلمند ہیں'),
(301, 3, 8, 'اے رب ہمارے! جب تو ہم کو ہدایت کر چکا تو ہمارے دلوں کا نہ پھیر اور اپنے ہاں سے ہمیں رحمت عطافرما بے شک تو بہت زیادہ دینے والا ہے'),
(302, 3, 9, 'اے ہمارے رب! توایک دن سب لوگوں کو جمع کرنے والا ہے جس میں کوئی شک نہیں بے شک الله اپنے وعدہ کا خلاف نہیں کرتا'),
(303, 3, 10, 'بے شک جو لوگ کافر ہیں ان کے مال او ران کی اولاد الله کے مقابلے میں ہر گز کام نہیں آئیں گے اوروہ لوگ دوزخ کا ایندھن ہیں'),
(304, 3, 11, 'جس طرح فرعون والوں اور ان سے پہلے لوگوں کا معاملہ تھا انہوں نے ہماری نشانیوں کو جھٹلایا پھر الله نے ان کے گناہوں کے سبب سے انہیں پکڑا اور الله سخت عذاب دینے والا ہے'),
(305, 3, 12, 'کافروں کو کہہ دے کہ اب تم مغلوب ہو گئے اور دوزخ کی طرف اکھٹے کیے جاؤ گے اوروہ برا ٹھکانہ ہے'),
(306, 3, 13, 'تمہارے سامنے ایک نمونہ دو فوجوں کا گزر چکا ہے جو آپس میں ملیں ایک فوج الله کی راہ میں لڑتی ہے اور دوسری فوج کافروں کی ہے وہ کافر مسلمانوں کو اپنے سے دوگنا دیکھ رہے تھے آنکھوں کے دیکھنے سے اور االله جسے چاہے اپنی مدد سے قوت دیتا ہے اس واقعہ میں دیکھنے والوں کے لیے عبرت ہے'),
(307, 3, 14, 'لوگوں کو مرغوب چیزوں کی محبت نے فریفتہ کیا ہوا ہے جیسے عورتیں اور بیٹے اور سونے اور چاندی کے جمع کیے ہوئے خزانے اور نشان کیے ہوئے گھوڑے اور مویشی اور کھیتی یہ دنیا کی زندگی کا فائدہ ہے اور الله ہی کے پاس اچھا ٹھکانہ ہے'),
(308, 3, 15, 'کہہ دے کیا میں تم کو اس سے بہتر بناؤں پرہیزگاروں کے لیے اپنے رب کے ہاں باغ ہیں جن کے نیچے نہریں بہتی ہیں ان میں ہمیشہ رہیں گے اور پاک عورتیں ہیں اور الله کی رضا مندی ہے اور الله بندوں کو خوب دیکھنے والا ہے'),
(309, 3, 16, 'وہ جو کہتے ہیں اے رب ہمارے! ہم ایمان لائے ہیں سو ہمیں ہمارے گناہ بخش دے اور ہمیں دوزخ کے عذاب سے بچا لے'),
(310, 3, 17, 'وہ صبر کرنے والے ہیں اور سچے ہیں اور فرمانبرداری کرنے والے ہیں اور خرچ کرنے والے ہیں اور پچھلی راتوں میں گناہ بخشوا نے والے ہیں'),
(311, 3, 18, 'الله نے اور فرشتوں نے اور علم والوں نے گواہی دی کہ اس کے سوا اورکوئی معبود نہیں وہی انصاف کا حاکم ہے اس کے سوا اورکوئی معبود نہیں زبردست حکمت والا ہے'),
(312, 3, 19, 'بےشک دین الله کے ہاں فرمابرداری ہی ہے اور جنہیں کتاب دی گئی تھی انہوں نے صحیح علم ہونے کے بعد آپس کی ضد کے باعث اختلاف کیا اور جو شخص الله کے حکموں کا انکار کرے تو الله جلد ہی حساب لینے والا ہے'),
(313, 3, 20, 'پھربھی اگر تجھ سے جھگڑیں تو ان سے کہہ دے کہ میں نے اپنا منہ الله کے تابع کیا ہے ان لوگوں نے بھی جو میرے ساتھ ہیں اور ان لوگوں سے کہہ دے جنہیں کتاب دی گئی ہے اور ان پڑھوں سے آیا تم بھی تابع ہوتے ہو پھر اگر وہ تابع ہو گئے تو انہوں نے بھی سیدھی راہ پالی اور اگر وہ منہ پھیریں تو تیرے ذمہ فقط پہنچا دینا ہے اور الله بندوں کو خوب دیکھنے والا ہے'),
(314, 3, 21, 'بے شک جولوگ الله کے حکموں کا انکار کرتے ہیں اور پیغمبروں کو ناحق قتل کرتے ہیں اور لوگوں میں سے انصاف کا حکم کرنے والوں کو قتل کرتے ہیں سو انہیں دردناک عذاب کی خوشخبری سنا دیے'),
(315, 3, 22, 'یہی وہ لوگ ہیں جنکی دنیا اور آخرت میں محنت ضائع ہو گئی اور ان کا کوئی مددگار نہیں'),
(316, 3, 23, 'کیا تو نے ان لوگوں کو نہیں دیکھا جنہیں ایک حصہ کتاب کا ملا وہ الله کی کتاب کی طرف بلائے جاتے ہیں تاکہ وہ کتاب ان میں فیصلہ کرے پھر ایک فرقہ ان میں سے پھر جاتا ہے ایسے حال میں کہ وہ منہ پھیرنے والے ہوتے ہیں'),
(317, 3, 24, 'یہ ا سلیے ہےکہ وہ کہتے ہیں کہ ہمیں ہرگز آگ نہیں لگے گی مگر چند دن گنتی کے اور ان کی بنائی ہوئی باتوں نے انہیں دین میں دھوکہ دیا ہوا ہے'),
(318, 3, 25, 'پھر ان کا کیا ہوگا جب ہم انہیں ایک دن جمع کریں گے جس کے آنے میں کوئی شبہ نہیں اور ہر کسی کواپنی کمائی کا اجر پورا دیا جائے گا اور ان پر کوئی ظلم نہیں کیا جائے گا'),
(319, 3, 26, 'تو کہہ اے الله! بادشاہی کے مالک! جسے تو چاہتا ہے سلطنت دیتاہے اور جس سے چاہتا ہے سلطنت چھین لیتا ہے جسے تو چاہتا ہے عزت دیتا ہے اور جسے توچاہے ذلیل کرتا ہے سب خوبی تیرے ہاتھ میں ہے بے شک تو ہر چیز قادر ہے'),
(320, 3, 27, 'تو رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اور زندہ کو مردہ سے نکالتا ہے اور مردہ کو زندہ سےنکالتا ہے اور جسے تو چاہتا ہے بے حساب رزق دیتا ہے'),
(321, 3, 28, 'مسلمان مسلمانوں کو چھوڑ کر کافروں کو دوست نہ بنائيں اور جوکوئی یہ کام کریں اسے الله سے کوئی تعلق نہیں مگر اس صورت میں کہ تم ان سے بچاؤ کرنا چاہو اور الله تمہیں اپنے سے ڈراتا ہے اور الله ہی کی طرف لوٹ کر جانا ہے'),
(322, 3, 29, 'تو کہہ دے اگر تم اپنے دل کی بات چھپاؤ یا ظاہر کرو اسے الله جانتا ہے اور جو کچھ آسمانوں اور جو کچھ زمین میں ہے اسے جانتا ہے اور الله ہر چیز ہر قادر ہے'),
(323, 3, 30, 'جس دن ہرشخص موجود پائے گا اپنے سامنے اس نیکی کو جو اس نے کی تھی اور جو کچھ کہ اس نے برائی کی تھی اس دن چاہے گا کہ کاش درمیان اس کے اور درمیان اس کی برائی کے مسافت دور کی ہو اور الله تمہیں اپنی ذات سے ڈراتا ہے اور الله بندوں پر شفقت کرنے والا ہے'),
(324, 3, 31, 'کہہ دو اگر تم الله کی محبت رکھتے ہو تو میری تابعداری کرو تاکہ تم سے الله محبت کرے اور تمہارے گناہ بخشے اور الله بخشنے والا مہربان ہے'),
(325, 3, 32, 'کہہ دو الله اور اس کے رسول کی فرمانبرداری کرو پھر اگر وہ منہ موڑیں تو الله کافروں کو دوست نہیں رکھتا'),
(326, 3, 33, 'بے شک اللهنے آدم کو اور نوح کو اور ابراھیم کی اولاد کو اور عمران کی اولاد کو سارے جہان سے پسند کیاہے'),
(327, 3, 34, 'جو ایک دوسرے کی اولاد تھے اور الله سننے والا جاننے والا ہے'),
(328, 3, 35, 'جب عمران کی عورت نے کہا اے میرے رب جو کچھ میرے پیٹ میں ہے سب سے آزاد کر کے میں نےتیری نذر کیا سو تو مجھ سے قبول فرما بے شک تو ہی سننے والا جاننے والا ہے'),
(329, 3, 36, 'پھر جب اسے جنا کہا اے میرے رب! میں نے تو وہ لڑکی جنی ہے اور جو کچھ اس نے جنا ہے الله اسے خوب جانتا ہے اور بیٹا بیٹی کی طرح نہیں ہوتا اور میں نے اس کا نام مریم رکھا اور میں اسے اور اس کی اولاد کو شیطان مردود سے بچا کر تیری پناہ میں دیتی ہوں'),
(330, 3, 37, 'پھر اسے اس کے رب نے اچھی طرح سے قبول کیا اور اسے اچھی طرح بڑھایا اور وہ زکریا کو سونپ دی جب زکریا اس کے پاس حجرہ میں آتے تو اس کے پاس کچھ کھانے کی چیز پاتے کہتے اے مریم! تیرے پاس یہ چیز کہاں سے آئی ہے وہ کہتی یہ الله کے ہاں سے آئی ہے الله جسے چاہے بے قیاس رزق دیتا ہے'),
(331, 3, 38, 'زکریا نے وہیں اپنے رب سے دعا کی کہا اے میرے رب! مجھے اپنے پاس سے پاکیزہ اولاد عطا فرما بے شک تو دعا کا سننے والا ہے'),
(332, 3, 39, 'پھر فرشتوں نے اس کو آواز دی جب وہ حجرے کے اندر نماز میں کھڑے تھے کہ بے شک الله تجھ کو یحییٰ کو خوشخبری دیتا ہے جو الله کے ایک حکم کی گواہی دے گا اور سردار ہوگا اور عورت کے پاس نہ جائے گا اور صالحین میں سے نبی ہوگا'),
(333, 3, 40, 'کہا اے میرے رب! میرا لڑکا کہاں سے ہوگا حالانکہ میں بڑھاپے کو پہنچ چکا ہوں اور میری بیوی بانجھ ہے فرمایا الله اسی طرح جو چاہتا ہے کرتا ہے'),
(334, 3, 41, 'کہا اے میرے رب! میرے لیے کوئی نشانی مقرر کر فرمایا تیرے لیے یہ نشانی ہے کہ تو لوگوں سے تین دن سوائے اشارہ کے بات نہ کر سکے گا اور اپنے رب کو بہت یاد کر اور شام اور صبح تسبیح کر'),
(335, 3, 42, 'اور جب فرشتوں نے کہا اے مریم! بے شک الله نے تجھے پسند کیا ہے تجھے پاک کیا ہے اور تجھے سب جہان کی عورتوں پر پسند کیا ہے'),
(336, 3, 43, 'اے مریم! اپنے رب کی بندگی کر اور سجدہ اور رکوع کرنے والوں کے ساتھ رکوع کر'),
(337, 3, 44, 'یہ غیب کی خبریں ہیں ہم بذریعہ وحی تمہیں اطلاع دیتے ہیں اورتو ان کے پاس نہیں تھا جب اپنا قلم ڈالنے لگے تھے کہ مریم کی کون پرورش کرے اور تو ان کے پاس نہیں تھا جب کہ وہ جھگڑتے تھے'),
(338, 3, 45, 'جب فرشتوں نے کہا اے مریم! الله تجھ کو ایک بات کی اپنی طرف سے بشارت دیتا ہے اس کا نام مسیح عیسیٰ بیٹا مریم کا ہوگا دنیا اور آخرت میں مرتبے والا اور الله کے مقربوں میں سے ہو گا'),
(339, 3, 46, 'اور جب کہ وہ ماں کی گود میں ہوگا تو لوگوں سے باتیں کرے گا اور جبکہ وہ ادھیڑ عمر کا ہوگا اور نیکوں میں سے ہوگا'),
(340, 3, 47, 'مریم نے کہا اے میرے رب! مجھے بیٹا کیسے ہو گا حالانکہ مجھے کسی آدمی نے ہاتھ نہیں لگایا فرمایا اسی طرح الله جو چاہے پیدا کرتا ہےجب کسی کام کا ارادہ کرتا ہے تو اس کو یہی کہتا ہے کہ ہوجا تو وہ ہو جاتا ہے'),
(341, 3, 48, 'اور اس کو کتاب سکھائے گا اور دانش عطا فرمائے گا اور توریت اور انجیل'),
(342, 3, 49, 'اور اس کو بنی اسرائیل کی طرف پیغمبر بنا کر بھیجے گا بے شک میں تمہارے رب کی طرف سے تمہارے پاس نشانیاں لے کر آیا ہوں کہ میں تمہیں مٹی سے ایک پرندہ کی شکل بنادیتا ہوں پھر اس میں پھونک مارتا ہوں اور وہ الله کے حکم سے اڑتا جانور ہو جاتا ہے اور مادر زاد اندھے اور کوڑھی کو اچھا کردیتا ہوں اور الله کے حکم سے مردے زندہ کرتا ہوں اور تمہیں بتا دیتا ہوں جو کھا کر آؤ اور جواپنے گھروں میں رکھ کر آؤ اس میں تمہار لیے نشانیاں ہیں اگر تم ایماندار ہو'),
(343, 3, 50, 'اور مجھ سے پہلی کتاب جو تورات ہے اس کی تصدیق کرنے والاہوں اور تا کہ تم کو بعض چیزیں حلال کر دوں جو تم پر حرام تھیں اور تمہارے پاس تمہارے رب کی طرف سے نشانی لے کر آیا ہوں سو الله سے ڈرو اور میرا کہنا مانو'),
(344, 3, 51, 'بے شک الله ہی میرا اور تمہارا رب ہے سو اسی کی بندگی کرو یہی سیدھا راستہ ہے'),
(345, 3, 52, 'جب عیسیٰ نے بنی اسرائیل کاکفر معلوم کیا تو کہاکہ الله کی راہ میں کون میرا مددگار ہے حواریو ں نے کہا ہم الله کے دین کی مدد کرنے والے ہیں ہم الله پر یقین لائے اور تو گواہ رہ کہ ہم فرمانبرادر ہونے والے ہیں'),
(346, 3, 53, 'اے رب ہمارے! ہم اس چیز پر ایمان لائے جو تو نے نازل کی اور ہم رسول کے تابعدار ہوئے سو تو ہمیں گواہی دینے والوں میں لکھ لے'),
(347, 3, 54, 'اور انہوں نے خفیہ تدبیر کی اور الله نے بھی خفیہ تدبیر فرمائی اور الله بہترین خفیہ تدبیر کرنے والو ں میں سے ہے'),
(348, 3, 55, 'جس وقت الله نے فرمایا اے عیسیٰ! بے شک میں تمہیں وفات دینے والا ہوں اور تمہیں اپنی طرف اٹھانے والا ہوں اور تمہیں کافروں سے پاک کرنے والا ہوں اور جو لوگ تیرے تابعدار ہوں گے انہیں ان لوگوں پر قیامت کے دن تک غالب رکھنے والا ہوں جو تیرے منکر ہیں پھر تم سب کو میری طرف لوٹ کر آنا ہوگا پھر میں تم میں فیصلہ کروں گا جس بات میں تم جھگڑتے تھے'),
(349, 3, 56, 'سو جو لوگ کافر ہوئے انہیں دنیا اور آخرت میں سخت عذاب دوں گا اور ان کا کوئی مددگار نہیں ہوگا'),
(350, 3, 57, 'اورجو لوگ ایمان لائے اور نیک کام کیے انہیں ان کا حق پورا پورا دے گا اور الله ظالموں کو پسند نہیں کرتا'),
(351, 3, 58, 'یہ آیتیں ہم تمہیں پڑھ کر سناتے ہیں اور نصیحت حکمت والی'),
(352, 3, 59, 'بے شک عیسیٰ کی مثال الله کے نزدیک آدم کی سی ہے اسے مٹی سے بنایا پھر اسے کہا کہ ہو جا پھر ہو گیا'),
(353, 3, 60, 'حق وہی ہے جو تیرا رب کہے پھر تو شک کرنے والو ں میں سے نہ ہو'),
(354, 3, 61, 'پھر جو کوئی تجھ سے اس واقعہ میں جھگڑے بعد اس کے کہ تیرے پاس صحیح علم آ چکا ہے تو کہہ دے آؤ ہم اپنے بیٹے اور تمہارے بیٹے اور اپنی عورتیں اور تمہاری عورتیں اور اپنی جانیں اور تمہاری جانیں بلائیں پھر سب التجا کریں اور الله کی لعنت ڈالیں ان پر جو جھوٹے ہوں'),
(355, 3, 62, 'بے شک یہی سچا بیان ہے اور الله کے سوا اور کوئی معبود نہیں اور بے شک الله ہی زبردست حکمت والا ہے'),
(356, 3, 63, 'پھر اگر پھر جائیں تو بے شک الله فساد کرنے والوں کو جانتا ہے'),
(357, 3, 64, 'کہہ اے اہلِ کتاب! ایک بات کی طرف آؤ جو ہمارے اور تمہارے درمیان برابر ہے کہ سوائے الله کے اور کسی کی بندگی نہ کریں اور اس کا کسی کو شریک نہ ٹھیرائیں اور سوائے الله کے کوئی کسی کو رب نہ بنائے پس اگر وہ پھر جائیں تو کہہ دو گواہ رہو کہ ہم تو فرمانبردار ہونے والے ہیں'),
(358, 3, 65, 'اے اہلِ کتاب! ابراھیم کے معاملہ میں کیوں جھگڑتے ہو حالانکہ تورات اور انجیل تو اس کے بعد اتری ہیں کیا تم یہ نہیں سجحھتے'),
(359, 3, 66, 'ہاں! تم وہ لوگ ہو جس چیز کا تمہیں علم تھا اس میں تو جھگڑے پس اس چیز میں کیوں جھگڑتے ہو جس چیز کا تمہیں علم ہی نہیں اور الله جانتا ہے اور تم نہیں جانتے'),
(360, 3, 67, 'ابراھیم نہ یہوودی تھے اور نہ نصرانی لیکن سیدھے راستے والے مسلمان تھے اور مشرکوں میں سے نہ تھے'),
(361, 3, 68, 'لوگوں میں سب سے زیادہ قریب ابراھیم کے وہ لوگ تھے جنہوں نے اس کی تابعداری کی اور یہ نبی اور جو اس نبی پر ایمان لائے اور الله ایمان والوں کا دوست ہے'),
(362, 3, 69, 'بعض اہلِ کتاب دل سے چاہتے ہیں کہ کسی طرح تم کو گمراہ کردیں اور گمراہ نہیں کرتے مگر اپنے نفسوں کو اور نہیں سمجتے'),
(363, 3, 70, 'اے اہلِ کتاب! الله کی آیتو ں کا کیوں انکار کرتے ہو حالانکہ تم گواہ ہو'),
(364, 3, 71, 'اے اہلِ کتاب! سچ میں جھوٹ کیوں ملاتے ہو اور سچی بات کو چھپاتے ہو حالانکہ تم جانتے ہو'),
(365, 3, 72, 'اور اہل کتاب میں سے ایک جماعت نے کہا جو کچھ مسلمانوں پر اترا ہے اس پر صبح ایمان لاؤ اور شام کو اس سے انکار کر دو شاید کہ وہ پھر جائیں'),
(366, 3, 73, 'اوراپنے مذہب والے کے سوا کسی کی بات نہ مانو ان سے کہہ دو کہ بے شک ہدایت وہی ہے جو الله ہدایت کرے اور یہ بات نہ مانو کہ کوئی شخص دیا جا سکتا ہے مثل اس کے کہ تم دیے گئے ہو یا کوئی گروہ خدا کے ہاں تم پر الزام قائم کرسکتا ہے ان سے کہہ دو کہ فضل الله کے اختیار میں ہے جسے چاہے وہ دیتا ہے اور الله کشائش والا جاننے والا ہے'),
(367, 3, 74, 'جسے چاہے اپنی مہربانی سے خاص کرتا ہے اور الله بڑے فضل والا ہے'),
(368, 3, 75, 'اور اہل کتاب میں بعض ایسے ہیں کہ اگر تو ان کے پاس ایک ڈھیر مال کا امانت رکھے وہ تجھ کو ادا کریں اور بعضے ان میں سے وہ ہیں اگر تو ان کے پاس ایک اشرفی امانت رکھے تو بھی تجھے واپس نہیں کریں گے ہاں جب تک کہ تو اس کے سر پر کھڑا رہے یا اس واسطے ہے کہ وہ کہتے ہیں ہم پر ان پڑھ لوگوں کا حق لینے میں کوئی گناہ نہیں اور الله پر وہ جھوٹ بولتے ہیں حالانکہ وہ جانتے ہیں'),
(369, 3, 76, 'گناہ کیوں نہ ہوگا جس شخص نے اپنا عہد پورا کیا اور الله سے ڈرا تو بے شک پرہیزگاروں کو دوست رکھتا ہے'),
(370, 3, 77, 'بے شک جو لوگ الله کے عہد اور اپنی قسمو ں کے بدلے حقیر معاوضہ لیتے ہیں آخرت میں ان کا کوئی حصہ نہیں اور ان سے الله کلام نہیں کرے گا اور قیامت کے دن ان کی طرف نہ دیکھے گا اور انہیں پاک بھی نہ کرے گا اور ان کے لیے دردناک عذاب ہے'),
(371, 3, 78, 'اوربے شک ان میں سے ایک جماعت ہے کہ کتاب کو زبان مروڑ کر پڑھتے ہیں تاکہ تم یہ خیال کرو کہ وہ کتاب میں سے ہے حالانکہ وہ کتاب میں سے نہیں ہے اوروہ کہتے ہیں کہ الله کے ہاں سے ہے حالانکہ وہ الله کے ہاں سے نہیں ہے اور الله پر جان بوجھ کر جھوٹ بولتے ہیں'),
(372, 3, 79, 'کسی انسان کے لیے یہ جائز نہیں ہے کہ الله اسے کتاب اور حکمت اور نبوت عطا فرمائے پھر وہ لوگوں سے یہ کہے کہ الله کو چھوڑ کر میرے بندے ہو جاؤ لیکن کہے گا کہ تم لوگ الله والے بن جاؤ اس لیے کہ تم الله کی کتاب سکھاتے ہو اوراس واسطے کہ تم پڑھتے ہو'),
(373, 3, 80, 'اورنہ یہ جائز ہے کہ تمہیں حکم کرے کہ تم فرشتوں اور نبیوں کو رب بنا لو کیا وہ تمہیں کفر سکھائے گا بعد اس کے کہ تم مسلمان ہو چکے ہو'),
(374, 3, 81, 'اورجب الله نے نبیوں سے عہد لیا اور البتہ جو کچھ میں تمہیں کتاب ابور علم سے دوں پھر تمہارے پاس پیغنبر آئے جو اس چیز کی تصدیق کرنے والا ہو جو تمہارے پاس ہے البتہ اس پر ایمان لے آنا اور البتہ اس کی مدد کرنا فرمایا کیاتم نے اقرار کیا اور اس شرط پر میرا عہد قبول کیا انہوں نے کہا ہم نے اقرار کیا الله نے فرمایا تو اب تم گواہ رہو میں بھی تمہارے ساتھ گواہ ہوں'),
(375, 3, 82, 'پھر جو کوئی اس کے بعد پھر جائے تو وہی لوگ نافرمان ہیں'),
(376, 3, 83, 'کیا الله کے دین کے سوا کوئی اور دین تلاش کرتے ہیں حالانکہ جو کوئی آسمان اور زمین میں ہے خوشی سے یا لاچاری سے سب اسی کے تابع ہے اور اسی کی طرف لوٹائے جائیں گے'),
(377, 3, 84, 'کہہ دو ہم الله پر ایمان لائے اور جو کچھ ہم پر نازل کیاگیا اور جو کچھ ابراھیم اور اسماعیل اور اسحاق اور یعقوب اور اس کی اولاد پر نازل کیا گیا اور جو کچھ موسیٰ اور عیسیٰ اور سب نبیوں کو ان کے رب کی طرف سے ملا ہم ان میں سے کسی کو جدا نہیں کرتے اور ہم اسی کے فرمانبردار ہیں'),
(378, 3, 85, 'اور جو کوئی اسلام کے سوا اور کوئی دین چاہے تو وہ اس سے ہر گز قبول نہیں کیا جائے گا اور وہ آخرت میں نقصان اٹھانے والوں میں سے ہوگا'),
(379, 3, 86, 'الله ایسے لوگوں کو کیونکر راہ دکھائے جو ایمان لانے کے بعد کافر ہوگئے اور گواہی دے چکے ہیں کہ بے شک یہ رسول سچا ہے اور ان کے پاس روشن نشانیاں آئی ہیں اور الله ظالموں کو راہ نہیں دکھاتا'),
(380, 3, 87, 'ایسے لوگوں کی یہ سزا ہے کہ ان پر الله اور فرشتوں اور سب لوگو ں کی لعنت ہو'),
(381, 3, 88, 'اس میں ہمیشہ رہنے والے ہوں گے ان سے عذاب ہلکا نہ کیا جائے گا اور نہ وہ مہلت دیے جائیں گے'),
(382, 3, 89, 'مگر جنہوں نے اس کے بعد توبہ کی اور نیک کام کیے تو بے شک الله بخشنے والا مہربان ہے'),
(383, 3, 90, 'بے شک جو لوگ ایمان لانے کے بعد منکر ہو گئے پھر انکار میں بڑھتے گئے ان کی توبہ ہر گز قبول نہیں کی جائے گی اور وہی گمراہ ہیں'),
(384, 3, 91, 'بے شک جو لوگ کافر ہوئے اور کفر کی حالت میں مر گئے تو کسی ایسے سے زمین بھر کر سونا بھی قبول نہیں کیا جائے گا اگرچہ وہ اس قدر سونا بدلے میں دے ان لوگوں کے لیے دردناک عذاب ہے اور ان کا کوئی مددگار نہیں ہوگا'),
(385, 3, 92, 'ہر گز نیکی میں کمال حاصل نہ کر سکو گے یہاں تک کہ اپنی پیاری چیز سے کچھ خرچ کرو اور جو چیز تم خرچ کرو گے بے شک الله اسے جاننے والا ہے'),
(386, 3, 93, 'بنی اسرائیل کے لیے سب کھانے کی چیزیں حلال تھیں مگر وہ چیز جو اسرائیل نے تورات نازل ہونے سے پہلےاپنے اوپر حرام کی تھی کہہ دو تورات لاؤ اور اسے پڑھو اگر تم سچے ہو'),
(387, 3, 94, 'پھر جس شخص نے اس کے بعد الله پر جھوٹ بنایا وہی بڑے بے انصاف ہیں'),
(388, 3, 95, 'کہہ دو الله نے سچ فرمایا ہے اب ابراھیم کے دین کے تابع ہو جاؤ جوایک ہی کےہو گئے تھے اور مشرکوں میں سے نہ تھے'),
(389, 3, 96, 'بے شک لوگوں کے واسطے جو سب سے پہلا گھر مقرر ہوا یہی ہے جو مکہ میں برکت والا ہے اور جہان کے لوگوں کے لیے راہ نما ہے'),
(390, 3, 97, 'اس میں ظاہر نشانیاں ہیں مقام ابراھیم ہے اور جو اس میں داخل ہو جائے وہ امن والا ہو جاتا ہے اور لوگوں پر اس گھر کا حج کرنا الله کا حق ہے جو شخص اس تک پہنچنے کی طاقت رکھتا ہو اور جو انکار کرے تو پھر الله جہان والوں سے بے پرواہ ہے'),
(391, 3, 98, 'کہہ دواے اہلِ کتاب الله کیا آیتوں کا کیوں انکار کرتے ہو اور جو کچھ تم کرتے ہو الله اس پر گواہ ہے'),
(392, 3, 99, 'کہہ دواے اہلِ کتاب الله کی راہ سے کیوں روکتے ہو اس شخص کو جو ایمان لائے اس میں عیب ڈھونڈتے ہو اورتم خود جانتے ہو اور تمہارے کام سے الله بے خبر نہیں ہے'),
(393, 3, 100, 'اے ایمان والو اگرتم اہلِ کتاب کی کسی جماعت کابھی کہا مانو گےتو وہ تمہیں ایمان لانے کے بعد کافر کر دیں گے'),
(394, 3, 101, 'اور تم کس طرح کافر ہو گے حالانکہ تم پر الله کی آیتیں پڑھی جاتی ہیں اور اس کا رسول تم میں موجود ہے اور جو شخص الله کو مضبوط پکڑے گا تو اسے ہی سیدھے راستے کی ہدایت کی جائے گی'),
(395, 3, 102, 'اے ایمان والو الله سے ڈرتے رہو جیسا اس سے ڈرنا چاہیئے اور نہ مرد مگر ایسے حال میں کہ تم مسملمان ہو'),
(396, 3, 103, 'اور سب مل کر الله کی رسی مضبوط پکڑو اور پھوٹ نہ ڈالو اور الله کا احسان اپنے اوپر یاد کرو جب کہ تم آپس میں دشمن تھے پھر تمہارے دلوں میں الفت ڈال دی پھر تم اس کے فضل سے بھائی بھائی ہو گئے اور تم آگ کے گڑھے کے کنارے پر تھے پھر تم کو اس سے نجات دی اس طرح تم پر الله اپنی نشانیاں بیان کرتا ہے تاکہ تم ہدایت پاؤ'),
(397, 3, 104, 'اور چاہیئے کہ تم میں سے ایک جماعت ایسی ہو جو نیک کام کی طرف بلاتی رہے اوراچھے کاموں کا حکم کرتی رہے اور برے کاموں سے روکتی رہے اور وہی لوگ نجات پانے والے ہیں'),
(398, 3, 105, 'ان لوگو ں کی طرح مت ہو جو متفرق ہو گئے بعد اس کے کہ ان کے پاس واضح احکام آئے انہوں نے اختلاف کیا اور ان کے لیے بڑا عذاب ہے'),
(399, 3, 106, 'جس دن بعضے منہ سفید اور بعضے منہ سیاہ ہوں گے سو وہ جن کے منہ سیاہ ہوں گے ان سے کہا جائے گا کیا تم ایمان لا کر کافر ہو گئے تھے اب اس کفر کرنےکے بدلے میں عذاب چکھو'),
(400, 3, 107, 'اور وہ لوگ جن کے منہ سفید ہوں گے تو وہ الله کی رحمت میں ہوں گے وہ اس میں ہمیشہ رہیں گے'),
(401, 3, 108, 'یہ الله کے احکام ہیں ہم تمہیں ٹھیک ٹھیک سناتے ہیں اور الله مخلوقات پر ظلم نہیں کرنا چاہتا'),
(402, 3, 109, 'اور جو کچھ آسمانوں اور جو کچھ زمین میں ہے سب الله ہی کا ہے اور سب کام الله ہی کے طرف پھیرے جاتے ہیں'),
(403, 3, 110, 'تم سب امتوں میں سے بہتر ہو جو لوگوں کے لیے بھیجی گئی ہیں اچھے کاموں کا حکم کرتے رہو اور برے کاموں سے روکتے رہو اور الله پر ایمان لاتے ہو اور اگر اہل کتاب ایمان لے آتے تو ان کے لیے بہتر تھا کچھ ان میں سے ایماندار ہیں اور اکثر ان میں سے نافرمان ہیں'),
(404, 3, 111, 'وہ زبان سے ستانے کے سوا تمہارا اور کچھ بگاڑ نہ سکیں گے اور اگر تم سے لڑیں گے تو پیٹھ پھیر دیں گے پھر مدد نہیں دیے جائیں گے'),
(405, 3, 112, 'ان پر ذلت لازم کی گئی ہے جہاں وہ پائے جائیں گے مگر ساتھ الله کی پناہ کے اور لوگو ں کی پناہ کے اور وہ الله کے غضب کے مستحق ہوئے اور ان پر پستی لازم کی گئی یہ اس واسطے ہے کہ الله کی نشانیوں کے ساتھ کفر کرتے تھے اور پیغمبروں کو ناحق قتل کرتے تھے یہ اس سبب سے ہے کہ انہوں نے نافرمانی کی اور حد سے نکل جاتے تھے'),
(406, 3, 113, 'وہ سب برابر نہیں اہل کتاب میں سے ایک فرقہ سیدھی راہ پر ہے وہ رات کے وقت الله کی آیتیں پڑھتے ہیں اور وہ سجدے کرتے ہیں'),
(407, 3, 114, 'الله اور قیامت کے دن پر ایمان لاتے ہیں اور اچھی بات کا حکم کرتے ہیں اور برے کاموں سے روکتے ہیں اور نیک کاموں میں دوڑتے ہیں اور وہی لوگ نیک بخت ہیں'),
(408, 3, 115, 'وہ لوگ جو نیک کام کریں گے اس سے محروم نہ کیے جائیں گے اور الله پرہیزگارو ں کا جاننے والا ہے'),
(409, 3, 116, 'بے شک جو لوگ کافر ہیں ان کے مال اور اولاد الله کے مقابلے میں کچھ کام نہ آئیں گے اوروہی لوگ دوزخی ہیں وہ اس آگ میں ہمیشہ رہنے والے ہیں'),
(410, 3, 117, 'اس دنیا کی زندگی میں جو کچھ خرچ کرتے ہیں اس کی مثال ایسی ہے جس طرح ایک ہوا ہو جس میں تیز سردی ہو وہ ایسے لوگو ں کی کھیتی کو لگ جائے جنہوں نے اپنے نفسوں پر ظلم کیا تھا پھر ا سکو برباد کر گئی اور الله نے ان پر ظلم نہیں کیا لیکن وہ اپنے او پر ظلم کرتے ہیں'),
(411, 3, 118, 'اے ایمان والو اپنوں کے سوا کسی کو بھیدی نہ بناؤ وہ تمہاری خرابی میں قصور نہیں کرتے جو چیز تمہیں تکلیف دے وہ انہیں پسند آتی ہے ان کے مونہوں سے دشمنی نکل پڑتی ہے اور جو ان کے سینے میں چپھی ہوئي ہے وہ بہت زیادہ ہے ہم نے تمہارے لیے نشانیاں بیان کر دی ہیں اگر تم عقل رکھتے ہو'),
(412, 3, 119, 'سن لو تم ان کے دوست ہو اور وہ تمہارے دوست نہیں اور تم تو سب کتابوں کو مانتے ہو اور جب وہ تم سے ملتے ہیں تو کہتے ہیں کہ ہم مسلمان ہیں اور جب الگ ہوتے ہیں تو تم پر غصہ سے انگلیاں کاٹ کاٹ کھاتے ہیں کہہ دو تم اپنے غصہ میں مرو الله کو دلوں کی باتیں خوب معلوم ہیں'),
(413, 3, 120, 'اگر تمہیں کوئی بھلائی پہنچے توانہیں بری لگتی ہے اور اگر تمہیں کوئی تکلیف پہنچے تو اس سے خوش ہوتے ہیں اور اگر تم صبر کرو اور پرہیزگاری کرو تو ان کے فریب سے تمہارا کچھ نہ بگڑے گا بے شک الله ان کے اعمال پر احاطہ کرنے والا ہے'),
(414, 3, 121, 'اور جب تو صبح کو اپنے گھر سے نکلا مسلمانوں کو لڑائی کا ٹھکانے پر بٹھا رہا تھا اور الله سننے والا جاننے والا ہے'),
(415, 3, 122, 'جب تم میں سے دو جماعتوں نے قصد کیا کہ نامردی کریں اور الله ا ن کا مددگار تھا اور چاہیئے کہ الله ہی پر مسلمان بھروسہ کریں'),
(416, 3, 123, 'اور الله بدر کی لڑائی میں تمہاری مدد کر چکا ہے حالانکہ تم کمزور تھے پس الله سے ڈرو تاکہ تم شکر کرو'),
(417, 3, 124, 'جب تو مسلمانوں کو کہتا تھا کیا تمہیں یہ کافی نہیں کہ تمہارا رب تمہاری مدد کے لیے تین ہزار فرشتے آسمان سے اترنے والے بھیجے'),
(418, 3, 125, 'بلکہ اگر تم صبر کرو اور پرہیزگاری کرو اور وہ تم پر ایک دم سے آ پہنچیں تو تمہارا رب پانچ ہزار فرشتے نشان دار گھوڑوں پر مدد کے لیے بھیجے گا'),
(419, 3, 126, 'اوراس چیز کواللہ نے تمہارے دل کی خوشی کے لیے کیا ہے اور تاکہ تمہارے دلوں کو اس سے اطمینان ہو اورمدد تو صرف الله ہی کی طرف سے ہے جو زبردست حکمت والا ہے'),
(420, 3, 127, 'تاکہ بعض کافروں کو ہلاک کرے یا انہیں ذلیل کرے پھر وہ ناکام ہو کر لوٹ جائیں'),
(421, 3, 128, 'تیرا کوئی اختیار نہیں ہے یا الله انہیں توبہ کی توفیق دے یاانہیں عذاب کرے کیوں کہ وہ ظالم ہیں'),
(422, 3, 129, 'اور جو کچھ آسمانو ں میں اور جو کچھ زمین میں ہے سب الله ہی کا ہے جسے چاہے بخش دے اور جسے چاہے عذاب کرے اور الله بخشنے والا مہربان ہے'),
(423, 3, 130, 'اے ایمان والو سود دونے پر دونا نہ کھاؤ اور الله سے ڈرو تاکہ تمہارا چھٹکارا ہو'),
(424, 3, 131, 'اوراس آگ سے بچو جو کافروں کے لیے تیار کی گئی ہے'),
(425, 3, 132, 'اور الله اور رسول کی تابعداری کرو تاکہ تم پر رحم کیے جاؤ'),
(426, 3, 133, 'اور اپنے رب کی بخشش کی طرف دوڑو اوربہشت کی طرف جس کا عرض آسمان اور زمین ہے جوپرہیزگاروں کے لیے تیار کی گئی ہے'),
(427, 3, 134, 'جو خوشی اور تکلیف میں خرچ کرتے ہیں اور غصہ ضبط کرنے والے ہیں اور لوگوں کو معاف کرنے والے ہیں اور الله نیکی کرنے والوں کو دوست رکھتا ہے'),
(428, 3, 135, 'اور وہ لوگ جب کوئی کھلا گناہ کر بیٹھیں یا اپنے حق میں ظلم کریں تو الله کو یاد کرتے ہیں اور اپنے گناہوں سے بخشش مانگتے ہیں اور سوائے الله کے اور کون گناہ بخشنے والا ہے اور اپنے کیے پر وہ اڑتے نہیں اور وہ جانتے ہیں'),
(429, 3, 136, 'یہ لوگ ان کا بدلہ ان کے رب کے ہاں سے بخشش ہے اور باغ ہیں جن کے نیچے نہریں بہتی ہوں گی ان باغوں میں ہمیشہ رہنے والے ہوں گے اور کام کرنے والوں کی کیسی اچھی مزدوری ہے'),
(430, 3, 137, 'تم سے پہلے کئی واقعات ہو چکے ہیں سو زمین میں سیر کرو اور دیکھو کہ جھٹلانے والوں کا کیا انجام ہوا'),
(431, 3, 138, 'یہ لوگوں کے واسطے بیان ہے اور ڈرنے والوں کے لیے ہدایت اور نصیحت ہے'),
(432, 3, 139, 'اورسست نہ ہو اور غم نہ کھاؤ اور تم ہی غالب رہو گے'),
(433, 3, 140, 'اگر تمہیں زخم پہنچا ہےتو انہیں بھی ایسا ہی زخم پہنچ چکا ہے اور ہم یہ دن لوگوں میں باری باری بدلتے رہتے ہیں اور تاکہ الله ایمان والوں کو جان لے اور تم میں سے بعضوں کو شہید کرے اور الله ظالموں کو پسند نہیں کرتا'),
(434, 3, 141, 'اور تاکہ الله ایمان والوں کو پاک کردے اور کافروں کو مٹا دے'),
(435, 3, 142, 'کیا تم یہ خیال کرتے ہو کہ جنت میں داخل ہو جاؤ گے اور ابھی تک الله نے نہیں ظاہرکیا ان لوگوں کو جو تم میں سے جہاد کرنے والے ہیں اور ابھی صبر کرنے والوں کو بھی ظاہر نہیں کیا'),
(436, 3, 143, 'اور تم موت سے پہلے اس کی ملاقات کی آرزو کرتے تھے سو اب تم نے اسے آنکھوں کے سامنہ دیکھ لیا'),
(437, 3, 144, 'اور محمد تو ایک رسول ہے اس سے پہلے بہت رسول گزرے پھرکیا اگر وہ مرجائے یا مارا جائے تو تم الٹے پاؤں پھر جاؤ گے اور جو کوئی الٹے پاؤں پھر جائے گا تو الله کا کچھ بھی نہیں بگاڑے گا اور الله شکر گزاروں کو ثواب دے گا'),
(438, 3, 145, 'اور الله کے حکم کے سوا کوئی مر نہیں سکتا ایک وقت مقرر لکھا ہوا ہے اور جو شخص دنیا کا بدلہ چاہے گا ہم اسے دنیا ہی میں دے دیں گے اور جو آخرت کا بدلہ چاہے گا ہم اسے دنیاہی میں دے دینگے اور ہم شکر گزاروں کو جزا دیں گے'),
(439, 3, 146, 'اور کئی نبی ہیں جن کے ساتھ ہو کر بہت الله والے لڑے ہیں پھر الله کی راہ میں تکلیف پہنچنے پر نہ ہارے ہیں اور نہ سست ہوئے اور نہ وہ دبے ہیں اور الله ثابت قدم رہنے والوں کو پسند کرتا ہے'),
(440, 3, 147, 'اور انہوں نے سوائے اس کے کچھ نہیں کہا کہ اے ہمارے رب ہمارے گناہ بخش دے اور جو ہمارے کام میں ہم سے زیادتی ہوئی ہے او رہمارے قدم ثابت رکھ اور کافروں کی قوم پر ہمیں مدد دے'),
(441, 3, 148, 'پھر الله نے ان کو دنیا کا ثواب اور آخرت کا عمدہ بدلہ دیا اور الله نیک کاموں کو پسند کرتا ہے'),
(442, 3, 149, 'اے ایمان والو اگر تم کافروں کا کہا مانو گے تو وہ تمہیں الٹے پاؤں پھیر دیں گے پھر تم نقصان میں جا پڑو گے'),
(443, 3, 150, 'بلکہ الله تمہارا مددگار ہے اور وہ بہترین مدد کرنے والا ہے'),
(444, 3, 151, 'اب ہم کافروں کے دلوں میں ہیبت ڈال دیں گے اس لیے کہ انہوں نے الله کا شریک ٹھیرایا جس کی اس نے کوئی دلیل نہیں اتاری اور ان کا ٹھکانا دوزخ ہے اور ظالموں کا وہ بہت بڑاٹھکانا ہے'),
(445, 3, 152, 'اور الله تو اپنا وعدہ تم سے سچا کر چکا جب تم اس کے حکم سے انہیں قتل کرنے لگے یہاں تک کہ جب تم نے نامردی کی اور کام میں جھگڑا ڈالا اور نافرمانی کی بعد اس کے کہ تم کو دکھا دی وہ چیز جسے تم پسند کرتے تھے بعض تم میں سے دنیا چاہتے تھے اور بعض تم میں سے آخرت کے طالب تھےپھر تمہیں ان سے پھیر دیا تاکہ تمہیں آزمائے اورالبتہ تحقیق تمہیں اس نے معاف کردیا ہے اور اللہ ایمانداروں پر فضل والا ہے'),
(446, 3, 153, 'جس وقت تم چڑھے جاتے تھے اور کسی کو مڑ کر نہ دیکھتے تھے اور رسول تمہیں تمہارےپیچھے سے پکار رہا تھا سو الله نے تمہیں اس کی پاداش میں غم دیا بسبب غم دینے کے تاکہ تم مغموم نہ ہو اس پر جو ہاتھ سے نکل گئی اور نہ اس پر جو تمہیں پیش آئی اور الله خبردار ہے اس چیز سے جو تم کرتے ہو'),
(447, 3, 154, 'پھر الله نے اس غم کے بعد تم پر چین یعنی اونگھ بھیجی اس نے بعضوں کو تم میں سے ڈھانک لیا اور بعضوں کو اپنی جان کا فکر لڑ رہا تھا الله پر جھوٹے خیال جاہلوں جیسے کر رہے تھے کہتے تھے ہمارے ہاتھ میں کچھ کام ہے کہہ دو کہ سب کام الله کے ہاتھ میں ہے وہ اپنے دل میں چھپاتے ہیں جو تیرے سامنے ظاہر نہیں کرتے کہتے ہیں اگر ہمارے ہاتھ میں کچھ کام ہوتا تو ہم اس جگہ مارے نہ جاتے کہہ دو اگر تم اپنے گھروں میں ہوتے البتہ اپنے گرنے کی جگہ پرباہر نکل آتے وہ لوگ جن پر قتل ہونا لکھا جا چکا تھا اور تاکہ الله آزمائے جو تمہارے سینوں میں ہے اور تاکہ اس چیز کو صاف کردے جو تمہارے دلوں میں ہے اور الله دلوں کے بھید جاننے والا ہے'),
(448, 3, 155, 'بے شک وہ لوگ جو تم میں پیٹھ پھیر گئے جس دن دونوں فوجیں ملیں سو شیطان نے ان کے گناہ کے سبب سے انہیں بہکا دیا تھا اور الله نے ان کو معاف کر دیا ہے بے شک الله بخشنے والا تحمل کرنے والا ہے'),
(449, 3, 156, 'اے ایمان والو تم ان لوگو ں کی طرح نہ ہو جو کافر ہوئے اور وہ اپنے بھائیوں سے کہتے ہیں جب وہ ملک میں سفر پر نکلیں یا جہاد پر جائیں اگر ہمارے پاس رہتے تو نہ مرتے اور نہ مارے جاتے تاکہ الله اس خیال سے ان کے دلوں میں افسوس ڈالے اور الله ہی جلاتا اور مارتا ہے اور الله تمہارے سب کاموں کو دیکھنے والا ہے'),
(450, 3, 157, 'اور اگر تم الله کی راہ میں مارے گئے یا مر گئے تو الله کی بخشش اور اس کی مہربانی اس چیز سے بہتر ہے جو وہ جمع کرتے ہیں'),
(451, 3, 158, 'اوراگرتم مرگئے یا مارے گئے تو البتہ تم سب الله ہی کے ہاں جمع کیے جاؤ گے'),
(452, 3, 159, 'پھر الله کی رحمت کے سبب سے تو ان کے لیے نرم ہو گیا اور اگرتو تند خو اور سخت دل ہوتا تو البتہ تیرے گرد سے بھاگ جاتے پس انہیں معاف کردے اور ان کے واسطے بخشش مانگ او رکام میں ان سے مشورہ لیا کر پھر جب تو اس کام کا ارادہ کر چکا تو الله پر بھروسہ کر بے شک الله توکل کرنے والے لوگوں کو پسند کرتا ہے'),
(453, 3, 160, 'ااگر الله تمہاری مدد کرے گا تو تم پر کوئی غالب نہ ہوسکے گا اور اگر اس نے مدد چھوڑ دی تو پھر ایسا کون ہے جو اس کے بعد تمہاری مدد کر سکے اور مسلمانوں کو الله ہی پر بھروسہ کرنا چاہیے'),
(454, 3, 161, 'اور کسی نبی کو یہ لائق نہیں کہ خیانت کرے گااور جو کوئی خیانت کرے گا اس چیز کو قیامت کے دن لائے گا جو خیانت کی تھی پھر ہر کوئی پورا پالے گا جو اس نے کمایا تھا اور وہ ظلم نہیں کیے جائیں گے'),
(455, 3, 162, 'آیا وہ شخص جو الله کی رضا کا تابع ہے اس کے برابر ہو سکتا ہے جو غضب الہیٰ کا مستحق ہوا اور اس کا ٹھکانہ دوزخ ہے اور کیسی وہ بری جگہ ہے'),
(456, 3, 163, 'الله کے ہاں لوگوں کے مختلف درجے ہیں اور الله دیکھتا ہے جو کچھ وہ کرتے ہیں'),
(457, 3, 164, 'الله نے ایمان والوں پر احسان کیا ہے جو ان میں انہیں میں سے رسول بھیجا ان پر اس کی آیتیں پڑھتا ہے اور انہیں پاک کرتا ہے اور انہیں کتاب اور دانش سکھاتا ہے اگرچہ وہ اس سے پہلے صریح گمراہی میں تھے'),
(458, 3, 165, 'کیا جب تمہیں ایک تکلیف پہنچی حالانکہ تم تو اس سے دو چند تکلیف پہنچا چکے ہو تو کہتے ہو یہ کہاں سے آئی کہہ دو یہ تکلیف تمہیں تمہاری طرف سے پہنچی ہے بے شک الله ہر چیز پر قادر ہے'),
(459, 3, 166, 'اور جو کچھ تمہیں اس دن پیش آیا جس دن دونوں جماعتیں ملیں سو الله کے حکم سے ہوا او رتاکہ الله ایمان دارو ں کو ظاہر کر دے'),
(460, 3, 167, 'اور تاکہ منافقوں کو ظاہر کر دے اور انہیں کہا گیا تھا کہ آؤ الله کی راہ میں لڑو یا دشمنوں کو دفع کرو تو انہوں نے کہا اگر ہمیں علم ہوتا کہ آج جنگ ہو گی تو ہم ضرور تمہارے ساتھ چلتے وہ اس وقت بہ نسبت ایمان کے کفر سے زیادہ قریب تھے وہ اپنے مونہوں سے وہ باتیں کہتے ہیں جو ان کے دلو ں میں نہیں ہیں اور جو کچھ وہ چھپاتے ہیں الله اس کو خوب جانتا ہے'),
(461, 3, 168, 'یہ وہ لوگ ہیں جو اپنے بھائیوں سے کہتے ہیں حالانکہ خود بیٹھ رہے تھے اگر وہ ہماری بات مانتے تو قتل نہ کیے جاتے کہہ دو اگر تم سچے ہو تو اپنی جانوں سے موت کو ہٹا دو'),
(462, 3, 169, 'اور جو لوگ الله کی راہ میں مارے گئے ہیں انہیں مردہ نہ سمجھو بلکہ وہ زندہ ہیں اپنے رب کے ہاں سے رزق دیے جاتے ہیں'),
(463, 3, 170, 'الله نے اپنے فضل سے جو انہیں دیا ہے اس پر خوش ہونے والے ہیں اور ان کی طرف سے بھی خوش ہوتے ہیں جو ابھی تک ان کے پیچھے سے ان کے پاس نہیں پہنچے اس لئے کہ نہ ان پر خوف ہے او رنہ وہ غم کھائیں گے'),
(464, 3, 171, 'الله کی نعمت اور فضل سے خوش ہوتے ہیں اوراس بات سے کہ الله ایمانداروں کی مزدوری کو ضائع نہیں کرتا'),
(465, 3, 172, 'جن لوگو ں نے الله اور اس کے رسول کا حکم مانا بعد اس کے کہ انہیں زخم پہنچ چکے تھے جو ان میں سے نیک ہیں اور پرہیزگارہوئے ان کے لیے بڑا اجر ہے'),
(466, 3, 173, 'جنہیں لوگوں نے کہا کہ مکہ والوں نے تمہارے مقابلے کے لیے سامان جمع کیا ہے سوتم ان سے ڈرو تو ان کا ایمان اور زیادہ ہوا اور کہا کہ ہمیں الله کافی ہے اور وہ بہترین کارساز ہے'),
(467, 3, 174, 'پھر مسلمان الله کی نعمت اور فضل کے ساتھ لوٹ آئے انہیں کوئی تکلیف نہ پہنچی اور الله کی مرضی کے تابع ہوئے اور الله بڑے فضل والا ہے'),
(468, 3, 175, 'سویہ شیطان ہے کہ اپنے دوستوں سے ڈراتا ہے پس تم ان سے مت ڈرو اورمجھ سے ڈرو اگر تم ایمان دار ہو'),
(469, 3, 176, 'اور وہ لوگ آپ ے غم میں نہ ڈال دیں جو کفر کی طرف دوڑتے ہیں وہ الله کا کچھ نہیں بگاڑیں گے الله ارادہ کرتا ہے کہ آخرت میں انہیں کوئی حصہ نہ دے اوران کے لیے بڑا عذاب ہے'),
(470, 3, 177, 'جنہوں نے ایمان کے بدلے کفر خرید لیا وہ الله کا کچھ نہیں بگاڑیں گے اور ان کے لیے دردناک عذاب ہے'),
(471, 3, 178, 'اور کافر یہ نہ سمجھیں کہ ہم جو انہیں مہلت دیتے ہیں یہ ان کے حق میں بھلائی ہے ہم انہیں مہلت اس لیے دیتے ہیں کہ وہ گناہ میں زیادتی کریں اور ان کے لیے خوار کرنے والا عذاب ہے'),
(472, 3, 179, 'الله مسلمانوں کو اس حالت پر رکھنا نہیں چاہتا جس پر اب تم ہو جب تک کہ ناپاک کو پاک سے جدا نہ کر دے اورالله کا یہ طریقہ نہیں ہے کہ تمہیں غیب پر مطلع کر دے لیکن الله اپنے رسولوں میں جسے چاہے چن لیتا ہے سو تم الله اور اس کے رسول پر ایمان لاؤ اور پرہیزگاری کرو تو تمہارے لیے بہت بڑا اجر ہے'),
(473, 3, 180, 'اور جو لوگ اس چیز پر بخل کرتے ہیں جو الله نے ان کو اپنے فضل سے دی ہے وہ یہ خیال نہ کریں کہ بخل ان کے حق میں بہتر ہے بلکہ یہ ان کے حق میں براہے قیامت کے دن وہ مال طوق بناا کر ان کے گلوں میں ڈالا جائے گا جس میں وہ بخل کرتے تھے اور الله ہی آسمانوں اور زمین کا وارث ہے اور جو کچھ تم کرتے ہو الله اسے جانتا ہے'),
(474, 3, 181, 'بے شک الله نے ان کی بات سنی ہے جنہوں نے کہا کہ بے شک الله فقیر ہے اور ہم دولت مند ہیں اب ہم ان کی بات لکھ رکھیں گے اور جو انہوں نے انبیاء کے ناحق خون کیے ہیں اور کہیں گے کہ جلتی آگ کا عذاب چکھو'),
(475, 3, 182, 'یہ اس چیز کے بدلےہے جو تمہارے ہاتھوں نے آگے بھیجا اور الله بندوں پر ظلم نہیں کرتا'),
(476, 3, 183, 'وہ لوگ جو کہتے ہیں کہ الله نے ہمیں حکم فرمایا تھا کہ ہم کسی پیغمبر پر ایمان نہ لائیں یہاں تک کہ وہ ہمارے پاس قربانی لائے کہ اسے آگ کھا جائے کہہ دو مجھ سے پہلے کتنے رسول نشانیاں لے کر تمہارے پاس آئے اور یہ نشانی بھی جو تم کہتے ہو پھر انہیں تم نے کیوں قتل کیا اگر تم سچے ہو'),
(477, 3, 184, 'پھر اگر یہ تجھے جھٹلائیں تو تجھ سے پہلے بہت سے رسول جھٹلائے گئے جو نشانیاں اور صحیفے اور روشن کتاب لائے'),
(478, 3, 185, 'ہر جان موت کا مزہ چکھنے والی ہے اور تمہیں قیامت کے دن پورے پورے بدلے ملیں گے پھر جو کوئی دوزخ سے دور رکھا گیا اور بہشت میں داخل کیا گیا سو وہ پورا کامیاب ہوا اور دنیا کی زندگی سوائے دھوکے کی پونجی کے اور کچھ نہیں'),
(479, 3, 186, 'البتہ تم اپنے مالوں اور جانوں میں آزمائے جاؤ گے اور البتہ پہلی کتاب والوں اور مشرکوں سے تم بہت بدگوئی سنو گے اور اگر تم نے صبر کیا اور پرہیزگاری کی تو یہ ہمت کے کاموں میں سے ہے'),
(480, 3, 187, 'اور جب الله نے اہلِ کتاب سے یہ عہد لیا کہ اسے لوگوں سے ضرور بیان کرو گے اورنہ چھپاؤ گے انہوں نے وہ عہد اپنی پیٹھ کے پیچھے پھینک دیا اور اس کے بدلے میں تھوڑا سا مول خرید کیا سو کیا ہی برا ہے جو وہ خریدتے ہیں'),
(481, 3, 188, 'مت گمان کر ان لوگوں کو جو خوش ہوتے ہیں جو کرتے ہیں اور چاہتے ہیں کہ اس چیز کے ساتھ تعریف کیے جائیں جو انہوں نے کی پس ہر گز تو انہیں عذاب سے خلاصی پانے والا خیال نہ کر اور ان کے لیے دردناک عذاب ہے'),
(482, 3, 189, 'اور آسمانوں اور زمین کی بادشاہی الله ہی کے واسطے ہے اور الله ہر چیز پر قادر ہے'),
(483, 3, 190, 'بے شک آسمان اور زمین کے بنانے اور رات اور دن کے آنے جانے میں البتہ عقلمندوں کے لیے نشانیاں ہیں'),
(484, 3, 191, 'وہ جو الله کو کھڑے اور بیٹھے اور کروٹ پر لیٹے یاد کرتے ہیں اور آسمان اور زمین کی پیدائش میں فکر کرتے ہیں (کہتے ہیں) اے ہمارے رب تو نے یہ بےفائدہ نہیں بنایا توسب عیبوں سے پاک ہے سو ہمیں دوزح کے عذاب سے بچا'),
(485, 3, 192, 'اے رب ہمارے جسے تو نے دوزخ میں داخل کیا سو تو نے اے رسوا کیا اور ظالموں کا کوئی مددگار نہیں ہوگا'),
(486, 3, 193, 'ہم نے ایک پکارنے والے سے سنا جو ایمان لانے کو پکارتا تھا کہ اپنے رب پر ایمان لاؤ سو ہم ایمان لائے اے رب ہمارے اب ہمارے گناہ بخش دے اور ہم سے ہمارا برائیاں دو رکردے اور ہمیں نیک لوگو ں کے ساتھ موت دے'),
(487, 3, 194, 'اے رب ہمارے او رہمیں دے جو تو نےہم سے اپنے رسولوں کے ذریعے سے وعدہ کیا ہے اور ہمیں قیامت کے دن رسوا نہ کر بے شک تو وعدہ کے خلاف نہیں کرتا'),
(488, 3, 195, 'پھر ان کے رب نے ان کی دعا قبول کی کہ میں تم میں سے کسی کام کرنے والے کا کام ضائع نہیں کرتا خواہ مرد ہو یا عورت تم آپس میں ایک دوسرے کے جز ہو پھر جن لوگوں نے وطن چھوڑا اور اپنے گھروں سے نکالے گئے اور میری راہ میں ستائے گئے او رلڑے اور مارے گئے البتہ میں ان سے ان کی برائیاں دور کروں گا اور انہیں باغوں میں داخل کروں گا جن کے نیچے نہریں بہتی ہوں گی یہ الله کے ہاں سے بدلہ ہے'),
(489, 3, 196, 'تجھ کو کافروں کا شہرو ں میں چلنا پھرنا دھوکہ نہ دے'),
(490, 3, 197, 'یہ تھوڑا سا فائدہ ہے پھر ان کا ٹھکانا دوزخ ہے اوروہ بہت برا ٹھکاناہے'),
(491, 3, 198, 'ے لیکن جو لوگ اپنے رب سے ڈرتے رہے ان کے لیے باغ ہیں جن کے نیچے نہریں بہتی ہیں ان میں وہ ہمیشہ رہیں گے یہ الله کے ہاں مہمانی ہے اور جو الله کے ہاں ہے وہ نیک بندوں کے لیے بدرجہا بہتر ہے'),
(492, 3, 199, 'اور اہل کتاب میں بعض ایسے بھی ہیں جو الله پر ایمان لاتے ہیں اور جو چیز تمہاری طرف نازل کی گئی اور جو ان کی طرف نازل کی گئی الله کے سامنے عاجزی کرنے والے ہیں الله کی آیتوں پر تھوڑا مول نہیں لیتے یہی ہیں جن کے لیے ان کے رب کے ہاں مزدوری ہے بے شک الله جلد حساب لینے والا ہے'),
(493, 3, 200, 'اے ایمان والو صبر کرو اور مقابلہ کے وقت مضبوط رہو اور لگے رہو اور الله سے ڈرتے رہو تاکہ تم نجات پاؤ');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'اے لوگو اپنے رب سے ڈرو جس نے تمہیں ایک جان سے پیدا کیا اور اسی جان سے اس کا جوڑا بنایا اور ان دونوں سے بہت سے مرد اور عورتیں پھیلائیں اس اللہ سے ڈرو جس کا واسطہ دے کر تم ایک دوسرے اپنا حق مانگتے ہو اور رشتہ داری کے تعلقات کو بگاڑنے سے بچو بے شک الله تم پر نگرانی کر رہا ہے'),
(495, 4, 2, 'اوریتیموں کوان کے مال دے دو اور ناپاک کو پاک سے نہ بدلو اور ان کے مال اپنے مال کے ساتھ ملا کر نہ کھا جاؤ یہ بڑا گناہ ہے'),
(496, 4, 3, 'اور اگر تم یتیم لڑکیوں سے بے انصافی کرنے سے ڈرتے ہوتوجوعورتیں تمہیں پسند آئیں ان میں سے دو دو تین تین چار چار سے نکاح کر لو اگر تمہیں خطرہ ہو کہ انصاف نہ کر سکو گے تو پھر ایک ہی سے نکاح کرو جو لونڈی تمہارے ملک میں ہو وہی سہی یہ طریقہ بے انصافی سے بچنے کے لیے زیادہ قریب ہے'),
(497, 4, 4, 'اور عورتوں کو ان کے مہر خوشی سے دے دو پھر اگر وہ اس میں سے اپنی خوشی سے تمہیں کچھ معاف کر دیں تو تم اسے مزہ دار خوشگوار سمجھ کر کھاؤ'),
(498, 4, 5, 'اور اپنے وہ مال جنہیں الله نے تمہاری زندگی کے قیام کا ذریعہ بنایا ہے بے سمجھو کے حوالے نہ کرو البتہ انہیں ان مالوں سے کھلاتے او رپہناتے رہو اور انہیں نصیحت کی بات کہتے رہو'),
(499, 4, 6, 'اور یتیموں کی آزمائش کرتے رہو یہاں تک کہ وہ نکاح کی عمر کو پہنچ جائیں پھر اگر ان میں ہوشیاری دیکھو تو ان کے مال ان کے حوالے کر دو اور انصاف کی حد سے تجاوز کر کے یتیموں کا مال نہ کھا جاؤ اور ان کے بڑے ہونے کے ڈر سے ان کا مال جلدی نہ کھاؤ اور جسے ضرورت نہ ہو تو وہ یتیم کے مال سے بچے او رجو حاجت مند ہو تو مناسب مقدار کھالے پھر جب ان کے مال ان کے حوالے کر و تو اس پر گواہ بنا لو اور حساب لینے کے لیے الله کافی ہے'),
(500, 4, 7, 'مردوں کا اس مال میں حصہ ہے جو ماں باپ اور رشتہ داروں نے چھوڑا ہو اور عورتوں کا بھی اس مال میں حصہ ہے جو ماں باپ اور رشتہ دارو ں نے چھوڑا ہو تھوڑا ہو یہ بہت یہ حصہ مقرر ہے'),
(501, 4, 8, 'اور جب تقسیم کے وقت رشتہ دار اور یتیم اور مسکین آئيں تو اس مال میں سے کچھ انہیں بھی دے دو اور ان کو معقول بات کہہ دو'),
(502, 4, 9, 'اور ایسے لوگو ں کو ڈرنا چاہيے اگر اپنے بعد چھوٹے چھوٹے بچے چھوڑ جائیں جن کی انہیں فکر ہو ان لوگو ں کو چاہیئے کہ خدا سے ڈریں اور سیدھی بات کہیں'),
(503, 4, 10, 'بے شک جو لوگ یتیموں کا مال ناحق کھاتے ہیں وہ اپنے پیٹ آگ سے بھرتے ہیں اور عنقریب آگ میں داخل ہوں گے'),
(504, 4, 11, 'الله تعالیٰ تمہاری اولاد کے حق میں تمہیں حکم دیتا ہے کہ ایک مرد کا حصہ دوعورتوں کے برابر ہے پھر اگر دو سے زاید لڑکیاں ہوں تو ا ن کے لیے دو تہائی اس مال میں سے ہے جو میت نے چھوڑا اور اگر ایک ہی لڑکی ہو تو اس کے لیے آدھا ہے اور اگر میت کی اولاد ہے تو اس کے والدین میں سے ہر ایک کو کل مال کا چھٹا حصہ ملنا چاہیئے اور اگر اس کی کوئی اولاد نہیں اور ماں باپ ہی اس کے وارث ہیں تو اس کی ماں کا ایک تہائی حصہ ہے پھر اگر میت کے بھائی بہن بھی ہوں تو اس کی ماں کا چھٹا حصہ ہے (یہ حصہ اس) وصیت کے بعد ہوگا جو وہ کر گیا تھا اور بعد ادا کرنے قرض کے تم نہیں جانتے تمہارے باپوں اور تمہارے بیٹوں میں سے کون تمہیں زیادہ نفع پہنچانے والا ہے الله کی طرف سے یہ حصہ مقرر کیا ہوا ہے بے شک الله خبردار حکمت والا ہے'),
(505, 4, 12, 'جو مال تمہارحی عورتیں چھوڑ مریں اس میں تمہارا آدھا حصہ ہے بشرطیکہ ان کی اولاد نہ ہو او راگر ان کی اولاد ہو تو اس میں سےجو چھوڑ جائیں ایک چوتھائي تمہاری ہے اس وصیت کے بعد جو وہ کر جائیں یا قرض کے بعد اور عورتوں کے لیے چوتھائی مال ہے جو تم چھوڑ کر مرو بشرطیکہ تمہاری اولاد نہ ہوپس اگر تمہاری اولاد ہو تو جو تم نے چھوڑا اس میں ان کا آٹھواں حصہ ہے اس وصیت کے بعد جو تم کر جاؤ یا قرض کے بعد اوراگر وہ مرد یا عورت جس کی یہ میراث ہے باپ بیٹا کچھ نہیں رکھتا اور اس میت کا ایک بھائي یا بہن ہے تو دونوں میں سے ہر ایک کا چھٹا حصہ ہے پس اگر اس سے زیادہ ہوں تو ایک تہائی میں سب شریک ہیں وصیت کی بات جوہو چکی ہو یا قرض کے بعد بشرطیکہ اورروں کا نقصان نہ ہو یہ الله کا حکم ہے اور الله جاننے والا تحمل کرنے والا ہے'),
(506, 4, 13, 'یہ الله کی باندھی ہوئی حدیں ہیں اور جو شخص الله اور اس کے رسول کے حکم پر چلے اسے بہشتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی ان میں ہمیشہ رہیں گے اور یہی ہے بڑی کامیابی'),
(507, 4, 14, 'اور جو شخص الله اور اس کے رسول کی نافرمانی کرے اوراس کی حدوں سے نکل جائے اسے آگ میں ڈالے گا اس میں ہمیشہ رہے گا اور اس کے لیے ذلت کا عذاب ہے'),
(508, 4, 15, 'اور تمہاری عورتوں میں سے جوکوئی بدکاری کرے ان پر اپنوں میں سے چار مرد گواہ لاؤ پھر اگر وہ گواہی دے دیں تو ان عورتوں کو ان گھروں میں بند رکھو یہاں تک کہ انہیں موت آ جائے یا الله ان کے لیے کوئی راستہ نکال دے'),
(509, 4, 16, 'اور تم میں سے جو دو مرد وہی بدکاری کریں تو ان کو تکلیف دو پھر اگر وہ توبہ کریں اور اپنی اصلاح کرلیں تو انہیں چھوڑ دو بےشک الله توبہ قبول کرنے والا مہربان ہے'),
(510, 4, 17, 'الله پر توبہ قبول کرنے کا حق انہیں لوگو ں کے لیے ہے جو جہالت کی وجہ سے برا کام کرتے ہیں اس کے بعد جلد ہی توبہ کر لیتے ہیں ان لوگو ں کو الله معاف کر دیتا ہے اور الله سب کچھ جاننے والا دانا ہے'),
(511, 4, 18, 'اور ان لوگوں کی توبہ قبول نہیں ہے جو برے کام کرتےرہتے ہیں یہاں تک کہ جب ان میں سے کسی کی موت کا وقت آ جاتا ہے اس وقت کہتا ہے کہ اب میں توبہ کرتا ہوں اوراسی طرح ان لوگو ں کی توبہ قبول نہیں ہے جو کفر کی حالت میں مرتے ہیں ان کے لیے ہم نے دردناک عذاب تیار کیا ہے'),
(512, 4, 19, 'اے ایمان والو! تمہیں یہ حلال نہیں کہ زبردستی عورتوں کو میراث میں لے لو اور ان کو اس واسطے نہ روکے رکھو کہ ان سے کچھ اپنا دیا ہوا مال واپس لے لو ہاں اگر وہ کسی صریح بدچلنی کا ارتکاب کریں اور عورتوں کے ساتھ اچھی طرح سے زندگی بسر کرو اگر وہ تمہیں نا پسند ہوں تو ممکن ہے کہ تمہیں ایک چیز پسند نہ آئے مگر الله نے اس میں بہت کچھ بھلائی رکھی ہو'),
(513, 4, 20, 'اور اگر تم ایک عورت کی جگہ دوسری عورت کو بدلنا چاہو اور ایک کو بہت سا مال دے چکے ہو تو اس میں سے کچھ بھی واپس نہ لو کیا تم اسے بہتان لگا کر اور صریح ظلم کر کے واپس لو گے'),
(514, 4, 21, 'تم اسے کیوں کر لے سکتے ہو جب کہ تم میں سے ہر ایک دوسرے سے لطف اندوز ہو چکا ہے اور وہ عورتیں تم سے پختہ عہد لے چکی ہیں'),
(515, 4, 22, 'ان عورتوں سے نکاح نہ کرو جن سے تمہارے ماں باپ نکاح کر چکے ہیں مگر جو پہلے ہو چکا بے حیائی ہے اور غضب کا کام ہے اور برا چلن ہے'),
(516, 4, 23, 'تم پر تمہاری مائیں اور بیٹیاں اور بہنیں اور پھوپھیاں اور خالائیں اور بھتیجیاں اور بھانجیاں اور جن ماؤں نے تمہیں دودھ پلایا اور تمہاری دودھ شریک بہنیں اور تمہاری عورتوں کی مائیں اور ان کی بیٹیاں جنہوں نے تمہاری گود میں پرورش پائی ہے ان بیویو ں کی لڑکیاں جن سے تمہار تعلق زن و شو ہو چکاہے اور اگر تعلق زن و شو نہ ہوا ہو تو تم پر اس نکاح میں کچھ گناہ نہیں اور تمہارے بیٹو ں کی عورتیں جو تمہاری پشت سے ہیں یہ سب عورتیں تم پر حرام ہیں اور دو بہنوں کو (ایک نکاح میں) اکھٹا کرنا حرام ہے مگر جو پہلے ہو چکا بے شک الله بخشنے والا مہربان ہے'),
(517, 4, 24, 'اور خاوند والی عورتیں مگر تمہارے ہاتھ جن کے مالک ہو جائیں یہ الله کا قانون تم پر لازم ہے اور ان کے سوا تم پرسب عورتیں حلال ہیں بشرطیکہ انہیں اپنے مال کے بدلے میں طلب کرو ایسے حال میں کہ نکاح کرنے والے ہو نہ یہ کہ آزاد شہوت رانی کرنے لگو پھر ان عورتوں میں سے جسے تم کام میں لائے ہو تو ان کے حق جو مقرر ہوئے ہیں وہ انہیں دے دو البتہ مہر کے مقرر ہو جانے کے بعد آپس کی رضا مندی سے باہمی کوئی سمجھوتہ ہو جائے تو اس میں کوئی گناہ نہیں بے شک الله خبردار حکمت والا ہے'),
(518, 4, 25, 'اور جو کوئی تم میں سے اس بات کی طاقت نہ رکھے کہ خاندانی مسلمان عورتیں نکاح میں لائے تو تمہاری ان لونڈیو ں میں سے کسی سے نکاح کر لے جو تمہارے قبضے میں ہوں اور ایماندار بھی ہوں اور الله تمہارے ایمانو ں کا حال خوب جانتا ہے تم آپس میں ایک ہو لہذا ان کے مالکوں کی اجازت سے ان سے نکاح کر لو اور دستور کے موافق ان کے مہر دے دو در آنحالیکہ نکاح میں آنے والیاں ہو ں آزاد شہوت رانیاں کرنے والیاں نہ ہوں اور نہ چھپی یاری کرنے والیاں پھر جب وہ قید نکاح میں آجائیں پھر اگر بے حیائی کا کام کریں تو ان پرآدھی سزاہے جو خاندانی عورتوں پر مقرر کی گئی ہے یہ سہولت اس کیلئے ہے جوکوئی تم سے تکلیف میں پڑنے سے دڑےاور صبر کروتو تمہارےحق میں بہتر ہے اور اللہ بخشنے والا مہربان ہے'),
(519, 4, 26, 'الله چاہتا ہے کہ تمہارے واسطے بیان کرے اور تمہیں پہلوں کی راہ پر چلائے اور تمہاری توبہ قبول کرے اور الله جاننے والا حکمت والا ہے'),
(520, 4, 27, 'اور الله چاہتا ہے کہ تم پر اپنی رحمت سے متوجہ ہو اور جو لوگ اپنے مزوں کے پیچھے لگے ہوئے ہیں وہ چاہتے ہیں کہ تم راہ راست سے بہت دور ہٹ جاؤ'),
(521, 4, 28, 'اللہ چاہتا ہے کہ تم سے بوجھ ہلکا کر دے کیوں کہ انسان کمزور پیدا کیا گیا ہے'),
(522, 4, 29, 'اے ایمان والو! آپس میں ایک دوسرے کے مال نا حق نہ کھاؤ مگر یہ کہ آپس کی خوشی سے تجارت ہو اور آپس میں کسی کو قتل نہ کرو بے شک اللہ تم پر مہربان ہے'),
(523, 4, 30, 'اور جو شخص تعدّی اور ظلم سے یہ کام کرے گا تو ہم اسے آگ میں ڈالیں گے اور یہ اللہ پر آسان ہے'),
(524, 4, 31, 'اگر تم ان بڑے گناہوں سے بچو گے جن سے تمہیں منع کیا گیا تو ہم تم سے تمہارے چھوٹے گناہ معاف کردیں گے اور تمہیں عزت کے مقام میں داخل کریں گے'),
(525, 4, 32, 'اور مت ہوس کرو اس فضیلت میں جو الله نے بعض کو بعض پر دی ہے مردوں کو اپنی کمائی سے حصہ ہے اور عورتوں کو اپنی کمائی سے حصہ ہے اور اللہ سے اس کا فضل مانگو بے شک اللہ کو ہر چیز کا علم ہے'),
(526, 4, 33, 'اور ہر شخص کے ہم نے وارث مقرر کر دیئے ہیں اس مال کے جو ماں باپ یا رشتہ دار چھوڑ کر مریں اور وہ لوگ جن سے تمہارے عہد و پیمان ہوں تو انہیں ان کا حصہ دے دو بے شک الله ہر چیز پر گواہ ہے'),
(527, 4, 34, 'مرد عورتوں پر حاکم ہیں اس واسطے کہ الله نے ایک کو ایک پر فضیلت دی ہے اور اس واسطے کہ انہوں نے اپنے مال خرچ کیے ہیں پھر جو عورتیں نیک ہیں وہ تابعدار ہیں مردوں کے پیٹھ پیچھے الله کی نگرانی میں (ان کے حقوق کی) حفاظت کرتی ہیں اور جن عورتو ں سےتمہیں سرکشی کا خطرہ ہو تو انہیں سمجھاؤ اور سونے میں جدا کر دو اور مارو پھر اگر تمہارا کہا مان جائیں تو ان پر الزام لگانے کے لیے بہانے مت تلاش کرو بے شک الله سب سے اوپر بڑا ہے'),
(528, 4, 35, 'اور اگر تمہیں کہیں میاں بیوی کے تعلقات بگڑ جانے کا خطرہ ہو تو ایک منصف مرد کے خاندان میں سے اور ایک منصف عورت کے خاندان میں سے مقرر کرو اگر یہ دونوں صلح کرنا چاہیں گے تو الله ان دونو ں میں موافقت کر دے گا بے شک الله سب کچھ جاننے والا خبردار ہے'),
(529, 4, 36, 'اور الله کی بندگی کرو اورکسی کو اس کا شریک نہ کرو اور ماں باپ کے ساتھ نیکی کرو اور رشتہ داروں اور یتیموں اور مسکینوں اور قریبی ہمسایہ اور اجنبی ہمسایہ اورپاس بیٹھنے والے او رمسافر او راپنے غلاموں کے ساتھ بھی نیکی کرو بے شک الله اترانے والے بڑائی کرنے والے کو پسند نہیں کرتا'),
(530, 4, 37, 'جو لوگ بخل کرتے ہیں اور لوگو ں کو بخل سکھاتے ہیں اور الله نے انہیں اپنے فضل سے جو دیا ہے اسے چھپاتے ہیں اور ہم نے کافروں کے لیے ذلت کا عذاب تیار کر رکھا ہے'),
(531, 4, 38, 'اور جو لوگ اپنے مالوں کو لوگوں کے دکھانے میں خرچ کرتے ہیں اور اللہ پر اور قیامت کے دن پر ایمان نہیں لاتے اور جس کا شیطان ساتھی ہوا تو وہ بہت برا ساتھی ہے'),
(532, 4, 39, 'اور اگر یہ الله اور قیامت کے دن پر ایمان لے آتے اور الله کے دیے ہوئے مال میں سے خرچ کرتے تو ان کا کیا نقصان تھا اور الله انہیں خوب جانتا ہے'),
(533, 4, 40, 'بے شک الله کسی کا ایک ذرہ برابر بھی حق نہیں رکھتا اور اگر نیکی ہو تو اس کو دگنا کر دیتا ہے اور اپنے ہاں سے بڑا ثواب دیتا ہے'),
(534, 4, 41, 'پھر کیا حال ہوگا جب ہم ہر امت میں سے گواہ بلائینگے اور تمیں ان پر گواہ کر کے لائیں گے'),
(535, 4, 42, 'جن لوگو ں نے کفر کیا تھا اور رسول کی نافرمانی کی تھی وہ اس دن کی آرزو کریں گے کہ زمین کے برابر ہو جائیں اور الله سے کوئی بات نہ چھپا سکیں گے'),
(536, 4, 43, 'اے ایمان والو! جس وقت کہ تم نشہ میں ہو نماز کے نزدیک نہ جاؤ یہاں تک کہ تم سمجھو سکوکہ تم کیا کہہ رہے ہو اور جنبی ہونے کی حالت میں مگر راستہ گزرتے ہوئے یہاں تک کہ غسل کر لو اور اگر تم بیمار ہو یا سفر میں ہو یا کوئی شخص تم میں سے رفع حاجت کر کے آئے یا عورتوں کے پاس گئے ہو پھر تمہیں پانی نہ ملے تو پاک مٹی سے کام لو اور اسے اپنے مونہوں پر اور ہاتھوں پر ملو بے شک الله معاف کرنے والا بخشنے والا ہے'),
(537, 4, 44, 'کیا تم نے ان لوگوں کو نہیں دیکھا جنہیں کچھ حصہ کتاب سے ملا ہے وہ گمراہی خریدتے ہیں اور چاہتے ہیں کہ تم بھی راستہ گم کر دو'),
(538, 4, 45, 'اور الله تمہارے دشمنوں کو خوب جانتا ہے اور تمہاری حمایت اور مدد کے لیے الله ہی کافی ہے'),
(539, 4, 46, 'یہودیوں میں بعض ایسے ہیں جو الفاظ کو ان کے محل سے پھیر دیتے ہیں او ر کہتے ہیں ہم نے سنا اور نہ مانا او رکہتے ہیں کہ سن نہ سنایا جائے تو اور کہتے ہیں راعِنا اپنی زبان کو مروڑ کر اور دین میں طعن کرنے کے خیال سےاور اگر وہ کہتے ہیں کہ ہم نے سنا اور ہمنے مانا اور سن تو اور ہم پر نظر کو تو ان کے حق میں بہتر اور درست ہوتا لیکن ان کے کفر کے سبب سے الله نے ا ن پر لعنت کی سو ان میں سے بہت کم لوگ ایمان لائیں گے'),
(540, 4, 47, 'اے کتاب والو اس پر ایمان لے آؤ جو ہم نے نازل کیا ہے اس کتاب کی تصدیق کرتا ہے جو تمہارے پاس ہے اس سے پہلے کہ ہم بہت سے چہرو ں کو مٹا ڈالیں پھر انہیں پیٹھ کیطرف الٹ دیں یا ان پر لعنت کریں جسطرح ہم نے ہفتے کے دن والوں پر لعنت کی تھی اور الله کا حکم تو نافذ ہو کر ہی رہتا ہے'),
(541, 4, 48, 'بے شک الله اسے نہیں بخشتا جو اس کا شریک کرے اور شرک کے ماسوا دوسرےگناہ جسے چاہے بخشتا ہے اور جس نے الله کا شریک ٹھیرایا اس نے بڑا ہی گناہ کیا'),
(542, 4, 49, 'کیا تم نے ان لوگو ں کو نہیں دیکھا جو اپنی پاکیزگی کا دم بھرتے ہیں بلکہ الله جسے چاہے پاک کرتا ہے اور ان پر تاگے کے برابر بھی ظلم نہ ہوگا'),
(543, 4, 50, 'دیکھو یہ لوگ الله پر کیسا جھوٹ باندھتے ہیں یہی ایک صریح گنا ہ کافی ہے'),
(544, 4, 51, 'کیا تم نے ان لوگوں کو نہیں دیکھا جنہیں کتاب کا کچھ حصہ دیا گیا وہ بتوں اور شیطانوں کو مانتے ہیں اور کافروں سے یہ کہتے ہیں کہ یہ لوگ مسلمانوں سے زیادہ راہِ راست پر ہیں'),
(545, 4, 52, 'یہی وہ لوگ ہیں جن پر الله کی لعنت ہے اور جس پر الله لعنت کرے تو اس کا کوئي مددگار نہیں پائے گا'),
(546, 4, 53, 'کیا سلطنت میں ان کا بھی کچھ حصہ ہے پھر تو یہ لوگوں کو ایک تِل بھر بھی نہیں دیں گے'),
(547, 4, 54, 'یا لوگو ں پر حسد کرتے ہیں جو الله نے ان کو اپنے فضل سے دیا ہے ہم نے تو ابراھیم کی اولاد کو کتاب اور حکمت ادا کی ہے اور ان کوہم نے بڑی بادشاہی دی ہے'),
(548, 4, 55, 'پھران میں سے کوئی اس پر ایمان لایا اورکوئی اس سے ہٹ گیا اور دوزخ کی بھڑکتی ہوئی آگ کافی ہے'),
(549, 4, 56, 'بے شک جن لوگو ں نے ہماری آیتوں کا انکار کیا انہیں ہم آگ میں ڈال دیں گے جس وقت ان کی کھالیں جل جائیں گی تو ہم انکو اور کھالیں بدل دیں گے تاکہ عذاب چکھتے رہیں بے شک الله زبردست حکمت والا ہے'),
(550, 4, 57, 'او رجو لوگ ایمان لائے اور نیک کام کیے انہیں ہم ایسے باغوں میں داخل کریں گے جن کے نیچے نہریں بہتی ہوں گی ان میں ہمیشہ ہمیشہ رہنے والے ہوں گے ان کے لیے وہاں ستھری عورتیں ہوں گی اور ہم انہیں گھنی چھاؤں میں رکھیں گے'),
(551, 4, 58, 'بے شک الله تمہیں حکم دیتا ہے کہ امانتیں امانت والوں کو پہنچا دو اور جب لوگوں کے درمیان فیصلہ کرو تو انصاف سے فیصلہ کرو بے شک تمہیں نہایت اچھی نصیحت کرتا ہے بے شک الله سننے والا دیکھنے والا ہے'),
(552, 4, 59, 'اے ایمان والو الله کی فرمانبرداری کرو اور رسول کی فرمانبرداری کرو اور ان لوگوں کی جو تم میں سے حاکم ہوں پھر اگر آپس میں کوئی چیز میں جھگڑا کرو تو اسے الله اور اس کے رسول کی طرف پھیرو اگر تم الله اور قیامت کے دن پر یقین رکھتے ہو یہی بات اچھی ہے اور انجام کے لحاظ سے بہت بہتر ہے'),
(553, 4, 60, 'کیا تم لوگوں نے ان لوگوں کو نہیں دیکھا جواس چیز پر ایمان لانے کا دعویٰ کرتے ہیں جو تجھ پر نازل کی گئی ہے اور جو چیز تم سےپہلے نازل کی گئی ہے وہ چاہتے ہیں کہ اپنا فیصلہ شیطان سے کرائیں حالانکہ انہیں حکم دیا گیا ہے کہ اسے نہ مانیں اور شیطان تو چاہتا ہے کہ انہیں بہکا کر دو رجا ڈالے'),
(554, 4, 61, 'اور جب انہیں کہا جاتا ہے جو چیز الله نے نازل کی ہے اس کی طرف آؤ اور رسول کی طرف آؤ توتو منافقوں کو دیکھے گا کہ تجھ سے پہلو تہی کرتے ہیں'),
(555, 4, 62, 'پھر کیاہوتا ہے جب ان کے اپنے ہاتھوں سے لائی ہوئی مصیبت ان پر آتی ہے پھر تیرے پاس آ کر خدا کی قسمیں کھاتے ہیں کہ ہم کو تو سوائے بھلائیا ور باہمی موافقت کے اور کوئی غرض نہ تھی'),
(556, 4, 63, 'یہ وہ لوگ ہیں کہ الله جانتا ہے جو ان کے دلوں میں ہے تو ان سے منہ پھیر لے اور انہیں نصیحت کرو ان سے ایسی بات کہو جو ان کے دلوں میں اتر جائے'),
(557, 4, 64, 'اورہم نے کبھی کوئی رسول نہیں بھیجا مگر اسی واسطے کہ الله کے حکم سے اس کی تابعداری کی جائے اور جب انہوں نے اپنے نفسوں پر ظلم کیا تھا تیرے پاس آتے پھر الله سے معافی مانگتے اور رسول بھی ان کی معافی کی درخواست کرتا تو یقیناًیہ الله کو بخشنے والا رحم کرنے والا پاتے'),
(558, 4, 65, 'سو تیرے رب کی قسم ہے یہ کبھی مومن نہیں ہوں گے جب تک کہ اپنے اختلافات میں تجھے منصف نہ مان لیں پھر تیرے فیصلہ پر اپنے دلوں میں کوئی تنگی نہ پائیں اور خوشی سے قبول کریں'),
(559, 4, 66, 'اور اگر ہم ان پر حکم کرتے کہ اپنی جانوں کو ہلاک کر دو یا اپنے گھروں سے نکل جاؤ تو ان میں سے بہت ہی کم آدمی اس پر عمل کرتے اور اگر یہ لوگ کریں جو ان کو نصیحت کی جاتی ہے تو یہ ان کے لیے زیادہ بہتر ہوتا اور دین میں زیادہ ثابت رکھنے والا ہوتا'),
(560, 4, 67, 'اور اس وقت البتہ ہم ان کو اپنے ہاں سے بڑا ثواب دیتے'),
(561, 4, 68, 'اور البتہ انھیں سیدھا راستہ دکھاتے'),
(562, 4, 69, 'اورجو شخص الله اور اس کے رسول کا فرمانبردار ہو تو وہ ان کے ساتھ ہوں گے جن پر الله نے انعام کیا وہ نبی اور صدیق اور شہید اور صالح ہیں اور یہ رفیق کیسے اچھے ہیں'),
(563, 4, 70, 'یہ الله کی طرف سے احسان ہے اور الله کافی ہے جاننے والا'),
(564, 4, 71, 'اے ایمان والو! اپنے ہتھیار لے لو پھر جدا جدا فوج ہو کر نکلویا سب اکھٹے ہوکر نکلو'),
(565, 4, 72, 'اور بے شک تم میں بعض ایسا بھی ہے جو لڑائی سے جی چراتا ہے پھر اگر تم پر کوئی مصیبت آجائے تو کہتا ہے الله نے مجھ پر فضل کیا کہ میں ان لوگوں کے ساتھ نہ تھا'),
(566, 4, 73, 'اور اگر الله کی طرف سے تم پر فضل ہو تو اس طرح کہنے لگتا ہے کہ گویا تمہارے اور اس کے درمیان دوستی کا کوئی تعلق ہی نہیں کہ کاش میں بھی ان کے ساتھ ہوتا تو بڑی مراد پاتا'),
(567, 4, 74, 'سو چاہیئے کہ الله کی راہ میں وہ لوگ لڑیں جو دنیا کی زندگی کو آخرت کے بدلے بیچتے ہیں اور جو کوئی الله کی راہ میں لڑے پھر مارا جائے یا غالب رہے تو اسے ہم بڑا ثواب دیں گے'),
(568, 4, 75, 'اور کیا وجہ ہے کہ تم الله کی راہ میں ان بے بس مردوں اور عورتوں اور بچوں کی خاطر نہ لڑو جو کہتے ہیں اے ہمارے رب ہمیں اس بستی سے نکال جس کے باشندے ظالم ہیں اور ہمارے واسطے اپنے ہاں سے کوئی حمایتی کر دے اور ہمارے واسطےاپنے ہاں سے کوئی مددگار بنا دے'),
(569, 4, 76, 'جو ایمان والے ہیں وہ الله کی راہ میں لڑتے ہیں اور جو کافر ہیں وہ شیطان کی راہ میں لڑتے ہیں سو تم شیطان کے ساتھیوں سے لڑو بے شک شیطان کا فریب کمزور ہے'),
(570, 4, 77, 'کیا تم نے ان لوگوں کو نہیں دیکھا جنہیں کہا گیا تھا کہ اپنے ہاتھ روکے رکھو اور نماز قائم کرو اور زکواة دو پھر جب انہیں لڑنے کا حکم دیا گیا اس وقت ان میں سے ایک جماعت لوگوں سے ایسا ڈرنے لگی جیسا الله کا ڈر ہو یا اس سے بھی زیادہ ڈر اور کہنے لگے اے رب ہمارے تو نے ہم پر لڑنا یوں فرض کیا کیوں نہ ہمیں تھوڑی مدت اور مہلت دی ان سے کہ دو دنیا کا فائدہ تھوڑا ہے اور آخرت پرہیزگاروں کے لیے بہتر ہے اور ایک تاگے کے برابر بھی تم سے بے انصافی نہیں کی جائے گی'),
(571, 4, 78, 'تم جہاں کہیں ہو گے موت تمہیں آ ہی پکڑے گی اگرچہ تم مضبوط قلعوں میں ہی ہو اور اگر انہیں کوئی فائدہ پہنچتا ہے تو لوگ کہتے ہیں کہ یہ الله کی طرف سے ہے اور اگر کوئی نقصان پہنچتا ہے تو کہتے ہیں کہ یہ تیری طرف سے ہے ان لوگوں کو کیا ہو گیا ہے کہ کوئی بات ان کی سمجھ میں نہیں آتی'),
(572, 4, 79, 'تجھے جو بھی بھلائی پہنچے وہ الله کی طرف سے ہے اور جو تجھے برائی پہنچے وہ تیرے نفس کی طرف سے ہے ہم نے تجھے لوگوں کو پیغام پہنچانے واا بنا کر بھیجا ہے اور الله کی گواہی کافی ہے'),
(573, 4, 80, 'جس نے رسول کا حکم مانا اس نے الله کا حکم مانا اور جس نے منہ موڑا تو ہم نے تجھے ان پر نگہبان بنا کر نہیں بھیجا'),
(574, 4, 81, 'اور کہتے ہیں قبول کیا پھر جب تیرے ہاں سے باہر گئے تو ان میں سے ایک گروہ رات کو جمع ہو کر تمہاری باتو ں کے خلاف مشورہ کرتا ہے اور الله لکھتا ہے جو وہ مشورہ کرتے ہیں تو ان کی پرواہ نہ کر اور الله پر بھروسہ کر اور الله کارساز کافی ہے'),
(575, 4, 82, 'کیا یہ لوگ قرآن میں غور نہیں کرتے اور اگر یہ قرآن سوائے الله کے کسی اور کی طرف سے ہوتا تو وہ اس میں بہت اختلاف پاتے'),
(576, 4, 83, 'اور جب ان کے پاس کوئی خبر امن یا ڈر کی پہنچتی ہے تو اسے مشہور کر دیتے ہیں اور اگر اسے رسول او راپنی جماعت کے ذمہ دار اصحاب تک پہنچاتے تو اس کی تحقیق کرتے جو ان میں تحقیق کرنے والے ہیں اوراگر تم پر الله کا فضل اوراس کی مہربانی نہ ہوتی تو البتہ تم شیطان کے پیچھے ہو لیتے سوائے چند لوگوں کے'),
(577, 4, 84, 'سو تو الله کی راہ میں لڑ تو سوائے اپنی جان کے کسی کا ذمہ دار نہیں اور مسلمانوں کو تاکید کر قریب ہے کہ الله کافرو ں کی لڑائی بند کر دے اور الله لڑائی میں بہت ہی سخت ہےاور سزا دینے میں بھی بہت سخت ہے'),
(578, 4, 85, 'جو کوئی اچھی بات میں سفارش کرے اسے بھی اس میں سے ایک حصہ ملے گا اور جو کوئی بری بات میں سفارش کرے اس میں سے ایک بوجھ اس پر بھی ہے اور الله ہر چیز پر قدرت رکھنے والا ہے'),
(579, 4, 86, 'اور جب تمہیں کوئی دعا دے تو تم اس سے بہتر دعا دو یا الٹ کر ویسی ہی کہو بے شک الله ہر چیز کا حساب لینے والا ہے'),
(580, 4, 87, 'الله وہ ہے جس کے سوا کوئی بندگی نہیں بے شک قیامت کے دن تم سب کو جمع کرے گا اس میں کوئی شک نہیں اور الله سے بڑھ کر کس کی بات سچی ہو سکتی ہے'),
(581, 4, 88, 'پھر تمہیں کیا ہو گیا ہے کہ منافقوں کے معاملہ میں دو گروہ ہو رہے ہیں اور الله نے ان کے اعمال کے سبب سےانہیں الٹ دیا ہے کیا تم چاہتے ہو جسے الله نے گمراہ کیا ہو اسے راہ پر لاؤ اور جسے الله گمراہ کرے تو اس کے لیے ہرگز کوئی راہ نہیں پائے گا'),
(582, 4, 89, 'وہ تو چاہتے ہیں کہ جیسے وہ کافر ہوئے ہیں تم بھی کافر ہو جاؤ پھر تم سب برابر ہو جاؤ لہذا ان میں سے کسی کو اپنا دوست نہ بناؤ جب تک وہ الله کی راہ میں ہجرت کر کے نہ آ جائیں پھر اگر وہ اس بات کو قبول نہ کریں تو جہاں پاؤ انہیں پکڑو اور قتل کرو اور ان میں سے کسی کو اپنے دوست اور مددگار نہ بناؤ'),
(583, 4, 90, 'البتہ وہ منافق اس حکم سے مستثنیٰ ہیں جو کسی ایسی قوم سے جا ملیں جس کے ساتھ تمہارا معاہدہ ہو یا وہ جو تمہارے پاس آتے ہیں اور لڑائی سے دل برداشتہ ہیں نہ تم سے لڑتے ہیں اور نہ اپنی قوم سے اور اگر الله چاہتا تو انہیں تم پر مسلط کر دیتا ہے پھر وہ تم سے لڑتے ہیں سو اگر وہ تم سے یک سو رہیں اور تم سے نہ لڑیں او رتمہاری طرف صلح کا ہاتھ بڑھائیں تو الله نے تمہیں ان پر کوئی راہ نہیں دی'),
(584, 4, 91, 'ایک اور قسم کے تم منافق دیکھو گے جو چاہتے ہیں تم سے بھی امن میں رہیں اور اپنی قوم سے بھی جب کبھی وہ فساد کی طرف لوٹائے جاتے ہیں تو اس میں کود پڑتے ہیں پھر اگر وہ تم سے یک سو نہ رہیں اور تہارے آگے صلح پیش نہ کریں اور اپنے ہاتھ نہ روکیں تو انہیں جہاں پا پکڑو او رمار ڈالو اور ان پر ہاتھ اٹھانے کے لیے ہم نے تمہیں کھلی حجت دے دی ہے'),
(585, 4, 92, 'اور مسلمانوں کا یہ کام نہیں کہ کسی مسلمان کو قتل کرے مگر غلطی سے اور جو مسلمان کو غلطی سے قتل کرے تو ایک مسلمان کی گردن آزاد کرے اور مقتول کے وارثوں کو خون بہا دے مگر یہ کہ وہ خون بہا معاف کر دیں پھر اگر وہ مسلمان مقتول کسی ایسی قوم میں تھا جس سے تمہاری دشمنی ہے تو ایک مومن غلام آزاد کرنا ہے اور اگر وہ مقتول مسلمان کسی ایسی قوم میں سے تھا جس سے تمہارا معاہدہ ہے تو اس کے وارثوں کو خون بہا دیا جائے گا اور ایک مومن غلام کو آزاد کرنا ہوگا پھر جو غلام نہ پائے وہ پے درپے دو مہینے کے روزے رکھے الله سےگناہ بخشوانے کے لیے اور الله جاننے والا حکمت والا ہے'),
(586, 4, 93, 'اور جو کوئی کسی مسلمان کو جان کر قتل کرے اس کی سزا دوزخ ہے جس میں وہ ہمیشہ رہے گا اس پر الله کا غضب اور اس کی لعنت ہے اور الله نے اس کےلیے بڑا عذاب تیا رکیا ہے'),
(587, 4, 94, 'اے ایمان والو! جب الله کی راہ میں سفر کرو تو تحقیق کر لیا کرو او جو تم پر سلام کہے اس کو مت کہو کہ مسلمان نہیں ہے تم دنیا کی زندگی کا سامان چاہتے ہو سو الله کے ہاں بہت غنیمتیں ہیں تم بھی تو اس سے پہلے ایسے ہی تھے پھر الله نے تم پر احسان کیا لہذا تحقیق سے کام لیا کرو بے شک الله تمہارے کاموں سے باخبر ہے'),
(588, 4, 95, 'مسلمانوں میں سے جو لوگ کسی عذر کے بغیر گھر بیٹھے رہتے ہیں اور وہ جو الله کی راہ میں جان و مال سے جہاد کرتے ہیں دونوں برابر نہیں ہیں الله نے بیٹھنے والوں پر جان و مال سے جہاد کرنے والوں کا درجہ بڑھایا دیا ہے اگرچہ ہر ایک سے الله نے بھلائي کا وعدہ کیا ہے اور الله نے لڑنے والوں کو بیٹھنے والوں سے اجر عظیم میں زیادہ کیا ہے'),
(589, 4, 96, 'ان کے لیے الله کی طرف سے بڑے درجے اور مغفرت اور رحمت ہے اور الله معاف کرنے والا رحم کرنے والا ہے'),
(590, 4, 97, 'بے شک جو لوگ اپنے نفسوں پر ظلم کر رہے تھے ان کی روحیں جب فرشتوں نے قبض کیں توان سے پوچھا کہ تم کس حال میں تھے انہوں نے جواب دیا ہم اس ملک میں بے بس تھے فرتشوں نے کہا کیا الله کی زمین وسیع نہ تھی کہ تم اس میں ہجرت کر جاتےسو ایسوں کا ٹھکانہ دوزخ ہے اور بہت ہی برا ٹھکانہ ہے'),
(591, 4, 98, 'ہاں جو مرد اور عورتیں اور بچے کافی کمزور ہیں جو نکلنے کا کوئی ذریعہ اور راستہ نہیں پاتے'),
(592, 4, 99, 'پس امید ہے کہ ایسوں کو الله معاف کر دے اور الله معاف کرنے والا بخشنے والا ہے'),
(593, 4, 100, 'اور جو کوئی الله کی راہ میں وطن چھوڑے اس کے عوض جگہ بہت اور کشائش پائے گا اور جو کوئی اپنے گھر سے الله اور رسول کی طرف ہجرت کر کے نکلے پھر اس کو موت پالے تو الله کے ہاں اس کا ثواب ہو چکا اور الله بخشنے والا مہربان ہے'),
(594, 4, 101, 'اور جب تم سفر کے لیے نکلو تو تم پر کوئی گناہ نہیں نماز میں سے کچھ کم کر دو اگر تمہیں یہ ڈر ہو کہ کافر تمہیں ستائیں گے بےشک کافر تمہارے صریح دشمن ہیں'),
(595, 4, 102, 'اے نبی جب تم مسلمانو ں میں موجود ہو اور انہیں نماز پڑھانے کے لیے کھڑا ہو تو چاہیئے ان میں سے ایک جماعت تیرے ساتھ کھڑی ہو اور اپنے ہتھیار ساتھ لے لیں پھر جب یہ سجدہ کریں تو تیرے پیچھے سے ہٹ جائیں اور دوسری جماعت آئے جس نے نماز نہیں پڑھی وہ تیرے ساتھ نماز پڑھتے اور وہ بھی اپنے بچاؤ اور اپنے ہتھیار ساتھ رکھیں کافر چاہتے ہیں کہ کسی طرح تم اپنے ہتھیاروں اور اسباب سے بے خبر ہو جاؤ تاکہ تم پر یک بارگی ٹوٹ پڑیں اور اگر تم بارش کی وجہ سے تکلیف محسوس کرو یا بیمار ہو تو ہتھیار رکھ دینے میں کوئي مضائقہ نہیں اور (تب بھی) اپنا بچاؤ ساتھ رکھو بے شک الله نے کافروں کے لیے ذلت کا عذاب تیار کر رکھا ہے'),
(596, 4, 103, 'پھر جب نماز سے فارغ ہو جاؤ تو الله کو کھڑے او ربیٹھے اور لیٹے ہونے کی حالت میں یاد کرو پھر جب تمہیں اطمینان ہو جائے تو پوری نماز پڑھو بے شک نماز اپنے مقرر وقتوں میں مسلمانوں پر فرض ہے'),
(597, 4, 104, 'اور ان لوگوں کا پیچھا کرنے سے ہمت نہ ہارو اگر تم تکلیف اٹھاتے ہو تو وہ بھی تمہاری طرح تکلیف اٹھاتے ہیں حالانکہ تم الله سے جس چیز کے امیدوار ہو وہ نہیں ہیں اور الله سب کچھ جاننے والا حکمت ولا ہے'),
(598, 4, 105, 'بے شک ہم نے تیری طرف سچی کتاب اتاری ہے تاکہ تو لوگوں میں انصاف کرے جو کچھ تمہیں الله سجھا دے اور تو بد دیانت لوگوں کی طرف سے جھگڑنے والا نہ ہو'),
(599, 4, 106, 'اور الله سے بخشش مانگ بے شک الله بخشنے والا مہربان ہے'),
(600, 4, 107, 'اور ان لوگوں کی طرف سے مت جھگڑو جو اپنے دل میں دغا رکھتے ہیں جو شخص دغا باز گناہگار ہو بے شک الله اسے پسند نہیں کرتا'),
(601, 4, 108, 'یہ لوگوں سے تو چھپتے ہیں اور خدا سے نہیں چھپتے حالانکہ جب وہ راتوں کو ایسی باتوں کے مشورے کیا کرتے ہیں جن کو وہ پسند نہیں کرتا ان کے ساتھ ہوا کرتا ہے اور خدا ان کے (تمام) کاموں پر احاطہ کئے ہوئے ہے'),
(602, 4, 109, 'ہاں تم لوگوں نے ان مجرمو ں کی طرف سے دنیا کی زندگی میں تو جھگڑا کر لیا پھر قیامت کے دن ان کی طرف سے الله سے کون جھگڑے گا یا ان کا وکیل کون ہوگا'),
(603, 4, 110, 'اور جو کوئی برا فعل کرے یا اپنے نفس پر ظلم کرے پھراس کے بعد اللهسےبخشوائے تو الله کو بخشنے والا مہربان پائے'),
(604, 4, 111, 'اور جو کوئی گناہ کرے سو اپنےہی حق میں کرتا ہے اور الله سب باتوں کا جاننے والا حکمت والا ہے'),
(605, 4, 112, 'اور جو کوئی خطا یا گناہ کرے پھر کسی بے گنا پر تہمت لگادے تو اس نے بڑے بہتان اور صریح گناہ کا بار سمیٹ لیا'),
(606, 4, 113, 'اور اگر تجھ پر الله کا فضل اور اس کی رحمت نہ ہوتی تو ان میں سے ایک گروہ نے تمہیں غلط فہمی میں مبتلا کرنے کا فیصلہ کرہی لیا تھا حالا نکہ وہ اپنے سوا کسی کو غلط فہمی میں مبتلانہیں کر سکتے تھے اور وہ تمہارا کچھ نہیں بگاڑ سکتے تھے اور الله نے تجھ پر کتاب اور حکمت نازل کی ہے اور تجھے وہ باتیں سکھائی ہیں جو تو نہ جانتا تھا اور الله کا تجھ پر بہت بڑا فضل ہے'),
(607, 4, 114, 'ان لوگو ں کی خفیہ سرگوشیوں میں اکثر کوئی بھلائی نہیں ہوتی ہاں مگر کوئی پوشیدہ طور پر صدقہ کرنے یا کسی نیک کام کرنے یا لوگو ں میں صلح کرانے میں کی جائے تو یہ بھلی بات ہے اور جو شخص یہ کام الله کی رضا جوئی کے لیے کرے تو ہم اسے بڑا ثواب دیں گے'),
(608, 4, 115, 'اور جو کوئی رسول کی مخالفت کرے بعد اس کے کہ اس پر سیدھی راہ کھل چکی ہو اور سب مسلمانوں کے راستہ کے خلاف چلے تو ہم اسے اسی طرف چلائیں گے جدھر وہ خود پھر گیا ہے اور اسے دوزح میں ڈال دینگے اور وہ بہت برا ٹھکانا ہے'),
(609, 4, 116, 'بے شک الله اس کو نہیں بخشتا جو کسی کو اس کا شریک بنائے اس کے سوا جسے چاہے بخش دے اور جس نے الله کا شریک ٹھیرایا وہ بڑی دور کی گمراہی میں جا پڑا'),
(610, 4, 117, 'یہ لوگ الله کے سوا عورتو ں کی عبادت کرتے ہیں اور صرف شیطان سرکش کی عبادت کرتے ہیں'),
(611, 4, 118, 'جس پر الله کی لعنت ہے اور شیطان نے کہا کہ اے الله میں تیرے بندوں میں سے حصہ مقرر لوں گا'),
(612, 4, 119, 'اور البتہ انہیں ضرور گمراہ کروں گا اور البتہ ضرور انہیں امیدیں دلاؤں گا اور البتہ ضرور انہیں حکم کروں گا کہ جانوروں کے کان چریں اور البتہ ضرور انہیں حکم دوں گا کہ جانورں کے کان چیریں اور البتہ ضرور انہیں حکم دوں گاکہ الله کی بنائی ہوئی صورتیں بدلیں اور جو شخص الله کو چھوڑ کر شیطان کو دوست بنائے گا وہ صریح نقصان میں جا پڑا'),
(613, 4, 120, 'شیطان ان سے وعدے کرتا ہےاور انہیں امیدیں دلاتا ہے اور شیطان ان سے صرف جھوٹے وعدے کرتا ہے'),
(614, 4, 121, 'ایسے لوگو ں کا ٹھکانہ دورخ ہے ااور اس ے کہیں بچنے کے لیے جگہ نہ پائیں گے'),
(615, 4, 122, 'اور جو لوگ ایمان لائے اور اچھے کام کیے انہیں ہم باغوں میں داخل کر ینگے جن کے نیچے نہریں بہتی ہیں ان میں ہمیشہ رہیں گے الله کا وعدہ سچا ہے اور الله سے زیادہ سچا کون ہے'),
(616, 4, 123, 'نہ تمہاری امیدوں پر مدار ہے اور نہ اہل کتاب کی امیدوں پر جو کوئی برائی کا کام کرے گا اس کی سزا دیا جائے گا اور الله کے سوا اپنا کوئی حمایتی اور مددگار نہیں پائے گا'),
(617, 4, 124, 'اور جو کوئی اچھے کام کرے گا مرد ہے یا عورت درآنحالینکہ وہ ایما ندار ہو تو وہ لوگ جنت میں داخل ہوں گے اور کھجور کے شگاف برابر بھی ظلم نہیں کیے جائیں گے'),
(618, 4, 125, 'اس شخص سے بہتر دین میں کون ہے جس نے الله کے حکم پر پیشانی رکھی اور وہ نیکی کرنے والا ہو اور ابراھیم حنیف کے دین کی پیروی کرے اور الله نے ابراھیم کوخاص دوست بنا لیا ہے'),
(619, 4, 126, 'اور الله ہی کا ہے جو کچھ آسمانوں اور زمین میں ہے اور الله سب چیزو ں کا احاطہ کیے ہوئے ہے'),
(620, 4, 127, 'اور تجھ سے عورتو ں کے نکاح کی رخصت مانگتے ہیں کہہ دے الله تمہیں ان کی اجازت دیتا ہے اور وہ جو تمہیں قرآن سنایا جاتا ہے سو ان یتیم عورتوں کا حکم ہے جنہیں تم نہیں دیتے جو ان کے لیے مقرر کیا گیا ہے او رچاہتے ہو کہ ان سے نکاح کرو اور کمزور لڑکوں کے بارے میں ہے اور یہ کہ یتیموں کے حق میں انصاف پر قائم رہو اور جو تم نیکی کرو گے پس تحقیق الله اسے جاننے والا ہے'),
(621, 4, 128, 'اور اگر کوئی عورت اپنے خاوند کے لڑنے یا منہ پھیرنے سے ڈرے تو دونوں پر کوئی گناہ نہیں کہ آپس میں کسی طرح صلح کر لیں اور یہ صلح بہتر ہے اور دلوں میں حرص موجود ہے اور اگر تم نیکی کرو او رپرہیزگاری کرو تو الله کو تمہارے اعمال کی پوری خبر ہے'),
(622, 4, 129, 'اور تم عورتوں کو ہرگز برابر نہیں رکھ سکو گے اگرچہ اس کی حرص کرو سوتم بالکل ہی ایک طرف نہ جھک جاؤ کہ دوسری عورت کو لٹکی ہوئی چھوڑ دو اور اگر اصلاح کرتے رہو اور پرہیزگاری کرتے رہو تو الله بحشنے والا مہربان ہے'),
(623, 4, 130, 'اور اگر دونوں میاں بیوی جدا ہو جائیں تو الله اپنی و سعت سے ہر ایک کو بے پروا کردے گا اور الله وسعت کرنے والا حکمت والا ہے'),
(624, 4, 131, 'اور جو کچھ اسمانوں اور جو کچھ زمین میں ہے وہ الله ہی کا ہے اور ہم نے پہلی کتاب والوں کو اور تمہیں حکم دیا ہے کہ الله سے ڈرو اور اگر ناشکری کرو گے تو جو کچھ آسمانو ں میں ہے اور جو کچھ زمین میں ہے سب الله ہی کا ہے اور الله بے پرواہ تعریف کیا ہواہے'),
(625, 4, 132, 'اور جو کچھ آسمانو ں اور زمین میں سب الله ہی کا ہے اور الله کارسازکافی ہے'),
(626, 4, 133, 'اگر چاہے تو اے لوگو تمہیں لے جائے اوراوروں کو لے آئے اور الله اس پر قادر ہے'),
(627, 4, 134, 'جو شخص دنیا کا ثواب چاہتا ہے تو الله کے ہاں دنیا اور آخرت کا ثواب ہے اور الله سننے والا دیکھنے والا ہے'),
(628, 4, 135, 'اے ایمان والو! انصاف پر قائم رہو الله کی طرف گواہی دو اگرچہ اپنی جانوں پر ہو یا اپنے ماں باپ اور رشہ داروں پر اگر کوئی مالدار ہے یا فقیر ہے تو الله ان کا تم سے زیادہ خیر خوا ہ ہے سو تم انصاف کرنے میں دل کی خواہش کی پیروی نہ کرو اور اگر تم کج بیانی کرو گے یا پہلو تہی کرو گے تو بلاشبہ الله تمہارے سب اعمال سے با خبر ہے'),
(629, 4, 136, 'اے ایمان والو! الله اور اس کے رسول پر یقین لاؤ اور اس کتاب پر جو اس نے اپنے رسول پر نازل کی ہےاور اس کتاب پر جو پہلے نازل کی تھی اور جو کوئی الله کا انکار کرے اور اس کے فرشتوں کا اور اس کے رسولوں کا اور قیامت کے دن کا تو وہ شخص بڑی دور کی گمراہی میں جا پڑا'),
(630, 4, 137, 'بے شک وہ لوگ جو ایمان لائے پھر کفر کیا پھر ایمان لائے پھر کفر کیا پھر کفر میں بڑھتے رہے تو الله ان کو ہرگز نہیں بخشے گا اور نہ انہیں راہ دکھائے گا'),
(631, 4, 138, 'منافقوں کو خوشخبری سنا دے کہ ان کے واسطے دردناک عذاب ہے'),
(632, 4, 139, 'وہ جو مسلمانوں کو چھوڑ کر کافروں کو اپنا دوست بناتے ہیں کیا ان کے ہاں سے عزت چاہتے ہیں سو ساری عزت الله ہی کے قبضہ میں ہے'),
(633, 4, 140, 'اور الله نے تم پر قرآن میں حکم اتارا ہے کہ جب تم الله کی آیتوں پر انکار اور مذاق ہوتا سنو تو ان کے ساتھ نہ بیٹھو یہاں تک کہ کسی بات میں مشغول ہوں ورنہ تم بھی ان جیسے ہو جاؤ گے اور الله منافقوں اور کافروں کو دوزخ میں ایک ہی جگہ اکھٹا کرنے والا ہے'),
(634, 4, 141, 'وہ منافق جو تمہارے متعلق انتظار کرتے ہیں پھر اگر تمہیں الله کی طرف سے فتح ہو تو کہتے ہیں کیا ہم تمہارے ساتھ نہ تھےاور اگر کافرو ں کو کچھ حصہ مل جائے تو کہتے ہیں کیا ہم تم پر غالب نہ آنے لگے تھے اور کیا ہم نے تمہیں مسلمانوں سے بچا نہیں لیا سو الله تمہارا اور ان کا قیامت میں فیصلہ کرے گا اور (وہاں) الله کافروں کو مسلمانوں کے مقابلہ میں ہرگز غالب نہیں کرے گا'),
(635, 4, 142, 'منافق الله کو فریب دیتے ہیں اور وہی ان کو فریب دے گا اور جب وہ نماز میں کھڑے ہوتے ہیں تو سست بن کر کھڑے ہوتے ہیں لوگو ں کو دکھا تے ہیں اور الله کو بہت کم یاد کرتے ہیں'),
(636, 4, 143, 'کفر اور ایمان کے درمیان ڈانوں ڈول ہیں نہ پورے اس طرف ہیں اور نہ پورے اس طرف اور جسے الله گمراہ کر دے تو اس کے واسطے ہرگزکہیں راہ نہ پائے گا'),
(637, 4, 144, 'اے ایمان والو مسلمانوں کو چھوڑ کر کافروں کو اپنا دوست نہ بناؤ کیا تم اپنے اوپر الله کا صریح الزام لینا چاہتے ہو'),
(638, 4, 145, 'بے شک منافق دوزخ کے سب سےنیچے درجہ میں ہوں گے تو ان کے واسطے کوئی مددگار ہرگز نہ پائے گا'),
(639, 4, 146, 'مگر جنہوں نے توبہ کی اور اپنی اصلاح کی اور الله کو مضبوط پکڑا اور اپنے دین کو خالص الله ہی کے لیے کیاتو وہ لوگ ایمان والوں کے ساتھ ہیں اور الله جلدی ایمان والوں کو بہت بڑا ثواب دے گا'),
(640, 4, 147, ') اے منافقو) الله تمہیں سزادے کر کیا کرے گا اگر تم شکر گزار بنو اور ایمان لے آؤ اور الله قدردان جاننے والا ہے'),
(641, 4, 148, 'الله کو کسی کی بڑی بات کا ظاہر کرنا پسند نہیں مگر جس پر ظلم ہواہو اور الله سننے والا جاننے والا ہے'),
(642, 4, 149, 'اور اگرتم نیک کام اعلانیہ کر ویا اسے خفیہ کرو یا کسی برائی کو معاف کردو تو الله بڑا معاف کرنے والا قدرت والا ہے'),
(643, 4, 150, 'بے شک جو لوگ الله اور اس کے رسولوں کے ساتھ کفر کرتے ہیں اور چاہتے ہیں کہ الله اوراس کے رسولوں کے درمیان فرق رکھیں اور کہتے ہیں کہ ہم بعضوں پر ایمان لائے ہیں اور بعضوں کے منکر ہیں اور چاہتے ہیں کہ کفر اور ایمان کے درمیان ایک راہ نکالیں'),
(644, 4, 151, 'ایسے لوگ یقیناً کافر ہیں اور ہم نے کافرو ں کے واسطے ذلت کا عذاب تیار کر رکھا ہے'),
(645, 4, 152, 'اور جو لوگ الله پر ایمان لائے اور رسولوں پر ان میں سے کسی کو جدا نہ کیاان لوگوں کو الله جلدان کے ثواب دے گا اور الله بخشنے والا مہربان ہے'),
(646, 4, 153, 'اہلِ کتاب تجھ سےدرخواست کرتے ہیں کہ تو ان پر آسمان سے لکھی ہوئی کتب اتار لائے سو موسیٰ سے اس سے بڑی چیز مانگ چکے ہیں اور کہا ہمیں الله کو بالکل سامنے لا کر دکھا دے ان کے اس ظلم کے باعث ان پر بجلی ٹوٹ پڑی پھر بہت سی نشانیاں پہنچ چکنے کے بعد بچھڑے کو بنا لیا پھر ہم نے وہ بھی معاف کر دیا اور ہم نے موسیٰ کو بڑا رعب دیاتھا'),
(647, 4, 154, 'اور لوگوں پر طور اٹھا کر ان سے عہد لیا اور ہم نے کہا کہ دروازہ میں سجدہ کرتے ہوئےداخل ہو اور ہم نے کہا کہ ہفتے کے بارے میں زیادتی نہ کرو اور ہم نے ان سے پختہ عہد لیا'),
(648, 4, 155, 'پھر ان کی عہد شکنی پر اور الله کی آیتوں سے منکر ہونے پر اور پیغمبروں کا ناحق خون کرنے پر اور اس کہنے پرکہ ہمارے دلوں پرپر دے رہے ہیں انہیں سزا ملی پردے نہیں بلکہ الله نے ان کے دلوں پر کفر کے سبب سے مہر کر دی ہے سو ایمان نہیں لاتے مگر تھوڑے'),
(649, 4, 156, 'اور ان کے کفر او رمریم پر بڑا بہتان باندھنے کے سبب سے'),
(650, 4, 157, 'اور ان کے اس کہنے پر کہ ہم نے مسیح عیسیٰ مریم کے بیٹے کو قتل کیا جو الله کا رسول تھا حالانکہ انہوں نے نہ اسے قتل کیا اور نہ سولی پر چڑھایا لیکن ان کو اشتباہ ہو گیا اورجن لوگوں نے اس کے بارے میں اختلاف کیا ہے وہ بھی دراصل شک میں مبتلا ہیں ان کے پا س بھی اس معاملہ میں کوئی یقین نہیں ہے محض گمان ہی کی پیروی ہے انہوں نے یقیناً مسیح کو قتل نہیں کیا'),
(651, 4, 158, 'بلکہ اسے الله نے اپنی طرف اٹھا لیا اور الله زبردست حکمت والا ہے'),
(652, 4, 159, 'اور اہلِ کتاب میں کوئی ایسانہ ہوگا جواسکی موت سے پہلے اس پر ایمان نہ لائے گا اور قیامت کے دن وہ ان پر گواہ ہو گا'),
(653, 4, 160, 'سویہود کے گناہوں کے سبب سے ہم نے ان پر بہت سی پاک چیزیں حرام کر دیں جو ان پر حلا ل تھیں اور اس سبب سے الله کی راہ سے بہت روکتے تھے'),
(654, 4, 161, 'اور ان کو سود لینے کے سبب سے حالانکہ اس سے منع کیے گئے تھے اور اس سبب سے کہ لوگو ں کا مال ناحق کھاتے تھے اور ان میں سے جو کافر ہیں ہم نے ان کے لیے دردناک عذاب تیار کر رکھا ہے'),
(655, 4, 162, 'لیکن ان میں سے جو علم میں پختہ ہیں اور مسلمان ہیں سومانتے ہیں اس کو جو تجھ پر نازل ہوا اور جو تجھ سے پہلے نازل ہو چکا ہے اور نماز قائم کرنے والے اور زکواة دینے والے اور اللهاور قیامت پر ایمان لانے والے ہیں یہ وہ لوگ ہیں جنہیں ہم بڑا ثواب عطا فرمائیں گے'),
(656, 4, 163, 'ہم نے تیری طرف وحی بھیجی جیسی نوح پر وحی بھیجی اور ان نبیوں پر جو اس کے بعد آئے اور ابراھیمؑ اور اسماعیلؑ اور اسحاقؑ اور یعقوبؑ اور اس کی اولاد اور عیسیٰؑ اور ایوبؑ اوریونسؑ اور ھارونؑ اور سلیمانؑ پر وحی بھیجی اور ہم نے داؤدؑ کو زبور دی'),
(657, 4, 164, 'اور ایسے رسول بھیجے جن کا حال اس سے پہلےہم تمہیں سنا چکیں ہیں اور ایسے رسول جن کا ہم نے تم سے بیان نہیں کیا اور الله نے موسیٰ سے خاص طور پر کلام فرمایا'),
(658, 4, 165, 'ہم نے پیغمبر بھیجے خوشخبری دینے والے اور ڈرانے والے تاکہ ان لوگوں کا الله پر پیغمبرو ں کے بعد الزا م نہ رہے اور الله غالب حکمت والا ہے'),
(659, 4, 166, 'لیکن الله اس پر شاہد ہے جو تم پر نازل کیا کہ اسے اپنے علم سے نازل کیا اور فرشتے بھی گواہ ہیں اورالله گواہی دینے والا کافی ہے'),
(660, 4, 167, 'بے شک جو لوگ کافر ہوئے اور الله کی راہ سے روکا وہ بڑی دور کی گمراہی میں جا پڑے'),
(661, 4, 168, 'بے شک جو لوگ کافر ہوئے اور ظلم کیا الله انہیں کبھی نہیں بخشے گا اور نہ ان کو سیدھی راہ دکھائے گا'),
(662, 4, 169, 'مگر دوزخ کی راہ جس میں وہ ہمیشہ رہیں گے اور الله پر یہ آسان ہے'),
(663, 4, 170, 'اے لوگو! تمہارے پاس تمہارے رب کی طرف سے ٹھیک بات لے کر رسول آ چکا سو مان لو تاکہ تمہارا بھلا ہو اور اگر انکار کرو گے تو الله ہی کا ہے جو کچھ آسمانوں اور زمین میں ہے اور الله سب کچھ جاننے والا حکمت والا ہے'),
(664, 4, 171, 'اے اہِل کتاب تم اپنے دین میں حد سے نہ نکلو اور الله کی شان میں سوائے پکی بات کے نہ کہو بے شک مسیح عیسیٰ مریم کا بیٹا الله کا رسول ہے اور الله کا ایک کلمہ ہےجسے الله نے مریم تک پہنچایا اور الله کی طرف سے ایک جان ہے سو الله پر اور اس کے سب رسولوں پر ایمان لاؤ اور نہ کہو کہ خدا تین ہیں اس بات کو چھوڑ دو تمہارے لیے بہتر ہو گا بے شک الله اکیلا معبود ہے وہ اس سے پاک ہے اس کی اولاد ہو اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور الله کارساز کافی ہے'),
(665, 4, 172, 'مسیح خدا کا بندہ بننے سے ہرگز عار نہیں کرے گا اور نہ مقرب فرشتے اور جو کوئی اس کی بندگی سے انکار کرے گا اور تکبر کرے گا پھران سب کو اپنی طرف اکھٹاکرے گا'),
(666, 4, 173, 'پھر جو لوگ ایمان لائے ہوں گے اور اچھے کام کیے ہوں گے انہیں تو ان کا پورا ثواب دے گا اور انہیں اپنے فضل سے زیادہ دے گا اور جن لوگوں نے انکار کیا اورتکبر کیا انہیں درد دینے والا عذاب دے گا اور وہ الله کے سوا اپنے واسطے کوئی دوست اور مددگار نہیں پائیں گے'),
(667, 4, 174, 'اے لوگو! تمہارے پاس تمہارے پروردگار کی طرف سے ایک دلیل آ چکی ہے اور ہم نےتمہاری طرف ایک ظاہر روشنی اتاری ہے'),
(668, 4, 175, 'سو جو لوگ الله پر ایمان لائے اور انھوں نے الله کومظبوط پکڑا انہیں الله اپنی رحمت اور اپنے فضل میں داخل کرے گا اور اپنے تک ان کو سیدھا راستہ دکھائے گا'),
(669, 4, 176, 'تجھ سے حکم دریافت کرتے ہیں کہہ دو الله تمہیں کلالہ کے بارے میں حکم دیتا ہے اگر کوئی شخص مر جائے جس کی اولاد نہ ہو اور اس کی ایک بہن ہو تو اسے اس کے تمام ترکہ کانصف ملے گا اور وہ شخص اس بہن کا وارث ہو گا اگر اس کی کوئی اولاد نہ ہو اور اگر دو بہنیں ہوں تو انہیں کل ترکہ میں سے دو تہائی ملے گا اور اگر چند وارث بھائی بہن ہوں مرد اور عورت تو ایک مرد کو دو عورتوں کے حصہ کے برابر ملے گا الله تم سے اس لیے بیان کرتا ہے کہ تم گمراہ نہ ہو جاؤ اور الله ہر چیز کو جاننے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'اے ایمان والو! عہدوں کوپورا کرو تمہارے لیے چوپائے مویشی حلال ہیں سوائے ان کے جو تمہیں آگے سنائے جائیں گے مگر شکار کو احرام کی حالت میں حلال نہ جانو الله جو چاہے حکم دیتا ہے'),
(671, 5, 2, 'اے ایمان والو! الله کی نشانیوں کو حلال نہ سمجھو اور نہ حرمت والے مہینے کو اور نہ حرم میں قربانی ہونے والے جانور کو اور نہ ان جانوروں کو جن کے گلے میں پٹے پڑے ہوئے ہوں اور نہ حرمت والے گھر کی طرف آنے والوں کو جو اپنے رب کا فضل اور اس کی خوشی ڈھونڈتے ہیں اور جب تم احرام کھول دو پھر شکار کرو اور تمہیں اس قوم کی دشمنی جو کہ تمہیں حرمت والی مسجد سے روکتی تھی اس بات کا باعث نہ بنے کہ زیادتی کرنے لگو اور آپس میں نیک کام اور پرہیز گاری پر مدد کرو اورگناہ اور ظلم پر مدد نہ کرو اور الله سے ڈرو بے شک الله سخت عذاب دینے والا ہے'),
(672, 5, 3, 'تم پر مردار اور لہو اور سور کا گوشت حرام کیا گیا ہے اور وہ جانور جس پر الله کے سوا کسی اور کا نام پکارا جائے جو گلا گھوٹ کر یا چوٹ سے یا بلندی سے گر کر یا سینگ مارنے سے مر گیا ہو اور وہ جسے کسی درندے نے پھاڑ ڈالا ہو مگر جسے تم نے ذبح کر لیا ہو اور وہ جو کسی تھان پر ذبح کیا جائے اور یہ کہ جوئے کے تیروں سے تقسیم کرو یہ سب گناہ ہیں آج تمہارے دین سے کافر نا امید ہو گئے سو ان سے نہ ڈرو اور مجھ سے ڈرو آج میں تمہارے لیے تمہارا دین پورا کر چکا اور میں نے تم پر اپنا احسان پورا کر دیا اور میں نے تمہارے واسطے اسلام ہی کو دین پسند کیا ہے پھر جو کوئی بھوک سے بیتاب ہو جائے لیکن گناہ پر مائل نہ ہو تو الله معاف کرنے والا مہربان ہے'),
(673, 5, 4, 'تجھ سے پوچھتے ہیں کہ ان کے لیے کیا چیز حلال ہے کہہ دو تمہارے واسطے سب پاکیزہ چیزیں حلا ل کی گئی ہیں اور جو شکاری جانور جسے شکار پر دوڑنے کی تعلیم دو کہ انہیں سکھاتے ہو اس میں سے جو الله نے تمہیں سکھایا ہے سو اس میں سے کھاؤ جو وہ تمہارے لیے پکڑ رکھیں اور اس پر الله کا نا م لو اور الله سے ڈرتے رہو بیشک الله جلد حساب لینے والا ہے'),
(674, 5, 5, 'آج تمہارے واسطے سب پاکیزہ چیزیں حلا کی گئی ہیں اور اہلِ کتاب کا کھانا تمہیں حلال ہے اور تمہارا کھانا انہیں حلال ہے اور تمہارے لیے پاک دامن مسلمان عورتیں حلال ہیں اوران میں سے پاک دامن عورتیں جنہیں تم سے پہلے کتاب دی گئی ہےجب ان کے مہر انہیں دے دو ایسے حال میں کہ نکاح میں لانے والے ہو نہ بدکاری کرنے والے اورنہ خفیہ آشنائی کرنے والے اورجوایمان سے منکر ہوا تو اس کی محنت ضائع ہوئی اور وہ آخرت میں نقصان اٹھانے والوں میں سے ہو گا'),
(675, 5, 6, 'اے ایمان والو! جب تم نماز کے لیے اٹھو تو اپنے منہ دھو لو اور ہاتھ کہنیوں تک اور اپنے سروں پر مسح کرو اور اپنے پاؤں ٹخنوں تک دھو لو اور اگر تم ناپاک ہو تو نہا لو اور اگرتم بیمار ہو یا سفر پر ہو یا کوئی تم میں سے جائے ضرور سے آیا ہو یا عورتوں کے پاس گئے ہو پھر تم پانی نہ پاؤ تو پاک مٹی سے تیمم کر لو اور اسے اپنے مونہوں او رہاتھوں پر مل لو الله تم پر تنگی کرنا نہیں چاہتا لیکن تمہیں پاک کرنا چاہتا ہے اور تاکہ اپنا احسان تم پر پورا کرے تاکہ تم شکر کرو'),
(676, 5, 7, 'اور الله کا انعام جو تم پر ہوا ہے اسے یاد کرو اور اس کا عہد جس کا تم سے معاہدہ کیا ہے جب تم نے کہا تھا کہ ہم نے سنا اور مان لیا اور الله سے ڈرتے رہو الله دلو ں کی بات خوب جانتا ہے'),
(677, 5, 8, 'اے ایمان والو! الله کے واسطے انصاف کی گواہی دینے کے لیے کھڑے ہو جاؤ اور کسی قوم کی دشمنی کا باعث انصاف کو ہر گز نہ چھوڑو انصاف کرو یہی بات تقویٰ کے زیادہ نزدیک ہے اور الله سے ڈرتے رہو جو کچھ تم کرتے ہو بے شک الله اس سے خبردارہے'),
(678, 5, 9, 'الله نے ایمان والوں سے اور جو نیک کام کرتے ہیں بخشش اور بڑے اجر کا وعدہ کیا ہے'),
(679, 5, 10, 'اور جن لوگو ں نے کفر کیا اور ہماری آیتیں جھٹلائیں وہ دوزخی ہیں'),
(680, 5, 11, 'اے ایمان والو! الله کا احسان اپنے اوپر یاد کرو جب لوگو ں نے ارادہ کیا کہ تم پر دست درازی کریں پھر الله نے ان کے ہاتھ تم پر اٹھنے سےروک دیئے اور الله سے ڈرتے رہو اور ایمان والوں کو الله ہی پر بھروسہ کرنا چاہیے'),
(681, 5, 12, 'اور الله نے بنی اسرائیل سے عہد لیا تھا اور ہم نے ان میں سے بارہ سردار مقرر کیے اور الله نےکہا میں تمہارے ساتھ ہوں اگر تم نماز کی پابندی کرو گے اور زکواة دیتے رہو گے اور میرے سب رسولوں پر ایمان لاؤ گے اور ان کی مدد کرو گے اور الله کواچھے طور پر قرض دیتے رہو گے تو میں ضرور تمہارے گناہ تم سےدور کردوں گا اور تمہیں باغوں میں داخل کروں گا جن کے نیچے نہریں بہتی ہیں پھر جوکوئی تم میں سے اس کے بعد کافر ہوا وہ بے شک سیدھے راستے سے گمراہ ہوا'),
(682, 5, 13, 'پھر ان کی عہد شکنی کے باعث ہم نے ان پر لعنت کی اوران کے دلوں کوسخت کر دیا وہ لوگ کلام کو ا سکے ٹھکانے سے بدلتے ہیں اور اس نصیحت سے نفع اٹھانا بھول گئے جو انہوں کی گئی تھی اور تو ہمشیہ ان کی کسی نہ کسی خیانت پر اطلاع پاتا رہے گا مگر تھوڑے ان میں سے سو انہیں معاف کر اور درگزر کر بے شک الله نیکی کرنے والوں کو پسند کرتا ہے'),
(683, 5, 14, 'اورجو لوگ اپنے آپ کو نصاریٰ کہتے ہیں ان سے بھی ہم نے عہد لیا تھا پھر وہ اس نصیحت سے نفع اٹھانا بھول گئے جو انہیں کی گئی تھی پھر ہم نے ان کے درمیان ایک دوسرے دشمنی اور بغض قیامت تک کے لیے ڈال دیا اور الله ان کا کیا ہوا انہیں جتلا دے گا'),
(684, 5, 15, 'اے اہلِ کتاب تحقیق تمہارے پاس ہمارا رسول آیا ہے جو بہت سی چیزیں تم پر ظاہر کرتا ہے جنہیں تم کتاب سے چھاپتے تھے اوربہت سی چیزوں سے درگزرکرتا ہے بے شک تمہارے پاس الله کی طرف سے روشنی اور واضح کتاب آئی ہے'),
(685, 5, 16, 'الله سلامتی کی راہیں دکھاتا ہے اسے جو اس کی رضا کا تابع ہو اور انہیں اپنے حکم سے اندھیروں سے روشنی کی طرف نکالتا ہے اور انہیں سیدھی راہ پر چلاتا ہے'),
(686, 5, 17, 'بے شک وہ کافر ہوئے جنہوں نے کہا الله تو وہی مسیح مریم کا بیٹا ہے کہہ دے پھر الله کے سامنے کس کا بس چل سکتا ہے اگر وہ چاہے کہ مسیح مریم کے بیٹے اور اس کی ماں اور جتنے لوگ زمین میں ہیں سب کو ہلاک کر دے اور آسمانوں اور زمین اور ان دونوں کے درمیان کی سلطنت الله ہی کے واسطے ہے جو چاہے پیدا کرتا ہے اور الله ہر چیز پر قادر ہے'),
(687, 5, 18, 'اوریہود اور نصاریٰ کہتے ہیں کہ ہم الله کے بیٹے اور اس کے پیارے ہیں کہہ دو پھر تمہارے گناہوں کے باعث وہ تمہیں کیوں عذاب دیتا ہے بلکہ تم بھی اور مخلوقات کی طرح ایک آدمی ہو جسے چاہے بخش دے اور جسے چاہے سزا دے اور آسمانوں اور زمین اوران دونوں کے درمیان کی سلطنت الله ہی کے لیے ہے اور اسی کی طرف لوٹ کر جانا ہے'),
(688, 5, 19, 'اے اہل کتاب تحقیق تمہارے پاس ہمارا پیغمبر آ یا جو تمہیں صاف صاف بتلاتا ہے ایسے وقت میں رسولوں کا سلسلہ موقوف تھا تاکہ تم یوں نہ کہنے لگو کہ ہمارے پاس کوئی خوشبخبری دینے والا اور ڈرانے والانہیں آیاسوتمہارےپاس خوشخبری دینے والااورڈرانےوالاآ گیا ہے اور الله ہر چیز پر قادر ہے'),
(689, 5, 20, 'اور جب موسیٰ نے اپنی قوم سے کہاکہ اے میری قوم الله کا احسان اپنے اوپر یاد کرو جب کہ تم میں نبی پیدا کیے اور تمہیں بادشاہ بنایا اور تمہیں وہ دیا جو جہان میں کسی کو نہ دیا تھا'),
(690, 5, 21, 'اے میری قوم اس پاک زمین میں داخل ہو جاؤ جو الله نے تمہارے لیے مقرر کر دی اور پیچھے نہ ہٹو ورنہ نقصان میں جا پڑو گے'),
(691, 5, 22, 'انہوں نے کہا اے موسیٰ بے شک وہاں ایک زبردست قوم ہے اور ہم وہاں ہرگز نہ جائیں گے یہاں تک کہ وہ وہاں سے نکل جائیں پھراگروہ وہاں سےنکل جائیں تو ہم ضرور داخل ہو ں گے'),
(692, 5, 23, 'الله سے ڈرنے والوں میں سے دو مردوں نے کہا جن پر الله کا فضل تھا کہ ان پر حملہ کر کے دروازہ میں گھس جاؤ پھر جب تم اس میں گھس جاؤ گے تو تم ہی غالب ہو گے اور الله پر بھروسہ رکھو اگر تم ایمان دار ہو'),
(693, 5, 24, 'کہا اے موسیٰ ہم کبھی وہاں داخل نہیں ہو ں گے جب تک کہ وہ اس میں ہیں سو تو اور تیرا رب جائے اور تم دونوں لڑو ہم تو یہیں بیٹھیں ہیں'),
(694, 5, 25, 'موسیٰ نے کہا اے میرے رب میرے اختیار میں تو سوائے میری جان اور میرے بھائی کے اور کوئی نہیں سو ہمارے درمیان اور اس نافرمان قوم کے درمیان جدائی ڈال دے'),
(695, 5, 26, 'فرمایا تحقیق وہ زمین ان پر چالیس بر س حرام کی گئی ہے اس ملک میں سرگرداں پھریں گے سو تو نافرمان قوم پر افسوس نہ کر'),
(696, 5, 27, 'تو اہلِ کتاب کو آدم کے دو بیٹوں کا قصہ صحیح طور پر پڑھکر سنا دے جب ان دونوں نے قربانی کی ان میں سے ایک کی قربانی قبول ہو گئی اور دوسرے کی نہ ہوئی اس نے کہا میں تجھے مار ڈالوں گا اس نے جواب دیا الله پرہیز گاروں ہی سے قبول کرتا ہے'),
(697, 5, 28, 'اگرتو مجھے قتل کرنے کے لیے ہاتھ اٹھائے گا تو میں تجھے قتل کرنے کے لیے ہاتھ نہ اٹھاؤں گا میں الله رب العالمین سے ڈرتا ہوں'),
(698, 5, 29, 'میں چاہتا ہوں کہ میرا اور اپنا گناہ تو ہی سمیٹ لے اور دوزخی بن جائے اور ظالموں کی یہی سزا ہے'),
(699, 5, 30, 'پھر اسے اس کے نفس نے اپنے بھائی کے خون پر راضی کر لیا پھر اسے مار ڈالا پس وہ نقصان اٹھانے والوں میں سے ہو گیا'),
(700, 5, 31, 'پھر الله نے ایک کوا بھیجا جو زمین کریدتا تھا تاکہ اسے دکھلائے کہ اپنے بھائی کی لاش کو کس طرح چھپاتا ہے اس نے کہا افسوس مجھ پر اس کوے جیسا بھی نہ ہو سکا کہ اپنے بھائی کی لاش چھپانے کی تدبیر کرتا پھر پچھتانے لگا'),
(701, 5, 32, 'اس سبب سے ہم نے بنی اسرائیل پر لکھا کہ جس نے کسی انسان کو خون کے بدلے یا زمین میں فساد پھیلانے کے سوا کسی اور وجہ سے قتل کیا گویا اس نے تمام انسانوں کو قتل کر دیا اورجس نے کسی کو زندگی بخشی اس نے گویا تمام انسانوں کی زندگی بخشی اورہمارے رسولوں ان کے پاس کھلے حکم لا چکے ہیں پھر بھی ان میں سے بہت لوگ زمین میں زیادتیاں کرنے والے ہیں'),
(702, 5, 33, 'ان کی بھی یہی سزا ہے جو الله اوراس کے رسول سے لڑتے ہیں اورملک میں فساد کرنے کو دوڑتے ہیں یہ کہ ان کو قتل کیا جائے یا وہ سولی چڑھائے جائیں یا ان کے ہاتھ اور پاؤں مخالف جانب سے کاٹے جائیں یا وہ جلا وطن کر دیے جائیں یہ ذلت ان کے لیے دنیا میں ہے اور آخرت میں ان کے لیے بڑا عذاب ہے'),
(703, 5, 34, 'مگر جنہوں نے تمہارے قابو پانے سے پہلے توبہ کر لی تو جان لو کہ الله بخشنے والا مہربان ہے'),
(704, 5, 35, 'اے ایمان والو الله سے ڈرو اور الله کا قرب تلاش کرو اور الله کی راہ میں جہاد کرو تاکہ تم کامیاب ہو جاؤ'),
(705, 5, 36, 'بے شک جو لوگ کافر ہیں اگر ان کے پاس دنیا بھر کی چیزیں ہوں اور اس کے ساتھ اتنا ہی اور ہو تاکہ قیامت کے عذاب سے بچنے کے لیے بدلہ میں دیں تو بھی ان سے قبول نہ ہوگا اوران کے لیے دردناک عذاب ہے'),
(706, 5, 37, 'وہ چاہیں گے ُکہ آگ سے نکل جائیں حالانکہ وہ اس سے نکلنے والے نہیں اور ان کے لیے دائمی عذاب ہے'),
(707, 5, 38, 'اور چور خواہ مرد ہو یا عورت دونوں کے ہاتھ کاٹ دو یہ ان کی کمائی کا بدلہ اور الله کی طرف سے عبرت ناک سزا ہے اور الله غالب حکمت والا ہے'),
(708, 5, 39, 'پھر جس نے اپنے ظلم کے بعد توبہ کی اور اصلاح کر لی تو الله اس کی توبہ قبول کر لے گا بے شک الله بخشنے والا مہربان ہے'),
(709, 5, 40, 'کیا تجھے معلوم نہیں کہ آسمانوں اور زمین کی سلطنت الله ہی کے واسطے ہے وہ جسے چاہے عذاب دے اور جسے چاہے بخش دے اور الله سب چیزوں پر قادر ہے'),
(710, 5, 41, 'اے رسول انکا غم نہ کر جو دوڑ کر کفر میں گرتے ہیں وہ لوگ جو اپنے منہ سے کہتے ہیں کہ ہم مومن ہیں حالانکہ ان کے دل مومن نہیں ہیں اور وہ جو یہودی ہیں جھوٹ بولنے کے لیے جاسوسی کرتے ہیں وہ دوسری جماعت کے جاسوس ہیں جو تجھ تک نہیں آئی بات کو اس کےٹھکانے سے بدل دیتے ہیں کہتے ہیں کہ تمہیں یہ حکم ملے تو قبول کر لینا اور اگر یہ نہ ملے تو بچتے رہنا اور جسے الله گمراہ کرنا چاہے سو تو الله کے ہاں ا سکے لیے کچھ نہیں کر سکتا یہ وہی لوگ ہیں جن کے دل پاک کرنے کا الله نے ارادہ نہیں کیا ان کے لیے دنیا میں ذلت ہے اور آخرت میں بڑا عذا ب ہے'),
(711, 5, 42, 'جھوٹ بولنے کے لیے جاسوسی کرنے والے ہیں اور بہت حرام کھانے والے ہیں سو اگر وہ تیرے پاس آئیں تو ان میں فیصلہ کر دے یا ان سے منہ پھیر لے اور اگر تو ان سے منہ پھیر لے گا تو وہ تیرا کچھ نہ بگاڑ سکیں گے اور اگر تو فیصلہ کرے تو ان میں انصاف سے فیصلہ کر بے شک الله انصاف کرنے والوں کو دوست رکھتا ہے'),
(712, 5, 43, 'اور وہ تجھے کس طرح منصف بنائیں گے حالانکہ ان کے پاس تو تورات ہے جس میں الله کا حکم ہے پھر اس کے بعد ہٹ جاتے ہیں اور یہ مومن نہیں ہیں'),
(713, 5, 44, 'ہم نے تورات نازل کی کہ اس میں ہدایت اور روشنی ہے اس پر پیغمبر جو الله کے فرمانبردار تھے یہود کو حکم کرتے تھے اور اس کی خبر گیری پر مقرر تھے سوتم لوگو ں سے نہ ڈرو اور مجھ سے ڈرو اور میری آیتوں کے بدلے میں تھوڑا مول مت لو اور جو کوئی اس کے موافق فیصلہ نہ کر لے جو الله نے اتارا تو وہی لوگ کافر ہیں'),
(714, 5, 45, 'اور ہم نے ان پراس کتاب میں لکھا تھا کہ جان بدلے جان کے اور آنکھ بدلے آنکھ کے اور ناک بدلے ناک کے اور کان بدلے کان کے اور دانت بدلے دانت کے اور زخموں کا بدلہ ان کے برابر ہے پھر جس نے معاف کر دیا تو وہ گناہ سے پاک ہو گیا اور جو کوئی اس کے موافق حکم نہ کرے جو الله نے اتارا سو وہی لوگ ظالم ہیں'),
(715, 5, 46, 'اور ہم نے ان کے پیچھے ان ہی کے قدموں پر عیسیٰ مریم کے بیٹے کو بھیجا جو اپنے سے پہلی کتاب تورات کی تصدیق کرنے والا تھا اور ہم نے اسے انجیل دی جس میں ہدایت اور روشنی تھی اپنے سے پہلی کتاب تورات کی تصدیق کرنے والا تھا اور راہ بتانے والی اور ڈرنے والوں کیلئے نصیحت تھی'),
(716, 5, 47, 'اور چاہیئے کہ ابخیل والے اس کے موافق حکم کریں جو الله نےاس میں اتارا ہے اور جو چیز الله نے اتاری ہے جو شخص اس کے موافق حکم نہ کرے سو وہی لوگ نافرمان ہیں'),
(717, 5, 48, 'ہم نے تجھ پر سچی کتاب اتاری جو اپنے سے پہلی کتابوں کی تصدیق کرنے والی ہے اور ان کے مضامین پر نگہبانی کرنے والی ہے سو تو ان میں اس کے موافق حکم کر جو الله نے اتارا ہے اور جو حق تیرے پاس آیا ہے اس سے منہ موڑ کر ان کی خواہشات کی پیروی نہ کر ہم نے تم میں سے ہر ایک کے لیے ایک شریعت اور واضح راہ مقرر کر دی ہے اور اگر الله چاہتا تو سب کو ایک ہی امت کر دیتا لیکن وہ تمہیں اپنے دیے ہوئے حکموں میں آزمانا چاہتا ہے لہذا نیکیوں میں ایک دوسرے سے بڑھنے کی کوشش کرو تو سب کو الله کے پاس پہنچنا ہے پھر تمہیں جتائے گا جس میں تم اختلاف کرتے تھے'),
(718, 5, 49, 'اور فرمایا کہ تو ان میں اس کے موافق حکم کر جو الله نے تارا ہے اوران کی خواہشوں کی پیروی نہ کر اوران سے بچتا رہ کہ تجھے کسی ایسے حکم سے بہکا نہ دیں جو الله نے تجھ پر اتارا ہے پھر اگر یہ منہ موڑیں تو جان لو کہ الله کا اردہ انہیں ان کے بعض گناہوں کی پاداش میں مصیبت میں مبتلا کرنے کا ہے اور لوگوں میں بہت سے نافرمان ہیں'),
(719, 5, 50, 'تو کیا پھر جاہلیت کا فیصلہ چاہتے ہیں حالانکہ جو لوگ یقین رکھنے والے ہیں ان کے ہاں الله سے بہتر اور کوئی فیصلہ کرنے والا نہیں'),
(720, 5, 51, 'اے ایمان والو یہود اور نصاریٰ کو دوست نہ بناؤ وہ آپس میں ایک دوسرے کے دوست ہیں اور جو کوئی تم میں سے ان کے ساتھ دوستی کرے تو وہ ان میں سے ہے الله ظالموں کو ہدایت نہیں کرتا'),
(721, 5, 52, 'پھر تو ان لوگوں کو دیکھے گا جن کے دلوں میں بیماری ہے ان میں دوڑ کر جا ملتے ہیں کہتے ہیں کہ ہمیں ڈر ہے کہ ہم پر زمانے کی گردش نہ آ جائے سو قریب ہے کہ الله جلدی فتح ظاہر فرماد ے یا کوئی اور حکم اپنے ہاں سے ظاہر کرے پھر یہ اپنے دل کی چھپی ہوئی بات پر شرمندہ ہوں گے'),
(722, 5, 53, 'اور مسلمان کہتے ہیں کیا یہ وہی لوگ ہیں جو الله کے نام کی پکی قسمیں کھاتے تھے کہ ہم تمہارے ساتھ ہیں ان کے اعمال برباد ہو گئے پھر وہ نقصان اٹھانے والے ہو گئے'),
(723, 5, 54, 'اے ایمان والو جو کوئی تم میں سے اپنے دین سے پھر جائے گا تو عنقریب الله ایسی قوم کو لائے گا کہ الله ان کو چاہتا ہے اور وہ اس کو چاہتے ہیں مسلمانوں پر نرم دل ہوں گے اور کافروں پر زبردست الله کی راہ میں لڑیں گے اور کسی کی ملامت سے نہیں ڈریں گے یہ الله کا فضل ہے جسے چاہے دیتا ہے اور الله کشائش والا جاننے والا ہے'),
(724, 5, 55, 'تمہارا دوست تو الله اور اس کا رسول اور ایمان دار لوگ ہیں جو نماز قائم کرتے ہیں اور زکواة دیتے ہیں اور وہ عاجزی کرنے والے ہیں'),
(725, 5, 56, 'اور جو شخص الله اور اس کے رسول اور ایمان داروں کو دوست رکھے تو الله کی جماعت وہی غالب ہونے والی ہے'),
(726, 5, 57, 'اے ایمان والو! ان لوگوں کو اپنا دوست نہ بناؤ جنہوں نے تمہارے دین کو ہنسی اور کھیل بنا رکھا ہے ان لوگو ں میں سے جنہیں تم سے پہلے کتاب دی گئی اور کافروں کو اور الله سے ڈرو اگر تم ایمان دار ہو'),
(727, 5, 58, 'اور جب تم نماز کے لیے پکارتے ہو تو وہ لوگ اس کے ساتھ ہنسی اور کھیل کرتے ہیں یہ ا س واسطے کہ وہ لوگ بے عقل ہیں'),
(728, 5, 59, 'کہہ دو اے اہلِ کتاب تم ہم میں کون سا عیب پاتے ہو بجز اس کے کہ ہم الله پر ایمان لائے ہیں اوراس پر جو ہمارے پاس بھیجی گئی ہے اور اس پر جو پہلے بھیجی جا چکی ہے باوجود اس کے تم میں اکثر لوگ نافرمان ہیں'),
(729, 5, 60, 'کہہ دو میں تم کو بتلاؤں الله کے ہاں ان میں سے کس کی بری جزا ہے وہی جس پر الله نے لعنت کی اور اس پر غضب نازل کیا اور بعضوں کو ان میں سے بندربنا دیا اور بعضوں کو سور اورجنہوں نے شیطان کی بندگی کی وہی لوگ درجہ میں بدتر ہیں اور راہِ راست سے بھی بہت دور ہیں'),
(730, 5, 61, 'اور جب تمہارے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان لائے حالانکہ وہ کافر ہی آئے تھے اور کافر ہی گئے اور الله خوب جانتا ہے جو کچھ وہ چھپاتے تھے'),
(731, 5, 62, 'اور تو ان میں سے اکثر کو دیکھے گا کہ گناہ اور ظلم پر اور حرام کھانے پر دوڑتے ہیں بہت برا ہے جو کچھ وہ کر رہے ہیں'),
(732, 5, 63, 'ان کے فقراء اور علماء گناہ کی بات کہنے اور حرام مال کھانے سے انہیں کیوں نہیں منع کرتے البتہ بری ہے وہ چیز جو وہ کرتے ہیں'),
(733, 5, 64, 'اور یہود کہتے ہیں الله کا ہاتھ بند ہوگیا ہے انہیں کے ہاتھ بند ہوں اور انہیں اس کہنے پر لعنت ہے بلکہ اس کے دونوں ہاتھ کھلے ہوئے ہیں جس طرح چاہے خرچ کرتا ہے جو کلام تیرے رب کی طرف سے تم پر نازل ہوا ہے وہ ان میں سے اکثر لوگوں کی سرکشی اور کفر میں زیادتی کا باعث بن گیا اور ہم نے ان کے درمیان قیامت تک عداوت اور دشمنی ڈال دی ہے جب کبھی لڑائی کے لیے آگ سلگاتے ہیں تو الله اس کو بجھا دیتا ہے یہ زمین میں فساد پھیلانے کی کوشش کرتے ہیں اور الله فساد کرنے والوں کو پسند نہیں کرتا'),
(734, 5, 65, 'اور اگر اہل کتاب ایمان لاتے اور ڈرتے تو ہم ان میں سے ان کی برائياں دور کر دیتے اور ضرور انہیں نعمت کے باغوں میں داخل کر تے'),
(735, 5, 66, 'اور اگر وہ تورات اور انجیل کوقائم رکھتے اور اس کو جو ان پر ان کے رب کی طرف سے نازل ہوا ہے تو اپنے اوپر سے اور اپنے پاؤں کے نیچے سے کھاتے کچھ لوگ ان میں سیدھی راہ پر ہیں اور اکثر ان میں سے برے کام کر رہے ہیں'),
(736, 5, 67, 'اے رسول جو تجھ پر تیرے رب کی طرف سے اترا ہے اسے پہنچا دے اور اگر تو نے ایسا نہ کیا تو اس کی پیغمبری کا حق ادا نہیں کیا اور الله تجھے لوگوں سے بچائے گا بے شک الله کافروں کی قوم کو راستہ نہیں دکھاتا'),
(737, 5, 68, 'کہہ دو اے اہل کتاب تم کسی راہ پر نہیں ہو جب تک کہ تم تورات اور انجیل اور جو چیز تمہارے رب کی طرف سے نازل کی گئی ہے قائم نہ کرو اور ضرور ہے کہ یہ فرمان جو تم پرنازل ہوا ہے ان میں سے اکثر کی سرکشی اور انکار کو اور زيادہ بڑھائے گا مگر انکار کرنے والاوں کے حال پر کچھ افسوس نہ کرو'),
(738, 5, 69, 'بے شک جو مسلمان ہیں اور جو یہودی ہیں اور صائبی اور نصاریٰ جو کوئی الله اور قیامت پر ایمان لایا اور نیک کام کیے تو ان پر کوئی خوف نہیں ہوگا اور نہ وہ غمگین ہوں گے'),
(739, 5, 70, 'ہم نے بنی اسرائیل سے پختہ وعدہ لیا تھا اور ان کی طرف کئی رسول بھیجے تھے جب کبھی کوئی رسول ان کے پاس وہ حکم لایا جو ان کے نفس نہیں چاہتے تھے تو ایک جماعت کو جھٹلایا اور ایک جماعت کو قتل کر ڈالا'),
(740, 5, 71, 'اور یہی گمان کیا کہ کوئی فتنہ نہیں ہوگا پھر اندھے اور بہرے ہوئے پھر الله نے ان کی توبہ قبول کی پھر ان میں سے اکثر اندھے اور بہرے ہو گئے اور جو کچھ وہ کرتے ہیں الله دیکھتا ہے'),
(741, 5, 72, 'البتہ تحقیق وہ لوگ کافر ہوئے جنہوں نے کہا بے شک الله وہی مسیح مرین کا بیٹا ہی ہے حالانکہ مسیح نے کہا اے بنی اسرائیل اس الله کی بندگی کرو جو میرا اور تمہارا رب ہے بے شک جس نے الله کا شریک ٹھیرایا سو الله نے اس پر جنت حرام کی اور اس کا ٹھکانا دوزخ ہے اور ظالموں کا کوئی مددگار نہیں ہو گا'),
(742, 5, 73, 'جنہوں نے کہا الله تین میں سے ایک ہے بے شک وہ کافر ہوئے حالانکہ سوائے ایک معبود کے اورکوئی معبود نہیں اور اگر وہ اس بات سے باز نہ آئيں گے جو وہ کہتے ہیں تو ان میں سے کفر پر قائم رہنے والوں کو دردناک عذاب پہنچے گا'),
(743, 5, 74, 'الله کے آگے کیوں توبہ نہیں کرتے اور اس سے گناہ نہیں بخشواتے اور الله بخشنے والا مہربان ہے'),
(744, 5, 75, 'مسیح مریم کا بیٹا تو صرف ایک پیغمبر ہی ہے جس سے پہلے اور بھی پیغمبر گزر چکے ہیں اور اس کی ماں ولی ہے وہ دونوں کھانا کھاتے تھے دیکھ ہم انہیں کیسی دلیلیں بتلاتے ہیں پھر دیکھووہ کہاں الٹے جا تے ہیں'),
(745, 5, 76, 'کہہ دو تم الله کر چوڑ کر ایسی چیز کی بندگی کرتے ہو جو تمہارے نقصان اور نفع کے مالک نہیں اور الله وہی ہے سننے والا جاننے والا'),
(746, 5, 77, 'کہہ اے اہلِ کتاب تم اپنے دین میں ناحق زیادتی مت کرو اور ان لوگو ں کی خواہشات کی پیروی نہ کرو جو اس سے پہلے گمراہ ہو چکے اور انہوں نے بہتوں کو گمراہ کیا اور سیدھی راہ سے دور ہو گئے'),
(747, 5, 78, 'بنی اسرائیل میں سے جو کافر ہوئے ان پر داؤد اور عیسیٰ بیٹے مریم کی زبان پر لعنت کی گئی یہ اس لیے کہ وہ نافرمان تھے اور حد سے گزر گئے تھے'),
(748, 5, 79, 'آپس می ں برے کام سے منع نہ کرتے تھے جو وہ کر رہے تھے کیسا ہی برا کام ہےجووہ کرتے تھے'),
(749, 5, 80, 'تو دیکھے گا تو ان میں سے بہت سے لوگ کافروں سے دوستی رکھتے ہیں انہوں نے کیسا ہی برا سامان اپنے نفسوں کے لیے آگے بھیجا اور وہ یہ کہ ان پر الله کا غضب ہوا اور وہ ہمیشہ عذاب میں رہنے والے ہیں'),
(750, 5, 81, 'اور اگر وہ الله اور نبی پر اور اس چیز پر جواس کی طرف سے نازل کی گئی ہے ایمان لاتے تو کافروں کو دوست نہ بناتے لیکن ان میں سے اکثر لوگ نافرمان ہیں'),
(751, 5, 82, 'تو سب لوگو ں سے زیادہ مسلمانوں کا دشمن یہودیوں اور مشرکوں کو پائے گا اور تو سب سے نزدیک محبت میں مسلمانوں سے ان لوگوں کو پائے گا جو کہتے ہیں کہ ہم نصاریٰ ہیں یہ اس لیے اکہ ان میں علماء اور فقراء ہیں اور اس لیے کہ وہ تکبر نہیں کرتے'),
(752, 5, 83, 'اور جب اس چیز کو سنتے ہیں جو رسول پر اتری تو ان کی آنکھوں کو دیکھے گا کہ آنسوؤں سے بہتی ہیں اس لیے کہ انہوں نے حق کو پہچان لیا کہتے ہیں اے رب ہمارے کہ ہم ایمان لائے تو ہمیں ماننے والوں کے ساتھ لکھ لے'),
(753, 5, 84, 'اور ہمیں کیا ہے ہم الله پر ایمان نہ لائيں اور اس چیز پر جو ہمیں حق سے پہنچی ہے اور اس کی طمع رکھتے ہیں کہ ہمیں ہمارا رب نیکو ں میں داخل کر ے گا'),
(754, 5, 85, 'پھر الله نے انہیں اس کہنے کے بدلے ایسے باغ دئیے کہ جن کے نیچے نہریں بہتی ہیں ان میں ہمیشہ رہیں گے اور نیکی کرنے والوں کا یہی بدلہ ہے'),
(755, 5, 86, 'اور وہ لوگ جو کافر ہوئے اور ہماری آیتوں کو جھٹلایا وہ دوزخ کے رہنے والے ہیں'),
(756, 5, 87, 'اے ایمان والو ان ستھری چیزوں کو حرام نہ کرو جو الله نے تمہارے لیے حلال کی ہیں اور حد سے نہ بڑھو بے شک الله حد سے بڑھنے والوں کو پسند نہیں کرتا'),
(757, 5, 88, 'اور الله کے رزق میں سے جو چیز حلال ستھری ہو کھاؤ اور الله سے ڈرو جس پر تم ایمان رکھتے ہو'),
(758, 5, 89, 'الله تمہیں تمہاری بی ہودہ قسموں پر نہیں پکڑتا لیکن ان قسموں پرپکڑتا ہے جنہیں تم مستحکم کر دو سو اس کا کفارہ دس مسکینوں کو اوسط درجہ کا کھانا دینا ہے جو تم اپنے گھر والوں کو دیتے ہو یا دس مسکینوں کو کپڑا پہنانا یا گردن آزاد کرنی پھر جو شخص یہ نہ پائے تو تین دن کے روزے رکھنے ہیں اسی طرح تمہای قسموں کا کفارہ ہے جب تم قسم کھاؤ اور اپنی قسموں کی حفاظت کرو اسی طرح تمہارے لیے اپنے حکم بیان کرتا ہے تاکہ تم شکر کرو'),
(759, 5, 90, 'اے ایمان والو شراب اور جوا اور بت اور فال کے تیر سب شیطان کے گندے کام ہیں سو ان سے بچتے رہو تاکہ تم نجات پاؤ'),
(760, 5, 91, 'شیطان تو یہی چاہتا ہے کہ شراب اور جوئے کے ذریعے سےتم میں دشمنی اور بغض ڈال دے اور تمہیں الله کی یاد سے اور نماز سے روکے سو اب بھی باز آجاؤ'),
(761, 5, 92, 'اور الله اور رسول کا حکم مانو اور بچتے رہو پھر اگر تم پھر جاوگےتوجان لو کہ ہمارے رسول کے ذمہ صرف کھول کر پہنچا دینا ہی ہے'),
(762, 5, 93, 'جو لو گ ایمان لائے اور نیک کام کیے ان پر اس میں کوئی گناہ نہیں جو پہلے کھا چکے جب کہ آئندہ کو پرہیزگار ہوئے اور ایمان لائے اور عمل نیک کیے پھر پرہیزگار ہوئے اور نیکی کی اور الله نیکی کرنے والوں کو دوست رکھتا ہے'),
(763, 5, 94, 'اے ایمان والو! البتہ ایک بات سے تمہیں آزمائے گا اس شکار سے جس پر تمہارے ہاتھ اور تمہارے نیزے پہنچیں گے تاکہ الله معلوم کرے کہ بن دیکھے اس سے کون ڈرتا ہے پھر جس نے اس کے بعد زیادتی کی تو اس کے لیے دردناک عذاب ہے'),
(764, 5, 95, 'اے ایمان والو! جس وقت تم احرام میں ہو تو شکار کونہ قتل کرو اور جو کوئی تم میں سے اسےجان بوجھ کر مارے تو اس مارے ہوئے کے برابر مویشی میں سے اس پر بدلہ لازم ہے جو تم میں سے دو معتبر آدمی تجویز کریں بشرطیکہ قربانی کعبہ تک پہنچنے والی ہو یا کفارہ مسکینوں کا کھانا کھلانا ہو یا اس کے برابر روزے تاکہ اپنے کام کا وبال چکھے الله نے اس چیز کو معاف کیا جو گزر چکی اور جو کوئی پھر کرے گا الله اس سے بدلہ لے گا اور الله غالب بدلہ لینے والا ہے'),
(765, 5, 96, 'تمہارے لیے دریا کا شکار کرنا اوراس کا کھانا حلال کیا گیا ہے تمہارے واسطے اور مسافروں کے لیے فائدہ ہے اور تم پر جنگل کا شکار کرناحرام کیا گیا ہے جب تک کہ تم احرام میں ہو اور اس الله سے ڈرو جس کی طرف جمع کیے جاؤ گے'),
(766, 5, 97, 'الله نے کعبہ کو جو بزرگی والا گھر ہے لوگوں کے لیے قیام کا باعث کر دیا ہے اور عزت والے مہینے کو اور حرم میں قربانی والے جانور کو بھی اور جن کے گلے میں پٹہ ڈال کر کر کعبہ کو لے جائیں یہ اس لیے ہے کہ تم جان لو کہ بے شک الله کو معلوم ہے کہ جو کچھ آسمانوں اور زمین میں ہے اور بے شک الله ہر چیز کو جاننے والا ہے'),
(767, 5, 98, 'جان لو بے شک الله سخت عذاب والا ہے اور بے شک الله بخشنے والا مہربان ہے'),
(768, 5, 99, 'رسول کے ذمہ سوائے پہنچانے کے اورکچھ نہیں اور الله کو معلوم ہے جو تم ظاہر کرتے ہو اور جو چھپ کر کرتے ہو'),
(769, 5, 100, 'کہہ دو کہ ناپاک اور پاک برابر نہیں اگرچہ تمہیں ناپاک کی کثرت بھی معلوم ہو سو اے عقل مندو الله سے ڈرتے رہو تاکہ تمہاری نجات ہو'),
(770, 5, 101, 'اے ایمان والو! ایسی بات مت پوچھو کہ اگر تم پر ظاہر کی جائیں تو تمہیں بری لگیں اور اگر یہ باتیں یسے وقت میں پوچھو گے جب کہ قرآن نازل ہو رہا ہے تو تم پر ظاہر کر دی جائیں گی گذشتہ سوالات الله نے معاف کر دیے ہیں اور الله بخشنے والا بردبار ہے'),
(771, 5, 102, 'ایسی باتیں تم سے پہلے ایک جماعت پوچھ چکی ہے پھر وہ ان باتوں کے وہ مخالف ہوگئے'),
(772, 5, 103, 'الله نے بحیرہ اور سائبہ اور وصیلہ اور حام مقرر نہیں کیے لیکن کافر الله پر بہتان باندھتے ہیں اور ان میں سے اکثر بیوقوف ہیں'),
(773, 5, 104, 'اور جب انہیں کہا جاتا ہے اس کی طرف آؤ جو الله نے نازل کیا ہ اور رسول کی طرف تو کہتے ہیں ہمیں وہ کافی ہے جس پر ہم نے باپ دادا کو پایا بھلا اگر چہ ان کے باپ دادانہ کچھ علم رکھتے ہوں نہ انہوں نے ہدایت پائی ہو تو بھی ایسا ہی کریں گے'),
(774, 5, 105, 'اے ایمان والو! تم پر اپنی جان کی فکر لازم ہے تمہارا کچھ نہیں بگاڑتا جو کوئی گمراہ ہو جب کہ تم ہدایت یافتہ ہو تم سب کو الله کی طرف لوٹ کر جانا ہے پھر وہ تمہیں بتلا دے گا جو کچھ تم کرتے تھے'),
(775, 5, 106, 'اے ایمان والو! جب کہ تم میں سے کسی کو موت آ پہنچے تو وصیت کے وقت تمہارے درمیان تم میں سے معتبر آدمی گواہ ہو نے چاہیئیں یا تمہارے سوا دو گواہ اور ہوں اگر تم نے زمین پر سفر کیاہو پھر تمہیں موت کی مصیبت آ پہچنے ان دونوں کو نماز کے بعد کھڑا کرو وہ دونوں الله کی قسمیں کھائیں اگر تمہیں کہیں شبہ ہو کہ ہم قسم کے بدلے مال نہیں لیتے اگرچہ رشتہ داری ہی کیوں نہ ہو اور ہم الله کی گواہی نہیں چھپاتے ورنہ ہم بے شک گناہگار ہوں گے'),
(776, 5, 107, 'پھر اگر اس بات کی اطلاع ہو جائے کہ وہ دونوں گناہ کے مستحق ہوئے تو ان کی جگہ اور دو گواہ کھڑےہوں ان میں سے جن کا حق دبایا گیا ہے جو سب سے زیادہ میت کے قریب ہوں پھر الله کی قسم کھائیں کہ ہماری گواہی ان کی گواہی سے زیادہ سچی ہے اور ہم نے زیادتی نہیں کی ورنہ ہم بے شک ظالموں میں سے ہوں گے'),
(777, 5, 108, 'یہ اس امر کا قریب ذریعہ ہے کہ وہ لوگ واقع کو ٹھیک طور پر ظاہر کر دیں یہ اس بات سے ڈر جائیں کہ قسمیں ان کی قسموں کے بعد رد کی جائیں گی ارو الله سے ڈرتے رہو اور سنو اور الله نافرمانوں کو سیدھی راہ پر نہیں چلاتا'),
(778, 5, 109, 'جن دن الله سب پیغمبروں کو جمع کرے گا پھر کہے گا تمہیں کیا جواب دیا گیا تھا وہ کہیں گے ہمیں کچھ خبر نہیں تو ہی چھپی باتو ں کا جاننے والا ہے'),
(779, 5, 110, 'جب الله کہے گا اے عیسیٰ مریم کے بیٹے میرا احسان یاد کر جو تجھ پر اور تیری ماں پر ہوا ہےجب میں نے روح پاک سے تیری مدد کی تو لوگوں سے گود میں اور ادھیڑ عمر میں بات کرتا تھا اور جب میں نے تجھے کتاب اورحکمت اور تورات اور انجیل سکھائی اور جب تو مٹی سے جانور کی صورت میرے حکم سے بناتا تھا پھرتو اس میں پھونک مارتا تھا تب وہ میرے حکم سے اڑنے والا ہو جاتا تھا اور مادر زاد اندھے کو اور کوڑھی کومیرے حکم سے اچھا کرتا تھا اور جب مردوں کو میرے حکم سے نکال کھڑا کرتا تھا اور جب میں نے بنی اسرائیل کو تجھ سے روکا جب تو ان کے پاس نشانیاں لے کر آیا تو جو ان میں کافر تھے وہ کہنے لگے اور کچھ نہیں یہ تو صریح جادو ہے'),
(780, 5, 111, 'اورجب میں نے حواریوں کے دل میں ڈال دیا کہ مجھ پر اور میرے رسول پر ایمان لاؤ تو کہنے لگے ہم یمان لائے اور تو گواہ رہے کہ ہم الله کے فرمانبردار ہیں'),
(781, 5, 112, 'جب حواریوں نے کہا اے عیسیٰ مریم کے بیٹے کیا تیرا رب کر سکتا ہے کہ ہم پر خوان بھرا ہوا آسمان سے اتارے کہا الله سے ڈرو اگر تم ایمان دار ہو'),
(782, 5, 113, 'انہوں نے کہا ہم چاہتے ہیں کہ اس میں سے کھائیں اور ہمارے دل مطمئن ہو جائیں اور ہم جان لیں کہ تو نے ہم سے سچ کہا ہے اور ہم اس پر گواہ ر ہیں'),
(783, 5, 114, 'عیسیٰ مریم کے بیٹے نے کہا اے اللهرب ہمارے ہم پر بھرا ہوا خوان آسمان سے اتار جو ہمارے پہلوں اور پچھلوں کیلئے عید ہو اور تیری طرف سےایک نشانی ہو اور ہمیں رزق دے اور تو ہی سب سے بہتر رزق دینے والا ہے'),
(784, 5, 115, 'الله نے فرمایا بے شک میں وہ خوان تم پر اتارو ں گا پھر اس کے بعد جو کوئی تم میں سے ناشکری کرے گا تو میں اسے ایسی سزا دوں گا جو دنیا میں کسی کو نہ دی ہو گی'),
(785, 5, 116, 'اور جب الله فرمائے گا اے عیسیٰ مریم کے بیٹے کیا تو نے لوگو ں سے کہا تھا کہ خدا کے سوا مجھے اور میری ماں کو بھی خدا بنا لو وہ عرض کرے گا تو پاک ہے مجھے لائق نہیں ایسی بات کہوں کہ جس کا مجھے حق نہیں اگر میں نے یہ کہا ہوگا تو تجھے ضرور معلوم ہو گا جو میرے دل میں ہے تو جانتا ہے اور جو تیرے دل میں ہے وہ میں نہیں جانتا بے شک تو ہی چھپی ہوئی باتو ں کا جاننے والا ہے'),
(786, 5, 117, 'میں نے ان سے اس کے سوا کچھ نہیں کہا جس کا تو نے مجھے حکم دیا تھا کہ الله کی بندگی کرو جو میرا اور تمہارا رب ہے اور میں اس وقت تک ان کا نگران تھا جب تک ان میں رہا پھر جب تو نے مجھے اٹھا لیا تو تو ہی ان کا نگران تھا اور تو ہر چیز سے خبردار ہے'),
(787, 5, 118, 'اگر تو انہیں عذاب دے تو وہ تیرے ہی بندے ہیں اور اگر تو انہیں معاف کر دے تو تو ہی زبردست حکمت والا ہے'),
(788, 5, 119, 'الله فرمائے گا یہ وہ دن ہے جس میں سچوں کو ان کا سچ کام آئے گا ان کے لیے باغ ہیں جن کے نیچے نہریں بہتی ہیں ان میں سے ہمیشہ رہنے والے ہوں گے ان سے الله راضی ہوا اور وہ اس سے راضی ہوئے یہی بڑی کامیابی ہے'),
(789, 5, 120, 'آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے سب الله ہی کی سلطنت ہے اور وہ ہرچیز پر قادر ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'سب تعریف الله ہی کے لیے ہے جس نے آسمان اور زمین بنائے اور اندھیرا اوراجالا بنایا پھر بھی یہ کافر اوروں کو اپنے رب کے ساتھ برابر ٹھیراتے ہیں'),
(791, 6, 2, 'الله وہی ہے جس نے تمہیں مٹی سے پیدا کیا پھر ایک وقت مقرر کر دیا اور اس کےہاں ایک مدت مقرر ہے تم پھر بھی شک کرتے ہو'),
(792, 6, 3, 'اور وہی ایک الله آسمانوں میں بھی ہے اور زمین میں بھی تمہارے ظاہر اور چھپے سب حال جانتا ہے او رجانتا ہے جو کچھ تم کرتے ہو'),
(793, 6, 4, 'ان کے رب کی نشانیوں میں سے کوئی نشانی ایسی نہیں جو ان کے سامنے آئی ہو اور انہوں نے منہ نہ موڑا ہو'),
(794, 6, 5, 'اب جو حق ان کے پاس آیا تو اسے بھی انہوں نے جھٹلا دیا جس چیز کا اب تک وہ مذاق اڑاتے رہے ہیں عنقریب اس کے متعلق کچھ خبریں ان کو پہنچیں گی'),
(795, 6, 6, 'کیا وہ دیکھتے نہیں کہ ہم ان سے پہلے بھی کتنی امتیں ہلاک کر دیں ہم نے انہیں زمین میں وہ اقتدار بخشا تھا جو تمہیں نہیں بخشا اور ہم نے ان پر آسمان سے خوب بارشیں برسائیں اوران کے نیچے نہریں بہا دیں پھر ہم نے انہیں ان کے گناہوں کی پاداش میں ہلاک کر دیا اور ہم نے ان کے بعد اور امتوں کو پیدا کیا'),
(796, 6, 7, 'اور اگر ہم تم پر کوئی کاغذ پر لکھی ہوئی کتاب اتار دیتے اور لوگوں سے اپنے ہاتھوں سے چھو کر بھی دیکھ لیتے تب بھی کافر یہی کہتے ہیں کہ یہ تو صریح جادو ہے'),
(797, 6, 8, 'اور کہتے ہیں اس پر کوئی فرشتہ کیوں نہیں اتارا گیا اور اگر ہم فرشتہ اتارے تو اب تک فیصلہ ہو چکا ہوتا پھر انہیں مہلت نہ دی جاتی'),
(798, 6, 9, 'اور اگر ہم کسی کو فرشتہ کو رسول بنا کر بھیجتے تو وہ بھی آدمی ہی کی صورت میں ہوتا اور انہیں اسی میں شبہ میں ڈالے جس میں اب مبتلا ہیں'),
(799, 6, 10, 'اور تم سے پہلے بھی بہت سے رسولوں کا مذاق اڑایا جا چکا ہے پھر جن لوگوں نے ان سے مذاق کیا تھا انہیں اسی عذاب نے آ گھیرا جس کا مذاق اڑاتے تھے'),
(800, 6, 11, 'کہہ دو کہ ملک میں سیر کرو پھر دیکھو جھٹلانے والوں کا کیا انجام ہوا'),
(801, 6, 12, 'ان سے پوچھو آسمان اور زمین میں جو کچھ ہے وہ کس کا ہے کہہ دو سب کچھ الله ہی کا ہے اس نے اپنے اوپر رحم لازم کر لیا ہے وہ قیامت کے دن تم سب کو ضرور اکھٹا کرے گا جس میں کچھ شک نہیں جو لوگ اپنی جانوں کا نقصان میں ڈال چکے وہ ایمان نہیں لاتے'),
(802, 6, 13, 'اور الله ہی کا ہے جو کچھ رات اور دن میں پایا جاتا ہے اور وہی سننے والا جاننے والا ہے'),
(803, 6, 14, 'کہہ دو جو الله آُسمانوں اور زمین کا بنانے والا ہے کیا اس کے سوا کسی اور کو اپنا مددگار بناؤں اور وہ سب کو کھلاتا ہے اور اسے کوئی نہیں کھلاتا کہہ دو مجھے تو حکم دیا گیا ہے کہ سب سے پہلے اس کا فرمانبردار ہو جاؤں اور تو ہرگز مشرکوں میں شامل نہ ہو'),
(804, 6, 15, 'کہہ دو اگر میں اپنے رب کی نافرمانی کروں تو ایک بڑے دن کےعذاب سے ڈرتا ہوں'),
(805, 6, 16, 'جس سے اس دن عذاب ٹل گیا تو اس پر الله نے رحم کر دیا اور یہی بڑی کامیابی ہے'),
(806, 6, 17, 'اور اگر الله تجھے کوئی تکلیف پہنچائے تواس کے سوا اور کوئی دور کرنے والا نہیں اور اگر تجھے کوئی بھلائی پہنچائے تو وہ ہر چیز پر قادر ہے'),
(807, 6, 18, 'اوراپنے بندوں پر اسی کا زور ہے اور وہی حکمت والا خبردار ہے'),
(808, 6, 19, 'تو پوچھ سب سے بڑا گواہ کون ہے کہہ دو الله میرے اور تمہارے درمیان گواہ ہے اور مجھ پر یہ قرآن اتارا گیا ہے تاکہ تمہیں اس کے ذریعہ سے ڈراؤں اور جس جس کو یہ قرآن پہنچے کیا تم گواہی دیتے ہو کہ الله کے ساتھ اور بھی کوئی معبود ہیں کہہ دو میں تو گواہی نہیں دیتا کہہ دو وہی ایک معبود ہے اور میں تمہارے شرک سے بیزار ہوں'),
(809, 6, 20, 'جنہیں ہم نے کتاب دی ہے وہ اسے پہچانتے ہیں جیسے اپنے بیٹوں کو پہچانتے ہیں اور جو لوگ اپنی جانوں کو نقصان میں ڈال چکے ہیں وہی ایمان نہیں لاتے'),
(810, 6, 21, 'جو شخص الله پر بہتان باندھے یا اس کی آیتوں کو جھٹلائے اس سے زیادہ ظالم کون ہے بے شک ظالم نجات نہیں پائیں گے'),
(811, 6, 22, 'اور جس دن ہم ان سب کو جمع کریں گے پھر ان لوگوں سے کہیں گے جنہوں نے شرک کیا تھا تمہارے شریک کہاں ہیں جن کاتمہیں دعویٰ تھا'),
(812, 6, 23, 'پھر سوائے اس کے ان کا اورکوئی بہانہ نہ ہوگا کہیں گے ہمیں الله اپنے پرودگار کی قسم ہے کہ ہم تو مشرک نہیں تھے'),
(813, 6, 24, 'دیکھو اپنے اوپر انہوں نے کیسا جھوٹ بولا اور جو باتیں وہ بنایا کرتا تھے وہ سب غائب ہو گئیں'),
(814, 6, 25, 'اور بعض ان میں سے تیری طرف کان لگائے رہتے ہیں اور ہم نے ان کے دلوں پر پردے ڈال رکھے ہیں جس کی وجہ سے وہ کچھ نہیں سمجھتے اور ان کے کانو ں میں گرانی ہے اور اگر یہ تمام نشانیاں بھی دیکھ لیں تو بھی ان پر ایمان نہ لادیں گے جب وہ تمہارے پاس آ کر تم سے جھگڑتے ہیں تو کافر کہتے ہیں کہ یہ تو پہلے لوگو ں کی کہانیاں ہی ہیں'),
(815, 6, 26, 'اور یہ لوگ اس سے روکتے ہیں اور خود بھی اس سے دور بھاگتے ہیں اور انہیں ہلاک کر تے مگر اپنے آپ کو اور سمجھتے نہیں'),
(816, 6, 27, 'کاش تم اس وقت کی حالت دیکھ سکتے جب وہ دوزخ کے کنارے کھڑے کیے جائیں گے اس وقت کہیں گے کاش کوئی صورت ایسی ہو کہ ہم واپس بھیج دیے جائیں اور اپنے رب کی نشانیوں کو نہ جھٹلائیں اور ایمان والوں میں ہوجائیں'),
(817, 6, 28, 'بلکہ جس چیز کو اس سے پہلے چھپاتے تھے وہ ظاہر ہو گئی اور اگر یہ واپس بھیج دیے جائیں تب بھی وہی کام کریں گے جن سے انہیں منع کیا گیا تھا اور یقیناً یہ جھوٹے ہیں'),
(818, 6, 29, 'اور کہتےہیں اس دنیا کی زندگی کے سوا ہمارے لیے اور کوئی زندگی نہیں اور ہم اٹھائے نہیں جائیں گے'),
(819, 6, 30, 'اور کاش کہ تو دیکھے جس وقت وہ اپنے رب کے سامنے کھڑے کیے جائیں گے وہ فرمائے گا کیا یہ سچ نہیں کہیں گے ہاں ہمیں رب کی قسم ہے فرمائے گا تو اپنے کفر کے بدلے عذاب چکھو'),
(820, 6, 31, 'وہ لوگ تباہ ہوئے جنہوں نے اپنے رب کی ملاقات کو جھٹلایا یہاں تک کہ جب ان پر قیامت اچانک آ پہنچے گی تو کہیں گے اے افسوس ہم نے اس میں کیسی کوتاہی کی اور وہ اپنے بوجھ اپنے پیٹوں پر اٹھائیں گےخبرداروہ برا بوجھ ہے جسے وہ اٹھائیں گے'),
(821, 6, 32, 'اور دنیا کی زندگی تو ایک کھیل اور تماشہ ہے اور البتہ آخرت کا گھر ان لوگوں کے لیے بہتر ہے جو پرہیزگار ہوئے کیا تم نہیں سمجھتے'),
(822, 6, 33, 'ہمیں معلوم ہے کہ ان کی باتیں تمہیں غم میں ڈالتی ہیں سو وہ تجھے نہیں جھٹلاتے بلکہ یہ ظالم الله کی آیتوں کا انکار کرتے ہیں'),
(823, 6, 34, 'اور بہت سے رسول تم سے پہلے جھٹلائے گئے پھر انہوں نےجھٹلائے جانے پر صبر کیا اور ایذا دیے گئے یہاں تک کہ ان کو ہماری مدد پہنچی اور الله کے فیصلے کوئی بدل نہیں سکتا اور تمہیں پیغمبروں کے حالات کچھ پہنچ چکے ہیں'),
(824, 6, 35, 'اور اگر ان کا منہ پھیرنا تم پر گراں ہو رہا ہے پھر اگر تم سے ہو سکے تو کوئی سرنگ زمین میں تلاش کر یا آسمان سے سیڑھی لگا پھر ان کے پاس کوئی معجزہ لا اور اگر الله چاہتا تو سب کو سیدھی راہ پر جمع کر دیتا سو تو نادانوں میں سے نہ ہو'),
(825, 6, 36, 'وہی مانتے ہیں جو سنتے ہیں اور الله مردوں کو زندہ کرے گا پھر اس کی طرف لوٹائے جائیں گے'),
(826, 6, 37, 'اور کہتے ہیں اس کے رب کی طرف سے اس پر کوئی نشانی کیوں نہیں اتری کہہ دو الله اس پر قادر ہے کہ نشانی اتارے اور لیکن ان میں سے اکثر نہیں جانتے'),
(827, 6, 38, 'اور کوئی چلنے والا زمین میں نہیں اور نہ کوئی پرندہ کہ اپنے دوبازؤں سے اڑ تا ہے مگر یہ تمہاری ہی طرح کی جماعتیں ہیں ہم نے ان کی تقدیر کےلکھنے میں کوئی کسرنہیں چھوڑی پھر سب اپنے رب کے سامنے جمع کیے جائیں گے'),
(828, 6, 39, 'اور جو لوگ ہماری آیتوں کو جھٹلاتے ہیں وہ بہرےاور گونگے ہیں اندھیروں میں ہیں الله جسے چاہے گمراہ کر دے اور جسے چاہے سیدھی راہ پر ڈال دے'),
(829, 6, 40, 'کہہ دو دیکھو تو سہی اگر تم پرخدا کا عذاب آئے یا تم پر قیامت ہی آ جائے تو کیا خدا کے سوا کسی اور کو پکارو گے اگر تم سچے ہو'),
(830, 6, 41, 'بلکہ اسی کو پکارتے ہو پھر اگر وہ چاہتا ہے تو اس مصیبت کو دور کر دیتا ہے جس کے لیے اسے پکارتے ہو اور جنہیں تم الله کا شریک بناتے ہو انہیں بھول جاتے ہو'),
(831, 6, 42, 'اور ہم نے تجھ سے پہلے بہت سی امتوں کے ہاں رسول بھیجےتھے پھر ہم نے انہیں سختی اور تکلیف میں پکڑا تاکہ وہ عاجزی کریں'),
(832, 6, 43, 'پھر کیوں نہ ہوا کہ جب ان پر ہمارا عذاب آیا تو عاجزی کرتے لیکن ان کے دل سخت ہو گئے اور شیطان نے انہیں وہ کام آراستہ کر دکھائے جو وہ کرتے تھے'),
(833, 6, 44, 'پھر جب وہ اس نصیحت کو بھول گئے جو ان کو کی گئی تھی تو ہم نے ان پر ہر چیز کے دروازے کھول دیئے یہاں تک کہ جب وہ ان چیزوں پر خوش ہو گئےجو انہیں دی گئیں تھیں ہم نے انہیں اچانک پکڑ لیا وہ اس وقت نا امید ہوکر کر رہ گئے'),
(834, 6, 45, 'پھر ان ظالموں کی جڑ کاٹ دی گئی اور الله ہی کے لیے سب تعریف ہے جو سارے جہان کا پالنے والا ہے'),
(835, 6, 46, 'ان سے کہہ دو دیکھو تو سہی اگر الله ہی کے لیے سب تعریف ہے جو سارے جہان کا پالنے والا ہےاگر الله تمہارے کان اور آنکھیں چھین لے اور تمہارے دلوں پر مہر لگا دے تو الله کے سوا کوئی ایسا رب ہے جو تمہیں یہ چیزیں لادے دیکھ ہم کیوں کر طرح طرح کی نشانیاں بیان کرتے ہیں پھر بھی یہ منہ موڑتے ہیں'),
(836, 6, 47, 'کہہ دو اگرتم پر الله کا عذاب اچانک یا ظاہر آ جائے تو ظالموں کے سوا اور کون ہلاک ہو گا'),
(837, 6, 48, 'اور ہم پیغبروں کو صرف اس لیے بھیجا کرتے ہیں کہ وہ بشارت دیں اور ڈرائیں پھر جو شخص ایمان لے آوے اور اپنی اصلاح کر لے سو ان پر کوئی ڈر نہ ہوگا اور نہ وہ غم کھائيں گے'),
(838, 6, 49, 'اور جنہوں نے ہماری آیتوں کو جھٹلایا انہیں عذاب پہنچے گا اس لیے کہ وہ نافرمانی کرتے تھے'),
(839, 6, 50, 'کہہ دو میں تم سے یہ نہیں کہتا کہ میرے پاس الله کے خزانے ہیں اور نہ میں غیب کا علم رکھتا ہوں اور نہ یہ کہتا ہوں کہ میں فرشتہ ہوں میں تو صرف اس وحی کی پیروی کرتا ہوں جو مجھ پر نازل کی جاتی ہے کہہ دو کیا اندھا اور آنکھوں والا دونوں برابر ہو سکتے ہیں کیا تم غور نہیں کرتے'),
(840, 6, 51, 'اور اس قرآن کے ذریعے سے ان لوگو ں کو ڈرا جنہیں اس کا ڈر ہے کہ وہ اپنے رب کے سامنے جمع کیے جائیں گے اس طرح پر کہ الله کے سوا ان کوئی مددگار اور سفارش کرنے والا نہ ہو گا تاکہ وہ پرہیزگار ہوجائیں'),
(841, 6, 52, 'اور جو لوگ اپنے رب کو صبح و شام پکارتے ہیں انہیں اپنے سے دور نہ کر جو الله کی رضا چاہتے ہیں تیرے ذمہ ان کا کوئی حساب نہیں ہے اور نہ تیرا کوئی حساب ان کے ذمہ اگر تو نے انہیں دو ہٹا یا پس تو بے انصافوں میں سے ہوگا'),
(842, 6, 53, 'اور اسی طرح ہم نے بعض کو بعض کے ذریعہ سے آزمایا ہے تاکہ یہ لوگ کہیں کیا یہی ہیں ہم میں سے جن پر الله نے فضل کیا ہے کیا الله شکر گزارو ں کو جاننے والا نہیں ہے'),
(843, 6, 54, 'اور ہماری آیتوں کو ماننے والے جب تیرے پاس آئیں تو کہہ دو کہ تم پر سلام ہے تمہارے رب نے اپنے ذمہ رحمت لازم کی ہےجو تم میں سے ناواقفیت سے برائی کرے پھر اس کے بعد توبہ کرے اور نیک ہو جائے تو بےشک وہ بخشنے والا مہربان ہے'),
(844, 6, 55, 'اور اسی طرح ہم آیتوں کو تفصیل سے بیان کرتے ہیں اور تاکہ گنہگاروں کا راستہ واضح ہو جائے'),
(845, 6, 56, 'کہہ دو مجھے منع کیا گیا ہے اس سے کہ میں بندگی کروں ان کی جنہیں تم الله کے سوا پکارتے ہو کہہ دو میں تمہاری خواہشات کے پیچھے نہیں چلتا کیوں کہ میں اس وقت گمراہ ہو جاؤں گا اور ہدایت پانے والوں میں سے نہ رہوں گا'),
(846, 6, 57, 'کہہ دو میرے پاس تو میرے رب کی طرف سے ایک دلیل ہے اور تم اس کو جھٹلاتے ہو جس چیز کو تم جلدی چاہتے ہو وہ میرے پاس نہیں ہے الله کے سوا اور کسی کا حکم نہیں ہے وہ حق بیان کرتا ہے اور وہ بہترین فیصلہ کرنے والا ہے'),
(847, 6, 58, 'کہہ دو اگر میرے پاس وہ چیز ہوتی جس کی تم جلدی کر رہے ہو تو اس معاملہ میں فیصلہ ہوگیا ہو تا جو میرے اور تمہارے درمیان ہے اور الله ظالموں کو خوب جانتا ہے'),
(848, 6, 59, 'اور اسی کے پاس غیب کی کنجیاں ہیں جنہیں اس کےسوا کوئی نہیں جانتا جو کچھ جنگل اور دریا میں ہے وہ سب جانتا ہے اور کوئی پتہ نہیں گرتا مگر وہ اسے بھی جانتاہے اور کوئی دانہ زمین کے تاریک حصوں میں نہیں پڑتا اور نہ کوئی تر اور خشک چیز ہے مگر یہ سب کچھ کتاب روشن میں ہیں'),
(849, 6, 60, 'اور وہ وہی ہے جو تمہیں رات کو اپنے قبضے میں لے لیتا ہے اور جو کچھ تم دن میں کر چکے ہو وہ جانتا ہے پھر تمہیں دن میں اٹھا دیتا ہے تاکہ وہ وعدہ پورا ہو جو مقرر ہو چکا ہے پھر اسی کی طرف تم لوٹائے جاؤ گے پھر تمہیں خبر دے گا اس کی جو کچھ تم کرتے تھے'),
(850, 6, 61, 'اور وہی اپنے بندوں پر غالب ہے اور تم پر نگہبان بھیجتا ہے یہاں تک کہ جب تم میں سے کسی کو موت آ پہنچتی ہے تو ہمارے بھیجے ہوئے فرشتے اسے قبضہ میں لے لیتے اور وہ ذرا کوتاہی نہیں کرتے'),
(851, 6, 62, 'پھر الله کی طرف پہنچائیں جائیں گے جو ا نکا سچا مالک ہے خوب سن لو کہ فیصلہ الله ہی کا ہوگا اور بہت جلدی حساب لینے والا ہے'),
(852, 6, 63, 'کہہ دو تمہیں جنگل اور دریا کے اندھیروں سے کون بچا تا ہے جب اسے عاجزی سے اور چھپا کر پکارتے ہوکہ اگر ہمیں اس آفت سے بچا لے تو البتہ ہم ضرور شکر گزار کرنے والوں میں سے ہوں گے'),
(853, 6, 64, 'کہہ دو الله تمہیں اس سے اور ہر سختی سے بچا تا ہے تم پھر بھی شرک کرتے ہو'),
(854, 6, 65, 'کہہ دو وہ اس پر قادر ہے کہ تم پر عذاب اوپر سے بھیجے یا تمہارے پاؤں کے نیچے سے یا تمہیں مختلف فرقے کر کے ٹکرا دے اور ایک کودوسرے کی لڑائی کا مزہ چکھا دے دیکھو ہم کس طرح مختلف طریقوں سے دلائل بیان کرتے ہیں تاکہ وہ سمجھ جائیں'),
(855, 6, 66, 'اور تیری قوم نے اسے جھٹلایا ہے حالانکہ وہ حق ہے کہہ دو میں تمہارا ذمہ دار نہیں بنایا گیا'),
(856, 6, 67, 'ہر خبر کے ظاہر ہونے کا ایک وقت مقرر ہے اور عنقریب جان لو گے'),
(857, 6, 68, 'اور جب تو ان لوگو ں کو دیکھے جو ہماری آیتو ں میں جھگڑتے ہیں تو ان سے الگ ہو جا یہاں تک کہ کسی اور بات میں بحث کرنے لگیں اور اگر تجھے شیطان بھلا دے تو یاد آجانے کے بعد ظالموں کے پاس نہ بیٹھ'),
(858, 6, 69, 'اور جھگڑنے والوں کے حساب میں سے پرہیزگاروں کے ذمہ کوئی چیز نہیں لیکن نصیحت کرنی ہے شاید کہ وہ ڈر جائیں'),
(859, 6, 70, 'اور انہیں چھوڑ دو جنہوں نے اپنے دین کو کھیل اور تماشا بنا رکھا ہے اور دنیاکی زندگی نے انہیں دھوکہ دیا ہے اور انہیں قرآن سے نصیحت کرتا تاکہ کوئی اپنے کیے میں گرفتار نہ ہو جائے کہ اس کے لیے الله کے سوا کوئی دوست اور سفارش کرنے والا نہ ہوگا اور اگر دنیا بھر کا معاوضہ بھی دے گا تب بھی اس سے نہ لیا جائے گا یہی وہ لوگ ہیں جو اپنے کیے میں گرفتار ہوئے ان کے پینے کے لیے گرم پانی ہوگا اور ان کے کفر کے بدلہ میں دردناک عذاب ہو گا'),
(860, 6, 71, 'انہیں کہہ دوکہ کیا ہم الله کے سوا انہیں پکاریں جو ہمیں نہ نفع پہنچا سکیں اور نہ نقصان دے سکیں اور کیا ہم الٹے پاؤں پھر جائیں اس کے بعد کہ الله نے ہمیں سیدھی راہ دکھائی ہے اس شخص کی طرح جسےجنگل میں جنوں نے راستہ بھلا دیا ہو جب کہ وہ حیران ہو اس کے ساتھی اسے راستے کی طرف بلاتے ہوں کہ ہمارے پاس چلا آ کہہ دو الله نے جو راہ بتلائی وہی سیدھی ہے اور ہمیں حکم دیا گیا ہے کہ ہم پروردگار عالم کے تابع رہیں'),
(861, 6, 72, 'اور یہ کہ نماز قائم رکھو اور الله سے ڈرتے رہو وہی ہے جس کے سامنے اکھٹے کیے جاؤ گے'),
(862, 6, 73, 'اور وہی ہے جس نے آسمانوں اورزمین کو ٹھیک طور پر بنایا ہے اور جس دن کہے گا کہ ہو جا تو وہ ہو جائے گا اس کی بات سچی ہے جس دن صورمیں پھونکا جائے گاتو اسی کی بادشاہی ہو گی چھپی اور ظاہر باتوں کا جاننے والا ہے اور وہی حکمت والا خبردار ہے'),
(863, 6, 74, 'اور یاد کر جب ابراہیم نے اپنے باپ آزر سے کہا کیا بتوں کو خدا جانتاہے میں تجھے اور تیرے قوم کو صریح گمراہی میں دیکھتا ہوں'),
(864, 6, 75, 'اور ہم نے اسی طرح ابراھیم کو آسمانوں اور زمین کے عجائبات دکھائے ابور تاکہ وہ یقین کرنے والوں میں سے ہو جائے'),
(865, 6, 76, 'پھر جب رات نے اس ہر اندھیرا کیا اس نے ایک ستارہ دیکھا کہا یہ میرا رب ہے پھر جب وہ غائب ہو گیا تو کہا میں غائب ہونے والوں کو پسند نہیں کرتا'),
(866, 6, 77, 'پھر جب چاند کو چمکتا ہوا دیکھا کہا یہ میرا رب ہے پھر جب وہ غائب ہو گیا تو کہا اگر مجھے میرا رب ہدایت نہ کرے گا تو میں ضرور گمراہوں میں سے ہوجاؤں گا'),
(867, 6, 78, 'پھر جب آفتاب کو چمکتاہوا دیکھا کہا یہی میرا رب ہے یہ سب سے بڑا ہے پھر جب وہ غائب ہو گیا کہا اے میری قوم میں ان سے بیزار ہوں جنہیں تم الله کا شریک بناتے ہو'),
(868, 6, 79, 'سب سے یک سو ہو کر میں نے اپنے منہ کو اسی کی طرف متوجہ کیا جس نے آسمان اور زمین بنائی اور میں شرک کرنے والوں میں سے نہیں ہوں'),
(869, 6, 80, 'اور اس کی قوم نے اس سے جھگڑا کیا اس نے کہا کیا تم مجھ سے الله کے ایک ہونے میں جھگڑتے ہو اور اس نے میری رہنمائی کی ہے اور جنہیں تم شریک کرتے ہو میں ان سے نہیں ڈرتا مگر یہ کہ میرا رب مجھے کوئی تکلیف پہنچانا چاہے میرے رب نے علم کے لحاظ سے سب چیزوں پر احاطہ کیا ہوا ہے کیا تم سوچتے نہیں'),
(870, 6, 81, 'اور تمہارے شریکوں سے کیوں ڈروں حالانکہ تم اس بات سے نہیں ڑرتے کہ الله کا شریک ٹھیراتے ہو اس چیز کو جس کی الله نے تم پر کوئی دلیل نہیں اتاری اگر تم کو کچھ سمجھ ہے تو (بتاؤ) دونوں جماعتوں میں سے امن کا زیادہ مستحق کون ہے'),
(871, 6, 82, 'جو لوگ ایمان لائے اور انہوں نے اپنے ایمان میں شرک نہیں ملایا امن انہیں کے لیے ہے اور وہی راہ راست پر ہیں'),
(872, 6, 83, 'اور یہ ہماری دلیل ہے کہ ہم نے ابراہیم کو اس کی قوم کے مقابلہ میں دی تھی ہم جس کے چاہیں درجے بلند کرتے ہیں بے شک تیرا رب حکمت والا جاننے والا ہے'),
(873, 6, 84, 'اور ہم نے ابراھیم کو اسحاق اور یعقوب بخشا ہم نے سب کو ہدایت دی اور اس سے پہلے ہم نے نوح کو ہدایت دی اور اس کی اولاد میں سے داؤد اور سلیمان اور ایوب اور یوسف اور موسیٰ اور ہارون ہیں اور اسی طرح ہم نیکو کاروں کو بدلہ دیتے ہیں'),
(874, 6, 85, 'اور زکریا اور یحییٰ اور عیسیٰ اور الیاس سب نیکو کاروں سے ہیں'),
(875, 6, 86, 'اور اسماعیل اورالیسع اوریونس اور لوط او رہم نے سب کو سارے جہان والوں پر بزرگی دی'),
(876, 6, 87, 'اور ان کے باپ دادوں اور ان کی اولاد اور ان کے بھائیوں میں سے بعضوں کو ہم نے ہدایت دی اور ہم نے انہیں پسند کیا اور سیدھی راہ پر چلایا'),
(877, 6, 88, 'یہ الله کی ہدایت ہے اپنے بندوں کو جسے چاہے اس پر چلاتا ہے اور اگر یہ لوگ شرک کرتے تو البتہ جو کچھ انہوں نے کیا تھا سب کچھ ضائع ہو جاتا'),
(878, 6, 89, 'یہی لوگ تھے جنہیں ہم نے کتاب اور شریعت اور نبوت دی تھی پھر اگر مکہ والے ان باتو ں کو نہ مانیں تو ہم نے ان باتو ں کے ماننے کے لیے ایسے لوگ مقرر کر دیے جوان کے منکر نہیں ہیں'),
(879, 6, 90, 'یہ وہ لوگ تھے جنہیں الله نے ہدایت دی سو تو ان کے طریقہ پر چل کہہ دو میں تم سے اس پر کوئی مزدوری نہیں مانگتا یہ تو جہان والوں کے لیے محض نصیحت ہے'),
(880, 6, 91, 'اور انہوں نے الله کو صحیح طور پر نہیں پہچانا جب انہوں نے کہا الله نے کسی انسان پر کوئی چیز نہیں اتاری تھی جو لوگو ں کے واسطے روشنی اور ہدایت تھی جسے تم نے ورق ورق کر کےد کھلا یا اوربہت سی باتو ں کو چھپا رکھا اور تمہیں وہ چیزیں سکھائیں جنہیں تم اور تمہارے باپ دااد نہیں جانتے تھے تو کہہ دو الله ہی نے اتاری تھی پھرانہیں چھوڑ دو کہ اپنی بحث میں کھیلتے رہیں'),
(881, 6, 92, 'اور یہ کتاب جسے ہم نے اتارا ہےبرکت والی ہے ان کی تصدیق کرنے والی ہے جو اس سے پہلے تھیں اور تاکہ تو مکہ والوں کو اور اس کے آس پاس والوں کو ڈرائے اور جو لوگ آخرت پر یقین رکھتے ہیں وہی اس پر ایمان لاتے ہیں اور وہی اپنی نماز کی حفاظت کرتے ہیں'),
(882, 6, 93, 'اور اس سے زیادہ ظالم کون ہو گا جو الله پربہتان باندھے یا یہ کہے کہ مجھ پر وحی نازل ہوئی ہے حالانکہ اس پر وحی نہ اتری ہو اور جو کہے میں بھی ایسی چیز اتار سکتا ہوں جیسی کہ الله نے اتاری ہے اور اگر تو دیکھے جس وقت ظالم موت کی سختیوں میں ہوں گے اور فرشتے اپنے ہاتھ بڑھانے والےہوں گے کہ اپنی جانوں کو نکالو آج تمہیں ذلت کا عذاب ملے گا اس سبب سے کہ تم الله پر جھوٹی باتیں کہتے تھے اور اس کی آیتوں کے ماننے سے تکبر کرتے تھے'),
(883, 6, 94, 'اور البتہ تم ہمارے پاس ایک ایک ہو کر آ گئے ہو جس طرح ہم نے تمہیں پہلی دفعہ پیدا کیا تھا اور جو کچھ ہم نے تمہیں دیا تھا وہ اپنے پیچھے ہی چھوڑ آئے ہو اور تمہارے ساتھ ان کی سفارش کرنے والوں کو نہیں دیکھتے جنہیں تم خیال کرتے تھے کہ وہ تمہارے معاملےمیں شریک ہیں تمہارا آپس میں قطع تعلق ہو گیا ہے اور جو تم خیال کرتے تھے وہ سب جاتا رہا'),
(884, 6, 95, 'بے شک الله دانے اور گٹھلی کا پھاڑنے والا ہے مردہ سے زندہ کو نکالتا ہے اور زندہ سے مردہ نکالنے والا ہے الله یہی ہے پھر کدھر الٹے پھرے جا رہے ہو'),
(885, 6, 96, 'وہ صبح کا نکالنے والا ہے اور اس نے آرام کے لیے رات بنائی اسی نے چاند اور سورج کا حساب مقرر کیا ہے یہ غالب جاننے والے کا اندازہ ہے'),
(886, 6, 97, 'اور اسی نے تمہارے لیے ستارے بنائے ہیں تاکہ ان کے ذریعے سے جنگل اوردریا کےاندھیروں میں راستہ معلوم کر سکو تحقیق ہم نے کھول کر نشانیاں بیان کر دی ہیں ان لوگو ں کے لیے جو جانتے ہیں'),
(887, 6, 98, 'اور الله وہی ہے جس نے ایک شخص سے تم سب کو پیدا کیا پھر ایک تو تمہارا ٹھکانا ہے اور ایک امانت رکھے جانےکی جگہ تحقحق ہم نے کھول کر نشانیاں بیان کر دی ہیں ان کے لیے جو سوچتے ہیں'),
(888, 6, 99, 'اور اسی نے آسمان سے پانی اتارا پھر ہم نے اس سے ہر چیز اگنے والی نکالی پھر ہم نے اس سے سبز کھیتی نکالی جس سے ہم ایک دوسرے پرچڑھے ہوئے دانے نکالتے ہیں اور کھجور کے شگوفوں میں سے پھل کے جھکے ہوئے گچھے اور انگور اور زیتون اور انار کے باغ آپس میں ملتے جلتے اور جدا جدا بھی ہر ایک درخت کے پھل کو دیکھو جب وہ پھل لاتا ہے اور اس کے پکنے کو دیکھو ان چیزوں میں ایمان والوں کے لیے نشانیاں ہیں'),
(889, 6, 100, 'اور اللہ کے شریک جنوں کو ٹھیراتے ہیں حالانکہ اس نے انہیں پیدا کیا ہے اور جہالت سے اس کے لیے بیٹے اور بیٹیاں تجویز کرتے ہیں وہ پاک ہے اور ان باتوں سے بھی بلند ہے جو وہ بیان کرتے ہیں'),
(890, 6, 101, 'آسمانوں اور زمین کو از سر نو پیدا کرنے والا ہے اس کا بیٹا کیوں کر ہو سکتا ہے حالانکہ اس کی کوئی بیوی نہیں اور اس نے ہر چیز کو بنایاہے اور وہ ہر چیز کو جاننے والا ہے'),
(891, 6, 102, 'یہی اللہ تمہارا رب ہے اس کے سوائے اورکوئی معبود نہیں ہر چیز کا پیدا کرنے والا ہے پس اسی کی عبادت کرو اور وہ ہر چیز کا کا رساز ہے'),
(892, 6, 103, 'اسے آنکھیں نہیں دیکھ سکتیں اور وہ آنکھوں کو دیکھ سکتا ہے اوثر وہ نہایت باریک بین خبردار ہے'),
(893, 6, 104, 'تحقیق تمہارے ہاں تمہارے رب کی طرف سے نشانیاں آ چکی ہیں پھر جس نے دیکھ لیا تو خود ہی نفع اٹھایا اور جو اندھا رہا سو اپنا نقصان کیا اور میں تمہارا نگہبان نہیں ہوں'),
(894, 6, 105, 'اور اسی طرح ہم مختلف طریقوں سے دلائل بیان کرتے ہیں تاکہ وہ کہیں کہ تو نے کسی سے پڑھا ہے اور تاکہ ہم سمجھداروں کے لیے واضح کر دیں'),
(895, 6, 106, 'تو اس کی تابعداری کر جو تیرےرب کی طرف سے وحی کی گئی ہے اس کے سوا اور کوئی معبود نہیں اور مشرکوں سے منہ پھیرے'),
(896, 6, 107, 'اور اگر الله چاہتا تو وہ شرک نہ کرتے اور ہم نے تجھے ا ن پر نگہبان نہیں بنایا اور تو ان کا ذمہ دار نہیں ہے'),
(897, 6, 108, 'اور جن کی یہ الله کے سوا پرستش کر تےہیں انہیں برا نہ کہوورنہ وہ بے سمجھی سے زیادتی کرکے الله کو برا کہیں گے اس طرح ہر ایک جماعت کی نظر میں ان کے اعمال کوہم نے آراستہ کر دیا ہے پھر ان سب کو اپنے رب کی طرف لوٹ کر آنا ہے تب وہ انہیں بتلائے گا جو کچھ کیا کرتے تھے'),
(898, 6, 109, 'اور وہ الله کے نام کی پکی قسمیں کھاتے ہیں کہ اگر ان کے پاس کوئی نشانی آئے تو اس پر ضرور ایمان لاویں گے ان سے کہہ دو کہ نشانیاں تو اللہ کے ہاں ہیں اور تمہیں اے مسلمانو کیا خبر ہے کہ جب نشانیاں آہیں گی تو یہ لوگ ایمان لے ہی آئیں گے'),
(899, 6, 110, 'اور ہم بھی ان کے دلوں کو اور ان کی نگاہوں کو پھیر دیں گے جس طرح یہ اس پر پہلی دفعہ ایمان نہیں لاتے اور ہم انہیں ان کی سرکشی میں حیران رہنے دیں گے'),
(900, 6, 111, 'اور اگر ہم ان پر فرشتے بھی اتار دیں اور ان سے مردے باتیں بھی کریں اور ان کے سامنے ہم ہر چیز کو زندہ بھی کر دیں تو بھی یہ لوگ ایمان لانے والے نہیں مگر یہ کہ الله چاہے لیکن اکثر ان میں سے جاہل ہیں'),
(901, 6, 112, 'اور اسی طرح ہم نے ہر نبی کے لیے شریر آدمیوں اور جنوں کو دشمن بنایا جو کہ ایک دوسرے کو طمع کرہوئی باتیں فریب دینے کے لیے سکھاتے ہیں اور اگر تیرا رب چاہتا تو یہ کام نہ کرتے سو تو انہیں اور جوجھوٹ بناتے ہیں اسے چھوڑ دے'),
(902, 6, 113, 'اور تاکہ ان طمع کی ہوئی باتوں کی طرف ان لوگوں کے دل مائل ہوں جنہیں آخرت پر یقین نہیں اور تاکہ وہ لوگ ان باتو ں کو پسند کریں اور تاکہ وہ کریں جو برے کام وہ کر رہے ہیں'),
(903, 6, 114, 'کیا میں الله کےسوا اور کسی کو منصف بناؤں حالانکہ اس نے تمہاری طرف ایک واضح کتاب اتاری ہے اور جنہیں ہم نے کتاب دی ہے وہ جانتے ہیں کہ یہ ٹھیک تیرے رب کی طرف سے نازل ہوئی ہے پس تو شک کرنے والوں میں سے نہ ہو'),
(904, 6, 115, 'اور تیرے رب کی باتیں سچائی اور انصاف کی انتہائی حد تک پہنچی ہوئی ہیں اس کی باتو ں کو کوئی بدل نہیں سکتا اور وہ سننے والا جاننے والا ہے'),
(905, 6, 116, 'اور اگر تو کہا مانے گا اکثر ان لوگو ں کا جو دنیا میں ہیں تو تجھے الله کی راہ سے ہٹا دیں گے وہ تو اپنے خیال پر چلتے اور قیاس آرائیاں کرتے ہیں'),
(906, 6, 117, 'تیرا رب خوب جانتا ہے اسے جو اس کے راہ سے ہٹ جاتا ہے اور سیدھے راستہ پر چلنے والوں کو بھی خوب جانتا ہے'),
(907, 6, 118, 'سو تم اس جانوروں میں سے کھاؤ جس پر الله کا نام لیا گیا ہے اگر تم اس کے حکموں پر ایمان لانے والے ہو'),
(908, 6, 119, 'کیا وجہ ہے کہ تم وہ چیز نہ کھاؤ جس پر الله کا نام لیا گیا ہو حالانکہ وہ واضح کر چکا ہے جو کچھ اس نے تم پر حرام کیا ہے ہاں مگر وہ چیز جس کی طرف تم مجبور ہو جاؤ اوربہت سے لوگ بےعلمی کے باعث اپنے خیالات کے باعث اپنے خیالات کی بناء پر لوگو ں کو بہکاتے ہیں تیرا رب حد سے بڑھنے والوں کو خوب جانتا ہے'),
(909, 6, 120, 'تم ظاہری اورباطنی سب گناہ چھوڑ دو بے شک جو لوگ گناہ کرتے ہیں عنقریب اپنے کیے کی سزا پائیں گے'),
(910, 6, 121, 'اور جس چیز پر الله کا نام نہیں لیا گیا اس میں سے نہ کھاؤ اور بے شک یہ کھانا گناہ ہے اور بے شک شیطان اپنے دوستوں کے دلو ں میں ڈالتے ہیں تاکہ وہ تم سے جھگڑیں اور اگر تم نے ان کا کہا مانا تو تم بھی مشرک ہو جاؤ گے'),
(911, 6, 122, 'بھلا وہ شخص جو مردہ تھا پھر ہم نے اسے زندہ کر دیا اور ہم نے اسے روشنی دی کہ اسے لوگوں میں لیے پھرتا ہےوہ اس کے برابر ہو سکتا ہے جو اندھیروں میں پڑا ہو وہاں سے نکل نہیں سکتا اسی طرح کافروں کی نظر میں ان کے کام آراستہ کر دیئے گئے ہیں'),
(912, 6, 123, 'اور اسی طرح ہر بستی میں ہم نے گناہگاروں کے سردار بنا دیےہیں تاکہ وہاں اپنے مکرو فریب کا جال پھیلائیں حالانکہ وہ اپنے فریب کے جال میں آپ پھنستے ہیں مگر وہ سمجھتے نہیں'),
(913, 6, 124, 'جب ان کے پاس کوئی نشانی آتی ہے تو کہتے ہیں ہم نہیں مانیں گے جب تک کہ وہ چیز خود ہمیں نہ دی جائے جو الله کے رسولوں کو دی گئی ہے اور الله بہتر جانتا ہے کہ پیغمبری کا کام کس سے لے وہ وقت قریب ہے جب یہ مجرم اپنی مکاریو ں کی پاداش میں الله کے ہاں ذلت اور سخت عذاب میں مبتلا ہوں گے'),
(914, 6, 125, 'سو جسے الله چاہتا ہے کہ ہدایت دے تو اس کے سینہ کو اسلام کے قبول کرنے کے لیے کھول دیتا ہے اور جس کے متعلق چاہتا ہے کہ گمراہ کرے اس کے سینہ کو بے حد تنگ کر دیتا ہے گو کہ وہ آسمان پر چڑھتا ہے اسی طرح الله تعالیٰ ایمان نہ لانے والوں پر پھٹکار ڈالتا ہے'),
(915, 6, 126, 'اور یہ تیرے رب کا سیدھا راستہ ہے ہم نے نصیحت حاصل کرنے والوں کے لیے آیتوں کو صاف صاف کر کے بیان کر دیا ہے'),
(916, 6, 127, 'ان کے لیے اپنے رب کے ہاں سلامتی کا گھر ہے اور وہ ان کے اعمال کے سبب سے ان کا مددگار ہے'),
(917, 6, 128, 'اور جس دن ان سب کو جمع کرے گا جنوں کی جماعت سے فرمائے گا تم نے آدمیوں سے بہت سے اپنے تابع کر لئے تھے اور آدمیوں میں سے جو جنوں کے دوست تھے کہیں گے اے رب ہمارے ہم میں سے ہر ایک نے دوسرے سے کام نکالا اور ہم اپنی اس معیاد کو آ پہنچے جو تو نے ہمارے واسطے مقرر کی تھی فرمائے گا تم سب کا ٹھکانا آگ ہے اس میں ہمیشہ رہو گے اس سے صرف وہی بچیں گے جنہیں الله بچائے گا بے شک تیرا رب حکمت والا جاننے والا ہے'),
(918, 6, 129, 'اور اسی طرح ہم گنہگاروں کو ایک دوسرے کے ساتھ ان کے اعمال کے سبب سے ملا دیں گے'),
(919, 6, 130, 'اے جنو اور انسانو! کی جماعت کیا تمہارے پاس تم ہی میں سے رسول نہیں آئے تھے جو تمہیں میرے احکام سناتے تھے وہ اس دن کی ملاقات سے تمہیں ڈراتے تھے کہیں گے ہم اپنے گناہ کا راقرار کرتے ہیں اور انہیں دنیا کی زندگی نے دھوکہ دیا ہے او راپنے اوپر ہی گواہی دیں گے کہ وہ کافر تھے'),
(920, 6, 131, 'یہ اس لیے ہوا کہ تیار رب بستیوں کو ظلم کرنے کے باوجود ہلاک نہیں کیاکرتا اس حال میں کہ وہ بے خبر ہوں'),
(921, 6, 132, 'اور ہر ایک کے لیے ان کے عمل کے لحاظ سے درجے ہیں اور تیرا رب ان کے کاموں سے بے خبر نہیں'),
(922, 6, 133, 'اور تیرا رب بے پرواہ رحمت والا ہے اگر وہ چاہے تم سب کو اٹھالے اور تمہارے بعد جسے چاہے تمہاری جگہ آباد کردے جس طرح تمہیں ایک دوسری قوم کی نسل سے پیدا کیا ہے'),
(923, 6, 134, 'جس چیز کا تمہیں وعدہ دیا جاتا ہے وہ ضرور آنے والی ہے اور تم عاجز نہیں کر سکتے'),
(924, 6, 135, 'کہہ دو اے لوگو تم اپنی جگہ پر کام کرتے رہو اور میں بھی کرتا ہوں عنقریب معلوم کر لو گے آخرت کا گھر کس کے لیے ہوتا ہے بے شک ظالم نجات نہیں پاتے'),
(925, 6, 136, 'اور الله کی پیدا کی ہوئی کھیتی اور مویشیوں میں سے ایک حصہ اس کے لیے مقرر کرتے ہیں اور اپنے خیال کے مطابق کہتے ہیں کہ یہ الله کا حصہ ہے اور یہ ہمارے شریکوں کا ہے سو جو حصہ ان کے شریکوں کا ہے وہ الله کی طرف نہیں جا سکتا اور جو الله کا ہے وہ ان کے شریکوں کی طرف جا سکتا ہے کیسا برا فیصلہ کرتے ہیں'),
(926, 6, 137, 'اور اسی طرح بہت سے مشرکوں کے خیال میں ان کے شریکوں نے اپنی اولاد کے قتل کرنے کو خوشنما بنا دیا ہے تاکہ انہیں ہلاکت میں مبتلا کر دیں اور ان پر ان کے دین کو مشتبہ بنا دیں اگر الله تعالیٰ چاہتا تو ایسا نہ کرتے سو انہیں چھوڑ دو اور جو وہ افتراء کر تے ہیں'),
(927, 6, 138, 'اور کہتے ہیں یہ جانور اور کھیت محفوظ ہیں انہیں صرف وہی لوگ کھا سکتے ہیں جنہیں ہم چاہیں اورکچھ جانور ہیں جن پر سواری حرام کر دی گئی ہے اور کچھ جانور ہیں جن پر الله کا نام نہیں لیتے یہ سب الله پر افتراء ہے عنقریب الله انہیں اس افترا کی سزا دے گا'),
(928, 6, 139, 'اور کہتے ہیں جو کچھ ان جانوروں کے پیٹ میں ہے یہ ہمارے مردوں کے لیے خاص ہے اور ہماری عورتو ں پر حرام ہے اور جو بچہ مردہ ہو تو دونوں اس کے کھانے میں برابر ہیں الله انہیں ان باتو ں کی سزا دے گا بے شک وہ حکمت والا جاننے والا ہے'),
(929, 6, 140, 'تحقیق خسارے میں پڑے وہ لوگ جنہوں نے اپنی اولاد کو جہالت اور نادانی کی بنا پر قتل کیا اور الله پر بہتان باندھ کر اس رزق کو حرام کر لیا جو الله نے انہیں دیا تھا بے شک وہ گمراہ ہوئے اور سیدھی راہ پر نہ آئے'),
(930, 6, 141, 'اور اسی نے وہ باغ پیدا کیے جو چھتو ں پر چڑھائے جاتے ہیں او رجو نہیں چڑھائے جاتے اور کھجور کے درخت او رکھیتی جن کے پھل مختلف ہیں اور زیتون اور انار پیدا کیے جو ایک دوسرے سے مشابہاور جدا جدا بھی ہیں ان کے پھل کھاؤ جب وہ پھل لائیں اور جس دن اسے کاٹو اس کا حق ادا کرو اور بے جا خرچ نہ کرو بے شک وہ بے جا خرچ کرنے والوں کو پسند نہیں کرتا'),
(931, 6, 142, 'اور بوجھ اٹھانے والے مویشی پیدا کیے اور زمین سے لگے ہوئے اور الله کے رزق میں سے کھاؤ اور شیطان کے قدموں پر نہ چلو وہ تمہارا صریح دشمن ہے'),
(932, 6, 143, 'آٹھ قسمیں پیدا کیں بھیڑ میں سے دو اور بکری میں سے دو تو پوچھ کہ دونوں نر الله نے حرام کیے ہیں یا دونوں مادہ یا وہ بچہ جو دونوں مادہ کے رحم میں ہے مجھے اس کی سند بتلاؤ اگر سچے ہو'),
(933, 6, 144, 'اور اونٹ اور گائے سے دو دو قسمیں پیدا کیں تو پوچھ دونوں نر حرا م کیے ہیں یا دونوں مادہ یا وہ بچہ جو دونوں مادہ کے رحم میں ہے کیا تم موجود تھے جس وقت الله نے تمہیں حکم دیا تھا پھر اس سے زیادہ ظالم کون ہے جو الله پر جھوٹا بہتان باندھے تاکہ لوگو ں کو بلا تحقیق گمراہ کرے بے شک الله ظالموں کو ہدایت نہیں کرتا'),
(934, 6, 145, 'کہہ دو کہ میں اس وحی میں جو مجھے پہنچی ہے کسی چیز کو کھانے والے پر حرام نہیں پاتا جو اسے کھائے مگر یہ کہ وہ مردار ہو یا بہتا ہوا خون یا سور کا گوشت کہ وہ ناپاک ہے یا وہ ناجائز ذبیحہ جس پر الله کے سوا کسی اور کا نام پکارا جائے پھر جو تمہیں بھوک سے بے اختیار ہوجائے ایسی حال میں کہ نہ بغاوت کرنے والا اور نہ حد سے گزرنے والا ہو تیرا رب بخشنے والا مہربان ہے'),
(935, 6, 146, 'یہود پرہم نے ایک ناخن والا جانور حرام کیا تھا اور گائے اوربکری میں سے ان دونوں کی چربی حرا م کی تھی مگر جو پشت پر یا انتڑیوں پر لگی ہوئی ہو یا جو ہڈی سے ملی ہوئی ہو ہم نے ان کی شرارت کے باعث انہیں یہ سزا دی تھی اور بے شک ہم سچے ہیں'),
(936, 6, 147, 'پھر اگر تجھے جھٹلائیں تو کہہ دو تمہارا رب بہت وسیع رحمت والا ہے اورگناہگار لوگوں سے اس کا عذاب نہیں ٹلے گا'),
(937, 6, 148, 'اب مشرک کہیں گے اگر الله چاہتا تو نہ ہم اور نہ ہمارے باپ دادا شرک کرتے اور نہ ہم کسی چیز کو حرام کرتے اورنہ ہم کسی چیز کو حرام کرتے اسی طرح ان لوگوں نے جھٹلایا جو ان سے پہلے تھے یہاں تک کہ انہو ں نے ہمارا عذاب چکھا کہہ دو تمہارے ہاں کوئی ثبوت ہے تو اسے ہمارے سامنے لاؤ تم فقط خیالی باتوں پر چلتے ہو اور صرف تخمینہ ہی کرتے ہو'),
(938, 6, 149, 'کہہ دو پس الله کا الزام پورا ہو چکا پس اگر وہ چاہتا تو تم سب کو ہدایت کردیتا'),
(939, 6, 150, 'ان سے کہہ دو اپنے گواہ لاؤ جو اس بات کی گواہی دیں کہ الله نے ان چیزوں کو حرام کیا ہے پھر اگر وہ ایسی گواہی دیں توتو ان کا اعتبار نہ کر اور جنہوں نے ہماری آیتوں کو جھٹلایا ہے اور جو آخرت پریقین نہیں رکھتے اور وہ اوروں کو اپنے رب کے برابر کرتے ہیں'),
(940, 6, 151, 'کہہ دو آؤ میں تمہیں سنا دوں جو تمہارے رب نے تم پر حرام کیا ہے یہ کہ اس کے ساتھ کسی کو شریک نہ بناؤ اور ماں باپ کے ساتھ نیکی کرو اور تنگدستی کے سبب اپنی اولاد کو قتل نہ کرو ہم تمہیں اور انہیں رزق دیں گے اور بے حیائی کے ظاہر اور پوشیدہ کاموں کے قریب نہ جاؤ اور نا حق کسی جان کو قتل نہ کرو جس کا قتل الله نے حرام کیا ہے تمہیں یہ حکم دیتا ہے تاکہ تم سمجھ جاؤ'),
(941, 6, 152, 'اور سوائے کسی بہتر طریقہ کے یتیم کے مال کے پاس نہ جاؤ یہاں تک کہ وہ اپنی جوانی کو پہنچے اور ناپ اور تول کو انصاف سے پورا کرو ہم کسی کو اس کی طاقت سے زیادہ تکلیف نہیں دیتے اور جب بات کہو انصاف سے کہو اگرچہ رشتہ داری ہو اور الله کا عہد پورا کرو تمہیں یہ حکم دیا ہے تاکہ تم نصیحت حاصل کرو'),
(942, 6, 153, 'اور بے شک یہی میرا سیدھا راستہ ہے سو اسی کا اتباع کرواور دوسرے راستوں پر مت چلو وہ تمہیں الله کی راہ سے ہٹا دیں گے تمہیں اسی کا حکم دیا ہے تاکہ تم پرہیزگار ہو جاؤ'),
(943, 6, 154, 'پھر ہم نےنیکوں پر نعمت پوری کرنے کے لیے موسیٰ کو کتاب دی جس میں ہر چیز کی تفصیل اور ہدایت اور رحمت تھی تاکہ وہ لوگ اپنے رب کی ملاقات پر ایمان لائیں'),
(944, 6, 155, 'یہ برکت والی کتاب ہم نے اتاری ہے سو اس کا اتباع کرو اور ڈرو تاکہ تم پر رحم کیا جائے'),
(945, 6, 156, 'تاکہ تم یہ نہ کہو کہ ہم سے پہلے دو فرقوں پر کتاب نازل ہوئی تھی اورہم تو ان کے پڑھنے پڑھانے سے بے خبر تھے'),
(946, 6, 157, 'یا یہ کہو کہ اگر ہم پر کتاب نازل کی جاتی تو ہم ان سے بہتر راہ پر چلتے سو تمہارے پاس تمہارے رب کی ھرف سے ایک واضح کتاب اور ہدایت اور رحمت آ چکی ہے اب اس سے زیادہ کون ظالم ہے جو الله کی آیتوں کوجھٹلائے اور ان سے منہ موڑے جو لوگ ہماری آیتوں سے منہ موڑتے ہیں ہم انہیں ان کے منہ موڑنے کے باعث برے عذاب کی سزا دیں گے'),
(947, 6, 158, 'یہ لوگ اس کے منتظر ہیں کہ ان کے پا س فرشتے آویں یا تیرا رب آئے یا تیرے رب کی کوئی نشانی آئے گی تو کسی ایسے شخص کا ایمان کام نہ آئے گا جو پہلے ایمان نہ لایا ہو یا اس نے ایمان لانے کے بعد کوئی نیک کام نہ کیا ہو کہہ دو انتظار کرو ہم بھی انتظار کرنے والے ہیں'),
(948, 6, 159, 'جنہوں نے اپنے دین کو ٹکڑے ٹکڑے کر دیا اور کئی جماعتیں بن گئے تیرا ان سے کوئی تعلق نہیں اس کا کام الله ہی کے حوالے ہے پھر وہی انہیں بتلائے گا جو کچھ وہ کرتے تھے'),
(949, 6, 160, 'جو کوئی ایک نیکی کرے گا اس کے لیے دس گنا اجر ہے اور جو بدی کرے گا سو اسے اسی کے برابر سزا دی جائے گی اور ان پر ظلم نہ کیا جائے گا'),
(950, 6, 161, 'کہہ دو میرے رب نے مجھےایک سیدھا راستہ بتلا دیا ہے ایک صحیح دین ابراھیم کی ملت جو ایک ہی طرف کاتھا اور مشرکوں میں سے نہیں تھا'),
(951, 6, 162, 'کہہ دو بے شک میری نماز اور میری قربانی اور میرا جینا اور میرا مرنا الله ہی کے لیے ہے جو سارے جہان کا پالنے والا ہے'),
(952, 6, 163, 'اس کا کوئی شریک نہیں اور مجھے اسی کا حکم دیا گیا تھا اور میں سب سے پہلے فرمانبردار ہوں'),
(953, 6, 164, 'کہہ دو کیا اب میں الله کے سوا اور کوئی رب تلاش کروں حالانکہ وہی ہر چیز کا رب ہے اور جو شخص کوئی گناہ کرے گاتو وہ اسی کے ذمہ ہے اور ایک شخص دوسرے کا بوجھ نہیں اٹھائے گا پھر تمہارے رب کے ہاں ہی سب کو لوٹ کر جانا ہے سو جن باتو ں میں تم جھگڑتے تھے وہ تمہیں بتلاد ے گا'),
(954, 6, 165, 'اس نے تمہیں زمین میں نائب بنا یا ہے اور بعض کے بعض پر درجے بلند کر دیے ہیں تاکہ تمہیں اپنے دیے ہوئے حکموں میں آزمائے بے شک تیرا رب جلدی عذاب دینے والا ہے اور بے شک وہ بخشنے والا مہربان ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'المصۤ'),
(956, 7, 2, 'یہ کتاب تیری طرف بھیجی گئی ہے تاکہ تو اس کے ذریعہ سے ڈرائے اور اس سے تیرے دل میں تنگی نہ ہونی چاہیئے اور یہ ایمان والوں کے لیے نصیحت ہے'),
(957, 7, 3, 'جو چیز تمہارے رب کی طرف سے تم پر اتری ہے اس کا اتباع کرو اور الله کو چھوڑ کر دوسرے دوستوں کی تابعداری نہ کرو تم لوگ بہت ہی کم نصیحت مانتے ہو'),
(958, 7, 4, 'اور کتنی بستیاں ہم نے ہلاک کر دی ہیں جن پر ہمارا عذاب رات کو آیا ایسی حالت میں کہ دوپہر کوسونے والے تھے'),
(959, 7, 5, 'جس وقت ان پر ہمارا عذاب آیا پھر ان کی یہی پکار تھی کہتے تھے بے شک ہم ہی ظالم تھے'),
(960, 7, 6, 'پھر ہم ان لوگوں سے ضرور سوال کریں گے جن کے پاس پیغمبر بھیجے گئے تھے اور ان پیغمبروں سے ضرور پوچھیں گے'),
(961, 7, 7, 'پھر اپنے علم کی بناء پر ان کے سامنے بیان کر دیں گے اور ہم کہیں حاضر نہ تھے'),
(962, 7, 8, 'اورواقعی اس دن وزن بھی ہوگا جس شخص کا پلہ بھاری ہو گا سو ایسے لوگ کامیاب ہوں گے'),
(963, 7, 9, 'اور جس کا پلہ ہلکا ہو گا سو یہ لوگ ہوں گے جنہوں نے اپنا نقصان کیا اس لیے کہ ہماری آیتوں کا انکار کرتے تھے'),
(964, 7, 10, 'اور ہم نے تمہیں زمین میں جگہ دی اور اس میں تمہاری زندگی کا سامان بنا دیا تم بہت کم شکر کرتے ہو'),
(965, 7, 11, 'او رہم نے تمہیں پیدا کیا پھر تمہاری صورتیں بنائیں پھر فرشتوں کو حکم دیا کہ آدم کو سجدہ کرو پھر سوائے ابلیس کے سب نے سجدہ کیا وہ سجدہ کرنے والوں میں سے نہ تھا'),
(966, 7, 12, 'فرمایاتجھے سجدہ کرنے سے کس چیز نے منع کیا ہے جب کہ میں نے تجھے حکم دیا کہا میں اس سے بہتر ہوں تو نے مجھے آگ سے بنایا اور اسے مٹی سے بنایا ہے'),
(967, 7, 13, 'کہاتو یہاں سے اتر جا تجھے یہ لائق نہیں کہ یہاں تکبر کرے پس نکل جا بے شک تو ذلیلوں میں سے ہے'),
(968, 7, 14, 'کہا مجھے اس دن تک مہلت دے جس دن لوگ قبرو ں سے اٹھائے جائیں گے'),
(969, 7, 15, 'فرمایا تجھے مہلت دی گئی ہے'),
(970, 7, 16, 'کہا جیسا تو نے مجھے گمراہ کیا ہے میں بھی ضرور ان کی تاک میں تیری سیدھی راہ پر بیٹھوں گا'),
(971, 7, 17, 'پھر ان کے پاس ان کے آگے ان کے پیچھے ان کے دائیں اور ان کے بائیں سےآؤں گا اور تو اکثر کو ان میں سے شکر گزار نہیں پائے گا'),
(972, 7, 18, 'فرمایا یہاں سے ذلیل و خوار ہو کر نکل جا جو شخص ان سے تیرا کہا مانے گا میں تم سب کو جہنم میں بھر دوں گا'),
(973, 7, 19, 'اور اے آدم تو اور تیری عورت جنت میں رہو پھر جہاں سے چاہو کھاؤ اور اس درخت کے پاس نہ جاؤ ورنہ بے انصافوں میں سے ہو جاؤ گے'),
(974, 7, 20, 'پھرانہیں شیطان نے بہکایا تاکہان کی شرم گاہیں جو ایک دوسرے سے چھپائی گئی تھیں ان کے سامنے کھول دے اور کہا تمہیں تمہارے رب نے اس درخت سے نہیں روکا مگر اس لیے کہ کہیں تم فرشتے ہو جاؤ یا ہمیشہ رہنے والے ہو جاؤ'),
(975, 7, 21, 'اور ان کے روبرو قسم کھائی کہ البتہ میں تمہارا خیرا خواہ ہوں'),
(976, 7, 22, 'پھر انہیں دھوکہ سے مائل کر لیا پھرجب ان دونوں نے درخت کو چکھا تو ان پر ان کی شرم گاہیں کھل گئیں اور اپنے اوپر بہشت کے پتے جوڑنے لگے اورانہیں ان کے رب نے پکارا کیا میں نے تمہیں اس درخت سے منع نہیں کیا تھا اور تمہیں کہ نہ دیا تھا کہ شیطان تمہارا کھلا دشمن ہے'),
(977, 7, 23, 'ان دونوں نے کہا اے رب ہمارے ہم نے اپنی جانوں پر ظلم کیا اور اگر تو ہمیں نہ بخشے گا اور ہم پر رحم نہ کرے گا تو ہم ضرور تباہ ہوجائیں گے'),
(978, 7, 24, 'فرمایا یہاں سے اترو تم ایک دوسرے کے دشمن ہو گے اور تمہارے لیے زمین میں ٹھکانا ہے اور ایک وقت تک نفع اٹھانا ہے'),
(979, 7, 25, 'فرمایا تم اسی میں زندہ رہو گے اور اسی میں مرو گے اور اسی سے نکالے جاؤ گے'),
(980, 7, 26, 'اے آدم کی اولاد ہم نے تم پر پوشاک اتاری جو تمہاری شرم گاہیں ڈھانکتی ہیں اور آرائش کے کپڑے بھی اتارے اور پرہیزگاری کا لباس وہ سب سے بہتر ہے یہ الله کی قدرت کی نشانیاں ہیں تاکہ وہ نصیحت حاصل کریں'),
(981, 7, 27, 'اے آدم کی اولاد تمہیں شیطان نہ بہکائے جیسا کہ اس نے تمہارے ماں باپ کو بہشت سے نکال دیا ان سے ان کے کپڑے اتروائے تاکہ تمہیں ان کی شرمگاہیں دکھائے وہ اور اس کی قوم تمہیں دیکھتی ہے جہاں سے تم انہیں نہیں دیکھتے ہم نے شیطانوں کو ان لوگوں کا دوست بنادیا ہے جوایمان نہیں لاتے'),
(982, 7, 28, 'اور جب کوئی برا کام کرتے ہیں تو کہتے ہیں کہ ہم نے اسی طرح اپنے باپ دادا کو کرتے دیکھا ہے اور الله نے بھی ہمیں یہ حکم دیا ہے کہہ دو بے شک الله بے حیائی کا حکم نہیں کرتا الله کے ذمہ وہ باتیں کیوں لگاتے ہو جو تمہیں معلوم نہیں'),
(983, 7, 29, 'کہہ دو میرے رب نے انصاف کا حکم دیا ہے اور ہر نماز کےوقت اپنے منہ سیدھے کرو اور اس کے خالص فرمانبردار ہو کر اسے پکارو جس طرح تمہیں پہلے پیدا کیا ہے اسی طرح دوبارہ پیدا ہو گے'),
(984, 7, 30, 'ایک جماعت کو ہدایت دی اور ایک جماعت پر گمراہی ثابت ہو چکی انہوں نے الله کو چھوڑ کر شیطان کو اپنا دوست بنایا ہے اور خیال کرتے ہیں کہ وہ ہدایت پر ہیں'),
(985, 7, 31, 'اے آدم کی اولاد تم مسجد کی حاضری کے وقت اپنا لباس پہن لیا کرو اور کھاؤ اور پیئو اور حد سے نہ نکلو بے شک الله حد سے نکلنے والوں کو پسند نہیں کرتا'),
(986, 7, 32, 'کہہ دو الله کی زینت کو کس نے حرام کیا ہے جو اس نے اپنے بندو ں کے واسطے پیدا کی ہے اورکس نے کھانے کی ستھری چیزیں (حرام کیں) کہہ دو دنیا کی زندگی میں یہ نعمتیں اصل میں ایمان والوں کے لیے ہیں قیامت کے دن خالص انہیں کے لیے ہوجائیں گی اسی طرح ہم آیتیں مفصل بیان کرتے ہیں ان کے لیے جو سمجھتے ہیں'),
(987, 7, 33, 'کہہ دو میرے رب نے صرف بے حیائی کی باتو ں کو حرام کیا ہے خواہ وہ علانیہ ہوں یا پوشیدہ اور ہر گناہ کواور ناحق کسی پر ظلم کرنے کو بھی اور یہ کہ الله پر وہ باتیں کہو جو تم نہیں جانتے'),
(988, 7, 34, 'اور ہر ایک گروہ کے لیے ایک معیاد معین ہے پھر جب وہ معیاد ختم ہو گی اور وقت نہ ایک گھڑی پیچھے ہٹیں گے اور نہ آگے بڑھیں گے'),
(989, 7, 35, 'اے ادم کی اولاد اگر تم میں سے تمہارے پاس رسول آئیں جو تمہیں میری آیتیں سنائیں پھر جو شخص ڈرے گا اور اصلاح کرے گا ایسوں پر کوئی خوف نہ ہوگا اور نہ وہ غم کھائیں گے'),
(990, 7, 36, 'اور جنہوں نے ہماری آیتو ں کو جھٹلایا اوران سے تکبر کیا وہی دوزخی ہیں وہ اس میں ہمیشہ رہنے والے ہوں گے'),
(991, 7, 37, 'پھر اس سے زیادہ ظالم کون ہوگا الله پر بہتان باندھے یا اس کے حکموں کو جھٹلائے ان لوگوں کا جو کچھ نصیب ہے وہ ان کو مل جائے گا یہاں تک کہ جب ان کے ہاں ہمارے بھیجے ہوئے فرشتے ان کی روح قبض کرنے کے لیے آئيں گے تو کہیں گے کہ وہ کہاں گئے الله کو چھوڑ کر جن کی تم عبادت کرتے تھے کہیں گے ہم سے سب غائب ہو گئے اور اپنے کافر ہونے کااقرار کرنے لگیں گے'),
(992, 7, 38, 'فرمائے گا جنوں اور آدمیوں میں سے جو امتیں تم سے پہلے ہو چکی ہیں ان کے ساتھ دوزخ میں داخل ہو جاؤ جب ایک امت داخل ہو گی تو دوسری پر لعنت کرے گی یہاں تک کہ جب اس میں سب گر جائیں گے تو ن کے پچھلے پہلوں سے کہیں گے اے رب ہمارے ہمیں انہوں نے گمراہ کیا سو تو انہیں آگ کا دگنا عذاب دے فرمائے گا کہ دونوں کو دگنا ہے لیکن تم نہیں جانتے'),
(993, 7, 39, 'اور پہلے پچھلوں سے کہیں گے پس تمہیں ہم پر کوئی فضیلت نہیں پس بسب اپنی کمائی کے عذاب چکھو'),
(994, 7, 40, 'بے شک جنہوں نے ہماری آیتو ں کو جھٹلایا اوران کے مقابلہ میں تکبر کیا ان کے لیے آسمان کے دروازے نہیں کھولے جائیں گے اورنہ وہ جنت میں داخل ہوں گے یہاں تک کہ اونٹ سوئی کے ناکے میں گھس جائے اور ہم گناہگاروں کو اسی طرح سزا دیتے ہیں'),
(995, 7, 41, 'ان کے لیے دوزخ کا بچھونا اور اوپر سے اوڑھنا ہے اور ہم ظالموں کو ایسی ہی سزا دیا کرتے ہیں'),
(996, 7, 42, 'اور جو ایمان لانے اور نیکیاں کیں ہم کسی پر بوجھ نہیں رکھتے مگر اس کی طاقت کےموافق وہی بہشتی ہیں وہ اس میں ہمیشہ رہنے والے ہیں'),
(997, 7, 43, 'اور جوکچھ ان کے دلو ں میں خفگی ہو گی ہم اسے دور کردیں گے ان کے نیچے نہریں بہتی ہوں گی اور وہ کہیں گےکہ الله کاشکر ہے جس نے ہمیں یہاں تک پہنچایا اور ہم راہ نہ پاتے اگر الله ہماری رہنمائی نہ فرماتا بے شک ہمارے رب کے رسول سچی بات لائے تھے جور آواز آئے گی کہ یہ جنت ہے تم اپنے اعمال کے بدلے اس کے وارث ہو گئے ہو'),
(998, 7, 44, 'اور بہشت والے دوزخیوں کو پکاریں گے کہ ہم نے وعدہ سچا پایا جو ہمارے رب نے ہم سے کیا تھا آیاتم بھی اپنے رب کے وعدہ کو سچا پایا وہ کہیں گے ہاں پھر ایک پکارنے والا ان کے درمیان پکارے گا کہ ان ظالمو ں پر الله کی لعنت ہے'),
(999, 7, 45, 'جو الله کی راہ سے روکتے تھے اور اسے ٹیڑھا کرنا چاہتے تھے اور وہ آخرت کے منکر تھے'),
(1000, 7, 46, 'اور ان دونوں کے درمیان ایک دیوار ہو گی اور اعراف کے اوپر ایسے مرد ہوں گے کہ ہر ایک کو اس کی نشانی سے پہچان لیں گے اور جنت والوں کو پکار کر کہیں گے کہ تم پر سلام ہو وہ ابھی جنت میں داخل نہیں ہوئے اور امیدوار ہیں'),
(1001, 7, 47, 'اورجب ان کی نگاہ دوزخ والوں کی طرف پھرے گی تو کہیں گے اے رب ہمارے ہمیں ظالموں کے ساتھ نہ ملا'),
(1002, 7, 48, 'اور اعراف والے پکاریں گے جنہیں وہ ان کی نشانی سے پہچانتے ہوں گے کہیں گے تمہاری جماعت تمہارے کسی کام نہ آئی اور نہ وہ جو تم تکبر کیا کرتے تھے'),
(1003, 7, 49, 'یہ وہی ہیں جن کے متعلق تم قسم کھاتے تھے کہ انہیں الله کی رحمت نہیں پہنچے گی (انہیں کہا گیا ہے) جنت میں چلے جاؤ تم پر نہ ڈر ہے اور نہ تم غمگین ہو گے'),
(1004, 7, 50, 'اور دوزخ والے بہشت والوں کو پکاریں گے کہ ہم پر تھوڑا سا پانی بہا دو یا کچھ اس چیز میں سے دو جو تمہیں الله نے رزق دیا ہے کہیں گے بے شک الله نے انہیں دونوں چیزوں کو کافروں پر حرام کیا ہے'),
(1005, 7, 51, 'جنہوں نے اپنا دین تماشا اور کھیل بنایا اور انہیں دنیا کی زندگی نے دھوکے میں ڈال دیا ہے سو آج ہم انہیں بھلا دیں گے جس طرح انہوں نے اس دن کی ملاقات کو بھلا دیا تھا اور جیسا وہ ہماری آیتوں سے انکار کرتے تھے'),
(1006, 7, 52, 'اور ہم نے ان کے پاس ایک ایسی کتاب پہنچا دی ہے جسے ہم نے اپنے علم کامل سے بہت ہی واضح کر کے بیان کر دیا ہے وہ ہدایت ہے اوررحمت ہے'),
(1007, 7, 53, 'ان لوگو ں کے لیے ےجو ایمان لے آئے ہیں انہیں اور کسی بات کا انتظار نہیں صرف آخری نتیجہ کا انتظار ہے جس دن اس کا آخری نتیجہ سامنے آئے گا اس دن جو اسے پہلے بھولے ہوئے تھے کہیں گے کہ واقعی ہمارے رب کے رسول کی سچی باتیں لائے تھے سو اب کیا کوئی ہمارا سفارشی ہے جو ہماری سفارش کر ےیاکیا ہم پھر واپس بھیجے جا سکتے ہیں تاکہ ہم ان کے اعمال کے خلاف جنہیں کیا کرتے تھے دوسرے اعمال کریں بے شک انہوں نے اپنے آپ کو خسارے میں ڈا ل دیا اور جو باتیں بناتے تھے وہ سب گم ہو گئیں'),
(1008, 7, 54, 'بے شک تمہارا رب الله ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا پھرعرش پر قرار پکڑا رات سے دن کو ڈھانک دیتا ہے وہ اس کے پیچھے دوڑتا ہوا آتا ہے اور سورج اورچاند اور ستارے اپنے حکم کے تابعدار بنا کر پیدا کیے اسی کا کام ہے پیدا کرنا اور حکم فرمانا الله بڑی برکت والا ہے جو سارے جہان کا رب ہے'),
(1009, 7, 55, 'اپنے رب کو عاجزی اور چپکے سے پکارو اسے حد سے بڑھنے والے پسند نہیں آتے'),
(1010, 7, 56, 'اور زمین میں اس کی اصلاح کے بعد فساد مت کرو اور اسے ڈر اور طمع سے پکارو بے شک اللهکی رحمت نیکو کاروں سے قریب ہے'),
(1011, 7, 57, 'اور وہی ہے جو مینہ سے پہلے خوشخبری دینے والی ہوائیں چلاتا ہے یہاں تک کہ جب ہوائیں بھاری بادلو ں کو اٹھا لاتی ہیں تو ہم ا س بادل کو مردہ شہر کی طرف ہانک دیتے ہیں پھر ہم ا س بادل سے پانی اتارتے ہیں پھر اس سے سب طرح کے پھل نکالتے ہیں اسی طرح ہم مردوں کو نکالیں گے تاکہ تم نصیحت حاصل کرو'),
(1012, 7, 58, 'اور جو شہر پاکیزہ ہے اس کا سبزہ اس کے رب کے حکم سے نکلتا ہے اور جو اس میں خراب ہے جو کچھ اس میں سے نکلتا ہے ناقص ہی ہوتا ہے اسی طرح ہم شکر گزاروں کے لیے مختلف طریقوں سے آیتیں بیان کرتے ہیں'),
(1013, 7, 59, 'بے شک ہم نے نوح کو اس کی قوم کی طرف بھیجا پس اس نے کہا اے میری قوم الله کی بندگی کرو اس کے سوا تمہارا کوئی معبود نہیں میں تم پر ایک بڑے دن کے عذاب سے ڈرتا ہوں'),
(1014, 7, 60, 'اس کی قو م کے سرداروں نے کہا ہم تجھے صریح گمراہی میں دیکھتے ہیں'),
(1015, 7, 61, 'فرمایا اے میری قوم میں ہرگز گمراہ نہیں ہوں لیکن میں جہان کے پروردگار کی طرف سے بھیجا ہوا ہوں'),
(1016, 7, 62, 'تمہیں اپنے رب کے پیغام پہنچاتا ہوں اور تمہیں نصیحت کرتا ہوں اور الله کی طرف سے وہ باتیں جانتا ہوں جو تم نہیں جانتے'),
(1017, 7, 63, 'کیا تمہیں اس بات سے تعجب ہوا کہ تمہارے رب کی طرف سے تم ہی میں سے ایک مرد کی زبانی تمہارے پاس نصیحت آئی ہے تاکہ وہ تمہیں ڈرائے اورتاکہ تم پرہیزگار ہو جاؤ اور تاکہ تم پرحم کیے جاؤ'),
(1018, 7, 64, 'پھر انہو ں نے اسے جھٹلایا پھر ہم نے اسے اور ا سکے ساتھیوں کو کشتی میں بچا لیا اور جو ہماری آیتوں کو جھٹلاتے تھے انہیں غرق کر دیا بے شک وہ لوگ اندھے تھے'),
(1019, 7, 65, 'اور قوم عاد کی طرف ان کے بھائی ہود کو بھیجا فرمایا اے میری قوم الله کی بندگی کرو اس کے سوا تمہارا کوئی معبود نہیں سو کیا تم ڈرتے نہیں'),
(1020, 7, 66, 'اس کی قوم کےکافر سردار بولےہم تو تمہیں بے وقوف سمجھتے ہیں اور ہم تجھے جھوٹا خیال کرتے ہیں'),
(1021, 7, 67, 'فرمایا اے میری قوم میں بے وقوف نہیں ہوں لیکن میں پروردگار عالم کی طرف سے بھیجا ہوا ہوں'),
(1022, 7, 68, 'تمہیں اپنے رب کے پیغام پہنچاتا ہوں اور تمہارا امانت دار خیر خواہ ہوں'),
(1023, 7, 69, 'کیا تمہیں تعجب ہوا کہ تمہارے رب کی طرف سے تمہیں میں سے ایک مرد کی زبانی تمہارے پاس نصیحت آئی ہے تاکہ تمہیں ڈرائے اور یاد کرو جب کہ تمہیں قوم نوح کے بعد جانشین بنا یا اورڈیل ڈول میں تمہیں پھیلاؤ زیادہ دیا سو الله کی نعمتوں کو یاد کرو تاکہ تم نجات پاؤ'),
(1024, 7, 70, 'انہوں نے کہا کیا تو ہمارے پاس اس لیے آیا ہے کہ ہم ایک الله کی بندگی کریں اور ہمارے باپ دادا جنہیں پوجتے رہے انہیں چھوڑ دیں پس جس چیز سے تو ہمیں ڈراتا ہے وہ لے آ اگرتو سچا ہے'),
(1025, 7, 71, 'فرمایا تمہارے رب کی طرف سے تم پر عذاب اور غصہ واقع ہو چکا مجھ سے ان ناموں پر کیوں جھگڑتے ہو جوتم نے اور تمہارے باپ دادؤں نے مقرر کیے ہیں الله نے ان کے لیے کوئی دلیل نہیں اتاری سو انتظار کرو میں بھی تمہارے ساتھ انتظار کرنے والا ہوں'),
(1026, 7, 72, 'پھر ہم نے اسے اور اس کے ساتھیوں کو اپنی رحمت سے بچا لیا اور جو ہماری آیتوں کو جھٹلاتے تھے ان کی جڑ کاٹ دی اور وہ مومن نہیں تھے'),
(1027, 7, 73, 'اور ثمود کی طرف ان کے بھائی صالح کو بھیجا فرمایا اے میری قوم الله کی بندگی کرو اس کے سوا تمہارا کوئی معبود نہیں تمہیں تمہراے رب کی طرف سے دلیل پہنچ چکی ہے یہ الله کی اونٹنی تمہارے لیے نشانی ہے سو اسے چھوڑ دو کہ الله کی زمین میں کھائے اور اسے بری طرح سے ہاتھ نہ لگاؤ ورنہ تمہیں دردناک عذاب پکڑے گا'),
(1028, 7, 74, 'اور یاد کرو جب کہ تمہیں عاد کے بعد جانشین بنایا اور تمہیں زمین میں جگہ دی کہ نرم زمین میں محل بناتے ہو اور پہاڑو ں میں گھر تراشتے ہو سو الله کے احسان کو یاد کرو اور زمین میں فساد مت مچاتے پھرو'),
(1029, 7, 75, 'اس قوم کے متکبر سرداروں نے غریبوں سے کہا جو ایمان لاچکے تھے کیا تمہیں یقن ہے کہ صالح کو اس کے رب نے بھیجا ہے انہوں نے کہا جو وہ لے کر آیا ہے ہم اس پرایمان لانے والے ہیں'),
(1030, 7, 76, 'متکبروں نے کہا جس پر تمہیں یقین ہے ہم اسے نہیں مانتے'),
(1031, 7, 77, 'پھر اونٹنی کے پاؤں کاٹ ڈالے اور اپنے رب کے حکم سے سرکشی کی اور کہا اے صالح لے آ ہم پر جس سے تو ہمیں ڈراتا تھا اگر تو رسول ہے'),
(1032, 7, 78, 'پس انہیں زلزلہ نے آ پکڑا پھر صبح کو اپنے گھروں میں اوندھے پڑے ہوئے رہ گئے'),
(1033, 7, 79, 'پھر صالح ان سے منہ موڑ کر چلے اور فرمایا اے میری قوم میں تمہیں اپنے رب کا پیغام پہنچا چکا اور تمہاری خیر خواہی کی لیکن تم خیر خواہوں کو پسند نہیں کرتے تھے'),
(1034, 7, 80, 'اور لوط کو بھیجا جب اس نے اپنی قوم کو کہا کیا تم ایسی بے حیائی کرتے ہو کہ تم سے پہلے اسے جہان میں کسی نے نہیں کیا'),
(1035, 7, 81, 'بے شک تم عورتوں کو چھوڑ کر مردوں سے شہوت رانی کرتے ہو بلکہ تم حد سے بڑھنے والے ہو'),
(1036, 7, 82, 'اور اس کی قوم نے کوئی جواب نہیں دیا مگر یہی کہا کہ انہیں اپنےشہر سے نکال دو یہ لوگ بہت ہی پاک بننا چاہتے ہیں'),
(1037, 7, 83, 'پھر ہم نے اسےاور اس کے گھر والوں کو سوائے اس کی بیوی کے بچا لیا کہ وہ وہاں رہنے والو ں میں رہ گئی'),
(1038, 7, 84, 'اور ہم نے ان پر مینہ برسایا پھر دیکھیئے گناہگاروں کا کیا انجام ہوا'),
(1039, 7, 85, 'اورمدین کی طرف اس کے بھائی شعیب کو بھیجا فرمایااے میری قوم الله کی بندگی کرو اس کے سوا تمہار کوئی معبود نہیں تمہارے رب کی طرف سے تمہارے پاس دلیل پہنچ چکی ہے سو ناپ او رتول کو پورا کرو اور لوگوں کو ان کی چیزیں گھٹا کر نہ دو اور زمین میں اس کی اصلاح کے بعد فساد مت کرو یہ تمہارے لیے بہتر ہے اگر تم ایمان دار ہو'),
(1040, 7, 86, 'اور سڑکوں پر اس غرض سے مت بیٹھا کرو کہ الله پر ایمان لانے والوں کو دھمکیاں دو اور الله کی راہ سے روکو اوراس میں ٹیڑھا پن تلاش کرو اور اس حلات کو یاد کرو جب کہ تم تھوڑے تھے پھر الله نے تمہیں زیادہ کر دیا اور دیکھو فساد کرنے والوں کا انجام کیا ہوا ہے'),
(1041, 7, 87, 'اور اگر تم میں سے ایک جماعت اس پر ایمان لے آئی ہے جو میرے ذریعے سے بھیجا گیا ہے اور ایک جماعت ایمان نہیں لائی پس صبر کرو جب تک الله ہمارے درمیان فیصلہ کرے اور وہ سب سے بہتر فیصلہ کرنے والا ہے'),
(1042, 7, 88, 'اس کی قوم کے متکبر سرداروں نے کہا اے شعیب ہم تجھے اور انہیں جو تجھ پر ایمان لائے ہیں اپنے شہر سے ضرور نکال دیں گے یا یہ کہ تم ہمارے دین میں واپس آ جاؤ فرمایا کیا اگرچہ ہم اس دین کو ناپسند کرنے والے ہوں'),
(1043, 7, 89, 'ہم تو الله پر بہتان باندھنے والے ہو جائیں اگر تمہارے مذہب میں واپس آئيں بعد اس کے کہ الله نے ہمیں اس سے نجات دی ہے اور ہمیں یہ حق نہیں کہ تمہارے دین میں لوٹ کر آئيں مگر یہ کہ الله چاہے جو ہمارا رب ہے ہمارے رب کا علم ہر چیز پر احاطہ کیے ہوئے ہے ہم الله ہی پر بھروسہ کرتے ہیں اے رب ہمارے اور ہماری قوم کے درمیان حق کے موافق فیصلہ کر دے اور تو بہتر فیصلہ کرنے والا ہے'),
(1044, 7, 90, 'اس کی قوم میں جو کافر سردار تھے انہوں نے کہا اگر تم شعیب کی تابعداری کرو گے تو بے شک نقصان اٹھاؤ گے'),
(1045, 7, 91, 'پھر انہیں زلزلہ نے آ پکڑا پھر وہ صبح تک اپنے گھرو ں میں اوندھے پڑے ہوئے رہ گئے'),
(1046, 7, 92, 'جنہوں نے شعیب کو جھٹلایا گو وہ وہاں کبھی بسے ہی نہیں تھے جنہوں نے شعیب کو جھٹلایا وہی نقصان اٹھانے والے ہوئے'),
(1047, 7, 93, 'پھر ان سے منہ پھیرا اور کہا اے میری قوم تحقیق میں نے تمہیں اپنے رب کے احکام پہنچا دیے اور میں نے تمہارے لیے خیر خواہی کی پھر کافروں کی قوم پر میں کیوں کر غم کھاؤں'),
(1048, 7, 94, 'او رہم نے کسی بستی میں کوئی پیغمبرنہیں بھیجا مگر وہاں کے لوگوں کو سختی اور تکلیف میں پکڑا تاکہ وہ عاجزی کریں'),
(1049, 7, 95, 'پھر ہم نے برائی کی جگہ بھلائی بدل دی یہاں تک کہ وہ زیادہ ہو گئے اور کہنے لگے کہ ہمارے باپ داداؤں کو بھی تکلیف اور خوشی کا وقت آیا تھا پھر ہم نے انہیں اچانک پکڑا اور ان کو خبر نہ ہوئی'),
(1050, 7, 96, 'اور اگر بستیوں والے ایمان لے آتے اور ڈرتے تو ہم ان پر آسمان اور زمین سے نعمتوں کے دروازے کھول دیتے لیکن انہوں نے جھٹلایا پھر ہم نے ان کے اعمال کے سبب سے گرفت کی'),
(1051, 7, 97, 'کیا بستی والے نڈر ہو چکے ہیں کہ ہماری طرف سے ان پر رات کو عذاب آئے جب وہ سو رہے ہوں'),
(1052, 7, 98, 'یا بستیوں والے اس بات سے نڈر ہو چکے ہیں کہ ان پر ہمارا عذاب دن چڑھے آئے جب وہ کھیل رہے ہوں'),
(1053, 7, 99, 'کیا وہ الله کی اچانک پکڑ سے بے فکر ہوئے ہیں بس الله کی اچانک پکڑ سے بے فکر ہو گئے ہیں پس الله کی اچانک پکڑ سے بے فکر نہیں ہوتے مگر نقصان اٹھانے والے'),
(1054, 7, 100, 'کیا ان لوگو ں پر جو زمین کے وارث ہوئے ہیں وہاں کے لوگوں کے ہلاک ہونے کے بعد یہ ظاہر نہیں ہوا کہ اگر ہم چاہیں تو انہیں ان کے گناہوں کے سبب سے پکڑ لیں اور ہم نے ان کے دلوں پر مہر لگا دی ہے پس وہ نہیں سنتے'),
(1055, 7, 101, 'یہ بستیاں ہیں جن کے حالات ہم تمہیں سناتے ہیں بے شک ان کے پاس ان کے رسولروشن نشانیاں لے کر آئے تھے پھر اس بات پر ہرگز ایمان نہ لائے جسے پہلے جھٹلا چلے تھے کافروں کے دلوں پر الله اسی طرح مہر لگا دیتا ہے'),
(1056, 7, 102, 'اور ہم نے ان کے اکثر لوگو ں میں عہد کا نباہ نہیں پایا اور ان میں سے اکثر نافرمان پایا'),
(1057, 7, 103, 'اس کے بعد ہم نے موسیٰ کو اپنی نشانیاں دے کر فرعون اور اس کے سرداروں کی طرف بھیجا پھر انہوں نے نشانیوں سے بے انصافی کی پھر دیکھ مفسدوں کا انجام کیا ہوا'),
(1058, 7, 104, 'اور موسیٰ نے کہا اے فرعون بے شک میں رب العالمین کی طرف سے رسول ہو کر آیا ہوں'),
(1059, 7, 105, 'میرے لیے یہی مناسب ہے کہ سوائے سچ کے کوئی بات خدا کی طرف منسوب نہ کروں میں تمہارے رب کی طرف سے تمہارے پاس ایک بڑی دلیل لایا ہوں پس بنی اسرائیل کو میرے ساتھ بھیج دے'),
(1060, 7, 106, 'کہا اگرتو کوئی نشانی لے کر آیا ہے تو وہ لا اگر تو سچا ہے'),
(1061, 7, 107, 'پھر اس نے اپنا عصا ڈال دیا وہ اس وقت صریح اژدھا ہو گیا'),
(1062, 7, 108, 'او راپنا ہاتھ نکالا تو اسی وقت دیکھنے والوں کے لیے سفید نظر آنے لگا'),
(1063, 7, 109, 'فرعون کی قوم کے سرداروں نے کہا بے شک یہ بڑا ماہر جادو گر ہے'),
(1064, 7, 110, 'تمہیں تمہارے ملک سے نکالنا چاہتا ہے پس تم کیا مشورہ دیتے ہو'),
(1065, 7, 111, 'انہوں نے کہا کہ اسے اور اس کے بھائی کو مہلت دے اور شہروں میں جمع کرنے والے بھیج دے'),
(1066, 7, 112, 'تاکہ تیرے پاس ماہر جادوگر کو لے آئيں'),
(1067, 7, 113, 'اور جادوگر فرعون کے پاس آئے کہا اگر ہم غالب آئے تو ہمیں کچھ صلہ بھی ملے گا'),
(1068, 7, 114, 'کہا ہاں اور بے شک تم مقرب ہو جاؤ گے'),
(1069, 7, 115, 'کہا اے موسیٰ یا تو تو ڈال یا ہم ڈالتے ہیں'),
(1070, 7, 116, 'کہا تم ڈالوپس جب انہوں نے ڈالا تو لوگو ں کی نظر بندی کر دی اور انہیں ڈرایا اور اک طرح کا بڑا جادو دکھایا'),
(1071, 7, 117, 'اور ہم نے موسیٰ کو وحی کے ذریعے سے حکم دیا اپنا عصا ڈال دے سو وہ اسی وقت نگلنے لگا جو کھیل انہو ں نے بنا رکھا تھا'),
(1072, 7, 118, 'پھر حق ظاہر ہو گیا اور جو انہوں نے بنایا تھا وہ غلط ہو گیا'),
(1073, 7, 119, 'پھر اس جگہ ہار گئے اور ذلیل ہو کر لوٹے'),
(1074, 7, 120, 'اور جادوگر سجدہ میں گر پڑے'),
(1075, 7, 121, 'کہاہم رب العالمین پر ایمان لائے'),
(1076, 7, 122, 'جو موسیٰ اور ہارون کا رب ہے'),
(1077, 7, 123, 'فرعون نے کہا تم اس پر میری اجازت سے پہلے ہی ایمان لے آئے یہ تو مکر ہے جو تم سب نے اس شہر میں بنایا ہے تاکہ اس شہر کے رہنے والوں کو نکال دو سو اب تمہیں معلوم ہو جائے گا'),
(1078, 7, 124, 'میں ضرور تمہارے ہاتھ اور دوسری طرف سے پاؤں کاٹوں گا پھر تم سب کو سولی چڑھا دوں گا'),
(1079, 7, 125, 'انہوں نے کہا ہمیں تو اپنے رب کی طرف لوٹ کر جانا ہی ہے'),
(1080, 7, 126, 'اور تمہیں ہم سے یہی دشمنی ہے کہ ہم نے اپنے رب کی نشانیوں کو مان لیا جب وہ ہمارے پاس آئیں اے ہمارے رب ہمارے اوپر صبر ڈال اورہمیں مسلمان کر کے موت دے'),
(1081, 7, 127, 'اور فرعون کی قوم کے سرداروں نے کہا کیا تو موسیٰ اور اس کی قوم کو چھوڑتا ہے تاکہ وہ ملک میں فساد کریں اور تجھے اورتیرے معبودوں کو چھوڑ دے کہا ہم ان کے بیٹوں کو قتل کریں گے اور ان کی عورتو ں کو زندہ رکھیں گے اور بے شک ہم ان پر غالب ہیں'),
(1082, 7, 128, 'موسیٰ نے اپنی قوم سے کہا الله سے مدد مانگو اور صبر کرو بے شک زمین الله کی ہے اپنے بندوں میں سے جسےچاہے اس کا وارث بنا دے اور انجام بخیر پرہیزگاروں کا ہی ہوتا ہے'),
(1083, 7, 129, 'انہوں نے کہا تیرے آنے سےپہلے بھی ہمیں تکلیفیں دی گئیں اور تیرے آنے کے بعد بھی فرمایا تمہارا رب بہت جلد تمہارے دشمن کو ہلاک کر دے گا اور اس کی بجائے تمہیں اس سرزمین کا مالک بنا دے گا پھر دیکھے گاتم کیا کرتے ہو'),
(1084, 7, 130, 'اور ہم نے فرعون والوں کو قحطوں میں اور میوں کی کمی میں پکڑ لیا تاکہ وہ نصیحت مانیں'),
(1085, 7, 131, 'جب ان پر خوشحالی آتی تو کہتے کہ یہ تو ہمارے لیے ہونا ہی چاہیئے اور اگر انہیں کوئی بدحالی پیش آتی تو موسیٰ اور ان کے ساتھیوں کی نحوست بتلاتے یاد رکھو ان کی نحوست الله کے علم میں ہے لیکن اکثر لوگ نہیں جانتے'),
(1086, 7, 132, 'اور کہا جوکوئی نشانی تو ہمارے پاس لائے گا ہم پر اس کے ذریعہ سے جادو کرے سو ہم تجھ پر ہر گز ایمان نہ لائیں گے'),
(1087, 7, 133, 'پھر ہم نے ان پر طوفان اور ٹدی اور جوئیں اورمینڈک اور خون یہ سب کھلے کھلےمعجزے بھیجے پھر بھی انہوں نے تکبر ہی کیا اور لوگ گناہگار تھے'),
(1088, 7, 134, 'اور جب ان پر کوئی عذاب آتا تو کہتے اے موسیٰ ہمارے لیے اپنے رب سے دعا کر جس کا اس نے تجھ سے عہد کر رکھا ہے اگر تو نے ہم سے یہ عذاب دور کر دیا تو بے شک ہم تجھ پر ایمان لے آئيں گے اور بنی اسرائیل کو تیرے ساتھ بھیج دیں گے'),
(1089, 7, 135, 'پھر جب ہم نےان سے ایک مدت تک عذاب اٹھا لیا کہ انہیں اس مدت تک پہنچنا تھا اس وقت وہ عہد توڑ ڈالتے'),
(1090, 7, 136, 'پھر ہم نے ان سے بدلہ لیا پھر ہم نے انہیں دریا میں ڈبو دیا اس لیے کہ انہوں نے ہماری آیتوں کو جھٹلایا اور وہ ان سے غافل تھے'),
(1091, 7, 137, 'اور ہم نے ان لوگوں کو وارث کر دیا جو اس زمین کے مشرق و مغرب میں کمزور سمجھے جاتے تھے کہ جس میں ہم نے برکت رکھی ہے او رتیرے رب کا نیک وعدہ بنی اسرائیلک کے حق میں ان کے صبر کے باعث پورا ہو گیا اور فرعون اور اس کی قوم نے جو کچھ بنایا تھا ہم نے اسے تباہ کر دیا اور جو وہ اونچی عمارتیں بناتے تھے'),
(1092, 7, 138, 'اور ہم نے بنی اسرائیل کو دریا سے پار اتارا تو ایک ایسی قوم پر پہنچے جو اپنے بتوں کے پوجنے میں لگے ہوئے تھے کہا اے موسیٰ ہمیں بھی ایک ایسا معبود بنا دے جیسے ان کے معبود ہیں فرمایا بے شک تم لوگ جاہل ہو'),
(1093, 7, 139, 'یہ لوگ جس چیز میں لگے ہوئے ہیں وہ تباہ ہونے والی ہے اور جو وہ کر رہے ہیں وہ غلط ہے'),
(1094, 7, 140, 'کہا کیا الله کے سوا تمہارے لیے اور معبود بنا دوں حالانکہ اس نے تمہیں سارے جہاں پر فضیلت دی ہے'),
(1095, 7, 141, 'اور یا د کرو جب ہم نے تمہیں فرعون والوں سے نجات دی جو تمہیں برا عذاب دیتے تھے تمہارے بیٹوں کو مار ڈال دیتے تھے اور تمہاری عورتوں کو زندہ رکھتے تھے اور اس میں تمہارے رب کا بڑا احسان تھا'),
(1096, 7, 142, 'اور موسیٰ سے ہم نے تیس رات کا وعدہ کیا اور انہیں اور دس سے پورا کیا پھر تیرے رب کی مدت چالیس راتیں پوری ہو گئی اور موسیٰ نے اپنے بھائی ہارون سے کہا کہ میری قوم میں میرا جانشین رہ اور اصلاح کرتے رہو اور مفسدوں کی راہ پر مت چل'),
(1097, 7, 143, 'اور جب موسیٰ ہمارے مقرر کردہ وقت پر آئے اور ان کے رب نے ان سے باتیں کیں تو عرض کیا کہ اے میرے رب مجھے دکھا کہ میں تجھے دیکھوں فرمایا کہ تو مجھے ہر گز نہیں دیکھ سکتا لیکن تو پہاڑ کی طرف دیکھتا رہ اگر وہ اپنی جگہ پر ٹھہرا رہا تو تو مجھے دیکھ سکے گا پھر جب اس کے رب نے پہاڑ کی طرف تجلی کی تو اس کو ریزہ ریزہ کر دیا اور موسیٰ بے ہوش ہو کر گر پڑے پھر جب ہوش میں آئے تو عرض کی کہ تیری ذات پاک ہے میں تیری جانب میں توبہ کرتا ہوں اور میں سب سے پہلا یقین لانے والا ہوں'),
(1098, 7, 144, 'فرمایا اے موسیٰ میں نے پیغمبری اور ہم کلامی سے دوسرے لوگوں پر تجھے امتیاز دیا ہے جو کچھ میں نے تجھے عطا کیا ہے اسے لے لو اور شکر کرنے والوں میں سے ہو جاؤ'),
(1099, 7, 145, 'اور ہم نے اسے تختیوں پر ہر قسم کی نصیحت اور ہر چیز کی تفصیل لکھ دی سو انہیں مضبوطی سے پکڑ لے او راپنی قوم کو حکم کر کہ اس کی بہتر باتوں پر عمل کریں عنقریب میں تمہیں نافرمانوں کا ٹھکانہ دکھاؤں گا'),
(1100, 7, 146, 'پھر میں اپنی آیتوں سے انہیں پھیر دوں گا جو زمین میں نا حق تکبر کرتے ہیں اور اگر وہ ساری نشانیاں بھی دیکھ لیں تو بھی ایمان نہیں لائیں گے اور اگر ہدایت کا راستہ دیکھیں تو اسے اپنا راہ نہیں بنائیں گے یہ اس لیے ہے کہ انہوں نے ہماری آیتو ں کو جھٹلایا اور ان سے بے خبر رہے'),
(1101, 7, 147, 'اور جنھوں نے ہماری آیتوں کو اور آخرت کی ملاقات کو جھٹلایا ان کے اعمال ضائع ہو گئے انہیں وہی سزا دی جائے گی جو کچھ وہ کیا کرتے تھے'),
(1102, 7, 148, 'اور موسیٰ کی قوم نے اس کے بعد اپنے زیوروں سے بچھڑا بنا لیا ایک جسم تھا جس میں گائے کی آواز تھی کیا انہوں نے یہ نہ دیکھا کہ وہ ان سے بات بھی نہیں کرتا اور نہ ہی انہیں راہ بتاتا ہے اسے معبود بنا لیا اور وہ ظالم تھے'),
(1103, 7, 149, 'اور جب نادم ہوئے اور معلوم کیا کہ بیشک وہ گمراہ ہوگئے تھےتو کہنے لگے اگر ہمارے رب نے ہم پر رحم نہ کیا اور ہمیں نہ بخشا تو بے شک ہم نقصان پانے والوں میں سے ہوں گے'),
(1104, 7, 150, 'اور جب موسیٰ اپنی قو م کےی طرف غصہ اور رنج میں بھرے ہوئے واپس آئے تو فرمایا تم نے میرے بعد یہ بڑی نامعقول حرکت کی کیا تم نے اپنے رب کے حکم سے پہلے ہی جلد بازی کر لی اور تختیاں پھینک دیں اور اپنے بھائی کا سر پکڑ ا اسے پنی طرف کھینچنے لگا اس نے کہا کہ اے میری ماں کے بیٹے لوگوں نے مجھے کمزور سمجھا اور قریب تھے کہ مجھے مار ڈالیں سو مجھ پر دشمنوں کو نہ ہنسا اور مجھے گناہگار لوگو ں میں نہ ملا'),
(1105, 7, 151, 'کہا اے میرے رب مجھے اور میرے بھائی کو معاف فرما اور ہمیں اپنی رحمت میں داخل کر اور تو سب سے زيادہ رحم کرنے والا ہے'),
(1106, 7, 152, 'بے شک جنہوں نے بچھڑے کو معبود بنایا انہیں ان کے رب کی طرف سے غضب اور دنیا کی زندگی میں ذلت پہنچے گی او رہم بہتان باندھنے والوں کو یہی سزا دیتے ہیں'),
(1107, 7, 153, 'اور جنہوں نے برے کام کیے پھراس کے بعد توبہ کی اور ایمان لے آئے تو بے شک تیرا رب توبہ کے بعد البتہ بخشنے والا مہربان ہے'),
(1108, 7, 154, 'اور جب موسیٰ کا غصہ ٹھنڈا ہوا تو اس نے تختیوں کو اٹھایا اور جو ان میں لکھا ہو ا تھا اس میں ان کے واسطے ہدایت اور رحمت تھی جو اپنے رب سے ڈرتے ہیں'),
(1109, 7, 155, 'اور موسیٰ نے اپنی قوم میں سے ستر مرد ہمارے وعدہ گا ہ پر لانے کے لیے چن لیے پھر جب انہیں زلزلہ نے پکڑ ا تو کہا اے میرے رب اگر تو چاہتا تو پہلے ہی انہیں ہلاک کر دیتا کیا تو ہمیں اس کام پر ہلاک کرتا ہے جو ہماری قوموں کے بیوقوفوں نے کیا یہ سب تیری آزمائش ہے جسے تو چاہے اس سے گمراہ کر دے اور جسے چاہے سیدھا رکھے تو ہی ہمارا کارساز ہے سو ہمیں بخش دے اور ہم پر رحم کر اور تو سب سے بہتر بخشنے والا ہے'),
(1110, 7, 156, 'او رہمارے لیے اس دنیا میں اور آخرت میں بھلائی لکھ ہم نے تیری طرف رجوع کیا فرمایا میں اپنا عذاب جسے چاہتا ہوں کرتا ہوں اور میری رحمت سب چیزوں سے وسیع ہے پس وہ رحمت ان کے لیے لکھوں گا جو ڈرتے ہیں اجور جو زکواة دیتے ہیں اور جو ہماری آیتوں پر ایمان لاتے ہیں'),
(1111, 7, 157, 'وہ لوگ جو اس رسول کی پیروی کرتے ہیں اور جو نبی امی ہے جسے اپنے ہاں تورات اور انجیل میں لکھا ہوا پاتے ہیں وہ ان کو نیکی کا حکم کرتا ہے اور برے کام سے روکتا ہے اوران کے لیے سب پاک چیزیں حلا ل کرتا ہے اور ان پر ناپاک چیزیں حرام کرتا ہے اور ان پر سے ان کے بوجھ اور وہ قیدیں اتارتا ہے جو ان پر تھیں سو جو لوگ اس پر ایمان لائے اور اس کی حمایت کی اور اسے مدد دی اور اس کے نور کے تابع ہو ئے جو اس کے ساتھ بھیجا گیا ہے یہی لوگ نجات پانے والے ہیں'),
(1112, 7, 158, 'کہہ دو اے لوگو تم سب کی طرف الله کا رسول ہوں جس کی حکومت آسمانوں اور زمین میں ہے اس کے سوا اور کوئی معبود نہیں وہی زندہ کرتا اور مارتا ہے پس الله پر ایمان لاؤ اوراس کے رسول نبی امی پر جو کہ الله پر اور اس کے سب کلاموں پر یقین رکھتا ہے اور اس کی پیروی کرو تاکہ تم راہ پاؤ'),
(1113, 7, 159, 'اور موسیٰ کی قوم میں سے ایک جماعت ہے جو حق کی راہ بتاتے ہیں اور اسی کے موافق انصاف کرتے ہیں'),
(1114, 7, 160, 'اور ہم نے انہیں جدا جدا کر دیا بارہ دادوں کی اولاد جو بڑی بڑی جماعتیں تھیں اور موسیٰ کو ہم نے حکم بھیجا جب اس کی قوم نے اس سے پانی مانگا کہ اپنی لاٹھی اس پتھر پر مار تو اس سے بارہ چشمے پھوٹ نکلے ہر قبیلہ نے اپنے گھاٹ پہچان لیا اور ہم نے ان پر ابر کا سایہ کیا اور ہم نے من و سلویٰ اتارا ہم نے جو ستھری چیزیں تمہیں دی ہیں وہ کھاؤ اور انہوں نے ہمارا کوئی نقصان نہیں کیا لیکن اپنا ہی نقصان کرتے تھے'),
(1115, 7, 161, 'اور جب انہیں حکم دیا گیا کہ تم اس بستی میں جا کر رہو اور وہاں جہاں سے تم چاہو کھاؤ اور زباں سے یہ کہتے جاؤ توبہ ہے اور دروازہ میں جھک کر داخل ہو ہم تمہاری غلطیاں معاف کر دیں گے اور نیکو کاروں کو اور زیادہ اجر دیں گے'),
(1116, 7, 162, 'سو ان میں سے ظالموں نے دوسرا لفظ اس کے سوا بدل دیا جو ان سے کہا گیا تھا پھر ہم نے ان پر آسمان سے عذاب بھیجا اس لیے کہ وہ ظلم کرتے تھے'),
(1117, 7, 163, 'اور ان سے اس بستی کا حال پوچھ جو دریا کے کنارے پر تھی جب ہفتہ کے معاملہ میں حد سے بڑھنے لگے جب ان کے پاس مچھلیاں ہفتہ کے دن پانی کے اوپر آنے لگیں اور جس دن ہفتہ نہ ہو تو نہ آتی تھیں ہم نے انہیں اس طرح آزمایا اس لیے کہ وہ نافرمان تھے'),
(1118, 7, 164, 'اور جب ان میں سے ایک جماعت نے کہا ان لوگوں کو کیوں نصیحت کرتے ہو جنہیں الله ہلاک کرنے والا ہے انہیں سخت عذاب دینے والا ہے انہوں نے کہا تمہارے رب کے روبر عذر کرنے کے لیے اور شاید کہ یہ ڈر جائیں'),
(1119, 7, 165, 'پھر جب وہ بھول گئے اس چیز کو جو انہیں سمجھائی گئی تھی توہم نے انہیں نجات دی جو برے کام سے منع کرتے تھے اور ظالموں کو ان کی نافرمانی کے باعث برے عذاب میں پکڑا'),
(1120, 7, 166, 'پھر جب وہ اس کام میں حد سے آگے بڑھ گئے جس سے روکے گئے تھے تو ہم نے حکم دیا کہ ذلیل ہونے والے بندر ہو جاؤ'),
(1121, 7, 167, 'اور یاد کر جب تیرے رب نے خبر دی تھی کہ یہودپر قیامت کے دن تک ایسے شخص کو ضرور بھیجتا رہے گا جو انہیں براعذاب دیتا رہے بے شک تیرا رب جلدی عذاب دینے والا ہے اور تحقیق وہ بحشنے والا مہربان ہے'),
(1122, 7, 168, 'اورہم نے انہیں ملک میں مختلف جماعتوں میں متفرق کر دیا بعضے ان میں سے نیک ہیں اور بعضے اور طرح کے اور ہم نے انہیں بھلائیوں اور برائیوں سے آزمایاتاکہ وہ لوٹ آئيں'),
(1123, 7, 169, 'پھر ان کے بعد ان کے ایسے جانشین ہوئے جو کتاب کے وارث بنے اس ادنیٰ زندگی کا مال و متاع لےلیتے ہیں اور کہتے ہیں کہ ہمیں معاف کر دیا جائے گا اور اگر ایسا ہی مال ان کے سامنے پھر آئے تو اسے لے لیتے ہیں کیا ان سے کتاب میں عہد نہیں لے لیا گیا تھا کہ الله کے سوا سچ کے اور کچھ نہ کہیں اور انہوں نے جو کچھ اس میں لکھا ہے پڑھا ہے اور آخرت کا گھر ڈرنے والوں کے لیے بہتر ہے کیا تم سمجھتے نہیں'),
(1124, 7, 170, 'اور جو لوگ کتاب کے پابند ہیں اور نماز کی پابندی کرتے ہیں بے شک ہم نیکی کرنے والوں کا ثواب ضائع نہیں کریں گے'),
(1125, 7, 171, 'اورجب ہم نے ان پر پہاڑ اٹھایا گویا کہ وہ سائبان ہے اور وہ ڈرے کہ ان پر گرے گا ہم نے کہا جو ہم نے تمہیں دیا ہے اسے مضبوطی سے پکڑو اور جو اس میں ہے اسے یاد رکھو تاکہ تم پرہیزگار ہو جاؤ'),
(1126, 7, 172, 'اور جب تیرے رب نے بنی آدم کی پیٹھوں سے ان کی اولاد کو نکالا اور ان سے ان کی جانوں پر اقرار کرایا کہ میں تمہارا رب نہیں ہوں انہوں نے کہا ہاں ہے ہم اقرار کرتے ہیں کبھی قیامت کے دن کہنے لگو کہ ہمیں تو اس کی خبر نہیں تھی'),
(1127, 7, 173, 'یا کہنے لگو ہمارے باپ دادا نے ہم سے پہلے شرک کیاتھا اور ہم ان کے بعد ان کی اولاد تھے کیاتو ہمیں اس کام پر ہلاک کرتا ہے جو گمراہوں نے کیا'),
(1128, 7, 174, 'اور اسی طرح ہم کھول کر آیتیں بیان کرتے ہیں تاکہ وہ لوٹ آئيں'),
(1129, 7, 175, 'اور انہیں اس شخص کا حال سنا دے جسے ہم نے اپنی آیتیں دی تھیں پھر وہ ان سے نکل گیا پھر اس کے پیچھے شیطان لگا تو وہ گمراہوں میں سے ہو گیا'),
(1130, 7, 176, 'اور اگر ہم چاتے تو ان کی آیتو ں کی برکت سے اس کا رتبہ بلند کرتے لیکن وہ دنیا کی طرف مائل ہو گیا اور اپنی خواہش کے تابع ہو گیا اس کا تو ایسا حال ہے جیسے کتا اس پر تو سختی کرے تو بھی ہانپے اور اگر چھوڑ دے تو بھی ہانپے یہ ان لوگوں کی مثال ہے جنہوں نے ہماری آیتوں کو جھٹلایا سو یہ حالات بیان کر دے شاید کہ وہ فکر کریں'),
(1131, 7, 177, 'جنہوں نے ہماری آیتوں کو جھٹلایا ان کی بری مثال ہے اور وہ اپنا ہی نقصان کرتے رہے'),
(1132, 7, 178, 'جسے الله تعالیٰ ہدایت دے وہی راہ پاتا ہے اور جسے گمراہ کر دےپس وہی لوگ نقصان اٹھانے والے ہیں'),
(1133, 7, 179, 'اور ہم نے دوزخ کے لیے بہت سے جن اور آدمی پیدا کیے ہیں ان کے دل ہیں کہ ان سے سمجھتے نہیں اور آنکھیں ہیں کہ ان سے دیکھتے نہیں اور کان ہیں کہ ان سے سنتے نہیں وہ ایسے ہیں جیسے چوپائے بلکہ ان سے بھی گمراہی میں زیادہ ہیں یہی لوگ غافل ہیں'),
(1134, 7, 180, 'اور سب اچھے نام الله ہی کے لیے ہیں سو اسے انہیں نامو ں سے پکارو اور چھوڑ دو ان کو جو الله کے نامو ں میں کجروی اختیار کرتے ہیں وہ اپنے کیے کی سزا پا کر رہیں گے'),
(1135, 7, 181, 'اور ان لوگوں میں سےجنہیں ہم نے پیدا کیا ایک جماعت ہے جو سچی راہ بتاتی ہے اور اسی کے موافق انصاف کرتے ہیں'),
(1136, 7, 182, 'اور جنہوں نے ہماری آیتوں کو جھٹلایا ہم انہیں آہستہ آہستہ پکڑیں گے ایسی جگہ سے جہاں انہیں خبر بھی نہ ہو گی'),
(1137, 7, 183, 'اور میں انہیں مہلت دوں گا بے شک میری تدبیر بڑی مضبوط ہے'),
(1138, 7, 184, 'کیا انہوں نے غور نہیں کیاکہ ان کے ساتھی کو جنوں تو نہیں ہے وہ تو کھلم کھلا ڈرانے والا ہے'),
(1139, 7, 185, 'اور کیا انہوں نے آسمان اور زمین کی سلطنت کو نہیں دیکھا اور دوسری چیزوں کو جو الله نے پیدا کی ہیں اور یہ کہ ممکن ہے کہ ان کی اجل قریب ہی ہو پھرقرآن کے بعد کس بات پر یہ لوگ ایمان لائيں گے'),
(1140, 7, 186, 'جسے الله گمراہ کر دے اسے کوئی راہ دکھانے والا نہیں اور انہیں الله چھوڑ دیتا ہے کہ اپنی سرکشی میں حیران پھیر یں'),
(1141, 7, 187, 'قیامت کے متعلق تجھ سے پوچھتے ہیں کہ اس کی آمد کا کونسا وقت ہے کہہ دو اس کی خبر تو میرے رب ہی کے ہاں ہے وہی اس کے وقت پر ظاہر کر دکھائے گا وہ آسمانو ں اور زمین میں بھاری بات ہے وہ تم پر محض اچانک آجائے گی تجھ سے پوچھتے ہیں گویا کہ تو اس کی تلاش میں لگا ہوا ہے کہہ دو اس کی خبر خاص الله ہی کے ہاں ہے لیکن اکثر لوگ نہیں سمجھتے'),
(1142, 7, 188, 'کہہ دو میں اپنی ذات کے نفع و نقصان کا بھی مالک نہیں مگرجو الله چاہے اور اگر میں غیب کی بات جان سکتا تو بہت کچھ بھلائیاں حاصل کر لیتا اور مجھے تکلیف نہ پہنچتی میں تو محض ڈرانے والا اور خوشخبری دینے والا ہوں ان لوگوں کو جو ایمان دار ہیں'),
(1143, 7, 189, 'وہ وہی ہے جس نے تمہیں ایک جان سے پیدا کیا اور اسی سے اس کا جوڑ بنایا تاکہ اس سے آرام پائے پھر جب میاں نے بیوی سے ہم بستری کی تو اس کو ہلکا سا حمل رہ گیا پھر اسے لیے پھرتی رہی پھر جب وہ بوجھل ہو گئی تب دونوں میاں بیوی نے الله سے جو ان کا مالک ہے دعا کی اگر آپ نے ہمیں صحیح سالم اولاد دے دی تو ہم ضرور شکر گزار ہوں گے'),
(1144, 7, 190, 'پھر جب الله نے انکو صحیح سالم اولاد دی تو الله کی دی ہوئی چیزوں میں وہ دونوں الله کا شریک بنانے لگے سو الله ان کے شرک سے پاک ہے'),
(1145, 7, 191, 'کیا ایسوں کو شریک بناتے ہیں جو کچھ بھھی نہیں بنا سکتے اور وہ خود بنائے ہوئے ہیں'),
(1146, 7, 192, 'اور نہ وہ ان کی مدد کر سکتے ہیں اور نہ اپنی ہی مدد کر سکتے ہیں'),
(1147, 7, 193, 'اور اگر تم انہیں راستہ کی طرف بلاؤ تو تمہاری تابعداری نہ کریں برابر ہے کہ تم انہیں پکارو یا چپکے رہو'),
(1148, 7, 194, 'بے شک جنہیں تم الله کے سوا پکارتے ہو وہ تمہاری طرح کے بندے ہیں پھر انہیں پکار کر دیکھو پھر چاہے کہ وہ تمہاری پکار کو قبول کریں اگر تم سچے ہو'),
(1149, 7, 195, 'کیا ان کے پاؤں ہیں جن سے وہ چلیں یا ان کے ہاتھ ہیں جن سے وہ پکڑیں یا ان کی آنکھیں ہیں جن سے وہ دیکھیں یا ان کے کان ہیں جن سے وہ سنیں کہ تو اپنے شریکوں کو پکارو پھر میری برائی کی تدبیر کرو پھر مجھے ذرا مہلت نہ دو'),
(1150, 7, 196, 'بےشک میرا حمایتی الله ہے جس نے کتاب نازل فرمائی اور وہ نیکو کاروں کی حمایت کرتا ہے'),
(1151, 7, 197, 'اور جنہیں تم پکارتے ہو وہ تمہاری مدد نہیں کر سکتے اور نہ اپنی جان کی مدد کر سکتے ہیں'),
(1152, 7, 198, 'اور اگر تم انہیں راستہ کی طرف پکارو تو وہ کچھ نہیں سنیں گے اور تو دیکھے گا کہ وہ تیری طرف دیکھتے ہیں حالانکہ وہ کچھ نہیں دیکھتے'),
(1153, 7, 199, 'درگزر کر اور نیکی کا حکم دے اور جاہلوں سے الگ رہ'),
(1154, 7, 200, 'اور اگر تجھے کوئی وسوسہ شیطان کی طرف سے آئے تو الله کی پناہ مانگ لیا کر بے شک وہ سننے والا جاننے والا ہے'),
(1155, 7, 201, 'بے شک جو لوگ خدا سے ڈرتے ہیں جب انہیں کوئي خطرہ شیطان کی طرف سے آتا ہے تو وہ یاد میں لگ جاتے ہیں پھر اچانک ان کیآنکھیں کھل جاتی ہیں'),
(1156, 7, 202, 'اور جو شیاطین کے تابع ہیں وہ انہیں گمراہی میں کھینچے چلے جاتے ہیں پھر وہ باز نہیں آتے'),
(1157, 7, 203, 'اور جب توان کے پاس کوئی معجزہ نہیں لاتا تو کہتے ہیں کہ تو فلاں معجزہ کیوں نہیں لایا کہہ دو میں اس کا اتباع کرتا ہوں جو مجھ پر میرے رب کی طرف سے بھیجا جاتا ہے یہ تمہارے رب کی طرف سے بہت سی دلیلیں ہیں اور ہدایت اور رحمت ہے ان لوگو ں کے لیے جو ایمان دار ہیں'),
(1158, 7, 204, 'اورجب قرآن پڑھا جاتا جائے تو اسے کان لگا کر سنو اور چپ رہو تاکہ تم پر رحم کیا جائے'),
(1159, 7, 205, 'اوراپنے رب کو اپنے دل میں عاجزی کرتا ہوا اور ڈرتاہوا یاد کرتا رہ اور صبح اور شام بلند آواز کی نسبت ہلکی آواز سے اور غافلوں سے نہ ہو'),
(1160, 7, 206, 'بے شک جو تیرے رب کے ہاں ہیں وہ اس کی بندگی سے تکبر نہیں کرتے اور اس کی پاک ذات کو یاد کرتے ہیں اور اسی کو سجدہ کرتے ہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'تجھ سے غنیمت کا حکم پوچھتے ہیں کہہ دے غنیمت کا مال الله اور رسول کا ہے سو الله سے ڈرو اور آپس میں صلح کرو اور الله اور اس کے رسول کا حکم مانو اگر ایمان دار ہو'),
(1162, 8, 2, 'ایمان والے وہی ہیں جب الله کا نام آئے تو ان کے دل ڈر جائیں اورجب اس کی آیتیں ان پر پڑھی جائیں تو ان کا ایمان زیادہ ہو جاتا ہے اور اور وہ اپنے رب پر بھروسہ رکھتے ہیں'),
(1163, 8, 3, 'وہ جو نماز قائم کرتے ہیں اور جو ہم نے انہیں رزق دیا ہے اس میں سے خرچ کرتے ہیں'),
(1164, 8, 4, 'یہی سچے ایمان والے ہیں ان کے رب کے ہاں ان کے لیے درجے ہیں اور بخشش ہے اور عزت کا رزق ہے'),
(1165, 8, 5, 'جیسے تیرے رب نے تیرے گھر سے سچائی کے ساتھ نکالا اور بے شک ایک جماعت مسلمانوں میں سے نا پسند کرنے والی تھی'),
(1166, 8, 6, 'وہ تجھ سے حق بات میں اس کے ظاہر ہو چکنے کے بعد جھگڑتے تھے گویا وہ آنکھوں سے دیکھتے ہوئے موت کی طرف ہانکے جاتے ہیں'),
(1167, 8, 7, 'اور جس وقت دو جماعتوں میں سے ایک کا الله تم سے وعدہ کرتا تھا کہ وہ تمہارے ہاتھ لگے گی اور تم چاہتے تھے جس میں کانٹا نہ ہو وہ تمہیں ملے اور الله چاہتا تھا کہ اپنے حکم سے حق کو ثابت کردے اور کافروں کی جڑ کاٹ دے'),
(1168, 8, 8, 'تاکہ حق کو ثابت کر دے اور باطل کو مٹا دے اگرچہ گناہگار ناراض ہوں'),
(1169, 8, 9, 'جب تم اپنے رب سے فریاد کر رہے تھے اس نے جواب میں فرمایا کہ میں تمہاری مدد کے لیے پے درپے ایک ہزار فرشتے بھیج رہا ہوں'),
(1170, 8, 10, 'اور یہ تو الله نے فقط خوش خبری دی تھی اور تاکہ تمہارے دل اس سے مطمئن ہو جائیں اور مدد تو صرف الله ہی کی طرف سے ہے بے شک الله غالب حکمت والا ہے'),
(1171, 8, 11, 'جس وقت اس نے تم پر اپنی طرف سے تسکین کے لیے اونگھ ڈال دی اور تم پر آسمان سے پانی اتارا تاکہ اس سے تمہیں پاک کر دے اور شیطان کی نجاست تم سے دور کر دے اور تمہارے دلوں کو مضبوط کر دے اور اس سے تمہارے قدم جما دے'),
(1172, 8, 12, 'جب تیرے رب نے فرشتوں کو حکم بھیجا کہ میں تمہارے ساتھ ہوں تم مسلمانوں کے دل ثابت رکھو میں کافروں کے دلوں میں دہشت ڈال دوں گا سو گردنوں پر مارواور ان کے پور پور پر مارو'),
(1173, 8, 13, 'یہ اس لیے ہے کہ وہ الله اوراس کے رسول کے مخالف ہیں اور جو کوئی اللہ اور اس کےرسول کا مخالف ہو تو بے شک الله سخت عذب دینے والا ہے'),
(1174, 8, 14, 'یہ تو چکھ لو اور جان لو کہ بے شک کافروں کے لیے دوزخ کا عذاب ہے'),
(1175, 8, 15, 'اے ایمان والو جب تم کافروں سے میدان جنگ میں ملو تو ان سے پیٹھیں نہ پھیرو'),
(1176, 8, 16, 'اور جو کوئی اس دن ان سے پیٹھ پھیرے گا مگر یہ کہ لڑائی کا ہنر کرتا ہو یا فوج میں جا ملتا ہو سو وہ الله کا غضب لے کر پھرا اور اس کا ٹھکانا دوزخ ہے اور بہت برا ٹھکانا ہے'),
(1177, 8, 17, 'سو تم نے انہیں قتل نہیں کیا بلکہ الله نے انہیں قتل کیا اور تو نے مٹھی نہیں پھینکی جب کہ پھینکی تھی بلکہ الله نے پھینکی تھی اور تاکہ ایمان والوں پر اپنی طرف سے خوب احسان کرے بے شک الله سننے والا جاننے والا ہے'),
(1178, 8, 18, 'یہ تو ہو چکا اور بےشک الله کافروں کی تدبیر کو کمزور کرنے والا ہے'),
(1179, 8, 19, 'اگر تم فیصلہ چاہتے ہو تو تمہارا فیصلہ آ چکا اور اگر باز آؤ تو تمہارے لیے بہتر ہے اور اگر پھر یہی کرو گے تو ہم بھی پھر یہی کریں گے اور تمہاری جمعیت ذرا بھی کام نہیں آئے گی اگرچہ وہ بہت ہوں اوربے شک اللہ ایمان والوں کے ساتھ ہے'),
(1180, 8, 20, 'اے ایمان والو الله اور اس کے رسول کا حکم مانو اور سن کر اس سے مت پھرو'),
(1181, 8, 21, 'اور ان لوگو ں کی طرح نہ ہو جنہوں نے کہا ہم نے سن لیا اور وہ سنتے نہیں'),
(1182, 8, 22, 'بے شک سب جانوں میں سے بدتر الله کے نزدیک وہی بہرے گونگے ہیں جو نہیں سمجھتے'),
(1183, 8, 23, 'اور اگر الله ان میں کچھ بھلائی جانتا تو انہیں سنا دیتا اور اگر انہیں اب سنا دے تو منہ پھیر کر بھاگیں'),
(1184, 8, 24, 'اے ایمان والو الله اور رسول کا حکم مانو جس وقت تمہیں اس کام کی طرف بلائے جس میں تمہاری زندگی ہے اور جان لو کہ الله آدمی اور اس کے دل کے درمیان آڑ بن جاتا ہے اور بے شک اسی کی طرف جمع کیے جاؤ گے'),
(1185, 8, 25, 'اور تم اس فتنہ سے بچتے رہو جو تم میں سے خاص ظالموں پر ہی نہ پڑے گا اور جان لو کہ بے شک الله سخت عذاب کرنے والا ہے'),
(1186, 8, 26, 'اور یاد کرو جس وقت تم تھوڑے تھے ملک میں کمزور سمجھے جاتے تھے تم ڈرتے تھے کہ لوگ تمہیں اچک لیں پھر اس نے تمہیں ٹھکانا بنا دیا اور اپنی مدد سے تمہیں قوت دی اور تمہیں ستھری چیزوں سے رزق دیاتاکہ تم شکر کرو'),
(1187, 8, 27, 'اے ایمان والو الله اور اس کے رسول سے خیانت نہ کرو اور آپس کی امانتوں میں بھی خیانت نہ کرو حالانکہ تم جانتے ہو'),
(1188, 8, 28, 'اور جان لو کہ تمہارے مال اور تمہاری اولاد ایک امتحان کی چیز ہے اور بے شک الله کہ ہاں بڑا اجر ہے'),
(1189, 8, 29, 'اے ایمان والو اگر تم الله سے ڑرتے رہو گے تو الله تمہیں ایک فیصلہ کی چیز دے گا اور تم سے تمہارے گناہ دور کر دے گا اور تمہیں بخش دے گا اور الله بڑے فضل والا ہے'),
(1190, 8, 30, 'اور جب کافر تیرے متعلق تدبیریں سوچ رہے تھے کہ تمہیں قید کر دیں یا تمہیں قتل کر دیں یا تمہیں دیس بدر کر دیں وہ اپنی تدبیریں کر رہے تھے اور الله ہی اپنی تدبیر کر ہا تھا اور الله بہترین تدبیر کرنے والا ہے'),
(1191, 8, 31, 'اور جب ان کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو کہتے ہیں کہ ہم نے سن لیا اور اگر ہم چاہیں تو اس کے برابر ہم بھی کہہ دیں اس میں پہلو ں کے قصے کے سوا اور کچھ نہیں'),
(1192, 8, 32, 'اورجب انہوں نے کہا کہ اے الله اگر یہ دین تیری طرف سے حق ہے تو ہم پر آسمان سے پتھر برسا یا ہم پر دردناک عذاب لا'),
(1193, 8, 33, 'اور الله ایسا نہ کرے گا کہ انہیں تیرتے ہوئے عذاب دے اور الله عذاب کرنے الا نہیں درآنحالیکہ وہ بخشش مانگتے ہوں'),
(1194, 8, 34, 'اور الله انہیں عذاب کیوں نہ دے حالانکہ وہ مسجد حرام سے روکتے ہیں اور وہ اس کے اہل نہیں ہیں اس میں تو اہل پرہیزگاری ہیں لیکن ان میں سے اکثر نہیں سمجھتے'),
(1195, 8, 35, 'اورکعبہ کے پاس ان کی نماز سوائے سیٹیاں اور تالیاں بجانے کے اور کچھ نہیں تھی سو عذاب سبب اس کے کہ تم کفر کرتے تھے'),
(1196, 8, 36, 'بے شک جو لوگ کافر ہیں وہ اپنے مال خرچ کرتے ہیں تاکہ الله کی راہ سے روکیں سو ابھی اور بھی خرچ کریں گے پھر وہ ان کے لیے حسرت ہو گا پھر مغلوب کیے جائیں گے اور جو کافر ہیں وہ دوزخ کی طرف جمع کیے جائیں گے'),
(1197, 8, 37, 'تاکہ الله ناپاک کو پاک سے جدا کر دے اور ایک ناپاک کو دوسرے پر دھر کر ڈھیر بنائے پھر اسے دوزخ میں ڈال دے وہی لوگ نقصان اٹھانے والے ہیں'),
(1198, 8, 38, 'کافروں سے کہہ دو اگر وہ باز آجائيں تو جو کچھ گزر چکا وہ انہیں معاف کر دیا جائے گا اور اگر لوٹیں گے تو پہلے کافروں کے حق میں قانون نافذ ہو چکا ہے'),
(1199, 8, 39, 'اور تم ان سے اس حد تک لڑو کہ شرک کا غلبہ نہ رہنے پائے اور سارا دین الله ہی کا ہو جکائے پھر اگر یہ باز آجائیں تو الله ان کے اعمال دیکھنے والا ہے'),
(1200, 8, 40, 'اور اگر وہ پھر جائیں تو جان لو کہ الله تمہارا دوست ہے اور بہت اچھا دوست ہے اوربہت اچھا مددگار ہے'),
(1201, 8, 41, 'اور جان لو کہ جو کچھ تمہیں بطور غنیمت ملے خواہ کوئی چیز ہو تو اس میں سے پانچواں حصہ الله اور اس کے رسول کا ہے اور رشتہ داروں اور یتیموں اور مسکینوں اور مسافروں کے لیے ہے اگر تمہیں الله پر یقین ہے اور اس چیز پر جو ہم نے اپنے بندے پر فیصلہ کے دن اتاری جس دن دونوں جماعتیں ملیں اور الله ہر چیز پر قادر ہے'),
(1202, 8, 42, 'جس وقت تم درلے کنارے پر تھے اور وہ پرلےکنارے پر اور قافلہ تم سے نیچے اتر گیا تھا اور اگرتم آپس میں وعدہ کرتے تو ایک ساتھ وعدہ پر نہ پہنچتے لیکن الله کو ایک کام کرنا تھا جو مقرر ہو چکا تھا تاکہ جو ہلاک وہ اتمام حجت کے بعد ہلاک ہو اور جو زندہ رہے وہ اتمام حجت کے بعد زندہ رہے اور بے شک الله سننے والا جاننے والا ہے'),
(1203, 8, 43, 'جب کہ الله نے وہ کافر تجھے تیرے خواب میں تھوڑے کر کے دکھلائے اور اگرتجھے بہت دکھلا دیتا تو تم لوگ نامردی کر تے اور کام میں جھگڑا ڈالتے لیکن الله نے بچا لیا جو بات دلوں میں ہے وہ اسے خوب معلوم ہے'),
(1204, 8, 44, 'اور جب تمہیں وہ فوج مقابلہ کے وقت تمہاری آنکھوں میں تھوڑی کر کے دکھائی اور تمہیں ان کی آنکھوں میں تھوڑا کر کے دکھایا تاکہ الله ایک کام پورا کر دے جو مقرر ہو چکا تھا اور ہر کام الله تک ہی پہنچتا ہے'),
(1205, 8, 45, 'اے ایمان والو! جب کسی فوج سے ملو تو ثابت قدم رہو اور الله کو بہت یاد کرو تاکہ تم نجات پاؤ'),
(1206, 8, 46, 'اور الله اوراس کے رسول کا کہا مانو اور آپس میں نہ جھگڑو ورنہ بزدل ہو جاؤ گے اور تمہاری ہوا اکھڑ جائے گی اور صبر کرو بے شک الله صبر کرنے والوں کے ساتھ ہے'),
(1207, 8, 47, 'اور ان لوگوں جیسا نہ ہونا جو اتراتے ہوئے اور لوگوں کو دکھانے کے لیے گھروں سے نکل آئے اور الله کی راہ سے روکتے تھے اور جو کچھ یہ کرتے ہیں الله اس پر احاطہ کرنے والا ہے'),
(1208, 8, 48, 'اورجس وقت شیطان نے ان کے اعمال کو ان کی نظروں میں خوشنما کر دیا اورکہا کہ آج کے دن لوگوں میں سے کوئی بھی تم پر غالب نہ ہوگا اور میں تمہارا حمایتی ہوں پھر جب دونوں فوجیں سامنے ہوئیں تووہ اپنے ایڑیوں پر الٹا پھرا اور کہا میں تمہارے ساتھ نہیں ہوں میں ایسی چیر دیکھتا ہوں جو تم نہیں دیکھتے میں الله سے ڈرتا ہوں اور الله سخت عذاب کرنے والا ہے'),
(1209, 8, 49, 'اس وقت منافق اور جن کے دلو ں میں مرض تھا کہتے تھے کہ انہیں ان کے دین نے مغلوب کر رکھا ہے اور جو کوئی الله پر بھروسہ کرے تو الله زبردست حکمت والا ہے'),
(1210, 8, 50, 'اور اگر تو دیکھے جس وقت فرشتے کافروں کی جان قبض کرتے ہیں ان کے مونہوں او رپیٹہوں پر مارتے ہیں اور کہتے ہیں جلنے کا عذاب چکھو'),
(1211, 8, 51, 'یہ اسی کا بدلہ ہے جو تمہارے ہاتھوں نے آگے بھیجا اور بے شک الله بندوں پر ظلم نہیں کرتا'),
(1212, 8, 52, 'جیسا فرعونیوں اوران سے پہلے لوگوں کا حال ہوا تھا انہوں نے الله کی آیتوں سے انکار کیا تو الله نے ان کے گناہوں کی سزا میں انہیں پکڑ لیا بے شک الله زبردست اور سخت عذاب کرنے والا ہے'),
(1213, 8, 53, 'اس کا سبب یہ ہے کہ الله ہر گز اس نعمت کو نہیں بدلتا جو اس نے کسی قوم کو دی تھی جب تک وہ خود اپنے دلوں کی حالت نہ بدلیں اوراس لیے کہ الله سننے والا جاننے والا ہے'),
(1214, 8, 54, 'جیسے فرعونیوں اور ان سے پہلے لوگوں کاحال ہوا تھا انہوں نے اپنے رب کی آیتوں کو جھٹلایا تو ہم نے انہیں ان کے گناہوں کے سبب ہلاک کر ڈالا اور فرعونیوں کو ڈبو دیا اور سب ظالم تھے'),
(1215, 8, 55, 'اور الله کے ہاں سب جانداروں میں سے بدتر وہ ہیں جنہوں نے کفر کیا پھر وہ ایمان نہیں لاتے'),
(1216, 8, 56, 'جن لوگوں سے تو نے عہد لیا پھر وہ ہر دفعہ اپنے عہد کو توڑتے ہیں اور وہ نہیں ڈرتے'),
(1217, 8, 57, 'سو اگر کبھی تو انہیں لڑائی میں پائے تو انہیں ایسی سزا دے کہ ان کے پچھلے دیکھ کر بھاگ جائیں تاکہ انہیں عبرت ہو'),
(1218, 8, 58, 'اور اگر تمہیں کسی قوم سے دغابازی کا ڈر ہو تو ان کا عہد ان کی طرف پھینک دو ایسی طرح پر کہ تم اور وہ برابر ہو جاؤ بے شک الله دغا بازوں کو پسند نہیں کرتا'),
(1219, 8, 59, 'اور کافر یہ نہ خیال کریں کہ وہ بھاگ نکلے ہیں بے شک وہ ہمیں ہر گز عاجز نہ کر سکیں گے'),
(1220, 8, 60, 'اور ان سے لڑنے کے لیے جو کچھ (سپاہیانہ) قوت سے پلے ہوئے گھوڑوں سے جمع کر سکو سو تیار رکھو کہ اس سے الله کے دشمنوں پر اور تمہارے دشمنوں پر اور ان کےسوا دوسروں پر جنہیں تم نہیں جانتے الله انہیں جانتا ہے ہیبت پڑے اور الله کی راہ میں جو کچھ تم خرچ کرو گےتمہیں (اس کا ثواب) پورا ملے گا اور تم سے بے انصافی نہیں ہو گی'),
(1221, 8, 61, 'اور اگر وہ صلح کے لیے مائل ہوں تو تم بھی مائل ہو جاؤ اور الله پر بھروسہ کرو بے شک وہی سننے والا جاننے والا ہے'),
(1222, 8, 62, 'اور اگر وہ چاہیں کہ تمہیں دھوکہ دیں تو تجھے الله کافی ہے جس نے تمہیں اپنی مدد سے اور مسلمانوں سے قوت بحشی'),
(1223, 8, 63, 'اور ان کے دلو ں میں الفت ڈال دی جو کچھ زمین میں ہے اگر سارا تو خرچ کر دیتا ان کے دلوں میں الفت نہ ڈال سکتا لیکن الله نے ان میں الفت ڈال دی بے شک الله غالب حکمت والا ہے'),
(1224, 8, 64, 'اے نبی! تجھے اور مومنوں کو جو تیرے تابعدار ہیں الله کافی ہے'),
(1225, 8, 65, 'اے نبی! مسلمانوں کو جہاد کی ترغیب دو اگر تم میں بیس آدمی ثابت قدم رہنے والے ہوں گے تو وہ سو پر غالب آئیں گے اور اگر تم میں سو ہوں گے تو ہزار کافروں پر غالب آئیں گے اس لیے کہ وہ لوگ کچھ نہیں سمجھتے'),
(1226, 8, 66, 'اب الله نےتم سے بوجھ ہلکا کر دیا اور معلوم کر لیا کہ تم میں کس قدر کمزوری ہے پس اگر تم سو ثابت قدم رہنے والے ہوں گے تو دو سو پر غالب آئيں گے اور اگر ہرار ہوں گے تو الله کے حکم سے دو ہزار پر غالب آئيں گے اور الله صبر کرنے والوں کے ساتھ ہے'),
(1227, 8, 67, 'نبی کو نہیں چاہیئے کہ اپنے ہاں قیدیوں کو رکھے یہاں تک کہ ملک میں خوب خونریزی کر لے تم دنیا کی زندگی کا سامان چاہتے ہو اور الله آخرت کا ارادہ کرتا ہے اور الله غالب حکمت والا ہے'),
(1228, 8, 68, 'اگر الله کا حکم پہلے نہ ہو چکا ہوتا تو جو تم نے لیا اس کے بدلے تم پربڑا عذاب ہوتا'),
(1229, 8, 69, 'پس جو مال تمہیں غنیمت میں حلال اور طیب ملا ہے اسے کھاؤ اور الله سے ڈرو بے شک الله بخشنے والا مہربان ہے'),
(1230, 8, 70, 'اے نبی! جو قیدی تمہارے ہاتھ میں ہیں ان سے کہہ دو کہ اگر الله تمہارے دلوں میں نیکی معلوم کر ے گا تو تمہیں اس سے بہتر دے گا جو تم سے لیا گیا ہے اور تمہیں بخشے گا اور الله بحشنے والا مہربان ہے'),
(1231, 8, 71, 'اور اگر یہ لوگ تم سے دغا کرنا چاہیں گےیہ تو پہلے ہی الله سے دغا کر چکے ہیں پھر الله نے انہیں گرفتار کرا دیا اور الله جاننے والا حکمت والا ہے'),
(1232, 8, 72, 'بے شک جو لوگ ایمان لائے اور گھر چھوڑا اور اپنے مالوں اور جانوں سے الله کی راہ میں لڑے اور جن لوگوں نے جگہ دی اور مدد کی وہ آپس میں ایک دوسرے کے رفیق ہیں اورجو ایمان لائے اور گھر نہیں چھوڑا تمہیں ان کی وراثت سے کوئی تعلق نہیں یہاں تک کہ وہ ہجرت کریں اور اگر وہ دین کے معاملہ میں مدد چاہیں تو تمہیں ان کی مدد کرنی لازم ہے مگران لوگو ں کے مقابلہ میں کہ ان میں اور تم میں عہد ہو اور جو تم کرتے ہو الله اسے دیکھتا ہے'),
(1233, 8, 73, 'اور جو لوگ کافر ہیں وہ ایک دوسرے کے رفیق ہیں اگر تم یوں نہ کرو گے تو ملک میں فتنہ پھیلے گا اوروہ بڑا فساد ہوگا'),
(1234, 8, 74, 'اور جو لوگ ایمان لائے اوراپنے گھر چھوڑے اور الله کی راہ میں لڑے اورجن لوگوں نے انہیں جگہ دی اور ان کی مدد کی وہی سچے مسلمان ہیں ان کے لیے بخشش اور عزت کی روزی ہے'),
(1235, 8, 75, 'اورجو لوگ اس کے بعد ایمان لائے اور گھر چھوڑے اور تمہارے ساتھ ہو کر لڑے سو وہ لوگ بھی تمہیں میں سے ہیں اور رشتہ دار آپس میں الله کے حکم کے مطابق ایک دوسرے کے زیادہ حق دار ہیں بے شک الله ہر چیز سے خبردار ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'الله اور اس کے رسول کی طرف سے ان مشرکوں سے بیزاری ہے جن سے تم نے عہد کیا تھا'),
(1237, 9, 2, 'سو اس ملک میں چار مہینے پھر لو اور جان لو کہ تم الله کو عاجز نہیں کر سکوگے اور بے شک الله کافروں کو ذلیل کرنے والا ہے'),
(1238, 9, 3, 'اور اللہ اور اس کے رسول کی طرف سے بڑے حج کے دن لوگوں کو آگاہ کیا جاتا ہے کہ اللہ اور اس کا رسول مشرکوں سے بیزار ہیں پس اگر تم توبہ کرو تو تمہارے لئے بہتر ہے اور اگر نہ مانو تو جان لو کہ تم اللہ کو ہرگز عاجز کرنے والے نہیں اور کافروں کو درد ناک عذاب کی خوشخبری سنادو'),
(1239, 9, 4, 'مگر جن مشرکوں سے تم نے عہد کیا تھا پھر انہوں نے تمہارے ساتھ کوئی قصور نہیں کیا اور تمہارے مقابلے میں کسی کی مد نہیں کی سو ان سے ان کا عہد ان کی مدت تک پورا کر دو بے شک الله پرہیز گارو ں کو پسند کرتا ہے'),
(1240, 9, 5, 'پھر جب عزت والے مہینے گزر جائیں تو مشرکوں کو جہاں پاؤ قتل کر دو اور پکڑو اور انہیں گھیر لو اور ان کی تاک میں ہر جگہ بیٹھو پھر اگر وہ توبہ کریں اور نماز قائم کریں اور زکواة دیں تو ان کا راستہ چھوڑ دو بے شک الله بخشنے والا مہربان ہے'),
(1241, 9, 6, 'اور اگر کوئی مشرک تم سے پناہ مانگے تو اسے پناہ دے دو یہاں تک کہ الله کا کلام سنے پھر اسے اس کی امن کی جگہ پہنچا دو یہ اس لیے ہے کہ وہ لوگ بے سمجھ ہیں'),
(1242, 9, 7, 'بھلا مشرکوں کے لیے الله اور اس کے رسول کے ہاں عہدکیوں کر ہو سکتا ہے ہاں جن لوگوں کے ساتھ تم نے مسجد حرام کے نزدیک عہد کیا ہے اگر وہ قائم رہیں توتم بھی قائم رہو بے شک الله پرہیزگاروں کو پسند کرتا ہے'),
(1243, 9, 8, 'کیوں کر صلح ہو اور اگر وہ تم پر غلبہ پائیں تو نہ تمہاری قرابت کا لحاظ کریں اور نہ عہد کا تمہیں اپنی منہ کی باتوں سے راضی کرتے ہیں اور ان کے دل نہیں مانتے اور ان میں سے اکثر بد عہد ہیں'),
(1244, 9, 9, 'انہوں نے الله کی آیتوں کو تھوڑی قیمت پر بیچ ڈالا پھر الله کے راستے سے روکتے ہیں بے شک وہ برا ہے جو کچھ وہ کرتے ہیں'),
(1245, 9, 10, 'یہ لوگ کسی مومن کے حق میں نہ رشتہ داری کا خیال کرتے ہیں اور نہ عہد اور یہی لوگ حد سے گزرنے والے ہیں'),
(1246, 9, 11, 'اگر یہ توبہ کریں اور نماز قائم کریں اور زکواة دیں تو دین میں تمہارے بھائی ہیں اور ہم سمجھ داروں کے لیے کھول کھول کر احکام بیان کرتے ہیں'),
(1247, 9, 12, 'اور اگر وہ عہد کرنے کے بعد اپنی قسمیں تو ڑ دیں اور تمہارے دین میں عیب نکالیں تو کفر کے سرداروں سے لڑو ان کی قسموں کوئی اعتبار نہیں تاکہ وہ باز آ آئیں'),
(1248, 9, 13, 'خبردار! تم ایسے لوگوں سے کیوں نہ لڑو جنہوں نے اپنی قسموں کو توڑ ڈالا اور پیغمبر کو جلا وطن کرنے کا ارادہ کیا اور انہوں نے پہلے تم سے عہد شکنی کی کیا تم ان سے ڈرتے ہو الله زيادہ حق دار ہے کہ تم اس سے ڈرو اگر تم ایمان دار ہو'),
(1249, 9, 14, 'ان سے لڑو تاکہ الله انہیں تمہارے ہاتھوں سے عذاب دے اور انہیں ذلیل کرے اور تمہیں ان پر غلبہ دے اور مسلمانوں کے دلوں کو ٹھنڈا کرے'),
(1250, 9, 15, 'اور ان کے دلوں سے غصہ دور کر ے اور الله جسے چاہے توبہ نصیب کرے اور الله جاننے والا حکمت والا ہے'),
(1251, 9, 16, 'کیا تم یہ خیال کرتے ہو کہ چھوڑ دیے جاؤ گے حالانکہ ابھی الله نے ایسے لوگو ں کو جدا ہی نہیں کیا جنہوں نے تم میں سے جہاد کیا اور الله اور اس کے رسول اورمومنوں کے سوا کسی کو دلی دوست نہیں بنایا اور الله تمہارے سب کاموں سے با خبر ہے'),
(1252, 9, 17, 'مشرکوں کا کام نہیں کہ الله کی مسجدیں آباد کریں جب کہ وہ اپنے آپ پر کفر کی گواہی دے رہے ہوں ان لوگوں کے سب اعمال بے کار ہیں اور وہ ہمیشہ آگ میں رہیں گے'),
(1253, 9, 18, 'الله کی مسجدیں وہی آباد کرتا ہے جو الله پر اور آخرت پر ایمان لایا اور نماز قائم کی اور زکواة دی اور الله کے سوا کسی سے نہ ڈرا سو وہ لوگ امیدوار ہیں کہ ہدایت والوں میں سے ہوں'),
(1254, 9, 19, 'کیا تم نے حاجیوں کا پانی پلانا اور مسجد حرام کا آباد کرنا اس کے برابر کر دیا جو الله پر اور آخرت کے دن پر ایمان لایا اور الله کی راہ میں لڑا الله کہ ہاں یہ برابر نہیں ہیں اور الله ظالم لوگوں کو راستہ نہیں دکھاتا'),
(1255, 9, 20, 'جو لوگ ایمان لائے اور گھر چھوڑے اور الله کی راہ میں اپنے مالوں اور جانوں سے لڑے الله کے ہاں ان کے لیے بڑا درجہ ہے اور وہی لوگ مراد پانے والے ہیں'),
(1256, 9, 21, 'انہیں ان کا رب اپنی طرف سے مہربانی اور رضا مندی اور باغوں کی خوشخبری دیتا ہے جن میں انہیں ہمیشہ آرام ہوگا'),
(1257, 9, 22, 'ان میں ہمیشہ رہیں گے بے شک الله کے ہاں بڑا ثواب ہے'),
(1258, 9, 23, 'اے ایمان والو! اپنے باپوں اور بھائیوں سے دوستی نہ رکھو اگر وہ ایمان پر کفر کو پسند کریں اور تم میں سے جو ان سے دوستی رکھے گا سو وہی لوگ ظالم ہیں'),
(1259, 9, 24, 'کہہ دے اگر تمہارے باپ اور بیٹے اور بھائی اوربیویاں اور برادری اور مال جو تم نے کمائے ہیں اور سوداگری جس کے بند ہونے سے تم ڈرتے ہو اور مکانات جنہیں تم پسند کر تے ہو تمہیں الله اور اس کے رسول اوراس کی راہ میں لڑنے سے زیادہ پیارے ہیں تو انتظار کرو یہاں تک کہ الله اپنا حکم بھیجے اور الله نافرمانوں کو راستہ نہیں دکھاتا'),
(1260, 9, 25, 'الله بہت سے میدانوں میں تمہاری مدد کر چکا ہے اور حنین کے دن جب تم اپنی کثرت پر خوش ہوئے پھر وہ تمہارے کچھ کام نہ آئی اور تم پر زمین باوجود اپنی فراخی کے تنگ ہو گئی پھر تم پیٹھ پھیر کر ہٹ گئے'),
(1261, 9, 26, 'پھر الله نے اپنی طرف سے اپنے رسول پر اور ایمان والوں پر تسکین نازل فرمائی اور وہ فوجیں اتاریں کہ جنہیں تم نے نہیں دیکھا اور کافروں کو عذاب دیا اور کافروں کو یہی سزا ہے'),
(1262, 9, 27, 'پھر اس کے بعد جسے الله چاہے تو بہ نصیب کرے گا اور الله بخشنے والا مہربان ہے'),
(1263, 9, 28, 'اے ایمان والو! مشرک تو پلید ہیں سو اس برس کے بعد مسجد حرام کے نزدیک نہ آنے پائیں اور اگر تم تنگدستی سے ڈرتے ہو تو آئندہ اگر الله چاہے تمہیں اپنے فضل سے غنسی کر دے گا بے شک الله جاننے والا حکمت والا ہے'),
(1264, 9, 29, 'ان لوگوں سے لڑو جو الله پر اور آخرت کے دن پر ایمان نہیں لاتے اور نہ اسے حرام جانتے ہیں جسے الله اور اس کے رسول نے حرام کیا ہے اور سچا دین قبول نہیں کرتے ان لوگوں میں سے جو اہلِ کتاب ہیں یہاں تک کہ ذلیل ہو کر اپنے ہاتھ سے جزیہ دیں'),
(1265, 9, 30, 'اور یہود کہتے ہیں کہ عزیر الله کا بیٹا ہے اور عیسائی کہتے ہیں مسیح الله کا بیٹا ہے یہ ان کی منہ کی باتیں ہیں وہ کافروں کی سی باتیں بنانے لگے ہیں جو ان سے پہلے گزرے ہیں الله انہیں ہلاک کرے یہ کدھر الٹے جا رہے ہیں'),
(1266, 9, 31, 'انہوں نے اپنے عالموں اور درویشوں کو الله کے سوا خدا بنا لیا ہے اور مسیح مریم کے بیٹےکو بھی حالانکہ انہیں حکم یہی ہوا تھا کہ ایک الله کے سوا کسی کی عبادت نہ کریں اس کے سوا کوئی معبود نہیں وہ ان لوگوں کے شریک مقرر کرنے سے پاک ہے'),
(1267, 9, 32, 'چاہتے ہیں کہ الله کی روشنی کو اپنے مونہوں سے بجھا دیں الله اپنی روشنی کو پورا کیے بغیر نہیں رہے گا اور اگرچہ کافر ناپسند ہی کریں'),
(1268, 9, 33, 'اس نے اپنے رسول کو ہدایت اور سچا دین دے کر بھیجا ہے تاکہ اسے سب دینوں پر غالب کرے اور اگرچہ مشرک نا پسندکریں'),
(1269, 9, 34, 'اے ایمان والو! بہت سے عالم اور فقیر لوگوں کا مال ناحق کھاتے ہیں اور الله کی راہ سے روکتے ہیں اور جو لوگ سونا اورچاندی جمع کرتے ہیں اور اسے الله کی راہ میں خرچ نہیں کرتے انہیں دردناک عذاب کی خوشخبری سنا دیجیئے'),
(1270, 9, 35, 'جس دن وہ دوزخ کی آگ میں گرم کیا جائے گا پھر اس سے ان کی پیشانیاں اور پہلو اور پیٹھیں داغی جائیں گی یہ وہی ہے جو تم نے اپنے لیے جمع کیا تھا سو اس کا مزہ چکھو جوتم جمع کرتے تھے'),
(1271, 9, 36, 'بے شک الله کے ہاں مہینوں کی گنتی بارہ مہینے ہیں الله کی کتاب میں جس دن سے الله نے زمین اور آسمان پیدا کیے ان میں سے چار عزت والے ہیں یہی سیدھا دین ہے سو ان میں اپنے اوپر ظلم نہ کرو اور تم سب مشرکوں سے لڑو جیسے وہ سب تم سے لڑتے ہیں اور جان لو کہ الله پرہیزگاروں کے ساتھ ہے'),
(1272, 9, 37, 'یہ مہینوں کا ہٹا دینا کفر میں اور ترقی ہے اس سے کا فر گمراہی میں پڑتے ہیں اس مہینے کو ایک برس تو حلال کر لیتے ہیں اور دوسرے برس اسے حرام رکھتے ہیں تاکہ ان بارہ مہینوں کی گنتی پوری کر لیں جنہیں الله نے عزت دی ہے پھر حلا ل کر لیتے ہیں جو الله نے حرام کیا ہے ان کے برے اعمال انھیں بھلے دکھائی دیتے ہیں اور الله کافروں کو ہدایت نہیں کرتا'),
(1273, 9, 38, 'اے ایمان والو تمہیں کیا ہوا جب تمہیں کہا جاتا ہے کہ الله کی راہ میں کوچ کرو تو زمین پر گرے جاتے ہو کیا تم آخرت کو چھوڑ کر دنیا کی زندگی پر خوش ہو گئے ہو دنیا کی زندگی کا فائدہ تو آخرت کے مقابلہ میں بہت ہی کم ہے'),
(1274, 9, 39, 'اگر تم نہ نکلو گے تو الله تمہیں دردناک عذاب میں مبتلا کر ے گا اور تمہاری جگہ اور لوگ پیدا کر ے گا اور تم اسے کوئی نقصان نہیں پہنچا سکو گے اور الله ہر چیز پر قادر ہے'),
(1275, 9, 40, 'اگرتم رسول کی مدد نہ کرو گے تو اس کی الله نے مدد کی ہے جس وقت اسے کافروں نے نکالا تھا کہ وہ دو میں سے دوسرا تھاجب وہ دونوں غار میں تھے جب وہ اپنے ساتھی سے کہہ رہا تھا تو غم نہ کھا بے شک الله ہمارے ساتھ ہے پھر الله نے اپنی طرف سے اس پر تسکین اتاری اور اس کی مدد کو وہ فوجیں بھیجیں جنہیں تم نے نہیں دیکھا اورکافروں کی بات کو پست کر دیا اور بات تو الله ہی کی بلند ہے اور الله زبردست (اور) حکمت والا ہے'),
(1276, 9, 41, 'تم ہلکے ہو یا بوجھل نکلو اور اپنے مالوں اور جانوں سے الله کی راہ میں لڑو یہ تمہارے حق میں بہتر ہے اگر تم سمجھتے ہو'),
(1277, 9, 42, 'اگر مال نزدیک ہوتا اور سفر ہلکا ہوتا تو وہ ضرور تیرے ساتھ ہوتے لیکن انہیں مسافت لمبی نظر آئی اور اب الله کی قسمیں کھائیں گے کہ اگر ہم سے ہو سکتا تو ہم تمہارے ساتھ ضرور چلتے اپنی جانوں کو ہلاک کرتے ہیں اور الله جانتا ہے کہ وہ جھوٹے ہیں'),
(1278, 9, 43, 'الله نے تمہیں معاف کر دیا تم نےانہیں کیوں رخصت دی یہاں تک کہ تیرے لیے سچے ظاہر ہو جاتے اور تو جھوٹوں کو جان لیتا'),
(1279, 9, 44, 'جو لوگ الله پر اور آخرت کے دن پر ایمان لاتے ہیں وہ تم سے رخصت نہیں مانگتے اس سے کہ اپنے مالوں اورجانوں سے جہاد کریں اور الله پرہیزگاروں کو خوب جانتا ہے'),
(1280, 9, 45, 'تم سے رخصت وہی مانگتے ہیں جو الله پر اور آخرت کے دن پر ایمان نہیں رکھتے اور ان کے دل شک میں پڑے ہوئے ہیں سو وہ اپنے شک میں بھٹک رہے ہیں'),
(1281, 9, 46, 'اور اگر وہ نکلنا چاہتے تو اس کے لیے کوئی سامان ضرور تیار کرتے لیکن الله نے ان کا اٹھنا پسند نہ کیا سو انہیں روک دیا اور حکم ہوا کہ بیٹھنے والوں کے ساتھ بیٹھے رہو'),
(1282, 9, 47, 'اگر وہ تم میں نکلتے تو سوائے فساد کے اور کچھ نہ بڑھاتے اور تم میں فساد ڈلوانے کی غرض سے دوڑے دوڑے پھرتے اورتم میں ان کے جاسوس بھی ہیں اور الله ظالموں کو خوب جانتا ہے'),
(1283, 9, 48, 'یہ پہلے بھی فساد کے طالب رہے ہیں اور بہت سی باتوں میں تمہارے لئےالٹ پھیر کرتے رہے ہیں یہاں تک کہ حق آ پہنچا اور الله کا حکم غالب ہوا اور وہ ناخوش ہی رہے'),
(1284, 9, 49, 'اور ان میں سے بعض کہتے ہیں کہ مجھے تو اجازت ہی دیجیئے اور فتنہ میں نہ ڈالیے خبردار! وہ فتنہ میں پڑ چکے ہیں اوربے شک دوزخ کافروں پر احاطہ کرنے والی ہے'),
(1285, 9, 50, 'اگر تمہیں آسائش حاصل ہوتی ہے تو انہیں بری لگتی ہے اور اگر کوئی مشکل پڑتی ہے تو کہتے ہیں کہ ہم نے تو اپنا کام پہلے ہی سنبھال لیا تھا اور خوشیاں مناتے لوٹ جاتے ہیں'),
(1286, 9, 51, 'کہہ دو ہمیں ہر گز نہ پہنچے گا مگر وہی جو الله نے ہمارے لیے لکھ دیا وہی ہمارا کارساز ہے اور الله ہی پر چاہیئے کہ مومن بھروسہ کریں'),
(1287, 9, 52, 'کہہ دو تم ہمارے حق میں دو بھلائیوں میں سے ایک کے منتظر ہو اور ہم تمہارے حق میں اس بات کے منتظر ہیں کہ الله اپنے ہاں سے تم پرکوئی عذاب نازل کرے یا ہمارے ہاتھوں سے تم بھی انتظار کرو ہم بھی تمہارے ساتھ انتظار کرتے ہیں'),
(1288, 9, 53, 'کہہ دو تم خوشی سے خر چ کرو یا ناخوشی سے تم سے ہرگز قبول نہیں کیا جائے گا بے شک تم نافرمان لوگ ہو'),
(1289, 9, 54, 'اور ان کے خر چ کے قبول ہونے سے کوئی چیز مانع نہیں ہوئی سوائے اس کے کہ انہوں نے الله اور اس کے رسول سے کفرکیا اورنماز میں سست ہو کر آتے ہیں اور ناخوش ہو کر خرچ کرتے ہیں'),
(1290, 9, 55, 'سو تو ان کے مال اور اولاد سے تعجب نہ کر الله یہی چاہتا ہے کہ ان چیزوں کی وجہ سے دنیا کی زندگی میں انہیں عذاب دے اور کفر کی حالت میں ان کی جانیں نکلیں'),
(1291, 9, 56, 'اور الله کی قسمیں کھاتے ہیں کہ وہ بے شک تم میں سے ہیں حالانکہ وہ تم میں سے نہیں لیکن وہ ڈرتے ہیں'),
(1292, 9, 57, 'اگر وہ کوئی پناہ کی جگہ یا غاریا گھسنے کی جگہ پائیں تو دوڑتے ہوئے ادھر جائیں'),
(1293, 9, 58, 'اور بعضے ان میں سے وہ ہیں جو خیرات مانگتے ہیں تجھے طعن دیتے ہیں سو اگر انہیں اس میں سے مل جائے تو راضی ہوتے ہیں اور اگر نہ ملے تو فوراً ناراض ہو جاتے ہیں'),
(1294, 9, 59, 'اور کیا اچھا ہوتا اگر وہ اس پر راضی ہو جاتے جو انہیں الله اور اس کے رسول نے دیا ہے اور کہتے ہیں ہمیں الله کافی ہے وہ ہمیں اپنے فضل سے دے گا اور اس کا رسول ہم الله ہی کی طرف رغبت کرنے والے ہیں'),
(1295, 9, 60, 'زکوة مفلسوں اور محتاجوں اوراس کا کام کرنے والوں کا حق ہے اورجن کی دلجوئی کرنی ہے اور غلاموں کی گردن چھوڑانے میں اور قرض داروں کے قرض میں اور الله کی راہ میں اورمسافر کو یہ الله کی طرف سے مقرر کیاہوا ہے اور الله جاننے والا حکمت والا ہے'),
(1296, 9, 61, 'اور بعضے ان میں سے پیغمبر کو ایذا دیتے ہیں اور کہتے ہیں کہ یہ شخص نِرا کان ہے کہہ دے وہ کان تمہاری بھلائی کے لیے ہے الله پر یقین رکھتا ہے اور مسلمانوں کی بات کا یقین کرتا ہے اور تم میں سے ایمان والوں کے حق میں رحمت ہے اور جو لوگ رسول الله کو ایذا دیتے ہیں ان کے لیے دردناک عذاب ہے'),
(1297, 9, 62, 'تمہارے سامنے الله کی قسمیں کھاتے ہیں تاکہ تمہیں راضی کریں اور الله اور اس کے رسول کو راضی کرنا بہت ضروری ہے اگر وہ ایمان رکھتے ہیں'),
(1298, 9, 63, 'کیا وہ نہیں جانتے کہ جو شخص الله اور اس کے رسول کا مقابلہ کرتا ہے تو اس کے واسطے دوزخ کی آگ ہے اس میں ہمیشہ رہے گا یہ بڑی ذلت ہے'),
(1299, 9, 64, 'منافق اس بات سے ڈرتے ہیں کہ مسلمانوں پر کوئی ایسی سورة نازل ہو کہ انہیں بتا دے جو منافقوں کے دل میں ہے کہہ دو ہنسی کیے جاؤ جس بات سے تم ڈرتے ہو الله اسے ضرور ظاہر کر دے گا'),
(1300, 9, 65, 'اور اگر تم ان سے دریافت کرو تو کہیں گے کہ ہم یونہی بات چیت اور دل لگی کر رہے تھےکہہ دو کیا الله سے اور اس کی آیتوں سے اور اس کے رسول سے تم ہنسی کرتے تھے'),
(1301, 9, 66, 'بہانے مت بناؤ ایمان لانے کے بعد تم کافر ہو گئے اگر ہم تم میں سے بعض کو معاف کر دیں گے تو بعض کو عذاب بھی دیں گے کیوں کہ وہ گناہ کرتے رہے ہیں'),
(1302, 9, 67, 'منافق مر د اور منافق عورتیں ایک دوسرے کے ہم جنس ہیں برے کامو ں کا حکم کرتے ہیں اور نیک کاموں سے منع کرتے ہیں او رہاتھ بند کیے رہتے ہیں وہ الله کو بھول گئے سوالله نے انہیں بھلا دیا بے شک منافق وہی نافرمان ہیں'),
(1303, 9, 68, 'الله نے منافق مردوں اورمنافق عورتوں اور کافروں کو دوزخ کی آگ کا وعدہ دیا ہے پڑے رہیں گے اس میں وہی انہیں کافی ہے اور الله نے ان پر لعنت کی ہے اوران کے لیے دائمی عذاب ہے'),
(1304, 9, 69, 'جس طرح تم سے پہلے لوگ تم سے طاقت میں زيادہ تھے اور مال اور اولاد میں بھی زيادہ تھے پھر وہ اپنے حصہ سے فائدہ اٹھا گئے اور تم نے اپنے حصہ سے فائدہ اٹھایا جیسے تم سے پہلہے لوگ اپنے حصہ سے فائدہ اٹھا گئے اور تم بھی انہیں کی سی چال چلتے ہو یہ وہ لوگ ہیں جن کے اعمال دنیا اور آخرت میں ضائع ہو گئے اور وہی نقصان اٹھانے والے ہیں'),
(1305, 9, 70, 'کیا انہیں ان لوگو ں کی خبر نہیں پہنچی جو ان سے پہلے تھے نوح کی قوم اور عاد اور ثمود اورابراھیم کی قوم اورمدین والوں کی اوران بستیوں کی خبر جو الٹ دی گئی تھیں ان کے پاس ان کے رسول صاف احکام لے کر پہنچے سو الله ایسا نہ تھا کہ ان پر ظلم کرتا لیکن وہی اپنے آپ پر ظلم کرتے تھے'),
(1306, 9, 71, 'اور ایمان والے مرد اور ایمان والی عورتیں ایک دوسرے کےمددگار ہیں نیکی کا حکم کرتے ہیں اوربرائی سے روکتے ہیں اورنماز قائم کرتے ہیں اور زکواة دیتے ہیں اور الله اور اس کے رسول کی فرمانبرداری کرتے ہیں یہی لوگ ہیں جن پر الله رحم کرے گا بے شک الله زبردست حکمت والا ہے'),
(1307, 9, 72, 'الله نے ایمان دار مردوں اورایمان والی عورتوں کو باغوں کا وعدہ دیا ہے جن کے نیچے نہریں بہتی ہوں گیان میں ہمیشہ رہنے والے ہوں گے اورعمدہ مکانوں اور ہمیشگی کے باغوں میں اور الله کی رضا ان سب سے بڑی ہے یہی وہ بڑی کامیابی ہے'),
(1308, 9, 73, 'اے نبی! کافروں اور منافقوں سے لڑائی کر اوران پر سختی کر اور ان کا ٹھکانا دوزخ ہے اور وہ بری جگہ ہے'),
(1309, 9, 74, 'الله کی قسمیں کھاتے ہیں کہ ہم نے نہیں کہا اوربے شک انہوں نے کفر کا کلمہ کہا ہے اورمسلمان ہونے کے بعد کافر ہوگئے اورانہوں نے قصد کیا تھا ایسی چیز کا جونہیں پا سکے اور یہ سب کچھ اسی کا بدلہ تھا کہ انہیں الله اور اس کے رسول نے اپنے فضل سے دولتمند کر دیا ہے سو اگر وہ توبہ کریں تو ان کے لیے بہتر ہے اور اگر وہ منہ پھیر لیں تو الله انہیں دنیا اور آخرت میں دردناک عذاب دے گا اور انہیں روئے زمین پر کوئی دوست اور کوئی مددگار نہیں ملے گا'),
(1310, 9, 75, 'اور بعضے ان میں سے وہ ہیں جنہوں نے الله سے عہد کیا تھا کہ اگر وہ ہمیں اپنے فضل سے دے تو ہم ضرور خیرات کیا کریں گے اور نیکو ں میں سےہو جائیں'),
(1311, 9, 76, 'پھر جب الله نے اپنے فضل سے دیا تو اس میں بخل کرنے لگے اورمنہ موڑ کر پھر بیٹھے'),
(1312, 9, 77, 'تو نتیجہ یہ ہوا کہ اس دن تک الله سے ملیں گے الله نے ان کے دلوں میں نفاق پیدا کر دیا اس لیے کہ انہوں نے جو الله سے وعدہ کیا تھا اسے پورا نہ کیا اوروہ اس لیے جھوٹ بولا کرتے تھے'),
(1313, 9, 78, 'کیا وہ نہیں جانتے کہ الله ان کا بھید اور ان کا مشورہ جانتا ہے اوریہ کہ الله غیب کی باتیں جاننے والا ہے'),
(1314, 9, 79, 'وہ لوگ جو ان مسلمانوں پر طعن کرتے ہیں جودل کھول کر خیرات کرتے ہیں اور جو لوگ اپنی محنت کے سوا طاقت نہیں رکھتے پھر ان پر ٹھٹھا کرتے ہیں الله ان سے ٹھٹھا کرتا ہے اوران کے لیے دردناک عذاب ہے'),
(1315, 9, 80, 'تو ان کے لیے بخشش مانگ یا نہ مانگ اگر تو ان کے لیے ستر دفعہ بھی بخشش مانگے گا تو بھی الله انہیں ہر گز نہیں بخشے گا یہ اس لیے کہ انہوں نے الله اور اس کے رسول سےکفر کیا اور الله نافرمانوں کو راستہ نہیں دکھاتا'),
(1316, 9, 81, 'جو لوگ پیچھے رہ گئے وہ رسول الله کی مرضی کے خلاف بیٹھ رہنے سے خوش ہوتے ہیں اوراس بات کو ناپسند کیا کہ اپنے مالوں اور جانوں سے الله کی راہ میں جہاد کریں اور کہا گرمی میں مت نکلو کہہ دو کہ دوزخ کی آگ کہیں زیادہ گرم ہے کاش یہ سمجھ سکتے'),
(1317, 9, 82, 'سووہ تھوڑا سا ہنسیں اور زیادہ روئیں ان کے اعمال کے بدلے جو کرتے رہے ہیں'),
(1318, 9, 83, 'سواگر تجھے الله ان میں سے کسی فرقہ کی طرف پھر لے جائے پھر تجھ سے نکلنے کی اجازت چاہیں تو کہہ دو کہ تم میرے ساتھ کبھی بھی ہرگز نہ نکلو گے اور میرے ساتھ ہو کر کسی دشمن سے نہ لڑو گے تمہیں پہلی مرتبہ بیٹھنا پسند آیا سو پیچھے رہنے والوں کے ساتھ بیٹھے رہو'),
(1319, 9, 84, 'اوران میں سے جو مرجائے کسی پر کبھی نماز نہ پڑھ اور نہ اس کی قبر پر کھڑا ہو بے شک انہوں نے الله اور اس کے رسول سے کفر کیا اور نافرمانی کی حالت میں مر گئے'),
(1320, 9, 85, 'اور ان کے مالوں اوراولاد سے تعجب نہ کر الله یہی چاہتا ہے کہ انہیں ان چیزوں کے باعث دنیا میں عذاب دے اور ان کی جانیں نکلیں ایسے حال میں کہ وہ کافر ہی ہوں'),
(1321, 9, 86, 'اور جب کوئی سورة نازل ہوتی ہے کہ الله پر ایمان لاؤ اور اس کے رسول کےساتھ ہو کر جہاد کرو تو ان میں سے دولت مند بھی تجھ سے رخصت مانگتے ہیں اورکہتے ہیں کہ ہمیں چھوڑ دے کہ بیٹھنے والوں کے ساتھ ہو جائیں'),
(1322, 9, 87, 'وہ خوش ہیں کہ پیچھے رہ جانے والی عورتوں کے ساتھ رہ جائیں اور ان کے دلوں پر مہر کر دی گئی ہے سووہ نہیں سمجھتے'),
(1323, 9, 88, 'لیکن رسول اور جو لوگ ان کے ساتھ ایمان والے ہیں وہ اپنے مالوں اور جانوں سے جہاد کرتے ہیں اور انہیں لوگو ں کے لیے بھلائیاں ہیں اور وہی نجات پانے والے ہیں'),
(1324, 9, 89, 'الله نے ان کے لیے باغ تیار کیے ہیں جن کے نیچے نہریں بہتی ہیں ان میں ہمیشہ رہیں گےیہی بڑی کامیابی ہے'),
(1325, 9, 90, 'اور بہانے کرنے والے گنوار آئے تاکہ انہیں رخصت مل جائے اوربیٹھ رہے وہ جنہوں نے الله اوراس کے رسول سے جھوٹ بولا تھا جو ان میں سے کافر ہیں عنقریب انہیں دردناک عذاب پہنچے گا'),
(1326, 9, 91, 'ضعیفوں اور مریضوں پر اور ان لوگوں پر جو نہیں پاتے جو خر چ کریں کوئی گناہ نہیں ہے جب الله اور اس کے رسول کے ساتھ خیر خواہی کریں نیکو کاروں پر کوئی الزام نہیں ہے اور الله بخشنے والا مہربان ہے'),
(1327, 9, 92, 'اور ان لوگو ں پر بھی کوئی گناہ نہیں کہ جب وہ تیرے پاس آئیں کہ انہیں سواری دے تو نے کہا میرے پاس کوئی چیز نہیں کہ تمہیں اس پر سوار کر دوں تو وہ لوٹ گئے اوراس غم سے کہ ان کے پاس خرچ موجود نہیں تھا ان کی آنکھوں سے آنسو بہہ رہے تھے'),
(1328, 9, 93, 'الزام ان لوگوں پر ہے جو دولتمند ہیں اورتم سے اجازت طلب کرتے ہیں اس بات سے وہ خوش ہیں کہ پیچھے رہنے والیوں کے ساتھ رہ جائیں اور الله نے ان کے دلوں پر مہر کر دی ہے پس وہ نہیں سمجھتے'),
(1329, 9, 94, 'جب تم ان کی طرف واپس جاؤ گے تو تم سے عذر کریں گے کہہ دو عذر مت کرو ہم تمہاری بات ہرگز نہیں مانیں گے تمہارے سب حالات اللہ ہمیں بتاچکا ہے اور ابھی الله اور اس کا رسول تمہارے کام کو دیکھے گا پھر تم غائب اور حاضر کے جاننے والےکی طرف لوٹائے جاؤ گے سو وہ تمہیں بتا دے گا جو تم کر رہے تھے'),
(1330, 9, 95, 'جب تم ان کی طرف پھر جاؤ گے تو تمہارے سامنے الله کی قسمیں کھائیں گےتاکہ تم ان سے در گزر کرو بے شک وہ پلید ہیں اور جو کام کرتے رہے ہیں ان کے بدلے ان کا ٹھکانا دوزخ ہے'),
(1331, 9, 96, 'وہ لوگ تمہارے سامنے قسمیں کھائیں گے تاکہ تم ان سے خوش ہو جاؤ اگر تم ان سے خوش بھی ہو جاؤ تو بھی الله نافرمانوں سے خوش نہیں ہوتا'),
(1332, 9, 97, 'گنوار کفر اور نفاق میں بہت سخت ہیں اور اس قابل ہیں کہ جو احکام الله نے اپنے رسول پر نازل فرمائے ہیں ان سے واقف نہ ہوں اور الله جاننے والا حکمت والا ہے'),
(1333, 9, 98, 'اور بعض گنوار ایسے ہیں کہ جو کچھ خرچ کرتے ہیں اسے تاوان سمجھتے ہیں اور تم پر زمانہ کی گردشوں کا انتظار کرتے ہیں انہیں پر بری گردش آئے اور الله سننے والا جاننے والا ہے'),
(1334, 9, 99, 'اور بعضے گنوار ایسے ہیں کہ الله پر اور قیامت کے دن پر ایمان لاتے ہیں اور جو کچھ خرچ کرتے ہیں اسے الله کے نزدیک ہونے اور پیغمبر کی دعاؤں کا ذریعہ سمجھتے ہیں خبردار بے شک وہ ان کے لیے نزدیکی کا سبب ہے عنقریب انہیں اللہ اپنی رحمت میں داخل کرے گا بے شک الله بخشنے والا مہربان ہے'),
(1335, 9, 100, 'اور جو لوگ قدیم میں پہلے ہجرت کرنے والوں اور مدد دینے والو ں میں سے اور وہ لوگ جو نیکی میں ان کی پیروی کرنے والے ہیں الله ان سے راضی ہوئےاوروہ اس سے راضی ہوئےان کے لیے ایسے باغ تیار کیے ہیں جن کے نیچے نہریں بہتی ہیں ان میں ہمیشہ رہیں گے یہ بڑی کامیابی ہے'),
(1336, 9, 101, 'اور تمہارے گرد و نواح کے بعضے گنوار منافق ہیں اور بعض مدینہ والے بھی نفاق پر اڑے ہوئے ہیں تم انہیں نہیں جانتے ہم انہیں جانتے ہیں ہم انہیں دوہری سزا دیں گے پھر وہ بڑے عذاب کی طرف لوٹائے جائیں گے'),
(1337, 9, 102, 'اور کچھ اور بھی ہیں کہ انھوں نے اپنے گناہوں کا اقرار کیا ہے انہوں نے اپنے نیک اور بد کاموں کو ملا دیاہے قریب ہے کہ الله انہیں معاف کر دے بے شک الله بخشنے والا مہربان ہے'),
(1338, 9, 103, 'ان کے مالوں میں سے زکواة لے کہ اس سے ان کے ظاہر کو پاک اور ان کے باطن کو صاف کر دے اورانہیں دعا دے بے شک تیری دعا ان کے لیے تسکین ہے اوراللہ سننے والا جاننے والا ہے'),
(1339, 9, 104, 'کیا یہ لوگ نہیں جانتے الله ہی اپنے بندوں کی توبہ قبول فرماتا ہے اور صدقات لیتا ہے اور بے شک الله ہی توبہ قبول کرنے والا مہربان ہے'),
(1340, 9, 105, 'اور کہہ دےکہ کام کیے جاؤ پھر عنقریب الله اور اس کا رسول اور مسلمان تمہارے کام کو دیکھ لیں گے اور عنقریب تم غائب اور حاضر کے جاننے والے کی طرف لوٹائے جاؤ گےپھر وہ تمہیں بتا دے گا جو کچھ تم کرتے تھے'),
(1341, 9, 106, 'اور کچھ اور لوگ ہیں جن کا کام الله کے حکم پر موقوف ہے خواہ انہیں عذاب دے یا انہیں معاف کر دے اور الله جاننے والا حکمت والا ہے'),
(1342, 9, 107, 'اور جنہوں نے نقصان پہنچانے اور کفر کرنے اورمسلمانوں میں تفریق ڈالنے کے لیے مسجد بنائی ہے اورواسطے گھات لگانے ان لوگوں کے جو الله اور اس کے رسول سے پہلے لڑ چکے ہیں اور البتہ قسمیں کھائیں گہ ہمارا مقصد تو صرف بھلائی تھی اور اللهگواہی دیتا ہے کہ بے شک وہ جھوٹے ہیں'),
(1343, 9, 108, 'تو اس میں کبھی کھڑانہ ہو البتہ وہ مسجد جس کی بنیاد پہلے دن سے پرہیزگاری پر رکھی گئی ہے وہ اس کے قابل ہے تو اس میں کھڑا ہو اس میں ایسے لوگ ہیں جو پاک رہنے کو پسند کرتے ہیں اور الله پاک رہنے والوں کو پسند کرتا ہے'),
(1344, 9, 109, 'بھلا جس نے اپنی عمارت کی بنیاد الله سے ڈرنے اوراس کی رضا مندی پر رکھی ہو وہ بہتر ہے یا جس نے اپنی عمارت کی بنیاد ایک کھائی کے کنارے پررکھی جو گرنے والی ہے پھر وہ اسے دوزخ کی آگ میں لے گری اور الله ظالموں کو راہ نہیں دکھاتا'),
(1345, 9, 110, 'جوعمارت انہوں نے بنائی ہے ہمیشہ ان کے دلو ں میں کھٹکتی رہے گی مگر جب ان کے دل ٹکڑے ہوجائیں اور الله جاننے والا حکمت والا ہے'),
(1346, 9, 111, 'بے شک الله مسلمانوں سے ان کی جان اوران کا مال اس قیمت پر خرید لیے ہیں کہ ان کی جنت ہے الله کی راہ میں لڑتے ہیں پھر قتل کرتے ہیں اور قتل بھی کیے جاتے ہیں یہ توریت اور انجیل اور قرآن میں سچا وعدہ ہے جس کا پورا کرنااسے ضروری ہے اور الله سے زیادہ وعدہ پورا کرنے والا کون ہے جو سودا تم نے اس سے کیا ہے اس سے خوش رہو اور یہ بڑی کامیابی ہے'),
(1347, 9, 112, 'توبہ کرنے والے عبادت کرنے والے شکر کرنے والے روزہ رکھنے والے رکوع کرنے والے سجدہ کرنے والے اچھے کاموں کا حکم کرنے والے بری باتوں سے روکنے والے الله کی حدوں کی حفاظت کرنے والے اور ایسے مومنو ں کو خوشخبری سنا دے'),
(1348, 9, 113, 'پیغمبراور مسلمانوں کو یہ بات مناسب نہیں کہ مشرکوں کے لیے بخشش کی دعا کریں اگرچہ وہ رشتہ دار ہی ہوں جب کہ ان پر ظاہر ہو گیا ہے کہ وہ دوزخی ہیں'),
(1349, 9, 114, 'اور ابراھیم کا اپنے باپ کے لیے بخشش کی دعا کرنا اور ایک وعدہ کے سبب سے تھا جو وہ اس سے کر چکے تھے پھر جب انہیں معلوم ہوا کہ وہ الله کا دشمن ہے تو اس سے بیزار ہو گئے بے شک ابراھیم بڑے نرم دل تحمل والے تھے'),
(1350, 9, 115, 'اورالله ایسا نہیں ہے کہ کسی قوم کو صحیح راستہ بتلانے کے بعد گمراہ کر دے جب تک ان پر واضح نہ کر دے وہ چیز جس سے انہیں بچنا چاہیے بے شک الله ہر چیز کو جاننے والا ہے'),
(1351, 9, 116, 'آسمانوں اور زمین میں الله ہی کی سلطنت ہے وہی جلاتا ہے اور مارتا ہے اور الله کے سوا تمہارا کوئی دوست اور مددگار نہیں'),
(1352, 9, 117, 'الله نے نبی کے حال پر رحمت سے توجہ فرمائی اور مہاجرین اور انصار کے حال پر بھی جنہوں نے ایسی تنگی کے وقت میں نبی کا ساتھ دیا بعد اس کے کہ ان میں سے بعض کے دل پھر جانے کے قریب تھے اپنی رحمت سے ان پر توجہ فرمائی بے شک وہ ان پر شفقت کرنے والا مہربان ہے'),
(1353, 9, 118, 'اور ان تینوں پر بھی جن کا معاملہ ملتوی کیا گیا یہاں تک کہ جب ان پر زمین باوجود کشادہ ہونے کے تنگ ہوگئی اور ان کی جانیں بھی ان پر تنگ ہوگئیں اور انہوں نے سمجھ لیا کہ الله سے کوئی پناہ نہیں سو اسی کی طرف آنے کے پھر بھی اپنی رحمت سے ان پر متوجہ ہوا تاکہ وہ توبہ کریں بے شک الله توبہ قبول کرنے والا مہربان ہے'),
(1354, 9, 119, 'اے ایمان والو! الله سے ڈرتے رہو اور سچوں کے ساتھ رہو'),
(1355, 9, 120, 'مدینہ والوں اور ان کے آس پاس دیہات کے رہنے والوں کو یہ مناسب نہیں تھا کہ الله کے رسول سے پیچھے رہ جائیں اور نہ یہ کہ اپنی جانوں کو اس کی جان سے زیادہ عزیز سمجھیں یہ اس لیے ہے کہ انہیں الله کی راہ میں جو تکلیف پہنچتی ہے پیاس کی یا ماندگی کی یا بھوک کی یاہ وہ ایسی جگہ چلتے ہیں جو کافروں کے غصہ کو بھڑکائے اور یا کافروں سے کوئی چیز چھین لیتے ہیں ہر بات پر ان کے لیے عمل صالح لکھا جاتا ہے بے شک الله نیکی کرنے والوں کا اجر ضائع نہیں کرتا'),
(1356, 9, 121, 'اور جو وہ تھوڑا یا بہت خرچ کرتے ہیں یا کوئی میدان طے کرتے ہیں تو یہ سب کچھ ان کے لیے لکھ لیا جاتا ہے تاکہ الله انہیں ان کے اعمال کا اچھا بدلہ دے'),
(1357, 9, 122, 'اورایسا تو نہیں ہوسکتا کہ مسلمان سب کے سب کوچ کریں سو کیوں نہ نکلا ہر فرقے میں سے ایک حصہ تاکہ دین میں سمجھ پیدا کریں اور جب اپنی قوم کی طرف واپس آئیں تو ان کو ڈرائیں تاکہ وہ بچتے رہیں'),
(1358, 9, 123, 'اے ایمان والو اپنے نزدیک کے کافروں سے لڑو اور چاہئے کہ وہ تم میں سختی پائیں اور جان لو کہ اللہ پر ہیزگاروں کے ساتھ ہے'),
(1359, 9, 124, 'اور جب کوئی سورت نازل ہوتی ہے تو ان میں سے بعض کہتے ہیں کہ اس سورت نےتم میں سے کس کے ایمان کو بڑھایا ہے سو جو لوگ ایمان والے ہیں اس سورت نے ان کے ایمان کو بڑھایا ہے اور وہ خوش ہوتے ہیں'),
(1360, 9, 125, 'اور جن کے دلوں میں مرض ہے سوان کے حق میں نجاست پر نجاست بڑھا دی اور وہ مرتے دم تک کافر ہی رہے'),
(1361, 9, 126, 'کیا وہ نہیں دیکھتے کہ وہ ہر سال میں ایک دفعہ یا دو دفعہ آزمائے جاتے ہیں پھر بھی توبہ نہیں کرتے اور نہ نصیحت حاصل کرتے ہیں'),
(1362, 9, 127, 'اورجب کوئی سورت نازل ہوتی ہے تو ان میں ایک دوسرے کی طرف دیکھنے لگتا ہے کہ کیا تمہیں کوئی مسلمان دیکھتا ہے پھر چل نکلتے ہیں الله نے ان کے دل پھیر دئے ہیں اور لیے کہ وہ لوگ سمجھ نہیں رکھتے'),
(1363, 9, 128, 'البتہ تحقیق تمہارے پاس تم ہی میں سے رسول آیا ہے اسے تمہاری تکلیف گراں معلوم ہوتی ہے تمہاری بھلائی پر وہ حریص ہے مومنوں پر نہایت شفقت کرنے والا مہربان ہے'),
(1364, 9, 129, 'پھر اگر یہ لوگ پھر جائیں تو کہہ دو مجھے الله ہی کافی ہے اس کے سوا اور کوئی معبود نہیں اسی پر میں بھروسہ کرتا ہوں اور وہی عرش عظیم کا مالک ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'یہ حکمت والی کتا ب کی آیتیں ہیں'),
(1366, 10, 2, 'کیا اس بات سے لوگوں کو تعجب ہوا کہ ہم نے ان میں سے ایک شخص کے پاس وحی بھیج دی کہ سب آدمیوں کو ڈرائے اور جو ایمان لائیں انہیں یہ خوشخبری سنائے کہ انہیں اپنے رب کے ہاں پہنچ کر پورا مرتبہ ملے گا کافر کہتے ہیں کہ یہ شخص صریح جادوگر ہے'),
(1367, 10, 3, 'بے شک تمہارا رب الله ہی ہے جس نے آسمان اور زمین چھ دن میں بنائے پھر عرش پر قائم ہوا وہی ہر کام کا انتظام کرتا ہے اس کی اجازت کے سوا کوئی سفارش کرنے والا نہیں ہے یہی الله تمہارا پروردگار ہے سو اسی کی عبادت کرو کیا تم پھر بھی نہیں سمجھتے'),
(1368, 10, 4, 'تم سب کو اسی کی طرف لوٹ کر جانا ہے الله کا وعدہ سچا ہے وہی پہلی مرتبہ پیدا کرتا ہے پھر وہی دوبارہ پیدا کرے گا تاکہ جو لوگ ایمان لائے اورنیک کام کیے انہیں انصاف کے ساتھ بدلہ دے اور جن لوگو ں نے کفر کیا ان کے واسطے کھولتا ہوا پانی پینے کو ہوگا اوران کے کفر کے سبب سے دردناک عذاب ہوگا'),
(1369, 10, 5, 'وہی ہے جس نے سورج کو روشن بنایا اور چاند کو منور فرمایا اور چاند کی منزلیں مقرر کیں تاکہ تم برسوں کا شمار اور حساب معلوم کر سکو یہ سب کچھ الله نے تدبیر سے پیدا کیا ہے وہ اپنی آیتیں سمجھداروں کے لیے کھول کھول کر بیان فرماتا ہے'),
(1370, 10, 6, 'رات اور دن کے آنے جانے میں اور جو چیزیں الله نے آسمانوں اور زمین میں پیدا کی ہیں ان میں ان لوگو ں کے لیے نشانیاں ہیں جو ڈرتے ہیں'),
(1371, 10, 7, 'البتہ جو لوگ ہم سے ملنے کی امید نہیں رکھتے اور دنیاکی زندگی پر خوش ہوئے اور اسی پر مطمئن ہو گئے اور جو لوگ ہماری نشانیوں سے غافل ہیں'),
(1372, 10, 8, 'ان کا ٹھکانا آگ ہے بسبب اس کے جو کرتے تھے'),
(1373, 10, 9, 'بے شک جو لوگ ایمان لائے اورانہوں نے نیک کام کیے انہیں ان کا رب ان کے ایمان کے سبب ہدایت کرے گا ان کے نیچے نعمت کے باغوں میں نہریں بہتی ہوں گی'),
(1374, 10, 10, 'اس جگہ ان کی دعا یہ ہو گی کہ اے الله تیری ذات پاک ہے اوروہاں ان کا باہمی تحفہ سلام ہوگا اور ان کی دعا کا خاتمہ اس پر ہوگا سب تعریف الله کے لیے ہے جو سارے جہان کا پالنے والا ہے'),
(1375, 10, 11, 'اور اگر الله لوگوں کو برائی جلد پہنچا دے جس طرح وہ بھلائی جلدی مانگتے ہیں تو ان کی عمر ختم کر دی جائے سو ہم چھوڑے رکھتے ہیں ان لوگوں کو جنہیں ہماری ملاقات کی امید نہیں کہ اپنی سرکشی میں بھٹکتے رہیں'),
(1376, 10, 12, 'اورجب انسان کو تکلیف پہنچتی ہے تو لیٹے اور بیٹھے اورکھڑے ہونے کی حالت میں ہمیں پکارتا ہے پھر جب ہم اس سے اس تکلیف کودور کر دیتے ہیں تو اس طرح گزر جاتا ہے گویا کہ ہمیں کسی تکلیف پہنچنے پر پکارا ہی نہ تھا اس طرح بیباکوں کو پسند آیا ہے جو کچھ وہ کر رہے ہیں'),
(1377, 10, 13, 'اور البتہ ہم تم سے پہلے کئی امتوں کو ہلاک کر چکے ہیں جب انہوں نے ظلم اختیار کیا حالانکہ پیغمبر ان کے پاس کھلی نشانیاں لائے تھے اور وہ ہر گز ایمان لانے والے نہ تھے ہم گناہگارو ں کو ایسی ہی سزا دیا کرتے ہیں'),
(1378, 10, 14, 'پھر ہم نے تمہیں ان کے بعد زمین میں نائب بنایا تاکہ دیکھیں تم کیا کرتے ہو'),
(1379, 10, 15, 'اور جب ان کے سامنے ہماری واضح آیتیں پڑھی جاتی ہیں وہ لوگ کہتے ہیں جنہیں ہم سے ملاقات کی امید نہیں کہ اس کے سوا کوئی قرآن لے آ یا اسے بدل دے تو کہہ دے میرا کام نہیں کہ اپنی طرف سے اسے بدل دوں میں اس کی تابعداری کرتا ہوں جو میری طرف وحی کی جائے اگر میں اپنے رب کی نافرمانی کروں توبڑے دن کے عذاب سے ڈرتا ہوں'),
(1380, 10, 16, 'کہہ دو اگر الله چاہتا تو میں اسے تمہارےسامنے نہ پڑھتا اورنہ وہی تمہیں اس سے خبردار کرتا کیوں کہ اس سے پہلے تم میں ایک عمر گذار چکا ہوں کیا پھر تم نہیں سمجھتے'),
(1381, 10, 17, 'پھر اس سے بڑا ظالم کون ہو گا جو الله پر بہتان باندھے یا اس کی آیتوں کو جھٹلائے بے شک گناہگاروں کابھلا نہیں ہوتا'),
(1382, 10, 18, 'اور الله کے سوا اس چیز کی پرستش کرتے ہیں جونہ انہیں نقصان پہنچا سکے اورنہ انہیں نفع دے سکے اور کہتے ہیں الله کے ہاں یہ ہمارے سفارشی ہیں کہہ دو کیا تم الله کو بتلاتے ہو جو اسے آسمانوں اور زمین میں معلوم نہیں وہ پاک ہے اوران لوگو ں کے شرک سے بلند ہے'),
(1383, 10, 19, 'اوروہ لوگ ایک ہی امت تھے پھر جدا جدا ہو گئے اور اگر ایک بات تمہارے پرودگار کی طرف سے پہلے نہ ہو چکی ہو تی تو جن باتوں میں وہ اختلاف کر تے ہیں ان میں فیصلہ کر دیا جاتا ہے'),
(1384, 10, 20, 'اور کہتے ہیں اس پر اس کے رب سے کوئی نشانی کیوں نہ اتری سو تو کہہ دے کہ غیب کی بات الله ہی جانتا ہے سو تم انتظار کرو میں بھی تمہارے ساتھ انتظار کرتا ہوں'),
(1385, 10, 21, 'اورجب ہم لوگوں کو اپنی رحمت کا مزہ چکھاتے ہیں اس تکلیف کے بعد جو انہیں پہنچی تھی تو وہ ہماری آیتو ں کے متعلق حیلے کرنے لگتے ہیں کہہ دو کہ الله بہت جلد حیلہ کرنے والا ہے بے شک ہمارے فرشتے تمہارے سب حیلو ں کو لکھ رہے ہیں'),
(1386, 10, 22, 'وہ وہی ہے جو تمہیں جنگل اور دریا میں سیر کرنے کی توفیق دیتا ہے یہاں تک کہ جب تم کشتیوں میں بیٹھتے ہو اور وہ کشتیاں لوگو ں کو موافق ہوا کے ذریعہ سے لے کرچلتی ہیں اور وہ لوگ ان سے خوش ہوتے ہیں تو ناگہاں تیز ہوا چلتی ہے اور ہر طرف سے ان پر لہریں چھانے لگتی ہیں اور و ہ خیال کرتے ہیں کہ بےشک وہ لہروں میں گھر گئے ہیں تو سب خالص اعتقاد سے الله ہی کو پکارنے لگتے ہیں کہ اگر تو ہمیں اس مصیبت سے بچادے تو ہم ضرور شکر گزار رہیں گے'),
(1387, 10, 23, 'پھر جب الله انہیں نجات دے دیتا ہے تو ملک میں ناحق شرارت کرنے لگتے ہیں اے لوگو تمہاری شرارت کا وبال تمہاری جانو ں پر ہی پڑے گا دنیا کی زندگی کا نفع اٹھا لو پھر ہمارے ہاں ہی تمہیں لوٹ کر آناہے پھر ہم تمہیں بتلادیں گے جو کچھ تم کیا کرتے تھے'),
(1388, 10, 24, 'دنیا کی زندگی کی مثال مینہ کی سی ہے کہ اسے ہم نے آسمان سے اتارا پھراس کے ساتھ سبزہ مل کر نکلا جسے آدمی اور جانور کھاتے ہیں یہاں تک کہ جب زمین سبزے سے خوبصورت اور آراستہ ہو گئی اورزمین والوں نے خیال کیا کہ وہ اس پر بالکل قابض ہو چکے ہیں تو اس پر ہماری طرف سے دن یا رات میں کوئی حادثہ آ پڑا سو ہم نے اسے ایسا صاف کر دیا کہ گویا کل وہا ں کچھ بھی نہ تھا اس طرح ہم نشانیوں کو کھول کر بیان کرتے ہیں اور لوگو ں کے سامنے جو غور کرتے ہیں'),
(1389, 10, 25, 'اور الله سلامتی کے گھر کی طرف بلاتا ہے اور جسے چاہے سیدھا راستہ دکھاتا ہے'),
(1390, 10, 26, 'جنہوں نے بھلائی کی ان کے لئے بھلائی ہے اور زیادتی بھی اور ان کے منہ پر سیاہی اور رسوائی نہیں چڑھے گی وہ بہشتی ہیں وہ اسی میں ہمیشہ رہیں گے'),
(1391, 10, 27, 'اور جنہوں نے برے کام کئے تو برائی کا بدلہ ویسا ہی ہوگا کہ ان پر ذلت چھائے گی اور انہیں اللہ سے بچانے والا کوئی نہ ہوگا گویا کہ ان کے مونہوں پر اندھیری رات کے ٹکڑے اوڑدئے گئے ہیں یہی دوزخی ہیں وہ اس میں ہمیشہ رہیں گے'),
(1392, 10, 28, 'اور جس دن ہم ان سب کو جمع کرینگے پھر مشرکوں سے کہیں گے تم اور تمہارے شریک اپنی جگہ کھڑے رہو تو ہم ان میں پھوٹ ڈال دینگے اور ان کے شریک کہیں گے کہ تم ہماری عبادت نہیں کرتے تھے'),
(1393, 10, 29, 'سو اللہ ہمارے اور تمہارے درمیان گواہ کافی ہے کہ ہمیں تمہاری عبادت کی خبر ہی نہ تھی'),
(1394, 10, 30, 'اس جگہ ہر شخص اپنے پہلے کئے ہوئے کاموں کو جانچ لے گا اور یہ لوگ اللہ کی طرف لوٹائے جائینگے جو ان کا حقیقی مالک ہے اور جو جھوٹ وہ باندھا کرتے تھے ان سے جاتا رہے گا'),
(1395, 10, 31, 'کہو تمہیں آسمان اور زمین سے کون روزی دیتا ہے یا کانوں اور آنکھوں کا کون مالک ہے اور زندہ کو مردہ سے کون نکلتا ہے اور مردہ کو زندہ سے کون نکلتا ہے اور سب کاموں کا کون انتظام کرتا ہے سو کہیں گے کہ اللہ تو کہہ دو کہ پھر (اللہ) سے کیوں نہیں ڈرتے'),
(1396, 10, 32, 'یہی الله تمہارا سچا رب ہے حق کے بعد گمراہی کے سوا اور ہے کیا سوتم کدھر پھرے جاتے ہو'),
(1397, 10, 33, 'اسی طرح ان نافرمانوں کے حق میں تیرے رب کا فیصلہ ثابت ہو کر رہا کہ یہ ایمان نہیں لائیں گے'),
(1398, 10, 34, 'کہہ دو آیا تمہارے شریکوں میں کوئی ایسا ہے جو مخلوقات کو پیدا کرے پھر اسے دوبارہ زندہ کرے کہہ دو الله پہلے پیدا کرتا ہے پھر اسے لوٹائے گا سو تم کہاں پھیرے جاتے ہو'),
(1399, 10, 35, 'کہہ دو آیا تمہارے شریکوں میں کوئی ہے جو صحیح راہ بتلائے کہہ دو الله ہی صحیح راہ بتلاتا ہے تو جو اب صحیح راستہ بتلائے اسکی بات ماننی چاہیئے یا اس کی جو خود راہ نہ پائے مگر جب کوئی اوراسے راہ بتلائے سو تمہیں کیا ہوگیا کیا انصاف کرتے ہو'),
(1400, 10, 36, 'اور وہ اکثر اٹکل پر چلتے ہیں بےشک حق بات کے سمجھنے میں اٹکل ذرا بھی کام نہیں دیتی بے شک الله جانتا ہے جو کچھ وہ کرتے ہیں'),
(1401, 10, 37, 'اوریہ قرآن ایسا نہیں کہ الله کے سوا اسے کوئی اپنی طرف سے بنا لائے اور لیکن اپنے سے پہلے کلام کی تصدیق کرتا ہے اوران چیزوں کو بیان کرتا ہے جو تم پر لکھی گئی اس میں کوئی شک نہیں کہ یہ رب العالمین کی طرف سے ہے'),
(1402, 10, 38, 'کیا یہ لوگ کہتے ہیں اس نے اسے خود بنایا ہے کہہ دو تم ایک ہی ایسی سورت لے آؤ اور الله کے سوا جسے بلا سکو بلا لو اگر تم سچے ہو'),
(1403, 10, 39, 'بلکہ انہوں نے اس چیز کو جھٹلایا جسے وہ سمجھ نہ سکے اوربھی اس کی حقیقت ان پر کھلی نہیں اسی طرح جو لوگ ان سے پہلے تھے انہوں نے بھی جھٹلایا تھا سو دیکھ لو کہ ظالموں کاانجام کیسا ہوا'),
(1404, 10, 40, 'اور ان میں سے بعض ایسے ہیں کہ اس پر ایمان لے آتے ہیں اور بعض ایسے ہیں کہ ایمان نہیں لاتے اور تمہارا رب شریروں سے خوب واقف ہے'),
(1405, 10, 41, 'اوراگر تجھے جھٹلائیں تو کہہ دے میرے لیے میرا کام اور تمہارے لیے تمہارا کام تم میرے کام کے جواب دہ نہیں اور میں تمہارے کام کا جواب دہ نہیں ہوں'),
(1406, 10, 42, 'اور ان میں بعض ایسے ہیں کہ تمہاری طرف کان لگاتے ہیں کیا تم بہروں کو سنا سکتے ہو اگرچہ وہ نہ سمجھیں'),
(1407, 10, 43, 'اور ان میں بعض ایسے ہیں کہ تمہاری طرف دیکھتے ہیں کیا تم اندھوں کو راہ دکھا دوگے اگر کچھ بھی نہ دیکھتے ہوں'),
(1408, 10, 44, 'بے شک الله لوگوں پر ذرہ ظلم نہیں کرتا لیکن لوگ ہی اپنے آپ پر ظلم کرتے ہیں'),
(1409, 10, 45, 'اور جس دن انہیں جمع کرے گا گویا وہ نہیں رہے تھے مگر ایک گھڑی دن کی ایک دوسرے کو پہچانیں گے بے شک خسارے میں رہے جنہوں نے الله کی ملاقات کو جھٹلایا اور راہ پانے والے نہ ہوئے'),
(1410, 10, 46, 'اور اگر ہم تمہیں ان وعدوں میں سے کوئی چیز دکھا دیں جو ہم نے ان سے کیے ہیں یاتمہیں وفات دیں پھر انہیں ہماری طرف لوٹنا ہے پھرالله شاہدہے ان کاموں پر جو کرتے ہیں'),
(1411, 10, 47, 'اورہر امت کا یک رسول ہے پھر جب ان کے پاس ان کا رسول آیا تو ان کے درمیان انصاف سے فیصلہ کیا گیا اور ان پر ظلم نہیں کیا جاتا'),
(1412, 10, 48, 'اور کہتے ہیں یہ وعدہ کب ہے اگر تم سچے ہو'),
(1413, 10, 49, 'کہہ دومیں اپنی ذات کے برے اور بھلے کا بھی مالک نہیں مگر جو الله چاہے ہر امت کا ایک وقت مقرر ہے جب وہ وقت آتا ہے تو ایک گھڑی بھی دیر نہیں کر سکتےہیں اور نہ جلدی کر سکتے ہیں'),
(1414, 10, 50, 'کہہ دو بھلا دیکھو تو اگر تم پر اس کا عذاب رات یا دن کو آجائے تو عذاب میں سے کون سی ایسی چیز ہے کہ مجرم اس کو جلدی مانگتے ہیں'),
(1415, 10, 51, 'کیا پھر جب وہ آ چکے گا تب اس پر ایمان لاؤ گے اب مانتے ہو اور تم اس کی جلدی کرتے تھے'),
(1416, 10, 52, 'پھر ظالموں سے کہا جائے گا ہمیشگی کا عذاب چکھتے رہو تمہیں نہیں بدلا دیا جاتا مگر اس چیز کا جو تم کرتے تھے'),
(1417, 10, 53, 'اور تم سے پوچھتے ہیں کیا یہ بات سچ ہے کہہ دو ہاں میرے رب کی قسم بے شک یہ سچ ہے اور تم عاجز کرنے والے نہیں ہو'),
(1418, 10, 54, 'اور اگر ہر ایک نافرمان کے پاس روئے زمین کی تمام چیزیں ہوں البتہ اپنے بدلے میں دے ڈالے اور جب وہ عذاب دیکھیں گے تو دل میں نادم ہوں گے اور ان کے درمیان انصاف سے فیصلہ ہوگا اور ان پر ظلم نہیں کیا جائے گا'),
(1419, 10, 55, 'خبردار بےشک الله ہی کا ہے جو کچھ آسمان اور زمین میں ہے خبردار بے شک الله کا وعدہ سچا ہے لیکن اکثر لوگ نہیں جانتے'),
(1420, 10, 56, 'وہی زندہ کرتا ہے اور مارتا ہے اور اسی کی طرف پھر کر جاؤ گے'),
(1421, 10, 57, 'اے لوگو تمہارے رب سے نصیحت اور دلوں کے روگ کی شفا تمہارے پاس آئی ہے اور ایمان داروں کے لیے ہدایت اور رحمت ہے'),
(1422, 10, 58, 'کہہ دو (قرآن) الله کے فضل اور اس کی رحمت سےہے سو اسی پر انہیں خوش ہونا چاہیئے یہ ان چیزوں سے بہتر ہے جو جمع کرتے ہیں'),
(1423, 10, 59, 'کہہ دو بھلا دیکھو تو الله نے تمہارے لیے جو رزق نازل فرمایاہے تم نے اس میں سے بعض کو حرام اوربعض کو حلال کر دیا کہہ دو الله نے تمہیں حکم دیا ہے یا الله پر افترا کرتے ہو'),
(1424, 10, 60, 'اور جو لوگ الله پر افترا کرتے ہیں قیامت کے دن کی نسبت ان کا کیا خیال ہے بے شک الله لوگوں پر مہربان ہے لیکن اکثر لوگ شکر نہیں کرتے'),
(1425, 10, 61, 'اور تم جس حال میں ہوتے ہو یا قرآن میں سے کچھ پڑھتے ہو یا تم لوگ کوئی کام کرتے ہوتو ہم وہاں موجود ہوتے ہیں جب تم اس میں مصروف ہوتے ہو اور تمہارے رب سے ذرہ بھر بھی کوئی چیز پوشدیہ نہیں ہے نہ زمین میں اور نہ آسمان میں اور نہ کوئی چیز اس سے چھوٹی اور نہ بڑی مگر کتاب روشن میں ہے'),
(1426, 10, 62, 'خبردار بے شک جو الله کے دوست ہیں نہ ان پر ڈر ہے اور نہ وہ غمگین ہوں گے'),
(1427, 10, 63, 'جو لوگ ایمان لائے اور ڈرتے رہے'),
(1428, 10, 64, 'ان کے لیے دنیا کی زندگی اور آخرت میں خوشخبری ہے الله کی باتوں میں تبدیلی نہیں ہوتی یہی بڑی کامیابی ہے'),
(1429, 10, 65, 'اور ان کی بات سے غم نہ کربے شک عزت سب الله ہی کے لیے ہے وہی سننے والا جاننے والا ہے'),
(1430, 10, 66, 'خبردار جو کوئی آسمانوں میں ہے اور جو کوئی زمین میں ہے سب الله کا ہے اور یہ جو الله کے سوا شریکوں کو پکارتے ہیں وہ نہیں پیروی کرتے مگر گمان کی اور نہیں ہیں وہ مگر اٹکل کرتے ہیں'),
(1431, 10, 67, 'وہی تو ہے جس نے تمہارے لیے رات بنائی تاکہ اسمیں آرام کرو اور دن دکھلانے والا بنایا بے شک اس میں ان لوگوں کے لیے نشانیاں ہیں جو سنتے ہیں'),
(1432, 10, 68, 'کہتے ہیں الله نے بیٹا بنا لیا وہ پاک ہے وہ بے نیاز ہے جو کچھ آسمانوں میں اور جو کچھ زمین میں ہے سب اسی کا ہے تمہارے پاس اس کی کوئی سند نہیں ہے تم الله پر ایسی باتیں کیوں کہتے ہو جو جانتے نہیں'),
(1433, 10, 69, 'کہہ دو جو لوگ الله پر افترا کرتے ہیں نجات نہیں پائیں گے'),
(1434, 10, 70, 'دنیا میں تھوڑا سا نفع اٹھا لینا ہے پھر ہماری طرف انہیں لوٹنا ہے پھر ہم انہیں سخت عذاب چکھائیں گے بسبب اس کے کہ کفر کرتے تھے'),
(1435, 10, 71, 'اور انہیں نوح کا حال سنا جب اس نے اپنی قوم سے کہا اے قوم اگر تمہیں میرا تم میں رہنا اور الله کی آیتوں سے نصیحت کرنا ناگوار ہو تو میں الله پر بھروسہ کرتا ہوں اب تم سب ملکر اپنا کام مقرر کرو اور اپنے شریکوں کو جمع کرو پھر تمہیں اپنے کام میں شبہ نہ رہے پھر وہ کام میرے ساتھ کر گزرو اور مجھے مہلت نہ دو'),
(1436, 10, 72, 'پھر اگر منہ پھیرو تو میں نے تم سے کچھ معاوضہ نہیں مانگا میرا معاوضہ الله پر ہے اور مجھے حکم دیا گیا ہے کہ فرمانبرداروں میں سے رہوں'),
(1437, 10, 73, 'پھر انہوں نے اسے جھٹلایا پھر ہم نے اسے اور اس کے ساتھیوں کو کشتی میں بچا لیا اور انہیں خلیفہ بنا دیا اور جن لوگو ں نے ہماری آیتوں کو جھٹلایا انہیں غرق کر دیا سو دیکھ لو کہ جو لوگ ڈرائے گئے تھے ان کا انجام کیسا ہوا'),
(1438, 10, 74, 'پھر ہم نے نوح کے بعد اور پیغمبر اپنی اپنی قوم کی طرف بھیجے تو وہ ان کے پاس کھلی نشانیاں لائے پھر بھی ان سے یہ نہ ہوا کہ اس بات پر ایمان لے آئيں جسے پہلے وہ جھٹلا چکے تھے اسی طرح ہم حد سے نکل جانے والوں کے دلوں پر مہر لگا دیتے ہیں'),
(1439, 10, 75, 'پھر ہم نے ان کے بعد موسیٰ اورہارون کو فرعون اور اس کےسرداروں کے پاس اپنی نشانیاں دے کر بھیجا پھر انہوں نے تکبر کیا اور وہ لوگ گنہگار تھے'),
(1440, 10, 76, 'پھر جب انہیں ہمارے ہاں سے سچی بات پہنچی کہنے لگے یہ تو کھلا جادو ہے'),
(1441, 10, 77, 'موسیٰ نے کہا کیاتم حق بات کو یہ کہتے ہو جب وہ تمہارے پاس آئی کیا یہ جادو ہے اور جادو کرنے والے نجات نہیں پاتے'),
(1442, 10, 78, 'انہوں نے کہا کیاتو ہمارے ہاں آیا ہے کہ ہمیں اس راستہ سے پھیر دے جس پر ہم نے اپنے باپ دادوں کو پایا ہے تم دونوں کو اس ملک میں سرداری مل جائے اور ہم تو تمہیں ماننے والے نہیں ہیں'),
(1443, 10, 79, 'اور فرعون نے کہا میرے پاس ہردانا جادوگر کو لے آؤ'),
(1444, 10, 80, 'پھر جب جادوگر آئے انہیں موسیٰ نے کہا ڈالو جو تم ڈالتے ہو'),
(1445, 10, 81, 'پھر جب انہوں نے ڈالا موسیٰ نے کہا جو تم لائے ہو وہ جادو ہے الله اسے ابھی درہم برہم کر دے گا بے شک الله شریروں کے کام نہیں سنوارتا'),
(1446, 10, 82, 'اور الله اپنے حکم سےحق بات کو سچا کرتا ہے اگرچہ گنہگار برا ہی مانیں'),
(1447, 10, 83, 'پھر کوئی بھی موسیٰ پر ایمان نہ لایا مگر اس کی قوم کے چند لڑکے اور وہ بھی فرعون اور ان کے سرداروں سے ڈرتے ڈرتے کہ کہیں وہ انہیں مصیبت میں نہ ڈال دے اور بے شک فرعون زمین میں سرکشی کرنے والا تھا اوربے شک وہ حد سے گزرنے والوں میں سے تھا'),
(1448, 10, 84, 'اور موسیٰ نے کہا اے میری قوم اگر تم الله پر ایمان لائے ہو تو اسی پر بھروسہ کرو اگر تم فرمانبردار ہو'),
(1449, 10, 85, 'تب وہ بولے ہم الله ہی پر بھروسہ کرتے ہیں اے رب ہمارے ہم پر اس ظالم قوم کا زور نہ آزما'),
(1450, 10, 86, 'اور ہمیں مہربانی فرما کر ان کافروں سے چھڑا دے'),
(1451, 10, 87, 'اور ہم نے موسیٰ اور اس کے بھائی کو حکم بھیجا کہ اپنی قوم کے واسطے مصر میں گھر بناؤ اوراپنے گھروں کو مسجدیں سمجھو اور نماز قائم کرو اورایمان والوں کو خوشخبری دو'),
(1452, 10, 88, 'اورموسیٰ نے کہا اے رب ہمارے تو نے فرعون اوراس کے سرداروں کو دنیا کی زندگی میں آرائش اور ہر طرح کا مال دیا ہے اے رب ہمارے یہاں تک کہ انہوں نےتیرے راستہ سے گمراہ کر دیا اے رب ہمارے ان کے مالوں کو برباد کر دے اور ان کے دلوں کو سخت کر دے پس یہ ایمان نہیں لائیں گے یہاں تک کہ دردناک عذاب دیکھیں'),
(1453, 10, 89, 'فرمایا تمہاری دعا قبول ہو چکی سو تم دونوں ثابت قدم رہو اوربے عقلوں کی راہ پر مت چلو'),
(1454, 10, 90, 'اور ہم نے بنی اسرائیل کو دریا سے پار کر دیا پھر فرعون اور اس کے لشکر نے ظلم اور زيادتی سے ان کا پیچھا کیا یہاں تک کہ جب ڈوبنےلگا کہا میں ایمان لایا کہ کوئی معبود نہیں مگر جس پر بنی اسرائیل ایمان لائے ہیں اور میں فرمانبردار میں سے ہوں'),
(1455, 10, 91, 'اب یہ کہتا ہے اورتو اس سے پہلے نافرمانی کرتا رہا اور مفسدوں میں داخل رہا'),
(1456, 10, 92, 'سو آج ہم تیرے بدن کو نکال لیں گے تاکہ تو پچھلوں کے لیے عبرت ہو اوربے شک بہت سے لوگ ہماری نشانیوں سے بے خبر ہیں'),
(1457, 10, 93, 'اور البتہ تحقیق ہم نے بنی اسرائیل کو رہنے کی عمدہ جگہ دی اور کھانے کو ستھری چیزیں دیں وہ باوجود علم ہونے کے خلاف کرتے رہے بےشک تیرا رب قیامت کے دن ان میں فیصلہ کرے گا جس بات میں کہ وہ اختلاف کرتے تھے'),
(1458, 10, 94, 'سو اگرتمہیں اس چیز میں شک ہے جو ہم نے تیری طرف اتاری تو ان سے پوچھ لے جو تجھ سے پہلے کتاب پڑھتے ہیں بے شک تیرے پاس تیرے رب سے حق بات آئی ہے سو شک کرنے والوں میں ہرگز نہ ہو'),
(1459, 10, 95, 'اوران میں سے بھی نہ ہو جنہوں نے الله کی آیتوں کو جھٹلایا پھر تو بھی نقصان اٹھانے والوں میں سے ہوگا'),
(1460, 10, 96, 'جن پر تیرے رب کی بات ثابت ہو چکی ہے وہ ایمان نہیں لائیں گے'),
(1461, 10, 97, 'اگرچہ انہیں ساری نشانیاں پہنچ جائیں جب تک کہ دردناک عذاب نہ دیکھ لیں'),
(1462, 10, 98, 'سو کوئی بستی ایسی کیوں نہ ہوئی جو ایمان لاتی تو اس کا ایمان اسے نفع دیتا سوائے یونس کی قوم کے کہ جب وہ ایمان لائے تو ہم نے دنیا کی زندگی میں ان سے ذلت کا عذاب دور کر دیا اور ہم نے انہیں ایک وقت تک فائدہ پہنچایا'),
(1463, 10, 99, 'اور اگر تیرا رب چاہتا تو جتنے لوگ زمین میں ہیں سب کے سب ایمان لے آتے پھر کیا تو لوگوں پر زبردستی کرے گا کہ وہ ایمان لے آئيں'),
(1464, 10, 100, 'اورکسی کے بھی بس میں نہیں کہ الله کے حکم کے سوا ایمان لے آئے اور الله انکے لیے کفر کا فیصلہ کرتا ہے جو نہیں سوچتے'),
(1465, 10, 101, 'کہہ دو دیکھوکہ آسمانوں اور زمین میں کیا کچھ ہے اور بے ایمان قوم کو معجزے اور ڈرانے والے کچھ فائدہ نہیں دیتے'),
(1466, 10, 102, 'پھر کیا وہ انہیں لوگوں کے دنوں کا سا انتظار کرتے ہیں جو ان سے پہلے گزرے ہیں کہہ دو اچھا انتظار کرو میں بھی تمہارے ساتھ انتظار کرتا ہوں'),
(1467, 10, 103, 'پھر ہم اپنے رسولوں اوران لوگوں کو جو ایمان لاتے ہیں بچا لیتے ہیں اسی طرح ہمارا ذمہ ہے کہ ایمان والوں کوبچا لیں'),
(1468, 10, 104, 'کہہ دو اے لوگو اگر تمہیں میرے دین میں شک ہے تو الله کے سوا جن کی تم عبادت کرتے ہو میں ان کی عبادت نہیں کرتا بلکہ میں الله کی عبادت کرتا ہوں جو تمہیں وفات دیتا ہے اور مجھے حکم ہوا ہے کہ ایمانداروں میں رہوں'),
(1469, 10, 105, 'اور یہ بھی کہ یک سوہوکر دین کی طرف رخ کیے رہو اور مشرکوں میں نہ ہو'),
(1470, 10, 106, 'اور الله کے سوا ایسی چیز کونہ پکار جو نہ تیرا بھلا کرے اور نہ برا پھر اگرتو نے ایسا کیا تو بے شک ظالموں میں سے ہو جائے گا'),
(1471, 10, 107, 'اوراگر الله تمہیں کوئی تکلیف پہنچائے تو اس کے سوا اسے ہٹانے والا کوئی نہیں اور اگر تمہیں کوئی بھلائی پہنچانا چاہے توکوئی اس کے فضل کو پھیرنے والا نہیں اپنے بندوں میں سے جسے چاہتا ہے اپنا فضل پہنچاتا ہے اور وہی بحشنے والا مہربان ہے'),
(1472, 10, 108, 'کہہ دو اے لوگو تمہیں تمہارے رب سے حق پہنچ چکا ہے پس جو کوئی راہ پر آئے سو وہ اپنے بھلے کے لیے راہ پاتا ہے اور جو گمراہ رہے گا اس کا وبال اسی پر پڑے گا اور میں تمہارا ذمہ دارنہیں ہوں'),
(1473, 10, 109, 'اورجو کچھ تیری طرف وحی کیا گیا ہے ا س پر چل اور صبر کر یہاں تک کہ الله فیصلہ کر دے اور وہ بہتر فیصلہ کرنے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'یہ ایسی کتاب ہےکہ جس کی آیتیں حکیم خبردار کی طرف سے مستحکم کر دی گئی ہیں پھر مفصل بیان کی گئی ہیں'),
(1475, 11, 2, 'یہ کہ الله کے سوا کسی کی عبادت نہ کرو میں تمہارے لیے اس کی طرف سے ڈرانے والا اور خوشخبری دینے والا ہوں'),
(1476, 11, 3, 'اور یہ کہ تم اپنے رب سے معافی مانگو پھراس کی طرف رجوع کرو تاکہ تمہیں ایک وقت مقرر تک اچھا فائدپہنچائے اورجس نے بڑھ کر نیکی کی ہو اس کو بڑھ کر بدلہ دے اور اگر تم پھر جاؤ گے تو میں تم پر ایک بڑے دن کے عذاب سے ڈرتا ہوں'),
(1477, 11, 4, 'تمہیں الله کی طرف لوٹ کر جانا ہے اوروہ ہر چیز پر قادر ہے'),
(1478, 11, 5, 'خبردار جس وقت وہ کپڑے ڈھانکتے ہیں وہ جانتا ہے جو کچھ وہ چھپا کر اور ظاہر کر کے کرتے ہیں بےشک وہ دلوں کی باتوں کو جاننے والا ہے'),
(1479, 11, 6, 'ارو زمین پر کوئی چلنے والا نہیں مگر اس کی روزی الله پر ہے اور جانتا ہے جہاں وہ ٹھیرتا ہے اور جہاں وہ سونپا جاتا ہے سب کچھ واضح کتاب میں ہے'),
(1480, 11, 7, 'اور وہی ہےجس نے آسمان اور زمین چھ دن میں بنائے اور اس کا تخت پانی پر تھا کہ تمہیں آزمائے کہ تم میں سے کون اچھا کام کرتا ہے اور اگر تو کہے کہ مرنے کے بعد اٹھو گے تو منکرین یہ کہیں گے کہ یہ تو صریح جادو ہے'),
(1481, 11, 8, 'اور اگر ہم ایک مدت معلوم تک ان سے عذاب کو روک رکھیں توضرور کہیں گے کس چیر نے عذاب کو روک دیا خبردار جس دن ان پر عذاب آئے گا ان سے نہ پھیرا جائے گا اور انہیں وہ چیز گھیرے گی جس پر ٹھٹایا کیاکرتے تھے'),
(1482, 11, 9, 'اور اگر ہم انسان کو اپنی رحمت کا مزہ چکھا کر پھر اس سے چھین لیتے ہیں تو وہ ناامید ناشکرا ہو جاتا ہے'),
(1483, 11, 10, 'اور اگر مصیبت پہنچنے کے بعد نعمتوں کا مزہ چکھاتے ہیں تو کہتا ہے کہ میری سختیاں جاتی رہیں کیوں کہ وہ اترانے والا شیخی خورا ہے'),
(1484, 11, 11, 'مگر جو لوگ صابر ہیں اور نیکیاں کرتے ہیں ان کے لیے بخشش اوربڑا ثواب ہے'),
(1485, 11, 12, 'پھر شاید آپ اس میں سے کچھ چھوڑ بیٹھیں گے جو آپ کی طرف وحی کیا گیا ہے اور ان کے اس کہنے سے آپ کا دل تنگ ہوگا کہ اس پر کوئی خزانہ کیوں نہ اتر آیا یا اس کے ساتھ کوئی فرشتہ کیوں نہ آیا آپ تو محض ڈرانے والے ہیں اور الله ہر چیز کا ذمہ دار ہے'),
(1486, 11, 13, 'کیا کہتے ہیں کہ تو نے قرآن خود بنا لیا ہے کہہ دو تم بھی ایسی دس سورتیں بنا لاؤ اور الله کے سوا جس کو بلا سکو بلا لو اگر تم سچے ہو'),
(1487, 11, 14, 'پھر اگر تمہارا کہنا پورا نہ کریں تو جان لو کہ قرآن الله کے علم سے نازل کیا گیا ہے اور یہ بھی کہ اس کے سوا کوئی معبود نہیں پس کیا تم فرمانبرداری کرنے والے ہو'),
(1488, 11, 15, 'جو کوئی دنیا کی زندگی اور اس کی آرائش چاہتا ہے تو انکے اعمال ہم یہیں پورے کر دیتے ہیں اور انہیں کچھ نقصان نہیں دیا جاتا'),
(1489, 11, 16, 'یہ وہی ہیں جن کیلئے آخرت میں آگ کے سوا کچھ نہیں اور برباد ہوگیا جو کچھ انہوں نے دنیا میں کیا تھا اور خراب ہو گیا جو کچھ کمایا تھا'),
(1490, 11, 17, 'بھلا وہ شخص جو اپنے رب کے صاف راستہ پر ہو اور اس کے ساتھ الله کی طرف سے ایک گواہ بھی ہو اور اس سے پہلے موسیٰ کی کتاب گواہ تھی جو امام اور رحمت تھی یہی لوگ قرآن کو مانتے ہیں اور جو کوئی سب فرقوں میں سے اس کا منکر ہوتو اس کا ٹھکانا دوزخ ہے سو تو قرآ ن کی طرف سے شبہ میں نہ رہ بے شک یہ تیرے رب کی طرف سے حق ہے لیکن اکثر لوگ ایمان نہیں لاتے'),
(1491, 11, 18, 'اور اس سے بڑھ کر ظالم کون ہوگا جو الله پر جھوٹ باندھے وہ لوگ اپنے رب کے روبر پیش کیے جائیں گے اور گواہ کہیں گے کہ یہی ہیں کہ جنہوں نے اپنے رب پر جھوٹ بولا تھا خبرادر ظالموں پر الله کی لعنت ہے'),
(1492, 11, 19, 'جو الله کی راہ سے روکتے ہیں اور اس میں کجی پیدا کرنا چاہتے ہیں اوروہی آخرت کے منکر ہیں'),
(1493, 11, 20, 'یہ لوگ زمین میں عاجز کرنے والے نہیں تھے اور ان کے لیے سوا الله کے کوئی دوست نہ تھا انہیں دگنا عذاب کیا جائے گا یہ لوگ نہ سن سکتے تھے اور نہ دیکھتے تھے'),
(1494, 11, 21, 'انہوں نے اپنے آپ کو خسارہ میں ڈال دیا اور ان سے ضائع ہوگیا جو جھوٹ وہ باندھتے تھے'),
(1495, 11, 22, 'بے شک یہی لوگ آخرت میں سب سے زیادہ نقصان میں ہوں گے'),
(1496, 11, 23, 'البتہ جولوگ ایمان لائے اورنیک کام کیے اور اپنے رب کے سامنے عاجزی کی وہ جنت میں رہنے والے ہیں وہ اس میں ہمیشہ رہیں گے'),
(1497, 11, 24, 'دونوں فریق کی مثال ایسی ہے جیسے ایک اندھا اوربہرا ہو اور دوسرا دیکھنے والا اور سننے والاکیا دونوں کا حال برابر ہے پھر تم کیوں نہیں سمجھتے'),
(1498, 11, 25, 'اور ہم نے نوح کو اس کی قوم کی طرف بھیجا بے شک میں تمہیں صاف ڈرانے والا ہوں'),
(1499, 11, 26, 'کہ الله کے سوا کسی کی عبادت نہ کرو بے شک میں تم پر دردناک دن کے عذاب سے ڈرتا ہوں'),
(1500, 11, 27, 'پھر اس کی قوم کے جو کافر سردار تھے وہ بولے ہمیں تو تم ہم جیسے ہی ایک آدمی نظر آتے ہو اور ہمیں تو تمہارے پیرو وہی نظر آتے ہیں جو ہم میں سے رذیل ہیں وہ بھی سرسری نظر سے اورہم تم میں اپنے سے کوئی فضیلت بھی نہیں پاتے بلکہ تمہیں جھوٹا خیال کرتے ہیں'),
(1501, 11, 28, 'اس نے کہا اے میری قوم دیکھو تو اگر میں اپنے رب کی طرف سے صاف راستہ پر ہوں اور اس نے مجھ پر اپنے ہاں سے رحمت بھیجی ہو پھر وہ تمہیں دکھائی نہ دے تو کیا میں زبردستی تمہارے گلے مڑھ دوں اور تم اس سے نفرت کرتے ہو'),
(1502, 11, 29, 'اور اے میری قوم میں تم سے اس پر کچھ مال نہیں مانگتا میری مزدوری الله ہی کے ذمہ ہے اور میں ایمانداروں کو ہٹانے والا نہیں بے شک و ہ اپنے رب سے ملنے والے ہیں لیکن تمہیں جاہل قوم دیکھتا ہوں'),
(1503, 11, 30, 'اور اے میری قوم اگر میں انہیں ہٹا دوں تو مجھے الله سے کون چھڑائے گا کیا پھر تم نہیں سمجھتے'),
(1504, 11, 31, 'اور میں تمہیں نہیں کہتا کہ میرے پاس الله کے خزانے ہیں اور نہ یہ کہ میں غیب دان ہوں اور نہ یہ کہتا ہوں کہ میں فرشتہ ہوں اور نہ یہ کہوں گاکہ جو لوگ تمہاری نظر میں حقیر ہیں الله ان کو بھلائی نہ دے گا الله خوب جانتا ہے جو کچھ ان کے دلوں میں ہے ایسا کہوں تو میں بے انصاف ہوں'),
(1505, 11, 32, 'کہا اے نوح تو نے ہم سے جھگڑا کیا او ربہت جھگڑا کر چکا اب لے آ جو تو ہم سے وعدہ کرتا ہے اگر تو سچا ہے'),
(1506, 11, 33, 'نوح نے کہا اسے تو اگرچاہے گا الله ہی لائے گا اور تم اسے روک نہ سکو گے'),
(1507, 11, 34, 'اور میری نصیحت تمہیں فائدہ نہ دے گی خواہ میں کتنی ہی نصیحت کرنا چاہوں اگر الله کو تمہیں گمراہ رکھنا ہی منظور ہے وہی تمہارا رب ہے اور اسی کی طرف تمہیں پھر جانا ہے'),
(1508, 11, 35, 'کیا کہتے ہیں کہ قرآن کو خود بنا لایا ہے کہہ دو اگر میں بنا لایا ہوں تو اس کا گناہ مجھ پر ہے اور میں تمہارے گناہوں سے بری ہوں'),
(1509, 11, 36, 'اور نوح کی طرف وحی کی گئی کہ تیری قوم میں سے اب کوئی ایمان نہیں لائے گا مگر جو لا چکا پھر غم نہ کر اور کاموں پر جو کر رہے ہیں'),
(1510, 11, 37, 'اور ہمارے روبرو اور ہمارے حکم سے کشتی بنا اور ظالمو ں کے حق میں مجھ سے کوئی بات نہ کر بے شک وہ غرق کیے جائیں گے'),
(1511, 11, 38, 'اور وہ کشتی بناتے تھے اور جب اس کی قوم کے سردار اس پر گزرتے اس سے ہنسی کرتے کہتے اگر تم ہم پر ہنستے ہو تو ہم بھی تم پر ہنسیں گے جیسے تم ہنستے ہو'),
(1512, 11, 39, 'تمہیں جلدی معلوم ہو جائے گا کہ کس پر عذاب آتا ہے جو اسے رسوا کرے گا اورکسی پر دائمی عذاب اترتا ہے'),
(1513, 11, 40, 'یہاں تک کہ جب ہمارا حکم پہنچا اور تنور نے جوش مارا ہم نے کہا کشتی میں ہر قسم کے جوڑا نر مادہ چڑھا لے اور اپنے گھر والوں کو مگر وہ جن کے متعلق فیصلہ ہو چکا ہے اور سب ایمان والوں کو اور اس کے ساتھ ایمان تو بہت کم لائے تھے'),
(1514, 11, 41, 'اور کہا اس میں سوار ہو جاؤ اس کا چلنا اور ٹھیرنا الله کے نا م سے ہےبے شک میرا رب بخشنے والا مہربان ہے'),
(1515, 11, 42, 'اور وہ انہیں پہاڑ جیسی لہروں میں لیے جارہی تھی اورنوح نے اپنے بیٹے کو پکارا جب کہ وہ کنارے پر تھا اے بیٹے ہمارے ساتھ سوار ہو جا اور کافروں کے ساتھ نہ رہ'),
(1516, 11, 43, 'کہا میں ابھی کسی پہاڑ کی پناہ لے لیتا ہوں جو مجھے پانی سے بچا لے گا کہا آج الله کے حکم سے کوئی بچانے والا نہیں مگر جس پر وہی رحم کرے اور دونوں کے درمیان موج حائل ہو گئی پھر ڈوبنے والوں میں ہوگیا'),
(1517, 11, 44, 'اور حکم آیا اے زمین اپنا پانی نگل جا اور اے آسمان تھم جا اور پانی سکھا دیا گیا اور کام ہو چکا اور کشتی جودی پہاڑ پر ٹھری اور کہہ دیا گیا کہ ظالموں پر پھٹکار ہے'),
(1518, 11, 45, 'اور نوح نے اپنے رب کو پکارا اے رب میرا بیٹا میرے گھر والو ں میں سے ہے اوربے شک تیار وعدہ سچا ہے اور تو سب سے بڑا حاکم ہے'),
(1519, 11, 46, 'فرمایا اے نوح وہ تیرے گھر والو ں میں سے نہیں ہے کیوں کہ اس کے عمل اچھے نہیں ہیں سو مجھ سے مت پوچھ جس کا تجھے علم نہیں میں تمہیں نصیحت کرتا ہوں کہ کہیں جاہلوں میں نہ ہو جاؤ'),
(1520, 11, 47, 'کہا اے رب میں تیری پناہ لیتا ہوں اس بات سے کہ تجھ سے وہ بات پوچھوں جو مجھے معلوم نہیں اور اگر تو نے مجھے نہ بخشا اورمجھ پر رحم نہ کیا تو میں نقصان والوں میں ہو جاؤں گا'),
(1521, 11, 48, 'کہا گیا اے نوح ہماری طرف سے سلامتی اور برکتوں کے ساتھ جو تم پر اور تمہارے ساتھ والوں پر رہیں گی کشتی سے اتر اور دوسرے فرقے ہیں کہ ہم انھیں دنیا میں فائدہ دیں گے پھر انہیں ہماری طرف سے دردناک عذاب پہنچے گا'),
(1522, 11, 49, 'یہ غیب کی خبریں ہیں جنہیں ہم آپ کی طرف وحی کر رہے ہیں اس سے پہلے نہ تو آپ ہی جانتے تھے اور نہ آپ کی قوم جانتی تھی پس صبر کر کیوں کہ بہتر انجام پرہیزگاروں کے لیے ہے'),
(1523, 11, 50, 'اور ہم نے عاد کی طرف ان کے بھائی ہود کو بھیجا اے قوم الله کی بندگی کرو اس کے سوا تمہارا کوئی حاکم نہیں تم سب جھوٹ کہتے ہو'),
(1524, 11, 51, 'اے قوم میں اس پر تم سے مزدوری نہیں مانگتا میری مزدوری اسی پر ہے جس نے مجھے پیدا کیا پھر کیا تم نہیں سمجھتے'),
(1525, 11, 52, 'اور اے قوم اپنے رب سے معافی مانگو پھر اس کی طرف رجوع کرو وہ تم پر خوب بارشیں برسائے گا اور تمہاری قوت کو اوربڑھائے گا اورتم نافرمان ہو کر نہ پھر جاؤ'),
(1526, 11, 53, 'کہا اے ہود تو ہمارے پاس کوئی معجزہ بھی نہ لایا اور ہم تیرے کہنے سے اپنے معبودوں کو چھوڑنے والے نہیں اورنہ ہم تجھے ماننے والے ہیں'),
(1527, 11, 54, 'ہم تو یہی کہتے ہیں کہ تجھےہمارے کسی معبود نے بری طرح جھپٹ لیا ہے کہا بے شک میں الله کو گواہ کرتا ہوں اورتم بھی گواہ رہو کہ میں ان چیزوں سے بیزار ہوں جنہیں تم الله کے سوا شریک کرتے ہو'),
(1528, 11, 55, 'سوتم سب مل کر میرے حق میں برائی کرو پھر مجھے مہلت نہ دو'),
(1529, 11, 56, 'میں نے الله پر بھروسہ کیا ہے جو میرا اور تمہارا رب ہے کوئی بھی زمین پر ایسا چلنے والا نہیں کہ جس کی چوٹی اس نے نہ پکڑ رکھی ہو بے شک میرا رب سیدھے راستے پر ہے'),
(1530, 11, 57, 'پھر اگر تم منہ پھیرو گے تو جو مجھے دے کر بھیجا گیا تھا وہ تمہیں پہنچا دیا اور میرا رب تمہاری جگہ اور قوم پیدا کر دے گا اور تم اس کا کچھ بھی بگاڑنہیں سکو گے بے شک میرا رب ہر چیزپر نگہبان ہے'),
(1531, 11, 58, 'اور جب ہمارا حکم پہنچا تو ہم نے ہود کو اورانہیں جو اس کے ساتھ ایمان لائے تھے اپنی رحمت سے بچا لیا اور ہم نے انہیں سخت عذاب سے نجات دی'),
(1532, 11, 59, 'اور یہ عاد تھے کہ اپنے رب کی باتوں سے منکر ہوئے اوراس کے رسولوں کو نہ مانا اور ہر ایک جبار سرکش کا حکم مانتے تھے'),
(1533, 11, 60, 'اور اس دنیا میں بھی اپنے پیچھے لعنت چھوڑ گئے اور قیامت کے دن بھی خبردار بےشک عاد نے اپنے رب کا انکار کیا تھا خبردار عاد جو ہود کی قوم تھی الله کی رحمت سے دور کی گئی'),
(1534, 11, 61, 'اور ثمود کی طرف ان کے بھائی صالح کو بھیجا اے میری قوم الله کی بندگی کرو اس کے سوا تمہارا کوئی معبود نہیں اسی نے تمہیں زمین سے بنایا اورتمہیں اس میں آباد کیا پس اس سے معافی مانگو پھر اس کی طرف رجوع کرو بے شک میرا رب نزدیک ہے قبول کرنے والا'),
(1535, 11, 62, 'انہوں نے کہا اے صالح اس سے پہلے تو ہمیں تجھ سے بڑی امید تھی تم ہمیں ان معبودوں کے پوجنے سےمنع کرتے ہو جنہیں ہمارے باپ دادا پوجتے چلے آئے ہیں اورجس طرف تم ہمیں بلاتے ہو اس سے تو ہم بڑے شک میں ہیں'),
(1536, 11, 63, 'صالح نے کہا اےمیری قوم بھلا دیکھو تو اگر میں اپنے رب کی طرف سے کوئی کھلی دلیل رکھتا ہوں اور اس کی طرف سے میرے پاس رحمت بھی آ چکی ہو پھر اگر میں اس کی نافرمانی کروں تو مجھے اس سے کون بچا سکتا ہے پھر تم مجھے نقصان کے سوا اور کیا دے سکو گے'),
(1537, 11, 64, 'اور اےمیری قوم یہ الله کی اونٹنی تمہارے لیے نشانی ہے سواسے چھوڑ دو الله کی زمین میں کھاتی پھرے اور اسے برائی سے چھونا بھی نہیں (ورنہ) پھر تمہیں عذاب بہت جلد آ پکڑے گا'),
(1538, 11, 65, 'پھر انہوں نے اس کے پاؤں کاٹ ڈالے تب صالح نے کہا تین دن تک اپنے گھروں میں فائدہ اٹھا لو یہ وعدہ ہے جو جھوٹا نہ ہوگا'),
(1539, 11, 66, 'پھر جب ہمارا حکم آ پہنچا تو ہم نے صالح کو اورجو اس کے ساتھ ایمان لائے تھے اپنی رحمت سے بچا لیا اور اس دن کی رسوائی سے نجات دی بے شک تیرا رب وہی زور والا زبردست ہے'),
(1540, 11, 67, 'اوران ظالموں کو ہولناک آواز نے پکڑ لیا پھر صبح کو اپنے گھروں میں اوندھے پڑے ہوئے رہ گئے'),
(1541, 11, 68, 'گویا کہ کبھی وہاں رہے ہی نہ تھے خبردار ثمود نے اپنے رب کا انکار کیا تھا خبردار ثمود پر پھٹکار ہے'),
(1542, 11, 69, 'اور ہمارے بھیجے ہوئے ابراھیم کے پاس خوشخبری لے کر آئے انہوں نے کہا سلام اس نے کہا سلام پس دیر نہ کی کہ ایک بھنا ہوا بچھڑا لے آیا'),
(1543, 11, 70, 'پھر جب دیکھا کہ ان کے ہاتھ اس تک نہیں پہنچتے تو انہیں اجنبی سمجھا اوران سے ڈرا انہوں نے کہا خوف نہ کرو ہم تو لوط کی قوم کی طرف بھیجحے گئے ہیں'),
(1544, 11, 71, 'اور اس کی عورت کھڑی تھی تب وہ ہنس پڑی پھر ہم نے اسے اسحاق کے پیدا ہونے کی خوشخبری دی اور اسحاق کے بعد یعقوب کی'),
(1545, 11, 72, 'وہ بولی اے افسوس کیا میں بوڑھی ہو کر جنوں گی میرا خاوند بھی بوڑھا ہے یہ تو ایک عجیب بات ہے'),
(1546, 11, 73, 'انہوں نے کہا کیاتو الله کے حکم سے تعجب کرتی ہے تم پر اے گھر والو الله کی رحمت اور اس کی برکتیں ہیں بے شک وہ تعریف کیا ہوا بزرگ ہے'),
(1547, 11, 74, 'جب ابراھیم سے ڈر جاتا رہا اور اسے خوشخبری آئی ہم سے قوم لوط کے حق میں جھگڑنے لگا'),
(1548, 11, 75, 'بے شک ابراھیم بردبار نرم دل اور الله کی طرف رجوع کرنے والا تھا'),
(1549, 11, 76, 'اے ابراھیم یہ خیال چھوڑ دے کیوں کہ تیرے رب کا حکم آ چکا ہے اور بے شک ان پرعذاب آ کر ہی رہے گا جو ٹلنے والا نہیں'),
(1550, 11, 77, 'اور جب ہمارے بھیجے ہوئے لوط کے پاس پہنچے تو ان کے آنے سے غمگین ہوا اور دل میں تنگ ہوا اور کہا آج کا دن بڑا سخت ہے'),
(1551, 11, 78, 'اور اس کے پاس اس کی قوم بے اختیار دوڑتی آئی اور یہ لوگ پہلے ہی سے برے کام کیا کرتے تھے کہا اےمیری قوم یہ میری بیٹیاں ہیں یہ تمہارے لیے پاک ہیں سو تم الله سے ڈرو اور میرے مہمانو ں میں مجھے ذلیل نہ کرو کیا تم میں کوئی بھی بھلا آدمی نہیں'),
(1552, 11, 79, 'انہوں نے کہا البتہ تحقیق تو جانتا ہے کہ ہمیں تیری بیٹیوں سے کوئی غرض نہیں اور تجھے معلوم ہے جو ہم چاہتے ہیں'),
(1553, 11, 80, 'کہا کاش کہ مجھے تمہارے مقابلے کی طاقت ہو تی یا میں کسی زبردست سہارے کی پناہ جا لیتا'),
(1554, 11, 81, 'فرشتوں نے کہا اے لوط بے شک ہم تیرے رب کے بھیجے ہوئے ہیں یہ تم تک ہرگز نہ پہنچ سکیں گے سو کچھ حصہ رات رہے اپنے لوگوں کو لے نکل اور تم میں سے کوئی مڑ کر نہ دیکھے مگر تیری عورت کہ اس پر بھی وہی بلا آنے والی ہے جو ان پر آئے گی ان کے وعدہ کا وقت صبح ہے کیا صبح کا وقت نزدیک نہیں ہے'),
(1555, 11, 82, 'پھر جب ہمارا حکم پہنچا تو ہم نے وہ بستیاں الٹ دیں اور اس زمین پر کھنگر کے پتھر برسانا شروع کیے جو لگاتار گر رہے تھے'),
(1556, 11, 83, 'جن پر تیرے رب کے ہاں سے خاص نشان بھی تھا اور یہ بستیاں ان ظالموں سے کچھ دور نہیں ہیں'),
(1557, 11, 84, 'اورمدین کی طرف ان کے بھائی شعیب کو بھیجا کہ اے میری قوم الله کی بندگی کرو اس کے سوا تمہارا کوئی معبود نہیں اور ناپ اور تول کو نہ گھٹاؤ میں تمہیں آسودہ حال دیکھتا ہوں اور تم پر ایک گھیر لینے والے دن کے عذاب سے ڈرتا ہوں'),
(1558, 11, 85, 'اور اے میری قوم انصاف سے ناپ اور تول کو پورا کرو اور لوگو ں کو ان کی چیزیں گھٹا کر نہ دو اور زمین میں فساد نہ مچاؤ'),
(1559, 11, 86, 'الله کا دیا جو باقی بچ رہے وہ تمہارے لیے بہتر ہے اگر تم ایماندار ہو اور میں تمہارا نگہبان نہیں ہوں'),
(1560, 11, 87, 'انہوں نے کہا اے شعیب کیا تیری نماز تجھے یہی حکم دیتی ہے کہ ہم ان چیزوں کو چھوڑ دیں جنہیں ہمارے باپ باپ دادا پوجتے تھے یا اپنے مالوں میں اپنی خواہش کے مطابق معاملہ نہ کریں بے شک تو البتہ بردبار نیک چلن ہے'),
(1561, 11, 88, 'کہا اے میری قوم دیکھو تو سہی اگر مجھے اپنے رب کی طرف سے سمجھ آ گئی ہے اور اس نے مجھے عمدہ روزی دی ہے اور میں یہ نہیں چاہتا کہ جس کام سے تجھے منع کروں میں اس کے خلاف کروں میں تو اپنی طاقت کے مطابق اصلاح ہی چاہتا ہوں اور مجھے تو صرف الله ہی سے توفیق حاصل ہوتی ہے میں اسی پر بھروسہ کرتا ہوں اور اسی کی طرف رجوع کرتا ہوں'),
(1562, 11, 89, 'اوراے میری قوم کہیں میری ضد سے ایسا جرم نہ کر بیٹھنا جس سے وہی مصیبت نہ آ پڑے جیسی کہ قوم نوح یا قوم ہود یا قوم صالح پر پڑی تھی اور لوط کی قوم بھی تم سے دور نہیں'),
(1563, 11, 90, 'اور اپنے الله سے معافی مانگو پھر اس کی طرف رجوع کرو بے شک میرا رب مہربان محبت والا ہے'),
(1564, 11, 91, 'انہوں نے کہا اے شعیب ہم بہت سی باتیں نہیں سمجھتے جو تم کہتے ہو اوربے شک ہم البتہ تمہیں اپنےمیں کمزور پاتے ہیں اور اگر تیری برادری نہ ہوتی تو تجھے ہم سنگسارکردیتے اور ہماری نظر میں تیری کوئی عزت نہیں ہے'),
(1565, 11, 92, 'کہا اے میری قوم کیا میری برادری کا دباؤ تم پر الله سے زیادہ ہے اس کو تم نے پس پشت ڈال دیا ہے بے شک میرا رب تمہارے سب اعمال پر احاطہ کرنے والا ہے'),
(1566, 11, 93, 'اور اے میری قوم اپنی جگہ پر کام کیے جاؤ میں تم بھی کام کرتا ہوں آئندہ معلوم کر لو گے کس پر رسوا کرنے والا عذاب آتا ہے اور جھوٹا کون ہے اورانتظار کرو بے شک میں بھی تمہارے ساتھ انتظار کر رہا ہوں'),
(1567, 11, 94, 'اور جب ہمارا حکم آ گیا تو ہم نے شعیب کو اور ان لوگوں کو جو ان کے ساتھ ایمان لائے تھے اپنی رحمت سے بچا لیا اوران ظالموں کو کڑک نے آ پکڑا پھر صبح کو اپنے گھروں میں اوندھے پڑے ہوئے رہ گئے'),
(1568, 11, 95, 'گویا کبھی وہاں بسے ہی نہ تھے خبردار مدین پر پھٹکار ہے جیسے ثمود پر پھٹکار ہوئی تھی'),
(1569, 11, 96, 'اور البتہ تحقیق ہم نے موسیٰ کو اپنی نشانیاں اور واضع سند دے کر بھیجا'),
(1570, 11, 97, 'فرعون اور اس کے سرداروں کے ہاں پھر وہ فرعون کے حکم پر چلے اور فرعون کا حکم ٹھیک بھی نہ تھا'),
(1571, 11, 98, 'قیامت کے دن اپنی قوم کے آگے ہو گا پھر انہیں آگ میں لاڈالے گا اوربرا گھاٹ ہے جس پر وہ پہنچے'),
(1572, 11, 99, 'اور اپنے پیچھے اس جہان میں بھی لعنت چھوڑ گئے اور قیامت کے دن کے لیے بھی برا ہی انعام ہے جو انہیں دیا جائے گا'),
(1573, 11, 100, 'یہ بستیوں کے تھوڑے سے حالات ہیں کہ تجھے سنا رہے ہیں ان میں سے کچھ تو اب تک باقی ہیں اورکچھ اجڑی پڑی ہیں'),
(1574, 11, 101, 'اور ہم نے ان پر ظلم نہیں کیا لیکن وہی اپنی جانو ں پر ظلم کر گئے پھر ان کے وہ معبود کچھ کام نہ آئے جنہیں و ہ الله کے سوا پکارتے تھے جس وقت تیرے رب کا حکم آ پہنچا اور ان معبودوں نے سوا ہلاکت کے انہیں کچھ بھی فائدہ نہ دیا'),
(1575, 11, 102, 'اور تیرے رب کی پکڑ ایسی ہی ہوتی ہے جب وہ ظالم بستیوں کو پکڑتا ہے اور اس کی پکڑ سخت تکلیف دہ ہے'),
(1576, 11, 103, 'اس بات میں نشانی ہے اس کے لیے جو آخرت کے عذاب سے ڈرتا ہے یہ ایک ایسا دن ہوگا جس میں سب لوگ جمع ہوں گے اور یہی دن ہے جس میں سب حاضر کیے جائیں گے'),
(1577, 11, 104, 'اور ہم اسے تھوڑی مدت کے لیے ملتوی کیے ہوئے ہیں'),
(1578, 11, 105, 'جب وہ دن آئے گا تو کوئی شخص الله کی اجازت کے سوا بات بھی نہ کر سکے گا سو ان میں سے بعض بدبخت ہیں اوربعض نیک بخت'),
(1579, 11, 106, 'پھر جو بد ہوں گےتو وہ آگ میں ہوں گے کہ اس میں ان کی چیخ و پکار پڑی رہے گی'),
(1580, 11, 107, 'اس میں ہمیشہ رہیں گے جب تک آسمان زمین قائم ہیں ہاں اگر تیرے الله ہی کو منظور ہو (تو دوسری بات ہے) بے شک تیار رب جو چاہے اسے پورےطور سے کر سکتا ہے'),
(1581, 11, 108, 'اور جو لوگ نیک بخت ہیں سو جنت میں ہو ں گے اس میں ہمیشہ رہیں گے جب تک آسمان زمین قائم ہیں ہاں اگر تیرے الله ہی کو منظور ہو تو (دوسری بات ہے) یہ بے انتہا عطیہ ہو گا'),
(1582, 11, 109, 'سو تو ان چیزوں سے شک میں نہ رہ جنہیں یہ پوجتے ہیں یہ لوگ کچھ نہیں پوجتے مگر اسی طرح سے کہ جس طرح ان سے پہلے ان کے باپ دادا پوجتے تھے اور بے شک ہم انہیں عذاب کا پورا حصہ دے کر رہیں گے'),
(1583, 11, 110, 'اور البتہ ہم نے موسیٰ کو کتاب دی تھی پھراس میں اختلاف کیا گیا اور اگر تیرے رب کی طرف سے ایک بات مقرر نہ ہو چکی ہوتی تو ان میں فیصلہ ہو جاتا اور بے شک اس کی طرف سے ایسے شک میں ہیں کہ مطمئن نہیں ہو نے دیتا'),
(1584, 11, 111, 'اورجتنے لوگ ہیں جب وقت آیا تیرا رب انہیں ان کے اعمال پورے دے گا بےشک وہ خبردار ہے اس چیز سے جو کر رہے ہیں'),
(1585, 11, 112, 'سو تو پکا رجیسا تجھے حکم دیا گیا ہے اور جنہوں نے تیرے ساتھ توبہ کی ہے اور حد سے نہ بڑھو بے شک وہ دیکھتا ہے جو کچھ تم کرتے ہو'),
(1586, 11, 113, 'اوران کی طرف مت جھکو جو ظالم ہیں پھر تمہیں بھی آگ چھوئے گی اور الله کے سوا تمہارا کوئی مددگار نہیں ہے پھر کہیں سے مدد نہ پاؤ گے'),
(1587, 11, 114, 'اور دن کے دونو ں طرف اورکچھ حصہ رات کا نماز قائم کر بے شک نیکیاں برائیوں کو دور کرتی ہیں یہ نصیحت حاصل کرنے والوں کے لیے نصیحت ہے'),
(1588, 11, 115, 'اور صبر کر بے شک الله نیکی کرنے والوں کا اجر ضائع نہیں کرتا'),
(1589, 11, 116, 'سو ان جماعتوں میں ایسے لوگ کیوں نہ ہوئے جو تم سے پہلے تھیں جو ملک میں فساد پھیلانے سے منع کرتے بجز چند آدمیوں کے جنہیں ہم نے ان میں سے بچا لیا تھا اور جن لوگو ں نے نافرمانی کی تھی وہ تو انہیں لذتوں کے پیچھے پڑے رہے جو ان کو دی گئی تھیں اور وہ مجرم تھے'),
(1590, 11, 117, 'اور تیرا رب ہرگز ایسا نہیں جو بستیوں کو زبردستی ہلاک کر دے اور وہاں کے لوگ نیک ہوں'),
(1591, 11, 118, 'اور اگر تیرا رب چاہتا تو سب لوگو ں کو ایک رستہ پر ڈال دیتا اور ہمیشہ اختلاف میں رہیں گے'),
(1592, 11, 119, 'مگر جس پر تیرے رب نے رحم کیا اور اسی لیے انہیں پیدا کیا ہے اور تیرے رب کی یہ بات پوری ہو کر رہے گی کہ البتہ دوزخ کو اکھٹے جنوں اور آدمیوں سے بھر دوں گا'),
(1593, 11, 120, 'اور ہم رسولوں کے حالات تیرے پاس اس لیے بیان کرتے ہیں کہ ان سے تیرے دل کو مضبوط کر دیں اور ان واقعات میں تیرے پاس حق بات پہنچ جائے گی اور ایمانداروں کے لیے نصیحت اور یاد دہانی ہے'),
(1594, 11, 121, 'اوران سے کہہ دو جو ایمان نہیں لاتے اپنے جگہ پر کام کیے جاؤ ہم بھی کام کرتے ہیں'),
(1595, 11, 122, 'اور انتظار کرو ہم بھی منتظر ہیں'),
(1596, 11, 123, 'اور آسمانوں اور زمین کی پوشیدہ بات الله ہی جانتا ہے اور سب کام کا رجوع اسی کی طرف ہے پس اسی کی عبادت کرو اور اسی پر بھروسہ رکھ اور تیرا رب بے خبر نہیں اس سے جو کرتے ہو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'یہ روشن کتاب کی آیتیں ہیں'),
(1598, 12, 2, 'ہم نے اس قرآن کو عربی زبان میں تمہارے سمجھنے کے لیے نازل کیا'),
(1599, 12, 3, 'ہم تیرے پاس بہت اچھا قصہ بیان کرتے ہیں اس واسطے کہ ہم نے تیری طرف یہ قرآن بھیجا ہے اور تو اس سے پہلے البتہ بے خبروں میں سے تھا'),
(1600, 12, 4, 'جس وقت یوسف نے اپنے باپ سے کہا اے باپ میں گیارہ ستاروں اور سورج اور چاند کو خواب میں دیکھا کہ وہ مجھے سجدہ کر رہے ہیں'),
(1601, 12, 5, 'کہا اے بیٹا اپنا خواب بھائیوں کے سامنے بیان مت کرنا وہ تیرے لیے کوئی نہ کوئی فریب بنا دیں گے شیطان انسان کا صریح دشمن ہے'),
(1602, 12, 6, 'اوراسی طرح تیرا رب تجھے برگزیدہ کرے گا اور تجھےخواب کی تعبیر سکھائے گا اور اپنی نعمتیں تجھ پر اور یعقوب کے گھرانے پر پوری کرے گا جس طرح کہ اس سے پہلے تیرے باپ دادا ابراھیم اور اسحاق پر پوری کر چکا ہےبے شک تیرا رب جاننے والا حکمت والا ہے'),
(1603, 12, 7, 'البتہ یوسف اور اس کے بھائیوں کے قصہ میں پوچھنے والو ں کے لیے نشانیاں ہیں'),
(1604, 12, 8, 'جب انہوں نے کہا البتہ یوسف اور اس کا بھائی ہمارے باپ کو ہم سے زیادہ پیارا ہے حالانکہ ہم طاقتور جماعت ہیں بے شک ہمارا باپ صریح غلطی پر ہے'),
(1605, 12, 9, 'یوسف کو مار ڈالو یا کسی ملک میں پھینک دو تاکہ باپ کی توجہ اکیلے تم پر رہے اور اس کے بعد نیک آدمی ہو جانا'),
(1606, 12, 10, 'ان میں سے ایک کہنے والے نے کہا کہ یوسف کو قتل نہ کرواور اسے گمنام کنوئیں میں ڈال دو کہ اسے کوئی مسافر اٹھا لے جائے اگر تم کرنے ہی والے ہو'),
(1607, 12, 11, 'انہوں نے کہااےباپ کیا بات ہے کہ تو یوسف پر ہمارا اعتبار نہیں کرتا اور ہم تو اس کے خیر خواہ ہیں'),
(1608, 12, 12, 'کل اسے ہمارے ساتھ بھیج دے کہ وہ کھائے اور کھیلے اوربےشک ہم ا سکے نگہبان ہیں'),
(1609, 12, 13, 'اس نے کہا مجھے اس سے غم ہوتا ہے کہ تم اسے لے جاؤ اور اس سے ڈرتا ہوں کہ اسے بھیڑیا کھا جائے اورتم اس سے بے خبر رہو'),
(1610, 12, 14, 'انہوں نے کہا اگر اسے بھیڑیا کھا گیا اور ہم ایک طاقتور جماعت ہیں بے شک ہم اس وقت البتہ نقصان اٹھانے والے ہوں گے'),
(1611, 12, 15, 'جب اسے لے کر چلے اور متفق ہوئے کہ اسے گمنام کنوئیں میں ڈالیں تو ہم نے یوسف کی طرف وحی بھیجی کہ تو ضرور انہیں ایک دن آگاہ کرے گا ان کے اس کام سے اور وہ تجھے نہ پہچانیں گے'),
(1612, 12, 16, 'اور کچھ رات گئی اپنےباپ کے پاس روتے ہوئے آئے'),
(1613, 12, 17, 'کہا اے ہمارے باپ ہم تو آپس میں دوڑنے میں مصروف ہوئے اور یوسف کو اپنے اسباب کے پاس چھوڑ گئے تب اسے بھیڑیا کھا گیا او رتو ہمارے کہنے پر یقین نہیں کرے گا اگرچہ ہم سچےہی ہوں'),
(1614, 12, 18, 'اور اسی کے کرتے پر جھوٹ موٹ کا خون بھی لگا لائے اس نے کہا نہیں بلکہ تم نے دل سے ایک بات بنائی ہے اب صبر ہی بہتر ہے اورالله ہی سے مدد مانگتا ہوں اس بات پر جو تم بیان کر تے ہو'),
(1615, 12, 19, 'اور ایک قافلہ آیا پھر انہوں نے اپنا پانی بھرنے والا بھیجا اس نے اپنا ڈول لٹکایا کہا کیا خوشی کی بات ہے یہ ایک لڑکا ہے اور اسے تجارت کا مال سمجھ کر چھپا لیا اور الله خوب جانتا ہے جو کچھ وہ کر رہے تھے'),
(1616, 12, 20, 'اسے بھائی ناقص قیمت پر بیچ آئے گنتی کی چونیوں پر اور اس سے بیزار ہو رہے تھے'),
(1617, 12, 21, 'اور جس نے اسے مصر میں خرید کیا اس نے اپنی عورت سے کہا اس کی عزت کر شاید ہمارے کام آئے یا ہم اسے بیٹا بنا لیں اس طرح ہم نے یوسف کو اس ملک میں جگہ دی اور تاکہ ہم اسے خواب کی تعبیر سکھائیں اور الله اپنا کام جیت کر رہتا ہے لیکن اکثر لوگ نہیں جانتے'),
(1618, 12, 22, 'اور جب اپنی جوانی کو پہنچا تو ہم نے اسے حکم اور علم دیا اور نیکوں کو ایسا ہی بدلہ دیتے ہیں'),
(1619, 12, 23, 'اورجس عورت کے گھر میں تھا وہ اسے پھسلا نے لگی اور دروازے بند کر لیے اور کہنے لگی لو آؤ اس نے کہا الله کی پناہ وہ تو میرا آقا ہے جس نے مجھے عزت سے رکھا ہے بے شک ظالم نجات نہیں پاتے'),
(1620, 12, 24, 'اور البتہ اس عورت نے تو اس پر ارادہ کر لیا تھا اور اگر وہ اپنے رب کی دلیل نہ دیکھ لیتا تو اس کا اردہ کر لیتا اسی طرح ہوا تاکہ ہم اس سے برائی اور بے حیائی کو ٹال دیں بے شک وہ ہمارے چنے ہوئے بندوں میں سے تھا'),
(1621, 12, 25, 'اور دونوں دروازے کی طرف دوڑے اور عورت نے اس کا کرتہ پیچھے سےپھاڑ ڈالا اور دونوں نے عور ت کے خاوند کو دروازے کے پاس پایا کہنے لگی کہ جو تیرے گھر کے لوگوں سے برااردہ کرے اس کی تو یہی سزا ہے کہ قید کیا جائے یا سخت سزا دی جائے'),
(1622, 12, 26, 'کہا یہی مجھ سے اپنا مطلب نکالنے کو پھسلاتی تھی اور عورت کے گھر والوں میں سے ایک گواہ نے گواہی دی اگر اس کا کرتہ آگے سے پھٹا ہوا ہے تو عورت سچی ہے اور وہ جھوٹا ہے'),
(1623, 12, 27, 'اور اگر اس کا کرتہ پیچھے سے پھٹاہوا ہے تو یہ جھوٹی ہے اور وہ سچا ہے'),
(1624, 12, 28, 'پھر جب عزیز نے اس کا کرتہ پیچھے سے پھٹا ہوا دیکھا کہابے شک یہ تم عورتوں کا ایک فریب ہے بے شک تمہارا فریب بڑا ہوتا ہے'),
(1625, 12, 29, 'یوسف تو اس سے درگزر کر اور تو اے عورت اپنے گنا ہ کی معافی مانگ کیوں کہ تو ہی خطا کار ہے'),
(1626, 12, 30, 'اورعوتوں نے شہر میں چرچا کیا کہ عزیز کی عورت اپنے غلام کو چاہتی ہے بے شک اس کی محبت میں فریفتہ ہوگئی ہے ہم تو اسے صریح غلطی پر دیکھتے ہیں'),
(1627, 12, 31, 'پھر جب عزیز کی بیوی نے ان کی ملامت سنی تو انہیں بھلا بھیجا اور ان کے واسطے ایک مجلس تیار کی اور ان میں سے ہر ایک کے ہاتھ میں ایک چھری دی اور کہا ان کے سامنے نکل آ پھر جب انہوں نےاسے دیکھا تو حیرت میں رہ گئیں اور اپنے ہاتھ کاٹ لیے اور کہا الله پاک ہے یہ انسان تو نہیں ہے یہ تو کوئی بزرگ فرشتہ ہے'),
(1628, 12, 32, 'کہا یہی وہ ہے کہ جس کے معاملہ میں تم نے مجھےملامت کی تھی اور البتہ تحقیق میں نے اس سے دلی خواہش ظاہر کی تھی پھر اس نے اپنے آپ کو روک لیا اور اگر وہ میرا کہنا نہ مانے گا تو ضرور قید کر دیا جائے گا اور ذلیل ہو کررہے گا'),
(1629, 12, 33, 'یوسف نے کہا اے میرے رب میرے لیے قید حانہ بہتر ہے اس کام سے کہ جس کی طرف مجھے بلا رہی ہیں اوراگر تو مجھ سے ان کا فریب دفع نہ کرے گا تو ان کی طرف مائل ہوجاؤں گا اور جاہلوں میں سے ہو جاؤں گا'),
(1630, 12, 34, 'پھر اس کے رب نے اس کی دعا قبول کی پس ان کا فریب ان سے دور کر دیا گیا کیوں کہ وہی سننے والا جاننے والا ہے'),
(1631, 12, 35, 'ان لوگو ں کو نشانیاں دیکھنے کے بعد یوں سمجھ میں آیا کہ اسے ایک مدت تک قید کر دیں'),
(1632, 12, 36, 'اور اس کے ساتھ دو جوان قید خانہ میں داخل ہوئے ان میں سے ایک نے کہا میں دیکھتا ہوں کہ شراب نچوڑتا ہوں اور دوسرے نے کہا میں دیکھتا ہوں کہ اپنے سر پر روٹی اٹھا رہا ہوں کہ اس میں سے جانور کھاتے ہیں ہمیں اس کی تعبیر بتلا ہم تجھے نیکو کار سمجھتے ہیں'),
(1633, 12, 37, 'کہا جو کھانا تمہیں دیا جاتا ہے وہ ابھی آنے نہ پائے گا کہ اس سے پہلے میں تمہیں اس کی تعبیر بتلا دوں گا یہ ان چیزو ں سے ہے جو میرے رب نے مجھے سکھائی ہیں بے شک میں نے اس قوم کا مذہب ترک کر دیا ہے جو الله پرایمان نہیں لاتی اور وہ آخرت کے بھی منکر ہیں'),
(1634, 12, 38, 'اور میں اپنے باپ دادا ابراھیم اور اسحاق اور یعقوب کے مذہب کا تابع ہو گیا ہوں ہمیں یہ جائز نہیں کی اللہ کے ساتھ کسی کو بھی شریک کریں یہ ہم پر اور سب لوگوں پر اللہ کا فضل ہے لیکن بہت لوگ شکر نہیں کرتے'),
(1635, 12, 39, 'اے قید خانہ کے رفیقو کیا کئی جدا جدا معبود بہتر ہیں یا اکیلا الله جو زبردست ہے'),
(1636, 12, 40, 'تم اس کے سوا کچھ نہیں پوجتے مگر چند ناموں کو جو تم نے اور تمہارے باپ داداؤں نے مقرر کر لیے ہیں الله نے ان کے متعلق کوئی سند نہیں اتاری حکومت سوا الله کے کسی کی نہیں ہے اس نے حکم دیا ہے کہ اس کے سوا کسی کی عبادت نہ کرو یہی سیدھا راستہ ہے لیکن اکثر آدمی نہیں جانتے'),
(1637, 12, 41, 'اے قید خانہ کے رفیقو تم دونوں میں سے ایک جو ہے وہ اپنے آقا کو شراب پلائے گا جو دوسرا ہے وہ سولی دیا جائے گا پھر اس کے سر میں سے پرندے کھائیں گے اس کام کا فیصلہ ہو گیا ہے جس کی تم تحقیق چاہتے تھے'),
(1638, 12, 42, 'اوران دونو ں میں سے جسے شیطان نے اسے اپنے آقا سے ذکر کرنا بھلا دیا پھر قید میں کئی برس رہا'),
(1639, 12, 43, 'اور بادشاہ نے کہا میں خواب دیکھتا ہوں کہ سات موٹی گائیں ہیں انہیں سات دبلی گائی ں کھاتی ہیں اور سات سبز خوشے ہیں اور سات خشک اے دربار والو مجھے میرے خواب کی تعبیر بتلاؤ اگر تم خواب کی تعبیر دینے والے ہو'),
(1640, 12, 44, 'انہوں نے کہا یہ خیالی خواب ہیں اور ہم ایسے خوابوں کی تعبیر نہیں جانتے'),
(1641, 12, 45, 'اور وہ بولا جو ان دونو ں میں سے بچا تھا اور اسے مدت کے بعد یاد آ گیا میں تمہیں اس کی تعبیر بتلاؤں گا سو تم مجھے بھیج دو'),
(1642, 12, 46, 'اے یوسف اے سچے ہمیں اس کی تعبیر بتلا کہ سات موٹی گایوں کو سات دبلی کھا رہی ہیں اور سات سبز خوشےہیں اور سات خشک تاکہ میں لوگو ں کے پاس لے جاؤں شاید وہ سمجھ جائیں'),
(1643, 12, 47, 'کہا تم سات برس لگاتار کھیتی کرو گے پھر جو کاٹو تو اسے اس کے خوشوں میں رہن ے دو مگر تھوڑا سا جو تم کھاؤ'),
(1644, 12, 48, 'پھر اس کے بعد سات برس سختی کے آئيں گے جو تم نے ان کے لیے رکھا تھا کھا جائیں گے مگر تھوڑا سا جوتم بیج کے واسطے روک رکھو گے'),
(1645, 12, 49, 'پھر اس کے بعد ایک سال آئے گا اس میں لوگوں پر مینہ برسے گا اسمیں رس نچوڑیں گے'),
(1646, 12, 50, 'اور بادشاہ نے کہا اسے میرے پاس لے آؤ پھر جب اس کے پاس قاصد پہنچا کہا اپنے آقا کے ہاں واپس جا اور اس سے پوچھ ان عورتوں کا کیا حال ہے جنہوں نے اپنے ہاتھ کاٹے تھے بے شک میرا رب ان کے فریب سے خوب واقف ہے'),
(1647, 12, 51, 'کہاتمہارا کیا واقعہ تھا جب تم نے یوسف کو پھسلایا تھا انہوں نے کہا الله پاک ہے ہمیں اس میں کوئی برائی معلوم نہیں ہوئی عزیز کی عورت بولی اب سچی بات ظاہر ہو گئی میں نے ہی اسے پھسلانا چاہا تھا اور وہ سچا ہے'),
(1648, 12, 52, 'یہ اس لیے کیا تاکہ عزیز معلوم کر لے کہ میں نے اس کی غائبانہ خیانت نہیں کی تھی اوربے شک الله خیانت کرنے والوں کے فریب کو چلنے نہیں دیتا'),
(1649, 12, 53, 'اور میں اپنے نفس کو پاک نہیں کہتا بے شک نفس تو برائی سکھاتاہے مگر جس پرمیرا رب مہربانی کرے بے شک میرا رب بخشنے والا مہربان ہے'),
(1650, 12, 54, 'اور بادشاہ نے کہا کہ اسے میرے پاس لے آؤ تاکہ اسے خاص اپنے پاس رکھوں پھر جب اس سے بات چیت کی کہا بے شک تو آج سے ہمارے ہاں تو بڑا معزز اور معتبر ہے'),
(1651, 12, 55, 'کہا مجھے ملکی خزانوں پر مامور کر دو بے شک میں خوب حفاظت کرنے والا جاننے والا ہوں'),
(1652, 12, 56, 'اور ہم نے اس طور پر یوسف کو اس ملک میں بااختیار بنا دیا کہ اس میں جہاں چاہے رہےہم جس پر چاہیں اپنی رحمت متوجہ کر دیں اور ہم نیکی کرنے والوں کا اجر ضائع نہیں کرتے'),
(1653, 12, 57, 'اور آخرت کا ثواب ان کے لیے بہتر ہے جو ایمان لائے اور پرہیزگاری میں رہے'),
(1654, 12, 58, 'اور یوسف کے بھائی آئے پھر اس کے ہاں داخل ہوئے تواس نے انہیں پہچان لیا اور وہ پہچان نہیں سکے'),
(1655, 12, 59, 'اور جب انہیں ان کا سامان تیار کر دیا کہا میرے پاس وہ بھائی بھی لے آنا جو تمہارے باپ کی طرف سے ہے تم نہیں دیکھتے میں ناپ پورا دیتا ہوں اوربڑا مہمان نواز ہوں'),
(1656, 12, 60, 'پھر اگر تم اسے میرے پاس نہ لائے تو نہ تمہیں میرے ہاں سے پیمانہ ملے گا اور نہ تم میرے پاس آنا'),
(1657, 12, 61, 'انہوں نے کہا اس کے باپ سے خواہش کریں گے اور ہم یہ کر کے ہی رہیں گے'),
(1658, 12, 62, 'اور اپنے خدمتگاروں سے کہہ دیا کہ ان کی پونجی ان کے اسباب میں رکھ دو تاکہ وہ اسے پہچانیں جب وہ لوٹ کر اپنےگھر جائیں شاید وہ پھر آجائیں'),
(1659, 12, 63, 'پھر جب اپنے باپ کے ہاں پہنچے کہا اے باپ! ہمارا پیمانہ روک لیا گیا پس آپ ہمارے ساتھ ہمارے بھائی کو بھیج دیجیئے کہ ہم پیمانہ لائیں اور بے شک ہم اس کا نگہبان ہیں'),
(1660, 12, 64, 'کہا میں تمہارا اس پرکیا اعتبارکروں مگر وہی جیسا اس سے پہلے اس کے بھائی پر اعتبار کیا تھا سو الله بہتر نگہبان ہے اور وہ سب مہربانوں سے مہربان ہے'),
(1661, 12, 65, 'اورجب انہوں نے اپنا اسباب کھولا انہوں نے اپنی پونجی پائی جو انہیں واپس کر دی گئی تھی کہا اے ہمارے باپ! ہمیں اور کیا چاہیئے یہ ہماری پونجی ہمیں واپس کر دی گئی ہے اور اپنے گھر والوں کے لیے غلہ لائیں گے او راپنے بھائی کی حفاظت کریں گے اور ایک اونٹ کا بوجھ اور زیادہ لائیں گے اور یہ بوجھ ملنا آسان ہے'),
(1662, 12, 66, 'کہا اسے ہرگز تمہارے ساتھ نہیں بھیجوں گا یہاں تک کہ مجھے الله کا عہد دو کہ البتہ اسے میرے ہاں ضرور پہنچا دویں گے مگر یہ کہ تم سب گھر جاؤ پھر جب سب نے اسے عہد دیا کہا ہماری باتوں کا الله شاہد ہے'),
(1663, 12, 67, 'اور کہا اے میرے بیٹو! ایک دروازے سے داخل نہ ہونا اور مختلف دروازوں سے داخل ہونا اور میں تمہیں الله کی کیسی بات سے بچا نہیں سکتا الله کے سوا کسی کا حکم نہیں ہے اسی پر میرا بھروسہ ہے اوربھروسہ کرنے والوں کو اسی پر بھروسہ کرنا چاہئے'),
(1664, 12, 68, 'اور جب کہ اسی طرح داخل ہوئے جس طرح ان کے باپ نے حکم دیا تھا انہیں الله کی کسی بات سے کچھ نہ بچا سکتا تھا مگر یعقوب کے دل میں ایک خواہش تھی جسے اس نے پورا کیا اور وہ تو ہمارے سکھلانے سے علم والا تھا لیکن اکثر آدمی نہیں جانتے'),
(1665, 12, 69, 'اور جب وہ یوسف کے پاس گئے تو اس نے اپنے بھائی کو اپنے پاس جگہ دی کہا کہ بے شک میں تیرا بھائی ہوں پس جو کچھ یہ کرتے رہے ہیں اس پر غم نہ کر'),
(1666, 12, 70, 'پھر جب یوسف نے اس کا سامان تیار کر دیا تو اپنے بھائی کے اسباب میں کٹورا رکھ دیا پھر پکارے نے والے نے پکارا اے قافلہ والو! بے شک تم البتہ چور ہو'),
(1667, 12, 71, 'اس کی طرف متوجہ ہو کر کہنے لگے تمہاری کیا چیز گم ہوگئی ہے'),
(1668, 12, 72, 'انہوں نے کہا ہمیں بادشاہ کا کٹورا نہیں ملتا جو اسے لا ئےگا ایک اونٹ بھر کا غلہ پائے گا اور میں اس کا ضامن ہوں'),
(1669, 12, 73, 'انہوں نے کہا الله کی قسم تمہیں معلوم ہے ہم اس ملک میں شرارت کرنے کے لیے نہیں آئے اور نہ ہم کبھی چور تھے'),
(1670, 12, 74, 'انہوں نے کہا پھر اس کی کیا سزا ہے اگر تم جھوٹے نکلو'),
(1671, 12, 75, 'انہوں نے کہا اس کی سزا یہ ہے کہ جس کےاسباب میں سے پایا جائے پس وہی اس کے بدلہ میں جائے ہم ظالموں کو یہی سزا دیتے ہیں'),
(1672, 12, 76, 'پھر یوسف نے اپنے بھائی کے اسباب سے پہلےان کے اسباب دیکھنے شروع کیے پھر وہ کٹورا اپنے بھائی کے اسباب سے نکالا ہم نے یوسف کو ایسی تدبیر بتائی تھی بادشاہ کے قانون سے تو وہ اپنے بھائی کو ہرگز نہ لے سکتا تھا مگر یہ کہ الله چاہے ہم جس کے چاہیں درجے بلند کرتے ہیں اور ہر ایک دانا سے بڑھ کر دوسرا دانا ہے'),
(1673, 12, 77, 'انہوں نے کہا اگر اس نے چوری کی ہے تو اس سے پہلے اس کے بھائی نے بھی چوری کی تھی تب یوسف نے اپنے دل میں آہستہ سے کہا اور انہیں نہیں جتایا کہا تم درجے میں بدتر ہو اور الله خوب جانتا ہے جو کچھ تم بیان کرتے ہو'),
(1674, 12, 78, 'انہوں نے کہا اے عزیز! بے شک اس کا باپ بوڑھا بڑی عمر کا ہے سو اسکی جگہ ہم میں سے ایک کو رکھ لے ہم تم کو احسان کرنے والا دیکھتے ہیں'),
(1675, 12, 79, 'کہا الله کی پناہ کہ ہم بجز اس کے جس کے پاس اپنا اسباب پایا کسی اور کو پکڑیں تب تو ہم بڑے ظالم ہیں'),
(1676, 12, 80, 'پھر جب اس سےناامید ہوئے مشورہ کرنے کے لیے اکیلے ہو بیٹھے ان میں سے بڑے نے کہا کیا تمہیں معلوم نہیں کہ تمہارے باپ نے تم سے الله کا عہد لیا تھا اور پہلے جویوسف کے حق میں قصور کر چکے ہو سو میں تو اس ملک سے ہرگز نہیں جاؤں گا یہاں تک کہ میرا با پ مجھے حکم دے یا میرے لیے الله کوئی حکم فرمائے اور وہ بہتر فیصلہ کرنے والا ہے'),
(1677, 12, 81, 'تم اپنے باپ کے پاس لوٹ جاؤ اور کہو اے ہمارے باپ! تیرے بیٹے نے چوری کی اور ہم نے وہی کہا تھا جس کا ہمیں علم تھا اور ہمیں غیب کی خبر نہ تھی'),
(1678, 12, 82, 'اوراس گاؤں سے پوچھ لیجیئے جس میں ہم تھے اور اس قافلہ سے بھی جس میں ہم آئے ہیں اور بے شک ہم سچے ہیں'),
(1679, 12, 83, 'کہا بلکہ تم نے دل سے ایک بات بنا لی ہے اب صبر ہی بہتر ہے الله سے امید ہے کہ شاید الله ان سب کو میرے پاس لے آئے وہی جاننے والا حکمت والا ہے'),
(1680, 12, 84, 'اور اس نے ان سے منہ پھیر لیا اور کہا ہائے یوسف! اور غم سے اس کی آنکھیں سفید ہو گئیں پس وہ سخت غمگین ہوا'),
(1681, 12, 85, 'انہوں نے کہا الله کی قسم تو یوسف کی یاد کو نہیں چھوڑے گا یہاں تک کہ نکما ہو جائے یا ہلاک ہوجائے'),
(1682, 12, 86, 'کہا میں تو اپنی پریشانی اور غم کا اظہار الله ہی کے سامنے کرتا ہوں اور الله کی طرف سے میں وہ جانتا ہوں جو تم نہیں جانتے'),
(1683, 12, 87, 'اے میرے بیٹو! جاؤ یوسف اور اس کے بھائی کی تلاش کرو اور الله کی رحمت سے نا امید نہ ہو بے شک الله کی رحمت سے نا امید نہیں ہوتے مگر وہی لوگ جو کافر ہیں'),
(1684, 12, 88, 'پھر جب وہ ان کے پاس آئے تو کہا اے عزیز! ہمیں اور ہمارے گھر والوں کو قحط کی وجہ سے بڑی تکلیف ہے اور کچھ نکمی چیز لائے ہیں سو آپ پورا غلہ بھر دیجیئے اورخیرات دیجئے بے شک الله خیرات دینے والوں کو ثواب دیتا ہے'),
(1685, 12, 89, 'کہا تمہیں یاد ہے جو کچھ تم نے یوسف اور اس کے بھائی کے ساتھ کیا تھا جب تمہیں سمجھ نہ تھی'),
(1686, 12, 90, 'کہا کیا تو ہی یوسف ہے کہا میں ہی یوسف ہوں اور یہ میرا بھائی ہے الله نے ہم پر احسان کیابے شک جو ڈرتا ہے اور صبر کتا ہے تو الله بھی نیکوں کا اجر ضائع نہیں کرتا'),
(1687, 12, 91, 'انہوں نے کہا الله کی قسم البتہ تحقیق الله نے تمہیں ہم پر بزرگی دی اوربے شک ہم غلط کار تھے'),
(1688, 12, 92, 'کہا آج تم پر کوئی الزام نہیں الله تمہیں بخشے اور وہ سب سے زیادہ مہربان ہے'),
(1689, 12, 93, 'یہ کرتہ میرا لے جاؤ اور اسے میرے باپ کے منہ پر ڈال دو کہ وہ بینا ہوجائے اور میرے پاس اپنے سب کنبے کو لے آؤ'),
(1690, 12, 94, 'اور جب قافلہ روانہ ہوا تو ان کے باپ نے کہا بے شک میں یوسف کی بو پاتا ہوں اگر مجھے دیوانہ نہ بناؤ'),
(1691, 12, 95, 'لوگو ں نے کہا الله کی قسم بے شک تو البتہ اپنی گمراہی میں مبتلا ہے'),
(1692, 12, 96, 'پھر جب خوشخبری دینے والا آیا اس نے وہ کرتہ اس کے منہ پر ڈال دیا تو بینا ہو گیا کہا میں نے تمہیں نہیں کہا تھا کہ میں الله کی طرف سے وہ جانتا ہوں جو تم نہیں جانتے'),
(1693, 12, 97, 'انہوں نے کہا اے ہمارے باپ! ہمارے گناہ بحشوا دیجیئے بے شک ہم ہی غلط کار تھے'),
(1694, 12, 98, 'کہا عنقریب اپنے رب سے تمہارے لی معافی مانگوں گا بے شک وہ غفور رحیم ہے'),
(1695, 12, 99, 'پھر جب یوسف کے پاس آئے تو اس نے اپنے ماں باپ کو اپنے پاس جگہ دی اور کہا مصر میں داخل ہو جاؤ اگر الله نے چاہا تو امن سے رہو گے'),
(1696, 12, 100, 'اور اپنے ماں باپ کو تخت پر اونچا بٹھایا اور اس کے آگے سب سجدہ میں گر پڑے اور کہا اےباپ میرے اس پہلے خواب کی یہ تعبیر ہے اسے میرے رب نے سچ کر دکھایا اوراس نے مجھ پر احسان کیا جب مجھے قید خانے سے نکالا اور تمہیں گاؤں سے لے آیا اس کے بعد کہ شیطان مجھ میں اورمیرے بھائیوں میں جھگڑا ڈال چکا بے شک میرا رب جس کے لیے چاہتا ہے مہربانی فرماتا ہے بے شک وہی جاننے والا حکمت والا ہے'),
(1697, 12, 101, 'اے میرے رب تو نے مجھےکچھ حکومت دی ہے اور مجھے خوابوں کی تعبیر کا علم بھی سکھلایا ہے اے آسمانو ں اور زمین کے بنانے والے! دنیا اور آخرت میں تو ہی میرا کارساز ہے تو مجھے اسلام پر موت دے اور مجھے نیک بختوں میں شامل کر دے'),
(1698, 12, 102, 'یہ غیب کی خبریں کہیں جو ہم تیرے ہاں بھیجتے ہیں اور تو ان کے پاس نہیں تھا جب کہ انہوں نے اپنا ارادہ پکا کر لیا اور وہ تدبیریں کر رہے تھے'),
(1699, 12, 103, 'اور اکثر لوگ ایمان لانے والے نہیں خواہ تو کتنا ہی چاہے'),
(1700, 12, 104, 'اور آپ اس پر ان سے کوئی مزدوری بھی تو نہیں مانگتے یہ تو صرف تمام جہانوں کے لیے نصیحت ہے'),
(1701, 12, 105, 'اور آسمانوں اور زمین میں بہتی سی نشانیاں ہیں جن پر سے یہ گزرتے ہیں اوران سے منہ پھیر لیتے ہیں'),
(1702, 12, 106, 'اوران میں سے اکثر ایسے بھی ہیں جو الله کو مانتے بھی ہیں اور شرک بھی کرتے ہیں'),
(1703, 12, 107, 'کیا اس سے بے خوف ہو چکے ہیں کہ انہیں الله کے عذاب کی ایک آفت آپہنچے یا اچانک قیامت ان پر آ جائے اور انہیں خبر بھی نہ ہو'),
(1704, 12, 108, 'کہہ دو میرا اور میرے تابعداروں کا بصیرت کے ساتھ یہ راستہ ہے کہ میں لوگوں کو الله کی طرف بلا رہا ہوں اور الله پا ک ہے اور میں شرک کرنے والوں میں سے نہیں ہوں'),
(1705, 12, 109, 'ااور تجھ سے پہلے ہم نے جتنے پیغمبر بھیجے وہ سب بستیو ں کے رہنے و الے مرد ہی تھے ہم ان کی طرف وحی بھیجتے تھے پھر وہ زمین میں سیر کر کے کیوں نہیں دیکھتے کہ ان لوگو ں کا انجام کیا ہوا جوان سے پہلے تھے اور البتہ آخرت کا گھر پرہیز کرنے والوں کے لیے بہتر ہے پھر تم کیوں نہیں سمجھتے'),
(1706, 12, 110, 'یہاں تک کہ جب رسول ناامید ہونے لگے اورخیال کیا کہ ان سے جھوٹ کہا گیا تھا تب انہیں ہماری مدد پہنچی پھر جنہیں ہم نے چاہا بچا لیا اور ہمارے عذاب کو نافرمانوں سے کوئی بھی روک نہیں سکتا'),
(1707, 12, 111, 'البتہ ان لوگو ں کے حالات میں عقلمندوں کے لیے عبرت ہے کوئی بنائی ہوئی بات نہیں ہے بلکہ اس کلام کے موافق ہے جو اس سے پہلے ہے اور ہر چیز کا بیان اور ہدایت اور رحمت ان لوگوں کے لیے ہے جو ایمان لاتے ہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'یہ کتاب کی آیتیں ہیں اور جو کچھ تجھ پر تیرے رب سے اترا سوحق ہے اورلیکن اکثر آدمی ایمان نہیں لاتے'),
(1709, 13, 2, 'الله وہ ہے جس نے آسمانوں کو ستونوں کے بغیر بلند کیا جنہیں تم دیکھ رہے ہو پھر عرش پر قائم ہوا اور سورج اور چاند کو کام پر لگا دیا ہر ایک اپنے وقت معین پر چل رہا ہے وہ ہر ایک کام کاانتظام کرتا ہے نشانیاں کھول کر بتاتا ہے تاکہ تم اپنے رب سے ملنے کا یقین کر لو'),
(1710, 13, 3, 'اور اسی نے زمین کو پھیلایا اور اس میں پہاڑاور دریا بنائے اور زمین میں ہر ایک پھل دوقسم کا بنایا دن کو رات سے چھپا دیتا ہے بے شک اس میں سوچنے والوں کے لیے نشانیاں ہیں'),
(1711, 13, 4, 'اور زمین میں ٹکڑے ایک دوسرے سے ملے ہوئے ہیں اور انگور کے باغ ہیں اور کھیتیاں اور کھجوریں ہیں ایک کی جڑ ملی ہوئی بعض بن ملی انہیں پانی بھی ایک ہی دیا جاتا ہے اور ہم ایک کو دوسرے پر پھلوں میں فضیلت دیتے ہیں بے شک اسمیں عقل مندوں کے لیے بڑی نشانیاں ہیں'),
(1712, 13, 5, 'اگر تو عجیب بات چاہے تو ان کا یہ کہنا عجب ہے کہ کیا جب ہم مٹی ہو گئے کیا نئے سرے سے بنائیں جائیں گے یہی وہ ہیں جو اپنے رب سے منکر ہو گئے اور انہیں کی گردنوں میں طوق ہوں گے اور یہی دوزخی ہیں و ہ اس میں ہمیشہ رہیں گے'),
(1713, 13, 6, 'اور تجھ سے بھلائی سے پہلے برائی کو جلد مانگتے ہیں اور ان سے پہلے بہت سے عذاب سے گزر چکے ہیں اور بے شک تیرا رب لوگو ں کو باوجود ان کے ظلم کے معاف بھی کرتا ہے اور تیرے رب کا عذاب بھی سخت ہے'),
(1714, 13, 7, 'اورکافر کہتے ہیں اس کے رب سے اس پر کوئی نشانی کیوں نہیں اتری تم تومحض ڈرانے والے ہوں اور ہر قوم کے لیے ایک رہبر ہوتا آیا ہے'),
(1715, 13, 8, 'الله کو معلوم ہے کہ جو کچھ ہر مادہ اپنے پیٹ میں لیے ہوئے ہے اورجو کچھ پیٹ میں سکڑتا اور بڑھتا ہے اور اس کے ہاں ہر چیز کا اندازہ ہے'),
(1716, 13, 9, 'پوشیدہ اور ظاہر کا جاننے والا ہے سب سے بڑا بلند مرتبہ ہے'),
(1717, 13, 10, 'تم میں سے جو شخص کوئی بات چپکے سے کہے یا پکار کرکہے اور جو شخص رات میں کہیں چھپ جائے یا دن میں چلے پھرے یہ سب برابر ہیں'),
(1718, 13, 11, 'ہر شخص حفاظت کے لیے کچھ فرشتے ہیں اس کے آگے اورپیچھے الله کے حکم سے اس کی نگہبانی کرتے ہیں بے شک الله کسی قوم کی حالت نہیں بدلتا جب تک وہ خود اپنی حالت کو نہ بدلے اور جب الله کو کسی قوم کی برائی چاہتا ہے پھر اسے کوئی نہیں روک سکتا اور اس کے سوا ان کا کوئی مددگار نہیں ہو سکتا'),
(1719, 13, 12, 'وہی ہے جو تمہیں خوف یا امید دلانے کے لیے بجلی دکھاتا اور بھاری بادلوں کو اٹھاتا ہے'),
(1720, 13, 13, 'اور رعد ا سکی پاکی کے ساتھ ا سکی تعریف کرتا ہے اور سب فرشتے اس کے ڈر سے اور بجلیاں بھیجتا ہے پھر انہیں جس پر چاہتا ہے گرا دیتا ہے اور یہ تو الله کے بارے میں جھگڑتے ہیں حالانکہ وہ بڑی قوت والا ہے'),
(1721, 13, 14, 'اسی کو پکارنا بجا ہے اوراس کے سوا جن لوگوں کو پکارتے ہیں وہ ان کے کچھ بھی کام نہیں آتے مگر جیسا کوئی پانی کی طرف اپنے دونوں ہاتھ پھیلائے کہ اس کے منہ میں آجائے حالانکہ وہ اس کے منہ تک نہیں پہنچتا اور کافروں کی جتنی پکار ہے سب گمراہی ہے'),
(1722, 13, 15, 'اور چارو ناچار الله ہی کو آسمان والے اور زمین والے سجدہ کرتے ہیں اور ان کے سائے بھی صبح اور شام'),
(1723, 13, 16, 'کہو آسمانوں اور زمین کا رب کون ہے کہہ دو الله کہو پھر کیا تم نے الله کے سوا ان چیزوں کو معبود نہیں بنا رکھا جو اپنے نفسوں کے نفع اور نقصان کےبھی مالک نہیں کہو کیا اندھا اور دیکھنے والا برابر ہوسکتا ہے یا کہیں اندھیرا اور روشنی برابر ہو سکتے ہیں کیا جنہیں انہوں نے الله کا شریک بنا رکھا ہے انہو ں نے بھی الله کی مخلوق جیسی کوئی مخلوق بنائی ہے پھر مخلوق ان کی نظر میں مشتبہ ہو گئی ہے پیدا کرنے والااللہ ہے اور وہ اکیلا زبردست ہے'),
(1724, 13, 17, 'اس نے آسمان سے پانی اتارا پھر اس سے اپنی مقدار میں نالے بہنے لگے پھر وہ سیلاب پھولا ہوا جھاگ اوپر لایا اور جس چیز کو آگ میں زیور یا کسی اور اسباب بنانے کے لیے پگھلاتے ہیں اس پر بھی ویسا ہی جھاگ ہوتا ہے الله حق اور باطل کی مثال بیان فرماتا ہے پھر جو جھاگ ہے وہ یونہی جاتا رہتا ہے اور جو لوگوں کو فائدہ دے وہ زمین میں ٹھیر جاتا ہے اسی طرح الله مثالیں بیان فرماتا ہے'),
(1725, 13, 18, 'جنہو ں نے اپنے رب کا حکم مانا ان کے واسطے بھلائی ہے اور جنہوں نے اس کا حکم نہ مانا اگر ان کے پاس سارا ہو جو کچھ زمین میں ہے اور اس کے ساتھ اتنا ہی او رہو تو سب جرمانہ میں دینا قبول کریں گے ان لوگو ں کے لیے برا حساب ہے اور ان کا ٹھکانا دوزخ ہے اوروہ برا ٹھکانا ہے'),
(1726, 13, 19, 'بھلا جو شخص جانتا ہے کہ تیرے رب سے تجھ پر جو کچھ اترا ہے حق ہے اس کےبرابر ہو سکتا ہے جو اندھا ہے سمجھتے تو عقل والے ہی ہیں'),
(1727, 13, 20, 'وہ لوگ جو الله کے عہد کو پوراکرتے ہیں اور اس عہد کو نہیں توڑتے'),
(1728, 13, 21, 'اور وہ لوگ جو ملاتے ہیں جس کے ملانے کو الله نے فرمایا ہے اور اپنے رب سے ڈرتے ہیں اور برے حساب کا خوف رکھتے ہیں'),
(1729, 13, 22, 'وہ جنہوں نے اپنے رب کی رضا مندی کے لیے صبر کیا اور نماز قائم کی اور ہمارے دیئے ہوئے میں سے پوشیدہ اور ظاہر خرچ کیا اور برائی کے مقابلے میں بھلائی کرتے ہیں انہیں کے لیے آخرت کا گھر ہے'),
(1730, 13, 23, 'ہمیشہ رہنے کے باغ جن میں وہ خود بھی رہیں گے اور ان کے باپ دادا اوربیویوں اور اولاد میں سے بھی جو نیکو کار ہیں اور ان کے پاس فرشتے ہر دروازے سے آئیں گے'),
(1731, 13, 24, 'کہیں گے تم پر سلامتی ہو تمہارے صبر کرنے کی وجہ سے پھر آخرت کا گھر کیا ہی اچھا ہے'),
(1732, 13, 25, 'اور جو لو گ الله کا عہد مضبوط کرنے کے بعد توڑ تے ہیں اور اس چیز کو توڑتے ہیں جسے الله نے جوڑنے کا حکم فرمایا اور ملک میں فساد کرتے ہیں ان کے لیے لعنت ہے اور ان کے لیے برا گھر ہے'),
(1733, 13, 26, 'الله ہی جس کے لیے چاہتا ہے روزی فراخ اور تنگ کرتا ہے اور دنیا کی زندگی پر خوش ہیں اور دنیا کی زندگی آخرت کے مقابلے میں کچھ نہیں مگر تھوڑاسا اسباب'),
(1734, 13, 27, 'اور کافر کہتے ہیں اس پراس کے رب سے کوئی نشانی کیوں نہیں اتری کہہ دو الله جس کو چاہتا ہے گمراہ کر دیتاہے اور جو اس کی طرف رجوع کرتا ہے اسے اپنے تک پہنچنے کا راستہ دکھاتا ہے'),
(1735, 13, 28, 'وہ لوگ جو ایمان لائے اور ان کے دلوں کو الله کی یاد سے تسکین ہوتی ہے خبردار! الله کی یاد ہی سے دل تسکین پاتے ہیں'),
(1736, 13, 29, 'جو لوگ ایمان لائے او راچھے کام کیے ان کے لیے خوشخبری اور اچھا ٹھکانا ہے'),
(1737, 13, 30, 'اسی طرح ہم نے تجھےایک امت میں بھیجا ہے کہ اس سے پہلے کئی امتیں گزرچکی ہیں تاکہ تو انہیں سناد ے جو ہم نے تیری طرف حکم بھیجا ہے اور وہ تو رحمنٰ کے منکر ہیں کہہ دو وہی میرا رب ہے جس کے سوا کوئی معبود نہیں اسی پر میں نے بھروسہ کیا ہے اور اسی کی طرف میرا رجوع ہے'),
(1738, 13, 31, 'اور اگر تحقیق کوئی ایسا قرآن نازل ہوتا جس سے پہاڑ چلتے یا اس سے زمین کے ٹکڑے ہو جاتے یا اس سے مردے بول اٹھتے (تب بھی نہ مانتے) بلکہ سب کام الله کے ہاتھ میں ہیں پھر کیا ایمان والے اس بات سے نا امید ہو گئے ہیں کہ اگر الله چاہتا تو سب آدمیوں کو ہدایت کر دیتا اور کافروں پر تو ہمیشہ ان کی بداعمالی سے کوئی نہ کوئی مصیبت آتی رہے گی یا وہ بلا ان کے گھر کے قریب نازل ہوگی یہاں تک کہ الله کا وعدہ پورا ہو بے شک الله اپنے وعدے کے خلاف نہیں کرتا'),
(1739, 13, 32, 'اور تجھ سے پہلے کئی رسولوں سے ہنسی کی گئی ہے پھر میں نے کافروں کو مہلت دی پھر انہیں پکڑ لیا پھر ہمارا عذاب کیسا تھا'),
(1740, 13, 33, 'بھلا جو ہر کسی کے سر پر لیے کھڑا ہے جو اس نے کیا ہے اور انہوں نے الله کے لیے شریک بنا رکھے ہیں کہہ دو ان کے نام بتلاؤ کیا الله کو وہ بات بتاتے ہو جسے وہ زمین میں نہیں جانتا یا اوپر ہی کی باتیں کرتے ہو بلکہ کافروں کے فریب انہیں بھلے معلوم کرائے گئے ہیں اور وہ راستہ سے روکے گئے ہیں اور جسے الله گمراہ کرے پھر اسے کوئی بھی ہدایت دینے والا نہیں ہے'),
(1741, 13, 34, 'ان کے لیے دنیا کی زندگی میں عذاب ہے اور البتہ آخرت کا عذاب تو بہت ہی سخت ہے اور انہیں الله سے بچانےوالا کوئی نہیں ہو گا'),
(1742, 13, 35, 'اس جنت کا حال جس کا پرہیزگاروں سے وعدہ کیا گیا ہے اس کے نیچے نہریں بہتی ہیں جس کے میوے اور سائے ہمیشہ رہیں گے یہ پرہیزگاروں کا انجام ہے اور کافروں کا انجام آگ ہے'),
(1743, 13, 36, 'اور وہ لوگ جنہیں ہم نے کتاب دی ہے اس سےخوش ہوتے ہیں جو تجھ پر نازل ہوا اور جماعتوں میں سے بعض لوگ اس کی بعضی بات نہیں مانتے کہہ دو مجھے تو یہی حکم ہوا ہے کہ الله کی بندگی کروں اور اس کے ساتھ کسی کو شریک نہ کروں اس کی طرف بلاتا ہوں اور اسی کی طرف میرا ٹھکانا ہے'),
(1744, 13, 37, 'اور اسی طرح ہم نے یہ کلام اتارا کتاب عربی زبان میں اور اگرتو ان کی خواہش کے مطابق چلے بعد اس علم کے جو تجھے پہنچ چکا ہے تیرا الله سے کوئی حمایتی اور بچانے والا نہ ہوگا'),
(1745, 13, 38, 'او رالبتہ تحقیق ہم نے تجھ سے پہلے کئی رسول بھیجے اور ہم نے انہیں بیویاں اور اولاد بھی دی تھی اور کسی رسول کے اختیار میں نہ تھا کہ وہ الله کے حکم کے سوا کوئی معجزہ لاتا ہر زمانے کے مناسب احکام ہوتے ہیں'),
(1746, 13, 39, 'الله جو چاہے موقوف کر دیتا ہے اور باقی رکھتا ہے اور اسی کے پاس اصل کتاب ہے'),
(1747, 13, 40, 'اور اگر ہم تجھے کوئی وعدہ دکھا دیں جو ہم نے ان سے کیا ہے یا تجھے اٹھا لیں سوتیرے ذمہ تو پہنچا دینا ہے اور ہمارے ذمہ حساب لینا ہے'),
(1748, 13, 41, 'کیا وہ نہیں دیکھتے کہ ہم زمین کو اس کے کناروں سے گھٹاتے چلے آتے ہیں اور الله حکم کرتاہے کوئی اس کے حکم کو ہٹا نہیں سکتا اور وہ جلد حساب لینے والا ہے'),
(1749, 13, 42, 'اور ان سے پہلےلوگ بھی تدبیریں کر چکے ہیں سو اصل تدبیر تو الله ہی کی ہےجو کچھ کوئی کرتا ہے اسے سب خبر رہتی ہے اورابھی کافروں کو معلوم ہو جائے گا کہ نیک انجام کس کا ہے'),
(1750, 13, 43, 'اور کہتے ہیں کہ تو رسول نہیں ہے کہہ دو میرے اور تمہارے درمیان الله گواہ کافی ہے اور وہ شخص جس کے پاس کتاب کا علم ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'یہ ایک کتا ب ہے ہم نے اسے تیری طرف نازل کیا ہے تاکہ تو لوگوں کو ان کے رب کے حکم سے اندھیروں سے روشنی کی طرف غالب تعرف کیے ہوئے راستہ کی طرف نکالے'),
(1752, 14, 2, 'یعنی الله جس کے لیے ہے جو کچھ آسمانوں اور زمین میں ہے اور کافروں پر افسوس کہ انہیں سخت عذاب ہونا ہے'),
(1753, 14, 3, 'جو دنیا کی زندگی کو آخرت کے مقابلہ میں پسند کرتے ہیں اور الله کی راہ سے روکتے ہیں اور اس میں کجی تلاش کرتے ہیں وہ دور کی گمراہی میں پڑے ہوئے ہیں'),
(1754, 14, 4, 'اور ہم نے ہر پیغمبر کو اس کی قوم کی زبان میں پیغمبر بنا کر بھیجا ہے تاکہ انہیں سمجھا سکے پھر الله جسے چاہتا ہے گمراہ کرتا ہے اور جسے چاہتا ہے ہدایت دیتا ہے اوروہ غالب حکمت والا ہے'),
(1755, 14, 5, 'اورالبتہ تحقیق ہم نے موسیٰ کو اپنی نشانیاں دےکر بھیجا تھا کہاپنی قوم کو اندھیروں سے روشنی کی طرف نکال اورانہیں الله کے دن یاد دلا بے شک اس میں ہر ایک صبر شکر کرنے والے کے لیے بڑی نشانیاں ہیں'),
(1756, 14, 6, 'اورجب موسیٰ نے اپنی قوم سے کہا الله کا احسان اپنے اوپر یاد کرو جب تمہیں فرعون کی قوم سے چھڑا یا وہ تمہیں برا عذاب چکھاتے تھے اور تمہارے بیٹوں کو ذبح کرتے تھے اور تمہاری عورتوں کو زندہ رکھتے تھے اور اسمیں تمہارے رب کی طرف سے بڑی آزمائش تھی'),
(1757, 14, 7, 'اور جب تمہارے رب نے سنا دیا تھا کہ البتہ اگر تم شکر گزاری کرو گے تو اور زیادہ دوں گا اور اگر ناشکری کرو گے تو میرا عذاب بھی سخت ہے'),
(1758, 14, 8, 'اور موسیٰ نے کہا اگر تم اور جو لوگ زمین میں ہیں سارے کفر کرو گے تو الله بے پروا تعریف کیا ہوا ہے'),
(1759, 14, 9, 'کیا تمہیں ان لوگوں کی خبر نہیں پہنچی جوتم سے پہلے تھے نوح کی قوم اور عاد اور ثمود اورجو ان کے بعد ہوئے الله کےسوا جنہیں کوئی نہیں جانتا ان کے پاس ان کے رسول نشانیاں لے کر آئے پھرانہوں نے اپنے ہاتھ اپنے مونہوں میں لوٹائے اور کہا ہم نہیں مانتے جو تمہیں دے کر بھیجا گیا ہے اور جس دین کی طرف تم ہمیں بلاتے ہو ہمیں تو اس میں بڑا شک ہے'),
(1760, 14, 10, 'ان کے رسولوں نے کہا کیا تمہیں الله میں شک ہے جس نے آسمان او زمین بنائے وہ تمہیں بلاتا ہے تاکہ تمہارے کچھ گناہ بخشے اور تمہیں ایک مقررہ وقت تک مہلت دے انہوں نے کہا تم بھی تو ہمارے جیسے انسان ہو تم چاہتے ہو کہ ہمیں ان چیزوں سے روک دو جنہیں ہمارے باپ دادا پوجتے رہے سو کوئی کھلا ہوامعجزہ لاؤ'),
(1761, 14, 11, 'ان سے ان کے رسولوں نے کہا ضرور ہم بھی تمہارے جیسے ہی آدمی ہیں لیکن الله اپنے بندوں میں جس پر چاھتا ہے احسان کرتا ہے اور ہمارا کام نہیں کہ ہم الله کی اجازت کے سوا تمہیں کوئی معجزہ لا کر دکھائیں اور ایمان والوں کا بھروسہ اللهہی پر ہونا چاہیئے'),
(1762, 14, 12, 'اور ہم کیوں الله پر بھروسہ نہ کریں حالانکہ اسی نے ہمیں (سیدھے) راستوں کی راہ نمائی کی ہے اور ہم ضرور صبر کریں گے اس ایذاپر جو تم ہمیں دیتے ہو اور توکل کرنے والوں کو الله ہی پر بھروسہ کرنا چاہیے'),
(1763, 14, 13, 'اور کافروں نے اپنے رسولوں سے کہا ہم تمہیں اپنے ملک سے نکال دیں گے یا ہمارے دین میں لوٹ آؤ تب انہیں ان کے رب نے حکم بھیجا کہ ہم ان ظالموں کو ضرور ہلاک کر دیں گے'),
(1764, 14, 14, 'اور ان کے بعد اس زمین میں تمہیں آباد کر دیں گے یہ اس کے لیے ہے جو میرے سامنے کھڑا ہونے سے ڈرا اور جس نے میرے عذاب سے خوف کھایا'),
(1765, 14, 15, 'اور پیغمبروں نے فیصلہ چاہا اور ہر ایک سرکش ضدی نامراد ہوا'),
(1766, 14, 16, 'اوراس کے پیچھے دوزخ ہے اور اسے پیپ کا پانی پلایا جائے گا'),
(1767, 14, 17, 'جسے گھونٹ گھونٹ پیے گا اور اسے گلےسے نہ اتار سکے گا اور اس پر ہر طرف سے موت آئے گی اور وہ نہیں مرے گا اوراس کے پیچھے سخت عذاب ہوگا'),
(1768, 14, 18, 'ان کی مثال جنہوں نے اپنے رب سے انکار کیا ایسی ہے کہ ان کے اعمال گویا راکھ ہیں کہ جیسی آندھی کے دن ہوا اڑا کر لے گئی ہو جوکچھ انہوں نے کمایا تھا اس میں کچھ بھی ان کے ہاتھ میں نہ رہا ہویہ بھی بڑی دو رکی گمراہی ہے'),
(1769, 14, 19, 'کیا تو نے نہیں دیکھا کہ الله نے آسمانوں اور زمین کو ٹھیک طور پر بنایا اگر وہ چاہے تو تمہیں لے جائے اور نئی مخلوق لے آئے'),
(1770, 14, 20, 'اور یہ الله پر کچھ مشکل نہیں ہے'),
(1771, 14, 21, 'اوریہ سب الله کے سامنے کھڑیں ہوں گے تب کمزور متکبروں سے کہیں گے ہم تو تمہارے تابع تھے سوہمیں الله کے عذاب سے کچھ بچاؤ گے وہ کہیں گے اگر ہمیں الله ہدایت کرتا تو ہم تمہیں ہدایت کرتے اب ہمارے لیے برابر ہے کہ ہم چیخیں چلائیں یا صبر کریں ہمارے بچنے کی کوئی صورت نہیں'),
(1772, 14, 22, 'اور جب فیصلہ ہو چکے گا تو شیطان کہے گا بےشک الله نے تم سے سچا وعدہ کیا تھا اورمیں نے بھی تم سے وعدہ کیا تھا پھر میں نے وعدہ خلافی کی اور میرا تم پر اس کے سوا کوئی زور نہ تھا کہ میں نے تمہیں بلایا پھر تم نے میری بات کو مان لیا پھر مجھے الزام نہ دو اور اپنے آپ کو الزام دونہ میں تمہارا فریادرس ہوں اور نہ تم میرے فریاد رس ہو میں خود تمہارے اس فعل سے بیزار ہوں کہ تم اس سےپہلے مجھے شریک بناتے تھے بے شک ظالموں کے لیے دردناک عذاب ہے'),
(1773, 14, 23, 'اور جو لوگ ایمان لائے تھے اورنیک کام کیے تھے وہ باغوں میں داخل کیے جائیں گے جن کے نیچے نہریں بہتی ہوں گی ان میں اپنے رب کے حکم سے ہمیشہ رہیں گے آپس میں دعائے خیر ان کی سلام ہو گی'),
(1774, 14, 24, 'کیاتو نےنہیں دیکھا کہ الله نے کلمہ پاک کی ایک مثال بیان کی ہے گویا وہ ایک پاک درخت ہے کہ جس کی جڑ مضبوط اور اس کی شاخ آسمان ہے'),
(1775, 14, 25, 'وہ اپنے رب کے حکم سے ہر وقت اپنا پھل لاتا ہے اور الله لوگوں کے واسطے مثالیں بیان کرتا ہے تاکہ وہ سمجھیں'),
(1776, 14, 26, 'اور ناپاک کلمہ کی مثال ایک ناپاک درخت کی سی ہے جو زمین کے اوپر ہی سے اکھاڑ لیا جائے اسے کچھ ٹھیراؤ نہیں ہے'),
(1777, 14, 27, 'الله ایمان والوں کو دنیا اور آخرت کی زندگی میں سچی بات پر ثابت قدم رکھتا ہے اور ظالمو ں کو گمراہ کر تا ہے اور الله جوچاہتا ہے کرتا ہے'),
(1778, 14, 28, 'کیاتو نے انہیں نہیں دیکھا کہ جنہوں نے الله کی نعمت کےبدلے میں ناشکری کی اور اپنی قوم کو تباہی کے گھر میں اتارا'),
(1779, 14, 29, 'جو دوزخ ہے اس میں داخل ہوں گے اور وہ برا ٹھکانا ہے'),
(1780, 14, 30, 'اور لوگو ں نے الله کی راہ سے بہکانے کے لیے شریک بنا رکھے ہیں کہہ دو نفع اٹھا لو پھر تمہیں آگ کی طرف لوٹنا ہے'),
(1781, 14, 31, 'میرے بندوں کو کہہ دو جو ایمان لائے ہیں نماز قائم رکھیں اور ہمارے دیے ہوئے رزق میں سے پوشیدہ اور ظاہر خرچ کریں اس سے پہلے کہ وہ دن آئے جس میں نہ خرید و فروخت ہے نہ دوستی'),
(1782, 14, 32, 'الله وہ ہے جس نے آسمان اور زمین بنائے اور آسمان سے پانی نازل کیا پھر اس سے تمہارے کھانے کو پھل نکالے اور کشتیاں تمہارے تابع کر دیں تاکہ دریا میں اس کے حکم سے چلتی رہیں اور نہریں تمہارے تابع کر دیں'),
(1783, 14, 33, 'اور سورج اور چاند کو تمہارے تابع کر دیا جو ہمیشہ چلنے والے ہیں اور تمہارے لیے رات اور دن کوتابع کیا'),
(1784, 14, 34, 'اور جو چیز تم نے ان سے مانگی اس نے تمہیں دی اور اگر الله کی نعمتیں شمار کرنے لگو تو انہیں شمار نہ کر سکو بے شک انسان بڑا بےانصاف اور ناشکرا ہے'),
(1785, 14, 35, 'اورجس وقت ابراھیم نے کہا اے میرے رب! اس شہر کوامن والا کر دے اور مجھے اور میری اولاد کو بت پرستی سے بچا'),
(1786, 14, 36, 'اے میرے رب! انہوں نے بہت لوگوں کو گمراہ کیا ہے پس جس نے میری پیروی کی وہ تو میرا ہے اورجس نے نافرمانی کی پس تحقیق تو بخشنے والا مہربان ہے'),
(1787, 14, 37, 'اے رب میرے! میں نے اپنی کچھ اولاد ایسےمیدان میں بسائی ہے جہاں کھیتی نہیں تیرے عزت والے گھر کے پاس اے رب ہمارے! تاکہ نماز کو قائم رکھیں پھرکچھ لوگو ں کے دل ان کی طرف مائل کر دے اور انہیں میووں کی روزی دے تاکہ وہ شکر کریں'),
(1788, 14, 38, 'اے رب ہمارے! بے شک تو جانتا ہے جو کچھ ہم چھپاتے ہیں اورجو کچھ ظاہر کرتے ہیں اور الله پر کوئی چیز زمین اورآسمان میں پوشیدہ نہیں'),
(1789, 14, 39, 'الله کا شکر ہے جس نے مجھے اتنی بڑی عمر میں اسماعیل اور اسحاق بخشے بے شک میرا رب دعاؤں کا سننے والا ہے'),
(1790, 14, 40, 'اے میرے رب! مجھے اور میری اولاد کو نماز قائم کرنے والا بنا دے اے ہمارے رب! اورمیری دعا قبول فرما'),
(1791, 14, 41, 'اے ہمارے رب! مجھے اورمیرے ماں باپ کو اور ایمانداروں کو حساب قائم ہونے کے دن بخش دے'),
(1792, 14, 42, 'تو ہر گز خیال نہ کر کہ الله ان کاموں سے بے خبر ہے جو ظالم کرتے ہیں انہیں صرف اس دن تک مہلت دے رکھی ہے جس میں نگاہیں پھٹی رہ جائیں گی'),
(1793, 14, 43, 'وہ سرا اٹھائے ہوئے دوڑتے چلے جارہے ہوں گے کہ ا ن کی نظر ان کی طرف ہٹ کرنہیں آوے گی اور ان کے دل اڑ گئے ہوں گے'),
(1794, 14, 44, 'اور لوگوں کو اس دن سے ڈراوے کہ ان پر عذاب آئے گا تب ظالم کہیں گے اے رب ہمارے! ہمیں تھوڑی مدت تک مہلت دے کہ ہم تیرا بلانا قبول کر لیں اور رسولوں کی پیروی کر لیں کیاتم نے پہلے قسم نہیں کھائی تھی کہ تمہیں کہیں جانا ہی نہیں ہے'),
(1795, 14, 45, 'اور تم انہیں لوگو ں کی بستیوں میں آباد تھے جنہوں نے اپنی جانوں پر ظلم کیا تھا اور تمہیں معلوم ہو چکا تھا کہ ہم نے ان کے ساتھ کیا کیا تھا او رہم نے تمہیں سب قصے بتلائے تھے'),
(1796, 14, 46, 'اور ان لوگو ں نے اپنی تدبیریں کی تھیں اوران کی تدبیریں الله کے سامنے تھیں اگرچہ ان کی تدبیریں ایسی تھی کہ ان سے پہاڑ بھی ٹل جائیں'),
(1797, 14, 47, 'پس الله کو اپنے رسولوں سے وعدہ خلافی کرنے والا خیال نہ کریں بے شک الله زبردست بدلہ لینے والا ہے'),
(1798, 14, 48, 'جس دن اس زمین میں سے اور زمین بدلی جائے گی اور آسمان بدلے جاویں گے اور سب کے سب ایک زبردست الله کے روبرو پیش ہوں گے'),
(1799, 14, 49, 'اورتو اس دن گناہگاروں کو زنجیروں میں جکڑے ہوئے دیکھے گا'),
(1800, 14, 50, 'کرتےان کے گندھک کے ہوں گے اوران کے چہرو ں پر آگ لپٹی ہو گی'),
(1801, 14, 51, 'تاکہ الله ہر شخص کو اس کے کیے کی سزا دے بے شک الله بڑی جلدی حساب لینے والا ہے'),
(1802, 14, 52, 'یہ قرآن لوگو ں کے لیے اعلان ہے اور تاکہ اس کے ذریعے سے لوگوں کو ڈرایا جائے اور تاکہ وہ معلوم کر لیں کہ وہی ایک معبود ہے اور تاکہ عقلمند نصیحت حاصل کریں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'یہ آیتیں کتاب کی ہیں اور قرآن واضح کی'),
(1804, 15, 2, 'کافر بڑی حسرت کریں گے کہ کاش وہ مسلمان ہو جاتے'),
(1805, 15, 3, 'انہیں چھوڑ دو کھا لیں اور فائدہ اٹھا لیں اور انہیں آرزو بھلائے رکھے سو آئندہ معلوم کر لیں گے'),
(1806, 15, 4, 'اور ہم نے جتنی بستیاں ہلاک کی ہیں ان سب کے لیے ایک مقرر وقت لکھا ہوا تھا'),
(1807, 15, 5, 'کوئی قوم اپنے وقت مقرر سے نہ پہلے ہلاک ہوئی ہے نہ پیچھے رہی ہے'),
(1808, 15, 6, 'اور انہوں نے کہا اے وہ شخص جس پر قرآن نازل کیا گیا ہے بے شک تو مجنون ہے'),
(1809, 15, 7, 'اگر تم سچے ہو تو ہمارے پاس فرشتوں کو کیوں نہیں لاتے'),
(1810, 15, 8, 'ہم فرشتہ تو فیصلہ ہی کے لیے بھیجا کرتے ہیں اور اس وقت انہیں مہلت نہیں ملے گی'),
(1811, 15, 9, 'ہم نے یہ نصیحت اتار دی ہے اور بے شک ہم اس کے نگہبان ہیں'),
(1812, 15, 10, 'اور تجھ سے پہلے ہم پہلی قوموں میں بھی رسول بھیج چکے ہیں'),
(1813, 15, 11, 'اور وہ بھی جب کوئی رسول ان کے پاس آتا ہے تو اس سے ٹھٹھا ہی کرتے'),
(1814, 15, 12, 'اسی طرح ہم یہ ٹھٹھا ان مجرمو ں کے دلوں میں ڈال دیتے ہیں'),
(1815, 15, 13, 'یہ لوگ قرآن پر ایمان نہیں لاتے اور یہ پہلوں کا دستور چلا آیا ہے'),
(1816, 15, 14, 'اور اگر ہم ان پر آسمان سے دروازہ کھول دیں پھراس میں سے چڑھ جائیں'),
(1817, 15, 15, 'البتہ کہیں گے کہ ہماری نظر بندی کر دی گئی تھی بلکہ ہم پر جادو کیا گیا ہے'),
(1818, 15, 16, 'او رالبتہ تحقیق ہم نے آسمان پر برج بنائے ہیں اور دیکھنے والو ں کی نطر میں اسے رونق دی ہے'),
(1819, 15, 17, 'اور ہم نے اسے ہرشیطان مردود سے محفوظ رکھا'),
(1820, 15, 18, 'مگر جس نے چوری سے سن لیا تو اس کے پیچھے چمکتا ہوا انگارہ پڑا'),
(1821, 15, 19, 'اور ہم نے زمین کو پھیلایا اور اس پر پہاڑ رکھ دیے اوراس میں ہر چیز اندازے سے اگائی'),
(1822, 15, 20, 'اوراس میں تمہارے لیے روزی کے اسباب بنا دیئے اوران کے لیے بھی جنہیں تم روزی دینے والے نہیں ہو'),
(1823, 15, 21, 'او رہر چیز کے ہمارے پاس خزانے ہیں اورہم صرف اسے اندازہ معین پر نازل کرتے ہیں'),
(1824, 15, 22, 'اور ہم نے بادل اٹھانے والی ہوائیں بھیجیں پھر ہم نے آسمان سے پانی نازل کیا پھر وہ تمہیں پلایا اور تمہارے پاس اس کا خزانہ نہیں ہے'),
(1825, 15, 23, 'اور بے شک ہم ہی زندہ کرتے اور مارتے ہیں اورا خیر مالک بھی ہم ہی ہیں'),
(1826, 15, 24, 'اور ہمیں تم میں سے اگلے او رپچھلے سب معلوم کر لیں'),
(1827, 15, 25, 'اور بے شک تیرا رب ہی انہیں جمع کرے گا بے شک وہ حکمت والا خبردار ہے'),
(1828, 15, 26, 'اور البتہ تحقیق ہم نے انسان کو بجتی ہوئی مٹی سے جو سڑے ہوئے گارے سے تھی پیدا کیا'),
(1829, 15, 27, 'اور ہم نے اس سے پہلے جنوں کو آگ کے شعلے سے بنایا تھا'),
(1830, 15, 28, 'اور جب تیرے رب نے فرشتوں سے کہا کہ میں ایک بشر کو بجتی ہوئی مٹی سے جو کےسڑے ہوئے گارے کی ہو گی پیدا کرنے والا ہوں'),
(1831, 15, 29, 'پھر جب میں اسے ٹھیک بنالوں اور اس میں اپنی روح پھونک دوں تو تم اس کے آگے سجدہ میں گر پڑنا'),
(1832, 15, 30, 'پھر سب کے سب فرشتوں نے سجدہ کیا'),
(1833, 15, 31, 'مگر ابلیس نے انکار کیا کہ سجدہ کرنے والوں کے ساتھ ہو'),
(1834, 15, 32, 'فرمایا اے ابلیس! تجھے کیا ہوا کہ سجدہ کرنے والوں کے ساتھ نہ ہوا'),
(1835, 15, 33, 'کہا میں ایسا نہ تھا کہ ایک ایسے بشر کو سجدہ کروں جسے تو نے بجتی ہوئی مٹی سے جو سڑے ہوئے گارے کی تھی پیدا کیا ہے'),
(1836, 15, 34, 'کہا تو آسمان سے نکل جا بے شک تو مردود ہو گیا'),
(1837, 15, 35, 'اور بے شک تجھ پر قیامت کے دن تک لعنت رہے گی'),
(1838, 15, 36, 'کہا اے میرے رب! تو پھر مجھے قیامت کے دن تک مہلت دے'),
(1839, 15, 37, 'فرمایا بےشک تجھے مہلت ہے'),
(1840, 15, 38, 'وقت معلوم کے دن تک'),
(1841, 15, 39, 'کہا اے میرے رب! جیسا تو نے مجھے گمراہ لیا ہے البتہ ضرور ضرور میں زمین میں انہیں ان کے گناہوں کو مرغوب کر کے دکھاؤں گا اور ان سب کو گمراہ کروں گا'),
(1842, 15, 40, 'سوائے تیرے ان بندوں کے جو ان میں مخلص ہو ں گے'),
(1843, 15, 41, 'فرمایا یہ راستہ مجھ پر سیدھا ہے'),
(1844, 15, 42, 'بے شک میرے بندوں پر تیرا کچھ بھی بس نہیں چلے گا مگر جو گمراہوں میں سے تیرا تابعدار ہوا'),
(1845, 15, 43, 'اور بے شک ان سب کا وعدہ دوزخ پر ہے'),
(1846, 15, 44, 'اس کے سات دروازے ہیں ہر دروازے کے لیے ان کے الگ الگ حصے ہیں'),
(1847, 15, 45, 'بے شک پرہیزگار باغوں اور چشموں میں رہیں گے'),
(1848, 15, 46, 'ان باغوں میں سلامتی اور امن سے جا کر رہو'),
(1849, 15, 47, 'اور ان کے دلوں میں جو کینہ تھا ہم وہ سب دور کر دیں گے سب بھائی بھائی ہوں گے تختوں پر آمنے سامنے بیٹھنے والے ہوں گے'),
(1850, 15, 48, 'انہیں وہاں کوئی تکلیف نہیں پہنچے گی اور نہ وہ وہاں سے نکالے جائیں گے'),
(1851, 15, 49, 'میرے بندوں کو اطلاع دے کہ بےشک میں بحشنے والا مہربان ہوں'),
(1852, 15, 50, 'اور بے شک میرا عذاب وہی دردناک عذاب ہے'),
(1853, 15, 51, 'اور انہیں ابراھیم کے مہمانوں کا حال سنا دو'),
(1854, 15, 52, 'جب اس کے گھر میں داخل ہوئے اور کہا سلام اس نے کہا بے شک ہمیں تم سے ڈر معلوم ہوتا ہے'),
(1855, 15, 53, 'کہا ڈرو مت بے شک ہم تمہیں ایک دن لڑکے کی خوشخبری سناتے ہیں'),
(1856, 15, 54, 'کہا مجھے اب بڑھاپے میں خوشخبری سناتے ہو سو کس چیز کی خوشخبری سناتے ہو'),
(1857, 15, 55, 'انہوں نے کہا ہم نے تمہیں بھی سچی خوشخبری سنائی ہے سو تو نا امید نہ ہو'),
(1858, 15, 56, 'کہا اپنے رب کی رحمت سے نا امید تو گمراہ لوگ ہی ہوا کرتے ہیں'),
(1859, 15, 57, 'کہا اے فرشتو! پھر تمہارا کیا مقصد ہے'),
(1860, 15, 58, 'انہوں نے کہا ہم ایک نافرمان قوم کی طرف بھیجے گئے ہیں'),
(1861, 15, 59, 'مگر لوط کے گھر والے کہ ہم ان سب کو بچا لیں گے'),
(1862, 15, 60, 'مگر اس کی بیوی ہم نے فیصلہ کیا ہے کہ وہ پیچھے رہنے والو ں میں سے ہے'),
(1863, 15, 61, 'پھر جب لوط کے گھر فرشتے پہنچے'),
(1864, 15, 62, 'کہا بے شک تم اجنبی لوگ ہو'),
(1865, 15, 63, 'انہوں نے کہا بلکہ ہم تیرے پاس وہ چیز لے کر آئے ہیں جس میں وہ جھگڑتے تھے'),
(1866, 15, 64, 'او رہم تیرے پاس پکی بات لائے ہیں اوربے شک ہم سچ کہتے ہیں'),
(1867, 15, 65, 'پس تم اپنے گھر والوں کو کچھ رات رہے لے نکلو اور تو ان کے پیچھے چل اور تم میں سے کوئی مڑ کر نہ دیکھے اور چلے جاؤ جہاں تمہیں حکم ہے'),
(1868, 15, 66, 'اور ہم نے لوط کو قطعی طور پر یہ بات واضح کر دی تھی کہ صبح ہوتے ہی ان کی جڑ کاٹ دی جائے گی'),
(1869, 15, 67, 'اور شہر والے خوشیاں کرتے ہوئے آئے'),
(1870, 15, 68, 'لوط نے کہا یہ لوگ میرے مہمان ہیں سو مجھے ذلیل نہ کرو'),
(1871, 15, 69, 'اور الله سے ڈرو اور مجھے بے آبرو نہ کرو'),
(1872, 15, 70, 'انہوں نے کہا کیاہم نے تمہیں دنیا بھر کی حمایت سے منع نہیں کیا ہے'),
(1873, 15, 71, 'کہا یہ میری بیٹیاں حاضر ہیں اگر تم کرنے والے ہو'),
(1874, 15, 72, 'تیری جان کی قسم ہے وہ اپنی مستی میں اندھے ہو رہے تھے'),
(1875, 15, 73, 'پھر دن نکلتے ہی انہیں ہولناک آواز نے آ لیا'),
(1876, 15, 74, 'پھر ہم نے ان بستیوں کو زیرو زبر کر دیا اور ان پر کنکر کے پتھر برسائے'),
(1877, 15, 75, 'بے شک اس واقعہ میں اہلِ بصیرت کے لیے نشانیاں ہیں'),
(1878, 15, 76, 'اور بے شک یہ بستیاں سیدھے راستے پر واقع ہیں'),
(1879, 15, 77, 'بے شک اس میں ایمانداروں کے لیے نشانیاں ہیں'),
(1880, 15, 78, 'اوربن کے لوگ بھی بدکار تھے'),
(1881, 15, 79, 'پھر ہم نےان سےبھی بدلہ لیا اور یہ دونوں بستیاں کھلے راستہ پر واقع ہیں'),
(1882, 15, 80, 'او ربے شک حجر والوں نے رسولوں کو جھٹلایا تھا'),
(1883, 15, 81, 'اور ہم نے انہیں اپنی نشانیاں بھی دی تھیں پر وہ ان سے روگردانی کرتے تھے'),
(1884, 15, 82, 'او وہ لوگ پہاڑوں کو تراش کر گھر بناتے تھے کہ امن میں رہیں'),
(1885, 15, 83, 'پھر انہیں صبح کے وقت سخت آواز نے آ پکڑا'),
(1886, 15, 84, 'پھر ان کے دنیاوی ہنر ان کے کچھ بھی کام نہ آئے'),
(1887, 15, 85, 'اور ہم نے آسمانوں اور زمین اور ان کی درمیانی چیزوں کو بغیر حکمت کے پیدا نہیں کیا اور قیامت ضرور آنے والی ہے پر تو ان سے خوش خلقی کے ساتھ کنارہ کر'),
(1888, 15, 86, 'بے شک تیار رب وہی پیدا کرنے والا جاننے والا ہے'),
(1889, 15, 87, 'اور ہم نے تمہیں سات آیتیں دیں جو (نماز میں) دہرائی جاتی ہیں اور قرآن عظمت والا دیا'),
(1890, 15, 88, 'اور تو اپنی آنکھ اٹھا کر بھی ان چیزوں کو نہ دیکھ جو ہم نے مختلف قسم کے کافروں کو استعمال کے لیے دے رکھی ہیں اور ان پر غم نہ کر اور اپنے بازو ایمان والوں کے لیے جھکا دے'),
(1891, 15, 89, 'اور کہہ دو بے شک میں کھلم کھلا ڈرانے والا ہوں'),
(1892, 15, 90, 'جیسا ہم نے (عذاب) ان بانٹنے والو ں پر بھیجا ہے'),
(1893, 15, 91, 'جنہوں نے قرآن کو ٹکڑے ٹکڑے کیا ہے'),
(1894, 15, 92, 'پھر تیرے رب کی قسم ہے البتہ ہم ان سب سے سوال کریں گے'),
(1895, 15, 93, 'اس چیز سے کہ وہ کرتے تھے'),
(1896, 15, 94, 'سو تو کھول کر سنا دے جو تجھے حکم دیا گیا ہے اور مشرکوں کی پروا نہ کر'),
(1897, 15, 95, 'بے شک ہم تیری طرف سے ٹھٹھا کرنے والوں کے لیے کافی ہیں'),
(1898, 15, 96, 'اور جو الله کے ساتھ دوسرا خدا مقرر کرتے ہیں سو عنقریب معلوم کر لیں گے'),
(1899, 15, 97, 'اور ہم جانتے ہیں کہ تیرا دل ان باتوں سے تنگ ہوتا ہے جو وہ کہتے ہیں'),
(1900, 15, 98, 'سو تو اپنے رب کی تسبیح حمد کے ساتھ کیے جا اور سجدہ کرنے والوں میں سے ہو'),
(1901, 15, 99, 'اور اپنے رب کی عبادت کرتے رہو یہاں تک کہ تمہیں موت آجائے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'الله کا حکم آ پہنچا تم اس میں جلدی مت کرو وہ لوگو ں کے شرک سے پاک اور برتر ہے'),
(1903, 16, 2, 'وہ اپنے بندوں سے جس کے پاس چاہتا ہے فرشتوں کو وحی دے کر بھیج دیتا ہے یہ کہ خبردار کر دو کہ میرے سوا کوئی عبادت کے لائق نہیں پس مجھ سے ڈرتے رہو'),
(1904, 16, 3, 'اسی نے آسمان اور زمین کو ٹھیک طور پر بنایا ہے وہ ان کے شرک سے پاک ہے'),
(1905, 16, 4, 'اسی نے آدمی کو ایک بوند سے پیدا کیا پھر وہ یکایک کھلم کھلا جھگڑنے لگا'),
(1906, 16, 5, 'اور تمہارے واسطے چار پایوں کو بھی اسی نے بنایا ان میں تمہارے لیے جاڑے کابھی سامان ہے اوربھی بہت سے فائدے ہیں اور ان میں سے کھاتے بھی ہو'),
(1907, 16, 6, 'اور تمہاے لیے ان میں زینت بھی ہے جب شام کو چرا کر لاتے ہو اور جب چرانے لے جاتے ہو'),
(1908, 16, 7, 'اور وہ تمہارے بوجھ اٹھا کر ان شہروں تک لے جاتے ہیں کہ جہاں تک تم جان کو تکلیف میں ڈالنے کے سوا نہیں پہنچ سکتے تھے بے شک تمہارا رب شفقت کرنے والا مہربان ہے'),
(1909, 16, 8, 'اور گھوڑے خچر اور گدھے پیدا کیے کہ ان پر سوار ہو اور زینت کے لیے اور وہ چیزیں پیدا کرتا ہے جو تم نہیں جانتے'),
(1910, 16, 9, 'اور الله تک سیدھی راہ پہنچتی ہے اور بعض ان میں ٹیڑھی بھی ہیں اور اگر الله چاہتا تو تم سب کو سیدھی راہ بھی دکھا دیتا'),
(1911, 16, 10, 'وہی ہے جس نے آسمان سے تمہارے لیے پانی نازل کیا اسی میں سے پیتے ہو اور اسی سے درخت ہوتے ہیں جن میں چراتے ہو'),
(1912, 16, 11, 'تمہارے واسطے اسی سے کھیتی اور زیتوں اورکھجوریں اورانگور اور ہر قسم کے میوے اگاتا ہے بے شک اس میں ان لوگو ں کے لیے نشانی ہے جو غور کرتے ہیں'),
(1913, 16, 12, 'اور رات اور دن اور سورج اور چاند کو تمہارے کام میں لگا دیا ہے اور اسی کے حکم سے ستارے بھی کام میں لگے ہوئے ہیں بے شک اس میں لوگوں کے لیے نشانیاں ہیں جو سمجھ رکھتے ہیں'),
(1914, 16, 13, 'اور تمہارے واسطے جو چیزیں زمین میں رنگ برنگ کی پھیلائی ہیں ان میں لوگوں کے لیے نشانی ہے ہے جو سوچتے ہیں'),
(1915, 16, 14, 'اور وہ وہی ہے جس نے دریا کو کام میں لگا دیا کہ اس میں تازہ گوشت کھاؤ اور اسی سے زیور نکالو جسے تم پہنتے ہو اور تو اس میں جہازوں کو دیکھتا ہے کہ پانی کو چیرتے ہوئے چلے جاتے ہیں اور تاکہ تم اس کے فضل کو تلاش کرو اور تاکہ تم شکر کرو'),
(1916, 16, 15, 'اور زمین پر پہاڑوں کے بوجھ ڈال دیے تاکہ تمہیں لے کر نہ ڈگمگائے اور تمہارے لیے نہریں اور راستے بنا دیے تاکہ تم راہ پاؤ'),
(1917, 16, 16, 'اور نشانیاں بنائیں اور ستاروں سے لوگ راہ پاتے ہیں'),
(1918, 16, 17, 'پھر کیا جو شخص پیدا کرے اس کے برابر ہے جو کچھ بھی پیدا نہ کرے کیا تم سوچتے نہیں'),
(1919, 16, 18, 'اور اگر تم الله کی نعمتو ں کو گننے لگو تو ان کا شمار نہیں کر سکو گےبے شک الله بخشنے والا مہربان ہے'),
(1920, 16, 19, 'اور الله جانتا ہے جو تم چھپاتے ہو اور جو تم ظاہر کرتےہو'),
(1921, 16, 20, 'اور جنہیں الله کے سوا پکارتے ہیں وہ کچھ بھی پیدانہیں کرتے اور وہ خود پیدا کیے ہوئے ہیں'),
(1922, 16, 21, 'وہ تو مردے ہیں جن میں جان نہیں اور وہ نہیں جانتے کہ لوگ کب اٹھائے جائیں گے'),
(1923, 16, 22, 'تمہارا معبود اکیلا معبود ہے پھر جو آخرت پر ایمان نہیں رکھتے ان کے دل نہیں مانتے اور وہ تکبر کرنے والے ہیں'),
(1924, 16, 23, 'ضرور الله جانتا ہے جو کچھ چھپاتے ہیں اور جو کچھ ظاہر کرتے ہیں بے شک وہ غرور کرنے والوں کو پسند نہیں کرتا'),
(1925, 16, 24, 'اور جب ان سے کہا جائے کہ تمہارے رب نے کیا نازل کیا ہے کہتے ہیں پہلے لوگوں کے قصے ہیں تاکہ قیامت کے دن اپنے بوجھ پورے اٹھائيں اورکچھ ان کے بوجھ جنہیں بے علمی سے گمراہ کرتے ہیں خبردار برا بوجھ ہے جواٹھاتے ہیں'),
(1926, 16, 25, 'ان سے پہلےلوگوں نے بھی مکر کیا تھا پھر الله نے ان کی عمارت کو جڑوں سے ڈھا دیا پھر ان پر اوپر سے چھت گر پڑی اور ان پر عذاب آیا جہاں سے انہیں خبر بھی نہ تھی'),
(1927, 16, 26, 'پھر قیامت کے دن انہیں رسوا کرے گا اور کہے گا میرے شریک کہاں ہیں جن پر تمہیں بڑی ضد تھی جنہیں علم دیا گیا تھا وہ کہیں گے کہ بے شک آج کافروں کے لیے رسوائی اور برائی ہے'),
(1928, 16, 27, 'یہ وہ لوگ ہیں کہ فرشتوں نے ان کی ایسی حالت میں روح نکالی تھی کہ وہ اپنے آپ پر ظلم کر رہے تھے'),
(1929, 16, 28, 'پھر وہ صلح کا پیغام بھیجیں گے کہ ہم تو کوئی برا کام نہ کرتے تھے کیوں نہیں بے شک الله کو تمہارے اعمال کی پوری خبر ہے'),
(1930, 16, 29, 'سو دوزخ کے دروازوں میں داخل ہو جاؤ اس میں ہمیشہ رہو پس متکبرین کا کیا ہی برا ٹھکانہ ہے'),
(1931, 16, 30, 'اور پرہیزگاروں سے کہا جاتا ہے کہ تمہارے رب نے کیا نازل کیا ہے تو کہتے ہیں اچھی چیز جنہوں نے نیکی کی ہے (ان کے لیے) اس دنیا میں بھی بہتری ہے اور البتہ آخرت کا گھر تو بہت ہی بہتر ہے اور پرہیزگاروں کا کیا ہی اچھا گھر ہے'),
(1932, 16, 31, 'ہمیشہ رہنے کے باغ ہیں جن میں وہ داخل ہوں گے اور ان کے نیچے نہریں بہتی ہوں گی جو چاہیں گے انہیں وہاں ملے گا الله پرہیزگاروں کو ایسا ہی بدلہ دے گا'),
(1933, 16, 32, 'جن کی جان فرشتے قبض کرتے ہیں ایسے حال میں کہ وہ پاک ہیں فرشتے کہیں گے تم پر سلامتی ہو بہشت میں داخل ہو جاؤ بسبب ان کاموں کے جو تم کرتے تھے'),
(1934, 16, 33, 'کیا اب اس کے منتظر ہیں کہ ان پر فرشتے آویں یا تیرے رب کا حکم آئے اسی طرح ان سے پہلوں نے بھی کیا تھا اور الله نے ان پر ظلم نہیں کیا اور لیکن وہ اپنے نفسوں پر ظلم کرتے تھے'),
(1935, 16, 34, 'پھر انہیں ان کے بد اعمال کے نتیجے مل کر رہے اورجس کی وہ ہنسی اڑایا کرتے تھے وہی ان پر نازل ہوا'),
(1936, 16, 35, 'اور مشرک کہتے ہیں اگر الله چاہتا تو ہم اس کے سوا کسی چیز کی عبادت نہ کرتے اور نہ ہمارے باپ دادا اور اس کے حکم کے سوا ہم کسی چیز کو حرام نہ ٹھیراتے اسی طرح کیا ان لوگو ں نے جو ان سے پہلے تھے پھر رسولوں کے ذمہ تو صرف صاف پہنچا دینا ہے'),
(1937, 16, 36, 'اور البتہ تحقیق ہم نے ہر امت میں یہ پیغام دے کر رسول بھیجا کہ الله کی عبادت کرو اور شیطان سے بچو پھر ان میں سے بعض کو الله نے ہدایت دی اوربعض پر گمراہی ثابت ہوئی پھر ملک میں پھر کر دیکھو کہ جھٹلانے والوں کا انجام کیا ہوا'),
(1938, 16, 37, 'اگر تو انہیں ہدایت پرلانے کی طمع کرے تو الله ہدایت نہیں دیتا اس شخص کو جسے گمراہ کر دے اور نہ ان کے لیے کوئی مددگار ہوگا'),
(1939, 16, 38, 'اور الله کی سخت قسمیں کھا کر کہتے ہیں کہ الله انہیں اٹھائے گا اس شخص کو جو مر جائے گا ہاں اس نے اپنے ذمہ پکا وعدہ کر لیا ہے لیکن بہت سے لوگ نہیں جانتے'),
(1940, 16, 39, 'تاکہ ان پر ظاہر کر دے وہ بات جس میں یہ جھگڑتے ہیں اور تاکہ کافر معلوم کر لیں کہ وہ جھوٹے تھے'),
(1941, 16, 40, 'ہم جس کام کےکرنے کا ارادہ کرتے ہیں تو اس کے لیے ہمارا اتنا ہی کہنا کافی ہے کہ ہم اسے کہہ دیں کہ ہو جا پھر ہو جاتا ہے'),
(1942, 16, 41, 'اورجنہوں نے الله کے واسطے گھر چھوڑا اس کےبعد ان پر ظلم کیا گیا تھا تو البتہ ہم نے انہیں دنیا میں اچھی جگہ دیں گے اور آخرت کا ثواب تو بہت ہی بڑا ہے کاش یہ لوگ سمجھ جاتے'),
(1943, 16, 42, 'جو لوگ ثابت قدم رہے اور اپنے رب پر بھروسہ کیا'),
(1944, 16, 43, 'اور ہم نے تجھ سے پہلے بھی تو انسان ہی بھیجے تھے جن کی طرف ہم وحی بھیجا کرتے تھے سو اگر تمہیں معلوم نہیں تو اہلِ علم سے پوچھ لو'),
(1945, 16, 44, 'ہم نے انہیں معجزات اور کتابیں دے کر بھیجا تھا اور ہم نے تیری طرف قرآن نازل کیا تاکہ لوگو ں کے لیے واضح کر دے جو ان کی طرف نازل کیا گیا ہے اور تاکہ وہ سوچ لیں'),
(1946, 16, 45, 'پس کیا وہ لوگ نڈر ہو گئے ہیں جو برے فریب کرتے ہیں اس سے کہ الله انہیں زمین میں دھنسا دے یاان پر عذاب آئے جہاں سے انہیں خبر بھی نہ ہو'),
(1947, 16, 46, 'یا انہیں چلتے پھرتے پکڑے پس وہ عاجز کرنے والے نہیں ہیں'),
(1948, 16, 47, 'یا انہیں ڈرانے کے بعد پکڑے پس تحقیق تمہارا رب نہایت ہی شفیق رحم کرنے والا ہے'),
(1949, 16, 48, 'کیا وہ الله کی پیدا کی ہوئی چیزوں کونہیں دیکھتے کہ کے سائے دائیں اوربائیں طرف جھکے جارہے ہیں اور نہایت عاجزی کے ساتھ الله کو سجدہ کررہے ہیں'),
(1950, 16, 49, 'اور جو آسمان میں ہے اور جو زمین میں ہے جانداروں سے اور فرشتے سب الله ہی کو سجدہ کرتے ہیں اوروہ تکبرنہیں کرتے'),
(1951, 16, 50, 'وہ اپنے بالادست رب سے ڈرتے ہیں اور انہیں جو حکم دیا جاتا ہے وہ بجا لاتے ہیں'),
(1952, 16, 51, 'الله نے کہا ہے دو معبود نہ بناؤ وہ ایک ہی معبود ہے پھر مجھ ہی سے ڈرو'),
(1953, 16, 52, 'اوراسی کا ہے جوکچھ آسمانوں اور زمین میں ہے اور عبادت اسی کی لازم ہے پھرکیا الله کے سوا اوروں سے ڈرتے ہو'),
(1954, 16, 53, 'اور تمہارے پاس جو نعمت بھی ہے سو الله کی طرف سے ہے پھر جب تمہیں تکلیف پہنچتی ہے تواسی سے فریاد کرتے ہو'),
(1955, 16, 54, 'پھر جب تم سے تکلیف دور کر دیتا ہےتو فوراً تم میں سے ایک جماعت اپنے رب کے ساتھ شریک بنانے لگتی ہے'),
(1956, 16, 55, 'تاکہ جو نعمتیں ہم نےانہیں دی تھیں ان کی ناشکری کریں خیر نفع اٹھالو آ گے چل کر معلوم کر لو گے'),
(1957, 16, 56, 'اور جنہیں وہ جانتے بھی نہیں ان کے لیے ہماری دی ہوئی چیزوں میں سے ایک حصہ مقرر کرتے ہیں الله کی قسم البتہ تم سے ان بہتانوں کی ضرور باز پرس ہوگی'),
(1958, 16, 57, 'اور الله کے لیے بیٹیاں ٹھیراتے ہیں وہ اس سے پاک ہے اور اپنے لیے جو دل چاہتا ہے'),
(1959, 16, 58, 'اور جب ان میں سے کسی کی بیٹی کی خوشخبری دی جائے اس کا منہ سیاہ ہو جاتا ہے اور وہ غمگین ہوتا ہے'),
(1960, 16, 59, 'اس خوشخبری کی برائی باعث لوگوں سے چھپتا پھرتا ہے آیا اسے ذلت قبول کر کے رہنے دے یااس کو مٹی میں دفن کر دے دیکھو کیا ہی برا فیصلہ کرتے ہیں'),
(1961, 16, 60, 'جو آخرت کو نہیں مانتے ان کی بری مثال ہے اور الله کی شان سب سے بلند ہے اور وہی زبردست حکمت والا ہے'),
(1962, 16, 61, 'اور اگر الله لوگوں کوانکی بے انصافی پر پکڑے تو زمین پر کسی جاندار کو نہ چھوڑے لیکن ایک مدت مقرر تک انہیں مہلت دیتا ہے پھر جب ان کا وقت آتا ہے تو نہ ایک گھڑی پیچھے ہٹ سکتے ہیں اورنہ آگے بڑھ سکتے ہیں'),
(1963, 16, 62, 'اور اللهکے لیے وہ چیزیں تجویز کرتے ہیں جنہیں آپ بھی پسند نہیں کرتے اورزبان سے جھوٹے دعوے کرتے ہیں کہ آخرت کی بھلائی انہیں کے لیے ہے اسمیں شک نہیں کہ ان کے لیے آگ ہے اور بے شک وہ سب سے پہلے دوزخ میں بھیجے جائیں گے'),
(1964, 16, 63, 'الله کی قسم ہے! ہم نے تجھ سے پہلے بھی قوموں میں رسول بھیجے تھے پھر شیطان نے لوگوں کو ان کی بداعمالیاں اچھی کر دکھائیں سو آج بھی ان کا وہی دوست ہے اور ان کے لیے دردناک عذاب ہے'),
(1965, 16, 64, 'اور ہم نے اسی لیے تجھ پر کتاب اتاری ہے کہ تو انہیں وہ چیز کھول کر سنا دے جس میں وہ جھگڑ رہے ہیں اور ایمانداروں کے لیے ہدایت اور رحمت بھی ہے'),
(1966, 16, 65, 'اور الله نے آسمان سے پانی اتارا پھراس سے مردہ زمین کو زندہ کر دیا اس میں ان لوگو ں کے لیے نشانی ہے جو سنتے ہیں'),
(1967, 16, 66, 'اوربے شک تمہارے لیے چارپایوں میں سوچنے کی جگہ ہے ہم نےان کے جسم سے خون او رگوبر کے درمیان خالص دودھ پیدا کردیتے ہیں جو پینے والوں کے لیے خوشگوار ہے'),
(1968, 16, 67, 'اور کھجور اور انگور کے پھلوں سے نشہ اور اچھی غذا بھی بناتے ہو اس میں لوگوں کے لیے نشانی ہے جو سمجھتے ہیں'),
(1969, 16, 68, 'اور تیرے رب نے شہد کی مکھی کو حکم دیا کہ پہاڑوں میں اور درختوں میں اور ان چھتوں میں گھر بنائے جو اس کے لیے بناتے ہیں'),
(1970, 16, 69, 'پھر ہر قسم کے میوں سے کھا پھر اپنے رب کی تجویز کردہ آسان راہوں پر چل ان کے پیٹ سے پینے کی چیز نکلتی ہے جس کے رنگ مختلف ہیں اس میں لوگوں کے لیے شفا ہے بے شک اس میں ان لوگو ں کے لیے نشانی ہے جو سوچتے ہیں'),
(1971, 16, 70, 'اور الله نے تمہیں پیدا کیا پھر وہی تمہیں مارتاہے اور کوئی تم میں سے نکمی عمر تک پہنچایا جاتا ہے جو سمجھ دار ہونے کے بعد نادان ہو جاتا ہے بےشک الله جاننے والا قدرت والا ہے'),
(1972, 16, 71, 'اور الله نے تم میں سے بعض کو بعض پر روزی میں فضیلت دی پھر جنہیں فضیلت دی گئی وہ اپنے حصہ کا مال اپنے غلاموں کو دینےو الے نہیں کہ وہ اس میں برابر ہو جائیں پھر کیا الله کی نعمت کا انکار کرتے ہیں'),
(1973, 16, 72, 'اور الله نے تمہارے واسطے تمہاری ہی قسم سے عورتیں پیدا کیں اور تمہیں تمہاری عورتوں سے بیٹے اور پوتے دیئے اور تمہیں کھانے کے لیے اچھی چیزیں دیں پھرکیا جھوٹی باتیں تو مان لیتے ہیں اور الله کی نعمتوں کا انکار کرتے ہیں'),
(1974, 16, 73, 'اور الله کے سوا ان کی عبادت کرتے ہیں جو آسمانوں ارو زمین سے انہیں رزق پہنچانے میں کچھ بھی اختیارنہیں رکھتے اور نہ رکھ سکتے ہیں'),
(1975, 16, 74, 'پس الله کے لئے مثالیں نہ گھڑو بے شک الله جانتا ہے اور تم نہیں جانتے'),
(1976, 16, 75, 'الله ایک مثال بیان فرماتا ہے ایک غلام ہے کسی دوسرے کی ملک میں جو کسی چیز کا اختیارنہیں رکھتا اور ایک دوسرا آدمی ہے جسے ہم نے اچھی روزی دے رکھی ہے اور وہ ظاہر اور پوشیدہ اسے خرچ کرتا ہے کیا دونوں برابر ہیں سب تعریف الله کے لیے ہے مگر اکثر ان میں سے نہیں جانتے'),
(1977, 16, 76, 'اور الله ایک او رمثال دو آدمیوں کی بیان فرماتا ہے ایک ان میں سے گونگا ہے کچھ بھی نہیں کر سکتااور اپنے آقا پر ایک بوجھ ہے جہاں کہیں اسے بھیجے اس سے کوئی خوبی کی بات بن نہ آئے کیا یہ اور وہ برابر ہے جو لوگوں کو انصاف کا حکم دیتا ہے اور وہ خود بھی سیدھے راستے پر قائم ہے'),
(1978, 16, 77, 'اور آسمانوں اور زمین کی پوشیدہ باتیں تو الله ہی کو معلوم ہیں اور قیامت کا معاملہ تو ایسا ہے جیسا آنکھ کا جھپکنا یا اس سےبھی قریب تر بے شک الله ہر چیز پر قادر ہے'),
(1979, 16, 78, 'اور الله نے تمہیں تمہاری ماؤں کے پیٹ سے نکالا تم کسی چیز کو نہ جانتے تھے اور تمہیں کان اور آنکھیں اور دل دیئے تاکہ تم شکر کرو'),
(1980, 16, 79, 'کیا پرندوں کو نہیں دیکھتے کہ آسمان کی فضا میں تھمے ہوئے ہیں انہیں الله کے سوا کون تھامے ہوئے ہے بے شک اس میں بھی ایمانداروں کے لیے بڑی نشانیاں ہیں'),
(1981, 16, 80, 'اور الله نے تمہارے گھروں کو تمہارے لیے آرام کی جگہ بنایا ہے اور تمہارے لیے چارپایوں کی کھالوں سے خیمے بنائے جنہیں تم اپنے سفر اور قیام کے دن ہلکے پاتے ہو اوربھیڑوں کی اون سے اور اونٹوں کی روؤں سے اوربکریوں کے بالوں سے کتنے ہی سامان اور مفید چیزیں وقت مقرر تک کے لیے بنا دیں'),
(1982, 16, 81, 'اور الله نے تمہارے لیے اپنی بنائی ہوئی چیزوں کے سائےبنا دیئے اور تمہارے لیے پہاڑوں میں چھپنے کی جگہیں بنا دیں اور تمہارے لیے کرتے بنا دیئے جو تمہیں گرمی سے بچاتے ہیں اور زرہیں جو تمہیں لڑائیں میں بچاتی ہیں اسی طرح الله اپنا احسان تم پر پورا کرتا ہے تاکہ تم فرمانبردار ہو جاؤ'),
(1983, 16, 82, 'پھربھی اگر نہ مانیں تو تم پر صاف صاف پیغام پہنچا دینا ہی ہے'),
(1984, 16, 83, 'وہ الله کی نعمتیں پہچانتے ہیں پھر منکر ہو جاتے ہیں اوراکثر ان میں سے ناشکر گزار ہیں'),
(1985, 16, 84, 'اور جس دن ہم ہر قوم میں سے ایک گواہ کھڑا کریں گے پھر نہ کافروں کو اجازت دی جائے گی اورنہ ان کا کوئی عذر قبول کیا جائے گا'),
(1986, 16, 85, 'اور جب ظالم عذاب دیکھیں گے پھر نہ ان سے ہلکا کیا جائے گا اورنہ انہیں مہلت دی جائے گی'),
(1987, 16, 86, 'اور جب مشرک اپنے شریکوں کو دیکھیں گے تو کہیں گے اے ہمارے رب! یہی ہمارے شریک ہیں جنہیں ہم تیرے سوا پکارتے تھے پھر وہ انہیں جواب دیں گے کہ تم سراسر جھوٹے ہو'),
(1988, 16, 87, 'اور وہ اس دن الله کے سامنے سر جھکا دیں گے اور بھول جائیں گے وہ جو جھوٹ بناتے تھے'),
(1989, 16, 88, 'جو لوگ منکر ہوئے اور الله کی راہ سے روکتے رہے ہم ان پر عذاب پر عذاب بڑھاتے جائیں گے بسبب اس کے کہ وہ فساد کرتے تھے'),
(1990, 16, 89, 'اور جس دن ہر ایک گروہ میں سے ان پر انہیں میں سے ایک گواہ کھڑا کریں گے اور تجھے ان پر گواہ بنائیں گے اور ہم نے تجھ پر ایک ایسی کتاب نازل کی ہے جس میں ہر چیز کا کافی بیان ہے اور وہ مسلمانوں کے لیے ہدات اور رحمت اور خوشخبری ہے'),
(1991, 16, 90, 'بے شک الله انصاف کرنے کا اوربھلائی کرنے کا اور رشتہ داروں کو دینے کا حکم کرتا ہے اوربے حیائی اوربری بات اور ظلم سے منع کرتا ہے تمہیں سمجھاتا ہے تاکہ تم سمجھو'),
(1992, 16, 91, 'اور الله کا عہد پورا کرو جب آپس میں عہد کرو اور قسموں کو پکاکرنے کے بعد نہ توڑو حالانکہ تم نے الله کو اپنے اوپر گواہ بنا یا ہے بے شک الله جانتا ہے جو تم کرتے ہو'),
(1993, 16, 92, 'اور اس عورت جیسے نہ بنو جواپنا سوت محنت کے بعد کاٹ کر توڑ ڈالے کہ تم اپنی قسموں کو آپس میں فساد کا ذریعہ بنانے لگو محض اس لیے کہ ایک گروہ دوسرے گروہ سے بڑھ جائے الله اس میں تمہاری آزمائش کرتا ہے اور جس چیز میں تم اختلاف کرتے ہو الله اسے ضرور قیامت کے دن ظاہر کردے گا'),
(1994, 16, 93, 'اور اگر الله چاہتا تو تم سب کو ایک ہی جماعت بنا دیتا اورلیکن وہ جسے چاہتا ہے گمراہی میں پڑا رہنے دیتا ہے اور جسے چاہتاہے ہدایت دیتا ہے اور البتہ تم سے پوچھا جائے گا کہ کیا کرتےتھے'),
(1995, 16, 94, 'اور تم اپنی قسموں کو آپس میں فساد کا ذریعہ نہ بناؤ کبھی قدم جمنے کے بعد پھسل نہ جائے پھر تمہیں اس سبب سے کہ تم نے راہِ خدا سے روکا تکلیف اٹھانی پڑے اور تمہیں بڑا عذاب ہو'),
(1996, 16, 95, 'اور الله سے عہد کو تھوڑے سے داموں پر نہ بیچو جو کچھ الله کے ہاں ہے وہی تمہارے لیے بہتر ہے اگر تم جانتے ہو'),
(1997, 16, 96, 'جو تمہارے پاس ہے ختم ہو جائے گا اور جو الله کے پاس ہے کبھی ختم نہ ہوگا اور ہم صبر کرنے والوں کو ان کے اچھے کاموں کا جو کرتے تھے ضرور بدلہ دیں گے'),
(1998, 16, 97, 'جس نے نیک کام کیا مرد ہو یا عورت اور وہ ایمان بھی رکھتا ہے تو ہم اسے ضروراچھی زندگی بسر کرائیں گے اور ان کا حق انہیں بدلےمیں دیں گے انکےاچھے کاموں کے عوض میں جو کرتے تھے'),
(1999, 16, 98, 'سو جب تو قرآن پڑھنے لگے تو شیطان مردود سے الله کی پناہ لے'),
(2000, 16, 99, 'اس کا زور ان پرنہیں چلتا جو ایمان رکھتے ہیں اور اپنے رب پر بھروسہ کرتے ہیں'),
(2001, 16, 100, 'اس کا زور تو انہیں پر ہے جو اسے دوست بناتے ہیں اور جو الله کے ساتھ شریک مانتے ہیں'),
(2002, 16, 101, 'اورجب ہم ایک آیت کی جگہ دوسری بدلتے ہیں اور الله خوب جانتا ہے جو اتارتا ہے تو کہتے ہیں کہ تو بنا لاتا ہے یہ بات نہیں لیکن اکثر ان میں سے نہیں سمجھتے'),
(2003, 16, 102, 'تو کہہ دے اسےتیرے رب کی طرف سے پاک فرشتے نے سچائی کے ساتھ اتارا ہے تاکہ ایمان والوں کے دل جما دے اور فرمانبرداروں کے لیے ہدایت اور خوشخبری ہے'),
(2004, 16, 103, 'اور ہمیں خوب معلوم ہے کہ وہ کہتے ہیں اسے تو ایک آدمی سکھاتا ہے حالانکہ جس کی طرف نسبت کرتے ہیں اس کی زبان عجمی ہے اور یہ صاف عربی زبان ہے'),
(2005, 16, 104, 'وہ لوگ جنہیں الله کی باتوں پر یقین نہیں الله بھی انہیں ہدایت نہیں دیتا اور ان کے لیے دردناک عذاب ہے'),
(2006, 16, 105, 'جھوٹ تو وہ لوگ بناتے ہیں جنہیں الله کی باتوں پر یقین نہیں اور وہی لوگ جھوٹے ہیں'),
(2007, 16, 106, 'جو کوئی ایمان لانے کے بعد الله سے منکر ہوا مگر وہ جو مجبور کیا گیا ہو اور اس کا دل ایمان پر مطمئن ہو اور لیکن وہ جو دل کھول کر منکر ہوا تو ان پر الله کا غضب ہے اور ان کے لیے بہت بڑا عذاب ہے'),
(2008, 16, 107, 'یہ اس لیے کہ انہوں نے دنیا کی زندگی کو آخرت پر محبوب بنایا اور نیز اس لیے کہ الله کافروں کو ہدایت نہیں دیتا'),
(2009, 16, 108, 'یہ وہی ہیں کہ الله نے ان کے دلوں پر اورکانوں پر اور آنکھوں پر مہر کر دی اور وہی غافل بھی ہیں'),
(2010, 16, 109, 'ضرور وہی لوگ آخرت میں نقصان اٹھانے والے ہیں'),
(2011, 16, 110, 'پھر بے شک تیرا رب ان کے لئے جنہوں نےمصیبت میں پڑنے کے بعد ہجرت کی پھر جہاد کیا اور صبر کیا بے شک تیرارب ان باتو ں کے بعد بحشنے والا مہربان ہے'),
(2012, 16, 111, 'ٰجس دن ہر شخص اپنے ہی لیے جھگڑتا ہو آئے گا اور ہر شخص کو اس کے عمل کا پورا بدلہ دیا جائے گا اوران پر کچھ بھی ظلم نہ ہوگا'),
(2013, 16, 112, 'اور الله ایک ایسی بستی کی مثال بیان فرماتا ہے جہاں ہر طرح کا امن چین تھا اس کی روزی بافراغت ہر جگہ سے چلی آتی تھی پھر الله کےاحسانوں کی ناشکری کی پھر الله نے ان کے برے کاموں کے سبب سے جو وہ کیا کرتے تھے یہ مزہ چکھایا کہ ان پر فاقہ اور خوف چھا گیا'),
(2014, 16, 113, 'اور البتہ ان کے پاس انہیں میں سے رسول بھی آیا مگر انہوں نے اسے جھٹلایا پھر انہیں عذاب نے آ پکڑا ایسے حال میں کہ وہ ظالم تھے'),
(2015, 16, 114, 'پھر تمہیں جو الله نے حلال طیب روزی دی ہے کھاؤ اور الله کے احسان کا شکر کرو اگر تم صرف اسی کو پوجتے ہو'),
(2016, 16, 115, 'تم پر صرف مردار اور خون اور سور کا گوشت حرام کیا ہے اوروہ چیز بھی جو الله کے سوا کسی اور کے نام سے پکاری گئی ہو پھر جوبھوک کے مارے بیتاب ہو جائے نہ وہ باغی ہو اورنہ حد سے گزرنے والا توالله بخشنے والا مہربان ہے'),
(2017, 16, 116, 'اور اپنی زبانوں سے جھوٹ بنا کر نہ کہو کہ یہ حلا ل ہے اوریہ حرا م ہے تاکہ الله پر بہتان باندھو بے شک جو الله پر بہتان باندھتے ہیں انکا بھلانہ ہوگا'),
(2018, 16, 117, 'تھوڑا سا فائدہ اٹھا لیں اوران کے لیے دردناک عذاب ہے'),
(2019, 16, 118, 'اورجو لوگ یہودی ہیں ہم نے ان پر حرا م کی جو تجھے پہلے سنا چکے ہیں اورہم نے ان پر ظلم نہیں کیا لیکن وہ اپنے اوپر آپ ظلم کرتے تھے'),
(2020, 16, 119, 'پھر تیرا رب ان کے لیے جو جہالت سے برے کام کرتے رہے پھر اس کے بعد انہوں نے توبہ کر لی اور سدھر گئے بے شک تیرا رب اس کے بعد البتہ بخشنے والا مہربان ہے'),
(2021, 16, 120, 'بے شک ابراہیم ایک پوری امت تھا الله کا فرمانبردار تمام راہوں سے ہٹا ہوا اور مشرکوں میں سے نہ تھا'),
(2022, 16, 121, 'اس کی نعمتوں کا شکر کرنے والا اسے الله نے چن لیا اور اسے سیدھی راہ پر چلایا'),
(2023, 16, 122, 'اور ہم نے اسے دنیا میں بھی خوبی دی تھی اور وہ آخرت میں بھی اچھے لوگو ں میں ہو گا'),
(2024, 16, 123, 'پھر ہم نے تیرے پاس وحی بھیجی کہ تمام راہوں سے ہٹنے والے ابراہیم کے دین پر چل اور وہ مشرکوں میں سے نہ تھا'),
(2025, 16, 124, 'ہفتہ کا دن انہی پر مقرر کیا گیا تھا جو اس میں اختلاف کرتےتھے او رتیرا رب ان میں قیامت کےدن فیصلہ کرے گا جس میں وہ اختلاف کرتے تھے'),
(2026, 16, 125, 'اپنے رب کے راستے کی طرف دانشمندی اور عمدہ نصیحت سے بلا اور ان سے پسندیدہ طریقہ سے بحث کر بے شک تیرا رب خوب جانتا ہے کہ کون اس کے راستہ سے بھٹکا ہوا ہے اور ہدایت یافتہ کو بھی خوب جانتا ہے'),
(2027, 16, 126, 'اور اگر بدلہ لو تو اتنا بدلہ لو جتنی تمہیں تکلیف پہنچائی گئی ہے اور اگر صبر کرو تو یہ صبر کرنے والوں کے لیے بہتر ہے'),
(2028, 16, 127, 'اورصبر کر اور تیرا صبر کرنا الله ہی کی توفیق سے ہے اور ان پر غم نہ کھا اور ان کے مکروں سے تنگ دل نہ ہو'),
(2029, 16, 128, 'بے شک الله ان کے ساتھ ہے جو پرہیزگار ہیں اور جو نیکی کرتے ہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'وہ پاک ہے جس نے راتوں رات اپنے بندے کو مسجدحرام سے مسجد اقصیٰ تک سیر کرائی جس کے اردگرد ہم نے برکت رکھی ہے تاکہ ہم اسے اپنی کچھ نشانیاں دکھائیں بے شک وہ سننے والا دیکھنے ولا ہے'),
(2031, 17, 2, 'اور ہم نے موسیٰ کو کتاب دی اور اسے بنی اسرائیل کے لیے ہدایت بنایا کہ میرے سوا کسی کو کارساز نہ بناؤ'),
(2032, 17, 3, 'اے ان کی نسل جنہیں ہم نے نوح کے ساتھ سوار کیا تھا بے شک وہ شکر گزار بندہ تھا'),
(2033, 17, 4, 'اور ہم نے بنی اسرائیل کو کتاب میں یہ بات بتلا دی تھی کہ تم ضرور ملک میں دو مرتبہ خرابی کرو گے اوربڑی سرکشی کرو گے'),
(2034, 17, 5, 'پھر جب پہلا وعدہ آیا تو ہم نے تم پر اپنے بندے سخت لڑائی والے بھیجے پھر وہ تمہارے گھروں میں گھس گئے اور الله کا وعدہ تو پورا ہونا ہی تھا'),
(2035, 17, 6, 'پھر ہم نے تمہیں دشمنوں پر غلبہ دیا اور تمہیں مال اور اولاد میں ترقی دی اور تمہیں بڑی جماعت والا بنا دیا'),
(2036, 17, 7, 'اگر تم نے بھلائی کی تو اپنے ہی لیےکی اور اگر برائی کی تو وہ بھی اپنے ہی لیےکی پھر جب دوسرا وعدہ آیا تاکہ تمہارے چہروں پر رسوائی پھیر دیں اور مسجد میں گھس جائیں جس طرح پہلی بار گھس گئے تھے اور جس چیز پر قابو پائیں اس کا ستیاناس کر دیں'),
(2037, 17, 8, 'تمہارا رب قریب ہے کہ تم پر رحم کرے اور اگر تم پھر وہی کرو گے تو ہم بھی پھر وہی کریں گے اور ہم نے دوزخ کو کافروں کے لیے قید خانہ بنایا ہے'),
(2038, 17, 9, 'بے شک یہ قرآن وہ راہ بتاتا ہے جو سب سے سیدھی ہے اور ایمان والوں کو جو نیک کام کرتے ہیں اس بات کی خوشخبری دیتاہے ان کے لیے بڑا ثواب ہے'),
(2039, 17, 10, 'اور یہ بھی بتاتا ہے کہ جو لوگ آخرت پر ایمان نہیں لاتے ہم نے ان کے لیے دردناک عذاب تیار کیا ہے'),
(2040, 17, 11, 'اور انسان برائی مانگتا ہے جس طرح وہ بھلائی مانگتا ہے اور انسان جلد باز ہے'),
(2041, 17, 12, 'اور ہم نے رات اور دن کے دو نمونے بنا دیے پھر رات کے نمونے کو دھندلا کر دیا اور دن کا نمونہ نظر آنے کے لیے روشن کر دیا تاکہ تم اپنے رب کا فضل تلاش کرو اور تاکہ تم برسوں کی گنتی اور حساب معلوم کر لو اور ہم نے ہر چیز کی تفصیل کر دی'),
(2042, 17, 13, 'اور ہم نے ہر آدمی کا نامہ اعمال اس کی گردن کےساتھ لگادیا ہے اور قیامت کے دن ہم اس کا نامہ اعمال نکال کر سامنے کردیں گے'),
(2043, 17, 14, 'اپنا نامہ اعمال پڑھ لے آج اپنا حساب لینے کے لیے تو ہی کافی ہے'),
(2044, 17, 15, 'جو سیدھے راستے پر چلا تو اپنے ہی لیے چلا اور جو بھٹک گیا تو بھٹکنے کا نقصان بھی وہی اٹھائے گا اور کوئی بوجھ اٹھانے والا دوسرے کا بوجھ نہیں اٹھائے گا اور ہم سزا نہیں دیتے جب تک کسی رسول کو نہیں بھیج لیتے'),
(2045, 17, 16, 'اور جب ہم کسی بستی کو ہلاک کرنا چاہتے ہیں تو وہاں کے دولتمندوں کو کوئی حکم دیتے ہیں پھر وہ وہاں نافرمانی کرتے ہیں تب ان پر حجت تمام ہوجاتی ہے اور ہم اسے برباد کر دیتے ہیں'),
(2046, 17, 17, 'اور نوح کے بعد ہم نےقوموں کے کئی دور ہلاک کر دیئے ہیں اور تیرا رب اپنے بندوں کے گناہوں کا جاننے والا دیکھنے والا کافی ہے'),
(2047, 17, 18, 'جو کوئی دنیا چاہتا ہے تو ہم اسے سردست دنیا میں سے جس قدر چاہتے ہیں دیتے ہیں پھر ہم نے اس کے لیے جہنم تیار کر رکھی ہے جس میں وہ ذلیل و خوار ہوکر رہے گا'),
(2048, 17, 19, 'اورجو آخرت چاہتا ہے اوراس کے لیے مناسب کوشش بھی کرتا ہے اور وہ مومن بھی ہے تو ایسے لوگوں کی کوشش مقبول ہو گی'),
(2049, 17, 20, 'ہم ہر فریق کو اپنی پروردگاری بخششوں سے مدد دیتے ہیں ان کو بھی اور تیرے رب کی بخشش کسی پر بند نہیں'),
(2050, 17, 21, 'دیکھو ہم نے ایک کو دوسرے پر کیسی فضیلت دی ہے اور آخرت کے تو بڑے درجے اوربڑی فضیلت ہے'),
(2051, 17, 22, 'الله کے ساتھ اور کوئی معبود نہ بنا ورنہ تو ذلیل بے کس ہو کر بیٹھے گا'),
(2052, 17, 23, 'اور تیرا رب فیصلہ کر چکا ہے اس کے سوا کسی کی عبادت نہ کرو اور ماں باپ کے ساتھ نیکی کرو اور اگر تیرے سامنے ان میں سے ایک یا دونوں بڑھاپے کو پہنچ جائیں تو انہیں اف بھی نہ کہو اور نہ انہیں جھڑکو اور ان سے ادب سے بات کرو'),
(2053, 17, 24, 'اوران کے سامنے شفقت سے عاجزی کے ساتھ جھکے رہو اور کہو اے میرے رب جس طرح انہوں نے مجھے بچپن سےپالا ہے اسی طرح تو بھی ان پر رحم فرما'),
(2054, 17, 25, 'جو تمہارے دلوں میں ہے تمہارا رب خوب جانتا ہے اگر تم نیک ہو گے تو وہ توبہ کرنے والوں کو بخشنے والا ہے'),
(2055, 17, 26, 'اور رشتہ دار اور مسکین اور مسافر کو اس کا حق دے دو اور مال کو بے جا خرچ نہ کرو'),
(2056, 17, 27, 'بے شک بیجا خرچ کرنے والے شیطانو ں کے بھائی ہیں اور شیطان اپنے رب کا ناشکر گزار ہے'),
(2057, 17, 28, 'اوراگر تجھے اپنے رب کے فضل کے انتظار میں کہ جس کی تجھے امید ہے منہ پھیرنا پڑے تو ان سے نرم بات کہہ دے'),
(2058, 17, 29, 'اور اپنا ہاتھ اپنی گردن کے ساتھ بندھا ہوانہ رکھ اور نہ اسے کھول دے بالکل ہی کھول دینا پھر تو پشیمان تہی دست ہو کر بیٹھ رہے گا'),
(2059, 17, 30, 'بے شک تیرا رب جس کے لئے چاہے رزق کشادہ کر تا ہے اور تنگ بھی کرتا ہے بے شک وہ اپنے بندوں کو جاننے والا دیکھنے والا ہے'),
(2060, 17, 31, 'اور اپنی اولاد کو تنگدستی کے ڈر سے قتل نہ کرو ہم انہیں بھی رزق دیتے ہیں اور تمہیں بھی بے شک ان کا قتل کرنا بڑا گناہ ہے'),
(2061, 17, 32, 'اور زنا کے قریب نہ جاؤ بے شک وہ بے حیائی ہے اور بری راہ ہے'),
(2062, 17, 33, 'اور جس جان کو قتل کرنا الله نے حرام کر دیا ہے اسے ناحق قتل نہ کرنا اور جو کوئی ظلم سے مارا جائے تو ہم نے اس کے ولی کے واسطے اختیار دے دیا ہے لہذاا قصاص میں زيادتی نہ کرے بے شک اس کی مدد کی گئی ہے'),
(2063, 17, 34, 'اور یتیم کے مال کے پاس نہ جاؤ مگر جس طریقہ سے بہتر ہو جب تک وہ اپنی جوانی کو پہنچے اور عہد کو پورا کرو بے شک عہد کی بازپرس ہو گی'),
(2064, 17, 35, 'اور ناپ تول کر دو تو پورا ناپو اور صحیح ترازو سے تول کر دو یہ بہتر ہے اور انجام بھی اس کا اچھا ہے'),
(2065, 17, 36, 'اورجس بات کی تجھے خبر نہیں اس کے پیچھے نہ پڑ بے شک کان اورآنکھ اور دل ہر ایک سے باز پرس ہو گی'),
(2066, 17, 37, 'اور زمین پر اتراتا ہوا نہ چل بے شک تو نہ زمین کو پھاڑ ڈالے گا او رنہ لمبائی میں پہاڑوں تک پہنچے گا'),
(2067, 17, 38, 'ان میں سے ہر ایک بات تیرے رب کے ہاں ناپسند ہے'),
(2068, 17, 39, 'یہ اس حکمت میں سے ہے جسے تیرے رب نے تیری طرف وحی کیا ہے اور الله کے ساتھ اور کسی کو معبود نہ بنا ورنہ تو ملزم مردود بنا کر جہنم میں ڈال دیا جائے گا'),
(2069, 17, 40, 'کیا تمہارے رب نے تمہیں چن کر بیٹے دے دیئے اور اپنے لئےفرشتوں کو بیٹیاں بنا لیا تم بڑی بات کہتے ہو'),
(2070, 17, 41, 'اور ہم نے اس قرآن میں کئی طرح سے بیان کیا تاکہ وہ سمجھیں حالانکہ اس سے انہیں نفرت ہی بڑھتی جاتی ہے'),
(2071, 17, 42, 'کہہ دو اگر اس کے ساتھ اور بھی معبود ہوتے جیسا وہ کہتے ہیں تب تو انہوں نے عرش والے تک کوئی راستہ نکال لیا ہوتا'),
(2072, 17, 43, 'وہ پاک ہے اور جو کچھ وہ کہتے ہیں اس سے وہ بہت ہی بلند ہے'),
(2073, 17, 44, 'ساتوں آسمان اور زمین اور جو کوئی ان میں ہے اس کی پاکی بیان کرتے ہیں اور ایسی کوئی چیز نہیں جو ا سکی حمد کے ساتھ تسبیح نہ کرتی ہو لیکن تم ان کی تسبیح کو نہیں سمجھتے بے شک وہ بردبار بخشنے والا ہے'),
(2074, 17, 45, 'اورجب تو قرآن پڑھتا ہے ہم تیرے اور ان لوگو ں کے درمیان جو آخرت کو نہیں مانتے ایک چھپا ہو ا پردہ کر دیتے ہیں'),
(2075, 17, 46, 'اور ہم نے ان کے دلو ں پر پردے کر دیے ہیں تاکہ اسے نہ سمجھیں اور ان کے کانوں میں گرانی ڈال دی ہے اور جب تو قرآن میں صرف اپنے رب ہی کا ذکر کرتا ہے تو پیٹھ پھیر کر کفرت سے بھاگتے ہیں'),
(2076, 17, 47, 'ہم خوب جانتے ہیں جس غرض سے یہ سنتے ہیں جب یہ لوگ تیری طرف کان لگاتے ہیں اور جس وقت آپس میں سرگوشیاں کرتے ہیں جب یہ ظالم کہتے ہیں کہ تم محض ایسے شخص کا ساتھ دیتے ہو جس پر جادو کیا گیا ہے'),
(2077, 17, 48, 'دیکھ تیرے لیے کیسی مثالیں بیان کرتے ہیں سو گمراہ ہو گئے پھر وہ راستہ نہیں پا سکتے'),
(2078, 17, 49, 'اور کہتے ہیں کیا جب ہم ہڈیاں اور چورا ہو جائیں گے پھر نئے بن کر اٹھیں گے'),
(2079, 17, 50, 'کہہ دو تم پتھر یا لوہا ہوجاؤ'),
(2080, 17, 51, 'یا کوئی اور چیز جسے تم اپنے دلوں میں مشکل سمجھتے ہو پھر وہ کہیں گے ہمیں دوبارہ کون لوٹائے گا کہہ دو وہی جس نے تمہیں پہلی مرتبہ پیدا کیا ہے پھر تمہارے سامنے سروں کو ہلاکر کہیں گے کہ وہ کب ہو گا کہہ دو شاید وہ وقت بھی قریب آ گیا ہو'),
(2081, 17, 52, 'جس دن تمہیں پکارے گا پھر اس کی تعریف کرتے ہوئے چلے آؤ گے اور خیال کرو گے کہ بہت ہی کم ٹھہرے تھے'),
(2082, 17, 53, 'اور میرے بندوں سے کہہ دو کہ وہی بات کہیں جو بہتر ہو بےشک شیطان آپس میں لڑا دیتا ہے بے شک شیطان انسان کا کھلا دشمن ہے'),
(2083, 17, 54, 'تمہارا رب خوب جانتا ہے اگر چاہے تم پر رحم کرے اور اگر چاہے تمہیں عذاب دے او رہم نے تجھے ان پر ذمہ دار بنا کر نہیں بھیجا'),
(2084, 17, 55, 'اور تیرا رب خوب جانتا ہے جو آسمانوں اور زمین میں ہے اور ہم نے بعض پیغمبروں کو بعض پر فضیلت دی ہے او رہم نے داؤد کو زبور دی تھی'),
(2085, 17, 56, 'کہہ دو انہیں پکارو جنہیں تم اس کے سوا سمجھتے ہو وہ نہ تمہاری تکلیف دور کر سکیں گے اور نہ اسے بدلیں گے'),
(2086, 17, 57, 'وہ لوگ جنہیں یہ پکارتے ہیں جو ان میں سے زیادہ مقرب ہیں وہ بھی اپنے رب کی طرف نیکیوں کا ذریعہ تلاش کرتے ہیں اور اس کی مہربانی کی امید رکھتے ہیں اور اس کے عذاب سے ڈرتے ہیں بےشک تیرے رب کا عذاب ڈرنے کی چیز ہے'),
(2087, 17, 58, 'اور ایسی کوئی بستی نہیں جسے ہم قیامت سے پہلے ہلاک نہ کریں یا اسے سخت عذاب نہ دیں یہ بات کتاب میں لکھی ہوئی ہے'),
(2088, 17, 59, 'اور ہم نے اس لیےمعجزات بھیجنے موقوف کر دیےکہ پہلوں نے انہیں جھٹلایا تھا اور ہم نے ثمود کو اونٹنی کا کھلا ہوا معجزہ دیا تھا پھر بھی انہوں نے اس پر ظلم کیا اور یہ معجزات تو ہم محض ڈرانے کے لیے بھیجتے ہیں'),
(2089, 17, 60, 'اور جب ہم نے تم سے کہہ دیا کہ تیرے رب نے سب کو قابو میں کر رکھا ہے اور وہ خواب جو ہم نے تمہیں دکھایا اور وہ خبیث درخت جس کا ذکر قرآن میں ہے ان سب کو ان لوگو ں کے لیے فتنہ بنا دیا اور ہم تو انہیں ڈراتے ہیں سو اس سے ان کی شرارت اوربھی بڑھتی جاتی ہے'),
(2090, 17, 61, 'اور جب ہم نے فرشتوں سے کہا آدم کو سجدہ کرو تو سوائے ابلیس کے سب سجدہ میں گر پڑے کہا کیا میں ایسے شخص کو سجدہ کروں جسےتو نے مٹی بنایا ہے'),
(2091, 17, 62, 'کہا بھلا دیکھ تو یہ شخص جسے تو نے مجھ سے بڑھایا اگرتو مجھے قیامت کے دن تک مہلت دے تو میں بھی سوائے چند لوگوں کے اس کی نسل کو قابو میں کر کے رہونگا'),
(2092, 17, 63, 'فرمایا جا۔ پھر ان میں سے جو کوئی تیرے ساتھ ہوا تو جہنم تم سب کی پوری سزا ہے'),
(2093, 17, 64, 'ان میں سے جسے تو اپنی آواز سنا کر بہکا سکتا ہے بہکا لے اور ان پر اپنے سوار اور پیادے بھی چڑھا دے اوران کے مال اور اولاد میں بھی شریک ہو جا اور ان سے وعدے کر اور شیطان کے وعدے بھی محض فریب ہی تو ہیں'),
(2094, 17, 65, 'بے شک میرے بندوں پر تیرا غلبہ نہیں ہو گا اور تیرا رب کافی کارساز ہے'),
(2095, 17, 66, 'تمہارا رب وہ ہے جو تمہارے لیے دریا میں کشتیاں چلاتا ہے تاکہ تم اس کا فضل تلاش کرو بے شک وہی تم پر بڑا مہربان ہے'),
(2096, 17, 67, 'اور جب تم پر دریا میں کوئی مصیبت آتی ہے تو بھول جاتے ہو جنہیں الله کے سوا پکارتے تھےپھر جب وہ تمہیں خشکی کی طرف بچا لاتا ہے تو تم اس سے منہ موڑ لیتے ہو اور انسان بڑا ہی ناشکرا ہے'),
(2097, 17, 68, 'پھر کیا تم اس بات سے نڈر ہو گئے کہ وہ تمہیں خشکی کی طرف لاکر زمین میں دھنسا دے یا تم پر پتھر برسانے والی آندھی بھیج دے پھر تم کسی کو اپنا مددگار نہ پاؤ'),
(2098, 17, 69, 'یا تم اس بات سے بالکل نڈر ہو گئے ہو کہ وہ دوبارہ تمہیں پھر دریا میں لوٹا لائے پھر تم پر ہوا کا سخت طوفان بھیج دے پھر تمہاری ناشکری سے تمہیں غرق کر دے پھر اپنی طرف سے ہم پر کوئی باز پرس کرنے والا بھی نہ پاؤ'),
(2099, 17, 70, 'اور ہم نے آدم کی اولاد کو عزت دی ہے اور خشکی اور دریا میں اسے سوار کیا اور ہم نے انہیں ستھری چیزو ں سے رزق دیا اور اپنی بہت سی مخلوقات پر انہیں فضیلت عطا کی'),
(2100, 17, 71, 'جس دن ہم ہر فرقہ کو ان کے سرداروں کے ساتھ بلائیں گے سوجسے اس کا اعمال نامہ ا سکے داہنے ہاتھ میں دیا گیا سو وہ لوگ اپنا اعمال نامہ پڑھیں گے اور وہ تاگے کے برابر ظلم نہیں کئے جائیں گے'),
(2101, 17, 72, 'اور جو کوئی اس جہان میں اندھا رہا تو وہ آخرت میں بھی اندھا ہو گا اور راستہ سے بہت دور ہٹا ہوا'),
(2102, 17, 73, 'اور بے شک وہ قریب تھے کہ تجھے اس چیز سے بہکا دیں جو ہم نے تجھ پر بذریعہ وحی بھیجی ہے تاکہ تو اس کے سوا ہم پر بہتان باندھنے لگے اور پھر تجھے اپنا دوست بنا لیں'),
(2103, 17, 74, 'اور اگرہم تجھے ثابت قدم نہ رکھتے تو کچھ تھوڑا سا ان کی طرف جھکنے کے قریب تھا'),
(2104, 17, 75, 'اس وقت ہم تجھے زندگی میں اور موت کے بعد دہرا عذاب چکھاتے پھر تو اپنے واسطے ہمارے مقابلے میں کوئی مددگار نہ پاتا'),
(2105, 17, 76, 'اور وہ تو تجھے اس زمین سے دھکیل دینے کو تھے تاکہ تجھے اس سے نکال دیں پھر وہ بھی تیرے بعد بہت ہی کم ٹھرتے'),
(2106, 17, 77, 'تم سے پہلے جتنے رسول ہم نے بھیجے ہیں ان کا یہی دستور رہا ہے او رہمارے دستور میں تم تبدیلی نہیں پاؤ گے'),
(2107, 17, 78, 'آفتاب کے ڈھلنےسے رات کے اندھیرے تک نماز پڑھا کرو اور صبح کی نماز بھی بے شک صبح کی نماز میں مجمع ہو تا ہے'),
(2108, 17, 79, 'اور کسی وقت رات میں تہجد پڑھا کرو جو تیرے لیے زائد چیز ہے قریب ہے کہ تیرا رب مقام محمود میں پہنچا دے'),
(2109, 17, 80, 'اور کہہ اے میرے رب مجھے خوبی کے ساتھ پہنچا دے اور مجھے خوبی کے ساتھ نکال لے اور میرے لیے اپنی طرف سے غلبہ دے جس کے ساتھ نصرت ہو'),
(2110, 17, 81, 'اور کہہ دو کہ حق آیا اور باطل مٹ گیا بے شک باطل مٹنے ہی والا تھا'),
(2111, 17, 82, 'اور ہم قرآن میں ایسی چیزیں نازل کرتے ہیں کہ وہ ایمانداروں کے حق میں شفا اور رحمت ہیں اور ظالموں کو اس سےاور زیادہ نقصان پہنچتا ہے'),
(2112, 17, 83, 'اورجب ہم انسان پر انعام کرتے ہیں تو منہ پھیر لیتا ہے اور پہلو تہی کرتا ہے اور جب اسے کوئی تکلیف پہنچتی ہے تو نا امیدہوجاتا ہے'),
(2113, 17, 84, 'کہہ دو کہ ہر شخص اپنے طریقہ پر کام کرتا ہے پھر تمہارا رب خوب جانتا ہے کہ سب سے زیادہ ٹھیک راہ پر کون ہے'),
(2114, 17, 85, 'اور یہ لوگ تجھے رو ح کے متعلق سوال کرتے ہیں کہہ دو روح میرے رب کے حکم سے ہے اور تمہیں جو علم دیا گیا ہے وہ بہت ہی تھوڑا ہے'),
(2115, 17, 86, 'او راگر ہم چاہیں تو جو کچھ ہم نے تیری طرف وحی کی ہے اسے اٹھا لیں پھر تجھے اس کے لیے ہمارے مقابلہ میں کوئی حمایتی نہ ملے'),
(2116, 17, 87, 'مگر یہ صرف تیرے رب کی رحمت ہے بے شک تجھ پر اس کی بڑی عنایت ہے'),
(2117, 17, 88, 'کہہ دو اگر سب آدمی اور سب جن مل کر بھی ایسا قرآن لانا چاہیں تو ایسا نہیں لا سکتے اگرچہ ان میں سے ہر ایک دوسرے کا مددگار کیوں نہ ہو'),
(2118, 17, 89, 'او رہم نے اس قرآن میں لوگوں کے لیے ہر ایک قسم کی مثال بھی کھول کر بیان کر دی ہے پھر بھی اکثر لوگ انکار کیے بغیر نہ رہے'),
(2119, 17, 90, 'اور کہا ہم تمہیں ہرگز نہ مانیں گے یہاں تک کہ تو ہمارے لیے زمین میں سے کوئی چشمہ جاری کر دے'),
(2120, 17, 91, 'یا تیرے لیے کھجور اور انگور کا کوئی باغ ہو پھر تو اس باغ میں بہت سی نہریں جاری کر دے'),
(2121, 17, 92, 'یا جیسا تو خیال کرتا ہے ہم پرکوئی آسمان کا ٹکڑا گرادے یا تو الله اور فرشتوں کو روبرو لے آ'),
(2122, 17, 93, 'یا تیرے پاس کوئی سونے کا گھر ہو یا تو آسمان پر چڑھ جائے او رہم تو تیرے چڑھنے کا بھی یقین نہیں کریں گے یہاں تک کہ توہمارے پاس ایسی کتاب لائے جسے ہم بھی پڑھ سکیں کہہ دو میرا رب پاک ہے میں تو فقط ایک بھیجا ہوا انسان ہوں'),
(2123, 17, 94, 'اورلوگوں کو ایمان لانے سے جب کہ ان کے پاس ہدایت آ گئی صرف اسی چیز نے روکا ہے کہ کہنے لگے کیا الله نے آدمی کو رسول بنا کر بھیجا ہے'),
(2124, 17, 95, 'کہہ دو اگر زمین میں فرشتے اطمینان سے چلتے پھرتے ہوتے تو ہم آسمان سے ان پر فرشتہ ہی رسول بنا کر بھیجتے'),
(2125, 17, 96, 'کہہ دوکہ الله میرے اور تمہارے درمیان گواہ کافی ہے بے شک وہ اپنے بندوں سے خبردار دیکھنے والا ہے'),
(2126, 17, 97, 'اور جسے الله راہ دکھا دے وہی راہ پانے والا ہے اورجسے گمراہ کر دے پھر تو ان کے لیے الله کے سوا کوئی دوست نہیں پائے گا اور ہم نے انہیں قیامت کے دن مونہوں کے بل اندھے گونگے بہرے کر کے اٹھائیں گے ان کا ٹھکانا دوزخ ہے جب بجھنے لگے گی تو ان پر اوربھڑکا دیں گے'),
(2127, 17, 98, 'یہ ان کی سزا اس لیے ہے کہ انہوں نے ہماری آیتوں کا انکار کیا اور کہا کہ کیا جب ہم ہڈیاں اور چورا ہو جائیں گے توپھر نئے ے سرے سے بنا کر اٹھائے جائیں گے'),
(2128, 17, 99, 'کیا انہوں نے نہیں دیکھا جس الله نے آسمانوں اور زمین کوبنایا ہے وہ ان جیسے اوپر بھی بنا سکتا ہے اور اس نے ان کے لیے ایک وقت مقرر کر رکھا ہے جس میں کوئی شک نہیں اس پر بھی ظالم انکار کیئے بغیر نہ رہے'),
(2129, 17, 100, 'کہہ دو اگر میرے رب کی رحمت کے خزانے تمہارے ہاتھ میں ہوتے تو تم انہیں خرچ ہو جانے کے ڈر سے بند ہی کر رکھتے اور انسان بڑا تنگ دل ہے'),
(2130, 17, 101, 'اور البتہ تحقیق ہم نے موسیٰ کو نو کھلی نشانیاں دی تھیں پھر بھی بنی اسرائیل سےبھی پوچھ لو جب موسیٰ ان کے پاس آئے تو فرعون نے اسے کہا اے موسیٰ میں تو تجھے جادو کیا ہوا خیال کرتا ہوں'),
(2131, 17, 102, 'کہا یہ تو تجھے معلوم ہے کہ یہ آسمانوں اور زمین کے مالک ہی نے لوگوں کو سوجھانے کے لیے نازل کی ہیں اور بے شک میں تجھے اے فرعون ہلاک کیا ہوا خیال کرتا ہوں'),
(2132, 17, 103, 'پھر اس نے ارادہ کیا کہ انہیں اس زمین سے نکال دے تب ہم نے اسے اور اس کے سب ساتھیوں کو غرق کر دیا'),
(2133, 17, 104, 'اور اس کے بعد ہم نے بنی اسرائیل سے کہا کہ تم اس زمین میں آباد رہو پھر جب آخرت کا وعدہ آ ئے گا ہم تمہیں سمیٹ کر لے آئیں گے'),
(2134, 17, 105, 'اور ہم نے اس قرآن کو سچائی سے نازل کیا اور وہ سچائی سے ہی نازل ہوا اور ہم نے تجھے صرف خوشی سنانے والا اور ڈرانے والا بنا کر بھیجا ہے'),
(2135, 17, 106, 'اور ہم نے قرآن کو تھوڑا تھوڑا کر کے اتارا تاکہ تو مہلت کے ساتھ اسے لوگوں کو پڑھ کر سنائے اور ہم نے اسے آہستہ آہستہ اتارا ہے'),
(2136, 17, 107, 'کہہ دوتم اسے مانو یا نہ مانو بے شک وہ لوگ جنہیں اس سے پہلے علم دیا گیا ہے جب ان پر پڑھا جاتا ہے تو تھوڑیوں پر سجدہ میں گرتے ہیں'),
(2137, 17, 108, 'اورکہتے ہیں ہمارا رب پاک ہے بے شک ہمارے رب کا وعدہ ہو کر رہے گا'),
(2138, 17, 109, 'اور تھوڑیوں پر روتے ہوئے گرتے ہیں اور ان میں عاجزی زیادہ کر دیتا ہے'),
(2139, 17, 110, 'کہہ دو الله کہہ کر یا رحمٰن کہہ کر پکارو جس نام سے پکاروسب اسی کے عمدہ نام ہیں اور اپنی نماز میں نہ چلا کر پڑھ اور نہ بالکل ہی آہستہ پڑھ اور اس کے درمیان راستہ اختیار کر'),
(2140, 17, 111, 'اور کہہ دو سب تعریفیں الله کے لیے ہیں جس کی نہ کوئی اولاد ہے اورنہ کوئی اس کا سلطنت میں شریک ہے اور نہ کوئی کمزوری کی وجہ سے اس کا مددگار ہے اور اس کی بڑائی بیان کرتےر ہو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'سب تعریف الله کے لیے جس نے اپنے بندہ پر کتاب اتاری اور اس میں ذرا بھی کجی نہیں رکھی'),
(2142, 18, 2, 'ٹھیک اتاری تاکہ اس سخت عذاب سے ڈراوے جو اس کے ہاں ہے اورایمان داروں کو خوشخبری دے جو اچھے کام کرتے ہیں کہ ان کے لیے اچھا بدلہ ہے'),
(2143, 18, 3, 'جس میں وہ ہمیشہ رہیں گے'),
(2144, 18, 4, 'اور انہیں بھی ڈرائے جو کہتے ہیں کہ الله اولاد رکھتا ہے'),
(2145, 18, 5, 'ان کے پاس اس کی کوئی دلیل نہیں ہے اور نہ ان کے باپ دادا کے پاس تھی کیسی سخت بات ہے جو ان کے منہ سے نکلتی ہے وہ لوگ بالکل جھوٹ کہتے ہیں'),
(2146, 18, 6, 'پھر شایدتو ان کے پیچھے افسوس سے اپنی جان ہلاک کر دے گا اگر یہ لوگ اس بات پر ایمان نہ لائے'),
(2147, 18, 7, 'جو کچھ زمین پر ہے بے شک ہم نے اسے زمین کی زینت بنا دیا ہے تاکہ ہم انہیں آزمائیں کہ ان میں کون اچھے کام کرتا ہے'),
(2148, 18, 8, 'اورجو کچھ اس پر ہے بے شک ہم سب کو چٹیل میدان کر دیں گے'),
(2149, 18, 9, 'کیا تم خیال کرتے ہو کہ غار اور کتبہ والے ہماری نشانیوں والے عجیب چیز تھے'),
(2150, 18, 10, 'جب کہ چند جوان اس غار میں آ بیٹھے پھر کہا اے ہمارے رب ہم پر اپنی طرف سے رحمت نازل فرما اور ہمارے اس کام کے لیے کامیابی کا سامان کر دے'),
(2151, 18, 11, 'پھر ہم نے کئی سال تک غار میں ان کے کان بند کر دیے'),
(2152, 18, 12, 'پھر ہم نے انہیں اٹھایا تاکہ معلوم کریں کہ دونوں جماعتوں میں سے کس نے یاد رکھی ہے جتنی مدت وہ رہے'),
(2153, 18, 13, 'ہم تمہیں ان کا صحیح حال سناتے ہیں بے شک وہ کئی جوان تھے جو اپنے رب پر ایمان لائے اور ہم نے انہیں اور زيادہ ہدایت دی'),
(2154, 18, 14, 'اورہم نے ان کے دل مضبوط کر دیے جب وہ یہ کہہ کر اٹھ کھڑے ہوئے کہ ہمارا رب آسمانوں اور زمین کا رب ہے ہم اس کے سوا کسی معبود کو ہرگز نہ پکاریں گے ورنہ ہم نے بڑی ہی بیجا بات کہی'),
(2155, 18, 15, 'یہ ہماری قوم ہے انہوں نے الله کے سوا اورمعبود بنا لیے ہیں ان پر کوئی کھلی دلیل کیوں نہیں لاتے پھر اس سے بڑا ظالم کون ہوگا جس نے الله پر جھوٹ باندھا'),
(2156, 18, 16, 'اور جب تم ان سے الگ ہو گئے ہواور الله کے سوا جنہیں وہ معبود بناتے ہیں تب غار میں چل کر پناہ لو تم پر تمہارا رب اپنی رحمت پھیلا دے گا اور تمہارے لیے تمہارےاس کام میں آرام کا سامان کر دے گا'),
(2157, 18, 17, 'اور تو سورج کو دیکھے گا جب وہ نکلتا ہے تو ان کے غار کے دائیں طرف سے ہٹا ہوا رہتا ہے اور جب ڈوبتا ہے تو ان کی بائیں طرف سے کتراتا ہو گزر جاتا ہے وہ اس کے میدان میں ہیں یہ الله کی نشانیوں میں سے ہے جسے الله ہدایت دے وہی ہدایت پانے والا ہے اور جسے وہ گمراہ کر دے پھر اس کے لیے تمہیں کوئی بھی کارساز پر لانے والا نہیں ملے گا'),
(2158, 18, 18, 'اور تو انہیں جاگتا ہوا خیال کرے گا حالانکہ وہ سو رہے ہیں اورہم انہیں دائیں بائیں پلٹتے رہتے ہیں اور ان کا کتا چوکھٹ کی جگہ اپنے دونوں بازو پھیلائے بیٹھا ہے اگر تم انہیں جھانک کر دیکھو تو الٹے پاؤں بھاگ کھڑے ہوئے اورالبتہ تم پر ان کی دہشت چھا جائے'),
(2159, 18, 19, 'اوراسی طرح ہم نے انہیں جگا دیا تاکہ ایک دوسرے سے پوچھیں ان میں سے ایک نے کہا تم کتنی دیر ٹہرے ہو انہوں نے کہا ہم ایک دن یا دن سے کم ٹھیرے ہیں کہا تمہارا رب خوب جانتا ہے جتنی دیر تم ٹھہرے ہو اب اپنے میں سے ایک کو یہ اپنا روپیہ دے کر اس شہر میں بھیجو پھر دیکھے کون سا کھانا ستھرا ہے پھر تمہارے پاس اس میں سے کھانا لائے اور نرمی سے جائے اور تمہارے متعلق کسی کو نہ بتائے'),
(2160, 18, 20, 'بے شک وہ لوگ اگر تمہاری اطلاع پائیں گے تو تمہیں سنگسار کر دیں گےیا اپنے دین میں لوٹا لیں گے پھر تم کبھی فلاح نہیں پا سکو گے'),
(2161, 18, 21, 'اور اسی طرح ہم نے ان کی خبر ظاہر کر دی تاکہ لوگ سمجھ لیں کہ الله کا وعدہ سچا ہے اور قیامت میں کوئی شک نہیں جبکہ لوگ ان کے معاملہ میں جھگڑ رہے تھے پھر کہا ان پر ایک عمارت بنا دو انکا رب ان کا حال خوب جانتا ہے ان لوگوں نے کہا جو اپنے معاملے میں غالب آ گئے تھے کہ ہم ان پر ضرور ایک مسجد بنائیں گے'),
(2162, 18, 22, 'بعض کہیں گے تین ہیں چوتھا ان کا کتا ہے اور بعض اٹکل پچو سے کہیں گے پانچ ہیں چھٹا ان کا کتا ہے اور بعض کہیں گے سات ہیں آٹھواں ان کا کتا ہے کہہ دو ان کی گنتی میرا رب ہی خوب جانتا ہے ان کا اصلی حال تو بہت ہی کم لوگ جانتے ہیں سو تو ان کے بارے میں سرسری گفتگو کے سوا جھگڑا نہ کرو ان میں سے کسی سےبھی انکا حال دریافت نہ کر'),
(2163, 18, 23, 'اورکسی چیز کے متعلق یہ ہر گز نہ کہو کہ میں کل اسے کر ہی دوں گا'),
(2164, 18, 24, 'مگر یہ کہ الله چاہے اور اپنے رب کو یاد کر لے جب بھول جائے اور کہہ دو امید ہے کہ میرا رب مجھے اس سے بھی بہتر راستہ دکھائے'),
(2165, 18, 25, 'اور وہ اپنے غار میں تین سو سے زائد نو برس رہے ہیں'),
(2166, 18, 26, 'کہہ دو الله بہتر جانتا ہے کہ کتنی مدت رہے تمام آسمانوں اور زمین کا علم غیب اسی کو ہے کیا عجیب دیکھتا اور سنتا ہے ان کا الله کے سوا کوئی بھی مددگار نہیں اور نہ ہی وہ اپنے حکم میں کسی کو شریک کرتا ہے'),
(2167, 18, 27, 'اور اپنے رب کی کتاب سے جو تیری طرف وحی کی گئی ہے پڑھا کرو اس کی باتوں کوکوئی بدلنے والا نہیں ہے اور تو اس کے سوا کوئی پناہ کی جگہ نہیں پائے گا'),
(2168, 18, 28, 'تو ان لوگو ں کی صحبت میں رہ جو صبح اور شام اپنے رب کو پکارتے ہیں اسی کی رضا مندی چاہتے ہیں اور تو اپنی آنکھوں کو ان سے نہ ہٹا کہ دنیا کی زندگی کی زینت تلاش کرنے لگ جائے اور اس شخص کا کہنا نہ مان جس کے دل کو ہم نے اپنی یاد سے غافل کر دیاہے اور اپنی خواہش کے تابع ہو گیا ہے اور ا سکا معاملہ حد سے گزر ا ہوا ہے'),
(2169, 18, 29, 'اور کہہ دو سچی بات تمہارے رب کی طرف سے ہے پھر جو چاہے مان لے اور جو چاہے انکار کر دے بے شک ہم نے ظالموں کے لیے آگ تیار کر رکھی ہے انہیں اس کی قناتیں گھیر لیں گی اور اگر فریاد کریں گے تو ایسے پانی سے فریاد رسی کیے جائیں گے جو تانبے کی طرح پگھلا ہوا ہو گا مونہوں کو جھلس دے گا کیا ہی برا پانی ہو گا او رکیا ہی بری آرام گاہ ہو گی'),
(2170, 18, 30, 'بے شک جو لوگ ایمان لائے اور اچھے کام کیے ہم بھی اس کا اجر ضائع نہیں کریں گے جس نے اچھے کام کیے'),
(2171, 18, 31, 'وہی لوگ ہیں جن کے لیے ہمیشہ رہنے کے باغ ہیں جن کے نیچے نہریں بہہ رہی ہوں گی انہیں وہاں سونے کے کنگن پہنائے جائیں گے اور باریک اور موٹے ریشم کا سبز لباس پہنیں گے وہاں تختوں پر تکیے لگانے والے ہوں گے کیا ہی اچھا بدلہ ہے اور کیا ہی اچھی آرام گا ہے'),
(2172, 18, 32, 'اور انہیں دو شحصوں کی مثال سنا دو ان دونوں میں سے ایک لیے ہم نے انگور کے دو باغ تیار کیے اور ان کے گرد اگر کھجوریں لگائیں اوران دونو ں کے درمیان کھیتی بھی لگا رکھی تھی'),
(2173, 18, 33, 'دونوں باغ اپنے پھل لاتے ہیں اور پھل لانے میں کچھ کمی نہیں کرتے اور ان دونوں کے درمیان ہم نے ایک نہر بھی جاری کردی ہے'),
(2174, 18, 34, 'اور اسے پھل مل گیا پھر اس نے اپنے ساتھی سے باتیں کرتے ہوئے کہا کہ میں تجھ سے مال میں بھی زيادہ ہوں اور جماعت کے لحاظ سے بھی زیادہ معزز ہوں'),
(2175, 18, 35, 'اور اپنے باغ میں داخل ہوا ایسے حال میں کہ وہ اپنی جان پر ظلم کرنے والا تھا کہا میں نہیں خیال کرتا کہ یہ باغ کبھی برباد ہو گا'),
(2176, 18, 36, 'اورمیں قیامت کو ہونے والی خیال نہیں کرتا اور البتہ اگر میں اپنے رب کے ہاں لوٹایا بھی گیا تو اس سے بھی بہتر جگہ پاؤں گا'),
(2177, 18, 37, 'اسے اس کے ساتھی نے گفتگو کے دوران میں نے کہا کیا تو اس کا منکر ہو گیا ہے جس نے تجھے مٹی سے پھر نطفہ سے بنایا پھر تجھے پورا آدمی بنا دیا'),
(2178, 18, 38, 'لیکن میرا تو الله ہی رب ہے اور میں اس کے ساتھ کسی کو شریک نہیں کروں گا'),
(2179, 18, 39, 'اور جب تو اپنے باغ میں آیا تھا تو نے کیوں نہ کہا جو الله چاہے تو ہوتا ہے اور الله کی مدد کے سوا کوئی طاقت نہیں اگر تو مجھے دیکھتا ہے کہ میں تجھ سے مال اور اولاد میں کم ہوں'),
(2180, 18, 40, 'پھر امید ہے کہ میرا رب مجھے تیرے باغ سے بہتر دے اور اس پر لو کا ایک جھونکا آسمان سے بھیج دے پھر وہ چٹیل میدان ہوجائے'),
(2181, 18, 41, 'یا اس کا پانی خشک ہوجائے پھر تو اسے ہرگز تلاش کرکے نہ لاسکے گا'),
(2182, 18, 42, 'اور اس کا پھل سمیٹ لیا گیا پھر وہ اپنے ہاتھ ہی ملتا رہ گیا اس پر جو اس نے اس باغ میں خرچ کیا تھا اور وہ اپنی چھتریوں پر گرا پڑا تھا اور کہا کاش میں اپنے رب کے ساتھ کسی کو شریک نہیں کرتا'),
(2183, 18, 43, 'اور اس کی کوئی جماعت نہ تھی جو الله کےسوا اس کی مدد کرتے اور نہ وہ خود ہی بدلہ لے سکا'),
(2184, 18, 44, 'یہاں سب اختیار الله سچے ہی کا ہے اسی کا انعام بہتر ہے اور اسی کا دیاہوا بدلہ اچھا ہے'),
(2185, 18, 45, 'اور ان سے دنیا کی زندگی کی مثال بیان کرو مثل ایک پانی کے ہے جسے ہم نے آسمان سے برسایا پھر زمین کی روئیدگی پانی کے ساتھ مل گئی پھر وہ ریزہ ریز ہ ہو گئی کہ اسے ہوا ئیں اڑاتی پھرتی ہیں اور الله ہر چیز پر قدرت رکنے والا ہے'),
(2186, 18, 46, 'مال اور اولاد تو دنیا کی زندگی کی رونق ہیں اور تیرے رب کے ہاں باقی رہنے والی نیکیاں ثواب اور آخرت کی امید کے لحاظ سے بہتر ہیں'),
(2187, 18, 47, 'اورجس دن ہم پہاڑوں کو چلائیں گے اور تو زمین کو صاف میدان دیکھے گا اور سب کو جمع کریں گے اور ان میں سے کسی کو بھی نہ چھوڑیں گے'),
(2188, 18, 48, 'اور سب تیرے رب کے سامنے صف باندھ کر پیش کیے جائیں گے البتہ تحقیق تم ہمارے پاس آئے ہو جیسا ہم نے تم کو پہلی بار پیدا کیا تھا بلکہ تم نے خیال کیا تھا کہ ہم تمہارے لیے کوئی وعدہ مقرر نہ کریں گے'),
(2189, 18, 49, 'اور اعمال نامہ رکھ دیا جائے گا پھر مجرموں کو دیکھے گا اس چیز سے ڈرنے والے ہوں گے جو اس میں ہے اور کہیں گے افسوس ہم پر یہ کیسا اعمال نامہ ہے کہ اس نے کوئی چھوٹی یا بڑی بات نہیں چھوڑی مگر سب کو محفوظ کیا ہوا ہے اورجو کچھ انہوں نے کیا تھا سب کو موجود پائیں گے اور تیرا رب کسی پر ظلم نہیں کرے گا'),
(2190, 18, 50, 'اور جب ہم نے فرشتوں سے کہا آدم کو سجدہ کرو تو سوائے ابلیس کے سب نے سجدہ کیا وہ جنوں میں سے تھا سو اپنے رب کے حکم کی نافرمانی کی پھر کیا تم مجھے چھوڑ کر اسے اور اس کی اولاد کو کارساز بناتے ہو حالانکہ وہ تمہارے دشمن ہیں بے انصافوں کو برا بدل ملا'),
(2191, 18, 51, 'نہ تو آسمان اور زمین کے بناتے وقت اور نہ انہیں بناتے وقت میں نے انہیں بلایا اورمیں گمراہ کرنے والوں کو اپنا مددگار بنانے والا نہ تھا'),
(2192, 18, 52, 'اورجس دن فرمائے گا میرے شریکوں کو پکارو جنہیں تم مانتے تھے پھر وہ انہیں پکاریں گے سو وہ انہیں جواب نہیں دیں گے اور ہم نے ان کے درمیان ہلاکت کی جگہ بنا دی ہے'),
(2193, 18, 53, 'اور گناہگار آگ کو دیکھیں گے اور سمجھیں گے کہ وہ اس میں گرنے والے ہیں اور اس سے بچنے کی کوئی راہ نہ پائیں گے'),
(2194, 18, 54, 'اور البتہ تحقیق ہم نے اس قرآن میں ان لوگوں کے لیے ہر ایک مثال کو کئی طرح سے بیان کیا ہے اور انسان بڑا ہی جھگڑالو ہے'),
(2195, 18, 55, 'اور جب ان کے پاس ہدایت آئی تو انہیں ایمان لانے اور اپنے رب سے معافی مانگنے سے کوئی چیز مانع نہیں ہوئی سوائے اس کے کہ انہیں پہلی امتوں کا سا معاملہ پیش آئے یا عذاب ان کے سامنے آجائے'),
(2196, 18, 56, 'اور ہم رسولوں کو صرف خوشخبری دینے اور ڈرانے والا بنا کر بھیجتے ہیں اور کافر نا حق جھگڑا کرتے ہیں تاکہ ا س سے سچی بات کو ٹلا دیں اور انہوں نے میری آیتوں کو اور جس سے انہیں ڈرایا گیا ہے مذاق بنا لیا ہے'),
(2197, 18, 57, 'اور اس سے زیادہ ظالم کون ہے جسے اس کے رب کی آیتوں سے نصیحت کی جائے پھر ان سے منہ پھیر لے اور جو کچھ اس کے ہاتھوں نے آگے بھیجا ہے بھول جائے بے شک ہم نے ان کے دلوں پر پردے ڈال دیے ہیں کہ اسے نہ سمجھیں اور ان کے کانوں میں گرانی ہے اور اگر تو انہیں ہدایت کی طرف بلائے تو بھی وہ ہر گز کبھی راہ پر نہ آئیں گے'),
(2198, 18, 58, 'اور تیرا رب بڑا بخنشے والا رحمت والا ہے اگر ان کے کیے پر انہیں پکڑنا چاہتا تو فوراً ہی عذاب بھیج دیتا بلکہ ان کے لیے ایک معیاد مقرر ہے اس کے سوا کوئی پناہ کی جگہ نہیں پائیں گے'),
(2199, 18, 59, 'اور یہ بستیاں ہیں جنہیں ہم نے ہلک کیا ہے جب انہوں نے ظلم کیا تھا اور ہم نے ان کی ہلاکت کا بھی ایک وقت مقرر کیا تھا'),
(2200, 18, 60, 'اورجب موسیٰ نے اپنے جوان سے کہا کہ میں نہ ہٹوں گا یہاں تک کہ دو دریاؤں کے ملنے کی جگہ پر پہنچ جاؤں یا سالہا سال چلتا جاؤں'),
(2201, 18, 61, 'پھر جب وہ دو دریاؤں کے جمع ہونے کی جگہ پر پہنچے دونوں اپنی مچھلی کو بھول گئے پھر مچھلی نے دریا میں سرنگ کی طرح کا راستہ بنا لیا'),
(2202, 18, 62, 'پھر جب وہ دونوں آگے بڑھ گئے تو اپنے جوان سے کہا کہ ہمارا ناشہ لے آ۔ البتہ تحقیق ہم نے اس سفر میں تکلیف اٹھائی ہے'),
(2203, 18, 63, 'کہا کیا تو نے دیکھا جب ہم اس پتھر کے پاس ٹھرے تومیں مچھلی کو وہیں بھول آیا اور مجھے شیطان ہی نے بھلایا ہے کہ اس کا ذکر کروں اور اس نے اپنی راہ سمندر میں عجیب طرح سے بنا لی'),
(2204, 18, 64, 'کہا یہی ہے جو ہم چاہتے تھے پھر اپنے قدموں کے نشان دیکھتے ہی الٹے پھرے'),
(2205, 18, 65, 'پھر ہمارے بندوں میں سے ایک بندہ کو پایا جسے ہم نے اپنے ہاں سے رحمت دی تھی اوراسے ہم نے اپنے پاس سے ایک علم سکھایا تھا'),
(2206, 18, 66, 'اسے موسیٰ نے کہا کیا میں تیرے ساتھ رہوں اس شرط پر کہ تو مجھے سکھائے اس میں سے جو تجھے ہدایت کا طریقہ سکھایا گیا ہے'),
(2207, 18, 67, 'کہا بے شک تو میرے ساتھ ہر گز صبر نہیں کر سکے گا'),
(2208, 18, 68, 'اورتو صبر کیسے کرے گا اس بات پر جو تیری سمجھ میں نہیں آئے گی'),
(2209, 18, 69, 'کہا انشاالله تو مجھے صابر ہی پائے گا اور میں کسی بات میں بھی تیری مخالفت نہیں کروں گا'),
(2210, 18, 70, 'کہا پس اگر تو میرے ساتھ رہے تو مجھ سے کسی بات کا سوال نہ کر یہاں تک کہ میں تیرے سامنے اس کا ذکر کروں پس دونوں چلے'),
(2211, 18, 71, 'یہاں تک کہ جب کشتی میں سوار ہوئے تو اسے پھاڑ دیا کہا کیا تو نے اس لیے پھاڑا ہے کہ کشتی کے لوگوں کو غرق کر دے البتہ تو نے خطرناک بات کی ہے'),
(2212, 18, 72, 'کہا کیا میں نے تجھے نہیں کہا تھا کہ تو میرے ساتھ صبر نہیں کر سکے گا'),
(2213, 18, 73, 'کہا میرے بھول جانے پر گرفت نہ کر اورمیرے معاملہ میں سختی نہ کر'),
(2214, 18, 74, 'پھر دونوں چلے یہاں تک کہ انہیں ایک لڑکا ملا تو اسے مار ڈالا کہا تو نے ایک بے گناہ کو ناحق مار ڈالا البتہ تو نے بڑی بات کی'),
(2215, 18, 75, 'کہا کیا میں نے تجھے نہیں کہا تھا کہ تو میرے ساتھ صبر نہیں کر سکے گا'),
(2216, 18, 76, 'کہا اگراس کے بعدمیں آپ سے کسی چیز کا سوال کروں تو مجھے ساتھ نہ رکھیں آپ میری طرف سے معذوری تک پہنچ جائیں گے'),
(2217, 18, 77, 'پھر ودنوں چلے یہاں تک کہ جب ایک گاؤں والوں پر گزرے توان سے کھانا مانگا انہوں نے مہمان نوازی سے انکار کر دیا پھر انہوں نے وہاں ایک دیوار پائی جو گرنےہی والی تھی تب اسے سیدھا کر دیا کہا اگر آپ چاہتے تو اس کام پر کوئی اجرت ہی لے لیتے'),
(2218, 18, 78, 'کہا اب میرے اور تیرے درمیان جدائی ہے اب میں تجھے ان باتوں کا راز بتاتا ہوں جن پر تو صبر نہ کر سکا'),
(2219, 18, 79, 'جو کشتی تھی سو وہ محتاج لوگوں کی تھی جو دریا میں مزدوری کرتے تھے پھر میں نے اس میں عیب کر دینا چاہا اور ان کے آگے ایک بادشاہ تھا جو ہر ایک کشتی کو زبردستی پکڑ رہا تھا'),
(2220, 18, 80, 'اور رہا لڑکا سو اس کے ماں باپ ایمان دار تھے سو ہم ڈرے کہ انہیں بھی سر کشی اور کفر میں مبتلا نہ کرے'),
(2221, 18, 81, 'پھر ہم نے چاہا کہ ان کا رب اس کے بدلہ میں انہیں ایسی اولاد دے جو پاکیزگی میں اس سے بہتر اور محبت میں اس سے بڑھ کر ہو'),
(2222, 18, 82, 'اور جو دیوار تھی سو وہ اس شہر کے دو یتیم بچوں کی تھی اور اس کے نیچے ان کا خزانہ تھا اور ان کا باپ نیک آدمی تھا پس تیرے رب نے چاہا کہ وہ جوان ہو کر اپنا خزانہ تیرے رب کی مہربانی سے نکالیں اور یہ کام میں نے اپنے ارادے سے نہیں کیا یہ حقیقت ہے اس کی جس پر تو صبر نہیں کر سکا'),
(2223, 18, 83, 'اور آپ سے ذوالقرنین کا حال پوچھتے ہیں کہہ دو کہ اب میں تمہیں اس کا حال سناتا ہوں'),
(2224, 18, 84, 'ہم نے اسے زمین میں حکمرانی دی تھی اور اسے ہر طرح کا سازو سامان دیا تھا'),
(2225, 18, 85, 'تو اس نے ایک ساز و سامان تار کیا'),
(2226, 18, 86, 'یہاں تک کہ جب سورج ڈوبنے کی جگہ پہنچا تو اسے ایک گرم چشمے میں ڈوبتا ہوا پایا اور وہاں ایک قوم بھی پائی ہم نے کہااے ذوالقرنین یا انھیں سزا دے اور یا ان سے نیک سلوک کر'),
(2227, 18, 87, 'کہا جو ان میں ظالم ہے اسے تو ہم سزا ہی دیں گے پھر وہ اپنے رب کے ہاں لوٹایا جائے گا پھر وہ اسے اوربھی سخت سزا دے گا'),
(2228, 18, 88, 'اور جو کوئی ایمان لائے گا اور نیکی کرے گا تو اسے نیک بدلہ ملے گا اور ہم بھی اپنے معاملے میں اسے آسان ہی حکم دیں گے'),
(2229, 18, 89, 'پھر اس نے ایک ساز و سامان تیار کیا'),
(2230, 18, 90, 'یہاں تک کہ جب سورج نکلنے کی جگہ پہنچا تو اس نے سورج کو ایک ایسی قوم پر نکلتے ہوئے پایا کہ جس کے لیے ہم نے سورج کے ادھر کوئی آڑ نہیں رکھی تھی'),
(2231, 18, 91, 'اسی طرح ہی ہے اور اس کے حال کی پوری خبر ہمارےہی پاس ہے'),
(2232, 18, 92, 'پھر اس نے ایک ساز و سامان تیار کیا'),
(2233, 18, 93, 'یہاں تک کہ جب دو پہاڑوں کے درمیان پہنچا ان دونوں سے اس طرف ایک ایسی قوم کو دیکھا جو بات نہیں سمجھ سکتی تھی'),
(2234, 18, 94, 'انہوں نے کہا کہ اے ذوالقرنین بے شک یاجوج ماجوج اس ملک میں فساد کرنے والے ہیں پھر کیا ہم آپ کے لیے کچھ محصول مقرر کر دیں اس شرط پر کہ آپ ہمارےاور ان کے درمیان ایک دیوار بنا دیں'),
(2235, 18, 95, 'کہا جو میرے رب نے قدرت دی ہے کافی ہے سو طاقت سے میری مدد کرو کہ میں تمہارے اور ان کے درمیان ایک مضبوط دیوار بنا دوں'),
(2236, 18, 96, 'مجھے لوہے کے تختے لا دو یہاں تک کہ جب دونوں سروں کے بیچ کو برابر کر دیا تو کہا کہ دھونکو یہاں تک کہ جب اسے آگ کر دیا تو کہا کہ تم میرے پاس تانبا لاؤ تاکہ اس پر ڈال دوں'),
(2237, 18, 97, 'پھر وہ نہ اس پر چڑھ سکتے تھے اور نہ اس میں نقب لگا سکتے تھے'),
(2238, 18, 98, 'کہا یہ میرے رب کی رحمت ہے پھر جب میرے رب کا وعدہ آئے گا تو اسے ریزہ ریزہ کر دے گا اور میرے رب کا وعدہ سچا ہے'),
(2239, 18, 99, 'اور ہم چھوڑدیں گے بعض ان کے اس دن بعض میں گھسیں گے اور صورمیں پھونکا جائے گا پھر ہم ان سب کو جمع کر یں گے'),
(2240, 18, 100, 'اور ہم دوزخ کو اس دن کافروں کے سامنے پیش کریں گے'),
(2241, 18, 101, 'جن کی آنکھوں پر ہماری یاد سے پردہ پڑا ہوا تھا اور وہ سن بھی نہ سکتے تھے'),
(2242, 18, 102, 'پھر کافر کیا خیال کرتے ہیں کہ میرے سوا میرےبندوں کو اپنا کارساز بنا لیں گے بے شک ہم نے کافروں کے لیے دوزخ کو مہمانی بنایاہے'),
(2243, 18, 103, 'کہہ دو کیا میں تمہیں بتاؤں جو اعمال کے لحاظ سے بالکل خسارے میں ہیں'),
(2244, 18, 104, 'وہ جن کی ساری کوشش دنیا کی زندگی میں کھوئی گئی اور وہ خیال کرتے ہیں کہ بے شک وہ اچھے کام کر رہے ہیں'),
(2245, 18, 105, 'یہ وہی لوگ ہیں جنہوں نے اپنے رب کی نشانیوں کا اور اس کے روبرو جانے کا انکار کیا ہے پھر ان کے سارے اعمال ضائع ہو گئے سو ہم ان کے لیے قیامت کے دن کوئی وزن قائم نہیں کریں گے'),
(2246, 18, 106, 'یہ سزا ا ن کی جہنم ہے ا سلیے کہ انہوں نے کفر کیا اور میری آیتوں اور میرے رسولوں کا مذاق بنایا تھا'),
(2247, 18, 107, 'بے شک جو لوگ ایمان لائے اور اچھے کام کئے ان کی مہمانی کے لیے فردوس کے باغ ہوں گےبے شک جو لوگ ایمان لائے اور اچھےکام کئےان کی مہمانی کے لیے فردوس کے باغ ہوں گے'),
(2248, 18, 108, 'ان میں ہمیشہ رہیں گے وہاں سے جگہ بدلنی نہ چاہیں گے'),
(2249, 18, 109, 'کہہ دو اگر میرے رب کی باتیں لکھنےکے لئے سمندر سیاہی بن جائے تو میرے رب کی باتیں ختم ہونے سے پہلے سمندر ختم ہو جائے اور اگرچہ اس کی مدد کے لیے ہم ایسا ہی اور سمندر لائیں'),
(2250, 18, 110, 'کہہ دو کہ میں بھی تمہارے جیسا آدمی ہی ہوں میری طرف وحی کی جاتی ہے کہ تمہارا معبود ایک ہی معبود ہے پھر جو کوئی اپنے رب سے ملنے کی امید رکھے تو اسے چاہیئے کہ اچھے کام کرے اور اپنے رب کی عبادت میں کسی کو شریک نہ بنائے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کھیصۤ'),
(2252, 19, 2, 'یہ تیرے رب کی مہربانی کا ذکر ہے جو اس کے بندے زکریا پر ہوئی'),
(2253, 19, 3, 'جب اس نے اپنے رب کو خفیہ آواز سے پکارا'),
(2254, 19, 4, 'کہا اے میرے رب میری ہڈیاں کمزور ہو گئی ہیں اور سر میں بڑھاپا چمکنے لگا ہے او رمیرے رب تجھ سے مانگ کر میں کبھی محروم نہیں ہوا'),
(2255, 19, 5, 'اور بے شک میں اپنے بعد اپنے رشتہ داروں سے ڈرتا ہوں اور میری بیوی بانجھ ہے پس تو اپنے ہاں سے ایک وارث عطا کر'),
(2256, 19, 6, 'جو میرا اور یعقوب کے خاندان کا بھی وارث ہو اور میرے رب اسے پسندیدہ بنا'),
(2257, 19, 7, 'اے زکریا بے شک ہم تجھے ایک لڑکے کی خوشخبری دیتے ہیں جس کا نام یحییٰ ہوگا اس سےپہلے ہم نے اس نام کا کوئی پیدا نہیں کیا'),
(2258, 19, 8, 'کہا اے میرے رب میرے لیے لڑکا کہاں سے ہوگا حالانکہ میری بیوی بانجھ ہے اور میں بڑھاپے میں انتہائی درجہ کو پہنچ گیا ہوں'),
(2259, 19, 9, 'کہا ایسا ہی ہوگا تیرے رب نے کہاہے وہ مجھ پر آسان ہے اور میں نے تجھے اس سے پہلے پیدا کیا حالانکہ تو کوئی چیز نہ تھا'),
(2260, 19, 10, 'کہا اے میرے رب میرے لئےکوئی نشانی مقرر کر کہا تیری نشانی یہ ہے کہ توتین رات تک مسلسل لوگوں سے بات نہیں کر سکے گا'),
(2261, 19, 11, 'پھر حجرہ سے نکل کر اپنی قوم کے پاس آئے اور انہیں اشارہ سے کہا کہ تم صبح و شام خدا کی تسبیح کیا کرو'),
(2262, 19, 12, 'اے یحییٰ کتاب کو مضبوطی سے پکڑ اور ہم نے اسےبچپن ہی میں حکمت عطا کی'),
(2263, 19, 13, 'اور اسے اپنے ہاں سے رحم دلی او رپاکیز گی عنایت کی اور وہ پرہیز گار تھا'),
(2264, 19, 14, 'اور اپنے ماں باپ کے ساتھ نیک سلوک کرنے والا اور سرکش نافرمان نہ تھا'),
(2265, 19, 15, 'اور اس پر سلام ہو جس دن وہ پیدا ہوا اور جس دن مرے گا اور جس دن وہ زندہ کرکے اٹھایا جائے گا'),
(2266, 19, 16, 'اور اس کتاب میں مریم کا ذکر کر جب کہ وہ اپنے لوگوں سے علیحدہ ہو کر مشرقی مقام میں جا بیٹھی'),
(2267, 19, 17, 'پھر لوگوں کے سامنے سے پردہ ڈال لیا پھر ہم نے اس کے پاس اپنے فرشتے کو بھیجا پھر وہ اس کے سامنے پورا آدمی بن کر ظاہر ہوا'),
(2268, 19, 18, 'کہا بے شک میں تجھ سے الله کی پناہ مانگتی ہوں اگر تو پرہیزگار ہے'),
(2269, 19, 19, 'کہا میں تو بس تیرے رب کا بھیجا ہوا ہوں تاکہ تجھے پاکیزہ لڑکا دوں'),
(2270, 19, 20, 'کہا میرے لیے لڑکا کہاں سے ہوگا حالانکہ مجھے کسی آدمی نے ہاتھ نہیں لگایا اور نہ میں بدکار ہوں'),
(2271, 19, 21, 'کہا ایسا ہی ہوگا تیرے رب نے کہا ہے وہ مجھ پر آسان ہے اور تاکہ ہم اسے لوگوں کے لیے نشانی اور اپنی طرف سے رحمت بنائیں اور یہ بات طے ہو چکی ہے'),
(2272, 19, 22, 'پھر اس (بچہ کے ساتھ) حاملہ ہوئی پھر اسے لے کر کسی دور جگہ میں چلی گئی'),
(2273, 19, 23, 'پھر اسے درد زہ ایک کھجور کی جڑ میں لے آیا کہا اے افسوس میں اس سے پہلے مرگئی ہوتی اور میں بھولی بھلائی ہوتی'),
(2274, 19, 24, 'پھر اسے اس کے نیچے سے پکارا کہ غم نہ کر تیرے رب نے تیرے نیچے سے ایک چشمہ پیدا کر دیا'),
(2275, 19, 25, 'اورتو کھجور کے تنہ کو پکڑ کر اپنی طرف ہلا تجھ پر پکی تازہ کھجوریں گریں گی'),
(2276, 19, 26, 'سوکھا اور پی اور آنکھ ٹھنڈی کر پھر اگر تو کوئی آدمی دیکھے تو کہہ دے کہ میں نے رحمان کے لیے روزہ کی نذر مانی ہے سو آج میں کسی انسان سے بات نہیں کروں گی'),
(2277, 19, 27, 'پھر وہ اسے قوم کے پا س اٹھا کر لائی انہوں نے کہا اے مریم البتہ تو نے عجیب بات کر دکھائی'),
(2278, 19, 28, 'اے ہارون کی بہن نہ تو تیرا باپ ہی برا آدمی تھا اور نہ ہی تیری ماں بدکار تھی'),
(2279, 19, 29, 'تب اس نے لڑکے کی طرف اشارہ کیا انہوں نے کہا ہم پنگوڑھے والے بچے سے کیسے بات کریں'),
(2280, 19, 30, 'کہا بے شک میں الله کا بندہ ہوں مجھےاس نے کتاب دی ہے اور مجھے نبی بنایا ہے'),
(2281, 19, 31, 'اور مجھے با برکت بنایاہے جہاں کہیں ہوں اور مجھے کو نماز اور زکواة کی وصیت کی ہے جب تک میں زندہ ہوں'),
(2282, 19, 32, 'اور اپنی ماں کے ساتھ نیکی کرنے والا اور مجھے سرکش بدبخت نہیں بنایا'),
(2283, 19, 33, 'اور مجھ پر سلام ہے جس دن میں پیدا ہوا اور جس دن مروں گا اور جس دن زندہ کر کے اٹھایا جاؤں گا'),
(2284, 19, 34, 'یہ عیسیٰ مریم کا بیٹا ہے سچی بات جس میں وہ جھگڑ رہے ہیں'),
(2285, 19, 35, 'الله کی شان نہیں کہ وہ کسی کو بیٹا بنائے وہ پاک ہے جب کسی کام کا فیصلہ کرتا ہے توصرف اسے کن کہتا ہے پھر وہ ہو جاتا ہے'),
(2286, 19, 36, 'اور بے شک الله تعالیٰ میرا اور تمہارا رب ہے سواسی کی عبادت کرو یہ سیدھا راستہ ہے'),
(2287, 19, 37, 'پھر جماعتیں آپس میں مختلف ہو گئیں سوکافروں کے لیے ایک بڑے دن کے آنے سے خرابی ہے'),
(2288, 19, 38, 'کیا خوب سنتے اور دیکھتے ہوں گے جس دن ہمارے پاس آئیں گے لیکن ظالم آج صریح گمراہی میں ہیں'),
(2289, 19, 39, 'اور انہیں حسرت کے دن سے ڈرا جس دن سارے معاملہ کا فیصلہ ہوگا اور وہ غفلت میں ہیں او رایمان نہیں لاتے'),
(2290, 19, 40, 'بے شک ہم ہی زمین کے وارث ہوں گے اور ان کے بھی جو اس پر ہیں اور ہماری طرف لوٹائے جائیں گے'),
(2291, 19, 41, 'اور کتاب میں ابراھیم کا ذکر بے شک وہ سچا نبی تھا'),
(2292, 19, 42, 'جب اپنے باپ سے کہا اے میرے باپ تو کیوں پوجتاہے ایسے کو جونہ سنتا ہے اور نہ دیکھتا ہے اور نہ تیرےکچھ کام آ سکے'),
(2293, 19, 43, 'اے میرے باپ بے شک مجھے وہ علم حاصل ہوا ہے جو تمہیں حاصل نہیں تو آپ میری تابعداری کریں میں آپ کو سیدھا راستہ دکھاؤں گا'),
(2294, 19, 44, 'اے میرے باپ شیطان کی عبادت نہ کر بے شک شیطان الله کا نافرمان ہے'),
(2295, 19, 45, 'اے میرے باپ بے شک مجھے خوف ہے کہ تم پر الله کا عذاب آئے پھر شیطان کے ساتھی ہوجاؤ'),
(2296, 19, 46, 'کہا اے ابراھیم کیا تو میرے معبودوں سے پھرا ہوا ہے البتہ اگر تو باز نہ آیا میں تجھے سنگسار کردوں گا اور مجھ سے ایک مدت تک دور ہو جا'),
(2297, 19, 47, 'کہا تیری سلامتی رہے اب میں اپنے رب سے تیری بخشش کی دعا کروں گا بے شک وہ مجھ پر بڑا مہربان ہے'),
(2298, 19, 48, 'اور میں تمہیں چھوڑتا ہوں اور جنہیں تم الله کے سوا پکارتے ہو اور میں اپنے رب ہی کو پکاروں گا امید ہے کہ میں اپنے رب کو پکار کر محروم نہ رہوں گا'),
(2299, 19, 49, 'پھر جب ان سے علیحدٰہ ہوا اور اس چیز سے جنہیں وہ الله کے سوا پوجتے تھے ہم نے اسے اسحاق اور یعقوب عطا کیا اور ہم نے ہر ایک کو نبی بنایا'),
(2300, 19, 50, 'اور ہم نے ان سب کو اپنی رحمت سے حصہ دیا اور ہم نے ان کا نیک نام بلند کیا'),
(2301, 19, 51, 'اور کتاب میں موسیٰ کا ذکر کر بے شک وہ خاص بندے اور بھیجے ہوئے پیغمبر تھے'),
(2302, 19, 52, 'اور ہم نے اسے کوہِ طور کے دائیں طرف سے پکارا اور اسے راز کی بات کہنے کے لیے قریب بلایا'),
(2303, 19, 53, 'اور ہم نے اسے اپنی رحمت سے ان کے بھائی ہارون کو نبی بنا کر عطا کیا'),
(2304, 19, 54, 'اور کتاب میں اسماعیل کا بھی ذکر کر بے شک وہ وعدہ کا سچا اور بھیجا ہوا پیغمبر تھا'),
(2305, 19, 55, 'اور اپنے گھر والوں کو نماز اور زکوٰةکا حکم کرتا تھا اور وہ اپنے رب کے ہاں پسندیدہ تھا'),
(2306, 19, 56, 'اور کتاب میں ادریس کا ذکر کر بے شک وہ سچا نبی تھا'),
(2307, 19, 57, 'اور ہم نے اسے بلند مرتبہ پر پہنچایا'),
(2308, 19, 58, 'یہ وہ لوگ ہیں جن پر الله نے انعام کیا پیغمبروں میں اور آدم کی اولاد میں سے اور ان میں سے جنہیں ہم نے نوح کے ساتھ سوار کیا تھا اور ابراھیم اور اسرائیل کی اولاد میں سے اور ان میں سے جنہیں ہم نے ہدایت کی اور پسند کیا جب ان پر الله کی آیتیں پڑھی جاتی ہیں تو روتے ہوئے سجدے میں گرتے ہیں'),
(2309, 19, 59, 'پھر ان کی جگہ ایسے ناخلف آئے جنہو ں نے نماز ضائع کی اور خواہشوں کے پیچھے پڑ گئے پھر عنقریب گمراہی کی سزا پائیں گے'),
(2310, 19, 60, 'مگر جس نے توبہ کی اور ایمان لایا اور نیک کام کیے سو وہ لوگ بہشت میں داخل ہوں گے اور ان کا ذرا نقصان نہ کیا جائے گا'),
(2311, 19, 61, 'ہمیشگی کے باغوں میں جن کا رحمان نے اپنے بندوں سے وعدہ کیا ہے جو ان کی آنکھوں سے پوشیدہ ہے بے شک اس کا وعدہ پورا ہونے والا ہے'),
(2312, 19, 62, 'اس میں سوائے سلام کے کوئی فضول بات نہ سنیں گے اور انہیں وہاں صبح و شام کھانا ملے گا'),
(2313, 19, 63, 'یہ وہ جنت ہے کہ ہم اپنے بندوں میں سے اس کو وارث بنائیں گے جو پرہیزگار ہوگا'),
(2314, 19, 64, 'اورہم تیرے رب کے حکم کے سوا نہیں اترتے اسی کا ہے جو کچھ ہمارے سامنے ہے اور جو کچھ ہمارے پیچھے ہے اور جو کچھ اس کے درمیان ہے او ر تیرا رب بھولنے والا نہیں'),
(2315, 19, 65, 'آسمانوں اور زمین کا رب ہے اور جو چیز ان کے درمیان ہے سو اسی کی عبادت کرو اسی کی عبادت پر قائم رہ کیا تیرے علم میں ا س جیسا کوئی اور ہے'),
(2316, 19, 66, 'اور انسان کہتا ہے جب میں مرجاؤں گا تو کیا پھر زندہ کر کے نکالا جاؤں گا'),
(2317, 19, 67, 'کیا انسان کو یاد نہیں ہے کہ اس سے پہلے ہم نے اسےبنایا تھا اور وہ کوئی چیز بھی نہ تھا'),
(2318, 19, 68, 'سو تیرے رب کی قسم ہے ہم انہیں اور ان کے شیطانوں کو ضرور جمع کریں گے پھر ہم انہیں گھٹنوں پر گرے ہوئے دوزخ کے گرد حاضر کریں گے'),
(2319, 19, 69, 'پھر ہر گروہ میں سے ان لوگوں کو الگ کر لیں گے جو الله سے بہت ہی سرکش تھے'),
(2320, 19, 70, 'پھر ہم ان لوگو ں کو خوب جانتے ہیں جو دوزخ میں جانے کے زیادہ مستحق ہیں'),
(2321, 19, 71, 'اور تم میں سے کوئی بھی ایسا نہیں جس کا اس پر گزر نہ ہو یہ تیرے رب پر لازم مقرر کیا ہوا ہے'),
(2322, 19, 72, 'پھر ہم انہیں بچا لیں گے جو ڈرتے ہیں اور ظالموں کو اس میں گھٹنوں پر گرے ہوئے چھوڑ دیں گے'),
(2323, 19, 73, 'اور جب انہیں ہماری کھلی ہوئی آیتیں سنائی جاتی ہیں تو کافر ایمان داروں سے کہتے ہیں دونوں فریقوں میں سے کس کا مرتبہ بہتر ہے اور محفل کس کی اچھی ہے'),
(2324, 19, 74, 'اور ہم ان سے پہلے کتنی جماعتیں ہلاک کرچکے ہیں وہ سامان اور نمود میں بہتر تھے'),
(2325, 19, 75, 'کہہ دو جو شخص گمراہی میں پڑا ہوا ہے سو الله بھی اسے ڈھیل دیتا ہے یہاں تک کہ جب اس چیز کو دیکھیں گے جس کا انہیں نے وعدہ دیا گیا تھا یا عذاب یا قیامت تب معلوم کر لیں گے مرتبے میں کون برا ہے اور لشکر کس کا کمزور ہے'),
(2326, 19, 76, 'اور جو لوگ ہدایت پر ہیں الله انہیں زیادہ ہدایت دیتا ہے اور باقی رہنے والی نیکیاں تیرے رب کے نزدیک ثواب اور انجام کے لحاظ سے بہت ہی بہتر ہیں'),
(2327, 19, 77, 'کیا تو نے اس شخص کو دیکھا جس نے ہماری آیتوں کا انکار کیا اور کہتا ہے کہ مجھے ضرور مال اور اولاد ملے گی'),
(2328, 19, 78, 'کیا اس نے غیب پر اطلاع پائی ہے یا اس نے الله سے اقرار لے رکھا ہے'),
(2329, 19, 79, 'ہرگز نہیں ہم لکھ لیتے ہیں جو کچھ وہ کہتا ہے اور اس کے لیے عذاب بڑھاتے جاتے ہیں'),
(2330, 19, 80, 'اور ہم اس کے وارث ہوں گے جو کچھ وہ کہتا ہے او رہمارے ہاں تنہا آئے گا'),
(2331, 19, 81, 'اور انہوں نے الله کے سوا معبود بنا لیے ہیں تاکہ وہ ان کے مددگار ہوں'),
(2332, 19, 82, 'ہرگز نہیں وہ جلد ہی ان کی عبادت کا انکار کریں گے اور ان کے مخالف ہوجائیں گے'),
(2333, 19, 83, 'کیا تو نے نہیں دیکھا ہم نے شیطانو ں کوکافروں پر چھوڑ رکھا ہے وہ انھیں ابھارتے رہتے ہیں'),
(2334, 19, 84, 'سوتو ان کے لیے عذاب کی جلدی نہ کر ہم خود ان کے دن گن رہے ہیں'),
(2335, 19, 85, 'جس دن ہم پرہیزگاروں کے رحمان کے پاس مہمان بنا کر جمع کریں گے'),
(2336, 19, 86, 'اور گناہگاروں کو دوزخ کی طرف پیاسا ہانکیں گے'),
(2337, 19, 87, 'کسی کو سفارش کا اختیار نہیں ہوگا مگر جس نے رحمان کے ہاں سے اجازت لی ہو'),
(2338, 19, 88, 'اورکہتے ہیں کہ رحمان نے بیٹا بنا لیا'),
(2339, 19, 89, 'البتہ تحقیق تم سخت بات زبان پر لائے ہو'),
(2340, 19, 90, 'کہ جس سے ابھی آسمان پھٹ جائیں اور زمین چر جائے اور پہاڑ ٹکڑے ہو کر گر پڑیں'),
(2341, 19, 91, 'اس لئے کہ انہوں نے رحمان کے لیے بیٹا تجویز کیا'),
(2342, 19, 92, 'اور رحمان کی یہ شان نہیں کہ کسی کوبیٹا بنائے'),
(2343, 19, 93, 'جو کچھ آسمانوں اور زمین میں ہے ان میں سے ایسا کوئی نہیں جو رحمان کا بندہ بن کر نہ آئے'),
(2344, 19, 94, 'البتہ تحقیق اس نے انھیں شمار کر رکھا ہے اور ان کی گنتی گن رکھی ہے'),
(2345, 19, 95, 'او رہر ایک ان میں سے اس کے ہاں اکیلا آئے گا'),
(2346, 19, 96, 'بے شک جو ایمان لائے اور نیک کام کیے عنقریب رحمان ان کے لیے محبت پیدا کرے گالاشبہ جو لوگ ایمان لائے اور انہوں نے اچھے کام کیے الله تعالیٰ ان کے لیے محبت پیدا کر ے گا'),
(2347, 19, 97, 'سو ہم نے فرمان کو تیری زبان میں اس لیےآسان کیا ہے کہ تو اس سے پرہیز گاروں کو خوشخبری سنا دے اور جھگڑنے والوں کو ڈرا دے'),
(2348, 19, 98, 'اور ہم ان سے پہلے کئی جماعتیں ہلاک کر چکے ہیں کیا تو کسی کی ان میں سے آہٹ پاتا ہے یا ان کی بھنک سنتا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طہۤ'),
(2350, 20, 2, 'ہم نے تم پر قرآن اس لیے نازل نہیں کیا کہ تم تکلیف اٹھاؤ'),
(2351, 20, 3, 'بلکہ اس شخص کے لیے نصیحت ہےجو ڈرتا ہے'),
(2352, 20, 4, 'اس کی طرف سے نازل ہوا ہے جس نے زمین اور بلند آسمانوں کو پیدا کیا'),
(2353, 20, 5, 'رحمان جو عرش پر جلوہ گر ہے'),
(2354, 20, 6, 'اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور جوکچھ گیلی زمین کے نیچے ہے'),
(2355, 20, 7, 'اور اگرتو پکار کر بات کہے تو وہ پوشیدہ اور اس سے بھی زیادہ پوشیدہ کو جانتا ہے'),
(2356, 20, 8, 'الله ہے جس کے سوا کوئی معبود نہیں اس کے سب نام اچھے ہیں'),
(2357, 20, 9, 'اور کیا تجھے موسیٰ کی بات پہنچی ہے'),
(2358, 20, 10, 'جب اس نے آگ دیکھی تو اپنے گھر والوں سے کہا کہ ٹھہرو میں نے آگ دیکھی ہے شاید کہ میں اس سے تمہارے پاس کوئی چنگاری لاؤں یا وہاں کوئی رہبر پاؤں'),
(2359, 20, 11, 'پھر جب وہ اس کے پاس آئے تو آواز آئی کہ اے موسیٰ'),
(2360, 20, 12, 'میں تمہارا رب ہوں سو تم اپنی جوتیاں اتار دو بے شک تم پاک وادی طویٰ میں ہو'),
(2361, 20, 13, 'اور میں نے تجھے پسند کیا ہے جو کچھ وحی کی جارہی ہےاسے سن لو'),
(2362, 20, 14, 'بے شک میں ہی الله ہوں میرے سوا کوئی معبود نہیں پس میری ہی بندگی کر اور میری ہی یاد کے لیے نماز پڑھا کر'),
(2363, 20, 15, 'بے شک قیامت آنے والی ہے میں اسے پوشیدہ رکھنا چاہتا ہوں تاکہ ہر شخص کو اس کے کیے کا بدلہ مل جائے'),
(2364, 20, 16, 'سو تمہیں قیامت سے ایسا شخص باز نہ رکھنے پائے جو اس پر ایمان نہیں رکھتا اوراپنی خواہشوں پر چلتا ہے پھر تم تباہ ہوجاؤ'),
(2365, 20, 17, 'اور اے موسیٰ تیرے دائیں ہاتھ میں کیا ہے'),
(2366, 20, 18, 'کہا یہ میری لاٹھی ہے ا س پر ٹیک لگاتا ہوں اور اس سے اپنی بکریوں پر پتے جھاڑتا ہوں اور اس میں میرے لیے اور بھی فائدے ہیں'),
(2367, 20, 19, 'فرمایا اے موسیٰ اسے ڈال دو'),
(2368, 20, 20, 'پھر اسے ڈال دیا تو اسی وقت وہ دوڑتا ہوا سانپ ہو گیا'),
(2369, 20, 21, 'فرمایا اسے پکڑ لے اور نہ ڈر ہم ابھی اسےپہلی حالت پر پھیر دیں گے'),
(2370, 20, 22, 'اور اپنا ہاتھ اپنی بغل سے ملا دے بلا عیب سفید ہو کر نکلے گا یہ دوسری نشانی ہے'),
(2371, 20, 23, 'تاکہ ہم تجھے اپنی بڑی نشانیوں میں سے بعض دکھائیں'),
(2372, 20, 24, 'فرعون کے پاس جا بے شک وہ سرکش ہو گیا ہے'),
(2373, 20, 25, 'کہا اے میرے رب میرا سینہ کھول دے'),
(2374, 20, 26, 'اورمیرا کام آسان کر'),
(2375, 20, 27, 'اور میری زبان سے گرہ کھول دے'),
(2376, 20, 28, 'کہ میری بات سمجھ لیں'),
(2377, 20, 29, 'اور میرے لیے میرے کنبے میں سے ایک معاون بنا دے'),
(2378, 20, 30, 'ہارون کو جو میرا بھائی ہے'),
(2379, 20, 31, 'ا س سے میری کمر مضبوط کر دے'),
(2380, 20, 32, 'اور اسے میرے کام میں شریک کر دے'),
(2381, 20, 33, 'تاکہ ہم تیری پاک ذات کا بہت بیان کریں'),
(2382, 20, 34, 'اور تجھے بہت یاد کریں'),
(2383, 20, 35, 'بے شک تو ہمیں خوب دیکھتا ہے'),
(2384, 20, 36, 'فرمایا اے موسیٰ تیری درخواست منظور ہے'),
(2385, 20, 37, 'اورالبتہ تحقیق ہم نے تجھ پر ایک دفعہ اور بھی احسان کیا ہے'),
(2386, 20, 38, 'جب ہم نے تیری ماں کے دل میں بات ڈال دی تھی'),
(2387, 20, 39, 'کہ اسے صندوق میں ڈال دے پھر اسے دریا میں ڈال دے پر اسے دریا کنارے پر ڈال دے گا اسے میرا دشمن اور اس کا دشمن اٹھا لے گا اور میں نے تجھ پر اپنی طرف سے محبت ڈال دی اور تاکہ تو میرے سامنے پرورش پائے'),
(2388, 20, 40, 'جب تیری بہن کہتی جا رہی تھی کیا تمہیں ایسی عورت بتاؤں جو اسے اچھی طرح پالے پھر ہم نے تجھے تیری ماں کے پاس پہنچا دیا کہ اس کی آنکھ ٹھنڈی ہو اور غم نہ کھائے اور تو نے ایک شخص کو مار ڈالا پھر ہم نے تجھے اس غم سے نکالا اور ہم نے تجھے کئی مرتبہ آزمائش میں ڈالا پھر تو مدین والوں میں کئی برس رہا پھر تو اے موسیٰ تقدیر سے یہاں آیا'),
(2389, 20, 41, 'اور میں نے تجھے خاص اپنے واسطے بنایا'),
(2390, 20, 42, 'تو اور تیرا بھائی میری نشانیاں لے کر جاؤ اور میری یاد میں کوتاہی نہ کرو'),
(2391, 20, 43, 'فرعون کے پاس جاؤ بے شک وہ سرکش ہو گیا ہے'),
(2392, 20, 44, 'سو اس سے نرمی سے بات کرو شاید وہ نصیحت حاصل کرے یا ڈر جائے'),
(2393, 20, 45, 'کہا اے ہمارے رب ہمیں ڈر ہے کہ وہ ہم پر زیادتی کرے یا یہ کہ زیادہ سرکشی کرے'),
(2394, 20, 46, 'فرمایا ڈرو مت میں تمہارے ساتھ سنتا اور دیکھتا ہوں'),
(2395, 20, 47, 'سو تم دونوں اس کے پاس جاؤ اورکہو کہ بے شک ہم تیرے رب کی طرف سے پیغام لے کر آئے ہیں کہ بنی اسرائیل کو ہمارے ساتھ بھیج دے اور انہیں تکلیف نہ دے ہم تیرے پاس تیرے رب کی طرف سے نشانی لے کر آئے ہیں اور سلامتی اس کے لیے ہے جو سیدھی راہ پر چلے'),
(2396, 20, 48, 'بے شک ہمیں وحی سے بتایا گیا ہے کہ عذاب اسی پر ہوگا جو جھٹلائے اور منہ پھیر لے'),
(2397, 20, 49, 'کہا اے موسیٰ پھر تمہارا رب کون ہے'),
(2398, 20, 50, 'کہا ہمارا رب وہ ہے جس نے ہر چیز کو اس کی صورت عطا کی پھر راہ دکھائی'),
(2399, 20, 51, 'کہا پھر پہلی جماعتوں کا کیا حال ہے'),
(2400, 20, 52, 'کہا ان کا علم میرے رب کے ہاں کتاب میں ہے میرا رب نہ غلطی کرتا ہے اور نہ بھولتا ہے'),
(2401, 20, 53, 'جس نے تمہارے لیے زمین کو بچھونا بنایا اور تمہارے لیے اس میں راستے بنائے اور آسمان سے پانی نازل کیا پھر ہم نے اس میں طرح طرح کی مختلف سبزیاں نکالیں'),
(2402, 20, 54, 'کھاؤ اور اپنے مویشیوں کو چراؤ بے شک اس میں عقل والوں کے لیے نشانیاں ہیں'),
(2403, 20, 55, 'اسی زمین سے ہم نے تمہیں بنایا اور اسی میں لوٹائیں گے اور دوبارہ اسی سے نکالیں گے'),
(2404, 20, 56, 'اورہم نے فرعون کو اپنی سب نشانیاں دکھا دیں پھر اس نے جھٹلایا اورانکار کیا'),
(2405, 20, 57, 'کہا اے موسیٰ کیا تو ہمیں اپنے جادو سے ہمارے ملک سے نکالنے کے لیے آیا ہے'),
(2406, 20, 58, 'سو ہم بھی تیرے مقابلے میں ایک ایسا ہی جادو لائیں گے سوہمارے او راپنے درمیان ایک وعدہ مقرر کر دے نہ ہم اس کے خلاف کریں اورنہ تو کسی صاف میدان میں'),
(2407, 20, 59, 'کہا تمہارا وعدہ جشن کا دن ہے اور دن چڑھے لوگ اکھٹے کیے جائیں'),
(2408, 20, 60, 'پھر فرعون لوٹ گیا اور اپنے مکر کا سامان جمع کیا پھر آیا'),
(2409, 20, 61, 'موسیٰ نے کہا افسوس تم الله پر بہتان نہ باندھو ورنہ وہ کسی عذاب سے تمہیں ہلاک کر دے گا اوربے شک جس نے جھوٹ بنایا وہ غارت ہوا'),
(2410, 20, 62, 'پھر ان کا آپس میں اختلاف ہو گیا اور خفیہ گفتگو کرتے رہے'),
(2411, 20, 63, 'کہا بے شک یہ دونوں جادوگر ہیں چاہتے ہیں کہ اپنے جادو کے زور سے تمہیں تمہارے ملک سے نکال دیں اور تمہارے عمدہ طریقہ کو موقوف کر دیں'),
(2412, 20, 64, 'پھر تم اپنی تدبیر جمع کر کے صف باندھ کر آؤ اور تحقیق آج جیت گیا جو غالب رہا'),
(2413, 20, 65, 'کہا اے موسیٰ یا تو ڈال اوریا ہم پہلے ڈالنے والے ہوں'),
(2414, 20, 66, 'کہا بلکہ تم ڈالو پس اچانک ان کی رسیاں او رلاٹھیاں ان کے جادو سے اس کے خیال میں دوڑ رہی ہیں'),
(2415, 20, 67, 'پھر موسیٰ نے اپنے دل میں ڈر محسوس کیا'),
(2416, 20, 68, 'ہم نے کہا ڈرو مت بیشک تو ہی غالب ہوگا'),
(2417, 20, 69, 'او رجو تیرے دائیں ہاتھ میں ہے ڈال دے کہ نگل جائے جو کچھ انہوں نے بنایا ہےجو کچھ کہ انہوں نے بنایا ہے صرف جادوگر کا فریب ہے اورجادوگر کا بھلا نہیں ہوتا جہاں بھی ہو'),
(2418, 20, 70, 'پھر جادوگر سجدہ میں گر پڑے کہا ہم ہارون اورموسیٰ کے رب پر ایمان لائے'),
(2419, 20, 71, 'کہا تم میری اجازت سے پہلےہی اس پر ایمان لے آئے بے شک یہ تمہارا سردار ہے جس نے تمہیں جادو سکھایا سو اب میں تمہارے ہاتھ اور دوسری طرف کے پاؤں کٹواؤں گا اورتمہیں کھجور کے تنوں پر سولی دوں گا اورتمہیں معلوم ہوجائے گا ہم میں سے کس کا عذاب سخت اور دیر تک رہنے والا ہے'),
(2420, 20, 72, 'کہا ہم تجھے ہرگزترجیح نہ دیں گے ان کھلی ہوئی نشانیوں کے مقابلہ میں جو ہمارے پاس آ چکی ہیں اورنہ اس کے مقابلہ میں جس نے ہمیں پیدا کیا ہے سو تو کر گزر جو تجھے کرنا ہے تو صرف اس دنیا کی زندگی پر حکم چلا سکتا ہے'),
(2421, 20, 73, 'بے شک ہم اپنے رب پر ایمان لائے ہیں تاکہ ہمارے گناہ معاف کرے اور جو تو نے ہم سے زبردستی جادو کرایا اور الله بہتر اور سدا باقی رہنے والا ہے'),
(2422, 20, 74, 'بے شک جو شخص اپنے رب کے پا س مجرم ہو کر آئے گا سواس کے لیے دوزخ ہے جس میں نہ مرے گا اور نہ جیے گا'),
(2423, 20, 75, 'اورجو اس کے پاس مومن ہوکر آئے گا حالانکہ اس نے اچھے کام بھی کیے ہوں تو ان کے لیے بلند مرتبے ہوں گے'),
(2424, 20, 76, 'ہمیشہ رہنے کے باغ جن کے نیچے نہریں بہتی ہوں گی وہ ان میں ہمیشہ رہیں گے اور یہ اس کی جزا ہے جو گناہ سے پاک ہوا'),
(2425, 20, 77, 'او رہم نے البتہ موسیٰ کو وحی کی کہ میرے بندوں کو راتوں رات لے جا پھر ان کے لیے دریامیں خشک راستہ بنا دے پکڑے جانے سے نہ ڈر اور نہ کسی خطرہ کا خوف کھا'),
(2426, 20, 78, 'پھر فرعون نے اپنے لشکر کو لے کر ان کا پیچھا کیا پھر انہیں دریا نےڈھانپ لیا جیسا ڈھانپا'),
(2427, 20, 79, 'اور فرعون نے اپنی قوم کو بہکایا اور راہ پر نہ لایا'),
(2428, 20, 80, 'اے بنی اسرائیل ہم نے تمہیں تمہارے دشمن سے نجات دی اور تم سے طور کی دائیں طرف کا وعدہ کیا اور تم پر من و سلویٰ اتارا'),
(2429, 20, 81, 'کھاؤ جو ستھری چیزیں ہم نے تمہیں دی ہیں اور اس میں حد سے نہ گزرو پھر تم پر میرا غضب نازل ہو گا اور جس پرمیرا غضب نازل ہوا سو وہ گڑھے میں جا گرا'),
(2430, 20, 82, 'اور بے شک میں بڑا بخشنے والا ہوں اس کو جو توبہ کرے اور ایمان لائے اور اچھے کام کرے پھر ہدایت پر قائم رہے'),
(2431, 20, 83, 'اور اے موسیٰ تجھے اپنی قوم سے پہلے جلدی آنے کا کیا سبب ہوا'),
(2432, 20, 84, 'کہا وہ بھی میرے پیچھے یہ آ رہے ہیں اور اے میرے رب میں جلدی تیرے طرف آیا تاکہ تو خوش ہو'),
(2433, 20, 85, 'فرمایا تیری قوم کو تیرے بعد ہم نے آزمائش میں ڈال دیاہےاور انہیں سامری نے گمراہ کر دیا ہے'),
(2434, 20, 86, 'پھر موسیٰ اپنی قوم کی طرف غصہ میں بھرے ہوئے افسوس کرتے ہوئے لوٹے کہا اے میری قوم کیا تمہارے رب نے تم سے اچھا وعدہ نہیں کیا تھا پھر کیا تم پر بہت زمانہ گزر گیا تھا یا تم نے چاہا کہ تم پر تمہارے رب کا غصہ نازل ہو تب تم نے مجھ سے وعدہ خلافی کی'),
(2435, 20, 87, 'کہا ہم نےاپنے اختیا ر سے آپ سے وعدہ خلافی نہیں کی لیکن ہم سے اس قوم کے زیور کا بوجھ اٹھوایا گیا تھا سو ہم نے اسے ڈال دیا پھر اسی طرح سامری نے ڈال دیا'),
(2436, 20, 88, 'پھر ان کے لیے ایک بچھڑا نکال لایا ایک جسم جس میں گائے کی آواز تھی پھر کہا یہ تمہارا اور موسیٰ کا معبود ہے سو وہ بھول گیا ہے'),
(2437, 20, 89, 'کیا پس وہ نہیں دیکھتے کہ وہ انہیں کس بات کا جواب نہیں دیتا اور ان کے نقصان اور نفع کا بھی اسے اختیار نہیں'),
(2438, 20, 90, 'اور انہیں ہارون نے اس سے پہلے کہہ دیا تھا اے میری قوم اس سے تمہاری آزمائش کی گئی ہے اور بے شک تمہارا رب رحمان ہے سو میری پیروی کرو اور میرا کہا مانو'),
(2439, 20, 91, 'کہا ہم برابر اسی پر جمے بیٹھے رہیں گے یہاں تک کہ موسیٰ ہمارے پاس لوٹ کر آئے'),
(2440, 20, 92, 'کہا اے ہارون تمہیں کس چیز نے روکا جب تم نے دیکھا تھا کہ وہ گمراہ ہو گئے ہیں'),
(2441, 20, 93, 'تو میرے پیچھے نہ آیا کیا تو نے بھی میری حکم عدولی کی'),
(2442, 20, 94, 'کہا اے میری ماں کے بیٹے میری داڑھی اور اور سر نہ پکڑ بیشک میں ڈرا اس سے کہ تو کہے گا تو نے بنی اسرائیل میں پھوٹ ڈال دی اور میرے فیصلہ کا انتظار نہ کیا'),
(2443, 20, 95, 'کہا اے سامری تیرا کیا معاملہ ہے'),
(2444, 20, 96, 'کہا میں نے وہ چیز دیکھی تھی جو دوسروں نے نہ دیکھی پھر میں نے رسول کے نقشِ قدم کی ایک مٹھی مٹی میں لے کر ڈال دی اور میرے دل نے مجھے ایسی ہی بات سوجھائی'),
(2445, 20, 97, 'کہا بس چلا جا تیرے لیے زندگی میں یہ سزا ہے کہ توکہے گا ہاتھ نہ لگانا اورتیرے لیے ایک وعدہ ہے جو تجھ سے ٹلنے والا نہیں اور تو اپنے معبود کو دیکھ جس پر تو جما بیٹھا تھا ہم اسے ضرور جلا دیں گے پھر اسے دریا میں بکھیر کر بہا دیں گے'),
(2446, 20, 98, 'تمہارا معبود ہی الله ہے جس کے سوا کوئی معبود نہیں اس کے علم میں سب چیز سما گئی ہے'),
(2447, 20, 99, 'ہم اسی طرح سے تجھے گزشتہ لوگوں کی کچھ خبریں سناتے ہیں اور ہم نے تجھے اپنے ہاں سے ایک نصیحت نامہ دیا ہے'),
(2448, 20, 100, 'جس نے اس سے منہ پھیرا سو وہ قیامت کے دن بوجھ اٹھائے گا'),
(2449, 20, 101, 'اس میں ہمیشہ رہیں گے اوران کے لیے قیامت کے دن بُرا بوجھ ہوگا'),
(2450, 20, 102, 'جس دن صور میں پھونکا جائے گا اور ہم اس دن مجرموں کو نیلی آنکھوں والے کر کے جمع کر دیں گے'),
(2451, 20, 103, 'چپکے چپکے آپس میں باتیں کہتے ہوں گے کہ تم صرف دس دن ٹھیرے ہو'),
(2452, 20, 104, 'ہم خوب جان لیں گے جو کچھ وہ کہیں گے جب ان میں سے بڑا سمجھدار کہے گا کہ تم صرف ایک ہی دن ٹھہرے ہو'),
(2453, 20, 105, 'اور تجھ سے پہاڑوں کا حال پوچھتے ہیں سوکہہ دے میرا رب انہیں بالکل اڑا دے گا'),
(2454, 20, 106, 'پھر زمین کو چٹیل میدان کر کے چھوڑے گا'),
(2455, 20, 107, 'تو اس میں کجی اور ٹیلا نہیں دیکھے گا'),
(2456, 20, 108, 'اس دن پکارےنے والے کا اتباع کریں گے اس میں کوئی کجی نہیں ہوگی اور رحمان کے ڈر سے آوازیں دب جائیں گی پھر تو پاؤں کی آہٹ کے سوا کچھ نہیں سنے گا'),
(2457, 20, 109, 'اس دن سفارش کام نہیں آئے گی مگر جسے رحمان نے اجازت دی اور اس کی بات پسند کی'),
(2458, 20, 110, 'وہ جانتا ہے جو کچھ ان کے آگے اور پیچھے ہے اور ان کا علم اسے احاطہ نہیں کر سکتا'),
(2459, 20, 111, 'اور سب منہ حّی و قیوم کے سامنے جھک جائیں گے اور تحقیق نامراد ہوا جس نے ظلم کا بوجھ اٹھایا'),
(2460, 20, 112, 'اور جو نیک کام کرے گا اور وہ مومن بھی ہو تو اسے ظلم اور حق تلفی کا کوئی خوف نہیں ہو گا'),
(2461, 20, 113, 'اور اسی طرح ہم نے اسے عربی قرآن نازل کیا ہے اور ہم نے اس میں طرح طرح سے ڈرانے کی باتیں سنائیں تاکہ وہ ڈریں یا ان میں سمجھ پیدا کر دے'),
(2462, 20, 114, 'سو الله بادشاہ حقیقی بلند مرتبے والا ہے اور تو قرآن کے لینے میں جلدی نہ کر جب تک اس کا اترنا پورا نہ ہوجائےاور کہہ اے میرے رب مجھے اور زیادہ علم دے'),
(2463, 20, 115, 'اور ہم نے اس سے پہلے آدم سے بھی عہد لیا تھا پھر وہ بھول گیا اور ہم نے اس میں پختگی نہ پائی'),
(2464, 20, 116, 'اور جب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو سوائے ابلیس کے سب نے سجدہ کیا اس نے انکار کیا'),
(2465, 20, 117, 'پھر ہم نے کہا اے آدم بے شک یہ تیرا اور تیری بیوی کا دشمن ہے سو تمہیں جنت سے نہ نکلوا دے پھر تو تکلیف میں پڑ جائے'),
(2466, 20, 118, 'بے شک تو اس میں بھوکا اورننگا نہیں ہوگا'),
(2467, 20, 119, 'اور بے شک تو اس میں نہ پیاسا ہوگا اور نہ تجھے دھوپ لگے گی'),
(2468, 20, 120, 'پھر شیطان نے اس کے دل میں خیال ڈالا کہا اے آدم کیا میں تجھے ہمیشگی کا درخت بتاؤں اور ایسی بادشاہی جس میں ضعف نہ آئے'),
(2469, 20, 121, 'پھر دونوں نے اس درخت سے کھایا تب ان پر ان کی برہنگی ظاہر ہوگئی اور اپنے اوپر جنت کے پتے چپکانے لگے اور آدم نے اپنے رب کی نافرمانی کی پھر بھٹک گیا'),
(2470, 20, 122, 'پھر اس کے رب نے اسے سرفراز کیا پھر اس کی توبہ قبول کی اور راہ دکھائی'),
(2471, 20, 123, 'فرمایا تم دونوں یہاں سے نکل جاؤ تم میں سے ایک دوسرے کا دشمن ہے پھر اگرتمہیں میری طرف سے ہدایت پہنچے پھر جو میری ہدایت پر چلے گا تو گمراہ نہیں ہو گا اور نہ تکلیف اٹھائے گا'),
(2472, 20, 124, 'اور جو میرے ذکر سے منہ پھیرے گا تو اس کی زندگی بھی تنگ ہوگی اور اسے قیامت کے دن اندھا کر کے اٹھا ئيں گے'),
(2473, 20, 125, 'کہے گا اے میرے رب تو نے مجھے اندھا کر کے کیوں اٹھایا حالانکہ میں بینا تھا'),
(2474, 20, 126, 'فرمائے گا اسی طرح تیرے پاس ہماری آیتیں پہنچی تھیں پھر تو نے انھیں بھلا دیا تھا اور اسی طرح آج تو بھی بھلایا گیا ہے'),
(2475, 20, 127, 'اور اسی طرح ہم بدلہ دیں گے جو حد سے نکلا اور اپنے رب کی آیتوں پر ایمان نہیں لیا اور البتہ آخرت کا عذاب بڑا سخت اور دیرپا ہے'),
(2476, 20, 128, 'سو کیا انہیں اس بات سے بھی سمجھ نہیں آئی کہ ہم نے ان سے پہلے کئی جماعتیں ہلاک کر دی ہیں یہ لوگ ان کی جگہوں پر پھرتے ہیں بیشک اس میں عقل والوں کے لیے نشانیاں ہیں'),
(2477, 20, 129, 'اور اگر تیرے رب کی طرف سے ایک بات پہلے طے شدہ نہ ہوتی اور معیاد معین نہ ہوتی تو عذاب لازمی طور پر ہوتا'),
(2478, 20, 130, 'پس صبر کر اس پر جو کہتے ہیں اور سورج کے نکلنے اور ڈوبنے سے پہلے اپنے رب کی حمد کے ساتھ تسبیح بیان کر اور رات کی کچھ گھڑیوں میں اور دن کے اول اور آخر میں تسبیح کر تاکہ تجھے خوشی حاصل ہو'),
(2479, 20, 131, 'اور تو اپنی نظر ان چیزو ں کی طرف نہ دوڑا جو ہم نے مختلف قسم کے لوگوں کو دنیاوی زندگی کی رونق کے سامان دے رکھے ہیں تاکہ ہم انہیں اس میں آزمائیں او رتیرے رب کا رزق بہتر اور دیرپا ہے'),
(2480, 20, 132, 'اور اپنے گھر والو ں کو نماز کا حکم کر اور خود بھی اس پر قائم رہ ہم تجھ سے روزی نہیں مانگتے ہم تجھے روزی دیتے ہیں اور پرہیزگاری کا انجام اچھا ہے'),
(2481, 20, 133, 'اور لوگ کہتے ہیں کہ یہ ہمارے پاس اپنے رب سے کوئی نشانی کیوں نہیں لے آتا کیا ان کے پاس پہلی کتابوں کی شہادت نہیں پہنچی'),
(2482, 20, 134, 'اور اگر ہم انہیں اس سے پہلے کسی عذاب سے ہلاک کر دیتے تو کہتے اے ہمارے رب تو نے ہمارے پاس کوئی رسول کیوں نہ بھیجا تاکہ ہم ذلیل و خوار ہونے سے پہلے تیرے حکموں پر چلتے'),
(2483, 20, 135, 'کہہ دو ہر ایک انتظار کرنے والا ہے سو تم بھی انتظار کرو آئندہ تمہیں معلوم ہو جائے گا کہ سیدھی راہ پر کون ہے اور ہدایت پانے والا کون ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'لوگو ں کے حساب کا وقت قریب آ گیا ہے اوروہ غفلت میں پڑ کر منہ پھیرنے والے ہیں'),
(2485, 21, 2, 'ان کے رب کی طرف سمجھانے کے لیے کوئی ایسی نئی بات ان کے پاس نہیں آتی کہ جسے سن کر ہنسی میں نہ ٹال دیتے ہوں'),
(2486, 21, 3, 'ان کے دل کھیل میں لگے ہوئے ہیں اور ظالم پوشیدہ سرگوشیاں کرتے ہیں کہ یہ تمہاری طرح ایک انسان ہی تو ہے پھر کیا تم دیدہ دانستہ جادو کی باتیں سنتے جاتے ہو'),
(2487, 21, 4, 'رسول نے کہا میرا رب آسمان اور زمین کی سب باتیں جانتا ہے اور وہ سننے والا اور جاننے والا ہے'),
(2488, 21, 5, 'بلکہ کہتے ہیں کہ یہ بیہودہ خواب ہیں بلکہ اس نے جھوٹ بنایا ہے بلکہ وہ شاعر ہے پھر چاہیے کہ ہمارے پاس کوئی نشانی لائے جس طرح پہلے پیغمبر بھیجے گئے تھے'),
(2489, 21, 6, 'ان سے پہلے کوئی بستی ایمان نہیں لائی تھی جسے ہم نے ہلاک کیا کیا اب یہ ایمان لائیں گے'),
(2490, 21, 7, 'اور ہم نے تم سے پہلے بھی تو آدمیوں ہی کو رسول بنا کر بھیجا تھا ان کی طرف ہم وحی بھیجا کرتے تھے اگر تم نہیں جانتے تو علم والوں سے پوچھ لو'),
(2491, 21, 8, 'او رہم نے ان کے ایسے بدن بھی نہیں بنائے تھے کہ وہ کھانانہ کھائیں اور نہ وہ ہمیشہ رہنے والے تھے'),
(2492, 21, 9, 'پھر ہم نے ان سے وعدہ سچا کر دیا تب انہیں اور جسے ہم نے چاہا نجات دی اور ہم نے حد سے بڑھنے والوں کوہلاک کر دیا'),
(2493, 21, 10, 'البتہ تحقیق ہم نے تمہارے پاس ایک ایسی کتاب بھیجی ہے جس میں تمہاری نصیحت ہے کیا پس تم نہیں سمجھتے'),
(2494, 21, 11, 'اور ہم نے بہت سی بستیوں کو جو ظالم تھیں غارت کر دیا ہے اور ان کے بعد ہم نے اور قومیں پیدا کیں'),
(2495, 21, 12, 'پھر جب انہوں نے ہمارے عذاب کی آہٹ پائی تو وہ فوراً وہاں سے بھاگنے لگے'),
(2496, 21, 13, 'مت بھاگو اور لوٹ جاؤ جہاں تم نے عیش کیا تھا اور اپنے گھروں میں جاؤ تاکہ تم سے پوچھا جائے'),
(2497, 21, 14, 'کہنے لگے ہائے ہماری کم بختی بے شک ہم ہی ظالم تھے'),
(2498, 21, 15, 'سو ان کی یہی پکار رہی یہاں تک کہ ہم نے انہیں ایسا کر دیا جس طرح کھیتی کٹی ہوئی ہو اور وہ بجھ کر رہ گئے'),
(2499, 21, 16, 'اور ہم نے آسمان اور زمین کو اورجو کچھ ان کے بیچ میں ہے کھیلتے ہوئے نہیں بنایا'),
(2500, 21, 17, 'اور اگر ہم کھیل ہی بنانا چاہتے تو اپنے پا س کی چیزوں کو بناتے اگر ہمیں یہی کرنا ہوتا'),
(2501, 21, 18, 'بلکہ ہم حق کو باطل پر پھینک مارتے ہیں پھر وہ باطل کا سر توڑ دیتا ہے پھر وہ مٹنے والا ہوتا ہے او رتم پر افسوس ہے ان باتوں سے جو تم بناتے ہو'),
(2502, 21, 19, 'اور اسی کا ہے جو کوئي آسمانوں اور زمین میں ہے اور جو اس کے ہاں ہیں اس کی عبادت سے سرکشی نہیں کرتےاور نہ تھکتے ہیں'),
(2503, 21, 20, 'رات اور دن تسبیح کرتے ہیں سستی نہیں کرتے'),
(2504, 21, 21, 'کیاانہوں نے زمین کی چیزوں سے ایسے معبود بنا رکھے ہیں جو زندہ کریں گے'),
(2505, 21, 22, 'اگر ان دونوں میں الله کے سوا اور معبود ہوتے تو دونوں خراب ہو جاتے سو الله عرش کا مالک ان باتوں سے پاک ہے جو یہ بیان کرتے ہیں'),
(2506, 21, 23, 'جو کچھ وہ کرتاہے اس سے پوچھا نہیں جاتا اور وہ پوچھے جاتے ہیں'),
(2507, 21, 24, 'کیا انہوں نے اس کے سوا اور بھی معبود بنا رکھے ہیں کہہ دو اپنی دلیل لاؤ یہ میرے ساتھ والو ں کی کتاب اور مجھ سے پہلے لوگوں کی کتابیں موجود ہیں بلکہ اکثران میں سے حق جانتے ہی نہیں اس لیے منہ پھیرے ہوئے ہیں'),
(2508, 21, 25, 'اور ہم نے تم سے پہلے ایسا کوئی رسول نہیں بھیجا جس کی طرف یہ وحی نہ کی ہو کہ میرے سوا اور کوئی معبود نہیں سومیری ہی عبادت کرو'),
(2509, 21, 26, 'اور کہتے ہیں کہ الله نے اولاد بنا رکھی ہے وہ پاک ہے لیکن وہ معزز بندے ہیں'),
(2510, 21, 27, 'بات کرنے میں اس سے پیش قدمی نہیں کرتے اور وہ اسی کے حکم پر کام کرتے ہیں'),
(2511, 21, 28, 'وہ جانتا ہے جو ان کے آگے اور جو ان کے پیچھے ہے اور وہ شفاعت بھی نہیں کرتے مگر اسی کے لیے جس سے وہ خوش ہو اور وہ اس کی ہیبت سے ڈرتے ہیں'),
(2512, 21, 29, 'اورجو کوئی ان میں سے یہ کہے کہ بے شک میں اس کے سوا خدا ہوں تو اسی پر ہم اسے جہنم کی سزاد یں گے ہم اسی طرح ظالموں کو سزا دیا کرتے ہیں'),
(2513, 21, 30, 'کیا منکروں نے نہیں دیکھا کہ آسمان اور زمین جڑے ہوئے تھے پھر ہم نے انھیں جدا جدا کر دیا اورہم نے ہر جاندار چیز کو پانی سے بنایا کیا پھر بھی یقین نہیں کرتے'),
(2514, 21, 31, 'اورہم نے زمین میں بھاری پہاڑ رکھ دیئے تاکہ انہیں لے کر داھر اُدھر نہ جھکنے پائے اور ہم نے اس میں کشادہ راہیں بنا دی ہیں تاکہ وہ راہ پائیں'),
(2515, 21, 32, 'اور ہم نے آسمان کو ایک محفوظ چھت بنا دیا اور وہ آسمان کی نشانیوں سے منہ موڑنے واے ہیں'),
(2516, 21, 33, 'اور وہی ہے جس نے رات اور دن اور سورج اور چاند بنائے سب اپنے اپنے چکر میں پھرتے ہیں'),
(2517, 21, 34, 'اور ہم نے تجھ سے پہلے کسی آدمی کو ہمیشہ کے لیے زندہ رہنے نہیں دیا پھر کیا اگر تو مر گیا تو وہ رہ جائیں گے'),
(2518, 21, 35, 'ہر ایک جاندار موت کا مزہ چکھنے والا ہے اور ہم تمہیں برائی اوربھلائی سے آزمانے کے لیے جانچتے ہیں اورہماری طرف لوٹائے جاؤ گے'),
(2519, 21, 36, 'اور جہاں تمہیں کافر دیکھتے ہیں تو انہیں تجھ سے سوائے ٹھٹھا کرنے کے اور کوئی کام نہیں کیا یہی شخص ہے جو تمہارے معبودوں کا نام لیتا ہے اور وہ رحمان کے نام سے منکر ہیں'),
(2520, 21, 37, 'آدمی جلد باز بنایا گیا ہے میں تمہیں اپنی نشانیاں ابھی دکھاتا ہوں سو جلدی مت کرو'),
(2521, 21, 38, 'ور کہتے ہیں یہ وعدہ کب ہوگا اگر تم سچے ہو'),
(2522, 21, 39, 'کاش یہ منکر اس وقت کو جان لیں کہ اپنے مونہوں اوراپنی پیٹھوں سے آگ کو روک نہیں سکیں گے اور نہ وہ مدد کیے جائیں گے'),
(2523, 21, 40, 'بلکہ وہ ان پر ناگہان آئے گی پھر وہ ان کے ہوش کھو دے گی پھر نہ اسے ٹال سکیں گے اورنہ انہیں مہلت دی جائے گی'),
(2524, 21, 41, 'اور تجھ سے پہلے بھی رسولوں کے ساتھ ٹھٹھا کیا گیا ہے پھر جس عذاب کی بابت وہ ہنسی کیا کرتے تھے ان ٹھٹھا کرنے والوں پر وہی آ پڑا'),
(2525, 21, 42, 'کہہ دو تمہاری رات اور دن میں رحمان سے کون نگہبانی کرتا ہے بلکہ وہ اپنے رب کے ذکر سے منہ موڑنے والے ہیں'),
(2526, 21, 43, 'کیا ہم سے ان کےمعبود انہیں بچائے رکھتے ہیں وہ تو خود اپنی بھی مدد نہیں کر سکتے اورنہ ہمارے مقابلہ میں ان کا کوئی ساتھ دے گا'),
(2527, 21, 44, 'بلکہ ہم نے ان کو اور ان کے باپ دادا کو خوب سامان دیا یہاں تک کہ ان پر ایک عرصہ دراز گزر گیا کیا وہ یہ نہیں دیکھتے کہ بے شک ہم زمین کو ہر طرف سے گھٹاتے چلے جاتے ہیں سو کیا یہ لوگ غالب آنے والے ہیں'),
(2528, 21, 45, 'کہہ دو کہ میں تو صرف وحی کے ذریعہ سے تمہیں ڈراتا ہوں اور یہ بہرے جس وقت ڈرائے جاتے ہیں سنتے ہی نہیں'),
(2529, 21, 46, 'اور البتہ اگر انہیں تیرے رب کا عذاب کا ایک جھونکا بھی لگ جائے تو ضرور کہیں گے کہ ہائے ہماری کم بختی بے شک ہم ظالم تھے'),
(2530, 21, 47, 'اور قیامت کے دن ہم انصاف کی ترازو قائم کریں گے پھرکسی پر کچھ ظلم نہ کیا جائے گا اور اگر رائی کے دانہ کے برابر بھی عمل ہو گا تو اسے بھی ہم لے آئیں گے اور ہم ہی حساب لینے کے لیے کافی ہیں'),
(2531, 21, 48, 'اور البتہ تحقیق ہم نے موسیٰ اور ہارون کو فیصلہ کرنے والی اور روشنی دینے والی اور پرہیز گاروں کو نصیحت کرنے والی کتاب دی تھی'),
(2532, 21, 49, 'جو اپنے رب سے بن دیکھے ڈرتے ہیں اور قیامت کا بھی خوف رکھنے والے ہیں'),
(2533, 21, 50, 'اور یہ ایک مبارک نصیحت ہے جسے ہم نازل کیاہے پھر کیا تم اس کے بھی منکر ہو'),
(2534, 21, 51, 'اور ہم نے پہلے ہی سے ابراھیم کو اس کی صلاحیت عطا کی تھی اور ہم اس سے واقف تھے'),
(2535, 21, 52, 'جب اس نے اپنے باپ اور اپنی قوم سے کہا کہ یہ کیسی مورتیں ہیں جن پر تم مجاور بنے بیٹھے ہو'),
(2536, 21, 53, 'انہوں نے کہا ہم نے اپنے باپ دااد کو انہیں کی پوجا کرتے پایا ہے'),
(2537, 21, 54, 'کہا البتہ تحقیق تم اور تمہارے باپ دادا صریح گمراہی میں رہے ہو'),
(2538, 21, 55, 'انھوں نے کہا کیا تو ہمارے پاس سچی بات لایا ہے یا تو دل لگی کرتا ہے'),
(2539, 21, 56, 'کہا بلکہ تمہارا رب تو آسمانوں اور زمین کا رب ہے جس نے انہیں بنایا ہے اور میں اسی بات کا قائل ہوں'),
(2540, 21, 57, 'اور الله کی قسم میں تمہارے بتوں کا علاج کروں گا جب تم پیٹھ پھیر کر جا چکو گے'),
(2541, 21, 58, 'پھر ان کے بڑے کے سوا سب کو ٹکڑے ٹکڑے کر دیا تاکہ اس کی طرف رجوع کریں'),
(2542, 21, 59, 'انہوں نے کہا ہمارے معبودوں کے ساتھ کس نے یہ کیا ہے بے شک وہ ظالموں میں سے ہے'),
(2543, 21, 60, 'انہو ں نے کہا ہم نے سنا ہے کہ ایک جو ان بتوں کو کچھ کہا کرتا ہے اسے ابراھیم کہتے ہیں'),
(2544, 21, 61, 'کہنے لگے اسے لوگوں کے سامنے لے آؤ تاکہ وہ دیکھیں'),
(2545, 21, 62, 'کہنے لگے اے ابراھیم کیا تو نے ہمارے معبودوں کے ساتھ یہ کیا ہے'),
(2546, 21, 63, 'کہا بلکہ ان کے اس بڑے نے یہ کیا ہے سو ان سے پوچھ لو اگر وہ بولتے ہیں'),
(2547, 21, 64, 'پھر وہ اپنے دل میں سوچ کر کہنے لگے بے شک تم ہی بے انصاف ہو'),
(2548, 21, 65, 'پھر انہو ں نے سر نیچا کر کے کہا تو جانتا ہے کہ یہ بولا نہیں کرتے'),
(2549, 21, 66, 'کہا پھر کیا تم الله کے سوا اس چیز کی پوجا کرتے ہو جو نہ تمہیں نفع دے سکےاور نہ نقصان پہنچا سکے'),
(2550, 21, 67, 'میں تم سے اور جنہیں الله کے سوا پوجتے ہو بیزار ہوں پھر کیا تمھیں عقل نہیں ہے'),
(2551, 21, 68, 'انھوں نے کہا اگر تمہیں کچھ کرنا ہے تو اسے جلا دو اور اپنے معبودوں کی مدد کرو'),
(2552, 21, 69, 'ہم نے کہا اے آگ ابراھیم پر سرد اورراحت ہو جا'),
(2553, 21, 70, 'اور انہوں نے اس کی برائی چاہی سو ہم نے انہیں ناکام کر دیا'),
(2554, 21, 71, 'اور ہم اسے اورلوط کو بچا کراس زمین کی طرف لے آئے جس میں ہم نے جہان کے لیے برکت رکھی ہے'),
(2555, 21, 72, 'اور ہم نے اسے اسحاق بخشا اور انعام میں یعقوب دیا اور سب کونیک بخت کیا'),
(2556, 21, 73, 'اورہم نے انہیں پیشوا بنایا جو ہمارے حکم سے رہنمائی کیا کرتے تھے اور ہم نے انہیں اچھے کام کرنے اور نماز قائم کرنے اور زکوٰة دینے کا حکم دیا تھا اور وہ ہماری ہی بندگی کیا کرتے تھے'),
(2557, 21, 74, 'اور لوط کو ہم نے حکمت اور علم عطا کیا تھا اور ہم اسے اس بستی سے جو گندے کام کیا کرتی تھی بچا کر لے آئے بے شک وہ لوگ برے نافرمانی کرنے والے تھے'),
(2558, 21, 75, 'اوراسے ہم نے اپنی رحمت میں لے لیا بے شک وہ نیک بختوں میں سے تھا'),
(2559, 21, 76, 'اور نوح کو جب اس نے اس سے پہلے پکارا پھر ہم نے اس کی دعا قبول کر لی پھر ہم نے اسے اور اس کے گھر والوں کو گھبراہٹ سے بچا لیا'),
(2560, 21, 77, 'اور ہم نے اس کی مدد کی ان لوگوں پر جو ہماری آیتیں جھٹلاتے تھے بے شک وہ بُرے لوگ تھے پھر ہم نے ان سب کو غرق کر دیا'),
(2561, 21, 78, 'اور داؤد اور سلیمان کو جب وہ کھیتی کے جھگڑا میں فیصلہ کرنے لگے جب کہ اس میں کچھ لوگوں کی بکریاں رات کے وقت جا پڑیں اور ہم اس فیصلہ کو دیکھ رہے تھے'),
(2562, 21, 79, 'پھر ہم نے وہ فیصلہ سلیمان کو سمجھا دیا اور ہر ایک کو ہم نے حکمت اور علم دیا تھا اور ہم نے داؤد کے ساتھ پہاڑ او رپرندے تابع کیے جو تسبیح کیا کرتے تھے اور یہ سب کچھ ہم ہی کرنے والے تھے'),
(2563, 21, 80, 'اور ہم نےاسے تمہارے لیے زر ہیں بنانا بھی سکھایا تاکہ تمہیں لڑائی میں محفوظ رکھیں پھر کیا تم شکر کرتے ہو'),
(2564, 21, 81, 'اور زور سے چلنے والی ہوا سلیمان کے تابع کی جو اس کے حکم سے اس زمین کی طرف چلتی جہاں ہم نے برکت دی ہے اور ہم ہر چیز کو جاننے والے ہیں'),
(2565, 21, 82, 'اورتو کچھ ایسے جن تھے جو دریا میں اس کے واسطے غوطہ لگاتے تھے اور اس کے سوا اور کام بھی کرتے تھے اور ہم ان کی حفاظت کرنے والے تھے'),
(2566, 21, 83, 'اورجب کہ ایوب نے اپنے رب کو پکارا کہ مجھے روگ لگ گیا ہے حالانکہ تو سب رحم کرنے والوں سے زیادہ رحم کرنے والا ہے'),
(2567, 21, 84, 'پھر ہم نے اس کی دعا قبول کی اورجواسے تکلیف تھی ہم نے دور کر دی اور اسے اس کے گھر والے دیئے اور اتنا ہی ان کے ساتھ اپنی رحمت سے اوربھی دیا اور عبادت کرنے والوں کے لیے نصیحت ہے'),
(2568, 21, 85, 'اور اسماعیل اور ادریس اور ذوالکفل کو یہ سب صبر کرنے والے تھے'),
(2569, 21, 86, 'اور ہم نے انہیں اپنی رحمت میں داخل کر لیا بے شک وہ نیک بختوں میں سے تھے'),
(2570, 21, 87, 'اور مچھلی والے کو جب غصہ ہو کر چلا گیا پھر خیال کیا کہ ہم اسے نہیں پکڑیں گے اندھیروں میں پکارا کہ تیرے سوا کوئی معبود نہیں ہے تو بے عیب ہے بے شک میں بے انصافوں میں سے تھا'),
(2571, 21, 88, 'پھر ہم نے اس کی دعا قبول کی اور اسے غم سے نجات دی اور ہم ایمان داروں کو یونہی نجات دیا کرتے ہیں'),
(2572, 21, 89, 'اور ذکریا کو جب اس نے اپنے رب کو پکارا اے رب مجھے اکیلا نہ چھوڑ اور تو سب سے بہتر وارث ہے'),
(2573, 21, 90, 'پھر ہم نے اس کی دعا قبول کی اور اسے یحییٰ عطا کیا اور اس کے لیے اس کی بیوی کو درست کر دیا بے شک یہ لوگ نیک کاموں میں دوڑ پڑتے تھے اور ہمیں امید اور ڈر سے پکارا کرتے تھے اور ہمارے سامنے عاجزی کرنے والے تھے'),
(2574, 21, 91, 'اوروہ عورت جس نے اپنی عصمت کو محفوظ رکھا پھر ہم نے اس میں اپنی روح پھونک دی اور اسے اور اس کے بیٹے کو جہان کے لیے نشانی بنایا'),
(2575, 21, 92, 'یہ لوگ تمہارے گروہ کے ہیں جو ایک ہی گروہ ہے اور میں تمہارا رب ہوں پھر میری ہی عبادت کرو'),
(2576, 21, 93, 'اور ان لوگوں نے اپنے دین میں اختلاف پیدا کر لیا سب ہمارے پاس ہی آنے والے ہیں'),
(2577, 21, 94, 'پھر جو کوئی اچھے کام کرے گا اور وہ مومن بھی ہوگا تو اس کی کوشش رائگان نہ جائے گی اوربے شک ہم اس کے لکھنے والے ہیں'),
(2578, 21, 95, 'اورجن بستیوں کو ہم فنا کر چکے ہیں ان کے لیے ناممکن ہے کہ وہ پھر لوٹ کر آئيں'),
(2579, 21, 96, 'یہاں تک کہ جب یاجوج اور ماجوج کھول دیئے جائیں گے اور وہ ہر بلندی سے دوڑتے چلے آئيں گے'),
(2580, 21, 97, 'اور سچا وعدہ نزدیک آ پہنچے گا پھر اس وقت منکرو ں کی آنکھیں اوپر لگی رہ جائیں گی ہائے کم بختی ہماری بے شک ہم تو اس سے غفلت میں پڑے ہوئے تھے بلکہ ہم ہی ظالم تھے'),
(2581, 21, 98, 'بے شک تم اور الله کے سوا جو کچھ پوجتے ہو دوزخ کا ایندھن ہے تم سب اس میں داخل ہو گے'),
(2582, 21, 99, 'اگر یہ معبود ہوتے تو اس میں داخل نہ ہوتے اور سب اس میں ہمیشہ رہنے والے ہیں'),
(2583, 21, 100, 'ان کے لیے دوزخ میں چیخیں ہو ں گی اوروہ اس میں کچھ نہیں سنیں گے'),
(2584, 21, 101, 'بے شک جن کے لیے ہماری طرف سے بھلائی مقدر ہو چکی ہے وہ اس سے دور رکھے جائیں گے'),
(2585, 21, 102, 'اس کی آہٹ بھی نہ سنیں گے اور وہ اپنی من مانی مرادوں میں ہمیشہ رہیں گے'),
(2586, 21, 103, 'اور انہیں بڑا بھاری خوف بھی پریشان نہیں کرے گا اور ان سے فرشتے آ ملیں گے یہی وہ تمہارا دن ہے جس کا تمہیں وعدہ دیا جاتا تھا'),
(2587, 21, 104, 'جس دن ہم آسمان کو اس طرح لپیٹیں گے جیسے خطوں کا طومار لپیٹا جاتا ہے جس طرح ہم نے پہلی بار پیدا کیا تھا دوبارہ بھی پیدا کریں گے یہ ہمارے ذمہ وعدہ ہے بے شک ہم پورا کرنے والے ہیں'),
(2588, 21, 105, 'اور البتہ تحقیق ہم نصیحت کے بعد زبور میں لکھ چکے ہیں کہ بے شک زمین کے وارث ہمارے نیک بندے ہی ہوں گے'),
(2589, 21, 106, 'بےشک اس میں خدا پرستوں کے لیے ایک پیغام ہے'),
(2590, 21, 107, 'او رہم نے توتمہیں تمام جہان کے لوگوں کے حق میں رحمت بنا کر بھیجا ہے'),
(2591, 21, 108, 'کہہ دو مجھے تو یہی حکم آیا ہے کہ تمہارا معبود ایک معبود ہے پھر کیا اس کے آگے سر جھکاتے ہو'),
(2592, 21, 109, 'پھر اگر وہ منہ موڑیں تو کہہ دو کہ میں نے یکساں طور پر خبر دے دی ہے اورمجھے معلوم نہیں کہ نزدیک ہے یا دور ہے جس کا تم سے وعدہ کیا جاتا ہے'),
(2593, 21, 110, 'بے شک وہ جانتا ہے جو بات پکار کر کہو اور جانتا ہے جو تم چھپاتے ہو'),
(2594, 21, 111, 'اور میں نہیں جانتا شاید وہ تمہارے لیے امتحان ہو اور ایک وقت تک دنیا کا فائدہ پہنچانا منظور ہو'),
(2595, 21, 112, 'کہ اے رب انصاف کا فیصلہ کر دے اور ہمارا رب بڑا مہربان ہے اسی سے مدد مانگتے ہیں ان باتوں پر جو تم بیان کرتے ہو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'اے لوگو اپنے رب سے ڈرو بے شک قیامت کا زلزلہ ایک بڑی چیز ہے'),
(2597, 22, 2, 'جس دن اسے دیکھو گے ہر دودھ پلانے والی اپنے دودھ پیتے کو بھول جائے گی اور ہر حمل والی اپنا حمل ڈال دے گی اور تجھے لوگ مدہوش نظر آئیں گے اور وہ مدہوش نہ ہوں گے لیکن الله کا عذاب سخت ہوگا'),
(2598, 22, 3, 'اور بعضے لوگ وہ ہیں جو الله کے معاملے میں بے سمجھی سے جھگڑتے ہیں اور ہر شیطان سرکش کے کہنے پر چلتے ہیں'),
(2599, 22, 4, 'جس کے حق میں لکھاجا چکا ہے کہ جو اسے یار بنائے گا تو وہ اسے گمراہ کر کے رہے گا اور اسے دوزخ کے عذاب کا راستہ دکھائے گا'),
(2600, 22, 5, 'اے لوگو اگر تمہیں دوبارہ زندہ ہونے میں شک ہے تو ہم نے تمہیں مٹی سے پھر قطرہ سے پھر جمے ہوئے خون سے پھر گوشت کی بوٹی نقشہ بنی ہوئی اور بغیر نقشہ بنی ہوئی سے بنایا تاکہ ہم تمہارے سامنے ظاہر کر دیں اور ہم ر حم میں جس کو چاہتے ہیں ایک مدت معین تک ٹھیراتے ہیں پھر ہم تمہیں بچہ بنا کر باہر لاتے ہیں پھر تاکہ تم اپنی جوانی کو پہنچو اور کچھ تم میں سے مرجاتے ہیں اور کچھ تم میں سے نکمی عمر تک پہنچائے جاتے ہیں کہ سمجھ بوجھ کا درجہ پاکر ناسمجھی کی حالت میں جا پڑتا ہے اور تم زمین کوسوکھی دیکھتے ہو پھر جب ہم اس پر پانی برساتے ہیں تو تر و تازہ ہو جاتی ہے او رہر قسم کے خوش نمانباتات اُگ آتے ہیں'),
(2601, 22, 6, 'یہ اس لیے ہے کہ الله ہی برحق ہے اور مردوں کو زندہ کرے گا اور وہ ہر بات پر قادر ہے'),
(2602, 22, 7, 'اور بے شک قیامت آنے والی ہے جس میں کوئی شک نہیں اور بے شک الله قبروں والوں کو دوبارہ اٹھائے گا'),
(2603, 22, 8, 'اور بعضاً وہ شخص ہے جو الله کے معاملہ میں بے سمجھی اور دلیل اور روشن کتاب کے بغیر تکبر سے جھگڑتا ہے'),
(2604, 22, 9, 'تاکہ الله کی راہ سے بہکائے اس کے لیے دنیا میں رسوائی ہے اور قیامت کے دن بھی ہم اسے دوزخ کے عذاب کا مزہ چکھاویں گے'),
(2605, 22, 10, 'یہ تیرے ہاتھوں کے کیے ہوئے کاموں کا بدلہ ہے اور بےشک الله بندوں پر ظلم کرنے والا نہیں'),
(2606, 22, 11, 'اور بعض وہ لوگ ہیں کہ الله کی بندگی کنارے پر ہو کر کرتے ہیں پھر اگر اسے کچھ فائدہ پہنچ گیا تو اس عبادت پر قائم ہو گیا اور اگر تکلیف پہنچ گئی تو منہ کے بل پھر گیا دنیا اور آخرت گنوائی یہی وہ صریح خسارا ہے'),
(2607, 22, 12, 'الله کے سوا ایسی چیز کو پکارتا ہے جو نہ اسے ضرر دے سکے اور نہ اسے فائدہ پہنچا سکے یہی وہ پرلے درجہ کی گمراہی ہے'),
(2608, 22, 13, 'ایسے کو پکارتا ہے جس کا ضرر اس کے نفع سے نزدیک تر ہے ایسا کارساز بھی برا اور ایسا رفیق بھی برا ہے'),
(2609, 22, 14, 'بے شک الله ان لوگوں کو جو ایمان لائے اور اچھے کام کیے ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی بے شک الله جو چاہتا ہے کرتا ہے'),
(2610, 22, 15, 'جسے یہ خیال ہو کہ الله دنیا اور آخرت میں اس کی ہرگز مدد نہ کرے گااسے چاہیے کہ چھت میں ایک رسی لٹکائے پھر اسے کاٹ دے پھر دیکھے کہ اس کی تدبیر اس کے غصہ کو دور کرتی ہے'),
(2611, 22, 16, 'اور اسی طرح ہم نےاس قرآن کو واضح آیتیں بنا کر نازل کیا ہے اور بے شک الله جسے چاہتا ہے ہدایت کرتا ہے'),
(2612, 22, 17, 'بے شک الله مسلمانوں اور یہودیوں اور صابیوں اور عیسائیوں اورمجوسیوں اور مشرکوں میں قیامت کے دن فیصلہ کرے گا بےشک ہر چیز الله کے سامنے ہے'),
(2613, 22, 18, 'کیا تم نےنہیں دیکھا کہ جو کوئی آسمانوں میں ہے اور جو کوئی زمین میں ہے اور سورج اور چاند اور ستارے اور پہاڑ اور درخت اورچار پائے اور بہت سے آدمی الله ہی کو سجدہ کرتے ہیں اور بہت سے ہیں کہ جن پر عذاب مقرر ہو چکا ہے اور جسے الله ذلیل کرتا ہے پھر اسے کوئی عزت نہیں دےسکتا بے شک الله جو چاہتا ہے کرتا ہے'),
(2614, 22, 19, 'یہ دو فریق ہیں جو اپنے رب کے معاملہ میں جھگڑتے ہیں پھر جو منکر ہیں ان کے لیے آگ کے کپڑے قطع کیے گئے ہیں اور ان کےسروں پر کھولتا ہوا پانی ڈالا جائے گا'),
(2615, 22, 20, 'جس سے جو کچھ ان کے پیٹ میں ہے اور کھالیں جھلس دی جائیں گی'),
(2616, 22, 21, 'اور ان پر لوہے کے گرز پڑیں گے'),
(2617, 22, 22, 'جب گھبرا کر وہاں سے نکلنا چاہیں گے اسی میں لوٹا دیئے جائیں گے اور دوزخ کا عذاب چکھتے رہو'),
(2618, 22, 23, 'بے شک الله ان لوگوں کو جو ایمان لائے اور اچھے کام کیے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی وہاں انہیں سونے کے کنگن اور موتی پہنائیں جائیں گے اور وہاں ان کا لباس ریشمی ہوگا'),
(2619, 22, 24, 'اور انہوں نے عمدہ بات کی راہ پائی اور تعریف والے الله کی راہ پائی'),
(2620, 22, 25, 'بے شک جو منکر ہوئے اور لوگوں کو الله کے راستہ اور مسجد حرام سے روکتے ہیں جسے ہم نے سب لوگو ں کے لیے بنایا ہے وہاں اس جگہ کا رہنے والا اورباہروالادونوں برابر ہیں اور جو وہاں ظلم سے کجروی کرنا چاہے تو ہم اسے دردناک عذاب چکھائیں گے'),
(2621, 22, 26, 'اور جب ہم نے ابراھیم کے لیے کعبہ کی جگہ معین کر دی کہ میرے ساتھ کسی کو شریک نہ کراور میرے گھر کو طواف کرنے والوں اور قیام کرنے والوں اور رکوع و سجود کرنے والوں کے لیے پاک رکھ'),
(2622, 22, 27, 'اور لوگوں میں حج کا اعلان کر دے کہ تیرے پاس پا پیادہ او رپتلے دُبلے اونٹوں پر دور دراز راستوں سے آئیں'),
(2623, 22, 28, 'تاکہ اپنے فائدوں کے لیے آموجود ہوں اورتاکہ جو چار پائے الله نےانہیں دیے ہیں ان پر مقررہ دنوں میں الله کا نام یاد کریں پھر ان میں سے خود بھی کھاؤ اور محتاج فقیر کو بھی کھلاؤ'),
(2624, 22, 29, 'پھر چاہیئے کہ اپنا میل کچیل دور کریں اور اپنی نذریں پوری کریں اور قدیم گھر کا طواف کریں'),
(2625, 22, 30, 'یہی حکم ہے اور جو الله کی معزز چیزوں کی تعظیم کرے گا سو یہ اس کے لیے اس کے رب کے ہاں بہتر ہے اور تمہارے لیے مویشی حلال کر دیئے گئے ہیں مگر وہ جو تمہیں پڑھ کر سنائے جاتے ہیں پھر بتوں کی ناپاکی سے بچو اور جھوٹی بات سے بھی پرہیز کرو'),
(2626, 22, 31, 'خاص الله کے ہو کر رہو اس کے ساتھ کسی کو شریک نہ کرو اور جو الله کے ساتھ کسی کو شریک کرتا ہے تو گویا وہ آسمان سے گر پڑا پھر اسے پرندے اچک لیتے ہیں یا اسے ہوا اڑا کر کسی دور جگہ پھینک دیتی ہے'),
(2627, 22, 32, 'بات یہی ہے اور جو شخص الله کی نامزد چیزوں کی تعظیم کرتا ہے سو یہ دل کی پرہیز گاری ہے'),
(2628, 22, 33, 'تمہارے لیے ان میں ایک وقت معین تک فائدے ہیں پھر اس کے ذبح ہونے کی جگہ قدیم گھر کے قریب ہے'),
(2629, 22, 34, 'اور ہر امت کے لیے ہم نے قربانی مقرر کر دی تھی تاکہ الله نے جو چار پائے انہیں دیے ہیں ان پر الله کا نام یاد کیا کریں پھر تم سب کا معبود تو ایک الله ہی ہے پس اس کے فرمانبردار رہو اور عاجزی کرنے والوں کو خوشخبری سنا دو'),
(2630, 22, 35, 'وہ لوگ جب الله کا نام لیا جائے تو ان کے دل ڈر جاتے ہیں اور جب ان پر مصیبت آئے تو صبر کرنے والے ہیں اور نماز قائم کرنے والے ہیں اور جو کچھ ہم نے انہیں دیا ہے اس میں سے خرچ کرتے ہیں'),
(2631, 22, 36, 'اور ہم نے تمہارے لیے قربانی کے اونٹ کو الله کی نشانیوں میں سے بنایا ہے تمہارے لیے ان میں فائدے بھی ہیں پھر ان پر الله کا نام کھڑا کر کے لو پھر جب وہ کسی پہلو پر گر پڑیں تو ان میں سے خود کھاؤ اور صبر سے بیٹھنے والے اور سائل کو بھی کھلاؤ الله نے انہیں تمہارے لیے ایسا مسخر کر دیا ہے تاکہ تم شکر کرو'),
(2632, 22, 37, 'الله کو نہ ان کا گوشت اور نہ ان کا خون پہنچتا ہے البتہ تمہاری پرہیز گاری اس کے ہاں پہنچتی ہے اسی طرح انہیں تمہارے تابع کر دیا تاکہ تم الله کی بزرگی بیان کرو اس پر کہ اس نے تمہیں ہدایت کی اور نیکوں کو خوشخبری سنا دو'),
(2633, 22, 38, 'بیشک الله ایمان والوں سے دشمنوں کو ہٹا دے گا الله کسی دغا باز نا شکر گزار کو پسند نہیں کرتا'),
(2634, 22, 39, 'جن سے کافر لڑتے ہیں انہیں بھی لڑنے کی اجازت دی گئی ہے اس لیے کہ ان پر ظلم کیا گیا اور بیشک الله ان کی مدد کرنے پر قادر ہے'),
(2635, 22, 40, 'وہ لوگ جنہیں ناحق ان کے گھروں سے نکال دیا گیا ہے صرف اس کہنے پر کہ ہمارا رب الله ہے اور اگر الله لوگوں کو ایک دوسرے سے نہ ہٹاتا تو تکیئے اور مدرسے اورعبادت خانے اور مسجدیں ڈھا دی جاتیں جن میں الله کا نام کثرت سے لیا جاتا ہے اور الله ضرور اس کی مدد کر ے گا جو اللہ کی مددکرے گابیشک الله زبردست غالب ہے'),
(2636, 22, 41, 'وہ لوگ اگر ہم انہیں دنیا میں حکومت دے دیں تو نماز کی پابندی کریں اور زکوٰة دیں اور نیک کام کا حکم کریں اور برے کاموں سے روکیں اور ہر کام کا انجام تو الله کے ہی ہاتھ میں ہے'),
(2637, 22, 42, 'اور اگر تمہیں جھٹلائیں تو ان سے پہلے نوح کی قوم اور عاد اور ثمود'),
(2638, 22, 43, 'اور ابراھیم کی قوم اور لوط کی قوم'),
(2639, 22, 44, 'اور مدین والے اپنے اپنے نبی کو جھٹلا چکے ہیں اور موسیٰ کو بھی جھٹلایا گیا پھر میں نے منکروں کو مہلت دی پھر میں نے انہیں پکڑا پھر میری پکڑ کیسی تھی'),
(2640, 22, 45, 'سو کتنی بستیاں ہم نے ہلاک کر دیں اور وہ گناہ گار تھیں اب وہ اپنی چھتوں پر گری پڑی ہیں اور کتنے کنوئیں نکمے اور کتنے پکے محل اجڑے اجڑے پڑے ہیں'),
(2641, 22, 46, 'کیا انھوں نے ملک میں سیر نہیں کی پھر ان کے ایسے دل ہوجاتے جن سے سمجھتے یا ایسے کان ہو جاتے جن سے سنتے پس تحقیق بات یہ ہے کہ آنکھیں اندھی نہیں ہوتیں بلکہ دل جو سینوں میں ہیں اندھے ہو جاتے ہیں'),
(2642, 22, 47, 'اور تجھ سے عذاب جلدی مانگتے ہیں اور الله اپنے وعدہ کا ہرگز خلاف نہیں کرے گا اور ایک دن تیرے رب کے ہاں ہزار برس کے برابر ہوتا ہے جوتم گنتے ہو'),
(2643, 22, 48, 'اور کتنی بستیوں کو میں نے مہلت دی حالانکہ وہ ظالم تھیں پھر میں نے انہیں پکڑا اور میری طرف ہی پھر کر آنا ہے'),
(2644, 22, 49, 'کہہ دو اے لوگو میں تو صرف تمہیں صاف صاف ڈرانے والا ہوں'),
(2645, 22, 50, 'پھر جو لوگ ایمان لائے اوراچھے کام کیے ان کے لیے بخشش اور عزت کی روزی ہے'),
(2646, 22, 51, 'اور جنہوں نے ہماری آیتیوں کے پست کرنے میں کوشش کی وہی دوزخی ہیں'),
(2647, 22, 52, 'اور ہم نے تجھ سے پہلے کوئی بھی ایسا رسول اور نبی نہیں بھیجا کہ جس نے جب کوئی تمنا کی ہو اور شیطان نے اس کی تمنا میں کچھ آمیزش نہ کی ہو پھر الله ّ شیطان کی آمیزش کو دور کرکے اپنی آیتوں کو محفوظ کردیتا ہے اور الله جاننے والا حکمت والا ہے'),
(2648, 22, 53, 'تاکہ شیطان کی آمیزش کو ان لوگوں کے لیے آزمائش بنا دے جن کے دلوں میں بیماری ہے اور جن کے دل سخت ہیں اور بے شک ظالم بڑی ضدی میں پڑے ہوئے ہیں'),
(2649, 22, 54, 'اور تاکہ علم والے اسے تیرے رب کی طرف سے حق سمجھ کر ایمان لے آئیں پھر ان کے دل اس کے لیے جھک جائیں اور بیشک الله ایمان داروں کو سیدھے راستہ کی طرف ہدایت کرنے والا ہے'),
(2650, 22, 55, 'اور منکر قرآن کی طرف سے ہمیشہ شک میں رہیں گے یہاں تک کہ قیامت یکاک ان پر آ مودجود ہو یا منحوس دن کا عذاب ان پر نازل ہو'),
(2651, 22, 56, 'اس دن الله ہی کی حکومت ہوگی وہی ان میں فیصلہ کر لے گا پھر جو ایمان لائے اور اچھے کام کیے وہ نعمت کے باغوں میں ہوں گے'),
(2652, 22, 57, 'اورجو منکر ہوئے اور ہماری آیتوں کو جھٹلایا سو انکے لیے ذلت کا عذاب ہے'),
(2653, 22, 58, 'اور جنہوں نے الله کی راہ میں ہجرت کی پھر قتل کیے گئے یا مر گئے البتہ انہیں الله اچھا رزق دے گا اور بے شک الله سب سے بہتر رزق دینے والا ہے'),
(2654, 22, 59, 'البتہ انہیں ایسی جگہ پہنچائے گا جسے پسند کریں گے اور بیشک الله جاننے والا بردبار ہے'),
(2655, 22, 60, 'بات یہ ہے اور جس نے اسی قدر بدلہ لیا جس قدر اسے تکلیف دی گئی تھی پھر اس پر زیادتی کی گئی تو الله ضرور اس کی مد کرے گا بے شک الله درگزر کرنے والا معاف کرنے والا ہے'),
(2656, 22, 61, 'وہ اس لیے کہ الله رات کو دن میں اور دن کو رات میں داخل کیا کرتا ہے اور بے شک الله سننے والا دیکھنے والا ہے'),
(2657, 22, 62, 'یہ اس لیے کہ حق الله ہی کی ہستی ہے اور جنہیں اس کے سوا پکارتے ہیں باطل ہیں اور بے شک الله ہی بلند مرتبہ بڑائی والا ہے'),
(2658, 22, 63, 'کیا تو نے نہیں دیکھا کہ الله نے آسمان سے پانی نازل کیا پھر زمین سرسبز ہوجاتی ہے بے شک الله مہربان خبردار ہے'),
(2659, 22, 64, 'جوکچھ آسمانوں اور زمین میں ہے سب اسی کا ہے اور بے شک الله وہی بے نیاز قابل تعریف ہے'),
(2660, 22, 65, 'کیا تم نے نہیں دیکھا کہ الله نے زمین کی سب چیزوں اور کشتیوں کو تمہارے تابع کر دیا ہےجو دریا میں اس کے حکم سے چلتی ہیں اور آسمان کو زمین پر گرنے سے تھامے ہوئے ہے مگر اس کے حکم سے بے شک الله لوگوں پر نرمی کرنے والا نہایت رحم کرنے والا ہے'),
(2661, 22, 66, 'اور وہ وہی ہے جس نے تمہیں زندہ کیا پھر تمہیں مارے گا پھر تمہیں زندہ کرے گا بے شک انسان البتہ بڑا ہی نا شکرا ہے'),
(2662, 22, 67, 'ہم نے ہر قوم کے لیے ایک دستور مقرر کر دیا ہے جس پر وہ چلتے ہیں پھر انہیں تمہارے ساتھ اس معاملہ میں جھگڑنا نہ چاہیئے اور اپنے رب کی طرف بلا بے شک تو البتہ سیدھے راستہ پر ہے'),
(2663, 22, 68, 'اور اگر تجھ سے جھگڑا کریں تو کہہ دے الله بہتر جانتا ہے جو تم کرتے ہو'),
(2664, 22, 69, 'الله قیامت کے دن تمہارے درمیان فیصلہ کرے گا جس چیز میں تم اختلاف کرتے تھے'),
(2665, 22, 70, 'کیا تجھے معلوم نہیں کہ الله جانتا ہے جو کچھ آسمان اور زمین میں ہے یہ سب کتاب میں لکھا ہوا ہے یہ الله پر آسان ہے'),
(2666, 22, 71, 'اور الله کے سوا ایسی چیزکو پوجتے ہیں جس پر اس نے کوئی سند نہیں اتاری اور نہ انکے پاس کوئی اس کا علم ہے اور ظالموں کا کوئی مددگار نہ ہوگا'),
(2667, 22, 72, 'اور جب انہیں ہماری کھلی کھلی آیتیں پڑھ کر سنائی جائیں تو تم منکروں کے چہروں میں ناراضگی دیکھو گے قریب ہوتے ہیں کہ جو لوگ انہیں ہماری آیتیں پڑھ کر سناتے ہیں ان پر حملہ کر دیں کہہ دو کیا میں تمہیں اس سے بھی بدتر بات بتاؤں آگ ہے کہ جس کا الله نے منکروں سے وعدہ کیا ہے اور وہ بری جگہ ہے'),
(2668, 22, 73, 'اے لوگو ایک مثال بیان کی جاتی ہے اسے کان لگا کر سنو جنہیں تم الله کے سوا پکارتے ہو وہ ایک مکھی بھی نہیں بنا سکتے اگرچہ وہ سب اس کے لیے جمع ہوجائیں اور اگر ان سے مکھی کوئی چیز چھین لے تو اسے مکھی سے چھڑا نہیں سکتے عابد اور معبود دونوں ہی عاجز ہیں'),
(2669, 22, 74, 'انہوں نے الله کی کچھ بھی قدر نہ کی بے شک الله زوروالا غالب ہے'),
(2670, 22, 75, 'فرشتوں اور آدمیوں میں سے الله ہی پیغام پہنچانے کے لیے چن لیتا ہے بے شک الله سننے والا دیکھنے والا ہے'),
(2671, 22, 76, 'وہ ان کے اگلے اور پچھلے حالات جانتا ہے اور سب کاموں کا مدار الله پر ہے'),
(2672, 22, 77, 'اے ایمان والو رکوع اور سجدہ کرو اور اپنے رب کی بندگی کرو اور بھلائی کرو تاکہ تمہارا بھلا ہو'),
(2673, 22, 78, 'اور الله کی راہ میں کوشش کرو جیسا کوشش کرنے کا حق ہے اس نے تمہیں پسند کیا ہے اور دین میں تم پر کسی طرح کی سختی نہیں کی تمہارے باپ ابراھیم کا دین ہے اسی نے تمہارا نام پہلے سےمسلمان رکھا تھااوراس قرآن میں بھی تاکہ رسول تم پر گواہ بنے اور تم لوگو ں پر گواہ بنو پس نماز قائم کرو اور زکوٰة دو اور الله کو مضبوط ہو کر پکڑو وہی تمہارا مولیٰ ہے پھر کیا ہی اچھامولیٰ اور کیا ہی اچھا مددگار ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'ٰبے شک ایمان والے کامیاب ہو گئے'),
(2675, 23, 2, 'جو اپنی نماز میں عاجزی کرنے والے ہیں'),
(2676, 23, 3, 'اور جو بے ہودہ باتو ں سے منہ موڑنے والے ہیں'),
(2677, 23, 4, 'اور جو زکواة دینے والے ہیں'),
(2678, 23, 5, 'اور جو اپنی شرم گاہوں کی حفاظت کرنے والے ہیں'),
(2679, 23, 6, 'مگر اپنی بیویوں یا لونڈیوں پر اس لیے کہ ان میں کوئی الزام نہیں'),
(2680, 23, 7, 'پس جو شخص اس کے علاوہ طلب گار ہو تو وہی حد سے نکلنے والے ہیں'),
(2681, 23, 8, 'اور جو اپنی امانتوں اور اپنے وعدہ کا لحاظ رکھنے والے ہیں'),
(2682, 23, 9, 'اور جو اپنی نمازوں کی حفاظت کرتے ہیں'),
(2683, 23, 10, 'وہی وارث ہیں'),
(2684, 23, 11, 'جو جنت الفردوس کے وارث ہوں گے وہ اس میں ہمیشہ رہنے والے ہوں گے'),
(2685, 23, 12, 'اور البتہ ہم نے انسان کومٹی کے خلاصہ سے پیدا کیا'),
(2686, 23, 13, 'پھر ہم نے حفاظت کی جگہ میں نطفہ بنا کر رکھا'),
(2687, 23, 14, 'پھر ہم نے نطفہ کا لوتھڑا بنایا پھر ہم نے لوتھڑے سے گوشت کی بوٹی بنائی پھر ہم نے اس بوٹی سے ہڈیاں بنائیں پھر ہم نے ہڈیوں پر گوشت پہنایا پھر اسے ایک نئی صورت میں بنا دیا سو الله بڑی برکت والا سب سےبہتر بنانے والا ہے'),
(2688, 23, 15, 'پھر تم اس کے بعد مرنے والے ہو'),
(2689, 23, 16, 'پھر تم قیامت کے دن اٹھائے جاؤ گے'),
(2690, 23, 17, 'اورہم ہی نے تمہارے اوپر سات آسمان بنائے ہیں اور ہم بنانے میں بے خبر نہ تھے'),
(2691, 23, 18, 'اور ہم نے ایک اندازہ کے ساتھ آسمان سے پانی نازل کیا پھر اسے زمین میں ٹھیرایا اور ہم اس کے لے جانے پر بھی قادر ہیں'),
(2692, 23, 19, 'پھر ہم نے اس سے تمہارے لیے کھجور اور انگور کے باغ اگا دیے جن میں تمہارے بہت سے میوے ہیں اور انہی میں سے کھاتے ہو'),
(2693, 23, 20, 'اور وہ درخت جو طور سینا سے نکلتا ہے جو کھانے والوں کے لیے روغن اور سالن لے کر اگتا ہے'),
(2694, 23, 21, 'اور بے شک تمہارے لیے چارپایوں میں بھی عبرت ہے کہ ہم تمہیں ان کے پیٹ کی چیزوں میں سے پلاتے ہیں اور تمہارے لیے ان میں اوربھی بہت سے فائدے ہیں اور ان میں سے بعض کو کھاتے ہو'),
(2695, 23, 22, 'اور ان پر اور کشتیوں پر سوار بھی کیے جاتے ہو'),
(2696, 23, 23, 'اور ہم نے نوح کو اس کی قوم کے پاس بھیجا پھر اس نے کہا اے میری قوم الله کی بندگی کرو اس کے سوا تمہارا کوئی معبود نہیں پھر تم کیوں نہیں ڈرتے'),
(2697, 23, 24, 'سواس کی قوم کے کافر سرداروں نے کہا کہ یہ بس تم ہی جیسا آدمی ہے تم پر بڑائی حاصل کرنا چاہتا ہے اور اگر الله چاہتا تو فرشتے بھیج دیتا ہم نے اپنے پہلے باب دادا سے یہ بات کبھی نہیں سنی'),
(2698, 23, 25, 'یہ تو بس ایک دیوانہ آدمی ہے پس اس کا ایک وقت تک انتظار کرو'),
(2699, 23, 26, 'کہا اے میرے رب تو میری مدد کر کیوں کہ انہو ں نے مجھے جھٹلایا ہے'),
(2700, 23, 27, 'پھر ہم نے اس کی طرف وحی کی کہ ہماری آنکھوں کے سامنے اور ہمارے حکم سے کشتی بنا پھر جب ہمارا حکم آ پہنچے اور تنور ابلنے لگے پس تو کشتی میں ہر چیز کا جوڑا نر مادہ اور اپنے گھر والوں کو بٹھا لے مگر ان میں سے وہ شخص جس کے لیے پہلے فیصلہ ہو چکا ہے او رظالموں کے معاملہ میں مجھ سے بات نہ کر بے شک وہ غرق کیے جائیں گے'),
(2701, 23, 28, 'پھر جب تو اور جو تیرے ساتھ ہے کشتی پر چڑھ بیٹھو تو کہہ الله کا شکر ہے جس نے ہمیں ظالموں سے چھوڑایا'),
(2702, 23, 29, 'اور کہہ اے میرے رب مجھے برکت کے ساتھ اتار اور تو بہتر اتارنے والا ہے'),
(2703, 23, 30, 'اس واقعہ میں بہت سی نشانیاں ہیں بے شک ہم تو آزمائش کرنے والے تھے'),
(2704, 23, 31, 'پھر ہم نے انکے بعد ایک دوسرا دور پیدا کیا'),
(2705, 23, 32, 'پھر ان میں بھی انہیں میں سے ایک رسول بھیجا کہ الله کی عبادت کرو تمہارے لیے اس کے سوا اورکوئی معبود نہیں پھر تم کیوں نہیں ڈرتے'),
(2706, 23, 33, 'اوراس کی قوم کے سرداروں نے کہا جنہوں نے کفر کیا تھا اور قیامت کی آمد کو جھٹلاتے تھے اور جنہیں ہم نے دنیا کی زندگی میں آسودہ کر رکھا تھا کہ یہ بس تمہیں جیسا آدمی ہے وہی کھاتا ہے جو تم کھاتے ہو اور وہی پیتا ہے جو تم پیتے ہو'),
(2707, 23, 34, 'اوراگر تم نےاپنے جیسے آدمی کی فرمانبرداری کی توبے شک تم گھاٹے میں پڑ گئے'),
(2708, 23, 35, 'کیا تمہیں وعدہ دیتا ہے کہ جب تم مر جاؤ گے اورمٹی اور ہڈیاں ہو جاؤ گے تو تم نکالے جاؤ گے'),
(2709, 23, 36, 'بہت ہی بعید بہت ہی بعید بات ہے جو تم سے کہی جاتی ہے'),
(2710, 23, 37, 'ہماری صرف یہی دنیا کی زندگی ہے مرتے اور جیتےہیں اور ہم اٹھائے نہیں جائیں گے'),
(2711, 23, 38, 'بس یہ ایک ایسا شخص ہے جو الله پر جھوٹ باندھتا ہے اور ہم اسے ماننے والے نہیں ہیں'),
(2712, 23, 39, 'کہا اے میرے رب میری مدد کر کہ انہو ں نے مجھے جھٹلایا ہے'),
(2713, 23, 40, 'فرمایا تھوڑی دیر کے بعد یہ خود نادم ہوں گے'),
(2714, 23, 41, 'پھر انہیں ایک سخت آواز نے سچے وعدہ کا موافق آ پکڑا پھر ہم نے انہیں خس و خاشاک کر دیا پھر ظالموں پر الله کی پٹکار ہے'),
(2715, 23, 42, 'پھر ہم نے ان کے بعد اوربہت سے دور پیدا کیے'),
(2716, 23, 43, 'کوئی جماعت نہ اپنے وقت سے آگے بڑھ سکتی ہے نہ پیچھے ہٹ سکتی ہے'),
(2717, 23, 44, 'پھر ہم اپنے رسول لگاتار بھیجتے رہے جب کوئی رسول اپنی قوم کے پاس آیا وہ اسے جھٹلاتی ہی رہی پھر ہم بھی ایک کے بعد دوسری کو ہلاک کرتے گئے اور ہم نے ان کو افسانے بنا دیے پھر ان لوگوں پر پھٹکار ہے جو ایمان نہیں لاتے'),
(2718, 23, 45, 'پھر ہم نے موسیٰ اور اس کے بھائی ہارون کو اپنی نشانیاں اور کھلی سند دے کر'),
(2719, 23, 46, 'فرعون اور اس کے سرداروں کے پاس بھیجا پھر انہوں نے تکبر کیا اور وہ سرکش لوگ تھے'),
(2720, 23, 47, 'پھر کہا کیا ہم اپنے جیسے دو شخصوں پر ایمان لائیں جن کی قوم ہماری غلامی کر رہی ہو'),
(2721, 23, 48, 'پھر ان دونوں کو جھٹلایا پھر ہلاک کر دیے گئے'),
(2722, 23, 49, 'اور البتہ ہم نے موسیٰ کو کتاب دی تھی تاکہ وہ ہدایت پائیں'),
(2723, 23, 50, 'اور ہم نے مریم کے بیٹے اور اس کی ماں کو نشانی بنایا تھا اور انہیں ایک ٹیلہ پر جگہ دی جہاں ٹھیرےنے کا موقع اور پانی جاری تھا'),
(2724, 23, 51, 'ا ے رسولو! ستھری چیزیں کھاؤ اور اچھے کام کرو بے شک میں جانتا ہوں جو تم کرتے ہو'),
(2725, 23, 52, 'اور بے شک یہ تمہاری جماعت ایک ہی جماعت ہے اور میں تم سب کا رب ہوں پس مجھ سے ڈرو'),
(2726, 23, 53, 'پھر انہوں نے اپنے دین کو آپس میں ٹکڑے ٹکڑے کر لیا ہر ایک جماعت اس ٹکڑے پو جو ان کے پاس ہے خوش ہونے والے ہیں'),
(2727, 23, 54, 'پھر ایک وقت تک انہیں اپنے نشہ میں پڑا رہنے دو'),
(2728, 23, 55, 'کیا وہ یہ خیال کرتے ہیں کہ ہم انہیں مال اور اولاد میں ترقی دے رہے ہیں'),
(2729, 23, 56, 'انہیں فائدہ پہچانے میں جلدی کر رہے ہیں بلکہ یہ نہیں سمجھتے'),
(2730, 23, 57, 'بے شک جو اپنے رب کی ہیبت سے ڈرنے والے ہیں'),
(2731, 23, 58, 'اور جو اپنے رب کی آیتوں پر ایمان لاتے ہیں'),
(2732, 23, 59, 'اور جو اپنے رب کے ساتھ کسی کو شریک نہیں کرتے'),
(2733, 23, 60, 'اور جو دیتے ہیں جو کچھ دیتے ہیں اور ان کے دل اس سے ڈرتے ہیں کہ وہ اپنے رب کی طرف لوٹنے والے ہیں'),
(2734, 23, 61, 'یہی لوگ نیک کامو ں میں جلدی کرتے ہیں اور وہی نیکیوں میں آگے بڑھنے والے ہیں'),
(2735, 23, 62, 'اور ہم کسی پر اس کی طاقت سے بڑھ کر بوجھ نہیں ڈالتے اور ہمارے پاس ایک کتاب ہے جو سچ بولے گی اور ان پر ظلم نہیں کیا جائے گا'),
(2736, 23, 63, 'بلکہ ان کے دل اس سے بے ہوشی میں پڑے ہوئے ہیں اوراس کے سوا ان کے اوربھی کام ہیں کہ جنہیں وہ کیا کرتے ہیں'),
(2737, 23, 64, 'یہاں تک کہ جب ہم ان میں سے آسودہ حال لوگوں کو عذاب میں پکڑیں گے فوراً وہ چلائیں گے'),
(2738, 23, 65, 'آج کے دن مت چلاؤ بے شک تم ہم سے چھڑائے نہ جاؤ گے'),
(2739, 23, 66, 'تمہیں میری آیتیں سنائی جاتی تھیں پھر تم ایڑیوں پر الٹے بھاگتے تھے'),
(2740, 23, 67, 'غرور میں آ کر اسے کہانی سمجھ کر چلے جایا کرتے تھے'),
(2741, 23, 68, 'کیا انہوں نے اس ارشاد میں غور ہی نہیں کیا یا ان کے پاس ایسی بات آئی ہے جو ان کے پہلے باپ دادا کے پاس نہیں آئی تھی'),
(2742, 23, 69, 'یا یہ لوگ اپنے رسول کو پہچانتے نہیں تب یہ اس کے منکر ہیں'),
(2743, 23, 70, 'یا کہتے ہیں کہ اسے جنون ہے بلکہ رسول ان کے پاس حق بات لے کر آیا ہے اور ان میں سے اکثر حق کو ناپسند کرنے والے ہیں'),
(2744, 23, 71, 'اوراگر حق ان کی خواہشوں کے مطابق ہوتا تو آسمان اور زمین میں اورجو کچھ ان میں ہے درہم برہم ہو گیا ہوتا بلکہ ہم نے تو ان کی نصیحت انہیں پہنچا دی ہے سو وہ اپنی نصیحت سے منہ موڑنے والے ہیں'),
(2745, 23, 72, 'کیا تو ان سے کچھ اجرت مانگتا ہے سو تیرے رب کی اجرت بہتر ہے اور وہی سب سے بہتر روزی دینے والا ہے'),
(2746, 23, 73, 'اوربے شک تو انہیں سیدھے راستہ کی طرف بلاتا ہے'),
(2747, 23, 74, 'اور جو لوگ آخرت کو نہیں مانتے سیدھے راستہ سے ہٹنے والے ہیں'),
(2748, 23, 75, 'اور اگر ہم ا ن پر رحم کر کے ان کی تکلیف کو دور کر دیں تو بھی وہ اپنی سرکشی میں گمراہ پڑے رہیں گے'),
(2749, 23, 76, 'اور البتہ ہم نے انہیں عذاب میں مبتلا بھی کیا پھر بھی اپنے رب کے سامنے عاجزی نہ کی اورنہ گڑگڑائے'),
(2750, 23, 77, 'یہاں تک کہ جب ہم نے ان پر سخت عذاب کا دروازہ کھولا تو فوراً اس میں نا امید ہوگئے'),
(2751, 23, 78, 'اور اسی نے تمہارے کان اور آنکھیں اور دل بنائےہیں تم بہت ہی کم شکر کرتے ہو'),
(2752, 23, 79, 'اور اسی نے تمہیں زمین میں پھیلا رکھا ہے اور اسی کی طرف جمع کیے جاؤ گے'),
(2753, 23, 80, 'اور وہی زندہ کرتا ہے اورمارتا ہے اور رات اور دن کا بدلنا اسی کی اختیار میں ہے سو کیا تم نہیں سمجھتے'),
(2754, 23, 81, 'بلکہ وہی کہتے ہیں جو پہلے لوگ کہتے تھے'),
(2755, 23, 82, 'کہتے ہیں کیا جب ہم مرجائیں گے اور مٹی اور ہڈیاں ہو جائیں گے تو کیا ہم دوبارہ زندہ کیے جائیں گے'),
(2756, 23, 83, 'اس کا تو ہم سے اوراس سے پہلے اور ہمارے باپ دادا سے وعدہ ہوتا چلاآیا ہے یہ صرف اگلےلوگوں کی کہانیاں ہیں'),
(2757, 23, 84, 'ان سے پوچھو یہ زمین اور جو کچھ اس میں ہے کس کا ہے اگر تم جانتے ہو'),
(2758, 23, 85, 'وہ فوراً کہیں گے الله کاہے کہہ دو پھر تم کیوں نہیں سمجھتے'),
(2759, 23, 86, 'ان سے پوچھو کہ ساتوں آسمانوں اور عرش عظیم کا مالک کون ہے'),
(2760, 23, 87, 'وہ فوراً کہیں گے الله ہے کہہ دوکیاپھر تم الله سے نہیں ڈرتے'),
(2761, 23, 88, 'ان سے پوچھو کہ ہر چیز کی حکومت کس کے ہاتھ میں ہے اور وہ بچا لیتا ہے اور اسے کوئی نہیں بچا سکتا اگر تم جانتے ہو'),
(2762, 23, 89, 'وہ فوراً کہیں گے الله ہی کے ہاتھ میں ہے کہہ دو پھرتم کیسے دیوانے ہو رہے ہو'),
(2763, 23, 90, 'بلکہ ہم نے تو ان کے پاس حق بات پہنچا دی اور بے شک وہ البتہ جھوٹے ہیں'),
(2764, 23, 91, 'الله نے کوئی بھی بیٹا نہیں بنایا اورنہ اس کے ساتھ کوئی معبود ہی ہے اگر ہوتا تو ہر خدا اپنی بنائی ہوئی چیز کو الگ لے جاتا اور ایک دوسرے پر چڑھائی کرتا الله پاک ہے جو یہ بیان کرتے ہیں'),
(2765, 23, 92, 'غائب اور حاضر سب کا جاننے والا ہے وہ بہت بلند ہے اس سے جسے یہ شریک بناتے ہیں'),
(2766, 23, 93, 'کہہ دو اے میرے رب اگر تو مجھے دکھائے وہ چیز جس کا انہیں وعدہ دیا جارہا ہے'),
(2767, 23, 94, 'تو اے میرے رب مجھے ظالموں میں شامل نہ کر'),
(2768, 23, 95, 'اور ہم اس پر قادر ہیں کہ تجھے دکھا دیں جو ہم نے ان سے وعدہ کیا ہے'),
(2769, 23, 96, 'بری بات کے جواب میں وہ کہو جو بہتر ہے ہم خوب جانتے ہیں جو یہ بیان کرتے ہیں'),
(2770, 23, 97, 'اور کہواے میرے رب میں شیطانی خطرات سے تیری پناہ مانگتا ہوں'),
(2771, 23, 98, 'اوراےمیرے رب میں تیری پناہ چاہتا ہوں اس سے کہ شیطان میرے پاس آئيں'),
(2772, 23, 99, 'یہاں تک کہ جب ان میں سے کسی کو موت آئے گی تو کہے گا اے میرے رب مجھے پھر بھیج دے'),
(2773, 23, 100, 'تاکہ جسے میں چھوڑ آیا ہوں اس میں نیک کام کر لوں ہر گز نہیں ایک بات ہی بات ہے جسے یہ کہہ رہا ہے اور ان کے آگے قیامت تک ایک پردہ پڑا ہوا ہے'),
(2774, 23, 101, 'پھر جب صور پھونکا جائے گا تو اس دن ان میں نہ رشتہ داریاں رہیں گے اور نہ کوئی کسی کو پوچھے گا'),
(2775, 23, 102, 'پھر جن کا پلہ بھاری ہوا تو وہی فلاح پائیں گے'),
(2776, 23, 103, 'اورجن کا پلہ ہلکا ہوگا تو وہی یہ لوگ ہوں گے جنہوں نے اپنا نقصان کیا ہمیشہ جہنم میں رہنے والے ہوں گے'),
(2777, 23, 104, 'ان کے مونہوں کو آگ جھلس دے گی اور وہ اس میں بدشکل ہونے والے ہوں گے'),
(2778, 23, 105, 'کیا تمہیں ہماری آیتیں نہیں سنائی جاتی تھیں پھر تم انہیں جھٹلاتے تھے'),
(2779, 23, 106, 'کہیں گے اے ہمارے رب ہم پر ہماری بدبختی غالب آگئی تھی اور ہم لوگ گمراہ تھے'),
(2780, 23, 107, 'اے رب ہمارے ہمیں اس سے نکال دے اگر پھر کریں تو بے شک ہم ظالم ہوں گے'),
(2781, 23, 108, 'فرمائے گا اس میں پھٹکارے ہوئے پڑے رہو اور مجھ سے نہ بولو'),
(2782, 23, 109, 'میرے بندوں میں سے ایک گروہ تھا جو کہتے تھے اے ہمارے رب ہم ایمان لائے تو ہمیں بخش دے اور ہم پر رحم کرو اور تو بہت بڑا رحم کرنے والا ہے'),
(2783, 23, 110, 'سو تم نے ان کی ہنسی اڑائی یہا ں تک کہ انہوں نے تمہیں میری یاد بھی بھلادی اور تم ان سے ہنسی ہی کرتے رہے'),
(2784, 23, 111, 'آج میں نے انہیں ان کے صبر کا بدلہ دیا کہ وہی کامیاب ہوئے'),
(2785, 23, 112, 'فرمائے گا تم زمین پر گنتی کے کتنےبرس رہے'),
(2786, 23, 113, 'کہیں گے ایک دن یا اس سے بھی کم رہے ہیں پس آپ گنتی کرنے والوں سے پوچھ لیں'),
(2787, 23, 114, 'فرمائے گا تم اس میں بہت نہیں تھوڑا ہی رہے ہو کاش کہ تم سمجھ لیتے'),
(2788, 23, 115, 'سو کیا تم یہ خیال کرتے ہو کہ ہم نے تمہیں نکما پیدا کیا ہے اور یہ کہ تم ہمارے پاس لوٹ کر نہیں آؤ گے'),
(2789, 23, 116, 'سو الله بہت ہی عالیشان ہے جو حقیقی بادشاہ ہے اس کے سوا اور کوئی معبود نہیں عرش عظیم کا مالک ہے'),
(2790, 23, 117, 'اور جس نے الله کے ساتھ اور معبو کو پکارا جس کی اس کے پاس کوئی سند نہیں تو اس کا حساب اسی کے رب کے ہاں ہوگا بے شک کافر نجات نہیں پائیں گے'),
(2791, 23, 118, 'اورکہو اے میرے رب معاف کر اور رحم کر اور تو سب سے بہتر رحم کرنے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'یہ ایک سورت ہے جسے ہم نے نازل کیا ہے اور اس کے احکام ہم نے ہی فرض کئے ہیں اور ہم نے اس میں صاف صاف آیتیں نازل کی ہیں تاکہ تم سمجھو'),
(2793, 24, 2, 'بدکار عورت اور بدکار مرد سو دونوں میں سے ہر ایک کو سو سو دُرّے مارو اور تمہیں الله کے معاملہ میں ان پر ذرا رحم نہ آنا چاہیئے اگر تم الله پر اور قیامت کے دن پر ایمان رکھتے ہو اور ان کی سزا کے وقت مسلمانوں کی ایک جماعت کو حاضر رہنا چاہیئے'),
(2794, 24, 3, 'بدکار مرد سوائے بدکار عورت یا مشرکہ کے نکاح نہیں کرے گا اور بدکار عورت سے سوائے بد کار مرد یا مشرک کے اور کوئی نکاح نہیں کرے گا اور ایمان والوں پر یہ حرام کیا گیا ہے'),
(2795, 24, 4, 'اور جو لوگ پاک دامن عورتوں پر تہمت لگاتے ہیں اور پھر چار گواہ نہیں لاتے تو انہیں اسّی درے مارو اور کبھی ان کی گواہی قبول نہ کرو اور وہی لوگ نافرمان ہیں'),
(2796, 24, 5, 'مگر جنہوں نے اس کے بعد توبہ کر لی اور درست ہو گئے تو بے شک الله ہی بخشنے والا نہایت رحم والا ہے'),
(2797, 24, 6, 'او رجو لوگ اپنی بیویوں پر تہمت لگاتے ہیں اور ان کے لیے سوائے اپنے اورکوئی گواہ نہیں تو ایسے شخص کی گواہی کی یہ صورت ہے کہ چار مرتبہ الله کی قسم کھا کر گواہی دے کہ بے شک وہ سچا ہے'),
(2798, 24, 7, 'او رپانچویں مرتبہ یہ کہے کہ اس پر اللہ کی لعنت ہواگر وہ جھوٹا ہے'),
(2799, 24, 8, 'او رعورت کی سزا کو یہ بات دور کر دے گی کہ الله کو گواہ کر کے چار مرتبہ یہ کہے کہ بے شک اس پر الله کا غضب پڑے اگر وہ سچا ہے'),
(2800, 24, 9, 'اور پانچویں مرتبہ کہے کہ بے شک اس پر الله کا غضب پڑے اگر وہ سچا ہے'),
(2801, 24, 10, 'اور اگر تم پر الله کا فضل اور اس کی رحمت نہ ہوتی اور یہ کہ الله توبہ قبول کرنے والا حکمت والا ہے (تو کیا کچھ نہ ہوتا)'),
(2802, 24, 11, 'بے شک جو لوگ یہ طوفان لائے ہیں تم ہی میں سے ایک گروہ ہے تم سے اپنے حق میں برا نہ سمجھو بلکہ وہ تمہارے لیے بہتر ہے ان میں سے ہر ایک کے لیے بقدرِ عمل گناہ ہے اور جس نے ان میں سے سب سے زیادہ حصہ لیا اس کے لیے بڑا عذاب ہے'),
(2803, 24, 12, 'جب تم نے یہ بات سنی تھی تو مسلمان مردوں اور مسلمان عورتوں نے اپنے لوگوں کے ساتھ نیک گمان کیوں نہ کیا او رکیو ں نہ کہا کہ یہ صریح بہتان ہے'),
(2804, 24, 13, 'یہ لوگ اس پرچار گواہ کیو ں نہ لائے پھر جب وہ گواہ نہ لائے تو الله کے نزدیک وہی جھوٹے ہیں'),
(2805, 24, 14, 'اور اگر تم پر الله کا فضل اور دنیا اور آخرت میں اس کی رحمت نہ ہوتی تو اس چرچا کرنے میں تم پر کوئی بڑی آفت پڑتی'),
(2806, 24, 15, 'جب تم اسے اپنی زبانوں سے نکالنے لگے اور اپنے مونہوں سے وہ بات کہنی شروع کر دی جس کا تمہیں علم بھی نہ تھا اور تم نے اسے ہلکی بات سمجھ لیا تھا حالانکہ وہ الله کے نزدیک بڑی بات ہے'),
(2807, 24, 16, 'اور جب تم نے اسے سنا تھا تو کیوں نہ کہہ دیا کہ ہمیں تو اس کا منہ سے نکالنا بھی لائق نہیں سبحان الله یہ بڑا بہتان ہے'),
(2808, 24, 17, 'الله تمہیں نصیحت کرتا ہے کہ پھر کبھی ایسا نہ کرنا اگر تم ایمان دار ہو'),
(2809, 24, 18, 'اور الله تمہارے لیے آیتیں بیان کرتا ہے اور الله جاننے والا حکمت والا ہے'),
(2810, 24, 19, 'بے شک جو لوگ چاہتے ہیں کہ ایمانداروں میں بدکاری کا چرچا ہو ان کے لیے دنیا اور آخرت میں دردناک عذاب ہے اور الله جانتا ہے اور تم نہیں جانتے'),
(2811, 24, 20, 'اور اگرتم پر الله کا فضل اوراس کی رحمت نہ ہوتی اوریہ کہ الله نرمی کرنے والا مہربان ہے (تو کیا کچھ نہ ہوتا)'),
(2812, 24, 21, 'اے ایمان والو شیطان کے قدموں پر نہ چلو اورجو کوئی شیطان کے قدموں پرچلے گا سو وہ تو اسے بے حیائی اور بری باتیں ہی بتائے گا اوراگر تم پر الله کا فضل اوراس کی رحمت نہ ہوتی تو تم میں سے کوئی کبھی بھی پاک صاف نہ ہوتا اورلیکن الله جسے چاہتا ہے پاک کر دیتا ہے اور الله سننے والا جاننے والا ہے'),
(2813, 24, 22, 'اور تم میں سے بزرگی اور کشائش والے اس بات پر قسم نہ کھائیں کہ رشتہ داروں اور مسکینوں اور الله کی راہ میں ہجرت کرنے والوں کو نہ دیا کریں گے اور انہیں معاف کرنا اور درگذر کرنا چاہیے کیا تم نہیں چاہتے کہ الله تمہیں معاف کر دے اور الله بخشنے والا نہایت رحم والا ہے'),
(2814, 24, 23, 'جو لوگ پاک دامنوں بے خبر ایمان والیوں پر تہمت لگاتے ہیں ان پر دنیا اور آخرت میں لعنت ہے اوران کے لیےبڑا عذاب ہے'),
(2815, 24, 24, 'جس دن ان پر ان کی زبانیں اور ان کے ہاتھ پاؤں گواہی دیں گے جو کچھ وہ کیا کرتے تھے'),
(2816, 24, 25, 'اس دن الله انہیں انصاف سے پوری جزا دے گا اور جان لیں گے بے شک الله ہی حق بیان کرنے والا ہے'),
(2817, 24, 26, 'ناپاک عورتیں ناپاک مردوں کے لیے ہیں اور ناپاک مرد ناپاک عورتو ں کے لیے ہیں او رپاک عورتیں پاک مردوں کے لیے ہیں اور پاک مرد پاک عورتوں کے لیے ہیں وہ لوگ اس سے پاک ہیں جو یہ کہتے ہیں ا ن کے لیے بخشش اورعزت کی روزی ہے'),
(2818, 24, 27, 'اے ایمان والو اپنے گھروں کے سوا اور کسی کے گھروں میں نہ جایا کرو جب تک اجازت نہ لے لو او رگھر والوں پر سلام نہ کر لو یہ تہارے لیے بہتر ہے تاکہ تم نصیحت حاصل کرو'),
(2819, 24, 28, 'پھر اگر وہاں کسی کو نہ پاؤ تو اندر نہ جاؤ جب تک کہ تمہیں اجازت نہ دی جائے اور اگر تمہیں کہا جائے کہ لوٹ جاؤ تو اپس چلے جاؤ یہ تمہارے حق میں بہتر ہے اور جو کچھ تم کرتے ہو الله جانتا ہے'),
(2820, 24, 29, 'تم پراس میں کوئی گناہ نہیں کہ ان گھروں میں جاؤ جہاں کوئی نہیں بستا ان میں تمہارا سامان ہے اور الله جانتا ہے جو تم ظاہر کرتے ہو اور جو تم چھپاتے ہو'),
(2821, 24, 30, 'ایمان والوں سے کہہ دو کہ وہ اپنی نگاہ نیچی رکھا کریں اور اپنی شرم گاہوں کو بھی محفوظ رکھیں یہ ان کے لیے بہت پاکیزہ ہے بے شک الله جانتا ہے جو وہ کرتے ہیں'),
(2822, 24, 31, 'اور ایمان والیوں سے کہہ دو کہ اپنی نگاہ نیچی رکھیں اور اپنی عصمت کی حفاظت کریں اور اپنی زینت کو ظاہر نہ کریں مگر جو جگہ اس میں سے کھلی رہتی ہے اوراپنے دوپٹے اپنے سینوں پر ڈالے رکھیں اوراپنی زینت ظاہر نہ کریں مگر اپنے خاوندوں پر یا اپنے باپ یا خاوند کے باپ یا اپنے بھائیوں یا بھتیجوں یا بھانجوں پر یا اپنی عورتوں پر یا اپنے غلاموں پر یا ان خدمت گاروں پر جنہیں عورت کی حاجت نہیں یا ان لڑکوں پر جو عورتوں کی پردہ کی چیزوں سے واقف نہیں اور اپنے پاؤں زمین پر زور سے نہ ماریں کہ ان کا مخفی زیور معلوم ہوجائے اوراے مسلمانو تم سب الله کے سامنے توبہ کرو تاکہ تم نجات پاؤ'),
(2823, 24, 32, 'اور جو تم میں مجرّد ہوں اور جو تمہارے غلام اور لونڈیاں نیک ہوں سب کے نکاح کرادو اگر وہ مفلس ہو ں گے تو الله اپنے فضل سے انہیں غنی کر دے گا اور الله کشائش والا سب کچھ جاننے والا ہے'),
(2824, 24, 33, 'اور چاہیے کہ پاک دامن رہیں وہ جو نکاح کی توفیق نہیں رکھتے یہاں تک کہ الله انہیں اپنے فضل سے غنی کر دے اور تمہارے غلاموں میں سے جو لوگ مال دے کر آزادی کی تحریر چاہیں تو انہیں لکھ دو بشرطیکہ ان میں بہتری کے آثار پاؤ اور انہیں الله کے مال میں سے دو جو اس نے تمہیں دیا ہے اور تمہاری لونڈیاں جو پاک دامن رہنا چاہتی ہیں انہیں دنیا کی زندگی کے فائدہ کی غرض سے زنا پر مجبور نہ کرو اور جو انہیں مجبور کرے گا تو الله ان کے مجبور ہونے کے بعد بخشنے والا مہربان ہے'),
(2825, 24, 34, 'اور البتہ ہم نے تمہارے پاس روشن آیتیں بھیج دی ہیں اور جن میں تم سے پہلوں کے حالات ہیں او رجو پرہیز گاروں کے لیے نصیحت ہیں'),
(2826, 24, 35, 'الله آسمانوں اور زمین کا نور ہے اس کے نور کی مثال ایسی ہے جیسے طاق میں چراغ ہو چراغ شیشے کی قندیل میں ہے قندیل گویا کہ موتی کی طرح چمکتا ہوا ستارا ہے زیتون کے مبارک درخت سے روشن کیا جاتا ہے نہ مشرق کی طرف ہے اور نہ مغرب کی طرف اس کا تیل قریب ہے کہ روشن ہوجائے اگرچہ اسے آگ نے نہ چھوا ہو روشنی ہے الله جسے چاہتا ہے اپنی روشنی کی راہ دکھاتا ہے اور الله کے لیے مثالیں بیان فرماتا ہے اور الله ہر چیز کا جاننے والا ہے'),
(2827, 24, 36, 'ان گھروں میں جن کی تعظیم کرنے اور ان میں اس کا نام یاد کرنے کا الله نے حکم دیا ان میں صبح اور شام الله کی تسبیح پڑھتے ہیں'),
(2828, 24, 37, 'ایسے آدمی جنہیں سوداگری اور خرید و فروخت الله کے ذکر اور نماز کے پڑھنے اور زکوٰة کے دینے سے غافل نہیں کرتی اس دن سے ڈرتے ہیں جس میں دل اور آنکھیں الٹ جائیں گی'),
(2829, 24, 38, 'تاکہ الله انہیں ان کے عمل کا اچھا بدلہ دے اور انہیں اپنے فضل سے اور بھی دے اور الله جسے چاہتا ہے بے حساب روزی دیتا ہے'),
(2830, 24, 39, 'اور جو کافر ہیں ان کے اعمال ایسے ہیں جیسے جنگل میں چمکتی ہوئی ریت ہو جسے پیاسا پانی سمجھتا ہے یہاں تک کہ جب اس کے پاس آتا ہے اسے کچھ بھی نہیں پاتا اور الله ہی کو اپنے پاس پاتا ہے پھر الله نے اس کا حساب پورا کر دیا اور الله جلد حساب لینے والا ہے'),
(2831, 24, 40, 'یا جیسے گہرے دریا میں اندھیرے ہوں اس پر ایک لہر چڑھ آتی ہے اس پرایک او رلہر ہے اس کے اوپر بادل ہے اوپر تلے بہت سے اندھیرے ہیں جب اپنا ہاتھ نکالے تو اسے کچھ بھی دیکھ نہ سکے اور جسے الله ہی نے نور نہ دیا ہو اس کے لیے کہیں نور نہیں ہے'),
(2832, 24, 41, 'کیا تم نے نہیں دیکھا کہ آسمانوں اور زمین کے رہنے والے اور پرند جو پر پھیلائے اڑتے ہیں سب الله ہی کی تسبیح کرتے ہیں ہر ایک نے اپنی نماز اور تسبیح سمجھ رکھی ہے اور الله جانتا ہے جو کچھ وہ کرتے ہیں'),
(2833, 24, 42, 'اور آسمانوں اور زمین کی بادشاہی الله ہی کی ہے اور الله ہی کی طرف لوٹ کر جانا ہے'),
(2834, 24, 43, 'کیا تو نے نہیں دیکھا الله ہی بادل کو چلاتا ہے پھر اسے ملاتا ہے پھر اسے تہہ بر تہہ کرتا ہے پھر تو بارش کو دیکھتا ہے کہ اس کے بیچ میں سے نکلتی ہے اور آسمان سے جو ان میں اولوں کے پہاڑ ہیں ان میں سے اولے برساتا ہے پھر انہیں جس پر چاہتا ہے گراتا ہے اور جس سے چاہتا ہے روک لیتا ہے قریب ہے کہ اس کی بجلی کی چمک آنکھوں کو لے جائے'),
(2835, 24, 44, 'الله ہی رات اور دن کو بدلتا ہے بے شک اس میں آنکھوں والوں کے لیے عبرت ہے'),
(2836, 24, 45, 'اور الله نے ہر جاندار کو پانی سے بنایا ہے سو بعض ان میں سے اپنےپیٹ کے بل چلتے ہیں اور بعض ان میں سے دو پاؤں پر چلتے ہیں اور اور بعض ان میں سے چار پاؤں پر چلتے ہیں الله جو چاہتا ہے پیدا کرتا ہے بے شک الله ہر چیز پر قادر ہے'),
(2837, 24, 46, 'البتہ ہم نے کھلی کھلی آیتیں نازل کر دی ہیں اور الله جسے چاہے سیدھے راستہ پر چلاتا ہے'),
(2838, 24, 47, 'اور کہتے ہیں ہم الله اور رسول پرایمان لائے اور ہم فرمانبردار ہو گئے پھر ایک گروہ ان میں سے اس کے بعد پھر جاتا ہے اور وہ لوگ مومن نہیں ہیں'),
(2839, 24, 48, 'اورجب انہیں الله اور اس کے رسول کی طرف بلایا جائے تاکہ ان میں فیصلہ کرے تب ہی ایک گروہ ان میں سے منہ موڑنے والے ہیں'),
(2840, 24, 49, 'اور اگر انہیں حق پہنچتا ہو تو اس کی طرف گردن جھکائے آتے ہیں'),
(2841, 24, 50, 'کیا ان کے دلوں میں بیماری ہے یا شک میں پڑے ہیں یا ڈرتے ہیں اس سے کہ ان پر الله اور اس کا رسول ظلم کرے گا بلکہ وہی ظالم ہیں'),
(2842, 24, 51, 'مومنوں کی بات تو یہی ہوتی ہے جب انہیں الله اور اس کے رسول کی طرف بلایا جاتا ہے تاکہ وہ ان کے درمیان فیصلہ کرے وہ کہتے ہیں کہ ہم نے سنا اور مان لیا اور وہی لوگ نجات پانے والے ہیں'),
(2843, 24, 52, 'اور جو شخص الله اور اس کے رسول کی اطاعت کرتا ہے اور الله سے ڈرتا ہے اور اس کی نافرمانی سے بچتا ہے بس وہی کامیاب ہونے والے ہیں'),
(2844, 24, 53, 'اور الله کی پکی قسمیں کھا کر کہتے ہیں کہ اگر آپ انہیں حکم دیں تو سب کچھ چھوڑ کر نکل جائیں کہہ دو قسمیں نہ کھاؤ دستور کے موافق فرمانبرداری چاہیئے بے شک الله جانتا ہے جو تم کرتے ہو'),
(2845, 24, 54, 'کہہ دو الله اور اس کے رسول کی فرمانبرداری کرو پھر اگر منہ پھیرو گے تو پیغمبر تو وہی ہے جس کا وہ ذمہ دار ہے اورتم پر وہ ہے جو تمہارے ذمہ لازم کیا گیا ہے اور اگر اس کی فرمانبرداری کرو گے تو ہدایت پاؤ گے اور رسول کے ذمہ صرف صاف طور پر پہنچا دینا ہے'),
(2846, 24, 55, 'الله نے ان لوگوں سے وعدہ کیا ہے جو تم میں سے ایمان لائے اور نیک عمل کیے کہ انہیں ضرور ملک کی حکومت عطا کرے گا جیسا کہ ان سے پہلوں کو عطا کی تھی اور ان کے لیے جس دین کو پسند کیا ہے اسے ضرور مستحکم کر دے گا اور البتہ ان کے خوف کو امن سے بدل دے گا بشرطیکہ میری عبادت کرتے رہیں اور میرے ساتھ کسی کو شریک نہ کریں اور جواس کے بعد ناشکری کرے وہی فاسق ہوں گے'),
(2847, 24, 56, 'اور نماز پڑھا کرو اور زکوٰة دیا کرو اور رسول کی فرمانبرداری کرو تاکہ تم پر رحم کیا جائے'),
(2848, 24, 57, 'کافروں کی نسبت یہ خیال نہ کر کہ ملک میں عاجز کر دیں گے اور ان کا ٹھکانہ دوزخ ہے اور بہت ہی برا ٹھکانہ ہے'),
(2849, 24, 58, 'اے ایمان والو تمہارے غلام اور تمہارےوہ لڑکے جو ابھی بالغ نہیں ہوئے تم سے ان تین وقتوں میں اجازت لے کر آیا کریں صبح کی نماز سے پہلے اور دوپہر کے وقت جب کہ تم اپنے کپڑے اتار دیتے ہو اور عشا کی نماز کے بعد یہ تین وقت تمہارے پردوں کے ہیں ان کے بعد تم پر اور نہ ان پر کوئی الزام ہے تم آپس میں ایک دوسرے کے پاس آنے جانے والے ہو اسی طرح الله تمہارے لیے آیتیں کھول کر بیان کرتا ہے اور الله جاننے والا حکمت والا ہے'),
(2850, 24, 59, 'اورجب تمہارے لڑکے بلوغ کو پہنچ جائیں انہیں بھی اجازت لے کر آنا چاہیے جس طرح کہ ان سے پہلے لوگ اجازت لے کر آتے ہیں الله اس طرح تمہارے لیے کھول کر احکام بیان کرتا ہے اور الله جاننے والا حکمت والا ہے'),
(2851, 24, 60, 'اوروہ بڑی بوڑھی عورتیں جو نکاح کی رغبت نہیں رکھتیں ان پر اس بات میں کوئی گناہ نہیں کہ اپنے کپڑے اتار رکھیں بشرطیکہ زینت کا اظہار نہ کریں اور اس سے بھی بچیں توان کے لیے بہتر ہے اور الله سننے والا جاننے والا ہے'),
(2852, 24, 61, 'اندھے پر اور لنگڑ ے پر اور بیمار پر اور خود تم پر اس بات میں کوئی گناہ نہیں کہ تم اپنے گھروں سے کھانا کھاؤ یا اپنے باپ کے گھروں سے یا اپنی ماؤں کے گھروں سے یا اپنے بھائیوں کے گھروں سے یا اپنی بہنوں کے گھروں سے یا اپنے چچاؤں کے گھروں سے یا اپنی پھوپھیوں کے گھروں سے یا اپنے ماموؤں کے گھروں سے یا اپنی خالاؤں کےگھروں سے یا ان گھروں سے جنکی کنجیاں تمہارے اختیار میں ہیں یا اپنے دوستوں کے گھروں سے تم پر کوئی گناہ نہیں کہ مل کر کھاؤ یا الگ الگ کھاؤ پھر جب گھروں میں داخل ہونا چاہو تو اپنے لوگوں سے سلام کیا کرو جو الله کی طرف سے مبارک اور عمدہ دعا ہے اسی طرح الله تمہارے لیے احکام بیان فرماتا ہے تاکہ تم سمجھو'),
(2853, 24, 62, 'مومن تو وہی ہیں جو الله اور اس کے رسول پر ایمان لائے ہیں اور جب وہ اس کے ساتھ کسی جمع ہونے کے کام میں ہوتے ہیں تو چلے نہیں جاتے جب تک اس سے اجازت نہ لیں جو لوگ تجھ سے اجازت لیتے ہیں وہی ہیں جوالله اور اس کے رسول پرایمان لائے ہیں پھر جب تجھ سے اپنے کسی کام کے لیے اجازت مانگیں تو ان میں سے جسے تو چاہے عزت دے اور ان کے لیے الله سے بخشش کی دعا کر الله بخشنے والا نہایت رحم والا ہے'),
(2854, 24, 63, 'رسول کے بلانے کو آپس میں ایک دوسرے کے بلانے جیسا نہ سمجھو الله انہیں جانتا ہے جو تم میں سے چھپ کر کھسک جاتے ہیں سو جو لوگ الله کے حکم کی مخالفت کرتے ہیں انہیں اس سے ڈرنا چاہیئے کہ ان پر کوئی آفت آئے یا ان پر کوئی دردناک عذاب نازل ہوجائے'),
(2855, 24, 64, 'خبردار الله ہی کا ہے جو کچھ آسمانوں اور زمین میں ہے اسے معلوم ہے جس حال پر تم ہو اور جس دن اس کی طرف پھیر لائے جائیں گے تو انہیں بتائے گا جو کچھ وہ کرتے تھے اور الله ہر چیز کو جاننے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'وہ بڑی برکت والا ہے جس نے اپنے بندے پر قرآن نازل کیا تاکہ تمام جہان کے لیےڈرانے والا ہو'),
(2857, 25, 2, 'وہ جس کی آسمانوں اور زمین میں سلطنت ہے اور اس نے نہ کسی کو بیٹا بنایا ہے اور نہ کوئی سلطنت میں اس کا شریک ہے اور اس نے ہر چیز کو پیدا کر کے اندازہ پر قائم کر دیا'),
(2858, 25, 3, 'اور انہوں نے الله کے سوا ایسے معبود بنا رکھے ہیں جو کچھ بھی پیدا نہیں کر سکتے حالانکہ وہ خود پیدا کیے گئے ہیں اور وہ اپنی ذات کے لیے نقصان اور نفع کے مالک نہیں اور موت اور زندگی اور دوبارہ اٹھنے کے بھی مالک نہیں'),
(2859, 25, 4, 'اور کافر کہتے ہیں کہ یہ تو محض جھوٹ ہے جسے اس نے بنا لیا ہے اور دوسرے لوگوں نے اس میں ا س کی مدد کی ہے پس وہ بڑے ظلم اور جھوٹ پر اتر آئے ہیں'),
(2860, 25, 5, 'اور کہتے ہیں کہ پہلوں کی کہانیاں ہیں کہ جنہیں اس نے لکھ رکھا ہے پس وہی اس پر صبح اور شام پڑھی جاتی ہیں'),
(2861, 25, 6, 'کہہ دو کہ اسے تو اس نے نازل کیا ہے جو آسمانوں اور زمین کی پوشیدہ باتیں جانتا ہے بے شک وہ بخشنے والا نہایت رحم والا ہے'),
(2862, 25, 7, 'اور کہتے ہیں اس رسول کو کیا ہوگیا کہ کھاناکھاتا اور بازاروں میں پھرتا ہے اس کے پاس کوئی فرشتہ کیوں نہیں بھیجا گیا کہ اس کے ساتھ وہ بھی ڈرانے والا ہوتا'),
(2863, 25, 8, 'یا اس کے پاس کوئی خزانہ آجاتا یا اس کے لیے باغ ہوتا جس میں سےکھاتا اور بے انصافوں نے کہا کہ تم تو بس ایک ایسے شخص کے تابع ہوگئے جس پر جادو کیا گیا ہے'),
(2864, 25, 9, 'دیکھو تو تمہارے لیے کیسی مثالیں بیان کرتے ہیں پس وہ ایسے گمراہ ہو ئے کہ راستہ بھی نہیں پا سکتے'),
(2865, 25, 10, 'بڑی برکت ہے اس کی جو چاہے تو تیرے لیے اس سے بہتر باغ بنا دے جن کے نیچے نہریں بہتی ہوں اور تیرے لیے محل بنا دے'),
(2866, 25, 11, 'بلکہ انہوں نے تو قیامت کو جھوٹ سمجھ لیا ہے اور ہم نے اس کے لیے آگ تیار کی ہے جو قیامت کو جھٹلاتا ہے'),
(2867, 25, 12, 'جب وہ انہیں دور سے دیکھے گی تو اس کے جوش و خروش کی آواز سنیں گے'),
(2868, 25, 13, 'اور جب وہ اس کے کسی تنگ مکان میں جکڑکر ڈال دیے جائیں گے تو وہاں موت کو پکاریں گے'),
(2869, 25, 14, 'آج ایک موت کو نہ پکارو اور بہت سی موتوں کو پکارو'),
(2870, 25, 15, 'کہہ دو کیا بہتر ہے یا وہ بہشت جس کا پرہیز گارو ں کے لیے وعدہ کیا گیا ہے جو ان کا بدلہ اور ٹھکانہ ہوگی'),
(2871, 25, 16, 'وہاں انہیں جو چاہیں گے ملے گا وہ اس میں ہمیشہ رہیں گے یہ وعدہ تیرے رب کے ذمہ ہے جو قابل درخواست ہے'),
(2872, 25, 17, 'اورجس دن انہیں اور ان کے معبودوں کو جمع کرے گا جنھیں وہ الله کے سوا پوجتے تھے تو فرمائے گا کیا تم ہی نے میرے ان بندوں کو گمراہ کیا تھا یا وہ خود راستہ بھول گئے تھے'),
(2873, 25, 18, 'کہیں گے تو پاک ہے ہمیں یہ کب لایق تھا کہ تیرے سوا کسی اور کو کارساز بناتے لیکن تو نے انہیں اور ان کے باپ دادا کو یہاں تک آسودگی دی تھی کہ وہ یاد کرنا بھول گئے اور یہ لوگ تباہ ہونے والے تھے'),
(2874, 25, 19, 'سو تمہارے معبودوں نے تمہاری باتو ں میں تمہیں جھٹلا دیا سو تم نہ تو ٹال سکتے ہو اور نہ مدد دے سکتے ہو اور جو تم میں سے ظلم کرے گا ہم اسے بڑا عذاب چکھائیں گے'),
(2875, 25, 20, 'اور ہم نے تجھ سے پہلے جتنے پیغمبر بھیجے وہ کھانا بھی کھاتے تھے اور بازاروں میں بھی چلتے پھرتے تھے اورہم نے تمہیں ایک دوسرے کے لیے آزمائش بنایا کیا تم ثابت قدم رہتے ہو اور تیرا رب سب کچھ دیکھنے والا ہے'),
(2876, 25, 21, 'اور ان لوگوں نے کہا جو ہم سے ملنے کی امید نہیں رکھتے کہ ہمارے پاس فرشتے کیوں نہ بھیجے گئے یا ہم اپنے رب کو دیکھ لیتے البتہ انہوں نے اپنے آپ کو بہت بڑاسمجھ لیا ہے اور بہت بڑی سرکشی کی ہے'),
(2877, 25, 22, 'جس دن فرشتوں کو دیکھیں گے اس دن مجرموں کے لیے کوئی خوشی نہیں ہو گی اورکہیں گے آڑ کر دی جائے'),
(2878, 25, 23, 'اور جو عمل انہوں نے کیے تھے ہم ان کی طرف متوجہ ہوں گے پھر انہیں اڑتی ہوئی خاک کر دیں گے'),
(2879, 25, 24, 'اس دن بہشتیوں کا ٹھکانا بہتر ہوگا اور دوپہر کی آرام گاہ بھی عمدہ ہو گی'),
(2880, 25, 25, 'اور جس دن آسمان بادل سے پھٹ جائے گا اور فرشتے بکثرت اتارے جائیں گے'),
(2881, 25, 26, 'اس دن حقیقی حکومت رحمنٰ ہی کی ہوگی اور وہ دن کافروں پر بڑا سخت ہوگا'),
(2882, 25, 27, 'اور اس دن ظالم اپنے ہاتھ کاٹ کاٹ کھائے گا کہے گا اے کاش میں بھی رسول کے ساتھ راہ چلتا'),
(2883, 25, 28, 'ہائے میری شامت کاش میں نے فلاں کو دوست نہ بنایا ہوتا'),
(2884, 25, 29, 'اسی نے تو نصیحت کے آنے کےبعد مجھے بہکا دیا اور شیطان تو انسان کو رسوا کرنے والا ہی ہے'),
(2885, 25, 30, 'اور رسول کہے گا اے میرے رب بےشک میری قوم نے اس قرآن کو نظر انداز کر رکھا تھا'),
(2886, 25, 31, 'اور ہم اسی مجرموں کو ہر ایک نبی کا دشمن بناتے رہے ہیں اور ہدایت کرنے اور مدد کرنے کے لیے تیرا رب کافی ہے'),
(2887, 25, 32, 'اور کافر کہتے ہیں کہ اس پر یکبارگی قرآن کیوں نازل نہیں کیا گیا اسی طرح اتارا گیا تاکہ ہم اس سے تیرے دل کو اطمینان دیں اور ہم نے اسے ٹھہر ٹھہر کر پڑھ سنایا'),
(2888, 25, 33, 'اور جو انوکھی بات تیرے سامنے لائیں گے ہم بھی تمہیں اس کا بہت ٹھیک جواب اوربہت عمدہ حل بتائیں'),
(2889, 25, 34, 'جو لوگ مونہوں کے بل گھسیٹ کر جہنم میں ڈالیں جائیں گے یہی برے درجے والے ہیں اور بہت ہی بڑے گمراہ ہیں'),
(2890, 25, 35, 'اور البتہ تحقیق ہم نے موسیٰ کو کتاب دی اور ہم نے اس کے ساتھ اس کے بھائی ہارون کو وزیر بنایا'),
(2891, 25, 36, 'پھر ہم نے کہا تم دونو ان لوگوں کی طرف جاؤ جنہو ں نے ہماری آیتیں جھٹلائی ہیں پھر ہم نے انہیں جڑ سے اکھاڑ کر پھینک دیا'),
(2892, 25, 37, 'اور نوح کی قوم کوبھی جب انہو ں نے رسولوں کو جھٹلایا تو ہم نے انہیں غرق کر دیا اور ہم نے انہیں لوگوں کے لیے نشانی بنا دیا اور ہم نے ظالموں کے لیے دردناک عذاب تیار کیا ہے'),
(2893, 25, 38, 'اور عاد اور ثمود اورکنوئیں والوں کو بھی اوربہت سے دور جو ان کے درمیان تھے'),
(2894, 25, 39, 'اور ہم نے ہر ایک کو مثالیں دے کر سمجھایا تھا اور سب کو ہم نے ہلاک کر دیا'),
(2895, 25, 40, 'اور یہ اس بستی پر بھی گزرے ہیں جس پر بری طرح پتھر برسائے گئے سو کیا یہ لوگ اسے دیکھتے نہیں رہتے بلکہ یہ لوگ مر کر زندہ ہونے کی امید ہی نہیں رکھتے'),
(2896, 25, 41, 'اور جب یہ لوگ تمہیں دیکھتے ہیں تو بس تم سے مذاق کرنے لگتے ہیں کیا یہی ہے جسےالله نے رسول بنا کر بھیجا'),
(2897, 25, 42, 'اس نے تو ہمیں ہمارے معبودوں سے ہٹا ہی دیا ہوتا اگر ہم ان پر قائم نہ رہتے اور انہیں جلدی معلوم ہوجائے گا جب عذاب دیکھیں گے کہ کون شخص گمراہ تھا'),
(2898, 25, 43, 'کیا تم نے اس شخص کو دیکھا جس نے اپنا خدا اپنی خوہشات نفسانی کو بنا رکھا ہے پھر کیا تو اس کا ذمہ دار ہو سکتا ہے'),
(2899, 25, 44, 'یا تو خیال کرتا ہے کہ اکثر ان میں سے سنتے یا سمجھتے ہیں یہ تو محض چوپایوں کی طرح ہیں بلکہ ان سے بھی زیادہ گمراہ ہیں'),
(2900, 25, 45, 'کیا تو نے اپنے رب کی طرف نہیں دیکھا کہ اس نے سایہ کو کیسے پھیلایا ہے اور اگر چاہتا تو اسے ٹھہرا رکھتا پھر ہم نے سورج کو اس کا سبب بنا دیا ہے'),
(2901, 25, 46, 'پھر ہم اسے آہستہ آہستہ اپنی طرف سمیٹتے ہیں'),
(2902, 25, 47, 'اور وہی ہے جس نے تمہارے لیے رات کو اوڑھنا اور نیند کو راحت بنا دیا اور دن چلنے پھرنے کے لیے بنایا'),
(2903, 25, 48, 'اوروہی تو ہے جو اپنی رحمت سے پہلے خوشخبری لانے والی ہوائیں چلاتا ہے اور ہم نے آسمان سے پاک پانی نازل فرمایا'),
(2904, 25, 49, 'تاکہ ہم اس سے مرے ہوئے شہر کو زندہ کریں اور اسے اپنی پیدا کی ہوئی چیزوں، چارپایوں اوربہت سے آدمیوں کوپلائیں'),
(2905, 25, 50, 'اور ہم نے اسے لوگوں میں بانٹ دیا ہے تاکہ نصیحت حاصل کریں پس بہت سے آدمی ناشکری کیے بغیر نہ رہے'),
(2906, 25, 51, 'اور اگر ہم چاہتے تو ہر گاؤں میں ایک ڈرانے والا بھیج دیتے'),
(2907, 25, 52, 'پس کافروں کا کہا نہ مان اس کے ساتھ بڑے زور سے ان کا مقابلہ کر'),
(2908, 25, 53, 'اور وہی ہے جس نے دو دریاؤں کو آپس میں ملا دیا یہ میٹھا خوشگوار ہے او ریہ کھاری کڑوا ہے اوران دونوں میں ایک پردہ اور مستحکم آڑ بنا دی'),
(2909, 25, 54, 'اوروہی ہے جس نے انسان کو پانی سے پیدا کیا پھر ا س کے لیے رشتہ نسب اور دامادی قائم کیا اور تیرا رب ہر چیز پر قادر ہے'),
(2910, 25, 55, 'اور وہ الله کے سوا ایسے کو پوجتے ہیں جو انہیں نہ نفع دے سکتے ہیں نہ نقصان پہنچا سکتے ہیں اور کافر اپنے رب کی طرف پیٹھ پھیرنے والا ہے'),
(2911, 25, 56, 'اور ہم نےتمہیں محض خوشخبری دینے والا اور ڈرانے والا بنا کر بھیجا ہے'),
(2912, 25, 57, 'کہہ دو میں اس پر تم سے کوئی مزدوری نہیں مانگتا مگر جو شخص اپنے رب کی طرف راستہ معلوم کرنا چاہے'),
(2913, 25, 58, 'اورتم اس زندہ خدا پر بھروسہ رکھو جو کبھی نہ مرے گا اور اس کی تسبیح اورحمد کرتے رہو اور وہ اپنے بندوں کے گناہوں سے کافی خبردار ہے'),
(2914, 25, 59, 'جس نے آسمان اور زمین اور جو کچھ ان میں ہے چھ دن میں بنایا پھر عرش پر قائم ہوا وہ رحمنٰ ہے پس ا س کی شان کسی خبردار سے پوچھو'),
(2915, 25, 60, 'اورجب ان سے کہا جاتا ہے کہ رحمنٰ کو سجدہ کرو توکہتے ہیں رحمنٰ کیا ہے کیا ہم اسے سجدہ کریں جس کے لیے تو کہہ دے اور اس سے انہیں اور زیادہ نفرت ہوتی ہے'),
(2916, 25, 61, 'بڑا برکت والا ہے وہ جس نے آسمان میں ستارے بنائے اور اس میں چراغ اور چمکتا ہوا چاند بھی بنایا'),
(2917, 25, 62, 'اور وہی ہے جس نے رات اور دن یکے بعد دیگرے آنے والے بنائے یہ اس کے لیے ہے جو سمجھنا چاہے یا شکر کرنا چاہے'),
(2918, 25, 63, 'اور رحمنٰ کے بندے وہ ہیں جو زمین پردبے پاؤں چلتے ہیں اورجب ان سے بے سمجھ لوگ بات کریں تو کہتے ہیں سلام ہے'),
(2919, 25, 64, 'اور وہ لوگ جو اپنے رب کے سامنے سجدہ میں اور کھڑے ہوکر رات گزارتے ہیں'),
(2920, 25, 65, 'اور وہ لوگ جو کہتے ہیں اے ہمارے رب ہم سے دوزخ کا عذاب دور کر دے بے شک اس کا عذاب پوری تباہی ہے'),
(2921, 25, 66, 'بے شک وہ برا ٹھکانا اوربڑی قیام گاہ ہے'),
(2922, 25, 67, 'اوروہ لوگ جب خرچ کرتے ہیں تو فضول خرچی نہیں کرتے اور نہ تنگی کرتے ہیں اور ان کا خرچ ان دونوں کے درمیان اعتدال پر ہوتا ہے'),
(2923, 25, 68, 'اوروہ جو الله کے سوا کسی اور معبود کونہیں پکارتے اور اس شخص کوناحق قتل نہیں کرتے جسے الله نے حرام کر دیا ہے اور زنا نہیں کرتے اور جس شخص نے یہ کیا وہ گناہ میں جا پڑا'),
(2924, 25, 69, 'قیامت کے دن اسے دگنا عذاب ہو گا اس میں ذلیل ہو کر پڑا رہے گا'),
(2925, 25, 70, 'مگر جس نے توبہ کی اور ایمان لایا اور نیک کام کیے سو انہیں الله برائیوں کی جگہ بھلائیاں بدل دے گا اور الله بخشنے والا مہربان ہے'),
(2926, 25, 71, 'اور جس نے توبہ کی اور نیک کام کیے تو وہ الله کی طرف رجوع کرتا ہے'),
(2927, 25, 72, 'اور جو بے ہودہ باتوں میں شامل نہیں ہوتے اور جب بیہودہ باتوں کے پاس سے گزریں تو شریفانہ طور سے گزرتے ہیں'),
(2928, 25, 73, 'اور وہ لوگ جب انہیں ان کے رب کی آیتوں سے سمجھایا جاتا ہے تو ان پر بہرے اندھے ہو کر نہیں گرتے'),
(2929, 25, 74, 'اور وہ جو کہتے ہیں کہ ہمارے رب ہمیں ہماری بیویوں اور اولاد کی طرف سے آنکھوں کی ٹھنڈک عطا فرما اور ہمیں پرہیزگاروں کا پیشوا بنا دے'),
(2930, 25, 75, 'یہی لوگ ہیں جنہیں ان کے صبر کے بدلہ میں جنت کے بالا خانے دیے جائیں گے اور ان کا وہاں دعا اور سلام سے استقبال کیا جائے گا'),
(2931, 25, 76, 'اس میں ہمیشہ رہنے والے ہوں گے ٹھیرنے اور رہنے کی خوب جگہ ہے'),
(2932, 25, 77, 'کہہ دو میرا رب تمہاری پروا نہیں کرتا اگرتم اسے نہ پکارو سو تم جھٹلا توچکے ہو پھر اب تو اس کا وبال پڑ کر رہے گا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طسمۤ'),
(2934, 26, 2, 'یہ روشن کتاب کی آیتیں ہیں'),
(2935, 26, 3, 'شاید تو اپنی جان ہلاک کرنے والا ہے اس لیے کہ وہ ایمان نہیں لاتے'),
(2936, 26, 4, 'اگر ہم چاہیں تو ان پر آسمان سے ایسی نشانی نازل کریں کہ اس کے آگے ان کی گردنیں جھک جائیں'),
(2937, 26, 5, 'اور ان کے پاس رحمنٰ کی طرف سے کوئی نئی بات نصیحت کی ایسی نہیں آئی کہ وہ اس سے منہ نہ موڑ لیتے ہوں'),
(2938, 26, 6, 'سو یہ جھٹلاتو چکے اب ان کے پاس اس چیز کی حقیقت آئے گی جس پر ٹھٹھا کیا کرتے تھے'),
(2939, 26, 7, 'کیا وہ زمین کو نہیں دیکھتے ہم نے اس میں ہر ایک قسم کی کتنی عمدہ چیزیں اُگائی ہیں'),
(2940, 26, 8, 'البتہ اس میں بڑی نشانی ہے اور ان میں سے اکثر ایمان لانے والے نہیں'),
(2941, 26, 9, 'اور بے شک تیرا رب زبردست رحم کرنے والا ہے'),
(2942, 26, 10, 'اور جب تیرے رب نے موسیٰ کو پکارا کہ اس ظالم قوم کے پاس جا'),
(2943, 26, 11, 'فرعون کی قوم کے پاس کیا وہ ڈرتے نہیں'),
(2944, 26, 12, 'عرض کی اے میرے رب میں ڈرتا ہوں کہ مجھے جھٹلا دیں'),
(2945, 26, 13, 'اور میرا سینہ تنگ ہوجائے اور میری زبان نہ چلے پس ہارون کو پیغام دے'),
(2946, 26, 14, 'اور میرے ذمہ ان کا ایک گناہ بھی ہے سو میں ڈرتا ہوں کہ مجھے مار نہ ڈالیں'),
(2947, 26, 15, 'فرمایا ہر گز نہیں تم دونوں ہماری نشانیاں لے کر جاؤ ہم تمہارے ساتھ سننے والے ہیں'),
(2948, 26, 16, 'سو فرعون کے پاس جاؤ اور کہو کہ ہم پروردگار عالم کا پیغام لے کر آئے ہیں'),
(2949, 26, 17, 'یہ کہ ہمارے ساتھ بنی اسرائیل کو بھیج دے'),
(2950, 26, 18, 'کہا کیا ہم نےتمہیں بچپن میں پرورش نہیں کیا اورتو نے ہم میں اپنی عمر کے کئی سال گزارے'),
(2951, 26, 19, 'اور تو اپنا وہ کرتوت کر گیا جوکر گیا اور تو ناشکروں میں سے ہے'),
(2952, 26, 20, 'کہا جب میں نے وہ کام کیا تھاتو میں بےخبر تھا'),
(2953, 26, 21, 'پھر میں تم سے تمہارے ڈر کے مارے بھاگ گیا تب مجھے میرے رب نے دانائی عطا کی اور مجھے رسول بنایا'),
(2954, 26, 22, 'اور یہ احسان جو تو مجھ پر رکھتا ہے اسی لیے تو نے بنی اسرائیل کو غلام بنا رکھا ہے'),
(2955, 26, 23, 'فرعون نے کہا رب العالمین کیا چیز ہے'),
(2956, 26, 24, 'فرمایا آسمانوں اور زمین اور جو کچھ ان کے درمیان ہے سب کا پروردگار ہے اگر تمہیں یقین آئے'),
(2957, 26, 25, 'اپنے گرد والوں سے کہا کیا تم سنتے نہیں ہو'),
(2958, 26, 26, 'فرمایا تمہارا ور تمہارے پہلے باپ دادا کا رب ہے'),
(2959, 26, 27, 'کہا بے شک تمہارا رسول جو تمہارے پاس بھیجا گیا ہے ضرور دیوانہ ہے'),
(2960, 26, 28, 'فرمایا مشر ق مغرب اور جو ان کے درمیان ہے سب کا پروردگار ہے اگر تم عقل رکھتے ہو'),
(2961, 26, 29, 'کہا اگر تو نے میرے سوا اور کوئی معبود بنایا تو تمہیں قید میں ڈال دوں گا'),
(2962, 26, 30, 'فرمایا اگرچہ میں تیرے پاس ایک روشن چیز لے آؤں'),
(2963, 26, 31, 'کہا اگر تو سچا ہے تو وہ چیز لا'),
(2964, 26, 32, 'پھر اس نےاپناعصا ڈال دیا سو اسی وقت وہ صریح اژدھا ہو گیا'),
(2965, 26, 33, 'اوراپنا ہاتھ نکالا سو اسی وقت وہ دیکھنے والوں کو چمکتا ہوا دکھائی دیا'),
(2966, 26, 34, 'اپنے گرد کے سرداروں سے کہا کہ بے شک یہ بڑا ماہر جادوگر ہے'),
(2967, 26, 35, 'چاہتا ہے کہ تمہیں تمہارے دیس سے اپنے جادو کے زور سےنکال دےپھر تم کیا رائے دیتے ہو'),
(2968, 26, 36, 'کہا اسےاور اس کے بھائی کو مہلت دو اور شہروں میں چپڑاسیوں کو بھیج دیجیئے'),
(2969, 26, 37, 'کہ تیرے پاس بڑے ماہر جادوگروں کو لے آئیں'),
(2970, 26, 38, 'پھر سب جادوگر ایک مقرر دن پر جمع کیے گئے'),
(2971, 26, 39, 'اور لوگوں سے کہا گیا کیا تم بھی اکھٹے ہوتے ہو'),
(2972, 26, 40, 'تاکہ اگر جادوگر غالب آجائیں تو ہم انہی کی راہ پر رہیں'),
(2973, 26, 41, 'پھر جب جادوگر آئے تو فرعون سے کہا اگر ہم غالب آگئے تو کیا ہمیں کوئی بڑا انعام ملے گا'),
(2974, 26, 42, 'کہا ہاں اور بیشک تم اس وقت مقربوں میں داخل ہو جاؤ گے'),
(2975, 26, 43, 'موسیٰ نے ان سے کہا ڈالو جو تم ڈالتے ہو'),
(2976, 26, 44, 'پھر انہوں نے اپنی رسیاں اور لاٹھیاں ڈال دیں اور کہا فرعون کے اقبال سے ہماری فتح ہے'),
(2977, 26, 45, 'پھر موسیٰ نے اپنا عصا ڈالا پھر وہ فوراً ہی نگلنے لگا جو انہوں نے جھوٹ بنایا تھا'),
(2978, 26, 46, 'پھر جادوگر سجدے میں گر پڑے'),
(2979, 26, 47, 'کہا ہم رب العالمین پر ایمان لائے'),
(2980, 26, 48, 'جو موسیٰ اور ہارون کا رب ہے'),
