(3116, 26, 184, 'اور اس سے ڈرو جس نے تمہیں اور پہلی خلقت کو بنایا'),
(3117, 26, 185, 'کہنے لگے کہ تم پر تو کسی نے جادو کر دیا'),
(3118, 26, 186, 'اورتو بھی ہم جیسا ایک آدمی ہے اور ہمارے خیال میں تو تو جھوٹا ہے'),
(3119, 26, 187, 'سو ہم پر آسمان کا کوئی ٹکڑا گرا دے اگر تو سچا ہے'),
(3120, 26, 188, 'کہا میرا رب خوب جانتا ہے جو کچھ تم کرتے ہو'),
(3121, 26, 189, 'پھر اسے جھٹلایا پھر انہیں سائبان والے دن کے عذاب نے پکڑ لیا بے شک وہ بڑے دن کا عذاب تھا'),
(3122, 26, 190, 'البتہ اس میں بڑی نشانی ہے اور ان میں سے اکثر ایمان لانے والے نہیں'),
(3123, 26, 191, 'اور بے شک تیرا رب زبردست رحم کرنے والا ہے'),
(3124, 26, 192, 'اور یہ قرآن رب العالمین کا اتارا ہوا ہے'),
(3125, 26, 193, 'اسے امانت دار فرشتہ لے کر آیا ہے'),
(3126, 26, 194, 'تیرے دل پر تاکہ تو ڈرانے والوں میں سے ہو'),
(3127, 26, 195, 'صاف عربی زبان میں'),
(3128, 26, 196, 'اور البتہ اس کی خبر پہلوں کی کتابوں میں بھی ہے'),
(3129, 26, 197, 'کیا ان کے لیے نشانی کافی نہیں کہ اسے بنی اسرائیل کے علماء بھی جانتے ہیں'),
(3130, 26, 198, 'اور اگر ہم اسے کسی عجمی پر نازل کرتے'),
(3131, 26, 199, 'پھر وہ اسے ان کے سامنے پڑھتا تو بھی ایمان نہ لاتے'),
(3132, 26, 200, 'اسی طرح ہم نے اس انکار کو گناہگاروں کے دل میں ڈال رکھا ہے'),
(3133, 26, 201, 'وہ دردناک عذاب دیکھے بغیر اس پر ایمان نہیں لائیں گے'),
(3134, 26, 202, 'پھر وہ ان پر اچاناک آئے گا اور انہیں خبر بھی نہ ہوگی'),
(3135, 26, 203, 'پھر کہیں گے کیا ہمیں مہلت مل سکتی ہے'),
(3136, 26, 204, 'کیا ہمارے عذاب کو جلد چاہتے ہیں'),
(3137, 26, 205, 'بھلا دیکھ اگر ہم انہیں چند سال فائدہ اٹھانے دیں'),
(3138, 26, 206, 'پھر ان کے پاس وہ عذاب آئے جس کا وعدہ دیے جاتے ہیں'),
(3139, 26, 207, 'تو جو انہوں نے فائدہ اٹھایا ہے کیا ان کے کچھ کام بھی آئے گا'),
(3140, 26, 208, 'اور ہم نے ایسی کوئی بستی ہلاک نہیں کی جس کے لیے ڈرانے والے نہ آئے ہوں'),
(3141, 26, 209, 'نصیحت دینے کے لیے اور ہم ظالم نہیں تھے'),
(3142, 26, 210, 'اور قرآن کو شیطان لے کر نہیں نازل ہوئے'),
(3143, 26, 211, 'اور نہ یہ ان کا کام ہے اور نہ وہ اسے کر سکتے ہیں'),
(3144, 26, 212, 'وہ تو سننے کی جگہ سے بھی دور کر دیے گئے ہیں'),
(3145, 26, 213, 'سو الله کے ساتھ کسی اور معبود کو نہ پکار ورنہ تو بھی عذاب میں مبتلا ہو جائے گا'),
(3146, 26, 214, 'اور اپنے قریب کے رشتہ داروں کو ڈرا'),
(3147, 26, 215, 'اور جو ایمان لانے والے تیرے ساتھ ہیں ان کے لیے اپنا بازو جھکا ئے رکھ'),
(3148, 26, 216, 'پھر اگر تیری نافرمانی کریں تو کہہ دے میں تمہارے کام سے بیزار ہوں'),
(3149, 26, 217, 'اور زبردست رحم والے پر بھروسہ کر'),
(3150, 26, 218, 'جو تجھے دیکھتا ہے جب تو اٹھتا ہے'),
(3151, 26, 219, 'اور نمازیوں میں تیری نشست و برخاست دیکھتا ہے'),
(3152, 26, 220, 'بیشک وہ سننے والا جاننے والا ہے'),
(3153, 26, 221, 'کیا میں تمہیں بتاؤں شیطان کس پر اترتے ہیں'),
(3154, 26, 222, 'ہر جھوٹے گناہگار پر اترتے ہیں'),
(3155, 26, 223, 'وہ سنی ہوئی باتیں پہنچاتے ہیں اوراکثر ان میں سے جھوٹے ہوتے ہیں'),
(3156, 26, 224, 'اور شاعروں کی پیروی تو گمراہ ہی کرتے ہیں'),
(3157, 26, 225, 'کیا تم نےنہیں دیکھاکہ وہ ہر میدان میں بھٹکتے پھرتے ہیں'),
(3158, 26, 226, 'اور جو وہ کہتے ہیں کرتے نہیں'),
(3159, 26, 227, 'مگروہ جو ایمان لائے اور نیک کام کیے اور الله کوبہت یاد کیا اور مظلوم ہونے کے بعد بدلہ لیا اور ظالموں کو ابھی معلوم ہو جائے گا کہ کس کروٹ پر پڑتے ہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طسۤ یہ آیتیں قرآن کی اور کتاب روشن کی ہیں'),
(3161, 27, 2, 'ایمان داورں کے لیے ہدایت اور خوشخبری ہے'),
(3162, 27, 3, 'جو نماز ادا کرتے ہیں اور زکوةٰ دیتے ہیں اور وہ آخرت پر یقین رکھتے ہیں'),
(3163, 27, 4, 'البتہ جو لوگ آخرت پر ایمان نہیں رکھتے ہم نےان کے اعمال ان کے لیے اچھے کر دکھائے ہیں پس وہ سرگرداں پھرتے ہیں'),
(3164, 27, 5, 'وہی ہیں جنہیں برا عذاب ہونا ہے اور وہ آخرت میں بڑے ہی خسارہ میں ہوں گے'),
(3165, 27, 6, 'اور تجھے بڑے حکمت والے علم والے کی طرف سے قرآن دیاجارہا ہے'),
(3166, 27, 7, 'جب موسیٰ نے اپنے گھر والوں سے کہا کہ میں نے آگ دیکھی ہے میں ابھی وہاں سے تمہارے پاس کوئی خبر لاتا ہوں یا کوئی انگارا سلگا کر لاتا ہوں تاکہ تم سینکو'),
(3167, 27, 8, 'پھر جب موسیٰ وہاں آئے تو آواز آئی کہ جو آگ میں اور اس کے پاس ہے وہ با برکت ہے اور الله پاک ہے جو تمام جہان کا رب ہے'),
(3168, 27, 9, 'اے موسیٰ وہ میں الله ہوں زبردست حکمت والا'),
(3169, 27, 10, 'اور اپنی لاٹھی ڈال دے پھر جب اسے دیکھا کہ وہ سانپ کی طرح چل رہی ہے تو پیٹھ پھیر کر بھاگا اورپیچھے مڑ کر نہ دیکھا اے موسیٰ ڈرو مت کیونکہ میرے حضور میں رسول ڈرا نہیں کرتے'),
(3170, 27, 11, 'مگر جس نے ظلم کیا پھر برائی کے بعد اسے نیکی سے بدل دیا ہوتو میں بخشنے والا مہربان ہوں'),
(3171, 27, 12, 'اور اپنا ہاتھ اپنےگریبان میں ڈال دے وہ سفید بے عیب نکلے گا یہ دونوں ملا کر نونشانیاں فرعون اور اس کی قوم کی طرف لے کرجا کیونکہ وہ بدکار لوگ ہیں'),
(3172, 27, 13, 'پھر جب ان کے پاس آنکھیں کھولنے والی ہماری نشانیاں آئیں تو کہنے لگے یہ تو صاف جادو ہے'),
(3173, 27, 14, 'اور انہوں نے انکا ظلم اور تکبر سے انکار کرد یا حالانکہ ان کے دل یقین کر چکے تھے پھر دیکھ مفسدوں کا انجام کیسا ہوا'),
(3174, 27, 15, 'اور ہم نے داؤد اور سلیمان کو علم دیا اور کہنے لگے الله کا شکر ہے جس نے ہمیں بہت سے ایمان دار بندوں پر فضیلت دی'),
(3175, 27, 16, 'اور سلیمان داؤد کا وارث ہوا اور کہا اے لوگو ہمیں پرندوں کی بولی سکھائی گئی ہے او رہمیں ہر قسم کے سازو سامان دیے گئے ہیں بے شک یہ صریح فضیلت ہے'),
(3176, 27, 17, 'اور سلیمان کے پاس اس کے لشکر جن اور انسان اور پرندے جمع کیے جاتے پھر انکی جماعتیں بنائی جاتیں'),
(3177, 27, 18, 'یہاں تک کہ جب چیونٹیوں کے میدان پر پہنچے ایک چینوٹی نے کہا اے چیونٹیو اپنے گھروں میں گھس جاؤ تمہیں سلیمان اور اس کی فوجیں نہ پیس ڈالیں اور انہیں خبر بھی نہ ہو'),
(3178, 27, 19, 'پھر اس کی بات سے مسکرا کر ہنس پڑا اور کہا اے میرے رب مجھے توفیق دے کہ میں تیرے احسان کا شکر کروں جو تو نے مجھ پر اور میرے ماں باپ پر کیا اور یہ کہ میں نیک کام کروں جوتو پسند کرے اور مجھے اپنی رحمت سے اپنے نیک بندوں میں شامل کر لے'),
(3179, 27, 20, 'اور پرندوں کی حاضری لی تو کہا کیا بات ہے جو میں ہُد ہُد کو نہیں دیکھتا کیا وہ غیر حاضر ہے'),
(3180, 27, 21, 'میں اسے سخت سزا دوں گا یا اسے ذبح کر دوں گا یا وہ میرے پاس کوئی صاف دلیل بیان کرے'),
(3181, 27, 22, 'پھر تھوڑی دیر کے بعد ہُد ہُد حاضر ہوا اور کہا کہ میں حضور کے پاس وہ خبر لایا ہوں جو حضور کو معلوم نہیں اور سباسے آپ کے پاس ایک یقینی خبر لایا ہوں'),
(3182, 27, 23, 'میں نے ایک عورت کو پایا جو ان پر بادشاہی کرتی ہے اور اسے ہر چیز دی گئی ہے اور اس کا بڑا تخت ہے'),
(3183, 27, 24, 'میں نے پایا کہ وہ اور اس کی قوم الله کے سوا سورج کو سجدہ کرتے ہیں اور شیطان نے ان کے اعمال کو انہیں آرستہ کر دکھایا ہے اور انہیں راستہ سے روک دیا ہے سو وہ راہ پر نہیں چلتے'),
(3184, 27, 25, 'الله ہی کو کیوں نہ سجدہ کریں جو آسمانوں اور زمین کی چھپی ہوئی چیزوں کو ظاہر کرتا ہے اور جو تم چھپاتے ہو اورجو ظاہر کرتے ہو سب کو جانتا ہے'),
(3185, 27, 26, 'الله ہی ایسا ہےکہ اس کے سواکوئی معبود نہیں ہے اوروہ عرش عظیم کا مالک ہے'),
(3186, 27, 27, 'کہا ہم ابھی دیکھ لیتے ہیں کہ تو سچ کہتا ہے یا جھوٹوں میں سے ہے'),
(3187, 27, 28, 'میرا یہ خط لے جا اور ان کی طرف ڈال دے پھر ان کے ہاں سے واپس آ جا پھر دیکھ وہ کیا جواب دیتے ہیں'),
(3188, 27, 29, 'کہنے لگی اے دربار والو میرے پاس ایک معزز خط ڈالا گیا ہے'),
(3189, 27, 30, 'وہ خط سلیمان کی طرف سے ہے اور وہ یہ ہے الله کے نام سے شروع کرتا ہوں جو بے حد مہربان نہایت رحم والا ہے'),
(3190, 27, 31, 'میرے سامنے تکبر نہ کرو اور میرے پاس مطیع ہو کر چلی آؤ'),
(3191, 27, 32, 'کہنے لگی اے دربار والو مجھے میرے کام میں مشورہ دو میں کوئی بات تمہارے حاضر ہوئے بغیر طے نہیں کر تی'),
(3192, 27, 33, 'کہنے لگے ہم بڑے طاقتور اور بڑے لڑنے والے ہیں اور کام تیرے اختیار میں ہے سو دیکھ لے جو حکم دینا ہے'),
(3193, 27, 34, 'کہنے لگی بادشاہ جب کسی بستی میں داخل ہوتے ہیں اسے خراب کر دیتے ہیں اور وہاں کے سرداروں کو بے عزت کرتے ہیں اور ایسا ہی کریں گے'),
(3194, 27, 35, 'اور میں ان کی طرف کچھ تحفہ بھیجتی ہوں پھر دیکھتی ہوں کہ ایلچی کیا جواب لے کر آتے ہیں'),
(3195, 27, 36, 'پھر جب سلیمان کے پاس آیا فرمایا کیا تم میری مال سے مدد کرنا چاہتے ہو سو جو کچھ مجھے الله نے دیا ہے اس سے بہتر ہے جو تمہیں دیا ہے بلکہ تم ہی اپنے تحفہ سے خوش رہو'),
(3196, 27, 37, 'ان کی طرف واپس جاؤ ہم ان پرا یسے لشکر لے کر پہنچیں گے جن کا وہ مقابلہ نہ کر سکیں گے اور ہم انہیں وہاں سے ذلیل کر کے نکال دیں گے'),
(3197, 27, 38, 'کہا اے دربار والو تم میں کوئی ہے کہ میرے پاس اس کا تخت لے آئے اس سے پہلے کہ وہ میرے پاس فرمانبردار ہو کر آئيں'),
(3198, 27, 39, 'جنوں میں سے ایک دیو نے کہا میں تمہیں وہ لا دیتا ہوں اس سے پہلے کہ تو اپنی جگہ سے اٹھے اور میں اس کے لیے طاقتور امانت دار ہوں'),
(3199, 27, 40, 'اس شخص نے کہا جس کے پاس کتاب کا علم تھا میں اسے تیری آنکھ جھپکنے سے پہلے لا دیتا ہوں پھر جب اسے اپنے روبرو رکھا دیکھا تو کہنے لگا یہ میرے رب کا ایک فضل ہے تاکہ میری آزمائش کرے کیا میں شکر کرتا ہوں یا ناشکری اور جو شخص شکر کرتا ہے اپنے ہی نفع کے لیے شکر کرتا ہے اورجو ناشکری کرتا ہے تو میرا رب بھی بے پرواہ عزت والا ہے'),
(3200, 27, 41, 'کہا اس کے لیے اس کے تخت کی صورت بدل دو ہم دیکھیں کیا اسے پتہ لگتا ہے یا انہیں میں ہوتی ہے جنہیں پتہ نہیں لگتا'),
(3201, 27, 42, 'پھر جب آئی کہا گیا کیا تیرا تخت ایسا ہی ہے کہنے لگی گویاکہ یہ وہی ہے اور ہمیں تو پہلے ہی معلوم ہو گیا تھا اور ہم فرمانبردار ہو چکے ہیں'),
(3202, 27, 43, 'اور اسے ان چیزو ں سے روک دیا جنہیں الله کےسوا پوجتی تھی بے شک وہ کافروں میں سے تھی'),
(3203, 27, 44, 'اسے کہا گیا اس محل میں داخل ہو پھر جب اسے دیکھا خیال کیا کہ وہ گہرا پانی ہے اور اپنی پنڈلیاں کھولیں کہا یہ تو ایسا محل ہے جس میں شیشے جڑے ہوئے ہیں کہنے لگی اے میرے رب میں نے اپنے نفس پر ظلم کیا تھا اور میں سلیمان کے ساتھ الله کی فرمانبردار ہوئی جو سارے جہاں کا رب ہے'),
(3204, 27, 45, 'اور ہم نے ثمود کی طرف ان کے بھائی صالح کو بھیجاکہ الله کی بندگی کرو پھر وہ دو فریق ہو کر آپس میں جھگڑنے لگے'),
(3205, 27, 46, 'کہا اے میری قوم بھلائی سے پہلے برائی کو کیوں جلدی مانگتے ہو الله سے گناہ کیوں نہیں بخشواتے تاکہ تم پر رحم کیا جائے'),
(3206, 27, 47, 'کہنے لگے ہمیں تو تجھ سے اور تیرے ساتھیوں سے نحوست معلوم ہوئی کہا تہاری نحوست الله کی طرف سے ہے بلکہ تم لوگ آزمائش میں ڈالے گئے ہو'),
(3207, 27, 48, 'اور اس شہر میں نو آدمی ایسے تھے جو زمین میں فساد کرتے تھے اوراصلاح نہیں کرتے تھے'),
(3208, 27, 49, 'کہنے لگے آپس میں الله کی قسم کھاؤ کہ صالح اور اس کے گھر والوں پر شبخوں ماریں پھر اس کے وارث سے کہہ دیں ہم تو اس کے کنبہ کی ہلاکت کے وقت موجود ہی نہ تھے اور بے شک ہم سچے ہیں'),
(3209, 27, 50, 'اور انہوں نے ایک داؤ کیا اور ہم نے بھی ایساداؤ کیا کہ انہیں خبربھی نہ ہوئی'),
(3210, 27, 51, 'پھر دیکھو ان کے داؤ کا کیا انجام ہوا کہ ہم نے انہیں اوران کی ساری قوم کو ہلاک کر دیا'),
(3211, 27, 52, 'سو یہ ان کے گھر ہیں جو ان کے ظلم کے سبب سے ویران پڑے ہیں بے شک اس میں دانشمندو ں کے لیے عبرت ہے'),
(3212, 27, 53, 'اور ہم نے ایمان اور تقویٰ والوں کو نجات دی'),
(3213, 27, 54, 'اورلوط کو جب اس نے اپنی قوم کو کہا کیا تم بے حیائی کرتے ہو حالانکہ تم سمجھ دا ر ہو'),
(3214, 27, 55, 'کیا تم عورتوں کو چھوڑ کر مردوں پر خواہش کر کے آتے ہو بلکہ تم لوگ جاہل ہو'),
(3215, 27, 56, 'پھر اس کی قوم کا اور کوئی جواب نہ تھا سوائے ا س کے کہ یہ کہا لوط کے گھر والوں کو اپنی بستی سے نکال دو یہ لوگ بڑے پاک بنتے ہیں'),
(3216, 27, 57, 'پھر ہم نے لوط اور اس کے گھر والوں کو سوائے اس کی بیوی کے بچا لیا ہم اس کو پیچھے رہنے والو ں میں ٹھہرا چکے تھے'),
(3217, 27, 58, 'اورہم نے ان پر مینہ برسایا پھر ڈرائے ہوؤں کا مینہ برا تھا'),
(3218, 27, 59, 'کہہ دے سب تعریف الله کے لیے ہے اور اس کے برگزیدہ بندوں پر سلام ہے بھلا الله بہتر ہے یا جنہیں وہ شریک بناتے ہیں'),
(3219, 27, 60, 'بھلا کس نے آسمان اور زمین بنائے اور تمہارے لیے آسمان سے پانی اتار پھر ہم نے اس سے رونق والے باغ اگائے تمہارا کام نہ تھا کہ ان کے درخت اگاتے کیا الله تعالیٰ کے ساتھ کوئی اوربھی معبود ہے بلکہ یہ لوگ کجروی کر رہے ہیں'),
(3220, 27, 61, 'بھلا زمین کو ٹھہرنے کی جگہ کس نے بنایا اور اس میں ندیاں جاری کیں اور زمین کے لنگر بنائے اور دو دریاؤں میں پردہ رکھا کیا الله کے ساتھ کوئی اوربھی معبود ہے بلکہ اکثر ان میں بے سمجھ ہیں'),
(3221, 27, 62, 'بھلا کون ہے جوبے قرار کی دعا قبول کرتا ہے اوربرائی کو دور کرتا ہے اور تمہیں زمین میں نائب بناتا ہے کیا الله کے ساتھ کوئی اور معبود بھی ہے تم بہت ہی کم سمجھتے ہو'),
(3222, 27, 63, 'بھلا کون ہے جو تمہیں جنگل اور دریا کے اندھیروں میں راستہ بتاتاہے اور اپنی رحمت سے پہلے کون خوشخبری کی ہوائیں چلاتا ہے کیا الله کے ساتھ کوئی اور معبود ہے الله ان کے شرک کرنے سے بہت بلند ہے'),
(3223, 27, 64, 'بھلا کون ہے جو از سرِ نو خلقت کو پیدا کرتا ہے پھر اسے دوبارہ بنائے گا اور کون ہے وہ جو تمہیں آسمان اور زمین سے روزی دیتا ہے کیا الله کے ساتھ کوئی اور بھی معبود ہے کہہ دے اپنی دلیل لاؤ اگر تم سچےہو'),
(3224, 27, 65, 'کہہ دے الله کے سوا آسمانوں اور زمین میں کوئی بھی غیب کی بات نہیں جانتا اور انہیں اس کی بھی خبر نہیں کہ کب اٹھائے جائیں گے'),
(3225, 27, 66, 'بلکہ آخرت کے معاملے میں تو ان کی سمجھ گئی گزری ہے بلکہ وہ اس سے شک میں ہیں بلکہ وہ اس سے اندھے ہی ہیں'),
(3226, 27, 67, 'اور کافر کہتے ہیں کہ کیا جب ہم اور ہمارے باپ دادا مٹی ہوگئے کیا ہم زمین سے نکالے جائیں گے'),
(3227, 27, 68, 'اس کا تو ہم سے اور ہمارےپہلے باپ دادا سے وعدہ ہوتا آیا ہے یہ تو صرف پہلوں کی کہانیاں ہیں'),
(3228, 27, 69, 'کہہ دے تم زمین میں چل پھر کر دیکھو کہ گناہگاروں کا کیا انجام ہوا'),
(3229, 27, 70, 'اور ان پر غم نہ کر اور ان کے فریب کرنے سے تنگ دل نہ ہو'),
(3230, 27, 71, 'اور کہتے ہیں یہ وعدہ کب ہوگا اگر تم سچے ہو'),
(3231, 27, 72, 'کہہ دے شاید بعض وہ چیزیں جن کی تم جلدی کرتے ہو تمہاری پیٹھ کے پیچھے آلگی ہوں'),
(3232, 27, 73, 'اور بے شک تیرا رب تو لوگوں پر فضل کرتا ہے لیکن ان میں سے اکثر شکر نہیں کرتے'),
(3233, 27, 74, 'اور بے شک تیرا رب جانتا ہے جو ان کے دلوں میں پوشیدہ ہے اور جو وہ ظاہر کرتے ہیں'),
(3234, 27, 75, 'اور آسمان اور زمین میں ایسی کوئی پوشیدہ بات نہیں جو روشن کتاب میں نہ ہو'),
(3235, 27, 76, 'بے شک یہ قرآن بنی اسرائیل پر اکثر ان باتوں کو ظاہر کرتا ہے جن میں وہ اختلاف کرتے ہیں'),
(3236, 27, 77, 'اور بے شک وہ ایمانداروں کے لیے ہدایت اور رحمت ہے'),
(3237, 27, 78, 'بے شک تیرا رب ان کے درمیان اپنے حکم سے فیصلہ کرے گا اور وہ غالب علم والا ہے'),
(3238, 27, 79, 'سو الله پر بھروسہ کر بے شک تو صریح حق پر ہے'),
(3239, 27, 80, 'البتہ تو مردوں کو نہیں سنا سکتا اور نہ بہروں کو اپنی پکارسنا سکتا ہے جب وہ پیٹھ پھیر کر لوٹیں'),
(3240, 27, 81, 'اور نہ تو اندھوں کو ان کی گمراہی دور کر کے ہدایت کر سکتا ہے تو ان ہی کو سنا سکتا ہے جو ہماری آیتوں پر ایمان لائیں سو وہی مان بھی لیتے ہیں'),
(3241, 27, 82, 'اور جب ان پر وعدہ پورا ہوگا تو ہم ان کے لیے زمین سے ایک جانور نکالیں گے جو ان سے باتیں کرے گا کہ لوگ ہماری آیتوں پر یقین نہیں لاتے تھے'),
(3242, 27, 83, 'اورجس دن ہم ہر امت میں سے ایک گروہ ان لوگوں کا جمع کریں گے جو ہماری آیتوں کو جھٹلاتے تھے پھر ان کی جماعت بندی ہو گی'),
(3243, 27, 84, 'یہاں تک کہ جب سب حاضر ہوں گے کہے گا کیا تم نے میری آیتوں کو جھٹلایا تھا حالانکہ تم انہیں سمجھے بھی نہ تھے یا کیا کرتے رہے ہو'),
(3244, 27, 85, 'اور ان کے ظلم سے ان پر الزام قائم ہو جائے گا پھر وہ بول بھی نہ سکیں گے'),
(3245, 27, 86, 'کیا نہیں دیکھتے کہ ہم نے رات بنائی تاکہ اس میں چین حاصل کریں اور دیکھنے کو دن بنایا البتہ اس میں ان لوگوں کے لیے نشانیاں ہیں جو ایمان لاتے ہیں'),
(3246, 27, 87, 'اور جس دن صور پھونکا جائے گا تو جوکوئی آسمان میں ہے اور جو کوئی زمین میں ہے سب ہی گھبرائیں گے مگر جسے الله چاہے اور سب اس کے پاس عاجز ہو کر چلے آئیں گے'),
(3247, 27, 88, 'اور تو جو پہاڑوں کو جمے ہوئے دیکھ رہا ہے یہ تو بادلوں کی طرح اڑتے پھریں گے اس الله کی کاریگری سے جس نے ہر چیز کو مضبوط بنا رکھا ہے اسے خبر ہے جو تم کرتے ہو'),
(3248, 27, 89, 'جو نیکی لائے گا سواسے اس سے بہتر بدلہ ملے گا اور وہ اس دن کی گھبراہٹ سے بھی امن میں ہوں گے'),
(3249, 27, 90, 'اور جو برائی لائے گا سو ان کے منہ آگ میں اوندھے ڈالے جائیں گے تمہیں وہی بدلہ مل رہا ہے جو تم کرتے تھے'),
(3250, 27, 91, 'مجھے تو یہی حکم دیا گیا ہے کہ اس شہر کے مالک کی بندگی کروں جس نے اسے عزت دی ہے اور ہر ایک چیز اسی کی ہے اور مجھے حکم دیا گیا ہے کہ میں فرمانبرداروں میں رہوں'),
(3251, 27, 92, 'اور یہ بھی کہ قرآن سنا دوں پھر جو کوئی راہ پر آ گیا تو وہ اپنے بھلے کو راہ پر آتا ہے اور جو گمراہ ہوا تو کہہ دو میں تو صرف ڈرانے والوں میں سے ہوں'),
(3252, 27, 93, 'اور کہہ دو سب تعریف الله کے لیے ہےتمہیں عنقریب اپنی نشانیاں دکھا دے گا پھرانہیں پہچان لو گے اور تیرا رب اس سے بے خبر نہیں جو تم کرتے ہو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طسمۤ'),
(3254, 28, 2, 'یہ روشن کتاب کی آیتیں ہیں'),
(3255, 28, 3, 'ہم تجھے ایمان داروں کے فائدے کے لیے موسیٰ اور فرعون کا کچھ صحیح حال سناتے ہیں'),
(3256, 28, 4, 'بے شک فرعون زمین پر سرکش ہو گیا تھا اور وہاں کے لوگوں کے کئی گروہ کر دیئے تھے ان میں سے ایک گروہ کو کمزور کر رکھا تھا ان کے لڑکوں کو قتل کرتا تھا اور ان کی لڑکیوں کو زندہ رکھتا تھا بے شک وہ مفسدوں میں سے تھا'),
(3257, 28, 5, 'اور ہم چاہتے تھے کہ ان پر احسان کریں جو ملک میں کمزور کیے گئے تھے اورانہیں سردار بنا دیں اور انہیں وارث کریں'),
(3258, 28, 6, 'اور انہیں ملک پر قابض کریں اور فرعون ہامان اور ان کے لشکروں کو وہ چیز دکھا دیں جس کا وہ خطر کرتے تھے'),
(3259, 28, 7, 'اور ہم نے موسیٰ کی ماں کو حکم بھیجا کہ اسے دودھ پلا پھر جب تجھے اس کا خوف ہو تو اسے دریا میں ڈال دے اور کچھ خوف اور غم نہ کر بے شک ہم اسے تیرے پاس واپس پہنچا دیں گے اور اسے رسولوں میں سے بنانے والے ہیں'),
(3260, 28, 8, 'پھر اسے فرعون کے گھر والو ں نے اٹھا لیا تاکہ بالآخر وہ ان کا دشمن اور غم کا باعث بنے بے شک فرعون اور ہامان اور ان کے لشکر خطا کار تھے'),
(3261, 28, 9, 'اور فرعون کی عورت نے کہا یہ تو میرے اور تیرے لیے آنکھوں کی ٹھنڈک ہے اسے قتل نہ کرو شاید ہمارے کام آئے یا ہم اسے بیٹا بنالیں اور انہیں کچھ خبر نہ تھی'),
(3262, 28, 10, 'اور صبح کو موسی ٰکی ماں کا دل بے قرار ہو گیا قریب تھی کہ بے قراری ظاہر کر دے اگر ہم اس کے دل کو صبر نہ دیتے تاکہ اسے ہمارے وعدے کا یقین رہے'),
(3263, 28, 11, 'اور اس کی بہن سے کہا اس کے پیچھے چلی جا پھر اسے اجنبی ہو کر دیکھتی رہی اور انہیں خبر نہ ہوئی'),
(3264, 28, 12, 'اور ہم نے پہلے سے اس پر دائیوں کا دودھ حرام کر دیا تھا پھر بولی میں تمہیں ایسے گھر والے بتاؤں جو اس کی تمہارے لیے پرورش کریں اور وہ اس کے خیر خواہ ہوں'),
(3265, 28, 13, 'پھر ہم نے اسے اس کی ماں کے پا س پہنچا دیا تاکہ ا س کی آنکھیں ٹھنڈی رہیں اور غمگین نہ ہو اور جان لے کہ الله کا وعدہ سچا ہے لیکن اکثر آدمی نہیں جانتے'),
(3266, 28, 14, 'اور جب اپنی جوانی کو پہنچا اور پورا توانا ہوا تو ہم نے اسے حکمت اور علم دیا اور ہم نیکوں کو اسی طرح بدلہ دیا کرتے ہیں'),
(3267, 28, 15, 'اور شہر میں لوگوں کی بے خبری کے وقت داخل ہوا پھر وہاں دو شخصوں کو لڑتے ہوئے پایا یہ ایک اس کی جماعت کا تھااوریہ دوسرا اس کے دشمنوں میں سے تھا پھر اس نے جو اس کی جماعت کا تھا اپنے دشمن پر اس سے مدد چاہی تب موسیٰ نے اس پر مکا مارا پس اس کا کام تمام کر دیا کہا یہ تو شیطانی حرکت ہے بے شک وہ کھلا دشمن اور گمراہ کرنے والا ہے'),
(3268, 28, 16, 'کہا اے میرے رب! بے شک میں نے اپنی جان پر ظلم کیا سو مجھے بخش دے پھر اسے بخش دیا بے شک وہ بخشنے والا مہربان ہے'),
(3269, 28, 17, 'کہا اے میرے رب! جیسا تو نے مجھ پر فضل کیا ہے پھر میں گناہگاروں کا کبھی مددگار نہیں ہوں گا'),
(3270, 28, 18, 'پھر شہر میں ڈرتا انتظار کرتا ہوا صبح کو گیا پھر وہی شخص جس نے کل اس سے مدد مانگی تھی اسے پکار رہا ہے موسیٰ نے اس سے کہا کہ بے شک تو صریح گمراہ ہے'),
(3271, 28, 19, 'پھر جب ارادہ کیا کہ اس پر ہاتھ ڈالے جو ان دونوں کا دشمن تھا کہا اے موسیٰ! کیا تو چاہتا ہے کہ مجھے مار ڈالے جیسا تو نے کل ایک آدمی کو مار ڈالا ہے تو یہی چاہتا ہے کہ ملک میں زبردستی کرتا پھرے اور تو نہیں چاہتا کہ اصلاح کرنے والوں میں سے ہو'),
(3272, 28, 20, 'اور شہر کے پرلے سرے سے ایک آدمی دوڑتا ہوا آیا کہا اے موسیٰ! دریا والے ترے متعلق مشورہ کرتے ہیں کہ تجھ کو مار ڈالیں سو نکل بے شک میں تیرا بھلا چاہنے والا ہوں'),
(3273, 28, 21, 'پھر وہاں سے ڈرتا انتظار کرتا ہوا نکلا کہا اے میرے رب! مجھے ظالم قوم سے بچا لے'),
(3274, 28, 22, 'اور جب مدین کی طرف رخ کیا تو کہا امید ہے کہ میرا رب مجھے سیدھا راستہ بتا دے گا'),
(3275, 28, 23, 'اور جب مدین کے پانی پر پہنچا وہاں لوگوں کی ایک جماعت کو پانی پلاتے ہوئے پایا اور ان سے ورے دو عورتوں کو پایا جو اپنے جانور روکے ہوئے کھڑی تھیں کہا تمہارا کیا حال ہے بولیں جب تک چرواہے نہیں ہٹ جاتے ہم نہیں پلاتیں اور ہمارا باپ بوڑھابڑی عمر کا ہے'),
(3276, 28, 24, 'پھر ان کے جانوروں کو پانی پلا دیا پھر سایہ کی طرف ہٹ کر آیا کہ اے میرے رب تو میری طرف جو اچھی چیز اتارے میں اس کا محتاج ہوں'),
(3277, 28, 25, 'پھر ان دونوں میں سے ایک اس کے پاس شرم سے چلتی ہوئی آئی کہا میرے باپ نے تمہیں بلایا ہے کہ تمہیں پلائی کی اجرت دے پھر جب اس کے پاس پہنچا اور اس کے تمام حال بیان کیا کہا خوف نہ کر تواس بے انصاف قوم سے بچ آیا ہے'),
(3278, 28, 26, 'ان دونوں میں سے ایک بولی اے باپ! اسے نوکر رکھ لے بے شک بہتر نوکر جسے تو رکھنا چاہے وہ ہے جو زور آوار امانت دار ہو'),
(3279, 28, 27, 'کہا میں چاہتاہوں کہ اپنی ان دونوں بیٹیوں میں سے ایک کا تجھ سے نکاح کر دوں اس شرط پر کہ تو آٹھ برس تک میری نوکری کرے پھر اگر تو دس پورے کر دے تو تیری طرف سے احسان ہے او رمیں نہیں چاہتا کہ تجھے تکلیف میں ڈالوں اگر الله نے چاہا تو مجھے نیک بختوں سے پائے گا'),
(3280, 28, 28, 'کہا میرے او رتیرے درمیان یہ وعدہ ہو چکا ان دونو ں مدتوں میں سے جونسی پوری کر دوں تو مجھ پر زیادتی نہ ہو اور الله ہمارے قول پر گواہ ہے'),
(3281, 28, 29, 'پھر جب موسیٰ وہ مدت پوری کر چکا اور اپنے گھر والوں کو لے کر چلا کوہِ طور کی طرف سے ایک آگ دیکھی اپنے گھر والوں سے کہا ٹھیرو میں نے ایک آگ دیکھی ہے شاید تمہارے پاس وہا ں کی کچھ خبر یا آگ کا انگارہ لے آؤں تاکہ تم سینکو'),
(3282, 28, 30, 'پھر جب اس کے پاس پہنچا تو میدان کے داہنے کنارے سے برکت والی جگہ میں ایک درخت سے آواز آئی کہ اے موسیٰ! میں الله جہان کا رب ہوں'),
(3283, 28, 31, 'اور یہ کہ اپنی لاٹھی ڈال دے پھر جب اسے دیکھا کہ سانپ کی طرح لہرا رہا ہے تو منہ پھیر کر الٹا بھاگا اور پیچھے مڑ کر نہ دیکھا اے موسیٰ! سامنے آ اور ڈر نہیں بے شک تو امن والوں سے ہے'),
(3284, 28, 32, 'اپنے گریبان میں اپنا ہاتھ ڈال وہ بغیر کسی عیب کے چمکتا ہوا نکلے گا اور رفع خوف کے لیے اپنا بازو اپنی طرف ملا سو تیرے رب کی طرف سے فرعون اور اس کے سرداروں کے لیے یہ دو سندیں ہیں بے شک وہ نافرمان لوگ ہیں'),
(3285, 28, 33, 'کہا اے میرے رب! میں نے ان کے ایک آدمی کو قتل کیا ہے پس میں ڈرتا ہوں کہ مجھے مارڈ الیں گے'),
(3286, 28, 34, 'اور میرے بھائی ہارون کی زبان مجھ سے زياہ روان ہے اسے میرے ساتھ مددگار بنا کر بھیج کہ میری تصدیق کرےکیونکہ میں ڈرتا ہوں کہ وہ مجھے جھٹلائیں گے'),
(3287, 28, 35, 'فرمایا ہم تیرے بازو کو تیرے بھائی سے مضبوط کر دیں گے اور تمہیں غلبہ دیں گے پھر وہ تم تک پہنچ نہیں سکیں گے ہماری نشانیوں کے سبب سےتم اورتمہارے تابعدار غالب رہیں گے'),
(3288, 28, 36, 'پھر جب موسیٰ ان کے پاس ہماری کھلی نشانیاں لے کر آیا تو کہنے لگے کہ یہ تو محض ایک بنایا ہوا جادو ہے اور ہم نے اسے اپنے پہلے باپ دادا سے سنا ہی نہیں ہے'),
(3289, 28, 37, 'اور موسیٰ نے کہا میرا رب خوب جانتاہے جو اس کی طرف سے ہدایت لے کر آیا ہے اور جس کے لیے آخرت کا گھر ہے بے شک ظالم نجات نہیں پائیں گے'),
(3290, 28, 38, 'اور فرعون نے کہا اے سردارو! میں نہیں جانتا کہ میرے سوا تمہارا او رکوئی معبود ہے پس اے ہامان! تو میرے لیے گارا پکوا پھر میرے لیے ایک بلند محل بنوا کہ میں موسیٰ کے خدا کو جھانکوں اور بے شک میں اسے جھوٹا سمجھتا ہوں'),
(3291, 28, 39, 'اور اس نے اور اس کے لشکروں نے زمین پر ناحق تکبر کیا اور خیال کیا کہ وہ ہماری طرف لوٹ کر نہیں آئيں گے'),
(3292, 28, 40, 'پھر ہم نے اسے اور اس کے لشکروں کو پکڑ لیا پھر انہیں دریا میں پھینک دیا سو دیکھ لو ظالموں کا کیا انجام ہوا'),
(3293, 28, 41, 'اور ہم نے انہیں پیشوا بنایا وہ دوزخ کی طرف بلاتے تھے اور قیامت کے دن انہیں مدد نہیں ملے گی'),
(3294, 28, 42, 'اور ہم نے اس دنیا میں ان کے پیچھے لعنت لگا دی اور وہ قیامت کے دن بھی بدحالوں میں ہوں گے'),
(3295, 28, 43, 'اور ہم نے موسیٰ کو پہلی امتوں کے ہلاک کرنے کے بعد کتاب دی تھی جو لوگوں کے لیے بینائی اور ہدایت اور رحمت تھی تاکہ وہ سمجھیں'),
(3296, 28, 44, 'اور تم غربی جانب نہیں تھے جب ہم نے موسیٰ کی طرف حکم بھیجا اور نہ اس واقعہ کو دیکھنے والے تھے'),
(3297, 28, 45, 'لیکن ہم نے بہت سی نسلیں پیدا کیں پھران پر مدت دراز گزاری اورتو مدین والوں میں نہیں رہتا تھا کہ انہیں ہماری آیتیں سناتا لیکن ہم رسول بھیجتے رہے'),
(3298, 28, 46, 'اور تو طور کے کنارے پر نہ تھا جب ہم نے آواز دی لیکن تیرے رب کا یہ انعام ہے تاکہ ان لوگو ں کو ڈرائے جن کے پاس تجھ سے پہلے کوئی ڈرانے والا نہیں آیا تاکہ وہ نصیحت حاصل کریں'),
(3299, 28, 47, 'اور اگر یہ بات نہ ہوتی کہ ان کے اپنے ہی اعمال کے سبب سے ان پر مصیبت نازل ہوجائے پھر کہتے اے ہمارے رب! تو نے ہمارے پاس رسول کیوں نہ بھیجا تاکہ ہم تیرے حکموں کی تابعداری کرتے اور ایمان والو ں میں ہوتے'),
(3300, 28, 48, 'پھر جب ان کے پاس ہماری طرف سے حق آ پہنچا تو کہنے لگے کیوں نہیں دیا گیا جیسا موسی کو دیا گیا تھا کیا انہوں نے اس چیز کا انکار نہیں کیا تھا جو موسیٰ کو اس سے پہلے دی گئی تھی کہنے لگے دونوں جادو گر آپس میں موافق ہیں اور کہا ہم کسی کو بھی نہیں مانتے'),
(3301, 28, 49, 'کہہ دو پس الله کے ہاں سے کوئی ایسی کتاب لاؤ جو ان دونوں سے ہدایت میں بڑھ کر ہو کہ میں اس پر چلوں اگر تم سچے ہو'),
(3302, 28, 50, 'پھر اگر تمہارا کہنا نہ مانیں تو جان لو کہ وہ صرف اپنی خواہشوں کے تابع ہیں اور اس سے بڑھ کر کون گمراہ ہوگا جو الله کی ہدایت چھوڑ کر اپنی خواہشوں پر چلتا ہو بے شک الله ظالم قوم کو ہدایت نہیں کرتا'),
(3303, 28, 51, 'اور البتہ ہم ان کے پاس ہدایت بھیجتے رہے تاکہ وہ نصیحت حاصل کریں'),
(3304, 28, 52, 'جن لوگوں کو ہم نے اس سے پہلے کتاب دی ہے وہ اس پر ایمان لاتے ہیں'),
(3305, 28, 53, 'اور جب ان پر پڑھا جاتا ہے کہتے ہیں ہم اس پر ایمان لائے ہمارے رب کی طرف سے یہ حق ہے ہم تو اسے پہلے ہی مانتے تھے'),
(3306, 28, 54, 'یہ وہ لوگ ہیں جنہیں ان کے صبر کی وجہ سے دگنا بدلہ ملے گا اوربھلائی سے برائی کو دور کرتے ہیں اور جو ہم نےانہیں دیا ہے اس میں سےخرچ کرتے ہیں'),
(3307, 28, 55, 'اورجب بے ہودہ بات سنتے ہیں تو اس سے منہ پھیر لیتے ہیں اور کہتے ہیں ہمارے لے ہمارے اعمال تمہارے لیے تمہارے اعمال تم پر سلام ہو ہم بے سمجھوں کو نہیں چاہتے'),
(3308, 28, 56, 'بے شک تو ہدایت نہیں کر سکتا جسے تو چاہے لیکن الله ہدایت کرتا ہے جسے چاہے اور وہ ہدایت والوں کو خوب جانتا ہے'),
(3309, 28, 57, 'اور کہتے ہیں اگر ہم تیرے ساتھ ہدایت پر چلیں تو اپنے ملک سے اچک لیے جائیں کیا ہم نے انہیں حرم میں جگہ نہیں دی جوامن کا مقام ہے جہاں ہر قسم کے میووں کا رزق ہماری طرف سے پہنچایا جاتا ہے لیکن اکثر ان میں سے نہیں جانتے'),
(3310, 28, 58, 'اور ہم نے بہت سی بستیوں کو ہلاک کر ڈالا جو اپنے سامان عیش پر نازاں تھے سو یہ ان کے گھر ہیں کہ ان کے بعد آباد نہیں ہوئے مگر بہت کم اورہم ہی وارث ہوئے'),
(3311, 28, 59, 'اور تیرا رب بستیوں کو ہلاک نہیں کیا کرتا جب تک ان کے بڑے شہر میں پیغمبر نہ بھیج لے جو انہیں ہماری آیتیں پڑھ کر سنائے اور ہم بستیوں کو ہلاک نہیں کیا کرتے مگر اس حالت میں کہ وہاں کے باشندے ظالم ہوں'),
(3312, 28, 60, 'اور جو چیز تمہیں دی گئی ہے وہ دنیا کی زندگی کا فائدہ اور اس کی زینت ہے جو چیز الله کے ہاں ہے وہ بہتر اورباقی رہنے والی ہے کیا تم نہیں سمجھتے'),
(3313, 28, 61, 'پھر کیا وہ شخص جس سے ہم نے اچھا وعدہ کیا ہو سو وہ اسے پانے والا بھی ہو اس کے برابر ہے جسے ہم نے دنیا کی زندگی کا فائدہ دیا پھر وہ قیامت کے دن پکڑا ہوا آئے گا'),
(3314, 28, 62, 'اور جس دن انہیں پکارے گا پھرکہے گا میرے شریک کہاں ہیں جن کا تم دعوی کرتے تھے'),
(3315, 28, 63, 'جن پر الزام قائم ہو گا وہ کہیں گے اے رب ہمارے! وہ یہی ہیں جنہیں ہم نے بہکایا تھا انہیں ہم نے گمرا کیا تھا جیسا کہ ہم گمراہ تھے ہم تیرے رو برو بیزار ہوتے ہیں یہ ہمیں نہیں پوجتے تھے'),
(3316, 28, 64, 'اور کہا جائے گا اپنے شریکو ں کو پکارو پھرانہیں پکاریں گے تو وہ انہیں جواب نہ دیں گے اور عذاب دیکھیں گے کاش یہ لوگ ہدایت پر ہوتے'),
(3317, 28, 65, 'اورجس دن انہیں پکارے گا پھر کہے گا تم نے پیغام پہچانے والوں کو کیا جواب دیا تھا'),
(3318, 28, 66, 'پھر اس دن انہیں کوئی بات نہیں سوجھے گی پھر وہ آپس میں بھی نہیں پوچھ سکیں گے'),
(3319, 28, 67, 'پھر جس نے توبہ کی اور ایمان لایا اور نیک عمل کیے سو امید ہے کہ وہ نجات پانے والوں میں سے ہوگا'),
(3320, 28, 68, 'اور تیرا رب جو چاہے پیدا کرتا ہے اور جسے چاہے پسند کرے انہیں کوئی اختیار نہیں ہے الله ان کے شرک سے پاک اور برتر ہے'),
(3321, 28, 69, 'اورتیرا رب جانتا ہے جو ان کے سینوں میں پوشیدہ ہے اور جو ظاہر کرتے ہیں'),
(3322, 28, 70, 'اور وہی الله ہے اس کے سوا کوئی معبود نہیں دنیا اور آخرت میں اسی کی تعریف ہے اور اسی کی حکومت ہے اورتم اسی کی طرف لوٹائے جاؤ گے'),
(3323, 28, 71, 'کہہ دو بھلا یہ تو بتاؤ اگر الله تم پر ہمیشہ کے لیے قیامت تک رات ہی رہنے دے تو الله کے سوا کون سا معبود ہے جو تمہارے لیے روشنی لائے کیا تم سنتے نہیں ہو'),
(3324, 28, 72, 'کہہ دو بھلا یہ تو بتاؤ اگر الله تم پر ہمیشہ کے لیے قیامت تک دن ہی رہنے دے تو الله کے سوا کون سا معبود ہے جو تمہارے لیے رات لائے جس میں آرام پاؤ کیا تم دیکھتے نہیں ہو'),
(3325, 28, 73, 'اوراس نے اپنی رحمت سے تمہارے لیے رات اور دن کو بنایا تاکہ تم اس میں آرام پاؤ اور اپنے رب کا فضل تلاش کرو اور تاکہ تم شکر کرو'),
(3326, 28, 74, 'اور جس دن انہیں پکارے گا پھر کہے گا وہ کہاں ہیں جنہیں تم میرا شریک سمجھتے تھے'),
(3327, 28, 75, 'اور ہم ہر امت میں سے ایک گواہ نکال کر لائیں گے پھر کہیں گے کہ اپنی دلیل پیش کرو تب جان لیں گے کہ سچی بات الله ہی کی تھی اور جو جھوٹ بنایا کرتے تھے ان سے جاتا رہے گا'),
(3328, 28, 76, 'بے شک قارون موسیٰ کی قوم میں سے تھا پھر ان پر اکڑنے لگا اور ہم نے اسے اتنے خزانے دیے تھے کہ اسکی کنجیاں ایک طاقت ور جماعت کو اٹھانی مشکل ہوتیں جب اس سے اس کی قوم نے کہا اِترا مت بے شک الله اِترانے والوں کو پسند نہیں کرتا'),
(3329, 28, 77, 'اور جو کچھ تجھے الله نے دیا ہے اس سے آخرت کا گھر حاصل کر اور اپنا حصہ دنیا میں سے نہ بھول اور بھلائی کر جس طرح الله نے تیرے ساتھ بھلائی کی ہے اور ملک میں فساد کا خواہاں نہ ہو بے شک الله فساد کرنے والوں کو پسندنہیں کرتا'),
(3330, 28, 78, 'کہا یہ تو مجھے ایک ہنر سے ملا ہے جو میرے پاس ہے کیا اسے معلوم نہیں کہ الله نے اس سے پہلے بہت سی امتیں جو اس سے قوت میں بڑھ کر اور جمیعت میں زیادہ تھیں ہلاک کر ڈالی ہیں اور گناہگاروں سے ان کے گناہوں کے بارے میں پوچھا نہیں جائے گا'),
(3331, 28, 79, 'اپنی قوم کے سامنے اپنے ٹھاٹھ سے نکلا جو لوگ دنیا کی زندگی کے طالب تھے کہنے لگے اے کاش ہمارے لیے بھی ویسا ہوتا جیسا کہ قارون کو دیا گیا ہے بے شک وہ بڑے نصیب والا ہے'),
(3332, 28, 80, 'اور علم والوں نے کہا تم پر افسوس ہے الله کا ثواب بہتر ہے اس کے لیے جو ایمان لایا اور نیک کام کیا مگر صبر کرنے والوں سوا نہیں ملا کرتا'),
(3333, 28, 81, 'پھر ہم نے اسے اور اس کے گھر کو زمین میں دھنسا دیا پھر اس کی ایسی کوئی جماعت نہ تھی جو اسے الله سے بچا لیتی اور نہ وہ خود بچ سکا'),
(3334, 28, 82, 'اور وہ لوگ جو کل اس کے مرتبہ کی تمنا کرتے تھے آج صبح کو کہنے لگے کہ ہائے شامت! الله اپنے بندوں میں سے جس کے لیے چاہتا ہے روزی کشادہ کر دیتا ہے اور تنگ کر دیتا ہے اگر ہم پر الله کا احسان نہ ہوتا تو ہمیں بھی دھنسا دیتا ہائے! کافر نجات نہیں پا سکتے'),
(3335, 28, 83, 'یہ آخرت کا گھر ہم انہیں کو دیتے ہیں جو ملک میں ظلم اور فساد کا ارادہ نہیں رکھتے اور نیک انجام تو پرہیز گاروں ہی کا ہے'),
(3336, 28, 84, 'جو بھلائی لے کر آیا اسے اس سے بہتر ملے گا اور جو برائی لے کر آیا پس برائیاں کرنے والوں کو وہی سزا ملے گی جو کچھ وہ کرتے تھے'),
(3337, 28, 85, 'جس نے تم پر قرآن فرض کیا وہ تمہیں لوٹنے کی جگہ پھیر لائے گا کہہ دو میرا رب خوب جانتا ہے کہ ہدایت کون لے کر آیا ہے اور کون صریح گمراہی میں پڑا ہواہے'),
(3338, 28, 86, 'اور تمہیں امید نہ تھی کہ تم پر کتاب اتاری جائے گی مگر تمہارے رب کی مہربانی ہوئی پھر تم کافروں کی طرفداری نہ کرنا'),
(3339, 28, 87, 'اور وہ تمہیں الله کی آیتوں سے بعد اس کے کہ تم پر نازل ہو چکی ہیں روک نہ دیں اوراپنے رب کی طرف بلا اور مشرکوں میں ہر گز شامل نہ ہو'),
(3340, 28, 88, 'اور الله کے ساتھ اور کسی معبود کو نہ پکار اس کے سوا اور کوئی معبود نہیں اس کی ذات کے سوا ہر چیز فنا ہونے والی ہے اسی کا حکم ہے اور اسی کی طرف تم لوٹ کر جاؤ گے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'المۤ'),
(3342, 29, 2, 'کیا لوگ خیال کرتے ہیں یہ کہنے سےکہ ہم ایمان لائے ہیں چھوڑ دیئے جائیں گے اور ان کی آزمائش نہیں کی جائے گی'),
(3343, 29, 3, 'اور جو لوگ ان سے پہلے گزر چکے ہیں ہم نے انہیں بھی آزمایا تھا سو الله انہیں ضرور معلوم کرے گا جو سچے ہیں اور ان کو بھی جو جھوٹے ہیں'),
(3344, 29, 4, 'کیا وہ لوگ جو برے کام کرتے ہیں یہ سمجھتے ہیں کہ ہمارے قابو سے نکل جائیں گے برا ہے جو فیصلہ کرتے ہیں'),
(3345, 29, 5, 'جو شخص الله سے ملنے کی امید رکھتا ہو سو الله کا وعدہ آ رہا ہے اور وہ سننے والا جاننے والا ہے'),
(3346, 29, 6, 'اور جو شخص کوشش کرتا ہے تو اپنے ہی بھلے کے لیے کرتا ہے بے شک الله سارے جہان سے بے نیاز ہے'),
(3347, 29, 7, 'اور جو لوگ ایمان لائے اور نیک کام کیے ہم ان کے گناہوں کو ان سے دور کردیں گےاور انہیں ان کے اعمال کا بہت اچھا بدلہ دیں گے'),
(3348, 29, 8, 'اور ہم نے انسان کو اپنے ماں باپ کے ساتھ اچھا سلوک کرنے کا حکم دیا ہے اور اگر وہ تجھے اس بات پر مجبور کریں کہ تو میرے ساتھ اسے شریک بنائے جسے تو جانتا بھی نہیں تو ان کا کہنا نہ مان تم سب نے لوٹ کرمیرے ہاں ہی آنا ہے تب میں تمہیں بتا دوں گا جو کچھ تم کرتے تھے'),
(3349, 29, 9, 'اور جو لوگ ایمان لائے اور اچھے کام کیے ہم انہیں ضرور نیک بندوں میں داخل کر یں گے'),
(3350, 29, 10, 'اورکچھ ایسے لوگ بھی ہیں جو کہتے ہیں ہم الله پر ایمان لائے پھر جب انہیں الله کی راہ میں کوئی تکلیف پہنچتی ہے لوگوں کی تکلیف کو الله کے عذاب کے برابر سمجھتے ہیں اور اگر تیرے رب کی طرف سے مدد پہنچے تو کہتے ہیں ہم تو تمہارے ساتھ تھے اور کیا الله جہان والوں کے دلوں کی باتوں سے اچھی طرح واقف نہیں ہے'),
(3351, 29, 11, 'اور البتہ الله انہیں ضرور معلوم کرے گا جو ایمان لائے اور البتہ منافقوں کو بھی معلوم کرکے رہے گا'),
(3352, 29, 12, 'اور کافر ایمان والوں سے کہتے ہیں کہ تم ہمارے طریقہ پر چلو اور ہم تمہارے گناہ اٹھا لیں گے حالانکہ وہ ان کے گناہوں میں سے کچھ بھی اٹھانے والے نہیں بے شک وہ جھوٹے ہیں'),
(3353, 29, 13, 'اور البتہ اپنے بوجھ اٹھائیں گے اور اپنےبوجھ کے ساتھ اور کتنے بوجھ اور البتہ قیامت کےد ن ان سے ضرور پوچھا جائے گا ان باتو ں کے متعلق جو وہ بناتے تھے'),
(3354, 29, 14, 'اور ہم نے نوح کو اس کی قوم کی طرف بھیجا پھر وہ ان میں پچاس برس کم ہزار برس رہے پھر انہیں طوفان نے آ پکڑا اور وہ ظالم تھے'),
(3355, 29, 15, 'پھر ہم نے نوح کو اور کشتی والوں کو نجات دی اور ہم نے کشتی کو جہان والوں کے لیے نشانی بنا دیا'),
(3356, 29, 16, 'اور ابراھیم کو جب اس نے اپنی قوم سے کہا کہ الله کی عبادت کرو اور اس سے ڈرو اگر تم سمجھ رکھتے ہو تو تمہارے لیے یہی بہتر ہے'),
(3357, 29, 17, 'تم الله کے سوا بتوں ہی کی عبادت کرتے ہو اور جھوٹ بناتے ہو جنہیں تم الله کے سوا پوجتے ہو وہ تمہاری روزی کے مالک نہیں سو تم الله ہی سے روزی مانگو اور اسی کی عبادت کرو اوراسی کا شکر کرو اسی کے پاس لوٹائے جاؤ گے'),
(3358, 29, 18, 'اور اگر تم جھٹلاؤ تو تم سے پہلے بہت سی جماعتیں جھٹلا چکی ہیں اور رسول کے ذمہ تو بس کھول کر پہنچا دینا ہی ہے'),
(3359, 29, 19, 'کیا انہوں نے نہیں دیکھا کہ الله کس طرح پہلی دفعہ مخلوق کوپیدا کرتا ہے پھر اسے لوٹائے گا بے شک یہ الله پر آسان ہے'),
(3360, 29, 20, 'کہہ دو ملک میں چلو پھرو پھر دیکھو کہ اس نے کس طرح مخلوق کو پہلی دفعہ پیدا کیا پھر الله آخری دفعہ بھی پیدا کرے گا بےشک الله ہر چیز پر قادر ہے'),
(3361, 29, 21, 'جسے چاہے گا عذاب دے گا اور جس پر چاہے رحم کرے گا اور اسی کی طرف لوٹائے جاؤ گے'),
(3362, 29, 22, 'اور تم زمین اور آسمان میں عاجز نہیں کر سکتے اور الله کےسوا تمہارا کوئی دوست اور مددگار نہیں ہے'),
(3363, 29, 23, 'اور جن لوگو ں نے الله کی آیتوں اور اس کے ملنے سے انکار کیا وہ میری رحمت سے نا امید ہو گئے ہیں اور ان کے لیے دردناک عذاب ہے'),
(3364, 29, 24, 'پھر اس کی قوم کا اس کے سوا اور کوئی جواب نہ تھا کہ اسے مار ڈالو یا جلا ڈالو پھر الله نے اسے آگ سے نجات دی بے شک اس میں ان کے لیے نشانیاں ہیں جو ایماندار ہیں'),
(3365, 29, 25, 'او رکہا تم الله کو چھوڑ کر بتوں کو لیے بیٹھے ہو تمہاری آپس کی محبت دنیا کی زندگی میں ہے پھر قیامت کے دن ایک دوسرے کا انکار کرے گا اور ایک دوسرے پر لعنت کرے گا اور تمہارا ٹھکانہ آگ ہوگا اور تمہارے لیے کوئی بھی مددگار نہ ہوگا'),
(3366, 29, 26, 'پھر اس پر لوط ایمان لایا اور ابراھیم نے کہا بے شک میں اپنے رب کی طرف ہجرت کرنے والا ہوں بے شک وہ غالب حکمت والا ہے'),
(3367, 29, 27, 'اور ہم نے اسے اسحاق اور یعقوب عطا کیا اور اس کی نسل میں نبوت اور کتاب مقرر کردی اور ہم نے اسے اس کا بدلہ دنیا میں دیا اور وہ آخرت میں بھی البتہ نیکوں میں سے ہو گا'),
(3368, 29, 28, 'اور لوط کو بھیجا جب اس نے اپنی قوم سے کہا کہ تم وہ بے حیائی کرتے ہو جو تم سے پہلے کسی نے نہیں کی'),
(3369, 29, 29, 'کیا تم مردوں کے پاس جاتے ہو اور تم ڈاکے ڈالتے ہو اور اپنی مجلس میں برا کام کرتے ہو پھر اس کی قوم کے پاس اس کے سوا کوئی جواب نہ تھا کہ تو ہم پر الله کا عذاب لے آ اگر تو سچا ہے'),
(3370, 29, 30, 'کہا اے میرے رب ان شریر لوگو ں پر میری مدد کر'),
(3371, 29, 31, 'اور جب ہمارے بھیجے ہوئے ابراھیم کے پاس خوشخبری لے کر آئے کہنے لگے ہم اس بستی کے لوگوں کو ہلاک کرنے والے ہیں یہاں کے لوگ بڑے ظالم ہیں'),
(3372, 29, 32, 'کہا اس میں لوط بھی ہے کہنے لگے ہم خوب جانتے ہیں جو اس میں ہے ہم لوط اور اس کے کنبہ کو بچا لیں گے مگر اس کی بیوی وہ پیچھے رہ جانے والوں میں ہو گی'),
(3373, 29, 33, 'اور جب ہمارے بھیجے ہوئے لوط کے پاس آئے تو اسے ان کا آنا برا معلوم ہوا اور تنگدل ہوئے فرشتوں نے کہا خوف نہ کر اور غم نہ کھا بے شک ہم تجھے اور تیرے کنبہ کو بچا لیں گے مگرتیری بیوی پیچھے رہنے والوں میں ہوگی'),
(3374, 29, 34, 'ہم ا س بستی والوں پر اس لیے کہ بدکاری کرتے رہے ہیں آسمان سے عذاب نازل کرنے والے ہیں'),
(3375, 29, 35, 'اور ہم نے عقلمندوں کے لیے اس بستی کا نشان نظر آتا ہوا چھوڑ دیا'),
(3376, 29, 36, 'اور مدین کے پاس ان کے بھائی شعیب کو بھیجا پس کہا اے میری قوم! الله کی عبادت کرو اور قیامت کے دن کی امید رکھو اور ملک میں فساد نہ مچاؤ'),
(3377, 29, 37, 'سو انہوں نے اسےجھٹلایا تب انہیں زلزلہ نے آ پکڑا پھر وہ اپنے گھروں میں اوندھے پڑے رہ گئے'),
(3378, 29, 38, 'اور ہم نے عاد اور ثمود کو ہلاک کیا اور تمہیں ان کے کچھ مکانات بھی دکھائی دیتے ہیں اور شیطان نے ان کے اعمال انہیں آراستہ کر دکھائے اور انہیں راہ سے روک دیا حالانکہ وہ سمجھدار تھے'),
(3379, 29, 39, 'اور قارون اور فرعون اور ہامان کو ہلاک کیا اور البتہ ان کے پاس موسیٰ نشانیاں بھی لے کر آئے تھے پھر انہوں نے ملک میں سرکشی کی اور وہ بھاگ کر نہ جا سکے'),
(3380, 29, 40, 'پھر ہم نے ہر ایک کو اس کے گناہ پر پکڑا پھر کسی پر تو ہم نے پتھروں کا مینہ بر سایا اور ان میں سے کسی کو کڑک نے آپکڑا اور کسی کو ان میں سے زمین میں دھنسا دیا اور کسی کو ان میں سے غرق کر دیا اور الله ایسا نہ تھا کہ ان پر ظلم کرے لیکن وہی اپنے اوپر ظلم کیا کرتے تھے'),
(3381, 29, 41, 'ان لوگوں کی مثال جنہوں نے الله کے سوا حمایت بنا رکھے ہیں مکڑی کی سی مثال ہے جس نے گھر بنایا اور بے شک سب گھروں سے کمزور گھر مکڑی کا ہے کاش وہ جانتے'),
(3382, 29, 42, 'بےشک الله جانتا ہے جسے وہ اس کے سوا پکارتے ہیں اور وہ غالب حکمت والا ہے'),
(3383, 29, 43, 'اور یہ مثالیں ہیں جنہیں ہم لوگوں کے لیے بیان کرتے ہیں اور انہیں وہی سمجھتے ہیں جو علم والے ہیں'),
(3384, 29, 44, 'الله نے آسمانو ں اور زمین کو مناسب طور پر بنایا ہے بے شک اس میں ایمان والوں کے لیے بڑی نشانی ہے'),
(3385, 29, 45, 'جو کتاب تیری طرف وحی کی گئی ہے اسے پڑھا کرو اور نماز کے پابند رہو بے شک نماز بے حیائی اوربری بات سے روکتی ہے اور الله کی یاد بہت بڑی چیز ہے اور الله جانتا ہے جو تم کرتے ہو'),
(3386, 29, 46, 'اور اہل کتاب سے نہ جھگڑو مگر ایسے طریقے سے جو عمدہ ہو مگر جو ان میں بے انصاف ہیں اور کہہ دو ہم اس پر ایمان لائے جو ہماری طرف نازل کیا گیا اور تمہاری طرف نازل کیا گیا اور ہمارا اور تمہارا معبود ایک ہی ہے اور ہم اسی کے فرمانبردار ہونے والے ہیں'),
(3387, 29, 47, 'اور اسی طرح ہم نے تیری طرف کتاب نازل کی ہے پھر جنہیں ہم نے کتاب دی تھی وہ تو اس پر ایمان لائے ہیں اور ان میں سے بھی کچھ لوگ اس پر ایمان لائے ہیں اور ہماری آیتوں کا کافر ہی انکار کیا کرتے ہیں'),
(3388, 29, 48, 'اور اس سے پہلے نہ تو کوئی کتاب پڑھتا تھا اور نہ اسے اپنے دائیں ہاتھ سے لکھتا تھا اس وقت االبتہ باطل پرست شک کرتے'),
(3389, 29, 49, 'بلکہ وہ روشن آیتیں ہیں ان کے دلوں میں جنہیں علم دیا گیاہے اور ہماری آیتوں کا صرف ظالم ہی انکار کرتے ہیں'),
(3390, 29, 50, 'اور کہتے ہیں اس پر اس کے رب کی طرف سے نشانیاں کیوں نہ اتریں کہہ دو نشانیاں تو الله ہی کے اختیار میں ہیں اور میں تو بس کھول کر سنا دینے والا ہوں'),
(3391, 29, 51, 'کیا ان کے لیے یہ کافی نہیں کہ ہم نے تجھ پر کتاب نازل کی جو ان پر پڑھی جاتی ہے بے شک اس میں رحمت ہے اور ایمان والوں کے لیے نصیحت ہے'),
(3392, 29, 52, 'کہہ دو الله میرے اور تمہارے درمیان گواہ کافی ہے جو کچھ آسمانوں اور زمین میں ہے جانتا ہے اور جو لوگ جھوٹ پر ایمان لائے اورالله کا انکار کیا وہی نقصان پانے والے ہیں'),
(3393, 29, 53, 'اور تجھ سے عذاب جلدی مانگتے ہیں اور اگر ایک وعدہ مقرر نہ ہوتا تو ا ن پر عذاب آ جاتا اور البتہ ان پر اچانک آئے گا اور انہیں خبر بھی نہ ہوگی'),
(3394, 29, 54, 'تجھ سے عذاب جلدی مانگتے ہیں اور بے شک دوزخ کافروں کو گھیرلینے والی ہے'),
(3395, 29, 55, 'جس دن عذاب انہیں ان کے اوپر سے اور پاؤں کے نیچے سے گھیر لے گا اور کہے گا جو تم کیا کرتے تھے اس کا مزہ چکھو'),
(3396, 29, 56, 'اے میرے بندو جو ایمان لائے ہو میری زمین کشادہ ہے پس میری ہی عبادت کرو'),
(3397, 29, 57, 'ہر جاندار موت کا مزہ چکھنے والا ہے پھر ہمارے ہی پاس پھر کر آؤ گے'),
(3398, 29, 58, 'اور جو ایمان لائے اور نیک کام کیے البتہ ہم انہیں جنت کے بالا خانوں میں جگہ دیں گے جن کے نیچے نہریں بہتی ہوں گی وہاں ہمیشہ رہیں گے عمل کرنے والوں کا کیا اچھا بدلہ ہے'),
(3399, 29, 59, 'جنہوں نے صبر کیا اور اپنے رب پر بھروسہ رکھتے ہیں'),
(3400, 29, 60, 'اور بہت سے جانور ہیں جو اپنا رزق اٹھائے نہیں پھرتے الله ہی انہیں اور تمہیں رزق دیتا ہے اور وہ سننے والا جاننے والا ہے'),
(3401, 29, 61, 'اور البتہ اگر تو اان سے پوچھے کہ آسمانوں اور زمین کو کس نے پیدا کیا اور سورج اور چاند کو کس نے کام میں لگایا تو ضرور کہیں گے الله نے پھر کہاں الٹے جا رہے ہیں'),
(3402, 29, 62, 'الله ہی اپنے بندوں میں سے جس کے لیے چاہتا ہے رزق کشادہ کر دیتا ہے اورتنگ کر دیتا ہے بے شک الله ہر چیز کا جاننے والا ہے'),
(3403, 29, 63, 'اور البتہ اگر تو ان سے پوچھے آسمان سے کس نے پانی اتارا پھر اس سے زمین کو اس کے مرنے کے بعد زندہ کیا کہیں گے الله نے کہہ دو سب تعریف الله ہی کے لیے ہے لیکن ان میں سے اکثر نہیں سمجھتے'),
(3404, 29, 64, 'اور یہ دنیا کی زندگی تو صرف کھیل اور تماشا ہے اور اصل زندگی عالم آخرت کی ہے کاش وہ سمجھتے'),
(3405, 29, 65, 'پھر جب کشتی میں سوار ہوتے ہیں تو خالص اعتقاد سے الله ہی کو پکارتے ہیں پھر جب انہیں نجات دے کر خشکی کی طرف لے آتا ہے فوراً ہی شرک کرنے لگتے ہیں'),
(3406, 29, 66, 'تاکہ ناشکری کریں اس نعمت کی جو ہم نے انہیں دی تھی اور مزے اڑاتے رہیں عنقریب انہیں معلوم ہو جائے گا'),
(3407, 29, 67, 'کیا وہ نہیں دیکھتے کہ ہم نے حرم کو امن کی جگہ بنا دیا ہے اور لوگ ان کے آس پاس سے اچک لیے جاتے ہیں کیا یہ لوگ جھوٹ پر ایمان رکھتے ہیں اور الله کی نعمت کی ناشکری کرتے ہیں'),
(3408, 29, 68, 'اور اس سے بڑھ کر کون ظالم ہے جو الله پر جھوٹ باندھے یا حق کو جھٹلائے جب اس کے پاس آئے کیا دوزخ میں کافروں کا ٹھکانہ نہیں ہے'),
(3409, 29, 69, 'اور جنہوں نے ہمارے لیے کوشش کی ہم انہیں ضرور اپنی راہیں سمجھا دیں گے اور بے شک الله نیکو کاروں کے ساتھ ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'المۤ'),
(3411, 30, 2, 'روم مغلوب ہو گئے'),
(3412, 30, 3, 'نزدیک کے ملک میں اور وہ مغلوب ہونے کے بعد عنقریب غالب آجائیں گے'),
(3413, 30, 4, 'چند ہی سال میں پہلے اور پچھلے سب کا م الله کے ہاتھ میں ہیں اور اس دن مسلمان خوش ہوں گے'),
(3414, 30, 5, 'الله کی مدد سے مدد کرتا ہے جس کی چاہتا ہے اور وہ غالب رحم والا ہے'),
(3415, 30, 6, 'الله کا وعدہ ہو چکا الله اپنے وعدہ کا خلاف نہیں کرے گا لیکن اکثر آدمی نہیں جانتے'),
(3416, 30, 7, 'دنیا کی زندگی کی ظاہر باتیں جانتے ہیں اور وہ آخرت سے غافل ہی ہیں'),
(3417, 30, 8, 'کیا وہ اپنے دل میں خیال نہیں کرتے کہ الله نے آسمانوں اور زمین کواور جو کچھ ان دونوں کے درمیان ہے عمدگی سے اور وقت مقرر تک کے لیے بنایا ہے اور بے شک بہت سے لوگ اپنے رب سے ملنے کے منکر ہیں'),
(3418, 30, 9, 'کیا انہوں نے ملک میں پھر کر نہیں دیکھا کہ ان سے پہلوں کا کیسا انجام ہوا وہ ان سے بھی بڑھ کر قوت والے تھے اور انہوں نے زمین کو جوتا تھا اور ان سے بہت زیادہ آباد کیا تھا اور ان کے پاس ان کے رسول معجزات لے کر بھی آئے تھے پھر الله ایسا نہ تھا کہ ان پر ظلم کرتا بلکہ وہی اپنے نفسوں پر ظلم کرتے تھے'),
(3419, 30, 10, 'پھر برا کرنے والوں کا انجام بھی برا ہی ہوا اس لیے کہ انہوں نے الله کی آیتوں کو جھٹلایا اور ان کی ہنسی اڑاتے رہے'),
(3420, 30, 11, 'الله ہی مخلوق کو پہلی بار پیدا کرتا ہے پھر وہ اسے دوبارہ پیدا کرے گا پھر اس کے پاس لوٹ کر آؤ گے'),
(3421, 30, 12, 'اور جس دن قیامت قائم ہو گی گناہگار نا امید ہو جائیں گے'),
(3422, 30, 13, 'اور ان کے معبودوں میں سے کوئی ان کی سفارش کرنے والا نہ ہوگا اور اپنے معبودوں سے منکر ہو جائیں گے'),
(3423, 30, 14, 'اور جس دن قیامت قائم ہوگی اس دن لوگ جدا جدا ہوجائیں گے'),
(3424, 30, 15, 'پھر جو ایما ن لائے اورنیک کام کیے سووہ بہشت میں خوش حال ہوں گے'),
(3425, 30, 16, 'اورجنہوں نے انکار کیا اور ہماری آیتوں اور آخرت کے آنے کو جھٹلایا وہ عذاب میں ڈالے جائیں گے'),
(3426, 30, 17, 'پھر الله کی تسبیح کرو جب تم شام کرو اور جب تم صبح کرو'),
(3427, 30, 18, 'اور آسمانوں اور زمین میں اسی کی تعریف ہے اور پچھلے پہر بھی اور جب دوپہر ہو'),
(3428, 30, 19, 'زندہ کو مردہ سے اور مردہ کو زندہ سے نکالتا ہے اور زمین کو اس کے مرنے کے بعد زندہ کرتا ہے اور اسی طرح تم نکالے جاؤ گے'),
(3429, 30, 20, 'اور اس کی نشانیوں میں سے ہے کہ تمہیں مٹی سے بنایا پھر تم انسان بن کر پھیل رہے ہو'),
(3430, 30, 21, 'اور اس کی نشانیوں میں سے یہ بھی ہے کہ تمہارے لیے تمہیں میں سے بیویاں پیدا کیں تاکہ ان کے پاس چین سے رہو اور تمہارے درمیان محبت اور مہربانی پیدا کر دی جو لوگ غور کرتے ہیں ان کے لیے اس میں نشانیاں ہیں'),
(3431, 30, 22, 'اور اس کی نشانیوں میں سے آسمانوں اور زمین کا پیدا کرنا اور تمہاری زبانوں اور رنگتوں کا مختلف ہونا ہے بے شک اس میں علم والوں کے لیے نشانیاں ہیں'),
(3432, 30, 23, 'اور اس کی نشانیوں میں سے تمہارا رات اور دن میں سونا اور اس کے فضل کا تلاش کرنا ہے بے شک اس میں سننے والوں کے لیے نشانیاں ہیں'),
(3433, 30, 24, 'اور اس کی نشانیوں میں سے یہ ہے کہ تمہیں خوف اور امید دلانے کو بجلی دکھاتا ہے اور اوپر سے پانی برساتا ہے پھراس سے زمین خشک ہو جانے کے بعد زندہ کرتا ہے بے شک اس میں عقلمندوں کے لئےنشانیاں ہیں'),
(3434, 30, 25, 'اور اس کی نشانیوں میں سے یہ ہے کہ آسمان اور زمین ا س کے حکم سے قائم ہیں پھر جب تمہیں پکار کر زمین میں سے بلائے گا اسی وقت تم نکل آؤ گے'),
(3435, 30, 26, 'اور اسی کا ہے جو کچھ آسمانوں اور زمین میں ہے سب اسکے حکم کے تابع ہیں'),
(3436, 30, 27, 'اور وہی ہے جو پہلی بار بناتا ہے پھر اسے لوٹائے گا اور وہ اس پر آسان ہے اور آسمانوں اور زمین میں اس کی شان نہایت بلند ہے اور وہ غالب حکمت والا ہے'),
(3437, 30, 28, 'وہ تمہارے لیے تمہارے ہی حال کی ایک مثال بیان فرماتا ہے کیا جن کے تم مالک ہو وہ اس میں جو ہم نے تمہیں دیا ہے تمہارے شریک ہیں پھر اس میں تم برابر ہو تم ان سے اس طرح ڈرتے ہو جس طرح اپنوں سے ڈرتے ہو اس طرح ہم عقل والوں کے لیے آیتیں کھول کر بیان کرتے ہیں'),
(3438, 30, 29, 'بلکہ یہ بے انصاف بے سمجھے اپنی خوہشوں پر چلتے ہیں پھر کون ہدایت کر سکتا ہے جسے الله نے گمراہ کر دیا اور ان کا کوئی بھی مددگار نہیں'),
(3439, 30, 30, 'سو تو ایک طرف کا ہو کر دین پرسیدھا منہ کیے چلا جا الله کی دی ہوئی قابلیت پر جس پر اس نے لوگوں کو پیدا کیا ہے الله کی بناوٹ میں ردو بدل نہیں یہی سیدھا دین ہے لیکن اکثر آدمی نہیں جانتے'),
(3440, 30, 31, 'اسی کی طرف رجوع کیے رہو اور اس سے ڈرو اور نماز قائم کرو اور مشرکوں میں سے نہ ہوجاؤ'),
(3441, 30, 32, 'جنہوں نے اپنے دین کو ٹکڑے ٹکڑے کر دیا اور کئی فرقے ہو گئے سب فرقے اسی سے خوش ہیں جو ان کے پا س ہے'),
(3442, 30, 33, 'اور ولوگوں کو جب کوئی دکھ پہنچتا ہے تو اپنے رب کی طرف رجوع ہو کر اسے پکارتے ہیں پھر جب وہ انہیں اپنی رحمت کا مزہ چکھاتا ہے توایک گروہ ان میں سے اپنے رب سے شرک کرنے لگتا ہے'),
(3443, 30, 34, 'تاکہ جو ہم نے انہیں دیا ہے اس کی ناشکری کریں سوفائدہ اٹھا لو عنقریب تمہیں معلوم ہو جائے گا'),
(3444, 30, 35, 'کیا ہم نے ان کے لیے کوئی سند بھیجی ہے کہ وہ انہیں شرک کرنا بتا رہی ہے'),
(3445, 30, 36, 'اور جب ہم لوگوں کو رحمت کا مزہ چکھاتے ہیں تو اس پر خوش ہوجاتے ہیں اور اگر انہیں ان کے گذشتہ اعمال کے سبب سے دکھ پہنچتا ہے تو فوراً نا امید ہو جاتے ہیں'),
(3446, 30, 37, 'کیا وہ نہیں دیکھتے کہ الله جس کے لیے چاہتا ہے رزق کشادہ کرتا ہے اور تنگ کرتا ہے بے شک اس میں ایمان لانے والوں کے لیے نشانیاں ہیں'),
(3447, 30, 38, 'پھر رشتہ دار اور محتاج اور مسافر کو اس کا حق دے یہ بہتر ہے ان کے لیے جو الله کی رضا چاہتے ہیں اور وہی نجات پانے والے ہیں'),
(3448, 30, 39, 'اور جو سود پر تم دیتے ہو تاکہ لوگوں کے مال میں بڑھتا رہے سو الله کے ہاں وہ نہیں بڑھتا اور جو زکواة دیتے ہو جس سے الله کی رضا چاہتے ہو سو یہ وہ ہی لوگ ہیں جن کے دونے ہوئے'),
(3449, 30, 40, 'الله وہ ہے جس نے تمہیں پیدا کیا پھر تمہیں روزی دی پھر تمہیں مارے گا پھر تمہیں زندہ کرے گا کیا تمہارے معبودوں میں سے کبھی کوئی ایسا ہے جو ان کاموں میں سے کچھ بھی کر سکے وہ پاک ہے اور ان کے شریکوں سے بلند ہے'),
(3450, 30, 41, 'خشکی اور تری میں لوگوں کے اعمال کے سبب سے فساد پھیل گیا ہے تاکہ الله انہیں ان کے بعض اعمال کا مزہ چکھائے تاکہ وہ باز آجائیں'),
(3451, 30, 42, 'کہہ دو ملک میں چلو پھرو اور دیکھو جو لوگ پہلے گزرے ہیں ان کا کیسا انجام ہوا ان میں سے اکثر مشرک ہی تھے'),
(3452, 30, 43, 'سو تو اپنا منہ سیدھی راہ پرسیدھا رکھ اس سے پہلے کہ وہ دن آ پہنچے جسے الله کی طرف سے پھرنا نہیں اس دن لوگ جدا جدا ہوں گے'),
(3453, 30, 44, 'جس نے کفر کیا سو اس کے کفر کا وبال اسی پر ہے اور جس نے اچھے کام کیے تو وہ اپنے لیے سامان کر رہے ہیں'),
(3454, 30, 45, 'تاکہ جو ایمان لائے اور اچھےے کام کیے الله انہیں اپنے فضل سے بدلہ دے بے شک الله ناشکروں کو پسند نہیں کرتا'),
(3455, 30, 46, 'اور اس کی نشانیوں میں سے ایک یہ ہے کہ خوشخبری لانے والی ہوائیں چلاتا ہے اور تاکہ تمہیں اپنی مہربانی کا کچھ مزہ چکھا دیں اور تاکہ کشتیاں اس کے حکم سے چلیں اور تاکہ اس کے فضل سے تلاش کرو اور تاکہ تم شکر کرو'),
(3456, 30, 47, 'اور ہم تم سے پہلے کتنے رسول اپنی اپنی قوم کے پاس بھیج چکے ہیں سو ان کے پاس نشانیاں لے کر آئے پھر ہم نے ان سے بدلہ لیا جو گناہگار تھے اور مومنوں کی مدد ہم پر لازم تھی'),
(3457, 30, 48, 'الله وہ ہے جو ہوائیں چلاتا ہے پھر وہ بادل کو اٹھاتی ہیں پھر اسے آسمان میں جس طرح چاہے پھیلا دیتا ہےاور اسے ٹکڑے ٹکڑے کر دیتا ہے پھر تو مینہ کو دیکھے گا کہ اس کے اندر سے نکلتا ہے پھر جب اسے اپنے بندوں میں سے جسے چاہتا ہے پہنچاتا ہے تو وہ خوش ہو جاتے ہیں'),
(3458, 30, 49, 'اور اگرچہ ان پر برسنے سے پہلے وہ نا امید تھے'),
(3459, 30, 50, 'پھر تو الله کی رحمت کی نشانیوں کو دیکھ کہ زمین کو خشک ہونے کے بعد کس طرح سر سبز کرتا ہے بے شک وہی مردوں کو پھر زندہ کرنے والا ہے اوروہ ہر چیز پر قادر ہے'),
(3460, 30, 51, 'اور اگر ہم ایسی ہوا چلائیں کہ جس سے وہ کھیتی کو زرد دیکھیں تو اس کے بعدوہ ناشکری کرنے لگ جائیں'),
(3461, 30, 52, 'بے شک تو مردوں کو نہیں سنا سکتا اور نہ بہروں کو آواز سنا سکتا ہے جب وہ پیٹھ پھیر کرپھر جائیں'),
(3462, 30, 53, 'اور تم اندھوں کوان کے الٹے راستے سے سیدھے راستہ پر نہیں لا سکتے تم تو بس انہیں لوگوں کو سنا سکتے ہو جو ہماری آیتوں پر ایمان لاتے ہیں سو وہی ماننے والے ہیں'),
(3463, 30, 54, 'الله ہی ہے جس نے تمہیں کمزوری کی حالت میں پیدا کیا پھر کمزوری کے بعد قوت عطا کی پھر قوت کے بعد ضعف اور بڑھاپا بنایا جو چاہتاہے پیدا کرتا ہے اوروہی جاننے والا قدرت والا ہے'),
(3464, 30, 55, 'اورجس دن قیامت قائم ہو گی گناہگار قسمیں کھائیں گے کہ ہم ایک گھڑی سے بھی زیادہ نہیں ٹھہرے تھے اسی طرح وہ الٹے جاتے تھے'),
(3465, 30, 56, 'اور جن لوگو ں کو علم اور ایمان دیا گیا تھا کہیں گے کہ الله کی کتاب کے مطابق تم قیامت تک رہے ہو سو یہ قیامت کا ہی دن ہے لیکن تمہیں اس کا یقین ہی نہ تھا'),
(3466, 30, 57, 'تو اس دن ظالموں کو ان کا عذر کچھ فائدہ نہ دے گا اور نہ ان سے توبہ قبول کی جائے گی'),
(3467, 30, 58, 'اور ہم نے لوگوں کے لیے اس قرآن میں ہر طرح کی مثال بیان کر دی ہے اور اگر تم ان کے سامنے کوئی نشانی پیش کرو تو کافر یہ کہہ دیں گے کہ تم تو جھوٹے ہو'),
(3468, 30, 59, 'جو لوگ یقین نہیں کرتے الله ان کے دلوں پر یونہی مہر کر دیتا ہے'),
(3469, 30, 60, 'سوتو صبر کر بے شک الله کاوعدہ سچا ہے اور جو لوگ یقین نہیں رکھتے وہ تجھے بےبرداشت نہ بنادیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'المۤ'),
(3471, 31, 2, 'یہ آیتیں حکمت والی کتا ب کی ہیں'),
(3472, 31, 3, 'جو نیک بختوں کے لیے ہدایت اور رحمت ہے'),
(3473, 31, 4, 'وہ جو نماز ادا کرتے ہیں اور زکواة دیتے ہیں اور آخرت پر بھی یقین رکھتے ہیں'),
(3474, 31, 5, 'یہی لوگ اپنے رب کی ہدایت پر ہیں اور یہی لوگ نجات پانے والے ہیں'),
(3475, 31, 6, 'اور بعض ایسے آدمی بھی ہیں جو کھیل کی باتوں کے خریدار ہیں تاکہ بن سمجھے الله کی راہ سے بہکائیں اور اس کی ہنسی اڑائیں ایسے لوگو ں کے لیے ذلت کا عذاب ہے'),
(3476, 31, 7, 'اورجب اس پر ہماری آیتیں پڑھی جاتی ہیں تو تکبرکرتا ہوا منہ موڑ لیتا ہے جیسے اس نے سنا ہی نہیں گویا اس کے دونوں کان بہرے ہیں سواسے دردناک عذاب کی خوشخبری دے'),
(3477, 31, 8, 'بے شک جو لوگ ایمان لائے اور نیک کام کیے ان کے لیے نعمت کے باغ ہیں'),
(3478, 31, 9, 'جہاں ہمیشہ رہیں گے الله کا سچا وعدہ ہو چکا اور وہ زبردست حکمت والا ہے'),
(3479, 31, 10, 'آسمانوں کو بے ستون بنایا تم انہیں دیکھ رہے ہو اور زمین میں مضبوط پہاڑ رکھ دیے تاکہ تمہیں لے کر ادھر ادھر نہ جھکے اور اس میں ہر قسم کے جانور پھیلا دیے اور ہم نے آسمان سے مینہ برسایا پھر ہم نے زمین میں ہر قسم کی عمدہ چیزیں اگائیں'),
(3480, 31, 11, 'یہ تو الله کی ساخت ہے پھر مجھے دکھاؤ کہ اس کے سوا غیر نے کیاپیدا کیا ہے بلکہ ظالم صریح گمراہی میں پڑے ہوئے ہیں'),
(3481, 31, 12, 'اور ہم نے لقمان کو دانائی عطا فرمائی کہ اللہ کا شکر کرتے رہو اور جو شخص شکر کرے گا وہ اپنے ذاتی نفع کے لیے شکر کرتا ہے اور جو ناشکری کرے گا تو الله بے نیاز خوبیوں والا ہے'),
(3482, 31, 13, 'اور جب لقمان نے اپنے بیٹے کو نصیحت کرتے ہوئے کہا تھا کہ بیٹا الله کے ساتھ کسی کو شریک نہ ٹھہرانا بے شک شرک کرنا بڑا بھاری ظلم ہے'),
(3483, 31, 14, 'اور ہم نے انسان کو اس کے ماں باپ کے متعلق تاکید کی ہے اس کی ماں نے ضعف پر ضعف اٹھا کر اسے پیٹ میں رکھا اور دو برس میں اس کا دودھ چھڑانا ہے تو میری اور اپنے ماں باپ کی شکر گزاری کرے میری ہی طرف لوٹ کر آنا ہے'),
(3484, 31, 15, 'اور اگر تجھ پر اس بات کا زور ڈالیں تو میرے ساتھ اس کو شریک بنائے جس کو تو جانتا بھی نہ ہو تو ان کا کہنا نہ مان اور دنیا میں ان کے ساتھ نیکی سے پیش آ اور ان لوگوں کی راہ پر چل جو میری طرف رجوع ہوگئے پھر تمہیں لوٹ کر میرے ہی پاس آنا ہے پھر میں تمہیں بتاؤں گا کہ تم کیا کیا کرتے تھے'),
(3485, 31, 16, 'بیٹا اگر کوئی عمل رائی کے دانہ کے برابر ہو پھر وہ کسی پتھر کے اندر ہو یا وہ آسمان کے اندر ہو یا زمین کے اندر ہو تب بھی الله اس کو حاضر کر دے گا بے شک الله بڑا باریک بین (اور) باخبر ہے'),
(3486, 31, 17, 'بیٹا نماز پڑھا کر اور اچھے کاموں کی نصیحت کیا کر اوربرے کاموں سے منع کیا کر اور تجھ پر جو مصیبت آئے اس پر صبر کیا کر بے شک یہ ہمت کے کاموں میں سے ہیں'),
(3487, 31, 18, 'اور لوگوں سے اپنا رخ نہ پھیر اور زمین پر اترا کر نہ چل بے شک الله کسی تکبر کرنے والے فخر کرنے والے کو پسند نہیں کرتا'),
(3488, 31, 19, 'اور اپنے چلنے میں میانہ روی اختیار کر اور اپنی آواز پست کر بے شک آوازوں میں سب سے بری آواز گدھوں کی ہے'),
(3489, 31, 20, 'کیا تم نے نہیں دیکھا جو کچھ آسمانوں میں اورجوکچھ زمین میں ہے سب کو الله نے تمہارے کام پر لگایا رکھا ہے اور تم پراپنی ظاہری اور باطنی نعمتیں پوری کر دی ہیں اور لوگوں میں سے ایسے بھی ہیں جو الله کے معاملے میں جھگڑتے ہیں نہ انہیں علم ہے اور نہ ہدایت ہے اور نہ روشنی بخشنے والی کتاب ہے'),
(3490, 31, 21, 'اور جب ان سے کہا جاتا ہے اس پر چلو جو الله اس پر چلو جو الله نے نازل کیا ہے تو کہتے ہیں کہ ہم تو اس پر چلیں گے جس پر ہم نے اپنے باپ دادا کو پایا ہے کیا اگرچہ شیطان ان کے بڑوں کو دوزخ کے عذاب کی طرف بلاتا رہا ہو'),
(3491, 31, 22, 'اور جس نے نیک ہو کر اپنا منہ الله کے سامنے جھکا دیا تو اس نے مضبوط کڑے کو تھام لیا اور آخر کار ہر معاملہ الله ہی کے حضور میں پیش ہونا ہے'),
(3492, 31, 23, 'اور جس نے انکار کیا پس تو اس کے انکار سے غم نہ کھا انہیں ہمارے پاس آنا ہے پھر ہم انہیں بتا دیں گے کہ انہوں نے کیا کیا ہے بے شک الله دلوں کے راز جانتا ہے'),
(3493, 31, 24, 'ہم انہیں تھوڑا سا عیش دے رہے ہیں پھر ہم انہیں سخت عذاب کی طرف گھسیٹ کر لے جائیں گے'),
(3494, 31, 25, 'اور اگر آپ ان سے پوچھیں کہ آسمانوں اور زمین کو کس نے بنایا ہے تو ضرور کہیں گے کہ الله نے کہہ دو الحمدُلله بلکہ ان میں سے اکثر نہیں جانتے'),
(3495, 31, 26, 'الله ہی کا ہے جو کچھ آسمانوں اور زمین میں ہے بے شک الله بے نیاز سب خوبیوں والا ہے'),
(3496, 31, 27, 'اوراگروہ جو زمین میں درخت ہیں سب قلم ہوجائیں گے اور دریا سیاہی اس کے بعد اس دریا میں سات اور دریا سیاہی کے آ ملیں تو بھی الله کی باتیں ختم نہ ہوں بے شک الله زبردست حکمت والا ہے'),
(3497, 31, 28, 'تم سب کا پیدا کرنا اور مرنے کے بعد زندہ کرنا ایسا ہی ہے جیسا ایک شخص کا بے شک الله سنتا دیکھتا ہے'),
(3498, 31, 29, 'کیا تو نے نہیں دیکھا کہ الله رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اور سورج اور چاند کو کام پر لگا رکھا ہے ہر ایک وقت مقرر تک چلتا رہے گا اور یہ کہ الله تمہارے کام سے خبردار ہے'),
(3499, 31, 30, 'یہ اس لیے کہ الله ہی حق ہے اور اس کے سوا جس کو وہ پکارتے ہیں جھوٹ ہے اور الله ہی بلند مرتبہ بزرگ ہے'),
(3500, 31, 31, 'کیا آپ نے نہیں دیکھا کہ الله ہی کے فضل سے دریا میں کشتیاں چلتی ہیں تاکہ تمہیں اپنی نشانیاں دکھائے بے شک اس میں ہر ایک صابر شاکر کےلیے نشانیاں ہیں'),
(3501, 31, 32, 'اور جب انہیں سائبانوں کی طرح موج ڈھانک لیتی ہے تو خالص اعتقاد سے الله ہی کو پکارتے ہیں پھر جب انہیں نجات دے کر خشکی کی طرف لےآتا ہے تو بعض ان میں سے راہِ راست پر رہتے ہیں اور ہماری نشانیوں سے وہی لوگ انکار کرتے ہیں جو بد عہد نا شکر گزار ہیں'),
(3502, 31, 33, 'اے لوگو اپنے رب سے ڈرو اور اس دن سے ڈرو جس میں نہ باپ اپنے بیٹے کے کام آئے گا اور نہ بیٹا اپنے باپ کے کچھ کام آئے گا الله کا وعدہ سچا ہے پھر دنیا کی زندگی تمہیں دھوکا میں نہ ڈال دے اور نہ دغا باز تمہیں الله سے دھوکہ میں رکھیں'),
(3503, 31, 34, 'بے شک الله ہی کو قیامت کی خبر ہے اور وہی مینہ برساتا ہے اور وہی جانتا ہے جو کچھ ماؤں کے پیٹوں میں ہوتا ہے اور کوئی نہیں جانتا کہ کل کیا کرے گا اور کوئی نہیں جانتا کہ کس زمین پر مرے گا بے شک الله جاننے والا خبردار ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'المۤ'),
(3505, 32, 2, 'اس میں کچھ شک نہیں کہ یہ کتاب جہان کے پالنے والے کی طرف سے نازل ہوئی ہے'),
(3506, 32, 3, 'کیا وہ کہتے ہیں کہ اس نے خود بنائی ہے بلکہ یہ سچی کتاب تیرے رب کی طرف سے ہے تاکہ تو اس قوم کوڈرائے جن کے پاس تجھ سے پہلے کوئی ڈرانے والا نہیں آیا تاکہ وہ راہ پر آئيں'),
(3507, 32, 4, 'الله وہ ہے جس نے آسمانوں اور زمین کو اورجو کچھ ان میں ہے چھ روز میں بنایا پھر عرش پر قائم ہوا تمہارے لیے اس کے سوا نہ کوئی کارساز ہے نہ سفارشی پھر کیا تم نہیں سمجھتے'),
(3508, 32, 5, 'وہ آسمان سے لے کر زمین تک ہر کام کی تدبیر کرتا ہے پھراس دن بھی جس کی مقدار تمہاری گنتی سے ہزار برس ہو گی وہ انتظام اس کی طرف رجوع کرے گا'),
(3509, 32, 6, 'وہی چھپی اور کھلی بات کا جاننے والا زبردست مہربان ہے'),
(3510, 32, 7, 'جس نے جو چیز بنائی خوب بنائی اور انسان کی پیدائش مٹی سے شروع کی'),
(3511, 32, 8, 'پھر اس کی اولاد نچڑے ہوئے حقیر پانی سے بنائی'),
(3512, 32, 9, 'پھراس کے اعضا درست کیے اور اس میں اپنی روح پھونکی اور تمہارے لیے کان اور آنکھیں اور دل بنایا تم بہت تھوڑا شکر کرتے ہو'),
(3513, 32, 10, 'اور کہتے ہیں کہ ہم جب زمین میں نیست و نابود ہو گئے تو کیا پھر نئے سرے سے پیدا ہوں گے بلکہ وہ اپنے رب سے ملنے کے منکر ہیں'),
(3514, 32, 11, 'کہہ دو تمہاری جان موت کا وہ فرشتہ قبض کرے گا جو تم پر مقرر کیا گیا ہے پھر تم اپنے رب کے پاس لوٹائے جاؤ گے'),
(3515, 32, 12, 'اور کبھی تو دیکھے جس وقت منکر اپنے رب کے سامنے سر جھکائے ہوئے ہوں گے اے رب ہمارے ہم نے دیکھ اور سن لیا اب ہمیں پھر بھیج دے کہ اچھے کام کریں ہمیں یقین آ گیا ہے'),
(3516, 32, 13, 'اور اگر ہم چاہتے ہیں تو ہر شخص کو ہدایت پر لے آتے لیکن ہماری بات پوری ہو کر رہی کہ ہم جنوں اور آدمیوں سے جہنم بھر کر رہیں گے'),
(3517, 32, 14, 'تو اب اس کا مزہ چکھو کہ تم اپنے اس دن کے آنے کو بھول گئے تھے ہم نے تمہیں بھلا دیا اور اپنے کیے کے بدلہ میں ہمیشہ کا عذاب چکھو'),
(3518, 32, 15, 'بس ہماری آیتوں پر وہ ایمان لاتے ہیں کہ جب انہیں وہ آیتیں یاد دلائی جاتی ہیں تو وہ سجدہ میں گر پڑتے ہیں اور اپنے رب کی حمد کے ساتھ تسبیح بیان کرتے ہیں اور وہ تکبر نہیں کرتے'),
(3519, 32, 16, 'اپنے بستروں سے اٹھ کر اپنے رب کو خوف اور امید سے پکارتے ہیں اور ہمارے دیئے میں سے کچھ خرچ بھی کرتے ہیں'),
(3520, 32, 17, 'پھر کوئی شخص نہیں جانتا کہ ان کے عمل کے بدلہ میں ان کی آنکھو ں کی کیا ٹھنڈک چھپا رکھی ہے'),
(3521, 32, 18, 'کیا مومن اس کے برابر ہے جو نا فرمان ہو برابر نہیں ہو سکتے'),
(3522, 32, 19, 'سو وہ لوگ جو ایمان لائے اور اچھے کام کیے تو ان کے ان کاموں کے سبب جو وہ کیا کرتے تھے مہمانی میں ہمیشہ رہنے کے باغ ہیں'),
(3523, 32, 20, 'اور جنہوں نے نافرمانی کی ان کا ٹھکانا آگ ہے جب وہاں سے نکلنے کا ارداہ کریں گے تو اس میں پھر لوٹا دیئے جائیں گے اور انہیں کہا جائے گا آگ کا وہ عذاب چکھو جسے تم جھٹلایا کرتے تھے'),
(3524, 32, 21, 'اور ہم انہیں قریب کا عذاب بھی اس بڑے عذاب سے پہلے چکھائیں گے تاکہ وہ باز آ جائیں'),
(3525, 32, 22, 'اوراس سے بڑھ کر کون ظالم ہوگا جسے اس کے رب کی آیتوں سے سمجھایا جائے پھر وہ ان سے منہ موڑے ہمیں تو گنہگاروں سے بدلہ لینا ہے'),
(3526, 32, 23, 'اور البتہ ہم نے موسیٰ کو کتاب دی تھی پھر آپ اس کے ملنے میں شک نہ کریں اور ہم نے ہی اسے بنی اسرائیل کے لیے راہ نما بنایا تھا'),
(3527, 32, 24, 'اور ہم نے ان میں سے پیشوا بنائے تھے جو ہمارے حکم سے رہنمائی کرتے تھے جب انہوں نے صبر کیا تھا اور وہ ہماری آیتوں پر یقین بھی رکھتے تھے'),
(3528, 32, 25, 'بے شک تیرا رب ہی قیامت کے دن ان میں فیصلہ کرے گا جس بات میں وہ اختلاف کرتے تھے'),
(3529, 32, 26, 'کیا انھیں اس سے بھی رہنمائی نہ ہوئی کہ ان سے پہلے ہم نے کتنی جماعتیں ہلاک کر دی ہیں جن کے گھروں میں یہ چلتے پھرتے ہیں بے شک اس میں بڑی نشانیاں ہیں پھر کیا وہ سنتے بھی نہیں'),
(3530, 32, 27, 'کیا وہ نہیں دیکھتے کہ ہم پانی کو خشک زمین کی طرف رواں کر کے ا س سے کھیتی نکالتے ہیں جس سے ان کے چار پائے اوروہ خود بھی کھاتے ہیں پھر کیا وہ دیکھتے نہیں'),
(3531, 32, 28, 'اور کہتے ہیں کہ اگر تم سچے ہو تو یہ فیصلہ کب ہوگا'),
(3532, 32, 29, 'کہہ دو کہ فیصلہ کا دن کافرو ں کو ان کا ایمان لانا نفع نہ دے گا اور نہ ہی انہیں مہلت دی جائے گی'),
(3533, 32, 30, 'سو ان سے کنارہ کر اور انتظار کر وہ بھی انتظار کر رہے ہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'اے نبی الله سے ڈر اور کافروں اور منافقوں کا کہا نہ مان بے شک الله جاننے والا حکمت والا ہے'),
(3535, 33, 2, 'اور اس کی تابعداری کر جو تیرے رب کی طرف سے تیری طرف بھیجا گیا ہے بے شک الله تمہارے کاموں سے خبردار ہے'),
(3536, 33, 3, 'اور الله پر بھروسہ کر اور الله ہی کارساز کافی ہے'),
(3537, 33, 4, 'الله نے کسی شخص کے سینہ میں دو دل نہیں بنائے اور نہ الله نے تمہاری ان بیویوں کوجن سے تم اظہار کرتے ہو تمہاری ماں بنایا ہے اور نہ تمہارے منہ بولے بیٹوں کو تمہارا بیٹا بنایا ہے یہ تمہارے منہ کی بات ہے اور الله سچ فرماتا ہے اور وہی سیدھا راستہ بتاتا ہے'),
(3538, 33, 5, 'انہیں ان کے اصلی باپوں کے نام سے پکارو الله کے ہاں یہی پورا انصاف ہے سواگر تمہیں ان کے باپ معلوم نہ ہوں تو تمہارے دینی بھائی اور دوست ہیں اور تمہیں اس میں بھول چوک ہو جائے تو تم پر کچھ گناہ نہیں لیکن وہ جو تم دل کے ارادہ سے کرو اور الله بخشنے والا مہربان ہے'),
(3539, 33, 6, 'نبی مسلمانو ں کے معاملہ میں ان سے بھی زيادہ دخل دینے کا حقدار ہے اور اس کی بیویاں ان کی مائیں ہیں اور رشتہ دار الله کی کتاب میں ایک دوسرے سے زیادہ تعلق رکھتے ہیں بہ نسبت دوسرے مومنین اور مہاجرین کے مگر یہ کہ تم اپنے دوستوں سے کچھ سلوک کرنا چاہو یہ بات لوح محفوظ میں لکھی ہوئی ہے'),
(3540, 33, 7, 'اور جب ہم نے نبیوں سے عہد لیا اورآپ سے اورنوح اور ابراھیم اور موسیٰ اور مریم کے بیٹے عیسیٰ سے بھی اور ان سے ہم نے پکا عہد لیا تھا'),
(3541, 33, 8, 'تاکہ سچوں سے ان کے سچ کا حال دریافت کرے اور کافروں کے لیے دردناک عذاب تیار کیا ہے'),
(3542, 33, 9, 'اے ایمان والو! الله کے احسان کو یاد کرو جو تم پر ہوا جب تم پر کئی لشکر چڑھ آئے پھر ہم نے ان پر ایک آندھی بھیجی اور وہ لشکر بھیجے جنہیں تم نے نہیں دیکھا اورجو کچھ تم کر رہے تھے الله دیکھ رہا تھا'),
(3543, 33, 10, 'جب وہ لوگ تم پر تمہارے اوپر کی طرف اورنیچے کی طرف سے چڑھ آئے اورجب آنکھیں پتھرا گئی تھیں اور کلیجے منہ کو آنے لگے تھے اورتم الله کے ساتھ طرح طرح کے گمان کر رہے تھے'),
(3544, 33, 11, 'اس موقع پر ایماندار آزمائے گئے اور سخت ہلا دیے گئے'),
(3545, 33, 12, 'اورجب کہ منافق اورجن کے دلوں میں شک تھا کہنے لگے کہ الله اور اس کے رسول نے جو ہم سے وعدہ کیا تھا صرف دھوکا ہی تھا'),
(3546, 33, 13, 'اورجب کہ ان میں سے ایک جماعت کہنے لگی اے مدینہ والو! تمہارے لیے ٹھیرنے کا موقع نہیں سو لوٹ چلو اور ان میں سے کچھ لوگ نبی سے رخصت مانگنے لگے کہنے لگے کہ ہمارے گھر اکیلے ہیں اور حالانکہ وہ اکیلے نہ تھے وہ صرف بھاگنا چاہتے تھے'),
(3547, 33, 14, 'اور اگر کسی طرف سے کوئی ان پر گُھس آتا پھر ان سے فساد کی درخواست کی جاتی تو فساد پر آمادہ ہو جاتے اور دیر نہ کرتے مگر بہت ہی کم'),
(3548, 33, 15, 'حالانکہ اس سے پہلے الله سے عہد کر چکے تھے کہ پیٹھ نہ پھیریں گے اور الله سے عہد کرنے کی باز پرس ہوگی'),
(3549, 33, 16, 'کہہ دو اگر تم موت یا قتل سے بھاگو گے تو تمہیں کوئی فائدہ نہیں ہوگا اور اس وقت سوائے تھوڑے دنوں کے نفع نہیں اٹھاؤ گے'),
(3550, 33, 17, 'کہہ دو کون ہے جو تمہیں الله سے بچا سکے اگر وہ تمہارے ساتھ برائی کرنا چاہے یا تم پر مہربانی کرنا چاہے اور الله کے سوا نہ کوئی اپنا حمایتی پائیں گے اور نہ کوئی مددگار'),
(3551, 33, 18, 'تحقیق الله تم میں سے روکنے والوں کو جانتاہے اور جو اپنے بھائیوں سے کہتے ہیں کہ ہمارے پاس آجاؤ اورلڑائی میں بہت ہی کم آتے ہیں'),
(3552, 33, 19, 'تم سے ہمدردی کرتے ہوئے پھر جب ڈر کا وقت آجائے تو تُو انھیں دیکھے گا کہ تیری طرف دیکھتے ہیں ان کی آنکھیں پھرتی ہیں جیسے کسی پر موت کی بے ہوشی آئے پھر جب ڈر جاتا رہے تو تمہیں تیز زبانوں سے طعنہ دیتے ہیں مال کے لالچی ہیں یہ لوگ ایمان نہیں لائے تو الله نے ان کے تمام اعمال ضائع کر دیے اور یہ بات الله پر بالکل آسان ہے'),
(3553, 33, 20, 'خیال کرتے ہیں کہ فوجیں نہیں گئیں اور اگر فوجیں آجائیں تو آرزوکریں کہ کاش ہم باہر گاؤں میں جا رہیں تمہاری خبریں پوچھا کریں اور اگر تم میں بھی رہیں تو بہت ہی کم لڑیں'),
(3554, 33, 21, 'البتہ تمہارے لیے رسول الله میں اچھا نمونہ ہے جو الله اور قیامت کی امید رکھتا ہے اور الله کو بہت یاد کرتا ہے'),
(3555, 33, 22, 'اور جب مومنوں نے فوجوں کو دیکھا تو کہا یہ وہ ہے جس کا ہم سے الله اور اس کے رسول نے وعدہ کیا تھا اور الله اور اس کے رسول نے سچ کہا تھا اور اس سے ان کے ایمان اور فرمانبرداری میں ترقی ہو گئی'),
(3556, 33, 23, 'ایمان والوں میں سے ایسے آدمی بھی ہیں جنہوں نے الله سے جو عہد کیا تھا اسے سچ کر دکھایا پھر ان میں سے بعض تو اپنا کام پورا کر چکے اور بعض منتظر ہیں اور عہد میں کوئی تبدیلی نہیں کی'),
(3557, 33, 24, 'تاکہ الله سچوں کو ان کے سچ کا بدلہ دے اور اگر چاہے تو منافقوں کوعذاب دے یا ان کی توبہ قبول کرے بے شک الله بخشنے والا مہربان ہے'),
(3558, 33, 25, 'اور الله نے کافروں کو ان کے غصہ میں بھرا ہوا لوٹایا انہیں کچھ بھی ہاتھ نہ آیا اور الله نے مسلمانوں کی لڑائی اپنے ذمہ لے لی اور الله طاقت ور غالب ہے'),
(3559, 33, 26, 'اور جن جن اہل کتاب نے ان کی مدد ی تھی انہیں ان کے قلعوں سے نیچے اتار دیا اور ان کے دلوں میں خوف ڈال دیا بعض کو تم قتل کرنے لگے اور بعض کو قید کر لیا'),
(3560, 33, 27, 'اور ان کی زمین اوران کے گھروں اور ان کےمالوں کا تمہیں مالک بنا دیا اور زمین کا جس پر تم نے کبھی قدم نہیں رکھا تھا اور الله ہر چیز پر قادر ہے'),
(3561, 33, 28, 'اے نبی اپنی بیویوں سے کہہ دو اگر تمہیں دنیا کی زندگی اور اس کی آرائش منظور ہے تو آؤ میں تمہیں کچھ دے دلا کر اچھی طرح سے رخصت کر دوں'),
(3562, 33, 29, 'اور اگر تم الله اور اس کے رسول اور آخرت کو چاہتی ہو تو الله نے تم میں سے نیک بختوں کے لیے بڑا اجر تیار کیا ہے'),
(3563, 33, 30, 'اے نبی کی بیویو تم میں سے جو کوئی کھلی ہوئی بدکاری کرے تو اسے دگنا عذاب دیا جائے گا اور یہ الله پر آسان ہے'),
(3564, 33, 31, 'اور جو تم میں سے الله اور اس کےرسول کی فرمانبرداری کرے گی اور نیک کام کرے گی تو ہم اسے اس کا دہرااجر دیں گے اور ہم نے اس کے لیے عزت کا رزق بھی تیار کر رکھا ہے'),
(3565, 33, 32, 'اے نبی کی بیویو تم معمولی عورتوں کی طرح نہیں ہو اگر تم الله سے ڈرتی ر ہو اور دبی زبان سے بات نہ کہو کیونکہ جس کے دل میں مرض ہے وہ طمع کرے گا اور بات معقول کہو'),
(3566, 33, 33, 'اور اپنے گھروں میں بیٹھی رہو اور گزشتہ زمانہ جاہلیت کی طرح بناؤ سنگھار دکھاتی نہ پھرو اور نماز پڑھو اور زکواة دو اور الله اور اس کے رسول کی فرمانبرداری کرو الله یہی چاہتا ہے کہ اے اس گھر والو تم سے ناپاکی دور کرے اور تمہیں خوب پاک کرے'),
(3567, 33, 34, 'اور تمہارے گھروں میں جو الله کی آیتیں اور حکمت کی باتیں پڑھی جاتیں ہیں انہیں یاد رکھو بیشک الله رازدان خبردار ہے'),
(3568, 33, 35, 'بیشک الله نے مسلمان مردوں اور مسلمان عورتوں اور ایمان دار مردوں اور ایماندار عورتوں اور فرمانبردار مردوں اور فرمانبردارعورتوں اور سچے مردوں اور سچی عورتوں اور صبر کرنے والے مردوں اور صبر کرنے والی عورتوں اور عاجزی کرنے والے مردوں اور عاجزی کرنے والی عورتوں اور خیرات کرنے والے مردوں اور خیرات کرنے والی عورتوں اور روزہ دار مردوں اور روزدار عورتوں اور پاک دامن مردوں اور پاک دامن عورتوں اور الله کو بہت یاد کرنے والے مردوں اور بہت یاد کرنے والی عورتوں کے لیے بخشش اور بڑا اجر تیار کیا ہے'),
(3569, 33, 36, 'اور کسی مومن مرد اور مومن عورت کو لائق نہیں کہ جب الله اور اس کا رسول کسی کام کا حکم دے تو انہیں اپنے کام میں اختیار باقی رہے اور جس نے الله اور اس کے رسول کی نافرمانی کی تو وہ صریح گمراہ ہوا'),
(3570, 33, 37, 'اور جب تو نے اس شخص سے کہا جس پر الله نے احسان کیا اور تو نے احسان کیا اپنی بیوی کو اپنے پاس رکھ الله سے ڈر اور تو اپنے دل میں ایک چیز چھپاتا تھا جسے الله ظاہر کرنے والا تھا اور تو لوگوں سے ڈرتا تھا حالانکہ الله زیادہ حق رکھتا ہے کہ تو اس سے ڈرے پھر جب زید اس سے حاجت پوری کر چکا تو ہم نے تجھ سے اس کا نکاح کر دیا تاکہ مسلمانوں پر ان کے منہ بولے بیٹوں کی بیویوں کے بارے میں کوئی گناہ نہ ہو جب کہ وہ ان سے حاجت پوری کر لیں اور الله کا حکم ہوکر رہنے والا ہے'),
(3571, 33, 38, 'نبی پر اس بات میں کوئی گناہ نہیں ہے جو الله نے اس اس کے لیے مقرر کر دی ہے جیسا کہ الله کا پہلے لوگو ں میں دستور تھا اور الله کا کام اندازے پر مقرر کیا ہوا ہے'),
(3572, 33, 39, 'جو لوگ الله کا پیغام پہنچاتے رہے اور الله سے ڈرتے رہے اور الله حساب لینے والا کافی ہے'),
(3573, 33, 40, 'محمد تم میں سے کسی مرد کے باپ نہیں لیکن وہ الله کے رسول اور سب نبیوں کے خاتمے پر ہیں اور الله ہر بات جانتا ہے'),
(3574, 33, 41, 'اے ایمان والو الله کو بہت یاد کرو'),
(3575, 33, 42, 'اور اس کی صبح و شام پاکی بیان کرو'),
(3576, 33, 43, 'وہی ہے جو تم پر رحمت بھیجتا ہے اور اس کے فرشتے بھی تاکہ تمہیں اندھیروں سے روشنی کی طرف نکالے اور وہ ایمان والوں پر نہایت رحم والا ہے'),
(3577, 33, 44, 'جس دن وہ اس سے ملیں گے ان کے لیے سلام کا تحفہ ہوگا اور ان کے لیے عزت کا اجر تیار کر رکھا ہے'),
(3578, 33, 45, 'اے نبی ہم نے آپ کو بلاشبہ گواہی دینے والا اور خوشخبری دینے والا اور ڈرانے والا بنا کر بھیجا ہے'),
(3579, 33, 46, 'اور الله کی طرف اس کے حکم سے بلانے اور چراغ روشن بنایا ہے'),
(3580, 33, 47, 'اور ایمان والوں کو خوشخبری دے اس بات کی کہ ان کے لیے الله کی طرف سے بہت بڑا فضل ہے'),
(3581, 33, 48, 'اور کفار اور منافقین کا کہنا نہ مانیے اور ان کی ایذا رسانی کی پرواہ نہ کیجیے اور الله پر بھروسہ کیجیئے اور الله کارساز کافی ہے'),
(3582, 33, 49, 'اے ایمان والو جب تم مومن عورتوں سے نکاح کرو پھر انہیں طلاق دے دو اس سے پہلے کہ تم انہیں چھوو تو تمہارے لیے ان پر کوئی عدت نہیں ہے کہ تم ان کی گنتی پوری کرنے لگو سو انہیں کچھ فائدہ دو اور انہیں اچھی طرح سے رخصت کر دو'),
(3583, 33, 50, 'اے نبی ہم نے آپ کے لیے آپ کی بیویاں حلال کر دیں جن کے آپ مہر ادا کر چکے ہیں اور وہ عورتیں جو تمہاری مملومکہ ہیں جو الله نے آپ کو غنیمت میں دلوادی ہیں اور آپ کے چچا کی بیٹیاں اور آپ کی پھوپھیوں کی بیٹیاں اور آپ کے ماموں کی بیٹیاں اور آپ کے خالاؤں کی بیٹیاں جنہوں نے آپ کے ساتھ ہجرت کی اور اس مسلمان عورت کو بھی جو بلاعوض اپنے کوپیغمبر کو دے دے بشرطیکہ پیغمبر اس کو نکاح میں لانا چاہے یہ خالص آپ کے لیے ہے نہ اور مسلمانوں کے لیے ہمیں معلوم ہے جو کچھ ہم نے مسلمانو ں پر ان کی بیویوں اور لونڈیوں کے بارے میں مقرر کیا ہے تاکہ آپ پر کوئی دِقت نہ رہے اور الله معاف کرنے والا مہربان ہے'),
(3584, 33, 51, 'آپ ان میں سے جسے چاہیں چھوڑ دیں اور جسے چاہیں اپنے پاس جگہ دیں اور ان میں سے جسے آپ چاہیں جنہیں آپ نے علیحدہ کر دیا تھا تو آپ پر کوئی گناہ نہیں یہ اس سے زیادہ قریب ہے کہ ان کی آنکھیں ٹھنڈی ہوں اور غمزدہ نہ ہو اور ان سب کو جو آپ دیں اس پر راضی ہوں اور جو کچھ تمہارے دلوں میں ہے الله جانتا ہے اورالله جاننے والا بردبار ہے'),
(3585, 33, 52, 'اس کے بعد آپ کے لیے عورتیں حلال نہیں اور نہ یہ کہ آپ ان سےاورعورتیں تبدیل کریں اگر چہ آپ کو ان کا حسن پسند آئے مگر جو آپ کی مملوکہ ہوں اور الله ہر ایک چیز پر نگران ہے'),
(3586, 33, 53, 'اے ایمان والو نبی کے گھروں میں داخل نہ ہو مگر اس وقت کہ تمہیں کھانے کےلئے اجازت دی جائے نہ اس کی تیاری کا انتظام کرتے ہوئے لیکن جب تمہیں بلایا جائے تب داخل ہو پھر جب تم کھا چکو تو اٹھ کر چلے جاؤ اور باتوں کے لیے جم کر نہ بیٹھو کیوں کہ اس سے نبی کو تکلیف پہنچتی ہے اور وہ تم سے شرم کرتا ہے اور حق بات کہنے سے الله شرم نہیں کرتا اور جب نبی کی بیویوں سے کوئی چیز مانگو تو پردہ کے باہر سے مانگا کرو اس میں تمہارے اوران کے دلوں کے لیے بہت پاکیزگی ہے اور تمہارے لیے جائز نہیں کہ تم رسول الله کو ایذا دو اور نہ یہ کہ تم اپ کی بیویوں سے آپ کے بعد کبھی بھی نکاح کرو بے شک یہ الله کےنزدیک بڑا گناہ ہے'),
(3587, 33, 54, 'اگر تم کوئی بات ظاہر کرو یا اسے چھپاؤ تو بے شک الله ہر چیز کو جاننے والا ہے'),
(3588, 33, 55, 'ان پر اپنے باپوں کے سامنے ہونے میں کوئی گناہ نہیں اور نہ اپنے بیٹوں کے اور نہ اپنے بھائیوں کے اور نہ اپنے بھتیجوں کے اورنہ اپنے بھانجوں کے اور نہ اپنی عورتوں کے اور نہ اپنے غلاموں کے اور الله سے ڈرتی رہو بے شک ہر چیز الله کے سامنے ہے'),
(3589, 33, 56, 'بے شک الله اور اس کے فرشتے نبی پر درود بھیجتے ہیں اے ایمان والو تم بھی اسپر دورد اور سلام بھیجو'),
(3590, 33, 57, 'جو لوگ الله اور اس کے رسول کو ایذا دیتے ہیں ان پر اللهنے دنیا اور آخرت میں لعنت کی ہے اور ان کے لیے ذلت کا عذاب تیار کر کھا ہے'),
(3591, 33, 58, 'اور جو ایمان دار مردوں اور عورتوں کو ناکردہ گناہوں پر ستاتے ہیں سو وہ اپنے سر بہتان اور صریح گناہ لیتے ہیں'),
(3592, 33, 59, 'اے نبی اپنی بیویوں اور بیٹیوں اور مسلمانوں کی عورتوں سے کہہ دو کہ اپنے مونہوں پر نقاب ڈالا کریں یہ اس سے زیادہ قریب ہے کہ پہچانی جائیں پھر نہ ستائی جائیں اور الله بخشنے والا نہایت رحم والا ہے'),
(3593, 33, 60, 'اگر منافق اور وہ جن کے دلوں میں مرض ہے اور مدینہ میں غلط خبریں اڑانے والے باز نہ آئیں گے تو آپ کوہم ان کے پیچھے لگا دیں گے پھر وہ اس شہر میں تیرے پاس نہ ٹھیریں گے'),
(3594, 33, 61, 'مگر بہت کم لعنت کیے گئے ہیں جہاں کہیں پائیں جائیں گے پکڑے جائیں گے اور قتل کیے جائیں گے'),
(3595, 33, 62, 'یہی الله کا قانون ہے ان لوگو ں میں جو اس سے پہلے ہو گزر چکے ہیں اور آپ الله کے قانون میں کوئی تبدیلی ہرگز نہ پائیں گے'),
(3596, 33, 63, 'آپ سے لوگ قیامت کے متعلق پوچھتے ہیں کہہ دو اس کا علم تو صرف الله ہی کو ہے اور اپ کو کیا خبر کہ شاید قیامت قریب ہی ہو'),
(3597, 33, 64, 'بے شک الله نے کافروں پر لعنت کی ہے اور ان کے لیے دوزخ تیار کر رکھا ہے'),
(3598, 33, 65, 'وہ اس میں ہمیشہ رہیں گے نہ کوئی دوست پائیں گے اور نہ کوئی مددگار'),
(3599, 33, 66, 'جس دن ان کے منہ آگ میں الٹ دیے جائیں گے کہیں گے اے کاش ہم نے الله اور رسول کا کہا مانا ہوتا'),
(3600, 33, 67, 'اور کہیں گے اے ہمارے رب ہم نے اپنے سرداروں اوربڑوں کا کہا مانا سو انہوں نے ہمیں گمراہ کیا'),
(3601, 33, 68, 'اے ہمارے رب انہیں دگنا عذاب دے اور ان پر بڑی لعنت کر'),
(3602, 33, 69, 'اے ایمان والو تم ان لوگو ں جیسے نہ ہوجاؤ جنہوں نے موسیٰ کو ستایا پھر الله نے موسیٰ کو ان کی باتوں سے بری کر دیا اور وہ الله کے نزدیک بڑی عزت والا تھا'),
(3603, 33, 70, 'اے ایمان والو الله سے ڈرو اور ٹھیک بات کیا کرو'),
(3604, 33, 71, 'تاکہ وہ تمہارے اعمال کو درست کرے اور تمہارے گناہ معاف کر دے اور جس نے الله اور اس کے رسول کا کہنا مانا سو اس نے بڑی کامیابی حاصل کی'),
(3605, 33, 72, 'ہم نے آسمانوں اور زمین اور پہاڑوں کے سامنے امانت پیش کی پھر انہوں نے اسکے اٹھانے سے انکار کر دیا اور اس سے ڈر گئے اور اسے انسان نے اٹھا لیا بے شک وہ بڑا ظالم بڑا نادان تھا'),
(3606, 33, 73, 'تاکہ الله منافق مردوں اور منافق عورتوں اورمشرک مردوں اورمشرک عورتوں کو عذاب دے اور مومن مردوں اورمومن عورتوں پر مہربانی کرے اور الله معاف کرنے والا مہربان ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'سب تعریف الله ہی کے لیے ہے جو کچھ آسمانوں اور زمین میں ہے سب اسی کا ہے اور آخرت میں بھی اسی کے لیے سب تعریف ہے اور وہ حکمت والا خبردار ہے'),
(3608, 34, 2, 'وہ جانتا ہے جو زمین میں داخل ہوتا ہے اور جو اس میں سے نکلتا ہے اور جو آسمان سے نازل ہوتا ہے اور جو اس میں چڑھتا ہے اور وہ نہایت رحم والا بخشنے والا ہے'),
(3609, 34, 3, 'اور کافر کہتے ہیں کہ ہم پر قیامت نہیں آئے گی کہہ دو ہاں (آئے گی) قسم ہے میرے رب غائب کے جاننے والے کی البتہ تم پر ضرور آئے گی جس سے آسمانوں اور زمین کی کوئی چیز ذرّہ کے برابر بھی غائب نہیں اور نہ ذرّہ سے چھوٹی اور نہ بڑی کوئی بھی ایسی چیز نہیں جو لوح محفوظ میں نہ ہو'),
(3610, 34, 4, 'تاکہ الله ان لوگوں کو جزا دے جو ایمان لائے اور انہوں نے نیک عمل کیے انہیں کے لیے بخشش اور عزت والا رزق ہے'),
(3611, 34, 5, 'اور جو ہماری آیتوں کے رد کرنے میں کوشش کرتے پھرتے ہیں ان کے لیے ذلت کا عذاب ہے'),
(3612, 34, 6, 'اور جنہیں علم دیا گیا ہے وہ خیال کرتے ہیں کہ جو کچھ آپ کی طرف آپ کے رب کی طرف سے نازل ہوا ہے وہ ٹھیک ہے اور وہ غالب تعریف کئےہوئے کی راہ دکھاتا ہے'),
(3613, 34, 7, 'اور کافر کہتے ہیں کیا ہم تمہیں وہ آدمی بتائیں جو تمہیں خبر دیتا ہے کہ جب تم پورے طور پر ریزہ ریزہ ہو جاؤ گے تو پھر نئے سرے سے پیدا کیے جاؤ گے'),
(3614, 34, 8, 'کیا اس نے الله پر جھوٹ بنا لیا ہے یا اسے جنون ہے نہیں بلکہ جو لوگ آخرت پر یقین نہیں رکھتے وہ عذاب اور دور کی گمراہی میں ہیں'),
(3615, 34, 9, 'کیا وہ آسمان اور زمین کو نہیں دیکھتے جو ان کے آگے اور پیچھے ہے اگر ہم چاہیں تو انہیں زمین میں دھنسا دیں یا ان پر کوئی آسمان کا ٹکڑا گرا دیں الله کی طرف رجوع کرنے والے بندے کے لیے اس میں بڑی نشانیاں ہیں'),
(3616, 34, 10, 'اور بے شک ہم نے داؤد کو اپنی طرف سے بزرگی دی تھی اے پہاڑو ان کی تسبیح کی آواز کا جواب دیا کرو اور پرندوں کو تابع کر دیا تھا اور ہم نے ان کے لیے لوہا نرم کر دیا تھا'),
(3617, 34, 11, 'کہ کشادہ زرہیں بنا اور اندازے سے کڑیاں جوڑ اور تم سب نیک کام کرو بے شک میں جو تم کرتے ہو خوب دیکھ رہا ہوں'),
(3618, 34, 12, 'اور ہوا کو سلیمان کے تابع کر دیا تھا جس کی صبح کی منزل مہینے بھر کی راہ اور شام کی منزل مہینے بھر کی راہ تھی اور ہم نے اس کے لیے تانبے کا چشمہ بہا دیا تھا اور کچھ جن اس کے آگے اس کے رب کے حکم سے کام کیا کرتے تھے اور جو کوئی ان میں سے ہمارے حکم سے پھر جاتا تھاتو ہم اسے آگ کا عذاب چکھاتے تھے'),
(3619, 34, 13, 'جو وہ چاہتا اس کے لیے بناتے تھے قلعے اور تصویریں اور حوض جیسے لگن اور جمی رہنے والی دیگیں اے داؤد والو تم شکریہ میں نیک کام کیا کرو اور میرے بندوں میں سے شکر گزار تھوڑے ہیں'),
(3620, 34, 14, 'پھر جب ہم نے اس پر موت کا حکم کیا تو انہیں ا سکی موت کا پتہ نہ دیا مگر گھن کے کیڑے نے جو اس کے عصا کو کھا رہا تھا پھر جب گر پڑا تو جنوں نے معلوم کیا کہ اگر وہ غیب کو جانتے ہوتے تو اس ذلت کے عذاب میں نہ پڑے رہتے'),
(3621, 34, 15, 'بے شک قوم سبا کے لیے ان کی بستی میں ایک نشان تھا دائیں اور بائیں دو باغ اپنے رب کی روزی کھاؤ اور اس کا شکر کرو عمدہ شہر رہنے کو اور بخشنے والا رب'),
(3622, 34, 16, 'پھر انہوں نے نافرمانی کی پھر ہم نے ان پر سخت سیلاب بھیج دیا اور ہم نے ان کے دونوں باغوں کے بدلے میں دو باغ بد مزہ پھل کے اور جھاؤ کے اور کچھ تھوڑی سی بیریوں کے بدل دیے'),
(3623, 34, 17, 'یہ ہم نے ان کی ناشکری کا بدلہ دیا اور ہم ناشکروں ہی کو برا بدلہ دیا کرتے ہیں'),
(3624, 34, 18, 'اورہم نے ان کے اور ان بستیوں کے درمیان جن میں ہم نے برکت رکھی تھی بہت سے گاؤں آباد کر رکھے تھے جو نظر آتے تھے اور ہم نے ان میں منزلیں مقرر کر دیں تھیں ان میں راتوں اور دنوں کو امن سے چلو'),
(3625, 34, 19, 'پھر انہوں نے کہا اے ہمارے رب ہماری منزلوں کو دور دور کردےا ور انہوں نے اپنی جانوں پر ظلم کیا سو ہم نے انہیں کہانیاں بنا دیا اور ہم نے انہیں پورے طور پر پارہ پارہ کر دیا بے شک اس میں ہر ایک صبر شکرکرنے والے کے لیے نشانیاں ہیں'),
(3626, 34, 20, 'اور البتہ شیطان نے ان پر اپنا گمان سچ کر دکھایا سوائے ایمان داروں کے ایک گروہ کے سب اس کے تابع ہو گئے'),
(3627, 34, 21, 'حالانکہ ان پر اس کا کوئی زور بھی نہیں تھا مگر یہی کہ ہم نے ظاہر کرنا تھا کون آخرت پر ایمان لاتا ہے اورکون اس سے شک میں پڑا ہوا ہے اور تیرا رب ہر چیز پر نگہبان ہے'),
(3628, 34, 22, 'کہہ دوالله کے سوا جن کا تمہیں گھمنڈ ہے انہیں پکارو وہ نہ تو آسمان ہی میں ذرّہ بھر اختیار رکھتے ہیں اور نہ زمین میں اور نہ ان کا ان میں کچھ حصہ ہے اور نہ ان میں سے الله کا کوئی مددکار ہے'),
(3629, 34, 23, 'اور اس کے ہاں سفارش نفع نہ دے گی مگر اسی کو جس کے لیے وہ اجازت دے گا یہاں تک کہ جب ان کے دل سے گھبراہٹ دور ہوجاتی ہے کہتے ہیں تمہارے رب نے کیا فرمایا وہ کہتے ہیں سچی بات فرمائی اوروہی عالیشان اور سب سے بڑا ہے'),
(3630, 34, 24, 'کہہ دو تمہیں آسمانوں اور زمین سے کون رزق دیتا ہے کہو الله اور بے شک ہم یا تم ہدایت پر ہیں یا صریح گمراہی میں'),
(3631, 34, 25, 'کہہ دو نہ تم پوچھے جاؤ گے اس کی نسبت جو ہم نے جرم کیا ہے اور نہ ہم ہی پوچھیں جائیں گے ا سکی بابت جو تم کرتے ہو'),
(3632, 34, 26, 'کہہ دو ہم سب کو ہمارا رب جمع کرے گا پھر ہمارے درمیان انصاف سے فیصلہ کرے گا اور وہی فیصلہ کرنے والا جاننے ولا ہے'),
(3633, 34, 27, 'کہہ دو جنہیں تم نے اس سے شریک بنا کر ملا رکھا ہے مجھے بھی تو دکھاؤ بلکہ وہی الله غالب حکمت والا ہے'),
(3634, 34, 28, 'اور ہم نے آپ کو جو بھیجا ہے تو صرف سب لوگوں کو خوشی اور ڈر سنانے کے لیے لیکن اکثر لوگ نہیں جانتے'),
(3635, 34, 29, 'اور کہتے ہیں یہ وعدہ کب ہے اگر تم سچے ہو'),
(3636, 34, 30, 'کہہ دو تمہارے لیے ایک دن کا وعدہ ہے کہ جس سے نہ ایک گھڑی پیچھے ہو سکتے ہو اور نہ آگے بڑھ سکتے ہو'),
(3637, 34, 31, 'اور کافر کہتے ہیں ہم اس قرآن پر ہرگز ایمان نہیں لائیں گے اور نہ اس پر جو اس سے پہلے موجود ہے اورکاش آپ دیکھتے جب کہ ظالم اپنے رب کے حضور میں کھڑے کیے جائیں گے ایک ان میں سے دوسرے کی بات کو رد کر رہا ہوگا جو لوگ کمزور سمجھے جاتے تھے وہ ان سے کہیں گے جو بڑے بنتے تھے اگر تم نہ ہوتے تو ہم ایمان دار ہوتے'),
(3638, 34, 32, 'جو لوگ بڑے بنتے تھے ان سے کہیں گے جو کمزور سمجھے جاتے تھے کیا ہم نے تمہیں ہدایت سے روکا تھا بعد اس کے کہ وہ تمہارے پاس آ چکی تھی بلکہ تم خود ہی مجرم تھے'),
(3639, 34, 33, 'اورجو لوگ کمزور سمجھے جاتے تھے وہ ان سے کہیں گے جو متکبر تھے بلکہ (تمہارے) رات اور دن کے فریب نے جب تم ہمیں حکم دیا کرتے تھے کہ ہم الله کا انکار کر دیں اوراس کے لیے شریک ٹھیرائیں اور دل میں بڑے پشیمان ہوں گے جب عذاب کو سامنے دیکھیں گے اورکافروں کی گردنوں میں ہم طوق ڈالیں گے جو کچھ وہ کیا کرتے تھے اسی کا تو بدلہ پا رہے ہیں'),
(3640, 34, 34, 'اور ہم نے جس کسی بستی میں کوئی ڈرانے والا بھیجا تو وہاں کے دولتمندوں نے یہی کہا کہ تم جو لے کر آئے ہو ہم نہیں مانتے'),
(3641, 34, 35, 'اور یہ بھی کہا کہ ہم مال اوراولاد میں تم سے بڑھ کر ہیں اور ہمیں کوئی عذاب نہ دیا جائے گا'),
(3642, 34, 36, 'کہہ دو میرا رب جس کے لیے چاہتا ہے روزی کشادہ کر دیتا ہے اور کم کر دیتا ہے اور لیکن اکثر آدمی نہیں جانتے'),
(3643, 34, 37, 'اور تمہاے مال اور اولاد ایسی چیز نہیں جو تمہیں مرتبہ میں ہمارے قریب کر دے مگر جو ایمان لایا اور نیک کام کیے پس وہی لوگ ہیں جن کے لئے دگنا بدلہ ہے اس کا جو انہوں نے کیا اور وہی بالاخانوں میں امن سے ہوں گے'),
(3644, 34, 38, 'اوروہ جو ہماری آیتوں کے رد کرنے میں کوشش کرتے ہیں وہ عذاب میں پکڑ کر حاضر کیے جائیں گے'),
(3645, 34, 39, 'کہہ دو بے شک میرا رب ہی اپنے بندو ں میں سے جسے چاہے روزی کشادہ کر دیتا ہے اور جسے چاہے تنگ کر دیتا ہے اور جو کوئی چیز بھی تم خرچ کرتے ہو سو وہی اس کا عوض دیتا ہے اور وہ سب سے بہتر روزی دینے والا ہے'),
(3646, 34, 40, 'اور جس دن وہ ان سب کو جمع کرے گا پھر فرشتوں سے فرمائے گا کیا یہی ہیں جو تمہاری عبادت کیا کرتے تھے'),
(3647, 34, 41, 'وہ عرض کریں گے تو پاک ہے ہماراتو تجھ سے ہی تعلق ہے نہ ان سے بلکہ یہ شیطانوں کی عبادت کرتے تھے ان میں سے اکثر انہیں کے معتقد تھے'),
(3648, 34, 42, 'پھر آج تم میں سے کوئی کسی کے نفع اور نقصان کا مالک نہیں اور ہم ظالموں سے کہیں گے تم اس آگ کا عذاب چکھو جسے تم جھٹلایا کرتے تھے'),
(3649, 34, 43, 'اور جب انہیں ہماری واضح آیتیں سنائی جاتی ہیں تو کہتے ہیں کہ یہ محض ایسا شخص ہے جو چاہتا ہے کہ تمہیں ان چیزوں سے رو ک دے جنہیں تمہارے باپ دادا پوجتے تھے اور (قرآن کی نسبت) کہتے ہیں کہ یہ محض ایک تراشا ہوا جھوٹ ہے اورکافروں نے حق کے متعلق کہا جب ان کے پاس آیا کہ یہ محض ایک صریح جادو ہے'),
(3650, 34, 44, 'اورہم نے انہیں کوئی کتاب نہیں دی کہ وہ اسے پڑھتے ہوں اور ہم نے ان کی طرف آپ سے پہلے کوئی ڈرانے والا نہیں بھیجا'),
(3651, 34, 45, 'اوران لوگوں نے بھی جھٹلایا جو ان سے پہلے تھے اور یہ لوگ اس کے دسویں حصہ کونہیں پہنچے جو ہم نے انہیں دیا تھا پس انہوں نے میرے رسولوں کو جھٹلایا پھر میرا کیسا عذاب ہوا'),
(3652, 34, 46, 'کہہ دو میں تمہیں ایک بات نصیحت کرتا ہوں کہ تم الله کے لیے دو دو ایک ایک کھڑے ہو کر غور کرو کہ تمہارے اس ساتھی کو جنون تو نہیں ہے وہ تمہیں ایک سخت عذاب آنے سے پہلے ڈرانے والا ہے'),
(3653, 34, 47, 'کہہ دو اس پر جو اجرات میں نے تم سے مانگی ہو وہ تمہارے ہی پاس رہے میری مزدوری تو الله ہی پر ہے اوروہ ہر چیز پر گواہ ہے'),
(3654, 34, 48, 'کہہ دو میرا رب سچا دین برسا رہا ہے اور وہ چھپی ہوئی چیزوں کو خوب جانتا ہے'),
(3655, 34, 49, 'کہہ دو حق آ گیا ہے اور جھوٹے معبود نہ پہلی بار پیدا کرتے ہیں اور نہ دوبارہ پیدا کریں گے'),
(3656, 34, 50, 'کہہ دو اگر میں غلط راستہ پر ہوں تو میری غلطی کا وبال مجھ ہی پر ہوگا اور اگر میں سیدھی راہ پر ہو ں تو ا سلیے کہ میرا رب میری طرف وحی کرتا ہے بے شک وہ سننے والا قریب ہے'),
(3657, 34, 51, 'اورکاش آپ دیکھیں جب کہ وہ گھبرائےہوئے ہوں گے پس نہ بچ سکیں گے اور پا س ہی سے پکڑ لیے جائیں گے'),
(3658, 34, 52, 'اور کہیں گے ہم اس (قرآن) پرایمان لے آئے ہیں اور اتنی دور سے (ایمان کا) ان کے ہاتھ آناکہاں ممکن ہے'),
(3659, 34, 53, 'حالانکہ پہلے تو اس کا انکار کرتے رہے اور بے تحقیق باتیں دور ہی دور سے ہانکا کرتے تھے'),
(3660, 34, 54, 'اور ان میں اور ان کی خواہش میں آڑ کر دی جائے گی جیسا کہ ان کے ہم خیال لوگوں کے ساتھ اس سے پہلے کیا گیا بے شک وہ بھی حیرت انگیز شک میں پڑے ہوئے تھے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'سب تعریف الله کے لیے ہے جو آسمانوں اور زمین کا بنانے والا ہے فرشتوں کو رسول بنانے والا ہے جن کے دو دو تین تین چار چار پر ہیں وہ پیدائش میں جو چاہے زیادہ کر دیتا ہے بے شک الله ہر چیز پر قادر ہے'),
(3662, 35, 2, 'الله بندوں کے لیے جو رحمت کھولتا ہے اسے کوئی بند نہیں کر سکتا اور جسے وہ بند کر دے تو اس کے بعد کوئی کھولنے والا نہیں اور وہ زبردست حکمت والا ہے'),
(3663, 35, 3, 'اے لوگو الله کے اس احسان کو یاد کرو جو تم پر ہے بھلا الله کے سوا کوئی اور بھی خالق ہے جو تمہیں آسمان اور زمین سے روزی دیتا ہو اس کے سوا اور کوئی معبود نہیں پھر کہاں الٹے جا رہے ہو'),
(3664, 35, 4, 'اور اگر وہ آپ کو جھٹلائیں تو آپ سے پہلے بھی کئی رسول جھٹلائے گئے اور الله ہی کی طرف سب کام لوٹائے جاتے ہیں'),
(3665, 35, 5, 'اے لوگو بے شک الله کا وعدہ سچا ہے پھر تمہیں دنیا کی زندگی دھوکے میں نہ ڈالے اور تمہیں الله کے بارے میں دھوکہ باز دھوکا نہ دے'),
(3666, 35, 6, 'بے شک شیطان تو تمہارا دشمن ہے سو تم بھی اسے دشمن سمجھو وہ تو اپنی جماعت کو بلاتا ہے تاکہ وہ دوزخیوں میں سے ہو جائیں'),
(3667, 35, 7, 'جن لوگوں نے انکار کیا ان کے لیے سخت عذاب ہے اور جو ایمان لائے اورنیک عمل کیے انہیں کے لیے بخشش اور بڑا اجر ہے'),
(3668, 35, 8, 'بھلا جس کے برے کام بھلے کر دکھائے ہوں پھر وہ ان کو اچھا بھی جانتا ہو (نیک کے برابر ہو سکتا ہے) پھرالله جس کو چاہتا ہے گمراہ کر تا ہے اور جسے چاہتاہے ہدایت کرتا ہے پھر آپ ان پر افسوس کھا کھا کر ہلاک نہ ہوجائیں کیوں کہ الله خوب جانتا ہے جو وہ کر رہے ہیں'),
(3669, 35, 9, 'اور الله ہی وہ ہے جوہوائیں چلاتا ہے پھروہ بادل اٹھاتی ہیں پھر ہم اسے مرے ہوئے شہروں کی طرف چلاتے ہیں پھر ہم اس سے زمین کو مرنے کے بعد زندہ کرتے ہیں اسی طرح دوبارہ اٹھایا جانا ہے'),
(3670, 35, 10, 'جو شخص عزت چاہتا ہو سو الله ہی کے لیے سب عزت ہے اسی کی طرف سب پاکیزہ باتیں چڑھتی ہیں اور نیک عمل اس کو بلند کرتا ہے اور جو لوگ بری تدبیریں کرتے ہیں انہی کے لیے سخت عذاب ہے اوران کی بری تدبیر ہی برباد ہو گی'),
(3671, 35, 11, 'اور الله ہی نے تمہیں مٹی سے پیدا کیا پھر نطفہ سے پھر تمہیں جوڑے بنایا اور کوئی مادہ حاملہ نہیں ہو تی اور نہ وہ جنتی ہے مگر اس کے علم سے اور نہ کوئی بڑی عمر والا عمر دیا جاتا ہے اور نہ اس کی عمر کم کی جاتی ہے مگر وہ کتاب میں درج ہے بے شک یہ بات الله پر آسان ہے'),
(3672, 35, 12, 'اور دو سمندر برا بر نہیں ہوتے یہ ایک میٹھا پیاس بجھانے والا ہے کہ ا سکا پینا خوشگوار ہے اور یہ دوسرا کھاری کڑوا ہے اور ہر ایک میں سے تم تازہ گوشت کھاتے ہو اور زیور نکالتے ہو جو تم پہنتے ہو اور تو جہازوں کو دیکھتا ہے کہ اس میں پانی کو پھاڑتے جاتے ہیں تاکہ تم اس کا فضل تلاش کرو اور تاکہ اس کا شکر کرو'),
(3673, 35, 13, 'وہ رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اور اسی نے سورج اور چاند کو کام میں لگا رکھا ہے ہر ایک وقت مقرر تک چل رہا ہے یہی الله تمہارا رب ہے اسی کی بادشاہی ہے اور جنہیں تم اس کے سوا پکارتے ہو وہ ایک گھٹلی کے چھلکے کے مالک نہیں'),
(3674, 35, 14, 'اگر تم انہیں پکارو تو وہ تمہاری پکار کو نہیں سنتے اور اگر وہ سن بھی لیں تو تمہیں جواب نہیں دیتے اور قیامت کے دن تمہارے شرک کا انکار کر دیں گے اور تمہیں خبر رکھنے والے کی طرح کوئی نہیں بتائے گا'),
(3675, 35, 15, 'اے لوگو تم الله کی طرف محتاج ہواور الله بے نیاز تعریف کیا ہوا ہے'),
(3676, 35, 16, 'اگر وہ چاہے تو تمہیں لے جائے اور نئی مخلوق لے آئے'),
(3677, 35, 17, 'اور یہ بات الله تعالیٰ پر کچھ مشکل نہیں'),
(3678, 35, 18, 'اور کوئی بوجھ اٹھانے والا دوسرے کا بوجھ نہیں اٹھائے گا اور اگر کوئی بوجھ والا اپنے بوجھ کی طرف بلائے گا تو اس کے بوجھ میں سے کچھ بھی اٹھایا نہ جائے گا اگرچہ قریبی رشتہ داری ہو بے شک آپ انہیں لوگوں کو ڈراتے ہیں جو بن دیکھے اپنے رب سے ڈرتے ہیں اور نماز قائم کرتے ہیں اور جو پاک ہوتا ہے سو وہ اپنے ہی لیے پا ک ہوتا ہے اور الله ہی کی طرف لوٹ کر جانا ہے'),
(3679, 35, 19, 'اور اندھا اور دیکھنے والا برابر نہیں ہے'),
(3680, 35, 20, 'اور نہ اندھیرے اور نہ روشنی'),
(3681, 35, 21, 'اور نہ سایہ اور نہ دھوپ'),
(3682, 35, 22, 'اور زندے اور مردے برابر نہیں ہیں بے شک الله سناتا ہے جسے چاہے اور آپ انہیں سنانے والے نہیں جو قبروں میں ہیں'),
(3683, 35, 23, 'نہیں ہیں آپ مگر ڈرانے والے'),
(3684, 35, 24, 'بے شک ہم نے آپ کو سچا دین دے کر خوشخبری اور ڈرانے والا بنا کر بھیجا ہے اور کوئی امت نہیں گزری مگر اس میں ایک ڈرانے والا گزر چکا ہے'),
(3685, 35, 25, 'اور اگر وہ آپ کو جھٹلائیں تو ان لوگوں نے بھی جھٹلایا ہے جو ان سے پہلے ہوئے ان کے پاس ان کے رسول واضح دلیلیں اور صحیفے اور کتاب روشن لے کر آئے'),
(3686, 35, 26, 'پھرمیں نے انہیں پکڑا جومنکر ہوئے پھر میرا عذاب کیسا ہوا'),
(3687, 35, 27, 'کیا تو نے نہیں دیکھا کہ الله ہی آسمان سے پانی اتارتا ہے پھر ہم اس کےذریعے سے پھل نکالتے ہیں جن کے رنگ مختلف ہوتے ہیں اور پہاڑو ں میں مختلف رنگتو ں کے کچھ تو سفید اور کچھ سرخ اور بہت سیاہ بھی ہیں'),
(3688, 35, 28, 'اور اسی طرح آدمیوں اور زمین پر چلنے والے جانوروں اور چوپایوں کے بھی مختلف رنگ ہیں بے شک الله سے اس کے بندوں میں سے عالم ہی ڈرتے ہیں بے شک الله غالب بخشنے والا ہے'),
(3689, 35, 29, 'بے شک جو لوگ الله کی کتاب پڑھتے ہیں اور نماز قائم کرتے ہیں اور پوشیدہ اور ظاہر اس میں سے خرچ کرتے ہیں جو ہم نے انہیں دیا ہے وہ ایسی تجارت کے امیدوار ہیں کہ اس میں خسارہ نہیں'),
(3690, 35, 30, 'تاکہ الله انہیں ان کے اجر پورے دے اور انہیں اپنے فضل سے زیادہ دےبے شک وہ بخشنے والا قدردان ہے'),
(3691, 35, 31, 'اور وہ کتاب جو ہم نے آپ کی طرف وحی کی ہے وہ ٹھیک ہے ا س کتاب کی تصدیق کرنے والی ہے جو اس سے پہلے آ چکی بے شک الله اپنے بندو ں سے باخبر دیکھنے والا ہے'),
(3692, 35, 32, 'پھر ہم نے اپنی کتاب کا ان کو وارث بنایا جنہیں ہم نے اپنے بندوں میں سے چن لیا پس بعض ان میں سے اپنے نفس پرظلم کرنے والے ہیں اور بعض ان میں سے میانہ رو ہیں اور بعض ان میں سے الله کے حکم سے نیکیوں میں پیش قدمی کرنے والے ہیں یہی تو الله کا بڑا فضل ہے'),
(3693, 35, 33, 'ہمیشہ رہنے کے باغ ہیں وہ ان میں داخل ہوں گے انہیں وہاں سونے کے کنگن اورموتی پہنائیں جائیں گے اور اس میں ان کا لباس ریشم کا ہوگا'),
(3694, 35, 34, 'اور وہ کہیں گے الله کا شکر ہے جس نے ہم سے غم دور کر دیا بے شک ہمارا رب بخشنے والا قدردان ہے'),
(3695, 35, 35, 'وہ جس نے اپنے فضل سے ہمیں سدا رہنے کی جگہ میں اتارا جہاں ہمیں نہ کوئی رنج پہنچتا ہے اور نہ کوئی تکلیف'),
(3696, 35, 36, 'اورجو منکر ہو گئے ان کے لیے دوزخ کی آگ ہے نہ ان پر قضا آئے گی کہ مرجائیں اور نہ ہی ان سے اس کا عذاب ہلکا کیا جائے گا اس طرح ہم ہر ناشکرے کو سزا دیا کرتے ہیں'),
(3697, 35, 37, 'اوروہ اس میں چلائیں گے کہ اے ہمارے رب ہمیں نکال ہم نیک کام کریں برخلاف ان کاموں کے جو کیا کرتے تھے کیا ہم نےتمہیں اتنی عمر نہیں دی تھی جس میں سمجھنے والا سمجھ سکتا تھا اور تمہارے پاس ڈرانے والا آیا تھا پس مزہ چکھو پس ظالموں کا کوئی مددگار نہیں'),
(3698, 35, 38, 'بے شک الله آسمانوں اور زمین کے غیب جانتا ہے بے شک وہ سینوں کے بھید خوب جانتا ہے'),
(3699, 35, 39, 'وہی ہے جس نے تمہیں زمین میں قائم مقام بنایا پس جو کفر کرے گا اس کے کفر کا وبال اسی پر ہوگا اور کافروں کا کفر ان کے رب کے ہاں ناراضگی کے سوا اور کچھ نہیں زیادہ کرتا'),
(3700, 35, 40, 'کہہ دو کیا تم نے اپنے ان معبودو ں کو بھی دیکھا جنہیں تم الله کےسوا پکارتے ہو وہ مجھے دکھاؤ کہ انہوں نے زمین میں کیاپیداکیا ہے یا ان کا کچھ حصہ آسمانوں میں بھی ہے یا انہیں ہم نے کوئی کتاب دی ہے کہ وہ اس کی سند رکھتے ہیں (نہیں) بلکہ ظالم آپس میں ایک دوسرے کو دھوکہ دیتے ہیں'),
(3701, 35, 41, 'بے شک الله ہی آسمانوں اور زمین کو تھامے ہوئے ہے اس سے کہ وہ اپنی جگہ سے ٹل جائیں اور اگر وہ دونوں اپنی جگہ سے ہٹ جائیں تو ان کو کوئی بھی اس کے بعد روک نہیں سکتا بے شک وہ بردبار بخشنے والا ہے'),
(3702, 35, 42, 'اوروہ الله کی پختہ قسمیں کھاتے تھے اگر ان کے پاس کوئی بھی ڈرانے والا آیا تو ہر ایک امت سے زیادہ ہدایت پر ہوں گے پھر جب ان کے پاس ڈرانے والا آیا تو اس سے ان کو اور بھی نفرت بڑھ گئی'),
(3703, 35, 43, 'کہ ملک میں سرکشی اور بری تدبیریں کرنے لگ گئے اور بری تدبیر تو تدبیرکرنے والے ہی پر لٹ پڑتی ہے پھر کیا وہ اسی برتاؤ کے منتظر ہیں جو پہلے لوگوں سے برتا گیا پس تو الله کے قانون میں کوئی تبدیلی نہیں پائے گا اورتو الله کے قانوں میں کوئی تغیر نہیں پائے گا'),
(3704, 35, 44, 'کیا انہوں نے زمین میں سیر نہیں کی کہ وہ دیکھتے ان لوگو ں کا کیسا برا انجام ہوا جو ان سے پہلے تھے اور وہ ان سے زیادہ طاقتور تھے اور الله ایسا نہیں ہے کہ اسے کوئی چیز آسمانوں میں اور نہ زمین میں عاجز کر دے بے شک وہ جاننے والا قدرت والا ہے'),
(3705, 35, 45, 'اور اگر الله لوگوں سے ان کے اعمال پر گرفت کرتا تو سطح زمین پر کوئی جاندار نہ چھوڑتا لیکن وہ انہیں ایک وقت مقرر تک ڈھیل دیتا ہے پس جب ا نکا وقت مقرر آجائے گا تو بے شک الله اپنے بندوں کو خوب دیکھ رہا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یسۤ'),
(3707, 36, 2, 'قرآن حکمت والے کی قم ہے'),
(3708, 36, 3, 'بے شک آپ رسولوں میں سے ہیں'),
(3709, 36, 4, 'سیدھے راستے پر'),
(3710, 36, 5, 'غالب رحمت والے کا اتارا ہوا ہے'),
(3711, 36, 6, 'تاکہ آپ اس قوم کو ڈرائیں جن کے باپ دادا نہیں ڈرائے گئے سو وہ غافل ہیں'),
(3712, 36, 7, 'ان میں سے اکثر پر خدا کافرمان پورا ہو چکا ہے پس وہ ایمان نہیں لائیں گے'),
(3713, 36, 8, 'بے شک ہم نے ان کی گردنوں میں طوق ڈال دیے ہیں پس وہ ٹھوڑیوں تک ہیں سو وہ اوپر کو سر اٹھائے ہوئے ہیں'),
(3714, 36, 9, 'اور ہم نے ان کے سامنے ایک دیواربنادی ہے اور ان کے پیچھے بھی ایک دیوارہے پھر ہم نے انہیں ڈھانک دیا ہے کہ وہ دیکھ نہیں سکتے'),
(3715, 36, 10, 'اور ان پر برابر ہے کیا آپ ان کو ڈرائیں یا نہ ڈرائیں وہ ایمان نہیں لائیں گے'),
(3716, 36, 11, 'بے شک آپ اسی کو ڈرا سکتے ہیں جو نصیحت کی پیروی کرے اوربن دیکھے رحمان سے ڈرے پس خوشخبری دے دو اس کو بخشش اوراجر کی جو عزت والا ہے'),
(3717, 36, 12, 'بے شک ہم ہی مردوں کو زندہ کریں گے اور جو انھوں نے آگے بھیجا اور جو پیچھے چھوڑا اس کو لکھتے ہیں اور ہم نے ہر چیز کو کتاب واضح (لوح محفوظ) میں محفوظ کر رکھا ہے'),
(3718, 36, 13, 'اور ان سے بستی والوں کا حال مشال کے طور پر بیان کر جب کہ ان کے پاس رسول آئے'),
(3719, 36, 14, 'جب ہم نے ان کے پاس دو کو بھیجا انھوں نے ان کو جھٹلایا پھر ہم نے تیسرے سے مدد کی پھر انہوں نے کہا ہم تمہاری طرف بھیجے گئے ہیں'),
(3720, 36, 15, 'انہوں نے کہا تم کچھ اور نہیں ہو مگر ہماری طرح انسان ہو اور رحمان نے کوئی چیز نہیں اتاری تم اور کچھ نہیں ہو مگر جھوٹ بول رہے ہو'),
(3721, 36, 16, 'انہوں نے کہا ہمارا رب جانتا ہے کہ ہم تمہاری طرف بھیجے ہوئے ہیں'),
(3722, 36, 17, 'اورہمارے ذمے کھلم کھلا پہنچا دینا ہی ہے'),
(3723, 36, 18, 'انہوں نے کہا ہم نے تو تمہیں منحوس سمجھا ہے اگر تم باز نہ آؤ گے توہم تمہیں سنگسار کر دیں گے اور تمہیں ہمارے ہاتھ سے ضرور دردناک عذاب پہنچے گا'),
(3724, 36, 19, 'انہوں نے کہا تمہاری نحوست تو تمہارے ساتھ ہے کیا اگر تمہیں نصیحت کی جائے (تو اسے نحوست سمجھتے ہو) بلکہ تم حد سے بڑھنے والے ہو'),
(3725, 36, 20, 'اور شہر کے پرلے کنارے سے ایک آدمی دوڑتا ہوا آیا کہا اے میری قوم رسولوں کی پیروی کرو'),
(3726, 36, 21, 'ان کی پیروی کرو جو تم سےکوئی اجر نہیں مانگتے اوروہ ہدایت پانے والے ہیں'),
(3727, 36, 22, 'اور میرے لیے کیا ہے کہ میں اس کی عبادت نہ کروں جس نے مجھے پیدا کیا ہے اور اسی کی طرف تم لوٹائے جاؤ گے'),
(3728, 36, 23, 'کیا میں اس کے سوا اوروں کو معبود بناؤں کہ اگر رحمان مجھے تکلیف دینے کا ارادہ کرے تو ان کی سفارش کچھ بھی میرے کام نہ آئے اور نہ وہ مجھے چھڑا سکیں'),
(3729, 36, 24, 'بے شک تب میں صریح گمراہی میں ہوں گا'),
(3730, 36, 25, 'بے شک میں تمہارے رب پر ایمان لایا پس میری بات سنو'),
(3731, 36, 26, 'کہا گیا جنت میں داخل ہو جا اس نے کہا اے کاش! میری قوم بھی جان لیتی'),
(3732, 36, 27, 'کہ میرے رب نے مجھے بخش دیا اور مجھے عزت والوں میں کر دیا'),
(3733, 36, 28, 'اور ہم نے اس کی قوم پر اس کے بعد کوئی فوج آسمان سے نہ اتاری اور نہ ہم اتارنے والے تھے'),
(3734, 36, 29, 'صرف ایک ہی چیخ تھی کہ جس سے وہ بجھ کر رہ گئے'),
(3735, 36, 30, 'کیا افسوس ہے بندوں پر ان کے پاس ایساکوئی بھی رسول نہیں آیا جس سے انہوں نے ہنسی نہ کی ہو'),
(3736, 36, 31, 'کیا یہ نہیں دیکھ چکے کہ ہم نے ان سے پہلے کتنی قوموں کو ہلاک کر دیا وہ ان کے پاس لوٹ کر نہیں آئے'),
(3737, 36, 32, 'اور سب کے سب ہمارے پاس حاضر ہیں'),
(3738, 36, 33, 'اور ان کے لیے خشک زمین بھی ایک نشانی ہے جسے ہم نے زندہ کیا اور اس سے اناج نکالا جس سے وہ کھاتے ہیں'),
(3739, 36, 34, 'اور اس میں ہم نے کھجوروں اور انگوروں کے باغ بنائے اور ان میں چشمے جاری کیے'),
(3740, 36, 35, 'تاکہ وہ اس کے پھل کھائیں اور یہ چیزیں ان کے ہاتھوں کی بنائی ہوئی نہیں ہیں پھر کیوں شکر نہیں کرتے'),
(3741, 36, 36, 'وہ ذات پاک ہے جس نے زمین سے اگنے والی چیزوں کوگوناگوں بنایا اور خود ان میں سے بھی اور ان چیزوں میں سے بھی جنہیں وہ نہیں جانتے'),
(3742, 36, 37, 'اور ان کے لیے رات بھی ایک نشانی ہے کہ ہم اس کے اوپرسے دن کو اتار دیتے ہیں پھر ناگہاں وہ اندھیرے میں رہ جاتے ہیں'),
(3743, 36, 38, 'اور سورج اپنے ٹھکانے کی طرف چلتا رہتا ہے یہ زبردست خبردار کا اندازہ کیا ہوا ہے'),
(3744, 36, 39, 'اور ہم نے چاند کی منزلیں مقرر کر دی ہیں یہاں تک کہ پرانی ٹہنی کی طرح ہوجاتا ہے'),
(3745, 36, 40, 'نہ سورج کی مجال ہے ہ چاند کو جا پکڑے اور نہ رات ہی دن سے پہلے آ سکتی ہے اور ہر ایک ایک آسمان میں تیرتا پھرتا ہے'),
(3746, 36, 41, 'اور ان کے لیے یہ بھی نشانی ہے ہم نے ان کی نسل کوبھری کشتی میں سوار کیا'),
(3747, 36, 42, 'اور ان کے لیے اسی طرح کی اور بھی چیزیں بنائی ہیں جن پر وہ سوار ہوتے ہیں'),
(3748, 36, 43, 'اور اگر ہم چاہتے تو انہیں ڈبو دیتے پھر نہ ان کا کوئی فریاد رس ہوتا اور نہ وہ بچائے جاتے'),
(3749, 36, 44, 'مگر یہ ہماری مہربانی ہے اور انہیں ایک مدت تک فائدہ دینا ہے'),
(3750, 36, 45, 'اور جب ان سے کہا جاتا ہے کہ اپنے سامنے اور پیچھے آنے والے عذاب سے ڈرو تاکہ تم پر رحم کیا جائے'),
(3751, 36, 46, 'اوران کے پاس ان کے رب کی نشانیوں میں سے ایسی کوئی بھی نشانی نہیں آتی جس سے وہ منہ موڑ لیتے ہوں'),
(3752, 36, 47, 'اور جب ان سے کہا جاتا ہےکہ الله کے رزق میں سے کچھ خرچ کیا کرو تو کافر ایمانداروں سے کہتے ہیں کیا ہم اسے کھلائیں گےکہ اگر الله چاہتا تو خود اسے کھلا سکتا تھا تم جو ہو تو صاف گمراہی میں پڑے ہوئے ہو'),
(3753, 36, 48, 'اور کہتے ہیں یہ وعدہ کب ہوگا اگر تم سچے ہو'),
(3754, 36, 49, 'وہ صرف ایک چیخ ہی کا انتظار کر رہے ہیں جو انہیں آ لے گی اور وہ آپس میں جھگڑ رہے ہوں گے'),
(3755, 36, 50, 'پس نہ تو وہ وصیت کر سکیں گے اور نہ اپنے گھر والوں کی طرف واپس جا سکیں گے'),
(3756, 36, 51, 'اور صور پھونکا جائے گا تو وہ فوراً اپنی قبروں سے نکل کر اپنے رب کی طرف دوڑے چلے آئیں گے'),
(3757, 36, 52, 'کہیں گے ہائے افسوس کس نے ہمیں ہماری خوابگاہ سے اٹھایا یہی ہے جو رحمان نے وعدہ کیا تھا اور رسولوں نے سچ کہا تھا'),
(3758, 36, 53, 'وہ تو صرف ایک ہی زور کی آواز ہو گی پھر وہ سب ہمارے سامنے حاضر کیے جائیں گے'),
(3759, 36, 54, 'پھر اس دن کسی پرکچھ بھی ظلم نہ کیا جائے گا اورتم اسی کا بدلہ پاؤ گے جو کیا کرتے تھے'),
(3760, 36, 55, 'بے شک بہشتی اس دن مزہ سے دل بہلا رہے ہوں گے'),
(3761, 36, 56, 'وہ اوران کی بیویاں سایوں میں تختوں پر تکیہ لگائے ہوئے بیٹھے ہوں گے'),
(3762, 36, 57, 'ان کے لیے وہاں میوہ ہوگا اور انہیں ملے گا جو وہ مانگیں گے'),
(3763, 36, 58, 'پروردگار نہایت رحم والے کی طرف سے انہیں سلام فرمایا جاوے گا'),
(3764, 36, 59, 'اے مجرمو! آج الگ ہو جاؤ'),
(3765, 36, 60, 'اے آدم کی اولاد! کیا میں نے تمہیں تاکید نہ کر دی تھی کہ شیطان کی عبادت نہ کرنا کیونکہ وہ تمہارا صریح دشمن ہے'),
(3766, 36, 61, 'اور یہ کہ میری ہی عبادت کرنا یہ سیدھا راستہ ہے'),
(3767, 36, 62, 'اور البتہ اس نے تم میں سے بہت لوگوں کو گمراہ کیا تھا کیا پس تم نہیں سمجھتے تھے'),
(3768, 36, 63, 'یہی دوزخ ہے جس کا تم سے وعدہ کیا جاتا تھا'),
(3769, 36, 64, 'آج اس میں داخل ہو جاؤ اس کے بدلے جوتم کفر کیا کرتے تھے'),
(3770, 36, 65, 'آج ہم ان کے مونہوں پر مہر لگا دیں گے اور ہمارے ساتھ ان کے ہاتھ بولیں گے اور ان کے پاؤں شہادت دیں گے اس پر جو وہ کیا کرتے تھے'),
(3771, 36, 66, 'اور اگر ہم چاہیں تو ان کی آنکھیں مٹاڈالیں پس وہ راستہ کی طرف دوڑیں پھر وہ کیوں کر دیکھ سکیں'),
(3772, 36, 67, 'اور اگر ہم چاہیں تو ان کی صورتیں ان جگہوں پر مسخ کر دیں پس نہ وہ آگے چل سکیں او ر نہ ہی واپس لوٹ سکیں'),
(3773, 36, 68, 'اور ہم جس کی عمر زیادہ کرتے ہیں بناوٹ میں اسے الٹا گھٹاتے چلے جاتے ہیں کیا یہ لوگ نہیں سمجھتے'),
(3774, 36, 69, 'اور ہم نے نبی کو شعر نہیں سکھایا اور نہ یہ اس کے مناسب ہی تھا یہ تو صرف نصیحت اور واضح قرآن ہے'),
(3775, 36, 70, 'تاکہ جو زندہ ہے اسے ڈرائے اور کافروں پر الزام ثابت ہو جائے'),
(3776, 36, 71, 'کیا انہوں نے نہیں دیکھا کہ ہم نے ان کے لیے اپنے ہاتھوں سے چار پائے بنائے جن کے وہ مالک ہیں'),
(3777, 36, 72, 'اور انہیں ان کے بس میں کر دیا ہے پھر ان میں سے کسی پر چڑھتے ہیں اور کسی کو کھاتے ہیں'),
(3778, 36, 73, 'اور ان کے لیے ان میں اور بہت سے فائدے اور پینے کی چیزیں ہیں پھر کیوں شکر نہیں کرتے'),
(3779, 36, 74, 'اور الله کے سوا انہوں نے اور معبود بنا رکھے ہیں تاکہ وہ ان کی مدد کریں'),
(3780, 36, 75, 'وہ ان کی مدد نہیں کر سکیں گے اور وہ ان کے حق میں ایک فریق (مخالف) ہوں گے جو حاضر کیے جائیں گے'),
(3781, 36, 76, 'پھر آپ ان کی بات سے غمزدہ نہ ہوں بے شک ہم جانتے ہیں جو وہ چھپاتے ہیں اور جو ظاہر کرتے ہیں'),
(3782, 36, 77, 'کیا آدمی نہیں جانتاکہ ہم نے اسے منی کے ایک قطرے سے بنایا ہے پھر وہ کھلم کھلا دشمن بن کر جھگڑنے لگا'),
(3783, 36, 78, 'اور ہماری نسبت باتیں بنانے لگا اور اپنا پیدا ہونا بھول گیا کہنے لگا بوسیدہ ہڈیوں کو کون زندہ کر سکتا ہے'),
(3784, 36, 79, 'کہہ دوانہیں وہی زندہ کرے گا جس نے انہیں پہلی بار پیدا کیا تھا اور وہ سب کچھ بنانا جانتا ہے'),
(3785, 36, 80, 'وہ جس نے تمہارے لیے سبز درخت سے آگ پیدا کر دی کہ تم جھٹ پٹ اس سے آگ سلگا لیتے ہو'),
(3786, 36, 81, 'کیا وہ جس نے آسمانوں اور زمین کو بنا دیا اس پر قاد رنہیں کہ ان جیسے اور بنائے کیوں نہیں وہ بہت کچھ بنانے ولا ماہر ہے'),
(3787, 36, 82, 'اس کی تو یہ شان ہے کہ جب وہ کسی چیز کا ارادہ کرتا ہے تو اتنا ہی فرما دیتا ہے کہ ہو سو وہ ہو جاتی ہے'),
(3788, 36, 83, 'پس وہ ذات پاک ہے جس کے ہاتھ میں ہر چیز کا کامل اختیار ہے اور اسی کی طرف تم لوٹائے جاؤ گے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'صف باندھ کر کھڑے ہونے والو ں کی قسم ہے'),
(3790, 37, 2, 'پھر جھڑک کر ڈانٹنے والوں کی'),
(3791, 37, 3, 'پھر ذکر الہیٰ کے تلاوت کرنے والوں کی'),
(3792, 37, 4, 'البتہ تمہارا معبود ایک ہی ہے'),
(3793, 37, 5, 'آسمانوں اور زمین اور اس کے اندر کی سب چیزو ں کا اور مشرقوں کا رب ہے'),
(3794, 37, 6, 'ہم نے نیچے کے آسمان کو ستاروں سے سجایا ہے'),
(3795, 37, 7, 'اور اسے ہر ایک سرکش شیطان سے محفوظ رکھا ہے'),
(3796, 37, 8, 'وہ عالم بالا کی باتیں نہیں سن سکتے اور ان پر ہر طرف سے (انگارے) پھینکے جاتے ہیں'),
(3797, 37, 9, 'بھگانے کے لیے اور ان پر ہمیشہ کا عذاب ہے'),
(3798, 37, 10, 'مگر جو کوئی اچک لے جائے تو اس کے پیچھے دہکتا ہوا انگارہ پڑتا ہے'),
(3799, 37, 11, 'پس ان سے پوچھیئے کیا ان کا بنانا زیادہ مشکل ہے یا ان کا جنہیں ہم نے پیدا کیا ہے بے شک ہم نے انہیں لیسدار مٹی سے پیدا کیا ہے'),
