(3933, 37, 145, 'پھر ہم نے اسے میدان میں ڈال دیا اور وہ بیمار تھا'),
(3934, 37, 146, 'اور ہم نے اس پر ایک درخت بیلدار اگا دیا'),
(3935, 37, 147, 'اور ہم نے اس کو ایک لاکھ یا اس سے زیادہ لوگوں کے پاس بھیجا'),
(3936, 37, 148, 'پس وہ لوگ ایمان لائے پھر ہم نے انہیں ایک وقت تک فائدہ اٹھانے دیا'),
(3937, 37, 149, 'پس ان سے پوچھیئے کیا آپ کے رب کے لیے تو لڑکیا ں ہیں اور ان کے لیے لڑکے'),
(3938, 37, 150, 'کیا ہم نے فرشتوں کو عورتیں بنایا ہے اور وہ دیکھ رہے تھے'),
(3939, 37, 151, 'خبرادر! بے شک وہ اپنے جھوٹ سے کہتے ہیں'),
(3940, 37, 152, 'کہ الله کی اولاد ہے اور بے شک وہ جھوٹے ہیں'),
(3941, 37, 153, 'کیا اس نے بیٹیوں کو بیٹوں سے زیادہ پسند کیا ہے'),
(3942, 37, 154, 'تمہیں کیا ہوا کیسا فیصلہ کرتے ہو'),
(3943, 37, 155, 'پس کیا تم غور نہیں کرتے'),
(3944, 37, 156, 'یا تمہارے پاس کوئی واضح دلیل ہے'),
(3945, 37, 157, 'پس اپنی کتاب لے آؤ اگر تم سچے ہو'),
(3946, 37, 158, 'اور انہوں نے اس کے اور جنّوں کے درمیان رشتہ قائم کر دیا ہے اور جنوں کو معلوم ہے کہ وہ ضرور حاضر کیے جائیں گے'),
(3947, 37, 159, 'الله پا ک ہے ان باتو ں سے جو وہ بناتے ہیں'),
(3948, 37, 160, 'مگر الله کے خاص بندے'),
(3949, 37, 161, 'پس بے شک تم اور جنہیں تم پوجتے ہو'),
(3950, 37, 162, 'کسی کو گمراہ نہیں کر سکتے'),
(3951, 37, 163, 'مگر اسی کو جو دوزخ میں جانے والا ہے'),
(3952, 37, 164, 'اور ہم میں سے کوئی بھی ایسا نہیں کہ جس کے لیے ایک درجہ معین نہ ہو'),
(3953, 37, 165, 'اور بے شک ہم صف باندھے کھڑے رہنے والے ہیں'),
(3954, 37, 166, 'اور بے شک ہم ہی تسبیح کرنے والے ہیں'),
(3955, 37, 167, 'اور وہ تو کہا کرتے تھے'),
(3956, 37, 168, 'اگر ہمارے پاس پہلے لوگوں کی کتاب ہوتی'),
(3957, 37, 169, 'تو ہم الله کے خالص بندے ہوتے'),
(3958, 37, 170, 'پس انہوں نے اس کا انکار کیا سو وہ جان لیں گے'),
(3959, 37, 171, 'اور ہمارا حکم ہمارے بندوں کے حق میں جو رسول ہیں پہلے سے ہو چکا ہے'),
(3960, 37, 172, 'بے شک وہی مدد دیئے جائیں گے'),
(3961, 37, 173, 'اور بےشک ہمارا لشکر ہی غالب رہے گا'),
(3962, 37, 174, 'پھر آپ ان سے کچھ مدت تک منہ موڑ لیجیئے'),
(3963, 37, 175, 'اور انہیں دیکھتے رہیئے پس وہ بھی دیکھ لیں گے'),
(3964, 37, 176, 'کیا وہ ہمارا عذاب جلدی مانگتے ہیں'),
(3965, 37, 177, 'پس جب ان کے میدان میں آ نازل ہوگا تو کیسی بری صبح ہو گی ان کی جو ڈرائے گئے'),
(3966, 37, 178, 'اور ان سے کچھ مدت منہ موڑ لیجیئے'),
(3967, 37, 179, 'اور دیکھتے رہیئےسو وہ بھی دیکھ لیں گے'),
(3968, 37, 180, 'آپ کا رب پاک ہے عزت کا مالک ان باتوں سے جو وہ بیان کرتے ہیں'),
(3969, 37, 181, 'اور رسولوں پر سلام ہو'),
(3970, 37, 182, 'اور سب تعریف الله کے لیے ہے جو سارے جہان کا رب ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'قرآن کی قسم ہے جو سراسر نصیحت ہے'),
(3972, 38, 2, 'بلکہ جو لوگ منکر ہیں وہ محض تکبر اور مخالفت میں پڑے ہیں'),
(3973, 38, 3, 'ہم نے ان سے پہلے کتنی قومیں ہلاک کر دی ہیں سو انہوں نے بڑی ہائے پکار کی اور وہ وقت خلاصی کا نہ تھا'),
(3974, 38, 4, 'اور انہوں نے تعجب کیا کہ ان کے پاس انہیں میں سے ڈرانے والا آیا اور منکروں نے کہا کہ یہ تو ایک بڑا جھوٹا جادوگر ہے'),
(3975, 38, 5, 'کیا اس نے کئی معبودوں کو صرف ایک معبود بنایا ہے بے شک یہ بڑی عجیب بات ہے'),
(3976, 38, 6, 'اور ان میں سے سردار یہ کہتے ہوئے چل پڑے کہ چلو اور اپنے معبودوں پر جمے رہو بے شک اس میں کچھ غرض ہے'),
(3977, 38, 7, 'ہم نے یہ بات اپنے پچھلے دین میں نہیں سنی یہ تو ایک بنائی ہوئی بات ہے'),
(3978, 38, 8, 'کیا ہم میں سے اسی پر نصیحت اتاری گئی بلکہ انہیں تو میری نصیحت میں بھی شک ہے بلکہ انہوں نے میرا ابھی عذاب بھی نہیں چکھا'),
(3979, 38, 9, 'کیا ان کے پاس تیرے خدائے غالب فیاض کی رحمت کے خزانے ہیں'),
(3980, 38, 10, 'یا انہیں آسمانوں اور زمین کی حکومت اور جو کچھ ان کے درمیان ہے حاصل ہے تو سیڑھیاں لگا کر چڑھ جائیں'),
(3981, 38, 11, 'وہاں ان کے لشکر شکست پائیں گے'),
(3982, 38, 12, 'ان سے پہلے قوم نوح اور عاد اور میخوں والا فرعون'),
(3983, 38, 13, 'اور ثمود اور لوط کی قوم اور بن والے بھی جھٹلا چکے ہیں یہی وہ لشکر ہیں'),
(3984, 38, 14, 'ان سب نے رسولوں کو جھٹلایا تھا پس میرا عذاب آ موجود ہوا'),
(3985, 38, 15, 'اور یہ ایک ہی چیخ کے منتظر ہیں جسے کچھ دیر نہیں لگے گی'),
(3986, 38, 16, 'اور کہتے ہیں اے رب ہمارے! ہمارا حصہ ہمیں حساب کے دن سے پہلے ہی دے دے'),
(3987, 38, 17, 'ان کی اِن باتوں پر صبر کر اور ہمارے بندے داؤد کو یاد کر جو بڑا طاقتور تھا بے شک وہ رجوع کرنے والا تھا'),
(3988, 38, 18, 'بے شک ہم نے پہاڑوں کو اس کے تابع کر دیا تھا کہ وہ شام اور صبح کو تسبیح کرتے تھے'),
(3989, 38, 19, 'اور پرندوں کو بھی جو جمع ہوجاتے تھے ہر ایک اس کے تابع تھا'),
(3990, 38, 20, 'اورہم نے اس کی سلطنت کو مضبوھ کر دیا تھا اور ہم نے اسے نبوت دی تھی اور مقدمات کے فیصلے کرنے کا سلیقہ (دیا تھا)'),
(3991, 38, 21, 'اور کیا آپ کو دو جھگڑنے والوں کی خبر بھی پہنچی جب وہ عبادت خانہ کی دیوار پھاند کر آئے'),
(3992, 38, 22, 'جب وہ داؤد کے پاس آئے تو وہ ان سے گھبرایا کہا ڈر نہیں دو جھگڑنے والے ہیں ایک نے دوسرے پر زیادتی کی ہے پس آپ ہمارے درمیا ن انصاف کا فیصلہ کیجیئے اور بات کو دور نہ ڈالیئے اور ہمیں سیدھی راہ پر چلائیے'),
(3993, 38, 23, 'بے شک یہ میرا بھائی ہے اس کے پاس ننانوے دنبیاں ہیں اور میرے پاس صرف ایک دنبی ہے پس اس نے کہا مجھے وہ بھی دے دے اور اس نے مجھے گفتگو میں دبا لیا ہے'),
(3994, 38, 24, 'کہا البتہ اس نے تجھ پر ظلم کیا جو تیری دنبی کو اپنی دنبیوں میں ملانے کا سوال کیا گور اکثر شریک ایک دوسرے پر زیادتی کیا کرتے ہیں مگر جو ایماندار ہیں اور انہوں نے نیک کام بھی کیے اور وہ بہت ہی کم ہیں اور داؤد سمجھ گیا کہ ہم نے اسے آزمایا ہے پھر اس نے اپنے رب سے معافی مانگی اور سجدہ میں گر پڑا اور توبہ کی'),
(3995, 38, 25, 'پھر ہم نے اس کی یہ غلطی معاف کر دی اور اس کے لیے ہمارے ہاں مرتبہ اور اچھا ٹھکانہ ہے'),
(3996, 38, 26, 'اے داؤد! ہم نے تجھے زمین میں بادشاہ بنایا ہے پس تم لوگو ں میں انصاف سے فیصلہ کیا کرو اور نفس کی خواہش کی پیروی نہ کرو کہ وہ تمہیں الله کی راہ سے ہٹا دے گی بے شک جو الله کی راہ سے گمراہ ہوتے ہیں ان کے لیے سخت عذاب ہے اس لیے کہ وہ حساب کے دن کوبھول گئے'),
(3997, 38, 27, 'اور ہم نے آسمان اور زمین کو اور جو کچھ ان کے بیچ میں ہے بیکار تو پیدا نہیں کیا یہ تو ان کا خیال ہے جو کافر ہیں پھرکافروں کے لیے ہلاکت ہے جو آگ ہے'),
(3998, 38, 28, 'کیا ہم کردیں گے ان کو جو ایمان لائے اور نیک کام کیے ان کی طرح جو زمین میں فساد کرتے ہیں یا ہم پرہیز گاروں کو بدکاروں کی طرح کر دیں گے'),
(3999, 38, 29, 'ایک کتاب ہے جو ہم نے آپ کی طرف نازل کی بڑی برکت والی تاکہ وہ اس کی آیتوں میں غور کریں اور تاکہ عقلمند نصحیت حاصل کریں'),
(4000, 38, 30, 'اور ہم نے داؤد کو سلیمان عطا کیا کیسا اچھا بندہ تھا بےشک وہ رجوع کرنے والا تھا'),
(4001, 38, 31, 'جب اس کے سامنے شام کے وقت تیز رو گھوڑے حاضر کیے گئے'),
(4002, 38, 32, 'تو کہا میں نے مال کی محبت کو یاد الہیٰ سے عزیز سمجھا یہاں تک کہ آفتاب غروب ہو گیا'),
(4003, 38, 33, 'ان کو میرے پاس لوٹا لاؤ پس پنڈلیوں اور گردنوں پر (تلوار) پھیرنے لگا'),
(4004, 38, 34, 'اور ہم نے سلیمان کو آزمایا تھا اور اس کی کرسی پر ایک دھڑ ڈال دیا تھا پھر وہ رجوع ہوا'),
(4005, 38, 35, 'کہا اے میرے رب! مجھے معاف کر اور مجھے ایسی حکومت عنایت فرما کہ کسی کو میرے بعد شایاں نہ ہو بے شک تو بہت بڑا عنایت کرنے والا ہے'),
(4006, 38, 36, 'پھر ہم نے ہوا کو اس کے تابع کر دیا کہ وہ اس کے حکم سے بڑی نرمی سے چلتی تھی جہاں اسے پہنچنا ہوتا تھا'),
(4007, 38, 37, 'اور شیطان کو جو سب معمار اور غوطہ زن تھے'),
(4008, 38, 38, 'اور دوسروں کو جو زنجیروں میں جکڑے ہوئے تھے'),
(4009, 38, 39, 'یہ ہماری بخشش ہے پس احسان کر یا اپنے پاس رکھ کوئی حساب نہیں'),
(4010, 38, 40, 'اور البتہ (سلیمان) کے لیے ہمارے پاس مرتبہ اورعمدہ مقام ہے'),
(4011, 38, 41, 'اور ہمارے بندے ایوب کو یاد کر جب اس نے اپنے رب کو پکارا کہ مجھے شیطان نے تکلیف اور عذاب پہنچایا ہے'),
(4012, 38, 42, 'اپنا پاؤں (زمین پر مار) یہ ٹھنڈا چشمہ نہانے اور پینے کو ہے'),
(4013, 38, 43, 'اور ہم نے ان کو ان کے اہل و عیال اور کتنے ہی اور بھی اپنی مہربانی سے عنایت فرمائے اور عقلمندوں کے لیے نصیحت ہے'),
(4014, 38, 44, 'اور اپنے ہاتھ میں جھاڑو کا مٹھا لے کر مارے اور قسم نہ توڑ بے شک ہم نے ایوب کو صابر پایا وہ بڑے اچھے بندے الله کی طرف رجوع کرنے والے تھے'),
(4015, 38, 45, 'اور ہمارے بندوں ابراھیم اور اسحاق اور یعقوب کو یاد کر جو ہاتھو ں اور آنکھوں والے تھے'),
(4016, 38, 46, 'بے شک ہم نے انہیں ایک خاص فضیلت دی یعنی ذکر آخرت کے لیے چن لیا تھا'),
(4017, 38, 47, 'اور بے شک وہ ہمارے نزدیک برگزیدہ بندوں میں سے تھے'),
(4018, 38, 48, 'اور اسماعیل اور الیسع اور ذوالکفل کو بھی یاد کر اور یہ سب نیک لوگو ں میں سے تھے'),
(4019, 38, 49, 'یہ نصیحت ہے اور بے شک پرہیز گاروں کے لئے اچھا ٹھکانا ہے'),
(4020, 38, 50, 'ہمیشہ رہنے کے باغ ہیں ان کے لئے ان کے دروازے کھولے جائیں گے'),
(4021, 38, 51, 'وہاں تکیہ لگا کر بیٹھیں گے وہاں بہت سے میوے اور شراب طلب کریں گے'),
(4022, 38, 52, 'اور ان کے پاس نیچی نگاہ والی ہم عمر عورتیں ہوں گی'),
(4023, 38, 53, 'یہی ہے جس کا تم سے حساب کے دن کے لیے وعدہ کیا جاتا ہے'),
(4024, 38, 54, 'بے شک یہ ہمارا رزق ہے جو کبھی ختم نہیں ہوگا'),
(4025, 38, 55, 'یہی بات ہے اور بے شک سرکشوں کے لیے برا ٹھکانا ہے'),
(4026, 38, 56, 'یعنی دوزخ جس میں وہ گریں گے پس وہ کیسی بری جگہ ہے'),
(4027, 38, 57, 'یہ ہے پھر وہ اس کو چکھیں جو کھولتا ہوا پانی اور پیپ ہے'),
(4028, 38, 58, 'اور اس کی شکل اور بھی کئی طرح کی چیزیں ہوں گی'),
(4029, 38, 59, 'یہ ایک جماعت ہے جو تمہارے ساتھ دوزخ میں داخل ہونے والی ہے (متبوع کہیں گے) ان پر خدا کی مار یہ بھی دوزخ ہی میں آ رہے ہیں'),
(4030, 38, 60, '(تابع ہونے والے) کہیں گے بلکہ تم پر خدا کی مار تم ہی تو اس بلا کو ہمارے سامنے لائے جو بہت ہی برا ٹھکانا ہے'),
(4031, 38, 61, 'تابع کہیں گے اے ہمارے رب! جو اس بلا کو آگے لایا اسے آگ میں دگنا عذاب دے'),
(4032, 38, 62, 'اور کہیں گے کہ جن لوگوں کو ہم دنیا میں برا سمجھتے تھے ہمیں دکھائی کیوں نہیں دیتے'),
(4033, 38, 63, 'کیا ہم ان سے (ناحق) تمسخر کرتے تھے یا ان سے ہماری نگاہیں پھر گئی ہیں'),
(4034, 38, 64, 'بے شک یہ دوزخیوں کا آپس میں جھگڑنا بالکل سچی بات ہے'),
(4035, 38, 65, 'کہہ دو میں تو ایک ڈرانے والا ہوں اور الله کے سوا کوئی اور معبود نہیں ہے ایک ہے بڑا غالب'),
(4036, 38, 66, 'آسمانوں اور زمین کا پرودگار اورجو کچھ ان کے درمیان ہے غالب بڑا معاف کرنے والا ہے'),
(4037, 38, 67, 'کہہ دو یہ ایک بڑی خبر ہے'),
(4038, 38, 68, 'تم اس سے منہ پھیرنے والے ہو'),
(4039, 38, 69, 'مجھے فرشتو ں کے متعلق کوئی علم نہیں تھا جب کہ وہ جھگڑ رہے تھے'),
(4040, 38, 70, 'مجھے تو یہی وحی کیا گیا ہے کہ میں تمہیں صاف صاف ڈراؤں'),
(4041, 38, 71, 'جب تیرے رب نے فرشتوں سے کہا کہ میں ایک انسان مٹی سے بنانے والا ہوں'),
(4042, 38, 72, 'پھرجب میں اسے پورے طور پر بنا لوں اور اس میں اپنی رو ح پھونک دوں تو اس کے لیے سجدہ میں گر پڑنا'),
(4043, 38, 73, 'پھر سب کے سب فرشتوں نے سجدہ کیا'),
(4044, 38, 74, 'مگر ابلیس نے نہ کیا تکبر کیا اور کافروں میں سے ہو گیا'),
(4045, 38, 75, 'فرمایا اے ابلیس! تمہیں اس کےے سجدہ کرنے سے کس نے منع کیا جسے میں نے اپنے ہاتھوں سے بنایا کیا تو نے تکبر کیا یا تو بڑوں میں سے تھا'),
(4046, 38, 76, 'اس نے عرض کی میں اس سے بہتر ہوں مجھے تو نے آگ سے بنایااور اسے مٹی سے بنایا'),
(4047, 38, 77, 'فرمایا پھر تو یہاں سے نکل جاکیوں نکہ تو راندہ گیا ہے'),
(4048, 38, 78, 'اور تجھ پر قیامت تک میری لعنت ہے'),
(4049, 38, 79, 'عرض کی اے میرے رب! پھر مجھے مردوں کے زندہ ہونے تک مہلت دے'),
(4050, 38, 80, 'فرمایا پس تمہیں مہلت ہے'),
(4051, 38, 81, 'وقت معین کے دن تک'),
(4052, 38, 82, 'عرض کی تیری عز ت کی قسم! میں ان سب کو گمراہ کر دوں گا'),
(4053, 38, 83, 'مگر ان میں جو تیرے خالص بندے ہوں گے'),
(4054, 38, 84, 'فرمایا حق بات یہ ہے اور میں حق ہی کہا کرتا ہوں'),
(4055, 38, 85, 'میں تجھ سے اوران میں سے جو تیرے تابع ہوں گے سب سے جہنم بھر دوں گا'),
(4056, 38, 86, 'کہہ دو میں اس پر تم سے کوئی مزدوری نہیں مانگتا اور نہ میں تکلف کرنے والوں میں ہوں'),
(4057, 38, 87, 'یہ قرآن تو تمام جہان کے لیے نصیحت ہے'),
(4058, 38, 88, 'اور تم کچھ مدت کے بعد اس کا حال ضرور جان لوگے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'یہ کتاب الله کی طرف سے نازل کی گئی ہے جو غالب حکمت والا ہے'),
(4060, 39, 2, 'بے شک ہم نے یہ کتاب ٹھیک طور پر آپ کی طرف نازل کی ہے پس تو خالص الله ہی کی فرمانبرداری مدِ نظر رکھ کر اسی کی عبادت کر'),
(4061, 39, 3, 'خبردار! خالص فرمانبرداری الله ہی کے لیے ہے جنہوں نے اس کے سوا اور کارساز بنا لیے ہیں ہم ان کی عبادت نہیں کرتے مگر اس لیے کہ وہ ہمیں الله سے قریب کر دیں بے شک الله ان کے درمیان ان باتوں میں فیصلہ کرے گا جن میں وہ اختلاف کرتےتھے بے شک الله اسے ہدایت نہیں کرتا جو جھوٹا ناشکرگزار ہو'),
(4062, 39, 4, 'اگر الله چاہتا کہ کسی کو فرزند بنائے تواپنی مخلوقات میں سے جسے چاہتا چن لیتا وہ پاک ہے وہ الله ایک بڑا غالب ہے'),
(4063, 39, 5, 'اس نے آسمانوں اور زمین کو حکمت سے پیدا کیا وہ رات کو دن پر لپیٹ دیتا ہے اور دن کو رات پر لپیٹ دیتا ہے اور اس نے سورج اور چاند کو تابع کر دیا ہے ہر ایک وقت مقرر تک چل رہا ہے خبرادار! وہی غالب بخشنے والا ہے'),
(4064, 39, 6, 'اس نے تمہیں ایک جان سے پیدا کیا پھر اس نے اس سے اس کی بیوی بنائی اور تمہارے لیے آٹھ نر اور مادہ چارپایوں کے پیدا کیے وہ تمہیں تمہاری ماؤں کے پیٹوں میں ایک کیفیت کے بعد دوسری کیفیت پر تین اندھیروں میں بناتا ہے یہی الله تمہارا رب ہے اسی کی بادشاہی ہے اس کے سوا کوئی معبود نہیں پس تم کہاں پھرے جا رہے ہو'),
(4065, 39, 7, 'اگر تم انکار کرو تو بے شک الله تم سے بے نیاز ہے اور وہ اپنے بندوں کے لیے کفر کو پسند نہیں کرتا اور اگر تم شکر کرو تو وہ اسے تمہارے لیے پسند کرتا ہے اور کوئی بوجھ اٹھانے والا دوسرے کا بوجھ نہیں اٹھائے گا پھر اپنے رب ہی کی طرف تمہیں لوٹ کر جانا ہے سو وہ تمہیں بتا دے گا جو کچھ تم کرتے رہے ہو بے شک وہ سینوں کے بھید جاننے والا ہے'),
(4066, 39, 8, 'اور جب انسان کو تکلیف پہنچتی ہے تو اپنے رب کواس کی طرف رجوع کر کے پکارتا ہے پھر جب وہ اسے کوئی نعمت اپنی طرف سے عطا کرتا ہے تو جس کے لیے پہلے پکارتا تھا اسے بھول جاتا ہے اور اس کے لیے شریک بناتا ہے تاکہ اس کی راہ سے گمراہ کرے کہہ دو اپنے کفر میں تھوڑی مدت فائد ہ اٹھا لے بے شک تو دوزخیوں میں سے ہے'),
(4067, 39, 9, '(کیا کافر بہتر ہے) یا وہ جو رات کے اوقات میں سجدہ اور قیام کی حالت میں عبادت کر رہا ہو آخرت سے ڈر رہا ہو اور اپنے رب کی رحمت کی امید کر رہا ہو کہہ دو کیا علم والے اور بے علم برابر ہو سکتے ہیں سمجھتے وہی ہیں جو عقل والے ہیں'),
(4068, 39, 10, 'کہہ دو اے میرے بندو جو ایمان لائےہو اپنے رب سے ڈرو ان کے لیے جنھوں نے اس دنیا میں نیکی کی ہے اچھا بدلہ ہے اور الله کی زمین کشادہ ہے بے شک صبر کرنے والوں کو ان کا اجر بے حساب دیا جائے گا'),
(4069, 39, 11, 'کہہ دو مجھے حکم ہوا ہے کہ میں الله کی اس طرح عبادت کروں کہ عبادت کو اس کے لیے خاص رکھوں'),
(4070, 39, 12, 'اور مجھے یہ بھی حکم ہوا ہے کہ میں سب سے پہلا فرمانبردار بنوں'),
(4071, 39, 13, 'کہہ دو میں بڑے دن کے عذاب سے ڈرتا ہوں اگر اپنے رب کی نافرمانی کروں'),
(4072, 39, 14, 'کہہ دو میں خالص الله ہی کی اطاعت کرتے ہوئے اس کی عبادت کر تا ہوں'),
(4073, 39, 15, 'پھر تم اس کے سوا جس کی چاہو عبادت کرو کہہ دو خسارہ اٹھانے والے وہ ہیں جنہوں نے اپنے جان اور اپنے گھر والوں کو قیامت کے روز خسارہ میں ڈال دیا یاد رکھو! یہ صریح خسارہ ہے'),
(4074, 39, 16, 'ان کے اوپر بھی آگ کے سائبان ہوں گے اور ان کے نیچے بھی سائبان ہوں گے یہی بات ہے جس کا ا لله اپنے بندوں کو خوف دلاتا ہے کہ اے میرے بندو! مجھ سے ڈرتے رہو'),
(4075, 39, 17, 'اور جو لوگ شیطانوں کو پوجنے سے بچتے رہے اور الله کی طرف رجوع ہوئے ان کے لیے خوشخبری ہے پس میرےبندوں کو خوشخبری دے دو'),
(4076, 39, 18, 'جو توجہ سے بات کو سنتے ہیں پھر اچھی بات کی پیروی کرتے ہیں یہی ہیں جنہیں الله نے ہدایت کی ہے اور یہی عقل والے ہیں'),
(4077, 39, 19, 'پس کیا جسے عذاب کا حکم ہو چکا ہے (نجات والے کے برابر ہے) کیا آپ اسے چھوڑ سکتے ہیں جو آگ میں ہے'),
(4078, 39, 20, 'لیکن جو لوگ اپنےرب سے ڈرتے رہے ان کے لیے بالا خانے ہیں جن کے اوپر اوربالا خانے بنے ہوئے ہیں ان کے نیچے نہریں چلتی ہوں گی یہ الله کا وعدہ ہے اور الله اپنے وعدے کے خلاف نہیں کرتا'),
(4079, 39, 21, 'کیا آپ نے نہیں دیکھا کہ الله ہی آسمان سے پانی اتارتا ہے پھر اسے چشمے بنا کر زمین میں چلا دیتا ہے پھر اس کے ذریعے سے کھیتی مختلف رنگوں کی اگاتا ہے پھر خوب ابھرتی ہے پھر آپ اسے زردشدہ دیکھتے ہیں پھر اسے ریزہ ریزہ کر دیتا ہے بے شک اس میں عقل مندوں کے لیے عبرت ہے'),
(4080, 39, 22, 'بھلا جس کا سینہ الله نے دین اسلام کے لئے کھول دیا ہے سو وہ اپنے رب کی طرف سے روشنی میں ہے سوجن لوگوں کے دل الله کے ذکر سے متاثر نہیں ہوتے ان کے لیے بڑی خرابی ہے یہ لوگ کھلی گمراہی میں ہیں'),
(4081, 39, 23, 'الله ہی نے بہترین کلام نال کیا ہے یعنی کتاب باہم ملتی جلتی ہے (اس کی آیات) دہرائی جاتی ہیں جس سے خدا ترس لوگوں کے رونگٹے کھڑے ہو جاتے ہیں پھر ان کی کھالیں نرم ہوجاتی ہیں اور دل یاد الہیٰ کی طرف راغب ہوتے ہیں یہی الله کی ہدایت ہے اس کے ذریعے سے جسے چاہے راہ پر لے آتا ہے اور جسے الله گمراہ کر دے اسے راہ پر لانے والا کوئی نہیں'),
(4082, 39, 24, 'بھلا جو شخص اپنے منہ کو قیامت کے دن برے عذاب کی سپر بنائے گا اور ایسے ظالموں کو حکم ہوگا جو کچھ تم کیا کرتے تھے اس کا مزہ چکھو'),
(4083, 39, 25, 'ان سے پہلے لوگوں نے بھی جھٹلایا تھاپھر ان پر اس طرح عذاب آیا کہ ان کو خبر بھی نہ ہوئی'),
(4084, 39, 26, 'پھر الله نے ان کو دنیا ہی کی زندگی میں رسوائی کا مزہ چکھایا اور آخرت کا عذاب تو اور بھی زیادہ ہے کاش وہ جانتے'),
(4085, 39, 27, 'اورہم نے لوگو ں کے لیے اس قرآن میں ہر قسم کی مثال بیان کر دی ہے تاکہ وہ نصیحت پکڑیں'),
(4086, 39, 28, 'وہ عربی زبان کا بے عیب قرآن ہے تاکہ یہ لوگ ڈریں'),
(4087, 39, 29, 'الله نے ایک مثال بیان کی ہے ایک غلام ہے جس میں کئی ضدی شریک ہیں اور ایک غلام سالم ایک ہی شخص کا ہے کیا دونوں کی حالت برابر ہے سب تعریف الله ہی کے لیے ہے مگر ان میں سے اکثر نہیں سمجھتے'),
(4088, 39, 30, 'بے شک آپ کو بھی مرنا ہے اور ان کو بھی مرنا ہے'),
(4089, 39, 31, 'پھر بے شک تم قیامت کے دن اپنے رب کے ہاں آپس میں جھگڑو گے'),
(4090, 39, 32, 'پھر اس سے کون زیادہ ظالم ہے جس نے الله پرجھوٹ بولا اور سچی بات کو جھٹلایا جب اس کے پاس آئی کیا دوزخ میں کافروں کا ٹھکانا نہیں ہے'),
(4091, 39, 33, 'اور جو سچی بات لایا اور جس نے اس کی تصدیق کی وہی پرہیزگار ہیں'),
(4092, 39, 34, 'ان کے لیے جو کچھ وہ چاہیں گے ان کے رب کے پاس موجود ہوگا نیکو کارو ں کا یہی بدلہ ہے'),
(4093, 39, 35, 'تاکہ الله ان سے وہ برائیاں دور کر دے جو انہوں نے کی تھیں اور الله ان کو ان کا اجر دے ان نیک کاموں کے بدلہ میں جو وہ کیا کرتے تھے'),
(4094, 39, 36, 'کیا الله اپنے بندے کو کافی نہیں اور وہ آپ کو ان لوگوں سے ڈراتے ہیں جو اس کے سوا ہیں اور جسے الله گمراہ کر دے تو اسے راہ پر لانے والا کوئی نہیں'),
(4095, 39, 37, 'اور جسے الله راہ پر لے آئے تو اسے کوئی گمراہ کرنے والا نہیں کیا الله غالب بدلہ لینے والا نہیں ہے'),
(4096, 39, 38, 'او راگر آپ ان سے پوچھیں آسمانوں اور زمین کو کس نے پیدا کیا ہے تو وہ ضرور کہیں گے الله نے کہہ دو بھلا دیکھو تو سہی جنہیں تم الله کے سوا پکارتے ہو اگر الله مجھے تکلیف دینا چاہے تو کیا وہ اس کی تکلیف کو دور کر سکتے ہیں یا وہ مجھ پر مہربانی کرنا چاہے تو کیا وہ اس مہربانی کو روک سکتے ہیں کہہ دو مجھے الله کافی ہے توکل کرنے والے اسی پر توکل کیا کرتے ہیں'),
(4097, 39, 39, 'کہہ دو اے میری قوم تم اپنی جگہ پر کام کیے جاؤ میں بھی کر رہا ہوں پھر تمہیں معلوم ہو جائے گا'),
(4098, 39, 40, 'کہ کس پر عذاب آتا ہےجو اسے رسوا کردے اورکس پردائمی عذاب اترتا ہے'),
(4099, 39, 41, 'بے شک ہم نے آپ پر یہ کتاب سچی لوگو ں کے لیے اتاری ہے پھر جو راہ پر آیا سو اپنے لیے اور جو گمراہ ہوا سو وہ گمراہ ہوتا ہے اپنے برے کو اور آپ ان کے ذمہ دار نہیں ہیں'),
(4100, 39, 42, 'اللہ ہی جانوں کو ان کی موت کے وقت قبض کرتا ہے اور ان جانوں کو بھی جن کی موت ان کے سونے کے وقت نہیں آئی پھر ان جانوں کو روک لیتا ہے جن پر موت کا حکم فرما چکا ہے اور باقی جانوں کو ایک میعاد معین تک بھیج دیتا ہے بے شک اس میں ان لوگوں کے لیے نشانیاں ہیں جو غور کرتے ہیں'),
(4101, 39, 43, 'کیا انہوں نے الله کےسوا اور حمایتی بنا رکھے ہیں کہہ دوکیا اگرچہ وہ کچھ بھی اختیار نہ رکھتے ہوں اور نہ عقل رکھتے ہوں'),
(4102, 39, 44, 'کہہ دو ہر طرح کی حمایت الله ہی کے اختیار میں ہے آسمانوں اور زمین میں اسی کی حکومت ہے پھر اسی کی طرف تم لوٹائے جاؤ گے'),
(4103, 39, 45, 'اور جب ایک الله کا ذکر کیا جاتا ہے تو لوگ آخرت پر یقین نہیں رکھتے ان کے دل نفرت کرتے ہیں اور جب اس کے سوا اوروں کا ذکر کیا جاتا ہے تو فوراً خوش ہو جاتے ہیں'),
(4104, 39, 46, 'کہہ دو اے الله آسمانوں اور زمین کے پیدا کرنے والے ہر چھپی او رکھلی بات کے جاننے والے تو ہی اپنے بندوں میں فیصلہ کرے گا اس بات میں جس میں وہ اختلاف کر رہے ہیں'),
(4105, 39, 47, 'اور اگر ظالموں کے پاس جو کچھ زمین میں ہے سب ہو اور اسی قدر اس کے ساتھ اور بھی ہو تو قیامت کے بڑے عذاب کے معاوضہ میں دے کر چھوٹنا چاہیں گے اور اللہ کی طرف سے انہیں وہ پیش آئے گا کہ جس کا انہیں گمان بھی نہ تھا'),
(4106, 39, 48, 'اور برے کاموں کی برائی ان پر ظاہر ہو جائے گی اوران کو وہ عذاب کہ جس پر ہنسی کیا کرتے تھے پکڑ لے گا'),
(4107, 39, 49, 'پھر جب آدمی پر کوئی مصیبت آتی ہے تو ہمیں پکارتا ہے پھر جب ہم اسے اپنی نعمت عطا کرتے ہیں تو کہتا ہے یہ تومجھے میری عقل سے ملی ہے بلکہ یہ نعمت آزمائش ہے ولیکن ان میں سے اکثر نہیں جانتے'),
(4108, 39, 50, 'بے شک یہی بات وہ لوگ کہہ چکے ہیں جو ان سے پہلے تھے پس ان کے نہ کام آیا جو کچھ وہ کماتے رہے'),
(4109, 39, 51, 'پھر ان پر ان کے اعمال کی برائی آ پڑی اور ان میں سے جو لوگ ظلم کر رہے ہیں عنقریب ان کو بھی برے نتائج ان برے عملوں کے پہنچیں گے اور وہ عاجز کرنے والے نہیں ہیں'),
(4110, 39, 52, 'اور کیا انہیں معلوم نہیں کہ الله ہی روزی کشادہ کر تا ہے جس کی چاہے اور تنگ کرتا ہے بے شک اس میں ان لوگو ں کے لیے نشانیاں ہیں جو ایمان رکھتے ہیں'),
(4111, 39, 53, 'کہہ دو اے میرے بندو جنہوں نے اپنی جانوں پر ظلم کیا ہے الله کی رحمت سے مایوس نہ ہو بے شک الله سب گناہ بخش دے گا بے شک وہ بخشنے والا رحم والا ہے'),
(4112, 39, 54, 'اور اپنے رب کی طرف رجوع کرو اور اس کا حکم مانو اس سے پہلے کہ تم پر عذاب آئے پھر تمہیں مدد بھی نہ مل سکے گی'),
(4113, 39, 55, 'اور ان اچھی باتوں کی پیروی کرو جو تمہارے رب کی طرف سے نازل کی گئی ہیں اس سے پہلے کہ تم پر ناگہاں عذاب آ جائے اور تمہیں خبر بھی نہ ہو'),
(4114, 39, 56, 'کہیں کوئی نفس کہنے لگے ہائے افسوس اس پر جو میں نے الله کے حق میں کوتاہی کی اور میں تو ہنسی ہی کرتا رہ گیا'),
(4115, 39, 57, 'یا کہنے لگے اگر الله مجھے ہدایت کرتا تو میں پرہیزگاروں میں ہوتا'),
(4116, 39, 58, 'یا کہنے لگے جس وقت عذاب کو دیکھے گا کہ کاش مجھے میسر ہو واپس لوٹنا تو میں نیکو کاروں میں سے ہوجاؤں'),
(4117, 39, 59, 'ہاں تیرے پاس میری آیتیں آ چکی تھیں سو تو نے انہیں جھٹلایا اور تو نے تکبر کیا اور تو منکروں میں سے تھا'),
(4118, 39, 60, 'اور قیامت کے دن آپ ان لوگوں کو دیکھیں گے جو الله پر جھوٹے الزام لگاتے ہیں کہ ان کے منہ سیاہ ہوں گے کیا دوزخ میں تکبر کرنے والوں کا ٹھکانہ نہیں ہے'),
(4119, 39, 61, 'اور الله ان لوگوں کو کامیابی کے ساتھ نجات دے گا جو (شرک و کفر سے) بچتےتھے انہیں تکلیف نہیں پہنچے گی اور نہ وہ غمگین ہوں گے'),
(4120, 39, 62, 'الله ہی ہر چیز کا پیدا کرنے والا ہے اور وہی ہر چیز کا نگہبان ہے'),
(4121, 39, 63, 'آسمانوں اور زمین کی کنجیاں اسی کے ہاتھ میں ہیں اور جو الله کی آیتوں کے منکر ہوئے وہی نقصان اٹھانے والے ہیں'),
(4122, 39, 64, 'کہہ دو اے جاہلو کیا مجھے الله کے سوا اور کی عبادت کرنے کا حکم دیتے ہو'),
(4123, 39, 65, 'اور بے شک آپ کی طرف اور ان کی طرف وحی کیا جا چکا ہے جو آپ سے پہلے ہو گزرے ہیں کہ اگرتم نے شرک کیا تو ضرور تمہارے عمل برباد ہو جائیں گے اور تم نقصان اٹھانے والوں میں سے ہو گے'),
(4124, 39, 66, 'بلکہ الله ہی کی عبادت کرو اور جس کے شکر گزار رہو'),
(4125, 39, 67, 'اور انھوں نے الله کی قدر نہیں کی جیسا کہ اس کی قدر کرنے کا حق ہے اور یہ زمین قیامت کے دن سب اسی کی مٹھی میں ہو گی اور آسمان اس کے داہنے ہاتھ میں لپٹے ہوئےہوں گے وہ پاک اور برتر ہے اس سے جو وہ شریک ٹھیراتے ہیں'),
(4126, 39, 68, 'اور صور پھونکا جائے گا تو بے ہوش ہو جاے گا جو کوئی آسمانوں اورجو کوئی زمین میں ہے مگر جسے الله چاہے پھر وہ دوسری دفعہ صور پھونکا جائے گا تو یکایک وہ کھڑے دیکھ رہے ہوں گے'),
(4127, 39, 69, 'اور زمین اپنے رب کے نور سے چمک اٹھے گی اور کتاب رکھ دی جاوے گی اور نبی اور گواہ لائے جاویں گے اور ان میں انصاف سے فیصلہ کیا جاوے گا اور ان پر ظلم نہ کیا جائے گا'),
(4128, 39, 70, 'اورہر شخص کو جو کچھ اس نے کیا تھا پورا پورا بدلہ دیا جائے گا اور وہ خوب جانتا ہے جو کچھ وہ کر رہے ہیں'),
(4129, 39, 71, 'اور جو کافر ہیں دوزخ کی طرف گروہ گروہ ہانکے جائیں گے یہاں تک کہ جب اسکے پاس آئیں گے تو اس کے دروازے کھول دیے جائیں گے اور ان سے اس کے داروغہ کہیں گے کیا تمہارے پاس تم ہی میں سے رسول نہیں آئےتھے جو تمہیں تمہارے رب کی آیتیں پڑھ کر سناتے تھے اور آج کے دن کےپیش آنے سے تمہیں ڈراتے تھے کہیں گے ہاں لیکن عذاب کا حکم (علم ازلی میں) منکرو ں پر ہو چکا تھا'),
(4130, 39, 72, 'کہا جائے گا دوزخ کے دروازوں میں داخل ہو جاؤ اس میں سدا رہو گے پس وہ تکبر کرنے والوں کے لیے کیسا برا ٹھکانہ ہے'),
(4131, 39, 73, 'اور وہ لوگ جو اپنے رب سے ڈرتے رہے جنت کی طرف گرو ہ گروہ لے جانے جائیں گے یہاں تک کہ جب وہ اس کے پا پہنچ جائیں گے اور اس کے دروازے کھلے ہوئے ہوں گے اور ان سے اس کے داروغہ کہیں گے تم پر سلام ہو تم اچھے لوگ ہو اس میں ہمیشہ کے لیے داخل ہو جاؤ'),
(4132, 39, 74, 'اوروہ کہیں گے الله کا شکر ہے جس نے ہم سے اپنا وعدہ سچا کیا اور ہمیں اس زمین کا وارث کر دیا کہ ہم جنت میں جہاں چاہیں رہیں پھر کیا خوب بدلہ ہے عمل کرنے والوں کا'),
(4133, 39, 75, 'اور آپ فرشتوں کو حلقہ باندھے ہوئے عرش کے گرد دیکھیں گے اپنے رب کی حمد کے ساتھ تسبیح پڑھ رہے ہیں اور درمیان انصاف سے فیصلہ کیا جائے گا اور سب کہیں گے سب تعریف الله ہی کے لیے ہے جو سارے جہانوں کا رب ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حمۤ'),
(4135, 40, 2, 'یہ کتاب الله کی طرف سے نازل ہوئی ہے جو غالب ہر چیز کا جاننے والا ہے'),
(4136, 40, 3, 'گناہ بخشنے والا اور توبہ قبول کرنے والا سخت عذاب دینے والا قدرت والا ہے اس کے سوا کوئی معبود نہیں اسی کی طرف لوٹ کر جانا ہے'),
(4137, 40, 4, 'الله کی آیتوں میں نہیں جھگڑتے مگر وہ لوگ جو کافر ہیں پس ان کا شہرو ں میں چلنا پھرنا آپ کو دھوکا نہ دے'),
(4138, 40, 5, 'ان سے پہلے قوم نوح اور ان کے بعد اور فرقے بھی جھٹلا چکے ہیں اور ہر ایک امت نے اپنے رسول کو پکڑنے کا ارادہ کیا اور غلط باتوں کے ساتھ بحث کرتے رہے تاکہ اس سے دین حق کو مٹا دیں پھر ہم نے انھیں پکڑ لیا پھر کیسی سزا ہوئی'),
(4139, 40, 6, 'اور اسی طرح منکروں پر الله کا کلام پورا ہوا کہ وہ دوزخی ہیں'),
(4140, 40, 7, 'جو (فرشتے) عرش کو اٹھائے ہوئے ہیں اور جو اس کے گرد ہیں وہ اپنے رب کی حمد کے ساتھ تسبیح کرتے رہتے ہیں اور اس پر ایمان لاتے ہیں اور ایمانداروں کے لیے بخشش مانگتے ہیں کہ اے ہمارے رب تیری رحمت اور تیرا علم سب پر حاوی ہے پھر جن لوگو ں نے توبہ کی ہے اور تیرے راستے پر چلتے ہیں انہیں بخش دے اور انہیں دوزخ کے عذاب سے بچا لے'),
(4141, 40, 8, 'اے ہمارے رب اور انہیں بہشتوں میں داخل کر جو ہمیشہ رہیں گی جن کا تو نے ان سے وعدہ کیا ہے اور ان کو جو ان کے باپ دادوں اوران کی بیویوں اوران کی اولاد میں سے نیک ہیں بے شک تو غالب حکمت والا ہے'),
(4142, 40, 9, 'اور انہیں برائیوں سے بچا اور جس کو تو اس دن برائیوں سے بچائے گا سو اس پر تو نے رحم کر دیا اور یہ بڑی کامیابی ہے'),
(4143, 40, 10, 'بے شک جو لوگ کافر ہیں انہیں پکار کر کہا جائے گا جیسی تمہیں (اس وقت) اپنے سے نفرت ہے اس سے بڑھ کر الله کو (تم سے) نفرت تھی جبکہ تم ایمان کی طرف بلائے جاتے تھے پھر نہیں مانا کرتے تھے'),
(4144, 40, 11, 'وہ کہیں گے اے ہمارے رب تو نے ہمیں دو بار موت دی اورتو نے ہمیں دوبارہ زندہ کیا پس ہم نے اپنے گناہوں کا اقرار کر لیا پس کیا نکلنے کی بھی کوئی راہ ہے'),
(4145, 40, 12, 'یہ عذاب اس لیے ہے کہ جب تم اکیلے الله کی طرف بلایا جاتا تھا تو انکار کرتے تھے اورجب اس کے ساتھ شریک کیا جاتا تھا تو مان لیتے تھے سو یہ فیصلہ الله کا ہے جو عالیشان بڑے رتبے والا ہے'),
(4146, 40, 13, 'وہی ہے جو تمہیں اپنی نشانیاں دکھاتا ہے اور تمہارے لیے آسمان سے رزق نازل کرتا ہے اور سمجھتا وہی ہے جو (الله کی طرف) رجوع کرتا ہے'),
(4147, 40, 14, 'پس الله کو پکارو اس کے لیے عبادت کو خالص کرتے ہوئے اگرچہ کافر برا منائیں'),
(4148, 40, 15, 'وہ اونچے درجوں والا عرش کا مالک ہے اپنے حکم سے اپنےبندوں میں سے جس کے پاس چاہتا ہے وحی بھیجتا ہے تاکہ وہ ملاقات (قیامت) کے دن سے ڈرائے'),
(4149, 40, 16, 'جس دن وہ سب نکل کھڑے ہوں گے الله پر ان کی کوئی بات چھپی نہ رہے گی آج کس کی حکومت ہے الله ہی کی جو ایک ہے بڑا غالب'),
(4150, 40, 17, 'آج کے دن ہر شخص اپنے کیے کا بدلہ پائے گا آج کچھ ظلم نہ ہوگا بے شک الله جلد حساب لینے والا ہے'),
(4151, 40, 18, 'اور انہیں قریب آنے والی (مصیبت) کے دن سے ڈرا جب کہ غم کے مارے کلیجے منہ کو آ رہے ہوں گے ظالموں کا کوئی حمایتی نہیں ہوگا اور نہ کوئی سفارشی جس کی بات مانی جائے'),
(4152, 40, 19, 'وہ آنکھوں کی خیانت اور دل کے بھید جانتا ہے'),
(4153, 40, 20, 'اور الله ہی انصاف کے ساتھ فیصلہ کرے گا اور جنہیں وہ اس کے سوا پکارتے ہیں وہ کچھ بھی فیصلہ نہیں کر سکتے بیشک الله ہی سب کچھ سننے والا دیکھنے والا ہے'),
(4154, 40, 21, 'کیا انہوں نے زمین میں سیر نہیں کی کہ وہ دیکھتے ان لوگو ں کا انجام کیسا تھا جو ان سے پہلے ہو گزرے ہیں وہ قوت میں ان سے بڑھ کر تھے اور زمین میں آثار کے اعتبار سے بھی پھر الله نے انہیں ان کے گناہوں کے سبب سے پکڑ لیا اوران کے لیے الله سے کوئی بچانے والا نہ تھا'),
(4155, 40, 22, 'یہ اس لیے کہ ان کے پاس ان کے رسول روشن دلیلیں لے کر آتے تھے تو وہ انکار کرتے تھے پس انہیں الله نے پکڑ لیا بے شک وہ بڑا قوت والا سخت عذاب دینے والا ہے'),
(4156, 40, 23, 'اور ہم نے موسیٰ کو اپنے معجزات اور واضح دلیل دے کر بھیجا تھا'),
(4157, 40, 24, 'فرعون اور ہامان اور قارون کی طرف تو وہ کہنے لگے بڑا جھوٹا جادوگر ہے'),
(4158, 40, 25, 'پس جب وہ ان کے پاس ہماری طرف سے سچا دین لائے تو کہنے لگے ان لوگوں کے بیٹوں کو قتل کر دو جو موسیٰ پر ایمان لائے ہیں اور ان کی عورتوں کو زندہ رہنے دو اور کافروں کے داؤ تو محض غلط ہوا کرتے ہیں'),
(4159, 40, 26, 'اور فرعون نے کہا مجھے چھوڑ دو میں موسیٰ کو قتل کر دوں اور وہ اپنے رب کو پکارے میں ڈرتا ہوں کہ وہ تمہارے دین کو بدل ڈالے گا یا یہ کہ زمین میں فساد پھیلائے گا'),
(4160, 40, 27, 'اور موسیٰ نے کہا میں تو اپنی اور تمہارے رب کی پناہ لے چکا ہوں ہر ایک متکبّر سے جو حساب کے دن پر یقین نہیں رکھتا'),
(4161, 40, 28, 'اور فرعون کی قوم میں سے ایک ایمان دار آدمی نے کہا جو اپنا ایمان چھپاتا تھا کیاتم ایسے آدمی کو قتل کرتے ہو جو یہ کہتا ہے کہ میرا رب الله ہے اوروہ تمہارے پاس وہ روشن دلیلیں تمہارے رب کی طرف سے لایا ہے اور اگر وہ جھوٹا ہے تو اسی پر ا س کے جھوٹ کا وبال ہے اور اگر وہ سچا ہے تو تمہیں کچھ نہ کچھ وہ (عذاب) جس کا وہ تم سے وعدہ کرتا ہے پہنچے گا اور الله اس کو راہ پر نہیں لاتا جو حد سے بڑھنے والا بڑا جھوٹا ہے'),
(4162, 40, 29, 'اے میری قوم آج تو تمہاری حکومت ہے تم ملک میں غالب ہو ہماری کون مدد کرے گا اگر ہم پر الله کا عذاب آگیا فرعون نے کہا میں تو تمہیں وہی سوجھاتا ہوں جو مجھے سوجھی ہے اور میں تمہیں سیدھا ہی راستہ بتاتا ہوں'),
(4163, 40, 30, 'اور اس شخص نے کہا جو ایمان لایا تھا کہ اےمیری قوم مجھے تو تمہاری نسبت (پہلی) امتوں جیسے دن کا اندیشہ ہو رہا ہے'),
(4164, 40, 31, 'جیسا کہ قوم نوح اور عاد اور ثمود اور ان سے پچھلوں کا حال ہوا اور الله تو بندوں پر کچھ بھی ظلم نہیں کرنا چاہتا'),
(4165, 40, 32, 'اور اے میری قوم مجھے تم پر چیخ و پکار (قیامت) کے دن کا اندیشہ ہے'),
(4166, 40, 33, 'جس دن تم پیٹھ پھیر کر بھاگو گے الله سے تمہیں کوئی بچانے والا نہیں ہوگا اور جسے الله گمراہ کر دے پھر اسے کوئی راہ بتانے والا نہیں'),
(4167, 40, 34, 'اور تمہارے پاس یوسف بھی اس سے پہلے واضح دلیلیں لے کر آ چکا پس تم ہمیشہ اس سے شک میں رہے جو وہ تمہارے پاس لایا یہاں تک کہ جب وہ فوت ہو گیا تو تم نےکہا کہ الله اس کے بعد کوئی رسول ہر گز نہیں بھیجے گا اسی طرح الله گمراہ کرتا ہے اس کو جو حد سے بڑھنے والا شک کرنے والا ہے'),
(4168, 40, 35, 'جو لوگ الله کی آیتوں میں کسی دلیل کے سوا جو ان کے پاس پہنچی ہو جھگڑتے ہیں الله اور ایمان والوں کے نزدیک (یہ) بڑی نازیبا بات ہے الله ہر ایک متکبّر سرکش کے دل پر اسی طرح مہر کر دیا کرتا ہے'),
(4169, 40, 36, 'اور فرعون نے کہا اے ہامان میرے لیے ایک محل بنا تاکہ میں راستوں پر پہنچوں'),
(4170, 40, 37, 'یعنی آسمان کے راستوں پر پھر موسیٰ کے خدا کی طرف جھانک کر دیکھوں اور میں تو اسے جھوٹا خیال کرتا ہوں اور اسی طرح فرعون کو اس کا برا عمل اچھا معلوم ہوا اور وہ راستہ سے روکا گیا تھا اور فرعون کی ہر تدبیر غارت ہی گئی'),
(4171, 40, 38, 'اوراس شخص نے کہا جو ایمان لایا تھا اے میری قوم تم میری پیروی کرو میں تمہیں نیکی کی راہ بتاؤں گا'),
(4172, 40, 39, 'اے میری قوم دنیا کی زندگی بس (چند روزہ) فائدے ہیں اور آخرت کا گھر ہی ٹھہرنے کی جگہ ہے'),
(4173, 40, 40, 'جس نے برا کام کیا تو اتنی ہی سزا پائے گا اور جس نے نیک کام کیا خواہ وہ مرد ہو یا عورت اور وہ ایمانداربھی ہو سو وہ جنت میں داخل ہوں گے جہاں انہیں بے حساب روزی ملے گی'),
(4174, 40, 41, 'اور اے میری قوم کیا بات ہے میں تو تمہیں نجات کی طرف بلاتا ہوں اور تم مجھے دوزخ کی طرف بلاتے ہو'),
(4175, 40, 42, 'تم مجھے اس بات کی طرف بلاتے ہو کہ میں الله کا منکر ہو جاؤں اور اس کے ساتھ اسے شریک کروں جسے میں جانتا بھی نہیں اور میں تمہیں غالب بخشنے والے کی طرف بلاتاہوں'),
(4176, 40, 43, 'بے شک تم مجھے جس کی طرف بلاتے ہو وہ نہ دنیا میں بلانے کے قابل ہے اور نہ آخرت میں اور بےشک ہمیں الله کی طرف لوٹ کر جانا ہے اور بے شک حد سے بڑھنے والے ہی دوزخی ہیں'),
(4177, 40, 44, 'پھر تم میری بات کو یاد کرو گے اور میں اپنا معاملہ الله کے سپرد کرتا ہوں بے شک الله بندوں کو دیکھ رہا ہے'),
(4178, 40, 45, 'پھر الله نے اسے تو ان کے فریبوں کی برائی سے بچایا اور خود فرعونیوں پر سخت عذاب آ پڑا'),
(4179, 40, 46, 'وہ صبح او رشام آگ کے سامنے لائے جاتے ہیں اور جس دن قیامت قائم ہو گی (حکم ہوگا) فرعونیوں کو سخت عذاب میں لے جاؤ'),
(4180, 40, 47, 'اور جب دوزخی آپس میں جھگڑیں گےپھر کمزور سرکشوں سے کہیں گے کہ ہم تمہارے پیرو تھے پھر کیا تم ہم سے کچھ بھی آگ دور کر سکتے ہو'),
(4181, 40, 48, 'سرکش کہیں گے ہم تم سبھی اس میں پڑے ہوئے ہیں بے شک الله اپنے بندوں میں فیصلہ کر چکا ہے'),
(4182, 40, 49, 'اور دوزخی جہنم کے داروغہ سے کہیں گے کہ تم اپنے رب سے عرض کرو کہ وہ ہم سے کسی روز تو عذاب ہلکا کر دیا کرے'),
(4183, 40, 50, 'وہ کہیں گے کیا تمہارے پاس تمہارے رسول نشانیاں لے کر نہ آئے تھے کہیں گے ہاں (آئے تھے) کہیں گے پس پکارو اور کافروں کا پکارنا محض بے سود ہو گا'),
(4184, 40, 51, 'ہم اپنے رسولوں اور ایمانداروں کے دنیا کی زندگی میں بھی مددگار ہیں اور اس دن جب کہ گواہ کھڑے ہوں گے'),
(4185, 40, 52, 'جس دن ظالموں کو ان کا عذر کرنا کچھ بھی نفع نہ دے گا اور ان پر پھٹکار پڑے گی اور ان کے لیے برا گھر ہوگا'),
(4186, 40, 53, 'اور ہم نے موسیٰ کو ہدایت دی تھی اور ہم نے بنی اسرائیل کو کتاب کا وارث کر دیا تھا'),
(4187, 40, 54, 'جو عقلمندوں کے لیے ہدایت اور نصیحت تھی'),
(4188, 40, 55, 'پس صبر کر بے شک الله کا وعدہ سچا ہے اور اپنے گناہ کی معافی مانگ اور شام اور صبح اپنے رب کی حمد کے ساتھ پاکی بیان کر'),
(4189, 40, 56, 'بے شک جو لوگ الله کی آیتوں میں بغیر اس کے کہ ان کے پاس کوئی دلیل آئی ہو جھگڑتے ہیں اور کچھ نہیں بس ان کے دل میں بڑائی ہے کہ وہ اس تک کبھی پہنچنے والے نہیں سو الله سے پناہ مانگو کیوں کہ وہ سننے والا دیکھنے والا ہے'),
(4190, 40, 57, 'البتہ آسمانوں اور زمین کا پیدا کرنا آدمیوں کے پیدا کرنے کی نسبت بڑا کام ہے لیکن اکثر لوگ نہیں جانتے'),
(4191, 40, 58, 'اور اندھا اور دیکھنے والا برابر نہیں اور جو لوگ ایمان لائے اور نیک کام کئے وہ اور بدکار برابر نہیں تم بہت ہی کم سمجھتے ہو'),
(4192, 40, 59, 'بے شک قیامت آنے والی ہے اس میں کوئی شک نہیں لیکن اکثر لوگ یقین نہیں کرتے'),
(4193, 40, 60, 'اور تمہارے رب نے فرمایا ہے مجھے پکارو میں تمہاری دعا قبول کروں گا بے شک جو لوگ میری عبادت سے سرکشی کرتے ہیں عنقریب وہ ذلیل ہو کر دوزخ میں داخل ہوں گے'),
(4194, 40, 61, 'الله ہی ہے جس نے تمہارے لیے رات بنائی تاکہ اس میں آرام کرو اور دن کو ہر چیز دکھانے والا بنایا بے شک الله لوگوں پر بڑے فضل والا ہے لیکن اکثر لوگ شکر نہیں کرتے'),
(4195, 40, 62, 'یہی الله تمہارا رب ہے ہر چیز کا پیدا کرنے والا اس کے سوا کوئی معبود نہیں پھر کہاں الٹے جا رہے ہو'),
(4196, 40, 63, 'اسی طرح وہ لوگ بھی الٹے چلا کرتے تھے جو الله کی نشانیوں کا انکار کیا کرتے تھے'),
(4197, 40, 64, 'الله ہی ہے جس نے تمہارے لیے زمین کو آرام گاہ بنایا اور آسمان کو چھت اور تمہاری صورتیں بنائیں اور پاکیزہ چیزوں سے تمہیں رزق دیا وہی الله تمہارا پالنے والا ہے پس سارے جہان کا پالنے والا الله با برکت ہے'),
(4198, 40, 65, 'وہی ہمیشہ زندہ ہے اس کے سوا کوئی معبود نہیں پس اسی کو پکارو خاص اسی کی بندگی کرتے ہوئے سب تعریف الله کے لیے ہے جو سارے جہان کا پالنے والا ہے'),
(4199, 40, 66, 'کہہ دو مجھے تو ان چیزوں کی عبادت سے منع کیا گیا ہے جن کو تم الله کے سوا پکارتے ہو جب کہ میرے رب کی طرف سے میرے پاس کھلی کھلی نشانیاں آچکی ہیں اور مجھے یہ حکم دیا گیا ہے کہ میں رب العالمین کے سامنے سر جھکاؤں'),
(4200, 40, 67, 'وہی ہے جس نے تمہیں مٹی سے پھر نطفے سے پھر خون بستہ سے پیدا کیا پھر وہ تمہیں بچہ بنا کر نکالتا ہے پھر باقی رکھتا ہے تاکہ تم اپنی جوانی کو پہنچو پھر یہاں تک کہ تم بوڑھے ہو جاتے ہو کچھ تم میں اس سے پہلے مر جاتے ہیں (بعض کو زندہ رکھتا ہےاور تاکہ تم وقت مقررہ تک پہنچو اور تاکہ تم سمجھو'),
(4201, 40, 68, 'وہی ہے جو زندہ کرتا ہے اور مارتا ہے پس جب وہ کسی امر کیا فیصلہ کرلیتا ہے تو صرف اس سے یہی کہتا ہے کو ہو جا تو وہ ہو جاتا ہے'),
(4202, 40, 69, 'کیا آپ نے ان لوگوں کو نہیں دیکھا جو الله کی آیتوں میں جھگڑتے ہیں وہ کہاں پھرے چلے جا رہے ہیں'),
(4203, 40, 70, 'وہ لوگ جنہوں نے کتاب کو اور جو کچھ ہم نے رسولوں کو دے کر بھیجا تھا سب کو جھٹلا دیا پس انہیں معلوم ہو جائے گا'),
(4204, 40, 71, 'جب کہ طوق اور زنجیریں ان کے گلے میں ڈال کر گھسیٹے جائيں گے'),
(4205, 40, 72, 'کھولتے پانی میں پھر آگ میں جھونکے جائیں گے'),
(4206, 40, 73, 'پھر ان سے کہا جائے گا کہاں ہیں جنھیں تم شریک بتاتے تھے'),
(4207, 40, 74, 'الله کے سوا وہ کہیں گے ہم سے وہ کھوئے گئے بلکہ ہم تو اس سے پہلے کسی چیز کو بھی نہیں پکارتے تھے اسی طرح الله کافروں کو گمراہ کرتا ہے'),
(4208, 40, 75, 'یہ عذاب تمہیں اس لیے ہوا کہ تم ملک میں ناحق خوشیاں مناتے تھے اور اس لیے بھی کہ تم اترایا کرتے تھے'),
(4209, 40, 76, 'جہنم کے دروازوں میں ہمیشہ رہنے کے لیے داخل ہو جاؤ پھر تکبر کرنے والوں کا کیا ہی برا ٹھکانہ ہے'),
(4210, 40, 77, 'پھر صبر کر بے شک الله کا وعدہ سچا ہے پھر جس (عذاب) کا ہم ان سے وعدہ کر رہے ہیں کچھ تھوڑا سا اگر ہم آپ کو دکھا دیں یا ہم آپ کو وفات دے د یں تو ہماری طرف ہی سب لوٹائے جائیں گے'),
(4211, 40, 78, 'اورہم نے آپ سے پہلے کئی رسول بھیجے تھے بعض ان میں سے وہ ہیں جن کا حال ہم نے آپ پر بیان کر دیا اور بعض وہ ہیں کہ ہم نے آپ پر انکا حال بیان نہیں کیا اور کسی رسول سے یہ نہ ہو سکا کہ کوئی معجزہ اذنِ الہیٰ کے سوا ظاہر کر سکے پھر جس وقت الله کا حکم آئے گا ٹھیک فیصلہ ہو جائے گا اوراس وقت باطل پرست نقصان اٹھائیں گے'),
(4212, 40, 79, 'الله ہی ہے جس نے تمہارے لیے چوپائے بنائے تاکہ تم ان میں سے بعض پر سوار ہو اور بعض کو تم کھاتے ہو'),
(4213, 40, 80, 'اور تمہارے لیے ان میں بہت سے فوائد ہیں اور تاکہ تم ان پر سوار ہو کر اپنی حاجت کو پہنچو جو تمہارے سینوں میں ہے اور ان پر اور نیز کشتیوں پر تم سوارکیے جاتے ہو'),
(4214, 40, 81, 'اور وہ تمہیں اپنی نشانیاں دکھاتا ہے پس تم الله کی کون کون سی نشانیوں کا انکار کرو گے'),
(4215, 40, 82, 'پس کیا انہوں نے ملک میں چل پھر کر نہیں دیکھا کہ جو لوگ ان سے پہلے ہو گزرے ہیں ان کا کیا انجام ہوا وہ لوگ ان سے زیادہ تھے اور قوت اور نشانوں میں (بھی) جو کہ زمین پر چھوڑگئے ہیں بڑھے ہوئے تھے پس ان کے نہ کام آیا جو کچھ وہ کماتے تھے'),
(4216, 40, 83, 'پس جب ان کے رسول ان کے پا س کھلی دلیلیں لائے تووہ اپنے علم و دانش پر اترانے لگے اور جس پر وہ ہنسی کرتےتھے وہ ان پر الٹ پڑا'),
(4217, 40, 84, 'پھر جب انہوں نے ہمارا عذاب آتے دیکھا تو کہنے لگے کہ ہم الله پر ایمان لائےجو ایک ہے اور ہم نے ان چیزوں کا انکار کیا جنہیں ہم اس کا شریک ٹھیراتے تھے'),
(4218, 40, 85, 'پس انہیں ان کے ایمان نے نفع نہ دیا جب انہوں نے ہمارا عذاب دیکھ لیا یہ سنت الہیٰ ہے جو اس کے بندو ں میں گزر چکی ہے اور اس وقت کافر خسارہ میں رہ گئے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حمۤ'),
(4220, 41, 2, '(یہ کتاب) بڑے مہربان نہایت رحم والے کی طرف سے نازل ہوئی ہے'),
(4221, 41, 3, 'کہ جس کی آیتیں عربی زبان میں علم والوں کے لیے واضح ہیں'),
(4222, 41, 4, 'خوشخبری دینے والی ڈرانے والی ہےپھر ان میں سے اکثر نے تو منہ ہی پھیر لیا پھر وہ سنتے بھی نہیں'),
(4223, 41, 5, 'اور کہتے ہیں ہمارے دل اس بات سے کہ جس کی طرف تو ہمیں بلاتا ہے پردوں میں ہیں اور ہمارے کانوں میں بوجھ ہے اور ہمارے اور آپ کے درمیان پردہ پڑا ہوا ہے پھر آپ اپنا کام کیے جائیں ہم بھی اپنا کام کر رہے ہیں'),
(4224, 41, 6, 'آپ ان سے کہہ دیں کہ میں بھی تم جیسا ایک آدمی ہوں میری طرف یہی حکم آتا ہے کہ تمہارا معبود ایک ہی ہے پھراس کی طرف سیدھے چلے جاؤ اور اس سے معافی مانگو اور مشرکوں کے لیے ہلاکت ہے'),
(4225, 41, 7, 'جو زکواة نہیں دیتے اور وہ آخرت کے بھی منکر ہیں'),
(4226, 41, 8, 'بے شک جو لوگ ایمان لائے اور انہوں نے نیک کام بھی کیے ان کے لیے بے انتہا اجر ہے'),
(4227, 41, 9, 'کہہ دو کیا تم اس کا انکار کرتے ہو جس نے دو دن میں زمین بنائی اور تم اس کے لیے شریک ٹھیراتے ہو وہی سب جہانوں کا پروردگار ہے'),
(4228, 41, 10, 'اور اس نے زمین میں اوپر سے پہاڑ رکھے اور اس میں برکت دی اور چار دن میں اس کی غذاؤں کا اندازہ کیا (یہ جواب) پوچھنے والوں کے لیے پورا ہے'),
(4229, 41, 11, 'پھر وہ آسمان کی طرف متوجہ ہوا اور وہ دھؤاں تھا پس اس کو اور زمین کو فرمایا کہ خوشی سے آؤ یا جبر سے دونوں نے کہا ہم خوشی سے آئے ہیں'),
(4230, 41, 12, 'پھر انہیں دو دن میں سات آسمان بنا دیا اور اس نے ہر ایک آسمان میں اس کا کام القا کیا اور ہم نے پہلے آسمان کو چراغوں سے زینت دی اور حفاظت کے لیے بھی یہ زبردست ہر چیز کے جاننے والے کا اندازہ ہے'),
(4231, 41, 13, 'پس اگر وہ نہ مانیں تو کہہ دو میں تمہیں کڑک سے ڈراتا ہوں جیسا کہ قوم عاد اور ثمود پر کڑک آئی تھی'),
(4232, 41, 14, 'جب ان کے پاس رسول آئے ان کے سامنے سے اور ان کے پیچھے سے کہ سوائے الله کے کسی کی عبادت نہ کرو تو کہنے لگےکہ اگر ہمارا رب چاہتا تو فرشتے نازل کر دیتا پس ہم تو اس چیز سے جو تم دے کر بھیجے گئے ہو منکر ہیں'),
(4233, 41, 15, 'پس قوم عاد نے زمین میں ناحق تکبر کیا اورکہا ہم سے طاقت میں کون زیادہ ہے کیا انہوں نے دیکھا نہیں کہ الله جس نے انہیں پیدا کیا ہے وہ ان سے طاقت میں کہیں بڑھ کر ہے وہ ہماری آیتوں کا انکار کرتے رہے'),
(4234, 41, 16, 'پس ہم نے ان پر منحوس دنوں میں تیز آندھی بھیجی تاکہ ہم انہیں ذلت کے عذاب کا مزہ دنیا کی زندگی میں چکھا دیں اور آخرت کا عذاب تواور بھی ذلت کا ہے اور ان کی مدد نہ کی جائے گی'),
(4235, 41, 17, 'اور وہ جو قوم ثمود تھی ہم نے انہیں ہدایت کی سو انہوں نے گمراہی کو بمقابلہ ہدایت کے پسند کیا پھر انہیں کڑاکے کے ذلیل کرنے والے عذاب نے آ لیا ان کے اعمال کے سبب سے'),
(4236, 41, 18, 'اورجو لوگ ایمان لائے اور ڈرتے رہتے تھے ہم نے انہیں بچا لیا'),
(4237, 41, 19, 'اور جس دن الله کے دشمن دوزخ کی طرف ہانکیں جائیں گے تو وہ روک لیے جائیں گے'),
(4238, 41, 20, 'یہاں تک کہ جب وہ اس کے پاس آ پہنچیں گے تو ان پر ان کے کان اور ان کی آنکھیں اور ان کی کھالیں گواہی دیں گی جو کچھ وہ کیا کرتے تھے'),
(4239, 41, 21, 'وہ اپنی کھالوں سے کہیں گے کہ تم نے ہمارے خلاف کیوں گواہی دی وہ کہیں گے کہ ہمیں الله نے گویائی دی جس نے ہر چیز کو گویائی بخشی ہے اور اسی نے پہلی مرتبہ تمہیں پیدا کیا اور اسی کی طرف تم لوٹائے جاؤ گے'),
(4240, 41, 22, 'اور تم اپنے کانوں اور آنکھوں اور چمڑو ں کی اپنے اوپر گواہی دینے سے پردہ نہ کرتے تھے لیکن تم نے یہ گمان کیا تھا جو کچھ تم کرتے ہو اس میں سے بہت سی چیزوں کو الله نہیں جانتا'),
(4241, 41, 23, 'او رتمہارے اسی خیال نے جو تم نے اپنے رب کے حق میں کیا تھا تمہیں برباد کیا پھر تم نقصان اٹھانے والو ں میں سے ہوگئے'),
(4242, 41, 24, 'پس اگر وہ صبر کریں تو بھی ان کا ٹھکانہ آگ ہی ہے اور اگر وہ معافی چاہیں گے تو انہیں معافی نہیں دی جائے گی'),
(4243, 41, 25, 'اورہم نے ان کے لیے کچھ ہمنشین مقرر کر دیئے پس انہوں نے ان کو وہ (برے کام) اچھے کر دکھائے جو پہلے کر چکے تھے اورجو پیچھے کریں گے اور ان پر حکم الہیٰ ثابت ہو چکا تھاپہلی امتوں کے ضمن میں جو ان سے پہلے جنوں اور انسانوں میں سے گزر چکی تھیں بے شک وہ نقصان اٹھانے والے تھے'),
(4244, 41, 26, 'اور کافروں نے کہا کہ تم اس قرآن کو نہ سنو اور اس میں غل مچاؤ تاکہ تم غالب ہو جاؤ'),
(4245, 41, 27, 'پس ہم ضرور کافرو ں کو سخت عذاب کا مزہ چکھائیں گے اور ہم ان کے بدترین اعمال کا بدلہ دیں گے جو وہ کیا کرتے تھے'),
(4246, 41, 28, 'الله کے دشمنوں کی یہی سزا آگ ہی ہے ان کے لیے اس میں ہمیشہ رہنے کا گھر ہے اس کا بدلہ جو ہماری آیتوں کاانکار کیا کرتے تھے'),
(4247, 41, 29, 'اور کافر کہیں گے اے ہمارے رب ہمیں وہ لوگ دکھا جنہوں نے ہمیں گمراہ کیا تھا جنّوں اور انسانوں میں سے ہم انہیں اپنے قدموں کے نیچے ڈال دیں تاکہ وہ بہت ذلیل ہوں'),
(4248, 41, 30, 'بے شک جنہوں نے کہا تھا کہ ہمارا رب الله ہے پھر اس پر قائم رہے ان پر فرشتے اتریں گے کہ تم خوف نہ کرو اور نہ غم کرو اور جنت میں خوش رہو جس کا تم سے وعدہ کیا جاتا تھا'),
(4249, 41, 31, 'ہم تمہارے دنیا میں بھی دوست تھے اور آخرت میں بھی اور بہشت میں تمہارے لیے ہر چیز موجود ہے جس کو تمہارا دل چاہے اور تم جو وہاں مانگو گے ملے گا'),
(4250, 41, 32, 'بخشنے والے نہایت رحم والے کی طرف سے مہمانی ہے'),
(4251, 41, 33, 'اور اس سے بہتر کس کی بات ہے جس نے لوگوں کو الله کی طرف بلایا اور خود بھی اچھے کام کیے اور کہا بے شک میں بھی فرمانبرداروں میں سے ہوں'),
(4252, 41, 34, 'اور نیکی اور بدی برابر نہیں ہو تی (برائی کا) دفعیہ اس بات سے کیجیئے جو اچھی ہو پھر ناگہاں وہ شخص جو تیرے اور اس کے درمیان دشمنی تھی ایسا ہوگا گویاکہ وہ مخلص دوست ہے'),
(4253, 41, 35, 'اور یہ بات نہیں دی جاتی مگر انہیں جو صابر ہوتے ہیں اور یہ بات نہیں دی جاتی مگر اس کو جو بڑا بخت والا ہے'),
(4254, 41, 36, 'اور اگر آپ کو شیطان سے کوئی وسوسہ آنے لگے تو الله کی پناہ مانگیئے بے شک وہی سب کچھ سننے والا جاننے والا ہے'),
(4255, 41, 37, 'اور اس کی نشانیوں میں سے رات اور دن اور سورج اور چاند ہیں سورج کو سجدہ نہ کرو اور نہ چاند کو اور اس الله کو سجدہ کرو جس نے انہیں پیدا کیا ہے اگر تم اسی کی عبادت کرتے ہو'),
(4256, 41, 38, 'پھر اگر وہ تکبر کریں تو وہ لوگ جو آپ کے رب کے پاس ہیں رات دن اس کی تسبیح کرتے ہیں اور تھکتے نہیں'),
(4257, 41, 39, 'اور اس کی نشانیوں میں سے یہ ہے کہ تو زمین کو دبی ہوئی دیکھتا ہے پھر جب ہم اس پر پانی برساتے ہیں تو ابھرتی ہے اور پھولتی ہے بے شک جس نے اسےزندہ کیا وہی مردوں کو زندہ کرے گا بے شک وہی ہر چیز پر قادر ہے'),
(4258, 41, 40, 'بے شک جو لوگ ہماری آیتوں میں کجروی کرتے ہیں وہ ہم سے چھپے نہیں رہتے کیا وہ شخص جو آگ میں ڈالا جائے گا بہتر ہے یا وہ جو قیامت کے دن امن سے آئے گا جو چاہو کرو جو کچھ تم کرتے ہو وہ دیکھ رہا ہے'),
(4259, 41, 41, 'بے شک وہ لوگ جنہوں نے نصیحت سے انکار کیا جب کہ وہ ان کے پاس آئی اور تحقیق وہ البتہ عزت والی کتاب ہے'),
(4260, 41, 42, 'جس میں نہ آگے اور نہ پیچھے سے غلطی کا دخل ہے حکمت والے تعریف کیے ہوئے کی طرف سے نازل کی گئی ہے'),
(4261, 41, 43, 'آپ سے وہی بات کہی جاتی ہے جو آپ سے پہلے رسولوں سے کہی گئی تھی بے شک آپ کا رب بخشنے والا اور دردناک عذاب دینے والا بھی ہے'),
(4262, 41, 44, 'اور اگر ہم اسے عجمی زبان کا قرآن بنا دیتے توکہتے کہ اس کی آیتیں صاف صاف بیان کیوں نہیں کی گئیں کیا عجمی کتاب اور عربی رسول کہہ دو یہ ایمان داروں کے لیے ہدایت اور شفا ہے او رجو لوگ ایمان نہیں لاتے ان کے کان بہرے ہیں اور وہ قرآن ان کے حق میں نابینائی ہے وہ لوگ (گو یاکہ) دور جگہ سے پکارے جا رہے ہیں'),
(4263, 41, 45, 'اورہم نے موسیٰ کو کتاب دی تھی پھر اس میں اختلاف کیا گیا اور اگر آپ کے رب کی طرف سے ایک بات صادر نہ ہو چکی ہوتی تو ان کا فیصلہ ہی ہو چکا ہوتا اورانہیں تو قرآن میں قوی شک ہے'),
(4264, 41, 46, 'جو کوئی نیک کام کرتاہے تو اپنے لیے اور برائی کرتا ہے تو اپنے سر پر اور آپ کا رب تو بندوں پر کچھ بھی ظلم نہیں کرتا'),
(4265, 41, 47, 'قیامت کی خبرکا اسی کی طرف حوالہ دیا جاتا ہے اور کوئی پھل اپنے غلافوں سے نہیں نکلتا اور نہ کوئی مادہ حاملہ ہوتی ہے اور نہ وضح حمل کرتی ہے مگر اس کے علم سے اور جس دن انہیں پکارے گا کہ میرے شریک کہاں ہیں کہیں گے ہم نے آپ سے عرض کر دیا کہ ہم میں سے کسی کو بھی خبر نہیں'),
(4266, 41, 48, 'اور ان سے وہ کھوئے جائیں گے جنہیں اس سے پہلے پکارتے تھے اوریقین کر لیں گے کہ انہیں کسی طرح بھی چھٹکارا نہیں'),
(4267, 41, 49, 'انسان بھلائی مانگنے سے نہیں تھکتا اور اگر اسے کوئی تکلیف پہنچ جائے تو مایوس اور نا امید ہو جاتا ہے'),
(4268, 41, 50, 'اور اگر ہم اسے اس مصیبت کے بعدجو اس پر آئی تھی اپنی رحمت کا مزہ چکھاتے ہیں تو کہتا ہے یہ میرا حق تھا اور میں نہیں خیال کرتا کہ قیامت قائم ہو گی اور اگر میں اپنے رب کے پاس گیا بھی تو بے شک میرے لیے اس کے ہاں بھلائی ہی ہو گی ہم کافروں کو ضرور بتائیں گے جو کچھ وہ کرتے رہے اور ہم ضرور انہیں سخت عذاب چکھائیں گے'),
(4269, 41, 51, 'اور جب ہم نے انسان پر انعام کیا تو اس نے منہ پھیر لیا اور کنارہ کش ہو گیا اور جب اس کو تکلیف پہنچی تو لمبی چوڑی دعا کرنے لگا'),
(4270, 41, 52, 'کہہ دو بھلا دیکھوتو سہی اگر یہ قرآن الله کی طرف سے ہو پھر تم اس کا انکار کر بیٹھے تو ایسے پرلے درجہ کے ضدی سے کون زیادہ گمراہ ہو گا'),
(4271, 41, 53, 'عنقریب ہم اپنی نشانیاں انہیں دنیا میں دکھائیں گے اور خود ان کے نفس میں یہاں تک کہ ان پر واضح ہو جائے گا کہ وہی حق ہے کیا ان کے رب کی یہ بات کافی نہیں کہ وہ ہر چیز کو دیکھ رہا ہے'),
(4272, 41, 54, 'خبردار انہیں اپنے رب کے پاس حاضر ہونے میں شک ہے خبردار بے شک وہ ہر چیز کوگھیرے ہوئے ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حمۤ'),
(4274, 42, 2, 'عۤسۤقۤ'),
(4275, 42, 3, 'اسی طرح سے الله زبردست حکمت والا آپ کی طرف وحی کرتا ہے اور ان کی طرف بھی (کرتا تھا) جو آپ سے پہلے تھے'),
(4276, 42, 4, 'اسی کا ہے جو کچھ آسمانوں اور زمین میں ہے اور وہ بلند مرتبہ بزرگی والا ہے'),
(4277, 42, 5, 'قریب ہے کہ آسمان اوپر سے پھٹ جائیں اور سب فرشتے اپنے رب کی حمد کے ساتھ تسبیح کرتے ہیں اور ان کے لیے جو زمین میں ہیں مغفرت مانگتے ہیں خبردار بے شک الله ہی بخشنے والا نہایت رحم والا ہے'),
(4278, 42, 6, 'اور وہ لوگ جنہوں نے اس کے سوا اور کارساز بنا رکھے ہیں الله ان کا حال دیکھ رہا ہے اور آپ ان کے ذمہ دار نہیں ہیں'),
(4279, 42, 7, 'اور اسی طرح ہم نے آپ پر عربی زبان میں قرآن نازل کیا تاکہ آپ مکہ والوں اور اس کے آس پاس والوں کو ڈرائیں اور قیامت کے دن سے بھی ڈرائیں جس میں کوئی شبہ نہیں (اس روز) ایک جماعت جنت میں اور ایک جماعت جہنم میں ہو گی'),
(4280, 42, 8, 'اور اگر الله چاہتا تو ان سب کو ایک ہی جماعت کر دیتا لیکن الله جسے چاہتا ہے اپنی رحمت میں داخل کرتا ہے اور ظالموں کا نہ کوئی دوست ہے اور نہ کوئی مددگار'),
(4281, 42, 9, 'کیا انہوں نے اس کے سوا اوربھی مددگار بنا رکھے ہیں پھر الله ہی مددگار ہے اور وہی مردووں کو زندہ کرے گا اور وہ ہر چیز پر قادر ہے'),
(4282, 42, 10, 'اور جس بات میں بھی تم اختلاف کرتے ہو سو اس کا فیصلہ الله کے سپرد ہے وہی الله میرا رب ہے اسی پر میرا بھروسہ ہے اور اس کی طرف میں رجوع کرتا ہوں'),
(4283, 42, 11, 'وہ آسمانوں اور زمین کا پیدا کرنے والا ہے اسی نے تمہاری جنس سے تمہارے جوڑے بنائے اور چارپایوں کےبھی جوڑے بنائے تمہیں زمین میں پھیلاتا ہے کوئی چیز اس کی مثل نہیں اور وہ سننے والا دیکھنے والا ہے'),
(4284, 42, 12, 'اس کے ہاتھ میں آسمانوں اور زمین کی کنجیاں ہیں روزی کشادہ کرتا ہے جس کی چاہے اور تنگ کر دیتا ہے بے شک وہ ہر چیز کو جاننے والا ہے'),
(4285, 42, 13, 'تمہارے لیے وہی دین مقرر کیا جس کا نوح کو حکم دیا تھا اور اسی راستہ کی ہم نے آپ کی طرف وحی کی ہے اور اسی کا ہم نے ابراھیم اور موسیٰ اور عیسیٰ کو حکم دیا تھا کہ اسی دین پر قائم رہو اور اس میں پھوٹ نہ ڈالنا جس چیز کی طرف آپ مشرکوں کو بلاتے ہیں وہ ان پر گراں گزرتی ہے الله جسے چاہے اپنی طرف کھینچ لیتا ہے اور جو اس کی طرف رجوع کرتا ہے اسے راہ دکھاتا ہے'),
(4286, 42, 14, 'اور اہلِ کتاب جوجدا جدا فرقے ہوئے تو علم آنے کے بعد اپنی باہمی ضد سے ہوئے اور اگر تیرے رب کی طرف سے ایک وقت مقرر (قیامت) تک کا وعدہ نہ ہوتا تو ان میں فیصلہ ہوگیا ہوتا اور جو ان کے بعد کتاب کے وارث بنائے گئے ہیں (زمانہِ نبوی کے اہل کتاب) وہ اس (دین) کی نسبت حیرت انگیز شک میں ہیں'),
(4287, 42, 15, 'تو آپ اسی دین کی طرف بلائیے اور قائم رہیئے جیسا آپ کو حکم دیا گیا ہے اور ان کی خواہشوں پر نہ چلیئے اور کہہ دو کہ میں اس پر یقن لایا ہوں جو اللهنےکتاب نازل کی ہے اور مجھے حکم دیا گیا ہے کہ میں تمہارے درمیان انصاف کروں اللہ ہی ہمارا اور تمہارا پروردگار ہے ہمارے لیے ہمارےاعمال ہیں اور تمہارے لیے تمہارے اعمال اور تمہارے درمیان کوئی جھگڑا نہیں الله ہم سب کو جمع کر لے گا اور اسی کی طرف لوٹ کر جانا ہے'),
(4288, 42, 16, 'اور جو لوگ الله (کے دین) کے بارے میں جھگڑتے ہیں بعد اس کے کہ وہ مان لیا گیا ان لوگوں کی حجّت ان کے رب کے ہاں باطل ہے اور ان پر غضب ہے اور ان کے لیے سخت عذاب ہے'),
(4289, 42, 17, 'الله ہی ہے جس نے سچی کتاب اور ترازو نازل کی اور آپ کو کیا معلوم شاید قیامت قریب ہو'),
(4290, 42, 18, 'اس کی جلدی تووہی کرتے ہیں جو اس پر ایمان نہیں رکھتے اور جو ایمان رکھتے ہیں وہ اس سے ڈر رہے ہیں اور جانتے ہیں کہ وہ برحق ہے خبردار بے شک جو لوگ قیامت کے بارہ میں جھگڑا کرتے ہیں وہ پرلے درجے کی گمراہی میں ہیں'),
(4291, 42, 19, 'الله اپنے بندوں پر بڑا مہربان ہے جسے (جس قدر) چاہے روزی دیتا ہے اوروہ بڑا طاقتور زبردست ہے'),
(4292, 42, 20, 'جو کوئی آخرت کی کھیتی کا طالب ہو ہم اس کے لیے اس کھیتی میں برکت دیں گے اور جو دنیا کی کھیتی کا طالب ہو اسے (بقدر مناسب) دنیا میں دیں گے اور آخرت میں اس کا کچھ حصہ نہیں ہوگا'),
(4293, 42, 21, 'کیا ان کے اور شریک ہیں جنہوں نے ان کے لیے دین کا وہ طریقہ نکالا ہے جس کی الله نے اجازت نہیں دی اور اگر فیصلہ کا وعدہ نہ ہوا ہوتا تو ان کا دنیا ہی میں فیصلہ ہو گیا ہو تا اور بے شک ظالموں کے لیے دردناک عذاب ہے'),
(4294, 42, 22, 'آپ ظالموں کو (قیامت کے دن) دیکھیں گے کہ اپنے اعمال (کے وبال) سے ڈر رہے ہوں گے اور ان پر پڑنے والا اور جو لوگ ایمان لائے اور نیک کام کیے وہ بہشت کے باغوں میں ہوں گے انہیں جو چاہیں گے اپنے رب کے ہاں سے ملے گا یہی وہ بڑا فضل ہے'),
(4295, 42, 23, 'یہی وہ (فضل) جس کی الله اپنے بندوں کو خوشخبری دے دیتا ہے جو ایمان لائے اور نیک کام کیے کہہ دو میں تم سے اس پر کوئی اجرات نہیں مانگتا بجز رشتہ داری کی محبت کے اور جو نیکی کمائے گا تو ہم اس میں اس کے لیے بھلائی زیادہ کردیں گے بے شک الله بخشنے والا قدردان ہے'),
(4296, 42, 24, 'کیا وہ کہتے ہیں کہ آپ نے الله پر جھوٹ باندھا ہے پس اگر الله چاہے تو آپ کے دل پر مُہر کر دے اور الله باطل کو مٹا دیتا ہے اور سچ کو اپنی کلام سے ثابت کر دیتا ہے بے شک وہ سینوں کے بھید خوب جانتا ہے'),
(4297, 42, 25, 'اور وہی ہے جو اپنے بندوں کی توبہ قبول کرتا ہے اور ان کے گناہ معاف کر دیتا ہے اور جانتا ہے جو تم کرتے ہو'),
(4298, 42, 26, 'اور ان کی دعا قبول کرتا ہے جو ایمان لائے اور نیک کام کیے اور انہیں اپنے فضل سے زیادہ دیتا ہے او رکافروں کے لیے سخت عذاب ہے'),
(4299, 42, 27, 'اور اگر الله اپنے بندوں کی روزی کشادہ کر دے تو زمین پر سرکشی کرنے لگیں لیکن وہ ایک اندازے سے اتارتا ہے جتنی چاہتا ہے بے شک وہ اپنے بندوں سے خوب خبردار دیکھنے والا ہے'),
(4300, 42, 28, 'اور وہی ہے جو ناامید ہو جانے کے بعد مینہ برساتا ہے اور اپنی رحمت کو پھیلاتا ہے اور وہی کارساز احمد کے لائق ہے'),
(4301, 42, 29, 'اور اس کی نشانیوں میں سے ایک یہ بھی ہے کہ آسمانوں اور زمین کو بنایا اور اس پر ہر قسم کے چلنے والے جانور پھیلائے اوروہ جب چاہے گا ان کے جمع کرنے پر قادر ہے'),
(4302, 42, 30, 'اور تم پر جو مصیبت آتی ہے تو وہ تمہارے ہی ہاتھوں کے کیے ہوئے کاموں سے آتی ہے اور وہ بہت سے گناہ معاف کر دیتا ہے'),
(4303, 42, 31, 'اور تم زمین میں عاجز کرنے والے نہیں اور سوائے الله کے نہ کوئی تمہارا کارساز ہے اور نہ کوئی مددگار'),
(4304, 42, 32, 'اور اس کی نشانیوں میں سے سمندر میں پہاڑوں جیسے جہاز ہیں'),
(4305, 42, 33, 'اگر وہ چاہے تو ہوا کو ٹھیرا دے پس وہ اس کی سطح پر کھڑ ے رہ جائیں بے شک اس میں ہر صبر کرنے والے شکر گزار کے لیے نشانیاں ہیں'),
(4306, 42, 34, 'یا ان کے برے اعمال کے سبب سے انہیں تباہ کر دے اور بہتوں کو معاف بھی کر دیتا ہے'),
(4307, 42, 35, 'اور جان لیں وہ جو ہماری آیتوں میں جھگڑتے ہیں کہ ان کے لیے پناہ کی کوئی جگہ نہیں'),
(4308, 42, 36, 'پھر جو کچھ تمہیں دیا گیا ہے وہ دنیا کی زندگی کا سامان ہے اور جو کچھ الله کے پاس ہے وہ بہتر اور سدا رہنے والا ہے یہ ان کے لیے ہے جو ایمان لائے اور اپنے رب پر توکل کرتے ہیں'),
(4309, 42, 37, 'اوروہ جو بڑے بڑے گناہوں اور بے حیائی سے بچتے ہیں اور جب غضہ ہوتے ہیں تو معاف کر دیتے ہیں'),
(4310, 42, 38, 'اور وہ جو اپنے رب کا حکم مانتے ہیں اور نماز ادا کرتے ہیں اور ان کا کام باہمی مشورے سے ہوتا ہے اور ہمارے دیے ہوئے میں سے کچھ دیا بھی کرتے ہیں'),
(4311, 42, 39, 'اور وہ لوگ جب ان پر ظلم ہوتا ہے تو بدلہ لیتے ہیں'),
(4312, 42, 40, 'اوربرائی کا بدلہ ویسی ہی برائی ہے پس جس نے معاف کر دیا اور صلح کر لی تو اس کا اجر الله کے ذمہ ہے بے شک وہ ظالموں کو پسند نہیں کرتا'),
(4313, 42, 41, 'اور جو کوئی ظلم اٹھانے کے بعد بدلہ لے تو ان پر کوئی الزام نہیں'),
(4314, 42, 42, 'الزام تو ان پر ہے جو لوگوں پر ظلم کرتے ہیں اور ملک میں ناحق سرکشی کرتے ہیں یہی ہیں جن کے لیے دردناک عذاب ہے'),
(4315, 42, 43, 'اور البتہ جس نے صبر کیا اور معاف کر دیا بے شکے یہ بڑی ہمت کا کام ہے'),
(4316, 42, 44, 'اور جسے الله گمراہ کر دے سو اس کے بعد اس کا کوئی کارساز نہیں اور ظالموں کو دیکھیں گے جب وہ عذاب دیکھیں گے تو کہیں گے کیا واپس جانے کا بھی کوئی راستہ ہے'),
(4317, 42, 45, 'اور آپ انہیں دیکھیں گے کہ وہ دوزخ کے سامنے لائے جائيں گے ایسے حال میں ذلت کے مارے جھکے ہوئے ہوں گے چھپی نگاہ سے دیکھ رہے ہوں گے اور وہ لوگ کہیں گے جو ایمان لائے تھے بے شک خسارہ اٹھانے والے وہی لوگ ہیں جنہوں نے اپنے آپ کو اوراپنے گھر والوں کو قیامت کے دن خسارہ میں رکھا خبردار بے شک ظالم ہی ہمیشہ عذاب میں ہوں گے'),
(4318, 42, 46, 'اور ان کا الله کے سوا کوئی بھی حمایتی نہ ہوگا کہ ان کو بچائے اورجسے الله گمراہ کرے اس کے لیے کوئی بھی راستہ نہیں'),
(4319, 42, 47, 'اس سے پہلے اپنے رب کا حکم مان لو کہ وہ دن آجائے جو الله کی طرف سے ٹلنے والا نہیں اس دن تمہارے لیے کوئی جائے پناہ نہیں ہو گی اور نہ تم انکارکر سکو گے'),
(4320, 42, 48, 'پھر بھی اگر نہ مانیں تو ہم نے آپ کو ان پر محافظ بنا کرنہیں بھیجا ہے آپ پر تو صرف پہنچا دینا ہے اور جب ہم انسان کو اپنی کوئی رحمت چکھاتے ہیں تو اس سے خوش ہوجاتا ہے اور اگر اس پر اس کے اعمال سے اس سے کوئی مصیبت پڑ جاتی ہے تو انسان بڑا ہی ناشکرا ہے'),
(4321, 42, 49, 'آسمانوں اور زمین میں الله ہی کی بادشاہی ہے جو چاہتا ہے پیدا کر تا ہے جسے چاہتا ہے لڑکیاں عطا کرتا ہے اور جسے چاہتا ہے لڑکے بخشتا ہے'),
(4322, 42, 50, 'یا لڑکے اور لڑکیاں ملا کر دیتا ہے اور جسے چاہتا ہے بانجھ کر دیتا ہے بے شک وہ خبردار قدرت والا ہے'),
(4323, 42, 51, 'اور کسی انسان کا حق نہیں کہ اس سے الله کلام کر لے مگر بذریعہ وحی یا پردے کے پیچھے سے یا کوئی فرشتہ بھیج دے کہ وہ اس کے حکم سے القا کر لے جو چاہے بے شک وہ بڑاعالیشان حکمت والا ہے'),
(4324, 42, 52, 'اور اسی طرح ہم نے آپ کی طرف اپنا حکم سے قرآن نازل کیا آپ نہیں جانتے تھے کہ کتاب کیا ہے او رایمان کیا ہے او رلیکن ہم نے قرآن کو ایسا نور بنایا ہے کہ ہم اس کے ذریعہ سے ہم اپنے بندوں سے جسے چاہتے ہیں ہدایت کرتے ہیں اور بے شک آپ سیدھا راستہ بتاتے ہیں'),
(4325, 42, 53, 'اس الله کا راستہ جس کے قبضہ میں آسمانوں اور زمین کی سب چیزیں ہیں خبردار الله ہی کی طرف سب کام رجوع کرتے ہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حمۤ'),
(4327, 43, 2, 'روشن کتاب کی قسم ہے'),
(4328, 43, 3, 'ہم نے اسے عربی زبان میں قرآن بنایا ہے تاکہ تم سمجھو'),
(4329, 43, 4, 'اور یہ کتاب لوح محفوظ میں ہمارے نزدیک بلند مرتبہ حکمت والی ہے'),
(4330, 43, 5, 'کیا تمہارے سمجھانے سے ہم اس لیے منہ پھیر لیں گے کہ تم بیہودہ لوگ ہو'),
(4331, 43, 6, 'اور پہلے لوگوں میں بھی ہم نے بہت سے نبی بھیجے ہیں'),
(4332, 43, 7, 'اور ان کے پاس ایسا کوئی نبی نہ آتا تھا کہ جس سے وہ ٹھٹھا نہ کرتے تھے'),
(4333, 43, 8, 'پھر ہم نے ان میں بڑے زور والوں کو ہلاک کر دیا او رپہلوں کی مثال گزرچکی ہے'),
(4334, 43, 9, 'اور اگر آپ ان سے پوچھیں کہ آسمانوں اور زمین کو کس نے پیدا کیا ہے تو ضرور کہیں گے کہ انہیں اس بڑے زبردست جاننے والے نے پیدا کیا ہے'),
(4335, 43, 10, 'وہ جس نے زمین کو تمہارا بچھونا بنایا اور تمہارے لیے اس میں راستے بنائے تاکہ تم راہ پاؤ'),
(4336, 43, 11, 'اور وہ جس نے آسمان سے اندازے کے ساتھ پانی اتارا پھر ہم نے اس سے مردہ بستی کو زندہ کیا تم بھی اس طرح (قبروں سے) نکالے جاؤ گے'),
(4337, 43, 12, 'اوروہ جس نے ہر قسم کے جوڑے بنائے اور تمہارے لیے وہ کشتیاں اور چار پائے بنائے جن پر تم سوار ہوتے ہو'),
(4338, 43, 13, 'تاکہ ان کی پیٹھ پر چڑھ کر اپنے رب کا احسان یاد کرو جب کہ تم ان پر خوب بیٹھ جاؤ اورکہو وہ ذات پاک ہے جس نے ہمارے لیے اسے مطیع کر دیا اور ہم اسے قابو میں لانے والے نہ تھے'),
(4339, 43, 14, 'اور بےشک ہم اپنے رب کی طرف لوٹنے والے ہیں'),
(4340, 43, 15, 'اورلوگوں نے اس کے بندوں کو اس کی اولاد بنا دیا بے شک انسان صریح ناشکرا ہے'),
(4341, 43, 16, 'کیا اس نے اپنی مخلوقات میں سے بیٹیاں لے لیں اور تمہیں بیٹے چن کر دیئے'),
(4342, 43, 17, 'اور جب ان میں سے کسی کو اس چیز کی خوشخبری دی جائے جسے رحمان کے لیے ٹھیراتا ہے تو اس کا منہ سیاہ ہو جاتا ہے اور و ہ دل میں کڑھتا رہتا ہے'),
(4343, 43, 18, 'کیا اس کے لیے وہ ہے جو زیور میں پلتی ہے اور وہ جھگڑتے میں بات نہیں کر سکتی'),
(4344, 43, 19, 'اورفرشتوں کو جو رحمان کے بندے ہیں عورتیں فرض کر لیا کیا انھوں نے پیدا ہوتے دیکھا ہے ان کی گواہی لکھی جائے گی اور ان سے پوچھا ئے گا'),
(4345, 43, 20, 'اور کہتے ہیں اگر رحمان چاہتا تو ہم انہیں نہ پوجتے انہیں اس کی کچھ خبر نہیں وہ محض اٹکل دوڑاتے ہیں'),
(4346, 43, 21, 'کیا ہم نے انہیں اس سے پہلے کوئی کتاب دی ہے کہ یہ اس پر قائم ہیں'),
(4347, 43, 22, 'بلکہ وہ کہتے ہیں کہ ہم نے اپنے باپ دادا کو ایک طریقہ پر پایا ہے اور انہیں کے ہم پیرو ہیں'),
(4348, 43, 23, 'اور اسی طرح ہم نے آپ سے پہلے کسی گاؤں میں بھی کوئی ڈرانے والا بھیجا تو وہاں کے دولت مندوں نے (یہی) کہا کہ ہم نے اپنے باپ دادا کو ایک طریقہ پر پایا اور ہم انہیں کے پیرو ہیں'),
(4349, 43, 24, 'رسول نے کہا اگرچہ میں تمہارے پاس اس سے بھی بہتر طریقہ لاؤں جس پر تم نے اپنے باپ دادا کو پایا انہوں نے کہا جو کچھ تو لایا ہے ہم اس کے منکر ہیں'),
(4350, 43, 25, 'پھر ہم نے ان سے بدلہ لیا پھر دیکھ جھٹلانے والوں کا انجام کیا ہوا'),
(4351, 43, 26, 'اور جب ابراھیم نے اپنے باپ اور اپنی قوم سے کہا کہ بے شک میں ان سے بیزار ہوں جن کی تم عبادت کرتے ہو'),
(4352, 43, 27, 'سوائے اس ذات کے جس نے تجھے پیدا کیا سو بے شک وہی تجھے راہ دکھائے گا'),
(4353, 43, 28, 'اور یہی بات اپنی اولاد میں پیچھے چھوڑ گیا تاکہ وہ رجوع کریں'),
(4354, 43, 29, 'بلکہ میں نے ان کو اوران کے باپ دادا کو خوب سامان دیا یہاں تک کہ ان کے پاس سچا قرآن اور صاف صاف بتانے والا رسول آیا'),
(4355, 43, 30, 'اورجب ان کے پاس سچا قرآن پہنچا تو کہا کہ یہ توجادو ہے اور ہم اسے نہیں مانتے'),
(4356, 43, 31, 'اور کہا کیوں یہ قرآن ان دو بستیوں کےکسی سردار پر نازل نہیں کیا گیا'),
(4357, 43, 32, 'کیا وہ آپ کے رب کی رحمت تقسیم کرتے ہیں ان کی روزی تو ہم نے ان کے درمیان دنیا کی زندگی میں تقسیم کی ہے اور ہم نے بعض کے بعض پر درجے بلند کیے تاکہ ایک دوسرے کو محکوم بنا کررکھے اور آپ کے رب کی رحمت اس سے کہیں بہتر ہے جو وہ جمع کرتے ہیں'),
(4358, 43, 33, 'اوراگر یہ نہ ہوتا کہ سب لوگ ایک طریقہ کے ہوجائیں گے (کافر) تو جو الله کے منکر ہیں انکے گھروں کی چھت اور ان پر چڑھنے کی سیڑھیاں چاندی کی کر دیتے'),
(4359, 43, 34, 'اوران کے گھروں کے دروازے اور تخت بھی چاندی کے کر دیتے جن پر وہ تکیہ لگا کر بیٹھتے ہیں'),
(4360, 43, 35, 'اور سونے کے بھی اور یہ سب کچھ دنیا کی زندگی کا سامان ہے اور آخرت آپ کے رب کے ہاں پرہیزگاروں کے لیے ہے'),
(4361, 43, 36, 'اورجو الله کی یاد سے غافل ہوتا ہے تو ہم اس پر ایک شیطان متعین کر تے ہیں پھر وہ اس کا ساتھی رہتا ہے'),
(4362, 43, 37, 'اور شیاطین آدمیوں کو راستے سے روکتے ہیں اور وہ سمجھتے ہیں کہ ہم راہِ راست پر ہیں'),
(4363, 43, 38, 'یہاں تک کہ جب وہ ہمارے پاس آئے گا تو کہے گا اے کاش میرے اور تیرے درمیان مشرق اور مغرب کی دوری ہوتی پس کیسا برا ساتھی ہے'),
(4364, 43, 39, 'اورآج تمہیں یہ بات ہر گز نفع نہ دے گی چونکہ تم نے ظلہم کیا تھا بے شک تم عذاب میں شریک ہو'),
(4365, 43, 40, 'پس کیا آپ بہروں کو سنا سکتے ہیں یااندھوں کو راہ دکھا سکتے ہیں اور انہیں جو صریح گمراہی میں ہیں'),
(4366, 43, 41, 'پس اگر ہم آپ کو (دنیا سے) اٹھالیں تو بھی ہم ان سے بدلہ لیں گے'),
(4367, 43, 42, 'یا اگر ہم آپ کووہ دکھا بھی دیں جس کا ہم نے ان سے وعدہ کیا ہے تو ہم ان پر قادر ہیں'),
(4368, 43, 43, 'پھر آپ مضبوطی سے پکڑیں اسے جو آپ کی طرف وحی کیا گیا ہے بے شک آپ سیدھے راستہ پر ہیں'),
(4369, 43, 44, 'اور بے شک وہ (قرآن) آپ کے لیے اور آپ کی قوم کے لیے ایک نصیحت ہے اور تم سب سے ا سکی باز پرس ہو گی'),
(4370, 43, 45, 'اور آپ ان سب پیغمبروں سے جنہیں ہم نے آپ سے پہلے بھیجا ہے پوچھ لیجیئے کیا ہم نے رحمان کے سوا دوسرے معبود ٹھیرا لئے تھے کہ انکی عبادت کی جائے'),
(4371, 43, 46, 'اور ہم نے موسیٰ کو اپنی نشانیاں دے کر فرعون اور اس کے امرائے دربار کی طرف بھیجا تھا سو اس نے کہا کہ میں پروردگار عالم کا رسول ہوں'),
(4372, 43, 47, 'پس جب وہ ان کے پاس ہماری نشانیاں لایا تو وہ اس کی ہنسی اڑانے لگے'),
(4373, 43, 48, 'اور ہم ان کو جو کوئی نشانی دکھاتے تھے تو ایک دوسرے سے بڑھ کر ہوتی تھی اور ہم نے انہیں عذاب میں پکڑا تاکہ وہ باز آ جائیں'),
(4374, 43, 49, 'اور انہوں نے کہا اے جادوگر اپنے رب سے ہمارے لیے اس عہد سے جو تجھ سے اس نے کیا ہے دعا کر ہم ضرور راہ پر آجائیں گے'),
(4375, 43, 50, 'پھر جب ہم ان سے عذاب ہٹا لیتے تو اسی وقت عہد کو توڑ دیتے'),
(4376, 43, 51, 'اور فرعون نے اپنی قوم میں منادی کر کے کہہ دیا اے میری قوم کیا میرےلیے مصر کی بادشاہت نہیں اور کیا یہ نہریں میرے (محل کے) نیچے سے نہیں بہہ رہی ہیں پھر کیا تم نہیں دیکھتے'),
(4377, 43, 52, 'کیا میں اس سے بہتر نہیں ہوں جو ذلیل ہے اور صاف صاف بات بھی نہیں کر سکتا'),
(4378, 43, 53, 'پھر اس کے لیے سونےکے کنگن کیوں نہیں اتارے گئے یا اس کے ہمراہ فرشتے پرے باندھے ہوئے آئے ہوتے'),
(4379, 43, 54, 'پس اس نے اپنی قوم کو احمق بنا دیا پھر ا س کے کہنے میں آ گئے کیو ں کہ وہ بدکار لوگ تھے'),
(4380, 43, 55, 'پس جب انہوں نے ہمیں غصہ دلا دیا تو ہم نے ان سے بدلہ لیا ہم نے ان سب کو غرق کر دیا'),
(4381, 43, 56, 'پھر ہم نے انہیں گئے گزرے اور پیچھے آنے والوں کے لیے کہاوت بنا دیا'),
(4382, 43, 57, 'اورجب ابن مریم کی مثال بیان کی گئی تو اسی وقت آپ کی قوم کے لوگ اس سے کھلکھلا کر ہنسنے لگے'),
(4383, 43, 58, 'اورکہا کیا ہمارے معبود بہتر ہیں یا وہ یہ ذکر صرف آپ سے جھگڑنے کے لیے کرتے ہیں بلکہ وہ تو جھگڑالو ہی ہیں'),
(4384, 43, 59, 'وہ تو ہمارا ایک بندہ ہے جس پر ہم نے انعام کیا اور اسے بنی اسرائیل کے لیے نمونہ بنا دیا تھا'),
(4385, 43, 60, 'اور اگر ہم چاہیں تم میں سے فرشتے پیدا کریں جو زمین میں تمہاری جگہ رہیں'),
(4386, 43, 61, 'اور البتہ عیسیٰ قیامت کی ایک نشانی ہے پس تم اس میں شبہ نہ کرو اور میری تابعداری کرو یہی سیدھا راستہ ہے'),
(4387, 43, 62, 'اور تمہیں شیطان نہ روکنے پائیں کیوں کہ وہ تمہارا صریح دشمن ہے'),
(4388, 43, 63, 'اور جب عیسیٰ واضح دلیلیں لے کر آیا تھا تو اس نے کہا تھا کہ میں تمہارے پاس دانائی کی باتیں لایا ہوں اورتاکہ تم پر بعض وہ باتیں واضح کر دوں جن میں تم اختلاف کرتے تھے پس الله سے ڈرو اور میرا حکم مانو'),
(4389, 43, 64, 'بے شک الله ہی میرا اور تمہارا پروردگار ہے پس اسی کی عبادت کرو وہی سیدھا راستہ ہے'),
(4390, 43, 65, 'پھر لوگ ایک دوسرے سے مختلف ہو گئے پس جنہوں نے ظلم کیا ان کے لیے دردناک دن کے عذاب سے تباہی ہے'),
(4391, 43, 66, 'کیا وہ قیامت کے ہی منتظر ہیں کہ ان پر یکایک آجائے اور ان کو خبر بھی نہ ہو'),
(4392, 43, 67, 'اس دن دوست بھی آپس میں دشمن ہو جائیں گے مگر پرہیز گار لوگ'),
(4393, 43, 68, '(کہا جائے گا) اے میرے بندو تم پر آج نہ کوئی خوف ہے اور نہ تم غمگین ہو گے'),
(4394, 43, 69, 'جو لوگ ہماری آیتوں پر ایمان لائے اور فرمانبردار تھے'),
(4395, 43, 70, 'تم اور تمہاری بیویاں خوشیاں کرتے ہوئے جنت میں داخل ہوجاؤ'),
(4396, 43, 71, 'ان کے سامنے سونے کے پیالے پیش کیے جائیں گے اور آبخورے بھی اور وہاں جس چیز کو دل چاہے گا اوبر جس سے آنکھیں خوش ہوں گی موجود ہو گی اور تم اس میں ہمیشہ رہو گے'),
(4397, 43, 72, 'اور یہی وہ جنت ہے جس کے تم وارث بنائے گئے ہو ان اعمال کے بدلے میں جو تم کرتے تھے'),
(4398, 43, 73, 'تمہارے لیے وہاں بہت سے میوے ہیں جن میں سے کھایا کرو گے'),
(4399, 43, 74, 'بے شک گناہگار عذاب دوزخ ہی میں ہمیشہ رہیں گے'),
(4400, 43, 75, 'ان سے ہلکا نہ کیا جائے گا اور وہ اسی میں مایوس پڑے رہیں گے'),
(4401, 43, 76, 'اور ہم نے تو ان پر ظلم نہیں کیا لیکن وہ خود ہی ظالم تھے'),
(4402, 43, 77, 'اور وہ پکاریں گے اے مالک تیرا پروردگار ہمارا کام تمام کر دے وہ کہے گا بے شک تمہیں تو ہمیشہ رہنا ہے'),
(4403, 43, 78, 'ہم تو تمہارے پاس سچا دین لا چکےاورلیکن تم میں سے اکثر دین حق سے نفرت کرتے ہیں'),
(4404, 43, 79, 'کیا انہوں نے کوئی بات طے کرلی ہے تو ہم بھی طے کرنے والے ہیں'),
(4405, 43, 80, 'کیا وہ خیال کرتے ہیں کہ ہم ان کا بھید اور مشورہ نہیں سنتے کیوں نہیں اور ہمارے بھیجے ہوئے فرشتے ان کے پاس لکھ رہے ہیں'),
(4406, 43, 81, 'کہہ دو اگر الله کا بیٹا ہوتا تو سب سے پہلے میں عبادت کرتا'),
(4407, 43, 82, 'آسمانوں اور زمین اور عرش کا رب پاک ہے ان باتوں سے جو وہ بناتے ہیں'),
(4408, 43, 83, 'پھر انہیں چھوڑ دو بک بک اور کھیل کود میں لگے رہیں یہاں تک کہ وہ دن دیکھ لیں جس کا ان سے وعدہ کیا جاتا ہے'),
(4409, 43, 84, 'او روہی ہے جو آسمان میں بھی معبود ہے اور زمین میں بھی معبود ہے اور وہی حکمت والا جاننے والا ہے'),
(4410, 43, 85, 'اور وہ بڑا بابرکت ہے جس کی حکومت آسمانوں اور زمین میں ہے اور جو ان دونوں کے درمیان موجود ہے اور اسی کے پاس قیامت کا علم ہے اور اسی کی طرف تم سب لوٹائے جاؤ گے'),
(4411, 43, 86, 'اورجنہیں وہ اس کے سوا پکارتے ہیں انہیں تو شفاعت کا بھی اختیا رنہیں ہاں جن لوگوں نے حق بات کا اقرار کیا تھا اور وہ تصدیق بھی کرتے تھے'),
(4412, 43, 87, 'اور اگر آپ ان سے پوچھیں کہ انہیں کس نے پیدا کیا ہے تو ضرور کہیں گے الله نے پھر کہا ں بہکے جا رہے ہیں'),
(4413, 43, 88, 'اور قسم ہے رسول کے یا رب پکارنے کی بے شک یہ ایسے لوگ ہیں کہ ایمان نہ لائیں گے'),
(4414, 43, 89, 'پس آپ بھی ان سے منہ پھیر لیں اور سلام کہہ دیں پس انہیں خو دمعلوم ہو جائے گا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حمۤ'),
(4416, 44, 2, 'روشن کتاب کی قسم ہے'),
(4417, 44, 3, 'ہم نے اسے مبارک رات میں نازل کیا ہے بے شک ہمیں ڈرانا مقصود تھا'),
(4418, 44, 4, 'سارے کام جو حکمت پر مبنی ہیں اسی رات تصفیہ پاتے ہیں'),
(4419, 44, 5, 'ہمارے خاص حکم سے کیوں کہ ہمیں رسول بھیجنا منظور تھا'),
(4420, 44, 6, 'آپ کے پروردگار کی رحمت ہے بے شک وہی سب کچھ سننے والا جاننے والا ہے'),
(4421, 44, 7, 'آسمانوں اور زمین کا رب ہے اور جو کچھ ان کے درمیان ہے اگر تم یقین کر نے والے ہو'),
(4422, 44, 8, 'اس کے سوا اورکوئی معبود نہیں زندہ کرتا ہے اور مارتا ہے تمہارا بھی رب ہے او رتمہارے پہلے باپ دادا کا بھی'),
(4423, 44, 9, 'بلکہ وہ تو شک میں کھیل رہے ہیں'),
(4424, 44, 10, 'سو اس دن کا انتظار کیجیئے کہ آسمان دھواں ظاہر لائے'),
(4425, 44, 11, 'جو لوگوں کو ڈھانپ لے یہی دردناک عذاب ہے'),
(4426, 44, 12, 'اے ہمارے رب ہم سے یہ عذاب دور کر دے بے شک ہم ایمان لانے والے ہیں'),
(4427, 44, 13, 'وہ کہاں سمجھتے ہیں حالانکہ ان کے پاس کھول کر سنانے والا رسول بھی آ چکا ہے'),
(4428, 44, 14, 'پھر اس سے بھی پھر گئے اور کہا کہ سکھایا ہوا دیوانہ ہے'),
(4429, 44, 15, 'ہم اس عذاب کو تھوڑی دیر کے لیےہٹا دیں گے تم پھر وہی کرنے والے ہو'),
(4430, 44, 16, 'جس دن ہم بڑی سخت پکڑ پکڑیں گے بے شک ہم بدلہ لینے والے ہیں'),
(4431, 44, 17, 'اور ان سے پہلے ہم فرعون کی قوم کو آزما چکے ہیں اور ان کے پاس ایک عزت والا رسول بھی آیا تھا'),
(4432, 44, 18, 'کہ الله کے بندوں کو میرے حوالہ کر دو بے شک میں تمہارے لیے ایک امانت دار رسول ہوں'),
(4433, 44, 19, 'اور یہ کہ الله کے خلاف سرکشی نہ کرو میں تمہارے پاس کھلی دلیل لایا ہوں'),
(4434, 44, 20, 'اور بے شک میں نے اپنے اور تمہارے رب کی پناہ لی ہے اس واسطے کہ تم مجھے سنگسار کرو'),
(4435, 44, 21, 'اور اگرتم میری بات پر ایمان نہیں لاتے تو مجھ سے الگ ہو جاؤ'),
(4436, 44, 22, 'پس اس نے اپنے رب کو پکارا کہ یہ تو مجرم لوگ ہیں'),
(4437, 44, 23, 'حکم ہوا پس میرے بندوں کو رات کے وقت لے چل کیوں کہ تمہارا پیچھا کیا جائے گا'),
(4438, 44, 24, 'اور سمندر کو ٹھہرا ہوا چھوڑ دے بے شک وہ لشکر ڈوبنے والے ہیں'),
(4439, 44, 25, 'کتنے انہوں نے باغات اور چشمے چھوڑے ہیں'),
(4440, 44, 26, 'اور کھیتیاں اور مقام عمدہ'),
(4441, 44, 27, 'اورنعمت کے سازو سامان جس میں وہ مزے کیا کرتے تھے'),
(4442, 44, 28, 'اسی طرح ہوا اورہم نے ان کا ایک دوسری قوم کو وارث کر دیا'),
(4443, 44, 29, 'پس ان پر نہ آسمان روویا اور نہ زمین اور نہ ان کو مہلت دی گئی'),
(4444, 44, 30, 'اورہم نے بنی اسرائیل کو اس ذلت کےعذاب سے نجات دی'),
(4445, 44, 31, '(یعنی) فرعون سے بے شک وہ ایک سرکش حد سے بڑھنے والا تھا'),
(4446, 44, 32, 'اور ہم نے اپنے علم سے ان کو جہان والوں پر چن لیا تھا'),
(4447, 44, 33, 'اور ہم نے ان کو نشانیاں دی تھیں جن میں صریح آزمائش تھی'),
(4448, 44, 34, 'بے شک یہ لوگ کہتے ہیں'),
(4449, 44, 35, 'کہ او رمرنا نہیں ہے مگر یہی ہمارا پہلی بار کا مرنا اور ہم دوبارہ اٹھائے جانے والے نہیں'),
(4450, 44, 36, 'پس ہمارے باپ دادا کو لے آؤ اگر تم سچے ہو'),
(4451, 44, 37, 'کیا وہ بہتر ہیں یا تبع کی قوم اوروہ لوگ جو ان سے پہلے ہوئے ہم نے انہیں ہلاک کر دیا کیوں کہ وہ مجرم تھے'),
(4452, 44, 38, 'اور ہم نے آسمانوں اور زمین کو اورجو کچھ اس کے درمیان میں ہے کھیل کے لیے نہیں بنایا'),
(4453, 44, 39, 'ہم نے انہیں بہت ہی مصلحت سے بنایا ہے لیکن اکثر ان میں سے نہیں جانتے'),
(4454, 44, 40, 'بے شک فیصلہ کا دن ان سب کے لیے مقرر ہو چکا ہے'),
(4455, 44, 41, 'جس دن کوئی دوست کسی دوست کے کچھ بھی کام نہیں آئے گا اورنہ انہیں مدد ملے گی'),
(4456, 44, 42, 'مگر جس پر الله نے رحم کیابے شک وہ زبردست رحم والا ہے'),
(4457, 44, 43, 'بے شک تھوہر کا درخت'),
(4458, 44, 44, 'گناہگارو ں کا کھانا ہے'),
(4459, 44, 45, 'پگھلے ہوئے تانبے کی طرح پیٹو ں میں کھولے گا'),
(4460, 44, 46, 'جیسے پکتا ہوا پانی کھولتا ہے'),
(4461, 44, 47, 'اسے پکڑ لو پس اسے دوزخ کے درمیان دھکیل کر لے جاؤ'),
(4462, 44, 48, 'پھر اس کے سر پر عذاب کا کھولتا ہوا پانی ڈالو'),
(4463, 44, 49, 'چکھ بے شک تو تو بڑا عزت والا بزرگی والا ہے'),
(4464, 44, 50, 'بے شک یہی ہے جس کی نسبت تم شک کیا کرتے تھے'),
(4465, 44, 51, 'بے شک پرہیزگار ہی امن کی جگہ میں ہوں گے'),
(4466, 44, 52, 'باغوں اور چشموں میں'),
(4467, 44, 53, 'باریک ریشم اورگاڑھا پہن کر آمنے سامنے بیٹھے ہوں گے'),
(4468, 44, 54, 'یونہی ہوگا اور ہم ان کا نکاح بڑی آنکھوں والی حوروں سے کر دیں گے'),
(4469, 44, 55, 'وہ اس میں ہر قسم کا میوہ امن و اطمینان سے طلب کریں گے'),
(4470, 44, 56, 'وہا ں پہلی موت کے سوا اور موت کا مرہ نہ چکھیں گے اورانہیں الله دوزح کے عذاب سے بچا لے گا'),
(4471, 44, 57, 'یہ آپکے رب کا فضل ہوگا یہی وہ بڑی کامیابی ہے'),
(4472, 44, 58, 'اس قرآن کو ہم نے آپ کی زبان میں آسان کر دیا تاکہ وہ سمجھیں'),
(4473, 44, 59, 'پس آپ انتظار کیجیئے بے شک وہ بھی انتظار کر ہے ہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حمۤ'),
(4475, 45, 2, 'یہ کتاب الله زبردست حکمت والے کی طرف سے نازل ہوئی ہے'),
(4476, 45, 3, 'بے شک آسمانوں اور زمین میں ایمانداروں کے لیے نشانیاں ہیں'),
(4477, 45, 4, 'اور (نیز) تمہارے پیدا کرنے میں اور جانوروں کے پھیلانے میں یقین والوں کے لیے نشانیاں ہیں'),
(4478, 45, 5, 'اور (نیز) رات اور دن کے بدل کر آنے میں اور اس میں جو الله نے آسمان سے رزق (پانی) نازل کیا پھر اس کے ذریعے سے زمین کو اس کے مر جانے کے بعد زندہ کیا اور ہواؤں کے بدل کر لانے میں عقل مندوں کے لیے نشانیاں ہیں'),
(4479, 45, 6, 'یہ الله کی آیات ہیں جو ہم آپ کو بالکل سچی پڑھ کر سناتے ہیں پس الله اور اس کی آیات کے بعد وہ کس بات پر ایمان لائیں گے'),
(4480, 45, 7, 'ہر سخت جھوٹے گناہگار کے لیے تباہی ہے'),
(4481, 45, 8, 'جو آیات الہیٰ سنتا ہے جو اس پر پڑھی جاتی ہیں پھر نا حق تکبر کی وجہ سے اصرار کرتا ہے گویاکہ اس نے سنا ہی نہیں پس اسے دردناک عذاب کی خوشخبری دے دو'),
(4482, 45, 9, 'اورجب ہماری آیتوں میں سے کسی کو سن لیتاہے تو اس کی ہنسی اڑاتا ہے ایسوں کے لیے ذلت کا عذاب ہے'),
(4483, 45, 10, 'ان کے سامنے جہنم ہے اور جو کچھ انہوں نے کمایا تھا ان کے کچھ بھی کام نہ آئے گا اور نہ وہ معبود کام آئيں گے جنہیں الله کے سوا حمایتی بنا رکھا تھا اور ان کے لیے بڑاعذاب ہے'),
(4484, 45, 11, 'یہ (قرآن) تو ہدایت ہے اور جو اپنے رب کی آیتوں کے منکر ہیں ان کے لیے سخت دردناک عذاب ہے'),
(4485, 45, 12, 'الله ہی ہے جس نے تمہارے لیے سمندر کو تابع کر دیا تاکہ ا س میں اس کے حکم سے جہاز چلیں اورتاکہ تم اس کا فضل تلاش کرو اور تاکہ تم اس کا شکر کرو'),
(4486, 45, 13, 'اور اس نے آسمانوں اور زمین کی سب چیزوں کو اپنے فضل سے تمہارے کام پر لگا دیا ہے بے شک اس میں فکر کرنے والوں کے لیے نشانیاں ہیں'),
(4487, 45, 14, 'ان سے کہہ دو جو ایمان لائے کہ انہیں معاف کر دیں ایام الہیٰ (عذاب) کی امید نہیں رکھتے تاکہ وہ ایک قوم کو بدلہ دے اس کا جو وہ کرتے رہے'),
(4488, 45, 15, 'جو کوئی نیک کام کرتا ہے وہ اپنے ہی لیے کرتا ہے اورجو کوئی برائی کرتا ہے تو اپنے سر پر وبال لیتا ہے پھر تم اپنے رب کی طرف لوٹائے جاؤ گے'),
(4489, 45, 16, 'اور بے شک ہم نے بنی اسرائیل کو کتاب اورحکومت اور نبوت دی تھی اور ہم نے پاکیزہ چیزوں سے روزی دی اور ہم نے انہیں جہان والوں پر بزرگی دی'),
(4490, 45, 17, 'اور انہیں دین کے کھلے کھلے احکام بھی دیے پھر انہوں نے اختلاف کیا تو علم آنے کے بعد صرف آپس کی ضد سے بے شک آپ کا رب قیامت کے دن ان میں فیصلہ کرے گا جس چیز میں وہ باہم اختلاف کیا کرتے تھے'),
(4491, 45, 18, 'پھر ہم نے آپ کو دین کے ایک طریقہ پر مقرر کر دیا پس آپ اسی کی پیروی کیجیئے اور ان کی خواہشوں کی پیروی نہ کیجیئے جو علم نہیں رکھتے'),
(4492, 45, 19, 'کیوں کہ وہ الله کے سامنے آپ کے کچھ بھی کام نہ آئیں گے اور بے شک ظالم ایک دوسرے کے دوست ہیں اور الله ہی پرہیز گاروں کا دوست ہے'),
(4493, 45, 20, 'یہ قرآن لوگوں کے لیے بصیرت اورہدایت ہے اور یقین کرنے والو ں کے لیے رحمت ہے'),
(4494, 45, 21, 'کیا گناہ کرنے والوں نے یہ سمجھ لیا ہے کہ ہم ان کو ایمانداروں نیک کام کرنے والوں کے برابر کر دیں گے ان کا جینا اور مرنا برابر ہے وہ بہت ہی برا فیصلہ کرتے ہیں'),
(4495, 45, 22, 'اور الله نے آسمانوں اور زمین کو جسے چاہئیں بنایا ہے اور تاکہ ہر نفس کو اس کا بدلہ دیا جائے جو اس نے کمایا ہے اور ان پر کوئی ظلم نہ ہوگا'),
(4496, 45, 23, 'بھلا آپ نے اس کو بھی دیکھا جو اپنی خواہش کا بندہ بن گیا اور الله نے باوجود سمجھ کے اسے گمراہ کر دیا اور اس کے کان اور دل پر مہر کر دی اور اس کی آنکھوں پر پردہ ڈال دیا پھر الله کے بعد اسے کون ہدایت کر سکتا ہے پھر تم کیوں نہیں سمجھتے'),
(4497, 45, 24, 'اور کہتے ہیں ہمارا یہی دنیا کا جینا ہے ہم مرتے ہیں اور جیتے ہیں اور زمانہ ہی ہمیں ہلاک کرتا ہے حالانکہ انہیں اس کی کچھ بھی حقیقت معلوم نہیں محض اٹکلیں دوڑاتے ہیں'),
(4498, 45, 25, 'اور جب انہیں ہماری واضح آیتیں پڑھ کر سنائی جاتی ہیں تو سوائے اس کے ان کی اور کوئی دلیل نہیں ہوتی کہتے ہیں ہمارے باپ دادا کو لے آؤ اگر تم سچے ہو'),
(4499, 45, 26, 'کہہ دو الله ہی تمہیں زندہ کرتا ہے پھر تمہیں مارتا ہے پھر وہی تم سب کو قیامت میں جمع کرے گا جس میں کوئی شک نہیں لیکن اکثر آدمی نہیں جانتے'),
(4500, 45, 27, 'اور آسمانوں اور زمین کی بادشاہی الله ہی کی ہے اور جس دن قیامت قائم ہو گی اس دن جھٹلانے والے نقصان اٹھائیں گے'),
(4501, 45, 28, 'اور آپ ہر ایک جماعت کو گھٹنے ٹیکے ہوئے دیکھیں گے ہر ایک جماعت اپنے نامہٴ اعمال کی طرف بلائی جائے گی (کہا جائے گا) آج تمہیں تمہارے اعمال کا بدلہ دیا جائے گا'),
(4502, 45, 29, 'یہ ہمارا دفتر تم پر سچ سچ بول رہا ہے کیونکہ جو کچھ تم کیا کرتے تھے اسے ہم لکھ لیا کرتے تھے'),
(4503, 45, 30, 'پس جو لوگ ایمان لائے اورانہوں نے نیک کام کیے انہیں ان کا پروردگار اپنی رحمت میں داخل کرے گا یہ صریح کامیابی ہے'),
(4504, 45, 31, 'اور جنہوں نے کفر کیا (انہیں کہا جائے گا) کیا تمہیں ہماری آیتیں نہیں سنائی جاتی تھیں پھر تم نے غرور کیا اور تم نافرمان لوگ تھے'),
(4505, 45, 32, 'اور جب کہا جاتا تھا کہ الله کا وعدہ سچا ہے اور قیامت میں کوئی شک نہیں تو تم کہتے تھے ہم نہیں جانتے قیامت کیا چیز ہے ہم تو اس کو محض خیالی بات جانتے ہیں اور ہمیں یقین نہیں'),
(4506, 45, 33, 'اور ان پر ان کےاعمال کی برائی ظاہر ہو جائے گی اور ان پر وہ آفت آ پڑے گی جس سے وہ ٹھٹھا کرتے تھے'),
(4507, 45, 34, 'اور کہا جائے گا آج ہم تمہیں فراموش کر دیں گے جیسا تم نے اپنے اس دن کے ملنے کو فراموش کر دیا تھا اور تمہارا ٹھکانہ دوزخ ہے اور تمہارا کوئی مددگارنہیں'),
(4508, 45, 35, 'یہ اس لیے کہ تم الله کی آیتوں کی ہنسی اڑایا کرتے تھے اور تمہیں دنیا کی زندگی نے دھوکے میں ڈال دیا تھا پس آج وہ اس سے نہ نکالے جائیں گے اور نہ ان سے توبہ طلب کی جائے گی'),
(4509, 45, 36, 'پس سب تعریف الله ہی کے لیے ہے جو آسمانوں کا رب اور زمین کا رب سارے جہانوں کا رب ہے'),
(4510, 45, 37, 'اور آسمانوں اور زمین میں اسی کی عزت ہے اور وہی زبردست حکمت والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حمۤ'),
(4512, 46, 2, 'یہ کتاب الله کی طرف سے اتاری گئی ہے جو غالب حکمت والا ہے'),
(4513, 46, 3, 'ہم نے آسمانوں اور زمین کو اور جو ان کے درمیان ہے کسی مصلحت ہی سے اور ایک خاص وقت تک کے لیے پیدا کیا ہے اورکافروں کو جس چیز سے ڈرایا جاتا ہے اس سے منہ پھیر لیتے ہیں'),
(4514, 46, 4, 'کہہ دو بھلا بتاؤ تو سہی جنہیں تم الله کے سوا پکارتے ہو مجھے دکھاؤ کہ انہوں نے زمین میں کون سی چیز پیدا کی ہے یا آسمانوں میں ان کا کوئی حصہ ہے میرے پاس اس سے پہلے کی کوئی کتاب لاؤ یا کوئی علم چلا آتا ہو وہ لاؤ اگر تم سچے ہو'),
(4515, 46, 5, 'اور اس سے بڑھ کر کون گمراہ ہے جو الله کےسوا اسےپکارتا ہے جو قیامت تک اس کے پکارنے کا جواب نہ دے سکے اور انہیں ان کے پکارنے کی خبر بھی نہ ہو'),
(4516, 46, 6, 'او ر جب لوگ جمع کئےجائیں گے تو وہ ان کے دشمن ہو جائیں گے اور ان کی عبادت کے منکر ہوں گے'),
(4517, 46, 7, 'اور جب ان پر ہماری واضح آیتیں پڑھی جاتی ہیں تو کافر حق کو کہتے ہیں جب وہ ان کے پاس آچکا کہ یہ تو کھلم کھلاجادوہے'),
(4518, 46, 8, 'کیا وہ کہتے ہیں آپ نے اسے خود بنا لیا ہے کہہ دو اگر میں نے اسے خود بنا لیا ہے تو تم مجھے الله سے بچانے کی کچھ بھی طاقت نہیں رکھتے وہی بہتر جانتا ہے جو باتیں تم اس میں بناتے ہو میرے اور تمہارے درمیان وہی گواہ کافی ہے اور وہ بخشنے والا نہایت رحم والا ہے'),
(4519, 46, 9, 'کہہ دو میں کوئی انوکھا رسول نہیں ہوں اور میں نہیں جانتا کہ میرے ساتھ کیا کیا جائے گا اور نہ تمہارے ساتھ میں نہیں پیروی کرتا مگر اس کی جو میری طرف وحی کیا جاتا ہے سوائے اس کے نہیں کہ میں کھلم کھلا ڈرانے والا ہوں'),
(4520, 46, 10, 'کہہ دو بتاؤ تو سہی اگر یہ کتاب الله کی طرف سے ہو اور تم اس کے منکر ہو اور بنی اسرائیل کا ایک گواہ ایک ایسی کتاب پر گواہی دے کر ایمان بھی لے آیا اور تم اکڑے ہی رہے بے شک الله ظالموں کو ہدایت نہیں کرتا'),
(4521, 46, 11, 'اور کافروں نے ایمانداروں سے کہا اگر یہ دین بہتر ہوتا تو یہ اس پر ہم سے پہلے نہ دوڑ کر جاتے اورجب انہوں نے اس کے ذریعے سے ہدایت نہیں پائی تو کہیں گے یہ تو پرانا جھوٹ ہے'),
(4522, 46, 12, 'اور اس سے پہلے موسیٰ کی کتاب ہے جو رہنما اور رحمت تھی او ریہ کتاب ہے جو اسے سچا کرتی ہے عربی زبان میں ظالموں کو ڈرانے کے لیے اور نیکوں کو خوشخبری دینے کے لیے'),
(4523, 46, 13, 'بے شک جنہوں نے کہا کہ ہمارا رب الله ہے پھر اسی پر جمے رہے پس ان پر کوئی خوف نہیں اور نہ وہ غمگین ہوں گے'),
(4524, 46, 14, 'یہی بہشتی ہیں اس میں ہمیشہ رہیں گے بدلےان کاموں کے جو وہ کیا کرتے تھے'),
(4525, 46, 15, 'اور ہم نے انسان کو اپنے والدین کے ساتھ نیکی کرنے کی تاکید کی کہ اسے اس کی ماں نے تکلیف سے اٹھائے رکھا اور اسے تکلیف سے جنا اوراس کا حمل اور دودھ کا چھڑانا تیس مہینے ہیں یہاں تک کہ جب وہ اپنی جوانی کو پہنچا اور چالیں سال کی عمر کو پہنچا تو اس نے کہا اے میرے رب مجھے توفیق دے کہ میں تیری نعمت کا شکر ادا کروں جو تو نے مجھ پر انعام کی اور میرے والدین پر اور میں نیک عمل کروں جسے تو پسند کرے اور میرے لیے میری اولاد میں اصلاح کر بے شک میں تیری طرف رجوع کرتا ہوں اور بے شک میں فرمانبردار میں ہوں'),
(4526, 46, 16, 'یہی وہ لوگ ہیں جن سے ہم وہ نیک عمل قبول کرتے ہیں جو انہوں نے کیے اور بہشتیوں میں شامل کر کے ان کے گناہوں سے درگزر کرتے ہیں یہ اس سچے وعدے کے مطابق ہے جو ان سے کیا گیا تھا'),
(4527, 46, 17, 'اور جس نے اپنے ماں باپ سے کہا کہ تم پر تف ہے کیاتم مجھے یہ وعدہ دیتے ہو کہ میں قبر سے نکالا جاؤں گا حالانکہ مجھ سے پہلے بہت سی امتیں گزر گئیں اور وہ دونوں الله سے فریاد کر رہے ہیں کہ ارے تیرا ناس ہو ایمان لا بے شک الله کاوعدہ سچا ہے پھر وہ کہتا ہے یہ ہے کیا مگر پہلوں کے افسانے'),
(4528, 46, 18, 'یہ وہ لوگ ہیں کہ ان کے حق میں بھی ان لوگوں کے ساتھ الله کا قول پورا ہو کر رہا جو ان سے پہلے جن اور انسان ہوگزرے ہیں بے شک وہی خسارہ اٹھانے والے ہیں'),
(4529, 46, 19, 'اور ہر ایک کے لیے اپنے اپنے اعمال کے مطابق درجے ہیں تاکہ الله ان کے اعمال کا انہیں پورا عوض دے اور ان پر کچھ بھی ظلم نہ ہوگا'),
(4530, 46, 20, 'اور جس دن کافر آگ کے روبرو لائے جائیں گے ان سے (کہا جائے گا) تم (اپنا حصہ) پاک چیزو ں میں سے اپنی دنیا کی زندگی میں لے چکے اور تم ان سے فائدہ اٹھا چکے پس آج تمہیں ذلت کا عذاب دیا جائے گا بدلے اس کے جو تم زمین میں ناحق اکڑا کرتے تھے اوربدلے اس کے جو تم نافرمانی کیاکرتے تھے'),
(4531, 46, 21, 'اور قوم عاد کے بھائی کا ذکر کر جب اس نے اپنی قوم کو (وادی) احقاف میں ڈرایا اور اس سے پہلے اورپیچھے کئی ڈرانے والے گزرے کہ سوائے الله کے کسی کی عبادت نہ کرو بے شک میں تم پر ایک بڑے دن کے عذاب سے ڈرتا ہوں'),
(4532, 46, 22, 'انہوں نے کہا کیا تو ہمارے پاس اس لیے آیا ہے کہ تو ہمیں ہمارے معبودوں سے بہکا دے پس ہم پردہ (عذاب) لے آ جس کا تو ہم سے وعدہ کرتا ہے اگر تو سچا ہے'),
(4533, 46, 23, 'اس نے کہا اس کا علم تو الله کے پاس ہے اورمیں تمہیں وہ (پیغام) پہنچاتا ہوں جو میں دے کر بھیجا گیا ہوں لیکن میں تمہیں دیکھ رہا ہوں تم ایک جاہل قوم ہو'),
(4534, 46, 24, 'پھر جب انہوں نے اسے دیکھا کہ وہ ایک ابر ہے جو ان کے میدانوں کی طرف بڑھا چلا آ رہا ہے کہنے لگے کہ یہ تو ابر ہے جو ہم پر برسے گا (نہیں) بلکہ یہ وہی ہے جسے تم جلدی چاہتے تھے یعنی آندھی جس میں دردناک عذاب ہے'),
(4535, 46, 25, 'وہ اپنے رب کے حکم سے ہر ایک چیز کو برباد کر دے گی پس وہ صبح کو ایسے ہو گئے کہ سوائے ان کے گھروں کے کچھ نظر نہ آتا تھا ہم اسی طرح مجرم لوگوں کو سزا دیا کرتے ہیں'),
(4536, 46, 26, 'اور ہم نے ان لوگوں کو ان باتوں میں قدرت دی تھی کہ تمہیں ان باتوں میں قدرت نہیں دی اور ہم نے انہیں کان اور آنکھیں اور دل دیئے تھے پھر نہ تو ان کے کان ہی کام آئے اور نہ ان کی آنکھیں ہی کام آئیں او رنہ ان کے دل ہی کچھ کام آئے کیوں کہ وہ الله کی آیتوں کو انکار ہی کرتے رہے اور جس عذاب کا وہ ٹھٹھا اڑیا کرتے تھے ان پر آن پڑا'),
(4537, 46, 27, 'اور ہم ہلاک کر چکے ہیں جو تمہارے آس پاس بستیاں ہیں اور طرح طرح کے اپنے نشان قدرت بھی دکھائے تاکہ وہ باز آجائيں'),
(4538, 46, 28, 'پھر ان معبودوں نے کیوں نہ مدد کی جن کو انہوں نے الله کے سوا مرتبہ حاصل کرنے کے لیے معبود بنا رکھا تھا بلکہ وہ تو ان سے کھو ئےگئے تھے اور یہ ان کا جھوٹ تھا اور جو کچھ وہ ڈھکوسلے بنایا کرتےتھے'),
(4539, 46, 29, 'اورجب ہم نے آپ کی طرف چند ایک جنوں کو پھیر دیا جو قرآن سن رہے تھے پس جب وہ آپ کے پاس حاصر ہوئے تو کہنے لگے چپ رہو پھر جب ختم ہوا تو اپنی قوم کی طرف واپس لوٹے ایسے حال میں کہ وہ ڈرانے والے تھے'),
(4540, 46, 30, 'کہنے لگے اےہماری قوم بیشک ہم نے ایک کتاب سنی ہے جو موسیٰ کے بعد نازل ہوئی ہے ان کی تصدیق کرنے والی ہے جو اس سے پہلے ہو چکیں حق کی طرف ا ور سیدھے راستہ کی طرف رہنمائی کرتی ہے'),
(4541, 46, 31, 'اے ہماری قوم الله کی طرف بلانے والے کو مان لو اور اس پر ایمان لے آؤ وہ تمہارے لیے تمہارے گناہ بخش دے گا اور تمہیں دردناک عذاب سے بچا لے گا'),
(4542, 46, 32, 'اور جو الله کی طرف بلانے والے کو نہ مانے گا تو وہ زمین میں اسے عاجر نہیں کر سکے گا اور الله کے سوا اس کا کوئی مددگار نہ ہوگا یہی لوگ صریح گمراہی میں ہیں'),
(4543, 46, 33, 'کیا انہوں نے نہیں دیکھا جس الله نے آسمانوں اور زمین کو پیدا کرنے میں نہیں تھکا اس پر قاد رہے کہ مردوں کو زندہ کردے کیوں نہیں وہ تو ہر ایک چیز پر قادر ہے'),
(4544, 46, 34, 'اور جس دن کافر آگ کے سامنے لائے جائیں گے (ان سے کہا جائے گا) کیا یہ امر واقعی نہیں ہے کہیں گے ہمیں اپنے رب کی قسم ضرور امر واقعی ہے ارشاد ہو گا تواپنے کفر کے بدلہ میں اس کا عذاب چکھو'),
(4545, 46, 35, 'پھر صبرکر جیسا کہ عالی ہمت رسولوں نے کیا ہے اور ان کے لیے جلدی نہ کر گویا کہ وہ جس دن عذاب دیکھیں گے جس کا ان سے وعدہ کیا جاتا ہے (تو انہیں ایسا معلوم ہو گا) کہ ایک دن میں سے ایک گھڑی بھر رہے تھے آپ کا کام پہنچا دینا تھا سو کیا نافرمان لوگوں کے سوا اور کوئی ہلاک ہوگا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'وہ لوگ جو منکر ہوئے اورانہوں نے لوگوں کو بھی الله کے راستہ سے روکا تو الله نے ان کے اعمال برباد کر دیئے'),
(4547, 47, 2, 'اوروہ جو ایمان لائے اور انہوں نے اچھے کام کیے اورجو کچھ محمد پر نازل کیا گیا اسپر بھی ایمان لائے اور انہوں نے اچھے کام بھی کیے اور جو کچھ محمد پر نازل کیا گیا اس پر بھی ایمان لائے حالانکہ وہ ان کے رب کی طرف سے برحق بھی ہے تو الله ان کی برائیوں کو مٹا دے گا اور ان کا حال درست کرے گا'),
(4548, 47, 3, 'یہ اس لیے کہ جولوگ منکر ہیں انہوں نے جھوٹ کی پیروی کی اور جو لوگ ایمان لائے انہوں نے اپنے رب کی طرف سے حق کی پیروی کی اسی طرح اللہ لوگوں کے لیے ان کی مثالیں بیان کرتا ہے'),
(4549, 47, 4, 'پس جب تم ان کے مقابل ہو جو کافر ہیں تو ان کی گردنیں مارو یہاں تک کہ جب تم ان کو خوب مغلوب کر لو تو ان کی مشکیں کس لو پھر یا تو اس کے بعد احسان کرو یا تاوان لے لو یہاں تک کہ لڑائی اپنے ہتھیار ڈال دے یہی (حکم) ہے اور اگر الله چاہتا تو ان سے خود ہی بدلہ لے لیتا لیکن وہ تمہارا ایک دوسرے کے ساتھ امتحان کرنا چاہتا ہے اور جو الله کی راہ میں مارے گئے ہیں الله ان کے اعمال برباد نہیں کرے گا'),
(4550, 47, 5, 'جلدی انہیں راہ دکھائے گا اور ان کاحال درست کر دے گا'),
(4551, 47, 6, 'اور انہیں بہشت میں داخل کرے گا جس کی حقیقت انہیں بتا دی ہے'),
(4552, 47, 7, 'اے ایمان والو اگر تم الله کی مدد کرو گے وہ تمہاری مدد کرے گا اور تمہارے قدم جمائے رکھے گا'),
(4553, 47, 8, 'اور جو منکر ہیں سو ان کے لیے تباہی ہے اور وہ ان کے اعمال اکارت کر دے گا'),
(4554, 47, 9, 'یہ اس لیے کہ انہیں نے ناپسند کیا جو الله نے اتارا ہے سو اس نے ان کے اعمال ضائع کر دیے'),
(4555, 47, 10, 'کیا انہوں نے زمین میں سیر نہیں کی وہ دیکھتے ان کا انجام کیسا ہوا جو ان سے پہلے تھے الله نے انہیں ہلاک کر دیا اور منکروں کے لیے ایسی ہی (سزائیں) ہیں'),
(4556, 47, 11, 'یہ اس لیے کہ اللهان کا حامی ہے جو ایمان لائے اور کفار کا کوئی بھی حامی نہیں'),
(4557, 47, 12, 'بے شک الله انہیں داخل کرے گا جو ایمان لائے اور نیک کام کیے بہشتوں میں جن کے نبیچے نہریں بہتی ہو ں گی اور جو کافر ہیں وہ عیش کر رہے ہیں اور اس طرح کھاتے ہیں جس طرح چار پائے کھاتے ہیں اور دوزخ ان کا ٹھکانہ ہے'),
(4558, 47, 13, 'اور کتنی ہی بستیاں تھی جو آپ کی اس بستی سے طاقت میں بڑھ کر تھیں جس کے رہنے والوں نے آپ کو نکال دیا ہے ہم نے انہیں ہلاک کر دیا تو ان کا کوئی بھی مددگار نہ ہوا'),
(4559, 47, 14, 'پس کیا وہ شخص جو اپنے رب کی طرف سے واضح دلیل پر ہو وہ اس جیسا ہو سکتا ہے جسے اس کے برے عمل اچھے کر کے دکھائے گئے ہوں اور انہوں نے اپنی ہی خواہشوں کی پیروی کی ہو'),
(4560, 47, 15, 'اس جنت کی کیفیت جس کا پرہیزگاروں سے وعدہ کیا جاتا ہے (یہ ہے) کہ اس میں ایسے پانی کی نہریں ہو گی جو بگڑنے والا نہیں اور کچھ دودھ کی نہریں جن کا مزہ کبھی نہیں بدلے گا اور کچھ نہریں ایسے شراب کی جو پینے والوں کے لیے خوش ذائقہ ہو گا اور کچھ نہریں صاف شہد کی اور ان کے لیے وہاں ہر قسم کے میوے ہوں اوراپنے رب کی بخشش (کیا وہ) ان جیسے ہو سکتے ہیں جو ہمیشہ دوزخ میں رہیں گے اور انہیں کھولتا ہوا پانی پلایا جائے گا سو وہ ان کی آنتوں کے ٹکڑے ٹکڑے کر ڈالے گا'),
(4561, 47, 16, 'اور ان میں سے بعض وہ ہیں جو آپ کی بات سنتے ہیں یہاں تک کہ جب آپ کے ہاں سے نکل جاتے ہیں تو ان سے کہتے ہیں جنہیں علم دیا گیا ہے اس نے ابھی ابھی کیا کہا یہی لوگ ہیں کہ الله نے ان کے دلوں پر مہر کر دی ہے اور انہوں نے اپنی خواہشوں کی پیروی کی ہے'),
(4562, 47, 17, 'اور جو راستہ پر آگئے ہیں الله انہیں اور زیادہ ہدایت دیتا اور انہیں پرہیزگاری عطا کرتا ہے'),
(4563, 47, 18, 'پھر کیا وہ اس گھڑی کا انتظار کرتے ہیں کہ ان پر ناگہان آئے پس تحقیق اس کی علامتیں تو ظاہر ہو چکیں ہیں پھر جب وہ آگئی تو ان کا سمجھنا کیا فائدہ دے گا'),
(4564, 47, 19, 'پس جان لو کہ سوائے الله کے کوئی معبود نہیں اور اپنے اور مسلمان مردوں اور مسلمان عورتوں کے گناہوں کی معافی مانگیئے اور الله ہی تمہارے لوٹنے اور آرام کرنے کی جگہ کو جانتا ہے'),
(4565, 47, 20, 'اور کہتے ہیں وہ لوگ جو ایمان لائے کوئی سورت کیوں نہیں نازل ہوئی سو جس وقت کوئی صاف (مضمون) کی سورت نازل ہوتی ہے اور اس میں جہاد کا بھی ذکر ہوتا ہے تو جن لوگو ں کے دلوں میں بیماری (نفاق) ہے آپ ان لوگو ں کو دیکھتے ہیں کہ وہ آپ کی طرف اس طرح دیکھتے ہیں جیسے کسی پر موت کی بیہوشی طاری ہو پس ایسے لوگوں کے لیے تباہی ہے'),
(4566, 47, 21, 'حکم ماننا اور نیک بات کہنا (لازم ہے) پس جب بات قرار پا جائے تو اگر وہ الله کے سچے رہے تو ان کے لیے بہتر ہے'),
(4567, 47, 22, 'پھر تم سے یہ بھی توقع ہے اگرتم ملک کے حاکم ہو جاؤ تو ملک میں فساد مچانے اور قطع رحمی کرنے لگو'),
(4568, 47, 23, 'یہی وہ لوگ ہیں جن پر الله نے لعنت کی ہے پھرانہیں بہرا اوراندھا بھی کر دیا ہے'),
(4569, 47, 24, 'پھر کیوں قرآن پر غور نہیں کرتے کیا ان کے دلوں پر قفل پڑے ہوئے ہیں'),
(4570, 47, 25, 'بے شک جو لوگ پیچھے کی طرف الٹے پھر گئے بعد اس کے کہ ان پر سیدھا راستہ ظاہر ہو چکا شیطان نے ان کے سامنے برے کامو ں کو بھلا کر دکھایا اور انہیں آرزو دلائی'),
(4571, 47, 26, 'یہ اس لیے کہ وہ ان لوگوں سے کہنے لگے جنہوں نے اسے ناپسند کیا اس کو جو الله نے نازل کیا ہے کہ بعض باتوں میں ہم تمہاراکہا مانیں گے اور الله ان کی رازداری کو جانتا ہے'),
(4572, 47, 27, 'پھر کیا حال ہوگا جب ان کی روحیں فرشتے قبض کریں گے ان کے مونہوں اور پیٹھوں پر مار رہے ہوں گے'),
(4573, 47, 28, 'یہ اس لیے کہ یہ اس پر چلے جس پر الله ناراض ہے اور انہوں نے الله کی رضا مندی کو برا جانا پھر اس نے بھی ان کے اعمال اکارت کر دیئے'),
(4574, 47, 29, 'کیا وہ لوگ کہ جن کے دلوں میں مرض (نفاق) ہے یہ سمجھے ہوئے ہیں کہ الله ان کی دبی دشمنی ظاہر نہ کرے گا'),
(4575, 47, 30, 'اور اگر ہم چاہتے تو آپ کو وہ لوگ دکھا دیتے پس آپ اچھی طرح سے انہیں ان کے نشان سےپہچان لیتے اور آپ انہیں طرز کلام سے پہچان لیں گے اور الله تمہارے اعمال کو جانتا ہے'),
(4576, 47, 31, 'اور ہم تمہیں آزمائیں گے یہاں تک کہ ہم تم میں سے جہاد کرنے والوں کو اور صبر کرنے والوں کو معلوم کر لیں اور تمہارے حالات کو جانچ لیں'),
(4577, 47, 32, 'بے شک جنہوں نے انکار کر دیا اور الله کی راہ سے روکا اور رسول کی مخالفت کی بعد اس کے کہ ان پر سیدھا راستہ صاضح ہو چکا وہ الله کا کچھ بھی نہیں بگاڑ سکیں گے اور ان کے اعمال کو اکارت کر دے گا'),
(4578, 47, 33, 'اے ایمان والو الله کا حکم مانو اوراس کے رسول کا حکم مانو اور اپنے اعمال کو ضائع نہ کرو'),
(4579, 47, 34, 'بےشک جنہوں نے انکار کیا اور الله کی راہ سے روکا پھر مر گئے درآنحالیکہ وہ کافر تھے سو الله ان کو ہرگز نہیں بخشے گا'),
(4580, 47, 35, 'پس تم سست نہ ہو اور نہ صلح کی طرف بلاؤ اور تم ہی غالب رہو گے اور الله تمہارے ساتھ ہے اور وہ ہر گز تمہارےاعمال میں نقصان نہیں دیگا'),
(4581, 47, 36, 'بلاشبہ دنیا کی زندگی تو کھیل اور تماشا ہے اور اگر تم ایمان لاؤ اور پرہیزگاری اختیار کرو تو تمہیں تمہارے اجر دے گا اور تم سے تمہارے مال نہیں مانگے گا'),
(4582, 47, 37, 'اور اگر تم سے وہ (مال) مانگے پھر تمہیں تنگ کرے تو تم بخل کرنے لگو اور تمہارے دلی کینے ظاہر کر دے'),
(4583, 47, 38, 'خبردار تم وہ لوگ ہو کہ الله کی راہ میں خرچ کرنے کو بلائے جاتے ہو تو کوئی تم میں سے وہ ہے جو بخل کرتا ہے اورجو بخل کرتا ہے سو وہ اپنی ہی ذات سے بخل کرتا ہے اور الله بے پرواہ ہے اور تم ہی محتاج ہو اور اگر تم نہ مانو گے تو وہ اور قوم سوائے تمہارے بدل دے گا پھر وہ تمہاری طرح نہ ہوں گے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'بے شک ہم نے آپ کو کھلم کھلا فتح دی'),
(4585, 48, 2, 'تاکہ آپ کے اگلے اور پچھلے گناہ معاف کر دے اور اپنی نعمت آپ پر تمام کر دے اور تاکہ آپ کو سیدھے راستہ پر چلائے'),
(4586, 48, 3, 'اور تاکہ الله آپ کی زبردست مدد کرے'),
(4587, 48, 4, 'وہی تو ہے جس نے ایمانداروں کے دلو ں میں اطمینان اتارا تاکہ ان کا ایمان اور زیادہ ہو جائے اور آسمانوں اور زمین کے لشکر سب الله ہی کے ہیں اور الله خبردار حکمت والا ہے'),
(4588, 48, 5, 'تاکہ ایمان والے مردوں اور عورتوں کو بہشتوں میں داخل کرے جن کے نیچے نہریں بہہ رہی ہوں گی ان میں ہمیشہ رہیں گے اور ان پر سے ان کے گناہ دور کر دے گا اور الله کے ہاں یہ بڑی کامیابی ہے'),
(4589, 48, 6, 'اور تاکہ منافق مردوں اور عورتوں کواور مشرک مردوں اور عورتوں کو عذاب دے جو الله کے بارے میں براگمان رکھتے ہیں انہیں پر بری گردش ہے اور الله نے ان پر غضب نازل کیا اور ان پر لعنت کی اوران کے لیے دوزخ تیار کر رکھا ہےاور وہ برا ٹھکانہ ہے'),
(4590, 48, 7, 'اور الله ہی کے سب لشکر آسمانوں اور زمین میں ہیں اور الله بڑا غالب حکمت والا ہے'),
(4591, 48, 8, 'بے شک ہم نے آپ کو گواہ بناکر بھیجا اور خوشخبری دینے والا اور ڈرانے والا'),
(4592, 48, 9, 'تاکہ تم الله پر اور اس کے رسول پر ایمان لاؤ اور اس کی مدد کرو اور اس کی عزت کرو اور صبح اور شام اس کی پاکی بیان کرو'),
(4593, 48, 10, 'بے شک جو لوگ آپ سے بیعت کر رہے ہیں وہ الله ہی سے بیعت کر رہے ہیں ان کے ہاتھوں پر الله کا ہاتھ ہے پس جو اس عہد کو) تو ڑ دے گا سو توڑنے کا وبال خود اسی پر ہو گا اور جو وہ عہد پورا کرے گا جو اس نے الله سے کیا ہے سو عقریب وہ اسے بہت بڑا اجر دے گا'),
(4594, 48, 11, 'عنقریب آ پ سے وہ لوگ کہیں گے جو بدویوں میں سے پیچھے رہ گئے تھے کہ ہمیں ہمارے اور اہل و عیال نے مشغول رکھا ہے آپ ہمارے لیے مغفرت مانگیئے وہ اپنی زبانوں سے وہ بات کہتے ہیں جو ان کے دلوں میں نہیں ہے کہدو وہ کون ہے جو الله کے سامنے تمہارے لیے کسی چیز کا (کچھ بھی) اختیار رکھتا ہوگا اگر الله تمہیں کوئی نقصان یا کوئی نفع پہنچانا چاہے بلکہ الله تمہارے سب اعمال پر خبردار ہے'),
(4595, 48, 12, 'بلکہ تم نے خیال کیا تھا کہ رسول الله اور مسلمان اپنے گھر والوں کی طرف کبھی بھی واپس نہ لوٹیں گے اور تمہارے دلوں میں یہ بات اچھی معلوم ہوئی اور تم نے بہت برا گمان کیا اور تم ہلاک ہونے والے لوگ تھے'),
(4596, 48, 13, 'اورجو لوگ الله اور اس کے رسول پر ایمان نہیں لائے سو ہم نے ایسے کافروں کے لیے بھڑکتی ہوئی آگ تیار کر رکھی ہے'),
(4597, 48, 14, 'اور آسمانوں اور زمین کی حکومت الله ہی کے لیے ہے وہ جسے چاہے بخشے اورجسے چاہے عذاب دے اور الله بخشنے والا بڑا مہربان ہے'),
(4598, 48, 15, 'عنقریب کہیں گے وہ لوگ جو پیچھے رہ گئے تھے جب تم غنیمتوں کی طرف ان کے لینے کے لیے جانے لگو گے کہ ہمیں چھوڑو ہم تمہارے ساتھ چلیں وہ چاہتے ہیں کہ الله کا حکم بدل دیں کہہ دو کہ تم ہرگز ہمارے ساتھ نہ چلو گے الله نے اس سے پہلے ہی ایسا فرما دیا ہے پس وہ کہیں گے کہ (نہیں) بلکہ تم ہم سے حسد کرتے ہو بلکہ وہ لوگ بات ہی کم سمجھتے ہیں'),
(4599, 48, 16, 'ان پیچھے رہ جکانے والے بدوؤں سے کہہ دو کہ بہت جلد تمہیں ایک سخت جنگجو قوم سے لڑنے کے لیے بلایا جائے گا تم ان سے لڑو گے یا وہ اطاعت قبول کر لے گی پھر اگر تم نے حکم مان لیا تو الله تمہیں بہت ہی اچھا انعام دے گا اور اگر تم پھر گئے جیسا کہ پہلے پھر گئے تھے تو تمہیں سخت عذاب دے گا'),
(4600, 48, 17, 'نہ اندھے پر کچھ گناہ ہے اور نہ لنگڑے ہی پرکچھ گناہ ہے اور نہ بیمار ی پرکچھ گناہے ہے اور جوکوئی الله اور اس کے رسول کی اطاعت کرے گا تو اسے ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی اور جو نافرمانی کرے گا اسے سخت سزا دے گا'),
(4601, 48, 18, 'بے شک الله مسلمانوں سے راضی ہوا جب وہ آپ سے درخت کے نیچے بیعت کر رہے تھے پھر اس نے جان لیا جو کچھ ان کے دلو ں میں تھا پس اس نے ان پر اطمینان نازل کر دیا اور انہیں جلد ہی فتح دے دی'),
(4602, 48, 19, 'اوربہت سی غنیمتیں بھی دے گا جنہیں وہ لیں گے اور الله زبردست حکمت والا ہے'),
(4603, 48, 20, 'الله نے تم سے بہت سی غنیمتوں کا وعدہ کیا ہے جنہیں تم حاصل کرو گے پھر تمہیں اس نے یہ جلدی دےدی اوراس نےتم سے لوگوں کے ہاتھ روک دیئے اور تاکہ ایمان لانے والوں کے لیے یہ ایک نشان ہو اور تاکہ تم سیدھے راستہ پر چلائے'),
(4604, 48, 21, 'اور بھی فتوحات ہیں کہ جو (اب تک) تمہارے بس میں نہیں آئیں البتہ الله کے بس میں ہیں اور الله ہر چیز پر قادر ہے'),
(4605, 48, 22, 'اور اگر کافر تم سے لڑتے تو پیٹھ پھیر کر بھاگ پڑتے پھر نہ کوئی حمایتی پاتے نہ کوئی مددگار'),
(4606, 48, 23, 'الله کا قدیم دستور پہلے سے یونہی چلا آتا ہے اور تو اس کے دستور کو بدلا ہوا نہ پائے گا'),
(4607, 48, 24, 'اوروہی ہے جس نے وادی مکہ میں ان کے ہاتھ تم سے اور تمہارے ہاتھ ان سے روک دیے اس کے بعد اس نے تمہیں ان پر غالب کر دیا تھا اور الله ان سب باتوں کو جو تم کر رہے تھے دیکھ رہا تھا'),
(4608, 48, 25, 'وہ تو وہی ہیں جنہوں نے انکار کیا اور تمہیں مسجد حرام سے روکا اور قربانی کے جانوروں کو روکے رکھا اس سے کہ وہ اپنی قربان گاہ تک پہنچیں اور اگر کچھ مرد ایمان والے اور عورتیں ایمان والی نہ ہوتیں جنہیں تم نہیں جانتے تھے کہ تم انہیں پامال کر دیتے پھر ان کی طرف سے تم پر نادانستگی سے الزام آتا (تو تمہیں لڑنے سے نہ روکا جاتا) تاکہ الله اپنی رحمت میں جسے چاہے داخل کرے اگر وہ ٹل گئے ہوتے تو ہم ان میں سے جو کافر ہیں انہیں دردناک عذاب دیتے'),
(4609, 48, 26, 'جب کہ کافروں نے اپنے دل میں سخت جوش پیدا کیا تھا جہالت کا جوش تھا پھر الله نے بھی اپنی تسکین اپنے رسول اور ایمان والوں پر نازل کر دی اور ان کو پرہیزگاری کی بات پر قائم رکھا اور وہ اسی کے لائق اور قابل بھی تھے اور الله ہر چیز کو جانتا ہے'),
(4610, 48, 27, 'بے شک الله نے اپنے رسول کا خواب سچا کر دکھایا کہ اگر الله نے چاہا تو تم امن کے ساتھ مسجد حرام میں ضرور داخل ہو گے اپنے سر منڈاتے ہوئے اور بال کتراتے ہوئے بے خوف و خطر ہوں گے پس جس بات کو تم نہ جانتے تھے اس نے اسے جان لیا تھا پھر اس نے اس سے پہلےہی ایک فتح بہت جلدی کر دی'),
(4611, 48, 28, 'وہی تو ہے جس نے اپنے رسول کو ہدایت اور سچا دین دے کر بھیجا تاکہ اسے ہر ایک دین پر غالب کرے اور الله کی شہادت کافی ہے'),
(4612, 48, 29, 'محمد الله کے رسول ہیں اور جو لوگ آپ کے ساتھ ہیں کفار پر سخت ہیں آپس میں رحم دل ہیں تو انہیں دیکھے گا کہ رکوع و سجود کر رہے ہیں الله کا فضل اوراس کی خوشنودی تلاش کرتے ہیں ان کی شناخت ان کے چہرو ں میں سجدہ کا نشان ہے یہی وصف ان کا تو رات میں ہے اور انجیل میں ان کا وصف ہے مثل اس کھیتی کے جس نے اپنی سوئی نکالی پھر اسے قوی کر دیا پھر موٹی ہوگئی پھر اپنے تنہ پر کھڑی ہوگئی کسانوں کو خوش کرنے لگی تاکہ الله ان کی وجہ سے کفار کو غصہ دلائے الله ان میں سے ایمان داروں اورنیک کام کرنے والوں کے لیے بخشش اور اجر عظیم کا وعدہ کیا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'اے ایمان والو الله اور اس کے رسول کے سامنے پہل نہ کرو الله سے ڈرتے رہو بے شک الله سب کچھ سننے والا جاننے والا ہے'),
(4614, 49, 2, 'اے ایمان والو اپنی آوازیں نبی کی آواز سے بلند نہ کیا کرو اور نہ بلندآواز سے رسول سے بات کیا کرو جیسا کہ تم ایک دوسرے سے کیا کرتے ہو کہیں تمہارے اعمال برباد نہ ہوجائیں اور تمہیں خبر بھی نہ ہو'),
(4615, 49, 3, 'بے شک جو لوگ اپنی آوازیں رسول الله کے حضور دھیمی کر لیتے ہیں یہی لوگ ہیں کہ الله نے ان کے دلوں کو پرہیزگاری کے لیے جانچ لیا ہےان کے لیے بخشش اور بڑا اجر ہے'),
(4616, 49, 4, 'بے شک جو لوگ آپ کو حجروں کے باہر سے پکارتے ہیں اکثر ان میں سے عقل نہیں رکھتے'),
(4617, 49, 5, 'اور اگر وہ صبر کرتے یہاں تک کہ آپ ان کے پاس سے نکل کر آتے تو ان کے لیے بہتر ہوتا اور الله بخشنے والا نہایت رحم والا ہے'),
(4618, 49, 6, 'اے ایمان والو اگر کوئی فاسق تمہاے پاس کوئی سی خبر لائے تو اس کی تحقیق کیا کرو کہیں کسی قوم پر بے خبری سے نہ جا پڑو پھر اپنے کیے پر پشیمان ہونے لگو'),
(4619, 49, 7, 'اور جان لو کہ تم میں الله کا رسول موجود ہے اگر وہ بہت سی باتوں میں تمہارا کہا مانے تو تم پر مشکل پڑ جائے لیکن الله نے تمہارے دلوں میں ایمان کی محبت ڈال دی ہے اوراس کو تمہارے دلوں میں اچھاکردکھایا ہے اور تمہارے دل میں کفر اور گناہ اور نافرمانی کی نفرت ڈال دی ہے یہی لوگ ہدایت یافتہ ہیں'),
(4620, 49, 8, 'الله کے فضل اور احسان سے اور الله جاننے والا حکمت والا ہے'),
(4621, 49, 9, 'اور اگر مسلمانوں کے دو گروہ آپس میں لڑ پڑیں تو ان کے درمیان صلح کرا دو پس اگر ایک ان میں دوسرے پر ظلم کرے تو اس سے لڑو جو زیادتی کرتا ہے یہاں تک کہ وہ الله کے حکم کی طرف رجوع کرے پھر اگر وہ رجوع کرے تو ان دونوں میں انصاف سے صلح کرادو اورانصاف کرو بے شک الله انصاف کرنے والوں کو دوست رکھتا ہے'),
(4622, 49, 10, 'بے شک مسلمان آپس میں بھائی بھائی ہیں سواپنے بھائیوں میں صلح کرادو اور الله سے ڈرو تاکہ تم پر رحم کیا جائے'),
(4623, 49, 11, 'اے ایمان والو ایک قوم دوسری قوم سے ٹھٹھا نہ کرے عجب نہیں کہ وہ ان سے بہتر ہوں اور نہ عورتیں دوسری عورتوں سے ٹھٹھا کریں کچھ بعید نہیں کہ وہ ان سے بہتر ہوں اور ایک دوسرے کو طعنے نہ دو اور نہ ایک دوسرے کے نام دھرو فسق کے نام لینے ایمان لانے کے بعد بہت برے ہیں اور جو باز نہ آئیں سووہی ظالم ہیں'),
(4624, 49, 12, 'اے ایمان والو بہت سی بدگمانیوں سے بچتے رہو کیوں کہ بعض گمان تو گناہ ہیں اور ٹٹول بھی نہ کیا کرو اور نہ کوئی کسی سے غیبت کیا کرے کیا تم میں سے کوئی پسند کرتا ہے کہ اپنے مردہ بھائی کا گوشت کھائے سواس کو توتم ناپسند کرتے ہو اور الله سے ڈرو بے شک الله بڑا توبہ قبول کرنے والا نہایت رحم والا ہے'),
(4625, 49, 13, 'ا ے لوگو ہم نے تمہیں ایک ہی مرد اور عورت سے پیدا کیا ہے اور تمہارے خاندان اور قومیں جو بنائی ہیں تاکہ تمہیں آپس میں پہچان ہو بے شک زیادہ عزت والا تم میں سے الله کے نزدیک وہ ہے جو تم میں سے زیادہ پرہیزگار ہے بے شک الله سب کچھ جاننے والا خبردار ہے'),
(4626, 49, 14, 'بدویوں نے کہا ہم ایمان لے آئے ہیں کہہ دو تم ایمان نہیں لائے لیکن تم کہو کہ ہم مسلمان ہو گئے ہیں اورابھی تک ایمان تمہارے دلوں میں داخل نہیں ہوا اور اگر تم الله اور اس کے رسول کا حکم مانو تو تمہارے اعمال میں سے کچھ بھی کم نہیں کرے گا بے شک الله بخشنے والا نہایت رحم والا ہے'),
(4627, 49, 15, 'بے شک سچے مسلمان تو وہی ہیں جو الله اور اس کے رسول پر ایمان لائے پھر انہوں نے شک نہ کیا اور اپنے مالوں اور اپنی جانوں سے الله کی راہ میں جہاد کیا وہی سچے (مسلمان) ہیں'),
(4628, 49, 16, 'کہہ دوکیا تم الله کو اپنی دین داری جتاتے ہو اور الله جانتا ہے جو کچھ آسمانوں میں اور زمین میں ہے اور الله ہر چیز کو جاننے والا ہے'),
(4629, 49, 17, 'آپ پر اپنے اسلام لانے کا احسان جتاتے ہیں کہہ دو مجھ پراپنے اسلام لانے کا احسان نہ جتلاؤ بلکہ الله تم پر احسان رکھتا ہے کہ اس نے ایمان کی طرف تمہاری رہنمائی کی اگر تم سچے ہو'),
(4630, 49, 18, 'بے شک الله آسمانوں اور زمین کی سب مخفی چیزیں جانتا ہےاوردیکھ رہاہےجوتم کررہےہو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قۤ۔ اس قرآن کی قسم جو بڑا شان والا ہے'),
(4632, 50, 2, 'بلکہ وہ تعجب کرتے ہیں کہ ان کے پاس انہیں میں سے ایک ڈرانے والا آیا پس کافرو ں نے کہا کہ یہ تو ایک عجیب بات ہے'),
(4633, 50, 3, 'کیا جب ہم مرجائیں گے اورمٹی ہو جائیں گے یہ دوبارہ زندگی بعید از قیاس ہے'),
(4634, 50, 4, 'ہمیں معلوم ہے جو زمین ان میں سے کم کرتی ہے اور ہمارے پاس ایک کتاب ہے جس میں سب کھچ محفوظ ہے'),
(4635, 50, 5, 'بلکہ انہوں نے حق کو جھٹلایا جب کہ وہ ان کے پاس آیا پس وہ ایک الجھی ہوئی بات میں پڑے ہوئے ہیں'),
(4636, 50, 6, 'پس کیا انہوں نے غور سے اپنے اوپر آسمان کو نہیں دیکھا کہ ہم نے کس طرح اسے بنایا اور آراستہ کیا ہے اور اس میں کوئی بھی شگاف نہیں'),
(4637, 50, 7, 'اور ہم نے زمین کو بچھا دیا اوراس میں مضبوط ڈال دیے اور اس میں ہر قسم کی خوشنما چیزیں اگائیں'),
(4638, 50, 8, 'ہر رجوع کرے والے بندے کے لیے بصیرت اور نصیحت ہے'),
(4639, 50, 9, 'اور ہم نے آسمان سے برکت والا پانی اتارا پھر ہم پھر ہم نے اس کے ذریعے سے باغ اگائے اور اناج جن کے کھیت کاٹے جاتے ہیں'),
(4640, 50, 10, 'او رلمبی لمبی کھجوریں جن کے خوشے تہہ بہ تہہ ہیں'),
(4641, 50, 11, 'بندوں کے لیے روزی اورہم نے اس سے ایک مردہ بستی کو زندہ کیا دوبارہ نکلنا اس طرح ہے'),
(4642, 50, 12, 'ان سے پہلے قوم نوح اور کنوئیں والوں نے اور قوم ثمود نے جھٹلایا'),
(4643, 50, 13, 'اور قوم عاد اور فرعون اور قوم لوط نے'),
(4644, 50, 14, 'اور بن والو ں اور قوم تبعّ نے ہر ایک نےرسولوں کو جھٹلایا تو ہمارا وعدہ عذاب ثابت ہوا'),
(4645, 50, 15, 'کی ہم پہلی بار پیدا کرنے میں تھک گئے ہیں (نہیں) بلکہ وہ از سرِ نو پیدا کرنے کے متعلق شک میں ہیں'),
(4646, 50, 16, 'اور بے شک ہم نے انسان کو پیدا کیا اور ہم جانتے ہیں جو وسوسہ اس کے دل میں گزرتا ہے اور ہم اس سے اس کی رگ گلو سے بھی زیادہ قریب ہیں'),
(4647, 50, 17, 'جب کہ ضبط کرنے والے دایں اوربائیں بیٹھیں ہوئے ضبط کرتے جاتے ہیں'),
(4648, 50, 18, 'وہ منہ سے کوئی بات نہیں نکالتا مگراس کے پاس ایک ہوشیار محافظ ہوتا ہے'),
(4649, 50, 19, 'اور موت کی بے ہوشی تو ضرور آ کر رہے گی یہی ہے وہ جس سے تو گریز کرتا تھا'),
(4650, 50, 20, 'اور صور میں پھونکا جائے گا وعدہ عذاب کا دن یہی ہے'),
(4651, 50, 21, 'اور ہر ایک شخص آئے گا اس کے ساتھ ایک ہانکنے والا اورایک گواہی دینے والا ہوگا'),
(4652, 50, 22, 'بے شک تو تو اس دن سے غفلت میں رہا پس ہم نے تجھ سے تیرا پردہ دور کر دیا پس تیری نگاہ آج بڑی تیز ہے'),
(4653, 50, 23, 'اور اس کا ساتھی کہے گا یہ ہے جو میرے پاس تیار ہے (حکم ہوگ)'),
(4654, 50, 24, 'تم دونوں ہر کافر سرکش کو دوزخ میں ڈال دو'),
(4655, 50, 25, 'جو نیکی سےروکنے والا حد سےبڑھنے والا شک کرنے والا ہے'),
(4656, 50, 26, 'جس نے الله کے ساتھ کوئی دوسرا معبود ٹھیرایا پس اسے سخت عذاب میں ڈال دو'),
(4657, 50, 27, 'اس کا ہم نشین کہے گا اے ہمارے رب میں نے اسے گمراہ نہیں کیا تھا بلکہ وہ خود ہی بڑی گمراہی میں پڑا ہوا تھا'),
(4658, 50, 28, 'فرمائے گا تم میرے پاس مت جھگڑو اور میں تو پہلے تمہاری طرف اپنے عذاب کا وعدہ بھیج چکا تھا'),
(4659, 50, 29, 'میرے ہاں کی بات بدلی نہیں جاتی اور نہ ہی بندو ں کے لیے ظالم ہوں'),
(4660, 50, 30, 'جس دن ہم جہنم سے کہیں گے کیا تو بھر چکی اوروہ کہے گی کیا کچھ اوربھی ہے'),
(4661, 50, 31, 'اوربہشت پرہیزگاروں کے لیے قریب لائی جائے گی کہ کچھ فاصلہ نہ ہوگا'),
(4662, 50, 32, 'یہی ہے جس کا تم سے وعدہ کیا جاتا تھا ہر رجوع کرنے والے اور حفاظت کرنے والے کے لیے'),
(4663, 50, 33, 'جو کوئی الله سے بن دیکھے ڈرا اور رجوع کرنے والا دل لے کر آیا'),
(4664, 50, 34, 'اس میں سلامتی سے داخل ہو جاؤ ہمیشہ رہنے کا دن یہی ہے'),
(4665, 50, 35, 'انہیں جو کچھ وہ چاہیں گے وہاں ملے گا اور ہمارے پاس اوربھی زیادہ ہے'),
(4666, 50, 36, 'اور ہم نے ان سے پہلے کتنی قومیں ہلاک کر دیں جو قوت میں ان سے بڑھ کر تھیں پھر (عذاب کے وقت) شہرو ں میں دوڑتے پھرنے لگے کہ کوئی پناہ کی جگہ بھی ہے'),
(4667, 50, 37, 'بے شک اس میں شخص کے لیے بڑی عبرت ہے جس کے پاس (فہیم) دل ہو یا وہ متوجہ ہو کر (بات کی طرف) کان ہی لگا دیتا ہو'),
(4668, 50, 38, 'اور بے شک ہم نےآسمانوں اور زمین کو پیدا کیا اورجو کچھ ان کے درمیان میں ہے چھ دن میں اور ہمیں کچھ بھی تکان نہ ہوئی'),
(4669, 50, 39, 'پس ان باتوں پر صبر کر جو وہ کہتے ہیں اور اپنے رب کی پاکیزگی بیان کر تعریف کے ساتھ دن نکلنے سے پہلے اور دن چھپنے سے پہلے'),
(4670, 50, 40, 'اورکچھ رات میں بھی اس کی تسبیح کر اور نماز کے بعد بھی'),
(4671, 50, 41, 'اور توجہ سے سنیئے جس دن پکارنے والا پاس سے پکارے گا'),
(4672, 50, 42, 'جس دن وہ ایک چیخ کو بخوبی سنیں گے یہ دن قبروں سے نکلنے کا ہوگا'),
(4673, 50, 43, 'بے شک ہم ہی زندہ کرتے اورمارتے ہیں اورہماری طرف ہی لوٹ کر آنا ہے'),
(4674, 50, 44, 'جس دن ان پر سے زمین پھٹ جائے گی لوگ دوڑتے ہوئے نکل آئيں گے یہ لوگو ں کا جمع کرنا ہمیں بہت آسان ہے'),
(4675, 50, 45, 'ہم جانتے ہیں جو کچھ وہ کہتے ہیں اور آپ ا ن پر کچھ زبردستی کرنے والے نہیں پھر آپ قرآن سے اس کو نصیحت کیجیئے جو میرے عذاب سے ڈرتا ہو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'قسم ہے ان ہواؤں کی جو (غبار وغیرہ) اڑانے والی ہیں'),
(4677, 51, 2, 'پھر ا ن بادلوں کی جو بوجھ (بارش کا) اٹھانے والے ہیں'),
(4678, 51, 3, 'پھر ان کشتیوں کی جو نرمی سے چلنے والی ہیں'),
(4679, 51, 4, 'پھر ان فرشتوں کی جو حکم کے موافق چیزیں تقسیم کرنے واے ہیں'),
(4680, 51, 5, 'بے شک جس قیامت کا تم سے وعدہ کیا جاتا ہےوہ سچ ہے'),
(4681, 51, 6, 'اور بے شک اعمال کی جزا ضرور ہونے والی ہے'),
(4682, 51, 7, 'آسمان جالی دار کی قسم ہے'),
(4683, 51, 8, 'البتہ تم پیچیدہ بات میں پڑے ہوئے ہو'),
(4684, 51, 9, 'قرآن سے وہی روکا جاتا ہےجوازل سے گمراہ ہے'),
(4685, 51, 10, 'اٹکل پچو باتیں بنانے والے غارت ہوں'),
(4686, 51, 11, 'وہ جو غفلت میں بھولے ہوئے ہیں'),
(4687, 51, 12, 'پوچھتے ہیں فیصلے کا دن کب ہوگا'),
(4688, 51, 13, 'جس دن وہ آگ پر عذاب دیے جائیں گے'),
(4689, 51, 14, 'اپنی شرارت کا مزہ چکہو یہی ہے وہ (عذاب) جس کی تم جلدی کرتے تھے'),
(4690, 51, 15, 'بے شک پرہیزگار باغات اور چشموں میں ہوں گے'),
(4691, 51, 16, 'لے رہے ہوں گے جو کچھ انہیں ان کا رب عطا کرے گا بے شک وہ اس سے پہلے نیکو کار تھے'),
(4692, 51, 17, 'وہ رات کے وقت تھوڑا عرصہ سویا کرتے تھے'),
(4693, 51, 18, 'اور آخر رات میں مغفرت مانگا کرتے تھے'),
(4694, 51, 19, 'اور ان کے مالوں میں سوال کرنے والے اور محتاج کا حق ہوتا تھا'),
(4695, 51, 20, 'اور زمین میں یقین کرنے والوں کے لیے نشانیاں ہیں'),
(4696, 51, 21, 'اور خود تمہاری نفسوں میں بھی پس کیا تم غور سے نہیں دیکھتے'),
(4697, 51, 22, 'اور تمہاری روزی آسمان میں ہے اور جو تم سے وعدہ کیا جاتا ہے'),
(4698, 51, 23, 'پس آسمان اور زمین کے مالک کی قسم ہے بے شک یہ (قرآن) برحق ہے جیسا تم باتیں کرتے ہو'),
(4699, 51, 24, 'کیا آپ کو ابراھیم کے معزز مہمانوں کی بات پہنچی ہے'),
(4700, 51, 25, 'جب کہ وہ اس پر داخل ہوئے پھر انہوں نے سلام کیا ابراھیم نے سوال کا جواب دیا (خیال کیا) کچھ اجنبی سے لوگ ہیں'),
(4701, 51, 26, 'پس چپکے سے اپنے گھر والوں کے پاس گیا اور ایک موٹا بچھڑا (تلا ہوا) لایا'),
(4702, 51, 27, 'پھر ان کے سامنے لا رکھا فرمایا کیا تم کھاتے نہیں'),
(4703, 51, 28, 'پھر ان سے خوف محسوس کیا انہوں نے کہا تم ڈرو نہیں اور انہوں نے اسے ایک دانشمند لڑکے کی خوشخبری دی'),
(4704, 51, 29, 'پھر ان کی بیوی شور مچاتی ہوئی آگے بڑھی اور اپنا ماتھا پیٹ کر کہنے لگی کیا بڑھیا بانجھ جنے گی'),
(4705, 51, 30, 'انہوں نے کہا تیرے رب نے یونہی فرمایا ہے بے شک وہ حکمت والا دانا ہے'),
(4706, 51, 31, 'فرمایا اے رسولو! تمہارا کیا مطلب ہے'),
(4707, 51, 32, 'انہوں نے کہا ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں'),
(4708, 51, 33, 'تاکہ ہم ان پر مٹی کے پتھر برسائیں'),
(4709, 51, 34, 'وہ آپ کے رب کی طرف حدسے بڑھنے والوں کے لیے مقرر ہو چکے ہیں'),
(4710, 51, 35, 'پھر ہم نے نکال لیا جو بھی وہا ں ایمان دار تھا'),
(4711, 51, 36, 'پھر ہم نے وہاں سوائے مسلمانوں کے ایک گھر کے نہ پایا'),
(4712, 51, 37, 'اورہم نے اس واقعہ میں ایسے لوگو ں کے لیے ایک عبرت رہنے دی جو دردناک عذاب سے ڈرتے ہیں'),
(4713, 51, 38, 'اور موسیٰ کے قصہ میں بھی عبرت ہے جب کہ ہم نے فرعون کے پا س ایک کھلی دلیل دے کر بھیجا'),
(4714, 51, 39, 'سو اس نے مع اپنے ارکانِ سلطنت کے سرتابی کی اور کہا یہ جادوگر یا دیوانہ ہے'),
(4715, 51, 40, 'پھر ہم نے اسے اور اس کے لشکروں کو پکڑ لیا پھر ہم نے انہیں سمند رمیں پھینک دیا اور اس نے کام ہی ملامت کا کیا تھا'),
(4716, 51, 41, 'اور قوم عاد میں بھی (عبرت ہے) جب ہم نے ان پر سخت آندھی بھیجی'),
(4717, 51, 42, 'جو کسی چیز کو نہ چھوڑتی جس پر سے وہ گزرتی مگر اسے بوسیدہ ہڈیوں کی طرح کر دیتی'),
(4718, 51, 43, 'اور قوم ثمود میں بھی (عبرت ہے) جب ہ ان سے کہا گیا ایک وقت معین تک کا فائدہ اٹھاؤ'),
(4719, 51, 44, 'پھر انہوں نے اپنے رب کے حکم سے سرتابی کی تو ان کو بجلی نے آ پکڑا اوروہ دیکھ رہے تھے'),
(4720, 51, 45, 'پھر نہ تو وہ اٹھ ہی سکے اور نہ وہ بدلہ ہی لے سکے'),
(4721, 51, 46, 'اور قوم نوح کو اس سے پہلے (ہلاک کر دیا) بے شک وہ نافرمان لوگ تھے'),
(4722, 51, 47, 'اور ہم نے آسمان کو قدرت سے بنایا اور ہم وسیع قدرت رہنے والے ہیں'),
(4723, 51, 48, 'اورہم نے ہی زمین کو بچھایا پھر ہم کیا خوب بچھانے والے ہیں'),
(4724, 51, 49, 'اور ہم نے ہی ہر چیز کا جوڑا پیدا کیا تاکہ تم غور کرو'),
(4725, 51, 50, 'پھر الله کی طرف دوڑو بے شک میں تمہارے لیے الله کی طرف سے کھلم کھلا ڈرانے والا ہوں'),
(4726, 51, 51, 'اور اللہ کے ساتھ کوئی دوسرا معبود نہ ٹھراؤ بےشک میں تمہارے لئے اس کی طرف سے کھلم کھلا ڈرانے والا ہوں'),
(4727, 51, 52, 'اسی طرح ان سے پہلوں کے پا س بھی جب کوئی رسول آیا تو انہوں نے یہی کہا کہ یہ جادوگر یا دیوانہ ہے'),
(4728, 51, 53, 'کیا ایک دوسرے سے یہی کہ مرے تھے نہیں بلکہ وہ خود ہی سرکش ہیں'),
(4729, 51, 54, 'پس آپ ان کی پرواہ نہ کیجیئے آپ پر کوئی الزام نہیں'),
(4730, 51, 55, 'اور نصیحت کرتے رہیئے بے شک ایمان والوں کو نصیحت نفع دیتی ہے'),
(4731, 51, 56, 'اور میں نے جن اور انسان کو بنایا ہے تو صرف اپنی بندگی کے لیے'),
(4732, 51, 57, 'میں ان سے کوئی روزی نہیں چاہتا ہوں اور نہ ہی چاہتا ہوں کہ وہ مجھے کھلائیں'),
(4733, 51, 58, 'بے شک الله ہی بڑا روزی دینے والا زبردست طاقت والا ہے'),
(4734, 51, 59, 'پس بے شک ان کے لیے جو ظالم ہیں حصہ ہے جیسا کہ ان کے ساتھیوں کا حصہ تھا تو وہ مجھ سے جلدی کا مطالبہ نہ کریں'),
(4735, 51, 60, 'پس ہلاکت ہے ان کے لیے جو کافر ہیں اس دن جس کا ان سے وعدہ کیا جاتا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'قسم ہے طور کی'),
(4737, 52, 2, 'اور اس کتاب کی جو لکھی گئی ہے'),
(4738, 52, 3, 'کشادہ ورقوں میں'),
(4739, 52, 4, 'اور آباد گھر کی قسم ہے'),
(4740, 52, 5, 'اور اونچی چھت کی'),
(4741, 52, 6, 'اور جوش مارتے ہوئے سمندر کی'),
(4742, 52, 7, 'بے شک آپ کے رب کا عذاب واقع ہوکر رہے گا'),
(4743, 52, 8, 'اسے کوئی ٹالنے والا نہیں ہے'),
(4744, 52, 9, 'جس دن آسمان تھرتھرا کر لرزنے لگے گا'),
(4745, 52, 10, 'اور پہاڑ تیزی سے چلنے لگیں گے'),
(4746, 52, 11, 'پس اس دن جھٹلانے والوں کے لیے ہلاکت ہے'),
(4747, 52, 12, 'جو جھوٹی باتوں میں لگے ہوئے کھیل رہے ہیں'),
(4748, 52, 13, 'جس دن وہ دوزخ کی آگ کی طرف بری طرح سے دھکیلے جائیں گے'),
(4749, 52, 14, 'یہی وہ آگ ہے جسے تم دنیا میں جھٹلاتے تھے'),
(4750, 52, 15, 'پس کیایہ جادو ہے یا تم دیکھتے نہیں'),
(4751, 52, 16, 'اس میں داخل ہو جاؤ پس تم صبر کرو یا نہ کرو تم پر برابر ہے تمہیں تو ویسا ہی بدلہ دیا جائے گا جیسا تم کرتے تھے'),
(4752, 52, 17, 'بے شک پرہیز گار باغوں اور نعمتوں میں ہوں گے'),
(4753, 52, 18, 'محظوظ ہو رہے ہوں گے اس سے جو انہیں ان کے رب نے عطا کی ہے اور ان کو ان کے رب نے عذاب دوزخ سے بچا دیا ہے'),
(4754, 52, 19, 'مزے سے کھاؤ اور پیو بدلے ان (اعمال) کے جو تم کیا کرتے تھے'),
(4755, 52, 20, 'تختوں پر تکیہ لگائے ہوئے جو قطاروں میں بچھے ہوئے ہیں اور ہم ان کا نکاح بڑی بڑی آنکھوں والی حوروں سے کر دیں گے'),
(4756, 52, 21, 'اور جو لوگ ایمان لائے اور ان کی اولاد نے ایمان میں ان کی پیروی کی ہم ان کے ساتھ ان کی اولاد کو بھی (جنت) میں ملا دیں گے اور ان کے عمل میں سے کچھ بھی کم نہ کریں گے ہر شخص اپنے عمل کے ساتھ وابستہ ہے'),
(4757, 52, 22, 'اور ہم انہیں اور زیادہ میوے دیں گے اور گوشت جو وہ چاہیں گے'),
(4758, 52, 23, 'وہاں ایک دوسرے سے شراب کا پیالہ لیں گے جس میں نہ بکواس ہو گی نہ گناہ کا کام'),
(4759, 52, 24, 'اور ان کے پاس لڑکے ان کی خدمت کے لیے پھر رہے ہوں گے گویا وہ غلافوں میں رکھے ہوئے موتی ہیں'),
(4760, 52, 25, 'اورایک دوسرے کی طرف متوجہ ہو کر آپس میں پوچھیں گے'),
(4761, 52, 26, 'کہیں گے ہم تو اس سے پہلے اپنے گھروں میں ڈرا کرتے تھے'),
(4762, 52, 27, 'پس الله نے ہم پر احسان کیا اور ہمیں لُو کے عذاب سے بچا لیا'),
(4763, 52, 28, 'بے شک ہم اس سے پہلے اسے پکارا کرتے تھے بے شک وہ بڑا ہی احسان کرنے والا نہایت رحم والا ہے'),
(4764, 52, 29, 'پس نصیحت کرتے رہئے آپ اپنے رب کے فضل سے نہ کاہن ہیں نہ دیوانہ ہیں'),
(4765, 52, 30, 'کیا وہ کہتے ہیں کہ وہ شاعر ہے ہم اس پر گردشِ زمانہ کا انتظار کر رہے ہیں'),
(4766, 52, 31, 'کہہ دو تم انتظار کرتے رہو بے شک میں بھی تمہارے ساتھ منتظر ہوں'),
(4767, 52, 32, 'کیا ان کی عقلیں انہیں اس بات کا حکم دیتی ہیں یا وہ خود ہی سرکش ہیں'),
(4768, 52, 33, 'یا وہ کہتے ہیں کہ اس نے اسے خود بنا لیا ہے بلکہ وہ ایمان ہی نہیں لاتے'),
(4769, 52, 34, 'پس کوئی کلام اس جیسا لے آئيں اگر وہ سچے ہیں'),
(4770, 52, 35, 'کیا وہ بغیر کسی خالق کے پیدا ہو گئے ہیں یا وہ خود خالق ہیں'),
(4771, 52, 36, 'یا انہوں نے آسمانوں اور زمین کوبنایا ہے نہیں بلکہ وہ یقین ہی نہیں کرتے'),
(4772, 52, 37, 'کیا ان کے پاس آپ کے رب کے خزانے ہیں یا وہ داروغہ ہیں'),
(4773, 52, 38, 'کیا ان کے پاس کوئی سیڑھی ہے کہ وہ اس پر چڑھ کر سن آتے ہیں تو لے آئے ان میں سے سننے والا کوئی دلیل واضح'),
(4774, 52, 39, 'کیا اس کے لیے توبیٹیاں ہیں اور تمہارے لیے بیٹے'),
(4775, 52, 40, 'کیا آپ ان سے کوئی صلہ مانگتے ہیں کہ وہ تاوان سے دبے جا رہے ہیں'),
(4776, 52, 41, 'یا ان کے پاس علم غیب ہے کہ وہ اسے لکھتے رہتے ہیں'),
(4777, 52, 42, 'کیا وہ کوئی داؤ کرنا چاہتے ہیں پس جو منکر ہیں وہی داؤ میں آئے ہوئے ہیں'),
(4778, 52, 43, 'کیا سوائے الله کے ان کا کوئی اور معبود ہے الله اس سےپاک ہے جو وہ شریک ٹھہراتے ہیں'),
(4779, 52, 44, 'اگر وہ ایک ٹکڑا آسمان سے گرتا ہوا دیکھ لیں تو کہہ دیں کہ تہ بہ تہ جما ہوا بادل ہے'),
(4780, 52, 45, 'پس آپ انہیں چھوڑ دیجیئے یہاں تک کہ وہ اپنا وہ دن دیکھ لیں جس میں وہ بے ہوش ہو کر گر پڑیں گے'),
(4781, 52, 46, 'جس دن ان کا داؤ ان کے کچھ بھی کام نہ آئے گا اور نہ انہیں مدد دی جائے گی'),
(4782, 52, 47, 'اوربے شک ان ظالموں کو علاوہ اس کے ایک عذاب (دنیا میں) ہوگا لیکن اکثر ان میں سے نہیں جانتے'),
(4783, 52, 48, 'اور اپنے رب کا حکم آنے تک صبر کر کیوں کہ بےشک آپ ہماری آنکھوں کے سامنے ہیں اور اپنے رب کی حمد کے ساتھ تسبیح کیجیئے جب آپ اٹھا کریں'),
(4784, 52, 49, 'اور (کچھ حصہ رات میں بھی) اس کی تسبیح کیا کیجیئے اور ستاروں کے غروب ہونے کے بعد بھی');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'ستارے کی قسم ہے جب وہ ڈوبنے لگے'),
(4786, 53, 2, 'تمہارا رفیق نہ گمراہ ہوا ہے اورنہ بہکا ہے'),
(4787, 53, 3, 'اور نہ وہ اپنی خواہش سے کچھ کہتا ہے'),
(4788, 53, 4, 'یہ تو وحی ہے جو اس پر آتی ہے'),
(4789, 53, 5, 'بڑے طاقتور (جبرائیل) نے اسے سکھایا ہے'),
(4790, 53, 6, 'جو بڑا زور آور ہے پس وہ قائم ہوا (اصلی صورت میں)'),
(4791, 53, 7, 'اور وہ (آسمان کے) اونچے کنارے پر تھا'),
(4792, 53, 8, 'پھر نزدیک ہوا پھر اور بھی قریب ہوا'),
(4793, 53, 9, 'پھر فاصلہ دو کمان کے برابر تھا یا اس سے بھی کم'),
(4794, 53, 10, 'پھر اس نے الله کےبندے کے دل میں القا کیا جو کچھ القا کیا دل نے'),
(4795, 53, 11, 'جھوٹ نہیں کہا تھا جو دیکھا تھا'),
(4796, 53, 12, 'پھر جو کچھ اس نے دیکھا تم اس میں جھگڑتے ہو'),
(4797, 53, 13, 'اور اس نے اس کو ایک بار اور بھی دیکھا ہے'),
(4798, 53, 14, 'سدرة المنتہیٰ کے پاس'),
(4799, 53, 15, 'جس کے پاس جنت الماویٰ ہے'),
(4800, 53, 16, 'جب کہ اس سدرة پر چھا رہا تھا جو چھا رہا تھا (یعنی نور)'),
(4801, 53, 17, 'نہ تو نظر بہکی نہ حد سے بڑھی'),
(4802, 53, 18, 'بے شک اس نے اپنے رب کی بڑی بڑی نشانیاں دیکھیں'),
(4803, 53, 19, 'پھر کیا تم نے لات اور عزیٰ کو بھی دیکھاہے'),
(4804, 53, 20, 'اور تیسرے منات گھٹیا کو (دیکھا ہے)'),
(4805, 53, 21, 'کیا تمہارے لیے بیٹے اور اس کے لیے بیٹیاں ہیں'),
(4806, 53, 22, 'تب تو یہ بہت ہی بری تقسیم ہے'),
(4807, 53, 23, 'یہ تو صرف نام ہی نام ہیں جو تم نے اور تمہارے باپ دادا نے گھڑ لیے ہیں جن پر خدا نے کوئی سندبھی نہیں اتاری وہ محض وہم اور اپنی خواہش کی پیروی کرتے ہیں حالانکہ ان کے پاس ان کے رب کے ہاں سے ہدایت آ چکی ہے'),
(4808, 53, 24, 'پھر کیا انسان کو وہی مل جاتا ہے جس کی تمنا کرتا ہے'),
(4809, 53, 25, 'پس آخرت اور دنیا الله ہی کے اختیار میں ہے'),
(4810, 53, 26, 'اور بہت سے فرشتے آسمان میں ہیں کہ جن کی شفاعت کسی کے کچھ بھی کام نہیں آتی مگر اس کے بعدکہ الله جس کے لیے چاہے اجازت دے اور پسند کرے'),
(4811, 53, 27, 'بے شک جو لوگ آخرت پر ایمان نہیں لاتے وہ فرشتوں کے عورتوں کے سے نام رکھتے ہیں'),
(4812, 53, 28, 'اور اس بات کو کچھ بھی نہیں جانتے محص وہم پر چلتے ہیں اوروہم حق بات کی جگہ کچھ بھی کام نہیں آتا'),
(4813, 53, 29, 'پھر تم اس کی پرواہ نہ کرو جس نے ہماری یاد سے منہ پھیر لیا ہے اور صرف دنیا ہی کی زندگی چاہتا ہے'),
(4814, 53, 30, 'ان کی سمجھ کی یہیں تک رسائی ہے بے شک آپ کا رب اس کو خوب جانتا ہے جو اس کے راستہ سے بہکا اور اس کو بھی خوب جانتا ہے جو راہ پر آیا'),
(4815, 53, 31, 'اور الله ہی کا ہے جو کچھ آسمانوں اور زمین میں ہے تاکہ براکرنے والوں کو ان کے بدلہ دے اور نیکی کرنے والوں کو نیک بدلہ دے'),
(4816, 53, 32, 'وہ جو بڑے گناہوں اور بے حیائی کی باتوں سے بچتے ہیں مگر صغیرہ گناہوں سے بے شک آپ کا رب بڑی وسیع بخشش والا ہے وہ تمہیں خوب جانتا ہے جب کہ تمہیں زمین سے پیدا کیا تھا اور جب کہ تم اپنی ماں کے پیٹ میں بچے تھے پس اپنے آپ کو پاک نہ سمجھو وہ پرہیزگار کو خوب جانتا ہے'),
(4817, 53, 33, 'بھلا آپ نے اس شخص کو دیکھا جس نے منہ پھیر لیا'),
(4818, 53, 34, 'اور تھوڑا سا دیا اور سخت دل ہو گیا'),
(4819, 53, 35, 'کیا اس کے پاس غیب کا علم ہے کہ وہ دیکھ رہا ہے'),
(4820, 53, 36, 'کیا اسے ان باتوں کی خبر نہیں پہنچی جو موسیٰ کے صحیفوں میں ہیں'),
(4821, 53, 37, 'اورابراھیم کے جس نے (اپنا عہد) پورا کیا'),
(4822, 53, 38, 'وہ یہ کہ کوئی کسی کا بوجھ نہیں اٹھائے گا'),
(4823, 53, 39, 'اور یہ کہ انسان کو وہی ملتا ہے جو کرتا ہے'),
(4824, 53, 40, 'اور یہ کہ اس کی کوشش جلد دیکھی جائے گی'),
(4825, 53, 41, 'پھر اسے پورا بدلہ دیا جائے گا'),
(4826, 53, 42, 'اوریہ کہ سب کو آپ کے رب ہی کی طرف پہنچتا ہے'),
(4827, 53, 43, 'اور یہ کہ وہی ہنساتا ہے اوررلاتا ہے'),
(4828, 53, 44, 'اور یہ کہ وہی مارتا ہے اور زندہ کرتا ہے'),
(4829, 53, 45, 'اور یہ کہ اسی نے جوڑا نر اور مادہ کا پیدا کیا ہے'),
(4830, 53, 46, 'ایک بوند سے جب کہ وہ ٹپکائی جائے'),
(4831, 53, 47, 'اور یہ کہ دوسری بارزندہ کر کے اٹھانا اسی کے ذمہ ہے'),
(4832, 53, 48, 'اور یہ کہ وہی غنی اور سرمایہ دار کرتا ہے'),
(4833, 53, 49, 'اور یہ کہ وہی شعریٰ کا رب ہے'),
(4834, 53, 50, 'اور یہ کہ اسی نے عاد اولیٰ کو ہلاک کیا تھا'),
(4835, 53, 51, 'اور ثمود کو پس اسے باقی نہ چھوڑا'),
(4836, 53, 52, 'اور اس سے پہلے نوح کی قوم کو بے شک وہ زیادہ ظالم اور زيادہ سرکش تھے'),
(4837, 53, 53, 'اور الٹی بستی کو اس نے دے ٹپکا'),
(4838, 53, 54, 'پس اس پر وہ (تباہی) چھا گئی جوچھا گئی'),
(4839, 53, 55, 'پس اپنے رب کی کون کون سی نعمت میں تو شک کرے گا'),
(4840, 53, 56, 'یہ بھی ایک ڈرانے والا ہے پہلے ڈرانے والوں میں سے'),
(4841, 53, 57, 'آنے والی قریب آ پہنچی'),
(4842, 53, 58, 'سوائے الله کے اسے کوئی ہٹانے والا نہیں'),
(4843, 53, 59, 'پس کیا اس بات سے تم تعجب کرتے ہو'),
(4844, 53, 60, 'اور ہنستے ہو اور روتے نہیں'),
(4845, 53, 61, 'اور تم کھیل رہے ہو'),
(4846, 53, 62, 'پس الله کے آگے سجدہ کرو اور اس کی عبادت کرو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قیامت قریب آ گئی اور چاند پھٹ گیا'),
(4848, 54, 2, 'اور اگر وہ کوئی معجزہ دیکھ لیں تو اس سے منہ موڑ لیں اور کہیں یہ تو ہمیشہ سے چلا آتا جادو ہے'),
(4849, 54, 3, 'اور انہوں نے جھٹلایا اور اپنی خواہشوں کی پیروی کی اور ہر بات کے لیے ایک وقت مقرر ہے'),
(4850, 54, 4, 'اور ان کے پاس وہ خبریں آ چکی ہیں جن میں کافی تنبیہ ہے'),
(4851, 54, 5, 'اورپوری دانائی بھی ہے پر ان کو ڈرانے والوں سے فائدہ نہیں پہنچا'),
(4852, 54, 6, 'پس ان سے منہ موڑ لے جس دن پکارنے والا ایک نا پسند چیز کے لیے پکارے گا'),
(4853, 54, 7, 'اپنی آنکھیں نیچے کیے ہوئے قبروں سے نکل پڑیں گے جیسے ٹڈیاں پھیل پڑی ہوں'),
(4854, 54, 8, 'بلانے والے کی طرف بھاگے جا رہے ہوں گے یہ کافر کہہ رہے ہوں گے یہ تو بڑا ہی سخت دن ہے'),
(4855, 54, 9, 'ان سے پہلے قوم نوح نے بھی جھٹلایا تھا پس انہوں نے ہمارے بندے کو جھٹلایا اور کہا دیوانہ ہے اور اسے جھڑک دیا گیا'),
(4856, 54, 10, 'پھر نوح نے اپنے رب کو پکارا کہ میں تو مغلوب ہو گیا تو میری مدد کر'),
(4857, 54, 11, 'پھر ہم نے موسلا دھار پانی سے آسمان کے دروازے کھول دیے'),
(4858, 54, 12, 'اور ہم نے زمین سے چشمے جاری کر دیے پھر جہاں تک پانی کا چڑھاؤ چڑھنا ٹھہر چکا تھاچڑھ آیا'),
(4859, 54, 13, 'اور ہم نے نوح کو تختوں اور کیلوں والی کشتی پر سوار کیا'),
(4860, 54, 14, 'جو ہماری عنایت سے چلتی تھی یہ اس کا بدلہ تھا جس کا انکار کیا گیا تھا'),
(4861, 54, 15, 'اور ہم نے اس کو ایک نشان بنا کر چھوڑ دیا پس کیا کوئی نصیحت پکڑنے والا ہے'),
(4862, 54, 16, 'پھر (دیکھا) ہمارا عذاب اور ڈرانا کیسا تھا'),
(4863, 54, 17, 'اور البتہ ہم نے تو سمجھنے کے لیے قرآن کو آسان کر دیا پھر کوئی ہے کہ سمجھے'),
(4864, 54, 18, 'قوم عاد نے بھی جھٹلایا تھا پھر (دیکھا) ہمارا عذاب اور ڈرانا کیسا تھا'),
(4865, 54, 19, 'بے شک ہم نے ایک دن سخت آندھی بھیجی تھی جس کی نحوست دائمی تھی'),
(4866, 54, 20, 'جو لوگوں کو ایسا پھینک رہی تھی کہ گویا وہ کھجور کے جڑ سے اکھڑے ہوئے پیڑ ہیں'),
(4867, 54, 21, 'پھر (دیکھا) ہمارا عذاب اور ڈرانا کیسا تھا'),
(4868, 54, 22, 'اور البتہ ہم نے قرآن کو سمجھنے کے لیے آسان کر دیا ہے پھر ہے کوئی کہ سمجھے'),
(4869, 54, 23, 'قوم ثمود نے بھی ڈرانے والوں کو جھٹلایا تھا'),
(4870, 54, 24, 'پس کہا کیا ہم اپنے میں سے ایک آدمی کے کہنے پر چلیں گے تب تو ہم ضرور گمراہی اور دیوانگی میں جا پڑیں گے'),
(4871, 54, 25, 'کیا ہم میں سے اسی پر وحی بھیجی گئی بلکہ وہ بڑا جھوٹا (اور) شیخی خورہ ہے'),
(4872, 54, 26, 'عنقریب انہیں معلوم ہو جائے گا کون بڑا جھوٹا (اور) شیخی خورہ ہے'),
(4873, 54, 27, 'بے شک ہم ان کی آزمائش کے لیے اونٹنی بھیجنے والے ہیں پس (اے صالح) ان کا انتظار کر اور صبر کر'),
(4874, 54, 28, 'اور ان سے کہہ دو کہ پانی ان میں بٹ گیا ہے ہر ایک اپنی باری سے پانی پلایا کرے'),
(4875, 54, 29, 'پھر انہوں نے اپنے رفیق کو بلایا تب اس نے ہاتھ بڑھایا اور (اس کی) کانچیں کاٹ ڈالیں'),
(4876, 54, 30, 'پھر (دیکھا) ہمارا عذاب اور ڈرانا کیسا تھا'),
(4877, 54, 31, 'بے شک ہم نے ان پر ایک زور کی چیخ کا عذاب بھیجا پھر وہ ایسے ہو گئے جیسا کانٹو ں کی باڑ کا چورا'),
(4878, 54, 32, 'اور البتہ ہم نے قرآن کو سمجھنے کے لیے آسان کر دیا ہے پھر ہے کوئی سمجھنے والا'),
(4879, 54, 33, 'قوم لوط نے بھی ڈرانے والوں کو جھٹلایا تھا'),
(4880, 54, 34, 'بے شک ہم نے ان پر پتھر برسائے سوائے لوط کے گھر والوں کے ہم نے انہیں پچھلی رات نجات دی'),
(4881, 54, 35, 'یہ ہماری طرف سے فضل ہے جو شکر کرتا ہے ہم اسے ایسا ہی بدلہ دیا کرتے ہیں'),
(4882, 54, 36, 'اور وہ انہیں ہماری پکڑ سے ڈرا چکا تھا پس وہ ڈرانے میں شک کرنے لگے'),
(4883, 54, 37, 'اور البتہ اس سے اس کے مہمانوں کا مطالبہ کرنے لگے تو ہم نے ان کی آنکھیں مٹا دیں پس (کہا) میرے عذاب اور میرے ڈرانے کا مزہ چکھو'),
(4884, 54, 38, 'اور بے شک صبح کو ان پر ایک عذاب نہ ٹلنے والا آ پڑا'),
(4885, 54, 39, 'پس میرے عذاب اور میرے ڈرانے کا مزہ چکھو'),
(4886, 54, 40, 'اور البتہ ہم نے سمجھنے کے لیے قرآن کو آسان کر دیا ہے پھر ہے کوئی سمجھنے والا'),
(4887, 54, 41, 'اور البتہ فرعون کے خاندان کے پاس بھی ڈرانے والے آئے تھے'),
(4888, 54, 42, 'انہوں نے ہماری سب نشانیوں کو جھٹلایا پھر ہم نے انہیں بڑی زبردست پکڑ سے پکڑا'),
(4889, 54, 43, 'کیا تمہارے منکر ان لوگوں سے اچھے ہیں یا تمہارے لیے کتابوں میں نجات لکھی ہے'),
(4890, 54, 44, 'کیا وہ یہ کہتے ہیں کہ ہم زبردست جماعت ہیں'),
(4891, 54, 45, 'عنقریب یہ جماعت بھی شکست کھائے گی اور پیٹھ پھیر کر بھاگیں گے'),
(4892, 54, 46, 'بلکہ قیامت ان کے وعدے کا وقت ہے اور قیامت زیادہ دہشت ناک اور تلخ تر ہے'),
(4893, 54, 47, 'بے شک مجرم گمراہی اور جنون میں ہیں'),
(4894, 54, 48, 'جس دن اپنے منہ کے بل دوزخ میں گھسیٹے جائیں گے (کہا جائے گا) آگ لگنے کا مزہ چکھو'),
(4895, 54, 49, 'بے شک ہم نے ہر چیز اندازے سے بنائی ہے'),
(4896, 54, 50, 'اور ہمارا حکم تو ایک ہی بات ہوتی ہے جیسا کہ پلک جھپکنا'),
(4897, 54, 51, 'اور البتہ ہم تمہارے جیسوں کو غارت کر چکے ہیں پھر کیا کوئی سمجھنے والا ہے'),
(4898, 54, 52, 'اور پھر جو کچھ بھی انہوں نے کیا ہے وہ اعمال ناموں میں موجود ہے'),
(4899, 54, 53, 'اور ہر چھوٹا اور بڑا کام لکھا ہوا ہے'),
(4900, 54, 54, 'بے شک پرہیزگار باغوں اور نہروں میں ہوں گے'),
(4901, 54, 55, 'عزت کے مقام میں قادر مطلق بادشاہ کے حضور میں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'رحمنٰ ہی نے'),
(4903, 55, 2, 'قرآن سکھایا'),
(4904, 55, 3, 'اس نے انسان کو پیدا کیا'),
(4905, 55, 4, 'اسے بولنا سکھایا'),
(4906, 55, 5, 'سورج اور چاند ایک حساب سے چل رہے ہیں'),
(4907, 55, 6, 'اوربیلیں اور درخت سجدہ کر رہے ہیں'),
(4908, 55, 7, 'اور آسمان کو اسی نے بلند کر دیا اور ترازو قائم کی'),
(4909, 55, 8, 'تاکہ تم تولنے میں زیادتی نہ کرو'),
(4910, 55, 9, 'اور انصاف سے تولو اور تول نہ گھٹاؤ'),
(4911, 55, 10, 'اور اس نے خلقت کے لیے زمین کو بچھا دیا'),
(4912, 55, 11, 'اس میں میوے اور غلافوں والی کھجوریں ہیں'),
(4913, 55, 12, 'اور بھوسے دار اناج اور پھول خوشبو دار ہیں'),
(4914, 55, 13, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4915, 55, 14, 'اس نے انسان کو ٹھیکری کی طرح بجتی ہوئی مٹی سے پیدا کیا'),
(4916, 55, 15, 'اور اس نے جنوں کو آگ کے شعلے سے پیدا کیا'),
(4917, 55, 16, 'پھر تم (اے جن و انس) اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4918, 55, 17, 'وہ دونوں مشرقوں اور مغربوں کا مالک ہے'),
(4919, 55, 18, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4920, 55, 19, 'اس نے دو سمندر ملا دیئے جو باہم ملتے ہیں'),
(4921, 55, 20, 'ان دونوں میں پردہ ہے کہ وہ حد سے تجاوز نہیں کرسکتے'),
(4922, 55, 21, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4923, 55, 22, 'ان دونوں میں سے موتی اور مونگا نکلتا ہے'),
(4924, 55, 23, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4925, 55, 24, 'اور سمند ر میں پہا ڑوں جیسے کھڑے ہوئے جہاز اسی کے ہیں'),
(4926, 55, 25, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4927, 55, 26, 'جو کوئی زمین پر ہے فنا ہوجانے والا ہے'),
(4928, 55, 27, 'اور آپ کے پروردگار کی ذات باقی رہے گی جو بڑی شان اور عظمت والا ہے'),
(4929, 55, 28, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4930, 55, 29, 'اس سے مانگتے ہیں جو آسمانوں اور زمین میں ہیں ہر روز وہ ایک کام میں ہے'),
(4931, 55, 30, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4932, 55, 31, 'اے جن و انس ہم تمہارے لیے جلد ہی فارغ ہو جائیں گے'),
(4933, 55, 32, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4934, 55, 33, 'اے جنوں اور انسانوں کے گروہ اگر تم آسمانوں اور زمین کی حدود سے باہر نکل سکتے ہو تو نکل جاؤ تم بغیر زور کے نہ نکل سکو گے (اور وہ ہے نہیں)'),
(4935, 55, 34, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4936, 55, 35, 'تم پر آگے کے شعلے اور دھواں چھوڑا جائے گا پھر تم بچ نہ سکو گے'),
(4937, 55, 36, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4938, 55, 37, 'پھر جب آسمان پھٹ جائے گا اور پھٹ کر گلابی تیل کی طرح سرخ ہو جائے گا'),
(4939, 55, 38, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4940, 55, 39, 'پس اس دن اپنے گناہ کی بات نہ کوئی انسان اور نہ کوئی جن پوچھا جائے گا'),
(4941, 55, 40, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4942, 55, 41, 'مجرم اپنے چہرے کے نشان سے پہچانے جائیں گے پس پیشانی کے بالوں اور پاؤں سے پکڑے جائیں گے'),
(4943, 55, 42, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4944, 55, 43, 'یہی وہ دوزخ ہے جسے مجرم جھٹلاتے تھے'),
(4945, 55, 44, 'گناہ گار جہنم میں اور کھولتے ہوئے پانی میں تڑپتے پھریں گے'),
(4946, 55, 45, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4947, 55, 46, 'اوراس کے لیے جو اپنے رب کے سامنے کھڑا ہونے سے ڈرتا ہے دو باغ ہوں گے'),
(4948, 55, 47, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4949, 55, 48, 'جن میں بہت سی شاخیں ہوں گی'),
(4950, 55, 49, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4951, 55, 50, 'ان دونوں میں دو چشمے جاری ہوں گے'),
(4952, 55, 51, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4953, 55, 52, 'ان دونوں میں ہر میوہ کی دو قسمیں ہوں گی'),
(4954, 55, 53, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4955, 55, 54, 'ایسے فرشتو ں پر تکیہ لگائے بیٹھے ہوں گے کہ جن کا استر مخملی ہوگا اور دونوں باغوں کا میوہ جھک رہا ہوگا'),
(4956, 55, 55, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4957, 55, 56, 'ان میں نیچی نگاہوں والی عورتیں ہوں گی نہ تو انہیں ان سے پہلے کسی انسان نے اور نہ کسی جن نے چھوا ہوگا'),
(4958, 55, 57, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4959, 55, 58, 'گویا کہ وہ یاقوت اور مونگا ہیں'),
(4960, 55, 59, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4961, 55, 60, 'نیکی کا بدلہ نیکی کے سوا اور کیا ہے'),
(4962, 55, 61, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4963, 55, 62, 'اور ان دو کے علاوہ اور دو باغ ہوں گے'),
(4964, 55, 63, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4965, 55, 64, 'وہ دونوں بہت ہی سبز ہوں گے'),
(4966, 55, 65, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4967, 55, 66, 'ان دونوں میں دو چشمے ابلتےہوئے ہوں گے'),
(4968, 55, 67, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4969, 55, 68, 'ان دونوں میں میوے اور کھجوریں اور انار ہوں گے'),
(4970, 55, 69, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4971, 55, 70, 'ان میں نیک خوبصورت عورتیں ہوں گی'),
(4972, 55, 71, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4973, 55, 72, 'وہ حوریں جو خیموں میں بند ہوں گی'),
(4974, 55, 73, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4975, 55, 74, 'نہ انہیں ان سےپہلے کسی انسان نے اور نہ کسی جن نے چھوا ہوگا'),
(4976, 55, 75, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4977, 55, 76, 'قالینوں پر تکیہ لگائے ہوئے ہوں گے جو سبز اور نہایت قیمتی نفیس ہوں گے'),
(4978, 55, 77, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے'),
(4979, 55, 78, 'آپ کے رب کا نام با برکت ہے جو بڑی شان اور عظمت والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'جب واقع ہونے والی واقع ہو گی'),
(4981, 56, 2, 'جس کے واقع ہونے میں کچھ بھی جھوٹ نہیں'),
(4982, 56, 3, 'پست کرنے والی اور بلند کرنے والی'),
(4983, 56, 4, 'جب کہ زمین بڑے زور سے ہلائی جائے گی'),
(4984, 56, 5, 'اور پہاڑ ٹکڑے ٹکڑے ہو کر چورا ہو جائیں گے'),
(4985, 56, 6, 'سو و ہ غبار ہو کر اڑتے پھریں گے'),
(4986, 56, 7, 'اور (اس وقت) تمہاری تین جماعتیں ہو جائیں گی'),
(4987, 56, 8, 'پھر داہنے والے کیا خوب ہی ہیں داہنے والے'),
(4988, 56, 9, 'اوربائیں والے کیسے برے ہیں بائیں والے'),
(4989, 56, 10, 'اور سب سے اول ایمان لانے والے سب سے اول داخل ہونے والے ہیں'),
(4990, 56, 11, 'وہ الله کے ساتھ خاص قرب رکھنے والے ہیں'),
(4991, 56, 12, 'نعمت کے باغات ہوں گے'),
(4992, 56, 13, 'پہلوں میں سے بہت سے'),
(4993, 56, 14, 'اور پچھلوں میں سے تھوڑے سے'),
(4994, 56, 15, 'تختوں پر جو جڑاؤ ہوں گے'),
(4995, 56, 16, 'آمنے سامنے تکیہ لگائے ہوئےبیٹھے ہوں گے'),
(4996, 56, 17, 'ان کے پاس ایسے لڑکے جو ہمیشہ لڑکے ہی رہیں گے آمد و رفت کیا کریں گے'),
(4997, 56, 18, 'آبخورے اور آفتابے اور ایسا جام شراب لے کر جو بہتی ہوئی شراب سے بھرا جائے گا'),
(4998, 56, 19, 'نہ اس سے ان کو دردِ سر ہوگا اور نہ اس سے عقل میں فتور آئے گا'),
(4999, 56, 20, 'اور میوے جنہیں وہ پسند کریں گے'),
(5000, 56, 21, 'اور پرندوں کا گوشت جو ان کو مرغوب ہو گا'),
(5001, 56, 22, 'اوربڑی بڑی آنکھوں والی حوریں'),
(5002, 56, 23, 'جیسے موتی کئی تہو ں میں رکھےہوئے ہوں'),
(5003, 56, 24, 'بدلے اس کے جو وہ کیا کرتے تھے'),
(5004, 56, 25, 'وہ وہاں کوئی لغو اور گناہ کی بات نہیں سنیں گے'),
(5005, 56, 26, 'مگر سلام سلام کہنا'),
(5006, 56, 27, 'اور داہنے والے کیسے اچھے ہوں گے داہنے والے'),
(5007, 56, 28, 'وہ بے کانٹوں کی بیریوں میں ہوں گے'),
(5008, 56, 29, 'اور گتھے ہوئے کیلو ں میں'),
(5009, 56, 30, 'اورلمبے سایوں میں'),
(5010, 56, 31, 'اور پانی کی آبشاروں میں'),
(5011, 56, 32, 'اور باافراط میوں میں'),
(5012, 56, 33, 'جونہ کبھی منقطع ہوں گے اور نہ ان میں روک ٹوک ہو گی'),
(5013, 56, 34, 'اور اونچے فرشتوں میں'),
(5014, 56, 35, 'بے شک ہم نے انہیں (حوروں کو) ایک عجیب انداز سے پیدا کیا ہے'),
(5015, 56, 36, 'پس ہم نے انہیں کنواریاں بنا دیا ہے'),
(5016, 56, 37, 'دل لبھانے والی ہم عمر بنایا ہے'),
(5017, 56, 38, 'داہنے والوں کے لیے'),
(5018, 56, 39, 'بہت سے پہلوں میں سے ہوں گے'),
(5019, 56, 40, 'اور بہت سے پچھلوں میں سے'),
(5020, 56, 41, 'اوربائیں والے کیسے برے ہیں بائیں والے'),
(5021, 56, 42, 'وہ لووں اور کھولتے ہوئے پانی میں ہوں گے'),
(5022, 56, 43, 'اور سیاہ دھوئیں کے سائے میں'),
(5023, 56, 44, 'جو نہ ٹھنڈا ہوگا اور نہ راحت بخش'),
(5024, 56, 45, 'بے شک وہ اس سے پہلے خوش حال تھے'),
(5025, 56, 46, 'اور بڑے گناہ (شرک) پر اصرار کیا کرتے تھے'),
(5026, 56, 47, 'اور کہا کرتے تھے کیا جب ہم مر جائیں گے اور مٹی اور ہڈیاں ہو جائیں گے تو کیا ہم پھر اٹھاے جائیں گے'),
(5027, 56, 48, 'اور کیا ہمارے اگلے باپ دادا بھی'),
(5028, 56, 49, 'کہہ دو بے شک پہلے بھی اور پچھلے بھی'),
(5029, 56, 50, 'ایک معین تاریخ کے وقت پر جمع کیے جاویں گے'),
(5030, 56, 51, 'پھر بے شک تمہیں اے گمراہو جھٹلانے والو'),
(5031, 56, 52, 'البتہ تھوہر کا درخت کھانا ہوگا'),
(5032, 56, 53, 'پھر اس سے پیٹ بھرنے ہوں گے'),
(5033, 56, 54, 'پھر اس پر کھولتا ہوا پانی پینا ہوگا'),
(5034, 56, 55, 'پھر پینا ہو گا پیاسے اونٹوں کا سا پینا'),
(5035, 56, 56, 'قیامت کے دن یہ ان کی مہمانی ہو گی'),
(5036, 56, 57, 'ہم نے ہی تمہیں پیدا کیا ہے پس کیوں تم تصدیق نہیں کرتے'),
(5037, 56, 58, 'بھلا دیکھو (تو) (منی) جو تم ٹپکاتے ہو'),
(5038, 56, 59, 'کیا تم اسے پیدا کرتے ہو یا ہم ہی پیدا کرنے والے ہیں'),
(5039, 56, 60, 'ہم نے ہی تمہارے درمیان موت مقرر کر دی ہے اور ہم عاجز نہیں ہیں'),
(5040, 56, 61, 'اس بات سے کہ ہم تم جیسے لوگ بدل لائیں اور تمہیں ایسی صورت میں بنا کھڑا کریں جو تم نہیں جانتے'),
(5041, 56, 62, 'اور تم پہلی پیدائش کو جان چکے ہو پھر کیوں تم غور نہیں کرتے'),
(5042, 56, 63, 'بھلا دیکھو جو کچھ تم بولتے ہو'),
(5043, 56, 64, 'کیا تم اسے اگاتے ہو یا ہم اگانے والے ہیں'),
(5044, 56, 65, 'اگر ہم چاہیں تو اسے چورا چورا کر دیں پھر تم تعجب کرتے رہ جاؤ'),
(5045, 56, 66, 'کہ بے شک ہم پر تو تاوان پڑ گیا'),
(5046, 56, 67, 'بلکہ ہم بے نصیب ہو گئے'),
(5047, 56, 68, 'بھلا دیکھو تو سہی وہ پانی جو تم پیتے ہو'),
(5048, 56, 69, 'کیا تم نے اسے بادل سے اتارا ہے یا ہم اتارنے والے ہیں'),
(5049, 56, 70, 'اگر ہم چاہیں تو اسے کھاری کر دیں پس کیوں تم شکر نہیں کرتے'),
(5050, 56, 71, 'بھلا دیکھو تو سہی وہ آگ جو تم سلگاتے ہو'),
(5051, 56, 72, 'کیا تم نے اس کا درخت پیدا کیا ہے یا ہم پیدا کرنے والے ہیں'),
(5052, 56, 73, 'ہم نے اسے یادگار اور مسافروں کے لیے فائدہ کی چیز بنا دیا ہے'),
(5053, 56, 74, 'پس اپنے رب کے نام کی تسبیح کر جو بڑا عظمت والا ہے'),
(5054, 56, 75, 'پھر میں تاروں کے ڈوبنے کی قسم کھاتا ہوں'),
(5055, 56, 76, 'اور بے شک اگر سمجھو تو یہ بڑی قسم ہے'),
(5056, 56, 77, 'کہ بے شک یہ قرآن بڑی شان والا ہے'),
(5057, 56, 78, 'ایک پوشیدہ کتاب میں لکھا ہوا ہے'),
(5058, 56, 79, 'جسے بغیر پاکو ں کے اور کوئی نہیں چھوتا'),
(5059, 56, 80, 'پروردگار عالم کی طرف سے نازل ہوا ہے'),
(5060, 56, 81, 'سو کیا تم اس کلام کو سرسری بات سمجھتے ہو'),
(5061, 56, 82, 'اور اپنا حصہ تم یہی لیتے ہو کہ اسے جھٹلاتے ہو'),
(5062, 56, 83, 'پھر کس لیے روح کو روک نہیں لیتے جب کہ وہ گلے تک آ جاتی ہے'),
(5063, 56, 84, 'اورتم اس وقت دیکھا کرتے ہو'),
(5064, 56, 85, 'اور تم سے زیادہ ہم اس کے قرب ہوتے ہیں لیکن تم نہیں دیکھتے'),
(5065, 56, 86, 'پس اگر تمہارا حساب کتاب ہونے والا نہیں ہے'),
(5066, 56, 87, 'تو تم اس روح کو کیوں نہیں لوٹا دیتے اگر تم سچے ہو'),
(5067, 56, 88, 'پھر (جب قیامت آئے گی) اگر وہ مقربین میں سے ہے'),
(5068, 56, 89, 'تو (اس کے لیے) راحت اور خوشبو میں اور عیش کی باغ ہیں'),
(5069, 56, 90, 'اور اگر وہ داہنے والوں میں سے ہے'),
(5070, 56, 91, 'تو اے شخص تو جو داہنے والوں میں سے ہے تجھ پر سلام ہو'),
(5071, 56, 92, 'اور اگر وہ جھٹلانے والے گمراہوں میں سے ہے'),
(5072, 56, 93, 'تو کھولتا ہوا پانی مہمانی ہے'),
(5073, 56, 94, 'اور دوزخ میں داخل ہونا ہے'),
(5074, 56, 95, 'بے شک یہ تحقیقی یقینی بات ہے'),
(5075, 56, 96, 'پس اپنے رب کی نام تسبیح کر جو بڑا عظمت والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'الله کی پاکیزگی بیان کرتے ہیں وہ جو آسمانوں اور زمین میں ہیں اور وہ زبردست حکمت والا ہے'),
(5077, 57, 2, 'آسمانوں اور زمین کی بادشاہت اسی کے یے ہے وہ زندہ کرتا ہے اور مارتا ہے اور وہ ہر چیز پر قادر ہے'),
(5078, 57, 3, 'وہی سب سے پہلا اور سب سے پچھلا اور ظاہر اور پوشیدہ ہے اور وہی ہر چیز کو جاننے والا ہے'),
(5079, 57, 4, 'وہی ہے جس نے آسمانوں اور زمین کو چھ دن میں بنایا پھر وہ عرش پر قائم ہوا وہ جانتا ہے جو چیز زمین میں داخل ہوتی ہے اور جو اس سے نکلتی ہے اور جو آسمان سے اترتی ہے اور جو اس میں اوپر چڑھتی ہے اور وہ تمہارے ساتھ ہے جہاں کہیں تم ہو اور الله اس کو جو تم کرتے ہو دیکھتا ہے'),
(5080, 57, 5, 'آسمانوں اورزمین کی حکومت اسی کے لیے ہے اور سب امور الله ہی کی طرف لوٹائے جاتے ہیں'),
(5081, 57, 6, 'وہ رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اوروہ سینوں کے بھید خوب جانتا ہے'),
(5082, 57, 7, 'الله اور اس کے رسول پر ایمان لاؤ اور اس میں سے خرچ کرو جس میں اس نے تمہیں پہلوں کا جانشین بنایا ہے پس جو لوگ تم میں سے ایمان لائے اور انہوں نے خرچ کیا ان کے لیے بڑا اجر ہے'),
(5083, 57, 8, 'اور تمہیں کیا ہوا جو الله پر ایمان نہیں لاتے اور رسول تمہیں تمہارے رب پرایمان لانے کے لیے بلا رہا ہے اور تم سے عہد بھی لے چکا ہے اگر تم ایمان لانے والے ہو'),
(5084, 57, 9, 'وہی ہے جو اپنے بندے پر کھلی کھلی آیتیں نازل کر رہا ہے تاکہ تمہیں اندھیروں میں سے نکال کر روشنی میں لائے اور بے شک الله تم پر بڑا مہربان نہایت رحم والا ہے'),
(5085, 57, 10, 'اور تمہیں کیا ہوگیا جو الله کی راہ میں خرچ نہیں کرتے حالانکہ آسمانوں اور زمین کا ورثہ تو الله ہی کے لیے ہے تم میں سے اور کوئی اس کے برابر ہو نہیں سکتا جس نے فتح مکہ سے پہلے خرچ کیا اور جہاد کیا یہ ہیں کہ الله کے نزدیک جن کا بڑا درجہ ہے ان لوگو ں پر ہے جنہوں نے بعد میں خرچ کیا اور جہاد کیا اور الله نے ہر ایک سے نیک جزا کا وعدہ کیا ہے اور الله تمہارے کاموں سے خبردار ہے'),
(5086, 57, 11, 'ایسا کون ہے جو الله کو اچھا قرض دے پھر وہ اس کو ا سکے لیے دگنا کر دے اوراس کے لیے عمدہ بدلہ ہے'),
(5087, 57, 12, 'جس دن آپ ایماندار مردوں اور عورتوں کو دیکھیں گے کہ ان کا نور ان کے سامنے اور ان کے داہنے دوڑ رہا ہوگا تمہیں آج ایسے باغوں کی خوشخبری ہے کہ ان کے نیچے نہریں چلتی ہیں وہ ان میں ہمیشہ رہیں گے یہی وہ بڑی کامیابی ہے'),
(5088, 57, 13, 'جس دن منافق مرد اورمنافق عورتیں ان سے کہیں گے جو ایمان لائے ہیں کہ ہمارا انتظار کرو کہ ہم بھی تمہارے نور سے روشنی لے لیں کہا جائے گا اپنے پیچھے لوٹ جاؤ پھر روشنی تلاش کرو پس ان کے درمیان ایک دیوار کھڑی کر دی جائے گی جس میں ایک دروازہ ہو گا اس کے اندر تو رحمت ہو گی اور اس کے باہر کی طرف عذاب ہو گا'),
(5089, 57, 14, 'وہ انہیں پکاریں گے کیا ہم تمہارے ساتھ نہ تھے وہ کہیں گے کیوں نہیں لیکن تم نے اپنے آپ کو فتنہ میں ڈالا اور راہ دیکھتے اور شک کرتے رہے اور تمہیں آرزوؤں نے دھوکہ دیا یہاں تک کہ الله کا حکم آ پہنچا اور تمہیں الله کے بارے میں شیطان نے دھوکہ دیا'),
(5090, 57, 15, 'پس آج نہ تم سے کوئی تاوان لیا جائے گا اور نہ ان سے جنہوں نے انکار کیا تھا تمہارا سب کا ٹھکانا دوزخ ہے وہی تمہارا رفیق ہے اوربہت ہی بری جگہ ہے'),
(5091, 57, 16, 'کیا ایمان والوں کے لیے اس بات کا وقت نہیں آیا کہ ان کے دل الله کی نصیحت اور جو دین حق نازل ہوا ہے اس کے سامنے جھک جائیں اور ان لوگوں کی طرح نہ ہوجائیں جنہیں ان سے پہلے کتاب (آسمانی) ملی تھی پھر ان پر مدت لمبی ہو گئی تو ان کے دل سخت ہو گئے اور ان میں سے بہت سے نافرمان ہیں'),
(5092, 57, 17, 'اور جان لو کہ الله ہی زمین کو اس کے مرنے پیچھے زندہ کرتا ہے ہم نے تو تمہارے لیے کھول کھول کر نشانیاں بیان کر دی ہیں تاکہ تم سمجھو'),
(5093, 57, 18, 'بے شک خیرات کرنے والے مرد اور خیرات کرنے والی عورتیں اور جنہوں نے الله کو اچھا قرض دیا ان کے لیے دگنا کیا جائے گا اور انہیں عمدہ بدلہ ملے گا'),
(5094, 57, 19, 'اور جو لوگ الله اور اس کے رسولوں پر ایمان لائے وہی لوگ اپنے رب کے نزدیک صدیق اور شہید ہیں ان کے لیے ان کا اجر اور ان کی روشنی ملے گی اور جنہوں نے کفر کیا اورہماری آیتوں کو جھٹلایا یہی لوگ دوزخی ہیں'),
(5095, 57, 20, 'جان لو کہ یہ دنیا کی زندگی محض کھیل اور تماشا اور زیبائش اور ایک دوسرے پر آپس میں فخر کرنا اور ایک دوسرے پر مال اور اولاد میں زیادتی چاہنا ہے جیسے بارش کی حالت کہ اس کی سبزی نے کسانوں کو خوش کر دیا پھر وہ خشک ہو جاتی ہے تو تو اسے زرد شدہ دیکھتا ہے پھر وہ چورا چورا ہو جاتی ہے اور آخرت میں سخت عذاب ہے اور الله کی مغفرت اور اس کی خوشنودی ہے اور دنیاکی زندگی سوائے دھوکے کے اسباب کے اور کیا ہے'),
(5096, 57, 21, 'اپنے رب کی مغفرت کی طرف دوڑو اور جنت کی طرف جس کا عرض آسمان اور زمین کے عرض کے برابر ہے ان کے لیے تیار کی گئی ہے جو الله اور اس کے رسولوں پر ایمان لائے یہ الله کا فضل ہے وہ جسے چاہتا ہے دیتا ہے اور الله بڑے فضل والا ہے'),
(5097, 57, 22, 'جو کوئی مصیبت زمین پر یاخودتم پر پڑتی ہے وہ اس سے پیشتر کہ ہم اسے پیدا کریں کتاب میں لکھی ہوتی ہے بے شک یہ الله کے نزدیک آسان بات ہے'),
(5098, 57, 23, 'تاکہ جو چیز تمہارے ہاتھ سے جاتی رہے اس پر رنج نہ کرو اور جو تمہیں دے اس پر اتراؤ نہیں اور الله کسی اترانے والے شیخی خورے کو پسند نہیں کرتا'),
(5099, 57, 24, 'جو خود بھی بخل کرتے ہیں اور لوگوں کو بھی بخل کا حکم دیتے ہیں اور جو کوئی منہ موڑے تو الله بھی بے پرواہ خوبیوں والا ہے'),
(5100, 57, 25, 'البتہ ہم نے اپنے رسولوں کو نشانیاں دے کر بھیجا اور ان کے ہمراہ ہم نے کتاب اور ترازوئے (عدل) بھی بھیجی تاکہ لوگ انصاف کو قائم رکھیں اور ہم نے لوہا بھی اتارا جس میں سخت جنگ کے سامان اورلوگوں کے فائدے بھی ہیں اور تاکہ الله معلوم کرے کہ کون اس کی اور اس کے رسولوں کی غائبانہ مدد کرتا ہے بے شک الله بڑا زور آور غالب ہے'),
(5101, 57, 26, 'اور ہم نے نوح اور ابراھیم کو بھیجا تھا اور ہم نے ان دونوں کیاولاد میں نبوت اور کتاب رکھی تھی پس بعض تو ان میں راہِ راست پر رہے اور بہت سے ان میں سے نافرمان ہیں'),
(5102, 57, 27, 'پھر اس کے بعد ہم نے اپنے اور رسول بھیجے اور عیسیٰ ابن مریم کو بعد میں بھیجا اور اسے ہم نے انجیل دی اور اس کے ماننے والوں کے دلوں میں ہم نے نرمی اور مہربانی رکھ دی اور ترک دنیا جو انہوں نے خود ایجاد کی ہم نے وہ ان پرفرض نہیں کی تھی مگر انہوں نے رضائے الہیٰ حاصل کرنے کے لیے ایسا کیا پس اسے نباہ نہ سکے جیسے نباہنا چاہیئے تھا تو ہم نے انہیں جو ان میں سے ایمان لائے ان کا اجر دے دیا اور بہت سے تو ان میں بدکار ہی ہیں'),
(5103, 57, 28, 'اے ایمان والو الله سے ڈرو اور اس کے رسول پر ایمان لاؤ وہ تمہیں اپنی رحمت سے دوہرا حصہ دے گا اور تمہیں ایسا نورعطا کرے گا تم اس کے ذریعہ سے چلو اور تمہیں معاف کر دے گا اور الله بخشنے والا نہایت رحم ولا ہے'),
(5104, 57, 29, 'تاکہ اہلِ کتاب یہ نہ سمجھیں کہ (مسلمان) الله کے فضل میں سے کچھ بھی حاصل نہیں کر سکتے اوریہ کہ فضل تو الله ہی کے ہاتھ میں ہے جس کو چاہے دے اور الله بڑا فضل کرنے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'بے شک الله نے اس عورت کی بات سن لی ہے جو آپ سے اپنے خاوند کے بارے میں جھگڑتی تھی اور الله کی جناب میں شکایت کرتی تھی اور الله تم دونوں کی گفتگو سن رہا تھا بے شک الله سب کچھ سننے والا دیکھنے والا ہے'),
(5106, 58, 2, 'جو لوگ تم میں سے اپنی عورتوں سے ظہار کرتے ہیں وہ ان کی مائیں نہیں ہو جاتیں ان کی مائیں تو وہی ہیں جنہوں نے انہیں جنا ہے اور بےشک انہوں نے ایک بیہودہ اور جھوٹی بات منہ سے نکالی ہے اور بے شک الله معاف کرنے والا بخشنے والا ہے'),
(5107, 58, 3, 'اور جو لوگ اپنی بیویوں سے اظہار کرتے ہیں پھر اس کہی ہوئی بات سے پھرنا چاہیں تو ایک غلام ایک دوسرے کو ہاتھ لگانے سے پہلے آزاد کر دیں یہ اس کے لیے اس سے تمہیں نصیحت ہو اور الله جو کچھ تم کرتے ہو اس کی خبر رکھتا ہے'),
(5108, 58, 4, 'پس جو شخص نہ پائے تو دو مہینے کے لگاتار روزے رکھے اس سے پہلےکہ ایک دوسرے کو چھوئیں پس جو کوئی ایسا نہ کر سکے تو ساٹھ مسکینوں کو کھانا کھلائے یہ ا س لیے کہ تم الله اور اس کے رسول پر ایمان لاؤ اور یہ الله کی حدیں ہیں اورمنکروں کے لیے دردناک عذاب ہے'),
(5109, 58, 5, 'بے شک جو لوگ الله اور اس کے رسول کی مخالفت کرتے ہیں وہ ذلیل کیے جائیں گے جس طرح ذلیل کیے گئے وہ لوگ جو ان سے پہلے تھے اور ہم نے تو صاف صاف آیتیں نازل کر دی ہیں اور منکروں کے لیے ذلت کا عذاب ہے'),
(5110, 58, 6, 'جس دن ان سب کو الله قبروں سے اٹھائے گا پھر ان کو بتائے گا کہ وہ کیا کرتے تھے جس کو الله نے یاد رکھا ہے اور وہ بھول گئے ہیں اور الله کے سامنے ہر چیز موجود ہے'),
(5111, 58, 7, 'کیا آپ نے نہیں دیکھا الله جانتا ہے جو کچھ آسمانوں اور زمین میں ہے (یہاں تک) کہ جو کوئی مشورہ تین آدمیوں میں ہوتا ہے تو وہ چوتھا ہوتا ہے اور جو پانچ میں ہوتا ہے تو وہ چھٹا ہوتا ہے اور خواہ اس سے کم کی سرگوشی ہو یا زیادہ کی مگر وہ ہر جگہ ان کے ساتھ ہوتا ہے پھر انہیں قیامت کے دن بتائے گا کہ وہ کیا کرتے تھے بے شک الله ہر چیز کو جاننے والا ہے'),
(5112, 58, 8, 'کیا آپ نے ان کو نہیں دیکھا جو سرگوشی کرنے سے روکے گئے تھے پھر وہ اسی بات کی طرف لوٹتے ہیں جس سے انہیں روکا گیا تھا اور گناہ اور سرکشی اور رسول کی نافرمانی کی سرگوشی کرتے ہیں اور جب وہ آپ کے پاس آتے ہیں تو آپ کو ایسے لفظوں سے سلام کرتے ہیں جن سے الله نے آپ کو سلام نہیں دیا اور اپنے دلوں میں کہتے ہیں کہ ہمیں الله اس پر کیوں عذاب نہیں دیتا جو ہم کہہ رہے ہیں ان کے لیے دوزخ کافی ہے وہ اس میں داخل ہوں گے پس وہ کیا ہی برا ٹھکانہ ہے'),
(5113, 58, 9, 'اے ایمان والو جب تم آپس میں سرگوشی کرو تو گناہ اور سرکشی اور رسول کی نافرمانی کی سرگوشی نہ کرو اورنیکی اور پرہیز گاری کی سرگوشی کرو اور الله سے ڈرو جس کی طرف تم جمع کیے جاؤ گے'),
(5114, 58, 10, '(یہ) سرگوشی تو صرف شیطانی بات ہے تاکہ ایمان داروں کو غمناک کر دے حالانکہ بغیر حکم الله کے کچھ بھی ضرر نہیں دے سکتی اور ایمان والے تو الله ہی پربھروسہ رکھتے ہیں'),
(5115, 58, 11, 'اے ایمان والو جب تمہیں مجلسوں میں کھل کر بیٹھنے کو کہا جائے تو کھل کر بیٹھو الله تمیں فراخی دے گا اور جب کہا جائے کہ اٹھ جاؤ تو اٹھ جاؤ تم میں سے الله ایمان داروں کے اور ان کے جنہیں علم دیا گیا ہے درجے بلند کرے گا اور جو کچھ تم کرتے ہواللہ اس سے خبردار ہے'),
(5116, 58, 12, 'اے ایمان والو جب تم رسول سے سرگوشی کرو تو اپنی سرگوشی سے پہلے صدقہ دے لیا کرو یہ تمہارے لیے بہتر اور زیادہ پاکیزہ بات ہے پس اگر نہ پاؤ تو الله بخشنے والا نہایت رحم والا ہے'),
(5117, 58, 13, 'کیا تم اپنی سرگوشی سے پہلے صدقہ دینے سے ڈر گئے پھر جب تم نے نہ کیا اور الله نے تمہیں معاف بھی کردیا تو (بس) نماز ادا کرو اور زکواة دو اور الله اور اس کے رسول کی اطاعت کرو اور جو کچھ تم کرتے ہو الله اس سے خبردار ہے'),
(5118, 58, 14, 'کیا آپ نے ان کو نہیں دیکھا جنہوں نے اس قوم سے دوستی رکھی ہے جن پر الله کا غضب ہے نہ وہ تم میں سے ہیں اور نہ ان میں سے اوروہ جان بوجھ کر جھوٹ پر قسمیں کھاتے ہیں'),
(5119, 58, 15, 'الله نے ان کے لیے سخت عذاب تیار کر رکھا ہے بے شک وہ بہت ہی برا ہے جو کچھ وہ کرتے ہیں'),
(5120, 58, 16, 'انہوں نے اپنی قسموں کو ڈھال بنا لیا ہے پس وہ (لوگوں کو) الله کی راہ سے روکتے ہیں تو ان کے لیے ذلیل کرنے والاعذآب ہے'),
(5121, 58, 17, 'الله کے مقابلہ میں نہ تو ان کے مال ہی کچھ کام آئیں گے اور نہ ان کی اولاد کچھ کام آئے گی یہ دوزخی لوگ ہیں وہ اس میں ہمیشہ رہنے والے ہیں'),
(5122, 58, 18, 'جس دن الله ان سب کو قبروں سے اٹھائے گا تو اس کے سامنے بھی ایسی ہی قسمیں کھائیں گے جیسی کہ تمہارے سامنے کھاتے ہیں اور سمھجھ رہے ہیں کہ ہم رستے پر ہیں خبردار بے شک وہی جھوٹے ہیں'),
(5123, 58, 19, 'ان پر شیطان نے غلبہ پا لیا ہے پس اس نے انہیں الله کا ذکر بھلا دیا ہے یہی شیطان کا گروہ ہے خبردار بے شک شیطان کا گروہ ہی نقصان اٹھانے والا ہے'),
(5124, 58, 20, 'بے شک جو لوگ الله اورا سکے رسول کی مخالفت کرتے ہیں یہی لوگ ذلیلوں میں ہیں'),
(5125, 58, 21, 'الله نے لکھ دیا ہے کہ میں اور میرے رسول ہی غالب رہیں گے بے شک الله زور آور زبردست ہے'),
(5126, 58, 22, 'آپ ایسی کوئی قوم نہ پائیں گے جو الله اور قیامت کے دن پر ایمان رکھتی ہو اور ان لوگو ں سے بھی دوستی رکھتے ہوں جو الله اور اس کے رسول کی مخالفت کرتے ہیں گو وہ ان کے باپ یا بیٹے یا بھائی یا کنبے کے لوگ ہی کیوں نہ ہوں یہی وہ لوگ ہیں جن کے دلوں میں اللہ نے ایمان لکھ دیا ہے اور ان کو اپنے فیض سے قوت دی ہے اور وہ انہیں بہشتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہوں گی وہ ان میں ہمیشہ رہیں گے الله ان سے راضی ہوا اور وہ اس سے راضی ہوئے یہی الله کا گروہ ہے خبردار بے شک الله کا گروہ ہی کامیاب ہونے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'جو مخلوقات آسمانوں میں ہے اور جو زمین میں ہے الله کی تسبیح کرتی ہے اور وہی غالب حکمت والا ہے'),
(5128, 59, 2, 'وہی ہے جس نے اہلِ کتاب کے کافروں کو ان کے گھروں سے پہلا لشکر جمع کرنے کے وقت نکال دیا حالانکہ تمہیں ان کے نکلنے کا گمان بھی نہ تھا اور وہ یہی سمجھ رہے تھے کہ ان کے قلعے انہیں الله سے بچا لیں گے پھر الله کا عذاب ان پر وہاں سے آیا کہ جہاں کا ان کو گمان بھی نہ تھا اوران کے دلوں میں ہیبت ڈال دی کہ اپنے گھر وں کو اپنے اورمسلمانوں کے ہاتھوں سے آپ اجاڑنے لگے پس اے آنکھوں والو عبرت حاصل کرو'),
(5129, 59, 3, 'اوراگر الله نے ان کے لیے دیس نکالا نہ لکھ دیا ہوتا تو انہیں دنیا ہی میں سزا دیتا اور آخرت میں تو ان کے لیے آگ کا عذاب ہے'),
(5130, 59, 4, 'یہ ا سلیے کہ انہوں نے الله اوراس کے رسول کی مخالفت کی اور جو الله کی مخالفت کرے تو بے شک الله سخت عذاب دینے والا ہے'),
(5131, 59, 5, 'مسلمانوں تم نے جو کھجور کا پیڑ کاٹ ڈالا یا اس کو اس کی جڑوں پر کھڑا رہنے دیا یہ سب الله کے حکم سے ہوا اور تاکہ وہ نافرمانوں کو ذلیل کرے'),
(5132, 59, 6, 'اور جوکچھ الله نے اپنے رسول کو ان سے مفت دلا دیا سو تم نے اس پر گھوڑے نہیں دوڑائے اور نہ اونٹ لیکن الله اپنے رسولوں کو غالب کر دیتا ہے جس پر چاہے اور الله ہر چیز پر قادر ہے'),
(5133, 59, 7, 'جو مال الله نے اپنےرسول کو دیہات والوں سے مفت دلایا سو وہ الله اور رسول اور قرابت والوں اور یتمیموں اور مسکینوں اور مسافروں کے لیے ہے تاکہ وہ تمہارے دولتمندوں میں نہ پھرتا رہے اور جو کچھ تمہیں رسول دے اسے لے لو اور جس سے منع کرے اس سے باز رہو اور الله سے ڈرو بیشک الله سخت عذاب دینے والا ہے'),
(5134, 59, 8, 'وہ مال وطن چھوڑنے والے مفلسوں کے لیے بھی ہے جو اپنے گھروں اور مالوں سے نکالے گئے الله کا فضل اس کی رضا مندی چاہتے ہیں اوروہ الله اور اس کے رسول کی مدد کرتے ہیں یہی سچے (مسلمان) ہیں'),
(5135, 59, 9, 'اور وہ (مال) ان کے لیے بھی ہے کہ جنہوں نے ان سے پہلے (مدینہ میں) گھر اور ایمان حاصل کر رکھا ہے جو ان کے پاس وطن چھوڑ کرآتا ہے اس سے محبت کرتے ہیں اور اپنے سینوں میں اس کی نسبت کوئی خلش نہیں پاتے جو مہاجرین کو دیا جائے اور وہ اپنی جانوں پر ترجیح دیتے ہیں اگرچہ ان پر فاقہ ہو اور جو اپنے نفس کے لالچ سے بچایا جائے پس وہی لوگ کامیاب ہیں'),
(5136, 59, 10, 'اور ان کے لیے بھی جو مہاجرین کے بعد آئے (اور) دعا مانگا کرتے ہیں کہ اے ہمارے رب ہمیں اور ہمارےان بھائیوں کو بخش دے جو ہم سے پہلے ایمان لائے ہیں اور ہمارے دلوں میں ایمانداروں کی طرف سے کینہ قائم نہ ہونے پائے اے ہمارے رب بے شک تو بڑا مہربان نہایت رحم والا ہے'),
(5137, 59, 11, 'کیا آپ نے منافقوں کو نہیں دیکھا جواپنے اہل کتاب کے کافر بھائیوں سے کہتے ہیں کہ اگر تم نکالے گئے تو ضرور ہم بھی تمہارے ساتھ نکلیں گے اور تمہارے معاملہ میں کبھی کسی کی بات نہ مانیں گے اور اگر تم سے لڑائی ہو گی تو ہم تمہاری مدد کریں گے اور الله گواہی دیتا ہے کہ وہ سراسر جھوٹے ہیں'),
(5138, 59, 12, 'اگر وہ نکالے گئے تو یہ ان کے ساتھ نہ نکلیں گے اور اگر ان سے لڑائی ہوئی تو یہ ان کی مدد بھی نہ کریں گے اور اگر ان کی مدد کریں گے تو پیٹھ پھیر کر بھاگیں گے پھر ان کو مدد نہ دی جائے گی'),
(5139, 59, 13, 'البتہ تمہارا خوف ان کے دلوں میں الله (کے خوف) سے زیادہ ہے یہ اس لیے کہ وہ لوگ سمجھتے نہیں'),
(5140, 59, 14, 'وہ تم سے سب ملکر بھی نہیں لڑ سکتے مگر محفوظ بستیوں میں یا دیواروں کی آڑ میں ان کی لڑائی تو آپس میں سخت ہے آپ ان کو متفق سمجھتے ہیں حالانکہ ان کے دل الگ الگ ہیں یہ اس لیے کہ وہ لوگ عقل نہیں رکھتے'),
(5141, 59, 15, 'ان کا حال تو پہلوں جیسا ہے کہ جنہوں نے ابھی اپنے کام کی سزا پائی ہے اور ان کے لیے (آخرت میں) دردناک عذاب ہے'),
(5142, 59, 16, '(اور) مشال شیطان کی سی ہے کہ وہ آدمی سے کہتا ہے کہ تو منکر ہو جا پھر جب وہ منکر ہو جاتا ہے تو کہتا ہے بے شک میں تم سے بری ہوں کیوں کہ میں الله سے ڈرتا ہوں جو سارے جہاں کا رب ہے'),
(5143, 59, 17, 'پس ان دونوں کا انجام یہ ہوتا ہے کہ وہ دونوں دوزخ میں ہوں گے اس میں ہمیشہ رہیں گے اور ظالموں کی یہی سزا ہے'),
(5144, 59, 18, 'اے ایمان والو الله سے ڈرو اور ہر شخص کو دیکھنا چاہیئے کہ اس نے کل کے لئے کیا آگے بھیجا ہے اور الله سے ڈرو کیوں کہ الله تمہارے کاموں سے خبردار ہے'),
(5145, 59, 19, 'اور ان کی طرح نہ ہوں جنہوں نے الله کو بھلا دیاپھر الله نے بھی ان کو (ایسا کر دیا) کہ وہ اپنے آپ ہی کو بھول گئے یہی لوگ نافرمان ہیں'),
(5146, 59, 20, 'دوزخی اور جنتی برابر نہیں ہو سکتے جنتّی ہی بامراد ہیں'),
(5147, 59, 21, 'اگر ہم اس قرآن کو کسی پہاڑ پرنازل کرتے تو آپ اسے دیکھتے کہ الله کے خوف سے جھک کر پھٹ جاتا اور ہم یہ مثالیں لوگوں کے لیے بیان کرتے ہیں تاکہ وہ غور کریں'),
(5148, 59, 22, 'وہی الله ہے کہ اس کے سوا کوئی معبود نہیں سب چھپی اور کھلی باتوں کا جاننے والا ہے وہ بڑا مہربان نہایت رحم والا ہے'),
(5149, 59, 23, 'وہی الله ہے کہ اس کے سوا کوئی معبود نہیں وہ بادشاہ پاک ذات سلامتی دینے والا امن دینے والا نگہبان زبردست خرابی کا درست کرنے والا بڑی عظمت والا ہے الله پاک ہے اس سے جو اس کے شریک ٹھیراتے ہیں'),
(5150, 59, 24, 'وہی الله ہے پیدا کرنے والا ٹھیک ٹھیک بنانے والا صورت دینے والا اس کے اچھے اچھے نام ہیں سب چیزیں اسی کی تسبیح کرتی ہیں جو آسمانوں میں اور زمین میں ہیں اور وہی زبردست حکمت والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'اے ایمان والو میرے دشمنوں اور اپنے دشمنوں کو دوست نہ بناؤ کہ ان کے پاس دوستی کے پیغام بھیجتے ہو حالانکہ تمہارے پاس جو سچا دین آیا ہے اس کے یہ منکر ہو چکے ہیں رسول کو اور تمہیں ا س بات پر نکالتے ہیں کہ تم الله اپنے رب پر ایمان لائے ہو اگر تم جہاد کے لیے میری راہ میں اور میری رضا جوئی کے لیے نکلے ہوتو ان کو دوست نہ بناؤ تم ان کے پاس پوشیدہ دوستی کے پیغام بھیجتے ہو حالانکہ میں خوب جانتا ہوں جو کچھ تم مخفی اور ظاہر کرتے ہو اور جس نے تم میں سے یہ کام کیا تو وہ سیدھے راستہ سے بہک گیا'),
(5152, 60, 2, 'اگر وہ تم پر قابو پائیں تو تمہارے دشمن ہو جائیں اور تم پر اپنے ہاتھ اور اپنی زبانیں برائی سے دراز کریں اور چاہتے ہیں کہ کہیں تم کافر ہو جاؤ'),
(5153, 60, 3, 'نہ تمہیں تمہارے رشتے ناطے اور نہ تمہاری اولاد قیامت کے دن نفع دیں گے وہ تمہارے درمیان فیصلہ کرے گا اور جو تم کرتے ہو الله اسے خوب دیکھتا ہے'),
(5154, 60, 4, 'بے شک تمہارے لیے ابراہیم میں اچھا نمونہ ہے اوران لوگوں میں جو اس کے ہمراہ تھے جب کہ انہوں نے اپنی قوم سے کہا تھا بے شک ہم تم سے بیزار ہیں اور ان سے جنہیں تم الله کے سوا پوجتے ہو ہم نے تمہارا انکار کر دیا اور ہمارے او رتمہارے درمیان دشمنی اور بیَر ہمیشہ کے لیے ظاہر ہو گیا یہاں تک کہ تم ایک الله پر ایمان لاؤ مگر ابراھیم کا اپنے باپ سے کہنا کہ میں تمہارے لیے معافی مانگوں گا اور میں الله کی طرف سے تمہارے لیے کسی بات کا مالک بھی نہیں ہوں اے ہمارے رب ہم نے تجھ ہی پر بھروسہ کیا اور تیری ہی طرف ہم رجوع ہوئے اور تیری ہی طرف لوٹنا ہے'),
(5155, 60, 5, 'اے ہمارے رب ہمیں ان کا تختہ مشق نہ بنا جو کافر ہیں اور اے ہمارے رب ہمیں معاف کر بے شک تو ہی غالب حکمت والا ہے'),
(5156, 60, 6, 'البتہ تمہارے لیے ان میں ایک نیک نمونہ ہے اس کے لیے جو الله اور قیامت کے دن کی امید رکھتا ہو اور جو کوئی منہ موڑے تو بے شک الله بھی بے پرواہ خوبیوں والا ہے'),
(5157, 60, 7, 'شاید کہ الله تم میں اور ان میں کہ جن سے تمہیں دشمنی ہے دوستی قائم کر دے اور الله قادر ہے اور الله بخشنے والا نہایت رحم والا ہے'),
(5158, 60, 8, 'الله تمہیں ان لوگو ں سے منع نہیں کرتا جو تم سے دین کے بارے میں نہیں لڑتے اورنہ انہوں نے تمہیں تمہارے گھروں سے نکالا ہے اس بات سے کہ تم ان سے بھلائی کرو اور ان کے حق میں انصاف کرو بیشک الله انصاف کرنے والوں کو پسند کرتا ہے'),
(5159, 60, 9, 'تمہیں الله انہیں سے منع کرتا ہے کہ جو دین میں تم سے لڑے اورانہوں نے تمہیں تمہارے گھروں سے نکال دیا اور تمہارے نکالنے پر (لوگوں کی) مدد بھی کی کہ ان سے دوستی کرو اورجس نے ان سے دوستی کی تو پھر وہی ظالم بھی ہیں'),
(5160, 60, 10, 'اے ایمان والو جب تمہارے پاس مومن عورتیں ہجرت کر کے آئيں تو ان کی جانچ کر لو الله ہی ان کے ایمان کو خوب جانتا ہے پس اگر تم انہیں مومن معلوم کر لو تو انہیں کفار کی طرف نہ لوٹاؤ نہ وہ (عورتیں) ان کے لیے حلال ہیں اور نہ وہ (کافر) ان کے لیے حلال ہیں اور ان کفار کو دے دو جو کچھ انہوں نے خرچ کیا اور تم پر گناہ نہیں کہ تم ان سے نکاح کر لو جب تم انہیں ان کے مہر دے دو اور کافر عورتوں کے ناموس کو قبضہ میں نہ رکھو اور جو تم نےان عورتوں پر خرچ کیا تھا مانگ لو اور جو انہوں نے خرچ کیا کہ وہ مانگ لیں الله کا یہی حکم ہے جو تمہارے لیے صاد رفرمایا اور الله سب کچھ جاننے والا حکمت والا ہے'),
(5161, 60, 11, 'اور اگرکوئی عورت تمہاری عورتوں میں سے کفار کے پاس نکل گئی ہے پھر تمہاری باری آجائے تو ان مسلمانو ں کو دے دو جن کی بیویاں چلی گئی ہیں جتنا کہ انہوں نے دیا تھا اوراس الله سے ڈرو کہ جس پر تم ایمان لائے ہو'),
(5162, 60, 12, 'اے نبی جب آپ کے پاس ایمان والی عورتیں اس بات پر بیعت کرنے کو آئیں کہ الله کے ساتھ کسی کو شریک نہ کرینگی اور نہ چوری کریں گی اور نہ زنا کریں گی اور نہ اپنی اولاد کو قتل کریں گی اور نہ بہتان کی اولاد لائیں گی جسے اپنے ہاتھوں اور پاؤں کے درمیان (نطفہٴ شوہر سے جنی ہوئی) بنا لیں اور نہ کسی نیک بات میں آپ کی نافرمانی کریں گی تو ان کی بیعت قبول کر اور ان کے لیے الله سے بخشش مانگ بے شک الله بخشنے والا نہایت رحم والا ہے'),
(5163, 60, 13, 'اے ایمان والو اس قوم سے دوستی نہ کرو جن پر الله کا غضب ہوا وہ تو آخرت سے ایسے نا امید ہو گئے کہ جیسے کافر اہلِ قبور سے نا امید ہو گئے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'جو مخلوقات آسمانو ں میں اور جو زمین میں ہے الله کی تسبیح کرتی ہے اور وہی غالب حکمت والا ہے'),
(5165, 61, 2, 'اے ایمان والو کیو ں کہتے ہو جو تم کرتے نہیں'),
(5166, 61, 3, 'الله کے نزدیک بڑی ناپسند بات ہے جو کہو اس کو کرو نہیں'),
(5167, 61, 4, 'بے شک الله تو ان کو پسند کرتا ہے جو اس کی راہ میں صف باندھ کر لڑتے ہیں گویا وہ سیسہ پلائی ہوئی دیوار ہے'),
(5168, 61, 5, 'اور جب موسیٰ نے اپنی قوم سے کہا کہ اے میری قوم مجھےکیوں ستاتے ہو حالانکہ تم جانتے ہو کہ میں تمہاری طرف الله کا رسول ہوں پس جب وہ پھر گئے تو الله نے ان کے دل پھیر دیئے اور الله نافرمان لوگو ں کو ہدایت نہیں کرتا'),
(5169, 61, 6, 'اور جب عیسیٰ بن مریم نے کہا اے بنی اسرائیل بے شک میں الله کا تمہاری طرف سے رسول ہوں تورات جو مجھ سے پہلے ہے اس کی تصدیق کرنے والا ہوں اور ایک رسول کی خوشخبری دینے والا ہوں جو میرے بعد آئے گا اس کا نام احمد ہو گا پس جب وہ واضح دلیلیں لے کر ان کے پاس آ گیا تو کہنے لگے یہ تو صریح جادو ہے'),
(5170, 61, 7, 'اور اس سے زیادہ کون ظالم ہے جو الله پر جھوٹ باندھے حالانکہ اسلام کی طرف اسے بلایا جارہا ہو اور ظالم لوگوں کو ہدایت نہیں کرتا'),
(5171, 61, 8, 'وہ چاہتے ہیں کہ الله کا نور اپنے مونہوں سے بجھا دیں اور الله اپنا نور پورا کر کے رہے گا اگرچہ کافر برا مانیں'),
(5172, 61, 9, 'وہی تو ہے جس نے اپنا رسول ہدایت اور سچا دین دے کر بھیجا تاکہ اس کو سب دینوں پر غالب کرے اگرچہ مشرک نا پسند کریں'),
(5173, 61, 10, 'اے ایمان والو کیا میں تمہیں ایسی تجارت بتاؤں جو تمہیں دردناک عذاب سے نجات دے'),
(5174, 61, 11, 'تم الله اور اس کے رسول پر ایمان لاؤ اور تم الله کی راہ میں اپنے مالوں اور اپنی جانوں سے جہاد کرو یہی تمہارے لیے بہتر ہے اگر تم جانتے ہو'),
(5175, 61, 12, 'وہ تمہارے لیے تمہارے گناہ بخش دے گا اور تمہیں بہشتوں میں داخل کر ے گا جن کے نیچے نہریں بہہ رہی ہوں گی اور پاکیزہ مکانوں میں ہمیشہ رہنے کے باغو ں میں یہ بڑی کامیابی ہے'),
(5176, 61, 13, 'اور دوسری بات جو تم پسند کرتے ہو الله کی طرف سے مدد ہے اور جلدی فتح اور ایمان والوں کو خوشخبری دے دے'),
(5177, 61, 14, 'ایمان والو الله کے مددگار ہو جاؤ جیسا کہ ابن مریم نے حواریوں سے کہا تھا کہ الله کی راہ میں میرا مددگار کون ہے حواریو ں نے کہا ہم الله کے مددگار ہیں پھر ایک گروہ بنی اسرائیل کا ایمان لایا اور ایک گروہ کافر ہو گیا پھر ہم نے ایمان داروں کو ان کے دشمنوں پر غالب کر دیا پھر تو وہی غالب ہو کر رہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'جو مخلوقات آسمانوں اور زمین میں ہے الله کی تسبیح کرتی ہے وہ بادشاہ پاک ذات غالب حکمت والا ہے'),
(5179, 62, 2, 'وہی ہے جس نے ان پڑھوں میں ایک رسول انہیں میں سے مبعوث فرمایا جو ان پر اس کی آیتیں پڑھتا ہے اور انہیں پاک کرتا ہے اور انہیں کتاب اور حکمت سکھاتا ہے اور بے شک وہ اس سے پہلے صریح گمراہی میں تھے'),
(5180, 62, 3, 'اور دوسروں کے لیے بھی جو ابھی ان سے نہیں ملے اور وہ زبردست حکمت والا ہے'),
(5181, 62, 4, 'یہ الله کا فضل ہے جسے چاہے دے اور الله بڑا فضل کرنے والا ہے'),
(5182, 62, 5, 'ان لوگو ں کی مثال جنہیں تورات اٹھوائی گئی پھر انہوں نے اسے نہ اٹھایا گدھے کی سی مثال ہے جو کتابیں اٹھاتا ہے ان لوگوں کی بہت بری مثال ہے جنہوں نے الله کی آیتو ں کو جھٹلایا اور الله ظالم لوگوں کو ہدایت نہیں کرتا'),
(5183, 62, 6, 'کہہ دو اے لوگو جو یہودی ہو اگر تم خیال کرتے ہو کہ تم ہی الله کے دوست ہو سوائے دوسرے لوگوں کے تو موت کی آرزو کرو اگر تم سچے ہو'),
(5184, 62, 7, 'اور وہ لوگ اس کی کبھی بھی تمنا نہ کریں گے بسبب ان (عملوں) کے جو ان کے ہاتھوں نے آگے بھیجے اور الله ظالموں کو خوب جانتا ہے'),
(5185, 62, 8, 'کہہ دو بے شک وہ موت جس سے تم بھاگتے ہو سو وہ تو ضرور تمہیں ملنے والی ہے پھر تم اس کی طرف لوٹائے جاؤ گے جو ہر چھپی اور کھلی بات کا جاننے والا ہے پھر وہ تمہیں بتائے گا جو کچھ تم کیا کرتے تھے'),
(5186, 62, 9, 'اے ایمان والو جب جمعہ کے دن نماز کے لیے اذان دی جائے تو ذکر الہیٰ کی طرف لپکو اور خرید و فروخت چھوڑ دو تمہارے لیے یہی بات بہتر ہے اگر تم علم رکھتے ہو'),
(5187, 62, 10, 'پس جب نماز ادا ہو چکے تو زمین میں چلو پھرو اور الله کا فضل تلاش کرو اور الله کو بہت یاد کرو تاکہ تم فلاح پاؤ'),
(5188, 62, 11, 'اور جب وہ لوگ تجارت یاتماشہ دیکھتے ہیں تو اس پر ٹوٹ پڑتے ہیں اور آپ کو کھڑا ہوا چھوڑ جاتے ہیں کہہ دو جو الله کے پاس ہے وہ تماشا اور تجارت سے کہیں بہتر ہے اور الله بہتر روزی دینے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'جب آپ کے پاس منافق آتے ہیں تو کہتے ہیں ہم گواہی دیتے ہیں کہ بے شک آپ الله کے رسول ہیں اور الله جانتا ہے کہ بے شک آپ اس کے رسول ہیں اور الله گواہی دیتا ہے کہ بے شک منافق جھوٹے ہیں'),
(5190, 63, 2, 'انہوں نے اپنی قسموں کو ڈھال بنا کر رکھا ہے پھر (لوگوں کو) الله کی راہ سے روکتے ہیں بے شک کیسا برا کام ہے جو وہ کر رہے ہیں'),
(5191, 63, 3, 'یہ اس لیے کہ وہ ایمان لائے پھر منکر ہو گئے پس ان کے دلوں پر مہر کر دی گئی ہے پس وہ نہیں سمجھتے'),
(5192, 63, 4, 'اور جب آپ ان کو دیکھیں تو اپ کو ان کے ڈیل ڈول اچھے لگیں اور اگر وہ بات کریں تو آپ انکی بات سن لیں گویا کہ وہ دیوار سے لگی ہوئی لکڑیاں ہیں وہ ہر آواز کو اپنے ہی اُوپر خیال کرتے ہیں وہی دشمن ہیں پس ان سے ہوشیار رہيے الله انہیں غارت کرے کہاں وہ بہکے جا رہے ہیں'),
(5193, 63, 5, 'اور جب ان سے کہا جائے کہ آؤ تمہارے لیے رسول الله مغفرت طلب کریں تو اپنے سر پھیر لیتے ہیں اور آپ انہیں دیکھیں گے کہ وہ رکتے ہیں ایسے حال میں کہ وہ تکبر کرنے والے ہیں'),
(5194, 63, 6, 'برابر ہے خواہ وہ آپ ان کے لیے معافی مانگیں یا نہ مانگیں اللهانہیں ہر گز نہیں بخشے گا بے شک الله بدکار قوم کو ہدایت نہیں کرتا'),
(5195, 63, 7, 'وہی لوگ ہیں جوکہتے ہیں کہ ان پر خرچ نہ کرو جو رسول الله کے پاس ہیں یہاں تک کہ وہ تتر بتر ہو جائیں اور آسمانوں اور زمین کے خزانے الله ہی کے لیے ہیں لیکن منافق نہیں سمجھتے'),
(5196, 63, 8, 'وہ کہتے ہیں کہ اگر ہم مدینہ کی طرف لوٹ کر گئے تو اس میں سے عزت والا ذلیل کو ضرور نکال دے گا اور عزت تو الله اوراس کے رسول اور مومنین ہی کے لیے ہے لیکن منافق نہیں جانتے'),
(5197, 63, 9, 'اے ایمان والو تمہیں تمہارے مال اور تمہاری اولاد الله کے ذکر سے غافل نہ کر دیں اور جو کوئی ایسا کرے گا سووہی نقصان اٹھانے والے ہیں'),
(5198, 63, 10, 'اوراس میں سے خرچ کرو جو ہم نے تمہیں روزی دی ہے اس سے پہلے کہ کسی کو تم میں سے موت آجائے تو کہے اے میرے رب تو نے مجھے تھوڑی مدت کے لیے ڈھیل کیوں نہ دی کہ میں خیرات کرتا اور نیک لوگو ں میں ہو جاتا'),
(5199, 63, 11, 'اور الله کسی نفس کو ہرگز مہلت نہیں دے گا جب اس کی اجل آجائے گی اور الله اس سے خبردار ہے جو تم کرتے ہو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'جو مخلوقات آسمانوں میں اور جو زمین میں ہے الله کی تسبیح کرتی ہے اسی کی حکومت ہے اور اس کی تعریف ہے اوروہ ہر چیز پر قادر ہے'),
(5201, 64, 2, 'اسی نے تو تمہیں پیدا کیا ہے پھر کوئی تم میں سے کافر ہے اور کوئی مومن اورجو کچھ تم کر رہے ہو الله دیکھ رہا ہے'),
(5202, 64, 3, 'اسی نے آسمانوں اور زمین کو ٹھیک طور پر بنایا ہے اور تمہاری صورت بنائی پھر تمہاری صورتیں اچھی بنائیں اور اسی کی طرف لوٹ کر جانا ہے'),
(5203, 64, 4, 'وہ جانتا ہے جو کچھ آسمانوں اور زمین میں ہے اور وہ جانتاہے جو تم چھپاتے ہو اور جو تم ظاہر کرتے ہو اور الله ہی سینوں کے بھید جانتا ہے'),
(5204, 64, 5, 'کیا تمہارے ہاں ان کی خبر نہیں پہنچی جو اس ے پہلے منکر ہوئے پس انہوں نے اپنے کام کا وبال چکھا اوران کے لیے دردناک عذاب ہے'),
(5205, 64, 6, 'یہ اس لیے کہ ان کے پاس ان کے رسول واضح دلیلیں لے کر آتے تھے تووہ کہا کرتے تھے کہ کیا آدمی ہم کو راہ دکھائیں گے پس انہوں نے انکار کیا اورمنہ موڑ لیا اور الله نے پروا نہ کی اور الله بے نیاز تعریف کیا ہوا ہے'),
(5206, 64, 7, 'کافروں نے سمجھ لیا کہ قبروں سے اٹھائے نہ جائیں گے کہہ دوکیوں نہیں مجھے اپنے رب کی قسم ہے ضرور اٹھائے جاؤ گے پھر تمہیں بتلایا جائے گا جو کچھ تم نے کیا تھا اور یہ بات الله پر آسان ہے'),
(5207, 64, 8, 'پس الله اوراس کے رسول پر ایمان لاؤ اور اس نور پر جو ہم نے نازل کیا ہے اور الله اس سے جو تم کرتے ہو خبردار ہے'),
(5208, 64, 9, 'جس دن تمہیں جمع ہونے کے دن جمع کرے گا وہ دن ہار جیت کا ہے اور جو کوئی الله پر ایمان لائے اور نیک عمل کرے الله اس سے اس کی برائیاں دور کر دے گا اور اسے بہشتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہوں گی ان میں ہمیشہ رہیں گے یہی بڑی کامیابی ہے'),
(5209, 64, 10, 'اورجنہوں نے انکار کیا اور ہماری آیتوں کو جھٹلایا یہی لوگ دوزخی ہیں اس میں ہمیشہ رہیں گے اوروہ بری جگہ ہے'),
(5210, 64, 11, 'الله کے حکم کے بغیر کوئی مصیبت بھی نہیں آتی اور جو الله پر ایمان رکھتا ہے وہ اس کے دل کو ہدایت دیتا ہے اور الله ہر چیز جاننے والا ہے'),
(5211, 64, 12, 'اور الله اور اس کے رسول کی فرمانبرداری کرو پھر اگر تم نے منہ موڑ لیا تو ہمارے رسول پر بھی صرف کھول کر ہی پہنچا دینا ہے'),
(5212, 64, 13, 'الله ہی ہے اس کے سوا کوئی معبود نہیں اور الله ہی پر ایمانداروں کو بھروسہ رکھنا چاہیئے'),
(5213, 64, 14, 'اے ایمان والو بے شک تمہاری بیویوں اور اولاد میں سے بعض تمہارے دشمن بھی ہیں سو ان سے بچتے رہو اور اگرتم معاف کرو اور درگزر کرو ارو بخش دو تو الله بھی بخشنے واا نہایت رحم والا ہے'),
(5214, 64, 15, 'تمہارے مال اور اولاد تمہارے لیے محض آزمائش ہیں اور الله کے پاس تو بڑا اجر ہے'),
(5215, 64, 16, 'پس جہاں تک تم سے ہو سکے الله سے ڈرو اور سنو اور حکم مانو اور اپنے بھلے کے لیے خرچ کرو اورجو شخص اپنے دل کے لالچ سے محفوظ رکھا گیا سووہی فلاح بھی پانے والے ہیں'),
(5216, 64, 17, 'اگر تم الله کو نیک قرض دو تو وہ اسے تمہارے لیے دگنا کر دے گا اور تمہیں بخش دے گا اور الله بڑا قدردان حلم والا ہے'),
(5217, 64, 18, 'سب چھپی اورکھلی کا جاننے والا غالب حکمت والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اے نبی جب تم عورتوں کو طلاق دو تو ان کی عدت کے موقع پر طلاق دو اور عدت گنتے رہو اور الله سے ڈرتے رہو جو تمہارا رب ہے نہ تم ہی ان کو ان کے گھروں سے نکالو اور نہ وہ خود ہی نکلیں مگر جب کھلم کھلا کوئی بے حیائی کا کام کریں اور یہ الله کی حدیں ہیں اور جو الله کی حدوں سے بڑھاا تو اس نے اپنے نفس پر ظلم کیا آپ کو کیا معلوم کہ شاید الله اس کے بعد اور کوئی نئی بات پیدا کر دے'),
(5219, 65, 2, 'پس جب وہ اپنی عدت کو پہنچ جائیں تو انہیں دستور سے رکھ لو یا انہیں دستور سے چھوڑ دو اور دو معتبر آدمی اپنے میں سے گواہ کر لو اور الله کے لیے گواہی پوری دو یہ نصیحت کی باتیں انہیں سمجھائی جاتی ہیں جو الله اور قیامت پر ایمان رکھتے ہیں اور جو الله سے ڈرتا ہے الله اس کے لیے نجات کی صورت نکال دیتا ہے'),
(5220, 65, 3, 'اور اسے رزق دیتا ہے جہاں سے اسے گمان بھی نہ ہو اور جو الله پر بھروسہ کرتا ہے سو وہی اس کو کافی ہے بے شک الله اپنا حکم پورا کرنے والا ہے الله نے ہر چیز کے لیے ایک اندازہ مقرر کر دیا ہے'),
(5221, 65, 4, 'اور تمہاری عورتوں میں سے جن کو حیض کی امید نہیں رہی ہے اگر تمہیں شبہ ہو تو ان کی عدت تین مہینے ہیں اور ان کی بھی جنہیں ابھی حیض نہیں آیا اور حمل والیوں کی عدت ان کے بچہ جننے تک ہے اور جو الله سے ڈرتا ہے وہ اس کے کام آسان کر دیتا ہے'),
(5222, 65, 5, 'یہ الله کا حکم ہے جو اس نے تمہاری طرف نازل کیا ہے اور جو الله سے ڈرتا ہے وہ اس سے اس کی برائیاں دور کر دیتا ہے اور اسے بڑااجر بھی دیتا ہے'),
(5223, 65, 6, 'طلاق دی ہوئی عورتوں کو وہیں رکھو جہاں تم اپنے مقدور کے موافق رہتے ہو اورانہیں ایذا نہ دو انہیں تنگ کرنے کے لیے اور اگر وہ حاملہ ہوں تو انہیں نان ونفقہ دو جب تک وہ وضع حمل کریں پس اگر پلائيں دودھ تمہارے لیے تو دو ان کو ان کی اجرت دو اور آپس میں دستور کے مطابق مشورہ کر لو اور اگر تم آپس میں تنگی کرو تو اس کے لیے دوسری عورت دودھ پلائے گی'),
(5224, 65, 7, 'مقدور والا اپنے مقدور کے موافق خرچ کرے اور اگر تنگ دست ہو تو جو کچھ الله نے اسے دیا ہے اس میں سے خرچ کرے الله کسی کو تکلیف نہیں دیتا مگر اسی قدر جو اسے دے رکھا ہے عنقریب الله تنگی کے بعد آسانی کر دے گا'),
(5225, 65, 8, 'اور کتنی ہی بستیاں اپنے رب اوراس کے رسولوں کے حکم سے سر کش ہوگئیں پھر ہم نے بھی ان سے سخت حساب لیا اور ان کو بری سزا دی'),
(5226, 65, 9, 'پس ان بستیوں نے اپنے کام کا وبال چکھا اور ان کا انجام کار بربادی ہوئی'),
(5227, 65, 10, 'الله نے ان کے لیے سخت عذاب تیار کر رکھا ہے پھر اے عقل والو الله سے ڈرتے رہو جو ایمان لا چکے ہو بے شک الله نے تمہاری طرف نصیحت نازل کی ہے'),
(5228, 65, 11, 'یعنی ایک رسول جو تمہیں الله کی واضح آیتیں پڑھ کر سناتا ہے تاکہ جو ایمان لائے اورانہوں نے نیک کام بھی کیے انہیں اندھیروں سے نکال کر روشنی میں لے جائے اورجو الله پر ایمان لائے اور اس نے نیک کام بھی کیے تو اسے ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی وہ ان میں سدا رہیں گے الله نے اس کو بہت اچھی روزی دی ہے'),
(5229, 65, 12, 'الله ہی ہے جس نے سات آسمان پیدا کیے اور زمینیں بھی اتنی ہی ان میں حکم نازل ہوا کرتا ہے تاکہ تم جان لو کہ الله ہر چیز پر قادر ہے اور الله نے ہر چیز کو علم سے احاطہ کر رکھا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'اے نبی آپ کیوں حرام کرتے ہیں جو الله نے آپ کے لیے حلال کیا ہے آپ اپنی بیویوں کی خوشنودی چاہتے ہیں اور الله بخشنے والا نہایت رحم والا ہے'),
(5231, 66, 2, 'الله نے تمہارے لیے اپنی قسموں کا توڑ دینا فرض کر دیا ہے اور الله ہی تمہارا مالک ہے اوروہی سب کا جاننے والا حکمت والا ہے'),
(5232, 66, 3, 'اور جب نبی نے چھپا کر اپنی کسی بیوی سے ایک بات کہہ دی اور پھر جب اس بیوی نے وہ بات بتا دی اور الله نے اس کو نبی پر ظاہر کر دیا تو نبی نے اس میں سے کچھ بات جتلا دی اور کچھ ٹال دی پس جب پیغمبر نے اس کو وہ بات جتلا دی تو بولی آپ کو کس نے یہ بات بتا دی آپ نے فرمایا مجھے خدائے علیم و خبیر نے یہ بات بتلائی'),
(5233, 66, 4, 'اگر تم دونوں الله کی جناب میں توبہ کرو تو (بہتر) ورنہ تمہارے دل تو مائل ہو ہی چکے ہیں اوراگر تم آپ کے خلاف ایک دوسرے کی مدد کرو گی تو بے شک اللهآپ کا مددگار ہے اور جبرائیل اور نیک بخت ایمان والے بھی اور سب فرشتے اس کے بعد آپ کے حامی ہیں'),
(5234, 66, 5, 'اگر نبی تمیں طلاق دے دے تو بہت جلد اس کا رب اس کے بدلے میں تم سے اچھی بیویاں دے دے گا فرمانبردار ایمان والیاں نمازی توبہ کرنے والی عبادت گزار روزہ دار بیوائیں اور کنواریاں'),
(5235, 66, 6, 'اے ایمان والو اپنے آپ کو اور اپنے گھر والوں کو دوزخ سے بچاؤ جس کا ایندھن آدمی اور پتھر ہیں اس پر فرشتے سخت دل قوی ہیکل مقرر ہیں وہ الله کی نافرمانی نہیں کرتے جو وہ انہیں حکم دے اور وہی کرتے ہیں جو انہیں حکم دیا جاتا ہے'),
(5236, 66, 7, 'اے کافرو آج بہانے نہ بناؤ تمہیں وہی بدلہ دیا جائے گا جو تم کیا کرتے تھے'),
(5237, 66, 8, 'اے ایمان والو الله کے سامنے خالص توبہ کرو کچھ بعید نہیں کہ تمہارا رب تم سے تمہارے گناہ دور کر دے اور تمہیں بہشتوں میں داخل کرے جن کے نیچے نہریں بہتی ہوں گی جس دن الله اپنے نبی کو اور ان کو جو اس کے ساتھ ایمان لائے رسوا نہیں کرے گا ان کا نور ان کے آگے اور ان کے دائیں دوڑ رہا ہوگا وہ کہہ رہے ہوں گے اے ہمارے رب ہمارے لیے ہمارا نور پورا کر اور ہمیں بخش دے بے شک تو ہر چیز پر قادر ہے'),
(5238, 66, 9, 'اے نبی کافروں اور منافقوں سے جہاد کر اور ان پر سختی کر اور انکا ٹھکانا دوزخ ہے اور وہ بہت ہی بری جگہ ہے'),
(5239, 66, 10, 'الله کافرو ں کے لیے ایک مثال بیان کرتا ہے نوح اور لوط کی بیوی کی وہ ہمارے دو نیک بندوں کے نکاح میں تھیں پھر ان دونوں نے ان کی خیانت کی سو وہ الله کے غضب سے بچانے میں ان کے کچھ بھی کام نہ آئے اور کہا جائے گا دونوں دوزخ میں داخل ہونے والوں کے ساتھ داخل ہو جاؤ'),
(5240, 66, 11, 'اور الله ایمان داروں کے لیے فرعون کی بیوی کی مثال بیان کرتا ہے جب اس نے کہاکہ اے میرے رب میرے لیے اپنے پاس جنت میں ایک گھر بنا اور مجھے فرعون اور اس کے کام سے نجات دے اور مجھے ظالموں کی قوم سے نجات دے'),
(5241, 66, 12, 'اور مریم عمران کی بیٹی (کی مثال بیان کرتا ہے) جس نے اپنی عصمت کو محفوظ رکھا پھر ہم نے اس میں اپنی طرف سے روح پھونکی اور اس نے اپنے رب کی باتوں کو اور اس کی کتابوں کو سچ جانا اوروہ عبادت کرنے والو ں میں سے تھی');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'وہ ذات با برکت ہے جس کے ہاتھ میں سب حکومت ہے اور وہ ہر چیز پر قادر ہے'),
(5243, 67, 2, 'جس نے موت اور زندگی کو پیدا کیا تاکہ تمہیں آزمائيں کہ تم میں کس کے کام اچھے ہیں اور وہ غالب بخشنے والا ہے'),
(5244, 67, 3, 'جس نے سات آسمان اوپر تلے بنائے تو رحمان کی اس صنعت میں کوئی خلل نہ دیکھے گا تو پھر نگاہ دوڑا کیا تجھے کوئی شگاف دکھائی دیتا ہے'),
(5245, 67, 4, 'پھر دوبارہ نگاہ کر تیری طرف نگاہ ناکام لوٹ آئے گی اور وہ تھکی ہوئی ہو گی'),
(5246, 67, 5, 'اور ہم نے دنیا کے آسمان کو چراغوں سے آراستہ کیا ہے اور ہم نے انہیں شیطانوں کو مارنے کے لیے آلہ بنا دیا ہے اور ہم نے ان کے لیے بھڑکتی آگ کا عذاب تیار کر رکھا ہے'),
(5247, 67, 6, 'اور جنہوں نے اپنے رب کا انکار کیا ہے ان کے لیے جہنم کا عذاب ہے اور وہ بہت ہی بری جگہ ہے'),
(5248, 67, 7, 'جب اس میں ڈالے جائیں گے تو اس کے شور کی آواز سنیں گے اور وہ جوش مارتی ہوگی'),
(5249, 67, 8, 'ایسا معلوم ہو گا کہ جوش کی وجہ سے ابھی پھٹ پڑے گی جب اس میں ایک گروہ ڈالا جائے گا تو ان سے دوزخ کے داروغہ پوچھیں گے کیاتمہارے پاس کوئی ڈرانے والا نہیں آیا تھا'),
(5250, 67, 9, 'وہ کہیں گے ہاں بے شک ہمارے پاس ڈرنے والا آیا تھا پر ہم نے جھٹلا دیا اور کہہ دیا کہ الله نے کچھ بھی نازل نہیں کیا تم خود بڑی گمراہی میں پڑے ہوئے ہو'),
(5251, 67, 10, 'اور کہیں گے کہ اگر ہم نے سنا یا سمجھا ہوتا تو ہم دوزخیوں میں نہ ہوتے'),
(5252, 67, 11, 'پھر وہ اپنے گناہ کا اقرار کریں گے سو دوزخيوں پر پھٹکار ہے'),
(5253, 67, 12, 'بے شک جو لوگ اپنے رب سے بن دیکھے ڈرتے ہیں ان کے لیے بخشش اور بڑا اجر ہے'),
(5254, 67, 13, 'اور تم اپنی بات کو چھپاؤ یا ظاہر کرو بے شک وہ سینوں کے بھید خوب جانتا ہے'),
(5255, 67, 14, 'بھلا وہ نہیں جانتا جس نے (سب کو) پیدا کیا وہ بڑا باریک بین خبردار ہے'),
(5256, 67, 15, 'وہی تو ہے جس نے تمہارے لیے زمین کو نرم کر دیا سو تم اس کے راستوں میں چلو پھر اور الله کے رزق میں سے کھاؤ اور اسی کے پاس پھر کر جانا ہے'),
(5257, 67, 16, 'کیا تم اس سے ڈرتے نہیں جو آسمان میں ہے کہ وہ تمہیں زمین میں دھنسا دے پس یکایک وہ لرزنے لگے'),
(5258, 67, 17, 'کیا تم اس سے نڈر ہو گئے ہو جو آسمان میں ہے وہ تم پر پتھر برسا دے پھر تمہیں معلوم ہو جائے گا کہ میرا ڈرانا کیسا ہے'),
(5259, 67, 18, 'اور ان سے پہلے لوگ بھی جھٹلا چکے ہیں پھر ہماری ناراضگی کاکیا نتیجہ ہوا'),
(5260, 67, 19, 'اور کیا انہوں نے اپنے اوپر پرندوں کو پر کھولتے اور سکیڑتے ہوئے نہیں دیکھا جنہیں رحمان کے سوا کوئی نہیں تھام رہا بے شک وہ ہر چیز کو دیکھ رہاہے'),
(5261, 67, 20, 'بھلا وہ تمہارا کون سا لشکر ہے جو رحمنٰ کے مقابلہ میں تمہاری مدد کرے گا کچھ نہیں کافر تو دھوکے میں پڑے ہوئے ہیں'),
(5262, 67, 21, 'بھلا وہ کون ہے جو تم کو روزی دے گا اگر وہ اپنی روزی بند کر لے کچھ نہیں بلکہ وہ سرکشی اور نفرت میں اڑے بیٹھے ہیں'),
(5263, 67, 22, 'پس کیا وہ شخص جو اپنے منہ کے بل اوندھا چلتا ہے وہ زیادہ راہِ راست پر ہے یا وہ جو سیدھے راستے پر سیدھا چلا جاتا ہے'),
(5264, 67, 23, 'کہہ دو اسی نے تم کو پیدا کیا ہے اور تمہارے لیے کان اور آنکھ اور دل بھی بنائے ہیں (مگر) تم بہت ہی کم شکر کرتے ہو'),
(5265, 67, 24, 'کہہ دو اسی نے تمہیں زمین میں پھیلایا ہے اور اسی کے پاس جمع کر کے لائے جاؤ گے'),
(5266, 67, 25, 'اور وہ کہتے ہیں کہ یہ وعدہ کب ہو گا اگر تم سچے ہو'),
(5267, 67, 26, 'کہہ دو اس کی خبر تو الله ہی کو ہے اور میں تو صاف صاف ڈرانے والا ہوں'),
(5268, 67, 27, 'پھر جب وہ اسے قریب دیکھیں گے تو ان کی صورتیں بگڑ جائیں گی جو کافر ہیں اور کہا جائے گا یہ وہی ہے جسے تم دنیا میں مانگا کرتے تھے'),
(5269, 67, 28, 'کہہ دو بھلادیکھو تو سہی اگر الله مجھے اور میرے ساتھ والوں کو ہلاک کرے یا ہم پر رحم کرے پھر وہ کون ہے جو منکروں کو دردناک عذاب سے بچا سکے'),
(5270, 67, 29, 'کہہ دو وہی رحمنٰ ہے ہم اس پر ایمان لائے اور اسی پر ہم نے بھروسہ بھی کررکھا پس عنقریب تم جان لو گے کون صریح گمراہی میں ہے'),
(5271, 67, 30, 'کہہ دو بھلا دیکھو تو سہی اگر تمہارا پانی خشک ہو جائے تو وہ کون ہے جو تمہارے پاس صاف پانی لے آئے گا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نۤ قلم کی قسم ہے اور اس کی جو اس سے لکھتے ہیں'),
(5273, 68, 2, 'آپ الله کے فضل سے دیوانہ نہیں ہیں'),
(5274, 68, 3, 'اور آپ کے لیے تو بے شمار اجر ہے'),
(5275, 68, 4, 'اور بے شک آپ تو بڑے ہی خوش خلق ہیں'),
(5276, 68, 5, 'پس عنقریب آپ بھی دیکھ لیں گے اور وہ بھی دیکھ لیں گے'),
(5277, 68, 6, 'کہ تم میں سے کون دیوانہ ہے'),
(5278, 68, 7, 'بے شک آپ کا رب ہی خوب جانتا ہے کہ کون اس کی راہ سے بہکا ہے اور وہ ہدایت پانے والوں کو بھی خوب جانتا ہے'),
(5279, 68, 8, 'پس آپ جھٹلانےوالوں کا کہا نہ مانیں'),
(5280, 68, 9, 'وہ تو چاہتے ہیں کہ کہیں آپ نرمی کریں تو وہ بھی نرمی کریں'),
(5281, 68, 10, 'اور ہر قسمیں کھانے والے ذلیل کا کہا نہ مان'),
(5282, 68, 11, 'جو طعنے دینے والا چغلی کھانے والا ہے'),
(5283, 68, 12, 'نیکی سے روکنے والا حد سے بڑھاہوا گناہگار ہے'),
(5284, 68, 13, 'بڑا اجڈ اس کے بعد بد اصل بھی ہے'),
(5285, 68, 14, 'اس لئے کہ وہ مال اور اولاد والا ہے'),
(5286, 68, 15, 'جب اس پر ہماری آیتیں پڑھی جاتی ہیں تو کہتا ہے پہلوں کی کہانیاں ہیں'),
(5287, 68, 16, 'عنقریب ہم اس کی ناک پر داغ لگائیں گے'),
(5288, 68, 17, 'بے شک ہم نے ان کو آزمایا ہے جیسا کہ ہم نے باغ والوں کو آزمایا تھا جب انہوں نے قسم کھائی تھی کہ وہ ضرور صبح ہوتے ہی اس کا پھل توڑ لیں گے'),
(5289, 68, 18, 'اور انشاالله بھی نہ کہا تھا'),
(5290, 68, 19, 'پھر تو اس پر رات ہی میں آپ کے رب کی طرف سے ایک جھونکا چل گیا درآنحالیکہ وہ سونے والے تھے'),
(5291, 68, 20, 'پھر وہ کٹی ہوئی کھیتی کی طرح ہو گیا'),
(5292, 68, 21, 'پھر وہ صبح کو پکارنے لگے'),
(5293, 68, 22, 'کہ اپنے کھیت پر سویرے چلو اگر تم نے پھل توڑنا ہے'),
(5294, 68, 23, 'پھر وہ آپس میں چپکے چپکے یہ کہتے ہوئے چلے'),
(5295, 68, 24, 'کہ تمہارے باغ میں آج کوئی محتاج نہ آنے پائے'),
(5296, 68, 25, 'اور وہ سویرے ہی بڑے اہتمام سے پھل توڑنے کی قدرت کا خیال کر کے چل پڑے'),
(5297, 68, 26, 'پس جب انہوں نے اسے دیکھا تو کہنے لگےکہ ہم تو راہ بھول گئے ہیں'),
(5298, 68, 27, 'بلکہ ہم تو بدنصیب ہیں'),
(5299, 68, 28, 'پھر ان میں سے اچھے آدمی نے کہا کیامیں نے تمہیں نہیں کہا تھا کہ تم کس لیے تسبیح نہیں کرتے'),
(5300, 68, 29, 'انہوں نے کہا ہمارا رب پاک ہے بے شک ہم ظالم تھے'),
(5301, 68, 30, 'پھر ایک دوسرے کی طرف متوجہ ہو کر آپس میں ملامت کرنے لگے'),
(5302, 68, 31, 'انہوں نے کہا ہائے افسوس بے شک ہم سرکش تھے'),
(5303, 68, 32, 'شاید ہمارا رب ہمارے لیے اس سے بہتر باغ بدل دے بے شک ہم اپنے رب کی طرف رجوع کرنے والے ہیں'),
(5304, 68, 33, 'عذاب یونہی ہوا کرتا ہے اور البتہ آخرت کا عذاب تو کہیں بڑھ کر ہے کاش وہ جانتے'),
(5305, 68, 34, 'بے شک پرہیزگاروں کے لیے ان کے رب کے ہاں نعمت کے باغ ہیں'),
(5306, 68, 35, 'پس کیا ہم فرمانبرداروں کو مجرموں کی طرح کر دیں گے'),
(5307, 68, 36, 'تمہیں کیا ہوگیا کیسا فیصلہ کر رہے ہو'),
(5308, 68, 37, 'کیا تمہارے پاس کوئی کتاب ہے جس میں تم پڑھتے ہو'),
(5309, 68, 38, 'کہ بے شک تمہیں آخرت میں ملے گا جو تم پسند کرتے ہو'),
(5310, 68, 39, 'کیا تمہارے لیے ہم نے قسمیں کھا لی ہیں جو قیامت تک چلی جائيں گی کہ بے شک تمہیں وہی ملے گا جو تم حکم کرو گے'),
(5311, 68, 40, 'ان سے پوچھیئے کون سا ان میں اس بات کا ذمہ دار ہے'),
(5312, 68, 41, 'کیا ان کے معبود ہیں پھر اپنے معبودوں کو لے آئيں اگر وہ سچے ہیں'),
(5313, 68, 42, 'جس دن پنڈلی کھولی جائے گی اور وہ سجدہ کرنے کو بلائے جائیں گے تو وہ نہ کر سکیں گے'),
(5314, 68, 43, 'ان کی آنکھیں جھکی ہوں گی ان پر ذلت چھا رہی ہو گی اوروہ پہلے (دنیا میں) سجدہ کے لیے بلائے جاتے تھے حالانکہ وہ صحیح سالم ہوتے تھے'),
(5315, 68, 44, 'پس مجھے اور اس کلام کے جھٹلانے والوں کو چھوڑ دو ہم انہیں بتدریج (جہنم کی طرف) لے جائے گے اس طور پر کہ انہیں خبر بھی نہیں ہو گی'),
(5316, 68, 45, 'اور ہم انکو ڈھیل دیتے ہیں بے شک ہماری تدبیر زبردست ہے'),
(5317, 68, 46, 'کیا آپ ان سے کچھ اجرت مانگتے ہیں کہ جس کا تاوان کا ان پر بوجھ پڑ رہا ہے'),
(5318, 68, 47, 'یا ان کے پاس غیب کی خبر ہے کہ وہ اسے لکھ لیتے ہیں'),
(5319, 68, 48, 'پھر آپ اپنے رب کے حکم کا انتظار کریں اور مچھلی والے جیسے نہ ہوجائیں جب کہ اس نے اپنے رب کو پکارا اور وہ بہت ہی غمگین تھا'),
(5320, 68, 49, 'اگر اس کے رب کی رحمت اسے نہ سنبھال لیتی تو وہ برے حال سے چٹیل میدان میں پھینکا جاتا'),
(5321, 68, 50, 'پس اسے اس کے رب نے نوازا پھر اسے نیک بختو ں میں کر دیا'),
(5322, 68, 51, 'اور بالکل قریب تھا کہ کافر آپ کو اپنی تیز نگاہوں سے پھسلا دیں جب کہ انہوں نے قرآن سنا اور کہتے ہیں کہ یہ تو دیوانہ ہے'),
(5323, 68, 52, 'اور حالانکہ یہ قرآن تمام دنیا کے لیے صرف نصیحت ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'قیامت'),
(5325, 69, 2, 'قیامت کیا چیز ہے'),
(5326, 69, 3, 'اور تمہیں کس چیز نے بتایا کہ قیامت کیا ہے'),
(5327, 69, 4, 'ثمود اور عاد نے قیامت کو جھٹلایا تھا'),
(5328, 69, 5, 'سو ثمود تو سخت ہیبت ناک چیخ سے ہلاک کیے گئے'),
(5329, 69, 6, 'اور لیکن قوم عاد سو وہ ایک سخت آندھی سے ہلاک کیے گئے'),
(5330, 69, 7, 'وہ ان پر سات راتیں اور آٹھ دن لگاتار چلتی رہی (اگر تو موجود ہوتا) اس قوم کو اس طرح گرا ہوا دیکھتا کہ گویا کہ گھری ہوئی کھجوروں کے تنے ہیں'),
(5331, 69, 8, 'سو کیا تمہیں ان کا کوئی بچا ہوا نظر آتا ہے'),
(5332, 69, 9, 'اور فرعون اس سے پہلے کے لوگ اور الٹی ہوئی بستیوں والے گناہ کے مرتکب ہوئے'),
(5333, 69, 10, 'پس انہوں نے اپنے رب کے رسول کی نافرمانی کی تو الله نے انہیں سخت پکڑ لیا'),
(5334, 69, 11, 'بے شک ہم نے جب پانی حد سے گزر گیا تھا تو تمہیں کشتی میں سوار کر لیا تھا'),
(5335, 69, 12, 'تاکہ ہم اسے تمہارے لیے ایک یادگار بنائیں اور اس کو کان یا د رکھنے والے یاد رکھیں'),
(5336, 69, 13, 'پھر جب صور میں پھونکا جائے گا ایک بار پھونکا جانا'),
(5337, 69, 14, 'اور زمین اور پہاڑ اٹھائے جائیں گے پس وہ دونوں ریزہ ریزہ کر دیئے جائیں گے'),
(5338, 69, 15, 'پس اس دن قیامت ہو گی'),
(5339, 69, 16, 'اور آسمان پھٹ جائے گا اوروہ اس دن بودا ہوگا'),
(5340, 69, 17, 'اور اس کے کنارے پر فرشتے ہوں گے اور عرشِ الہیٰ کو اپنے اوپر اس دن آٹھ فرشتے اٹھائیں گے'),
(5341, 69, 18, 'اس دن تم پیش کیے جاؤ گے تمہارا کوئی راز مخفی نہ رہے گا'),
(5342, 69, 19, 'جس کو اس کا اعمال نامہ اس کے داہنے ہاتھ میں دیا جائے گا سو وہ کہے گا لو میرا اعمال نامہ پڑھو'),
(5343, 69, 20, 'بے شک میں سمجھتا تھا کہ میں اپنا حساب دیکھوں گا'),
(5344, 69, 21, 'سووہ دل پسند عیش میں ہو گا'),
(5345, 69, 22, 'بلند بہشت میں'),
(5346, 69, 23, 'جس کے میوے جھکے ہوں گے'),
(5347, 69, 24, 'کھاؤ اور پیئو ان کاموں کے بدلے میں جو تم نے گزشتہ دنوں میں آگے بھیجے تھے'),
(5348, 69, 25, 'اور جس کا اعمال نامہ اس کے بائیں ہاتھ میں دیا گیا تو کہے گا اے کاش میرا اعمال نامہ نہ ملتا'),
(5349, 69, 26, 'اور میں نہ جانتا کہ میرا حساب کیا ہے'),
(5350, 69, 27, 'کاش وہ (موت) خاتمہ کرنے والی ہوتی'),
(5351, 69, 28, 'میرا مال میرے کچھ کام نہ آیا'),
(5352, 69, 29, 'مجھ سے میری حکومت بھی جاتی رہی'),
(5353, 69, 30, 'اسے پکڑو اسے طوق پہنا دو'),
(5354, 69, 31, 'پھر اسے دوزخ میں ڈال دو'),
(5355, 69, 32, 'پھر ایک زنجیر میں جس کا طول ستر گز ہے اسے جکڑ دو'),
(5356, 69, 33, 'بے شک وہ الله پر یقین نہیں رکھتا تھا جو عظمت والا ہے'),
(5357, 69, 34, 'اور نہ وہ مسکین کے کھانا کھلانے کی رغبت دیتا تھا'),
(5358, 69, 35, 'سو آج اس کا یہاں کوئی دوست نہیں'),
(5359, 69, 36, 'اور نہ کھانا ہے مگر زخموں کا دھون'),
(5360, 69, 37, 'اسے سوائے گناہگاروں کے کوئی نہیں کھائے گا'),
(5361, 69, 38, 'سو میں ان چیزوں کی قسم کھاتا ہوں جو تم دیکھتے ہو'),
(5362, 69, 39, 'اوران کی جو تم نہیں دیکھتے'),
(5363, 69, 40, 'کہ بے شک یہ (قرآن) رسول کریم کی زبان سے نکلا ہے'),
(5364, 69, 41, 'اور وہ کسی شاعر کا قول نہیں (مگر) تم بہت ہی کم یقین کرتے ہو'),
(5365, 69, 42, 'اور نہ ہی کسی جادوگر کا قول ہے تم بہت ہی کم غور کرتے ہو'),
(5366, 69, 43, 'وہ پرودگار عالم کا نازل کیا ہوا ہے'),
(5367, 69, 44, 'اور اگر وہ کوئی بناوٹی بات ہمارے ذمہ لگاتا'),
(5368, 69, 45, 'تو ہم اس کا داہنا ہاتھ پکڑ لیتے'),
(5369, 69, 46, 'پھر ہم اس کی رگِ گردن کاٹ ڈالتے'),
(5370, 69, 47, 'پھر تم میں سے کوئی بھی اس سے روکنے والا نہ ہوتا'),
(5371, 69, 48, 'اور بے شک وہ تو پرہیزگاروں کے لیے ایک نصیحت ہے'),
(5372, 69, 49, 'اور بے شک ہم جانتے ہیں کہ بعض تم میں سے جھٹلانے والے ہیں'),
(5373, 69, 50, 'اور بے شک وہ کفار پر باعث حسرت ہے'),
(5374, 69, 51, 'اور بے شک وہ یقین کرنے کے قابل ہے'),
(5375, 69, 52, 'پس اپنے رب کے نام کی تسبیح کر جو بڑا عظمت والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'ایک سوال کرنے والے نے اس عذاب کا سوال کیا جو واقع ہونے والا ہے'),
(5377, 70, 2, 'کافرو ں کے لیے کہ اس کا کوئی ٹالنے والا نہیں'),
(5378, 70, 3, 'جس الله کی طرف سے واقع ہو گا جو سیڑھیوں کا (یعنی آسمانوں کا) مالک ہے'),
(5379, 70, 4, '(جن سیڑھیوں سے) فرشتے اور اہلِ ایمان کی روحیں اس کے پاس چڑھ کر جاتی ہیں (اور وہ عذاب) اس دن ہو گا جس کی مقدار پچاس ہزار سال کی ہے'),
(5380, 70, 5, 'آپ اچھی طرح سے صبر کیے رہیں'),
(5381, 70, 6, 'بے شک وہ اسے دور دیکھتے ہیں'),
(5382, 70, 7, 'اور ہم اسے قریب دیکھتے ہیں'),
(5383, 70, 8, 'جس دن آسمان پگھلے ہوئے تانبے کی مانند ہوگا'),
(5384, 70, 9, 'اور پہاڑ دھنی ہوئی رنگداراؤن کی طرح ہوں گے'),
(5385, 70, 10, 'اور کوئی دوست کسی دوست کو نہیں پوچھے گا'),
(5386, 70, 11, 'وہ انہیں کھائیں جائیں گے مجرم چاہے گا کہ کاش اس دن کے عذاب کے بدلے میں اپنے بیٹوں کو دے دے'),
(5387, 70, 12, 'اوراپنی بیوی اور اپنے بھائی کو'),
(5388, 70, 13, 'اور اپنے اس کنبہ کو جو اسے پناہ دیتا تھا'),
(5389, 70, 14, 'اوران سب کو جو زمین میں ہیں پھر اپنے آپ کو بچا لے'),
(5390, 70, 15, 'ہرگز نہیں بے شک وہ تو ایک آگ ہے'),
(5391, 70, 16, 'کھالوں کو اتارنے والی'),
(5392, 70, 17, 'اس کو بلائے گی جس نے پیٹھ پھیری اورمنہ موڑا'),
(5393, 70, 18, 'اور مال جمع کیا اورگن گن کر رکھا'),
(5394, 70, 19, 'بے شک انسان کم ہمت پیدا ہوا ہے'),
(5395, 70, 20, 'جب اسے تکلیف پہنچتی ہے تو چلا اٹھتا ہے'),
(5396, 70, 21, 'اور جب اسے مال ملتا ہے تو بڑا بخیل ہے'),
(5397, 70, 22, 'مگر وہ نمازی'),
(5398, 70, 23, 'جو اپنی نماز پر ہمیشہ سے قائم ہیں'),
(5399, 70, 24, 'اوروہ جن کے مالو ں میں حصہ معین ہے'),
(5400, 70, 25, 'سائل اور غیر سائل کے لیے'),
(5401, 70, 26, 'اوروہ جو قیامت کے دن کا یقین رکھتے ہیں'),
(5402, 70, 27, 'اور وہ جو اپنے رب کے عذاب سے ڈرنے والے ہیں'),
(5403, 70, 28, 'بے شک ان کے رب کے عذاب کا خطرہ لگا ہوا ہے'),
(5404, 70, 29, 'اوروہ جو اپنی شرم گاہوں کی حفاظت کرتے ہیں'),
(5405, 70, 30, 'مگر اپنی بیویوں یا اپنی لونڈیوں سے سو بے شک انہیں کوئی ملامت نہیں'),
(5406, 70, 31, 'پس جو کوئی اس کے سوا چاہے سو وہی لوگ حد سے بڑھنے والے ہیں'),
(5407, 70, 32, 'اور وہ جواپنی امانتوں اور عہدوں کی رعایت رکھتے ہیں'),
(5408, 70, 33, 'اور وہ جو اپنی گواہیوں پر قائم رہتے ہیں'),
(5409, 70, 34, 'اور وہ جو اپنی نمازوں کی حفاظت کرتے ہیں'),
(5410, 70, 35, 'وہی لوگ باغوں میں عزت سے رہیں گے'),
(5411, 70, 36, 'پس کافروں کو کیا ہوگیا کہ آپ کی طرف دوڑے آ رہے ہیں'),
(5412, 70, 37, 'دائیں اور بائیں سے ٹولیاں بنا کر'),
(5413, 70, 38, 'کیا ہر ایک ان میں سے طمع رکھتا ہے کہ وہ نعمت کے باغ میں داخل کیا جاوے گا'),
(5414, 70, 39, 'ہرگز نہیں بے شک ہم نے انہیں اس چیز سے پیدا کیا ہے جسے وہ بھی جانتے ہیں'),
(5415, 70, 40, 'پس میں مشرقوں اور مغربوں کے پروردگار کی قسم کھاتا ہوں (یعنی اپنی ذات کی) کہ ہم ضرور قادر ہیں'),
(5416, 70, 41, 'اس بات پر کہ ہم ان سے بہتر لوگ بدل کر لا سکتے ہیں اور ہم عاجز بھی نہیں ہیں'),
(5417, 70, 42, 'پھر انہیں چھوڑ دو کہ وہ بیہودہ باتوں اور کھیل میں لگے رہیں یہاں تک کہ وہ دن دیکھ لیں جس کا ان سے وعدہ کیا جاتا ہے'),
(5418, 70, 43, 'جس دن وہ دوڑتے ہوئے قبروں سے نکل پڑیں گے گویا کہ وہ ایک نشان کی طرف دوڑے چلے جارہے ہیں'),
(5419, 70, 44, 'ان کی نگاہیں جھکی ہوں گی ان پر ذلت چھا رہی ہو گی یہی وہ دن ہے جس کا ان سے وعدہ کیا جاتا تھا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'بے شک ہم نے نوح کو اس کی قوم کی طرف بھیجا تھا کہ اپنی قوم کو ڈرا اس سے پہلے کہ ان پر دردناک عذاب آ پڑے'),
(5421, 71, 2, 'اس نے کہا کہ اے میری قوم بے شک میں تمہارے لیے کھلم کھلا ڈرانے والا ہوں'),
(5422, 71, 3, 'کہ الله کی عبادت کرو اور اس سے ڈرو اور میرا کہا مانو'),
(5423, 71, 4, 'وہ تمہارے لیے تمہارے گناہ بخش دے گا اور تمہیں ایک وقت مقرر تک مہلت دے گا بے شک الله کا وقت ٹھیرایا ہوا ہے جب آجائے گا تو اس میں تاخیر نہ ہوگی کا ش تم جانتے'),
(5424, 71, 5, 'کہا اے میرے رب میں نے اپنی قوم کو رات اور دن بلایا'),
(5425, 71, 6, 'پھر وہ میرے بلانے سے اوربھی زيادہ بھاگتے رہے'),
(5426, 71, 7, 'اور بے شک جب کبھی میں نے انہیں بلایا تاکہ تو انہیں معاف کر دے تو انہوں نے اپنی انگلیاں اپنے کانوں میں رکھ لیں اور انہوں نے اپنے کپڑے اوڑھ لیے اور ضد کی اوربہت بڑا تکبر کیا'),
(5427, 71, 8, 'پھر میں نے انہیں کھلم کھلا بھی بلایا'),
(5428, 71, 9, 'پھر میں نے انہیں علانیہ بھی کہا اور مخفی طور پر بھی کہا'),
(5429, 71, 10, 'پس میں نے کہا اپنے رب سے بخشش مانگو بے شک وہ بڑا بخشنے والا ہے'),
(5430, 71, 11, 'وہ آسمان سے تم پر (موسلا داھار) مینہ برسائے گا'),
(5431, 71, 12, 'اور مال اور اولاد سے تمہاری مدد کرے گا اور تمہارے لیے باغ بنادے گا اور تمہارے لیے نہریں بنا دے گا'),
(5432, 71, 13, 'تمہیں کیا ہو گیا تم الله کی عظمت کا خیال نہیں رکھتے'),
(5433, 71, 14, 'حالانکہ اس نے تمہیں کئی طرح سے بنا یا ہے'),
(5434, 71, 15, 'کیا تم نہیں دیکھتے کہ الله نے سات آسمان اوپر تلے (کیسے) بنائے ہیں'),
(5435, 71, 16, 'اوران میں چاند کو چمکتا ہوا بنایا اور آفتاب کو چراغ بنا دیا'),
(5436, 71, 17, 'اور الله ہی نے تمہیں زمین میں سے ایک خاص طور پر پیدا کیا'),
(5437, 71, 18, 'پھر وہ تمہیں اسی میں لوٹائے گا اور اسی میں سے (پھر) باہر نکالے گا'),
(5438, 71, 19, 'اور الله ہی نے تمہارے لیے زمین کو فرش بنایا ہے'),
(5439, 71, 20, 'تاکہ تم اس کے کھلے راستوں میں چلو'),
(5440, 71, 21, 'نوح نے کہا اے میرے رب بے شک انہوں نے میرا کہنا نہ مانا اور اس کو مانا جس کو اس کے مال اور اولاد نے نقصان کے سوا کچھ بھی فائد نہں دیا'),
(5441, 71, 22, 'اور انہوں نے بڑی زبردست چال چلی'),
(5442, 71, 23, 'اور کہا تم اپنے معبودوں کوہرگز نہ چھوڑو اور نہ ودّ اور سواع اور یغوث اور یعوق اورنسرکو چھوڑو'),
(5443, 71, 24, 'اور انہوں نے بہتوں کو گمراہ کر دیا اور (اب آپ) ان ظالموں کی گمراہی اور بڑھا دیجیئے'),
(5444, 71, 25, 'وہ اپنے گناہوں کے سبب سے غرق کر دیئے گئے پھر دوزخ میں داخل کیے گئے پس انہوں نے اپنے لیے سوائے الله کے کوئی مددگار نہ پایا'),
(5445, 71, 26, 'اور نوح نے کہا اے میرے رب زمین پر کافروں میں سے کوئی رہنے والا نہ چھوڑ'),
(5446, 71, 27, 'اگر تو نے ان کو چھوڑ دیا تو تیرے بندوں کو گمراہ کریں گے اور نسل بھی جو ہو گی تو فاجر اور کافر ہی ہو گی'),
(5447, 71, 28, 'اے میرے رب مجھے اور میرے ماں باپ کو بخش دے اور اس کی جو میرے گھر میں ایماندار ہو کر داخل ہوجائے اور ایماندار مردوں اور عورتوں کو اور ظالموں کو تو بربادی کے سوا اور کچھ زیادہ نہ کر');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'کہہ دو کہ مجھے اس بات کی وحی آئی ہے کہ کچھ جن (مجھ سے قرآن پڑھتے) سن گئے ہیں پھر انہوں نے (اپنی قوم سے) جا کر کہہ دیا کہ ہم نے عجیب قرآن سنا ہے'),
(5449, 72, 2, 'جو نیکی کی طرف رہنمائی کرتا ہے سو ہم اس پر ایمان لائے ہیں اور ہم اپنے رب کا کسی کو شریک نہ ٹھیرائیں گے'),
(5450, 72, 3, 'اورہمارے رب کی شان بلند ہے نہ اس کی کوئی بیوی ہے اور نہ بیٹا'),
(5451, 72, 4, 'اور ہم میں سے بعض بے وقوف ہیں جو الله پر جھوٹی باتیں بنایا کرتے تھے'),
(5452, 72, 5, 'اور ہمیں خیال تھا کہ انسان اور جن الله پر ہرگز چھوٹ نہ بولیں گے'),
(5453, 72, 6, 'اورکچھ آدمی جنوں کے مردوں سے پناہ لیاکرتے تھے سو انہوں نے ان کی سرکشی اور بڑھا دی'),
(5454, 72, 7, 'اور وہ بھی سمجھے ہوئے تھے جیسا کہ تم نے سمجھ رکھا ہے کہ الله ہر گز کسی کو (رسول بنا کر) نہ بھیجحے گا'),
(5455, 72, 8, 'اور ہم نے آسمان کو ٹٹولا تو ہم نے اسے سخت پہروں اور شعلوں سے بھرا ہو ا پایا'),
(5456, 72, 9, 'اورہم نے اس کے ٹھکانوں میں سننے کے لیے بیٹھا کرتے تھے پس جو کوئی اب کان دھرتا ہے تو وہ اپنے لیے ایک انگارہ تاک لگانے ہوئے پاتا ہے'),
(5457, 72, 10, 'اورہم نہیں جانتے کہ زمین والوں کے ساتھ نقصان کا ارادہ کیا گیا ہےیا ان کی نسبت ان کے رب نےراہ راست پر لانے کا ارادہ کیا ہے'),
(5458, 72, 11, 'اور کچھ تو ہم میں سے نیک ہیں اور کچھ اور طرح کے ہم بھی مختلف طریقوں پر تھے'),
(5459, 72, 12, 'اور بے شک ہم نے سمجھ لیا ہے کہ ہم الله کو زمین میں کبھی عاجز نہ کر سکیں گے اور نہ ہی ہم بھاگ کر عاجز کر سکیں گے'),
(5460, 72, 13, 'اور جب ہم نے ہدایت کی بات سنی تو ہم اس پر ایمان لے آئے پھر جو اپنے رب پر ایمان لے لآیا تو نہ اسے نقصان کا ڈر رہے گا اور نہ ظلم کا'),
(5461, 72, 14, 'اور کچھ تو ہم میں سے فرمانبردار ہیں اور کچھ نافرمان پس جوکوئی فرمانبردار ہو گیا سو ایسے لوگوں نے سیدھا راستہ تلاش کر لیا'),
(5462, 72, 15, 'اور لیکن جو ظالم ہیں سو وہ دوزح کا ایندھن ہوں گے'),
(5463, 72, 16, 'اور اگر (مکہ والے) سیدھے راستے پر قائم رہتے تو ہم ان کو با افراط پانی سے سیراب کرتے'),
(5464, 72, 17, 'تاکہ اس (ارزانی) میں ان کا امتحان کریں اورجس نے اپنے رب کی یاد سے منہ موڑا تو وہ اسے سخت عذاب میں ڈالے گا'),
(5465, 72, 18, 'اوربے شک مسجدیں الله کے لیے ہیں پس تم الله کے ساتھ کسی کو نہ پکارو'),
(5466, 72, 19, 'اورجب الله کا بندہ (نبی) اس کو پکارنے کھڑا ہوتا ہے تو لوگ اس پر جھمگٹا کرنے لگتے ہیں'),
(5467, 72, 20, 'کہہ دو میں تو اپنے رب ہی کو پکارتا ہوں اور اس کے ساتھ کسی کوبھی شریک نہیں کرتا'),
(5468, 72, 21, 'کہہ دو میں نہ تمہارے کسی ضرر کا اختیار رکھتا ہوں اورنہ کسی بھلائی کا'),
(5469, 72, 22, 'کہہ دو مجھے الله سے کوئی نہیں بچا سکے گا اور نہ مجھے اس کے سوا پناہ ملے گی'),
(5470, 72, 23, 'مگر الله کا پیغام اور اس کا حکم پہنچانا ہے اور جو کوئی الله اوراس کے رسول کی نافرمانی کرے گا تو اس کے لیے دوزخ کی آگ ہے جس میں وہ سدا رہے گا'),
(5471, 72, 24, 'یہاں تک کہ جب وہ (عذاب) دیکھیں گے جس کا ان سے وعدہ کیا جاتا ہے تو وہ جان لیں گے کہ کس کے مددگار کمزور اور شمار میں کم ہیں'),
(5472, 72, 25, 'کہہ دو مجھے خبر نہیں جس کا تم سے وعدہ کیا گیا ہے وہ قریب ہے یا اس کے لیے میرا رب کوئی مدت ٹھیراتا ہے'),
(5473, 72, 26, 'وہ غیب جاننے والا ہے اپنے غائب کی باتوں پر کسی کو واقف نہیں کرتا'),
(5474, 72, 27, 'مگر اپنے پسندیدہ رسول کو پھراس کے آگے اور پیچھے محافظ مقرر کر دیتا ہے'),
(5475, 72, 28, 'تاکہ وہ بظاہر جان لے کہ انہوں نے اپنے رب کے پیغامات پہنچا دیے اور الله نے تمام کاموں کو اپنے قبضے میں کر رکھا ہے ارواس نے ہر چیز کی گنتی شمار کر رکھی ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'اے چادر اوڑھنے والے'),
(5477, 73, 2, 'رات کو قیام کر مگر تھوڑا ساحصہ'),
(5478, 73, 3, 'آدھی رات یا اس میں سے تھوڑا ساحصہ کم کر دے'),
(5479, 73, 4, 'یا اس پر زیادہ کر دو اور قرآن کو ٹھہر ٹھہر کر پڑھا کرو'),
(5480, 73, 5, 'ہم عنقریب آپ پر ایک بھاری بات کا (بوجھ) ڈالنے والے ہیں'),
(5481, 73, 6, 'بےشک رات کا اٹھنا نفس کو خوب زیر کرتا ہے اور بات بھی صحیح نکلتی ہے'),
(5482, 73, 7, 'بے شک دن میں آپ کے لیے بڑا کام ہے'),
(5483, 73, 8, 'اور اپنے رب کا نام لیا کرو اور سب سے الگ ہو کر اسی کی طرف آ جاؤ'),
(5484, 73, 9, 'وہ مشرق اور مغرب کا مالک ہے اس کے سوا اور کوئی معبود نہیں پس اسی کو کارساز بنا لو'),
(5485, 73, 10, 'اور کافروں کی باتوں پر صبر کرو اور انہیں عمدگی سے چھوڑ دو'),
(5486, 73, 11, 'اورمجھے اور جھٹلانے والے دولت مندوں کو چھوڑ دو اور انہیں تھوڑی سی مدت و مہلت دو'),
(5487, 73, 12, 'بے شک ہمارے پاس بیڑیاں اور جہنم ہے'),
(5488, 73, 13, 'اور گلے میں اٹکنے والا کھانا اور دردناک عذاب'),
(5489, 73, 14, 'جس دن زمین اور پہاڑ لرزیں گے اور پہاڑ ریگ رواں کے تودے ہو جائیں گے'),
(5490, 73, 15, 'ہم نے تمہاری طرف تم پر گواہی دینے والا ایک رسول بھیجا ہے کہ جس طرح فرعون کی طرف ایک رسول بھیجا تھا'),
(5491, 73, 16, 'پھر فرعون نے اس رسول کی نافرمانی کی تو ہم نے اسے سخت پکڑ سے پکڑ لیا'),
(5492, 73, 17, 'پھر تم کس طرح بچو گے اگر تم نے بھی انکار کیا اس دن جو لڑکوں کو بوڑھا کر دے گا'),
(5493, 73, 18, 'اس دن آسمان پھٹ جائے گا اس کا وعدہ ہو کر رہے گا'),
(5494, 73, 19, 'بے شک یہ (قرآن) ایک نصیحت ہے پھر جو چاہے اپنے رب کی طرف آنے کا راستہ بنا لے'),
(5495, 73, 20, 'بے شک آپ کا رب جانتا ہے کہ آپ اور جو لوگ آپ کے ساتھ ہیں (کبھی) دو تہائی رات کے قریب اور (کبھی) آدھی رات اور (کبھی) تہائی رات سے (نماز تہجد) میں کھڑے ہوتے ہیں اور الله ہی رات اور دن کا اندازہ کرتا ہے اسے معلوم ہے کہ تم اس کو نباہ نہیں سکتے سو اس نے تم پر رحم کیا پس پڑھو جتنا قرآن میں سے آسان ہو اسے علم ہے کہ تم میں سے کچھ بیمار ہوں گے اور کچھ اور لوگ بھی جو الله کا فضل تلاش کرتے ہوئے زمین پر سفر کریں گے اورکچھ اور لوگ ہوں گے جو الله کی راہ میں جہاد کریں گے پس پڑھو جو اس میں سےآسان ہو اور نماز قائم کرو اور زکوةٰ دو اور الله کو اچھی طرح (یعنی اخلاص سے) قرض دو اورجو کچھ نیکی آگے بھیجوگے اپنے واسطے تو اس کو الله کے ہاں بہتر اور بڑے اجر کی چیز پاؤ گے اور الله سے بخشش مانگو بے شک الله بخشنے والا نہایت رحم والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'اے کپڑے میں لپٹنے والے'),
(5497, 74, 2, 'اٹھو پھر (کافروں کو) ڈراؤ'),
(5498, 74, 3, 'اور اپنے رب کی بڑائی بیان کرو'),
(5499, 74, 4, 'اور اپنے کپڑے پاک رکھو'),
(5500, 74, 5, 'اورمیل کچیل دور کرو'),
(5501, 74, 6, 'اوربدلہ پانے کی غرض سے احسان نہ کرو'),
(5502, 74, 7, 'اوراپنے رب کے لیے صبر کرو'),
(5503, 74, 8, 'پھر جب صور میں پھونکا جائے گا'),
(5504, 74, 9, 'پس وہ اس دن بڑا کٹھن دن ہو گا'),
(5505, 74, 10, 'کافروں پر وہ آسان نہ ہو گا'),
(5506, 74, 11, 'مجھے اور اس کو چھوڑ دو کہ جس کو میں نے اکیلا پیدا کیا'),
(5507, 74, 12, 'اوراس کوبڑھنے والا مال دیا'),
(5508, 74, 13, 'اور حاضر رہنے والے بیٹے دیئے'),
(5509, 74, 14, 'اور اس کے لیے ہر طرح کا سامان تیار کر دیا'),
(5510, 74, 15, 'پھر وہ طمع کرتا ہے کہ میں اور بڑھا دوں'),
(5511, 74, 16, 'ہرگز نہیں بےشک وہ ہماری آیات کا سخت مخالف ہے'),
(5512, 74, 17, 'عنقریب میں اسے اونچی گھاٹی پر چڑھاؤں گا'),
(5513, 74, 18, 'بے شک اس نے سوچا اور اندازہ لگایا'),
(5514, 74, 19, 'پھر اسے الله کی مار اس نے کیسا اندازہ لگایا'),
(5515, 74, 20, 'پھر اسے الله کی مار اس نے کیسا اندازہ لگایا'),
(5516, 74, 21, 'پھر اس نے دیکھا'),
(5517, 74, 22, 'پھر اس نے تیوری چڑھائی اور منہ بنایا'),
(5518, 74, 23, 'پھر پیٹھ پھیر لی اور تکبر کیا'),
(5519, 74, 24, 'پھر کہا یہ تو ایک جادو ہے جو چلا آتا ہے'),
(5520, 74, 25, 'یہ تو ہو نہ ہو آدمی کا کلام ہے'),
(5521, 74, 26, 'عنقریب اس کو دوزخ میں ڈالوں گا'),
(5522, 74, 27, 'اور آپ کو کیا خبر کہ دوزخ کیا ہے'),
(5523, 74, 28, 'نہ باقی رکھے اور نہ چھوڑے'),
(5524, 74, 29, 'آدمی کو جھلس دے'),
(5525, 74, 30, 'اس پر انیس (فرشتے) مقرر ہیں'),
(5526, 74, 31, 'اور ہم نے دوزخ پر فرشتے ہی رکھے ہیں اور ان کی تعداد کافروں کے لیے آزمائش بنائی ہے تاکہ جن کو کتاب دی گئی ہے وہ یقین کر لیں اور ایمان داروں کا ایمان بڑھے اورتاکہ اہلِ کتاب اور ایمان دار شک نہ کریں اور تاکہ جن کے دلوں میں (نفاق کی) بیماری ہے اورکافر یہ کہیں کہ الله کی اس بیان سے کیاغرض ہے اور الله اس طرح سے جسے چاہتاہے گمراہ کر تا ہے اور جسے چاہتا ہے ہدایت کرتا ہے اور آپ کے رب کے لشکروں کو اس کے سوا اور کوئی نہیں جانتا اور دوزخ (کا حال بیان کرنا) صرف آدمیوں کی نصیحت کے لیے ہے'),
(5527, 74, 32, 'نہیں نہیں قسم ہے چاند کی'),
(5528, 74, 33, 'اور رات کی جب وہ ڈھلے'),
(5529, 74, 34, 'اور صبح کی جب وہ روشن ہوجائے'),
(5530, 74, 35, 'کہ وہ (دوزخ) بڑی بڑی مصیبتوں میں سے ایک ہے'),
(5531, 74, 36, 'انسان کو ڈرانے والی ہے'),
(5532, 74, 37, 'تم میں سے ہر ایک کے لیے خواہ کوئی اس کے آگے آئے یا پیچھے ہٹے'),
(5533, 74, 38, 'ہر شخص اپنے اعمال کے سبب گروی ہے'),
(5534, 74, 39, 'مگر داہنے والے'),
(5535, 74, 40, 'باغوں میں ہو ں گے ایک دوسرے سے پوچھیں گے'),
(5536, 74, 41, 'گناہگارو ں کی نسبت'),
(5537, 74, 42, 'کس چیز نے تمہیں دوزخ میں ڈالا'),
(5538, 74, 43, 'وہ کہیں گے کہ ہم نمازی نہ تھے'),
(5539, 74, 44, 'اور نہ ہم مسکینوں کو کھانا کھلاتے تھے'),
(5540, 74, 45, 'اور ہم بکواس کرنے والوں کے ساتھ بکواس کیاکرتے تھے'),
(5541, 74, 46, 'اور ہم انصاف کے دن کو جھٹلایا کرتے تھے'),
(5542, 74, 47, 'یہاں تک کہ ہمیں موت آ پہنچی'),
(5543, 74, 48, 'پس ان کو سفارش کرنے والوں کی سفارش نفع نہ دے گی'),
(5544, 74, 49, 'پسانہیں کیا ہو گیا کہ وہ نصیحت سے منہ موڑرہے ہیں'),
(5545, 74, 50, 'گویا کہ وہ بدکنے والے گدھے ہیں'),
(5546, 74, 51, 'جو شیر سے بھاگے ہیں'),
(5547, 74, 52, 'بلکہ ہر ایک آدمی ان میں سے چاہتا ہے کہ اسے کھلے ہوئے صحیفے دیئے جائیں'),
(5548, 74, 53, 'ہرگز نہیں بلکہ وہ آخرت سے نہیں ڈرتے'),
(5549, 74, 54, 'ہرگز نہیں بے شک یہ (قرآن) ایک نصیحت ہے'),
(5550, 74, 55, '(پس جو چاہے اس کو یاد کر لے'),
(5551, 74, 56, 'اور کوئی بھی یاد نہیں کر سکتا مگر جبکہ الله ہی چاہے وہی جس سے ڈرنا چاہیے اوروہی بخشنے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'قیامت کے دن کی قسم ہے'),
(5553, 75, 2, 'اور پشیمان ہونے والے شخص کی قسم ہے'),
(5554, 75, 3, 'کیا انسان سمجھتا ہے کہ ہم اس کی ہڈیاں جمع نہ کریں گے'),
(5555, 75, 4, 'ہاں ہم تو اس پر قادر ہیں کہ اس کی پور پور درست کر دیں'),
(5556, 75, 5, 'بلکہ انسان تو چاہتا ہے کہ آئندہ بھی نافرمانی کرتا رہے'),
(5557, 75, 6, 'پوچھتا ہےکہ قیامت کا دن کب ہو گا'),
(5558, 75, 7, 'پس جب آنکھیں چندھیا جائیں گی'),
(5559, 75, 8, 'اور چاند بے نور ہو جائے گا'),
(5560, 75, 9, 'اور سورج اور چاند اکھٹے کیے جائیں گے'),
(5561, 75, 10, 'اس دن انسان کہے گا کہ بھاگنے کی جگہ کہاں ہے'),
(5562, 75, 11, 'ہر گز نہیں کہیں پناہ نہیں'),
(5563, 75, 12, 'اس دن آپ کے رب ہی کی طرف ٹھکانہ ہے'),
(5564, 75, 13, 'اس دن انسان کو بتا دیا جائے گا کہ وہ کیا لایا اور کیا چھوڑ آیا'),
(5565, 75, 14, 'بلکہ انسان اپنے اوپر خود شاہد ہے'),
(5566, 75, 15, 'گو وہ کتنے ہی بہانے پیش کرے'),
(5567, 75, 16, 'آپ (وحی ختم ہونے سے پہلے) قرآن پراپنی زبان نہ ہلایا کیجیئے تاکہ آپ اسے جلدی جلدی لیں'),
(5568, 75, 17, 'بے شک اس کا جمع کرنا اور پڑھا دینا ہمارے ذمہ ہے'),
(5569, 75, 18, 'پھر جب ہم ا سکی قرأت کر چکیں تو اس کی قرأت کا اتباع کیجیئے'),
(5570, 75, 19, 'پھر بے شک اس کا کھول کر بیان کرنا ہمارے ذمہ ہے'),
(5571, 75, 20, 'ہر گز نہیں بلکہ تم تو دنیا کو چاہتے ہو'),
(5572, 75, 21, 'اور آخرت کو چھوڑتے ہو'),
(5573, 75, 22, 'کئی چہرے اس دن تر و تازہ ہو ں گے'),
(5574, 75, 23, 'اپنے رب کی طرف دیکھتے ہوں گے'),
(5575, 75, 24, 'اور کتنے چہرے اس دن اداس ہو ں گے'),
(5576, 75, 25, 'خیال کر رہے ہوں گے کہ ان کے ساتھ کمر توڑ دینے والی سختی کی جائے گی'),
(5577, 75, 26, 'نہیں نہیں جب کہ جان گلے تک پہنچ جائے گی'),
(5578, 75, 27, 'اورلوگ کہیں گے کوئی جھاڑنے والا ہے'),
(5579, 75, 28, 'اور وہ خیال کرے گا کہ یہ وقت جدائی کا ہے'),
(5580, 75, 29, 'اور ایک پنڈلی دوسری پنڈلی سے لپٹ جائے گی'),
(5581, 75, 30, 'تیرے رب کی طرف اس دن چلنا ہوگا'),
(5582, 75, 31, 'پھر نہ تو اس نے تصدیق کی اور نہ نماز پڑھی'),
(5583, 75, 32, 'بلکہ جھٹلایا اورمنہ موڑا'),
(5584, 75, 33, 'پھر اپنے گھر والوں کی طرف اکڑتا ہوا چلا گیا'),
(5585, 75, 34, '(اے انسان) تیرے لیے افسوس پرافسوس ہے'),
(5586, 75, 35, 'پھر تیرے لیے افسوس پر افسوس ہے'),
(5587, 75, 36, 'کیاانسان یہ سمجھ رہا ہے کہ وہ یونہی چھوڑ دیا جائے گا'),
(5588, 75, 37, 'کیا وہ ٹپکتی منی کی ایک بوند نہ تھا'),
(5589, 75, 38, 'پھر وہ لوتھڑا بنا پھر الله نے اسے بنا کر ٹھیک کیا'),
(5590, 75, 39, 'پھر اس نے مرد و عورت کا جوڑا بنایا'),
(5591, 75, 40, 'پھر کیا وہ الله مردے زندہ کردینے پر قادر نہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'انسان پر ضرور ایک ایسا زمانہ بھی آیا ہے کہ اس کا کہیں کچھ بھی ذکر نہ تھا'),
(5593, 76, 2, 'بے شک ہم نے انسان کو ایک مرکب بوند سے پیدا کیا ہم اس کی آزمائش کرنا چاہتے تھے پس ہم نے اسے سننے والا دیکھنے والا بنا دیا'),
(5594, 76, 3, 'بے شک ہم نے اسے راستہ دکھا دیا یا تو وہ شکر گزار ہے اور یا ناشکرا'),
(5595, 76, 4, 'بے شک ہم نے کافروں کے لیے زنجیریں اور طوق اور دھکتی آگ تیار کر رکھی ہے'),
(5596, 76, 5, 'بے شک نیک ایسی شراب کے پیالے پئیں گے جس میں چشمہ کافور کی آمیزش ہو گی'),
(5597, 76, 6, 'وہ ایک چشمہ ہو گا جس میں سے الله کے بندے پئیں گے اس کو آسانی سے بہا کر لے جائیں گے'),
(5598, 76, 7, 'وہ اپنی منتیں پوری کرتے ہیں اور اس دن سے ڈرتے رہتے ہیں جس کی مصیبت ہر جگہ پھیلی ہوئی ہوگی'),
(5599, 76, 8, 'اور وہ اس کی محبت پرمسکین اور یتیم اور قیدی کو کھانا کھلاتے ہیں'),
(5600, 76, 9, 'ہم جو تمہیں کھلاتے ہیں تو خاص الله کے لیے نہ ہمیں تم سے بدلہ لینا مقصود ہے اور نہ شکرگزاری'),
(5601, 76, 10, 'ہم تو اپنے رب سے ایک اداس (اور) ہولناک دن سے ڈرتے ہیں'),
(5602, 76, 11, 'پس الله اس دن کی مصیبت سے انہیں بچا لے گا اور ان کے سامنے تازگی اور خوشی لائے گا'),
(5603, 76, 12, 'اوران کے صبر کے بدلے ان کو جنت اور ریشمی پوشاکیں دے گا'),
(5604, 76, 13, 'اس میں تختوں پر تکیہ لگائے ہوئے ہوں گے نہ وہاں دھوپ دیکھیں گے اور نہ سردی'),
(5605, 76, 14, 'اور ان پر اس کے سائے جھک رہے ہوں گے اور پھلوں کے گوشے بہت ہی قریب لٹک رہے ہوں گے'),
(5606, 76, 15, 'اوران پر چاندی کے برتن اور شیشے کے آبخوروں کا دور چل رہا ہوگا'),
(5607, 76, 16, 'شینیے بھی چاندی کے شیشے جو ایک خاص اناز پر ڈھالے گئے ہوں گے'),
(5608, 76, 17, 'اورانہیں وہاں ایسی شراب کا پیالہ پلایا جائے گا جس میں سونٹھ کی آمیزش ہو گی'),
(5609, 76, 18, 'وہ وہاں ایک چشمہ ہے جس کا نام سلسبیل ہے'),
(5610, 76, 19, 'اوران کے پاس سدا رہنے والے لڑکے (خادم) گھومتے ہوں گے جو تو ا ن کو دیکھےگا تو خیال کرے گا کہ وہ بکھرے ہوئے موتی ہیں'),
(5611, 76, 20, 'اورجب تو وہاں دیکھے گا تو نعمت اور بڑی سلطنت دیکھے گا'),
(5612, 76, 21, 'ان پرباریک سبز اور موٹے ریشم کے لباس ہوں گے اور انہیں چاندی کے کنگن پہنائے جائیں گے اور انہیں ان کا رب پاک شراب پلائے گا'),
(5613, 76, 22, 'بے شک یہ تمہارے (نیک اعمال کا) بدلہ ہے اور تمہاری کوشش مقبول ہوئی'),
(5614, 76, 23, 'بےشک ہم نےہی آپ پر یہ قرآن تھوڑا تھوڑا اتارا ہے'),
(5615, 76, 24, 'پھر آپ اپنے رب کے حکم کا انتظار کیاکر یں اوران میں سے کسی بدکار یا ناشکرے کا کہا نہ مانا کریں'),
(5616, 76, 25, 'اوراپنے رب کا نام صبح اور شام یاد کیا کریں'),
(5617, 76, 26, 'اورکچھ حصہ رات میں بھی اس کو سجدہ کیجیئے اوررات میں دیر تک اس کی تسبیح کیجیئے'),
(5618, 76, 27, 'بے شک یہ لوگ دنیا کو چاہتے ہیں اور اپنے پیچھے ایک بھاری دن کو چھوڑتے ہیں'),
(5619, 76, 28, 'ہم ہی نے انہیں پیدا کیااور ان کے جوڑ مضبوط کر دیئے اورجب ہم چاہیں ان جیسےان کے بدلے اور لا سکتے ہیں'),
(5620, 76, 29, 'بے شک یہ ایک نصیحت ہے پس جو کوئی چاہے اپنے رب کی طرف راستہ اختیار کرے'),
(5621, 76, 30, 'اور تم جب ہی چاہو گے جب الله چاہے گا بے شک الله سب کچھ جاننے والا حکمت والا ہے'),
(5622, 76, 31, 'جس کو چاہتا ہے اپنی رحمت میں داخل کرتا ہے اورظالموں کے لیے تو اس نے دردناک عذاب تیار کر رکھا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'ان ہواؤں کی قسم ہے جو نفع پہنچانے کے لیے بھیجی جاتی ہیں'),
(5624, 77, 2, 'پھر ان ہواؤں کی جو تندی سے چلتی ہیں'),
(5625, 77, 3, 'اوران ہواؤں کی جو بادلوں کو اٹھا کر پھیلاتی ہیں'),
(5626, 77, 4, 'پھر ان ہواؤں کی جو بادلوں کو متفرق کر دیتی ہیں'),
(5627, 77, 5, 'پھر ان ہواؤں کی جو (دل میں) الله کی یاد کا القا کرتی ہیں'),
(5628, 77, 6, 'الزام اتارنے یا ڈرانے کے لیے'),
(5629, 77, 7, 'جن کا تم سے وعدہ کیا جاتا ہے وہ ضرور ہونے والی ہے'),
(5630, 77, 8, 'پس جب ستارے مٹا دیئے جائیں گے'),
(5631, 77, 9, 'اورجب آسمان پھٹ جائیں گے'),
(5632, 77, 10, 'اورجب پہاڑ اڑائے جائیں گے'),
(5633, 77, 11, 'اور جب رسول وقت معین پرجمع کیے جائیں گے'),
(5634, 77, 12, 'کس دن کے لیے تاخیر کی گئی تھی'),
(5635, 77, 13, 'فیصلہ کے دن کے لیے'),
(5636, 77, 14, 'اور آپکو کیا معلوم کہ فیصلہ کا دن کیا ہے'),
(5637, 77, 15, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5638, 77, 16, 'کیا ہم نے پہلوں کو ہلاک نہیں کر ڈالا'),
(5639, 77, 17, 'پھر ہم ان کے پیچھے دوسروں کو چلائیں گے'),
(5640, 77, 18, 'مجرموں کے ساتھ ہم ایسا ہی برتاؤ کرتے ہیں'),
(5641, 77, 19, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5642, 77, 20, 'کیا ہم نے تمہیں ایک ذلیل پانی سے نہیں پیدا کیا'),
(5643, 77, 21, 'پھر ہم نے اس کو ایک محفوظ ٹھکانے میں رکھ دیا'),
(5644, 77, 22, 'ایک معین اندازے تک'),
(5645, 77, 23, 'پھر ہم نے اندازہ لگایا تو ہم تو کیسے اچھے اندازہ لگانے والے ہیں'),
(5646, 77, 24, 'اس دن جھٹلانے والوں کے لیےتباہی ہے'),
(5647, 77, 25, 'کیا ہم نے زمین کو جمع کرنے والی نہیں بنایا'),
(5648, 77, 26, 'زندوں اور مردوں کو'),
(5649, 77, 27, 'اور ہم نے اس میں مضبوط اونچے اونچے پہاڑ رکھ دیئے اور ہم نے تمہیں میٹھا پانی پلایا'),
(5650, 77, 28, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5651, 77, 29, 'اس (دوزخ) کی طرف چلو جسے تم جھٹلایا کرتےتھے'),
(5652, 77, 30, 'ایک سائبان کی طرف چلو جسکے تین حصے ہیں'),
(5653, 77, 31, 'نہ وہ سایہ کرے اور نہ تپش سے بچائے'),
(5654, 77, 32, 'بے شک وہ محل جیسے انگارے پھینکے گی'),
(5655, 77, 33, 'گویا کہ وہ زرد اونٹ ہیں'),
(5656, 77, 34, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5657, 77, 35, 'یہ وہ دن ہے جس میں بات بھی نہ کر سکیں گے'),
(5658, 77, 36, 'اور نہ انہیں عذر کرنے کی اجازت ہو گی'),
(5659, 77, 37, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5660, 77, 38, 'یہ فیصلہ کا دن ہے ہم تمہیں اور پہلوں کو جمع کریں گے'),
(5661, 77, 39, 'پس اگر تمہارے پاس کوئی تدبیر ہے تو مجھ پر کر دیکھو'),
(5662, 77, 40, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5663, 77, 41, 'بے شک پرہیزگار ٹھنڈی چھاؤں اور چشموں میں ہوں گے'),
(5664, 77, 42, 'اور میووں میں جو وہ چاہیں گے'),
(5665, 77, 43, 'مزے سے کھاؤ اور پیئو ان کاموں کے بدلے جو تم کرتے رہے'),
(5666, 77, 44, 'بے شک ہم اسی طرح نیکو کاروں کو بدلہ دیتے ہیں'),
(5667, 77, 45, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5668, 77, 46, 'کھاؤ اور چند روز فائدہ اٹھاؤ بے شک تم مجرم ہو'),
(5669, 77, 47, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5670, 77, 48, 'اورجب ان سے کہا جاتا تھا کہ رکوع کرو تورکوع نہ کرتے تھے'),
(5671, 77, 49, 'اس دن جھٹلانے والوں کے لیے تباہی ہے'),
(5672, 77, 50, 'پس اس کے بعد کس بات پر ایمان لائیں گے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'کس چیز کی بابت وہ آپس میں سوال کرتے ہیں'),
(5674, 78, 2, 'اس بڑی خبر کے متعلق'),
(5675, 78, 3, 'جس میں وہ اختلاف کر رہے ہیں'),
(5676, 78, 4, 'ہرگز ایسا نہیں عنقریب وہ جان لیں گے'),
(5677, 78, 5, 'پھر ہر گز ایسا نہیں عنقریب وہ جان لیں گے'),
(5678, 78, 6, 'کیا ہم نے زمین کو فرش نہیں بنایا'),
(5679, 78, 7, 'اور پہاڑوں کو میخیں'),
(5680, 78, 8, 'اور ہم نے تمہیں جوڑے جوڑے پیدا کیا'),
(5681, 78, 9, 'اور تمہاری نیند کو راحت کا باعث بنایا'),
(5682, 78, 10, 'اور رات کو پردہ پوش بنایا'),
(5683, 78, 11, 'اور دن کو روزی کمانے کے لیے بنایا'),
(5684, 78, 12, 'اور ہم نے تمہارے اوپر سات سخت (آسمان) بنائے'),
(5685, 78, 13, 'اور ایک جگمگاتا ہوا چراغ بنایا'),
(5686, 78, 14, 'اور ہم نے بادلوں سے زور کا پانی اتارا'),
(5687, 78, 15, 'تاکہ ہم اس سے اناج اور گھاس اگائیں'),
(5688, 78, 16, 'اور گھنے باغ اگائیں'),
(5689, 78, 17, 'بے شک فیصلہ کا دن معین ہو چکا ہے'),
(5690, 78, 18, 'جس دن صور میں پھونکا جائے گا پھر تم گروہ درگروہ چلے آؤ گے'),
(5691, 78, 19, 'اور آسمان کھولا جائے گا تو (اس میں) دروازے ہوجائیں گے'),
(5692, 78, 20, 'اور پہاڑ اڑائے جائیں گے توریت ہو جائیں گے'),
(5693, 78, 21, 'بے شک دوزخ گھات میں لگی ہے'),
(5694, 78, 22, 'سرشکوں کے لیے ٹھکانہ ہے'),
(5695, 78, 23, 'کہ وہ اس میں ہمیشہ پڑے رہیں گے'),
(5696, 78, 24, 'نہ وہاں کسی ٹھنڈک کا مزہ چکھیں گے اور نہ کسی پینے کی چیز کا'),
(5697, 78, 25, 'مگر گرم پانی اور بہتی پیپ'),
(5698, 78, 26, 'پوراپورا بدلہ ملے گا'),
(5699, 78, 27, 'بے شک وہ حساب کی امید نہ رکھتے تھے'),
(5700, 78, 28, 'اور ہماری آیتوں کوبہت جھٹلایا کرتے تھے'),
(5701, 78, 29, 'اور ہم نے ہر چیز کو کتاب میں شمار کر رکھا ہے'),
(5702, 78, 30, 'پس چکھو سو ہم تمہارے لیے عذاب ہی زیادہ کرتے رہیں گے'),
(5703, 78, 31, 'بے شک پرہیزگاروں کے لیے کامیابی ہے'),
(5704, 78, 32, 'باغ اور انگور'),
(5705, 78, 33, 'اور نوجوان ہم عمر عورتیں'),
(5706, 78, 34, 'اور پیالے چھلکتے ہوئے'),
(5707, 78, 35, 'نہ وہاں بیہودہ باتیں سنیں گے اور نہ جھوٹ'),
(5708, 78, 36, 'آپ کے رب کی طرف سے حسب اعمال بدلہ عطا ہوگا'),
(5709, 78, 37, 'جو آسمانوں اور زمین کا رب ہے اور جو کچھ ان کے درمیان ہے بڑامہربان کہ وہ اس سے بات نہیں کر سکیں گے'),
(5710, 78, 38, 'جس دن جبرائیل اور سب فرشتے صف باندھ کر کھڑے ہوں گےکوئی نہیں بولے گا مگر وہ جس کو رحمنٰ اجازت دے گا اور وہ بات ٹھیک کہے گا'),
(5711, 78, 39, 'یہ یقینی دن ہے پس جو چاہے اپنے رب کے پاس ٹھکانا بنا لے'),
(5712, 78, 40, 'بے شک ہم نے تمہیں ایک عنقریب آنے والے عذاب سے ڈرایا ہے جس دن آدمی دیکھے گا جو کچھ اس کے ہاتھوں نے آگے بھیجا تھا اور کافر کہے گا اے کاش میں مٹی ہو گیا ہوتا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'جورڑوں میں گھس کر نکالنے والوں کی قسم ہے'),
(5714, 79, 2, 'اور بند کھولنے والوں کی'),
(5715, 79, 3, 'اورتیزی سے تیرنے والوں کی'),
(5716, 79, 4, 'پھر دوڑ کر آگے بڑھ جانے والوں کی'),
(5717, 79, 5, 'پھر ہر امر کی تدبیر کرنے والوں کی'),
(5718, 79, 6, 'جس دن کانپنے والی کانپے گی'),
(5719, 79, 7, 'اس کے پیچھے آنے والی پیچھے آئے گی'),
(5720, 79, 8, 'کئی دل اس دن دھڑک رہے ہوں گے'),
(5721, 79, 9, 'ان کی آنکھیں جھکی ہوئی ہوں گی'),
(5722, 79, 10, 'وہ کہتے ہیں کیا ہم پہلی حالت میں لوٹائے جائیں گے'),
(5723, 79, 11, 'کیا جب ہم بوسیدہ ہڈیاں ہوجائیں گے'),
(5724, 79, 12, 'کہتے ہیں کہ یہ تو اس وقت خسارہ کا لوٹنا ہوگا'),
(5725, 79, 13, 'پھر وہ واقعہ صرف ایک ہی ہیبت ناک آواز ہے'),
(5726, 79, 14, 'پس وہ اسی وقت میدان میں آ موجود ہوں گے'),
(5727, 79, 15, 'کیا آپ کو موسیٰ کا حال معلوم ہوا ہے'),
(5728, 79, 16, 'جب کہ مقدس وادی طویٰ میں اس کے رب نے اسےپکارا'),
(5729, 79, 17, 'فرعون کے پاس جاؤ کیونکہ اس نے سرکشی کی ہے'),
(5730, 79, 18, 'پس کہو کیا تیری خواہش ہے کہ تو پاک ہو'),
(5731, 79, 19, 'اور میں تجھے تیرے رب کی طرف راہ بتاؤں کہ تو ڈرے'),
(5732, 79, 20, 'پس اس نے اس کو بڑی نشانی دکھائی'),
(5733, 79, 21, 'تو اس نے جھٹلایا اور نافرمانی کی'),
(5734, 79, 22, 'پھر کوشش کرتا ہوا واپس لوٹا'),
(5735, 79, 23, 'پھر اس نے سب کو جمع کیا پھر پکارا'),
(5736, 79, 24, 'پھر کہا کہ میں تمہارا سب سے برتر رب ہوں'),
(5737, 79, 25, 'پھر الله نے اس کو آخرت اور دنیا کے عذاب میں پکڑ لیا'),
(5738, 79, 26, 'بے شک اس میں اس کے لیے عبرت ہے جو ڈرتا ہے'),
(5739, 79, 27, 'کیا تمہارا بنانا بڑی بات ہے یا آسمان کا جس کو ہم نے بنایا ہے'),
(5740, 79, 28, 'ا سکی چھت بلند کی پھر اس کو سنوارا'),
(5741, 79, 29, 'اور اس کی رات اندھیری کی اور اس کے دن کو ظاہر کیا'),
(5742, 79, 30, 'اور اس کے بعد زمین کو بچھا دیا'),
(5743, 79, 31, 'اس سے اس کا پانی اور اس کا چارا نکالا'),
(5744, 79, 32, 'او رپہاڑوں کو خوب جما دیا'),
(5745, 79, 33, 'تمہارے لیے اور تمہارے چار پایوں کے لیے سامان حیات ہے'),
(5746, 79, 34, 'پس جب وہ بڑا حادثہ آئے گا'),
(5747, 79, 35, 'جس دن انسان اپنے کیے کو یاد کرے گا'),
(5748, 79, 36, 'اور ہر دیکھنے والے کے لیے دوزخ سامنے لائی جائے گی'),
(5749, 79, 37, 'سو جس نے سرکشی کی'),
(5750, 79, 38, 'اور دنیا کی زندگی کو ترجیح دی'),
(5751, 79, 39, 'سو بے شک اس کا ٹھکانا دوزخ ہی ہے'),
(5752, 79, 40, 'اور لیکن جو اپنے رب کے سامنے کھڑا ہونے سے ڈرتارہا اور اس نے اپنے نفس کو بری خواہش سے روکا'),
(5753, 79, 41, 'سو بے شک اس کا ٹھکانا بہشت ہی ہے'),
(5754, 79, 42, 'آپ سے قیامت کی بابت پوچھتے ہیں کہ اس کا قیام کب ہوگا'),
(5755, 79, 43, 'آپ کو اس کےذکر سے کیا واسطہ'),
(5756, 79, 44, 'اس کے علم کی انتہا آپ کے رب ہی کی طرف ہے'),
(5757, 79, 45, 'بے شک آپ تو صرف اس کو ڈرانے والے ہیں جو اس سے ڈرتا ہے'),
(5758, 79, 46, 'جس دن اسے دیکھ لیں گے (تو یہی سمجھیں گے کہ دنیا میں) گویا ہم ایک شام یا اس کی صبح تک ٹھیرے تھے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'پیغمبر چین بجیں ہوئے اور منہ موڑ لیا'),
(5760, 80, 2, 'کہ ان کے پاس ایک اندھا آیا'),
(5761, 80, 3, 'اور آپ کو کیا معلوم کہ شاید وہ پاک ہوجائے'),
(5762, 80, 4, 'یا وہ نصیحت پکڑ لے تو اس کو نصیحت نفع دے'),
(5763, 80, 5, 'لیکن وہ جو پروا نہیں کرتا'),
(5764, 80, 6, 'سو آپ کے لیے توجہ کرتے ہیں'),
(5765, 80, 7, 'حالانکہ آپ پر اس کےنہ سدھرنے کا کوئی الزام نہیں'),
(5766, 80, 8, 'اور لیکن جو آپ کے پاس دوڑتا ہوا آیا'),
(5767, 80, 9, 'اور وہ ڈر رہا ہے'),
(5768, 80, 10, 'تو آپ اس سے بے پروائی کرتے ہیں'),
(5769, 80, 11, 'ایسا نہیں چاہیئے بے شک یہ تو ایک نصیحت ہے'),
(5770, 80, 12, 'پس جو چاہے اس کو یاد کرے'),
(5771, 80, 13, 'وہ عزت والے صحیفوں میں ہے'),
(5772, 80, 14, 'جو بلند مرتبہ اور پاک ہیں'),
(5773, 80, 15, 'ان لکھنے والوں کے ہاتھوں میں'),
(5774, 80, 16, 'جو بڑے بزرگ نیکو کار ہیں'),
(5775, 80, 17, 'انسان پر خدا کی مار وہ کیسا ناشکرا ہے'),
(5776, 80, 18, 'اس نے کس چیز سے اس کو بنایا'),
(5777, 80, 19, 'ایک بوند سے اس کوبنایا پھر اس کا اندزہ ٹھیرایا'),
(5778, 80, 20, 'پھراس پر راستہ آسان کر دیا'),
(5779, 80, 21, 'پھر اس کو موت دی پھر اس کو قبر میں رکھوایا'),
(5780, 80, 22, 'پھر جب چاہے گا اٹھا کر کھڑا کرے گا'),
(5781, 80, 23, 'ایسا نہیں چاہیئے اس نے تعمیل نہیں کی جو اس کو حکم دیا تھا'),
(5782, 80, 24, 'پس انسان کو اپنے کھانے کی طرف غور کرنا چاہیئے'),
(5783, 80, 25, 'کہ ہم نے اوپر سے مینہ برسایا'),
(5784, 80, 26, 'پھر ہم نے زمین کو چیر کر پھاڑا'),
(5785, 80, 27, 'پھر ہم نے اس میں اناج ا گایا'),
(5786, 80, 28, 'اورانگور اور ترکاریاں'),
(5787, 80, 29, 'اور زیتون اور کھجور'),
(5788, 80, 30, 'اور گھنے باغ'),
(5789, 80, 31, 'اور میوے اور گھاس'),
(5790, 80, 32, 'تمہارے لیے اور تمہارے چار پایوں کے لیے سامان حیات'),
(5791, 80, 33, 'پھر جس وقت کانوں کا بہرا کرنے والا شور برپا ہوگا'),
(5792, 80, 34, 'جس دن آدمی اپنے بھائی سے بھاگے گا'),
(5793, 80, 35, 'اور اپنی ماں اور باپ سے'),
(5794, 80, 36, 'اور اپنی بیوی اوراپنے بیٹوں سے'),
(5795, 80, 37, 'ہر شخص کی ایسی حالت ہوگی جو اس کو اوروں کی طرف سے بے پروا کر دے گی'),
(5796, 80, 38, 'اورکچھ چہرے اس دن چمک رہے ہوں گے'),
(5797, 80, 39, 'ہنستے ہوئے خوش و خرم'),
(5798, 80, 40, 'اور کچھ چہرے اس دن ایسے ہوں گے کہ ان پر گرد پڑی ہو گی'),
(5799, 80, 41, 'ان پر سیاہی چھا رہی ہو گی'),
(5800, 80, 42, 'یہی لوگ ہیں منکر نافرمان');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'جب سورج کی روشنی لپیٹی جائے'),
(5802, 81, 2, 'اور جب ستارے گر جائیں'),
(5803, 81, 3, 'اور جب پہاڑ چلائے جائیں'),
(5804, 81, 4, 'اور جب دس مہینے کی گابھن اونٹنیاں چھوڑ دی جائیں'),
(5805, 81, 5, 'اور جب جنگلی جانور اکھٹے ہوجائیں'),
(5806, 81, 6, 'اور جب سمندر جوش دیئے جائیں'),
(5807, 81, 7, 'اور جب جانیں جسموں سے ملائی جائیں'),
(5808, 81, 8, 'اور جب زندہ درگور لڑکی سے پوچھا جائے'),
(5809, 81, 9, 'کہ کس گناہ پر ماری گئی تھی'),
(5810, 81, 10, 'اور جب اعمال نامے کھل جائیں'),
(5811, 81, 11, 'اور آسمان کا پوست اتارا جائے'),
(5812, 81, 12, 'اورجب دوزخ دھکائی جائے'),
(5813, 81, 13, 'اورجب جنت قریب لائی جائے'),
(5814, 81, 14, 'تو ہر شخص جان لے گا کہ وہ کیا لے کر آیا ہے'),
(5815, 81, 15, 'پس میں قسم کھاتا ہوں پیچھے ہٹنے والے'),
(5816, 81, 16, 'سیدھے چلنے والے غیب ہو جانے والے ستارو ں کی'),
(5817, 81, 17, 'اور قسم ہے رات کی جب وہ جانے لگے'),
(5818, 81, 18, 'اور قسم ہے صبح کی جب وہ آنے لگے'),
(5819, 81, 19, 'بے شک یہ قرآن ایک معزز رسول کا لایا ہوا ہے'),
(5820, 81, 20, 'جو بڑا طاقتور ہے عرش کے مالک کے نزدیک بڑے رتبہ والا ہے'),
(5821, 81, 21, 'وہاں کا سردار امانت دار ہے'),
(5822, 81, 22, 'اور تمہارا رفیق کوئی دیوانہ نہیں ہے'),
(5823, 81, 23, 'اور اس نے اس کو کُھلے کنارے پر دیکھا بھی ہے'),
(5824, 81, 24, 'اور وہ غیب کی باتوں پر بخیل نہیں ہے'),
(5825, 81, 25, 'اور وہ کسی شیطان مردود کا قول نہیں ہے'),
(5826, 81, 26, 'پس تم کہاں چلے جا رہے ہو'),
(5827, 81, 27, 'یہ تو جہان بھرکے لیے نصیحت ہی نصیحت ہے'),
(5828, 81, 28, 'اس کے لیے جو تم میں سے سیدھا چلنا چاہے'),
(5829, 81, 29, 'اور تم توجب ہی چاہو گے کہ جب الله چاہے گا جو تمام جہان کا رب ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'جب آسمان پھٹ جائے'),
(5831, 82, 2, 'اورجب ستارے جھڑ جائیں'),
(5832, 82, 3, 'اور جب سمندر ابل پڑیں'),
(5833, 82, 4, 'اور جب قبریں اکھاڑ دی جائیں'),
(5834, 82, 5, 'تب ہر شخص جان لے گا کہ کیا آگے بھیجا اور کیا پیچھے چھوڑ آیا'),
(5835, 82, 6, 'اے انسان تجھے اپنے رب کریم کے بارے میں کس چیز نے مغرور کر دیا'),
(5836, 82, 7, 'جس نے تجھے پیدا کیا پھر تجھے ٹھیک کیا پھر تجھے برابر کیا'),
(5837, 82, 8, 'جس صورت میں چاہا تیرے اعضا کو جوڑ دیا'),
(5838, 82, 9, 'نہیں نہیں بلکہ تم جزا کو نہیں مانتے'),
(5839, 82, 10, 'اور بے شک تم پر محافظ ہیں'),
(5840, 82, 11, 'عزت والے اعمال لکھنے والے'),
(5841, 82, 12, 'وہ جانتے ہیں جو تم کرتے ہو'),
(5842, 82, 13, 'بےشک نیک لوگ نعمت میں ہوں گے'),
(5843, 82, 14, 'اور بے شک نافرمان دوزخ میں ہوں گے'),
(5844, 82, 15, 'انصاف کے دن اس میں داخل ہوں گے'),
(5845, 82, 16, 'اور وہ اس سے کہیں جانے نہ پائیں گے'),
(5846, 82, 17, 'اورتجھے کیا معلوم انصاف کا دن کیا ہے'),
(5847, 82, 18, 'پھر تجھے کیا خبر کہ انصاف کا دن کیا ہے'),
(5848, 82, 19, 'جس دن کوئی کسی کے لیے کچھ بھی نہ کر سکے گااور اس دن الله ہی کا حکم ہوگا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'کم تولنے والوں کے لیے تباہی ہے'),
(5850, 83, 2, 'وہ لوگ کہ جب لوگوں سے ماپ کر لیں تو پورا کریں'),
(5851, 83, 3, 'اور جب ان کو ماپ کر یا تول کردیں توگھٹا کر دیں'),
(5852, 83, 4, 'کیا وہ خیال نہیں کرتے کہ وہ اٹھائے جائیں گے'),
(5853, 83, 5, 'اس بڑے دن کے لیے'),
(5854, 83, 6, 'جس دن سب لوگ رب العالمین کے سامنے کھڑے ہو ں گے'),
(5855, 83, 7, 'ہر گز ایسا نہیں چاہیے بے شک نافرمانوں کے اعمال نامے سجین میں ہیں'),
(5856, 83, 8, 'اور آپ کو کیا خبر کہ سجین کیا ہے'),
(5857, 83, 9, 'ایک دفتر ہے جس میں لکھا جاتا ہے'),
(5858, 83, 10, 'اس دن جھٹلانے والوں کے لئے تباہی ہے'),
(5859, 83, 11, 'وہ جو انصاف کے دن کو جھٹلاتے ہیں'),
(5860, 83, 12, 'اور اس کو وہی جھٹلاتا ہے جو حد سے بڑھا ہوا گناہگار ہے'),
(5861, 83, 13, 'جب اس پر ہماری آیتیں پڑھی جاتی ہیں تو کہتا ہے پہلوں کی کہانیاں ہیں'),
(5862, 83, 14, 'ہر گز نہیں بلکہ ان کے (بڑے) کاموں سے ان کے دلو ں پر زنگ لگ گیا ہے'),
(5863, 83, 15, 'ہر گز نہیں بے شک وہ اپنے رب سے اس دن روک دیئے جائیں گے'),
(5864, 83, 16, 'پھر بے شک وہ دوزخ میں گرنے والے ہیں'),
(5865, 83, 17, 'پھر کہا جائے گا کہ یہی ہے وہ جسے تم جھٹلاتے تھے'),
(5866, 83, 18, 'ہر گز نہیں بےشک نیکوں کے اعمال نامے علییں میں ہیں'),
(5867, 83, 19, 'اور آپ کو کیا خبر کہ علیّین کیا ہے'),
(5868, 83, 20, 'ایک دفتر ہے جس میں لکھا جاتا ہے'),
(5869, 83, 21, 'اسے مقرب فرشتے دیکھتے ہیں'),
(5870, 83, 22, 'بے شک نیکو کار جنت میں ہوں گے'),
(5871, 83, 23, 'تختوں پر بیٹھے دیکھ رہے ہوں گے'),
(5872, 83, 24, 'آپ ان کے چہروں میں نعمت کی تازگی معلوم کریں گے'),
(5873, 83, 25, 'ان کو خالص شراب مہر لگی ہوئی پلائی جائے گی'),
(5874, 83, 26, 'اس کی مہر مشک کی ہو گی اور رغبت کرنے والوں کو اس کی رغبت کرنی چاہیئے'),
(5875, 83, 27, 'اور اس میں تسنیم ملی ہو گی'),
(5876, 83, 28, 'وہ ایک چشمہ ہے اس میں سےمقرب پئیں گے'),
(5877, 83, 29, 'بے شک نافرمان (دنیا میں) ایمان داروں سے ہنسی کیا کرتے تھے'),
(5878, 83, 30, 'اور جب ان کے پاس سے گزرتے تو آپس میں آنکھ سے اشارے کرتے تھے'),
(5879, 83, 31, 'اور جب اپنے گھر والوں کے پاس لوٹ کر جاتے تو ہنستے ہوئے جاتے تھے'),
(5880, 83, 32, 'اور جب ان کو دیکھتے تو کہتے بے شک یہی گمراہ ہیں'),
(5881, 83, 33, 'حالانکہ وہ ان پر نگہبان بنا کر نہیں بھیجے گئے تھے'),
(5882, 83, 34, 'پس آج وہ لوگ جو ایمان لائے کفار سے ہنس رہے ہوں گے'),
(5883, 83, 35, 'تختوں پر بیٹھے دیکھ رہے ہوں گے'),
(5884, 83, 36, 'آیا کافروں کو بدلہ دیا گیا ہے ان اعمال کا جو وہ کیا کرتے تھے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'جب آسمان پھٹ جائے گا'),
(5886, 84, 2, 'اور اپنے رب کا حکم سن لے گا اور وہ اسی لائق ہے'),
(5887, 84, 3, 'اور جب زمین پھیلا دی جائے گی'),
(5888, 84, 4, 'اور جو کچھ اس میں ہے ڈال دے گی اور خالی ہو جائے گی'),
(5889, 84, 5, 'اور اپنے رب کا حکم سن لے گی اور وہ اسی لائق ہے'),
(5890, 84, 6, 'اے انسان تو اپنے رب کے پاس پہنچنے تک کام میں کوشش کر رہا ہے پھر اس سے جا ملے گا'),
(5891, 84, 7, 'پھر جس کا اعمال نامہ اس کے دائیں ہاتھ میں دیا گیا'),
(5892, 84, 8, 'تو اس سے آسانی کے ساتھ حساب لیا جائے گا'),
(5893, 84, 9, 'اور وہ اپنے اہل و عیال میں خوش واپس آئے گا'),
(5894, 84, 10, 'اور لیکن جس کو نامہٴ اعمال پیٹھ پیچھے سے دیا گیا'),
(5895, 84, 11, 'تو وہ موت کو پکارے گا'),
(5896, 84, 12, 'اور دوزخ میں داخل ہوگا'),
(5897, 84, 13, 'بے شک وہ اپنے اہل و عیال میں بڑا خوش و خرم تھا'),
(5898, 84, 14, 'بے شک اس نے سمجھ لیا تھا کہ ہر گز نہ لوٹ کر جائے گا'),
(5899, 84, 15, 'کیوں نہیں بے شک اس کا رب تو اس کو دیکھ رہا تھا'),
(5900, 84, 16, 'پس شام کی سرخی کی قسم ہے'),
(5901, 84, 17, 'اور رات کی اور جو کچھ اس نے سمیٹا'),
(5902, 84, 18, 'اور چاند کی جب کہ وہ پورا ہوجائے'),
(5903, 84, 19, 'کہ تمہیں ایک منزل سے دوسری منزل پر چڑھنا ہوگا'),
(5904, 84, 20, 'پھر انہیں کیا ہو گیا کہ ایمان نہیں لاتے'),
(5905, 84, 21, 'اور جب ان پر قرآن پڑھا جائے تو سجدہ نہیں کرتے'),
(5906, 84, 22, 'بلکہ جو لوگ منکر ہیں جھٹلاتے ہیں'),
(5907, 84, 23, 'اور الله خوب جانتا ہے وہ جو (دل میں) محفوظ رکھتے ہیں'),
(5908, 84, 24, 'پس انہیں دردناک عذاب کی خوشخبری دے دو'),
(5909, 84, 25, 'مگر جو لوگ ایمان لائے اورانہوں نے نیک عمل کیے ان کے لیے بے انتہا اجر ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'آسمان کی قسم ہے جس میں برج ہیں'),
(5911, 85, 2, 'اوراس دن کی جس کا وعدہ کیا گیا ہے'),
(5912, 85, 3, 'اور اس دن کی جو حاضر ہوتا ہے اور اس کی جس کے پاس حاضر ہوتے ہیں'),
(5913, 85, 4, 'خندقوں والے ہلاک ہوئے'),
(5914, 85, 5, 'جس میں آگ تھی بہت ایندھن والی'),
(5915, 85, 6, 'جب کہ وہ اس کے کنارو ں پر بیٹھے ہوئے تھے'),
(5916, 85, 7, 'اور وہ ایمانداروں سے جو کچھ کر رہے تھے اس کو دیکھ رہے تھے'),
(5917, 85, 8, 'اور ان سے اسی کا توبدلہ لے رہے تھے کہ وہ الله زبردست خوبیوں والے پر ایمان لائے تھے'),
(5918, 85, 9, 'وہ کہ جس کے قبضہ میں آسمان اور زمین ہیں اور الله ہر چیز پر شاہد ہے'),
(5919, 85, 10, 'بے شک جنہوں نے ایمان دار مردوں اور ایمان دار عورتوں کو ستایا پھر توبہ نہ کی تو ان کے لیے جہنم کا عذاب ہے اور ان کے لیے جلانے والا عذاب ہے'),
(5920, 85, 11, 'بے شک جو لوگ ایمان لائے اور انہوں نے نیک کام بھی کیے ان کے لیے باغ ہیں جن کے نیچے نہریں بہتی ہوں گی یہی بڑی کامیابی ہے'),
(5921, 85, 12, 'بے شک تیرے رب کی پکڑ بھی سخت ہے'),
(5922, 85, 13, 'بے شک وہی پہلے پیدا کرتا ہے اور دوبارہ پیدا کرے گا'),
(5923, 85, 14, 'اور وہی ہے بخشنے والا محبت کرنے والا'),
(5924, 85, 15, 'عرش کا مالک بڑی شان والا'),
(5925, 85, 16, 'جو چاہے کرنے والا'),
(5926, 85, 17, 'کیا آپ کے پاس لشکروں کا حال پہنچا'),
(5927, 85, 18, 'فرعون اور ثمود کے'),
(5928, 85, 19, 'بلکہ منکر تو جھٹلانے میں لگے ہوئے ہیں'),
(5929, 85, 20, 'اور الله ہر طرف سے ان کو گھیرے ہوئے ہے'),
(5930, 85, 21, 'بلکہ وہ قرآن ہے بڑی شان والا'),
(5931, 85, 22, 'لوح محفوظ میں (لکھا ہوا ہے)');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'آسمان کی قسم ہے اور رات کو آنے والے کی'),
(5933, 86, 2, 'اور آپ کو کیا معلوم رات کو آنے والا کیا ہے'),
(5934, 86, 3, 'وہ چمکتا ہوا ستارہ ہے'),
(5935, 86, 4, 'ایسی کوئی بھی جان نہیں کہ جس پر ایک محافظ مقرر نہ ہو'),
(5936, 86, 5, 'پس انسان کو دیکھنا چاہیے کہ وہ کس چیز سے پیدا کیا گیا ہے'),
(5937, 86, 6, 'ایک اچھلتے ہوئے پانی سے پیدا کیا گیا ہے'),
(5938, 86, 7, 'جو پیٹھ اور سینے کی ہڈیوں کے درمیان سے نکلتا ہے'),
(5939, 86, 8, 'بے شک وہ اس کے لوٹانے پر قادر ہے'),
(5940, 86, 9, 'جس دن بھید ظاہر کیے جائیں گے'),
(5941, 86, 10, 'تو اس کے لیے نہ کوئی طاقت ہو گی اور نہ کوئی مددگار'),
(5942, 86, 11, 'آسمان اور بارش والے کی قسم ہے'),
(5943, 86, 12, 'اور زمین کی جو پھٹ جاتی ہے'),
(5944, 86, 13, 'بے شک قرآن قطعی بات ہے'),
(5945, 86, 14, 'اوروہ ہنسی کی بات نہیں ہے'),
(5946, 86, 15, 'بے شک وہ ایک تدبیر کر رہے ہیں'),
(5947, 86, 16, 'اور میں بھی ایک تدبیر کر رہا ہوں'),
(5948, 86, 17, 'پس کافرو ں کو تھوڑے دنوں کی مہلت دے دو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'اپنے رب کے نام کی تسبیح کیا کر جو سب سے اعلیٰ ہے'),
(5950, 87, 2, 'وہ جس نے پیدا کیا پھر ٹھیک بنایا'),
(5951, 87, 3, 'اور جس نے اندازہ ٹھہرایا پھر راہ دکھائی'),
(5952, 87, 4, 'اور وہ جس نے چارہ نکالا'),
(5953, 87, 5, 'پھر اس کو خشک چورا سیاہ کر دیا'),
(5954, 87, 6, 'البتہ ہم آپ کو پڑھائیں گے پھر آپ نہ بھولیں گے'),
(5955, 87, 7, 'مگر جو الله چاہے بے شک وہ ہرظاہر اور چھپی بات کو جانتا ہے'),
(5956, 87, 8, 'اور ہم آپ کو آسان شریعت کے سمجھنے کی توفیق دیں گے'),
(5957, 87, 9, 'پس آپ نصیحت کیجیئے اگر نصیحت فائدہ دے'),
(5958, 87, 10, 'جو الله سے ڈرتا ہے وہ جلدی سمجھ جائے گا'),
(5959, 87, 11, 'اور اس سے بڑا بدنصیب الگ رہے گا'),
(5960, 87, 12, 'جو سخت آگ میں داخل ہوگا'),
(5961, 87, 13, 'پھر اس میں نہ تو مرے گا اور نہ جیئے گا'),
(5962, 87, 14, 'بے شک وہ کامیاب ہوا جو پاک ہو گیا'),
(5963, 87, 15, 'اور اپنے رب کا نام یاد کیا پھر نماز پڑھی'),
(5964, 87, 16, 'بلکہ تم تودنیا کی زندگی کو ترجیح دیتے ہو'),
(5965, 87, 17, 'حالانکہ آخرت بہتر اور زیادہ پائدار ہے'),
(5966, 87, 18, 'بے شک یہی پہلے صحیفوں میں ہے'),
(5967, 87, 19, '(یعنی) ابراھیم اور موسیٰ کے صحیفوں میں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'کیا آپ کے پاس سب پرچھا جانے والی (قیامت) کا حال پہنچا'),
(5969, 88, 2, 'کئی چہرو ں پر اس دن ذلت برس رہی ہوگی'),
(5970, 88, 3, 'محنت کرنے والے تھکنے والے'),
(5971, 88, 4, 'دھکتی ہوئی آگ میں کریں گے'),
(5972, 88, 5, 'انہیں ابلتے ہوئے چشمے سے پلایا جائے گا'),
(5973, 88, 6, 'ان کے لیے کوئی کھانا سوائے کانٹے دار جھاڑی کے نہ ہوگا'),
(5974, 88, 7, 'جو نہ فربہ کرتی ہے اور نہ بھوک کود ور کرتی ہے'),
(5975, 88, 8, 'کئی منہ اس دن ہشاش بشاش ہوں گے'),
(5976, 88, 9, 'اپنی کوشش سے خوش ہوں گے'),
(5977, 88, 10, 'اونچے باغ میں ہوں گے'),
(5978, 88, 11, 'وہاں کوئی لغو بات نہیں سنیں گے'),
(5979, 88, 12, 'وہاں ایک چشمہ جاری ہوگا'),
(5980, 88, 13, 'وہاں اونچے اونچے تخت ہوں گے'),
(5981, 88, 14, 'اور آبخورے سامنے چنے ہوئے'),
(5982, 88, 15, 'اورگاؤ تکیے قطار سے لگے ہوئے'),
(5983, 88, 16, 'اور مخملی فرش بچھے ہوئے'),
(5984, 88, 17, 'پھر کیا وہ اونٹوں کی طرف نہیں دیکھتے کہ کیسے بنائے گئے ہیں'),
(5985, 88, 18, 'اور آسمان کی طرف کہ کیسے بلند کیے گئے ہیں'),
(5986, 88, 19, 'اور پہاڑوں کی طرف کہ کیسے کھڑے کیے گئے ہیں'),
(5987, 88, 20, 'اور زمین کی طرف کہ کیسے بچھائی گئی ہے'),
(5988, 88, 21, 'پس آپ نصیحت کیجئے بے شک آپ تو نصیحت کرنے والے ہیں'),
(5989, 88, 22, 'آپ ان پر کوئی داروغہ نہیں ہیں'),
(5990, 88, 23, 'مگر جس نے منہ موڑا اورانکار کیا'),
(5991, 88, 24, 'سو اسے الله بہت بڑا عذاب دے گا'),
(5992, 88, 25, 'بےشک ہماری طرف ہی ان کو لوٹ کر آنا ہے'),
(5993, 88, 26, 'پھر ہمارےہی ذمہ ان کا حساب لینا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'فجر کی قسم ہے'),
(5995, 89, 2, 'اور دس راتوں کی'),
(5996, 89, 3, 'اور جفت اور طاق کی'),
(5997, 89, 4, 'اور رات کی جب وہ گزر جائے'),
(5998, 89, 5, 'ان چیزو ں کی قسم عقلمندوں کے واسطے معتبر ہے'),
(5999, 89, 6, 'کیا آپ نے نہیں دیکھا کہ آپ کے رب نے عاد کے ساتھ کیا سلوک کیا'),
(6000, 89, 7, 'جو نسل ارم سے ستونوں والے تھے'),
(6001, 89, 8, 'کہ ان جیسا شہرو ں میں پیدا نہیں کیا گیا'),
(6002, 89, 9, 'اور ثمود کے ساتھ جنہوں نے پتھروں کو وادی میں تراشا تھا'),
(6003, 89, 10, 'اور فرعون میخوں والوں کے ساتھ'),
(6004, 89, 11, 'ان سب نے ملک میں سرکشی کی'),
(6005, 89, 12, 'پھر انہوں نے بہت فساد پھیلایا'),
(6006, 89, 13, 'پھر ان پر تیرے رب نے عذاب کا کوڑا پھینکا'),
(6007, 89, 14, 'بے شک آپ کا رب تاک میں ہے'),
(6008, 89, 15, 'لیکن انسان تو ایسا ہے کہ جب اسے اس کا رب آزماتا ہے پھر اسے عزت اور نعمت دیتا ہے تو کہتا ہے کہ میرے رب نے مجھے عزت بخشی ہے'),
(6009, 89, 16, 'لیکن جب اسے آزماتا ہے پھر اس پر اس کی روزی تنگ کر تا ہے تو کہتا ہے میرے رب نے مجھے ذلیل کر دیا'),
(6010, 89, 17, 'ہرگز نہیں بلکہ تم یتیم کی عزت نہیں کرتے'),
(6011, 89, 18, 'اور نہ مسکین کو کھانا کھلانے کی ترغیب دیتے ہو'),
(6012, 89, 19, 'اور میت کا ترکہ سب سمیٹ کر کھا جاتے ہو'),
(6013, 89, 20, 'اور مال سے بہت زیادہ محبت رکھتے ہو'),
(6014, 89, 21, 'ہرگز نہیں جب زمین کوٹ کوٹ کر ریزہ ریزہ کر دی جائے گی'),
(6015, 89, 22, 'اور آپ کے رب کا (تخت) آجائے گا اور فرشتے بھی صف بستہ چلے آئيں گے'),
(6016, 89, 23, 'اوراس دن دوزخ لائی جائے گی اس دن انسان سمجھے گا اور اس وقت اس کو سمجھنا کیا فائدہ دے گا'),
(6017, 89, 24, 'کہے گا اے کاش میں اپنی زندگی کے لیے کچھ آگے بھیجتا'),
(6018, 89, 25, 'پس اس دن اس کا ساعذاب کوئی بھی نہ دے گا'),
(6019, 89, 26, 'اور نہ اس کے جکڑنے کے برابر کوئی جکڑنے والا ہو گا'),
(6020, 89, 27, '(ارشاد ہوگا) اے اطمینان والی روح'),
(6021, 89, 28, 'اپنے رب کی طرف لوٹ چل تو اس سے راضی وہ تجھ سے راضی'),
(6022, 89, 29, 'پس میرے بندو ں میں شامل ہو'),
(6023, 89, 30, 'اور میری جنت میں داخل ہو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'ا س شہر کی قسم ہے'),
(6025, 90, 2, 'حالانکہ آپ اس شہر میں مقیم ہیں'),
(6026, 90, 3, 'اورباپ کی اور اس کی اولاد کی قسم ہے'),
(6027, 90, 4, 'کہ بےشک ہم نے انسان کو مصیبت میں پیدا کیا ہے'),
(6028, 90, 5, 'کیا وہ خیال کرتا ہےکہ اس پر کوئی بھی ہرگز قابو نہ پا سکے گا'),
(6029, 90, 6, 'کہتا ہے کہ میں نے مال برباد کر ڈالا'),
(6030, 90, 7, 'کیا وہ خیال کرتا ہے کہ اسے کسی نے بھی نہیں دیکھا'),
(6031, 90, 8, 'کیا ہم نے اس کے لیے دو آنکھیں نہیں بنائیں'),
(6032, 90, 9, 'اور زبان اور دو ہونٹ'),
(6033, 90, 10, 'اور ہم نے اسے دونوں راستے دکھائے'),
(6034, 90, 11, 'پس وہ (دین کی) گھاٹی میں سے نہ ہو کر نکلا'),
(6035, 90, 12, 'اور آپ کو کیا معلوم کہ وہ گھاٹی کیا ہے'),
(6036, 90, 13, 'گردن کا چھوڑانا'),
(6037, 90, 14, 'یا بھوک کے دن میں کھلانا'),
(6038, 90, 15, 'کسی رشتہ دار یتیم کو'),
(6039, 90, 16, 'یا کسی خاک نشین مسکین کو'),
(6040, 90, 17, 'پھر وہ ان میں سے ہو جو ایمان لائے اور انہوں نے ایک دوسرے کو صبر کی وصیت کی اور رحم کرنے کی وصیت کی'),
(6041, 90, 18, 'یہی لوگ دائیں والے ہیں'),
(6042, 90, 19, 'اور جنہوں نے ہماری آیتوں سے انکار کیا وہی بائیں والے ہیں'),
(6043, 90, 20, 'انہیں پر چاروں طرف سے بند کی ہوئی آگ ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'سورج کی اور اس کی دھوپ کی قسم ہے'),
(6045, 91, 2, 'اور چاند کی جب وہ اس کے پیچھے آئے'),
(6046, 91, 3, 'اور دن کی جب وہ اس کو روشن کر دے'),
(6047, 91, 4, 'اور رات کی جب وہ اس کو ڈھانپ لے'),
(6048, 91, 5, 'اور آسمان کی اور اس کی جس نے اس کو بنایا'),
(6049, 91, 6, 'اور زمین اور اس کی جس نے اس کو بچھایا'),
(6050, 91, 7, 'اور جان کی اور اس کی جس نے اس کو درست کیا'),
(6051, 91, 8, 'پھر اس کو اس کی بدی اور نیکی سمجھائی'),
(6052, 91, 9, 'بے شک وہ کامیاب ہوا جس نے اپنی روح کو پاک کر لیا'),
(6053, 91, 10, 'اوربے شک وہ غارت ہوا جس نے اس کو آلودہ کر لیا'),
(6054, 91, 11, 'ثمود نے اپنی سرکشی سے (صالح کو) جھٹلایا تھا'),
(6055, 91, 12, 'جب کہ ان کا بڑا بدبخت اٹھا'),
(6056, 91, 13, 'پس ان سے الله کے رسول نے کہا کہ الله کی اونٹنی اور اس کے پانی پینے کی باری سے بچو'),
(6057, 91, 14, 'پس انہوں نے اس کو جھٹلایا اور اونٹنی کی کونچیں کاٹ ڈالیں پھر ان پر ان کے رب نےان کےگناہوں کے بدلے ہلاکت نازل کی پھر ان کو برابر کر دیا'),
(6058, 91, 15, 'اوراس نے اس کےانجام کی پروا نہ کی');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'رات کی قسم ہے جب کہ وہ چھاجائے'),
(6060, 92, 2, 'اور دن کی جبکہ وہ روشن ہو'),
(6061, 92, 3, 'اوراس کی قسم کہ جس نے نر و مادہ کوبنایا'),
(6062, 92, 4, 'بے شک تمہاری کوشش مختلف ہے'),
(6063, 92, 5, 'پھر جس نے دیا اور پرہیز گاری کی'),
(6064, 92, 6, 'اورنیک بات کی تصدیق کی'),
(6065, 92, 7, 'تو ہم اس کے لیے جنت کی راہیں آسان کر دیں گے'),
(6066, 92, 8, 'اورلیکن جس نے بخل کیا اوربے پرواہ رہا'),
(6067, 92, 9, 'اور نیک بات کو جھٹلایا'),
(6068, 92, 10, 'تو ہم اس کے لیے جہنم کی راہیں آسان کر دیں گے'),
(6069, 92, 11, 'اوراس کا مال اس کے کچھ بھی کام نہ آئے گا جب کہ وہ گھڑے میں گرے گا'),
(6070, 92, 12, 'بے شک ہمارے ذمے راہ دکھانا ہے'),
(6071, 92, 13, 'اور بے شک ہمارے ہی ہاتھ میں آخرت بھی اور دنیا بھی ہے'),
(6072, 92, 14, 'پس میں نے تمہیں بڑھکتی ہوئی آگ سے ڈرایا ہے'),
(6073, 92, 15, 'جس میں صرف وہی بد بخت داخل ہوگا'),
(6074, 92, 16, 'جس نے جھٹلایا اورمنہ موڑا'),
(6075, 92, 17, 'اوراس آگ سے وہ بڑا پرہیز گار دور رہے گا'),
(6076, 92, 18, 'جو اپنا مال دیتا ہے تاکہ وہ پاک ہو جائے'),
(6077, 92, 19, 'اور اس پر کسی کا کوئی احسان نہیں کہ جس کا بدلہ دیا جائے'),
(6078, 92, 20, 'وہ تو صرف اپنے سب سے برتر رب کی رضا مندی کے لیے دیتا ہے'),
(6079, 92, 21, 'اوروہ عنقریب خوش ہو جائے گا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'دن کی روشنی کی قسم ہے'),
(6081, 93, 2, 'اور رات کی جب وہ چھا جائے'),
(6082, 93, 3, 'آپ کے رب نے نہ آپ کو چھوڑا ہے اور نہ بیزار ہوا ہے'),
(6083, 93, 4, 'اور البتہ آخرت آپ کے لیے دنیا سے بہتر ہے'),
(6084, 93, 5, 'اور آپ کا رب آپ کو (اتنا) دے گا کہ آپ خوش ہو جائیں گے'),
(6085, 93, 6, 'کیا اس نے آپ کو یتیم نہیں پایا تھا پھر جگہ دی'),
(6086, 93, 7, 'اور آپ کو (شریعت سے) بے خبر پایا پھر (شریعت کا) راستہ بتایا'),
(6087, 93, 8, 'اوراس نے آپ کو تنگدست پایا پھر غنی کر دیا'),
(6088, 93, 9, 'پھر یتیم کو دبایا نہ کرو'),
(6089, 93, 10, 'اورسائل کو جھڑکا نہ کرو'),
(6090, 93, 11, 'اورہر حال میں اپنے رب کے احسان کا ذکر کیا کرو');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'کیا ہم نے آپ کا سینہ نہیں کھول دیا'),
(6092, 94, 2, 'اور کیا آپ سے آپ کا وہ بوجھ نہیں اتار دیا'),
(6093, 94, 3, 'جس نے آپ کی کمر جھکا دی تھی'),
(6094, 94, 4, 'اور ہم نے آپ کا ذکر بلند کر دیا'),
(6095, 94, 5, 'پس بے شک ہر مشکل کے ساتھ آسانی ہے'),
(6096, 94, 6, 'بے شک ہر مشکل کے ساتھ آسانی ہے'),
(6097, 94, 7, 'پس جب آپ (تبلیغ احکام سے) فارغ ہوں تو ریاضت کیجئے'),
(6098, 94, 8, 'اور اپنے رب کی طرف دل لگائیے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'انجیر اور زیتون کی قسم ہے'),
(6100, 95, 2, 'اور طور سینا کی'),
(6101, 95, 3, 'اور اس شہر (مکہ) کی جو امن والا ہے'),
(6102, 95, 4, 'بے شک ہم نے انسان کو بڑے عمدہ انداز میں پیدا کیا ہے'),
(6103, 95, 5, 'پھر ہم نے اسے سب سے نیچے پھینک دیا ہے'),
(6104, 95, 6, 'مگر جو ایمان لائے اور نیک کام کئے سو ان کے لیے تو بے انتہا بدلہ ہے'),
(6105, 95, 7, 'پھر اس کے بعد آپ کو قیامت کے معاملہ میں کون جھٹلا سکتا ہے'),
(6106, 95, 8, 'کیا الله سب حاکموں سے بڑا حاکم نہیں (ضرور ہے)');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'اپنے رب کے نام سے پڑھیئے جس نے سب کو پیدا کیا'),
(6108, 96, 2, 'انسان کو خون بستہ سے پیدا کیا'),
(6109, 96, 3, 'پڑھیئے اور آپ کا رب سب سے بڑھ کر کرم والا ہے'),
(6110, 96, 4, 'جس نے قلم سے سکھایا'),
(6111, 96, 5, 'انسان کو سکھایا جو وہ نہ جانتا تھا'),
(6112, 96, 6, 'ہرگز نہیں بے شک آدمی سرکش ہو جاتا ہے'),
(6113, 96, 7, 'جب کہ اپنے آپ کو غنی پاتا ہے'),
(6114, 96, 8, 'بے شک آپ کے رب ہی کی طرف لوٹ کر جانا ہے'),
(6115, 96, 9, 'کیا آپ نے اس کو دیکھا جو منع کرتا ہے'),
(6116, 96, 10, 'ایک بندے کو جب کہ وہ نماز پڑھتا ہے'),
(6117, 96, 11, 'بھلا دیکھو تو سہی اگر وہ راہ پر ہوتا'),
(6118, 96, 12, 'یا پرہیز گاری سکھاتا'),
(6119, 96, 13, 'بھلا دیکھو تو سہی اگر اس نے جھٹلایا اور منہ موڑ لیا'),
(6120, 96, 14, 'تو کیا وہ نہیں جانتا کہ الله دیکھ رہا ہے'),
(6121, 96, 15, 'ہرگزایسا نہیں چاہیئے اگر وہ باز نہ آیا تو ہم پیشانی کے بال پکڑ کر گھسیٹیں گے'),
(6122, 96, 16, 'پیشانی جھوٹی خطا کار'),
(6123, 96, 17, 'پس وہ اپنے مجلس والوں کو بلا لے'),
(6124, 96, 18, 'ہم بھی مؤکلین دوزخ کو بلا لیں گے'),
(6125, 96, 19, 'ہر گز ایسا نہیں چاہیئے آپ اس کا کہانہ مانیے اور سجدہ کیجیئے اور قرب حاصل کیجیئے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'بے شک ہم نے اس (قرآن) کو شب قدر میں اتارا ہے'),
(6127, 97, 2, 'اور آپ کو کیا معلوم کہ شب قدر کیا ہے'),
(6128, 97, 3, 'شب قدر ہزار مہینوں سے بہتر ہے'),
(6129, 97, 4, 'اس میں فرشتے اور روح نازل ہوتے ہیں اپنے رب کے حکم سے ہر کام پر'),
(6130, 97, 5, 'وہ صبح روشن ہونے تک سلامتی کی رات ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'اہلِ کتاب میں سے کافر اور مشرک لوگ باز آنے والے نہیں تھے یہاں تک کہ ان کے پاس کھلی دلیل آئے'),
(6132, 98, 2, 'یعنی ایک رسول الله کی طرف سے آئے جو پاک صحیفے پڑھ کر سنائے'),
(6133, 98, 3, 'جن میں درست مضامین لکھے ہوں'),
(6134, 98, 4, 'اور اہلِ کتاب نے جو اختلاف کیا تو واضح دلیل آنے کے بعد'),
(6135, 98, 5, 'اور انہیں صرف یہی حکم دیا گیا تھا کہ الله کی عبادت کریں ایک رخ ہو کر خالص اسی کی اطاعت کی نیت سے اورنماز قائم کریں اور زکواة دیں اور یہی محکم دین ہے'),
(6136, 98, 6, 'بے شک جو لوگ اہلِ کتاب میں سے منکر ہوئے اور مشرکین وہ دوزخ کی آگ میں ہوں گے اس میں ہمیشہ رہیں گے یہی لوگ بدترین مخلوقات ہیں'),
(6137, 98, 7, 'بے شک جولوگ ایمان لائے اورنیک کام کیے یہی لوگ بہترین مخلوقات ہیں'),
(6138, 98, 8, 'ان کا بدلہ ان کے رب کے ہاں ہمیشہ رہنے کے بہشت ہیں ان کے نیچے نہریں بہتی ہوں گی وہ ان میں ہمیشہ ہمیشہ رہیں گے الله ان سے راضی ہوا اور وہ اس سے راضی ہوئے یہ اس کے لئےہے جو اپنے رب سے ڈرتا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'جب زمین بڑے زور سے ہلا دی جائے گی'),
(6140, 99, 2, 'اور زمین اپنے بوجھ نکال پھینکے گی'),
(6141, 99, 3, 'اور انسان کہے گا کہ اس کو کیا ہوگیا'),
(6142, 99, 4, 'اس دن وہ اپنی خبریں بیا ن کرے گی'),
(6143, 99, 5, 'اس لئے کہ آپ کا رب اس کو حکم دے گا'),
(6144, 99, 6, 'اس دن لوگ مختلف حالتوں میں لوٹیں گے تاکہ انہیں ان کے اعمال دکھائے جائیں'),
(6145, 99, 7, 'پھر جس نے ذرہ بھر نیکی کی ہے وہ اس کو دیکھ لے گا'),
(6146, 99, 8, 'اورجس نے ذرہ بھر برائی کی ہے وہ اس کو دیکھ لے گا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'ان گھوڑوں کی قسم جو ہانپتے ہوئے دوڑتے ہیں'),
(6148, 100, 2, 'پھر (پتھر پر) ٹاپ مار کر آگ جھاڑتے ہیں'),
(6149, 100, 3, 'پھر صبح کے وقت دھاوا کرتے ہیں'),
(6150, 100, 4, 'پھر اس وقت غبار اُڑاتے ہیں'),
(6151, 100, 5, 'پھر اس وقت دشمنوں کی جماعت میں جا گھستے ہیں'),
(6152, 100, 6, 'بے شک انسان اپنے رب کا بڑا ناشکرا ہے'),
(6153, 100, 7, 'اوربے شک وہ اس بات پر خود شاہد ہے'),
(6154, 100, 8, 'اوربے شک وہ مال کی محبت میں بڑا سخت ہے'),
(6155, 100, 9, 'پس کیا وہ نہیں جانتا جب اکھاڑا جائے گا جو کچھ قبروں میں ہے'),
(6156, 100, 10, 'اورجو دلوں میں ہے وہ ظاہر کیا جائے گا'),
(6157, 100, 11, 'بے شک ان کا رب ان سے اس دن خوب خبردار ہو گا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'کھڑکھڑانے والی'),
(6159, 101, 2, 'وہ کھڑکھڑانے والی کیا ہے'),
(6160, 101, 3, 'اور آپ کو کیا خبر کہ وہ کھڑکھڑانے والی کیا ہے'),
(6161, 101, 4, 'جس دن لو گ بکھرے ہوئے پروانوں کی طرح ہوں گے'),
(6162, 101, 5, 'اور پہاڑ رنگی ہوئی دھنی ہوئی اُون کی طرح ہوں گے'),
(6163, 101, 6, 'تو جس کے اعمال (نیک) تول میں زیادہ ہوں گے'),
(6164, 101, 7, 'تو وہ خاطر خواہ عیش میں ہوگا'),
(6165, 101, 8, 'اور جس کے اعمال (نیک) تول میں کم ہوں گے'),
(6166, 101, 9, 'تواس کا ٹھکانا ہاویہ ہوگا'),
(6167, 101, 10, 'اور آپ کو کیا معلوم کہ وہ کیا چیز ہے'),
(6168, 101, 11, 'وہ دھکتی ہوئی آگ ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'تمہیں حرص نے غافل کر دیا'),
(6170, 102, 2, 'یہاں تک کہ قبریں جا دیکھیں'),
(6171, 102, 3, 'ایسا نہیں آئندہ تم جان لو گے'),
(6172, 102, 4, 'پھر ایسا نہیں چاہیئے آئندہ تم جان لو گے'),
(6173, 102, 5, 'ایسا نہیں چاہیئے کاش تم یقینی طور پر جانتے'),
(6174, 102, 6, 'البتہ تم ضرور دوزخ کو دیکھو گے'),
(6175, 102, 7, 'پھر تم اسے ضرور بالکل یقینی طور پر دیکھو گے'),
(6176, 102, 8, 'پھر اس دن تم سے نعمتوں کے متعلق پوچھا جائے گا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'زمانہ کی قسم ہے'),
(6178, 103, 2, 'بے شک انسان گھاٹے میں ہے'),
(6179, 103, 3, 'مگر جو لوگ ایمان لائے اور نیک کام کیے اور حق پر قائم رہنے کی اور صبر کرنے کی آپس میں وصیت کرتے رہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'ہر غیبت کرنے والے طعنہ دینے والے کے لیے ہلاکت ہے'),
(6181, 104, 2, 'جو مال کو جمع کرتا ہے اور اسے گنتا رہتا ہے'),
(6182, 104, 3, 'وہ خیال کرتا ہے کہ اس کا مال اسے سدا رکھے گا'),
(6183, 104, 4, 'ہرگز نہیں وہ ضرور حطمہ میں پھینکا جائے گا'),
(6184, 104, 5, 'اور آپ کو کیا معلوم حطمہ کیا ہے'),
(6185, 104, 6, 'وہ الله کی بھڑکائی ہوئی آگ ہے'),
(6186, 104, 7, 'جو دلوں تک جا پہنچتی ہے'),
(6187, 104, 8, 'بے شک وہ ان پر چاروں طرف سے بند کر دی جائے گی'),
(6188, 104, 9, 'لمبے لمبے ستونوں میں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'کیا آپ نے نہیں دیکھا کہ آپ کے رب نے ہاتھی والوں سے کیا برتاؤ کیا'),
(6190, 105, 2, 'کیا اس نے ان کی تدبیر کوبے کار نہیں بنا دیا تھا'),
(6191, 105, 3, 'اور اس نے ان پر غول کے غول پرندے بھیجے'),
(6192, 105, 4, 'جہاں پر پتھر کنکر کی قسم کے پھینکتے تھے'),
(6193, 105, 5, 'پھر انہیں کھائے ہوئے بھس کی طرح کر ڈالا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'اس لیے کہ قریش کو مانوس کر دیا'),
(6195, 106, 2, 'ان کو جاڑے اور گرمی کے سفر سے مانوس کرنے کے باعث'),
(6196, 106, 3, 'ان کو اس گھرکے مالک کی عبادت کرنی چاہیئے'),
(6197, 106, 4, 'جس نے ان کو بھوک میں کھلایا اور ان کو خوف سے امن دیا');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'کیا آپ نے اس کو دیکھا جو روزِ جرا کو جھٹلاتا ہے'),
(6199, 107, 2, 'پس وہ وہی ہے جو یتیم کو دھکے دیتا ہے'),
(6200, 107, 3, 'اور مسکین کو کھانا کھلانے کی ترغیب نہیں دیتا'),
(6201, 107, 4, 'پس ان نمازیوں کے لیے ہلاکت ہے'),
(6202, 107, 5, 'جو اپنی نماز سے غافل ہیں'),
(6203, 107, 6, 'جو دکھلاوا کرتے ہیں'),
(6204, 107, 7, 'اور برتنے کی چیز تک روکتے ہیں');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'بے شک ہم نے آپ کو کوثر دی'),
(6206, 108, 2, 'پس اپنے رب کے لیے نماز پڑھیئے اور قربانی کیجیئے'),
(6207, 108, 3, 'بے شک آپ کا دشمن ہی بے نام و نشان ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'کہہ دو اے کافرو'),
(6209, 109, 2, 'نہ تومیں تمہارے معبودوں کی عبادت کرتا ہوں'),
(6210, 109, 3, 'اور نہ تم ہی میرے معبود کی عبادت کرتے ہو'),
(6211, 109, 4, 'اور نہ میں تمہارے معبودوں کی عبادت کروں گا'),
(6212, 109, 5, 'اور نہ تم میرے معبود کی عبادت کرو گے'),
(6213, 109, 6, 'تمہارے لیے تمہارا دین ہے اور میرے لیے میرا دین');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'جب الله کی مدد اور فتح آ چکی'),
(6215, 110, 2, 'اورآپ نے لوگوں کو الله کے دین میں جوق در جوق داخل ہوتے دیکھ لیا'),
(6216, 110, 3, 'تو اپنے رب کی حمد کے ساتھ تسبیح کیجیئے اور اس سے معافی مانگیئے بےشک وہ بڑا توبہ قبول کرنے والا ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'ابو لہب کے دونوں ہاتھ ٹوٹ گئے اوروہ ہلاک ہو گیا'),
(6218, 111, 2, 'اس کا مال اورجو کچھ اس نے کمایا اس کے کام نہ آیا'),
(6219, 111, 3, 'وہ بھڑکتی ہوئی آگ میں پڑے گا'),
(6220, 111, 4, 'اور اس کی عورت بھی جو ایندھن اٹھائے پھرتی تھی'),
(6221, 111, 5, 'اس کی گردن میں موُنج کی رسیّ ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'کہہ دووہ الله ایک ہے'),
(6223, 112, 2, 'اللہ بے نیاز ہے'),
(6224, 112, 3, 'نہ اس کی کوئی اولاد ہے اور نہ وہ کسی کی اولاد ہے'),
(6225, 112, 4, 'اور اس کے برابر کا کوئی نہیں ہے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'کہہ دو صبح کے پیدا کرنے والے کی پناہ مانگتا ہوں'),
(6227, 113, 2, 'اس کی مخلوقات کے شر سے'),
(6228, 113, 3, 'اور اندھیری رات کے شر سے جب وہ چھا جائے'),
(6229, 113, 4, 'اورگرہوں میں پھونکنے والیوں کے شر سے'),
(6230, 113, 5, 'اور حسد کرنے والے کے شر سے جب وہ حسد کرے');
INSERT INTO `ur_ahmedali` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'کہہ دو میں لوگوں کے رب کی پناہ میں آیا'),
(6232, 114, 2, 'لوگوں کے بادشاہ کی'),
(6233, 114, 3, 'لوگوں کے معبود کی'),
(6234, 114, 4, 'اس شیطان کے شر سے جووسوسہ ڈال کر چھپ جاتا ہے'),
(6235, 114, 5, 'جو لوگوں کے سینوں میں وسوسہ ڈالتا ہے'),
(6236, 114, 6, 'جنوں اور انسانوں میں سے');
