INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'عظیم اور دائمی رحمتوں والے خدا کے نام سے'),
(2, 1, 2, 'ساری تعریف اللہ کے لئے ہے جو عالمین کا پالنے والا ہے'),
(3, 1, 3, 'وہ عظیم اوردائمی رحمتوں والا ہے'),
(4, 1, 4, 'روزِقیامت کا مالک و مختار ہے'),
(5, 1, 5, 'پروردگار! ہم تیری ہی عبادت کرتے ہیں ا ور تجھی سے مدد چاہتے ہیں'),
(6, 1, 6, 'ہمیں سیدھے راستہ کی ہدایت فرماتا رہ'),
(7, 1, 7, 'جو اُن لوگوں کا راستہ ہے جن پر تو نے نعمتیں نازل کی ہیں ان کا راستہ نہیں جن پر غضب نازل ہوا ہے یا جو بہکے ہوئے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الۤمۤ'),
(9, 2, 2, 'یہ وہ کتاب ہے جس میں کسی طرح کے شک و شبہ کی گنجائش نہیں ہے. یہ صاحبانِ تقویٰ اور پرہیزگار لوگوں کے لئے مجسم ہدایت ہے'),
(10, 2, 3, 'جو غیب پر ایمان رکھتے ہیں پابندی سے پورے اہتمام کے ساتھ نماز ادا کرتے ہیں اور جو کچھ ہم نے رزق دیا ہے اس میں سے ہماری راہ میں خرچ بھی کرتے ہیں'),
(11, 2, 4, 'وہ ان تمام باتوں پر بھی ایمان رکھتے ہیں جنہیں (اے رسول) ہم نے آپ پر نازل کیا ہے اور جو آپ سے پہلے نازل کی گئی ہیں اور آخرت پر بھی یقین رکھتے ہیں'),
(12, 2, 5, 'یہی وہ لوگ ہیں جو اپنے پروردگار کی طرف سے ہدایت کے حامل ہیں اور فلاح یافتہ اور کامیاب ہیں'),
(13, 2, 6, 'اے رسول! جن لوگوں نے کفر اختیار کرلیا ہے ان کے لئے سب برابر ہے. آپ انہیں ڈرائیں یا نہ ڈرائیں یہ ایمان لانے والے نہیں ہیں'),
(14, 2, 7, 'خدا نے ان کے دلوں اور کانوں پر گویا مہر لگا دی ہے کہ نہ کچھ سنتے ہیںاور نہ سمجھتے ہیں اور آنکھوں پر بھی پردے پڑ گئے ہیں. ان کے واسطےآخرت میں عذابِ عظیم ہے'),
(15, 2, 8, 'کچھ لوگ ایسے بھی ہیں جو یہ کہتے ہیں کہ ہم خدا اور آخرت پر ایمان لائے ہیں حالانکہ وہ صاحب ایمان نہیں ہیں'),
(16, 2, 9, 'یہ خدا اور صاحبان ایمان کو دھوکہ دینا چاہتے ہیں حالانکہ اپنے ہی کو دھوکہ دے رہے ہیں اور سمجھتے بھی نہیں ہیں'),
(17, 2, 10, 'ان کے دلوں میں بیماری ہے اور خدا نے نفاق کی بنا پر اسے اور بھی بڑھا دیا ہے. اب اس جھوٹ کے نتیجہ میں انہیں درد ناک عذاب ملے گا'),
(18, 2, 11, 'جب ان سے کہا جاتا ہے کہ زمین میں فساد نہ برپا کرو تو کہتے ہیں کہ ہم تو صرف اصلاح کرنے والے ہیں'),
(19, 2, 12, 'حالانکہ یہ سب مفسد ہیں اور اپنے فسادکو سمجھتے بھی نہیں ہیں'),
(20, 2, 13, 'جب ان سے کہا جاتا ہے کہ دوسرے مومنین کی طرح ایمان لے آؤ تو کہتے ہیں کہ ہم بے وقوفوں کی طرح ایمان اختیار کرلیں حالانکہ اصل میں یہی بے وقوف ہیں اور انہیں اس کی واقفیت بھی نہیں ہے'),
(21, 2, 14, 'جب یہ صاحبانِ ایمان سے ملتے ہیںتو کہتے ہیں کہ ہم ایمان لے آئے اور جب اپنے شیاطین کی خلوتوں میں جاتے ہیں تو کہتے ہیں کہ ہم تمہاری ہی پارٹی میں ہیں ہم تو صرف صاحبانِ ایمان کا مذاق اڑاتے ہیں'),
(22, 2, 15, 'حالانکہ خدا خود ان کو مذاق بنائے ہوئے ہے اور انہیںسرکشی میں ڈھیل دیئے ہوئے ہے جو انہیں نظر بھی نہیں آرہی ہے'),
(23, 2, 16, 'یہی وہ لوگ ہیں جنہوں نے ہدایت کو دے کرگمراہی خرید لی ہے جس تجارت سے نہ کوئی فائدہ ہے اور نہ اس میں کسی طرح کی ہدایت ہے'),
(24, 2, 17, 'ان کی مثال اس شخص کی ہے جس نے روشنی کے لئے آگ بھڑکائی اور جب ہر طرف روشنی پھیل گئی تو خدا نے اس کے نور کو سلب کرلیا اور اب اسے اندھیرے میں کچھ سوجھتا بھی نہیں ہے'),
(25, 2, 18, 'یہ سب بہرےً گونگے اور اندھے ہوگئے ہیں اور اب پلٹ کر آنے والے نہیں ہیں'),
(26, 2, 19, 'ان کی دوسری مثال آسمان کی اس بارش کی ہے جس میں تاریکی اور گرج چمک سب کچھ ہوکہ موت کے خوف سے کڑک دیکھ کر کانوں میں انگلیاں رکھ لیں. حالانکہ خدا کافروں کا احاطہ کئے ہوئے ہے اور یہ بچ کر نہیں جاسکتے ہیں'),
(27, 2, 20, 'قریب ہے کہ بجلی ان کی آنکھوں کو چکاچوندھ کردے کہ جب وہ چمک جائے تو چل پڑیں اور جب اندھیرا ہوجائے تو ٹھہر جائیں. خدا چاہے تو ان کی سماعت و بصارت کو بھی ختم کرسکتا ہے کہ وہ ہر شے پر قدرت و اختیار رکھنے والا ہے'),
(28, 2, 21, 'اے انسانو! پروردگار کی عبادت کرو جس نے تمہیں بھی پیدا کیا ہے اور تم سے پہلے والوں کو بھی خلق کیاہے. شاید کہ تم اسی طرح متقی اور پرہیزگار بن جاؤ'),
(29, 2, 22, 'اس پروردگار نے تمہارے لئے زمین کا فرش اور آسمان کا شامیانہ بنایا ہے اور پھر آسمان سے پانی برسا کر تمہاری روزی کے لئے زمین سے پھل نکالے ہیں لہذا اس کے لئے جان بوجھ کر کسی کو ہمسر اور مثل نہ بناؤ'),
(30, 2, 23, 'اگر تمہیں اس کلام کے بارے میں کوئی شک ہے جسے ہم نے اپنے بندے پر نازل کیا ہے تو اس کا جیسا ایک ہی سورہ لے آؤ اور اللہ کے علاوہ جتنے تمہارے مددگار ہیںسب کو بلالو اگر تم اپنے دعوے اور خیال میںسچّے ہو'),
(31, 2, 24, 'اور اگر تم ایسا نہ کرسکے اور یقینا نہ کرسکوگے تو اس آگ سے ڈرو جس کا ایندھن انسان اور پتھر ہیں اور جسے کافرین کے لئے مہیا کیا گیا ہے'),
(32, 2, 25, 'پیغمبر آپ ایمان اور عمل صالح والوں کو بشارت دے دیں کہ ان کے لئے ایسے باغات ہیں جن کے نیچے نہریں جاری ہیں. انہیں جب بھی وہاںکوئی پھل دیا جائے گا تو وہ یہی کہیں گے کہ یہ تو ہمیں پہلے مل چکا ہے. حالانکہ وہ صرف اس کے مشابہ ہوگا اور ان کے لئے وہاں پاکیزہ بیویاں بھی ہوں گی اور انہیں اس میں ہمیشہ رہنا بھی ہے'),
(33, 2, 26, 'اللہ اس بات میں کوئی شرم نہیں محسوس کرتا کہ وہ مچھر یا اس سے بھی کمتر کی مثال بیان کرے. اب جو صاحبانِ ایمان ہیں وہ جانتے ہیں کہ یہ سب پروردگار کی طرف سے بر حق ہے اور جنہوں نے کفر اختیار کیا ہے وہ یہی کہتے ہیں کہ آخر ان مثالوں سے خدا کا مقصد کیا ہے. خدا اسی طرح بہت سے لوگوں کو گمراہی میں چھوڑ دیتا ہے اور بہت سوں کو ہدایت دے دیتا ہے اور گمراہی صرف انہیں کا حصّہ ہے جو فاسق ہیں'),
(34, 2, 27, 'جو خدا کے ساتھ مضبوط عہد کرنے کے بعد بھی اسے توڑ دیتے ہیں اور جسے خدا نے جوڑنے کا حکم دیا ہے اسے کاٹ دیتے ہیں اور زمین میں فساد برپا کرتے ہیں یہی وہ لوگ ہیں جو حقیقتا خسارہ والے ہیں'),
(35, 2, 28, 'آخر تم لوگ کس طرح کفر اختیار کرتے ہو جب کہ تم بے جان تھے اور خدا نے تمہیں زندگی دی ہے اور پھرموت بھی دے گا اور پھر زندہ بھی کرے گا اور پھر اس کی بارگاہ میں پلٹا کر لے جائے جاؤگے'),
(36, 2, 29, 'وہ خدا وہ ہے جس نے زمین کے تمام ذخیروں کو تم ہی لوگوں کے لئے پیدا کیا ہے. اس کے بعد اس نے آسمان کا رخ کیا تو سات مستحکم آسمان بنادیئے اور وہ ہر شے کا جاننے والا ہے'),
(37, 2, 30, 'اے رسول. اس وقت کو یاد کرو جب تمہارے پروردگار نے ملائکہ سے کہا کہ میں زمین میں اپنا خلیفہ بنانے والا ہوں اور انہوں نے کہا کہ کیا اسے بنائے گاجو زمین میں فساد برپا کرے اور خونریزی کرے جب کہ ہم تیری تسبیح اور تقدیس کرتے ہیں توارشاد ہوا کہ میں وہ جانتا ہوں جو تم نہیں جانتے ہو'),
(38, 2, 31, 'اور خدا نے آدم علیھ السّلام کو تمام اسمائ کی تعلیم دی اور پھر ان سب کو ملائکہ کے سامنے پیش کرکے فرمایا کہ ذرا تم ان سب کے نام تو بتاؤ اگر تم اپنے خیالِ استحقاق میں سچّے ہو'),
(39, 2, 32, 'ملائکہ نے عرض کی کہ ہم تو اتنا ہی جانتے ہیں جتنا تونے بتایا ہے کہ تو صاحبِ علم بھی ہے اور صاحبِ حکمت بھی'),
(40, 2, 33, 'ارشاد ہوا کہ آدم علیھ السّلام اب تم انہیں باخبر کردو. تو جب آدم علیھ السّلام نے باخبر کردیا تو خدا نے فرمایا کہمیں نے تم سے نہ کہا تھا کہ میں آسمان و زمین کے غیب کو جانتا ہوں اور جو کچھ تم ظاہر کرتے ہو یا چھپاتے ہو سب کو جانتا ہوں'),
(41, 2, 34, 'اور یاد کرو وہ موقع جب ہم نے ملائکہ سے کہا کہ آدم علیھ السّلام کے لئے سجدہ کرو تو ابلیس کے علاوہ سب نے سجدہ کرلیا. اس نے انکار اور غرور سے کام لیا اور کافرین میں ہوگیا'),
(42, 2, 35, 'اور ہم نے کہا کہ اے آدم علیھ السّلام! اب تم اپنی زوجہ کے ساتھ جنّت میں ساکن ہوجاؤ اور جہاں چاہو آرام سے کھاؤ صرف اس درخت کے قریب نہ جانا کہ اپنے اوپر ظلم کرنے والوں میں سے ہوجاؤگے'),
(43, 2, 36, 'تب شیطان نے انہیں فریب دینے کی کوشش کی اور انہیں ان نعمتوں سے باہر نکال لیا اور ہم نے کہا کہ اب تم سب زمین پر اترجاؤ وہاں ایک دوسرے کی دشمنی ہوگی اور وہیں تمہارا مرکز ہوگا اور ایک خاص وقت تک کے لئے عیش زندگانی رہےگی'),
(44, 2, 37, 'پھر آدم علیھ السّلام نے پروردگار سے کلمات کی تعلیم حاصل کی اور ان کی برکت سے خدا نے ان کی توبہ قبول کرلی کہ وہ توبہ قبول کرنے والا اور مہربان ہے'),
(45, 2, 38, 'اور ہم نے یہ بھی کہا کہ یہاں سے اترپڑو پھر اگر ہماری طرف سے ہدایت آجائے تو جو بھی اس کا اتباع کرلے گا اس کے لئے نہ کوئی خوف ہوگا نہ حزن'),
(46, 2, 39, 'جو لوگ کافر ہوگئے اور انہوں نے ہماری نشانیوں کو جھٹلا دیا وہ جہنمّی ہیں اور ہمیشہ وہیں پڑے رہیں گے'),
(47, 2, 40, 'اے بنی اسرائیل ہماری نعمتوں کو یاد کرو جو ہم نے تم پر نازل کی ہیں اور ہمارے عہد کو پورا کرو ہم تمہارے عہد کو پورا کریں گے اور ہم سے ڈرتے رہو'),
(48, 2, 41, 'ہم نے جو قرآن تمہاری توریت کی تصدیق کے لئے بھیجا ہے اس پر ایمان لے آؤ اور سب سے پہلے کافر نہ بن جاؤ ہماری آیتوں کو معمولی قیمت پر نہ بیچو اور ہم سے ڈرتے رہو'),
(49, 2, 42, 'حقکو باطل سے مخلوط نہ کرو اور جان بوجھ کر حق کی پردہ پوشی نہ کرو'),
(50, 2, 43, 'نماز قائم کرو.زکوٰ ِ ادا کرو اور رکوع کرنے والوں کے ساتھ رکوع کرو'),
(51, 2, 44, 'کیا تم لوگوں کو نیکیوں کا حکم دیتے ہو اور خود اپنے کو بھول جاتے ہو جب کہ کتاب خدا کی تلاوت بھی کرتے ہو . کیا تمہارے پاس عقل نہیں ہے'),
(52, 2, 45, 'صبر او ر نماز کے ذریعہ مدد مانگو. نماز بہت مشکل کام ہے مگر ان لوگوں کے لئے جو خشوع و خضوع والے ہیں'),
(53, 2, 46, 'اور جنہیں یہ خیال ہے کہ انہیں اللہ سے ملاقات کرنا ہے اور اس کی بارگاہ میں واپس جانا ہے'),
(54, 2, 47, 'اے بنی اسرائیل! ہماری ان نعمتوں کویاد کرو جو ہم نے تمہیں عنایت کی ہیں اور ہم نے تمہیں عالمین سے بہتر بنایا ہے'),
(55, 2, 48, 'اس دن سے ڈرو جس دن کوئی کسی کابدل نہ بن سکے گا اور کسی کی سفارش قبول نہ ہوگی. نہ کوئی معاوضہ لیا جائے گا اور نہ کسی کی مدد کی جائے گی'),
(56, 2, 49, 'اور جب ہم نے تم کوفرعون والوں سے بچالیا جو تمہیں بدترین دکھ دے رہے تھےً تمہارے بچوں کو قتل کررہے تھے اور عورتوں کو زندہ رکھتے تھے اور اس میں تمہارے لئے بہت بڑا امتحان تھا'),
(57, 2, 50, 'ہمارا یہ احسان بھی یادکرو کہ ہم نے دریا کو شگافتہ کرکے تمہیں بچالیا اور فرعون والوں کو تمہاری نگاہوں کے سامنے ڈبو دیا'),
(58, 2, 51, 'اور ہم نے موسٰی علیھ السّلام سے چالیس راتوں کا وعدہ لیا تو تم نے ان کے بعد گو سالہ تیار کرلیا کہ تم بڑے ظالم ہو'),
(59, 2, 52, 'پھر ہم نے تمہیں معاف کردیا کہ شاید شکر گزار بن جاؤ'),
(60, 2, 53, 'اور ہم نے موسٰی علیھ السّلام کو کتاب اور حق و باطل کو جدا کرنے والا قانون دیا کہ شاید تم ہدایت یافتہ بن جاؤ'),
(61, 2, 54, 'اور وہ وقت بھی یاد کرو جب موسٰی علیھ السّلام نے اپنی قوم سے کہا کہ تم نے گو سالہ بنا کر اپنے اوپر ظلم کیا ہے. اب تم خالق کی بارگاہ میں توبہ کرو اور اپنے نفسوں کو قتل کر ڈالو کہ یہی تمہارے حق میں خیر ہے. پھر خدا نے تمہاری توبہ قبول کرلی کہ وہ بڑا توبہ قبول کرنے والا مہربان ہے'),
(62, 2, 55, 'اور وہ وقت بھی یاد کرو جب تم نے موسٰی علیھ السّلام سے کہا کہ ہم اس وقت تک ایمان نہ لائیں گے جب تک خدا کو اعلانیہ نہ دیکھ لیں جس کے بعد بجلی نے تم کو لے ڈالا اور تم دیکھتے ہی رہ گئے'),
(63, 2, 56, 'پھر ہم نے تمہیں موت کے بعد زندہ کردیا کہ شاید اب شکر گزار بن جاؤ'),
(64, 2, 57, 'اور ہم نے تمہارے سروں پر ابر کا سایہ کیا. تم پر من و سلٰوی نازل کیا کہ پاکیزہ رزق اطمینان سے کھاؤ. ان لوگوں نے ہمارا کچھ نہیں بگاڑا بلکہ خود اپنے نفس پر ظلم کیا ہے'),
(65, 2, 58, 'اوروہ وقت بھی یاد کروجب ہم نے کہا کہ اس قریہ میں داخل ہوجاؤ اور جہاں چاہو اطمینان سے کھاؤ اور دروازہ سے سجدہ کرتے ہوئے اور حطکہتے ہوئے داخل ہو کہ ہم تمہاری خطائیں معاف کردیں گے اور ہم نیک عمل والوں کی جزا میں اضافہ بھی کردیتے ہیں'),
(66, 2, 59, 'مگر ظالموں نے جو بات ان سے کہی گئی تھی اسے بدل دیا تو ہم نے ان ظالموں پر ان کی نافرمانی کی بنا ئ پر آسمان سے عذاب نازل کر دیا'),
(67, 2, 60, 'اور اس موقع کو یاد کرو جب موسٰی علیھ السّلام نے اپنی قوم کے لئے پانی کا مطالبہ کیا تو ہم نے کہا کہ اپنا اعصا پتھر پر مارو جس کے نتیجہ میں بارہ چشمے جاری ہوگئے اور سب نے اپنا اپنا گھاٹ پہچان لیا. اب ہم نے کہا کہ من وسلوٰی کھاؤ اور چشمہ کا پانی پیو اور روئے زمینمیں فساد نہ پھیلاؤ'),
(68, 2, 61, 'اور وہ وقت بھی یادکرو جب تم نے موسٰی علیھ السّلام سے کہا کہ ہم ایک قسم کے کھانے پر صبر نہیں کرسکتے. آپ پروردگار سے دعا کیجئے کہ ہمارے لئے زمین سے سبزیً ککڑیً لہسنً مسوراور پیاز وغیرہ پیدا کرے. موسٰی علیھ السّلام نے تمہیں سمجھایا کہ کیا بہترین نعمتوں کے بدلے معمولی نعمت لینا چاہتے ہو تو جاؤ کسی شہر میں اتر پڑو وہاں یہ سب کچھ مل جائے گا. اب ان پر ذلّت اور محتاجی کی مار پڑ گئی اور وہ غضب الٰہی میں گرفتار ہوگئے. یہ سب اس لئے ہوا کہ یہ لوگ آیات الٰہی کا انکار کرتے تھے اور ناحق انبیائ خدا کو قتل کردیا کرتے تھے. اس لئے کہ یہ سب نافرمان تھے اور ظلم کیا کرتے تھے'),
(69, 2, 62, 'جو لوگ بظاہر ایمان لائے یا یہودی ً نصاریٰ اور ستارہ پرست ہیں ان میں سے جو واقعی اللہ اور آخرت پر ایمان لائے گا اور نیک عمل کرے گا اس کے لئے پروردگار کے یہاں اجر و ثواب ہے اور کوئی حزن و خوف نہیں ہے'),
(70, 2, 63, 'اور اس وقت کو یاد کرو جب ہم نے تم سے توریت پر عمل کرنے کا عہد لیا اور تمہارے سروں پر کوہ طور کو لٹکا دیا کہ اب توریت کو مضبوطی سے پکڑو اور جو کچھ اس میں ہے اسے یاد رکھو شاید اس طرح پرہیز گار بن جاؤ'),
(71, 2, 64, 'پھر تم لوگوں نے انحراف کیا کہ اگر فضل هخدا اور رحمت الٰہی شامل حال نہ ہوتی تو تم خسارہ والوں میں سے ہوجاتے'),
(72, 2, 65, 'تم ان لوگوں کو بھی جانتے ہو جنہوں نے شنبہ کے معاملہ میں زیادتی سے کام لیا تو ہم نے حکم دے دیا کہ اب ذلّت کے ساتھ بندر بن جائیں'),
(73, 2, 66, 'اور ہم نے اس جنسی تبدیلی کو دیکھنے والوں او ر بعد والوں کے لئے عبرت اور صاحبانِ تقویٰ کے لئے نصیحت بنا دیا'),
(74, 2, 67, 'اور وہ موقع بھی یاد کروجب موسٰی علیھ السّلام نے قوم سے کہا کہ خدا کا حکم ہے کہ ایک گائے ذبح کرو تو ان لوگوں نے کہا کہ آپ ہمیں مذاق بنا رہے ہیں. فرمایا پناہ بخدا کہ میں جاہلوں میں سے ہوجاؤں'),
(75, 2, 68, 'ان لوگوں نے کہا کہ اچھا خدا سے دعا کیجئے کہ ہمیں اس کی حقیقت بتائے. انہوں نے کہا کہ ایسی گائے چاہئے جو نہ بوڑھی ہو نہ بچہ. درمیانی قسم کی ہو. اب حکم خدا پر عمل کرو'),
(76, 2, 69, 'ان لوگوں نے کہا یہ بھی پوچھئے کہ رنگ کیا ہوگا. کہا کہ حکم خدا ہے کہ زرد بھڑک دار رنگ کی ہو جو دیکھنے میں بھلی معلوم ہو'),
(77, 2, 70, 'ان لوگوں نے کہا کہ ایسی تو بہت سی گائیں ہیں اب کون سی ذبح کریں اسے بیان کیاجائے ہم انشائ اللرُ تلاش کرلیں گے'),
(78, 2, 71, 'حکم ہوا کہ ایسے گائے جو کاروباری نہ ہونہ زمین جوتے نہ کھیت سینچے ایسی صاف ستھری کہ اس میں کوئی دھبّہ بھی نہ ہو. ان لوگوں نے کہا کہ اب آپ نے ٹھیک بیان کیا ہے. اس کے بعد ان لوگوں نے ذبح کردیا حالانکہ وہ ایسا کرنے والے نہیں تھے'),
(79, 2, 72, 'اور جب تم نے ایک شخص کو قتل کردیا اور اس کے قاتل کے بارے میں جھگڑا کرنے لگے جبکہ خدا اس راز کا واضح کرنے والا ہے جسے تم چھپا رہے تھے'),
(80, 2, 73, 'تو ہم نے کہا کہ مقتول کو گائے کے ٹکڑے سے مس کردو خدا اسی طرح مفِدوں کو زندہ کرتا ہے اور تمہیں اپنی نشانیاں دکھلاتا ہے کہ شاید تمہیں عقل آجائے'),
(81, 2, 74, 'پھر تمہارے دل سخت ہوگئے جیسے پتھر یا اس سے بھی کچھ زیادہ سخت کہ پتھروں میں سے تو بعض سے نہریں بھی جاری ہوجاتی ہیںاور بعض شگافتہ ہوجاتے ہیں تو ان سے پانی نکل آتا ہے اور بعض خورِ خدا سے گر پڑتے ہیں. لیکن اللہ تمہارے اعمال سے غافل نہیں ہے'),
(82, 2, 75, 'مسلمانو! کیا تمہیں امید ہے کہ یہ یہودی تمہاری طرح ایمان لے آئیں گے جبکہ ان کے اسلاف کا ایک گروہ کلاهِ خدا کو سن کر تحریف کردیتا تھا حالانکہ سب سمجھتے بھی تھے اور جانتے بھی تھے'),
(83, 2, 76, 'یہ یہودی ایمان والوںسے ملتے ہیں تو کہتے ہیں کہ ہم بھی ایمان لے آئے اور آپس میں ایک دوسرے ملتے ہیں توکہتے ہیں کہ کیا تم مسلمانوںکو توریت کے مطالب بتادوگے کہ وہ اپنے نبی کے اوصاف سے تمہارے اوپر استدلال کریں کیا تمہیں عقل نہیں ہے کہ ایسی حماقت کروگے'),
(84, 2, 77, 'کیا تمہیں نہیں معلوم کہ خدا سب کچھ جانتا ہے جس کا یہ اظہار کررہے ہیں اورجس کی یہ پردہ پوشی کررہے ہیں'),
(85, 2, 78, 'ان یہودیوں میں کچھ ایسے جاہل بھی ہیں جو توریت کو یاد کئے ہوئے ہیں اور اس کے بارے میں سوائے امیدوں کے کچھ نہیں جانتے ہیں حالانکہ یہ ا ن کا فقط خیالِ خام ہے'),
(86, 2, 79, 'وائے ہو ان لوگوںپر جو اپنے ہاتھ سے کتاب لکھ کر یہ کہتے ہیں کہ یہ خدا کی طرف سے ہے تاکہ اسے تھوڑے دام میں بیچ لیں. ان کے لئے اس تحریر پر بھی عذاب ہے اور اس کی کمائی پر بھی'),
(87, 2, 80, 'یہ کہتے ہیں کہ ہمیں آتش جہّنم چند دن کے علاوہ چھو بھی نہیں سکتی. ان سے پوچھئے کہ کیا تم نے اللہ سے کوئی عہد لے لیا ہے جس کی وہ مخالفت نہیں کرسکتا یا اس کے خلاف جہالت کی باتیں کررہے ہو'),
(88, 2, 81, 'یقینا جس نے کوئی برائی حاصل کی اور اس کی غلطی نے اسے گھیر لیا وہ لوگ اہلِ جہّنم ہیں اور وہیں ہمیشہ رہنے والے ہیں'),
(89, 2, 82, 'اور جو ایمان لائے اور انہوں نے نیک عمل کئے وہ اہل جنّت ہیں اور وہیں ہمیشہ رہنے والے ہیں'),
(90, 2, 83, 'اس وقت کو یاد کرو جب ہم نے بنی اسرائیل سے عہد لیا کہ خبردار خدا کے علاوہ کسی کی عبادت نہ کرنا اور ماں باپً قرابتداروںً یتیموں اور مسکینوں کے ساتھ اچھا برتاؤ کرنا. لوگوں سے اچھی باتیں کرناً نماز قائم کرناًزکوِٰ ادا کرنا لیکن اس کے بعد تم میں سے چند کے علاوہ سب منحرف ہوگئے اور تم لوگ تو بس اعراض کرنے والے ہی ہو'),
(91, 2, 84, 'اور ہم نے تم سے عہد لیا کہ آپس میں ایک دوسرے کا خون نہ بہانا اور کسی کواپنے وطن سے نکال باہر نہ کرنا اور تم نے اس کا اقرار کیا اور تم خود ہی اس کے گواہ بھی ہو'),
(92, 2, 85, 'لیکن اس کے بعد تم نے قتل و خون شروع کردیا. لوگوں کو علاقہ سے باہر نکالنے لگے اور گناہ وتعدی میں ظالموںکی مدد کرنے لگے حالانکہ کوئی قیدی بن کر آتا ہے تو فدیہ دے کر آزاد بھی کرالیتے ہو جبکہ شروع سے ان کا نکالنا ہی حرام تھا. کیا تم کتاب کے ایک حصّہ پر ایمان رکھتے ہو اور ایک کا انکار کردیتے ہو. ایسا کرنے والوں کی کیا سزاہے سوائے اس کے کہ زندگانی دنیا میں ذلیل ہوں اور قیامت کے دن سخت ترین عذاب کی طرف پلٹاد یئے جائیں گے . اور اللہ تمہارے کرتوت سے بے خبر نہیں ہے'),
(93, 2, 86, 'یہ وہ لوگ ہیں جنہوں نے آخر کو دے کر دنیا خریدلی ہے اب نہ ان کے عذاب میں تخفیف ہوگی اور نہ ان کی مدد کی جائے گی'),
(94, 2, 87, 'ہم نے موسٰی علیھ السّلام کو کتاب دی ان کے پیچھے رسولوںکا سلسلہ قائم کیا. عیسٰی علیھ السّلام بن مریم کو واضح معجزات عطا کئےً روح القدس سے ان کی تائید کرادی لیکن کیا تمہارا مستقل طریقہ یہی ہے کہ جب کوئی رسول علیھ السّلام تمہاری خواہش کے خلاف کوئی پیغام لے کر آتا ہے تو اکڑ جاتے ہو اور ایک جماعت کو جھٹلا دیتے ہو اور ایک کو قتل کردیتے ہو'),
(95, 2, 88, 'اور یہ لوگ کہتے ہیں کہ ہمارے دلوں پر پردے پڑے ہوئے ہیں ہماری کچھ سمجھ میں نہیں آتا بے شک ان کے کفر کی بنا پر ان پر خدا کی مار ہے اور یہ بہت کم ایمان لے آئیں گے'),
(96, 2, 89, 'اور جب ان کے پاس خدا کی طرف سے کتاب آئی ہے جو ان کی توریت وغیرہ کی تصدیق بھی کرنے والی ہے اور اس کے پہلے وہ دشمنوںکے مقابلہ میں اسی کے ذریعہ طلب فتح بھی کرتے تھے لیکن اس کے آتے ہی منکر ہوگئے حالانکہ اسے پہچانتے بھی تھے تو اب کافروں پر خدا کی لعنت ہے'),
(97, 2, 90, 'ان لوگوں نے اپنے نفس کا کتنا برا سودا کیا ہے کہ زبردستی خدا کے نازل کئے کا انکار کر بیٹھے اس ضد میں کہ خدا اپنے فضل و کر م سے اپنے جس بندے پر جو چاہے نازل کردیتا ہے. اب یہ غضب بالائے غضب کے حقدار ہیں اور ان کے لئے رسوا کن عذاب بھی ہے'),
(98, 2, 91, 'جب ان سے کہا جاتا ہے کہ خدا کے نازل کئے ہوئے پر ایمان لے آؤ تو کہتے ہیں کہ ہم صرف اس پر ایمان لے آئیں گے جو ہم پر نازل ہوا ہے اور اس کے علاوہ سب کا انکار کردیتے ہیں. اگرچہ وہ بھی حق ہے اور ان کے پاس جو کچھ ہے اس کی تصدیق کرنے والا بھی ہے. اب آپ ان سے کہئے کہ اگر تم مومن ہو تو اس سے پہلے انبیائ خدا کو قتل کیوں کرتے تھے'),
(99, 2, 92, 'یقینا موسٰی علیھ السّلام تمہارے پاس واضح نشانیاں لے کر آئے لیکن تم نے ان کے بعد گو سالہ کو اختیار کرلیا اور تم واقعی ظالم ہو'),
(100, 2, 93, 'اور اس وقت کو یاد کرو جب ہم نے تم سے توریت پر عمل کرنے کا عہد لیا اور تمہارے سروں پر کوسِ طور کو معلق کردیا کہ توریت کو مضبوطی سئے پکڑلو تو انہوں نے ڈر کے مارے فورا اقرار کرلیا کہ ہم نے سن تو لیا لیکن پھر نافرمانی بھی کریں گے کہ ان کے دلوں میں گو سالہ کی محبت گھول کر پلادی گئی ہے. پیغمبر ان سے کہہ دو کہ اگر تم ایماندار ہو تو تمہارا ایمان بہت برے احکام دیتا ہے'),
(101, 2, 94, 'ان سے کہو کہ اگر سارے انسانوں میںدا» آخرت فقط تمہارے لئے ہے اور تم اپنے دعوے میں سچّے ہو تو موت کی تمّنا کرو'),
(102, 2, 95, 'اور یہ اپنے پچھلے اعمال کی بنا پر ہرگز موت کی تمّنا نہیں کریں گے کہ خدا ظالموں کے حالات سے خوب واقف ہے'),
(103, 2, 96, 'اے رسول آپ دیکھیں گے کہ یہ زندگی کے سب سے زیادہ حریص ہیں اور بعض مشرکین تو یہ چاہتے ہیں کہ انہیں ہزار برس کی عمر دے دی جائے جبکہ یہ ہزار برس بھی زندہ رہیں تو طول هحیات انہیںعذابِ الٰہی سے نہیں بچا سکتا. اللہ ان کے اعمال کو خوب دیکھ رہا ہے'),
(104, 2, 97, 'اے رسول کہہ دیجئے کہ جو شخص بھی جبریل کا دشمن ہے اسے معلوم ہونا چاہئے کہ جبریل نے آپ کے دل پر قرآن حکم خدا سے اتارا ہے جو سابق کتابوں کی تصدیق کرنے والاً ہدایت اور صاحبانِ ایمان کے لئے بشارت ہے'),
(105, 2, 98, 'جو بھی اللہً ملائکہً مرسلین اور جبریل و میکائیکل کا دشمن ہوگا اسے معلوم رہے کہ خدا بھی تمام کافروں کا دشمن ہے'),
(106, 2, 99, 'ہم نے آپ کی طرف روشن نشانیاں نازل کی ہیں اور ان کا انکار فاسقوں کے سوا کوئی نہ کرے گا'),
(107, 2, 100, 'ان کا حال یہ ہے کہ جب بھی انہوں نے کوئی عہد کیا تو ایک فریق نے ضرور توڑ دیا بلکہ ان کی اکثریت بے ایمان ہی ہے'),
(108, 2, 101, 'اور جب ان کے پاس خدا کی طرف سے سابق کی کتابوں کی تصدیق کرنے والا رسول آیا تو اہلِ کتاب کی ایک جماعت نے کتابِ خدا کو پبُ پشت ڈال دیا جیسے وہ اسے جانتے ہی نہ ہوں'),
(109, 2, 102, 'اور ان باتوں کا اتباع شروع کردیا جو شیاطین سلیمان علیھ السّلامکی سلطنت میں جپا کرتے تھے حالانکہ سلیمان علیھ السّلام کافر نہیں تھے. کافر یہ شیاطین تھے جو لوگوں کو جادو کی تعلیم دیتے تھے اور پھر جو کچھ دو فرشتوں ہاروت ماروت پر بابل میں نازل ہوا ہے. وہ اس کی بھی تعلیم اس وقت تک نہیںدیتے تھے جب تک یہ کہہ نہیں دیتے تھے کہ ہم ذریعہ امتحان ہیں. خبردار تم کافر نہ ہوجانا .لیکن وہ لوگ ان سے وہ باتیں سیکھتے تھے جس سے میاں بیوی کے درمیان جھگڑاکرادیں حالانکہ اذنِ خدا کے بغیر وہ کسی کو نقصان نہیں پہنچا سکتے. یہ ان سے وہ سب کچھ سیکھتے تھے جو ان کے لئے مضر تھا اور اس کا کوئی فائدہ نہیںتھا یہ خوب جانتے تھے کہ جو بھی ان چیزوں کو خریدے گا اس کا آخرت میں کوئی حصہّ نہ ہوگا. انہوں نے اپنے نفس کا بہت برا سو دا کیا ہے اگر یہ کچھ جانتے اور سمجھتے ہوں'),
(110, 2, 103, 'اگر یہ لوگ ایمان لے آتے اور متقی بن جاتے تو خدائی ثواب بہت بہتر تھا. اگر ان کی سمجھ میں آجاتا'),
(111, 2, 104, 'ایمان والو! راعنا (ہماری رعایت کرو) نہ کہا کرو «انظرنامِ کہا کرو اور غور سے سنا کرو اور یاد رکھو کہ کافرین کے لئے بڑا دردناک عذاب ہے'),
(112, 2, 105, 'کافر اہلِ کتاب یہود و نصارٰی اور عام مشرکین یہ نہیں چاہتے کہ تمہارے او پر پروردگار کی طرف سے کوئی خیر نازل ہو حالانکہ اللہ جسے چاہتاہے اپنی رحمت کے لئے مخصوص کرلیتا ہے اور اللہ بڑے فضل و کرم کا مالک ہے'),
(113, 2, 106, 'اور اے رسول ہم جب بھی کسی آیت کو منسوخ کرتے ہیں یا دلوں سے محو کردیتے ہیں تو اس سے بہتر یا اس کی جیسی آیت ضرور لے آتے ہیں. کیا تم نہیں جانتے کہ اللہ ہر شے پر قادر ہے'),
(114, 2, 107, 'کیا تم نہیں جانتے کہ آسمان و زمین کی حکومت صرف اللہ کے لئے ہے اور اس کے علاوہ کوئی سرپرست ہے نہ مددگار'),
(115, 2, 108, 'اے لوگو! کیا تم یہ چاہتے ہو کہ اپنے رسول سے ویسا ہی مطالبہ کرو جیسا کہ موسٰی علیھ السّلامسے کیاگیا تھا تویاد رکھو کہ جس نے ایمان کو کفر سے بدل لیا وہ بدترین راستہ پر گمراہ ہوگیا ہے'),
(116, 2, 109, 'بہت سے اہلِ کتاب یہ چاہتے ہیں کہ تمہیں بھی ایمان کے بعد کافر بنالیں وہ تم سے حسد رکھتے ہیں ورنہ حق ان پر بالکل واضح ہے تو اب تم انہیں معاف کردو اور ان سے درگزر کرو یہاں تک کہ خدا اپنا کوئی حکم بھیج دے اور اللہ ہر شے پر قادر ہے'),
(117, 2, 110, 'اور تم نماز قائم کرو اور زکوِٰ ادا کرو کہ جو کچھ اپنے واسطے پہلے بھیج دو گے سب خدا کے یہاں مل جائے گا. خدا تمہارے اعمال کو خوب دیکھنے والا ہے'),
(118, 2, 111, 'یہ یہودی کہتے ہیں کہ جنّت میں یہودیوں اور عیسائیوں کے علاوہ کوئی داخل نہ ہوگا. یہ صرف ان کی امیدیں ہیں. ان سے کہہ دیجئے کہ اگر تم سچّے ہو تو کوئی دلیل لے آؤ'),
(119, 2, 112, 'نہیں جو شخص اپنا رخ خدا کی طرف کردے گااور نیک عمل کرے گا اس کے لئے پروردگار کے یہاں اجر ہے اور نہ کوئی خوف ہے نہ حزن'),
(120, 2, 113, 'اور یہودی کہتے ہیں کہ نصاریٰ کا مذہب کچھ نہیں ہے اور نصاریٰ کہتے ہیں کہ یہودیوں کی کوئی بنیاد نہیں ہے حالانکہ دونوں ہی کتاب الٰہی کی تلاوت کرتے ہیں اور اس کے پہلے جاہل مشرکین عرب بھی یہی کہا کرتے تھے. خدا ان سب کے درمیان روزِ قیامت فیصلہ کرنے والا ہے'),
(121, 2, 114, 'اور اس سے بڑھ کر ظالم کون ہوگا جو مساجد خدا میں اس کا نام لینے سے منع کرے اور ان کی بربادی کی کوشش کرے. ان لوگوں کا حصّہ صرف یہ ہے کہ مساجد میں خوفزدہ ہوکر داخل ہوں اور ان کے لئے دنیا میں رسوائی ہے اور آخرت میں عذاب عظیم'),
(122, 2, 115, 'اور اللہ کے لئے مشرق بھی ہے اور مغرب بھی لہٰذا تم جس جگہ بھی قبلہ کا رخ کرلو گے سمجھو وہیں خدا موجود ہے وہ صاحبِ وسعت بھی ہے اور صاحبِ علم بھی ہے'),
(123, 2, 116, 'اور یہودی کہتے ہیں کہ خدا کے اولاد بھی ہے حالانکہ وہ پاک و بے نیاز ہے. زمین و آسمان میں جو کچھ بھی ہے سب اسی کا ہے اور سب اسی کے فرمانبردار ہیں'),
(124, 2, 117, 'وہ زمین و آسمان کا موجد ہے اور جب کسی امر کا فیصلہ کرلیتا ہے تو صرف کن کہتا ہے اور وہ چیز ہوجاتی ہے'),
(125, 2, 118, 'یہ جاہل مشرکین کہتے ہیں کہ خدا ہم سے کلام کیوں نہیں کرتا اور ہم پر آیت کیوں نازل نہیں کرتا. ان کے پہلے والے بھی ایسی ہی جہالت کی باتیں کرچکے ہیں. ان سب کے دل ملتے جلتے ہیں اور ہم نے تو اہلِ یقین کے لئے آیات کو واضح کردیا ہے'),
(126, 2, 119, 'اے پیغمبر ہم نے آپ کو حق کے ساتھ بشارت دینے والا اور ڈرانے والا بناکر بھیجا ہے اور آپ سے اہلِ جہنّم کے بارے میں کوئی سوال نہ کیا جائے گا'),
(127, 2, 120, 'یہود و نصاریٰ آپ سے اس وقت تک راضی نہ ہوں گے جب تک آپ ان کی ملّت کی پیروی نہ کرلیں تو آپ کہہ دیجئے کہ ہدایت صرف ہدایت هپروردگار ہے--- اور اگر آپ علم کے آنے کے بعد ان کی خواہشات کی پیروی کریں گے تو پھر خدا کے عذاب سے بچانے والا نہ کوئی سرپرست ہوگا نہ مددگار'),
(128, 2, 121, 'اور جن لوگوں کو ہم نے قرآن دیا ہے وہ اس کی باقاعدہ تلاوت کرتے ہیں اور ان ہی کا اس پر ایمان بھی ہے اور جو اس کا انکار کرے گااس کاشمار خسارہ والوں میں ہوگا'),
(129, 2, 122, 'اے بنی اسرائیل ان نعمتوں کو یاد کرو جو ہم نے تمہیں عنایت کی ہیں اور جن کے طفیل تمہیں سارے عالم سے بہتر بنا دیا ہے'),
(130, 2, 123, 'اور اس دن سے ڈرو جس دن کوئی کسی کا فدیہ نہ ہوسکے گا اور نہ کوئی معاوضہ کام آئے گا نہ کوئی سفارش ہوگی اور نہ کوئی مدد کی جاسکے گی'),
(131, 2, 124, 'اور اس وقت کو یاد کرو جب خدانے چند کلمات کے ذریعے ابراہیم علیھ السّلام کا امتحان لیا اور انہوں نے پورا کردیا تو اس نے کہا کہ ہم تم کو لوگوں کا امام اور قائد بنا رہے ہیں. انہوں نے عرض کی کہ میری ذریت? ارشاد ہوا کہ یہ عہدئہ امامت ظالمین تک نہیں جائے گا'),
(132, 2, 125, 'اور اس وقت کو یاد کرو جب ہم نے خانہ کعبہ کو ثواب اور امن کی جگہ بنایا اور حکم دے دیا کہ مقام ابراہیم کو مصّلی بناؤ اور ابراہیم علیھ السّلام و اسماعیل علیھ السّلام سے عہد لیا کہ ہمارے گھر کو طواف اور اعتکاف کرنے والوںاو ر رکوع و سجدہ کرنے والوں کے لئے پاک و پاکیزہ بنائے رکھو'),
(133, 2, 126, 'اور اس وقت کویاد کرو جب ابراہیم علیھ السّلام نے دعا کی کہ پروردگار اس شہر کو امن کاشہر قرار دے دے اور اس کے ان اہلِ شہر کو جو اللہ اور آخرت پر ایمان رکھتے ہوں پھلوں کا رزق عطا فرما. ارشاد ہوا کہ پھر جو کافر ہوجائیں گے انہیں دنیا میں تھوڑی نعمتیں دے کر آخرت میں عذابِ جہّنم میں زبردستی ڈھکیل دیا جائے گا جو بدترین انجام ہے'),
(134, 2, 127, 'اور اس وقت کو یاد کرو جب ابراہیم علیھ السّلامو اسماعیل علیھ السّلامخانہ کعبہ کی دیواروں کو بلند کر رہے تھے اوردل میں یہ دعا تھی کہ پروردگار ہماری محنت کو قبول فرمالے کہ تو بہترین سننے والا اورجاننے والا ہے'),
(135, 2, 128, 'پروردگار ہم دنوںکو اپنا مسلمان او ر فرمانبردار قرار دے دے اور ہماری اولاد میں بھی ایک فرمانبردار امت پیدا کر. ہمیں ہمارے مناسک دکھلا دے اورہماری توبہ قبول فرما کہ تو بہترین توبہ قبول کرنے والا مہربان ہے'),
(136, 2, 129, 'پروردگار ان کے درمیان ایک رسول کو مبعوث فرما جو ان کے سامنے تیری آیتوں کی تلاوت کرے. انہیں کتاب و حکمت کی تعلیم دے اور انکے نفوس کو پاکیزہ بنائے. بے شک تو صاحبِ عزّت اور صاحبِ حکمت ہے'),
(137, 2, 130, 'اور کون ہے جو ملّاُ ابراہیم علیھ السّلام سے اعراض کرے مگر یہ کہ اپنے ہی کوبے وقوف بنائے . اور ہم نے انہیں دنیا میں منتخب قرار دیا ہے اور وہ آخرت میں نیک کردار لوگوں میں ہیں'),
(138, 2, 131, 'جب ان سے ان کے پروردگار نے کہا کہ اپنے کو میرے حوالے کردو تو انہوں نے کہا کہ میں رب العالمین کے لئے سراپا تسلیم ہوں'),
(139, 2, 132, 'اور اسی بات کی ابراہیم علیھ السّلام اور یعقوب علیھ السّلام نے اپنی اولاد کو وصیت کی کہ اے میرے فرزندوں اللہ نے تمہارے لئے دین کو منتخب کردیا ہے اب اس وقت تک دنیا سے نہ جانا جب تک واقعی مسلمان نہ ہوجاؤ'),
(140, 2, 133, 'کیا تم اس وقت تک موجود تھے جب یعقوب علیھ السّلام کا وقاُ موت آیا اور انہوں نے اپنی اولاد سے پوچھا کہ میرے بعد کس کی عبادت کرو گے تو انہوں نے کہا کہ آپ کے اور آپ کے آباؤ اجداد ابراہیم علیھ السّلام و اسماعیل علیھ السّلامو اسحاق علیھ السّلامکے پروردگار خدائے وحدہِ لاشریک کی اور ہم اسی کے مسلمان اور فرمانبردار ہیں'),
(141, 2, 134, 'یہودیو! یہ قوم تھی جو گزر گئی انہیں وہ ملے گا جو انہوں نے کمایا اور تمہیں وہ ملے گا جو تم کماؤ گے تم سے ان کے اعمال کے بارے میں سوال نہ ہوگا'),
(142, 2, 135, 'اور یہ یہودی اور عیسائی کہتے ہیں کہ تم لوگ بھی یہودی اورعیسائی ہوجاؤ تاکہ ہدایت پا جاؤ توآپ کہہ دیں کہ صحیح راستہ باطل سے کترا کر چلنے والے ابراہیم علیھ السّلام کا راستہ ہے کہ وہ مشرکین میں نہیں تھے'),
(143, 2, 136, 'اور مسلمانو! تم ان سے کہو کہ ہم اللہ پر اور جو اس نے ہماری طرف بھیجا ہے اور جو ابراہیم علیھ السّلامً اسماعیل علیھ السّلام ً اسحاق علیھ السّلامً یعقوب علیھ السّلامً اولاد یعقوب علیھ السّلام کی طرف نازل کیا ہے اور جو موسیٰ علیھ السّلام ً عیسیٰ علیھ السّلام اور انبیائ علیھ السّلام کو پروردگار کی طرف سے دیا گیا ہے ان سب پر ایمان لے آئے ہیں. ہم پیغمبروں علیھ السّلام میں تفریق نہیں کرتے اور ہم خدا کے سچّے مسلمان ہیں'),
(144, 2, 137, 'اب اگر یہ لوگ بھی ایسا ہی ایمان لے آئیں گے تو ہدایت یافتہ ہوجائیں گے اور اگر اعراض کریںگے تو یہ صرف عناد ہوگا اور عنقریب اللہ تمہیں ان سب کے شر سے بچا لے گا کہ وہ سننے والا بھی ہے اورجاننے والا بھی ہے'),
(145, 2, 138, 'رنگ تو صرف اللہ کا رنگ ہے اور اس سے بہتر کس کا رنگ ہوسکتا ہے اور ہم سب اسی کے عبادت گزار ہیں'),
(146, 2, 139, 'اے رسول کہہ دیجئے کہ کیا تم ہم سے اس خدا کے بارے میں جھگڑتے ہو جو ہمارا بھی پروردگار ہے اور تمہارا بھی---- تو ہمارے لئے ہمارے اعمال ہیں اور تمہارے لئے تمہارے اعمال اورہم تو صرف خدا کے مخلص بندے ہیں'),
(147, 2, 140, 'کیا تمہارا کہنا یہ ہے کہ ابراہیم علیھ السّلامً اسماعیل علیھ السّلامً اسحاق ً یعقوب علیھ السّلام ا ور اولادیعقوب علیھ السّلام یہودی یا نصرانی تھے تو اے رسول کہہ دیجئے کہ کیا تم خدا سے بہتر جانتے ہو .اور اس سے بڑا ظالم کون ہوگا جس کے پاس خدائی شہادت موجود ہو اور وہ پھر پردہ پوشی کرے اور اللہ تمہارے اعمال سے غافل نہیں ہے'),
(148, 2, 141, 'یہ امّت گزر چکی ہے اس کا حصّہ وہ ہے جو اس نے کیا ہے اور تمہارا حصّہ وہ ہے جو تم کرو گے اور خدا تم سے ان کے اعمال کے بارے میں کوئی سوال نہیں کرے گا'),
(149, 2, 142, 'عنقریب احمق لوگ یہ کہیں گے کہ ان مسلمانوں کو اس قبلہ سے کس نے موڑ دیا ہے جس پر پہلے قائم تھے تو اے پیغمبر کہہ دیجئے کہ مشرق و مغرب سب خدا کے ہیں وہ جسے چاہتاہے صراط مستقیم کی ہدایت دے دیتا ہے'),
(150, 2, 143, 'اور تحویل قبلہ کی طرح ہم نے تم کو درمیانی اُمت قرار دیا ہے تاکہ تم لوگوںکے اعمال کے گواہ رہو اور پیغمبر تمہارے اعمال کے گواہ رہیں اور ہم نے پہلے قبلہ کو صرف اس لئے قبلہ بنایا تھا کہ ہم یہ دیکھیں کہ کون رسول کا اتباع کرتاہے اور کون پچھلے پاؤں پلٹ جاتا ہے. اگرچہ یہ قبلہ ان لوگوںکے علاوہ سب پر گراں ہے جن کی اللہ نے ہدایت کردی ہے اور خدا تمہارے ایمان کو ضائع نہیں کرنا چاہتا. وہ بندوں کے حال پر مہربان اور رحم کرنے والا ہے'),
(151, 2, 144, 'اے رسول ہم آپ کی توجہ کو آسمان کی طرف دیکھ رہے ہیں تو ہم عنقریب آپ کو اس قبلہ کی طرف موڑ دیں گے جسے آپ پسند کرتے ہیں لہٰذا آپ اپنا رخ مسجدالحرام کی جہت کی طرف موڑ دیجئے اور جہاں بھی رہئے اسی طرف رخ کیجئے. اہلِ کتاب خوب جانتے ہیں کہ خدا کی طرف سے یہی برحق ہے اور اللہ ان لوگوں کے اعمال سے غافل نہیں ہے'),
(152, 2, 145, 'اگر آپ ان اہلِ کتاب کے پاس تمام آیتیں بھی پیش کردیں کہ یہ آپ کے قبلہ کو مان لیں تو ہرگز نہ مانیں گے اور آپ بھی ان کے قبلہ کو نہ مانیں گے اور یہ آپس میں بھی ایک دوسرے کے قبلہ کو نہیں مانتے اور علم کے آجانے کے بعد اگر آپ ان کی خواہشات کا اتباع کرلیں گے تو آپ کا شمار ظالموں میں ہوجائے گا'),
(153, 2, 146, 'جن لوگوں کو ہم نے کتاب دی ہے وہ رسول کو بھی اپنی اولاد کی طرح پہچانتے ہیں. بس ان کا ایک گروہ ہے جو حق کو دیدہ و دانستہ چھپا رہا ہے'),
(154, 2, 147, 'اے رسول یہ حق آپ کے پروردگار کی طرف سے ہے لہٰذاآپ ان شک وشبہ کرنے والوں میں نہ ہوجائیں'),
(155, 2, 148, 'ہر ایک کے لئے ایک رخ معین ہے اور وہ اسی کی طرف منہ کرتا ہے. اب تم نیکیوں کی طرف سبقت کرو اور تم سب جہاں بھی رہوگے خدا ایک دن سب کو جمع کردے گا کہ وہ ہر شے پر قادر ہے'),
(156, 2, 149, 'پیغمبر آپ جہاںسے باہر نکلیں اپنا رخ مسجد الحرام کی سمت ہی رکھیں کہ یہی پروردگار کی طرف سے حق ہے--- اور اللہ تم لوگوںکے اعمال سے غافل نہیں ہے'),
(157, 2, 150, 'اور آپ جہاں سے بھی نکلیں اپنا رخ خانئہ کعبہ کی طرف رکھیں اور پھر تم سب جہاں رہو تم سب بھی اپنا رخ ادھر ہی رکھو تاکہ لوگوں کے لئے تمہارے اوپر کوئی حجت نہ رہ جائے سوائے ان لوگوں کے کہ جو ظالم ہیں تو ان کا خوف نہ کرو بلکہ اللہ سے ڈرو کہ ہم تم پر اپنی نعمت تمام کردینا چاہتے ہیں کہ شاید تم ہدایت یافتہ ہوجاؤ'),
(158, 2, 151, 'جس طرح ہم نے تمہارے درمیان تم ہی میں سے ایک رسول بھیجا ہے جو تم پر ہماری آیات کی تلاوت کرتاہے تمہیں پاک و پاکیزہ بنا تا ہے اور تمہیں کتاب و حکمت کی تعلیم دیتا ہے اور وہ سب کچھ بتاتاہے جو تم نہیں جانتے تھے'),
(159, 2, 152, 'اب تم ہم کو یاد کرو تاکہ ہم تمہیں یاد رکھیں اور ہمارا شکریہ ادا کرو اور کفرانِ نعمت نہ کرو'),
(160, 2, 153, 'ایمان والو! صبر اور نماز کے ذریعہ مدد مانگو کہ خدا صبر کرنے والوں کے ساتھ ہے'),
(161, 2, 154, 'اور جو لوگ راہ هخدا میں قتل ہوجاتے ہیں انہیں مفِدہ نہ کہو بلکہ وہ زندہ ہیں لیکن تمہیں ان کی زندگی کا شعور نہیں ہے'),
(162, 2, 155, 'اور ہم یقینا تمہیں تھوڑے خوف تھوڑی بھوک اور اموالًنفوس اور ثمرات کی کمی سے آزمائیں گے اور اے پیغمبر آپ ان صبر کرنے والوں کو بشارت دے دیں'),
(163, 2, 156, 'جو مصیبت پڑنے کے بعد یہ کہتے ہیں کہ ہم اللہ ہی کے لئے ہیں اور اسی کی بارگاہ میں واپس جانے والے ہیں'),
(164, 2, 157, 'کہ ان کے لئے پروردگار کی طرف سے صلوات اور رحمت ہے اور وہی ہدایت یافتہ ہیں'),
(165, 2, 158, 'بے شک صفا اور مروہ دونوںپہاڑیاں اللہ کی نشانیوں میں ہیںلہٰذاجو شخص بھی حج یا عمرہ کرے اس کے لئے کوئی حرج نہیں ہے کہ ان دونوں پہاڑیوںکا چکر لگائے اور جو مزید خیر کرے گا تو خدا اس کے عمل کا قدر دان اوراس سے خوب واقف ہے'),
(166, 2, 159, 'جو لوگ ہمارے نازل کئے ہوئے واضح بیانات اور ہدایات کو ہمارے بیان کردینے کے بعد بھی حُھپاتے ہیں ان پر اللہ بھی لعنت کرتا ہے اور تمام لعنت کرنے والے بھی لعنت کرتے ہیں'),
(167, 2, 160, 'علاوہ ان لوگوں کے جو توبہ کرلیں اور اپنے کئے کی اصلاح کرلیں اور جس کو چھپایا ہے اس کو واضح کردیں تو ہم ان کی توبہ قبول کرلیتے ہیں کہ ہم بہترین توبہ قبول کرنے والے اور مہربان ہیں'),
(168, 2, 161, 'جو لوگ کافرہوگئے اور اسی حالاُ کفر میں مر گئے ان پر اللہ ملائکہ اور تمام انسانوں کی لعنت ہے'),
(169, 2, 162, 'وہ اسی لعنت میں ہمیشہ رہیں گے کہ نہ ان کے عذاب میں تخفیف ہوگی اور نہ انہیں مہلت دی جائے گی'),
(170, 2, 163, 'اور تمہارا خدا بس ایک ہے. اس کے علاوہ کوئی خدا نہیں ہے وہی رحمان بھی ہے اور وہی رحیم بھی ہے'),
(171, 2, 164, 'بیشک زمین و آسمان کی خلقت روز و شب کی رفت وآمد. ان کشتیوں میں جو دریاؤں میں لوگوں کے فائدہ کے لئے چلتی ہیں اور اس پانی میں جسے خدا نے آسمان سے نازل کرکے اس کے ذریعہ مفِدہ زمینوںکو زندہ کردیا ہے اور اس میں طرح طرح کے چوپائے پھیلا دیئے ہیںاور ہواؤں کے چلانے میں اور آسمان و زمین کے درمیان لَسخر کئے جانے والے بادل میں صاحبانِ عقل کے لئے اللہ کی نشانیاں پائی جاتی ہیں'),
(172, 2, 165, 'لوگوں میں کچھ ا یسے لوگ بھی ہیں جو اللہ کے علاوہ دوسروں کو اس کا مثل قرار دیتے ہیں اور ان سے اللہ جیسی محبت بھی کرتے ہیں جب کہ ایمان والوںکی تمام تر محبّت خدا سے ہوتی ہے اور اے کاش ظالمین اس بات کو اس وقت دیکھ لیتے جو عذاب کو دیکھنے کے بعد سمجھیں گے کہ ساری قوت صرف اللہ کے لئے ہے اور اللہ سخت ترین عذاب کرنے والا ہے'),
(173, 2, 166, 'اس وقت جبکہ پیر اپنے مریدوں سے بیزاری کا اظہار کریں گے اور سب کے سامنے عذاب ہوگا اور تمام وسائل منقطع ہوچکے ہوں گے'),
(174, 2, 167, 'اور مرید بھی یہ کہیں گے کہ اے کاش ہم نے ان سے اسی طرح بیزاری اختیار کی ہوتی جس طرح یہ آج ہم سے نفرت کر رہے ہیں. خدا ان سب کے اعمال کو اسی طرح حسرت بنا کر پیش کرے گا اور ان میں سے کوئی جہّنم سے نکلنے والا نہیں ہے'),
(175, 2, 168, 'اے انسانو! زمین میں جو کچھ بھی حلال و طیب ہے اسے استعمال کرو اور شیطانی اقدامات کا اتباع نہ کرو کہ وہ تمہاراکھلا ہوا دشمن ہے'),
(176, 2, 169, 'وہ بس تمہیں بدعملی اور بدکاری کا حکم دیتا ہے اوراس بات پر آمادہ کرتاہے کہ خدا کے خلاف جہالت کی باتیںکرتے رہو'),
(177, 2, 170, 'جب ان سے کہاجاتا ہے کہ جو کچھ خدا نے نازل کیا ہے اس کا اتباع کرو تو کہتے ہیں کہ ہم اس کا اتباع کریں گے جس پر ہم نے اپنے باپ دادا کوپایا ہے. کیا یہ ایسا ہی کریں گے چاہے ان کے باپ دادا بے عقل ہی رہے ہوں اورہدایت یافتہ نہ رہے ہوں'),
(178, 2, 171, 'جو لوگ کافر ہوگئے ہیں ان کے پکارنے والے کی مثال اس شخص کی ہے جو جانوروں کو آواز دے اور جانور پکار اور آواز کے علاوہ کچھ نہ سنیں اور نہ سمجھیں. یہ کفار بہرےً گونگے اور اندھے ہیں. انہیں عقل سے سروکار نہیںہے'),
(179, 2, 172, 'صاحبانِ ایمان جو ہم نے پاکیزہ رزق عطا کیا ہے اسے کھاؤ اور دینے والے خدا کا شکریہ ادا کرو اگر تم اس کی عبادت کرتے ہو'),
(180, 2, 173, 'اس نے تمہارے اوپر بس مفِدارً خونً سور کا گوشت اور جو غیر خدا کے نام پر ذبح کیا جائے اس کو حرام قرار دیا ہے پھر بھی اگر کوئی مضطر ہوجائے اور حرام کاطلب گار اور ضرورت سے زیادہ استعمال کرنے والا نہ ہو تو اس کے لئے کوئی گناہ نہیں ہے . بیشک خدا بخشنے والا اورمہربان ہے'),
(181, 2, 174, 'جو لوگ خدا کی نازل کی ہوئی کتاب کے احکام کو حُھپاتے ہیں اور اسے تھوڑی قیمت پر بیچ ڈالتے ہیں وہ درحقیقت اپنے پیٹ میں صرف آگ بھر رہے ہیں اور خدا روز هقیامت ان سے بات بھی نہ کرے گااور نہ انہیں پاکیزہ قرار دے گا اور ان کے لئے دردناک عذاب ہے'),
(182, 2, 175, 'یہی وہ لوگ ہیں جنہوں نے گمراہی کو ہدایت کے عوض اور عذاب کو مغفرت کے عوض خرید لیا ہے آخریہ آتش جہّنم پر کس قدرصبرکریں گے'),
(183, 2, 176, 'یہ عذاب صرف اس لئے ہے کہ اللہ نے کتاب کو حق کے ساتھ نازل کیا ہے اور اس میں اختلاف کرنے والے حق سے بہت دورہوکر جھگڑے کررہے ہیں'),
(184, 2, 177, 'نیکی یہ نہیں ہے کہ اپنا رخ مشرق اور مغرب کی طرف کرلو بلکہ نیکی اس شخص کا حصّہ ہے جو اللہ اور آخرت ملائکہ اور کتاب پر ایمان لے آئے اورمحبت خدا میں قرابتداروں ً یتیموںً مسکینوں ً غربت زدہ مسافروںً سوال کرنے والوں اور غلاموں کی آزادی کے لئے مال دے اور نماز قائم کرے اور زکوِٰ ادا کرے اور جو بھی عہد کرے اسے پوراکرے اور فقر وفاقہ میں اور پریشانیوں اور بیماریوں میں اور میدانِ جنگ کے حالات میں صبرکرنے والے ہوںتویہی لوگ اپنے دعوائے ایمان و احسان میں سچے ہیں اور یہی صاحبان تقویٰ اور پرہیزگار ہیں'),
(185, 2, 178, 'ایمان والو! تمہارے اوپر مقتولین کے بارے میں قصاص لکھ دیا گیا ہے آزادکے بدلے آزاد اورغلام کے بدلے غلام اور عورت کے بدلے عورت. اب اگرکسی کو مقتول کے وارث کی طرف سے معافی مل جائے تو نیکی کا اتباع کرے اور احسان کے ساتھ اس کے حق کو ادا کردے. یہ پروردگار کی طرف سے تمہارے حق میں تخفیف اور رحمت ہے لیکن اب جو شخص زیادتی کرے گا اس کے لئے دردناک عذاب بھی ہے'),
(186, 2, 179, 'صاحبانِ عقل تمہارے لئے قصاص میں زندگی ہے کہ شاید تم اس طرح متقی بن جاؤ'),
(187, 2, 180, 'تمہارے اوپر یہ بھی لکھ دیا ہے کہ جب تم میں سے کسی کی موت سامنے آجائے تو اگر کوئی مال چھوڑا ہے تواپنے ماں باپ اور قرابتداروں کے لئے وصیت کردے یہ صاحبانِ تقویٰ پر ایک طرح کا حق ہے'),
(188, 2, 181, 'اس کے بعد وصیت کو سن کر جو شخص تبدیل کردے گا اس کا گناہ تبدیل کرنے والے پر ہوگا تم پر نہیں. خدا سب کا سننے والا اور سب کے حالات سے باخبر ہے'),
(189, 2, 182, 'پھر اگر کوئی شخص وصیت کرنے والے کی طرف سے طرفداری یا ناانصافی کا خوف رکھتاہو اور وہ ورثہ میں صلح کرادے تو اس پر کوئی گناہ نہیں ہے. اللہ بڑا بخشنے والا اور مہربان ہے'),
(190, 2, 183, 'صاحبانِ ایمان تمہارے اوپر روزے اسی طرح لکھ دیئے گئے ہیں جس طرح تمہارے پہلے والوںپر لکھے گئے تھے شایدتم اسی طرح متقی بن جاؤ'),
(191, 2, 184, 'یہ روزے صرف چند دن کے ہیںلیکن اس کے بعد بھی کوئی شخص مریض ہے یا سفر میں ہے تو اتنے ہی دن دوسرے زمانے میں رکھ لے گا اور جو لوگ صرف شدت اور مشقت کی بنائ پر روزے نہیں رکھ سکتے ہیں وہ ایک مسکین کو کھانا کھلا دیں اور اگراپنی طرف سے زیادہ نیکی کردیں تو اور بہتر ہے--- لیکن روزہ رکھنا بہرحال تمہارے حق میں بہتر ہے اگر تم صاحبانِ علم و خبر ہو'),
(192, 2, 185, 'ماسِ رمضان وہ مہینہ ہے جس میں قرآن نازل کیا گیا ہے جو لوگوں کے لئے ہدایت ہے اور اس میں ہدایت اور حق و باطل کے امتیاز کی واضح نشانیاں موجود ہیں لہٰذا جو شخص اس مہینہ میں حاضر رہے اس کا فرض ہے کہ روزہ رکھے اور جو مریض یا مسافر ہو وہ اتنے ہی دن دوسرے زمانہ میں رکھے. خدا تمہارے بارے میں آسانی چاہتا ہے زحمت نہیں چاہتا. اور اتنے ہی دن کا حکم اس لئے ہے کہ تم عدد پورے کردو اور اللہ تعالیٰ کی دی ہوئی ہدایت پر اس کی کبریائی کا اقرار کرو اور شاید تم اس طرح اس کے شکر گزار بندے بن جاؤ'),
(193, 2, 186, 'اور اے پیغمبر! اگر میرے بندے تم سے میرے بارے میں سوال کریں تو میں ان سے قریب ہوں. پکارنے والے کی آواز سنتا ہوں جب بھی پکارتا ہے لہٰذا مجھ سے طلب قبولیت کریں اور مجھ ہی پرایمان و اعتماد رکھیں کہ شاید اس طرح راسِ راست پر آجائیں'),
(194, 2, 187, 'تمہارے لئے ماہ رمضان کی رات میں عورتوں کے پاس جانا حلال کر دےا گےا ہے۔ہو تمہارے لئے پردہ پوش ہیں اور تم انکے لئے ۔خدا کو معلام ہے کہ تم اپنے ہی نفس سے خیانت کرتے تھے تو اس نے تمہاری توبہ قبول کرکے تمہےں معاف کر دےا۔ اب تم بہ اطمینان مباشرت کرو اور جو خدا نے تمہارے لئے مقدر کیا ہے اس کی آرزو کرو اوراس وقت تک کھا پی سکتے ہوجب تک فجر کا سیاہ ڈورا ،سفید ڈورے سے نمایاں نہ ہو جائے۔ اس کے بعد رات کی سیاہی تک روزہ کو پورا کرو اور خبردار مسجدوںمیں اعتکاف کے موقع پر عورتوں سے مباشرت نہ کرنا۔یہ سب مقررہ حدود الٰہی ہیں ۔ان کے قرےب بھی نہ جانا ۔اللہ اس طرح اپنی آیتوں کو لوگوں کے لئے واضح طور پر بیان کرتا ہے کہ شاید وہ متقی اور پرہیزگار بن جائیں'),
(195, 2, 188, 'اور خبردار ایک دوسرے کا مال ناجائز طریقے سے نہ کھانااور نہ حکام کے حوالہ کر دینا کہ رشوت دےکر حرام طریقے سے لوگوں کے اموال کو کھا جاﺅ،جب کہ تم جانتے ہو کہ یہ تمہارا مال نہیں ہے'),
(196, 2, 189, 'اے پیمبر ےہ لوگ آپ سے چاند کے بارے میں سوال کرتے ہیں تو فرمادیجئے کہ ےہ لوگوں کے لئے اور حج کے لئے وقت معلوم کرنے کا ذریعہ ہے۔اور یہ کوئی نیکی نہیں ہے کہ مکانات میں پچھواڑے کی طرف سے آﺅ،بلکہ نیکی ان کے لئے ہے جو پرہیزگار ہوں اور مکانات میں دروازوں کی طرف سے آئیں اور اللہ سے ڈرو شاید تم کامیاب ہو جاﺅ'),
(197, 2, 190, 'جولوگ تم سے جنگ کرتے ہیں تم بھی ان سے راہِ خدا میں جہادکرو اور زےادتی نہ کرو کہ خدا زیادتی کرنے والوں کو دوست نہیں رکھتا'),
(198, 2, 191, 'اور ان مشرکین کو جہاں پاؤ قتل کردو اور جس طرح انہوں نے تم کو آوارہ وطن کردیا ہے تم بھی انہیں نکال باہر کردو--- اور فتنہ پردازی تو قتل سے بھی بدتر ہے. اور ان سے مسجد الحرام کے پاس اس وقت تک جنگ نہ کرنا جب تک وہ تم سے جنگ نہ کریں. اس کے بعد جنگ چھیڑ دیں تو تم بھی چپ نہ بیٹھو اور جنگ کرو کہ یہی کافرین کی سزا ہے'),
(199, 2, 192, 'پھر اگر جنگ سے باز آجائیں تو خدا بڑا بخشنے والا مہربان ہے'),
(200, 2, 193, 'اوران سے اس وقت تک جنگ جاری رکھو جب تک سارا فتنہ ختم نہ ہوجائے اور دین صرف اللہ کا نہ رہ جائے پھر اگر وہ لوگ باز آجائیں توظالمین کے علاوہ کسی پر زیادتی جائز نہیں ہے'),
(201, 2, 194, 'شہر حرام کا جواب شہر حرام ہے اور حرمات کا بھی قصاص ہے لہٰذا جو تم پر زیادتی کرے تم بھی ویسا ہی برتاؤ کرو جیسی زیادتی اس نے کی ہے اور اللہ سے ڈرتے رہو اور یہ سمجھ لو کہ خدا پرہیزگاروں ہی کے ساتھ ہے'),
(202, 2, 195, 'او رراسِ خدا میں خرچ کرو اور اپنے نفس کو ہلاکت میں نہ ڈالو. نیک برتاؤ کرو کہ خدا نیک عمل کرنے والوں کے ساتھ ہے'),
(203, 2, 196, 'حج اور عمرہ کو اللہ کے لئے تمام کرو اب اگر گرفتار ہوجاؤ توجو قربانی ممکن ہو وہ دے دو اور اس وقت تک سر نہ منڈواؤ جب تک قربانی اپنی منزل تک نہ پہنچ جائے. اب جو تم میں سے مریض ہے یا اس کے سر میں کوئی تکلیف ہے تو وہ روزہ یاصدقہ یا قربانی دے دے پھر جب اطمینان ہوجائے تو جس نے عمرہ سے حج تمتع کا ارادہ کیا ہے وہ ممکنہ قربانی دے دے اور قربانی نہ مل سکے تو تین روزے حج کے دوران اور سات واپس آنے کے بعد رکھے کہ اس طرح دس پورے ہوجائیں. یہ حج تمتع اور قربانی ان لوگوں کے لئے ہے جن کے اہلِ مسجدالحرام کے حاضر شمار نہیں ہوتے اور اللہ سے ڈرتے رہو اوریہ یاد رکھو کہ خدا کا عذاب بہت سخت ہے'),
(204, 2, 197, 'حج چند مقررہ مہینوں میں ہوتا ہے اور جو شخص بھی اس زمانے میں اپنے اوپر حج لازم کرلے اسے عورتوں سے مباشرتً گناہ اور جھگڑے کی اجازت نہیں ہے اور تم جو بھی خیر کرو گے خدا اسے جانتا ہے اپنے لئے زادُ راہ فراہم کرو کہ بہترین زادُ راہ تقویٰ ہے اوراے صاحبانِ عقل! ہم سے ڈرو'),
(205, 2, 198, 'تمہارے لئے کوئی حرج نہیں ہے کہ اپنے پروردگار کے فضل وکرم کو تلاش کرو پھر جب عرفات سے کوچ کرو تو مشعرالحرام کے پاس ذکر خداکرو اور اس طرح ذکر کرو جس طرح اس نے ہدایت دی ہے اگرچہ تم لوگ اس کے پہلے گمراہوں میں سے تھے'),
(206, 2, 199, 'پھر تمام لوگوں کی طرح تم بھی کوچ کرو اور اللہ سے استغفار کروکہ اللہ بڑا بخشنے والا اور مہربان ہے'),
(207, 2, 200, 'پھر جب سارے مناسک تمام کرلو توخدا کو اسی طرح یاد رکھوجس طرح اپنے باپ دادا کو یاد کرتے ہو بلکہ اس سے بھی زیادہ کہ بعض لوگ ایسے ہیں جو کہتے ہیں کہ پروردگار ہمیں دنیا ہی میں نیکی دے دے اور ان کا آخرت میں کوئی حصّہ نہیں ہے'),
(208, 2, 201, 'اور بعض کہتے ہیں کہ پروردگار ہمیں دنیا میں بھی نیکی عطا فرما اورآخرت میں بھی اور ہم کو عذاب جہّنم سے محفوظ فرما'),
(209, 2, 202, 'یہی وہ لوگ ہیں جن کے لئے ان کی کمائی کا حصّہ ہے اور خدا بہت جلد حساب کرنے والاہے'),
(210, 2, 203, 'اور چند معین دنوں میں ذک» خدا کرو. اسکے بعد جو دو دن کے اندر جلدی کرے گا اس پر بھی کوئی گناہ نہیں ہے اور جو تاخیر کرے گا اس پر بھی کوئی گناہ نہیں ہے بشرطیکہ پرہیزگار رہا ہو اور اللہ سے ڈرو اور یہ یاد رکھو کہ تم سب اسی کی طرف محشور کئے جاؤ گے'),
(211, 2, 204, 'انسانوں میں ایسے لوگ بھی ہیں جن کی باتیں زندگانی دنیا میںبھلی لگتی ہیں اور وہ اپنے دل کی باتوں پر خدا کو گواہ بناتے ہیں حالانکہ وہ بدترین دشمن ہیں'),
(212, 2, 205, 'اور جب آپ کے پاس سے منہ پھیرتے ہیں تو زمین میں فساد برپا کرنے کی کوشش کرتے ہیں اور کھیتیوں اور نسلوں کو برباد کرتے ہیں جب کہ خدا فساد کو پسند نہیں کرتا ہے'),
(213, 2, 206, 'جب ان سے کہا جاتاہے کہ تقویٰ الٰہی اختیار کرو تو غرور گناہ کے آڑے آجاتا ہے ایسے لوگوں کے لئے جہّنم کافی ہے جو بدترین ٹھکانا ہے'),
(214, 2, 207, 'اور لوگوں میں وہ بھی ہیں جو اپنے نفس کو مرضی پروردگار کے لئے بیچ ڈالتے ہیں اور اللہ اپنے بندوں پر بڑا مہربان ہے'),
(215, 2, 208, 'ایمان والو! تم سب مکمل طریقہ سے اسلام میں داخل ہوجاؤ اور شیطانی اقدامات کا اتباع نہ کرو کہ وہ تمہارا کھلا ہوا دشمن ہے'),
(216, 2, 209, 'پھر اگر کھلی نشانیوں کے آجانے کے بعد لغزش پیدا ہوجائے تو یاد رکھو کہ خدا سب پر غالب ہے اور صاحبِ حکمت ہے'),
(217, 2, 210, 'یہ لوگ اس بات کا انتظار کررہے ہیں کہ ابر کے سایہ کے پیچھے عذابِ خدا یا ملائکہ آجائیں اور ہر امر کا فیصلہ ہوجائے اور سارے امور کی بازگشت تو خدا ہی کی طرف ہے'),
(218, 2, 211, 'ذرا بنی اسرائیل سے پوچھئے کہ ہم نے انہیں کس قدر نعمتیں عطا کی ہیں---- او رجو شخص بھی نعمتوں کے آجانے کے بعد انہیں تبدیل کردے گا وہ یاد رکھے کہ خدا کاعذاب بہت شدید ہوتا ہے'),
(219, 2, 212, 'اصل میں کافروں کے لئے زندگانی دنیا آراستہ کردی گئی ہے اور وہ صاحبانِ ایمان کا مذاق اڑاتے ہیں حالانکہ قیامت کے دن متقی اور پرہیزگار افراد کا درجہ ان سے کہیں زیادہ بالاتر ہوگا اور اللہ جس کو چاہتا ہے بے حساب رزق دیتا ہے'),
(220, 2, 213, '( فطری اعتبار سے) سارے انسان ایک قوم تھے . پھر اللہ نے بشارت دینے والے اور ڈرانے والے انبیائ بھیجے اور ان کے ساتھ برحق کتاب نازل کی تاکہ لوگوں کے اختلافات کا فیصلہ کریں اور اصل اختلاف ان ہی لوگوں نے کیا جنہیں کتاب مل گئی ہے اور ان پر آیات واضح ہوگئیں صرف بغاوت اور تعدی کی بنا پر----- تو خدا نے ایمان والوں کو ہدایت دے دی اور انہوں نے اختلافات میں حکم الٰہی سے حق دریافت کرلیا اور وہ تو جس کو چاہتا ہے صراظُ مستقیم کی ہدایت دے دیتا ہے'),
(221, 2, 214, 'کیا تمہارا خیال ہے کہ تم آسانی سے جنّت میںداخل ہوجاؤ گے جبکہ ابھی تمہارے سامنے سابق اُمتوں کی مثال پیش نہیں آئی جنہیںفقر و فاقہ اور پریشانیوں نے گھیر لیا اور اتنے جھٹکے دیئے گئے کہ خود رسول اور ان کے ساتھیوں نے یہ کہناشروع کردیا کہ آخر خدائی امداد کب آئے گی. تو آگاہ ہوجاؤ کہ خدائی امداد بہت قریب ہے'),
(222, 2, 215, 'پیغمبر یہ لوگ آپ سے سوال کرتے ہیں کہ راسِ خدا میں کیا خرچ کریں تو آپ کہہ دیجئے کہ جو بھی خرچ کرو گے وہ تمہارے والدینً قرابتدرً ایتامً مساکین اور غربت زدہ مسافروں کے لئے ہوگا اور جو بھی کا»خیر کروگے خدا اسے خوب جانتا ہے'),
(223, 2, 216, 'تمہارے اوپر جہاد فرض کیا گیا ہے اور وہ تمہیں ناگوار ہے اور یہ ممکن ہے کہ جسے تم اِرا سمجھتے ہو وہ تمہارے حق میں بہتر ہو اور جسے تم دوست رکھتے ہو وہ اِرا ہو خدا سب کو جانتا ہے اور تم نہیں جانتے ہو'),
(224, 2, 217, 'پیغمبر یہ آپ سے محترم مہینوں کے جہاد کے بارے میں سوال کرتے ہیں تو آپ کہہ دیجئے کہ ان میں جنگ کرنا گناہ کبیرہ ہے اور راسِ خدا سے روکنا اور خدا اورمسجدالحرام کی حرمت کا انکار ہے اور اہلِ مسجد الحرام کا وہاںسے نکال دینا خدا کی نگاہ میں جنگ سے بھی بدتر گناہ ہے اور فتنہ تو قتل سے بھی بڑا جرم ہے--- اور یہ کفار برابر تم لوگوں سے جنگ کرتے رہیں گے یہاں تک کہ ان کے امکان میں ہو تو تم کو تمہارے دین سے پلٹا دیں. اور جو بھی اپنے دین سے پلٹ جائے گا اور کفر کی حالت میں مر جائے گا اس کے سارے اعمال برباد ہوجائیں گئے اور وہ جہنّمی ہوگا اور وہیں ہمیشہ رہے گا'),
(225, 2, 218, 'بےشک جو لوگ ایمان لائے اور جنہوں نے ہجرت کی اور راہ خدا میں جہاد کیا وہ رحمت الٰہی کی امید رکھتے ہیں اور خدا بہت بخشنے والا اور مہربان ہے'),
(226, 2, 219, 'یہ آپ سے شراب اورجوئے کے بارے میں سوال کرتے ہیں تو کہہ دیجئے کہ ان دونوں میں بہت بڑا گناہ ہے اور بہت سے فائدے بھی ہیں لیکن ان کا گناہ فائدے سے کہیں زیادہ بڑا ہے اور یہ راسِ خدا میں خرچ کے بارے میں سوال کرتے ہیں کہ کیا خرچ کریں تو کہہ دیجئے کہ جو بھی ضرورت سے زیادہ ہو. خدا اسی طرح اپنی آیات کو واضح کرکے بیان کرتا ہے کہ شاید تم فکر کرسکو'),
(227, 2, 220, 'دنیا میں بھی اور آخرت میں بھی---- اور یہ لوگ تم سے یتیموں کے بارے میں سوال کرتے ہیں تو کہہ دو کہ ان کے حال کی اصلاح بہترین بات ہے اور اگر ان سے مل چُل کر رہو تو یہ بھی تمہارے بھائی ہیں اور اللہ بہتر جانتاہے کہ مصلح کون ہے اور مفسدکون ہے اگر وہ چاہتا تو تمہیں مصیبت میں ڈال دیتا لیکن وہ صاحبِ عزّت بھی ہے اور صاحبِ حکمت بھی ہے'),
(228, 2, 221, 'خبردار مشرک عورتوں سے اس وقت تک نکاح نہ کرنا جب تک ایمان نہ لے آئیں کہ ایک مومن کنیز مشرک آزاد عورت سے بہتر ہے چاہے وہ تمہیں کتنی ہی بھلی معلوم ہو اورمشرکین کو بھی لڑکیاں نہ دینا جب تک مسلمان نہ ہوجائیں کہ مسلمان غلام آزاد مشرک سے بہتر ہے چاہے وہ تمہیں کتنا ہی اچھا کیوں نہ معلوم ہو. یہ مشرکین تمہیں جہّنم کی دعوت دیتے ہیں اور خدا اپنے حکم سے جنّت اورمغفرت کی دعوت دیتا ہے اور اپنی آیتوں کو واضح کرکے بیان کرتاہے کہ شاید یہ لوگ سمجھ سکیں'),
(229, 2, 222, 'اور اے پیغمبر یہ لوگ تم سے ایاّم حیض کے بارے میں سوال کرتے ہیں تو کہہ دو کہ حیض ایک اذیت اور تکلیف ہے لہذا اس زمانے میں عورتوں سے الگ رہو اور جب تک پاک نہ ہوجائیں ان کے قریب نہ جاؤ پھر جب پاک ہوجائیں تو جس طرح سے خدا نے حکم دیا ہے اس طرح ان کے پاس جاؤ. بہ تحقیق خدا توبہ کرنے والوں اور پاکیزہ رہنے والوں کو دوست رکھتا ہے'),
(230, 2, 223, 'تمہاری عورتیں تمہاری کھیتیاں ہیں لہٰذا اپنی کھیتی میں جہاں چاہو داخل ہوجاؤ اور اپنے واسطے پیشگی اعمال خدا کی بارگاہ میں بھیج دو اور اس سے ڈرتے رہو---- یہ سمجھو کہ تمہیں اس سے ملاقات کرنا ہے اورصاحبانِ ایمان کو بشارت دے دو'),
(231, 2, 224, 'خبردار خدا کو اپنی قسموںکا نشانہ نہ بناؤ کہ قسموںکو نیکی کرنےً تقویٰ اختیارکرنے اور لوگوں کے درمیان اصلاح کرنے میں مانع بنادو اللہ سب کچھ سننے اور جاننے والا ہے'),
(232, 2, 225, 'خدا تمہاری لغواور غیر ارادی قسموںکا مواخذہ نہیں کرتا ہے لیکن جس کو تمہارے دلوں نے حاصل کیا ہے اس کا ضرور مواخذہ کرے گا. وہ بخشنے والا بھی ہے اور برداشت کرنے والا بھی'),
(233, 2, 226, 'جو لوگ اپنی بیویوں سے ترک جماع کی قسم کھا لیتے ہیں انہیں چار مہینے کی مہلت ہے. اس کے بعد واپس آگئے تو خدا غفوررحیم ہے'),
(234, 2, 227, 'اورطلاق کا ارادہ کریں تو خدا سن بھی رہا ہے اور دیکھ بھی رہاہے'),
(235, 2, 228, 'مطلقہ عورتیں تین حیض تک انتظار کریں گی اور انہیں حق نہیں ہے کہ جو کچھ خدا نے ان کے رحم میں پیدا کیا ہے اس کی پردہ پوشی کریں اگر ان کا ایمان اللہ اور آخرت پر ہے. اورپھر ان کے شوہر اس مدّت میں انہیں واپس کرلینے کے زیادہ حقدار ہیں اگر اصلاح چاہتے ہیں .اور عورتوں کے لئے ویسے ہی حقوق بھی ہیں جیسی ذمہ داریاں ہیں اور مردوں کو ان پر ایک امتیاز حاصل ہے اور خدا صاحبِ عزّت و حکمت ہے'),
(236, 2, 229, 'طلاق دو مرتبہ دی جائے گی. اس کے بعد یا نیکی کے ساتھ روک لیا جائے گا یا حسنِ سلوک کے ساتھ آزاد کردیا جائے گا اور تمہارے لئے جائز نہیں ہے کہ جو کچھ انہیں دے دیا ہے اس میں سے کچھ واپس لو مگر یہ کہ یہ اندیشہ ہو کہ دونوں حدود الٰہی کو قائم نہ رکھ سکیں گے تو جب تمہیں یہ خوف پیدا ہوجائے کہ وہ دونوں حدود الٰہی کو قائم نہ رکھ سکیں گے تو دونوں کے لئے آزادی ہے اس فدیہ کے بارے میں جو عورت مرد کو دے. لیکن یہ حدود الہٰیہ ہیں ان سے تجاوز نہ کرنا اور جو حدود الٰہی سے تجاوز کرے گا وہ ظالمین میں شمار ہوگا'),
(237, 2, 230, 'پھر اگر تیسری مرتبہ طلاق دے دی تو عورت مرد کے لئے حلال نہ ہوگی یہاں تک کہ دوسرا شوہر کرے پھر اگر وہ طلاق دے دے تو دونوں کے لئے کوئی حرج نہیں ہے کہ آپس میں میل کرلیں اگر یہ خیال ہے کہ حدود الہٰیہ کو قائم رکھ سکیں گے. یہ حدود الہٰیہ ہیں جنہیں خدا صاحبانِ علم واطلاع کے لئے واضح طور سے بیان کررہا ہے'),
(238, 2, 231, 'اور جب تم عورتوں کو طلاق دو اور وہ مدّاُ عدت کے خاتمہ کے قریب پہنچ جائیں تو یا انہیں اصلاح اور حسنِ معاشرت کے ساتھ روک لو یا انہیں حسنِ سلوک کے ساتھ آزاد کردو اور خبردار نقصان پہنچانے کی غرض سے انہیں نہ روکنا کہ ان پر ظلم کرو کہ جو ایسا کرے گا وہ خود اپنے نفس پر ظلم کرے گا اور خبردار آیات الٰہی کو مذاق نہ بناؤ. خدا کی نعمت کو یاد کرو. اس نے کتاب و حکمت کو تمہاری نصیحت کےلئے نازل کیا ہے اور یاد رکھو کہ وہ ہر شے کا جاننے والا ہے'),
(239, 2, 232, 'اور جب تم لوگ عورتوں کو طلاق دو اور ان کی مدّاُ عدت پوری ہوجائے تو خبردار انہیں شوہر کرنے سے نہ روکنا اگر وہ شوہروں کے ساتھ نیک سلوک پر راضی ہوجائیں. اس حکم کے ذریعے خدا انہیں نصیحت کرتا ہے جن کا ایمان اللہ اور روزِ آخرت پر ہے اور ان احکام پر عمل تمہارے لئے باعجُ تزکیہ بھی ہے اور باعث طہارت بھی. اللہ سب کچھ جانتا ہے اور تم نہیں جانتے ہو'),
(240, 2, 233, 'اور مائیں اپنی اولاد کو دو برس کامل دودھ پلائیں گی جو رضاعت کو پورا کرنا چاہے گا اس درمیان صاحب اولاد کا فرض ہے کہ ماؤں کی روٹی اور کپڑے کا مناسب طریقہ سے انتظام کرے. کسی شخص کو اس کی وسعت سے زیادہ تکلیف نہیں دی جاسکتی. نہ ماں کو اس کی اولاد کے ذریعہ تکلیف دینے کا حق ہے اور نہ باپ کو اس کی اولاد کے ذریعہ. اور وارث کی بھی ذمہ داری ہے کہ وہ اسی طرح اجرت کا انتظام کرے. پھر اگر دونوں باہمی رضامندی اور مشورہ سے دودھ چھڑانا چاہیں تو کوئی حرج نہیں ہے اور اگر تم اپنی اولاد کے لئے دودھ پلانے والی تلاش کرنا چاہو تو اس میں بھی کوئی حرج نہیں ہے بشرطیکہ متعارف طریقہ کی اجرت ادا کردو اور اللہ سے ڈرو اور یہ سمجھو کہ وہ تمہارے اعمال کو خوب دیکھ رہا ہے'),
(241, 2, 234, 'اور جو لوگ تم میں سے بیویاں چھوڑ کر مرجائیں ان کی بیویاں چار مہینے دس دن انتظار کریں گی جب یہ مدّت پوری ہو جائے تو جو مناسب کام اپنے حق میں کریں اس میں کوئی حرج نہیں ہے خدا تمہارے اعمال سے خو ب باخبر ہے'),
(242, 2, 235, 'تمہارے لئے نکاح کے پیغام کی پیشکش یا دل ہی دل میں پوشیدہ ارادہ میں کوئی حرج نہیں ہے. خدا کو معلوم ہے کہ تم بعد میں ان سے تذکرہ کرو گے لیکن فی الحال خفیہ وعدہ بھی نہ لو صرف کوئی نیک بات کہہ دو تو کوئی حرج نہیں ہے اور جب تک مقررہ مدّت پوری نہ ہوجائے عقد نکاح کا ارادہ نہ کرنا یہ یاد رکھو کہ خدا تمہارے دل کی باتیں خوب جانتا ہے لہذا اس سے ڈرتے رہو اور یہ جان لو کہ وہ غفور بھی ہے اور حلیم واِردبار بھی'),
(243, 2, 236, 'اور تم پر کوئی ذمہ داری نہیں ہے اگر تم نے عورتوں کو اس وقت طلاق دے دی جب کہ ان کو چھوا بھی نہیں ہے اور ان کے لئے کوئی مہر بھی معین نہیں کیا ہے البتہ انہیں کچھ مال و متاع دیدو. مالدار اپنی حیثیت کے مطابق اور غریب اپنی حیثیت کے مطابق. یہ متاع بقدر مناسب ہونا ضروری ہے کہ یہ نیک کرداروں پر ایک حق ہے'),
(244, 2, 237, 'اور اگر تم نے ان کو چھونے سے پہلے طلاق دے دی اور ان کے لئے مہر معین کر چکے تھے تو معین مہر کا نصف دینا ہوگا مگر یہ کہ وہ خود معاف کردیں یا ان کا ولی معاف کر دے اور معاف کردینا تقویٰ سے زیادہ قریب تر ہے اور آپس میں بزرگی کو فراموش نہ کرو. خدا تمہارے اعمال کو خوب دیکھ رہا ہے'),
(245, 2, 238, 'اپنی تمام نمازوں اور بالخصوص نماز وسطٰی کی محافظت اور پابندی کرو اور اللہ کی بارگاہ میں خشوع و خضوع کے ساتھ کھڑے ہوجاؤ'),
(246, 2, 239, 'پھر اگر خوف کی حالت ہو تو پیدل ًسوار جس طرح ممکن ہو نماز اداکرو اور جب اطمینان ہوجائے تو اس طرح ذکر خدا کرو جس طرح اس نے تمہاری لاعلمی میں تمہیں بتایا ہے'),
(247, 2, 240, 'اور جو لوگ مدّاُ حیات پوری کررہے ہوں اور ازواج کو چھوڑ کر جارہے ہوں انہیں چاہئے کہ اپنی ازواج کے لئے ایک سال کے خرچ اور گھر سے نہ نکالنے کی وصیت کرکے جائیں پھر اگر وہ خود سے نکل جائیں تو تمہارے لئے کوئی حرج نہیں ہے وہ اپنے بارے میں جو بھی مناسب کام انجام دیں خدا صاحبِ عزّت اور صاحبِ حکمت بھی ہے'),
(248, 2, 241, 'اور مطلقہ عورتوں کو دستور کے مطابق کچھ خرچہ دینا، یہ متقی لوگوں کی ذمے داری ہے'),
(249, 2, 242, 'اسی طرح پروردگار اپنی آیات کو بیان کرتا ہے کہ شاید تمہیں عقل آجائے'),
(250, 2, 243, 'کیا تم نے ان لوگوں کو نہیں دیکھا جو ہزاروں کی تعداد میںاپنے گھروں سے نکل پڑے موت کے خوف سے اور خدا نے انہیںموت کا حکم دے دیا اور پھر زندہ کردیا کہ خدا لوگوں پر بہت فضل کرنے والا ہے لیکن اکثر لوگ شکریہ نہیں ادا کرتے ہیں'),
(251, 2, 244, 'اور راسِ خدا میں جہاد کرو اور یاد رکھو کہ خدا سننے والا بھی ہے اور جاننے والا بھی ہے'),
(252, 2, 245, 'کون ہے جو خدا کو قرض حسن دے اور پھر خدا اسے کئی گنا کرکے واپس کردے خدا کم بھی کرسکتاہے اور زیادہ بھی اور تم سب اسی کی بارگاہ میں پلٹائے جاؤگے'),
(253, 2, 246, 'کیا تم نے موسیٰ علیھ السّلامکے بعد بنی اسرائیل کی اس جماعت کونہیں دیکھا جس نے اپنے نبی سے کہا کہ ہمارے واسطے ایک بادشاہ مقرر کیجئے تاکہ ہم راسِ خدا میں جہاد کریں. نبی نے فرمایا کہ اندیشہ یہ ہے کہ تم پر جہادواجب ہوجائے توتم جہاد نہ کرو. ان لوگوں نے کہا کہ ہم کیوںکر جہاد نہ کریں گے جب کہ ہمیں ہمارے گھروں اور بال بّچوںسے الگ نکال باہر کردیا گیا ہے. اس کے بعد جب جہاد واجب کردیا گیا تو تھوڑے سے افراد کے علاوہ سب منحرف ہوگئے اور اللہ ظالمین کو خوب جانتا ہے'),
(254, 2, 247, 'ان کے پیغمبر علیھ السّلامنے کہا کہ اللہ نے تمہارے لئے طالوت کو حاکم مقرر کیا ہے. ان لوگوں نے کہا کہ یہ کس طرح حکومت کریں گے ان کے پاس تو مال کی فراوانی نہیں ہے ان سے زیادہ تو ہم ہی حقدار حکومت ہیں. نبی نے جواب دیا کہ انہیں اللہ نے تمہارے لئے منتخب کیا ہے اور علم و جسم میں وسعت عطا فرمائی ہے اور اللہ جسے چاہتا ہے اپنا ملک دے دیتا ہے کہ وہ صاحبِ وسعت بھی ہے اور صاحبِ علم بھی'),
(255, 2, 248, 'اور ان کے پیغمبر علیھ السّلام نے یہ بھی کہا کہ ان کی حکومت کی نشانی یہ ہے یہ تمہارے پاس وہ تابوت لے آئیں گے جس میںپروردگار کی طرف سے سامانِ سکون اور آل موسیٰ علیھ السّلام اور آل ہارون علیھ السّلام کا چھوڑا ہوا ترکہ بھی ہے. اس تابوت کو ملائکہ اٹھائے ہوئے ہوں گے اور اس میں تمہارے لئے قدراُ پروردگار کی نشانی بھی ہے اگر تم صاحبِ ایمان ہو'),
(256, 2, 249, 'اس کے بعد جب طالوت علیھ السّلام لشکر لے کرچلے تو انہوں نے کہا کہ اب خدا ایک نہر کے ذریعہ تمہارا امتحان لینے والا ہے جو اس میں سے پی لے گا وہ مجھ سے نہ ہوگا اور جو نہ چکھے گا وہ مجھ سے ہوگا مگر یہ کہ ایک چلّو پانی لے لے. نتیجہ یہ ہوا کہ سب نے پانی پی لیا سوائے چند افراد کے---- پھر جب وہ صاحبانِ ایمان کو لے کر آگے بڑھے لوگوں نے کہا کہ آج تو جالوت اور اس کے لشکروں کے مقابلہ کی ہمت نہیں ہے اور ایک جماعت نے جسے خدا سے ملاقات کرنے کا خیال تھا کہا کہ اکثر چھوٹے چھوٹے گروہ بڑی بڑی جماعتوں پر حکم خدا سے غالب آجاتے ہیں اور اللہ صبر کرنے والوں کے ساتھ ہے'),
(257, 2, 250, 'اور یہ لوگ جب جالوت اور اس کے لشکروں کے مقابلے کے لئے نکلے تو انہوں نے کہا کہ خدایا ہمیں بے پناہ صبر عطا فرما ہمارے قدموں کو ثبات دے اور ہمیں کافروں کے مقابلہ میں نصرت عطا فرما'),
(258, 2, 251, 'نتیجہ یہ ہوا کہ ان لوگوں نے جالوت کے لشکر کو خدا کے حکم سے شکست دے دی اور داؤد علیھ السّلامنے جالوت کو قتل کردیا اور اللہ نے انہیں ملک اور حکمت عطاکردی اور اپنے علم سے جس قدر چاہا دے دیا اور اگر اسی طرح خدا بعض کو بعض سے نہ روکتا رہتا تو ساری زمین میں فساد پھیل جاتا لیکن خدا عالمین پر بڑا فضل کرنے والا ہے'),
(259, 2, 252, 'یہ آیااُ الہٰیہ ہیں جن کو ہم واقعیت کے ساتھ آپ کو پڑھ کر سناتے ہیں اور آپ یقینا مرسلین میں سے ہیں'),
(260, 2, 253, 'یہ سب رسول علیھ السّلاموہ ہیں جنہیں ہم نے بعض کو بعض پر فضیلت دی ہے. ان میں سے بعض وہ ہیں جن سے خدا نے کلام کیا ہے اور بعض کے درجات بلند کئے ہیں اور ہم نے عیسیٰ علیھ السّلام بن مریم کو کھلی ہوئی نشانیاںدی ہیں اور روح القدس کے ذریعہ ان کی تائید کی ہے. اگر خدا چاہتا تو ان رسولوں کے بعد والے ان واضح معجزات کے آجانے کے بعد آپس میں جھگڑا نہ کرتے لیکن ان لوگوں نے (خدا کے جبر نہ کرنے کی بنا پر) اختلاف کیا ہے . بعض ایمان لائے اور بعض کافر ہو گئے اور اگر خدا طے کر لیتا تو یہ جھگڑا نہ کرسکتے لیکن خدا جو چاہتا ہے وہی کرتا ہے'),
(261, 2, 254, 'اے ایمان والو جو تمہیں رزق دیا گیا ہے اس میں سے راسِ خدا میں خرچ کرو قبل اسکے کہ وہ دن آجائے جس دن نہ تجارت ہوگی نہ دوستی کام آئے گی اور نہ سفارش. اورکافرین ہی اصل میں ظالمین ہیں'),
(262, 2, 255, 'اللہ جس کے علاوہ کوئی خدا نہیں ہے زندہ بھی ہے اوراسی سے کِل کائنات قائم ہے اسے نہ نیند آتی ہے نہ اُونگھ آسمانوں اور زمین میں جو کچھ بھی ہے سب اسی کا ہے. کون ہے جو اس کی بارگاہ میں اس کی اجازت کے بغیر سفارش کرسکے. وہ جو کچھ ان کے سامنے ہے اور جو پبُ پشت ہے سب کوجانتا ہے اور یہ اس کے علم کے ایک حّصہ کا بھی احاطہ نہیں کرسکتے مگر وہ جس قدر چاہے. اس کی کرس علم و اقتدار زمین و آسمان سے وسیع تر ہے اور اسے ا ن کے تحفظ میں کوئی تکلیف بھی نہیں ہوتی وہ عالی مرتبہ بھی ہے اور صاحبِ عظمت بھی'),
(263, 2, 256, 'دین میں کسی طرح کا جبر نہیں ہے. ہدایت گمراہی سے الگ اور واضح ہوچکی ہے. اب جو شخص بھی طاغوت کا انکار کرکے اللہ پر ایمان لے آئے وہ اس کی مضبوط رسّی سے متمسک ہوگیا ہے جس کے ٹوٹنے کا امکان نہیں ہے اور خدا سمیع بھی ہے اور علیم بھی ہے'),
(264, 2, 257, 'اللہ صاحبانِ ایمان کا ولی ہے وہ انہیں تاریکیوں سے نکال کر روشنی میں لے آتا ہے اور کفارکے ولی طاغوت ہیں جو انہیں روشنی سے نکال کر اندھیروں میں لے جاتے ہیں یہی لوگ جہّنمی ہیں اور وہاں ہمیشہ رہنے والے ہیں'),
(265, 2, 258, 'کیا تم نے اس کے حال پر نظر نہیں کی جس نے ابراہیم علیھ السّلام سے پروردگار کے بارے میں بحث کی صرف اس بات پر کہ خدا نے اسے ملک دے دیا تھا جب ابرہیم علیھ السّلام نے یہ کہا کہ میرا خدا جلِتا بھی ہے اور مارتا بھی ہے تو اس نے کہا کہ یہ کام میں بھی کرسکتا ہوں تو ابراہیم علیھ السّلام نے کہا کہ میرا خدامشرق سے آفتاب نکالتا ہے تو مغرب سے نکال دے تو کافر حیران رہ گیا اور اللہ ظالم قوم کی ہدایت نہیں کرتا ہے'),
(266, 2, 259, 'یا اس بندے کی مثال جس کا گذر ایک قریہ سے ہوا جس کے سارے عرش و فرش گرچکے تھے تو اس بندہ نے کہا کہ خدا ان سب کو موت کے بعد کس طرح زندہ کرے گا تو خدا نے اس بندہ کو سوسال کے لئے موت دے دی اورپھرزندہ کیا اور پوچھا کہ کتنی دیر پڑے رہے تو اس نے کہا کہ ایک دن یا کچھ کم . فرمایا نہیں . سو سال ذرا اپنے کھانے اور پینے کو تو دیکھو کہ خراب تک نہیں ہوا اور اپنے گدھے پر نگاہ کرو (کہ سڑ گل گیا ہے) اور ہم اسی طرح تمہیں لوگوں کے لئے ایک نشانی بنانا چاہتے ہیں پھر ان ہڈیوںکو دیکھو کہ ہم کس طرح جوڑ کر ان پر گوشت چڑھاتے ہیں. پھر جب ان پر یہ بات واضح ہوگئی تو بیساختہ آواز دی کہ مجھے معلوم ہے کہ خدا ہر شے پرقادر ہے'),
(267, 2, 260, 'اور اس موقع کو یاد کرو جب ابراہیم علیھ السّلامنے التجا کی کہ پروردگار مجھے یہ دکھا دے کہ تو مفِدوںکو کس طرح زندہ کرتا ہے . ارشاد ہوا کیا تمہارا ایمان نہیں ہے . عرض کی ایمان توہے لیکن اطمینان چاہتا ہوں . ارشاد ہوا کہ چار طائر پکڑلو اور انہیں اپنے سے مانوس بناؤ پھر ٹکڑے ٹکڑے کرکے ہر پہاڑ پر ایک حصّہ رکھ دو اور پھرآواز دو سب دوڑتے ہوئے آجائیں گے اور یاد رکھو کہ خدا صاحبِ عزّت بھی اور صاحبِ حکمت بھی'),
(268, 2, 261, 'جو لوگ راسِ خدا میں اپنے اموال خرچ کرتے ہیں ان کے عمل کی مثال اس دانہ کی ہے جس سے سات بالیاں پیدا ہوں اور پھر ہر بالی میں سو سو دانے ہوںاور خدا جس کے لئے چاہتا ہے اضافہ بھی کردیتا ہے کہ وہ صاحبِ وسعت بھی ہے اور علیم و دانا بھی'),
(269, 2, 262, 'جو لوگ راسِ خدا میں اپنے اموال خرچ کرتے ہیں اور اس کے بعد احسان نہیں جتاتے اور اذّیت بھی نہیں دیتے ان کے لئے پروردگار کے یہاں اجر بھی ہے اور نہ کوئی خوف ہے نہ حزن'),
(270, 2, 263, 'نیک کلام اور مغفرت اس صدقہ سے بہتر ہے جس کے پیچھے دل دکھانے کا سلسلہ بھی ہو . خدا سب سے بے نیاز اور بڑا اِرد بار ہے'),
(271, 2, 264, 'ایمان والو اپنے صدقات کو منّت گزاری اور اذیت سے برباد نہ کرو اس شخص کی طرح جو اپنے مال کو دنیا دکھانے کے لئے صرف کرتاہے اور اس کا ایمان نہ خدا پر ہے اور نہ آخرت پر اس کی مثال اس صاف چٹان کی ہے جس پر گرد جم گئی ہو کہ تیز بارش کے آتے ہی بالکل صاف ہوجائے یہ لوگ اپنی کمائی پر بھی اختیار نہیں رکھتے اور اللہ کافروں کی ہدایت بھی نہیں کرتا'),
(272, 2, 265, 'اور جو لوگ اپنے اموال کو رضائے خدا کی طلب اور اپنے نفس کے استحکام کی غرض سے خرچ کرتے ہیں ان کے مال کی مثال اس باغ کی ہے جو کسی بلندی پر ہو اور تیز بارش آکر اس کی فصل دوگنا بنا دے اور اگر تیز بارش نہ آئے تو معمولی بارش ہی کافی ہوجائے اور اللہ تمہارے اعمال کی نیتوں سے خوب باخبر ہے'),
(273, 2, 266, 'کیا تم میں سے کوئی یہ پسند کرتا ہے کہ اس کے پاس کھجور اور انگور کے باغ ہوں. ان کے نیچے نہریں جاری ہوں ان میں ہر طرح کے پھل ہوں--- اور آدمی بوڑھاہوجائے . اس کے کمزور بچے ہوں اور پھراچانک تیز گرم ہوا جس میں آگ بھری ہو چل جائے اور سب جل کر خاک ہوجائے . خداا سی طرح اپنی آیات کو واضح کرکے بیان کرتا ہے کہ شاید تم فکر کرسکو'),
(274, 2, 267, 'اے ایمان والو اپنی پاکیزہ کمائی اور جو کچھ ہم نے زمین سے تمہارے لئے پیدا کیا ہے سب میں سے راسِ خدا میں خرچ کرو اور خبردار انفاق کے ارادے سے خراب مال کو ہاتھ بھی نہ لگانا کہ اگر یہ مال تم کو دیا جائے تو آنکھ بند کئے بغیر چھوؤ گے بھی نہیں یاد رکھو کہ خدا سب سے بے نیاز ہے اور سزاوار حمد و ثنا بھی ہے'),
(275, 2, 268, 'شیطان تم سے فقیری کا وعدہ کرتا ہے اور تمہیں برائیوں کا حکم دیتا ہے اور خدا مغفرت اور فضل و احسان کا وعدہ کرتا ہے . خدا صاحبِ وسعت بھی ہے اور علیم و دانا بھی'),
(276, 2, 269, 'وہ جس کو بھی چاہتا ہے حکمت عطا کردیتا ہے اور جسے حکمت عطا کردی جائے اسے گویاخیرکثیر عطا کردیا گیا اور اس بات کوصاحبانِ عقل کے علاوہ کوئی نہیں سمجھتا ہے'),
(277, 2, 270, 'اور تم جو کچھ بھی راسِ خدا میں خرچ کرو گے یا نذر کروگے تو خدا اس سے باخبر ہے البتہ ظالمین کا کوئی مددگار نہیں ہے'),
(278, 2, 271, 'اگر تم صدقہ کوعلی الاعلان دوگے تو یہ بھی ٹھیک ہے اور اگر حُھپا کر فقرائ کے حوالے کردوگے تو یہ بھی بہت بہترہے اور اس کے ذریعہ تمہارے بہت سے گناہ معاف ہوجائیں گے اور خدا تمہارے اعمال سے خوب باخبر ہے'),
(279, 2, 272, 'اے پیغمبران کی ہدایت پا جانے کی ذمہ داری آپ پر نہیںہے بلکہ خدا جس کو چاہتا ہے ہدایت دے دیتا ہے اورلوگو جو مال بھی تم راسِ خدا میں خرچ کروگے وہ دراصل اپنے ہی لئے ہوگا اور تم تو صرف خوشنودی خدا کے لئے خرچ کرتے ہو اور جوکچھ بھی خرچ کروگے وہ پوراپورا تمہاری طرف واپس آئے گا اور تم پرکسی طرح کا ظلم نہ ہوگا'),
(280, 2, 273, 'یہ صدقہ ان فقرائ کے لئے ہے جو راسِ خدا میں گرفتار ہوگئے ہیں اور کسی طرف جانے کے قابل بھی نہیں ہیں ناواقف افراد انہیں ان کی حیا و عفت کی بنا پر مالدار سمجھتے ہیں حالانکہ تم آثار سے ان کی غربت کا اندازہ کرسکتے ہو اگرچہ یہ لوگوں سے چمٹ کر سوال نہیں کرتے ہیںاور تم لوگ جو کچھ بھی انفاق کرو گے خدا اسے خوب جانتا ہے'),
(281, 2, 274, 'جو لوگ اپنے اموال کو راسِ خدا میں رات میں .دن میں خاموشی سے اور علی الاعلان خرچ کرتے ہیں ان کے لئے پیش پروردگار اجر بھی ہے اور انہیں نہ کوئی خوف ہوگا اور نہ حزن'),
(282, 2, 275, 'جو لوگ سود کھاتے ہیں وہ روزِ قیامت اس شخص کی طرح اُٹھیں گے جسے شیطان نے چھوکر مخبوط الحواس بنا دیا ہو اس لئے کہ انہوں نے یہ کہہ دیا ہے کہ تجارت بھی سود جیسی ہے جب کہ خدا نے تجارت کو حلال قرار دیا ہے اور سودکو حرام . اب جس کے پاس خدا کی طرف سے نصیحت آگئی اور اس نے سود کو ترک کردیاتو گزشتہ کاروبار کا معاملہ خداکے حوالے ہے اور جو اس کے بعد بھی سود لے تو وہ لوگ سب جہّنمی ہیں او ر وہیں ہمیشہ رہنے والے ہیں'),
(283, 2, 276, 'خدا سود کو برباد کردیتا ہے اور صدقات میں اضافہ کردیتا ہے اور خدا کسی بھی ناشکرے گناہگار کو دوست نہیں رکھتا'),
(284, 2, 277, 'جو لوگ ایمان لے آئے اور انہوں نے نیک عمل کئے نماز قائم کی . زکوِٰ ادا کی ان کے لئے پروردگار کے یہاں اجر ہے اور ان کے لئے کسی طرح کا خوف یا حزن نہیں ہے'),
(285, 2, 278, 'ایمان والو اللہ سے ڈرو اور جو سود باقی رہ گیا ہے اسے چھوڑ دو اگر تم صاحبان هایمان ہو'),
(286, 2, 279, 'اگر تم نے ایسا نہ کیا تو خد ا و رسول سے جنگ کرنے کے لئے تیار ہو جاؤ اور اگر توبہ کرلو تو اصل مال تمہارا ہی ہے . نہ تم ظلم کروگے نہ تم پر ظلم کیا جائے گا'),
(287, 2, 280, 'اور اگر تمہارا مقروض تنگ دست ہے تو اسے وسعت حال تک مہلت دی جائے گی اور اگرتم معاف کردو گے توتمہارے حق میں زیادہ بہتر ہے بشرطیکہ تم اسے سمجھ سکو'),
(288, 2, 281, 'اس دن سے ڈرو جب تم سب پلٹا کر اللہ کی بارگاہ میں لے جائے جاؤگے اس کے بعد ہر نفس کو اس کے کئے کا پورا پورا بدلہ ملے گا اور کسی پر کوئی ظلم نہیں کیا جائے گا'),
(289, 2, 282, 'ایمان والو جب بھی آپس میں ایک مقررہ مدّت کے لئے قرض کا لین دین کرو تو اسے لکھ لو اورتمہارے درمیان کوئی بھی کاتب لکھے لیکن انصاف کے ساتھ لکھے اور کاتب کو نہ چاہئے کہ خدا کی تعلیم کے مطابق لکھنے سے انکار کرے . اسے لکھ دینا چاہئے اور جس کے ذمہ قرض ہے اس کو چاہئے کہ وہ املا کرے اور اس خدا سے ڈرتا رہے جو اس کا پالنے والاہے اور کسی طرح کی کمی نہ کرے . اب اگرجس کے ذمہ قرض ہے وہ نادان کمزور یامضمون بتانے کے قابل نہیں ہے تو اس کا ولی مضمون تیار کرے---- اور اپنے مردوں میں سے دو کو گواہ بناؤ اور دو مذِد نہ ہوں تو ایک مذِد اور دو عورتیں تاکہ ایک بہکنے لگے تو دوسری یاد دلادے اور گواہوں کو چاہئے کہ گواہی کے لئے بلائے جائیں تو انکار نہ کریں اور خبردار لکھا پڑھی سے ناگواری کا اظہار نہ کرنا چاہے قرض چھوٹا ہو یا بڑا مّدت معین ہونی چاہئے .یہی پروردگار کے نزدیک عدالت کے مطابق اورگواہی کے لئے زیادہ مستحکم طریقہ ہے او ر اس امر سے قریب تر ہے کہ آپس میںشک و شبہ نہ پیدا ہو---- ہاں اگر تجارت نقد ہے جس سے تم لوگ آپس میں اموال کی الٹ پھیر کرتے ہو تو نہ لکھنے میں کوئی حرج بھی نہیں ہے اور اگر تجارت میں بھی گواہ بناؤ تو کاتب یا گواہ کو حق نہیں کہ اپنے طرزِ عمل سے لوگوں کو نقصان پہنچائے اور نہ لوگوں کو یہ حق ہے اوراگرتم نے ایسا کیا تویہ اطاعت خدا سے نافرمانی'),
(290, 2, 283, 'اور اگر تم سفر میں ہو اور کوئی کاتب نہیں مل رہا ہے تو کوئی رہن رکھ دو اور ایک کو دوسرے پر اعتبار ہو تو جس پراعتبار ہے اس کو چاہئے کہ امانت کو واپس کردے اورخدا سے ڈرتا رہے---- اور خبردار گواہی کو حُھپانا نہیں کہ جو ایسا کرے گا اس کا دل گنہگار ہوگا اور اللہ تمہارے اعمال سے خوب باخبر ہے'),
(291, 2, 284, 'اللہ ہی کے لئے زمین و آسمان کی کِل کائنات ہے تم اپنے دل کی باتوں کا اظہار کرو یا ان پر پردہ ڈالو وہ سب کا محاسبہ کرے گا وہ جس کو چاہے گا بخش دے گااور جس پر چاہے گا عذاب کرے گا وہ ہرشے پر قدرت و اختیار رکھنے والا ہے'),
(292, 2, 285, 'رسول ان تمام باتوں پر ایمان رکھتا ہے جو اس کی طرف نازل کی گئی ہیں اور مومنین بھی سب اللہ اور ملائکہ اور مرسلین پر ایمان رکھتے ہیں ان کا کہنا ہے کہ ہم رسولوں کے درمیان تفریق نہیں کرتے . ہم نے پیغام الٰہی کو صَنا اور اس کی اطاعت کی پروردگار اب تیری مغفرت درکار ہے اور تیری ہی طرف پلٹ کر آنا ہے'),
(293, 2, 286, 'اللہ کس نفس کو اس کی وسعت سے زیادہ تکلیف نہیں دیتا . ہر نفس کے لئے اس کی حاصل کی ہوئی نیکیوں کا فائدہ بھی ہے اور اس کی کمائی ہوئی برائیوں کا مظلمہ بھی---- پروردگار! ہم جو کچھ بھول جائیں یا ہم سے غلطی ہوجائے اس کا ہم سے مواخذہ نہ کرنا . خدایا ہم پر ویسا بوجھ نہ ڈالنا جیسا پہلے والی امتوں پر ڈالا گیا ہے پروردگار ! ہم پر وہ بار نہ ڈالنا جس کی ہم میںطاقت نہ ہو . ہمیں معاف کردیناً ہمیں بخش دیناً ہم پر رحم کرنا توہمارا مولا اور مالک ہے اب کافروں کے مقابلہ میں ہماری مدد فرما');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الۤم'),
(295, 3, 2, 'اللہ جس کے علاوہ کوئی خدا نہیں ہے اور وہ ہمیشہ زندہ ہے اور ہر شے اسی کے طفیل میں قائم ہے'),
(296, 3, 3, 'اس نے آپ پر وہ برحق کتاب نازل کی ہے جو تمام کتابوں کی تصدیق کرنے والی ہے اور توریت و انجیل بھی نازل کی ہے'),
(297, 3, 4, 'اس سے پہلے لوگوں کے لئے ہدایت بنا کر اور حق و باطل میں فرق کرنے والی کتاب بھی نازل کی ہے بیشک جو لوگ آیااُ الٰہی کا انکار کرتے ہیں ان کے واسطے شدید عذاب ہے اور خدا سخت انتقام لینے والا ہے'),
(298, 3, 5, 'خدا کے لئے آسمان و زمین کی کوئی شے مخفی نہیں ہے'),
(299, 3, 6, 'وہ خدا جس طرح چاہتا ہے رحهِ مادر کے اندر تصویریں بناتا ہے اس کے علاوہ کوئی خدا نہیں ہے وہ صاحب عزّت بھی ہے اور صاحب هحکمت بھی'),
(300, 3, 7, 'اس نے آپ پروہ کتاب نازل کی ہے جس میں سے کچھ آیتیں لَحکم اور واضح ہیں جو اصل کتاب ہیں اور کچھ متشابہ ہیں - اب جن کے دلوں میں کجی ہے وہ ا ن ہی متشابہات کے پیچھے لگ جاتے ہیں تاکہ فتنہ برپا کریں اور من مانی تاویلیں کریں حالانکہ اس کی تاویل کا حکم صرف خدا کو ہے اور انہیں جو علم میں رسوخ رکھنے والے ہیں - جن کا کہنا یہ ہے کہ ہم اس کتاب پر ایمان رکھتے ہیں اور یہ سب کی سب محکم و متشابہ ہمارے پروردگار ہی کی طرف سے ہے اور یہ بات سوائے صاحبانِ عقل کے کوئی نہیں سمجھ سکتا ہے'),
(301, 3, 8, 'ان کا کہنا ہے کہ پروردگار جب تونے ہمیں ہدایت دے دی ہے تو اب ہمارے دلوں میں کجی نہ پیدا ہونے پائے اور ہمیں اپنے پاس سے رحمت عطا فرما کہ تو بہترین عطا کرنے والا ہے'),
(302, 3, 9, 'خدایا تو تمام انسانوں کو اس دن جمع کرنے والا ہے جس میں کوئی شک نہیں ہے. اور اللہ کا وعدہ غلط نہیں ہوتا'),
(303, 3, 10, 'جو لوگ کافر ہوگئے ہیں ان کے اموال و اولاد کچھ بھی کام آنے والے نہیں ہیں اور وہ جہّنم کا ایندھن بننے والے ہیں'),
(304, 3, 11, 'ان کا حال بھی فرعونیوں اور ان سے پہلے لوگوں کا سا ہو گا جنھوں نے بمارے آیات کو جھٹلایا، پس الله نے انہیں ان کے گناہوں کے وجہ سے گرفت میں لے لیا اور الله سخت عذاب دینے والا ہے'),
(305, 3, 12, '(اے رسول) جنہوں نے انکار کیا ہے ان سے کہدیجیے: تم عنقریب مغلوب ہو جاؤ گے اور جہنم کے طرف اکھٹے کیے جاؤ گے اور وہ بدترین ٹھکانا ہے'),
(306, 3, 13, 'تمہارے واسطے ان دونوں گروہوں کے حالات میں ایک نشانی موجود ہے جو میدان جنگ میں آمنے سامنے آئے کہ ایک گروہ راسِ خدا میں جہاد کررہا تھا اور دوسرا کافر تھا جو ان مومنین کو اپنے سے دوگنا دیکھ رہا تھا اور اللہ اپنی نصرت کے ذریعہ جس کی چاہتا ہے تائید کرتا ہے اور اس میں صاحبان هنظر کے واسطے سامان هعبرت و نصیحت بھی ہے'),
(307, 3, 14, 'لوگوں کے لئے خواہشااُ دنیا - عورتیں,اولاد, سونے چاندی کے ڈھیر, تندرست گھوڑے یا چوپائےً کھیتیاں سب مزیّن اور آراستہ کردی گئی ہیں کہ یہی متاع دنیا ہے اور اللہ کے پاس بہترین انجام ہے'),
(308, 3, 15, 'پیغمبر آپ کہہ دیں کہ کیا میں ان سب سے بہتر چیز کی خبر دوں- جو لوگ تقوٰی اختیار کرنے والے ہیں ان کے لئے پروردگار کے یہاں وہ باغات ہیں جن کے نیچے نہریں جاری ہیں اور وہ ان میں ہمیشہ رہنے والے ہیں . ان کے لئے پاکیزہ بیویاں ہیں اور اللہ کی خوشنودی ہے اور اللہ اپنے بندوں کے حالات سے خوب باخبر ہے'),
(309, 3, 16, 'قابل تعریف ہیں وہ لوگ جو یہ کہتے ہیں کہ پروردگار ہم ایمان لے آئے- ہمارے گناہوں کو بخش دے اور ہمیں آتش جہّنم سے بچالے'),
(310, 3, 17, 'یہ سب صبر کرنے والے, سچ بولنے والے, اطاعت کرنے والے, راہ خدا میں خرچ کرنے والے اور ہنگاهِ سحر استغفار کرنے والے ہیں'),
(311, 3, 18, 'اللہ خود گواہ ہے کہ اس کے علاوہ کوئی خدا نہیں ہے ملائکہ اور صاحبانِ علم گواہ ہیں کہ وہ عدل کے ساتھ قائم ہے- اس کے علاوہ کوئی خدا نہیں ہے اور وہ صاحبِ عزّت و حکمت ہے'),
(312, 3, 19, 'دین,اللہ کے نزدیک صرف اسلام ہے اور اہل کتاب نے علم آنے کے بعد ہی جھگڑا شروع کیا ہے صرف آپس کی شرارتوں کی بنائ پر اور جو بھی آیات الٰہی کا انکار کرے گا تو خدا بہت جلد حساب کرنے والا ہے'),
(313, 3, 20, 'اے پیغمبر اگر یہ لوگ آپ سے کٹ حجتی کریں تو کہہ دیجئے کہ میرا رخ تمام تر اللہ کی طرف ہے اور میرے پیرو بھی ایسے ہی ہیں اور پھر اہلِ کتاب اور جاہل مشرکین سے پوچھئے کیا تم اسلام لے آئے- اگر وہ اسلام لے آئے تو گویا ہدایت پاگئے اور اگر منہ پھیر لیا تو آپ کا فرض صرف تبلیغ تھا اور اللہ اپنے بندوں کو خوب پہچانتا ہے'),
(314, 3, 21, 'جو لوگ آیات الٰہیہ کا انکار کرتے ہیں اور ناحق انبیائ کو قتل کرتے ہیں اور ان لوگوں کو قتل کرتے ہیں جو عدل و انصاف کا حکم دینے والے ہیں انہیں دردناک عذاب کی خبرصَنادیجئے'),
(315, 3, 22, 'یہی وہ لوگ ہیں جن کے اعمال دنیا میں بھی برباد ہوگئے اور آخرت میں بھی ان کا کوئی مددگار نہیں ہے'),
(316, 3, 23, 'کیا تم نے ان لوگوں کو نہیں دیکھا جنہیں کتاب کا تھوڑا سا حصّہ دے دیا گیا کہ انہیں کتاب هخدا کی طرف فیصلہ کے لئے بلایا جاتا ہے تو ایک فریق مکر جاتا ہے اور وہ بالکل کنارہ کشی کرنے والے ہیں'),
(317, 3, 24, 'یہ اس لئے کہ ان کا عقیدہ ہے کہ انہیں آتش جہّنم صرف چند دن کے لئے چھوئے گی اور ان کو دین کے بارے میں ان کی افتراپردازیوں نے دھوکہ میں رکھا ہے'),
(318, 3, 25, 'اس وقت کیا ہوگا جب ہم سب کو اس دن جمع کریں گے جس میں کسی شک اور شبہہ کی گنجائش نہیں ہے اور ہر نفس کو اس کے کئے کا پورا پورا بدلہ دیا جائے گا اور کسی پر کوئی ظلم نہیں کیا جائے گا'),
(319, 3, 26, 'پیغمبر آپ کہئے کہ خدایا تو صاحب هاقتدار ہے جس کو چاہتا ہے اقتدار دیتا ہے اور جس سے چاہتا ہے سلب کرلیتا ہے- جس کو چاہتا ہے عزّت دیتا ہے اور جس کو چاہتا ہے ذلیل کرتا ہے- سارا خیر تیرے ہاتھ میں ہے اور تو ہی ہر شے پر قادر ہے'),
(320, 3, 27, 'تو رات کو دن اور دن کو رات میں داخل کرتا ہے اور مفِدہ کو زندہ سے اور زندہ کو مفِدہ سے نکالتا ہے اور جسے چاہتا ہے بے حساب رزق دیتا ہے'),
(321, 3, 28, 'خبردار صاحبانِ ایمان .مومنین کو چھوڑ کر کفار کو اپنا و لی اور سرپرست نہ بنائیں کہ جو بھی ایسا کرے گا اس کا خدا سے کوئی تعلق نہ ہوگا مگر یہ کہ تمہیں کفار سے خوف ہو تو کوئی حرج بھی نہیں ہے اور خدا تمہیں اپنی ہستی سے ڈراتا ہے اور اسی کی طرف پلٹ کر جانا ہے'),
(322, 3, 29, 'آپ ان سے کہہ دیجئے کہ تم دل کی باتوں کو چھپاؤ یا اس کا اظہار کرو خدا تو بہرحال جانتا ہے اور وہ زمین و آسمان کی ہر چیز کو جانتا ہے اور ہر شے پر قدرت و اختیار رکھنے والا بھی ہے'),
(323, 3, 30, 'اس دن کو یاد کرو جب ہر نفس اپنے نیک اعمال کو بھی حاضر پائے گا اور اعمال بد کو بھی جن کو دیکھ کر یہ تمناّ کرے گا کہ کاش ہمارے اور ان اِرے اعمال کے درمیان طویل فاصلہ ہوجاتا اور خدا تمہیں اپنی ہستی سے ڈراتا ہے اور وہ اپنے بندوں پر مہربان بھی ہے'),
(324, 3, 31, 'اے پیغمبر! کہہ دیجئے کہ اگر تم لوگ اللہ سے محبّت کرتے ہو تو میری پیروی کرو- خدا بھی تم سے محبّت کرے گا اور تمہارے گناہوں کو بخش دے گا کہ وہ بڑا بخشنے والا اور مہربان ہے'),
(325, 3, 32, 'کہہ دیجئے کہ اللہ اور رسول کی اطاعت کرو کہ جو اس سے روگردانی کرے گا تو خدا کافرین کو ہرگز دوست نہیں رکھتا ہے'),
(326, 3, 33, 'اللہ نے آدم علیھ السّلام,نوح علیھ السّلام اور آل ابراہیم علیھ السّلام اور آل عمران علیھ السّلام کو منتخب کرلیا ہے'),
(327, 3, 34, 'یہ ایک نسل ہے جس میں ایک کا سلسلہ ایک سے ہے اور اللہ سب کی سننے والا اور جاننے والا ہے'),
(328, 3, 35, 'اس وقت کو یاد کرو جب عمران علیھ السّلام کی زوجہ نے کہا کہ پروردگار میں نے اپنے شکم کے بّچے کو تیرے گھر کی خدمت کے لئے نذر کردیا ہے اب تو قبول فرمالے کہ تو ہر ایک کی سننے والا اور نیتوں کا جاننے والا ہے'),
(329, 3, 36, 'اس کے بعد جب ولادت ہوئی تو انہوں نے کہا پروردگار یہ تو لڑکی ہے حالانکہ اللہ خوب جانتا ہے کہ وہ کیا ہے اور وہ جانتا ہے کہ لڑکا اس لڑکی جیسا نہیں ہوسکتا.اور میں نے اس کا نام مریم رکھا ہے اور میں اسے اور اس کی اولاد کو شیطان رجیم سے تیری پناہ میں دیتی ہوں'),
(330, 3, 37, 'تو خدا نے اسے بہترین انداز سے قبول کرلیا اور اس کی بہترین نشوونما کا انتظام فرمادیا اور زکریا علیھ السّلام نے اس کی کفالت کی کہ جب زکریا علیھ السّلاممحراب هعبادت میں داخل ہوتے تو مریم کے پاس رزق دیکھتے اور پوچھتے کہ یہ کہاں سے آیا اور مریم جواب دیتیں کہ یہ سب خدا کی طرف ہے- وہ جسے چاہتا ہے رزق بے حساب عطا کردیتا ہے'),
(331, 3, 38, 'اس وقت زکریا علیھ السّلامنے اپنے پرودگار سے دعا کی کہ مجھے اپنی طرف سے ایک پاکیزہ اولاد عطا فرما کہ تو ہر ایک کی دعا کا سننے والا ہے'),
(332, 3, 39, 'تو ملائکہ نے انہیں اس وقت آواز دی جب وہ محراب میں کھڑے مصرورِ عبادت تھے کہ خدا تمہیں یحیٰی علیھ السّلام کی بشارت دے رہا ہے جو اس کے کلمہ کی تصدیق کرنے والا, سردار, پاکیزہ کردار اور صالحین میں سے نبی ہوگا'),
(333, 3, 40, 'انہوں نے عرض کی کہ میرے یہاں کس طرح اولاد ہوگی جب کہ مجھ پر بڑھاپا آگیا ہے اور میری عورت بھی بانجھ ہے تو ارشاد ہوا کہ خدا اسی طرح جو چاہتا ہے کرتاہے'),
(334, 3, 41, 'انہوں نے کہا کہ پروردگار میرے لئے قبولیت هدعا کی کوئی علامت قرار دے دے- ارشاد ہوا کہ تم تین دن تک اشاروں کے علاوہ بات نہ کرسکو گے اور خدا کا ذکر کثرت سے کرتے رہنا اور صبح و شام اس کی تسبیح کرتے رہنا'),
(335, 3, 42, 'اور اس وقت کو یاد کرو جب ملائکہ نے مریم کو آواز دی کہ خدا نے تمہیں چن لیا ہے اور پاکیزہ بنادیا ہے اور عالمین کی عورتوں میں منتخب قرار دے دیا ہے'),
(336, 3, 43, 'اے مریم تم اپنے پروردگار کی اطاعت کرو, سجدہ کرو, اور رکوع کرنے والوں کے ساتھ رکوع کرو'),
(337, 3, 44, 'پیغمبر یہ غیب کی خبریں ہیں جن کی وحی ہم آپ کی طرف کررہے ہیں اور آپ تو ان کے پاس نہیں تھے جب وہ قرعہ ڈال رہے تھے کہ مریم کی کفالت کون کرے گا اور آپ ان کے پاس نہیں تھے جب وہ اس موضوع پر جھگڑا کررہے تھے'),
(338, 3, 45, 'اور اس وقت کو یاد کرو جب ملائکہ نے کہا کہ اے مریم خدا تم کو اپنے کلمہ مسیح عیسٰی علیھ السّلامبن مریم کی بشارت دے رہا ہے جو دنیا اور آخرت میں صاحبِ وجاہت اور مقربین بارگاہ الٰہی میں سے ہے'),
(339, 3, 46, 'وہ لوگوں سے گہوارہ میں بھی بات کرے گا اور بھرپور جوان ہونے کے بعد بھی اور صالحین میں سے ہوگا'),
(340, 3, 47, 'مریم نے کہا کہ میرے یہاں فرزند کس طرح ہوگا جب کہ مجھ کو کسی بشر نے چھوا بھی نہیں ہے- ارشاد ہوا کہ اسی طرح خدا جو چاہتاہے پیدا کرتا ہے جب وہ کسی کام کا فیصلہ کرلیتا ہے تو کہتا ہے کہ ہوجا اور وہ چیز ہوجاتی ہے'),
(341, 3, 48, 'اور خدا اس فرزند کو کتاب و حکمت اور توریت و انجیل کی تعلیم دے گا'),
(342, 3, 49, 'اور اسے بنی اسرائیل کی طرف رسول بنائے گا اور وہ ان سے کہے گا کہ میں تمھارے پاس تمہارے پروردگار کی طرف سے نشانی لے کر آیا ہوں کہ میں تمہارے لئے مٹی سے پرندہ کی شکل بناؤں گا اور اس میں کچھ دم کردوں گا تو وہ حکهِ خدا سے پرندہ بن جائے گا اور میں پیدائشی اندھے اور َمبروص کا علاج کروں گا اور حکهِ خدا سے مفِدوں کو زندہ کروں گا اور تمہیں اس بات کی خبردوں گا کہ تم کیا کھاتے ہو اور کیا گھر میں ذخیرہ کرتے ہو- ان سب میں تمہارے لئے نشانیاں ہیں اگر تم صاحبانِ ایمان ہو'),
(343, 3, 50, 'اور میں اپنے پہلے کی کتاب توریت کی تصدیق کرنے والا ہوں اور میں بعض ان چیزوں کو حلال قرار دوں گا جو تم پر حرام تھیں اور تمھارے پروردگار کی طرف سے نشانی لے کر آیا ہوں لہذا اس سے ڈرو اور میری اطاعت کرو'),
(344, 3, 51, 'اللہ میرا اور تمہارا دونوں کا رب ہے لہذا اس کی عبادت کرو کہ یہی صراظُ مستقیم ہے'),
(345, 3, 52, 'پھر جب عیسٰی علیھ السّلام نے قوم سے کفر کا احساس کیا تو فرمایا کہ کون ہے جو خدا کی راہ میں میرا مددگار ہو...حواریین نے کہا کہ ہم اللہ کے مددگار ہیں- اس پر ایمان لائے ہیں اور آپ گواہ رہیں کہ ہم مسلمان ہیں'),
(346, 3, 53, 'پروردگار ہم ان تمام باتوں پر ایمان لے آئے جو تو نے نازل کی ہیں اور تیرے رسول کا اتباع کیا لہذا ہمارا نام اپنے رسول کے گواہوں میں درج کرلے'),
(347, 3, 54, 'اور یہودیوں نے عیسٰی علیھ السّلام سے مکاری کی تو اللہ نے بھی جوابی تدبیر کی اور خدا بہترین تدبیر کرنے والا ہے'),
(348, 3, 55, 'اور جب خدا نے فرمایا کہ عیسٰی علیھ السّلام ہم تمہاری مدّاُ قیام دنیا پوری کرنے والے اور تمہیں اپنی طرف اُٹھالینے والے اور تمھیں کفار کی خباثت سے نجات دلانے والے اور تمھاری پیروی کرنے والوں کو انکار کرنے والوں پر قیامت تک کی برتری دینے والے ہیں- اس کے بعد تم سب کی بازگشت ہماری طرف ہوگی اور ہم تمہارے اختلافات کا صحیح فیصلہ کردیں گے'),
(349, 3, 56, 'پھر جن لوگوں نے کفر اختیار کیا ان پر دنیا اور آخرت میں شدید عذاب کریں گے اور ان کا کوئی مددگار نہ ہوگا'),
(350, 3, 57, 'اور جو لوگ ایمان لے آئے اور انہوں نے نیک اعمال کئے ان کو مکمل اجر دیں گے اور خدا ظلم کرنے والوں کو پسند نہیں کرتا'),
(351, 3, 58, 'یہ تمام نشانیاں اور پفِاز حکمت تذکرے ہیں جو ہم آپ سے بیان کررہے ہیں'),
(352, 3, 59, 'عیسٰی علیھ السّلام کی مثال اللہ کے نزدیک آدم علیھ السّلام جیسی ہے کہ انہیں مٹی سے پیدا کیا اور پھر کہا ہوجا اور وہ ہوگیا'),
(353, 3, 60, 'حق تمہارے پروردگار کی طرف سے آچکا ہے لہذا خبردار اب تمہارا شمار شک کرنے والوں میں نہ ہونا چاہئے'),
(354, 3, 61, 'پیغمبر علم کے آجانے کے بعد جو لوگ تم سے کٹ حجتی کریں ان سے کہہ دیجئے کہ آؤ ہم لوگ اپنے اپنے فرزند, اپنی اپنی عورتوں اور اپنے اپنے نفسوں کو بلائیں اور پھر خدا کی بارگاہ میں دعا کریں اور جھوٹوں پر خدا کی لعنت قرار دیں'),
(355, 3, 62, 'یہ سب حقیقی واقعات ہیں اور خدا کے علاوہ کوئی دوسرا خدا نہیں ہے اور وہی خدا صاحبِ عزّت و حکمت'),
(356, 3, 63, 'اب اس کے بعد بھی یہ لوگ انحراف کریں تو خدا مفسدین کو خوب جانتا ہے'),
(357, 3, 64, 'اے پیغمبر آپ کہہ دیں کہ اہلِ کتاب آؤ ایک منصفانہ کلمہ پر اتفاق کرلیں کہ خدا کے علاوہ کسی کی عبادت نہ کریں کسی کو اس کا شریک نہ بنائیں آپس میں ایک دوسرے کو خدائی کا درجہ نہ دیں اور اس کے بعد بھی یہ لوگ منہ موڑیں تو کہہ دیجئے کہ تم لوگ بھی گواہ رہنا کہ ہم لوگ حقیقی مسلمان اور اطاعت گزار ہیں'),
(358, 3, 65, 'اے اہلِ کتاب آخر ابراہیم علیھ السّلام کے بارے میں کیوں بحث کرتے ہو جب کہ توریت اور انجیل ان کے بعد نازل ہوئی ہے کیا تمہیں اتنی بھی عقل نہیں ہے'),
(359, 3, 66, 'اب تک تم نے ان باتوں میں بحث کی ہے جن کا کچھ علم تھا تو اب اس بات میں کیوں بحث کرتے ہو جس کا کچھ بھی علم نہیں ہے بیشک خدا جانتا ہے اور تم نہیں جانتے ہو'),
(360, 3, 67, 'ابراہیم علیھ السّلام نہ یہودی تھے اور نہ عیسائی وہ مسلمان حق پرست اور باطل سے کنارہ کش تھے اور وہ مشرکین میں سے ہرگز نہیں تھے'),
(361, 3, 68, 'یقینا ابراہیم علیھ السّلام سے قریب تر ان کے پیرو ہیں اور پھر یہ پیغمبر اور صاحبانِ ایمان ہیں اور اللہ صاحبان هایمان کا سرپرست ہے'),
(362, 3, 69, 'اہلِ کتاب کا ایک گروہ یہ چاہتا ہے کہ تم لوگوں کو گمراہ کردے حالانکہ یہ اپنے ہی کو گمراہ کررہے ہیں اور سمجھتے بھی نہیں ہیں'),
(363, 3, 70, 'اے اہلِ هکتاب تم آیات هالٰہیٰہ کا انکار کیوں کررہے ہو جب کہ تم خود ہی ان کے گواہ بھی ہو'),
(364, 3, 71, 'اے اہلِ کتاب کیوں حق کو باطل سے مشتبہ کرتے ہو اور جانتے ہوئے حق کی پردہ پوشی کرتے ہو'),
(365, 3, 72, 'اور اہلِ کتاب کی ایک جماعت نے اپنے ساتھیوں سے کہا کہ جو کچھ ایمان والوں پر نازل ہوا ہے اس پر صبح کو ایمان لے آؤ اور شام کو انکار کر دو شاید اس طرح وہ لوگ بھی پلٹ جائیں'),
(366, 3, 73, 'اور خبردار ان لوگوں کے علاوہ کسی پر اعتبار نہ کرنا جو تمہارے دین کا اتباع کرتے ہیں.... پیغمبر آپ کہہ دیں کہ ہدایت صرف خدا کی ہدایت ہے اور ہرگز یہ نہ ماننا کہ خدا ویسی ہی فضیلت اور نبوت کسی اور کو بھی دے سکتا ہے جیسی تم کو دی ہے یا کوئی تم سے پیش پروردگار بحث بھی کرسکتا ہے پیغمبر آپ کہہ دیجئے کہ فضل و کرم خدا کے ہاتھ میں ہے وہ جسے چاہتا عطا کرتا ہے اور وہ صاحبِ وسعت بھی ہے اور صاحبِ علم بھی'),
(367, 3, 74, 'وہ اپنی رحمت سے جسے چاہتا ہے مخصوص کرتا ہے اور وہ بڑے فضل والا ہے'),
(368, 3, 75, 'اور اہلِ کتاب میں سے کچھ ایسے بھی ہیں جن کے پاس ڈھیر بھر مال بھی امانت رکھ دیا جائے تو واپس کردیں گے اور کچھ ایسے بھی ہیں کہ ایک دینار بھی امانت رکھ دی جائے تو اس وقت تک واپس نہ کریں گے جب تک ان کے سر پر کھڑے نہ رہو- یہ اس لئے کہ ان کا کہنا یہ ہے کہ عربوں کی طرف سے ہمارے اوپر کوئی ذمہ داری نہیں ہے یہ خدا کے خلاف جھوٹ بولتے ہیں اور جانتے بھی ہیں کہ جھوٹے ہیں'),
(369, 3, 76, 'بیشک جو اپنے عہد کو پورا کرے گا اور خورِ خدا پیدا کرے گا تو خدا متقّین کو دوست رکھتا ہے'),
(370, 3, 77, 'جو لوگ اللہ سے کئے گئے عہد اور قسم کو تھوڑی قیمت پر بیچ ڈالتے ہیں ان کے لئے آخرت میں کوئی حصّہ نہیں ہے اور نہ خدا ان سے بات کرے گا اور نہ روزِ قیامت ان کی طرف نظر کرے گا اور نہ انہیں گناہوں کی آلودگی سے پاک بنائے گا اور ان کے لئے دردناک عذاب ہے'),
(371, 3, 78, 'ان ہی یہودیوں میں سے بعض وہ ہیں جو کتاب پڑھنے میں زبان کو توڑ موڑ دیتے ہیں تاکہ تم لوگ اس تحریف کو بھی اصل کتاب سمجھنے لگو حالانکہ وہ اصل کتاب نہیں ہے اور یہ لوگ کہتے ہیں کہ یہ سب اللہ کی طرف سے ہے حالانکہ اللہ کی طرف سے ہرگز نہیں ہے یہ خدا کے خلاف جھوٹ بولتے ہیں حالانکہ سب جانتے ہیں'),
(372, 3, 79, 'کسی بشر کے لئے یہ مناسب نہیں ہے کہ خدا اسے کتاب و حکمت اور نبوت عطا کردے اور پھر وہ لوگوں سے یہ کہنے لگے کہ خدا کو چھوڑ کر ہمارے بندے بن جاؤ بلکہ اس کا قول یہی ہوتا ہے کہ اللہ والے بنو کہ تم کتاب کی تعلیم بھی دیتے ہو اور اسے پڑھتے بھی رہتے ہو'),
(373, 3, 80, 'وہ یہ حکم بھی نہیں دے سکتا کہ ملائکہ یا انبیائ کو اپنا پروردگار بنالو کیا وہ تمہیں کفر کا حکم دے سکتا ہے جب کہ تم لوگ مسلمان ہو'),
(374, 3, 81, 'اور اس وقت کو یاد کرو جب خدا نے تمام انبیائ سے عہد لیا کہ ہم تم کو جو کتاب و حکمت دے رہے ہیں اس کے بعد جب وہ رسول آجائے جو تمہاری کتابوں کی تصدیق کرنے والا ہے تو تم سب اس پر ایمان لے آنا اور اس کی مدد کرنا .اور پھر پوچھا کیا تم نے ان باتوں کا اقرار کرلیا اور ہمارے عہد کو قبول کرلیا تو سب نے کہا بیشک ہم نے اقرار کرلیا- ارشاد ہوا کہ اب تم سب گواہ بھی رہنا اور میں بھی تمہارے ساتھ گواہوں میں ہوں'),
(375, 3, 82, 'اس کے بعد جو انحراف کرے گا وہ فاسقین کی منزل میں ہوگا'),
(376, 3, 83, 'کیا یہ لوگ دینِ خدا کے علاوہ کچھ اور تلاش کررہے ہیں جب کہ زمین و آسمان کی ساری مخلوقات بہ رضا و رغبت یا بہ جبر وکراہت اسی کی بارگاہ میں سر تسلیم خم کئے ہوئے ہے اور سب کو اسی کی بارگاہ میں واپس جانا ہے'),
(377, 3, 84, 'پیغمبر ان سے کہہ دیجئے کہ ہمارا ایمان اللہ پر ہے اور جو ہم پر نازل ہوا ہے اور جو ابراہیم علیھ السّلام,اسماعیل علیھ السّلام, اسحاق علیھ السّلامًیعقوب علیھ السّلام اور اسباط علیھ السّلامپر نازل ہوا ہے اور جو موسٰی علیھ السّلام, عیسٰی علیھ السّلام اور انبیائ کو خدا کی طرف سے دیا گیا ہے ان سب پر ہے. ہم ان کے درمیان تفریق نہیں کرتے ہیں اور ہم خدا کے اطاعت گزار بندے ہیں'),
(378, 3, 85, 'اور جو اسلام کے علاوہ کوئی بھی دین تلاش کرے گا تو وہ دین اس سے قبول نہ کیا جائے گا اور وہ قیامت کے دن خسارہ والوں میں ہوگا'),
(379, 3, 86, 'خدا اس قوم کو کس طرح ہدایت دے گا جو ایمان کے بعد کافر ہوگئی اور وہ خود گواہ ہے کہ رسول برحق ہے اور ان کے پاس کھلی نشانیاں بھی آچکی ہیں بیشک خدا ظالم قوم کو ہدایت نہیں دیتا'),
(380, 3, 87, 'ان لوگوں کی جزا یہ ہے کہ ان پر خدا ً ملائکہ اور انسان سب کی لعنت ہے'),
(381, 3, 88, 'یہ ہمیشہ اسی لعنت میں گرفتار رہیں گے .ان کے عذاب میں تخفیف نہ ہوگی اور نہ انہیں مہلت دی جائے گی'),
(382, 3, 89, 'علاوہ ان لوگوں کے جنہوں نے اس کے بعد توبہ کرلی اور اصلاح کرلی کہ خدا غفور اور رحیم ہے'),
(383, 3, 90, 'جن لوگوں نے ایمان کے بعد کفر اختیار کرلیا اور پھر کفر میں بڑھتے ہی چلے گئے ان کی توبہ ہرگز قبول نہ ہوگی اور وہ حقیقی طور پر گمراہ ہیں'),
(384, 3, 91, 'جن لوگوں نے کفر اختیار کیا اور اسی کفر کی حالت میں مرگئے ان سے ساری زمین بھر کر سونا بھی بطور فدیہ قبول نہیں کیا جائے گا اور ان کے لئے دردناک عذاب ہے اور ان کا کوئی مددگار نہیں ہے'),
(385, 3, 92, 'تم نیکی کی منزل تک نہیں پہنچ سکتے جب تک اپنی محبوب چیزوں میں سے راسِ خدا میں انفاق نہ کرو اور جو کچھ بھی انفاق کرو گے خدا اس سے بالکل باخبر ہے'),
(386, 3, 93, 'بنی اسرائیل کے لئے تمام کھانے حلال تھے سوائے اس کے جسے توریت کے نازل ہونے سے پہلے اسرائیل نے اپنے اوپر ممنوع قرار دے لیا تھا- اب تم توریت کو پڑھو اگر تم اپنے دعوٰی میں سچےّ ہو'),
(387, 3, 94, 'اس کے بعد جو بھی خدا پر بہتان رکھے گا اس کا شمار ظالمین میں ہوگا'),
(388, 3, 95, 'پیغمبر آپ کہہ دیجئے کہ خدا سچاّ ہے .تم سب ملّت ابراہیم علیھ السّلام کا اتباع کرو وہ باطل سے کنارہ کش تھے اور مشرکین میں سے نہیں تھے'),
(389, 3, 96, 'بیشک سب سے پہلا مکان جو لوگوں کے لئے بنایا گیا ہے وہ مکّہ میں ہے مبارک ہے اور عالمین کے لئے مجسم ہدایت ہے'),
(390, 3, 97, 'اس میں کھلی ہوئی نشانیاں مقام ابراہیم علیھ السّلامہے اور جو اس میں داخل ہوجائے گا وہ محفوظ ہوجائے گا اور اللہ کے لئے لوگوں پر اس گھر کا حج کرنا واجب ہے اگر اس راہ کی استطاعت رکھتے ہوں اور جو کافر ہوجائے تو خدا تمام عالمین سے بے نیاز ہے'),
(391, 3, 98, 'اے اہل ههکتاب کیوں آیات الٰہی کا انکار کرتے ہو جب کہ خدا تمہارے اعمال کا گواہ ہے'),
(392, 3, 99, 'کہئے اے اہلِ کتاب کیوں صاحبانِ ایمان کو راسِ خدا سے روکتے ہو اور اس میں کجی تلاش کرتے ہو جبکہ تم خود اس کی صحت کے گواہ ہو اور اللہ تمہارے اعمال سے غافل نہیں ہے'),
(393, 3, 100, 'اے ایمان والواگر تم نے اہلِ کتاب کے ِس گروہ کی اطاعت کرلی تو یہ تم کو ایمان کے بعد کفر کی طرف پلٹا دیں گے'),
(394, 3, 101, 'اور تم لوگ کس طرح کافر ہوجاؤ گے جب کہ تمہارے سامنے آیات هالٰہیٰہ کی تلاوت ہورہی ہے اور تمہارے درمیان رسول موجود ہے اور جو خدا سے وابستہ ہوجائے سمجھو کہ اسے سیدھے راستہ کی ہدایت کردی گئی'),
(395, 3, 102, 'ایمان والو اللہ سے اس طرح ڈرو جو ڈرنے کا حق ہے اور خبردار اس وقت تک نہ مرنا جب تک مسلمان نہ ہوجاؤ'),
(396, 3, 103, 'اور اللہ کی ر سّی کو مضبوطی سے پکڑے رہو اور آپس میں تفرقہ نہ پیدا کرو اور اللہ کی نعمت کو یاد کرو کہ تم لوگ آپس میں دشمن تھے اس نے تمہارے دلوں میں اُلفت پیدا کردی تو تم اس کی نعمت سے بھائی بھائی بن گئے اور تم جہّنم کے کنارے پر تھے تو اس نے تمہیں نکال لیا اور اللہ اسی طرح اپنی آیتیں بیان کرتا ہے کہ شاید تم ہدایت یافتہ بن جاؤ'),
(397, 3, 104, 'اور تم میں سے ایک گروہ کو ایسا ہونا چاہئے جو خیر کی دعوت دے, نیکیوں کا حکم دے برائیوں سے منع کرے اور یہی لوگ نجات یافتہ ہیں'),
(398, 3, 105, 'اور خبردار ان لوگوں کی طرح نہ ہوجاؤ جنہوں نے تفرقہ پیدا کیا اور واضح نشانیوں کے آجانے کے بعد بھی اختلاف کیا کہ ان کے لئے عذاب هعظیم ہے'),
(399, 3, 106, 'قیامت کے دن جب بعض چہرے سفید ہوں گے اور بعض سیاہ. جن کے چہرے سیاہ ہوں گے ان سے کہا جائے گا کہ تم ایمان کے بعد کیوں کافر ہوگئے تھے اب اپنے کفر کی بنائ پر عذاب کا مزہ چکھو'),
(400, 3, 107, 'اور جن کے چہرے سفید اور روشن ہوں گے وہ رحمت الٰہی میں ہمیشہ ہمیشہ رہیں گے'),
(401, 3, 108, 'یہ آیااُ الٰہی ہیں جن کی ہم حق کے ساتھ تلاوت کررہے ہیں اور اللہ عالمین کے بارے میں ہرگز ظلم نہیں چاہتا'),
(402, 3, 109, 'زمین و آسمان میں جو کچھ ہے سب اللہ کے لئے ہے اور اسی کی طرف سارے امور کی باز گشت ہے'),
(403, 3, 110, 'تم بہترین امت ہو جسے لوگوں کے لئے منظرعام پر لایا گیا ہے تم لوگوں کو نیکیوں کا حکم دیتے ہو اور برائیوں سے روکتے ہو اور اللہ پر ایمان رکھتے ہو اور اگر اہل کتاب بھی ایمان لے آتے تو ان کے حق میں بہتر ہوتا لیکن ان میں صرف چند مومنین ہیں اور اکثریت فاسق ہے'),
(404, 3, 111, 'یہ تم کو اذیت کے علاوہ کوئی نقصان نہیں پہنچاسکتے اور اگر تم سے جنگ بھی کریں گے تو میدان چھوڑ کر بھاگ جائیں گے اور پھر ان کی مدد بھی نہ کی جائے گی'),
(405, 3, 112, 'ان پر ذلّت کے نشان لگادئیے گئے ہیں یہ جہاں بھی رہیں مگر یہ کہ خدائی عہد یا لوگوں کے معاہدہ کی پناہ مل جائے .یہ غضبِ الٰہی میں رہیں گے اور ان پر مسکنت کی مار رہے گی. یہ اس لئے ہے کہ یہ آیات الٰہی کا انکار کرتے تھے اور ناحق انبیائ کو قتل کرتے تھے. یہ اس لئے کہ یہ نافرمان تھے اور زیادتیاں کیا کرتے تھے'),
(406, 3, 113, 'یہ لوگ بھی سب ایک جیسے نہیں ہیں .اہلِ کتاب ہی میں وہ جماعت بھی ہے جو دین پر قائم ہے راتوں کو آیات الٰہی کی تلاوت کرتی ہے اور سجدہ کرتی ہے'),
(407, 3, 114, 'یہ اللہ اور آخرت پر ایمان رکھتے ہیں. نیکیوں کا حکم دیتے ہیں برائیوں سے روکتے ہیں اور نیکیوں کی طرف سبقت کرتے ہیں اور یہی لوگ صالحین اور نیک کرداروں میں ہیں'),
(408, 3, 115, 'یہ جو بھی خیر کریں گے اس کا انکار نہ کیا جائے گا اور اللہ متّقین کے اعمال سے خوب باخبر ہے'),
(409, 3, 116, 'جن لوگوں نے کفر اختیار کیا ان کے مال و اولاد کچھ کام نہ آئیں گے اور یہ حقیقی جہّنمی ہیں اور وہیں ہمیشہ رہنے والے ہیں'),
(410, 3, 117, 'یہ لوگ زندگانی دنیا میں جو کچھ خرچ کرتے ہیں اس کی مثال اس ہوا کی ہے جس میں بہت پالا ہو اور وہ اس قوم کے کھیتوں پر گر پڑے جنہوں نے اپنے اوپر ظلم کیا ہے اور اسے تباہ کردے اور یہ ظلم ان پر خدا نے نہیں کیا ہے بلکہ یہ خود اپنے اوپر ظلم کرتے ہیں'),
(411, 3, 118, 'اے ایمان والو خبردار غیروں کو اپنا راز دار نہ بنانا یہ تمہیں نقصان پہنچانے میں کوئی کوتاہی نہ کریں گے- یہ صرف تمہاری مشقت و مصیبت کے خواہش مند ہیں- ان کی عداوت زبان سے بھی ظاہر ہے اور جو دل میں چھپا رکھا ہے وہ تو بہت زیادہ ہے. ہم نے تمہارے لئے نشانیوں کو واضح کرکے بیان کردیا ہے اگر تم صاحبانِ عقل ہو'),
(412, 3, 119, 'خبردار... تم ان سے دوستی کرتے ہو اور یہ تم سے دوستی نہیں کرتے ہیں- تم تمام کتابوں پر ایمان رکھتے ہو اور یہ جب تم سے ملتے ہیں تو کہتے ہیں کہ ہم ایمان لے آئے اور جب اکیلے ہوتے ہیں تو غّصہ سے انگلیاںکاٹتے ہیں- پیغمبر آپ کہہ دیجئے کہ تم اسی غّصہ میں مرجاؤ. خدا سب کے دل کے حالات سے خوب باخبر ہے'),
(413, 3, 120, 'تمہیں ذرا بھی نیکی ملے تو انہیں برا لگے گا اور تمہیں تکلیف پہنچ جائے گی تو خوش ہوں گے اور اگر تم صبر کرو اور تقوٰی اختیار کرو تو ان کے مکر سے کوئی نقصان نہ ہوگا خدا ان کے اعمال کا احاطہ کئے ہوئے ہے'),
(414, 3, 121, 'اس وقت کو یاد کرو جب تم صبح سویرے گھر سے نکل پڑے اور مومنین کو جنگ کی پوزیشن بتارہے تھے اور خدا سب کچھ سننے والا اور جاننے والا ہے'),
(415, 3, 122, 'اس وقت جب تم میں سے دو گروہوں نےصَستی کا مظاہرہ کرنا چاہا لیکن بچ گئے کہ اللہ ان کا سرپرست تھا اور اسی پر ایمان والوں کو بھروسہ کرنا چاہئے'),
(416, 3, 123, 'اور اللہ نے بدر میں تمہاری مدد کی ہے جب کہ تم کمزور تھے لہذا اللہ سے ڈرو شاید تم شکر گزار بن جاؤ'),
(417, 3, 124, 'اس وقت جب آپ مومنین سے کہہ رہے تھے کہ کیا یہ تمہارے لئے کافی نہیں ہے کہ خدا تین ہزار فرشتوں کو نازل کرکے تمہاری مدد کرے'),
(418, 3, 125, 'یقینا اگر تم صبر کرو گے اور تقوٰی اختیار کرو گے اور دشمن فی الفور تم تک آجائیں گے تو خدا پانچ ہزار فرشتوں سے تمہاری مدد کرے گا جن پر بہادری کے نشان لگے ہوں گے'),
(419, 3, 126, 'اور اس امداد کو خدانے صرف تمہارے لئے بشارت اور اطمینانِ قلب کا سامان قرار دیا ہے ورنہ مدد تو ہمیشہ صرف خدائے عزیز و حکیم ہی کی طرف سے ہوتی ہے'),
(420, 3, 127, 'تاکہ کفار کے ایک حصّہ کو کاٹ دے یا ان کو ذلیل کردے کہ وہ رسوا ہوکر پلٹ جائیں'),
(421, 3, 128, 'ان معاملات میں آپ کا کوئی حصہّ نہیں ہے .چاہے خدا توبہ قبول کرے یا عذاب کرے. یہ سب بہرحال ظالم ہیں'),
(422, 3, 129, 'اور اللرُ ہی کے لئے زمین و آسمان کی کل کائنات ہے وہ جس کو چاہتا ہے بخش دیتا ہے اور جس پر چاہتا ہے عذاب کرتا ہے وہ غفور بھی ہے اور رحیم بھی ہے'),
(423, 3, 130, 'اے ایمان والو یہ دوگنا چوگنا سود نہ کھاؤ اور ا للہ سے ڈرو کہ شاید نجات پاجاؤ'),
(424, 3, 131, 'اور اس آگ سے بچو جو کافروں کے واسطے مہیا کی گئی ہے'),
(425, 3, 132, 'اور اللہ و رسول کی اطاعت کرو کہ شاید رحم کے قابل ہوجاؤ'),
(426, 3, 133, 'اور اپنے پروردگار کی مغفرت اور اس جنّت کی طرف سبقت کرو جس کی وسعت زمین و آسمان کے برابر ہے اور اسے ان صاحبان هتقوٰی کے لئے مہیاّ کیا گیا ہے'),
(427, 3, 134, 'جو راحت اور سختی ہر حال میں انفاق کرتے ہیں اور غصّہ کو پی جاتے ہیں اور لوگوں کو معاف کرنے والے ہیں اور خدا احسان کرنے والوں کو دوست رکھتا ہے'),
(428, 3, 135, 'وہ لوگ وہ ہیں کہ جب کوئی نمایاں گناہ کرتے ہیں یا اپنے نفس پر ظلم کرتے ہیں تو خدا کو یاد کرکے اپنے گناہوں پر استغفار کرتے ہیں اور خدا کے علاوہ کون گناہوں کا معاف کرنے والا ہے اور وہ اپنے کئے پر جان بوجھ کر اصرار نہیں کرتے'),
(429, 3, 136, 'یہی وہ ہیں جن کی جزا مغفرت ہے اور وہ جنّت ہے جس کے نیچے نہریں جاری ہیں .وہ اسی میں ہمیشہ رہنے والے ہیں اور عمل کرنے کی یہ جزا بہترین جزا ہے'),
(430, 3, 137, 'تم سے پہلے مثالیں گزر چکی ہیں اب تم زمین میں سیر کرو اور دیکھو کہ جھٹلانے والوں کا کیا انجام ہوتا ہے'),
(431, 3, 138, 'یہ عام انسانوں کے لئے ایک بیان حقائق ہے اور صاحبانِ تقویٰ کے لئے ہدایت اور نصیحت ہے'),
(432, 3, 139, 'خبردار سستی نہ کرنا. مصائب پر محزون نہ ہونا اگر تم صاحب هایمان ہو تو سر بلندی تمہارے ہی لئے ہے'),
(433, 3, 140, 'اگر تمہیں کوئی تکلیف چھولیتی ہے تو قوم کو بھی اس سے پہلے ایسی ہی تکلیف ہوچکی ہے اور ہم تو زمانے کو لوگوں کے درمیان الٹتے پلٹتے رہتے ہیں تاکہ خدا صاحبانِ ایمان کو دیکھ لے اور تم میں سے بعض کو شہدائ قرار دے اور وہ ظالمین کو دوست نہیں رکھتا ہے'),
(434, 3, 141, 'اور خدا صاحبان هایمان کو چھانٹ کر الگ کرنا چاہتا تھا اور کافروں کو مٹا دینا چاہتا تھا'),
(435, 3, 142, 'کیا تمہارا یہ خیال ہے کہ تم جنّت میں یوں ہی داخل ہوجاؤ گے جب کہ خدا نے تم میں سے جہاد کرنے والوں اور صبر کرنے والوں کو بھی نہیں جانا ہے'),
(436, 3, 143, 'تم موت کی ملاقات سے پہلے اس کی بہت تمنّا کیا کرتے تھے اور جیسے ہی اسے دیکھا دیکھتے ہی رہ گئے'),
(437, 3, 144, 'اور محمد تو صرف ایک رسول ہیں جن سے پہلے بہت سے رسول گزر چکے ہیں کیااگر وہ مر جائیں یا قتل ہو جائیں تو تم اُلٹے پیروں پلٹ جاؤ گے تو جو بھی ایسا کرے گا وہ خدا کا کوئی نقصان نہیں کرے گا اور خدا تو عنقریب شکر گزاروں کو ان کی جزا دے گا'),
(438, 3, 145, 'کوئی نفس بھی اسُنِ پروردگار کے بغیر نہیں مرسکتا ہے سب کی ایک اجل اور مدّت معین ہے اور جو دنیا ہی میں بدلہ چاہے گا ہم اسے وہ دیں گے اور جو آخرت کا ثواب چاہے گا ہم اسے اس میں سے عطا کردیں گے اور ہم عنقریب شکر گزاروں کو جزا دیں گے'),
(439, 3, 146, 'اور بہت سے ایسے نبی گزر چکے ہیں جن کے ساتھ بہت سے اللہ والوں نے اس شان سے جہاد کیا ہے کہ راسِ خدا میں پڑنے والی مصیبتوں سے نہ کمزور ہوئے اور نہ بزدلی کا اظہار کیا اور نہ دشمن کے سامنے ذلّت کا مظاہرہ کیا اور اللہ صبر کرنے والوں ہی کو دوست رکھتا ہے'),
(440, 3, 147, 'ان کا قول صرف یہی تھا کہ خدایا ہمارے گناہوں کو بخش دے- ہمارے امور میں زیادتیوں کو معاف فرما- ہمارے قدموں کو ثبات عطا فرما اور کافروں کے مقابلہ میں ہماری مدد فرما'),
(441, 3, 148, 'تو خدا نے انہیں معاوضہ بھی دیا اور آخرت کا بہترین ثواب بھی دیا اور اللہ نیک عمل کرنے والوں کو دوست رکھتا ہے'),
(442, 3, 149, 'ایمان والو اگر تم کفر اختیار کرنے والوں کی اطاعت کرلو گے تو یہ تمہیں اُلٹے پاؤںپلٹا لے جائیں گے اور پھر تم ہی اُلٹے خسارہ والوں میں ہوجاؤ گے'),
(443, 3, 150, 'بلکہ خدا تمہارا سرپرست ہے اور و ہ بہترین مدد کرنے والا ہے'),
(444, 3, 151, 'ہم عنقریب کافروں کے دلوں میں تمہارا رعب ڈال دیں گے کہ انہوں نے اس کو خدا کا شریک بنایا ہے جس کے بارے میں خدا نے کوئی دلیل نازل نہیں کی ہے اور ان کا انجام جہّنم ہوگا اور وہ ظالمین کا بدترین ٹھکانہ ہے'),
(445, 3, 152, 'خدا نے اپنا وعدہ اس وقت پورا کردیا جب تم اس کے حکم سے کفاّر کو قتل کررہے تھے یہاں تک کہ تم نے کمزوری کا مظاہرہ کیا اور آپس میں جھگڑا کرنے لگے اور اس وقت خدا کی نافرمانی کی جب اس نے تمہاری محبوب شے کو دکھلا دیا تھا تم میں کچھ دنیا کے طلب گار تھے اور کچھ آخرت کے- اس کے بعد تم کو ان کفاّر کی طرف سے پھیر دیا تاکہ تمہارا امتحان لیا جائے اور پھر اس نے تمہیں معاف بھی کردیا کہ وہ صاحبانِ ایمان پر بڑا فضل و کرم کرنے والا ہے'),
(446, 3, 153, 'اس وقت کو یاد کرو جب تم بلندی پر جارہے تھے اور مڑ کر کسی کو دیکھتے بھی نہ تھے جب کہ رسول پیچھے کھڑے تمہیں آواز دے رہے تھے جس کے نتیجہ میں خدا نے تمہیں غم کے بدلے غم دیا تاکہ نہ اس پر رنجیدہ ہو جو چیز ہاتھ سے نکل گئی اور نہ اس مصیبت پر جو نازل ہوگئی ہے اور اللہ تمہارے اعمال سے خوب باخبر ہے'),
(447, 3, 154, 'اس کے بعد خدا نے ایک گروہ پرپُر سکون نیند طاری کردی اور ایک کو نیند بھی نہ آئی کہ اسے صرف اپنی جان کی فکر تھی اور ان کے ذہن میں خلاف حق جاہلیت جیسے خیالات تھے اور وہ یہ کہہ رہے تھے کہ جنگ کے معاملات میں ہمارا کیا اختیار ہے پیغمبر آپ کہہ دیجئے کہ اختیار صرف خدا کا ہے- یہ اپنے دل میں وہ باتیں چھپائے ہوئے ہیں جن کا آپ سے اظہار نہیں کرتے اور کہتے ہیں کہ اگر اختیار ہمارے ہاتھ میں ہوتا تو ہم یہاں نہ مارے جاتے تو آپ کہہ دیجئے کہ اگر تم گھروں میں بھی رہ جاتے تو جن کے لئے شہادت لکھ دی گئی ہے وہ اپنے مقتل تک بہرحال جاتے اور خدا تمہارے دلوں کے حال کو آزمانا چاہتا ہے اور تمہارے ضمیر کی حقیقت کو واضح کرنا چاہتا ہے اور وہ خود ہر دل کا حال جانتا ہے'),
(448, 3, 155, 'جن لوگوں نے دونوں لشکروں کے ٹکراؤ کے دن پیٹھ پھیرلی یہ وہی ہیں جنہیں شیطان نے ان کے کئے دھرے کی بنائ پر بہکا دیاہے اور خدا نے ان کو معاف کردیا کہ وہ غفور اور حلیم ہے'),
(449, 3, 156, 'اے ایمان والوں خبردار کافروں جیسے نہ ہوجاؤ جنہوں نے اپنے ساتھیوں کے سفر یا جنگ میں مرنے پر یہ کہنا شروع کردیا کہ وہ ہمارے پاس ہوتے تو نہ مرتے اور نہ قتل کئے جاتے.. خدا تمہاری علیحدگی ہی کو ان کے لئے باعجُ مسرّت قرار دینا چاہتا ہے کہ موت و حیات اسی کے اختیار میں ہے اور وہ تمہارے اعمال سے خوب باخبر ہے'),
(450, 3, 157, 'اگر تم راسِ خدا میں مرگئے یا قتل ہوگئے تو خدا کی طرف سے مغفرت اور رحمت ان چیزوں سے کہیں زیادہ بہتر ہے جنہیں یہ جمع کررہے ہیں'),
(451, 3, 158, 'اور تم اپنی موت سے مرو یا قتل ہوجاؤ سب اللہ ہی کی بارگاہ میں حاضر کئے جاؤ گے'),
(452, 3, 159, 'پیغمبر یہ اللہ کی مہربانی ہے کہ تم ان لوگوں کے لئے نرم ہو ورنہ اگر تم بدمزاج اور سخت دل ہوتے تو یہ تمہارے پاس سے بھاگ کھڑے ہوتے لہذا اب انہیں معاف کردو- ان کے لئے استغفار کرو اور ان سے امر جنگ میں مشورہ کرو اور جب ارادہ کرلو تو اللہ پر بھروسہ کرو کہ وہ بھروسہ کرنے والوں کو دوست رکھتا ہے'),
(453, 3, 160, 'اللہ تمہاری مدد کرے گا تو کوئی تم پر غالب نہیں آسکتا اور وہ تمہیں چھوڑ دے گا تو اس کے بعد کون مدد کرے گا اور صاحبانِ ایمان تو اللہ ہی پر بھروسہ کرتے ہیں'),
(454, 3, 161, 'کسی نبی کے لئے یہ ممکن نہیں ہے کہ وہ خیانت کرے اور جو خیانت کرے گا وہ روزِ قیامت خیانت کے مال سمیت حاضر ہوگا اس کے بعد ہر نفس کو اس کے کئے کا پورا پورا بدلہ دیا جائے گا اور کسی پر کوئی ظلم نہیں کیاجائے گا'),
(455, 3, 162, 'کیا رضائے الٰہی کا اتباع کرنے والا اس کے جیسا ہوگا جو غضب الٰہی میں گرفتار ہو کہ اس کا انجام جہّنم ہے اور وہ بدترین منزل ہے'),
(456, 3, 163, 'سب کے پیش پروردگار درجات ہیں اور خدا سب کے اعمال سے باخبر ہے'),
(457, 3, 164, 'یقنیا خدا نے صاحبانِ ایمان پر احسان کیا ہے کہ ان کے درمیان ان ہی میں سے ایک رسول بھیجا ہے جو ان پر آیات الٰہیٰہ کی تلاوت کرتا ہے انہیں پاکیزہ بناتا ہے اور کتاب و حکمت کی تعلیم دیتا ہے اگرچہ یہ لوگ پہلے سے بڑی کھلی گمراہی میں مبتلا تھے'),
(458, 3, 165, 'کیا جب تم پر وہ مصیبت پڑی جس کی دوگنی تم کفّارپر ڈال چکے تھے تو تم نے یہ کہنا شروع کردیا کہ یہ کیسے ہوگیا تو پیغمبر آپ کہہ دیجئے کہ یہ سب خود تمہاری طرف سے ہے اور اللہ ہر شے پر قادر ہے'),
(459, 3, 166, 'اورجو کچھ بھی اسلام و کفر کے لشکر کے مقابلہ کے دن تم لوگوں کو تکلیف پہنچی ہے وہ خدا کے علم میں ہے اور اسی لئے کہ وہ مومنین کو جاننا چاہتا تھا'),
(460, 3, 167, 'اور منافقین کو بھی دیکھنا چاہتا تھا .ان منافقین سے کہا گیا کہ آؤ راسِ خدا میں جہاد کرو یا اپنے نفس سے دفاع کرو تو انہوں نے کہا کہ ہم کو معلوم ہوتا کہ واقعی جنگ ہوگی تو تمہارے ساتھ ضرور آتے.. یہ ایمان کی نسبت کفر سے زیادہ قریب تر ہیں اور زبان سے وہ کہتے ہیں جو دل میں نہیں ہوتا اور اللہ ان کے پوشیدہ امور سے باخبر ہے'),
(461, 3, 168, 'یہی وہ لوگ ہیں جنہوں نے اپنے مقتول بھائیوں کے بارے میں یہ کہنا شروع کردیا کہ وہ ہماری اطاعت کرتے تو ہرگز قتل نہ ہوتے تو پیغمبر ان سے کہہ دیجئے کہ اگر اپنے دعوٰی میں سچےّ ہو تو اب اپنی ہی موت کو ٹال دو'),
(462, 3, 169, 'اور خبردار راسِ خدا میں قتل ہونے والوں کو مردہ خیال نہ کرنا وہ زندہ ہیں اور اپنے پروردگار کے یہاں رزق پارہے ہیں'),
(463, 3, 170, 'خدا کی طرف سے ملنے والے فضل و کرم سے خوش ہیں اور جو ابھی تک ان سے ملحق نہیں ہوسکے ہیں ان کے بارے میں یہ خوش خبری رکھتے ہیں کہ ان کے واسطے بھی نہ کوئی خوف ہے اور نہ حزن'),
(464, 3, 171, 'وہ اپنے پروردگار کی نعمت, اس کے فضل اور اس کے وعدہ سے خوش ہیں کہ وہ صاحبانِ ایمان کے اجر کو ضائع نہیں کرتا'),
(465, 3, 172, 'یہ صاحبانِ ایمان ہیں جنہوں نے زخمی ہونے کے بعد بھی خدا اور رسول کی دعوت پر لبیک کہی- ان کے نیک کردار اور متقی افراد کے لئے نہایت درجہ اج» عظیم ہے'),
(466, 3, 173, 'یہ وہ ایمان والے ہیں کہ جب ان سے بعض لوگوں نے کہاکہ لوگو ں نے تمہارے لئے عظےم لشکر جمع کرلیا ہے لہذا ان سے ڈرو تو ان کے ایمان میں اور اضافہ ہوگیا اور انہوں نے کہا کہ ہمارے لئے خدا کافی ہے اور وہی ہمارا ذمہ دار ہے'),
(467, 3, 174, 'پس یہ مجاہدین خدا کے فضل و کرم سے یوں پلٹ آئے کہ انہیں کوئی تکلیف نہیں پہنچی اور انہوں نے رضائے الٰہی کا اتباع کیااور اللہ صاحبِ فضلِ عظیم ہے'),
(468, 3, 175, 'یہ شیطان صرف اپنے چاہنے والوں کو ڈراتا ہے لہذا تم ان سے نہ ڈرو اور اگر مومن ہو تو مجھ سے ڈرو'),
(469, 3, 176, 'اور آپ کفر میں تیزی کرنے والوں کی طرف سے رنجیدہ نہ ہوں یہ خدا کا کوئی نقصان نہیں کرسکتے... خدا چاہتا ہے کہ ان کا آخرت میں کوئی حصّہ نہ رہ جائے اور صرف عذابِ عظیم رہ جائے'),
(470, 3, 177, 'جن لوگوں نے ایمان کے بدلے کفر خرید لیا ہے وہ خدا کو کوئی نقصان نہیں پہنچاسکتے اور ان کے لئے دردناک عذاب ہے'),
(471, 3, 178, 'اور خبردار یہ کفاّر یہ نہ سمجھیں کہ ہم جس قدر راحت و آرام دے رہے ہیں وہ ان کے حق میں کوئی بھلائی ہے- ہم تو صرف اس لئے دے رہے ہیں کہ جتنا گناہ کرسکیں کرلیں ورنہ ان کے لئے رسوا کن عذاب ہے'),
(472, 3, 179, 'خدا صاحبان هایمان کو ان ہی حالات میں نہیں چھوڑ سکتا جب تک خبیث اور طیّب کو الگ الگ نہ کردے اور وہ تم کو غیب پر مطلع بھی نہیں کرنا چاہتا ہاں اپنے نمائندوں میں سے کچھ لوگوں کو اس کام کے لئے منتخب کرلیتا ہے لہذا تم خدا اور رسول پر ایمان رکھو اور اگر ایمان و تقوٰی اختیار کرو گے تو تمہارے لئے اجر عظیم ہے'),
(473, 3, 180, 'اور خبردار جو لوگ خدا کے دیئے ہوئے میں بخل کرتے ہیں ان کے بارے میں یہ نہ سوچنا کہ اس بخل میں کچھ بھلائی ہے- یہ بہت اِرا ہے اور عنقریب جس مال میں بخل کیا ہے وہ روزِ قیامت ان کی گردن میں طوق بنا دیا جائے گا اور اللہ ہی کے لئے زمین و آسمان کی ملکیت ہے اور وہ تمہارے اعمال سے خوب باخبر ہے'),
(474, 3, 181, 'اللہ نے ان کی بات کوبھی سن لیا ہے جن کا کہنا ہے کہ خدا فقیر ہے اور ہم مالدار ہیں- ہم ان کی اس مہمل بات کو اور ان کے انبیائ کے ناحق قتل کرنے کو لکھ رہے ہیں اور انجام کار ان سے کہیں گے کہ اب جہّنم کا مزا چکھو'),
(475, 3, 182, 'اس لئے کہ تم نے پہلے ہی اس کے اسباب فراہم کرلئے ہیں اور خدا اپنے بندوں پر ظلم نہیں کرتا ہے'),
(476, 3, 183, 'جو لوگ یہ کہتے ہیں کہ اللہ نے ہم سے عہد لیا ہے کہ ہم اس وقت تک کسی رسول پر ایمان نہ لائیں جب تک وہ ایسی قربانی پیش نہ کرے جسے آسمانی آگ کھا جائے تو ان سے کہہ دیجئے کہ مجھ سے پہلے بہت سے رسول معجزات اور تمہاری فرمائش کے مطابق صداقت کی نشانی لے آئے پھر تم نے انہیں کیوں قتل کردیا اگر تم اپنی بات میں سچےّ ہو'),
(477, 3, 184, 'اس کے بعد بھی آپ کی تکذیب کریں تو آپ سے پہلے بھی رسولوں کی تکذیب ہوچکی ہے جو معجزات, مواعظ اور روشن کتاب سب کچھ لے کر آئے تھے'),
(478, 3, 185, 'ہر نفس موت کا مزہ چکھنے والا ہے اور تمہارا مکمل بدلہ تو صرف قیامت کے دن ملے گا- اس وقت جسے جہّنم سے بچا لیا گیا اور جنّت میں داخل کردیا گیا وہ کامیاب ہے اور زندگانی دنیا تو صرف دھوکہ کاسرمایہ ہے'),
(479, 3, 186, 'یقینا تم اپنے اموال اور نفوس کے ذریعہ آزمائے جاؤگے اور جن کو تم سے پہلے کتاب دی گئی ہے اور جو مشرک ہوگئے ہیں سب کی طرف سے بہت اذیت ناک باتیں سنوگے- اب اگر تم صبر کروگے اور تقوٰی اختیارکروگے تو یہی امورمیں استحکام کا سبب ہے'),
(480, 3, 187, 'اس موقع کو یاد کرو جب خدا نے جن کو کتاب دی ان سے عہد لیا کہ اسے لوگوں کے لئے بیان کریں گے اور اسے حُھپائیں گے نہیں.. لیکن انہوں نے اس عہد کو پبُ پشت ڈال دیا اور تھوڑی قیمت پر بیچ دیا تو یہ بہت اِرا سودا کیا ہے'),
(481, 3, 188, 'خبردار جو لوگ اپنے کئے پر مغرور ہیں اور چاہتے ہیں کہ جو اچھے کام نہیں کئے ہیں ان پر بھی ان کی تعریف کی جائے تو خبردار انہیں عذاب سے محفوظ خیال بھی نہ کرنا- ان کے لئے دردناک عذاب ہے'),
(482, 3, 189, 'اور اللہ کے لئے زمین و آسمان کی کل حکومت ہے اور وہ ہر شے پر قادر ہے'),
(483, 3, 190, 'بے شک زمین و آسمان کی خلقت لیل و نہار کی آمدو رفت میں صاحبانِ عقل کے لئے قدرت کی نشانیاں ہیں'),
(484, 3, 191, 'جو لوگ اٹھتے, بیٹھتے, لیٹتے خدا کو یاد کرتے ہیں اور آسمان و زمین کی تخلیق میں غوروفکر کرتے ہیں... کہ خدایا تو نے یہ سب بے کار نہیں پیدا کیا ہے- تو پاک و بے نیاز ہے ہمیں عذاب جہّنم سے محفوظ فرما'),
(485, 3, 192, 'پروردگار تو جسے جہّنم میں ڈال دے گا گویا اسے ذلیل و فِسوا کردیا اور ظالمین کا کوئی مددگار نہیں ہے'),
(486, 3, 193, 'پروردگار ہم نے اس منادی کو صَنا جو ایمان کی آواز لگا رہا تھا کہ اپنے پروردگار پر ایمان لے آؤ تو ہم ایمان لے آئے- پروردگار اب ہمارے گناہوں کو معاف فرما اور ہماری برائیوں کی پردہ پوشی فرما اور ہمیں نیک بندوں کے ساتھ محشور فرما'),
(487, 3, 194, 'پروردگار جو تو نے اپنے رسولوں سے وعدہ کیا ہے اسے عطا فرما اور روزِ قیامت ہمیں رسوا نہ کرنا کہ تو وعدہ کے خلاف نہیں کرتا'),
(488, 3, 195, 'پس خدا نے ان کی دعا کوقبول کیا کہ میں تم میںسے کسی بھی عمل کرنے والے کے عمل کو ضائع نہیں کروں گا چاہے وہ مرد ہو یا عورت- تم میں بعض بعض سے ہیں- پس جن لوگوں نے ہجرت کی اور اپنے وطن سے نکالے گئے اور میری راہ میں ستائے گئے اور انہوں نے جہاد کیا اور قتل ہوگئے تو میں ان کی برائیوں کی پردہ پوشی کروں گا اور انہیں ان جنتوں میں داخل کروں گا جن کے نیچے نہریں جاری ہوں گی- یہ خدا کی طرف سے ثواب ہے اور اس کے پاس بہترین ثواب ہے'),
(489, 3, 196, 'خبردار تمہیں کفّار کا شہر شہر چکر لگانا دھوکہ میں نہ ڈال دے'),
(490, 3, 197, 'یہ حقیر سرمایہ اور سامانِ تعیش ہے اس کے بعد انجام جہّنم ہے اور وہ بدترین منزل ہے'),
(491, 3, 198, 'لیکن جن لوگوں نے تقوٰی الٰہی اختیار کیا ان کے لئے وہ باغات ہیں جن کے نیچے نہریں جاری ہوں گی- خدا کی طرف سے یہ سامانِ ضیافت ہے اور جو کچھ اس کے پاس ہے سب نیک افراد کے لئے خیر ہی خیرہے'),
(492, 3, 199, 'اہلِ کتاب میں وہ لوگ بھی ہیں جو اللہ پر اور جو کچھ تمہاری طرف نازل ہوا ہے اور جو اُن کی طرف نازل ہوا ہے سب پر ایمان رکھتے ہیں- اللہ کے سامنے سر جھکائے ہوئے ہیں- آیااُ خدا حقیرسی قیمت پر فروخت نہیں کرتے- ان کے لئے پروردگار کے یہاں ان کا اجر ہے اور خدا بہت جلد حساب کرنے والا ہے'),
(493, 3, 200, 'اے ایمان والو صبر کرو.صبر کی تعلیم دو.جہاد کے لئے تیاری کرو اور اللہ سے ڈرو شاید تم فلاح یافتہ اور کامیاب ہوجاؤ');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'انسانو! اس پروردگار سے ڈرو جس نے تم سب کو ایک نفس سے پیدا کیا ہے اور اس کا جوڑا بھی اسی کی جنس سے پیدا کیا ہے اور پھر دونوں سے بکثرت مرد وعورت دنیا میں پھیلا دئیے ہیں اور اس خدا سے بھی ڈرو جس کے ذریعہ ایک دوسرے سے سوال کرتے ہو اور قرابتداروں کی بے تعلقی سے بھی- اللہ تم سب کے اعمال کا نگراں ہے'),
(495, 4, 2, 'اور یتیموں کو ان کا مال دے دو اور ان کے مال کو اپنے مال سے نہ بدلو اور ان کے مال کو اپنے مال کے ساتھ ملا کر نہ کھا جاؤ کہ یہ گناہ کبیرہ ہے'),
(496, 4, 3, 'اور اگر یتیموں کے بارے میں انصاف نہ کرسکنے کا خطرہ ہے تو جو عورتیں تمہیں پسند ہیں دو. تین. چار ان سے نکاح کرلو اور اگر ان میں بھی انصاف نہ کرسکنے کا خطرہ ہے تو صرف ایک... یا جو کنیزیں تمہارے ہاتھ کی ملکیت ہیں, یہ بات انصاف سے تجاوز نہ کرنے سے قریب تر ہے'),
(497, 4, 4, 'عورتوں کو ان کا مہر عطا کردو پھر اگر وہ خوشی خوشی تمہیں دینا چاہیں تو شوق سے کھالو'),
(498, 4, 5, 'اور ناسمجھ لوگوں کو ان کے وہ اموال جن کو تمہارے لئے قیام کا ذریعہ بنایا گیا ہے نہ دو- اس میں ان کے کھانے کپڑے کا انتظام کردو اور ان سے مناسب گفتگو کرو'),
(499, 4, 6, 'اور یتیموں کا امتحان لو اور جب وہ نکاح کے قابل ہوجائیں تو اگر ان میں رشید ہونے کا احساس کروتو ان کے اموال ان کے حوالے کردو اور زیادتی کے ساتھ یا اس خوف سے کہ کہیں وہ بڑے نہ ہوجائیں جلدی جلدی نہ کھاجاؤ ... اور تم میں جو غنی ہے وہ ان کے مال سے پرہیز کرے اور جو فقیر ہے وہ بھی صرف بقدر مناسب کھائے- پھر جب ان کے اموال ان کے حوالے کرو تو گواہ بنالو اور خدا تو حساب کے لئے خود ہی کافی ہے'),
(500, 4, 7, 'مردوں کے لئے ان کے والدین اور اقربا کے ترکہ میں ایک حصہّ ہے اور عورتوں کے لئے بھی ان کے والدین اور اقربا کے ترکہ میں سے ایک حصہّ ہے وہ مال بہت ہو یا تھوڑا یہ حصہّ بطور فریضہ ہے'),
(501, 4, 8, 'اور اگر تقسیم کے وقت دیگر قرابتدار, ایتام, مساکین بھی آجائیں تو انہیں بھی اس میں سے بطور رزق دے دو اور ان سے نرم اور مناسب گفتگو کرو'),
(502, 4, 9, 'اور ان لوگوں کو اس بات سے ڈرنا چاہئے کہ اگر وہ خود اپنے بعد ضعیف و ناتواں اولاد چھوڑ جاتے تو کس قدر پریشان ہوتے لہذا خدا سے ڈریں اور سیدھی سیدھی گفتگو کریں'),
(503, 4, 10, 'جو لوگ ظالمانہ انداز سے یتیموں کا مال کھاجاتے ہیں وہ درحقیقت اپنے پیٹ میں آگ بھررہے ہیں اور عنقریب واصل جہّنم ہوں گے'),
(504, 4, 11, 'اللہ تمہاری اولاد کے بارے میں تمہیں ہدایت فرماتا ہے، ایک لڑکے کا حصہ دو لڑکیوں کے حصے کے برابر ہے، پس اگر لڑکیاں دو سے زائد ہوں تو ترکے کا دو تہائی ان کا حق ہے اور اگر صرف ایک لڑکی ہے تو نصف (ترکہ) اس کا ہے اور میت کی اولاد ہونے کی صورت میں والدین میں سے ہر ایک کو ترکے کا چھٹا حصہ ملے گا اور اگر میت کی اولاد نہ ہو بلکہ صرف ماں باپ اس کے وارث ہوں تواس کی ماں کو تیسرا حصہ ملے گا، پس اگر میت کے بھائی ہوں تو ماں کوچھٹا حصہ ملے گا، یہ تقسیم میت کی وصیت پر عمل کرنے اور اس کے قرض کی ادائیگی کے بعد ہو گی، تمہیں نہیں معلوم تمہارے والدین اور تمہاری اولاد میں فائدے کے حوالے سے کون تمہارے زیادہ قریب ہے، یہ حصے اللہ کے مقرر کردہ ہیں، یقینا اللہ بڑا جاننے والا، باحکمت ہے'),
(505, 4, 12, 'اور تمہیںاپنی بیویوں کے ترکے میں سے اگر ان کی اولاد نہ ہو نصف حصہ ملے گا اور اگر ان کی اولاد ہو تو ان کے ترکے میں سے چوتھائی تمہارا ہو گا، یہ تقسیم میت کی وصیت پر عمل کرنے اور قرض ادا کرنے کے بعد ہو گی اور تمہاری اولاد نہ ہو تو انہیں تمہارے ترکے میں سے چوتھائی ملے گااور اگر تمہاری اولاد ہو تو انہیں تمہارے ترکے میںسے آٹھواں حصہ ملے گا، یہ تقسیم تمہاری وصیت پر عمل کرنے اور قرض ادا کرنے کے بعد ہو گی اور اگر کوئی مرد یا عورت بے اولادہو اور والدین بھی زندہ نہ ہوں اور اس کا ایک بھائی یا ایک بہن ہو تو بھائی اور بہن میں سے ہر ایک کو چھٹا حصہ ملے گا، پس اگر بہن بھائی ایک سے زیادہ ہوں تو سب ایک تہائی حصے میں شریک ہوں گے، یہ تقسیم وصیت پرعمل کرنے اور قرض ادا کرنے کے بعد ہو گی، بشرطیکہ ضرر رساں نہ ہو، یہ نصیحت اللہ کی طرف سے ہے اور اللہ بڑا دانا، بردبار ہے'),
(506, 4, 13, 'یہ سب الٰہی حدود ہیں اور جو اللہ و رسول کی اطاعت کرے گا خدا اسے ان جنتوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی اور وہ ان میں ہمیشہ رہیں گے اور درحقیقت یہی سب سے بڑی کامیابی ہے'),
(507, 4, 14, 'اور جو خدا و رسول کی نافرمانی کرے گا اور ا سکے حدود سے تجاوز کرجائے گا خدا اسے جہّنم میں داخل کردے گا اور وہ وہیں ہمیشہ رہے گا اور اس کے لئے رسوا کن عذاب ہے'),
(508, 4, 15, 'اور تمہاری عورتوں میں سے جو عورتیں بدکاری کریں ان پر اپنوں میں سے چار گواہوں کی گواہی لو اور جب گواہی دے دیں تو انہیں گھروں میں بند کردو- یہاں تک کہ موت آجائے یا خدا ان کے لئے کوئی راستہ مقرر کردے'),
(509, 4, 16, 'اور تم میں سے جو آدمی بدکاری کریں انہیں اذیت دو پھر اگر توبہ کرلیں اور اپنے حال کی اصلاح کرلیں تو ان سے اعراض کرو کہ خدا بہت توبہ قبول کرنے والا اور مہربان ہے'),
(510, 4, 17, 'توبہ خد اکے ذمہ صرف ان لوگوں کے لئے ہے جو جہالت کی بنائ پر برائی کرتے ہیں اور پھر فورا توبہ کرلیتے ہیں کہ خدا ان کی توبہ کو قبول کرلیتا ہے وہ علیم و دانا بھی ہے اور صاحبِ حکمت بھی'),
(511, 4, 18, 'اور توبہ ان لوگوں کے لئے نہیں ہے جو پہلے برائیاں کرتے ہیں اور پھر جب موت سامنے آجاتی ہے تو کہتے ہیں کہ اب ہم نے توبہ کرلی اور نہ ان کے لئے ہے جو حالاُ کفر میں مرجاتے ہیں کہ ان کے لئے ہم نے بڑا دردناک عذاب مہّیا کر رکھا ہے'),
(512, 4, 19, 'اے ایمان والو تمہارے لئے جائز نہیں ہے کہ جبرا عورتوں کے وارث بن جاؤ اور خبردار انہیں منع بھی نہ کرو کہ جو کچھ ان کو دے دیا ہے اس کا کچھ حصہّ لے لو مگر یہ کہ واضح طور پر بدکاری کریں اور ان کے ساتھ نیک برتاؤ کرو اب اگر تم انہیں ناپسند بھی کرتے ہو تو ہوسکتا ہے کہ تم کسی چیز کو ناپسند کرتے ہو اور خدا اسی میں خیر کثیر قرار دے دے'),
(513, 4, 20, 'اور اگر تم ایک زوجہ کی جگہ پر دوسری زوجہ کو لانا چاہو اور ایک کو مال کثیر بھی دے چکے ہو تو خبردار اس میں سے کچھ واپس نہ لینا- کیا تم اس مال کو بہتان اور کھلے گناہ کے طور پر لینا چاہتے ہو'),
(514, 4, 21, 'اور آخر کس طرح تم مال کو واپس لو گے جب کہ ایک دوسرے سے متصل ہوچکا ہے اور ان عورتوں نے تم سے بہت سخت قسم کا عہد لیا ہے'),
(515, 4, 22, 'اور خبردار جن عورتوں سے تمہارے باپ دادا نے نکاح(جماع) کیا ہے ان سے نکاح نہ کرنا مگر وہ جو اب تک ہوچکا ہے... یہ کھلی ہوئی برائی اور پروردگار کا غضب اور بدترین راستہ ہے'),
(516, 4, 23, 'تمہارے اوپر تمہاری مائیں, بیٹیاں, بہنیں, پھوپھیاں, خالائیں, بھتیجیاں, بھانجیاں, وہ مائیں جنہوں نے تم کو دودھ پلایا ہے, تمہاری رضاعی(دودھ شریک) بہنیں, تمہاری بیویوں کی مائیں, تمہاری پروردہ عورتیں جو تمہاری آغوش میں ہیں اور ان عورتوں کی اولاد, جن سے تم نے دخول کیا ہے ہاں اگر دخول نہیں کیا ہے تو کوئی حرج نہیں ہے اور تمہارے فرزندوں کی بیویاں جو فرزند تمہارے صلب سے ہیں اور دو بہنوں کا ایک ساتھ جمع کرنا سب حرام کردیا گیا ہے علاوہ اس کے جو اس سے پہلے ہوچکا ہے کہ خدا بہت بخشنے والا اور مہربان ہے'),
(517, 4, 24, 'اورتم پر حرام ہیں شادی شدہ عورتیں- علاوہ ان کے جو تمہاری کنیزیں بن جائیں- یہ خدا کا کھلا ہوا قانون ہے اور ان سب عورتوں کے علاوہ تمہارے لئے حلال ہے کہ اپنے اموال کے ذریعہ عورتوں سے رشتہ پیدا کرو عفت و پاک دامنی کے ساتھً سفاح و زنا کے ساتھ نہیں پس جو بھی ان عورتوں سے تمتع کرے ان کی اجرت انہیں بطور فریضہ دے دے اور فریضہ کے بعد آپس میں رضا مندی ہوجائے تو کوئی حرج نہیں ہے بیشک اللہ علیم بھی ہے اور حکیم بھی ہے'),
(518, 4, 25, 'اور جس کے پاس اس قدر مالی وسعت نہیں ہے کہ مومن آزاد عورتوں سے نکاح کرے تو وہ مومنہ کنیز عورت سے عقد کرلے- خدا تمہارے ایمان سے باخبر ہے تم سب ایک دوسرے سے ہو- ان کنیزوں سے ان کے اہل کی اجازت سے عقد کرو اور انہیں ان کی مناسب اجرت(مہر) دے دو- ان کنیزوں سے عقد کرو جو عفیفہ اور پاک دامن ہوں نہ کہ کھّلم کھلاّ زنا کار ہوں اور نہ چوری چھاَپے دوستی کرنے والی ہوں پھر جب عقد میں آگئیں تو اگر زنا کرائیں تو ان کے لئے آزاد عورتوں کے نصف کے برابر سزا ہے- یہ کنیزوں سے عقد ان کے لئے ہے جو بے صبری کا خطرہ رکھتے ہوں ورنہ صبر کرو تو تمہارے حق میں بہتر ہے اور اللہ غفور و رحیم ہے'),
(519, 4, 26, 'وہ چاہتا ہے کہ تمہارے لئے واضح احکام بیان کردے اور تمہیں تم سے پہلے والوں کے طریقہ کار کی ہدایت کردے اور تمہاری توبہ قبول کرلے اور وہ خوب جاننے والا اور صاحبِ حکمت ہے'),
(520, 4, 27, 'خدا چاہتا ہے کہ تمہاری توبہ قبول کرے اور خواہشات کی پیروی کرنے والے چاہتے ہیں کہ تمہیں بالکل ہی راسِ حق سے دور کردیں'),
(521, 4, 28, 'خدا چاہتا ہے کہ تمہارے لئے تخفیف کا سامان کردے اور انسان تو کمزو ر ہی پیدا کیا گیا ہے'),
(522, 4, 29, 'اے ایمان والو- آپس میںایک دوسرے کے مال کو ناحق طریقہ سے نہ کھا جایا کرو ... مگر یہ کہ باہمی رضامندی سے معاملت ہو اور خبردار اپنے نفس کو قتل نہ کرو - اللہ تمہارے حال پر بہت مہربان ہے'),
(523, 4, 30, 'اور جو ایسا اقدام حدود سے تجاوز اور ظلم کے عنوان سے کرے گا ہم عنقریب اسے جہّنم میں ڈال دیں گے اور اللہ کے لئے یہ کام بہت آسان ہے'),
(524, 4, 31, 'اگر تم بڑے بڑے گناہوں سے جن سے تمہیں روکا گیا ہے پرہیز کرلو گے تو ہم دوسرے گناہوں کی پردہ پوشی کردیں گے اور تمہیں باعزّت منزل تک پہنچادیں گے'),
(525, 4, 32, 'اور خبردار جو خدا نے بعض افراد کو بعض سے کچھ زیادہ دیا ہے اس کی تمنّا اور آرزو نہ کرنا مردوں کے لئے وہ حصہّ ہے جو انہوں نے کمایا ہے اور عورتوں کے لئے وہ حصہّ ہے جو انہوں نے حاصل کیا ہے- اللہ سے اس کے فضل کا سوال کرو کہ وہ بیشک ہر شے کا جاننے والا ہے'),
(526, 4, 33, 'اور جو کچھ ماں باپ یا اقربا نے چھوڑا ہے ہم نے سب کے لئے ولی و وارث مقرر کردئیے ہیں اور جن سے تم نے عہد و پیمان کیا ہے ان کا حصہ ّبھی انہیں دے دو بیشک اللہ ہر شے پر گواہ اور نگراں ہے'),
(527, 4, 34, 'مرد عورتوں کے حاکم اور نگراں ہیں ان فضیلتوں کی بنا پر جو خد انے بعض کو بعض پر دی ہیں اور اس بنا پر کہ انہوں نے عورتوں پر اپنا مال خرچ کیا ہے- پس نیک عورتیں وہی ہیں جو شوہروں کی اطاعت کرنے والی اور ان کی غیبت میں ان چیزوں کی حفاظت کرنے والی ہیں جن کی خدا نے حفاظت چاہی ہے اور جن عورتوں کی نافرمانی کا خطرہ ہے انہیں موعظہ کرو- انہیں خواب گاہ میں الگ کردو اور مارو اور پھر اطاعت کرنے لگیں تو کوئی زیادتی کی راہ تلاش نہ کرو کہ خدا بہت بلند اور بزرگ ہے'),
(528, 4, 35, 'اور اگر دونوں کے درمیان اختلاف کا اندیشہ ہے تو ایک دَکُم مرد کی طرف سے اور ایک عورت والوں میں سے بھیجو- پھر وہ دونوں اصلاح چاہیں گے تو خدا ان کے درمیان ہم آہنگی پیدا کردے گا بیشک اللہ علیم بھی ہے اور خبیر بھی'),
(529, 4, 36, 'اور اللہ کی عبادت کرو اور کسی شے کو اس کا شریک نہ بناؤ اور والدین کے ساتھ نیک برتاؤ کرو اور قرابتداروں کے ساتھ اور یتیموں, مسکینوں, قریب کے ہمسایہ, دور کے ہمسایہ, پہلو نشین, مسافر غربت زدہ, غلام و کنیز سب کے ساتھ نیک برتاؤ کرو کہ اللہ مغرور اور متکبر لوگوں کو پسند نہیں کرتا'),
(530, 4, 37, 'جو خود بھی بخل کرتے ہیں اور دوسروں کو بھی بخل کا حکم دیتے ہیں اور جو کچھ خدانے اپنے فضل و کرم سے عطا کیا ہے اس پر پردہ ڈالتے ہیں اور ہم نے کافروں کے واسطے فِسوا کن عذاب مہّیا کر رکھا ہے'),
(531, 4, 38, 'اور جو لوگ اپنے اموال کو لوگوں کو دکھانے کے لئے خرچ کرتے ہیں اور اللہ اور آخرت پر ایمان نہیں رکھتے ہیں انہیں معلوم ہونا چاہئے کہ جس کا شیطان ساتھی ہوجائے وہ بدترین ساتھی ہے'),
(532, 4, 39, 'ان کا کیا نقصان ہے اگر یہ اللہ اور آخرت پر ایمان لے آئیں اور جو اللہ نے بطور رزق دیا ہے اسے اس کی راہ میں خرچ کریں اور اللہ ہر ایک کو خوب جانتا ہے'),
(533, 4, 40, 'اللہ کسی پر ذرہ برابر ظلم نہیں کرتا- انسان کے پاس نیکی ہوتی ہے تو اسے دوگنا کردیتا ہے اور اپنے پاس سے اجر عظیم عطا کرتا ہے'),
(534, 4, 41, 'اس وقت کیا ہوگا جب ہم ہر امت کو اس کے گواہ کے ساتھ بلائیں گے اور پیغمبر آ پ کو ان سب کا گواہ بناکر بلائیں گے'),
(535, 4, 42, 'اس دن جن لوگوں نے کفر اختیار کیا ہے اور رسول کی نافرمانی کی ہے یہ خواہش کریں گے کہ اے کاش ان کے اوپر سے زمین برابر کردی جاتی اور وہ خدا سے کسی بات کو نہیں چھپا سکتے ہیں'),
(536, 4, 43, 'ایمان والو خبر دار نشہ کی حالت میں نماز کے قریب بھی نہ جانا جب تک یہ ہوش نہ آجائے کہ تم کیا کہہ رہے ہو اور جنابت کی حالت میں بھی مگر یہ کہ راستہ سے گزر رہے ہو جب تک غسل نہ کرلو اور اگر بیمار ہو یا سفر کی حالت میں ہو اور کسی کے پیخانہ نکل آئے, یا عورتوں سے باہمی جنسی ربط قائم ہوجائے اور پانی نہ ملے تو پاک مٹی سے تمیم کرلو اس طرح کہ اپنے چہروں اور ہاتھوں پر مسح کرلو بیشک خدا بہت معاف کرنے والا اور بخشنے والا ہے'),
(537, 4, 44, 'کیا تم نے ان لوگوں کو نہیں دیکھا ہے جنہیں کتاب کا تھوڑا سا حصہّ دے دیا گیا ہے کہ وہ گمراہی کا سودا کرتے ہیں اور چاہتے ہیں کہ تم بھی راستہ سے بہک جاؤ'),
(538, 4, 45, 'اور اللہ تمہارے دشمنوں کو خوب جانتا ہے اور وہ تمہاری سرپرستی اور مدد کے لئے کافی ہے'),
(539, 4, 46, 'یہودیوں میں وہ لوگ بھی ہیں جو کلمات الٰہٰیہ کو ان کی جگہ سے ہٹا دیتے ہیں اور کہتے ہیں کہ ہم نے بات سنی اور نافرمانی کی اور تم بھی سنو مگر تمہاری بات نہ سنی جائے گی یہ سب زبان کے توڑ مروڑ اور دین میں طعنُہ زنی کی بنا پر ہوتا ہے حالانکہ اگر یہ لوگ یہ کہتے کہ ہم نے سنا اور اطاعت کی آپ بھی سنئے اور نظ» کرم کیجئے تو ان کے حق میں بہتر اور مناسب تھا لیکن خدا نے ان کے کفر کی بنا پر ان پر لعنت کی ہے تو یہ ایمان نہ لائیں گے مگر بہت قلیل تعداد میں'),
(540, 4, 47, 'اے وہ لوگ جنہیں کتاب دی گئی ہے ہمارے نازل کئے ہوئے قرآن پر ایمان لے آؤ جو تمہاری کتابوں کی تصدیق کرنے والا ہے قبل اس کے کہ ہم تمھارے چہروں کو بگاڑ کر پشت کی طرف پھیر دیں یا ان پر اس طرح لعنت کریں جس طرح ہم نے اصحابِ سبت پر لعنت کی ہے اور اللہ کا حکم بہرحال نافذ ہے'),
(541, 4, 48, 'اللہ اس بات کو معاف نہیں کرسکتا کہ اس کا شریک قرار دیا جائے اور اس کے علاوہ جس کو چاہے بخش سکتا ہے اور جو بھی اس کا شریک بنائے گا اس نے بہت بڑا گناہ کیا ہے'),
(542, 4, 49, 'کیا تم نے ان لوگوں کو نہیں دیکھا جو اپنے نفس کی پاکیزگی کا اظہار کرتے ہیں ... حالانکہ اللہ جس کو چاہتا ہے پاکیزہ بناتا ہے اور بندوں پر دھاگے کے برابر بھی ظلم نہیں ہوتا'),
(543, 4, 50, 'دیکھو تو انہوں نے کس طرح خدا پر کھّلم کّھلا الزام لگایا ہے اور یہی ان کے کھّلم کّھلا گناہ کے لئے کافی ہے'),
(544, 4, 51, 'کیا تم نے نہیں دیکھا کہ جن لوگوں کو کتاب کا کچھ حصہّ دے دیا گیا وہ شیطان اور بتوں پر ایمان رکھتے ہیں اور کفار کو بھی بتاتے ہیں کہ یہ لوگ ایمان والوں سے زیادہ سیدھے راستے پر ہیں'),
(545, 4, 52, 'یہی وہ لوگ ہیں جن پر خدا نے لعنت کی ہے اور جس پر خدا لعنت کردے آپ پھر اس کا کوئی مددگار نہ پائیں گے'),
(546, 4, 53, 'کیا ملک دنیا میں ان کا بھی کوئی حصہ ّہے کہ لوگوں کو بھوسی برابر بھی نہیں دینا چاہتے ہیں'),
(547, 4, 54, 'یا وہ ان لوگوں سے حسد کرتے ہیں جنہیں خدا نے اپنے فضل و کرم سے بہت کچھ عطا کیا ہے تو پھر ہم نے آلِ ابراہیم علیھ السّلامکو کتاب و حکمت اور ملک عظیم سب کچھ عطا کیا ہے'),
(548, 4, 55, 'پھر ان میں سے بعض ان چیزوں پر ایمان لے آئے اور بعض نے انکار کردیا اور ان لوگوں کے لئے دہکتا ہوا جہّنم ہی کافی ہے'),
(549, 4, 56, 'بیشک جن لوگوں نے ہماری آیتوں کا انکار کیا ہے ہم انہیں آگ میں بھون دیں گے اور جب ایک کھال پک جائے گی تو دوسری بدل دیں گے تاکہ عذاب کا مزہ چکھتے رہیں خدا سب پر غالب اور صاحبِ حکمت ہے'),
(550, 4, 57, 'اور جو لوگ ایمان لے آئے اور انہوں نے نیک عمل کئے ہم عنقریب انہیں جنتوں میں داخل کریں گے جن کے نیچے نہریں جاری ہوں گی اور وہ ان ہی میں ہمیشہ رہیں گے ان کے لئے وہاں پاکیزہ بیویاں ہوں گی اور انہیں گھنی چھاؤں میں رکھا جائے گا'),
(551, 4, 58, 'بیشک اللہ تمہیں حکم دیتا ہے کہ امانتوں کو ان کے اہل تک پہنچا دو اور جب کوئی فیصلہ کرو تو انصاف کے ساتھ کرو اللہ تمہیں بہترین نصیحت کرتا ہے بیشک اللہ سمیع بھی ہے اور بصیر بھی'),
(552, 4, 59, 'ایمان والو اللہ کی اطاعت کرو رسول اور صاحبانِ امر کی اطاعت کرو جو تم ہی میں سے ہیں پھر اگر آپس میں کسی بات میں اختلاف ہوجائے تو اسے خدا اور رسول کی طرف پلٹا دو اگر تم اللہ اور روز آخرت پر ایمان رکھنے والے ہو- یہی تمہارے حق میں خیر اور انجام کے اعتبار سے بہترین بات ہے'),
(553, 4, 60, 'کیا آپ نے ان لوگوں کو نہیں دیکھا جن کا خیال یہ ہے کہ وہ آپ پر اور آپ کے پہلے نازل ہونے والی چیزوں پر ایمان لے آئے ہیں اور پھر یہ چاہتے ہیں کہ سرکش لوگوں کے پاس فیصلہ کرائیں جب کہ انہیں حکم دیا گیا ہے کہ طاغوت کا انکار کریں اور شیطان تو یہی چاہتا ہے کہ انہیں گمراہی میں دور تک کھینچ کر لے جائے'),
(554, 4, 61, 'اور جب ان سے کہا جاتا ہے کہ حکم خدا .... اور اس کے رسول کی طرف آؤ تو تم منافقین کو دیکھو گے کہ وہ شدّت سے انکار کردیتے ہیں'),
(555, 4, 62, 'پس اس وقت کیا ہوگا جب ان پر ان کے اعمال کی بنا پر مصیبت نازل ہوگی اور وہ آپ کے پاس آکر خدا کی قسم کھائیں گے کہ ہمارا مقصد صرف نیکی کرنا اور اتحاد پیدا کرنا تھا'),
(556, 4, 63, 'یہی وہ لوگ ہیں جن کے دل کا حال خدا خوب جانتا ہے لہذا آپ ان سے کنارہ کش رہیں انہیں نصیحت کریں اور ان کے دل پر اثر کرنے والی محل موقع کے مطابق بات کریں'),
(557, 4, 64, 'اور ہم نے کسی رسول کو بھی نہیں بھیجا ہے مگر صرف اس لئے کہ حکِم خدا سے اس کی اطاعت کی جائے اور کاش جب ان لوگوں نے اپنے نفس پر ظلم کیا تھا تو آپ کے پاس آتے اور خود بھی اپنے گناہوں کے لئے استغفار کرتے اور رسول بھی ان کے حق میں استغفار کرتا تو یہ خدا کو بڑا ہی توبہ قبول کرنے والا اور مہربان پاتے'),
(558, 4, 65, 'پس آپ کے پروردگار کی قسم کہ یہ ہرگز صاحبِ ایمان نہ بن سکیں گے جب تک آپ کو اپنے اختلافات میں حکُم نہ بنائیں اور پھر جب آپ فیصلہ کردیں تو اپنے دل میں کسی طرح کی تنگی کا احساس نہ کریں اور آپ کے فیصلہ کے سامنے سراپا تسلیم ہوجائیں'),
(559, 4, 66, 'اور اگر ہم ان منافقین پر فرض کردیتے کہ اپنے کو قتل کر ڈالیں یا گھر چھوڑ کر نکل جائیں تو چند افراد کے علاوہ کوئی عمل نہ کرتا حالانکہ اگر یہ اس نصیحت پر عمل کرتے تو ان کے حق میں بہتر ہی ہوتا اور ان کو زیادہ ثبات حاصل ہوتا'),
(560, 4, 67, 'اور ہم انہیں اپنی طرف سے اج» عظیم بھی عطا کرتے'),
(561, 4, 68, 'اور انہیں سیدھے راستہ کی ہدایت بھی کردیتے'),
(562, 4, 69, 'اور جو بھی اللہ اور رسول کی اطاعت کرے گا وہ ان لوگوں کے ساتھ رہے گا جن پر خدا نے نعمتیں نازل کی ہیں انبیائً صدیقین, شہدائ اور صالحین اور یہی بہترین رفقائ ہیں'),
(563, 4, 70, 'یہ اللہ کی طرف سے فضل و کرم ہے اور خدا ہر ایک کے حالات کے علم کے لئے کافی ہے'),
(564, 4, 71, 'ایمان والو اپنے تحفظ کا سامان سنبھال لو اور جماعت جماعت یا اکٹھا جیسا موقع ہو سب نکل پڑو'),
(565, 4, 72, 'تم میں ایسے لوگ بھی گھس گئے ہیں جو لوگوں کو روکیں گے اور اگر تم پر کوئی مصیبت آگئی تو کہیں گے کہ خد انے ہم پر احسان کیا کہ ہم ان کے ساتھ حاضر نہیں تھے'),
(566, 4, 73, 'اور اگر تمہیں خدائی فضل و کرم مل گیا تو اس طرح جیسے تمہارے ان کے درمیان کبھی دوستی ہی نہیں تھی کہنے لگیں گے کہ کاش ہم بھی ان کے ساتھ ہوتے اور کامیابی کی عظیم منزل پر فائز ہوجاتے'),
(567, 4, 74, 'اب ضرورت ہے کہ راسِ خدا میں وہ لوگ جہاد کریں جو زندگانی دنیا کو آخرت کے عوض بیچ ڈالتے ہیں اور جو بھی راسِ خدا میں جہاد کرے گا وہ قتل ہوجائے یا غالب آجائے دونوں صورتوں میں ہم اسے اج» عظیم عطا کریں گے'),
(568, 4, 75, 'اور آخر تمہیں کیا ہوگیا ہے کہ تم اللہ کی راہ میں اور ان کمزور مردوں, عورتوں اور بچوں کے لئے جہاد نہیں کرتے ہو جنہیں کمزور بناکر رکھا گیا ہے اور جو برابر دعا کرتے ہیں کہ خدایا ہمیں اس قریہ سے نجات دے دے جس کے باشندے ظالم ہیں اور ہمارے لئے کوئی سرپرست اور اپنی طرف سے مددگار قرار دے دے'),
(569, 4, 76, 'ایمان والے ہمیشہ اللہ کی راہ میں جہاد کرتے ہیں اور جو کافر ہیں وہ ہمیشہ طاغوت کی راہ میں لڑتے ہیں لہذا تم شیطان کے ساتھیوں سے جہاد کرو بیشک شیطان کا مکر بہت کمزور ہوتا ہے'),
(570, 4, 77, 'کیا تم نے ان لوگوں کو نہیں دیکھا جن سے کہا گیا تھا کہ ہاتھ روکے رکھو اور نماز قائم کرو, زکوِٰادا کرو(تو بے چین ہوگئے) اور جب جہاد واجب کردیا گیا تو ایک گروہ لوگوں(دشمنوں) سے اس قدر ڈرتا تھا جیسے خدا سے ڈرتا ہو یا اس سے بھی کچھ زیادہ اور یہ کہتے ہیں کہ خدایا اتنی جلدی کیوں جہاد واجب کردیا کاش تھوڑی مدت تک اور ٹال دیاجاتا پیغمبرآپ کہہ دیجئے کہ دنیا کا سرمایہ بہت تھوڑا ہے اور آخرت صاحبانِ تقوٰی کے لئے بہترین جگہ ہے اور تم پر دھاگہ برابر بھی ظلم نہیں کیا جائے گا'),
(571, 4, 78, 'تم جہاں بھی رہو گے موت تمہیں پالے گی چاہے مستحکم قلعوں میں کیوں نہ بند ہوجاؤ- ان لوگوں کا حال یہ ہے کہ اچھے حالات پیدا ہوتے ہیں تو کہتے ہیں کہ یہ خدا کی طرف سے ہے اور مصیبت آتی ہے تو کہتے ہیں کہ یہ آپ کی طرف سے ہے تو آپ کہہ دیجئے کہ سب خدا کی طرف سے ہے پھر آخر اس قوم کو کیا ہوگیا ہے کہ یہ کوئی بات سمجھتی ہی نہیں ہے'),
(572, 4, 79, 'تم تک جو بھی اچھائی اور کامیابی پہنچی ہے وہ اللہ کی طرف سے ہے اور جو بھی برائی پہنچی ہے وہ خود تمہاری طرف سے ہے .... اور اے پیغمبرہم نے آپ کو لوگوں کے لئے رسول بنایا ہے اور خدا گواہی کے لئے کافی ہے'),
(573, 4, 80, 'جو رسول کی اطاعت کرے گا اس نے اللہ کی اطاعت کی اور جو منہ موڑ لے گا تو ہم نے آپ کو اس کا ذمہ دار بناکر نہیں بھیجا ہے'),
(574, 4, 81, 'اور یہ لوگ پہلے اطاعت کی بات کرتے ہیں- پھر جب آپ کے پاس سے باہر نکلتے ہیں تو ایک گروہ اپنے قول کے خلاف تدبیریں کرتا ہے اور خدا ان کی ان باتوں کو لکھ رہا ہے- آپ ان سے اعراض کریں اور خدا پر بھروسہ کریں اور خدا اس ذمہ داری کے لئے کافی ہے'),
(575, 4, 82, 'کیا یہ لوگ قرآن میں غور و فکر نہیں کرتے ہیں کہ اگر وہ غیر خدا کی طرف سے ہوتا تو اس میں بڑا اختلاف ہوتا'),
(576, 4, 83, 'اور جب ان کے پاس امن یا خوف کی خبر آتی ہے تو فورا نشر کردیتے ہیں حالانکہ اگر رسول اور صاحبانِ امر کی طرف پلٹا دیتے تو ان سے استفادہ کرنے والے حقیقت حال کا علم پیدا کرلیتے اور اگر تم لوگوں پر خدا کا فضل اور ا سکی رحمت نہ ہوتی تو چند افراد کے علاوہ سب شیطان کا اتباع کرلیتے'),
(577, 4, 84, 'اب آپ راسِ خدا میں جہاد کریں اور آپ اپنے نفس کے علاوہ دوسروں کے مکلف نہیں ہیں اور مومنین کو جہاد پر آمادہ کریں- عنقریب خدا کفار کے شر کو روک دے گا اور اللہ انتہائی طاقت والا اور سخت سزا دینے والا ہے'),
(578, 4, 85, 'جو شخص اچھی سفارش کرے گا اسے اس کا حصہ ّملے گااور جو اِری سفارش کرے گا اسے اس میں سے حصہّ ملے گا اور اللہ ہر شے پر اقتدار رکھنے والا ہے'),
(579, 4, 86, 'اور جب تم لوگوں کو کوئی تحفہ(سلام) پیش کیا جائے تو اس سے بہتر یا کم سے کم ویسا ہی واپس کرو کہ بیشک اللہ ہر شے کا حساب کرنے والا ہے'),
(580, 4, 87, 'اللہ وہ خدا ہے جس کے علاوہ کوئی معبود نہیں ہے- وہ تم سب کو روزِ قیامت جمع کرے گا اور اس میں کوئی شک نہیں ہے اور اللہ سے زیادہ سچی بات کون کرنے والا ہے'),
(581, 4, 88, 'آخر تمہیں کیا ہوگیا ہے کہ منافقین کے بارے میں دو گروہ ہوگئے ہو جب کہ اللہ نے ان کے اعمال کی بنا پر انہیں اُلٹ دیا ہے کیا تم اسے ہدایت دینا چاہتے ہو خدا نے جسے گمراہی پر چھوڑ دیا ہے- حالانکہ جسے خدا گمراہی میں چھوڑ دے اس کے لئے تم کوئی راستہ نہیں نکال سکتے'),
(582, 4, 89, 'یہ منافقین چاہتے ہیں کہ تم بھی ان کی طرح کافر ہوجاؤ اور سب برابر ہوجائیں تو خبردار تم انہیں اپنا دوست نہ بنانا جب تک راسِ خدا میں ہجرت نہ کریں پھر یہ انحراف کریں تو انہیں گرفتار کرلو اور جہاں پا جاؤ قتل کردو اور خبر دار ان میں سے کسی کو اپنا دوست اور مددگار نہ بنانا'),
(583, 4, 90, 'علاوہ ان کے جو کسی ایسی قوم سے مل جائیں جن کے اور تمہارے درمیان معاہدہ ہو یا وہ تمہارے پاس دل تنگ ہوکر آجائیں کہ نہ تم سے جنگ کریں گے اور نہ اپنی قوم سے- اور اگر خدا چاہتا تو ان کو تمہارے اوپر مسلّط کردیتا اور وہ تم سے بھی جنگ کرتے لہذا اگر تم سے الگ رہیں اور جنگ نہ کریں اور صلح کا پیغام دیں تو خدا نے تمہارے لئے ان کے اوپر کوئی راہ نہیں قرار دی ہے'),
(584, 4, 91, 'عنقریب تم ایک اور جماعت کو پاؤ گے جو چاہتے ہیں کہ تم سے بھی محفوظ رہیں اور اپنی قوم سے بھی- یہ جب بھی فتنہ کی طرف بلائے جاتے ہیں الٹے اس میں اوندھے منہ گر پڑتے ہیں لہذا یہ اگر تم سے الگ نہ ہوں اور صلح کا پیغام نہ دیں اور ہاتھ نہ روکیں تو انہیں گرفتار کرلو اور جہاں پاؤ قتل کردو یہی وہ ہیں کہ جن پر تمہیں کھلا غلبہ عطا کیا گیا ہے'),
(585, 4, 92, 'اور کسی مومن کو یہ حق نہیں ہے کہ وہ کسی مومن کو قتل کردے مگر غلطی سے اور جو غلطی سے قتل کردے اسے چاہئے کہ ایک غلام آزاد کرے اور مقتول کے وارثوں کو دیت دے مگر یہ کہ وہ معاف کردیں پھر اگر مقتول ایسی قوم سے ہے جو تمہاری دشمن ہے اور(اتفاق سے) خود مومن ہے تو صرف غلام آزاد کرنا ہوگا اور اگر ایسی قوم کا فرد ہے جس کا تم سے معاہدہ ہے تو اس کے اہل کو دیت دینا پڑے گی اور ایک مومن غلام آزاد کرنا ہوگا اور غلام نہ ملے تو دو ماہ کے مسلسل روزے رکھنا ہوں گے یہی اللہ کی طرف سے توبہ کا راستہ ہے اور اللہ سب کی نیتوں سے باخبر ہے اور اپنے احکام میں صاحب هحکمت بھی ہے'),
(586, 4, 93, 'اور جو بھی کسی مومن کو قصدا قتل کردے گا اس کی جزا جہّنم ہے- اسی میں ہمیشہ رہنا ہے اور اس پر خدا کا غضب بھی ہے اور خدا لعنت بھی کرتا ہے اور اس نے اس کے لئے عذابِ عظیم بھی مہیاّ کررکھا ہے'),
(587, 4, 94, 'ایمان والو جب تم راسِ خدا میں جہاد کے لئے سفر کرو تو پہلے تحقیق کرلو اور خبردار جو اسلام کی پیش کش کرے اس سے یہ نہ کہنا کہ تو مومن نہیں ہے کہ اس طرح تم زندگانی دنیا کا چند روزہ سرمایہ چاہتے ہو اور خدا کے پاس بکثرت فوائد پائے جاتے ہیں- آخر تم بھی تو پہلے ایسے ہی کافر تھے- خدا نے تم پر احسان کیا کہ تمہارے اسلام کو قبول کرلیا(اور دل چیرنے کی شرط نہیں لگائی) تو اب تم بھی اقدام سے پہلے تحقیق کرو کہ خدا تمہارے اعمال سے خوب باخبر ہے'),
(588, 4, 95, 'اندھے بیمار اور معذور افراد کے علاوہ گھر بیٹھ رہنے والے صاحبانِ ایمان ہرگز ان لوگوں کے برابر نہیں ہوسکتے جو راسِ خدا میں اپنے جان و مال سے جہاد کرنے والے ہیں- اللہ نے اپنے مال اور جان سے جہاد کرنے والوں کو بیٹھ رہنے والوں پر امتیاز عنایت کئے ہیں اور ہر ایک سے نیکی کا وعدہ کیا ہے اور مجاہدین کو بیٹھ رہنے والوں کے مقابلہ میں اج» عظیم عطا کیا ہے'),
(589, 4, 96, 'اس کی طرف سے درجات مغفرت اور رحمت ہے اور وہ بڑا بخشنے والا اور مہربان ہے'),
(590, 4, 97, 'جن لوگوں کو ملائکہ نے اس حال میں اٹھایا کہ وہ اپنے نفس پر ظلم کرنے والے تھے ان سے پوچھا کہ تم کس حال میں تھے- انہوں نے کہا کہ ہم زمین میں کمزور بنادئے گئے تھے. ملائکہ نے کہا کہ کیا زمین هخدا وسیع نہیں تھی کہ تم ہجرت کرجاتے- ان لوگوں کا ٹھکاناجہّنم ہے اور وہ بدترین منزل ہے'),
(591, 4, 98, 'علاوہ ان کمزور مردوں, عورتوں اور بچوں کے جن کے اختیار میں کوئی تدبیر نہ تھی اور وہ کوئی راستہ نہ نکال سکتے تھے'),
(592, 4, 99, 'یہی وہ لوگ ہیں جن کو عنقریب خدا معاف کردے گا کہ وہ بڑا معاف کرنے والا اور بخشنے والا ہے'),
(593, 4, 100, 'اور جو بھی راسِ خدا میں ہجرت کرے گا وہ زمین میں بہت سے ٹھکانے اور وسعت پائے گا اور جو اپنے گھر سے خدا و رسول کی طرف ہجرت کے ارادہ سے نکلے گا اس کے بعد اسے موت بھی آجائے گی تو اس کا اجر اللہ کے ذمہ ہے اور اللہ بڑا بخشنے والا مہربان ہے'),
(594, 4, 101, 'اور جب تم زمین میں سفر کرو تو تمہارے لئے کوئی حرج نہیں ہے کہ اپنی نمازیں قصر کردو اگر تمہیں کفار کے حملہ کردینے کا خوف ہے کہ کفار تمہارے لئے کھاَلے ہوئے دشمن ہیں'),
(595, 4, 102, 'اور جب آپ مجاہدین کے درمیان ہوں اور ان کے لئے نماز قائم کریں تو ان کی ایک جماعت آپ کے ساتھ نماز پڑھے اور اپنے اسلحہ ساتھ رکھے اس کے بعد جب یہ سجدہ کرچکیں تو یہ پشت پناہ بن جائیں اور دوسری جماعت جس نے نماز نہیں پڑھی ہے وہ آکر شریمَ نماز ہوجائے اور اپنے اسلحہ اور بچاؤ کے سامان اپنے ساتھ رکھے- کفار کی خواہش یہی ہے کہ تم اپنے سازو سامان اور اسلحہ سے غافل ہوجاؤ تو یہ یکبارگی حملہ کردیں .... ہاں اگر بارش یا بیماری کی وجہ سے اسلحہ نہ اٹھاسکتے ہو تو کوئی حرج نہیں ہے کہ اسلحہ رکھ دو لیکن بچاؤ کا سامان ساتھ رکھو- اللہ نے کفر کرنے والوں کے لئے رسوا کن عذاب مہیّا کررکھا ہے'),
(596, 4, 103, 'اس کے بعد جب یہ نماز تمام ہوجائے تو کھڑے, بیٹھے, لیٹے ہمیشہ خدا کو یاد کرتے رہو اور جب اطمینان حاصل ہوجائے تو باقاعدہ نماز قائم کرو کہ نماز صاحبانِ ایمان کے لئے ایک وقت معین کے ساتھ فریضہ ہے'),
(597, 4, 104, 'اور خبردار دشمنوں کا پیچھا کرنے میں سستی سے کام نہ لینا کہ اگر تمہیں کوئی بھی رنج پہنچتا ہے تو تمہاری طرح کفار کو بھی تکلیف پہنچتی ہے اور تم اللہ سے وہ امیدیں رکھتے ہو جو انہیں حاصل نہیں ہیں اور اللہ ہر ایک کی نیت کا جاننے والا اور صاحبِ حکمت ہے'),
(598, 4, 105, 'ہم نے آپ کی طرف یہ برحق کتاب نازل کی ہے کہ لوگوں کے درمیان حکم خدا کے مطابق فیصلہ کریں اور خیانت کاروں کے طرفدار نہ بنیں'),
(599, 4, 106, 'اور اللہ سے استغفار کیجئے کہ اللہ بڑا غفور و رحیم ہے'),
(600, 4, 107, 'اور خبردار جو لوگ خود اپنے نفس سے خیانت کرتے ہیں ان کی طرف سے دفاع نہ کیجئے گا کہ خدا خیانت کار مجرموں کو ہرگز دوست نہیں رکھتا ہے'),
(601, 4, 108, 'یہ لوگ انسانوں کی نظروں سے اپنے کو چھپاتے ہیں اور خدا سے نہیں چھپ سکتے ہیں جب کہ وہ اس وقت بھی ان کے ساتھ رہتا ہے جب وہ ناپسندیدہ باتوں کی سازش کرتے ہیں اور خدا ان کے تمام اعمال کا احاطہ کئے ہوئے ہے'),
(602, 4, 109, 'ہوشیار .... اس وقت تم نے زندگانی دنیا میں ان کی طرف سے جھگڑا شروع بھی کردیا تو اب روزِ قیامت ان کی طرف سے اللہ سے کون جھگڑا کرے گا اور کون ان کا وکیل اور طرفدار بنے گا'),
(603, 4, 110, 'اور جو بھی کسی کے ساتھ برائی کرے گا یا اپنے نفس پر ظلم کرے گا اس کے بعد استغفار کرے گا تو خدا کو غفور اور رحیم پائے گا'),
(604, 4, 111, 'اور جو قصدا گناہ کرتا ہے وہ اپنے ہی خلاف کرتا ہے اور خدا سب کا جاننے والا ہے اور صاحبِ حکمت بھی ہے'),
(605, 4, 112, 'اور جو شخص بھی کوئی غلطی یا گناہ کرکے دوسرے بے گناہ کے سر ڈال دیتا ہے وہ بہت بڑے بہتان اور کِھلے گناہ کا ذمہ دار ہوتا ہے'),
(606, 4, 113, 'اگر آپ پر فضل خدا اور رحمت پروردگار کا سایہ نہ ہوتا تو ان کی ایک جماعت نے آپ کوبہکانے کا ارادہ کرلیا تھا اور یہ اپنے علاوہ کسی کو گمراہ نہیں کرسکتے اور آپ کو کوئی تکلیف نہیں پہنچاسکتے اور اللہ نے آپ پر کتاب اور حکمت نازل کی ہے اور آپ کو ان تمام باتوں کا علم دے دیا ہے جن کا علم نہ تھا اور آپ پر خدا کا بہت بڑا فضل ہے'),
(607, 4, 114, 'ان لوگوں کی اکثر راز کی باتوں میں کوئی خیر نہیںہے مگر وہ شخص جو کسی صدقہ, کا» خیر یا لوگوں کے درمیان اصلاح کا حکم دے اور جو بھی یہ سارے کام رضائے الٰہی کی طلب میں انجام دے گا ہم اسے اجر هعظیم عطا کریں گے'),
(608, 4, 115, 'اور جو شخص بھی ہدایت کے واضح ہوجانے کے بعد رسول سے اختلاف کرے گا اور مومنین کے راستہ کے علاوہ کوئی دوسرا راستہ اختیار کرے گا اسے ہم ادھر ہی پھیر دیں گے جدھر وہ پھر گیا ہے اور جہّنم میں جھونک دیں گے جو بدترین ٹھکانا ہے'),
(609, 4, 116, 'خدا اس بات کو معاف نہیں کرسکتا کہ اس کا شریک قرار دیا جائے اور اس کے علاوہ جس کو چاہے بخش سکتا ہے اور جو خدا کا شریک قرار دے گا وہ گمراہی میں بہت دور تک چلا گیا ہے'),
(610, 4, 117, 'یہ لوگ خدا کو چھوڑ کر بس عورتوں کی پرستش کرتے ہیں اور سرکش شیطان کو آواز دیتے ہیں'),
(611, 4, 118, 'جس پر خدا کی لعنت ہے اور اس نے خدا سے بھی کہہ دیا کہ میں تیرے بندوں میں سے ایک مقرر حصّہ ضرور لے لوں گا'),
(612, 4, 119, 'اور انہیں گمراہ کروں گا- امیدیں دلاؤں گا اور ایسے احکام دوں گا کہ وہ جانورں کے کان کاٹ ڈالیں گے پھر حکم دوں گا تو اللہ کی مقررہ خلقت کو تبدیل کردیں گے اور جو خد اکو چھوڑ کر شیطان کو اپنا ولی اور سرپرست بنائے گا وہ کھلے ہوئے خسارہ میں رہے گا'),
(613, 4, 120, 'شیطان ان سے وعدہ کرتا ہے اور انہیں امیدیں دلاتا ہے اور وہ جو بھی وعدہ کرتاہے وہ دھوکہ کے سوا کچھ نہیں ہے'),
(614, 4, 121, 'یہی وہ لوگ ہیں جن کا انجام جہّنم ہے اور وہ اس سے چھٹکارا نہیں پاسکتے ہیں'),
(615, 4, 122, 'اور جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ہم عنقریب انہیں ان جنتّوں میں داخل کریں گے جن کے نیچے نہریں جاری ہوں گی وہ ان میں ہمیشہ ہمیشہ رہیں گے- یہ خدا کا برحق وعدہ ہے اور خدا سے زیادہ راست گو کون ہوسکتا ہے'),
(616, 4, 123, 'کوئی کام نہ تمہاری امیدوں سے بنے گا نہ اہلِ کتاب کی امیدوں سے- جو بھی اِرا کام کرے گا اس کی سزا بہرحال ملے گی اور خدا کے علاوہ اسے کوئی سرپرست اور مددگار بھی نہیں مل سکتا ہے'),
(617, 4, 124, 'اور جو بھی نیک کام کرے گا چاہے وہ مرد ہو یا عورت بشرطیکہ وہ صاحب هایمان بھی ہو- ان سب کو جنّت میں داخل کیا جائے گا اور ان پر ذرّہ برابر ظلم نہیں کیا جائے گا'),
(618, 4, 125, 'اور اس سے اچھا دیندار کون ہوسکتا ہے جو اپنا رخ خد اکی طرف رکھے اور نیک کردار بھی ہو اور ملّت ابراہیم علیھ السّلام کا اتباع کرے جو باطل سے کترانے والے تھے اور اللہ نے ابراہیم علیھ السّلام کو اپنا خلیل اور دوست بنایا ہے'),
(619, 4, 126, 'اور اللہ ہی کے لئے زمین و آسمان کی کل کائنات ہے اور اللہ ہر شے پر احاطہ رکھنے والا ہے'),
(620, 4, 127, 'پیغمبر یہ لوگ آپ سے یتیم لڑکیوں کے بارے میں حکهِ خدا دریافت کرتے ہیں تو آپ کہہ دیجئے کہ ان کے بارے میں خدا اجازت دیتا ہے اور جو کتاب میں تمہارے سامنے حکم بیان کیا جاتا ہے وہ ان یتیم عورتوں کے بارے میں ہے جن کو تم ان کا حق میراث نہیں دیتے ہو اور چاہتے ہو کہ ان سے نکاح کرکے سارا مال روک لو اور ان کمزور بّچوں کے بارے میں ہے کہ یتیموں کے بارے میں انصاف کے ساتھ قیام کرو اور جو بھی تم کا»خیر کرو گے خدا اس کا بخوبی جاننے والا ہے'),
(621, 4, 128, 'اور اگر کوئی عورت شوہر سے حقوق ادا نہ کرنے یا اس کی کنارہ کشی سے طلاق کا خطرہ محسوس کرے تو دونوں کے لئے کوئی حرج نہیں ہے کہ کسی طرح آپس میں صلح کرلیں کہ صلح میں بہتری ہے اور بخل تو ہر نفس کے ساتھ حاضر رہتا ہے اور اگر تم اچھا برتاؤ کرو گے اور زیادتی سے بچو گے تو خدا تمہارے اعمال سے خوب باخبر ہے'),
(622, 4, 129, 'اور تم کتنا ہی کیوں نہ چاہو عورتوں کے درمیان مکمل انصاف نہیں کرسکتے ہو لیکن اب بالکل ایک طرف نہ جھک جاؤ کہ دوسری کو معلق چھوڑ دو اور اگر اصلاح کرلو اور تقویٰ اختیار کرو تو اللہ بہت بخشنے والا اور مہربان ہے'),
(623, 4, 130, 'اور اگر دونوں الگ ہی ہونا چاہیں تو خدا دونوں کو اپنے خزانے کی وسعت سے غنی اور بے نیاز بنادے گا کہ اللہ صاحبِ هوسعت بھی ہے اور صاحبِ حکمت بھی ہے'),
(624, 4, 131, 'اور اللہ کے لئے زمین و آسمان کی کل کائنات ہے اور ہم نے تم سے پہلے اہلِ کتاب کو اور اب تم کو یہ وصیت کی ہے کہ اللہ سے ڈرو اور اگر کفر اختیار کرو گے تو خدا کا کوئی نقصان نہیں ہے اس کے لئے زمین و آسمان کی کل کائنات ہے اور وہ بے نیاز بھی ہے اور قابل هحمدو ستائش بھی ہے'),
(625, 4, 132, 'اور اللہ کے لئے زمین و آسمان کی کل ملکیت ہے اور وہ سب کی نگرانی اور کفالت کے لئے کافی ہے'),
(626, 4, 133, 'وہ چاہے تو تم سب کو اٹھا لے جائے اور دوسروں لوگوں کو لے آئے اور وہ ہر شے پر قادر ہے'),
(627, 4, 134, 'جو انسان دنیا کا ثواب اور بدلہ چاہتا ہے (اسے معلوم ہونا چاہئے) کہ خدا کے پاس دنیا اور آخرت دونوں کا انعام ہے اور وہ ہر ایک کا سننے والا اور دیکھنے والا ہے'),
(628, 4, 135, 'اے ایمان والو! عدل و انصاف کے ساتھ قیام کرو اور اللہ کے لئے گواہ بنو چاہے اپنی ذات یا اپنے والدین اور اقربا ہی کے خلاف کیوں نہ ہو- جس کے لئے گواہی دینا ہے وہ غنی ہو یا فقیر اللہ دونوں کے لئے تم سے اولیٰ ہے لہذا خبردار خواہشات کا اتباع نہ کرنا تاکہ انصاف کرسکو اور اگر توڑ مروڑ سے کام لیا یا بالکل کنارہ کشی کرلی تو یاد رکھو کہ اللہ تمہارے اعمال سے خوب باخبر ہے'),
(629, 4, 136, 'ایمان والو! اللہ ً رسول اور وہ کتاب جو رسول پر نازل ہوئی ہے اور وہ کتاب جو اس سے پہلے نازل ہوچکی ہے سب پر ایمان لے آؤ اور یاد رکھو کہ جو خدا, ملائکہ, کتب سماویہ, رسول اور روزِ قیامت کا انکار کرے گا وہ یقینا گمراہی میں بہت دور نکل گیا ہے'),
(630, 4, 137, 'جو لوگ ایمان لائے اور پھر کفر اختیار کرلیا پھر ایمان لے آئے اور پھر کافر ہوگئے اور پھر کفر میں شدید و مزید ہوگئے تو خدا ہرگز انہیں معاف نہیں کرسکتا اور نہ سیدھے راستے کی ہدایت دے سکتا ہے'),
(631, 4, 138, 'آپ ان منافقین کو دردناک عذاب کی بشارت دے دیں'),
(632, 4, 139, 'جو لوگ مومنین کو چھوڑ کر کفار کو اپنا ولی اور سرپرست بناتے ہیں ... کیا یہ ان کے پاس عزّت تلاش کررہے ہیں جب کہ ساری عزّت صرف اللہ کے لئے ہے'),
(633, 4, 140, 'اور اس نے کتاب میں یہ بات نازل کردی ہے کہ جب آیااُ الٰہی کے بارے میں یہ سنو کہ ان کا انکار اور استہزائ ہورہا ہے تو خبردار ان کے ساتھ ہرگز نہ بیٹھنا جب تک وہ دوسری باتوں میں مصروف نہ ہوجائیں ورنہ تم ان ہی کے مثل ہوجاؤ گے خدا کفار اور منافقین سب کو جہّنم میں ایک ساتھ اکٹھا کرنے والا ہے'),
(634, 4, 141, 'یہ منافقین تمہارے حالات کا انتظار کرتے رہتے ہیں کہ تمہیں خدا کی طرف سے فتح نصیب ہو تو کہیں گے کیا ہم تمہارے ساتھ نہیں تھے اور اگر کفاّر کو کوئی حصّہ مل جائے گا تو ان سے کہیں گے کہ کیا ہم تم پر غالب نہیں آگئے تھے اور تمہیں مومنین سے بچا نہیں لیا تھا تو اب خدا ہی قیامت کے دن تمہارے درمیان فیصلہ کرے گا اور خدا کفاّر کے لئے صاحبان هایمان کے خلاف کوئی راہ نہیں دے سکتا'),
(635, 4, 142, 'منافقین خدا کو دھوکہ دینا چاہتے ہیں اور خدا ان کو دھوکہ میں رکھنے والا ہے اور یہ نماز کے لئے اٹھتے بھی ہیں تو سستی کے ساتھ- لوگوں کو دکھانے کے لئے عمل کرتے ہیں اور اللہ کو بہت کم یاد کرتے ہیں'),
(636, 4, 143, 'یہ کفر و اسلام کے درمیان حیران و سرگرداں ہیں نہ ان کی طرف ہیں اور نہ اُن کی طرف اور جس کو خدا گمراہی میں چھوڑ دے اس کے لئے آپ کو کوئی راستہ نہ ملے گا'),
(637, 4, 144, 'ایمان والو خبردار مومنین کو چھوڑ کر کفاّر کو اپنا ولی اور سرپرست نہ بنانا کیا تم چاہتے ہو کہ اللہ کے لئے اپنے خلاف صریحی دلیل و حجت قرار دے لو'),
(638, 4, 145, 'بے شک منافقین جہّنم کے سب سے نچلے طبقہ میں ہوں گے اور آپ ان کے لئے کوئی مددگار نہ پائیں گے'),
(639, 4, 146, 'علاوہ ان لوگوں کے جو توبہ کرلیں اور اپنی اصلاح کرلیں اور خدا سے وابستہ ہوجائیں اور دین کو خالص اللہ کے لئے اختیار کریں تو یہ صاحبان ایمان کے ساتھ ہوں گے اور عنقریب اللرُ ان صاحبان ایمان کو اجر عظیم عطا کرے گا'),
(640, 4, 147, 'خدا تم پر عذاب کرکے کیا کرے گا اگر تم اس کے شکر گزار اور صاحبِ ایمان بن جاؤ اور وہ تو ہر ایک کے شکریہ کا قبول کرنے والا اور ہر ایک کی نیت کا جاننے والا ہے'),
(641, 4, 148, 'اللہ مظلوم کے علاوہ کسی کی طرف سے بھی علی الاعلان اِرا کہنے کو پسند نہیں کرتا اور اللہ ہر بات کا سننے والا اور تمام حالات کا جاننے والا ہے'),
(642, 4, 149, 'تم کسی خیر کا اظہار کرو یا اسے مخفی رکھو یا کسی برائی سے درگزر کرو تو اللہ گناہوں کا معاف کرنے والا اور صاحبِ اختیار ہے'),
(643, 4, 150, 'بے شک جو لوگ اللہ اور رسول کا انکار کرتے ہیں اور خدا اور رسول کے درمیان تفرقہ پیدا کرنا چاہتے ہیں اور یہ کہتے ہیں کہ ہم بعض پر ایمان لائیں گے اور بعض کا انکار کریں گے اور چاہتے ہیں کہ ایمان و کفر کے درمیان سے کوئی نیا راستہ نکال لیں'),
(644, 4, 151, 'تو درحقیقت یہی لوگ کافر ہیں اور ہم نے کافروں کے لئے بڑا رسوا کن عذاب مہّیا کررکھا ہے'),
(645, 4, 152, 'اور جو لوگ اللہ اور رسول پر ایمان لے آئے اور رسولوں کے درمیان تفرقہ نہیں پیدا کیا خدا عنقریب انہیں ان کا اجر عطا کرے گا اور وہ بہت زیادہ بخشنے والا اور مہربانی کرنے والا ہے'),
(646, 4, 153, 'پیغمبر یہ اہل هکتاب آپ سے مطالبہ کرتے ہیں کہ ان پر آسمان سے کوئی کتاب نازل کردیجئے تو انہوں نے موسٰی سے اس سے زیادہ سنگین سوال کیا تھا جب ان سے یہ کہا کہ ہمیں اللہ کو علی الاعلان دکھلا دیجئے تو ان کے ظلم کی بنا پر انہیں ایک بجلی نے اپنی گرفت میں لے لیا پھر انہوں نے ہماری نشانیوں کے آجانے کے باوجود گوسالہ بنالیا تو ہم نے اس سے بھی درگزر کیا اور موسٰی علیھ السّلام کو کھلی ہوئی دلیل عطا کردی'),
(647, 4, 154, 'اور ہم نے ان کے عہد کی خلاف ورزی کی بنا پر ان کے سروں پر کوئہ طور کو بلند کردیا اور ان سے کہا کہ دروازہ سے سجدہ کرتے ہوئے داخل ہو اور ان سے کہا کہ خبردار شنبہ کے معاملہ میں زیادتی نہ کرنا اور ان سے بڑا سخت عہد لے لیا'),
(648, 4, 155, 'پس ان کے عہد کو توڑ دینے ,آیات هخدا کے انکار کرنے اور انبیائ کو ناحق قتل کر دینے اور یہ کہنے کی بنا پر کہ ہمارے دلوں پر فطرتا غلاف چڑھے ہوئے ہیں حالانکہ ایسا نہیں ہے بلکہ خدا نے ان کے کفر کی بنا پر ان کے دلوں پر مہر لگادی ہے اور اب چند ایک کے علاوہ کوئی ایمان نہ لائے گا'),
(649, 4, 156, 'اور ان کے کفر اور مریم پر عظیم بہتان لگانے کی بنا پر'),
(650, 4, 157, 'اور یہ کہنے کی بنا پر کہ ہم نے مسیح عیسٰی علیھ السّلام بن مریم رسول اللہ کو قتل کردیا ہے حالانکہ انہوں نے نہ انہیں قتل کیا ہے اور نہ سولی دی ہے بلکہ دوسرے کو ان کی شبہیہ بنا دیا گیا تھا اور جن لوگوں نے عیسٰی علیھ السّلام کے بارے میں اختلاف کیا ہے وہ سب منزل ه شک میں ہیں اور کسی کو گمان کی پیروی کے علاوہ کوئی علم نہیں ہے اور انہوں نے یقینا انہیں قتل نہیں کیا ہے'),
(651, 4, 158, 'بلکہ خدا نے اپنی طرف اٹھا لیا ہے اور خدا صاحبِ غلبہ اور صاحب هحکمت بھی ہے'),
(652, 4, 159, 'اور کوئی اہل کتاب میں ایسا نہیں ہے جو اپنی موت سے پہلے ان پر ایمان نہ لائے اور قیامت کے دن عیسٰی علیھ السّلام اس کے گواہ ہوں گے'),
(653, 4, 160, 'پس ان یہودیوں کے ظلم کی بنا پر ہم نے جن پاکیزہ چیزوں کو حلال کر رکھا تھا ان پر حرام کردیا اور ان کے بہت سے لوگوں کو راسِ خدا سے روکنے کی بنا پر'),
(654, 4, 161, 'اور سود لینے کی بنا پر جس سے انہیں روکا گیا تھا اور ناجائز طریقہ سے لوگوں کا مال کھانے کی بنا پر اور ہم نے کافروں کے لئے بڑا دردناک عذاب مہّیا کیا ہے'),
(655, 4, 162, 'لیکن ان میں سے جو لوگ علم میں رسوخ رکھنے والے اور ایمان لانے والے ہیں وہ ان سب پر ایمان رکھتے ہیں جو تم پر نازل ہوا ہے یا تم سے پہلے نازل ہوچکا ہے اور بالخصوص نماز قائم کرنے والے اور زکوِٰدینے والے اور آخرت پر ایمان رکھنے والے ہیں ہم عنقریب ان سب کو اج» عظیم عطا کریں گے'),
(656, 4, 163, 'ہم نے آپ کی طرف اسی طرح وحی نازل کی ہے جس طرح نوح علیھ السّلاماور ان کے بعد کے انبیائ کی طرف وحی کی تھی اور ابراہیم علیھ السّلام,اسماعیل علیھ السّلام,اسحاق علیھ السّلام,یعقوب علیھ السّلام,اسباط علیھ السّلام,عیسٰی علیھ السّلام ,ایوب علیھ السّلام, یونس علیھ السّلام,ہارون علیھ السّلام اور سلیمان علیھ السّلام کی طرف وحی کی ہے اور داؤد علیھ السّلام کو زبور عطا کی ہے'),
(657, 4, 164, 'کچھ رسول ہیں جن کے قصے ّہم آپ سے بیان کرچکے ہیں اور کچھ رسول ہیں جن کا تذکرہ ہم نے نہیں کیا ہے اور اللہ نے موسٰی علیھ السّلام سے باقاعدہ گفتگو کی ہے'),
(658, 4, 165, 'یہ سارے رسول بشارت دینے والے اور ڈرانے والے اس لئے بھیجے گئے تاکہ رسولوں کے آنے کے بعد انسانوں کی حجت خدا پر قائم نہ ہونے پائے اور خدا سب پر غالب اور صاحب ه حکمت ہے'),
(659, 4, 166, 'یہ مانیں یا نہ مانیں)لیکن خدا نے جو کچھ آپ پر نازل کیا ہے وہ خود اس کی گواہی دیتا ہے کہ اس نے اسے اپنے علم سے نازل کیا ہے اور ملائکہ بھی گواہی دیتے ہیں اور خدا خود بھی شہادت کے لئے کافی ہے'),
(660, 4, 167, 'بے شک جن لوگوں نے کفر اختیار کیا اور راسِ هخد ا سے منع کردیا وہ گمراہی میں بہت دور تک چلے گئے ہیں'),
(661, 4, 168, 'اورجن لوگوں نے کفر اختیار کرنے کے بعد ضِلم کیا ہے خدا انہیں ہرگز معاف نہیں کرسکتا اور نہ انہیں کسی راستہ کی ہدایت کرسکتا ہے'),
(662, 4, 169, 'سوائے جہّنم کے راستے کے جہاں ان کو ہمیشہ ہمیشہ رہنا ہے اور یہ خدا کے لئے بہت آسان ہے'),
(663, 4, 170, 'اے انسانو!تمہارے پاس پروردگار کی طرف سے حق لے کر رسول آگیا ہے لہذا اس پر ایمان لے آؤ اسی میں تمہاری بھلائی ہے اور اگر تم نے کفر اختیار کیا تو یاد رکھو کہ زمین و آسمان کی کل کائنات خدا کے لئے ہے اور وہی علم والا بھی ہے اور حکمت والا بھی ہے'),
(664, 4, 171, 'اے اہل کتاب اپنے دین میں حد سے تجاوز نہ کرو اور خدا کے بارے میں حق کے علاوہ کچھ نہ کہو ---مسیح عیسٰی علیھ السّلام بن مریم صرف اللہ کے رسول اور اس کا کلمہ ہیں جسے مریم کی طرف القائ کیا گیا ہے اور وہ اس کی طرف سے ایک روح ہیں لہذا خدا اور مرسلین پر ایمان لے آؤ اور خبردار تین کا نام بھی نہ لو -اس سے باز آجاؤ کہ اسی میں بھلائی ہے -اللہ فقط خدائے واحد ہے اور وہ اس بات سے منزہ ہے کہ اس کا کوئی بیٹا ہو ,زمین و آسمان میں جو کچھ بھی ہے سب اسی کا ہے اور وہ کفالت و وکالت کے لئے کافی ہے'),
(665, 4, 172, 'نہ مسیح کو اس بات سے انکار ہے کہ وہ بندئہ خدا ہیں اور نہ ملائکہ مقربین کو اس کی بندگی سے کوئی انکار ہے اور جو بھی اس کی بندگی سے انکار و استکبار کرے گا تو اللہ سب کو اپنی بارگاہ میں محشور کرے گا'),
(666, 4, 173, 'پھر جو لوگ ایمان لے آئے اور انہوں نے نیک اعمال کئے انہیں مکمل اجر دے گا اور اپنے فضل و کرم سے اضافہ بھی کردے گا اور جن لوگوں نے انکار اور تکّبرسے کام لیا ہے ان پر دردناک عذاب کرے گا اور انہیں خدا کے علاوہ نہ کوئی سرپرست ملے گا اور نہ مددگار'),
(667, 4, 174, 'اے انسانو !تمہارے پاس تمہارے پروردگار کی طرف سے برہان آچکا ہے اور ہم نے تمہاری طرف روشن نور بھی نازل کردیا ہے'),
(668, 4, 175, 'پس جو لوگ اللہ پر ایمان لائے اور اس سے وابستہ ہوئے انہیں وہ اپنی رحمت اور اپنے فضل میں داخل کرلے گا اور سیدھے راستہ کی ہدایت کردے گا'),
(669, 4, 176, 'پیغمبر یہ لوگ آپ سے فتوٰی دریافت کرتے ہیں تو آپ کہہ دیجئے کہ کلالہ (بھائی بہن)کے بارے میں خدا خود یہ حکم بیان کرتا ہے کہ اگر کوئی شخص مر جائے اور اس کی اولاد نہ ہو اور صرف بہن وارث ہو تو اسے ترکہ کا نصف ملے گا اور اسی طرح اگر بہن مر جائے اور اس کی اولاد نہ ہو تو بھائی اس کا وارث ہوگا -پھر اگر وارث دو بہنیں ہیں تو انہیں ترکہ کا دو تہائی ملے گا اور اگر بھائی بہن دونوں ہیں تو مرد کے لئے عورت کا رَہرا حصّہ ہوگا خدا یہ سب واضح کررہا ہے تاکہ تم بہکنے نہ پاؤ اور خدا ہر شے کا خوب جاننے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'ایمان والو اپنے عہد و پیمان اور معاملات کی پابندی کرو تمہارے لئے تمام چوپائے حلال کردیئے گئے ہیں علاوہ ان کے جوتمہیں پڑھ کر سنائے جارہے ہیں. مگر حالاُ احرام میں شکار کو حلال مت سمجھ لینا بے شک اللہ جو چاہتا حکم دیتا ہے'),
(671, 5, 2, 'ایمان والو !خبردار خدا کی نشانیوں کی حرمت کو ضائع نہ کرنا اور نہ محترم مہینے. قربانی کے جانور اور جن جانوروں کے گلے میں پٹےّ باندھ دیئے گئے ہیں اور جو لوگ خانہ خدا کا ارادہ کرنے والے ہیں اور فرض پروردگار اور رضائے الٰہی کے طلبگار ہیں ان کی حرمت کی خلاف ورزی کرنا اور جب احرام ختم ہوجائے تو شکار کرو اور خبردار کسی قوم کی عداوت فقط اس بات پر کہ اس نے تمہیں مسجدالحرام سے روک دیا ہے تمہیں ظلم پر آمادہ نہ کردے --نیکی اور تقویٰ پر ایک دوسرے کی مدد کرو اور گناہ اور تعدّی پر آپس میں تعاون نہ کرنا اور اللہ سے ڈرتے رہنا کہ اس کا عذاب بہت سخت ہے'),
(672, 5, 3, 'تمہارے اوپر حرام کردیا گیا ہے مردار -خون - سور کا گوشت اور جو جانور غیر خدا کے نام پر ذبح کیا جائے اورمنخنقہ اور موقوذہً اور متردیہ اور نطیحہ اور جس کو درندہ کھا جائے مگریہ کہ تم خود ذبح کرلو اور جو نصاب پر ذبح کیا جائے اور جس کی تیروں کے ذریعہ قرعہ اندازی کرو کہ یہ سب فسق ہے اور کفار تمہارے دین سے مایوس ہوگئے ہیں لہذا تم ان سے نہ ڈرو اور مجھ سے ڈرو -آج میں نے تمہارے لئے دین کو کامل کردیا ہے اور اپنی نعمتوں کو تمام کردیا ہے اور تمہارے لئے دین اسلام کو پسندیدہ بنادیا ہے لہذا جو شخص بھوک میں مجبور ہوجائے اور گناہ کی طرف مائل نہ ہو تو خدا بڑا بخشنے والا مہربان ہے'),
(673, 5, 4, 'پیغمبر یہ تم سے سوال کرتے ہیں کہ ان کے لئے کیا حلال کیا گیا ہے تو کہہ دیجئے کہ تمہارے لئے تمام پاکیزہ چیزیں حلال ہیں اور جو کچھ تم نے شکاری کتوں کو سکھا رکھا ہے اور خدائی تعلیم میں سے کچھ ان کے حوالہ کر دیاہے تو جو کچھ وہ پکڑ کے لائیں اسے کھالو اور اس پر نام خدا ضرور لو اور اللہ سے ڈرو کہ وہ بہت جلد حساب کرنے والا ہے'),
(674, 5, 5, 'آج تمہارے لئے تمام پاکیزہ چیزیں حلال کردی گئی ہیں اور اہلِ کتاب کا طعام بھی تمہارے لئے حلال ہے اور تمہارا طعام ان کے لئے حلال ہے اور اہلِ ایمان کی آزاد اور پاک دامن عورتیں یا ان کی آزاد عورتیں جن کو تم سے پہلے کتاب دی گئی ہے تمہارے لئے حلال ہے بشرطیکہ تم ان کی اجرت دے دو پاکیزگی کے ساتھ -نہ کھّلم کھلاّ زنا کی اجرت کے طور پر اور نہ پوشیدہ طور پر دوستی کے انداز سے اور جو بھی ایمان سے انکار کرے گا اس کے اعمال یقینا برباد ہوجائیں گے اور وہ آخرت میں گھاٹا اُٹھانے والوں میں ہوگا'),
(675, 5, 6, 'ایمان والو جب بھی نماز کے لئے اٹھو تو پہلے اپنے چہروں کو اور کہنیوں تک اپنے ہاتھوںکو دھوؤ اور اپنے سر اور گٹّے تک پیروں کا مسح کرو اور اگر جنابت کی حالت میں ہو تو غسل کرو اور اگر مریض ہو یا سفر کے عالم میں ہو یا پیخانہ وغیرہ نکل آیا ہے یا عورتوں کو باہم لمس کیاہے اور پانی نہ ملے تو پاک مٹی سے تیمم کرلو -اس طرح کہ اپنے چہرے اور ہاتھوں کا مسح کرلو کہ خدا تمہارے لئے کسی طرح کی زحمت نہیں چاہتا. بلکہ یہ چاہتا ہے کہ تمہیں پاک و پاکیزہ بنا دے اور تم پر اپنی نعمت کو تمام کردے شاید تم اس طرح اس کے شکر گزار بندے بن جاؤ'),
(676, 5, 7, 'اور اپنے اوپر اللہ کی نعمت اور اس کے اس عہد کو یاد کرو جو اس نے تم سے لیاہے جب تم نے یہ کہاکہ ہم نے سن لیا اور اطاعت کی اور خبردار اللہ سے ڈرتے رہو کہ اللہ دل کے رازوں کا بھی جاننے والا ہے'),
(677, 5, 8, 'ایمان والو خدا کے لئے قیام کرنے والے اور انصاف کے ساتھ گواہی دینے والے بنو اور خبردار کسی قوم کی عداوت تمہیں اس بات پر آمادہ نہ کردے کہ انصاف کو ترک کردو -انصاف کرو کہ یہی تقوٰی سے قریب تر ہے اور اللرُسے ڈرتے رہو کہ اللہ تمہارے اعمال سے خوب باخبر ہے'),
(678, 5, 9, 'اللہ نے صاحبان ایمان اور عمل صالح والوں سے وعدہ کیا ہے کہ ان کے لئے مغفرت اور اج» عظیم ہے'),
(679, 5, 10, 'اور جن لوگوں نے کفر اختیار کیا اور ہماری آیات کی تکذیب کی وہ سب کے سب جہّنمی ہیں'),
(680, 5, 11, 'ایمان والو! اپنے اوپر اللہ کی اس نعمت کو یاد کرو کہ جب ایک قوم نے تمہاری طرف ہاتھ بڑھانے کا ارادہ کیاتو خدا نے ان کے ہاتھوں کو تم تک پہنچنے سے روک دیا اور اللہ سے ڈرتے رہو کہ صاحبان هایمان اللہ ہی پر بھروسہ کرتے ہیں'),
(681, 5, 12, 'اور بے شک اللہ نے بنی اسرائیل سے عہد لیا اور ہم نے ان میں سے بارہ نقیب بھیجے اور خدا نے یہ بھی کہہ دیاکہ ہم تمہارے ساتھ ہیں اگر تم نے نماز قائم کی اور زکوِٰ ادا کی اور ہمارے رسولوں پر ایمان لے آئے اور ان کی مدد کی اور خدا کی راہ میں قرض حسنہ دیاتو ہم یقینا تمہاری برائیوں سے درگزر کریں گے اور تمہیں ان باغات میں داخل کریں گے جن کے نیچے نہریں جاری ہوں گی -پھر جو اس کے بعد کفر کرے گا تو وہ بہت اِرے راستہ کی طرف بہک گیا ہے'),
(682, 5, 13, 'پھر ان کی عہد شکنی کی بنا پر ہم نے ان پر لعنت کی اور ان کے دلوں کو سخت بنا دیا وہ ہمارے کلمات کو ان کی جگہ سے ہٹا دیتے ہیں اور انہوں نے ہماری یاددہانی کا اکثر حصّہ فراموش کردیا ہے اور تم ان کی خیانتوں پر برابر مطلع ہوتے رہوگے علاوہ چند افراد کے لہذا ان سے درگزر کرو اور ان کی طرف سے کنارہ کشی کرو کہ اللہ احسان کرنے والوں کو دوست رکھتا ہے'),
(683, 5, 14, 'اور جن لوگوں کا کہنا ہے کہ ہم نصرانی ہیں ہم نے ان سے بھی عہد لیا اور انہوں نے بھی ہماری نصیحت (انجیل)کے ایک حصّہ کو فراموش کردیا تو ہم نے بطور سزا ان کے درمیان عداوت اور بغض کو قیامت تک کے لئے راستہ دے دیا اور عنقریب خدا انہیں ان باتوں سے باخبر کرے گا جو وہ انجام دے رہے تھے'),
(684, 5, 15, 'اے اہل کتاب تمہارے پاس ہمارا رسول آچکا ہے جو اُن میں سے بہت سی باتوں کی وضاحت کررہا ہے جن کو تم کتاب هخدا میں سے چھاَپا رہے تھے اور بہت سی باتوں سے درگزر بھی کرتا ہے تمہارے پاس خدا کی طرف سے نور اور روشن کتاب آچکی ہے'),
(685, 5, 16, 'جس کے ذریعہ خدا اپنی خوشنودی کا اتباع کرنے والوں کو سلامتی کے راستوں کی ہدایت کرتا ہے اور انہیں تاریکیوں سے نکال کر اپنے حکم سے نور کی طرف لے آتا ہے اور انہیں صراط مستقیم کی ہدایت کرتا ہے'),
(686, 5, 17, 'یقینا وہ لوگ کافر ہیں جن کا کہنا یہ ہے کہ مسیح علیھ السّلامابن مریم ہی اللہ ہیں -پیغمبر آپ ان سے کہئے کہ پھر خدا کے مقابلہ میں کون کسی امر کا صاحبِ اختیار ہوگا اگر وہ مسیح علیھ السّلام بن مریم اور ان کی ماں اور سارے اہل زمین کو مار ڈالنا چاہے اور اللہ ہی کے لئے زمین و آسمان اور ان کے درمیان کی کل حکومت ہے -وہ جسے بھی چاہتا ہے پیدا کرتا ہے اور وہ ہر شے پر قدرت رکھنے والا ہے'),
(687, 5, 18, 'اور یہودیوں نے یہ کہنا شروع کردیا کہ ہم اللہ کے فرزند اور اس کے دوست ہیں تو پیغمبر آپ کہہ دیجئے کہ پھر خدا تم پر تمہارے گناہوں کی بنا پر عذاب کیوںکرتا ہے ... بلکہ تم اس کی مخلوقات میں سے بشر ہو اور وہ جس کو چاہتا ہے بخش دیتا ہے اور جس پر چاہتا ہے عذاب کرتا ہے اور اس کے لئے زمین و آسمان اور ان کے درمیان کی کل کائنات ہے اور اسی کی طرف سب کی بازگشت ہے'),
(688, 5, 19, 'اے اہلِ کتاب تمہارے پاس رسولوں کے ایک وقفہ کے بعد ہمارا یہ رسول آیا ہے کہ تم یہ نہ کہو کہ ہمارے پاس کوئی بشیر و نذیر نہیں آیا تھا تو لو یہ بشیر و نذیر آگیا ہے اور خدا ہر شے پر قادر ہے'),
(689, 5, 20, 'اور اس وقت کو یاد کرو جب موسٰی نے اپنی قوم سے کہا کہ اے قوم اپنے اوپر اللہ کی نعمت کو یاد کرو جب اس نے تم میں انبیائ قرار دئیے اور تمہیں بادشاہ بنایا اور تمہیں وہ سب کچھ دے دیا جو عالمین میں کسی کو نہیں دیا تھا'),
(690, 5, 21, 'اور اے قوم اس ارعَ مقدس میں داخل ہوجاؤ جسے اللہ نے تمہارے لئے لکھ دیا ہے اور میدان سے اُلٹے پاؤں نہ پلٹ جاؤ کہ اُلٹے خسارہ والوں میں سے ہوجاؤ گے'),
(691, 5, 22, 'ان لوگوں نے کہا کہ موسٰی علیھ السّلام وہاں تو جابروں کی قوم آباد ہے اور ہم اس وقت تک داخل نہ ہوں گے جب تک وہ وہاں سے نکل نہ جائیں پھر اگر وہ نکل گئے تو ہم یقینا داخل ہوجائیں گے'),
(692, 5, 23, 'مگر دو انسان جنہیں خدا کا خوف تھا اور ان پر خدا نے فضل و کرم کیا تھا انہوں نے کہا کہ ان کے دروازے سے داخل ہوجاؤ -اگر تم دروازے میں داخل ہوگئے تو یقینا غالب آجاؤ گے اور اللہ پر بھروسہ کرو اگر تم صاحبانِ ایمان ہو'),
(693, 5, 24, 'ان لوگوں نے کہا کہ موسٰی علیھ السّلام ہم ہرگز وہاں داخل نہ ہوں گے جب تک وہ لوگ وہاں ہیں -آپ اپنے پروردگار کے ساتھ جاکر جنگ کیجئے ہم یہاں بیٹھے ہوئے ہیں'),
(694, 5, 25, 'موسٰی علیھ السّلامنے کہا پروردگار میں صرف اپنی ذات اور اپنے بھائی کا اختیار رکھتا ہوں لہذا ہمارے اور اس فاسق قوم کے درمیان جدائی پیدا کردے'),
(695, 5, 26, 'ارشاد ہوا کہ اب ان پر چالیس سال حرام کردئیے گئے کہ یہ زمین میں چکرّ لگاتے رہیں گے لہذا تم اس فاسق قوم پر افسوس نہ کرو'),
(696, 5, 27, 'اور پیغمبر علیھ السّلامآپ ان کو آدم علیھ السّلامکے دونوں فرزندوں کا سچا ّقصّہ پڑھ کر سنائیے کہ جب دونوں نے قربانی دی اور ایک کی قربانی قبول ہوگئی اور دوسرے کی نہ ہوئی تو اس نے کہا کہ میں تجھے قتل کردوں گا تو دوسرے نے جواب دیا کہ میرا کیا قصور ہے خدا صرف صاحبان ه تقوٰی کے اعمال کو قبول کرتا ہے'),
(697, 5, 28, 'اگر آپ میری طرف قتل کے لئے ہاتھ بڑھائیں گے بھی تو میں آپ کی طرف ہرگز ہاتھ نہ بڑھاؤں گا کہ میں عالمین کے پالنے والے خدا سے ڈرتا ہوں'),
(698, 5, 29, 'میں چاہتا ہوں کہ آپ میرے اور اپنے دونوں کے گناہوں کا مرکز بن جائیے اور جہنمیوں میں شامل ہوجائیے کہ ظالمین کی یہی سزا ہے'),
(699, 5, 30, 'پھر اس کے نفس نے اسے بھائی کے قتل پر آمادہ کردیا اور اس نے اسے قتل کردیا اور وہ خسارہ والوں میں شامل ہوگیا'),
(700, 5, 31, 'پھر خدا نے ایک کوّا بھیجا جو زمین کھود رہا تھا کہ اسے دکھلائے کہ بھائی کی لاش کو کس طرح چھپائے گا تو اس نے کہا کہ افسوس میں اس کوّے کے جیسا بھی نہ ہوسکا کہ اپنے بھائی کی لاش کو زمین میں چھاَپا دیتا اور اس طرح وہ نادمین اور پشیمان لوگوں میں شامل ہوگیا'),
(701, 5, 32, 'اسی بنا پر ہم نے بنی اسرائیل پر لکھ دیا کہ جو شخص کسی نفس کو .... کسی نفس کے بدلے یا روئے زمین میں فساد کی سزا کے علاوہ قتل کرڈالے گا اس نے گویا سارے انسانوں کو قتل کردیا اور جس نے ایک نفس کو زندگی دے دی اس نے گویا سارے انسانوں کو زندگی دے دی اور بنی اسرائیل کے پاس ہمارے رسول کھلی ہوئی نشانیاں لے کر آئے مگر اس کے بعد بھی ان میں کے اکثر لوگ زمین میں زیادتیاں کرنے والے ہی رہے'),
(702, 5, 33, 'بس خدا و رسول سے جنگ کرنے والے اور زمین میں فساد کرنے والوں کی سزا یہی ہے کہ انہیں قتل کردیا جائے یا سولی پر چڑھا دیا جائے یا ان کے ہاتھ اور پیر مختلف سمت سے قطع کردئیے جائیں یا انہیں ارض ه وطن سے نکال باہر کیا جائے .یہ ان کے لئے دنیا میں رسوائی ہے اور ان کے لئے آخرت میں عذاب هعظیم ہے'),
(703, 5, 34, 'علاوہ ان لوگوں کے جو تمہارے قابو میں آنے سے پہلے ہی توبہ کرلیں تو سمجھ لو کہ خدا بڑا بخشنے والا مہربان ہے'),
(704, 5, 35, 'ایمان والو اللہ سے ڈرو اور اس تک پہنچے کا وسیلہ تلاش کرو اوراس کی راہ میں جہاد کرو کہ شاید اس طرح کامیاب ہوجاؤ'),
(705, 5, 36, 'یقینا جن لوگوں نے کفر اختیار کیا اگر ان کے پاس ساری زمین کا سرمایہ ہو اور اتنا ہی اور شامل کردیں کہ روز هقیامت کے عذاب کا بدلہ ہوجائے تو یہ معاوضہ قبول نہ کیا جائے گا اور ان کے لئے دردناک عذاب ہوگا'),
(706, 5, 37, 'یہ لوگ چاہتے ہیں جہّنم سے نکل جائیں حالانکہ یہ نکلنے والے نہیں ہیں اور ان کے لئے ایک مستقل عذاب ہے'),
(707, 5, 38, 'چور مرد اور چور عورت دونوں کے ہاتھ کاٹ دو کہ یہ ان کے لئے بدلہ اور خدا کی طرف سے ایک سزا ہے اور خدا صاحبِ عزّت بھی ہے اور صاحبِ حکمت بھی ہے'),
(708, 5, 39, 'پھر ظلم کے بعد جو شخص توبہ کرلے اور اپنی اصلاح کرلے تو خدا اس کی توبہ قبول کرلے گا کہ اللہ بڑا بخشنے والا اور مہربان ہے'),
(709, 5, 40, 'کیا تم کو نہیں معلوم کہ زمین و آسمان کا ملک صرف خدا ہی کے لئے ہے وہ جس پر چاہتا ہے عذاب کرتا ہے اور جس کو چاہتا ہے بخش دیتا ہے اور اللہ ہر شے پر قادر و مختار ہے'),
(710, 5, 41, 'اے رسول ایمان کا دعویٰ کرنے والوں میں جو لوگ کفر کی طرف جلد بازی سے بڑھ رہے ہیں ان کی حرکات سے آپ رنجیدہ نہ ہوں -یہ صرف زبان سے ایمان کا نام لیتے ہیں اور ان کے دل مومن نہیں ہیں اور یہودیوں میں سے بھی بعض ایسے ہیں جو جھوٹی باتیں سنتے ہیں اور دوسری قوم والے جو آپ کے پاس حاضر نہیں ہوئے انہیں سناتے ہیں - یہ کلمات کو ان کی جگہ سے ہٹا دیتے ہیں اور لوگوں سے کہتے ہیں کہ اگر پیغمبر کی طرف سے یہی دیا جائے تو لے لینا اور اگر یہ نہ دیا جائے تو پرہیز کرنا اور جس کے فتنہ کے بارے میں خدا ارادہ کرلے اس کے بارے میں آپ کا کوئی اختیار نہیں ہے یہ وہ افراد ہیں جن کے بارے میں خدا نے یہ ارادہ ہی نہیں کیا کہ زبردستی ان کے دلوں کو پاک کردے گا -ان کے لئے دنیا میں بھی رسوائی ہے اور آخرت میں بھی عذابِ عظیم ہے'),
(711, 5, 42, 'یہ جھوٹ کے سننے والے اور حرام کے کھانے والے ہیں لہذا اگر آپ کے پاس مقدمہ لے کر آئیں تو آپ کو اختیار ہے کہ فیصلہ کردیں یا اعراض کرلیں کہ اگر اعراض بھی کرلیں گے تو یہ آپ کو کوئی نقصان نہ پہنچا سکیں گے لیکن اگر فیصلہ ہی کریں تو انصاف کے ساتھ کریں کہ خدا انصاف کرنے والوں کو دوست رکھتا ہے'),
(712, 5, 43, 'اور یہ کس طرح آپ سے فیصلہ کرائیں گے جب کہ ان کے پاس توریت موجود ہے جس میں حکهِ خدا بھی ہے اور یہ اس کے بعد بھی منہ پھیر لیتے ہیں اور پھر ایمان لانے والے نہیں ہیں'),
(713, 5, 44, 'بیشک ہم نے توریت کو نازل کیا ہے جس میں ہدایت اور نور ہے اور اس کے ذریعہ اطاعت گزار انبیائ یہودیوں کے لئے فیصلہ کرتے ہیں اور اللہ والے اور علمائے یہود اس چیز سے فیصلہ کرتے ہیں جس کا کتاب هخدا میں ان کو محافظ بنایا گیا ہے اور جس کے یہ گواہ بھی ہیں لہذا تم ان لوگوں سے نہ ڈرو صرف ہم سے ڈرو اور خبردار تھوڑی سی قیمت کے لئے ہماری آیات کا کاروبار نہ کرنا اور جو بھی ہمارے نازل کئے ہوئے قانون کے مطابق فیصلہ نہ کرے گا وہ سب کافر شمار ہوں گے'),
(714, 5, 45, 'اور ہم نے توریت میں یہ بھی لکھ دیا ہے کہ جان کا بدلہ جان اور آنکھ کا بدلہ آنکھ اور ناک کا بدلہ ناک اور کان کا بدلہ کان اور دانت کا بدلہ دانت ہے اور زخموں کا بھی بدلہ لیا جائے گا اب اگر کوئی شخص معاف کردے تو یہ اس کے گناہوں کا بھی کفارہ ہوجائے گا اور جو بھی خدا کے نازل کردہ حکم کے خلاف فیصلہ کرے گا وہ ظالموں میں سے شمار ہوگا'),
(715, 5, 46, 'اور ہم نے ان ہی انبیائ کے نقشِ قدم پر عیسٰی علیھ السّلامبن مریم کو چلادیا جو اپنے سامنے کی توریت کی تصدیق کرنے والے تھے اور ہم نے انہیں انجیل دے دی جس میں ہدایت اور نور تھا اور وہ اپنے سامنے کی توریت کی تصدیق کرنے والی اور ہدایت تھی اور صاحبانِ تقوٰی کے لئے سامانِ نصیحت تھی'),
(716, 5, 47, 'اہلِ انجیل کو چاہئے کہ خدا نے جو حکم نازل کیا ہے اس کے مطابق فیصلہ کریں کہ جو بھی تنزیل هخدا کے مطابق فیصلہ نہ کرے گا وہ فاسقوں میں شمار ہوگا'),
(717, 5, 48, 'اور اے پیغمبر ہم نے آپ کی طرف کتاب نازل کی ہے جو اپنے پہلے کی توریت اور انجیل کی لَصدق اور محافظ ہے لہذا آپ ان کے درمیان تنزیل خدا کے مطابق فیصلہ کریں اور خدا کی طرف سے آئے ہوئے حق سے الگ ہوکر ان کی خواہشات کا اتباع نہ کریں .ہم نے سب کے لئے الگ الگ شریعت اور راستہ مقرر کردیا ہے اور خدا چاہتا تو سب کو ایک ہی امت بنا دیتا لیکن وہ اپنے دئیے ہوئے قانون سے تمہاری آزمائش کرنا چاہتا ہے لہذا تم سب نیکیوں کی طرف سبقت کرو کہ تم سب کی بازگشت اللہ ہی کی طرف ہے -وہاں وہ تمہیں ان تمام باتوں سے باخبر کرے گا جن میں تم اختلاف کررہے تھے'),
(718, 5, 49, 'اور پیغمبر آپ ان کے درمیان تنزیلِ خدا کے مطابق فیصلہ کریں اور ان کی خواہشات کا اتباع نہ کریں اور اس بات سے بچتے رہیں کہ یہ بعض احکام الٰہی سے منحرف کردیں -پھر اگر یہ خود منحرف ہوجائیں تو یاد رکھیں کہ خدا ان کو ان کے بعض گناہوں کی مصیبت میں مبتلا کرنا چاہتا ہے اور انسانوں کی اکثریت فاسق اور دائرسِ اطاعت سے خارج ہے'),
(719, 5, 50, 'کیا یہ لوگ جاہلیت کا حکم چاہتے ہیں جب کہ صاحبانِ یقین کے لئے اللہ کے فیصلہ سے بہتر کس کا فیصلہ ہوسکتا ہے'),
(720, 5, 51, 'ایمان والو یہودیوں اور عیسائیوں کو اپنا دوست اور سرپرست نہ بناؤ کہ یہ خود آپس میں ایک دوسرے کے دوست ہیں اور تم میں سے کوئی انہیں دوست بنائے گا تو ان ہی میں شمار ہوجائے گا بیشک اللہ ظالم قوم کی ہدایت نہیں کرتا ہے'),
(721, 5, 52, 'پیغمبر آپ دیکھیں گے کہ جن کے دلوں میں نفاق کی بیماری ہے وہ دوڑ دوڑ کر ان کی طرف جارہے ہیں اور یہ عذر بیان کرتے ہیں کہ ہمیں گردشِ زمانہ کا خوف ہے -پس عنقریب خدا اپنی طرف سے فتح یا کوئی دوسرا امر لے آئے گا تو یہ اپنے دل کے چھپائے ہوئے راز پر پشیمان ہوجائیں گے'),
(722, 5, 53, 'اور صاحبان هایمان کہیں گے کہ کیا یہی لوگ ہیں جنہوں نے نام ه خدا پر سخت ترین قسمیں کھائی تھیں کہ ہم تمہارے ساتھ ہیں تو اب ان کے اعمال برباد ہوگئے اور یہ خسارہ والے ہوگئے'),
(723, 5, 54, 'ایمان والو تم میں سے جو بھی اپنے دین سے پلٹ جائے گا ...تو عنقریب خدا ایک قوم کو لے آئے گا جو اس کی محبوب اور اس سے محبت کرنے والی مومنین کے سامنے خاکسار اور کفار کے سامنے صاحبِ عزت, راسِ خدا میں جہاد کرنے والی اور کسی ملامت کرنے والے کی ملامت کی پرواہ نہ کرنے والی ہوگی -یہ فضلِ خدا ہے وہ جسے چاہتا ہے عطا کرتا ہے اور وہ صاحبِ وسعت اور علیم و دانا بھی ہے'),
(724, 5, 55, 'ایمان والو بس تمہارا ولی اللہ ہے اور اس کا رسول اور وہ صاحبانِ ایمان جو نماز قائم کرتے ہیں اور حالت رکوع میں زکوِٰ دیتے ہیں'),
(725, 5, 56, 'اور جو بھی اللہ ,رسول اور صاحبانِ ایمان کو اپنا سرپرست بنائے گا تو اللہ کی ہی جماعت غالب آنے والی ہے'),
(726, 5, 57, 'ایمان والو خبردار اہل ه کتاب میں جن لوگوں نے تمہارے دین کو مذاق اور تماشہ بنالیا ہے اور دیگر کفار کو بھی اپنا ولی اور سرپرست نہ بنانا اور اللہ سے ڈرتے رہنا اگر تم واقعی صاحب ایمان ہو'),
(727, 5, 58, 'اور جب تم نماز کے لئے اذان دیتے ہو تو یہ اس کو مذا ق اور کھیل بنالیتے ہیں اس لئے کہ یہ بالکل بے عقل قوم ہیں'),
(728, 5, 59, 'پیغمبر آپ کہہ دیجئے کہ اے اہلِ کتاب کیا تم ہم سے صرف اس بات پر ناراض ہوکہ ہم اللہ اور اس نے جو کچھ ہماری طرف یا ہم سے پہلے نازل کیا ہے ان سب پر ایمان رکھتے ہیں اور تمہاری اکثریت فاسق اور نافرمان ہے'),
(729, 5, 60, 'کیا ہم تم کو خدا کے نزدیک ایک منزل کے اعتبار سے اس سے بدتر عیب کی خبر دیں کہ جس پر خد انے لعنت اور غضب نازل کیا ہے اوران میں سے بعض کو بندر اور سور بنادیا ہے اور جس نے بھی طاغوت کی عبادت کی ہے وہ جگہ کے اعتبار سے بدترین اور سیدھے راستہ سے انتہائی بہکا ہوا ہے'),
(730, 5, 61, 'اور یہ جب تمہارے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان لے آئے ہیں حالانکہ یہ کفر ہی کے ساتھ داخل ہوئے ہیں اور اسی کو لے کر نکلے ہیں اور خدا ان سب باتوں کو خوب جانتا ہے جن کو یہ چھپائے ہوئے ہیں'),
(731, 5, 62, 'ان میں سے بہت سوں کو آپ دیکھیں گے کہ وہ گناہ اور ظلم اور حرام خوری میں دوڑتے پھرتے ہیں اور یہ بدترین کام انجام دے رہے ہیں'),
(732, 5, 63, 'آخر انہیں اللہ والے اور علمائ ان کے جھوٹ بولنے اور حرام کھانے سے کیوں نہیں منع کرتے -یہ یقینا بہت برا کررہے ہیں'),
(733, 5, 64, 'اور یہودی کہتے ہیں کہ خدا کے ہاتھ بندھے ہوئے ہیں جب کہ اصل میں ان ہی کے ہاتھ بندھے ہوئے ہیں اور یہ اپنے قول کی بنا پر ملعون ہیں اور خدا کے دونوں ہاتھ کھلے ہوئے ہیں اور وہ جس طرح چاہتا ہے خرچ کرتاہے اور جو کچھ آپ پر پروردگار کی طرف سے نازل ہوا ہے اس کا انکار ان میں سے بہت سوں کے کفر اور ان کی سرکشی کو اور بڑھادے گا اور ہم نے ان کے درمیان قیامت تک کے لئے عداوت اور بغض پیدا کردیا ہے کہ جب بھی جنگ کی آگ بھڑکانا چاہیں گے خدا بجھا دے گا اور یہ زمین میں فساد کی کوشش کررہے ہیں اور خدا مفسدوں کو دوست نہیں رکھتا'),
(734, 5, 65, 'اور اگر اہلِ کتاب ایمان لے آتے اور ہم سے ڈرتے رہتے تو ہم ان کے گناہوں کو معاف کردیتے اور انہیں نعمتوں کے باغات میں داخل کردیتے'),
(735, 5, 66, 'اور اگر یہ لوگ توریت و انجیل اور جو کچھ ان کی طرف پروردگار کی طرف سے نازل کیا گیا ہے سب کو قائم کرتے تو اپنے اوپر اور قدموں کے نیچے سے رزق هخدا حاصل کرتے ,ان میں سے ایک قوم میانہ رو ہے اور زیادہ حصّہ لوگ بدترین اعمال انجام دے رہے ہیں'),
(736, 5, 67, 'اے پیغمبر آپ اس حکم کو پہنچادیں جو آپ کے پروردگار کی طرف سے نازل کیا گیا ہے اور اگر آپ نے یہ نہ کیا تو گویا اس کے پیغام کو نہیں پہنچایا اور خدا آپ کو لوگوں کے شر سے محفوظ رکھے گا کہ اللہ کافروں کی ہدایت نہیں کرتا ہے'),
(737, 5, 68, 'کہہ دیجئے کہ اے اہل کتاب تمہارا کوئی مذہب نہیں ہے جب تک توریت و انجیل اور جو کچھ پروردگار کی طرف سے نازل ہوا ہے اسے قائم نہ کرو اور جو کچھ آپ کے پاس پروردگار کی طرف سے نازل ہوا ہے وہ ان کی کثیر تعداد کی سرکشی اور کفر میں اضافہ کردے گا تو آپ کافروں کے حال پر رنجیدہ نہ ہوں'),
(738, 5, 69, 'بیشک جو لوگ صاحبِ ایمان ہیںیا یہودی اور ستارہ پرست اور عیسائی ہیں ان میں سے جو بھی اللہ اور آخرت پر واقعی ایمان لائے گا اور عمل صالح کرے گا اس کے لئے نہ خوف ہے اور نہ اُسے حزن ہوگا'),
(739, 5, 70, 'ہم نے بنی اسرائیل سے عہد لیا ہے اور ان کی طرف بہت سے رسول بھیجے ہیں لیکن جب ان کے پاس کوئی رسول ان کی خواہش کے خلاف حکم لے آیا تو انہوں نے ایک جماعت کی تکذیب کی اور ایک گروہ کو قتل کردیتے ہیں'),
(740, 5, 71, 'اور ان لوگوں نے خیال کیا کہ اس میں کوئی خرابی نہ ہوگی اسی لئے یہ حقائق سے اندھے اور بہرے ہوگئے ہیں -اس کے بعد خدا نے ان کی توبہ قبول کرلی لیکن پھر بھی اکثریت اندھی بہری ہوگئی اور خدا ان کے تمام اعمال پر گہری نگاہ رکھتا ہے'),
(741, 5, 72, 'یقینا وہ لوگ کافر ہیں جن کا کہنا ہے کہ اللہ ہی مسیح علیھ السّلامبن مریم ہیں جبکہ خود مسیح کا کہنا ہے کہ اے بنی اسرائیل اپنے اور میرے پروردگار کی عبادت کرو -جو کوئی اس کا شریک قرار دے گا اس پر خدا نے جنّت حرام کردی ہے اور اس کا انجام جہّنم ہے اور ظالموں کا کوئی مددگار نہ ہوگا'),
(742, 5, 73, 'یقینا وہ لوگ کافر ہیں جن کا کہنا یہ ہے کہ اللہ تین میں کا تیسرا ہے .... حالانکہ اللہ کے علاوہ کوئی خدا نہیں ہے اور اگر یہ لوگ اپنے قول سے باز نہ آئیں گے تو ان میں سے کِفر اختیار کرنے والوں پر دردناک عذاب نازل ہوجائے گا'),
(743, 5, 74, 'یہ لوگ خدا کی بارگاہ میں توبہ اور استغفار کیوں نہیں کرتے جب کہ اللہ بہت بخشنے والا اور مہربان ہے'),
(744, 5, 75, 'مسیح علیھ السّلامبن مریم کچھ نہیں ہیں صرف ہمارے رسول ہیں جن سے پہلے بہت سے رسول گزر چکے ہیں اور ان کی ماں صدیقہ تھیں اور وہ دونوں کھانا کھایا کرتے تھے .... دیکھو ہم اپنی نشانیوں کو کس طرح واضح کرکے بیان کرتے ہیں اور پھر دیکھو کہ یہ لوگ کس طرح بہکے جارہے ہیں'),
(745, 5, 76, 'پیغمبر آپ ان سے کہئے کہ کیا تم اللہ کو چھوڑ کر ان کی عبادت کرتے ہو جو تمہارے لئے نفع اور نقصان کے مالک بھی نہیں ہیں اور خدا سننے والا بھی ہے اور جاننے والا بھی ہے'),
(746, 5, 77, 'پیغمبر آپ کہہ دیجئے کہ اے اہلِ هکتاب اپنے دین میں ناحق غلو سے کام نہ لو اور اس قوم کی خواہشات کا اتباع نہ کرو جو پہلے سے گمراہ ہوچکی ہے اور بہت سے لوگوں کو گمراہ بھی کرچکی ہے اور سیدھے راستہ سے بہک چکی ہے'),
(747, 5, 78, 'بنی اسرائیل میں سے کفر اختیار کرنے والوں پر جناب داؤد علیھ السّلام اور جناب عیسٰی علیھ السّلامکی زبان سے لعنت کی جاچکی ہے کہ ان لوگوں نے نافرمانی کی اور ہمیشہ حد سے تجاوز کیا کرتے تھے'),
(748, 5, 79, 'انہوں نے جو برائی بھی کی ہے اس سے باز نہیں آتے تھے اور بدترین کام کیا کرتے تھے'),
(749, 5, 80, 'ان میں سے اکثر کو آپ دیکھیں گے کہ یہ کفار سے دوستی کرتے ہیں-انہوں نے اپنے نفس کے لئے جو سامان پہلے سے فراہم کیا ہے وہ بہت اِرا سامان ہے جس پر خدا ان سے ناراض ہے اور وہ عذاب میں ہمیشہ رہنے والے ہیں'),
(750, 5, 81, 'اگر یہ لوگ اللرُ اور نبی موسٰی علیھ السّلام اور جو کچھ ان پر نازل ہوا ہے سب پر ایمان رکھتے تو ہرگز کفاّر کو دوست نہ بناتے لیکن ان میں سے بہت سے لوگ فاسق اور نافرمان ہیں'),
(751, 5, 82, 'آپ دیکھیں گے کہ صاحبانِ ایمان سے سب سے زیادہ عداوت رکھنے والے یہودی اور مشرک ہیں اور ان کی محبت سے سب سے زیادہ قریب وہ لوگ ہیں جو کہتے ہیں کہ ہم نصرانی ہیں ...._ یہ اس لئے ہے کہ ان میں بہت سے قسیس اور راہب پائے جاتے ہیں اور یہ متکبر اور برائی کرنے والے نہیں ہیں'),
(752, 5, 83, 'اور جب اس کلام کو سنتے ہیں جو رسول پر نازل ہوا ہے تو تم دیکھتے ہو کہ ان کی آنکھوں سے بے ساختہ آنسو جاری ہوجاتے ہیں کہ انہوں نے حق کو پہچان لیا ہے اور کہتے ہیں کہ پروردگار ہم ایمان لے آئے ہیں لہذا ہمارا نام بھی تصدیق کرنے والوں میں درج کرلے'),
(753, 5, 84, 'بھلا ہم اللہ اور اپنے پاس آنے والے حق پر کس طرح ایمان نہ لائیں گے جب کہ ہماری خواہش ہے کہ پروردگار ہمیں نیک کردار بندوں میں شامل کرلے'),
(754, 5, 85, 'تو اس قول کی بنا پر پروردگار نے انہیں وہ باغات دے دئیے جن کے نیچے نہریں بہہ رہی ہیں اور وہ انہی میں ہمیشہ رہنے والے ہیں کہ یہی نیک کردار لوگوں کی جزا ہے'),
(755, 5, 86, 'اور جن لوگوں نے کفر اختیار کرلیا اور ہماری آیات کو جھٹلا دیا تو وہ لوگ جہنّمی ہیں'),
(756, 5, 87, 'ایمان والو جن چیزوں کو خدا نے تمہارے لئے حلال کیا ہے انہیں حرام نہ بناؤ اور حد سے تجاوز نہ کرو کہ خدا تجاوز کرنے والوں کو دوست نہیں رکھتا ہے'),
(757, 5, 88, 'اور جو اس نے رزق حلال و پاکیزہ دیا ہے اس کو کھاؤ اور اس خدا سے ڈرتے رہو جس پر ایمان رکھنے والے ہو'),
(758, 5, 89, 'خدا تم سے بے مقصد قسمیں کھانے پر مواخذہ نہیں کرتا ہے لیکن جن قسموں کی گرہ دل نے باندھ لی ہے ان کی مخالفت کا کفارہ دس مسکینوں کے لئے اوسط درجہ کا کھانا ہے جو اپنے گھر والوں کو کھلاتے ہو یا ان کا کپڑا یا ایک غلام کی آزادی ہے .... پھر اگر یہ سب ناممکن ہو تو تین روزے رکھو ...._ کہ یہ تمہاری قسموں کا کفارہ ہے جب بھی تم قسم کھا کر اس کی مخالفت کرو اپنی قسموں کا تحفظ کرو کہ خدا اس طرح اپنی آیات کو واضح کرکے بیان کرتا ہے کہ شاید تم اس کے شکر گزار بندے بن جاؤ'),
(759, 5, 90, 'ایمان والو !شرابً جوا ,بت ,پانسہ یہ سب گندے شیطانی اعمال ہیں لہذا ان سے پرہیز کرو تاکہ کامیابی حاصل کرسکو'),
(760, 5, 91, 'شیطان تو بس یہی چاہتا ہے کہ شراب اور جوے کے بارے میں تمہارے درمیان بغض اور عداوت پیدا کردے اور تمہیں یاد خدا اور نماز سے روک دے تو کیا تم واقعا رک جاؤ گے'),
(761, 5, 92, 'اور دیکھو اللہ کی اطاعت کرو اور رسول کی اطاعت کرو اور نافرمانی سے بچتے رہو ورنہ اگر تم نے روگردانی کی توجان لو کہ رسول کی ذمہ داری صرف واضح پیغام کا پہنچادینا ہے'),
(762, 5, 93, 'جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ان کے لئے اس میں کوئی حرج نہیں ہے جو کچھ کھا پی چکے ہیں جب کہ وہ متقی بن گئے اور ایمان لے آئے اور نیک اعمال کئے اور پرہیز کیااور ایمان لے آئے اور پھر پرہیز کیا اور نیک عمل کیا اور نیک اعمال کرنے والوں ہی کو دوست رکھتا ہے'),
(763, 5, 94, 'ایمان والو !اللہ ان شکاروں کے ذریعہ تمہارا امتحان ضرور لے گا جن تک تمہارے ہاتھ اور نیزے پہنچ جاتے ہیں تاکہ وہ یہ دیکھے کہ اس سے لوگوں کے غائبانہ میں بھی کون کون ڈرتا ہے پھر جو اس کے بعد بھی زیادتی کرے گا اس کے لئے دردناک عذاب ہے'),
(764, 5, 95, 'ایمان والو حالاُ احرام میں شکار کو نہ مارو اور جو تم میں قصدا ایسا کرے گا اس کی سزا انہی جانوروں کے برابر ہے جنہیں قتل کیا ہے جس کا فیصلہ تم میں سے دو عادل افراد کریں اور اس قربانی کو کعبہ تک جانا چاہئے یا مساکین کے کھانے کی شکل میں کفارہ دیا جائے یا اس کے برابر روزے رکھے جائیں تاکہ اپنے کام کے انجام کا مزہ چکھیں -اللہ نے گزشتہ معاملات کو معاف کردیا ہے لیکن اب جو دوبارہ شکار کرے گا تو اس سے انتقام لے گا اور وہ سب پر غالب آنے والا اور بدلہ لینے والا ہے'),
(765, 5, 96, 'تمہارے لئے دریائی جانور کا شکار کرنا اور اس کا کھانا حلال کردیا گیا ہے کہ تمہارے لئے اور قافلوں کے لئے فائدہ کا ذریعہ ہے اور تمہارے لئے خشکی کا شکار حرام کردیا گیا ہے جب تک حالت احرام میں رہو اور اس خدا سے ڈرتے رہو جس کی بارگاہ میں حاضر ہونا ہے'),
(766, 5, 97, 'اللہ نے کعبہ کو جو بیت الحرام ہے اور محترم مہینے کو اور قربانی کے عام جانوروں کو اور جن جانوروں کے گلے میں پٹہ ڈال دیا گیا ہے سب کو لوگوں کے قیام و صلاح کا ذریعہ قرار دیا ہے تاکہ تمہیں یہ معلوم رہے کہ اللہ زمین و آسمان کی ہر شے سے باخبر ہے اور وہ کائنات کی ہر شے کا جاننے والا ہے'),
(767, 5, 98, 'یاد رکھو خدا سخت عذاب کرنے والا بھی ہے اور غفور و رحیم بھی ہے'),
(768, 5, 99, 'اور رسول پر تبلیغ کے علاوہ کوئی ذمہ داری نہیں ہے اور اللہ سے جن باتوں کا تم اظہار کرتے ہو یا چھپاتے ہو سب سے باخبر ہے'),
(769, 5, 100, 'پیغمبر آپ کہہ دیجئے کہ خبیث اور پاکیزہ برابر نہیں ہوسکتے ,چاہے خبیث کی کثرت اچھی ہی کیوں نہ لگے صاحبان عقل اللہ سے ڈرتے رہو شاید کہ تم اس طرح کامیاب ہوجاؤ'),
(770, 5, 101, 'ایمان والو ان چیزوں کے بارے میں سوال نہ کرو جو تم پر ظاہر ہوجائیں تو تمہیں بری لگیں اور اگر نزول قرآن کے وقت دریافت کرلو گے تو ظاہر بھی کردی جائیں گی اور اب تک کی باتوں کواللہ نے معاف کردیا ہے کہ وہ بڑا بخشنے والا اور برداشت کرنے والا ہے'),
(771, 5, 102, 'تم سے سے پہلے والی قوموں نے بھی ایسے ہی سوالات کئے اور اس کے بعد پھر کافر ہوگئے'),
(772, 5, 103, 'اللہ نے بحیرہ ,سائبہ ,وصیلہ اور ہام کا کوئی قانون نہیں بنایا یہ جو لوگ کافر ہوگئے ہیں وہ اللہ پر جھوٹا بہتان باندھتے ہیں اور ان میں کے اکثر بے عقل ہیں'),
(773, 5, 104, 'اور جب ان سے کہا جاتا ہے کہ خد اکے نازل کئے ہوئے احکام اور اس کے رسول کی طرف آؤ تو کہتے ہیں کہ ہمارے لئے وہی کافی ہے جس پر ہم نے اپنے آباؤ و اجداد کو پایا ہے چاہے ان کے آباؤ و اجداد نہ کچھ سمجھتے ہوں اور نہ کسی طرح کی ہدایت کرتے ہوں'),
(774, 5, 105, 'ایمان والو اپنے نفس کی فکر کرو -اگر تم ہدایت یافتہ رہے تو گمراہوں کی گمراہی تمہیں کوئی نقصان نہیں پہنچاسکتی -تم سب کی بازگشت خدا کی طرف ہے پھر وہ تمہیں تمہارے اعمال سے باخبر کرے گا'),
(775, 5, 106, 'ایمان والو جب تم میں سے کسی کی موت سامنے آجائے تو گواہی کا خیال رکھنا کہ وصیت کے وقت دو عادل گواہ تم میں سے ہوں یا پھر تمہارے غیر میں سے ہوں اگر تم سفر کی حالت میں ہو اور وہیں موت کی مصیبت نازل ہوجائے ان دونوں کو نماز کے بعد روک کر رکھو پھر اگر تمہیں شک ہو تو یہ خدا کی قسم کھائیں کہ ہم اس گواہی سے کسی طرح کا فائدہ نہیں چاہتے ہیں چاہے قرابتدار ہی کا معاملہ کیوں نہ ہو اور نہ خدائی شہادت کو چھپاتے ہیں کہ اس طرح ہم یقینا گناہگاروں میں شمار ہوجائیں گے'),
(776, 5, 107, 'پھر اگر معلوم ہوجائے کہ وہ دونوں گناہ کے حقدار ہوگئے ہیں تو ان کی جگہ دو دوسرے آدمی کھڑے ہوں گے ان افراد میں سے جو میت سے قریب تر ہیں اور وہ قسم کھائیں گے کہ ہماری شہادت ان کی شہادت سے زیادہ صحیح ہے اور ہم نے کسی طرح کی زیادتی نہیں کی ہے کہ اس طرح ظالمین میں شمار ہوجائیں گے'),
(777, 5, 108, 'یہ بہترین طریقہ ہے کہ شہادت کو صحیح طریقہ سے پیش کریں یا اس بات سے ڈریں کہ کہیں ہماری گواہی دوسروں کی گواہی کے بعد رد نہ کردی جائے اوراللہ سے ڈرتے رہو اور اس کی سنو کہ وہ فاسق قوم کی ہدایت نہیں کرتا ہے'),
(778, 5, 109, 'جس دن خدا تمام مرسلین کو جمع کرکے سوال کرے گا کہ تمہیں قوم کی طرف سے تبلیغ کا کیا جواب ملا تو وہ کہیں گے کہ ہم کیا بتائیں تو خود ہی غیب کا جاننے والا ہے'),
(779, 5, 110, 'اور جب اللہ نے کہا کہ اے عیسٰی بن مریم کیا تم نے لوگوں سے یہ کہہ دیا ہے کہ اللہ کو چھوڑ کو مجھے اور میری ماں کو خدا مان لو .... تو عیسٰی نے عرض کی کہ تیری ذات بے نیاز ہے میں ایسی بات کیسے کہوں گا جس کا مجھے کوئی حق نہیں ہے اور اگر میں نے کہا تھا تو تجھے تو معلوم ہی ہے کہ تو میرے دل کا حال جانتا ہے اور میں تیرے اسرار نہیں جانتا ہوں -تو ,تو غیب کا جاننے والا بھی ہے'),
(780, 5, 111, 'اور جب ہم نے حواریین کی طرف الہام کیا کہ ہم پر اور ہمارے رسولوں پر ایمان لے آؤ تو انہوں نے کہا کہ ہم ایمان لے آئے اور تو گواہ رہنا کہ ہم مسلمان ہیں'),
(781, 5, 112, 'اور جب حواریین نے کہا کہ اے عیسٰی بن مریم کیا تمہارے رب میں یہ طاقت بھی ہے کہ ہمارے اوپر آسمان سے دسترخوان نازل کردے تو انہوں نے جواب دیا کہ تم اگر مومن ہو تو اللہ سے ڈرو'),
(782, 5, 113, 'ان لوگوں نے کہا کہ ہم چاہتے ہیں کہ ہم اس میں سے کھائیں اور اطمینان قلب پیدا کریں اور یہ جان لیں کہ آپ نے ہم سے سچ کہا ہے اور ہم خود بھی اس کے گواہوں میں شامل ہوجائیں'),
(783, 5, 114, 'عیسٰی بن مریم نے کہا خدایا پروردگار !ہمارے اوپر آسمان سے دستر خوان نازل کردے کہ ہمارے اول و آخر کے لئے عید ہوجائے اور تیری قدرت کی نشانی بن جائے اور ہمیں رزق دے کہ تو بہترین رزق دینے والا ہے'),
(784, 5, 115, 'پروردگار نے کہا کہ ہم نازل تو کررہے ہیں لیکن اس کے بعد جو تم میں سے انکار کرے گا اس پر ایسا عذاب نازل کریں گے کہ عالمین میں کسی پر نہیں کیا ہے'),
(785, 5, 116, 'اور جب اللہ نے کہا کہ اے عیسٰی بن مریم کیا تم نے لوگوں سے یہ کہہ دیا ہے کہ اللہ کو چھوڑ کو مجھے اور میری ماں کو خدا مان لو .... تو عیسٰی نے عرض کی کہ تیری ذات بے نیاز ہے میں ایسی بات کیسے کہوں گا جس کا مجھے کوئی حق نہیں ہے اور اگر میں نے کہا تھا تو تجھے تو معلوم ہی ہے کہ تو میرے دل کا حال جانتا ہے اور میں تیرے اسرار نہیں جانتا ہوں -تو ,تو غیب کا جاننے والا بھی ہے'),
(786, 5, 117, 'میں نے ان سے صرف وہی کہا ہے جس کا تو نے حکم دیا تھا کہ میری اور اپنے پروردگار کی عبادت کرو اور میں جب تک ان کے درمیان رہا ان کا گواہ اور نگراں رہا -پھر جب تو نے مجھے اٹھالیا تو تو ان کا نگہبان ہے اور تو ہر شے کا گواہ اور نگراں ہے'),
(787, 5, 118, 'اگر تو ان پر عذاب کرے گا تو وہ تیرے ہی بندے ہیں اور اگر معاف کردے گا تو صاحب عزت بھی ہے اور صاحب حکمت بھی ہے'),
(788, 5, 119, 'اللہ نے کہا کہ یہ قیامت کا دن ہے جب صادقین کو ان کا سچ فائدہ پہنچائے گا کہ ان کے لئے باغات ہوں گے جن کے نیچے نہریں جاری ہوں گی اور وہ ان میں ہمیشہ ہمیشہ رہیں گے -خدا ان سے راضی ہوگا اور وہ خدا سے اور یہی ایک عظیم کامیابی ہے'),
(789, 5, 120, 'اللہ کے لئے زمین و آسمان اور ان کے درمیان کی کل حکومت ہے اور وہ ہر شے پر قدرت رکھنے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'ساری تعریف اس اللہ کے لئے ہے جس نے آسمانوں اور زمین کو پیدا کیا ہے اور تاریکیوں اور نور کو مقرر کیا ہے اس کے بعد بھی کفر اختیار کرنے والے دوسروں کو اس کے برابر قرار دیتے ہیں'),
(791, 6, 2, 'وہ خدا وہ ہے جس نے تم کو مٹی سے پیدا کیا ہے پھر ایک مدت کا فیصلہ کیا ہے اور ایک مقررہ مدت اس کے پاس اور بھی ہے لیکن اس کے بعد بھی تم شک کرتے ہو'),
(792, 6, 3, 'وہ آسمانوں اور زمین ہرجگہ کا خدا ہے وہ تمہارے باطن اور ظاہر اور جو تم کاروبار کرتے ہو سب کو جانتا ہے'),
(793, 6, 4, 'ان لوگوں کے پاس ان کے پروردگار کی کوئی نشانی نہیں آتی مگر یہ کہ یہ اس سے کنارہ کشی اختیار کرلیتے ہیں'),
(794, 6, 5, 'ان لوگوں نے اس کے پہلے بھی حق کے آنے کے بعد حق کا انکار کیا ہے عنقریب ان کے پاس جن چیزوں کا مذاق اڑاتے تھے ان کی خبریں آنے والی ہیں'),
(795, 6, 6, 'کیا انہوں نے نہیں دیکھا کہ ہم نے ان سے پہلے کتنی نسلوں کو تباہ کردیا ہے جنہیں تم سے زیادہ زمین میں اقتدار دیا تھا اور ان پر موسلادھار پانی بھی برسایا تھا -ان کے قدموں میں نہریں بھی جاری تھیں پھر ان کے گناہوں کی بنا پر انہیں ہلاک کردیا اور ان کے بعد دوسری نسل جاری کردی'),
(796, 6, 7, 'اور اگر ہم آپ پر کاغذ پر لکھی ہوئی کتاب بھی نازل کردیتے اور یہ اپنے ہاتھ سے چھو بھی لیتے تو بھی ان کے کافر یہی کہتے کہ یہ کھلا ہوا جادو ہے'),
(797, 6, 8, 'اور یہ کہتے ہیں کہ ان پر مُلکُ کیوں نہیں نازل ہوتا حالانکہ ہم ملُک نازل کردیتے تو کام کا فیصلہ ہوجاتا اور پھر انہیں کسی طرح کی مہلت نہ دی جاتی'),
(798, 6, 9, 'اگر ہم پیغمبر کو فرشتہ بھی بناتے تو بھی مرد ہی بناتے اور وہی لباس پہنچاتے جو مرد پہنا کرتے ہیں'),
(799, 6, 10, 'پیغمبر آپ سے پہلے بہت سے رسولوں کا مذاق اُڑایا گیا ہے جس کے نتیجہ میں مذاق اُڑانے والوں کے لئے ان کا مذاق ہی ان کے گلے پڑ گیا اور وہ اس میں گھر گئے'),
(800, 6, 11, 'آپ ان سے کہہ دیجئے کہ زمین میں سیر کریں پھر اس کے بعد دیکھیں کہ جھٹلانے والوں کا انجام کیا ہوتا ہے'),
(801, 6, 12, 'ان سے کہئے کہ زمین و آسمان میں جو کچھ بھی ہے وہ سب کس کے لئے ہے? پھر بتائیے کہ سب اللہ ہی کے لئے ہے اس نے اپنے اوپر رحمت کو لازم قرار دے لیا ہے وہ تم سب کو قیامت کے دن اکٹھا کرے گا جس میں کسی شک کی گنجائش نہیں ہے -جن لوگوں نے اپنے نفس کو خسارہ میں ڈال دیا ہے وہ اب ایمان نہ لائیں گے'),
(802, 6, 13, 'اور اس خدا کے لئے وہ تمام چیزیں ہیں جو رات اور دن میں ثابت ہیں اور وہ سب کی سننے والا اور سب کا جاننے والا ہے'),
(803, 6, 14, 'آپ کہئے کہ کیا میں خدا کے علاوہ کسی اور کو اپنا ولی بنالوں جب کہ زمین و آسمان کا پیدا کرنے والا وہی ہے اور وہی سب کو کھلاتا ہے اس کو کوئی نہیں کھلاتا ہے. کہئے کہ مجھے حکم دیا گیا ہے کہ میں سب سے پہلا اطاعت گزار بنوں اور خبردار تم لوگ مشرک نہ ہوجانا'),
(804, 6, 15, 'کہئے کہ میں اپنے خدا کی نافرمانی کروں تو مجھے بڑے سنگین دن کے عذاب کا خطرہ ہے'),
(805, 6, 16, 'اس دن جس سے عذاب ٹال دیا جائے اس پر خدا نے بڑا رحم کیا اور یہ ایک کھلی ہوئی کامیابی ہے'),
(806, 6, 17, 'اگر خدا کی طرف سے تم کو کوئی نقصان پہنچ جائے تو اس کے علاوہ کوئی ٹالنے والا بھی نہیں ہے اور اگر وہ خیر دے دے تو وہی ہر شے پر قدرت رکھنے والا ہے'),
(807, 6, 18, 'اور اپنے تمام بندوں پر غالب اور صاحب حکمت اور باخبر رہنے والا ہے'),
(808, 6, 19, 'آپ کہہ دیجئے کہ گواہی کے لئے سب سے بڑی چیز کیا ہے اور بتادیجئے کہ خدا ہمارے اور تمہارے درمیان گواہ ہے اور میری طرف اس قرآن کی وحی کی گئی ہے تاکہ اس کے ذریعہ میں تمہیں اور جہاں تک یہ پیغام پہنچے سب کو ڈراؤں کیا تم لوگ گواہی دیتے ہو کہ خدا کے ساتھ کوئی اور خدا بھی ہے تو آپ کہہ دیجئے کہ میں اس کی گواہی نہیں دے سکتا اور بتادیجئے کہ خدا صرف خدائے واحد ہے اور میں تمہارے شرک سے بیزار ہوں'),
(809, 6, 20, 'جن لوگوں کو ہم نے کتاب دی ہے وہ پیغمبر کو اسی طرح پہچانتے ہیں جس طرح اپنی اولاد کو پہنچاتے ہیں لیکن جن لوگوں نے اپنے نفس کو خسارہ میں ڈال دیا ہے وہ ایمان نہیں لاسکتے'),
(810, 6, 21, 'اس سے زیادہ ظالم کون ہوسکتا ہے جو خدا پر بہتان باندھے اور اس کی آیات کی تکذیب کرے -یقینا ان ظالمین کے لئے نجات نہیں ہے'),
(811, 6, 22, 'قیامت کے دن ہم سب کو ایک ساتھ اُٹھائیں گے پھر مشرکین سے کہیں گے کہ تمہارے وہ شرکائ کہاں ہیں جو تمہارے خیال میں خدا کے شریک تھے'),
(812, 6, 23, 'اس کے بعد ان کا کوئی فتنہ نہ ہوگا سوائے اس کے کہ یہ کہہ دیں کہ خدا کی قسم ہم مشرک نہیں تھے'),
(813, 6, 24, 'دیکھئے انہوں نے کس طرح اپنے آپ کو جھٹلایا اور کس طرح ان کا افترا حقیقت سے دور نکلا'),
(814, 6, 25, 'اور ان میں سے بعض لوگ کان لگا کر آپ کی بات سنتے ہیں لیکن ہم نے ان کے دلوں پر پردے ڈال دئیے ہیں ... یہ سمجھ نہیں سکتے ہیں اور ان کے کانوں میں بھی بہراپن ہے -یہ اگر تمام نشانیوں کو دیکھ لیں تو بھی ایمان نہ لائیں گے یہاں تک کہ جب آپ کے پاس آئیں گے تو بھی بحث کریں گے اور کفار کہیں گے کہ یہ قرآن تو صرف اگلے لوگوں کی کہانی ہے'),
(815, 6, 26, 'یہ لوگ دوسروں کو قرآن سے روکتے ہیں اور خود بھی دور بھاگتے ہیں حالانکہ یہ اپنے ہی کو ہلاک کررہے ہیں اور انہیں شعور تک نہیں ہے'),
(816, 6, 27, 'اگر آپ دیکھتے کہ یہ کس طرح جہنّم کے سامنے کھڑے کئے گئے اور کہنے لگے کہ کاش ہم پلٹا دئیے جاتے اور پروردگار کی نشانیوں کی تکذیب نہ کرتے اور مومنین میں شامل ہوجاتے'),
(817, 6, 28, 'بلکہ ان کے لئے وہ سب واضح ہوگیا جسے پہلے سے چھپارہے تھے اور اگر یہ پلٹا بھی دئیے جائیں تو وہی کریں گے جس سے یہ روکے گئے ہیں اور یہ سب جھوٹے ہیں'),
(818, 6, 29, 'اور یہ کہتے ہیں کہ یہ صرف زندگانی دنیا ہے اور ہم دوبارہ نہیں اُٹھائے جائیں گے'),
(819, 6, 30, 'اور اگر آپ اس وقت دیکھتے جب انہیں پروردگار کے سامنے کھڑا کیا جائے گا اور ان سے خطاب ہوگا کہ کیا یہ سب حق نہیں ہے تو وہ لوگ کہیں گے کہ بیشک ہمارے پروردگار کی قسم یہ سب حق ہے تو ارشاد ہوگا کہ اب اپنے کفر کے عذاب کا مزہ چکھو'),
(820, 6, 31, 'بیشک وہ لوگ خسارہ میں ہیں جنہوں نے اللہ کی ملاقات کا انکار کیا یہاں تک کہ جب اچانک قیامت آگئی تو کہنے لگے کہ ہائے افسوس ہم نے قیامت کے بارے میں بہت کوتاہی کی ہے -اس وقت وہ اپنے گناہوں کا بوجھ اپنی پشت پر لادے ہوں گے اور یہ بدترین بوجھ ہوگا'),
(821, 6, 32, 'اور یہ زندگانی دنیا صرف کھیل تماشہ ہے اور دار آخرت صاحبان تقوٰی کے لئے سب سے بہتر ہے ... کیا تمہاری سمجھ میں یہ بات نہیں آرہی ہے'),
(822, 6, 33, 'ہمیں معلوم ہے کہ آپ کو ان کے اقوال سے دکھ ہوتا ہے لیکن یہ آپ کی تکذیب نہیں کررہے ہیں بلکہ یہ ظالمین آیات الٰہی کا انکار کررہے ہیں'),
(823, 6, 34, 'اور آپ سے پہلے والے رسولوں کو بھی جھٹلایا گیا ہے تو انہوں نے اس تکذیب اور اذیت پر صبر کیا ہے یہاں تک کہ ہماری مدد آگئی اور اللہ کی باتوں کا کوئی بدلنے والا نہیں ہے اور آپ کے پاس تو مرسلین کی خبریں آچکی ہیں'),
(824, 6, 35, 'اور اگر ان کا اعراض و انحراف آپ پر گراں گزرتا ہے تو اگر آپ کے بس میں ہے کہ زمین میں سرنگ بنادیں یا آسمان میں سیڑھی لگا کر کوئی نشانی لے آئیں تو لے آئیں ....بیشک اگر خدا چاہتا تو جبرا سب کو ہدایت پر جمع ہی کردیتا لہذا آپ اپنا شمار ناواقف لوگوں میں نہ ہونے دیں'),
(825, 6, 36, 'بس بات کو وہی لوگ قبول کرتے ہیں جو سنتے ہیں اور مفِدوں کو تو خدا ہی اُٹھائے گا اور پھر اس کی بارگاہ میں پلٹائے جائیں گے'),
(826, 6, 37, 'اور یہ کہتے ہیں کہ ان کے اوپر کوئی نشانی کیوں نہیں نازل ہوتی تو کہہ دیجئے کہ خدا تمہاری پسندیدہ نشانی بھی نازل کرسکتا ہے لیکن اکثریت اس بات کو بھی نہیں جانتی ہے'),
(827, 6, 38, 'اور زمین میں کوئی بھی رینگنے والا یا دونوں پروں سے پرواز کرنے والا طائر ایسا نہیں ہے جو اپنی جگہ پر تمہاری طرح کی جماعت نہ رکھتا ہو -ہم نے کتاب میں کسی شے کے بیان میں کوئی کمی نہیں کی ہے اس کے بعد سب اپنے پروردگار کی بارگاہ میں پیش ہوں گے'),
(828, 6, 39, 'اور جن لوگوں نے ہماری آیات کی تکذیب کی وہ بہرے گونگے تاریکیوں میں پڑے ہوئے ہیں اور خدا جسے چاہتا ہے یوں ہی گمراہی میں رکھتا ہے اور جسے چاہتا ہے صراط مستقیم پر لگا دیتا ہے'),
(829, 6, 40, 'آپ ان سے کہئے کہ تمہارا کیا خیال ہے کہ اگر تمہارے پاس عذاب یا قیامت آجائے تو کیا تم اپنے دعوے کی صداقت میں غیر خدا کو بلاؤ گے'),
(830, 6, 41, 'نہیں تم خدا ہی کو پکارو گے اور وہی اگر چاہے گا تو اس مصیبت کو رفع کرسکتا ہے ....اور تم اپنے مشرکانہ خداؤں کو بھول جاؤ گے'),
(831, 6, 42, 'ہم نے تم سے پہلے والی اُمتوں کی طرف بھی رسول بھیجے ہیں اس کے بعد انہیں سختی اور تکلیف میں مبتلا کیا کہ شاید ہم سے گڑ گڑائیں'),
(832, 6, 43, 'پھر ان سختیوں کے بعد انہوں نے کیوں فریاد نہیں کی بات یہ ہے کہ ان کے دل سخت ہوگئے ہیں اور شیطان نے ان کے اعمال کو ان کے لئے آراستہ کردیا ہے'),
(833, 6, 44, 'پھر جب ان نصیحتوں کو بھول گئے جو انہیں یاد دلائی گئی تھیں تو ہم نے امتحان کے طور پر ان کے لئے ہر چیز کے دروازے کھول دئیے یہاں تک کہ جب وہ ان نعمتوں سے خوشحال ہوگئے تو ہم نے اچانک انہیں اپنی گرفت میں لے لیا اور وہ مایوس ہوکر رہ گئے'),
(834, 6, 45, 'پھر ظالمین کا سلسلہ منقطع کردیا گیا اور ساری تعریف اس خد اکے لئے ہے جو رب العالمین ہے'),
(835, 6, 46, 'آپ ان سے پوچھئے کہ اگر خد انے ان کی سماعت و بصارت کو لے لیا اور ان کے دل پر مہر لگادی تو خدا کے علاوہ دوسرا کون ہے جو دوبارہ انہیں یہ نعمتیں دے سکے گا دیکھو ہم اپنی نشانیوں کو کس طرح الٹ پلٹ کر دکھلاتے ہیں اور پھر بھی یہ منہ موڑ لیتے ہیں'),
(836, 6, 47, 'آپ ان سے کہئے کہ کیا تمہارا خیال ہے کہ اگر اچانک یا علی الاعلان عذاب آجائے تو کیا ظالمین کے علاوہ کوئی اور ہلاک کیا جائے گا'),
(837, 6, 48, 'اور ہم تو مرسلین کو صرف بشارت دینے والا اور ڈرانے والا بنا کر بھیجتے ہیں اس کے بعد جو ایمان لے آئے اور اپنی اصلاح کرلے اس کے لئے نہ خوف ہے اور نہ حزن'),
(838, 6, 49, 'اور جن لوگوں نے تکذیب کی انہیں ان کی نافرمانیوں کی وجہ سے عذاب اپنی لپیٹ میں لے لے گا'),
(839, 6, 50, 'آپ کہئے کہ ہمارا دعو ٰی یہ نہیں ہے کہ ہمارے پاس خدائی خزانے ہیں یا ہم عالم الغیب ہیں اور نہ ہم یہ کہتے ہیں کہ ہم مُلک ہیں. ہم تو صرف وحی پروردگار کا اتباع کرتے ہیں اور پوچھئے کہ کیا اندھے اور بینا برابر ہوسکتے ہیں آخر تم کیوں نہیں سوچتے ہو'),
(840, 6, 51, 'اور آپ اس کتاب کے ذریعہ انہیں ڈرائیں جنہیں یہ خوف ہے کہ خدا کی بارگاہ میں حاضر ہوں گے اور اس کے علاوہ کوئی سفارش کرنے والا یا مددگار نہ ہوگا شاید ان میں خوف خدا پیدا ہوجائے'),
(841, 6, 52, 'اور خبردار جو لوگ صبح و شام اپنے خدا کو پکارتے ہیں اور خدا ہی کو مقصود بنائے ہوئے ہیں انہیں اپنی بزم سے الگ نہ کیجئے گا. نہ آپ کے ذمہ ان کا حساب ہے اور نہ ان کے ذمہ آپ کا حساب ہے کہ آپ انہیں دھتکار دیں اور اس طرح ظالموں میں شمارہوجائیں'),
(842, 6, 53, 'اور اسی طرح ہم نے بعض کو بعض کے ذریعہ آزمایا ہے تاکہ وہ یہ کہیں کہ یہی لوگ ہیں جن پر خدا نے ہمارے درمیان فضل و کرم کیا ہے اور کیا وہ اپنے شکر گزار بندوں کو بھی نہیں جانتا'),
(843, 6, 54, 'اور جب آپ کے پاس وہ لوگ آئیں جو ہماری آیتوں پر ایمان رکھتے ہیں تو ان سے کہئے السلام علیکم ...._ تمہارے پروردگار نے اپنے اوپر رحمت لازم قرر دے لی ہے کہ تم میں جو بھی ازروئے جہالت برائی کرے گا اور اس کے بعد توبہ کرکے اپنی اصلاح کرلے گا تو خدا بہت زیادہ بخشنے والا اور مہربان ہے'),
(844, 6, 55, 'اور ہم اسی طرح اپنی نشانیوں کو تفصیل کے ساتھ بیان کرتے ہیں تاکہ مجرمین کا راستہ ان پر واضح ہوجائے'),
(845, 6, 56, 'آپ کہہ دیجئے کہ مجھے اس بات سے روکا گیا ہے کہ میں ان کی عبادت کروں جنہیں تم خدا کو چھوڑ کر پکارتے ہو کہہ دیجئے کہ میں تمہاری خواہشات کا اتباع نہیں کرسکتا کہ اس طرح گمراہ ہوجاؤں گا اور ہدایت یافتہ نہ رہ سکوں گا'),
(846, 6, 57, 'کہہ دیجئے کہ میں پروردگار کی طرف سے کھلی ہوئی دلیل رکھتا ہوں اور تم نے اسے جھٹلایا ہے تو میرے پاس وہ عذاب نہیں ہے جس کی تمہیں جلدی ہے حکم صرف اللہ کے اختیار میں ہے وہی حق کو بیان کرتا ہے اور وہی بہترین فیصلہ کرنے والا ہے'),
(847, 6, 58, 'کہہ دیجئے کہ اگر میرے اختیار میں وہ عذاب ہوتا جس کی تمہیں جلدی ہے تو اب تک ہمارے تمہارے درمیان فیصلہ ہوچکا ہوتا اور خدا ظالمین کو خوب جانتا ہے'),
(848, 6, 59, 'اور اس کے پاس غیب کے خزانے ہیں جنہیں اس کے علاوہ کوئی نہیں جانتا ہے اور وہ خشک و تر سب کا جاننے والا ہے -کوئی پتہ بھی گرتا ہے تو اسے اس کا علم ہے زمین کی تاریکیوں میں کوئی دانہ یا کوئی خشک و تر ایسا نہیں ہے جو کتاب همبین کے اندر محفوظ نہ ہو'),
(849, 6, 60, 'اور وہی خدا ہے جو تمہیں رات میں گویا کہ ایک طرح کی موت دے دیتا ہے اور دن میں تمہارے تمام اعمال سے باخبر ہے اور پھر دن میں تمہیں اُٹھادیتا ہے تاکہ مقررہ مدت حیات پوری کی جاسکے -اس کے بعد تم سب کی باز گشت اسی کی بارگاہ میں ہے اور پھر وہ تمہیں تمہارے اعمال کے بارے میں باخبر کرے گا'),
(850, 6, 61, 'اور وہی خدا ہے جو اپنے بندوں پر غالب ہے اور تم سب پر محافظ فرشتے بھیجتا ہے یہاں تک کہ جب کسی کی موت کا وقت آجاتا ہے تو ہمارے بھیجے ہوئے نمائندے اسے اُٹھالیتے ہیں اور کسی طرح کی کوتاہی نہیں کرتے'),
(851, 6, 62, 'پھر سب اپنے مولائے برحق پروردگار کی طرف پلٹا دئیے جاتے ہیں .... آگاہ ہوجاؤ کہ فیصلہ کا حق صرف اسی کو ہے اور وہ بہت جلدی حساب کرنے والا ہے'),
(852, 6, 63, 'ان سے کہئے کہ خشکی اور تری کی تاریکیوں سے کون نجات دیتا ہے جسے تم گڑگڑا کر اور خفیہ طریقہ سے آواز دیتے ہو کہ اگر اس مصیبت سے نجات دے دے گا تو ہم شکر گزار بن جائیں گے'),
(853, 6, 64, 'کہہ دیجئے کہ خد اہی تمہیں ان تمام ظلمات سے اور ہر کرب و بے چینی سے نجات دینے والا ہے اس کے بعد تم لوگ شرک اختیار کرلیتے ہو'),
(854, 6, 65, 'کہہ دیجئے کہ وہی اس بات پر بھی قادر ہے کہ تمہارے اوپر سے یا پیروں کے نیچے سے عذاب بھیج دے یا ایک گروہ کو دوسرے سے ٹکرادے اور ایک کے ذریعہ دوسرے کو عذاب کا مزہ چکھادے -دیکھو ہم کس طرح آیات کو پلٹ پلٹ کر بیان کرتے ہیں کہ شاید ان کی سمجھ میں آجائے'),
(855, 6, 66, 'اور آپ کی قوم نے اس قرآن کی تکذیب کی حالانکہ وہ بالکل حق ہے تو آپ کہہ دیجئے کہ میں تمہارا نگہبان نہیں ہوں'),
(856, 6, 67, 'ہر خبر کے لئے ایک وقت مقررہے اور عنقریب تمہیں معلوم ہوجائے گا'),
(857, 6, 68, 'اور جب تم دیکھو کہ لوگ ہماری نشانیوں کے بارے میں بے ربط بحث کررہے ہیں تو ان سے کنارہ کش ہوجاؤ یہاں تک کہ وہ دوسری بات میں مصروف ہوجائیں اور اگر شیطان غافل کردے تو یاد آنے کے بعد پھر ظالموں کے ساتھ نہ بیٹھنا'),
(858, 6, 69, 'اور صاحبان هتقو یٰ پر ان کے حساب کی کوئی ذمہ داری نہیں ہے لیکن یہ ایک یاددہانی ہے کہ شاید یہ لوگ بھی تقوٰی اختیار کرلیں'),
(859, 6, 70, 'اور ان لوگوں کو چھوڑ دو جنہوں نے اپنے دین کو کھیل تماشہ بنا رکھا ہے اور انہیں زندگانی دنیا نے دھوکہ میں مبتلا کردیا ہے اور ان کو یاد دہانی کراتے رہو کہ مبادا کوئی شخص اپنے کئے کی بنا پر ایسے عذاب میں مبتلا ہوجائے کہ اللہ کے علاوہ کوئی سفارش کرنے والا اور مدد کرنے والا نہ ہو اور سارے معاوضے اکٹھا بھی کردے تو اسے قبول نہ کیا جائے یہی وہ لوگ ہیں جنہیں ان کے کرتوت کی بنا پر بلاؤں میں مبتلا کیا گیا ہے کہ اب ان کے لئے گرم پانی کا مشروب ہے اور ان کے کفر کی بنا پر دردناک عذاب ہے'),
(860, 6, 71, 'پیغمبر آپ کہہ دیجئے کہ ہم خدا کو چھوڑ کر ان کو پکاریں جو نہ فائدہ پہنچاسکتے ہیں اور نہ نقصان اور اس طرح اللہ کے ہدایت دینے کے بعد اُلٹے پاؤں پلٹ جائیں جس طرح کہ کسی شخص کو شیطان نے روئے زمین پر بہکا دیا ہو اور وہ حیران و سرگرداں مارا مارا پھر رہا ہو اور اس کے کچھ اصحاب اسے ہدایت کی طرف پکار رہے ہوں کہ ادھر آجاؤ .... آپ کہہ دیجئے کہ ہدایت صرف اللہ کی ہدایت ہے اور ہمیں حکم دیا گیا ہے کہ ہم رب العالمین کی بارگاہ میں سراپا تسلیم رہیں'),
(861, 6, 72, 'اور دیکھو نماز قائم کرواور اللہ سے ڈرو کہ وہی وہ ہے جس کی بارگاہ میں حاضر کئے جاؤ گے'),
(862, 6, 73, 'وہی وہ ہے جس نے آسمان و زمین کو حق کے ساتھ پیدا کیا ہے اور وہ جب بھی کہتا ہے کہ ہوجا تو وہ چیز ہوجاتی ہے اس کا قول برحق ہے اور جس دن صور پھونکا جائے گا اس دن سارا اختیار اسی کے ہاتھ میں ہوگا وہ غائب اور حاضر سب کا جاننے والا صاحب حکمت اور ہر شے سے باخبر ہے'),
(863, 6, 74, 'اور اس وقت کو یاد کرو جب ابراہیم علیھ السّلامنے اپنے مربی باپ آزر سے کہا کہ کیا تو ان بتوں کو خدا بنائے ہوئے ہے .میں تو تجھے اور تیری قوم کو کِھلی ہوئی گمراہی میں دیکھ رہا ہوں'),
(864, 6, 75, 'اور اسی طرح ہم ابراہیم علیھ السّلامکو آسمان و زمین کے اختیارات دکھلاتے ہیں اور اس لئے کہ وہ یقین کرنے والوں میں شامل ہوجائیں'),
(865, 6, 76, 'پس جب ان پر رات کی تاریکی چھائی اور انہوں نے ستارہ کو دیکھا تو کہا کہ کیا یہ میرا رب ہے . پھر جب وہ غروب ہوگیا تو انہوں نے کہا کہ میں ڈوب جانے والوں کو دوست نہیں رکھتا'),
(866, 6, 77, 'پھر جب چاند کو چمکتا دیکھا تو کہا کہ پھر یہ رب ہوگا .پھر جب وہ بھی ڈوب گیا تو کہا کہ اگر پروردگار ہی ہدایت نہ دے گا تو میں گمراہوں میں ہوجاؤں گا'),
(867, 6, 78, 'پھر جب چمکتے ہوئے سورج کو دیکھا تو کہا کہ پھر یہ خدا ہوگا کہ یہ زیادہ بڑا ہے لیکن جب وہ بھی غروب ہوگیا تو کہا کہ اے قوم میں تمہارے شِرک سے بری اور بیزار ہوں'),
(868, 6, 79, 'میرا رخ تمام تر اس خدا کی طرف ہے جس نے آسمانوں اور زمین کو پیدا کیا ہے اور میں باطل سے کنارہ کش ہوں اور مشرکوں میں سے نہیں ہوں'),
(869, 6, 80, 'اور جب ان کی قوم نے ان سے کٹ حجتی کی تو کہا کہ کیا مجھ سے خدا کے بارے میں بحث کررہے ہو جس نے مجھے ہدایت دے دی ہے اور میں تمہارے شریک کردہ خداؤں سے بالکل نہیں ڈرتا ہوں .مگر یہ کہ خود میرا خدا کوئی بات چاہے کہ اس کا علم ہر شے سے وسیع تر ہے کیا یہ بات تمہاری سمجھ میں نہیں آتی ہے'),
(870, 6, 81, 'اور میں کس طرح تمہارے خداؤں سے ڈر سکتا ہوں جب کہ تم اس بات سے نہیں ڈرتے ہو کہ تم نے ان کو خدا کا شریک بنادیا ہے جس کے بارے میں خدا کی طرف سے کوئی دلیل نہیں نازل ہوئی ہے تو اب دونوں فریقوں میں کون زیادہ امن و سکون کا حقدار ہے اگر تم جاننے والے ہو تو بتاؤ'),
(871, 6, 82, 'جو لوگ ایمان لے آئے اور انہوں نے اپنے ایمان کو ظلم سے آلودہ نہیں کیا ان ہی کے لئے امن وسکون ہے اور وہی ہدایت یافتہ ہیں'),
(872, 6, 83, 'یہ ہماری دلیل ہے جسے ہم نے ابراہیم علیھ السّلامکو ان کی قوم کے مقابلہ میں عطا کی اور ہم جس کو چاہتے ہیں اس کے درجات کو بلند کردیتے ہیں . بیشک تمہارا پروردگار صاحب هحکمت بھی ہے اور باخبر بھی ہے'),
(873, 6, 84, 'اور ہم نے ابراہیم علیھ السّلامکو اسحاق علیھ السّلامو یعقوب علیھ السّلامدئیے اور سب کو ہدایت بھی دی اور اس کے پہلے نوح علیھ السّلامکو ہدایت دی اور پھر ابراہیم علیھ السّلامکی اولاد میں داؤد علیھ السّلام ,سلیمان علیھ السّلامًایوب علیھ السّلامً یوسف علیھ السّلام,موسٰی علیھ السّلام ,اور ہارون علیھ السّلامقرار دئیے اور ہم ِسی طرح نیک عمل کرنے والوں کو جزا دیتے ہیں'),
(874, 6, 85, 'اور زکریا علیھ السّلام,یحیٰی علیھ السّلام,عیسٰی علیھ السّلاماور الیاس علیھ السّلام کو بھی رکھا جو سب کے سب نیک کرداروں میں تھے'),
(875, 6, 86, 'اور اسماعیل علیھ السّلام, الیسع علیھ السّلام ,یونس علیھ السّلام اور لوط علیھ السّلام بھی بنائے اور سب کو عالمین سے افضل و بہتر بنایا'),
(876, 6, 87, 'اور پھر ان کے باپ دادا ,اولاد اور برادری میں سے اور خود انہیں بھی منتخب کیا اور سب کو سیدھے راستہ کی ہدایت کردی'),
(877, 6, 88, 'یہی خدا کی ہدایت ہے جسے جس بندے کو چاہتا ہے عطا کردیتاہے اور اگر یہ لوگ شرک اختیار کرلیتے تو ان کے بھی سارے اعمال برباد ہوجاتے'),
(878, 6, 89, 'یہی وہ افراد ہیں جنہیں ہم نے کتاب ,حکومت اور نبوت عطا کی ہے . اب اگر یہ لوگ ان باتوں کا بھی انکار کرتے ہیں تو ہم نے ان باتوں کا ذمہ دار ایک ایسی قوم کو بنایا ہے جو انکار کرنے والی نہیں ہے'),
(879, 6, 90, 'یہی وہ لوگ ہیں جنہیں اللہ نے ہدایت دی ہے لہذا آپ بھی اسی ہدایت کے راستہ پر چلیں اور کہہ دیجئے کہ ہم تم سے اس کار تبلیغ و ہدایت کا کوئی اجر نہیں چاہتے ہیں یہ قرآن تو عالمین کی یاددہانی کا ذریعہ ہے'),
(880, 6, 91, 'اور ان لوگوں نے واقعی خدا کی قدر نہیں کی جب کہ یہ کہہ دیا کہ اللہ نے کسی بشر پر کچھ نہیں نازل کیا ہے تو ان سے پوچھئے کہ یہ کتاب جو موسٰی علیھ السّلاملے کر آئے تھے جو نور اور لوگوں کے لئے ہدایت تھی اور جسے تم نے چند کاغذات بنادیا ہے اور کچھ حصّہ ظاہر کیا اور کچھ چھپادیا حالانکہ اس کے ذریعہ تمہیں وہ سب بتادیا گیا تھا جو تمہارے باپ دادا کو بھی نہیں معلوم تھا یہ سب کس نے نازل کیا ہے اب آپ کہہ دیجئے کہ وہ وہی اللہ ہے اور پھر انہیں چھوڑ دیجئے یہ اپنی بکواس میں کھیلتے پھریں'),
(881, 6, 92, 'اور یہ کتاب جو ہم نے نازل کی ہے یہ بابرکت ہے اور اپنے پہلے والی کتابوں کی تصدیق کرنے والی ہے اور یہ اس لئے ہے کہ آپ مکّہ اور اس کے اطراف والوں کو ڈرائیں اور جو لوگ آخرت پر ایمان رکھتے ہیں وہ اس پر ایمان رکھتے ہیں اور اپنی نماز کی پابندی کرتے ہیں'),
(882, 6, 93, 'اور اس سے بڑا ظالم کون ہوگا جو خدا پر جھوٹا الزام لگائے یا اس کے نازل کئے بغیر یہ کہہ دے کہ مجھ پر وحی نازل ہوئی ہے اور جو یہ کہہ دے کہ میں بھی خدا کی طرح باتیں نازل کرسکتا ہوں اور اگر آپ دیکھتے کہ ظالم موت کی سختیوں میں ہیں اور ملائکہ اپنے ہاتھ بڑھائے ہوئے آواز دے رہے ہیں کہ اب اپنی جان نکال دو کہ آج تمہیں تمہارے جھوٹے بیانات اور الزامات پر رسوائی کا عذاب دیا جائے گا کہ تم آیات خدا سے انکار اور استکبار کررہے تھے'),
(883, 6, 94, 'اور بالآخر تم اسی طرح ہمارے پاس تنہا آئے جس طرح ہم نے تم کو پیدا کیا تھا اور جو کچھ تمہیں دیا تھا سب اپنے پیچھے چھوڑ آئے اور ہم تمہارے ساتھ تمہارے ان سفارش کرنے والوں کو بھی نہیں دیکھتے جنہیں تم نے اپنے لئے خدا کا شریک بنایا تھا. تمہارے ان کے تعلقات قطع ہوگئے اور تمہارا خیال تم سے غائب ہوگیا'),
(884, 6, 95, 'خدا ہی وہ ہے جو گٹھلی اور دانے کا شگافتہ کرنے والا ہے -وہ لَردہ سے زندہ اور زندہ سے لَردہ کو نکالتا ہے. یہی تمہارا خدا ہے تو تم کدھر بہکے جارہے ہو'),
(885, 6, 96, 'وہ نور سحر کا شگافتہ کرنے والا ہے اور اس نے رات کو وجہ سکون اور شمس و قمر کو ذریعہ حساب بنایا ہے . یہ اس صاحب هعزّت و حکمت کی مقرر کردہ تقدیر ہے'),
(886, 6, 97, 'وہی وہ ہے جس نے تمہارے لئے ستارے مقرر کئے ہیں کہ ان کے ذریعہ خشکی اور تری کی تاریکیوں میں راستہ معلوم کرسکو -ہم نے تمام نشانیوں کو اس قوم کے لئے تفصیل کے ساتھ بیان کیا ہے جو جاننے والی ہے'),
(887, 6, 98, 'وہی وہ ہے جس نے تم سب کو ایک نفس سے پیدا کیا ہے پھر سب کے لئے قرار گاہ اور منزلِ ودیعت مقرر کی ہے. ہم نے صاحبانِ فہم کے لئے تمام نشانیوں کو تفصیل کے ساتھ بیان کردیا ہے'),
(888, 6, 99, 'وہی خدا وہ ہے جس نے آسمان سے پانی نازل کیا ہے پھر ہم نے ہر شے کے کوئے نکالے پھر ہری بھری شاخیں نکالیں جس سے ہم گتھے ہوئے دانے نکالتے ہیں اور کھجور کے شگوفے سے لٹکتے ہوئے گچھےّ پیدا کئے اور انگور و زیتون اور انار کے باغات پیدا کئے جو شکل میں ملتے جلتے اور مزہ میں بالکل الگ الگ ہیں -ذرا اس کے پھل اور اس کے پکنے کی طرف غور سے دیکھو کہ اس میں صاحبان هایمان کے لئے کتنی نشانیاں پائی جاتی ہیں'),
(889, 6, 100, 'اور ان لوگوں نے جنّات کو خدا کا شریک بنادیا ہے حالانکہ خدا نے انہیں پیدا کیا ہے پھر اس کے لئے بغیر جانے بوجھے بیٹے اور بیٹیاں بھی تیار کردی ہیں .جب کہ وہ بے نیاز اور ان کے بیان کردہ اوصاف سے کہیں زیادہ بلند وبالا ہے'),
(890, 6, 101, 'وہ زمین و آسمان کا ایجاد کرنے والا ہے -اس کے اولاد کس طرح ہوسکتی ہے اس کی تو کوئی بیوی بھی نہیں ہے اور وہ ہر شے کا خالق ہے اور ہرچیز کا جاننے والا ہے'),
(891, 6, 102, 'وہی اللہ تمہارا پروردگار ہے جس کے علاوہ کوئی خدا نہیں -وہ ہر شے کا خالق ہے اسی کی عبادت کرو .وہی ہر شے کا نگہبان ہے'),
(892, 6, 103, 'نگاہیں اسے پا نہیں سکتیں اور وہ نگاہوں کا برابر ادراک رکھتا ہے کہ وہ لطیف بھی ہے اور خبیر بھی ہے'),
(893, 6, 104, 'تمہارے پاس تمہارے پروردگار کی طرف سے دلائل آچکے ہیں اب جو بصیرت سے کام لے گا وہ اپنے لئے اور جو اندھا بن جائے گا وہ بھی اپنا ہی نقصان کرے گا اور میں تم لوگوں کا نگہبان نہیں ہوں'),
(894, 6, 105, 'اور اسی طرح ہم پلٹ پلٹ کر آیتیں پیش کرتے ہیں اور تاکہ وہ یہ کہیں کہ آپ نے قرآن پڑھ لیا ہے اور ہم جاننے والوں کے لئے قرآن کو واضح کردیں'),
(895, 6, 106, 'آپ اپنی طرف آنے والی وحی الٰہی کا اتباع کریں کہ اس کے علاوہ کوئی خدا نہیں ہے اور مشرکین سے کنارہ کشی کرلیں'),
(896, 6, 107, 'اور اگر خدا چاہتا تو یہ شرک ہی نہ کرسکتے اور ہم نے آپ کو ان کا نگہبان نہیں بنایا ہے اور نہ آپ ان کے ذمہ دار ہیں'),
(897, 6, 108, 'اور خبردار تم لوگ انہیں برا بھلا نہ کہو جن کو یہ لوگ خدا کو چھوڑ کر پکارتے ہیں کہ اس طرح یہ دشمنی میں بغیر سمجھے بوجھے خدا کو برا بھلا کہیں گے ہم نے اسی طرح ہر قوم کے لئے اس کے عمل کو آراستہ کردیا ہے اس کے بعد سب کی بازگشت پروردگار ہی کی بارگاہ میں ہے اور وہی سب کو ان کے اعمال کے بارے میں باخبر کرے گا'),
(898, 6, 109, 'اور ان لوگوں نے اللہ کی سخت قسمیں کھائیں کہ ان کی مرضی کی نشانی آئی تو ضرور ایمان لے آئیں گے تو آپ کہہ دیجئے کہ نشانیاں تو سب خدا ہی کے پاس ہیں اور تم لوگ کیا جانو کہ وہ آبھی جائیں گی تو یہ لوگ ایمان نہ لائیں گے'),
(899, 6, 110, 'اور ہم ان کے قلب و نظر کو اس طرح پلٹ دیں گے جس طرح یہ پہلے ایمان نہیں لائے تھے اور ان کو گمراہی میں ٹھوکر کھانے کے لئے چھوڑ دیں گے'),
(900, 6, 111, 'اور اگر ہم ان کی طرف ملائکہ نازل بھی کردیں اور ان سے مردے کلام بھی کرلیں اور ان کے سامنے تمام چیزوں کو جمع بھی کردیں تو بھی یہ ایمان نہ لائیں گے مگر یہ کہ خدا ہی چاہ لے لیکن ان کی اکثریت جہالت ہی سے کام لیتی ہے'),
(901, 6, 112, 'اور اسی طرح ہم نے ہر نبی کے لئے جنات و انسان کے شیاطین کو ان کا دشمن قرار دے دیا ہے یہ آپس میں ایک دوسرے کی طرف دھوکہ دینے کے لئے مہمل باتوں کے اشارے کرتے ہیں اور اگر خدا چاہ لیتا تو یہ ایسا نہ کرسکتے لہذا اب آپ انہیں ان کے افترا کے حال پر چھوڑ دیں'),
(902, 6, 113, 'اور یہ اس لئے کرتے ہیں کہ جن لوگوں کا ایمان آخرت پر نہیں ہے ان کے دل ان کی طرف مائل ہوجائیں اور وہ اسے پسند کرلیں اور پھر خود بھی ان ہی کی طرح افترا پردازی کرنے لگیں'),
(903, 6, 114, 'کیا میں خدا کے علاوہ کوئی حکم تلاش کروں جب کہ وہی وہ ہے جس نے تمہاری طرف مفصل کتاب نازل کی ہے اور جن لوگوں کو ہم نے کتاب دی ہے انہیں معلوم ہے کہ یہ قرآن ان کے پروردگار کی طرف سے حق کے ساتھ نازل ہوا ہے لہذا ہرگز آپ شک کرنے والوں میں شامل نہ ہوں'),
(904, 6, 115, 'اور آپ کے رب کا کلمہ قرآن صداقت اور عدالت کے اعتبار سے بالکل مکمل ہے اس کا کوئی تبدیل کرنے والا نہیں ہے اور وہ سننے والا بھی ہے اور جاننے والا بھی ہے'),
(905, 6, 116, 'اور اگر آپ روئے زمین کی اکثریت کا اتباع کرلیں گے تو یہ راسِ خدا سے بہکادیں گے.یہ صرف گمان کا اتباع کرتے ہیں اور صرف اندازوں سے کام لیتے ہیں'),
(906, 6, 117, 'آپ کے پروردگار کو خوب معلوم ہے کہ کون اس کے راستے سے بہکنےوالا ہے. اور کون ہدایت حاصل کرنے والا ہے'),
(907, 6, 118, 'لہذا تم لوگ صرف اس جانور کا گوشت کھاؤ جس پر خد اکا نام لیا گیا ہو اگر تمہارا ایمان اس کی آیتوں پر ہے'),
(908, 6, 119, 'اورتمہیں کیا ہوگیا ہے کہ تم وہ نہیں کھاتے ہو جس پر نام خدا لیا گیا ہے جب کہ اس نے جن چیزوں کو حرام کیا ہے انہیں تفصیل سے بیان کردیا ہے مگر یہ کہ تم مجبور ہوجاؤ تو اور بات ہے اور بہت سے لوگ تو اپنی خواہشات کی بنا پر لوگوں کو بغیر جانے بوجھے گمراہ کرتے ہیں .اور تمہارا پروردگار ان زیادتی کرنے والوں کو خوب جانتا ہے'),
(909, 6, 120, 'اور تم لوگ ظاہری اور باطنی تمام گناہوں کو چھوڑ دو کہ جو لوگ گناہ کا ارتکاب کرتے ہیں عنقریب انہیں ان کے اعمال کا بدلہ دیا جائے گا'),
(910, 6, 121, 'اور دیکھو جس پر ناهِ خدا نہ لیا گیا ہو اسے نہ کھانا کہ یہ فسق ہے اور شیاطین تو اپنے والوں کی طرف خفیہ اشارے کرتے ہی رہتے ہیں تاکہ یہ لوگ تم سے جھگڑا کریں اور اگر تم لوگوں نے ان کی اطاعت کر لی تو تمہارا شمار بھی مشرکین میں ہوجائے گا'),
(911, 6, 122, 'کیا جو شخص مفِدہ تھا پھر ہم نے اسے زندہ کیااوراس کے لئے ایک نور قرار دیا جس کے سہارے وہ لوگوں کے درمیان چلتا ہے اس کی مثال اس کی جیسی ہوسکتی ہے جو تاریکیوں میں ہو اور ان سے نکل بھی نہ سکتا ہو -اسی طرح کفار کے لئے ان کے اعمال کو آراستہ کردیا گیا ہے'),
(912, 6, 123, 'اور اسی طرح ہم نے ہر قریہ میں بڑے بڑے مجرمین کو موقع دیا ہے کہ وہ مکاّری کریں اور ان کی مکاّری کا اثر خود ان کے علاوہ کسی پر نہیں ہوتا ہے لیکن انہیں اس کا بھی شعور نہیں ہے'),
(913, 6, 124, 'اور جب ان کے پاس کوئی نشانی آتی ہے تو کہتے ہیں کہ ہم اس وقت تک ایمان نہ لائیں گے جب تک ہمیں بھی ویسی ہی وحی نہ دی جائے جیسی رسولوں کو دی گئی ہے جب کہ اللہ بہتر جانتا ہے کہ اپنی رسالت کو کہاں رکھے گا اور عنقریب مجرمین کو ان کے جرم کی پاداش میں خدا کے یہاں ذلّت اور شدید ترین عذاب کا سامنا کرنا ہوگا'),
(914, 6, 125, 'پس خدا جسے ہدایت دینا چاہتا ہے اس کے سینے کو اسلام کے لئے کشادہ کردیتا ہے اور جس کو گمراہی میں چھوڑنا چاہتا ہے اس کے سینے کو ایسا تنگ اور دشوار گزار بنادیتا ہے جیسے آسمان کی طرف بلند ہورہا ہو ,وہ اسی طرح بے ایمانوں پر ان کی کثافت کو مسّلط کردیتاہے'),
(915, 6, 126, 'اور یہ ہی تمہارے پروردگار کا سیدھا راستہ ہے .ہم نے نصیحت حاصل کرنے والوں کے لئے آیات کو مفصل طور سے بیان کردیا ہے'),
(916, 6, 127, 'ان لوگوں کے لئے پروردگار کے نزدیک دارالّسلام ہے اور وہ ان کے اعمال کی بنا پر ان کا سرپرست ہے'),
(917, 6, 128, 'اور جس دن وہ سب کو محشور کرے گا تو کہے گا کہ اے گروہ هجناّت تم نے تو اپنے کوانسانوں سے زیادہ بنا لیا تھا اور انسانوں میں ان کے دوست کہیں گے کہ پروردگار ہم میں سب نے ایک دوسرے سے فائدہ اٹھایا ہے اور اب ہم اس مدّت کو پہنچ گئے ہیں جو تو نے ہماری مہلت کے واسطے معین کی تھی .ارشاد ہوگا تواب تمہارا ٹھکانہ جہّنم ہے جہاں ہمیشہ ہمیشہ رہنا ہے مگر یہ کہ خدا ہی آزادی چاہ لے -بیشک تمہارا پروردگار صاحب هحکمت بھی ہے اور جاننے والا بھی ہے'),
(918, 6, 129, 'اور اسی طرح ہم بعض ظالموں کو ان کے اعمال کی بنا پر بعض پر مسلّط کردیتے ہیں'),
(919, 6, 130, 'اے گروہ جن و انس کیا تمہارے پاس تم میں سے رسول نہیں آئے جو ہماری آیتوں کو بیان کرتے اور تمہیں آج کے دن کی ملاقات سے ڈراتے -وہ کہیں گے کہ ہم خود اپنے خلاف گواہ ہیں اور ان کو زندگانی دنیا نے دھوکہ میں ڈال رکھا تھا انہوں نے خود اپنے خلاف گواہی دی کہ وہ کافر تھے'),
(920, 6, 131, 'یہ اس لئے کہ تمہارا پروردگار کسی بھی قریہ کو ظلم و زیادتی سے اس طرح تباہ و برباد نہیں کرنا چاہتا کہ اس کے رہنے والے بالکل غافل اور بے خبر ہوں'),
(921, 6, 132, 'اور ہر ایک کے لئے اس کے اعمال کے مطابق درجات ہیں اور تمہارا پروردگار ان کے اعمال سے غافل نہیں ہے'),
(922, 6, 133, 'اور آپ کا پروردگار بے نیاز اور صاحبِ رحمت ہے -وہ چاہے تو تم سب کو دنیا سے اٹھالے اور تمہاری جگہ پر جس قوم کو چاہے لے آئے جس طرح تم کو دوسری قوم کی اولاد میں رکھا ہے'),
(923, 6, 134, 'یقینا جو تم سے وعدہ کیا گیا ہے وہ سامنے آنے والا ہے اور تم خدا کو عاجز بنانے والے نہیں ہو'),
(924, 6, 135, 'پیغمبر آپ کہہ دیجئے کہ تم بھی اپنی جگہ پر عمل کرو اور میں بھی کررہا ہوں -عنقریب تم کو معلوم ہوجائے گا کہ انجام کار کس کے حق میں بہتر ہے -بیشک ظالم کامیاب ہونے والے نہیں ہیں'),
(925, 6, 136, 'اور ان لوگوں نے خدا کی پیدا کی ہوئی کھیتی میں اور جانوروں میں اس کا حصہّ بھی لگایا ہے اور یہ اعلان کیا ہے کہ یہ ان کے خیال کے مطابق خد اکے لئے ہے اور یہ ہمارے شریکوں کے لئے ہے -اس کے بعد جو شرکائ کا حصّہ ہے وہ خدا تک نہیں جاسکتا ہے .اور جو خدا کا حصہّ ہے وہ ان کے شریکوں تک پہنچ سکتا ہے.کس قدر بدترین فیصلہ ہے جو یہ لوگ کررہے ہیں'),
(926, 6, 137, 'اور اسی طرح ان شریکوں نے بہت سے مشرکین کے لئے اولاد کے قتل کو بھی آراستہ کردیا ہے تاکہ ان کو تباہ و برباد کردیں اور ان پر دین کو مشتبہ کردیں حالانکہ خدا اس کے خلاف چاہ لیتا تو یہ کچھ نہیں کرسکتے تھے لہذا آپ ان کو ان کی افترا پردازیوں پر چھوڑ دیں اور پریشان نہ ہوں'),
(927, 6, 138, 'اور یہ لوگ کہتے ہیں کہ یہ جانور اور کھیتی اچھوتی ہے اسے ان کے خیالات کے مطابق وہی کھاسکتے ہیں جن کے بارے میں وہ چاہیں گے اور کچھ چوپائے ہیں جن کی پیٹھ حرام ہے اور کچھ چوپائے جن کو ذبح کرتے وقت نام خدا بھی نہیں لیا گیا اور سب کی نسبت خدا کی طرف دے رکھی ہے عنقریب ان تمام بہتانوں کا بدلہ انہیں دیا جائے گا'),
(928, 6, 139, 'اور کہتے ہیں کہ ان چوپاؤں کے پیٹ میں جو بچے ّہیں وہ صرف ہمارے مذِدوں کے لئے ہیں اور عورتوں پر حرام ہیں -ہاں اگر مفِدار ہوں تو سب شریک ہوں گے ,عنقریب خدا ان بیانات کا بدلہ دے گا کہ وہ صاحب هحکمت بھی ہے اور سب کا جاننے والا بھی ہے'),
(929, 6, 140, 'یقینا وہ لوگ خسارہ میں ہیں جنہوں نے حماقت میں بغیر جانے بوجھے اپنی اولاد کو قتل کردیا اور جو رزق خدا نے انہیں دیا ہے اسے اسی پر بہتان لگا کر اپنے اوپر حرام کرلیا -یہ سب بہک گئے ہیں اور ہدایت یافتہ نہیں ہیں'),
(930, 6, 141, 'وہ خدا وہ ہے جس نے تختوں پر چڑھائے ہوئے بھی اور اس کے خلاف بھی ہر طرح کے باغات پیدا کئے ہیں اور کھجور اور زراعت پیدا کی ہے جس کے مزے مختلف ہیں اور زیتون اورانار بھی پیدا کئے ہیں جن میں بعض آپس میں ایک دوسرے سے ملتے جلتے ہیں اور بعض مختلف ہیں -تم سب ان کے پھلوں کو جب وہ تیار ہوجائیں تو کھاؤ اور جب کاٹنے کا دن آئے تو ان کا حق ادا کردو اور خبردار اسراف نہ کرنا کہ خدا اسراف کرنے والوں کو دوست نہیں رکھتا ہے'),
(931, 6, 142, 'اور چوپاؤں میں بعض بوجھ اٹھانے والے اور بعض زمین سے لگ کر چلنے والے ہیں -تم سب خدا کے دئیے ہوئے رزق کو کھاؤ اور شیطانی قدموںکی پیروی نہ کرو کہ شیطان تمہارا کھلا ہوا دشمن ہے'),
(932, 6, 143, 'آٹھ قسم کے جوڑے ہیں -بھیڑ کے دو اور بکری کے دو -ان سے کہئے خدا نے ان دونوں کے نر حرام کئے ہیں یا مادہ یا وہ بچے جو ان کی مادہ کے پیٹ میں پائے جاتے ہیں -ذرا تم لوگ اس بارے میں ہمیں بھی خبردو اگر اپنے دعویٰ میں سچے ہو'),
(933, 6, 144, 'اور اونٹ میں سے دو اور گائے میں سے دو .... ان سے کہیے کہ خدا نے نروں کو حرام قرار دیا ہے یا مادینوں کو یا ان کو جو مادینوں کے شکم میں ہیں .... کیا تم لوگ اس وقت حاضر تھے جب خدا ان کو حرام کرنے کی وصیت کررہا تھا -پھر اس سے بڑا ظالم کون ہے جو خدا پر جھوٹا الزام لگائے تاکہ لوگوں کو بغیر جانے بوجھے گمراہ کرے -یقینا اللہ ظالم قوم کو ہدایت نہیں دیتا ہے'),
(934, 6, 145, 'آپ کہہ دیجئے کہ میں اپنی طرف آنے والی وحی میں کسی بھی کھانے والے کے لئے کوئی حرام نہیں پاتا مگر یہ کہ مفِدار ہو یا بہایا ہوا خون یا سور کا گوشت ہوکہ یہ سب نجس اور گندگی ہے یا وہ نافرمانی ہو جسے غیر خد اکے نام پر ذبح کیا گیا ہو .... اس کے بعد بھی کوئی مجبور ہوجائے اور نہ سرکش ہو نہ حد سے تجاوز کرنے والا تو پروردگار بڑا بخشنے والا مہربان ہے'),
(935, 6, 146, 'اور یہودیوں پر ہم نے ہر ناخن والے جانور کو حرام کردیا اور گائے اور بھیڑ کی چربی کو حرام کردیا مگر جو چربی کہ پیٹھ پر ہو یا آنتوں پر ہو یا جو ہڈیوں سے لگی ہوئی ہو .... یہ ہم نے ان کو ان کی بغاوت اور سرکشی کی سزادی ہے اور ہم بالکل سچےّ ہیں'),
(936, 6, 147, 'پھر اگر یہ لوگ آپ کو جھٹلائیں تو کہہ دیجئے کہ تمہارا پروردگار بڑی وسیع رحمت والا ہے لیکن اس کا عذاب مجرمین سے ٹالابھی نہیں جاسکتا ہے'),
(937, 6, 148, 'عنقریب یہ مشرکین کہیں گے کہ اگر خدا چاہتا تو نہ ہم مشرک ہوتے نہ ہمارے باپ دادا اور نہ ہم کسی چیز کو حرام قرار دیتے -اسی طرح ان سے پہلے والوں نے رسولوں کی تکذیب کی تھی یہاں تک کہ ہمارے عذاب کا مزہ چکھ لیا -ان سے کہہ دیجئے کہ تمہارے پاس کوئی دلیل ہے تو ہمیں بھی بتاؤ -تم تو صرف خیالات کا اتباع کرتے ہو اور اندازوں کی باتیں کرتے ہو'),
(938, 6, 149, 'کہہ دیجئے کہ اللہ کے پاس منزل تک پہنچانے والی دلیلیں ہیں وہ اگر چاہتا تو جبرا تم سب کو ہدایت دے دیتا'),
(939, 6, 150, 'کہہ دیجئے کہ ذرا اپنے گواہوں کو تو لاؤ جو گواہی دیتے ہیں کہ خدا نے اس چیز کو حرام قرار دیا ہے ...._ اس کے بعد وہ گواہی بھی دے دیں تو آپ ان کے ساتھ گواہی نہ دیجئے گا اور ان لوگوں کی خواہشات کا اتباع نہ کیجئے گا جنہوں نے ہماری آیتوں کو جھٹلایا ہے اور وہ آخرت پر ایمان نہیں رکھتے ہیں اور اپنے پروردگار کا ہمسر قرار دیتے ہیں'),
(940, 6, 151, 'کہہ دیجئے کہ آؤ ہم تمہیں بتائیں کہ تمہارے پروردگار نے کیا کیا حرام کیا ہے .... خبردار کسی کو اس کا شریک نہ بنانا اور ماں باپ کے ساتھ اچھا برتاؤ کرنا .اپنی اولاد کو غربت کی بنا پر قتل نہ کرنا کہ ہم تمہیں بھی رزق دے رہے ہیں اور انہیں بھی ... اور بدکاریوں کے قریب نہ جانا وہ ظاہری ہوں یا چھپی ہوئی اور کسی ایسے نفس کو جسے خدا نے حرام کردیا ہے قتل نہ کرنا مگر یہ کہ تمہارا کوئی حق ہو .یہ وہ باتیں ہیں جن کی خدا نے نصیحت کی ہے تاکہ تمہیں عقل آجائے'),
(941, 6, 152, 'اور خبردار مال هیتیم کے قریب بھی نہ جانا مگر اس طریقہ سے جو بہترین طریقہ ہو یہاں تک کہ وہ توانائی کی عمر تک پہنچ جائیں اور ناپ تول میں انصاف سے پورا پورا دینا -ہم کسی نفس کو اس کی وسعت سے زیادہ تکلیف نہیں دیتے ہیں اور جب بات کرو تو انصاف کے ساتھ چاہے اپنی اقرباہی کے خلاف کیوں نہ ہو اور عہد خدا کو پورا کرو کہ اس کی پروردگار نے تمہیں وصیت کی ہے کہ شاید تم عبرت حاصل کرسکو'),
(942, 6, 153, 'اور یہ ہمارا سیدھا راستہ ہے اس کا اتباع کرو اور دوسرے راستوں کے پیچھے نہ جاؤ کہ راہ هخدا سے الگ ہوجاؤ گے اسی کی پروردگار نے ہدایت دی ہے کہ اس طرح شاید متقی اور پرہیز گار بن جاؤ'),
(943, 6, 154, 'اس کے بعد ہم نے موسٰی علیھ السّلام کو اچھی باتوں کی تکمیل کرنے والی کتاب دی اور اس میں ہر شے کو تفصیل سے بیان کردیا اور اسے ہدایت اور رحمت قرار دے دیا کہ شاید یہ لوگ لقائے الٰہی پر ایمان لے آئیں'),
(944, 6, 155, 'اور یہ کتاب جو ہم نے نازل کی ہے یہ بڑی بابرکت ہے لہذا اس کا اتباع کرو اور تقوٰی اختیار کرو کہ شاید تم پر رحم کیا جائے'),
(945, 6, 156, 'یہ نہ کہنے لگو کہ کتاب خدا تو ہم سے پہلے دو جماعتوں پر نازل ہوئی تھی اور ہم اس کے پڑھنے پڑھانے سے بے خبر تھے'),
(946, 6, 157, 'یا یہ کہو کہ ہمارے اوپر بھی کتاب نازل ہوتی تو ہم ان سے زیادہ ہدایت یافتہ ہوتے تو اب تمہارے پروردگار کی طرف سے واضح دلیل اور ہدایت و رحمت آچکی ہے پھر اس کے بعد اس سے بڑا ظالم کون ہوگا جو اللہ کی نشانیوں کو جھٹلائے اور ان سے اعراض کرے ...عنقریب ہم اپنی نشانیوں سے اعراض کرنے والوں پر بدترین عذاب کریں گے اور وہ ہماری آیتوں سے اعراض کرنے والے اور روکنے والے ہیں'),
(947, 6, 158, 'یہ صرف اس بات کے منتظر ہیں کہ ان کے پاس ملائکہ آجائیں یا خود پروردگار آجائے یا اس کی بعض نشانیاں آجائیں تو جس دن اس کی بعض نشانیاں آجائیں گی اس دن جو نفس پہلے سے ایمان نہیں لایا ہے یا اس نے ایمان لانے کے بعد کوئی بھلائی نہیں کی ہے اس کے ایمان کا کوئی فائدہ نہ ہوگا تو اب کہہ دیجئے کہ تم لوگ بھی انتظار کرو اور میں بھی انتظار کررہا ہوں'),
(948, 6, 159, 'جن لوگوں نے اپنے دین میں تفرقہ پیدا کیا اور ٹکڑے ٹکڑے ہوگئے ان سے آپ کا کوئی تعلق نہیں ہے -ان کا معاملہ خدا کے حوالے ہے پھر وہ انہیں ان کے اعمال کے بارے میں باخبر کرے گا'),
(949, 6, 160, 'جو شخص بھی نیکی کرے گا اسے دس گنا اجر ملے گا اور جو برائی کرے گا اسے صرف اتنی ہی سزا ملے گی اور کوئی ظلم نہ کیا جائے گا'),
(950, 6, 161, 'آپ کہہ دیجئے کہ میرے پروردگار نے مجھے سیدھے راستے کی ہدایت دے دی ہے جو ایک مضبوط دین اور باطل سے اعراض کرنے والے ابراہیم علیھ السّلامکا مذہب ہے اور وہ مشرکین میں سے ہرگز نہیں تھے'),
(951, 6, 162, 'کہہ دیجئے کہ میری نماز ,میری عبادتیں ,میری زندگی ,میری موت سب اللہ کے لئے ہے جو عالمین کا پالنے والا ہے'),
(952, 6, 163, 'اس کا کوئی شریک نہیں ہے اور اسی کا مجھے حکم دیا گیا ہے اور میں سب سے پہلا مسلمان ہوں'),
(953, 6, 164, 'کہہ دیجئے کہ کیا میں خدا کے علاوہ کوئی اور رب تلاش کروں جب کہ وہی ہر شے کا پالنے والا ہے اور جو نفس جو کچھ کرے گا اس کا وبال اسی کی ذمہ ہوگا اور کوئی نفس دوسرے کا بوجھ نہ اٹھائے گا -اس کے بعد تم سب کی بازگشت تمہارے پروردگار کی طرف ہے پھر وہ بتائے گا کہ تم کس چیز میں اختلاف کررہے تھے'),
(954, 6, 165, 'وہی وہ خدا ہے جس نے تم کو زمین میں نائب بنایا اور بعض کے درجات کو بعض سے بلند کیا تاکہ تمہیں اپنے دئیے ہوئے سے آزمائے -تمہارا پروردگار بہت جلد حساب کرنے والا ہے اور وہ بیشک بہت بخشنے والا مہربان ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'الۤمۤصۤ'),
(956, 7, 2, 'یہ کتاب آپ کی طرف نازل کی گئی ہے لہذا آپ اس کی طرف سے کسی تنگی کا شکار نہ ہوں اور اس کے ذریعہ لوگوں کو ڈرائیں یہ کتاب صاحبانِ ایمان کے لئے مکمل یاددہانی ہے'),
(957, 7, 3, 'لوگوتم اس کا اتباع کرو جو تمہارے پروردگار کی طرف سے نازل ہوا ہے اور اس کے علاوہ دوسرے سرپرستوں کا اتباع نہ کرو ,تم بہت کم نصیحت مانتے ہو'),
(958, 7, 4, 'اور کتنے قرئیے ہیں جنہیں ہم نے برباد کردیا اور ان کے پاس ہمارا عذاب اس وقت آیا جب وہ رات میں سورہے تھے یا دن میں قیلولہ کررہے تھے'),
(959, 7, 5, 'پھر ہمارا عذاب آنے کے بعد ان کی پکار صرف یہ تھی کہ یقینا ہم لوگ ظالم تھے'),
(960, 7, 6, 'پس اب ہم ان لوگوں سے بھی سوال کریں گے اور ان کے رسولوں سے بھی سوال کریں گے'),
(961, 7, 7, 'پھر ان سے ساری داستان علم و اطلاع کے ساتھ بیان کریں گے اور ہم خود بھی غائب تو تھے نہیں'),
(962, 7, 8, 'آج کے دن اعمال کا وزن ایک برحق شے ہے پھر جس کے نیک اعمال کا پّلہ بھاری ہوگا وہی لوگ نجات پانے والے ہیں'),
(963, 7, 9, 'اور جن کا پّلہ ہلکا ہوگیا یہی وہ لوگ تھے جنہوں نے اپنے نفس کو خسارہ میں رکھا کہ وہ ہماری آیتوں پر ظلم کررہے تھے'),
(964, 7, 10, 'یقینا ہم نے تم کو زمین میں اختیار دیا اور تمہارے لئے سامان زندگی قرار دئیے مگر تم بہت کم شکر ادا کرتے ہو'),
(965, 7, 11, 'اور ہم نے تم سب کو پیدا کیا پھر تمہاری صورتیں مقرر کیں -اس کے بعد ملائکہ کو حکم دیا کہ آدم کو سجدہ کریں تو سب نے سجدہ کرلیا صرف ابلیس نے انکار کردیا اور وہ سجدہ کرنے والوں میں شامل نہیں ہوا'),
(966, 7, 12, 'ارشاد ہوا کہ تجھے کس چیز نے روکا تھا کہ تونے میرے حکم کے بعد بھی سجدہ نہیں کیا -اس نے کہا کہ میں ان سے بہتر ہوں -تو نے مجھے آگ سے پیدا کیا ہے اور انہیں خاک سے بنایا ہے'),
(967, 7, 13, 'فرمایا تو یہاں سے اتر جا تجھے حق نہیں ہے کہ یہاں غرور سے کام لے -نکل جا کہ تو ذلیل لوگوں میں ہے'),
(968, 7, 14, 'اس نے کہا کہ پھر مجھے قیامت تک کی مہلت دے دے'),
(969, 7, 15, 'ارشاد ہوا کہ تو مہلت والوں میں سے ہے'),
(970, 7, 16, 'اس نے کہا کہ پس جس طرح تو نے مجھے گمراہ کیا ہے میں تیرے سیدھے راستہ پر بیٹھ جاؤں گا'),
(971, 7, 17, 'اس کے بعد سامنے ,پیچھے اور داہنے بائیں سے آؤں گا اور تو اکثریت کو شکر گزار نہ پائے گا'),
(972, 7, 18, 'فرمایا کہ یہاں سے نکل جا تو ذلیل اور مردود ہے اب جو بھی تیرا اتباع کرے گا میں تم سب سے جہّنم کو بھردوں گا'),
(973, 7, 19, 'اور اے آدم علیھ السّلامتم اور تمہاری زوجہ دونوں جنّت میں داخل ہوجاؤ اور جہاں جو چاہو کھاؤ لیکن اس درخت کے قریب نہ جانا کہ ظلم کرنے والوں میں شمار ہوجاؤ گے'),
(974, 7, 20, 'پھر شیطان نے ان دونوں میں وسوسہ پیدا کرایا کہ جن شرم کے مقامات کو چھپا رکھا ہے وہ نمایاں ہوجائیں اور کہنے لگا کہ تمہارے پروردگار نے تمہیں اس درخت سے صرف اس لئے روکا ہے کہ تم فرشتے ہوجاؤ گے یا تم ہمیشہ رہنے والو میں سے ہوجاؤ گے'),
(975, 7, 21, 'اور دونوں سے قسم کھائی کہ میں تمہیں نصیحت کرنے والوں میں ہوں'),
(976, 7, 22, 'پھر انہیں دھوکہ کے ذریعہ درخت کی طرف جھکا دیا اور جیسے ہی ان دونوں نے چکّھا شرمگاہیں کھلنے لگیں اور انہوں نے درختوں کے پتے جوڑ کر شرمگاہوں کو چھپانا شروع کردیا تو ان کے رب نے آواز دی کہ کیا ہم نے تم دونوں کو اس درخت سے منع نہیں کیا تھا اور کیا ہم نے تمہیں نہیں بتایا تھا کہ شیطان تمہارا کھلا ہوا دشمن ہے'),
(977, 7, 23, 'ان دونوں نے کہا کہ پروردگار ہم نے اپنے نفس پر ظلم کیا ہے اب اگر تو معاف نہ کرے گا اور رحم نہ کرے گا تو ہم خسارہ اٹھانے والوں میں ہوجائیں گے'),
(978, 7, 24, 'ارشاد ہوا کہ تم سب زمین میں اتر جاؤ اور سب ایک دوسرے کے دشمن ہو -زمین میں تمہارے لئے ایک مّدت تک ٹھکانا اور سامان زندگانی ہے'),
(979, 7, 25, 'فرمایا کہ وہیں تمہیں جینا ہے اور وہیں مرنا ہے اور پھر اسی زمین سے نکالے جاؤ گے'),
(980, 7, 26, 'اے اولادُ آدم ہم نے تمہارے لئے لباس نازل کیا ہے جس سے اپنی شرمگاہوں کا پردہ کرو اور زینت کا لباس بھی دیا ہے لیکن تقوٰی کا لباس سب سے بہتر ہے یہ بات آیات الٰہیٰہ میں ہے کہ شاید وہ لوگ عبرت حاصل کرسکیں'),
(981, 7, 27, 'اے اولاد آدم خبردار شیطان تمہیں بھی نہ بہکادے جس طرح تمہارے ماں باپ کو جنّت سے نکال لیا اس عالم میں کہ ان کے لباس الگ کرادئیے تاکہ شرمگاہیں ظاہر ہوجائیں -وہ اور اس کے قبیلہ والے تمہیں دیکھ رہے ہیں اس طرح کہ تم انہیں نہیں دیکھ رہے ہو بیشک ہم نے شیاطین کو بے ایمان انسانوں کا دوست بنادیا ہے'),
(982, 7, 28, 'اور یہ لوگ جب کوئی براکام کرتے ہیں تو کہتے ہیں کہ ہم نے آباؤ اجداد کو اسی طریقہ پر پایا ہے اور اللہ نے یہی حکم دیا ہے -آپ فرمادیجئے کہ خدا بری بات کا حکم دے ہی نہیں سکتا ہے کیا تم خدا کے خلاف وہ کہہ رہے ہو جو جانتے بھی نہیں ہو'),
(983, 7, 29, 'کہہ دیجئے کہ میرے پروردگار نے انصاف کا حکم دیا ہے اور تم سب ہر نماز کے وقت اپنا رخ سیدھا رکھا کرو اور خدا کو خالص دین کے ساتھ پکارو اس نے جس طرح تمہاری ابتدا کی ہے اسی طرح تم پلٹ کر بھی جاؤ گے'),
(984, 7, 30, 'اس نے ایک گروہ کو ہدایت دی ہے اور ایک پر گمراہی مسلط ہوگئی ہے کہ انہوں نے شیاطین کو اپنا ولی بنالیا ہے اور خدا کو نظر انداز کردیا ہے اور پھر ان کا خیال ہے کہ وہ ہدایت یافتہ بھی ہیں'),
(985, 7, 31, 'اے اولاد آدم ہر نماز کے وقت اور ہر مسجد کے پاس زینت ساتھ رکھو اور کھاؤ پیو مگر اسراف نہ کرو کہ خدا اسراف کرنے والوں کو دوست نہیں رکھتا ہے'),
(986, 7, 32, 'پیغمبر آپ پوچھئے کہ کس نے اس زینت کو جس کو خدا نے اپنے بندوں کے لئے پیدا کیا ہے اور پاکیزہ رزق کو حرام کردیا ہے ... اور بتائیے کہ یہ چیزیں روزِ قیامت صرف ان لوگوں کے لئے ہیں جو زندگانی دنیا میں ایمان لائے ہیں .ہم اسی طرح صاحبان هعلم کے لئے مفصل آیات بیان کرتے ہیں'),
(987, 7, 33, 'کہہ دیجئے کہ ہمارے پروردگار نے صرف بدکاریوں کو حرام کیا ہے چاہے وہ ظاہری ہوں یا باطنی .... اور گناہ اور ناحق ظلم اور بلادلیل کسی چیز کو خدا کا شریک بنانے اور بلاجانے بوجھے کسی بات کو خدا کی طرف منسوب کرنے کو حرام قرار دیا ہے'),
(988, 7, 34, 'ہر قوم کے لئے ایک وقت مقر ّر ہے جب وہ وقت آجائے گا تو ایک گھڑی کے لئے نہ پیچھے ٹل سکتا ہے اور نہ آگے بڑھ سکتا ہے'),
(989, 7, 35, 'اے اولاد آدم جب بھی تم میں سے ہمارے پیغمبر تمہارے پاس آئیں گے اور ہماری آیتوں کو بیان کریں گے تو جو بھی تقوی ٰ اختیار کرے گا اور اپنی اصلاح کرلے گا اس کے لئے نہ کوئی خوف ہے اور نہ وہ رنجیدہ ہوگا'),
(990, 7, 36, 'اور جن لوگوں نے ہماری آیات کی تکذیب کی اور اکڑ گئے وہ سب جہّنمی ہیں اور اسی میں ہمیشہ رہنے والے ہیں'),
(991, 7, 37, 'اس سے بڑا ظالم کون ہے جو خدا پر جھوٹا الزام لگائے یا اس کی آیات کی تکذیب کرے -ان لوگوں کو ان کی قسمت کا لکھا ملتا رہے گا یہاں تک کہ جب ہمارے نمائندے فرشتے مّدت حیات پوری کرنے کے لئے آئیں گے تو پوچھیں گے کہ وہ سب کہاں ہیں جن کو تم خدا کو چھوڑ کر پکارا کرتے تھے وہ لوگ کہیں گے کہ وہ سب تو گم ہوگئے اور اس طرح اپنے خلاف خود بھی گواہی دیں گے کہ وہ لوگ کافر تھے'),
(992, 7, 38, 'ارشاد ہوگا کہ تم سے پہلے جو جن و انس کی مجرم جماعتیں گزر چکی ہیں تم بھی انہی کے ساتھ جہّنم میں داخل ہوجاؤ-حالت یہ ہوگی کہ ہر داخل ہونے والی جماعت دوسری پر لعنت کرے گی اور جب سب اکٹھا ہوجائیں گے تو بعد والے پہلے والوں کے بارے میں کہیں گے کہ پروردگار ا ن ہی نے ہمیں گمراہ کیا ہے لہٰذا ان کے عذاب کو دوگنا کردے -ارشاد ہوگا کہ سب کا عذاب دوگنا ہے صرف تمہیں معلوم نہیں ہے'),
(993, 7, 39, 'پھر پہلے والے بعد والوں سے کہیں گے کہ تمہیں ہمارے اوپر کوئی فضیلت نہیں ہے لہذا اپنے کئے کا عذاب چکھو'),
(994, 7, 40, 'بیشک جن لوگوں نے ہماری آیتوں کی تکذیب کی اور غرور سے کام لیا ان کے لئے نہ آسمان کے دروازے کھولے جائیں گے اور نہ وہ جنّت میں داخل ہوسکیں گے جب تک اونٹ سوئی کے ناکہ کے اندر داخل نہ ہوجائے اور ہم اسی طرح مجرمین کو سزا دیا کرتے ہیں'),
(995, 7, 41, 'ان کے لئے جہّنم ہی کا فرش ہوگا اور ان کے اوپر اسی کا اوڑھنا بھی ہوگا اور ہم اسی طرح ظالموں کو ان کے اعمال کا بدلہ دیا کرتے ہیں'),
(996, 7, 42, 'اور جن لوگوں نے ایمان اختیار کیا اور نیک اعمال کئے ہم کسی نفس کو اس کی وسعت سے زیادہ تکلیف نہیں دیتے وہی لوگ جنّتی ہیں اور اس میں ہمیشہ رہنے والے ہیں'),
(997, 7, 43, 'اور ہم نے ان کے سینوں سے ہر کینہ کو الگ کردیا. ان کے قدموں میں نہریں جاری ہوں گی اور وہ کہیں گے کہ طُکر ہے پروردگار کا کہ اس نے ہمیں یہاں تک آنے کا راستہ بتادیا ورنہ اس کی ہدایت نہ ہوتی تو ہم یہاں تک آنے کا راستہ بھی نہیں پاسکتے تھے -بیشک ہمارے رسول سب دینِ حق لے کر آئے تھے تو پھر انہیں آواز دی جائے گی کہ یہ وہ جّنتہے جس کا تمہیں تمہارے اعمال کی بنا پر وارث بنایا گیا ہے'),
(998, 7, 44, 'اور جنّتی لوگ جہّنمیوں سے پکار کر کہیں گے کہ جو کچھ ہمارے پروردگار نے ہم سے وعدہ کیا تھا وہ ہم نے تو پالیا کیا تم نے بھی حسب وعدہ حاصل کرلیا ہے وہ کہیں گے بیشک .پھر ایک منادی آواز دے گا کہ ظالمین پر خدا کی لعنت ہے'),
(999, 7, 45, 'جو راہ هخدا سے روکتے تھے اور اس میں کجی پیدا کیا کرتے تھے اور آخرت کے منکر تھے'),
(1000, 7, 46, 'اور ان کے درمیان پردہ ڈال دیا جائے گا اور اعراف پر کچھ لوگ ہوں گے جو سب کو ان کی نشانیوں سے پہچان لیں گے اور اصحابِ جنّت کو آواز دیں گے کہ تم پر ہمارا سلام -وہ جنّت میں داخل نہ ہوئے ہوں گے لیکن اس کی خواہش رکھتے ہوں گے'),
(1001, 7, 47, 'اور پھر جب ان کی نظر جہّنموالوں کی طرف مڑ جائے گی تو کہیں گے کہ پروردگار ہم کو ان ظالمین کے ساتھ نہ قرار دینا'),
(1002, 7, 48, 'اور اعراف والے ان لوگوں کو جنہیں وہ نشانیوں سے پہچانتے ہوں گے آواز دیں گے کہ آج نہ تمہاری جماعت کام آئی اور نہ تمہارا غرور کام آیا'),
(1003, 7, 49, 'کیا یہی لوگ ہیں جن کے بارے میں تم قسمیں کھایا کرتے تھے کہ انہیں رحمت خدا حاصل نہ ہوگی جن سے ہم نے کہہ دیا ہے کہ جاؤ جنّت میں چلے جاؤ تمہارے لئے نہ کوئی خوف ہے اور نہ کوئی حزن'),
(1004, 7, 50, 'اور جہّنم والے جنّت والوں سے پکار کر کہیں گے کہ ذرا ٹھنڈا پانی یا خدا نے جو رزق تمہیں دیا ہے اس میں سے ہمیں بھی پہنچاؤ تو وہ لوگ جواب دیں گے کہ ان چیزوں کو اللہ نے کافروں پر حرام کردیا ہے'),
(1005, 7, 51, 'جن لوگوں نے اپنے دین کو کھیل تماشہ بنالیا تھا اور انہیں زندگانی دنیا نے دھوکہ دے دیا تھا تو آج ہم انہیں اسی طرح بھلادیں گے جس طرح انہوں نے آج کے دن کی ملاقات کو بھلادیا تھا اور ہماری آیات کا دیدہ و دانستہ انکار کررہے تھے'),
(1006, 7, 52, 'ہم ان کے پاس ایسی کتاب لے آئے ہیں جسے علم و اطلاع کے ساتھ مفصل بیان کیا ہے اور وہ صاحبانِ ایمان کے لئے ہدایت اور رحمت ہے'),
(1007, 7, 53, 'کیا یہ لوگ صرف انجام کار کا انتظار کررہے ہیں تو جس دن انجام سامنے آجائے گا تو جو لوگ پہلے سے اسے بھولے ہوئے تھے وہ کہنے لگیں گے کہ بیشک ہمارے پروردگار کے رسول صحیح ہی پیغام لائے تھے تو کیا ہمارے لئے بھی شفیع ہیں جو ہماری سفارش کریں یا ہمیں واپس کردیا جائے تو ہم جو اعمال کرتے تھے اس کے علاوہ دوسرے قسم کے اعمال کریں -درحقیقت ان لوگوں نے اپنے کو خسارہ میں ڈال دیا ہے اور ان کی ساری افترا پردازیاں غائب ہوگئی ہیں'),
(1008, 7, 54, 'بیشک تمہارا پروردگار وہ ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا ہے اور اس کے بعد عرش پر اپنا اقتدار قائم کیا ہے وہ رات کو دن پر ڈھانپ دیتاہے اور رات تیزی سے اس کے پیچھے دوڑا کرتی ہے اور آفتاب و ماہتاب اور ستارے سب اسی کے حکم کے تابع ہیں اسی کے لئے خلق بھی ہے اور امر بھی وہ نہایت ہی صاحب هبرکت اللہ ہے جو عالمین کا پالنے والا ہے'),
(1009, 7, 55, 'تم اپنے رب کو گڑگڑا کراور خاموشی کے ساتھ پکارو کہ وہ زیادتی کرنے والوں کو دوست نہیں رکھتا ہے'),
(1010, 7, 56, 'اور خبردار زمین میں اصلاح کے بعد فساد نہ پیدا کرنا اور خدا سے ڈرتے ڈرتے اور امیدوار بن کر دعا کرو کہ اس کی رحمت صاحبان هحسن هعمل سے قریب تر ہے'),
(1011, 7, 57, 'وہ خدا وہ ہے جو ہواؤں کو رحمت کی بشارت بناکر بھیجتا ہے یہاں تک کہ جب ہوائیں وزنی بادلوں کو اٹھالیتی ہیں تو ہم ان کو مردہ شہروں کو زندہ کرنے کے لئے لے جاتے ہیں اور پھر پانی برسادیتے ہیں اور اس کے ذریعہ مختلف پھل پیدا کردیتے ہیں اور اسی طرح ہم مفِدوں کو زندہ کردیا کرتے ہیں کہ شاید تم عبرت و نصیحت حاصل کرسکو'),
(1012, 7, 58, 'اور پاکیزہ زمین کا سبزہ بھی اس کے پروردگار کے حکم سے خوب نکلتا ہے اور جو زمین خبیث ہوتی ہے اس کا سبزہ بھی خراب نکلتا ہے ہم اسی طرح شکر کرنے والی قوم کے لئے اپنی آیتیں الٹ پلٹ کر بیان کرتے ہیں'),
(1013, 7, 59, 'یقینا ہم نے نوح علیھ السّلامکو ان کی قوم کی طرف بھیجا تو انہوں نے کہا کہ اے قوم والو اللہ کی عبادت کرو کہ اس کے علاوہ تمہارا کوئی خدا نہیں ہے. میں تمہارے بارے میں عذاب عظیم سے ڈرتا ہوں'),
(1014, 7, 60, 'تو قوم کے رؤسا نے جواب دیا کہ ہم تم کو کھلی ہوئی گمراہی میں دیکھ رہے ہیں'),
(1015, 7, 61, 'نوح علیھ السّلامنے کہا کہ اے قوم مجھ میں گمراہی نہیں ہے بلکہ میں ربّالعالمین کی طرف سے بھیجا ہوا نمائندہ ہوں'),
(1016, 7, 62, 'میں تم تک اپنے پروردگار کے پیغامات پہنچاتا ہوں اور تمہیں نصیحت کرتا ہوں اور اللہ کی طرف سے وہ سب جانتا ہوں جو تم نہیں جانتے ہو'),
(1017, 7, 63, 'کیا تمہیں اس بات پر تعجب ہے کہ تمہارے پروردگار کی طرف سے تم ہی میں سے سے ایک مرد پر ذکر نازل ہوجائے کہ وہ تمہیں ڈرائے اور تم متقی بن جاؤ اور شاید اس طرح قابل هرحم بھی ہوجاؤ'),
(1018, 7, 64, 'پھر ان لوگوں نے نوح علیھ السّلامکی تکذیب کی تو ہم نے انہیں اور ان کے ساتھیوں کو کشتی میں نجات دے دی اور جن لوگوں نے ہماری آیتوں کو جھٹلایا تھا انہیں غرق کردیا کہ وہ سب بالکل اندھے لوگ تھے'),
(1019, 7, 65, 'اور ہم نے قوم عاد علیھ السّلامکی طرف ان کے بھائی ہود کو بھیجا تو انہوں نے کہا کہ اے قوم والو اللہ کی عبادت کرو اس کے علاوہ تمہارا دوسرا خدا نہیں ہے کیا تم ڈرتے نہیں ہو'),
(1020, 7, 66, 'قوم میں سے کفر اختیار کرنے والے رؤسا نے کہا کہ ہم تم کو حماقت میں مبتلا دیکھ رہے ہیں اور ہمارے خیال میں تم جھوٹوں میں سے ہو'),
(1021, 7, 67, 'انہوں نے جواب دیا کہ مجھ میں حماقت نہیں ہے بلکہ میں ربّ العالمین کی طرف سے فرستادہ رسول ہوں'),
(1022, 7, 68, 'میں تم تک اپنے رب کے پیغامات پہنچاتا ہوں اور تمہارے لئے ایک امانتدار ناصح ہوں'),
(1023, 7, 69, 'کیا تم لوگوں کو اس بات پر تعجب ہے کہ تم تک ذکر خدا تمہارے ہی کسی آدمی کے ذریعہ آجائے تاکہ وہ تمہیں ڈرائے اور یاد کرو کہ تم کو اس نے قوم نوح علیھ السّلامکے بعد زمین میں جانشین بنایا ہے اور مخلوقات میں وسعت عطا کی ہے لہذا تم اللہ کی نعمتوں کو یاد کرو کہ شاید اسی طرح فلاح اور نجات پاجاؤ'),
(1024, 7, 70, 'ان لوگوں نے کہا کہ کیا آپ یہ پیغام لائے ہیں کہ ہم صرف ایک خدا کی عبادت کریں اور جن کی ہمارے آباؤ و اجداد عبادت کرتے تھے ان سب کو چھوڑ دیں تو آپ اپنے وعدہ و عید کو لے کر آئیے اگر اپنی بات میں سچے ّہیں'),
(1025, 7, 71, 'انہوں نے جواب دیا کہ تمہارے اوپر پروردگا رکی طرف سے عذاب اور غضب طے ہوچکا ہے -کیا تم مجھ سے ان ناموں کے بارے میں جھگڑا کرتے ہو جو تم نے اور تمہارے بزرگوں نے خود طے کرلئے ہیں اور خدا نے ان کے بارے میں کوئی برہان نازل نہیں کیا ہے تو اب تم عذاب کا انتظار کرو میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں'),
(1026, 7, 72, 'پھر ہم نے انہیں اور ان کے ساتھیوں کو اپنی رحمت سے نجات دے دی اور اپنی آیات کی تکذیب کرنے والوں کی نسل منقطع کردی اور وہ لوگ ہرگز صاحبان هایمان نہیں تھے'),
(1027, 7, 73, 'اور ہم نے قوم ثمود علیھ السّلام کی طرف ان کے بھائی صالح علیھ السّلامکو بھیجا تو انہوں نے کہا کہ اے قوم والو اللہ کی عبادت کرو کہ اس کے علاوہ کوئی خدا نہیں ہے -تمہارے پاس پروردگار کی طرف سے دلیل آچکی ہے -یہ خدائی ناقہ ہے جو تمہارے لئے اس کی نشانی ہے -اسے آزاد چھوڑ دو کہ زمینِ خدا میں کھاتا پھرے اور خبردار اسے کوئی تکلیف نہ پہنچانا کہ تم کو عذاب الیم اپنی گرفت میں لے لے'),
(1028, 7, 74, 'اس وقت کو یاد کرو جب اس نے تم کو قوم عاد علیھ السّلام کے بعد جانشین بنایا اور زمین میںاس طرح بسایا کہ تم ہموار زمینوں میں قصر بناتے تھے اور پہاڑوں کو کاٹ کاٹ کر گھر بناتے تھے تو اب اللہ کی نعمتوں کو یاد کرو اور زمین میں فساد نہ پھیلاتے پھرو'),
(1029, 7, 75, 'تو ان کی قوم کے بڑے لوگوں نے کمزور بنادئیے جانے والے لوگوں میں سے جو ایمان لائے تھے ان سے کہنا شروع کیا کہ کیا تمہیں اس کا یقین ہے کہ صالح علیھ السّلامخدا کی طرف سے بھیجے گئے ہیں انہوں نے کہا کہ بیشک ہمیں ان کے پیغام کا ایمان اور ایقان حاصل ہے'),
(1030, 7, 76, 'تو بڑے لوگوں نے جوابد یا کہ ہم تو ان باتوں کے منکر ہیں جن پر تم ایمان لانے والے ہو'),
(1031, 7, 77, 'پھر یہ کہہ کر ناقہ کے پاؤں کاٹ دئیے اور حکم خدا سے سرتابی کی اور کہا کہ صالح علیھ السّلاماگر تم خدا کے رسول ہو تو جس عذاب کی دھمکی دے رہے تھے اسے لے آؤ'),
(1032, 7, 78, 'تو انہیں زلزلہ نے اپنی گرفت میں لے لیاکہ اپنے گھر ہی میں سربہ زانو رہ گئے'),
(1033, 7, 79, 'تو اس کے بعد صالح علیھ السّلامنے ان سے منہ پھیر لیا اور کہا کہ اے قوم میں نے خدائی پیغام کو پہنچایا تم کو نصیحت کی مگر افسوس کہ تم نصیحت کرنے والوں کو دوست نہیں رکھتے ہو'),
(1034, 7, 80, 'اور لوط علیھ السّلام کو یاد کرو کہ جب انہوں نے اپنی قوم سے کہا کہ تم بدکاری کرتے ہو اس کی تو تم سے پہلے عالمین میں کوئی مثال نہیں ہے'),
(1035, 7, 81, 'تم ازراسِ شہوت عورتوں کے بجائے مردوں سے تعلقات پیدا کرتے ہو اور تم یقینا اسراف اور زیادتی کرنے والے ہو'),
(1036, 7, 82, 'اور ان کی قوم کے پاس کوئی جواب نہ تھا سوائے اس کے کہ انہوں نے لوگوں کو ابھارا کہ انہیں اپنے قریہ سے نکال باہر کرو کہ یہ بہت پاک باز بنتے ہیں'),
(1037, 7, 83, 'تو ہم نے انہیں اور ان کے تمام اہل کو نجات دے دی ان کی زوجہ کے علاوہ کہ وہ پیچھے رہ جانے والوں میں سے تھی'),
(1038, 7, 84, 'ہم نے ان کے اوپر خاص قسم کے (پتھروں)کی بارش کی تو اب دیکھو کہ مجرمین کا انجام کیسا ہوتا ہے'),
(1039, 7, 85, 'اور ہم نے قوم مدین کی طرف ان کے بھائی شعیب علیھ السّلامکو بھیجا تو انہوں نے کہا کہ اللہ کی عبادت کرو کہ اس کے علاوہ کوئی خدا نہیں ہے - تمہارے پاس پروردگار کی طرف سے دلیل آچکی ہے اب ناپ تول کو پورا پورا رکھو اور لوگوں کو چیزیں کم نہ دو اور اصلاح کے بعد زمین میں فساد برپا نہ کرو -یہی تمہارے حق میں بہتر ہے اگر تم ایمان لانے والے ہو'),
(1040, 7, 86, 'اورخبردار ہر راستہ پر بیٹھ کر ایمان والوں کو ڈرانے دھمکانے اور راہ هخدا سے روکنے کا کام چھوڑ دو کہ تم اس میں بھی کجی تلاش کررہے ہو اور یہ یاد کرو کہ تم بہت کم تھے خدا نے کثرت عطا کی اور یہ دیکھو کہ فساد کرنے والوں کا انجام کیا ہوتا ہے'),
(1041, 7, 87, 'اور اگر تم میں سے ایک جماعت میرے لائے ہوئے پیغام پر ایمان لے آئی اور ایک ایمان نہیں لائی تو ٹھہرو یہاں تک کہ خدا ہمارے درمیان اپنا فیصلہ صادر کردے کہ وہ بہترین فیصلہ کرنے والا ہے'),
(1042, 7, 88, 'ان کی قوم کے مستکبرین نے کہا کہ اے شعیب علیھ السّلامہم تم کو اور تمہارے ساتھ ایمان لانے والوں کو اپنی بستی سے نکال باہر کریں گے یا تم بھی پلٹ کر ہمارے مذہب پر آجاؤ .... انہوں نے جواب دیا کہ چاہے ہم تمہارے مذہب سے بیزار ہی کیوں نہ ہوں'),
(1043, 7, 89, 'یہ اللہ پر بڑا بہتان ہوگا اگر ہم تمہارے مذہب پرآجائیں جب کہ اس نے ہم کو اس مذہب سے نجات دے دی ہے اور ہمیں حق نہیں ہے کہ ہم تمہارے مذہب پر آجائیں جب تک خدا خود نہ چاہے .ہمارے پروردگار کا علم ہر شے پر حاوی ہے اور ہمارا اعتماد اسی کے اوپر ہے -خدایا تو ہمارے اور ہماری قوم کے درمیان حق سے فیصلہ فرمادے کہ تو بہترین فیصلہ کرنے والا ہے'),
(1044, 7, 90, 'تو ان کی قوم کے کفاّر نے کہا کہ اگر تم لوگ شعیب علیھ السّلامکا اتباع کرلو گے تو تمہارا شمار خسارہ والوں میں ہوجائے گا'),
(1045, 7, 91, 'نتیجہ یہ ہوا کہ انہیں زلزلہ نے اپنی گرفت میں لے لیا اور اپنے گھر میں سربہ زانو ہوگئے'),
(1046, 7, 92, 'جن لوگوں نے شعیب علیھ السّلامکی تکذیب کی وہ ایسے برباد ہوئے گویا اس بستی میں بسے ہی نہیں تھے -جن لوگوں نے شعیب علیھ السّلامکو جھٹلایا وہی خسارہ والے قرار پائے'),
(1047, 7, 93, 'اس کے بعد شعیب علیھ السّلامنے ان سے منہ پھیر لیا اور کہا کہ اے قوم میں نے اپنے پروردگار کے پیغامات کو پہنچادیا اور تمہیں نصیحت بھی کی تو اب کفر اختیار کرنے والوں کے حال پر کس طرح افسوس کروں'),
(1048, 7, 94, 'اور ہم نے جب بھی کسی قریہ میں کوئی نبی بھیجا تو اہل قریہ کو نافرمانی پر سختی اور پریشانی میں ضرور مبتلا کیا کہ شاید وہ لوگ ہماری بارگاہ میں تضرع و زاری کریں'),
(1049, 7, 95, 'پھر ہم نے برائی کی جگہ اچھائی دے دی یہاں تک کہ وہ لوگ بڑھ نکلے اور کہنے لگے کہ یہ تکلیف و راحت تو ہمارے بزرگوں تک بھی آچکی ہے تو ہم نے اچانک انہیں اپنی گرفت میں لے لیا اور انہیں اس کا شعور بھی نہ ہوسکا'),
(1050, 7, 96, 'اور اگر اہل قریہ ایمان لے آتے اور تقوٰی اختیار کرلیتے تو ہم ان کے لئے زمین اور آسمان سے برکتوں کے دروازے کھول دیتے لیکن انہوں نے تکذیب کی تو ہم نے ان کو ان کے اعمال کی گرفت میں لے لیا'),
(1051, 7, 97, 'کیا اہلِ قریہ اس بات سے مامون ہیں کہ یہ سوتے ہی رہیں اور ہمارا عذاب راتوں رات نازل ہوجائے'),
(1052, 7, 98, 'یا اس بات سے مطمئن ہیں کہ یہ کھیل کود میں مصروف رہیں اور ہمارا عذاب دن دھاڑے نازل ہوجائے'),
(1053, 7, 99, 'کیا یہ لوگ خدائی تدبیر کی طرف سے مطمئن ہوگئے ہیں جب کہ ایسا اطمینان صرف گھاٹے میں رہنے والوں کو ہوتا ہے'),
(1054, 7, 100, 'کیا ایک قوم کے بعد دوسرے زمین کے وارث ہونے والوں کو یہ ہدایت نہیں ملتی کہ اگر ہم چاہتے تو ان کے گناہوں کی بنا پر انہیں بھی مبتلائے مصیبت کردیتے اور ان کے دلوں پر مہرلگادیتے اور پھر انہیں کچھ سنائی نہ دیتا'),
(1055, 7, 101, 'یہ وہ بستیاں ہیں جن کی خبریں ہم آپ سے بیان کررہے ہیں کہ ہمارے پیغمبران کے پاس معجزات لے کر آئے مگر پہلے سے تکذیب کرنے کی بنا پر یہ ایمان نہ لاسکے .ہم اسی طرح کافروں کے دلوں پر مہر لگادیا کرتے ہیں'),
(1056, 7, 102, 'ہم نے ان کی اکثریت میں عہدو پیمان کی پاسداری نہیں پائی اور ان کی اکثریت کو فاسق اور حدود اطاعت سے خارج ہی پایا'),
(1057, 7, 103, 'پھر ان سب کے بعد ہم نے موسٰی علیھ السّلامکو اپنی نشانیاں دے کر فرعون اور اس کی قوم کی طرف بھیجا تو ان لوگوں نے بھی ظلم کیا تو اب دیکھو کہ فساد کرنے والوں کا انجام کیا ہوتا ہے'),
(1058, 7, 104, 'اور موسٰی علیھ السّلامنے فرعون سے کہا کہ میں رب العالمین کی طرف سے فرستادہ پیغمبر ہوں'),
(1059, 7, 105, 'میرے لئے لازم ہے کہ میں خدا کے بارے میں حق کے علاوہ کچھ نہ کہوں -میں تیرے پاس تیرے رب کی طرف سے معجزہ لے کر آیا ہوں لہٰذا بنی اسرائیل کو میرے ساتھ بھیج دے'),
(1060, 7, 106, 'اس نے کہا کہ اگر تم معجزہ لائے ہو اور اپنی بات میں سچےّ ہو تو وہ معجزہ پیش کرو'),
(1061, 7, 107, 'موسٰی علیھ السّلامنے اپنا عصا پھینک دیا اور وہ اچھا خاصا سانپ بن گیا'),
(1062, 7, 108, 'اور پھر اپنے ہاتھ کو نکالا تو وہ دیکھنے والوں کے لئے انتہائی روشن اور چمکدار تھا'),
(1063, 7, 109, 'فرعون کی قوم کے رؤسا نے کہا کہ یہ تو سمجھدار جادوگر ہے'),
(1064, 7, 110, 'جو تم لوگوں کو تمہاری سرزمین سے نکالنا چاہتاہے اب تم لوگوں کا کیا خیال ہے'),
(1065, 7, 111, 'لوگوں نے کہا کہ ان کو اور ان کے بھائی کو روک لیجئے اور مختلف شہروں میں جمع کرنے والوں کو بھیجئے'),
(1066, 7, 112, 'جو تمام ماہر جادوگروں کو بلا کر لے آئیں'),
(1067, 7, 113, 'جادوگر فرعون کے پاس حاضر ہو گئے اور انہوں نے کہا کہ اگر ہم غالب آگئے تو کیا ہمیں اس کی اجرت ملے گی'),
(1068, 7, 114, '. فرعون نے کہا بیشک تم میرے دربار میں مقرب ہوجاؤ گے'),
(1069, 7, 115, 'ان لوگوں نے کہا کہ موسٰی علیھ السّلامآپ عصاپھیکنیں گے یا ہم اپنے کام کا آغاز کریں'),
(1070, 7, 116, 'موسٰی علیھ السّلامنے کہا کہ تم ابتدا کرو -ان لوگوں نے رسیاں پھینکیں تو لوگوں کی آنکھوں پر جادو کردیا اور انہیں خوفزدہ کردیا اور بہت بڑے جادو کا مظاہرہ کیا'),
(1071, 7, 117, 'اور ہم نے موسٰی علیھ السّلامکو اشارہ کیا کہ اب تم بھی اپنا عصا ڈال دو وہ ان کے تمام جادو کے سانپوں کو نگل جائے گا'),
(1072, 7, 118, 'نتیجہ یہ ہوا کہ حق ثابت ہوگیا اور ان کا کاروبار باطل ہوگیا'),
(1073, 7, 119, 'وہ سب مغلوب ہوگئے اور ذلیل ہوکر واپس ہوگئے'),
(1074, 7, 120, 'اور جادوگر سب کے سب سجدہ میں گر پڑے'),
(1075, 7, 121, 'ان لوگوں نے کہا کہ ہم عالمین کے پروردگار پر ایمان لے آئے'),
(1076, 7, 122, 'یعنی موسٰی علیھ السّلاماور ہارون علیھ السّلامکے رب پر'),
(1077, 7, 123, 'فرعون نے کہا کہ تم میری اجازت سے پہلے کیسے ایمان لے آئے .... یہ تمہارا مکر ہے جو تم شہر میں پھیلارہے ہو تاکہ لوگوں کو شہر سے باہر نکال سکو تو عنقریب تمہیں اس کا انجام معلوم ہوجائے گا'),
(1078, 7, 124, 'میں تمہارے ہاتھ اور پاؤں مختلف سمتوں سے کاٹ دوں گا اور اس کے بعد تم سب کو سولی پر لٹکادوں گا'),
(1079, 7, 125, 'ان لوگوں نے جواب دیا کہ ہم لوگ بہرحال اپنے پروردگار کی بارگاہ میں پلٹ کر جانے والے ہیں'),
(1080, 7, 126, 'اور تو ہم سے صرف اس بات پر ناراض ہے کہ ہم اپنے رب کی نشانیوں پر ایمان لے آئے ہیں .... خدایا ہم پر صبر کی بارش فرما اور ہمیں مسلمان دنیا سے اٹھانا'),
(1081, 7, 127, 'اور فرعون کی قوم کے ایک گروہ نے کہا کہ کیا تو موسٰی علیھ السّلاماور ان کی قوم کو یوں ہی چھوڑ دے گا کہ یہ زمین میں فساد برپا کریں اور تجھے اور تیرے خداؤں کو چھوڑ دیں -اس نے کہا کہ میں عنقریب ان کے لڑکوں کو قتل کر ڈالوں گا اور ان کی عورتوں کو زندہ رکھوں گا .میں ان پر قوت اور غلبہ رکھتا ہوں'),
(1082, 7, 128, 'موسٰی علیھ السّلامنے اپنی قوم سے کہا کہ اللہ سے مدد مانگو اور صبر کرو -زمین اللہ کی ہے وہ اپنے بندوں میں جس کو چاہتا ہے وارث بناتا ہے اور انجام کار بہرحال صاحبان هتقوٰی کے لئے ہے'),
(1083, 7, 129, 'قوم نے کہا کہ ہم تمہارے آنے سے پہلے بھی ستائے گئے اور تمہارے آنے کے بعد بھی ستائے گئے -موسٰی علیھ السّلامنے جواب دیا کہ عنقریب تمہارا پروردگار تمہارے دشمن کو ہلاک کردے گا اور تمہیں زمین میں اس کا جانشین بنادے گا اور پھر دیکھے گا تمہارا طرز عمل کیسا ہوتا ہے'),
(1084, 7, 130, 'اور ہم نے آل فرعون کو قحط اور ثمرات کی کمی کی گرفت میں لے لیا کہ شاید وہ اسی طرح نصیحت حاصل کرسکیں'),
(1085, 7, 131, 'اس کے بعد جب ان کے پاس کوئی نیکی آئی تو انہوں نے کہا کہ یہ تو ہمارا حق ہے اور جب برائی آئی تو کہنے لگے کہ یہ موسٰی علیھ السّلاماور ان کے ساتھیوں کا اثر ہے -آگاہ ہوجاؤ کہ ان کی بدشگونی کے اسباب خدا کے یہاں معلوم ہیں لیکن ان کی اکثریت اس رازسے بے خبر ہے'),
(1086, 7, 132, 'اور قوم والوں نے کہاکہ موسٰی علیھ السّلامتم کتنی ہی نشانیاں جادو کرنے کے لئے لاؤ ہم تم پر ایمان لانے والے نہیں ہیں'),
(1087, 7, 133, 'پھر ہم نے ان پر طوفان ,ٹڈی ,جوں ,مینڈک اور خون کو مفصل نشانی بناکر بھیجا لیکن ان لوگوں نے استکبار اور انکار سے کام لیا اور یہ لوگ واقعا مجرم لوگ تھے'),
(1088, 7, 134, 'اور جب ان پر عذاب نازل ہوگیا تو کہنے لگے کہ موسٰی علیھ السّلام اپنے رب سے دعا کرو جس بات کا اس نے وعدہ کیا ہے اگر تم نے اس عذاب کو دور کرادیاتو ہم تم پر ایمان بھی لائیں گے اور بنی اسرائیل کو تمہارے حوالے بھی کردیں گے'),
(1089, 7, 135, 'اس کے بعد جب ہم نے ایک مّدت کے لئے عذاب کو برطرف کردیا تو پھر اپنے عہد کو توڑنے والوں میں شامل ہوگئے'),
(1090, 7, 136, 'پھر ہم نے ان سے انتقام لیا اور انہیں دریا میں غرق کردیا کہ انہوں نے ہماری آیات کو جھٹلایا تھا اور ان کی طرف سے غفلت برتنے والے تھے'),
(1091, 7, 137, 'اور ہم نے مستضعفین کو شرق و غرب زمین کا وارث بنادیااور اس میں برکت عطا کردی اور اس طرح بنی اسرائیل پر اللہ کی بہترین بات تمام ہوگئی کہ انہوں نے صبر کیاتھا اور جو کچھ فرعون اور اس کی قوم والے بنارہے تھے ہم نے سب کو برباد کردیا اور ان کی اونچی اونچی عمارتوں کو مسمار کردیا'),
(1092, 7, 138, 'اور ہم نے بنی اسرائیل کو دریا پار پہنچا دیا تو وہ ایک ایسی قوم کے پاس پہنچے جو اپنے بتوں کے گرد مجمع لگائے بیٹھی تھی -ان لوگوں نے موسٰی علیھ السّلامسے کہا کہ موسٰی علیھ السّلامہمارے لئے بھی ایسا ہی خدا بنادو جیسا کہ ان کا خدا ہے انہوں نے کہا کہ تم لوگ بالکل جاہل ہو'),
(1093, 7, 139, 'ان لوگوںکا نظام برباد ہونے والا اور ان کے اعمال باطل ہیں'),
(1094, 7, 140, 'کیا میں خدا کے علاوہ تمہارے لئے دوسرا خدا تلاش کروں جب کہ اس نے تمہیں عالمین پر فضیلت دی ہے'),
(1095, 7, 141, 'اور جب ہم نے تم کو فرعون والوں سے نجات دی جو تمہیں بدترین عذاب میں مبتلا کررہے تھے تمہارے لڑکوں کو قتل کررہے تھے اور لڑکیوں کو خدمت کے لئے باقی رکھ رہے تھے اور اس میں تمہارے لئے پروردگار کی طرف سے سخت ترین امتحان تھا'),
(1096, 7, 142, 'اور ہم نے موسٰی علیھ السّلامسے تیس راتوں کا وعدہ لیا اور اسے دس مزید راتوں سے مکمل کردیا کہ اس طرح ان کے رب کا وعدہ چالیس راتوں کا وعدہ ہوگیا اور انہوں نے اپنے بھائی ہارون علیھ السّلامسے کہا کہ تم قوم میں میری نیابت کرو اور اصلاح کرتے رہو اور خبردار مفسدوں کے راستہ کا اتباع نہ کرنا'),
(1097, 7, 143, 'تو اس کے بعد جب موسٰی علیھ السّلامہمارا وعدہ پورا کرنے کے لئے آئے اور ان کے رب نے ان سے کلام کیا تو انہوں نے کہا کہ پروردگار مجھے اپنا جلوہ دکھادے ارشاد ہوا تم ہرگز مجھے نہیں دیکھ سکتے ہو البتہ پہاڑ کی طرف دیکھو اگر یہ اپنی جگہ پر قائم رہ گیا تو پھر مجھے دیکھ سکتے ہو .اس کے بعد جب پہاڑ پر پروردگار کی تجلی ہوئی تو پہاڑ چور چور ہوگیا اور موسٰی علیھ السّلام بیہوش ہوکر گر پڑے پھر جب انہیں ہوش آیا تو کہنے لگے کہ پروردگار تو پاک و پاکیزہ ہے میں تیری بارگاہ میں توبہ کرتا ہوں اور میں سب سے پہلا ایمان لانے والا ہوں'),
(1098, 7, 144, 'ارشاد ہوا کہ موسٰی علیھ السّلامہم نے تمام انسانوں میں اپنی رسالت اور اپنے کلام کے لئے تمہارا انتخاب کیا ہے لہذا اب اس کتاب کو لے لو اور اللہ کے شکر گذار بندوں میں ہوجاؤ'),
(1099, 7, 145, 'اور ہم نے توریت کی تختیوں میں ہر شے میں سے نصیحت کاحصہ اور ہر چیز کی تفصیل لکھ دی ہے لہذا اسے مضبوطی کے ساتھ پکڑلو اور اپنی قوم کوحکم دو کہ اس کی اچھی اچھی باتوں کو لے لیں. میں عنقریب تمہیں فاسقین کے گھردکھلادوں گا'),
(1100, 7, 146, 'میں عنقریب اپنی آیتوں کی طرف سے ان لوگوں کو پھیر دوں گا جو روئے زمین میں ناحق اکڑتے پھرتے ہیں اور یہ کسی بھی نشانی کو دیکھ لیں ایمان لانے والے نہیں ہیں -ان کا حال یہ ہے کہ ہدایت کا راستہ دیکھیں گے تو اسے اپنا راستہ نہ بنائیں گے اور گمراہی کا راستہ دیکھیں گے تو اسے فورا اختیار کرلیں گے یہ سب اس لئے ہے کہ انہوں نے ہماری نشانیوں کو جھٹلایا ہے اوران کی طرف سے غافل تھے'),
(1101, 7, 147, 'اور جن لوگوں نے ہماری نشانیوں اور آخرت کی ملاقات کو جھٹلایا ہے ان کے اعمال برباد ہیں اور ظاہر ہے کہ انھیں ویساہی بدلہ تو دیا جائے گا جیسے اعمال کررہے ہیں'),
(1102, 7, 148, 'اور موسٰی علیھ السّلامکی قوم نے ان کے بعد اپنے زیورات سے گوسالہ کا مجسمہ بنایا جس میں آواز بھی تھی کیاان لوگوں نے نہیں دیکھا کہ وہ نہ بات کرنے کے لائق ہے اور نہ کوئی راستہ دکھا سکتا ہے انہوں نے اسے خدا بنالیا اور وہ لوگ واقعاظلم کرنے والے تھے'),
(1103, 7, 149, 'اور جب وہ پچھتائے اور انہوں نے دیکھ لیا کہ وہ بہک گئے ہیں تو کہنے لگے کہ اگر ہمارا پروردگار ہمارے او پر رحم نہ کرے گا اور ہمیں معاف نہ کرے گا تو ہم خسارہ والوں میں شامل ہوجائیں گے'),
(1104, 7, 150, 'اورجب موسٰی علیھ السّلاماپنی قوم کی طرف واپس آئے تو غیظ و غضب کے عالم میں کہنے لگے کہ تم نے میرے بعد بہت بفِی حرکت کی ہے -تم نے حکم خدا کے آنے میں کس قدر عجلت سے کام لیا ہے اور پھر انہوں نے توریت کی تختیوں کو ڈال دیا اور اپنے بھائی کا سرپکڑ کر کھینچنے لگے -ہارون علیھ السّلامنے کہا بھائی قوم نے مجھے کمزور بنادیا تھا اور قریب تھاکہ مجھے قتل کردیتے تو میں کیا کرتا -آپ دشمنوں کو طعنہ کا موقع نہ دیجئے اور میرا شمار ظالمین کے ساتھ نہ کیجئے'),
(1105, 7, 151, 'موسٰی علیھ السّلامنے کہا کہ پروردگار مجھے اور میرے بھائی کو معاف کردے اور ہمیں اپنی رحمت میں داخل کرلے کہ تو سب سے زیادہ رحم کرنے والاہے'),
(1106, 7, 152, 'بیشک جن لوگوں نے گوسالہ کو اختیار کیا ہے عنقریب ان پر غضب پروردُگار نازل ہوگا اور ان کے لئے زندگانی دنیا میں بھی ذلّت ہے اور ہم اسی طرح افترا کرنے والوں کوسزا دیا کرتے ہیں'),
(1107, 7, 153, 'اور جن لوگوں نے بفِے اعمال کئے اور پھر توبہ کرلی اور ایمان لے آئے تو توبہ کے بعد تمھارا پروردگار بہت بخشنے والا بڑا مہربان ہے'),
(1108, 7, 154, 'اس کے بعد جب موسٰی کا غّصہ ٹھنڈا پڑگیا تو انہوں نے تختیوں کو اٹھالیا اور اس کے نسخہ میں ہدایت اور رحمت کی باتیں تھیں ان لوگوں کے لئے جو اپنے پروردگار سے ڈرنے والے تھے'),
(1109, 7, 155, 'اور موسٰی علیھ السّلامنے ہمارے وعدہ کے لئے اپنی قوم کے ستر ّ افراد کا انتخاب کیا پھر اس کے بعد جب ایک جھٹکے نے انھیں اپنی لپیٹ میں لے لیا تو کہنے لگے کہ پرودگار اگر تو چاہتا تو انھیں پہلے ہی ہلاک کردیتا اور مجھے بھی -کیا اب احمقوں کی حرکت کی بناپرہمیں بھی ہلاک کردے گا یہ تو صرف تیرا امتحان ہے جس سے جس کو چاہتا ہے گمراہی میں چھوڑدیتا ہے اور جس کو چاہتا ہے ہدایت دے دیتا ہے تو ہمارا ولی ہے -ہمیں معاف کردے اورہم پر رحم فرما کہ تو بڑا بخشنے والا ہے'),
(1110, 7, 156, 'اور ہمارے لئے اس دار دنیا اور آخرت میں نیکی لکھ دے -ہم تیری ہی طرف رجوع کررہے ہیں -ارشاد ہوا کہ میرا عذاب جسے میں چاہوں گا اس تک پہنچے گا اور میری رحمت ہر شے پر وسیع ہے جسے میں عنقریب ان لوگوں کے لئے لکھ دوں گا جو خوف خدا رکھنے والے -زکوِٰادا کرنے والے اور ہماری نشانیوں پر ایمان لانے والے ہیں'),
(1111, 7, 157, 'جو لوگ کہ رسولِ نبی امّی کا اتباع کرتے ہیں جس کا ذکر اپنے پاس توریت اور انجیل میں لکھا ہوا پاتے ہیں کہ وہ نیکیوں کا حکم دیتا ہے اور برائیوں سے روکتا ہے اور پاکیزہ چیزوں کو حلال قرار دیتاہے اور خبیث چیزوں کو حرام قرار دیتا ہے اور ان پر سے احکام کے سنگین بوجھ اور قیدوبند کو اٹھا دیتا ہے پس جو لوگ اس پر ایمان لائے اس کا احترام کیا اس کی امداد کی اور اس نور کا اتباع کیا جو اس کے ساتھ نازل ہوا ہے وہی درحقیقت فلاح یافتہ اور کامیاب ہیں'),
(1112, 7, 158, 'پیغمبر ----- کہہ دو کہ میں تم سب کی طرف اس اللہ کا رسول اور نمائندہ ہوں جس کے لئے زمین وآسمان کی مملکت ہے-اس کے علاوہ کوئی خدا نہیں ہے -وہی حیات دیتا ہے اور وہی موت دیتاہے لہٰذا اللہ اور اس کے پیغمبرامی ّپر ایمان لے آؤ جو اللہ اور اس کے کلمات پر ایمان رکھتا ہے اور اسی کا اتباع کرو کہ شاید اسی طرح ہدایت یافتہ ہوجاؤ'),
(1113, 7, 159, 'اور موسٰی علیھ السّلام کی قوم میں سے ایک ایسی جماعت بھی ہے جو حق کے ساتھ ہدایت کرتی ہے اور معاملات میں حق وانصاف کے ساتھ کام کرتی ہے'),
(1114, 7, 160, 'اور ہم نے بنی اسرائیل کو یعقوب علیھ السّلام کی بارہ اولاد کے بارہ حصّو ں پر تقسیم کردیا اور موسٰی علیھ السّلام کی طرف وحی کی جب ان کی قوم نے پانی کا مطالبہ کیا کہ زمین پر عصا ماردو -انہوں نے عصا ماراتو بارہ چشمے جاری ہوگئے اس طرح کہ ہر گردہ نے اپنے گھاٹ کو پہچان لیا اور ہم نے ان کے سروں پر ابر کا سایہ کیااور ان پر من و سلوٰی جیسی نعمت نازل کی کہ ہمارے دیئے ہوئے پاکیزہ رزق کو کھاؤ اور ان لوگوں نے مخالفت کرکے ہمارے اوپر ظلم نہیں کیا بلکہ یہ اپنے ہی نفس پر ظلم کررہے تھے'),
(1115, 7, 161, 'اور اس وقت کو یاد کرو جب ان سے کہاگیا کہ اس قریہ میں داخل ہوجاؤ اور جو چاہو کھاؤ لیکن حطّہ کہہ کر داخل ہونا اور داخل ہوتے وقت سجدہ کرتے ہوئے داخل ہونا تاکہ ہم تمھاری خطاؤں کو معاف کردیں کہ ہم عنقریب نیک عمل والوں کے اجر میںاضافہ بھی کردیں گے'),
(1116, 7, 162, 'لیکن ظالموں نے جو انھیں بتایا گیا تھا اس کو بدل کرکچھ اور کہنا شروع کردیا تو ہم نے ان کے اوپر آسمان سے عذاب نازل کردیا کہ یہ فسق اور نافرمانی کررہے تھے'),
(1117, 7, 163, 'اور ان سے اس قریہ کے بارے میں پوچھو جو سمندر کے کنارے تھا اور جس کے باشندے شنبہ کے بارے میں زیادتی سے کام لیتے تھے کہ ان کی مچھلیاں شنبہ کے دن سطح آب تک آجاتی تھیں اور دوسرے دن نہیں آتی تھیں تو انہوں نے حیلہ گری کرنا شروع کردی -ہم اسی طرح ان کا امتحان لیتے تھے کہ یہ لوگ فسق اور نافرمانی سے کام لے رہے تھے'),
(1118, 7, 164, 'اور جب ان کی ایک جماعت نے مصلحین سے کہا کہ تم کیوں ایسی قوم کو نصیحت کرتے ہو جسے اللہ ہلاک کرنے والا ہے یا اس پرشدید عذاب کرنے والا ہے تو انہوں نے کہا کہ ہم پروردگار کی بارگاہ میں عذرچاہتے ہیں اور شاید یہ لوگ متقی بن ہی جائیں'),
(1119, 7, 165, 'اس کے بعد جب انہوں نے یاد دہانی کو فراموش کردیا تو ہم نے برائیوں سے روکنے والوں کو بچالیا اور ظالموں کو ان کے فسق اور بدکرداری کی بنا پرسخت ترین عذاب کی گرفت میں لے لیا'),
(1120, 7, 166, 'پھر جب دوبارہ ممانعت کے باوجود سرکشی کی تو ہم نے حکم دے دیا کہ اب ذلّت کے ساتھ بندر بن جاؤ'),
(1121, 7, 167, 'اور اس وقت کو یاد کرو جب تمھارے پروردگار نے علی الاعلان کہہ دیا کہ قیامت تک ان پر ایسے افراد مسلّط کئے جائیں گے جو انھیں بدترین سختیوں میں مبتلا کریں گے کہ تمھارا پروردگار جلدی عذاب کرنے والا بھی ہے اور بہت زیادہ بخشنے والا مہربان بھی ہے'),
(1122, 7, 168, 'اورہم نے بنیاسرائیل کو مختلف ٹکڑوں میں تقسیم کردیا بعض نیک کردار تھے اور بعض اس کے خلاف -اور ہم نے انھیں آرام اور سختی کے ذریعہ آزمایا کہ شاید راستہ پر آجائیں'),
(1123, 7, 169, 'اس کے بعد ان میں ایک نسل پیدا ہوئی جو کتاب کی وراث تو بنی لیکن دنیا کا ہر مال لیتی رہی اور یہ کہتی رہی کہ عنقریب ہمیں بخش دیا جائے گا اور پھر ویسا ہی مال مل گیا تو پھر لے لیا تو کیا ان سے کتاب کا عہد نہیں لیا گیا کہ خبردار خدا کے بارے میں حق کے علاوہ کچھ نہ کہیں اور انہوں نے کتاب کو پڑھا بھی ہے اور دار آخرت ہی صاحبان هتقویٰ کے لئے بہترین ہے کیا تمھاری سمجھ میں نہیں آتا ہے'),
(1124, 7, 170, 'اور جو لوگ کتاب سے تمسک کرتے ہیں اور انہوں نے نماز قائم کی ہے توہم صالح اورنیک کردار لوگوں کے اجر کو ضائع نہیں کرتے ہیں'),
(1125, 7, 171, 'اور اس وقت کو یاد دلِاؤ جب ہم نے پہاڑ کو ایک سائبان کی طرح ان کے سروں پر معلّق کردیا اور انہوں نے گمان کرلیا کہ یہ اب گرنے والا ہے تو ہم نے کہا کہ توریت کو مضبوطی کے ساتھ پکڑو اور جو کچھ اس میں ہے اسے یاد کرو شاید اس طرح متقی اور پرہیزگار بن جاؤ'),
(1126, 7, 172, 'اور جب تمھارے پروردگار نے فرزند ان آدم علیھ السّلامکی پشتوں سے انکی ذرّیت کو لے کر انھیںخود ان کے اوپر گواہ بناکر سوال کیا کہ کیا میں تمھارا خدا نہیں ہوں تو سب نے کہا بیشک ہم اس کے گواہ ہیں -یہ عہد اس لئے لیا کہ روز هقیامت یہ نہ کہہ سکو کہ ہم اس عہد سے غافل تھے'),
(1127, 7, 173, 'یا یہ کہہ دو کہ ہم سے پہلے ہمارے بزرگوں نے شرک کیا تھا اور ہم صرف ان کی اولاد میں تھے تو کیا اہل باطل کے اعمال کی بنا پر ہم کو ہلاک کردے گا'),
(1128, 7, 174, 'اور اسی طرح ہم آیتوں کو مفّصل بیان کرتے ہیں اور شاید یہ لوگ پلٹ کرآجائیں'),
(1129, 7, 175, 'اور انھیں اس شخص کی خبر سنائیے جس کو ہم نے اپنی آیتیں عطا کیں پھر وہ ان سے بالکل الگ ہوگیا اور شیطان نے اس کا پیچھا پکڑلیا تو وہ گمراہوں میں ہوگیا'),
(1130, 7, 176, 'اور اگر ہم چاہتے تو اسے ان ہی آیتوں کے سبب بلند کردیتے لیکن وہ خود زمین کی طرف جھک گیا اور اس نے خواہشات کی پیروی اختیار کرلی تو اب اس کی مثال کِتّے جیسی ہے کہ اس پر حملہ کرو توبھی زبان نکالے رہے اور چھوڑ دو تو بھی زبان نکالے رہے -یہ اس قوم کی مثال ہے جس نے ہماری آیات کی تکذیب کی تو اب آپ ان قصّوں کو بیان کریں کہ شاید یہ غور وفکر کرنے لگیں'),
(1131, 7, 177, 'کس قدر بفِی مثال ہے اس قوم کی جس نے ہماری آیات کی تکذیب کی اور وہ لوگ اپنے ہی نفس پرظلم کررہے تھے'),
(1132, 7, 178, 'جس کو خدا ہدایت دے دے ہدایت یافتہ ہے اور جس کو گمراہی میں چھوڑدے وہی خسارہ والوں میں ہے'),
(1133, 7, 179, 'اور یقینا ہم نے انسان و جنات کی ایک کثیر تعداد کو گویا جہّنم کے لئے پیدا کیا ہے کہ ان کے پاس دل ہیں مگر سمجھتے نہیں ہیں اور آنکھیں ہیں مگر دیکھتے نہیں ہیں اور کان ہیں مگر سنتے نہیں ہیں -یہ چوپایوں جیسے ہیں بلکہ ان سے بھی زیادہ گمراہ ہیں اور یہی لوگ اصل میں غافل ہیں'),
(1134, 7, 180, 'اور اللہ ہی کے لئے بہترین نام ہیں لہذا اسے ان ہی کے ذریعہ پکارو اور ان لوگوں کو چھوڑ دو جو اس کے ناموں میں بے دینی سے کام لیتے ہیں عنقریب انہیں ان کے اعمال کا بدلہ دیا جائے گا'),
(1135, 7, 181, 'اور ہماری مخلوقات ہی میں سے وہ قوم بھی ہے جو حق کے ساتھ ہدایت کرتی ہے اور حق ہی کے ساتھ انصاف کرتی ہے'),
(1136, 7, 182, 'اور جن لوگوں نے ہماری آیات کی تکذیب کی ہم انہیں عنقریب اس طرح لپیٹ لیں گے کہ انہیں معلوم بھی نہ ہوگا'),
(1137, 7, 183, 'اور ہم تو انہیں ڈھیل دے رہے ہیں کہ ہماری تدبیر بہت مستحکم ہوتی ہے'),
(1138, 7, 184, 'اور کیا ان لوگوں نے یہ غور نہیں کیا کہ ان کے ساتھی پیغمبر میں کسی طرح کا جنون نہیں ہے -وہ صرف واضح طور سے عذاب الٰہی سے ڈرانے والا ہے'),
(1139, 7, 185, 'اور کیا ان لوگوں نے زمین و آسمان کی حکومت اور خدا کی تمام مخلوقات میں غور نہیں کیا اور یہ کہ شاید ان کی اجل قریب آگئی ہو تو یہ اس کے بعد کس بات پر ایمان لے آئیں گے'),
(1140, 7, 186, 'جسے خدا ہی گمراہی میں چھوڑ دے اس کا کوئی ہدایت کرنے والا نہیں ہے اور وہ انہیں سرکشی میں چھوڑ دیتا ہے کہ ٹھوکریں کھاتے پھریں'),
(1141, 7, 187, 'پیغمبر !یہ آپ سے قیامت کے بارے میں سوال کرتے ہیں کہ اس کا ٹھکانا کب ہے تو کہہ دیجئے کہ اس کا علم میرے پروردگار کے پاس ہے وہی اس کو بروقت ظاہر کرے گا یہ قیامت زمین و آسمان دونوں کے لئے بہت گراں ہے اور تمہارے پاس اچانک آنے والی ہے یہ لوگ آپ سے اس طرح سوال کرتے ہیں گویا آپ کو اس کی مکمل فکر ہے تو کہہ دیجئے کہ اس کا علم اللہ کے پاس ہے لیکن اکثر لوگوں کو اس کا علم بھی نہیں ہے'),
(1142, 7, 188, 'آپ کہہ دیجئے کہ میں خود بھی اپنے نفس کے نفع و نقصان کا اختیارنہیں رکھتا ہوں مگر جو خد اچاہے اور اگر میں غیب سے باخبر ہوتا تو بہت زیادہ خیر انجام دیتا اور کوئی برائی مجھ تک نہ آسکتی -میں تو صرف صاحبان هایمان کے لئے بشارت دینے والا اور عذاب الٰہی سے ڈرنے والا ہوں'),
(1143, 7, 189, 'وہی خد اہے جس نے تم سب کو ایک نفس سے پیدا کیا ہے اور پھر اسی سے اس کا جوڑا بنایا ہے تاکہ اس سے سکون حاصل ہو اس کے بعد شوہر نے زوجہ سے مقاربت کی تو ہلکا سا حمل پید اہوا جسے وہ لئے پھرتی رہی پھر حمل بھاری ہوا اور وقت ولادت قریب آیا تو دونوں نے پروردگار سے دعا کی کہ اگر ہم کو صالح اولاد دے دے گا تو ہم تیرے شکر گزار بندوں میں ہوں گے'),
(1144, 7, 190, 'اس کے بعد جب اس نے صُالح فرزند دے دیا تو اللہ کی عطا میں اس کا شریک قرار دے دیا جب کہ خدا ان شریکوں سے کہیں زیادہ بلند و برتر ہے'),
(1145, 7, 191, 'کیا یہ لوگ انہیں شریک بناتے ہیں جو کوئی شے خلق نہیں کرسکتے اور خود بھی مخلوق ہیں'),
(1146, 7, 192, 'اور ان کے اختیار میں خود اپنی مدد بھی نہیں ہے اور وہ کسی کی نصرت بھی نہیں کرسکتے ہیں'),
(1147, 7, 193, 'اور اگر آپ انہیں ہدایت کی طرف دعوت دیں تو ساتھ بھی نہ آئیں گے ان کے لئے سب برابر ہے انہیں بلائیں یا چپ رہ جائیں'),
(1148, 7, 194, 'تم لوگ جن لوگوں کو اللہ کو چھوڑ کر پکارتے ہو سب تم ہی جیسے بندے ہیں لہذا تم انہیں بلاؤ اور وہ تمہاری آواز پر لبیک کہیں اگر تم اپنے خیال میں سچےّ ہو'),
(1149, 7, 195, 'کیا ان کے پاس چلنے کے قابل پیر -حملہ کرنے کے قابل ہاتھ ,دیکھنے کے قابل آنکھیں اور سننے کے لائق کان ہیں جن سے کام لے سکیں -آپ کہہ دیجئے کہ تم لوگ اپنے شرکائ کو بلاؤ اور جو مکر کرنا چاہتے ہو کرو اور ہرگز مجھے مہلت نہ دو (دیکھوں تم کیا کرسکتے ہو'),
(1150, 7, 196, 'بیشک میرا مالک و مختار وہ خداہے جس نے کتاب نازل کی ہے اور وہ نیک بندوں کا والی و وارث ہے'),
(1151, 7, 197, 'اور اسے چھوڑ کر تم جنہیں پکارتے ہو وہ نہ تمہاری مدد کرسکتے ہیں اور نہ اپنے ہی کام آسکتے ہیں'),
(1152, 7, 198, 'اگر تم ان کو ہدایت کی دعوت دو گے تو سن بھی نہ سکیں گے اور دیکھو گے تو ایسالگے گا جیسے تمہاری ہی طرف دیکھ رہے ہیں حالانکہ دیکھنے کے لائق بھی نہیں ہیں'),
(1153, 7, 199, 'آپ عفو کا راستہ اختیار کریں نیکی کا حکم دیں اور جاہلوں سے کنارہ کشی کریں'),
(1154, 7, 200, 'اور اگر شیطان کی طرف سے کوئی غلط خیال پیدا کیا جائے تو خدا کی پناہ مانگیں کہ وہ ہر شے کا سننے والا اور جاننے والا ہے'),
(1155, 7, 201, 'جو لوگ صاحبان هتقویٰ ہیں جب شیطان کی طرف سے کوئی خیال چھونا بھی چاہتا ہے تو خدا کو یاد کرتے ہیں اور حقائق کو دیکھنے لگتے ہیں'),
(1156, 7, 202, 'اور مشرکین کے برادران شیاطین انہیں گمراہی میں کھینچ رہے ہیں اور اس میں کوئی کوتاہی نہیں کرتے ہیں'),
(1157, 7, 203, 'اور اگر آپ ان کے پاس کوئی نشانی نہ لائیں تو کہتے ہیں کہ خود آپ کیوں نہیں منتخب کرلیتے تو کہہ دیجئے کہ میں تو صرف وحی پروردگار کا اتباع کرتا ہوں -یہ قران تمہارے پروردگار کی طرف سے دلائل ہدایت اور صاحبان هایمان کے لئے رحمت کی حیثیت رکھتا ہے'),
(1158, 7, 204, 'اور جب قرآن کی تلاوت کی جائے تو خاموش ہوکر غور سے سنو کہ شاید تم پر رحمت نازل ہوجائے'),
(1159, 7, 205, 'اور خدا کو اپنے دل ہی دل میں تضرع اور خوف کے ساتھ یاد کرو اور قول کے اعتبار سے بھی اسے کم بلند آواز سے صبح و شام یاد کرو اور خبردار غافلوں میں نہ ہوجاؤ'),
(1160, 7, 206, 'جو لوگ اللہ کی بارگاہ میں مقرب ہیں وہ اس کی عبادت سے تکبر نہیں کرتے ہیں اور اسی کی بارگاہ میں سجدہ ریز رہتے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'پیغمبر یہ لوگ آپ سے انفال کے بارے میں سوال کرتے ہیں تو آپ کہہ دیجئے کہ انفال سب اللہ اور رسول کے لئے ہیں لہٰذا تم لوگ اللہ سے ڈرو اور آپس میں اصلاح کرو اور اللہ و رسول کی اطاعت کرو اگر تم اس پر ایمان رکھنے والے ہو'),
(1162, 8, 2, 'صاحبان هایمان درحقیقت وہ لوگ ہیں جن کے سامنے ذکر خدا کیا جائے تو ان کے دلوں میں خورِ خدا پیدا ہو اور اس کی آیتوں کی تلاوت کی جائے تو ان کے ایمان میں اضافہ ہوجائے اور وہ لوگ اللہ ہی پر توکل کرتے ہیں'),
(1163, 8, 3, 'وہ لوگ نماز قائم کرتے ہیں اور ہمارے دئیے ہوئے رزق میں سے انفاق بھی کرتے ہیں'),
(1164, 8, 4, 'یہی لوگ حقیقتاصاحب هایمان ہیں اور ان ہی کے لئے پروردگار کے یہاں درجات اور مغفرت اور باعزّت روزی ہے'),
(1165, 8, 5, 'جس طرح تمہارے رب نے تمہیں تمہارے گھر سے حق کے ساتھ برآمد کیا اگرچہ مومنین کی ایک جماعت اسے ناپسند کررہی تھی'),
(1166, 8, 6, 'یہ لوگ آپ سے حق کے واضح ہوجانے کے بعد بھی اس کے بارے میں بحث کرتے ہیں جیسے کہ موت کی طرف ہنکائے جارہے ہوں اور حسرت سے دیکھ رہے ہوں'),
(1167, 8, 7, 'اور اس وقت کو یاد کرو جب کہ خدا تم سے وعدہ کررہا تھا کہ دو گروہوں میں سے ایک تمہارے لئے بہرحال ہے اور تم چاہتے تھے کہ وہ طاقت والا گروہ نہ ہو اور اللہ اپنے کلمات کے ذریعہ حق کو ثابت کرنا چاہتا ہے اور کفاّر کے سلسلہ کو قطع کردینا چاہتا ہے'),
(1168, 8, 8, 'تاکہ حق ثابت ہوجائے اور باطل فنا ہوجائے چاہے مجرمین اسے کسی قدر اِرا کیوں نہ سمجھیں'),
(1169, 8, 9, 'جب تم پروردگار سے فریاد کررہے تھے تو اس نے تمہاری فریاد سن لی کہ میں ایک ہزار ملائکہ سے تمہاری مدد کررہا ہوں جو برابر ایک کے پیچھے ایک آرہے ہیں'),
(1170, 8, 10, 'اور اسے ہم نے صرف ایک بشارت قرار دیا تاکہ تمہارے دل مطمئن ہوجائیں اور مدد تو صرف اللہ ہی کی طرف سے ہے -اللہ ہی صاحب هعزّت اور صاحب هحکمت ہے'),
(1171, 8, 11, 'جس وقت خدا تم پر نیند غالب کررہا تھا جو تمہارے لئے باعث سکون تھی اور آسمان سے پانی نازل کررہا تھا تاکہ تمہیں پاکیزہ بنادے اور تم سے شیطان کی کثافت کو دور کردے اور تمہارے دلوں کو مطمئن بنادے اور تمہارے قدموں کو ثبات عطا کردے'),
(1172, 8, 12, 'جب تمہارا پروردگار ملائکہ کو وحی کررہاتھا کہ میں تمہارے ساتھ ہوں لہٰذا تم صاحبان هایمان کو ثبات قدم عطا کرو میں عنقریب کفاّ رکے دلوں میں رعب پیداکردوں گا لہذا تم کفاّر کی گردن کو مار دو اور ان کی تمام انگلیوں کے پُور پُور کاٹ دو'),
(1173, 8, 13, 'یہ اس لئے ہے کہ ان لوگوں نے خدا و رسول کی مخالفت کی ہے اور جو خدا و رسول کی مخالفت کرے گا تو خدا اس کے لئے سخت عذاب کرنے والا ہے'),
(1174, 8, 14, 'یہ تو دنیا کی سزا ہے جسے یہاں چکھو اور اس کے بعد کافروں کے لئے جہّنمکا عذاب بھی ہے'),
(1175, 8, 15, 'اے ایمان والو جب کفاّر سے میدان هجنگ میں ملاقات کرو تو خبردار انہیں پیٹھ نہ دکھانا'),
(1176, 8, 16, 'اور جو آج کے دن پیٹھ دکھائے گا وہ غضب الٰہی کا حق دار ہوگا اور اس کا ٹھکانا جہّنم ّہوگا جو بدترین انجام ہے علاوہ ان لوگوں کے جو جنگی حکمت علمی کی بنا پر پیچھے ہٹ جائیں یا کسی دوسرے گروہ کی پناہ لینے کے لئے اپنی جگہ چھوڑ دیں'),
(1177, 8, 17, 'پس تم لوگوں نے ان کفاّر کو قتل نہیں کیا بلکہ خدا نے قتل کیا ہے اور پیغمبرآپ نے سنگریزے نہیں پھینکے ہیں بلکہ خدا نے پھینکے ہیں تاکہ صاحبان هایمان پر خوب اچھی طرح احسان کردے کہ وہ سب کی سننے والا اور سب کا حال جاننے والا ہے'),
(1178, 8, 18, 'یہ تو یہ احسان ہے اور خدا کفاّر کے مکر کو کمزور بنانے والا ہے'),
(1179, 8, 19, 'اگر تم لوگ فتح چاہتے ہو تو یہ فتح آگئی اور اگر تم جنگ سے باز آجاؤ تو اس میں تمہارے لئے بھلائی ہے اور اگر دوبارہ ایسا کرو گے تو ہم بھی پلٹ کر آرہے ہیں اور تمہارا گروہ کتنا ہی بڑ اکیوں نہ ہو کام آنے والا نہیں ہے اور اللہ ہمیشہ صاحبان هایمان کے ساتھ ہے'),
(1180, 8, 20, 'ایمان والو اللہ و رسول کی اطاعت کرو اور اس سے رو گردانی نہ کرو جب کہ تم صَن بھی رہے ہو'),
(1181, 8, 21, 'اور ان لوگوں جیسے نہ ہوجاؤ جو یہ کہتے ہیں کہ ہم نے سنا حالانکہ وہ کچھ نہیں سن رہے ہیں'),
(1182, 8, 22, 'اللہ کے نزدیک بد ترین زمین پر چلنے والے وہ بہرے اور گونگے ہیں جو عقل سے کام نہیں لیتے ہیں'),
(1183, 8, 23, 'اور اگر خدا ان میں کسی خیر کو دیکھتا تو انہیں ضرور سناتا اور اگر سنا بھی دیتا تو یہ منہ پھرا لیتے اور اعراض سے کام لیتے'),
(1184, 8, 24, 'اے ایمان والو اللہ و رسول کی آواز پر لبیک کہو جب وہ تمہیں اس امر کی طرف دعوت دیں جس میں تمہاری زندگی ہے اور یاد رکھو کہ خدا انسان اور اس کے دل کے درمیان حائل ہوجاتا ہے اور تم سب اسی کی طرف حاضر کئے جاؤ گے'),
(1185, 8, 25, 'اور اس فتنہ سے بچو جو صرف ظالمین کو پہنچنے والا نہیں ہے اور یاد رکھو کہ اللہ سخت ترین عذاب کا مالک ہے'),
(1186, 8, 26, 'مسلمانو !اس وقت کو یاد کرو جب تم مکّہ میں قلیل تعداد میں اور کمزور تھے -تم ہر آن ڈرتے تھے کہ لوگ تمہیں اچک لے جائیں گے کہ خد انے تمہیں پناہ دی اور اپنی مدد سے تمہاری تائید کی -تمہیں پاکیزہ روزی عطا کی تاکہ تم اس کا شکریہ ادا کرو'),
(1187, 8, 27, 'ایمان والو خدا و رسول اور اپنی امانتوں کے بارے میں خیانت نہ کرو جب کہ تم جانتے بھی ہو'),
(1188, 8, 28, 'اور جان لو کہ یہ تمہاری اولاد اور تمہارے اموال ایک آزمائش ہیں اور خدا کے پاس اجر عظیم ہے'),
(1189, 8, 29, 'ایمان والو اگر تم تقوٰی الٰہی اختیار کرو گے تو وہ تمہیں حق و باطل میں تفرقہ کی صلاحیت عطا کردے گا -تمہاری برائیوں کی پردہ پوشی کرے گا -تمہارے گناہوں کو معاف کردے گا کہ وہ بڑا فضل کرنے والا ہے'),
(1190, 8, 30, 'اور پیغمبرآپ اس وقت کو یاد کریں جب کفار تدبیریں کرتے تھے کہ آپ کو قید کرلیں یا شہر بدر کردیں یا قتل کردیں اور ان کی تدبیروں کے ساتھ خدا بھی اس کے خلاف انتظام کررہا تھا اور وہ بہترین انتظام کرنے والا ہے'),
(1191, 8, 31, 'اور ان کا یہ حال ہے کہ جب ہماری آیتوں کی تلاوت کی جاتی ہے تو کہتے ہیں کہ سن لیا -ہم خود بھی چاہیں تو ایسا ہی کہہ سکتے ہیں یہ تو صرف پچھلے لوگوں کی داستانیں ہیں'),
(1192, 8, 32, 'اور اس وقت کو یاد کرو جب انہوں نے کہا کہ خدایا اگر یہ تیری طرف سے حق ہے تو ہم پر آسمان سے پتھر برسادے یا عذاب الیم نازل کردے'),
(1193, 8, 33, 'حالانکہ اللہ ان پر اس وقت تک عذاب نہ کرے گا جب تک« پیغمبر مِ آپ ان کے درمیان ہیں اور خدا ان پر عذاب کرنے والا نہیں ہے اگر یہ توبہ اور استغفار کرنے والے ہوجائیں'),
(1194, 8, 34, 'اور ان کے لئے کون سی بات ہے کہ خدا ان پر عذاب نہ کرے جب کہ یہ لوگوں کو مسجد الحرام سے روکتے ہیں اور اس کے متولی بھی نہیں ہیں -اس کے ولی صرف متقی اور پرہیزگار افراد ہیں لیکن ان کی اکثریت اس سے بھی بے خبر ہے'),
(1195, 8, 35, 'ان کی تو نماز بھی مسجد الحرام کے پاس صرف تالی اور سیٹی ہے لہذا اب تم لوگ اپنے کفر کی بنا پر عذاب کا مزہ چکھو'),
(1196, 8, 36, 'جن لوگوں نے کفر اختیار کیا یہ اپنے اموال کو صرف اس لئے خرچ کرتے ہیں کہ لوگوں کو راسِ خدا سے روکیں تو یہ خرچ بھی کریں گے اور اس کے بعدیہ بات ان کے لئے حسرت بھی بنے گی اور آخر میں مغلوب بھی ہوجائیں گے اور جن لوگوں نے کفر اختیار کیا یہ سب جہّنم کی طرف لے جائے جائیں گے'),
(1197, 8, 37, 'تاکہ خدا خبیث کو پاکیزہ سے الگ کردے اور پھر خبیث کو ایک پر ایک رکھ کر ڈھیر بنادے اور سب کو اکٹھا جہّنم میں جھونک دے کہ یہی لوگ خسارہ اور گھاٹے والے ہیں'),
(1198, 8, 38, 'پیغمبر آپ کافروں سے کہہ دیجئے کہ اگر یہ لوگ اپنے کفر سے باز آجائیں تو ان کے گزشتہ گناہ معاف کردئیے جائیں گے لیکن اگر پھر پلٹ گئے تو گزشتہ لوگوں کا طریقہ بھی گزر چکا ہے'),
(1199, 8, 39, 'اور تم لوگ ان کفاّر سے جہاد کرو یہاں تک کہ فتنہ کا وجود نہ رہ جائے اور سارا دین صرف اللہ کے لئے رہ جائے پھر اگر یہ لوگ باز آجائیں تو اللہ ان کے اعمال کا خوب دیکھنے والا ہے'),
(1200, 8, 40, 'اور اگر دوبارہ پلٹ جائیں تو یاد رکھو کہ خدا تمہارا مولا اور سرپرست ہے اور وہ بہترین مولا و مالک اور بہترین مددگار ہے'),
(1201, 8, 41, 'اور یہ جان لو کہ تمہیں جس چیز سے بھی فائدہ حاصل ہو اس کا پانچواں حصہ اللہ , رسول ,رسول کے قرابتدار ,ایتام ,مساکین اور مسافران هغربت زدہ کے لئے ہے اگر تمہارا ایمان اللہ پر ہے اور اس نصرت پر ہے جو ہم نے اپنے بندے پر حق و باطل کے فیصلہ کے دن جب دو جماعتیں آپس میں ٹکرا رہی تھیں نازل کی تھی اور اللہ ہر شے پر قادر ہے'),
(1202, 8, 42, 'جب کہ تم وادی کے قریبی محاذ پر تھے اور وہ لوگ دور والے محاذ پر تھے اور قافلہ تم سے نشیب میں تھا اور اگر تم پہلے سے جہاد کے وعدہ پر نکلتے تو یقینا اس کے خلاف کرتے لیکن خدا ہونے والے امر کا فیصلہ کرنا چاہتا تھا تاکہ جو ہلاک ہو وہ دلیل کے ساتھ اور جو زندہ رہے وہ بھی دلیل کے ساتھ اور اللہ سب کی سننے والا اور سب کے حال هدل کا جاننے والا ہے'),
(1203, 8, 43, 'جب خدا ان کو تمہاری نظروں میں کم کرکے دکھلا رہا تھا کہ اگر زیادہ دکھلا دیتا تو تم سست پڑ جاتے اور آپس ہی میں جھگڑا کرنے لگتے لیکن خدا نے تمہیں اس جھگڑے سے بچالیا کہ وہ دل کے رازوں سے بھی باخبر ہے'),
(1204, 8, 44, 'اور جب خدا مقابلہ کے وقت تمہاری نظروں میں دشمنوں کو کم دکھلا رہا تھا اور ان کی نظروں میں تمہیں کم کرکے دکھلا رہا تھا تاکہ اس امر کا فیصلہ کردے جو ہونے والا تھا اور سارے امور کی بازگشت اللہ ہی کی طرف ہے'),
(1205, 8, 45, 'ایمان والو جب کسی گروہ سے مقابلہ کرو تو ثبات هقدم سے کام لو اور اللہ کو بہت یاد کرو کہ شاید اسی طرح کامیابی حاصل کرلو'),
(1206, 8, 46, 'اور اللہ اور اس کے رسول کی اطاعت کرو اور آپس میں اختلاف نہ کرو کہ کمزور پڑ جاؤ اور تمہاری ہوا بگڑ جائے اور صبر کرو کہ اللہ صبر کرنے والوں کے ساتھ ہے'),
(1207, 8, 47, 'اور ان لوگوں جیسے نہ ہوجاؤ جو اپنے گھروں سے اتراتے ہوئے اور لوگوں کو دکھانے کے لئے نکلے اور راہ هخدا سے روکتے رہے کہ اللہ ان کے اعمال کا احاطہ کئے ہوئے ہے'),
(1208, 8, 48, 'اور اس وقت کو یاد کرو جب شیطان نے ان کے لئے ان کے اعمال کو آراستہ کردیا اور کہا کہ آج کوئی تم پر غالب آنے والا نہیں ہے اور میں تمہارا مددگار ہوں -اس کے بعد جب دونوں گروہ آمنے سامنے آئے تو الٹے پاؤں بھاگ نکلا اور کہا کہ میں تم لوگوں سے بری ہوں میں وہ دیکھ رہا ہوں جو تم نہیں دیکھ رہے ہو اور میں اللہ سے ڈرتا ہوں کہ وہ سخت عذاب کرنے والا ہے'),
(1209, 8, 49, 'جب منافقین اور جن کے دلوں میں کھوٹ تھا کہہ رہے تھے کہ ان لوگوں کو ان کے دین نے دھوکہ دیا ہے حالانکہ جو شخص اللہ پر اعتماد کرتا ہے تو خد اہر شے پر غالب آنے والا اور بڑی حکمت والا ہے'),
(1210, 8, 50, 'کاش تم دیکھتے کہ جب فرشتے ان کی جان نکال رہے تھے اور ان کے منہ اور پیٹھ پر مارتے جاتے تھے کہ اب جہنّم کا مزہ چکھو'),
(1211, 8, 51, 'یہ اس لئے کہ تمہارے پچھلے اعمال کا نتیجہ یہی ہے اور خدا اپنے بندوں پر ظلم کرنے والا نہیں ہے'),
(1212, 8, 52, 'جو حال آلِ فرعون اور ان کے پہلے والوں کا تھا کہ انہوں نے آیااُ الٰہیہ کا انکار کیا تو خد انے انہیں ان کے گناہوں کی گرفت میں لے لیا کہ اللہ قوی بھی ہے اور شدید عذاب کرنے والا بھی ہے'),
(1213, 8, 53, 'یہ اس لئے کہ خدا کسی قوم کو دی ہوئی نعمت کو اس وقت تک نہیں بدلتا جب تک وہ خود اپنے تئیں تغیر نہ پیدا کردیں کہ خدا سننے والا بھی ہے اور جاننے والا بھی ہے'),
(1214, 8, 54, 'جس طرح آلِ فرعون اور ان کے پہلے والوں کا انجام ہوا کہ انہوں نے پروردگار کی آیات کا انکار کیا تو ہم نے ان کے گناہوں کی بنا پر انہیں ہلاک کردیا اور آل هفرعون کو غرق کردیا کہ یہ سب کے سب ظالم تھے'),
(1215, 8, 55, 'زمین پر چلنے والوں میں بدترین افراد وہ ہیں جنہوں نے کفر اختیار کرلیا اور اب وہ ایمان لانے والے نہیں ہیں'),
(1216, 8, 56, 'جن سے آپ نے عہد لیا اور اس کے بعد وہ ہر مرتبہ اپنے عہد کو توڑ دیتے ہیں اور خدا کا خوف نہیں کرتے'),
(1217, 8, 57, 'اگر وہ جنگ میں تمہارے قبضہ میں آجائیں تو انہیں اور ان کے پشتیبان سب کو نکال باہر کرو تاکہ عبرت حاصل کریں'),
(1218, 8, 58, 'اور اگر کسی قوم سے کسی خیانت یا بدعہدی کا خطرہ ہے تو آپ بھی ان کے عہد کو ان کی طرف پھینک دیں کہ اللہ خیانت کاروں کو دوست نہیں رکھتا ہے'),
(1219, 8, 59, 'اور خبردار کافروں کو یہ خیال نہ ہو کہ وہ آگے بڑھ گئے وہ کبھی مسلمانوں کو عاجز نہیں کرسکتے'),
(1220, 8, 60, 'اور تم سب ان کے مقابلہ کے لئے امکانی قوت اور گھوڑوں کی صف بندی کا انتظام کرو جس سے اللہ کے دشمن -اپنے دشمن اور ان کے علاوہ جن کو تم نہیں جانتے ہو اور اللہ جانتا ہے سب کو خوفزدہ کردو اور جو کچھ بھی راہ هخدا میں خرچ کرو گے سب پورا پورا ملے گا اور تم پر کسی طرح کا ظلم نہیں کیا جائے گا'),
(1221, 8, 61, 'اور اگر وہ صلح کی طرف مائل ہوں تو تم بھی جھک جاؤ اور اللہ پر بھروسہ کرو کہ وہ سب کچھ سننے والا اور جاننے والا ہے'),
(1222, 8, 62, 'اور اگر یہ آپ کو دھوکہ دینا چاہیں گے تو خدا آپ کے لئے کافی ہے -اس نے آپ کی تائید ,اپنی نصرت اور صاحبان ه ایمان کے ذریعہ کی ہے'),
(1223, 8, 63, 'اور ان کے دلوں میں محبّت پیدا کردی ہے کہ اگر آپ ساری دنیا خرچ کردیتے تو بھی ان کے دلوں میں باہمی الفت نہیں پیدا کرسکتے تھے لیکن خدا نے یہ الفت و محبّت پیدا کردی ہے کہ وہ ہر شے پر غالب اور صاحب هحکمت ہے'),
(1224, 8, 64, 'اے پیغمبر آپ کے لئے خدا اور وہ مومنین کافی ہیں جو آپ کا اتباع کرنے والے ہیں'),
(1225, 8, 65, 'اے پیغمبر آپ لوگوں کو جہاد پر آمادہ کریں اگر ان میں بیس بھی صبر کرنے والے ہوں گے تو دو سو پر غالب آجائیں گے اور اگر سو ہوں گے تو ہزاروں کافروں پر غالب آجائیں گے اس لئے کہ کفاّر سمجھدار قوم نہیں ہیں'),
(1226, 8, 66, 'اب اللہ نے تمہارا بار ہلکا کردیا ہے اور اس نے دیکھ لیا ہے کہ تم میں کمزوری پائی جاتی ہے تو اگرتم میں سو بھی صبر کرنے والے ہوں گے تو دو سو پر غالب آجائیں گے اور اگر ہزار ہوں گے تو بحکم خدا دو ہزار پر غالب آجائیں گے اور اللہ صبر کرنے والوں کے ساتھ ہے'),
(1227, 8, 67, 'کسی نبی کو یہ حق نہیں ہے کہ وہ قیدی بنا کر رکھے جب تک زمین میں جہاد کی سختیوں کا سامنا نہ کرے .تم لوگ تو صرف مال دنیا چاہتے ہو جب کہ اللہ آخرت چاہتا ہے اور وہی صاحبِ عزّت و حکمت ہے'),
(1228, 8, 68, 'اگر خدا کی طرف سے پہلے فیصلہ نہ ہوچکا ہوتا تو تم لوگوں نے جو فدیہ لے لیا تھا اس پر عذابِ عظیم نازل ہوجاتا'),
(1229, 8, 69, 'پس اب جو مال غنیمت حاصل کرلیا ہے اسے کھاؤ کہ وہ حلال اور پاکیزہ ہے اور تقوٰی الٰہی اختیار کرو کہ اللہ بہت بخشنے والا اور مہربان ہے'),
(1230, 8, 70, 'اے پیغمبر اپنے ہاتھ کے قیدیوں سے کہہ دیجئے کہ اگر خدا تمہارے دلوں میں نیکی دیکھے گا تو جو مال تم سے لے لیا گیا ہے اس سے بہتر نیکی تمہیں عطا کردے گا اور تمہیں معاف کردے گا کہ وہ بڑا بخشنے والا اور مہربان ہے'),
(1231, 8, 71, 'اور اگر یہ آپ سے خیانت کرنا چاہتے ہیں تو اس سے پہلے خدا سے خیانت کرچکے ہیں جس کے بعد خدا نے ان پر قابو عطا کردیا کہ وہ سب کچھ جاننے والا اور صاحبِ حکمت ہے'),
(1232, 8, 72, 'بیشک جو لوگ ایمان لائے اور انہوں نے ہجرت کی اور راسِ خدا میں اپنے جان و مال سے جہاد کیا اور جنہوں نے پناہ دی اور مدد کی یہ سب آپس میں ایک دوسرے کے ولی ہیں اور جن لوگوں نے ایمان اختیار کرکے ہجرت نہیں کی ان کی ولایت سے آپ کا کوئی تعلق نہیں ہے جب تک ہجرت نہ کریں اور اگر دین کے معاملہ میں تم سے مدد مانگیں تو تمہارا فرض ہے کہ مدد کردو علاوہ اس قوم کے مقابلہ کے جس سے تمہارا معاہدہ ہوچکا ہے کہ اللہ تمہارے اعمال کو خوب دیکھنے والا ہے'),
(1233, 8, 73, 'جو لوگ کفر والے ہیں وہ آپس میں ایک دوسرے کے مددگار ہیں اور اگر تم ایمان والوں کی مدد نہ کرو گے تو زمین میں فتنہ اور عظیم فساد برپا ہوجائے گا'),
(1234, 8, 74, 'اور جن لوگوں نے ایمان اختیار کیا اور ہجرت کی اور راسِ خدا میں جہاد کیا اور پناہ دی اور نصرت کی وہی درحقیقت واقعی مومن ہیں اور ان ہی کے لئے مغفرت اور باعزّت رزق ہے'),
(1235, 8, 75, 'اور جو لوگ بعد میں ایمان لے آئے اور ہجرت کی اور آپ کے ساتھ جہاد کیا وہ بھی تم ہی میں سے ہیں اور قرابتدار کتابِ خدا میں سب آپس میں ایک دوسرے سے زیادہ اوّلیت اور قربت رکھتے ہیں بیشک اللہ ہر شے کا بہترین جاننے والا ہے.');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'مسلمانو جن مشرکین سے تم نے عہد و پیمان کیا تھا اب ان سے خدا و رسول کی طرف سے مکمل بیزاری کا اعلان ہے'),
(1237, 9, 2, 'لہذا کافرو !چار مہینے تک آزادی سے زمین میں سیر کرو اور یہ یاد رکھو کہ خدا سے بچ کر نہیں جاسکتے اور خدا کافروں کو ذلیل کرنے والا ہے'),
(1238, 9, 3, 'اور اللہ و رسول کی طرف سے حج اکبر کے دن انسانوں کے لئے اعلانِ عام ہے کہ اللہ اور اس کے رسول دونوں مشرکین سے بیزار ہیں لہذا اگر تم توبہ کرلو گے تو تمہارے حق میں بہتر ہے اور اگر انحراف کیا تو یاد رکھنا کہ تم اللہ کو عاجز نہیں کرسکتے ہو اور پیغمبر آپ کافروں کو دردناک عذاب کی بشارت دے دیجئے'),
(1239, 9, 4, 'علاوہ ان افراد کے جن سے تم مسلمانوں نے معاہدہ کررکھا ہے اور انہوں نے کوئی کوتاہی نہیں کی ہے اور تمہارے خلاف ایک دوسرے کی مدد نہیں کی ہے تو چار مہینے کے بجائے جو مدّت طے کی ہے اس وقت تک عہد کو پورا کرو کہ خدا تقویٰ اختیار کرنے والوں کو دوست رکھتا ہے'),
(1240, 9, 5, 'پھر جب یہ محترم مہینے گزر جائیں تو کفاّر کو جہاں پاؤ قتل کردو اور گرفت میں لے لو اور قید کردو اور ہر راستہ اور گزر گاہ پر ان کے لئے بیٹھ جاؤ اور راستہ تنگ کردو -پھر اگر توبہ کرلیں اور نماز قائم کریں اور زکوِٰادا کریں تو ان کا راستہ چھوڑ دو کہ خدا بڑا بخشنے والا اور مہربان ہے'),
(1241, 9, 6, 'اور اگر مشرکین میں کوئی تم سے پناہ مانگے تو اسے پناہ دے دو تاکہ وہ کتاب هخدا صَنے اس کے بعد اسے آزاد کرکے جہاں اس کی پناہ گاہ ہو وہاں تک پہنچا دو اور یہ مراعات اس لئے ہے کہ یہ جاہل قوم حقائق سے آشنا نہیں ہے'),
(1242, 9, 7, 'اللہ و رسول کے نزدیک عہد شکن مشرکین کا کوئی عہد و پیمان کس طرح قائم رہ سکتا ہے ہاں اگر تم لوگوں نے کسی سے مسجد الحرام کے نزدیک عہد کرلیا ہے تو جب تک وہ لوگ اپنے عہد پر قائم رہیں تم بھی قائم رہو کہ اللہ متقی اور پرہیزگار افراد کو دوست رکھتا ہے'),
(1243, 9, 8, 'ان کے ساتھ کس طرح رعایت کی جائے جب کہ یہ تم پر غالب آجائیں تو نہ کسی ہمسائگی اور قرابت کی نگرانی کریں گے اور نہ کوئی عہد و پیمان دیکھیں گے - یہ تو صرف زبانی تم کو خوش کررہے ہیں ورنہ ان کا دل قطعی منکر ہے اور ان کی اکثریت فاسق اور بدعہد ہے'),
(1244, 9, 9, 'انہوں نے آیات هالٰہیہ کے بدلے بہت تھوڑی منفعت کو لے لیا ہے اور اب راہ هخدا سے روک رہے ہیں -یہ بہت برا کام کررہے ہیں'),
(1245, 9, 10, 'یہ کسی مومن کے بارے میں کسی قرابت یا قول و اقرار کی پرواہ نہیں کرتے ہیں یہ صرف زیادتی کرنے والے لوگ ہیں'),
(1246, 9, 11, 'پھر بھی اگر یہ توبہ کرلیں اور نماز قائم کریں اور زکوٰ ِ ادا کریں تو دین میں تمہارے بھائی ہیں اور ہم صاحبان هعلم کے لئے اپنی آیات کو تفصیل کے ساتھ بیان کرتے رہتے ہیں'),
(1247, 9, 12, 'اور اگر یہ عہد کے بعد بھی اپنی قسموں کو توڑ دیں اور دین میں طعنہ زنی کریں تو کفر کے سربراہوں سے کھل کر جہاد کرو کہ ان کی قسموں کا کوئی اعتبار نہیں ہے شاید یہ اسی طرح اپنی حرکتوں سے باز آجائیں'),
(1248, 9, 13, 'کیا تم اس قوم سے جہاد نہ کرو گے جس نے اپنے عہد و پیمان کو توڑ دیا ہے اور رسول کو وطن سے نکال دینے کا ارادہ بھی کرلیا ہے اور تمہارے مقابلہ میں مظالم کی پہل بھی کی ہے -کیا تم ان سے ڈرتے ہو تو خدا زیادہ حقدار ہے کہ اس کا خوف پیدا کرو اگر تم صاحب ه ایمان ہو'),
(1249, 9, 14, 'ان سے جنگ کرو اللہ انہیں تمہارے ہاتھوں سے سزا دے گا اور رسوا کرے گا اور تمہیں ان پر فتح عطا کرے گا اور صاحب ایمان قوم کے دلوں کو ٹھنڈا کردے گا'),
(1250, 9, 15, 'اور ان کے دلوں کا غصّہ دور کردے گا اور اللہ جس کی توبہ کو چاہتا ہے قبول کرلیتا ہے کہ وہ صاحبِ علم بھی ہے اور صاحب هحکمت بھی ہے'),
(1251, 9, 16, 'کیا تمہارا خیال یہ ہے کہ تم کو اسی طرح چھوڑ دیا جائے گا جب کہ اللہ نے ابھی یہ بھی نہیں دیکھا ہے کہ تم میں جہاد کرنے والے کون لوگ ہیں جنہوں نے خدا -رسول اور صاحبان ایمان کو چھوڑ کر کسی کو دوست نہیں بنایا ہے اور اللہ تمہارے اعمال سے خوب باخبر ہے'),
(1252, 9, 17, 'یہ کام مشرکین کا نہیں ہے کہ وہ مساجد خدا کو آباد کریں جب کہ وہ خود اپنے نفس کے کفر کے گواہ ہیں -ان کے اعمال برباد ہیں اور وہ جہّنم میں ہمیشہ رہنے والے ہیں'),
(1253, 9, 18, 'اللہ کی مسجدوں کو صرف وہ لوگ آباد کرتے ہیں جن کا ایمان اللہ اور روز آخرت پر ہے اور جنہوں نے نماز قائم کی ہے زکوِٰادا کی ہے اور سوائے خدا کے کسی سے نہیں ڈرے یہی وہ لوگ ہیں جو عنقریب ہدایت یافتہ لوگوں میں شمار کئے جائیں گے'),
(1254, 9, 19, 'کیا تم نے حاجیوں کے پانی پلانے اور مسجد الحرام کی آبادی کو اس کا جیسا سمجھ لیا ہے جو اللہ اور آخرت پر ایمان رکھتا ہے اور راسِ خدا میں جہاد کرتا ہے -ہرگز یہ دونوں اللہ کے نزدیک برابر نہیں ہوسکتے اور اللہ ظالم قوم کی ہدایت نہیں کرتا ہے'),
(1255, 9, 20, 'بیشک جو لوگ ایمان لے آئے اور انہوں نے ہجرت کی اور راہ خدا میں جان اور مال سے جہاد کیا وہ اللہ کے نزدیک عظیم درجہ کے مالک ہیں اور درحقیقت وہی کامیاب بھی ہیں'),
(1256, 9, 21, 'اللہ ان کو اپنی رحمت ,رضامندی اور باغات کی بشارت دیتا ہے جہاں ان کے لئے دائمی نعمتیں ہوں گی'),
(1257, 9, 22, 'وہ ان ہی باغات میں ہمیشہ رہیں گے کہ اللہ کے پاس عظیم ترین اجر ہے'),
(1258, 9, 23, 'ایمان والو خبردار اپنے باپ دادا اور بھائیوں کو اپنا دوست نہ بناؤ اگر وہ ایمان کے مقابلہ میں کفر کو دوست رکھتے ہوں اور جو شخص بھی ایسے لوگوں کو اپنا دوست بنائے گا وہ ظالموں میں شمار ہوگا'),
(1259, 9, 24, 'پیغمبرآپ کہہ دیجئے کہ اگر تمہارے باپ داداً اولادً برادران ,ازواج ,عشیرہ و قبیلہ اور وہ اموال جنہیں تم نے جمع کیا ہے اور وہ تجارت جس کے خسارہ کی طرف سے فکرمند رہتے ہو اور وہ مکانات جنہیں پسند کرتے ہو تمہاری نگاہ میں اللہ ًاس کے رسول اور راہ هخدا میں جہاد سے زیادہ محبوب ہیں تو وقت کا انتظار کرو یہاں تک کہ امر الہی آجائے اور اللہ فاسق قوم کی ہدایت نہیں کرتا ہے'),
(1260, 9, 25, 'بیشک اللہ نے کثیر مقامات پر تمہاری مدد کی ہے اور حنین کے دن بھی جب تمہیں اپنی کثرت پر ناز تھا لیکن اس نے تمہیں کوئی فائدہ نہیں پہنچایا اور تمہارے لئے زمین اپنی وسعتوں سمیت تنگ ہوگئی اور اس کے بعد تم پیٹھ پھیر کر بھاگ نکلے'),
(1261, 9, 26, 'پھر اس کے بعد خدا نے اپنے رسول اور صاحبان هایمان پر سکون نازل کیا اور وہ لشکر بھیجے جنہیں تم نے نہیں دیکھا اور کفر اختیار کرنے والوں پر عذاب نازل کیا یہی کافرین کی جزا اور ان کا انجام ہے'),
(1262, 9, 27, 'اس کے بعد خدا جس کی چاہے گا توبہ قبول کرلے گا وہ بڑا بخشنے والا مہربان ہے'),
(1263, 9, 28, 'ایمان والو !مشرکین صررِ نجاست ہیں لہذا خبردار اس سال کے بعد مسجدالحرام میں داخل نہ ہونے پائیں اور اگر تمہیں غربت کا خوف ہے تو عنقریب خدا چاہے گا تو اپنے فضل و کرم سے تمہیں غنی بنادے گا کہ وہ صاحب هعلم بھی ہے اور صاحب هحکمت بھی ہے'),
(1264, 9, 29, 'ان لوگوں سے جہاد کرو جو خدا اور روز هآخرت پر ایمان نہیں رکھتے اور جس چیز کو خدا و رسول نے حرام قرار دیا ہے اسے حرام نہیں سمجھتے اور اہل کتاب ہوتے ہوئے بھی دین حق کا التزام نہیں کرتے .... یہاں تک کہ اپنے ہاتھوں سے ذلّت کے ساتھ تمہارے سامنے جزیہ پیش کرنے پر آمادہ ہوجائیں'),
(1265, 9, 30, 'اور یہودیوں کا کہنا ہے کہ عزیر اللہ کے بیٹے ہیں اور نصارٰی کہتے ہیں کہ مسیح اللہ کے بیٹے ہیں یہ سب ان کی زبانی باتیں ہیں-ان باتوں میں یہ بالکل ان کے مثل ہیں جو ان کے پہلے کفار کہا کرتے تھے ,اللہ ان سب کو قتل کرے یہ کہاں بہکے چلے جارہے ہیں'),
(1266, 9, 31, 'ان لوگوں نے اپنے عالموں اور راہبوں اور مسیح بن مریم کو خدا کو چھوڑ کر اپنا رب بنالیا ہے حالانکہ انہیں صرف خدائے یکتا کی عبادت کا حکم دیا گیا تھا جس کے علاوہ کوئی خدا نہیں ہے وہ واحد و بے نیاز ہے اور ان کے مشرکانہ خیالات سے پاک و پاکیزہ ہے'),
(1267, 9, 32, 'یہ لوگ چاہتے ہیں کہ نو» خدا کواپنے منہ سے پھونک مار کر بجھا دیں حالانکہ خدا اس کے علاوہ کچھ ماننے کے لئے تیار نہیں ہے کہ وہ اپنے نور کو تمام کردے چاہے کافروں کو یہ کتنا ہی برا کیوںنہ لگے'),
(1268, 9, 33, 'وہ خدا وہ ہے جس نے اپنے رسول کو ہدایت اور دینِ حق کے ساتھ بھیجا تاکہ اپنے دین کو تمام ادیان پر غالب بنائے چاہے مشرکین کو کتنا ہی ناگوار کیوں نہ ہو'),
(1269, 9, 34, 'ایمان والو نصارٰی کے بہت سے علمائ اور راہب ,لوگوں کے اموال کو ناجائز طریقہ سے کھاجاتے ہیں اور لوگوں کو راسِ خدا سے روکتے ہیں .... اور جو لوگ سونے چاندی کا ذخیرہ کرتے ہیں اور اسے راہ هخدا میں خرچ نہیں کرتے پیغمبر,آپ انہیں دردناک عذاب کی بشارت دے دیں'),
(1270, 9, 35, 'جس دن وہ سونا چاندی آتش هجہنّم میں تپایا جائے گا اور اس سے ان کی پیشانیوں اور ان کے پہلوؤں اور پشت کو داغا جائے گا کہ یہی وہ ذخیرہ ہے جو تم نے اپنے لئے جمع کیا تھا اب اپنے خزانوں اور ذخیروں کا مزہ چکھو'),
(1271, 9, 36, 'بیشک مہینوں کی تعداد اللہ کے نزدیک کتابِ خدا میں اس دن سے بارہ ہے جس دن اس نے آسمانوں اور زمین کو پیدا کیا ہے -ان میں سے چار مہینے محترم ہیں اور یہی سیدھا اور مستحکم دین ہے لہٰذا خبردار ان مہینوں میں اپنے اوپر ظلم نہ کرنا اور تمام مشرکین سے اسی طرح جہاد کرنا جس طرح وہ تم سے جنگ کرتے ہیں اور یہ یاد رکھنا کہ خدا صرف متقی اور پرہیزگار لوگوں کے ساتھ ہے'),
(1272, 9, 37, 'محترم مہینوں میں تقدیم و تاخیر کفر میں ایک قسم کی زیادتی ہے جس کے ذریعہ کفاّر کو گمراہ کیا جاتا ہے کہ وہ ایک سال اسے حلال بنالیتے ہیں اور دوسرے سال حرام کردیتے ہیں تاکہ اتنی تعداد برابر ہوجائے جتنی خدا نے حرام کی ہے اور حرام هخدا حلال بھی ہوجائے -ان کے بدترین اعمال کو ان کی نگاہ میں آراستہ کردیا گیا ہے اور اللہ کافر قوم کی ہدایت نہیں کرتا ہے'),
(1273, 9, 38, 'ایمان والو تمہیں کیا ہوگیا ہے کہ جب تم سے کہا گیا کہ راہ هخدا میں جہاد کے لئے نکلو تو تم زمین سے چپک کر رہ گئے کیا تم آخرت کے بدلے زندگانی دنیا سے راضی ہوگئے ہو تو یاد رکھو کہ آخرت میں اس متاع هزندگانی دنیا کی حقیقت بہت قلیل ہے'),
(1274, 9, 39, 'اگر تم راہ هخدا میں نہ نکلو گے تو خدا تمہیں دردناک عذا میں مبتلا کرے گا اور تمہارے بدلے دوسری قوم کو لے آئے گا اور تم اسے کوئی نقصان نہیں پہنچا سکتے ہو کہ وہ ہر شے پر قدرت رکھنے والا ہے'),
(1275, 9, 40, 'اگر تم پیغمبر کی مدد نہ کرو گے تو ان کی مدد خدا نے کی ہے اس وقت جب کفاّر نے انہیں وطن سے باہر نکال دیا اور وہ ایک شخص کے ساتھ نکلے اور دونوں غار میں تھے تو وہ اپنے ساتھی سے کہہ رہے تھے کہ رنج نہ کرو خدا ہمارے ساتھ ہے پھر خدا نے اپنی طرف سے اپنے پیغمبر پر سکون نازل کردیا اور ان کی تائید ان لشکروں سے کردی جنہیں تم نہ دیکھ سکے اور اللہ ہی نے کفاّر کے کلمہ کو پست بنادیا ہے اور اللہ کا کلمہ درحقیقت بہت بلند ہے کہ وہ صاحب هعزت و غلبہ بھی ہے اور صاحب هحکمت بھی ہے'),
(1276, 9, 41, 'مسلمانو !تم ہلکے ہو یا بھاری گھر سے نکل پڑو اور راہ هخدا میں اپنے اموال اور نفوس سے جہاد کرو کہ یہی تمہارے حق میں خیر ہے اگرتم کچھ جانتے ہو'),
(1277, 9, 42, 'پیغمبر !اگر کوئی قریبی فائدہ یا آسان سفر ہوتا تو یہ ضرور تمہارا اتباع کرتے لیکن ان کے لئے دور کا سفر مشکل بن گیا ہے اور عنقریب یہ خدا کی قسم کھائیں گے کہ اگر ممکن ہوتا تو ہم ضرور آپ کے ساتھ نکل پڑتے -یہ اپنے نفس کو ہلاک کررہے ہیں اور خدا خوب جانتا ہے کہ یہ جھوٹے ہیں'),
(1278, 9, 43, 'پیغمبر!خدا نے آپ سے درگزر کیا کہ آپ نے کیوں انہیں پیچھے رہ جانے کی اجازت دے دی بغیر یہ معلوم کئے کہ ان میں کون سچاّ ہے اور کون جھوٹا ہے'),
(1279, 9, 44, 'جو لوگ اللہ اور روز هآخرت پر ایمان رکھتے ہیں وہ ہرگز اپنے جان و مال سے جہاد کرنے کے خلاف اجازت نہ طلب کریں گے کہ خدا صاحبانِ تقوٰی کو خوب جانتا ہے'),
(1280, 9, 45, 'یہ اجازت صرف وہ لوگ طلب کرتے ہیں جن کا ایمان اللہ اور روز آخرت پر نہیں ہے اور ان کے دلوں میں شبہ ہے اور وہ اسی شبہ میں چکّر کاٹ کررہے ہیں'),
(1281, 9, 46, 'یہ اگر نکلنا چاہتے تو اس کے لئے سامان تیار کرتے لیکن خد اہی کو ان کا نکلنا پسند نہیں ہے اس لئے اس نے ان کے ارادوں کو کمزور رہنے دیا اور ان سے کہا گیا کہ اب تم بیٹھنے والوں کے ساتھ بیٹھے رہو'),
(1282, 9, 47, 'اگر یہ تمہارے درمیان نکل بھی پڑتے تو تمہاری وحشت میں اضافہ ہی کردیتے اور تمہارے درمیان فتنہ کی تلاش میں گھوڑے دوڑاتے پھرتے اور تم میں ایسے لوگ بھی تھے جو ان کی سننے والے بھی تھے اور اللہ تو ظالمین کو خوب جاننے والا ہے'),
(1283, 9, 48, 'بیشک انہوں نے اس سے پہلے بھی فتنہ کی کوشش کی تھی اور تمہارے امور کو الٹ پلٹ دینا چاہا تھا یہاں تک کہ حق آگیا اور امر خدا واضح ہوگیا اگرچہ یہ لوگ اسے ناپسند کررہے تھے'),
(1284, 9, 49, 'ان میں وہ لوگ بھی ہیں جو کہتے ہیں کہ ہم کو اجازت دے دیجئے اور فتنہ میں نہ ڈالئے تو آگاہ ہوجاؤ کہ یہ واقعا فتنہ میں گر چکے ہیں اور جہنّم تو کافرین کو ہر طرف سے احاطہ کئے ہوئے ہے'),
(1285, 9, 50, 'ان کا حال یہ ہے کہ آپ تک نیکی آ تی ہے تو انہیں بفِی لگتی ہے اور کوئی مصیبت آجاتی ہے تو کہتے ہیں کہ ہم نے اپنا کام پہلے ہی ٹھیک کرلیا تھا اور خوش و خرم واپس چلے جاتے ہیں'),
(1286, 9, 51, 'آپ کہہ دیجئے کہ ہم تک وہی حالات آتے ہیں جو خدا نے ہمارے حق میں لکھ دیئے ہیں وہی ہمارا مولا ہے اور صاحبان هایمان اسی پر توکّل اور اعتماد رکھتے ہیں'),
(1287, 9, 52, 'آپ کہہ دیجئے کہ تم ہمارے بارے میں جس بات کا بھی انتظار کررہے ہو وہ دو میں سے ایک نیکی ہے اور ہم تمہارے بارے میں اس بات کا انتظار کررہے ہیں کہ خدا اپنی طرف سے یا ہمارے ہاتھوں سے تمہیں عذاب میں مبتلا کردے لہٰذا اب عذاب کا انتظار کرو ہم بھی تمہارے ساتھ انتظار کررہے ہیں'),
(1288, 9, 53, 'کہہ دیجئے کہ تم بخوشی خرچ کرو یا جبرا تمہارا عمل قبول ہونے والا نہیں ہے کہ تم ایک فاسق قوم ہو'),
(1289, 9, 54, 'اور ان کے نفقات کو قبول ہونے سے صرف اس بات نے روک دیا ہے کہ انہوں نے خدا اور رسول کا انکار کیا ہے اور یہ نمازبھی سستی اور کسلمندی کے ساتھ بجالاتے ہیں اور راسِ خدا میں کراہت اور ناگواری کے ساتھ خرچ کرتے ہیں'),
(1290, 9, 55, 'تمہیں ان کے اموال اور اولاد حیرت میں نہ ڈال دیں بس اللہ کاارادہ یہی ہے کہ ان ہی کے ذریعہ ان پر زندگانی دنیا میں عذاب کرے اور حالت هکفر ہی میں ان کی جان نکل جائے'),
(1291, 9, 56, 'اور یہ اللہ کی قسم کھاتے ہیں کہ یہ تم ہی میں سے ہیں حالانکہ یہ تم میں سے نہیں ہیں یہ لوگ بزدل لوگ ہیں'),
(1292, 9, 57, 'ان کو کوئی پناہ گاہ یا غار یا گھس بیٹھنے کی جگہ مل جائے تو اس کی طرف بے تحاشہ بھاگ جائیں گے'),
(1293, 9, 58, 'اور ان ہی میں سے وہ بھی ہیں جو خیرات کے بارے میں الزام لگاتے ہیں کہ انہیں کچھ مل جائے تو راضی ہوجائیں گے اور نہ دیا جائے تو ناراض ہوجائیں گے'),
(1294, 9, 59, 'حالانکہ اے کاش یہ خدا و رسول کے دئیے ہوئے پر راضی ہوجاتے اور یہ کہتے کہ ہمارے لئے اللہ ہی کافی ہے عنقریب وہ اور اس کا رسول اپنے فضل و احسان سے عطا کردیں گے او رہم تو صرف اللہ کی طرف رغبت رکھنے والے ہیں'),
(1295, 9, 60, 'صدقات و خیرات بس فقرائ ,مساکین اور ان کے کام کرنے والے اور جن کی تالیف هقلب کی جاتی ہے اور غلاموں کی گردن کی آزادی میں اور قرضداروں کے لئے اور راسِ خدا میں غربت زدہ مسافروں کے لئے ہیں یہ اللہ کی طرف سے فریضہ ہے اور اللہ خوب جاننے والا اور حکمت والا ہے'),
(1296, 9, 61, 'ان میں سے وہ بھی ہیں جو پیغمبر کو اذیت دیتے ہیں اور کہتے ہیں کہ وہ تو صرف کان ہیں -آپ کہہ دیجئے کہ تمہارے حق میں بہتری کے کان ہیں کہ خدا پر ایمان رکھتے ہیں اور مومنین کی تصدیق کرتے ہیں اور صاحبانِ ایمان کے لئے رحمت ہیں اور جو لوگ رسول خدا کو اذیت دیتے ہیں ان کے واسطے دردناک عذاب ہے'),
(1297, 9, 62, 'یہ لوگ تم لوگوں کو راضی کرنے کے لئے خدا کی قسم کھاتے ہیں حالانکہ خدا و رسول اس بات کے زیادہ حقدار تھے کہ اگر یہ صاحبان هایمان تھے تو واقعا انہیں اپنے اعمال و کردار سے راضی کرتے'),
(1298, 9, 63, 'کیا یہ نہیں جانتے ہیں کہ جو خدا و رسول سے مخالفت کرے گا اس کے لئے آتش جہنمّ ہے اور اسی میں ہمیشہ رہنا ہے اور یہ بہت بڑی رسوائی ہے'),
(1299, 9, 64, 'منافقین کو یہ خوف بھی ہے کہ کہیں کوئی سورہ نازل ہوکر مسلمانوں کو ان کے دل کے حالات سے باخبر نہ کردے تو آپ کہہ دیجئے کہ تم اور مذاق اڑاؤ اللہ بہرحال اس چیز کو منظر عام پر لے آئے گا جس کا تمہیں خطرہ ہے'),
(1300, 9, 65, 'اور اگر آپ ان سے باز پرس کریں گے تو کہیں گے کہ ہم تو صرف بات چیت اور دل لگی کررہے تھے تو آپ کہہ دیجئے کہ کیا اللہ اور اس کی آیات اور رسول کے بارے میں مذاق اڑارہے تھے'),
(1301, 9, 66, 'تو اب معذرت نہ کرو -تم نے ایمان کے بعد کفر اختیار کیا ہے -ہم اگر تم میں کی ایک جماعت کو معاف بھی کردیں تو دوسری جماعت پر ضرور عذاب کریں گے کہ یہ لوگ مجرم ہیں'),
(1302, 9, 67, 'منافق مرد اور منافق عورتیں آپس میں سب ایک دوسرے سے ہیں -سب برائیوں کا حکم دیتے ہیں اور نیکیوں سے روکتے ہیں اور اپنے ہاتھوں کو راہ هخدا میں خرچ کرنے سے روکے رہتے ہیں -انہوں نے اللہ کو بھلا دیا ہے تو اللہ نے انہیں بھی نظرانداز کردیا ہے کہ منافقین ہی اصل میں فاسق ہیں'),
(1303, 9, 68, 'اور اللہ نے نےم منافق مردوں اور عورتوں سے اور تمام کافروں سے آتش جہنمّ کا وعدہ کیا ہے جس میں یہ ہمیشہ رہنے والے ہیں -وہی ان کے واسطے کافی ہے اور ان کے لئے ہمیشہ رہنے والا عذاب ہے'),
(1304, 9, 69, 'تمہاری مثال تم سے پہلے والوں کی ہے جو تم سے زیادہ طاقتور تھے اور تم سے زیادہ مالدار اور صاحب هاولاد بھی تھے -انہوں نے اپنے حصّہ سے خوب فائدہ اٹھایا اور تم نے بھی اسی طرح اپنے نصیب سے فائدہ اٹھایا جس طرح تم سے پہلے والوں نے اٹھایا تھا اور اسی طرح لغویات میں داخل ہوئے جس طرح وہ لوگ داخل ہوئے تھے حالانکہ وہی وہ لوگ ہیں جن کے اعمال دنیا اور آخرت میں برباد ہوگئے اور وہی وہ لوگ ہیں جو حقیقتا خسارہ والے ہیں'),
(1305, 9, 70, 'کیا ان لوگوں کے پاس ان سے پہلے والے افراد قوم نوح علیھ السّلام ,عاد علیھ السّلام ,ثمود علیھ السّلام ,قوم ابراہیم علیھ السّلام,اصحاب مدین اور الٹ دی جانے والی بستیوں کی خبرنہیں آئی جن کے پاس ان کے رسول واضح نشانیاں لے کر آئے اور انہوں نے انکار کردیا -خدا کسی پر ظلم کرنے والا نہیں ہے لوگ خود اپنے نفس پر ظلم کرتے ہیں'),
(1306, 9, 71, 'مومن مرد اور مومن عورتیں آپس میں سب ایک دوسرے کے ولی اور مددگار ہیں کہ یہ سب ایک دوسرے کو نیکیوں کا حکم دیتے ہیں اور برائیوں سے روکتے ہیں,نماز قائم کرتے ہیں -زکوِٰ ادا کرتے ہیں اللہ اور رسول کی اطاعت کرتے ہیں -یہی وہ لوگ ہیں جن پر عنقریب خدا رحمت نازل کرے گا کہ وہ ہر شے پر غالب اور صاحبِ حکمت ہے'),
(1307, 9, 72, 'اللہ نے مومن مرد اور مومن عورتوں سے ان باغات کا وعدہ کیا ہے جن کی نیچے نہریں جاری ہوں گی -یہ ان میں ہمیشہ رہنے والے ہیں -ان جنااُ عدن میں پاکیزہ مکانات ہیں اور اللہ کی مرضی تو سب سے بڑی چیز ہے اور یہی ایک عظیم کامیابی ہے'),
(1308, 9, 73, 'پیغمبر !کفار اور منافقین سے جہاد کیجئے اور ان پر سختی کیجئے کہ ان کا انجام جہنّم ہے جو بدترین ٹھکانا ہے'),
(1309, 9, 74, 'یہ اپنی باتوں پر اللہ کی قسم کھاتے ہیں کہ ایسا نہیں کہا حالانکہ انہوں نے کلمہ کفر کہا ہے اور اپنے اسلام کے بعد کافر ہوگئے ہیں اور وہ ارادہ کیا تھا جو حاصل نہیں کرسکے اور ان کا غصہّ صرف اس بات پر ہے کہ اللہ اور رسول نے اپنے فضل و کرم سے مسلمانوں کو نواز دیا ہے -بہرحال یہ اب بھی توبہ کرلیں تو ان کے حق میں بہتر ہے اور منہ پھیرلیں تو اللہ ان پر دنیا اور آخرت میں دردناک عذاب کرے گا اور روئے زمین پر کوئی ان کا سرپرست اور مددگار نہ ہوگا'),
(1310, 9, 75, 'ان میں وہ بھی ہیں جنہوں نے خدا سے عہد کیا کہ اگر وہ اپنے فضل و کرم سے عطا کردیگا تو اس کی راہ میں صدقہ دیں گے اور نیک بندوں میں شامل ہوجائیں گے'),
(1311, 9, 76, 'اس کے بعد جب خد انے اپنے فضل سے عطا کردیا تو بخل سے کام لیا اور کنارہ کش ہوکر پلٹ گئے'),
(1312, 9, 77, 'تو ان کے بخل نے ان کے دلوں میں نفاق راسخ کردیا اس دن تک کے لئے جب یہ خدا سے ملاقات کریں گے اس لئے کہ انہوں نے خدا سے کئے ہوئے وعدہ کی مخالفت کی ہے اور جھوٹ بولے ہیں'),
(1313, 9, 78, 'کیا یہ نہیں جانتے کہ خدا ان کے رازِ دل اور ان کی سرگوشیوں کو بھی جانتا ہے اور وہ سارے غیب کا جاننے والا ہے'),
(1314, 9, 79, 'جو لوگ صدقات میں فراخدلی سے حصّہ لینے والے مومنین اور ان غریبوں پر جن کے پاس ان کی محنت کے علاوہ کچھ نہیں ہے الزام لگاتے ہیں اور پھر ان کا مذاق اڑاتے ہیں خدا ان کا بھی مذاق بنادے گا اور اس کے پاس بڑا دردناک عذاب ہے'),
(1315, 9, 80, 'آپ ان کے لئے استغفار کریں یا نہ کریں -اگر ستر ّمرتبہ بھی استغفار کریں گے تو خدا انہیں بخشنے والا نہیں ہے اس لئے کہ انہوں نے اللہ اور رسول کا انکار کیا ہے اور خدا فاسق قوم کو ہدایت نہیں دیتا ہے'),
(1316, 9, 81, 'جو لوگ جنگ تبوک میں نہیں گئے وہ رسول اللہ کے پیچھے بیٹھے رہ جانے پر خوش ہیں اور انہیں اپنے جان و مال سے راسِ خدامیں جہاد ناگوار معلوم ہوتا ہے اور یہ کہتے ہیں کہ تم لوگ گرمی میں نہ نکلو ...._ تو پیغمبر آپ کہہ دیجئے کہ آتش جہنّم اس سے زیادہ گرم ہے اگر یہ لوگ کچھ سمجھنے والے ہیں'),
(1317, 9, 82, 'اب یہ لوگ ہنسیں کم اور روئیں زیادہ کہ یہی ان کے کئے کی جزا ہے'),
(1318, 9, 83, 'اس کے بعد اگر خدا آپ کو ان کے کسی گروہ تک میدان سے واپس لائے اور یہ دوبارہ خروج کی اجازت طلب کریں تو آپ کہہ دیجئے تم لوگ کبھی میرے ساتھ نہیں نکل سکتے اور کسی دشمن سے جہاد نہیں کرسکتے تم نے پہلے ہی بیٹھے رہنا پسند کیا تھا تو اب بھی پیچھے رہ جانے والوں کے ساتھ بیٹھے رہو'),
(1319, 9, 84, 'اور خبردار ان میں سے کوئی مر بھی جائے تو اس کی نماز جنازہ نہ پڑھئے گا اور اس کی قبر پر کھڑے بھی نہ ہویئے گا کہ ان لوگوں نے خدا اور رسول کا انکار کیا ہے اور حالاُ فسق میں دنیا سے گزر گئے ہیں'),
(1320, 9, 85, 'اور ان کے اموال و اولاد آپ کو بھلے نہ معلوم ہوں خدا ان کے ذریعہ ان پر دنیا میں عذاب کرنا چاہتا ہے اور چاہتا ہے کہ کفر کی حالت میں ان کا دم نکل جائے'),
(1321, 9, 86, 'اور جب کوئی سورہ نازل ہوتا ہے کہ اللہ پر ایمان لے آؤاور رسول کے ساتھ جہاد کرو تو ان ہی کے صاحبانِ حیثیت آپ سے اجازت طلب کرنے لگتے ہیں اور کہتے ہیں کہ ہمیں ان ہی بیٹھنے والوں کے ساتھ چھوڑ دیجئے'),
(1322, 9, 87, 'یہ اس بات پر راضی ہیں کہ پیچھے رہ جانے والوں کے ساتھ رہ جائیں .ان کے دلو ںپر مہر لگ گئی ہے اب یہ کچھ سمجھنے والے نہیں ہیں'),
(1323, 9, 88, 'لیکن رسول اور ان کے ساتھ ایمان لانے والوں نے راہ ه خدا میں اپنے جان و مال سے جہاد کیا ہے اور ان ہی کے لئے نیکیاں ہیں اور وہی فلاح یافتہ اور کامیاب ہیں'),
(1324, 9, 89, 'اللہ نے ان کے لئے وہ باغات مہیا کئے ہیں جن کے نیچے نہریں جاری ہیں اور وہ ان ہی میں ہمیشہ رہنے والے ہیں اور یہی بہت بڑی کامیابی ہے'),
(1325, 9, 90, 'اور آپ کے پاس دیہاتی معذرت کرنے والے بھی آگئے کہ انہیں بھی گھر بیٹھنے کی اجازت دے دی جائے اور وہ بھی بیٹھ رہے جنہوں نے خدا و رسول سے غلط بیانی کی تھی تو عنقریب ان میں کے کافروں پر بھی دردناک عذاب نازل ہوگا'),
(1326, 9, 91, 'جو لوگ کمزور ہیں یابیمار ہیں یا ان کے پاس راسِ خدا میں خرچ کرنے کے لئے کچھ نہیں ہے ان کے بیٹھے رہنے میں کوئی حرج نہیں ہے بشرطیکہ خدا و رسول کے حق میں اخلاص رکھتے ہوں کہ نیک کردار لوگوں پر کوئی الزام نہیں ہوتا اور اللہ بہت بخشنے والا مہربان ہے'),
(1327, 9, 92, 'اور ان پر بھی کوئی الزام نہیں ہے جو آپ کے پاس آئے کہ انہیں بھی سواری پر لے لیجئے اور آپ ہی نے کہہ دیا کہ ہمارے پاس سواری کا انتظام نہیں ہے اور وہ آپ کے پاس سے اس عالم میں پلٹے کہ ان کی آنکھوں سے آنسو جاری تھے اور انہیں اس بات کا رنج تھا کہ ان کے پاس راہ هخدا میں خرچ کرنے کے لئے کچھ نہیں ہے'),
(1328, 9, 93, 'الزام ان لوگوں پرہے جو غنی اور مالدار ہوکر بھی اجازت طلب کرتے ہیں اور چاہتے ہیں کہ پسماندہ لوگوں میں شامل ہوجائیں اور خدا نے ان کے دلوں پر مہرلگادی ہے اور اب کچھ جاننے والے نہیں ہیں'),
(1329, 9, 94, 'یہ تخلف کرنے والے منافقین تم لوگوں کی واپسی پر طرح طرح کے عذر بیان کریں گے تو آپ کہہ دیجئے کہ تم لوگ عذر نہ بیان کرنے والے نہیں ہیں اللہ نے ہمیں تمہارے حالات بتادئیے ہیں -وہ یقیناتمہارے اعمال کو دیکھ رہا ہے اور رسول بھی دیکھ رہا ہے اس کے بعد تم حاضر و غیب کے عالم خدا کی بارگاہ میں واپس کئے جاؤ گے اور وہ تمہیں تمہارے اعمال سے باخبر کرے گا'),
(1330, 9, 95, 'عنقریب یہ لوگ تمہاری واپسی پر خدا کی قسم کھائیں گے کہ تم ان سے درگزر کردو تو تم کنارہ کشی اختیار کرلو -یہ مجسمئہ خباثت ہیں. ان کا ٹھکانا جہّنم ہے جو ان کے کئے کی صحیح سزا ہے'),
(1331, 9, 96, 'یہ تمہارے سامنے قسم کھاتے ہیں کہ تم ان سے راضی ہوجاؤ تو اگر تم راضی بھی ہوجاؤ تو خدا فاسق قوم سے راضی ہونے والا نہیں ہے'),
(1332, 9, 97, 'یہ دیہاتی کفر اور نفاق میں بہت سخت ہیں اور اسی قابل ہیں کہ جو کتاب خدا نے اپنے رسول پر نازل کی ہے اس کے حدود اور احکام کو نہ پہچانیں اور اللہ خوب جاننے والا اور صاحبِ حکمت ہے'),
(1333, 9, 98, 'ان ہی اعراب میں وہ بھی ہیں جو اپنے انفاق کو گھاٹا سمجھتے ہیں اور آپ کے بارے میں مختلف گردشوں کا انتظار کیا کرتے ہیں تو خود ان کے اوپر اِری گردش کی مار ہے اوراللہ خوب سننے والا اور جاننے والا ہے'),
(1334, 9, 99, 'ان ہی اعراب میں وہ بھی ہیں جو ا للہ اور آخرت پر ایمان رکھتے ہیں اور اپنے انفاق کو خدا کی قربت اور رسول کی دعائے رحمت کا ذریعہ قرار دیتے ہیں اور بیشک یہ ان کے لئے سامانِ قربت ہے عنقریب خدا انہیں اپنی رحمت میں داخل کرلے گا کہ وہ غفور بھی ہے اور رحیم بھی ہے'),
(1335, 9, 100, 'اور مہاجرین و انصار میں سے سبقت کرنے والے اور جن لوگوں نے نیکی میں ان کا اتباع کیا ہے ان سب سے خدا راضی ہوگیا ہے اور یہ سب خدا سے راضی ہیں اور خدا نے ان کے لئے وہ باغات مہیا کئے ہیں جن کے نیچے نہریں جاری ہیں اور یہ ان میں ہمیشہ رہنے والے ہیں اور یہی بہت بڑی کامیابی ہے'),
(1336, 9, 101, 'اور تمہارے گرد دیہاتیوں میں بھی منافقین ہیں اور اہل مدینہ میں تو وہ بھی ہیں جو نفاق میں ماہر اور سرکش ہیں تم ان کو نہیں جانتے ہو لیکن ہم خوب جانتے ہیں -عنقریب ہم ان پر دوہرا عذاب کریں گے اس کے بعد یہ عذاب عظیم کی طرف پلٹا دئیے جائیں گے'),
(1337, 9, 102, 'اور دوسرے وہ لوگ جنہوں نے اپنے گناہوں کا اعتراف کیا کہ انہوں نے نیک اور بداعمال مخلوط کردئیے ہیں عنقریب خدا ان کی توبہ قبول کرلے گا کہ وہ بڑا بخشنے والا اور مہربان ہے'),
(1338, 9, 103, 'پیغمبر آپ ان کے اموال میں سے زکوِٰلے لیجئے کہ اس کے ذریعہ یہ پاک و پاکیزہ ہوجائیں اور انہیں دعائیں دیجئے کہ آپ کی دعا ان کے لئے تسکین قلب کا باعث ہوگی اور خدا سب کا سننے والا اور جاننے والا ہے'),
(1339, 9, 104, 'کیا یہ نہیں جانتے کہ اللہ ہی اپنے بندوں کی توبہ قبول کرتا ہے اور زکوِٰ و خیرات کو وصول کرتا ہے اور وہی بڑا توبہ قبول کرنے والا اور مہربان ہے'),
(1340, 9, 105, 'اور پیغمبر کہہ دیجئے کہ تم لوگ عمل کرتے رہو کہ تمہارے عمل کواللہ ً رسول اور صاحبانِ ایمان سب دیکھ رہے ہیں اور عنقریب تم اس خدائے عالم الغیب والشہادہ کی طرف پلٹا دئیے جاؤ گے اور وہ تمہیں تمہارے اعمال سے باخبر کرے گا'),
(1341, 9, 106, 'اور کچھ ایسے بھی ہیں جنہیں حکم هخدا کی امید پر چھوڑ دیا گیا ہے کہ یا خدا ان پر عذاب کرے گا یا ان کی توبہ کو قبول کرلے گا وہ بڑا جاننے والا اور صاحبِ حکمت ہے'),
(1342, 9, 107, 'اور جن لوگوں نے مسجد ضرار بنائی کہ اس کے ذریعہ اسلام کو نقصان پہنچائیں اور کفر کو تقویت بخشیں اور مومنین کے درمیان اختلاف پیدا کرائیں اور پہلے سے خدا و رسول سے جنگ کرنے والوں کے لئے پناہ گاہ تیار کریں وہ بھی منافقین ہی ہیں اور یہ قسم کھاتے ہیں کہ ہم نے صرف نیکی کے لئے مسجد بنائی ہے حالانکہ یہ خدا گواہی دیتا ہے کہ یہ سب جھوٹے ہیں'),
(1343, 9, 108, 'خبردار آپ اس مسجد میں کبھی کھڑے بھی نہ ہوں بلکہ جس مسجد کی بنیاد روز اوّل سے تقوٰیٰ پر ہے وہ اس قابل ہے کہ آپ اس میں نماز ادا کریں -اس میں وہ مرد بھی ہیں جو طہارت کو دوست رکھتے ہیں اور خدا بھی پاکیزہ افراد سے محبت کرتا ہے'),
(1344, 9, 109, 'کیا جس نے اپنی بنیاد خوف خدا اور رضائے الٰہی پر رکھی ہے وہ بہتر ہے یا جس نے اپنی بنیاد اس گرتے ہوئے کگارے کے کنارے پر رکھی ہو کہ وہ ساری عمارت کو لے کر جہّنم میں گر جائے اوراللہ ظالم قوم کی ہدایت نہیں کرتا ہے'),
(1345, 9, 110, 'اور ہمیشہ ان کی بنائی ہوئی عمارت کی بنیاد ان کے دلوں میں شک کا باعث بنی رہے گی مگر یہ کہ ان کے دل کے ٹکڑے ٹکڑے ہوجائیں اور انہیں موت آجائے کہ اللہ خوب جاننے والا اور صاحبِ حکمت ہے'),
(1346, 9, 111, 'بیشک اللہ نے صاحبانِ ایمان سے ان کے جان و مال کو جنّت کے عوض خریدلیا ہے کہ یہ لوگ راہ هخدا میں جہاد کرتے ہیں اور دشمنوں کو قتل کرتے ہیں اور پھر خود بھی قتل ہوجاتے ہیں یہ وعدئہ برحق توریت ,انجیل اور قرآن ہر جگہ ذکر ہوا ہے اور خدا سے زیادہ اپنی عہد کا پورا کرنے والا کون ہوگا تو اب تم لوگ اپنی اس تجارت پر خوشیاں مناؤ جو تم نے خدا سے کی ہے کہ یہی سب سے بڑی کامیابی ہے'),
(1347, 9, 112, 'یہ لوگ توبہ کرنے والے ,عبادت کرنے والے ,حمد پروردگار کرنے والے ,راسِ خد امیں سفر کرنے والے ,رکوع کرنے والے ,سجدہ کرنے والے ,نیکیوں کا حکم دینے والے ,برائیوں سے روکنے والے اور حدود الٰہیہ کی حفاظت کرنے والے ہیں اور اے پیغمبر آپ انہیں جنّت کی بشارت دیدیں'),
(1348, 9, 113, 'نبی اور صاحبانِ ایمان کی شان یہ نہیں ہے کہ وہ مشرکین کے حق میں استغفار کریں چاہے وہ ان کے قرابتدار ہی کیوں نہ ہوں جب کہ یہ واضح ہوچکا ہے کہ یہ اصحاب جہّنم ہیں'),
(1349, 9, 114, 'اور ابراہیم کا استغفار ان کے باپ کے لئے صرف اس وعدہ کی بنا پر تھا جو انہوں نے اس سے کیا تھا اس کے بعد جب یہ واضح ہوگیا کہ وہ دشمن خدا ہے تو اس سے بربَت اور بیزاری بھی کرلی کہ ابراہیم بہت زیادہ تضرع کرنے والے اور اِردبار تھے'),
(1350, 9, 115, 'اور اللہ کسی قوم کو ہدایت دینے کے بعد اس وقت تک گمراہ نہیں قرار دیتا جب تک ان پر یہ واضح نہ کردے کہ انہیں کن چیزوں سے پرہیز کرنا ہے -بیشک اللہ ہر شے کا جاننے والا ہے'),
(1351, 9, 116, 'اللہ ہی کے لئے زمین و آسمان کی حکومت ہے اور وہی حیات و موت کا دینے والا ہے اس کے علاوہ تمہارا نہ کوئی سرپرست ہے نہ مددگار'),
(1352, 9, 117, 'بیشک خدا نے پیغمبر اور ان مہاجرین و انصار پر رحم کیا ہے جنہوں نے تنگی کے وقت میں پیغمبر کا ساتھ دیا ہے جب کہ ایک جماعت کے دلوں میں کجی پیدا ہورہی تھی پھر خدا نے ان کی توبہ کو قبول کرلیا کہ وہ ان پر بڑا ترس کھانے والا اور مہربانی کرنے والا ہے'),
(1353, 9, 118, 'اوراللہ نے ان تینوں پر بھی رحم کیا جو جہاد سے پیچھے رہ گئے یہاں تک کہ زمین جب اپنی وسعتوں سمیت ان پر تنگ ہوگئی اور ان کے دم پر بن گئی اور انہوں نے یہ سمجھ لیا کہ ا ب اللہ کے علاوہ کوئی پناہ گاہ نہیں ہے تواللہ نے ان کی طرف توجہ فرمائی کہ وہ توبہ کرلیں اس لئے کہ وہ بڑا توبہ قبول کرنے والا اور مہربان ہے'),
(1354, 9, 119, 'ایمان والواللہ سے ڈرو اور صادقین کے ساتھ ہوجاؤ'),
(1355, 9, 120, 'اہل مدینہ یا اس کے اطراف کے دیہاتیوں کو یہ حق نہیں ہے کہ وہ رسول خدا سے الگ ہوجائیں یا اپنے نفس کو ان کے نفس سے زیادہ عزیز سمجھیں اس لئے کہ انہیں کوئی پیاسً تھکن یا بھوک راہ هخدا میں نہیں لگتی ہے اور نہ وہ کّفار کے دل رَکھانے والا کوئی قدم اٹھاتے ہیں نہ کسی بھی دشمن سے کچھ حاصل کرتے ہیں مگر یہ کہ ان کے عمل نیک کو لکھ لیا جاتا ہے کہ خدا کسی بھی نیک عمل کرنے والے کے اجر و ثواب کو ضائع نہیں کرتا ہے'),
(1356, 9, 121, 'اور یہ کوئی چھوٹ یا بڑا خرچ راسِ خدا میں نہیں کرتے ہیں اور نہ کسی وادی کو طے کرتے ہیں مگر یہ کہ اسے بھی ان کے حق میں لکھ دیا جاتا ہے تاکہ خدا انہیں ان کے اعمال سے بہتر جزا عطا کرسکے'),
(1357, 9, 122, 'صاحبان ایمان کا یہ فرض نہیں ہے کہ وہ سب کے سب جہاد کے لئے نکل پڑیں تو ہر گروہ میں سے ایک جماعت اس کام کے لئے کیوں نہیں نکلتی ہے کہ دین کا علم حاصل کرے اور پھر جب اپنی قوم کی طرف پلٹ کر آئے تو اسے عذاب الٰہی سے ڈرائے کہ شاید وہ اسی طرح ڈرنے لگیں'),
(1358, 9, 123, 'ایمان والو اپنے آس پاس والے کفار سے جہاد کرو اور وہ تم میں سختی اور طاقت کا احساس کریں اور یاد رکھو کہ اللہ صرف پرہیز گار افراد کے ساتھ ہے'),
(1359, 9, 124, 'اور جب کوئی سورہ نازل ہوتا ہے تو ان میں سے بعض یہ طنز کرتے ہیں کہ تم میں سے کس کے ایمان میں اضافہ ہوگیا ہے تو یاد رکھیں کہ جو ایمان والے ہیں ان کے ایمان میں اضافہ ہوتا ہے اور وہ خوش بھی ہوتے ہیں'),
(1360, 9, 125, 'اور جن کے دلوں میں مرض ہے ان کے مرض میں سورہ ہی سے اضافہ ہوجاتا ہے اور وہ کفر ہی کی حالت میں مرجاتے ہیں'),
(1361, 9, 126, 'کیا یہ نہیں دیکھتے ہیں کہ انہیں ہر سال ایک دو مرتبہ بلا میں مبتلا کیا جاتا ہے پھراس کے بعد بھی نہ توبہ کرتے ہیں اور نہ عبرت حاصل کرتے ہیں'),
(1362, 9, 127, 'اور جب کوئی سورہ نازل ہوتا ہے تو ایک دوسرے کی طرف دیکھنے لگتا ہے کہ کوئی دیکھ تو نہیں رہا ہے اور پھر پلٹ جاتے ہیں تو خدا نے بھی ان کے قلوب کو پلٹ دیاہے کہ یہ سمجھنے والی قوم نہیں ہے'),
(1363, 9, 128, 'یقینا تمہارے پاس وہ پیغمبر آیا ہے جو تم ہی میں سے ہے اور اس پر تمہاری ہر مصیبت شاق ہوتی ہے وہ تمہاری ہدایت کے بارے میں حرص رکھتا ہے اور مومنین کے حال پر شفیق اور مہربان ہے'),
(1364, 9, 129, 'اب اس کے بعد بھی يه لوگ منه پھير ليں تو کہديجئے کہ خدا میرے لیے کافی ہے، اس کے سوا کوئی خدا نہیں، میرا اعتماداسى پر ہے اور وہى عرش اعظم کا پروردگار ہے.');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الۤر -پُر از حکمت کتاب کی آیتیں ہیں'),
(1366, 10, 2, 'کیا لوگوں کے لئے یہ بات عجیب ہے کہ انہیں میں سے ایک مرد پر ہم یہ وحی نازل کردیں کہ لوگوں کو عذاب سے ڈراؤ اور صاحبانِ ایمان کو بشارت دے دو کہ ان کے لئے پروردگار کی بارگاہ میں بلند ترین درجہ ہے .... مگر یہ کافر کہتے ہیں کہ یہ کھلا ہوا جادوگر ہے'),
(1367, 10, 3, 'بیشک تمہارا پروردگار وہ ہے جس نے زمین و آسمان کو چھ دنوں میں پیدا کیا ہے پھر اس کے بعد عرش پر اپنا اقتدار قائم کیاہے وہ تمام امور کی تدبیر کرنے والا ہے کوئی اس کی اجازت کے بغیر شفاعت کرنے والا نہیں ہے .وہی تمہارا پروردگار ہے اور اسی کی عبادت کرو کیا تمہیں ہوش نہیں آرہا ہے'),
(1368, 10, 4, 'اسی کی طرف تم سب کی بازگشت ہے -یہ خدا کا سّچا وعدہ ہے -وہی خلقت کا آغاز کرنے والا ہے اور واپس لے جانے والا ہے تاکہ ایمان اور نیک عمل والوں کو عادلانہ جزا دے سکے اور جو کافر ہوگئے ہیں ان کے لئے تو گرم پانی کا مشروب ہے اور ان کے کفر کی بنا پر دردناک عذاب بھی ہے'),
(1369, 10, 5, 'اسی خدا نے آفتاب کو روشنی اورچاند کو نور بنایا ہے پھر چاند کی منزلیں مقرر کی ہیں تاکہ ان کے ذریعہ برسوں کی تعداد اور دوسرے حسابات دریافت کرسکو -یہ سب خدا نے صرف حق کے ساتھ پیدا کیا ہے کہ وہ صاحبان هعلم کے لئے اپنی آیتوں کو تفصیل کے ساتھ بیان کرتا ہے'),
(1370, 10, 6, 'بیشک دن رات کی آمدورفت اور جو کچھ خدا نے آسمان و زمین میں پیدا کیا ہے ان سب میں صاحبان هتقویٰ کے لئے اس کی نشانیاں پائی جاتی ہیں'),
(1371, 10, 7, 'یقینا جو لوگ ہماری ملاقات کی امید نہیں رکھتے ہیں اور زندگانی دنیا پر ہی راضی اور مطمئن ہوگئے ہیں اور جو لوگ ہماری آیات سے غافل ہیں'),
(1372, 10, 8, 'یہ سب وہ ہیں جن کے اعمال کی بنا پر ان کا ٹھکانا جہّنم ہے'),
(1373, 10, 9, 'بیشک جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے خدا ان کے ایمان کی بنا پر اس منزل کی طرف ان کی رہنمائی کرے گا جہاں نعمتوں کے باغات کے نیچے نہریں جاری ہوں گی'),
(1374, 10, 10, 'وہاں ان کا قول یہ ہوگا کہ خدایا تو پاک اور بے نیاز ہے اور ان کا تحفہ سلام ہوگا اور ان کا آخری بیان یہ ہوگا کہ ساری تعریف خدائے رب العالمین کے لئے ہے'),
(1375, 10, 11, 'اگر خدا لوگوں کے لئے برائی میں بھی اتنی ہی جلدی کردیتا جتنی یہ لوگ بھلائی میں چاہتے ہیں تو اب تک ان کی مدت کا فیصلہ ہوچکا ہوتا - ہم تو اپنی ملاقات کی امید نہ رکھنے والوں کو ان کی سرکشی میں چھوڑ دیتے ہیں کہ یہ یونہی ٹھوکریں کھاتے پھریں'),
(1376, 10, 12, 'انسان کو جب کوئی نقصان پہنچتا ہے تو اٹھتے بیٹھتے کروٹیں بدلتے ہم کو پکارتا ہے اور جب ہم اس نقصان کو دور کردیتے ہیں تو یوں گزر جاتا ہے جیسے کبھی کسی مصیبت میں ہم کو پکارا ہی نہیں تھا بیشک زیادتی کرنے والوں کے اعمال یوں ہی ان کے سامنے آراستہ کردئیے جاتے ہیں'),
(1377, 10, 13, 'یقینا ہم نے تم سے پہلے والی امتوں کو ہلاک کردیا جب انہوں نے ظلم کیا اور ہمارے پیغمبر ہماری نشانیاں لے کر آئے تو وہ ایمان نہ لاسکے -ہم اسی طرح مجرم قوم کو سزا دیتے ہیں'),
(1378, 10, 14, 'اس کے بعد ہم نے تم کو روئے زمین پر ان کا جانشین بنادیا تاکہ دیکھیں کہ اب تم کیسے اعمال کرتے ہو'),
(1379, 10, 15, 'اور جب ان کے سامنے ہماری آیات کی تلاوت کی جاتی ہے تو جن لوگوں کو ہماری ملاقات کی امید نہیں ہے وہ کہتے ہیں کہ اس کے علاوہ کوئی دوسرا قرآن لائیے یا اسی کو بدل دیجئے تو آپ کہہ دیجئے کہ مجھے اپنی طرف سے بدلنے کا کوئی اختیار نہیں ہے میں تو صرف اس امر کا اتباع کرتا ہوں جس کی میری طرف وحی کی جاتی ہے میں اپنے پروردگار کی نافرمانی کروں تو مجھے ایک بڑے عظےم دن کے عذاب کا خوف ہے'),
(1380, 10, 16, 'آپ کہہ دیجئے کہ اگر خدا چاہتا تو میں تمہارے سامنے تلاوت نہ کرتا اور تمہیں اس کی اطلاع بھی نہ کرتا -آخر میں اس سے پہلے بھی تمہارے درمیان ایک مدت تک رہ چکا ہوں تو کیا تمہارے پاس اتنی عقل بھی نہیں ہے'),
(1381, 10, 17, 'اس سے بڑا ظالم کون ہے جو اللہ پر جھوٹا الزام لگائے یا اس کی آیتوں کی تکذیب کرے جب کہ وہ مجرمین کو نجات دینے والا نہیں ہے'),
(1382, 10, 18, 'اور یہ لوگ خدا کو چھوڑ کر ان کی پرستش کرتے ہیں جو نہ نقصان پہنچاسکتے ہیں اور نہ فائدہ اور یہ لوگ کہتے ہیں کہ یہ خدا کے یہاں ہماری سفارش کرنے والے ہیں تو آپ کہہ دیجئے کہ تم تو خدا کو اس بات کو اطلاع کررہے ہو جس کا علم اسے آسمان و زمین میں کہیں نہیں ہے وہ پاک و پاکیزہ ہے اور ان کے شرک سے بلند و برتر ہے'),
(1383, 10, 19, 'سارے انسان فطرتا ایک امت تھے پھر سب آپس میں الگ الگ ہوگئے اور اگر تمہارے رب کی بات پہلے سے طے نہ ہوچکی ہوتی تو یہ جس بات میں اختلاف کرتے ہیں اس کا فیصلہ ہوچکا ہوتا'),
(1384, 10, 20, 'یہ لوگ کہتے ہیں کہ خدا کی طرف سے ان پر کوئی نشانی کیوں نہیں نازل ہوتی تو آپ کہہ دیجئے کہ تمام غیب کا اختیار پروردگار کو ہے اور تم انتظار کرو اور میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں'),
(1385, 10, 21, 'اور جب تکلیف پہنچنے کے بعد ہم نے لوگوں کو ذرا رحمت کا مزہ چکھادیا تو فورا ہماری آیتوں میں مّکاری کرنے لگے تو آپ کہہ دیجئے کہ خدا تم سے تیز تر تدبیریں کرنے والا ہے اور ہمارے نمائندے تمہارے مکر کو برابر لکھ رہے ہیں'),
(1386, 10, 22, 'وہ خدا وہ ہے جو تمہیں خشکی اور سمندر میں سیر کراتا ہے یہاں تک کہ جب تم کشتی میں تھے اور پاکیزہ ہوائیں چلیں اور سارے مسافر خوش ہوگئے تو اچانک ایک تیز ہوا چل گئی اور موجوں نے ہر طرف سے گھیرے میں لے لیا اور یہ خیال پیدا ہوگیا کہ چارں طرف سے کَھرگئے ہیں تو دین خالص کے ساتھ اللہ سے دعا کرنے لگے کہ اگر اس مصیبت سے نجات مل گئی تو ہم یقینا شکر گزاروں میں ہوجائیں گے'),
(1387, 10, 23, 'اس کے بعد جب اس نے نجات دے دی تو زمین میں ناحق ظلم کرنے لگے -انسانو !یاد رکھو تمہارا ظلم تمہارے ہی خلاف ہوگا یہ صرف چند روزہ زندگی کا مزہ ہے اس کے بعد تم سب کی بازگشت ہماری ہی طرف ہوگی اور پھر ہم تمہیں بتائیں گے کہ تم دنیا میں کیا کررہے تھے'),
(1388, 10, 24, 'زندگانی دنیا کی مثال صرف اس بارش کی ہے جسے ہم نے آسمان سے نازل کیا پھر اس سے مل کر زمین سے وہ نباتات برآمد ہوئیں جن کو انسان اورجانور کھاتے ہیں یہاں تک کہ جب زمین نے سبزہ زار سے اپنے کو آراستہ کرلیا اور مالکوں نے خیال کرنا شروع کردیا کہ اب ہم اس زمین کے صاحب هاختیار ہیں تو اچانک ہمارا حکم رات یا دن کے وقت آگیا اور ہم نے اسے بالکل کٹا ہوا کھیت بنادیا گویا اس میں کل کچھ تھا ہی نہیں -ہم اسی طرح اپنی آیتوں کو مفصل طریقہ سے بیان کرتے ہیں اس قوم کے لئے جو صاحبِ فکر و نظر ہے'),
(1389, 10, 25, 'اللہ ہر ایک کو سلامتی کے گھر کی طرف دعوت دیتا ہے اور جسے چاہتا ہے سیدھے راستہ کی ہدایت دے دیتا ہے'),
(1390, 10, 26, 'جن لوگوں نے نیکی کی ہے ان کے واسطے نیکی بھی ہے اور اضافہ بھی اور ان کے چہروں پر نہ سیاہی ہوگی اور نہ ذلت ,وہ جنّت والے ہیں اور وہیں ہمیشہ رہنے والے ہیں'),
(1391, 10, 27, 'اور جن لوگوں نے برائیاں کمائی ہیں ان کے لئے ہر برائی کے بدلے ویسی ہی برائی ہے اور ان کے چہروں پر گناہوں کی سیاہی بھی ہوگی اور انہیں عذاب الٰہی سے بچانے والا کوئی نہ ہوگا -ان کے چہرے پر جیسے سیاہ رات کی تاریکی کا پردہ ڈال دیا گیا ہو -وہ اہل جہّنم ہیں اور اسی میں ہمیشہ رہنے والے ہیں'),
(1392, 10, 28, 'جس دن ہم سب کو اکٹھا جمع کریں گے اور اس کے بعد شرک کرنے والوں سے کہیں گے کہ تم اور تمہارے شرکائ سب اپنی اپنی جگہ ٹھہرو اور پھر ہم ان کے درمیان جدائی ڈال دیں گے اور ان کے شرکائ کہیں گے کہ تم ہماری عبادت تو نہیں کرتے تھے'),
(1393, 10, 29, 'اب خدا ہمارے اور تمہارے درمیان گواہی کے لئے کافی ہے کہ ہم تمہاری عبادت سے بالکل غافل تھے'),
(1394, 10, 30, 'اس وقت ہر شخص اپنے گزشتہ اعمال کا امتحان کرے گا اور سب مولائے برحق خداوندتعالیٰ کی بارگاہ میں پلٹا دئیے جائیں گے اور جو کچھ افترا کررہے تھے وہ سب بھٹک کر گم ہوجائے گا'),
(1395, 10, 31, 'پیغمبر ذرا ان سے پوچھئے کہ تمہیں زمین و آسمان سے کون رزق دیتا ہے اور کون تمہاری سماعت و بصارت کا مالک ہے اور کون لَردہ سے زندہ اور زندہ سے لَردہ کو نکالتا ہے اور کون سارے امور کی تدبیرکرتا ہے تو یہ سب یہی کہیں گے کہ اللہ !تو آپ کہئے کہ پھر اس سے کیوں نہیں ڈرتے ہو'),
(1396, 10, 32, 'وہی اللہ تمہارا برحق پالنے والا ہے اور حق کے بعد ضلالت کے سوا کچھ نہیں ہے تو تم کس طرح لے جائے جارہے ہو'),
(1397, 10, 33, 'اسی طرح خدا کا عذاب فاسقوں کے حق میں ثابت ہوگیا کہ وہ ایمان لانے والے نہیں ہیں'),
(1398, 10, 34, 'آپ ذرا پوچھئے کہ کیا تمہارے شریکوں میں کوئی ہے جو خلقت کی ابتدا کرے اور پھر دوبارہ اسے پلٹا سکے اور پھر بتائیے کہ اللہ ہی مخلوقات کی ابتدا کرتا ہے اور وہی انہیں پلٹاتا بھی ہے تو تم آخر کدھر چلے جارہے ہو'),
(1399, 10, 35, 'کہئے کہ کیا تمہارے شرکائ میں کوئی ایسا ہے جو حق کی ہدایت کرسکے اور پھر بتائیے کہ اللہ ہی حق کی ہدایت کرتا ہے اور جو حق کی ہدایت کرتا ہے وہ واقعا قابلِ اتباع ہے یا جو ہدایت کرنے کے قابل بھی نہیں ہے مگر یہ کہ خود اس کی ہدایت کی جائے تو آخر تمہیں کیاہوگیا ہے اور تم کیسے فیصلے کررہے ہو'),
(1400, 10, 36, 'اور ان کی اکثریت تو صرف خیالات کا اتباع کرتی ہے جب کہ گمان حق کے بارے میں کوئی فائدہ نہیں پہنچاسکتا بیشک اللہ ان کے اعمال سے خوب واقف ہے'),
(1401, 10, 37, 'اور یہ قرآن کسی غیر خدا کی طرف سے افترا نہیں ہے بلکہ اپنے ماسبق کی کتابوں کی تصدیق اور تفصیل ہے جس میں کسی شک کی گنجائش نہیں ہے یہ رب العالمین کا نازل کردہ ہے'),
(1402, 10, 38, 'کیا یہ لوگ یہ کہتے ہیں کہ اسے پیغمبر نے گڑھ لیا ہے تو کہہ دیجئے کہ تم اس کے جیسا ایک ہی سورہ لے آؤ اور خدا کے علاوہ جسے چاہو اپنی مدد کے لئے بلالو اگر تم اپنے الزام میں سچے ہو'),
(1403, 10, 39, 'درحقیقت ان لوگوں نے اس چیز کی تکذیب کی ہے جس کا مکمل علم بھی نہیں ہے اور اس کی تاویل بھی ان کے پاس نہیں آئی ہے اسی طرح ان کے پہلے والوں نے بھی تکذیب کی تھی اب دیکھو کہ ظلم کرنے والوں کا انجام کیا ہوتا ہے'),
(1404, 10, 40, 'ان میں بعض وہ ہیں جو اس پر ایمان لاتے ہیں اور بعض نہیں مانتے ہیں اور آپ کا پروردگار فساد کرنے والوں کو خوب جانتا ہے'),
(1405, 10, 41, 'اور اگر یہ آپ کی تکذیب کریں تو کہہ دیجئے کہ میرے لئے میرا عمل ہے اور تمہارے لئے تمہارا عمل ہے تم میرے عمل سے بری اور میں تمہارے اعمال سے بیزار ہوں'),
(1406, 10, 42, 'اور ان میں سے بعض ایسے بھی ہیں جو بظاہر کان لگا کر سنتے بھی ہیں لیکن کیا آپ بہروں کو بات سنانا چاہتے ہیں جب کہ وہ سمجھتے بھی نہیں ہیں'),
(1407, 10, 43, 'اور ان میں کچھ ہیں جو آپ کی طرف دیکھ رہے ہیں تو کیا آپ اندھوں کو بھی ہدایت دے سکتے ہیں چاہے وہ کچھ نہ دیکھ پاتے ہوں'),
(1408, 10, 44, 'اللہ انسانوں پر ذرّہ برابر ظلم نہیں کرتا ہے بلکہ انسان خود ہی اپنے اوپر ظلم کیا کرتے ہیں'),
(1409, 10, 45, 'جس دن خدا ان سب کو محشور کرے گا اس طرح کہ جیسے دنیا میں صرف ایک ساعت ٹھہرے ہوں اور وہ آپس میں ایک دوسرے کو پہچانتے ہوں گے یقیناجن لوگوں نے خدا کی ملاقات کا انکار کیا ہے وہ خسارہ میں رہے اور ہدایت یافتہ نہیں ہوسکے'),
(1410, 10, 46, 'اور ہم نے جن باتوں کا ان سے وعدہ کیا ہے انہیں آپ کو دکھادیں یا آپ کو پہلے ہی دنیا سے اٹھالیں انہیں تو بہرحال پلٹ کر ہماری ہی بارگاہ میں آنا ہے اس کے بعد خدا خود ان کے اعمال کا گواہ ہے'),
(1411, 10, 47, 'اور ہر امّت کے لئے ایک رسول ہے اور جب رسول آجاتا ہے تو ان کے درمیان عادلانہ فیصلہ ہوجاتا ہے اور ان پر کسی طرح کا ظلم نہیں ہوتا ہے'),
(1412, 10, 48, 'یہ لوگ کہتے ہیں کہ اگر آپ سّچے ہیں تو یہ وعدہ عذاب کب پورا ہوگا'),
(1413, 10, 49, 'کہہ دیجئے کہ میں اپنے نفس کے نقصان و نفع کا بھی مالک نہیں ہوں جب تک خدا نہ چاہے -ہر قوم کے لئے ایک مّدت معین ہے جس سے ایک ساعت کی بھی نہ تاخیر ہوسکتی ہے اور نہ تقدیم'),
(1414, 10, 50, 'کہہ دیجئے کہ تمہارا کیا خیال ہے اگر اس کا عذاب رات کے وقت یا دن میں آجائے تو تم کیا کرو گے آخر یہ مجرمین کس بات کی جلدی کررہے ہیں'),
(1415, 10, 51, 'تو کیا تم عذاب کے نازل ہونے کے بعد ایمان لاؤ گے .... اور کیا اب ایمان لاؤ گے جب کہ تم عذاب کی جلدی مچائے ہوئے تھے'),
(1416, 10, 52, 'اس کے بعد ظالمین سے کہا جائے گا کہ اب ہمیشگی کا مزہ چکھو -کیا تمہارے اعمال کے علاوہ کسی اور چیز کا بدلہ دیا جائے گا'),
(1417, 10, 53, 'یہ آپ سے دریافت کرتے ہیں کیا یہ عذاب برحق ہے تو فرما دیجئے کہ ہاں بیشک برحق ہے اور تم خدا کو عاجز نہیں بناسکتے'),
(1418, 10, 54, 'اگر ہر ظلم کرنے والے نفس کو ساری زمین کے خزینے مل جائیں تو وہ اس دن کے عذاب کے بدلے دے دیگا اور عذاب کے دیکھنے کے بعد اندر اندر نادم بھی ہوگا .... لیکن ان کے درمیان انصاف کے ساتھ فیصلہ کردیا جائے گا اور ان پر کسی طرح کا ظلم نہ کیا جائے گا'),
(1419, 10, 55, 'یاد رکھو کہ خدا ہی کے لئے زمین و آسمان کے کل خزانے ہیں اور آگاہ ہوجاؤ کہ خدا کا وعدہ برحق ہے اگرچہ لوگوں کی اکثریت نہیں سمجھتی ہے'),
(1420, 10, 56, 'وہی خدا حیات و موت کا دینے والا ہے اور اسی کی طرف تم سب کو پلٹا کر لے جایا جائے گا'),
(1421, 10, 57, 'ایہاالناس !تمہارے پاس پروردگار کی طرف سے نصیحت اور دلوں کی شفا کا سامان اور ہدایت اور صاحبانِ ایمان کے لئے رحمت قرآن آچکا ہے'),
(1422, 10, 58, 'پیغمبر کہہ دیجئے کہ یہ قرآن فضل و رحماُ خدا کا نتیجہ ہے لہٰذا انہیں اس پر خوش ہونا چاہئے کہ یہ ان کے جمع کئے ہوئے اموال سے کہیں زیادہ بہتر ہے'),
(1423, 10, 59, 'آپ کہہ دیجئے کہ خدانے تمہارے لئے رزق نازل کیا تو تم نے اس میں بھی حلال و حرام بنانا شروع کردیا تو کیا خدا نے تمہیں اس کی اجازت دی ہے یا تم خدا پر افترا کررہے ہو'),
(1424, 10, 60, 'اور جو لوگ خدا پر جھوٹا الزام لگاتے ہیں ان کا روز هقیامت کے بارے میں کیا خیال ہے -اللہ انسانوں پر فضل کرنے والا ہے لیکن ان کی اکثریت شکر کرنے والی نہیں ہے'),
(1425, 10, 61, 'اور پیغمبر آپ کسی حال میں رہیں اور قرآن کے کسی حصہ کی تلاوت کریں اور اے لوگوں تم کوئی عمل کرو ہم تم سب کے گواہ ہوتے ہیں جب بھی کوئی عمل کرتے ہو اور تمہارے پروردگار سے زمین و آسمان کا کوئی ذرّہ دور نہیں ہے اور کوئی شے ذرّہ سے بڑی یا چھوٹی ایسی نہیں ہے جسے ہم نے اپنی کھلی کتاب میں جمع نہ کردیا ہو'),
(1426, 10, 62, 'آگاہ ہوجاؤ کہ اولیائ خدا پر نہ خوف طاری ہوتا ہے اور نہ وہ محزون اور رنجیدہ ہوتے ہیں'),
(1427, 10, 63, 'یہ وہ لوگ ہیں جو ایمان لائے اور خدا سے ڈرتے رہے'),
(1428, 10, 64, 'ان کے لئے زندگانی دنیا اور آخرت دونوں مقامات پر بشارت اور خوشخبری ہے اور کلمات خدا میں کوئی تبدیلی نہیں ہوسکتی ہے اور یہی درحقیقت عظیم کامیابی ہے'),
(1429, 10, 65, 'پیغمبر آپ ان لوگوں کی باتوں سے رنجیدہ نہ ہوں -عزت سب کی سب صرف اللہ کے لئے ہے -وہی سب کچھ سننے والا ہے اور جاننے والا ہے'),
(1430, 10, 66, 'آگاہ ہوجاؤ کہ اللہ ہی کے لئے زمین و آسمان کی ساری مخلوقات اور کائنات ہے اور جو لوگ خدا کو چھوڑ کر دوسرے شریکوں کو پکارتے ہیں وہ ان کا بھی اتباع نہیں کرتے -یہ صرف اپنے خیالات کا اتباع کرتے ہیں اور یہ صرف اندازوں پر زندگی گزار رہے ہیں'),
(1431, 10, 67, 'وہ خدا وہ ہے جس نے تمہارے لئے رات بنائی ہے تاکہ اس میں سکون حاصل کرسکو اور دن کو روشنی کا ذریعہ بنایا ہے اور اس میں بھی بات سننے والی قوم کے لئے نشانیاں پائی جاتی ہیں'),
(1432, 10, 68, 'یہ لوگ کہتے ہیں کہ اللہ نے اپنا کوئی بیٹا بنایا ہے حالانکہ وہ پاک و بے نیاز ہے اور اس کے لئے زمین و آسمان کی ساری کائنات ہے -تمہارے پاس تو تمہاری بات کی کوئی دلیل بھی نہیں ہے-کیا تم لوگ خدا پر وہ الزام لگاتے ہو جس کا تمہیں علم بھی نہیں ہے'),
(1433, 10, 69, 'پیغمبر آپ کہہ دیجئے کہ جو لوگ خدا پر جھوٹا الزام لگاتے ہیں وہ کبھی کامیاب نہیں ہوسکتے'),
(1434, 10, 70, 'اس دنیا میں تھوڑا سا آرام ہے اس کے بعد سب کی بازگشت ہماری ہی طرف ہے -اس کے بعد ہم ان کے کفر کی بنا پر انہیں شدید عذاب کا مزہ چکھائیں گے'),
(1435, 10, 71, 'آپ ان کّفار کے سامنے نوح علیھ السّلام کا واقعہ بیان کریں کہ انہوں نے اپنی قوم سے کہا کہ اگر تمہارے لئے میرا قیام اور آیات الٰہٰیہ کا یاد دلانا سخت ہے تو میرا اعتماد اللہ پر ہے. تم بھی اپناارادہ پختہ کرلو اور اپنے شریکوں کو بلالو اور تمہاری کوئی بات تمہارے اوپر مخفی بھی نہ رہے -پھر جو جی چاہے کر گزرو اور مجھے کسی طرح کی مہلت نہ دو'),
(1436, 10, 72, 'پھر اگر تم انحراف کرو گے تو میں تم سے کوئی اجر بھی نہیں چاہتا -میرا اجر تو صرف اللہ کے ذمہ ہے اور مجھے حکم دیا گیا ہے کہ میں اس کے اطاعت گزاروں میں شامل رہوں'),
(1437, 10, 73, 'پھر قوم نے ان کی تکذیب کی تو ہم نے نوح علیھ السّلام اور ان کے ساتھیوں کو کشتی میں نجات دے دی اور انہیں زمین کا وارث بنادیا اور اپنی آیات کی تکذیب کرنے والوں کو ڈبو دیا تو اب دیکھئے کہ جن کو ڈرایا جاتا ہے ان کے نہ ماننے کا انجام کیا ہوتا ہے'),
(1438, 10, 74, 'اس کے بعد ہم نے مختلف رسول ان کی قوموں کی طرف بھیجے اور وہ ان کے پاس کھلی ہوئی نشانیاں لے کر آئے لیکن وہ لوگ پہلے کے انکار کرنے کی بنا پر ان کی تصدیق نہ کرسکے اور ہم اسی طرح ظالموں کے دل پر مہر لگادیتے ہیں'),
(1439, 10, 75, 'پھر ہم نے ان رسولوں کے بعد فرعون اور اس کی جماعت کی طرف موسٰی اور ہارون کو اپنی نشانیاں دے کر بھیجا تو ان لوگوں نے بھی انکار کردیا اور وہ سب بھی مجرم لوگ تھے'),
(1440, 10, 76, 'پھر جب موسٰی ان کے پاس ہماری طرف سے حق لے کر آئے تو انہوں نے کہا کہ یہ توکھلا ہوا جادو ہے'),
(1441, 10, 77, 'موسٰی نے جواب دیا کہ تم لوگ حق کے آنے کے بعد اسے جادو کہہ رہے ہو کیا یہ تمہارے نزدیک جادو ہے جب کہ جادوگر کبھی کامیاب نہیں ہوتے'),
(1442, 10, 78, 'ان لوگوں نے کہا تم یہ پیغام اس لئے لائے ہو کہ ہمیں باپ دادا کے راستہ سے منحرف کردو اور تم دونوں کو زمین میں حکومت و اقتدار مل جائے اور ہم ہرگز تمہاری بات مانتے والے نہیں ہیں'),
(1443, 10, 79, 'اور فرعون نے کہا کہ تمام ہوشیار ماہر جادوگروں کو میرے پاس حاضر کرو'),
(1444, 10, 80, 'پھر جب جادوگر آگئے تو موسٰی علیھ السّلامنے ان سے کہا جو جو کچھ پھینکنا چاہتے ہو پھینکو'),
(1445, 10, 81, 'پھر جب ان لوگوں نے رسیوں کو ڈال دیا تو موسٰی نے کہا کہ جو کچھ تم لے آئے ہو یہ جادو ہے اور اللہ اس کو بیکار کردے گا وہ مفسدین کے عمل کو درست نہیں ہونے دیتا ہے'),
(1446, 10, 82, 'اپنے کلمات کے ذریعہ حق کو ثابت کردیتا ہے چاہے مجرمین کو کتنا ہی برا کیوں نہ لگے'),
(1447, 10, 83, 'پھر بھی موسٰی پر ایمان نہ لائے مگر ان کی قوم کی ایک نسل اور وہ بھی فرعون اور اس کی جماعت کے خوف کے ساتھ کہ کہیں وہ کسی آزمائش میں نہ مبتلا کردے کہ فرعون بہت اونچا ہے اور وہ اسراف اور زیادتی کرنے والا بھی ہے'),
(1448, 10, 84, 'اور موسٰی نے اپنی قوم سے کہا کہ اگر تم واقعا اللہ پر ایمان لائے ہو تو اسی پر بھروسہ کرو اگر واقعا اس کے اطاعت گزار بندے ہو'),
(1449, 10, 85, 'تو ان لوگوں نے کہا کہ بیشک ہم نے خدا پر بھروسہ کیا ہے. خدایا ہمیں ظالموں کے فتنوں اور آزمائشوں کا مرکز نہ قرار دینا'),
(1450, 10, 86, 'اور اپنی رحمت کے ذریعہ کافر قوم کے شر سے محفوظ رکھنا'),
(1451, 10, 87, 'اور ہم نے موسٰی اور ان کے بھائی کی طرف وحی کی کہ اپنی قوم کے لئے مصر میں گھر بناؤ اور اپنے گھروں کو قبلہ قرار دو اور نماز قائم کرو اور مومنین کو بشار ت دے دو'),
(1452, 10, 88, 'اور موسٰی نے کہا کہ پروردگار تو نے فرعون اور اس کے ساتھیوں کو زندگانی دنیامیں اموال عطا کئے ہیں -خدایا یہ تیرے راستے سے بہکائیں گے -خدایا ان کے اموال کو برباد کردے -ان کے دلوں پر سختی نازل فرما یہ اس وقت تک ایمان نہ لائیں گے جب تک اپنی آنکھوں سے دردناک عذاب نہ دیکھ لیں'),
(1453, 10, 89, 'پروردگار نے فرمایا کہ تم دونوں کی دعا قبول کرلی گئی تو اب اپنے راستے پر قائم رہنا اور جاہلوں کے راستے کا اتباع نہ کرنا'),
(1454, 10, 90, 'اور ہم نے بنی اسرائیل کو دریا پار کرادیا تو فرعون اور اس کے لشکر نے ازراہ هظلم و تعدی ان کا پیچھا کیا یہاں تک کہ جب غرقابی نے اسے پکڑلیا تو اس نے آواز دی کہ میں اس خدائے وحدہ لاشریک پر ایمان لے آیا ہوں جس پر بنی اسرائیل ایمان لائے ہیں اور میں اطاعت گزاروں میں ہیں'),
(1455, 10, 91, 'تو آواز آئی .... کہ اب جب کہ تو پہلے نافرمانی کرچکا ہے اور تیرا شمار مفسدوں میں ہوچکا ہے'),
(1456, 10, 92, 'خیر ... آج ہم تیرے بدن کو بچالیتے ہیں تاکہ تو اپنے بعد والوں کے لئے نشانی بن جائے اگرچہ بہت سے لوگ ہماری نشانیوں سے غافل ہی رہتے ہیں'),
(1457, 10, 93, 'اور ہم نے بنی اسرائیل کو بہترین منزل عطا کی ہے اور انہیں پاکیزہ رزق عطا کیا ہے تو ان لوگوں نے آپس میں اختلاف نہیں کیا یہاں تک کہ ان کے پاس توریت آگئی تو اب خدا ان کے درمیان روزِ قیامت ان کے اختلافات کا فیصلہ کردے گا'),
(1458, 10, 94, 'اب اگر تم کو اس میں شک ہے جو ہم نے نازل کیا ہے تو ان لوگوں سے پوچھو جو اس کے پہلے کتاب توریت و انجیل پڑھتے رہے ہیں یقینا تمہارے پروردگار کی طرف سے حق آچکا ہے تو اب تم شک کرنے والوں میں شامل نہ ہو'),
(1459, 10, 95, 'اور ان لوگوں میں نہ ہو جاؤ جنہوں نے آیات الہٰیہ کی تکذیب کی ہے کہ اس طرح خسارہ والوں میں شمار ہوجاؤ گے'),
(1460, 10, 96, 'بیشک جن لوگوں پر کلمئہ عذابِ الٰہی ثابت ہوچکا ہے وہ ہرگز ایمان لانے والے نہیں ہیں'),
(1461, 10, 97, 'چاہے ان کے پاس تمام نشانیاں کیوں نہ آجائیں جب تک اپنی آنکھوں سے دردناک عذاب نہ دیکھ لیں گے'),
(1462, 10, 98, 'پس کوئی بستی ایسی کیوں نہیں ہے جو ایمان لے آئے اور اس کا ایمان اسے فائدہ پہنچائے علاوہ قوم یونس کے کہ جب وہ ایمان لے آئے تو ہم نے ان سے زندگانی دنیا میں رسوائی کا عذاب دفع کردیا اور انہیں ایک مّدت تک چین سے رہنے دیا'),
(1463, 10, 99, 'اور اگر خدا چاہتا تو روئے زمین پر رہنے والے سب ایمان لے آتے -تو کیا آپ لوگوں پر جبر کریں گے کہ سب مومن بن جائیں'),
(1464, 10, 100, 'اور کسی نفس کے امکان میں نہیں ہے کہ بغیر اجازت و توفیق پروردگار کے ایمان لے آئے اور وہ ان لوگوں پر خباثت کو لازم قرار دے دیتا ہے جو عقل استعمال نہیں کرتے ہیں'),
(1465, 10, 101, 'پیغمبر کہہ دیجئے کہ ذرا آسمان و زمین میں غور و فکر کرو .... اور یاد رکھئے کہ جو ایمان لانے والے نہیں ہیں ان کے حق میں نشانیاں اور ڈراوے کچھ کام آنے والے نہیں ہیں'),
(1466, 10, 102, 'اب کیا یہ لوگ ان ہی اِرے دنوں کا انتظار کررہے ہیں جو ان سے پہلے والوں پر گزر چکے ہیں تو کہہ دیجئے کہ پھر انتظار کرو میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں'),
(1467, 10, 103, 'اس کے بعد ہم اپنے رسولوں اور ایمان والوں کو نجات دیتے ہیں اور یہ ہمارے اوپر ایک حق ہے کہ ہم صاحبان هایمان کو نجات دلائیں'),
(1468, 10, 104, 'پیغمبر آپ کہہ دیجئے کہ اگر تم لوگوں کو میرے دین میں شک ہے تو میں ان کی پرستش نہیں کرسکتا جنہیں تم لوگ خدا کو چھوڑ کر پوج رہے ہو -میں تو صرف اس خدا کی عبادت کرتا ہوں جو تم سب کو موت دینے والا ہے اور مجھے حکم دیا گیا ہے کہ میں صاحبان هایمان میں شامل رہوں'),
(1469, 10, 105, 'اور آپ اپنا رخ بالکل دین کی طرف رکھیں. باطل سے الگ رہیں اور ہرگز مشرکین کی جماعت میں شمار نہ ہوں'),
(1470, 10, 106, 'اور خدا کے علاوہ کسی ایسے کو آواز نہ دیں جو نہ فائدہ پہنچا سکتا ہے اور نہ نقصان ورنہ ایسا کریں گے تو آپ کا شمار بھی ظالمین میں ہوجائے گا'),
(1471, 10, 107, 'اور اگر خدا نقصان پہنچانا چاہے تو اس کے علاوہ کوئی بچانے والا نہیں ہے اور اگر وہ بھلائی کا ارادہ کرلے تو اس کے فضل کا کوئی روکنے والا نہیں ہے وہ جس کو چاہتا ہے اپنے بندوں میں بھلائی عطا کرتا ہے وہ بڑا بخشنے والا اور مہربان ہے'),
(1472, 10, 108, 'پیغمبر آپ کہہ دیجئے کہ تمہارے پاس پروردگار کی طرف سے حق آچکا ہے اب جو ہدایت حاصل کرے گا وہ اپنے فائدہ کے لئے کرے گا اور جو گمراہ ہوجائے گا اس کا نقصان بھی اسی کو ہوگا اور میں تمہارا ذمہ دار نہیں ہوں'),
(1473, 10, 109, 'اور آپ صرف اس بات کا اتباع کریں جس کے آپ کی طرف وحی کی جاتی ہے اور صبر کرتے رہیں یہاں تک کہ خدا کوئی فیصلہ کردے اور وہ بہترین فیصلہ کرنے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الۤر - یہ وہ کتاب ہے جس کی آیتیں محکم بنائی گئی ہیں اور ایک صاحبِ علم و حکمت کی طرف سے تفصیل کے ساتھ بیان کی گئی ہیں'),
(1475, 11, 2, 'کہ اللہ کے علاوہ کسی کی عبادت نہ کرو -میں اسی کی طرف سے ڈرانے والا اور بشارت دینے والا ہوں'),
(1476, 11, 3, 'اور اپنے رب سے استغفار کرو پھر اس کی طرف متوجہ ہوجاؤ وہ تم کو مقررہ مدّت میں بہترین فائدہ عطا کرے گا اور صاحبِ فضل کو اس کے فضل کا حق دے گا اور میں تمہارے بارے میں ایک بڑے دن کے عذاب سے خوفزدہ ہوں'),
(1477, 11, 4, 'تم سب کی بازگشت خدا ہی کی طرف ہے اور وہ ہر شے پر قدرت رکھنے والا ہے'),
(1478, 11, 5, 'آگاہ ہوجاؤ کہ یہ لوگ اپنے سینوں کو دہرائے لے رہے ہیں کہ اس طرح پیغمبر سے چھپ جائیں تو آگاہ رہیں کہ یہ جب اپنے کپڑوں کو خوب لپیٹ لیتے ہیں تو اس وقت بھی وہ ان کے ظاہر و باطن دونوں کو جانتا ہے کہ وہ تمام سینوں کے رازوں کا جاننے والا ہے'),
(1479, 11, 6, 'اور زمین پر چلنے والی کوئی مخلوق ایسی نہیں ہے جس کا رزق خدا کے ذمہ نہ ہو -وہ ہر ایک کے سونپے جانے کی جگہ اور اس کے قرار کی منزل کو جانتا ہے اور سب کچھ کتاب مبین میں محفوظ ہے'),
(1480, 11, 7, 'اور وہی وہ ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا ہے اور اس کا تخت اقتدار پانی پر تھا تاکہ تمہیں آزمائے کہ تم میں سب سے بہتر عمل کرنے والا کون ہے اور اگر آپ کہیں گے کہ تم لوگ موت کے بعد پھر اٹھائے جانے والے ہو تو یہ کافر کہیں گے کہ یہ تو صرف ایک کھلا ہوا جادو ہے'),
(1481, 11, 8, 'اور اگر ہم ان کے عذاب کو ایک معینہ مدّت کے لئے ٹال دیں تو طنز کریں گے کہ عذاب کو کس چیز نے روک لیا ہے -آگاہ ہوجاؤ کہ جس دن عذاب آجائے گا تو پھر پلٹنے والا نہیں ہے اور پھر وہ عذاب ان کو ہر طرف سے گھیر لے گا جس کا یہ مذاق اڑا رہے تھے'),
(1482, 11, 9, 'اور اگر ہم انسان کو رحمت دے کر چھین لیتے ہیں تو مایوس ہوجاتا ہے اور کفر کرنے لگتا ہے'),
(1483, 11, 10, 'اور اگر تکلیف پہنچنے کے بعد نعمت اور آرام کا مزہ چکھا دیتے ہیں تو کہتا ہے کہ اب تو ہماری ساری برائیاں چلی گئیں اور وہ خوش ہوکر اکڑنے لگتا ہے'),
(1484, 11, 11, 'علاوہ ان لوگوں کے جو ایمان لائے ہیں اور انہوں نے نیک اعمال کئے ہیں کہ ان کے لئے مغفرت ہے اور بہت بڑا اجر بھی ہے'),
(1485, 11, 12, 'پس کیا تم ہماری وحی کے بعض حصوں کو اس لئے ترک کرنے والے ہو یا اس سے تمہارا سینہ اس لئے تنگ ہوا ہے کہ یہ لوگ کہیں گے کہ ان کے اوپر خزانہ کیوں نہیں نازل ہوا یا ان کے ساتھ َمُلک کیوں نہیں آیا ...تو آپ صرف عذاب الٰہی سے ڈرانے والے ہیں اور اللہ ہر شئے کا نگراں اور ذمہ دار ہے'),
(1486, 11, 13, 'کیا یہ لوگ کہتے ہیں کہ یہ قرآن بندے نے گڑھ لیا ہے تو کہہ دیجئے کہ اس کے جیسے دس سورہ گڑھ کر تم بھی لے آؤ اور اللہ کے علاوہ جس کو چاہو اپنی مدد کے لئے بلالو اگر تم اپنی بات میں سچے ہو'),
(1487, 11, 14, 'پھر اگر یہ آپ کی بات قبول نہ کریں تو تم سب سمجھ لو کہ جو کچھ نازل کیا گیا ہے سب خدا کے علم سے ہے اور اس کے علاوہ کوئی خدا نہیں ہے تو کیا اب تم اسلام لانے والے ہو'),
(1488, 11, 15, 'جو شخص زندگانی دنیا اور اس کی زینت ہی چاہتا ہے ہم اس کے اعمال کا پورا پورا حساب یہیں کردیتے ہیں اور کسی طرح کی کمی نہیں کرتے ہیں'),
(1489, 11, 16, 'اور یہی وہ ہیں جن کے لئے آخرت میں جہنم ّکے علاوہ کچھ نہیں ہے اور ان کے سارے کاروبار برباد ہوگئے ہیں اور سارے اعمال باطل و بے اثر ہوگئے ہیں'),
(1490, 11, 17, 'کیا جو شخص اپنے رب کی طرف سے کھلی دلیل رکھتا ہے اور اس کے پیچھے اس کا گواہ بھی ہے اور اس کے پہلے موسٰی کی کتاب گواہی دے رہی ہے جو قوم کے لئے پیشوا اور رحمت تھی -وہ افترا کرے گا بیشک صاحبانِ ایمان اسی پر ایمان رکھتے ہیں اور جو لوگ اس کا انکار کرتے ہیں ان کاٹھکانہ جہنم ّہے تو خبردار تم اس قرآن کی طرف سے شک میں مبتلا نہ ہونا -یہ خدا کی طرف سے برحق ہے اگرچہ اکثر لوگ اس پر ایمان نہیں لاتے ہیں'),
(1491, 11, 18, 'اور اس سے بڑا ظالم کون ہے جو اللہ پر جھوٹا الزام لگاتا ہے. یہی وہ لوگ ہیں جو خدا کے سامنے پیش کئے جائیں گے تو سارے گواہ گواہی دیں گے کہ ان لوگوں نے خدا کے بارے میں غلط بیانی سے کام لیا ہے تو آگاہ ہوجاؤ کہ ظالمین پر خدا کی لعنت ہے'),
(1492, 11, 19, 'جو راسِ خدا سے روکتے ہیں اور اس میں کجی پیدا کرنا چاہتے ہیں اور آخرت کے بارے میں کفر اور انکار کرنے والے ہیں'),
(1493, 11, 20, 'یہ لوگ نہ روئے زمین میں خدا کو عاجز کرسکتے ہیں اور نہ خد اکے علاوہ ان کا کوئی ناصر و مددگار ہے ان کا عذاب دگنا کردیا جائے گا کہ یہ نہ حق بات سن سکتے تھے اور نہ اس کے منظر عام کو دیکھ سکتے تھے'),
(1494, 11, 21, 'یہی وہ لوگ ہیں جنہوں نے خود اپنے نفس کو خسارہ میں مبتلا کیا اور ان سے وہ بھی گم ہوگئے جن کا افترا کیا کرتے تھے'),
(1495, 11, 22, 'یقینا یہ لوگ آخرت میں بہت بڑا گھاٹا اٹھانے والے ہیں'),
(1496, 11, 23, 'بیشک جو لوگ ایمان لے آئے اور انہوں نے نیک اعمال انجام دئیے اور اپنے رب کی بارگاہ میں عاجزی سے پیش آئے وہی اہل جّنت ہیں اور اس میں ہمیشہ رہنے والے ہیں'),
(1497, 11, 24, 'کافر اور مسلمان کی مثال اندھے بہرے اور دیکھنے سننے والے کی ہے تو کیا یہ دونوں مثال کے اعتبار سے برابر ہوسکتے ہیں تمہیں ہوش کیوں نہیں آتا ہے'),
(1498, 11, 25, 'اور ہم نے نوح کو ان کی قوم کی طرف اس پیغام کے ساتھ بھیجاکہ میں تمہارے لئے کھلے ہوئے عذاب الٰہی سے ڈرانے والا ہوں'),
(1499, 11, 26, 'اور یہ کہ خبردار تم اللہ کے علاوہ کسی کی عبادت نہ کرنا کہ میں تمہارے بارے میں دردناک دن کے عذاب کا خوف رکھتا ہوں'),
(1500, 11, 27, 'تو ان کی قوم کے بڑے لوگ جنہوں نے کفر اختیار کرلیا تھا -انہوں نے کہا کہ ہم تو تم کو اپنا ہی جیسا ایک انسان سمجھ رہے ہیں اور تمہارے اتباع کرنے والوں کو دیکھتے ہیں کہ وہ ہمارے پست طبقہ کے سادہ لوح افراد ہیں. ہم تم میں اپنے اوپر کوئی فضیلت نہیں دیکھتے ہیں بلکہ تمہیں جھوٹا خیال کرتے ہیں'),
(1501, 11, 28, 'انہوں نے جواب دیا کہ اے قوم تمہارا کیا خیال ہے کہ اگر میں اپنے پروردگار کی طرف سے دلیل رکھتا ہوں اور وہ مجھے اپنی طرف سے وہ رحمت عطا کردے جو تمہیں دکھائی نہ دے تو کیا میں ناگواری کے باوجود زبردستی تمہارے اوپر لادسکتا ہوں'),
(1502, 11, 29, 'اے قوم میں تم سے کوئی مال تو نہیں چاہتا ہوں -میرا اجر تو اللہ کے ذمہ ہے اور میں صاحبان هایمان کو نکال بھی نہیں سکتا ہوں کہ وہ لوگ اپنے پروردگار سے ملاقات کرنے والے ہیں البتہ میں تم کو ایک جاہل قوم تصور کررہا ہوں'),
(1503, 11, 30, 'اے قوم میں ان لوگوں کو نکال باہر کردوں تو اللہ کی طرف سے میرا مددگار کون ہوگا کیا تمہیں ہوش نہیں آتا ہے'),
(1504, 11, 31, 'اور میں تم سے یہ بھی نہیں کہتا ہوں کہ میرے پاس تمام خدائی خزانے موجود ہیں اور نہ ہر غیب کے جاننے کا دعوٰی کرتا ہوں اور نہ یہ کہتا ہوں کہ میں فرشتہ ہوں اور نہ جو لوگ تمہارے نگاہوں میں ذلیل ہیں ان کے بارے میں یہ کہتا ہوں کہ خدا انہیں خیر نہ دے گا -اللہ ان کے دلوں سے خوب باخبر ہے -میں ایسا کہہ دوں گا تو ظالموں میں شمار ہوجاؤں گا'),
(1505, 11, 32, 'ان لوگوں نے کہا کہ نوح آپ نے ہم سے جھگڑا کیا اور بہت جھگڑا کیا تو اب جس چیز کا وعدہ کررہے تھے اسے لے آؤ اگر تم اپنے دعوٰی میں سچے ہو'),
(1506, 11, 33, 'نوح نے کہا کہ وہ تو خدا لے آئے گا اگر چاہے گا اور تم اسے عاجز بھی نہیں کرسکتے ہو'),
(1507, 11, 34, 'اور میں تمہیں نصیحت بھی کرنا چاہوں تو میری نصیحت تمہارے کام نہیں آئے گی اگر خدا ہی تم کو گمراہی میں چھوڑ دینا چاہے -وہی تمہارا پروردگار ہے اور اسی کی طرف تم پلٹ کر جانے والے ہو'),
(1508, 11, 35, 'کیا یہ لوگ یہ کہتے ہیں کہ انہوں نے اپنے پاس سے گڑھ لیا ہے تو آپ کہہ دیجئے کہ اگر میں نے گڑھا ہے تو اس کا جرم میرے ذمہ ہے اور میں تمہارے جرائم سے بری اور بیزار ہوں'),
(1509, 11, 36, 'اور نوح کی طرف یہ وحی کی گئی کہ تمہاری قوم میں سے اب کوئی ایمان نہ لائے گا علاوہ ان کے جو ایمان لاچکے لہذا تم ان کے افعال سے رنجیدہ نہ ہو'),
(1510, 11, 37, 'اور ہماری نگاہوں کے سامنے ہماری وحی کی نگرانی میں کشتی تیار کرو اور ظالموں کے بارے میں مجھ سے بات نہ کرو کہ یہ سب غرق ہوجانے والے ہیں'),
(1511, 11, 38, 'اور نوح کشتی بنارہے تھے اور جب بھی قوم کی کسی جماعت کا گزر ہوتا تھا تو ان کا مذاق اڑاتے تھے -نوح نے کہا کہ اگر تم ہمارا مذاق اڑاؤ گے تو کل ہم اسی طرح تمہارا بھی مذاق اڑائیں گے'),
(1512, 11, 39, 'پھر عنقریب تمہیں معلوم ہوجائے گا کہ جس کے پاس عذاب آتا ہے اسے رسوا کردیا جاتا ہے اور پھر وہ عذاب دائمی ہی ہوجاتا ہے'),
(1513, 11, 40, 'یہاں تک کہ جب ہمارا حکم آگیا اور تنور سے پانی ابلنے لگا تو ہم نے کہا کہ نوح اپنے ساتھ ہر جوڑے میں سے دو کو لے لو اور اپنے اہل کو بھی لے لو علاوہ ان کے جن کے بارے میں ہلاکت کا فیصلہ ہوچکا ہے اور صاحبان هایمان کو بھی لے لو اور ان کے ساتھ ایمان والے بہت ہی کم تھے'),
(1514, 11, 41, 'نوح نے کہا کہ اب تم سب کشتی میں سوار ہوجاؤ خدا کے نام کے سہارے اس کا بہاؤ بھی ہے اور ٹھہراؤ بھی اور بیشک میرا پروردگار بڑا بخشنے والا مہربان ہے'),
(1515, 11, 42, 'اور وہ کشتی انہیں لے کر پہاڑوں جیسی موجوں کے درمیان چلی جارہی تھی کہ نوح نے اپنے فرزند کو آواز دی جو الگ جگہ پر تھا کہ فرزند ہمارے ساتھ کشتی میں سوار ہوجا اور کافروں میں نہ ہوجا'),
(1516, 11, 43, 'اس نے کہا کہ میں عنقریب پہاڑ پر پناہ لے لوں گا وہ مجھے پانی سے بچالے گا -نوح نے کہا کہ آج حکم خدا سے کوئی بچانے والا نہیں ہے سوائے اس کے جس پر خود خدا رحم کرے اور پھر دونوں کے درمیان موج حائل ہوگئی اور وہ ڈوبنے والوں میں شامل ہوگیا'),
(1517, 11, 44, 'اور قدرت کا حکم ہوا کہ اے زمین اپنے پانی کو نگل لے اور اے آسمان اپنے پانی کو روک لے .اور پھر پانی گھٹ گیا اور کام تمام کردیا گیا اور کشتی کوئہ جودی پر ٹھہر گئی اور آواز آئی کہ ہلاکت قوم هظالمین کے لئے ہے'),
(1518, 11, 45, 'اور نوح نے اپنے پروردگار کو پکارا کہ پروردگار میرا فرزند میرے اہل میں سے ہے اور تیرا وعدہ اہل کو بچانے کا برحق ہے اور تو بہترین فیصلہ کرنے والا ہے'),
(1519, 11, 46, 'ارشاد ہوا کہ نوح یہ تمہارے اہل سے نہیں ہے یہ عمل هغیر صالح ہے لہذا مجھ سے اس چیز کے بارے میں سوال نہ کرو جس کا تمہیں علم نہیں ہے -میں تمہیں نصیحت کرتا ہوں کہ تمہارا شمار جاہلوں میں نہ ہوجائے'),
(1520, 11, 47, 'نوح نے کہا کہ خدایا میں اس بات سے پناہ مانگتا ہوں کہ اس چیز کا سوال کروں جس کا علم نہ ہو اور اگر تو مجھے معاف نہ کرے گا اور مجھ پر رحم نہ کرے گا تو میں خسارہ والوں میں ہوجاؤں گا'),
(1521, 11, 48, 'ارشاد ہوا کہ نوح ہماری طرف سے سلامتی اور برکتوں کے ساتھ کشتی سے اترو یہ سلامتی اور برکت تمہارے ساتھ کی قوم پر ہے اور کچھ قومیں ہیں جنہیں ہم پہلے راحت دیں گے اس کے بعد ہماری طرف سے دردناک عذاب ملے گا'),
(1522, 11, 49, 'پیغمبر علیھ السّلامیہ غیب کی خبریں ہیں جن کی ہم آپ کی طرف وحی کررہے ہیں جن کا علم نہ آپ کو تھا اور نہ آپ کی قوم کو لہذا آپ صبر کریں کہ انجام صاحبان هتقویٰ کے ہاتھ میں ہے'),
(1523, 11, 50, 'اور ہم نے قوم عاد کی طرف ان کے بھائی ہود کو بھیجا تو انہوں نے کہا قوم والو اللہ کی عبادت کرو-اس کے علاوہ تمہارا کوئی معبود نہیں ہے.تم صرف افترا کرنے والے ہو'),
(1524, 11, 51, 'قوم والو میں تم سے کسی اجرت کا سوال نہیں کرتا میرا اجر تو اس پروردگار کے ذمہ ہے جس نے مجھے پیدا کیا ہے کیا تم عقل استعمال نہیں کرتے ہو'),
(1525, 11, 52, 'اے قوم خدا سے استغفار کرو اس کے بعد اس کی طرف ہمہ تن متوجہ ہوجاؤ وہ آسمان سے موسلادھار پانی برسائے گا اور تمہاری موجودہ قوت میں قوت کا اضافہ کردے گا اور خبردار مجرموں کی طرح منہ نہ پھیرلینا'),
(1526, 11, 53, 'ان لوگوں نے کہا اے ہود تم کوئی معجزہ تو لائے نہیں اور ہم صرف تمہارے کہنے پر اپنے خداؤں کو چھوڑنے والے اور تمہاری بات پر ایمان لانے والے نہیں ہیں'),
(1527, 11, 54, 'ہم صرف یہ کہنا چاہتے ہیں کہ ہمارے خداؤں میں سے کسی نے آپ کو دیوانہ بنادیا ہے -ہود نے کہا کہ میں خدا کو گواہ بناکر کہتا ہوں اور تم بھی گواہ رہنا کہ میں تمہارے شرک سے بیزار ہوں'),
(1528, 11, 55, 'لہذا تم سب مل کر میرے ساتھ مکاری کرو اور مجھے مہلت نہ دو'),
(1529, 11, 56, 'میرا اعتماد پروردگار پر ہے جو میرا اور تمہارا سب کا خدا ہے اور کوئی زمین پر چلنے والا ایسا نہیںہے جس کی پیشانی اس کے قبضہ میں نہ ہو میرے پروردگار کا راستہ بالکل سیدھا ہے'),
(1530, 11, 57, 'اس کے بعد بھی انحراف کرو تو میں نے خدائی پیغام کو پہنچادیا ہے اب خدا تمہاری جگہ پر دوسری قوموں کو لے آئے گا اور تم اس کا کچھ نہیں بگاڑ سکتے ہو بیشک میرا پروردگار ہر شے کا نگراں ہے'),
(1531, 11, 58, 'اور جب ہمارا حکم آگیا تو ہم نے ہود اور ان کے ساتھ ایمان لانے والوں کو اپنی رحمت سے بچالیااور انہیں سخت عذاب سے نجات دے دی'),
(1532, 11, 59, 'یہ قوم عاد ہے جس نے پروردگار کی آیتوں کا انکار کیا اس کے رسولوں کی نافرمانی کی اور ہر ظالم و سرکش کا اتباع کرلیا'),
(1533, 11, 60, 'اور اس دنیا میں بھی اور قیامت میں بھی ان کے پیچھے لعنت لگادی گئی ہے -آگاہ ہوجاؤ کہ عاد نے اپنے پررودگار کا کفر کیا تو اب ہود کی قوم عاد کے لئے ہلاکت ہی ہلاکت ہے'),
(1534, 11, 61, 'اور ہم نے قوم ثمود کی طرف ان کے بھائی صالح کو بھیجا اور انہوں نے کہا کہ اے قوم اللہ کی عبادت کرو اس کے علاوہ کوئی خدا نہیں ہے اس نے تمہیںزمین سے پیدا کیا ہے اور اس میں آباد کیا ہے اب اس سے استغفار کرو اور اس کی طرف متوجہ ہوجاؤ کہ میرا پروردگار قریب تر اور دعاؤں کا قبول کرنے والا ہے'),
(1535, 11, 62, 'ان لوگوں نے کہا کہ اے صالح اس سے پہلے تم سے بڑی امیدیں وابستہ تھیں کیا تم اس بات سے روکتے ہو کہ ہم اپنے بزرگوں کے معبودوں کی پرستش کریں -ہم یقینا تمہاری دعوت کی طرف سے شک اور شبہ میں ہیں'),
(1536, 11, 63, 'انہوں نے کہا اے قوم تمہارا کیا خیال ہے کہ اگر میں اپنے پروردگار کی طرف سے دلیل رکھتا ہوں اور اس نے مجھے رحمت عطا کی ہے تو اگر میں اس کی نافرمانی کروں گا تو اس کے مقابلہ میں کون میری مدد کرسکے گا تم تو گھاٹے میں اضافہ کے علاوہ کچھ نہیں کرسکتے ہو'),
(1537, 11, 64, 'اے قوم یہ ناقہ اللہ کی طرف سے ایک نشانی ہے اسے آزاد چھوڑ دو تاکہ زمین هخدا میں چین سے کھائے اور اسے کسی طرح کی تکلیف نہ دینا کہ تمہیں جلدی ہی کوئی عذاب اپنی گرفت میں لے لے'),
(1538, 11, 65, 'اس کے بعد بھی ان لوگوں نے اس کی کونچیں کاٹ دیں تو صالح نے کہا کہ اب اپنے گھروں میں تین دن تک اور آرام کرو کہ یہ وعدہ الٰہی ہے جو غلط نہیں ہوسکتا ہے'),
(1539, 11, 66, 'اس کے بعد جب ہمارا حکم عذاب آپہنچا تو ہم نے صالح اور ان کے ساتھ کے صاحبانِ ایمان کو اپنی رحمت سے اپنے عذاب اور اس دن کی رسوائی سے بچالیا کہ تمہارا پروردگار صاحب هقوت اور سب پر غالب ہے'),
(1540, 11, 67, 'اور ظلم کرنے والوں کو ایک چنگھاڑنے اپنی لپیٹ میں لے لیا تو وہ اپنے دیار میں اوندھے پڑے رہ گئے'),
(1541, 11, 68, 'جیسے کبھی یہاں بسے ہی نہیں تھے -آگاہ ہوجاؤ کہ ثمود نے اپنے رب کی نافرمانی کی ہے اور ہوشیار ہوجاؤ کہ ثمود کے لئے ہلاکت ہی ہلاکت ہے'),
(1542, 11, 69, 'اور ابراہیم کے پاس ہمارے نمائندے بشارت لے کر آئے اور آکر سلام کیا تو ابراہیم نے بھی سلام کیا اور تھوڑی دیر نہ گزری تھی کہ بھنا ہوا بچھڑا لے آئے'),
(1543, 11, 70, 'اور جب دیکھا کہ ان لوگوں کے ہاتھ ادھر نہیں بڑھ رہے ہیں تو تعجب کیا اور ان کی طرف سے خوف محسوس کیا انہوں نے کہا کہ آپ ڈریں نہیں ہم قوم لوط کی طرف بھیجے گئے ہیں'),
(1544, 11, 71, 'ابراہیم کی زوجہ اسی جگہ کھڑی تھیں یہ سن کر ہنس پڑیں تو ہم نے انہیں اسحاق کی بشارت دے دی اور اسحاق کے بعد پھر یعقوب کی بشارت دی'),
(1545, 11, 72, 'تو انہوں نے کہا کہ یہ مصیبت اب میرے یہاں بچہ ہوگا جب کہ میں بھی بوڑھی ہوں اور میرے میاں بھی بوڑھے ہیں یہ تو بالکل عجیب سی بات ہے'),
(1546, 11, 73, 'فرشتوں نے کہا کہ کیا تمہیں حکم الٰہی میں تعجب ہورہا ہے اللہ کی رحمت اور برکت تم گھر والوں پر ہے وہ قابلِ حمد اور صاحب همجد و بزرگی ہے'),
(1547, 11, 74, 'اس کے بعد جب ابراہیم کا خوف برطرف ہوا اور ان کے پاس بشارت بھی آچکی تو انہوں نے ہم سے قوم لوط کے بارے میں اصرار کرنا شروع کردیا'),
(1548, 11, 75, 'بیشک ابراہیم بہت ہی دردمند اور خدا کی طرف رجوع کرنے والے تھے'),
(1549, 11, 76, 'ابراہیم اس بات سے اعراض کرو -اب حکم خدا آچکا ہے اور ان لوگوں تک وہ عذاب آنے والا ہے جو پلٹایا نہیں جاسکتا'),
(1550, 11, 77, 'اور جب ہمارے فرستادے لوط کے پاس پہنچے تو وہ ان کے خیال سے رنجیدہ ہوئے اور تنگ دل ہوگئے اور کہا کہ یہ بڑا سخت دن ہے'),
(1551, 11, 78, 'اور ان کی قوم دوڑتی ہوئی آگئی اور اس کے پہلے بھی یہ لوگ ایسے برے کام کررہے تھے لوط نے کہا اے قوم یہ ہماری لڑکیاں تمہارے لئے زیادہ پاکیزہ ہیں خدا سے ڈرو اور مہمانوں کے بارے میں مجھے شرمندہ نہ کرو کیا تم میں کوئی سمجھدار آدمی نہیں ہے'),
(1552, 11, 79, 'ان لوگوں نے کہا کہ آپ جانتے ہیں کہ ہمیں آپ کی لڑکیوں میں کوئی دلچسپی نہیں ہے اور آپ کو معلوم ہے کہ ہم کیا چاہتے ہیں'),
(1553, 11, 80, 'لوط نے کہا کاش میرے پاس قوت ہوتی یا میں کسی مضبوط قلعہ میں پناہ لے سکتا'),
(1554, 11, 81, 'تو فرشتوں نے کہا کہ ہم آپ کے پروردگار کے نمائندے ہیں یہ ظالم آپ تک ہرگز نہیں پہنچ سکتے ہیں -آپ اپنے گھر والوں کو لے کر رات کے کسے حّصے میں چلے جائیے اور کوئی شخص کسی کی طرف مڑ کر بھی نہ دیکھے سوائے آپ کی زوجہ کے کہ اس تک وہی عذاب آنے والا ہے جو قوم تک آنے والا ہے ان کا وقت مقرر ہنگام هصبح ہے اور کیا صبح قریب نہیں ہے'),
(1555, 11, 82, 'پھر جب ہمارا عذاب آگیا تو ہم نے زمین کو تہ و بالا کردیا اور ان پر مسلسل کھرنجے دار پتھروں کی بارش کردی'),
(1556, 11, 83, 'جن پر خدا کی طرف سے نشان لگے ہوئے تھے اور وہ بستی ان ظالموں سے کچھ دور نہیں ہے'),
(1557, 11, 84, 'اور ہم نے مدین کی طرف ان کے بھائی شعیب کو بھیجا تو انہوں نے کہا کہ اے قوم اللہ کی عبادت کر کہ اس کہ علاوہ تیرا کوئی خدا نہیں ہے اور خبردار ناپ تول میں کمی نہ کرنا کہ میں تمہیں بھلائی میں دیکھ رہا ہوں اور میں تمہارے بارے میں اس دن کے عذاب سے ڈرتا ہوں جو سب کو احاطہ کرلے گا'),
(1558, 11, 85, 'اے قوم ناپ تول میں انصاف سے کام لو اور لوگوں کو کم چیزیں مت دو اور روئے زمین میں فساد مت پھیلاتے پھرو'),
(1559, 11, 86, 'اللہ کی طرف کا ذخیرہ تمہارے حق میں بہت بہتر ہے اگر تم صاحبِ ایمان ہو اور میں تمہارے معاملات کا نگراں اور ذمہ دار نہیں ہوں'),
(1560, 11, 87, 'ان لوگوں نے طنز کیا کہ شعیب کیا تمہاری نماز تمہیں یہ حکم دیتی ہے کہ ہم اپنے بزرگوں کے معبودوں کو چھوڑ دیں یا اپنے اموال میں اپنی مرضی کے مطابق تصرف نہ کریں تم تو بڑے بردبار اور سمجھ دار معلوم ہوتے ہو'),
(1561, 11, 88, 'شعیب نے کہا کہ اے قوم والو تمہارا کیا خیال ہے کہ اگر میں خدا کی طرف سے روشن دلیل رکھتا ہوں اور اس نے مجھے بہترین رزق عطا کردیا ہے اور میں یہ بھی نہیں چاہتا ہوں کہ جس چیز سے تم کو روکتا ہوں خود اسی کی خلاف ورزی کروں میں تو صرف اصلاح چاہتا ہوں جہاں تک میرے امکان میں ہو -میری توفیق صرف اللہ سے وابستہ ہے اسی پر میرا اعتماد ہے اور اسی کی طرف میں توجہ کررہا ہوں'),
(1562, 11, 89, 'اور اے قوم کہیں میری مخالفت تم پر ایسا عذاب نازل نہ کرادے جیسا عذاب قوم نوح علیھ السّلام,قوم ہود علیھ السّلامیا قوم صالح علیھ السّلام پر نازل ہوا تھا اور قوم لوط بھی تم سے کچھ دور نہیں ہے'),
(1563, 11, 90, 'اور اپنے پروردگار سے استغفار کرو اس کے بعد اس کی طرف متوجہ ہوجاؤ کہ بیشک میرا پروردگار بہت مہربان اور محبت کرنے والا ہے'),
(1564, 11, 91, 'ان لوگوں نے کہا کہ اے شعیب آپ کی اکثر باتیں ہماری سمجھ میں نہیں آتی ہیں اور ہم توآپ کو اپنے درمیان کمزور ہی پارہے ہیں کہ اگر آپ کا قبیلہ نہ ہوتا تو ہم آپ کو سنگسار کردیتے اور آپ ہم پر غالب نہیں آسکتے تھے'),
(1565, 11, 92, 'شعیب نے کہا کہ کیا میرا قبیلہ تمہاری نگاہ میں اللہ سے زیادہ عزیز ہے اور تم نے اللہ کو بالکل پس پشت ڈال دیا ہے جب کہ میرا پروردگار تمہارے اعمال کا خوب احاطہ کئے ہوئے ہے'),
(1566, 11, 93, 'اور اے قوم تم اپنی جگہ پر اپنا کام کرو میں اپنا کام کررہا ہوں عنقریب جان لو گے کہ کس کے پاس عذاب آکر اسے رسوا کردیتا ہے اور کون جھوٹا ہے اور انتظار کرو کہ میں بھی تمہارے ساتھ انتظار کرنے والا ہوں'),
(1567, 11, 94, 'اور جب ہمارا حکم (عذاب)آگیا تو ہم نے شعیب اور ان کے ساتھ ایمان لانے والوں کو اپنی رحمت سے بچالیا اور ظلم کرنے والوں کو ایک چنگھاڑنے پکڑ لیا تو وہ اپنے دیار ہی میں الٹ پلٹ ہوگئے'),
(1568, 11, 95, 'جیسے کبھی یہاں بسے ہی نہیں تھے اور آگاہ ہوجاؤ کہ قوم مدین کے لئے ویسے ہی ہلاکت ہے جیسے قوم ثمود ہلاک ہوگئی تھی'),
(1569, 11, 96, 'اور ہم نے موسٰی کو اپنی نشانیوں اور روشن دلیل کے ساتھ بھیجا'),
(1570, 11, 97, 'فرعون اور اس کی قوم کی طرف تو لوگوں نے فرعون کے حکم کا اتباع کرلیا جب کہ فرعون کا حکم عقل و ہوش والا حکم نہیں تھا'),
(1571, 11, 98, 'وہ روزِ قیامت اپنی قوم کے آگے آگے چلے گا اور انہیں جہّنم ّمیں وارد کردے گا جو بدترین وارد ہونے کی جگہ ہے'),
(1572, 11, 99, 'ان لوگوں کے پیچھے اس دنیا میں بھی لعنت لگادی گئی ہے اور روز قیامت بھی یہ بدترین عطیہ ہے جو انہیں دیا جائے گا'),
(1573, 11, 100, 'یہ چند بستیوں کی خبریں ہیں جو ہم آپ سے بیان کررہے ہیں -ان میں سے بعض باقی رہ گئی ہیں اور بعض کٹ پٹ کر برابر ہو گئی ہیں'),
(1574, 11, 101, 'اور ہم نے ان پر کوئی ظلم نہیں کیا ہے بلکہ انہوں نے خود اپنے اوپر ظلم کیا ہے تو عذاب کے آجانے کے بعد ان کے وہ خدا بھی کام نہ آئے جنہیں وہ خدا کو چھوڑ کر پکار رہے تھے اور ان خداؤں نے مزید ہلاکت کے علاوہ انہیں کچھ نہیں دیا'),
(1575, 11, 102, 'اور اسی طرح تمہارے پروردگار کی گرفت ہوتی ہے جب وہ ظلم کرنے والی بستیوں کو اپنی گرفت میں لے لیتا ہے کہ اس کی گرفت بہت ہی سخت اور دردناک ہوتی ہے'),
(1576, 11, 103, 'اس بات میں ان لوگوں کے لئے نشانی پائی جاتی ہے جو عذاب آخرت سے ڈرنے والے ہیں -وہ دن جس دن تمام لوگ جمع کئے جائیں گے اور وہ سب کی حاضری کا دن ہوگا'),
(1577, 11, 104, 'اور ہم اپنے عذاب کو صرف ایک معینہ مدّت کے لئے ٹال رہے ہیں'),
(1578, 11, 105, 'اس کے بعد جس دن وہ آجائے گا تو کوئی شخص بھی ِذنِ خدا کے بغیر کسی سے بات بھی نہ کرسکے گا -اس دن کچھ بدبخت ہوں گے اور کچھ نیک بخت'),
(1579, 11, 106, 'پس جو لوگ بدبخت ہوں گے وہ جہّنم میں رہیں گے جہاں اُن کے لئے صرف ہائے وائے اور چیخ پکار ہوگی'),
(1580, 11, 107, 'وہ وہیں ہمیشہ رہنے والے ہیں جب تک آسمان و زمین قائم ہیں مگر یہ کہ آپ کا پروردگار نکالنا چاہے کہ وہ جو بھی چاہے کرسکتا ہے'),
(1581, 11, 108, 'اور جو لوگ نیک بخت ہیں وہ جنت میں ہوں گے اور وہیں ہمیشہ رہیں گے جب تک کہ آسمان و زمین قائم ہیں مگر یہ کہ پروردگار اس کے خلاف چاہے ...._یہ خدا کی ایک عطا ہے جو ختم ہونے والی نہیں ہے'),
(1582, 11, 109, 'لہذا خدا کے علاوہ جس کی بھی یہ پرستش کرتے ہیں اس کی طرف سے آپ کسی شبہ میں نہ پڑیں یہ اسی طرح پرستش کررہے ہیں جس طرح ان کے باپ دادا کررہے تھے اور ہم انہیں پورا پورا حّصہ بغیر کسی کمی کے دیں گے'),
(1583, 11, 110, 'اور ہم نے موسٰی علیھ السّلام کو کتاب دی تو اس میں بھی اختلاف پیدا کردیا گیا اور اگر تمہارے پروردگار کی طرف سے پہلے بات نہ ہوگئی ہوتی تو ان کے درمیان فیصلہ کردیا جاتا اور یہ لوگ اس عذاب کی طرف سے شک میں پڑے ہوئے ہیں'),
(1584, 11, 111, 'اور یقیناتمہارا پروردگار سب کے اعمال کا پورا پورا بدلہ دے گا اور وہ ان سب کے اعمال سے خوب باخبر ہے'),
(1585, 11, 112, 'لہذا آپ کو جس طرح حکم دیا گیا ہے اسی طرح استقامت سے کام لیں اور وہ بھی جنہوں نے آپ کے ساتھ توبہ کرلی ہے اور کوئی کسی طرح کی زیادتی نہ کرے کہ خدا سب کے اعمال کو خوب دیکھنے والا ہے'),
(1586, 11, 113, 'اور خبردار تم لوگ ظالموں کی طرف جھکاؤ اختیار نہ کرنا کہ جہّنم کی آگ تمہیں چھولے گی اور خدا کے علاوہ تمہارا کوئی سرپرست نہیں ہوگا اور تمہاری مدد بھی نہیں کی جائے گی'),
(1587, 11, 114, 'اور پیغمبر آپ دن کے دونوں حصّو ں میں اور رات گئے نماز قائم کریں کہ نیکیاں برائیوں کو ختم کردینے والی ہیں اور یہ ذک» خدا کرنے والوں کے لئے ایک نصیحت ہے'),
(1588, 11, 115, 'اور آپ صبر سے کام لیں کہ خدا نیک عمل کرنے والوں کے اجر کو ضائع نہیں کرتا ہے'),
(1589, 11, 116, 'تو تمہارے پہلے والے زمانوں اور نسلوں میں ایسے صاحبان عقل کیوں نہیں پیدا ہوئے ہیں جو لوگوں کو زمین میں فساد پھیلانے سے روکتے علاوہ ان چند افراد کے جنہیں ہم نے نجات دے دی اور ظالم تو اپنے عیش ہی کے پیچھے پڑے رہے اور یہ سب کے سب مجرم تھے'),
(1590, 11, 117, 'اور آپ کے پروردگار کا کام یہ نہیں ہے کہ کسی قریہ کو ظلم کرکے تباہ کردے جب کہ اس کے رہنے والے اصلاح کرنے والے ہوں'),
(1591, 11, 118, 'اور اگر آپ کا پروردگار چاہ لیتا تو سارے انسانوں کو ایک قوم بنادیتا (لیکن وہ جبر نہیں کرتاہے) لہذا یہ ہمیشہ مختلف ہی رہیں گے'),
(1592, 11, 119, 'علاوہ ان کے جن پر خدا نے رحم کردیا ہو اور اسی لئے انہیں پیدا کیا ہے اور آپ کے پروردگار کی یہ بات قطعی حق ہے کہ ہم جہنم ّکو انسانوں اور جنوں سے بھر کر رہیں گے'),
(1593, 11, 120, 'اور ہم قدیم رسولوں کے واقعات آپ سے بیان کررہے ہیں کہ ان کے ذریعہ آپ کے دل کو مضبوط رکھیں اور ان واقعات میں حق ,نصیحت اور صاحبان هایمان کے لئے سامانِ عبرت بھی ہے'),
(1594, 11, 121, 'اور آپ ان بے ایمانوں سے کہہ دیں کہ تم اپنی جگہ پر کام کرو اور ہم اپنی جگہ پر اپنا کام کررہے ہیں'),
(1595, 11, 122, 'اور پھر انتظار کرو کہ ہم بھی انتظار کرنے والے ہیں'),
(1596, 11, 123, 'اور اللہ ہی کے لئے آسمان اور زمین کا کل غیب ہے اور اسی کی طرف تمام امور کی بازگشت ہے لہذا آپ اسی کی عبادت کریں اور اسی پر اعتماد کریں کہ آپ کا رب لوگوں کے اعمال سے غافل نہیں ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'آلر-یہ کتاب مبین کی آیتیں ہیں'),
(1598, 12, 2, 'ہم نے اسے عربی قرآن بناکر نازل کیا ہے کہ شاید تم لوگوں کو عقل آجائے'),
(1599, 12, 3, 'پیغمبر ہم آپ کے سامنے ایک بہترین قصہ بیان کررہے ہیں جس کی وحی اس قرآن کے ذریعہ آپ کی طرف کی گئی ہے اگرچہ اس سے پہلے آپ اس کی طرف سے بے خبر لوگوں میں تھے'),
(1600, 12, 4, 'اس وقت کو یاد کرو جب یوسف نے اپنے والد سے کہا کہ بابا میں نے خواب میں گیارہ ستاروں اور آفتاب و ماہتاب کو دیکھا ہے اور یہ دیکھا ہے کہ یہ سب میرے سامنے سجدہ کررہے ہیں'),
(1601, 12, 5, 'یعقوب نے کہا کہ بیٹا خبردار اپنا خواب اپنے بھائیوں سے بیان نہ کرنا کہ وہ لوگ الٹی سیدھی تدبیروں میں لگ جائیں گے کہ شیطان انسان کا بڑا کھلا ہوا دشمن ہے'),
(1602, 12, 6, 'اور اسی طرح تمہارا پروردگار تمہیں منتخب کرے گا اور تمہیں باتوں کی تاویل سکھائے گا اور تم پر اور یعقوب کی دوسری اولاد پر اپنی نعمت کو تمام کرے گا جس طرح تمہارے دادا پرداد ابراہیم اور اسحاق پر تمام کرچکا ہے بیشک تمہارا پروردگار بڑا جاننے والا ہے اور صاحب هحکمت ہے'),
(1603, 12, 7, 'یقینا یوسف اور ان کے بھائیوں کے واقعہ میں سوال کرنے والوں کے لئے بڑی نشانیاں پائی جاتی ہیں'),
(1604, 12, 8, 'جب ان لوگوں نے کہا کہ یوسف اور ان کے بھائی (ابن یامین)ہمارے باپ کی نگاہ میں زیادہ محبوب ہیں حالانکہ ہماری ایک بڑی جماعت ہے یقیناہمارے ماں باپ ایک کھلی ہوئی گمراہی میں مبتلا ہیں'),
(1605, 12, 9, 'تم لوگ یوسف کو قتل کردو یا کسی زمین میں پھینک دو تو باپ کا رخ تمہاری ہی طرف ہوجائے گا اور تم سب ان کے بعد صالح قوم بن جاؤ گے'),
(1606, 12, 10, 'ان میں سے ایک شخص نے کہا کہ یوسف کو قتل نہ کرو بلکہ کسی اندھے کنویں میں ڈال دو تاکہ کوئی قافلہ اٹھالے جائے اگر تم کچھ کرنا ہی چاہتے ہو'),
(1607, 12, 11, 'ان لوگوں نے یعقوب سے کہا کہ بابا کیا بات ہے کہ آپ یوسف کے بارے میں ہم پر بھروسہ نہیں کرتے ہیں حالانکہ ہم ان پر شفقت کرنے والے ہیں'),
(1608, 12, 12, 'کل ہمارے ساتھ بھیج دیجئے کچھ کھائے پئے اور کھیلے اور ہم تو اس کی حفاظت کرنے والے موجود ہی ہیں'),
(1609, 12, 13, 'یعقوب نے کہا کہ مجھے اس کالے جانا تکلیف پہنچاتا ہے اور میں ڈرتا ہوں کہ کہیں اسے بھیڑیا نہ کھاجائے اور تم غافل ہی رہ جاؤ'),
(1610, 12, 14, 'اور ان لوگوں نے کہا کہ اگر اسے بھیڑیا کھاگیا اور ہم سب اس کے بھائی ہی ہیں تو ہم بڑے خسارہ والوں میں ہوجائیں گے'),
(1611, 12, 15, 'اس کے بعد جب وہ سب یوسف کو لے گئے اور یہ طے کرلیا کہ انہیں اندھے کنویں میں ڈال دیں اور ہم نے یوسف کی طرف وحی کردی کہ عنقریب تم ان کو اس سازش سے باخبر کرو گے اور انہیں خیال بھی نہ ہوگا'),
(1612, 12, 16, 'اور وہ لوگ رات کے وقت باپ کے پاس روتے پیٹے آئے'),
(1613, 12, 17, 'کہنے لگے بابا ہم دوڑ لگانے چلے گئے اور یوسف کو اپنے سامان کے پاس چھوڑ دیا تو ایک بھیڑیا آکر انہیں کھاگیا اور آپ ہماری بات کا یقین نہ کریں گے چاہے ہم کتنے ہی سچے کیوں نہ ہوں'),
(1614, 12, 18, 'اور یوسف کے کرتے پر جھوٹا خون لگاکر لے آئے -یعقوب نے کہا کہ یہ بات صرف تمہارے دل نے گڑھی ہے لہذا میرا راستہ صبر جمیل کا ہے اور اللہ تمہارے بیان کے مقابلہ میں میرا مددگارہے'),
(1615, 12, 19, 'اور وہاں ایک قافلہ آیا جس کے پانی نکالنے والے نے اپنا ڈول کنویں میں ڈالا تو آواز دی ارے واہ یہ تو بچہ ہے اور اسے ایک قیمتی سرمایہ سمجھ کر چھپالیا اور اللہ ان کے اعمال سے خوب باخبر ہے'),
(1616, 12, 20, 'اور ان لوگوں نے یوسف کو معمولی قیمت پر بیچ ڈالا چند درہم کے عوض اور وہ لوگ تو ان سے بیزار تھے ہی'),
(1617, 12, 21, 'اور مصر کے جس شخص نے انہیں خریدا تھا اس نے اپنی بیوی سے کہاکہ اسے عزّت و احترام کے ساتھ رکھو شاید یہ ہمیں کوئی فائدہ پہنچائے یا ہم اسے اپنا فرزند بنالیں اور اس طرح ہم نے یوسف کو زمین میں اقتدار دیا اور تاکہ اس طرح انہیں خوابوں کی تعبیر کا علم سکھائیں اور اللہ اپنے کام پر غلبہ رکھنے والا ہے یہ اور بات ہے کہ اکثر لوگوں کو اس کا علم نہیں ہے'),
(1618, 12, 22, 'اور جب یوسف اپنی جوانی کی عمر کو پہنچے تو ہم نے انہیں حکم اور علم عطا کردیا کہ ہم اسی طرح نیک عمل کرنے والوں کو جزا دیا کرتے ہیں'),
(1619, 12, 23, 'اور اس نے ان سے اظہار همحبتّ کیا جس کے گھر میں یوسف رہتے تھے اور دروازے بند کردیئے اور کہنے لگی لو آؤ یوسف نے کہا کہ معاذ اللہ وہ میرا مالک ہے اس نے مجھے اچھی طرح رکھا ہے اور ظلم کرنے والے کبھی کامیاب نہیں ہوتے'),
(1620, 12, 24, 'اور یقینااس عورت نے ان سے برائی کا ارادہ کیااور وہ بھی ارادہ کربیٹھتے اگر اپنے رب کی دلیل نہ دیکھ لیتے یہ تو ہم نے اس طرح کا انتظام کیا کہ ان سے برائی اور بدکاری کا رخ موڑ دیں کہ وہ ہمارے مخلص بندوں میں سے تھے'),
(1621, 12, 25, 'اور دونوں نے دروازے کی طرف سبقت کی اور اس نے ان کا کرتا پیچھے سے پھاڑ دیا اور دونوں نے اس کے سردار کو دروازہ ہی پر دیکھ لیا -اس نے گھبرا کر فریاد کی کہ جو تمہاری عورت کے ساتھ برائی کا ارادہ کرے اس کی سزا اس کے علاوہ کیا ہے کہ اسے قیدی بنادیاجائے یا اس پر دردناک عذاب کیا جائے'),
(1622, 12, 26, 'یوسف نے کہا کہ اس نے خود مجھ سے اظہار محبت ّکیا ہے اور اس پر اس کے گھر والوں میں سے ایک گواہ نے گواہی بھی دے دی کہ اگر ان کا دامن سامنے سے پھٹا ہے تو وہ سچی ہے اور یہ جھوٹوں میں سے ہیں'),
(1623, 12, 27, 'اور اگر ان کا کرتا پیچھے سے پھٹا ہے تو وہ جھوٹی ہے یہ سچوں میںسے ہیں'),
(1624, 12, 28, 'پھر جو دیکھاکہ ان کا کرتا پیچھے سے پھٹا ہے توا س نے کہا کہ یہ تم عورتوں کی مّکاری ہے تمہارا مکر بہت عظیم ہوتا ہے'),
(1625, 12, 29, 'یوسف اب تم اس سے اعراض کرو اور زلیخا تو اپنے گناہ کے لئے استغفار کر کہ تو خطاکاروں میں ہے'),
(1626, 12, 30, 'اور پھر شہر کی عورتوں نے کہنا شروع کردیا کہ عزیز مصر کی عورت اپنے جوان کو اپنی طرف کھینچ رہی تھی اور اسے اس کی محبت نے مدہوش بنادیا تھا ہم یہ دیکھ رہے ہیں کہ یہ عورت بالکل ہی کھلی ہوئی گمراہی میں ہے'),
(1627, 12, 31, 'پھر جب زلیخا نے ان عورتوں کی مکاری اور تشہیر کا حال سنا تو بلا بھیجا اور ان کے لئے پرتکلف دعوت کا انتطام کرکے مسند لگادی اور سب کو ایک ایک چھری دے دی اور یوسف سے کہا کہ تم ان کے سامنے سے نکل جاؤ پھر جیسے ہی ان لوگوں نے دیکھا تو بڑا حسین و جمیل پایا اور اپنے ہاتھ کاٹ ڈالے اور کہا کہ ماشائ اللہ یہ تو آدمی نہیں ہے بلکہ کوئی محترم فرشتہ ہے'),
(1628, 12, 32, 'زلیخا نے کہا کہ یہی وہ ہے جس کے بارے میں تم لوگوں نے میری ملامت کی ہے اور میں نے اسے کھینچنا چاہا تھا کہ یہ بچ کر نکل گیا اور جب میری بات نہیں مانی تو اب قید کیا جائے گا اور ذلیل بھی ہوگا'),
(1629, 12, 33, 'یوسف نے کہا کہ پروردگار یہ قید مجھے اس کام سے زیادہ محبوب ہے جس کی طرف یہ لوگ دعوت دے رہی ہیں اور اگر تو ان کے مکر کو میری طرف سے موڑ نہ دے گا تو میں ان کی طرف مائل ہوسکتا ہوں اور میرا شمار بھی جاہلوں میں ہوسکتا ہے'),
(1630, 12, 34, 'تو ان کے پروردگار نے ان کی بات قبول کرلی اور ان عورتوں کے مکر کو پھیر دیا کہ وہ سب کی سننے والا اور سب کا جاننے والا ہے'),
(1631, 12, 35, 'اس کے بعد ان لوگوں کو تمام نشانیاں دیکھنے کے بعد بھی یہ خیال آگیا کہ کچھ مدّت کے لئے یوسف کو قیدی بنا دیں'),
(1632, 12, 36, 'اور قید خانہ میں ان کے ساتھ دو جوان اور داخل ہوئے ایک نے کہا کہ میں نے خواب میں اپنے کو شراب نچوڑتے دیکھا ہے اور دوسرے نے کہا میں نے دیکھا ہے کہ میں اپنے سر پر روٹیاں لادے ہوں اور پرندے اس میں سے کھارہے ہیں -ذرا اس کی تاوہل تو بتاؤ کہ ہماری نظر میں تم نیک کردار معلوم ہوتے ہو'),
(1633, 12, 37, 'یوسف نے کہا کہ جو کھانا تم کو دیا جاتا ہے وہ نہ آنے پائے گا اور میں تمہیں تعبیر بتادوں گا -یہ تعبیر مجھے میرے پروردگار نے بتائی ہے اور میں نے اس قوم کے راستے کو چھوڑ دیا ہے جس کا ایمان اللہ پر نہیں ہے اور وہ روز هآخرت کا بھی انکار کرنے والی ہے'),
(1634, 12, 38, 'میں اپنے باپ دادا ابراہیم -اسحاق اور یعقوب کے طریقے کا پیرو ہوں -میرے لئے ممکن نہیں ہے کہ میں کسی چیز کو بھی خدا کا شریک بناؤں -یہ تو میرے اوپر اور تمام انسانوں پر خدا کا فضل و کرم ہے لیکن انسانوں کی اکثریت شکر خدا نہیں کرتی ہے'),
(1635, 12, 39, 'میرے قید خانے کے ساتھیو !ذرا یہ تو بتاؤ کہ متفرق قسم کے خدا بہتر ہوتے ہیں یا ایک خدائے واحدُ و قہار'),
(1636, 12, 40, 'تم اس خدا کو چھوڑ کر صرف ان ناموں کی پرستش کرتے ہو جنہیں تم نے خود رکھ لیا ہے یا تمہارے آبائ و اجداد نے -اللہ نے ان کے بارے میں کوئی دلیل نازل نہیں کی ہے جب کہ حکم کرنے کا حق صرف خدا کو ہے اور اسی نے حکم دیا ہے کہ اس کے علاوہ کسی کی عبادت نہ کی جائے کہ یہی مستحکم اور سیدھا دین ہے لیکن اکثر لوگ اس بات کو نہیں جانتے ہیں'),
(1637, 12, 41, 'میرے قید خانے کے ساتھیو تم سے ایک اپنے مالک کو شراب پلائے گا اور دوسرا سولی پر لٹکادیا جائے گا اور پرندے اس کے سر سے نوچ نوچ کر کھائیں گے یہ اس بات کے بارے میں فیصلہ ہوچکا ہے جس کے بارے میں تم سوال کررہے ہو'),
(1638, 12, 42, 'اور پھر جس کے بارے میں خیال تھا کہ وہ نجات پانے والا ہے اس سے کہا کہ ذرا اپنے مالک سے میرا بھی ذکر کردینا لیکن شیطان نے اسے مالک سے ذکر کرنے کو بھلا دیا اور یوسف چند سال تک قید خانے ہی میں پڑے رہے'),
(1639, 12, 43, 'اور پھر ایک دن بادشاہ نے لوگوں سے کہا کہ میں نے خواب میں سات موٹی گائیں دیکھی ہیں جنہیں سات پتلی گائیں کھائے جارہی ہیں اور سات ہری تازی بالیاں دیکھی ہیں اور سات خشک بالیاں دیکھی ہیں تم سب میرے خواب کے بارے میں رائے دو اگر تمہیں خواب کی تعمبیر دینا آتا ہو تو'),
(1640, 12, 44, 'ان لوگوں نے کہا کہ یہ تو ایک خوابِ پریشاں ہے اور ہم ایسے خوابوں کی تاویل سے باخبر نہیں ہیں'),
(1641, 12, 45, 'اور پھر دونوں قیدیوں میں سے جو بچ گیا تھا اور جسے ایک مدت کے بعد یوسف کا پیغام یاد آیا اس نے کہا کہ میں تمہیں اس کی تعبیر سے باخبر کرتا ہوں لیکن ذرا مجھے بھیج تو دو'),
(1642, 12, 46, 'یوسف اے مرد صدیق !ذرا ان سات موٹی گایوں کے بارے میں جنہیں سات دبلی گائیں کھارہی ہیں اور سات ہری بالیوں اور سات خشک بالیوں کے بارے میں اپنی رائے تو بتاؤ شاید میں لوگوں کے پاس باخبر واپس جاؤں تو شاید انہیں بھی علم ہوجائے'),
(1643, 12, 47, 'یوسف نے کہا کہ تم لوگ سات برس تک مسلسل زراعت کرو گے تو جو غلہ پیدا ہو اسے بالیوں سمیت رکھ دینا علاوہ تھوڑی مقدار کے کہ جو تمہارے کھانے کے کام میں آئے'),
(1644, 12, 48, 'اس کے بعد سات سخت سال آئیں گے جو تمہارے سارے ذخیرہ کو کھاجائیں گے علاوہ اس تھوڑے مال کے جو تم نے بچا کر رکھا ہے'),
(1645, 12, 49, 'اس کے بعد ایک سال آئے گاجس میں لوگوں کی فریاد رسی اور بارش ہوگی اور لوگ خوب انگورنچوڑیں گے'),
(1646, 12, 50, 'تو بادشاہ نے یہ سن کر کہا کہ ذرا اسے یہاں تو لے آؤ پس جب نمائندہ آیا تو یوسف نے کہا کہ اپنے مالک کے پاس پلٹ کر جاؤ اور پوچھو کہ ان عورتوں کے بارے میں کیا خیال ہے جنہوں نے اپنے ہاتھ کاٹ ڈالے تھے کہ میرا پروردگار ان کے مکر سے خوب باخبر ہے'),
(1647, 12, 51, 'بادشاہ نے ان عورتوں سے دریافت کیا کہ آخر تمہارا کیا معاملہ تھا جب تم نے یوسف سے اظہار تعلق کیا تھا ان لوگوں نے کہا کہ ماشائ اللہ ہم نے ہرگز ان میں کوئی برائی نہیں دیکھی -تو عزیز مصر کی بیوی نے کہا کہ اب حق بالکل واضح ہوگیا ہے کہ میں نے خود انہیں اپنی طرف مائل کرنے کی کوشش کی تھی اور وہ صادقین میں سے ہیں'),
(1648, 12, 52, 'یوسف نے کہا کہ یہ ساری بات اس لئے ہے کہ بادشاہ کو یہ معلوم ہوجائے کہ میں نے اس کی عدم موجودگی میں کوئی خیانت نہیں کی ہے اور خدا خیانت کاروں کے مکر کو کامیاب نہیں ہونے دیتا'),
(1649, 12, 53, 'اور میں اپنے نفس کو بھی بری نہیں قرار دیتا کہ نفس بہرحال برائیوں کا حکم دینے والا ہے مگر یہ کہ میرا پروردگار رحم کرے کہ وہ بڑا بخشنے والا مہربان ہے'),
(1650, 12, 54, 'اور بادشاہ نے کہا کہ ذرا انہیں لے آؤ میں اپنے ذاتی امور میں ساتھ رکھوں گا اس کے بعد جب ان سے بات کی تو کہا کہ تم آج سے ہمارے دربار میں باوقار امین کی حیثیت سے رہو گے'),
(1651, 12, 55, 'یوسف نے کہا کہ مجھے زمین کے خزانوں پر مقرر کردو کہ میں محافظ بھی ہوں اور صاحب هعلم بھی'),
(1652, 12, 56, 'اور اس طرح ہم نے یوسف کو زمین میں اختیار دے دیا کہ وہ جہاں چاہیں رہیں -ہم اپنی رحمت سے جس کو بھی چاہتے ہیں مرتبہ دے دیتے ہیں اور کسی نیک کردار کے اجر کو ضائع نہیں کرتے'),
(1653, 12, 57, 'اور آخرت کا اجر تو ان لوگوں کے لئے بہترین ہے جو صاحبانِ ایمان ہیں اور خدا سے ڈرنے والے ہیں'),
(1654, 12, 58, 'اور جب یوسف کے بھائی مصر آئے اور یوسف کے پاس پہنچے تو انہوں نے سب کو پہچان لیا اور وہ لوگ نہیں پہچان سکے'),
(1655, 12, 59, 'پھر جب ان کا سامان تیار کردیا تو ان سے کہا کہ تمہارا ایک بھائی اور بھی ہے اسے بھی لے آؤ کیا تم نہیں دیکھتے ہو کہ میں سامان کی ناپ تول بھی برابر رکھتا ہوں اور مہمان نوازی بھی کرنے والا ہوں'),
(1656, 12, 60, 'اب اگر اسے نہ لے آئے تو آئندہ تمہیں بھی غلہ نہ دوں گا اور نہ میرے پاس آنے پاؤ گے'),
(1657, 12, 61, 'ان لوگوں نے کہا کہ ہم اس کے باپ سے بات چیت کریں گے اور ضرور کریں گے'),
(1658, 12, 62, 'اور یوسف نے اپنے جوانوں سے کہا کہ ان کی پونجی بھی ان کے سامان میں رکھ دو شاید جب گھر پلٹ کر جائیں تو اسے پہچان لیں اور اس طرح شاید دوبارہ پلٹ کر ضرور آئیں'),
(1659, 12, 63, 'اب جو پلٹ کر باپ کی خدمت میں آئے اور کہا کہ بابا جان آئندہ ہمیں غلہ سے روک دیا گیا ہے لہذا ہمارے ساتھ ہمارے بھائی کو بھی بھیج دیجئے تاکہ ہم غلہ حاصل کرلیں اور اب ہم اس کی حفاظت کے ذمہ دار ہیں'),
(1660, 12, 64, 'یعقوب نے کہا کہ ہم اس کے بارے میں تمہارے اوپر اسی طرح بھروسہ کریں جس طرح پہلے اس کے بھائی یوسف کے بارے میں کیا تھا -خیر خدا بہترین حفاظت کرنے والا ہے اور وہی سب سے زیادہ رحم کرنے والا ہے'),
(1661, 12, 65, 'پھر جب ان لوگوں نے اپنا سامان کھولا تو دیکھا کہ ان کی بضاعت (قیمت)واپس کردی گئی ہے تو کہنے لگا بابا جان اب ہم کیا چاہتے ہیں یہ ہماری پونجی بھی واپس کردی گئی ہے اب ہم اپنے گھر والوں کے لئے غلّہ ضرور لائیں گے اور اپنے بھائی کی حفاظت بھی کریں گے اور ایک اونٹ کا بار اور بڑھوالیں گے کہ یہ بات اس کی موجودگی میں آسان ہے'),
(1662, 12, 66, 'یعقوب نے کہا کہ میں اسے ہرگز تمہارے ساتھ نہ بھیجوں گا جب تک کہ خدا کی طرف سے عہد نہ کرو گے کہ اسے واپس ضرور لاؤ گے مگر یہ کہ تم ہی کو گھیر لیا جائے -اس کے بعد جب ان لوگوں نے عہد کرلیا تو یعقوب نے کہا کہ اللہ ہم لوگوں کے قول و قرار کا نگراں اور ضامن ہے'),
(1663, 12, 67, 'اور پھر کہا کہ میرے فرزندو د یکھو سب ایک دروازے سے داخل نہ ہونا اور متفرق دروازوں سے داخل ہونا کہ میں خدا کی طرف سے آنے والی بلاؤں میں تمہارے کام نہیں آسکتا حکم صرف اللہ کے ہاتھ میں ہے اور اسی پر میرا اعتماد ہے اور اسی پر سارے توکل کرنے والوں کو بھروسہ کرنا چاہئے'),
(1664, 12, 68, 'اور جب وہ لوگ اسی طرح داخل ہوئے جس طرح ان کے والد نے کہا تھا اگرچہ وہ خدائی بلا کو ٹال نہیں سکتے تھے لیکن یہ ایک خواہش تھی جو یعقوب کے دل میں پیدا ہوئی جسے انہوں نے پورا کرلیا اور وہ ہمارے دئیے ہوئے علم کی بنا پر صاحبِ علم بھی تھے اگرچہ اکثر لوگ اس حقیقت سے بھی ناواقف ہیں'),
(1665, 12, 69, 'اور جب وہ لوگ یوسف کے سامنے حاضر ہوئے تو انہوں نے اپنے بھائی کو اپنے پاس پناہ دی اور کہا کہ میں تمہارا بھائی «یوسف,, ہوں لہذا جو برتاؤ یہ لوگ کرتے رہے ہیں اب اس کی طرف سے رنج نہ کرنا'),
(1666, 12, 70, 'اس کے بعد جب یوسف نے ان کا سامان تیار کرادیا تو پیالہ کو اپنے بھائی کے سامان میں رکھوادیا اس کے بعد منادی نے آواز دی کہ قافلے والو تم سب چور ہو'),
(1667, 12, 71, 'ان لوگوں نے مڑ کر دیکھا اور کہا کہ آخر تمہاری کیا چیز گم ہوگئی ہے'),
(1668, 12, 72, 'ملازمین نے کہا کہ بادشاہ کا پیالہ نہیں مل رہا ہے اور جو اسے لے کر آئے گا اسے ایک اونٹ کا بار غلہ انعام ملے گا اور میں اس کا ذمہ دار ہوں'),
(1669, 12, 73, 'ان لوگوں نے کہا کہ خدا کی قسم تمہیں تو معلوم ہے کہ ہم زمین میں فساد کرنے کے لئے نہیں آئے ہیں اور نہ ہم چور ہیں'),
(1670, 12, 74, 'ملازموں نے کہا کہ اگر تم جھوٹے ثابت ہوئے تو اس کی سزا کیا ہے'),
(1671, 12, 75, 'ان لوگوں نے کہا کہ اس کی سزا خود وہ شخص ہے جس کے سامان میں سے پیالہ برآمد ہو ہم اسی طرح ظلم کرنے والوں کو سزادیتے ہیں'),
(1672, 12, 76, 'اس کے بعد بھائی کے سامان سے پہلے دوسرے بھائیوں کے سامان کی تلاشی لی اور آخر میں بھائی کے سامان میں سے پیالہ نکال لیا .... اور اس طرح ہم نے یوسف کے حق میں تدبیر کی کہ وہ بادشاہ کے قانون سے اپنے بھائی کو نہیں لے سکتے تھے مگر یہ کہ خدا خود چاہے ہم جس کو چاہتے ہیں اس کے درجات کو بلند کردیتے ہیں اور ہر صاحب هعلم سے برتر ایک صاحب هعلم ہوتا ہے'),
(1673, 12, 77, 'ان لوگوں نے کہا کہ اگر اس نے چوری کی ہے تو کیا تعجب ہے اس کا بھائی اس سے پہلے چوری کرچکا ہے -یوسف نے اس بات کو اپنے دل میں حُھپالیا اور ان پر اظہار نہیں کیا .... کہاکہ تم بڑے برے لوگ ہو اور اللہ تمہارے بیانات کے بارے میں زیادہ بہتر جاننے والا ہے'),
(1674, 12, 78, 'ان لوگوں نے کہا کہ اے عزیز مصر اس کے والد بہت ضعیف العمر ہیں لہذا ہم میں سے کسی ایک کو اس کی جگہ پر لے لیجئے اور اسے چھوڑ دیجئے کہ ہم آپ کو احسان کرنے والا سمجھتے ہیں'),
(1675, 12, 79, 'یوسف نے کہا کہ خدا کی پناہ کہ ہم جس کے پاس اپنا سامان پائیں اس کے علاوہ کسی دوسرے کو گرفت میں لے لیں اور اس طرح ظالم ہوجائیں'),
(1676, 12, 80, 'اب جب وہ لوگ یوسف کی طرف سے مایوس ہوگئے تو الگ جاکر مشورہ کرنے لگے تو سب سے بڑے نے کہا کہ کیا تمہیں نہیں معلوم کہ تمہارے باپ نے تم سے خدائی عہد لیا ہے اور اس سے پہلے بھی تم یوسف کے بارے میں کوتاہی کرحُکے ہو تو اب میں تو اس سرزمین کو نہ چھوڑوں گا یہاں تک کہ والد محترم اجازت دے دیں یا خدا میرے حق میں کوئی فیصلہ کردے کہ وہ بہترین فیصلہ کرنے والا ہے'),
(1677, 12, 81, 'ہاں تم لوگ باپ کی خدمت میں جاؤ اور عرض کرو کہ آپ کے فرزند نے چوری کی ہے اور ہم اسی بات کی گواہی دے رہے ہیں جس کا ہمیں علم ہے اور ہم غیب کی حفاظت کرنے والے نہیں ہیں'),
(1678, 12, 82, 'آپ اس بستی سے دریافت کرلیں جس میں ہم تھے اور اس قافلے سے پوچھ لیں جس میں ہم آئے ہیں اور ہم بالکل سچےّ ہیں'),
(1679, 12, 83, 'یعقوب نے کہا کہ یہ تمہارے دل نے ایک نئی بات گڑھ لی ہے میں پھر بھی صبرجمیل اختیار کروں گا کہ شائد خدا ان سب کو لے آئے کہ وہ ہر شئے کا جاننے والا اور صاحب هحکمت ہے'),
(1680, 12, 84, 'ےہ کہہ کر انہوں نے سب سے منھ پھےر لیااور کہا کہ افسوس ہے یوسف کے حال پر اوراتناروئے کہ آنکھیں سفیدہو گئیںاور غم کے گھونٹ پیتے رہے'),
(1681, 12, 85, 'ان لوگوں نے کہا کہ آپ اسی طرح یوسف کویاد کرتے رہیںگے یہاں تک کہ بیمار ہوجائیں یا ہلاک ہونے والوںمیں شامل ہو جائیں'),
(1682, 12, 86, 'یعقوب نے کہا کہ میں اپنے حزن و غم اور اپنی بےقراری کی فریاد اللہ کی بارگاہ میں کر رہا ہوں اورا س کی طرف سے وہ سب جانتا ہوںجو تم نہیں جانتے ہو'),
(1683, 12, 87, 'میرے فرزندو جاؤ یوسف اور ان کے بھائی کو خوب تلاش کرو اور رحمت خدا سے مایوس نہ ہونا کہ اس کی رحمت سے کافر قوم کے علاوہ کوئی مایوس نہیں ہوتا ہے'),
(1684, 12, 88, 'اب جو وہ لوگ دوبارہ یوسف کے پاس گئے تو کہنے لگے کہ اے عزیز ہم کو اور ہمارے گھر والوں کو بڑی تکلیف ہے اور ہم ایک حقیر سی پونجی لے کر آئے ہیں آپ ہمیں پورا پورا غلہ دے دیں اور ہم پر احسان کریں کہ خدا کا»خیر کرنے والوں کو جزائے خیر دیتا ہے'),
(1685, 12, 89, 'اس نے کہا کہ تمہیں معلوم ہے کہ تم نے یوسف اور ان کے بھائی کے ساتھ کیا برتاؤ کیا ہے جب کہ تم بالکل جاہل تھے'),
(1686, 12, 90, 'ان لوگوں نے کہا کہ کیا آپ ہی یوسف ہیں?انہوں نے کہا کہ بیشک میں یوسف ہوں اور یہ میرا بھائی ہے -اللہ نے ہمارے اوپر احسان کیا ہے اور جو کوئی بھی تقوٰی اور صبر اختیار کرتاہے اللہ نیک عمل کرنے والوں کے اجر کو ضائع نہیں کرتا ہے'),
(1687, 12, 91, 'ان لوگوں نے کہا کہ خدا کی قسم خدا نے آپ کو فضیلت اور امتیاز عطا کیا ہے اور ہم سب خطاکار تھے'),
(1688, 12, 92, 'یوسف نے کہا کہ آج تمہارے اوپر کوئی الزام نہیںہے .خدا تمہیں معاف کردے گا کہ وہ بڑا رحم کرنے والا ہے'),
(1689, 12, 93, 'جاؤ میری قمیض لے کر جاؤ اور بابا کے چہرہ پر ڈال دو کہ ان کی بصارت پلٹ آئے گی اور اس مرتبہ اپنے تمام گھر والوں کو ساتھ لے کر آنا'),
(1690, 12, 94, 'اب جو قافلہ مصر سے روانہ ہوا تو ان کے پدر بزرگوار نے کہا کہ میں یوسف کی خوشبو محسوس کررہا ہوں اگر تم لوگ مجھے سٹھیایا ہوا نہ کہو'),
(1691, 12, 95, 'ان لوگوں نے کہا کہ خدا کی قسم آپ ابھی تک اپنی پرانی گمراہی میں مبتلا ہیں'),
(1692, 12, 96, 'اس کے بعد جب بشیر نے آکر قمیض کو یعقوب کے چہرہ پر ڈال دیا تو دوبارہ صاحب هبصارت ہوگئے اور انہوں نے کہا کہ میں نے تم سے نہ کہا تھا کہ میں خدا کی طرف سے وہ جانتا ہوں جو تم نہیں جانتے ہو'),
(1693, 12, 97, 'ان لوگوں نے کہا بابا جان اب آپ ہمارے گناہوں کے لئے استغفار کریں ہم یقینا خطاکار تھے'),
(1694, 12, 98, 'انہوں نے کہا کہ میں عنقریب تمہارے حق میں استغفار کروں گا کہ میرا پروردگار بہت بخشنے والا اور مہربان ہے'),
(1695, 12, 99, 'اس کے بعد جب وہ لوگ سب یوسف کے یہاں حاضر ہوئے تو انہوں نے ماں باپ کو اپنے پہلو میں جگہ دی اور کہا کہ آپ لوگ مصر میں انشائ اللہ بڑے اطمینان و سکون کے ساتھ داخل ہوں'),
(1696, 12, 100, 'اور انہوں نے والدین کو بلند مقام پر تخت پر جگہ دی اور سب لوگ یوسف کے سامنے سجدہ میں گر پڑے یوسف نے کہا کہ بابا یہ میرے پہلے خواب کی تعبیر ہے جسے میرے پروردگار نے سچ کر دکھایا ہے اور اس نے میرے ساتھ احسان کیا ہے کہ مجھے قید خانہ سے نکال لیا اور آپ لوگوں کو گاؤں سے نکال کر مصر میں لے آیا جب کہ شیطان میرے اور میرے بھائیوں کے درمیان فساد پیدا کرچکا تھا -بیشک میرا پروردگار اپنے ارادوں کی بہترین تدبیر کرنے والا اور صاحب هعلم اور صاحب هحکمت ہے'),
(1697, 12, 101, 'پروردگار تو نے مجھے ملک بھی عطا کیا اور خوابوں کی تعبیر کا علم بھی دیا -تو زمین و آسمان کا پیدا کرنے والا ہے اور دنیا وآخرت میں میرا ولی اور سرپرست ہے مجھے دنیا سے فرمانبردار اٹھانا اور صالحین سے ملحق کردینا'),
(1698, 12, 102, 'پیغمبر !سب غیب کی خبریں ہیں جنہیں ہم وحی کے ذریعہ آپ تک پہنچا رہے ہیں ورنہ آپ تو اس وقت نہ تھے جب وہ لوگ اپنے کام پر اتفاق کررہے تھے اور یوسف کے بارے میں بفِی تدبیریں کررہے تھے'),
(1699, 12, 103, 'اور آپ کسی قدر کیوں نہ چاہیں انسانوں کی اکثریت ایمان لانے والی نہیں ہے'),
(1700, 12, 104, 'اور آپ ان سے تبلیغ رسالت کی اجرت تو نہیں مانگتے ہیں یہ قرآن تو عالمین کے لئے ایک ذکر اور نصیحت ہے'),
(1701, 12, 105, 'اور زمین و آسمان میں بہت سی نشانیاں ہیں جن سے لوگ گزر جاتے ہیں اور کنارہ کش ہی رہتے ہیں'),
(1702, 12, 106, 'اور ان میںکی اکثریت خدا پر ایمان بھی لاتی ہے تو شرک کے ساتھ'),
(1703, 12, 107, 'تو کیا یہ لوگ اس بات کی طرف سے مطمئن ہوگئے ہیں کہ کہیں ان پر عذاب الٰہی آکر چھاجائے یا اچانک قیامت آجائے اور یہ غافل ہی رہ جائیں'),
(1704, 12, 108, 'آپ کہہ دیجئے کہ یہی میرا راستہ ہے کہ میں بصیرت کے ساتھ خدا کی طرف دعوت دیتا ہوں اور میرے ساتھ میرا اتباع کرنے والا بھی ہے اور خدا پاک و بے نیازہے اور میں مشرکین میں سے نہیں ہوں'),
(1705, 12, 109, 'اور ہم نے آپ سے پہلے ان ہی مذِدوں کو رسول بنایا ہے جو آبادیوں میں رہنے والے تھے اور ہم نے ان کی طرف وحی بھی کی ہے تو کیا یہ لوگ زمین میں سیر نہیں کرتے کہ دیکھیں کہ ان سے پہلے والوں کا انجام کیا ہوا ہے اور دا» آخرت صرف صاحبانِ تقوٰی کے لئے بہترین منزل ہے -کیا تم لوگ عقل سے کام نہیں لیتے ہو'),
(1706, 12, 110, 'یہاں تک کہ جب ان کے انکار سے مرسلین مایوس ہونے لگے اور ان لوگوں نے سمجھ لیاکہ ان سے جھوٹا وعدہ کیا گیا ہے تو ہماری مدد مرسلین کے پاس آگئی اور ہم نے جن لوگوں کو چاہا انہیں نجات دے دی اور ہمارا عذاب مجرم قوم سے پلٹایا نہیں جاسکتا ہے'),
(1707, 12, 111, 'یقینا ان کے واقعات میں صاحبانِ عقل کے لئے سامان هعبرت ہے اور یہ کوئی ایسی بات نہیں ہے جسے گڑھ لیا جائے یہ قرآن پہلے کی تمام کتابوں کی تصدیق کرنے والا ہے اور اس میں ہر شے کی تفصیل ہے اور یہ صاحبِ ایمان قوم کے لئے ہدایت اور رحمت بھی ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'المۤرۤ -یہ کتاب هخدا کی آیتیں ہیں اور جو کچھ بھی آپ کے پروردگار کی طرف سے نازل کیا گیا ہے وہ سب برحق ہے لیکن لوگوں کی اکثریت ایمان لانے والی نہیں ہے'),
(1709, 13, 2, 'اللہ ہی وہ ہے جس نے آسمانوں کو بغیر کسی ستون کے بلند کردیا ہے جیسا کہ تم دیکھ رہے ہو اس کے بعد اس نے عر ش پر اقتدار قائم کیا اور آفتاب و ماہتاب کو لَسخر بنایا کہ سب ایک معینہ مدّت تک چلتے رہیں گے وہی تمام امور کی تدبیر کرنے والا ہے اور اپنی آ یات کو مفصل طور سے بیان کرتا ہے کہ شاید تم لوگ پروردگار کی ملاقات کا یقین پیدا کرلو'),
(1710, 13, 3, 'وہ خدا وہ ہے جس نے زمین کو پھیلایا اور اس میں ا ٹل قسم کے پہا ڑ قرار دئیے اور نہریں جاری کیں اور ہر پھل کا جوڑا قرار دیا وہ رات کے پردے سے دن کو ڈھانک دیتا ہے اور اس میں صاحبانِ فکر و نظر کے لئے بڑ ی نشانیاں پائی جاتی هہیں'),
(1711, 13, 4, 'اور زمین کے متعدد ٹکڑے آپس میں ایک دوسرے سے ملے ہوئے ہیں اور انگور کے باغات ہیں اور زرا عت ہے اور کھجوریں ہیں جن میں بعض دو شاخ کی ہیں اور بعض ایک شاخ کی ہیں اور سب ایک ہی پانی سے سینچے جاتے ہیں اور ہم بعض کو بعض پر کھانے میں ترجیح دیتے ہیں اور اس میں بھی صاحبانِ عقل کے لئے بڑی نشانیاں پائی جاتی ہیں'),
(1712, 13, 5, 'اور اگر تمہیں کسی بات پر تعّجب ہے تو تّعجب کی بات ان لوگوں کا یہ قول ہے کہ کیا ہم خاک ہوجانے کے بعد بھی نئے سرے سے دوبارہ پیدا کئے جائیں گے .یہی وہ لوگ ہیں جنہوں نے اپنے پروردگار کا انکار کیا ہے اور ان ہی کی گردنوں میں طوق ڈالے جائیں گے اور یہی اہل هجہّنم ہیں اور اسی میں ہمیشہ رہنے والے ہیں'),
(1713, 13, 6, 'اور اے رسول یہ لوگ آپ سے بھلائی سے پہلے ہی برائی (عذاب)چاہتے ہیں جب کہ ان کے پہلے بہت سی عذاب کی نظریں گزر چکی ہیں اور آپ کا پروردگار لوگوں کے لئے ان کے ظلم پر بخشنے والا بھی ہے اور بہت سخت عذاب کرنے والا بھی ہے'),
(1714, 13, 7, 'اور یہ کافر کہتے ہیں کہ ان کے اوپر کوئی نشانی (ہماری مطلوبہ)کیوں نہیں نازل ہوتی تو آپ کہہ دیجئے کہ میں صرف ڈرانے والا ہوں اور ہر قوم کے لئے ایک ہادی اور رہبر ہے'),
(1715, 13, 8, 'اللہ بہتر جانتا ہے کہ ہر عورت کے شکم میں کیا ہے اور اس کے رحم میں کیا کمی اور زیادتی ہوتی رہتی ہے اور ہر شے کی اس کے نزدیک ایک مقدار معین ہے'),
(1716, 13, 9, 'وہ غائب و حاضر سب کا جاننے والا بزرگ و بالاتر ہے'),
(1717, 13, 10, 'اس کے نزدیک تم میں کے سب برابر ہیں جو بات آہستہ کہے اور جو بلند آواز سے کہے اور جو رات میں چھپارہے اور دن میں چلتا رہے'),
(1718, 13, 11, 'اس کے لئے سامنے اور پیچھے سے محافظ طاقتیں ہیں جو حکم هخدا سے اس کی حفاظت کرتی ہیں اور خدا کسی قوم کے حالات کو اس وقت تک نہیں بدلتا جب تک وہ خود اپنے کو تبدیل نہ کرلے اور جب خدا کسی قوم پر عذاب کا ارادہ کرلیتا ہے تو کوئی ٹال نہیں سکتا ہے اور نہ اس کے علاوہ کوئی کسی کا والی و سرپرست ہے'),
(1719, 13, 12, 'وہی خدا ہے جو تمہیں ڈرانے اور لالچ دلانے کے لئے بجلیاں دکھاتا ہے اور پانی سے لدے ہوئے بوجھل بادل ایجاد کرتا ہے'),
(1720, 13, 13, 'گرج اس کی حمد کی تسبیح کرتی ہے اور فرشتے اس کے خوف سے حمد و ثنا کرتے رہتے ہیں اور بجلیوں کو بھیجتا ہے تو جس تک چاہتا ہے پہنچا دیتا ہے اور یہ لوگ اللہ کے بارے میں کج بحثی کررہے ہیں جب کہ وہ بہت مضبوط قوت اور عظیم طاقت والا ہے'),
(1721, 13, 14, 'برحق پکارنا صرف خدا ہی کا پکارنا ہے اور جو لوگ اس کے علاوہ دوسروں کو پکارتے ہیں وہ ان کی کوئی بات قبول نہیں کرسکتے سوائے اس شخص کے مانند جو پانی کی طرف ہتھیلی پھیلائے ہو کہ منہ تک پہنچ جائے اور وہ پہنچنے والا نہیں ہے اور کافروں کی دعا ہمیشہ گمراہی میں رہتی ہے'),
(1722, 13, 15, 'اللہ ہی کے لئے زمین و آسمان والے ہنسی خوشی یا زبردستی سجدہ کررہے ہیں اور صبح و شام ان کے سائے بھی سجدہ کناں ہیں'),
(1723, 13, 16, 'پیغمبر کہہ دیجئے کہ بتاؤ کہ زمین و آسمان کا پروردگار کون ہے اور بتادیجئے کہ اللہ ہی ہے اور کہہ دیجئے کہ تم لوگوں نے اس کو چھوڑ کر ایسے سرپرست اختیار کئے ہیں جو خود اپنے نفع و نقصان کے مالک نہیں ہیں اور کہئے کہ کیا اندھے اور بینا ایک جیسے ہوسکتے ہیں یا نورو ظلمت برابر ہوسکتے ہیں یا ان لوگوں نے اللہ کے لئے ایسے شریک بنائے ہیں جنہوں نے اسی کی طرح کی کائنات خلق کی ہے اور ان پر خلقت مشتبہ ہوگئی ہے. کہہ دیجئے کہ اللہ ہی ہر شے کا خالق ہے اور وہی یکتا اور سب پر غالب ہے'),
(1724, 13, 17, 'اس نے آسمان سے پانی برسایا تو وادیوں میں بقدر ظرف بہنے لگا اور سیلاب میں جوش کھا کر جھاگ آگیا اور اس دھات سے بھی جھاگ پیدا ہوگیا جسے آگ پر زیور یا کوئی دوسرا سامان بنانے کے لئے پگھلاتے ہیں. اسی طرح پروردگار حق و باطل کی مثال بیان کرتا ہے کہ جھاگ خشک ہوکر فنا ہوجاتا ہے اور جو لوگوں کو فائدہ پہنچانے والا ہے وہ زمین میں باقی رہ جاتا ہے اور خدا اسی طرح مثالیں بیان کرتا ہے'),
(1725, 13, 18, 'جو لوگ پروردگار کی بات کو قبول کرلیتے ہیں ان کے لئے نیکی ہے اور جو اس کی بات کو قبول نہیں کرتے انہیں زمین کے سارے خزانے بھی مل جائیں اور اسی قدر اور بھی مل جائے تو یہ بطور فدیہ دے دیں گے لیکن ان کے لئے بدترین حساب ہے اور ان کا ٹھکانا جہّنم ہے اور وہ بہت بفِا ٹھکانا ہے'),
(1726, 13, 19, ') کیا وہ شخص جو یہ جانتا ہے کہ جو کچھ آپ کے پروردگار کی طرف سے نازل ہوا سب برحق ہے وہ اس کے جیسا ہوسکتا ہے جو بالکل اندھا ہے (ہرگز نہیں)اس بات کو صرف صاحبانِ عقل ہی سمجھ سکتے ہیں'),
(1727, 13, 20, 'جو عہدُ خدا کو پورا کرتے ہیں اور عہد شکنی نہیں کرتے ہیں'),
(1728, 13, 21, 'اور جو ان تعلقات کو قائم رکھتے ہیں جنہیں خدا نے قائم رکھنے کا حکم دیا ہے اور اس سے ڈرتے رہتے ہیں اور بدترین حساب سے خوفزدہ رہتے ہیں'),
(1729, 13, 22, 'اور جنہوں نے مرضی خدا حاصل کرنے کے لئے صبر کیا ہے اور نماز قائم کی ہے اور ہمارے رزق میں سے خفیہ و علانیہ انفاق کیا ہے اور جو نیکی کے ذریعہ برائی کو دفع کرتے رہتے ہیں آخرت کا گھر ان ہی کے لئے ہے'),
(1730, 13, 23, 'یہ ہمیشہ رہنے والے باغات ہیں جن میں یہ خود اور ان کے آبائ و اجداد اور ازواج و اولاد میں سے سارے نیک بندے داخل ہوں گے اور ملائکہ ان کے پاس ہر دروازے سے حاضری دیں گے'),
(1731, 13, 24, 'کہیں گے کہ تم پر سلامتی ہو کہ تم نے صبر کیا ہے اور اب آخرت کا گھر تمہاری بہترین منزل ہے'),
(1732, 13, 25, 'اور جو لوگ عہدُ خدا کو توڑ دیتے ہیں اور جن سے تعلقات کا حکم دیا گیا ہے اان سے قطع تعلقات کرلیتے ہیں اور زمین میں فساد برپا کرتے ہیں ان کے لئے لعنت اور بدترین گھر ہے'),
(1733, 13, 26, 'اللہ جس کے لئے چاہتا ہے رزق کو وسیع یا تنگ کردیتا ہے اور یہ لوگ صرف زندگانی دنیا پر خوش ہوگئے ہیں حالانکہ آخرت کے مقابلہ میں زندگانی دنیا صرف ایک وقتی لذّت کا درجہ رکھتی ہے اور بس'),
(1734, 13, 27, 'اور یہ کافر کہتے ہیں کہ ان کے اوپر ہماری پسند کی نشانی کیوں نہیں نازل ہوتی تو پیغمبر کہہ دیجئے کہ اللہ جس کو چاہتا ہے گمراہی میں چھوڑ دیتا ہے اور جو اس کی طرف متوجہ ہوجاتے ہیں انہیں ہدایت دے دیتا ہے'),
(1735, 13, 28, 'یہ وہ لوگ ہیں جو ایمان لائے ہیں اور ان کے دُلوں کو یادُ خدا سے اطمینان حاصل ہوتا ہے اور آگاہ ہوجاؤ کہ اطمینان یادُ خدا سے ہی حاصل ہوتا ہے'),
(1736, 13, 29, 'جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ان کے لئے بہترین جگہ (بہشت)اور بہترین بازگشت ہے'),
(1737, 13, 30, 'اسی طرح ہم نے آپ کو ایک ایسی قوم کے درمیان بھیجا ہے جس سے پہلے بہت سی قومیں گزر چکیِ ہیں تاکہ آپ ان چیزوں کی تلاوت کریں جنہیں ہم نے آپ کی طرف بذریعہ وحی نازل کیا ہے حالانکہ وہ لوگ رحمان کا انکار کرنے والے ہیں آپ ان سے کہئے کہ وہ میرا رب ہے اور اس کے علاوہ کوئی خدا نہیں ہے اسی پر میرا اعتماد ہے اور اسی کی طرف بازگشت ہے'),
(1738, 13, 31, 'اور اگر کوئی قرآن ایسا ہو جس سے پہاڑوں کو اپنی جگہ سے چلایا جاسکے یا زمین طے کی جاسکے یا لَردوں سے کلام کیا جاسکے -تو وہ یہی قرآن ہے- بلکہ تمام امور اللہ کے لئے ہیں تو کیا ایمان والوں پر واضح نہیں ہوا کہ اگر خدا جبرا چاہتا تو سارے انسانوں کو ہدایت دے دیتا اور ان کافروں پر ان کے کرتوت کی بنا پر ہمیشہ کوئی نہ کوئی مصیبت پڑتی رہے گی یا ان کے دیار کے آس پاس مصیبت آتی رہے گی یہاں تک کہ وعدہ الٰہی کا وقت آجائے کہ اللہ اپنے وعدہ کے خلاف نہیں کرتا ہے'),
(1739, 13, 32, 'پیغمبر آپ سے پہلے بہت سے رسولوں کا مذاق اڑایا گیا ہے تو ہم نے کافروں کو تھوڑی دیر کی مہلت دیدی اور اس کے بعد اپنی گرفت میں لے لیا تو کیسا سخت عذاب ہوا'),
(1740, 13, 33, 'کیا وہ ذات جو ہر نفس کے اعمال کی نگراں ہے اور انہوں نے اس کے لئے شریک فرض کرلئے ہیں تو کہئے کہ ذرا شرکائ کے نام تو بتاؤ تم خدا کو ان شرکائ کی خبر دے رہے ہو جن کی ساری زمین میں اسے بھی خبر نہیں ہے یا فقط یہ ظاہری الفاظ ہیں اور سچ تو یہ ہے کہ کافروں کے لئے ان کا مکر آراستہ ہوگیا ہے اور انہیں راہ حق سے روک دیا گیا ہے اور جسے خدا ہدایت نہ دے اس کا کوئی ہدایت کرنے والا نہیں ہے'),
(1741, 13, 34, 'ان کے لئے زندگانی دنیا میں بھی عذاب ہے اور آخرت کا عذاب تو اور زیادہ سخت ہے اور پھر اللہ سے بچانے والا کوئی نہیں ہے'),
(1742, 13, 35, 'جس جنت کا صاحبانِ تقویٰ سے وعدہ کیا گیا ہے اس کی مثال یہ ہے کہ اس کے نیچے نہریں جاری ہوں گی اور اس کے پھل دائمی ہوں گے اور سایہ بھی ہمیشہ رہے گا -یہ صاحبان هتقوٰی کی عاقبت ہے اور کفار کا انجام کار بہرحال جّہنم ہے'),
(1743, 13, 36, 'اور جن لوگوں کو ہم نے کتاب دی ہے وہ اس تنزیل سے خوش ہیں اور بعض گروہ وہ ہیں جو بعض باتوں کا انکار کرتے ہیں تو آپ کہہ دیجئے کہ مجھے یہ حکم دیا گیا ہے کہ میں اللہ کی عبادت کروں اور کسی کو اس کا شریک نہ بناؤں میں اسی کی طرف دعوت دیتا ہوں اور اسی کی طرف سب کی بازگشت ہے'),
(1744, 13, 37, 'اور اسی طرح ہم نے اس قرآن کو عربی زبان کا فرمان بناکر نازل کیا ہے اور اگر آپ علم کے آجانے کے بعد ان کی خواہشات کا اتباع کرلیں گے تو اللہ کے مقابلے میں کوئی کسی کا سرپرست اور بچانے والا نہ ہوگا'),
(1745, 13, 38, 'ہم نے آپ سے پہلے بھی بہت سے رسول بھیجے ہیں اور ان کے لئے ازواج اور اولاد قرار دی ہے اور کسی رسول کے لئے یہ ممکن نہیں ہے وہ اللہ کی اجازت کے بغیر کوئی نشانی لے آئے کہ ہر مدّت اور معیاد کے لئے ایک نوشتہ مقرر ہے'),
(1746, 13, 39, 'اللہ جس چیز کو چاہتا ہے مٹادیتا ہے یا برقر ار رکھتا ہے کہ اصل کتاب اسی کے پاس ہے'),
(1747, 13, 40, 'اور اے پیغمبر ہم کفار سے کئے وعدہ عذاب کو تمہیں دکھلادیں یا تمہیں اٹھالیں تمہارا کام تو صرف احکام کا پہنچا دینا ہے حساب کی ذمہ داری ہماری اپنی ہے'),
(1748, 13, 41, 'کیا ان لوگوں نے نہیں دیکھا کہ ہم کس طرح زمین کی طرف آکر اس کو اطراف سے کم کردیتے ہیں اور اللہ ہی حکم دینے والا ہے کوئی اس کے حکم کا ٹالنے والا نہیں ہے اور وہ بہت تیز حساب کرنے والا ہے'),
(1749, 13, 42, 'اور ان کے پہلے والوں نے بھی مکر کیا ہے لیکن ساری تدبیریں اللہ ہی کے اختیار میں ہیں وہ ہر نفس کے کاروبار کو جانتا ہے اور عنقریب کفار بھی جان لیں گے کہ آخرت کا گھر کس کے لئے ہے'),
(1750, 13, 43, 'اور یہ کافر کہتے ہیں کہ آپ رسول نہیں ہیں کہہ دیجئے کہ ہمارے اور تمہارے درمیان رسالت کی گواہی کے لئے خدا کافی ہے اور وہ شخص کافی ہے جس کے پاس پوری کتاب کا علم ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الۤر -یہ کتاب ہے جسے ہم نے آپ کی طرف نازل کیا ہے تاکہ آپ لوگوں کو حکم خدا سے تاریکیوں سے نکال کر نور کی طرف لے آئیں اور خدائے عزیز و حمید کے راستے پر لگادیں'),
(1752, 14, 2, 'وہ اللہ وہ ہے جس کے لئے زمین و آسمان کی ہر شے ہے اور کافروں کے لئے تو سخت ترین اور افسوس ناک عذاب ہے'),
(1753, 14, 3, 'وہ لوگ جو زندگانی دنیا کو آخرت کے مقابلے میں پسند کرتے ہیں اور لوگوں کو راسِ خدا سے روکتے ہیں اور اس میں کجی پیدا کرنا چاہتے ہیں یہ گمراہی میں بہت دور تک چلے گئے ہیں'),
(1754, 14, 4, 'اور ہم نے جس رسول کو بھی بھیجا اسی کی قوم کی زبان کے ساتھ بھیجا تاکہ وہ لوگوں پر باتوں کو واضح کرسکے اس کے بعد خدا جس کو چاہتا ہے گمراہی میں چھوڑ دیتا ہے اور جس کو چاہتا ہے ہدایت دے دیتا ہے وہ صاحب هعزّت بھی ہے اور صاحبِ حکمت بھی'),
(1755, 14, 5, 'اور ہم نے موسٰی کو اپنی نشانیوں کے ساتھ بھیجا کہ اپنی قوم کو ظلمتوں سے نور کی طرف نکال کر لائیں اور انہیں خدائی دنوں کی یاد دلائیں کہ بیشک اس میں تمام صبر کرنے والے اور شکر کرنے والے کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(1756, 14, 6, 'اور اس وقت کو یاد کرو جب موسٰی نے اپنی قوم سے کہا کہ تم لوگ اللہ کی اس نعمت کو یاد کرو کہ اس نے تمہیں فرعون والوں سے نجات دلائی جب کہ وہ بدترین عذاب میں مبتلا کررہے تھے کہ تمہارے لڑکوں کو ذبح کررہے تھے اور تمہاری لڑکیوں کو (کنیزی کے لئے)زندہ رکھتے تھے اور اس میں تمہارے پروردگار کی طرف سے بڑا سخت امتحان تھا'),
(1757, 14, 7, 'اور جب تمہارے پروردگار نے اعلان کیا کہ اگر تم ہمارا شکریہ اد اکرو گے تو ہم نعمتوں میں اضافہ کردیں گے اور اگر کفرانِ نعمت کرو گے تو ہمارا عذاب بھی بہت سخت ہے'),
(1758, 14, 8, 'اور موسٰی نے یہ بھی کہہ دیا کہ اگر تم سب اور روئے زمین کے تمام بسنے والے بھی کافر ہوجائیں تو ہمارا اللہ سب سے بے نیاز ہے اور وہ قابلِ حمد و ستائش ہے'),
(1759, 14, 9, 'کیا تمہارے پاس اپنے سے پہلے والوں قوم نوح اور قوم عاد و ثمود اور جو ان کے بعد گزرے ہیں اور جنہیں خدا کے علاوہ کوئی نہیں جانتا ہے کی خبر نہیں آئی ہے کہ ان کے پاس اللہ کے رسول معجزات لے کر آئے اور انہوں نے ان کے ہاتھوں کو ان ہی کے منہ کی طرف پلٹا دیا اور صاف کہہ دیا کہ ہم تمہارے لائے ہوئے پیغام کے منکر ہیں اور ہمیں اس بات کے بارے میں واضح شک ہے جس کی طرف تم ہمیں دعوت دے رہے ہو'),
(1760, 14, 10, 'تو رسولوں نے کہا کہ کیا تمہیں اللہ کے بارے میں بھی شک ہے جو زمین و آسمان کا پیدا کرنے والا ہے اور تمہیں اس لئے بلاتا ہے کہ تمہارے گناہوں کو معاف کردے اور ایک معینہ مدّت تک زمین میں چین سے رہنے دے ....تو ان لوگوں نے جواب دیا کہ تم سب بھی ہمارے ہی جیسے بشر ہو اور چاہتے ہو کہ ہمیں ان چیزوں سے روک دو جن کی ہمارے باپ دادا عبادت کیا کرتے تھے تو اب کوئی کھلی ہوئی دلیل لے آؤ'),
(1761, 14, 11, 'ان سے رسولوں نے کہا کہ یقینا ہم تمہارے ہی جیسے بشر ہیں لیکن خدا جس بندے پر چاہتا ہے مخصوص احسان بھی کرتا ہے اور ہمارے اختیار میں یہ بھی نہیں ہے کہ ہم بلااذنِ خدا کوئی دلیل یا معجزہ لے آئیں اور صاحبان هایمان تو صرف اللہ ہی پر بھروسہ کرتے ہیں'),
(1762, 14, 12, 'اور ہم کیوں نہ اللہ پر بھروسہ کریں جب کہ اسی نے ہمیں ہمارے راستوں کی ہدایت دی ہے اور ہم یقینا تمہاری اذیتوں پر صبر کریں گے اور بھروسہ کرنے والے تو اللہ ہی پر بھروسہ کرتے ہیں'),
(1763, 14, 13, 'اور کافروں نے اپنے رسولوں سے کہا کہ ہم تم کو اپنی سرزمین سے نکال باہر کردیں گے یا تم ہمارے مذہب کی طرف پلٹ آؤ گے تو پروردگار نے ان کی طرف وحی کی کہ خبردار گھبراؤ نہیں ہم یقینا ظالمین کو تباہ و برباد کردیں گے'),
(1764, 14, 14, 'اور تمہیں ان کے بعد زمین میں آباد کردیں گے اور یہ سب ان لوگوں کے لئے ہے جو ہمارے مقام اور مرتبہ سے ڈرتے ہیں اور ہمارے عذاب کا خوف رکھتے ہیں'),
(1765, 14, 15, 'اور پیغمبروں نے ہم سے فتح کا مطالبہ کیا اور ان سے عناد رکھنے والے سرکش افراد ذلیل اور رسوا ہوگئے'),
(1766, 14, 16, 'ان کے پیچھے جہّنم ہے اور انہیں پیپ دار پانی پلایا جائے گا'),
(1767, 14, 17, 'جسے گھونٹ گھونٹ پئیں گے اور وہ انہیں گوارا نہ ہوگا اور انہیں موت ہر طرف سے گھیر لے گی حالانکہ وہ مرنے والے نہیں ہیں اور ان کے پیچھے بہت سخت عذاب لگا ہوا ہے'),
(1768, 14, 18, 'جن لوگوں نے اپنے پروردگار کا انکار کیا ان کے اعمال کی مثال اس راکھ کی ہے جسے اندھڑ کے دن کی تند ہوا اڑا لے جائے کہ وہ اپنے حاصل کئے ہوئے پر بھی کوئی اختیار نہ رکھیں گے اور یہی بہت دور تک پھیلی ہوئی گمراہی ہے'),
(1769, 14, 19, 'کیا تم نے نہیں دیکھا کہ اللہ نے زمین اور آسمانوں کو برحق پیدا کیا ہے وہ چاہے تو تم کو فنا کرکے ایک نئی مخلوق کو لاسکتا ہے'),
(1770, 14, 20, 'اور اللہ کے لئے یہ بات کوئی مشکل نہیں ہے'),
(1771, 14, 21, 'اور قیامت کے دن سب اللہ کے سامنے حاضر ہوں گے تو کمزور لوگ مستکبرین سے کہیں گے کہ ہم تو آپ کے پیروکار تھے تو کیا آپ عذاب الٰہی کے مقابلہ میں ہمارے کچھ کام آسکتے ہیں تو وہ جواب دیں گے کہ اگر خدا ہمیں ہدایت دے دیتا تو ہم بھی تمہیں ہدایت دے دیتے -اب تو ہمارے لئے سب ہی برابر ہے چاہے فریاد کریں یا صبر کریں کہ اب کوئی چھٹکارا ملنے والا نہیں ہے'),
(1772, 14, 22, 'اور شیطان تمام امور کا فیصلہ ہوجانے کے بعد کہے گا کہ اللہ نے تم سے بالکل برحق وعدہ کیا تھا اور میں نے بھی ایک وعدہ کیا تھا پھر میں نے اپنے وعدہ کی مخالفت کی اور میرا تمہارے اوپر کوئی زور بھی نہیں تھا سوائے اس کے کہ میں نے تمہیں دعوت دی اور تم نے اسے قبول کرلیا تو اب تم میری ملامت نہ کرو بلکہ اپنے نفس کی ملامت کرو کہ نہ میں تمہاری فریاد رسی کرسکتا ہوں نہ تم میری فریاد کو پہنچ سکتے ہو میں تو پہلے ہی سے اس بات سے بیزار ہوں کہ تم نے مجھے اس کا شریک بنا دیا اور بیشک ظالمین کے لئے بہت بڑا دردناک عذاب ہے'),
(1773, 14, 23, 'اور صاحبان ه ایمان و عمل صالح کو ان جنتوں میں داخل کیا جائے گا جن کے نیچے نہریں جاری ہوں گے وہ ہمیشہ حکم خدا سے وہیں رہیں گے اور ان کی ملاقات کا تحفہ سلام ہوگا'),
(1774, 14, 24, 'کیا تم نے نہیں دیکھا کہ اللہ نے کس طرح کلمہ طیبہ کی مثال شجرہ طیبہ سے بیان کی ہے جس کی اصل ثابت ہے اور اس کی شاخ آسمان تک پہنچی ہوئی ہے'),
(1775, 14, 25, 'یہ شجرہ ہر زمانہ میں حکم پروردگار سے پھل دیتا رہتا ہے اور خدا لوگوں کے لئے مثال بیان کرتا ہے کہ شاید اسی طرح ہوش میں آجائیں'),
(1776, 14, 26, 'اور کلمہ خبیثہ کی مثال اس شجرہ خبیثہ کی ہے جو زمین کے اوپر ہی سے اکھاڑ کر پھینک دیا جائے اور اس کی کوئی بنیاد نہ ہو'),
(1777, 14, 27, 'اللہ صاحبان هایمان کو قول ثابت کے ذریعہ دنیا اور آخرت میں ثابت قدم رکھتا ہے اور ظالمین کو گمراہی میں چھوڑ دیتا ہے اور وہ جو بھی چاہتا ہے انجام دیتا ہے'),
(1778, 14, 28, 'کیا تم نے ان لوگوں کو نہیں دیکھا جنہوں نے اللہ کی نعمت کو کفرانِ نعمت سے تبدیل کردیا اور اپنی قوم کو ہلاکت کی منزل تک پہنچادیا'),
(1779, 14, 29, 'وہ جہنم ّجس میں سب واصل ہوں گے اور وہ بدترین قرار گاہ ہے'),
(1780, 14, 30, 'اور ان لوگوں نے اللہ کے لئے مثل قرار دیئے تاکہ اس کے ذریعہ لوگوں کو بہکا سکیں تو آپ کہہ دیجئے کہ تھوڑے دن اور مزے کرلو پھر تو تمہارا انجام جہّنم ہی ہے'),
(1781, 14, 31, 'اور آپ میرے ایماندار بندوں سے کہہ دیجئے کہ نمازیں قائم کریں اور ہمارے رزق میں سے خفیہ اور علانیہ ہماری راہ میں انفاق کریں قبل اس کے کہ وہ دن آجائے جب نہ تجارت کام آئے گی اور نہ دوستی'),
(1782, 14, 32, 'اللہ ہی وہ ہے جس نے آسمان و زمین کو پیدا کیا ہے اور آسمان سے پانی برسا کر اس کے ذریعہ تمہاری روزی کے لئے پھل پیدا کئے ہیں اور کشتیوں کو مسخر کردیا ہے کہ سمندر میں اس کے حکم سے چلیں اور تمہارے لئے نہروں کو بھی مسخر کردیا ہے'),
(1783, 14, 33, 'اور تمہارے لئے حرکت کرنے والے آفتاب و ماہتاب کو بھی مسخر کردیا ہے اور تمہارے لئے رات اور دن کو بھی مسخر کردیا ہے'),
(1784, 14, 34, 'اور جو کچھ تم نے مانگا اس میں سے کچھ نہ کچھ ضرور دیا اور اگر تم اس کی نعمتوں کو شمار کرنا چاہو گے تو ہرگز شمار نہیں کرسکتے -بیشک انسان بڑا ظالم اور انکار کرنے والا ہے'),
(1785, 14, 35, 'اور اس وقت کو یاد کرو جب ابراہیم نے کہا کہ پروردگار اس شہر کو محفوظ بنادے اور مجھے اور میری اولاد کو بت پرستی سے بچائے رکھنا'),
(1786, 14, 36, 'پروردگار ان بتوں نے بہت سے لوگوں کو گمراہ کردیا ہے تو اب جو میرا اتباع کرے گا وہ مجھ سے ہوگا اور جو معصیت کرے گا اس کے لئے توِ بڑا بخشنے والا اور مہربان ہے'),
(1787, 14, 37, 'پروردگار میں نے اپنی ذریت میں سے بعض کو تیرے محترم مکان کے قریب بے آب و گیاہ وادی میں چھوڑ دیا ہے تاکہ نمازیں قائم کریں اب تو لوگوں کے دلوں کو ان کی طرف موڑ دے اور انہیں پھلوں کا رزق عطا فرما تاکہ وہ تیرے شکر گزار بندے بن جائیں'),
(1788, 14, 38, 'پروردگار ہم جس بات کا اعلان کرتے ہیں یا جس کو چھپاتے ہیں تو سب سے باخبر ہے اور اللہ پر زمین و آسمان میں کوئی چیز مخفی نہیں رہ سکتی'),
(1789, 14, 39, 'شکر ہے اس خدا کا جس نے مجھے بڑھاپے میں اسماعیل و اسحاق جیسی اولاد عطا کی ہے کہ بیشک میرا پروردگار دعاؤں کا سننے والا ہے'),
(1790, 14, 40, 'پروردگار مجھے اور میری ذریت میں نماز قائم کرنے والے قرار دے اور پروردگار میری دعا کو قبول کرلے'),
(1791, 14, 41, 'پروردگار مجھے اور میرے والدین کو اور تمام مومنین کو اس دن بخش دینا جس دن حساب قائم ہوگا'),
(1792, 14, 42, 'اور خبردار خدا کو ظالمین کے اعمال سے غافل نہ سمجھ لینا کہ وہ انہیں اس دن کے لئے مہلت دے رہا ہے جس دن آنکھیں خوف سے پتھرا جائیں گی'),
(1793, 14, 43, 'سر اٹھائے بھاگے چلے جارہے ہوں گے اور پلکیں بھی نہ پلٹتی ہوں گی اور ان کے دل دہشت سے ہوا ہورہے ہوں گے'),
(1794, 14, 44, 'اور آپ لوگوں کو اس دن سے ڈرائیں جس دن ان تک عذاب آجائے گا تو ظالمین کہیں گے کہ پروردگار ہمیں تھوڑی مدّت کے لئے پلٹا دے کہ ہم تیری دعوت کو قبول کرلیں اور تیرے رسولوں کا اتباع کرلیں تو جواب ملے گا کہ کیا تم نے اس سے پہلے قسم نہیں کھائی تھی کہ تمہیں کسی طرح کا زوال نہ ہوگا'),
(1795, 14, 45, 'اور تم تو ان ہی لوگوں کے مکانات میں ساکن تھے جنہوں نے اپنے اوپر ظلم کیا تھا اور تم پر واضح تھا کہ ہم نے ان کے ساتھ کیا برتاؤ کیا ہے اور ہم نے تمہارے لئے مثالیں بھی بیان کردی تھیں'),
(1796, 14, 46, 'اور ان لوگوں نے اپنا سارا مکر صرف کردیا اور خدا کی نگاہ میں ان کا سارا مکر ہے اگرچہ ان کا مکر ایسا تھا کہ اس سے پہاڑ بھی اپنی جگہ سے ہٹ جائیں'),
(1797, 14, 47, 'تو خبردار تم یہ خیال بھی نہ کرنا کہ خدا اپنے رسولوں سے کئے ہوئے وعدہ کی خلاف ورزی کرے گا اللہ سب پر غالب اور بڑا انتقام لینے والا ہے'),
(1798, 14, 48, 'اس دن جب زمین دوسری زمین میں تبدیل ہوجائے گی اور آسمان بھی بدل دیئے جائیں گے اور سب خدائے واحد و قہار کے سامنے پیش ہوں گے'),
(1799, 14, 49, 'اور تم اس دن مجرمین کو دیکھو گے کہ کس طرح زنجیروں میں جکڑے ہوئے ہیں'),
(1800, 14, 50, 'ان کے لباس قطران کے ہوں گے اور ان کے چہروں کو آگ ہر طرف سے ڈھانکے ہوئے ہوگی'),
(1801, 14, 51, 'تاکہ خدا ہر نفس کو اس کے کئے کا بدلہ دے دے کہ وہ بہت جلد حساب کرنے والا ہے'),
(1802, 14, 52, 'بیشک یہ قرآن لوگوں کے لئے ایک پیغام ہے تاکہ اسی کے ذریعہ انہیں ڈرایا جائے اور انہیں معلوم ہوجائے کہ خدا ہی خدائے واحد و یکتا ہے اور پھر صاحبانِ عقل نصیحت حاصل کرلیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الرۤ - یہ کتابِ خدا اور قرآنِ مبین کی آیات ہیں'),
(1804, 15, 2, 'ایک دن آنے والا ہے جب کفار بھی یہ تمنا کریں گے کہ کاش ہم بھی مسلمانِ ہوتے'),
(1805, 15, 3, 'انہیں ان کے حال پر چھوڑ دو کھائیں پئیں اور مزے اڑائیں اور امیدیں انہیں غفلت میں ڈالے رہیں عنقریب انہیں سب کچھ معلوم ہوجائے گا'),
(1806, 15, 4, 'اور ہم نے کسی قریہ کو ہلاک نہیں کیا مگر یہ کہ اس کے لئے ایک میعاد مقرر کردی تھی'),
(1807, 15, 5, 'کوئی امّت نہ اپنے وقت سے آگے بڑھ سکتی ہے اور نہ پیچھے ہٹ سکتی ہے'),
(1808, 15, 6, 'اور ان لوگوں نے کہا کہ اے وہ شخص جس پر قرآن نازل ہوا ہے تو دیوانہ ہے'),
(1809, 15, 7, 'اگر تو اپنے دعوٰی میں سچا ہے تو فرشتوں کو کیوں سامنے نہیں لاتاہے'),
(1810, 15, 8, 'حالانکہ ہم فرشتوں کو حق کے فیصلہ کے ساتھ ہی بھیجا کرتے ہیں اور اس کے بعد پھر کسی کو مہلت نہیں دی جاتی ہے'),
(1811, 15, 9, 'ہم نے ہی اس قرآن کو نازل کیا ہے اور ہم ہی اس کی حفاظت کرنے والے ہیں'),
(1812, 15, 10, 'اور ہم نے آپ سے پہلے بھی مختلف قوموں میں رسول بھیجے ہیں'),
(1813, 15, 11, 'اور جب ان کے پاس رسول آتے ہیں تو یہ صرف ان کا مذاق اڑاتے ہیں'),
(1814, 15, 12, 'اور ہم اسی طرح اس گمراہی کو مجرمین کے دل میں ڈال دیتے ہیں'),
(1815, 15, 13, 'یہ کبھی ایمان نہ لائیں گے کہ ان سے پہلے والوں کا طریقہ بھی ایسا ہی رہ چکا ہے'),
(1816, 15, 14, 'ہم اگر آسمان میں ان کے لئے کوئی دروازہ کھول دیں اور یہ لوگ دن دھاڑے اسی دروازے سے چڑھ جائیں'),
(1817, 15, 15, 'تو بھی کہیں گے کہ ہماری آنکھوں کو مدہوش کردیا گیا ہے اور ہم پر جادو کردیا گیا ہے'),
(1818, 15, 16, 'اور ہم نے آسمان میں برج بنائے اور انہیں دیکھنے والوں کے لئے ستاروں سے آراستہ کردیا'),
(1819, 15, 17, 'اور ہر شیطان رجیم سے محفوظ بنادیا'),
(1820, 15, 18, 'مگر یہ کہ کوئی شیطان وہاں کی بات چر اَانا چاہے تو اس کے پیچھے دہکتا ہوا شعلہ لگادیا گیا ہے'),
(1821, 15, 19, 'اور ہم نے زمین کو پھیلادیا ہے اور اس میں پہاڑوں کے لنگر ڈال دیئے ہیں اور ہر چیز کو معینہ مقدار کے مطابق پیدا کیا ہے'),
(1822, 15, 20, 'اور اس میں تمہارے لئے بھی اسبابِ معیشت قرار دئے ہیں اور ان کے لئے بھی جن کے تم رازق نہیں ہو'),
(1823, 15, 21, 'اور کوئی شے ایسی نہیں ہے جس کے ہمارے پاس خزانے نہ ہوں اور ہم ہر شے کو ایک معین مقدار میں ہی نازل کرتے ہیں'),
(1824, 15, 22, 'اور ہم نے ہواؤں کو بادلوں کا بوجھ اٹھانے والا بناکر چلایا ہے پھر آسمان سے پانی برسایا ہے جس سے تم کو سیراب کیا ہے اور تم اس کے خزانہ دار نہیں تھے'),
(1825, 15, 23, 'اور ہم ہی حیات و موت کے دینے والے ہیں اور ہم ہی سب کے والی و وارث ہیں'),
(1826, 15, 24, 'اور ہم تم سے پہلے گزر جانے والوں کو بھی جانتے ہیں اور بعد میں آنے والوں سے بھی باخبر ہیں'),
(1827, 15, 25, 'اور تمہارا پروردگار ہی سب کو ایک جگہ جمع کرے گا کہ وہ صاحبِ علم بھی ہے اور صاحب هحکمت بھی'),
(1828, 15, 26, 'اور ہم نے انسان کو سیاہی مائل نرم مٹی سے پیدا کیا ہے جو سوکھ کر کھن کھن بولنے لگی تھی'),
(1829, 15, 27, 'اور جناّت کو اس سے پہلے زہریلی آگ سے پیدا کیا ہے'),
(1830, 15, 28, 'اور اس وقت کو یاد کرو کہ جب تمہارے پروردگار نے ملائکہ سے کہا تھا کہ میں سیاہی مائل نرم کھنکھناتی ہوئی مٹی سے ایک بشر پیدا کرنے والا ہوں'),
(1831, 15, 29, 'پھر جب مکمل کرلوں اور اس میں اپنی روح حیات پھونک دوں تو سب کے سب سجدہ میں گر پڑنا'),
(1832, 15, 30, 'تو تمام ملائکہ نے اجتماعی طور پر سجدہ کرلیا تھا'),
(1833, 15, 31, 'علاوہ ابلیس کے کہ وہ سجدہ گزاروں کے ساتھ نہ ہوسکا'),
(1834, 15, 32, 'اللہ نے کہا کہ اے ابلیس تجھے کیا ہوگیا ہے کہ تو سجدہ گزاروں میں شامل نہ ہوسکا'),
(1835, 15, 33, 'اس نے کہا کہ میں ایسے بشر کو سجدہ نہیں کرسکتا جسے تو نے سیاہی مائل خشک مٹی سے پیدا کیا ہے'),
(1836, 15, 34, 'ارشاد ہوا کہ تو یہاں سے نکل جا کہ تو مردود ہے'),
(1837, 15, 35, 'اور تجھ پر قیامت کے دن تک لعنت ہے'),
(1838, 15, 36, 'اس نے کہا کہ پروردگار مجھے روز حشر تک کی مہلت دیدے'),
(1839, 15, 37, 'جواب ملا کہ تجھے مہلت دیدی گئی ہے'),
(1840, 15, 38, 'ایک معلوم اور معین وقت کے لئے'),
(1841, 15, 39, 'اس نے کہا کہ پروردگار جس طرح تو نے مجھے گمراہ کیا ہے میں ان بندوں کے لئے زمین میں ساز و سامان آراستہ کروں گا اور سب کو اکٹھا گمراہ کروں گا'),
(1842, 15, 40, 'علاوہ تیرے ان بندوں کے جنہیں تو نے خالص بنا لیا ہے'),
(1843, 15, 41, 'ارشاد ہوا کہ یہی میرا سیدھا راستہ ہے'),
(1844, 15, 42, 'میرے بندوں پر تیرا کوئی اختیار نہیں ہے علاوہ ان کے جو گمراہوں میں سے تیری پیروی کرنے لگیں'),
(1845, 15, 43, 'اور جہنم ّایسے تمام لوگوں کی آخری وعدہ گاہ ہے'),
(1846, 15, 44, 'اس کے سات دروازے ہیں اور ہر دروازے کے لئے ایک حصہ تقسیم کردیا گیا ہے'),
(1847, 15, 45, 'بیشک صاحبان ه تقوٰی باغات اور چشموں کے درمیان رہیں گے'),
(1848, 15, 46, 'انہیں حکم ہوگا کہ تم ان باغات میں سلامتی اور حفاظت کے ساتھ داخل ہو جاؤ'),
(1849, 15, 47, 'اور ہم نے ان کے سینوں سے ہر طرح کی کدورت نکال لی ہے اور وہ بھائیوں کی طرح آمنے سامنے تخت پر بیٹھے ہوں گے'),
(1850, 15, 48, 'نہ انہیں کوئی تکلیف چھو سکے گی اور نہ وہاں سے نکالے جائیں گے'),
(1851, 15, 49, 'میرے بندوں کو خبر کردو کہ میں بہت بخشنے والا اور مہربان ہوں'),
(1852, 15, 50, 'اور میرا عذاب بھی بڑا دردناک عذاب ہے'),
(1853, 15, 51, 'اور انہیں ابراہیم کے مہمانوں کے بارے میں اطلاع دیدو'),
(1854, 15, 52, 'جب وہ لوگ ابراہیم کے پاس وارد ہوئے اور سلام کیا تو انہوں نے کہا کہ ہم آپ سے خوفزدہ ہیں'),
(1855, 15, 53, 'انہوں نے کہا کہ آپ ڈریں نہیں ہم آپ کو ایک فرزند دانا کی بشارت دینے کے لئے آئے ہیں'),
(1856, 15, 54, 'ابراہیم نے کہا کہ اب جب کہ بڑھاپا چھاگیا ہے تو مجھے کس چیز کی بشارت دے رہے ہو'),
(1857, 15, 55, 'انہوں نے کہا کہ ہم آپ کو بالکل سچی بشارت دے رہے ہیں خبردر آپ مایوسوں میں سے نہ ہوجائیں'),
(1858, 15, 56, 'ابراہیم علیھ السّلام نے کہا کہ رحمت خدا سے سوائے گمراہوں کے کون مایوس ہوسکتا ہے'),
(1859, 15, 57, 'پھر کہا کہ مگر یہ تو بتائیے کہ آپ لوگوں کا مقصد کیا ہے'),
(1860, 15, 58, 'انہوں نے کہا کہ ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں'),
(1861, 15, 59, 'علاوہ لوط کے گھر والوں کے کہ ان میں کے ہر ایک کو نجات دینے والے ہیں'),
(1862, 15, 60, 'علاوہ ان کی بیوی کے کہ اس کے لئے ہم نے طے کردیا ہے کہ وہ عذاب میں رہ جانے والوں میں سے ہوگی'),
(1863, 15, 61, 'پھر جب فرشتے آل لوط کے پاس آئے'),
(1864, 15, 62, 'اور لوط نے کہا کہ تم تو اجنبی قسم کے لوگ معلوم ہوتے ہو'),
(1865, 15, 63, 'تو انہوں نے کہا ہم وہ عذاب لے کر آئے ہیں جس میں آپ کی قوم شک کیا کرتی تھی'),
(1866, 15, 64, 'اب ہم وہ برحق عذاب لے کر آئے ہیں اور ہم بالکل سچے ہیں'),
(1867, 15, 65, 'آپ رات گئے اپنے اہل کو لے کر نکل جائیں اور خود پیچھے پیچھے سب کی نگرانی کرتے چلیں اور کوئی پیچھے کی طرف مڑ کر بھی نہ دیکھے اور جدھر کا حکم دیا گیا ہے سب ادھر ہی چلے جائیں'),
(1868, 15, 66, 'اور ہم نے اس امر کا فیصلہ کرلیا ہے کہ صبح ہوتے ہوتے اس قوم کی جڑیں تک کاٹ دی جائیں گی'),
(1869, 15, 67, 'اور ادھر شہر والے نئے مہمانوں کو دیکھ کر خوشیاں مناتے ہوئے آگئے'),
(1870, 15, 68, 'لوط نے کہا کہ یہ ہمارے مہمان ہیں خبردار ہمیں بدنام نہ کرنا'),
(1871, 15, 69, 'اور اللہ سے ڈرو اور رسوائی کا سامان نہ کرو'),
(1872, 15, 70, 'ان لوگوں نے کہا کہ کیا ہم نے آپ کو سب کے آنے سے منع نہیں کردیا تھا'),
(1873, 15, 71, 'لوط نے کہا کہ یہ ہماری قوم کی لڑکیاں حاضر ہیں اگر تم ایسا ہی کرنا چاہتے ہو'),
(1874, 15, 72, 'آپ کی جان کی قسم یہ لوگ گمراہی کے نشہ میں اندھے ہورہے ہیں'),
(1875, 15, 73, 'نتیجہ یہ ہوا کہ صبح ہوتے ہی انہیں ایک چیخ نے اپنی گرفت میں لے لیا'),
(1876, 15, 74, 'پھر ہم نے انہیں تہ و بالا کردیا اور ان کے اوپر کھڑنجے اور پتھروں کی بارش کردی'),
(1877, 15, 75, 'ان باتوں میں صاحبان هہوش کے لئے بڑی نشانیاں پائی جاتی ہیں'),
(1878, 15, 76, 'اور یہ بستی ایک مستقل چلنے والے راستہ پر ہے'),
(1879, 15, 77, 'اور بیشک اس میں بھی صاحبانِ ایمان کے لئے نشانیاں ہیں'),
(1880, 15, 78, 'اور اگرچہ ایکہ والے ظالم تھے'),
(1881, 15, 79, 'تو ہم نے ان سے بھی انتقام لیا اور یہ دونوں بستیاں واضح شاہراہ پر ہیں'),
(1882, 15, 80, 'اور اصحاب حجر نے بھی مرسلین کی تکذیب کی'),
(1883, 15, 81, 'اور ہم نے انہیں بھی اپنی نشانیاں دیں تو وہ اعراض کرنے والے ہی رہ گئے'),
(1884, 15, 82, 'اور یہ لوگ پہاڑ کو تراش کر محفوظ قسم کے مکانات بناتے تھے'),
(1885, 15, 83, 'تو انہیں بھی صبح سویرے ہی ایک چنگھاڑ نے پکڑلیا'),
(1886, 15, 84, 'تو انہوں نے جس قدر بھی حاصل کیا تھا کچھ کام نہ آیا'),
(1887, 15, 85, 'اور ہم نے آسمان و زمین اور ان کے درمیان جو کچھ بھی ہے سب کو برحق پیدا کیا ہے اور قیامت بہرحال آنے والی ہے لہذا آپ ان سے خوبصورتی کے ساتھ درگزر کردیں'),
(1888, 15, 86, 'بیشک آپ کا پروردگار سب کا پیدا کرنے والا اور سب کا جاننے والا ہے'),
(1889, 15, 87, 'اور ہم نے آپ کو سبع مثانی اور قرآن عظیم عطا کیا ہے'),
(1890, 15, 88, 'لہذا آپ ان کفار میں بعض افراد کو ہم نے جو کچھ نعمااُ دنیا عطا کردی ہیں ان کی طرف نگاہ اٹھا کر بھی نہ دیکھیں اور اس کے بارے میں ہرگز رنجیدہ بھی نہ ہوں بس آپ اپنے شانوں کو صاحبانِ ایمان کے لئے جھکائے رکھیں'),
(1891, 15, 89, 'اور یہ کہہ دیں کہ میں تو بہت واضح انداز سے ڈرانے والا ہوں'),
(1892, 15, 90, 'جس طرح کہ ہم نے ان لوگوں پر عذاب نازل کیا ہے جو کتاب خدا کا حصہ ّبانٹ کرنے والے تھے'),
(1893, 15, 91, 'جن لوگوں نے قرآن کو ٹکڑے کردیا ہے'),
(1894, 15, 92, 'لہذا آپ کے پروردگار کی قسم کہ ہم ان سے اس بارے میں ضرور سوال کریں گے'),
(1895, 15, 93, 'جو کچھ وہ کیا کرتے تھے'),
(1896, 15, 94, 'پس آپ اس بات کا واضح اعلان کردیں جس کا حکم دیا گیا ہے اور مشرکین سے کنارہ کش ہوجائیں'),
(1897, 15, 95, 'ہم ان استہزائ کرنے والوں کے لئے کافی ہیں'),
(1898, 15, 96, 'جو خدا کے ساتھ دوسرا خدا قرار دیتے ہیں اور عنقریب انہیں ان کا حال معلوم ہوجائے گا'),
(1899, 15, 97, 'اور ہم جانتے ہیں کہ آپ ان کی باتوں سے دل تنگ دل ہورہے ہیں'),
(1900, 15, 98, 'تو اب اپنے پروردگار کے حمدکی تسبیح کیجئے اور سجدہ گزاروں میں شامل ہوجایئے'),
(1901, 15, 99, 'اور اس وقت تک اپنے رب کی عبادت کرتے رہئے جب تک کہ موت نہ آجائے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'امر الٰہی آگیا ہے لہذا اب بلاوجہ جلدی نہ مچاؤ کہ خدا ان کے شرک سے پاک و پاکیزہ اور بلند و بالا ہے'),
(1903, 16, 2, 'وہ جس بندے پر چاہتا ہے اپنے حکم سے ملائکہ کو روح کے ساتھ نازل کردیتا ہے کہ ان بندوں کو ڈراؤ اورسمجھاؤ کہ میرے علاوہ کوئی خدا نہیں ہے لہذا مجھی سے ڈریں'),
(1904, 16, 3, 'اسی خدا نے زمین و آسمان کو حق کے ساتھ پیدا کیا ہے اور وہ ان کے شریکوں سے بہت بلند و بالاتر ہے'),
(1905, 16, 4, 'اس نے انسان کو ایک قطرہ نجس سے پیدا کیا ہے مگر پھر بھی وہ کّھلم کھلّا جھگڑا کرنے والا ہوگیا ہے'),
(1906, 16, 5, 'اور اسی نے چوپایوں کو بھی پیدا کیا ہے جن میں تمہارے لئے گرم لباس اور دیگر منافع کا سامان ہے اور بعض کو تو تم کھاتے بھی ہو'),
(1907, 16, 6, 'اور تمہارے لئے ان ہی جانوروں میں سے زینت کا سامان ہے جب تم شام کو انہیں واپس لاتے ہو اور صبح کو چراگاہ کی طرف لے جاتے ہو'),
(1908, 16, 7, 'اور یہ حیوانات تمہارے بوجھ کو اٹھا کر ان شہروں تک لے جاتے ہیں جہاں تک تم جان جونکھوں میں ڈالے بغیرنہیں پہنچ سکتے تھے بیشک تمہارا پروردگار بڑا شفیق اور مہربان ہے'),
(1909, 16, 8, 'اور اس نے گھوڑے خچر اور گدھے کو پیدا کیا تاکہ اس پر سواری کرو اور اسے زینت بھی قرار دو اور وہ ایسی چیزوں کو بھی پیدا کرتا ہے جن کا تمہیں علم بھی نہیں ہے'),
(1910, 16, 9, 'اور درمیانی راستہ کی ہدایت خدا کی اپنی ذمہ داری ہے اور بعض راستے کج بھی ہوتے ہیں اور وہ چاہتا تو تم سب کو زبردستی راہ راست پر لے آتا'),
(1911, 16, 10, 'وہ وہی خدا ہے جس نے آسمان سے پانی نازل کیا ہے جس کا ایک حصّہ پینے والا ہے اور ایک حصّے سے درخت پیدا ہوتے ہیں جن سے تم جانوروں کو چراتے ہو'),
(1912, 16, 11, 'وہ تمہارے لئے زراعت,زیتون,خرمے,انگور اور تمام پھل اسی پانی سے پیدا کرتا ہے - اس امر میں بھی صاحبانِ فکر کے لئے اس کی قدرت کی نشانیاں پائی جاتی ہیں'),
(1913, 16, 12, 'اور اسی نے تمہارے رات دن اور آفتاب و ماہتاب سب کو مسخر کردیا ہے اور ستارے بھی اسی کے حکم کے تابع ہیں بیشک اس میں بھی صاحبانِ عقل کے لئے قدرت کی بہت سی نشانیاں پائی جاتی ہیں'),
(1914, 16, 13, 'اور جو کچھ تمہارے لئے اس زمین کے اندر مختلف رنگوں میں پیدا کیا ہے اس میں بھی عبرت حاصل کرنے والی قوم کے لئے اس کی نشانیاں پائی جاتی ہیں'),
(1915, 16, 14, 'اور وہی وہ ہے جس نے سمندروں کو مّسخردیا ہے تاکہ تم اس میں سے تازہ گوشت کھاسکو اور پہننے کے لئے زینت کا سامان نکال سکو اور تم تو دیکھ رہے ہو کہ کشتیاں کس طرح اس کے سینے کو چیرتی ہوئی چلی جارہی ہیں اور یہ سب اس لئے بھی ہے کہ تم اس کے فضل و کرم کو تلاش کرسکو اور شاید اسی طرح اس کے شکر گزار بندے بھی بن جاؤ'),
(1916, 16, 15, 'اور اس نے زمین میں پہاڑوں کے لنگر ڈال دئے تاکہ تمہیں لے کر اپنی جگہ سے ہٹ نہ جائے اور نہریں اور راستے بنادئے تاکہ منزل سفر میں ہدایت پاسکو'),
(1917, 16, 16, 'اور علامات معین کردیں اور لوگ ستاروں سے بھی راستے دریافت کرلیتے ہیں'),
(1918, 16, 17, 'کیا ایسا پیدا کرنے والا ان کے جیسا ہوسکتا ہے جو کچھ نہیں پیدا کرسکتے آخر تمہیں ہوش کیوں نہیں آرہا ہے'),
(1919, 16, 18, 'اور تم اللہ کی نعمتوں کو شمار بھی کرنا چاہو تو نہیں کرسکتے ہو بیشک اللہ بڑا مہربان اور بخشنے والا ہے'),
(1920, 16, 19, 'اور اللہ ہی تمہارے باطن و ظاہر دونوں سے باخبر ہے'),
(1921, 16, 20, 'اور اس کے علاوہ جنہیں یہ مشرکین پکارتے ہیں وہ خود ہی مخلوق ہیں اور وہ کسی چیز کو خلق نہیں کرسکتے ہیں'),
(1922, 16, 21, 'وہ تو مفِدہ ہیں ان میں زندگی بھی نہیں ہے اور نہ انہیں یہ خبر ہے کہ مفِدے کب اٹھائے جائیں گے'),
(1923, 16, 22, 'تمہارا خدا صرف ایک ہے اور جو لوگ آخرت پر ایمان نہیں رکھتے ہیں ان کے دل منکر قسم کے ہیں اور وہ خود مغرور و متکّبر ہیں'),
(1924, 16, 23, 'یقینا اللہ ان تمام باتوں کو جانتا ہے جنہیں یہ حُھپاتے ہیں یا جن کا اظہار کرتے ہیں وہ متکبرین کو ہرگز پسند نہیں کرتا ہے'),
(1925, 16, 24, 'اور جب ان سے پوچھا جاتا ہے کہ تمہارے پروردگار نے کیا کیا نازل کیا ہے تو کہتے ہیں کہ یہ سب پچھلے لوگوں کے افسانے ہیں'),
(1926, 16, 25, 'تاکہ یہ مکمل طور پر اپنا بھی بوجھ اٹھائیں اور اپنے ان مریدوں کا بھی بوجھ اٹھائیں جنہیں بلاعلم و فہم کے گمراہ کرتے رہے ہیں - بیشک یہ بڑا بدترین بوجھ اٹھانے والے ہیں'),
(1927, 16, 26, 'یقینا ان سے پہلے والوں نے بھی مکاریاں کی تھیں تو عذاب الٰہی ان کی تعمیرات تک آیا اور اسے جڑ سے اکھاڑ پھینک دیا اور ان کے سروں پر چھت گر پڑی اور عذاب ایسے انداز سے آیا کہ انہیں شعور بھی نہ پیدا ہوسکا'),
(1928, 16, 27, 'اس کے بعد وہ روزِ قیامت انہیں رسوا کرے گا اور پوچھے گا کہاں ہیں وہ میرے شریک جن کے بارے میں تم جھگڑا کیا کرتے تھے - اس وقت صاحبان هعلم کہیں گے کہ آج رسوائی اور برائی ان کافروں کے لئے ثابت ہوگئی ہے'),
(1929, 16, 28, 'جنہیں ملائکہ اس عالم میں اٹھاتے ہیں کہ وہ اپنے نفس کے ظالم ہوتے ہیں تو اس وقت اطاعت کی پیشکش کرتے ہیں کہ ہم تو کوئی برائی نہیں کرتے تھے. بیشک خدا خوب جانتا ہے کہ تم کیا کیا کرتے تھے'),
(1930, 16, 29, 'جاؤ اب جہنمّ کے دروازوں سے داخل ہوجاؤ اور ہمیشہ وہیں رہو کہ متکبرین کا ٹھکانا بہت برا ہوتا ہے'),
(1931, 16, 30, 'اور جب صاحبان هتقوٰی سے کہا گیا کہ تمہارے پروردگار نے کیا نازل کیا ہے تو انہوں نے کہا کہ سب خیر ہے - بیشک جن لوگوں نے اس دنیا میں نیک اعمال کئے ہیں ان کے لئے نیکی ہے اور آخرت کا گھر تو بہرحال بہتر ہے اور وہ لَتقّین کا بہترین مکان ہے'),
(1932, 16, 31, 'وہاں ہمیشہ رہنے والے باغات ہیں جن میں یہ لوگ داخل ہوں گے اور ان کے نیچے نہریں جاری ہوں گی وہ جو کچھ چاہیں گے سب ان کے لئے حاضر ہوگا کہ اللہ اسی طرح ان صاحبان ه تقوٰی کو جزا دیتا ہے'),
(1933, 16, 32, 'جنہیں ملائکہ اس عالم میں اٹھاتے ہیں کہ وہ پاک و پاکیزہ ہوتے ہیں اور ان سے ملائکہ کہتے ہیں کہ تم پر سلام ہو اب تم اپنے نیک اعمال کی بنا پر جنّت میں داخل ہوجاؤ'),
(1934, 16, 33, 'کیا یہ لوگ صرف اس بات کا انتظار کررہے ہیں کہ ان کے پاس ملائکہ آجائیں یا حکم پروردگار آجائے تو یہی ان کے پہلے والوں نے بھی کیا تھا اور اللہ نے ان پر کوئی ظلم نہیں کیا ہے بلکہ یہ خود ہی اپنے نفس پر ظلم کرتے رہے ہیں'),
(1935, 16, 34, 'نتیجہ یہ ہوا کہ ان کے اعمال کے بفِے اثرات ان تک پہنچ گئے اور جن باتوں کا یہ مذاق اڑایا کرتے تھے ان ہی باتوں نے انہیں اپنے گھیرے میں لے لیا اور پھر تباہ و برباد کردیا'),
(1936, 16, 35, 'اور مشرکین کہتے ہیں کہ اگر خدا چاہتا تو ہم یا ہمارے بزرگ اس کے علاوہ کسی کی عبادت نہ کرتے اور نہ اس کے حکم کے بغیر کسی شے کو حرام قرار دیتے - اسی طرح ان کے پہلے والوں نے بھی کیا تھا تو کیا رسولوں کی ذمہ دری واضح اعلان کے علاوہ کچھ اور بھی ہے'),
(1937, 16, 36, 'اوریقینا ہم نے ہر امّت میں ایک رسول بھیجا ہے کہ تم لوگ اللہ کی عبادت کرو اور طاغوت سے اجتناب کرو پھر ان میں بعض کو خدا نے ہدایت دے دی اور بعض پر گمراہی ثابت ہوگئی تو اب تم لوگ روئے زمین میں سیر کرو اور دیکھو کہ تکذیب کرنے والوں کا انجام کیا ہوتا ہے'),
(1938, 16, 37, 'اگر آپ کو خواہش ہے کہ یہ ہدایت پاجائیں تو اللہ جس کو گمراہی میں چھوڑ چکا ہے اب اسے ہدایت نہیں دے سکتا اور نہ ان کا کوئی مدد کرنے والا ہوگا'),
(1939, 16, 38, 'ان لوگوں نے واقعی اللہ کی قسم کھائی تھی کہ اللہ مرنے والوں کو دوبارہ زندہ نہیں کرسکتا ہے حالانکہ یہ اس کا برحق وعدہ ہے یہ اور بات ہے کہ اکثر لوگ اس حقیقت سے باخبر نہیں ہیں'),
(1940, 16, 39, 'وہ چاہتا ہے کہ لوگوں کے لئے اس امر کو واضح کردے جس میں وہ اختلاف کررہے ہیں اور کفار کو یہ معلوم ہوجائے کہ وہ واقعی جھوٹ بولا کرتے تھے'),
(1941, 16, 40, 'ہم جس چیز کا ارادہ کرلیتے ہیں اس سے فقط اتنا کہتے ہیں کہ ہوجا اور پھر وہ ہوجاتی ہے'),
(1942, 16, 41, 'اور جن لوگوں نے ظلم سہنے کے بعد راہ هخدا میں ہجرت اختیار کی ہے ہم عنقریب دنیا میں بھی ان کو بہترین مقام عطا کریں گے اور آخرت کا اجر تو یقینا بہت بڑا ہے .اگر یہ لوگ اس حقیقت سے باخبر ہوں'),
(1943, 16, 42, 'یہی وہ لوگ ہیں جنہوں نے صبر کیا ہے اور اپنے پروردگار پر بھروسہ کرتے رہے ہیں'),
(1944, 16, 43, 'اور ہم نے آپ سے پہلے بھی مذِدوں ہی کو رسول بنا کر بھیجا ہے اور ان کی طرف بھی وحی کرتے رہے ہیں تو ان سے کہئے کہ اگر تم نہیں جانتے ہو تو جاننے والوں سے دریافت کرو'),
(1945, 16, 44, 'اور ہم نے ان رسولوں کو معجزات اور کتابوں کے ساتھ بھیجا ہے اور آپ کی طرف بھی ذکر کو (قرآن)نازل کیا ہے تاکہ ان کے لئے ان احکام کو واضح کردیں جو ان کی طرف نازل کئے گئے ہیں اور شاید یہ اس بارے میں کچھ غور و فکر کریں'),
(1946, 16, 45, 'کیا یہ برائیوں کی تدبیریں کرنے والے کفار اس بات سے مطمئن ہوگئے ہیں کہ اللہ اچانک انہیں زمین میں دھنسادے یا ان تک اس طرح عذاب آجائے کہ انہیں اس کا شعور بھی نہ ہو'),
(1947, 16, 46, 'یا انہیں چلتے پھرتے گرفتار کرلیا جائے کہ یہ اللہ کو عاجز نہیں کرسکتے ہیں'),
(1948, 16, 47, 'یا پھر انہیں ڈرا ڈرا کر دھیرے دھیرے گرفت میں لیا جائے کہ تمہارا پروردگار بڑا شفیق اور مہربان ہے'),
(1949, 16, 48, 'کیا ان لوگوں نے ان مخلوقات کو نہیں دیکھا ہے جن کا سایہ داہنے بائیں پلٹتا رہتا ہے کہ سب اسی کی بارگاہ میں تواضع و انکسار کے ساتھ سجدہ ریز ہیں'),
(1950, 16, 49, 'اور اللہ ہی کے لئے آسمان کی تمام چیزیں اور زمین کے تمام چلنے والے اور ملائکہ سجدہ ریز ہیں اور کوئی استکبار کرنے والا نہیں ہے'),
(1951, 16, 50, 'یہ سب اپنے پروردگار کی برتری اور عظمت سے خوفزدہ ہیں اور اسی کے امر کے مطابق کام کررہے ہیں'),
(1952, 16, 51, 'اور اللہ نے کہہ دیا ہے کہ خبردار دو خدا نہ بناؤ کہ اللہ صرف خدائے واحد ہے لہذا مجھ ہی سے ڈرو'),
(1953, 16, 52, 'اور اسی کے لئے زمین و آسمان کی ہر شے ہے اور اسی کے لئے دائمی اطاعت بھی ہے تو کیا تم غیر خدا سے بھی ڈرتے ہو'),
(1954, 16, 53, 'اور تمہارے پاس جو بھی نعمت ہے وہ سب اللہ ہی کی طرف سے ہے اور اس کے بعد بھی جب تمہیں کوئی تکلیف چھولیتی ہے تو تم اسی سے فریاد کرتے ہو'),
(1955, 16, 54, 'اور جب وہ تکلیف کو دور کردیتاہے تو تم میں سے ایک گروہ اپنے پروردگار کا شریک بنانے لگتا ہے'),
(1956, 16, 55, 'تاکہ ان نعمتوں کا انکار کردیں جو ہم نے انہیں عطا کی ہیں خیر چند روز اور مزے کرلو اس کے بعد انجام معلوم ہی ہوجائے گا'),
(1957, 16, 56, 'اور یہ ہمارے دئے ہوئے رزق میں سے ان کا بھی حصہ ّقرار دیتے ہیں جنہیں جانتے بھی نہیں ہیں تو عنقریب تم سے تمہارے افترا کے بارے میں بھی سوال کیا جائے گا'),
(1958, 16, 57, 'اور یہ اللہ کے لئے لڑکیاں قرار دیتے ہیں جب کہ وہ پاک اور بے نیاز ہے اور یہ جو کچھ چاہتے ہیں وہ سب ان ہی کے لئے ہے'),
(1959, 16, 58, 'اور جب خود ان میں سے کسی کو لڑکی کی بشارت دی جاتی ہے تو اس کا چہرہ سیاہ پڑ جاتا ہے اور وہ خون کے گھونٹ پینے لگتا ہے'),
(1960, 16, 59, 'قوم سے منہ حُھپاتا ہے کہ بہت بفِی خبر سنائی گئی ہے اب اس کی ذلّت سمیت زندہ رکھے یا خاک میں ملادے یقینا یہ لوگ بہت بفِا فیصلہ کررہے ہیں'),
(1961, 16, 60, 'جن لوگوں کو آخرت پرایمان نہیں ہے ان کی مثال بدترین مثال ہے اور اللہ کے لئے بہترین مثال ہے کہ وہی صاحب هعزّت اور صاحب هحکمت ہے'),
(1962, 16, 61, 'اگر خدا لوگوں سے ان کے ظلم کا مواخذہ کرنے لگتا تو روئے زمین پر ایک رینگنے والے کو بھی نہ چھوڑتا لیکن وہ سب کو ایک معین مدّت تک کے لئے ڈھیل دیتا ہے اس کے بعد جب وقت آجائے گا تو پھر نہ ایک ساعت کی تاخیر ہوگی اور نہ تقدیم'),
(1963, 16, 62, 'یہ خدا کے لئے وہ قرار دیتے ہیں جو خود اپنے لئے ناپسند کرتے ہیں اور ان کی زبانیں یہ غلط بیانی بھی کرتی ہیں کہ آخرت میں ان کے لئے نیکی ہے حالانکہ یقینی طور پر ان کے لئے جہّنم اور یہ سب سے پہلے ہی ڈالے جائیں گے'),
(1964, 16, 63, 'اللہ کی اپنی قسم کہ ہم نے تم سے پہلے مختلف قوموں کی طرف رسول بھیجے تو شیطان نے ان کے کاروبار کو ان کے لئے آراستہ کردیا اور وہی آج بھی ان کا سرپرست ہے اور ان کے لئے بہت بڑا دردناک عذاب ہے'),
(1965, 16, 64, 'اور ہم نے آپ پر کتاب صرف اس لئے نازل کی ہے کہ آپ ان مسائل کی وضاحت کردیں جن میں یہ اختلاف کئے ہوئے ہیں اور یہ کتاب صاحبان هایمان کے لئے مجسمئہ ہدایت اور رحمت ہے'),
(1966, 16, 65, 'اور اللہ ہی نے آسمان سے پانی برسایا ہے اور اس کے ذریعہ زمین کو مردہ ہونے کے بعد دوبارہ زندہ کیا ہے اس میں بھی بات سننے والی قوم کے لئے نشانیاں پائی جاتی ہیں'),
(1967, 16, 66, 'اور تمہارے لئے حیوانات میں بھی عبرت کا سامان ہے کہ ہم ان کے شکم سے گوبر اور خون کے درمیان سے خالص دودھ نکالتے ہیں جو پینے والوں کے لئے انتہائی خوشگوار معلوم ہوتا ہے'),
(1968, 16, 67, 'اور پھر خرمہ اور انگور کے پھلوں سے وہ شیرہ نکالتے ہیں جس سے تم نشہ اور بہترین رزق سب کچھ تیار کرلیتے ہو اس میں بھی صاحبان هعقل کے لئے نشانیاں پائی جاتی ہیں'),
(1969, 16, 68, 'اور تمہارے پروردگار نے شہد کی مکھی کو اشارہ دیا کہ پہاڑوں اور درختوں اور گھروں کی بلندیوں میں اپنے گھر بنائے'),
(1970, 16, 69, 'اس کے بعد مختلف پھلوں سے غذا حاصل کرے اور نرمی کے ساتھ خدائی راستہ پر چلے جس کے بعد اس کے شکم سے مختلف قسم کے مشروب برآمد ہوں گے جس میں پورے عالم انسانیت کے لئے شفا کا سامان ہے اور اس میں بھی فکر کرنے والی قوم کے لئے ایک نشانی ہے'),
(1971, 16, 70, 'اور اللہ ہی نے تمہیں پیدا کیا ہے پھر وہ ہی وفات دیتا ہے اور بعض لوگوں کو اتنی بدترین عمر تک پلٹا دیا جاتا ہے کہ علم کے بعد بھی کچھ جاننے کے قابل نہ رہ جائیں. بیشک اللہ ہر شے کا جاننے والا اور ہر شے پر قدرت رکھنے والا ہے'),
(1972, 16, 71, 'اور اللہ ہی نے بعض کو رزق میں بعض پر فضیلت دی ہے تو جن کو بہتر بنایا گیا ہے وہ اپنا بقیہ رزق ان کی طرف نہیں پلٹا دیتے ہیں جو ان کے ہاتھوں کی ملکیت ہیں حالانکہ رزق میں سب برابر کی حیثیت رکھنے والے ہیں تو کیا یہ لوگ اللہ ہی کی نعمت کا انکار کررہے ہیں'),
(1973, 16, 72, 'اور اللہ نے تم ہی میں سے تمہارا جوڑا بنایا ہے پھر اس جوڑے سے اولاد قرار دی ہے اور سب کو پاکیزہ رزق دیا ہے تو کیا یہ لوگ باطل پر ایمان رکھتے ہیں اور اللہ ہی کی نعمت سے انکار کرتے ہیں'),
(1974, 16, 73, 'اور یہ لوگ اللہ کو چھوڑ کر ان کی عبادت کرتے ہیں جو نہ آسمان و زمین میں کسی رزق کے مالک ہیں اور نہ کسی چیز کی طاقت ہی رکھتے ہیں'),
(1975, 16, 74, 'تو خبردار اللہ کے لئے مثالیں بیان نہ کرو کہ اللہ سب کچھ جانتا ہے اور تم کچھ نہیں جانتے ہو'),
(1976, 16, 75, 'اللہ نے خود اس غلام مملوک کی مثال بیان کی ہے جو کسی چیز کا اختیار نہیں رکھتا ہے اور اس آزاد انسان کی مثال بیان کی ہے جسے ہم نے بہترین رزق عطا کیا ہے اور وہ اس میں سے خفیہ اور علانیہ انفاق کرتا رہتا ہے تو کیا یہ دونوں برابر ہوسکتے ہیں ساری تعریف صرف اللہ کے لئے ہی ہے مگر ان لوگوں کی اکثریت کچھ نہیں جانتی ہے'),
(1977, 16, 76, 'اور اللہ نے ایک اور مثال ان دو انسانوں کی بیان کی ہے جن میں سے ایک گونگا ہے اور اس کے بس میں کچھ نہیں ہے بلکہ وہ خود اپنے مولا کے سر پر ایک بوجھ ہے کہ جس طرف بھی بھیج دے کوئی خیر لے کر نہ آئے گا تو کیا وہ اس کے برابر ہوسکتا ہے جو عدل کا حکم دیتا ہے اور سیدھے راستہ پر گامزن ہے'),
(1978, 16, 77, 'آسمان و زمین کا سارا غیب اللہ ہی کے لئے ہے اور قیامت کا حکم تو صرف ایک پلک جھپکنے کے برابر یا اس سے بھی قریب تر ہے اور یقینا اللہ ہر شے پر قدرت رکھنے والا ہے'),
(1979, 16, 78, 'اور اللہ ہی نے تمہیں شکم مادر سے اس طرح نکالا ہے کہ تم کچھ نہیں جانتے تھے اور اسی نے تمہارے لئے کان آنکھ اور دل قرار دئے ہیں کہ شاید تم شکر گزار بن جاؤ'),
(1980, 16, 79, 'کیا ان لوگوں نے پرندوں کی طرف نہیں دیکھا کہ وہ کس طرح فضائے آسمان میں مسخر ہیں کہ اللہ کے علاوہ انہیں کوئی روکنے والا اور سنبھالنے والا نہیںہے بیشک اس میں بھی اس قوم کے لئے بہت سی نشانیاں ہیں جو ایمان رکھنے والی قوم ہے'),
(1981, 16, 80, 'اور اللہ ہی نے تمہارے لئے تمہارے گھروں کو وجہ سکون بنایا ہے اور تمہارے لئے جانوروں کی کھالوں سے ایسے گھر بنادئے ہیں جن کو تم روزِ سفر بھی ہلکاسمجھتے ہو اور روزِ اقامت بھی ہلکا محسوس کرتے ہو اور پھر ان کے اون, روئیں اور بالوں سے مختلف سامان هزندگی اور ایک مدّت کے لئے کارآمد چیزیں بنادیں'),
(1982, 16, 81, 'اور اللہ ہی نے تمہارے لئے مخلوقات کا سایہ قرار دیا ہے اور پہاڑوں میں چھپنے کی جگہیں بنائی ہیں اور ایسے پیراہن بنائے ہیں جو گرمی سے بچاسکیں اور پھر ایسے پیراہن بنائے ہیں جو ہتھیاروں کی زد سے بچاسکیں. وہ اسی طرح اپنی نعمتوں کو تمہارے اوپر تمام کردیتا ہے کہ شاید تم اطاعت گزار بن جاؤ'),
(1983, 16, 82, 'پھر اس کے بعد بھی اگر یہ ظالم منہ پھیر لیں تو آپ کا کام صرف واضح پیغام کا پہنچا دینا ہے اور بس'),
(1984, 16, 83, 'یہ لوگ اللہ کی نعمت کو پہچانتے ہیں اور پھر انکار کرتے ہیں اور ان کی اکثریت کافر ہے'),
(1985, 16, 84, 'اور قیامت کے دن ہم ہر امّت میں سے ایک گواہ لائیں گے اور اس کے بعد کافروں کو کسی طرح کی اجازت نہ دی جائے گی اور نہ ان کا عذر ہی سناُ جائے گا'),
(1986, 16, 85, 'اور جب ظالمین عذاب کو دیکھ لیں گے تو پھر اس میں کوئی تخفیف نہ ہوگی اور نہ انہیں کسی قسم کی مہلت دی جائے گی'),
(1987, 16, 86, 'اور جب مشرکین اپنے شرکائ کو دیکھیں گے تو کہیں گے پروردگار یہی ہمارے شرکائ تھے جنہیں ہم تجھے چھوڑ کر پکارا کرتے تھے پھر وہ شرکائ اس بات کو ان ہی کی طرف پھینک دیں گے کہ تم لوگ بالکل جھوٹے ہو'),
(1988, 16, 87, 'اور پھر اس دن اللہ کی بارگاہ میں اطاعت کی پیشکش کریں گے اور جن باتوں کا افتراکیا کرتے تھے وہ سب غائب اور بے کار ہوجائیں گی'),
(1989, 16, 88, 'جن لوگوں نے کفر اختیار کیا اور راہ خدا میں رکاوٹ پیدا کی ہم نے ان کے عذاب میں مزید اضافہ کردیا کہ یہ لوگ فساد برپا کیا کرتے تھے'),
(1990, 16, 89, 'اور قیامت کے دن ہم ہر گروہ کے خلاف ان ہی میں کا ایک گواہ اٹھائیں گے اور پیغمبر آپ کو ان سب کا گواہ بناکر لے آئیں گے اور ہم نے آپ پر کتاب نازل کی ہے جس میں ہر شے کی وضاحت موجود ہے اور یہ کتاب اطاعت گزاروں کے لئے ہدایت ,رحمت اور بشارت ہے'),
(1991, 16, 90, 'بیشک اللہ عدل ,احسان اور قرابت داروں کے حقوق کی ادائیگی کا حکم دیتا ہے اور بدکاری ,ناشائستہ حرکات اور ظلم سے منع کرتا ہے کہ شاید تم اسی طرح نصیحت حاصل کرلو'),
(1992, 16, 91, 'اور جب کوئی عہد کرو تو اللہ کے عہد کو پورا کرو اور اپنی قسموں کو ان کے استحکام کے بعد ہرگز مت توڑو جب کہ تم اللہ کو کفیل اور نگراں بناچکے ہو کہ یقینا اللہ تمہارے افعال کو خوب جانتا ہے'),
(1993, 16, 92, 'اور خبردار اس عورت کے مانند نہ ہوجاؤ جس نے اپنے دھاگہ کو مضبوط کاتنے کے بعد پھر اسے ٹکڑے ٹکڑے کرڈالا-کیا تم اپنے معاہدے کو اس چالاکی کا ذریعہ بناتے ہو کہ ایک گروہ دوسرے گروہ سے زیادہ فائدہ حاصل کرے-اللہ تمہیں ان ہی باتوں کے ذریعے آزما رہا ہے اور یقینا روزِ قیامت اس امر کی وضاحت کردے گا جس میںتم آپس میں اختلاف کررہے تھے'),
(1994, 16, 93, 'اور اگر پروردگار چاہتا تو جبراتم سب کو ایک قوم بنادیتا لیکن وہ اختیار دے کر جسے چاہتا ہے گمراہی میں چھوڑ دیتا ہے اور جسے چاہتا ہے منزل ہدایت تک پہنچادیتاہے اور تم سے یقینا ان اعمال کے بارے میں سوال کیا جائے گا جوتم دنیا میں انجام دے رہے تھے'),
(1995, 16, 94, 'اور خبردار اپنی قسموں کو فساد کا ذریعہ نہ بناؤ کہ نو مسلم افراد کے قدم ثابت ہونے کے بعد پھر اکھڑ جائیں اور تمہیں راہ هخدا سے روکنے کی پاداش میں بڑے عذاب کا مزہ چکھنا پڑے اور تمہارے لئے عذابِ عظیم ہوجائے'),
(1996, 16, 95, 'اور خبردار اللہ کے عہدے کے عوض معمولی قیمت (مال دنیا) نہ لو کہ جو کچھ اللہ کے پاس ہے وہی تمہارے حق میں بہتر ہے اگر تم کچھ جانتے بوجھتے ہو'),
(1997, 16, 96, 'جو کچھ تمہارے پاس ہے وہ سب خرچ ہوجائے گا اور جو کچھ اللہ کے پاس ہے وہی باقی رہنے والا ہے اور ہم یقینا صبر کرنے والوں کو ان کے اعمال سے بہتر جزا عطا کریں گے'),
(1998, 16, 97, 'جو شخص بھی نیک عمل کرے گا وہ مرد ہو یا عورت بشرطیکہ صاحب هایمان ہو ہم اسے پاکیزہ حیات عطا کریں گے اور انہیں ان اعمال سے بہتر جزا دیں گے جو وہ زندگی میں انجام دے رہے تھے'),
(1999, 16, 98, 'لہذا جب آپ قرآن پڑھیں تو شیطان هرجیم کے مقابلہ کے لئے اللہ سے پناہ طلب کریں'),
(2000, 16, 99, 'شیطان ہرگز ان لوگوں پر غلبہ نہیں پاسکتا جو صاحبان هایمان ہیں اور جن کا اللہ پر توکل اور اعتماد ہے'),
(2001, 16, 100, 'اس کا غلبہ صرف ان لوگوں پر ہوتا ہے جو اسے سرپرست بناتے ہیں اور اللہ کے بارے میں شرک کرنے والے ہیں'),
(2002, 16, 101, 'اور ہم جب ایک آیت کی جگہ پر دوسری آیت تبدیل کرتے ہیں تو اگرچہ خدا خوب جانتا ہے کہ وہ کیا نازل کررہا ہے لیکن یہ لوگ یہی کہتے ہیں کہ محمد تم افترا کرنے والے ہو حالانکہ ان کی اکثریت کچھ نہیں جانتی ہے'),
(2003, 16, 102, 'تو آپ کہہ دیجئے کہ اس قرآن کوروح القدس جبرئیل نے تمہارے پروردگار کی طرف سے حق کے ساتھ نازل کیا ہے تاکہ صاحبانِ ایمان کو ثبات و استقلال عطاکرے اور یہ اطاعت گزاروں کے لئے ایک ہداےت اور بشارت ہے'),
(2004, 16, 103, 'اور ہم خوب جانتے ہیں کہ یہ مشرکین یہ کہتے ہیں کہ انہیں کوئی انسان اس قرآن کی تعلیم دے رہا ہے-حالانکہ جس کی طرف یہ نسبت دیتے ہیں وہ عجمی ہے اور یہ زبان عربی واضح و فصیح ہے'),
(2005, 16, 104, 'بیشک جو لوگ اللہ کی نشانیوں پر ایمان نہیں لاتے ہیں خدا انہیں ہدایت بھی نہیں دیتا ہے اور ان کے لئے دردناک عذاب بھی ہے'),
(2006, 16, 105, 'یقینا غلط الزام لگانے والے صرف وہی افراد ہوتے ہیں جو اللہ کی آیتوں پر ایمان نہیں رکھتے ہیں اور وہی جھوٹے بھی ہوتے ہیں'),
(2007, 16, 106, 'جو شخص بھی ایمان لانے کے بعد کفر اختیار کرلے .... علاوہ اس کے کہ جو کفر پر مجبور کردیا جائے اور اس کا دل ایمان کی طرف سے مطمئن ہو ....اور کفر کے لئے سینہ کشادہ رکھتا ہو اس کے اوپر خدا کا غضب ہے اور اس کے لئے بہت بڑا عذاب ہے'),
(2008, 16, 107, 'یہ اس لئے کہ ان لوگوں نے زندگانی دنیا کو آخرت پر مقدم کیا ہے اور اللہ ظالم قوموں کو ہرگز ہدایت نہیں دیتا ہے'),
(2009, 16, 108, 'یہی وہ لوگ ہیں جن کے دلوں پر اور آنکھ کان پر کفر کی چھاپ لگا دی گئی ہے اور یہی وہ لوگ ہیں جوحقیقتا حقائق سے غافل ہیں'),
(2010, 16, 109, 'اور یقینا یہی لوگ آخرت میں گھاٹا اٹھانے والوں میں ہیں'),
(2011, 16, 110, 'اس کے بعد تمہارا پروردگار ان لوگوں کے لئے جنہوں نے فتنوں میں مبتلا ہونے کے بعد ہجرت کی ہے اور پھر جہاد بھی کیا ہے اور صبر سے بھی کام لیا ہے یقینا تمہارا پروردگار بہت زیادہ بخشنے والا اور مہربان ہے'),
(2012, 16, 111, 'قیامت کا دن وہ دن ہوگا جب ہر انسان اپنے نفس کی طرف سے دفاع کرنے کے لئے حاضر ہوگا اور نفس کو اس کے عمل کا پورا پورا بدلہ دیا جائے گا اور کسی پر ظلم نہیں کیا جائے گا'),
(2013, 16, 112, 'اور اللہ نے اس قریہ کی بھی مثال بیان کی ہے جو محفوظ اور مطمئن تھی اور اس کا رزق ہر طرف سے باقاعدہ آرہا تھا لیکن اس قریہ کے رہنے والوں نے اللہ کی نعمتوں کا انکار کیا تو خدا نے انہیں بھوک اور خوف کے لباس کا مزہ چکھادیا صرف ان کے ان اعمال کی بنا پر کہ جو وہ انجام دے رہے تھے'),
(2014, 16, 113, 'اور یقینا ان کے پاس رسول آیا تو انہوں نے اس کی تکذیب کردی تو پھر ان تک عذاب آپہنچا کہ یہ سب ظلم کرنے والے تھے'),
(2015, 16, 114, 'لہذا اب تم اللہ کے دئے ہوئے رزق حلال و پاکیزہ کو کھاؤ اور اس کی عبادت کرنے والے ہو تو اس کی نعمتوں کا شکریہ بھی ادا کرتے رہو'),
(2016, 16, 115, 'اس نے تمہارے لئے صرف اَمفِدار ,خون ,سور کا گوشت اور جو غیر خدا کے نام پر ذبح کیا جائے اسے حرام کردیا ہے اور اس میں بھی اگر کوئی شخص مضطر و مجبور ہوجائے اور نہ بغاوت کرے نہ حد سے تجاوز کرے تو خدا بہت بڑا بخشنے والا اور مہربان ہے'),
(2017, 16, 116, 'اور خبردار جو تمہاری زبانیں غلط بیانی سے کام لیتی ہیں اس کی بنا پر یہ نہ کہو کہ یہ حلال ہے اور یہ حرام ہے کہ اس طرح خدا پر جھوٹا بہتان باندھنے والے ہوجاؤ گے اور جو اللہ پر جھوٹا بہتان باندھتے ہیں ان کے لئے فلاح اور کامیابی نہیں ہے'),
(2018, 16, 117, 'یہ دنیا صرف ایک مختصر لذّت ہے اور اس کے بعد ان کے لئے بڑا دردناک عذاب ہے'),
(2019, 16, 118, 'اور ہم نے یہودیوں کے لئے ان تمام چیزوں کو حرام کردیا ہے جن کا تذکرہ ہم پہلے کرچکے ہیں اور یہ ہم نے ظلم نہیں کیا ہے بلکہ وہ خود اپنے نفس پر ظلم کرنے والے تھے'),
(2020, 16, 119, 'اس کے بعد تمہارا پروردگار ان لوگوں کے لئے جنہوں نے نادانی میں برائیاں کی ہیں اور اس کے بعد توبہ بھی کرلی ہے اور اپنے کو سدھار بھی لیا ہے تمہارا پروردگار ان باتوں کے بعد بہت بڑا بخشنے والا اور مہربان ہے'),
(2021, 16, 120, 'بیشک ابراہیم علیھ السّلامایک مستقل امّت اور اللہ کے اطاعت گزار اور باطل سے کترا کر چلنے والے تھے اور مشرکین میں سے نہیں تھے'),
(2022, 16, 121, 'وہ اللہ کی نعمتوں کے شکر گزار تھے خدا نے انہیں منتخب کیا تھا اور سیدھے راستہ کی ہدایت دی تھی'),
(2023, 16, 122, 'اور ہم نے انہیں دنیا میں بھی نیکی عطا کی اور آخرت میں بھی ان کا شمار نیک کردار لوگوں میں ہوگا'),
(2024, 16, 123, 'اس کے بعد ہم نے آپ کی طرف وحی کی کہ ابراہیم علیھ السّلام حنیف کے طریقہ کا اتباع کریں کہ وہ مشرکین میں سے نہیں تھے'),
(2025, 16, 124, 'ہفتہ کے دن کی تعظیم صرف ان لوگوں کے لئے قرار دی گئی تھی جو اس کے بارے میں اختلاف کررہے تھے اور آپ کا پروردگار روز قیامت ان تمام باتوں کا فیصلہ کردے گا جن میں یہ لوگ اختلاف کررہے ہیں'),
(2026, 16, 125, 'آپ اپنے رب کے راستے کی طرف حکمت اور اچھی نصیحت کے ذریعہ دعوت دیں اور ان سے اس طریقہ سے بحث کریں جو بہترین طریقہ ہے کہ آپ کا پروردگار بہتر جانتا ہے کہ کون اس کے راستے سے بہک گیا ہے اور کون لوگ ہدایت پانے والے ہیں'),
(2027, 16, 126, 'اور اگر تم ان کے ساتھ سختی بھی کرو تو اسی قدر جس قدر انہوں نے تمہارے ساتھ سختی کی ہے اور اگر صبر کرو تو صبر بہرحال صبر کرنے والوں کے لئے بہترین ہے'),
(2028, 16, 127, 'اور آپ صبر ہی کریں کہ آپ کا صبر بھی اللہ ہی کی مدد سے ہوگا اور ان کے حال پر رنجیدہ نہ ہوں اور ان کی مکاّریوں کی وجہ سے تنگدلی کا بھی شکار نہ ہوں'),
(2029, 16, 128, 'بیشک اللہ ان لوگوں کے ساتھ ہے جنہوں نے تقوٰی اختیار کیا ہے اور جو نیک عمل انجام دینے والے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'پاک و پاکیزہ ہے وہ پروردگار جو اپنے بندے کو راتوں رات مسجد الحرام سے مسجد اقصٰی تک لے گیا جس کے اطراف کو ہم نے بابرکت بنایا ہے تاکہ ہم اسے اپنی بعض نشانیاں دکھلائیں بیشک وہ پروردگار سب کی سننے والا اور سب کچھ دیکھنے والا ہے'),
(2031, 17, 2, 'اور ہم نے موسٰی کو کتاب عنایت کی اور اس کتاب کو بنی اسرائیل کے لئے ہدایت بنا دیا کہ خبردار میرے علاوہ کسی کو اپنا کارساز نہ بنانا'),
(2032, 17, 3, 'یہ بنی اسرائیل ان کی اولاد ہیں جن کو ہم نے نوح کے ساتھ کشتی میں اٹھایاتھا جو ہمارے شکر گزار بندے تھے'),
(2033, 17, 4, 'اور ہم نے بنی اسرائیل کو کتاب میں یہ اطلاع بھی دے دی تھی کہ تم زمین میں دو مرتبہ فساد کرو گے اور خوب بلندی حاصل کرو گے'),
(2034, 17, 5, 'اس کے بعد جب پہلے وعدہ کا وقت آگیا تو ہم نے تمہارے اوپر اپنے ان بندوں کو مسلّط کردیا جو بہت سخت قسم کے جنگجو تھے اور انہوں نے تمہارے دیار میں چن چن کر تمہیں مارا اور یہ ہمارا ہونے والا وعدہ تھا'),
(2035, 17, 6, 'اس کے بعد ہم نے تمہیں دوبار ان پر غلبہ دیا اور اموال و اولاد سے تمہاری مدد کی اور تمہیں بڑے گروہ والا بنادیا'),
(2036, 17, 7, 'اب تم نیک عمل کرو گے تو اپنے لئے اور برا کرو گے تو اپنے لئے کرو گے-اس کے بعد جب دوسرے وعدہ کا وقت آگیا تو ہم نے دوسری قوم کو مسلّط کردیا تاکہ تمہاری شکلیں بگاڑ دیں اور مسجد میں اس طرح داخل ہوں جس طرح پہلے داخل ہوئے تھے اور جس چیز پر بھی قابو پالیں اسے باقاعدہ تباہ و برباد کردیں'),
(2037, 17, 8, 'امید ہے کہ تمہارا پروردگار تم پر رحم کردے لیکن اگر تم نے دوبارہ فساد کیا تو ہم پھر سزا دیں گے اور ہم نے جہنم ّکو کافروں کے لئے ایک قید خانہ بنادیا ہے'),
(2038, 17, 9, 'بیشک یہ قرآن اس راستہ کی ہدایت کرتا ہے جو بالکل سیدھا ہے اور ان صاحبان هایمان کو بشارت دیتا ہے جو نیک اعمال بجالاتے ہیں کہ ان کے لئے بہت بڑا اجر ہے'),
(2039, 17, 10, 'اور جو لوگ آخرت پر ایمان نہیں رکھتے ہیں ان کے لئے ہم نے ایک دردناک عذاب مہیا ّکر رکھا ہے'),
(2040, 17, 11, 'اور انسان کبھی کبھی اپنے حق میں بھلائی کی طرح برائی کی دعا مانگنے لگتا ہے کہ انسان بہت جلد باز واقع ہوا ہے'),
(2041, 17, 12, 'اور ہم نے رات اور دن کو اپنی نشانی قرار دیا ہے پھر ہم رات کی نشانی کو مٹا دیتے ہیں اور دن کی نشانی کو روشن کردیتے ہیں تاکہ تم اپنے پروردگار کے فضل و انعام کو طلب کرسکو اور سال اور حساب کے اعداد معلوم کرسکو اور ہم نے ہر شے کو تفصیل کے ساتھ بیان کردیا ہے'),
(2042, 17, 13, 'اور ہم نے ہر انسان کے نامئہ اعمال کو اس کی گردن میں آویزاں کردیا ہے اور روز قیامت اسے ایک کھلی ہوئی کتاب کی طرح پیش کردیں گے'),
(2043, 17, 14, 'کہ اب اپنی کتاب کو پڑھ لو آج تمہارے حساب کے لئے یہی کتاب کافی ہے'),
(2044, 17, 15, 'جو شخص بھی ہدایت حاصل کرتا ہے وہ اپنے فائدہ کے لئے کرتا ہے اور جو گمراہی اختیار کرتا ہے وہ بھی اپنا ہی نقصان کرتا ہے اور کوئی کسی کا بوجھ اٹھانے والا نہیں ہے اور ہم تو اس وقت تک عذاب کرنے والے نہیں ہیں جب تک کہ کوئی رسول نہ بھیج دیں'),
(2045, 17, 16, 'اور ہم نے جب بھی کسی قریہ کو ہلاک کرنا چاہا تو اس کے ثروت مندوں پر احکام نافذ کردئے اور انہوں نے ان کی نافرمانی کی تو ہماری بات ثابت ہوگئی اور ہم نے اسے مکمل طور پر تباہ کردیا'),
(2046, 17, 17, 'اور ہم نے نوح کے بعد بھی کتنی اّمتوں کو ہلاک کردیا ہے اور تمہارا پروردگار بندوں کے گناہوں کا بہترین جاننے والا اور دیکھنے والا ہے'),
(2047, 17, 18, 'جو شخص بھی دنیا کا طلب گار ہے ہم اس کے لئے جلد ہی جو چاہتے ہیں دے دیتے ہیں پھر اس کے بعد اس کے لئے جہنمّ ہے جس میں وہ ذلّت و رسوائی کے ساتھ داخل ہوگا'),
(2048, 17, 19, 'اور جو شخص آخرت کا چاہنے والا ہے اور اس کے لئے ویسی ہی سعی بھی کرتا ہے اور صاحب هایمان بھی ہے تو اس کی سعی یقینا مقبول قرار دی جائے گی'),
(2049, 17, 20, 'ہم آپ کے پروردگار کی عطا و بخشش سے ِن کی اور اُن کی سب کی مدد کرتے ہیں اور آپ کے پروردگار کی عطا کسی پر بند نہیں ہے'),
(2050, 17, 21, 'آپ دیکھئے کہ ہم نے کس طرح بعض کو بعض پر فضیلت دی ہے اور پھر آخرت کے درجات اور وہاں کی فضیلتیں تو اور زیادہ بزرگ و برتر ہیں'),
(2051, 17, 22, 'خبردار اپنے پروردگار کے ساتھ کوئی دوسرا خدا قرار نہ دینا کہ اس طرح قابل همذمّت اور لاوارث بیٹھے رہ جاؤ گے اور کوئی خدا کام نہ آئے گا'),
(2052, 17, 23, 'اور آپ کے پروردگار کا فیصلہ ہے کہ تم سب اس کے علاوہ کسی کی عبادت نہ کرنا اور ماں باپ کے ساتھ اچھا برتاؤ کرنا اور اگر تمہارے سامنے ان دونوں میں سے کوئی ایک یا دونوں بوڑھے ہوجائیں تو خبردار ان سے اُف بھی نہ کہنا اور انہیں جھڑکنا بھی نہیں اور ان سے ہمیشہ شریفانہ گفتگو کرتے رہنا'),
(2053, 17, 24, 'اور ان کے لئے خاکساری کے ساتھ اپنے کاندھوں کو جھکا دینا اور ان کے حق میں دعا کرتے رہنا کہ پروردگار اُن دونوں پر اسی طرح رحمت نازل فرما جس طرح کہ انہوں نے بچپنے میں مجھے پالا ہے'),
(2054, 17, 25, 'تمہارا پروردگار تمہارے دلوں کے حالات سے خوب باخبر ہے اور اگر تم صالح اور نیک کردار ہو تو وہ توبہ کرنے والوں کے لئے بہت بخشنے والا بھی ہے'),
(2055, 17, 26, 'اور دیکھو قرابتداروں کو اور مسکین کو اور مسافر غربت زدہ کو اس کا حق دے دو اور خبردار اسراف سے کام نہ لینا'),
(2056, 17, 27, 'اسراف کرنے والے شیاطین کے بھائی بند ہیں اور شیطان تو اپنے پروردگار کا بہت بڑا انکار کرنے والا ہے'),
(2057, 17, 28, 'اگر تم کو اپنے رب کی رحمت کے انتظار میں جس کے تم امیدوار ہو ان افراد سے کنارہ کش بھی ہونا پڑے تو ان سے نرم انداز سے گفتگو کرنا'),
(2058, 17, 29, 'اور خبردار نہ اپنے ہاتھوں کو گردنوں سے بندھا ہوا قرار دو اور نہ بالکل پھیلا دو کہ آخر میں قابلِ ملامت اور خالی ہاتھ بیٹھے رہ جاؤ'),
(2059, 17, 30, 'تمہارا پروردگار جس کے لئے چاہتا ہے رزق کو وسیع یا تنگ بنا دیتا ہے وہ اپنے بندوں کے حالات کا خوب جاننے والا اور دیکھنے والا ہے'),
(2060, 17, 31, 'اور خبردار اپنی اولاد کو فاقہ کے خوف سے قتل نہ کرنا کہ ہم انہیں بھی رزق دیتے ہیں اور تمہیں بھی رزق دیتے ہیں بیشک ان کا قتل کردینا بہت بڑا گناہ ہے'),
(2061, 17, 32, 'اور دیکھو زنا کے قریب بھی نہ جانا کہ یہ بدکاری ہے اور بہت بفِا راستہ ہے'),
(2062, 17, 33, 'اور کسی نفس کو جس کو خدا نے محترم بنایا ہے بغیر حق کے قتل بھی نہ کرنا کہ جو مظلوم قتل ہوتا ہے ہم اس کے ولی کو بدلہ کا اختیار دے دیتے ہیں لیکن اسے بھی چاہئے کہ قتل میں حد سے آگے نہ بڑھ جائے کہ ِس کی بہرحال مدد کی جائے گی'),
(2063, 17, 34, 'اور یتیم کے مال کے قریب بھی نہ جانا مگر اس طرح جو بہترین طریقہ ہے یہاں تک کہ وہ توانا ہوجائے اور اپنے عہدوں کو پورا کرنا کہ عُہد کے بارے میں سوال کیا جائے گا'),
(2064, 17, 35, 'اور جب ناپو تو پورا ناپو اور جب تولو تو صحیح ترازو سے تولو کہ یہی بہتری اور بہترین انجام کا ذریعہ ہے'),
(2065, 17, 36, 'اور جس چیز کا تمہیں علم نہیں ہے اس کے پیچھے مت جانا کہ روزِ قیامت سماعتً بصارت اور قواُ قلب سب کے بارے میں سوال کیا جائے گا'),
(2066, 17, 37, 'اور روئے زمین پر اکڑ کر نہ چلنا کہ نہ تم زمین کو شق کرسکتے ہو اور نہ سر اٹھا کر پہاڑوں کی بلندیوں تک پہنچ سکتے ہو'),
(2067, 17, 38, 'یہ سب باتیں وہ ہیں جن کی برائی تمہارے پروردگار کے نزدیک سخت ناپسند ہے'),
(2068, 17, 39, 'یہ وہ حکمت ہے جس کی وحی تمہارے پروردگار نے تمہاری طرف کی ہے اور خبردار خدا کے ساتھ کسی اور کو خدا نہ قرار دینا کہ جہنمّ میں ملامت اور ذلّت کے ساتھ ڈال دیئے جاؤ'),
(2069, 17, 40, 'کیا تمہارے پروردگار نے تم لوگوں کے لئے لڑکوں کو پسند کیا ہے اور اپنے لئے ملائکہ میں سے لڑکیاں بنائی ہیں یہ تم بہت بڑی بات کہہ رہے ہو'),
(2070, 17, 41, 'اور ہم نے اس قرآن میں سب کچھ طرح طرح سے بیان کردیا ہے کہ یہ لوگ عبرت حاصل کریں لیکن ان کی نفرت ہی میں اضافہ ہورہا ہے'),
(2071, 17, 42, 'تو آپ کہہ دیجئے کہ ان کے کہنے کے مطابق اگر خدا کے ساتھ کچھ اور خدا بھی ہوتے تو اب تک صاحب هعرش تک پہنچنے کی کوئی راہ نکال لیتے'),
(2072, 17, 43, 'وہ پاک اور بے نیاز ہے اور ان کی باتوں سے بہت زیادہ بلند و بالا ہے'),
(2073, 17, 44, 'ساتوں آسمان اور زمین اور جو کچھ ان کے درمیان ہے سب اس کی تسبیح کررہے ہیں اور کوئی شے ایسی نہیں ہے جو اس کی تسبیح نہ کرتی ہو یہ اور بات ہے کہ تم ان کی تسبیح کو نہیں سمجھتے ہو. پروردگار بہت برداشت کرنے والا اور درگذر کرنے والا ہے'),
(2074, 17, 45, 'اور جب تم قرآن پڑھتے ہو تو ہم تمہارے اور آخرت پر ایمان نہ رکھنے والوں کے درمیان حجاب قائم کردیتے ہیں'),
(2075, 17, 46, 'اور ان کے دلوں پر پردے ڈال دیتے ہیں کہ کچھ سمجھ نہ سکیں اور ان کے کانوں کو بہرہ بنادیتے ہیں اور جب تم قرآن میں اپنے پروردگار کا تنہا ذکر کرتے ہو تو یہ الٹے پاؤ ںمتنفر ہوکر بھاگ جاتے ہیں'),
(2076, 17, 47, 'ہم خوب جانتے ہیں کہ یہ لوگ آپ کی طرف کان لگا کر سنتے ہیں تو کیا سنتے ہیں اور جب یہ باہم راز داری کی باتیں کرتے ہیں تو ہم اسے بھی جانتے ہیں یہ ظالم آپس میں کہتے ہیں کہ تم لوگ ایک جادو زدہ انسان کی پیروی کررہے ہو'),
(2077, 17, 48, 'ذرا دیکھو کہ انہوں نے تمہارے لئے کیسی مثالیں بیان کی ہیں اور اس طرح ایسے گمراہ ہوگئے ہیں کہ کوئی راستہ نہیں مل رہا ہے'),
(2078, 17, 49, 'اور یہ کہتے ہیں کہ جب ہم ہڈی اور خاک ہوجائیں گے تو کیا دوبارہ نئی مخلوق بناکر اٹھائے جائیں گے'),
(2079, 17, 50, 'آپ کہہ دیجئے کہ تم پتھر یا لوہا بن جاؤ'),
(2080, 17, 51, 'یا تمہارے خیال میں جو اس سے بڑی مخلوق ہوسکتی ہو وہ بن جاؤ پس عنقریب یہ لوگ کہیں گے کہ ہمیں کون دوبارہ واپس لاسکتا ہے تو کہہ دیجئے کہ جس نے تمہیں پہلی مرتبہ پیدا کیا ہے پھر یہ لوگ استہزائ میں سر ہلائیں گے اور کہیں گے کہ یہ سب کب ہوگا تو کہہ دیجئے کہ شاید قریب ہی ہوجائے'),
(2081, 17, 52, 'جس دن وہ تمہیں بلائے گا اور تم سب اس کی تعریف کرتے ہوئے لبیک کہو گے اور خیال کرو گے کہ بہت تھوڑی دیر دنیا میں رہے ہو'),
(2082, 17, 53, 'اور میرے بندوں سے کہہ دیجئے کہ صرف اچھی باتیں کیا کریں ورنہ شیطان یقینا ان کے درمیان فساد پیدا کرنا چاہے گا کہ شیطان انسان کا کِھلا ہوا دشمن ہے'),
(2083, 17, 54, 'تمہارا پروردگار تمہارے حالات سے بہتر واقف ہے وہ چاہے گا تو تم پر رحم کرے گا اور چاہے گا تو عذاب کرے گا اور پیغمبر ہم نے آپ کو ان کا ذمہ دار بناکر نہیں بھیجا ہے'),
(2084, 17, 55, 'اور آپ کا پروردگار زمین و آسمان کی ہر شے سے باخبر ہے اور ہم نے بعض انبیائ کو بعض پر فضیلت دی ہے اور داؤد کو زبور عطا کی ہے'),
(2085, 17, 56, 'اور ان لوگوں سے کہہ دیجئے کہ خدا کے علاوہ جن کا بھی خیال ہے سب کو بلالیں کوئی نہ ان کی تکلیف کو دور کرنے کا اختیار رکھتا ہے اور نہ ان کے حالات کے بدلنے کا'),
(2086, 17, 57, 'یہ جن کو خدا سمجھ کر پکارتے ہیں وہ خود ہی اپنے پروردگار کے لئے وسیلہ تلاش کررہے ہیں کہ کون زیادہ قربت رکھنے والا ہے اور سب اسی کی رحمت کے امیدوار اور اسی کے عذاب سے خوفزدہ ہیں یقینا آپ کے پروردگار کا عذاب ڈرنے کے لائق ہے'),
(2087, 17, 58, 'اور کوئی نافرمان آبادی ایسی نہیں ہے جسے ہم قیامت سے پہلے برباد نہ کردیں یا اس پرشدید عذاب نہ نازل کردیں کہ یہ بات کتاب میں لکھ دی گئی ہے'),
(2088, 17, 59, 'اور ہمارے لئے منہ مانگی نشانیاں بھیجنے سے صرف یہ بات مانع ہے کہ پہلے والوں نے تکذیب کی ہے اور ہلاک ہوگئے ہیں اور ہم نے قوم ثمود کو ان کی خواہش کے مطابق اونٹنی دے دی جو ہماری قدرت کو روشن کرنے والی تھی لیکن ان لوگوں نے اس پر ظلم کیا اور ہم تو نشانیوں کو صرف ڈرانے کے لئے بھیجتے ہیں'),
(2089, 17, 60, 'اور جب ہم نے کہہ دیا کہ آپ کا پروردگار تمام لوگوں کے حالات سے باخبر ہے اور جو خواب ہم نے آپ کو دکھلایا ہے وہ صرف لوگوں کی آزمائش کا ذریعہ ہے جس طرح کہ قرآن میں قابل هلعنت شجرہ بھی ایسا ہی ہے اور ہم لوگوں کو ڈراتے رہتے ہیں لیکن ان کی سرکشی بڑھتی ہی جارہی ہے'),
(2090, 17, 61, 'اور جب ہم نے ملائکہ سے کہا کہ آدم کو سجدہ کرو تو سب نے سجدہ کرلیا سوائے ابلیس کے کہ اس نے کہا کہ کیا میں اسے سجدہ کروں جسے تو نے مٹی سے بنایا ہے'),
(2091, 17, 62, 'کیا تو نے دیکھا ہے کہ یہ کیا شے ہے جسے میرے اوپر فضیلت د ے دی ہے اب اگر تو نے مجھے قیامت تک کی مہلت دے دی تو میں ان کی ذریت میں چند افراد کے علاوہ سب کا گلا گھونٹتا رہوں گا'),
(2092, 17, 63, 'جواب ملا کہ جا اب جو بھی تیرا اتباع کرے گا تم سب کی جزا مکمل طور پر جہنمّ ہے'),
(2093, 17, 64, 'جا جس پر بھی بس چلے اپنی آواز سے گمراہ کر اور اپنے سوار اور پیادوں سے حملہ کردے اور ان کے اموال اور اولاد میں شریک ہوجا اور ان سے خوب وعدے کر کہ شیطان سوائے دھوکہ دینے کے اور کوئی سچا وعدہ نہیں کرسکتا ہے ('),
(2094, 17, 65, 'بیشک میرے اصلی بندوں پر تیرا کوئی بس نہیں ہے اور آپ کا پروردگار ان کی نگہبانی کے لئے کافی ہے'),
(2095, 17, 66, 'اور آپ کا پروردگار ہی وہ ہے جو تم لوگوں کے لئے سمندر میں کشتیاں چلاتا ہے تاکہ تم اس کے فضل و کرم کو تلاش کرسکو کہ وہ تمہارے حال پر بڑا مہربان ہے'),
(2096, 17, 67, 'اور جب دریا میں تمہیں کوئی تکلیف پہنچی تو خدا کے علاوہ سب غائب ہوگئے جنہیں تم پکار رہے تھے اور پھر جب خدا نے تمہیں بچا کر خشکی تک پہنچا دیا تو تم پھر کنارہ کش ہوگئے اور انسان تو بڑا ناشکرا ہے'),
(2097, 17, 68, 'کیا تم اس بات سے محفوظ ہوگئے ہو کہ وہ تمہیں خشکی ہی میں دھنسادے یا تم پر پتھروں کی بوچھاڑ کردے اور اس کے بعد پھر کوئی کارساز نہ ملے'),
(2098, 17, 69, 'یا اس بات سے محفوظ ہوگئے ہو کہ وہ دوبارہ تمہیں سمندر میں لے جائے اور پھر تیز آندھیوں کو بھیج کر تمہارے کفر کی بنا پر تمہیں غرق کردے اور اس کے بعد کوئی ایسا نہ پاؤ جو ہمارے حکم کا پیچھا کرسکے'),
(2099, 17, 70, 'اور ہم نے بنی آدم کو کرامت عطا کی ہے اور انہیں خشکی اور دریاؤں میں سواریوں پر اٹھایا ہے اور انہیں پاکیزہ رزق عطا کیا ہے اور اپنی مخلوقات میں سے بہت سوں پر فضیلت دی ہے'),
(2100, 17, 71, 'قیامت کا دن وہ ہوگا جب ہم ہر گروہ انسانی کو اس کے پیشوا کے ساتھ بلائیں گے اور اس کے بعد جن کا نامئہ اعمال ان کے داہنے ہاتھ میں دیا جائے گا وہ اپنے صحیفہ کو پڑھیں گے اور ان پر ریشہ برابر ظلم نہیں ہوگا'),
(2101, 17, 72, 'اور جو اسی دنیا میں اندھا ہے وہ قیامت میں بھی اندھا اور بھٹکا ہوا رہے گا'),
(2102, 17, 73, 'اور یہ ظالم اس بات کے کوشاں تھے کہ آپ کو ہماری وحی سے ہٹا کر دوسری باتوں کے افترا پر آمادہ کردیں اور اس طرح یہ آپ کو اپنا دوست بنالیتے'),
(2103, 17, 74, 'اور اگر ہماری توفیق خاص نے آپ کو ثابت قدم نہ رکھا ہوتا تو آپ (بشری طور پر) کچھ نہ کچھ ان کی طرف مائل ضرور ہوجاتے'),
(2104, 17, 75, 'اور پھر ہم زندگانی دنیا اور موت دونوں مرحلوں پر رَہرا مزہ چکھاتے اور آپ ہمارے خلاف کوئی مددگار اور کمک کرنے والا بھی نہ پاتے'),
(2105, 17, 76, 'اور یہ لوگ آپ کو زمین مکہّ سے دل برداشتہ کررہے تھے کہ وہاں سے نکال دیں حالانکہ آپ کے بعد یہ بھی تھوڑے دنوں سے زیادہ نہ ٹھہر سکے'),
(2106, 17, 77, 'یہ آپ سے پہلے بھیجے جانے والے رسولوں میں ہمارا طریقہ کار رہا ہے اور آپ ہمارے طریقہ کار میں کوئی تغیر نہ پائیں گے'),
(2107, 17, 78, 'آپ زوال آفتاب سے رات کی تاریکی تک نماز قائم کریں اور نماز صبح بھی کہ نماز صبح کے لئے گواہی کا انتظام کیا گیا ہے'),
(2108, 17, 79, 'اور رات کے ایک حصہ میں قرآن کے ساتھ بیدار رہیں یہ آپ کے لئے اضافہ خیر ہے عنقریب آپ کا پروردگار اسی طرح آپ کو مقام محمود تک پہنچادے گا'),
(2109, 17, 80, 'اور یہ کہئے کہ پروردگار مجھے اچھی طرح سے آبادی میں داخل کر اور بہترین انداز سے باہر نکال اور میرے لئے ایک طاقت قرار دے دے جو میری مددگار ثابت ہو'),
(2110, 17, 81, 'اور کہہ دیجئے کہ حق آگیا اور باطل فنا ہوگیا کہ باطل بہرحال فنا ہونے والا ہے'),
(2111, 17, 82, 'اور ہم قرآن میں وہ سب کچھ نازل کررہے ہیں جو صاحبان هایمان کے لئے شفا اور رحمت ہے اور ظالمین کے لئے خسارہ میں اضافہ کے علاوہ کچھ نہ ہوگا'),
(2112, 17, 83, 'اور ہم جب انسان پر کوئی نعمت نازل کرتے ہیں تو وہ پہلو بچا کر کنارہ کش ہوجاتا ہے اور جب تکلیف ہوتی ہے تو مایوس ہوجاتا ہے'),
(2113, 17, 84, 'آپ کہہ دیجئے کہ ہر ایک اپنے طریقہ پر عمل کرتا ہے تو تمہارا پروردگار بھی خوب جانتا ہے کہ کون سب سے زیادہ سیدھے راستہ پر ہے'),
(2114, 17, 85, 'اور پیغمبر یہ آپ سے روح کے بارے میں دریافت کرتے ہیں تو کہہ دیجئے کہ یہ میرے پروردگار کا ایک امر ہے اور تمہیں بہت تھوڑا سا علم دیا گیا ہے'),
(2115, 17, 86, 'اور اگر ہم چاہیں تو جو کچھ آپ کو وحی کے ذریعہ دیا گیا ہے اسے اُٹھالیں اور اس کے بعد ہمارے مقابلہ میں کوئی سازگار اور ذمہ دار نہ ملے'),
(2116, 17, 87, 'مگر یہ کہ آپ کے پروردگار کی مہربانی ہوجائے کہ اس کا فضل آپ پر بہت بڑا ہے'),
(2117, 17, 88, 'آپ کہہ دیجئے کہ اگر انسان اور جنات سب اس بات پر متفق ہوجائیں کہ اس قرآن کا مثل لے آئیں تو بھی نہیں لاسکتے چاہے سب ایک دوسرے کے مددگار اور پشت پناہ ہی کیوں نہ ہوجائیں'),
(2118, 17, 89, 'اور ہم نے اس قرآن میں ساری مثالیں اُلٹ پلٹ کر بیان کردی ہیں لیکن اس کے بعد پھر بھی اکثر لوگوں نے کفر کے علاوہ ہر بات سے انکار کردیا ہے'),
(2119, 17, 90, 'اور ان لوگوں نے کہنا شروع کردیا کہ ہم تم پر ایمان نہ لائیں گے جب تک ہمارے لئے زمین سے چشمہ نہ جاری کردو'),
(2120, 17, 91, 'یا تمہارے پاس کھجور اور انگور کے باغ ہوں جن کے درمیان تم نہریں جاری کردو'),
(2121, 17, 92, 'یا ہمارے اوپر اپنے خیال کے مطابق آسمان کو ٹکڑے ٹکڑے کرکے گرادو یا اللہ اور ملائکہ کو ہمارے سامنے لاکر کھڑا کردو'),
(2122, 17, 93, 'یا تمہارے پاس سونے کا کوئی مکان ہو یا تم آسمان کی بلندی پر چڑھ جاؤ اور اس بلندی پر بھی ہم ایمان نہ لائیں گے جب تک کوئی ایسی کتاب نازل نہ کردو جسے ہم پڑھ لیں آپ کہہ دیجئے کہ ہمارا پروردگار بڑا بے نیاز ہے اور میں صرف ایک بشر ہوں جسے رسول بناکر بھیجا گیا ہے'),
(2123, 17, 94, 'اور ہدایت کے آجانے کے بعد لوگوں کے لئے ایمان لانے سے کوئی شے مانع نہیں ہوئی مگر یہ کہ کہنے لگے کہ کیا خدا نے کسی بشر کو رسول بناکر بھیج دیا ہے'),
(2124, 17, 95, 'تو آپ کہہ دیجئے کہ اگر زمین میں ملائکہ اطمینان سے ٹہلتے ہوتے تو ہم آسمان سے ملک ہی کو رسول بناکر بھیجتے'),
(2125, 17, 96, 'کہہ دیجئے کہ ہمارے اور تمہارے درمیان گواہ بننے کے لئے خدا کافی ہے کہ وہی اپنے بندوں کے حالات سے باخبر ہے اور ان کی کیفیات کا دیکھنے والا ہے'),
(2126, 17, 97, 'اور جس کو خدا ہدایت د ے دے وہی ہدایت یافتہ ہے اور جس کو گمراہی میں چھوڑ دے اس کے لئے اس کے علاوہ کوئی مددگار نہ پاؤ گے اور ہم انہیں روز قیامت منہ کے بل گونگے اندھے بہرے محشور کریں گے اور ان کا ٹھکانا جہنم ّہوگا کہ جس کی آگ بجھنے بھی لگے گی تو ہم شعلوں کو مزید بھڑکا دیں گے'),
(2127, 17, 98, 'یہ اس بات کی سزا ہے کہ انہوں نے ہماری نشانیوں کا انکار کیا ہے اور یہ کہا ہے کہ ہم ہڈیاں اور مٹی کا ڈھیر بن جائیں گے تو کیا دوبارہ ازسر هنو پھر پیدا کئے جائیں گے'),
(2128, 17, 99, 'کیا ان لوگوں نے یہ نہیں دیکھا ہے کہ جس نے آسمان و زمین کو پیدا کیا ہے وہ اس کا جیسا دوسرا بھی پیدا کرنے پر قادر ہے اور اس نے ان کے لئے ایک مدّت مقرر کردی ہے جس میں کسی شک کی گنجائش نہیں ہے مگر ظالموں نے کفر کے علاوہ ہر چیز سے انکار کردیا ہے'),
(2129, 17, 100, 'آپ کہہ دیجئے کہ اگر تم لوگ میرے پروردگار کے خزانوں کے مالک ہوتے تو خرچ ہوجانے کے خوف سے سب روک لیتے اور انسان تو تنگ دل ہی واقع ہوا ہے'),
(2130, 17, 101, 'اور ہم نے موسٰی کو نوکِھلی ہوئی نشانیاں دی تھیں تو بنی اسرائیل سے پوچھو کہ جب موسٰی ان کے پاس آئے تو فرعون نے ان سے کہہ دیا کہ میں تو تم کو سحر زدہ خیال کررہا ہوں'),
(2131, 17, 102, 'موسٰی نے کہا کہ تمہیں معلوم ہے کہ سب معجزات آسمان و زمین کے مالک نے بصیرت کا سامان بناکر نازل کئے ہیں اور اے فرعون میں خیال کررہا ہوں کہ تیری شامت آگئی ہے'),
(2132, 17, 103, 'فرعون نے چاہا کہ ان لوگوں کو اس سرزمین سے نکال باہر کردے لیکن ہم نے اس کو اس کے ساتھیوں سمیت دریا میں غرق کردیا'),
(2133, 17, 104, 'اور اس کے بعد بنی اسرائیل سے کہہ دیا کہ اب زمین میں آباد ہوجاؤ پھر جب آخرت کے وعدہ کا وقت آجائے گا تو ہم تم سب کو سمیٹ کر لے آئیں گے'),
(2134, 17, 105, 'اور ہم نے اس قرآن کو حق کے ساتھ نازل کیاہے اور یہ حق ہی کے ساتھ نازل ہوا ہے اور ہم نے آپ کو صرف بشارت دینے والا اورڈرانے والا بناکر بھیجا ہے'),
(2135, 17, 106, 'اور ہم نے قرآن کو متفرق بناکر نازل کیا ہے تاکہ تم تھوڑا تھوڑا لوگوں کے سامنے پڑھو اور ہم نے خود اسے تدریجا نازل کیا ہے'),
(2136, 17, 107, 'آپ کہہ دیجئے کہ تم ایمان لاؤ یا نہ لاؤ جن کو اس کے پہلے علم دے دیا گیا ہے ان پرتلاوت ہوتی ہے تو منہ کے بل سجدہ میں گر پڑتے ہیں'),
(2137, 17, 108, 'اور کہتے ہیں کہ ہمارا رب پاک و پاکیزہ ہے اور اس کا وعدہ یقینا پورا ہونے والا ہے'),
(2138, 17, 109, 'اور وہ منہ کے بل گر پڑتے ہیں روتے ہیں اوروہ قرآن ان کے خشوع میں اضافہ کردیتا ہے'),
(2139, 17, 110, 'آپ کہہ دیجئے کہ اللہ کہہ کر پکارو یا رحمٰن کہہ کر پکارو جس طرح بھی پکارو گے اس کے تمام نام بہترین ہیں اور اپنی نمازوں کو نہ چلاکر پڑھو اورنہ بہت آہستہ آہستہ بلکہ دونوں کا درمیانی راستہ نکالو'),
(2140, 17, 111, 'اور کہو کہ ساری حمد اس اللہ کے لئے ہے جس نے نہ کسی کو فرزند بنایا ہے اورنہ کوئی اس کے ملک میں شریک ہے اور نہ کوئی اس کی کمزوری کی بنا پر اس کاسرپرست ہے اورپھر باقاعدہ اس کی بزرگی کا اعلان کرتے رہو');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'ساری حمد اس خدا کے لئے ہے جس نے اپنے بندے پر کتاب نازل کی ہے اور اس میں کسی طرح کی کجی نہیں رکھی ہے'),
(2142, 18, 2, 'اسے بالکل ٹھیک رکھا ہے تاکہ اس کی طرف سے آنے والے سخت عذاب سے ڈرائے اورجو مومنین نیک اعمال کرتے ہیں انہیں بشارت دے دوکہ ان کے لئے بہترین اجر ہے'),
(2143, 18, 3, 'وہ اس میں ہمیشہ رہنے والے ہیں'),
(2144, 18, 4, 'اورپھر ان لوگوں کو عذاب الٰہی سے ڈرایئے جو یہ کہتے ہیں کہ اللہ نے کسی کو اپنا فرزند بنایا ہے'),
(2145, 18, 5, 'اس سلسلے میں نہ انہیں کوئی علم ہے اور نہ ان کے باپ دادا کو-یہ بہت بڑی بات ہے جو ان کے منہ سے نکل رہی ہے کہ یہ جھوٹ کے علاوہ کوئی بات ہی نہیں کرتے'),
(2146, 18, 6, 'تو کیا آپ شدّت هافسوس سے ان کے پیچھے اپنی جان خطرہ میں ڈال دیں گے اگر یہ لوگ اس بات پرایمان نہ لائے'),
(2147, 18, 7, 'بیشک ہم نے روئے زمین کی ہر چیز کو زمین کی زینت قرار دے دیا ہے تاکہ ان لوگوں کا امتحان لیں کہ ان میں عمل کے اعتبار سے سب سے بہتر کون ہے'),
(2148, 18, 8, 'اور ہم آخرکار روئے زمین کی ہر چیز کو چٹیل میدان بنادینے والے ہیں'),
(2149, 18, 9, 'کیا تمہارا خیال یہ ہے کہ کہف و رقیم والے ہماری نشانیوں میں سے کوئی تعجب خیز نشانی تھے'),
(2150, 18, 10, 'جبکہ کچھ جوانوں نے غار میں پناہ لی اور یہ دعا کی کہ پروردگار ہم کو اپنی رحمت عطا فرما اور ہمارے لئے ہمارے کام میں کامیابی کا سامان فراہم کردے'),
(2151, 18, 11, 'تو ہم نے غار میں ان کے کانوں پر چند برسوں کے لئے پردے ڈال دیئے'),
(2152, 18, 12, 'پھر ہم نے انہیں دوبارہ اٹھایا تاکہ یہ دیکھیں کہ دونوں گروہوں میں اپنے ٹھہرنے کی مدّت کسے زیادہ معلوم ہے'),
(2153, 18, 13, 'ہم آپ کو ان کے واقعات بالکل سچے سچے بتارہے ہیں-یہ چند جوان تھے جو اپنے پروردگار پر ایمان لائے تھے اور ہم نے ان کی ہدایت میں اضافہ کردیا تھا'),
(2154, 18, 14, 'اوران کے دلوں کو مطمئن کردیا تھا اس وقت جب یہ سب یہ کہہ کر اٹھے کہ ہمارا پروردگار آسمانوں اور زمین کا مالک ہے ہم اس کے علاوہ کسی خدا کو نہ پکاریں گے کہ اس طرح ہم بے عقلی کی بات کے قائل ہوجائیں گے'),
(2155, 18, 15, 'یہ ہماری قوم ہے جس نے خدا کو چھوڑ کر دوسرے خدا اختیار کرلئے ہیں آخر یہ لوگ ان خداؤں کے لئے کوئی واضح دلیل کیوں نہیں لاتے ...._ پھر اس سے زیادہ ظالم کون ہے جو پروردگار پر افترا کرے اور اس کے خلاف الزام لگائے'),
(2156, 18, 16, 'اور جب تم نے ان سے اور خدا کے علاوہ ان کے تمام معبودوں سے علیحدگی اختیار کرلی ہے تو اب غارمیں پناہ لے لو تمہارا پروردگار تمہارے لئے اپنی رحمت کا دامن پھیلادے گا اور اپنے حکم سے آسانیوں کا سامان فراہم کردے گا'),
(2157, 18, 17, 'اور تم دیکھو گے کہ آفتاب جب طلوع کرتا ہے تو ان کے غارسے داہنی طرف کترا کر نکل جاتا ہے اور جب ڈوبتا ہے توبائیں طرف جھک کر نکل جاتا ہے اور وہ وسیع مقام پر آرام کررہے ہیں-یہ اللہ کی نشانیوں میں سے ایک نشانی ہے جس کو خدا ہدایت دے دے وہی ہدایت هیافتہ ہے اور جس کو وہ گمراہی میں چھوڑ دے اس کے لئے کوئی راہنما اور سرپرست نہ پاؤ گے'),
(2158, 18, 18, 'اور تمہارا خیال ہے کہ وہ جاگ رہے ہیں حالانکہ وہ عالهِ خواب میں ہیں اورہم انہیں داہنے بائیں کروٹ بھی بدلوارہے ہیں اور ان کا کتا ّڈیوڑھی پر دونوں ہاتھ پھیلائے ڈٹا ہوا ہے اگر تم ان کی کیفیت پر مطلع ہوجاتے تو اُلٹے پاؤں بھاگ نکلتے اورتمہارے دل میں دہشت سما جاتی'),
(2159, 18, 19, 'اور اس طرح ہم نے انہیں دوبارہ زندہ کیا تاکہ آپس میں ایک دوسرے سے سوال کریں تو ایک نے کہا کہ تم نے کتنی مدت توقف کیا ہے تو سب نے کہا کہ ایک دن یا اس کا ایک حصہّ ان لوگوں نے کہا کہ تمہارا پروردگار اس مدّت سے بہتر باخبر ہے اب تم اپنے سکے دے کر کسی کو شہر کی طرف بھیجو وہ دیکھے کہ کون سا کھانا بہتر ہے اور پھر تمہارے لئے رزق کا سامان فراہم کرے اور وہ آہستہ جائے اورکسی کو تمہارے بارے میں خبر نہ ہونے پائے'),
(2160, 18, 20, 'یہ اگرتمہارے بارے میں باخبر ہوگئے تو تمہیں سنگسار کردیں گے یا تمہیں بھی اپنے مذہب کی طرف پلٹا لیں گے اور اس طرح تم کبھی نجات نہ پاسکو گے'),
(2161, 18, 21, 'اور اس طرح ہم نے قوم کو ان کے حالات پر مطلع کردیا تاکہ انہیں معلوم ہوجائے کہ اللہ کا وعدہ سچاّ ہے اور قیامت میں کسی طرح کا شبہ نہیں ہے جب یہ لوگ آپس میں ان کے بارے میں جھگڑا کررہے تھے اور یہ طے کررہے تھے کہ ان کے غار پر ایک عمارت بنادی جائے-خدا ان کے بارے میں بہتر جانتا ہے اور جو لوگ دوسروں کی رائے پر غالب آئے انہوں نے کہا کہ ہم ان پر مسجد بنائیں گے'),
(2162, 18, 22, 'عنقریب یہ لوگ کہیں گے کہ وہ تین تھے اور چوتھا ان کا کتاّ تھا اور بعض کہیں گے کہ پانچ تھے اور چھٹا ان کا کتا تھا اور یہ سب صرف غیبی اندازے ہوں گے اور بعض تو یہ بھی کہیں گے کہ وہ سات تھے اور آٹھواں ان کا کتا تھا-آپ کہہ دیجئے کہ خدا ان کی تعداد کو بہتر جانتا ہے اورچند افراد کے علاوہ کوئی نہیں جانتا ہے لہذا آپ ان سے ظاہری گفتگو کے علاوہ واقعا کوئی بحث نہ کریں اور ان کے بارے میں کسی سے دریافت بھی نہ کریں'),
(2163, 18, 23, 'اور کسی شے کے لئے یہ نہ کہیں کہ میں یہ کام کل کرنے والا ہوں'),
(2164, 18, 24, 'مگر جب تک خدا نہ چاہے اور بھول جائیں تو خدا کو یاد کریں اور یہ کہیں کہ عنقریب میرا خدا مجھے واقعیت سے قریب تر امر کی ہدایت کردے گا'),
(2165, 18, 25, 'اور یہ لوگ اپنے غار میں تین سو برس رہے اور اس پر نو دن کا اضافہ بھی ہوگیا'),
(2166, 18, 26, 'آپ کہہ دیجئے کہ اللہ ان کی مدّاُ قیام سے زیادہ باخبر ہے اسی کے لئے آسمان و زمین کا سارا غیب ہے اور اس کی سماعت وبصارت کا کیا کہنا ان لوگوں کے لئے اس کے علاوہ کوئی سرپرست نہیں ہے اورنہ وہ کسی کو اپنے حکم میں شریک کرتا ہے'),
(2167, 18, 27, 'اور جو کچھ کتاب پروردگار سے وحی کے ذریعہ آپ تک پہنچایا گیا ہے آپ اسی کی تلاوت کریں کہ کوئی اس کے کلمات کو بدلنے والا نہیں ہے اور اس کو چھوڑ کر کوئی دوسرا ٹھکانا بھی نہیں ہے'),
(2168, 18, 28, 'اور اپنے نفس کو ان لوگوں کے ساتھ صبر پر آمادہ کرو جو صبح و شام اپنے پروردگار کو پکارتے ہیں اور اسی کی مرضی کے طلب گار ہیں اور خبردار تمہاری نگاہیں ان کی طرف سے پھر نہ جائیں کہ زندگانی دنیا کی زینت کے طلب گار بن جاؤ اور ہرگز اس کی اطاعت نہ کرنا جس کے قلب کو ہم نے اپنی یاد سے محروم کردیا ہے اور وہ اپنی خواہشات کا پیروکار ہے اور اس کا کام سراسر زیادتی کرنا ہے'),
(2169, 18, 29, 'اور کہہ دو کہ حق تمہارے پروردگار کی طرف سے ہے اب جس کا جی چاہے ایمان لے آئے اورجس کا جی چاہے کافر ہوجائے ہم نے یقینا کافرین کے لئے اس آگ کا انتظام کردیا ہے جس کے پردے چاروں طرف سے گھیرے ہوں گے اور وہ فریاد بھی کریں گے تو پگھلے ہوئے تانبے کی طرح کے کھولتے ہوئے پانی سے ان کی فریاد رسی کی جائے گی جو چہروں کو بھون ڈالے گا یہ بدترین مشروب ہے اور جہنم ّبدترین ٹھکانا ہے'),
(2170, 18, 30, 'یقینا جو لوگ ایمان لے آئے اور انہوں نے نیک اعمال کئے ہم ان لوگوں کے اجر کو ضائع نہیں کرتے ہیں جو اچھے اعمال انجام دیتے ہیں'),
(2171, 18, 31, 'ان کے لئے وہ دائمی جنتیں ہیں جن کے نیچے نہریں جاری ہوں گی انہیں سونے کے کنگنپہنائے جائیں گے اور یہ باریک اور دبیز ریشم کے سبز لباس میں ملبوس اور تختوں پر تکیے لگائے بیٹھے ہوں گے یہی ان کے لئے بہترین ثواب اورحسین ترین منزل ہے'),
(2172, 18, 32, 'اور ان کفار کے لئے ان دو انسانوں کی مثال بیان کردیجئے جن میں سے ایک کے لئے ہم نے انگور کے دو باغ قرار دیئے اور انہیں کھجوروں سے گھیر دیا اور ان کے درمیان زراعت بھی قرار دے دی'),
(2173, 18, 33, 'پھر دونوں باغات نے خوب پھل دیئے اور کسی طرح کی کمی نہیں کی اور ہم نے ان کے درمیان نہر بھی جاری کردی'),
(2174, 18, 34, 'اور اس کے پاس پھل بھی تھے تو اس نے اپنے غریب ساتھی سے بات کرتے ہوئے کہا کہ میں تم سے مال کے اعتبار سے بڑھا ہوا ہوں اور افراد کے اعتبار سے بھی زیادہ باعزت ہوں'),
(2175, 18, 35, 'وہ اسی عالم میں اپنے نفس پر ظلم کررہا تھا اپنے باغ میں داخل ہوا اور کہنے لگا کہ میں تو خیال بھی کرتا ہوں کہ یہ کبھی تباہ بھی نہیں ہوسکتا ہے'),
(2176, 18, 36, 'اور میرا گمان بھی نہیں ہے کہ کبھی قیامت قائم ہوگی اورپھر اگر میں پروردگار کی بارگاہ میں واپس بھی گیا تو اس سے بہتر منزل حاصل کرلوں گا'),
(2177, 18, 37, 'اس کے ساتھی نے گفتگو کرتے ہوئے کہا کہ تو نے اس کا انکار کیا ہے جس نے تجھے خاک سے پیدا کیا ہے پھر نطفہ سے گزارا ہے اور پھر ایک باقاعدہ انسان بنادیا ہے'),
(2178, 18, 38, 'لیکن میرا ایمان یہ ہے کہ اللہ میرا رب ہے اور میں کسی کو اس کا شریک نہیں بناسکتا ہوں'),
(2179, 18, 39, 'اور ایسا کیوں نہ ہوا کہ جب تو اپنے باغ میں داخل ہوتا تو کہتا ماشائ اللہ اس کے علاوہ کسی کے پاس کوئی قوت نہیں ہے اگر تو یہ دیکھ رہا ہے کہ میں مال اور اولاد کے اعتبار سے تجھ سے کم تر ہوں'),
(2180, 18, 40, 'تو امیدوار ہوں کہ میرا پروردگار مجھے بھی تیرے باغات سے بہتر باغات عنایت کردے اور ان باغات پر آسمان سے ایسی آفت نازل کردے جو سب کو خاک کردے اور چٹیل میدان بنادے'),
(2181, 18, 41, 'یا ان باغات کا پانی خشک ہوجائے اور تو اس کے طلب کرنے پر بھی قادر نہ ہو'),
(2182, 18, 42, 'اور پھر اس کے باغ کے پھل آفت میں گھیر دیئے گئے تو وہ ان اخراجات پر ہاتھ ملنے لگا جو اس نے باغ کی تیاری پر صرف کئے تھے جب کہ باغ اپنی شاخوں کے بل اُلٹا پڑا ہوا تھا اور وہ کہہ رہا تھا کہ اے کاش میں کسی کو اپنے پروردگار کا شریک نہ بناتا'),
(2183, 18, 43, 'اور اب اس کے پاس وہ گروہ بھی نہیں تھا جو خدا کے مقابلہ میں اس کی مدد کرتا اور وہ بدلہ بھی نہیں لے سکتا تھا'),
(2184, 18, 44, 'اس وقت ثابت ہوا کہ قیامت کی نصرت صرف خدائے برحق کے لئے ہے وہی بہترین ثواب دینے والا ہے اور وہی انجام بخیر کرنے والا ہے'),
(2185, 18, 45, 'اور انہیں زندگانی دنیا کی مثال اس پانی کی بتائیے جسے ہم نے آسمان سے نازل کیا تو زمین کی روئیدگی اس سے مل جل گئی پھر آخر میں وہ ریزہ ریزہ ہوگئی جسے ہوائیں اڑادیتی ہیں اور اللہ ہر شے پر قدرت رکھنے والا ہے'),
(2186, 18, 46, 'مال اور اولاد زندگانی دنیا کی زینت ہیں اور باقی رہ جانے والی نیکیاں پروردگار کے نزدیک ثواب اور امید دونوں کے اعتبار سے بہتر ہیں'),
(2187, 18, 47, 'اور قیامت کا دن وہ ہوگا جب ہم پہاڑوں کو حرکت میں لائیں گے اور تم زمین کو بالکل کھلا ہوا دیکھو گے اور ہم سب کو اس طرح جمع کریں گے کہ کسی ایک کو بھی نہیں چھوڑیں گے'),
(2188, 18, 48, 'اور سب تمہارے پروردگارکے سامنے صف بستہ پیش کئے جائیں گے اورارشاد ہوگا کہ تم آج اسی طرح آئے ہو جس طرح ہم نے پہلی مرتبہ تمہیں پیدا کیا تھا لیکن تمہارا خیال تھا کہ ہم تمہارے لئے کوئی وعدہ گاہ نہیں قرار دیں گے'),
(2189, 18, 49, 'اور جب نامئہ اعمال سامنے رکھا جائے گا تو دیکھو گے کہ مجرمین اس کے مندرجات کو دیکھ کر خوفزدہ ہوں گے اور کہیں گے کہ ہائے افسوس اس کتاب نے تو چھوٹا بڑا کچھ نہیں چھوڑا ہے اور سب کو جمع کرلیا ہے اور سب اپنے اعمال کو بالکل حاضر پائیں گے اور تمہارا پروردگار کسی ایک پر بھی ظلم نہیں کرتا ہے'),
(2190, 18, 50, 'اور جب ہم نے ملائکہ سے کہا کہ آدم کو سجدہ کرو تو ابلیس کے علاوہ سب نے سجدہ کرلیا کہ وہ جناّت میں سے تھا پھر تو اس نے حکم خدا سے سرتابی کی تو کیا تم لوگ مجھے چھوڑ کر شیطان اور اس کی اولاد کو اپنا سرپرست بنا رہے ہو جب کہ وہ سب تمہارے دشمن ہیں یہ تو ظالمین کے لئے بدترین بدل ہے'),
(2191, 18, 51, 'ہم نے ان شیاطین کو نہ زمین و آسمان کی خلقت کا گواہ بنایا ہے اور نہ خود ان ہی کی خلقت کا اور نہ ہم ظالمین کو اپنا قوت بازو اور مددگار بناسکتے ہیں'),
(2192, 18, 52, 'اور اس دن خدا کہے گا کہ میرے ان شریکوں کو بلاؤ جن کی شرکت کا تمہیں خیال تھا اور وہ پکاریں گے لیکن وہ لوگ جواب بھی نہیں دیں گے اور ہم نے تو ان کے درمیان ہلاکت کی منزل قرار دے دی ہے'),
(2193, 18, 53, 'اور مجرمین جب جہنمّ کی آگ کو دیکھیں گے تو انہیں یہ خیال پیدا ہوگا کہ وہ اس میں جھونکے جانے والے ہیں اور اس وقت اس آگ سے بچنے کی کوئی راہ نہ پاسکیں گے'),
(2194, 18, 54, 'اورہم نے اس قرآن میں لوگوں کے لئے ساری مثالیں اُلٹ پلٹ کر بیان کردی ہیں اور انسان تو سب سے زیادہ جھگڑا کرنے والاہے'),
(2195, 18, 55, 'اور لوگوں کے لئے ہدایت کے آجانے کے بعد کون سی شے مانع ہوگئی ہے کہ یہ ایمان نہ لائیں اوراپنے پروردگار سے استغفار نہ کریں مگر یہ کہ ان تک بھی اگلے لوگوں کا طریقہ آجائے یا ان کے سامنے سے بھی عذاب آجائے'),
(2196, 18, 56, 'اور ہم تو رسولوں کو صرف بشارت دینے والا اور عذاب سے ڈرانے والا بناکر بھیجتے ہیں اور کفاّر باطل کے ذریعہ جھگڑا کرتے ہیں کہ اس کے ذریعہ کو برباد کردیں اور انہوں نے ہماری نشانیوں کو اور جس بات سے ڈرائے گئے تھے سب کو ایک مذاق بنالیا ہے'),
(2197, 18, 57, 'اور اس سے بڑا ظالم کون ہوگا جسے آیات هالٰہٰیہ کی یاد دلائی جائے اور پھر اس سے اعراض کرے اور اپنے سابقہ اعمال کو بھول جائے ہم نے ان کے دلوں پر پردے ڈال دیئے ہیں کہ یہ حق کو سمجھ سکیں اور ان کے کانوں میں بہراپن ہے اور اگر آپ انہیں ہدایت کی طرف بلائیں گے بھی تو یہ ہرگز ہدایت حاصل نہ کریں گے'),
(2198, 18, 58, 'آپ کا پروردگار بڑا بخشنے والا اور صاحبِ رحمت ہے وہ اگر ان کے اعمال کا مواخذہ کرلیتا تو فورا ہی عذاب نازل کردیتا لیکن اس نے ان کے لئے ایک وقت مقرر کردیا ہے جس وقت اس کے علاوہ یہ کوئی پناہ نہ پائیں گے'),
(2199, 18, 59, 'اور یہ وہ بستیاں ہیں جنہیں ہم نے ان کے ظلم کی بنا پر ہلاک کردیا ہے اور ان کی ہلاکت کا ایک وقت مقرر کردیا تھا'),
(2200, 18, 60, 'اور اس وقت کو یاد کرو جب موسٰی نے اپنے جوان سے کہا کہ میں چلنے سے باز نہ آؤں گا یہاں تک کہ دو دریاؤں کے ملنے کی جگہ پر پہنچ جاؤں یا یوں ہی برسوں چلتا رہوں'),
(2201, 18, 61, 'پھر جب دونوں مجمع البحرین تک پہنچ گئے تو اپنی مچھلی چھوڑ گئے اور اس نے سمندر میں سرنگ بناکر اپنا راستہ نکال لیا'),
(2202, 18, 62, 'پھر جب دونوں مجمع البحرین سے آگے بڑھ گئے تو موسٰی نے اپنے جوان صالح سے کہا کہ اب ہمارا کھانا لاؤ کہ ہم نے اس سفر میں بہت تکان برداشت کی ہے'),
(2203, 18, 63, 'اس جوان نے کہا کہ کیا آپ نے یہ دیکھا ہے کہ جب ہم پتھر کے پاس ٹھہرے تھے تو میں نے مچھلی وہیں چھوڑ دی تھی اور شیطان نے اس کے ذکر کرنے سے بھی غافل کردیا تھا اور اس نے دریا میں عجیب طرح سے راستہ بنالیا تھا'),
(2204, 18, 64, 'موسٰی نے کہا کہ پس وہی جگہ ہے جسے ہم تلاش کررہے تھے پھر دونوں نشان قدم دیکھتے ہوئے الٹے پاؤں واپس ہوئے'),
(2205, 18, 65, 'تو اس جگہ پر ہمارے بندوں میں سے ایک ایسے بندے کو پایا جسے ہم نے اپنی طرف سے رحمت عطا کی تھی اور اپنے علم خاص میں سے ایک خاص علم کی تعلیم دی تھی'),
(2206, 18, 66, 'موسٰی نے اس بندے سے کہا کہ کیا میں آپ کے ساتھ رہ سکتا ہوں کہ آپ مجھے اس علم میں سے کچھ تعلیم کریں جو رہنمائی کا علم آپ کو عطا ہوا ہے'),
(2207, 18, 67, 'اس بندہ نے کہا کہ آپ میرے ساتھ صبر نہ کرسکیں گے'),
(2208, 18, 68, 'اور اس بات پر کیسے صبر کریں گے جس کی آپ کو اطلاع نہیں ہے'),
(2209, 18, 69, 'موسٰی نے کہا کہ آپ انشائ اللہ مجھے صابر پائیں گے اور میں آپ کے کسی حکم کی مخالفت نہ کروں گا'),
(2210, 18, 70, 'اس بندہ نے کہا کہ اگر آپ کو میرے ساتھ رہنا ہے تو بس کسی بات کے بارے میں اس وقت تک سوال نہ کریں جب تک میں خود اس کا ذکر نہ شروع کردوں'),
(2211, 18, 71, 'پس دونوں چلے یہاں تک کہ جب کشتی میں سوار ہوئے تو اس بندہ خدا نے اس میں سوراخ کردیا موسٰی نے کہا کہ کیا آپ نے اس لئے سوراخ کیا ہے کہ سواریوں کو ڈبو دیں یہ تو بڑی عجیب و غریب بات ہے'),
(2212, 18, 72, 'اس بندہ خدا نے کہا کہ میں نے نہ کہا تھا کہ آپ میرے ساتھ صبر نہ کرسکیں گے'),
(2213, 18, 73, 'موسٰی نے کہا کہ خیر جو فروگزاشت ہوگئی اس کا مواخذہ نہ کریں اور معاملات میں اتنی سختی سے کام نہ لیں'),
(2214, 18, 74, 'پھر دونوں آگے بڑھے یہاں تک کہ ایک نوجوان نظر آیا اور اس بندہ خدا نے اسے قتل کردیا موسٰی نے کہا کہ کیا آپ نے ایک پاکیزہ نفس کو بغیر کسی نفس کے قتل کردیا ہے یہ تو بڑی عجیب سی بات ہے'),
(2215, 18, 75, 'بندہ صالح نے کہا کہ میں نے کہا تھا کہ آپ میرے ساتھ صبر نہیں کرسکتے ہیں'),
(2216, 18, 76, 'موسٰی نے کہا کہ اس کے بعد میں کسی بات کا سوال کروں تو آپ مجھے اپنے ساتھ نہ رکھیں کہ آپ میری طرف سے منزل عذر تک پہنچ چکے ہیں'),
(2217, 18, 77, 'پھر دونوں آگے چلتے رہے یہاں تک کہ ایک قریہ والوں تک پہنچے اور ان سے کھانا طلب کیا ان لوگوں نے مہمان بنانے سے انکار کردیا پھر دونوں نے ایک دیوار دیکھی جو قریب تھا کہ گر پڑتی - بندہ صالح نے اسے سیدھا کردیا تو موسٰی نے کہا کہ آپ چاہتے تو اس کی اجرت لے سکتے تھے'),
(2218, 18, 78, 'بندہ صالح نے کہا کہ یہ میرے اور تمہارے درمیان جدائی کا موقع ہے عنقریب میں تمہیں ان تمام باتوں کی تاویل بتادوں گا جن پر تم صبر نہیں کرسکے'),
(2219, 18, 79, 'یہ کشتی چند مساکین کی تھی جو سمندر میں باربرداری کا کام کرتے تھے میں نے چاہا کہ اسے عیب دار بنادوں کہ ان کے پیچھے ایک بادشاہ تھا جو ہر کشتی کو غصب کرلیا کرتا تھا'),
(2220, 18, 80, 'اور یہ بچّہ .... اس کے ماں باپ مومن تھے اور مجھے خوف معلوم ہوا کہ یہ بڑا ہوکر اپنی سرکشی اور کفر کی بنا پر ان پر سختیاں کرے گا'),
(2221, 18, 81, 'تو میں نے چاہا کہ ان کا پروردگار انہیں اس کے بدلے ایسا فرزند دیدے جو پاکیزگی میں اس سے بہترہو اور صلئہ رحم میں بھی'),
(2222, 18, 82, 'اور یہ دیوار شہر کے دو یتیم بچوں کی تھی اور اس کے نیچے ان کا خزانہ دفن تھا اور ان کا باپ ایک نیک بندہ تھا تو آپ کے پروردگار نے چاہا کہ یہ دونوں طاقت و توانائی کی عمر تک پہنچ جائیں اور اپنے خزانے کو نکال لیں- یہ سب آپ کے پروردگار کی رحمت ہے اور میں نے اپنی طرف سے کچھ نہیں کیا ہے اور یہ ان باتوں کی تاویل ہے جن پر آپ صبر نہیں کرسکے ہیں'),
(2223, 18, 83, 'اور اے پیغمبر علیھ السّلامیہ لوگ آپ سے ذوالقرنین کے بارے میں سوال کرتے ہیں تو آپ کہہ دیجئے کہ میں عنقریب تمہارے سامنے ان کا تذکرہ پڑھ کر صَنادوں گا'),
(2224, 18, 84, 'ہم نے ان کو زمین میں اقتدار دیا اور ہر شے کا سازوسامان عطا کردیا'),
(2225, 18, 85, 'پھر انہوں نے ان وسائل کو استعمال کیا'),
(2226, 18, 86, 'یہاں تک کہ جب وہ غروب آفتاب کی منزل تک پہنچے تو دیکھا کہ وہ ایک کالی کیچڑ والے چشمہ میں ڈوب رہا ہے اور اس چشمہ کے پاس ایک قوم کو پایا تو ہم نے کہا کہ تمہیں اختیار ہے چاہے ان پر عذاب کرو یا ان کے درمیان حسن سلوک کی روش اختیار کرو'),
(2227, 18, 87, 'ذوالقرنین نے کہا کہ جس نے ظلم کیا ہے اس پر بہرحال عذاب کروں گا یہاں تک کہ وہ اپنے رب کی بارگاہ میں پلٹایا جائے گا اور وہ اسے بدترین سزا دے گا'),
(2228, 18, 88, 'اور جس نے ایمان اور عمل صالح اختیار کیا ہے اس کے لئے بہترین جزا ہے اور میں بھی اس سے اپنے امور میں آسانی کے بارے میں کہوں گا'),
(2229, 18, 89, 'اس کے بعد انہوں نے دوسرے وسائل کا پیچھا کیا'),
(2230, 18, 90, 'یہاں تک کہ جب طلوع آفتاب کی منزل تک پہنچے تو دیکھا کہ وہ ایک ایسی قوم پر طلوع کررہا ہے جس کے لئے ہم نے آفتاب کے سامنے کوئی پردہ بھی نہیں رکھا تھا'),
(2231, 18, 91, 'یہ ہے ذوالقرنین کی داستان اور ہمیں اس کی مکمل اطلاع ہے'),
(2232, 18, 92, 'اس کے بعد انہوں نے پھر ایک ذریعہ کو استعمال کیا'),
(2233, 18, 93, 'یہاں تک کہ جب وہ دو پہاڑوں کے درمیان پہنچ گئے تو ان کے قریب ایک قوم کو پایا جو کوئی بات نہیں سمجھتی تھی'),
(2234, 18, 94, 'ان لوگوں نے کسی طرح کہا کہ اے ذوالقرنین یاجوج وماجوج زمین میں فساد برپا کررہے ہیں تو کیا یہ ممکن ہے کہ ہم آپ کے لئے اخراجات فراہم کردیں اور آپ ہمارے اور ان کی درمیان ایک رکاوٹ قرار دیدیں'),
(2235, 18, 95, 'انہوں نے کہا کہ جو طاقت مجھے میرے پروردگار نے دی ہے وہ تمہارے وسائل سے بہتر ہے اب تم لوگ قوت سے میری امداد کرو کہ میں تمہارے اور ان کے درمیان ایک روک بنادوں'),
(2236, 18, 96, 'چند لوہے کی سلیں لے آؤ- یہاں تک کہ جب دونوں پہاڑوں کے برابر ڈھیر ہوگیا تو کہا کہ آگ پھونکو یہاں تک کہ جب اسے بالکل آگ بنادیا تو کہا آؤ اب اس پر تانبا پگھلا کر ڈال دیں'),
(2237, 18, 97, 'جس کے بعد نہ وہ اس پر چڑھ سکیں اور نہ اس میں نقب لگا سکیں'),
(2238, 18, 98, 'ذوالقرنین نے کہا کہ یہ پروردگار کی ایک رحمت ہے اس کے بعد جب وعدہ الٰہی آجائے گا تو اس کو ریزہ ریزہ کردے گا کہ وعدہ رب بہرحال برحق ہے'),
(2239, 18, 99, 'اور ہم نے انہیں اس طرح چھوڑ دیا ہے کہ ایک دوسرے کے معاملات میں دخل اندازی کرتے رہیں اور پھر جب شُور پھونکا جائے گا تو ہم سب کو ایک جگہ اکٹھا کرلیں گے'),
(2240, 18, 100, 'اور اس د ن جہّنم کو کافرین کے سامنے باقاعدہ پیش کیا جائے گا'),
(2241, 18, 101, 'وہ کافر جن کی نگاہیں ہمارے ذکر کی طرف سے پردہ میں تھیں اور وہ کچھ صَننا بھی نہیں چاہتے تھے'),
(2242, 18, 102, 'تو کیا کافروں کا خیال یہ ہے کہ یہ ہمیں چھوڑ کر ہمارے بندوں کو اپنا سرپرست بنالیں گے تو ہم نے جہنمّ کو کافرین کے لئے بطور منزل مہیّا کردیا ہے'),
(2243, 18, 103, 'پیغمبر کیا ہم آپ کو ان لوگوں کے بارے میں اطلاع دیں جو اپنے اعمال میں بدترین خسارہ میں ہیں'),
(2244, 18, 104, 'یہ وہ لوگ ہیں جن کی کوشش زندگانی دنیا میں بہک گئی ہے اور یہ خیال کرتے ہیں کہ یہ اچھے اعمال انجام دے رہے ہیں'),
(2245, 18, 105, 'یہی وہ لوگ ہیں جنہوں نے آیات پروردگار اور اس کی ملاقات کا انکار کیا ہے تو ان کے اعمال برباد ہوگئے ہیں اور ہم قیامت کے دن ان کے لئے کوئی وزن قائم نہیں کریں گے'),
(2246, 18, 106, 'ان کی جزا ان کے کفر کی بنا پر جہنم ّہے کہ انہوں نے ہمارے رسولوں اور ہماری آیتوں کو مذاق بنا لیا ہے'),
(2247, 18, 107, 'یقینا جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ان کی منزل کے لئے جنّت الفردوس ہے'),
(2248, 18, 108, 'وہ ہمیشہ اس جنّت میں رہیں گے اور اس کی تبدیلی کی خواہش بھی نہ کریں گے'),
(2249, 18, 109, 'آپ کہہ دیجئے کہ اگر میرے پروردگار کے کلمااُ کے لئے سمندر بھی روشنی جائیں تو کلمات رب کے ختم ہونے سے پہلے ہی سارے سمندر ختم ہوجائیں گے چاہے ان کی مدد کے لئے ہم ویسے ہی سمندر اور بھی لے ائیں'),
(2250, 18, 110, 'آپ کہہ دیجئے کہ میں تمہارا ہی جیسا ایک بشر ہوں مگر میری طرف وحی آتی ہے کہ تمہارا خدا ایک اکیلا ہے لہذا جو بھی اس کی ملاقات کا امیدوار ہے اسے چاہئے کہ عمل صالح کرے اور کسی کو اپنے پروردگار کی عبادت میں شریک نہ بنائے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کۤھیعۤصۤ'),
(2252, 19, 2, 'یہ زکریا کے ساتھ تمہارے پروردگار کی مہربانی کا ذکر ہے'),
(2253, 19, 3, 'جب انہوں نے اپنے پروردگار کو دھیمی آواز سے پکارا'),
(2254, 19, 4, 'کہا کہ پروردگار میری ہڈیاں کمزور ہوگئی ہیں اور میرا سر بڑھاپے کی آگ سے بھڑک اٹھا ہے اور میں تجھے پکارنے سے کبھی محروم نہیں رہا ہوں'),
(2255, 19, 5, 'اور مجھے اپنے بعد اپنے خاندان والوں سے خطرہ ہے اور میری بیوی بانجھ ہے تو اب مجھے ایک ایسا ولی اور وارث عطا فرمادے'),
(2256, 19, 6, 'جو میرا اور آل یعقوب کا وارث ہو اور پروردگار اسے اپنا پسندیدہ بھی قرار دے دے'),
(2257, 19, 7, 'زکریا ہم تم کو ایک فرزند کی بشارت دیتے ہیں جس کا نام یحیٰی ہے اور ہم نے اس سے پہلے ان کا ہمنام کوئی نہیں بنایا ہے'),
(2258, 19, 8, 'زکریا نے عرض کی پروردگار میرے فرزند کس طرح ہوگا جب کہ میری بیوی بانجھ ہے اور میں بھی بڑھاپے کی آخری حد کو پہنچ گیا ہوں'),
(2259, 19, 9, 'ارشاد ہوا اسی طرح تمہارے پروردگار کا فرمان ہے کہ یہ بات میرے لئے بہت آسان ہے اور میں نے اس سے پہلے خود تمہیں بھی پیدا کیا ہے جب کہ تم کچھ نہیں تھے'),
(2260, 19, 10, 'انہوں نے کہا کہ پروردگار اس ولادت کی کوئی علامت قرار دے دے ارشاد ہوا کہ تمہاری نشانی یہ ہے کہ تم تین دنوں تک برابر لوگوں سے کلام نہیں کرو گے'),
(2261, 19, 11, 'اس کے بعد زکریا محرابِ عبادت سے قوم کی طرف نکلے اور انہیں اشارہ کیا کہ صبح و شام اپنے پروردگار کی تسبیح کرتے رہو'),
(2262, 19, 12, 'یحیٰی علیھ السّلام! کتاب کو مضبوطی سے پکڑ لو اور ہم نے انہیں بچپنے ہی میں نبوت عطا کردی'),
(2263, 19, 13, 'اور اپنی طرف سے مہربانی اور پاکیزگی بھی عطا کردی اور وہ خورِ خدا رکھنے والے تھے'),
(2264, 19, 14, 'اور اپنے ماں باپ کے حق میں نیک برتاؤ کرنے والے تھے اور سرکش اور نافرمان نہیں تھے'),
(2265, 19, 15, 'ان پر ہمار اسلام جس دن پیدا ہوئے اور جس دن انہیں موت آئی اور جس دن وہ دوبارہ زندہ اٹھائے جائیں گے'),
(2266, 19, 16, 'اور پیغمبر علیھ السّلام اپنی کتاب میں مریم کا ذکر کرو کہ جب وہ اپنے گھر والوں سے الگ مشرقی سمت کی طرف چلی گئیں'),
(2267, 19, 17, 'اور لوگوں کی طرف پردہ ڈال دیا تو ہم نے اپنی روح کو بھیجا جو ان کے سامنے ایک اچھا خاصا آدمی بن کر پیش ہوا'),
(2268, 19, 18, 'انہوں نے کہا کہ اگر تو خوف خدا رکھتا ہے تو میں تجھ سے خدا کی پناہ مانگتی ہوں'),
(2269, 19, 19, 'اس نے کہاکہ میں آپ کے رب کا فرستادہ ہوں کہ آپ کو ایک پاکیزہ فرزند عطا کردوں'),
(2270, 19, 20, 'انہوں نے کہا کہ میرے یہاں فرزند کس طرح ہوگا جب کہ مجھے کسی بشر نے چھوا بھی نہیں ہے اور میں کوئی بدکردار نہیں ہوں'),
(2271, 19, 21, 'اس نے کہا کہ اسی طرح آپ کے پروردگار کا ارشاد ہے کہ میرے لئے یہ کام آسان ہے اور اس لئے کہ میں اسے لوگوں کے لئے نشانی بنادوں اور اپنی طرف سے رحمت قرار دیدوں اور یہ بات طے شدہ ہے'),
(2272, 19, 22, 'پھر وہ حاملہ ہوگئیں اور لوگوں سے دور ایک جگہ چلی گئیں'),
(2273, 19, 23, 'پھر وضع حمل کا وقت انہیں ایک کھجور کی شاخ کے قریب لے آیا تو انہوں نے کہا کہ اے کاش میں اس سے پہلے ہی مر گئی ہوتی اور بالکل فراموش کردینے کے قابل ہوگئی ہوتی'),
(2274, 19, 24, 'تو اس نے نیچے سے آواز دی کہ آپ پریشان نہ ہوں خدا نے آپ کے قدموں میں چشمہ جاری کردیا ہے'),
(2275, 19, 25, 'اور خرمے کی شاخ کو اپنی طرف ہلائیں اس سے تازہ تازہ خرمے گر پڑیں گے'),
(2276, 19, 26, 'پھر اسے کھائیے اور پیجئے اور اپنی آنکھوں کو ٹھنڈی رکھئے پھر اس کے بعد کسی انسان کو دیکھئے تو کہہ دیجئے کہ میں نے رحمان کے لئے روزہ کی نذر کرلی ہے لہذا آج میں کسی انسان سے بات نہیں کرسکتی'),
(2277, 19, 27, 'اس کے بعد مریم بچہ کو اٹھائے ہوئے قوم کے پاس آئیں تو لوگوں نے کہا کہ مریم یہ تم نے بہت بفِا کام کیا ہے'),
(2278, 19, 28, 'ہارون کی بہن نہ تمہارا باپ بفِا آدمی تھا اور نہ تمہاری ماں بدکردار تھی'),
(2279, 19, 29, 'انہوں نے اس بچہ کی طرف اشارہ کردیا تو قوم نے کہا کہ ہم اس سے کیسے بات کریں جو گہوارے میں بچہ ہے'),
(2280, 19, 30, 'بچہ نے آواز دی کہ میں اللہ کا بندہ ہوں اس نے مجھے کتاب دی ہے اور مجھے نبی بنایا ہے'),
(2281, 19, 31, 'اور جہاں بھی رہوں بابرکت قرار دیا ہے اور جب تک زندہ رہوں نماز اور زکوِٰکی وصیت کی ہے'),
(2282, 19, 32, 'اور اپنی والدہ کے ساتھ حَسن سلوک کرنے والا بنایا ہے اور ظالم و بدنصیب نہیں بنایا ہے'),
(2283, 19, 33, 'اور سلام ہے مجھ پر اس دن جس دن میں پیدا ہوا اور جس دن مروں گا اور جس دن دوبارہ زندہ اٹھایا جاؤں گا'),
(2284, 19, 34, 'یہ ہے عیسٰی بن مریم کے بارے میں قول حق جس میں یہ لوگ شک کررہے تھے'),
(2285, 19, 35, 'اللہ کے لئے مناسب نہیں ہے کہ وہ کسی کو اپنا فرزند بنائے وہ پاک و بے نیاز ہے جس کسی بات کا فیصلہ کرلیتا ہے تو اس سے کہتا ہے کہ ہوجا اور وہ چیز ہوجاتی ہے'),
(2286, 19, 36, 'اور اللہ میرا اور تمہارا دونوں کا پروردگار ہے لہذا اس کی عبادت کرو اور یہی صراط همستقیم ہے'),
(2287, 19, 37, 'پھر مختلف گروہوں نے آپس میں اختلاف کیا اور ویل ان لوگوں کے لئے ہے جنہوں نے کفر اختیار کیا اور انہیں بڑے سخت دن کا سامنا کرنا ہوگا'),
(2288, 19, 38, 'اس دن جب ہمارے پاس آئیں گے تو خوب سنیں اور دیکھیں گے لیکن یہ ظالم آج کھلی ہوئی گمراہی میں مبتلا ہیں'),
(2289, 19, 39, 'اور ان لوگوں کو اس حسرت کے دن سے ڈرائیے جب قطعی فیصلہ ہوجائے گا اگرچہ یہ لوگ غفلت کے عالم میں پڑے ہوئے ہیں اور ایمان نہیں لارہے ہیں'),
(2290, 19, 40, 'بیشک ہم زمین اور جو کچھ زمین پر ہے سب کے وارث ہیں اور سب ہماری ہی طرف پلٹا کر لائے جائیں گے'),
(2291, 19, 41, 'اور کتاب هخدا میں ابراہیم علیھ السّلام کا تذکرہ کرو کہ وہ ایک صدیق پیغمبر تھے'),
(2292, 19, 42, 'جب انہوں نے اپنے پالنے والے باپ سے کہا کہ آپ ایسے کی عبادت کیوں کرتے ہیں جو نہ کچھ سنتا ہے نہ دیکھتا ہے اور نہ کسی کام آنے والا ہے'),
(2293, 19, 43, 'میرے پاس وہ علم آچکا ہے جو آپ کے پاس نہیں آیا ہے لہذا آپ میرا اتباع کریں میں آپ کو سیدھے راستہ کی ہدایت کردوں گا'),
(2294, 19, 44, 'بابا شیطان کی عبادت نہ کیجئے کہ شیطان رحمان کی نافرمانی کرنے والا ہے'),
(2295, 19, 45, 'بابا مجھے یہ خوف ہے کہ آپ کو رحمان کی طرف سے کوئی عذاب اپنی گرفت میں لے لے اور آپ شیطان کے دوست قرار پاجائیں'),
(2296, 19, 46, 'اس نے جواب دیا کہ ابراہیم علیھ السّلامکیا تم میرے خداؤں سے کنارہ کشی کرنے والے ہو تو یاد رکھو کہ اگر تم اس روش سے باز نہ آئے تو میں تمہیں سنگسار کردوں گا اور تم ہمیشہ کے لئے مجھ سے دور ہوجاؤ'),
(2297, 19, 47, 'ابراہیم علیھ السّلامنے کہا کہ خدا آپ کو سلامت رکھے میں عنقریب اپنے رب سے آپ کے لئے مغفرت طلب کروں گا کہ وہ میرے حال پر بہت مہربان ہے'),
(2298, 19, 48, 'اور آپ کو آپ کے معبودوں سمیت چھوڑ کر الگ ہوجاؤں گا اور اپنے رب کو آواز دوں گا کہ اس طرح میں اپنے پروردگار کی عبادت سے محروم نہ رہوں گا'),
(2299, 19, 49, 'پھر جب ابراہیم علیھ السّلامنے انہیں اور ان کے معبودوں کو چھوڑ دیا تو ہم نے انہیں اسحاق علیھ السّلامو یعقوب علیھ السّلام جیسی اولاد عطا کی اور سب کو نبی قرار دے دیا'),
(2300, 19, 50, 'اور پھر انہیں اپنی رحمت کا ایک حصہ ّبھی عطا کیا اور ان کے لئے صداقت کی بلند ترین زبان بھی قرار دے دی'),
(2301, 19, 51, 'اور اپنی کتاب میں موسٰی علیھ السّلام کا بھی تذکرہ کرو کہ وہ میرے مخلص بندے اور رسول و نبی تھے'),
(2302, 19, 52, 'اور ہم نے انہیں کوسِ طور کے داہنے طرف سے آواز دی اور راز و نیاز کے لئے اپنے سے قریب بلالیا'),
(2303, 19, 53, 'اور پھر انہیں اپنی رحمت خاص سے ان کے بھائی ہارون علیھ السّلام پیغمبر کو عطا کردیا'),
(2304, 19, 54, 'اور اپنی کتاب میں اسماعیل علیھ السّلام کا تذکرہ کرو کہ وہ وعدے کے سچے ّاور ہمارے بھیجے ہوئے پیغمبر علیھ السّلامتھے'),
(2305, 19, 55, 'اور وہ اپنے گھر والوں کو نماز اور زکوِٰ کا حکم دیتے تھے اور اپنے پروردگار کے نزدیک پسندیدہ تھے'),
(2306, 19, 56, 'اور کتاب خدا میں ادریس علیھ السّلامکا بھی تذکرہ کرو کہ وہ بہت زیادہ سچے پیغمبر علیھ السّلامتھے'),
(2307, 19, 57, 'اور ہم نے ان کو بلند جگہ تک پہنچادیا ہے'),
(2308, 19, 58, 'یہ سب وہ انبیائ ہیں جن پر اللہ نے نعمت نازل کی ہے ذرّیت آدم میں سے اور ان کی نسل میں سے جن کو ہم نے نوح علیھ السّلامکے ساتھ کشتی میں اٹھایا ہے اور ابراہیم علیھ السّلام و اسرائیل علیھ السّلامکی ذرّیت میں سے اور ان میں سے جن کو ہم نے ہدایت دی ہے اور انہیں منتخب بنایا ہے کہ جب ان کے سامنے رحمان کی آیتیں پڑھی جاتی ہیں تو روتے ہوئے سجدہ میں گر پڑتے ہیں'),
(2309, 19, 59, 'پھر ان کے بعد ان کی جگہ پر وہ لوگ آئے جنہوں نے نماز کو برباد کردیا اور خواہشات کا اتباع کرلیا پس یہ عنقریب اپنی گمراہی سے جاملیں گے'),
(2310, 19, 60, 'علاوہ ان کے جنہوں نے توبہ کرلی, ایمان لے آئے اور عمل صالح کیا کہ وہ جنّت میں داخل ہوں گے اور ان پر کسی طرح کا ظلم نہیں کیا جائے گا'),
(2311, 19, 61, 'ہمیشہ رہنے والی جنّت جس کا رحمان نے اپنے بندوں سے غیبی وعدہ کیا ہے اور یقینا اس کا وعدہ سامنے آنے والا ہے'),
(2312, 19, 62, 'اس جنّت میں سلام کے علاوہ کوئی لغو آواز سننے میں نہ آئے گی اور انہیں صبح و شام رزق ملتا رہے گا'),
(2313, 19, 63, 'یہی وہ جنّت ہے جس کا وارث ہم اپنے بندوں میں متقی افراد کو قرار دیتے ہیں'),
(2314, 19, 64, 'اور اے پیغمبر علیھ السّلامہم فرشتے آپ کے پروردگار کے حکم کے بغیر نازل نہیں ہوتے ہیں ہمارے سامنے یا پس پشت یا اس کے درمیان جو کچھ ہے سب اس کے اختیار میں ہے اور آپ کا پروردگار بھولنے والا نہیں ہے'),
(2315, 19, 65, 'وہ آسمان و زمین اور جو کچھ ان کے درمیان ہے سب کا مالک ہے لہذا اس کی عبادت کرو اور اس عبادت کی راہ میں صبر کرو کیا تمہارے علم میں اس کا کوئی ہمنام ہے'),
(2316, 19, 66, 'اور انسان یہ کہتا ہے کہ کیا جب ہم مرجائیں گے تو دوبارہ زندہ کرکے نکالے جائیں گے'),
(2317, 19, 67, 'کیا یہ اس بات کو یاد نہیں کرتا ہے کہ پہلے ہم نے ہی اسے پیدا کیا ہے جب یہ کچھ نہیں تھا'),
(2318, 19, 68, 'اور آپ کے رب کی قسم ہم ان سب کو اور ان کے شیاطین کو ایک جگہ اکٹھا کریں گے پھر سب کوجہّنم کے اطراف گھٹنوں کے بل حاضر کریں گے'),
(2319, 19, 69, 'پھر ہر گروہ سے ایسے افراد کو الگ کرلیں گے جو رحمان کے حق میں زیادہ نافرمان تھے'),
(2320, 19, 70, 'پھر ہم ان لوگوں کو بھی خوب جانتے ہیں جو جہّنم میں جھونکے جانے کے زیادہ سزاوار ہیں'),
(2321, 19, 71, 'اور تم میں سے کوئی ایسا نہیں ہے جسے جہّنم کے کنارے حاضر نہ ہونا ہو کہ یہ تمہارے رب کا حتمی فیصلہ ہے'),
(2322, 19, 72, 'اس کے بعد ہم متقی افراد کو نجات دے دیں گے اور ظالمین کوجہّنم میں چھوڑ دیں گے'),
(2323, 19, 73, 'اور جب ان کے سامنے ہماری کِھلی ہوئی آیتیں پیش کی جاتی ہیں تو ان میں کے کافر صاحبان ایمان سے کہتے ہیں کہ ہم دونوں میں کس کی جگہ بہتر اور کس کی منزل زیادہ حسن ہے'),
(2324, 19, 74, 'اور ہم نے ان سے پہلے کتنی ہی جماعتوں کوہلاک کُردیا ہے جو سازوسامان اور نام و نمود میں ان سے کہیں زیادہ بہتر تھے'),
(2325, 19, 75, 'آپ کہہ دیجئے کہ جو شخص گمراہی میں پڑا رہے گا خدا اسے اور ڈھیل دیتا رہے گا یہاں تک کہ یہ وعدہ الٰہی کو دیکھ لیں .... یا عذاب کی یا قیامت کی شکل میں .... پھر انہیں معلوم ہوجائے گا کہ جگہ کے اعتبار سے بدترین اور مددگاروں کے اعتبار سے کمزور ترین کون ہے'),
(2326, 19, 76, 'اور اللہ ہدایت یافتہ افراد کی ہدایت میں اضافہ کردیتا ہے اور باقی رہنے والی نیکیاں آپ کے پروردگار کے نزدیک ثواب اور بازگشت کے اعتبار سے بہترین اور بلند ترین ہیں'),
(2327, 19, 77, 'کیا تم نے اس شخص کو بھی دیکھا ہے جس نے ہماری آیات کا انکار کیا اور یہ کہنے لگا کہ ہمیں قیامت میں بھی مال اور اولاد سے نوازا جائے گا'),
(2328, 19, 78, 'یہ غیب سے باخبر ہوگیا ہے یا اس نے رحمان سے کوئی معاہدہ کرلیا ہے'),
(2329, 19, 79, 'ہرگز ایسا نہیں ہے ہم اس کی باتوں کو درج کررہے ہیں اور اس کے عذاب میں اور بھی اضافہ کردیں گے'),
(2330, 19, 80, 'اور اس کے مال و اولاد کے ہم ہی مالک ہوں گے اور یہ تو ہماری بارگاہ میں اکیلا حاضر ہوگا'),
(2331, 19, 81, 'اور ان لوگوں نے خدا کو چھوڑ کر دوسرے خدا اختیار کرلئے ہیں تاکہ وہ ان کے لئے باعث عزّت بنیں'),
(2332, 19, 82, 'ہرگز نہیں عنقریب یہ معبود خود ہی ان کی عبادت سے انکار کردیں گے اور ان کے مخالف ہوجائیں گے'),
(2333, 19, 83, 'کیا تم نے نہیں دیکھا کہ ہم نے شیاطین کو کافروں پر مسلّط کردیا ہے اور وہ ان کو بہکاتے رہتے ہیں'),
(2334, 19, 84, 'آپ ان کے بارے میں عذاب کی جلدی نہ کریں ہم ان کے دن خود ہی شمار کررہے ہیں'),
(2335, 19, 85, 'قیامت کے دن ہم صاحبان هتقویٰ کو رحمان کی بارگاہ میں مہمانوں کی طرح جمع کریں گے'),
(2336, 19, 86, 'اور مجفِمین کوجہّنم کی طرف پیاسے جانوروں کی طرح دھکیل دیں گے'),
(2337, 19, 87, 'اس وقت کوئی شفاعت کا صاحب هاختیار نہ ہوگا مگر وہ جس نے رحمان کی بارگاہ میں شفاعت کا عہد لے لیا ہے'),
(2338, 19, 88, 'اور یہ لوگ کہتے ہیں کہ رحمان نے کسی کو اپنا فرزند بنالیا ہے'),
(2339, 19, 89, 'یقینا تم لوگوں نے بڑی سخت بات کہی ہے'),
(2340, 19, 90, 'قریب ہے کہ اس سے آسمان پھٹ پڑے اور زمین شگافتہ ہوجائے اور پہاڑ ٹکڑے ٹکڑے ہوکر گر پڑیں'),
(2341, 19, 91, 'کہ ان لوگوں نے رحمان کے لئے بیٹا قرار دے دیا ہے'),
(2342, 19, 92, 'جب کہ یہ رحمان کے شایانِ شان نہیں ہے کہ وہ کسی کو اپنا بیٹا بنائے'),
(2343, 19, 93, 'زمین و آسمان میں کوئی ایسا نہیں ہے جو اس کی بارگاہ میں بندہ بن کر حاضر ہونے والا نہ ہو'),
(2344, 19, 94, 'خدا نے سب کا احصائ کرلیا ہے اور سب کو باقاعدہ شمار کرلیا ہے'),
(2345, 19, 95, 'اور سب ہی کل روز هقیامت اس کی بارگاہ میں حاضر ہونے والے ہیں'),
(2346, 19, 96, 'بیشک جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے عنقریب رحمان لوگوں کے دلوں میں ان کی محبت پیدا کردے گا'),
(2347, 19, 97, 'بس ہم نے اس قرآن کو تمہاری زبان میں اس لئے آسان کردیا ہے کہ تم متقین کو بشارت دے سکو اور جھگڑالو قوم کو عذاب الٰہی سے ڈرا سکو'),
(2348, 19, 98, 'اور ہم نے ان سے پہلے کتنی ہی نسلوں کو برباد کردیا ہے کیا تم ان میں سے کسی کو دیکھ رہے ہو یا کسی کی آہٹ بھی سن رہے ہو');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طہۤ'),
(2350, 20, 2, 'ہم نے آپ پر قرآن اس لئے نہیں نازل کیا ہے کہ آپ اپنے کو زحمت میں ڈال دیں'),
(2351, 20, 3, 'یہ تو ان لوگوں کی یاد دہانی کے لئے ہے جن کے دلوں میں خوف خدا ہے'),
(2352, 20, 4, 'یہ اس خدا کی طرف سے نازل ہوا ہے جس نے زمین اور بلند ترین آسمانوں کو پیدا کیا ہے'),
(2353, 20, 5, 'وہ رحمان عرش پر اختیار و اقتدار رکھنے والا ہے'),
(2354, 20, 6, 'اس کے لئے وہ سب کچھ ہے جو آسمانوں میں ہے یا زمین میں ہے یا دونوں کے درمیان ہے اور زمینوں کی تہ میں ہے'),
(2355, 20, 7, 'اگر تم بلند آواز سے بھی بات کرو تو وہ راز سے بھی مخفی تر باتوں کو جاننے والا ہے'),
(2356, 20, 8, 'وہ اللہ ہے جس کے علاوہ کوئی خدا نہیں ہے اس کے لئے بہترین نام ہیں'),
(2357, 20, 9, 'کیا تمہارے پاس موسٰی کی داستان آئی ہے'),
(2358, 20, 10, 'جب انہوں نے آگ کو دیکھا اور اپنے اہل سے کہا کہ تم اسی مقام پر ٹھہرو میں نے آگ کو دیکھا ہے شاید میں اس میں سے کوئی انگارہ لے آؤں یا اس منزل پر کوئی رہنمائی حاصل کرلوں'),
(2359, 20, 11, 'پھر جب موسٰی اس آگ کے قریب آئے تو آواز دی گئی کہ اے موسٰی'),
(2360, 20, 12, 'میں تمہارا پروردگار ہوں لہذا اپنی جوتیوں کو اتار دو کہ تم طویٰ نام کی ایک مقدس اور پاکیزہ وادی میں ہو'),
(2361, 20, 13, 'اور ہم نے تم کو منتخب کرلیا ہے لہذا جو وحی کی جارہی ہے اسے غور سے سنو'),
(2362, 20, 14, 'میں اللہ ہوں میرے علاوہ کوئی خدا نہیں ہے میری عبادت کرو اور میری یاد کے لئے نماز قائم کرو'),
(2363, 20, 15, 'یقینا وہ قیامت آنے والی ہے اور میں اسے چھپائے رہوں گا تاکہ ہر نفس کو اس کی کوشش کا بدلہ دیا جاسکے'),
(2364, 20, 16, 'اور خبردار تمہیں قیامت کے خیال سے وہ شخص روک نہ دے جس کا ایمان قیامت پر نہیں ہے اور جس نے اپنے خواہشات کی پیروی کی ہے کہ اس طرح تم ہلاک ہوجاؤ گے'),
(2365, 20, 17, 'اور اے موسٰی یہ تمہارے داہنے ہاتھ میں کیا ہے'),
(2366, 20, 18, 'انہوں نے کہا کہ یہ میرا عصا ہے جس پر میں تکیہ کرتا ہوں اور اس سے اپنی بکریوں کے لئے درختوں کی پتیاں جھاڑتا ہوں اور اس میں میرے اور بہت سے مقاصد ہیں'),
(2367, 20, 19, 'ارشاد ہوا تو موسٰی اسے زمین پر ڈال دو'),
(2368, 20, 20, 'اب جو موسٰی نے ڈال دیا تو کیا دیکھا کہ وہ سانپ بن کر دوڑ رہا ہے'),
(2369, 20, 21, 'حکم ہوا کہ اسے لے لو اور ڈرو نہیں کہ ہم عنقریب اسے اس کی پرانی اصل کی طرف پلٹا دیں گے'),
(2370, 20, 22, 'اور اپنے ہاتھ کو سمیٹ کر بغل میں کرلو یہ بغیر بیماری کے سفید ہوکر نکلے گا اور یہ ہماری دوسری نشانی ہوگی'),
(2371, 20, 23, 'تاکہ ہم تمہیں اپنی بڑی نشانیاں دکھاسکیں'),
(2372, 20, 24, 'جاؤ فرعون کی طرف جاؤ کہ وہ سرکش ہوگیا'),
(2373, 20, 25, 'موسٰی نے عرض کی پروردگار میرے سینے کو کشادہ کردے'),
(2374, 20, 26, 'میرے کام کو آسان کردے'),
(2375, 20, 27, 'اور میری زبان کی گرہ کو کھول دے'),
(2376, 20, 28, 'کہ یہ لوگ میری بات سمجھ سکیں'),
(2377, 20, 29, 'اور میرے اہل میں سے میرا وزیر قرار دے دے'),
(2378, 20, 30, 'ہارون کو جو میرا بھائی بھی ہے'),
(2379, 20, 31, 'اس سے میری پشت کو مضبوط کردے'),
(2380, 20, 32, 'اسے میرے کام میں شریک بنادے'),
(2381, 20, 33, 'تاکہ ہم تیری بہت زیادہ تسبیح کرسکیں'),
(2382, 20, 34, 'اور تیرا بہت زیادہ ذکر کرسکیں'),
(2383, 20, 35, 'یقینا تو ہمارے حالات سے بہتر باخبر ہے'),
(2384, 20, 36, 'ارشاد ہوا موسٰی ہم نے تمہاری مراد تمہیں دے دی ہے'),
(2385, 20, 37, 'اور ہم نے تم پر ایک اور احسان کیا ہے'),
(2386, 20, 38, 'جب ہم نے تمہاری ماں کی طرف ایک خاص وحی کی'),
(2387, 20, 39, 'کہ اپنے بچہ کو صندوق میں رکھ دو اور پھر صندوق کو دریا کے حوالے کردو موجیں اسے ساحل پر ڈال دیں گی اور ایک ایسا شخص اسے اٹھالے گا جو میرا بھی دشمن ہے اور موسٰی کا بھی دشمن ہے اور ہم نے تم پر اپنی محبت کا عکس ڈال دیا تاکہ تمہیں ہماری نگرانی میں پالا جائے'),
(2388, 20, 40, 'اس وقت کو یاد کرو جب تمہاری بہن جارہی تھیں کہ فرعون سے کہیں کہ کیا میں کسی ایسے کا پتہ بتاؤں جو اس کی کفالت کرسکے اور اس طرح ہم نے تم کو تمہاری ماں کی طرف پلٹا دیا تاکہ ان کی آنکھیں ٹھنڈی ہوجائیں اور وہ رنجیدہ نہ ہوں اور تم نے ایک شخص کو قتل کردیا تو ہم نے تمہیں غم سے نجات دے دی اور تمہارا باقاعدہ امتحان لے لیا پھر تم اہل مدین میں کئی برس تک رہے اس کے بعد تم ایک منزل پر آگئے اے موسٰی'),
(2389, 20, 41, 'اور ہم نے تم کو اپنے لئے منتخب کرلیا'),
(2390, 20, 42, 'اب تم اپنے بھائی کے ساتھ میری نشانیاں لے کر جاؤ اور میری یاد میں سستی نہ کرنا'),
(2391, 20, 43, 'تم دونوں فرعون کی طرف جاؤ کہ وہ سرکش ہوگیا ہے'),
(2392, 20, 44, 'اس سے نرمی سے بات کرنا کہ شاید وہ نصیحت قبول کرلے یا خوف زدہ ہوجائے'),
(2393, 20, 45, 'ان دونوں نے کہا کہ پروردگار ہمیں یہ خوف ہے کہ کہیں وہ ہم پر زیادتی نہ کرے یا اور سرکش نہ ہوجائے'),
(2394, 20, 46, 'ارشاد ہوا تم ڈرو نہیں میں تمہارے ساتھ ہوں سب کچھ سن بھی رہا ہوں اور دیکھ بھی رہا ہوں'),
(2395, 20, 47, 'فرعون کے پاس جاکر کہو کہ ہم تیرے پروردگار کے فرستادہ ہیں بنی اسرائیل کو ہمارے حوالے کردے اور ان پر عذاب نہ کر کہ ہم تیرے پاس تیرے پروردگار کی نشانی لے کر آئے ہیں اور ہمارا سلام ہو اس پر جو ہدایت کا اتباع کرے'),
(2396, 20, 48, 'بیشک ہماری طرف یہ وحی کی گئی ہے کہ تکذیب کرنے والے اور منہ پھیرنے والے پر عذاب ہے'),
(2397, 20, 49, 'اس نے کہا کہ موسٰی تم دونوں کا رب کون ہے'),
(2398, 20, 50, 'موسٰی نے کہا کہ ہمارا رب وہ ہے جس نے ہر شے کو اس کی مناسب خلقت عطا کی ہے اور پھر ہدایت بھی دی ہے'),
(2399, 20, 51, 'اس نے کہا کہ پھر ان لوگوں کا کیا ہوگا جو پہلے گزر چکے ہیں'),
(2400, 20, 52, 'موسٰی نے کہا کہ ان باتوں کاعلم میرے پروردگار کے پاس اس کی کتاب میں محفوظ ہے وہ نہ بہکتا ہے اور نہ بھولتا ہے'),
(2401, 20, 53, 'اس نے تمہارے لئے زمین کو گہوارہ بنایا ہے اور اس میں تمہارے لئے راستے بنائے ہیں اور آسمان سے پانی برسایا ہے جس کے ذریعہ ہم نے مختلف قسم کے نباتات کا جوڑا پیدا کیا ہے'),
(2402, 20, 54, 'کہ تم خود بھی کھاؤ اور اپنے جانوروں کو بھی چراؤ بے شک اس میں صاحبان هعقل کے لئے بڑی نشانیاں پائی جاتی ہیں'),
(2403, 20, 55, 'اسی زمین سے ہم نے تمہیں پیدا کیا ہے اور اسی میں پلٹا کرلے جائیں گے اور پھر دوبارہ اسی سے نکالیں گے'),
(2404, 20, 56, 'اور ہم نے فرعون کو اپنی ساری نشانیاں دکھلادیں لیکن اس نے تکذیب کی اور انکار کردیا'),
(2405, 20, 57, 'اس نے کہا کہ موسٰی تم اس لئے آئے ہو کہ ہم کو ہمارے علاقہ سے اپنے جادو کے ذریعہ باہر نکال دو'),
(2406, 20, 58, 'اب ہم بھی ایسا ہی جادو لے آئیں گے لہذا اپنے اور ہمارے درمیان ایک وقت مقرر کردو جس کی نہ ہم مخالفت کریں اور نہ تم اور وہ وعدہ گاہ بھی ایک صاف کھلے میدان میں ہو'),
(2407, 20, 59, 'موسٰی نے کہا کہ تمہارا وعدہ کا دن زینت (عید) کا دن ہے اور اس دن تمام لوگ وقت چاشت اکٹھا کئے جائیں گے'),
(2408, 20, 60, 'اس کے بعد فرعون واپس چلا گیا اور اپنے مکر کو اکٹھا کرنے لگا اور اس کے بعد پھر سامنے آیا'),
(2409, 20, 61, 'موسٰی نے ان لوگوں سے کہا کہ تم پر وائے ہو اللہ پر افترا نہ کرو کہ وہ تم کو عذاب کے ذریعہ تباہ و برباد کردے گا اور جس نے اس پر بہتان باندھا وہ یقینا رسوا ہوا ہے'),
(2410, 20, 62, 'اس پر وہ لوگ آپس میں جھگڑا کرنے لگے اور سرگوشیوں میں مصروف ہوگئے'),
(2411, 20, 63, 'ان لوگوں نے کہا کہ یہ دونوں جادوگر ہیں جو تم لوگوں کو اپنے جادو کے زور پر تمہاری سرزمین سے نکال دینا چاہتے ہیں اور تمہارے اچھے خاصے طریقہ کو مٹا دینا چاہتے ہیں'),
(2412, 20, 64, 'لہذا تم لوگ اپنی تدبیروں کو جمع کرو اور پرا باندھ کر ان کے مقابلے پر آجاؤ جو آج کے دن غالب آجائے گا وہی کامیاب کہا جائے گا'),
(2413, 20, 65, 'ان لوگوں نے کہا کہ موسٰی تم اپنے جادو کو پھینکو گے یا ہم لوگ پہل کریں'),
(2414, 20, 66, 'موسٰی نے کہا کہ نہیں تم ابتدا کرو ایک مرتبہ کیا دیکھا کہ ان کی رسیاں اور لکڑیاں جادو کی بنا پر ایسی لگنے لگیں جیسے سب دوڑ رہی ہوں'),
(2415, 20, 67, 'تو موسٰی نے اپنے دل میں (قوم کی گمراہی کا) خوف محسوس کیا'),
(2416, 20, 68, 'ہم نے کہا کہ موسٰی ڈرو نہیں تم بہرحال غالب رہنے والے ہو'),
(2417, 20, 69, 'اور جو کچھ تمہارے ہاتھ میں ہے اسے ڈال دو یہ ان کے سارے کئے دھرے کو چن لے گا ان لوگوں نے جو کچھ کیا ہے وہ صرف جادوگر کی چال ہے اور بس اور جادوگر جہاں بھی جائے کبھی کامیاب نہیں ہوسکتا'),
(2418, 20, 70, 'یہ دیکھ کر سارے جادوگر سجدہ میں گر پڑے اور آواز دی کہ ہم موسٰی اور ہارون کے پروردگار پر ایمان لے آئے'),
(2419, 20, 71, 'فرعون نے کہا کہ تم میری اجازت کے بغیر ہی ایمان لے آئے تو یہ تم سے بھی بڑا جادوگر ہے جس نے تمہیں جادو سکھایا ہے اب میں تمہارے ایک طرف کے ہاتھ اور دوسری طرف کے پاؤں کاٹ دوں گا اور تمہیں خرمہ کی شاخ پر سولی دے دوں گا اور تمہیں خوب معلوم ہوجائے گا کہ زیادہ سخت عذاب کرنے والا اور دیر تک رہنے والا کون ہے'),
(2420, 20, 72, 'ان لوگوں نے کہا کہ ہمارے پاس جو کھلی نشانیاں آچکی ہیں اور جس نے ہم کو پیدا کیا ہے ہم اس پر تیری بات کو مقدم نہیں کرسکتے اب تجھے جو فیصلہ کرنا ہو کرلے تو فقط اس زندگانی دنیا ہی تک فیصلہ کرسکتا ہے'),
(2421, 20, 73, 'ہم اپنے پروردگار پر ایمان لے آئے ہیں کہ وہ ہماری خطاؤں کو معاف کردے اور اس جادو کو بخش دے جس پر تو نے ہمیں مجبور کیا تھا اور اللہ سب سے بہتر ہے ا ورہی باقی رہنے والا ہے'),
(2422, 20, 74, 'یقینا جو اپنے رب کی بارگاہ میں مجرم بن کر آئے گا اس کے لئے وہ جہنم ّہے جس میں نہ مرسکے گا اور نہ زندہ رہ سکے گا'),
(2423, 20, 75, 'اور جو اس کے حضور صاحب هایمان بن کر حاضر ہوگا اور اس نے نیک اعمال کئے ہوں گے اس کے لئے بلند ترین درجات ہیں'),
(2424, 20, 76, 'ہمیشہ رہنے والی جنت ّجس کے نیچے نہریں جاری ہوں گی اور وہ اس میں ہمیشہ رہیں گے کہ یہی پاکیزہ کردار لوگوں کی جزا ہے'),
(2425, 20, 77, 'اور ہم نے موسٰی کی طرف وحی کی کہ میرے بندوں کو لے کر راتوں رات نکل جاؤ پھر ان کے لئے دریا میں عصا مار کر خشک راستہ بنادو تمہیں نہ فرعون کے پالینے کا خطرہ ہے اور نہ ڈوب جانے کا'),
(2426, 20, 78, 'تب فرعون نے اپنے لشکر سمیت ان لوگوں کا پیچھا کیا اور دریا کی موجوں نے انہیں باقاعدہ ڈھانک لیا'),
(2427, 20, 79, 'اور فرعون نے درحقیقت اپنی قوم کو گمراہ ہی کیا ہے ہدایت نہیں دی ہے'),
(2428, 20, 80, 'بنی اسرائیل ! ہم نے تم کو تمہارے دشمن سے نجات دلائی ہے اور طور کی داہنی طرف سے توریت دینے کا وعدہ کیا ہے اور من و سلویٰ بھی نازل کیا ہے'),
(2429, 20, 81, 'تم ہمارے پاکیزہ رزق کو کھاؤ اور اس میں سرکشی اور زیادتی نہ کرو کہ تم پر میرا غضب نازل ہوجائے کہ جس پر میرا غضب نازل ہوگیا وہ یقینا برباد ہوگیا'),
(2430, 20, 82, 'اور میں بہت زیادہ بخشنے والا ہوں اس شخص کے لئے جو توبہ کرلے اور ایمان لے آئے اور نیک عمل کرے اور پھر راسِ ہدایت پر ثابت قدم رہے'),
(2431, 20, 83, 'اور اے موسٰی تمہیں قوم کو چھوڑ کر جلدی آنے پر کس شے نے آمادہ کیا ہے'),
(2432, 20, 84, 'موسٰی نے عرض کی کہ وہ سب میرے پیچھے آرہے ہیں اور میں نے راہ هخیر میں اس لئے عجلت کی ہے کہ تو خوش ہوجائے'),
(2433, 20, 85, 'ارشاد ہوا کہ ہم نے تمہارے بعد تمہاری قوم کا امتحان لیا اور سامری نے انہیں گمراہ کردیا ہے'),
(2434, 20, 86, 'یہ سن کر موسٰی اپنی قوم کی طرف محزون اور غصہّ میں بھرے ہوئے پلٹے اور کہا کہ اے قوم کیا تمہارے رب نے تم سے بہترین وعدہ نہیں کیا تھا اور کیا اس عہد میں کچھ زیادہ طول ہوگیا ہے یا تم نے یہی چاہا کہ تم پر پروردگار کا غضب وارد ہوجائے اس لئے تم نے میرے وعدہ کی مخالفت کی'),
(2435, 20, 87, 'قوم نے کہا کہ ہم نے اپنے اختیار سے آپ کے وعدہ کی مخالفت نہیں کی ہے بلکہ ہم پر قوم کے زیورات کا بوجھ لاد دیا گیا تھا تو ہم نے اسے آگ میں ڈال دیا اور اس طرح سامری نے بھی اپے زیورات کو ڈال دیا'),
(2436, 20, 88, 'پھر سامری نے ان کے لئے ایک مجسمہ گائے کے بچے کا نکالا جس میں آواز بھی تھی اور کہا کہ یہی تمہارا اور موسٰی کا خدا ہے جس سے موسٰی غافل ہوکر اسے طور پر ڈھونڈنے چلے گئے ہیں'),
(2437, 20, 89, 'کیا یہ لوگ اتنا بھی نہیں دیکھتے کہ یہ نہ ان کی بات کا جواب دے سکتا ہے اور نہ ان کے کسی نقصان یا فائدہ کا اختیار رکھتا ہے'),
(2438, 20, 90, 'اور ہارون نے ان لوگوں سے پہلے ہی کہہ دیا کہ اے قوم اس طرح تمہارا امتحان لیا گیا ہے اور تمہارا رب رحمان ہی ہے لہذا میرا اتباع کرو اور میرے امر کی اطاعت کرو'),
(2439, 20, 91, 'ان لوگوں نے کہا کہ ہم اس کے گرد جمع رہیں گے یہاں تک کہ موسٰی ہمارے درمیان واپس آجائیں'),
(2440, 20, 92, 'موسٰی نے ہارون سے خطاب کرکے کہا کہ جب تم نے دیکھ لیا تھا کہ یہ قوم گمراہ ہوگئی ہے تو تمہیں کون سی بات آڑے آگئی تھی'),
(2441, 20, 93, 'کہ تم نے میرا اتباع نہیں کیا, کیا تم نے میرے امر کی مخالفت کی ہے'),
(2442, 20, 94, 'ہارون نے کہا کہ بھیّا آپ میری داڑھی اور میرا سر نہ پکڑیں مجھے تو یہ خوف تھا کہ کہیں آپ یہ نہ کہیں کہ تم نے بنی اسرائیل میں اختلاف پیدا کردیا ہے اور میری بات کا انتظار نہیں کیا ہے'),
(2443, 20, 95, 'پھر موسٰی نے سامری سے کہا کہ تیرا کیا حال ہے'),
(2444, 20, 96, 'اس نے کہا کہ میں نے وہ دیکھا ہے جو ان لوگوں نے نہیں دیکھا ہے تو میں نے نمائندہ پروردگار کے نشانِ قدم کی ایک مٹھی خاک اٹھالی اور اس کو گوسالہ کے اندر ڈال دیا اور مجھے میرے نفس نے اسی طرح سمجھایا تھا'),
(2445, 20, 97, 'موسٰی نے کہا کہ اچھا جا دور ہوجا اب زندگانی دنیا میں تیری سزا یہ ہے کہ ہر ایک سے یہی کہتا پھرے گا کہ مجھے چھونا نہیں اور آخرت میں ایک خاص وعدہ ہے جس کی مخالفت نہیں ہوسکتی اور اب دیکھ اپنے خدا کو جس کے گرد تو نے اعتکاف کر رکھا ہے کہ میں اسے جلاکر خاکستر کردوں گا اور اس کی راکھ دریا میں اڑادوں گا'),
(2446, 20, 98, 'یقینا تم سب کا خدا صرف اللہ ہے جس کے علاوہ کوئی خدا نہیں ہے اور وہی ہر شے کا وسیع علم رکھنے والا ہے'),
(2447, 20, 99, 'اور ہم اسی طرح گزشتہ دور کے واقعات آپ سے بیان کرتے ہیں اور ہم نے اپنی بارگاہ سے آپ کو قرآن بھی عطا کردیا ہے'),
(2448, 20, 100, 'جو اس سے اعراض کرے گا وہ قیامت کے دن اس انکار کا بوجھ اٹھائے گا'),
(2449, 20, 101, 'اور پھر اسی حال میں رہے گا اور قیامت کے دن یہ بہت بڑا بوجھ ہوگا'),
(2450, 20, 102, 'جس دن صور پھونکا جائے گا اور ہم تمام مجرمین کو بدلے ہوئے رنگ میں اکٹھا کریں گے'),
(2451, 20, 103, 'یہ سب آپس میں یہ بات کررہے ہوں گے کہ ہم دنیا میں صرف دس ہی دن تو رہے ہیں'),
(2452, 20, 104, 'ہم ان کی باتوں کو خوب جانتے ہیں جب ان کا سب سے ہوشیار یہ کہہ رہا تھا کہ تم لوگ صرف ایک دن رہے ہو'),
(2453, 20, 105, 'اور یہ لوگ آپ سے پہاڑوں کے بارے میں پوچھتے ہیں کہ قیامت میں ان کا کیا ہوگا تو کہہ دیجئے کہ میرا پروردگار انہیں ریزہ ریزہ کرکے اڑادے گا'),
(2454, 20, 106, 'پھر زمین کو چٹیل میدان بنادے گا'),
(2455, 20, 107, 'جس میں تم کسی طرح کی کجی ناہمواری نہ دیکھو گے'),
(2456, 20, 108, 'اس دن سب داعی پروردگار کے پیچھے دوڑ پڑیں گے اور کسی طرح کی کجی نہ ہوگی اور ساری آوازیں رحمان کے سامنے دب جائیں گی کہ تم گھنگھناہٹ کے علاوہ کچھ نہ سنو گے'),
(2457, 20, 109, 'اس دن کسی کی سفارش کام نہ آئے گی سوائے ان کے جنہیں خدا نے اجازت دے دی ہو اور وہ ان کی بات سے راضی ہو'),
(2458, 20, 110, 'وہ سب کے سامنے اور پیچھے کے حالات سے باخبر ہے اور کسی کا علم اس کی ذات کو محیط نہیں ہے'),
(2459, 20, 111, 'اس دن سارے چہرے خدائے حی و قیوم کے سامنے جھکے ہوں گے اور ظلم کا بوجھ اٹھانے والا ناکام اور رسوا ہوگا'),
(2460, 20, 112, 'اور جو نیک اعمال کرے گا اور صاحبِ ایمان ہوگا وہ نہ ظلم سے ڈرے گا اور نہ نقصان سے'),
(2461, 20, 113, 'اور اسی طرح ہم نے قرآن کو عربی زبان میں نازل کیا ہے اور اس میں طرح طرح سے عذاب کا تذکرہ کیا ہے کہ شاید یہ لوگ پرہیز گار بن جائیں یا قرآن ان کے اندر کسی طرح کی عبرت پیدا کردے'),
(2462, 20, 114, 'پس بلند و برتر ہے وہ خدا جو بادشاہ هبرحق ہے اور آپ وحی کے تمام ہونے سے پہلے قرآن کے بارے میں عجلت سے کام نہ لیا کریں اور یہ کہتے رہیں کہ پروردگار میرے علم میں اضافہ فرما'),
(2463, 20, 115, 'اور ہم نے آدم سے اس سے پہلے عہد لیا مگر انہوں نے اسے ترک کردیا اور ہم نے ان کے پاس عزم و ثبات نہیں پایا'),
(2464, 20, 116, 'اور جب ہم نے ملائکہ سے کہا کہ تم سب آدم کے لئے سجدہ کرو تو ابلیس کے علاوہ سب نے سجدہ کرلیا اور اس نے انکار کردیا'),
(2465, 20, 117, 'تو ہم نے کہا کہ آدم یہ تمہارا اور تمہاری زوجہ کا دشمن ہے کہیں تمہیں جنت ّسے نکال نہ دے کہ تم زحمت میں پڑجاؤ'),
(2466, 20, 118, 'بیشک یہاں جنتّ میں تمہارا فائدہ یہ ہے کہ نہ بھوکے رہو گے اور نہ برہنہ رہو گے'),
(2467, 20, 119, 'اور یقینا یہاں نہ پیاسے رہو گے اور نہ دھوپ کھاؤ گے'),
(2468, 20, 120, 'پھر شیطان نے انہیں وسوسہ میں مبتلا کرنا چاہا اور کہا کہ آدم میں تمہیں ہمیشگی کے درخت کی طرف رہنمائی کردوں اور ایسا لَلک بتادوں جو کبھی زائل نہ ہو'),
(2469, 20, 121, 'تو ان دونوں نے درخت سے کھالیا اور اور ان کے لئے ان کا آ گا پیچھا ظاہر ہوگیا اور وہ اسے جنتّ کے پتوں سے چھپانے لگے اور آدم نے اپنے پروردگار کی نصیحت پر عمل نہ کیا تو راحت کے راستہ سے بے راہ ہوگئے'),
(2470, 20, 122, 'پھر خدا نے انہیں چن لیا اور ان کی توبہ قبول کرلی اور انہیں راستہ پر لگادیا'),
(2471, 20, 123, 'اور حکم دیا کہ تم دونوں یہاں سے نیچے اتر جاؤ سب ایک دوسرے کے دشمن ہوں گے اس کے بعد اگر میری طرف سے ہدایت آجائے تو جو میری ہدایت کی پیروی کرے گا وہ نہ گمراہ ہوگا اور نہ پریشان'),
(2472, 20, 124, 'اور جو میرے ذکر سے اعراض کرے گا اس کے لئے زندگی کی تنگی بھی ہے اور ہم اسے قیامت کے دن اندھا بھی محشور کریں گے'),
(2473, 20, 125, 'وہ کہے گا کہ پروردگار یہ تو نے مجھے اندھا کیوں محشور کیا ہے جب کہ میں دا» دنیا میں صاحبِ بصارت تھا'),
(2474, 20, 126, 'ارشاد ہوگا کہ اسی طرح ہماری آیتیں تیرے پاس آئیں اور تونے انہیں بھلا دیا تو آج تو بھی نظر انداز کردیا جائے گا'),
(2475, 20, 127, 'اور ہم زیادتی کرنے والے اور اپنے رب کی نشانیوں پر ایمان نہ لانے والوں کو اسی طرح سزا دیتے ہیں اور آخرت کا عذاب یقینا سخت ترین اور ہمیشہ باقی رہنے والا ہے'),
(2476, 20, 128, 'کیا انہیں اس بات نے رہنمائی نہیں دی کہ ہم نے ان سے پہلے کتنی نسلوں کو ہلاک کردیا جو اپنے علاقہ میں نہایت اطمینان سے چل پھر رہے تھے بیشک اس میں صاحبان هعقل کے لئے بڑی نشانیاں ہیں'),
(2477, 20, 129, 'اور اگر آپ کے رب کی طرف سے بات طے نہ ہوچکی ہوتی اور وقت مقرر نہ ہوتا تو عذاب لازمی طور پر آچکا ہوتا'),
(2478, 20, 130, 'لہذا آپ ان کی باتوں پر صبر کریں اور آفتاب نکلنے سے پہلے اور اس کے ڈوبنے کے بعد اپنے رب کی تسبیح کرتے رہیں اور رات کے اوقات میں اور دن کے اطراف میں بھی تسبیح پروردگار کریں کہ شاید آپ اس طرح راضی اور خوش ہوجائیں'),
(2479, 20, 131, 'اور خبردار ہم نے ان میں سے بعض لوگوں کو جو زندگانی دنیا کی رونق سے مالا مال کردیا ہے اس کی طرف آپ نظر اٹھا کر بھی نہ دیکھیں کہ یہ ان کی آزمائش کا ذریعہ ہے اور آپ کے پروردگار کا رزق اس سے کہیں زیادہ بہتر اور پائیدار ہے'),
(2480, 20, 132, 'اور اپنے اہل کو نماز کا حکم دیں اور اس پر صبر کریں ہم آپ سے رزق کے طلبگار نہیں ہیں ہم تو خود ہی رزق دیتے ہیں اور عاقبت صرف صاحبانِ تقویٰ کے لئے ہے'),
(2481, 20, 133, 'اور یہ لوگ کہتے ہیں کہ یہ اپنے رب کی طرف سے کوئی نشانی کیوں نہیں لاتے ہیں تو کیا ان کے پاس اگلی کتابوں کی گواہی نہیں آئی ہے'),
(2482, 20, 134, 'اور اگر ہم نے رسول سے پہلے انہیں عذاب کرکے ہلاک کردیا ہوتا تو یہ کہتے پروردگار تو نے ہماری طرف رسول کیوں نہیں بھیجا کہ ہم ذلیل اور رسوا ہونے سے پہلے ہی تیری نشانیوں کا اتباع کرلیتے'),
(2483, 20, 135, 'آپ کہہ دیجئے کہ سب اپنے اپنے وقت کا انتظار کررہے ہیں تم بھی انتظار کرو عنقریب معلوم ہوجائے گا کہ کون لوگ سیدھے راستہ پر چلنے والے اور ہدایت یافتہ ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'لوگوں کے لئے حساب کا وقت آپہنچا ہے اور وہ ابھی غفلت ہی میں پڑے ہوئے ہیں اور کنارہ کشی کئے جارہے ہیں'),
(2485, 21, 2, 'ان کے پاس ان کے پروردگار کی طرف سے کوئی نئی یاد دہانی نہیں آتی مگر یہ کہ کان لگا کر سوُ لیتے ہیں اور پھر کھیل تماشے میں لگ جاتے ہیں'),
(2486, 21, 3, 'ان کے دل بالکل غافل ہوگئے ہیں اور یہ ظالم اس طرح آپس میں راز و نیاز کی باتیں کیا کرتے ہیں کہ یہ بھی تو تمہارے ہی طرح کے ایک انسان ہیں کیا تم دیدہ و دانستہ ان کے جادو کے چکر میں آرہے ہو'),
(2487, 21, 4, 'تو پیغمبر نے جواب دیا کہ میرا پروردگار آسمان و زمین کی تمام باتوں کو جانتا ہے وہ خوب سننے والا اور جاننے والا ہے'),
(2488, 21, 5, 'بلکہ یہ لوگ کہتے ہیں کہ یہ تو سب خواب ه پریشاں کا مجموعہ ہے بلکہ یہ خود پیغمبر کی طرف سے افترائ ہے بلکہ یہ شاعر ہیں اور شاعری کررہے ہیں ورنہ ایسی نشانی لے کر آتے جیسی نشانی لے کر پہلے پیغمبر علیھ السّلامبھیجے گئے تھے'),
(2489, 21, 6, 'ان سے پہلے ہم نے جن بستیوں کو سرکشی کی بنا پر تباہ کر ڈالا وہ تو ایمان لائے نہیں یہ کیا ایمان لائیں گے'),
(2490, 21, 7, 'اور ہم نے آپ سے پہلے بھی جن رسولوں علیھ السّلامکو بھیجا ہے وہ سب مرد ہی تھے جن کی طرف ہم وحی کیا کرتے تھے-تو تم لوگ اگر نہیں جانتے ہو تو جاننے والوں سے دریافت کرلو'),
(2491, 21, 8, 'اور ہم نے ان لوگوں کے لئے بھی کوئی ایسا جسم نہیں بنایا تھا جو کھانا نہ کھاتا ہو اور وہ بھی ہمیشہ رہنے والے نہیں تھے'),
(2492, 21, 9, 'پھر ہم نے ان کے وعدہ کو سچ کر دکھایا اور انہیں اور ان کے ساتھ جن کو چاہا بچالیا اور زیادتی کرنے والوں کو تباہ و برباد کردیا'),
(2493, 21, 10, 'بیشک ہم نے تمہاری طرف وہ کتاب نازل کی ہے جس میں خود تمہارا بھی ذکر ہے تو کیا تم اتنی بھی عقل نہیں رکھتے ہو'),
(2494, 21, 11, 'اور ہم نے کتنی ہی ظالم بستیوں کو تباہ کردیا اور ان کے بعد ان کی جگہ پر دوسری قوموں کو ایجاد کردیا'),
(2495, 21, 12, 'پھر جب ان لوگوں نے عذاب کی آہٹ محسوس کی تو اسے دیکھ کر بھاگنا شروع کردیا'),
(2496, 21, 13, 'ہم نے کہا کہ اب بھاگو نہیں اور اپنے گھروں کی طرف اور اپنے سامان عیش و عشرت کی طرف پلٹ کر جاؤ کہ تم سے اس کے بارے میں پوچھ گچھ کی جائے گی'),
(2497, 21, 14, 'ان لوگوں نے کہا کہ ہائے افسوس ہم واقعا ظالم تھے'),
(2498, 21, 15, 'اور یہ کہہ کر فریاد کرتے رہے یہاں تک کہ ہم نے انہیں کٹی ہوئی کھیتی کی طرح بنا کر ان کے سارے جوش کو ٹھنڈا کردیا'),
(2499, 21, 16, 'اور ہم نے آسمان و زمین اور ان کے درمیان کی تمام چیزوں کو کھیل تماشے کے لئے نہیں بنایا ہے'),
(2500, 21, 17, 'ہم کھیل ہی بنانا چاہتے تو اپنی طرف ہی سے بنالیتے اگر ہمیں ایسا کرنا ہوتا'),
(2501, 21, 18, 'بلکہ ہم تو حق کو باطل کے سر پر دے مارتے ہیں اور اس کے دماغ کو کچل دیتے ہیں اور وہ تباہ و برباد ہوجاتا ہے اور تمہارے لئے ویل ہے کہ تم ایسی بے ربط باتیں بیان کررہے ہو'),
(2502, 21, 19, 'اور اسی خدا کے لئے زمین و آسمان کی کل کائنات ہے اور جو افراد اس کی بارگاہ میں ہیں وہ نہ اس کی عبادت سے اکڑ کر انکار کرتے ہیں اور نہ تھکتے ہیں'),
(2503, 21, 20, 'دن رات اسی کی تسبیح کرتے ہیں اور سستی کا بھی شکار نہیں ہوتے ہیں'),
(2504, 21, 21, 'کیا ان لوگوں نے زمین میں ایسے خدا بنا لئے ہیں جو ان کو زندہ کرنے والے ہیں'),
(2505, 21, 22, 'یاد رکھو اگر زمین و آسمان میں اللہ کے علاوہ اور خدا بھی ہوتے تو زمین و آسمان دونوں برباد ہوجاتے عرش کا مالک پروردگار ان کے بیانات سے بالکل پاک و پاکیزہ ہے'),
(2506, 21, 23, 'اس سے باز پرس کرنے والا کوئی نہیں ہے اور وہ ہر ایک کا حساب لینے والا ہے'),
(2507, 21, 24, 'کیا ان لوگوں نے اس کے علاوہ اور خدا بنالئے ہیں تو آپ کہہ دیجئے کہ ذرا اپنی دلیل تو لاؤ-یہ میرے ساتھ والوں کا ذکر اور مجھ سے پہلے والوں کا ذکر سب موجود ہے لیکن ان کی اکثریت حق سے ناواقف ہے اور اسی لئے کنارہ کشی کررہی ہے'),
(2508, 21, 25, 'اور ہم نے آپ سے پہلے کوئی رسول نہیں بھیجا مگر یہ کہ اس کی طرف یہی وحی کرتے رہے کہ میرے علاوہ کوئی خدا نہیں ہے لہذا سب لوگ میری ہی عبادت کرو'),
(2509, 21, 26, 'اور لوگوں نے یہ کہنا شروع کردیا کہ اللہ نے کسی کو بیٹا بنالیا ہے حالانکہ وہ اس امر سے پاک و پاکیزہ ہے بلکہ وہ سب اس کے محترم بندے ہیں'),
(2510, 21, 27, 'جو کسی بات پر اس پر سبقت نہیں کرتے ہیں اور اس کے احکام پر برابر عمل کرتے رہتے ہیں'),
(2511, 21, 28, 'وہ ان کے سامنے اور ان کے پس پشت کی تمام باتوں کو جانتا ہے اور فرشتے کسی کی سفارش بھی نہیں کرسکتے مگر یہ کہ خدا اس کو پسند کرے اور وہ اس کے خوف سے برابر لرزتے رہتے ہیں'),
(2512, 21, 29, 'اور اگر ان میں سے بھی کوئی یہ کہہ دے کہ خدا کے علاوہ میں بھی خدا ہوں تو ہم اس کو بھی جہنّم کی سزا دیں گے کہ ہم اسی طرح ظالموں کو سزا دیا کرتے ہیں'),
(2513, 21, 30, 'کیا ان کافروں نے یہ نہیں دیکھا کہ یہ زمین و آسمان آپس میں جڑے ہوئے تھے اور ہم نے ان کو الگ کیا ہے اور ہر جاندار کو پانی سے قرار دیا ہے پھر کیا یہ لوگ ایمان نہ لائیں گے'),
(2514, 21, 31, 'اور ہم نے زمین میں پہاڑ قرار دیئے ہیں کہ وہ ان لوگوں کو لے کر کسی طرف جھک نہ جائے اور پھر اس میں وسیع تر راستے قرار دیئے ہیں کہ اس طرح یہ منزل مقصود تک پہنچ سکیں'),
(2515, 21, 32, 'اور ہم نے آسمان کو ایک محفوظ چھت کی طرح بنایا ہے اور یہ لوگ اس کی نشانیوں سے برابر اعراض کررہے ہیں'),
(2516, 21, 33, 'وہ وہی خدا ہے جس نے رات دن اور آفتاب و ماہتاب سب کو پید اکیا ہے اور سب اپنے اپنے فلک میں تیر رہے ہیں'),
(2517, 21, 34, 'اور ہم نے آپ سے پہلے بھی کسی بشر کے لئے ہمیشگی نہیں قرار دی ہے تو کیا اگر آپ مرجائیں گے تو یہ لوگ ہمیشہ رہنے والے ہیں'),
(2518, 21, 35, 'ہر نفس موت کا مزہ چکھنے والا ہے اور ہم تو اچھائی اور برائی کے ذریعہ تم سب کو آزمائیں گے اور تم سب پلٹا کر ہماری ہی بارگاہ میں لائے جاؤ گے'),
(2519, 21, 36, 'اور جب بھی یہ کفّار آپ کو دیکھتے ہیں تو اس بات کا مذاق اُڑاتے ہیں کہ یہی وہ شخص ہے جو تمہارے خداؤں کا ذکر کیا کرتا ہے اور یہ لوگ خود تو رحمان کے ذکر سے انکار ہی کرنے والے ہیں'),
(2520, 21, 37, 'انسان کے خمیر میں عِجلت شامل ہوگئی ہے اور عنقریب ہم تمہیں اپنی نشانیاں دکھائیں گے تو پھر تم لوگ جلدی نہیں کرو گے'),
(2521, 21, 38, 'اور یہ لوگ کہتے ہیں کہ اگر تم سچے ّ ہو تو اس وعدہ قیامت کا وقت آخر کب آئے گا'),
(2522, 21, 39, 'کاش یہ کافر یہ جانتے کہ وہ ایسا وقت ہوگا جب یہ جہنمّ کی آگ کو نہ اپنے سامنے سے ہٹاسکیں گے اور نہ پشت سے اور نہ ان کی کوئی مدد کی جاسکے گی'),
(2523, 21, 40, 'بلکہ یہ قیامت ان تک اچانک آجائے گی اور انہیں مبہوت کردے گی پھر یہ نہ اسے رد کرسکیں گے اور نہ انہیں کوئی مہلت دی جائے گی'),
(2524, 21, 41, 'اور پیغمبر آپ سے پہلے بھی بہت سے رسولوں کا مذاق اڑایا گیا ہے جس کے بعد ان کفاّر کو اس عذاب نے گھیر لیا جس کا یہ مذاق اُڑارہے تھے'),
(2525, 21, 42, 'آپ ان سے کہہ دیجئے کہ انہیں رات یا دن میں رحمان کے عذاب سے بچانے کے لئے کون پہرہ دے سکتا ہے مگر یہ ہیں کہ رحمان کی یاد سے مسلسل منہ موڑے ہوئے ہیں'),
(2526, 21, 43, 'کیا ان کے پاس ایسے خدا موجود ہیں جو ہمارے بغیر انہیں بچا سکیں گے یہ بیچارے تو خود اپنی بھی مدد نہیں کرسکتے ہیں اور نہ انہیں خود بھی عذاب سے پناہ دی جائے گی'),
(2527, 21, 44, 'بلکہ ہم نے انہیں اور ان کے باپ دادا کو تھوڑی سی لّذت دنیا دے دی ہے یہاں تک کہ ان کا زمانہ طویل ہوگیا تو کیا یہ نہیں دیکھتے ہیں کہ ہم برابر زمین کی طرف آتے جارہے ہیں اور اس کو چاروں طرف سے کم کرتے جارہے ہیں کیا اس کے بعد بھی یہ ہم پر غالب آجانے والے ہیں'),
(2528, 21, 45, 'آپ کہہ دیجئے کہ میں تم لوگوں کو وحی کے مطابق ڈراتا ہوں اور بہرہ کو جب بھی ڈرایا جاتا ہے وہ آواز کو سنتا ہی نہیں ہے'),
(2529, 21, 46, 'حالانکہ انہیں عذاب الٰہی کی ہوا بھی چھوجائے تو کہہ اُٹھیں گے کہ افسوس ہم واقعی ظالم تھے'),
(2530, 21, 47, 'اور ہم قیامت کے دن انصاف کی ترازو قائم کریں گے اور کسی نفس پر ادنیٰ ظلم نہیں کیا جائے گا اور کسی کا عمل رائی کے دانہ کے برابر بھی ہے تو ہم اسے لے آئیں گے اور ہم سب کا حساب کرنے کے لئے کافی ہیں'),
(2531, 21, 48, 'اور ہم نے موسٰی علیھ السّلام اور ہارون علیھ السّلام کو حق و باطل میں فرق کرنے والی وہ کتاب عطا کی ہے جو ہدایت کی روشنی اور ان صاحبانِ تقویٰ کے لئے یاد الٰہی کا ذریعہ ہے'),
(2532, 21, 49, 'جو ازغیب اپنے پروردگار سے ڈرنے والے ہیں اور قیامت کے خوف سے لرزاں ہیں'),
(2533, 21, 50, 'اور یہ قرآن ایک مبارک ذکر ہے جسے ہم نے نازل کیا ہے تو کیا تم لوگ اس کا بھی انکار کرنے والے ہو'),
(2534, 21, 51, 'اور ہم نے ابراہیم علیھ السّلامکو اس سے پہلے ہی عقل سلیم عطا کردی تھی اور ہم ان کی حالت سے باخبر تھے'),
(2535, 21, 52, 'جب انہوں نے اپنے مربی باپ اور اپنی قوم سے کہا کہ یہ مورتیاں کیا ہیں جن کے گرد تم حلقہ باندھے ہوئے ہو'),
(2536, 21, 53, 'ان لوگوں نے کہا کہ ہم نے اپنے باپ دادا کو بھی ان ہی کی عبادت کرتے ہوئے دیکھا ہے'),
(2537, 21, 54, 'ابراہیم علیھ السّلامنے کہا کہ یقینا تم اور تمہارے باپ دادا سب کھلی ہوئی گمراہی میں ہو'),
(2538, 21, 55, 'ان لوگوں نے کہا کہ آپ کوئی حق بات لے کر آئے ہیں یا خالی کھیل تماشہ ہی کرنے والے ہیں'),
(2539, 21, 56, 'ابراہیم علیھ السّلامنے کہا کہ تمہارا واقعی رب وہی ہے جو آسمان و زمین کا رب ہے اور اسی نے ان سب کو پیدا کیا ہے اور میں اسی بات کے گواہوں میں سے ایک گواہ ہوں'),
(2540, 21, 57, 'اور خدا کی قسم میں تمہارے بتوں کے بارے میں تمہارے چلے جانے کے بعد کوئی تدبیر ضرور کروں گا'),
(2541, 21, 58, 'پھر ابراہیم علیھ السّلامنے ان کے بڑے کے علاوہ سب کو حُور چوِر کردیا کہ شاید یہ لوگ پلٹ کر اس کے پاس آئیں'),
(2542, 21, 59, 'ان لوگوں نے کہا کہ ہمارے خداؤں کے ساتھ یہ برتاؤ کس نے کیا ہے وہ یقینا ظالمین میں سے ہے'),
(2543, 21, 60, 'لوگوں نے بتایا کہ ایک جو ان ہے جوان کا ذکر کیا کرتا ہے اور اسے ابراہیم کہا جاتا ہے'),
(2544, 21, 61, 'ان لوگوں نے کہا کہ اسے لوگوں کے سامنے لے آؤ شاید لوگ گواہی دے سکیں'),
(2545, 21, 62, 'پھر ان لوگوں نے ابراہیم علیھ السّلام سے کہا کہ کیا تم نے ہمارے خداؤں کے ساتھ یہ برتاؤ کیا ہے'),
(2546, 21, 63, 'ابراہیم علیھ السّلامنے کہا کہ یہ ان کے بڑے نے کیا ہے تم ان سے دریافت کرکے دیکھو اگر یہ بول سکیں'),
(2547, 21, 64, 'اس پر ان لوگوں نے اپنے دلوں کی طرف رجوع کیا اور آپس میں کہنے لگے کہ یقینا تم ہی لوگ ظالم ہو'),
(2548, 21, 65, 'اس کے بعد ان کے سر شرم سے جھکا دئیے گئے اور کہنے لگے ابراہیم تمہیں تو معلوم ہے کہ یہ بولنے والے نہیں ہیں'),
(2549, 21, 66, 'ابراہیم نے کہا کہ پھر تم خدا کو چھوڑ کر ایسے خداؤں کی عبادت کیوں کرتے ہو جو نہ کوئی فائدہ پہنچاسکتے ہیں اور نہ نقصان'),
(2550, 21, 67, 'حیف ہے تمہارے اوپر اور تمہارے ان خداؤں پر جنہیں تم نے خدائے برحق کو چھوڑ کر اختیار کیا ہے کیا تم اتنا بھی نہیں سمجھتے ہو'),
(2551, 21, 68, 'ان لوگوں نے کہا کہ ابراہیم کو آگ میں جلادو اور اگر کچھ کرنا چاہتے ہو تو اس طرح اپنے خداؤں کی مدد کرو'),
(2552, 21, 69, 'تو ہم نے بھی حکم دیا کہ اے آگ ابراہیم علیھ السّلامکے لئے سرد ہوجا اور سلامتی کاسامان بن جا'),
(2553, 21, 70, 'اور ان لوگوں نے ایک مکر کا ارادہ کیا تھا تو ہم نے بھی انہیں خسارہ والا اور ناکام قرار دے دیا'),
(2554, 21, 71, 'اور ابراہیم علیھ السّلام اور لوط علیھ السّلام کو نجات دلاکر اس سرزمین کی طرف لے آئے جس میں عالمین کے لئے برکت کا سامان موجود تھا'),
(2555, 21, 72, 'اور پھر ابراہیم علیھ السّلامکو اسحاق علیھ السّلاماور ان کے بعد یعقوب علیھ السّلامعطا کئے اور سب کو صالح اور نیک کردار قرار دیا'),
(2556, 21, 73, 'اور ہم نے ان سب کو پیشوا قرار دیا جو ہمارے حکم سے ہدایت کرتے تھے اور ان کی طرف کارخیر کرنے نماز قائم کرنے اور زکوِٰادا کرنے کی وحی کی اور یہ سب کے سب ہمارے عبادت گزار بندے تھے'),
(2557, 21, 74, 'اور لوط علیھ السّلام کو یاد کرو جنہیں ہم نے قوت فیصلہ اور علم عطا کیا اور اس بستی سے نجات دلادی جو بدکاریوں میں مبتلا تھی کہ یقینا یہ لوگ بڑے بڑے اور فاسق تھے'),
(2558, 21, 75, 'اور ہم نے انہیں اپنی رحمت میں داخل کرلیا کہ وہ یقینا ہمارے نیک کردار بندوں میں سے تھے'),
(2559, 21, 76, 'اور نوح علیھ السّلامکو یاد کرو کہ جب انہوں نے پہلے ہی ہم کو آواز دی اور ہم نے ان کی گزارش قبول کرلی اور انہیں اور ان کے اہل کو بہت بڑے کرب سے نجات دلادی'),
(2560, 21, 77, 'اور ان لوگوں کے مقابلہ میں ان کی مدد کی جو ہماری آیتوں کی تکذیب کیا کرتے تھے کہ یہ لوگ بہت بری قوم تھے تو ہم نے ان سب کو غرق کردیا'),
(2561, 21, 78, 'اور داؤد علیھ السّلاماور سلیمان علیھ السّلام کو یاد کرو جب وہ دونوں ایک کھیتی کے بارے میں فیصلہ کررہے تھے جب کھیت میں قوم کی بکریاں گھصَ گئی تھیں اور ہم ان کے فیصلہ کو دیکھ رہے تھے'),
(2562, 21, 79, 'پھر ہم نے سلیمان علیھ السّلام کو صحیح فیصلہ سمجھا دیا اور ہم نے سب کو قوت فیصلہ اور علم عطا کیا تھا اور داؤد علیھ السّلام کے ساتھ پہاڑوں کو لَسّخر کردیا تھا کہ وہ تسبیح پروردگار کریں اور طیور کو بھی لَسّخر کردیا تھا اور ہم ایسے کام کرتے رہتے ہیں'),
(2563, 21, 80, 'اور ہم نے انہیں زرہ بنانے کی صنعت تعلیم دے دی تاکہ وہ تم کو جنگ کے خطرات سے محفوظ رکھ سکے تو کیا تم ہمارے شکر گزار بندے بنو گے'),
(2564, 21, 81, 'اور سلیماوُ کے لئے تیز و تند ہواؤں کو لَسّخر کردیا جو ان کے حکم سے اس سرزمین کی طرف چلتی تھیں جس میں ہم نے برکتیں رکھی تھیں اور ہم ہر شے کے جاننے والے ہیں'),
(2565, 21, 82, 'اور بعض جناّت کو بھی لَسّخر کردیا جو سمندر میں غوطے لگایا کرتے تھے اور اس کے علاوہ دوسرے کام بھی انجام دیا کرتے تھے اور ہم ان سب کے نگہبان تھے'),
(2566, 21, 83, 'اور ایوب علیھ السّلام کو یاد کرو جب انہوں نے اپنے پروردگار کو پکارا کہ مجھے بیماری نے چھولیا ہے اور تو بہترین رحم کرنے والا ہے'),
(2567, 21, 84, 'تو ہم نے ان کی دعا کو قبول کرلیا اور ان کی بیماری کو دور کردیا اور انہیں ان کے اہل وعیال دے دیئے اور ویسے ہی اور بھی دے دیئے کہ یہ ہماری طرف سے خاص مہربانی تھی اور یہ عبادت گزار بندوں کے لئے ایک یاد دہانی ہے'),
(2568, 21, 85, 'اور اسماعیل علیھ السّلام و ادریس علیھ السّلام و ذوالکفل علیھ السّلام کو یاد کرو کہ یہ سب صبر کرنے والوں میں سے تھے'),
(2569, 21, 86, 'اور سب کو ہم نے اپنی رحمت میں داخل کرلیا تھا اور یقینا یہ سب ہمارے نیک کردار بندوں میں سے تھے'),
(2570, 21, 87, 'اور یونس علیھ السّلامکو یاد کرو کہ جب وہ غصّہ میں آکر چلے اور یہ خیال کیا کہ ہم ان پر روزی تنگ نہ کریں گے اور پھر تاریکیوں میں جاکر آواز دی کہ پروردگار تیرے علاوہ کوئی خدا نہیں ہے تو پاک و بے نیاز ہے اور میں اپنے نفس پر ظلم کرنے والوں میں سے تھا'),
(2571, 21, 88, 'تو ہم نے ان کی دعا کو قبول کرلیا اور انہیں غم سے نجات دلادی کہ ہم اسی طرح صاحبان هایمان کو نجات دلاتے رہتے ہیں'),
(2572, 21, 89, 'اور زکریا علیھ السّلام کو یاد کرو کہ جب انہوں نے اپنے رب کو پکارا کہ پروردگار مجھے اکیلا نہ چھوڑ دینا کہ تو تمام وارثوں سے بہتر وارث ہے'),
(2573, 21, 90, 'تو ہم نے ان کی دعا کو بھی قبول کرلیا اور انہیں یحیٰی علیھ السّلام جیسا فرزند عطا کردیا اور ان کی زوجہ کو صالحہ بنادیا کہ یہ تمام وہ تھے جو نیکیوں کی طرف سبقت کرنے والے تھے اور رغبت اور خوف کے ہر عالم میں ہم ہی کو پکارنے والے تھے اور ہماری بارگاہ میں گڑ گڑا کر الِتجا کرنے والے بندے تھے'),
(2574, 21, 91, 'اور اس خاتون کو یاد کرو جس نے اپنی شرم کی حفاظت کی تو ہم نے اس میں اپنی طرف سے روح پھونک دی اور اسے اور اس کے فرزند کو تمام عالمین کے لئے اپنی نشانی قرار دے دیا'),
(2575, 21, 92, 'بیشک یہ تمہارا دین ایک ہی دین اسلام ہے اور میں تم سب کا پروردگار ہوں لہذا میری ہی عبادت کیا کرو'),
(2576, 21, 93, 'اور ان لوگوں نے تو اپنے دین کو بھی آپس میں ٹکڑے ٹکڑے کرلیا ہے حالانکہ یہ سب پلٹ کر ہماری ہی بارگاہ میں آنے والے ہیں'),
(2577, 21, 94, 'پھر جو شخص صاحب هایمان رہ کر نیک عمل کرے گا اس کی کوشش برباد نہ ہوگی اور ہم اس کی کوشش کو برابر لکھ رہے ہیں'),
(2578, 21, 95, 'اور جس بستی کو ہم نے تباہ کردیا ہے اس کے لئے بھی ناممکن ہے کہ قیامت کے دن ہمارے پاس پلٹ کر نہ آئے'),
(2579, 21, 96, 'یہاں تک کہ یاجوج ماجوج آزاد کردیئے جائیں گے اور زمین کی ہر بلندی سے دوڑتے ہوئے نکل پڑیں گے'),
(2580, 21, 97, 'اور اللہ کا سچا وعدہ قریب آجائے گا تو سب دیکھیں گے کہ کفار کی آنکھیں پتھرا گئی ہیں اور وہ کہہ رہے ہیں کہ وائے بر حال ماہم اس طرف سے بالکل غفلت میں پڑے ہوئے تھے بلکہ ہم اپنے نفس پر ظلم کرنے والے تھے'),
(2581, 21, 98, 'یاد رکھو کہ تم لوگ خود اور جن چیزوں کی تم پرستش کررہے ہو سب کو جہنمّ کا ایندھن بنایا جائے گا اور تم سب اسی میں وارد ہونے والے ہو'),
(2582, 21, 99, 'اگر یہ سب واقعا خدا ہوتے تو کبھی جہنمّ میں وارد نہ ہوتے حالانکہ یہ سب اسی میں ہمیشہ ہمیشہ رہنے والے ہیں'),
(2583, 21, 100, 'جہنمّ میں ان کے لئے چیخ پکار ہوگی اور وہ کسی کی بات سننے کے قابل نہ ہوں گے'),
(2584, 21, 101, 'بیشک جن لوگوں کے حق میں ہماری طرف سے پہلے ہی نیکی مقدر ہوچکی ہے وہ اس جہنمّ سے دور رکھے جائیں گے'),
(2585, 21, 102, 'اور اس کی بھنک بھی نہ سنیں گے اور اپنی حسب خواہش نعمتوں میں ہمیشہ ہمیشہ آرام سے رہیں گے'),
(2586, 21, 103, 'انہیں قیامت کا بڑے سے بڑا ہولناک منظر بھی رنجیدہ نہ کرسکے گا اور ان سے ملائکہ اس طرح ملاقات کریں گے کہ یہی وہ دن ہے جس کا تم سے وعدہ کیا گیا تھا'),
(2587, 21, 104, 'اس دن ہم تمام آسمانوں کو اس طرح لپیٹ دیں گے جس طرح خطوں کا طومار لپیٹا جاتا ہے اور جس طرح ہم نے تخلیق کی ابتدائ کی ہے اسی طرح انہیں واپس بھی لے آئیں گے یہ ہمارے ذمہ ایک وعدہ ہے جس پر ہم بہرحال عمل کرنے والے ہیں'),
(2588, 21, 105, 'اور ہم نے ذکر کے بعد زبور میں بھی لکھ دیا ہے کہ ہماری زمین کے وارث ہمارے نیک بندے ہی ہوں گے'),
(2589, 21, 106, 'یقینا اس میں عبادت گزار قوم کے لئے ایک پیغام ہے'),
(2590, 21, 107, 'اور ہم نے آپ کو عالمین کے لئے صرف رحمت بناکر بھیجا ہے'),
(2591, 21, 108, 'آپ کہہ دیجئے کہ ہماری طرف صرف یہ وحی آتی ہے کہ تمہارا خدا ایک ہے تو کیا تم اسلام لانے والے ہو'),
(2592, 21, 109, 'پھر اگر یہ منہ موڑ لیں تو کہہ دیجئے کہ ہم نے تم سب کو برابر سے آگاہ کردیا ہے .اب مجھے نہیں معلوم کہ جس عذاب کا وعدہ کیا گیا ہے وہ قریب ہے یا دور ہے'),
(2593, 21, 110, 'بیشک وہ خدا ان باتوں کو بھی جانتا ہے جن کا اظہار کیا جاتا ہے اور ان باتوں کو بھی جانتا ہے جن کو یہ لوگ چھپارہے ہیں'),
(2594, 21, 111, 'اور میں کچھ نہیں جانتا شاید یہ تاخیر عذاب بھی ایک طرح کا امتحان ہو یا ایک مدّت معین تک کا آرام ہو'),
(2595, 21, 112, 'پھر پیغمبر نے دعا کی کہ پروردگار ہمارے درمیان حق کے ساتھ فیصلہ کردے اور ہمارا رب یقینا مہربان اور تمہاری باتوں کے مقابلہ میں قابل استعانت ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'لوگو ! اپنے پروردگار سے ڈرو کہ قیامت کا زلزلہ بہت بڑی شے ہے'),
(2597, 22, 2, 'جس دن تم دیکھو گے کہ دودھ پلانے والی عورتیں اپنے دودھ پیتے بچوّں سے غافل ہوجائیں گی اور حاملہ عورتیں اپنے حمل کو گرادیں گی اور لوگ نشہ کی حالت میں نظر آئیں گے حالانکہ وہ بدمست نہیں ہوں گے بلکہ اللہ کا عذاب ہی بڑا سخت ہوگا'),
(2598, 22, 3, 'اور لوگوں میں کچھ ایسے بھی ہیں جو بغیر جانے بوجھے خدا کے بارے میں جھگڑا کرتے ہیں اور ہر سرکش شیطان کا اتباع کرلیتے ہیں'),
(2599, 22, 4, 'ان کے بارے میں یہ لکھ دیا گیا ہے کہ جو شیطان کو اپنا دوست بنائے گا شیطان اسے گمراہ کردے گا اور پھر جہنمّ کے عذاب کی طرف رہنمائی کردے گا'),
(2600, 22, 5, 'اے لوگو اگر تمہیں دوبارہ اٹھائے جانے میں شبہ ہے تو یہ سمجھ لو کہ ہم نے ہی تمہیں پہلے خاک سے بنایا ہے پھر نطفہ سے پھر جمے ہوئے خون سے پھر گوشت کے لوتھڑے سے جس میں سے کوئی مکمل ہوجاتا ہے اور کوئی ناقص ہی رہ جاتا ہے تاکہ ہم تمہارے اوپر اپنی قدرت کو واضح کردیں ہم جس چیز کو جب تک چاہتے ہیں رحم میں رکھتے ہیں اس کے بعد تم کو بچہ بناکر باہر لے آتے ہیں پھر زندہ رکھتے ہیں تاکہ جوانی کی عمر تک پہنچ جاؤ اور پھر تم میں سے بعض کو اٹھالیا جاتا ہے اور بعض کو پست ترین عمر تک باقی رکھا جاتا ہے تاکہ علم کے بعد پھر کچھ جاننے کے قابل نہ رہ جائے اور تم زمین کو مردہ دیکھتے ہو پھر جب ہم پانی برسادیتے ہیں تو وہ لہلہانے لگتی ہے اور ابھرنے لگتی ہے اور ہر طرح کی خوبصورت چیز اگانے لگتی ہے'),
(2601, 22, 6, 'یہ اس لئے ہے کہ وہ اللہ خدائے برحق ہے اور وہی مفِدوں کو زندہ کرتا ہے اور وہی ہر شے پر قدرت رکھنے والا ہے'),
(2602, 22, 7, 'اور قیامت یقینا آنے والی ہے اس میں کسی شک کی گنجائش نہیں ہے اور اللہ قبروں سے مفِدوں کو اٹھانے والا ہے'),
(2603, 22, 8, 'اور لوگوں میں ایسے بھی ہیں جو علم و ہدایت اور کتابِ لَنیر کے بغیر بھی خدا کے بارے میں بحث کرتے ہیں'),
(2604, 22, 9, 'وہ غرور سے منہ پھرائے ہوئے ہیں تاکہ دوسرے لوگوں کو بھی راسِ خدا سے گمراہ کرسکیں تو ایسے اشخاص کے لئے دنیا میں رسوائی ہے اور آخرت میں ہم انہیں جہنمّ کا مزہ چکھائیں گے'),
(2605, 22, 10, 'یہ اس بات کی سزا ہے جو تم پہلے کرچکے ہو اور خدا اپنے بندوں پر ہرگز ظلم کرنے والا نہیں ہے'),
(2606, 22, 11, 'اور لوگوں میں کچھ ایسے بھی ہیں جو خدا کی عبادت ایک ہی رخ پراور مشروط طریقہ سے کرتے ہیں کہ اگر ان تک خیر پہنچ گیا تو مطمئن ہوجاتے ہیں اور اگر کوئی مصیبت چھوگئی تو دین سے پلٹ جاتے ہیں یہ دنیا اور آخرت دونوں میں خسارہ میں ہیں اور یہی خسارہ کھلا ہوا خسارہ ہے'),
(2607, 22, 12, 'یہ اللہ کو چھوڑ کر انہیں پکارتے ہیں جو نہ انہیں نقصان پہنچاسکتے ہیں اور نہ فائدہ اور یہی دراصل بہت دور تک پھیلی ہوئی گمراہی ہے'),
(2608, 22, 13, 'یہ ان کو پکارتے ہیں جن کا نقصان ان کے فائدے سے زیادہ قریب تر ہے وہ ان کے بدترین سرپرست اور بدترین ساتھی ہیں'),
(2609, 22, 14, 'بیشک اللہ ایمان والوں اور نیک عمل کرنے والوں کو ان جنتوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی کہ اللہ جو چاہتا ہے انجام دیتا ہے'),
(2610, 22, 15, 'جس شخص کا خیال یہ ہے کہ اللہ دنیا اور آخرت میں اس کی مدد نہیں کرے گا اسے چاہئے کہ ایک رسی کے ذریعہ آسمان کی طرف بڑھے اور پھر اس رسی کو کاٹ دے اور پھر دیکھے کہ اس کی ترکیب اس چیز کو دور کرسکتی ہے یا نہیں جس کے غصّہ میں وہ مبتلا تھا'),
(2611, 22, 16, 'اور اسی طرح ہم نے اس قرآن کو واضح نشانیوں کی شکل میں نازل کیا ہے اور اللہ جس کو چاہتاہے ہدایت دے دیتا ہے'),
(2612, 22, 17, 'بیشک جو لوگ ایمان لائے اور جنہوں نے یہودیت اختیار کی یا ستارہ پرست ہوگئے یا نصرانی اور آتش پرست ہوگئے یا مشرک ہوگئے ہیں خدا قیامت کے دن ان سب کے درمیان یقینی فیصلہ کردے گا کہ اللہ ہر شے کا نگراں اور گواہ ہے'),
(2613, 22, 18, 'کیا تم نے نہیں دیکھا کہ زمین و آسمان میں جس قدر بھی صاحبانِ عقل و شعور ہیں اور آفتاب و ماہتاب اور ستارے ,پہاڑ ,درخت ,چوپائے اور انسانوں کی ایک کثرت سب ہی اللہ کے لئے سجدہ گزار ہیں اور ان میں سے بہت سے ایسے بھی ہیں جن پر عذاب ثابت ہوچکا ہے اور ظاہر ہے کہ جسے خدا ذلیل کرنا چاہے اسے کوئی عزّت دینے والا نہیں ہے کہ یقینا اللہ جو چاہتا ہے وہ کرسکتا ہے'),
(2614, 22, 19, 'یہ مومن و کافر دو باہمی دشمن ہیں جنہوں نے پروردگار کے بارے میں آپس میں اختلاف کیا ہے پھر جو لوگ کافر ہیں ان کے واسطے آگ کے کپڑے قطع کئے جائیں گے اور ان کے سروں پر گرما گرم پانی انڈیلا جائے گا'),
(2615, 22, 20, 'جس سے ان کے پیٹ کے اندر جو کچھ ہے اور ان کی جلدیں سب گل جائیں گی'),
(2616, 22, 21, 'اور ان کے لئے لوہے کے گرز مہیاّ کئے گئے ہیں'),
(2617, 22, 22, 'جب یہ جہنمّ کی تکلیف سے نکل بھاگنا چاہیں گے تو دوبارہ اسی میں پلٹا دیئے جائیں گے کہ ابھی اور جہنمّ کا مزہ چکّھو'),
(2618, 22, 23, 'بیشک اللہ صاحبان هایمان اور نیک عمل کرنے والوں کو ان جنتوں میں داخل کرتا ہے جن کے نیچے نہریں جاری ہوتی ہیں انہیں ان جنتوں میں سونے کے کنگن اور موتی پہنائے جائیں گے اور ان کا لباس اس جنّت میں ریشم کا لباس ہوگا'),
(2619, 22, 24, 'اور انہیں پاکیزہ قول کی طرف ہدایت دی گئی ہے اور انہیں خدائے حمید کے راستہ کی طرف رہنمائی کی گئی ہے'),
(2620, 22, 25, 'بیشک جن لوگوں نے کفر اختیار کیا اور لوگوں کو اللہ کے راستے اور مسجد الحرام سے روکتے ہیں جسے ہم نے تمام انسانوں کے لئے برابر سے قرار دیا ہے چاہے وہ مقامی ہوں یا باہر والے اور جو بھی اس مسجد میں ظلم کے ساتھ الحاد کا ارادہ کرے گا ہم اسے دردناک عذاب کا مزہ چکھائیں گے'),
(2621, 22, 26, 'اور اس وقت کو یاد دلائیں جب ہم نے ابراہیم علیھ السّلامکے لئے بیت اللہ کی جگہ مہیا کی کہ خبردار ہمارے بارے میں کسی طرح کا شرک نہ ہونے پائے اور تم ہمارے گھر کو طواف کرنے والے ,قیام کرنے والے اور رکوع و سجود کرنے والوں کے لئے پاک و پاکیزہ بنادو'),
(2622, 22, 27, 'اور لوگوں کے درمیان حج کا اعلان کردو کہ لوگ تمہاری طرف پیدل اور لاغر سواریوں پر دور دراز علاقوں سے سوار ہوکر آئیں گے'),
(2623, 22, 28, 'تاکہ اپنے منافع کا مشاہدہ کریں اور چند معین دنوں میں ان چوپایوں پر جو خدا نے بطور رزق عطا کئے ہیں خدا کا نام لیں اور پھر تم اس میں سے کھاؤ اور بھوکے محتاج افراد کو کھلاؤ'),
(2624, 22, 29, 'پھر لوگوں کو چاہئے کہ اپنے بدن کی کثافت کو دور کریں اور اپنی نذروں کو پورا کریں اور اس قدیم ترین مکان کا طواف کریں'),
(2625, 22, 30, 'یہ ایک حکم خدا ہے اور جو شخص بھی خدا کی محترم چیزوں کی تعظیم کرے گا وہ اس کے حق میں پیش پروردگار بہتری کا سبب ہوگی اور تمہارے لئے تمام جانور حلال کردیئے گئے ہیں علاوہ ان کے جن کے بارے میں تم سے بیان کیا جائے گا لہذا تم ناپاک بتوں سے پرہیز کرتے رہو اور لغو اور مہمل باتوں سے اجتناب کرتے رہو'),
(2626, 22, 31, 'اللہ کے لئے مخلص اور باطل سے کترا کر رہو اور کسی طرح کا شرک اختیار نہ کرو کہ جو کسی کو اس کا شریک بناتا ہے وہ گویا آسمان سے گر پڑتا ہے اور اسے پرندہ اچک لیتا ہے یا ہوا کسی دور دراز جگہ پر لے جاکر پھینک دیتی ہے'),
(2627, 22, 32, 'یہ ہمارا فیصلہ ہے اور جو بھی اللہ کی نشانیوں کی تعظیم کرے گا یہ تعظیم اس کے دل کے تقویٰ کا نتیجہ ہوگی'),
(2628, 22, 33, 'تمہارے لئے ان قربانی کے جانوروں میں ایک مقررہ مدّت تک فائدے ہی فائدے ہیں اس کے بعد ان کی جگہ خانہ کعبہ کے پاس ہے'),
(2629, 22, 34, 'اور ہم نے ہر قوم کے لئے قربانی کا طریقہ مقرر کردیا ہے تاکہ جن جانوروں کا رزق ہم نے عطا کیا ہے ان پر نام خدا کا ذکر کریں پھر تمہارا خدا صرف خدائے واحد ہے تم اسی کے اطاعت گزار بنو اور ہمارے گڑگڑانے والے بندوں کو بشارت دے دو'),
(2630, 22, 35, 'جن کے سامنے ذکر خدا آتا ہے تو ان کے دل لرز جاتے ہیں اور وہ جو مصیبت پر صبر کرنے والے اور نماز قائم کرنے والے ہیں اور ہم نے جو رزق دیا ہے اس میں سے ہماری راہ میں خرچ کرنے والے ہیں'),
(2631, 22, 36, 'اور ہم نے قربانیوں کے اونٹ کو بھی اپنی نشانیوں میں سے قرار دیا ہے اس میں تمہارے لئے خیر ہے لہذا اس پر کھڑے ہونے کی حالت ہی میں نام خدا کا ذکر کرو اور اس کے بعد جب اس کے تمام پہلو گر جائیں تو اس میں سے خود بھی کھاؤ اور قناعت کرنے والے اور مانگنے والے سب غریبوں کو کھلاؤ کہ ہم نے انہیں تمہارے لئے لَسّخر کردیا ہے تاکہ تم شکر گزار بندے بن جاؤ'),
(2632, 22, 37, 'خدا تک ان جانوروں کا گوشت جانے والا ہے اور نہ خون ... اس کی بارگاہ میں صرف تمہارا تقویٰ جاتا ہے اور اسی طرح ہم نے ان جانوروں کو تمہارا تابع بنادیا ہے کہ خدا کی دی ہوئی ہدایت پر اس کی کبریائی کا اعلان کرو اور نیک عمل والوں کو بشارت دے دو'),
(2633, 22, 38, 'بیشک اللہ صاحبانِ ایمان کی طرف سے دفاع کرتا ہے اور یقینا اللہ خیانت کرنے والے کافروں کو ہرگز دوست نہیں رکھتا ہے'),
(2634, 22, 39, 'جن لوگوں سے مسلسل جنگ کی جارہی ہے انہیں ان کی مظلومیت کی بنائ پر جہاد کی اجازت دے دی گئی ہے اور یقینا اللہ ان کی مدد پر قدرت رکھنے والا ہے'),
(2635, 22, 40, 'یہ وہ لوگ ہیں جو اپنے گھروں سے بلا کسی حق کے نکال دیئے گئے ہیں علاوہ اس کے کہ وہ یہ کہتے ہیں کہ ہمارا پروردگار اللہ ہے اور اگر خدا بعض لوگوں کو بعض کے ذریعہ نہ روکتا ہوتا تو تمام گرجے اور یہودیوں کے عبادت خانے اور مجوسیوں کے عبادت خانے اور مسجدیں سب منہدم کردی جاتیں اور اللہ اپنے مددگاروں کی یقینا مدد کرے گا کہ وہ یقینا صاحبِ قوت بھی ہے اور صاحبِ عزّت بھی ہے'),
(2636, 22, 41, 'یہ وہ لوگ ہیں جنہیں ہم نے زمین میں اختیار دیا تو انہوں نے نماز قائم کیً اور زکوِٰ ادا کی اور نیکیوں کا حکم دیا اور برائیوں سے روکا اور یہ طے ہے کہ جملہ امور کا انجام خدا کے اختیار میں ہے'),
(2637, 22, 42, 'اور پیغمبر علیھ السّلام اگر یہ لوگ آپ کو جھٹلاتے ہیں تو ان سے پہلے قوم نوح ,قوم عاد اور قوم ثمود نے یہی کام کیا تھا'),
(2638, 22, 43, 'اور قوم ابراہیم اور قوم لوط نے بھی'),
(2639, 22, 44, 'اور اصحاب مدین نے بھی اور موسٰی کو بھی جھٹلایا گیا ہے تو ہم نے کفار کو مہلت دے دی اور پھر اس کے بعد انہیں اپنی گرفت میں لے لیا تو سب نے دیکھ لیا کہ ہمارا عذاب کیسا ہوتا ہے'),
(2640, 22, 45, 'غرض کتنی ہی بستیاں ہیں جنہیں ہم نے تباہ و برباد کردیا کہ وہ ظالم تھیں تو اب وہ اپنی چھتوں کے بل الٹی پڑی ہیں اور ان کے کنویں معطل پڑے ہیں اور ان کے مضبوط محل بھی مسمار ہوگئے ہیں'),
(2641, 22, 46, 'کیا ان لوگوں نے زمین میں سیر نہیں کی ہے کہ ان کے پاس ایسے دل ہوتے جو سمجھ سکتے اور ایسے کان ہوتے جو سن سکتے اس لئے کہ درحقیقت آنکھیں اندھی نہیں ہوتی ہیں بلکہ وہ دل اندھے ہوتے ہیں جو سینوں کے اندر پائے جاتے ہیں'),
(2642, 22, 47, 'پیغمبر علیھ السّلام یہ لوگ آپ سے عذاب میں عجلت کا تقاضا کررہے ہیں حالانکہ خدا اپنے وعدہ کے خلاف ہرگز نہیں کرسکتا ہے اور آپ کے پروردگار کے نزدیک ایک دن بھی ان کے شمار کے ہزار سال کے برابر ہوتا ہے'),
(2643, 22, 48, 'اور کتنی ہی بستیاں تھیں جنہیں ہم نے مہلت دی حالانکہ وہ ظالم تھیں پھر ہم نے انہیں اپنی گرفت میں لے لیا اور بالآخر سب کی بازگشت ہماری ہی طرف ہے'),
(2644, 22, 49, 'آپ کہہ دیجئے کہ انسانو ! میں تمہارے لئے صرف واضح طور پر ڈرانے والا ہوں'),
(2645, 22, 50, 'پھر جو لوگ ایمان لے آئے اور انہوں نے نیک اعمال کئے ہیں ان کے لئے مغفرت اور بہترین رزق ہے'),
(2646, 22, 51, 'اور جن لوگوں نے ہماری نشانیوں کے بارے میں کوشش کی کہ ہم کو عاجز کردیں وہ سب کے سب جہنمّ میں جانے والے ہیں'),
(2647, 22, 52, 'اور ہم نے آپ سے پہلے کوئی ایسا رسول یا نبی نہیں بھیجا ہے کہ جب بھی اس نے کوئی نیک آرزو کی تو شیطان نے اس کی آرزو کی راہ میں رکاوٹ ڈال دی تو پھر خدا نے شیطان کی ڈالی ہوئی رکاوٹ کو مٹا دیا اور پھر اپنی آیات کو مستحکم بنادیا کہ وہ بہت زیادہ جاننے والا اور صاحبِ حکمت ہے'),
(2648, 22, 53, 'تاکہ وہ شیطانی القائ کو ان لوگوں کے لئے آزمائش بنادے جن کے قلوب میں مرض ہے اور جن کے دل سخت ہوگئے ہیں اور ظالمین یقینا بہت دور رس نافرمانی میں پڑے ہوئے ہیں'),
(2649, 22, 54, 'اور اس لئے بھی کہ صاحبانِ علم کو معلوم ہوجائے کہ یہ وحی پروردگار کی طرف سے برحق ہے اور اس طرح وہ ایمان لے آئیں اور پھر ان کے دل اس کی بارگاہ میں عاجزی کا اظہار کریں اور یقینا اللہ ایمان لانے والوں کو سیدھے ر استہ کی طرف ہدایت کرنے والا ہے'),
(2650, 22, 55, 'اور یہ کفر اختیار کرنے والے ہمیشہ اس کی طرف سے شبہ ہی میں رہیں گے یہاں تک کہ اچانک ان کے پاس قیامت آجائے یا کسی منحوس دن کا عذاب وارد ہوجائے'),
(2651, 22, 56, 'آج کے دن ملک اللہ کے لئے ہے اور وہی ان سب کے درمیان فیصلہ کرے گا پھر جو لوگ ایمان لے آئے اور انہوں نے نیک اعمال کئے وہ نعمتوں والی جنّت میں رہیں گے'),
(2652, 22, 57, 'اور جن لوگوں نے کفر اختیار کیا اور ہماری آیتوں کی تکذیب کی ان کے لئے نہایت درجہ کا رسوا کن عذاب ہے'),
(2653, 22, 58, 'اور جن لوگوں نے راسِ خدا میں ہجرت کی اور پھر قتل ہوگئے یا انہیں موت آگئی تو یقینا خدا انہیں بہترین رزق عطا کرے گا کہ وہ بیشک بہترین رزق دینے والا ہے'),
(2654, 22, 59, 'وہ انہیں ایسی جگہ پہنچائے گا جسے وہ پسند کرتے ہوں گے اور اللہ بہت زیادہ جاننے والا اور برداشت کرنے والا ہے'),
(2655, 22, 60, 'یہ سب اپنے مقام پر ہے لیکن اس کے بعد جو دشمن کو اتنی ہی سزادے جتنا کہ اسے ستایا گیا ہے اور پھربھی اس پر ظلم کیا جائے تو خدا اس کی مدد ضرور کرے گا کہ وہ یقینا بہت معاف کرنے والا اور بخشنے والا ہے'),
(2656, 22, 61, 'یہ سب اس لئے ہے کہ خدا رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے اور اللہ بہت زیادہ سننے والا اور دیکھنے والا ہے'),
(2657, 22, 62, 'یہ اس لئے ہے کہ خدا ہی یقینا برحق ہے اور اس کے علاوہ جس کو بھی یہ لوگ پکارتے ہیں وہ سب باطل ہیں اور اللہ بہت زیادہ بلندی والا اور بزرگی اور عظمت والا ہے'),
(2658, 22, 63, 'کیا تم نے نہیں دیکھا کہ اللہ نے آسمان سے پانی برسایا تو اس سے زمین سرسبز وشاداب ہوجاتی ہے یقینا اللہ بہت زیادہ مہربان اور حالات کی خبر رکھنے والا ہے'),
(2659, 22, 64, 'آسمانوں اور زمین میں جو کچھ بھی ہے سب اسی کے لئے ہے اور یقینا وہ سب سے بے نیاز اور قابلِ حمد و ستائش ہے'),
(2660, 22, 65, 'کیا تم نے نہیں دیکھا کہ اللہ نے تمہارے لئے زمین کی تمام چیزوں کو لَسّخر کردیا ہے اور کشتیاں بھی دریا میں اسی کے حکم سے چلتی ہیں اور وہی آسمانوں کو روکے ہوئے ہے کہ اس کی اجازت کے بغیر زمین پر نہیں گر سکتا ہے-اللہ اپنے بندوں پر بڑا شفیق اور مہربان ہے'),
(2661, 22, 66, 'وہی خدا ہے جس نے تم کو حیات دی ہے اور پھر موت دے گا اور پھر زندہ کرے گا مگر انسان بڑا انکار کرنے والا اور ناشکرا ہے'),
(2662, 22, 67, 'ہر امّت کے لئے ایک طریقہ عبادت ہے جس پر وہ عمل کررہی ہے لہذا اس امر میں ان لوگوں کو آپ سے جھگڑا نہیں کرنا چاہئے اور آپ انہیں اپنے پروردگار کی طرف دعوت دیں کہ آپ بالکل سیدھی ہدایت کے راستہ پر ہیں'),
(2663, 22, 68, 'اور اگر یہ آپ سے جھگڑا کریں تو کہہ دیجئے کہ اللہ تمہارے اعمال سے خوب باخبر ہے'),
(2664, 22, 69, 'اللہ ہی تمہارے درمیان روزِ قیامت ان باتوں کا فیصلہ کرے گا جن باتوں میں تم اختلاف کررہے ہو'),
(2665, 22, 70, 'کیا تمہیں نہیں معلوم ہے کہ اللہ زمین اور آسمان کی تمام باتوں کو خوب جانتا ہے اور یہ سب باتیں کتاب میں محفوظ ہیں اور سب باتیں خدا کے لئے بہت آسان ہیں'),
(2666, 22, 71, 'اور یہ لوگ خدا کو چھوڑ کر ان کی پرستش کرتے ہیں جن کے بارے میں نہ خدا نے کوئی دلیل نازل کی ہے اور نہ خود انہیں کوئی علم ہے اور ظالمین کے لئے واقعا کوئی مددگار نہیں ہے'),
(2667, 22, 72, 'اور جب ان کے سامنے ہماری واضح آیتیں پڑھ کر سنائی جاتی ہیں تو تم دیکھتے ہو کہ کفر اختیار کرنے والوں کے چہرہ پر ناگواری کے آثار ظاہر ہوجاتے ہیں اور قریب ہوتا ہے کہ وہ ان تلاوت کرنے والوں پر حملہ کربیٹھیں تو کہہ دیجئے کہ میں اس سے بدتر بات کے بارے میں بتلا رہا ہوں اور وہ جہنمّ ہے جس کا خدا نے کافروں سے وعدہ کیا ہے اور وہ بہت برا انجام ہے'),
(2668, 22, 73, 'انسانوں تمہارے لئے ایک مثل بیان کی گئی ہے لہذا اسے غور سے سنو-یہ لوگ جنہیں تم خدا کو چھوڑ کر آواز دیتے ہو یہ سب مل بھی جائیں تو ایک مکھی نہیں پیدا کرسکتے ہیں اور اگر مکھی ان سے کوئی چیز چھین لے تو یہ اس سے چھڑا بھی نہیں سکتے ہیں کہ طالب اور مطلوب دونوں ہی کمزور ہیں'),
(2669, 22, 74, 'افسوس کہ ان لوگوں نے خدا کی واقعی قدر نہیں پہچانی اور بیشک اللہ بڑا قدرت والا اور سب پر غالب ہے'),
(2670, 22, 75, 'اللہ ملائکہ اور انسانوں میں سے اپنے نمائندے منتخب کرتا ہے اور وہ بڑا سننے والا اور خوب دیکھنے والا ہے'),
(2671, 22, 76, 'وہ ان کے سامنے اور پبُ پشت کی تمام باتوں کو جانتا ہے اور تمام امور اسی کی طرف پلٹ کر جانے والے ہیں'),
(2672, 22, 77, 'ایمان والو ! رکوع کروً سجدہ کرو اور اپنے رب کی عبادت کرو اور کارخیر انجام دو کہ شاید اسی طرح کامیاب ہوجاؤ اور نجات حاصل کرلو'),
(2673, 22, 78, 'اور اللہ کے بارے میں اس طرح جہاد کرو جو جہاد کرنے کا حق ہے کہ اس نے تمہیں منتخب کیا ہے اور دین میں کوئی زحمت نہیں قرار دی ہے یہی تمہارے بابا ابراہیم علیھ السّلام کا دین ہے اس نے تمہارا نام پہلے بھی اور اس قرآن میں بھی مسلم اور اطاعت گزار رکھا ہے تاکہ رسول تمہارے اوپر گواہ رہے اور تم لوگوں کے اعمال کے گواہ رہو لہذا اب تم نماز قائم کرو زکوِٰ اداکرو اور اللہ سے باقاعدہ طور پر وابستہ ہوجاؤ کہ وہی تمہارا مولا ہے اور وہی بہترین مولا اور بہترین مددگار ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'یقینا صاحبانِ ایمان کامیاب ہوگئے'),
(2675, 23, 2, 'جو اپنی نمازوں میں گڑگڑانے والے ہیں'),
(2676, 23, 3, 'اور لغو باتوں سے اعراض کرنے والے ہیں'),
(2677, 23, 4, 'اور زکوِ ادا کرنے والے ہیں'),
(2678, 23, 5, 'اور اپنی شرمگاہوں کی حفاظت کرنے والے ہیں'),
(2679, 23, 6, 'علاوہ اپنی بیویوں اور اپنے ہاتھوں کی ملکیت کنیزوں کے کہ ان کے معاملہ میں ان پر کوئی الزام آنے والا نہیں ہے'),
(2680, 23, 7, 'پھر اس کے علاوہ جو کوئی اور راستہ تلاش کرے گا وہ زیادتی کرنے والا ہوگا'),
(2681, 23, 8, 'اور جو مومنین اپنی امانتوں اور اپنے وعدوں کا لحاظ رکھنے والے ہیں'),
(2682, 23, 9, 'اور جو اپنی نمازوں کی پابندی کرنے والے ہیں'),
(2683, 23, 10, 'درحقیقت یہی وہ وارثان هجنتّ ہیں'),
(2684, 23, 11, 'جو فردوس کے وارث بنیں گے اور اس میں ہمیشہ ہمیشہ رہنے والے ہیں'),
(2685, 23, 12, 'اور ہم نے انسان کو گیلی مٹی سے پیدا کیا ہے'),
(2686, 23, 13, 'پھر اسے ایک محفوظ جگہ پر نطفہ بناکر رکھا ہے'),
(2687, 23, 14, 'پھر نطفہ کوعلقہ بنایا ہے اور پھر علقہ سے مضغہ پیدا کیا ہے اور پھر مضغہ سے ہڈیاں پیدا کی ہیں اور پھر ہڈیوں پر گوشت چڑھایا ہے پھر ہم نے اسے ایک دوسری مخلوق بنادیا ہے تو کس قدر بابرکت ہے وہ خدا جو سب سے بہتر خلق کرنے والا ہے'),
(2688, 23, 15, 'پھر اس کے بعد تم سب مرجانے والے ہو'),
(2689, 23, 16, 'پھر اس کے بعد تم روز هقیامت دوبارہ اٹھائے جاؤ گے'),
(2690, 23, 17, 'اور ہم نے تمہارے اوپر تہ بہ تہ سات آسمان بنائے ہیں اور ہم اپنی مخلوقات سے غافل نہیں ہیں'),
(2691, 23, 18, 'اور ہم نے آسمان سے ایک مخصوص مقدار میں پانی نازل کیا ہے اور پھر اسے زمین میں ٹھہرا دیا ہے جب کہ ہم اس کے واپس لے جانے پر بھی قادر تھے'),
(2692, 23, 19, 'پھر ہم نے اس پانی سے تمہارے لئے خرمے اور انگور کے باغات پیدا کئے ہیں جن میں بہت زیادہ میوے پائے جاتے ہیں اور تم ان ہی میں سے کچھ کھا بھی لیتے ہو'),
(2693, 23, 20, 'اور وہ درخت پیدا کیا ہے جو طور سینا میں پیدا ہوتا ہے اس سے تیل بھی نکلتا ہے اور وہ کھانے والوں کے لئے سالن بھی ہے'),
(2694, 23, 21, 'اور بیشک تمہارے لئے ان جانوروں میں بھی عبرت کا سامان ہے کہ ہم ان کے شکم میں سے تمہارے سیراب کرنے کا انتظام کرتے ہیںاور تمہارے لئے ان میں بہت سے فوائد ہیں اور ان میں سے بھی تم کھاتے ہو'),
(2695, 23, 22, 'اور تمہیں ان جانوروں پر اور کشتیوں پر سوار کیا جاتا ہے'),
(2696, 23, 23, 'اور ہم نے نوح کو ان کی قوم کی طرف بھیجا تو انہوں نے کہا کہ قوم والو خدا کی عبادت کرو کہ تمہارے لئے اس کے علاوہ کوئی خدا نہیں ہے تو تم اس سے کیوں نہیں ڈرتے ہو'),
(2697, 23, 24, 'ان کی قوم کے کافر رؤسائ نے کہا کہ یہ نوح تمہارے ہی جیسے انسان ہیں جو تم پر برتری حاصل کرنا چاہتے ہیں حالانکہ خدا چاہتا تو ملائکہ کو بھی نازل کرسکتا تھا. یہ تو ہم نے اپنے باپ دادا سے کبھی نہیں سنا ہے'),
(2698, 23, 25, 'درحقیقت یہ ایک ایسے انسان ہیں جنہیں جنون ہوگیا ہے لہذا چند دن ان کے حالات کا انتظار کرو'),
(2699, 23, 26, 'تو نوح نے دعا کی کہ پروردگار ان کی تکذیب کے مقابلہ میں میری مدد فرما'),
(2700, 23, 27, 'تو ہم نے ان کی طرف وحی کی کہ ہماری نگاہ کے سامنے اور ہمارے اشارہ کے مطابق کشتی بناؤ اور پھر جب ہمارا حکم آجائے اور تنور ابلنے لگے تو اسی کشتی میں ہر جوڑے میں سے دو دو کو لے لینا اور اپنے اہل کو لے کر روانہ ہوجانا علاوہ ان افراد کے جن کے بارے میں پہلے ہی ہمارا فیصلہ ہوچکا ہے اور مجھ سے ظلم کرنے والوں کے بارے میں گفتگو نہ کرنا کہ یہ سب غرق کردیئے جانے والے ہیں'),
(2701, 23, 28, 'اس کے بعد جب تم اپنے ساتھیوں سمیت کشتی پر اطمینان سے بیٹھ جانا تو کہنا کہ شکر ہے اس پروردگار کا جس نے ہم کو ظالم قوم سے نجات دلادی ہے'),
(2702, 23, 29, 'اور یہ کہنا کہ پروردگار ہم کو بابرکت منزل پر اتارنا کہ تو بہترین اتارنے والا ہے'),
(2703, 23, 30, 'اس امر میں ہماری بہت سی نشانیاں ہیں اور ہم تو بس امتحان لینے والے ہیں'),
(2704, 23, 31, 'اس کے بعد پھر ہم نے دوسری قوموں کو پیدا کیا'),
(2705, 23, 32, 'اور ان میں بھی اپنے رسول کو بھیجا کہ تم سب اللہ کی عبادت کرو کہ اس کے علاوہ تمہارا کوئی خدا نہیں ہے تو کیا تم پرہیزگار نہ بنو گے'),
(2706, 23, 33, 'تو ان کی قوم کے ان رؤسائ نے جنہوں نے کفر اختیار کیا تھا اور آخرت کی ملاقات کا انکار کردیا تھا اور ہم نے انہیں زندگانی دنیا میں عیش و عشرت کا سامان دے دیا تھا وہ کہنے لگے کہ یہ تو تمہارا ہی جیسا ایک بشر ہے جو تم کھاتے ہو وہی کھاتا ہے اور جو تم پیتے ہو وہی پیتا بھی ہے'),
(2707, 23, 34, 'اور اگر تم نے اپنے ہی جیسے بشر کی اطاعت کرلی تو یقینا خسارہ اٹھانے والے ہوجاؤ گے'),
(2708, 23, 35, 'کیا یہ تم سے اس بات کا وعدہ کرتا ہے کہ جب تم مرجاؤ گے اور خاک اور ہڈی ہوجاؤ گے تو پھر دوبارہ نکالے جاؤ گے'),
(2709, 23, 36, 'حیف صد حیف تم سے کس بات کا وعدہ کیا جارہا ہے'),
(2710, 23, 37, 'یہ تو صرف ایک زندگانی دنیا ہے جہاں ہم مریں گے اور جئیں گے اور دوبارہ زندہ ہونے والے نہیں ہیں'),
(2711, 23, 38, 'یہ ایک ایسا آدمی ہے جو خدا پر بہتان باندھتا ہے اور ہم اس پر ایمان لانے والے نہیں ہیں'),
(2712, 23, 39, 'اس رسول نے کہا کہ پروردگار تو ہماری مدد فرما کہ یہ سب ہماری تکذیب کررہے ہیں'),
(2713, 23, 40, 'ارشاد ہوا کہ عنقریب یہ لوگ پشیمان ہوجائیں گے'),
(2714, 23, 41, 'نتیجہ یہ ہوا کہ انہیں ایک برحق چنگھاڑنے اپنی گرفت میں لے لیا اور ہم نے انہیں کوڑا کرکٹ بنادیا کہ ظالم قوم کے لئے ہلاکت اور بربادی ہی ہے'),
(2715, 23, 42, 'پھر اس کے بعد ہم نے دوسری قوموں کو ایجاد کیا'),
(2716, 23, 43, 'کوئی امتّ نہ اپنے مقررہ وقت سے آگے بڑھ سکتی ہے اور نہ پیچھے ہٹ سکتی ہے'),
(2717, 23, 44, 'اس کے بعد ہم نے مسلسل رسول بھیجے اور جب کسی امتّ کے پاس کوئی رسول آیا تو اس نے رسول کی تکذیب کی اور ہم نے بھی سب کو عذاب کی منزل میں ایک کے پیچھے ایک لگادیا اور سب کو ایک افسانہ بناکر چھوڑ دیا کہ ہلاکت اس قوم کے لئے ہے جو ایمان نہیں لاتی ہے'),
(2718, 23, 45, 'پھر ہم نے موسٰی اور ان کے بھائی ہارون کو اپنی نشانیوں اور واضح دلیل کے ساتھ بھیجا'),
(2719, 23, 46, 'فرعون اور اس کے زعمائ مملکت کی طرف تو ان لوگوں نے بھی استکبار کیا اور وہ تو تھے ہی اونچے قسم کے لوگ'),
(2720, 23, 47, 'تو ان لوگوں نے کہہ دیا کہ کیا ہم اپنے ہی جیسے دو آدمیوں پر ایمان لے آئیں جب کہ ان کی قوم خود ہماری پرستش کررہی ہے'),
(2721, 23, 48, 'یہ کہہ کر دونوں کو جھٹلا دیا اور اس طرح ہلاک ہونے والوں میں شامل ہوگئے'),
(2722, 23, 49, 'اور ہم نے موسٰی کو کتاب دی کہ شاید اس طرح لوگ ہدایت حاصل کرلیں'),
(2723, 23, 50, 'اور ہم نے ابن مریم اور ان کی والدہ کو بھی اپنی نشانی قرار دیا اور انہیں ایک بلندی پر جہاں ٹھہرنے کی جگہ بھی تھی اور چشمہ بھی تھا پناہ دی'),
(2724, 23, 51, 'اے میرے رسولو ! تم پاکیزہ غذائیں کھاؤ اور نیک کام کرو کہ میں تمہارے اعمال سے خوب باخبر ہوں'),
(2725, 23, 52, 'اور تمہارا سب کا دین ایک دین ہے اور میں ہی سب کا پروردگار ہوں لہذا بس مجھ سے ڈرو'),
(2726, 23, 53, 'پھر یہ لوگ آپس میں ٹکڑے ٹکڑے ہوگئے اور ہر گروہ جو کچھ بھی اس کے پاس ہے اسی پر خوش اور مگن ہے'),
(2727, 23, 54, 'اب آپ انہیں ایک مدتّ کے لئے اسی گمراہی میں چھوڑ دیں'),
(2728, 23, 55, 'کیا ان کا خیال یہ ہے کہ ہم جو کچھ مال اور اولاد دے رہے ہیں'),
(2729, 23, 56, 'یہ ان کی نیکیوں میں عجلت کی جاری ہے . نہیں ہرگز نہیں انہیں تو حقیقت کا شعور بھی نہیں ہے'),
(2730, 23, 57, 'بیشک جو لوگ خوف پروردگار سے لرزاں رہتے ہیں'),
(2731, 23, 58, 'اور جو اپنے پروردگار کی نشانیوں پر ایمان رکھتے ہیں'),
(2732, 23, 59, 'اور جو کسی کو بھی اپنے رب کا شریک نہیں بناتے ہیں'),
(2733, 23, 60, 'اور وہ لوگ جو بقدر امکان راسِ خدا میں دیتے رہتے ہیں اور انہیں یہ خوف لگا رہتا ہے کہ پلٹ کر اسی کی بارگاہ میں جانے والے ہیں'),
(2734, 23, 61, 'یہی وہ لوگ ہیں جو نیکیوں میں سبقت کرنے والے ہیں اور سب کے آگے نکل جانے والے ہیں'),
(2735, 23, 62, 'اور ہم کسی نفس کو اس کی وسعت سے زیادہ تکلیف نہیں دیتے ہیں اور ہمارے پاس وہ کتاب ہے جو حق کے ساتھ بولتی ہے اور ان پر کوئی ظلم نہیں کیا جاتا ہے'),
(2736, 23, 63, 'بلکہ ان کے قلوب اس کی طرف سے بالکل جہالت میں ڈوبے ہوئے ہیں اور ان کے پاس دوسرے قسم کے اعمال ہیں جنہیں وہ انجام دے رہے ہیں'),
(2737, 23, 64, 'یہاں تک کہ جب ہم نے ان کے مالداروں کو عذاب کی گرفت میں لے لیا تو اب سب فریاد کررہے ہیں'),
(2738, 23, 65, 'اب آج واویلا نہ کرو آج ہماری طرف سے کوئی مدد نہیں کی جائے گی'),
(2739, 23, 66, 'جب ہماری آیتیں تمہارے سامنے پڑھی جاتی تھیں تو تم الٹے پاؤں واپس چلے جارہے تھے'),
(2740, 23, 67, 'اکڑتے ہوئے اور قصہّ کہتے اور بکتے ہوئے'),
(2741, 23, 68, 'کیا ان لوگوں نے ہماری بات پر غور نہیں کیا یا ان کے پاس کوئی چیز آگئی ہے جو ان کے باپ دادا کے پاس بھی نہیں آئی تھی'),
(2742, 23, 69, 'یا انہوں نے اپنے رسول کو پہچانا ہی نہیں ہے اور اسی لئے انکار کررہے ہیں'),
(2743, 23, 70, 'یا ان کا کہنا یہ ہے کہ رسول میں جنون پایا جاتا ہے جب کہ وہ ان کے پاس حق لے کر آیا ہے اور ان کی اکثریت حق کو ناپسند کرنے والی ہے'),
(2744, 23, 71, 'اور اگر حق ان کی خواہشات کا اتباع کرلیتا تو آسمان و زمین اور ان کے مابین جو کچھ بھی ہے سب برباد ہوجاتا بلکہ ہم نے ان کو ان ہی کا ذکر عطا کیا ہے اور یہ اپنے ہی ذکر سے اعراض کئے ہوئے ہیں'),
(2745, 23, 72, 'تو کیا آپ ان سے اپنا خرچ مانگ رہے ہیں ہرگز نہیں خدا کا دیا ہوا خراج آپ کے لئے بہت بہتر ہے اور وہ بہترین رزق دینے والا ہے'),
(2746, 23, 73, 'اور آپ تو انہیں سیدھے راستہ کی دعوت دینے والے ہیں'),
(2747, 23, 74, 'اور جو لوگ آخرت پر ایمان نہیں لاتے ہیں وہ سیدھے راستہ سے ہٹے ہوئے ہیں'),
(2748, 23, 75, 'اور اگر ہم ان پر رحم کریں اور ان کی تکلیف کو دور بھی کردیں تو بھی یہ اپنی سرکشی پر اڑے رہیں گے اور گمراہ ہی ہوتے جائیں گے'),
(2749, 23, 76, 'اور ہم نے انہیں عذاب کے ذریعہ پکڑا بھی مگر یہ نہ اپنے پروردگار کے سامنے جھکے اور نہ ہی گڑگڑاتے ہیں'),
(2750, 23, 77, 'یہاں تک کہ ہم نے شدید عذاب کا دروازہ کھول دیا تو اسی میں مایوس ہوکر بیٹھ رہے'),
(2751, 23, 78, 'وہ وہی خدا ہے جس نے تمہارے لئے کان اور آنکھیں اور دل بنائے ہیں مگر تم بہت کم شکریہ ادا کرتے ہو'),
(2752, 23, 79, 'اور اسی نے تمہیں ساری زمین میں پھیلادیا ہے اور اسی کی بارگاہ میں تم اکٹھا کئے جاؤ گے'),
(2753, 23, 80, 'وہی وہ ہے جو حیات وموت کا دینے والا ہے اور اسی کے اختیار میں دن اور رات کی آمدورفت ہے تو تم عقل کیوں نہیں استعمال کرتے ہو'),
(2754, 23, 81, 'بلکہ ان لوگوں نے بھی وہی کہہ دیا جو ان کے پہلے والوں نے کہا تھا'),
(2755, 23, 82, 'کہ اگر ہم مرگئے اور مٹی اور ہڈی ہوگئے تو کیا ہم دوبارہ اٹھائے جانے والے ہیں'),
(2756, 23, 83, 'بیشک ایسا ہی وعدہ ہم سے بھی کیا گیا ہے اور ہم سے پہلے والوں سے بھی کیا گیا ہے لیکن یہ صرف پرانے لوگوں کی کہانیاں ہیں'),
(2757, 23, 84, 'تو ذرا آپ پوچھئے کہ یہ زمین اور اس کی مخلوقات کس کے لئے ہے اگر تمہارے پاس کچھ بھی علم ہے'),
(2758, 23, 85, 'یہ فورا کہیں گے کہ اللہ کے لئے ہے تو کہئے کہ پھر سمجھتے کیوں نہیں ہو'),
(2759, 23, 86, 'پھر مزید کہئے کہ ساتوں آسمان اور عرش اعظم کامالک کون ہے'),
(2760, 23, 87, 'تو یہ پھر کہیں گے کہ اللہ ہی ہے تو کہئے کہ آخر اس سے ڈرتے کیوں نہیں ہو'),
(2761, 23, 88, 'پھر کہئے کہ ہر شے کی حکومت کس کے اختیار میں ہے کہ وہی پناہ دیتا ہے اور اسی کے عذاب سے کوئی پناہ دینے والا نہیں ہے اگر تمہارا پاس کوئی بھی علم ہے'),
(2762, 23, 89, 'تو یہ فورا جواب دیں گے کہ یہ سب اللہ کے لئے ہے تو کہئے کہ آخر پھر تم پر کون سا جادو کیا جارہا ہے'),
(2763, 23, 90, 'بلکہ ہم ان کے پاس حق لے کر آئے ہیں اور یہ سب جھوٹے ہیں'),
(2764, 23, 91, 'یقینا خدا نے کسی کو فرزند نہیں بنایا ہے اور نہ اس کے ساتھ کوئی دوسرا خدا ہے ورنہ ہر خدا اپنی مخلوقات کو لے کر الگ ہوجاتا اور ہر ایک دوسرے پر برتری کی فکر کرتا اور کائنات تباہ و برباد ہوجاتی-اللرُ ان تمام باتوں سے پاک و پاکیزہ ہے'),
(2765, 23, 92, 'وہ حاضر و غائب سب کا جاننے والا ہے اور ان سب کے شرک سے بلند و بالاتر ہے'),
(2766, 23, 93, 'اور آپ کہئے کہ پروردگار اگر جس عذاب کا ان سے وعدہ کیا ہے مجھے دکھا بھی دینا'),
(2767, 23, 94, 'تو پروردگار مجھے اس ظالم قوم کے ساتھ نہ قرار دینا'),
(2768, 23, 95, 'اور ہم جس عذاب کا ان سے وعدہ کررہے ہیں اسے آپ کو دکھادینے کی قدرت بھی رکھتے ہیں'),
(2769, 23, 96, 'اور آپ برائی کو اچھائی کے ذریعہ رفع کیجئے کہ ہم ان کی باتوں کو خوب جانتے ہیں'),
(2770, 23, 97, 'اور کہئے کہ پروردگار میں شیطانوں کے وسوسوں سے تیری پناہ چاہتا ہوں'),
(2771, 23, 98, 'اور اس بات سے پناہ مانگتا ہوں کہ شیاطین میرے پاس آجائیں'),
(2772, 23, 99, 'یہاں تک کہ جب ان میں سے کسی کی موت آگئی تو کہنے لگا کہ پروردگار مجھے پلٹا دے'),
(2773, 23, 100, 'شاید میں اب کوئی نیک عمل انجام دوں. ہرگز نہیں یہ ایک بات ہے جو یہ کہہ رہا ہے اور ان کے پیچھے ایک عالم ه برزخ ہے جو قیامت کے دن تک قائم رہنے والا ہے'),
(2774, 23, 101, 'پھر جب صور پھونکا جائے گا تو نہ رشتہ داریاں ہوں گی اور نہ آپس میں کوئی ایک دوسرے کے حالات پوچھے گا'),
(2775, 23, 102, 'پھر جن کی نیکیوں کا پلہّ بھاری ہوگا وہ کامیاب اور نجات پانے والے ہوں گے'),
(2776, 23, 103, 'اور جن کی نیکیوں کا پلہّ ہلکا ہوگا وہ وہی لوگ ہوں گے جنہوں نے اپنے نفس کو خسارہ میں ڈال دیا ہے اور وہ جہنم ّ میں ہمیشہ ہمیشہ رہنے والے ہیں'),
(2777, 23, 104, 'جہنمّ کی آگ ان کے چہروں کو جھلس دے گی اور وہ اسی میں منہ بنائے ہوئے ہوں گے'),
(2778, 23, 105, 'کیا ایسا نہیں ہے کہ جب ہماری آیتیں تمہاری سامنے پڑھی جاتی تھیں تو تم ان کی تکذیب کیا کرتے تھے'),
(2779, 23, 106, 'وہ لوگ کہیں گے کہ پروردگار ہم پر بدبختی غالب آگئی تھی اور ہم گمراہ ہوگئے تھے'),
(2780, 23, 107, 'پروردگار اب ہمیں جہنمّ سے نکال دے اس کے بعد دوبارہ گناہ کریں تو ہم واقعی ظالم ہیں'),
(2781, 23, 108, 'ارشاد ہوگا کہ اب اسی میں ذلتّ کے ساتھ پڑے رہو اور بات نہ کرو'),
(2782, 23, 109, 'ہمارے بندوں میں ایک گروہ ایسا بھی تھا جن کا کہنا تھا کہ پروردگار ہم ایمان لائے ہیں لہذا ہمارے گناہوں کو معاف کردے اور ہم پر رحم فرما کہ تو بہترین رحم کرنے والا ہے'),
(2783, 23, 110, 'تو تم لوگوں نے ان کو بالکل مذاق بنالیا تھا یہاں تک کہ اس مذاق نے تمہیں ہماری یاد سے بالکل غافل بنادیا اور تم ان کا مضحکہ ہی اڑاتے رہ گئے'),
(2784, 23, 111, 'آج کے دن ہم نے ان کو یہ جزا دی ہے کہ وہ سراسر کامیاب ہیں'),
(2785, 23, 112, 'پھر خدا پوچھے گا کہ تم روئے زمین پر کتنے سال رہے'),
(2786, 23, 113, 'وہ جواب دیں گے کہ ایک دن یا اس کا بھی ایک ہی حصہّ اسے تو شمار کرنے والوں سے دریافت کرلے'),
(2787, 23, 114, 'ارشاد ہوگا کہ بیشک تم بہت مختصر رہے ہو اور کاش تمہیں اس کا ادراک ہوتا'),
(2788, 23, 115, 'کیا تمہارا خیال یہ تھا کہ ہم نے تمہیں بیکار پیدا کیا ہے اور تم ہماری طرف پلٹا کر نہیں لائے جاؤ گے'),
(2789, 23, 116, 'پس بلند و بالا ہے وہ خدا جو بادشاہ برحق ہے اور اس کے علاوہ کوئی خدا نہیں ہے اور وہ عرش کریم کا پروردگار ہے'),
(2790, 23, 117, 'اور جو اللہ کے ساتھ کسی اور خدا کو پکارے گا جس کی کوئی دلیل بھی نہیں ہے تو اس کا حساب پروردگار کے پاس ہے اور کافروں کے لئے نجات بہرحال نہیں ہے'),
(2791, 23, 118, 'اور پیغمبر علیھ السّلام آپ کہئے کہ پروردگار میری مغفرت فرما اور مجھ پر رحم کر کہ تو بہترین رحم کرنے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'یہ ایک سورہ ہے جسے ہم نے نازل کیا ہے اور فرض کیا ہے اور اس میں کھلی ہوئی نشانیاں نازل کی ہیں کہ شاید تم لوگ نصیحت حاصل کرسکو'),
(2793, 24, 2, 'زناکار عورت اور زنا کار مرد دونوں کو سو سوکوڑے لگاؤ اور خبردار دین خدا کے معاملہ میں کسی مروت کا شکار نہ ہوجانا اگر تمہارا ایمان اللہ اور روزآخرت پر ہے اور اس سزا کے وقت مومنین کی ایک جماعت کو حاضر رہنا چاہئے'),
(2794, 24, 3, 'زانی مرد اور زانیہ یا مشرکہ عورت ہی سے نکاح کرے گا اور زانیہ عورت زانی مرد یا مشرک مرد ہی سے نکاح کرے گی کہ یہ صاحبان هایمان پر حرام ہیں'),
(2795, 24, 4, 'اور جو لوگ پاک دامن عورتوں پر زنا کی تہمت لگاتے ہیں اور چار گواہ فراہم نہیں کرتے ہیں انہیں اسیّ کوڑے لگاؤ اور پھر کبھی ان کی گواہی قبول نہ کرنا کہ یہ لوگ سراسر فاسق ہیں'),
(2796, 24, 5, 'علاوہ ان افراد کے جو اس کے بعد توبہ کرلیں اور اپنے نفس کی اصلاح کرلیں کہ اللہ بہت زیادہ بخشنے والا اور مہربان ہے'),
(2797, 24, 6, 'اور جو لوگ اپنی بیویوں پر تہمت لگاتے ہیں اور ان کے پاس اپنے علاوہ کوئی گواہ نہیں ہوتا ہے تو ان کی اپنی گواہی چار گواہیوں کے برابر ہوگی اگر وہ چار مرتبہ قسم کھاکر کہیں کہ وہ سچےّ ہیں'),
(2798, 24, 7, 'اور پانچویں مرتبہ یہ کہیں کہ اگر وہ جھوٹے ہیں تو ان پر خدا کی لعنت ہے'),
(2799, 24, 8, 'پھر عورت سے بھی حد برطرف ہوسکتی ہے اگر وہ چار مرتبہ قسم کھاکر یہ کہے کہ یہ مرد جھوٹوں میں سے ہے'),
(2800, 24, 9, 'اور پانچویں مرتبہ یہ کہے کہ اگر وہ صادقین میں سے ہے تو مجھ پر خدا کا غضب ہے'),
(2801, 24, 10, 'اور اگر تم پر خدا کا فضل اور اس کی رحمت نہ ہوتی اور وہ توبہ قبول کرنے والا صاحب هحکمت نہ ہوتا تو اس تہمت کا انجام بہت برا ہوتا'),
(2802, 24, 11, 'بیشک جن لوگوں نے زنا کی تہمت لگائی وہ تم ہی میں سے ایک گروہ تھا تم اسے اپنے حق میں شر نہ سمجھو یہ تمہارے حق میں خیر ہے اور ہر شخص کے لئے اتنا ہی گناہ ہے جو اس نے خود کمایا ہے اور ان میں سے جس نے بڑا حصہّ لیا ہے اس کے لئے بڑا عذاب ہے'),
(2803, 24, 12, 'آخر ایسا کیوں نہ ہوا کہ جب تم لوگوں نے اس تہمت کو سناُ تھا تو مومنین و مومنات اپنے بارے میں خیر کا گمان کرتے اور کہتے کہ یہ تو کھلا ہوا بہتان ہے'),
(2804, 24, 13, 'پھر ایسا کیوں نہ ہوا کہ یہ چار گواہ بھی لے آتے اور جب گواہ نہیں لے آئے تو یہ اللہ کے نزدیک بالکل جھوٹے ہیں'),
(2805, 24, 14, 'اور اگر خدا کا فضل دنیا و آخرت میں اور اس کی رحمت نہ ہوتی تو جو چرچا تم نے کیا تھا اس میں تمہیں بہت بڑا عذاب گرفت میں لے لیتا'),
(2806, 24, 15, 'جب تم اپنی زبان سے چرچا کررہے تھے اور اپنے منہ سے وہ بات نکال رہے تھے جس کا تمہیں علم بھی نہیں تھا اور تم اسے بہت معمولی بات سمجھ رہے تھے حالانکہ اللہ کے نزدیک وہ بہت بڑی بات تھی'),
(2807, 24, 16, 'اور کیوں نہ ایسا ہوا کہ جب تم لوگوں نے اس بات کو سنا تھا تو کہتے کہ ہمیں ایسی بات کہنے کا کوئی حق نہیں ہے خدایا تو پاک و بے نیاز ہے اور یہ بہت بڑا بہتان ہے'),
(2808, 24, 17, 'اللرُتم کو نصیحت کرتا ہے کہ اگر تم صاحبِ ایمان ہو تو اب ایسی حرکت دوبارہ ہرگز نہ کرنا'),
(2809, 24, 18, 'اور اللہ تمہارے لئے اپنی نشانیوں کو واضح کرکے بیان کرتا ہے اور وہ صاحبِ علم بھی ہے اور صاحبِ حکمت بھی ہے'),
(2810, 24, 19, 'جو لوگ یہ چاہتے ہیں کہ صاحبانِ ایمان میں بدکاری کا چرچا پھیل جائے ان کے لئے بڑا دردناک عذاب ہے دنیا میں بھی اور آخرت میں بھی اور اللہ سب کچھ جانتا ہے صرف تم نہیں جانتے ہو'),
(2811, 24, 20, 'اور اگر اللہ کا فضل اور اس کی رحمت تمہارے شامل حال نہ ہوتی اور وہ شفیق اور مہربان نہ ہوتا'),
(2812, 24, 21, 'ایمان والو شیطان کے نقش قدم پر نہ چلنا کہ جو شیطان کے قدم بہ قدم چلے گا اسے شیطان ہر طرح کی برائی کا حکم دے گا اور اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی تو تم میں کوئی بھی پاکباز نہ ہوتا لیکن اللہ جسے چاہتا ہے پاک و پاکیزہ بنادیتا ہے اور وہ ہر ایک کی سننے والا اور ہر ایک کے حال هه دل کا جاننے والا ہے'),
(2813, 24, 22, 'اور خبردار تم میں سے کوئی شخص بھی جسے خدا نے فضل اور وسعت عطا کی ہے یہ قسم نہ کھالے کہ قرابتداروں اور مسکینوں اور راسِ خدا میں ہجرت کرنے والوں کے ساتھ کوئی سلوک نہ کرے گا. ہر ایک کو معاف کرنا چاہئے اور درگزر کرنا چاہئے کیا تم یہ نہیں چاہتے ہو کہ خدا تمہارے گناہوں کو بخش دے اور اللہ بیشک بڑا بخشنے والا اور مہربان ہے'),
(2814, 24, 23, 'یقینا جو لوگ پاکباز اور بے خبر مومن عورتوں پر تہمت لگاتے ہیں ان پر دنیا اور آخرت دونوں جگہ لعنت کی گئی ہے اور ان کے لئے عذابِ عظیم ہے'),
(2815, 24, 24, 'قیامت کے دن ان کے خلاف ان کی زبانیں اور ان کے ہاتھ پاؤں سب گواہی دیں گے کہ یہ کیا کررہے تھے'),
(2816, 24, 25, 'اس دن خدا سب کو پورا پورا بدلہ دے گا اور لوگوں کو معلوم ہوجائے گا کہ خدا یقینا برحق اور حق کا ظاہر کرنے والا ہے'),
(2817, 24, 26, 'خبیث چیزیں خبیث لوگوں کے لئے ہیں اور خبیث افراد خبیث باتوں کے لئے ہیں اور پاکیزہ باتیں پاکیزہ لوگوں کے لئے ہیں اور پاکیزہ افراد پاکیزہ باتوں کے لئے ہیں یہ پاکیزہ لوگ خبیث لوگوں کے اتہامات سے پاک و پاکیزہ ہیں اور ان کے لئے مغفرت اور باعزّت رزق ہے'),
(2818, 24, 27, 'ایمان والو خبردار اپنے گھروں کے علاوہ کسی کے گھر میں داخل نہ ہونا جب تک کہ صاحبِ خانہ سے اجازت نہ لے لو اور انہیں سلام نہ کرلو یہی تمہارے حق میں بہتر ہے کہ شاید تم اس سے نصیحت حاصل کرسکو'),
(2819, 24, 28, 'پھر اگر گھر میں کوئی نہ ملے تو اس وقت تک داخل نہ ہونا جب تک اجازت نہ مل جائے اور اگر تم سے کہا جائے کہ واپس چلے جاؤ تو واپس چلے جانا کہ یہی تمہارے لئے زیادہ پاکیزہ امر ہے اور اللہ تمہارے اعمال سے خوب باخبر ہے'),
(2820, 24, 29, 'تمہارے لئے کوئی حرج نہیں ہے کہ تم ایسے مکانات میں جو غیر آباد ہیں اور جن میں تمہارا کوئی سامان ہے داخل ہوجاؤ اور اللہ اس کو بھی جانتا ہے جس کا تم اظہار کرتے ہو اور اس کو بھی جانتا ہے جس کی تم پردہ پوشی کرتے ہو'),
(2821, 24, 30, 'اور پیغمبر علیھ السّلام آپ مومنین سے کہہ دیجئے کہ اپنی نگاہوں کو نیچی رکھیں اور اپنی شرمگاہوں کی حفاظت کریں کہ یہی زیادہ پاکیزہ بات ہے اور بیشک اللہ ان کے کاروبار سے خوب باخبر ہے'),
(2822, 24, 31, 'اور مومنات سے کہہ دیجئے کہ وہ بھی اپنی نگاہوں کو نیچا رکھیں اور اپنی عفت کی حفاظت کریں اور اپنی زینت کا اظہار نہ کریں علاوہ اس کے جو ازخود ظاہر ہے اور اپنے دوپٹہ کو اپنے گریبان پر رکھیں اور اپنی زینت کو اپنے باپ دادا.شوہر. شوہر کے باپ دادا .اپنی اولاد ,اور اپنے شوہر کی اولاد اپنے بھائی اور بھائیوں کی اولاد اور بہنوں کی اولاد اور اپنی عورتوں اور اپنے غلام اور کنیزوں اور ایسے تابع افراد جن میں عورت کی طرف سے کوئی خواہش نہیں رہ گئی ہے اور وہ بچےّ جو عورتوں کے پردہ کی بات سے کوئی سروکار نہیں رکھتے ہیں ان سب کے علاوہ کسی پر ظاہر نہ کریں اور خبردار اپنے پاؤں پٹک کر نہ چلیں کہ جس زینت کو چھپائے ہوئے ہیں اس کا اظہار ہوجائے اور صاحبانِ ایمان تم سب اللہ کی بارگاہ میں توبہ کرتے رہو کہ شاید اسی طرح تمہیں فلاح اور نجات حاصل ہوجائے'),
(2823, 24, 32, 'اور اپنے غیر شادی شدہ آزاد افراد اور اپنے غلاموں اور کنیزوں میں سے باصلاحیت افراد کے نکاح کا اہتمام کرو کہ اگر وہ فقیر بھی ہوں گے تو خدا اپنے فضل و کرم سے انہیں مالدار بنادے گا کہ خدا بڑی وسعت والا اور صاحب هعلم ہے'),
(2824, 24, 33, 'اور جو لوگ نکاح کی وسعت نہیں رکھتے ہیں وہ بھی اپنی عفت کا تحفظ کریں یہاں تک کہ خدا اپنے فضل سے انہیں غنی بنا دے اور جو غلام و کنیز مکاتبت کے طلبگار ہیں ان میں خیر دیکھو تو ان سے مکاتبت کرلو بلکہ جو مال خدا نے تمہیں دے رکھا ہے اس میں سے کچھ انہیں بھی دے دو اور خبردار اپنی کنیزوں کو اگر وہ پاکدامنی کی خواہشمند ہیں تو زنا پر مجبور نہ کرنا کہ ان سے زندگانی دنیا کا فائدہ حاصل کرنا چاہو کہ جو بھی انہیں مجبور کرے گا خدا مجبوری کے بعد ان عورتوں کے حق میں بہت زیادہ بخشنے والا اور مہربان ہے'),
(2825, 24, 34, 'اور ہم نے تمہاری طرف اپنی کھلیِ ہوئی نشانیاں اور تم سے پہلے گزر جانے والوں کی مثال اور صاحبانِ تقویٰ کے لئے نصیحت کا سامان نازل کیا ہے'),
(2826, 24, 35, 'اللرُ آسمانوں اور زمین کا نور ہے .اس کے نور کی مثال اس طاق کی ہے جس میں چراغ ہو اور چراغ شیشہ کی قندیل میں ہو اور قندیل ایک جگمگاتے ستارے کے مانند ہو جو زیتون کے بابرکت درخت سے روشن کیا جائے جو نہ مشرق والا ہو نہ مغرب والا اور قریب ہے کہ اس کا روغن بھڑک اٹھے چاہے اسے آگ مس بھی نہ کرے یہ نور بالائے نور ہے اور اللہ اپنے نور کے لئے جسے چاہتا ہے ہدایت دے دیتا ہے اور اسی طرح مثالیں بیان کرتا ہے اور وہ ہر شے کا جاننے والا ہے'),
(2827, 24, 36, 'یہ چراغ ان گھروں میں ہے جن کے بارے میں خدا کا حکم ہے کہ ان کی بلندی کا اعتراف کیا جائے اور ان میں اس کے نام کا ذکر کیا جائے کہ ان گھروں میں صبح و شام اس کی تسبیح کرنے والے ہیں'),
(2828, 24, 37, 'وہ مرد جنہیں کاروبار یا دیگر خرید و فروخت ذکر خدا, قیام نماز اور ادائے زکٰوِ سے غافل نہیں کرسکتی یہ اس دن سے ڈرتے ہیں جس دن کے ہول سے دل اور نگاہیں سب الٹ جائیں گی'),
(2829, 24, 38, 'تاکہ خدا انہیں ان کے بہترین اعمال کی جزا دے سکے اور اپنے فضل سے مزید اضافہ کرسکے اور خدا جسے چاہتا ہے رزق بے حساب عطا کرتا ہے'),
(2830, 24, 39, 'اور جن لوگوں نے کفر اختیار کرلیا ان کے اعمال اس ریت کے مانند ہیں جو چٹیل میدان میں ہو اور پیاسا اسے دیکھ کر پانی تصور کرے اور جب اس کے قریب پہنچے تو کچھ نہ پائے بلکہ اس خدا کو پائے جو اس کا پورا پورا حساب کردے کہ اللہ بہت جلد حساب کرنے والا ہے'),
(2831, 24, 40, 'یا ان اعمال کی مثال اس گہرے دریا کی تاریکیوں کی ہے جسے ایک کے اوپر ایک لہر ڈھانک لے اور اس کے اوپر تہ بہ تہ بادل بھی ہوں کہ جب وہ اپنے ہاتھ کو نکالے تو تاریکی کی بنا پر کچھ نظر نہ آئے اور جس کے لئے خدا نور نہ قرار دے اس کے لئے کوئی نور نہیں ہے'),
(2832, 24, 41, 'کیا تم نے نہیں دیکھا کہ اللہ کے لئے زمین و آسمان کی تمام مخلوقات اور فضا کے صف بستہ طائر سب تسبیح کررہے ہیں اور سب اپنی اپنی نماز اور تسبیح سے باخبر ہیں اور اللہ بھی ان کے اعمال سے خوب باخبر ہے'),
(2833, 24, 42, 'اور اللہ ہی کے لئے زمین و آسمان کی ملکیت ہے اور اسی کی طرف سب کی بازگشت ہے'),
(2834, 24, 43, 'کیا تم نے نہیں دیکھا کہ خدا ہی ابر کو چلاتا ہے اور پھر انہیں آپس میں جوڑ کر تہ بہ تہ بنا دیتاہے پھر تم دیکھو گے کہ اس کے درمیان سے بارش نکل رہی ہے اور وہ اسے آسمان سے برف کے پہاڑوں کے درمیان سے برساتا ہے پھر جس تک چاہتا ہے پہنچادیتا ہے اور جس کی طرف سے چاہتا ہے موڑ دیتا ہے اس کی بجلی کی چمک اتنی تیز ہے کہ قریب ہے کہ آنکھوں کی بینائی ختم کردے'),
(2835, 24, 44, 'اللرُ ہی رات اور دن کو الٹ پلٹ کرتا رہتا ہے اور یقینا اس میں صاحبانِ بصارت کے لئے سامانِ عبرت ہے'),
(2836, 24, 45, 'اور اللہ ہی نے ہر زمین پر چلنے والے کو پانی سے پیدا کیا ہے پھر ان میں سے بعض پیٹ کے بل چلتے ہیں اور بعض دو پیروں میں چلتے ہیں اور بعض چاروں ہاتھ پیر سے چلتے ہیں اور اللہ جو چاہتا ہے پیدا کرتا ہے کہ وہ ہر شے پر قدرت رکھنے والا ہے'),
(2837, 24, 46, 'یقینا ہم نے واضح کرنے والی آیتیں نازل کی ہیں اور اللہ جسے چاہتا ہے صراظُ مستقیم کی ہدایت دیدیتا ہے'),
(2838, 24, 47, 'اور یہ لوگ کہتے ہیں کہ ہم اللہ اور رسول پر ایمان لے آئے ہیں اور ان کی اطاعت کی ہے اور اس کے بعد ان میں سے ایک فریق منہ پھیرلیتا ہے اور یہ واقعا صاحبانِ ایمان نہیں ہیں'),
(2839, 24, 48, 'اور جب انہیں خدا و رسول کی طرف بلایا جاتا ہے کہ وہ ان کے درمیان فیصلہ کردیں تو ان میں سے ایک فریق کنارہ کش ہوجاتا ہے'),
(2840, 24, 49, 'حالانکہ اگر حق ان کے ساتھ ہوتا تو یہ سر جھکائے ہوئے چلے آتے'),
(2841, 24, 50, 'تو کیا ان کے دلوں میں کفر کی بیماری ہے یا یہ شک میں مبتلا ہوگئے ہیں یا انہیں یہ خوف ہے کہ خدا اور رسول ان پر ظلم کریں گے حالانکہ حقیقت یہ ہے کہ یہ لوگ خود ہی ظالمین ہیں'),
(2842, 24, 51, 'مومنین کو تو خدا اور رسول کی طرف بلایا جاتا ہے کہ وہ فیصلہ کریں گے تو ان کا قول صرف یہ ہوتا ہے کہ ہم نے سنا اور اطاعت کی اور یہی لوگ درحقیقت فلاح پانے والے ہیں'),
(2843, 24, 52, 'اور جو بھی اللہ اور رسول کی اطاعت کرے گا اور اس کے دل میں خوف هخدا ہوگا اور وہ پرہیزگاری اختیار کرے گا تو وہی کامیاب کہا جائے گا'),
(2844, 24, 53, 'اور ان لوگوں نے باقاعدہ قسم کھائی ہے کہ آپ حکم دے دیں گے تو یہ گھر سے باہر نکل جائیں گے تو آپ کہہ دیجئے کہ قسم کی ضرورت نہیں ہے عمومی قانون کے مطابق اطاعت کافی ہے کہ یقینا اللرُ ان اعمال سے باخبر ہے جو تم لوگ انجام دے رہے ہو'),
(2845, 24, 54, 'آپ کہہ دیجئے کہ اللہ اور اس کے رسول کی اطاعت کرو پھر اگر انحراف کرو گے تو رسول پر وہ ذمہ داری ہے جو اس کے ذمہ رکھی گئی ہے اور تم پر وہ مسئولیت ہے جو تمہارے ذمہ رکھی گئی ہے اور اگر تم اطاعت کرو گے تو ہدایت پاجاؤ گے اور رسول کے ذمہ واضح تبلیغ کے سوا اور کچھ نہیں ہے'),
(2846, 24, 55, 'اللرُ نے تم میں سے صاحبان هایمان و عمل صالح سے وعدہ کیا ہے کہ انہیں روئے زمین میں اسی طرح اپنا خلیفہ بنائے گا جس طرح پہلے والوں کو بنایا ہے اور ان کے لئے اس دین کو غالب بنائے گا جسے ان کے لئے پسندیدہ قرار دیا ہے اور ان کے خوف کو امن سے تبدیل کردے گا کہ وہ سب صرف میری عبادت کریں گے اور کسی طرح کا شرک نہ کریں گے اور اس کے بعد بھی کوئی کافر ہوجائے تو درحقیقت وہی لوگ فاسق اور بدکردار ہیں'),
(2847, 24, 56, 'اور نماز قائم کرو زکٰوِ ادا کرو اور رسول کی اطاعت کرو کہ شاید اسی طرح تمہارے حال پر رحم کیا جائے'),
(2848, 24, 57, 'خبردار یہ خیال نہ کرنا کہ کفر اختیار کرنے والے زمین میں ہم کو عاجز کردینے والے ہیں ان کا ٹھکانا جہنمّ ہے اور وہ بدترین انجام ہے'),
(2849, 24, 58, 'ایمان والو تمہارے غلام و کنیز اور وہ بچے ّجو ابھی سن بلوغ کو نہیں پہنچے ہیں ان سب کو چاہئے کہ تمہارے پاس داخل ہونے کے لئے تین اوقات میں اجازت لیں نماز صبح سے پہلے اور دوپہر کے وقت جب تم کپڑے اتار کر آرام کرتے ہو اور نماز عشائ کے بعد یہ تین اوقات پردے کے ہیں اس کے بعد تمہارے لئے یا ان کے لئے کوئی حرج نہیں ہے کہ ایک دوسرے کے پاس چکر لگاتے رہیں کہ اللہ اسی طرح اپنی آیتوں کو واضح کرکے بیان کرتا ہے اور بیشک اللہ ہر شے کا جاننے والا اور صاحبِ حکمت ہے'),
(2850, 24, 59, 'اور جب تمہارے بّچے حد بلوغ کو پہنچ جائیں تو وہ بھی اسی طرح اجازت لیں جس طرح پہلے والے اجازت لیا کرتے تھے پروردگار اسی طرح تمہارے لئے اپنی آیتوں کو واضح کرکے بیان کرتا ہے کہ وہ صاحب هعلم بھی ہے اور صاحبِ حکمت بھی ہے'),
(2851, 24, 60, 'اور ضعیفی سے بیٹھ رہنے والی عورتیں جنہیں نکاح سے کوئی دلچسپی نہیں ہے ان کے لئے کوئی حرج نہیں ہے کہ وہ اپنے ظاہری کپڑوں کو الگ کردیں بشرطیکہ زینت کی نمائش نہ کریں اور وہ بھی عفت کا تحفظ کرتی رہیں کہ یہی ان کے حق میں بھی بہتر ہے اور اللہ سب کی سننے والا اور سب کا حال جاننے والا ہے'),
(2852, 24, 61, 'نابینا کے لئے کوئی حرج نہیں ہے اور لنگڑے آدمی کے لئے بھی کوئی حرج نہیں ہے اور مریض کے لئے بھی کوئی عیب نہیں ہے اور خود تمہارے لئے بھی کوئی گناہ نہیں ہے کہ اپنے گھروں سے یا اپنے باپ دادا کے گھروں سے یا اپنی ماں اور نانی دادی کے گھروں سے یا اپنے بھائیوں کے گھروں سے یا اپنی بہنوں کے گھروں سے یا اپنے چچاؤں کے گھروں سے یا اپنی پھوپھیوں کے گھروں سے یا اپنے ماموؤں کے گھروں سے یا اپنی خالاؤں کے گھروں سے یا جن گھروں کی کنجیاں تمہارے اختیار میں ہیں یا اپنے دوستوں کے گھروں سے کھانا کھالو .... اور تمہارے لئے اس میں بھی کوئی حرج نہیں ہے کہ سب مل کر کھاؤ یا متفرق طریقہ سے کھاؤ بس جب گھروں میں داخل ہو تو کم از کم اپنے ہی اوپر سلام کرلو کہ یہ پروردگار کی طرف سے نہایت ہی مبارک اور پاکیزہ تحفہ ہے اور پروردگار اسی طرح اپنی آیتوں کو واضح طریقہ سے بیان کرتا ہے کہ شاید تم عقل سے کام لے سکو'),
(2853, 24, 62, 'مومنین صرف وہ افراد ہیں جو خدا اور رسول پر ایمان رکھتے ہوں اور جب کسی اجتماعی کام میں مصروف ہوں تو اس وقت تک کہیں نہ جائیں جب تک اجازت حاصل نہ ہوجائے بیشک جو لوگ آپ سے اجازت حاصل کرتے ہیں وہی اللہ اور رسول پر ایمان رکھتے ہیں لہذا جب آپ سے کسی خاص حالت کے لئے اجازت طلب کریں تو آپ جس کو چاہیں اجازت دے دیں اور ان کے حق میں اللہ سے استغفار بھی کریں کہ اللہ بڑا غفور اور رحیم ہے'),
(2854, 24, 63, 'مسلمانو ! خبردار رسول کو اس طرح نہ پکارا کرو جس طرح آپس میں ایک دوسرے کو پکارتے ہو اللہ ان لوگوں کو خوب جانتا ہے جو تم میں سے خاموشی سے کھسک جاتے ہیں لہذا جو لوگ حکهِ خدا کی مخالفت کرتے ہیں وہ اس امر سے ڈریں کہ ان تک کوئی فتنہ پہنچ جائے یا کوئی دردناک عذاب نازل ہوجائے'),
(2855, 24, 64, 'اور یاد رکھو کہ اللہ ہی کے لئے زمین و آسمان کی کل کائنات ہے اور وہ تمہارے حالات کو خوب جانتا ہے اور جس دن سب اس کی بارگاہ میں پلٹا کر لائے جائیں گے وہ سب کو ان کے اعمال کے بارے میں بتادے گا کہ وہ ہر شے کا جاننے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بابرکت ہے وہ خدا جس نے اپنے بندے پر فرقان نازل کیا ہے تاکہ وہ سارے عالمین کے لئے عذاب الٰہی سے ڈرانے والا بن جائے'),
(2857, 25, 2, 'آسمان و زمین کا سارا ملک اسی کے لئے ہے اور اس نے نہ کوئی فرزند بنایا ہے اور نہ کوئی اس کے ملک میں شریک ہے اس نے ہر شے کو خلق کیا ہے اور صحیح اندازے کے مطابق درست بنایا ہے'),
(2858, 25, 3, 'اور ان لوگوں نے خدا کو چھوڑ کر ایسے خدا بنالئے ہیں جو کسی بھی شے کے خالق نہیں ہیں بلکہ خود ہی مخلوق ہیں اور خود اپنے واسطے بھی کسی نقصان یا نفع کے مالک نہیں ہیں اور نہ ان کے اختیار میں موت و حیات یا حشر و نشر ہی ہے'),
(2859, 25, 4, 'اور یہ کفار کہتے ہیں کہ یہ قرآن صرف ایک جھوٹ ہے جسے انہوں نے گڑھ لیا ہے اور ایک دوسری قوم نے اس کام میں ان کی مدد کی ہے حالانکہ ان لوگوں نے خود ہی بڑا ظلم اور فریب کیا ہے'),
(2860, 25, 5, 'اور یہ لوگ کہتے ہیں کہ یہ تو صرف اگلے لوگوں کے افسانے ہیں جسے انہوں نے لکھوالیا ہے اور وہی صبح و شام ان کے سامنے پڑھے جاتے ہیں'),
(2861, 25, 6, 'آپ کہہ دیجئے کہ اس قرآن کو اس نے نازل کیا ہے جو آسمانوں اور زمین کے رازوں سے باخبر ہے اور یقینا وہ بڑا بخشنے والا اور مہربان ہے'),
(2862, 25, 7, 'اور یہ لوگ کہتے ہیں کہ اس رسول کو کیا ہوگیا ہے کہ یہ کھانا بھی کھاتا ہے اور بازاروں میں چکر بھی لگاتا ہے اور اس کے پاس کوئی ملک کیوں نہیں نازل کیا جاتا جو اس کے ساتھ مل کر عذاب هالٰہی سے ڈرانے والا ثابت ہو'),
(2863, 25, 8, 'یا اس کی طرف کوئی خزانہ ہی گرادیا جاتا یا اس کے پاس کوئی باغ ہی ہوتا جس سے کھاتا پیتا اور پھر یہ ظالم کہتے ہیں کہ تم لوگ تو ایک جادو زدہ آدمی کا اتباع کررہے ہو'),
(2864, 25, 9, 'دیکھو ان لوگوں نے تمہارے لئے کیسی کیسی مثالیں بیان کی ہیں یہ تو بالکل گمراہ ہوگئے ہیں اور اب راستہ نہیں پاسکتے ہیں'),
(2865, 25, 10, 'بابرکت ہے وہ ذات جو اگر چاہے تو تمہارے لئے اس سے بہتر سامان فراہم کردے ایسی جنتیں جن کے نیچے نہریں جاری ہوں اور پھر تمہارے لئے بڑے بڑے محل بھی بنادے'),
(2866, 25, 11, 'حقیقت یہ ہے کہ ان لوگوں نے قیامت کا انکار کیا ہے اور ہم نے قیامت کا انکار کرنے والوں کے لئے جہنم ّمہیاّ کردیا ہے'),
(2867, 25, 12, 'جب آتش جہنم ان لوگوں کو دور سے دیکھے گی تو یہ لوگ اس کے جوش و خروش کی آوازیں سنیں گے'),
(2868, 25, 13, 'اور جب انہیں زنجیروں میں جکڑ کر کسی تنگ جگہ میں ڈال دیا جائے تو وہاں موت کی رَہائی دیں گے'),
(2869, 25, 14, 'اس وقت ان سے کہا جائے گا کہ ایک موت کو نہ پکارو بلکہ بہت سی موتوں کو آواز دو'),
(2870, 25, 15, 'پیغمبر علیھ السّلامآپ ان سے پوچھئے کہ یہ عذاب زیادہ بہتر ہے یا وہ ہمیشگی کی جنت ّجس کا صاحبان هتقویٰ سے وعدہ کیا گیا ہے اور وہی ان کی جزا بھی ہے اور ان کا ٹھکانا بھی'),
(2871, 25, 16, 'ان کے لئے وہاں ہر خواہش کا سامان موجود ہے اور وہ اس میں ہمیشہ رہنے والے ہیں اور یہ پروردگار کے ذمہ ایک لازمی وعدہ ہے'),
(2872, 25, 17, 'اور جس دن بھی خدا ان کو اور ان کے خداؤں کو جنہیں یہ خدا کو چھوڑ کر پکارا کرتے تھے سب کو ایک منزل پر جمع کرے گا اور پوچھے گا کہ تم نے میرے بندوں کو گمراہ کیا تھا یا یہ ازخود بھٹک گئے تھے'),
(2873, 25, 18, 'تو یہ سب کہیں گے کہ تو پاک اور بے نیاز ہے اور ہمیں کیا حق ہے کہ تیرے علاوہ کسی اور کو اپنا سرپرست بنائیں اصل بات یہ ہے کہ تو نے انہیں اور ان کے بزرگوں کو عزّت دنیا عطا کردی تو یہ تیری یاد سے غافل ہوگئے اس لئے کہ یہ ہلاک ہونے والے لوگ ہی تھے'),
(2874, 25, 19, 'دیکھا تم لوگوں نے کہ تمہیں وہ بھی جھٹلا رہے ہیں جنہیں تم نے خدا بنایا تھا تو اب نہ تم عذاب کو ٹال سکتے ہو اور نہ اپنی مدد کرسکتے ہو اور جو بھی تم میں سے ظلم کرے گا اسے ہم بڑے سخت عذاب کا مزہ چکھائیں گے'),
(2875, 25, 20, 'اور ہم نے آپ سے پہلے بھی جن رسولوں کو بھیجا ہے وہ بھی کھانا کھایا کرتے تھے اور بازاروں میں چلتے تھے اور ہم نے بعض افراد کو بعض کے لئے وجہ آزمائش بنادیا ہے تو مسلمانو ! کیا تم صبر کرسکو گے جب کہ تمہارا پروردگار بہت بڑی بصیرت رکھنے والا ہے'),
(2876, 25, 21, 'اور جو لوگ ہماری ملاقات کی امید نہیں رکھتے وہ کہتے ہیں کہ آخر ہم پر فرشتے کیوں نہیں نازل ہوتے یا ہم خدا کو کیوں نہیں دیکھتے درحقیقت یہ لوگ اپنی جگہ پر بہت مغرور ہوگئے ہیں اور انتہائی درجہ کی سرکشی کررہے ہیں'),
(2877, 25, 22, 'جس دن یہ ملائکہ کو دیکھیں گے اس دن مجرمین کے لئے کوئی بشارت نہ ہوگی اور فرشتے کہیں گے کہ تم لوگ دور ہوجاؤ'),
(2878, 25, 23, 'پھر ہم ان کے اعمال کی طرف توجہ کریں گے اور سب کو اڑتے ہوئے خاک کے ذرّوں کے مانند بنا دیں گے'),
(2879, 25, 24, 'اس دن صرف جنت ّ والے ہوں گے جن کے لئے بہترین ٹھکانہ ہوگا اور بہترین آرام کرنے کی جگہ ہوگی'),
(2880, 25, 25, 'اور جس دن آسمان بادلوں کی وجہ سے پھٹ جائے گا اور ملائکہ جوق درجوق نازل کئے جائیں گے'),
(2881, 25, 26, 'اس دن درحقیقت حکومت پروردگار کے ہاتھ میں ہوگی اور وہ دن کافروں کے لئے بڑا سخت دن ہوگا'),
(2882, 25, 27, 'اس دن ظالم اپنے ہاتھوں کو کاٹے گا اور کہے گا کہ کاش میں نے رسول کے ساتھ ہی راستہ اختیار کیا ہوتا'),
(2883, 25, 28, 'ہائے افسوس-کاش میں نے فلاں شخص کو اپنا دوست نہ بنایا ہوتا'),
(2884, 25, 29, 'اس نے تو ذکر کے آنے کے بعد مجھے گمراہ کردیا اور شیطان تو انسان کا رسوا کرنے والا ہے ہی'),
(2885, 25, 30, 'اور اس دن ر سول آواز دے گا کہ پروردگار اس میری قوم نے اس قرآن کو بھی نظر انداز کردیا ہے'),
(2886, 25, 31, 'اور اسی طرح ہم نے ہر نبی کے لئے مجرمین میں سے کچھ دشمن قرار دیدیئے ہیں اور ہدایت اور امداد کے لئے تمہارا پروردگار بہت کافی ہے'),
(2887, 25, 32, 'اور یہ کافر یہ بھی کہتے ہیں کہ آخر انِ پر یہ قرآن ایک دفعہ کِل کاکِل کیوں نہیں نازل ہوگیا-ہم اسی طرح تدریجا نازل کرتے ہیں تاکہ تمہارے دل کو مطمئن کرسکیں اور ہم نے اسے ٹھہر ٹھہر کر نازل کیا ہے'),
(2888, 25, 33, 'اوریہ لوگ کوئی بھی مثال نہ لائیں گے مگر یہ کہ ہم اس کے جواب میں حق اور بہترین بیان لے آئیں گے'),
(2889, 25, 34, 'وہ لوگ جو جہنم ّکی طرف منہ کے بل کھینچ کر لائے جائیں گے ان کا ٹھکانا بدترین ہے اور وہ بہت زیادہ بہکے ہوئے ہیں'),
(2890, 25, 35, 'اور ہم نے موسیٰ کو کتاب عطا کی اور ان کے ساتھ ان کے بھائی ہارون کو ان کا وزیر بنادیا'),
(2891, 25, 36, 'پھر ہم نے کہا کہ اب تم دونوں اس قوم کی طرف جاؤ جس نے ہماری آیتوں کی تکذیب کی ہے اور ہم نے انہیں تباہ و برباد کردیا ہے'),
(2892, 25, 37, 'اور قوم نوح کو بھی جب انہوں نے رسولوں کو جھٹلایا تو ہم نے انہیں بھی غرق کردیا اور لوگوں کے لئے ایک نشانی بنا دیا اور ہم نے ظالمین کے لئے بڑا دردناک عذاب مّہیا کررکھا ہے'),
(2893, 25, 38, 'اور عاد و ثمود اور اصحاب رس اور ان کے درمیان بہت سی نسلوں اور قوموں کو بھی تباہ کردیا ہے'),
(2894, 25, 39, 'اور ان سب کے لئے ہم نے مثالیں بیان کیں اور سب کو ہم نے نیست و نابود کردیا'),
(2895, 25, 40, 'اور یہ لوگ اس بستی کی طرف آئے جس پر ہم نے پتھروں کی بوچھار کی تھی تو کیا ان لوگوں نے اسے نہیں دیکھاحقیقت یہ ہے کہ یہ لوگ قیامت کی امید ہی نہیں رکھتے'),
(2896, 25, 41, 'اور جب آپ کو دیکھتے ہیں تو صرف مذاق بنانا چاہتے ہیں کہ کیا یہی وہ ہے جسے خدا نے رسول بناکر بھیجا ہے'),
(2897, 25, 42, 'قریب تھا کہ یہ ہمیں ہمارے خداؤں سے منحرف کردے اگر ہم لوگ ان خداؤں پر صبر نہ کرلیتے اور عنقریب ان لوگوں کو معلوم ہوجائے گا جب یہ عذاب کو دیکھیں گے کہ زیادہ بہکاہوا کون ہے'),
(2898, 25, 43, 'کیا آپ نے اس شخص کو دیکھا ہے جس نے اپنی خواہشات ہی کو اپنا خدا بنالیا ہے کیا آپ اس کی بھی ذمہ داری لینے کے لئے تیار ہیں'),
(2899, 25, 44, 'کیا آپ کا خیال یہ ہے کہ ان کی اکثریت کچھ سنتی اور سمجھتی ہے ہرگز نہیں یہ سب جانوروں جیسے ہیں بلکہ ان سے بھی کچھ زیادہ ہی گم کردہ راہ ہیں'),
(2900, 25, 45, 'کیا آپ نے نہیں دیکھا کہ آپ کے پروردگار نے کس طرح سایہ کو پھیلا دیا ہے اور وہ چاہتا تو ایک ہی جگہ ساکن بنا دیتا پھر ہم نے آفتاب کو اس کی دلیل بنادیا ہے'),
(2901, 25, 46, 'پھر ہم نے تھوڑا تھوڑا کرکے اسے اپنی طرف کھینچ لیا ہے'),
(2902, 25, 47, 'اور وہی وہ خدا ہے جس نے رات کو تمہارا پردہ اور نیند کو تمہاری راحت اور دن کو تمہارے اٹھ کھڑے ہونے کا وقت قرار دیا ہے'),
(2903, 25, 48, 'اور وہی وہ ہے جس نے ہواؤں کو رحمت کی بشارت کے لئے رواں کردیا ہے اور ہم نے آسمان سے پاک و پاکیزہ پانی برسایا ہے'),
(2904, 25, 49, 'تاکہ اس کے ذریعہ مردہ شہر کو زندہ بنائیں اور اپنی مخلوقات میں سے جانوروں اور انسانوں کی ایک بڑی تعداد کو سیراب کریں'),
(2905, 25, 50, 'اور ہم نے ان کے درمیان پانی کو طرح طرح سے تقسیم کیا ہے تاکہ یہ لوگ نصیحت حاصل کریں لیکن انسانوں کی اکثریت نے ناشکری کے علاوہ ہر بات سے انکار کردیا ہے'),
(2906, 25, 51, 'اور ہم چاہتے تو ہر قریہ میں ایک ڈرانے والا بھیج دیتے'),
(2907, 25, 52, 'لہذا آپ کافروں کے کہنے میں نہ آئیں اور ان سے آخر دم تک جہاد کرتے رہیں'),
(2908, 25, 53, 'اور وہی خدا ہے جس نے دونوں دریاؤں کو جاری کیا ہے اس کا پانی مزیدار اور میٹھا ہے اور یہ نمکین اور کڑواہے اور دونوں کے درمیان حدُ فاصل اور مضبوط رکاوٹ بنا دی ہے'),
(2909, 25, 54, 'اور وہی وہ ہے جس نے پانی سے انسان کو پیدا کیا ہے اور پھر اس کو خاندان اور سسرال والا بنا دیا ہے اور آپ کا پروردگار بہت زیادہ قدرت والا ہے'),
(2910, 25, 55, 'اور یہ لوگ پروردگار کو چھوڑ کر ایسوں کی عبادت کرتے ہیں جو نہ فائدہ پہنچا سکتے ہیں اور نہ نقصان اور کافر تو ہمیشہ پروردگار کے خلاف ہی پشت پناہی کرتا ہے'),
(2911, 25, 56, 'اور ہم نے آپ کو صرف بشارت دینے والا اور ڈرانے والا بنا کر بھیجا ہے'),
(2912, 25, 57, 'آپ کہہ دیجئے کہ میں تم لوگوں سے کوئی اجر نہیں چاہتا مگر یہ کہ جو چاہے وہ اپنے پروردگار کا راستہ اختیار کرے'),
(2913, 25, 58, 'اور آپ اس خدائے حق و قیوم پر اعتماد کریں جسے موت آنے والی نہیں ہے اور اسی کی حمد کی تسبیح کرتے رہیں کہ وہ اپنے بندوں کے گناہوں کی اطلاع کے لئے خود ہی کافی ہے'),
(2914, 25, 59, 'اس نے آسمان و زمین اور اس کے درمیان کی مخلوقات کو چھ دنوں کے اندر پیدا کیا ہے اور اس کے بعد عرش پر اپنا اقتدار قائم کیا ہے وہ رحمان ہے اس کی تخلیق کے بارے میں اسی باخبر سے دریافت کرو'),
(2915, 25, 60, 'اور جب ان سے کہا جاتا ہے کہ رحمان کو سجدہ کرو تو کہتے ہیں کہ یہ رحمان کیا ہے کیا ہم اسے سجدہ کرلیں جس کے بارے میں تم حکم دے رہو اور اس طرح ان کی نفرت میں اور اضافہ ہوجاتا ہے'),
(2916, 25, 61, 'بابرکت ہے وہ ذات جس نے آسمان میں برج بنائے ہیں اور اس میں چراغ اور چمکتا ہوا چاند قرار دیا ہے'),
(2917, 25, 62, 'اور وہی وہ ہے جس نے رات اور دن میں ایک کو دوسرے کاجانشین بنایا ہے اس انسان کے لئے جو عبرت حاصل کرنا چاہے یا شکر پروردگار ادا کرنا چاہے'),
(2918, 25, 63, 'اور اللہ کے بندے وہی ہیں جو زمین پر آہستہ چلتے ہیں اور جب جاہل ان سے خطاب کرتے ہیں توسلامتی کا پیغام دے دیتے ہیں'),
(2919, 25, 64, 'یہ لوگ راتوں کو اس طرح گزارتے ہیں کہ اپنے رب کی بارگاہ میں کبھی سربسجود رہتے ہیں اور کبھی حالت قیام میں رہتے ہیں'),
(2920, 25, 65, 'اور یہ کہتے ہیں کہ پروردگار ہم سے عذاب جہنمّ کو پھیر دے کہ اس کا عذاب بہت سخت اور پائیدار ہے'),
(2921, 25, 66, 'وہ بدترین منزل اور محل اقامت ہے'),
(2922, 25, 67, 'اور یہ لوگ جب خرچ کرتے ہیں تو نہ اسراف کرتے ہیں اور نہ کنجوسی سے کام لیتے ہیں بلکہ ان دونوں کے درمیان اوسط درجے کا راستہ اختیار کرتے ہیں'),
(2923, 25, 68, 'اور وہ لوگ خدا کے ساتھ کسی اور خدا کو نہیں پکارتے ہیں اور کسی بھی نفس کو اگر خدا نے محترم قرار دیدیا ہے تو اسے حق کے بغیر قتل نہیں کرتے اور زنا بھی نہیں کرتے ہیں کہ جو ایسا عمل کرے گا وہ اپنے عمل کی سزا بھی برداشت کرے گا'),
(2924, 25, 69, 'جسے روز قیامت رَگنا کردیا جائے گا اور وہ اسی میں ذلّت کے ساتھ ہمیشہ ہمیشہ پڑا رہے گا'),
(2925, 25, 70, 'علاوہ اس شخص کے جو توبہ کرلے اور ایمان لے آئے اور نیک عمل بھی کرے کہ پروردگار اس کی برائیوں کو اچھائیوں سے تبدیل کردے گا اور خدا بہت زیادہ بخشنے والا اور مہربان ہے'),
(2926, 25, 71, 'اور جو توبہ کرلے گا اور عمل صالح انجام دے گا وہ اللہ کی طرف واقعا رجوع کرنے والا ہے'),
(2927, 25, 72, 'اور وہ لوگ جھوٹ اور فریب کے پاس حاضر بھی نہیں ہوتے ہیں اور جب لغو کاموں کے قریب سے گزرتے ہیں تو بزرگانہ انداز سے گزرجاتے ہیں'),
(2928, 25, 73, 'اور ان لوگوں کوجب آیات الہٰیہ کی یاد دلائی جاتی ہے توبہرے اوراندھے ہوکر گر نہیں پڑتے ہیں'),
(2929, 25, 74, 'اور وہ لوگ برابر دعا کرتے رہتے ہیں کہ خدایا ہمیں ہماری ازواج اور اولاد کی طرف سے خنکی چشم عطا فرما اور ہمیں صاحبان هتقویٰٰ کا پیشوا بنا دے'),
(2930, 25, 75, 'یہی وہ لوگ ہیں جنہیں ان کے صبر کی بنائ پر جنتّ کے بالا خانے عطا کئے جائیں گے اور وہاں انہیں تعظیم اور سلام کی پیشکش کی جائے گی'),
(2931, 25, 76, 'وہ انہی مقامات پرہمیشہ ہمیشہ رہیں گے کہ وہ بہترین مستقر اور حسین ترین محلِ اقامت ہے'),
(2932, 25, 77, 'پیغمبر آپ کہہ دیجئے کہ اگر تمہاری دعائیں نہ ہوتیں تو پروردگار تمہاری پروا بھی نہ کرتا تم نے اس کے رسول کی تکذیب کی ہے تو عنقریب اس کا عذاب بھی برداشت کرنا پڑے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طسم ۤ'),
(2934, 26, 2, 'یہ ایک واضح کتاب کی آیتیں ہیں'),
(2935, 26, 3, 'کیا آپ اپنے نفس کو ہلاکت میں ڈال دیں گے کہ یہ لوگ ایمان نہیں لارہے ہیں'),
(2936, 26, 4, 'اگر ہم چاہتے تو آسمان سے ایسی آیت نازل کردیتے کہ ان کی گردنیں خضوع کے ساتھ جھک جاتیں'),
(2937, 26, 5, 'لیکن ان کی طرف جب بھی خدا کی طرف سے کوئی نیا ذکر آتاہے تو یہ اس سے اعراض ہی کرتے ہیں'),
(2938, 26, 6, 'یقینا انہوں نے تکذیب کی ہے تو عنقریب ان کے پاس اس بات کی خبریں آجائیں گی جس کا یہ لوگ مذاق اڑا رہے تھے'),
(2939, 26, 7, 'کیا ان لوگوں نے زمین کی طرف نہیں دیکھا کہ ہم نے کس طرح عمدہ عمدہ چیزیں اُگائی ہیں'),
(2940, 26, 8, 'اس میں ہماری نشانی ہے لیکن ان کی اکثریت ایمان لانے والی نہیں ہے'),
(2941, 26, 9, 'اور آپ کاپروردگار صاحبِ عزت بھی ہے اور صاحب هحکمت بھی ہے'),
(2942, 26, 10, 'اور اس وقت کو یاد کرو جب آپ کے پروردگار نے موسیٰ کو آواز دی کہ اس ظالم قوم کے پاس جاؤ'),
(2943, 26, 11, 'یہ فرعون کی قوم ہے کیا یہ متقی نہ بنیں گے'),
(2944, 26, 12, 'موسیٰ نے کہا کہ پروردگار میں ڈرتا ہوں کہ یہ میری تکذیب نہ کریں'),
(2945, 26, 13, 'میرا دل تنگ ہورہا ہے اور میری زبان رواں نہیں ہے یہ پیغام ہارون کے پاس بھیج دے'),
(2946, 26, 14, 'اور میرے اوپر ان کاایک جرم بھی ہے تو مجھے خوف ہے کہ یہ مجھے قتل نہ کردیں'),
(2947, 26, 15, 'ارشاد ہوا کہ ہرگز نہیںتم دونوں ہی ہماری نشانیوں کو لے کر جاؤ اور ہم بھی تمہارے ساتھ سب سن رہے ہیں'),
(2948, 26, 16, 'فرعون کے پاس جاؤ اور کہو کہ ہم دونوں رب العالمین کے فرستادہ ہیں'),
(2949, 26, 17, 'کہ بنی اسرائیل کو میرے ساتھ بھیج دے'),
(2950, 26, 18, 'اس نے کہا کیا ہم نے تمہیں بچپنے میں پالا نہیں ہے اور کیا تم نے ہمارے درمیان اپنی عمر کے کئی سال نہیں گزارے ہیں'),
(2951, 26, 19, 'اور تم نے وہ کام کیا ہے جو تم کرگئے ہو اور تم شکریہ ادا کرنے والوں میں سے نہیں ہو'),
(2952, 26, 20, 'موسیٰ نے کہا کہ وہ قتل میں نے اس وقت کیا تھا جب میں قتل سے غافل تھا'),
(2953, 26, 21, 'پھر میں نے تم لوگوں کے خوف سے گریز اختیار کیا تو میرے رب نے مجھے نبوت عطا فرمائی اور مجھے اپنے نمائندوں میں سے قرار دے دیا'),
(2954, 26, 22, 'یہ احسان جو تربیت کے سلسلہ میں تو جتا رہا ہے تو تونے بڑا غضب کیا تھاکہ بنی اسرائیل کو غلام بنا لیا تھا'),
(2955, 26, 23, 'فرعون نے کہا کہ یہ ربّ العالمین کیا چیز ہے'),
(2956, 26, 24, 'موسیٰ نے کہا کہ زمین و آسمان اور اس کے مابین جو کچھ ہے سب کا پروردگار اگر تم یقین کرسکو'),
(2957, 26, 25, 'فرعون نے اپنے اطرافیوں سے کہا کہ تم کچھ سن رہے ہو'),
(2958, 26, 26, 'موسیٰ نے کہا کہ وہ تمہارا بھی رب ہے اور تمہارے باپ دادا کا بھی رب ہے'),
(2959, 26, 27, 'فرعون نے کہا کہ یہ رسول جو تمہاری طرف بھیجا گیا ہے یہ بالکل دیوانہ ہے'),
(2960, 26, 28, 'موسیٰ نے کہا وہ مشرق و مغرب اور جو کچھ اس کے درمیان ہے سب کا پرودگار ہے اگر تمہارے پاس عقل ہے'),
(2961, 26, 29, 'فرعون نے کہا کہ تم نے میرے علاوہ کسی خدا کو بھی اختیار کیا تو تمہیں قیدیوں میں شامل کردوں گا'),
(2962, 26, 30, 'موسیٰ نے جواب دیا کہ چاہے میں کھلی ہوئی دلیل ہی پیش کردوں'),
(2963, 26, 31, 'فرعون نے کہا وہ دلیل کیا ہے اگر تم سچےّ ہو تو پیش کرو'),
(2964, 26, 32, 'موسیٰ نے اپنا عصا ڈال دیا اور وہ سانپ بن کر رینگنے لگا'),
(2965, 26, 33, 'اور گریبان سے ہاتھ نکالا تو وہ سفید چمک دار نظر آنے لگا'),
(2966, 26, 34, 'فرعون نے اپنے اطراف والوں سے کہا کہ یہ تو بڑا ہوشیار جادوگر معلوم ہوتا ہے'),
(2967, 26, 35, 'اس کا مقصد یہ ہے کہ جادو کے زور پر تمہیں تمہاری زمین سے نکال باہر کردے تو اب تمہاری رائے کیا ہے'),
(2968, 26, 36, 'لوگوں نے کہا کہ انہیں اور ان کے بھائی کو روک لیجئے اور شہروں میں جادوگروں کو اکٹھاکرنے والوں کو روانہ کردیجئے'),
(2969, 26, 37, 'وہ لوگ ایک سے ایک ہوشیار جادوگرلے آئیں گے'),
(2970, 26, 38, 'غرض وقت مقرر پر تمام جادوگر اکٹھا کئے گئے'),
(2971, 26, 39, 'اور ان لوگوں سے کہا گیا کہ تم سب اس بات پر اجتماع کرنے والے ہو'),
(2972, 26, 40, 'شاید ہم لوگ ان ساحروں کا اتباع کرلیں اگر وہ غالب آگئے'),
(2973, 26, 41, 'اس کے بعد جب جادوگر اکٹھا ہوئے تو انہوں نے فرعون سے کہا کہ اگر ہم غالب آگئے تو کیا ہماری کوئی اجرت ہوگی'),
(2974, 26, 42, 'فرعون نے کہا کہ بے شک تم لوگ میرے مقربین میں شمار ہوگے'),
(2975, 26, 43, 'موسیٰ نے ان لوگوں سے کہا کہ جو کچھ پھینکنا چاہتے ہو پھینکو'),
(2976, 26, 44, 'تو ان لوگوں نے اپنی رسیوں اور چھڑیوں کو پھینک دیا اور کہا کہ فرعون کی عزّت و جلال کی قسم ہم لوگ غالب آنے والے ہیں'),
(2977, 26, 45, 'پھر موسیٰ نے بھی اپنا عصا ڈال دیا تو لوگوں نے اچانک کیا دیکھا کہ وہ سب کے جادو کو نگلے جارہا ہے'),
(2978, 26, 46, 'یہ دیکھ کر جادوگر سجدہ میں گر پڑے'),
(2979, 26, 47, 'اور ان لوگوں نے کہا کہ ہم تو رب العالمین پر ایمان لے آئے'),
(2980, 26, 48, 'جو موسیٰ اور ہارون دونوں کا رب ہے'),
(2981, 26, 49, 'فرعون نے کہا کہ تم لوگ میری اجازت سے پہلے ہی ایمان لے آئے یہ تم سے بھی بڑا جادوگر ہے جس نے تم لوگوں کو جادو سکھایا ہے میں تم لوگوں کے ہاتھ پاؤں مختلف سمتوں سے کاٹ دوں گا اور تم سب کو سولی پر لٹکا دوں گا'),
(2982, 26, 50, 'ان لوگوں نے کہا کہ کوئی حرج نہیں ہم سب پلٹ کر اپنے رب کی بارگاہ میں پہنچ جائیں گے'),
(2983, 26, 51, 'ہم تو صرف یہ چاہتے ہیں کہ ہمارا پروردگار ہماری خطاؤں کو معاف کردے کہ ہم سب سے پہلے ایمان لانے والے ہیں'),
(2984, 26, 52, 'اور ہم نے موسیٰ کی طرف وحی کی کہ میرے بندوں کو لے کر راتوں رات نکل جاؤ کہ تمہارا پیچھا کیا جانے والا ہے'),
(2985, 26, 53, 'پھر فرعون نے مختلف شہروں میں لشکر جمع کرنے والے روانہ کردیئے'),
(2986, 26, 54, 'کہ یہ تھوڑے سے افراد کی ایک جماعت ہے'),
(2987, 26, 55, 'اور ان لوگوں نے ہمیں غصہ دلا دیا ہے'),
(2988, 26, 56, 'اور ہم سب سارے سازوسامان کے ساتھ ہیں'),
(2989, 26, 57, 'نتیجہ میں ہم نے ان کو باغات اور چشموں سے نکال باہر کردیا'),
(2990, 26, 58, 'اور خزانوں اور باعزّت جگہوں سے بھی'),
(2991, 26, 59, 'اور ہم اسی طرح سزا دیتے ہیں اور ہم نے زمین کا وارث بنی اسرائیل کو بنادیا'),
(2992, 26, 60, 'پھر ان لوگوں نے موسیٰ اور ان کے ساتھیوں کا صبح سویرے پیچھا کیا'),
(2993, 26, 61, 'پھر جب دونوں ایک دوسرے کو نظر آنے لگے تو اصحاب موسیٰ نے کہا کہ اب تو ہم گرفت میںآجائیں گے'),
(2994, 26, 62, 'موسیٰ نے کہا کہ ہرگز نہیں ہمارے ساتھ ہمارا پروردگار ہے وہ ہماری راہنمائی کرے گا'),
(2995, 26, 63, 'پھر ہم نے موسیٰ کی طرف وحی کی کہ اپنا عصا دریا میں مار دیں چنانچہ دریا شگافتہ ہوگیا اور ہر حصہّ ایک پہاڑ جیسا نظر آنے لگا'),
(2996, 26, 64, 'اور دوسرے فریق کو بھی ہم نے قریب کردیا'),
(2997, 26, 65, 'اور ہم نے موسیٰ اور ان کے تمام ساتھیوں کو نجات دے دی'),
(2998, 26, 66, 'پھر باقی لوگوں کو غرق کردیا'),
(2999, 26, 67, 'اس میں بھی ہماری ایک نشانی ہے اور بنی اسرائیل کی اکثریت ایمان لانے والی نہیں تھی'),
(3000, 26, 68, 'اور تمہارا پروردگار صاحبِ عزّت بھی ہے اور مہربان بھی'),
(3001, 26, 69, 'اور انہیں ابراہیم کی خبر پڑھ کر سناؤ'),
(3002, 26, 70, 'جب انہوںنے اپنے مربی باپ اور قوم سے کہا کہ تم لوگ کس کی عبادت کررہے ہو'),
(3003, 26, 71, 'ان لوگوں نے کہا کہ ہم بتوں کی عبادت کرتے ہیں اور انہی کی مجاوری کرتے ہیں'),
(3004, 26, 72, 'تو ابراہیم نے کہا کہ جب تم ان کو پکارتے ہو تو یہ تمہاری آواز سنتے ہیں'),
(3005, 26, 73, 'یہ کوئی فائدہ یا نقصان پہنچاتے ہیں'),
(3006, 26, 74, 'ان لوگوں نے جواب دیا کہ ہم نے اپنے باپ داداکو ایسا ہی کرتے دیکھا ہے'),
(3007, 26, 75, 'ابراہیم نے کہا کہ کیا تم کو معلوم ہے کہ جن کی تم عبادت کرتے ہو'),
(3008, 26, 76, 'تم اور تمہارے تمام بزرگان خاندان'),
(3009, 26, 77, 'یہ سب میرے دشمن ہیں. رب العالمین کے علاوہ'),
(3010, 26, 78, 'کہ جس نے مجھے پیدا کیا ہے اور پھر وہی ہدایت بھی دیتا ہے'),
(3011, 26, 79, 'وہی کھانادیتا ہے اور وہی پانی پلاتا ہے'),
(3012, 26, 80, 'اور جب بیمار ہوجاتا ہوں تو وہی شفا بھی دیتا ہے'),
(3013, 26, 81, 'وہی موت دیتا ہے اور پھر وہی زندہ کرتا ہے'),
(3014, 26, 82, 'اور اسی سے یہ امید ہے کہ روزِ حساب میری خطاؤں کو معاف کردے'),
(3015, 26, 83, 'خدایا مجھے علم و حکمت عطا فرما اور مجھے صالحین کے ساتھ ملحق کردے'),
(3016, 26, 84, 'اور میرے لئے آئندہ نسلوں میں سچی زبان اور ذکر خیر قرار دے'),
(3017, 26, 85, 'اور مجھے جنت کے وارثوں میں سے قرار دے'),
(3018, 26, 86, 'اور میرے مربی کو بخش دے کہ وہ گمراہوں میں سے ہے'),
(3019, 26, 87, 'اور مجھے اس دن رسوا نہ کرنا جب سب قبروں سے اٹھائے جائیں گے'),
(3020, 26, 88, 'جس دن مال اور اولاد کوئی کام نہ آئے گا'),
(3021, 26, 89, 'مگر وہ جو قلب سلیم کے ساتھ اللہ کی بارگاہ میں حاضر ہو'),
(3022, 26, 90, 'اور جس دن جنتّ پرہیزگاروں سے قریب تر کردی جائے گی'),
(3023, 26, 91, 'اور جہنمّ کو گمراہوں کے سامنے کردیا جائے گا'),
(3024, 26, 92, 'اور جہنّمیوں سے کہا جائے گا کہ کہاں ہیں وہ جن کی تم عبادت کیا کرتے تھے'),
(3025, 26, 93, 'خدا کو چھوڑ کر وہ تمہاری مدد کریں گے یا اپنی مدد کریں گے'),
(3026, 26, 94, 'پھر وہ سب مع تمام گمراہوں کے جہنّم میں منہ کے بل ڈھکیل دیئے جائیں گے'),
(3027, 26, 95, 'اور ابلیس کے تمام لشکر والے بھی'),
(3028, 26, 96, 'اور وہ سب جہنمّ میں آپس میں جھگڑا کرتے ہوئے کہیں گے'),
(3029, 26, 97, 'کہ خدا کی قسم ہم سب کھلی ہوئی گمراہی میں تھے'),
(3030, 26, 98, 'جب تم کو رب العالمین کے برابر قرار دے رہے تھے'),
(3031, 26, 99, 'اور ہمیں مجرموں کے علاوہ کسی نے گمراہ نہیں کیا'),
(3032, 26, 100, 'اب ہمارے لئے کوئی شفاعت کرنے والا بھی نہیں ہے'),
(3033, 26, 101, 'اور نہ کوئی دل پسند دوست ہے'),
(3034, 26, 102, 'پس اے کاش ہمیں واپسی نصیب ہوجاتی تو ہم سب بھی صاحب هایمان ہوجاتے'),
(3035, 26, 103, 'اس میں بھی ہماری ایک نشانی ہے اور ان کی اکثریت بہرحال مومن نہیں تھی'),
(3036, 26, 104, 'اور تمہارا پروردگار سب پر غالب بھی ہے اور مہربان بھی ہے'),
(3037, 26, 105, 'اور نوح کی قوم نے بھی مرسلین کی تکذیب کی'),
(3038, 26, 106, 'جب ان کے بھائی نوح نے ان سے کہا کہ تم پرہیزگاری کیوں نہیں اختیار کرتے ہو'),
(3039, 26, 107, 'میں تمہارے لئے امانت دار نمائندہ پروردگار ہوں'),
(3040, 26, 108, 'پس اللہ سے ڈرو اور میری اطاعت کرو'),
(3041, 26, 109, 'اور میں اس تبلیغ کی کوئی اجر بھی نہیں چاہتا ہوں میری اجر ت تو رب العالمین کے ذمہ ہے'),
(3042, 26, 110, 'لہذا تم اللہ سے ڈرو اور میری اطاعت کرو'),
(3043, 26, 111, 'ان لوگوں نے کہا کہ ہم آپ پر کس طرح ایمان لے آئیں جبکہ آپ کے سارے پیروکار پست طبقہ کے لوگ ہیں'),
(3044, 26, 112, 'نوح نے کہا کہ میں کیا جانوں کہ یہ کیا کرتے تھے'),
(3045, 26, 113, 'ان کا حساب تو میرے پروردگار کے ذمہ ہے اگر تم اس بات کا شعور رکھتے ہو'),
(3046, 26, 114, 'اور میں مومنین کو ہٹانے ولا نہیں ہوں'),
(3047, 26, 115, 'میں تو صرف واضح طور پر عذاب الٰہی سے ڈرانے والا ہوں'),
(3048, 26, 116, 'ان لوگوں نے کہا کہ نوح اگر تم ان باتوں سے باز نہ آئے تو ہم تمہیں سنگسار کردیں گے'),
(3049, 26, 117, 'نوح نے یہ سن کر فریاد کی کہ پروردگار میری قوم نے مجھے جھٹلا دیا ہے'),
(3050, 26, 118, 'اب میرے اور ان کے درمیان کھلا ہوا فیصلہ فرما دے اور مجھے اور میرے ساتھی صاحبانِ ایمان کو نجات دے دے'),
(3051, 26, 119, 'پھر ہم نے انہیں اور ان کے ساتھیوں کو ایک بھری ہوئی کشتی میں نجات دے دی'),
(3052, 26, 120, 'اس کے بعد باقی سب کو غرق کردیا'),
(3053, 26, 121, 'یقینا اس میں بھی ہماری ایک نشانی ہے اور ان کی اکثریت ایمان لانے والی نہیں تھی'),
(3054, 26, 122, 'اور تمہارا پروردگار ہی سب پر غالب اور مہربان ہے'),
(3055, 26, 123, 'اورقوم عاد نے بھی مرسلین کی تکذیب کی ہے'),
(3056, 26, 124, 'جب ان کے بھائی ہود نے کہا کہ تم خورِ خدا کیوں نہیں پیدا کرتے ہو'),
(3057, 26, 125, 'میں تمہارے لئے ایک امانتدار پیغمبر ہوں'),
(3058, 26, 126, 'لہذا اللہ سے ڈرو اور میری اطاعت کرو'),
(3059, 26, 127, 'اور میں تو تم سے تبلیغ کا کوئی اجر بھی نہیں چاہتا ہوں میرا اجر صرف رب العالمین کے ذمہ ہے'),
(3060, 26, 128, 'کیا تم کھیل تماشے کے لئے ہر اونچی جگہ پر ایک یادگار بناتے ہو'),
(3061, 26, 129, 'اور بڑے بڑے محل تعمیر کرتے ہو کہ شاید اسی طرح ہمیشہ دنیا میں رہ جاؤ'),
(3062, 26, 130, 'اور جب حملہ کرتے ہو تو نہایت جابرانہ حملہ کرتے ہو'),
(3063, 26, 131, 'اب اللہ سے ڈرو اور میری اطاعت کرو'),
(3064, 26, 132, 'اور اس کا خوف پیدا کرو جس نے تمہاری ان تمام چیزوں سے مدد کی ہے جنہیں تم خوب جانتے ہو'),
(3065, 26, 133, 'تمہاری امداد جانوروں اور اولاد سے کی ہے'),
(3066, 26, 134, 'اور باغات اور چشموں سے کی ہے'),
(3067, 26, 135, 'میں تمہارے بارے میں بڑے سخت دن کے عذاب سے خوفزدہ ہوں'),
(3068, 26, 136, 'ان لوگوں نے کہا کہ ہمارے لئے سب برابر ہے چاہے تم ہمیں نصیحت کرو یا تمہارا شمارنصیحت کرنے والوں میں نہ ہو'),
(3069, 26, 137, 'یہ ڈرانا دھمکانا تو پرانے لوگوں کی عادت ہے'),
(3070, 26, 138, 'اور ہم پر عذاب ہونے والا نہیں ہے'),
(3071, 26, 139, 'پس قوم نے تکذیب کی اور ہم نے اسے ہلاک کردیا کہ اس میں بھی ہماری ایک نشانی ہے ا ور ان کی اکثریت بہرحا ل مومن نہیں تھی'),
(3072, 26, 140, 'اور تمہارا پروردگار غالب بھی ہے اور مہربان بھی ہے'),
(3073, 26, 141, 'او قوم ثمود نے بھی مرسلین کی تکذیب کی'),
(3074, 26, 142, 'جب ان کے بھائی صالح نے کہا کہ تم لوگ خدا سے کیوں نہیں ڈرتے ہو'),
(3075, 26, 143, 'میں تمہارے لئے ایک امانتدار پیغمبر ہوں'),
(3076, 26, 144, 'لہذا اللہ سے ڈرو اور میری اطاعت کرو'),
(3077, 26, 145, 'اور میں تم سے اس کام کی اجرت بھی نہیں چاہتا ہوں میری اجرت تو خدائے رب العالمین کے ذمہ ہے'),
(3078, 26, 146, 'کیا تم یہاں کی نعمتوں میں اسی آرام سے چھوڑ دیئے جاؤ گے'),
(3079, 26, 147, 'انہی باغات اور چشموں میں'),
(3080, 26, 148, 'اور انہی کھیتوں اور خرمے کے درختوں کے درمیان جن کی کلیاں نرم و نازک ہیں'),
(3081, 26, 149, 'اور جو تم پہاڑوں کو کاٹ کر آسائشی مکانات تعمیر کررہے ہو'),
(3082, 26, 150, 'ایسا ہرگز نہیں ہوگا لہذا اللہ سے ڈرو اور میری اطاعت کرو'),
(3083, 26, 151, 'اور زیادتی کرنے والوں کی بات نہ مانو'),
(3084, 26, 152, 'جو زمین میں فساد برپا کرتے ہیںاور اصلاح نہیں کرتے ہیں'),
(3085, 26, 153, 'ان لوگوں نے کہا کہ تم پر تو صرف جادو کردیا گیا ہے اور بس'),
(3086, 26, 154, 'تم ہمارے ہی جیسے ایک انسان ہو لہذا اگر سچےّ ہو تو کوئی نشانی اور معجزہ لے آؤ'),
(3087, 26, 155, 'صالح نے کہا کہ یہ ایک اونٹنی ہے ایک دن کا پانی اس کے لئے ہے اورایک مقرر دن کا پانی تمہارے لئے ہے'),
(3088, 26, 156, 'اور خبردار اسے کوئی تکلیف نہ پہنچانا ورنہ تمہیں سخت دن کا عذاب گرفتار کرلے گا'),
(3089, 26, 157, 'پھر ان لوگوں نے اس کے پیرکاٹ دیئے او بعد میں بہت شرمند ہوئے'),
(3090, 26, 158, 'کہ عذاب نے انہیں گھیر لیا اور یقینا اس میں بھی ہماری ایک نشانی ہے اور ان کی اکثریت ایمان والی نہیں تھی'),
(3091, 26, 159, 'اور تمہارا پروردگار سب پرغالب آنے والا اور صاحبِ رحمت بھی ہے'),
(3092, 26, 160, 'اور قوم لوط نے بھی مرسلین کو جھٹلایا'),
(3093, 26, 161, 'جب ان سے ان کے بھائی لوط نے کہا کہ تم خدا سے کیوں نہیں ڈرتے ہو'),
(3094, 26, 162, 'میں تمہارے حق میں ایک امانتدار پیغمبر ہوں'),
(3095, 26, 163, 'اللرُ سے ڈرو اور میری اطاعت کرو'),
(3096, 26, 164, 'اور میں تم سے اس امر کی کوئی اجرت بھی نہیں چاہتا ہوں میرا اجر تو صرف پروردگار کے ذمہ ہے جو عالمین کا پالنے والا ہے'),
(3097, 26, 165, 'کیا تم لوگ ساری دنیا میں صرف مُردوں ہی سے جنسی تعلقات پیدا کرتے ہو'),
(3098, 26, 166, 'اور ان ازواج کو چھوڑ دیتے ہو جنہیں پروردگار نے تمہارے لئے پیدا کیا ہے حقیقتا تم بڑی زیادتی کرنے والے لوگ ہو'),
(3099, 26, 167, 'ان لوگوں نے کہا کہ لوط اگر تم اس تبلیغ سے باز نہ آئے تو اس بستی سے نکال باہر کردیئے جاؤ گے'),
(3100, 26, 168, 'انہوں نے کہا کہ بہرحال میں تمہارے عمل سے بیزار ہوں'),
(3101, 26, 169, 'پروردگار ! مجھے اور میرے اہل کو ان کے اعمال کی سزا سے محفوظ رکھنا'),
(3102, 26, 170, 'تو ہم نے انہیں اور ان کے اہل سب کو نجات دے دی'),
(3103, 26, 171, 'سوائے اس ضعیفہ کے کہ جو پیچھے رہ گئی'),
(3104, 26, 172, 'پھر ہم نے ان لوگوں کو تباہ و برباد کردیا'),
(3105, 26, 173, 'اور ان کے اوپر زبردست پتھروں کی بارش کردی جو ڈرائے جانے والوں کے حق میں بدترین بارش ہے'),
(3106, 26, 174, 'اور اس میں بھی ہماری ایک نشانی ہے اور ان کی اکثریت بہرحال مومن نہیں تھی'),
(3107, 26, 175, 'اور تمہارا پروردگار عزیز بھی ہے اور رحیم بھی ہے'),
(3108, 26, 176, 'اور جنگل کے رہنے والوں نے بھی مرسلین کو جھٹلایا'),
(3109, 26, 177, 'جب ان سے شعیب نے کہا کہ تم خدا سے ڈرتے کیوں نہیں ہو'),
(3110, 26, 178, 'میں تمہارے لئے ایک امانتدار پیغمبر ہوں'),
(3111, 26, 179, 'لہذا اللہ سے ڈرو اور میری اطاعت کرو'),
(3112, 26, 180, 'اور میں تم سے اس کام کی کوئی اجرت بھی نہیں چاہتا ہوں کہ میرا اجر تو صرف رب العالمین کے ذمہ ہے'),
(3113, 26, 181, 'اور دیکھو ناپ تول کو ٹھیک رکھو اور لوگوں کو خسارہ دینے والے نہ بنو'),
(3114, 26, 182, 'اور وزن کرو تو صحیح اور سچیّ ترازو سے تولو'),
(3115, 26, 183, 'اور لوگوں کی چیزوں میں کمی نہ کیا کرو اور روئے زمین میں فساد نہ پھیلاتے پھرو'),
(3116, 26, 184, 'اور اس خدا سے ڈرو جس نے تمہیں اور تم سے پہلے والی نسلوں کو پیدا کیا ہے'),
(3117, 26, 185, 'ان لوگوں نے کہا کہ تم تو صرف جادو زدہ معلوم ہوتے ہو'),
(3118, 26, 186, 'اور تم ہمارے ہی جیسے ایک انسان ہو اور ہمیں تو جھوٹے بھی معلوم ہوتے ہو'),
(3119, 26, 187, 'اور اگر واقعا سچے ہو تو ہمارے اوپر آسمان کا کوئی ٹکڑا نازل کردو'),
(3120, 26, 188, 'انہوں نے کہا کہ ہمارا پروردگار تمہارے اعمال سے خوب باخبر ہے'),
(3121, 26, 189, 'پھر ان لوگوں نے تکذیب کی تو انہیں سایہ کے دن کے عذاب نے اپنی گرفت میں لے لیا کہ یہ بڑے سخت دن کا عذاب تھا'),
(3122, 26, 190, 'بیشک اس میں بھی ہماری ایک نشانی ہے اور ان کی اکثریت ایمان لانے والی نہیں تھی'),
(3123, 26, 191, 'اور تمہارا پروردگار بہت بڑا عزت والا اور مہربان ہے'),
(3124, 26, 192, 'اور یہ قرآن رب العالمین کی طرف سے نازل ہونے والا ہے'),
(3125, 26, 193, 'اسے جبریل امین لے کر نازل ہوئے ہیں'),
(3126, 26, 194, 'یہ آپ کے قلب پر نازل ہوا ہے تاکہ آپ لوگوں کو عذاب الٰہی سے ڈرائیں'),
(3127, 26, 195, 'یہ واضح عربی زبان میں ہے'),
(3128, 26, 196, 'اور اس کا ذکر سابقین کی کتابوں میں بھی موجود ہے'),
(3129, 26, 197, 'کیا یہ نشانی ان کے لئے کافی نہیں ہے کہ اسے بنی اسرائیل کے علمائ بھی جانتے ہیں'),
(3130, 26, 198, 'اور اگر ہم اسے کسی عجمی آدمی پر نازل کردیتے'),
(3131, 26, 199, 'اور وہ انہیں پڑھ کر سناتا تو یہ کبھی ایمان لانے والے نہیں تھے'),
(3132, 26, 200, 'اور اس طرح ہم نے اس انکار کو مجرمین کے دلوں تک جانے کا رستہ دے دیا ہے'),
(3133, 26, 201, 'کہ یہ ایمن لانے والے نہیں ہیں جب تک کہ دردناک عذاب نہ دیکھ لیں'),
(3134, 26, 202, 'کہ یہ عذاب ان پر اچانک نازل ہوجائے اور انہیں شعور تک نہ ہو'),
(3135, 26, 203, 'اس وقت یہ کہیں گے کہ کیا ہمیں مہلت دی جاسکتی ہے'),
(3136, 26, 204, 'تو کیا لوگ ہمارے عذاب کی جلدی کررہے ہیں'),
(3137, 26, 205, 'کیا تمہیں نہیں معلوم کہ ہم انہیں کئی سال کی مہلت دے دیں'),
(3138, 26, 206, 'اور اس کے بعد وہ عذاب آئے جس کا وعدہ کیا گیا ہے'),
(3139, 26, 207, 'تو بھی جو ان کو آرام دیا گیا تھا وہ ان کے کام نہ آئے گا'),
(3140, 26, 208, 'اور ہم نے کسی بستی کو ہلاک نہیں کیا مگر یہ کہ اس کے لئے ڈرانے والے بھیج دیئے تھے'),
(3141, 26, 209, 'یہ ایک یاد دہانی تھی اور ہم ہرگز ظلم کرنے والے نہیں ہیں'),
(3142, 26, 210, 'اور اس قرآن کو شیاطین لے کر حاضر نہیں ہوئے ہیں'),
(3143, 26, 211, 'یہ بات ان کے لئے مناسب بھی نہیں ہے اور ان کے بس کی بھی نہیں ہے'),
(3144, 26, 212, 'وہ تو وحی کے سننے سے بھی محروم ہیں'),
(3145, 26, 213, 'لہذا تم اللہ کے ساتھ کسی اور خدا کو مت پکارو کہ مبتلائے عذاب کردیئے جاؤ'),
(3146, 26, 214, 'اور پیغمبر آپ اپنے قریبی رشتہ داروں کو ڈرایئے'),
(3147, 26, 215, 'اور جو صاحبانِ ایمان آپ کا اتباع کرلیں ان کے لئے اپنے شانوں کو جھکا دیجئے'),
(3148, 26, 216, 'پھر یہ لوگ آپ کی نافرمانی کریں تو کہہ دیجئے کہ میں تم لوگوں کے اعمال سے بیزار ہوں'),
(3149, 26, 217, 'اور خدائے عزیز و مہربان پر بھروسہ کیجئے'),
(3150, 26, 218, 'جو آپ کو اس وقت بھی دیکھتا ہے جب آپ قیام کرتے ہیں'),
(3151, 26, 219, 'اور پھر سجدہ گزاروں کے درمیان آپ کا اٹھنا بیٹھنا بھی دیکھتا ہے'),
(3152, 26, 220, 'وہ سب کچھ سننے والا اور جاننے والا ہے'),
(3153, 26, 221, 'کیا ہم آپ کو بتائیں کہ شیاطین کس پر نازل ہوتے ہیں'),
(3154, 26, 222, 'وہ ہر جھوٹے اور بدکردار پر نازل ہوتے ہیں'),
(3155, 26, 223, 'جو فرشتوں کی باتوں پر کان لگائے رہتے ہیں اور ان میں کے اکثر لوگ جھوٹے ہیں'),
(3156, 26, 224, 'اور شعرائ کی پیروی وہی لوگ کرتے ہیں جو گمراہ ہوتے ہیں'),
(3157, 26, 225, 'کیا تم نہیں دیکھتے ہو کہ وہ ہر وادئی خیال میں چکر لگاتے رہتے ہیں'),
(3158, 26, 226, 'اور وہ کچھ کہتے ہیں جو کرتے نہیں ہیں'),
(3159, 26, 227, 'علاوہ ان شعرائ کے جو ایمان لائے اور انہوں نے نیک اعمال کئے اور بہت سارا ذکر خدا کیا اور ظلم سہنے کے بعد اس کا انتقام لیا اور عنقریب ظالمین کو معلوم ہوجائے گا کہ وہ کس جگہ پلٹا دیئے جائیں گے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طسۤ یہ قرآن اور روشن کتاب کی آیتیں ہیں'),
(3161, 27, 2, 'یہ صاحبانِ ایمان کے لئے ہدایت اور بشارت ہیں'),
(3162, 27, 3, 'جو نماز قائم کرتے ہیں زکوِٰ ا دا کرتے ہیں اور آخرت پر یقین رکھتے ہیں'),
(3163, 27, 4, 'بیشک جو لوگ آخرت پر ایمان نہیں رکھتے ہیں ہم نے ان کے اعمال کو ان کے لئے آراستہ کردیا ہے اور وہ ان ہی اعمال میں بھٹک رہے ہیں'),
(3164, 27, 5, 'ا ن ہی لوگوں کے لئے بدترین عذاب ہے اور یہ آخرت میں خسارہ والے ہیں'),
(3165, 27, 6, 'اور آپ کو یہ قرآن خدائے علیم و حکیم کی طرف سے عطا کیا جارہا ہے'),
(3166, 27, 7, 'اس وقت کو یاد کرو جب موسیٰ نے اپنے اہل سے کہا تھا کہ میں نے ایک آگ دیکھی ہے اور عنقریب میں اس سے راستہ کی خبر لاؤں گا یا آگ ہی کا کوئی انگارہ لے آؤں گا کہ تم تاپ سکو'),
(3167, 27, 8, 'اس کے بعد جب آگ کے پاس آئے تو آواز آئی کہ بابرکت ہے وہ خدا جو آگ کے اندر اور اس کے اطراف میں اپنا جلوہ دکھاتا ہے اور پاک و پاکیزہ ہے وہ پروردگار جو عالمین کا پالنے والا ہے'),
(3168, 27, 9, 'موسیٰ! میں وہ خدا ہوں جو سب پر غالب اور صاحبِ حکمت ہے'),
(3169, 27, 10, 'اب تم اپنے عصا کو زمین پر ڈال دو اس کے بعد موسیٰ نے جب دیکھا تو کیا دیکھا کہ وہ سانپ کی طرح لہرا رہا ہے موسیٰ الٹے پاؤں پلٹ پڑے اور مڑ کر بھی نہ دیکھا آواز آئی کہ موسیٰ ڈرو نہیں میری بارگاہ میں مرسلین نہیں ڈرا کرتے ہیں'),
(3170, 27, 11, 'ہاں کوئی شخص گناہ کرکے پھر توبہ کرلے اور اس برائی کو نیکی سے بدل دے تو میں بہت بخشنے والا مہربان ہوں'),
(3171, 27, 12, 'اور اپنے ہاتھ کو گریبان میں ڈال کر نکالو تو دیکھو گے کہ بغیر کسی بیماری کے سفید چمکدار نکلتا ہے یہ ان نو معجزات میں سے ایک ہے جنہیں فرعون اور اس کی قوم کے لئے دیا گیا ہے کہ یہ بڑی بدکار قوم ہے'),
(3172, 27, 13, 'مگر جب بھی ان کے پاس واضح نشانیاں آئیں تو انہوں نے کہہ دیا کہ یہ کِھلا ہوا جادو ہے'),
(3173, 27, 14, 'ان لوگوں نے ظلم اور غرور کے جذبہ کی بنائ پر انکار کردیا تھا ورنہ ان کے دل کو بالکل یقین تھا پھر دیکھو کہ ایسے مفسدین کا انجام کیا ہوتا ہے'),
(3174, 27, 15, 'اور ہم نے داؤد اور سلیمان کو علم عطا کیا تو دونوں نے کہا کہ خدا کا شکر ہے کہ اس نے ہمیں بہت سے بندوں پر فضیلت عطا کی ہے'),
(3175, 27, 16, 'اور پھر سلیمان داؤد کے وارث ہوئے اور انہوں نے کہا کہ لوگو مجھے پرندوں کی باتوں کا علم دیا گیا ہے اور ہر فضیلت کا ایک حصہ عطا کیا گیا ہے اور یہ خدا کا کھلا ہوا فضل و کرم ہے'),
(3176, 27, 17, 'اور سلیمان کے لئے ان کا تمام لشکر جنات انسان اور پرندے سب اکٹھا کئے جاتے تھے تو بالکل مرتب منظم کھڑے کر دیئے جاتے تھے'),
(3177, 27, 18, 'یہاں تک کہ جب وہ لوگ وادئی نمل تک آئے تو ایک چیونٹی نے آواز دی کہ چیونٹیوں سب اپنے اپنے سوراخوں میں داخل ہوجاؤ کہ سلیمان اور ان کا لشکر تمہیں پامال نہ کر ڈالے اور انہیں اس کا شعور بھی نہ ہو'),
(3178, 27, 19, 'سلیمان اس کی بات پر مسکرادیئے اور کہا کہ پروردگار مجھے توفیق دے کہ میں تیری اس نعمت کا شکریہ ادا کروں جو تو نے مجھے اور میرے والدین کو عطا کی ہے اور ایسا نیک عمل کروں کہ تو راضی ہوجائے اور اپنی رحمت سے مجھے اپنے نیک بندوں میں شامل کرلے'),
(3179, 27, 20, 'اور سلیمان نے ہد ہد کو تلاش کیا اور وہ نظر نہ آیا تو کہا کہ آخر مجھے کیا ہوگیا ہے کہ میں ہد ہد کو نہیں دیکھ رہا ہوں کیا وہ غائب ہوگیا ہے'),
(3180, 27, 21, 'میں اسے سخت ترین سزادوں گا یا پھر ذبح کر ڈالوں گا یا یہ کہ وہ میرے پاس کوئی واضح دلیل لے آئے گا'),
(3181, 27, 22, 'پھر تھوڑی دیر نہ گزری تھی کہ ہد ہد آگیا اور اس نے کہا کہ مجھے ایک ایسی بات معلوم ہوئی ہے جو آپ کو بھی معلوم نہیں ہے اور میں ملک سبا سے ایک یقینی خبر لے کر آیا ہوں'),
(3182, 27, 23, 'میں نے ایک عورت کو دیکھا ہے جو سب پر حکومت کررہی ہے اور اسے دنیا کی ہر چیز حاصل ہے اور اس کے پاس بہت بڑا تخت بھی ہے'),
(3183, 27, 24, 'میں نے دیکھا ہے کہ وہ اور اس کی قوم سب سورج کی پوجا کررہے ہیں اور شیطان نے ان کی نظروں میں اس عمل کو حسن بنادیا ہے اور انہیں صحیح راستہ سے روک دیا ہے کہ اب وہ یہ بھی نہیں سمجھتے ہیں'),
(3184, 27, 25, 'کہ کیوں نہ اس خدا کا سجدہ کریں جو آسمان و زمین کے پوشیدہ اسرار کو ظاہر کرنے والا ہے اور لوگ جو کچھ چھپاتے ہیں یا ظاہر کرتے ہیں سب کا جاننے والا ہے'),
(3185, 27, 26, 'وہ اللہ ہے جس کے علاوہ کوئی خدا نہیں ہے اور وہ عرش عظیم کا پروردگار ہے'),
(3186, 27, 27, 'سلیمان نے کہا کہ میں ابھی دیکھتا ہوں کہ تو نے سچ کہا ہے یا تیرا شمار بھی جھوٹوں میں ہے'),
(3187, 27, 28, 'یہ میرا خط لے کر جا اور ان لوگوں کے سامنے ڈال دے پھر ان کے پاس سے ہٹ جا اور یہ دیکھ کہ وہ کیا جواب دیتے ہیں'),
(3188, 27, 29, 'اس عورت نے کہا کہ میرے زعمائ سلطنت میری طرف ایک بڑا محترم خط بھیجا گیا ہے'),
(3189, 27, 30, 'جو سلیمان کی طرف سے ہے اور اس کا مضمون یہ ہے کہ شروع کرتا ہوں خدا کے نام سے جو بڑا رحمٰن و رحیم ہے'),
(3190, 27, 31, 'دیکھو میرے مقابلہ میں سرکشی نہ کرو اور اطاعت گزار بن کر چلے آؤ'),
(3191, 27, 32, 'زعمائ مملکت! میرے مسئلہ میں رائے دو کہ میں تمہاری رائے کے بغیر کوئی حتمی فیصلہ نہیں کرسکتی'),
(3192, 27, 33, 'ان لوگوں نے کہا کہ ہم صاحبانِ قوت اور ماہرین جنگ و جدال ہیں اور اختیار بہرحال آپ کے ہاتھ میں ہے آپ بتائیں کہ آپ کا حکلَ کیا ہے'),
(3193, 27, 34, 'اس نے کہا کہ بادشاہ جب کسی علاقہ میں داخل ہوتے ہیں تو بستی کو ویران کردیتے ہیں اور صاحبانِ عزّت کو ذلیل کردیتے ہیں اور ان کا یہی طریقہ کار ہوتا ہے'),
(3194, 27, 35, 'اور میں ان کی طرف ایک ہدیہ بھیج رہی ہوں اور پھر دیکھ رہی ہوں کہ میرے نمائندے کیا جواب لے کر آتے ہیں'),
(3195, 27, 36, 'اس کے بعد جب قاصد سلیمان کے پاس آیا تو انہوں نے کہا کہ تم اپنے مال سے میری امداد کرنا چاہتے ہو جب کہ جو کچھ خدا نے مجھے دیا ہے وہ تمہارے مال سے کہیں زیادہ بہتر ہے جاؤ تم خود ہی اپنے ہدیہ سے خوش رہو'),
(3196, 27, 37, 'جاؤ تم واپس جاؤ اب میں ایک ایسا لشکر لے کر آؤں گا جس کا مقابلہ ممکن نہ ہوگا اور پھر سب کو ذلّت و رسوائی کے ساتھ ملک سے باہر نکالوں گا'),
(3197, 27, 38, 'پھر اعلان کیا کہ میرے اشراف هسلطنت ! تم میں کون ہے جو اس کے تخت کو لے کر آئے قبل اس کے کہ وہ لوگ اطاعت گزار بن کر حاضر ہوں'),
(3198, 27, 39, 'تو جّنات میں سے ایک دیو نے کہا کہ میں اتنی جلدی لے آؤں گا کہ آپ اپنی جگہ سے بھی نہ اٹھیں گے میں بڑا صاحبِ قوت اور ذمہ دار ہوں'),
(3199, 27, 40, 'اور ایک شخص نے جس کے پاس کتاب کا ایک حصہّ علم تھا اس نے کہا کہ میں اتنی جلدی لے آؤں گا کہ آپ کی پلک بھی نہ جھپکنے پائے اس کے بعد سلیمان نے تخت کو اپنے سامنے حاضر دیکھا تو کہنے لگے یہ میرے پروردگار کا فضل و کرم ہے وہ میرا امتحان لینا چاہتا ہے کہ میں شکریہ ادا کرتا ہوں یا کفرانِ نعمت کرتا ہوں اور جو شکریہ ادا کرے گا وہ اپنے ہی فائدہ کے لئے کرے گا اور جو کفران هنعمت کرے گا اس کی طرف سے میرا پروردگار بے نیاز اور کریم ہے'),
(3200, 27, 41, 'سلیمان نے کہا کہ اس کے تخت کو ناقابل شناخت بنادیا جائے تاکہ ہم دیکھیں کہ وہ سمجھ پاتی ہے یا ناسمجھ لوگوں میں ہے'),
(3201, 27, 42, 'جب وہ آئی تو سلیمان نے کہا کہ کیا تمہارا تخت ایسا ہی ہے اس نے کہا کہ بالکل ایسا ہی ہے بلکہ شاید یہی ہے اور مجھے تو پہلے ہی علم ہوگیا تھا اور میں اطاعت گزار ہوگئی تھی'),
(3202, 27, 43, 'اور اسے اس معبود نے روک رکھا تھا جسے خدا کو چھوڑ کر معبود بنائے ہوئے تھی کہ وہ ایک کافر قوم سے تعلق رکھتی تھی'),
(3203, 27, 44, 'پھر اس سے کہا گیا کہ قصر میں داخل ہوجائے اب جو اس نے دیکھا تو سمجھی کہ کوئی گہرا پانی ہے اور اپنی پنڈلیاں کھول دیں سلیمان نے کہا کہ یہ ایک قلعہ ہے جسے شیشوں سے منڈھ دیا گیا ہے اور بلقیس نے کہا کہ میں نے اپنے نفس پر ظلم کیا تھا اور اب میں سلیمان کے ساتھ اس خدا پر ایمان لے آئی ہوں جو عالمین کا پالنے والا ہے'),
(3204, 27, 45, 'اور ہم نے قوم ثمود کی طرف ان کے بھائی صالح کو بھیجا کہ تم لوگ اللہ کی عبادت کرو تو دونوں فریق آپس میں جھگڑا کرنے لگے'),
(3205, 27, 46, 'صالح نے کہا کہ قوم والو آخر بھلائی سے پہلے برائی کی جلدی کیوں کررہے ہو تم لوگ اللہ سے استغفار کیوں نہیں کرتے کہ شاید تم پر رحم کردیا جائے'),
(3206, 27, 47, 'ان لوگوں نے کہا کہ ہم نے تم سے اور تمہارے ساتھیوں سے براشگون ہی پایا ہے انہوں نے کہا کہ تمہاری بدقسمتی اللہ کے پاس مقدر ہے اور یہ درحقیقت تمہاری آزمائش کی جارہی ہے'),
(3207, 27, 48, 'اور اس شہر میں نو افراد تھے جو زمین میں فساد برپا کرتے تھے اور اصلاح نہیں کرتے تھے'),
(3208, 27, 49, 'ان لوگوں نے کہا کہ تم سب آپس میں خدا کی قسم کھاؤ کہ صالح اور ان کے گھر والوں پر راتوں رات حملہ کردو گے اور بعد میں ان کے وارثوں سے کہہ دو گے کہ ہم ان کے گھر والوں کی ہلاکت کے وقت موجود ہی نہیں تھے اور ہم اپنے بیان میں بالکل سچےّ ہیں'),
(3209, 27, 50, 'اور پھر انہوں نے اپنی چال چلی اور ہم نے بھی اپنا انتظام کیا کہ انہیں خبر بھی نہ ہوسکی'),
(3210, 27, 51, 'پھر اب دیکھو کہ ان کی مکاّری کا انجام کیا ہوا کہ ہم نے ان رؤسائ کو ان کی قوم سمیت بالکل تباہ وبرباد کردیا'),
(3211, 27, 52, 'اب یہ ان کے گھر ہیں جو ظلم کی بنائ پر خالی پڑے ہوئے ہیں اور یقینا اس میں صاحبانِ علم کے لئے ایک نشانی پائی جاتی ہے'),
(3212, 27, 53, 'اور ہم نے ان لوگوں کو نجات د ے دی جو ایمان والے تھے اور تقویٰ الٰہی اختیار کئے ہوئے تھے'),
(3213, 27, 54, 'اور لوط کو یاد کرو جب انہوں نے اپنی قوم سے کہا کہ کیا تم آنکھیں رکھتے ہوئے بدکاری کا ارتکاب کر رہے ہو'),
(3214, 27, 55, 'کیا تم لوگ ازراہ هشہوت همذِدوں سے تعلقات پیدا کررہے ہو اور عورتوں کو چھوڑے دے رہے ہو اور درحقیقت تم لوگ بالکل جاہل قوم ہو'),
(3215, 27, 56, 'تو ان کی قوم کا کوئی جواب نہیں تھا سوائے اس کے کہ لوط والوں کو اپنی بستی سے نکال باہر کردو کہ یہ لوگ بہت پاکباز بن رہے ہیں'),
(3216, 27, 57, 'تو ہم نے لوط اور ان کے خاندان والوں کو زوجہ کے علاوہ سب کو نجات دے دی کہ وہ پیچھے رہ جانے والوں میں تھی'),
(3217, 27, 58, 'اور ہم نے ان پر عجیب و غریب قسم کی بارش کردی کہ جن لوگوں کو ڈرایا جاتا ہے ان پر بارشِ عذاب بھی بہت بری ہوتی ہے'),
(3218, 27, 59, 'آپ کہئے ساری تعریف اللہ کے لئے ہے اور سلام ہے اس کے ان بندوں پر جنہیں اس نے منتخب کرلیا ہے آیا خدا زیادہ بہتر ہے یا جنہیں یہ شریک بنارہے ہیں'),
(3219, 27, 60, 'بھلا وہ کون ہے جس نے آسمان و زمین کو پیدا کیا ہے اور تمہارے لئے آسمان سے پانی برسایا ہے پھر ہم نے اس سے خوشنما باغ اگائے ہیں کہ تم ان کے درختوں کو نہیں اگا سکتے تھے کیا خدا کے ساتھ کوئی اور خدا ہے ... نہیں بلکہ یہ لوگ خود اپنی طرف سے دوسروں کو خدا کے برابر بنارہے ہیں'),
(3220, 27, 61, 'بھلا وہ کون ہے جس نے زمین کو قرار کی جگہ بنایا اور پھر اس کے درمیان نہریں جاری کیں اور اس کے لئے پہاڑ بنائے اور دو دریاؤں کے درمیان حد فاصل قرار دی کیا خدا کے ساتھ کوئی اور بھی خدا ہے ہرگز نہیں اصل یہ ہے کہ ان کی اکثریت جاہل ہے'),
(3221, 27, 62, 'بھلا وہ کون ہے جو مضطر کی فریاد کو سنتا ہے جب وہ اس کو آواز دیتا ہے اور اس کی مصیبت کو دور کردیتا ہے اور تم لوگوں کو زمین کا وارث بناتا ہے کیا خدا کے ساتھ کوئی اور خدا ہے - نہیں - بلکہ یہ لوگ بہت کم نصیحت حاصل کرتے ہیں'),
(3222, 27, 63, 'بھلا وہ کون ہے جو خشکی اور تری کی تاریکیوں میں تمہاری رہنمائی کرتا ہے اور بارش سے پہلے بشارت کے طور پر ہوائیں چلاتا ہے کیا اللہ کے ساتھ کوئی اور خدا ہے - یقینا وہ خدا تمام مخلوقات سے کہیں زیادہ بلند و بالا ہے جنہیں یہ لوگ اس کا شریک بنارہے ہیں'),
(3223, 27, 64, 'یا کون ہے جو خلق کی ابتدا کرتا ہے اور پھر دوبارہ بھی وہی پیدا کرے گا اور کون ہے جو آسمان اور زمین سے رزق عطا کرتا ہے کیا خدا کے ساتھ کوئی اور بھی معبود ہے آپ کہہ دیجئے کہ اگر تم اپنے دعویٰ میں سچے ہو تو اپنی دلیل لے آؤ'),
(3224, 27, 65, 'کہہ دیجئے کہ آسمان و زمین میں غیب کا جاننے والا اللہ کے علاوہ کوئی نہیں ہے اور یہ لوگ نہیں جانتے ہیں کہ انہیں کب دوبارہ اٹھایا جائے گا'),
(3225, 27, 66, 'بلکہ آخرت کے بارے میں ان کا علم ناقص رہ گیا ہے بلکہ یہ اس کی طرف سے شک میں مبتلا ہیں بلکہ یہ بالکل اندھے ہیں'),
(3226, 27, 67, 'اورکفّار یہ کہتے ہیں کہ کیا جب ہم اور ہمارے باپ دادا سب مٹی ہوجائیں گے تو پھر دوبارہ نکالے جائیں گے'),
(3227, 27, 68, 'ایسا وعدہ ہم سے اور ہمارے باپ دادا سے بہت پہلے سے کیا جارہا ہے اور یہ سب اگلے لوگوں کی کہانیاں ہیں اور بس'),
(3228, 27, 69, 'آپ ان سے کہہ دیجئے کہ روئے زمین میں سیر کرو اور پھر دیکھو کہ مجرمین کا انجام کیسا ہوا ہے'),
(3229, 27, 70, 'اور آپ ان کے حال پر رنجیدہ نہ ہوں اور یہ جو چالیں چل رہے ہیں ان کی طرف سے بھی دل تنگ نہ ہوں'),
(3230, 27, 71, 'اور یہ لوگ کہتے ہیں کہ اگر تم سچے ہو تو یہ وعدہ آخر کب پورا ہونے والا ہے'),
(3231, 27, 72, 'تو کہہ دیجئے کہ بہت ممکن ہے کہ جس عذاب کی تم جلدی کررہے ہو اس کا کوئی حصّہ تمہارے پیچھے ہی لگاہوا ہو'),
(3232, 27, 73, 'اور آپ کا پروردگار بندوں کے حق میں بہت زیادہ مہربان ہے لیکن ان کی اکثریت شکریہ نہیں ادا کرتی ہے'),
(3233, 27, 74, 'اور آپ کا پروردگار وہ سب جانتا ہے جسے ان کے دل چھپائے ہوئے ہیں یا جس کا یہ اعلان کررہے ہیں'),
(3234, 27, 75, 'اور آسمان و زمین میں کوئی پوشیدہ چیز ایسی نہیں ہے جس کا ذکر کتاب مبین میں نہ ہو'),
(3235, 27, 76, 'بیشک یہ قرآن بنی اسرائیل کے سامنے ان بہت سی باتوں کی حکایت کرتا ہے جن کے بارے میں وہ آپس میں اختلاف کررہے ہیں'),
(3236, 27, 77, 'اور یہ قرآن صاحبان هایمان کے لئے ہدایت اور رحمت ہے'),
(3237, 27, 78, 'آپ کا پروردگار ان کے درمیان اپنے حکم سے فیصلہ کرتا ہے اور وہ سب پر غالب بھی ہے اور سب سے باخبر بھی ہے'),
(3238, 27, 79, 'لہٰذا آپ اسی پر اعتماد کریں کہ آپ واضح حق کے راستہ پر ہیں'),
(3239, 27, 80, 'آپ مفِدوں کو اور بہروں کو اپنی آواز نہیں سنا سکتے اگر وہ منہ پھیر کر بھاگ کھڑے ہوں'),
(3240, 27, 81, 'اور آپ اندھوں کو بھی ان کی گمراہی سے راہ هراست پر نہیں لاسکتے ہیں آپ اپنی آواز صرف ان لوگوں کو سناسکتے ہیں جو ہماری آیتوں پر ایمان رکھتے ہیں اور ہمارے اطاعت گزار ہیں'),
(3241, 27, 82, 'اور جب ان پر وعدہ پورا ہوگا تو ہم زمین سے ایک چلنے والا نکال کر کھڑا کردیں گے جو ان سے یہ بات کرے کہ کون لوگ ہماری آیات پر یقین نہیں رکھتے تھے'),
(3242, 27, 83, 'اور اس دن ہم ہر امت میں سے وہ فوج اکٹھا کریں گے جو ہماری آیتوں کی تکذیب کیا کرتے تھے اور پھر الگ الگ تقسیم کردیئے جائیں گے'),
(3243, 27, 84, 'یہاں تک کہ جب سب آجائیں گے تو ارشاد احدیت ہوگا کہ کیا تم لوگوں نے میری آیتوں کی تکذیب کی تھی حالانکہ تمہیں ان کا مکمل علم نہیں تھا یا تم کیا کررہے تھے'),
(3244, 27, 85, 'اور ان کے ظلم کی بنا پر ان پر بات ثابت ہوجائے گی اور وہ بولنے کے قابل بھی نہ ہوں گے'),
(3245, 27, 86, 'کیا ان لوگوں نے نہیں دیکھا کہ ہم نے رات کو پیدا کیا تاکہ یہ سکون حاصل کرسکیں اور دن کو روشنی کا ذریعہ بنایا اس میں صاحبان ه ایمان کے لئے ہماری بڑی نشانیاں ہیں'),
(3246, 27, 87, 'اور جس دن صور پھونکا جائے گا تو زمین و آسمان میں جو بھی ہے سب لرز جائیں گے علاوہ ان کے جن کو خدا چاہے اور سب اس کی بارگاہ میں سر جھکائے حاضر ہوں گے'),
(3247, 27, 88, 'اور تم دیکھو گے تو سمجھو گے کہ جیسے پہاڑ اپنی جگہ پر جامد ہیں حالانکہ یہ بادلوں کی طرح چل رہے ہوں گے .یہ اس خدا کی صنعت ہے جس نے ہر چیز کو محکم بنایا ہے اور وہ تمہارے تمام اعمال سے باخبر ہے'),
(3248, 27, 89, 'جو کوئی نیکی کرے گا اسے اس سے بہتر اجر ملے گا اور وہ لوگ روزِ قیامت کے خوف سے محفوظ بھی رہیں گے'),
(3249, 27, 90, 'اور جو لوگ برائی کریں گے انہیں منہ کے بھل جہّنم میں ڈھکیل دیا جائے گا کہ کیا تمہیں تمہارے اعمال کے علاوہ بھی کوئی معاوضہ دیا جاسکتا ہے'),
(3250, 27, 91, 'مجھے تو صرف یہ حکم دیا گیا ہے کہ میں اس شہر کے مالک کی عبادت کروں جس نے اسے محترم بنایا ہے اور ہر شے اسی کی ملکیت ہے اور مجھے یہ حکم دیا گیا ہے کہ میں اطاعت گزاروں میں شامل ہوجاؤں'),
(3251, 27, 92, 'اور یہ کہ میں قرآن کو پڑھ کر صَناؤں اب اس کے بعد جو ہدایت حاصل کرلے گا وہ اپنے فائدہ کے لئے کرے گا اور جو بہک جائے گا اس سے کہہ دیجئے کہ میں تو صرف ڈرانے والوں میں سے ہوں'),
(3252, 27, 93, 'اور یہ کہئے کہ ساری حمد صرف اللہ کے لئے ہے اور وہ عنقریب تمہیں اپنی نشانیاں دکھلائے گا اور تم پہچان لو گے اور تمہارا پروردگار تمہارے اعمال سے سے غافل نہیں ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طۤسۤم'),
(3254, 28, 2, 'یہ کتابِ مبین کی آیتیں ہیں'),
(3255, 28, 3, 'ہم آپ کو موسٰی اور فرعون کی سّچی خبر سنارہے ہیں ان لوگوں کے لئے جو ایمان لانے والے ہیں'),
(3256, 28, 4, 'فرعون نے روئے زمین پر بلندی اختیار کی اور اس نے اہلِ زمین کو مختلف حصوں میں تقسیم کردیا کہ ایک گروہ نے دوسرے کو بالکل کمزور بنادیا وہ لڑکوں کو ذبح کردیا کرتا تھا اور عورتوں کو زندہ رکھا کرتا تھا. وہ یقینا مفسدین میں سے تھا'),
(3257, 28, 5, 'اور ہم یہ چاہتے ہیں کہ جن لوگوں کو زمین میں کمزور بنادیا گیا ہے ان پر احسان کریں اور انہیں لوگوں کا پیشوا بنائیں اور زمین کا وارث قرار دیدیں'),
(3258, 28, 6, 'اور انہی کو روئے زمین کا اقتدار دیں اور فرعون وہامان اور ان کے لشکروں کو ان ہی کمزوروں کے ہاتھوں سے وہ منظر دکھلائیں جس سے یہ ڈر رہے ہیں'),
(3259, 28, 7, 'اور ہم نے مادر موسٰی کی طرف وحی کی کہ اپنے بچّہ کو دودھ پلاؤ اور اس کے بعد جب اس کی زندگی کا خوف پیدا ہو تو اسے دریا میں ڈال دو اور بالکل ڈرو نہیں اور پریشان نہ ہو کہ ہم اسے تمہاری طرف پلٹا دینے والے اور اسے مرسلین میں سے قرار دینے والے ہیں'),
(3260, 28, 8, 'پھر فرعون والوںنے اسے اٹھالیا کہ انجام کار ان کا دشمن اور ان کے لئے باعث هرنج و الم بنے .بیشک فرعون اور ہامان اور ان کے لشکر والے سب غلطی پر تھے'),
(3261, 28, 9, 'اور فرعون کی زوجہ نے کہا کہ یہ تو ہماری اور تمہاری آنکھوں کی ٹھنڈک ہے لہٰذا اسے قتل نہ کرو کہ شاید یہ ہمیں فائدہ پہنچائے اور ہم اسے اپنا فرزند بنالیں اور وہ لوگ کچھ نہیں سمجھ رہے تھے'),
(3262, 28, 10, 'اور موسٰی کی ماں کا دل بالکل خالی ہوگیا کہ قریب تھا کہ وہ اس راز کو فاش کردیتیں اگر ہم ان کے دل کو مطمئن نہ بنادیتے تاکہ وہ ایمان لانے والوں میں شامل ہوجائیں'),
(3263, 28, 11, 'اور انہوں نے اپنی بہن سے کہا کہ تم بھی ان کا پیچھا کرو تو انہوں نے دور سے موسٰی کو دیکھا جب کہ ان لوگوں کو اس کا احساس بھی نہیں تھا'),
(3264, 28, 12, 'اور ہم نے موسٰی پر دودھ پلانے والیوں کا دودھ پہلے ہی سے حرام کردیا تو موسٰی کی بہن نے کہا کہ کیا میں تمہیں ایسے گھر والوں کا پتہ بتاؤں جو اس کی کفالت کرسکیں اور وہ اس کے خیر خواہ بھی ہوں'),
(3265, 28, 13, 'پھر ہم نے موسٰی کو ان کی ماں کی طرف پلٹا دیا تاکہ ان کی آنکھ ٹھنڈی ہوجائے اور وہ پریشان نہ رہیں اور انہیں معلوم ہوجائے کہ اللہ کا وعدہ بہرحال سچا ّہے اگرچہ لوگوں کی اکثریت اسے نہیں جانتی ہے'),
(3266, 28, 14, 'اور جب موسٰی جوانی کی توانائیوں کو پہنچے اور تندرست ہوگئے تو ہم نے انہیں علم اور حکمت عطا کردی اور ہم اسی طرح نیک عمل والوں کو جزا دیا کرتے ہیں'),
(3267, 28, 15, 'اور موسٰی شہر میں اس وقت داخل ہوئے جب لوگ غفلت کی نیند میں تھے تو انہوں نے دو آدمیوں کو لڑتے ہوئے دیکھا ایک ان کے شیعوں میں سے تھا اور ایک دشمنوں میں سے تو جو ان کے شیعوں میں سے تھا اس نے دشمن کے ظلم کی فریاد کی تو موسٰی نے اسے ایک گھونسہ مار کر اس کی زندگی کا فیصلہ کردیا اور کہا کہ یہ یقینا شیطان کے عمل سے تھا اور یقینا شیطان دشمن اور کھنَا ہوا گمراہ کرنے والا ہے'),
(3268, 28, 16, 'موسٰی نے کہا کہ پروردگار میں نے اپنے نفس کے لئے مصیبت مول لے لی لہذا مجھ معاف کردے تو پروردگار نے معاف کردیا کہ وہ بہت بخشنے والا اور مہربان ہے'),
(3269, 28, 17, 'موسٰی نے کہا کہ پروردگار تونے میری مدد کی ہے لہذا میں کبھی مجرموں کا ساتھی نہیں بنوں گا'),
(3270, 28, 18, 'پھر صبح کے وقت موسٰی شہر میں داخل ہوئے تو خوفزدہ اور حالات کی نگرانی کرتے ہوئے کہ اچانک دیکھا کہ جس نے کل مدد کے لئے پکارا تھا وہ پھر فریاد کررہا ہے موسٰی نے کہا کہ یقینا تو کھنَا ہوا گمراہ ہے'),
(3271, 28, 19, 'پھر جب موسٰی نے چاہا کہ اس پر حملہ آور ہوں جو دونوں کا دشمن ہے تو اس نے کہا کہ موسٰی تم اسی طرح مجھے قتل کرنا چاہتے ہو جس طرح تم نے کل ایک بے گناہ کو قتل کیا ہے تم صرف روئے زمین میں سرکش حاکم بن کر رہنا چاہتے ہو اور یہ نہیں چاہتے ہو کہ تمہارا شمار اصلاح کرنے والوں میں ہو'),
(3272, 28, 20, 'اور ادھر آخ» شہر سے ایک شخص دوڑتا ہوا آیا اور اس نے کہا کہ موسٰی شہر کے بڑے لوگ باہمی مشورہ کررہے ہیں کہ تمہیں قتل کردیں لہذا تم شہر سے باہر نکل جاؤ میں تمہارے لئے نصیحت کرنے والوں میں سے ہوں'),
(3273, 28, 21, 'تو موسٰی شہر سے باہر نکلے خوفزدہ اور دائیں بائیں دیکھتے ہوئے اور کہا کہ پروردگار مجھے ظالم قوم سے محفوظ رکھنا'),
(3274, 28, 22, 'اور جب موسٰی نے مدین کا رخ کیا تو کہا کہ عنقریب پروردگار مجھے سیدھے راستہ کی ہدایت کردے گا'),
(3275, 28, 23, 'اور جب مدین کے چشمہ پروارد ہوئے تو لوگوں کی ایک جماعت کو دیکھا جو جانوروں کو پانی پلارہی تھی اور ان سے الگ دو عورتیں تھیں جو جانوروں کو روکے کھڑی تھیں .موسٰی نے پوچھا کہ تم لوگوں کا کیا مسئلہ ہے ان دونوں نے کہا کہ ہم اس وقت تک پانی نہیں پلاتے ہیں جب تک ساری قوم ہٹ نہ جائے اور ہمارے بابا ایک ضعیف العمر آدمی ہیں'),
(3276, 28, 24, 'موسٰی نے دونوں کے جانوروں کو پانی پلادیا اور پھر ایک سایہ میں آکر پناہ لے لی عرض کی پروردگار یقینا میں اس خیر کا محتاج ہوں جو تو میری طرف بھیج دے'),
(3277, 28, 25, 'اتنے میں دونوں میں سے ایک لڑکی کمال شرم و حیا کے ساتھ چلتی ہوئی آئی اور اس نے کہا کہ میرے بابا آپ کو بلارہے ہیں کہ آپ کے پانی پلانے کی اجرت دے دیں پھر جو موسٰی ان کے پاس آئے اور اپنا قصّہ بیان کیا تو انہوں نے کہا کہ ڈرو نہیں اب تم ظالم قوم سے نجات پاگئے'),
(3278, 28, 26, 'ان دونوں میں سے ایک لڑکی نے کہا کہ بابا آپ انہیں نوکر رکھ لیجئے کہ آپ جسے بھی نوکر رکھنا چاہیں ان میں سب سے بہتر وہ ہوگا جو صاحبِ قوت بھی ہو اور امانتدار بھی ہو'),
(3279, 28, 27, 'انہوں نے کہا کہ میں ان دونوں میں سے ایک بیٹی کا عقد آپ سے کرنا چاہتا ہوں بشرطیکہ آپ آٹھ سال تک میری خدمت کریں پھر اگر دس سال پورے کردیں تو یہ آپ کی طرف سے ہوگا اور میں آپ کو کوئی زحمت نہیں دینا چاہتا ہوں انشائ اللہ آپ مجھے نیک بندوں میں سے پائیں گے'),
(3280, 28, 28, 'موسٰی نے کہا کہ یہ میرے اور آپ کے درمیان کا معاہدہ ہے میں جو مدّت بھی پوری کردوں میرے اوپر کوئی ذمہ داری نہیں ہوگی اور میں جو کچھ بھی کہہ رہاہوں اللہ اس کا گواہ ہے'),
(3281, 28, 29, 'پھر جب موسٰی مدّت کو پورا کرچکے اور اپنے اہل کو لے کر چلے تو طور کی طرف سے ایک آگ نظر آئی انہوں نے اپنے اہل سے کہا کہ تم لوگ ٹھہرو میں نے ایک آگ دیکھی ہے شاید اس میں سے کوئی خبر لے آؤں یا کوئی چنگاری ہی لے آؤں کہ تم لوگ اس سے تاپنے کا کام لے سکو'),
(3282, 28, 30, 'پھر جو اس آگ کے قریب آئے تو وادی کے داہنے رخ سے ایک مبارک مقام پر ایک درخت سے آوز آئی موسٰی میں عالمین کا پالنے والا خدا ہوں'),
(3283, 28, 31, 'اور تم اپنے عصا کو زمین پر ڈال دو اور جو موسٰی نے دیکھا تو وہ سانپ کی طرح لہرا رہا تھا یہ دیکھ کر موسٰی پیچھے ہٹ گئے اور پھر مڑ کر بھی نہ دیکھا تو پھر آواز آئی کہ موسٰی آگے بڑھو اور ڈرو نہیں کہ تم بالکل مامون و محفوظ ہو'),
(3284, 28, 32, 'ذرا اپنے ہاتھ کو گریبان میں داخل کرو وہ بغیر کسی بیماری کے سفید اور چمکدار بن کر برآمد ہوگا اور خوف سے اپنے بازوؤں کو اپنی طرف سمیٹ لو - تمہارے لئے پروردگار کی طرف سے فرعون اور اس کی قوم کے سرداروں کی طرف یہ دو دلیلیں ہیں کہ یہ لوگ سب فاسق اور بدکار ہیں'),
(3285, 28, 33, 'موسٰی نے کہا کہ پروردگار میں نے ان کے ایک آدمی کو مار ڈالا ہے تو مجھے خوف ہے کہ یہ مجھے قتل کردیں گے اور کار تبلیغ رک جائے گا'),
(3286, 28, 34, 'اور میرے بھائی ہارون مجھ سے زیادہ فصیح زبان کے مالک ہیں لہٰذا انہیں میرے ساتھ مددگار بنادے جو میری تصدیق کرسکیں کہ میں ڈرتا ہوں کہ کہیں یہ لوگ میری تکذیب نہ کردیں'),
(3287, 28, 35, 'ارشاد ہوا کہ ہم تمہارے بازؤں کو تمہارے بھائی سے مضبوط کردیں گے اور تمہارے لئے ایسا غلبہ قرار دیں گے کہ یہ لوگ تم تک پہنچ ہی نہ سکیں اور ہماری نشانیوں کے سہارے تم اور تمہارے پیروکار ہی غالب رہیں گے'),
(3288, 28, 36, 'پھر جب موسٰی ان کے پاس ہماری کھلی ہوئی نشانیاں لے کر آئے تو ان لوگوں نے کہہ دیا کہ یہ تو صرف ایک گڑھا ہوا جادو ہے اور ہم نے اپنے گزشتہ بزرگوں سے اس طرح کی کوئی بات نہیں سنی ہے'),
(3289, 28, 37, 'اور موسٰی نے کہا کہ میرا پروردگار بہتر جانتا ہے کہ کون اس کی طرف سے ہدایت لے کر آیا ہے اور کس کے لئے آخرت کا گھر ہے یقینا ظالمین کے لئے فلاح نہیں ہے'),
(3290, 28, 38, 'اور فرعون نے کہا کہ میرے زعما ئ مملکت! میرے علم میں تمہارے لئے میرے علاوہ کوئی خدا نہیں ہے لہٰذا ہامان! تم میرے لئے مٹی کا پجاوا لگاؤ اور پھر ایک قلعہ تیار کرو کہ میں اس پر چڑھ کر موسٰی کے خدا کی خبر لے آؤں اور میرا خیال تو یہ ہی ہے کہ موسٰی جھوٹے ہیں'),
(3291, 28, 39, 'اور فرعون اور اس کے لشکر نے ناحق غرور سے کام لیا اور یہ خیال کرلیا کہ وہ پلٹا کر ہماری بارگاہ میں نہیں لائے جائیں گے'),
(3292, 28, 40, 'تو ہم نے فرعون اور اس کے لشکروں کو اپنی گرفت میں لے لیا اور سب کو دریا میں ڈال دیا تو دیکھو کہ ظالمین کا انجام کیسا ہوتا ہے'),
(3293, 28, 41, 'اور ہم نے ان لوگوں کوجہّنم کی طرف دعوت دینے والا پیشوا قرار د ے دیا ہے اور قیامت کے دن ان کی کوئی مدد نہیں کی جائے گی'),
(3294, 28, 42, 'اور دنیا میں بھی ہم نے ان کے پیچھے لعنت کو لگادیا ہے اور قیامت کے دن بھی ان کا شمار ان لوگوں میں ہوگا جن کے چہرے بگاڑ دیئے جائیں گے'),
(3295, 28, 43, 'اور ہم نے پہلی نسلوں کو ہلاک کردینے کے بعد موسٰی کو کتاب عطا کی جو لوگوں کے لئے بصیرت کا سامان اور ہدایت و رحمت ہے اور شاید اسی طرح یہ لوگ نصیحت حاصل کرلیں'),
(3296, 28, 44, 'اور آپ اس وقت طور کے مغربی رخ پر نہ تھے جب ہم نے موسٰی کی طرف اپنا حکم بھیجا اور آپ اس واقعہ کے حاضرین میں سے بھی نہیں تھے'),
(3297, 28, 45, 'لیکن ہم نے بہت سی قوموں کو پیدا کیا پھر ان پر ایک طویل زمانہ گزر گیا اور آپ تو اہل مدین میں بھی مقیم نہیں تھے کہ انہیں ہماری آیتیں پڑھ کر سناتے لیکن ہم رسول بنانے والے توتھے'),
(3298, 28, 46, 'اور آپ طِور کے کسی جانب اس وقت نہیں تھے جب ہم نے موسٰی کو آواز دی لیکن آپ کے پروردگار کی رحمت ہے کہ آپ اس قوم کو ڈرائیں جس کی طرف آپ سے پہلے کوئی پیغمبر نہیں آیا ہے کہ شاید وہ اس طرح عبرت اور نصیحت حاصل کرلیں'),
(3299, 28, 47, 'اور اگر ایسا نہ ہوتا کہ جب ان پر گذشتہ اعمال کی بنا پر کوئی مصیبت نازل ہوتی تو یہی کہتے کہ پروردگار تو نے ہماری طرف کوئی رسول کیوں نہیں بھیجا ہے کہ ہم تیری نشانیوں کی پیروی کرتے اور صاحبان ایمان میں شامل ہوجاتے'),
(3300, 28, 48, 'پھر اس کے بعد جب ہماری طرف سے حق آگیا تو کہنے لگے کہ انہیں وہ سب کیوں نہیں دیا گیا ہے جو موسٰی کو دیا گیا تھا تو کیا ان لوگوں نے اس سے پہلے موسٰی کا انکار نہیں کیا اور اب تو کہتے ہیں کہ توریت اور قرآن جادو ہے جو ایک دوسرے کی تائید کرتے ہیں اور ہم دونوں ہی کا انکار کرنے والے ہیں'),
(3301, 28, 49, 'تو آپ کہہ دیجئے کہ اچھا تم پروردگار کی طرف سے کوئی کتاب لے آؤ جو دونوں سے زیادہ صحیح ہو اور میں اس کا اتباع کرلوں اگر تم اپنی بات میں سچے ہو'),
(3302, 28, 50, 'پھر اگر قبول نہ کریں تو سمجھ لیجئے کہ یہ صرف اپنی خواہشات کا اتباع کرنے والے ہیں اور اس سے زیادہ گمراہ کون ہے جو خدائی ہدایت کے بغیر اپنی خواہشات کا اتباع کرلے جب کہ اللہ ظالم قوم کی ہدایت کرنے والا نہیں ہے'),
(3303, 28, 51, 'اور ہم نے مسلسل ان لوگوں تک اپنی باتیں پہنچائیں کہ شاید اسی طرح نصیحت حاصل کرلیں'),
(3304, 28, 52, 'جن لوگوں کو ہم نے اس سے پہلے کتاب دی ہے وہ اس قرآن پر ایمان رکھتے ہیں'),
(3305, 28, 53, 'اور جب ان کے سامنے اس کی تلاوت کی جاتی ہے تو کہتے ہیں کہ ہم ایمان لے آئے یہ ہمارے رب کی طرف سے برحق ہے اور ہم تو پہلے ہی سے تسلیم کئے ہوئے تھے'),
(3306, 28, 54, 'یہی وہ لوگ ہیں جن کو دہری جزا دی جائے گی کہ انہوں نے صبر کیا ہے اور یہ نیکیوں کے ذریعہ برائیوں کو دفع کرتے ہیں اور ہم نے جو رزق دیا ہے اس میں سے خرچ کرتے ہیں'),
(3307, 28, 55, 'اور جب لغو بات سنتے ہیں تو کنارہ کشی اختیار کرتے ہیں اور کہتے ہیں کہ ہمارے لئے ہمارے اعمال ہیں اور تمہارے لئے تمہارے اعمال ہیں تم پر ہمارا سلام کہ ہم جاہلوں کی صحبت پسند نہیں کرتے ہیں'),
(3308, 28, 56, 'پیغمبر بیشک آپ جسے چاہیں اسے ہدایت نہیں دے سکتے ہیں بلکہ اللہ جسے چاہتا ہے ہدایت دے دیتا ہے اور وہ ان لوگوں سے خوب باخبر ہے جو ہدایت پانے والے ہیں'),
(3309, 28, 57, 'اور یہ کفاّر کہتے ہیں کہ ہم آپ کے ساتھ حق کی پیروی کریں گے تو اپنی زمین سے اچک لئے جائیں گے تو کیا ہم نے انہیں ایک محفوظ حرم پر قبضہ نہیں دیا ہے جس کی طرف ہر شے کے پھل ہماری دی ہوئی روزی کی بنا پر چلے آرہے ہیں لیکن ان کی اکثریت سمجھتی ہی نہیں ہے'),
(3310, 28, 58, 'اور ہم نے کتنی ہی بستیوں کو ان کی معیشت کے غرور کی بنا پر ہلاک کردیا اب یہ ان کے مکانات ہیں جو ان کے بعد پھر آباد نہ ہوسکے مگر بہت کم اور درحقیقت ہم ہی ہر چیز کے وارث اور مالک ہیں'),
(3311, 28, 59, 'اور آپ کا پروردگار کسی بستی کو ہلاک کرنے والا نہیں ہے جب تک کہ اس کے مرکزمیں کوئی رسول نہ بھیج دے جو ان کے سامنے ہماری آیات کی تلاوت کرے اور ہم کسی بستی کے تباہ کرنے والے نہیں ہیں مگر یہ کہ اس کے رہنے والے ظالم ہوں'),
(3312, 28, 60, 'اور جو کچھ بھی تمہیں عطا کیا گیا ہے یہ چند روزہ لذت هدنیا اور زینت هدنیا ہے اور جو کچھ بھی خدا کی بارگاہ میں ہے وہ خیر اور باقی رہنے والا ہے کیا تم اتنا بھی نہیں سمجھتے ہو'),
(3313, 28, 61, 'کیا وہ بندہ جس سے ہم نے بہترین وعدہ کیا ہے اور وہ اسے پا بھی لے گا اس کے مانند ہے جسے ہم نے دنیا میں تھوڑی سی لذّت دے دی ہے اور پھر وہ روزہ قیامت خدا کی بارگاہ میں کھینچ کر حاضر کیا جائے گا'),
(3314, 28, 62, 'جس دن خدا ان لوگوں کو آواز دے گا کہ میرے وہ شرکائ کہاں ہیں جن کی شرکت کا تمہیں خیال تھا'),
(3315, 28, 63, 'تو جن شرکائ پر عذاب ثابت ہوچکا ہوگا وہ کہیں گے کہ پروردگار یہ ہیں وہ لوگ جن کو ہم نے گمراہ کیا ہے اور اسی طرح گمراہ کیا ہے جس طرح ہم خود گمراہ ہوئے تھے لیکن اب ان سے برائت چاہتے ہیں یہ ہماری عبادت تو نہیں کررہے تھے'),
(3316, 28, 64, 'تو کہا جائے گا کہ اچھا اپنے شرکائ کو پکارو تو وہ پکاریں گے لیکن کوئی جواب نہ پائیں گے بلکہ عذاب ہی کو دیکھیں گے تو کاش یہ دنیا ہی میں ہدایت یافتہ ہوجاتے'),
(3317, 28, 65, 'اور جس دن خدا ان سے پکار کر کہے گا کہ تم نے ہمارے رسولوں کو کیا جواب دیا تھا'),
(3318, 28, 66, 'تو ان پر ساری خبریں تاریک ہوجائیں گی اور وہ آپس میں ایک دوسرے سے سوال بھی نہ کرسکیں گے'),
(3319, 28, 67, 'لیکن جس نے توبہ کرلی اور ایمان لے آیا اور نیک عمل کیا وہ یقینا فلاح پانے والوں میں شمار ہوجائے گا'),
(3320, 28, 68, 'اور آپ کا پروردگار جسے چاہتا ہے پیدا کرتا ہے اور پسندکرتا ہے - ان لوگوں کو کسی کا انتخاب کرنے کا کوئی حق نہیں ہے - خدا ان کے شرک سے پاک اور بلند و برتر ہے'),
(3321, 28, 69, 'اور آپ کا پروردگار ان چیزوں کو بھی جانتا ہے جو یہ اپنے سینوں میں چھپائے ہوئے ہیں اور انہیں بھی جانتا ہے جن کا یہ اعلان و اظہار کرتے ہیں'),
(3322, 28, 70, 'وہ اللہ ہے جس کے علاوہ کوئی دوسرا خدا نہیں ہے اور اول و آخر اور دنیا و آخرت میں ساری حمد اسی کے لئے ہے اسی کے لئے حکم ہے اور اسی کی طرف تم سب کو واپس جانا پڑے گا'),
(3323, 28, 71, 'آپ کہئے کہ تمہارا کیا خیال ہے اگر خدا تمہارے لئے رات کو قیامت تک کے لئے ابدی بنادے تو کیا اس کے علاوہ اور کوئی معبود ہے جو تمہارے لئے روشنی کو لے آسکے تو کیا تم بات سنتے نہیں ہو'),
(3324, 28, 72, 'اور کہئے کہ اگر وہ دن کو قیامت تک کے لئے دائمی بنادے تو اس کے علاوہ کون معبود ہے - جوتمہارے لئے وہ رات لے آئے گا جس میں سکون حاصل کرسکو کیا تم دیکھتے نہیں ہو'),
(3325, 28, 73, 'یہ اس کی رحمت کا ایک حصہّ ہے کہ اس نے تمہارے لئے رات اور دن دونوں بنائے ہیں تاکہ آرام بھی کرسکو اور رزق بھی تلاش کرسکو اور شاید اس کا شکریہ بھی ادا کرسکو'),
(3326, 28, 74, 'اور قیامت کے دن خدا انہیں آواز دے گا کہ میرے وہ شریک کہاں ہیں جن کی شرکت کا تمہیں خیال تھا'),
(3327, 28, 75, 'اور ہم ہر قوم میں سے ایک گواہ نکال کر لائیں گے اور منکرین سے کہیں گے کہ تم بھی اپنی دلیل لے آؤ تب انہیں معلوم ہوگا کہ حق اللہ ہی کے لئے ہے اور پھر وہ جو افترا پردازیاں کیا کرتے تھے وہ سب گم ہوجائیں گی'),
(3328, 28, 76, 'بیشک قارون موسٰی کی قوم میں سے تھا مگر اس نے قوم پر ظلم کیا اور ہم نے بھی اسے اتنے خزانے دے دیئے تھے کہ ایک طاقت ور جماعت سے بھی اس کی کنجیاں نہیں اٹھ سکتی تھیں پھر جب اس سے قوم نے کہا کہ اس قدر نہ اتراؤ کہ خدا اترانے والوں کو دوست نہیں رکھتا ہے'),
(3329, 28, 77, 'اور جو کچھ خدا نے دیا ہے اس سے آخرت کے گھر کا انتظام کرو اور دنیا میں اپنا حصّہ بھول نہ جاؤ اور نیکی کرو جس طرح کہ خدا نے تمہارے ساتھ نیک برتاؤ کیا ہے اور زمین میں فساد کی کوشش نہ کرو کہ اللہ فساد کرنے والوں کو دوست نہیں رکھتا ہے'),
(3330, 28, 78, 'قارون نے کہا کہ مجھے یہ سب کچھ میرے علم کی بنا پر دیا گیا ہے تو کیا اسے یہ معلوم نہیں ہے کہ اللہ نے اس سے پہلے بہت سی نسلوں کو ہلاک کردیا ہے جو اس سے زیادہ طاقتور اور مال کے اعتبار سے دولت مند تھیں اور ایسے مجرموں سے تو ان کے گناہوں کے بارے میں سوال بھی نہیں کیا جاتا ہے'),
(3331, 28, 79, 'پھر قارون اپنی قوم کے سامنے زیب و زینت کے ساتھ برآمد ہوا تو جن لوگوں کے دل میں زندگانی دنیا کی خواہش تھی انہوںنے کہنا شروع کردیا کہ کاش ہمارے پاس بھی یہ ساز و سامان ہوتا جو قارون کو دیا گیا ہے یہ تو بڑے عظیم حصہ ّ کا مالک ہے'),
(3332, 28, 80, 'اور جنہیں علم دیا گیا تھا انہوں نے کہا کہ افسوس تمہارے حال پر - اللرُ کا ثواب صاحبان هایمان و عمل صالح کے لئے اس سے کہیں زیادہ بہتر ہے اور وہ ثواب صبر کرنے والوں کے علاوہ کسی کو نہیں دیا جاتا ہے'),
(3333, 28, 81, 'پھر ہم نے اسے اور اس کے گھر بار کو زمین میں دھنسا دیا اور نہ کوئی گروہ خدا کے علاوہ بچانے والا پیدا ہوا اور نہ وہ خود اپنا بچاؤ کرنے والا تھا'),
(3334, 28, 82, 'اور جن لوگوں نے کل اس کی جگہ کی تمناّ کی تھی وہ کہنے لگے کہ معاذ اللہ یہ تو خدا جس بندے کے لئے چاہتا ہے اس کے رزق میں وسعت پیدا کردیتا ہے اور جس کے یہاں چاہتا ہے تنگی پیدا کردیتا ہے اور اگر اس نے ہم پر احسان نہ کردیا ہوتا تو ہمیں بھی دھنسا دیا ہوتا معاذاللرُ کافروں کے لئے واقعا فلاح نہیں ہے'),
(3335, 28, 83, 'یہ دا» آخرت وہ ہے جسے ہم ان لوگوں کے لئے قرار دیتے ہیں جو زمین میں بلندی اور فساد کے طلبگار نہیں ہوتے ہیں اور عاقبت تو صرف صاحبانِ تقویٰ کے لئے ہے'),
(3336, 28, 84, 'جو کوئی نیکی کرے گا اسے اس سے بہتر اجر ملے گا اور جو کوئی برائی کرے گا تو برائی کرنے والوں کو اتنی ہی سزادی جائے گی جیسے اعمال وہ کرتے رہے ہیں'),
(3337, 28, 85, 'بیشک جس نے آپ پر قرآن کا فریضہ عائد کیا ہے وہ آپ کو آپ کی منزل تک ضرور واپس پہنچائے گا .آپ کہہ دیجئے کہ میرا پروردگار بہتر جانتا ہے کہ کون ہدایت لے کر آیا ہے اور کون کھلی ہوئی گمراہی میں ہے'),
(3338, 28, 86, 'اور آپ تو اس بات کے امیدوار نہیں تھے کہ آپ کی طرف کتاب نازل کی جائے یہ تو رحمت هپروردگار ہے لہذا خبردار آپ کافروں کا ساتھ نہ دیجئے گا'),
(3339, 28, 87, 'اور ہرگز یہ لوگ آیات کے نازل ہونے کے بعد ان کی تبلیغ سے آپ کو روکنے نہ پائیں اور آپ اپنے رب کی طرف دعوت دیجئے اور خبردار مشرکین میں سے نہ ہوجایئے'),
(3340, 28, 88, 'اور اللہ کے ساتھ کسی دوسرے خدا کو مت پکارو کہ اس کے علاوہ کوئی معبود نہیں ہے . اس کی ذات کے ماسوا ہر شے ہلاک ہونے والی ہے اور تم سب اسی کی بارگاہ کی طرف پلٹائے جاؤ گے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'المۤ ۤ'),
(3342, 29, 2, 'کیا لوگوں نے یہ خیال کر رکھا ہے کہ وہ صرف اس بات پر چھوڑ دیئے جائیں گے کہ وہ یہ کہہ دیں کہ ہم ایمان لے آئے ہیں اور ان کا امتحان نہیں ہوگا'),
(3343, 29, 3, 'بیشک ہم نے ان سے پہلے والوں کا بھی امتحان لیا ہے اور اللہ تو بہرحال یہ جاننا چاہتا ہے کہ ان میں کون لوگ سچے ہیں اور کون جھوٹے ہیں'),
(3344, 29, 4, 'کیا برائی کرنے والوں کا خیال یہ ہے کہ ہم سے آگے نکل جائیں گے یہ بہت غلط فیصلہ کررہے ہیں'),
(3345, 29, 5, 'جو بھی اللہ کی ملاقات کی امید رکھتا ہے اسے معلوم رہے کہ وہ مدّت یقینا آنے والی ہے اور وہ خدا سمیع بھی ہے اور علیم بھی ہے'),
(3346, 29, 6, 'اور جس نے بھی جہاد کیا ہے اس نے اپنے لئے جہاد کیا ہے اور اللہ تو سارے عالمین سے بے نیاز ہے'),
(3347, 29, 7, 'اور جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ہم یقینا ان کے گناہوں کی پردہ پوشی کریں گے اور انہیں ان کے اعمال کی بہترین جزا عطا کریں گے'),
(3348, 29, 8, 'اور ہم نے انسان کو ماں باپ کے ساتھ نیک برتاؤ کرنے کی نصیحت کی ہے اور بتایا ہے کہ اگر وہ کسی ایسی شے کو میرا شریک بنانے پر مجبور کریں جس کا تمہیں علم نہیں ہے تو خبردار ان کی اطاعت نہ کرنا کہ تم سب کی بازگشت میری ہی طرف ہے پھر میں بتاؤں گا کہ تم لوگ کیا کررہے تھے'),
(3349, 29, 9, 'اور جن لوگوں نے ایمان اختیار کیا اور نیک اعمال کئے ہم یقینا انہیں نیک بندوں میں شامل کرلیں گے'),
(3350, 29, 10, 'اور بعض لوگ ایسے ہیں جو کہتے ہیں کہ ہم اللہ پر ایمان لے آئے اس کے بعد جب راسِ خدا میں کوئی تکلیف ہوئی تو لوگوں کے فتنہ کو عذابِ خدا جیسا قرار دے دیا حالانکہ آپ کے پروردگار کی طرف سے کوئی مدد آجاتی تو فورا کہہ دیتے کہ ہم تو آپ ہی کے ساتھ تھے تو کیا خدا ان باتوں سے باخبر نہیں ہے جو عالمین کے دلوں میں ہیں'),
(3351, 29, 11, 'اور وہ یقینا انہیں بھی جاننا چاہتا ہے جو صاحبانِ ایمان ہیں اور انہیں بھی جاننا چاہتا ہے جو منافق ہیں'),
(3352, 29, 12, 'اور یہ کفّار ایمان لانے والوں سے کہتے ہیں کہ تم ہمارے راستہ کا اتباع کرلو ہم تمہارے گناہوں کے ذمہ دار ہیں حالانکہ وہ کسی کے گناہوں کا بوجھ اٹھانے والے نہیں ہیں اور سراسر جھوٹے ہیں'),
(3353, 29, 13, 'اور انہیں ایک دن اپنا بوجھ بھی اٹھانا ہی پڑے گا اور اس کے ساتھ ان لوگوں کا بھی اور روز ه قیامت ان سے ان باتوں کے بارے میں سوال بھی کیا جائے گا جن کا یہ افترا کیا کرتے تھے'),
(3354, 29, 14, 'اور ہم نے نوح علیھ السّلامکو ان کی قوم کی طرف بھیجا اور وہ ان کے درمیان پچاس سال کم ایک ہزار سال رہے پھر قوم کو طوفان نے اپنی گرفت میں لے لیا کہ وہ لوگ ظالم تھے'),
(3355, 29, 15, 'پھر ہم نے نوح علیھ السّلام کو اور ان کے ساتھ کشتی والوں کو نجات دے دی اور اسے عالمین کے لئے ایک نشانی قرار دے دیا'),
(3356, 29, 16, 'اور ابراہیم کو یاد کرو جب انہوں نے اپنی قوم سے کہا کہ اللہ کی عبادت کرو اور اس سے ڈرو کہ اسی میں تمہارے لئے بہتری ہے اگر تم کچھ جانتے ہو'),
(3357, 29, 17, 'تم خدا کو چھوڑ کر صرف بتوں کی پرستش کرتے ہو اور اپنے پاس سے جھوٹ گڑھتے ہو یاد رکھو کہ خدا کے علاوہ جن کی بھی پرستش کرتے ہو وہ تمہارے رزق کے مالک نہیں ہیں رزق خدا کے پاس تلاش کرو اور اسی کی عبادت کرو اسی کا شکریہ ادا کرو کہ تم سب اسی کی بارگاہ میں واپس لے جائے جاؤ گے'),
(3358, 29, 18, 'اور اگر تم تکذیب کرو گے تو تم سے پہلے بہت سی قومیں یہ کام کرچکی ہیں اور رسول کی ذمہ داری تو صرف واضح طور پر پیغام کا پہنچا دینا ہے'),
(3359, 29, 19, 'کیا ان لوگوں نے نہیں دیکھا کہ خدا کس طرح مخلوقات کو ایجاد کرتا ہے اور پھر دوبارہ واپس لے جاتا ہے یہ سب اللہ کے لئے بہت آسان ہے'),
(3360, 29, 20, 'آپ کہہ دیجئے کہ تم لوگ زمین میں سیر کرو اور دیکھو کہ خدا نے کس طرح خلقت کا آغاز کیا ہے اس کے بعد وہی آخرت میں دوبارہ ایجاد کرے گا بیشک وہی ہر شے پر قدرت رکھنے والا ہے'),
(3361, 29, 21, 'وہ جس پر چاہتا ہے عذاب کرتا ہے اور جس پر چاہتا ہے رحم کرتا ہے اور تم سب اسی کی طرف پلٹائے جاؤ گے'),
(3362, 29, 22, 'اور تم زمین یا آسمان میں اسے عاجز کرسکنے والے نہیں ہو اور اس کے علاوہ تمہارا کوئی سرپرست یا مددگار بھی نہیں ہے'),
(3363, 29, 23, 'اور جو لوگ خدا کی نشانیوں اور اس کی ملاقات کا انکار کرتے ہیں وہ میری رحمت سے مایوس ہیں اور ان کے لئے دردناک عذاب ہے'),
(3364, 29, 24, 'تو ان کی قوم کا جواب اس کے علاوہ کچھ نہ تھا کہ انہیں قتل کردو یا آگ میں جلا دو ...._ تو پروردگار نے انہیں اس آگ سے نجات دلادی بیشک اس میں بھی صاحبان هایمان کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(3365, 29, 25, 'اور ابراہیم نے کہا کہ تم نے صرف زندگانی دنیا کی محبتوں کو برقرار رکھنے کے لئے خدا کو چھوڑ کر بتوں کو اختیار کرلیا ہے اس کے بعد روز هقیامت تم میں سے ہر ایک دوسرے کا انکار کرے گا اور ایک دوسرے پر لعنت کرے گا جب کہ تم سب کا انجام جہّنم ہوگا اور تمہارا کوئی مددگار نہ ہوگا'),
(3366, 29, 26, 'پھر لوط ابراہیم پر ایمان لے آئے اور انہوں نے کہا کہ میں اپنے رب کی طرف ہجرت کررہا ہوں کہ وہی صاحب هعزّت اور صاحب حکمت ہے'),
(3367, 29, 27, 'اور ہم نے انہیں اسحاق اور یعقوب جیسی اولاد عطا کی اور پھر ان کی ذریت میں کتاب اور نبوت قرار دی اور انہیں دنیا میں بھی ان کا اجر عطا کیا اور آخرت میں بھی ان کا شمار نیک کردار لوگوں میں ہوگا'),
(3368, 29, 28, 'اور پھر لوط کو یاد کرو جب انہوں نے اپنی قوم سے کہا کہ تم لوگ ایسی بدکاری کررہے ہو جو سارے عالم میں تم سے پہلے کبھی کسی نے بھی نہیں کی ہے'),
(3369, 29, 29, 'تم لوگ مذِدوں سے جنسی تعلّقات پیدا کررہے ہو اور راستے قطع کررہے ہو اور اپنی محفلوں میں برائیوں کو انجام دے رہے ہو .... تو ان کی قوم کے پاس اس کے علاوہ کوئی جواب نہ تھا کہ اگر تم صادقین میں سے ہو تو وہ عذاب هالٰہی لے آؤ جس کی خبر دے رہے ہو'),
(3370, 29, 30, 'تو لوط نے کہا پروردگار تو اس فساد پھیلانے والی قوم کے مقابلہ میں میری مدد فرما'),
(3371, 29, 31, 'اور جب ہمارے نمائندہ فرشتے ابراہیم کے پاس بشارت لے کر آئے اور انہوں نے یہ خبر سنائی کہ ہم اس بستی والوں کو ہلاک کرنا چاہتے ہیں کہ اس بستی کے لوگ بڑے ظالم ہیں'),
(3372, 29, 32, 'تو ابراہیم نے کہا کہ اس میں تو لوط بھی ہیں - انہوں نے جواب دیا کہ ہم سب سے باخبر ہیں - ہم انہیں اور ان کے گھر والوں کو نجات دے دیں گے علاوہ ان کی زوجہ کے کہ وہ پیچھے رہ جانے والوں میں ہے'),
(3373, 29, 33, 'پھر جب ہمارے فرشتے لوط کے پاس آئے تو وہ پریشان ہوگئے اور ان کی مہمانی کی طرف سے دل تنگ ہونے لگے ان لوگوں نے کہا کہ آپ ڈریں نہیں اور پریشان نہ ہوں ہم آپ کو اور آپ کے گھر والوں کو بچالیں گے صرف آپ کی زوجہ کو نہیں کہ وہ پیچھے رہ جانے والوں میں ہوگئی ہے'),
(3374, 29, 34, 'ہم اس بستی پر آسمان سے عذاب نازل کرنے والے ہیں کہ یہ لوگ بڑی بدکاری کررہے ہیں'),
(3375, 29, 35, 'اور ہم نے اس بستی میں سے صاحبانِ عقل و ہوش کے لئے کِھلی ہوئی نشانی باقی رکھی ہے'),
(3376, 29, 36, 'اور ہم نے مدین کی طرف ان کے بھائی شعیب کو بھیجا تو انہوں نے کہا کہ لوگو اللہ کی عبادت کرو اور روزِ آخرت سے امیدیں وابستہ کرو اور خبردار زمین میں فساد نہ پھیلاتے پھرو'),
(3377, 29, 37, 'تو ان لوگوں نے انہیں جھٹلادیا اور نتیجہ میں انہیں ایک زلزلے نے اپنی گرفت میں لے لیا اور وہ اپنے گھروں میں اوندھے زانو کے بل پڑے رہے'),
(3378, 29, 38, 'اور عاد و ثمود کو یاد کرو کہ تم لوگوں کے لئے ان کے گھر بھی س»راہ واضح ہوچکے ہیں اور شیطان نے ان کے لئے ان کے اعمال کو آراستہ کردیا تھا اور انہیں راستہ سے روک دیا تھا حالانکہ وہ لوگ بہت ہوشیار تھے'),
(3379, 29, 39, 'اور قارون فرعون و ہامان کو بھی یاد دلاؤ جن کے پاس موسٰی کھنَی جوئی نشانیاں لے کر آئے تو ان لوگوں نے زمین میں استکبار سے کام لیا حالانکہ وہ ہم سے آگے جانے والے نہیں تھے'),
(3380, 29, 40, 'پھر ہم نے ہر ایک کو اس کے گناہ میں گرفتار کرلیا کسی پر آسمان سے پتھروں کی بارش کردی کسی کو ایک آسمانی چیخ نے پکڑ لیا اور کسی کو زمین میں دھنسا دیا اور کسی کو پانی میں غرق کردیا اور اللہ کسی پر ظلم کرنے والا نہیں ہے بلکہ یہ لوگ خود اپنے نفس پر ظلم کررہے ہیں'),
(3381, 29, 41, 'اور جن لوگوں نے خدا کو چھوڑ کر دوسرے سرپرست بنالئے ہیں ان کی مثال مکڑی جیسی ہے کہ اس نے گھر تو بنالیا لیکن سب سے کمزور گھر مکڑی کا گھر ہوتا ہے اگر ان لوگوں کے پاس علم و ادراک ہو'),
(3382, 29, 42, 'بیشک اللہ خوب جانتا ہے کہ اسے چھوڑ کر یہ لوگ کسے پکار رہے ہیں اور وہ بڑی عزّت والا اور صاحبِ حکمت ہے'),
(3383, 29, 43, 'اور یہ مثالیں ہم تمام عالم هانسانیت کے لئے بیان کر رہے ہیں لیکن انہیں صاحبانِ علم کے علاوہ کوئی نہیں سمجھ سکتا ہے'),
(3384, 29, 44, 'اللرُ نے آسمان اور زمین کو حق کے ساتھ پیدا کیا ہے اور اس میں صاحبان هایمان کے لئے اس کی قدرت کی نشانی پائی جاتی ہے'),
(3385, 29, 45, 'آپ جس کتاب کی آپ کی طرف وحی کی گئی ہے اسے پڑھ کر سنائیں اور نماز قائم کریں کہ نماز ہر برائی اور بدکاری سے روکنے والی ہے اور اللہ کا ذکر بہت بڑی شے ہے اور اللہ تمہارے کاروبار سے خوب باخبر ہے'),
(3386, 29, 46, 'اور اہلِ کتاب سے مناطرہ نہ کرو مگر اس انداز سے جو بہترین انداز ہے علاوہ ان سے جو ان میں سے ظالم ہیں اور یہ کہو کہ ہم اس پر ایمان لائے ہیں جو ہماری اور تمہاری دونوں کی طرف نازل ہوا ہے اور ہمارا اور تمہارا خدا ایک ہی ہے اور ہم سب اسی کے اطاعت گزار ہیں'),
(3387, 29, 47, 'اور اسی طرح ہم نے آپ کی طرف کتاب نازل کی تو جن لوگوں کو ہم نے کتاب دی ہے وہ اس کتاب پر ایمان رکھتے ہیں اور ِن میں سے بھی بعض لوگ ایمان رکھتے ہیں اور ہماری آیات کا انکار کافروں کے علاوہ کوئی نہیں کرتا ہے'),
(3388, 29, 48, 'اور اے پیغمبر آپ اس قرآن سے پہلے نہ کوئی کتاب پڑھتے تھے اور نہ اپنے ہاتھ سے کچھ لکھتے تھے ورنہ یہ اہل باطل شبہ میں پڑ جاتے'),
(3389, 29, 49, 'درحقیقت یہ قرآن چند کھلی ہوئی آیتوں کا نام ہے جو ان کے سینوں میں محفوظ ہیں جنہیں علم دیا گیا ہے اور ہماری آیات کا انکار ظالموں کے علاوہ کوئی نہیں کرتا ہے'),
(3390, 29, 50, 'اور یہ لوگ کہتے ہیں کہ آخر ان پر پروردگار کی طرف سے آیات کیوں نہیں نازل ہوتی ہیں تو آپ کہہ دیجئے کہ آیات سب اللہ کے پاس ہیں اور میں تو صرف واضح طور پر عذاب هالہٰی سے ڈرانے والا ہوں'),
(3391, 29, 51, 'کیا ان کے لئے یہ کافی نہیں ہے کہ ہم نے آپ پر کتاب نازل کی ہے جس کی ان کے سامنے تلاوت کی جاتی ہے اور یقینا اس میںرحمت اور ایماندار قوم کے لئے یاددہانی کا سامان موجود ہے'),
(3392, 29, 52, 'آپ کہہ دیجئے کہ ہمارے اور تمہارے درمیان گواہی کے لئے خدا کافی ہے جو آسمان اور زمین کی ہر چیز سے باخبر ہے اور جو لوگ باطل پر ایمان رکھتے ہیں اور اللہ کا انکار کرتے ہیں وہ یقینا خسارہ اٹھانے والے لوگ ہیں'),
(3393, 29, 53, 'اور یہ لوگ عذاب کی جلدی کررہے ہیں حالانکہ اگر اس کا وقت معین نہ ہوتا تو اب تک عذاب آچکا ہوتا اور وہ اچانک ہی آئے گا جب انہیں شعور بھی نہ ہوگا'),
(3394, 29, 54, 'یہ عذاب کی جلدی کررہے ہیں حالانکہ جہّنم یقینا کافروں کو اپنے گھیرے میں لینے والا ہے'),
(3395, 29, 55, 'جس دن عذاب انہیں اوپر سے اور نیچے سے ڈھانک لے گا اور کہے گا کہ اب اپنے اعمال کا مزہ چکھو'),
(3396, 29, 56, 'میرے ایماندار بندو! میری زمین بہت وسیع ہے لہٰذامیری ہی عبادت کرو'),
(3397, 29, 57, 'ہر نفس موت کا مزہ چکھنے والاہے اس کے بعد تم سب ہماری بارگاہ میں پلٹا کر لائے جاؤ گے'),
(3398, 29, 58, 'اور جو لوگ ایمان لائے ہیں اور انہوں نے نیک اعمال کئے ہیں انہیں ہم جنّت کے جھروکوں میں جگہ دیں گے جن کے نیچے نہریں جاری ہوں گی اور وہ ہمیشہ ان ہی میں رہیں گے کہ یہ ان عمل کرنے والوں کا بہترین اجر ہے'),
(3399, 29, 59, 'جن لوگوں نے صبر کیا ہے اور اپنے پروردگار پر بھروسہ کرتے ہیں'),
(3400, 29, 60, 'اور زمین پر چلنے والے بہت سے ایسے بھی ہیں جو اپنی روزی کا بوجھ نہیں اٹھاسکتے ہیں لیکن خدا انہیں اور تمہیں سب کو رزق دے رہا ہے وہ سب کی سننے والا اور سب کے حالات کا جاننے والا ہے'),
(3401, 29, 61, 'اور اگر آپ ان سے پوچھیں گے کہ آسمان و زمین کو کس نے پیدا کیا ہے اور آفتاب و ماہتاب کو کس نے لَسخّر کیا ہے تو فورا کہیں گے کہ اللہ,تو یہ کدھر بہکے چلے جارہے ہیں'),
(3402, 29, 62, 'اللرُ ہی جس کے رزق میں چاہتا ہے وسعت پیدا کردیتا ہے اور جس کے رزق کو چاہتا ہے تنگ کردیتا ہے وہ ہر شے کا خوب جاننے والا ہے'),
(3403, 29, 63, 'اور اگر آپ ان سے پوچھیں گے کس نے آسمان سے پانی برسایا ہے اور پھر زمین کو مفِدہ ہونے کے بعد زندہ کیا ہے تو یہ کہیں گے کہ اللہ ہی ہے تو پھر کہہ دیجئے کہ ساری حمد اللہ کے لئے ہے اور ان کی اکثریت عقل استعمال نہیں کررہی ہے'),
(3404, 29, 64, 'اور یہ زندگانی دنیا ایک کھیل تماشے کے سوا کچھ نہیں ہے اور آخرت کا گھر ہمیشہ کی زندگی کا مرکز ہے اگر یہ لوگ کچھ جانتے اور سمجھتے ہوں'),
(3405, 29, 65, 'پھر جب یہ لوگ کشتی میں سوار ہوتے ہیں تو ایمان و عقیدہ کے پورے اخلاص کے ساتھ خدا کو پکارتے ہیں پھر جب وہ نجات دے کر خشکی تک پہنچادیتا ہے تو فورا شرک اختیار کرلیتے ہیں'),
(3406, 29, 66, 'تاکہ جو کچھ ہم نے عطا کیا ہے اس کا انکار کردیں اور دنیا میں مزے اُڑائیں تو عنقریب انہیں اس کا انجام معلوم ہوجائے گا'),
(3407, 29, 67, 'کیا ان لوگوں نے نہیں دیکھا کہ ہم نے ان کے واسطے ایک محفوظ حرم بنادیا ہے جس کے چاروں طرف لوگ اچک لئے جاتے ہیں تو کیا یہ باطل پر ایمان رکھتے ہیں اور اللہ کی نعمت کا انکار کردیتے ہیں'),
(3408, 29, 68, 'اور اُس سے بڑا ظالم کون ہے جو اللہ کی طرف جھوٹی باتوں کی نسبت دے یا حق کے آجانے کے بعد بھی اس کا انکار کردے تو کیا جہّنم میں کفار کا ٹھکانا نہیں ہے'),
(3409, 29, 69, 'اور جن لوگوں نے ہمارے حق میں جہاد کیا ہے ہم انہیں اپنے راستوں کی ہدایت کریں گے اور یقینا اللرُحسن عمل والوں کے ساتھ ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الۤمۤ'),
(3411, 30, 2, 'روم والے مغلوب ہوگئے'),
(3412, 30, 3, 'قریب ترین علاقہ میں لیکن یہ مغلوب ہوجانے کے بعد عنقریب پھر غالب ہوجائیں گے'),
(3413, 30, 4, 'چند سال کے اندر ,اللہ ہی کے لئے اوّل و آخر ہر زمانہ کا اختیار ہے اور اسی دن صاحبانِ ایمان خوشی منائیں گے'),
(3414, 30, 5, 'اللرُکی نصرت و امداد کے سہارے کہ وہ جس کی امداد چاہتا ہے کردیتا ہے اور وہ صاحب هعزّت بھی ہے اور مہربان بھی ہے'),
(3415, 30, 6, 'یہ اللہ کا وعدہ ہے اور اللہ اپنے وعدہ کے خلاف نہیں کرتا ہے مگر لوگوں کی اکثریت اس حقیقت سے بھی بے خبر ہے'),
(3416, 30, 7, 'یہ لوگ صرف زندگانی دنیا کے ظاہر کو جانتے ہیں اور آخرت کی طرف سے بالکل غافل ہیں'),
(3417, 30, 8, 'کیا ان لوگوں نے اپنے اندر فکر نہیں کی ہے کہ خدا نے آسمان و زمین اور اس کے درمیان کی تمام مخلوقات کو برحق ہی پیدا کیا ہے اور ایک معین مدّت کے ساتھ لیکن لوگوں کی اکثریت اپنے پروردگار کی ملاقات سے انکار کرنے والی ہے'),
(3418, 30, 9, 'اور کیا ان لوگوں نے زمین میں سیر نہیں کی ہے کہ دیکھتے کہ ان سے پہلے والوں کا کیا انجام ہوا ہے جو طاقت میں ان سے زیادہ مضبوط تھے اور انہوں نے زراعت کرکے زمین کو ان سے زیادہ آباد کرلیا تھا اور ان کے پاس ہمارے نمائندے زیادہ کِھلی ہوئی نشانیاں بھی لے کر آئے تھے یقینا خدا اپنے بندوں پر ظلم نہیں کرتا ہے بلکہ یہ لوگ خود ہی اپنے اوپر ظلم کرتے ہیں'),
(3419, 30, 10, 'اس کے بعد برائی کرنے والوں کا انجام برا ہوا کہ انہوں نے خدا کی نشانیوں کو جھٹلادیا اور برابر ان کا مذاق اڑاتے رہے'),
(3420, 30, 11, 'اللرُ ہی تخلیق کی ابتدائ کرتا ہے اور پھر پلٹا بھی دیتا ہے اور پھر تم سب اسی کی بارگاہ میں واپس لے جائے جاؤ گے'),
(3421, 30, 12, 'اور جس دن قیامت قائم کی جائے گی اس دن سارے مجرمین مایوس ہوجائیں گے'),
(3422, 30, 13, 'اور ان کے شرکائ میں کوئی سفارش کرنے والا نہ ہوگا اور یہ خود بھی اپنے شرکائ کا انکار کرنے والے ہوں گے'),
(3423, 30, 14, 'اور جس دن قیامت قائم ہوگی سب لوگ آپس میں گروہوں میں تقسیم ہوجائیں گے'),
(3424, 30, 15, 'پس جو ایمان والے اور نیک عمل والے ہوں گے وہ باگُ جنّت میں نہال اور خوشحال ہوں گے'),
(3425, 30, 16, 'اور جن لوگوں نے کفر اختیار کیا اور ہماری آیات اور روز آخرت کی ملاقات کی تکذیب کی وہ عذاب میں ضرور گرفتار کئے جائیں گے'),
(3426, 30, 17, 'لہذا تم لوگ تسبیح پروردگار کرو اس وقت جب شام کرتے ہو اور جب صبح کرتے ہو'),
(3427, 30, 18, 'اور زمین و آسمان میں ساری حمد اسی کے لئے ہے اور عصر کے ہنگام اور جب دوپہر کرتے ہو'),
(3428, 30, 19, 'وہ خدا زندہ کو مفِدہ سے اور مفِدہ کو زندہ سے نکالتا ہے اور زمین کو مردہ ہوجانے کے بعد پھر زندہ کرتا ہے اور اسی طرح ایک دن تمہیں بھی نکالا جائے گا'),
(3429, 30, 20, 'اور اس کی نشانیوں میں سے ایک یہ بھی ہے کہ اس نے تمہیں خاک سے پیدا کیا ہے اور اس کے بعد تم بشر کی شکل میں پھیل گئے ہو'),
(3430, 30, 21, 'اور اس کی نشانیوں میں سے یہ بھی ہے کہ اس نے تمہارا جوڑا تم ہی میں سے پیدا کیاہے تاکہ تمہیں اس سے سکون حاصل ہو اورپھر تمہارے درمیان محبت اور رحمت قرار دی ہے کہ اس میں صاحبانِ فکر کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(3431, 30, 22, 'اور اس کی نشانیوں میں سے آسمان و زمین کی خلقت اور تمہاری زبانوں اور تمہارے رنگوں کا اختلاف بھی ہے کہ اس میں صاحبانِ علم کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(3432, 30, 23, 'اور اس کی نشانیوں میں سے یہ بھی ہے کہ تم رات اور دن کو آرام کرتے ہو اور پھر فضل خدا کو تلاش کرتے ہو کہ اس میں بھی سننے والی قوم کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(3433, 30, 24, 'اور اس کی نشانیوں میں سے یہ بھی ہے کہ وہ بجلی کو خوف اور امید کا مرکز بنا کر دکھلاتا ہے اور آسمان سے پانی برساتا ہے پھر اس کے ذریعہ مردہ زمین کو زندہ بناتا ہے بیشک اس میں بھی اس قوم کے لئے بہت سی نشانیاں ہیں جو عقل رکھنے والی ہے'),
(3434, 30, 25, 'اور اس کی نشانیوں میں سے یہ بھی ہے کہ آسمان و زمین اسی کے حکم سے قائم ہیں اور اس کے بعد وہ جب تم سب کو طلب کرے گا تو سب زمین سے یکبارگی برآمد ہوجائیں گے'),
(3435, 30, 26, 'آسمان و زمین میں جو کچھ بھی ہے سب اسی کی ملکیت ہے اور سب اسی کے تابع فرمان ہیں'),
(3436, 30, 27, 'اور وہی وہ ہے جو خلقت کی ابتدا ئ کرتا ہے اور پھر دوبارہ بھی پیدا کرے گا اور یہ کام اس کے لئے بے حد آسان ہے اور اسی کے لئے آسمان اور زمین میں سب سے بہترین مثال ہے اور وہی سب پر غالب آنے والا اور صاحب هحکمت ہے'),
(3437, 30, 28, 'اس نے تمہارے لئے تمہاری ہی مثال بیان کی ہے کہ جو رزق ہم نے تم کو عطا کیا ہے کیا اس میں تمہارے مملوک غلام و کنیز میں کوئی تمہارا شریک ہے کہ تم سب برابر ہوجاؤ اور تمہیں ان کا خوف اسی طرح ہو جس طرح اپنے نفوس کے بارے میں خوف ہوتا ہے ...._ بیشک ہم اپنی نشانیوں کو صاحب هعقل قوم کے لئے اسی طرح واضح کرکے بیان کرتے ہیں'),
(3438, 30, 29, 'حقیقت یہ ہے کہ ظالموں نے بغیر جانے بوجھے اپنی خواہشات کا اتباع کرلیا ہے تو جس کو خدا گمراہی میں چھوڑ دے اسے کون ہدایت دے سکتا ہے اور ان کا تو کوئی ناصر و مددگار بھی نہیں ہے'),
(3439, 30, 30, 'آپ اپنے رخ کو دین کی طرف رکھیں اور باطل سے کنارہ کش رہیں کہ یہ دین وہ فطرت الہیۤ ہے جس پر اس نے انسانوں کو پیدا کیا ہے اور خلقت الہٰی میں کوئی تبدیلی نہیں ہوسکتی ہے یقینا یہی سیدھا اور مستحکم دین ہے مگر لوگوں کی اکثریت اس بات سے بالکل بے خبر ہے'),
(3440, 30, 31, 'تم سب اپنی توجہ خدا کی طرف رکھو اور اسی سے ڈرتے رہو - نماز قائم کرو اور خبردار مشرکین میں سے نہ ہوجانا'),
(3441, 30, 32, 'ان لوگوں میں سے جنہوں نے دین میں تفرقہ پیدا کیا ہے اور گروہوں میں بٹ گئے ہیں پھر ہر گروہ جو کچھ اس کے پاس ہے اسی پر مست اور مگن ہے'),
(3442, 30, 33, 'اور لوگوں کو جب بھی کوئی مصیبت چھوجاتی ہے تو وہ پروردگار کو پوری توجہ کے ساتھ پکارتے ہیں اس کے بعد جب وہ رحمت کا مزہ چکھا دیتا ہے تو ان میں سے ایک گروہ شرک کرنے لگتا ہے'),
(3443, 30, 34, 'تاکہ جو کچھ ہم نے عطا کیا ہے اس کا انکار کردے تو تم مزے کرو اس کے بعد تو سب کو معلوم ہی ہوجائے گا'),
(3444, 30, 35, 'کیا ہم نے ان کے اوپر کوئی دلیل نازل کی ہے جو ان سے ان کے شرک کے جواز کو بیان کرتی ہے'),
(3445, 30, 36, 'اور جب ہم انسانوں کو رحمت کا مزہ چکھادیتے ہیں تو وہ خوش ہوجاتے ہیں اور جب انہیں ان کے سابقہ کردار کی بنا پر کوئی تکلیف پہنچ جاتی ہے تو وہ مایوسی کا شکار ہوجاتے ہیں'),
(3446, 30, 37, 'تو کیا ان لوگوں نے نہیں دیکھا کہ خدا جس کے رزق میں چاہتا ہے وسعت پیدا کردیتا ہے اور جس کے یہاں چاہتا ہے کمی کردیتا ہے اور اس میں بھی صاحبِ ایمان قوم کے لئے اس کی نشانیاں ہیں'),
(3447, 30, 38, 'اور تم قرابتدار مسکین اور غربت زدہ مسافر کو اس کا حق دے دو کہ یہ ان لوگوں کے حق میں خیر ہے جو رضائے الہٰی کے طلب گار ہیں اور وہی حقیقتا نجات حاصل کرنے والے ہیں'),
(3448, 30, 39, 'اور تم لوگ جوبھی سود دیتے ہو کہ لوگوں کے مال میں اضافہ ہوجائے تو خدا کے یہاں کوئی اضافہ نہیں ہوتا ہے ہاں جو زکوِٰ دیتے ہو اور اس میں رضائے خدا کا ارادہ ہوتا ہے تو ایسے لوگوں کو دگنا چوگنا دے دیا جاتا ہے'),
(3449, 30, 40, 'اللرُ ہی وہ ہے جس نے تم سب کو خلق کیا ہے پھر روزی دی ہے پھر موت دیتا ہے پھر زندہ کرتا ہے کیا تمہارے شرکائ میں کوئی ایسا ہے جو ان میں سے کوئی کام انجام دے سکے خدا ان تمام چیزوں سے جنہیں یہ سب شریک بنارہے ہیں پاک و پاکیزہ اور بلند و برتر ہے'),
(3450, 30, 41, 'لوگوں کے ہاتھوں کی کمائی کی بنا پر فساد خشکی اور تری ہر جگہ غالب آگیا ہے تاکہ خدا ان کے کچھ اعمال کا مزہ چکھا دے تو شاید یہ لوگ پلٹ کر راستے پر آجائیں'),
(3451, 30, 42, 'آپ کہہ دیجئے کہ ذرا زمین میں سیر کرکے دیکھو کہ تم سے پہلے والوں کا کیا انجام ہوا ہے جن کی اکثریت مشرک تھی'),
(3452, 30, 43, 'اور آپ اپنے رخ کو مستقیم اور مستحکم دین کی طرف رکھیں قبل اس کے کہ وہ دن آجائے جس کی واپسی کا کوئی امکان نہیں ہے اور جس دن لوگ پریشان ہوکر الگ الگ ہوجائیں گے'),
(3453, 30, 44, 'جو کفر کرے گا وہ اپنے کفر کا ذمہ دار ہوگا اور جو لوگ نیک عمل کررہے ہیں وہ اپنے لئے راہ ہموار کررہے ہیں'),
(3454, 30, 45, 'تاکہ خدا ایمان اور نیک عمل کرنے والوں کو اپنے فضل سے جزا دے سکے کہ وہ کافروں کو دوست نہیں رکھتا ہے'),
(3455, 30, 46, 'اور اس کی نشانیوں میں سے ایک یہ بھی ہے کہ وہ ہواؤں کو خوش خبری دینے والا بناکر بھیجتا ہے اور اس لئے بھی کہ تمہیں اپنی رحمت کا مزہ چکھائے اور اس کے حکم سے کشتیاں چلیں اور تم اپنا رزق حاصل کرسکو اور شاید اس طرح شکر گزار بھی بن جاؤ'),
(3456, 30, 47, 'اور ہم نے تم سے پہلے بہت سے رسول ان کی قوموں کی طرف بھیجے ہیں جو ان کے پاس کھلی ہوئی نشانیاں لے کر آئے پھر ہم نے مجرمین سے انتقام لیا اور ہمارا فرض تھا کہ ہم صاحبانِ ایمان کی مدد کریں'),
(3457, 30, 48, 'اللرُ ہی وہ ہے جو ہواؤں کو چلاتا ہے تو وہ بادلوں کو اڑاتی ہیں پھر وہ ان بادلوں کو جس طرح چاہتا ہے آسمان میں پھیلادیتا ہے اور اسے ٹکڑے کردیتا ہے اور اس کے درمیان سے پانی برساتا ہے پھر یہ پانی ان بندوں تک پہنچ جاتا ہے جن تک وہ پہنچانا چاہتا ہے تو وہ خوش ہوجاتے ہیں'),
(3458, 30, 49, 'اگرچہ وہ اس بارش کے نازل ہونے سے پہلے مایوسی کا شکار ہوگئے تھے'),
(3459, 30, 50, 'اب تم رحماُ خدا کے ان آثار کو دیکھو کہ وہ کس طرح زمین کو مردہ ہوجانے کے بعد زندہ کردیتا ہے بیشک وہی مفِدوں کو زندہ کرنے والا ہے اور وہی ہر شے پر قدرت رکھنے والا ہے'),
(3460, 30, 51, 'اور اگر ہم زہریلی ہوا چلا دیتے اور یہ ہر طرف موسم خزاں جیسی زردی دیکھ لیتے تو بالکل ہی کفر اختیار کرلیتے'),
(3461, 30, 52, 'تو آپ مفِدوں کو اپنی آواز نہیں سناسکتے ہیں اور بہروں کو بھی نہیں سناسکتے ہیں جب وہ منہ پھیر کر چل دیں'),
(3462, 30, 53, 'اور آپ اندھوں کو بھی ان کی گمراہی سے ہدایت نہیں کرسکتے ہیں آپ تو صرف ان لوگوں کو سناسکتے ہیں جو ہماری آیتوں پر ایمان رکھتے ہیں اور مسلمان ہیں'),
(3463, 30, 54, 'اللرُ ہی وہ ہے جس نے تم سب کو کمزوری سے پیدا کیا ہے اور پھر کمزوری کے بعد طاقت عطا کی ہے اور پھر طاقت کے بعد کمزوری اور ضعیفی قرار دی ہے وہ جو چاہتا ہے پیدا کردیتا ہے کہ وہ صاحب هعلم بھی ہے اور صاحب قدرت بھی ہے'),
(3464, 30, 55, 'اور جس دن قیامت قائم ہوگی اس دن مجرمین قسم کھاکر کہیں گے کہ وہ ایک ساعت سے زیادہ دنیا میں نہیںٹہرے ہیں درحقیقت یہ اسی طرح دنیا میں بھی افترا پردازیاں کیا کرتے تھے'),
(3465, 30, 56, 'اور جن لوگوں کو علم اور ایمان دیا گیا ہے وہ کہیں گے کہ تم لوگ کتاب خدا کے مطابق قیامت کے دن تک ٹہرے رہے تو یہ قیامت کا دن ہے لیکن تم لوگ بے خبر بنے ہوئے ہو'),
(3466, 30, 57, 'پھر آج ظالموں کو نہ کوئی معذرت فائدہ پہنچائے گی اور نہ ان کی کوئی بات سنی جائے گی'),
(3467, 30, 58, 'اور ہم نے اس قرآن میں ہر طرح کی مثال بیان کردی ہے اور اگر آپ ساری نشانیاں لے کر بھی آجائیں تو کافر یہی کہیں گے کہ آپ لوگ صرف اہل ه باطل ہیں'),
(3468, 30, 59, 'بیشک اسی طرح خدا ان لوگوں کے دلوں پر مہفِ لگادیتا ہے جو علم رکھنے والے نہیں ہیں'),
(3469, 30, 60, 'لہٰذاآپ صبر سے کام لیں کہ خدا کا وعدہ برحق ہے اور خبردار جو لوگ یقین اس امر کا نہیں رکھتے ہیں وہ آپ کو ہلکانہ بنادیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الۤمۤ'),
(3471, 31, 2, 'یہ حکمت سے بھری ہوئی کتاب کی آیتیں ہیں'),
(3472, 31, 3, 'جو ان نیک کردار لوگوں کے لئے ہدایت اور رحمت ہے'),
(3473, 31, 4, 'جو نماز قائم کرتے ہیں اور زکوِٰادا کرتے ہیں اور آخرت پر یقین رکھتے ہیں'),
(3474, 31, 5, 'یہی لوگ اپنے پروردگار کی طرف سے ہدایت پر ہیں اور یہی فلاح پانے والے ہیں'),
(3475, 31, 6, 'لوگوں میں ایسا شخص بھی ہے جو مہمل باتوں کو خریدتا ہے تاکہ ان کے ذریعہ بغیر سمجھے بوجھے لوگوں کو راسِ خدا سے گمراہ کرے اور آیااُ الہٰیہ کا مذاق اڑائے درحقیقت ایسے ہی لوگوں کے لئے دردناک عذاب ہے'),
(3476, 31, 7, 'اور جب اس کے سامنے آیات الہٰیہ کی تلاوت کی جاتی ہے تو اکڑ کر منہ پھیرلیتا ہے جیسے اس نے کچھ سنا ہی نہیں ہے اور جیسے اس کے کان میں بہرا پن ہے .ایسے شخص کو درناک عذاب کی بشارت دے دیجئے'),
(3477, 31, 8, 'بیشک جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ان کے لئے نعمتوں سے بھری ہوئی جنّت ہے'),
(3478, 31, 9, 'وہ اسی میں ہمیشہ رہنے والے ہیں کہ یہ خدا کا برحق وعدہ ہے اور وہ صاحبِ عزّت بھی ہے اور صاحبِ حکمت بھی ہے'),
(3479, 31, 10, 'اسی نے آسمانوں کو بغیر ستون کے پیدا کیا ہے تم دیکھ رہے ہو اور زمین میں بڑے بڑے پہاڑ ڈال دیئے ہیں کہ تم کو لے کر جگہ سے ہٹنے نہ پائے اور ہر طرح کے جانور پھیلادیئے ہیں اور ہم نے آسمان سے پانی برسایا ہے اور اس کے ذریعہ زمین میں ہر قسم کا نفیس جوڑا پیدا کردیا ہے'),
(3480, 31, 11, 'یہ خدا کی تخلیق ہے تو کافرو اب تم دکھلاؤ کہ اس کے علاوہ تمہارے خداؤں نے کیا پیدا کیا ہے اور حقیقت یہ ہے کہ یہ ظالمین کھلی ہوئی گمراہی میں مبتلا ہیں'),
(3481, 31, 12, 'اور ہم نے لقمان کو حکمت عطا کی کہ پروردگار کا شکریہ ادا کرو اور جو بھی شکریہ ادا کرتا ہے وہ اپنے ہی فائدہ کے لئے کرتا ہے اور جو کفرانِ نعمت کرتا ہے اسے معلوم رہے کہ خدا بے نیاز بھی ہے اور قابل حمدوثنا بھی ہے'),
(3482, 31, 13, 'اور اس وقت کو یاد کرو جب لقمان نے اپنے فرزند کو نصیحت کرتے ہوئے کہا کہ بیٹا خبردار کسی کو خدا کا شریک نہ بنانا کہ شرک بہت بڑا ظلم ہے'),
(3483, 31, 14, 'اور ہم نے انسان کو ماں باپ کے بارے میں نصیحت کی ہے کہ اس کی ماں نے دکھ پر دکھ سہہ کر اسے پیٹ میں رکھا ہے اور اس کی دودھ بڑھائی بھی دوسال میں ہوئی ہے ...._ کہ میرا اور اپنے ماں باپ کا شکریہ ادا کرو کہ تم سب کی بازگشت میری ہی طرف ہے'),
(3484, 31, 15, 'اور اگر تمہارے ماں باپ اس بات پر زور دیں کہ کسی ایسی چیز کو میرا شریک بناؤ جس کا تمہیں علم نہیں ہے تو خبردار ان کی اطاعت نہ کرنا لیکن دنیا میں ان کے ساتھ نیکی کا برتاؤ کرنا اور اس کے راستے کو اختیار کرنا جو میری طرف متوجہ ہو پھر اس کے بعد تم سب کی بازگشت میری ہی طرف ہے اور اس وقت میں بتاؤں گا کہ تم لوگ کیا کررہے تھے'),
(3485, 31, 16, 'بیٹا نیکی یا بدی ایک رائی کے دانہ کے برابر ہو اور کسی پتھر کے اندر ہو یا آسمانوں پر ہو یا زمینوں کی گہرائیوں میں ہو تو خدا روزِ قیامت ضرور اسے سامنے لائے گا کہ وہ لطیف بھی ہے اور باخبر بھی ہے'),
(3486, 31, 17, 'بیٹا نماز قائم کرو, نیکیوں کا حکم دو, برائیوں سے منع کرو, اور اس راہ میں جو مصیبت پڑے اس پر صبر کرو کہ یہ بہت بڑی ہمت کا کام ہے'),
(3487, 31, 18, 'اور خبردار لوگوں کے سامنے اکڑ کر منہ نہ پُھلا لینا اور زمین میں غرور کے ساتھ نہ چلنا کہ خدا اکڑنے والے اور مغرور کو پسند نہیں کرتا ہے'),
(3488, 31, 19, 'اور اپنی رفتار میں میانہ روی سے کام لینا اور اپنی آواز کو دھیما رکھنا کہ سب سے بدتر آواز گدھے کی آواز ہوتی ہے (جو بلاسبب بھونڈے انداز سے چیختا رہتا ہے'),
(3489, 31, 20, 'کیا تم لوگوں نے نہیں دیکھا کہ اللہ نے زمین و آسمان کی تمام چیزوں کو تمہارے لئے لَسخّر کردیا ہے اور تمہارے لئے تمام ظاہری اور باطنی نعمتوں کو مکمل کردیا ہے اور لوگوں میں بعض ایسے بھی ہیں جو علم و ہدایت اور روشن کتاب کے بغیر بھی خدا کے بارے میں بحث کرتے ہیں'),
(3490, 31, 21, 'اور جب ان سے کہا جاتا ہے کہ جو کچھ اللہ نے نازل کیا ہے اس کا اتباع کرو تو کہتے ہیں کہ ہم اس کا اتباع کرتے ہیں جس پر اپنے باپ دادا کو عمل کرتے دیکھا ہے چاہے شیطان ان کو عذاب جہّنم کی طرف دعوت دے رہا ہو'),
(3491, 31, 22, 'اور جو اپنا روئے حیات خدا کی طرف موڑ دے اور وہ نیک کردار بھی ہو تو اس نے ریسمان ہدایت کو مضبوطی سے پکڑ لیا ہے اور تمام امور کا انجام اللہ کی طرف ہے'),
(3492, 31, 23, 'اور جو کفر اختیار کرلے اس کے کفر سے تم کو رنجیدہ نہیں ہونا چاہئے کہ سب کی بازگشت ہماری ہی طرف ہے اور اس وقت ہم بتائیں گے کہ انہوں نے کیا کیا ہے بیشک اللہ دلوں کے رازوں کو بھی جانتا ہے'),
(3493, 31, 24, 'ہم انہیں تھوڑے دن تک آرام دیں گے اور پھر سخت ترین عذاب کی طرف کھینچ کر لے آئیں گے'),
(3494, 31, 25, 'اور اگر آپ ان سے سوال کریں کہ زمین و آسمان کا خالق کون ہے تو کہیں گے کہ اللہ تو پھر کہئے کہ ساری حمد اللہ کے لئے ہے اور ان کی اکثریت بالکل جاہل ہے'),
(3495, 31, 26, 'اللرُہی کے لئے زمین و آسمان کی تمام چیزیں ہیں اور وہی بے نیاز بھی ہے اور قابل ستائش بھی'),
(3496, 31, 27, 'اور اگر روئے زمین کے تمام درخت قلم بن جائیں اور سمندر کا سہارا دینے کے لئے سات سمندر اور آ جائیں تو بھی کلمات الہٰی تمام ہونے والے نہیں ہیں بیشک اللہ صاحبِ عزت ّبھی ہے اور صاحب حکمت بھی ہے'),
(3497, 31, 28, 'تم سب کی خلقت اور تم سب کا دوبارہ زندہ کرنا سب ایک ہی آدمی جیسا ہے اور اللہ یقینا سب کچھ سننے والا اور دیکھنے والا ہے'),
(3498, 31, 29, 'کیا تم نے نہیں دیکھا کہ خدا ہی رات کو دن میں اور دن کو رات میں داخل کرتا ہے اور اس نے چاند اور سورج کو لَسخّر کردیا ہے کہ سب ایک معیّن مدّت تک چلتے رہیں گے اور اللہ تمہارے تمام اعمال سے باخبر ہے'),
(3499, 31, 30, 'یہ سب اسی لئے ہے کہ خدا معبود برحق ہے اور اس کے علاوہ جس کو بھی یہ لوگ پکارتے ہیں وہ سب باطل ہیں اور اللہ بلند و بالا اور بزرگ و برتر ہے'),
(3500, 31, 31, 'کیا تم نے نہیں دیکھا کہ نعمت خدا ہی سے کشتیاں دریا میں چل رہی ہیں تاکہ وہ تمہیں اپنی نشانیاں دکھلائے کہ اس میں تمام صبر و شکر کرنے والوں کے لئے بڑی نشانیاں پائی جاتی ہیں'),
(3501, 31, 32, 'اور جب سائبانوں کی طرح کوئی موج انہیں ڈھانکنے لگتی ہے تو دین کے پورے اخلاص کے ساتھ خدا کو آواز دیتے ہیں اور جب وہ نجات دے کر خشکی تک پہنچا دیتا ہے تو اس میں سے کچھ ہی اعتدال پر رہ جاتے ہیں اور ہماری آیات کا انکار غدار اور ناشکرے افراد کے علاوہ کوئی نہیں کرتا ہے'),
(3502, 31, 33, 'انسانو! اپنے پروردگار سے ڈرو اور اس دن سے ڈرو جس دن نہ باپ بیٹے کے کام آئے گا اور نہ بیٹا باپ کے کچھ کام آسکے گا بیشک اللہ کا وعدہ برحق ہے لہٰذا تمہیں زندگانی دنیا دھوکہ میں نہ ڈال دے اور خبردار کوئی دھوکہ دینے والا بھی تمہیں دھوکہ نہ دے سکے'),
(3503, 31, 34, 'یقینا اللہ ہی کے پاس قیامت کا علم ہے اور وہی پانی برساتا ہے اور شکم کے اندر کا حال جانتا ہے اور کوئی نفس یہ نہیں جانتا ہے کہ وہ کل کیا کمائے گا اور کسی کو نہیں معلوم ہے کہ اسے کس زمین پر موت آئے گی بیشک اللہ جاننے والا اور باخبر ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الۤمۤ'),
(3505, 32, 2, 'بیشک اس کتاب کی تنزیل عالمین کے پروردگار کی طرف سے ہے'),
(3506, 32, 3, 'کیا ان لوگوں کا کہنا یہ ہے کہ یہ رسول کا افترائ ہے, ہرگز نہیں یہ آپ کے پروردگار کی طرف سے برحق ہے تاکہ آپ اس قوم کو ڈرائیں جس کی طرف سے آپ سے پہلے کوئی ڈرانے والا رسول علیھ السّلامنہیں آیا ہے کہ شاید یہ ہدایت یافتہ ہوجائیں'),
(3507, 32, 4, 'اللرُہی وہ ہے جس نے آسمان و زمین اور اس کی تمام درمیانی مخلوقات کو چھ دن کے اندر پیدا کیا ہے اور اس کے بعد عرش پر اپنا اقتدار قائم کیا ہے اور تمہارے لئے اس کو چھوڑ کر کوئی سرپرست یا سفارش کرنے والا نہیں ہے کیا تمہاری سمجھ میں یہ بات نہیں آرہی ہے'),
(3508, 32, 5, 'وہ خدا آسمان سے زمین تک کے اُمور کی تدبیر کرتا ہے پھر یہ امر اس کی بارگاہ میں اس دن پیش ہوگا جس کی مقدار تمہارے حساب کے مطابق ہزار سال کے برابر ہوگی'),
(3509, 32, 6, 'وہ خدا حاضر و غائب سب کا جاننے والا اور صاحبِ عزّت و مہربان ہے'),
(3510, 32, 7, 'اس نے ہر چیز کو حسوُ کے ساتھ بنایا ہے اور انسان کی خلقت کا آغاز مٹی سے کیا ہے'),
(3511, 32, 8, 'اس کے بعد اس کی نسل کو ایک ذلیل پانی سے قرار دیا ہے'),
(3512, 32, 9, 'اس کے بعد اسے برابر کرکے اس میں اپنی روح پھونک دی ہے اور تمہارے لئے کان, آنکھ اور دل بنادیئے ہیں مگر تم بہت کم شکریہ ادا کرتے ہو'),
(3513, 32, 10, 'اور یہ کہتے ہیں کہ اگر ہم زمین میں گم ہوگئے تو کیا نئی خلقت میں پھر ظاہر کئے جائیں گے - بات یہ ہے کہ یہ اپنے پروردگار کی ملاقات کے منکر ہیں'),
(3514, 32, 11, 'آپ کہہ دیجئے کہ تم کو وہ ملک الموت زندگی کی آخری منزل تک پہنچائے گا جو تم پر تعینات کیا گیا ہے اس کے بعد تم سب پروردگار کی بارگاہ میں پیش کئے جاؤ گے'),
(3515, 32, 12, 'اور کاش آپ دیکھتے جب مجرمین پروردگار کی بارگاہ میں سر جھکائے کھڑے ہوں گے - پروردگار ہم نے سب دیکھ لیا اور سن لیا اب ہمیں دوبارہ واپس کردے کہ ہم نیک عمل کریں بیشک ہم یقین کرنے والوں میں ہیں'),
(3516, 32, 13, 'اور ہم چاہتے تو جبرا ہرنفس کو اس کی ہدایت دے دیتے لیکن ہماری طرف سے یہ بات طے ہوچکی ہے کہ ہم جہّنم کو جنات اور تمام گمراہ انسانوں سے بھردیں گے'),
(3517, 32, 14, 'لہذا تم لوگ اس بات کا مزہ چکھو کہ تم نے آج کے دن کی ملاقات کو اِھلا دیا تھا تو ہم نے بھی تم کو نظرانداز کردیا ہے اب اپنے گزشتہ اعمال کے بدلے دائمی عذاب کا مزہ چکھو'),
(3518, 32, 15, 'ہماری آیتوں پر ایمان لانے والے افراد بس وہ ہیں جنہیں آیات کی یاد دلائی جاتی ہے تو سجدہ میں گر پڑتے ہیں اور اپنے رب کی حمدوثنا کی تسبیح کرتے ہیں اور تکبر نہیں کرتے ہیں'),
(3519, 32, 16, 'ان کے پہلو بستر سے الگ رہتے ہیں اور وہ اپنے پروردگار کو خوف اور طمع کی بنیاد پر پکارتے رہتے ہیں اور ہمارے دیئے ہوئے رزق سے ہماری راہ میں انفاق کرتے رہتے ہیں'),
(3520, 32, 17, 'پس کسی نفس کو نہیں معلوم ہے کہ اس کے لئے کیا کیا خنکی چشم کا سامان چھپا کر رکھا گیا ہے جو ان کے اعمال کی جزا ہے'),
(3521, 32, 18, 'کیا وہ شخص جو صاحبِ ایمان ہے اس کے مثل ہوجائے گا جو فاسق ہے ہرگز نہیں دونوں برابر نہیں ہوسکتے'),
(3522, 32, 19, 'جو لوگ ایمان لائے ہیں اور انہوں نے نیک اعمال کئے ہیں ان کے لئے آرام کرنے کی جنتیں ہیں جو ان کے اعمال کی جزا ہیں'),
(3523, 32, 20, 'اور جن لوگوں نے فسق اختیار کیا ہے ان کا ٹھکاناجہّنم ہے کہ جب اس سے نکلنے کا ارادہ کریں گے تو دوبارہ پلٹا دیئے جائیں گے اور کہا جائے گا کہ اس جہنمّ کی آگ کا مزہ چکھو جس کا تم انکار کیا کرتے تھے'),
(3524, 32, 21, 'اور ہم یقینا بڑے عذاب سے پہلے انہیں معمولی عذاب کا مزہ چکھائیں گے کہ شاید اسی طرح راہ راست پر پلٹ آئیں'),
(3525, 32, 22, 'اور اس سے بڑا ظالم کون ہے جسے آیات هالہٰیہ کی یاد دلائی جائے اور پھر اس سے اعراض کرے تو ہم یقینا مجرمین سے انتقام لینے والے ہیں'),
(3526, 32, 23, 'اور ہم نے موسٰی علیھ السّلام کو بھی کتاب عطا کی ہے لہذا آپ کو اپنے قرآن کے منجانب اللہ ہونے میں شک نہیں ہونا چاہئے اور ہم نے کتاب هموسٰی علیھ السّلام کو بنی اسرائیل کے لئے ہدایت قرار دیا ہے'),
(3527, 32, 24, 'اور ہم نے ان میں سے کچھ لوگوں کو امام اور پیشوا قرار دیا ہے جو ہمارے امر سے لوگوں کی ہدایت کرتے ہیں اس لئے کہ انہوں نے صبر کیا ہے اور ہماری آیتوں پر یقین رکھتے تھے'),
(3528, 32, 25, 'بیشک آپ کا پروردگار ان کے درمیان روزِ قیامت ان تمام باتوں کا فیصلہ کردے گا جن میں یہ آپس میں اختلاف رکھتے تھے'),
(3529, 32, 26, 'تو کیا ان کی ہدایت کے لئے یہ کافی نہیں ہے کہ ہم نے ان سے پہلے بہت سی قوموں کو ہلاک کردیا ہے جن کی بستیوں میں یہ چل پھررہے ہیں اور اس میں ہماری بہت سی نشانیاں ہیں تو کیا یہ سنتے نہیں ہیں'),
(3530, 32, 27, 'کیا ان لوگوں نے یہ نہیں دیکھا ہے کہ ہم پانی کو چٹیل میدان کی طرف بہالے جاتے ہیں اور اس کے ذریعہ زراعت پیدا کرتے ہیں جسے یہ خود بھی کھاتے ہیں اور ان کے جانور بھی کھاتے ہیں تو کیا یہ دیکھتے نہیں ہیں'),
(3531, 32, 28, 'اور یہ لوگ کہتے ہیں کہ آخر وہ فتح کا دن کب آئے گا اگر آپ لوگ سچےّ ہیں'),
(3532, 32, 29, 'تو کہہ دیجئے کہ فتح کے دن پھر کفر اختیار کرنے والوں کا ایمان کام نہیں آئے گا اور نہ انہیں مہلت ہی دی جائے گی'),
(3533, 32, 30, 'لہذا آپ ان سے کنارہ کش رہیں اور وقت کا انتظار کریں کہ یہ بھی انتظار کرنے والے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'اے پیغمبر خدا سے ڈرتے رہئیے اور خبردار کافروں اور منافقوں کی اطاعت نہ کیجئے گا یقینا اللہ ہر شے کا جاننے والا اور صاحب ه حکمت ہے'),
(3535, 33, 2, 'اور جو کچھ آپ کے پروردگار کی طرف سے وحی کی جاتی ہے اسی کا اتباع کرتے رہیں کہ اللہ تم لوگوں کے اعمال سے خوب باخبر ہے'),
(3536, 33, 3, 'اور اپنے خدا پر اعتماد کیجئے کہ وہ نگرانی کے لئے بہت کافی ہے'),
(3537, 33, 4, 'اور اللہ نے کسی مرد کے سینے میں دو دل نہیں قرار دیئے ہیں اور تمہاری وہ بیویاں جن سے تم ا ظہار کرتے ہو انہیں تمہاری واقعی ماں نہیں قرار دیا ہے اور نہ تمہاری منہ بولی اولاد کواولادقرار دیا ہے یہ سب تمہاری زبانی باتیں ہیں اور اللہ تو صرف حق کی بات کہتا ہے اور سیدھے راستے کی طرف ہدایت کرتا ہے'),
(3538, 33, 5, 'ان بچوں کو ان کے باپ کے نام سے پکارو کہ یہی خدا کی نظر میں انصاف سے قریب تر ہے اور اگر ان کے باپ کو نہیں جانتے ہو تو یہ دین میں تمہارے بھائی اور دوست ہیں اور تمہارے لئے اس بات میں کوئی حرج نہیں ہے جو تم سے غلطی ہوگئی ہے البتہ تم اس بات کے ضرور ذمہ دار ہو جو تمہارے دلوں سے قصداانجام دی ہے اور اللہ بہت بخشنے والا اور مہربان ہے'),
(3539, 33, 6, 'بیشک نبی تمام مومنین سے ان کے نفس کے بہ نسبت زیادہ اولیٰ ہے اور ان کی بیویاں ان سب کی مائیں ہیں اور مومنین و مہاجرین میں سے قرابتدار ایک دوسرے سے زیادہ اولویت اور قربت رکھتے ہیں مگر یہ کہ تم اپنے دوستوں کے ساتھ نیک برتاؤ کرنا چاہو تو کوئی بات نہیں ہے یہ بات کتابِ خدا میں لکھی ہوئی موجود ہے'),
(3540, 33, 7, 'اوراس وقت کو یاد کیجئے جب ہم نے تمام انبیائ علیھ السّلامسے اور بالخصوص آپ سے اور نوح علیھ السّلام, ابراہیم علیھ السّلام ,موسیٰ علیھ السّلام اور عیسٰی بن علیھ السّلام مریم سے عہد لیا اور سب سے بہت سخت قسم کا عہد لیا'),
(3541, 33, 8, 'تاکہ صادقین سے ان کی صداقت تبلیغ کے بارے میں سوال کیا جائے اور خدا نے کافروں کے لئے بڑا دردناک عذاب مہیاّ کر رکھا ہے'),
(3542, 33, 9, 'ایمان والو! اس وقت اللہ کی نعمت کو یاد کرو جب کفر کے لشکر تمہارے سامنے آگئے اور ہم نے ان کے خلاف تمہاری مدد کے لئے تیز ہوا اور ایسے لشکر بھیج دیئے جن کو تم نے دیکھا بھی نہیں تھا اور اللہ تمہارے اعمال کوخوب دیکھنے والا ہے'),
(3543, 33, 10, 'اس وقت جب کفار تمہارے اوپر کی طرف سے اور نیچے کی سمت سے آگئے اور دہشت سے نگاہیں خیرہ کرنے لگیں اور کلیجے منہ کو آنے لگے اور تم خدا کے بارے میں طرح طرح کے خیالات میں مبتلا ہوگئے'),
(3544, 33, 11, 'اس وقت مومنین کا باقاعدہ امتحان لیا گیا اور انہیں شدید قسم کے جھٹکے دیئے گئے'),
(3545, 33, 12, 'اور جب منافقین اور جن کے دلوں میں مرض تھا یہ کہہ رہے تھے کہ خدا و رسول نے ہم سے صرف دھوکہ دینے والا وعدہ کیا ہے'),
(3546, 33, 13, 'اور جب ان کے ایک گروہ نے کہہ دیا کہ مدینہ والو اب یہاں ٹھکانا نہیں ہے لہذا واپس بھاگ چلو اور ان میں سے ایک گروہ نبی سے اجازت مانگ رہا تھا کہ ہمارے گھر خالی پڑے ہوئے ہیں حالانکہ وہ گھر خالی نہیں تھے بلکہ یہ لوگ صرف بھاگنے کا ارادہ کررہے تھے'),
(3547, 33, 14, 'حالانکہ اگر ان پر چاروں طرف سے لشکر داخل کردیئے جاتے اور پھر ان سے فتنہ کا سوال کیا جاتا تو فورا حاضر ہوجاتے اور تھوڑی دیر سے زیادہ نہ ٹہرتے'),
(3548, 33, 15, 'اور ان لوگوں نے اللہ سے یقینی عہد کیا تھا کہ ہرگز پیٹھ نہ پھرائیں گے اور اللہ کے عہد کے بارے میں بہرحال سوال کیا جائے گا'),
(3549, 33, 16, 'آپ کہہ دیجئے کہ اگر تم قتل یا موت کے خوف سے بھاگنا بھی چاہو تو فرار کام آنے والا نہیں ہے اور دنیا میں تھوڑا ہی آرام کرسکو گے'),
(3550, 33, 17, 'کہہ دیجئے کہ اگر خدا برائی کا ارادہ کرلے یا بھلائی ہی کرنا چاہے تو تمہیں اس سے کون بچاسکتا ہے اور یہ لوگ اس کے علاوہ نہ کوئی سرپرست پاسکتے ہیں اور نہ مددگار'),
(3551, 33, 18, 'خدا ان لوگوں کو بھی خوب جانتا ہے جو جنگ سے روکنے والے ہیں اور اپنے بھائیوں سے یہ کہنے والے ہیں کہ ہماری طرف آجاؤ اور یہ خود میدان جنگ میں بہت کم آتے ہیں'),
(3552, 33, 19, 'یہ تم سے جان چراتے ہیں اور جب خوف سامنے آجائے گا تو آپ دیکھیں گے کہ آپ کی طرف اس طرح دیکھیں گے کہ جیسے ان کی آنکھیں یوں پھر رہی ہیں جیسے موت کی غشی طاری ہو اور جب خوف چلا جائے گا تو آپ پر تیز تر زبانوں کے ساتھ حملہ کریں گے اور انہیں مال غنیمت کی حرص ہوگی یہ لوگ شروع ہی سے ایمان نہیں لائے ہیں لہٰذا خدا نے ان کے اعمال کو برباد کردیا ہے اور خدا کے لئے یہ کام بڑا آسان ہے'),
(3553, 33, 20, 'یہ لوگ ابھی تک اس خیال میں ہیں کہ کفار کے لشکر گئے نہیں ہیں اور اگر دوبارہ لشکر آجائیں تو یہ یہی چاہیں گے کہ اے کاش دیہاتیوں کے ساتھ صحراؤں میں آباد ہوگئے ہوتے اور وہاں سے تمہاری خبریں دریافت کرتے رہتے اور اگر تمہارے ساتھ رہتے بھی تو بہت کم ہی جہاد کرتے'),
(3554, 33, 21, 'مسلمانو! تم میں سے اس کے لئے رسول کی زندگی میں بہترین نمونہ عمل ہے جو شخص بھی اللہ اور آخرت سے امیدیں وابستہ کئے ہوئے ہے اور اللہ کو بہت زیادہ یاد کرتا ہے'),
(3555, 33, 22, 'اور صاحبانِ ایمان کا یہ عالم ہے کہ جب انہوں نے کفر کے لشکروں کو دیکھا تو پکار اٹھے کہ یہ وہی بات ہے جس کا خدا اور رسول نے وعدہ کیا تھا اور خدا و رسول کا وعدہ بالکل سچا ہے اور اس ہجوم نے ان کے ایمان اور جذبہ تسلیم میں مزید اضافہ ہی کردیا'),
(3556, 33, 23, 'مومنین میں ایسے بھی مردُ میدان ہیں جنہوں نے اللہ سے کئے وعدہ کو سچ کر دکھایا ہے ان میں بعض اپنا وقت پورا کرچکے ہیں اور بعض اپنے وقت کا انتظار کررہے ہیں اور ان لوگوں نے اپنی بات میں کوئی تبدیلی نہیں پیدا کی ہے'),
(3557, 33, 24, 'تاکہ خدا صادقین کو ان کی صداقت کا بدلہ دے اور منافقین کو چاہے تو ان پر عذاب نازل کرے یا ان کی توبہ قبول کرلے کہ اللہ یقینا بہت بخشنے والا اور مہربان ہے'),
(3558, 33, 25, 'اور خدا نے کفّار کو ان کے غصّہ سمیت واپس کردیا کہ وہ کوئی فائدہ حاصل نہ کرسکے اور اللہ نے مومنین کو جنگ سے بچالیا اور اللہ بڑی قوت والا اور صاحبِ عزّت ہے'),
(3559, 33, 26, 'اور اس نے کفار کی پشت پناہی کرنے والے اہل کتاب کو ان کے قلعوں سے نیچے اتار دیا اور ان کے دلوں میں ایسا رعب ڈال دیا کہ تم ان میں سے کچھ کو قتل کررہے تھے اور کچھ کو قیدی بنارہے تھے'),
(3560, 33, 27, 'اور پھر تمہیں ان کی زمین, ان کے دیار اور ان کے اموال اور زمینوں کا بھی وارث بنادیا جن میں تم نے قدم بھی نہیں رکھا تھا اور بیشک اللہ ہر شے پر قادر ہے'),
(3561, 33, 28, 'پیغمبر آپ اپنی بیویوں سے کہہ دیجئے کہ اگر تم لوگ زندگانی دنیا اور اس کی زینت کی طلبگار ہو تو آؤ میں تمہیں متاغِ دنیا دے کر خوبصورتی کے ساتھ رخصت کردوں'),
(3562, 33, 29, 'اور اگر اللہ اور رسول اور آخرت کی طلبگار ہو تو خدا نے تم میں سے نیک کردار عورتوں کے لئے بہت بڑا اجر فراہم کر رکھا ہے'),
(3563, 33, 30, 'اے زنان هپیغمبر جو بھی تم میں سے کِھلی ہوئی برائی کا ارتکاب کرے گی اس کا عذاب بھی دہرا کردیا جائے گا اور یہ بات خد اکے لئے بہت آسان ہے'),
(3564, 33, 31, 'اور جو بھی تم میں سے خدا اور رسول کی اطاعت کرے اور نیک اعمال کرے اسے دہرا اجر عطا کریں گے اور ہم نے اس کے لئے بہترین رزق فراہم کیا ہے'),
(3565, 33, 32, 'اے زنانِ پیغمبر تم اگر تقویٰ اختیار کرو تو تمہارا مرتبہ کسی عام عورت جیسا نہیں ہے لہٰذا کسی آدمی سے لگی لپٹی بات نہ کرنا کہ جس کے دل میں بیماری ہو اسے لالچ پیدا ہوجائے اور ہمیشہ نیک باتیں کیا کرو'),
(3566, 33, 33, 'اور اپنے گھر میں بیٹھی رہو اور پہلی جاہلیت جیسا بناؤ سنگھار نہ کرو اور نماز قائم کرو اور زکوِٰادا کرو اوراللہ اور اس کے رسول کی اطاعت کرو - بس اللہ کا ارادہ یہ ہے اے اہلبیت علیھ السّلام کہ تم سے ہر برائی کو دور رکھے اور اس طرح پاک و پاکیزہ رکھے جو پاک و پاکیزہ رکھنے کا حق ہے'),
(3567, 33, 34, 'اور ازواج پیغمبر تمہارے گھروں میں جن آیات الٰہی اور حکمت کی باتوں کی تلاوت کی جاتی ہے انہیں یاد رکھنا کہ خدا بڑا باریک بین اور ہر شے کی خبر رکھنے والا ہے'),
(3568, 33, 35, 'بیشک مسلمان مرد اور مسلمان عورتیں اور مومن مرد اور مومن عورتیں اور اطاعت گزار مرد اور اطاعت گزار عورتیں اور سچے مرد اور سچی عورتیں اور صابر مرد اور صابر عورتیں اور فروتنی کرنے والے مرد اور فروتنی کرنے والی عورتیں اور صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں روزہ رکھنے والے مرد اور روزہ رکھنے والی عورتیں اور اپنی عفّت کی حفاظت کرنے والے مرد اور عورتیں اور خدا کا بکثرت ذکر کرنے والے مرد اور عورتیں.اللہ نے ان سب کے لئے مغفرت اور عظیم اجر مہّیاکررکھا ہے'),
(3569, 33, 36, 'اور کسی مومن مرد یا عورت کو اختیار نہیں ہے کہ جب خدا و رسول کسی امر کے بارے میں فیصلہ کردیں تو وہ بھی اپنے امر کے بارے میں صاحبِ اختیار بن جائے اور جو بھی خدا و رسول کی نافرمانی کرے گا وہ بڑی کھاَلی ہوئی گمراہی میں مبتلا ہوگا'),
(3570, 33, 37, 'اور اس وقت کو یاد کرو جب تم اس شخص سے جس پر خدا نے بھی نعمت نازل کی اور تم نے بھی احسان کیا یہ کہہ رہے تھے کہ اپنی زوجہ کو روک کر رکھو اوراللہ سے ڈرو اور تم اپنے دل میں اس بات کو چھپائے ہوئے تھے جسے خدا ظاہر کرنے والا تھا اور تمہیں لوگوں کے طعنوں کا خوف تھا حالانکہ خدا زیادہ حقدار ہے کہ اس سے ڈرا جائے اس کے بعد جب زید نے اپنی حاجت پوری کرلی تو ہم نے اس عورت کا عقد تم سے کردیا تاکہ مومنین کے لئے منہ بولے بیٹوں کی بیویوں سے عقد کرنے میں کوئی حرج نہ رہے جب وہ لوگ اپنی ضرورت پوری کرچکیں اوراللہ کا حکم بہرحال نافذ ہوکر رہتا ہے'),
(3571, 33, 38, 'نبی کے لئے خدا کے فرائض میں کوئی حرج نہیں ہے یہ گزشتہ انبیائ کے دور سے سنّت الہٰیّہ چلی آرہی ہے اوراللہ کا حکم صحیح اندازے کے مطابق مقرر کیا ہوا ہوتا ہے'),
(3572, 33, 39, 'وہ لوگ ا للہ کے پیغام کو پہنچاتے ہیں اور دل میں اس کا خوف رکھتے ہیں اور اس کے علاوہ کسی سے نہیں ڈرتے ہیںاوراللہ حساب کرنے کے لئے کافی ہے'),
(3573, 33, 40, 'محمد تمہارے مُردوں میں سے کسی ایک کے باپ نہیں ہیں لیکن و ہ اللہ کے رسول اور سلسلہ انبیائ علیھ السّلامکے خاتم ہیں اوراللہ ہر شے کا خوب جاننے والا ہے'),
(3574, 33, 41, 'ایمان والواللہ کا ذکر بہت زیادہ کیا کرو'),
(3575, 33, 42, 'اور صبح و شام اس کی تسبیح کیا کرو'),
(3576, 33, 43, 'وہی وہ ہے جو تم پر رحمت نازل کرتا ہے اور اس کے فرشتے بھی تاکہ تمہیں تاریکیوں سے نکال کر نور کی منزل تک لے آئے اور وہ صاحبان هایمان پر بہت زیادہ مہربان ہے'),
(3577, 33, 44, 'ان کی مدارات جس دن پروردگار سے ملاقات کریں گے سلامتی سے ہوگی اور ان کے لئے اس نے بہترین اجر مہّیا کر رکھا ہے'),
(3578, 33, 45, 'اے پیغمبر ہم نے آپ کو گواہ, بشارت دینے والا, عذاب الہٰی سے ڈرانے والا'),
(3579, 33, 46, 'اور خدا کی طرف اس کی اجازت سے دعوت دینے والا اور روشن چراغ بناکر بھیجا ہے'),
(3580, 33, 47, 'اور مومنین کو بشارت دے دیجئے کہ ان کے لئے اللہ کی طرف سے بہت بڑا فضل و کرم ہے'),
(3581, 33, 48, 'اور خبردار کفّار اور منافقین کی اطاعت نہ کیجئے گا اور ان کی اذیت کا خیال ہی چھوڑ دیجئے اوراللہ پر اعتماد کیجئے کہ وہ نگرانی کرنے کے لئے بہت کافی ہے'),
(3582, 33, 49, 'ایمان والو جب مومنات سے نکاح کرنا اور ان کو ہاتھ لگائے بغیر طلاق دے دینا تو تمہارے لئے کوئی حق نہیں ہے کہ ان سے و عدہ کا مطالبہ کرو لہٰذا کچھ عطیہ دے کر خوبصورتی کے ساتھ رخصت کردینا'),
(3583, 33, 50, 'اے پیغمبر ہم نے آپ کے لئے آپ کی بیویوں کو جن کا مہر دے دیا ہے اور کنیزوں کو جنہیں خدا نے جنگ کے بغیر عطا کردیا ہے اور آپ کے چچا کی بیٹیوں کو اور آپ کی پھوپھی کی بیٹیوں کو اور آپ کے ماموں کی بیٹیوں کو اور آپ کی خالہ کی بیٹیوں کو جنہوں نے آپ کے ساتھ ہجرت کی ہے اور اس مومنہ عورت کو جو اپنا نفس نبی کو بخش دے اگر نبی اس سے نکاح کرنا چاہے تو حلال کردیا ہے لیکن یہ صرف آپ کے لئے ہے باقی مومنین کے لئے نہیں ہے. ہمیں معلوم ہے کہ ہم نے ان لوگوں پر ان کی بیویوں اور کنیزوں کے بارے میں کیا فریضہ قرار دیا ہے تاکہ آپ کے لئے کوئی زحمت اور مشقّت نہ ہو اوراللہ بہت زیادہ بخشنے والا اور مہربان ہے'),
(3584, 33, 51, 'ان میں سے جس کو آپ چاہیں الگ کرلیں اور جس کو چاہیں اپنی پناہ میں رکھیں اور جن کو الگ کردیا ہے ان میں سے بھی کسی کو چاہیں تو کوئی حرج نہیں ہے - یہ سب اس لئے ہے تاکہ ان کی آنکھیں ٹھنڈی رہیں اور یہ رنجیدہ نہ ہوں اور جو کچھ آپ نے دیدیا ہے اس سے خوش رہیں اوراللہ تمہارے دلوں کا حال خوب جانتا ہے اور وہ ہر شے کا جاننے والا اور صاحبِ حکمت ہے'),
(3585, 33, 52, 'اس کے بعد آپ کے لئے دوسری عورتیں حلال نہیں ہیں اور نہ یہ جائز ہے کہ ان بیویوں کو بدل لیں چاہے دوسری عورتوں کا حسن کتنا ہی اچھا کیوں نہ لگے علاوہ ان عورتوں کے جو آپ کے ہاتھوں کی ملکیت ہیں اور خدا ہر شے کی نگرانی کرنے والا ہے'),
(3586, 33, 53, 'اے ایمان والو خبردار پیغمبر کے گھروں میں اس وقت تک داخل نہ ہونا جب تک تمہیں کھانے کے لئے اجازت نہ دے دی جائے اور اس وقت بھی برتنوں پر نگاہ نہ رکھنا ہاں جب دعوت دے دی جائے تو داخل ہوجاؤ اور جب کھالو تو فورا منتشر ہوجاؤ اور باتوں میں نہ لگ جاؤ کہ یہ بات پیغمبر کو تکلیف پہنچاتی ہے اور وہ تمہارا خیال کرتے ہیں حالانکہ اللہ حق کے بارے میں کسی بات کی شرم نہیں رکھتا اور جب ازواج پیغمبر سے کسی چیز کا سوال کرو تو پردہ کے پیچھے سے سوال کرو کہ یہ بات تمہارے اور ان کے دونوں کے دلوں کے لئے زیادہ پاکیزہ ہے اور تمہیں حق نہیں ہے کہ خدا کے رسول کو اذیت دو یا ان کے بعد کبھی بھی ان کی ازواج سے نکاح کرو کہ یہ بات خدا کی نگاہ میں بہت بڑی بات ہے'),
(3587, 33, 54, 'تم کسی شے کا اظہار کرو یا اس کی پردہ داری کرواللہ بہرحال ہر شے کا جاننے والا ہے'),
(3588, 33, 55, 'اور عورتوں کے لئے کوئی حرج نہیں ہے اگر اپنے باپ دادا, اپنی اولاد, اپنے بھائی, اپنے بھتیجے اور اپنے بھانجوں کے سامنے بے حجاب آئیں یا اپنی عورتوں اور اپنی کنیزوں کے سامنے آئیں لیکن تم سب اللہ سے ڈرتی رہو کہ اللہ ہر شے پر حاضر و ناظر ہے'),
(3589, 33, 56, 'بیشک اللہ اور اس کے ملائکہ رسول پر صلٰوات بھیجتے ہیں تو اے صاحبانِ ایمان تم بھی ان پر صلٰوات بھیجتے رہو اور سلام کرتے رہو'),
(3590, 33, 57, 'یقینا جو لوگ خدا اور اس کے رسول کو ستاتے ہیں ان پر دنیا اور آخرت میں خدا کی لعنت ہے اور خدا نے ان کے لئے رسوا کن عذاب مہیاّ کر رکھا ہے'),
(3591, 33, 58, 'اور جو لوگ صاحبانِ ایمان مرد یا عورتوں کو بغیر کچھ کئے دھرے اذیت دیتے ہیں انہوں نے بڑے بہتان اور کھلے گناہ کا بوجھ اپنے سر پر اٹھا رکھا ہے'),
(3592, 33, 59, 'اے پیغمبر آپ اپنی بیویوں, بیٹیوں, اور مومنین کی عورتوں سے کہہ دیجئے کہ اپنی چادر کو اپنے اوپر لٹکائے رہا کریں کہ یہ طریقہ ان کی شناخت یا شرافت سے قریب تر ہے اور اس طرح ان کو اذیت نہ دی جائے گی اور خدا بہت بخشنے والا اور مہربان ہے'),
(3593, 33, 60, 'پھر اگر منافقین اور جن کے دلوں میں بیماری ہے اور مدینہ میںافواہ پھیلانے والے اپنی حرکتوں سے باز نہ آئے تو ہم آپ ہی کو ان پر مسلّط کردیں گے اور پھر یہ آپ کے ہمسایہ میں صرف چند ہی دن رہ پائیں گے'),
(3594, 33, 61, 'یہ لعنت کے مارے ہوئے ہوں گے کہ جہاں مل جائیں گرفتار کرلئے جائیں اور ان کے ٹکڑے ٹکڑے کردیئے جائیں'),
(3595, 33, 62, 'یہ خدائی سّنت ان لوگوں کے بارے میں رہ چکی ہے جو گزر چکے ہیں اور خدائی سّنت میں تبدیلی نہیں ہوسکتی ہے'),
(3596, 33, 63, 'پیغمبر یہ لوگ آپ سے قیامت کے بارے میں سوال کرتے ہیں تو کہہ دیجئے کہ اس کا علم خدا کے پاس ہے اور تم کیا جانو شائد وہ قریب ہی ہو'),
(3597, 33, 64, 'بیشک اللہ نے کّفار پر لعنت کی ہے اور ان کے لئے جہّنم کا انتظام کیا ہے'),
(3598, 33, 65, 'وہ اس میں ہمیشہ ہمیشہ رہیں گے اور انہیں کوئی سرپرست یا مددگار نہیں ملے گا'),
(3599, 33, 66, 'جس دن ان کے چہرے جہنمّ کی طرف موڑدیئے جائیں گے اور یہ کہیں گے کہ اے کاش ہم نے اللہ اور رسول کی اطاعت کی ہوتی'),
(3600, 33, 67, 'اور کہیں گے کہ ہم نے اپنے سرداروں اور بزرگوں کی اطاعت کی تو انہوں نے راستہ سے بہکادیا'),
(3601, 33, 68, 'پروردگار اب ان پر دہرا عذاب نازل کر اور ان پر بہت بڑی لعنت کر'),
(3602, 33, 69, 'ایمان والو خبردار ان کے جیسے نہ بن جاؤ جنہوں نے موسٰی علیھ السّلام کو اذیت دی تو خدا نے انہیں ان کے قول سے بری ثابت کردیا اور و ہ اللہ کے نزدیک ایک باوجاہت انسان تھے'),
(3603, 33, 70, 'ایمان والو اللہ سے ڈرو اور سیدھی بات کرو'),
(3604, 33, 71, 'تاکہ وہ تمہارے اعمال کی اصلاح کردے اور تمہارے گناہوں کو بخش دے اور جو بھی خدا اور اس کے رسول کی اطاعت کرے گا وہ عظیم کامیابی کے درجہ پر فائز ہوگا'),
(3605, 33, 72, 'بیشک ہم نے امانت کو آسمانً زمین اور پہاڑ سب کے سامنے پیش کیا اور سب نے اس کے اٹھانے سے انکار کیا اور خوف ظاہر کیا بس انسان نے اس بوجھ کو اٹھالیا کہ انسان اپنے حق میں ظالم اور نادان ہے'),
(3606, 33, 73, 'تاکہ خدا منافق مرد اور منافق عورت اور مشرک مرد اور مشرک عورت سب پر عذاب نازل کرے اور صاحبِ ایمان مرد اور صاحبِ ایمان عورتوں کی توبہ قبول کرے کہ خدا بہت بخشنے والا اور مہربان ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'ساری تعریف اس اللہ کے لئے ہے جس کے اختیار میں آسمان اور زمین کی تمام چیزیں ہیں اور اسی کے لئے آخرت میں بھی حمد ہے اور وہی صاحبِ حکمت اور ہر بات کی خبر رکھنے والا ہے'),
(3608, 34, 2, 'وہ جانتا ہے کہ زمین میں کیا چیز داخل ہوتی ہے اور کیا چیز اس سے نکلتی ہے اور کیا چیز آسمان سے نازل ہوتی ہے اور کیا اس میں بلند ہوتی ہے اور وہ مہربان اور بخشنے والا ہے'),
(3609, 34, 3, 'اور کفّار کہتے ہیں کہ قیامت آنے والی نہیں ہے تو آپ کہہ دیجئے کہ میرے پروردگار کی قسم وہ ضرور آئے گی وہ عالم الغیب ہے اس کے علم سے آسمان و زمین کا کوئی ذرّہ دور نہیں ہے اور نہ اس سے چھوٹا اور نہ بڑا بلکہ سب کچھ اس کی روشن کتاب میں محفوظ ہے'),
(3610, 34, 4, 'تاکہ وہ ایمان لانے والے اور نیک اعمال انجام دینے والوں کو جزا دے کہ ان کے لئے مغفرت اور باعزّت رزق ہے'),
(3611, 34, 5, 'اور جن لوگوں نے ہماری آیتوں کے مقابلہ میں دوڑ دھوپ کی ان کے لئے دردناک سزا کا عذاب معّین ہے'),
(3612, 34, 6, 'اور جن لوگوں کو علم دیا گیا ہے وہ جانتے ہیں کہ جو کچھ آپ کی طرف پروردگار کی طرف سے نازل کیا گیا ہے سب بالکل حق ہے اور خدائے غالب و قابلِ حمد و ثنا کی طرف ہدایت کرنے والا ہے'),
(3613, 34, 7, 'اور جن لوگوں نے کفر اختیار کیا ان کا کہنا ہے کہ ہم تمہیں ایسے آدمی کا پتہ بتائیں جو یہ خبر دیتا ہے کہ جب تم مرنے کے بعد ٹکڑے ٹکڑے ہوجاؤ گے تو تمہیں نئی خلقت کے بھیس میں لایا جائے گا'),
(3614, 34, 8, 'اس نے اللہ پر جھوٹا الزام باندھا ہے یا اس میں جنون پایا جاتا ہے نہیں بلکہ جو لوگ آخرت پر ایمان نہیں رکھتے وہ عذاب اور گمراہی میں مبتلا ہیں'),
(3615, 34, 9, 'کیا انہوں نے آسمان و زمین میں اپنے سامنے اور پس پشت کی چیزوں کو نہیں دیکھا ہے کہ ہم چاہیں تو انہیں زمین میں دھنسا دیں یا ان کے اوپر آسمان کو ٹکڑے ٹکڑے کرکے گرادیں. اس میں ہر رجوع کرنے والے بندہ کے لئے قدرت کی نشانی پائی جاتی ہے'),
(3616, 34, 10, 'اور ہم نے داؤد کو یہ فضل عطا کیا کہ پہاڑو تم ان کے ساتھ تسبیح پروردگار کیا کرو اور پرندوں کو لَسخّر کردیا اور لوہے کو نرم کردیا'),
(3617, 34, 11, 'کہ تم کشادہ اور مکمل زرہیں بناؤ اور کڑیوں کے جوڑنے میں اندازہ کا خیال رکھو اور تم لوگ سب نیک عمل کرو کہ میں تم سب کے اعمال کا دیکھنے والا ہوں'),
(3618, 34, 12, 'اور ہم نے سلیمان علیھ السّلامکے ساتھ ہواؤں کو مسخر کردیا کہ ان کی صبح کی رفتار ایک ماہ کی مسافت تھی اور شام کی رفتار بھی ایک ماہ کے برابر تھی اور ہم نے ان کے لئے تانبے کا چشمہ جاری کردیا اور جنّات میں ایسے افراد بنادیئے جو خدا کی اجازت سے ان کے سامنے کام کرتے تھے اور جس نے ہمارے حکم سے انحراف کیا ہم اسے آتش جہّنم کا مزہ چکھائیں گے'),
(3619, 34, 13, 'یہ جنات سلیمان کے لئے جو وہ چاہتے تھے بنادیتے تھے جیسے محرابیں, تصویریں اور حوضوں کے برابر پیالے اور بڑی بڑی زمین میں گڑی ہوئی دیگیں آل داؤد شکر ادا کرو اور ہمارے بندوں میں شکر گزار بندے بہت کم ہیں'),
(3620, 34, 14, 'پھر جب ہم نے ان کی موت کا فیصلہ کردیا تو ان کی موت کی خبر بھی جنات کو کسی نے نہ بتائی سوائے دیمک کے جو ان کے عصا کو کھارہی تھی اور وہ خاک پر گرے تو جنات کو معلوم ہوا کہ اگر وہ غیب کے جاننے والے ہوتے تو اس ذلیل کرنے والے عذاب میں مبتلا نہ رہتے'),
(3621, 34, 15, 'اور قوم سبا کے لئے ان کے وطن ہی میں ہماری نشانی تھی کہ داہنے بائیں دونوں طرف باغات تھے. تم لوگ اپنے پروردگار کا دیا رزق کھاؤ اور اس کا شکر ادا کرو تمہارے لئے پاکیزہ شہر اور بخشنے والا پروردگار ہے'),
(3622, 34, 16, 'مگر ان لوگوں نے انحراف کیا تو ہم نے ان پر بڑے زوروں کا سیلاب بھیج دیا اور ان کے دونوں باغات کو ایسے دو باغات میں تبدیل کردیا جن کے پھل بے مزہ تھے اور ان میں جھاؤ کے درخت اور کچھ بیریاں بھی تھیں'),
(3623, 34, 17, 'یہ ہم نے ان کی ناشکری کی سزادی ہے اور ہم ناشکروں کے علاوہ کس کو سزادیتے ہیں'),
(3624, 34, 18, 'اور جب ہم نے ان کے اور ان بستیوں کے درمیان جن میں ہم نے برکتیں رکھی ہیں کچھ نمایاں بستیاں قرار دیں اور ان کے درمیان سیر کو معّین کردیا کہ اب دن و رات جب چاہو سفر کرو محفوظ رہوگے'),
(3625, 34, 19, 'تو انہوں نے اس پر بھی یہ کہا کہ پروردگار ہمارے سفر دور دراز بنادے اوراس طرح اپنے نفس پر ظلم کیا تو ہم نے انہیں کہانی بناکر چھوڑ دیا اور انہیں ٹکڑے ٹکڑے کردیا کہ یقینا اس میں صبر و شکر کرنے والوں کے لئے بڑی نشانیاں پائی جاتی ہیں'),
(3626, 34, 20, 'اور ان پر ابلیس نے اپنے گمان کو سچا کردکھایا تو مومنین کے ایک گروہ کو چھوڑ کر سب نے اس کا اتباع کرلیا'),
(3627, 34, 21, 'اور شیطان کو ان پر اختیار حاصل نہ ہوتا مگر ہم یہ جاننا چاہتے ہیں کون آخرت پر ایمان رکھتا ہے اور کون اس کی طرف سے شک میں مبتلا ہے اور آپ کا پروردگار ہر شے کا نگراں ہے'),
(3628, 34, 22, 'آپ کہہ دیجئے کہ تم لوگ انہیں پکارو جن کااللہ کو چھوڑ کر تمہیں خیال تھا تو دیکھو گے کہ یہ آسمان و زمین میں ایک ذرہ ّ برابر اختیار نہیں رکھتے ہیں اور ان کا آسمان و زمین میں کوئی حصہّ ہے اور نہ ان میں سے کوئی ان لوگوں کا پشت پناہ ہے'),
(3629, 34, 23, 'اس کے یہاں کسی کی سفارش بھی کام آنے والی نہیں ہے مگر وہ جس کو وہ خود اجازت دیدے یہاں تک کہ جب ان کے دلوں کی ہیبت دور کردی جائے گی تو پوچھیں گے کہ تمہارے پروردگار نے کیا کہا ہے تو وہ جواب دیں گے کہ جو کچھ کہا ہے حق کہا ہے اور وہ بلند و بالا اور بزرگ و برتر ہے'),
(3630, 34, 24, 'آپ کہئے کہ تمہیں آسمان و زمین سے روزی کون دیتا ہے اور پھر بتائیے کہ اللہ اور کہئے کہ ہم یا تم ہدایت پر ہیں یا کھلی ہوئی گمراہی میں ہیں'),
(3631, 34, 25, 'انہیں بتائیے کہ ہم جو خطا کریں گے اس کے بارے میں تم سے سوال نہ کیا جائے گا اور تم جو کچھ کرو گے اس کے بارے میں ہم سے سوال نہ ہوگا'),
(3632, 34, 26, 'کہئے کہ ایک دن خدا ہم سب کو جمع کرے گا اور پھر ہمارے درمیان حق کے ساتھ فیصلہ کرے گا اور وہ بہترین فیصلہ کرنے والا ہے اور جاننے والاہے'),
(3633, 34, 27, 'ان سے کہئے کہ ذرا ان شرکائ کو دکھلاؤ جن کو خدا سے ملادیا ہے - یہ ہرگز نہیں دکھا سکیں گے بلکہ خدا صرف خدائے عزیز و حکیم ہے'),
(3634, 34, 28, 'اور پیغمبر ہم نے آپ کو تمام لوگوں کے لئے صرف بشیر و نذیر بناکر بھیجا ہے یہ اور بات ہے کہ اکثر لوگ اس حقیقت سے باخبر نہیں ہیں'),
(3635, 34, 29, 'اور کہتے ہیں کہ اگر تم سچے ہو تو یہ وعدہ قیامت کب پورا ہوگا'),
(3636, 34, 30, 'کہہ دیجئے کہ تمہارے لئے ایک دن کا وعدہ مقرر ہے جس سے ایک ساعت پیچھے ہٹ سکتے ہو اور نہ آ گے بڑھ سکتے ہو'),
(3637, 34, 31, 'اور کفاّر یہ کہتے ہیں کہ ہم نہ اس قرآن پر ایمان لائیں گے اور نہ اس سے پہلے والی کتابوں پر تو کاش آپ دیکھتے جب ان ظالموں کو پروردگار کے حضور کھڑا کیا جائے گا اور ہر ایک بات کو دوسرے کی طرف پلٹائے گا اور جن لوگوں کو کمزور سمجھ لیا گیا ہے وہ اونچے بن جانے والوں سے کہیں گے کہ اگر تم درمیان میں نہ آگئے ہوتے تو ہم صاحب هایمان ہوگئے ہوتے'),
(3638, 34, 32, 'تو بڑے لوگ کمزور لوگوں سے کہیں گے کہ کیا ہم نے تمہیں ہدایت کے آنے کے بعد اس کے قبول کرنے سے روکا تھا ہرگز نہیں تم خود مجرم تھے'),
(3639, 34, 33, 'اور کمزور لوگ بڑے لوگوں سے کہیں گے کہ یہ تمہاری دن رات کی مکاری کا اثر ہے جب تم ہمیں حکم دیتے تھے کہ خدا کا انکار کریں اور اس کے لئے مثل قرار دیں اور عذاب دیکھنے کے بعد لوگ اپنے دل ہی دل میں شرمندہ بھی ہوں گے اور ہم کفر اختیار کرنے والوں کی گردن میں طوق بھی ڈال دیں گے کیا ان کو اس کے علاوہ کوئی بدلہ دیا جائے گا جو اعمال یہ کرتے رہے ہیں'),
(3640, 34, 34, 'اور ہم نے کسی بستی میں کوئی ڈرانے والا نہیں بھیجا مگر یہ کہ اس کے بڑے لوگوں نے یہ کہہ دیا کہ ہم تمہارے پیغام کا انکار کرنے والے ہیں'),
(3641, 34, 35, 'اور یہ بھی کہہ دیا کہ ہم اموال اور اولاد کے اعتبار سے تم سے بہتر ہیں اور ہم پر عذاب ہونے والا نہیں ہے'),
(3642, 34, 36, 'آ پ کہہ دیجئے کہ میرا پروردگار جس کے رزق میں چاہتا ہے کمی یا زیادتی کردیتا ہے لیکن اکثر لوگ نہیں جانتے ہیں'),
(3643, 34, 37, 'اور تمہارے اموال اور اولاد میں کوئی ایسا نہیں ہے جو تمہیں ہماری بارگاہ میں قریب بناسکے علاوہ ان کے جو ایمان لائے اور انہوں نے نیک اعمال کئے تو ان کے لئے ان کے اعمال کا دہرا بدلہ دیا جائے گا اور وہ جھروکوں میں امن وامان کے ساتھ بیٹھے ہوں گے'),
(3644, 34, 38, 'اور جو لوگ ہماری نشانیوں کے مقابلہ میں دوڑ دھوپ کررہے ہیں وہ جہّنم کے عذاب میں جھونک دیئے جائیں گے'),
(3645, 34, 39, 'بیشک ہمارا پروردگار اپنے بندوں میں جس کے رزق میں چاہتا ہے وسعت پیدا کرتا ہے اور جس کے رزق میں چاہتا ہے تنگی پیدا کرتا ہے اور جو کچھ اس کی راہ میں خرچ کرو گے وہ اس کا بدلہ بہرحال عطا کرے گا اور وہ بہترین رزق دینے والا ہے'),
(3646, 34, 40, 'اور جس دن خدا سب کو جمع کرے گا اور پھر ملائکہ سے کہے گا کہ کیا یہ لوگ تمہاری ہی عبادت کرتے تھے'),
(3647, 34, 41, 'تو وہ عرض کریں گے کہ تو پاک و بے نیاز اور ہمارا ولی ہے یہ ہمارے کچھ نہیں ہیں اور یہ جنّات کی عبادت کرتے تھے اور ان کی اکثریت ان ہی پر ایمان رکھتی تھی'),
(3648, 34, 42, 'پھر آج کوئی کسی کے نفع اور نقصان کا مالک نہیں ہوگا اور ہم ظلم کرنے والوں سے کہیں گے کہ اس جہّنم کے عذاب کا مزہ چکھو جس کی تکذیب کیا کرتے تھے'),
(3649, 34, 43, 'اور جب ان کے سامنے ہماری روشن آیتوں کی تلاوت کی جاتی ہے تو کہتے ہیں کہ یہ شخص صرف یہ چاہتا ہے کہ تمہیں ان سب سے روک دے جن کی تمہارے آبائ و اجداد پرستش کیا کرتے تھے اور یہ صرف ایک گڑھی ہوئی داستان ہے اور کفاّر تو جب بھی ان کے سامنے حق آتا ہے یہی کہتے ہیں کہ یہ ایک کھلا ہوا جادو ہے'),
(3650, 34, 44, 'اور ہم نے انہیں ایسی کتابیں نہیں عطا کی ہیں جنہیں یہ پڑھتے ہوں اور نہ ان کی طرف آپ سے پہلے کوئی ڈرانے والا بھیجا ہے'),
(3651, 34, 45, 'اور ان کے پہلے والوں نے بھی انبیائ علیھ السّلامکی تکذیب کی ہے حالانکہ ان کے پاس اس کا دسواں حصّہ بھی نہیں ہے جتنا ہم نے ان لوگوں کو عطا کیا تھا مگر انہوں نے بھی رسولوں کی تکذیب کی تو تم نے دیکھا کہ ہمارا عذاب کتنا بھیانک تھا'),
(3652, 34, 46, 'پیغمبر آپ کہہ دیجئے کہ میں تمہیں صرف اس بات کی نصیحت کرتا ہوں کہ اللہ کے لئے ایک ایک دو دو کرکے اٹھو اور پھر یہ غور کرو کہ تمہارے ساتھی میں کسی طرح کاجنون نہیں ہے - وہ صرف آنے والے شدید عذاب کے پیش آنے سے پہلے تمہارا ڈرانے والا ہے'),
(3653, 34, 47, 'کہہ دیجئے کہ میں جو اجر مانگ رہا ہوں وہ بھی تمہارے ہی لئے ہے میرا حقیقی اجر تو پروردگار کے ذمہ ہے اور وہ ہر شے کا گواہ ہے'),
(3654, 34, 48, 'کہہ دیجئے کہ میرا پروردگار حق کو برابر دل میں ڈالتا رہتا ہے اور وہ برابر غیب کا جاننے والا ہے'),
(3655, 34, 49, 'آپ کہہ دیجئے کہ حق آگیا ہے اور باطل نہ کچھ ایجاد کرسکتا ہے اور نہ دوبارہ پلٹا سکتا ہے'),
(3656, 34, 50, 'کہہ دیجئے کہ میں گمراہ ہوں گا تو اس کا اثر میرے ہی اوپر ہوگا اور اگر ہدایت حاصل کرلوں گا تو یہ میرے رب کی وحی کا نتیجہ ہوگا وہ سب کی سننے والا اور سب سے قریب تر ہے'),
(3657, 34, 51, 'اور کاش آپ دیکھئے کہ یہ گھبرائے ہوئے ہوں گے اور بچ نہ سکیں گے اور بہت قریب سے پکڑلئے جائیں گے'),
(3658, 34, 52, 'اور وہ کہیں گے کہ ہم ایمان لے آئے حالانکہ اتنی دور دراز جگہ سے ایمان تک دسترس کہاں ممکن ہے'),
(3659, 34, 53, 'اور یہ پہلے انکار کرچکے ہیں اور ازغیب باتیں بہت دور تک چلاتے رہے ہیں'),
(3660, 34, 54, 'اور اب تو ان کے اور ان چیزوں کے درمیان جن کی یہ خواہش رکھتے ہیں پردے حائل کردیئے گئے ہیں جس طرح ان کے پہلے والوں کے ساتھ کیا گیا تھا کہ وہ لوگ بھی بڑے بے چین کرنے والے شک میں پڑے ہوئے تھے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'تمام تعریفیں اس خدا کے لئے ہیں جو آسمان و زمین کا پیدا کرنے والا اور ملائکہ کو اپنا پیغامبر بنانے والا ہے وہ ملائکہ جن کے پر دو دو تین تین اور چار چار ہیں اور وہ خلقت میں جس قدر چاہتا ہے اضافہ کردیتا ہے کہ بیشک وہ ہر شے پر قدرت رکھنے والا ہے'),
(3662, 35, 2, 'اللرُ انسانوں کے لئے جو رحمت کا دروازہ کھول دے اس کا کوئی روکنے والا نہیں ہے اور جس کو روک دے اس کا کوئی بھیجنے والا نہیں ہے وہ ہر شے پر غالب اور صاحبِ حکمت ہے'),
(3663, 35, 3, 'انسانو! اپنے اوپراللہ کی نعمت کو یاد کرو کیا اس کے علاوہ بھی کوئی خالق ہے وہی تمہیں آسمان اور زمین سے روزی دیتا ہے اس کے علاوہ کوئی خدا نہیں ہے تو تم کس طرف بہکے چلے جارہے ہو'),
(3664, 35, 4, 'اور اگر یہ لوگ آپ کو جھٹلاتے ہیں تو آپ سے پہلے بہت سے رسولوں کو جھٹلایا جاچکا ہے اور تمام امور کی بازگشت خدا ہی کی طرف ہے'),
(3665, 35, 5, 'انسانو! اللرُ کا وعدہ سچا ہے لہٰذا زندگانی دنیا تم کو دھوکہ میں نہ ڈال دے اور دھوکہ دینے والا تمہیں دھوکہ نہ دے دے'),
(3666, 35, 6, 'بیشک شیطان تمہارا دشمن ہے تو اسے دشمن سمجھو وہ اپنے گروہ کو صرف اس بات کی طرف دعوت دیتا ہے کہ سب جہنمیوں میں شامل ہوجائیں'),
(3667, 35, 7, 'جن لوگوں نے کفر اختیار کیا ان کے لئے سخت عذاب ہے اور جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ان کے لئے مغفرت اور بہت بڑا اجر ہے'),
(3668, 35, 8, 'تو کیا وہ شخص جس کے بفِے اعمال کو اس طرح آراستہ کردیا گیا کہ وہ اسے اچھا سمجھنے لگا کسی مومن کے برابر ہوسکتا ہے - اللرُجس کو چاہتا ہے گمراہی میں چھوڑ دیتا ہے اور جس کو چاہتا ہے ہدایت دے دیتا ہے تو آپ افسوس کی بنا پر ان کے پیچھے اپنی جان نہ دے دیںاللہ ان کے کاروبار سے خوب باخبر ہے'),
(3669, 35, 9, 'اللرُ ہی وہ ہے جس نے ہواؤں کو بھیجا تو وہ بادلوں کو منتشر کرتی ہیں اور پھر ہم انہیں مفِدہ شہر تک لے جاتے ہیں اور زمین کے لَردہ ہوجانے کے بعد اسے زندہ کردیتے ہیں اور اسی طرح مفِدے دوبارہ اٹھائے جائیں گے'),
(3670, 35, 10, 'جو شخص بھی عزّت کا طلبگار ہے وہ سمجھ لے کہ عزّت سب پروردگار کے لئے ہے - پاکیزہ کلمات اسی کی طرف بلند ہوتے ہیں اور عمل صالح انہیں بلند کرتا ہے اور جو لوگ برائیوں کی تدبیریں کرتے ہیں ان کے لئے شدید عذاب ہے اور ان کا مکر بہرحال ہلاک اور تباہ ہونے والا ہے'),
(3671, 35, 11, 'اوراللہ ہی نے تم کو خاک سے پیدا کیا پھر نطفہ سے بنایا پھر تمہیں جوڑا قرار دیا اور جو کچھ عورت اپنے شکم میں اٹھاتی ہے یا پیدا کرتی ہے سب اس کے علم سے ہوتا ہے اور کسی بھی طویل العمر کو جو عمر دی جاتی ہے یا عمر میں کمی کی جاتی ہے یہ سب کتاب الٰہی میں مذکور ہے اوراللہ کے لئے یہ سب کام بہت آسان ہے'),
(3672, 35, 12, 'اور دو سمندر ایک جیسے نہیں ہوسکتے ایک کا پانی میٹھا اور خوشگوار ہے اور ایک کا کھارا اور کڑوا ہے اور تم دونوں سے تازہ گوشت کھاتے ہو اور ایسے زیورات برآمد کرتے ہو جو تمہارے پہننے کے کام آتے ہیں اور تم کشتیوں کو دیکھتے ہو کہ سمندر کا سینہ چیرتی چلی جاتی ہیں تاکہ تم فضل خدا تلاش کرسکو اور شاید اسی طرح شکر گزار بھی بن سکو'),
(3673, 35, 13, 'وہ خدا رات کو دن میں داخل کرتا ہے اور دن کو رات میں اس نے سورج اور چاند کو تابع بنادیا ہے اور سب اپنے مقررہ وقت کے مطابق سیر کررہے ہیں وہی تمہارا پروردگار ہے اسی کے اختیار میں سارا ملک ہے اور اس کے علاوہ تم جنہیں آواز دیتے ہووہ خرمہ کی گٹھلی کے چھلکے کے برابر بھی اختیار کے مالک نہیں ہیں'),
(3674, 35, 14, 'تم انہیں پکارو گے تو تمہاری آواز کو نہ سوُ سکیں گے اور صَن لیں گے تو تمہیں جواب نہ دے سکیں گے اور قیامت کے دن تو تمہاری شرکت ہی کا انکار کردیں گے اور ان کی باتوں کی اطلاع ایک باخبر ہستی کی طرح کوئی دوسرا نہیں کرسکتا'),
(3675, 35, 15, 'انسانو تم سب اللہ کی بارگاہ کے فقیر ہو اوراللہ صاحب هدولت اور قابلِ حمد و ثنا ہے'),
(3676, 35, 16, 'وہ چاہے تو تم سب کو اٹھالے جائے اور تمہارے بدلے دوسری مخلوقات لے آئے'),
(3677, 35, 17, 'اوراللہ کے لئے یہ کوئی مشکل کام نہیں ہے'),
(3678, 35, 18, 'اور کوئی شخص کسی دوسرے کے گناہوں کا بوجھ نہیں اٹھائے گا اور اگر کسی کو اٹھانے کے لئے بلایا بھی جائے گا تو اس میں سے کچھ بھی نہ اٹھایا جاسکے گا چاہے وہ قرابتدار ہی کیوں نہ ہو آپ صرف ان لوگوں کو ڈراسکتے ہیں جو ازغیب خدا سے ڈرنے والے ہیں اور نماز قائم کرنے والے ہیں اور جو بھی پاکیزگی اختیار کرے گا وہ اپنے فائدہ کے لئے کرے گا اور سب کی بازگشت خدا ہی کی طرف ہے'),
(3679, 35, 19, 'اور اندھے اور بینا برابر نہیں ہوسکتے'),
(3680, 35, 20, 'اور تاریکیاں اور نور دونوں برابر نہیں ہوسکتے'),
(3681, 35, 21, 'اور سایہ اور دھوپ دونوں برابر نہیں ہوسکتے'),
(3682, 35, 22, 'اور زندہ اور مفِدے برابر نہیں ہوسکتے اللہ جس کو چاہتا ہے اپنی بات صَنا دیتا ہے اور آپ انہیں نہیں سناسکتے جو قبروں کے اندر رہنے والے ہیں'),
(3683, 35, 23, 'آپ تو صرف ڈرانے والے ہیں'),
(3684, 35, 24, 'ہم نے آپ کو حق کے ساتھ بشارت دینے والا اور ڈرانے والا بناکر بھیجا ہے اور کوئی قوم ایسی نہیں ہے جس میں کوئی ڈرانے والا نہ گزرا ہو'),
(3685, 35, 25, 'اور اگر یہ لوگ آپ کی تکذیب کرتے ہیں تو ان کے پہلے والوں نے بھی یہی کیا ہے جب ان کے پاس مرسلین معجزات, صحیفے اور روشن کتاب لے کر آئے'),
(3686, 35, 26, 'تو ہم نے کافروں کو اپنی گرفت میں لے لیا پھر ہمارا عذاب کیسا بھیانک ہوا ہے'),
(3687, 35, 27, 'کیا تم نے نہیں دیکھا کہ خدا نے آسمان سے پانی نازل کیا پھر ہم نے اس سے مختلف رنگ کے پھل پیدا کئے اور پہاڑوں میں بھی مختلف رنگوں کے سفید اور سرخ راستے بنائے اور بعض بالکل سیاہ رنگ تھے'),
(3688, 35, 28, 'اور انسانوں اور چوپایوں اور جانوروں میں بھی مختلف رنگ کی مخلوقات پائی جاتی ہیں لیکن اللہ سے ڈرنے والے اس کے بندوں میں صرف صاحبانِ معرفت ہیں بیشک اللہ صاحب هعزّت اور بہت بخشنے والا ہے'),
(3689, 35, 29, 'یقینا جو لوگ اللہ کی کتاب کی تلاوت کرتے ہیں اور انہوں نے نماز قائم کی ہے اور جو کچھ ہم نے بطور رزق دیا ہے اس میں سے ہماری راہ میں خفیہ اور اعلانیہ خرچ کیا ہے یہ لوگ ایسی تجارت کے امیدوار ہیں جس میں کسی طرح کی تباہی نہیں ہے'),
(3690, 35, 30, 'تاکہ خدا ان کا پورا پورا اجر دے اور اپنے فضل و کرم سے اضافہ بھی کردے یقینا وہ بہت زیادہ بخشنے والا اور قدر کرنے والا ہے'),
(3691, 35, 31, 'اور جس کتاب کی وحی ہم نے آپ کی طرف کی ہے وہ برحق ہے اور اپنے پہلے والی کتابوں کی تصدیق کرنے والی ہے اور بیشک اللہ اپنے بندوں کے حالات سے باخبر اور خوب دیکھنے والا ہے'),
(3692, 35, 32, 'پھر ہم نے اس کتاب کا وارث ان افراد کو قرار دیا جنہیں اپنے بندوں میں سے چن لیا کہ ان میں سے بعض اپنے نفس پر ظلم کرنے والے ہیں اور بعض اعتدال پسند ہیں اور بعض خدا کی اجازت سے نیکیوں کی طرف سبقت کرنے والے ہیں اور درحقیقت یہی بہت بڑا فضل و شرف ہے'),
(3693, 35, 33, 'یہ لوگ ہمیشہ رہنے والی جنّت میں داخل ہوں گے انہیں سونے کے کنگن اور موتی کے زیورات پہنائے جائیں گے اور ان کا لباس جنّت میں ریشم کا ہوگا'),
(3694, 35, 34, 'اور یہ کہیں گے کہ خدا کا شکر ہے کہ اس نے ہم سے رنج و غم کو دور کر دیا اور بیشک ہمارا پروردگار بہت زیادہ بخشنے والا اور قدرداں ہے'),
(3695, 35, 35, 'اس نے ہمیں اپنے فضل و کرم سے ایسی رہنے کی جگہ پر وارد کیا ہے جہاں نہ کوئی تھکن ہمیں چھو سکتی ہے اور نہ کوئی تکلیف ہم تک پہنچ سکتی ہے'),
(3696, 35, 36, 'اور جن لوگوں نے کفر اختیار کیا ان کے لئے آتش جہّنم ہے اور نہ ان کی قضا ہی آئے گی کہ مرجائیں اور نہ عذاب ہی میں کوئی تخفیف کی جائے گی ہم اسی طرح ہر کفر کرنے والے کو سزا دیا کرتے ہیں'),
(3697, 35, 37, 'اور یہ وہاں فریاد کریں گے کہ پروردگار ہمیں نکال لے ہم اب نیک عمل کریں گے اس کے برخلاف جو پہلے کیا کرتے تھے تو کیا ہم نے تمہیں اتنی عمر نہیں دی تھی جس میں عبرت حاصل کرنے والے عبرت حاصل کرسکتے تھے اور تمہارے پاس تو ڈرانے والا بھی آیا تھا لہٰذا اب عذاب کا مزہ چکھو کہ ظالمین کا کوئی مددگار نہیں ہے'),
(3698, 35, 38, 'بیشک اللہ آسمان و زمین کے غیب کا جاننے والا ہے اور وہ دُلوں کے چھپے ہوئے اسرار کو بھی جانتا ہے'),
(3699, 35, 39, 'وہی وہ خدا ہے جس نے تم کو زمین میں اگلوں کا جانشین بنایا ہے اب جو کفر کرے گا وہ اپنے کفر کا ذمہ دار ہوگا اور کفر پروردگار کی نظر میں کافروں کے لئے سوائے غضب الہٰی اور خسارہ کے کسی شے میں اضافہ نہیں کرسکتا ہے'),
(3700, 35, 40, 'آپ کہہ دیجئے کہ کیا تم لوگوں نے ان شرکائ کو دیکھا ہے جنہیں خدا کو چھوڑ کر پکارتے ہو ذرا مجھے بھی دکھلاؤ کہ انہوں نے زمین میں کس چیز کو پیدا کیا ہے یا ان کی کوئی شرکت آسمان میں ہے یا ہم نے انہیں کوئی کتاب دی ہے کہ اس کی طرف سے وہ کسی دلیل کے حامل ہیں - یہ کچھ نہیں ہے اصل یہ ہے کہ ظالمین آپس میں ایک دوسرے سے بھی پرفریب وعدہ ہی کرتے ہیں'),
(3701, 35, 41, 'بیشک اللہ زمین و آسمان کو زائل ہونے سے روکے ہوئے ہے اور اس کے علاوہ دوسرا کوئی سنبھالنے والا ہوتا تو اب تک دونوں زائل ہوچکے ہوتے وہ بڑا اَبردبار اور بخشنے والا ہے'),
(3702, 35, 42, 'اور ان لوگوں نے باقاعدہ قسمیں کھائیں کہ اگر ہمارے پاس کوئی ڈرانے والا آگیا تو ہم تمام اُمّتوں سے زیادہ ہدایت یافتہ ہوں گے لیکن جب وہ ڈرانے والا آگیا تو سوائے نفرت کے کسی شے میں اضافہ نہیں ہوا'),
(3703, 35, 43, 'یہ زمین میں استکبار اور بڑی چالوں کا نتیجہ ہے حالانکہ بڑی چالیں چالباز ہی کو اپنے گھیرے میں لے لیتی ہیں تو اب یہ گزشتہ لوگوں کے بارے میں خدا کے طریقہ کار کے علاوہ کسی چیز کا انتظار نہیں کررہے ہیں اور خدا کا طریقہ کار بھی نہ بدلنے والا ہے اور نہ اس میں کسی طرح کا تغیر ہوسکتا ہے'),
(3704, 35, 44, 'تو کیا ان لوگوں نے زمین میں سیر نہیں کی کہ دیکھیں ان سے پہلے والوں کا انجام کیا ہوا ہے جب کہ وہ ان سے زیادہ طاقتور تھے اور خدا ایسا نہیں ہے کہ زمین و آسمان کی کوئی شے اسے عاجز بناسکے وہ یقینا ہر شے کا جاننے والا اور اس پر قدرت رکھنے والا ہے'),
(3705, 35, 45, 'اور اگراللہ تمام انسانوں سے ان کے اعمال کا مواخذہ کرلیتا تو روئے زمین پر ایک رینگنے والے کو بھی نہ چھوڑتا لیکن وہ ایک مخصوص اور معین مدّت تک ڈھیل دیتا ہے اس کے بعد جب وہ وقت آجائے گا تو پروردگار اپنے بندوں کے بارے میں خوب بصیرت رکھنے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یٰسۤ'),
(3707, 36, 2, 'قرآن حکیم کی قسم'),
(3708, 36, 3, 'آپ مرسلین میں سے ہیں'),
(3709, 36, 4, 'بالکل سیدھے راستے پر ہیں'),
(3710, 36, 5, 'یہ قرآن خدائے عزیز و مہربان کا نازل کیا ہوا ہے'),
(3711, 36, 6, 'تاکہ آپ اس قوم کو ڈرائیں جس کے باپ دادا کو کسی پیغمبر کے ذریعہ نہیں ڈرایا گیا تو سب غافل ہی رہ گئے'),
(3712, 36, 7, 'یقینا ان کی اکثریت پر ہمارا عذاب ثابت ہوگیا تو وہ ایمان لانے والے نہیں ہیں'),
(3713, 36, 8, 'ہم نے ان کی گردن میں طوق ڈال دیئے ہیں جو ان کی ٹھڈیوں تک پہنچے ہوئے ہیں اور وہ سر اٹھائے ہوئے ہیں'),
(3714, 36, 9, 'اور ہم نے ایک دیوار ان کے سامنے اور ایک دیوار ان کے پیچھے بنادی ہے اور پھر انہیں عذاب سے ڈھانک دیا ہے کہ وہ کچھ دیکھنے کے قابل نہیں رہ گئے ہیں'),
(3715, 36, 10, 'اور ان کے لئے سب برابر ہے آپ انہیں ڈرائیں یا نہ ڈرائیں یہ ایمان لانے والے نہیں ہیں'),
(3716, 36, 11, 'آپ صرف ان لوگوں کو ڈراسکتے ہیں جو نصیحت کا اتباع کریں اور بغیر دیکھے ازغیب خدا سے ڈرتے رہیں ان ہی لوگوں کو آپ مغفرت اور باعزت اجر کی بشارت دے دیں'),
(3717, 36, 12, 'بیشک ہم ہی مفِدوں کو زندہ کرتے ہیں اور ان کے گزشتہ اعمال اور ان کے آثار کولکھتے جاتے ہیں اور ہم نے ہر شے کو ایک روشن امام میں جمع کردیا ہے'),
(3718, 36, 13, 'اور پیغمبر آپ ان سے بطور مثال اس قریہ والوں کا تذکرہ کریں جن کے پاس ہمارے رسول آئے'),
(3719, 36, 14, 'اس طرح کہ ہم نے دو رسولوں کو بھیجا تو ان لوگوں نے جھٹلادیا تو ہم نے ان کی مدد کو تیسرا رسول بھی بھیجا اور سب نے مل کر اعلان کیا کہ ہم سب تمہاری طرف بھیجے گئے ہیں'),
(3720, 36, 15, 'ان لوگوں نے کہا تم سب ہمارے ہی جیسے بشر ہو اور رحمٰن نے کسی شے کو نازل نہیں کیا ہے تم صرف جھوٹ بولتے ہو'),
(3721, 36, 16, 'انہوں نے جواب دیا کہ ہمارا پروردگار جانتا ہے کہ ہم تمہاری طرف بھیجے گئے ہیں'),
(3722, 36, 17, 'اور ہماری ذمہ داری صرف واضح طور پر پیغام پہنچادینا ہے'),
(3723, 36, 18, 'ان لوگوں نے کہا کہ ہمیں تم منحوس معلوم ہوتے ہو اگر اپنی باتوں سے باز نہ آؤ گے تو ہم سنگسار کردیں گے اور ہماری طرف سے تمہیں سخت سزا دی جائے گی'),
(3724, 36, 19, 'ان لوگوں نے جواب دیا کہ تمہاری نحوست تمہارے ساتھ ہے کیا یہ یاد دہانی کوئی نحوست ہے حقیقت یہ ہے کہ تم زیادتی کرنے والے لوگ ہو'),
(3725, 36, 20, 'اور شہر کے ایک سرے سے ایک شخص دوڑتا ہوا آیا اور اس نے کہا کہ قوم والو مرسلین کا اتباع کرو'),
(3726, 36, 21, 'ان کا اتباع کرو جو تم سے کسی طرح کی اجرت کا سوال نہیں کرتے ہیں اور ہدایت یافتہ ہیں'),
(3727, 36, 22, 'اور مجھے کیا ہوگیا ہے کہ میں اس کی عبادت نہ کروں جس نے مجھے پیدا کیا ہے اور تم سب اسی کی بارگاہ میں پلٹائے جاؤ گے'),
(3728, 36, 23, 'کیا میں اس کے علاوہ دوسرے خدا اختیار کرلو ں جب کہ وہ مجھے نقصان پہنچانا چاہے تو کسی کی سفارش کام آنے والی نہیں ہے اور نہ کوئی بچاسکتا ہے'),
(3729, 36, 24, 'میں تو اس وقت کھلی ہوئی گمراہی میں ہوجاؤں گا'),
(3730, 36, 25, 'میں تمہارے پروردگار پر ایمان لایا ہوں لہٰذا تم میری بات سنو'),
(3731, 36, 26, 'نتیجہ میں اس بندہ سے کہا گیا کہ جنّت میں داخل ہوجا تو اس نے کہا کہ اے کاش میری قوم کو بھی معلوم ہوتا'),
(3732, 36, 27, 'کہ میرے پروردگار نے کس طرح بخش دیا ہے اور مجھے باعزّت لوگوں میں قرار دیا ہے'),
(3733, 36, 28, 'اور ہم نے اس کی قوم پر اس کے بعد نہ آسمان سے کوئی لشکر بھیجا ہے اور نہ ہم لشکر بھیجنے والے تھے'),
(3734, 36, 29, 'وہ تو صرف ایک چنگھاڑ تھی جس کے بعد سب کا شعلہ حیات سرد پڑگیا'),
(3735, 36, 30, 'کس قدر حسرتناک ہے ان بندوں کا حال کہ جب ان کے پاس کوئی رسول آتا ہے تو اس کا مذاق اُڑانے لگتے ہیں'),
(3736, 36, 31, 'کیا ان لوگوں نے نہیں دیکھا کہ ہم نے ان سے پہلے کتنی قوموں کو ہلاک کردیا ہے جو اب ان کی طرف پلٹ کر آنے والی نہیں ہیں'),
(3737, 36, 32, 'اور پھر سب ایک دن اکٹھا ہمارے پاس حاضر کئے جائیں گے'),
(3738, 36, 33, 'اور ان کے لئے ہماری ایک نشانی یہ مردہ زمین بھی ہے جسے ہم نے زندہ کیا ہے اور اس میں دانے نکالے ہیں جن میں سے یہ لوگ کھارہے ہیں'),
(3739, 36, 34, 'اور اسی زمین میں اَخرمے اور انگور کے باغات پیدا کئے ہیں اور چشمے جاری کئے ہیں'),
(3740, 36, 35, 'تاکہ یہ اس کے پھل کھائیں حالانکہ یہ سب ان کے ہاتھوں کا عمل نہیں ہے پھر آخر یہ ہمارا شکریہ کیوں نہیں ادا کرتے ہیں'),
(3741, 36, 36, 'پاک و بے نیاز ہے وہ خدا جس نے تمام جوڑوں کو پیدا کیا ہے ان چیزوں میں سے جنہیں زمین اگاتی ہے اور ان کے نفوس میں سے اور ان چیزوں میں سے جن کا انہیں علم بھی نہیں ہے'),
(3742, 36, 37, 'اور ان کے لئے ایک نشانی رات ہے جس میں سے ہم کھینچ کر دن کو نکال لیتے ہیں تو یہ سب اندھیرے میں چلے جاتے ہیں'),
(3743, 36, 38, 'اور آفتاب اپنے ایک مرکز پر دوڑ رہا ہے کہ یہ خدائے عزیز و علیم کی معین کی ہوئی حرکت ہے'),
(3744, 36, 39, 'اور چاند کے لئے بھی ہم نے منزلیں معین کردی ہیں یہاں تک کہ وہ آخر میں پلٹ کر کھجور کی سوکھی ٹہنی جیسا ہوجاتا ہے'),
(3745, 36, 40, 'نہ آفتاب کے بس میں ہے کہ چاند کو پکڑ لے اور نہ رات کے لئے ممکن ہے کہ وہ دن سے آگے بڑھ جائے .... اور یہ سب کے سب اپنے اپنے فلک اور مدار میں تیرتے رہتے ہیں'),
(3746, 36, 41, 'اور ان کے لئے ہماری ایک نشانی یہ بھی ہے کہ ہم نے ان کے بزرگوں کو ایک بھری ہوئی کشتی میں اٹھایا ہے'),
(3747, 36, 42, 'اور اس کشتی جیسی اور بہت سی چیزیں پیدا کی ہیں جن پر یہ سوار ہوتے ہیں'),
(3748, 36, 43, 'اور اگر ہم چاہیں تو سب کو غرق کردیں پھر نہ کوئی ان کافریاد رس پیدا ہوگا اور نہ یہ بچائے جاسکیں گے'),
(3749, 36, 44, 'مگر یہ کہ خود ہماری رحمت شامل حال ہوجائے اور ہم ایک مدّت تک آرام کرنے دیں'),
(3750, 36, 45, 'اور جب ان سے کہا جاتا ہے کہ اس عذاب سے ڈرو جو سامنے یا پیچھے سے آسکتا ہے شاید کہ تم پر رحم کیا جائے'),
(3751, 36, 46, 'تو ان کے پاس خدا کی نشانیوں میں سے کوئی نشانی نہیں آتی ہے مگر یہ کہ یہ کنارہ کشی اختیار کرلیتے ہیں'),
(3752, 36, 47, 'اور جب کہا جاتا ہے کہ جو رزق خدا نے دیا ہے اس میں سے اس کی راہ میں خرچ کرو تو یہ کفّار صاحبانِ ایمان سے طنزیہ طور پر کہتے ہیں کہ ہم انہیں کیوں کھلائیں جنہیں خدا چاہتا تو خود ہی کھلادیتا تم لوگ تو کِھلی ہوئی گمراہی میں مبتلا ہو'),
(3753, 36, 48, 'اور پھر کہتے ہیں کہ آخر یہ وعدہ قیامت کب پورا ہوگا اگر تم لوگ اپنے وعدہ میں سچّے ہو'),
(3754, 36, 49, 'درحقیقت یہ صرف ایک چنگھاڑ کا انتظار کررہے ہیں جو انہیں اپنی گرفت میں لے لے گی اور یہ جھگڑا ہی کرتے رہ جائیں گے'),
(3755, 36, 50, 'پھر نہ کوئی وصیّت کر پائیں گے اور نہ اپنے اہل کی طرف پلٹ کر ہی جاسکیں گے'),
(3756, 36, 51, 'اور پھر جب صور پھونکا جائے گا تو سب کے سب اپنی قبروں سے نکل کر اپنے پروردگار کی طرف چل کھڑے ہوں گے'),
(3757, 36, 52, 'کہیں گے کہ آخر یہ ہمیں ہماری خواب گاہ سے کس نے اٹھادیا ہے .... بیشک یہی وہ چیز ہے جس کا خدا نے وعدہ کیا تھا اور اس کے رسولوں نے سچ کہا تھا'),
(3758, 36, 53, 'قیامت تو صرف ایک چنگھاڑ ہے اس کے بعد سب ہماری بارگاہ میں حاضر کردیئے جائیں گے'),
(3759, 36, 54, 'پھر آج کے دن کسی نفس پر کسی طرح کا ظلم نہیں کیا جائے گا اور تم کو صرف ویسا ہی بدلہ دیا جائے گا جیسے اعمال تم کررہے تھے'),
(3760, 36, 55, 'بیشک اہل جنّت آج کے دن طرح طرح کے مشاغل میں مزے کررہے ہوں گے'),
(3761, 36, 56, 'وہ اور ان کی بیویاں سب جنّت کی چھاؤں میں تخت پر تکیئے لگائے آرام کررہے ہوں گے'),
(3762, 36, 57, 'ان کے لئے تازہ تازہ میوے ہوں گے اور اس کے علاوہ جو کچھ بھی وہ چاہیں گے'),
(3763, 36, 58, 'ان کے حق میں ان کے مہربان پروردگار کا قول صرف سلامتی ہوگا'),
(3764, 36, 59, 'اور اے مجرمو تم ذرا ان سے الگ تو ہوجاؤ'),
(3765, 36, 60, 'اولاد آدم کیا ہم نے تم سے اس بات کا عہد نہیں لیا تھا کہ خبردار شیطان کی عبادت نہ کرنا کہ وہ تمہارا کِھلا ہوا دشمن ہے'),
(3766, 36, 61, 'اور میری عبادت کرنا کہ یہی صراط مستقیم اور سیدھا راستہ ہے'),
(3767, 36, 62, 'اس شیطان نے تم میں سے بہت سی نسلوں کو گمراہ کردیا ہے تو کیا تم بھی عقل استعمال نہیں کرو گے'),
(3768, 36, 63, 'یہی وہ جہّنم ہے جس کا تم سے دنیا میں وعدہ کیا جارہا تھا'),
(3769, 36, 64, 'آج اسی میں چلے جاؤ کہ تم ہمیشہ کفر اختیار کیا کرتے تھے'),
(3770, 36, 65, 'آج ہم ان کے منہ پرلَہر لگادیں گے اور ان کے ہاتھ بولیں گے اوران کے پاؤں گواہی دیں گے کہ یہ کیسے اعمال انجام دیا کرتے تھے'),
(3771, 36, 66, 'اور ہم اگر چاہیں تو ان کی آنکھوں کو مٹا دیں پھر یہ راستہ کی طرف قدم بڑھاتے رہیں لیکن کہاں دیکھ سکتے ہیں'),
(3772, 36, 67, 'اور ہم چاہیں تو خود ان ہی کو بالکل مسخ کردیں جس کے بعد نہ آگے قدم بڑھاسکیں اور نہ پیچھے ہی پلٹ کر واپس آسکیں'),
(3773, 36, 68, 'اور ہم جسے طویل عمر دیتے ہیں اسے خلقت میں بچپنے کی طرف واپس کر دیتے ہیں کیا یہ لوگ سمجھتے نہیں ہیں'),
(3774, 36, 69, 'اور ہم نے اپنے پیغمبر کو شعر کی تعلیم نہیں دی ہے اور نہ شاعری اس کے شایان شان ہے یہ تو ایک نصیحت اور کِھلا ہوا روشن قرآن ہے'),
(3775, 36, 70, 'تاکہ اس کے ذریعہ زندہ افراد کو عذاب الہٰی سے ڈرائیں اور کفاّر پر حجّت تمام ہوجائے'),
(3776, 36, 71, 'کیا ان لوگوں نے نہیں دیکھا کہ ہم نے ان کے فائدے کے لئے اپنے دست قدرت سے چوپائے پیدا کردیئے ہیں تو اب یہ ان کے مالک کہے جاتے ہیں'),
(3777, 36, 72, 'اور پھر ہم نے ان جانوروں کو رام کردیا ہے تو بعض سے سواری کا کام لیتے ہیں اور بعض کو کھاتے ہیں'),
(3778, 36, 73, 'اور ان کے لئے ان جانوروں میں بہت سے فوائد ہیں اور پینے کی چیزیں بھی ہیں تو یہ شکر خدا کیوں نہیں کرتے ہیں'),
(3779, 36, 74, 'اور ان لوگوں نے خدا کو چھوڑ کر دوسرے خدا بنالئے ہیں کہ شائد ان کی مدد کی جائے گی'),
(3780, 36, 75, 'حالانکہ یہ ان کی مدد کی طاقت نہیں رکھتے ہیں اور یہ ان کے ایسے لشکر ہیں جنہیں خود بھی خدا کی بارگاہ میں حاضر کیا جائے گا'),
(3781, 36, 76, 'لہٰذا پیغمبر آپ ان کی باتوں سے رنجیدہ نہ ہوں ہم وہ بھی جانتے ہیں جو یہ چھپا رہے ہیں اور وہ بھی جانتے ہیں جس کا یہ اظہار کررہے ہیں'),
(3782, 36, 77, 'تو کیا انسان نے یہ نہیں دیکھا کہ ہم نے اسے نطفہ سے پیدا کیا ہے اور وہ یکبارگی ہمارا کھلا ہوا دشمن ہوگیا ہے'),
(3783, 36, 78, 'اور ہمارے لئے مثل بیان کرتا ہے اور اپنی خلقت کو بھول گیا ہے کہتا ہے کہ ان بوسیدہ ہڈیوں کو کون زندہ کرسکتا ہے'),
(3784, 36, 79, 'آپ کہہ دیجئے کہ جس نے پہلی مرتبہ پیدا کیا ہے وہی زندہ بھی کرے گا اور وہ ہر مخلوق کا بہتر جاننے والا ہے'),
(3785, 36, 80, 'اس نے تمہارے لئے ہرے درخت سے آگ پیدا کردی ہے تو تم اس سے ساری آگ روشن کرتے رہے ہو'),
(3786, 36, 81, 'تو کیا جس نے زمین و آسمان کو پیدا کیا ہے وہ اس بات پر قادر نہیں ہے کہ ان کا مثل دوباہ پیدا کردے یقینا ہے اور وہ بہترین پیدا کرنے والا اور جاننے والا ہے'),
(3787, 36, 82, 'اس کا امر صرف یہ ہے کہ کسی شے کے بارے میں یہ کہنے کا ارادہ کرلے کہ ہوجا اور وہ شے ہوجاتی ہے'),
(3788, 36, 83, 'پس پاک و بے نیاز ہے وہ خدا جس کے ہاتھوں میں ہر شے کا اقتدار ہے اور تم سب اسی کی بارگاہ میں پلٹا کر لے جائے جاؤ گے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'باقاعدہ طور پر صفیں باندھنے والوں کی قسم'),
(3790, 37, 2, 'پھر مکمل طریقہ سے تنبیہ کرنے والوں کی قسم'),
(3791, 37, 3, 'پھر ذکر خدا کی تلاوت کرنے والوں کی قسم'),
(3792, 37, 4, 'بیشک تمہارا خدا ایک ہے'),
(3793, 37, 5, 'وہ آسمان و زمین اور ان کے مابین کی تمام چیزوں کا پروردگار اور ہر مشرق کا مالک ہے'),
(3794, 37, 6, 'بیشک ہم نے آسمان دنیا کو ستاروں سے مزین بنادیا ہے'),
(3795, 37, 7, 'اور انہیں ہر سرکش شیطان سے حفاظت کا ذریعہ بنادیا ہے'),
(3796, 37, 8, 'کہ اب شیاطین عالم بالا کیباتیں سننے کی کوشش نہیں کرسکتے اور وہ ہر طرف سے مارے جائیں گے'),
(3797, 37, 9, 'ہنکانے کے لئے اور ان کے لئے ہمیشہ ہمیشہ کا عذاب ہے'),
(3798, 37, 10, 'علاوہ اس کے جو کوئی بات اچک لے تو اس کے پیچھے آگ کا شعلہ لگ جاتا ہے'),
(3799, 37, 11, 'اب ذرا ان سے دریافت کرو کہ یہ زیادہ دشوار گزار مخلوق ہیں یا جن کو ہم پیدا کرچکے ہیں ہم نے ان سب کو ایک لسدار مٹی سے پیدا کیا ہے'),
(3800, 37, 12, 'بلکہ تم تعجب کرتے ہو اور یہ مذاق اڑاتے ہیں'),
(3801, 37, 13, 'اور جب نصیحت کی جاتی ہے تو قبول نہیں کرتے ہیں'),
(3802, 37, 14, 'اور جب کوئی نشانی دیکھ لیتے ہیں تو مسخرا پن کرتے ہیں'),
(3803, 37, 15, 'اور کہتے ہیں کہ یہ تو ایک کھلا ہوا جادو ہے'),
(3804, 37, 16, 'کیا جب ہم مرجائیں گے اور مٹی اور ہڈی ہوجائیں گے تو کیا دوبارہ اٹھائیں جائیں گے'),
(3805, 37, 17, 'اور کیا ہمارے اگلے باپ دادا بھی زندہ کئے جائیں گے'),
(3806, 37, 18, 'کہہ دیجئے کہ بیشک اور تم ذلیل بھی ہوگے'),
(3807, 37, 19, 'یہ قیامت تو صرف ایک للکار ہوگی جس کے بعد سب دیکھنے لگیں گے'),
(3808, 37, 20, 'اور کہیں گے کہ ہائے افسوس یہ تو قیامت کا دن ہے'),
(3809, 37, 21, 'بیشک یہی وہ فیصلہ کا دن ہے جسے تم لوگ جھٹلایا کرتے تھے'),
(3810, 37, 22, 'فرشتو ذرا ان ظلم کرنے والوں کو اور ان کے ساتھیوں کو اور خدا کے علاوہ جن کی یہ عبادت کیا کرتے تھے سب کو اکٹھا تو کرو'),
(3811, 37, 23, 'اور ان کے تمام معبودوں کو اور ان کو جہّنم کا راستہ تو بتادو'),
(3812, 37, 24, 'اور ذرا ان کو ٹہراؤ کہ ابھی ان سے کچھ سوال کیا جائے گا'),
(3813, 37, 25, 'اب تمہیں کیا ہوگیا ہے کہ ایک دوسرے کی مدد کیوں نہیں کرتے ہو'),
(3814, 37, 26, 'بلکہ آج تو سب کے سب سر جھکائے ہوئے ہیں'),
(3815, 37, 27, 'اور ایک دوسرے کی طرف رخ کرکے سوال کررہے ہیں'),
(3816, 37, 28, 'کہتے ہیں کہ تم ہی تو ہو جو ہماری داہنی طرف سے آیا کرتے تھے'),
(3817, 37, 29, 'وہ کہیں گے کہ نہیں تم خود ہی ایمان لانے والے نہیں تھے'),
(3818, 37, 30, 'اور ہماری تمہارے اوپر کوئی حکومت نہیں تھی بلکہ تم خود ہی سرکش قوم تھے'),
(3819, 37, 31, 'اب ہم سب پر خدا کا عذاب ثابت ہوگیا ہے اور سب کو اس کا مزہ چکھنا ہوگا'),
(3820, 37, 32, 'ہم نے تم کو گمراہ کیا کہ ہم خود ہی گمراہ تھے'),
(3821, 37, 33, 'تو آج کے دن سب ہی عذاب میں برابر کے شریک ہوں گے'),
(3822, 37, 34, 'اور ہم اسی طرح مجرمین کے ساتھ برتاؤ کیا کرتے ہیں'),
(3823, 37, 35, 'ان سے جب کہا جاتا تھا کہ اللہ کے علاوہ کوئی خدا نہیں ہے تو اکڑ جاتے تھے'),
(3824, 37, 36, 'اور کہتے تھے کہ کیا ہم ایک مجنون شاعر کی خاطر اپنے خداؤں کو چھوڑ دیں گے'),
(3825, 37, 37, 'حالانکہ وہ حق لے کر آیا تھا اور تمام رسولوں کی تصدیق کرنے والا تھا'),
