(3927, 37, 139, 'اور بیشک یونس علیھ السّلام بھی مرسلین میں سے تھے'),
(3928, 37, 140, 'جب وہ بھاگ کر ایک بھری ہوئی کشتی کی طرف گئے'),
(3929, 37, 141, 'اور اہل کشتی نے قرعہ نکالا تو انہیں شرمندگی کا سامنا کرنا پڑا'),
(3930, 37, 142, 'پھر انہیں مچھلی نے نگل لیا جب کہ وہ خود اپنے نفس کی ملامت کررہے تھے'),
(3931, 37, 143, 'پھر اگر وہ تسبیح کرنے والوں میں سے نہ ہوتے'),
(3932, 37, 144, 'تو روزِ قیامت تک اسی کے شکم میں رہ جاتے'),
(3933, 37, 145, 'پھر ہم نے ان کو ایک میدان میں ڈال دیا جب کہ وہ مریض بھی ہوگئے تھے'),
(3934, 37, 146, 'اور ان پر ایک کدو کا درخت اُگادیا'),
(3935, 37, 147, 'اور انہیں ایک لاکھ یا اس سے زیادہ کی قوم کی طرف نمائندہ بناکر بھیجا'),
(3936, 37, 148, 'تو وہ لوگ ایمان لے آئے اور ہم نے بھی ایک مدّت تک انہیں آرام بھی دیا'),
(3937, 37, 149, 'پھر اے پیغمبر ان کفار سے پوچھئے کہ کیا تمہارے پروردگار کے پاس لڑکیاں ہیں اور تمہارے پاس لڑکے ہیں'),
(3938, 37, 150, 'یا ہم نے ملائکہ کو لڑکیوں کی شکل میں پیدا کیا ہے اور یہ اس کے گواہ ہیں'),
(3939, 37, 151, 'آگاہ ہوجاؤ کہ یہ لوگ اپنی من گھڑت کے طور پر یہ باتیں بناتے ہیں'),
(3940, 37, 152, 'کہ اللہ کے یہاں فرزند پیدا ہوا ہے اور یہ لوگ بالکل جھوٹے ہیں'),
(3941, 37, 153, 'کیا اس نے اپنے لئے بیٹوں کے بجائے بیٹیوں کا انتخاب کیا ہے'),
(3942, 37, 154, 'آخر تمہیں کیا ہوگیا ہے تم کیسا فیصلہ کررہے ہو'),
(3943, 37, 155, 'کیا تم غور و فکر نہیں کررہے ہو'),
(3944, 37, 156, 'یا تمہارے پاس اس کی کوئی واضح دلیل ہے'),
(3945, 37, 157, 'تو اپنی کتاب کو لے آؤ اگر تم اپنے دعویٰ میں سچے ہو'),
(3946, 37, 158, 'اور انہوں نے خدا اور جّنات کے درمیان بھی رشتہ قرار دے دیا حالانکہ جّنات کو معلوم ہے کہ انہیں بھی خدا کی بارگاہ میں حاضر کیا جائے گا'),
(3947, 37, 159, 'خدا ان سب کے بیانات سے بلند و برتر اور پاک و پاکیزہ ہے'),
(3948, 37, 160, 'علاوہ خدا کے نیک اور مخلص بندوں کے'),
(3949, 37, 161, 'پھر تم اور جس کی تم پرستش کررہے ہو'),
(3950, 37, 162, 'سب مل کر بھی اس کے خلاف کسی کو بہکا نہیں سکتے ہو'),
(3951, 37, 163, 'علاوہ اس کے جس کو جہّنم میں جانا ہی ہے'),
(3952, 37, 164, 'اور ہم میں سے ہر ایک کے لئے ایک مقام معّین ہے'),
(3953, 37, 165, 'اور ہم اس کی بارگاہ میں صف بستہ کھڑے ہونے والے ہیں'),
(3954, 37, 166, 'اور ہم اس کی تسبیح کرنے والے ہیں'),
(3955, 37, 167, 'اگرچہ یہ لوگ یہی کہا کرتے تھے'),
(3956, 37, 168, 'کہ اگر ہمارے پاس بھی پہلے والوں کا تذکرہ ہوتا'),
(3957, 37, 169, 'تو ہم بھی اللہ کے نیک اور مخلص بندے ہوتے'),
(3958, 37, 170, 'تو پھر ان لوگوں نے کفر اختیار کرلیا تو عنقریب انہیں اس کا انجام معلوم ہوجائے گا'),
(3959, 37, 171, 'اور ہمارے پیغامبربندوں سے ہماری بات پہلے ہی طے ہوچکی ہے'),
(3960, 37, 172, 'کہ ان کی مدد بہرحال کی جائے گی'),
(3961, 37, 173, 'اور ہمارا لشکر بہرحال غالب آنے والا ہے'),
(3962, 37, 174, 'لہذا آپ تھوڑے دنوں کے لئے ان سے منہ پھیر لیں'),
(3963, 37, 175, 'اور ان کو دیکھتے رہیں عنقریب یہ خود اپنے انجام کو دیکھ لیں گے'),
(3964, 37, 176, 'کیا یہ ہمارے عذاب کے بارے میں جلدی کررہے ہیں'),
(3965, 37, 177, 'تو جب وہ عذاب ان کے آنگن میں نازل ہوجائے گا تو وہ ڈرائی جانے والی قوم کی بدترین صبح ہوگی'),
(3966, 37, 178, 'اور آپ تھوڑے دنوں ان سے منہ پھیرلیں'),
(3967, 37, 179, 'اور دیکھتے رہیں عنقریب یہ خود دیکھ لیں گے'),
(3968, 37, 180, 'آپ کا پروردگار جو مالک عزت بھی ہے ان کے بیانات سے پاک و پاکیزہ ہے'),
(3969, 37, 181, 'اور ہمارا سلام تمام مرسلین پر ہے'),
(3970, 37, 182, 'اور ساری تعریف اس اللہ کے لئے ہے جو عالمین کا پروردگار ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'ص ۤ نصیحت والے قرآن کی قسم'),
(3972, 38, 2, 'حقیقت یہ ہے کہ یہ کفاّر غرور اور اختلاف میں پڑے ہوئے ہیں'),
(3973, 38, 3, 'ہم نے ان سے پہلے کتنی نسلوں کو تباہ کردیا ہے پھر انہوں نے فریاد کی لیکن کوئی چھٹکارا ممکن نہیں تھا'),
(3974, 38, 4, 'اور انہیں تعجب ہے کہ ان ہی میں سے کوئی ڈرانے والا کیسے آگیا اور کافروں نے صاف کہہ دیا کہ یہ تو جادوگر اور جھوٹا ہے'),
(3975, 38, 5, 'کیا اس نے سارے خداؤں کو جوڑ کر ایک خدا بنادیا ہے یہ تو انتہائی تعجب خیز بات ہے'),
(3976, 38, 6, 'اور ان میں سے ایک گروہ یہ کہہ کر چل دیا چلو اپنے خداؤں پر قائم رہو کہ اس میں ان کی کوئی غرض پائی جاتی ہے'),
(3977, 38, 7, 'ہم نے تو اگلے دور کی امتوّں میں یہ باتیں نہیں سنی ہیں اور یہ کوئی خود ساختہ بات معلوم ہوتی ہے'),
(3978, 38, 8, 'کیا ہم سب کے درمیان تنہا ا ن ہی پر کتاب نازل ہوگئی ہے حقیقت یہ ہے کہ انہیں ہماری کتاب میں شک ہے بلکہ اصل یہ ہے کہ ابھی انہوں نے عذاب کا مزہ ہی نہیں چکھا ہے'),
(3979, 38, 9, 'کیا ان کے پاس آپ کے صاحبِ عزت و عطا پروردگار کی رحمت کا کوئی خزانہ ہے'),
(3980, 38, 10, 'یا ان کے پاس زمین و آسمان اور اس کے مابین کا اختیار ہے تو یہ سیڑھی لگا کر آسمان پر چڑھ جائیں'),
(3981, 38, 11, 'تمام گروہوں میں سے ایک گروہ یہاں بھی شکست کھانے والا ہے'),
(3982, 38, 12, 'اس سے پہلے قوم نوح علیھ السّلام قوم عاد علیھ السّلام اور میخوں والا فرعون سب گزر چکے ہیں'),
(3983, 38, 13, 'اور ثمود, قوم لوط علیھ السّلام, جنگل والے لوگ یہ سب گروہ گزر چکے ہیں'),
(3984, 38, 14, 'ان میں سے ہر ایک نے رسول کی تکذیب کی تو ان پر ہمارا عذاب ثابت ہوگیا'),
(3985, 38, 15, 'یہ صرف اس بات کا انتظار کررہے ہیں کہ ایک ایسی چنگھاڑ بلند ہوجائے جس سے ادنیٰ مہلت بھی نہ مل سکے'),
(3986, 38, 16, 'اور یہ کہتے ہیں کہ پروردگار ہمارا قسمت کا لکھا ہوا روزِ حساب سے پہلے ہی ہمیں دیدے'),
(3987, 38, 17, 'آپ ان کی باتوں پر صبر کریں اور ہمارے بندے داؤد علیھ السّلام کو یاد کریں جو صاحب طاقت بھی تھے اور بیحد رجوع کرنے والے بھی تھے'),
(3988, 38, 18, 'ہم نے ان کے لئے پہاڑوں کو لَسّخر کردیا تھا کہ ان کے ساتھ صبح و شام تسبیح پروردگار کریں'),
(3989, 38, 19, 'اور پرندوں کو ان کے گرد جمع کردیا تھا سب ان کے فرمانبردار تھے'),
(3990, 38, 20, 'اور ہم نے ان کے ملک کو مضبوط بنادیا تھا اور انہیں حکمت اور صحیح فیصلہ کی قوت عطا کردی تھی'),
(3991, 38, 21, 'اور کیا آپ کے پاس ان جھگڑا کرنے والوں کی خبر آئی ہے جو محراب کی دیوار پھاند کر آگئے تھے'),
(3992, 38, 22, 'کہ جب وہ داؤد علیھ السّلام کے سامنے حاضر ہوئے تو انہوں نے خوف محسوس کیا اور ان لوگوں نے کہا کہ آپ ڈریں نہیں ہم دو فریق ہیں جس میں ایک نے دوسرے پر ظلم کیا ہے آپ حق کے ساتھ فیصلہ کردیں اور ناانصافی نہ کریں اور ہمیں سیدھے راستہ کی ہدایت کردیں'),
(3993, 38, 23, 'یہ ہمارا بھائی ہے اس کے پاس ننانوے رَنبیاں ہیں اور میرے پاس صرف ایک ہے یہ کہتا ہے کہ وہ بھی میرے حوالے کردے اور اس بات میں سختی سے کام لیتا ہے'),
(3994, 38, 24, 'داؤد علیھ السّلام نے کہا کہ اس نے تمہاری رَنبی کا سوال کرکے تم پر ظلم کیاہے اور بہت سے شرکائ ایسے ہی ہیں کہ ان میں سے ایک دوسرے پر ظلم کرتا ہے علاوہ ان لوگوں کے جو صاحبان هایمان و عمل صالح ہیں اور وہ بہت کم ہیں .... اور داؤد علیھ السّلام نے یہ خیال کیا کہ ہم نے ان کا امتحان لیا ہے لہٰذا انہوں نے اپنے رب سے استغفار کیا اور سجدہ میں گر پڑے اور ہماری طرف سراپا توجہ بن گئے'),
(3995, 38, 25, 'تو ہم نے اس بات کو معاف کردیا اور ہمارے نزدیک ان کے لئے تقرب اور بہترین بازگشت ہے'),
(3996, 38, 26, 'اے داؤد علیھ السّلامہم نے تم کو زمین میں اپنا جانشین بنایا ہے لہٰذا تم لوگوں کے درمیان حق کے ساتھ فیصلہ کرو اور خواہشات کا اتباع نہ کرو کہ وہ راسِ خدا سے منحرف کردیں بیشک جو لوگ راہ هخدا سے بھٹک جاتے ہیں ان کے لئے شدید عذاب ہے کہ انہوں نے روزِ حساب کو یکسر نظرانداز کردیا ہے'),
(3997, 38, 27, 'اور ہم نے آسمان اور زمین اور اس کے درمیان کی مخلوقات کو بیکار نہیں پیدا کیا ہے یہ تو صرف کافروں کا خیال ہے اور کافروں کے لئے جہّنم میں ویل کی منزل ہے'),
(3998, 38, 28, 'کیا ہم ایمان لانے والے اور نیک عمل کرنے والوں کو زمین میں فساد برپا کرنے والوں جیسا قرار دیدیں یا صاحبانِ تقویٰ کو فاسق و فاجر افراد جیسا قرار دیدیں'),
(3999, 38, 29, 'یہ ایک مبارک کتاب ہے جسے ہم نے آپ کی طرف نازل کیا ہے تاکہ یہ لوگ اس کی آیتوں میں غور و فکر کریں اور صاحبانِ عقل نصیحت حاصل کریں'),
(4000, 38, 30, 'اور ہم نے داؤد علیھ السّلام کو سلیمان علیھ السّلام جیسا فرزند عطا کیا جو بہترین بندہ اور ہماری طرف رجوع کرنے والا تھا'),
(4001, 38, 31, 'جب ان کے سامنے شام کے وقت بہترین اصیل گھوڑے پیش کئے گئے'),
(4002, 38, 32, 'تو انہوں نے کہا کہ میں ذکر خدا کی بنا پر خیر کو دوست رکھتا ہوں یہاں تک کہ وہ گھوڑے دوڑتے دوڑتے نگاہوں سے اوجھل ہوگئے'),
(4003, 38, 33, 'تو انہوں نے کہا کہ اب انہیں واپس پلٹاؤ اس کے بعد ان کی پنڈلیوں اور گردنوں کو ملنا شروع کردیا'),
(4004, 38, 34, 'اور ہم نے سلیمان علیھ السّلام کا امتحان لیا جب ان کی کرسی پر ایک بے جان جسم کو ڈال دیا تو پھر انہوں نے خدا کی طرف توجہ کی'),
(4005, 38, 35, 'اور کہا کہ پروردگار مجھے معاف فرما اور ایک ایسا ملک عطا فرما جو میرے بعد کسی کے لئے سزاوار نہ ہو کہ تو بہترین عطا کرنے والا ہے'),
(4006, 38, 36, 'تو ہم نے ہواؤں کو لَسخّر کردیا کہ ان ہی کے حکم سے جہاں جانا چاہتے تھے نرم رفتار سے چلتی تھیں'),
(4007, 38, 37, 'اور شیاطین میں سے تمام معماروں اور غوطہ خوروں کو تابع بنادیا'),
(4008, 38, 38, 'اور ان شیاطین کو بھی جو سرکشی کی بنا پر زنجیروں میں جکڑے ہوئے تھے'),
(4009, 38, 39, 'یہ سب میری عطا ہے اب چاہے لوگوں کو دیدو یا اپنے پاس رکھو تم سے حساب نہ ہوگا اور ان کے لئے ہمارے یہاں تقرب کا درجہ ہے اور بہترین بازگشت ہے'),
(4010, 38, 40, 'یہ سب میری عطا ہے اب چاہے لوگوں کو دیدو یا اپنے پاس رکھو تم سے حساب نہ ہوگا اور ان کے لئے ہمارے یہاں تقرب کا درجہ ہے اور بہترین بازگشت ہے'),
(4011, 38, 41, 'اور ہمارے بندے ایوب علیھ السّلام کو یاد کرو جب انہوں نے اپنے پروردگار کو پکارا کہ شیطان نے مجھے بڑی تکلیف اور اذیت پہنچائی ہے'),
(4012, 38, 42, 'تو ہم نے کہا کہ زمین پر پیروں کو رگڑو دیکھو یہ نہانے اور پینے کے لئے بہترین ٹھنڈا پانی ہے'),
(4013, 38, 43, 'اور ہم نے انہیں ان کے اہل و عیال عطا کردیئے اور اتنے ہی اور بھی دیدئے یہ ہماری رحمت اور صاحبانِ عقل کے لئے عبرت و نصیحت ہے'),
(4014, 38, 44, 'اور ایوب علیھ السّلام تم اپنے ہاتھوں میں سینکوں کا مٹھا لے کر اس سے مار دو اور قسم کی خلاف ورزی نہ کرو - ہم نے ایوب علیھ السّلامکو صابر پایا ہے - وہ بہترین بندہ اور ہماری طرف رجوع کرنے والا ہے'),
(4015, 38, 45, 'اور پیغمبر علیھ السّلام ہمارے بندے ابراہیم علیھ السّلام, اسحاق علیھ السّلام اور یعقوب علیھ السّلام کا ذکر کیجئے جو صاحبانِ قوت اور صاحبانِ بصیرت تھے'),
(4016, 38, 46, 'ہم نے ان کو آخرت کی یاد کی صفت سے ممتاز قرار دیا تھا'),
(4017, 38, 47, 'اور وہ ہمارے نزدیک منتخب اور نیک بندوں میں سے تھے'),
(4018, 38, 48, 'اور اسماعیل علیھ السّلاماور الیسع علیھ السّلام اور ذوالکفل علیھ السّلام کو بھی یاد کیجئے اور یہ سب نیک بندے تھے'),
(4019, 38, 49, 'یہ ایک نصیحت ہے اور صاحبانِ تقویٰ کے لئے بہترین بازگشت ہے'),
(4020, 38, 50, 'ہمیشگی کی جنتیں جن کے دروازے ان کے لئے کھلے ہوئے ہوں گے'),
(4021, 38, 51, 'وہاں تکیہ لگائے چین سے بیٹھے ہوں گے اور طرح طرح کے میوے اور شراب طلب کریں گے'),
(4022, 38, 52, 'اور ان کے پہلو میں نیچی نظر والی ہمسن بیبیاں ہوں گی'),
(4023, 38, 53, 'یہ وہ چیزیں ہیں جن کا روز قیامت کے لئے تم سے وعدہ کیا گیا ہے'),
(4024, 38, 54, 'یہ ہمارا رزق ہے جو ختم ہونے والا نہیں ہے'),
(4025, 38, 55, 'یہ ایک طرف ہے اور سرکشوں کے لئے بدترین بازگشت ہے'),
(4026, 38, 56, 'جہّنم ہے جس میں یہ وارد ہوں گے اور وہ بدترین ٹھکانا ہے'),
(4027, 38, 57, 'یہ ہے عذاب اس کا مزہ چکھیں گرم پانی ہے اور پیپ'),
(4028, 38, 58, 'اور اسی قسم کی دوسری چیزیں بھی ہیں'),
(4029, 38, 59, 'یہ تمہاری فوج ہے اسے بھی تمہارے ہمرا ہ جہّنم میں ٹھونس دیا جائے گا خدا ان کا بھلا نہ کرے اور یہ سب جہّنم میں جلنے والے ہیں'),
(4030, 38, 60, 'پھر مرید اپنے پیروں سے کہیں گے تمہارا بھلا نہ ہو تم نے اس عذاب کو ہمارے لئے مہیاّ کیا ہے لہٰذا یہ بدترین ٹھکانا ہے'),
(4031, 38, 61, 'پھر مزید کہیں گے کہ خدایا جس نے ہم کو آگے بڑھایا ہے اس کے عذاب کو جہّنم میں دوگنا کردے'),
(4032, 38, 62, 'پھر خود ہی کہیں گے کہ ہمیں کیا ہوگیا ہے کہ ہم ان لوگوں کو نہیں دیکھتے جنہیں شریر لوگوں میں شمار کرتے تھے'),
(4033, 38, 63, 'ہم نے ناحق ان کا مذاق اڑایا تھا یا اب ہماری نگاہیں ان کی طرف سے پلٹ گئی ہیں'),
(4034, 38, 64, 'یہ اہل جہنمّ کا باہمی جھگڑا ایک امر برحق ہے'),
(4035, 38, 65, 'آپ کہہ دیجئے کہ میں تو صرف ڈرانے والا ہوں اور خدائے واحد و قہار کے علاوہ کوئی دوسرا خدا نہیں ہے'),
(4036, 38, 66, 'وہی آسمان و زمین اور ان کی درمیانی مخلوقات کا پروردگار اور صاحبِ عزت اور بہت زیادہ بخشنے والا ہے'),
(4037, 38, 67, 'کہہ دیجئے کہ یہ قرآن بہت بڑی خبر ہے'),
(4038, 38, 68, 'تم اس سے اعراض کئے ہوئے ہو'),
(4039, 38, 69, 'مجھے کیا علم ہوتا کہ عالم بالا میں کیا بحث ہورہی تھی'),
(4040, 38, 70, 'میری طرف تو صرف یہ وحی آتی ہے کہ میں ایک کھلا ہوا عذاب الہٰی سے ڈرانے والا انسان ہوں'),
(4041, 38, 71, 'انہیں یاد دلائیے جب آپ کے پروردگار نے ملائکہ سے کہا کہ میں گیلی مٹی سے ایک بشر بنانے والا ہوں'),
(4042, 38, 72, 'جب اسے درست کرلوں اور اس میں اپنی روح پھونک دوں تو تم سب سجدہ میں گر پڑنا'),
(4043, 38, 73, 'تو تمام ملائکہ نے سجدہ کرلیا'),
(4044, 38, 74, 'علاوہ ابلیس کے کہ وہ اکڑ گیا اور کافروں میں ہوگیا'),
(4045, 38, 75, 'تو خدا نے کہا اے ابلیس تیرے لئے کیا شے مانع ہوئی کہ تو اسے سجدہ کرے جسے میں نے اپنے دست قدرت سے بنایا ہے تو نے غرور اختیار کیا یا تو واقعا بلند لوگوں میں سے ہے'),
(4046, 38, 76, 'اس نے کہا کہ میں ان سے بہتر ہوں تو نے مجھے آگ سے پیدا کیا ہے اور انہیں خاک سے پیدا کیا ہے'),
(4047, 38, 77, 'ارشاد ہوا کہ یہاں سے نکل جا تو مررَود ہے'),
(4048, 38, 78, 'اور یقینا تیرے اوپر قیامت کے دن تک میری لعنت ہے'),
(4049, 38, 79, 'اس نے کہا پروردگار مجھے روز قیامت تک کی مہلت بھی دیدے'),
(4050, 38, 80, 'ارشاد ہوا کہ تجھے مہلت دیدی گئی ہے'),
(4051, 38, 81, 'مگر ایک معّین وقت کے دن تک'),
(4052, 38, 82, 'اس نے کہا تو پھر تیری عزّت کی قسم میں سب کو گمراہ کروں گا'),
(4053, 38, 83, 'علاوہ تیرے ان بندوں کے جنہیں تو نے خالص بنالیا ہے'),
(4054, 38, 84, 'ارشاد ہوا تو پھر حق یہ ہے اور میں تو حق ہی کہتا ہوں'),
(4055, 38, 85, 'کہ میں جہنمّ کو تجھ سے اور تیرے پیروکاروں سے بھر دوں گا'),
(4056, 38, 86, 'اور پیغمبر آپ کہہ دیجئے کہ میں اپنی تبلیغ کا کوئی اجر نہیں چاہتا اور نہ میں بناوٹ کرنے والا غلط بیان ہوں'),
(4057, 38, 87, 'یہ قرآن تو عالمین کے لئے ایک نصیحت ہے'),
(4058, 38, 88, 'اور کچھ دنوں کے بعد تم سب کو اس کی حقیقت معلوم ہوجائے گی');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'یہ صاحبِ عزت و حکمت خدا کی نازل کی ہوئی کتاب ہے'),
(4060, 39, 2, 'ہم نے آپ کی طرف اس کتاب کو حق کے ساتھ نازل کیا ہے لہٰذا آپ مکمل اخلاص کے ساتھ خدا کی عبادت کریں'),
(4061, 39, 3, 'آگاہ ہوجاؤ کہ خالص بندگی صرف اللہ کے لئے ہے اور جن لوگوں نے اس کے علاوہ سرپرست بنائے ہیں یہ کہہ کر کہ ہم ان کی پرستش صرف اس لئے کرتے ہیں کہ یہ ہمیں اللہ سے قریب کردیں گے - اللہ ان کے درمیان تمام اختلافی مسائل میں فیصلہ کردے گا کہ اللہ کسی بھی جھوٹے اور ناشکری کرنے والے کو ہدایت نہیں دیتا ہے'),
(4062, 39, 4, 'اور وہ اگر چاہتا کہ اپنا فرزند بنائے تو اپنی مخلوقات میں جسے چاہتا اس کا انتخاب کرلیتا وہ پاک و بے نیاز ہے اور وہی خدائے یکتا اور قہار ہے'),
(4063, 39, 5, 'اس نے آسمان و زمین کو حق کے ساتھ پیدا کیا ہے وہ رات کو دن پر لپیٹ دیتا ہے اور اس نے آفتاب اور ماہتاب کو تابع بنادیا ہے سب ایک مقرّرہ مدّت تک چلتے رہیں گے آگاہ ہوجاؤ وہ سب پر غالب اور بہت بخشنے والا ہے'),
(4064, 39, 6, 'اس نے تم سب کو ایک ہی نفس سے پیدا کیا ہے اور پھر اسی سے اس کا جوڑا قرار دیا ہے اور تمہارے لئے آٹھ قسم کے چوپائے نازل کئے ہیں. وہ تم کو تمہاری ماؤں کے شکم میں تخلیق کی مختلف منزلوں سے گزارتا ہے اور یہ سب تین تاریکیوں میں ہوتا ہے - وہی اللہ تمہارا پروردگار ہے اسی کے قبضہ میں ملک ہے اس کے علاوہ کوئی خدا نہیں ہے پھر تم کدھر پھرے جارہے ہو'),
(4065, 39, 7, 'اگر تم کافر بھی ہوجاؤ گے تو خدا تم سے بے نیاز ہے اور وہ اپنے بندوں کے لئے کفر کو پسند نہیں کرتا ہے اور اگر تم اس کا شکریہ ادا کرو گے تو وہ اس بات کو پسند کرتا ہے اور کوئی شخص دوسرے کے گناہوں کا بوجھ اٹھانے والا نہیں ہے اس کے بعد تم سب کی بازگشت تمہارے پروردگار کی طرف ہے پھر وہ تمہیں بتائے گا کہ تم دنیا میں کیا کررہے تھے وہ دلوں کے حُھپے ہوئے رازوں سے بھی باخبر ہے'),
(4066, 39, 8, 'اور جب انسان کو کوئی تکلیف پہنچتی ہے تو پوری توجہ کے ساتھ پروردگار کو آواز دیتا ہے پھر جب وہ اسے کوئی نعمت دے دیتا ہے تو جس بات کے لئے اس کو پکار رہا تھا اسے یکسر نظرانداز کردیتا ہے اور خدا کے لئے مثل قرار دیتا ہے تاکہ اس کے راستے سے بہکا سکے تو آپ کہہ دیجئے کہ تھوڑے دنوں اپنے کفر میں عیش کرلو اس کے بعد تو تم یقینا جہّنم والوں میں ہو'),
(4067, 39, 9, 'کیا وہ شخص جو رات کی گھڑیوں میں سجدہ اور قیام کی حالت میں خدا کی عبادت کرتا ہے اور آخرت کا خوف رکھتا ہے اور اپنے پروردگار کی رحمت کا امیدوار ہے .... کہہ دیجئے کہ کیا وہ لوگ جو جانتے ہیں ان کے برابر ہوجائیں گے جو نہیں جانتے ہیں - اس بات سے نصیحت صرف صاحبانِ عقل حاصل کرتے ہیں'),
(4068, 39, 10, 'کہہ دیجئے کہ اے میرے ایماندار بندو! اپنے پروردگار سے ڈرو . جو لوگ اس دار دنیا میں نیکی کرتے ہیں ان کے لئے نیکی ہے اور اللہ کی زمین بہت وسیع ہے بس صبر کرنے والے ہی وہ ہیں جن کو بے حساب اجر دیا جاتا ہے'),
(4069, 39, 11, 'کہہ دیجئے کہ مجھے حکم دیا گیا ہے کہ میں اخلاص عبادت کے ساتھ اللہ کی عبادت کروں'),
(4070, 39, 12, 'اور مجھے حکم دیا گیا ہے کہ میں سب سے پہلا اطاعت گزار بن جاؤں'),
(4071, 39, 13, 'کہہ دیجئے کہ میں گناہ کروں تو مجھے بڑے سخت دن کے عذاب کا خوف ہے'),
(4072, 39, 14, 'کہہ دیجئے کہ میں صرف اللہ کی عبادت کرتا ہوں اور اپنی عبادت میں مخلص ہوں'),
(4073, 39, 15, 'اب تم جس کی چاہو عبادت کرو کہہ دیجئے کہ حقیقی خسارہ والے وہی ہیں جنہوں نے اپنے نفس اور اپنے اہل کو قیامت کے دن گھاٹے میں رکھا - آگاہ ہوجاؤ یہی کھلا ہوا خسارہ ہے'),
(4074, 39, 16, 'ان کے لئے اوپر سے جہنمّ کی آگ کے اوڑھنے ہوں گے اور نیچے سے بچھونے - یہی وہ بات ہے جس سے خدا اپنے بندوں کو ڈراتا ہے تو اے میرے بندو مجھ سے ڈرو'),
(4075, 39, 17, 'اور جن لوگوں نے ظالموں سے علیحدگی اختیار کی کہ ان کی عبادت کریں اور خدا کی طرف متوجہ ہوگئے ان کے لئے ہماری طرف سے بشارت ہے لہذا پیغمبر آپ میرے بندوں کو بشارت دے دیجئے'),
(4076, 39, 18, 'جو باتوں کو سنتے ہیں اور جو بات اچھی ہوتی ہے اس کا اتباع کرتے ہیں یہی وہ لوگ ہیں جنہیں خدا نے ہدایت دی ہے اور یہی وہ لوگ ہیں جو صاحبانِ عقل ہیں'),
(4077, 39, 19, 'کیا جس شخص پر کلمہ عذاب ثابت ہوجائے اور کیا جو شخص جہنمّ میں چلا ہی جائے آپ اسے نکال سکتے ہیں'),
(4078, 39, 20, 'البتہ جن لوگوں نے اپنے پروردگار کا خوف پیدا کیا ان کے لئے جنّت کے غرفے ہیں اور ان کے غرفوں پر مزید غرفے ہیں جن کے نیچے نہریں جاری ہوں گی - یہ خدا کا وعدہ ہے اور خدا اپنے وعدہ کے خلاف نہیں کرتا ہے'),
(4079, 39, 21, 'کیا تم نے نہیں دیکھا کہ خدا نے آسمان سے پانی نازل کیا ہے پھر اسے مختلف چشموں میں جاری کردیا ہے پھر اس کے ذریعہ مختلف رنگ کی زراعت پیدا کرتا ہے پھر وہ کھیتی سوکھ جاتی ہے تو اسے زرد رنگ میں دیکھتے ہو پھر اسے بھوسا بنادیتا ہے ان تمام باتوں میں صاحبانِ عقل کے لئے یاددہانی اور نصیحت کا سامان پایا جاتا ہے'),
(4080, 39, 22, 'کیا وہ شخص جس کے دل کو خدا نے اسلام کے لئے کشادہ کردیا ہے تو وہ اپنے پروردگار کی طرف سے نورانیت کا حامل ہے گمراہوں جیسا ہوسکتا ہے - افسوس ان لوگوں کے حال پر جن کے دل ذکر خدا کے لئے سخت ہوگئے ہیں تو وہ کھلی ہوئی گمراہی میں مبتلا ہیں'),
(4081, 39, 23, 'اللہ نے بہترین کلام اس کتاب کی شکل میں نازل کیا ہے جس کی آیتیں آپس میں ملتی جلتی ہیں اور بار بار دہرائی گئی ہیں کہ ان سے خوف خدا رکھنے والوں کے رونگٹے کھڑے ہوجاتے ہیں اس کے بعد ان کے جسم اور دل یاد خدا کے لئے نرم ہوجاتے ہیں یہی اللہ کی واقعی ہدایت ہے وہ جس کو چاہتا ہے عطا فرمادیتا ہے اور جس کو وہ گمراہی میں چھوڑ دے اس کا کوئی ہدایت کرنے والا نہیں ہے'),
(4082, 39, 24, 'کیا وہ شخص جو روز هقیامت بدترین عذاب کا بچاؤ اپنے چہرہ سے کرنے والا ہے نجات پانے والے کے برابر ہوسکتا ہے اور ظالمین سے تو یہی کہا جائے گا کہ اپنے کرتوت کا مزہ چکھو'),
(4083, 39, 25, 'اور ان کّفار سے پہلے والوں نے بھی رسولوں علیھ السّلام کو جھٹلایا تو ان پر اس طرح سے عذاب وارد ہوگیا کہ انہیں اس کا شعور بھی نہیں تھا'),
(4084, 39, 26, 'پھر خدا نے انہیں زندگی دنیا میں ذلّت کا مزہ چکھایا اور آخرت کا عذاب تو بہرحال بہت بڑا ہے اگر انہیں معلوم ہوسکے'),
(4085, 39, 27, 'اور ہم نے لوگوں کے لئے اس قرآن میں ہر طرح کی مثال بیان کردی ہے کہ شاید یہ عبرت اور نصیحت حاصل کرسکیں'),
(4086, 39, 28, 'یہ عربی زبان کا قرآن ہے جس میں کسی طرح کی کجی نہیں ہے شاید یہ لوگ اسی طرح تقویٰ اختیار کرلیں'),
(4087, 39, 29, 'اللہ نے اس شخص کی مثال بیان کی ہے جس میں بہت سے جھگڑا کرنے والے شرکائ ہوں اور وہ شخص جو ایک ہی شخص کے سپرد ہوجائے کیا دونوں حالات کے اعتبار سے ایک جیسے ہوسکتے ہیں ساری تعریف اللہ کے لئے ہے مگر ان کی اکثریت سمجھتی ہی نہیں ہے'),
(4088, 39, 30, 'پیغمبر آپ کو بھی موت آنے والی ہے اور یہ سب مرجانے والے ہیں'),
(4089, 39, 31, 'اس کے بعد تم سب روز هقیامت پروردگار کی بارگاہ میں اپنے جھگڑے پیش کرو گے'),
(4090, 39, 32, 'تو اس سے بڑا ظالم کون ہے جو خدا پر بہتان باندھے اور صداقت کے آجانے کے بعد اس کی تکذیب کرے تو کیاجہّنم میں کافرین کا ٹھکانا نہیں ہے'),
(4091, 39, 33, 'اور جو شخص صداقت کا پیغام لے کر آیا اور جس نے اس کی تصدیق کی یہی لوگ درحقیقت صاحبانِ تقویٰ اور پرہیزگار ہیں'),
(4092, 39, 34, 'ان کے لئے پروردگار کے یہاں وہ سب کچھ ہے جو وہ چاہتے ہیں اور یہی نیک عمل والوں کی جزا ہے'),
(4093, 39, 35, 'تاکہ خدا ان برائیوں کو دور کردے جو ان سے سرزد ہوئی ہیں اور ان کا اجر ان کے اعمال سے بہتر طور پر عطا کرے'),
(4094, 39, 36, 'کیا خدا اپنے بندوں کے لئے کافی نہیں ہے اور یہ لوگ آپ کو اس کے علاوہ دوسروں سے ڈراتے ہیں حالانکہ جس کو خدا گمراہی میں چھوڑ دے اس کا کوئی ہدایت کرنے والا نہیں ہے'),
(4095, 39, 37, 'اور جس کو وہ ہدایت دیدے اس کا کوئی گمراہ کرنے والا نہیں ہے کیا خدا سب سے زیادہ زبردست انتقام لینے والا نہیں ہے'),
(4096, 39, 38, 'اور اگر آپ ان سے سوال کریں گے کہ زمین و آسمان کو کس نے پیدا کیا ہے تو کہیں گے کہ اللہ ...._ تو کہہ دیجئے کہ کیا تم نے ان سب کا حال دیکھا ہے جن کی عبادت کرتے ہو کہ اگر خدا نقصان پہنچانے کا ارادہ کرلے تو کیا یہ اس نقصان کو روک سکتے ہیں یا اگر وہ رحمت کا ارادہ کرلے تو کیا یہ اس رحمت کو منع کرسکتے ہیں - آپ کہہ دیجئے کہ میرے لئے میرا خدا کافی ہے اور بھروسہ کرنے والے اسی پر بھروسہ کرتے ہیں'),
(4097, 39, 39, 'اور کہہ دیجئے کہ قوم والو تم اپنی جگہ پر عمل کرو اور میں اپنا عمل کررہا ہوں اس کے بعد عنقریب تمہیں سب کا حال معلوم ہوجائے گا'),
(4098, 39, 40, 'کہ کس کے پاس رسوا کرنے والا عذاب آتا ہے اور کس پر ہمیشہ رہنے والا عذاب نازل ہوتا ہے'),
(4099, 39, 41, 'ہم نے اس کتاب کو آپ کے پاس لوگوں کی ہدایت کے لئے حق کے ساتھ نازل کیا ہے اب جو ہدایت حاصل کرلے گا وہ اپنے فائدہ کے لئے کرے گا اور جو گمراہ ہوجائے گا وہ بھی اپنا ہی نقصان کرے گا اور آپ ان کے ذمہ دار نہیں ہیں'),
(4100, 39, 42, 'اللہ ہی ہے جو روحوں کو موت کے وقت اپنی طرف بلالیتا ہے اور جو نہیں مرتے ہیں ان کی روحوں کو بھی نیند کے وقت طلب کرلیتا ہے اور پھر جس کی موت کا فیصلہ کرلیتا ہے اس کی روح کو روک لیتا ہے اور دوسری روحوں کو ایک مقررہ مدّت کے لئے آزاد کردیتا ہے - اس بات میں صاحبان هفکر و نظر کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(4101, 39, 43, 'کیا ان لوگوں نے خدا کو چھوڑ کر سفارش کرنے والے اختیار کرلئے ہیں تو آپ کہہ دیجئے کہ ایسا کیوں ہے چاہے یہ لوگ کوئی اختیار نہ رکھتے ہوں اور کسی طرح کی بھی عقل نہ رکھتے ہوں'),
(4102, 39, 44, 'کہہ دیجئے کہ شفاعت کا تمام تراختیار اللہ کے ہاتھوں میں ہے اسی کے پاس زمین و آسمان کا سارا اقتدار ہے اور اس کے بعد تم بھی اسی کی بارگاہ میں پلٹائے جاؤ گے'),
(4103, 39, 45, 'اور جب ان کے سامنے خدائے یکتا کا ذکر آتا ہے تو جن کا ایمان آخرت پر نہیں ہے ان کے دل متنفر ہوجاتے ہیں اور جب اس کے علاوہ کسی اور کا ذکر آتا ہے تو خوش ہوجاتے ہیں'),
(4104, 39, 46, 'اب آپ کہئے کہ اے پروردگار اے زمین و آسمان کے خلق کرنے والے اور حاضر و غائب کے جاننے والے تو ہی اپنے بندوں کے درمیان ان مسائل کا فیصلہ کرسکتا ہے جن میں یہ آپس میں اختلاف کررہے ہیں'),
(4105, 39, 47, 'اور اگر ظلم کرنے والوں کو زمین کی تمام کائنات مل جائے اور اتنا ہی اور بھی مل جائے تو بھی یہ روزِ قیامت کے بدترین عذاب کے بدلہ میں سب دے دیں گے لیکن ان کے لئے خدا کی طرف سے وہ سب بہرحال ظاہر ہوگا جس کا یہ وہم و گمان بھی نہیں رکھتے تھے'),
(4106, 39, 48, 'اور ان کی ساری بدکرداریاں ان پر واضح ہوجائیں گی اور انہیں وہی بات اپنے گھیرے میں لے لے گی جس کا یہ مذاق اڑایا کرتے تھے'),
(4107, 39, 49, 'پھر جب انسان کو کوئی تکلیف پہنچتی ہے تو ہمیں پکارتا ہے اور اس کے بعد جب ہم کوئی نعمت دیدیتے ہیں تو کہتا ہے کہ یہ تو مجھے میرے علم کے زور پر دی گئی ہے حالانکہ یہ ایک آزمائش ہے اور اکثر لوگ اس کا علم نہیں رکھتے ہیں'),
(4108, 39, 50, 'ان سے پہلے والوں نے بھی یہی کہا تھا تو وہ کچھ ان کے کام نہیں آیا جسے وہ حاصل کررہے تھے'),
(4109, 39, 51, 'بلکہ ان کے اعمال کے برے اثرات ان تک پہنچ گئے اور ان کفاّر میں سے بھی جو لوگ ظلم کرنے والے ہیں ان تک ان کے اعمال کے اِرے اثرت پہنچیں گے اور وہ خدا کو عاجز نہیں کرسکتے ہیں'),
(4110, 39, 52, 'کیا انہیں یہ نہیں معلوم ہے کہ اللہ ہی جس کے رزق کو چاہتا ہے وسیع کردیتا ہے اور جس کے رزق کو چاہتا ہے تنگ کردیتا ہے. ان معاملات میں صاحبان ه ایمان کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(4111, 39, 53, 'پیغمبر آپ پیغام پہنچادیجئے کہ اے میرے بندو جنہوں نے اپنے نفس پر زیادتی کی ہے رحمت خدا سے مایوس نہ ہونا اللہ تمام گناہوں کا معاف کرنے والا ہے اور وہ یقینا بہت زیادہ بخشنے والا اور مہربان ہے'),
(4112, 39, 54, 'اور تم سب اپنے پروردگار کی طرف رجوع کرو اور اس کے لئے سراپا تسلیم ہوجاؤ قبل اس کے کہ تم تک عذاب آجائے تو پھر تمہاری مدد نہیں کی جاسکتی ہے'),
(4113, 39, 55, 'اور تمہارے رب کی طرف سے جو بہترین قانون نازل کیا گیا ہے اس کا اتباع کرو قبل اس کے کہ تم تک اچانک عذاب آجائے اور تمہیں اس کا شعور بھی نہ ہو'),
(4114, 39, 56, 'پھر تم میں سے کوئی نفس یہ کہنے لگے کہ ہائے افسوس کہ میں نے خدا کے حق میں بڑی کوتاہی کی ہے اور میں مذاق اڑانے والوں میں سے تھا'),
(4115, 39, 57, 'یا یہ کہنے لگے کہ اگر خدا مجھے ہدایت دے دیتا تو میں بھی صاحبانِ تقویٰ میں سے ہوجاتا'),
(4116, 39, 58, 'یا عذاب کے دیکھنے کے بعد یہ کہنے لگے کہ اگر مجھے دوبارہ واپس جانے کا موقع مل جائے تو میں نیک کردار لوگوں میں سے ہوجاؤں گا'),
(4117, 39, 59, 'ہاں ہاں تیرے پاس میری آیتیں آئی تھیں تو تو نے انہیں جھٹلادیا اور تکبر سے کام لیا اور کافروں میں سے ہوگیا'),
(4118, 39, 60, 'اور تم روزِ قیامت دیکھو گے کہ جن لوگوں نے اللہ پر بہتان باندھا ہے ان کے چہرے سیاہ ہوگئے ہیں اور کیا جہّنم میں تکبر کرنے والوں کا ٹھکانا نہیں ہے'),
(4119, 39, 61, 'اور خدا صاحبانِ تقویٰ کو ان کی کامیابی کے سبب نجات دے دے گاکہ کوئی برائی انہیں چھو بھی نہ سکے گی اور نہ انہیں کوئی رنج لاحق ہوگا'),
(4120, 39, 62, 'اللہ ہی ہر شے کا خالق ہے اور وہی ہر چیز کی نگرانی کرنے والا ہے'),
(4121, 39, 63, 'زمین و آسمان کی تمام کنجیاں اسی کے پاس ہیں اور جن لوگوں نے اس کی آیتوں کا انکار کیا وہی خسارہ میں رہنے والے ہیں'),
(4122, 39, 64, 'آپ کہہ دیجئے کہ اے جاہلو کیا تم مجھے اس بات کا حکم دیتے ہو کہ میں غیر خدا کی عبادت کرنے لگوں'),
(4123, 39, 65, 'اور یقینا تمہاری طرف اور تم سے پہلے والوں کی طرف یہی وحی کی گئی ہے کہ اگر تم شرک اختیار کرو گے تو تمہارے تمام اعمال برباد کردیئے جائیں گے اور تمہارا شمار گھاٹے والوں میں ہوجائے گا'),
(4124, 39, 66, 'تم صرف اللہ کی عبادت کرو اور اس کے شکر گزار بندوں میں ہوجاؤ'),
(4125, 39, 67, 'اور ان لوگوں نے واقعا اللہ کی قدر نہیں کی ہے جب کہ روزِ قیامت تمام زمین اسی کی مٹھی میں ہوگی اور سارے آسمان اسی کے ہاتھ میں لپٹے ہوئے ہوں گے وہ پاک و بے نیاز ہے اور جن چیزوں کو یہ اس کا شریک بناتے ہیں ان سے بلند و بالاتر ہے'),
(4126, 39, 68, 'اور جب صور پھونکا جائے گا تو زمین و آسمان کی تمام مخلوقات بیہوش ہوکر گر پڑیں گی علاوہ ان کے جنہیں خدا بچانا چاہے - اس کے بعد پھر دوبارہ پھونکا جائے گا تو سب کھڑے ہوکر دیکھنے لگیں گے'),
(4127, 39, 69, 'اور زمین اپنے رب کے نور سے جگمگا اٹھے گی اور اعمال کی کتاب رکھ دی جائے گی اور انبیائ علیھ السّلام اور شہدائ کو لایا جائے گا اور ان کے درمیان حق و انصاف کے ساتھ فیصلہ کیا جائے گا اور کسی پر ظلم نہیں کیا جائے گا'),
(4128, 39, 70, 'اور پھر ہر نفس کو اس کے اعمال کا پورا پورا بدلہ دیا جائے گا اور وہ سب کے اعمال سے پورے طور سے باخبر ہے'),
(4129, 39, 71, 'اور کفر اختیار کرنے والوں کو گروہ در گروہ جہّنم کی طرف ہنکایا جائے گا یہاں تک کہ اس کے سامنے پہنچ جائیں گے تو اس کے دروازے کھول دیئے جائیں گے اور اس کے خازن سوال کریں گے کیا تمہارے پاس رسول نہیں آئے تھے جو آیااُ رب کی تلاوت کرتے اور تمہیں آج کے دن کی ملاقات سے ڈراتے تو سب کہیں گے کہ بیشک رسول آئے تھے لیکن کافرین کے حق میں کلمئہ عذاب بہرحال ثابت ہوچکا ہے'),
(4130, 39, 72, 'تو کہا جائے گا کہ اب جہّنم کے دروازوں سے داخل ہوجاؤ اور اسی میں ہمیشہ ہمیشہ رہو کہ تکبّر کرنے والوں کا بہت اِرا ٹھکانا ہوتا ہے'),
(4131, 39, 73, 'اور جن لوگوں نے اپنے رب کا تقویٰ اختیار کیا انہیں جنّت کی طرف گروہ در گروہ لے جایا جائے گا یہاں تک کہ جب اس کے قریب پہنچیں گے اور اس کے دروازے کھول دیئے جائیں گے تو اس کے خزانہ دار کہیں گے کہ تم پر ہمارا سلام ہو تم پاک و پاکیزہ ہو لہٰذا ہمیشہ کے لئے جنت میں داخل ہوجاؤ'),
(4132, 39, 74, 'اور وہ کہیں گے کہ شکر خدا ہے کہ اس نے ہم سے کئے ہوئے اپنے وعدہ کو سچ کر دکھایا ہے اور ہمیں اپنی زمین کا وارث بنادیا ہے کہ جنت میں جہاں چاہیں آرام کریں اور بیشک یہ عمل کرنے والوں کا بہترین اجر ہے'),
(4133, 39, 75, 'اور تم دیکھو گے کہ ملائکہ عرش الہٰی کے گرد گھیرا ڈالے ہوئے اپنے رب کی حمد کی تسبیح کررہے ہیں اور لوگوں کے درمیان حق و انصاف کے ساتھ فیصلہ کردیا جائے گا اور ہر طرف ایک ہی آواز ہوگی کہ الحمدللہ رب العالمین');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حم ۤ'),
(4135, 40, 2, 'یہ خدائے عزیز و علیم کی طرف سے نازل کی ہوئی کتاب ہے'),
(4136, 40, 3, 'وہ گناہوں کا بخشنے والا, توبہ کا قبول کرنے والا, شدید عذاب کرنے والا اور صاحب هفضل و کرم ہے - اس کے علاوہ دوسرا خدا نہیں ہے اور سب کی بازگشت اسی کی طرف ہے'),
(4137, 40, 4, 'اللہ کی نشانیوں میں صرف وہ جھگڑا کرتے ہیں جو کافر ہوگئے ہیں لہٰذا ان کا مختلف شہروں میں چکر لگانا تمہیں دھوکہ میں نہ ڈال دے'),
(4138, 40, 5, 'ان سے پہلے بھی نوح علیھ السّلام کی قوم اور اس کے بعد والے گروہوں نے رسولوں کی تکذیب کی ہے اور ہر امت نے اپنے رسول کے بارے میں یہ ارادہ کیا ہے کہ اسے گرفتار کرلیں اور باطل کا سہارا لے کر جھگڑا کیا ہے کہ حق کو اُ کھاڑ کر پھینک دیں تو ہم نے بھی انہیں اپنی گرفت میں لے لیا تو تم نے دیکھا کہ ہمارا عذاب کیسا تھا'),
(4139, 40, 6, 'اور اسی طرح تمہارے پروردگار کا عذاب کافروں پر ثابت ہوچکا ہے کہ و ہ جہّنم میں جانے والے ہیں'),
(4140, 40, 7, 'جو فرشتے عرش الہٰی کو اٹھائے ہوئے ہیں اور جو اس کے گرد معین ہیں سب حمد خدا کی تسبیح کررہے ہیں اور اسی پر ایمان رکھتے ہیں اور صاحبانِ ایمان کے لئے استغفار کررہے ہیں کہ خدایا تیری رحمت اور تیرا علم ہر شے پر محیط ہے لہذا ان لوگوں کو بخش دے جنہوں نے توبہ کی ہے اور تیرے راستہ کا اتباع کیا ہے اور انہیںجہّنم کے عذاب سے بچالے'),
(4141, 40, 8, 'پروردگار انہیں اور ان کے باپ داداً ازواج اور اولاد میں سے جو نیک اور صالح افراد ہیں ان کو ہمیشہ رہنے والے باغات میں داخل فرما جن کا تونے ان سے وعدہ کیا ہے کہ بیشک تو سب پر غالب اور صاحبِ حکمت ہے'),
(4142, 40, 9, 'اور انہیں برائیوں سے محفوظ فرما کہ آج جن لوگوں کو تو نے برائیوں سے بچالیا گویا ان ہی پر رحم کیا ہے اور یہ بہت بڑی کامیابی ہے'),
(4143, 40, 10, 'بیشک جن لوگوں نے کفر اختیار کیا ان سے روزِ قیامت پکار کر کہا جائے گا کہ تم خود جس قدر اپنی جان سے بیزار ہو خدا کی ناراضگی اس سے کہیں زیادہ بڑی ہے کہ تم کو ایمان کی طرف دعوت دی جاتی تھی اور تم کفر اختیار کرلیتے تھے'),
(4144, 40, 11, 'وہ لوگ کہیں گے پروردگار تو نے ہمیں دو مرتبہ موت دی اور دو مرتبہ زندگی عطا کی تو اب ہم نے اپنے گناہوں کا اقرار کرلیا ہے تو کیا اس سے بچ نکلنے کی کوئی سبیل ہے'),
(4145, 40, 12, 'یہ سب اس لئے ہے کہ جب خدائے واحد کا نام لیا گیا تو تم لوگوں نے کفر اختیار کیا اور جب شرک کی بات کی گئی تو تم نے فورا مان لیا تو اب فیصلہ صرف خدائے بلند و بزرگ کے ہاتھ میں ہے'),
(4146, 40, 13, 'وہی وہ ہے جو تمہیں اپنی نشانیاں دکھلاتا ہے اور تمہارے لئے آسمان سے رزق نازل کرتا ہے اور اس سے وہی نصیحت حاصل کرتا ہے جو اس کی طرف رجوع کرتا ہے'),
(4147, 40, 14, 'لہٰذا تم خالص عبادت کے ساتھ خدا کو پکارو چاہے کافرین کو یہ کتنا ہی ناگوار کیوں نہ ہو'),
(4148, 40, 15, 'وہ خدا بلند درجات کا مالک اور صاحبِ عرش ہے وہ اپنے بندوں میں سے جس پر چاہتا ہے اپنے حکم سے وحی کو نازل کرتا ہے تاکہ ملاقات کے دن سے لوگوں کو ڈرائے'),
(4149, 40, 16, 'جس دن سب نکل کر سامنے آجائیں گے اور خدا پر کوئی بات مخفی نہیں رہ جائے گی - آج کس کا ملک ہے بس خدائے واحد و قہار کا ملک ہے'),
(4150, 40, 17, 'آج ہرنفس کو اس کے کئے کا بدلہ دیا جائے گا اور آج کسی طرح کا ظلم نہ ہوسکے گا بیشک اللہ بہت جلد حساب کرنے والا ہے'),
(4151, 40, 18, 'اور پیغمبر انہیں آنے والے دن کے عذاب سے ڈرائیے جب دم گھٹ گھٹ کر دل منہ کے قریب آجائیں گے اور ظالمین کے لئے نہ کوئی دوست ہوگا اور نہ سفارش کرنے والا جس کی بات سن لی جائے'),
(4152, 40, 19, 'وہ خدا نگاہوں کی خیانت کو بھی جانتا ہے اور دلوں کے حُھپے ہوئے بھیدوں سے بھی باخبر ہے'),
(4153, 40, 20, 'وہ حق کے ساتھ فیصلہ کرتا ہے اور اس کو چھوڑ کر یہ جن کی عبادت کرتے ہیں وہ تو کوئی فیصلہ بھی نہیں کرسکتے ہیں بیشک اللہ سب کی سننے والا اور سب کچھ دیکھنے والا ہے'),
(4154, 40, 21, 'کیا ان لوگوں نے زمین میں سیر نہیں کی کہ دیکھتے کہ ان سے پہلے والوں کا انجام کیا ہوا ہے جو ان سے زیادہ زبردست قوت رکھنے والے تھے اور زمین میں آثار کے مالک تھے پھر خدا نے انہیں ان کے گناہوں کی گرفت میں لے لیا اور اللہ کے مقابلہ میں ان کا کوئی بچانے والا نہیں تھا'),
(4155, 40, 22, 'یہ سب اس لئے ہوا کہ ان کے پاس رسول کِھلی ہوئی نشانیاں لے کر آتے تھے تو انہوں نے انکار کردیا تو پھر خدا نے بھی انہیں اپنی گرفت میں لے لیا کہ وہ بہت قوت والا اور سخت عذاب کرنے والا ہے'),
(4156, 40, 23, 'اور ہم نے موسٰی علیھ السّلام کو اپنی نشانیوں اور روشن دلیل کے ساتھ بھیجا ہے'),
(4157, 40, 24, 'فرعون ًہامان اور قارون کی طرف تو ان سب نے کہہ دیا کہ یہ جادوگر اور جھوٹے ہیں'),
(4158, 40, 25, 'تو پھر اس کے بعد جب وہ ہماری طرف سے حق لے کر آئے تو ان لوگوں نے کہہ دیا کہ جو ان پر ایمان لے آئیں ان کے لڑکوں کو قتل کردو اور لڑکیوں کو زندہ رکھو اور کافروں کا مکر بہرحال بھٹک جانے والا ہوتا ہے'),
(4159, 40, 26, 'اور فرعون نے کہا کہ ذرا مجھے چھوڑ دو میں موسٰی علیھ السّلام کا خاتمہ کردوں اور یہ اپنے رب کو پکاریں - مجھے خوف ہے کہ کہیں یہ تمہارے دین کو بدل نہ دیں اور زمین میں کوئی فساد نہ برپا کردیں'),
(4160, 40, 27, 'اور موسٰی علیھ السّلام نے کہا کہ میں اپنے اور تمہارے پروردگار کی پناہ حاصل کررہا ہوں ہر اس متکبر کے مقابلہ میں جس کا روز حساب پر ایمان نہیں ہے'),
(4161, 40, 28, 'اور فرعون والوں میں سے ایک مرد مومن نے جو اپنے ایمان کو حُھپائے ہوئے تھا یہ کہا کہ کیا تم لوگ کسی شخص کو صرف اس بات پر قتل کررہے ہو کہ وہ کہتا ہے کہ میرا پروردگار اللہ ہے اور وہ تمہارے رب کی طرف سے کھلی ہوئی دلیلیں بھی لے کر آیا ہے اور اگر جھوٹا ہے تو اس کے جھوٹ کا عذاب اس کے سر ہوگا اور اگر سچا نکل آیا تو جن باتوں سے ڈرا رہا ہے وہ مصیبتیں تم پر نازل بھی ہوسکتی ہیں - بیشک اللہ کسی زیادتی کرنے والے اور جھوٹے کی رہنمائی نہیں کرتا ہے'),
(4162, 40, 29, 'میری قوم والو بیشک آج تمہارے پاس حکومت ہے اور زمین پر تمہارا غلبہ ہے لیکن اگر عذاب خدا آگیا تو ہمیں اس سے کون بچائے گا فرعون نے کہا کہ میں تمہیں وہی باتیں بتارہا ہوں جو میں خود سمجھ رہا ہوں اور میں تمہیں عقلمندی کے راستے کے علاوہ اور کسی راہ کی ہدایت نہیں کررہا ہوں'),
(4163, 40, 30, 'اور ایمان لانے والے شخص نے کہا کہ اے قوم میں تمہارے بارے میں اس دن جیسے عذاب کا خطرہ محسوس کررہا ہوں جو دوسری قوموں کے عذاب کا دن تھا'),
(4164, 40, 31, 'قوم نوح, قوم عاد, قوم ثمود اور ان کے بعد والوں جیسا حال اور اللہ یقینا اپنے بندوں پر ظلم کرنا نہیں چاہتا ہے'),
(4165, 40, 32, 'اور اے قوم میں تمہارے بارے میں باہمی فریاد کے دن سے ڈر رہا ہوں'),
(4166, 40, 33, 'جس دن تم سب پیٹھ پھیر کر بھاگو گے اور اللہ کے مقابلہ میں کوئی تمہارا بچانے والا نہ ہوگا اور جس کو خدا گمراہی میں چھوڑ دے اس کا کوئی ہدایت کرنے والا نہیں ہے'),
(4167, 40, 34, 'اور اس سے پہلے یوسف علیھ السّلام بھی تمہارے پاس آئے تھے تو بھی تم ان کے پیغام کے بارے میں شک ہی میں مبتلا رہے یہاں تک کہ جب وہ دنیا سے چلے گئے تو تم نے یہ کہنا شروع کردیا کہ خدا اس کے بعد کوئی رسول نہیں بھیجے گا - اسی طرح خدا زیادتی کرنے والے اور شکی مزاج انسانوں کو ان کی گمراہی میں چھوڑ دیتا ہے'),
(4168, 40, 35, 'جو لوگ کہ آیات الہٰی میں بحث کرتے ہیں بغیر اس کے کہ ان کے پاس خدا کی طرف سے کوئی دلیل آئے وہ اللہ اور صاحبان هایمان کے نزدیک سخت نفرت کے حقدار ہیں اور اللہ اسی طرح ہر مغرور اور سرکش انسان کے دل پر لَہر لگادیتا ہے'),
(4169, 40, 36, 'اور فرعون نے کہا کہ ہامان میرے لئے ایک قلعہ تیار کر کہ میں اس کے اسباب تک پہنچ جاؤں'),
(4170, 40, 37, 'جوکہ آسمان کے راستے ہیں اور اس طرح موسٰی علیھ السّلامکے خدا کو دیکھ لوں اور میرا تو خیال یہ ہے کہ موسٰی علیھ السّلام جھوٹے ہیں اور کوئی خدا نہیں ہے ...... اور اسی طرح فرعون کے لئے اس کی بدعملی کو آراستہ کردیا گیا اور اسے راستہ سے روک دیا گیا اور فرعون کی چالوں کا انجام سوائے ہلاکت اور تباہی کے کچھ نہیں ہے'),
(4171, 40, 38, 'اور جو شخص ایمان لے آیا تھا اس نے کہا کہ اے قوم والو میرا اتباع کرو تو میں تمہیں ہدایت کا راستہ دکھاسکتا ہوں'),
(4172, 40, 39, 'قوم والو ...._ یاد رکھو کہ یہ حیات دنیا صرف چند روزہ لذت ہے اور ہمیشہ رہنے کا گھر صرف آخرت کا گھر ہے'),
(4173, 40, 40, 'جو بھی کوئی برائی کرے گا اسے ویسا ہی بدلہ دیا جائے گا اور جو نیک عمل کرے گا چاہے وہ مرد ہو یا عورت بشرطیکہ صاحبِ ایمان بھی ہو انہیں جنت میں داخل کیا جائے گا اور وہاں بلِ حساب رزق دیا جائے گا'),
(4174, 40, 41, 'اور اے قوم والو آخر تمہیں کیا ہوگیا ہے کہ میں تمہیں نجات کی دعوت دے رہا ہوں اور تم مجھے جہّنم کی طرف دعوت دے رہے ہو'),
(4175, 40, 42, 'تمہاری دعوت یہ ہے کہ میں خدا کا انکار کردوں اور انہیں اس کا شریک بنادوں جن کا کوئی علم نہیں ہے اور میں تم کو اس خدا کی طرف دعوت دے رہا ہوں جو صاحب هعزّت اور بہت زیادہ بخشنے والا ہے'),
(4176, 40, 43, 'بیشک جس کی طرف تم دعوت دے رہے ہو وہ نہ دنیا میں پکارنے کے قابل ہے اور نہ آخرت میں اور ہم سب کی بازگشت بالآخر اللہ ہی کی طرف ہے اور زیادتی کرنے والے ہی دراصل جہّنم والے ہیں'),
(4177, 40, 44, 'پھر عنقریب تم اسے یاد کرو گے جو میں تم سے کہہ رہا ہوں اور میں تو اپنے معاملات کو پروردگار کے حوالے کررہا ہوں کہ بیشک وہ تمام بندوں کے حالات کا خوب دیکھنے والا ہے'),
(4178, 40, 45, 'تو اللہ نے اس مرد مومن کو ان لوگوں کی چالوں کے نقصانات سے بچالیا اور فرعون والوں کو بدترین عذاب نے گھیر لیا'),
(4179, 40, 46, 'وہ جہّنم جس کے سامنے یہ ہر صبح و شام پیش کئے جاتے ہیں اور جب قیامت برپا ہوگی تو فرشتوں کو حکم ہوگا کہ فرعون والوں کو بدترین عذاب کی منزل میں داخل کردو'),
(4180, 40, 47, 'اور اس وقت کو یاد دلاؤ جب یہ سب جہّنم کے اندر جھگڑے کریں گے اور کمزور لوگ مستکبر لوگوں سے کہیں گے کہ ہم تمہاری پیروی کرنے والے تھے تو کیا تم جہّنم کے کچھ حصّہ سے بھی ہمیں بچاسکتے ہو اور ہمارے کام آسکتے ہو'),
(4181, 40, 48, 'تو استکبار کرنے والے کہیں گے کہ اب ہم سب کی منزل یہی ہے کہ اللہ بندوں کے درمیان فیصلہ کرچکا ہے'),
(4182, 40, 49, 'اس کے بعد جہّنم میں رہنے والے جہّنم کے خازنوں سے کہیں گے کہ اپنے پروردگار سے دعا کرو کہ ایک ہی دن ہمارے عذاب میں تخفیف کردے'),
(4183, 40, 50, 'وہ جواب دیں گے کہ کیا تمہارے پاس تمہارے رسول کھلی ہوئی نشانیاں لے کر نہیں آئے تھے وہ لوگ کہیں گے کہ بیشک آئے تھے تو جواب ملے گا پھر تم خود ہی آواز دو حالانکہ کافروں کی آواز اور فریاد بیکار ہی ثابت ہوگی'),
(4184, 40, 51, 'بیشک ہم اپنے رسول اور ایمان لانے والوں کی زندگانی دنیا میں بھی مدد کرتے ہیں اور اس دن بھی مدد کریں گے جب سارے گواہ اٹھ کھڑے ہوں گے'),
(4185, 40, 52, 'جس دن ظالمین کے لئے کوئی معذرت کارگر نہ ہوگی اور ان کے لئے لعنت اور بدترین گھر ہوگا'),
(4186, 40, 53, 'اور یقینا ہم نے موسٰی علیھ السّلامکو ہدایت عطا کی اور بنیاسرائیل کو کتاب کا وارث بنایا ہے'),
(4187, 40, 54, 'جو کتاب مجسمئہ ہدایت اور صاحبانِ عقل کے لئے نصیحت کا سامان تھی'),
(4188, 40, 55, 'لہٰذا آپ صبر کریں کہ اللہ کا وعدہ یقینا برحق ہے اور اپنے حق میں استغفار کرتے رہیں اور صبح و شام اپنے پروردگار کی حمد کی تسبیح کرتے رہیں'),
(4189, 40, 56, 'بیشک جو لوگ خدا کی طرف سے آنے والی دلیل کے بغیر خدا کی نشانیوں میں بحث کرتے ہیں ان کے دلوں میں بڑائی کے خیال کے علاوہ اور کچھ نہیں ہے اور وہ اس تک پہنچ بھی نہیں سکتے ہیں لہذا آپ خدا کی پناہ طلب کریں کہ وہی سب کی سننے والا اور سب کے حالات کا دیکھنے والا ہے'),
(4190, 40, 57, 'بیشک زمین و آسمان کا پیدا کردینا لوگوں کے پیدا کردینے سے کہیں زیادہ بڑا کام ہے لیکن لوگوں کی اکثریت یہ بھی نہیں جانتی ہے'),
(4191, 40, 58, 'اور یاد رکھو کہ اندھے اور بینا برابر نہیں ہوسکتے ہیں اور جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ہیں وہ بھی بدکاروں جیسے نہیں ہوسکتے ہیں مگر تم لوگ بہت کم نصیحت حاصل کرتے ہو'),
(4192, 40, 59, 'بیشک قیامت آنے والی ہے اور اس میں کسی شک کی گنجائش نہیں ہے لیکن لوگوں کی اکثریت اس بات پر ایمان نہیں رکھتی ہے'),
(4193, 40, 60, 'اور تمہارے پروردگار کا ارشاد ہے کہ مجھ سے دعا کرو میں قبول کروں گا اور یقینا جو لوگ میری عبادت سے اکڑتے ہیں وہ عنقریب ذلّت کے ساتھ جہّنم میں داخل ہوں گے'),
(4194, 40, 61, 'اللہ ہی وہ ہے جس نے تمہارے لئے رات کو پیدا کیا ہے تاکہ تم اس میں سکون حاصل کرسکو اور دن کو روشنی کا ذریعہ قرار دیا ہے بیشک وہ اپنے بندوں پر بہت زیادہ فضل و کرم کرنے والا ہے لیکن لوگوں کی اکثریت اس کا شکریہ ادا نہیں کرتی ہے'),
(4195, 40, 62, 'وہی تمہارا پروردگار ہے جو ہر شے کا خالق ہے اور اس کے علاوہ کوئی خدا نہیں ہے تو تم کدھر بہکے جارہے ہو'),
(4196, 40, 63, 'اسی طرح وہ لوگ بہکائے جاتے ہیں جو اللہ کی نشانیوں کا انکار کردیتے ہیں'),
(4197, 40, 64, 'اللہ ہی وہ ہے جس نے تمہارے لئے زمین کو مستقر اور آسمان کو عمارت قرار دیا ہے اور تمہاری صورت کو بہترین صورت بنایا ہے اور تمہیں پاکیزہ رزق عطا کیا ہے. وہی تمہارا پروردگار ہے تو عالمین کا پالنے والا کس قدر برکتوں کا مالک ہے'),
(4198, 40, 65, 'وہ ہمیشہ زندہ رہنے والا ہے اس کے علاوہ کوئی خدا نہیں ہے لہٰذا تم لوگ اخلاص دین کے ساتھ اس کی عبادت کرو کہ ساری تعریف اسی عالمین کے پالنے والے خدا کے لئے ہے'),
(4199, 40, 66, 'آپ کہہ دیجئے کہ مجھے اس بات سے منع کیا گیا ہے کہ میں ان کی عبادت کروں جنہیں تم خدا کو چھوڑ کر پرستش کے قابل بنائے ہوئے ہو جب کہ میرے پاس کِھلی ہوئی نشانیاں آچکی ہیں اور مجھے حکم دیا گیا ہے کہ میں رب العالمین کا اطاعت گزار بندہ رہوں'),
(4200, 40, 67, 'وہی خدا ہے جس نے تم کو مٹی سے پیدا کیا پھر نطفہ سے پھر جمے ہوئے خون سے پھر تم کو بچہ بناکر باہر لاتا ہے پھر زندہ رکھتا ہے کہ توانائیوں کو پہنچو پھر بوڑھے ہوجاؤ اور تم میں سے بعض کو پہلے ہی اٹھالیا جاتا ہے اور تم کو اس لئے زندہ رکھتا ہے کہ اپنی مقررہ مدّت کو پہنچ جاؤ اور شاید تمہیں عقل بھی آجائے'),
(4201, 40, 68, 'وہی وہ ہے جو حیات بھی دیتا ہے اور موت بھی دیتا ہے پھر جب کسی بات کا فیصلہ کرلیتا ہے تو اس سے کہتا ہے کہ ہوجا اور وہ ہوجاتی ہے'),
(4202, 40, 69, 'کیا تم نے ان لوگوں کو نہیں دیکھا ہے جو آیات الہٰی کے بارے میں جھگڑا کرتے ہیں آخر یہ کہاں بھٹکتے چلے جارہے ہیں'),
(4203, 40, 70, 'جن لوگوں نے کتاب اور ان باتوں کی تکذیب کی جن کو دے کہ ہم نے پیغمبروں کو بھیجا تھا انہیں عنقریب اس کا انجام معلوم ہوجائےگا'),
(4204, 40, 71, 'جب ان کی گردنوں میں طوق اور زنجیریں ڈالی جائیں گی اور انہیں کھینچا جائے گا'),
(4205, 40, 72, 'گرم پانی میں اور اس کے بعد جہّنم میں جھونک دیا جائے گا'),
(4206, 40, 73, 'پھر یہ کہا جائے گا کہ اب وہ کہاں ہیں جنہیں تم شریک بنایا کرتے تھے'),
(4207, 40, 74, 'خدا کو چھوڑ کر.... تو وہ لوگ جواب دیں گے کہ وہ ہم کو چھوڑ کر گم ہوگئے بلکہ ہم اس کے پہلے کسی کو نہیں پکارا کرتے تھے اور اللہ اسی طرح کافروں کو گمراہی میں چھوڑ دیتا ہے'),
(4208, 40, 75, 'یہ سب اس بات کا نتیجہ ہے کہ تم لوگ زمین میں باطل سے خوش ہوا کرتے تھے اور اکڑ کر چلا کرتے تھے'),
(4209, 40, 76, 'اب جہّنم کے دروازوں سے داخل ہوجاؤ اور اسی میں ہمیشہ رہو کہ اکڑنے والوں کا ٹھکانا بہت اِرا ہے'),
(4210, 40, 77, 'اب آپ صبر کریں کہ خدا کا وعدہ بالکل سچا ہے پھر یا تو ہم جن باتوں کی دھمکی دے رہے ہیں ان میں سے کچھ آپ کو دکھلادیں گے یا آپ کو پہلے ہی اٹھالیں گے تو بھی وہ سب پلٹا کر یہیں لائے جائیں گے'),
(4211, 40, 78, 'اور ہم نے آپ سے پہلے بھی بہت سے رسول بھیجے ہیں جن میں سے بعض کا تذکرہ آپ سے کیا ہے اور بعض کا تذکرہ بھی نہیں کیا ہے اور کسی رسول کے امکان میں یہ بات نہیں ہے کہ خدا کی اجازت کے بغیر کوئی معجزہ لے آئے پھر جب حکم خدا آگیا تو حق کے ساتھ فیصلہ کردیا گیا اور اس وقت اہل باطل ہی خسارہ میں رہے'),
(4212, 40, 79, 'اللہ ہی وہ ہے جس نے چوپایوں کو تمہارے لئے خلق کیا ہے جن میں سے بعض پر تم سواری کرتے ہو اور بعض کو کھانے میں استعمال کرتے ہو'),
(4213, 40, 80, 'اور تمہارے لئے ان میں بہت سے منافع ہیں اور اس لئے بھی کہ تم ان کے ذریعہ اپنی دلی مرادوں تک پہنچ سکو اور تمہیں ان جانوروں پر اور کشتیوں پر سوار کیا جاتا ہے'),
(4214, 40, 81, 'اور خدا تمہیں اپنی نشانیاں دکھلاتا ہے تو تم اس کی کس کس نشانی سے انکار کرو گے'),
(4215, 40, 82, 'کیا ان لوگوں نے زمین میں سیر نہیں کی ہے کہ دیکھتے کہ ان سے پہلے والوں کا انجام کیا ہوا ہے جو ان کے مقابلہ میں اکثریت میں تھے اور زیادہ طاقتور بھی تھے اور زمین میں آثار کے مالک تھے لیکن جو کچھ بھی کمایا تھا کچھ کام نہ آیا اور مبتلائے عذاب ہوگئے'),
(4216, 40, 83, 'پھر جب ان کے پاس رسول معجزات لے کر آئے تو اپنے علم کی بنا پر ناز کرنے لگے اور نتیجہ میں جس بات کا مذاق اُڑا رہے تھے اسی نے انہیں اپنے گھیرے میں لے لیا'),
(4217, 40, 84, 'پھر جب انہوں نے ہمارے عذاب کو دیکھا تو کہنے لگے کہ ہم خدائے یکتا پر ایمان لائے ہیں اور جن باتوں کا شرک کیا کرتے تھے سب کا انکار کررہے ہیں'),
(4218, 40, 85, 'تو عذاب کے دیکھنے کے بعد کوئی ایمان کام آنے والا نہیں تھا کہ یہ اللہ کا مستقل طریقہ ہے جو اس کے بندوں کے بارے میں گِزر چکا اَہے اور اسی وقت کافر خسارہ میں مبتلا ہوجاتے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حمۤ'),
(4220, 41, 2, 'یہ خدائے رحمن و رحیم کی تنزیل ہے'),
(4221, 41, 3, 'اس کتاب کی آیتیں تفصیل کے ساتھ بیان کی گئی ہیں عربی زبان کا قرآن ہے اس قوم کے لئے جو سمجھنے والی ہو'),
(4222, 41, 4, 'یہ قرآن بشارت دینے والا اور عذاب الہٰی سے ڈرانے والا بناکر نازل کیا گیا ہے لیکن اکثریت نے اس سے اعراض کیا ہے اور وہ کچھ سنتے ہی نہیں ہیں'),
(4223, 41, 5, 'اور کہتے ہیں کہ ہمارے دل جن باتوں کی تم دعوت دے رہے ہو ان کی طرف سے پردہ میں ہیں اور ہمارے کانوں میں بہراپن ہے اور ہمارے تمہارے درمیان پردہ حائل ہے لہذا تم اپنا کام کرو اور ہم اپنا کام کررہے ہیں'),
(4224, 41, 6, 'آپ کہہ دیجئے کہ میں بھی تمہارا ہی جیسا بشر ہوں لیکن میری طرف برابر وحی آتی ہے کہ تمہارا خدا ایک ہے لہٰذا اس کے لئے استقامت کرو اور اس سے استغفارکرو اور مشرکوں کے حال پر افسوس ہے'),
(4225, 41, 7, 'جو لوگ زکوِٰادا نہیں کرتے ہیں اور آخرت کا انکار کرنے والے ہیں'),
(4226, 41, 8, 'بیشک جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے ان کے لئے منقطع نہ ہونے والا اجر ہے'),
(4227, 41, 9, 'آپ کہہ دیجئے کہ کیا تم لوگ اس خدا کا انکار کرتے ہو جس نے ساری زمین کو دو دن میں پیدا کردیا ہے اور اس کا مثل قرار دیتے ہوئے جب کہ وہ عالمین کا پالنے والا ہے'),
(4228, 41, 10, 'اور اس نے اس زمین میں اوپر سے پہاڑ قرار دے دیئے ہیںاور برکت رکھ دی ہے اور چار دن کے اندر تمام سامان معیشت کو مقرر کردیا ہے جو تمام طلبگاروں کے لئے مساوی حیثیت رکھتا ہے'),
(4229, 41, 11, 'اس کے بعد اس نے آسمان کا رخ کیا جو بالکل دھواں تھا اور اسے اور زمین کو حکم دیا کہ بخوشی یا بہ کراہت ہماری طرف آؤ تو دونوں نے عرض کی کہ ہم اطاعت گزار بن کر حاضر ہیں'),
(4230, 41, 12, 'پھر ان آسمانوں کو دو دن کے اندر سات آسمان بنادیئے اور ہر آسمان میں اس کے معاملہ کی وحی کردی اور ہم نے آسمان دنیا کو چراغوں سے آراستہ کردیا ہے اور محفوظ بھی بنادیا ہے کہ یہ خدائے عزیز و علیم کی مقرر کی ہوئی تقدیر ہے'),
(4231, 41, 13, 'پھر اگر یہ اعراض کریں تو کہہ دیجئے کہ ہم نے تم کو ویسی ہی بجلی کے عذاب سے ڈرایا ہے جیسی قوم عاد و ثمود پر نازل ہوئی تھی'),
(4232, 41, 14, 'جب ان کے پاس سامنے اور پیچھے سے ہمارے نمائندے آئے کہ اللہ کے علاوہ کسی کی عبادت نہ کرو تو انہوں نے کہہ دیا کہ ہمارا خدا چاہتا تو ملائکہ کو نازل کرتا ہم تمہارے پیغام کے قبول کرنے والے نہیں ہیں'),
(4233, 41, 15, 'پھر قوم عاد نے زمین میں ناحق بلندی اور برتری سے کام لیا اور یہ کہنا شروع کردیا کہ ہم سے زیادہ طاقت والا کون ہے .... تو کیا ان لوگوں نے یہ نہیں دیکھا کہ جس نے ان سب کو پیدا کیا ہے وہ بہرحال ان سے زیادہ طاقت رکھنے والا ہے لیکن یہ لوگ ہماری نشانیوں کا انکار کرنے والے تھے'),
(4234, 41, 16, 'تو ہم نے بھی ان کے اوپر تیزوتند آندھی کو ان کی نحوست کے دنوں میں بھیج دیا تاکہ انہیں زندگانی دنیا میں بھی رسوائی کے عذاب کا مزہ چکھائیں اور آخرت کا عذاب تو زیادہ رسوا کن ہے اور وہاں ان کی کوئی مدد بھی نہیں کی جائے گی'),
(4235, 41, 17, 'اور قوم ثمود کو بھی ہم نے ہدایت دی لیکن ان لوگوں نے گمراہی کو ہدایت کے مقابلہ میں زیادہ پسند کیا تو ذلّت کے عذاب کی بجلی نے انہیں اپنی گرفت میں لے لیا ان اعمال کی بنا پر جو وہ انجام دے رہے تھے'),
(4236, 41, 18, 'اور ہم نے ان لوگوں کو نجات دے دی جو ایمان لانے والے اور تقویٰ اختیار کرنے والے تھے'),
(4237, 41, 19, 'اور جس دن دشمنانِ خدا کو جہّنم کی طرف ڈھکیلا جائے گا پھر انہیں زجر و توبیخ کی جائے گی'),
(4238, 41, 20, 'یہاں تک کہ جب سب جہّنم کے پاس آئیں گے تو ان کے کان اور ان کی آنکھیں اور جلد سب ان کے اعمال کے بارے میں ان کے خلاف گواہی دیں گے'),
(4239, 41, 21, 'اور وہ اپنے اعضائ سے کہیں گے کہ تم نے ہمارے خلاف کیسے شہادت دے دی تو وہ جواب دیں گے کہ ہمیں اسی خدا نے گویا بنایا ہے جس نے سب کو گویائی عطا کی ہے اور تم کو بھی پہلے دن اسی نے پیدا کیا ہے اور اب بھی پلٹ کر اسی کی بارگاہ میں جاؤ گے'),
(4240, 41, 22, 'اور تم اس بات سے پردہ پوشی نہیں کرتے تھے کہ کہیں تمہارے خلاف تمہارے کان, تمہاری آنکھیں اور گوشت پوست گواہی نہ دے دیں بلکہ تمہارا خیال یہ تھا کہ اللہ تمہارے بہت سے اعمال سے باخبر بھی نہیں ہے'),
(4241, 41, 23, 'اور یہی خیال جو تم نے اپنے پروردگار کے بارے میں قائم کیا تھا اسی نے تمہیں ہلاک کردیا ہے اور تم خسارہ والوں میں ہوگئے ہو'),
(4242, 41, 24, 'اب اگر یہ برداشت کریں تو بھی ان کا ٹھکانا جہّنم ہے اور اگر معذرت کرنا چاہیں تو بھی معذرت قبول نہیں کی جائے گی'),
(4243, 41, 25, 'اور ہم نے خود بھی ان کے لئے ہم نشین فراہم کردیئے تھے جنہوں نے اس کے پچھلے تمام امور ان کی نظروں میں آراستہ کردیئے تھے اور ان پر بھی وہی عذاب ثابت ہوگیا جو ان کے پہلے انسان اور جنّات کے گروہوں پر ثابت ہوچکا تھا کہ یہ سب کے سب خسارہ والوں میں تھے'),
(4244, 41, 26, 'اور کفاّر آپس میں کہتے ہیں کہ اس قرآن کو ہرگز مت سنو اور اس کی تلاوت کے وقت ہنگامہ کرو شاید اسی طرح ان پر غالب آجاؤ'),
(4245, 41, 27, 'تو اب ہم ان کفر کرنے والوں کو شدید عذاب کا مزہ چکھائیں گے اور انہیں ان کے اعمال کی بدترین سزادیں گے'),
(4246, 41, 28, 'یہ دشمنانِ خدا کی صحیح سزا جہّنم ہے جس میں ان کا ہمیشگی کا گھر ہے جو اس بات کی سزا ہے کہ یہ آیات الہیٰہ کا انکار کیا کرتے تھے'),
(4247, 41, 29, 'اور کفاّر یہ فریاد کریں گے کہ پروردگار ہمیں جنّات و انسان کے ان لوگوں کو دکھلادے جنہوں نے ہم کو گمراہ کیا تھا تاکہ ہم انہیں اپنے قدموں کے نیچے قرار دیدیں اور اس طرح وہ پست لوگوں میں شامل ہوجائیں'),
(4248, 41, 30, 'بیشک جن لوگوں نے یہ کہا کہ اللہ ہمارا رب ہے اور اسی پر جمے رہے ان پر ملائکہ یہ پیغام لے کر نازل ہوتے ہیں کہ ڈرو نہیں اور رنجیدہ بھی نہ ہو اور اس جنّت سے مسرور ہوجاؤ جس کا تم سے وعدہ کیا جارہا ہے'),
(4249, 41, 31, 'ہم زندگانی دنیا میں بھی تمہارے ساتھی تھے اور آخرت میں بھی تمہارے ساتھی ہیں یہاں جنّت میں تمہارے لئے وہ تمام چیزیں فراہم ہیں جن کے لئے تمہارا دل چاہتا ہے اور جنہیں تم طلب کرو گے'),
(4250, 41, 32, 'یہ بہت زیادہ بخشنے والے مہربان پروردگار کی طرف سے تمہاری ضیافت کا سامان ہے'),
(4251, 41, 33, 'اور اس سے زیادہ بہتر بات کس کی ہوگی جو لوگوں کو اللہ کی طرف دعوت دے اور نیک عمل بھی کرے اور یہ کہے کہ میں اس کے اطاعت گزاروں میں سے ہوں'),
(4252, 41, 34, 'نیکی اور برائی برابر نہیں ہوسکتی لہٰذا تم برائی کا جواب بہترین طریقہ سے دو کہ اس طرح جس کے اور تمہارے درمیان عداوت ہے وہ بھی ایسا ہوجائے گا جیسے گہرا دوست ہوتا ہے'),
(4253, 41, 35, 'اور یہ صلاحیت ان ہی کو نصیب ہوتی ہے جو صبر کرنے والے ہوتے ہیں اور یہ بات ان ہی کو حاصل ہوتی ہے جو بڑی قسمت والے ہوتے ہیں'),
(4254, 41, 36, 'اور جب تم میں شیطان کی طرف سے کوئی وسوسہ پیدا ہو تو اللہ کی پناہ طلب کرو کہ وہ سب کی سننے والا اور سب کا جاننے والا ہے'),
(4255, 41, 37, 'اور اسی خدا کی نشانیوں میں سے رات و دن اور آفتاب و ماہتاب ہیں لہٰذا آفتاب و ماہتاب کو سجدہ نہ کرو بلکہ اس خدا کو سجدہ کرو جس نے ان سب کو پیدا کیا ہے اگر واقعا اس کے عبادت کرنے والے ہو'),
(4256, 41, 38, 'پھر اگر یہ لوگ اکڑ سے کام لیں تو لیں جو لوگ پروردگار کی بارگاہ میں ہیں وہ دن رات اس کی تسبیح کررہے ہیں اور کسی وقت بھی تھکتے نہیں ہیں'),
(4257, 41, 39, 'اور اس کی نشانیوں میں سے یہ بھی ہے کہ تم زمین کو صاف اور مفِدہ دیکھ رہے ہو اور پھر جب ہم نے پانی برسا دیا تو زمین لہلہانے لگی اور اس میں نشوونما پیدا ہوگئی بیشک جس نے زمین کو زندہ کیا ہے وہی مفِدوں کا زندہ کرنے والا بھی ہے اور یقینا وہ ہر شے پر قادر ہے'),
(4258, 41, 40, 'بیشک جو لوگ ہماری آیات میں ہیرا پھیری کرتے ہیں وہ ہم سے حُھپنے والے نہیں ہیں .سوچو کہ جو شخص جہّنم میں ڈال دیا جائے گا وہ بہتر ہے یا جو روزِ قیامت بے خوف و خطر نظر آئے .تم جو چاہو عمل کرو وہ تمہارے تمام اعمال کا دیکھنے والا ہے'),
(4259, 41, 41, 'بیشک جن لوگوں نے قرآن کے آنے کے بعد اس کا انکار کردیا ان کا انجام اِرا ہے اور یہ ایک عالی مرتبہ کتاب ہے'),
(4260, 41, 42, 'جس کے قریب سامنے یا پیچھے کسی طرف سے باطل آبھی نہیں سکتا ہے کہ یہ خدائے حکیم و حمید کی نازل کی ہوئی کتاب ہے'),
(4261, 41, 43, 'پیغمبر آپ سے جو کچھ بھی کہا جاتا ہے یہ سب آپ سے پہلے والے رسولوں سے کہا جاچکاُ ہے اور آپ کا پروردگار بخشنے والا بھی ہے اور دردناک عذاب کا مالک بھی ہے'),
(4262, 41, 44, 'اور اگر ہم اس قرآن کو عجمی زبان میں نازل کردیتے تو یہ کہتے کہ اس کی آیتیں واضح کیوں نہیں ہیں اور یہ عجمی کتاب اور عربی انسان کا ربط کیا ہے. تو آپ کہہ دیجئے کہ یہ کتاب صاحبان ه ایمان کے لئے شفا اور ہدایت ہے اور جو لوگ ایمان نہیں رکھتے ہیں ان کے کانوں میں بہرا پن ہے اور وہ ان کو نظر بھی نہیں آرہا ہے اور ان لوگوں کو بہت دور سے پکارا جائے گا'),
(4263, 41, 45, 'اور یقینا ہم نے موسٰی کو کتاب دی تو اس میں بھی جھگڑا ڈال دیا گیا اور اگر آپ کے پروردگار کی طرف سے ایک بات پہلے سے طے نہ ہوگئی ہوتی تو اب تک ان کے درمیان فیصلہ کردیا گیا ہوتا اور یقینا یہ بڑے بے چین کردینے والے شک میں مبتلا ہیں'),
(4264, 41, 46, 'جو بھی نیک عمل کرے گا وہ اپنے لئے کرے گا اور جو اِرا کرے گا اس کا ذمہ دار بھی وہ خود ہی ہوگا اور آپ کا پروردگار بندوں پر ظلم کرنے والا نہیں ہے'),
(4265, 41, 47, 'قیامت کا علم اسی کی طرف پلٹا دیا جاتا ہے اور تِوروں سے جو پھل نکلتے ہیں یا عورتوں کو جو حمل ہوتا ہے یا جن بچوں کو وہ پیدا کرتی ہیں یہ سب اسی کے علم کے مطابق ہوتے ہیں اور جس دن وہ پکار کر پوچھے گا کہ میرے شرکائ کہاں ہیں تو مشرکین مجبورا عرض کریں گے کہ ہم پہلے ہی بتاچکے ہیں کہ ہم میں سے کوئی ان سے واقف نہیں ہے'),
(4266, 41, 48, 'اور وہ سب گم ہوجائیں گے جنہیں یہ پہلے پکارا کرتے تھے اور اب خیال ہوگا کہ کوئی چھٹکارا ممکن نہیں ہے'),
(4267, 41, 49, 'انسان بھلائی کی رَعا کرتے ہوئے کبھی نہیں تھکتا ہے اور جب کوئی تکلیف اسے چھو بھی لیتی ہے تو بالکل مایوس اور بے آس ہوجاتا ہے'),
(4268, 41, 50, 'اور اگر ہم اس تکلیف کے بعد پھر اسے رحمت کا مزہ چکھادیں تو فورا یہ کہہ دے گا کہ یہ تو میرا حق ہے اور مجھے تو خیال بھی نہیں ہے کہ قیامت قائم ہونے والی ہے اور اگر میں پروردگار کی طرف پلٹایا بھی گیا تو میرے لئے وہاں بھی نیکیاں ہی ہیں تو پھر ہم بھی کفار کو ضرور بتائیں گے کہ انہوں نے کیا کیا, کیا ہے اور انہیں سخت عذاب کا مزہ چکھائیں گے'),
(4269, 41, 51, 'اور ہم جب انسان کو نعمت دیتے ہیں تو ہم سے کنارہ کش ہوجاتا ہے اور پہلو بدل کر الگ ہوجاتا ہے اور جب برائی پہنچ جاتی ہے تو خوب لمبی چوڑی رَعائیں کرنے لگتا ہے'),
(4270, 41, 52, 'آپ کہہ دیجئے کہ کیا تمہیں یہ خیال ہے اگر یہ قرآن خدا کی طرف سے ہے اور تم نے اس کا انکار کردیا تو اس سے زیادہ کون گمراہ ہوگا جو اس سے اتنا سخت اختلاف کرنے والا ہو'),
(4271, 41, 53, 'ہم عنقریب اپنی نشانیوں کو تمام اطراف عالم میں اور خود ان کے نفس کے اندر دکھلائیں گے تاکہ ان پر یہ بات واضح ہوجائے کہ وہ برحق ہے اور کیا پروردگار کے لئے یہ بات کافی نہیں ہے کہ وہ ہر شے کا گواہ اور سب کا دیکھنے والا ہے'),
(4272, 41, 54, 'آگاہ ہوجاؤ کہ یہ لوگ اللہ سے ملاقات کی طرف سے شک میں مبتلا ہیں اور آگاہ ہوجاؤ کہ اللہ ہر شے پر احاطہ کئے ہوئے ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حم ۤ'),
(4274, 42, 2, 'عۤسۤقۤ'),
(4275, 42, 3, 'اسی طرح خدائے عزیز و حکیم آپ کی طرف اور آپ سے پہلے والوں کی طرف وحی بھیجتا رہتا ہے'),
(4276, 42, 4, 'زمین و آسمان میں جو کچھ بھی ہے سب اسی کی ملکیت ہے اور وہی خدائے بزرگ و برتر ہے'),
(4277, 42, 5, 'قریب ہے کہ آسمان اس کی ہیبت سے اوپر سے شگافتہ ہوجائیں اور ملائکہ بھی اپنے پروردگار کی حمد کی تسبیح کررہے ہیں اور زمین والوں کے حق میں استغفار کررہے ہیں آگاہ ہوجاؤ کہ خدا ہی وہ ہے جو بہت بخشنے والا اور مہربان ہے'),
(4278, 42, 6, 'اور جن لوگوں نے اس کے علاوہ سرپرست بنالئے ہیں اللہ ان سب کے حالات کا نگراں ہے اور پیغمبر آپ کسی کے ذمہ دار نہیں ہیں'),
(4279, 42, 7, 'اور ہم نے اسی طرح آپ کی طرف عربی زبان میں قرآن کی وحی بھیجی تاکہ آپ مکہ اور اس کے اطراف والوں کو ڈرائیں اور اس دن سے ڈرائیں جس دن سب کو جمع کیا جائے گا اور اس میں کسی شک کی گنجائش نہیں ہے اس دن ایک گروہ جنّت میں ہوگا اور ایک جہّنم میں ہوگا'),
(4280, 42, 8, 'اور اگر خدا چاہتا تو سب کو ایک قوم بنادیتا لیکن وہ جسے چاہتا ہے اسی کو اپنی رحمت میں داخل کرتا ہے اور ظالمین کے لئے نہ کوئی سرپرست ہے اور نہ مددگار'),
(4281, 42, 9, 'کیا ان لوگوں نے اس کو چھوڑ کر اپنے لئے سرپرست بنائے ہیں جب کہ وہی سب کا سرپرست ہے اور وہی اَمردوں کو زندہ کرتا ہے اور وہ ہر شے پر قدرت رکھنے والا ہے'),
(4282, 42, 10, 'اور تم جس چیز میں بھی اختلاف کرو گے اس کا فیصلہ اللہ کے ہاتھوں میں ہے. وہی میرا پروردگار ہے اور اسی پر میرا بھروسہ ہے اور اسی کی طرف میں رجوع کرتا ہوں'),
(4283, 42, 11, 'وہ آسمانوں اور زمین کا پیدا کرنے والا ہے اس نے تمہارے نفوس میں سے بھی جوڑا بنایا ہے اور جانوروں میں سے بھی جوڑے بنائے ہیں وہ تم کو اسی جوڑے کے ذریعہ دنیا میں پھیلا رہا ہے اس کا جیسا کوئی نہیں ہے وہ سب کی سننے والا اور ہر چیز کا دیکھنے والا ہے'),
(4284, 42, 12, 'آسمان و زمین کی تمام کنجیاں اسی کے اختیار میں ہیں وہ جس کے لئے چاہتا ہے اس کے رزق میں وسعت پیدا کردیتا ہے اور جس کے لئے چاہتا ہے تنگی پیدا کردیتا ہے وہ ہر شے کا خوب جاننے والا ہے'),
(4285, 42, 13, 'اس نے تمہارے لئے دین میں وہ راستہ مقرر کیا ہے جس کی نصیحت نوح کو کی ہے اور جس کی وحی پیغمبر تمہاری طرف بھی کی ہے اور جس کی نصیحت ابراہیم علیھ السّلام, موسٰی علیھ السّلام اور عیسٰی علیھ السّلام کو بھی کی ہے کہ دین کو قائم کرو اور اس میں تفرقہ نہ پیدا ہونے پائے مشرکین کو وہ بات سخت گراں گزرتی ہے جس کی تم انہیں دعوت دے رہے ہو اللہ جس کو چاہتا ہے اپنی بارگاہ کے لئے چن لیتا ہے اور جو اس کی طرف رجوع کرتا ہے اسے ہدایت دے دیتا ہے'),
(4286, 42, 14, 'اور ان لوگوں نے آپس میں تفرقہ اسی وقت پیدا کیا ہے جب ان کے پاس علم آچکا تھا اور یہ صرف آپس کی دشمنی کی بنا پر تھا اور اگر پروردگار کی طرف سے ایک بات پہلے سے ایک مدّت کے لئے طے نہ ہوگئی ہوتی تو اب تک ان کے درمیان فیصلہ ہوچکا ہوتا اور بیشک جن لوگوں کو ان کے بعد کتاب کا وارث بنایا گیا ہے وہ اس کی طرف سے سخت شک میں پڑے ہوئے ہیں'),
(4287, 42, 15, 'لہٰذا آپ اسی کے لئے دعوت دیں اور اس طرح استقامت سے کام لیں جس طرح آپ کو حکم دیا گیا ہے اور ا ن کی خواہشات کا اتباع نہ کریں اور یہ کہیں کہ میرا ایمان اس کتاب پر ہے جو خدا نے نازل کی ہے اور مجھے حکم دیا گیا ہے کہ تمہارے درمیان انصاف کروں اللہ ہمارا اور تمہارا دونوں کا پروردگار ہے ہمارے اعمال ہمارے لئے ہیں اور تمہارے اعمال تمہارے لئے - ہمارے اور تمہارے درمیان کوئی بحث نہیں ہے اللہ ہم سب کو ایک دن جمع کرے گا اور اسی کی طرف سب کی بازگشت ہے'),
(4288, 42, 16, 'اور جو لوگ اس کے مان لئے جانے کے بعد خدا کے بارے میں جھگڑا کرتے ہیں ان کی دلیل بالکل مہمل اور لغو ہے اور ان پر اللہ کا غضب ہے اور ان کے لئے شدید قسم کا عذاب ہے'),
(4289, 42, 17, 'اللہ ہی وہ ہے جس نے حق کے ساتھ کتاب اور میزان کو نازل کیا ہے اور آپ کو کیا معلوم کہ شاید قیامت قریب ہی ہو'),
(4290, 42, 18, 'اس کی جلدی صرف وہ لوگ کرتے ہیں جن کا اس پر ایمان نہیں ہے ورنہ جو ایمان والے ہیں وہ تو اس سے خوفزدہ ہی رہتے ہیں اور یہ جانتے ہیں کہ وہ بہرحال برحق ہے ہوشیار کہ جو لوگ قیامت کے بارے میں شک کرتے ہیں وہ گمراہی میں بہت دور تک چلے گئے ہیں'),
(4291, 42, 19, 'اللہ اپنے بندوں پر مہربان ہے وہ جس کو بھی چاہتا ہے رزق عطا کرتا ہے اور وہ صاحبِ قوت بھی ہے اور صاحبِ عزت بھی ہے'),
(4292, 42, 20, 'جو انسان آخرت کی کھیتی چاہتا ہے ہم اس کے لئے اضافہ کردیتے ہیں اور جو دنیا کی کھیتی کا طلبگار ہے اسے اسی میں سے عطا کردیتے ہیں اور پھر آخرت میں اس کا کوئی حصہ نہیں ہے'),
(4293, 42, 21, 'کیا ان کے لئے ایسے شرکائ بھی ہیں جنہوں نے دین کے وہ راستے مقرّر کئے ہیں جن کی خدا نے اجازت بھی نہیں دی ہے اور اگر فیصلہ کے دن کا وعدہ نہ ہوگیا ہوتا تو اب تک ان کے درمیان فیصلہ کردیا گیا ہوتا اور یقینا ظالموں کے لئے بڑا دردناک عذاب ہے'),
(4294, 42, 22, 'آپ دیکھیں گے کہ ظالمین اپنے کرتوت کے عذاب سے خوفزدہ ہیں اور وہ ان پر بہرحال نازل ہونے والا ہے اور جو لوگ ایمان لے آئے اور انہوں نے نیک اعمال کئے ہیں وہ جنّت کے باغات میں رہیں گے اور ان کے لئے پروردگار کی بارگاہ میں وہ تمام چیزیں ہیں جن کے وہ خواہشمند ہوں گے اور یہ ایک بہت بڑا فضل پروردگار ہے'),
(4295, 42, 23, 'یہی وہ فضلِ عظیم ہے جس کی بشارت پروردگار اپنے بندوں کو دیتا ہے جنہوں نے ایمان اختیار کیا ہے اور نیک اعمال کئے ہیں تو آپ کہہ دیجئے کہ میں تم سے اس تبلیغ رسالت کا کوئی اجر نہیں چاہتا علاوہ اس کے کہ میرے اقربا سے محبت کرو اور جو شخص بھی کوئی نیکی حاصل کرے گا ہم اس کی نیکی میں اضافہ کردیں گے کہ بیشک اللہ بہت زیادہ بخشنے والا اور قدرداں ہے'),
(4296, 42, 24, 'کیا ان لوگوں کا کہنا یہ ہے کہ رسول, اللہ پر جھوٹے بہتان باندھتا ہے جب کہ خدا چاہے تو تمہارے قلب پر بھی مہر لگا سکتا ہے اور خدا تو باطل کو مٹا دیتا ہے اور حق کو اپنے کلمات کے ذریعہ ثابت اور پائیدار بنادیتا ہے یقینا وہ دلوں کے رازوں کا جاننے والا ہے'),
(4297, 42, 25, 'اور وہی وہ ہے جو اپنے بندوں کی توبہ کوقبول کرتا ہے اور ان کی برائیوں کو معاف کرتا ہے اور وہ تمہارے اعمال سے خوب باخبر ہے'),
(4298, 42, 26, 'اور جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے وہی دعوت الٰہٰی کو قبول کرتے ہیں اور خدا اپنے فضل و کرم سے ان کے اجر میں اضافہ کردیتا ہے اور کافرین کے لئے تو بڑا سخت عذاب رکھا گیا ہے'),
(4299, 42, 27, 'اور اگر خدا تمام بندوں کے لئے رزق کو وسیع کردیتا ہے تو یہ لوگ زمین میں بغاوت کردیتے مگر وہ اپنی مشیت کے مطابق معیّنہ مقدار میں نازل کرتا ہے کہ وہ اپنے بندوں سے خوب باخبر ہے اوران کے حالات کا دیکھنے والا ہے'),
(4300, 42, 28, 'وہی وہ ہے جو ان کے مایوس ہوجانے کے بعد بارش کو نازل کرتاہے اور اپنی رحمت کو منتشر کرتا ہے اور وہی قابل تعریف مالک اور سرپرست ہے'),
(4301, 42, 29, 'اور اس کی نشانیوں میں سے زمین و آسمان کی خلقت اور ان کے اندر چلنے والے تمام جاندارہیں اور وہ جب چاہے ان سب کو جمع کرلینے پر قدرت رکھنے والا ہے'),
(4302, 42, 30, 'اور تم تک جو مصیبت بھی پہنچتی ہے وہ تمہارے ہاتھوں کی کمائی ہے اور وہ بہت سی باتوں کو معاف بھی کردیتاہے'),
(4303, 42, 31, 'اور تم زمین میں خدا کو عاجز نہیں کرسکتے ہو اورتمہارے لئے اس کے علاوہ کوئی سرپرست اور مددگار بھی نہیں ہے'),
(4304, 42, 32, 'اور اس کی نشانیوں میں سے سمندر میں چلنے والے بادبانی جہاز بھی ہیں جو پہاڑ کی طرح بلند ہیں'),
(4305, 42, 33, 'وہ اگر چاہے تو ہوا کو ساکن بنادے تو سب سطح آب پر جم کر رہ جائیں - بیشک اس حقیقت میں صبر وشکر کرنے والوں کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(4306, 42, 34, 'یا وہ انہیں ان کے اعمال کی بنا پرہلاک ہی کردے اور وہ بہت سی باتوں کو معاف بھی کردیتا ہے'),
(4307, 42, 35, 'اور ہماری آیتوں میں جھگڑا کرنے والوں کو معلوم ہوجانا چاہئے کہ ان کے لئے کوئی چھٹکارا نہیں ہے'),
(4308, 42, 36, 'پس تم کو جو کچھ بھی دیا گیا ہے وہ زندگانی دنیا کا چین ہے اوربس اور جو کچھ اللہ کی بارگاہ میں ہے وہ خیر اورباقی رہنے والا ہے ان لوگوں کے لئے جو ایمان رکھتے ہیں اوراپنے پروردگار پربھروسہ کرتے ہیں'),
(4309, 42, 37, 'اوربڑے بڑے گناہوں اور فحش باتوں سے پرہیز کرتے ہیں اور جب غصّہ آجاتا ہے تو معاف کردیتے ہیں'),
(4310, 42, 38, 'اور جو اپنے رب کی بات کو قبول کرتے ہیں اور نماز قائم کرتے ہیں اور آپس کے معاملات میں مشورہ کرتے ہیں اور ہمارے رزق میں سے ہماری راہ میں خرچ کرتے ہیں'),
(4311, 42, 39, 'اورجب ان پر کوئی ظلم ہوتا ہے تو اس کا بدلہ لے لیتے ہیں'),
(4312, 42, 40, 'اور ہر برائی کابدلہ اس کے جیسا ہوتا ہے پھرجو معاف کردے اور اصلاح کردے اس کااجر اللہ کے ذمہ ہے وہ یقینا ظالموں کو دوست نہیں رکھتا ہے'),
(4313, 42, 41, 'اورجو شخض بھی ظلم کے بعد بدلہ لے اس کے اوپرکوئی الزام نہیں ہے'),
(4314, 42, 42, 'الزام ان لوگوں پر ہے جولوگوں پر ظلم کرتے ہیں اور زمین میں ناحق زیادتیاں پھیلاتے ہیں ان ہی لوگوں کے لئے دردناک عذاب ہے'),
(4315, 42, 43, 'اوریقینا جو صبر کرے اور معاف کردے تو اس کایہ عمل بڑے حوصلے کا کام ہے'),
(4316, 42, 44, 'اور جس کوخدا گمراہی میں چھوڑ دے اس کے لئے اس کے بعد کوئی ولی اور سرپرست نہیں ہے اورتم دیکھو گے کہ ظالمین عذاب کو دیکھنے کے بعد یہ کہیں گے کہ کیا واپسی کا کوئی راستہ نکل سکتا ہے'),
(4317, 42, 45, 'اور تم انہیں دیکھو گے کہ وہ جہنمّ کے سامنے پیش کئے جائیں گے تو ذلّت سے ان کے سر جھکے ہوئے ہوں گے اور وہ کن انکھیوں سے دیکھتے بھی جائیں گے اور صاحبانِ ایمان کہیں گے کہ گھاٹے والے وہی افرادہیں جنہوں نے اپنے نفس اوراپنے اہل کو قیامت کے دن گھاٹے میں مبتلا کردیا ہے آگاہ ہوجاؤ کہ ظالموں کو بہرحال دائمی عذاب میں رہنا پڑے گا'),
(4318, 42, 46, 'اور ان کے لئے ایسے سرپرست بھی نہ ہوںگے جو خدا سے ہٹ کر ان کی مدد کرسکیں اور جس کو خدا گمراہی میں چھوڑ دے اس کے لئے کوئی راستہ نہیں ہے'),
(4319, 42, 47, 'تم لوگ اپنے پروردگار کی بات کو قبول کرو قبل اس کے کہ وہ دن آجائے جو پلٹنے والا نہیں ہے اس دن تمہارے لئے کوئی پناہ گاہ بھی نہ ہوگی اور عذاب کا انکار کرنے کی ہمت بھی نہ ہوگی'),
(4320, 42, 48, 'اب بھی اگر یہ لوگ اعتراض کریں تو ہم نے آپ کو ان کانگہبان بناکر نہیں بھیجا ہے آپ کا فرض صرف پیغام پہنچا دینا تھا اوربس اور ہم جب کسی انسان کو رحمت کامزہ چکھاتے ہیں تو وہ اکڑ جاتا ہے اورجب اس کے اعمال ہی کے نتیجہ میں کوئی برائی پہنچ جاتی ہے تو بہت زیادہ ناشکری کرنے والا بن جاتاہے'),
(4321, 42, 49, 'بیشک آسمان و زمین کا اختیار صرف اللہ کے ہاتھوں میں ہے وہ جوکچھ چاہتا ہے پیدا کرتا ہے جس کو چاہتا ہے بیٹیاں عطا کرتا ہے اور جس کوچاہتا ہے بیٹے عطاکرتاہے'),
(4322, 42, 50, 'یا پھر بیٹے اور بیٹیاں دونوں کوجمع کردیتا ہے اور جس کو چاہتا ہے بانجھ ہی بنا دیتا ہے یقینا وہ صاحبِ علم بھی ہے اور صاحبِ قدرت و اختیاربھی ہے'),
(4323, 42, 51, 'اورکسی انسان کے لئے یہ بات نہیں ہے کہ اللہ اس سے کلام کرے مگر یہ کہ وحی کردے یا پس پردہ سے بات کر لے یا کوئی نمائندہ فرشتہ بھیج دے اور پھر وہ اس کی اجازت سے جو وہ چاہتا ہے وہ پیغام پہنچا دے کہ وہ یقینا بلند و بالا اور صاحبِ حکمت ہے'),
(4324, 42, 52, 'اور اسی طرح ہم نے آپ کی طرف اپنے حکم سے روح (قرآن) کی وحی کی ہے آپ کونہیں معلوم تھا کہ کتاب کیا ہے اورایمان کن چیزوں کا نام ہے لیکن ہم نے اسے ایک نور قرار دیا ہے جس کے ذریعہ اپنے بندوں میں جسے چاہتے ہیں اسے ہدایت دے دیتے ہیں اور بیشک آپ لوگوں کو سیدھے راستہ کی ہدایت کررہے ہیں'),
(4325, 42, 53, 'اس خدا کا راستہ جس کے اختیار میں زمین و آسمان کی تمام چیزیں ہیں اوریقینا اسی کی طرف تمام اُمورکی بازگشت ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حمۤ'),
(4327, 43, 2, 'اس روشن کتاب کی قسم'),
(4328, 43, 3, 'بیشک ہم نے اسے عربی قرآن قرار دیا ہے تاکہ تم سمجھ سکو'),
(4329, 43, 4, 'اور یہ ہمارے پاس لوح محفوظ میں نہایت درجہ بلند اور اَپراز حکمت کتاب ہے'),
(4330, 43, 5, 'اورکیا ہم تم لوگوں کو نصیحت کرنے سے صرف اس لئے کنارہ کشی اختیار کرلیں کہ تم زیادتی کرنے والے ہو'),
(4331, 43, 6, 'اورہم نے تم سے پہلے والی قوموں میں بھی کتنے ہی پیغمبر بھیج دیئے ہیں'),
(4332, 43, 7, 'اور ان میں سے کسی کے پاس کوئی نبی نہیں آیا مگر یہ کہ ان لوگوں نے ان کا مذاق اڑایا'),
(4333, 43, 8, 'تو ہم نے ان سے زیادہ زبردست لوگوں کو تباہ و برباد کردیا اور یہ مثال جاری ہوگئی'),
(4334, 43, 9, 'اور آپ ان سے سوال کریں گے کہ زمین و آسمان کوکس نے پیدا کیا ہے تویقینا یہی کہیں گے کہ ایک زبردست طاقت والی اور ذی علم ہستی نے خلق کیاہے'),
(4335, 43, 10, 'وہ ہی جس نے تمہارے لئے زمین کو گہوارہ بنایا ہے اور اس میں راستے قرار دیئے ہیں تاکہ تم راہ معلوم کرسکو'),
(4336, 43, 11, 'اور جس نے آسمان سے ایک معین مقدار میں پانی نازل کیا اورپھر ہم نے اس کے ذریعہ اَمردہ زمینوں کو زندہ بنادیا اسی طرح تم بھی زمین سے نکالے جاؤ گے'),
(4337, 43, 12, 'اور جسں نے تمام جوڑوں کو خلق کیا ہے اور تمہارے لئے کشتیوں اور جانوروں میں سواری کا سامان فراہم کیا ہے'),
(4338, 43, 13, 'تاکہ ان کی پشت پرسکون سے بیٹھ سکو اورپھر جب سکون سے بیٹھ جاؤ تو اپنے پروردگار کی نعمت کو یاد کرو اور کہو کہ پاک و بے نیاز ہے وہ خدا جس نے اس سواری کو ہمارے لئے م اَسخرّ کردیا ہے ورنہ ہم اس کوقابو میں لاسکنے والے نہیں تھے'),
(4339, 43, 14, 'اور بہرحال ہم اپنے پروردگار ہی کی بارگاہ میں پلٹ کر جانے والے ہیں'),
(4340, 43, 15, 'اور ان لوگوں نے پروردگار کے لئے اس کے بندوں میں سے بھی ایک جزئ (اولاد) قرار دیدیا کہ انسان یقینا بڑا کھلا ہوا ناشکرا ہے'),
(4341, 43, 16, 'سچ بتاؤ کیا خدا نے اپنی تمام مخلوقات میں سے اپنے لئے لڑکیوں کو منتخب کیا ہے اور تمہارے لئے لڑکوں کو پسند کیا ہے'),
(4342, 43, 17, 'اور جب ان میں سے کسی کو اسی لڑکی کی بشارت دی جاتی ہے جو مثال انہوں نے رحمان کے لئے بیان کی ہے تو اس کا چہرہ سیاہ ہوجاتا ہے اور غصہّ کے گھونٹ پینے لگتاہے'),
(4343, 43, 18, 'کیا جس کو زیورات میں پالا جاتا ہے اور وہ جھگڑے کے وقت صحیح بات بھی نہ کرسکے (وہی خدا کی اولاد ہے'),
(4344, 43, 19, 'اوران لوگوں نے ان ملائکہ کوجو رحمان کے بندے ہیںلڑکی قرار دیدیا ہے کیا یہ ان کی خلقت کے گواہ ہیں تو عنقریب ان کی گواہی لکھ لی جائے گی اور پھر اس کے بارے میں سوال کیا جائے گا'),
(4345, 43, 20, 'اوریہ کہتے ہیں کہ خدا چاہتا توہم ان کی پرستش نہ کرتے انہیں اس بات کا کوئی علم نہیں ہے - یہ صرف اندازوں سے بات کرتے ہیں'),
(4346, 43, 21, 'کیا ہم نے اس سے پہلے انہیں کوئی کتاب دی ہے جس سے یہ تمسّک کئے ہوئے ہیں'),
(4347, 43, 22, 'نہیں بلکہ ان کا کہنا صرف یہ ہے کہ ہم نے اپنے باپ داداکو ایک طریقہ پر پایا ہے اورہم ان ہی کے نقش قدم پر ہدایت پانے والے ہیں'),
(4348, 43, 23, 'اور اسی طرح ہم نے آپ سے پہلے کی بستی میں کوئی پیغمبر نہیں بھیجا مگر یہ کہ اس بستی کے خوشحال لوگوں نے یہ کہہ دیا کہ ہم نے اپنے باپ دادا کو ایک طریقہ پر پایا ہے اور ہم ان ہی کے نقش قدم کی پیروی کرنے والے ہیں'),
(4349, 43, 24, 'تو پیغمبر نے کہا کہ چاہے میں اس سے بہتر پیغام لے آؤں جس پر تم نے اپنے باپ دادا کو پایا ہے تو انہوں نے جواب دیا کہ ہم تمہارے پیغام کے ماننے والے نہیں ہیں'),
(4350, 43, 25, 'پھر ہم نے ان سے بدلہ لے لیا تو اب دیکھو کہ تکذیب کرنے والوں کا کیا انجام ہوا ہے'),
(4351, 43, 26, 'اور جب ابراہیم نے اپنے (مربی) باپ اور اپنی قوم سے کہا کہ میں تمہارے تمام معبودوں سے بری اور بیزار ہوں'),
(4352, 43, 27, 'علاوہ اس معبود کے کہ جس نے مجھے پیدا کیا ہے کہ وہی عنقریب مجھے ہدایت دینے والا ہے'),
(4353, 43, 28, 'اور انہوں نے اس پیغام کو اپنی نسل میں ایک کلمہ باقیہ قرار دے دیا کہ شاید وہ لوگ خدا کی طرف پلٹ آئیں'),
(4354, 43, 29, 'بلکہ ہم نے ان لوگوں کو اور ان کے بزرگوں کو برابر آرام دیا یہاں تک کہ ان کے پاس حق اور واضح رسول آگیا'),
(4355, 43, 30, 'اور جب حق آگیا تو کہنے لگے کہ یہ تو جادو ہے اور ہم اس کے منکر ہیں'),
(4356, 43, 31, 'اور یہ کہنے لگے کہ آخر یہ قرآن دونوں بستیوں (مکہ و طائف) کے کسی بڑے آدمی پر کیوں نہیں نازل کیا گیا ہے'),
(4357, 43, 32, 'تو کیا یہی لوگ رحمت پروردگار کو تقسیم کررہے ہیں حالانکہ ہم نے ہی ان کے درمیان معیشت کو زندگانی دنیا میں تقسیم کیا ہے اور بعض کو بعض سے اونچا بنایا ہے تاکہ ایک دوسرے سے کام لے سکیں اور رحمت پروردگار ان کے جمع کئے ہوئے مال و متاع سے کہیں زیادہ بہتر ہے'),
(4358, 43, 33, 'اور اگر ایسا نہ ہوتا کہ تمام لوگ ایک ہی قوم ہوجائیں گے تو ہم رحمٰن کا انکار کرنے والوںکے لئے ان کے گھر کی چھتیں اور سیڑھیاں جن پر یہ چڑھتے ہیں سب کو چاند ی کا بنادیتے'),
(4359, 43, 34, 'اور ان کے گھر کے دروازے اور وہ تخت جن پر وہ تکیہ لگا کر بیٹھتے ہیں'),
(4360, 43, 35, 'اور سونے کے بھی . لیکن یہ سب صرف زندگانی دنیا کی لّذت کا سامان ہے اور آخرت پروردگار کے نزدیک صرف صاحبانِ تقویٰ کے لئے ہے'),
(4361, 43, 36, 'اور جو شخص بھی اللہ کے ذکر کی طرف سے اندھا ہوجائے گا ہم اس کے لئے ایک شیطان مقرر کردیں گے جو اس کا ساتھی اور ہم نشین ہوگا'),
(4362, 43, 37, 'اور یہ شیاطین ان لوگوں کو راستہ سے روکتے رہتے ہیں اور یہ یہی خیال کرتے ہیں کہ یہ ہدایت یافتہ ہیں'),
(4363, 43, 38, 'یہاں تک کہ جب ہمارے پاس آئیں گے تو کہیں گے کہ اے کاش ہمارے اور ان کے درمیان مشرق و مغرب کا فاصلہ ہوتا یہ تو بڑا بدترین ساتھی نکلا'),
(4364, 43, 39, 'اور یہ ساری باتیں آج تمہارے کام آنے والی نہیں ہیں کہ تم سب عذاب میں برابر کے شریک ہو کہ تم نے بھی ظلم کیا ہے'),
(4365, 43, 40, 'پیغمبر کیا آپ بہرے کو سنا سکتے ہیں یا اندھے کو راستہ دکھاسکتے ہیں یا اسے ہدایت دے سکتے ہیں جو ضلال مبین میں مبتلا ہوجائے'),
(4366, 43, 41, 'پھر ہم یا تو آپ کو دنیا سے اٹھالیں گے تو ہمیں تو ان سے بہرحال بدلہ لینا ہے'),
(4367, 43, 42, 'یا پھر عذاب آپ کو دکھا کر ہی نازل کریں گے کہ ہم اس کا بھی اختیار رکھنے والے ہیں'),
(4368, 43, 43, 'لہذا آپ اس حکم کو مضبوطی سے پکڑے رہیں جس کی وحی کی گئی ہے کہ یقینا آپ بالکل سیدھے راستہ پر ہیں'),
(4369, 43, 44, 'اور یہ قرآن آپ کے لئے اور آپ کی قوم کے لئے نصیحت کا سامان ہے اور عنقریب تم سب سے باز اَپرس کی جائے گی'),
(4370, 43, 45, 'اور آپ ان رسولوں سے سوال کریں جنہیں آپ سے پہلے بھیجا گیا ہے کیا ہم نے رحمٰن کے علاوہ بھی خدا قرار دیئے ہیں جن کی پرستش کی جائے'),
(4371, 43, 46, 'اور ہم نے موسٰی علیھ السّلام کو اپنی نشانیوں کے ساتھ فرعون اور اس کے روسائ قوم کی طرف بھیجا تو انہوں نے کہا کہ میں رب العالمین کی طرف سے رسول ہوں'),
(4372, 43, 47, 'لیکن جب ہماری نشانیوں کو پیش کیا تو وہ سب مضحکہ اڑانے لگے'),
(4373, 43, 48, 'اور ہم انہیں جو بھی نشانی دکھلاتے تھے وہ پہلے والی نشانی سے بڑھ کر ہی ہوتی تھی اور پھر انہیں عذاب کی گرفت میں لے لیا کہ شاید اسی طرح راستہ پر واپس آجائیں'),
(4374, 43, 49, 'اور ان لوگوں نے کہا کہ اے جادوگر (موسٰی) اپنے رب سے ہمارے بارے میں اس بات کی دعا کر جس بات کا تجھ سے وعدہ کیا گیا ہے تو ہم یقینا راستہ پر آجائیں گے'),
(4375, 43, 50, 'لیکن جب ہم نے عذاب کو دور کردیا تو انہوں نے عہد کو توڑ دیا'),
(4376, 43, 51, 'اور فرعون نے اپنی قوم سے پکار کر کہا اے قوم کیا یہ ملک مصر میرا نہیں ہے اور کیا یہ نہریں جو میرے قدموں کے نیچے جاری ہیں یہ سب میری نہیں ہیں پھر تمہیں کیوں نظر نہیں آرہا ہے'),
(4377, 43, 52, 'کیا میں اس شخص سے بہتر نہیں ہوں جو پست حیثیت کا آدمی ہے اور صاف بول بھی نہیں پاتا ہے'),
(4378, 43, 53, 'پھر کیوں اس کے اوپر سونے کے کنگن نہیں نازل کئے گئے اور کیوں اس کے ساتھ ملائکہ جمع ہوکر نہیں آئے'),
(4379, 43, 54, 'پس فرعون نے اپنی قوم کو سبک سر بنادیا اور انہوں نے اس کی اطاعت کرلی کہ وہ سب پہلے ہی سے فاسق اور بدکار تھے'),
(4380, 43, 55, 'پھر جب ان لوگوں نے ہمیں غضب ناک کردیا تو ہم نے ان سے بدلہ لے لیا اور پھر ان ہی سب کو اکٹھا غرق کردیا'),
(4381, 43, 56, 'پھر ہم نے انہیں گیا گزرا اور بعد والوں کے لئے ایک نمونہ عبرت بنادیا'),
(4382, 43, 57, 'اور جب ابن مریم کو مثال میں پیش کیا گیا تو آپ کی قوم شور مچانے لگی'),
(4383, 43, 58, 'اور کہنے لگے کہ ہمارے خدا بہتر ہیں یا وہ اور لوگوں نے ان کی مثال صرف ضد میں پیش کی تھی اور یہ سب صرف جھگڑا کرنے والے تھے'),
(4384, 43, 59, 'ورنہ عیسٰی علیھ السّلام ایک بندے تھے جن پر ہم نے نعمتیں نازل کیں اور انہیں بنی اسرائیل کے لئے اپنی قدرت کا ایک نمونہ بنادیا'),
(4385, 43, 60, 'اور اگر ہم چاہتے تو تمہارے بدلے ملائکہ کو زمین میں بسنے والا قرار دے دیتے'),
(4386, 43, 61, 'اور بیشک یہ قیامت کی واضح دلیل ہے لہٰذا اس میں شک نہ کرو اور میرا اتباع کرو کہ یہی سیدھا راستہ ہے'),
(4387, 43, 62, 'اور خبردار شیطان تمہیں راہ حق سے روک نہ دے کہ وہ تمہارا کھلا ہوا دشمن ہے'),
(4388, 43, 63, 'اور جب عیسٰی علیھ السّلام ان کے پاس معجزات لے کر آئے تو انہوں نے کہا کہ میں تمہارے پاس حکمت لے کر آیا ہوں اور اس لئے کہ بعض ان مسائل کی وضاحت کردوں جن میں تمہارے درمیان اختلاف ہے لہٰذا خدا سے ڈرو اور میری اطاعت کرو'),
(4389, 43, 64, 'اللہ ہی میرا اور تمہارا پروردگار ہے اور اسی کی عبادت کرو کہ یہی صراط مستقیم ہے'),
(4390, 43, 65, 'تو اقوام نے آپس میں اختلاف شروع کردیا افسوس ان کے حال پر ہے کہ جنہوں نے ظلم کیا کہ ان کے لئے دردناک دن کا عذاب ہے'),
(4391, 43, 66, 'کیا یہ لوگ صرف اس بات کا انتظار کررہے ہیں کہ اچانک قیامت آجائے اور انہیں اس کا شعور بھی نہ ہوسکے'),
(4392, 43, 67, 'آج کے دن صاحبانِ تقویٰ کے علاوہ تمام دوست ایک دوسرے کے دشمن ہوجائیں گے'),
(4393, 43, 68, 'میرے بندو آج تمہارے لئے نہ خوف ہے اور نہ تم پر حزن و ملال طاری ہوگا'),
(4394, 43, 69, 'یہی وہ لوگ ہیں جنہوں نے ہماری نشانیوں پر ایمان قبول کیا ہے اور ہمارے اطاعت گزار ہوگئے ہیں'),
(4395, 43, 70, 'اب تم سب اپنی بیویوں سمیت اعزاز و احترام کے ساتھ جنّت میں داخل ہوجاؤ'),
(4396, 43, 71, 'ان کے گرد سونے کی رکابیوں اور پیالیوں کا دور چلے گا اور وہاں ان کے لئے وہ تمام چیزیں ہوں گی جن کی دل میں خواہش ہو اور جو آنکھوں کو بھلی لگیں اور تم اس میں ہمیشہ رہنے والے ہو'),
(4397, 43, 72, 'اور یہی وہ جنّت ہے جس کا تمہیں ان اعمال کی بنا پر وارث بنایا گیا ہے جو تم انجام دیا کرتے تھے'),
(4398, 43, 73, 'اس میں تمہارے لئے بہت سے میوے ہیں جن میں سے تم کھاؤ گے'),
(4399, 43, 74, 'بیشک مجرمین عذابِ جہّنم میں ہمیشہ رہنے والے ہیں'),
(4400, 43, 75, 'ان سے عذاب منقطع نہیں ہوگا اور وہ مایوسی کے عالم میں وہاں رہیں گے'),
(4401, 43, 76, 'اور ہم نے ان پر کوئی ظلم نہیں کیا ہے یہ تو خود ہی اپنے اوپر ظلم کرنے والے تھے'),
(4402, 43, 77, 'اور وہ آواز دیں گے کہ اے مالک اگر تمہارا پروردگار ہمیںموت دے دے تو بہت اچھا ہو تو جواب ملے گا کہ تم اب یہیں رہنے والے ہو'),
(4403, 43, 78, 'یقینا ہم تمہارے پاس حق لے کر آئے لیکن تمہاری اکثریت حق کو ناپسند کرنے والی ہے'),
(4404, 43, 79, 'کیا انہوں نے کسی بات کا محکم ارادہ کرلیا ہے تو ہم بھی یہ کام جانتے ہیں'),
(4405, 43, 80, 'یا ان کا خیال یہ ہے کہ ہم ان کے راز اور خفیہ باتوں کو نہیں سن سکتے ہیں تو ہم کیا ہمارے نمائندے سب کچھ لکھ رہے ہیں'),
(4406, 43, 81, 'آپ کہہ دیجئے کہ اگر رحمٰن کے کوئی فرزند ہوتا تو میںسب سے پہلا عبادت گزار ہوں'),
(4407, 43, 82, 'وہ آسمان و زمین اور عرش کا مالک ان کی باتوں سے پاک اور منّزہ ہے'),
(4408, 43, 83, 'انہیں ان کے حال پر چھوڑ دیجئے باتیں بناتے رہیں اور کھیل تماشے میں لگے رہیں یہاں تک کہ اس دن کا سامنا کریں جس کا وعدہ دیا جارہا ہے'),
(4409, 43, 84, 'اور وہی وہ ہے جو آسمان میں بھی خدا ہے اور زمین میں بھی خدا ہے اور وہ صاحبِ حکمت بھی ہے اور ہر شے سے باخبر بھی ہے'),
(4410, 43, 85, 'اور بابرکت ہے وہ جس کے لئے آسمان و زمین اور اس کے مابین کا ملک ہے اور اسی کے پاس قیامت کا بھی علم ہے اور اسی کی طرف تم سب واپس کئے جاؤ گے'),
(4411, 43, 86, 'اور اس کے علاوہ جنہیں یہ لوگ پکارتے ہیں وہ سفارش کا بھی اختیار نہیں رکھتے ہیں...._ مگر وہ جو سمجھ بوجھ کر حق کی گواہی دینے والے ہیں'),
(4412, 43, 87, 'اور اگر آپ ان سے سوال کریں گے کہ خود ان کا خالق کون ہے تو کہیں گے کہ اللہ .... تو پھر یہ کدھر بہکے جارہے ہیں'),
(4413, 43, 88, 'اور اسی کو اس قول کا بھی علم ہے کہ خدایا یہ وہ قوم ہے جو ایمان لانے والی نہیں ہے'),
(4414, 43, 89, 'لہٰذا ان سے منہ موڑ لیجئے اور سلامتی کا پیغام دے دیجئے پھر عنقریب انہیں سب کچھ معلوم ہوجائے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حمۤ'),
(4416, 44, 2, 'روشن کتاب کی قسم'),
(4417, 44, 3, 'ہم نے اس قرآن کو ایک مبارک رات میں نازل کیا ہے ہم بیشک عذاب سے ڈرانے والے تھے'),
(4418, 44, 4, 'اس رات میں تمام حکمت و مصلحت کے امور کا فیصلہ کیا جاتا ہے'),
(4419, 44, 5, 'یہ ہماری طرف کا حکم ہوتا ہے اور ہم ہی رسولوں کے بھیجنے والے ہیں'),
(4420, 44, 6, 'یہ آپ کے پروردگار کی رحمت ہے اور یقینا وہ بہت سننے والا اور جاننے والا ہے'),
(4421, 44, 7, 'وہ آسمان و زمین اور اس کے مابین کا پروردگار ہے اگر تم یقین کرنے والے ہو'),
(4422, 44, 8, 'اس کے علاوہ کوئی خدا نہیں ہے . وہی حیات عطا کرنے والا ہے اور وہی موت دینے والا ہے - وہی تمہارا بھی پروردگار ہے اور تمہارے گزشتہ بزرگوں کا بھی پروردگار ہے'),
(4423, 44, 9, 'لیکن یہ لوگ شک کے عالم میں کھیل تماشہ کررہے ہیں'),
(4424, 44, 10, 'لہٰذا آپ اس دن کا انتظار کیجئے جب آسمان واضح قسم کا دھواں لے کر آجائے گا'),
(4425, 44, 11, 'جو تمام لوگوں کو ڈھانک لے گا کہ یہی دردناک عذاب ہے'),
(4426, 44, 12, 'تب سب کہیں گے کہ پروردگار اس عذاب کو ہم سے دور کردے ہم ایمان لے آنے والے ہیں'),
(4427, 44, 13, 'بھلا ان کی قسمت میں نصیحت کہاں جب کہ ان کے پاس واضح پیغام والا رسول بھی آچکا ہے'),
(4428, 44, 14, 'اور انہوں نے اس سے منہ پھیرلیا اور کہا کہ یہ سکھایا پڑھایا ہوا دیوانہ ہے'),
(4429, 44, 15, 'خیر ہم تھوڑی دیر کے لئے عذاب کو ہٹالیتے ہیں لیکن تم پھر وہی کرنے والے ہو جو کررہے ہو'),
(4430, 44, 16, 'ایک دن آئے گا جب ہم سخت قسم کی گرفت کریں گے کہ ہم یقینا بدلہ لینے والے بھی ہیں'),
(4431, 44, 17, 'اور ہم نے ان سے پہلے فرعون کی قوم کو آزمایا جب ان کے پاس ایک محترم پیغمبر آیا'),
(4432, 44, 18, 'کہ اللہ کے بندوں کو میرے حوالے کردو میں تمہارے لئے ایک امانت دار پیغمبر ہوں'),
(4433, 44, 19, 'اور خدا کے سامنے اونچے نہ بنو میں تمہارے پاس بہت واضح دلیل لے کر آیا ہوں'),
(4434, 44, 20, 'اور میں اپنے اور تمہارے رب کی پناہ چاہتا ہوں کہ تم مجھے سنگسار کردو'),
(4435, 44, 21, 'اور اگر تم ایمان نہیں لاتے ہو تو مجھ سے الگ ہوجاؤ'),
(4436, 44, 22, 'پھر انہوں نے اپنے رب سے رَعا کی کہ یہ قوم بڑی مجرم قوم ہے'),
(4437, 44, 23, 'تو ہم نے کہا کہ ہمارے بندوں کو لے کر راتوں رات چلے جاؤ کہ تمہارا پیچھا کیا جانے والا ہے'),
(4438, 44, 24, 'اور دریا کو اپنے حال پر ساکن چھوڑ کر نکل جاؤ کہ یہ لشکر غرق کیا جانے والا ہے'),
(4439, 44, 25, 'یہ لوگ کتنے ہی باغات اور چشمے چھوڑ گئے'),
(4440, 44, 26, 'اور کتنی ہی کھیتیاں اور عمدہ مکانات چھوڑ گئے'),
(4441, 44, 27, 'اور وہ نعمتیں جن میں مزے اُڑا رہے تھےیہی انجام ہوتا ہے اور ہم نے سب کا وارث دوسری قوم کو بنادیا'),
(4442, 44, 28, 'اور وہ نعمتیں جن میں مزے اُڑا رہے تھےیہی انجام ہوتا ہے اور ہم نے سب کا وارث دوسری قوم کو بنادیا'),
(4443, 44, 29, 'پھر تو ان پر نہ آسمان رویا اور نہ زمین اور نہ انہیں مہلت ہی دی گئی'),
(4444, 44, 30, 'اور ہم نے بنی اسرائیل کو رسوا کن عذاب سے بچالیا'),
(4445, 44, 31, 'فرعون کے شر سے جو زیادتی کرنے والوں میں بھی سب سے اونچا تھا'),
(4446, 44, 32, 'اور ہم نے بنی اسرائیل کو تمام عالمین میں سمجھ بوجھ کر انتخاب کیا ہے'),
(4447, 44, 33, 'اور انہیں ایسی نشانیاں دی ہیں جن میں کھلا ہوا امتحان پایا جاتا ہے'),
(4448, 44, 34, 'بیشک یہ لوگ یہی کہتے ہیں'),
(4449, 44, 35, 'کہ یہ صرف پہلی موت ہے اور بس اس کے بعد ہم اٹھائے جانے والے نہیں ہیں'),
(4450, 44, 36, 'اور اگر تم سچے ہو تو ہمارے باپ دادا کو قبروں سے اٹھا کر لے آؤ'),
(4451, 44, 37, 'بھلا یہ لوگ زیادہ بہتر ہیں یا قوم تّبع اور ان سے پہلے والے افراد جنہیں ہم نے اس لئے تباہ کردیا کہ یہ سب مجرم تھے'),
(4452, 44, 38, 'اور ہم نے زمین و آسمان اور اس کی درمیانی مخلوقات کو کھیل تماشہ کرنے کے لئے نہیں پیدا کیا ہے'),
(4453, 44, 39, 'ہم نے انہیں صرف حق کے ساتھ پیدا کیا ہے لیکن ان کی اکثریت اس امر سے بھی جاہل ہے'),
(4454, 44, 40, 'بیشک فیصلہ کا دن ان سب کے اٹھائے جانے کا مقررہ وقت ہے'),
(4455, 44, 41, 'جس دن کوئی دوست دوسرے دوست کے کام آنے والا نہیں ہے اور نہ ان کی کوئی مدد کی جائے گی'),
(4456, 44, 42, 'علاوہ اس کے جس پر خدا رحم کرے کہ بیشک وہ بڑا بخشنے والا اور مہربان ہے'),
(4457, 44, 43, 'بیشک آخرت میں ایک تھوہڑ کا درخت ہے'),
(4458, 44, 44, 'جو گناہگاروں کی غذا ہے'),
(4459, 44, 45, 'وہ پگھلے ہوئے تانبے کی مانند پیٹ میں جوش کھائے گا'),
(4460, 44, 46, 'جیسے گرم پانی جوش کھاتا ہے'),
(4461, 44, 47, 'فرشتوں کو حکم ہوگا کہ اسے پکڑو اور بیچوں بیچ جہّنم تک لے جاؤ'),
(4462, 44, 48, 'پھر اس کے سر پر کھولتے ہوئے پانی کا عذاب انڈیل دو'),
(4463, 44, 49, 'کہو کہ اب اپنے کئے کا مزہ چکھو کہ تم تو بڑے صاحب هعزّت اور محترم کہے جاتے تھے'),
(4464, 44, 50, 'یہی وہ عذاب ہے جس میں تم شک پیدا کررہے تھے'),
(4465, 44, 51, 'بیشک وہ صاحبانِ تقویٰ محفوظ مقام پر ہوں گے'),
(4466, 44, 52, 'باغات اور چشموں کے درمیان'),
(4467, 44, 53, 'وہ ریشم کی باریک اور موٹی پوشاک پہنے ہوئے ایک دوسرے کے سامنے بیٹھے ہوں گے'),
(4468, 44, 54, 'ایسا ہی ہوگا اور ہم بڑی بڑی آنکھوں والی حوروں سے ان کے جوڑے لگادیں گے'),
(4469, 44, 55, 'وہ وہاں ہر قسم کے میوے سکون کے ساتھ طلب کریں گے'),
(4470, 44, 56, 'اور وہاں پہلی موت کے علاوہ کسی موت کا مزہ نہیں چکھنا ہوگا اور خدا انہیں جہّنم کے عذاب سے محفوظ رکھے گا'),
(4471, 44, 57, 'یہ سب آپ کے پروردگار کا فضل و کرم ہے اور یہی انسان کے لئے سب سے بڑی کامیابی ہے'),
(4472, 44, 58, 'پس ہم نے اس قرآن کو آپ کی زبان سے آسان کردیا ہے کہ شاید یہ لوگ نصیحت حاصل کرلیں'),
(4473, 44, 59, 'پھر آپ انتظار کریں اور یہ لوگ بھی انتظار کر ہی رہے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حمۤ'),
(4475, 45, 2, 'اس کتاب کی تنزیل اس خدا کی طرف سے ہے جو صاحبِ عزّت بھی ہے اور صاحب هحکمت بھی ہے'),
(4476, 45, 3, 'بیشک آسمانوں اور زمینوں میں صاحبانِ هایمان کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(4477, 45, 4, 'اور خود تمہاری خلقت میں بھی اور جن جانورں کو وہ پھیلاتا رہتا ہے ان میں بھی صاحبانِ یقین کے لئے بہت سی نشانیاں ہیں'),
(4478, 45, 5, 'اور رات دن کی رفت و آمد میں اور جو رزق خدا نے آسمان سے نازل کیا ہے جس کے ذریعہ سے اَمردہ زمینوں کو زندہ بنایا ہے اور ہواؤں کے چلنے میں اس قوم کے لئے نشانیاں پائی جاتی ہیں جو عقل رکھنے والی ہے'),
(4479, 45, 6, 'یہ اللہ کی آیتیں ہیں جن کی حق کے ساتھ تلاوت کی جارہی ہے تو اللہ اور اس کی آیتوں کے بعد یہ کس بات پر ایمان لانے والے ہیں'),
(4480, 45, 7, 'بیشک ہر جھوٹے گنہگار کے حال پر افسوس ہے'),
(4481, 45, 8, 'جو تلاوت کی جانے والی آیات کو سنتا ہے اور پھر اکڑ جاتا ہے جیسے سنا ہی نہیں ہے تو اسے دردناک عذاب کی بشارت دیدیجئے'),
(4482, 45, 9, 'اور اسے جب بھی ہماری کسی نشانی کا علم ہوتا ہے تو اس کا مذاق اڑاتا ہے بیشک یہی وہ لوگ ہیں جن کے لئے رسوا کن عذاب ہے'),
(4483, 45, 10, 'ان کے پیچھے جہنّم ہے اور انہوں نے جو کچھ کمایا ہے اور جن لوگوں کو خدا کو چھوڑ کر سرپرست بنایا ہے کوئی کام آنے والا نہیں ہے اور ان کے لئے بہت بڑا عذاب ہے'),
(4484, 45, 11, 'یہ قرآن ایک ہدایت ہے اور جن لوگوں نے آیااُ خدا کا انکار کیا ہے ان کے لئے سخت قسم کا دردناک عذاب ہوگا'),
(4485, 45, 12, 'اللہ ہی نے تمہارے لئے دریا کو لَسخّر کیا ہے کہ اس کے حکم سے کشتیاں چل سکیں اور تم اس کے فضل و کرم کو تلاش کرسکو اور شاید اس کا شکریہ بھی ادا کرسکو'),
(4486, 45, 13, 'اور اسی نے تمہارے لئے زمین و آسمان کی تمام چیزوں کو لَسّخر کردیا ہے بیشمَ اس میں غور و فکر کرنے والی قوم کے لئے نشانیاں پائی جاتی ہیں'),
(4487, 45, 14, 'آپ صاحبانِ ایمان سے کہہ دیں کہ وہ خدائی دنوں کی توقع نہ رکھنے والوں سے درگزر کریں تاکہ خدا قوم کو ان کے اعمال کا مکمل بدلہ دے سکے'),
(4488, 45, 15, 'جو نیک کام کرے گا وہ اپنے فائدہ کے لئے کرے گا اور جو برائی کرے گا وہ اپنے ہی نقصان کے لئے کرے گا اس کے بعد تم سب پروردگار کی طرف پلٹائے جاؤ گے'),
(4489, 45, 16, 'اور یقینا ہم نے بنی اسرائیل کو کتابً حکومت اور نبوت عطا کی ہے اور انہیں پاکیزہ رزق دیا ہے اور انہیں تمام عالمین پر فضیلت دی ہے'),
(4490, 45, 17, 'اور انہیں اپنے امر کی کھلی ہوئی نشانیاں عطا کی ہیں پھر ان لوگوں نے علم آنے کے بعد آپس کی ضد میں اختلاف کیا تو یقینا تمہارا پروردگار روزِ قیامت ان کے درمیان ان تمام باتوں کا فیصلہ کردے گا جن میں یہ اختلاف کرتے رہے ہیں'),
(4491, 45, 18, 'پھر ہم نے آپ کو اپنے حکم کے واضح راستہ پر لگادیا لہٰذا آپ اسی کا اتباع کریں اور خبرار جاہلوں کی خواہشات کا اتباع نہ کریں'),
(4492, 45, 19, 'یہ لوگ خدا کے مقابلہ میں ذرہ برابر کام آنے والے نہیں ہیں اور ظالمین آپس میں ایک دوسرے کے دوست ہیں تو اللہ صاحبانِ تقویٰ کا سرپرست ہے'),
(4493, 45, 20, 'یہ لوگوں کے لئے روشنی کا مجموعہ اور یقین کرنے والی قوم کے لئے ہدایت اور رحمت ہے'),
(4494, 45, 21, 'کیا برائی اختیار کرلینے والوں نے یہ خیال کرلیا ہے کہ ہم انہیں ایمان لانے والوں اور نیک عمل کرنے والوں کے برابر قرار دے دیں گے کہ سب کی موت و حیات ایک جیسی ہو یہ ان لوگوں نے نہایت بدترین فیصلہ کیا ہے'),
(4495, 45, 22, 'اور اللہ نے آسمان و زمین کو حق کے ساتھ پیدا کیا ہے اور اس لئے بھی کہ ہر نفس کو اس کے اعمال کا بدلہ دیا جاسکے اور یہاں کسی پر ظلم نہیں کیا جائے گا'),
(4496, 45, 23, 'کیا آپ نے اس شخص کو بھی دیکھا ہے جس نے اپنی خواہش ہی کو خدا بنالیا ہے اور خدا نے اسی حالت کو دیکھ کر اسے گمراہی میں چھوڑ دیا ہے اور اس کے کان اور دل پر مہر لگادی ہے اور اس کی آنکھ پر پردے پڑے ہوئے ہیں اور خدا کے بعد کون ہدایت کرسکتا ہے کیا تم اتنا بھی غور نہیں کرتے ہو'),
(4497, 45, 24, 'اور یہ لوگ کہتے ہیں کہ یہ صرف زندگانی دنیا ہے اسی میں مرتے ہیں اور جیتے ہیں اور زمانہ ہی ہم کو ہلاک کردیتا ہے اور انہیں اس بات کا کوئی علم نہیں ہے کہ یہ صرف ان کے خیالات ہیں اور بس'),
(4498, 45, 25, 'اور جب ان کے سامنے ہماری کھلی ہوئی آیات کی تلاوت ہوتی ہے تو ان کی دلیل صرف یہ ہوتی ہے کہ اگر تم سچے ہو تو ہمارے باپ دادا کو زندہ کرکے لے آؤ'),
(4499, 45, 26, 'آپ کہہ دیجئے کہ خدا ہی تم کو بھی زندہ رکھے ہے پھر ایک دن موت دے گا اور آخر میں سب کو روز قیامت جمع کرے گا اور اس میں کوئی شک نہیں ہے لیکن اکثر لوگ اس بات کو بھی نہیں سمجھتے ہیں'),
(4500, 45, 27, 'اور اللہ ہی کے لئے زمین و آسمان کا ملک ہے اور جس دن قیامت قائم ہوگی اس دن اہل ه باطل کو واقعا خسارہ ہوگا'),
(4501, 45, 28, 'اور آپ ہر قوم کو گھٹنے کے بل بیٹھا ہوا دیکھیں گے اور سب کو ان کے نامئہ اعمال کی طرف بلایا جائے گا کہ آج تمہیں تمہارے اعمال کا بدلہ دیا جائے گا'),
(4502, 45, 29, 'یہ ہماری کتاب (نامئہ اعمال) ہے جو حق کے ساتھ بولتی ہے اور ہم اس میں تمہارے اعمال کو برابر لکھوا رہے تھے'),
(4503, 45, 30, 'اب جو لوگ ایمان لائے اور انہوں نے نیک اعمال کئے انہیں پروردگار اپنی رحمت میں داخل کرلے گا کہ یہی سب سے نمایاں کامیابی ہے'),
(4504, 45, 31, 'اور جن لوگوں نے کفر اختیار کیا تو کیا تمہارے سامنے ہماری آیات کی تلاوت نہیں ہورہی تھی لیکن تم نے اکڑ سے کام لیا اور بیشک تم ایک مجرم قوم تھے'),
(4505, 45, 32, 'اور جب یہ کہا گیا کہ اللہ کا وعدہ سچا ہے اور قیامت میں کوئی شک نہیں ہے تو تم نے کہہ دیا کہ ہم تو قیامت نہیں جانتے ہیں ہم اسے ایک خیالی بات سمجھتے ہیں اور ہم اس کا یقین کرنے والے نہیں ہیں'),
(4506, 45, 33, 'اور ان کے لئے ان کے اعمال کی برائیاں ثابت ہوگئیں اور انہیں اسی عذاب نے گھیر لیا جس کا وہ مذاق اڑایا کرتے تھے'),
(4507, 45, 34, 'اور ان سے کہا گیا کہ ہم تمہیں آج اسی طرح نظر انداز کردیں گے جس طرح تم نے آج کے دن کی ملاقات کو بھلادیا تھا اور تم سب کا انجام جہّنم ہے اور تمہارا کوئی مددگار نہیں ہے'),
(4508, 45, 35, 'یہ سب اس لئے ہے کہ تم نے آیات الہٰی کا مذاق بنایا تھا اور تمہیں زندگانی دنیا نے دھوکے میں رکھا تھا تو آج یہ لوگ عذاب سے باہرنہیں نکالے جائیں گے اور انہیں معافی مانگنے کا موقع بھی نہیں دیا جائے گا'),
(4509, 45, 36, 'ساری حمد اسی خدا کے لئے ہے جو آسمان و زمین اور تمام عالمین کا پروردگار اور مالک ہے'),
(4510, 45, 37, 'اسی کے لئے آسمان و زمین میں بزرگی اور کبریائی ہے اور وہی صاحبِ عزّت اور حکمت والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حمۤ'),
(4512, 46, 2, 'یہ خدائے عزیز و حکیم کی نازل کی ہوئی کتاب ہے'),
(4513, 46, 3, 'ہم نے آسمان و زمین اور ان کے درمیان کی تمام مخلوقات کو حق کے ساتھ اور ایک مقررہ مدّت کے ساتھ پیدا کیا ہے اور جن لوگوں نے کفر اختیار کرلیا وہ ان باتوں سے کنارہ کش ہوگئے ہیں جن سے انہیں ڈرایا گیا ہے'),
(4514, 46, 4, 'تو آپ کہہ دیں کہ کیا تم نے ان لوگوں کو دیکھا ہے جنہیں خدا کو چھوڑ کر پکارتے ہو ذ را مجھے بھی دکھلاؤ کہ انہوں نے زمین میں کیا پیدا کیا ہے یا ان کی آسمان میں کیا شرکت ہے - پھر اگر تم سچے ہو تو اس سے پہلے کی کوئی کتاب یا علم کا کوئی بقیہ ہمارے سامنے پیش کرو'),
(4515, 46, 5, 'اور اس سے زیادہ گمراہ کون ہے جو خدا کو چھوڑ کر ان کو پکارتا ہے جو قیامت تک اس کی آواز کا جواب نہیں دے سکتے ہیں اور ان کی آواز کی طرف سے غافل بھی ہیں'),
(4516, 46, 6, 'اور جب سارے لوگ قیامت میں محشور ہوں گے تو یہ معبود ان کے دشمن ہوجائیں گے اور ان کی عبادت کا انکار کرنے لگیں گے'),
(4517, 46, 7, 'اور جب ان کے سامنے ہماری روشن آیات کی تلاوت کی جاتی ہے تو یہ کفار حق کے آنے کے بعد اس کے بارے میں یہی کہتے ہیں کہ یہ کِھلا ہوا جادو ہے'),
(4518, 46, 8, 'تو کیا یہ لوگ یہ کہتے ہیں کہ رسول نے افترا کیا ہے تو آپ کہہ دیجئے کہ اگر میں نے افترا کیا ہے تو تم میرے کام آنے والے نہیں ہو اور خدا خوب جانتا ہے کہ تم اس کے بارے میں کیا کیا باتیں کرتے ہو اور میرے اور تمہارے درمیان گواہی کے لئے وہی کافی ہے اور وہی بہت بخشنے والا اور مہربان ہے'),
(4519, 46, 9, 'آپ کہہ دیجئے کہ میں کوئی نئے قسم کا رسول نہیں ہوں اور مجھے نہیں معلوم کہ میرے اور تمہارے ساتھ کیا برتاؤ کیا جائے گا میں تو صرف وحی الہٰی کا اتباع کرتا ہوں اور صرف واضح طور پر عذاب الٰہی سے ڈرانے والا ہوں'),
(4520, 46, 10, 'کہہ دیجئے کہ تمہارا کیا خیال ہے اگر یہ قرآن اللہ کی طرف سے ہے اور تم نے اس کا انکار کردیا جب کہ بنی اسرائیل کاایک گواہ ایسی ہی بات کی گواہی دے چکا ہے اور وہ ایمان بھی لایا ہے اور پھر بھی تم نے غرور سے کام لیا ہے بیشک اللہ ظالمین کی ہدایت کرنے والا نہیں ہے'),
(4521, 46, 11, 'اور یہ کفار ایمان والوں سے کہتے ہیں کہ اگر یہ دین بہتر ہوتا تو یہ لوگ ہم سے آگے اس کی طرف نہ دوڑ پڑتے اور جب ان لوگوں نے خود ہدایت نہیں حاصل کی تو اب کہتے ہیں کہ یہ بہت پرانا جھوٹ ہے'),
(4522, 46, 12, 'اور اس سے پہلے موسٰی علیھ السّلامکی کتاب تھی جو رہنما اور رحمت تھی اور یہ کتاب عربی زبان میں سب کی تصدیق کرنے والی ہے تاکہ ظلم کرنے والوں کو عذاب الہٰی سے ڈرائے اور یہ نیک کرداروں کے لئے مجسمئہ بشارت ہے'),
(4523, 46, 13, 'بیشک جن لوگوں نے اللہ کو اپنا رب کہا اور اسی پر جمے رہے ان کے لئے نہ کوئی خوف ہے اور نہ وہ رنجیدہ ہونے والے ہیں'),
(4524, 46, 14, 'یہی لوگ درحقیقت جنّت والے ہیں اور اسی میں ہمیشہ رہنے والے ہیں کہ یہی ان کے اعمال کی حقیقی جزا ہے'),
(4525, 46, 15, 'اور ہم نے انسان کو اس کے ماں باپ کے ساتھ نیک برتاؤ کرنے کی نصیحت کی کہ اس کی ماں نے بڑے رنج کے ساتھ اسے شکم میں رکھا ہے اور پھر بڑی تکلیف کے ساتھ پیدا کیا ہے اور اس کے حمل اور دودھ بڑھائی کا کل زمانہ تیس مہینے کا ہے یہاں تک کہ جب وہ توانائی کو پہنچ گیا اور چالیس برس کا ہوگیا تو اس نے دعا کی کہ پروردگار مجھے توفیق دے کہ میں تیری اس نعمت کا شکریہ ادا کروں جو تو نے مجھے اور میرے والدین کو عطا کی ہے اور ایسا نیک عمل کروں کہ تو راضی ہوجائے اور میری ذریت میں بھی صلاح و تقویٰ قرار دے کہ میں تیری ہی طرف متوجہ ہوں اور تیرے فرمانبردار بندوں میں ہوں'),
(4526, 46, 16, 'یہی وہ لوگ ہیں جن کے بہترین اعمال کو ہم قبول کرتے ہیں اور ان کی برائیوں سے درگزر کرتے ہیں یہ اصحاب جنّت میں ہیں اور یہ خدا کا وہ وعدہ ہے جو ان سے برابر کیا جارہا تھا'),
(4527, 46, 17, 'اور جس نے اپنے ماں باپ سے یہ کہا کہ تمہارے لئے حیف ہے کہ تم مجھے اس بات سے ڈراتے ہو کہ میں دوبارہ قبر سے نکالا جاؤں گا حالانکہ مجھ سے پہلے بہت سی قومیں گزر چکی ہیں اور وہ دونوں فریاد کررہے تھے کہ یہ بڑی افسوس ناک بات ہے بیٹا ایمان لے آ - خدا کا وعدہ بالکل سچا ہے تو کہنے لگا کہ یہ سب پرانے لوگوں کے افسانے ہیں'),
(4528, 46, 18, 'یہی وہ لوگ ہیں جن پر عذاب ثابت ہوچکا ہے ان امّتوں میں جو انسان و جّنات میں ان سے پہلے گزر چکی ہیں کہ بیشک یہ سب گھاٹے میں رہنے والے ہیں'),
(4529, 46, 19, 'اور ہر ایک کے لئے اس کے اعمال کے مطابق درجات ہوں گے اور یہ اس لئے کہ خدا ان کے اعمال کا پورا پورا بدلہ دیدے اور ان پر کسی طرح کا ظلم نہیں کیا جائے گا'),
(4530, 46, 20, 'اور جس دن کفار کو جہنمّ کے سامنے پیش کیا جائے گا کہ تم نے اپنے سارے مزے دنیاہی کی زندگی میں لے لئے اور وہاں آرام کرلیا تو آج ذلّت کے عذاب کی سزادی جائے گی کہ تم روئے زمین میں بلاوجہ اکڑ رہے تھے اور اپنے پروردگار کے احکام کی نافرمانی کررہے تھے'),
(4531, 46, 21, 'اور قوم عاد کے بھائی ہود کو یاد کیجئے کہ انہوں نے اپنی قوم کو احقاف میں ڈرایا .... اور ان کے قبل و بعد بہت سے پیغمبر علیھ السّلام گزر چکے ہیں .... کہ خبردار اللہ کے علاوہ کسی کی عبادت نہ کرو میں تمہارے بارے میں ایک بڑے سخت دن کے عذاب سے خوفزدہ ہوں'),
(4532, 46, 22, 'ان لوگوں نے کہا کہ کیا تم اسی لئے آئے ہو کہ ہمیں ہمارے خداؤں سے منحرف کردو تو اس عذاب کو لے آؤ جس سے ہمیں ڈرا رہے ہو اگر اپنی بات میں سچے ہو'),
(4533, 46, 23, 'انہوں نے کہا کہ علم تو بس خدا کے پاس ہے اور میں اسی کے پیغام کو پہنچادیتا ہوں جو میرے حوالے کیا گیا ہے لیکن میں تمہیں بہرحال جاہل قوم سمجھ رہا ہوں'),
(4534, 46, 24, 'اس کے بعد جب ان لوگوں نے بادل کو دیکھا کہ ان کی وادیوں کی طرف چلا آرہا ہے تو کہنے لگے کہ یہ بادل ہمارے اوپر برسنے والا ہے.... حالانکہ یہ وہی عذاب ہے جس کی تمہیں جلدی تھی یہ وہی ہوا ہے جس کے اندر دردناک عذاب ہے'),
(4535, 46, 25, 'یہ حکم خدا سے ہر شے کو تباہ و برباد کردے گی. نتیجہ یہ ہوا کہ سوائے مکانات کے کچھ نہ نظر آیا اور ہم اسی طرح مجرم قوم کو سزا دیا کرتے ہیں'),
(4536, 46, 26, 'اور یقینا ہم نے ان کو وہ اختیارات دیئے تھے جو تم کو نہیں دیئے ہیں اور ان کے لئے کان, آنکھ, دل سب قرار دیئے تھے لیکن نہ انہیں کانوں نے کوئی فائدہ پہنچایا اور نہ آنکھوں اور دلوں نے کہ وہ آیات الٰہی کا انکار کرنے والے افراد تھے اور انہیں عذاب نے گھیر لیا جس کا وہ مذاق اڑایا کرتے تھے'),
(4537, 46, 27, 'اور یقینا ہم نے تمہارے گرد بہت سی بستیوں کو تباہ کردیا ہے اور اپنی نشانیوں کو پلٹ پلٹ کر دکھلایا ہے کہ شاید یہ حق کی طرف واپس آجائیں'),
(4538, 46, 28, 'تو وہ لوگ ان کے کام کیوں نہیں آئے جن کو انہوں نے خدا کو چھوڑ کر وسیلہ تقرب کے طور پر خدا بنالیا تھا بلکہ وہ تو غائب ہی ہوگئے اور یہ ان لوگوں کا وہ جھوٹ اور افترا ہے جو وہ برابر تیار کیا کرتے تھے'),
(4539, 46, 29, 'اور جب ہم نے جنات میں سے ایک گروہ کو آپ کی طرف متوجہ کیا کہ قرآن سنیں تو جب وہ حاضر ہوئے تو آپس میں کہنے لگے کہ خاموشی سے سنو پھر جب تلاوت تمام ہوگئی تو فورا پلٹ کر اپنی قوم کی طرف ڈرانے والے بن کر آگئے'),
(4540, 46, 30, 'کہنے لگے کہ اے قوم والو ہم نے ایک کتاب کو صَنا ہے جو موسٰی علیھ السّلام کے بعد نازل ہوئی ہے یہ اپنے پہلے والی کتابوں کی تصدیق کرنے والی ہے اور حق و انصاف اور سیدھے راستہ کی طرف ہدایت کرنے والی ہے'),
(4541, 46, 31, 'قوم والو اللہ کی طرف دعوت دینے والے کی آواز پر لبیک کہو اور اس پر ایمان لے آؤ تاکہ اللہ تمہارے گناہوں کو بخش دے اور تمہیں دردناک عذاب سے پناہ دے دے'),
(4542, 46, 32, 'اور جو بھی داعی الٰہیۤ کی آواز پر لبیک نہیں کہے گا وہ روئے زمین پر خدا کو عاجز نہیں کرسکتا ہے اور اس کے لئے خدا کے علاوہ کوئی سرپرست بھی نہیں ہے بیشک یہ لوگ کھلی ہوئی گمراہی میں مبتلا ہیں'),
(4543, 46, 33, 'کیا انہوں نے نہیں دیکھا کہ جس خدا نے آسمان و زمین کو پیدا کیا ہے اور وہ ان کی تخلیق سے عاجز نہیں تھا وہ اس بات پر بھی قادر ہے کہ مفِدوں کو زندہ کردے کہ یقینا وہ ہر شے پر قدرت رکھنے والا ہے'),
(4544, 46, 34, 'اور جس دن یہ کفاّر جہّنم کے سامنے پیش کئے جائیں گے کہ کیا یہ حق نہیں ہے تو سب کہیں گے کہ بیشک پروردگار کی قسم یہ سب حق ہے تو ارشاد ہوگا کہ اب عذاب کا مزہ چکھو کہ تم پہلے اس کا انکار کررہے تھے'),
(4545, 46, 35, 'پیغمبر آپ اسی طرح صبر کریں جس طرح پہلے کے صاحبان ه عزم رسولوں نے صبر کیا ہے اور عذاب کے لئے جلدی نہ کریں یہ لوگ جس دن بھی اس عذاب کو دیکھیں گے جس سے ڈرایا جارہا ہے تو ایسا محسوس کریں گے جیسے دنیا میں ایک دن کی ایک گھڑی ہی ٹہرے ہیں یہ قرآن اتمام حجت ہے اور کیا فاسقوں کے علاوہ کسی اور قوم کو ہلاک کیا جائے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'جن لوگوں نے کفر کیا اور لوگوں کو راسِ خدا سے روکا خدا نے ان کے اعمال کو برباد کردیا'),
(4547, 47, 2, 'اور جن لوگوں نے ایمان اختیار کیا اور نیک اعمال کئے اور جو کچھ محمد پر نازل کیا گیا ہے اور پروردگار کی طرف سے برحق بھی ہے اس پر ایمان لے آئے تو خدا نے ان کی برائیوں کو دور کردیا اور ان کے حالات کی اصلاح کردی'),
(4548, 47, 3, 'یہ اس لئے کہ کفار نے باطل کا اتباع کیا ہے اور صاحبانِ ایمان نے اپنے پروردگار کی طرف سے آنے والے حق کا اتباع کیا ہے اور خدا اسی طرح لوگوں کے لئے مثالیں بیان کرتا ہے'),
(4549, 47, 4, 'پس جب کفار سے مقابلہ ہو تو ان کی گردنیں اڑادو یہاں تک کہ جب زخموں سے چور ہوجائیں تو ان کی مشکیں باندھ لو پھر اس کے بعد چاہے احسان کرکے چھوڑ دیا جائے یا فدیہ لے لیا جائے یہاں تک کہ جنگ اپنے ہتھیار رکھ دے - یہ یاد رکھنا اور اگر خدا چاہتا تو خود ہی ان سے بدلہ لے لیتا لیکن وہ ایک کو دوسرے کے ذریعہ آزمانا چاہتا ہے اور جو لوگ اس کی راہ میں قتل ہوئے وہ ان کے اعمال کو ضائع نہیں کرسکتا ہے'),
(4550, 47, 5, 'وہ عنقریب انہیں منزل تک پہنچادے گا اور ان کی حالت سنوار دے گا'),
(4551, 47, 6, 'وہ انہیں اس جنّت میں داخل کرے گا جو انہیں پہلے سے پہنچوا چکا ہے'),
(4552, 47, 7, 'ایمان والو اگر تم اللہ کی مدد کرو گے تو اللہ بھی تمہاری مدد کرے گا اور تمہیں ثابت قدم بنادے گا'),
(4553, 47, 8, 'اور جن لوگوں نے کفر اختیار کیا ان کے واسطے ڈگمگاہٹ ہے اور ان کے اعمال برباد ہیں'),
(4554, 47, 9, 'یہ اس لئے کہ انہوں نے خدا کے نازل کئے ہوئے احکام کو برا سمجھا تو خدا نے بھی ان کے اعمال کو ضائع کردیا'),
(4555, 47, 10, 'تو کیا ان لوگوں نے زمین میں سیر نہیں کی ہے کہ دیکھتے کہ ان سے پہلے والوں کا کیا انجام ہوا ہے بیشک اللہ نے انہیں تباہ و برباد کردیا ہے اور کفاّر کے لئے بالکل ایسی ہی سزا مقرر ہے'),
(4556, 47, 11, 'یہ سب اس لئے ہے کہ اللہ صاحبانِ ایمان کا مولا اور سرپرست ہے اور کافروں کا کوئی پرسانِ حال نہیں ہے'),
(4557, 47, 12, 'بیشک جو لوگ ایمان لائے اور انہوں نے نیک اعمال انجام دیئے خدا انہیں ایسے باغات میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی اور جن لوگوں نے کفر اختیار کیا وہ مزے کررہے ہیں اور اسی طرح کھارہے ہیں جس طرح جانور کھاتے ہیں اور ان کا آخری ٹھکانا جہّنم ہی ہے'),
(4558, 47, 13, 'اور کتنی ہی بستیاں تھیں جو تمہاری اس بستی سے کہیں زیادہ طاقتور تھیں جس نے تمہیں نکال دیا ہے جب ہم نے انہیں ہلاک کردیا تو کوئی مدد کرنے والا بھی نہ پیدا ہوا'),
(4559, 47, 14, 'تو کیا جس کے پاس پروردگار کی طرف سے کھلی ہوئی دلیل موجود ہے وہ اس کے مثل ہوسکتاہے جس کے لئے اس کے بدترین اعمال سنوار دیئے گئے ہیں اور پھر ان لوگوں نے اپنی خواہشات کا اتباع کرلیا ہے'),
(4560, 47, 15, 'اس جنّت کی صفت جس کا صاحبانِ تقویٰ سے وعدہ کیا گیا ہے یہ ہے کہ اس میں ایسے پانی کی نہریں ہیں جس میں کسی طرح کی بو نہیں ہے اور کچھ نہریں دودھ کی بھی ہیں جن کا مزہ بدلتا ہی نہیں ہے اور کچھ نہریں شراب کی بھی ہیں جن میں پینے والوں کے لئے لذّت ہے اور کچھ نہریں صاف و شفاف شہد کی ہیں اور ان کے لئے ہر طرح کے میوے بھی ہیں اور پروردگار کی طرف سے مغفرت بھی ہے تو کیا یہ متقی افراد ان کے جیسے ہوسکتے ہیں جو ہمیشہ جہّنم میں رہنے والے ہیں اور جنہیں گرما گرم پانی پلایا جائے گا جس سے آنتیں ٹکڑے ٹکڑے ہوجائیں گی'),
(4561, 47, 16, 'اور ان میں سے کچھ افراد ایسے بھی ہیں جو آپ کی باتیں بظاہر غور سے سنتے ہیں اس کے بعد جب آپ کے پاس سے باہر نکلتے ہیں تو جن لوگوں کو علم دیا گیا ہے ان سے کہتے ہیں کہ انہوں نے ابھی کیا کہا تھا یہی وہ لوگ ہیں جن کے دلوں پر خدا نے مہر لگادی ہے اور انہوں نے اپنی خواہشات کا اتباع کرلیا ہے'),
(4562, 47, 17, 'اور جن لوگوں نے ہدایت حاصل کرلی خدا نے ان کی ہدایت میں اضافہ کردیا اور ان کو مزید تقویٰ عنایت فرما دیا'),
(4563, 47, 18, 'پھر کیا یہ لوگ قیامت کا انتظار کررہے ہیں کہ وہ اچانک ان کے پاس آجائے جب کہ اس کی علامتیں ظاہر ہوگئی ہیں تو اگر وہ آبھی گئی تو یہ کیا نصیحت حاصل کریں گے'),
(4564, 47, 19, 'تو یہ سمجھ لو کہ اللہ کے علاوہ کوئی خدا نہیں ہے اور اپنے اور ایماندار مردوں اور عورتوں کے لئے استغفار کرتے رہو کہ اللہ تمہارے چلنے پھرنے اور ٹہرنے سے خوب باخبر ہے'),
(4565, 47, 20, 'اور جو لوگ ایمان لے آئے ہیں وہ یہ کہتے ہیں کہ آخر جہاد کے بارے میں سورہ کیوں نہیں نازل ہوتا اور جب سورہ نازل ہوگیا اور اس میں جہاد کا ذکر کردیا گیا تو آپ نے دیکھا کہ جن کے دلوں میں مرض تھا وہ آپ کی طرف اس طرح دیکھتے رہ گئے جیسے موت کی سی غشی طاری ہوگئی ہو تو ان کے واسطے ویل اور افسوس ہے'),
(4566, 47, 21, 'ان کے حق میں بہترین بات اطاعت اور نیک گفتگو ہے پھر جب جنگ کا معاملہ طے ہوجائے تو اگر خدا سے اپنے کئے وعدہ پر قائم رہیں تو ان کے حق میں بہت بہتر ہے'),
(4567, 47, 22, 'تو کیا تم سے کچھ بعید ہے کہ تم صاحبِ اقتدار بن جاؤ تو زمین میں فساد برپا کرو اور قرابتداروں سے قطع تعلقات کرلو'),
(4568, 47, 23, 'یہی وہ لوگ ہیں جن پر خدا نے لعنت کی ہے اور ان کے کانوں کو بہرا کردیا ہے اور ان کی آنکھوں کو اندھا بنادیا ہے'),
(4569, 47, 24, 'تو کیا یہ لوگ قرآن میں ذرا بھی غور نہیں کرتے ہیں یا ان کے دلوں پر قفل پڑے ہوئے ہیں'),
(4570, 47, 25, 'بیشک جو لوگ ہدایت کے واضح ہوجانے کے بعد الٹے پاؤں پلٹ گئے شیطان نے ان کی خواہشات کو آراستہ کردیا ہے اور انہیں خوب ڈھیل دے دی ہے'),
(4571, 47, 26, 'یہ اس لئے کہ ان لوگوں نے خدا کی نازل کی ہوئی باتوں کو ناپسند کرنے والوں سے یہ کہا کہ ہم بعض مسائل میں تمہاری ہی اطاعت کریں گے اور اللہ ان کی راز کی باتوں سے خوب باخبر ہے'),
(4572, 47, 27, 'پھر اس وقت کیا ہوگا جب ملائکہ انہیں دنیا سے اٹھائیں گے اور ان کے چہروں اور پشت پر مسلسل مارتے جائیں گے'),
(4573, 47, 28, 'یہ اس لئے کہ انہوں نے ان باتوں کا اتباع کیا ہے جو خدا کو ناراض کرنے والی ہیں اور اس کی مرضی کو ناپسند کیا ہے تو خدا نے بھی ان کے اعمال کو برباد کرکے رکھ دیا ہے'),
(4574, 47, 29, 'کیا جن لوگوں کے دلوں میں بیماری پائی جاتی ہے ان کا خیال یہ ہے کہ خدا ان کے دلوں کے کینوں کو باہر نہیں لائے گا'),
(4575, 47, 30, 'اور ہم چاہتے تو انہیں دکھلا دیتے اور آپ چہرہ کے آثار ہی سے پہچان لیتے اور ان کی گفتگو کے انداز سے تو بہرحال پہچان ہی لیں گے اور اللہ تم سب کے اعمال سے خوب باخبر ہے'),
(4576, 47, 31, 'اور ہم یقینا تم سب کا امتحان لیں گے تاکہ یہ دیکھیں کہ تم میں جہاد کرنے والے اور صبر کرنے والے کون لوگ ہیں اور اس طرح تمہارے حالات کو باقاعدہ جانچ لیں'),
(4577, 47, 32, 'بیشک جن لوگوں نے کفر اختیار کرلیا اور لوگوں کو خدا کی راہ سے روکا اور ہدایت کے واضح ہوجانے کے بعد بھی پیغمبر سے جھگڑا کیا وہ اللہ کو کوئی نقصان نہیں پہنچاسکتے ہیں اور اللہ عنقریب ان کے اعمال کو بالکل برباد کردے گا'),
(4578, 47, 33, 'ایمان والو اللہ کی اطاعت کرو اور رسول کی اطاعت کرو اور خبردار اپنے اعمال کو برباد نہ کرو'),
(4579, 47, 34, 'بیشک جن لوگوں نے کفر اختیار کیا اور خدا کی راہ میں رکاوٹ ڈالی اور پھر حالت کفر ہی میں مرگئے تو خدا انہیں ہرگز معاف نہیں کرے گا'),
(4580, 47, 35, 'لہٰذا تم ہمت نہ ہارو اور دشمن کو صلح کی دعوت نہ دو اور تم سربلند ہو اور اللہ تمہارے ساتھ ہے اور وہ ہرگز تمہارے اعمال کے ثواب کو کم نہیں کرے گا'),
(4581, 47, 36, 'یہ زندگانی دنیا تو صرف ایک کھیل تماشہ ہے اور اگر تم نے ایمان اور تقویٰ اختیار کرلیا تو خدا تمہیں مکمل اجر عطا کرے گا اور تم سے تمہارا مال طلب نہیں کرے گا'),
(4582, 47, 37, 'وہ اگر طلب بھی کرے اور اصرار بھی کرے تو تم بخل ہی کرو گے اور خدا تمہارے کینوں کو خود ہی ظاہر کردے گا'),
(4583, 47, 38, 'ہاں ہاں تم وہی لوگ ہو جنہیں راہ خدا میں خرچ کرنے کے لئے بلایا جاتا ہے تو تم میں سے بعض لوگ بخل کرنے لگتے ہیں اور جو لوگ بخل کرتے ہیں وہ اپنے ہی حق میں بخل کرتے ہیں اور خدا سب سے بے نیاز ہے تم ہی سب اس کے فقیر اور محتاج ہو اور اگر تم منہ پھیر لو گے تو وہ تمہارے بدلے دوسری قوم کو لے آئے گا جو اس کے بعد تم جیسے نہ ہوں گے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'بیشک ہم نے آپ کو کھلی ہوئی فتح عطا کی ہے'),
(4585, 48, 2, 'تاکہ خدا آپ کے اگلے پچھلے تمام الزامات کو ختم کردے اور آپ پر اپنی نعمت کو تمام کردے اور آپ کو سیدھے راستہ کی ہدایت د ے د ے'),
(4586, 48, 3, 'اور زبردست طریقہ سے آپ کی مدد کرے'),
(4587, 48, 4, 'وہی خدا ہے جس نے مومنین کے دلوں میں سکون نازل کیا ہے تاکہ ان کے ایمان میں مزید اضافہ ہوجائے اور اللہ ہی کے لئے زمین و آسمان کے سارے لشکر ہیں اور وہی تمام باتوں کا جاننے والا اور صاحبِ حکمت ہے'),
(4588, 48, 5, 'تاکہ مومن مرد اور مومنہ عورتوں کو ان جنتوں میں داخل کردے جن کے نیچے نہریں جاری ہیں وہ ان ہی میں ہمیشہ رہیں اور ان کی برائیوں کو ان سے دور کردے اور یہی اللہ کے نزدیک عظیم ترین کامیابی ہے'),
(4589, 48, 6, 'اور منافق مرد, منافق عورتیں اور مشرک مرد و عورت جو خدا کے بارے میں برے برے خیالات رکھتے ہیں ان سب پر عذاب نازل کرے ان کے سر عذاب کی گردش ہے اور ان پر اللہ کا غضب ہے خدا نے ان پر لعنت کی ہے اور ان کے لئے جہّنم کو مہیاّ کیا ہے جو بدترین انجام ہے'),
(4590, 48, 7, 'اور اللہ ہی کے لئے زمین و آسمان کے سارے لشکر ہیں اور وہ صاحبِ عزت بھی ہے اور صاحبِ حکمت بھی ہے'),
(4591, 48, 8, 'پیغمبر ہم نے آپ کو گواہی دینے والا بشارت دینے والا اور عذاب الٰہٰی سے ڈرانے والا بناکر بھیجا ہے'),
(4592, 48, 9, 'تاکہ تم سب اللہ اور اس کے رسول پر ایمان لے آؤ اور اس کی مدد کرو اور اس کا احترام کرو اور صبح و شام اللہ کی تسبیح کرتے رہو'),
(4593, 48, 10, 'بیشک جو لوگ آپ کی بیعت کرتے ہیں وہ درحقیقت اللہ کی بیعت کرتے ہیں اور ان کے ہاتھوں کے اوپر اللہ ہی کا ہاتھ ہے اب اس کے بعد جو بیعت کو توڑ دیتا ہے وہ اپنے ہی خلاف اقدام کرتا ہے اور جو عہد الٰہٰی کو پورا کرتا ہے خدا عنقریب اسی کو اجر عظیم عطا کرے گا'),
(4594, 48, 11, 'عنقریب یہ پیچھے رہ جانے والے گنوار آپ سے کہیں گے کہ ہمارے اموال اور اولاد نے مصروف کرلیا تھا لہٰذا آپ ہمارے حق میں استغفار کردیں. یہ اپنی زبان سے وہ کہہ رہے ہیں جو یقینا ان کے دل میں نہیں ہے تو آپ کہہ دیجئے کہ اگر خدا تمہیں نقصان پہنچانا چاہے یا فائدہ ہی پہنچانا چاہے تو کون ہے جو اس کے مقابلہ میں تمہارے امور کا اختیار رکھتا ہے .حقیقت یہ ہے کہ اللہ تمہارے اعمال سے خوب باخبر ہے'),
(4595, 48, 12, 'اصل میں تمہارا خیال یہ تھا کہ رسول اور صاحبانِ ایمان اب اپنے گھر والوں تک پلٹ کر نہیں آسکتے ہیں اور اس بات کو تمہارے دلوں میں خوب سجا دیا گیا تھا اور تم نے بدگمانی سے کام لیا اور تم ہلاک ہوجانے والی قوم ہو'),
(4596, 48, 13, 'اور جو بھی خدا و رسول پر ایمان نہ لائے گا ہم نے ایسے کافرین کے لئے جہّنم کا انتظام کر رکھا ہے'),
(4597, 48, 14, 'اور اللہ ہی کے لئے زمین و آسمان کا ملک ہے وہ جس کو چاہتا ہے بخش دیتا ہے اور جس پر چاہتا ہے عذاب کرتا ہے اور اللہ بہت بخشنے والا مہربان ہے'),
(4598, 48, 15, 'عنقریب یہ پیچھے رہ جانے والے تم سے کہیں گے جب تم مال غنیمت لینے کے لئے جانے لگو گے کہ اجازت دو ہم بھی تمہارے ساتھ چلے چلیں یہ چاہتے ہیں کہ اللہ کے کلام کو تبدیل کر دیں تو تم کہہ دو کہ تم لو گ ہمارے ساتھ نہیں آسکتے ہو اللہ نے یہ بات پہلے سے طے کردی ہے پھر یہ کہیں گے کہ تم لوگ ہم سے حسد رکھتے ہو حالانکہ حقیقت یہ ہے کہ یہ لوگ بات کو بہت کم سمجھ پاتے ہیں'),
(4599, 48, 16, 'آپ ان پیچھے رہ جانے والوں سے کہہ دیں کہ عنقریب تمہیں ایک ایسی قوم کی طرف بلایا جائے گا جو انتہائی سخت جنگجو قوم ہوگی کہ تم ان سے جنگ ہی کرتے رہو گے یا وہ مسلمان ہوجائیں گے تو اگر تم خدا کی اطاعت کرو گے تو وہ تمہیں بہترین اجر عنایت کرے گا اور اگر پھر منہ پھیر لو گے جس طرح پہلے کیا تھا تو تمہیں دردناک عذاب کے ذریعہ سزا دے گا'),
(4600, 48, 17, 'اندھے آدمی کے لئے کوئی گناہ نہیں ہے اور لنگڑے آدمی کے لئے بھی کوئی گناہ نہیں ہے اورمریض کی بھی کوئی ذمہ داری نہیں ہے اور جو اللہ اور اس کے رسول کی اطاعت کرے گا خدا اس کو ایسی جنتوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی اور جو روگردانی کرے گا وہ اسے دردناک عذاب کی سزا دے گا'),
(4601, 48, 18, 'یقینا خدا صاحبانِ ایمان سے اس وقت راضی ہوگیا جب وہ درخت کے نیچے آپ کی بیعت کررہے تھے پھر اس نے وہ سب کچھ دیکھ لیا جو ان کے دلوں میں تھا تو ان پر سکون نازل کردیا اور انہیں اس کے عوض قریبی فتح عنایت کردی'),
(4602, 48, 19, 'اور بہت سے منافع بھی دے دیئے جنہیں وہ حاصل کریں گے اور اللہ ہر ایک پر غالب آنے والا اور صاحب هحکمت ہے'),
(4603, 48, 20, 'اس نے تم سے بہت سے فوائد کا وعدہ کیا ہے جنہیں تم حاصل کرو گے پھر اس غنیمت هخیبر کو فورا عطا کردیا اور لوگوں کے ہاتھوں کو تم سے روک دیا اور تاکہ یہ صاحبانِ ایمان کے لئے ایک قدرت کی نشانی بنے اور وہ تمہیں سیدھے راستہ کی ہدایت دے دے'),
(4604, 48, 21, 'اور دوسری غنیمتیں جن پر تم قادر نہیں ہوسکے خدا ان پر بھی حاوی ہے اور خدا تو ہر شے پر قدرت رکھنے والا ہے'),
(4605, 48, 22, 'اور اگر یہ کفاّر تم سے جنگ کرتے تو یقینا منہ پھیر کر بھاگ جاتے اور پھر انہیں کوئی سرپرست اور مددگار نصیب نہ ہوتا'),
(4606, 48, 23, 'یہ اللہ کی ایک سنّت ہے جو پہلے بھی گزر چکی ہے اور تم اللہ کے طریقہ میں ہرگز کوئی تبدیلی نہیں پاؤ گے'),
(4607, 48, 24, 'وہی خدا ہے جس نے کفاّر کے ہاتھوں کو تم سے اور تمہارے ہاتھوں کو ان سے سرحد مکہ کے اندر تمہارے فتح پاجانے کے بعد بھی روک دیا اور اللہ تمہارے اعمال سے خوب باخبر ہے'),
(4608, 48, 25, 'یہی وہ لوگ ہیں جنہوں نے کفر اختیار کیا اور تم کو مسجد الحرام میں داخل ہونے سے روک دیا اور قربانی کے جانوروں کو روک دیا کہ وہ اپنی منزل پر پہنچنے سے رکے رہے اور اگر صاحبِ ایمان مرد اور باایمان عورتیں نہ ہوتیں جن کو تم نہیں جانتے تھے اور نادانستگی میں انہیں بھی پامال کر ڈالنے کا خطرہ تھا اور اس طرح تمہیں لاعلمی کی وجہ سے نقصان پہنچ جاتا تو تمہیں روکا بھی نہ جاتا - روکا اس لئے تاکہ خدا جسے چاہے اسے اپنی رحمت میں داخل کرلے کہ یہ لوگ الگ ہوجاتے تو ہم کفّار کو دردناک عذاب میں مبتلا کردیتے'),
(4609, 48, 26, 'یہ اس وقت کی بات ہے جب کفار نے اپنے دلوں میں زمانہ جاہلیت جیسی ضد قرار دے لی تھی کہ تم کو مکہ میں داخل نہ ہونے دیں گے تو اللہ نے اپنے رسول اور صاحبانِ ایمان پر سکون نازل کردیا اور انہیں کلمہ تقویٰ پر قائم رکھا اور وہ اسی کے حقدار اور اہل بھی تھے اور اللہ تو ہر شے کا جاننے والا ہے'),
(4610, 48, 27, 'بیشک خدا نے اپنے رسول کو بالکل سچا خوب دکھلایا تھا کہ خدا نے چاہا تو تم لوگ مسجد الحرام میں امن و سکون کے ساتھ سر کے بال منڈا کر اور تھوڑے سے بال کاٹ کر داخل ہوگے اور تمہیں کسی طرح کا خوف نہ ہوگا تو اسے وہ بھی معلوم تھا جو تمہیں نہیں معلوم تھا تو اس نے فتح مکہّ سے پہلے ایک قریبی فتح قرار دے دی'),
(4611, 48, 28, 'وہی وہ خدا ہے جس نے اپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجا تاکہ اسے تمام ادیان عالم پر غالب بنائے اور گواہی کے لئے بس خدا ہی کافی ہے'),
(4612, 48, 29, 'محمد(ص) اللہ کے رسول ہیں اور جو لوگ ان کے ساتھ ہیں وہ کفار کے لئے سخت ترین اور آپس میںانتہائی رحمدل ہیں تم انہیں دیکھوگے کہ بارگاہ ِاحدیّت میںسر خم کئے ہوئے سجدہ ریز ہیںاور اپنے پروردگارسے فضل وکرم اور اس کی خوشنودی کے طلب گار ہیں ،کثرتِ سجود کی بنا پر ان کے چہروں پر سجدہ کے نشانات پائے جاتے ہیںیہی ان کی مثال تورےت میں ہے اور یہی ان کی صفت انجیل میں ہے جیسے کوئی کھیتی ہو جو پہلے سوئی نکالے پھر اسے مضبوط بنائے پھر وہ موٹی ہو جائے اور پھر اپنے پیروں پر کھڑی ہو جائے کہ کاشتکاروں کو خوش کرنے لگے تاکہ ان کے ذریعہ کفار کو جلایا جائے اور اللہ نے صاحبانِ ایمان وعمل صالح سے مغفرت اور عظیم اجر کا وعدہ کیا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'ایمان والو خبردار خدا و ر رسول کے سامنے اپنی بات کو آگے نہ بڑھاؤ اور اللہ سے ڈرو بیشک اللہ ہر بات کا سننے والا اور جاننے والا ہے'),
(4614, 49, 2, 'ایمان والو خبردار اپنی آواز کو نبی کی آواز پر بلند نہ کرنا اور ان سے اس طرح بلند آواز میں بات بھی نہ کرنا جس طرح آپس میں ایک دوسرے کو پکارتے ہو کہیں ایسا نہ ہو کہ تمہارے اعمال برباد ہوجائیں اور تمہیں اس کا شعور بھی نہ ہو'),
(4615, 49, 3, 'بیشک جو لوگ رسول اللہ کے سامنے اپنی آواز کو دھیما رکھتے ہیں یہی وہ لوگ ہیں جن کے دلوں کو خدا نے تقویٰ کے لئے آزمالیا ہے اور ان ہی کے لئے مغفرت اور اجر عظیم ہے'),
(4616, 49, 4, 'بیشک جو لوگ آپ کو حجروں کے پیچھے سے پکارتے ہیں ان کی اکثریت کچھ نہیں سمجھتی ہے'),
(4617, 49, 5, 'اور اگر یہ اتنا صبر کرلیتے کہ آپ نکل کر باہر آجاتے تو یہ ان کے حق میں زیادہ بہتر ہوتا اور اللہ بہت بخشنے والا اور مہربان ہے'),
(4618, 49, 6, 'ایمان والو اگر کوئی فاسق کوئی خبر لے کر آئے تو اس کی تحقیق کرو ایسا نہ ہو کہ کسی قوم تک ناواقفیت میں پہنچ جاؤ اور اس کے بعد اپنے اقدام پر شرمندہ ہونا پڑے'),
(4619, 49, 7, 'اور یاد رکھو کہ تمہارے درمیان خدا کا رسول موجود ہے یہ اگر بہت سی باتوں میں تمہاری بات مان لیتا تو تم زحمت میں پڑ جاتے لیکن خدا نے تمہارے لئے ایمان کو محبوب بنادیا ہے اور اسے تمہارے دلوں میں آراستہ کردیا ہے اور کفر, فسق اور معصیت کو تمہارے لئے ناپسندیدہ قرار دے دیا ہے اور درحقیقت یہی لوگ ہدایت یافتہ ہیں'),
(4620, 49, 8, 'یہ اللہ کا فضل اور اس کی نعمت ہے اور اللہ سب کچھ جاننے والا بھی ہے اور صاحب هحکمت بھی ہے'),
(4621, 49, 9, 'اور اگر مومنین کے دو گروہ آپس میں جھگڑا کریں تو تم سب ان کے درمیان صلح کراؤ اس کے بعد اگر ایک دوسرے پر ظلم کرے تو سب مل کر اس سے جنگ کرو جو زیادتی کرنے والا گروہ ہے یہاں تک کہ وہ بھی حکم خدا کی طرف واپس آجائے پھر اگر پلٹ آئے تو عدل کے ساتھ اصلاح کردو اور انصاف سے کام لو کہ خدا انصاف کرنے والوں کو دوست رکھتا ہے'),
(4622, 49, 10, 'مومنین آپس میں بالکل بھائی بھائی ہیں لہٰذا اپنے بھائیوں کے درمیان اصلاح کرو اور اللہ سے ڈرتے رہو کہ شاید تم پر رحم کیا جائے'),
(4623, 49, 11, 'ایمان والو خبردار کوئی قوم دوسری قوم کا مذاق نہ اڑائے کہ شاید وہ اس سے بہتر ہو اور عورتوں کی بھی کوئی جماعت دوسری جماعت کا مسخرہ نہ کرے کہ شاید وہی عورتیں ان سے بہتر ہوں اور آپس میں ایک دوسرے کو طعنے بھی نہ دینا اور اِرے اِرے القاب سے یاد بھی نہ کرنا کہ ایمان کے بعد بدکاریً کا نام ہی بہت اِرا ہے اور جو شخص بھی توبہ نہ کرے تو سمجھو کہ یہی لوگ درحقیقت ظالمین ہیں'),
(4624, 49, 12, 'ایمان والو اکثر گمانوں سے اجتناب کرو کہ بعض گمان گناہ کا درجہ رکھتے ہیں اور خبردار ایک دوسرے کے عیب تلاش نہ کرو اور ایک دوسرے کی غیبت بھی نہ کرو کہ کیا تم میں سے کوئی اس بات کو پسند کرے گا کہ اپنے مفِدہ بھائی کا گوشت کھائے یقینا تم اسے اِرا سمجھو گے تو اللہ سے ڈرو کہ بیشک اللہ بہت بڑا توبہ کا قبول کرنے والا اور مہربان ہے'),
(4625, 49, 13, 'انسانو ہم نے تم کو ایک مرد اور ایک عورت سے پیدا کیا ہے اور پھر تم میں شاخیں اور قبیلے قرار دیئے ہیں تاکہ آپس میں ایک دوسرے کو پہچان سکو بیشک تم میں سے خدا کے نزدیک زیادہ محترم وہی ہے جو زیادہ پرہیزگارہے اور اللہ ہر شے کا جاننے والا اور ہر بات سے باخبر ہے'),
(4626, 49, 14, 'یہ بدوعرب کہتے ہیں کہ ہم ایمان لے آئے ہیں تو آپ کہہ دیجئے کہ تم ایمان نہیں لائے بلکہ یہ کہو کہ اسلام لائے ہیں کہ ابھی ایمان تمہارے دلوں میں داخل نہیں ہوا ہے اور اگر تم اللہ اور رسول کی اطاعت کرو گے تو وہ تمہارے اعمال میں سے کچھ بھی کم نہیں کرے گا کہ وہ بڑا غفور اور رحیم ہے'),
(4627, 49, 15, 'صاحبانِ ایمان صرف وہ لوگ ہیں جو اللہ اور اس کے رسول پر ایمان لے آئے اور پھر کبھی شک نہیں کیا اور اس کی راہ میں اپنے مال اور اپنی جان سے جہاد بھی کیا درحقیقت یہی لوگ اپنے دعویٰ ایمان میں سچےّ ہیں'),
(4628, 49, 16, 'آپ کہہ دیجئے کہ کیا تم خدا کو اپنا دین سکھارہے ہو جب کہ وہ آسمان اور زمین کی ہر شے سے باخبر ہے اور وہ کائنات کی ہر چیز کا جاننے والا ہے'),
(4629, 49, 17, 'یہ لوگ آپ پر احسان جتاتے ہیں کہ اسلام لے آئے ہیں تو آپ کہہ دیجئے کہ ہمارے اوپراپنے اسلام کا احسان نہ رکھو یہ خدا کا احسان ہے کہ اس نے تم کو ایمان لانے کی ہدایت دے دی ہے اگر تم واقعا دعوائے ایمان میں سچے ہو'),
(4630, 49, 18, 'بیشک اللہ آسمان و زمین کے ہر غیب کا جاننے والا اور وہ تمہارے تمام اعمال کا بھی دیکھنے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'قۤ - قرآن مجید کی قسم'),
(4632, 50, 2, 'لیکن ان کفاّر کو تعجب ہے کہ ان ہی میں سے کوئی شخص پیغمبر بن کر آگیا اس لئے ان کا کہنا یہ ہے کہ یہ بات بالکل عجیب ہے'),
(4633, 50, 3, 'کیا جب ہم مرکر خاک ہوجائیں گے تو دوبارہ واپس ہوں گے یہ بڑی بعید بات ہے'),
(4634, 50, 4, 'ہم جانتے ہیں کہ زمین ان کے جسموں میں سے کس قدر کم کردیتی ہے اور ہمارے پاس ایک محفوظ کتاب موجود ہے'),
(4635, 50, 5, 'حقیقت یہ ہے کہ ان لوگوں نے حق کے آنے کے بعد اس کا انکار کردیا ہے تو وہ ایک بے چینی کی بات میں مبتلا ہیں'),
(4636, 50, 6, 'کیا ان لوگوں نے اپنے اوپر آسمان کی طرف نہیں دیکھا ہے کہ ہم نے اسے کس طرح بنایا ہے اور پھر آراستہ بھی کردیا ہے اور اس میں کہیں شگاف بھی نہیں ہے'),
(4637, 50, 7, 'اور زمین کو فرش کردیا ہے اور اس میں پہاڑ ڈال دیئے ہیں اور ہر طرح کی خوبصورت چیزیں اُگادی ہیں'),
(4638, 50, 8, 'یہ خدا کی طرف رجوع کرنے والے ہر بندہ کے لئے سامان نصیحت اور وجہ بصیرت ہے'),
(4639, 50, 9, 'اور ہم نے آسمان سے بابرکت پانی نازل کیا ہے پھر اس سے باغات اور کھیتی کا غلہ اُگایا ہے'),
(4640, 50, 10, 'اور لمبی لمبی کھجوریں اُگائی ہیں جن کے بور آپس میں گتھے ہوئے ہیں'),
(4641, 50, 11, 'یہ سب ہمارے بندوں کے لئے رزق کا سامان ہے اور ہم نے اسی پانی سے اَمردہ زمینوں کو زندہ کیا ہے اور اسی طرح تمہیں بھی زمین سے نکالیں گے'),
(4642, 50, 12, 'ان سے پہلے قوم نوح اصحاب رس اور ثمود نے بھی تکذیب کی تھی'),
(4643, 50, 13, 'اور قوم عاد, فرعون اور برادرانِ لوط نے بھی'),
(4644, 50, 14, 'اور اصحاب ایکہ اور قوم تبع نے بھی اور ان سب نے رسولوں کی تکذیب کی تو ہمارا وعدہ پورا ہوگیا'),
(4645, 50, 15, 'تو کیا ہم پہلی خلقت سے عاجز تھے ہرگز نہیں - تو پھر حقیقت یہ ہے کہ یہ لوگ نئی خلقت کی طرف سے شبہ میں پڑے ہوئے ہیں'),
(4646, 50, 16, 'اور ہم نے ہی انسان کو پیدا کیا ہے اور ہمیں معلوم ہے کہ اس کا نفس کیا کیا وسوسے پیدا کرتا ہے اور ہم اس سے رگ گردن سے زیادہ قریب ہیں'),
(4647, 50, 17, 'جب کہ دو لکھنے والے اس کی حرکتوں کو لکھ لیتے ہیں جو داہنے اور بائیں بیٹھے ہوئے ہیں'),
(4648, 50, 18, 'وہ کوئی بات منہ سے نہیں نکالتا ہے مگر یہ کہ ایک نگہبان اس کے پاس موجود رہتا ہے'),
(4649, 50, 19, 'اور موت کی بیہوشی یقینا طاری ہوگی کہ یہی وہ بات ہے جس سے تو بھاگا کرتا تھا'),
(4650, 50, 20, 'اور پھر صور پھونکا جائے گا کہ یہ عذاب کے وعدہ کا دن ہے'),
(4651, 50, 21, 'اور ہر نفس کو اس انداز سے آنا ہوگا کہ اس کے ساتھ ہنکانے والے اور گواہی دینے والے فرشتے بھی ہوں گے'),
(4652, 50, 22, 'یقینا تم اس کی طرف سے غفلت میں تھے تو ہم نے تمہارے پردوں کو اُٹھادیاہے اور اب تمہاری نگاہ بہت تیز ہوگئی ہے'),
(4653, 50, 23, 'اور اس کا ساتھی فرشتہ کہے گا کہ یہ اس کا عمل میرے پاس تیار موجود ہے'),
(4654, 50, 24, 'حکم ہوگا کہ تم دونوں ہر ناشکرے سرکش کو جہنمّ میں ڈال دو'),
(4655, 50, 25, 'جو خیر سے روکنے والا حد سے تجاوز کرنے والا اور شبہات پیدا کرنے والا تھا'),
(4656, 50, 26, 'جس نے اللہ کے ساتھ دوسرے خدا بنادیئے تھے تم دونوں اس کو شدید عذاب میں ڈال دو'),
(4657, 50, 27, 'پھر اس کا ساتھی شیطان کہے گا کہ خدایا میں نے اس کو گمراہ نہیں کیا ہے یہ خود ہی گمراہی میں بہت دور تک چلا گیا تھا'),
(4658, 50, 28, 'ارشاد ہوگا کہ میرے پاس جھگڑا مت کرو میں پہلے ہی عذاب کی خبر دے چکا ہوں'),
(4659, 50, 29, 'میرے پاس بات میں تبدیلی نہیں ہوتی ہے اور نہ میں بندوں پر ظلم کرنے والا ہوں'),
(4660, 50, 30, 'جس دن ہم جہّنم سے کہیں گے کہ تو بھر گیا تو وہ کہے گا کہ کیا کچھ اور مل سکتا ہے'),
(4661, 50, 31, 'اور جنّت کو صاحبانِ تقویٰ سے قریب تر کردیا جائے گا'),
(4662, 50, 32, 'یہ وہ جگہ ہے جس کا وعدہ ہر خدا کی طرف رجوع کرنے والے اور نفس کی حفاظت کرنے والے سے کیا جاتا ہے'),
(4663, 50, 33, 'جو شخص بھی رحمان سے پبُ غیب ڈرتا ہے اور اس کی بارگاہ میں رجوع کرنے والے دل کی ساتھ حاضر ہوتا ہے'),
(4664, 50, 34, 'تم سب سلامتی کے ساتھی جنّت میں داخل ہوجاؤ کہ یہ ہمیشگی کا دن ہے'),
(4665, 50, 35, 'وہاں ان کے لئے جو کچھ بھی چاہیں گے سب حاضر رہے گا اور ہمارے پاس مزید بھی ہے'),
(4666, 50, 36, 'اور ان سے پہلے ہم نے کتنی ہی قوموں کو ہلاک کردیا ہے جو ان سے کہیں زیادہ طاقتور تھیں تو انہوں نے تمام شہروں کو چھان مارا تھا لیکن موت سے چھٹکارا کہاں ہے'),
(4667, 50, 37, 'اس واقعہ میں نصیحت کا سامان موجود ہے اس انسان کے لئے جس کے پاس دل ہو یا جو حضور قلب کے ساتھ بات سنتا ہو'),
(4668, 50, 38, 'اور ہم نے آسمان و زمین اور ان کے درمیان کی مخلوقات کو چھ دن میں پیدا کیا ہے اور ہمیں اس سلسلہ میں کوئی تھکن چھو بھی نہیں سکی ہے'),
(4669, 50, 39, 'لہٰذا آپ ان کی باتوں پر صبر کریں اور طلوع آفتاب سے پہلے اور غروب آفتاب سے پہلے اپنے رب کی تسبیح کیا کریں'),
(4670, 50, 40, 'اور رات کے ایک حصہ میں بھی اس کی تسبیح کریں اور سجدوں کے بعد بھی اس کی تسبیح کیا کریں'),
(4671, 50, 41, 'اور اس دن کو غور سے سنو جس دن قدرت کا منادی اسرافیل قریب ہی کی جگہ سے آواز دے گا'),
(4672, 50, 42, 'جس دن صدائے آسمان کو سب بخوبی سن لیں گے اور وہی دن قبروں سے نکلنے کا دن ہے'),
(4673, 50, 43, 'بیشک ہم ہی موت و حیات دینے والے ہیں اور ہماری ہی طرف سب کی بازگشت ہے'),
(4674, 50, 44, 'اسی دن زمین ان لوگوں کی طرف سے شق ہوجائے گی اور یہ تیزی سے نکل کھڑے ہوں گے کہ یہ حشر ہمارے لئے بہت آسان ہے'),
(4675, 50, 45, 'ہمیں خوب معلوم ہے کہ یہ لوگ کیا کررہے ہیں اور آپ ان پر جبر کرنے والے نہیں ہیں آپ قرآن کے ذریعہ ان لوگوں کو نصیحت کرتے رہیں جو عذاب سے ڈرنے والے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'ان ہواؤں کی قسم جو بادلوں کو منتشر کرنے والی ہیں'),
(4677, 51, 2, 'بھر بادل کا بوجھ اٹھانے والی ہیں'),
(4678, 51, 3, 'پھر دھیرے دھیرے چلنے والی ہیں'),
(4679, 51, 4, 'پھر ایک امر کی تقسیم کرنے والی ہیں'),
(4680, 51, 5, 'تم سے جس بات کا وعدہ کیا گیا ہے وہ سچی ہے'),
(4681, 51, 6, 'اور جزا و سزا بہرحال واقع ہونے والی ہے'),
(4682, 51, 7, 'اور مختلف راستوں والے آسمان کی قسم'),
(4683, 51, 8, 'کہ تم لوگ مختلف باتوں میں پڑے ہوئے ہو'),
(4684, 51, 9, 'حق سے وہی گمراہ کیا جاسکتا ہے جو بہکایا جاچکا ہے'),
(4685, 51, 10, 'بیشک اٹکل پچو لگانے والے مارے جائیں گے'),
(4686, 51, 11, 'جو اپنی غفلت میں بھولے پڑے ہوئے ہیں'),
(4687, 51, 12, 'یہ پوچھتے ہیں کہ آخر قیامت کا دن کب آئے گا'),
(4688, 51, 13, 'تو یہ وہی دن ہے جس دن انہیں جہّنم کی آگ پر تپایا جائے گا'),
(4689, 51, 14, 'کہ اب اپنا عذاب چکھو اور یہی وہ عذاب ہے جس کی تم جلدی مچائے ہوئے تھے'),
(4690, 51, 15, 'بیشک متقی افراد باغات اور چشموں کے درمیان ہوں گے'),
(4691, 51, 16, 'جو کچھ ان کا پروردگار عطا کرنے والا ہے اسے وصول کررہے ہوں گے کہ یہ لوگ پہلے سے نیک کردار تھے'),
(4692, 51, 17, 'یہ رات کے وقت بہت کم سوتے تھے'),
(4693, 51, 18, 'اور سحر کے وقت اللہ کی بارگاہ میں استغفار کیا کرتے تھے'),
(4694, 51, 19, 'اور ان کے اموال میں مانگنے والے اور نہ مانگنے والے محروم افراد کے لئے ایک حق تھا'),
(4695, 51, 20, 'اور زمین میں یقین کرنے والوں کے لئے بہت سی نشانیاں پائی جاتی ہیں'),
(4696, 51, 21, 'اور خود تمہارے اندر بھی - کیا تم نہیں دیکھ رہے ہو'),
(4697, 51, 22, 'اور آسمان میں تمہارا رزق ہے اور جن باتوں کا تم سے وعدہ کیا گیا ہے سب کچھ موجود ہے'),
(4698, 51, 23, 'آسمان و زمین کے مالک کی قسم یہ قرآن بالکل برحق ہے جس طرح تم خو دباتیں کررہے ہو'),
(4699, 51, 24, 'کیا تمہارے پاس ابراہیم علیھ السّلام کے محترم مہمانوں کا ذکر پہنچا ہے'),
(4700, 51, 25, 'جب وہ ان کے پاس وارد ہوئے اور سلام کیا تو ابراہیم علیھ السّلامنے جواب سلام دیتے ہوئے کہا کہ تم تو انجانی قوم معلوم ہوتے ہو'),
(4701, 51, 26, 'پھر اپنے گھر جاکر ایک موٹا تازہ بچھڑا تیار کرکے لے آئے'),
(4702, 51, 27, 'پھر ان کی طرف بڑھادیا اور کہا کیا آپ لوگ نہیں کھاتے ہیں'),
(4703, 51, 28, 'پھر اپنے نفس میں خوف کا احساس کیا تو ان لوگوں نے کہا کہ آپ ڈریں نہیں اور پھر انہیں ایک دانشمند فرزند کی بشارت دیدی'),
(4704, 51, 29, 'یہ سن کر ان کی زوجہ شور مچاتی ہوئی آئیں اور انہوں نے منہ پیٹ لیا کہ میں بڑھیا بانجھ (یہ کیا بات ہے'),
(4705, 51, 30, 'ان لوگوں نے کہا یہ ایسا ہی ہوگا یہ تمہارے پروردگار کا ارشاد ہے وہ بڑی حکمت والا اور ہر چیز کا جاننے والا ہے'),
(4706, 51, 31, 'ابراہیم علیھ السّلام نے کہا کہ اے فرشتو تمہیں کیا مہم درپیش ہے'),
(4707, 51, 32, 'انہوں نے کہا کہ ہمیں ایک مجرم قوم کی طرف بھیجا گیا ہے'),
(4708, 51, 33, 'تاکہ ان کے اوپر مٹی کے کھرنجے دار پتھر برسائیں'),
(4709, 51, 34, 'جن پر پروردگار کی طرف سے حد سے گزر جانے والوں کے لئے نشانی لگی ہوئی ہے'),
(4710, 51, 35, 'پھر ہم نے اس بستی کے تمام مومنین کو باہر نکال لیا'),
(4711, 51, 36, 'اور وہاں مسلمانوں کے ایک گھر کے علاوہ کسی کو پایا بھی نہیں'),
(4712, 51, 37, 'اور وہاں ان لوگوں کے لئے ایک نشانی بھی چھوڑ دی جو دردناک عذاب سے ڈرنے والے ہیں'),
(4713, 51, 38, 'اور موسٰی علیھ السّلام کے واقعہ میں بھی ہماری نشانیاں ہیں جب ہم نے ان کو فرعون کی طرف کھلی ہوئی دلیل دے کر بھیجا'),
(4714, 51, 39, 'تو اس نے لشکر کے دِم پر منہ موڑ لیا اور کہا کہ یہ جادوگر یا دیوانہ ہے'),
(4715, 51, 40, 'تو ہم نے اسے اور اس کی فوج کو گرفت میں لے کر دریا میں ڈال دیا اور وہ قابل ملامت تھا ہی'),
(4716, 51, 41, 'اور قوم عاد میں بھی ایک نشانی ہے جب ہم نے ان کی طرف بانجھ ہوا کو چلا دیا'),
(4717, 51, 42, 'کہ جس چیز کے پاس سے گزر جاتی تھی اسے بوسیدہ ہڈی کی طرح ریزہ ریزہ کردیتی تھی'),
(4718, 51, 43, 'اور قوم ثمود میں بھی ایک نشانی ہے جب ان سے کہا گیا کہ تھوڑے دنوں مزے کرلو'),
(4719, 51, 44, 'تو ان لوگوں نے حکم خدا کی نافرمانی کی تو انہیں بجلی نے اپنی گرفت میں لے لیا اور وہ دیکھتے ہی رہ گئے'),
(4720, 51, 45, 'پھر نہ وہ اٹھنے کے قابل تھے اور نہ مدد طلب کرنے کے لائق تھے'),
(4721, 51, 46, 'اور ان سے پہلے قوم نوح تھی کہ وہ تو سب ہی فاسق اور بدکار تھے'),
(4722, 51, 47, 'اور آسمان کو ہم نے اپنی طاقت سے بنایا ہے اور ہم ہی اسے وسعت دینے والے ہیں'),
(4723, 51, 48, 'اور زمین کو ہم نے فرش کیا ہے تو ہم بہترین ہموار کرنے والے ہیں'),
(4724, 51, 49, 'اور ہر شے میں سے ہم نے جوڑا بنایا ہے کہ شاید تم نصیحت حاصل کرسکو'),
(4725, 51, 50, 'لہذا اب خدا کی طرف دوڑ پڑو کہ میں کھلا ہوا ڈرانے والا ہوں'),
(4726, 51, 51, 'اور خبردار اس کے ساتھ کسی دوسرے کو خدا نہ بنانا کہ میں تمہارے لئے واضح طور پر ڈرانے والا ہوں'),
(4727, 51, 52, 'اسی طرح ان سے پہلے کسی قوم کے پاس کوئی رسول نہیں آیا مگر یہ کہ ان لوگوں نے یہ کہہ دیا کہ یہ جادوگر ہے یا دیوانہ'),
(4728, 51, 53, 'کیا انہوں نے ایک دوسرے کو اسی بات کی وصیت کی ہے - نہیں بلکہ یہ سب کے سب سرکش ہیں'),
(4729, 51, 54, 'لہٰذا آپ ان سے منہ موڑ لیں پھر آپ پر کوئی الزام نہیں ہے'),
(4730, 51, 55, 'اور یاد دہانی بہرحال کراتے رہے کہ یاد دہانی صاحبانِ ایمان کے حق میں مفید ہوتی ہے'),
(4731, 51, 56, 'اور میں نے جنات اور انسانوں کو صرف اپنی عبادت کے لئے پیدا کیا ہے('),
(4732, 51, 57, 'میں ان سے نہ رزق کا طلبگار ہوں اور نہ یہ چاہتا ہوں کہ یہ مجھے کچھ کھلائیں'),
(4733, 51, 58, 'بیشک رزق دینے والا, صاحبِ قوت اور زبردست صرف علیھ السّلام اللہ ہے'),
(4734, 51, 59, 'پھر ان ظالمین کے لئے بھی ویسے ہی نتائج ہیں جیسے ان کے اصحاب کے لئے تھے لہذا یہ جلدی نہ کریں'),
(4735, 51, 60, 'پھر کفار کے لئے اس دن ویل اور عذاب ہے جس دن کا ان سے وعدہ کیا جارہا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'طور کی قسم'),
(4737, 52, 2, 'اور لکھی ہوئی کتاب کی قسم'),
(4738, 52, 3, 'جو کشادہ اوراق میں ہے'),
(4739, 52, 4, 'اور بیت معمور کی قسم'),
(4740, 52, 5, 'اور بلند چھت (آسمان) کی قسم'),
(4741, 52, 6, 'اور بھڑکتے ہوئے سمندر کی قسم'),
(4742, 52, 7, 'یقینا تمہارے رب کا عذاب واقع ہونے والا ہے'),
(4743, 52, 8, 'اور اس کا کوئی دفع کرنے والا نہیں ہے'),
(4744, 52, 9, 'جس دن آسمان باقاعدہ چکر کھانے لگیں گے'),
(4745, 52, 10, 'اور پہاڑ باقاعدہ حرکت میں آجائیں گے'),
(4746, 52, 11, 'پھر جھٹلانے والوں کے لئے عذاب اور بربادی ہی ہے'),
(4747, 52, 12, 'جو محلات میں پڑے کھیل تماشہ کررہے ہیں'),
(4748, 52, 13, 'جس دن انہیں بھرپور طریقہ سے جہّنم میں ڈھکیل دیا جائے گا'),
(4749, 52, 14, 'یہی وہ جہّنم کی آگ ہے جس کی تم تکذیب کیا کرتے تھے'),
(4750, 52, 15, 'آیا یہ جادو ہے یا تمہیں کچھ سجھائی نہیں دے رہا ہے'),
(4751, 52, 16, 'اب اس میں چلے جاؤ پھر چاہے صبر کرو یا نہ کرو سب برابر ہے یہ تمہارے ان اعمال کی سزادی جارہی ہے جو تم انجام دیا کرتے تھے'),
(4752, 52, 17, 'بیشک صاحبانِ تقویٰ باغات اور نعمتوں کے درمیان رہیں گے'),
(4753, 52, 18, 'جو خدا عنایت کرے گا اس میں خوش حال رہیں گے اور خدا انہیں جہّنم کے عذاب سے محفوظ رکھے گا'),
(4754, 52, 19, 'اب یہیں آرام سے کھاؤ پیو ان اعمال کی بنا پر جو تم نے انجام دئیے تھے'),
(4755, 52, 20, 'وہ برابر سے بچھے ہوئے تختوں پر تکیہ لگائے بیٹھے ہوں گے اور ہم ان کا جوڑا کشادہ چشم حوروں کو قرار دیں گے'),
(4756, 52, 21, 'اور جو لوگ ایمان لائے اور ان کی اولاد نے بھی ایمان میں ان کا اتباع کیا تو ہم ان کی ذریت کو بھی ان ہی سے ملادیں گے اور کسی کے عمل میں سے ذرہ برابر بھی کم نہیں کریں گے کہ ہر شخص اپنے اعمال کا گروی ہے'),
(4757, 52, 22, 'اور ہم جس طرح کے میوے یا گوشت وہ چاہیں گے اس سے بڑھ کر ان کی امداد کریں گے'),
(4758, 52, 23, 'وہ آپس میں جام شراب پر جھگڑا کریں گے لیکن وہاں کوئی لغویت اور گناہ نہ ہوگا'),
(4759, 52, 24, 'اور ان کے گرد وہ نوجوان لڑکے چکر لگاتے ہوں گے جو پوشیدہ اور محتاط موتیوں جیسے حسین و جمیل ہوں گے'),
(4760, 52, 25, 'اور پھر ایک دوسرے کی طرف رخ کرکے سوال جواب کریں گے'),
(4761, 52, 26, 'کہیں گے کہ ہم تو اپنے گھر میں خدا سے بہت ڈرتے تھے'),
(4762, 52, 27, 'تو خدا نے ہم پر یہ احسان کیا اور ہمیں جہّنم کی زہریلی ہوا سے بچالیا'),
(4763, 52, 28, 'ہم اس سے پہلے بھی اسی سے دعائیں کیا کرتے تھے کہ وہ یقینا وہ بڑا احسان کرنے والا اور مہربان ہے'),
(4764, 52, 29, 'لہذا آپ لوگوں کو نصیحت کرتے رہیں - خدا کے فضل سے آپ نہ کاہن ہیں اور نہ مجنون'),
(4765, 52, 30, 'کیا یہ لوگ یہ کہتے ہیں کہ یہ شاعر ہے اور ہم اس کے بارے میں حوادث زمانہ کا انتظار کررہے ہیں'),
(4766, 52, 31, 'تو آپ کہہ دیجئے کہ بیشک تم انتظار کرو میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں'),
(4767, 52, 32, 'کیا ان کی عقلیں یہ باتیں بتاتی ہیں یا یہ واقعا سرکش قوم ہیں'),
(4768, 52, 33, 'یا یہ کہتے ہیں کہ نبی نے قرآن گڑھ لیا ہے اور حقیقت یہ ہے کہ یہ ایمان لانے والے ا نہیں ہیں'),
(4769, 52, 34, 'اگر یہ اپنی بات میں سچے ہیں تو یہ بھی ایسا ہی کوئی کلام لے آئیں'),
(4770, 52, 35, 'کیا یہ بغیر کسی چیز کے ازخود پیدا ہوگئے ہیں یا یہ خود ہی پیدا کرنے والے ہیں'),
(4771, 52, 36, 'یا انہوں نے آسمان و زمین کو پیدا کردیا ہے - حقیقت یہ ہے کہ یہ یقین کرنے والے نہیں ہیں'),
(4772, 52, 37, 'یا ان کے پاس پروردگار کے خزانے ہیں یہی لوگ حاکم ہیں'),
(4773, 52, 38, 'یا ان کے پاس کوئی سیڑھی ہے جس کے ذریعہ آسمان کی باتیں سن لیا کرتے ہیں تو ان کا سننے والا کوئی واضح ثبوت لے آئے'),
(4774, 52, 39, 'یا خدا کے لئے لڑکیاں ہیں اور تمہارے لئے لڑکے ہیں'),
(4775, 52, 40, 'یا تم ان سے کوئی اجر رسالت مانگتے ہو کہ یہ اس کے بوجھ کے نیچے دبے جارہے ہیں'),
(4776, 52, 41, 'یا ان کے پاس غیب کا علم ہے کہ یہ اسے لکھ رہے ہیں'),
(4777, 52, 42, 'یا یہ کوئی مکاری کرنا چاہتے ہیں تو یاد رکھو کہ کفار خود اپنی چال میں پھنس جانے والے ہیں'),
(4778, 52, 43, 'یا ان کے لئے خدا کے علاوہ کوئی دوسرا خدا ہے جب کہ خدا ان کے شرک سے پاک و پاکیزہ ہے'),
(4779, 52, 44, 'اور یہ اگر آسمان کے ٹکڑوں کو گرتا ہوا بھی دیکھ لیں گے تو بھی کہیں گے یہ تو تہ بہ تہ بادل ہیں'),
(4780, 52, 45, 'تو انہیں ان کے حال پر چھوڑ دیجئے یہاں تک کہ وہ دن دیکھ لیں جس دن بیہوش ہوجائیں گے'),
(4781, 52, 46, 'جس دن ان کی کوئی چال کام نہ آئے گی اور نہ کوئی مدد کرنے والا ہوگا'),
(4782, 52, 47, 'اور جن لوگوں نے ظلم کیا ہے ان کے لئے اس کے علاوہ بھی عذاب ہے لیکن ان کی اکثریت اس سے بے خبر ہے'),
(4783, 52, 48, 'آپ اپنے پروردگار کے حکم کے لئے صبر کریں آپ ہماری نگاہ کے سامنے ہیں اور ہمیشہ قیام کرتے وقت اپنے پروردگار کی تسبیح کرتے رہیں'),
(4784, 52, 49, 'اور رات کے ایک حصّہ میں اور ستاروں کے غروب ہونے کے بعد بھی تسبیحِ پروردگار کرتے رہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'قسم ہے ستارہ کی جب وہ ٹوٹا'),
(4786, 53, 2, 'تمہارا ساتھی نہ گمراہ ہوا ہے اور نہ بہکا'),
(4787, 53, 3, 'اور وہ اپنی خواہش سے کلام بھی نہیں کرتا ہے'),
(4788, 53, 4, 'اس کا کلام وہی وحی ہے جو مسلسل نازل ہوتی رہتی ہے'),
(4789, 53, 5, 'اسے نہایت طاقت والے نے تعلیم دی ہے'),
(4790, 53, 6, 'وہ صاحبِ حسن و جمال جو سیدھا کھڑا ہوا'),
(4791, 53, 7, 'جب کہ وہ بلند ترین افق پر تھا'),
(4792, 53, 8, 'پھر وہ قریب ہوا اور آگے بڑھا'),
(4793, 53, 9, 'یہاں تک کہ دو کمان یا اس سے کم کا فاصلہ رہ گیا'),
(4794, 53, 10, 'پھر خدا نے اپنے بندہ کی طرف جس راز کی بات چاہی وحی کردی'),
(4795, 53, 11, 'دل نے اس بات کو جھٹلایا نہیں جس کو آنکھوں نے دیکھا'),
(4796, 53, 12, 'کیا تم اس سے اس بات کے بارے میں جھگڑا کررہے ہو جو وہ دیکھ رہا ہے'),
(4797, 53, 13, 'اور اس نے تو اسے ایک بار اور بھی دیکھا ہے'),
(4798, 53, 14, 'سدرِالمنتہیٰ کے نزدیک'),
(4799, 53, 15, 'جس کے پاس جنت الماویٰ بھی ہے'),
(4800, 53, 16, 'جب سدرہ پر چھا رہا تھا جو کچھ کہ چھا رہا تھا'),
(4801, 53, 17, 'اس وقت اس کی آنکھ نہ بہکی اور نہ حد سے آگے بڑھی'),
(4802, 53, 18, 'اس نے اپنے پروردگار کی بڑی بڑی نشانیان دیکھی ہیں'),
(4803, 53, 19, 'کیا تم لوگوں نے لات اور عذٰی کو دیکھا ہے'),
(4804, 53, 20, 'اور منات جو ان کا تیسرا ہے اسے بھی دیکھا ہے'),
(4805, 53, 21, 'تو کیا تمہارے لئے لڑکے ہیں اور اس کے لئے لڑکیاں ہیں'),
(4806, 53, 22, 'یہ انتہائی ناانصافی کی تقسیم ہے'),
(4807, 53, 23, 'یہ سب وہ نام ہیں جو تم نے اور تمہارے باپ دادا نے طے کرلئے ہیں خدا نے ان کے بارے میں کوئی دلیل نازل نہیں کی ہے - درحقیقت یہ لوگ صرف اپنے گمانوں کا اتباع کررہے ہیں اور جو کچھ ان کا دل چاہتا ہے اور یقینا ان کے پروردگار کی طرف سے ان کے پاس ہدایت آچکی ہے'),
(4808, 53, 24, 'کیا انسان کو وہ سب مل سکتا ہے جس کی آرزو کرے'),
(4809, 53, 25, 'بس اللہ ہی کے لئے دنیا اور آخرت سب کچھ ہے'),
(4810, 53, 26, 'اور آسمانوں میں کتنے ہی فرشتے ہیں جن کی سفارش کسی کے کام نہیں آسکتی ہے جب تک خدا .... جس کے بارے میں چاہے اور اسے پسند کرے .... اجازت نہ دے دے'),
(4811, 53, 27, 'بیشک جو لوگ آخرت پر ایمان نہیں رکھتے ہیں وہ ملائکہ کے نام لڑکیوں جیسے رکھتے ہیں'),
(4812, 53, 28, 'حالانکہ ان کے پاس اس سلسلہ میں کوئی علم نہیں ہے یہ صرف وہم و گمان کے پیچھے چلے جارہے ہیں اور گمان حق کے بارے میں کوئی فائدہ نہیں پہنچا سکتا ہے'),
(4813, 53, 29, 'لہذا جو شخص بھی ہمارے ذکر سے منہ پھیرے اور زندگانی دنیا کے علاوہ کچھ نہ چاہے آپ بھی اس سے کنارہ کش ہوجائیں'),
(4814, 53, 30, 'یہی ان کے علم کی انتہا ہے اور بیشک آپ کا پروردگار خوب جانتا ہے کہ کون اس کے راستہ سے بہک گیا ہے اور کون ہدایت کے راستہ پر ہے'),
(4815, 53, 31, 'اوراللہ ہی کے لئے زمین و آسمان کے کل اختیارات ہیں تاکہ وہ بدعمل افراد کو ان کے اعمال کی سزادے سکے اور نیک عمل کرنے والوں کو ان کے اعمال کا اچھا بدلہ دے سکے'),
(4816, 53, 32, 'جو لوگ گناہانِ کبیرہ اور فحش باتوں سے پرہیز کرتے ہیں (گناہان صغیرہ کے علاوہ) بیشک آپ کا پروردگار ان کے لئے بہت وسیع مغفرت والا ہے وہ اس وقت بھی تم سب کے حالات سے خوب واقف تھا جب اس نے تمہیں خاک سے پیدا کیا تھا اور اس وقت بھی جب تم ماں کے شکم میں جنین کی منزل میں تھے لہذا اپنے نفس کو زیادہ پاکیزہ قرار نہ دو وہ متقی افراد کو خوب پہچانتا ہے'),
(4817, 53, 33, 'کیا آپ نے اسے بھی دیکھا ہے جس نے منہ پھیر لیا'),
(4818, 53, 34, 'اور تھوڑا سا راسِ خدا میں دے کر بند کردیا'),
(4819, 53, 35, 'کیا اس کے پاس علم غیب ہے جس کے ذریعے وہ دیکھ رہا ہے'),
(4820, 53, 36, 'یا اسے اس بات کی خبر ہی نہیں ہے جو موسٰی علیھ السّلام کے صحیفوں میں تھی'),
(4821, 53, 37, 'یا ابراہیم علیھ السّلام کے صحیفوں میں تھی جنہوں نے پورا پورا حق ادا کیا ہے'),
(4822, 53, 38, 'کوئی شخص بھی دوسرے کا بوجھ اٹھانے والا نہیں ہے'),
(4823, 53, 39, 'اور انسان کے لئے صرف اتنا ہی ہے جتنی اس نے کوشش کی ہے'),
(4824, 53, 40, 'اور اس کی کوشش عنقریب اس کے سامنے پیش کردی جائے گی'),
(4825, 53, 41, 'اس کے بعد اسے پورا بدلہ دیا جائے گا'),
(4826, 53, 42, 'اور بیشک سب کی آخری منزل پروردگار کی بارگاہ ہے'),
(4827, 53, 43, 'اور یہ کہ اسی نے ہنسایا بھی ہے اور ----- فِلایا بھی ہے'),
(4828, 53, 44, 'اور وہی موت و حیات کا دینے والا ہے'),
(4829, 53, 45, 'اور اسی نے نر اور مادہ کا جوڑا پیدا کیا ہے'),
(4830, 53, 46, 'اس نطفہ سے جو رحم میں ڈالا جاتا ہے'),
(4831, 53, 47, 'اور اسی کے ذمہ دوسری زندگی بھی ہے'),
(4832, 53, 48, 'اور اسی نے مالدار بنایا ہے اور سرمایہ عطا کیا ہے'),
(4833, 53, 49, 'اور وہی ستارہ شعریٰ کا مالک ہے'),
(4834, 53, 50, 'اور اسی نے پہلے قوم عاد کو ہلاک کیا ہے'),
(4835, 53, 51, 'اور قوم ثمود کو بھی پھر کسی کو باقی نہیں چھوڑا ہے'),
(4836, 53, 52, 'اور قوم نوح کو ان سے پہلے .کہ وہ لوگ بڑے ظالم اور سرکش تھے'),
(4837, 53, 53, 'اور اسی نے قوم لوط کی اُلٹی بستیوں کو پٹک دیا ہے'),
(4838, 53, 54, 'پھر ان کو ڈھانک لیا جس چیز نے کہ ڈھانک لیا'),
(4839, 53, 55, 'اب تم اپنے پروردگار کی کس نعمت پر شک کررہے ہو'),
(4840, 53, 56, 'بیشک یہ پیغمبر بھی اگلے ڈرانے والوں میں سے ایک ڈرانے والا ہے'),
(4841, 53, 57, 'دیکھو قیامت قریب آگئی ہے'),
(4842, 53, 58, 'اللہ کے علاوہ کوئی اس کا ٹالنے والا نہیں ہے'),
(4843, 53, 59, 'کیا تم اس بات سے تعجب کررہے ہو'),
(4844, 53, 60, 'اور پھر ہنستے ہو اور روتے نہیں ہو'),
(4845, 53, 61, 'اور تم بالکل غافل ہو'),
(4846, 53, 62, '( اب سے غنیمت ہے) کہ اللہ کے لئے سجدہ کرو اور اس کی عبادت کرو');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قیامت قریب آگئی اور چاند کے دو ٹکڑے ہوگئے'),
(4848, 54, 2, 'اور یہ کوئی بھی نشانی دیکھتے ہیں تو منہ پھیر لیتے ہیں اور کہتے ہیں کہ یہ ایک مسلسل جادو ہے'),
(4849, 54, 3, 'اور انہوں نے تکذیب کی اور اپنی خواہشات کا اتباع کیا اور ہر بات کی ایک منزل ہوا کرتی ہے'),
(4850, 54, 4, 'یقینا ان کے پاس اتنی خبریں آچکی ہیں جن میں تنبیہ کا سامان موجود ہے'),
(4851, 54, 5, 'انتہائی درجہ کی حکمت کی باتیں ہیں لیکن انہیں ڈرانے والی باتیں کوئی فائدہ نہیں پہنچاتیں'),
(4852, 54, 6, 'لہذا آپ ان سے منہ پھیر لیں جسن دن ایک بلانے والا (اسرافیل) انہیں ایک ناپسندہ امر کی طرف بلائے گا'),
(4853, 54, 7, 'یہ نظریں جھکائے ہوئے قبروں سے اس طرح نکلیں گے جس طرح ٹڈیاں پھیلی ہوئی ہوں'),
(4854, 54, 8, 'سب کسی بلانے والے کی طرف سر اٹھائے بھاگے چلے جارہے ہوں گے اور کفار یہ کہہ رہے ہوں گے کہ آج کا دن بڑا سخت دن ہے'),
(4855, 54, 9, 'ان سے پہلے قوم نوح نے بھی تکذیب کی تھی کہ انہوں نے ہمارے بندے کو جھٹلایا اور کہہ دیا کہ یہ دیوانہ ہے بلکہ اسے جھڑکا بھی گیا'),
(4856, 54, 10, 'تو اس نے اپنے پروردگار سے دعا کی کہ میں مغلوب ہوگیا ہوں میری مدد فرما'),
(4857, 54, 11, 'تو ہم نے ایک موسلا دھار بارش کے ساتھ آسمان کے دروازے کھول دیئے'),
(4858, 54, 12, 'اور زمین سے بھی چشمے جاری کردیئے اور پھر دونوں پانی ایک خاص مقررہ مقصد کے لئے باہم مل گئے'),
(4859, 54, 13, 'اور ہم نے نوح علیھ السّلامکو تختوں اور کیلوں والی کشتی میں سوار کرلیا'),
(4860, 54, 14, 'جو ہماری نگاہ کے سامنے چل رہی تھی اور یہ اس بندے کی جزا تھی جس کا انکار کیا گیا تھا'),
(4861, 54, 15, 'اور ہم نے اسے ایک نشانی بناکر چھوڑ دیا ہے تو کیا کوئی ہے جو نصیحت حاصل کرے'),
(4862, 54, 16, 'پھر ہمارا عذاب اور ڈرانا کیسا ثابت ہوا'),
(4863, 54, 17, 'اور ہم نے قرآن کو نصیحت کے لئے آسان کردیا ہے تو کیا کوئی نصیحت حاصل کرنے والا ہے'),
(4864, 54, 18, 'اور قوم عاد نے بھی تکذیب کی تو ہمارا عذاب اور ڈرانا کیسا رہا'),
(4865, 54, 19, 'ہم نے ان کی اوپر تیز و تند آندھی بھیج دی ایک مسلسل نحوست والے منحوس دن میں'),
(4866, 54, 20, 'جو لوگوں کو جگہ سے یوں اُٹھالیتی تھی جیسے اکھڑے ہوئے کھجور کے تنے ہوں'),
(4867, 54, 21, 'پھر دیکھو ہمارا ذاب اور ڈرانا کیسا ثابت ہوا'),
(4868, 54, 22, 'اور ہم نے قرآن کو نصیحت کے لئے آسان کردیا ہے تو کیا کوئی نصیحت حاصل کرنے والا ہے'),
(4869, 54, 23, 'اور ثمود نے بھی پیغمبروں علیھ السّلام کو جھٹلایا'),
(4870, 54, 24, 'اور کہہ دیا کہ کیا ہم اپنے ہی میں سے ایک شخص کا اتباع کرلیں اس طرح تو ہم گمراہی اور دیوانگی کا شکار ہوجائیں گے'),
(4871, 54, 25, 'کیا ہم سب کے درمیان ذکر صرف اسی پر نازل ہوا ہے درحقیقت یہ جھوٹا ہے اور بڑائی کا طلبگار ہے'),
(4872, 54, 26, 'تو عنقریب کل ہی انہیں معلوم ہوجائے گا جھوٹا اور متکبر کون ہے'),
(4873, 54, 27, 'ہم ان کے امتحان کے لئے ایک اونٹنی بھیجنے والے ہیں لہذا تم اس کا انتظار کرو اور صبر سے کام لو'),
(4874, 54, 28, 'اور انہیں باخبر کردو کہ پانی ان کے درمیان تقسیم ہوگا اور ہر ایک کو اپنی باری پر حاضر ہونا چاہئے'),
(4875, 54, 29, 'تو ان لوگوں نے اپنے ساتھی کو آواز دی اور اس نے اونٹنی کو پکڑ کر اس کی کونچیں کاٹ دیں'),
(4876, 54, 30, 'پھر سب نے دیکھا کہ ہمارا عذاب اور ڈرانا کیسا ثابت ہوا'),
(4877, 54, 31, 'ہم نے ان کے اوپر ایک چنگھاڑ کو بھیج دیا تو یہ سب کے سب باڑے کے بھوسے کی طرح ہوگئے'),
(4878, 54, 32, 'اور ہم نے قرآن کو نصیحت کے لئے آسان کردیا ہے تو کیا کوئی نصیحت حاصل کرنے والا ہے'),
(4879, 54, 33, 'اور قوم لوط نے بھی پیغمبروں علیھ السّلام کو جھٹلایا'),
(4880, 54, 34, 'تو ہم نے ان کے اوپر پتھر برسائے صرف لوط کی آل کے علاوہ کہ ان کو سحر کے ہنگام ہی بچالیا'),
(4881, 54, 35, 'یہ ہماری ایک نعمت تھی اور اسی طرح ہم شکر گزار بندوں کو جزادیتے ہیں'),
(4882, 54, 36, 'اور لوط نے انہیں ہماری گرفت سے ڈرایا لیکن ان لوگوں نے ڈرانے ہی میں شک کیا'),
(4883, 54, 37, 'اور ان سے مہمان کے بارے میں ناجائز مطالبات کرنے لگے تو ہم نے ان کی آنکھوں کو اندھا کردیا کہ اب عذاب اور ڈرانے کا مزہ چکھو'),
(4884, 54, 38, 'اور ان کے اوپر صبح سویرے نہ ٹلنے والا عذاب نازل ہوگیا'),
(4885, 54, 39, 'کہ اب ہمارے عذاب اور ڈرانے کا مزہ چکھو'),
(4886, 54, 40, 'اور ہم نے قرآن کو نصیحت کے لئے آسان کردیا ہے تو کیا کوئی نصیحت حاصل کرنے والا ہے'),
(4887, 54, 41, 'اور فرعون والوں تک بھی پیغمبر علیھ السّلام آئے'),
(4888, 54, 42, 'تو انہوں نے ہماری ساری نشانیوں کا انکار کردیا تو ہم نے بھی ایک زبردست صاحب هاقتدار کی طرح انہیں اپنی گرفت میں لے لیا'),
(4889, 54, 43, 'تو کیا تمہارے کفار ان سب سے بہتر ہیں یا ان کے لئے کتابوں میں کوئی معافی نامہ لکھ دیا گیا ہے'),
(4890, 54, 44, 'یا ان کا کہنا یہ ہے کہ ہمارے پاس بڑی جماعت ہے جو ایک دوسرے کی مدد کرنے والی ہے'),
(4891, 54, 45, 'عنقریب یہ جماعت شکست کھاجائے گی اور سب پیٹھ پھیر کر بھاگ جائیں گے'),
(4892, 54, 46, 'بلکہ ان کا موعد قیامت کا ہے اور قیامت انتہائی سخت اور تلخ حقیقت ہے'),
(4893, 54, 47, 'بیشک مجرمین گمراہی اور دیوانگی میں مبتلا ہیں'),
(4894, 54, 48, 'قیامت کے دن یہ آگ پر منہ کے بل کھینچے جائیں گے کہ اب جہنمّ کا مزہ چکھو'),
(4895, 54, 49, 'بیشک ہم نے ہر شے کو ایک اندازہ کے مطابق پیدا کیا ہے'),
(4896, 54, 50, 'اور ہمارا حکم پلک جھپکنے کی طرح کی ایک بات ہے'),
(4897, 54, 51, 'اور ہم نے تمہارے ساتھیوں کو پہلے ہی ہلاک کردیا ہے تو کیا کوئی نصیحت حاصل کرنے والا ہے'),
(4898, 54, 52, 'اور ان لوگوں نے جو کچھ بھی کیا ہے سب نامہ اعمال میں محفوظ ہے'),
(4899, 54, 53, 'اور ہر چھوٹا اور بڑا عمل اس میں درج کردیا گیا ہے'),
(4900, 54, 54, 'بیشک صاحبان هتقویٰ باغات اور نہروں کے درمیان ہوں گے'),
(4901, 54, 55, 'اس پاکیزہ مقام پر جو صاحبِ اقتدار بادشاہ کی بارگاہ میں ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'وہ خدا بڑا مہربان ہے'),
(4903, 55, 2, 'اس نے قرآن کی تعلیم دی ہے'),
(4904, 55, 3, 'انسان کو پیدا کیا ہے'),
(4905, 55, 4, 'اور اسے بیان سکھایا ہے'),
(4906, 55, 5, 'آفتاب و ماہتاب سب اسی کے مقرر کردہ حساب کے ساتھ چل رہے ہیں'),
(4907, 55, 6, 'اور بوٹیاں بیلیں اور درخت سب اسی کا سجدہ کررہے ہیں'),
(4908, 55, 7, 'اس نے آسمان کو بلند کیا ہے اور انصاف کی ترازو قائم کی ہے'),
(4909, 55, 8, 'تاکہ تم لوگ وزن میں حد سے تجاوز نہ کرو'),
(4910, 55, 9, 'اور انصاف کے ساتھ وزن کو قائم کرو اور تولنے میں کم نہ تولو'),
(4911, 55, 10, 'اور اسی نے زمین کو انسانوں کے لئے وضع کیا ہے'),
