(5050, 56, 71, 'کیا تم نے اس آگ کو دیکھا ہے جسے لکڑی سے نکالتے ہو'),
(5051, 56, 72, 'اس کے درخت کو تم نے پیدا کیا ہے یا ہم اس کے پیدا کرنے والے ہیں'),
(5052, 56, 73, 'ہم نے اسے یاد دہانی کا ذریعہ اور مسافروں کے لئے نفع کا سامان قرار دیا ہے'),
(5053, 56, 74, 'اب آپ اپنے عظیم پروردگار کے نام کی تسبیح کریں'),
(5054, 56, 75, 'اور میں تو تاروں کے منازل کی قسم کھاکر کہتا ہوں'),
(5055, 56, 76, 'اور تم جانتے ہو کہ یہ قسم بہت بڑی قسم ہے'),
(5056, 56, 77, 'یہ بڑا محترم قرآن ہے'),
(5057, 56, 78, 'جسے ایک پوشیدہ کتاب میں رکھا گیا ہے'),
(5058, 56, 79, 'اسے پاک و پاکیزہ افراد کے علاوہ کوئی چھو بھی نہیں سکتا ہے'),
(5059, 56, 80, 'یہ رب العالمین کی طرف سے نازل کیا گیا ہے'),
(5060, 56, 81, 'تو کیا تم لوگ اس کلام سے انکار کرتے ہو'),
(5061, 56, 82, 'اور تم نے اپنی روزی یہی قرار دے رکھی ہے کہ اس کا انکار کرتے رہو'),
(5062, 56, 83, 'پھر ایسا کیوں نہیں ہوتا کہ جب جان گلے تک پہنچ جائے'),
(5063, 56, 84, 'اور تم اس وقت دیکھتے ہی رہ جاؤ'),
(5064, 56, 85, 'اور ہم تمہاری نسبت مرنے والے سے قریب ہیں مگر تم دیکھ نہیں سکتے ہو'),
(5065, 56, 86, 'پس اگر تم کسی کے دباؤ میں نہیں ہو اور بالکل آزاد ہو'),
(5066, 56, 87, 'تو اس روح کو کیوں نہیں پلٹا دیتے ہو اگر اپنی بات میں سچے ہو'),
(5067, 56, 88, 'پھر اگر مرنے والا مقربین میں سے ہے'),
(5068, 56, 89, 'تو اس کے لئے آسائش, خوشبو دار پھول اور نعمتوں کے باغات ہیں'),
(5069, 56, 90, 'اور اگر اصحاب یمین میں سے ہے'),
(5070, 56, 91, 'تو اصحاب یمین کی طرف سے تمہارے لئے سلام ہے'),
(5071, 56, 92, 'اور اگر جھٹلانے والوں اور گمراہوں میں سے ہے'),
(5072, 56, 93, 'تو کھولتے ہوئے پانی کی مہمانی ہے'),
(5073, 56, 94, 'اور جہنّم میں جھونک دینے کی سزا ہے'),
(5074, 56, 95, 'یہی وہ بات ہے جو بالکل برحق اور یقینی ہے'),
(5075, 56, 96, 'لہذا اپنے عظیم پروردگار کے نام کی تسبیح کرتے رہو');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'محو تسبیح پروردگار ہے ہر وہ چیز جو زمین و آسمان میں ہے اور وہ پروردگار صاحبِ عزت بھی ہے اور صاحبِ حکمت بھی ہے'),
(5077, 57, 2, 'آسمان و زمین کا کل اختیار اسی کے پاس ہے اور وہی حیات و موت کا دینے والا ہے اور ہر شے پر اختیار رکھنے والا ہے'),
(5078, 57, 3, 'وہی اوّل ہے وہی آخر وہی ظاہر ہے وہی باطن اور وہی ہر شے کا جاننے والا ہے'),
(5079, 57, 4, 'وہی وہ ہے جس نے زمین و آسمان کو چھ دن میں پیدا کیا ہے اور پھر عرش پر اپنا اقتدار قائم کیا ہے وہ ہر اس چیز کو جانتا ہے جو زمین میں داخل ہوتی ہے یا زمین سے خارج ہوتی ہے اور جو چیز آسمان سے نازل ہوتی ہے اور آسمان کی طرف بلند ہوتی ہے اور وہ تمہارے ساتھ ہے تم جہاں بھی رہو اور وہ تمہارے اعمال کا دیکھنے والا ہے'),
(5080, 57, 5, 'آسمان و زمین کا ملک اسی کے لئے ہے اور تمام امور کی بازگشت اسی کی طرف ہے'),
(5081, 57, 6, 'وہ رات کو دن میں اور دن کو رات میں داخل کرتا ہے اور وہ سینوں کے رازوں سے بھی باخبر ہے'),
(5082, 57, 7, 'تم لوگ اللہ و رسول پر ایمان لے آؤ اور اس مال میں سے خرچ کرو جس میں اس نے تمہیں اپنا نائب قرار دیا ہے - تم میں سے جو لوگ ایمان لائے اور انہوں نے راسِ خدا میں خرچ کیا ان کے لئے اجر عظیم ہے'),
(5083, 57, 8, 'اور تمہیں کیا ہوگیا ہے کہ تم خدا پر ایمان نہیں لاتے ہو جب کہ رسو ل تمہیں دعوت دے رہا ہے کہ اپنے پروردگار پر ایمان لے آؤ اور خدا تم سے اس بات کا عہد بھی شلے چکا ہے اگر تم اعتبار کرنے والے ہو'),
(5084, 57, 9, 'وہی وہ ہے جو اپنے بندے پر کھلی ہوئی نشانیاں نازل کرتا ہے تاکہ تمہیں تاریکیوں سے نور کی طرف نکال کرلے آئے اور اللہ تمہارے حال پر یقینا مہربان و رحم کرنے والا ہے'),
(5085, 57, 10, 'اور تمہیں کیا ہوگیا ہے کہ تم راہ هخدا میں خرچ نہیں کرتے ہو جب کہ آسمان و زمین کی وراثت اسی کے لئے ہے اور تم میں سے فتح سے پہلے انفاق کرنے والا اور جہاد کرنے والا اس کے جیسا نہیں ہوسکتا ہے جو فتح کے بعد انفاق اور جہاد کرے - پہلے جہاد کرنے والے کا درجہ بہت بلند ہے اگرچہ خدا نے سب سے نیکی کا وعدہ کیا ہے اور وہ تمہارے جملہ اعمال سے باخبر ہے'),
(5086, 57, 11, 'کون ہے جو اللہ کو قرض حسنہ دے کہ وہ اس کو دوگنا کردے اور اس کے لئے باعزّت اجر بھی ہو'),
(5087, 57, 12, 'اس دن تم باایمان مرد اور باایمان عورتوں کو دیکھو گے کہ ان کا نور هایمان ان کے آگے آگے اور داہنی طرف چل رہا ہے اور ان سے کہا جارہا ہے کہ آج تمہاری بشارت کا سامان وہ باغات ہیں جن کے نیچے نہریں جاری ہیں اور تم ہمیشہ ان ہی میں رہنے والے ہو اور یہی سب سے بڑی کامیابی ہے'),
(5088, 57, 13, 'اس دن منافق مرد اور منافق عورتیں صاحبان هایمان سے کہیں گے کہ ذرا ہماری طرف بھی نظر مرحمت کرو کہ ہم تمہارے نور سے استفادہ کریں تو ان سے کہا جائے گا کہ اپنے پیچھے کی طرف پلٹ جاؤ اور اپنے شیاطین سے نور کی التماس کرو اس کے بعد ان کے درمیان ایک دیوار حائل کردی جائے گی جس کے اندر کی طرف رحمت ہوگی اور باہر کی طرف عذاب ہوگا'),
(5089, 57, 14, 'اور منافقین ایمان والوں سے پکار کر کہیں گے کہ کیا ہم تمہارے ساتھ نہیں تھے تو وہ کہیں گے بیشک مگر تم نے اپنے کو بلاؤں میں مبتلا کردیا اور ہمارے لئے مصائب کے منتظر رہے اور تم نے رسالت میں شک کیا اور تمہیں تمناؤں نے دھوکہ میں ڈالے رکھا یہاں تک کہ حکم خدا آگیا اور تمہیں دھوکہ باز شیطان نے دھوکہ دیا ہے'),
(5090, 57, 15, 'تو آج نہ تم سے کوئی فدیہ لیا جائے گا اور نہ کفار سے تم سب کا ٹھکانا جہنمّ ہے وہی تم سب کا صاحب هاختیار ہے اور تمہارا بدترین انجام ہے'),
(5091, 57, 16, 'کیا صاحبانِ ایمان کے لئے ابھی وہ وقت نہیں آیا ہے کہ ان کے دل ذکر خدا اور اس کی طرف سے نازل ہونے والے حق کے لئے نرم ہوجائیں اور وہ ان اہل کتاب کی طرح نہ ہوجائیں جنہیں کتاب دی گئی تو ایک عرصہ گزرنے کے بعد ان کے دل سخت ہوگئے اور ان کی اکثریت بدکار ہوگئی'),
(5092, 57, 17, 'یاد رکھو کہ خدا مردہ زمینوں کا زندہ کرنے والا ہے اور ہم نے تمام نشانیوں کو واضح کرکے بیان کردیا ہے تاکہ تم عقل سے کام لے سکو'),
(5093, 57, 18, 'بیشک خیرات کرنے والے مرد اور خیرات کرنے والی عورتیں اور جنہوں نے راسِ خدا میں اخلاص کے ساتھ مال خرچ کیا ہے ان کا اجر دوگنا کردیا جائے گا اور ان کے لئے بڑا باعزّت اجر ہے'),
(5094, 57, 19, 'اور جو لوگ اللہ اور رسول پر ایمان لائے وہی خدا کے نزدیک صدیق اور شہید کا درجہ رکھتے ہیں اور ا ن ہی کے لئے ان کا اجر اور نور ہے اور جنہوں نے کفر اختیار کرلیا اور ہماری آیات کی تکذیب کردی وہی دراصل اصحاب جہنّم ہیں'),
(5095, 57, 20, 'یاد رکھو کہ زندگی دنیا صرف ایک کھیل, تماشہ, آرائش, باہمی تفاخراور اموال و اولاد کی کثرت کا مقابلہ ہے اور بس جیسے کوئی بارش ہو جس کی قوت نامیہ کسان کو خوش کردے اور اس کے بعد وہ کھیتی خشک ہوجائے پھر تم اسے زرد دیکھو اور آخر میں وہ ریزہ ریزہ ہوجائے اور آخرت میں شدید عذاب بھی ہے اور مغفرت اور رضائے الۤہیبھی ہے اور زندگانی دنیا تو بس ایک دھوکہ کا سرمایہ ہے اور کچھ نہیں ہے'),
(5096, 57, 21, 'تم سب اپنے پروردگار کی مغفرت اور اس جّنت کی طرف سبقت کرو جس کی وسعت زمین و آسمان کے برابر ہے اور جسے ان لوگوں کے لئے مہیاّ کیا گیا ہے جو خدا اور رسول پر ایمان لائے ہیں یہی درحقیقت فضل خدا ہے جسے چاہتا ہے عطا کردیتا ہے اور اللہ تو بہت بڑے فضل کا مالک ہے'),
(5097, 57, 22, 'زمین میں کوئی بھی مصیبت وارد ہوتی ہے یا تمہارے نفس پر نازل ہوتی ہے تو نفس کے پیدا ہونے کے پہلے سے وہ کتاب الۤہی میں مقدر ہوچکی ہے اور یہ خدا کے لئے بہت آسان شے ہے'),
(5098, 57, 23, 'یہ تقدیر اس لئے ہے کہ جو تمہارے ہاتھ سے نکل جائے اس کا افسوس نہ کرو اور جو مل جائے اس پر غرور نہ کرو کہ اللہ اکڑنے والے مغرور افراد کو پسند نہیں کرتا ہے'),
(5099, 57, 24, 'جو خود بھی بخل کرتے ہیں اور دوسروں کو بھی بخل کا حکم دیتے ہیں اور جو بھی خدا سے روگردانی کرے گا اسے معلوم رہے کہ خدا سب سے بے نیاز اور قابلِ حمد و ستائش ہے'),
(5100, 57, 25, 'بیشک ہم نے اپنے رسولوں کو واضح دلائل کے ساتھ بھیجا ہے اور ان کے ساتھ کتاب اور میزان کو نازل کیا ہے تاکہ لوگ انصاف کے ساتھ قیام کریں اور ہم نے لوہے کو بھی نازل کیا ہے جس میں شدید جنگ کا سامان اور بہت سے دوسرے منافع بھی ہیں اور اس لئے کہ خدا یہ دیکھے کہ کون ہے جو بغیر دیکھے اس کی اور اس کے رسول کی مدد کرتا ہے اور یقینا اللہ بڑا صاحبِ قوت اور صاحبِ عزت ہے'),
(5101, 57, 26, 'اور ہم نے نوح علیھ السّلام اور ابراہیم علیھ السّلام کو بھیجا اور ان کی اولاد میں کتاب اور نبوت قرار دی تو ان میں سے کچھ ہدایت یافتہ تھے اور بہت سے فاسق اور بدکار تھے'),
(5102, 57, 27, 'پھر ہم نے ان ہی کے نقش قدم پر دوسرے رسول بھیجے اور ان کے پیچھے عیسٰی علیھ السّلام بن مریم علیھ السّلام کو بھیجا اور انہیں انجیل عطا کردی اور ان کا اتباع کرنے والوں کے دلوں میں مہربانی اور محبت قرار دے دی اور جس رہبانیت کو ان لوگوں نے ازخود ایجاد کرلیا تھا اور اس سے رضائے خدا کے طلبگار تھے اسے ہم نے ان کے اوپر فرض نہیں قرار دیا تھا اور انہوں نے خود بھی اس کی مکمل پاسداری نہیں کی تو ہم نے ان میں سے واقعا ایمان لانے والوں کو اجر عطا کردیا اور ان میں سے بہت سے تو بالکل فاسق اور بدکردار تھے'),
(5103, 57, 28, 'ایمان والو اللہ سے ڈرو اور رسول پر واقعی ایمان لے آؤ تاکہ خدا تمہیں اپنی رحمت کے دہرے حّصے عطا کردے اور تمہارے لئے ایسا نور قرار دے دے جس کی روشنی میں چل سکو اور تمہیں بخش دے اور اللہ بہت زیادہ بخشنے والا اور مہربان ہے'),
(5104, 57, 29, 'تاکہ اہلِ کتاب کو معلوم ہوجائے کہ وہ فضل خدا کے بارے میں کوئی اختیار نہیں رکھتے ہیں اور فضل تمام تر خدا کے ہاتھ میں ہے وہ جسے چاہتا ہے عطا کردیتا ہے اور وہ بہت بڑے فضل کا مالک ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'بیشک اللہ نے اس عورت کی بات سن لی جو تم سے اپنے شوہر کے بارے میں بحث کررہی تھی اور اللہ سے فریاد کررہی تھی اور اللہ تم دونوں کی باتیں سن رہا تھا کہ وہ سب کچھ سننے والا اور دیکھنے والا ہے'),
(5106, 58, 2, 'جو لوگ اپنی عورتوں سے ظہار کرتے ہیں ان کی عورتیں ان کی مائیں نہیں ہیں - مائیں تو صرف وہ عورتیں ہیں جنہوں نے پیدا کیا ہے .اور یہ لوگ یقینا بہت بفِی اور جھوٹی بات کہتے ہیں اور اللہ بہت معاف کرنے والا اور بخشنے والا ہے'),
(5107, 58, 3, 'جو لوگ تم میں سے اپنی عورتوں سے ظہار کریں اور پھر اپنی بات سے پلٹنا چاہیں انہیں چاہئے کہ عورت کو ہاتھ لگانے سے پہلے ایک غلام آزاد کریں کہ یہ خدا کی طرف سے تمہارے لئے نصیحت ہے اور خدا تمہارے اعمال سے خوب باخبر ہے'),
(5108, 58, 4, 'پھر کسی شخص کے لئے غلام ممکن نہ ہو تو آپس میں ایک دوسرے کو مس کرنے سے پہلے دو مہینے کے مسلسل روزے رکھے پھر یہ بھی ممکن نہ ہو تو ساٹھ مسکینوں کو کھانا کھلائے یہ اس لئے تاکہ تم خدا و رسول پر صحیح ایمان رکھو اور یہ اللہ کے مقرر کردہ حدود ہیں اور کافروں کے لئے بڑا دردناک عذاب ہے'),
(5109, 58, 5, 'بیشک جو لوگ خدا و رسول سے دشمنی کرتے ہیں وہ ویسے ہی ذلیل ہوں گے جیسے ان سے پہلے والے ذلیل ہوئے ہیں اور ہم نے کھلی ہوئی نشانیاں نازل کردی ہیں اور کافروں کے لئے رسوا کن عذاب ہے'),
(5110, 58, 6, 'جس دن خدا سب کو زندہ کرے گا اور انہیں ان کے اعمال سے باخبر کرے گا جسے اس نے محفوظ کر رکھا ہے اور ان لوگوں نے خود اِھلادیا ہے اور اللہ ہر شے کی نگرانی کرنے والا ہے'),
(5111, 58, 7, 'کیا تم نہیں دیکھتے ہو کہ اللہ زمین و آسمان کی ہر شے سے باخبر ہے .کہیں بھی تین آدمیوں کے درمیان راز کی بات نہیں ہوتی ہے مگر یہ کہ وہ ان کا چوتھا ہوتا ہے اور پانچ کی راز داری ہوتی ہے تو وہ ان کا چھٹا ہوتا ہے اور کم و بیش بھی کوئی رازداری ہوتی ہے تو وہ ان کے ساتھ ضرور رہتا ہے چاہے وہ کہیں بھی رہیں اس کے بعد روز هقیامت انہیں باخبر کرے گا کہ انہوں نے کیا کیا ہے کہ بیشک وہ ہر شے کا جاننے والا ہے'),
(5112, 58, 8, 'کیا تم نے ان لوگوں کو نہیں دیکھا جنہیں راز کی باتوں سے منع کیا گیا لیکن وہ پھر بھی ایسی باتیں کرتے ہیں اور گناہ اور ظلم اور رسول کی نافرمانی کے ساتھ راز کی باتیں کرتے ہیں اور جب تمہارے پاس آتے ہیں تو اس طرح مخاطب کرتے ہیں جس طرح خدا نے انہیں نہیں سکھایا ہے اور اپنے دل ہی دل میں کہتے ہیں کہ ہم غلطی پر ہیں تو خدا ہماری باتوں پر عذاب کیوں نہیں نازل کرتا - حالانکہ ان کے لئے جہنّم ہی کافی ہے جس میں انہیں جلنا ہے اور وہ بدترین انجام ہے'),
(5113, 58, 9, 'ایمان والو جب بھی راز کی باتیں کرو تو خبردار گناہ اور تعدی اور رسول کی نافرمانی کے ساتھ نہ کرنا بلکہ نیکی اور تقویٰ کے ساتھ باتیں کرنا اور اللہ سے ڈرتے رہنا کہ بالآخر اسی کی طرف پلٹ کر جانا ہے'),
(5114, 58, 10, 'یہ رازداری شیطان کی طرف سے صاحبانِ ایمان کو دکھ پہنچانے کے لئے ہوتی ہے حالانکہ وہ انہیں نقصان نہیں پہنچا سکتا ہے جب تک خدا اجازت نہ د ے دے اور صاحبانِ ایمان کا بھروسہ صرف اللہ پر ہوتا ہے'),
(5115, 58, 11, 'ایمان والو جب تم سے مجلس میں وسعت پیدا کرنے کے لئے کہا جائے تو دوسروں کو جگہ دیدو تاکہ خدا تمہیں جنّت میں وسعت دے سکے اور جب تم سے کہا جائے کہ اُٹھ جاؤ تو اُٹھ جاؤ کہ خدا صاحبان هایمان اور جن کو علم دیا گیا ہے ان کے درجات کو بلند کرنا چاہتا ہے اور اللہ تمہارے اعمال سے خوب باخبر ہے'),
(5116, 58, 12, 'ایمان والو جب بھی رسول سے کوئی راز کی بات کرو تو پہلے صدقہ نکال دو کہ یہی تمہارے حق میں بہتری اور پاکیزگی کی بات ہے پھر اگر صدقہ ممکن نہ ہو تو خدا بہت بخشنے والا اور مہربان ہے'),
(5117, 58, 13, 'کیا تم اس بات سے ڈر گئے ہو کہ اپنی رازدارانہ باتوں سے پہلے خیرات نکال دو اب جب کہ تم نے ایسا نہیں کیا ہے اور خدا نے تمہاری توبہ قبول کرلی ہے تو اب نماز قائم کرو اور زکوِٰ ادا کرو اور اللہ و رسول کی اطاعت کرو کہ اللہ تمہارے اعمال سے خوب باخبر ہے'),
(5118, 58, 14, 'کیا تم نے ان لوگوں کو نہیں دیکھا ہے جنہوں نے اس قوم سے دوستی کرلی ہے جس پر خدا نے عذاب نازل کیا ہے یہ نہ تم میں سے ہیں اور نہ ان میں سے اور یہ جھوٹی قسمیں کھاتے ہیں اور خود بھی اپنے جھوٹ سے باخبر ہیں'),
(5119, 58, 15, 'اللرُ نے ان کے لئے سخت عذاب مہّیا کر رکھا ہے کہ یہ بہت بفِے اعمال کررہے تھے'),
(5120, 58, 16, 'انہوں نے اپنی قسموں کو سپر بنالیا ہے اور راہ هخدا میں رکاوٹ ڈال رہے ہیں تو ان کے لئے رسوا کرنے والا عذاب ہے'),
(5121, 58, 17, 'اللرُ کے مقابلہ میں ان کا مال اور ان کی اولاد کوئی کام آنے والا نہیں ہے یہ سب جہنّمی ہیں اور وہیں ہمیشہ رہنے والے ہیں'),
(5122, 58, 18, 'جس دن خدا ان سب کو دوبارہ زندہ کرے گا اور یہ اس سے بھی ایسی ہی قسمیں کھائیں گے جیسی تم سے کھاتے ہیں اور ان کا خیال ہوگا کہ ان کے پاس کوئی بات ہے حالانکہ یہ بالکل جھوٹے ہیں'),
(5123, 58, 19, 'ان پر شیطان غالب آگیا ہے اور اس نے انہیں ذکر خدا سے غافل کردیا ہے آگاہ ہوجاؤ کہ یہ شیطان کا گروہ ہیں اور شیطان کا گروہ بہرحال خسارہ میں رہنے والا ہے'),
(5124, 58, 20, 'بیشک جو لوگ خدا و رسول سے دشمنی کرتے ہیں ان کا شمار ذلیل ترین لوگوں میں ہے'),
(5125, 58, 21, 'اللرُنے یہ لکھ دیا ہے کہ میں اور میرے رسول غالب آنے والے ہیں بیشک اللہ صاحبِ قوت اور صاحب هعزت ہے'),
(5126, 58, 22, 'آپ کبھی نہ دیکھیں گے کہ جو قوم اللہ اور روزِ آخرت پر ایمان رکھنے والی ہے وہ ان لوگوں سے دوستی کررہی ہے جو اللہ اور اس کے رسول سے دشمنی کرنے والے ہیں چاہے وہ ان کے باپ دادا یا اولاد یا برادران یا عشیرہ اور قبیلہ والے ہی کیوں نہ ہوں - اللرُنے صاحبان هایمان کے دلوں میں ایمان لکھ دیا ہے اور ان کی اپنی خاص روح کے ذریعہ تائید کی ہے اور وہ انہیں ان جنّتوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی اور وہ ا ن ہی میں ہمیشہ رہنے والے ہوں گے - خدا ان سے راضی ہوگا اور وہ خدا سے راضی ہوں گے یہی لوگ اللہ کا گروہ ہیں اور آگاہ ہوجاؤ کہ اللہ کا گروہ ہی نجات پانے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'اللہ ہی کے لئے محہُ تسبیح ہے جو کچھ بھی زمین و آسمان میں ہے اور وہی صاحب هعزت اور صاحب هحکمت ہے'),
(5128, 59, 2, 'وہی وہ ہے جس نے اہل کتاب کے کافروں کو پہلے ہی حشر میں ان کے وطن سے نکال باہر کیا تم تو اس کا تصور بھی نہیں کررہے تھے کہ یہ نکل سکیں گے اور ان کا بھی یہی خیال تھا کہ ان کے قلعے انہیں خدا سے بچالیں گے لیکن خدا ایسے رخ سے پیش آیا جس کا انہیں وہم و گمان بھی نہیں تھا اور ان کے دلوں میں رعب پیدا کردیا کہ وہ اپنے گھروں کو خود اپنے ہاتھوں سے اور صاحبان هایمان کے ہاتھوں سے اجاڑنے لگے تو صاحبانِ نظر عبرت حاصل کرو'),
(5129, 59, 3, 'اور اگر خدا نے ان کے حق میں جلاوطنی نہ لکھ دی ہوتی تو ان پر دنیا ہی میں عذاب نازل کردیتا اور آخرت میں تو جہنّم کا عذاب طے ہی ہے'),
(5130, 59, 4, 'یہ اس لئے کہ انہوں نے اللہ اور رسول سے اختلاف کیا اور جو خدا سے اختلاف کرے اس کے حق میں خدا سخت عذاب کرنے والا ہے'),
(5131, 59, 5, 'مسلمانو تم نے جو بھی کھجور کی شاخ کاٹی ہے یا اسے اس کی جڑوں پر رہنے دیا ہے یہ سب خدا کی اجازت سے ہوا ہے اور اس لئے تاکہ خدا فاسقین کو فِسوا کرے'),
(5132, 59, 6, 'اور خدا نے جو کچھ ان کی طرف سے مال غنیمت اپنے رسول کو دلوایا ہے جس کے لئے تم نے گھوڑے یا اونٹ کے ذریعہ کوئی دوڑ دھوپ نہیں کی ہے.... لیکن اللہ اپنے رسولوں کو غلبہ عنایت کرتا ہے اور وہ ہر شے پر قدرت رکھنے والا ہے'),
(5133, 59, 7, 'تو کچھ بھی اللہ نے اہل قریہ کی طرف سے اپنے رسول کو دلوایا ہے وہ سب اللہ ,رسول اور رسول کے قرابتدار, ایتام, مساکین اور مسافران غربت زدہ کے لئے ہے تاکہ سارا مال صرف مالداروں کے درمیان گھوم پھر کر نہ رہ جائے اور جو کچھ بھی رسول تمہیں دیدے اسے لے لو اور جس چیز سے منع کردے اس سے رک جاؤ اور اللہ سے ڈرو کہ اللہ سخت عذاب کرنے والا ہے'),
(5134, 59, 8, 'یہ مال ان مہاجر فقرائ کے لئے بھی ہے جنہیں ان کے گھروں اور اموال سے نکال باہر کردیا گیا ہے اور وہ صرف خدا کے فضل اور اس کی مرضی کے طلبگار ہیں اور خدا و رسول کی مدد کرنے والے ہیں کہ یہی لوگ دعوائے ایمان میں سچے ہیں'),
(5135, 59, 9, 'اور جن لوگوں نے دارالہجرت اور ایمان کو ان سے پہلے اختیار کیا تھا وہ ہجرت کرنے والوں کو دوست رکھتے ہیں اور جو کچھ انہیں دیا گیا ہے اپنے دلوں میں اس کی طرف سے کوئی ضرورت نہیں محسوس کرتے ہیں اور اپنے نفس پر دوسروں کو مقدم کرتے ہیں چاہے انہیں کتنی ہی ضرورت کیوں نہ ہو.... اور جسے بھی اس کیے نفس کی حرص سے بچالیا جائے وہی لوگ نجات پانے والے ہیں'),
(5136, 59, 10, 'اور جو لوگ ان کے بعد آئے اور ان کا کہنا یہ ہے کہ خدایا ہمیں معاف کردے اور ہمارے ان بھائیوں کو بھی جنہوں نے ایمان میں ہم پر سبقت کی ہے اور ہمارے دلوں میں صاحبانِ ایمان کے لئے کسی طرح کا کینہ نہ قرار دینا کہ تو بڑا مہربان اور رحم کرنے والا ہے'),
(5137, 59, 11, 'کیا تم نے منافقین کا حال نہیں دیکھا ہے کہ یہ اپنے اہل کتاب کافر بھائیوں سے کہتے ہیں کہ اگر تمہیں نکال دیا گیا تو ہم بھی تمہارے ساتھ نکل چلیں گے اور تمہارے بارے میں کسی کی اطاعت نہ کریں گے اور اگر تم سے جنگ کی گئی تو بھی ہم تمہاری مدد کریں گے اور خدا گواہ ہے کہ یہ سب بالکل جھوٹے ہیں'),
(5138, 59, 12, 'وہ اگر نکال بھی دیئے گئے تو یہ ان کے ساتھ نہ نکلیں گے اور اگر ان سے جنگ کی گئی تو یہ ہرگز ان کی مدد نہ کریں گے اور اگر مدد بھی کریں گے تو پیٹھ پھیر کر بھاگ جائیں گے پھر ان کی کوئی مدد کرنے والا نہ ہوگا'),
(5139, 59, 13, 'مسلمانو تم ان کے دلوں میں اللہ سے بھی زیادہ خوف رکھتے ہو یہ اس لئے کہ یہ قوم سمجھدار نہیں ہے'),
(5140, 59, 14, 'یہ کبھی تم سے اجتماعی طور پر جنگ نہ کریں گے مگر یہ کہ محفوظ بستیوں میں ہوں یا دیواروں کے پیچھے ہوں ان کی دھاک آپس میں بہت ہے اور تم یہ خیال کرتے ہو کہ یہ سب متحد ہیں ہرگز نہیں ان کے دلوں میں سخت تفرقہ ہے اور یہ اس لئے کہ اس قوم کے پاس عقل نہیں ہے'),
(5141, 59, 15, 'جس طرح کہ ابھی حال میں ان سے پہلے والوں کا حشر ہوا ہے کہ انہوں نے اپنے کام کے وبال کامزہ چکھ لیا ہے اور پھر ان کے لئے دردناک عذاب بھی ہے'),
(5142, 59, 16, 'ان کی مثال شیطان جیسی ہے کہ اس نے انسان سے کہا کہ کفر اختیار کرلے اور جب وہ کافر ہوگیا تو کہنے لگا کہ میں تجھ سے بیزار ہوں میں تو عالمین کے پروردگار سے ڈرتا ہوں'),
(5143, 59, 17, 'تو ان دونوں کا انجام یہ ہے کہ دونوں جہنّم میں ہمیشہ ہمیشہ رہنے والے ہیں اور یہی ظالمین کی واقعی سزا ہے'),
(5144, 59, 18, 'ایمان والو اللہ سے ڈرو اور ہر شخص یہ دیکھے کہ اس نے کل کے لئے کیا بھیج دیا ہے اور اللہ سے ڈرتے رہو کہ وہ یقینا تمہارے اعمال سے باخبر ہے'),
(5145, 59, 19, 'اور خبردار ان لوگوں کی طرح نہ ہوجانا جنہوں نے خدا کو اِھلادیا تو خدا نے خود ان کے نفس کو بھی اِھلا دیا اور وہ سب واقعی فاسق اور بدکار ہیں'),
(5146, 59, 20, 'اصحاب جنّت اور اصحاب جہنمّ ایک جیسے نہیں ہوسکتے, اصحاب جنّت وہی ہیں جو کامیاب ہونے والے ہیں'),
(5147, 59, 21, 'ہم اگر اس قرآن کو کسی پہاڑ پر نازل کردیتے تو تم دیکھتے کہ پہاڑ خورِ خدا سے لرزاں اور ٹکڑے ٹکڑے ہوا جارہا ہے اور ہم ان مثالوں کو انسانوں کے لئے اس لئے بیان کرتے ہیں کہ شاید وہ کچھ غور و فکر کرسکیں'),
(5148, 59, 22, 'وہ خدا وہ ہے جس کے علاوہ کوئی خدا نہیں ہے اور وہ حاضر و غائب سب کا جاننے والا عظیم اور دائمی رحمتوں کا مالک ہے'),
(5149, 59, 23, 'وہ اللہ وہ ہے جس کے علاوہ کوئی خدا نہیں ہے وہ بادشاہ, پاکیزہ صفات, بے عیب, امان دینے والا, نگرانی کرنے والا, صاحبِ عزت, زبردست اور کبریائی کا مالک ہے - وہ ان تمام باتوں سے پاک و پاکیزہ ہے جو مشرکین کیا کرتے ہیں'),
(5150, 59, 24, 'وہ ایسا خدا ہے جو پیدا کرنے والا, ایجاد کرنے والا اور صورتیں بنانے والا ہے اس کے لئے بہترین نام ہیں زمین و آسمان کا ہر ذرّہ اسی کے لئے محہُ تسبیح ہے اور وہ صاحبِ عزت و حکمت ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'ایمان والو خبردار میرے اور اپنے دشمنوں کو دوست مت بنانا کہ تم ان کی طرف دوستی کی پیش کش کرو جب کہ انہوں نے اس حق کا انکار کردیا ہے جو تمہارے پاس آچکا ہے اور وہ رسول کو اور تم کو صرف اس بات پر نکال رہے ہیں کہ تم اپنے پروردگار (اللرُ) پر ایمان رکھتے ہو .... اگر تم واقعا ہماری راہ میں جہاد اور ہماری مرضی کی تلاش میں گھر سے نکلے ہو تو ان سے خفیہ دوستی کس طرح کررہے ہو جب کہ میں تمہارے ظاہر و باطن سب کو جانتا ہوں اور جو بھی تم میں سے ایسا اقدام کرے گا وہ یقینا سیدھے راستہ سے بہک گیا ہے'),
(5152, 60, 2, 'یہ اگر تمہیں پاجائیں گے تو تمہارے دشمن ثابت ہوں گے اور تمہاری طرف برائی کے ارادے سے ہاتھ اور زبان سے اقدام کریں گے اور یہ چاہیں گے کہ تم بھی کافر ہوجاؤ'),
(5153, 60, 3, 'یقینا تمہارے قرابتدار اور تمہاری اولاد روزِ قیامت کام آنے والی نہیں ہے اس دن خدا تمہارے درمیان فیصلہ کردے گا اور وہ تمہارے اعمال سے خوب باخبرہے'),
(5154, 60, 4, 'تمہارے لئے بہترین نمونہ عمل ابراہیم علیھ السّلام اور ان کے ساتھیوں میں ہے جب انہوں نے اپنی قوم سے کہہ دیا کہ ہم تم سے اور تمہارے معبودوں سے بیزار ہیں - ہم نے تمہارا انکار کردیا ہے اور ہمارے تمہارے درمیان بغض اور عداوت بالکل واضح ہے یہاں تک کہ تم خدائے وحدہ لاشریک پر ایمان لے آؤ علاوہ ابراہیم علیھ السّلام کے اس قول کے جو انہوں نے اپنے مربّی باپ سے کہہ دیا تھا کہ میں تمہارے لئے استغفار ضرور کروں گا لیکن میں پروردگار کی طرف سے کوئی اختیار نہیں رکھتا ہوں. خدایا میں نے تیرے اوپر بھروسہ کیا ہے اور تیری ہی طرف رجوع کیا ہے اور تیری ہی طرف بازگشت بھی ہے'),
(5155, 60, 5, 'خدایا مجھے کفاّر کے لئے فتنہ و بلا نہ قرار دینا اور مجھے بخش دینا کہ تو ہی صاحب هعزت اور صاحبِ حکمت ہے'),
(5156, 60, 6, 'بیشک تمہارے لئے ان لوگوں میں بہترین نمونہ عمل ہے اس شخص کے لئے جو اللہ اور روزِ آخرت کا امیدوار ہے اور جو انحراف کرے گا خدا اس سے بے نیاز اور قابل حمد و ثنا ہے'),
(5157, 60, 7, 'قریب ہے کہ خدا تمہارے اور جن سے تم نے دشمنی کی ہے ان کے درمیان دوستی قرار دے دے کہ وہ صاحب هقدرت ہے اور بہت بڑا بخشنے والا اور مہربان ہے'),
(5158, 60, 8, 'وہ تمہیں ان لوگوں کے بارے میں جنہوں نے تم سے دین کے معاملہ میں جنگ نہیں کی ہے اور تمہیں وطن سے نہیں نکالا ہے اس بات سے نہیں روکتا ہے کہ تم ان کے ساتھ نیکی اور انصاف کرو کہ خدا انصاف کرنے والوں کو دوست رکھتا ہے'),
(5159, 60, 9, 'وہ تمہیں صرف ان لوگوں سے روکتا ہے جنہوں نے تم سے دین میں جنگ کی ہے اور تمہیں وطن سے نکال باہر کیا ہے اور تمہارے نکالنے پر دشمنوں کی مدد کی ہے کہ ان سے دوستی کرو اور جو ان سے دوستی کرے گا وہ یقینا ظالم ہوگا'),
(5160, 60, 10, '~ ایمان والو جب تمہارے پاس ہجرت کرنے والی مومن عورتیں آئیں تو پہلے ان کا امتحان کرو کہ اللہ ان کے ایمان کو خوب جانتا ہے پھر اگر تم بھی دیکھو کہ یہ مومنہ ہیں توخبردار انہیں کفاّر کی طرف واپس نہ کرنا - نہ وہ ان کے لئے حلال ہیں اور نہ یہ ان کے لئے حلال ہیں اور جو خرچہ کفاّر نے مہر کا دیا ہے وہ انہیں واپس کردو اور تمہارے لئے کوئی حرج نہیں ہے کہ تم ان کی اجرت (مہر) دینے کے بعد ان سے نکاح کرلو اور خبردار کافر عورتوں کی عصمت پکڑ کر نہ رکھو اور جو تم نے خرچ کیا ہے وہ کفار سے لے لو اور جو انہوں نے خرچ کیا ہے وہ تم سے لے لیں کہ یہی حکم خدا ہے جس کا فیصلہ خدا نے تمہارے درمیان کیا ہے اور وہ بڑا صاحب ه علم و حکمت ہے'),
(5161, 60, 11, 'اور اگر تمہاری کوئی بیوی کفاّر کی طرف چلی جائے اور پھر تم ان کو سزا دو تو جن کی بیوی چلی گئی ہے ان کا خرچ مال غنیمت میں سے دے دو اور اس اللہ سے بہرحال ڈرتے رہو جس پر ایمان رکھنے والے ہو'),
(5162, 60, 12, 'پیغمبر اگر ایمان لانے والی عورتیں آپ کے پاس اس امر پر بیعت کرنے کے لئے آئیں کہ کسی کو خدا کا شریک نہیں بنائیں گی اور چوری نہیں کریں گی - زنا نہیں کریں گی - اولاد کو قتل نہیں کریں گی اور اپنے ہاتھ پاؤں کے سامنے سے کوئی بہتان (لڑکا) لے کر نہیں آئیں گی اور کسی نیکی میں آپ کی مخالفت نہیں کریں گی تو آپ ان سے بیعت کا معاملہ کرلیں اور ان کے حق میں استغفار کریں کہ خدا بڑا بخشنے والا اور مہربان ہے'),
(5163, 60, 13, 'ایمان والو خبردار اس قوم سے ہرگز دوستی نہ کرنا جس پر خدا نے غضب نازل کیا ہے کہ وہ آخرت سے اسی طرح مایوس ہوں گے جس طرح کفاّر قبر والوں سے مایوس ہوجاتے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'زمین و آسمان کا ہر ذرہ اللہ کی تسبیح میں مشغول ہے اور وہی صاحبِ عزت اور صاحب هحکمت ہے'),
(5165, 61, 2, 'ایمان والو آخر وہ بات کیوں کہتے ہو جس پر عمل نہیں کرتے ہو'),
(5166, 61, 3, 'اللرُکے نزدیک یہ سخت ناراضگی کا سبب ہے کہ تم وہ کہو جس پر عمل نہیں کرتے ہو'),
(5167, 61, 4, 'بیشک اللہ ان لوگوں کو دوست رکھتا ہے جو اس کی راہ میں اس طرح صف باندھ کر جہاد کرتے ہیں جس طرح سیسہ پلائی ہوئی دیواریں'),
(5168, 61, 5, 'اور اس وقت کو یاد کرو جب موسٰی علیھ السّلامنے اپنی قوم سے کہا کہ قوم والو مجھے کیوں اذیت دے رہے ہو تمہیں تو معلوم ہے کہ میں تمہاری طرف اللہ کا رسول علیھ السّلام ہوں پھر جب وہ لوگ ٹیڑھے ہوگئے تو خدا نے بھی ان کے دلوں کو ٹیڑھا ہی کردیا کہ اللہ بدکار قوم کی ہدایت نہیں کرتا ہے'),
(5169, 61, 6, 'اور اس وقت کو یاد کرو جب عیسٰی علیھ السّلام بن مریم علیھ السّلام نے کہا کہ اے بنیآآ اسرائیل میں تمہاری طرف اللہ کا رسول ہوں اپنے پہلے کی کتاب توریت کی تصدیق کرنے والا اور اپنے بعد کے لئے ایک رسول کی بشارت دینے والا ہوں جس کا نام احمد ہے لیکن پھر بھی جب وہ معجزات لے کر آئے تو لوگوں نے کہہ دیا کہ یہ تو کِھلا ہوا جادو ہے'),
(5170, 61, 7, 'اور اس سے بڑا ظالم کون ہوگا جو خدا پر جھوٹ الزام لگائے جب کہ اسے اسلام کی دعوت دی جارہی ہو اور اللہ کبھی ظالم قوم کی ہدایت نہیں کرتا ہے'),
(5171, 61, 8, 'یہ لوگ چاہتے ہیں کہ نور خدا کو اپنے منہ سے بجھادیں اور اللہ اپنے نور کو مکمل کرنے والا ہے چاہے یہ بات کفار کو کتنی ہی ناگوار کیوں نہ ہو'),
(5172, 61, 9, 'وہی خدا وہ ہے جس نے اپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجا ہے تاکہ اسے تمام ادیان پر غالب بنائے چاہے یہ بات مشرکین کو کتنی ہی ناگوار کیوں نہ ہو'),
(5173, 61, 10, 'ایمان والو کیا میں تمہیں ایک ایسی تجارت کی طرف رہنمائی کروں جو تمہیں دردناک عذاب سے بچالے'),
(5174, 61, 11, 'اللرُ اور اس کے رسول پر ایمان لے آؤ اور راسِ خدا میں اپنے جان و مال سے جہاد کرو کہ یہی تمہارے حق میں سب سے بہتر ہے اگر تم جاننے والے ہو'),
(5175, 61, 12, 'وہ تمہارے گناہوں کو بھی بخش دے گا اور تمہیں ان جنتوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی اور اس ہمیشہ رہنے والی جنّت میں پاکیزہ مکانات ہوں گے اور یہی بہت بڑی کامیابی ہے'),
(5176, 61, 13, 'اور ایک چیز اور بھی جسے تم پسند کرتے ہو.... اللرُ کی طرف سے مدد اور قریبی فتح اور آپ مومنین کو بشارت دے دیجئے'),
(5177, 61, 14, 'ایمان والو اللہ کے مددگار بن جاؤ جس طرح عیسٰی بن مریم علیھ السّلام نے اپنے حواریین سے کہا تھا کہ اللہ کی راہ میں میرا مددگار کون ہے تو حواریین نے کہا کہ ہم اللہ کے ناصر ہیں لیکن پھربنی اسرائیل میں سے ایک گروہ ایمان لے آیا اور ایک گروہ کافر ہوگیا تو ہم نے صاحبان هایمان کی دشمن کے مقابلہ میں مدد کردی تو وہ غالب آکر رہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, 'زمین و آسمان کا ہر ذرہ اس خدا کی تسبیح کررہا ہے جو بادشاہ پاکیزہ صفات, اور صاحب هعزت اور صاحب هحکمت ہے'),
(5179, 62, 2, 'اس خدا نے مکہ والوں میں ایک رسول بھیجا ہے جو ان ہی میں سے تھا کہ ان کے سامنے آیات کی تلاوت کرے ,ان کے نفوس کو پاکیزہ بنائے اور انہیں کتاب و حکمت کی تعلیم دے اگرچہ یہ لوگ بڑی کھلی ہوئی گمراہی میں مبتلا تھے'),
(5180, 62, 3, 'اور ان میں سے ان لوگوں کی طرف بھی جو ابھی ان سے ملحق نہیں ہوئے ہیں اور وہ صاحبِ عزت بھی ہے اور صاحب هحکمت بھی'),
(5181, 62, 4, 'یہ ایک فضل هخدا ہے وہ جسے چاہتا ہے عطا کردیتا ہے اور وہ بڑے عظیم فضل کا مالک ہے'),
(5182, 62, 5, 'ان لوگوں کی مثال جن پر توریت کا بار رکھا گیا اور وہ اسے اُٹھا نہ سکے اس گدھے کی مثال ہے جو کتابوں کا بوجھ اٹھائے ہوئے ہو یہ بدترین مثال ان لوگوں کی ہے جنہوں نے آیات الۤہی کی تکذیب کی ہے اور خدا کسی ظالم قوم کی ہدایت نہیں کرتا ہے'),
(5183, 62, 6, 'آپ کہہ دیجئے کہ یہودیو اگر تمہارا خیال یہ ہے کہ تمام انسانوں میں تم ہی اللہ کے دوست ہو تو اگر اپنے دعویٰ میں سچے ہو تو موت کی تمناّکرو'),
(5184, 62, 7, 'اور یہ ہر گز موت کی تمنا ّنہیں کریں گے کہ ان کے ہاتھوں نے پہلے بہت کچھ کر رکھا ہے اور اللہ ظالمین کے حالات کو خوب جانتا ہے'),
(5185, 62, 8, 'آپ کہہ دیجئے کہ تم جس موت سے فرار کررہے ہو وہ خود تم سے ملاقات کرنے والی ہے اس کے بعد تم اس کی بارگاہ میں پلٹائے جاؤ گے جو حاضر و غائب سب کا جاننے والا ہے اور وہ تمہیں باخبر کرے گا کہ تم وار دنیا میں کیا کررہے تھے'),
(5186, 62, 9, 'ایمان والو جب تمہیں جمعہ کے دن نماز کے لئے پکارا جائے تو ذکر خدا کی طرف دوڑ پڑو اور کاروبار بند کردو کہ یہی تمہارے حق میں بہتر ہے اگر تم جاننے والے ہو'),
(5187, 62, 10, 'پھر جب نماز تمام ہوجائے تو زمین میں منتشر ہوجاؤ اور فضل خدا کو تلاش کرو اور خدا کو بہت یاد کرو کہ شاید اسی طرح تمہیں نجات حاصل ہوجائے'),
(5188, 62, 11, 'اور اے پیغمبر یہ لوگ جب تجارت یا لہو و لعب کو دیکھتے ہیں تو اس کی طرف دوڑ پڑتے ہیں اور آپ کو تنہا کھڑا چھوڑ دیتے ہیں آپ ان سے کہہ دیجئے کہ خدا کے پاس جو کچھ بھی ہے وہ اس کھیل اور تجارت سے بہرحال بہتر ہے اور وہ بہترین رزق دینے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'پیغمبر یہ منافقین آپ کے پاس آتے ہیں تو کہتے ہیں کہ ہم گواہی دیتے ہیں کہ آپ اللہ کے رسول ہیں اور اللہ بھی جانتا ہے کہ آپ اس کے رسول ہیںلیکن اللہ گواہی دیتا ہے کہ یہ منافقین اپنے دعویٰ میں جھوٹے ہیں'),
(5190, 63, 2, 'انہوں نے اپنی قسموں کو سپر بنالیا ہے اور لوگوں کو راسِ خدا سے روک رہے ہیں یہ ان کے بدترین اعمال ہیں جو یہ انجام دے رہے ہیں'),
(5191, 63, 3, 'یہ اس لئے ہے کہ یہ پہلے ایمان لائے پھر کافر ہوگئے تو ان کے دلوں پر مہر لگادی گئی تو اب کچھ نہیں سمجھ رہے ہیں'),
(5192, 63, 4, 'اور جب آپ انہیں دیکھیں گے تو ان کے جسم بہت اچھے لگیں گے اور بات کریں گے تو اس طرح کہ آپ سننے لگیں لیکن حقیقت میں یہ ایسے ہیں جیسے دیوار سے لگائی ہوئی سوکھی لکڑیاں کہ یہ ہر چیخ کو اپنے ہی خلاف سمجھتے ہیں اور یہ واقعا دشمن ہیں ان سے ہوشیار رہئے خدا انہیں غارت کرے یہ کہاں بہکے چلے جارہے ہیں'),
(5193, 63, 5, 'اور جب ان سے کہا جاتا ہے کہ آؤ رسول اللہ تمہارے حق میں استغفار کریں گے تو سر پھرا لیتے ہیں اور تم دیکھو گے کہ استکبار کی بنا پر منہ بھی موڑ لیتے ہیں'),
(5194, 63, 6, 'ان کے لئے سب برابر ہے چاہے آپ استغفار کریں یا نہ کریں خدا انہیں بخشنے والا نہیں ہے کہ یقینا اللہ بدکار قوم کی ہدایت نہیں کرتا ہے'),
(5195, 63, 7, 'یہی وہ لوگ ہیں جو کہتے ہیں کہ رسول اللہ کے ساتھیوں پر کچھ خرچ نہ کرو تاکہ یہ لوگ منتشر ہوجائیں حالانکہ آسمان و زمین کے سارے خزانے اللہ ہی کے لئے ہیں اور یہ منافقین اس بات کو نہیں سمجھ رہے ہیں'),
(5196, 63, 8, 'یہ لوگ کہتے ہیں کہ اگر ہم مدینہ واپس آگئے تو ہم صاحبان هعزت ان ذلیل افراد کو نکال باہر کریں گے حالانکہ ساری عزت اللہ , رسول اور صاحبان هایمان کے لئے ہے اور یہ منافقین یہ جانتے بھی نہیں ہیں'),
(5197, 63, 9, 'ایمان والو خبردار تمہارے اموال اور تمہاری اولاد تمہیں یاد خدا سے غافل نہ کردے کہ جو ایسا کرے گا وہ یقینا خسارہ والوں میں شمار ہوگا'),
(5198, 63, 10, 'اور جو رزق ہم نے عطا کیا ہے اس میں سے ہماری راہ میں خرچ کرو قبل اس کے کہ تم میں سے کسی کو موت آجائے اور وہ یہ کہے کہ خدایا ہمیں تھوڑے دنوں کی مہلت کیوں نہیں دے دیتا ہے کہ ہم خیرات نکالیں اور نیک بندوں میں شامل ہوجائیں'),
(5199, 63, 11, 'اور ہرگز خدا کسی کی اجل کے آجانے کے بعد اس میں تاخیر نہیں کرتا ہے اور وہ تمہارے اعمال سے خوب باخبر ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, 'زمین و آسمان کا ہر ذرہ خدا کی تسبیح کررہا ہے کہ اسی کے لئے ملک ہے اور اسی کے لئے حمد ہے اور وہی ہر شے پر قدرت رکھنے والا ہے'),
(5201, 64, 2, 'اسی نے تم سب کو پیدا کیا ہے پھر تم میں سے کچھ مومن ہیں اور کچھ کافر اور اللہ تمہارے اعمال کو خوب دیکھ رہا ہے'),
(5202, 64, 3, 'اسی نے آسمان و زمین کو حق کے ساتھ پیدا کیا ہے اور تمہاری صورت کو بھی انتہائی حسن بنایا ہے اور پھر اسی کی طرف سب کی بازگشت بھی ہے'),
(5203, 64, 4, 'وہ زمین و آسمان کی ہر شے سے باخبر ہے اور ان تمام چیزوں کو جانتا ہے جن کا تم اظہار کرتے ہو یا جنہیں تم چھپاتے ہو اور وہ سینوں کے رازوں سے بھی باخبر ہے'),
(5204, 64, 5, 'کیا تمہارے پاس پہلے کفر اختیار کرنے والوں کی خبر نہیں آئی ہے کہ انہوں نے اپنے کام کے وبال کا مزہ چکھ لیا اور ان کے لئے دردناک عذاب ہے'),
(5205, 64, 6, 'یہ اس لئے کہ ان کے پاس رسول کِھلی ہوئی نشانیاں لے کر آتے تھے تو انہوں نے صاف کہہ دیا کہ کیا بشر ہماری ہدایت کرے گا اور یہ کہہ کر انکار کردیا اور منہ پھیر لیا تو خدا بھی ان سے بے نیاز ہے اور وہ قابلِ تعریف غنی ہے'),
(5206, 64, 7, 'ان کفاّر کا خیال یہ ہے کہ انہیں دوبارہ نہیں اٹھایا جائے گا تو کہہ دیجئے کہ میرے پروردگار کی قسم تمہیں دوبارہ زندہ کیا جائے گا اور پھر بتایا جائے گا کہ تم نے کیا کیا, کیا ہے اور یہ کام خدا کے لئے بہت آسان ہے'),
(5207, 64, 8, 'لہذا خدا اور رسول اور اس نور پر ایمان لے آؤ جسے ہم نے نازل کیا ہے کہ اللہ تمہارے اعمال سے خوب باخبر ہے'),
(5208, 64, 9, 'وہ قیامت کے دن تم سب کو جمع کرے گا اور وہی ہارجیت کا دن ہوگا اور جو اللہ پر ایمان رکھے گا اور نیک اعمال کرے گا خدا اس کی برائیوں کو دور کردے گا اور اسے ان باغات میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی وہ ان ہی میں ہمیشہ ہمیشہ رہیں گے اور یہی سب سے بڑی کامیابی ہے'),
(5209, 64, 10, 'اور جن لوگوں نے کفر اختیار کیا اور ہماری آیات کو جھٹلایا وہ اصحاب جہنّم ہیں اور وہیں ہمیشہ رہنے والے ہیں اور یہ ان کا بدترین انجام ہے'),
(5210, 64, 11, 'کوئی مصیبت نازل نہیں ہوتی ہے مگر خدا کی اجازت سے اور جو صاحبِ ایمان ہوتا ہے خدا اس کے دل کی ہدایت کردیتا ہے اور خدا ہر شے کا خوب جاننے والا ہے'),
(5211, 64, 12, 'اور تم لوگ خدا کی اطاعت کرو اور رسول کی اطاعت کرو پھر اگر انحراف کرو گے تو رسول کی ذمہ داری واضح طور پر پیغام پہچادینے کے علاوہ کچھ نہیں ہے'),
(5212, 64, 13, 'اللرُ ہی وہ ہے جس کے علاوہ کوئی خدا نہیں ہے اور تمام صاحبانِ ایمان کو اسی پر بھروسہ کرنا چاہئے'),
(5213, 64, 14, 'ایمان والو - تمہا---ری بیویوں اور تمہاری اولاد میں سے بعض تمہارے دشمن ہیں لہذا ان سے ہوشیار رہو اور اگر انہیں معاف کردو اور ان سے درگزر کرو اور انہیں بخش دو تو اللہ بھی بہت بخشنے والا اور مہربان ہے'),
(5214, 64, 15, 'تمہارے اموال اور تمہاری اولاد تمہارے لئے صرف امتحان کا ذریعہ ہیں اور اجر عظیم صرف اللہ کے پاس ہے'),
(5215, 64, 16, 'لہذا جہاں تک ممکن ہو اللہ سے ڈرو اور اس کی بات سنو اور اطاعت کرو اور راسِ خدا میں خرچ کرو کہ اس میں تمہارے لئے خیر ہے اور جو اپنے ہی نفس کے بخل سے محفوظ ہوجائے وہی لوگ فلاح اور نجات پانے والے ہیں'),
(5216, 64, 17, 'اگر تم اللہ کو قرض حسنہ دو گے تو وہ اسے دوگنا بنادے گا اور تمہیں معاف بھی کردے گا کہ وہ بڑا قدر داں اور برداشت کرنے والا ہے'),
(5217, 64, 18, 'وہ حاضر و غائب کا جاننے والا اور صاحب هعزت و حکمت ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اے پیغمبر ! جب تم لوگ عورتوں کو طلاق دو تو انہیں عدت کے حساب سے طلاق دو اور پھر عدت کا حساب رکھو اور اللہ سے ڈرتے رہو کہ وہ تمہارا پروردگار ہے اور خبردار انہیں ان کے گھروں سے مت نکالنا اور نہ وہ خود نکلیں جب تک کوئی کِھلا ہوا گناہ نہ کریں - یہ خدائی حدود ہیں اور جو خدائی حدود سے تجاوز کرے گا اس نے اپنے ہی نفس پر ظلم کیا ہے تمہیں نہیں معلوم کہ شاید خدا اس کے بعد کوئی نئی بات ایجاد کردے'),
(5219, 65, 2, 'پھر جب وہ اپنی مدت کو پورا کرلیں تو انہیں نیکی کے ساتھ روک لو یا نیکی ہی کے ساتھ رخصت کردو اور طلاق کے لئے اپنے میں سے دو عادل افراد کو گواہ بناؤ اور گواہی کو صرف خدا کے لئے قائم کرو نصیحت ان لوگوں کو کی جارہی ہے جو خدا اور روز هآخرت پر ایمان رکھتے ہیں اور جو بھی اللہ سے ڈرتا ہے اللہ اس کے لئے نجات کی راہ پیدا کردیتا ہے'),
(5220, 65, 3, 'اور اسے ایسی جگہ سے رزق دیتا ہے جس کا خیال بھی نہیں ہوتا ہے اور جو خدا پر بھروسہ کرے گا خدا اس کے لئے کافی ہے بیشک خدا اپنے حکم کا پہنچانے والا ہے اس نے ہر شے کے لئے ایک مقدار معین کردی ہے'),
(5221, 65, 4, 'اور تمہاری عورتوں میں جو حیض سے مایوس ہوچکی ہیں اگر ان کے یائسہ ہونے میں شک ہو تو ان کا عدہ تین مہینے ہے اور اسی طرح وہ عورتیں جن کے یہاں حیض نہیں آتا ہے اور حاملہ عورتوں کا عدہ وضع حمل تک ہے اور جو خدا سے ڈرتا ہے خدا اس کے امر میں آسانی پیدا کردیتا ہے'),
(5222, 65, 5, 'یہ حکم خدا ہے جسے تمہاری طرف اس نے نازل کیا ہے اور جو خدا سے ڈرتا ہے خدا اس کی برائیوں کو دور کردیتا ہے اور اس کے اجر میں اضافہ کردیتا ہے'),
(5223, 65, 6, 'اور ان مطلقات کو سکونت دو جیسی طاقت تم رکھتے ہو اور انہیں اذیت مت دو کہ اس طرح ان پر تنگی کرو اور اگر حاملہ ہوں تو ان پر اس وقت تک انفاق کرو جب تک وضع حمل نہ ہوجائے پھر اگر وہ تمہارے بچوں کو دودھ پلائیں تو انہیں ان کی اجرت دو اور اسے آپس میں نیکی کے ساتھ طے کرو اور اگر آپس میں کشاکش ہوجائے تو دوسری عورت کو دودھ پلانے کا موقع دو'),
(5224, 65, 7, 'صاحبِ وسعت کو چاہئے کہ اپنی وسعت کے مطابق خرچ کرے اور جس کے رزق میں تنگی ہے وہ اسی میں سے خرچ کرے جو خدا نے اسے دیا ہے کہ خدا کسی نفس کو اس سے زیادہ تکلیف نہیں دیتا ہے جتنا اسے عطا کیا گیا ہے عنقریب خدا تنگی کے بعد وسعت عطا کردے گا'),
(5225, 65, 8, 'اور کتنی ہی بستیاں ہیں جنہوں نے حکم خدا و رسول کی نافرمانی کی تو ہم نے ان کا شدید محاسبہ کرلیا اور انہیں بدترین عذاب میں متبلا کردیا'),
(5226, 65, 9, 'پھر انہوں نے اپنے کام کے وبال کا مزہ چکھ لیا اور آخری انجام خسارہ ہی قرار پایا'),
(5227, 65, 10, 'اللرُ نے ان کے لئے شدید قسم کا عذاب مہیّا کر رکھا ہے لہذا ایمان والو اور عقل والو اللہ سے ڈرو کہ اس نے تمہاری طرف اپنے ذکر کو نازل کیا ہے'),
(5228, 65, 11, 'وہ رسول جو اللہ کی واضح آیات کی تلاوت کرتا ہے کہ ایمان اور نیک عمل کرنے والوں کو تاریکیوں سے نکال کر نور کی طرف لے آئے اور جو خدا پر ایمان رکھے گا اور نیک عمل کرے گا خدا اسے ان جنتوں میں داخل کرے گا جن کے نیچے نہریں جاری ہوں گی وہ ان ہی میں ہمیشہ رہنے والے ہیں اور اللہ نے انہیں یہ بہترین رزق عطا کیا ہے'),
(5229, 65, 12, 'اللرُہی وہ ہے جس نے ساتوں آسمانوں کو پیدا کیا ہے اور زمینوں میں بھی ویسی ہی زمینیں بنائی ہیں اس کے احکام ان کے درمیان نازل ہوتے رہتے ہیں تاکہ تمہیں یہ معلوم رہے کہ وہ ہر شے پر قادر ہے اور اس کا علم تمام اشیائ کو محیط ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'پیغمبر آپ اس شے کو کیوں ترک کررہے ہیں جس کو خدا نے آپ کے لئے حلال کیا ہے کیا آپ ازواج کی مرضی کے خواہشمند ہیں اور اللہ بہت بخشنے والا اور مہربان ہے'),
(5231, 66, 2, 'خدا نے فرض قرار دیا ہے کہ اپنی قسم کو کفارہ دے کر ختم کردیجئے اور اللہ آپ کا مولا ہے اور وہ ہر چیز کا جاننے والا اور صاحبِ حکمت ہے'),
(5232, 66, 3, 'اور جب نبی نے اپنی بعض ازواج سے راز کی بات بتائی اور اس نے دوسری کو باخبر کردیا اور خدا نے نبی پر ظاہر کردیا تو نبی نے بعض باتوں کو اسے بتایا اور بعض سے اعراض کیا پھر جب اسے باخبر کیا تو اس نے پوچھا کہ آپ کو کس نے بتایا ہے تو آپ نے کہا کہ خدائے علیم و خبیرنے'),
(5233, 66, 4, 'اب تم دونوں توبہ کرو کہ تمہارے دلوں میں کجی پیدا ہوگئی ہے ورنہ اگر اس کے خلاف اتفاق کرو گی تو یاد رکھو کہ اللہ اس کا سرپرست ہے اور جبریل اور نیک مومنین اور ملائکہ سب اس کے مددگار ہیں'),
(5234, 66, 5, 'وہ اگر تمہیں طلاق بھی دے دے گا تو خدا تمہارے بدلے اسے تم سے بہتر بیویاں عطا کردے گا مسلمہ, مومنہ, فرمانبردار, توبہ کرنے والی, عبادت گزار, روزہ رکھنے والی, کنواری اور غیر کنواری سب'),
(5235, 66, 6, 'ایمان والو اپنے نفس اور اپنے اہل کو اس آگ سے بچاؤ جس کا ایندھن انسان اور پتھر ہوں گے جس پر وہ ملائکہ معین ہوں گے جو سخت مزاج اور تندوتیز ہیں اور خدا کے حکم کی مخالفت نہیں کرتے ہیں اور جو حکم دیا جاتا ہے اسی پر عمل کرتے ہیں'),
(5236, 66, 7, 'اور اے کفر اختیار کرنے والو آج کوئی عذر پیش نہ کرو کہ آج تمہیں تمہارے اعمال کی سزا دی جائے گی'),
(5237, 66, 8, 'ایمان والو خلوص دل کے ساتھ توبہ کرو عنقریب تمہارا پروردگار تمہاری برائیوں کو مٹا دے گا اور تمہیں ان جنتوں میں داخل کردے گا جن کے نیچے نہریں جاری ہوں گی اس دن خدا اپنے نبی اور صاحبانِ ایمان کو رسوا نہیں ہونے دے گا ان کا نور ان کے آگے آگے اور داہنی طرف چل رہا ہوگا اور وہ کہیں گے کہ خدایا ہمارے لئے ہمارے نور کو مکمل کردے اور ہمیں بخش دے کہ تو یقینا ہر شے پر قدرت رکھنے والا ہے'),
(5238, 66, 9, 'پیغمبر آپ کفر اور منافقین سے جہاد کریں اور ان پر سختی کریں اور ان کا ٹھکانا جہنّم ہے اور وہی بدترین انجام ہے'),
(5239, 66, 10, 'خدا نے کفر اختیار کرنے والوں کے لئے زوجہ نوح اور زوجہ لوط کی مثال بیان کی ہے کہ یہ دونوں ہمارے نیک بندوں کی زوجیت میں تھیں لیکن ان سے خیانت کی تو اس زوجیت نے خدا کی بارگاہ میں کوئی فائدہ نہیں پہنچایا اور ان سے کہہ دیا گیا کہ تم بھی تمام جہنمّ میں داخل ہونے والوں کے ساتھ داخل ہوجاؤ'),
(5240, 66, 11, 'اور خدا نے ایمان والوں کے لئے فرعون کی زوجہ کی مثال بیان کی ہے کہ اس نے دعا کی کہ پروردگار میرے لئے جنّت میں ایک گھر بنادے اور مجھے فرعون اور اس کے کاروبار سے نجات دلادے اور اس پوری ظالم قوم سے نجات عطا کردے'),
(5241, 66, 12, 'اور مریم بنت عمران علیھ السّلام کی مثال جس نے اپنی عفت کی حفاظت کی تو ہم نے اس میں اپنی روح پھونک دی اور اس نے اپنے رب کے کلمات اور کتابوں کی تصدیق کی اور وہ ہمارے فرمانبردار بندوں میں سے تھی');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بابرکت ہے وہ ذات جس کے ہاتھوں میں سارا ملک ہے اور وہ ہر شے پر قادر و مختار ہے'),
(5243, 67, 2, 'اس نے موت و حیات کو اس لئے پیدا کیا ہے تاکہ تمہاری آزمائش کرے کہ تم میں حَسن عمل کے اعتبار سے سب سے بہتر کون ہے اور وہ صاحب هعزّت بھی ہے اور بخشنے والا بھی ہے'),
(5244, 67, 3, 'اسی نے سات آسمان تہ بہ تہ پیدا کئے ہیں اور تم رحمان کی خلقت میں کسی طرح کا فرق نہ دیکھو گے پھر دوبارہ نگاہ اٹھا کر دیکھو کہیں کوئی شگاف نظر آتا ہے'),
(5245, 67, 4, 'اس کے بعد بار بار نگاہ ڈالو دیکھو نگاہ تھک کر پلٹ آئے گی لیکن کوئی عیب نظر نہ آئے گا'),
(5246, 67, 5, 'ہم نے آسمان دنیا کو چراغوں سے مزین کیا ہے اور انہیں شیاطین کو سنگسار کرنے کا ذریعہ بنادیا ہے اور ان کے لئے جہنمّ کا عذاب الگ مہیّا کر رکھا ہے'),
(5247, 67, 6, 'اور جن لوگوں نے اپنے پروردگار کا انکار کیا ہے ان کے لئے جہنمّ کا عذاب ہے اور وہی بدترین انجام ہے'),
(5248, 67, 7, 'جب بھی وہ اس میں ڈالے جائیں گے اس کی چیخ سنیں گے اور وہ جوش مار رہا ہوگا'),
(5249, 67, 8, 'بلکہ قریب ہوگا کہ جوش کی شدت سے پھٹ پڑے جب بھی اس میں کسی گروہ کو ڈالا جائے گا تو اس کے داروغہ ان سے پوچھیں گے کہ کیا تمہارے پاس کوئی ڈرانے والا نہیں آیا تھا'),
(5250, 67, 9, 'تو وہ کہیں گے کہ آیا تو تھا لیکن ہم نے اسے جھٹلادیا اور یہ کہہ دیا کہ اللہ نے کچھ بھی نازل نہیں کیا ہے تم لوگ خود بہت بڑی گمراہی میں مبتلا ہو'),
(5251, 67, 10, 'اور پھر کہیں گے کہ اگر ہم بات سن لیتے اور سمجھتے ہوتے تو آج جہنّم والوں میں نہ ہوتے'),
(5252, 67, 11, 'تو انہوں نے خود اپنے گناہ کا اقرار کرلیا تو اب جہنم ّوالوںکے لئے تو رحمت خدا سے دوری ہی دوری ہے'),
(5253, 67, 12, 'بیشک جو لوگ بغیر دیکھے اپنے پروردگار کا خوف رکھتے ہیں ان کے لئے مغفرت اور اجر عظیم ہے'),
(5254, 67, 13, 'اور تم لوگ اپنی باتوں کو آہستہ کہو یا بلند آواز سے خدا تو سینوں کی رازوں کو بھی جانتا ہے'),
(5255, 67, 14, 'اور کیا پیدا کرنے والا نہیں جانتا ہے جب کہ وہ لطیف بھی ہے اور خبیر بھی ہے'),
(5256, 67, 15, 'اسی نے تمہارے لئے زمین کو نرم بنادیا ہے کہ اس کے اطراف میں چلو اور رزق خدا تلاش کرو پھر اسی کی طرف قبروں سے اٹھ کر جانا ہے'),
(5257, 67, 16, 'کیا تم آسمان میں حکومت کرنے والے کی طرف سے مطمئن ہوگئے کہ وہ تم کو زمین میں دھنسا دے اور وہ تمہیں گردش ہی دیتی رہے'),
(5258, 67, 17, 'یا تم اس کی طرف سے اس بات سے محفوظ ہوگئے کہ وہ تمہاے اوپر پتھروں کی بارش کردے پھر تمہیں بہت جلد معلوم ہوجائے گا کہ میرا ڈرانا کیسا ہوتا ہے'),
(5259, 67, 18, 'اور ان سے پہلے والوں نے بھی تکذیب کی ہے تو دیکھو کہ ان کا انجام کتنا بھیانک ہوا ہے'),
(5260, 67, 19, 'کیا ان لوگوں نے اپنے اوپر ان پرندوں کو نہیں دیکھا ہے جو پر پھیلا دیتے ہیں اور سمیٹ لیتے ہیں کہ انہیں اس فضا میں اللہ کے علاوہ کوئی نہیں سنبھال سکتا کہ وہی ہر شے کی نگرانی کرنے والا ہے'),
(5261, 67, 20, 'کیا یہ جو تمہاری فوج بنا ہوا ہے خدا کے مقابلہ میں تمہاری مدد کرسکتا ہے - بیشک کفار صرف دھوکہ میں پڑے ہوئے ہیں'),
(5262, 67, 21, 'یا یہ تم کو روزی دے سکتا ہے اگر خدا اپنی روزی کو روک لے - حقیقت یہ ہے کہ یہ لوگ نافرمانی اور نفرت میں غرق ہوگئے ہیں'),
(5263, 67, 22, 'کیا وہ شخص جو منہ کے بل چلتا ہے وہ زیادہ ہدایت یافتہ ہے یا جو سیدھے سیدھے صراظُ مستقیم پر چل رہا ہے'),
(5264, 67, 23, 'آپ کہہ دیجئے کہ خدا ہی نے تمہیں پیدا کیا ہے اور اسی نے تمہارے لئے کان آنکھ اور دل قرار دیئے ہیں مگر تم بہت کم شکریہ ادا کرنے والے ہو'),
(5265, 67, 24, 'کہہ دیجئے کہ وہی وہ ہے جس نے زمین میں تمہیں پھیلا دیا ہے اور اسی کی طرف تمہیں جمع کرکے لے جایا جائے گا'),
(5266, 67, 25, 'اور یہ لوگ کہتے ہیں کہ اگر تم لوگ سچے ہو تو یہ وعدہ کب پورا ہوگا'),
(5267, 67, 26, 'آپ کہہ دیجئے کہ علم اللہ کے پاس ہے اور میں تو صرف واضح طور پر ڈرانے والا ہوں'),
(5268, 67, 27, 'پھر جب اس قیامت کے عذاب کو قریب دیکھیں گے تو کافروں کے چہرے بگڑ جائیں گے اور ان سے کہا جائے گا کہ یہی وہ عذاب ہے جس کے تم خواستگار تھے'),
(5269, 67, 28, 'آپ کہہ دیجئے کہ تمہارا کیا خیال ہے کہ خدا مجھے اور میرے ساتھیوں کو ہلاک کردے یا ہم پر رحم کرے تو ان کافروں کا دردناک عذاب سے بچانے والا کون ہے'),
(5270, 67, 29, 'کہہ دیجئے کہ وہی خدائے رحمان ہے جس پر ہم ایمان لائے ہیں اور اسی پر ہمارا بھروسہ ہے پھر عنقریب تمہیں معلوم ہوجائے گا کہ کِھلی ہوئی گمراہی میں کون ہے'),
(5271, 67, 30, 'کہہ دیجئے کہ تمہارا کیا خیال ہے اگر تمہارا سارا پانی زمین کے اندر جذب ہوجائے تو تمہارے لئے چشمہ کا پانی بہا کر کون لائے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'نۤ, قلم اور اس چیز کی قسم جو یہ لکھ رہے ہیں'),
(5273, 68, 2, 'آپ اپنے پروردگار کی نعمت کے طفیل مجنون نہیں ہیں'),
(5274, 68, 3, 'اور آپ کے لئے کبھی نہ ختم ہونے والا اجر ہے'),
(5275, 68, 4, 'اور آپ بلند ترین اخلاق کے درجہ پر ہیں'),
(5276, 68, 5, 'عنقریب آپ بھی دیکھیں گے اور وہ بھی دیکھ لیں گے'),
(5277, 68, 6, 'کہ دیوانہ کون ہے'),
(5278, 68, 7, 'آپ کا پروردگار بہتر جانتا ہے کہ کون اس کے راستہ سے بہک گیا ہے اور کون ہدایت یافتہ ہے'),
(5279, 68, 8, 'لہذا آپ جھٹلانے والوں کی اطاعت نہ کریں'),
(5280, 68, 9, 'یہ چاہتے ہیں کہ آپ ذرا نرم ہوجائیں تو یہ بھی نرم ہوجائیں'),
(5281, 68, 10, 'اور خبردار آپ کسی بھی مسلسل قسم کھانے والے ذلیل'),
(5282, 68, 11, 'عیب جو اور اعلٰی درجہ کے چغلخور'),
(5283, 68, 12, 'مال میں بیحد بخل کرنے والے, تجاوز گناہگار'),
(5284, 68, 13, 'بدمزاج اور اس کے بعد بدنسل کی اطاعت نہ کریں'),
(5285, 68, 14, 'صرف اس بات پر کہ یہ صاحب همال و اولاد ہے'),
(5286, 68, 15, 'جب اس کے سامنے آیات هالۤہیہ کی تلاوت کی جاتی ہے تو کہہ دیتا ہے کہ یہ سب اگلے لوگوں کی داستانیں ہیں'),
(5287, 68, 16, 'ہم عنقریب اس کی ناک پر نشان لگادیں گے'),
(5288, 68, 17, 'ہم نے ان کو اسی طرح آزمایا ہے جس طرح باغ والوں کو آزمایا تھا جب انہوں نے قسم کھائی تھی کہ صبح کو پھل توڑ لیں گے'),
(5289, 68, 18, 'اور انشائ اللہ نہیں کہیں گے'),
(5290, 68, 19, 'تو خدا کی طرف سے راتوں رات ایک بلا نے چکر لگایا جب یہ سب سورہے تھے'),
(5291, 68, 20, 'اور سارا باغ جل کر کالی رات جیسا ہوگیا'),
(5292, 68, 21, 'پھر صبح کو ایک نے دوسرے کو آواز دی'),
(5293, 68, 22, 'کہ پھل توڑنا ہے تو اپنے اپنے کھیت کی طرف چلو'),
(5294, 68, 23, 'پھر سب گئے اس عالم میں کہ آپس میں راز دارانہ باتیں کررہے تھے'),
(5295, 68, 24, 'کہ خبردار آج باغ میں کوئی مسکین داخل نہ ہونے پائے'),
(5296, 68, 25, 'اور روک تھام کا بندوبست کرکے صبح سویرے پہنچ گئے'),
(5297, 68, 26, 'اب جو باغ کو دیکھا تو کہنے لگے کہ ہم تو بہک گئے'),
(5298, 68, 27, 'بلکہ بالکل سے محروم ہوگئے'),
(5299, 68, 28, 'تو ان کے منصف مزاج نے کہا کہ میں نے نہ کہا تھا کہ تم لوگ تسبیح پروردگار کیوں نہیں کرتے'),
(5300, 68, 29, 'کہنے لگے کہ ہمارا رب پاک و بے نیاز ہے اور ہم واقعا ظالم تھے'),
(5301, 68, 30, 'پھر ایک نے دوسرے کو ملامت کرنا شروع کردی'),
(5302, 68, 31, 'کہنے لگے کہ افسوس ہم بالکل سرکش تھے'),
(5303, 68, 32, 'شائد ہمارا پروردگار ہمیں اس سے بہتر دے دے کہ ہم اس کی طرف رغبت کرنے والے ہیں'),
(5304, 68, 33, 'اسی طرح عذاب نازل ہوتا ہے اور آخرت کا عذاب تو اس سے بڑا ہے اگر انہیں علم ہو'),
(5305, 68, 34, 'بیشک صاحبانِ تقویٰ کے لئے پروردگار کے یہاں نعمتوں کی جنّت ہے'),
(5306, 68, 35, 'کیا ہم اطاعت گزار وں کو مجرموں جیسا بنا دیں'),
(5307, 68, 36, 'تمہیں کیا ہو گیا ہے کیسا فیصلہ کر رہے ہو'),
(5308, 68, 37, 'یا تمہاری کوئی کتاب ہے جس میں یہ سب پڑھا کرتے ہو'),
(5309, 68, 38, 'کہ وہاں تمہاری پسند کی ساری چیزیں حاضر ملیں گی'),
(5310, 68, 39, 'یا تم نے ہم سے روزِ قیامت تک کی قسمیں لے رکھی ہیں کہ تمہیں وہ سب کچھ ملے گا جس کا تم فیصلہ کرو گے'),
(5311, 68, 40, 'ان سے پوچھئے کہ ان سب باتوں کا ذمہ دار کون ہے'),
(5312, 68, 41, 'یا ان کے لئے شرکائ ہیں تو اگر یہ سچے ہیں تو اپنے شرکائ کو لے آئیں'),
(5313, 68, 42, 'جس دن پنڈلی کھول دی جائے گی اور انہیں سجدوں کی دعوت دی جائے گی اور یہ سجدہ بھی نہ کرسکیں گے'),
(5314, 68, 43, 'ان کی نگاہیں شرم سے جھکی ہوں گی ذلّت ان پر چھائی ہوگی اور انہیں اس وقت بھی سجدوں کی دعوت دی جارہی تھی جب یہ بالکل صحیح و سالم تھے'),
(5315, 68, 44, 'تو اب مجھے اور اس بات کے جھٹلانے والوں کو چھوڑ دو ہم عنقریب انہیں اس طرح گرفتار کریں گے کہ انہیں اندازہ بھی نہ ہوگا'),
(5316, 68, 45, 'اور ہم تو اس لئے ڈھیل دے رہے ہیں کہ ہماری تدبیر مضبوط ہے'),
(5317, 68, 46, 'کیا آپ ان سے مزدوری مانگ رہے ہیں جو یہ اس کے تاوان کے بوجھ سے دبے جارہے ہیں'),
(5318, 68, 47, 'یا ان کے پاس کوئی غیب ہے جسے یہ لکھ رہے ہیں'),
(5319, 68, 48, 'اب آپ اپنے پروردگار کے حکم کے لئے صبر کریں اور صاحب هحوت جیسے نہ ہوجائیں جب انہوں نے نہایت غصّہ کے عالم میں آواز دی تھی'),
(5320, 68, 49, 'کہ اگر انہیں نعمت پروردگار نے سنبھال نہ لیا ہوتا تو انہیں چٹیل میدان میں برے حالوں میں چھوڑ دیا جاتا'),
(5321, 68, 50, 'پھر ان کے رب نے انہیں منتخب کرکے نیک کرداروں میں قرار دے دیا'),
(5322, 68, 51, 'اور یہ کفاّر قرآن کو سنتے ہیں تو ایسا لگتا ہے کہ عنقریب آپ کو نظروں سے پھسلادیں گے اور یہ کہتے ہیں کہ یہ تو دیوانے ہیں'),
(5323, 68, 52, 'حالانکہ یہ قرآن عالمین کے لئے نصیحت ہے اور بس');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'یقینا پیش آنے والی قیامت'),
(5325, 69, 2, 'اور کیسی پیش آنے والی'),
(5326, 69, 3, 'اور تم کیا جانو کہ یہ یقینا پیش آنے والی شے کیا ہے'),
(5327, 69, 4, 'قوم ثمود و عاد نے اس کھڑ کھڑانے والی کا انکار کیا تھا'),
(5328, 69, 5, 'تو ثمود ایک چنگھاڑ کے ذریعہ ہلاک کردیئے گئے'),
(5329, 69, 6, 'اور عاد کو انتہائی تیز و تند آندھی سے برباد کردیا گیا'),
(5330, 69, 7, 'جسے ان کے اوپر سات رات اور آٹھ دن کے لئے مسلسل مسخّرکردیا گیا تو تم دیکھتے ہو کہ قوم بالکل مفِدہ پڑی ہوئی ہے جیسے کھوکھلے کھجور کے درخت کے تنے'),
(5331, 69, 8, 'تو کیا تم ان کا کوئی باقی رہنے والا حصہّ دیکھ رہے ہو'),
(5332, 69, 9, 'اور فرعون اور اس سے پہلے اور الٹی بستیوں والے سب نے غلطیوں کا ارتکاب کیا ہے'),
(5333, 69, 10, 'کہ پروردگار کے نمائندہ کی نافرمانی کی تو پروردگار نے انہیں بڑی سختی سے پکڑ لیا'),
(5334, 69, 11, 'ہم نے تم کو اس وقت کشتی میں اٹھالیا تھا جب پانی سر سے چڑھ رہا تھا'),
(5335, 69, 12, 'تاکہ اسے تمہارے لئے نصیحت بنائیں اور محفوظ رکھنے والے کان سن لیں'),
(5336, 69, 13, 'پھر جب صور میں پہلی مرتبہ پھونکا جائے گا'),
(5337, 69, 14, 'اور زمین اور پہاڑوں کو اکھاڑ کر ٹکرا کر ریزہ ریزہ کردیا جائے گا'),
(5338, 69, 15, 'تو اس دن قیامت واقع ہوجائے گی'),
(5339, 69, 16, 'اور آسمان شق ہوکر بالکل پھس پھسے ہوجائیں گے'),
(5340, 69, 17, 'اور فرشتے اس کے اطراف پر ہوں گے اور عرش الہٰی کو اس دن آٹھ فرشتے اٹھائے ہوں گے'),
(5341, 69, 18, 'اس دن تم کو منظر عام پر لایا جائے گا اور تمہاری کوئی بات پوشیدہ نہ رہے گی'),
(5342, 69, 19, 'پھر جس کو نامئہ اعمال داہنے ہاتھ میں دیا جائے گا وہ سب سے کہے گا کہ ذرا میرا نامئہ اعمال تو پڑھو'),
(5343, 69, 20, 'مجھے پہلے ہی معلوم تھا کہ میرا حساب مجھے ملنے والا ہے'),
(5344, 69, 21, 'پھر وہ پسندیدہ زندگی میں ہوگا'),
(5345, 69, 22, 'بلند ترین باغات میں'),
(5346, 69, 23, 'اس کے میوے قریب قریب ہوں گے'),
(5347, 69, 24, 'اب آرام سے کھاؤ پیو کہ تم نے گزشتہ دنوں میں ان نعمتوں کا انتظام کیا ہے'),
(5348, 69, 25, 'لیکن جس کو نامئہ اعمال بائیں ہاتھ میں دیا جائے گا وہ کہے گا اے کاش یہ نامہ اعمال مجھے نہ دیا جاتا'),
(5349, 69, 26, 'اور مجھے اپنا حساب نہ معلوم ہوتا'),
(5350, 69, 27, 'اے کاش اس موت ہی نے میرا فیصلہ کردیا ہوتا'),
(5351, 69, 28, 'میرا مال بھی میرے کام نہ آیا'),
(5352, 69, 29, 'اور میری حکومت بھی برباد ہوگئی'),
(5353, 69, 30, 'اب اسے پکڑو اور گرفتار کرلو'),
(5354, 69, 31, 'پھر اسے جہنم ّمیں جھونک دو'),
(5355, 69, 32, 'پھر ایک ستر گز کی رسی میں اسے جکڑ لو'),
(5356, 69, 33, 'یہ خدائے عظیم پر ایمان نہیں رکھتا تھا'),
(5357, 69, 34, 'اور لوگوں کو مسکینوں کو کھلانے پر آمادہ نہیں کرتا تھا'),
(5358, 69, 35, 'تو آج اس کا یہاں کوئی غمخوار نہیں ہے'),
(5359, 69, 36, 'اور نہ پیپ کے علاوہ کوئی غذا ہے'),
(5360, 69, 37, 'جسے گنہگاروں کے علاوہ کوئی نہیں کھاسکتا'),
(5361, 69, 38, 'میں اس کی بھی قسم کھاتا ہوں جسے تم دیکھ رہے ہو'),
(5362, 69, 39, 'اور اس کی بھی جس کو نہیں دیکھ رہے ہو'),
(5363, 69, 40, 'کہ یہ ایک محترم فرشتے کا بیان ہے'),
(5364, 69, 41, 'اور یہ کسی شاعر کا قول نہیں ہے ہاں تم بہت کم ایمان لاتے ہو'),
(5365, 69, 42, 'اور یہ کسی کاہن کا کلام نہیںہے جس پر تم بہت کم غور کرتے ہو'),
(5366, 69, 43, 'یہ رب العالمین کا نازل کردہ ہے'),
(5367, 69, 44, 'اور اگر یہ پیغمبر ہماری طرف سے کوئی بات گڑھ لیتا'),
(5368, 69, 45, 'تو ہم اس کے ہاتھ کو پکڑ لیتے'),
(5369, 69, 46, 'اور پھر اس کی گردن اڑادیتے'),
(5370, 69, 47, 'پھر تم میں سے کوئی مجھے روکنے والا نہ ہوتا'),
(5371, 69, 48, 'اور یہ قرآن صاحبانِ تقویٰ کے لئے نصیحت ہے'),
(5372, 69, 49, 'اور ہم جانتے ہیں کہ تم میں سے جھٹلانے والے بھی ہیں'),
(5373, 69, 50, 'اور یہ کافرین کے لئے باعث هحسرت ہے'),
(5374, 69, 51, 'اور یہ بالکل یقینی چیز ہے'),
(5375, 69, 52, 'لہذا آپ اپنے عظیم پروردگار کے نام کی تسبیح کریں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'ایک مانگنے والے نے واقع ہونے والے عذاب کا سوال کیا'),
(5377, 70, 2, 'جس کا کافروں کے حق میں کوئی دفع کرنے والا نہیں ہے'),
(5378, 70, 3, 'یہ بلندیوں والے خدا کی طرف سے ہے'),
(5379, 70, 4, 'جس کی طرف فرشتے اور روح الامین بلند ہوتے ہیں اس ایک دن میں جس کی مقدار پچاس ہزار سال کے برابر ہے'),
(5380, 70, 5, 'لہذا آپ بہترین صبر سے کام لیں'),
(5381, 70, 6, 'یہ لوگ اسے دور سمجھ رہے ہیں'),
(5382, 70, 7, 'اور ہم اسے قریب ہی دیکھ رہے ہیں'),
(5383, 70, 8, 'جس دن آسمان پگھلے ہوئے تانبے کے مانند ہوجائے گا'),
(5384, 70, 9, 'اور پہاڑ دھنکے ہوئے اون جیسے'),
(5385, 70, 10, 'اور کوئی ہمدرد کسی ہمدرد کا پرسانِ حال نہ ہوگا'),
(5386, 70, 11, 'وہ سب ایک دوسرے کو دکھائے جائیں گے تو مجرم چاہے گا کہ کاش آج کے دن کے عذاب کے بدلے اس کی اولاد کو لے لیا جائے'),
(5387, 70, 12, 'اوربیوی اور بھائی کو'),
(5388, 70, 13, 'اور اس کنبہ کو جس میں وہ رہتا تھا'),
(5389, 70, 14, 'اور روئے زمین کی ساری مخلوقات کو اور اسے نجات دے دی جائے'),
(5390, 70, 15, 'ہرگز نہیں یہ آتش جہنمّ ہے'),
(5391, 70, 16, 'کھال اتار دینے والی'),
(5392, 70, 17, 'ان سب کو آواز دے رہی ہے جو منہ پھیر کر جانے والے تھے'),
(5393, 70, 18, 'اور جنہوں نے مال جمع کرکے بند کر رکھا تھا'),
(5394, 70, 19, 'بیشک انسان بڑا لالچی ہے'),
(5395, 70, 20, 'جب تکلیف پہنچ جاتی ہے تو فریادی بن جاتا ہے'),
(5396, 70, 21, 'اور جب مال مل جاتا ہے تو بخیل ہو جاتا ہے'),
(5397, 70, 22, 'علاوہ ان نمازیوں کے'),
(5398, 70, 23, 'جو اپنی نمازوں کی پابندی کرنے والے ہیں'),
(5399, 70, 24, 'اور جن کے اموال میں ایک مقررہ حق معین ہے'),
(5400, 70, 25, 'مانگنے والے کے لئے اور نہ مانگنے والے کے لئے'),
(5401, 70, 26, 'اور جو لوگ روز قیامت کی تصدیق کرنے والے ہیں'),
(5402, 70, 27, 'اور جو اپنے پروردگار کے عذاب سے ڈرنے والے ہیں'),
(5403, 70, 28, 'بیشک عذاب پروردگار بے خوف رہنے والی چیز نہیں ہے'),
(5404, 70, 29, 'اور جو اپنی شرمگاہوں کی حفاظت کرنے والے ہیں'),
(5405, 70, 30, 'علاوہ اپنی بیویوں اور کنیزوں کے کہ اس پر ملامت نہیں کی جاتی ہے'),
(5406, 70, 31, 'پھر جو اس کے علاوہ کا خواہشمند ہو وہ حد سے گزر جانے والا ہے'),
(5407, 70, 32, 'اور جو اپنی امانتوں اور عہد کا خیال رکھنے والے ہیں'),
(5408, 70, 33, 'اور جو اپنی گواہیوں پر قائم رہنے والے ہیں'),
(5409, 70, 34, 'اور جو اپنی نمازوں کا خیال رکھنے والے ہیں'),
(5410, 70, 35, 'یہی لوگ جنّت میں باعزّت طریقہ سے رہنے والے ہیں'),
(5411, 70, 36, 'پھر ان کافروں کو کیا ہوگیا ہے کہ آپ کی طرف بھاگے چلے آرہے ہیں'),
(5412, 70, 37, 'داہیں بائیں سے گروہ در گروہ'),
(5413, 70, 38, 'کیا ان میں سے ہر ایک کی طمع یہ ہے کہ اسے جنت النعیم میں داخل کردیا جائے'),
(5414, 70, 39, 'ہرگز نہیں انہیں تو معلوم ہے کہ ہم نے انہیں کس چیز سے پیدا کیا ہے'),
(5415, 70, 40, 'میں تمام مشرق و مغرب کے پروردگار کی قسم کھا کر کہتا ہوں کہ ہم قدرت رکھنے والے ہیں'),
(5416, 70, 41, 'اس بات پر کہ ان کے بدلے ان سے بہتر افراد لے آئیں اور ہم عاجز نہیں ہیں'),
(5417, 70, 42, 'لہذا انہیں چھوڑ دیجئے یہ اپنے باطل میں ڈوبے رہیں اور کھیل تماشہ کرتے رہیں یہاں تک کہ اس دن سے ملاقات کریں جس کا وعدہ کیا گیا ہے'),
(5418, 70, 43, 'جس دن یہ سب قبروں سے تیزی کے ساتھ نکلیں گے جس طرح کسی پرچم کی طرف بھاگے جارہے ہوں'),
(5419, 70, 44, 'ان کی نگاہیں جھکی ہوں گی اور ذلت ان پر چھائی ہوگی اور یہی وہ دن ہوگا جس کا ان سے وعدہ کیا گیا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'بیشک ہم نے نوح کو ان کی قوم کی طرف بھیجا کہ اپنی قوم کو دردناک عذاب کے آنے سے پہلے ڈراؤ'),
(5421, 71, 2, 'انہوں نے کہا اے قوم میں تمہارے لئے واضح طور پر ڈرانے والا ہوں'),
(5422, 71, 3, 'کہ اللہ کی عبادت کرو اس سے ڈرو اور میری اطاعت کرو'),
(5423, 71, 4, 'وہ تمہارے گناہوں کو بخش دے گا اور تمہیں ایک مقررہ وقت تک باقی رکھے گا اللہ کا مقررہ وقت جب آجائے گا تو وہ ٹالا نہیں جاسکتا ہے اگر تم کچھ جانتے ہو'),
(5424, 71, 5, 'انہوں نے کہا پروردگار میں نے اپنی قوم کو دن میں بھی بلایا اور رات میں بھی'),
(5425, 71, 6, 'پھر بھی میری دعوت کا کوئی اثر سوائے اس کے نہ ہوا کہ انہوں نے فرار اختیار کیا'),
(5426, 71, 7, 'اور میں نے جب بھی انہیں دعوت دی کہ تو انہیں معاف کردے تو انہوں نے اپنی انگلیوں کو کانوں میں رکھ لیا اور اپنے کپڑے اوڑھ لئے اور اپنی بات پر اڑ گئے اور شدت سے اکڑے رہے'),
(5427, 71, 8, 'پھر میں نے ان کو علی الاعلان دعوت دی'),
(5428, 71, 9, 'پھر میں نے اعلان بھی کیا اور خفیہ طور سے بھی دعوت دی'),
(5429, 71, 10, 'اور کہا کہ اپنے پروردگار سے استغفار کرو کہ وہ بہت زیادہ بخشنے والا ہے'),
(5430, 71, 11, 'وہ تم پر موسلا دھار پانی برسائے گا'),
(5431, 71, 12, 'اور اموال و اولاد کے ذریعہ تمہاری مدد کرے گا اور تمہارے لئے باغات اور نہریں قرار دے گا'),
(5432, 71, 13, 'آخر تمہیں کیا ہوگیا ہے کہ تم خدا کی عظمت کا خیال نہیں کرتے ہو'),
(5433, 71, 14, 'جب کہ اسی نے تمہیں مختلف انداز میں پیدا کیا ہے'),
(5434, 71, 15, 'کیا تم نے نہیں دیکھا کہ خدا نے کس طرح تہ بہ تہ سات آسمان بنائے ہیں'),
(5435, 71, 16, 'اور قمر کو ان میں روشنی اور سورج کو روشن چراغ بنایا ہے'),
(5436, 71, 17, 'اور اللہ ہی نے تم کو زمین سے پیدا کیا ہے'),
(5437, 71, 18, 'پھر تمہیں اسی میں لے جائے گا اور پھر نئی شکل میں نکالے گا'),
(5438, 71, 19, 'اور اللہ ہی نے تمہارے لئے زمین کو فرش بنادیا ہے'),
(5439, 71, 20, 'تاکہ تم اس میں مختلف کشادہ راستوں پر چلو'),
(5440, 71, 21, 'اور نوح نے کہا کہ پروردگار ان لوگوں نے میری نافرمانی کی ہے اور اس کا اتباع کرلیا ہے جو مال و اولاد میں سوائے گھاٹے کے کچھ نہیں دے سکتا ہے'),
(5441, 71, 22, 'اور ان لوگوں نے بہت بڑا مکر کیا ہے'),
(5442, 71, 23, 'اور لوگوں سے کہا ہے کہ خبردار اپنے خداؤں کو مت چھوڑ دینا اور ود, سواع, یغوث, یعوق, نسر کو نظرانداز نہ کردینا'),
(5443, 71, 24, 'انہوں نے بہت سوں کو گمراہ کردیا ہے اب تو ظالموں کے لئے ہلاکت کے علاوہ کوئی اضافہ نہ کرنا'),
(5444, 71, 25, 'یہ سب اپنی غلطیوں کی بنا پر غرق کئے گئے ہیں اور پھر جہنم ّمیں داخل کردیئے گئے ہیں اور خدا کے علاوہ انہیں کوئی مددگار نہیں ملا ہے'),
(5445, 71, 26, 'اور نوح نے کہا کہ پروردگار اس زمین پر کافروں میں سے کسی بسنے والے کو نہ چھوڑنا'),
(5446, 71, 27, 'کہ تو انہیں چھوڑ دے گا تو تیرے بندوں کو گمراہ کریں گے اور فاجر و کافر کے علاوہ کوئی اولاد بھی نہ پیدا کریں گے'),
(5447, 71, 28, 'پروردگار مجھے اور میرے والدین کو اور جو ایمان کے ساتھ میرے گھر میں داخل ہوجائیں اور تمام مومنین و مومنات کو بخش دے اور ظالموں کے لئے ہلاکت کے علاوہ کسی شے میں اضافہ نہ کرنا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, 'پیغمبر آپ کہہ دیجئے کہ میری طرف یہ وحی کی گئی ہے کہ جنوں کی ایک جماعت نے کان لگا کر قرآن کو سنا تو کہنے لگے کہ ہم نے ایک بڑا عجیب قرآن سنا ہے'),
(5449, 72, 2, 'جو نیکی کی ہدایت کرتا ہے تو ہم تو اس پر ایمان لے آئے ہیں اور کسی کو اپنے رب کا شریک نہ بنائیں گے'),
(5450, 72, 3, 'اور ہمارے رب کی شان بہت بلند ہے اس نے کسی کو اپنی بیوی بنایا ہے نہ بیٹا'),
(5451, 72, 4, 'اور ہمارے بیوقوف لوگ طرح طرح کی بے ربط باتیں کررہے ہیں'),
(5452, 72, 5, 'اور ہمارا خیال تو یہی تھا کہ انسان اور جناّت خدا کے خلاف جھوٹ نہ بولیں گے'),
(5453, 72, 6, 'اور یہ کہ انسانوں میں سے کچھ لوگ جناّت کے بعض لوگوں کی پناہ ڈھونڈ رہے تھے تو انہوں نے گرفتاری میں اور اضافہ کردیا'),
(5454, 72, 7, 'اور یہ کہ تمہاری طرح ان کا بھی خیال تھا کہ خدا کسی کو دوبارہ نہیں زندہ کرے گا'),
(5455, 72, 8, 'اور ہم نے آسمان کو دیکھا تو اسے سخت قسم کے نگہبانوں اور شعلوں سے بھرا ہوا پایا'),
(5456, 72, 9, 'اور ہم پہلے بعض مقامات پر بیٹھ کر باتیں سن لیا کرتے تھے لیکن اب کوئی سننا چاہے گا تو اپنے لئے شعلوں کو تیار پائے گا'),
(5457, 72, 10, 'اور ہمیں نہیں معلوم کہ اہل زمین کے لئے اس سے برائی مقصود ہے یا نیکی کا ارادہ کیا گیا ہے'),
(5458, 72, 11, 'اور ہم میں سے بعض نیک کردار ہیں اور بعض کے علاوہ ہیں اور ہم طرح طرح کے گروہ ہیں'),
(5459, 72, 12, 'اور ہمارا خیال ہے کہ ہم زمین میں خدا کو عاجز نہیں کرسکتے اور نہ بھاگ کر اسے اپنی گرفت سے عاجز کرسکتے ہیں'),
(5460, 72, 13, 'اور ہم نے ہدایت کو سنا تو ہم تو ایمان لے آئے اب جو بھی اپنے پروردگار پر ایمان لائے گا اسے نہ خسارہ کا خوف ہوگا اور نہ ظلم و زیادتی کا'),
(5461, 72, 14, 'اور ہم میں سے بعض اطاعت گزار ہیں اور بعض نافرمان اور جو اطاعت گزار ہوگا اس نے ہدایت کی راہ پالی ہے'),
(5462, 72, 15, 'اور نافرمان تو جہنمّ کے کندے ہوگئے ہیں'),
(5463, 72, 16, 'اور اگر یہ لوگ سب ہدایت کے راستے پر ہوتے تو ہم انہیں وافر پانی سے سیراب کرتے'),
(5464, 72, 17, 'تاکہ ان کا امتحان لے سکیں اور جو بھی اپنے رب کے ذکر سے اعراض کرے گا اسے سخت عذاب کے راستے پر چلنا پڑے گا'),
(5465, 72, 18, 'اور مساجد سب اللہ کے لئے ہیں لہذا اس کے علاوہ کسی کی عبادت نہ کرنا'),
(5466, 72, 19, 'اور یہ کہ جب بندہ خدا عبادت کے لئے کھڑا ہوا تو قریب تھا کہ لوگ اس کے گرد ہجوم کرکے گر پڑتے'),
(5467, 72, 20, 'پیغمبرآپ کہہ دیجئے کہ میں صرف اپنے پروردگار کی عبادت کرتا ہوں اور کسی کو اس کا شریک نہیں بناتا ہوں'),
(5468, 72, 21, 'کہہ دیجئے کہ میں تمہارے لئے کسی نقصان کا اختیار رکھتا ہوں اور نہ فائدہ کا'),
(5469, 72, 22, 'کہہ دیجئے کہ اللہ کے مقابلہ میں میرا بھی بچانے والا کوئی نہیں ہے اور نہ میں کوئی پناہ گاہ پاتا ہوں'),
(5470, 72, 23, 'مگر یہ کہ اپنے رب کے احکام اور پیغام کو پہنچادوں اور جو اللہ و رسول کی نافرمانی کرے گا اس کے لئے جہنم ّہے اور وہ اسی میں ہمیشہ رہنے والا ہے'),
(5471, 72, 24, 'یہاں تک کہ جب ان لوگوں نے اس عذاب کو دیکھ لیا جس کا وعدہ کیا گیا تھا تو انہیں معلوم ہوجائے گا کہ کس کے مددگار کمزور اور کس کی تعداد کمتر ہے'),
(5472, 72, 25, 'کہہ دیجئے کہ مجھے نہیں معلوم کہ وہ وعدہ قریب ہی ہے یا ابھی خدا کوئی اور مدّت بھی قرار دے گا'),
(5473, 72, 26, 'وہ عالم الغیب ہے اور اپنے غیب پر کسی کو بھی مطلع نہیں کرتا ہے'),
(5474, 72, 27, 'مگر جس رسول کو پسند کرلے تو اس کے آگے پیچھے نگہبان فرشتے مقرر کردیتا ہے'),
(5475, 72, 28, 'تاکہ وہ دیکھ لے کہ انہوں نے اپنے رب کے پیغامات کو پہنچادیا ہے اور وہ جس کے پاس جو کچھ بھی ہے اس پر حاوی ہے اور سب کے اعداد کا حساب رکھنے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'اے میرے چادر لپیٹنے والے'),
(5477, 73, 2, 'رات کو اٹھو مگر ذرا کم'),
(5478, 73, 3, 'آدھی رات یا اس سے بھی کچھ کم کر دو'),
(5479, 73, 4, 'یا کچھ زیادہ کردو اور قرآن کو ٹھہر ٹھہر کر باقاعدہ پڑھو'),
(5480, 73, 5, 'ہم عنقریب تمہارے اوپر ایک سنگین حکم نازل کرنے والے ہیں'),
(5481, 73, 6, 'بیشک رات کا اُٹھنا نفس کی پامالی کے لئے بہترین ذریعہ اور ذکر کا بہترین وقت ہے'),
(5482, 73, 7, 'یقینا آپ کے لئے دن میں بہت سے مشغولیات ہیں'),
(5483, 73, 8, 'اور آپ اپنے رب کے نام کا ذکر کریں اور اسی کے ہو رہیں'),
(5484, 73, 9, 'وہ مشرق و مغرب کا مالک ہے اور اس کے علاوہ کوئی خدا نہیں ہے لہذا آپ اسی کو اپنا نگراں بنالیں'),
(5485, 73, 10, 'اور یہ لوگ جو کچھ بھی کہہ رہے ہیں اس پر صبر کریں اور انہیں خوبصورتی کے ساتھ اپنے سے الگ کردیں'),
(5486, 73, 11, 'اور ہمیں اور ان دولت مند جھٹلانے والوں کو چھوڑ دیں اور انہیں تھوڑی مہلت دے دیں'),
(5487, 73, 12, 'ہمارے پاس ان کے لئے بیڑیاں اور بھڑکتی ہوئی آگ ہے'),
(5488, 73, 13, 'اور گلے میں پھنس جانے والا کھانا اور دردناک عذاب ہے'),
(5489, 73, 14, 'جس دن زمین اور پہاڑ لرزہ میں آجائیں گے اور پہاڑ ریت کا ایک ٹیلہ بن جائیں گے'),
(5490, 73, 15, 'بیشک ہم نے تم لوگوں کی طرف تمہارا گواہ بناکر ایک رسول بھیجا ہے جس طرح فرعون کی طرف رسول بھیجا تھا'),
(5491, 73, 16, 'تو فرعون نے اس رسول کی نافرمانی کی تو ہم نے اسے سخت گرفت میں لے لیا'),
(5492, 73, 17, 'پھر تم بھی کفر اختیار کرو گے تو اس دن سے کس طرح بچو گے جو بچوں کو بوڑھا بنادے گا'),
(5493, 73, 18, 'جس دن آسمان پھٹ پڑے گا اور یہ وعدہ بہرحال پورا ہونے والا ہے'),
(5494, 73, 19, 'یہ درحقیقت عبرت و نصیحت کی باتیں ہیں اور جس کا جی چاہے اپنے پروردگار کے راستے کو اختیار کرلے'),
(5495, 73, 20, 'آپ کا پروردگار جانتا ہے کہ دو تہائی رات کے قریب یا نصف شب یا ایک تہائی رات قیام کرتے ہیں اور آپ کے ساتھ ایک گروہ اور بھی ہے اور اللہ د ن و رات کا صحیح اندازہ رکھتا ہے وہ جانتا ہے کہ تم لوگ اس کا صحیح احصاءنہ کر سکوگے تو اس نے تمہارے اوپر مہربانی کر دی ہے اب جس قدر قرآن ممکن ہو اتنا پڑھ لو کہ وہ جانتا ہے کہ عنقریب تم میں سے بعض مریض ہوجائےں گے اور بعض رزق خدا کو تلاش کرنے کے لئے سفر میں چلے جائےں گے اور بعض راہِ خدا میں جہاد کریں گے تو جس قدر ممکن ہو تلاوت کرو اور نماز قائم کرو اور زکوٰة ادا کرو اور اللہ کو قرض حسنہ دو اور پھر جو کچھ بھی اپنے نفس کے واسطے نیکی پیشگی پھیج دو گے اسے خدا کی بارگاہ میں حاضر پاﺅگے ،بہتر اور اجر کے اعتبار سے عظیم تر۔اور اللہ سے استغفار کرو کہ وہ بہت زیادہ بخشنے والا اور مہربا ن ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'اے میرے کپڑا اوڑھنے والے'),
(5497, 74, 2, 'اٹھو اور لوگوں کو ڈراؤ'),
(5498, 74, 3, 'اور اپنے رب کی بزرگی کا اعلان کرو'),
(5499, 74, 4, 'اور اپنے لباس کو پاکیزہ رکھو'),
(5500, 74, 5, 'اور برائیوں سے پرہیز کرو'),
(5501, 74, 6, 'اور اس طرح احسان نہ کرو کہ زیادہ کے طلب گار بن جاؤ'),
(5502, 74, 7, 'اور اپنے رب کی خاطر صبر کرو'),
(5503, 74, 8, 'پھر جب صور پھونکا جائے گا'),
(5504, 74, 9, 'تو وہ دن انتہائی مشکل دن ہوگا'),
(5505, 74, 10, 'کافروں کے واسطے تو ہر گز آسان نہ ہوگا'),
(5506, 74, 11, 'اب مجھے اور اس شخص کو چھوڑ دو جس کو میں نے اکیلا پیدا کیا ہے'),
(5507, 74, 12, 'اور اس کے لئے کثیر مال قرار دیا ہے'),
(5508, 74, 13, 'اور نگاہ کے سامنے رہنے والے بیٹے قرار دیئے ہیں'),
(5509, 74, 14, 'اور ہر طرح کے سامان میں وسعت دے دی ہے'),
(5510, 74, 15, 'اور پھر بھی چاہتا ہے کہ اور اضافہ کردوں'),
(5511, 74, 16, 'ہرگز نہیں یہ ہماری نشانیوں کا سخت دشمن تھا'),
(5512, 74, 17, 'تو ہم عنقریب اسے سخت عذاب میں گرفتار کریں گے'),
(5513, 74, 18, 'اس نے فکر کی اور اندازہ لگایا'),
(5514, 74, 19, 'تو اسی میں مارا گیا کہ کیسا اندازہ لگایا'),
(5515, 74, 20, 'پھر اسی میں اور تباہ ہوگیا کہ کیسا اندازہ لگایا'),
(5516, 74, 21, 'پھر غور کیا'),
(5517, 74, 22, 'پھر تیوری چڑھا کر منہ بسور لیا'),
(5518, 74, 23, 'پھر منہ پھیر کر چلا گیا اور اکڑ گیا'),
(5519, 74, 24, 'اور آخر میں کہنے لگا کہ یہ تو ایک جادو ہے جو پرانے زمانے سے چلا آرہا ہے'),
(5520, 74, 25, 'یہ تو صرف انسان کا کلام ہے'),
(5521, 74, 26, 'ہم عنقریب اسے جہنمّ واصل کردیں گے'),
(5522, 74, 27, 'اور تم کیا جانو کہ جہنمّ کیا ہے'),
(5523, 74, 28, 'وہ کسی کو چھوڑنے والا اور باقی رکھنے والا نہیںہے'),
(5524, 74, 29, 'بدن کو جلا کر سیاہ کردینے والا ہے'),
(5525, 74, 30, 'اس پر انیس فرشتے معین ہیں'),
(5526, 74, 31, 'اور ہم نے جہنمّ کا نگہبان صرف فرشتوں کو قرار دیا ہے اور ان کی تعداد کو کفار کی آزمائش کا ذریعہ بنادیا ہے کہ اہل کتاب کو یقین حاصل ہوجائے اور ایمان والوں کے ایمان میں اضافہ ہوجائے اور اہل کتاب یا صاحبانِ ایمان اس کے بارے میں کسی طرح کا شک نہ کریں اور جن کے دلوں میں مرض ہے اور کفار یہ کہنے لگیں کہ آخر اس مثال کا مقصد کیا ہے اللہ اسی طرح جس کو چاہتا ہے گمراہی میں چھوڑ دیتا ہے اور جس کو چاہتا ہے ہدایت دے دیتا ہے اور اس کے لشکروں کو اس کے علاوہ کوئی نہیں جانتا ہے یہ تو صرف لوگوں کی نصیحت کا ایک ذریعہ ہے'),
(5527, 74, 32, 'ہوشیار ہمیں چاند کی قسم'),
(5528, 74, 33, 'اور جاتی ہوئی رات کی قسم'),
(5529, 74, 34, 'اور روشن صبح کی قسم'),
(5530, 74, 35, 'یہ جہنمّ بڑی چیزوں میں سے ایک چیز ہے'),
(5531, 74, 36, 'لوگوں کے ڈرانے کا ذریعہ'),
(5532, 74, 37, 'ان کے لئے جو آگے پیچھے ہٹنا چاہیں'),
(5533, 74, 38, 'ہر نفس اپنے اعمال میں گرفتار ہے'),
(5534, 74, 39, 'علاوہ اصحاب یمین کے'),
(5535, 74, 40, 'وہ جنتوں میں رہ کر آپس میں سوال کررہے ہوں گے'),
(5536, 74, 41, 'مجرمین کے بارے میں'),
(5537, 74, 42, 'آخر تمہیں کس چیز نے جہنمّ میں پہنچادیا ہے'),
(5538, 74, 43, 'وہ کہیں گے کہ ہم نماز گزار نہیں تھے'),
(5539, 74, 44, 'اور مسکین کو کھانا نہیں کھلایا کرتے تھے'),
(5540, 74, 45, 'لوگوں کے بفِے کاموں میں شامل ہوجایا کرتے تھے'),
(5541, 74, 46, 'اور روزِ قیامت کی تکذیب کیا کرتے تھے'),
(5542, 74, 47, 'یہاں تک کہ ہمیں موت آگئی'),
(5543, 74, 48, 'تو انہیں سفارش کرنے والوں کی سفارش بھی کوئی فائدہ نہ پہنچائے گی'),
(5544, 74, 49, 'آخر انہیں کیا ہوگیا ہے کہ یہ نصیحت سے منہ موڑے ہوئے ہیں'),
(5545, 74, 50, 'گویا بھڑکے ہوئے گدھے ہیں'),
(5546, 74, 51, 'جو شیر سے بھاگ رہے ہیں'),
(5547, 74, 52, 'حقیقتا ان میں ہر آدمی اس بات کا خواہش مند ہے کہ اسے کِھلی ہوئی کتابیں عطا کردی جائیں'),
(5548, 74, 53, 'ہرگز نہیں ہوسکتا اصل یہ ہے کہ انہیں آخرت کا خوف ہی نہیں ہے'),
(5549, 74, 54, 'ہاں ہاں بیشک یہ سراسر نصیحت ہے'),
(5550, 74, 55, 'اب جس کا جی چاہے اسے یاد رکھے'),
(5551, 74, 56, 'اور یہ اسے یاد نہ کریں گے مگر یہ کہ اللہ ہی چاہے کہ وہی ڈرانے کا اہل اور مغفرت کا مالک ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'میں روزِ قیامت کی قسم کھاتا ہوں'),
(5553, 75, 2, 'اور برائیوں پر ملامت کرنے والے نفس کی قسم کھاتا ہوں'),
(5554, 75, 3, 'کیا یہ انسان یہ خیال کرتا ہے کہ ہم اس کی ہڈیوں کو جمع نہ کرسکیں گے'),
(5555, 75, 4, 'یقینا ہم اس بات پر قادر ہیں کہ اس کی انگلیوں کے پور تک درست کرسکیں'),
(5556, 75, 5, 'بلکہ انسان یہ چاہتا ہے کہ اپنے سامنے برائی کرتا چلا جائے'),
(5557, 75, 6, 'وہ یہ پوچھتا ہے کہ یہ قیامت کب آنے والی ہے'),
(5558, 75, 7, 'تو جب آنکھیں چکا چوند ہوجائیں گی'),
(5559, 75, 8, 'اور چاند کو گہن لگ جائے گا'),
(5560, 75, 9, 'اور یہ چاند سورج اکٹھا کردیئے جائیں گے'),
(5561, 75, 10, 'اس دن انسان کہے گا کہ اب بھاگنے کا راستہ کدھر ہے'),
(5562, 75, 11, 'ہرگز نہیں اب کوئی ٹھکانہ نہیں ہے'),
(5563, 75, 12, 'اب سب کا مرکز تمہارے پروردگار کی طرف ہے'),
(5564, 75, 13, 'اس دن انسان کو بتایا جائے گا کہ اس نے پہلے اور بعد کیا کیا اعمال کئے ہیں'),
(5565, 75, 14, 'بلکہ انسان خود بھی اپنے نفس کے حالات سے خوب باخبر ہے'),
(5566, 75, 15, 'چاہے وہ کتنے ہی عذر کیوں نہ پیش کرے'),
(5567, 75, 16, 'دیکھئے آپ قرآن کی تلاوت میں عجلت کے ساتھ زبان کو حرکت نہ دیں'),
(5568, 75, 17, 'یہ ہماری ذمہ داری ہے کہ ہم اسے جمع کریں اور پڑھوائیں'),
(5569, 75, 18, 'پھر جب ہم پڑھوادیں تو آپ اس کی تلاوت کو دہرائیں'),
(5570, 75, 19, 'پھر اس کے بعد اس کی وضاحت کرنا بھی ہماری ہی ذمہ داری ہے'),
(5571, 75, 20, 'نہیں بلکہ تم لوگ دنیا کو دوست رکھتے ہو'),
(5572, 75, 21, 'اور آخرت کو نظر انداز کئے ہوئے ہو'),
(5573, 75, 22, 'اس دن بعض چہرے شاداب ہوں گے'),
(5574, 75, 23, 'اپنے پروردگار کی نعمتوں پر نظر رکھے ہوئے ہوں گے'),
(5575, 75, 24, 'اور بعض چہرے افسردہ ہوں گے'),
(5576, 75, 25, 'جنہیں یہ خیال ہوگا کہ کب کمر توڑ مصیبت وارد ہوجائے'),
(5577, 75, 26, 'ہوشیار جب جان گردن تک پہنچ جائے گی'),
(5578, 75, 27, 'اور کہا جائے گا کہ اب کون جھاڑ پھونک کرنے والا ہے'),
(5579, 75, 28, 'اور مرنے والے کو خیال ہوگا کہ اب سب سے جدائی ہے'),
(5580, 75, 29, 'اور پنڈلی پنڈلی سے لپٹ جائے گی'),
(5581, 75, 30, 'آج سب کو پروردگار کی طرف لے جایا جائے گا'),
(5582, 75, 31, 'اس نے نہ کلام خدا کی تصدیق کی اور نہ نماز پڑھی'),
(5583, 75, 32, 'بلکہ تکذیب کی اور منہ پھیر لیا'),
(5584, 75, 33, 'پھر اپنے اہل کی طرف اکڑتا ہوا گیا'),
(5585, 75, 34, 'افسوس ہے تیرے حال پر بہت افسوس ہے'),
(5586, 75, 35, 'حیف ہے اور صد حیف ہے'),
(5587, 75, 36, 'کیا انسان کا خیال یہ ہے کہ اسے اسی طرح آزاد چھوڑ دیا جائے گا'),
(5588, 75, 37, 'کیا وہ اس منی کا قطرہ نہیں تھا جسے رحم میں ڈالا جاتا ہے'),
(5589, 75, 38, 'پھر علقہ بنا پھر اسے خلق کرکے برابر کیا'),
(5590, 75, 39, 'پھر اس سے عورت اور مرد کا جوڑا تیار کیا'),
(5591, 75, 40, 'کیا وہ خدا اس بات پر قادر نہیں ہے کہ مفِدوں کو دوبارہ زندہ کرسکے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'یقینا انسان پر ایک ایسا وقت بھی آیا ہے کہ جب وہ کوئی قابل ذکر شے نہیں تھا'),
(5593, 76, 2, 'یقینا ہم نے انسان کو ایک ملے جلے نطفہ سے پیدا کیا ہے تاکہ اس کا امتحان لیں اور پھر اسے سماعت اور بصارت والا بنادیا ہے'),
(5594, 76, 3, 'یقینا ہم نے اسے راستہ کی ہدایت دے دی ہے چاہے وہ شکر گزار ہوجائے یا کفران نعمت کرنے والا ہوجائے'),
(5595, 76, 4, 'بیشک ہم نے کافرین کے لئے زنجیریں - طوق اور بھڑکتے ہوئے شعلوں کا انتظام کیا ہے'),
(5596, 76, 5, 'بیشک ہمارے نیک بندے اس پیالہ سے پئیں گے جس میں شراب کے ساتھ کافور کی آمیزش ہوگی'),
(5597, 76, 6, 'یہ ایک چشمہ ہے جس سے اللہ کے نیک بندے پئیں گے اور جدھر چاہیں گے بہاکر لے جائیں گے'),
(5598, 76, 7, 'یہ بندے نذر کو پورا کرتے ہیں اور اس دن سے ڈرتے ہیں جس کی سختی ہر طرف پھیلی ہوئی ہے'),
(5599, 76, 8, 'یہ اس کی محبت میں مسکینً یتیم اور اسیر کو کھانا کھلاتے ہیں'),
(5600, 76, 9, 'ہم صرف اللہ کی مرضی کی خاطر تمہیں کھلاتے ہیں ورنہ نہ تم سے کوئی بدلہ چاہتے ہیں نہ شکریہ'),
(5601, 76, 10, 'ہم اپنے پروردگار سے اس دن کے بارے میں ڈرتے ہیں جس دن چہرے بگڑ جائیں گے اور ان پر ہوائیاں اڑنے لگیں گی'),
(5602, 76, 11, 'تو خدا نے انہیں اس دن کی سختی سے بچالیا اور تازگی اور سرور عطا کردیا'),
(5603, 76, 12, 'اور انہیں ان کے صبر کے عوض جنّت اور حریر جنّت عطا کرے گا'),
(5604, 76, 13, 'جہاں وہ تختوں پر تکیہ لگائے بیٹھے ہوں گے نہ آفتاب کی گرمی دیکھیں گے اور نہ سردی'),
(5605, 76, 14, 'ان کے سروں پر قریب ترین سایہ ہوگا اور میوے بالکل ان کے اختیار میں کردیئے جائیں گے'),
(5606, 76, 15, 'ان کے گرد چاندی کے پیالے اور شیشے کے ساغروں کی گردش ہوگی'),
(5607, 76, 16, 'یہ ساغر بھی چاندی ہی کے ہوں گے جنہیں یہ لوگ اپنے پیمانہ کے مطابق بنالیں گے'),
(5608, 76, 17, 'یہ وہاں ایسے پیالے سے سیراب کئے جائیں گے جس میں زنجبیل کی آمیزش ہوگی'),
(5609, 76, 18, 'جو جنّت کا ایک چشمہ ہے جسے سلسبیل کہا جاتا ہے'),
(5610, 76, 19, 'اور ان کے گرد ہمیشہ نوجوان رہنے والے بچے گردش کررہے ہوں گے کہ تم انہیں دیکھو گے تو بکھرے ہوئے موتی معلوم ہوں گے'),
(5611, 76, 20, 'اور پھر دوبارہ دیکھو گے تو نعمتیں اور ایک ملک کبیر نظر آئے گا'),
(5612, 76, 21, 'ان کے اوپر کریب کے سبز لباس اور ریشم کے حلّے ہوں گے اور انہیں چاندی کے کنگن پہنائے جائیں گے اور انہیں ان کا پروردگار پاکیزہ شراب سے سیراب کرے گا'),
(5613, 76, 22, 'یہ سب تمہاری جزا ہے اور تمہاری سعی قابل قبول ہے'),
(5614, 76, 23, 'ہم نے آپ پر قرآن تدریجا نازل کیا ہے'),
(5615, 76, 24, 'لہذا آپ حکم اَ خدا کی خاطر صبر کریں اور کسی گنہگار یا کافر کی بات میں نہ آجائیں'),
(5616, 76, 25, 'اور صبح و شام اپنے پروردگار کے نام کی تسبیح کرتے رہیں'),
(5617, 76, 26, 'اور رات کے ایک حصہ میں اس کا سجدہ کریں اور بڑی رات تک اس کی تسبیح کرتے رہیں'),
(5618, 76, 27, 'یہ لوگ صرف دنیا کی نعمتوں کو پسند کرتے ہیں اور اپنے پیچھے ایک بڑے سنگین دن کو چھوڑے ہوئے ہیں'),
(5619, 76, 28, 'ہم نے ان کو پیدا کیا ہے اور ان کے اعضائ کو مضبوط بنایا ہے اور جب چاہیں گے تو انہیں بدل کر ان کے جیسے دوسرے افراد لے آئیں گے'),
(5620, 76, 29, 'یہ ایک نصیحت کا سامان ہے اب جس کا جی چاہے اپنے پروردگار کے راستہ کو اختیار کرلے'),
(5621, 76, 30, 'اور تم لوگ تو صرف وہی چاہتے ہو جو پروردگار چاہتا ہے بیشک اللہ ہر چیز کا جاننے والا اور صاحبِ حکمت ہے'),
(5622, 76, 31, 'وہ جسے چاہتا ہے اپنی رحمت میں داخل کرلیتا ہے اور اس نے ظالمین کے لئے دردناک عذاب مہیاّ کررکھا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'ان کی قسم جنہیں تسلسل کے ساتھ بھیجا گیا ہے'),
(5624, 77, 2, 'پھر تیز رفتاری سے چلنے والی ہیں'),
(5625, 77, 3, 'اور قسم ہے ان کی جو اشیائ کو منتشر کرنے والی ہیں'),
(5626, 77, 4, 'پھر انہیں آپس میں جدا کرنے والی ہیں'),
(5627, 77, 5, 'پھر ذ کر کو نازل کرنے والی ہیں'),
(5628, 77, 6, 'تاکہ عذر تمام ہو یا خوف پیدا کرایا جائے'),
(5629, 77, 7, 'جس چیز کا تم سے وعدہ کیا گیا ہے وہ بہرحال واقع ہونے والی ہے'),
(5630, 77, 8, 'پھر جب ستاروں کی چمک ختم ہوجائے'),
(5631, 77, 9, 'اور آسمانوں میں شگاف پیدا ہوجائے'),
(5632, 77, 10, 'اور جب پہاڑ اُڑنے لگیں'),
(5633, 77, 11, 'اور جب سارے پیغمبر علیھ السّلام ایک وقت میں جمع کرلئے جائیں'),
(5634, 77, 12, 'بھلا کس دن کے لئے ان باتوں میں تاخیر کی گئی ہے'),
(5635, 77, 13, 'فیصلہ کے دن کے لئے'),
(5636, 77, 14, 'اور آپ کیاجانیں کہ فیصلہ کا دن کیا ہے'),
(5637, 77, 15, 'اس دن جھٹلانے والوں کے لئے جہنمّ ہے'),
(5638, 77, 16, 'کیا ہم نے ان کے پہلے والوں کو ہلاک نہیں کردیا ہے'),
(5639, 77, 17, 'پھر دوسرے لوگوں کو بھی ان ہی کے پیچھے لگادیں گے'),
(5640, 77, 18, 'ہم مجرموں کے ساتھ اسی طرح کا برتاؤ کرتے ہیں'),
(5641, 77, 19, 'اور آج کے دن جھٹلانے والوں کے لئے بربادی ہی بربادی ہے'),
(5642, 77, 20, 'کیا ہم نے تم کو ایک حقیر پانی سے نہیں پیدا کیا ہے'),
(5643, 77, 21, 'پھر اسے ایک محفوظ مقام پر قرار دیا ہے'),
(5644, 77, 22, 'ایک معین مقدار تک'),
(5645, 77, 23, 'پھر ہم نے اس کی مقدار معین کی ہے تو ہم بہترین مقدار مقرر کرنے والے ہیں'),
(5646, 77, 24, 'آج کے دن جھٹلانے والوں کے لئے بربادی ہے'),
(5647, 77, 25, 'کیا ہم نے زمین کو ایک جمع کرنے والا ظرف نہیں بنایا ہے'),
(5648, 77, 26, 'جس میں زندہ مفِدہ سب کو جمع کریں گے'),
(5649, 77, 27, 'اور اس میں اونچے اونچے پہاڑ قرار دئے ہیں اور تمہیں شیریں پانی سے سیراب کیا ہے'),
(5650, 77, 28, 'آج جھٹلانے والوں کے لئے بربادی اور تباہی ہے'),
(5651, 77, 29, 'جاؤ اس طرف جس کی تکذیب کیا کرتے تھے'),
(5652, 77, 30, 'جاؤ اس دھوئیں کے سایہ کی طرف جس کے تین گوشے ہیں'),
(5653, 77, 31, 'نہ ٹھنڈک ہے اور نہ جہنمّ کی لپٹ سے بچانے والا سہارا'),
(5654, 77, 32, 'وہ ایسے انگارے پھینک رہا ہے جیسے کوئی محل'),
(5655, 77, 33, 'جیسے زرد رنگ کے اونٹ'),
(5656, 77, 34, 'آج کے دن جھٹلانے والوں کے لئے بربادی اور جہنمّ ہے'),
(5657, 77, 35, 'آج کے دن یہ لوگ بات بھی نہ کرسکیں گے'),
(5658, 77, 36, 'اور نہ انہیں اس بات کی اجازت ہوگی کہ عذر پیش کرسکیں'),
(5659, 77, 37, 'آج کے دن جھٹلانے والوں کے لئے جہنم ّہے'),
(5660, 77, 38, 'یہ فیصلہ کا دن ہے جس میں ہم نے تم کو اور تمام پہلے والوں کو اکٹھا کیا ہے'),
(5661, 77, 39, 'اب اگر تمہارے پاس کوئی چال ہو تو ہم سے استعمال کرو'),
(5662, 77, 40, 'آج تکذیب کرنے والوں کے لئے جہنم ّہے'),
(5663, 77, 41, 'بیشک متقین گھنی چھاؤں اور چشموں کے درمیان ہوں گے'),
(5664, 77, 42, 'اور ان کی خواہش کے مطابق میوے ہوں گے'),
(5665, 77, 43, 'اب اطمینان سے کھاؤ پیو ان اعمال کی بنا پر جو تم نے انجام دیئے ہیں'),
(5666, 77, 44, 'ہم اسی طرح نیک عمل کرنے والوں کو بدلہ دیتے ہیں'),
(5667, 77, 45, 'آج جھٹلانے والوں کے لئے جہنم ہے'),
(5668, 77, 46, 'تم لوگ تھوڑے دنوں کھاؤ اور آرام کرلو کہ تم مجرم ہو'),
(5669, 77, 47, 'آج کے دن تکذیب کرنے والوں کے لئے ویل ہے'),
(5670, 77, 48, 'اور جب ان سے کہا جاتا ہے کہ رکوع کرو تو نہیں کرتے ہیں'),
(5671, 77, 49, 'تو آج کے دن جھٹلانے والوں کے لئے جہنم ہے'),
(5672, 77, 50, 'آخر یہ لوگ اس کے بعد کس بات پر ایمان لے آئیں گے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'یہ لوگ آپس میں کس چیز کے بارے میں سوال کررہے ہیں'),
(5674, 78, 2, 'بہت بڑی خبر کے بارے میں'),
(5675, 78, 3, 'جس کے بارے میں ان میں اختلاف ہے'),
(5676, 78, 4, 'کچھ نہیں عنقریب انہیں معلوم ہوجائے گا'),
(5677, 78, 5, 'اور خوب معلوم ہوجائے گا'),
(5678, 78, 6, 'کیا ہم نے زمین کا فرش نہیں بنایا ہے'),
(5679, 78, 7, 'اورپہاڑوں کی میخیں نہیں نصب کی ہیں'),
(5680, 78, 8, 'اور ہم ہی نے تم کوجوڑا بنایا ہے'),
(5681, 78, 9, 'اور تمہاری نیند کو آرام کا سامان قرار دیا ہے'),
(5682, 78, 10, 'اور رات کو پردہ پوش بنایا ہے'),
(5683, 78, 11, 'اور دن کو وقت معاش قراردیا ہے'),
(5684, 78, 12, 'اور تمہارے سروں پر سات مضبوط آسمان بنائے ہیں'),
(5685, 78, 13, 'اور ایک بھڑکتا ہوا چراغ بنایا ہے'),
(5686, 78, 14, 'اور بادلوں میں سے موسلا دھار پانی برسایا ہے'),
(5687, 78, 15, 'تاکہ اس کے ذریعہ دانے اور گھاس برآمدکریں'),
(5688, 78, 16, 'اور گھنے گھنے باغات پیداکریں'),
(5689, 78, 17, 'بیشک فیصلہ کا دن معین ہے'),
(5690, 78, 18, 'جس دن صورپھونکا جائے گا اور تم سب فوج در فوج آؤ گے'),
(5691, 78, 19, 'اورآسمان کے راستے کھول دیئے جائیں گے اور دروازے بن جائیں گے'),
(5692, 78, 20, 'اورپہاڑوں کو جگہ سے حرکت دے دی جائے گی اوروہ ریت جیسے ہوجائیں گے'),
(5693, 78, 21, 'بیشک جہنم ان کی گھات میں ہے'),
(5694, 78, 22, 'وہ سرکشوں کا آخری ٹھکانا ہے'),
(5695, 78, 23, 'اس میں وہ مدتوں رہیں گے'),
(5696, 78, 24, 'نہ ٹھنڈک کا مزہ چکھ سکیں گے اور نہ کسی پینے کی چیز کا'),
(5697, 78, 25, 'علاوہ کھولتے پانی اورپیپ کے'),
(5698, 78, 26, 'یہ ان کے اعمال کامکمل بدلہ ہے'),
(5699, 78, 27, 'یہ لوگ حساب و کتاب کی امید ہی نہیں رکھتے تھے'),
(5700, 78, 28, 'اورانہوں نے ہماری آیات کی باقاعدہ تکذیب کی ہے'),
(5701, 78, 29, 'اور ہم نے ہر شے کو اپنی کتاب میں جمع کرلیا ہے'),
(5702, 78, 30, 'اب تم اپنے اعمال کا مزہ چکھو اورہم عذاب کے علاوہ کوئی اضافہ نہیں کرسکتے'),
(5703, 78, 31, 'بیشک صاحبانِ تقویٰ کے لئے کامیابی کی منزل ہے'),
(5704, 78, 32, 'باغات ہیں اورانگور'),
(5705, 78, 33, 'نوخیز دوشیزائیں ہیں اورسب ہمسن'),
(5706, 78, 34, 'اور چھلکتے ہوئے پیمانے'),
(5707, 78, 35, 'وہاں نہ کوئی لغو بات سنیں گے نہ گناہ'),
(5708, 78, 36, 'یہ تمہارے رب کی طرف سے حساب کی ہوئی عطا ہے اور تمہارے اعمال کی جزا'),
(5709, 78, 37, 'وہ آسمان و زمین اوران کے مابین کاپروردگار رحمٰن ہے جس کے سامنے کسی کو بات کرنے کا یارا نہیں ہے'),
(5710, 78, 38, 'جس دن روح القدس اورملائکہ صف بستہ کھڑے ہوں گے اورکوئی بات بھی نہ کرسکے گا علاوہ اس کے جسے رحمٰن اجازت دے دے اور ٹھیک ٹھیک بات کرے'),
(5711, 78, 39, 'یہی برحق دن ہے تو جس کا جی چاہے اپنے رب کی طرف ٹھکانا بنالے'),
(5712, 78, 40, 'ہم نے تم کو ایک قریبی عذاب سے ڈرایا ہے جس دن انسان اپنے کئے دھرے کو دیکھے گا اورکافرکہے گا کہ اے کاش میں خاک ہوگیا ہوتا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'قسم ہے ان کی جو ڈوب کر کھینچ لینے والے ہیں'),
(5714, 79, 2, 'اور آسانی سے کھول دینے والے ہیں'),
(5715, 79, 3, 'اور فضا میں پیرنے والے ہیں'),
(5716, 79, 4, 'پھر تیز رفتاری سے سبقت کرنے والے ہیں'),
(5717, 79, 5, 'پھر امور کا انتظام کرنے والے ہیں'),
(5718, 79, 6, 'جس دن زمین کو جھٹکا دیا جائے گا'),
(5719, 79, 7, 'اور اس کے بعد دوسرا جھٹکا لگے گا'),
(5720, 79, 8, 'اس دن دل لرز جائیں گے'),
(5721, 79, 9, 'آنکھیں خوف سے جھکیِ ہوں گی'),
(5722, 79, 10, 'یہ کفاّر کہتے ہیں کہ کیا ہم پلٹ کر پھر اس دنیا میں بھیجے جائیں گے'),
(5723, 79, 11, 'کیا جب ہم کھوکھلی ہڈیاں ہوجائیں گے تب'),
(5724, 79, 12, 'یہ تو بڑے گھاٹے والی واپسی ہوگی'),
(5725, 79, 13, 'یہ قیامت تو بس ایک چیخ ہوگی'),
(5726, 79, 14, 'جس کے بعد سب میدان هحشر میں نظر آئیں گے'),
(5727, 79, 15, 'کیا تمہارے پاس موسٰی کی خبر آئی ہے'),
(5728, 79, 16, 'جب ان کے رب نے انہیں طویٰ کی مقدس وادی میں آواز دی'),
(5729, 79, 17, 'فرعون کی طرف جاؤ وہ سرکش ہوگیا ہے'),
(5730, 79, 18, 'اس سے کہو کیا یہ ممکن ہے تو پاکیزہ کردار ہوجائے'),
(5731, 79, 19, 'اور میں تجھے تیرے رب کی طرف ہدایت کروں اور تیرے دل میں خوف پیدا ہوجائے'),
(5732, 79, 20, 'پھر انہوں نے اسے عظیم نشانی دکھلائی'),
(5733, 79, 21, 'تو اس نے انکار کردیا اور نافرمانی کی'),
(5734, 79, 22, 'پھر منہ پھیر کر دوڑ دھوپ میں لگ گیا'),
(5735, 79, 23, 'پھر سب کو جمع کیا اور آواز دی'),
(5736, 79, 24, 'اور کہا کہ میں تمہارا رب اعلیٰ ہوں'),
(5737, 79, 25, 'تو خدا نے اسے دنیا و آخرت دونو ںکے عذاب کی گرفت میں لے لیا'),
(5738, 79, 26, 'اس واقعہ میں خوف خدا رکھنے والوں کے لئے عبرت کا سامان ہے'),
(5739, 79, 27, 'کیا تمہاری خلقت آسمان بنانے سے زیادہ مشکل کام ہے کہ اس نے آسمان کو بنایا ہے'),
(5740, 79, 28, 'اس کی چھت کو بلند کیا اور پھر برابر کردیا ہے'),
(5741, 79, 29, 'اس کی رات کو تاریک بنایا ہے اور دن کی روشنی نکال دی ہے'),
(5742, 79, 30, 'اس کے بعد زمین کا فرش بچھایا ہے'),
(5743, 79, 31, 'اس میں سے پانی اور چارہ نکالا ہے'),
(5744, 79, 32, 'اور پہاڑوں کو گاڑ دیا ہے'),
(5745, 79, 33, 'یہ سب تمہارے اور جانوروں کے لئے ایک سرمایہ ہے'),
(5746, 79, 34, 'پھر جب بڑی مصیبت آجائے گی'),
(5747, 79, 35, 'جس دن انسان یاد کرے گا کہ اس نے کیا کیا ہے'),
(5748, 79, 36, 'اور جہنم کو دیکھنے والوں کے لئے نمایاں کردیا جائے گا'),
(5749, 79, 37, 'پھر جس نے سرکشی کی ہے'),
(5750, 79, 38, 'اور زندگانی دنیا کو اختیار کیا ہے'),
(5751, 79, 39, 'جہنم ّاس کا ٹھکانا ہوگا'),
(5752, 79, 40, 'اور جس نے رب کی بارگاہ میں حاضری کا خوف پیدا کیا ہے اور اپنے نفس کو خواہشات سے روکا ہے'),
(5753, 79, 41, 'تو جنّت اس کا ٹھکانا اور مرکز ہے'),
(5754, 79, 42, 'پیغمبر لوگ آپ سے قیامت کے بارے میں پوچھتے ہیں کہ اس کا ٹھکانا کب ہے'),
(5755, 79, 43, 'آپ اس کی یاد کے بارے میں کس منزل پر ہیں'),
(5756, 79, 44, 'اس کے علم کی ا نتہائ آپ کے پروردگار کی طرف ہے'),
(5757, 79, 45, 'آپ تو صرف اس کا خوف رکھنے والوں کو اس سے ڈرانے والے ہیں'),
(5758, 79, 46, 'گویا جب وہ لوگ اسے دیکھیں گے تو ایسا معلوم ہوگاجیسے ایک شام یا ایک صبح دنیامیں ٹھہرے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'اس نے منھ بسو رلیا اور پیٹھ پھیرلی'),
(5760, 80, 2, 'کہ ان کے پاس ایک نابیناآگیا'),
(5761, 80, 3, 'اور تمھیں کیا معلوم شاید وہ پاکیزہ نفس ہوجاتا'),
(5762, 80, 4, 'یا نصیحت حاصل کرلیتا تو وہ نصیحت ا س کے کام آجاتی'),
(5763, 80, 5, 'لیکن جو مستغنی بن بیٹھا ہے'),
(5764, 80, 6, 'آپ اس کی فکر میں لگے ہوئے ہیں'),
(5765, 80, 7, 'حالانکہ آپ پر کوئی ذمہ داری نہیں ہے اگر وہ پاکیزہ نہ بھی بنے'),
(5766, 80, 8, 'لیکن جو آپ کے پاس دوڑ کر آیاہے'),
(5767, 80, 9, 'اور وہ خوف خدا بھی رکھتاہے'),
(5768, 80, 10, 'آپ اس سے بے رخی کرتے ہیں'),
(5769, 80, 11, 'دیکھئے یہ قرآن ایک نصیحت ہے'),
(5770, 80, 12, 'جس کا جی چاہے قبول کرلے'),
(5771, 80, 13, 'یہ باعزّت صحیفوں میں ہے'),
(5772, 80, 14, 'جو بلند و بالا اور پاکیزہ ہے'),
(5773, 80, 15, 'ایسے لکھنے والوں کے ہاتھوں میں ہیں'),
(5774, 80, 16, 'جو محترم اور نیک کردار ہیں'),
(5775, 80, 17, 'انسان اس بات سے مارا گیا کہ کس قدر ناشکرا ہوگیا ہے'),
(5776, 80, 18, 'آخر اسے کس چیز سے پیدا کیا ہے'),
(5777, 80, 19, 'اسے نطفہ سے پیدا کیا ہے پھر اس کا اندازہ مقرر کیا ہے'),
(5778, 80, 20, 'پھر اس کے لئے راستہ کو آسان کیا ہے'),
(5779, 80, 21, 'پھر اسے موت دے کر دفنا دیا'),
(5780, 80, 22, 'پھر جب چاہا دوبارہ زندہ کرکے اُٹھا لیا'),
(5781, 80, 23, 'ہرگز نہیں اس نے حکم خدا کو بالکل پورا نہیں کیا ہے'),
(5782, 80, 24, 'ذرا انسان اپنے کھانے کی طرف تو نگاہ کرے'),
(5783, 80, 25, 'بے شک ہم نے پانی برسایا ہے'),
(5784, 80, 26, 'پھر ہم نے زمین کو شگافتہ کیا ہے'),
(5785, 80, 27, 'پھر ہم نے اس میں سے دانے پیدا کئے ہیں'),
(5786, 80, 28, 'اور انگور اور ترکادیاں'),
(5787, 80, 29, 'اور زیتون اور کھجور'),
(5788, 80, 30, 'اور گھنے گھنے باغ'),
(5789, 80, 31, 'اور میوے اور چارہ'),
(5790, 80, 32, 'یہ سب تمہارے اور تمہارے جانوروں کے لئے سرمایہ حیات ہے'),
(5791, 80, 33, 'پھر جب کان کے پردے پھاڑنے والی قیامت آجائے گی'),
(5792, 80, 34, 'جس دن انسان اپنے بھائی سے فرار کرے گا'),
(5793, 80, 35, 'اور ماں باپ سے بھی'),
(5794, 80, 36, 'اور بیوی اور اولاد سے بھی'),
(5795, 80, 37, 'اس دن ہر آدمی کی ایک خاص فکر ہوگی جو اس کے لئے کافی ہوگی'),
(5796, 80, 38, 'اس دن کچھ چہرے روشن ہوں گے'),
(5797, 80, 39, 'مسکراتے ہوئے کھلے ہوئے'),
(5798, 80, 40, 'اور کچھ چہرے غبار آلود ہوں گے'),
(5799, 80, 41, 'ان پر ذلّت چھائی ہوئی ہوگی'),
(5800, 80, 42, 'یہی لوگ حقیقتا کافر اور فاجر ہوں گے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'جب چادر آفتاب کو لپیٹ دیا جائے گا'),
(5802, 81, 2, 'جب تارے گر پڑیں گے'),
(5803, 81, 3, 'جب پہاڑ حرکت میں آجائیں گے'),
(5804, 81, 4, 'جب عنقریب جننے والی اونٹنیاں معطل کردی جائیں گی'),
(5805, 81, 5, 'جب جانوروں کو اکٹھا کیا جائے گا'),
(5806, 81, 6, 'جب دریا بھڑک اٹھیں گے'),
(5807, 81, 7, 'جب روحوں کو جسموں سے جوڑ دیا جائے گا'),
(5808, 81, 8, 'اور جب زندہ درگور لڑکیوں کے بارے میں سوال کیا جائے گا'),
(5809, 81, 9, 'کہ انہیں کس گناہ میں مارا گیا ہے'),
(5810, 81, 10, 'اور جب نامہ اعمال منتشر کردیئے جائیں گے'),
(5811, 81, 11, 'اور جب آسمان کا چھلکا اُتار دیا جائے گا'),
(5812, 81, 12, 'اور جب جہنمّ کی آگ بھڑکا دی جائے گی'),
(5813, 81, 13, 'اور جب جنّت قریب تر کردی جائے گی'),
(5814, 81, 14, 'تب ہر نفس کو معلوم ہوگا کہ اس نے کیا حاضر کیا ہے'),
(5815, 81, 15, 'تو میں ان ستاروں کی قسم کھاتا ہوں جو پلٹ جانے والے ہیں'),
(5816, 81, 16, 'چلنے والے اورحُھپ جانے والے ہیں'),
(5817, 81, 17, 'اور رات کی قسم جب ختم ہونے کو آئے'),
(5818, 81, 18, 'اور صبح کی قسم جب سانس لینے لگے'),
(5819, 81, 19, 'بے شک یہ ایک معزز فرشتے کا بیان ہے'),
(5820, 81, 20, 'وہ صاحبِ قوت ہے اور صاحبِ عرش کی بارگاہ کا مکین ہے'),
(5821, 81, 21, 'وہ وہاں قابل اطاعت اور پھر امانت دار ہے'),
(5822, 81, 22, 'اور تمہارا ساتھی پیغمبر دیوانہ نہیں ہے'),
(5823, 81, 23, 'اور اس نے فرشتہ کو بلند اُفق پر دیکھا ہے'),
(5824, 81, 24, 'اور وہ غیب کے بارے میں بخیل نہیں ہے'),
(5825, 81, 25, 'اور یہ قرآن کسی شیطان رجیم کا قول نہیں ہے'),
(5826, 81, 26, 'تو تم کدھر چلے جارہے ہو'),
(5827, 81, 27, 'یہ صرف عالمین کے لئے ایک نصیحت کا سامان ہے'),
(5828, 81, 28, 'جو تم میں سے سیدھا ہونا چاہے'),
(5829, 81, 29, 'اور تم لوگ کچھ نہیں چاہ سکتے مگریہ کہ عالمین کا پروردگار خدا چاہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'جب آسمان شگافتہ ہوجائے گا'),
(5831, 82, 2, 'اور جب ستارے بکھر جائیں گے'),
(5832, 82, 3, 'اور جب دریا بہہ کر ایک دوسرے سے مل جائیں گے'),
(5833, 82, 4, 'اور جب قبروں کو اُبھار دیا جائے گا'),
(5834, 82, 5, 'تب ہر نفس کو معلوم ہوگا کہ کیا مقدم کیا ہے اور کیا موخر کیا ہے'),
(5835, 82, 6, 'اے انسان تجھے رب کریم کے بارے میں کس شے نے دھوکہ میں رکھا ہے'),
(5836, 82, 7, 'اس نے تجھے پیدا کیا ہے اور پھر برابر کرکے متوازن بنایا ہے'),
(5837, 82, 8, 'اس نے جس صورت میں چاہاہے تیرے اجزائ کی ترکیب کی ہے'),
(5838, 82, 9, 'مگر تم لوگ جزا کا انکار کرتے ہو'),
(5839, 82, 10, 'اور یقینا تمہارے سروں پر نگہبان مقرر ہیں'),
(5840, 82, 11, 'جو باعزّت لکھنے والے ہیں'),
(5841, 82, 12, 'وہ تمہارے اعمال کو خوب جانتے ہیں'),
(5842, 82, 13, 'بے شک نیک لوگ نعمتوں میں ہوں گے'),
(5843, 82, 14, 'اور بدکار افراد جہنم ّمیں ہوں گے'),
(5844, 82, 15, 'وہ روز جزا اسی میں جھونک دیئے جائیں گے'),
(5845, 82, 16, 'اور وہ اس سے بچ کر نہ جاسکیں گے'),
(5846, 82, 17, 'اور تم کیا جانو کہ جزا کا دن کیا شے ہے'),
(5847, 82, 18, 'پھر تمہیں کیا معلوم کہ جزا کا دن کیسا ہوتا ہے'),
(5848, 82, 19, 'اس دن کوئی کسی کے بارے میں کوئی اختیار نہ رکھتا ہوگا اور سارا اختیار اللہ کے ہاتھوں میں ہوگا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'ویل ہے ان کے لئے جو ناپ تول میں کمی کرنے والے ہیں'),
(5850, 83, 2, 'یہ جب لوگوں سے ناپ کرلیتے ہیں تو پورا مال لے لیتے ہیں'),
(5851, 83, 3, 'اور جب ان کے لئے ناپتے یا تولتے ہیں تو کم کردیتے ہیں'),
(5852, 83, 4, 'کیا انہیں یہ خیال نہیں ہے کہ یہ ایک روز دوبارہ اٹھائے جانے والے ہیں'),
(5853, 83, 5, 'بڑے سخت دن میں'),
(5854, 83, 6, 'جس دن سب رب العالمین کی بارگاہ میں حاضر ہوں گے'),
(5855, 83, 7, 'یاد رکھو کہ بدکاروں کا نامہ اعمال سجین میں ہوگا'),
(5856, 83, 8, 'اور تم کیا جو کہ سجین کیا ہے'),
(5857, 83, 9, 'ایک لکھا ہوا دفتر ہے'),
(5858, 83, 10, 'آج کے دن ان جھٹلانے والوں کے لئے بربادی ہے'),
(5859, 83, 11, 'جو لوگ روز جزا کا انکار کرتے ہیں'),
(5860, 83, 12, 'اور اس کا انکار صرف وہی کرتے ہیں جو حد سے گزر جانے والے گنہگار ہیں'),
(5861, 83, 13, 'جب ان کے سامنے آیات کی تلاوت کی جاتی ہے تو کہتے ہیں کہ یہ تو پرانے افسانے ہیں'),
(5862, 83, 14, 'نہیں نہیں بلکہ ان کے دلوں پر ان کے اعمال کا زنگ لگ گیا ہے'),
(5863, 83, 15, 'یاد رکھو انہیں روز قیامت پروردگار کی رحمت سے محجوب کردیا جائے گا'),
(5864, 83, 16, 'پھر اس کے بعد یہ جہنمّ میں جھونکے جانے والے ہیں'),
(5865, 83, 17, 'پھر ان سے کہا جائے گا کہ یہی وہ ہے جس کا تم انکار رہے تھے'),
(5866, 83, 18, 'یاد رکھو کہ نیک کردار افراد کا نامہ اعمال علیین میں ہوگا'),
(5867, 83, 19, 'اور تم کیا جانو کہ علیین کیا ہے'),
(5868, 83, 20, 'ایک لکھا ہوا دفتر ہے'),
(5869, 83, 21, 'جس کے گواہ ملائکہ مقربین ہیں'),
(5870, 83, 22, 'بے شک نیک لوگ نعمتوں میں ہوں گے'),
(5871, 83, 23, 'تختوں پر بیٹھے ہوئے نظارے کررہے ہوں گے'),
(5872, 83, 24, 'تم ان کے چہروں پر نعمت کی شادابی کا مشاہدہ کروگے'),
(5873, 83, 25, 'انہیں سربمہر خالص شراب سے سیراب کیا جائے گا'),
(5874, 83, 26, 'جس کی مہر مشک کی ہوگی اور ایسی چیزوں میں شوق کرنے والوں کو آپس میں سبقت اور رغبت کرنی چاہئے'),
(5875, 83, 27, 'اس شراب میں تسنیم کے پانی کی آمیزش ہوگی'),
(5876, 83, 28, 'یہ ایک چشمہ ہے جس سے مقرب بارگاہ بندے پانی پیتے ہیں'),
(5877, 83, 29, 'بے شک یہ مجرمین صاحبانِ ایمان کا مذاق اُڑایا کرتے تھے'),
(5878, 83, 30, 'اور جب وہ ان کے پاس سے گزرتے تھے تو اشارے کنائے کرتے تھے'),
(5879, 83, 31, 'اور جب اپنے اہل کی طرف پلٹ کر آتے تھے تو خوش وخرم ہوتے تھے'),
(5880, 83, 32, 'اور جب مومنین کو دیکھتے تو کہتے تھے کہ یہ سب اصلی گمراہ ہیں'),
(5881, 83, 33, 'حالانکہ انہیں ان کا نگراں بنا کر نہیں بھیجا گیا تھا'),
(5882, 83, 34, 'تو آج ایمان لانے والے بھی کفاّر کا مضحکہ اُڑائیں گے'),
(5883, 83, 35, 'تختوں پر بیٹھے ہوئے دیکھ رہے ہوں گے'),
(5884, 83, 36, 'اب تو کفار کو ان کے اعمال کا پورا پورا بدلہ مل رہا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'جب آسمان پھٹ جائے گا'),
(5886, 84, 2, 'اور اپنے پروردگار کا حکم بجا لائے گا اور یہ ضروری بھی ہے'),
(5887, 84, 3, 'اور جب زمین برابر کرکے پھیلا دی جائے گی'),
(5888, 84, 4, 'اور وہ اپنے ذخیرے پھینک کر خالی ہوجائے گی'),
(5889, 84, 5, 'اور اپنے پروردگار کا حکم بجا لائے گی اور یہ ضروری بھی ہے'),
(5890, 84, 6, 'اے انسان تو اپنے پروردگار کی طرف جانے کی کوشش کررہا ہے تو ایک دن اس کا سامنا کرے گا'),
(5891, 84, 7, 'پھر جس کو نامہ اعمال داہنے ہاتھ میں دیا جائے گا'),
(5892, 84, 8, 'اس کا حساب آسان ہوگا'),
(5893, 84, 9, 'اور وہ اپنے اہل کی طرف خوشی خوشی واپس آئے گا'),
(5894, 84, 10, 'اور جس کو نامہ اعمال پشت کی طرف سے دیا جائے گا'),
(5895, 84, 11, 'وہ عنقریب موت کی دعا کرے گا'),
(5896, 84, 12, 'اور جہنمّ کی آگ میں داخل ہوگا'),
(5897, 84, 13, 'یہ پہلے اپنے اہل و عیال میں بہت خوش تھا'),
(5898, 84, 14, 'اور اس کا خیال تھا کہ پلٹ کر خدا کی طرف نہیں جائے گا'),
(5899, 84, 15, 'ہاں اس کا پروردگار خوب دیکھنے والا ہے'),
(5900, 84, 16, 'میں شفق کی قسم کھا کر کہتا ہوں'),
(5901, 84, 17, 'اور رات اورجن چیزوں کو وہ ڈھانک لیتی ہے ان کی قسم'),
(5902, 84, 18, 'اور چاند کی قسم جب وہ پورا ہوجائے'),
(5903, 84, 19, 'کہ تم ایک مصیبت کے بعد دوسری مصیبت میں مبتلا ہوگے'),
(5904, 84, 20, 'پھر انہیں کیا ہوگیا ہے کہ ایمان نہیں لے آتے ہیں'),
(5905, 84, 21, 'اور جب ان کے سامنے قرآن پڑھا جاتا ہے تو سجدہ نہیں کرتے ہیں'),
(5906, 84, 22, 'بلکہ کفاّر تو تکذیب بھی کرتے ہیں'),
(5907, 84, 23, 'اور اللہ خوب جانتا ہے جو یہ اپنے دلوں میں چھپائے ہوئے ہیں'),
(5908, 84, 24, 'اب آپ انہیں دردناک عذاب کی بشارت دے دیں'),
(5909, 84, 25, 'علاوہ صاحبانِ ایمان و عمل صالح کے کہ ان کے لئے نہ ختم ہونے والا اجر و ثواب ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'برجوں والے آسمان کی قسم'),
(5911, 85, 2, 'اور اس دن کی قسم جس کا وعدہ دیا گیا ہے'),
(5912, 85, 3, 'اور گواہ اور جس کی گواہی دی جائے گی اس کی قسم'),
(5913, 85, 4, 'اصحاب اخدود ہلاک کردیئے گئے'),
(5914, 85, 5, 'آگ سے بھری ہوئی خندقوں والے'),
(5915, 85, 6, 'جن میں آگ بھرے بیٹھے ہوئے تھے'),
(5916, 85, 7, 'اور وہ مومنین کے ساتھ جو سلوک کررہے تھے خود ہی اس کے گواہ بھی ہیں'),
(5917, 85, 8, 'اور انہوں نے ان سے صرف اس بات کا بدلہ لیا ہے کہ وہ خدائے عزیز و حمید پر ایمان لائے تھے'),
(5918, 85, 9, 'وہ خدا جس کے اختیار میں آسمان و زمین کا سارا ملک ہے اور وہ ہر شے کا گواہ اور نگراں بھی ہے'),
(5919, 85, 10, 'بے شک جن لوگوں نے ایماندار مردوں اور عورتوں کو ستایا اور پھر توبہ نہ کی ان کے لئے جہنمّ کا عذاب ہے اور ان کے لئے جلنے کا عذاب بھی ہے'),
(5920, 85, 11, 'بے شک جو لوگ ایمان لے آئے اور انہوں نے نیک اعمال کئے ان کے لئے وہ جنتیں ہیں جن کے نیچے نہریں جاری ہوں گی اور یہ بہت بڑی کامیابی ہے'),
(5921, 85, 12, 'بے شک آپ کے پروردگار کی پکڑ بہت سخت ہوتی ہے'),
(5922, 85, 13, 'وہی پیدا کرنے والا اور دوبارہ ایجاد کرنے والا ہے'),
(5923, 85, 14, 'وہی بہت بخشنے والا اور محبت کرنے والا ہے'),
(5924, 85, 15, 'وہ صاحبِ عرش مجید ہے'),
(5925, 85, 16, 'جو چاہتا ہے کرسکتا ہے'),
(5926, 85, 17, 'کیا تمہارے پاس لشکروں کی خبر آئی ہے'),
(5927, 85, 18, 'فرعون اور قوم ثمود کی خبر'),
(5928, 85, 19, 'مگر کفاّر تو صرف جھٹلانے میں پڑے ہوئے ہیں'),
(5929, 85, 20, 'اور اللہ ان کو پیچھے سے گھیرے ہوئے ہے'),
(5930, 85, 21, 'یقینا یہ بزرگ و برتر قرآن ہے'),
(5931, 85, 22, 'جو لوح محفوظ میں محفوظ کیا گیا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'آسمان اور رات کو آنے والے کی قسم'),
(5933, 86, 2, 'اور تم کیا جانو کہ طارق کیا ہے'),
(5934, 86, 3, 'یہ ایک چمکتا ہوا ستارہ ہے'),
(5935, 86, 4, 'کوئی نفس ایسا نہیں ہے جس کے اوپر نگراں نہ معین کیا گیا ہو'),
(5936, 86, 5, 'پھر انسان دیکھے کہ اسے کس چیز سے پیدا کیا گیا ہے'),
(5937, 86, 6, 'وہ ایک اُچھلتے ہوئے پانی سے پیدا کیا گیا ہے'),
(5938, 86, 7, 'جو پیٹھ اور سینہ کی ہڈیوں کے درمیان سے نکلتا ہے'),
(5939, 86, 8, 'یقینا وہ خدا انسان کے دوبارہ پیدا کرنے پر بھی قادر ہے'),
(5940, 86, 9, 'z جس دن رازوں کو آزمایا جائے گا'),
(5941, 86, 10, 'تو پھر نہ کسی کے پاس قوت ہوگی اور نہ مددگار'),
(5942, 86, 11, 'قسم ہے چکر کھانے والے آسمان کی'),
(5943, 86, 12, 'اور شگافتہ ہونے والی زمین کی'),
(5944, 86, 13, 'بے شک یہ قول فیصل ہے'),
(5945, 86, 14, 'اور مذاق نہیں ہے'),
(5946, 86, 15, 'یہ لوگ اپنا مکر کررہے ہیں'),
(5947, 86, 16, 'اور ہم اپنی تدبیر کررہے ہیں'),
(5948, 86, 17, 'تو کافروں کو چھوڑ دو اور انہیں تھوڑی سی مہلت دے دو');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'اپنے بلند ترین رب کے نام کی تسبیح کرو'),
(5950, 87, 2, 'جس نے پیدا کیا ہے اور درست بنایا ہے'),
(5951, 87, 3, 'جس نے تقدیر معین کی ہے اور پھر ہدایت دی ہے'),
(5952, 87, 4, 'جس نے چارہ بنایا ہے'),
(5953, 87, 5, 'پھر اسے خشک کرکے سیاہ رنگ کا کوڑا بنادیاہے'),
(5954, 87, 6, 'ہم عنقریب تمہیں اس طرح پڑھائیں گے کہ بھول نہ سکوگے'),
(5955, 87, 7, 'مگر یہ کہ خدا ہی چاہے کہ وہ ہر ظاہر اور مخفی رہنے والی چیز کو جانتا ہے'),
(5956, 87, 8, 'اور ہم تم کو آسان راستہ کی توفیق دیں گے'),
(5957, 87, 9, 'لہذا لوگوں کوسمجھاؤ اگر سمجھانے کا فائدہ ہو'),
(5958, 87, 10, 'عنقریب خوف خدا رکھنے والا سمجھ جائے گا'),
(5959, 87, 11, 'اور بدبخت اس سے کنارہ کشی کرے گا'),
(5960, 87, 12, 'جو بہت بڑی آگ میں جلنے والا ہے'),
(5961, 87, 13, 'پھر نہ اس میں زندگی ہے نہ موت'),
(5962, 87, 14, 'بے شک پاکیزہ رہنے والا کامیاب ہوگیا'),
(5963, 87, 15, 'جس نے اپنے رب کے نام کی تسبیح کی اور پھر نماز پڑھی'),
(5964, 87, 16, 'لیکن تم لوگ زندگانی دنیا کو مقدم رکھتے ہو'),
(5965, 87, 17, 'جب کہ آخرت بہتر اور ہمیشہ رہنے والی ہے'),
(5966, 87, 18, 'یہ بات تمام پہلے صحیفوں میں بھی موجود ہے'),
(5967, 87, 19, 'ابراہیم علیہ السّلام کے صحیفوں میں بھی اور موسٰی علیہ السّلام کے صحیفوں میں بھی');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'کیا تمہیں ڈھانپ لینے والی قیامت کی بات معلوم ہے'),
(5969, 88, 2, 'اس دن بہت سے چہرے ذلیل اور رسوا ہوں گے'),
(5970, 88, 3, 'محنت کرنے والے تھکے ہوئے'),
(5971, 88, 4, 'دہکتی ہوئی آگ میں داخل ہوں گے'),
(5972, 88, 5, 'انہیں کھولتے ہوئے پانی کے چشمہ سے سیراب کیا جائے گا'),
(5973, 88, 6, 'ان کے لئے کوئی کھانا سوائے خادار جھاڑی کے نہ ہوگا'),
(5974, 88, 7, 'جو نہ موٹائی پیدا کرسکے اور نہ بھوک کے کام آسکے'),
(5975, 88, 8, 'اور کچھ چہرے تر و تازہ ہوں گے'),
(5976, 88, 9, 'اپنی محنت و ریاضت سے خوش'),
(5977, 88, 10, 'بلند ترین جنت میں'),
(5978, 88, 11, 'جہاں کوئی لغو آواز نہ سنائی دے'),
(5979, 88, 12, 'وہاں چشمے جاری ہوں گے'),
(5980, 88, 13, 'وہاں اونچے اونچے تخت ہوں گے'),
(5981, 88, 14, 'اور اطراف میں رکھے ہوئے پیالے ہوں گے'),
(5982, 88, 15, 'اور قطار سے لگے ہوئے گاؤ تکیے ہوں گے'),
(5983, 88, 16, 'اور بچھی ہوئی بہترین مسندیں ہوں گی'),
(5984, 88, 17, 'کیا یہ لوگ اونٹ کی طرف نہیں دیکھتے ہیں کہ اسے کس طرح پیدا کیا گیا ہے'),
(5985, 88, 18, 'اور آسمان کو کس طرح بلند کیا گیا ہے'),
(5986, 88, 19, 'اور پہاڑ کو کس طرح نصب کیا گیا ہے'),
(5987, 88, 20, 'اور زمین کو کس طرح بچھایا گیا ہے'),
(5988, 88, 21, 'لہذا تم نصیحت کرتے رہو کہ تم صرف نصیحت کرنے والے ہو'),
(5989, 88, 22, 'تم ان پر مسلط اور ان کے ذمہ دار نہیں ہو'),
(5990, 88, 23, 'مگر جو منھ پھیر لے اور کافر ہوجائے'),
(5991, 88, 24, 'تو خدا اسے بہت بڑے عذاب میں مبتلا کرے گا'),
(5992, 88, 25, 'پھر ہماری ہی طرف ان سب کی بازگشت ہے'),
(5993, 88, 26, 'اور ہمارے ہی ذمہ ان سب کا حساب ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'قسم ہے فجر کی'),
(5995, 89, 2, 'اور دس راتوں کی'),
(5996, 89, 3, 'اور جفت و طاق کی'),
(5997, 89, 4, 'اور رات کی جب وہ جانے لگے'),
(5998, 89, 5, 'بے شک ان چیزوں میں قسم ہے صاحبِ عقل کے لئے'),
(5999, 89, 6, 'کیا تم نے نہیں دیکھا کہ تمہارے رب نے قوم عاد کے ساتھ کیا کیا ہے'),
(6000, 89, 7, 'ستون والے ارم والے'),
(6001, 89, 8, 'جس کا مثل دوسرے شہروں میں نہیں پیدا ہوا ہے'),
(6002, 89, 9, 'اور ثمود کے ساتھ جو وادی میں پتھر تراش کر مکان بناتے تھے'),
(6003, 89, 10, 'اور میخوں والے فرعون کے ساتھ'),
(6004, 89, 11, 'جن لوگوں نے شہروں میں سرکشی پھیلائی'),
(6005, 89, 12, 'اور خوب فساد کیا'),
(6006, 89, 13, 'تو پھر خدا نے ان پر عذاب کے کوڑے برسادیئے'),
(6007, 89, 14, 'بے شک تمہارا پروردگار ظالموں کی تاک میں ہے'),
(6008, 89, 15, 'لیکن انسان کا حال یہ ہے کہ جب خدا نے اس کو اس طرح آزمایا کہ عزّت اور نعمت دے دی تو کہنے لگا کہ میرے رب نے مجھے باعزّت بنایا ہے'),
(6009, 89, 16, 'اور جب آزمائش کے لئے روزی کو تنگ کردیا تو کہنے لگا کہ میرے پروردگار نے میری توہین کی ہے'),
(6010, 89, 17, 'ایسا ہرگز نہیں ہے بلکہ تم یتیموں کا احترام نہیں کرتے ہو'),
(6011, 89, 18, 'اور لوگوں کو م مسکینوں کے کھانے پر آمادہ نہیں کرتے ہو'),
(6012, 89, 19, 'اور میراث کے مال کو اکٹھا کرکے حلال و حرام سب کھا جاتے ہو'),
(6013, 89, 20, 'اور مال دنیا کو بہت دوست رکھتے ہو'),
(6014, 89, 21, 'یاد رکھو کہ جب زمین کو ریزہ ریزہ کردیا جائے گا'),
(6015, 89, 22, 'اور تمہارے پروردگار کا حکم اور فرشتے صف در صف آجائیں گے'),
(6016, 89, 23, 'اور جہنمّ کو اس دن سامنے لایا جائے گا تو انسان کو ہوش آجائے گا لیکن اس دن ہوش آنے کا کیا فائدہ'),
(6017, 89, 24, 'انسان کہے گا کہ کاش میں نے اپنی اس زندگی کے لئے کچھ پہلے بھیج دیا ہوتا'),
(6018, 89, 25, 'تو اس دن خدا ویسا عذاب کرے گا جو کسی نے نہ کیا ہوگا'),
(6019, 89, 26, 'اور نہ اس طرح کسی نے گرفتار کیا ہوگا'),
(6020, 89, 27, 'اے نفس مطمئن'),
(6021, 89, 28, 'اپنے رب کی طرف پلٹ آ اس عالم میں کہ تواس سے راضی ہے اور وہ تجھ سے راضی ہے'),
(6022, 89, 29, 'پھر میرے بندوں میں شامل ہوجا'),
(6023, 89, 30, 'اور میری جنت میں داخل ہوجا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'میں اس شہر کی قسم کھاتا ہوں'),
(6025, 90, 2, 'اور تم اسی شہر میں تو رہتے ہو'),
(6026, 90, 3, 'اور تمہارے باپ آدم علیھ السّلام اور ان کی اولاد کی قسم'),
(6027, 90, 4, 'ہم نے انسان کو مشقت میں رہنے والا بنایا ہے'),
(6028, 90, 5, 'کیا اس کا خیال یہ ہے کہ اس پر کوئی قابو نہ پاسکے گا'),
(6029, 90, 6, 'کہ وہ کہتا ہے کہ میں نے بے تحاشہ صرف کیا ہے'),
(6030, 90, 7, 'کیا اس کا خیال ہے کہ اس کو کسی نے نہیں دیکھا ہے'),
(6031, 90, 8, 'کیا ہم نے اس کے لئے دو آنکھیں نہیں قرار دی ہیں'),
(6032, 90, 9, 'اور زبان اور دو ہونٹ بھی'),
(6033, 90, 10, 'اور ہم نے اسے دونوں راستوں کی ہدایت دی ہے'),
(6034, 90, 11, 'پھر وہ گھاٹی پر سے کیوں نہیں گزرا'),
(6035, 90, 12, 'اورتم کیا جانو یہ گھاٹی کیا ہے'),
(6036, 90, 13, 'کسی گردن کا آزاد کرانا'),
(6037, 90, 14, 'یا بھوک کے دن میں کھانا کھلانا'),
(6038, 90, 15, 'کسی قرابتدار یتیم کو'),
(6039, 90, 16, 'یا خاکسار مسکین کو'),
(6040, 90, 17, 'پھر وہ ان لوگوں میں شامل ہوجاتا جو ایمان لائے اور انہوں نے صبر اور مرحمت کی ایک دوسرے کو نصیحت کی'),
(6041, 90, 18, 'یہی لوگ خو ش نصیبی والے ہیں'),
(6042, 90, 19, 'اور جن لوگوں نے ہماری آیات سے انکار کیا ہے وہ بد بختی والے ہیں'),
(6043, 90, 20, 'انہیں آگ میں ڈال کر اسے ہر طرف سے بند کردیا جائے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'آفتاب اور اس کی روشنی کی قسم'),
(6045, 91, 2, 'اور چاند کی قسم جب وہ اس کے پیچھے چلے'),
(6046, 91, 3, 'اور دن کی قسم جب وہ روشنی بخشے'),
(6047, 91, 4, 'اور رات کی قسم جب وہ اسے ڈھانک لے'),
(6048, 91, 5, 'اور آسمان کی قسم اور جس نے اسے بنایا ہے'),
(6049, 91, 6, 'اور زمین کی قسم اور جس نے اسے بچھایا ہے'),
(6050, 91, 7, 'اور نفس کی قسم اور جس نے اسے درست کیاہے'),
(6051, 91, 8, 'پھر بدی اور تقویٰ کی ہدایت دی ہے'),
(6052, 91, 9, 'بے شک وہ کامیاب ہوگیا جس نے نفس کو پاکیزہ بنالیا'),
(6053, 91, 10, 'اور وہ نامراد ہوگیا جس نے اسے آلودہ کردیا ہے'),
(6054, 91, 11, 'ثمود نے اپنی سرکشی کی بنا پر رسول کی تذکیب کی'),
(6055, 91, 12, 'جب ان کا بدبخت اٹھ کھڑا ہوا'),
(6056, 91, 13, 'تو خدا کے رسول نے کہا کہ خدا کی اونٹنی اور اس کی سیرابی کا خیال رکھنا'),
(6057, 91, 14, 'تو ان لوگوں نے اس کی تکذیب کی اور اس کی کونچیں کاٹ ڈالیں تو خدا نے ان کے گناہ کے سبب ان پر عذاب نازل کردیا اور انہیں بالکل برابر کردیا'),
(6058, 91, 15, 'اور اسے اس کے انجام کا کوئی خوف نہیں ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'رات کی قسم جب وہ دن کو ڈھانپ لے'),
(6060, 92, 2, 'اور دن کی قسم جب وہ چمک جائے'),
(6061, 92, 3, 'اور اس کی قسم جس نے مرد اور عورت کو پیدا کیا ہے'),
(6062, 92, 4, 'بے شک تمہاری کوششیں مختلف قسم کی ہیں'),
(6063, 92, 5, 'پھر جس نے مال عطا کیا اور تقویٰ اختیار کیا'),
(6064, 92, 6, 'اور نیکی کی تصدیق کی'),
(6065, 92, 7, 'تو اس کے لئے ہم آسانی کا انتظام کردیں گے'),
(6066, 92, 8, 'اور جس نے بخل کیا اور لاپروائی برتی'),
(6067, 92, 9, 'اور نیکی کو جھٹلایا ہے'),
(6068, 92, 10, 'اس کے لئے سختی کی راہ ہموار کردیں گے'),
(6069, 92, 11, 'اور اس کا مال کچھ کام نہ آئے گا جب وہ ہلاک ہوجائے گا'),
(6070, 92, 12, 'بے شک ہدایت کی ذمہ داری ہمارے اوپر ہے'),
(6071, 92, 13, 'اور دنیا و آخرت کا اختیار ہمارے ہاتھوں میں ہے'),
(6072, 92, 14, 'تو ہم نے تمہیں بھڑکتی ہوئی آگ سے ڈرایا'),
(6073, 92, 15, 'جس میں کوئی نہ جائے گا سوائے بدبخت شخص کے'),
(6074, 92, 16, 'جس نے جھٹلایا اور منھ پھیرلیا'),
(6075, 92, 17, 'اور اس سے عنقریب صاحبِ تقویٰ کو محفوظ رکھا جائے گا'),
(6076, 92, 18, 'جو اپنے مال کو دے کر پاکیزگی کا اہتمام کرتا ہے'),
(6077, 92, 19, 'جب کہ اس کے پاس کسی کا کوئی احسان نہیں ہے جس کی جزا دی جائے'),
(6078, 92, 20, 'سوائے یہ کہ وہ خدائے بزرگ کی مرضی کا طلبگار ہے'),
(6079, 92, 21, 'اور عنقریب وہ راضی ہوجائے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'قسم ہے ایک پہر چڑھے دن کی'),
(6081, 93, 2, 'اور قسم ہے رات کی جب وہ چیزوں کی پردہ پوشی کرے'),
(6082, 93, 3, 'تمہارے پروردگار نے نہ تم کو چھو ڑا ہے اور نہ تم سے ناراض ہوا ہے'),
(6083, 93, 4, 'اور آخرت تمہارے لئے دنیا سے کہیں زیادہ بہتر ہے'),
(6084, 93, 5, 'اور عنقریب تمہارا پروردگار تمہیں اس قدر عطا کردے گا کہ خوش ہوجاؤ'),
(6085, 93, 6, 'کیا اس نے تم کو یتیم پاکر پناہ نہیں دی ہے'),
(6086, 93, 7, 'اور کیا تم کو گم گشتہ پاکر منزل تک نہیں پہنچایا ہے'),
(6087, 93, 8, 'اور تم کو تنگ دست پاکر غنی نہیں بنایا ہے'),
(6088, 93, 9, 'لہذا اب تم یتیم پر قہر نہ کرنا'),
(6089, 93, 10, 'اور سائل کوجھڑک مت دینا'),
(6090, 93, 11, 'اور اپنے پروردگار کی نعمتوں کو برابر بیان کرتے رہنا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'کیا ہم نے آپ کے سینہ کو کشادہ نہیں کیا'),
(6092, 94, 2, 'اور کیا آپ کے بوجھ کو اتار نہیں لیا'),
(6093, 94, 3, 'جس نے آپ کی کمر کو توڑ دیا تھا'),
(6094, 94, 4, 'اور آپ کے ذکر کو بلند کردیا'),
(6095, 94, 5, 'ہاں زحمت کے ساتھ آسانی بھی ہے'),
(6096, 94, 6, 'بے شک تکلیف کے ساتھ سہولت بھی ہے'),
(6097, 94, 7, 'لہذا جب آپ فارغ ہوجائیں تو نصب کردیں'),
(6098, 94, 8, 'اور اپنے رب کی طرف رخ کریں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'قسم ہے انجیر اور زیتون کی'),
(6100, 95, 2, 'اور طورسینین کی'),
(6101, 95, 3, 'اور اس امن والے شہر کی'),
(6102, 95, 4, 'ہم نے انسان کو بہترین ساخت میں پیدا کیا ہے'),
(6103, 95, 5, 'پھر ہم نے اس کو پست ترین حالت کی طرف پلٹا دیا ہے'),
(6104, 95, 6, 'علاوہ ان لوگوں کے جو ایمان لائے اور انہوں نے نیک اعمال انجام دیئے تو ان کے لئے نہ ختم ہونے والا اجر ہے'),
(6105, 95, 7, 'پھر تم کو روز جزا کے بارے میں کون جھٹلا سکتا ہے'),
(6106, 95, 8, 'کیا خدا سب سے بڑا حاکم اور فیصلہ کرنے والا نہیں ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'اس خدا کا نام لے کر پڑھو جس نے پیدا کیا ہے'),
(6108, 96, 2, 'اس نے انسان کو جمے ہوئے خون سے پیدا کیا ہے'),
(6109, 96, 3, 'پڑھو اور تمہارا پروردگار بڑا کریم ہے'),
(6110, 96, 4, 'جس نے قلم کے ذریعے تعلیم دی ہے'),
(6111, 96, 5, 'اور انسان کو وہ سب کچھ بتادیا ہے جو اسے نہیں معلوم تھا'),
(6112, 96, 6, 'بے شک انسان سرکشی کرتا ہے'),
(6113, 96, 7, 'کہ اپنے کو بے نیاز خیال کرتا ہے'),
(6114, 96, 8, 'بے شک آپ کے رب کی طرف واپسی ہے'),
(6115, 96, 9, 'کیا تم نے اس شخص کو دیکھا ہے جو منع کرتا ہے'),
(6116, 96, 10, 'بندئہ خدا کو جب وہ نماز پڑھتا ہے'),
(6117, 96, 11, 'کیا تم نے دیکھا کہ اگر وہ بندہ ہدایت پر ہو'),
(6118, 96, 12, 'یا تقویٰ کا حکم دے تو روکنا کیسا ہے'),
(6119, 96, 13, 'کیا تم نے دیکھا کہ اگر اس کافر نے جھٹلایا اور منھ پھیر لیا'),
(6120, 96, 14, 'تو کیا تمہیں نہیں معلوم کہ اللہ دیکھ رہا ہے'),
(6121, 96, 15, 'یاد رکھو اگر وہ روکنے سے باز نہ آیا تو ہم پیشانی کے بال پکڑ کر گھسیٹیں گے'),
(6122, 96, 16, 'جھوٹے اور خطا کار کی پیشانی کے بال'),
(6123, 96, 17, 'پھر وہ اپنے ہم نشینوں کو بلائے'),
(6124, 96, 18, 'ہم بھی اپنے جلاد فرشتوں کوبلاتے ہیں'),
(6125, 96, 19, 'دیکھو تم ہرگز اس کی اطاعت نہ کرنا اور سجدہ کرکے قرب خدا حاصل کرو');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'بے شک ہم نے اسے شب قدر میں نازل کیا ہے'),
(6127, 97, 2, 'اور آپ کیا جانیں یہ شب قدر کیا چیز ہے'),
(6128, 97, 3, 'شب قدر ہزار مہینوں سے بہتر ہے'),
(6129, 97, 4, 'اس میں ملائکہ اور روح القدس اسُن خدا کے ساتھ تمام امور کو لے کر نازل ہوتے ہیں'),
(6130, 97, 5, 'یہ رات طلوع فجر تک سلامتی ہی سلامتی ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'اہل کتاب کے کفاّر اور دیگر مشرکین اپنے کفر سے الگ ہونے والے نہیں تھے جب تک کہ ان کے پاس کِھلی دلیل نہ آجاتی'),
(6132, 98, 2, 'اللہ کی طرف کا نمائندہ جو پاکیزہ صحفیوں کی تلاوت کرے'),
(6133, 98, 3, 'جن میں قیمتی اور مضبوط باتیں لکھی ہوئی ہیں'),
(6134, 98, 4, 'اور یہ اہل کتاب متفرق نہیں ہوئے مگر اس وقت جب ان کے پاس کِھلی ہوئی دلیل آگئی'),
(6135, 98, 5, 'اور انہیں صرف اس بات کا حکم دیا گیا تھا کہ خدا کی عبادت کریں اور اس عبادت کو اسی کے لئے خالص رکھیں اور نماز قائم کریں اور زکوٰہ ادا کریں اور یہی سچاّ اور مستحکم دین ہے'),
(6136, 98, 6, 'بے شک اہل کتاب میں جن لوگوں نے کفر اختیار کیا ہے اور دیگر مشرکین سب جہنمّ میں ہمیشہ رہنے والے ہیں اور یہی بدترین خلائق ہیں'),
(6137, 98, 7, 'اور بے شک جو لوگ ایمان لائے ہیں اور انہوں نے نیک اعمال کئے ہیں وہ بہترین خلائق ہیں'),
(6138, 98, 8, 'پروردگار کے یہاں ان کی جزائ وہ باغات ہیں جن کے نیچے نہریں جاری ہوں گی وہ انہی میں ہمیشہ رہنے والے ہیں خدا ان سے راضی ہے اور وہ اس سے راضی ہیں اور یہ سب اس کے لئے ہے جس کے دل میں خوف خدا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'جب زمین زوروں کے ساتھ زلزلہ میں آجائے گی'),
(6140, 99, 2, 'اور وہ سارے خزانے نکال ڈالے گی'),
(6141, 99, 3, 'اور انسان کہے گا کہ اسے کیا ہو گیا ہے'),
(6142, 99, 4, 'اس دن وہ اپنی خبریںبیان کرے گی'),
(6143, 99, 5, 'کہ تمہارے پروردگار نے اسے اشارہ کیا ہے'),
(6144, 99, 6, 'اس روز سارے انسان گروہ در گروہ قبروں سے نکلیں گے تاکہ اپنے اعمال کو دیکھ سکیں'),
(6145, 99, 7, 'پھر جس شخص نے ذرہ برابر نیکی کی ہے وہ اسے دیکھے گا'),
(6146, 99, 8, 'اور جس نے ذرہ برابر برائی کی ہے وہ اسے دیکھے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'فراٹے بھرتے ہوئے تیز رفتار گھوڑوں کی قسم'),
(6148, 100, 2, 'جوٹا پ مار کر چنگاریاں اُڑانے والے ہیں'),
(6149, 100, 3, 'پھر صبحدم حملہ کرنے والے ہیں'),
(6150, 100, 4, 'پھر غبار جنگ اڑانے والے نہیں'),
(6151, 100, 5, 'اور دشمن کی جمعیت میں در آنے والے ہیں'),
(6152, 100, 6, 'بے شک انسان اپنے پروردگار کے لئے بڑا ناشکرا ہے'),
(6153, 100, 7, 'اور وہ خود بھی اس بات کا گواہ ہے'),
(6154, 100, 8, 'اور وہ مال کی محبت میں بہت سخت ہے'),
(6155, 100, 9, 'کیا اسے نہیں معلوم ہے کہ جب مفِدوں کو قبروں سے نکالا جائے گا'),
(6156, 100, 10, 'اور دل کے رازوں کو ظاہر کردیا جائے گا'),
(6157, 100, 11, 'تو ان کا پروردگار اس دن کے حالات سے خوب باخبر ہوگا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'کھڑکھڑانے والی'),
(6159, 101, 2, 'اور کیسی کھڑکھڑانے والی'),
(6160, 101, 3, 'اور تمہیں کیا معلوم کہ وہ کیسی کھڑکھڑانے والی ہے'),
(6161, 101, 4, 'جس دن لوگ بکھرے ہوئے پتنگوں کی مانند ہوجائیں گے'),
(6162, 101, 5, 'اور پہاڑ دھنکی ہوئی روئی کی طرح اُڑنے لگیں گے'),
(6163, 101, 6, 'تو اس دن جس کی نیکیوں کا پلّہ بھاری ہوگا'),
(6164, 101, 7, 'وہ پسندیدہ عیش میں ہوگا'),
(6165, 101, 8, 'اور جس کا پلہّ ہلکا ہوگا'),
(6166, 101, 9, 'اس کا مرکز ہاویہ ہے'),
(6167, 101, 10, 'اور تم کیا جانو کہ ہاویہ کیا مصیبت ہے'),
(6168, 101, 11, 'یہ ایک دہکی ہوئی آگ ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'تمہیں باہمی مقابلہ کثرت مال و اولاد نے غافل بنادیا'),
(6170, 102, 2, 'یہاں تک کہ تم نے قبروں سے ملاقات کرلی'),
(6171, 102, 3, 'دیکھو تمہیں عنقریب معلوم ہوجائے گا'),
(6172, 102, 4, 'اور پھر خوب معلوم ہوجائے گا'),
(6173, 102, 5, 'دیکھو اگر تمہیں یقینی علم ہوجاتا'),
(6174, 102, 6, 'کہ تم جہنمّ کو ضرور دیکھو گے'),
(6175, 102, 7, 'پھر اسے اپنی آنکھوں دیکھے یقین کی طرح دیکھو گے'),
(6176, 102, 8, 'اور پھر تم سے اس دن نعمت کے بارے میں سوال کیا جائے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'قسم ہے عصر کی'),
(6178, 103, 2, 'بے شک انسان خسارہ میں ہے'),
(6179, 103, 3, 'علاوہ ان لوگوں کے جو ایمان لائے اور انہوں نے نیک اعمال کئے اور ایک دوسرے کو حق اور صبر کی وصیت و نصیحت کی');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'تباہی اور بربادی ہے ہر طعنہ زن اور چغلخور کے لئے'),
(6181, 104, 2, 'جس نے مال کو جمع کیا اور خوب اس کا حساب رکھا'),
(6182, 104, 3, 'اس کا خیال تھا کہ یہ مال اسے ہمیشہ باقی رکھے گا'),
(6183, 104, 4, 'ہرگز نہیں اسے یقینا حطمہ میں ڈال دیا جائے گا'),
(6184, 104, 5, 'اور تم کیا جانو کہ حطمہ کیا شے ہے'),
(6185, 104, 6, 'یہ اللہ کی بھڑکائی ہوئی آگ ہے'),
(6186, 104, 7, 'جو دلوں تک چڑھ سسجائے گی'),
(6187, 104, 8, 'وہ آگ ان کے اوپر گھیر دی جائے گی'),
(6188, 104, 9, 'لمبے لمبے ستونوں کے ساتھ');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'کیا تم نے نہیں دیکھا کہ تمہارے رب نے ہاتھی والوں کے ساتھ کیا برتاؤ کیاہے'),
(6190, 105, 2, 'کیا ان کی چال کو بے کار نہیں کردیا ہے'),
(6191, 105, 3, 'اور ان پر اڑتی ہوئی ابابیل کو بھیج دیا ہے'),
(6192, 105, 4, 'جو انہیں کھرنجوں کی کنکریاں ماررہی تھیں'),
(6193, 105, 5, 'پھر انہوں نے ان سب کو چبائے ہوئے بھوسے کے مانند بنادیا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'قریش کے انس و الفت کی خاطر'),
(6195, 106, 2, 'جو انہیں سردی اور گرمی کے سفرسے ہے ابرہہ کو ہلاک کردیا ہے'),
(6196, 106, 3, 'لہذا انہیں چاہئے کہ اس گھر کے مالک کی عبادت کریں'),
(6197, 106, 4, 'جس نے انہیں بھوک میں سیر کیا ہے اور خوف سے محفوظ بنایا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'کیا تم نے اس شخص کو دیکھا ہے جوقیامت کو جھٹلاتا ہے'),
(6199, 107, 2, 'یہ وہی ہے جو یتیم کو دھکےّ دیتا ہے'),
(6200, 107, 3, 'اور کسی کو مسکین کے کھانے کے لئے تیار نہیں کرتا ہے'),
(6201, 107, 4, 'تو تباہی ہے ان نمازیوں کے لئے'),
(6202, 107, 5, 'جو اپنی نمازوں سے غافل رہتے ہیں'),
(6203, 107, 6, 'دکھانے کے لئے عمل کرتے ہیں'),
(6204, 107, 7, 'اور معمولی ظروف بھی عاریت پر دینے سے انکار کردتے ہیں');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'بے شک ہم نے آپ کو کوثر عطا کیا ہے'),
(6206, 108, 2, 'لہذا آپ اپنے رب کے لئے نماز پڑھیں اور قربانی دیں'),
(6207, 108, 3, 'یقینا آپ کا دشمن بے اولاد رہے گا');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'آپ کہہ دیجئے کہ اے کافرو'),
(6209, 109, 2, 'میں ان خداؤں کی عبادت نہیں کرسکتا جن کی تم پوجا کرتے ہو'),
(6210, 109, 3, 'اور نہ تم میرے خدا کی عبادت کرنے والے ہو'),
(6211, 109, 4, 'اور نہ میں تمہارے معبودوں کو پوجا کرنے ولا ہوں'),
(6212, 109, 5, 'اور نہ تم میرے معبود کے عبادت گزار ہو'),
(6213, 109, 6, 'تمہارے لئے تمہارا دین ہے اور میرے لئے میرا دین ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'جب خدا کی مدد اور فتح کی منزل آجائے گی'),
(6215, 110, 2, 'اور آپ دیکھیں گے کہ لوگ دین خدا میں فوج در فوج داخل ہورہے ہیں'),
(6216, 110, 3, 'تو اپنے رب کی حمد کی تسبیح کریں او راس سے استغفار کریں کہ وہ بہت زیادہ توبہ قبول کرنے والا ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'ابو لہب کے ہاتھ ٹوٹ جائیں اور وہ ہلاک ہوجائے'),
(6218, 111, 2, 'نہ اس کا مال ہی اس کے کام آیا اور نہ اس کا کمایا ہوا سامان ہی'),
(6219, 111, 3, 'وہ عنقریب بھڑکتی ہوئی آگ میں داخل ہو گا'),
(6220, 111, 4, 'اور اس کی بیوی جو لکڑی ڈھونے والی ہے'),
(6221, 111, 5, 'اس کی گردن میں بٹی ہوئی رسی بندھی ہوئی ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'اے رسول کہہ دیجئے کہ وہ اللہ ایک ہے'),
(6223, 112, 2, 'اللہ برحق اور بے نیاز ہے'),
(6224, 112, 3, 'اس کی نہ کوئی اولاد ہے اور نہ والد'),
(6225, 112, 4, 'اور نہ اس کا کوئی کفو اور ہمسر ہے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'کہہ دیجئے کہ میں صبح کے مالک کی پناہ چاہتا ہوں'),
(6227, 113, 2, 'تمام مخلوقات کے شر سے'),
(6228, 113, 3, 'اور اندھیری رات کے شر سے جب اس کا اندھیرا چھا جائے'),
(6229, 113, 4, 'اور گنڈوں پر پھونکنے والیوں کے شر سے'),
(6230, 113, 5, 'اور ہرحسد کرنے والے کے شر سے جب بھی وہ حسد کرے');
INSERT INTO `ur_jawadi` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'اے رسول کہہ دیجئے کہ میں انسانوں کے پروردگار کی پناہ چاہتا ہوں'),
(6232, 114, 2, 'جو تمام لوگوں کا مالک اور بادشاہ ہے'),
(6233, 114, 3, 'سارے انسانوں کا معبود ہے'),
(6234, 114, 4, 'شیطانی وسواس کے شر سے جو نام خدا سن کر پیچھے ہٹ جاتا ہے'),
(6235, 114, 5, 'اور جو لوگوں کے دلوں میں وسوسے پیدا کراتا ہے'),
(6236, 114, 6, 'وہ جنات میں سے ہو یا انسانوں میں سے');
