INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1, 1, 1, 'شروع کرتا ہوں اللہ تعالیٰ کے نام سے جو بڑا مہربان نہایت رحم واﻻ ہے'),
(2, 1, 2, 'سب تعریف اللہ تعالیٰ کے لئے ہے جو تمام جہانوں کا پالنے واﻻ ہے'),
(3, 1, 3, 'بڑا مہربان نہایت رحم کرنے واﻻ'),
(4, 1, 4, 'بدلے کے دن (یعنی قیامت) کا مالک ہے'),
(5, 1, 5, 'ہم صرف تیری ہی عبادت کرتے ہیں اور صرف تجھ ہی سے مدد چاہتے ہیں'),
(6, 1, 6, 'ہمیں سیدھی (اور سچی) راه دکھا'),
(7, 1, 7, 'ان لوگوں کی راه جن پر تو نے انعام کیا ان کی نہیں جن پر غضب کیا گیا (یعنی وه لوگ جنہوں نے حق کو پہچانا، مگر اس پر عمل پیرا نہیں ہوئے) اور نہ گمراہوں کی (یعنی وه لوگ جو جہالت کے سبب راه حق سے برگشتہ ہوگئے)');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(8, 2, 1, 'الم'),
(9, 2, 2, 'اس کتاب (کے اللہ کی کتاب ہونے) میں کوئی شک نہیں پرہیزگاروں کو راه دکھانے والی ہے'),
(10, 2, 3, 'جو لوگ غیب پر ایمان ﻻتے ہیں اور نماز کو قائم رکھتے ہیں اور ہمارے دیئے ہوئے (مال) میں سے خرچ کرتے ہیں'),
(11, 2, 4, 'اور جو لوگ ایمان ﻻتے ہیں اس پر جو آپ کی طرف اتارا گیا اور جو آپ سے پہلے اتارا گیا، اور وه آخرت پر بھی یقین رکھتے ہیں'),
(12, 2, 5, 'یہی لوگ اپنے رب کی طرف سے ہدایت پر ہیں اور یہی لوگ فلاح اور نجات پانے والے ہیں'),
(13, 2, 6, 'کافروں کو آپ کا ڈرانا، یا نہ ڈرانا برابر ہے، یہ لوگ ایمان نہ ﻻئیں گے'),
(14, 2, 7, 'اللہ تعالیٰ نے ان کے دلوں پر اور ان کے کانوں پر مہر کر دی ہے اور ان کی آنکھوں پر پرده ہے اور ان کے لئے بڑا عذاب ہے'),
(15, 2, 8, 'بعض لوگ کہتے ہیں کہ ہم اللہ تعالیٰ پر اور قیامت کے دن پر ایمان رکھتے ہیں، لیکن درحقیقت وه ایمان والے نہیں ہیں'),
(16, 2, 9, 'وه اللہ تعالیٰ کو اور ایمان والوں کو دھوکا دیتے ہیں، لیکن دراصل وه خود اپنے آپ کو دھوکا دے رہے ہیں، مگر سمجھتے نہیں'),
(17, 2, 10, 'ان کے دلوں میں بیماری تھی اللہ تعالیٰ نے انہیں بیماری میں مزید بڑھا دیا اور ان کے جھوٹ کی وجہ سے ان کے لئے دردناک عذاب ہے'),
(18, 2, 11, 'اور جب ان سے کہا جاتا ہے کہ زمین میں فساد نہ کرو تو جواب دیتے ہیں کہ ہم تو صرف اصلاح کرنے والے ہیں'),
(19, 2, 12, 'خبردار ہو! یقیناً یہی لوگ فساد کرنے والے ہیں، لیکن شعور (سمجھ) نہیں رکھتے'),
(20, 2, 13, 'اور جب ان سے کہا جاتا ہے کہ اور لوگوں (یعنی صحابہ) کی طرح تم بھی ایمان لاؤ تو جواب دیتے ہیں کہ کیا ہم ایسا ایمان ﻻئیں جیسا بیوقوف ﻻئے ہیں، خبردار ہوجاؤ! یقیناً یہی بیوقوف ہیں، لیکن جانتے نہیں'),
(21, 2, 14, 'اور جب ایمان والوں سے ملتے ہیں تو کہتے ہیں کہ ہم بھی ایمان والے ہیں اور جب اپنے بڑوں کے پاس جاتے ہیں تو کہتے ہیں کہ ہم تو تمہارے ساتھ ہیں ہم تو ان سے صرف مذاق کرتے ہیں'),
(22, 2, 15, 'اللہ تعالیٰ بھی ان سے مذاق کرتا ہے اور انہیں ان کی سرکشی اور بہکاوے میں اور بڑھا دیتا ہے'),
(23, 2, 16, 'یہ وه لوگ ہیں جنہوں نے گمراہی کو ہدایت کے بدلے میں خرید لیا، پس نہ تو ان کی تجارت نے ان کو فائده پہنچایا اور نہ یہ ہدایت والے ہوئے'),
(24, 2, 17, 'ان کی مثال اس شخص کی سی ہے جس نے آگ جلائی، پس آس پاس کی چیزیں روشنی میں آئی ہی تھیں کہ اللہ ان کے نور کو لے گیا اور انہیں اندھیروں میں چھوڑ دیا، جو نہیں دیکھتے'),
(25, 2, 18, 'بہرے، گونگے، اندھے ہیں۔ پس وه نہیں لوٹتے'),
(26, 2, 19, 'یا آسمانی برسات کی طرح جس میں اندھیریاں اور گرج اور بجلی ہو، موت سے ڈر کر کڑاکے کی وجہ سے اپنی انگلیاں اپنے کانوں میں ڈال لیتے ہیں۔ اور اللہ تعالیٰ کافروں کو گھیرنے واﻻ ہے'),
(27, 2, 20, 'قریب ہے کہ بجلی ان کی آنکھیں اُچک لے جائے، جب ان کے لئے روشنی کرتی ہے تو اس میں چلتے پھرتے ہیں اور جب ان پر اندھیرا کرتی ہے تو کھڑے ہوجاتے ہیں اور اگر اللہ تعالیٰ چاہے تو ان کے کانوں اور آنکھوں کو بیکار کردے۔ یقیناً اللہ تعالیٰ ہر چیز پر قدرت رکھنے واﻻ ہے'),
(28, 2, 21, 'اے لوگو! اپنے اس رب کی عبادت کرو جس نے تمہیں اور تم سے پہلے کے لوگوں کو پیدا کیا، یہی تمہارا بچاؤ ہے'),
(29, 2, 22, 'جس نے تمہارے لئے زمین کو فرش اور آسمان کو چھت بنایا اور آسمان سے پانی اتار کر اس سے پھل پیدا کرکے تمہیں روزی دی، خبردار باوجود جاننے کے اللہ کے شریک مقرر نہ کرو'),
(30, 2, 23, 'ہم نے جو کچھ اپنے بندے پر اتارا ہے اس میں اگر تمہیں شک ہو اور تم سچے ہو تو اس جیسی ایک سورت تو بنا لاؤ، تمہیں اختیار ہے کہ اللہ تعالیٰ کے سوا اپنے مددگاروں کو بھی بلا لو'),
(31, 2, 24, 'پس اگر تم نے نہ کیا اور تم ہرگز نہیں کرسکتے تو (اسے سچا مان کر) اس آگ سے بچو جس کا ایندھن انسان اور پتھر ہیں، جو کافروں کے لئے تیار کی گئی ہے'),
(32, 2, 25, 'اور ایمان والوں اور نیک عمل کرنے والوں کو ان جنتوں کی خوشخبریاں دو، جن کےنیچے نہریں بہہ رہی ہیں۔ جب کبھی وه پھلوں کا رزق دیئے جائیں گے اور ہم شکل ﻻئے جائیں گے تو کہیں گے یہ وہی ہے جو ہم اس سے پہلے دیئے گئے تھے اور ان کے لئے بیویاں ہیں صاف ستھری اور وه ان جنتوں میں ہمیشہ رہنے والے ہیں'),
(33, 2, 26, 'یقیناً اللہ تعالیٰ کسی مثال کے بیان کرنے سے نہیں شرماتا، خواه مچھر کی ہو، یا اس سے بھی ہلکی چیز کی۔ ایمان والے تو اسے اپنے رب کی جانب سے صحیح سمجھتے ہیں اور کفار کہتے ہیں کہ اس مثال سے اللہ نے کیا مراد لی ہے؟ اس کے ذریعے بیشتر کو گمراه کرتا ہے اور اکثر لوگوں کو راه راست پر ﻻتا ہے اور گمراه تو صرف فاسقوں کو ہی کرتا ہے'),
(34, 2, 27, 'جو لوگ اللہ تعالیٰ کے مضبوط عہد کو توڑ دیتے ہیں اور اللہ تعالیٰ نے جن چیزوں کے جوڑنے کا حکم دیا ہے، انہیں کاٹتے اور زمین میں فساد پھیلاتے ہیں، یہی لوگ نقصان اٹھانے والے ہیں'),
(35, 2, 28, 'تم اللہ کے ساتھ کیسے کفر کرتے ہو؟ حاﻻنکہ تم مرده تھے اس نے تمہیں زنده کیا، پھر تمہیں مار ڈالے گا، پھر زنده کرے گا، پھر اسی کی طرف لوٹائے جاؤ گے'),
(36, 2, 29, 'وه اللہ جس نے تمہارے لئے زمین کی تمام چیزوں کو پیدا کیا، پھر آسمان کی طرف قصد کیا اور ان کو ٹھیک ٹھاک سات آسمان بنایا اور وه ہر چیز کو جانتا ہے'),
(37, 2, 30, 'اور جب تیرے رب نے فرشتوں سے کہا کہ میں زمین میں خلیفہ بنانے واﻻ ہوں، تو انہوں نے کہا ایسے شخص کو کیوں پیدا کرتا ہے جو زمین میں فساد کرے اور خون بہائے؟ اور ہم تیری تسبیح، حمد اور پاکیزگی بیان کرنے والے ہیں۔ اللہ تعالیٰ نے فرمایا، جو میں جانتا ہوں تم نہیں جانتے'),
(38, 2, 31, 'اور اللہ تعالیٰ نے آدم کو تمام نام سکھا کر ان چیزوں کو فرشتوں کے سامنے پیش کیا اور فرمایا، اگر تم سچے ہو تو ان چیزوں کے نام بتاؤ'),
(39, 2, 32, 'ان سب نے کہا اے اللہ! تیری ذات پاک ہے ہمیں تو صرف اتنا ہی علم ہے جتنا تونے ہمیں سکھا رکھا ہے، پورے علم وحکمت واﻻ تو تو ہی ہے'),
(40, 2, 33, 'اللہ تعالیٰ نے (حضرت) آدم ﴿علیہ السلام﴾ سے فرمایا تم ان کے نام بتا دو۔ جب انہوں نے بتا دیئے تو فرمایا کہ کیا میں نے تمہیں (پہلے ہی) نہ کہا تھا کہ زمین اور آسمانوں کا غیب میں ہی جانتا ہوں اور میرے علم میں ہے جو تم ﻇاہر کر رہے ہو اور جو تم چھپاتے تھے'),
(41, 2, 34, 'اور جب ہم نے فرشتوں سے کہا کہ آدم کو سجده کرو تو ابلیس کے سوا سب نے سجده کیا۔ اس نے انکار کیا اور تکبر کیا اور وه کافروں میں ہوگیا'),
(42, 2, 35, 'اور ہم نے کہہ دیا کہ اے آدم! تم اور تمہاری بیوی جنت میں رہو اور جہاں کہیں سے چاہو بافراغت کھاؤ پیو، لیکن اس درخت کے قریب بھی نہ جانا ورنہ ﻇالم ہوجاؤ گے'),
(43, 2, 36, 'لیکن شیطان نے ان کو بہکا کر وہاں سے نکلوا ہی دیا اور ہم نے کہہ دیا کہ اتر جاؤ! تم ایک دوسرے کے دشمن ہو اور ایک وقت مقرر تک تمہارے لئے زمین میں ٹھہرنا اور فائده اٹھانا ہے'),
(44, 2, 37, '(حضرت) آدم (علیہ السلام) نے اپنے رب سے چند باتیں سیکھ لیں اور اللہ تعالیٰ نے ان کی توبہ قبول فرمائی، بےشک وہی توبہ قبول کرنے واﻻ اور رحم کرنے واﻻ ہے'),
(45, 2, 38, 'ہم نے کہا تم سب یہاں سے چلے جاؤ، جب کبھی تمہارے پاس میری ہدایت پہنچے تو اس کی تابعداری کرنے والوں پر کوئی خوف وغم نہیں'),
(46, 2, 39, 'اور جو انکار کرکے ہماری آیتوں کو جھٹلائیں، وه جہنمی ہیں اور ہمیشہ اسی میں رہیں گے'),
(47, 2, 40, 'اے بنی اسرائیل! میری اس نعمت کو یاد کرو جو میں نے تم پر انعام کی اور میرے عہد کو پورا کرو میں تمہارے عہد کو پورا کروں گا اور مجھ ہی سے ڈرو'),
(48, 2, 41, 'اور اس کتاب پر ایمان لاؤ جو میں نے تمہاری کتابوں کی تصدیق میں نازل فرمائی ہے اور اس کے ساتھ تم ہی پہلے کافر نہ بنو اور میری آیتوں کو تھوڑی تھوڑی قیمت پر نہ فروخت کرو اور صرف مجھ ہی سے ڈرو'),
(49, 2, 42, 'اور حق کو باطل کے ساتھ خَلط مَلط نہ کرو اور نہ حق کو چھپاؤ، تمہیں تو خود اس کا علم ہے'),
(50, 2, 43, 'اور نمازوں کو قائم کرو اور زکوٰة دو اور رکوع کرنے والوں کے ساتھ رکوع کرو'),
(51, 2, 44, 'کیا لوگوں کو بھلائیوں کا حکم کرتے ہو؟ اور خود اپنے آپ کو بھول جاتے ہو باوجودیکہ تم کتاب پڑھتے ہو، کیا اتنی بھی تم میں سمجھ نہیں؟'),
(52, 2, 45, 'اور صبر اور نماز کے ساتھ مدد طلب کرو یہ چیز شاق ہے، مگر ڈر رکھنے والوں پر'),
(53, 2, 46, 'جو جانتے ہیں کہ بے شک وه اپنے رب سے ملاقات کرنے والے اور یقیناً وہ اسی کی طرف لوٹ کر جانے والے ہیں'),
(54, 2, 47, 'اے اوﻻد یعقوب! میری اس نعمت کو یاد کرو جو میں نے تم پر انعام کی اور میں نے تمہیں تمام جہانوں پر فضیلت دی'),
(55, 2, 48, 'اس دن سے ڈرتے رہو جب کوئی کسی کو نفع نہ دے سکے گا اور نہ ہی اس کی بابت کوئی سفارش قبول ہوگی اور نہ کوئی بدلہ اس کے عوض لیا جائے گا اور نہ وه مدد کئے جائیں گے'),
(56, 2, 49, 'اور جب ہم نے تمہیں فرعونیوں سے نجات دی جو تمہیں بدترین عذاب دیتے تھے جو تمہارے لڑکوں کو مار ڈالتے تھے اور تمہاری لڑکیوں کو چھوڑ دیتے تھے، اس نجات دینے میں تمہارے رب کی بڑی مہربانی تھی'),
(57, 2, 50, 'اور جب ہم نے تمہارے لئے دریا چیر (پھاڑ) دیا اور تمہیں اس سے پار کردیا اور فرعونیوں کو تمہاری نظروں کے سامنے اس میں ڈبو دیا'),
(58, 2, 51, 'اور ہم نے (حضرت) موسیٰ ﴿علیہ السلام﴾ سے چالیس راتوں کا وعده کیا، پھر تم نے اس کے بعد بچھڑا پوجنا شروع کردیا اور ﻇالم بن گئے'),
(59, 2, 52, 'لیکن ہم نے باوجود اس کے پھر بھی تمہیں معاف کردیا، تاکہ تم شکر کرو'),
(60, 2, 53, 'اور ہم نے (حضرت) موسیٰ﴿علیہ السلام﴾ کو تمہاری ہدایت کے لئے کتاب اور معجزے عطا فرمائے'),
(61, 2, 54, 'جب (حضرت موسیٰ) ﴿علیہ السلام﴾ نے اپنی قوم سے کہا کہ اے میری قوم! بچھڑے کو معبود بنا کر تم نے اپنی جانوں پر ﻇلم کیا ہے، اب تم اپنے پیدا کرنے والے کی طرف رجوع کرو، اپنے کو آپس میں قتل کرو، تمہاری بہتری اللہ تعالیٰ کے نزدیک اسی میں ہے، تو اس نے تمہاری توبہ قبول کی، وه توبہ قبول کرنے واﻻ اور رحم وکرم کرنے واﻻ ہے'),
(62, 2, 55, 'اور (تم اسے بھی یاد کرو) تم نے (حضرت) موسیٰ ﴿علیہ السلام﴾ سے کہا تھا کہ جب تک ہم اپنے رب کو سامنے نہ دیکھ لیں ہرگز ایمان نہ ﻻئیں گے (جس گستاخی کی سزا میں) تم پر تمہارے دیکھتے ہوئے بجلی گری'),
(63, 2, 56, 'لیکن پھر اس لئے کہ تم شکرگزاری کرو، اس موت کے بعد بھی ہم نے تمہیں زنده کردیا'),
(64, 2, 57, 'اور ہم نے تم پر بادل کا سایہ کیا اور تم پر من و سلویٰ اتارا (اور کہہ دیا) کہ ہماری دی ہوئی پاکیزه چیزیں کھاؤ، اور انہوں نے ہم پر ﻇلم نہیں کیا، البتہ وه خود اپنی جانوں پر ﻇلم کرتے تھے'),
(65, 2, 58, 'اور ہم نے تم سے کہا کہ اس بستی میں جاؤ اور جو کچھ جہاں کہیں سے چاہو بافراغت کھاؤ پیو اور دروازے میں سجدے کرتے ہوئے گزرو اور زبان سے حِطّہ کہو ہم تمہاری خطائیں معاف فرمادیں گے اور نیکی کرنے والوں کو اور زیاده دیں گے'),
(66, 2, 59, 'پھر ان ﻇالموں نے اس بات کو جو ان سے کہی گئی تھی بدل ڈالی، ہم نے بھی ان ﻇالموں پر ان کے فسق ونافرمانی کی وجہ سے آسمانی عذاب نازل کیا'),
(67, 2, 60, 'اور جب موسیٰ (علیہ السلام) نے اپنی قوم کے لئے پانی مانگا تو ہم نے کہا کہ اپنی ﻻٹھی پتھر پر مارو، جس سے باره چشمے پھوٹ نکلے اور ہر گروه نے اپنا چشمہ پہچان لیا (اور ہم نے کہہ دیا کہ) اللہ تعالیٰ کا رزق کھاؤ پیو اورزمین میں فساد نہ کرتے پھرو'),
(68, 2, 61, 'اور جب تم نے کہا اے موسیٰ! ہم سے ایک ہی قسم کے کھانے پر ہرگز صبر نہ ہوسکے گا، اس لئے اپنے رب سے دعا کیجیئے کہ وه ہمیں زمین کی پیداوار ساگ، ککڑی، گیہوں، مسور اور پیاز دے، آپ نے فرمایا، بہتر چیز کے بدلے ادنیٰ چیز کیوں طلب کرتے ہو! اچھا شہر میں جاؤ وہاں تمہاری چاہت کی یہ سب چیزیں ملیں گی۔ ان پر ذلت اور مسکینی ڈال دی گئی اور اللہ تعالی کا غضب لے کر وه لوٹے یہ اس لئے کہ وه اللہ تعالیٰ کی آیتوں کے ساتھ کفر کرتے تھے اور نبیوں کو ناحق قتل کرتے تھے، یہ ان کی نافرمانیوں اور زیادتیوں کا نتیجہ ہے'),
(69, 2, 62, 'مسلمان ہوں، یہودی ہوں، نصاریٰ ہوں یا صابی ہوں، جو کوئی بھی اللہ تعالیٰ پر اور قیامت کے دن پر ایمان ﻻئے اور نیک عمل کرے ان کے اجر ان کے رب کے پاس ہیں اور ان پر نہ تو کوئی خوف ہے اور نہ اداسی'),
(70, 2, 63, 'اور جب ہم نے تم سے وعده لیا اور تم پر طور پہاڑ ﻻکھڑا کردیا (اور کہا) جو ہم نے تمہیں دیا ہے، اسے مضبوطی سے تھام لو اور جو کچھ اس میں ہے اسے یاد کرو تاکہ تم بچ سکو'),
(71, 2, 64, 'لیکن تم اس کے بعد بھی پھر گئے، پھر اگر اللہ تعالیٰ کا فضل اور اس کی رحمت تم پر نہ ہوتی تو تم نقصان والے ہوجاتے'),
(72, 2, 65, 'اور یقیناً تمہیں ان لوگوں کا علم بھی ہے جو تم میں سے ہفتہ کے بارے میں حد سے بڑھ گئے اور ہم نے بھی کہہ دیا کہ تم ذلیل بندر بن جاؤ'),
(73, 2, 66, 'اسے ہم نے اگلوں پچھلوں کے لئے عبرت کا سبب بنا دیا اور پرہیزگاروں کے لئے وعﻆ ونصیحت کا'),
(74, 2, 67, 'اور (حضرت) موسیٰ﴿علیہ السلام﴾ نے جب اپنی قوم سے کہا کہ اللہ تعالیٰ تمہیں ایک گائے ذبح کرنے کا حکم دیتا ہے تو انہوں نے کہا ہم سے مذاق کیوں کرتے ہیں؟ آپ نے جواب دیا کہ میں ایسا جاہل ہونے سے اللہ تعالیٰ کی پناه پکڑتا ہوں'),
(75, 2, 68, 'انہوں نے کہا اے موسیٰ! دعا کیجیئے کہ اللہ تعالیٰ ہمارے لئے اس کی ماہیت بیان کردے، آپ نے فرمایا سنو! وه گائے نہ تو بالکل بڑھیا ہو، نہ بچہ، بلکہ درمیانی عمر کی نوجوان ہو، اب جو تمہیں حکم دیا گیا ہے بجا لاؤ'),
(76, 2, 69, 'وه پھر کہنے لگے کہ دعا کیجیے کہ اللہ تعالیٰ بیان کرے کہ اس کا رنگ کیا ہے؟ فرمایا وه کہتا ہے کہ وه گائے زرد رنگ کی ہے، چمکیلا اور دیکھنے والوں کو بھلا لگنے واﻻ اس کا رنگ ہے'),
(77, 2, 70, 'وه کہنے لگے کہ اپنے رب سے اور دعا کیجیئے کہ ہمیں اس کی مزید ماہیت بتلائے، اس قسم کی گائے تو بہت ہیں پتہ نہیں چلتا، اگر اللہ نے چاہا تو ہم ہدایت والے ہوجائیں گے'),
(78, 2, 71, 'آپ نے فرمایا کہ اللہ کا فرمان ہے کہ وه گائے کام کرنے والی زمین میں ہل جوتنے والی اور کھیتوں کو پانی پلانے والی نہیں، وه تندرست اور بےداغ ہے۔ انہوں نے کہا، اب آپ نے حق واضح کردیا گو وه حکم برداری کے قریب نہ تھے، لیکن اسے مانا اور وه گائے ذبح کردی'),
(79, 2, 72, 'جب تم نے ایک شخص کو قتل کر ڈاﻻ، پھر اس میں اختلاف کرنے لگے اور تمہاری پوشیدگی کو اللہ تعالیٰ ﻇاہر کرنے واﻻ تھا'),
(80, 2, 73, 'ہم نے کہا کہ اس گائے کا ایک ٹکڑا مقتول کے جسم پر لگا دو، (وه جی اٹھے گا) اسی طرح اللہ مردوں کو زنده کرکے تمہیں تمہاری عقل مندی کے لئے اپنی نشانیاں دکھاتا ہے'),
(81, 2, 74, 'پھر اس کے بعد تمہارے دل پتھر جیسے بلکہ اس سے بھی زیاده سخت ہوگئے، بعض پتھروں سے تو نہریں بہہ نکلتی ہیں، اور بعض پھٹ جاتے ہیں اور ان سے پانی نکل آتا ہے، اور بعض اللہ تعالیٰ کے ڈر سے گرگر پڑتے ہیں، اور تم اللہ تعالیٰ کو اپنے اعمال سے غافل نہ جانو'),
(82, 2, 75, '(مسلمانو!) کیا تمہاری خواہش ہے کہ یہ لوگ ایماندار بن جائیں، حاﻻنکہ ان میں ایسے لوگ بھی جو کلام اللہ کو سن کر، عقل وعلم والے ہوتے ہوئے، پھر بھی بدل ڈاﻻ کرتے ہیں'),
(83, 2, 76, 'جب ایمان والوں سے ملتے ہیں تو اپنی ایمانداری ﻇاہر کرتے ہیں اور جب آپس میں ملتے ہیں تو کہتے ہیں کہ مسلمانوں کو کیوں وه باتیں پہنچاتے ہو جو اللہ تعالیٰ نے تمہیں سکھائی ہیں، کیا جانتے نہیں کہ یہ تو اللہ تعالیٰ کے پاس تم پر ان کی حجت ہوجائے گی'),
(84, 2, 77, 'کیا یہ نہیں جانتے کہ اللہ تعالیٰ ان کی پوشیدگی اور ﻇاہرداری سب کو جانتا ہے؟'),
(85, 2, 78, 'ان میں سے بعض ان پڑھ ایسے بھی ہیں کہ جو کتاب کے صرف ﻇاہری الفاظ کو ہی جانتے ہیں اور صرف گمان اور اٹکل ہی پر ہیں'),
(86, 2, 79, 'ان لوگوں کے لئے “ویل” ہے جو اپنے ہاتھوں کی لکھی ہوئی کتاب کو اللہ تعالیٰ کی طرف کی کہتے ہیں اور اس طرح دنیا کماتے ہیں، ان کے ہاتھوں کی لکھائی کو اور ان کی کمائی کو ویل (ہلاکت) اور افسوس ہے'),
(87, 2, 80, 'یہ لوگ کہتے ہیں کہ ہم تو صرف چند روز جہنم میں رہیں گے، ان سے کہو کہ کیا تمہارے پاس اللہ تعالیٰ کا کوئی پروانہ ہے؟ اگر ہے تو یقیناً اللہ تعالیٰ اپنے وعدے کا خلاف نہیں کرے گا، (ہرگز نہیں) بلکہ تم تو اللہ کے ذمے وه باتیں لگاتے ہو جنہیں تم نہیں جانتے'),
(88, 2, 81, 'یقیناً جس نے بھی برے کام کئے اور اس کی نافرمانیوں نے اسے گھیر لیا، وه ہمیشہ کے لئے جہنمی ہے'),
(89, 2, 82, 'اور جو لوگ ایمان ﻻئیں اور نیک کام کریں وه جنتی ہیں جو جنت میں ہمیشہ رہیں گے'),
(90, 2, 83, 'اور جب ہم نے بنی اسرائیل سے وعده لیا کہ تم اللہ تعالیٰ کے سوا دوسرے کی عبادت نہ کرنا اور ماں باپ کے ساتھ اچھا سلوک کرنا، اسی طرح قرابتداروں، یتیموں اور مسکینوں کے ساتھ اور لوگوں کو اچھی باتیں کہنا، نمازیں قائم رکھنا اور زکوٰة دیتے رہا کرنا، لیکن تھوڑے سے لوگوں کے علاوه تم سب پھر گئے اور منھ موڑ لیا'),
(91, 2, 84, 'اورجب ہم نے تم سے وعده لیا کہ آپس میں خون نہ بہانا (قتل نہ کرنا) اور آپس والوں کو جلا وطن نہ کرنا، تم نے اقرار کیا اور تم اس کے شاہد بنے'),
(92, 2, 85, 'لیکن پھر بھی تم نے آپس میں قتل کیا اورآپس کے ایک فرقے کو جلا وطن بھی کیا اور گناه اور زیادتی کے کاموں میں ان کے خلاف دوسرے کی طرفداری کی، ہاں جب وه قیدی ہو کر تمہارے پاس آئے تو تم نے ان کے فدیے دیئے، لیکن ان کا نکالنا جو تم پر حرام تھا (اس کا کچھ خیال نہ کیا) کیا بعض احکام پر ایمان رکھتے ہو اور بعض کے ساتھ کفر کرتے ہو؟ تم میں سے جو بھی ایسا کرے، اس کی سزا اس کے سوا کیا ہو کہ دنیا میں رسوائی اور قیامت کے دن سخت عذاب کی مار، اور اللہ تعالیٰ تمہارے اعمال سے بےخبر نہیں'),
(93, 2, 86, 'یہ وه لوگ ہیں جنہوں نے دنیا کی زندگی کو آخرت کے بدلے خرید لیا ہے، ان کے نہ تو عذاب ہلکے ہوں گے اور نہ ان کی مدد کی جائے گی'),
(94, 2, 87, 'ہم نے (حضرت) موسیٰ کو کتاب دی اور ان کے پیچھے اور رسول بھیجے اور ہم نے (حضرت) عیسیٰ ابن مریم کو روشن دلیلیں دیں اور روح القدس سے ان کی تائید کروائی۔ لیکن جب کبھی تمہارے پاس رسول وه چیز ﻻئے جو تمہاری طبیعتوں کے خلاف تھی، تم نے جھٹ سے تکبر کیا، پس بعض کو تو جھٹلادیا اور بعض کو قتل بھی کرڈاﻻ'),
(95, 2, 88, 'یہ کہتے ہیں کہ ہمارے دل غلاف والے ہیں نہیں نہیں بلکہ ان کے کفر کی وجہ سے انہیں اللہ تعالیٰ نے ملعون کردیا ہے، ان کا ایمان بہت ہی تھوڑا ہے'),
(96, 2, 89, 'اور ان کے پاس جب اللہ تعالیٰ کی کتاب ان کی کتاب کو سچا کرنے والی آئی، حاﻻنکہ پہلے یہ خود (اس کے ذریعہ) کافروں پر فتح چاہتے تھے تو باوجود آجانے اور باوجود پہچان لینے کے پھر کفر کرنے لگے، اللہ تعالیٰ کی لعنت ہو کافروں پر'),
(97, 2, 90, 'بہت بری ہے وه چیز جس کے بدلے انہوں نے اپنے آپ کو بیچ ڈاﻻ، وه ان کا کفر کرنا ہے۔ اللہ تعالیٰ کی طرف سے نازل شده چیز کے ساتھ محض اس بات سے جل کر کہ اللہ تعالیٰ نے اپنا فضل اپنے جس بنده پر چاہا نازل فرمایا، اس کے باعﺚ یہ لوگ غضب پر غضب کے مستحق ہوگئے اور ان کافروں کے لئے رسوا کرنے واﻻ عذاب ہے'),
(98, 2, 91, 'اور جب ان سے کہا جاتا ہے کہ اللہ تعالیٰ کی اتاری ہوئی کتاب پر ایمان ﻻؤ تو کہہ دیتے ہیں کہ جو ہم پر اتاری گئی اس پر ہمارا ایمان ہے۔ حاﻻنکہ اس کے بعد والی کے ساتھ جو ان کی کتاب کی تصدیق کرنے والی ہے، کفر کرتے ہیں، اچھا ان سے یہ تو دریافت کریں کہ اگر تمہارا ایمان پہلی کتابوں پر ہے تو پھر تم نے اگلے انبیا کو کیوں قتل کیا؟'),
(99, 2, 92, 'تمہارے پاس تو موسیٰ یہی دلیلیں لے کر آئے لیکن تم نے پھر بھی بچھڑا پوجا تم ہو ہی ﻇالم'),
(100, 2, 93, 'جب ہم نے تم سے وعده لیا اور تم پر طور کو کھڑا کردیا (اور کہہ دیا) کہ ہماری دی ہوئی چیز کو مضبوط تھامو اور سنو! تو انہوں نے کہا، ہم نے سنا اور نافرمانی کی اور ان کے دلوں میں بچھڑے کی محبت (گویا) پلا دی گئی بسبب ان کے کفر کے۔ ان سے کہہ دیجیئے کہ تمہارا ایمان تمہیں برا حکم دے رہا ہے، اگر تم مومن ہو'),
(101, 2, 94, 'آپ کہہ دیجیئے کہ اگر آخرت کا گھر صرف تمہارے ہی لئے ہے، اللہ کے نزدیک اور کسی کے لئے نہیں، تو آو اپنی سچائی کے ﺛبوت میں موت طلب کرو'),
(102, 2, 95, 'لیکن اپنی کرتوتوں کو دیکھتے ہوئے کبھی بھی موت نہیں مانگیں گے اللہ تعالیٰ ﻇالموں کو خوب جانتا ہے'),
(103, 2, 96, 'بلکہ سب سے زیاده دنیا کی زندگی کا حریص اے نبی! آپ انہیں کو پائیں گے۔ یہ حرص زندگی میں مشرکوں سے بھی زیاده ہیں ان میں سے تو ہر شخص ایک ایک ہزار سال کی عمر چاہتا ہے، گویا یہ عمر دیا جانا بھی انہیں عذاب سے نہیں چھڑا سکتا، اللہ تعالیٰ ان کے کاموں کو بخوبی دیکھ رہا ہے'),
(104, 2, 97, '(اے نبی!) آپ کہہ دیجیئے کہ جو جبریل کا دشمن ہو جس نے آپ کے دل پر پیغام باری تعالیٰ اتارا ہے، جو پیغام ان کے پاس کی کتاب کی تصدیق کرنے واﻻ اور مومنوں کو ہدایت اور خوشخبری دینے واﻻ ہے'),
(105, 2, 98, '(تو اللہ بھی اس کا دشمن ہے) جو شخص اللہ کا اور اس کے فرشتوں اور اس کے رسولوں اور جبرائیل اور میکائیل کا دشمن ہو، ایسے کافروں کا دشمن خود اللہ ہے'),
(106, 2, 99, 'اور یقیناً ہم نے آپ کی طرف روشن دلیلیں بھیجی ہیں جن کا انکار سوائے بدکاروں کے کوئی نہیں کرتا'),
(107, 2, 100, 'یہ لوگ جب کبھی کوئی عہد کرتے ہیں توان کی ایک نہ ایک جماعت اسے توڑ دیتی ہے، بلکہ ان میں سے اکثر ایمان سے خالی ہیں'),
(108, 2, 101, 'جب کبھی ان کے پاس اللہ کا کوئی رسول ان کی کتاب کی تصدیق کرنے واﻻ آیا، ان اہل کتاب کے ایک فرقہ نے اللہ کی کتاب کو اس طرح پیٹھ پیچھے ڈال دیا، گویا جانتے ہی نہ تھے'),
(109, 2, 102, 'اور اس چیز کے پیچھے لگ گئے جسے شیاطین (حضرت) سلیمان کی حکومت میں پڑھتے تھے۔ سلیمان نے تو کفر نہ کیا تھا، بلکہ یہ کفر شیطانوں کا تھا، وه لوگوں کو جادو سکھایا کرتے تھے، اور بابل میں ہاروت ماروت دو فرشتوں پرجو اتارا گیا تھا، وه دونوں بھی کسی شخص کو اس وقت تک نہیں سکھاتے تھے جب تک یہ نہ کہہ دیں کہ ہم تو ایک آزمائش ہیں تو کفر نہ کر، پھر لوگ ان سے وه سیکھتے جس سے خاوند وبیوی میں جدائی ڈال دیں اور دراصل وه بغیر اللہ تعالیٰ کی مرضی کے کسی کو کوئی نقصان نہیں پہنچا سکتے، یہ لوگ وه سیکھتے ہیں جو انہیں نقصان پہنچائے اور نفع نہ پہنچا سکے، اور وه بالیقین جانتے ہیں کہ اس کے لینے والے کا آخرت میں کوئی حصہ نہیں۔ اور وه بدترین چیز ہے جس کے بدلے وه اپنے آپ کو فروخت کر رہے ہیں، کاش کہ یہ جانتے ہوتے'),
(110, 2, 103, 'اگر یہ لوگ صاحب ایمان متقی بن جاتے تو اللہ تعالیٰ کی طرف سے بہترین ﺛواب انہیں ملتا، اگر یہ جانتے ہوتے'),
(111, 2, 104, 'اے ایمان والو! تم (نبی صلی اللہ علیہ وسلم کو) “راعنا” نہ کہا کرو، بلکہ “انظرنا” کہو یعنی ہماری طرف دیکھئے اور سنتے رہا کرو اور کافروں کے لئے دردناک عذاب ہے'),
(112, 2, 105, 'نہ تو اہل کتاب کے کافر اور نہ مشرکین چاہتے ہیں کہ تم پر تمہارے رب کی کوئی بھلائی نازل ہو (ان کے اس حسد سے کیا ہوا) اللہ تعالیٰ جسے چاہے اپنی رحمت خصوصیت سے عطا فرمائے، اللہ تعالیٰ بڑے فضل واﻻ ہے'),
(113, 2, 106, 'جس آیت کو ہم منسوخ کردیں، یا بھلا دیں اس سے بہتر یا اس جیسی اور ﻻتے ہیں، کیا تو نہیں جانتا کہ اللہ تعالیٰ ہر چیز پر قادر ہے'),
(114, 2, 107, 'کیا تجھے علم نہیں کہ زمین و آسمان کا ملک اللہ ہی کے لئے ہے اور اللہ کے سوا تمہارا کوئی ولی اور مددگار نہیں'),
(115, 2, 108, 'کیا تم اپنے رسول سے یہی پوچھنا چاہتے ہو جو اس سے پہلے موسیٰ ﴿علیہ السلام﴾ سے پوچھا گیا تھا؟ (سنو) ایمان کو کفر سے بدلنے واﻻ سیدھی راه سے بھٹک جاتا ہے'),
(116, 2, 109, 'ان اہل کتاب کے اکثر لوگ باوجود حق واضح ہوجانے کے محض حسد وبغض کی بنا پر تمہیں بھی ایمان سے ہٹا دینا چاہتے ہیں، تم بھی معاف کرو اور چھوڑو یہاں تک کہ اللہ تعالیٰاپنا حکم ﻻئے۔ یقیناً اللہ تعالے ہر چیز پر قدرت رکھتا ہے'),
(117, 2, 110, 'تم نمازیں قائم رکھو اور زکوٰة دیتے رہا کرو اور جو کچھ بھلائی تم اپنے لئے آگے بھیجو گے، سب کچھ اللہ کے پاس پالو گے، بےشک اللہ تعالیٰ تمہارے اعمال کو خوب دیکھ رہا ہے'),
(118, 2, 111, 'یہ کہتے ہیں کہ جنت میں یہود ونصاریٰ کے سوا اور کوئی نہ جائے گا، یہ صرف ان کی آرزوئیں ہیں، ان سے کہو کہ اگر تم سچے ہو تو کوئی دلیل تو پیش کرو'),
(119, 2, 112, 'سنو! جو بھی اپنے آپ کو خلوص کے ساتھ اللہ کے سامنے جھکا دے۔ بےشک اسے اس کا رب پورا بدلہ دے گا، اس پر نہ تو کوئی خوف ہوگا، نہ غم اور اداسی'),
(120, 2, 113, 'یہود کہتے ہیں کہ نصرانی حق پر نہیں اور نصرانی کہتے ہیں کہ یہودی حق پر نہیں، حاﻻنکہ یہ سب لوگ تورات پڑھتے ہیں۔ اسی طرح ان ہی جیسی بات بےعلم بھی کہتے ہیں۔ قیامت کے دن اللہ ان کے اس اختلاف کا فیصلہ ان کے درمیان کردے گا'),
(121, 2, 114, 'اس شخص سے بڑھ کر ﻇالم کون ہے جو اللہ تعالیٰ کی مسجدوں میں اللہ تعالیٰ کے ذکر کئے جانے کو روکے اور ان کی بربادی کی کوشش کرے، ایسے لوگوں کو خوف کھاتے ہوئے اس میں جانا چاہیئے، ان کے لئے دنیا میں بھی رسوائی ہے اور آخرت میں بھی بڑا عذاب ہے'),
(122, 2, 115, 'اور مشرق اور مغرب کا مالک اللہ ہی ہے۔ تم جدھر بھی منھ کرو ادھر ہی اللہ کا منھ ہے، اللہ تعالیٰ کشادگی اور وسعت واﻻ اور بڑے علم واﻻ ہے'),
(123, 2, 116, 'یہ کہتے ہیں کہ اللہ تعالیٰ کی اوﻻد ہے، (نہیں بلکہ) وه پاک ہے زمین و آسمان کی تمام مخلوق اس کی ملکیت میں ہے اور ہر ایک اس کافرمانبردار ہے'),
(124, 2, 117, 'وه زمین اور آسمانوں کا ابتداءً پیدا کرنے واﻻ ہے، وه جس کام کو کرنا چاہے کہہ دیتا ہے کہ ہوجا، بس وه وہیں ہوجاتا ہے'),
(125, 2, 118, 'اسی طرح بے علم لوگوں نے بھی کہا کہ خود اللہ تعالیٰ ہم سے باتیں کیوں نہیں کرتا، یا ہمارے پاس کوئی نشانی کیوں نہیں آتی؟ اسی طرح ایسی ہی بات ان کے اگلوں نے بھی کہی تھی، ان کے اور ان کے دل یکساں ہوگئے ہم نے تو یقین والوں کے لئے نشانیاں بیان کردیں'),
(126, 2, 119, 'ہم نے آپ کو حق کے ساتھ خوشخبری دینے واﻻ اور ڈرانے واﻻ بنا کر بھیجا ہے اور جہنمیوں کے بارے میں آپ سے پرسش نہیں ہوگی'),
(127, 2, 120, 'آپ سے یہود ونصاریٰ ہرگز راضی نہیں ہوں گے جب تک کہ آپ ان کے مذہب کے تابع نہ بن جائیں، آپ کہہ دیجیئے کہ اللہ کی ہدایت ہی ہدایت ہے اور اگر آپ نے باوجود اپنے پاس علم آجانے کے، پھر ان کی خواہشوں کی پیروی کی تو اللہ کے پاس آپ کا نہ تو کوئی ولی ہوگا اورنہ مددگار'),
(128, 2, 121, 'جنہیں ہم نے کتاب دی ہے اور وه اسے پڑھنے کے حق کے ساتھ پڑھتے ہیں، وه اس کتاب پر بھی ایمان رکھتے ہیں اور جو اس کے ساتھ کفر کرے وه نقصان واﻻ ہے'),
(129, 2, 122, 'اے اوﻻد یعقوب! میں نے جو نعمتیں تم پر انعام کی ہیں انہیں یاد کرو اور میں نے تو تمہیں تمام جہانوں پر فضیلت دے رکھی تھی'),
(130, 2, 123, 'اس دن سے ڈرو جس دن کوئی نفس کسی نفس کو کچھ فائده نہ پہنچا سکے گا، نہ کسی شخص سے کوئی فدیہ قبول کیا جائے گا، نہ اسے کوئی شفاعت نفع دے گی، نہ ان کی مدد کی جائے گی'),
(131, 2, 124, 'جب ابراہیم ﴿علیہ السلام﴾ کو ان کے رب نے کئی کئی باتوں سے آزمایا اور انہوں نے سب کو پورا کردیا تو اللہ نے فرمایا کہ میں تمہیں لوگوں کا امام بنا دوں گا، عرض کرنے لگے: اور میری اوﻻد کو، فرمایا میرا وعده ﻇالموں سے نہیں'),
(132, 2, 125, 'ہم نے بیت اللہ کو لوگوں کے لئے ﺛواب اور امن وامان کی جگہ بنائی، تم مقام ابراہیم کو جائے نماز مقرر کرلو، ہم نے ابراہیم ﴿علیہ السلام﴾ اور اسماعیل ﴿علیہ السلام﴾ سے وعده لیا کہ تم میرے گھر کو طواف کرنے والوں اور اعتکاف کرنے والوں اور رکوع سجده کرنے والوں کے لئے پاک صاف رکھو'),
(133, 2, 126, 'جب ابراہیم نے کہا، اے پروردگار! تو اس جگہ کو امن واﻻ شہر بنا اور یہاں کے باشندوں کو جو اللہ تعالیٰ پر اور قیامت کے دن پر ایمان رکھنے والے ہوں، پھلوں کی روزیاں دے اللہ تعالیٰ نے فرمایا: میں کافروں کو بھی تھوڑا فائده دوں گا، پھر انہیں آگ کے عذاب کی طرف بےبس کردوں گا، یہ پہنچنے کی جگہ بری ہے'),
(134, 2, 127, 'ابراہیم ﴿علیہ السلام﴾ اور اسماعیل ﴿علیہ السلام﴾ کعبہ کی بنیادیں اور دیواریں اٹھاتے جاتے تھے اور کہتے جارہے تھے کہ ہمارے پروردگار! تو ہم سے قبول فرما، تو ہی سننے واﻻ اور جاننے واﻻ ہے'),
(135, 2, 128, 'اے ہمارے رب! ہمیں اپنا فرمانبردار بنا لے اور ہماری اوﻻد میں سے بھی ایک جماعت کو اپنی اطاعت گزار رکھ اور ہمیں اپنی عبادتیں سکھا اور ہماری توبہ قبول فرما، تو توبہ قبول فرمانے واﻻ اور رحم وکرم کرنے واﻻ ہے'),
(136, 2, 129, 'اے ہمارے رب! ان میں انہی میں سے رسول بھیج جو ان کے پاس تیری آیتیں پڑھے، انہیں کتاب وحکمت سکھائے اور انہیں پاک کرے، یقیناً تو غلبہ واﻻ اور حکمت واﻻ ہے'),
(137, 2, 130, 'دین ابراہیمی سے وہی بےرغبتی کرے گا جو محض بےوقوف ہو، ہم نے تو اسے دنیا میں بھی برگزیده کیا تھا اور آخرت میں بھی وه نیکوکاروں میں سے ہے'),
(138, 2, 131, 'جب کبھی بھی انہیں ان کے رب نے کہا، فرمانبردار ہوجا، انہوں نے کہا، میں نے رب العالمین کی فرمانبرداری کی'),
(139, 2, 132, 'اسی کی وصیت ابراہیم اور یعقوب نے اپنی اوﻻدکو کی، کہ ہمارے بچو! اللہ تعالیٰ نے تمہارے لئے اس دین کو پسند فرمالیا ہے، خبردار! تم مسلمان ہی مرنا'),
(140, 2, 133, 'کیا (حضرت) یعقوب کے انتقال کے وقت تم موجود تھے؟ جب انہوں نے اپنی اوﻻد کو کہا کہ میرے بعد تم کس کی عبادت کرو گے؟ تو سب نے جواب دیا کہ آپ کے معبود کی اور آپ کے آباواجداد ابرہیم ﴿علیہ السلام﴾ اور اسماعیل ﴿علیہ السلام﴾ اور اسحاق ﴿علیہ السلام﴾ کے معبود کی جو معبود ایک ہی ہے اور ہم اسی کے فرمانبردار رہیں گے'),
(141, 2, 134, 'یہ جماعت تو گزر چکی، جو انہوں نے کیا وه ان کے لئے ہے اور جو تم کرو گے تمہارے لئے ہے۔ ان کے اعمال کے بارے میں تم نہیں پوچھے جاؤ گے'),
(142, 2, 135, 'یہ کہتے ہیں کہ یہود و نصاریٰ بن جاؤ تو ہدایت پاؤ گے۔ تم کہو بلکہ صحیح راه پر ملت ابراہیمی والے ہیں، اور ابراہیم خالص اللہ کے پرستار تھے اور مشرک نہ تھے'),
(143, 2, 136, 'اے مسلمانو! تم سب کہو کہ ہم اللہ پر ایمان ﻻئے اور اس چیز پر بھی جو ہماری طرف اتاری گئی اور جو چیز ابراہیم اسماعیل اسحاق یعقوب (علیہم السلام) اور ان کی اوﻻد پر اتاری گئی اور جو کچھ اللہ کی جانب سے موسیٰ اور عیسیٰ (علیہما السلام) اور دوسرے انبیا (علیہم السلام) دیئے گئے۔ ہم ان میں سے کسی کے درمیان فرق نہیں کرتے، ہم اللہ کے فرمانبردار ہیں'),
(144, 2, 137, 'اگر وه تم جیسا ایمان ﻻئیں تو ہدایت پائیں، اور اگر منھ موڑیں تو وه صریح اختلاف میں ہیں، اللہ تعالی ان سے عنقریب آپ کی کفایت کرے گا اور وه خوب سننے اور جاننے واﻻ ہے'),
(145, 2, 138, 'اللہ کا رنگ اختیار کرو اور اللہ تعالیٰ سے اچھا رنگ کس کا ہوگا؟ ہم تو اسی کی عبادت کرنے والے ہیں'),
(146, 2, 139, 'آپ کہہ دیجیئے کیا تم ہم سے اللہ کے بارے میں جھگڑتے ہو جو ہمارا اور تمہارا رب ہے، ہمارے لئے ہمارے اعمال ہیں اور تمہارے لئے تمہارے اعمال، ہم تو اسی کے لئے مخلص ہیں'),
(147, 2, 140, 'کیا تم کہتے ہو کہ ابراہیم اور اسماعیل اور اسحاق اور یعقوب (علیہم السلام) اور ان کی اوﻻد یہودی یا نصرانی تھے؟ کہہ دو کہ کیا تم زیاده جانتے ہو، یا اللہ تعالیٰ؟ اللہ کے پاس شہادت چھپانے والے سے زیاده ﻇالم اور کون ہے؟ اور اللہ تمہارے کاموں سے غافل نہیں'),
(148, 2, 141, 'یہ امت ہے جو گزرچکی، جو انہوں نے کیا ان کے لئے ہے اور جو تم نے کیا تمہارے لئے، تم ان کےاعمال کے بارے میں سوال نہ کئے جاؤ گے'),
(149, 2, 142, 'عنقریب نادان لوگ کہیں گے کہ جس قبلہ پر یہ تھے اس سے انہیں کس چیز نے ہٹایا؟ آپ کہہ دیجیئے کہ مشرق ومغرب کا مالک اللہ تعالیٰ ہی ہے وه جسے چاہے سیدھی راه کی ہدایت کردے'),
(150, 2, 143, 'ہم نے اسی طرح تمہیں عادل امت بنایا ہے تاکہ تم لوگوں پر گواه ہوجاؤ اور رسول (صلی اللہ علیہ وسلم) تم پر گواه ہوجائیں، جس قبلہ پر تم پہلے سے تھے اسے ہم نے صرف اس لئے مقرر کیا تھا کہ ہم جان لیں کہ رسول کا سچا تابعدار کون ہے اور کون ہے جو اپنی ایڑیوں کے بل پلٹ جاتا ہے گو یہ کام مشکل ہے، مگر جنہیں اللہ نے ہدایت دی ہے (ان پر کوئی مشکل نہیں) اللہ تعالیٰ تمہارے ایمان ضائع نہ کرے گا اللہ تعالیٰ لوگوں کے ساتھ شفقت اور مہربانی کرنے واﻻ ہے'),
(151, 2, 144, 'ہم آپ کے چہرے کو بار بار آسمان کی طرف اٹھتے ہوئے دیکھ رہے ہیں، اب ہم آپ کو اس قبلہ کی جانب متوجہ کریں گے جس سے آپ خوش ہوجائیں، آپ اپنا منھ مسجد حرام کی طرف پھیر لیں اور آپ جہاں کہیں ہوں اپنا منھ اسی طرف پھیرا کریں۔ اہل کتاب کو اس بات کے اللہ کی طرف سے برحق ہونے کا قطعی علم ہے اور اللہ تعالیٰ ان اعمال سے غافل نہیں جو یہ کرتے ہیں'),
(152, 2, 145, 'اور آپ اگرچہ اہل کتاب کو تمام دلیلیں دے دیں لیکن وه آپ کے قبلے کی پیروی نہیں کریں گے اور نہ آپ ان کے قبلے کو ماننے والے ہیں اور نہ یہ آپس میں ایک دوسرے کے قبلے کو ماننے والے ہیں اور اگر آپ باوجودیکہ آپ کے پاس علم آچکا پھر بھی ان کی خواہشوں کے پیچھے لگ جائیں تو بالیقین آپ بھی ﻇالموں میں سے ہوجائیں گے'),
(153, 2, 146, 'جنہیں ہم نے کتاب دی ہے وه تو اسے ایسا پہچانتے ہیں جیسے کوئی اپنے بچوں کو پہچانے، ان کی ایک جماعت حق کو پہچان کر پھر چھپاتی ہے'),
(154, 2, 147, 'آپ کے رب کی طرف سے یہ سراسر حق ہے، خبردار آپ شک کرنے والوں میں سے نہ ہونا'),
(155, 2, 148, 'ہر شخص ایک نہ ایک طرف متوجہ ہو رہا ہے تم نیکیوں کی طرف دوڑو۔ جہاں کہیں بھی تم ہوگے، اللہ تمہیں لے آئے گا۔ اللہ تعالیٰ ہر چیز پر قادر ہے'),
(156, 2, 149, 'آپ جہاں سے نکلیں اپنا منھ (نماز کے لئے) مسجد حرام کی طرف کرلیا کریں، یہی حق ہے آپ کے رب کی طرف سے، جو کچھ تم کر رہے ہو اس سے اللہ تعالیٰ بےخبر نہیں'),
(157, 2, 150, 'اور جس جگہ سے آپ نکلیں اپنا منھ مسجد حرام کی طرف پھیر لیں اور جہاں کہیں تم ہو اپنے چہرے اسی طرف کیا کرو تاکہ لوگوں کی کوئی حجت تم پر باقی نہ ره جائے سوائے ان لوگوں کے جنہوں نے ان میں سے ﻇلم کیا ہے تم ان سے نہ ڈرو مجھ ہی سے ڈرو اور تاکہ میں اپنی نعمت تم پر پوری کروں اور اس لئے بھی کہ تم راه راست پاؤ'),
(158, 2, 151, 'جس طرح ہم نے تم میں تمہی میں سے رسول بھیجا جو ہماری آیتیں تمہارے سامنے تلاوت کرتا ہے اور تمہیں پاک کرتا ہے اور تمہیں کتاب وحکمت اور وه چیزیں سکھاتا ہے جن سے تم بےعلم تھے'),
(159, 2, 152, 'اس لئے تم میرا ذکر کرو، میں بھی تمہیں یاد کروں گا، میری شکر گزاری کرو اور ناشکری سے بچو'),
(160, 2, 153, 'اے ایمان والو! صبر اور نماز کے ذریعہ مدد چاہو، اللہ تعالی صبر والوں کا ساتھ دیتا ہے'),
(161, 2, 154, 'اور اللہ تعالیٰ کی راه کے شہیدوں کو مرده مت کہو وه زنده ہیں، لیکن تم نہیں سمجھتے'),
(162, 2, 155, 'اور ہم کسی نہ کسی طرح تمہاری آزمائش ضرور کریں گے، دشمن کے ڈر سے، بھوک پیاس سے، مال وجان اور پھلوں کی کمی سے اور ان صبر کرنے والوں کو خوشخبری دے دیجیئے'),
(163, 2, 156, 'جنہیں، جب کبھی کوئی مصیبت آتی ہے تو کہہ دیا کرتے ہیں کہ ہم تو خود اللہ تعالیٰ کی ملکیت ہیں اور ہم اسی کی طرف لوٹنے والے ہیں'),
(164, 2, 157, 'ان پر ان کے رب کی نوازشیں اور رحمتیں ہیں اور یہی لوگ ہدایت یافتہ ہیں'),
(165, 2, 158, 'صفااور مروه اللہ تعالیٰ کی نشانیوں میں سے ہیں، اس لئے بیت اللہ کا حج وعمره کرنے والے پر ان کا طواف کرلینے میں بھی کوئی گناه نہیں اپنی خوشی سے بھلائی کرنے والوں کا اللہ قدردان ہے اور انہیں خوب جاننے واﻻ ہے'),
(166, 2, 159, 'جو لوگ ہماری اتاری ہوئی دلیلوں اور ہدایت کو چھپاتے ہیں باوجودیکہ ہم اسے اپنی کتاب میں لوگوں کے لئے بیان کرچکے ہیں، ان لوگوں پر اللہ کی اور تمام لعنت کرنے والوں کی لعنت ہے'),
(167, 2, 160, 'مگر وه لوگ جو توبہ کرلیں اور اصلاح کرلیں اور بیان کردیں، تو میں ان کی توبہ قبول کرلیتا ہوں اور میں توبہ قبول کرنے واﻻ اور رحم وکرم کرنے واﻻ ہوں'),
(168, 2, 161, 'یقیناً جو کفار اپنے کفر میں ہی مرجائیں، ان پر اللہ تعالیٰ کی، فرشتوں کی اور تمام لوگوں کی لعنت ہے'),
(169, 2, 162, 'جس میں یہ ہمیشہ رہیں گے، نہ ان سے عذاب ہلکا کیا جائے گا اور نہ انہیں ڈھیل دی جائے گی'),
(170, 2, 163, 'تم سب کا معبود ایک ہی معبود ہے، اس کے سوا کوئی معبود برحق نہیں وه بہت رحم کرنے واﻻ اور بڑا مہربان ہے'),
(171, 2, 164, 'آسمانوں اور زمین کی پیدائش، رات دن کا ہیر پھیر، کشتیوں کا لوگوں کو نفع دینے والی چیزوں کو لئے ہوئے سمندروں میں چلنا، آسمان سے پانی اتار کر، مرده زمین کو زنده کردینا، اس میں ہر قسم کے جانوروں کو پھیلا دینا، ہواؤں کے رخ بدلنا، اور بادل، جو آسمان اور زمین کے درمیان مسخر ہیں، ان میں عقلمندوں کے لئے قدرت الٰہی کی نشانیاں ہیں'),
(172, 2, 165, 'بعض لوگ ایسے بھی ہیں جو اللہ کے شریک اوروں کو ٹھہرا کر ان سے ایسی محبت رکھتے ہیں، جیسی محبت اللہ سے ہونی چاہیئے اور ایمان والے اللہ کی محبت میں بہت سخت ہوتے ہیں کاش کہ مشرک لوگ جانتے جب کہ اللہ کے عذاب کو دیکھ کر (جان لیں گے) کہ تمام طاقت اللہ ہی کو ہے اور اللہ تعالیٰ سخت عذاب دینے واﻻ ہے (تو ہرگز شرک نہ کرتے)'),
(173, 2, 166, 'جس وقت پیشوا لوگ اپنے تابعداروں سے بیزار ہوجائیں گے اور عذاب کو اپنی آنکھوں سے دیکھ لیں گے اور کل رشتے ناتے ٹوٹ جائیں گے'),
(174, 2, 167, 'اور تابعدار لوگ کہنے لگیں گے، کاش ہم دنیا کی طرف دوباره جائیں تو ہم بھی ان سے ایسے ہی بیزار ہوجائیں جیسے یہ ہم سے ہیں، اسی طرح اللہ تعالیٰ انہیں ان کے اعمال دکھائے گا ان کو حسرت دﻻنے کو، یہ ہرگز جہنم سے نہ نکلیں گے'),
(175, 2, 168, 'لوگو! زمین میں جتنی بھی حلال اور پاکیزه چیزیں ہیں انہیں کھاؤ پیو اور شیطانی راه پر نہ چلو، وه تمہارا کھلا ہوا دشمن ہے'),
(176, 2, 169, 'وه تمہیں صرف برائی اور بےحیائی کا اور اللہ تعالیٰ پر ان باتوں کے کہنے کا حکم دیتا ہے جن کا تمہیں علم نہیں'),
(177, 2, 170, 'اور ان سے جب کبھی کہا جاتا ہے کہ اللہ تعالیٰ کی اتاری ہوئی کتاب کی تابعداری کرو تو جواب دیتے ہیں کہ ہم تو اس طریقے کی پیروی کریں گے جس پر ہم نے اپنے باپ دادوں کو پایا، گو ان کے باپ دادے بےعقل اور گم کرده راه ہوں'),
(178, 2, 171, 'کفار کی مثال ان جانوروں کی طرح ہے جو اپنے چرواہے کی صرف پکار اور آواز ہی کو سنتے ہیں (سمجھتے نہیں) وه بہرے، گونگے اور ا ندھے ہیں، انہیں عقل نہیں'),
(179, 2, 172, 'اے ایمان والو! جو پاکیزه چیزیں ہم نے تمہیں دے رکھی ہیں انہیں کھاؤ، پیو اور اللہ تعالیٰ کا شکر کرو، اگر تم خاص اسی کی عبادت کرتے ہو'),
(180, 2, 173, 'تم پر مرده اور (بہا ہوا) خون اور سور کا گوشت اور ہر وه چیز جس پر اللہ کے سوا دوسروں کا نام پکارا گیا ہو حرام ہے پھر جو مجبور ہوجائے اور وه حد سے بڑھنے واﻻ اور زیادتی کرنے واﻻ نہ ہو، اس پر ان کے کھانے میں کوئی گناه نہیں، اللہ تعالیٰ بخشش کرنے واﻻ مہربان ہے'),
(181, 2, 174, 'بے شک جو لوگ اللہ تعالیٰ کی اتاری ہوئی کتاب چھپاتے ہیں اور اسے تھوڑی تھوڑی سی قیمت پر بیچتے ہیں، یقین مانو کہ یہ اپنے پیٹ میں آگ بھر رہے ہیں، قیامت کے دن اللہ تعالیٰ ان سے بات بھی نہ کرے گا، نہ انہیں پاک کرے گا، بلکہ ان کے لئے دردناک عذاب ہے'),
(182, 2, 175, 'یہ وه لوگ ہیں جنہوں نے گمراہی کو ہدایت کے بدلے اور عذاب کو مغفرت کے بدلے خرید لیا ہے، یہ لوگ آگ کا عذاب کتنا برداشت کرنے والے ہیں'),
(183, 2, 176, 'ان عذابوں کا باعﺚ یہی ہے کہ اللہ تعالیٰ نے سچی کتاب اتاری اور اس کتاب میں اختلاف کرنے والے یقیناً دور کے خلاف میں ہیں'),
(184, 2, 177, 'ساری اچھائی مشرق ومغرب کی طرف منھ کرنے میں ہی نہیں بلکہ حقیقتاً اچھا وه شخص ہے جو اللہ تعالی پر، قیامت کے دن پر، فرشتوں پر، کتاب اللہ پر اور نبیوں پر ایمان رکھنے واﻻ ہو، جو مال سے محبت کرنے کے باوجود قرابت داروں، یتیموں، مسکینوں، مسافروں اور سوال کرنے والے کو دے، غلاموں کو آزاد کرے، نماز کی پابندی اور زکوٰة کی ادائیگی کرے، جب وعده کرے تب اسے پورا کرے، تنگدستی، دکھ درد اور لڑائی کے وقت صبر کرے، یہی سچے لوگ ہیں اور یہی پرہیزگار ہیں'),
(185, 2, 178, 'اے ایمان والو! تم پر مقتولوں کا قصاص لینا فرض کیا گیا ہے، آزاد آزاد کے بدلے، غلام غلام کے بدلے، عورت عورت کے بدلے۔ ہاں جس کسی کو اس کے بھائی کی طرف سے کچھ معافی دے دی جائے اسے بھلائی کی اتباع کرنی چاہئے اور آسانی کے ساتھ دیت ادا کرنی چاہئے۔ تمہارے رب کی طرف سے یہ تخفیف اور رحمت ہے اس کے بعد بھی جو سرکشی کرے اسے درد ناک عذاب ہوگا'),
(186, 2, 179, 'عقلمندو! قصاص میں تمہارے لئے زندگی ہے اس باعﺚ تم (قتل ناحق سے) رکو گے'),
(187, 2, 180, 'تم پر فرض کر دیا گیا ہے کہ جب تم میں سے کوئی مرنے لگے اور مال چھوڑ جاتا ہو تو اپنے ماں باپ اور قرابت داروں کے لئے اچھائی کے ساتھ وصیت کرجائے، پرہیزگاروں پر یہ حق اور ﺛابت ہے'),
(188, 2, 181, 'اب جو شخص اسے سننے کے بعد بدل دے اس کا گناه بدلنے والے پر ہی ہوگا، واقعی اللہ تعالیٰ سننے واﻻ جاننے واﻻ ہے'),
(189, 2, 182, 'ہاں جو شخص وصیت کرنے والے کی جانب داری یا گناه کی وصیت کردینے سے ڈرے پس وه ان میں آپس میں اصلاح کرادے تو اس پر گناه نہیں، اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(190, 2, 183, 'اے ایمان والو! تم پر روزے رکھنا فرض کیا گیا جس طرح تم سے پہلے لوگوں پر فرض کئے گئے تھے، تاکہ تم تقویٰ اختیار کرو'),
(191, 2, 184, 'گنتی کے چند ہی دن ہیں لیکن تم میں سے جو شخص بیمار ہو یا سفر میں ہو تو وه اور دنوں میں گنتی کو پورا کر لے اور اس کی طاقت رکھنے والے فدیہ میں ایک مسکین کو کھانا دیں، پھر جو شخص نیکی میں سبقت کرے وه اسی کے لئے بہتر ہے لیکن تمہارے حق میں بہتر کام روزے رکھنا ہی ہے اگر تم باعلم ہو'),
(192, 2, 185, 'ماه رمضان وه ہے جس میں قرآن اتارا گیا جو لوگوں کو ہدایت کرنے واﻻ ہے اور جس میں ہدایت کی اور حق وباطل کی تمیز کی نشانیاں ہیں، تم میں سے جو شخص اس مہینہ کو پائے اسے روزه رکھنا چاہئے، ہاں جو بیمار ہو یا مسافر ہو اسے دوسرے دنوں میں یہ گنتی پوری کرنی چاہئے، اللہ تعالیٰ کا اراده تمہارے ساتھ آسانی کا ہے، سختی کا نہیں، وه چاہتا ہے کہ تم گنتی پوری کرلو اور اللہ تعالیٰ کی دی ہوئی ہدایت پر اس کی بڑائیاں بیان کرو اور اس کا شکر کرو'),
(193, 2, 186, 'جب میرے بندے میرے بارے میں آپ سے سوال کریں تو آپ کہہ دیں کہ میں بہت ہی قریب ہوں ہر پکارنے والے کی پکار کو جب کبھی وه مجھے پکارے، قبول کرتا ہوں اس لئے لوگوں کو بھی چاہئے کہ وه میری بات مان لیا کریں اور مجھ پر ایمان رکھیں، یہی ان کی بھلائی کا باعﺚ ہے'),
(194, 2, 187, 'روزے کی راتوں میں اپنی بیویوں سے ملنا تمہارے لئے حلال کیا گیا، وه تمہارا لباس ہیں اور تم ان کے لباس ہو، تمہاری پوشیده خیانتوں کا اللہ تعالیٰ کو علم ہے، اس نے تمہاری توبہ قبول فرما کر تم سے درگزر فرمالیا، اب تمہیں ان سے مباشرت کی اور اللہ تعالیٰ کی لکھی ہوئی چیز کو تلاش کرنے کی اجازت ہے، تم کھاتے پیتے رہو یہاں تک کہ صبح کا سفید دھاگہ سیاه دھاگے سے ﻇاہر ہوجائے۔ پھر رات تک روزے کو پورا کرو اور عورتوں سے اس وقت مباشرت نہ کرو جب کہ تم مسجدوں میں اعتکاف میں ہو۔ یہ اللہ تعالیٰ کی حدود ہیں، تم ان کے قریب بھی نہ جاؤ۔ اسی طرح اللہ تعالیٰ اپنی آیتیں لوگوں کے لئے بیان فرماتا ہے تاکہ وه بچیں'),
(195, 2, 188, 'اور ایک دوسرے کا مال ناحق نہ کھایا کرو، نہ حاکموں کو رشوت پہنچا کر کسی کا کچھ مال ﻇلم وستم سے اپنا کر لیا کرو، حاﻻنکہ تم جانتے ہو'),
(196, 2, 189, 'لوگ آپ سے چاند کے بارے میں سوال کرتے ہیں آپ کہہ دیجیئے کہ یہ لوگوں (کی عبادت) کے وقتوں اور حج کے موسم کے لئے ہے (احرام کی حالت میں) اور گھروں کے پیچھے سے تمہارا آنا کچھ نیکی نہیں، بلکہ نیکی واﻻ وه ہے جو متقی ہو۔ اور گھروں میں تو دروازوں میں سے آیا کرو اور اللہ سے ڈرتے رہو، تاکہ تم کامیاب ہوجاؤ'),
(197, 2, 190, 'لڑو اللہ کی راه میں ان سے جو تم سے لڑتے ہیں اور زیادتی نہ کرو، اللہ تعالیٰ زیادتی کرنے والوں کو پسند نہیں فرماتا'),
(198, 2, 191, 'انہیں مارو جہاں بھی پاؤ اور انہیں نکالو جہاں سے انہوں نے تمہیں نکاﻻ ہے اور (سنو) فتنہ قتل سے زیاده سخت ہے اور مسجد حرام کے پاس ان سے لڑائی نہ کرو جب تک کہ یہ خود تم سے نہ لڑیں، اگر یہ تم سے لڑیں تو تم بھی انہیں مارو کافروں کا بدلہ یہی ہے'),
(199, 2, 192, 'اگر یہ باز آجائیں تو اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(200, 2, 193, 'ان سے لڑو جب تک کہ فتنہ نہ مٹ جائے اور اللہ تعالیٰ کا دین غالب نہ آجائے، اگر یہ رک جائیں (تو تم بھی رک جاؤ) زیادتی تو صرف ﻇالموں پر ہی ہے'),
(201, 2, 194, 'حرمت والے مہینے حرمت والے مہینوں کے بدلے ہیں اور حرمتیں ادلے بدلے کی ہیں جو تم پر زیادتی کرے تم بھی اس پر اسی کے مثل زیادتی کرو جو تم پر کی ہے اور اللہ تعالیٰ سے ڈرتے رہا کرو اور جان رکھو کہ اللہ تعالیٰ پرہیزگاروں کے ساتھ ہے'),
(202, 2, 195, 'اللہ تعالیٰ کی راه میں خرچ کرو اور اپنے ہاتھوں ہلاکت میں نہ پڑو اور سلوک واحسان کرو، اللہ تعالیٰ احسان کرنے والوں کو دوست رکھتا ہے'),
(203, 2, 196, 'حج اور عمرے کو اللہ تعالیٰ کے لئے پورا کرو، ہاں اگر تم روک لئے جاؤ تو جو قربانی میسر ہو، اسے کرڈالو اور اپنے سر نہ منڈواؤ جب تک کہ قربانی قربان گاه تک نہ پہنچ جائے البتہ تم میں سے جو بیمار ہو، یا اس کے سر میں کوئی تکلیف ہو (جس کی وجہ سے سر منڈالے) تو اس پر فدیہ ہے، خواه روزے رکھ لے، خواه صدقہ دے دے، خواه قربانی کرے پس جب تم امن کی حالت میں ہوجاؤ تو جو شخص عمرے سے لے کر حج تک تمتع کرے، پس اسے جو قربانی میسر ہو اسے کر ڈالے، جسے طاقت ہی نہ ہو وه تین روزے تو حج کے دنوں میں رکھ لے اور سات واپسی میں، یہ پورے دس ہوگئے۔ یہ حکم ان کے لئے ہے جو مسجد حرام کے رہنے والے نہ ہوں، لوگو! اللہ سے ڈرتے رہو اور جان لو کہ اللہ تعالیٰ سخت عذاب واﻻ ہے'),
(204, 2, 197, 'حج کے مہینے مقرر ہیں اس لئے جو شخص ان میں حج ﻻزم کرلے وه اپنی بیوی سے میل ملاپ کرنے، گناه کرنے اور لڑائی جھگڑے کرنے سے بچتا رہے، تم جو نیکی کرو گے اس سے اللہ تعالیٰ باخبر ہے اور اپنے ساتھ سفر خرچ لے لیا کرو، سب سے بہتر توشہ اللہ تعالیٰ کا ڈر ہے اور اے عقلمندو! مجھ سے ڈرتے رہا کرو'),
(205, 2, 198, 'تم پر اپنے رب کا فضل تلاش کرنے میں کوئی گناه نہیں جب تم عرفات سے لوٹو تو مشعر حرام کے پاس ذکر الٰہی کرو اور اس کا ذکر کرو جیسے کہ اس نے تمہیں ہدایت دی، حاﻻنکہ تم اس سے پہلے راه بھولے ہوئے تھے'),
(206, 2, 199, 'پھر تم اس جگہ سے لوٹو جس جگہ سے سب لوگ لوٹتے ہیں اور اللہ تعالیٰ سے طلب بخشش کرتے رہو یقیناً اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(207, 2, 200, 'پھر جب تم ارکان حج ادا کر چکو تو اللہ تعالیٰ کا ذکر کرو جس طرح تم اپنے باپ دادوں کا ذکر کیا کرتے تھے، بلکہ اس سے بھی زیاده بعض لوگ وه بھی ہیں جو کہتے ہیں اے ہمارے رب! ہمیں دنیا میں دے۔ ایسے لوگوں کا آخرت میں کوئی حصہ نہیں'),
(208, 2, 201, 'اور بعض لوگ وه بھی ہیں جو کہتے ہیں اے ہمارے رب! ہمیں دنیا میں نیکی دے اور آخرت میں بھی بھلائی عطا فرما اور ہمیں عذاب جہنم سے نجات دے'),
(209, 2, 202, 'یہ وه لوگ ہیں جن کے لئے ان کے اعمال کا حصہ ہے اور اللہ تعالیٰ جلد حساب لینے واﻻ ہے'),
(210, 2, 203, 'اور اللہ تعالیٰ کی یاد ان گنتی کے چند دنوں (ایام تشریق) میں کرو، دو دن کی جلدی کرنے والے پر بھی کوئی گناه نہیں، اور جو پیچھے ره جائے اس پر بھی کوئی گناه نہیں، یہ پرہیزگارکے لئے ہے اور اللہ تعالیٰ سے ڈرتے رہو اور جان رکھو کہ تم سب اسی کی طرف جمع کئے جاؤ گے'),
(211, 2, 204, 'بعض لوگوں کی دنیاوی غرض کی باتیں آپ کو خوش کر دیتی ہیں اور وه اپنے دل کی باتوں پر اللہ کو گواه کرتا ہے، حاﻻنکہ دراصل وه زبردست جھگڑالو ہے'),
(212, 2, 205, 'جب وه لوٹ کر جاتا ہے تو زمین میں فساد پھیلانے کی اور کھیتی اور نسل کی بربادی کی کوشش میں لگا رہتا ہے اور اللہ تعالیٰ فساد کو ناپسند کرتا ہے'),
(213, 2, 206, 'اور جب اس سے کہا جائے کہ اللہ سے ڈر تو تکبر اور تعصب اسے گناه پر آماده کر دیتا ہے، ایسے کے لئے بس جہنم ہی ہے اور یقیناً وه بد ترین جگہ ہے'),
(214, 2, 207, 'اور بعض لوگ وه بھی ہیں کہ اللہ تعالیٰ کی رضامندی کی طلب میں اپنی جان تک بیچ ڈالتے ہیں اور اللہ تعالیٰ اپنے بندوں پر بڑی مہربانی کرنے واﻻ ہے'),
(215, 2, 208, 'ایمان والو! اسلام میں پورے پورے داخل ہوجاؤ اور شیطان کے قدموں کی تابعداری نہ کرو وه تمہارا کھلا دشمن ہے'),
(216, 2, 209, 'اگر تم باوجود تمہارے پاس دلیلیں آجانے کے بھی پھسل جاؤ تو جان لو کہ اللہ تعالیٰ غلبہ واﻻ اور حکمت واﻻ ہے'),
(217, 2, 210, 'کیا لوگوں کو اس بات کا انتظار ہے کہ ان کے پاس خود اللہ تعالیٰ ابر کے سائبانوں میں آجائے اور فرشتے بھی اور کام انتہا تک پہنچا دیا جائے، اللہ ہی کی طرف تمام کام لوٹائے جاتے ہیں'),
(218, 2, 211, 'بنی اسرائیل سے پوچھو تو کہ ہم نے انہیں کس قدر روشن نشانیاں عطا فرمائیں اور جو شخص اللہ تعالیٰ کی نعمتوں کو اپنے پاس پہنچ جانے کے بعد بدل ڈالے (وه جان لے) کہ اللہ تعالیٰ بھی سخت عذابوں واﻻ ہے'),
(219, 2, 212, 'کافروں کے لئے دنیا کی زندگی خوب زینت دار کی گئی ہے، وه ایمان والوں سے ہنسی مذاق کرتے ہیں، حاﻻنکہ پرہیزگار لوگ قیامت کے دن ان سے اعلیٰ ہوں گے، اللہ تعالیٰ جسے چاہتا ہے بےحساب روزی دیتا ہے'),
(220, 2, 213, 'دراصل لوگ ایک ہی گروه تھے اللہ تعالیٰ نے نبیوں کو خوشخبریاں دینے اور ڈرانے واﻻ بنا کر بھیجا اور ان کے ساتھ سچی کتابیں نازل فرمائیں، تاکہ لوگوں کے ہر اختلافی امر کا فیصلہ ہوجائے۔ اور صرف ان ہی لوگوں نے جنھیں کتاب دی گئی تھی، اپنے پاس دﻻئل آچکنے کے بعد آپس کے بغض وعناد کی وجہ سے اس میں اختلاف کیا اس لئے اللہ پاک نے ایمان والوں کی اس اختلاف میں بھی حق کی طرف اپنی مشیئت سے رہبری کی اور اللہ جس کو چاہے سیدھی راه کی طرف رہبری کرتا ہے'),
(221, 2, 214, 'کیا تم یہ گمان کئے بیٹھے ہو کہ جنت میں چلے جاؤ گے، حاﻻنکہ اب تک تم پر وه حاﻻت نہیں آئے جو تم سے اگلے لوگوں پر آئے تھے۔ انہیں بیماریاں اور مصیبتیں پہنچیں اور وہ یہاں تک جھنجھوڑے گئے کہ رسول اور اس کے ساتھ کے ایمان والے کہنے لگے کہ اللہ کی مدد کب آئے گی؟ سن رکھو کہ اللہ کی مدد قریب ہی ہے'),
(222, 2, 215, 'آپ سے پوچھتے ہیں کہ وه کیا خرچ کریں؟ آپ کہہ دیجیئے جو مال تم خرچ کرو وه ماں باپ کے لئے ہے اور رشتہداروں اور یتیموں اور مسکینوں اور مسافروں کے لئے ہے اور تم جو کچھ بھلائی کرو گے اللہ تعالیٰ کو اس کا علم ہے'),
(223, 2, 216, 'تم پر جہاد فرض کیا گیا گو وه تمہیں دشوار معلوم ہو، ممکن ہے کہ تم کسی چیز کو بری جانو اور دراصل وہی تمہارے لئے بھلی ہو اور یہ بھی ممکن ہے کہ تم کسی چیز کو اچھی سمجھو، حاﻻنکہ وه تمہارے لئے بری ہو، حقیقی علم اللہ ہی کو ہے، تم محض بےخبر ہو'),
(224, 2, 217, 'لوگ آپ سے حرمت والے مہینوں میں لڑائی کی بابت سوال کرتے ہیں، آپ کہہ دیجیئے کہ ان میں لڑائی کرنا بڑا گناه ہے، لیکن اللہ کی راه سے روکنا، اس کے ساتھ کفر کرنا اور مسجد حرام سے روکنا اور وہاں کے رہنے والوں کو وہاں سے نکالنا، اللہ کے نزدیک اس سے بھی بڑا گناه ہے یہ فتنہ قتل سے بھی بڑا گناه ہے، یہ لوگ تم سے لڑائی بھڑائی کرتے ہی رہیں گے یہاں تک کہ اگر ان سے ہوسکے تو تمہیں تمہارے دین سے مرتد کردیں اور تم میں سے جو لوگ اپنے دین سے پلٹ جائیں اور اسی کفر کی حالت میں مریں، ان کے اعمال دنیوی اور اخروی سب غارت ہوجائیں گے۔ یہ لوگ جہنمی ہوں گے اور ہمیشہ ہمیشہ جہنم میں ہی رہیں گے'),
(225, 2, 218, 'البتہ ایمان ﻻنے والے، ہجرت کرنے والے، اللہ کی راه میں جہاد کرنے والے ہی رحمت الٰہی کے امیدوار ہیں، اللہ تعالیٰ بہت بخشنے واﻻ اور بہت مہربانی کرنے واﻻ ہے'),
(226, 2, 219, 'لوگ آپ سے شراب اور جوئے کا مسئلہ پوچھتے ہیں، آپ کہہ دیجیئے ان دونوں میں بہت بڑا گناه ہے اور لوگوں کو اس سے دنیاوی فائده بھی ہوتا ہے، لیکن ان کا گناه ان کے نفع سے بہت زیاده ہے۔ آپ سے یہ بھی دریافت کرتے ہیں کہ کیا کچھ خرچ کریں؟ تو آپ کہہ دیجیئے حاجت سے زائد چیز، اللہ تعالیٰ اسی طرح اپنے احکام صاف صاف تمہارے لئے بیان فرما رہا ہے، تاکہ تم سوچ سمجھ سکو،'),
(227, 2, 220, 'دنیا اور آخرت کے امور کو۔ اور تجھ سے یتیموں کے بارے میں بھی سوال کرتے ہیں آپ کہہ دیجیئے کہ ان کی خیرخواہی بہتر ہے، تم اگر ان کا مال اپنے مال میں ملا بھی لو تو وه تمہارے بھائی ہیں، بدنیت اور نیک نیت ہر ایک کو اللہ خوب جانتا ہے اور اگر اللہ چاہتا تو تمہیں مشقت میں ڈال دیتا، یقیناً اللہ تعالیٰ غلبہ واﻻ اور حکمت واﻻ ہے'),
(228, 2, 221, 'اور شرک کرنے والی عورتوں سے تاوقتیکہ وه ایمان نہ ﻻئیں تم نکاح نہ کرو، ایمان والی لونڈی بھی شرک کرنے والی آزاد عورت سے بہت بہتر ہے، گو تمہیں مشرکہ ہی اچھی لگتی ہو اور نہ شرک کرنے والے مردوں کے نکاح میں اپنی عورتوں کو دو جب تک کہ وه ایمان نہ ﻻئیں، ایمان والا غلام آزاد مشرک سے بہتر ہے، گو مشرک تمہیں اچھا لگے۔ یہ لوگ جہنم کی طرف بلاتے ہیں اور اللہ جنت کی طرف اور اپنی بخشش کی طرف اپنے حکم سے بلاتا ہے، وه اپنی آیتیں لوگوں کے لئے بیان فرما رہا ہے، تاکہ وه نصیحت حاصل کریں'),
(229, 2, 222, 'آپ سے حیض کے بارے میں سوال کرتے ہیں، کہہ دیجیئے کہ وه گندگی ہے، حالت حیض میں عورتوں سے الگ رہو اور جب تک وه پاک نہ ہوجائیں ان کے قریب نہ جاؤ، ہاں جب وه پاک ہوجائیں تو ان کے پاس جاؤ جہاں سے اللہ نے تمہیں اجازت دی ہے، اللہ توبہ کرنے والوں کو اور پاک رہنے والوں کو پسند فرماتا ہے'),
(230, 2, 223, 'تمہاری بیویاں تمہاری کھیتیاں ہیں، اپنی کھیتیوں میں جس طرح چاہو آؤ اور اپنے لئے (نیک اعمال) آگے بھیجو اور اللہ تعالیٰ سے ڈرتے رہا کرو اور جان رکھو کہ تم اس سے ملنے والے ہو اور ایمان والوں کو خوش خبری سنا دیجیئے'),
(231, 2, 224, 'اور اللہ تعالیٰ کو اپنی قسموں کا (اس طرح) نشانہ نہ بناؤ کہ بھلائی اور پرہیزگاری اور لوگوں کے درمیان کی اصلاح کو چھوڑ بیٹھو اور اللہ تعالیٰ سننے واﻻ جاننے واﻻ ہے'),
(232, 2, 225, 'اللہ تعالیٰ تمہیں تمہاری ان قسموں پر نہ پکڑے گا جو پختہ نہ ہوں ہاں اس کی پکڑ اس چیز پر ہے جو تمہارے دلوں کا فعل ہو، اللہ تعالیٰ بخشنے واﻻ اور بردبار ہے'),
(233, 2, 226, 'جو لوگ اپنی بیویوں سے (تعلق نہ رکھنے کی) قسمیں کھائیں، ان کے لئے چار مہینے کی مدت ہے، پھر اگر وه لوٹ آئیں تو اللہ تعالیٰ بھی بخشنے واﻻ مہربان ہے'),
(234, 2, 227, 'اور اگر طلاق کا ہی قصد کرلیں تو اللہ تعالیٰ سننے واﻻ، جاننے واﻻ ہے'),
(235, 2, 228, 'طلاق والی عورتیں اپنے آپ کو تین حیض تک روکے رکھیں، انہیں حلال نہیں کہ اللہ نے ان کے رحم میں جو پیدا کیا ہو اسے چھپائیں، اگر انہیں اللہ تعالیٰ پر اور قیامت کے دن پر ایمان ہو، ان کے خاوند اس مدت میں انہیں لوٹا لینے کے پورے حقدار ہیں اگر ان کا اراده اصلاح کا ہو۔ اور عورتوں کے بھی ویسے ہی حق ہیں جیسے ان پر مردوں کے ہیں اچھائی کے ساتھ۔ ہاں مردوں کو عورتوں پر فضلیت ہے اور اللہ تعالیٰ غالب ہے حکمت واﻻ ہے'),
(236, 2, 229, 'یہ طلاقیں دو مرتبہ ہیں، پھر یا تو اچھائی سے روکنا یا عمدگی کے ساتھ چھوڑ دینا ہے اور تمہیں حلال نہیں کہ تم نے انہیں جو دے دیا ہے اس میں سے کچھ بھی لو، ہاں یہ اور بات ہے کہ دونوں کو اللہ کی حدیں قائم نہ رکھ سکنے کا خوف ہو، اس لئے اگر تمہیں ڈر ہو کہ یہ دونوں اللہ کی حدیں قائم نہ رکھ سکیں گے تو عورت رہائی پانے کے لئے کچھ دے ڈالے، اس میں دونوں پر گناه نہیں یہ اللہ کی حدود ہیں خبردار ان سے آگے نہ بڑھنا اور جو لوگ اللہ کی حدوں سے تجاوز کرجائیں وه ﻇالم ہیں'),
(237, 2, 230, 'پھر اگر اس کو (تیسری بار) طلاق دے دے تو اب اس کے لئے حلال نہیں جب تک وه عورت اس کے سوا دوسرے سے نکاح نہ کرے، پھر اگر وه بھی طلاق دے دے تو ان دونوں کو میل جول کرلینے میں کوئی گناه نہیں بشرطیکہ یہ جان لیں کہ اللہ کی حدوں کو قائم رکھ سکیں گے، یہ اللہ تعالیٰ کی حدود ہیں جنہیں وه جاننے والوں کے لئے بیان فرما رہا ہے۔'),
(238, 2, 231, 'جب تم عورتوں کو طلاق دو اور وه اپنی عدت ختم کرنے پر آئیں تو اب انہیں اچھی طرح بساؤ، یا بھلائی کے ساتھ الگ کردو اور انہیں تکلیف پہنچانے کی غرض سے ﻇلم وزیادتی کے لئے نہ روکو، جو شخص ایسا کرے اس نے اپنی جان پر ﻇلم کیا۔ تم اللہ کے احکام کو ہنسی کھیل نہ بناؤ اور اللہ کا احسان جو تم پر ہے یاد کرو اور جو کچھ کتاب وحکمت اس نے نازل فرمائی ہے جس سے تمہیں نصیحت کر رہا ہے، اسے بھی۔ اور اللہ تعالیٰ سے ڈرتے رہا کرو اور جان رکھو کہ اللہ تعالیٰ ہر چیز کو جانتا ہے'),
(239, 2, 232, 'اور جب تم اپنی عورتوں کو طلاق دو اور وه اپنی عدت پوری کرلیں تو انہیں ان کے خاوندوں سے نکاح کرنے سے نہ روکو جب کہ وه آپس میں دستور کے مطابق رضامند ہوں۔ یہ نصیحت انہیں کی جاتی ہے جنہیں تم میں سے اللہ تعالیٰ پر اور قیامت کے دن پر یقین وایمان ہو، اس میں تمہاری بہترین صفائی اور پاکیزگی ہے۔ اللہ تعالیٰ جانتا ہے اور تم نہیں جانتے'),
(240, 2, 233, 'مائیں اپنی اوﻻد کو دو سال کامل دودھ پلائیں جن کا اراده دودھ پلانے کی مدت بالکل پوری کرنے کا ہو اور جن کے بچے ہیں ان کے ذمہ ان کا روٹی کپڑا ہے جو مطابق دستور کے ہو۔ ہر شخص اتنی ہی تکلیف دیا جاتا ہے جتنی اس کی طاقت ہو۔ ماں کو اس کے بچہ کی وجہ سے یا باپ کو اس کی اوﻻدکی وجہ سے کوئی ضرر نہ پہنچایا جائے۔ وارث پر بھی اسی جیسی ذمہ داری ہے، پھر اگر دونوں (یعنی ماں باپ) اپنی رضامندی اور باہمی مشورے سے دودھ چھڑانا چاہیں تو دونوں پر کچھ گناه نہیں اور اگر تمہارا اراده اپنی اوﻻد کو دودھ پلوانے کا ہو تو بھی تم پر کوئی گناه نہیں جب کہ تم ان کو مطابق دستور کے جو دینا ہو وه ان کے حوالے کردو، اللہ تعالیٰ سے ڈرتے رہو اور جانتے رہو کہ اللہ تعالیٰ تمہارے اعمال کی دیکھ بھال کر رہا ہے'),
(241, 2, 234, 'تم میں سے جو لوگ فوت ہوجائیں اور بیویاں چھوڑ جائیں، وه عورتیں اپنے آپ کو چار مہینے اور دس (دن) عدت میں رکھیں، پھر جب مدت ختم کرلیں تو جو اچھائی کے ساتھ وه اپنے لئے کریں اس میں تم پر کوئی گناه نہیں اور اللہ تعالیٰ تمہارے ہر عمل سے خبردار ہے'),
(242, 2, 235, 'تم پر اس میں کوئی گناه نہیں کہ تم اشارةً کنایتہً ان عورتوں سے نکاح کی بابت کہو، یا اپنے دل میں پوشیده اراده کرو، اللہ تعالیٰ کو علم ہے کہ تم ضرور ان کو یاد کرو گے، لیکن تم ان سے پوشیده وعدے نہ کرلو ہاں یہ اور بات ہے کہ تم بھلی بات بوﻻ کرو اور عقد نکاح جب تک کہ عدت ختم نہ ہوجائے پختہ نہ کرو، جان رکھو کہ اللہ تعالیٰ کو تمہارے دلوں کی باتوں کا بھی علم ہے، تم اس سے خوف کھاتے رہا کرو اور یہ بھی جان رکھو کہ اللہ تعالیٰ بخشش اور علم واﻻ ہے'),
(243, 2, 236, 'اگر تم عورتوں کو بغیر ہاتھ لگائے اور بغیر مہر مقرر کئے طلاق دے دو تو بھی تم پر کوئی گناه نہیں، ہاں انہیں کچھ نہ کچھ فائده دو۔ خوشحال اپنے انداز سے اور تنگدست اپنی طاقت کے مطابق دستور کے مطابق اچھا فائده دے۔ بھلائی کرنے والوں پر یہ ﻻزم ہے'),
(244, 2, 237, 'اور اگر تم عورتوں کو اس سے پہلے طلاق دے دو کہ تم نے انہیں ہاتھ لگایا ہو اور تم نے ان کا مہر بھی مقرر کردیا ہو تو مقرره مہر کا آدھا مہر دے دو، یہ اور بات ہے کہ وه خود معاف کردیں یا وه شخص معاف کردے جس کے ہاتھ میں نکاح کی گره ہے تمہارا معاف کردینا تقویٰ سے بہت نزدیک ہے اور آپس کی فضیلت اور بزرگی کو فراموش نہ کرو، یقیناً اللہ تعالیٰ تمہارے اعمال کو دیکھ رہا ہے'),
(245, 2, 238, 'نمازوں کی حفاﻇت کرو، بالخصوص درمیان والی نماز کی اور اللہ تعالیٰ کے لئے باادب کھڑے رہا کرو'),
(246, 2, 239, 'اگر تمہیں خوف ہو تو پیدل ہی سہی یا سوار ہی سہی، ہاں جب امن ہوجائے تو اللہ کا ذکر کرو جس طرح کہ اس نے تمہیں اس بات کی تعلیم دی جسے تم نہیں جانتے تھے'),
(247, 2, 240, 'جو لوگ تم میں سے فوت ہوجائیں اور بیویاں چھوڑ جائیں وه وصیت کر جائیں کہ ان کی بیویاں سال بھر تک فائده اٹھائیں انہیں کوئی نہ نکالے، ہاں اگر وه خود نکل جائیں تو تم پر اس میں کوئی گناه نہیں جو وه اپنے لئے اچھائی سے کریں، اللہ تعالیٰ غالب اور حکیم ہے'),
(248, 2, 241, 'طلاق والیوں کو اچھی طرح فائده دینا پرہیزگاروں پر ﻻزم ہے'),
(249, 2, 242, 'اللہ تعالیٰ اسی طرح اپنی آیتیں تم پر ﻇاہر فرما رہا ہے تاکہ تم سمجھو'),
(250, 2, 243, 'کیا تم نے انہیں نہیں دیکھا جو ہزاروں کی تعداد میں تھے اور موت کے ڈر کے مارے اپنے گھروں سے نکل کھڑے ہوئے تھے۔ اللہ تعالیٰ نے انہیں فرمایا مرجاؤ، پھر انہیں زنده کردیا بے شک اللہ تعالیٰ لوگوں پر بڑا فضل واﻻ ہے، لیکن اکثر لوگ ناشکرے ہیں'),
(251, 2, 244, 'اللہ تعالیٰ کی راه میں جہاد کرو اور جان لو کہ اللہ تعالیٰ سنتا، جانتا ہے'),
(252, 2, 245, 'ایسا بھی کوئی ہے جو اللہ تعالیٰ کو اچھا قرض دے پس اللہ تعالیٰ اسے بہت بڑھا چڑھا کر عطا فرمائے، اللہ ہی تنگی اور کشادگی کرتا ہے اور تم سب اسی کی طرف لوٹائے جاؤ گے'),
(253, 2, 246, 'کیا آپ نے (حضرت) موسیٰ کے بعد والی بنی اسرائیل کی جماعت کو نہیں دیکھا جب کہ انہوں نے اپنے پیغمبر سے کہا کہ کسی کو ہمارا بادشاه بنا دیجیئے تاکہ ہم اللہ کی راه میں جہاد کریں۔ پیغمبر نے کہا کہ ممکن ہے جہاد فرض ہوجانے کے بعد تم جہاد نہ کرو، انہوں نے کہا بھلا ہم اللہ کی راه میں جہاد کیوں نہ کریں گے؟ ہم تو اپنے گھروں سے اجاڑے گئے ہیں اور بچوں سے دور کردیئے گئے ہیں۔ پھر جب ان پر جہاد فرض ہوا تو سوائے تھوڑے سے لوگوں کے سب پھر گئے اور اللہ تعالیٰ ﻇالموں کو خوب جانتا ہے'),
(254, 2, 247, 'اور انہیں ان کے نبی نے فرمایا کہ اللہ تعالیٰ نے طالوت کو تمہارا بادشاه بنا دیا ہے تو کہنے لگے بھلا اس کی ہم پر حکومت کیسے ہوسکتی ہے؟ اس سے تو بہت زیاده حقدار بادشاہت کے ہم ہیں، اس کو تو مالی کشادگی بھی نہیں دی گئی۔ نبی نے فرمایا سنو، اللہ تعالیٰ نے اسی کو تم پر برگزیده کیا ہے اور اسے علمی اور جسمانی برتری بھی عطا فرمائی ہے بات یہ ہے کہ اللہ جسے چاہے اپنا ملک دے، اللہ تعالیٰ کشادگی واﻻ اور علم واﻻ ہے'),
(255, 2, 248, 'ان کے نبی نے انہیں پھر کہا کہ اس کی بادشاہت کی ﻇاہری نشانی یہ ہے کہ تمہارے پاس وه صندوق آ جائے گا جس میں تمہارے رب کی طرف سے دلجمعی ہے اور آل موسیٰ اور آل ہارون کا بقیہ ترکہ ہے، فرشتے اسے اٹھا کر ﻻئیں گے۔ یقیناً یہ تو تمہارے لئے کھلی دلیل ہے اگر تم ایمان والے ہو'),
(256, 2, 249, 'جب (حضرت) طالوت لشکروں کو لے کر نکلے تو کہا سنو اللہ تعالیٰ تمہیں ایک نہر سے آزمانے واﻻہے، جس نے اس میں سے پانی پی لیا وه میرا نہیں اور جو اسے نہ چکھے وه میرا ہے، ہاں یہ اور بات ہے کہ اپنے ہاتھ سے ایک چلو بھرلے۔ لیکن سوائے چند کے باقی سب نے وه پانی پی لیا (حضرت) طالوت مومنین سمیت جب نہر سے گزر گئے تو وه لوگ کہنے لگے آج تو ہم میں طاقت نہیں کہ جالوت اور اس کے لشکروں سے لڑیں۔ لیکن اللہ تعالیٰ کی ملاقات پر یقین رکھنے والوں نے کہا، بسا اوقات چھوٹی اور تھوڑی سی جماعتیں بڑی اور بہت سی جماعتوں پر اللہ کے حکم سے غلبہ پالیتی ہیں، اللہ تعالیٰ صبر والوں کے ساتھ ہے'),
(257, 2, 250, 'جب ان کا جالوت اور اس کے لشکر سے مقابلہ ہوا تو انہوں نے دعا مانگی کہ اے پروردگار ہمیں صبر دے، ﺛابت قدمی دے اور قوم کفار پر ہماری مدد فرما'),
(258, 2, 251, 'چنانچہ اللہ تعالیٰ کے حکم سے انہوں نے جالوتیوں کو شکست دے دی اور (حضرت) داؤد (علیہ السلام) کے ہاتھوں جالوت قتل ہوا اور اللہ تعالیٰ نے داؤد (علیہ السلام) کو مملکت وحکمت اور جتنا کچھ چاہا علم بھی عطا فرمایا۔ اگر اللہ تعالیٰ بعض لوگوں کو بعض سے دفع نہ کرتا تو زمین میں فساد پھیل جاتا، لیکن اللہ تعالیٰ دنیا والوں پر بڑا فضل وکرم کرنے واﻻ ہے'),
(259, 2, 252, 'یہ اللہ تعالیٰ کی آیتیں ہیں جنہیں ہم حقانیت کے ساتھ آپ پر پڑھتے ہیں، بالیقین آپ رسولوں میں سے ہیں'),
(260, 2, 253, 'یہ رسول ہیں جن میں سے ہم نے بعض کو بعض پر فضیلت دی ہے، ان میں سے بعض وه ہیں جن سے اللہ تعالیٰ نے بات چیت کی ہے اور بعض کے درجے بلند کئے ہیں، اور ہم نے عیسیٰ بن مریم کو معجزات عطا فرمائے اور روح القدس سے ان کی تائید کی۔اگر اللہ تعالیٰ چاہتا تو ان کے بعد والے اپنے پاس دلیلیں آجانے کے بعد ہرگز آپس میں لڑائی بھڑائی نہ کرتے، لیکن ان لوگوں نے اختلاف کیا، ان میں سے بعض تو مومن ہوئے اور بعض کافر، اور اگر اللہ تعالیٰ چاہتا تو یہ آپس میں نہ لڑتے، لیکن اللہ تعالیٰ جو چاہتا ہے کرتا ہے'),
(261, 2, 254, 'اے ایمان والو! جو ہم نے تمہیں دے رکھا ہے اس میں سے خرچ کرتے رہو اس سے پہلے کہ وه دن آئے جس میں نہ تجارت ہے نہ دوستی اور شفاعت اور کافر ہی ﻇالم ہیں'),
(262, 2, 255, 'اللہ تعالیٰ ہی معبود برحق ہے جس کے سوا کوئی معبود نہیں جو زنده اور سب کا تھامنے واﻻ ہے، جسے نہ اونگھ آئے نہ نیند، اس کی ملکیت میں زمین اور آسمانوں کی تمام چیزیں ہیں۔ کون ہے جو اس کی اجازت کے بغیر اس کے سامنے شفاعت کرسکے، وه جانتا ہے جو ان کے سامنے ہے اور جو ان کے پیچھے ہے اور وه اس کے علم میں سے کسی چیز کا احاطہ نہیں کرسکتے مگر جتنا وه چاہے، اس کی کرسی کی وسعت نے زمین و آسمان کو گھیر رکھا ہے اور اللہ تعالیٰ ان کی حفاﻇت سے نہ تھکتا اور نہ اکتاتا ہے، وه تو بہت بلند اور بہت بڑا ہے'),
(263, 2, 256, 'دین کے بارے میں کوئی زبردستی نہیں، ہدایت ضلالت سے روشن ہوچکی ہے، اس لئے جو شخص اللہ تعالیٰ کے سوا دوسرے معبودوں کا انکار کرکے اللہ تعالیٰ پر ایمان ﻻئے اس نے مضبوط کڑے کو تھام لیا، جو کبھی نہ ٹوٹے گا اور اللہ تعالیٰ سننے واﻻ، جاننے واﻻ ہے'),
(264, 2, 257, 'ایمان ﻻنے والوں کا کارساز اللہ تعالیٰ خود ہے، وه انہیں اندھیروں سے روشنی کی طرف نکال لے جاتا ہے اور کافروں کے اولیاء شیاطین ہیں۔ وه انہیں روشنی سے نکال کر اندھیروں کی طرف لے جاتے ہیں، یہ لوگ جہنمی ہیں جو ہمیشہ اسی میں پڑے رہیں گے'),
(265, 2, 258, 'کیا تونے اسے نہیں دیکھا جو سلطنت پا کر ابراہیم (علیہ السلام) سے اس کے رب کے بارے میں جھگڑ رہا تھا، جب ابراہیم (علیہ السلام) نے کہا کہ میرا رب تو وه ہے جو جلاتا ہے اور مارتا ہے، وه کہنے لگا میں بھی جلاتا اور مارتا ہوں، ابراہیم (علیہ السلام) نے کہا اللہ تعالیٰ سورج کو مشرق کی طرف سے لے آتا ہے تو اسے مغرب کی جانب سے لے آ۔ اب تو وه کافر بھونچکا ره گیا، اور اللہ تعالیٰ ﻇالموں کو ہدایت نہیں دیتا'),
(266, 2, 259, 'یا اس شخص کے مانند کہ جس کا گزر اس بستی پر ہوا جو چھت کے بل اوندھی پڑی ہوئی تھی، وه کہنے لگا اس کی موت کے بعد اللہ تعالیٰ اسے کس طرح زنده کرے گا؟ تو اللہ تعالی نے اسے مار دیا سو سال کے لئے، پھر اسے اٹھایا، پوچھا کتنی مدت تجھ پر گزری؟ کہنے لگا ایک دن یا دن کا کچھ حصہ، فرمایا بلکہ تو سو سال تک رہا، پھر اب تو اپنے کھانے پینے کو دیکھ کہ بالکل خراب نہیں ہوا اور اپنے گدھے کو بھی دیکھ، ہم تجھے لوگوں کے لئے ایک نشانی بناتے ہیں تو دیکھ کہ ہم ہڈیوں کو کس طرح اٹھاتے ہیں، پھر ان پر گوشت چڑھاتے ہیں، جب یہ سب ﻇاہر ہو چکا تو کہنے لگا میں جانتا ہوں کہ اللہ تعالیٰ ہر چیز پر قادر ہے'),
(267, 2, 260, 'اور جب ابراہیم (علیہ السلام) نے کہا کہ اے میرے پروردگار! مجھے دکھا تو مُردوں کو کس طرح زنده کرے گا؟ (جناب باری تعالیٰ نے) فرمایا، کیا تمہیں ایمان نہیں؟ جواب دیا ایمان تو ہے لیکن میرے دل کی تسکین ہو جائے گی، فرمایا چار پرند لو، ان کے ٹکڑے کر ڈالو، پھر ہر پہاڑ پر ان کا ایک ایک ٹکڑا رکھ دو پھر انہیں پکارو، تمہارے پاس دوڑتے ہوئے آجائیں گے اور جان رکھو کہ اللہ تعالیٰ غالب ہے حکمتوں واﻻ ہے'),
(268, 2, 261, 'جو لوگ اپنا مال اللہ تعالیٰ کی راه میں خرچ کرتے ہیں اس کی مثال اس دانے جیسی ہے جس میں سے سات بالیاں نکلیں اور ہر بالی میں سودانے ہوں، اور اللہ تعالیٰ جسے چاہے بڑھا چڑھا کر دے اور اللہ تعالیٰ کشادگی واﻻ اور علم واﻻ ہے'),
(269, 2, 262, 'جو لوگ اپنا مال اللہ تعالیٰ کی راه میں خرچ کرتے ہیں پھر اس کے بعد نہ تو احسان جتاتے ہیں نہ ایذا دیتے ہیں، ان کا اجر ان کے رب کے پاس ہے ان پر نہ تو کچھ خوف ہے نہ وه اداس ہوں گے'),
(270, 2, 263, 'نرم بات کہنا اور معاف کردینا اس صدقہ سے بہتر ہے جس کے بعد ایذا رسانی ہو اور اللہ تعالیٰ بےنیاز اور بردبار ہے'),
(271, 2, 264, 'اے ایمان والو! اپنی خیرات کو احسان جتا کر اور ایذا پہنچا کر برباد نہ کرو! جس طرح وه شخص جو اپنا مال لوگوں کے دکھاوے کے لئے خرچ کرے اور نہ اللہ تعالیٰ پر ایمان رکھے نہ قیامت پر، اس کی مثال اس صاف پتھر کی طرح ہے جس پر تھوڑی سی مٹی ہو پھر اس پر زوردار مینہ برسے اور وه اسے بالکل صاف اور سخت چھوڑ دے، ان ریاکاروں کو اپنی کمائی میں سے کوئی چیز ہاتھ نہیں لگتی اور اللہ تعالیٰ کافروں کی قوم کو (سیدھی) راه نہیں دکھاتا'),
(272, 2, 265, 'ان لوگوں کی مثال جو اپنا مال اللہ تعالیٰ کی رضامندی کی طلب میں دل کی خوشی اور یقین کے ساتھ خرچ کرتے ہیں اس باغ جیسی ہے جو اونچی زمین پر ہواور زوردار بارش اس پر برسے اور وه اپنا پھل دگنا ﻻوے اور اگر اس پر بارش نہ بھی برسے تو پھوار ہی کافی ہے اور اللہ تمہارے کام دیکھ رہا ہے'),
(273, 2, 266, 'کیا تم میں سے کوئی بھی یہ چاہتا ہے کہ اس کا کھجوروں اور انگوروں کا باغ ہو، جس میں نہریں بہہ رہی ہوں اور ہر قسم کے پھل موجود ہوں، اس شخص کا بڑھاپا آگیا ہو، اس کے ننھے ننھے سے بچے بھی ہوں اور اچانک باغ کو بگوﻻ لگ جائے جس میں آگ بھی ہو، پس وه باغ جل جائے، اسی طرح اللہ تعالیٰ تمہارے لئے آیتیں بیان کرتا ہے تاکہ تم غوروفکر کرو'),
(274, 2, 267, 'اے ایمان والو! اپنی پاکیزه کمائی میں سے اور زمین میں سے تمہارے لئے ہماری نکالی ہوئی چیزوں میں سے خرچ کرو، ان میں سے بری چیزوں کے خرچ کرنے کا قصد نہ کرنا، جسے تم خود لینے والے نہیں ہو، ہاں اگر آنکھیں بند کر لو تو، اور جان لو کہ اللہ تعالیٰ بے پرواه اور خوبیوں واﻻ ہے'),
(275, 2, 268, 'شیطان تمہیں فقیری سے دھمکاتا ہے اور بےحیائی کا حکم دیتا ہے، اور اللہ تعالیٰ تم سے اپنی بخشش اور فضل کا وعده کرتا ہے، اللہ تعالیٰ وسعت واﻻ اور علم واﻻ ہے'),
(276, 2, 269, 'وه جسے چاہے حکمت اور دانائی دیتا ہے اور جو شخص حکمت اور سمجھ دیا جائے وه بہت ساری بھلائی دیا گیا اور نصیحت صرف عقلمند ہی حاصل کرتے ہیں'),
(277, 2, 270, 'تم جتنا کچھ خرچ کرو یعنی خیرات اور جو کچھ نذر مانو اسے اللہ تعالیٰ بخوبی جانتا ہے، اور ﻇالموں کا کوئی مددگار نہیں'),
(278, 2, 271, 'اگر تم صدقے خیرات کو ﻇاہر کرو تو وه بھی اچھا ہے اور اگر تم اسے پوشیده پوشیده مسکینوں کو دے دو تو یہ تمہارے حق میں بہتر ہے، اللہ تعالیٰ تمہارے گناہوں کو مٹا دے گا اور اللہ تعالیٰ تمہارے تمام اعمال کی خبر رکھنے واﻻ ہے،'),
(279, 2, 272, 'انہیں ہدایت پر ﻻ کھڑا کرنا تیرے ذمہ نہیں بلکہ ہدایت اللہ تعالیٰ دیتا ہے جسے چاہتا ہے اور تم جو بھلی چیز اللہ کی راه میں دو گے اس کا فائده خود پاؤ گے۔ تمہیں صرف اللہ تعالیٰ کی رضامندی کی طلب کے لئے ہی خرچ کرنا چاہئے تم جو کچھ مال خرچ کرو گے اس کا پورا پورا بدلہ تمہیں دیا جائے گا، اور تمہارا حق نہ مارا جائے گا'),
(280, 2, 273, 'صدقات کے مستحق صرف وه غربا ہیں جو اللہ کی راه میں روک دیئے گئے، جو ملک میں چل پھر نہیں سکتے نادان لوگ ان کی بے سوالی کی وجہ سے انہیں مالدار خیال کرتے ہیں، آپ ان کے چہرے دیکھ کر قیافہ سے انہیں پہچان لیں گے وه لوگوں سے چمٹ کر سوال نہیں کرتے، تم جو کچھ مال خرچ کرو تو اللہ تعالیٰ اس کا جاننے واﻻ ہے'),
(281, 2, 274, 'جو لوگ اپنے مالوں کو رات دن چھپے کھلے خرچ کرتے ہیں ان کے لئے ان کے رب تعالیٰ کے پاس اجر ہے اور نہ انہیں خوف ہے اور نہ غمگینی'),
(282, 2, 275, 'سود خور لوگ نہ کھڑے ہوں گے مگر اسی طرح جس طرح وہ کھڑا ہوتا ہے جسے شیطان چھو کر خبطی بنا دے یہ اس لئے کہ یہ کہا کرتے تھے کہ تجارت بھی تو سود ہی کی طرح ہے، حالانکہ اللہ تعالیٰ نے تجارت کو حلال کیا اور سود کو حرام، جو شخص اپنے پاس آئی ہوئی اللہ تعالیٰ کی نصیحت سن کر رک گیا اس کے لئے وہ ہے جو گزرا اور اس کا معاملہ اللہ تعالیٰ کی طرف ہے، اور جو پھر دوبارہ ﴿حرام کی طرف﴾ لوٹا، وہ جہنمی ہے، ایسے لوگ ہمیشہ ہی اس میں رہیں گے'),
(283, 2, 276, 'اللہ تعالیٰ سود کو مٹاتا ہے اور صدقہ کو بڑھاتا ہے اور اللہ تعالیٰ کسی ناشکرے اور گنہگار سے محبت نہیں کرتا'),
(284, 2, 277, 'بے شک جو لوگ ایمان کے ساتھ ﴿سنت کے مطابق﴾ نیک کام کرتے ہیں، نمازوں کو قائم کرتے ہیں اور زکوٰة ادا کرتے ہیں، ان کا اجر ان کے رب تعالیٰ کے پاس ہے، ان پر نہ تو کوئی خوف ہے، نہ اداسی اور غم'),
(285, 2, 278, 'اے ایمان والو! اللہ تعالیٰ سے ڈرو اور جو سود باقی رہ گیا ہے وہ چھوڑ دو، اگر تم سچ مچ ایمان والے ہو'),
(286, 2, 279, 'اور اگر ایسا نہیں کرتے تو اللہ تعالیٰ سے اور اس کے رسول سے لڑنے کے لئے تیار ہو جاؤ، ہاں اگر توبہ کرلو تو تمہارا اصل مال تمہارا ہی ہے، نہ تم ظلم کرو نہ تم پر ظلم کیا جائے گا'),
(287, 2, 280, 'اور اگر کوئی تنگی والا ہو تو اسے آسانی تک مہلت دینی چاہئے اور صدقہ کرو تو تمہارے لئے بہت ہی بہتر ہے، اگر تم میں علم ہو'),
(288, 2, 281, 'اور اس دن سے ڈرو جس میں تم سب اللہ تعالیٰ کی طرف لوٹائے جاؤگے اور ہر شخص کو اس کے اعمال کا پورا پورا بدلہ دیا جائے گا اور ان پر ظلم نہیں کیا جائے گا'),
(289, 2, 282, 'اے ایمان والو! جب تم آپس میں ایک دوسرے سے میعاد مقرر پر قرض کا معاملہ کرو تو اسے لکھ لیا کرو، اور لکھنے والے کو چاہئے کہ تمہارا آپس کا معاملہ عدل سے لکھے، کاتب کو چاہئے کہ لکھنے سے انکار نہ کرے جیسے اللہ تعالیٰ نے اسے سکھایا ہے، پس اسے بھی لکھ دینا چاہئے اور جس کے ذمہ حق ہو وہ لکھوائے اور اپنے اللہ تعالیٰ سے ڈرے جو اس کا رب ہے اور حق میں سے کچھ گھٹائے نہیں، ہاں جس شخص کے ذمہ حق ہے وہ اگر نادان ہو یا کمزور ہو یا لکھوانے کی طاقت نہ رکھتا ہو تو اس کا ولی عدل کے ساتھ لکھوا دے اور اپنے میں سے دو مرد گواہ رکھ لو، اگر دو مرد نہ ہوں تو ایک مرد اور دو عورتیں جنہیں تم گوہوں میں سے پسند کر لو تاکہ ایک کی بھول چوک کو دوسری یاد دلا دے اور گواہوں کو چاہئے کہ وہ جب بلائے جائیں تو انکار نہ کریں اور قرض کو جس کی مدت مقرر ہے خواہ چھوٹا ہو یا بڑا ہو لکھنے میں کاہلی نہ کرو، اللہ تعالیٰ کے نزدیک یہ بات بہت انصاف والی ہے اور گواہی کو بھی درست رکھنے والی اور شق و شبہ سے بھی زیادہ بچانے والی ہے، ہاں یہ اور بات ہے کہ وہ معاملہ نقد تجارت کی شکل میں ہو جو آپس میں تم لین دین کر رہے ہو تو تم پر اس کے نہ لکھنے میں کوئی گناہ نہیں۔ خرید و فروخت کے وقت بھی گواہ مقرر کر لیا کرو اور ﴿یاد رکھو کہ﴾ نہ تو لکھنے والے کو نقصان پہنچایا جائے نہ گواہ کو اور اگر تم یہ کرو تو یہ تمہاری کھلی نافرمانی ہے، اللہ تعالیٰ سے ڈرو، اللہ تمہیں تعلیم دے رہا ہے اور اللہ تعالیٰ ہر چیز کو خوب جاننے والا ہے'),
(290, 2, 283, 'اور اگر تم سفر میں ہو اور لکھنے واﻻ نہ پاؤ تو رہن قبضہ میں رکھ لیا کرو، ہاں اگر آپس میں ایک دوسرے سے مطمئن ہو تو جسے امانت دی گئی ہے وه اسے ادا کردے اور اللہ تعالیٰ سے ڈرتا رہے جو اس کا رب ہے۔ اور گواہی کو نہ چھپاؤ اور جو اسے چھپالے وه گنہگار دل واﻻ ہے اور جو کچھ تم کرتے ہو اسے اللہ تعالیٰ خوب جانتا ہے'),
(291, 2, 284, 'آسمانوں اور زمین کی ہر چیز اللہ تعالیٰ ہی کی ملکیت ہے۔ تمہارے دلوں میں جو کچھ ہے اسے تم ﻇاہر کرو یا چھپاؤ، اللہ تعالیٰ اس کا حساب تم سے لے گا۔ پھر جسے چاہے بخشے اور جسے چاہے سزا دے اور اللہ تعالیٰ ہر چیز پر قادر ہے'),
(292, 2, 285, 'رسول ایمان ﻻیا اس چیز پر جو اس کی طرف اللہ تعالیٰ کی جانب سے اتری اور مومن بھی ایمان ﻻئے، یہ سب اللہ تعالیٰ اور اس کے فرشتوں پر اور اس کی کتابوں پر اور اس کے رسولوں پر ایمان ﻻئے، اس کے رسولوں میں سے کسی میں ہم تفریق نہیں کرتے، انہوں نے کہہ دیا کہ ہم نے سنا اور اطاعت کی، ہم تیری بخشش طلب کرتے ہیں اے ہمارے رب! اور ہمیں تیری ہی طرف لوٹنا ہے،'),
(293, 2, 286, 'اللہ تعالیٰ کسی جان کو اس کی طاقت سے زیاده تکلیف نہیں دیتا، جو نیکی وه کرے وه اس کے لئے اور جو برائی وه کرے وه اس پر ہے، اے ہمارے رب! اگر ہم بھول گئے ہوں یا خطا کی ہو تو ہمیں نہ پکڑنا، اے ہمارے رب! ہم پر وه بوجھ نہ ڈال جو ہم سے پہلے لوگوں پر ڈاﻻ تھا، اے ہمارے رب! ہم پر وه بوجھ نہ ڈال جس کی ہمیں طاقت نہ ہو اور ہم سے درگزر فرما! اور ہمیں بخش دے اور ہم پر رحم کر! تو ہی ہمارا مالک ہے، ہمیں کافروں کی قوم پر غلبہ عطا فرما');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(294, 3, 1, 'الم'),
(295, 3, 2, 'اللہ تعالیٰ وه ہے جس کے سوا کوئی معبود نہیں، جو زنده اور سب کا نگہبان ہے'),
(296, 3, 3, 'جس نے آپ پر حق کے ساتھ اس کتاب کو نازل فرمایا ہے، جو اپنے سے پہلے کی تصدیق کرنے والی ہے، اسی نے اس سے پہلے تورات اور انجیل کو اتارا تھا'),
(297, 3, 4, 'اس سے پہلے، لوگوں کو ہدایت کرنے والی بنا کر، اور قرآن بھی اسی نے اتارا، جو لوگ اللہ تعالیٰ کی آیتوں سے کفر کرتے ہیں ان کے لئے سخت عذاب ہے اور اللہ تعالیٰ غالب ہے، بدلہ لینے واﻻ ہے'),
(298, 3, 5, 'یقیناً اللہ تعالیٰ پر زمین وآسمان کی کوئی چیز پوشیده نہیں'),
(299, 3, 6, 'وه ماں کے پیٹ میں تمہاری صورتیں جس طرح چاہتا ہے بناتا ہے۔ اس کے سواکوئی معبود برحق نہیں وه غالب ہے، حکمت واﻻ ہے'),
(300, 3, 7, 'وہی اللہ تعالیٰ ہے جس نے تجھ پر کتاب اتاری جس میں واضح مضبوط آیتیں ہیں جو اصل کتاب ہیں اوربعض متشابہ آیتیں ہیں۔ پس جن کے دلوں میں کجی ہے وه تواس کی متشابہ آیتوں کے پیچھے لگ جاتے ہیں، فتنے کی طلب اور ان کی مراد کی جستجو کے لئے، حاﻻنکہ ان کے حقیقی مراد کو سوائے اللہ تعالیٰ کے کوئی نہیں جانتا اور پختہ ومضبوط علم والے یہی کہتے ہیں کہ ہم تو ان پر ایمان ﻻچکے، یہ ہمارے رب کی طرف سے ہیں اور نصیحت تو صرف عقلمند حاصل کرتے ہیں۔'),
(301, 3, 8, 'اے ہمارے رب! ہمیں ہدایت دینے کے بعد ہمارے دل ٹیڑھے نہ کردے اور ہمیں اپنے پاس سے رحمت عطا فرما، یقیناً تو ہی بہت بڑی عطا دینے واﻻ ہے'),
(302, 3, 9, 'اے ہمارے رب! تو یقیناً لوگوں کوایک دن جمع کرنے واﻻ ہے جس کے آنے میں کوئی شک نہیں، یقیناً اللہ تعالیٰ وعده خلافی نہیں کرتا'),
(303, 3, 10, 'کافروں کوان کے مال اور ان کی اوﻻد اللہ تعالیٰ (کے عذاب) سے چھڑانے میں کچھ کام نہ آئیں گی، یہ تو جہنم کا ایندھن ہی ہیں'),
(304, 3, 11, 'جیسا آل فرعون کاحال ہوا، اور ان کا جو ان سے پہلے تھے، انہوں نے ہماری آیتوں کوجھٹلایا، پھر اللہ تعالیٰ نے بھی انہیں ان کے گناہوں پر پکڑ لیا، اور اللہ تعالیٰ سخت عذاب واﻻ ہے'),
(305, 3, 12, 'کافروں سے کہہ دیجئے! کہ تم عنقریب مغلوب کئے جاؤ گے اور جہنم کی طرف جمع کئے جاؤ گے اور وه برا ٹھکانا ہے'),
(306, 3, 13, 'یقیناً تمہارے لئے عبرت کی نشانی تھی ان دو جماعتوں میں جو گتھ گئی تھیں، ایک جماعت تو اللہ تعالیٰ کی راه میں لڑ رہی تھی اور دوسرا گروه کافروں کا تھا وه انہیں اپنی آنکھوں سے اپنے سے دگنا دیکھتے تھے اور اللہ تعالیٰ جسے چاہے اپنی مدد سے قوی کرتا ہے۔ یقیناً اس میں آنکھوں والوں کے لئے بڑی عبرت ہے'),
(307, 3, 14, 'مرغوب چیزوں کی محبت لوگوں کے لئے مزین کر دی گئی ہے، جیسے عورتیں اور بیٹے اور سونے اور چاندی کے جمع کئے ہوئے خزانے اور نشاندار گھوڑے اور چوپائے اور کھیتی، یہ دنیا کی زندگی کا سامان ہے اور لوٹنے کا اچھا ٹھکانا تو اللہ تعالیٰ ہی کے پاس ہے'),
(308, 3, 15, 'آپ کہہ دیجئے! کیا میں تمہیں اس سے بہت ہی بہتر چیز بتاؤں؟ تقویٰ والوں کے لئے ان کے رب تعالیٰ کے پاس جنتیں ہیں جن کے نیچے نہریں بہہ رہی ہیں جن میں وه ہمیشہ رہیں گے اور پاکیزه بیویاں اور اللہ تعالیٰ کی رضامندی ہے، سب بندے اللہ تعالیٰ کی نگاه میں ہیں'),
(309, 3, 16, 'جو کہتے ہیں کہ اے ہمارے رب! ہم ایمان ﻻ چکے اس لئے ہمارے گناه معاف فرما اور ہمیں آگ کے عذاب سے بچا'),
(310, 3, 17, 'جو صبر کرنے والے اور سچ بولنے والے اور فرمانبرداری کرنے والے اور اللہ کی راه میں خرچ کرنے والے اور پچھلی رات کو بخشش مانگنے والے ہیں'),
(311, 3, 18, 'اللہ تعالیٰ، فرشتے اور اہل علم اس بات کی گواہی دیتے ہیں کہ اللہ کے سوا کوئی معبود نہیں اور وه عدل کو قائم رکھنے واﻻ ہے، اس غالب اور حکمت والے کے سوا کوئی عبادت کے ﻻئق نہیں'),
(312, 3, 19, 'بے شک اللہ تعالیٰ کے نزدیک دین اسلام ہی ہے، اور اہل کتاب نے اپنے پاس علم آجانے کے بعد آپس کی سرکشی اور حسد کی بنا پر ہی اختلاف کیا ہے اور اللہ تعالیٰ کی آیتوں کے ساتھ جو بھی کفر کرے اللہ تعالیٰ اس کا جلد حساب لینے واﻻ ہے'),
(313, 3, 20, 'پھر بھی اگر یہ آپ سے جھگڑیں توآپ کہہ دیں کہ میں اور میرے تابعداروں نے اللہ تعالیٰ کے سامنے اپنا سر تسلیم خم کر دیا ہے اور اہل کتاب سے اور انپڑھ لوگوں سے کہہ دیجیئے! کہ کیا تم بھی اطاعت کرتے ہو؟ پس اگر یہ بھی تابعدار بن جائیں تو یقیناً ہداہت والے ہیں اور اگر یہ روگردانی کریں، تو آپ پر صرف پہنچا دینا ہے اور اللہ بندوں کو خوب دیکھ بھال رہا ہے'),
(314, 3, 21, 'جو لوگ اللہ تعالیٰ کی آیتوں سے کفر کرتے ہیں اورناحق نبیوں کو قتل کر ڈالتے ہیں اور جو لوگ عدل وانصاف کی بات کہیں انہیں بھی قتل کر ڈالتے ہیں، تو اے نبی! انہیں دردناک عذاب کی خبردے دیجئے'),
(315, 3, 22, 'ان کے اعمال دنیا وآخرت میں غارت ہیں اور ان کا کوئی مددگار نہیں'),
(316, 3, 23, 'کیا آپ نے انہیں نہیں دیکھا جنہیں ایک حصہ کتاب کا دیا گیا ہے وه اپنے آپس کے فیصلوں کے لئے اللہ تعالیٰ کی کتاب کی طرف بلائے جاتے ہیں، پھر بھی ایک جماعت ان کی منھ پھیر کر لوٹ جاتی ہے'),
(317, 3, 24, 'اس کی وجہ ان کا یہ کہنا کہ ہمیں تو گنے چنے چند دن ہی آگ جلائے گی، ان کی گڑھی گڑھائی باتوں نے انہیں ان کے دین کے بارے میں دھوکے میں ڈال رکھا ہے'),
(318, 3, 25, 'پس کیا حال ہوگا جبکہ ہم انہیں اس دن جمع کرینگے؟ جس کے آنے میں کوئی شک نہیں اور ہر شخص اپنا اپنا کیا پورا پورا دیاجائے گا اور ان پر ﻇلم نہ کیاجائے گا'),
(319, 3, 26, 'آپ کہہ دیجئے اے اللہ! اے تمام جہان کے مالک! تو جسے چاہے بادشاہی دے اور جس سے چاہے سلطنت چھین لے اور تو جسے چاہے عزت دے اورجسے چاہے ذلت دے، تیرے ہی ہاتھ میں سب بھلائیاں ہیں، بے شک تو ہر چیز پر قادر ہے'),
(320, 3, 27, 'تو ہی رات کو دن میں داخل کرتا ہے اور دن کو رات میں لے جاتا ہے، تو ہی بے جان سے جاندار پیدا کرتا ہے اور تو ہی جاندار سے بے جان پیدا کرتا ہے، تو ہی ہے کہ جسے چاہتا ہے بے شمار روزی دیتا ہے'),
(321, 3, 28, 'مومنوں کو چاہئے کہ ایمان والوں کو چھوڑ کر کافروں کو اپنا دوست نہ بنائیں اور جوایسا کرے گا وه اللہ تعالیٰ کی کسی حمایت میں نہیں مگر یہ کہ ان کے شر سے کسی طرح بچاؤ مقصود ہو، اور اللہ تعالیٰ خود تمہیں اپنی ذات سے ڈرا رہا ہے اور اللہ تعالیٰ ہی کی طرف لوٹ جانا ہے'),
(322, 3, 29, 'کہہ دیجئے! کہ خواه تم اپنے سینوں کی باتیں چھپاؤ خواه ﻇاہر کرو اللہ تعالیٰ (بہرحال) جانتا ہے، آسمانوں اور زمین میں جو کچھ ہے سب اسے معلوم ہے اور اللہ تعالیٰ ہر چیز پر قادر ہے'),
(323, 3, 30, 'جس دن ہر نفس (شخص) اپنی کی ہوئی نیکیوں کو اور اپنی کی ہوئی برائیوں کو موجود پالے گا، آرزو کرے گا کہ کاش! اس کے اور برائیوں کے درمیان بہت ہی دوری ہوتی۔ اللہ تعالیٰ تمہیں اپنی ذات سے ڈرا رہا ہے اور اللہ تعالیٰ اپنے بندوں پر بڑا ہی مہربان ہے'),
(324, 3, 31, 'کہہ دیجئے! اگر تم اللہ تعالیٰ سے محبت رکھتے ہو تو میری تابعداری کرو، خود اللہ تعالیٰ تم سے محبت کرے گا اور تمہارے گناه معاف فرما دے گا اور اللہ تعالیٰ بڑا بخشنے واﻻ مہربان ہے'),
(325, 3, 32, 'کہہ دیجئے! کہ اللہ تعالیٰ اور رسول کی اطاعت کرو، اگر یہ منھ پھیر لیں تو بے شک اللہ تعالیٰ کافروں سے محبت نہیں کرتا'),
(326, 3, 33, 'بے شک اللہ تعالیٰ نے تمام جہان کے لوگوں میں سے آدم (علیہ السلام) کو اور نوح (علیہ السلام) کو، ابراہیم (علیہ السلام) کے خاندان اور عمران کے خاندان کو منتخب فرما لیا'),
(327, 3, 34, 'کہ یہ سب آپس میں ایک دوسرے کی نسل سے ہیں اور اللہ تعالیٰ سنتا جانتا ہے'),
(328, 3, 35, 'جب عمران کی بیوی نے کہا کہ اے میرے رب! میرے پیٹ میں جو کچھ ہے، اسے میں نے تیرے نام آزاد کرنے کی نذر مانی، تو میری طرف سے قبول فرما! یقیناً تو خوب سننے واﻻ اور پوری طرح جاننے واﻻ ہے'),
(329, 3, 36, 'جب بچی کو جنا تو کہنے لگیں کہ پروردگار! مجھے تو لڑکی ہوئی، اللہ تعالیٰ کو خوب معلوم ہے کہ کیا اوﻻد ہوئی ہے اور لڑکا لڑکی جیسا نہیں میں نے اس کا نام مریم رکھا، میں اسے اور اس کی اوﻻد کو شیطان مردود سے تیری پناه میں دیتی ہوں'),
(330, 3, 37, 'پس اسے اس کے پروردگار نے اچھی طرح قبول فرمایا اور اسے بہترین پرورش دی۔ اس کی خیر خبر لینے واﻻ زکریا (علیہ السلام) کو بنایا، جب کبھی زکریا (علیہ السلام) ان کے حجرے میں جاتے ان کے پاس روزی رکھی ہوئی پاتے، وه پوچھتے اے مریم! یہ روزی تمہارے پاس کہاں سے آئی؟ وه جواب دیتیں کہ یہ اللہ تعالیٰ کے پاس سے ہے، بے شک اللہ تعالیٰ جسے چاہے بے شمار روزی دے'),
(331, 3, 38, 'اسی جگہ زکریا (علیہ السلام) نے اپنے رب سے دعا کی، کہا کہ اے میرے پروردگار! مجھے اپنے پاس سے پاکیزه اوﻻد عطا فرما، بے شک تو دعا کا سننے واﻻ ہے'),
(332, 3, 39, 'پس فرشتوں نے انہیں آواز دی، جب کہ وه حجرے میں کھڑے نماز پڑھ رہے تھے، کہ اللہ تعالیٰ تجھے یحيٰ کی یقینی خوشخبری دیتا ہے جو اللہ تعالیٰ کے کلمہ کی تصدیق کرنے واﻻ، سردار، ضابط نفس اور نبی ہے نیک لوگوں میں سے'),
(333, 3, 40, 'کہنے لگے اے میرے رب! میرے ہاں بچہ کیسے ہوگا؟ میں بالکل بوڑھا ہو گیا ہوں اور میری بیوی بانجھ ہے، فرمایا، اسی طرح اللہ تعالیٰ جو چاہے کرتا ہے'),
(334, 3, 41, 'کہنے لگے پروردگار! میرے لئے اس کی کوئی نشانی مقرر کر دے، فرمایا، نشانی یہ ہے کہ تین دن تک تو لوگوں سے بات نہ کر سکے گا، صرف اشارے سے سمجھائے گا، تو اپنے رب کا ذکر کثرت سے کر اور صبح وشام اسی کی تسبیح بیان کرتا ره!'),
(335, 3, 42, 'اور جب فرشتوں نے کہا، اے مریم! اللہ تعالیٰ نے تجھے برگزیده کر لیا اور تجھے پاک کر دیا اور سارے جہان کی عورتوں میں سے تیرا انتخاب کر لیا'),
(336, 3, 43, 'اے مریم! تو اپنے رب کی اطاعت کر اور سجده کر اور رکوع کرنے والوں کے ساتھ رکوع کر'),
(337, 3, 44, 'یہ غیب کی خبروں میں سے ہے جسے ہم تیری طرف وحی سے پہنچاتے ہیں، تو ان کے پاس نہ تھا جب کہ وه اپنے قلم ڈال رہے تھے کہ مریم کو ان میں سے کون پالے گا؟ اور نہ تو ان کے جھگڑنے کے وقت ان کے پاس تھا'),
(338, 3, 45, 'جب فرشتوں نے کہا اے مریم اللہ تعالیٰ تجھے اپنے ایک کلمے کی خوشخبری دیتا ہے جس کا نام مسیح عیسیٰ بن مریم ہے جو دنیا اور آخرت میں ذیعزت ہے اور وه میرے مقربین میں سے ہے'),
(339, 3, 46, 'وه لوگوں سے اپنے گہوارے میں باتیں کرے گا اور ادھیڑ عمر میں بھی اور وه نیک لوگوں میں سے ہوگا'),
(340, 3, 47, 'کہنے لگیں الٰہی مجھے لڑکا کیسے ہوگا؟ حاﻻنکہ مجھے تو کسی انسان نے ہاتھ بھی نہیں لگایا، فرشتے نے کہا، اسی طرح اللہ تعالیٰ جو چاہے پیدا کرتا ہے، جب کبھی وه کسی کام کو کرنا جاہتا ہے تو صرف یہ کہہ دیتا ہے کہ ہو جا! تو وه ہو جاتا ہے'),
(341, 3, 48, 'اللہ تعالیٰ اسے لکھنا اور حکمت اور توراة اورانجیل سکھائے گا'),
(342, 3, 49, 'اور وه بنی اسرائیل کی طرف رسول ہوگا، کہ میں تمہارے پاس تمہارے رب کی نشانی ﻻیا ہوں، میں تمہارے لئے پرندے کی شکل کی طرح مٹی کا پرنده بناتا ہوں، پھر اس میں پھونک مارتا ہوں تو وه اللہ تعالیٰ کے حکم سے پرنده بن جاتاہے اور اللہ تعالیٰ کے حکم سے میں مادرزاد اندھے کو اور کوڑھی کو اچھا کر دیتا ہوں اور مرددوں کو زندہ کرتا ہوں اور جو کچھ تم کھاؤ اور جو اپنے گھروں میں ذخیره کرو میں تمہیں بتا دیتا ہوں، اس میں تمہارے لئے بڑی نشانی ہے، اگر تم ایمان ﻻنے والے ہو'),
(343, 3, 50, 'اور میں توراة کی تصدیق کرنے واﻻ ہوں جو میرے سامنے ہے اور میں اس لئے آیا ہوں کہ تم پر بعض وه چیزیں حلال کروں جو تم پر حرام کر دی گئی ہیں اور میں تمہارے پاس تمہارے رب کی نشانی ﻻیا ہوں، اس لئے تم اللہ تعالیٰ سے ڈرو اور میری فرمانبرداری کرو!'),
(344, 3, 51, 'یقین مانو! میرا اور تمہارا رب اللہ ہی ہے، تم سب اسی کی عبادت کرو، یہی سیدھی راه ہے'),
(345, 3, 52, 'مگر جب حضرت عیسیٰ علیہ السلام نے ان کا کفر محسوس کر لیا تو کہنے لگے اللہ تعالیٰ کی راه میں میری مدد کرنے واﻻ کون کون ہے؟ حواریوں نے جواب دیا کہ ہم اللہ تعالیٰ کی راه کے مددگار ہیں، ہم اللہ تعالیٰ پر ایمان ﻻئے اور آپ گواه رہئے کہ ہم تابعدار ہیں'),
(346, 3, 53, 'اے ہمارے پالنے والے معبود! ہم تیری اتاری ہوئی وحی پر ایمان ﻻئے اور ہم نے تیرے رسول کی اتباع کی، پس تو ہمیں گواہوں میں لکھ لے'),
(347, 3, 54, 'اور کافروں نے مکر کیا اور اللہ تعالیٰ نے بھی (مکر) خفیہ تدبیر کی اور اللہ تعالیٰ سب خفیہ تدبیر کرنے والوں سے بہتر ہے'),
(348, 3, 55, 'جب اللہ تعالیٰ نے فرمایا کہ اے عیسیٰ! میں تجھے پورا لینے واﻻ ہوں اور تجھے اپنی جانب اٹھانے واﻻ ہوں اور تجھے کافروں سے پاک کرنے واﻻ ہوں اور تیرے تابعداروں کو کافروں کے اوپر غالب کرنے واﻻ ہوں قیامت کے دن تک، پھر تم سب کا لوٹنا میری ہی طرف ہے میں ہی تمہارے آپس کے تمام تر اختلافات کا فیصلہ کروں گا'),
(349, 3, 56, 'پھر کافروں کو تو میں دنیا اور آخرت میں سخت تر عذاب دوں گا اور ان کا کوئی مددگار نہ ہوگا'),
(350, 3, 57, 'لیکن ایمان والوں اور نیک اعمال والوں کو اللہ تعالیٰ ان کا ﺛواب پورا پورا دے گا اور اللہ تعالیٰ ﻇالموں سے محبت نہیں کرتا'),
(351, 3, 58, 'یہ جسے ہم تیرے سامنے پڑھ رہے ہیں آیتیں ہیں اور حکمت والی نصیحت ہیں'),
(352, 3, 59, 'اللہ تعالیٰ کے نزدیک عیسیٰ (علیہ السلام) کی مثال ہو بہو آدم (علیہ السلام) کی مثال ہے جسے مٹی سے بنا کر کے کہہ دیا کہ ہو جا! پس وه ہو گیا!'),
(353, 3, 60, 'تیرے رب کی طرف سے حق یہی ہے خبردار شک کرنے والوں میں نہ ہونا'),
(354, 3, 61, 'اس لئے جو شخص آپ کے پاس اس علم کے آجانے کے بعد بھی آپ سے اس میں جھگڑے تو آپ کہہ دیں کہ آو ہم تم اپنے اپنے فرزندوں کو اور ہم تم اپنی اپنی عورتوں کواور ہم تم خاص اپنی اپنی جانوں کو بلالیں، پھر ہم عاجزی کے ساتھ التجا کریں اور جھوٹوں پر اللہ کی لعنت کریں'),
(355, 3, 62, 'یقیناً صرف یہی سچا بیان ہے اور کوئی معبود برحق نہیں بجز اللہ تعالیٰ کے اور بے شک غالب اور حکمت واﻻ اللہ تعالیٰ ہی ہے'),
(356, 3, 63, 'پھر بھی اگر قبول نہ کریں تو اللہ تعالیٰ بھی صحیح طور پر فسادیوں کو جاننے واﻻ ہے'),
(357, 3, 64, 'آپ کہہ دیجئے کہ اے اہل کتاب! ایسی انصاف والی بات کی طرف آو جو ہم میں تم میں برابر ہے کہ ہم اللہ تعالیٰ کے سوا کسی کی عبادت نہ کریں نہ اس کے ساتھ کسی کو شریک بنائیں، نہ اللہ تعالیٰ کو چھوڑ کر آپس میں ایک دوسرے کو ہی رب بنائیں۔ پس اگر وه منھ پھیر لیں تو تم کہہ دو کہ گواه رہو ہم تو مسلمان ہیں'),
(358, 3, 65, 'اے اہل کتاب! تم ابراہیم کی بابت کیوں جھگڑتے ہو حاﻻنکہ تورات وانجیل تو ان کے بعد نازل کی گئیں، کیا تم پھر بھی نہیں سمجھتے؟'),
(359, 3, 66, 'سنو! تم لوگ اس میں جھگڑ چکے جس کا تمہیں علم تھا پھر اب اس بات میں کیوں جھگڑتے ہو جس کا تمہیں علم ہی نہیں؟ اور اللہ تعالیٰ جانتا ہے اور تم نہیں جانتے،'),
(360, 3, 67, 'ابراہیم تو نہ یہودی تھے نہ نصرانی تھے بلکہ وه تو یک طرفہ (خالص) مسلمان تھے، وه مشرک بھی نہیں تھے،'),
(361, 3, 68, 'سب لوگوں سے زیاده ابراہیم سے نزدیک تر وه لوگ ہیں جنہوں نے ان کا کہا مانا اور یہ نبی اور جو لوگ ایمان ﻻئے، مومنوں کا ولی اور سہارا اللہ ہی ہے،'),
(362, 3, 69, 'اہل کتاب کی ایک جماعت چاہتی ہے کہ تمہیں گمراه کر دیں۔ دراصل وه خود اپنے آپ کو گمراه کر رہے ہیں اور سمجھتے نہیں'),
(363, 3, 70, 'اے اہل کتاب تم (باوجود قائل ہونے کے پھر بھی) دانستہ اللہ کی آیات کا کیوں کفر کر رہے ہو؟'),
(364, 3, 71, 'اے اہل کتاب! باوجود جاننے کے حق وباطل کو کیوں خلط ملط کر رہے ہو اور کیوں حق کو چھپا رہے ہو؟'),
(365, 3, 72, 'اور اہل کتاب کی ایک جماعت نے کہا کہ جو کچھ ایمان والوں پر اتارا گیا ہے اس پر دن چڑھے تو ایمان ﻻؤ اور شام کے وقت کافر بن جاؤ، تاکہ یہ لوگ بھی پلٹ جائیں'),
(366, 3, 73, 'اور سوائے تمہارے دین پر چلنے والوں کے اور کسی کا یقین نہ کرو۔ آپ کہہ دیجئے کہ بے شک ہدایت تو اللہ ہی کی ہدایت ہے (اور یہ بھی کہتے ہیں کہ اس بات کا بھی یقین نہ کرو) کہ کوئی اس جیسا دیا جائے جیسا تم دیئے گئے ہو، یا یہ کہ یہ تم سے تمہارے رب کے پاس جھگڑا کریں گے، آپ کہہ دیجئے کہ فضل تو اللہ تعالیٰ ہی کے ہاتھ میں ہے، وه جسے چاہے اسے دے، اللہ تعالیٰ وسعت واﻻ اورجاننے واﻻ ہے'),
(367, 3, 74, 'وه اپنی رحمت کے ساتھ جسے چاہے مخصوص کر لے اور اللہ تعالیٰ بڑے فضل واﻻ ہے'),
(368, 3, 75, 'بعض اہل کتاب تو ایسے ہیں کہ اگر انہیں تو خزانے کا امین بنا دے تو بھی وه تجھے واپس کر دیں اور ان میں سے بعض ایسے بھی ہیں کہ اگر تو انہیں ایک دینار بھی امانت دے تو تجھے ادا نہ کریں۔ ہاں یہ اوربات ہے کہ تو اس کے سر پر ہی کھڑا رہے، یہ اس لئے کہ انہوں نے کہہ رکھا ہے کہ ہم پر ان جاہلوں (غیر یہودی) کے حق کاکوئی گناه نہیں، یہ لوگ باوجود جاننے کے اللہ تعالیٰ پر جھوٹ کہتے ہیں'),
(369, 3, 76, 'کیوں نہیں (مواخذہ ہوگا) البتہ جو شخص اپنا قرار پورا کرے اور پرہیزگاری کرے، تو اللہ تعالیٰ بھی ایسے پرہیز گاروں سے محبت کرتاہے'),
(370, 3, 77, 'بے شک جو لوگ اللہ تعالیٰ کے عہد اور اپنی قسموں کو تھوڑی قیمت پر بیچ ڈالتے ہیں، ان کے لئے آخرت میں کوئی حصہ نہیں، اللہ تعالیٰ نہ تو ان سے بات چیت کرے گا نہ ان کی طرف قیامت کے دن دیکھے گا، نہ انہیں پاک کرے گا اور ان کے لئے دردناک عذاب ہے'),
(371, 3, 78, 'یقیناًان میں ایسا گروه بھی ہے جو کتاب پڑھتے ہوئے اپنی زبان مروڑتا ہے تاکہ تم اسے کتاب ہی کی عبارت خیال کرو حاﻻنکہ دراصل وه کتاب میں سے نہیں، اور یہ کہتے بھی ہیں کہ وه اللہ تعالیٰ کی طرف سے ہے حاﻻنکہ در اصل وه اللہ تعالی کی طرف سے نہیں، وه تو دانستہ اللہ تعالیٰ پر جھوٹ بولتے ہیں'),
(372, 3, 79, 'کسی ایسے انسان کو جسے اللہ تعالیٰ کتاب وحکمت اور نبوت دے، یہ ﻻئق نہیں کہ پھر بھی وه لوگوں سے کہے کہ تم اللہ تعالیٰ کو چھوڑ کر میرے بندے بن جاؤ، بلکہ وه تو کہے گا کہ تم سب رب کے ہو جاؤ، تمہارے کتاب سکھانے کے باعﺚ اور تمہارے کتاب پڑھنے کے سبب۔'),
(373, 3, 80, 'اور یہ نہیں (ہو سکتا) کہ وه تمہیں فرشتوں اورنبیوں کو رب بنا لینے کا حکم کرے، کیا وه تمہارے مسلمان ہونے کے بعد بھی تمہیں کفر کا حکم دے گا'),
(374, 3, 81, 'جب اللہ تعالیٰ نے نبیوں سے عہد لیا کہ جو کچھ میں تمہیں کتاب وحکمت دوں پھر تمہارے پاس وه رسول آئے جو تمہارے پاس کی چیز کو سچ بتائے تو تمہارے لئے اس پر ایمان ﻻنا اور اس کی مدد کرنا ضروری ہے۔ فرمایا کہ تم اس کے اقراری ہو اور اس پر میرا ذمہ لے رہے ہو؟ سب نے کہا کہ ہمیں اقرار ہے، فرمایا تو اب گواه رہو اور خود میں بھی تمہارے ساتھ گواہوں میں ہوں'),
(375, 3, 82, 'پس اس کے بعد بھی جو پلٹ جائیں وه یقیناً پورے نافرمان ہیں۔'),
(376, 3, 83, 'کیا وه اللہ تعالیٰ کے دین کے سوا اور دین کی تلاش میں ہیں؟ حاﻻنکہ تمام آسمانوں والے اور سب زمین والے اللہ تعالیٰ ہی کے فرمانبردار ہیں خوشی سے ہوں یا ناخوشی سے، سب اسی کی طرف لوٹائے جائیں گے'),
(377, 3, 84, 'آپ کہہ دیجئے کہ ہم اللہ تعالیٰ پر اور جو کچھ ہم پر اتارا گیا ہے اور جو کچھ ابراہیم (علیہ السلام) اور اسماعیل (علیہ السلام) اور یعقوب (علیہ السلام) اوران کی اوﻻد پر اتارا گیا اور جو کچھ موسیٰ وعیسیٰ (علیہما السلام) اور دوسرے انبیاء (علیہم السلام) اللہ تعالیٰ کی طرف سے دیئے گئے، ان سب پر ایمان ﻻئے، ہم ان میں سے کسی کے درمیان فرق نہیں کرتے اور ہم اللہ تعالیٰ کے فرمانبردار ہیں'),
(378, 3, 85, 'جو شخص اسلام کے سوا اور دین تلاش کرے، اس کا دین قبول نہ کیا جائے گا اور وه آخرت میں نقصان پانے والوں میں ہوگا'),
(379, 3, 86, 'اللہ تعالیٰ ان لوگوں کو کیسے ہدایت دے گا جو اپنے ایمان ﻻنے اور رسول کی حقانیت کی گواہی دینے اور اپنے پاس روشن دلیلیں آجانے کے بعد کافر ہو جائیں، اللہ تعالیٰ ایسے بے انصاف لوگوں کو راه راست پرنہیں ﻻتا'),
(380, 3, 87, 'ان کی تو یہی سزا ہے کہ ان پر اللہ تعالیٰ کی اور فرشتوں کی اور تمام لوگوں کی لعنت ہو'),
(381, 3, 88, 'جس میں یہ ہمیشہ پڑے رہیں گے، نہ تو ان سے عذاب ہلکا کیا جائے گا نہ انہیں مہلت دی جائے گی'),
(382, 3, 89, 'مگرجو لوگ اس کے بعد توبہ اور اصلاح کر لیں تو بے شک اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(383, 3, 90, 'بےشک جو لوگ اپنے ایمان ﻻنے کے بعد کفر کریں پھر کفر میں بڑھ جائیں، ان کی توبہ ہرگز ہرگز قبول نہ کی جائے گی، یہی گمراه لوگ ہیں'),
(384, 3, 91, 'ہاں جو لوگ کفر کریں اور مرتے دم تک کافر رہیں ان میں سے کوئی اگر زمین بھر سونا دے، گو فدیئے میں ہی ہو تو بھی ہرگز قبول نہ کیا جائے گا۔ یہی لوگ ہیں جن کے لئے تکلیف دینے واﻻ عذاب ہے اور جن کا کوئی مددگار نہیں'),
(385, 3, 92, 'جب تک تم اپنی پسندیده چیز سے اللہ تعالیٰ کی راه میں خرچ نہ کروگے ہرگز بھلائی نہ پاؤ گے، اور تم جو خرچ کرو اسے اللہ تعالیٰ بخوبی جانتا ہے'),
(386, 3, 93, 'توراة کے نزول سے پہلے (حضرت) یعقوب (علیہ السلام) نے جس چیز کو اپنے اوپر حرام کر لیا تھا اس کے سوا تمام کھانے بنی اسرائیل پر حلال تھے، آپ کہہ دیجئے کہ اگر تم سچے ہو تو توراة لے آو اور پڑھ کر سناؤ'),
(387, 3, 94, 'اس کے بعد بھی جو لوگ اللہ تعالیٰ پر جھوٹ بہتان باندھیں وہی ﻇالم ہیں'),
(388, 3, 95, 'کہہ دیجئے کہ اللہ تعالیٰ سچا ہے تم سب ابراہیم حنیف کے ملت کی پیروی کرو، جو مشرک نہ تھے'),
(389, 3, 96, 'اللہ تعالیٰ کا پہلا گھر جو لوگوں کے لئے مقرر کیا گیا وہی ہے جو مکہ (شریف) میں ہے جو تمام دنیا کے لئے برکت وہدایت واﻻ ہے'),
(390, 3, 97, 'جس میں کھلی کھلی نشانیاں ہیں، مقام ابراہیم ہے، اس میں جو آ جائے امن واﻻ ہو جاتا ہے اللہ تعالیٰ نے ان لوگوں پر جو اس کی طرف راه پا سکتے ہوں اس گھر کا حج فرض کر دیا گیا ہے۔ اور جو کوئی کفر کرے تو اللہ تعالیٰ (اس سے بلکہ) تمام دنیا سے بے پرواه ہے'),
(391, 3, 98, 'آپ کہہ دیجئے کہ اے اہل کتاب تم اللہ تعالیٰ کی آیتوں کے ساتھ کفر کیوں کرتے ہو؟ جو کچھ تم کرتے ہو، اللہ تعالیٰ اس پر گواه ہے'),
(392, 3, 99, 'ان اہل کتاب سے کہو کہ تم اللہ تعالیٰ کی راه سے لوگوں کو کیوں روکتے ہو؟ اور اس میں عیب ٹٹولتے ہو، حاﻻنکہ تم خود شاہد ہو، اللہ تعالیٰ تمہارے اعمال سے بے خبر نہیں'),
(393, 3, 100, 'اے ایمان والو! اگر تم اہل کتاب کی کسی جماعت کی باتیں مانو گے تو وه تمہیں تمہارے ایمان ﻻنے کے بعد مرتد کافر بنا دیں گے۔'),
(394, 3, 101, '﴿گو یہ ظاہر ہے کہ﴾ تم کیسے کفر کر سکتے ہو؟ باوجود یہ کہ تم پر اللہ تعالیٰ کی آیتیں پڑھی جاتی ہیں اور تم میں رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ موجود ہیں۔ جو شخص اللہ تعالیٰ (کے دین) کو مضبوط تھام لے تو بلاشبہ اسے راه راست دکھادی گئی'),
(395, 3, 102, 'اے ایمان والو! اللہ تعالیٰ سے اتنا ڈرو جتنا اس سے ڈرنا چاہئیے اور دیکھو مرتے دم تک مسلمان ہی رہنا'),
(396, 3, 103, 'اللہ تعالیٰ کی رسی کو سب مل کر مضبوط تھام لو اور پھوٹ نہ ڈالو، اور اللہ تعالیٰ کی اس وقت کی نعمت کو یاد کرو جب تم ایک دوسرے کے دشمن تھے، تو اس نے تمہارے دلوں میں الفت ڈال دی، پس تم اس کی مہربانی سے بھائی بھائی ہوگئے، اور تم آگ کے گڑھے کے کنارے پہنچ چکے تھے تو اس نے تمہیں بچا لیا۔ اللہ تعالیٰ اسی طرح تمہارے لئے اپنی نشانیاں بیان کرتا ہے تاکہ تم ہدایت پاؤ'),
(397, 3, 104, 'تم میں سے ایک جماعت ایسی ہونی چاہئے جو بھلائی کی طرف بلائے اور نیک کاموں کا حکم کرے اور برے کاموں سے روکے، اور یہی لوگ فلاح ونجات پانے والے ہیں'),
(398, 3, 105, 'تم ان لوگوں کی طرح نہ ہو جانا جنہوں نے اپنے پاس روشن دلیلیں آ جانے کے بعد بھی تفرقہ ڈاﻻ، اور اختلاف کیا، انہیں لوگوں کے لئے بڑا عذاب ہے'),
(399, 3, 106, 'جس دن بعض چہرے سفید ہوں گے اور بعض سیاه، سیاه چہرے والوں (سے کہا جائے گا) کہ کیا تم نے ایمان ﻻنے کے بعد کفر کیا؟ اب اپنے کفر کا عذاب چکھو'),
(400, 3, 107, 'اور سفید چہرے والے اللہ تعالیٰ کی رحمت میں داخل ہوں گے اور اس میں ہمیشہ رہیں گے'),
(401, 3, 108, 'اے نبی! ہم ان حقانی آیتوں کی تلاوت آپ پر کر رہے ہیں اور اللہ تعالیٰ کا اراده لوگوں پر ﻇلم کرنے کا نہیں'),
(402, 3, 109, 'اللہ تعالیٰ ہی کے لئے ہے جو کچھ آسمانوں اور زمین میں ہے اور اللہ تعالیٰ ہی کی طرف تمام کام لوٹائے جاتے ہیں'),
(403, 3, 110, 'تم بہترین امت ہو جو لوگوں کے لئے پیدا کی گئی ہے کہ تم نیک باتوں کا حکم کرتے ہو اور بری باتوں سے روکتے ہو، اور اللہ تعالیٰ پر ایمان رکھتے ہو، اگر اہل کتاب بھی ایمان ﻻتے تو ان کے لئے بہتر تھا، ان میں ایمان والے بھی ہیں لیکن اکثر تو فاسق ہیں'),
(404, 3, 111, 'یہ تمہیں ستانے کے سوا اور زیاده کچھ ضرر نہیں پہنچا سکتے، اگر لڑائی کا موقعہ آجائے تو پیٹھ موڑ لیں گے، پھر مدد نہ کیے جائیں گے'),
(405, 3, 112, 'ان پر ہر جگہ ذلت کی مار پڑی، اﻻّ یہ کہ اللہ تعالیٰ کی یا لوگوں کی پناه میں ہوں، یہ غضب الٰہی کے مستحق ہو گئے اور ان پر فقیری ڈال دی گئی، یہ اس لئے کہ یہ لوگ اللہ تعالیٰ کی آیتوں سے کفر کرتے تھے اور بے وجہ انبیا کو قتل کرتے تھے، یہ بدلہ ہے ان کی نافرمانیوں اور زیادتیوں کا'),
(406, 3, 113, 'یہ سارے کے سارے یکساں نہیں بلکہ ان اہل کتاب میں ایک جماعت (حق پر) قائم رہنے والی بھی ہے جو راتوں کے وقت بھی کلام اللہ کی تلاوت کرتے ہیں اور سجدے بھی کرتے ہیں'),
(407, 3, 114, 'یہ اللہ تعالیٰ پر اورقیامت کے دن پر ایمان بھی رکھتے ہیں، بھلائیوں کا حکم کرتے ہیں اور برائیوں سے روکتے ہیں اور بھلائی کے کاموں میں جلدی کرتے ہیں۔ یہ نیک بخت لوگوں میں سے ہیں'),
(408, 3, 115, 'یہ جو کچھ بھی بھلائیاں کریں ان کی ناقدری نہ کی جائے گی اور اللہ تعالیٰ پرہیزگاروں کو خوب جانتا ہے'),
(409, 3, 116, 'کافروں کو ان کے مال اور ان کی اوﻻد اللہ کے ہاں کچھ کام نہ آئیں گی، یہ تو جہنمی ہیں جو ہمیشہ اسی میں پڑے رہیں گے'),
(410, 3, 117, 'یہ کفار جو خرچ اخراجات کریں اس کی مثال یہ ہے کہ ایک تند ہوا چلی جس سے پاﻻ تھا جو ﻇالموں کی کھیتی پر پڑا اور اسے تہس نہس کر دیا۔ اللہ تعالیٰ نے ان پر ﻇلم نہیں کیا بلکہ وه خود اپنی جانوں پر ﻇلم کرتے تھے'),
(411, 3, 118, 'اے ایمان والو! تم اپنا دلی دوست ایمان والوں کے سوا اور کسی کو نہ بناؤ۔ (تم تو) نہیں دیکھتے دوسرے لوگ تمہاری تباہی میں کوئی کسر اٹھا نہیں رکھتے، وه تو چاہتے ہیں کہ تم دکھ میں پڑو ان کی عداوت تو خود ان کی زبان سے بھی ﻇاہر ہو چکی ہے اور جو ان کے سینوں میں پوشیده ہے وه بہت زیاده ہے، ہم نے تمہارے لئے آیتیں بیان کر دیں'),
(412, 3, 119, 'اگر عقلمند ہو (تو غور کرو) ہاں تم تو انہیں چاہتے ہو اور وه تم سے محبت نہیں رکھتے، تم پوری کتاب کو مانتے ہو، (وه نہیں مانتے پھر محبت کیسی؟) یہ تمہارے سامنے تو اپنے ایمان کا اقرار کرتے ہیں لیکن تنہائی میں مارے غصہ کے انگلیاں چباتے ہیں کہہ دو کہ اپنے غصہ ہی میں مر جاؤ، اللہ تعالیٰ دلوں کے راز کو بخوبی جانتا ہے'),
(413, 3, 120, 'تمہیں اگر بھلائی ملے تو یہ ناخوش ہوتے ہیں ہاں! اگر برائی پہنچے تو خوش ہوتے ہیں، تم اگر صبر کرو اور پرہیزگاری کرو تو ان کا مکر تمہیں کچھ نقصان نہ دے گا۔ اللہ تعالیٰ نے انکے اعمال کا احاطہ کر رکھا ہے'),
(414, 3, 121, 'اے نبی! اس وقت کو بھی یاد کرو جب صبح ہی صبح آپ اپنے گھر سے نکل کر مسلمانوں کو میدان جنگ میں لڑائی کے مورچوں پر باقاعده بٹھا رہے تھے اللہ تعالیٰ سننے جاننے واﻻ ہے'),
(415, 3, 122, 'جب تمہاری دو جماعتیں پست ہمتی کا اراده کر چکی تھیں، اللہ تعالیٰ انکا ولی اور مددگار ہے۔ اور اسی کی پاک ذات پر مومنوں کو بھروسہ رکھنا چاہئے'),
(416, 3, 123, 'جنگ بدر میں اللہ تعالیٰ نے عین اس وقت تمہاری مدد فرمائی تھی جب کہ تم نہایت گری ہوئی حالت میں تھے، اس لئے اللہ ہی سے ڈرو! (نہ کسی اور سے) تاکہ تمہیں شکر گزاری کی توفیق ہو'),
(417, 3, 124, '(اور یہ شکر گزاری باعﺚ نصرت وامداد ہو) جب آپ مومنوں کو تسلی دے رہے تھے، کیا آسمان سے تین ہزار فرشتے اتار کر اللہ تعالیٰ کا تمہاری مدد کرنا تمہیں کافی نہ ہوگا،'),
(418, 3, 125, 'کیوں نہیں، بلکہ اگر تم صبر و پرہیزگاری کرو اور یہ لوگ اسی دم تمہارے پاس آجائیں تو تمہارا رب تمہاری امداد پانچ ہزار فرشتوں سے کرے گا جو نشاندار ہوں گے'),
(419, 3, 126, 'اور یہ تو محض تمہارے دل کی خوشی اور اطمینان قلب کے لئے ہے، ورنہ مدد تو اللہ ہی کی طرف سے ہے جو غالب اور حکمتوں واﻻ ہے'),
(420, 3, 127, '(اس امداد الٰہی کا مقصد یہ تھا کہ اللہ) کافروں کی ایک جماعت کو کاٹ دے یا انہیں ذلیل کر ڈالے اور (سارے کے سارے) نامراد ہو کر واپس چلے جائیں'),
(421, 3, 128, 'اے پیغمبر! آپ کے اختیار میں کچھ نہیں، اللہ تعالیٰ چاہے تو ان کی توبہ قبول کرے یا عذاب دے، کیونکہ وه ﻇالم ہیں'),
(422, 3, 129, 'آسمانوں اور زمین میں جو کچھ ہے سب اللہ ہی کا ہے، وه جسے چاہے بخشے جسے چاہے عذاب کرے، اللہ تعالیٰ بخشش کرنے واﻻ مہربان ہے'),
(423, 3, 130, 'اے ایمان والو! بڑھا چڑھا کر سود نہ کھاؤ، اور اللہ تعالیٰ سے ڈرو تاکہ تمہیں نجات ملے'),
(424, 3, 131, 'اور اس آگ سے ڈرو جو کافروں کے لئے تیار کی گئی ہے'),
(425, 3, 132, 'اور اللہ اور اس کے رسول کی فرمانبرداری کرو تاکہ تم پر رحم کیا جائے'),
(426, 3, 133, 'اور اپنے رب کی بخشش کی طرف اور اس جنت کی طرف دوڑو جس کا عرض آسمانوں اور زمین کے برابر ہے، جو پرہیزگاروں کے لئے تیار کی گئی ہے'),
(427, 3, 134, 'جو لوگ آسانی میں سختی کے موقع پر بھی اللہ کے راستے میں خرچ کرتے ہیں، غصہ پینے والے اور لوگوں سے درگزر کرنے والے ہیں، اللہ تعالیٰ ان نیک کاروں سے محبت کرتا ہے'),
(428, 3, 135, 'جب ان سے کوئی ناشائستہ کام ہو جائے یا کوئی گناه کر بیٹھیں تو فوراً اللہ کا ذکر اور اپنے گناہوں کے لئے استغفار کرتے ہیں، فیالواقع اللہ تعالیٰ کے سوا اور کون گناہوں کو بخش سکتا ہے؟ اور وه لوگ باوجود علم کے کسی برے کام پر اڑ نہیں جاتے'),
(429, 3, 136, 'انہیں کا بدلہ ان کے رب کی طرف سے مغفرت ہے اور جنتیں ہیں جن کے نیچے نہریں بہتی ہیں، جن میں وه ہمیشہ رہیں گے، ان نیک کاموں کے کرنے والوں کا ﺛواب کیا ہی اچھا ہے'),
(430, 3, 137, 'تم سے پہلے بھی ایسے واقعات گزر چکے ہیں، سو زمین میں چل پھر کر دیکھ لو کہ (آسمانی تعلیم کے) جھٹلانے والوں کا کیا انجام ہوا؟'),
(431, 3, 138, 'عام لوگوں کے لئے تو یہ (قرآن) بیان ہے اور پرہیزگاروں کے لئے ہدایت ونصیحت ہے'),
(432, 3, 139, 'تم نہ سستی کرو اور غمگین ہو، تم ہی غالب رہو گے، اگر تم ایماندار ہو'),
(433, 3, 140, 'اگر تم زخمی ہوئے ہو تو تمہارے مخالف لوگ بھی تو ایسے ہی زخمی ہو چکے ہیں، ہم ان دنوں کو لوگوں کے درمیان ادلتے بدلتے رہتے ہیں۔ (شکست احد) اس لئے تھی کہ اللہ تعالیٰ ایمان والوں کو ﻇاہر کردے اور تم میں سے بعض کو شہادت کا درجہ عطا فرمائے، اللہ تعالیٰ ﻇالموں سے محبت نہیں کرتا'),
(434, 3, 141, '(یہ وجہ بھی تھی) کہ اللہ تعالیٰ ایمان والوں کو بالکل الگ کر دے اور کافروں کو مٹا دے'),
(435, 3, 142, 'کیا تم یہ سمجھ بیٹھے ہو کہ تم جنت میں چلے جاؤ گے، حاﻻنکہ اب تک اللہ تعالیٰ نے یہ ﻇاہر نہیں کیا کہ تم سے جہاد کرنے والے کون ہیں اور صبر کرنے والے کون ہیں؟'),
(436, 3, 143, 'جنگ سے پہلے تو تم شہادت کی آرزو میں تھے اب اسے اپنی آنکھوں سے اپنے سامنے دیکھ لیا'),
(437, 3, 144, '(حضرت) محمد ﴿صلی اللہ علیہ وسلم﴾ صرف رسول ہی ہیں، ان سے پہلے بہت سے رسول ہو چکے ہیں، کیا اگر ان کا انتقال ہو جائے یا یہ شہید ہو جائیں، تو تم اسلام سے اپنی ایڑیوں کے بل پھر جاؤ گے؟ اور جو کوئی پھر جائے اپنی ایڑیوں پر تو ہرگز اللہ تعالیٰ کا کچھ نہ بگاڑے گا، عنقریب اللہ تعالیٰ شکر گزاروں کو نیک بدلہ دے گا'),
(438, 3, 145, 'بغیر اللہ تعالیٰ کے حکم کے کوئی جاندار نہیں مرسکتا، مقرر شده وقت لکھا ہوا ہے، دنیا کی چاہت والوں کو ہم کچھ دنیا دے دیتے ہیں اور آخرت کا ﺛواب چاہنے والوں کو ہم وه بھی دیں گے۔ اوراحسان ماننے والوں کو ہم بہت جلد نیک بدلہ دیں گے'),
(439, 3, 146, 'بہت سے نبیوں کے ہم رکاب ہو کر، بہت سے اللہ والے جہاد کر چکے ہیں، انہیں بھی اللہ کی راه میں تکلیفیں پہنچیں لیکن نہ تو انہوں نے ہمت ہاری نہ سست رہے اور نہ دبے، اور اللہ صبر کرنے والوں کو (ہی) چاہتا ہے'),
(440, 3, 147, 'وه یہی کہتے رہے کہ اے پروردگار! ہمارے گناہوں کو بخش دے اور ہم سے ہمارے کاموں میں جو بے جا زیادتی ہوئی ہے اسے بھی معاف فرما اور ہمیں ﺛابت قدمی عطا فرما اور ہمیں کافروں کی قوم پر مدد دے'),
(441, 3, 148, 'اللہ تعالیٰ نے انہیں دنیا کا ﺛواب بھی دیا اور آخرت کے ﺛواب کی خوبی بھی عطا فرمائی اور اللہ تعالیٰ نیک لوگوں سے محبت کرتا ہے'),
(442, 3, 149, 'اے ایمان والو! اگر تم کافروں کی باتیں مانو گے تو وه تمہیں تمہاری ایڑیوں کے بل پلٹا دیں گے، (یعنی تمہیں مرتد بنا دیں گے) پھر تم نامراد ہو جاؤ گے'),
(443, 3, 150, 'بلکہ اللہ ہی تمہارا موﻻ ہے اور وہی بہترین مددگار ہے'),
(444, 3, 151, 'ہم عنقریب کافروں کے دلوں میں رعب ڈال دیں گے، اس وجہ سے کہ یہ اللہ کے ساتھ ان چیزوں کو شریک کرتے ہیں جس کی کوئی دلیل اللہ نے نہیں اتاری، ان کا ٹھکانہ جہنم ہے، اور ان ﻇالموں کی بری جگہ ہے'),
(445, 3, 152, 'اللہ تعالیٰ نے تم سے اپنا وعده سچا کر دکھایا جبکہ تم اس کے حکم سے انہیں کاٹ رہے تھے۔ یہاں تک کہ جب تم نے پست ہمتی اختیار کی اور کام میں جھگڑنے لگے اور نافرمانی کی، اس کے بعد کہ اس نے تمہاری چاہت کی چیز تمہیں دکھا دی، تم میں سے بعض دنیا چاہتے تھے اور بعض کا اراده آخرت کا تھا تو پھر اس نے تمہیں ان سے پھیر دیا تاکہ تم کو آزمائے اور یقیناً اس نے تمہاری لغزش سے درگزر فرما دیا اور ایمان والوں پر اللہ تعالیٰ بڑے فضل واﻻ ہے'),
(446, 3, 153, 'جب کہ تم چڑھے چلے جا رہے تھے اور کسی طرف توجہ تک نہیں کرتے تھے اور اللہ کے رسول تمہیں تمہارے پیچھے سے آوازیں دے رہے تھے، بس تمہیں غم پر غم پہنچا تاکہ تم فوت شده چیز پر غمگین نہ ہو اور نہ پہنچنے والی (تکلیف) پر اداس ہو، اللہ تعالیٰ تمہارے تمام اعمال سے خبردار ہے'),
(447, 3, 154, 'پھر اس نے اس غم کے بعد تم پر امن نازل فرمایا اور تم میں سے ایک جماعت کو امن کی نیند آنے لگی۔ ہاں کچھ وه لوگ بھی تھے کہ انہیں اپنی جانوں کی پڑی ہوئی تھی، وه اللہ تعالیٰ کے ساتھ ناحق جہالت بھری بدگمانیاں کر رہے تھے اور کہتے تھے کیا ہمیں بھی کسی چیز کا اختیار ہے؟ آپ کہہ دیجئے کہ کام کل کا کل اللہ کے اختیار میں ہے، یہ لوگ اپنے دلوں کے بھید آپ کو نہیں بتاتے، کہتے ہیں کہ اگر ہمیں کچھ بھی اختیار ہوتا تو یہاں قتل نہ کئے جاتے۔ آپ کہہ دیجئے کہ گو تم اپنے گھروں میں ہوتے پھر بھی جن کی قمست میں قتل ہونا تھا وه تو مقتل کی طرف چل کھڑے ہوتے، اللہ تعالیٰ کو تمہارے سینوں کے اندر کی چیز کا آزمانا اور جو کچھ تمہارے دلوں میں ہے، اس کو پاک کرنا تھا، اور اللہ تعالی سینوں کے بھید سے آگاه ہے'),
(448, 3, 155, 'تم میں سے جن لوگوں نے اس دن پیٹھ دکھائی جس دن دونوں جماعتوں کی مدبھیڑ ہوئی تھی یہ لوگ اپنے بعض کرتوتوں کے باعﺚ شیطان کے پھسلانے میں آگئے لیکن یقین جانو کہ اللہ تعالیٰ نے انہیں معاف کر دیا اللہ تعالیٰ ہے بخشنے واﻻ اور تحمل واﻻ'),
(449, 3, 156, 'اے ایمان والو! تم ان لوگوں کی طرح نہ ہو جانا جنہوں نے کفر کیا اور اپنے بھائیوں کے حق میں جب کہ وه سفر میں ہوں یا جہاد میں ہوں، کہا کہ اگر یہ ہمارے پاس ہوتے تو نہ مرتے اور نہ مارے جاتے، اس کی وجہ یہ تھی کہ اس خیال کو اللہ تعالیٰ ان کی دلی حسرت کا سبب بنا دے، اللہ تعالیٰ جلاتا ہے اور مارتا ہے اور اللہ تمہارے عمل کو دیکھ رہا ہے'),
(450, 3, 157, 'قسم ہے اگر اللہ تعالیٰ کی راه میں شہید کئے جاؤ یا اپنی موت مرو تو بے شک اللہ تعالیٰ کی بخشش ورحمت اس سے بہتر ہے جسے یہ جمع کر رہے ہیں'),
(451, 3, 158, 'بالیقین خواہ تم مر جاؤ یا مار ڈالے جاؤ جمع تو اللہ تعالیٰ کی طرف ہی کئے جاؤ گے۔'),
(452, 3, 159, 'اللہ تعالیٰ کی رحمت کے باعﺚ آپ ان پر نرم دل ہیں اور اگر آپ بد زبان اور سخت دل ہوتے تو یہ سب آپ کے پاس سے چھٹ جاتے، سو آپ ان سے درگزر کریں اور ان کے لئے استغفار کریں اور کام کا مشوره ان سے کیا کریں، پھر جب آپ کا پختہ اراده ہو جائے تو اللہ تعالیٰ پر بھروسہ کریں، بے شک اللہ تعالیٰ توکل کرنے والوں سے محبت کرتا ہے'),
(453, 3, 160, 'اگر اللہ تعالیٰ تمہاری مدد کرے تو تم پر کوئی غالب نہیں آسکتا اور اگر وہ تمہیں چھوڑ دے تو اس کے بعد کون ہے جو تمہاری مدد کرے؟ ایمان والوں کو اللہ تعالیٰ ہی پر بھروسہ رکھنا چاہئے'),
(454, 3, 161, 'ناممکن ہے کہ نبی سے خیانت ہو جائے ہر خیانت کرنے واﻻ خیانت کو لئے ہوئے قیامت کے دن حاضر ہوگا، پھر ہر شخص کو اپنے اعمال کا پورا پورا بدلہ دیا جائے گا، اور وه ﻇلم نہ کئے جائیں گے'),
(455, 3, 162, 'کیا پس وه شخص جو اللہ تعالیٰ کی خوشنودی کے درپے ہے، اس شخص جیسا ہے جو اللہ تعالیٰ کی ناراضگی لے کر لوٹتا ہے؟ اور جس کی جگہ جہنم ہے جو بدترین جگہ ہے'),
(456, 3, 163, 'اللہ تعالیٰ کے پاس ان کے الگ الگ درجے ہیں اور ان کے تمام اعمال کو اللہ بخوبی دیکھ رہا ہے'),
(457, 3, 164, 'بے شک مسلمانوں پر اللہ تعالیٰ کا بڑا احسان ہے کہ ان ہی میں سے ایک رسول ان میں بھیجا، جو انہیں اس کی آیتیں پڑھ کر سناتا ہے اور انہیں پاک کرتا ہے اور انہیں کتاب اور حکمت سکھاتا ہے، یقیناً یہ سب اس سے پہلے کھلی گمراہی میں تھے'),
(458, 3, 165, '(کیا بات ہے) کہ جب تمہیں ایک ایسی تکلیف پہنچی کہ تم اس جیسی دو چند پہنچا چکے، تو یہ کہنے لگے کہ یہ کہاں سے آگئی؟ آپ کہہ دیجئے کہ یہ خود تمہاری طرف سے ہے، بے شک اللہ تعالیٰ ہر چیز پر قادر ہے'),
(459, 3, 166, 'اور تمہیں جو کچھ اس دن پہنچا جس دن دو جماعتوں میں مڈ بھیڑ ہوئی تھی، وه سب اللہ کے حکم سے تھا اور اس لئے کہ اللہ تعالیٰ ایمان والوں کو ﻇاہری طور پر جان لے'),
(460, 3, 167, 'اور منافقوں کو بھی معلوم کر لے جن سے کہا گیا کہ آؤ اللہ کی راه میں جہاد کرو، یا کافروں کو ہٹاو، ٴ تو وه کہنے لگے کہ اگر ہم لڑائی جانتے ہوتے تو ضرور ساتھ دیتے، وه اس دن بہ نسبت ایمان کے کفر سے بہت قریب تھے، اپنے منھ سے وه باتیں بناتے ہیں جو ان کے دلوں میں نہیں، اور اللہ تعالیٰ خوب جانتا ہے جسے وه چھپاتے ہیں'),
(461, 3, 168, 'یہ وه لوگ ہیں جو خود بھی بیٹھے رہے اور اپنے بھائیوں کی بابت کہا کہ اگر وه بھی ہماری بات مان لیتے تو قتل نہ کئے جاتے۔ کہہ دیجئے! کہ اگر تم سچے ہو تو اپنی جانوں سے موت کو ہٹا دو'),
(462, 3, 169, 'جو لوگ اللہ کی راه میں شہید کئے گئے ہیں ان کو ہرگز مرده نہ سمجھیں، بلکہ وه زنده ہیں اپنے رب کے پاس روزیاں دیئے جاتے ہیں'),
(463, 3, 170, 'اللہ تعالیٰ نے اپنا فضل جو انہیں دے رکھا ہے اس سے بہت خوش ہیں اور خوشیاں منا رہے ہیں ان لوگوں کی بابت جو اب تک ان سے نہیں ملے ان کے پیچھے ہیں، اس پر کہ انہیں نہ کوئی خوف ہے اور نہ وه غمگین ہوں گے'),
(464, 3, 171, 'وه خوش ہوتے ہیں اللہ کی نعمت اور فضل سے اور اس سے بھی کہ اللہ تعالیٰ ایمان والوں کے اجر کو برباد نہیں کرتا'),
(465, 3, 172, 'جن لوگوں نے اللہ اور رسول کے حکم کو قبول کیا اس کے بعد کہ انہیں پورے زخم لگ چکے تھے، ان میں سے جنہوں نے نیکی کی اور پرہیزگاری برتی ان کے لئے بہت زیاده اجر ہے'),
(466, 3, 173, 'وه لوگ کہ جب ان سے لوگوں نے کہا کہ کافروں نے تمہارے مقابلے پر لشکر جمع کر لئے ہیں، تم ان سے خوف کھاؤ تو اس بات نے انہیں ایمان میں اور بڑھا دیا اور کہنے لگے ہمیں اللہ کافی ہے اور وه بہت اچھا کارساز ہے'),
(467, 3, 174, '(نتیجہ یہ ہوا کہ) اللہ کی نعمت وفضل کے ساتھ یہ لوٹے، انہیں کوئی برائی نہ پہنچی، انہوں نے اللہ تعالیٰ کی رضامندی کی پیروی کی، اللہ بہت بڑے فضل واﻻ ہے'),
(468, 3, 175, 'یہ خبر دینے واﻻ صرف شیطان ہی ہے جو اپنے دوستوں سے ڈراتا ہے تم ان کافروں سے نہ ڈرو اور میرا خوف رکھو، اگر تم مومن ہو'),
(469, 3, 176, 'کفر میں آگے بڑھنے والے لوگ تجھے غمناک نہ کریں، یقین مانو کہ یہ اللہ تعالیٰ کا کچھ نہ بگاڑ سکیں گے، اللہ تعالیٰ کا اراده ہے کہ ان کے لئے آخرت کا کوئی حصہ عطا نہ کرے، اور ان کے لئے بڑا عذاب ہے'),
(470, 3, 177, 'کفر کو ایمان کے بدلے خریدنے والے ہرگز ہرگز اللہ تعالیٰ کو کوئی نقصان نہیں پہنچا سکتے اور ان ہی کے لئے المناک عذاب ہے'),
(471, 3, 178, 'کافر لوگ ہماری دی ہوئی مہلت کو اپنے حق میں بہتر نہ سمجھیں، یہ مہلت تو اس لئے ہے کہ وه گناہوں میں اور بڑھ جائیں، ان ہی کے لئے ذلیل کرنے واﻻ عذاب ہے'),
(472, 3, 179, 'جس حال میں تم ہو اسی پر اللہ ایمان والوں کو نہ چھوڑ دے گا جب تک کہ پاک اور ناپاک کو الگ الگ نہ کر دے، اور نہ اللہ تعالیٰ ایسا ہے کہ تمہیں غیب سے آگاه کردے، بلکہ اللہ تعالیٰ اپنے رسولوں میں سے جس کا چاہے انتخاب کر لیتا ہے، اس لئے تم اللہ تعالیٰ پر اور اس کے رسولوں پر ایمان رکھو، اگر تم ایمان ﻻؤ اور تقویٰ کرو تو تمہارے لئے بڑا بھاری اجر ہے'),
(473, 3, 180, 'جنہیں اللہ تعالیٰ نے اپنے فضل سے کچھ دے رکھا ہے وه اس میں اپنی کنجوسی کو اپنے لئے بہتر خیال نہ کریں بلکہ وه ان کے لئے نہایت بدتر ہے، عنقریب قیامت والے دن یہ اپنی کنجوسی کی چیز کے طوق ڈالے جائیں گے، آسمانوں اور زمین کی میراث اللہ تعالیٰ ہی کے لئے اور جو کچھ تم کر رہے ہو، اس سے اللہ تعالیٰ آگاه ہے'),
(474, 3, 181, 'یقیناً اللہ تعالیٰ نے ان لوگوں کا قول بھی سنا جنہوں نے کہا کہ اللہ تعالیٰ فقیر ہے اور ہم تونگر ہیں ان کے اس قول کو ہم لکھ لیں گے۔ اور ان کا انبیا کو بلا وجہ قتل کرنا بھی، اور ہم ان سے کہیں گے کہ جلنے واﻻ عذاب چکھو!'),
(475, 3, 182, 'یہ تمہارے پیش کرده اعمال کا بدلہ ہے اور اللہ تعالیٰ اپنے بندوں پر ﻇلم کرنے واﻻ نہیں'),
(476, 3, 183, 'یہ وه لوگ ہیں جنہوں نے کہا کہ اللہ تعالیٰ نے ہمیں حکم دیا ہے کہ کسی رسول کو نہ مانیں جب تک وه ہمارے پاس ایسی قربانی نہ ﻻئے جسے آگ کھا جائے۔ آپ کہہ دیجئے کہ اگر تم سچے ہو تو مجھ سے پہلے تمہارے پاس جو رسول دیگر معجزوں کے ساتھ یہ بھی ﻻئے جسے تم کہہ رہے ہو تو پھر تم نے انہیں کیوں مار ڈاﻻ؟'),
(477, 3, 184, 'پھر بھی اگر یہ لوگ آپ کو جھٹلائیں تو آپ سے پہلے بھی بہت سے رسول جھٹلائے گئے ہیں جو روشن دلیلیں صحیفے اور منور کتاب لے کر آئے'),
(478, 3, 185, 'ہر جان موت کا مزه چکھنے والی ہے اور قیامت کے دن تم اپنے بدلے پورے پورے دیئے جاؤ گے، پس جو شخص آگ سے ہٹا دیا جائے اور جنت میں داخل کر دیا جائے بے شک وه کامیاب ہوگیا، اور دنیا کی زندگی تو صرف دھوکے کی جنس ہے'),
(479, 3, 186, 'یقیناً تمہارے مالوں اور جانوں سے تمہاری آزمائش کی جائے گی اور یہ بھی یقین ہے کہ تمہیں ان لوگوں کی جو تم سے پہلے کتاب دیئے گئے اور مشرکوں کی بہت سی دکھ دینے والی باتیں بھی سننی پڑیں گی اور اگر تم صبر کر لو اور پرہیزگاری اختیار کرو تو یقیناً یہ بہت بڑی ہمت کا کام ہے'),
(480, 3, 187, 'اور اللہ تعالیٰ نے جب اہل کتاب سے عہد لیا کہ تم اسے سب لوگوں سے ضرور بیان کرو گے اور اسے چھپاؤ گے نہیں، تو پھر بھی ان لوگوں نے اس عہد کو اپنی پیٹھ پیچھے ڈال دیا اور اسے بہت کم قیمت پر بیچ ڈاﻻ۔ ان کا یہ بیوپار بہت برا ہے'),
(481, 3, 188, 'وه لوگ جو اپنے کرتوتوں پر خوش ہیں اور چاہتے ہیں کہ جو انہوں نے نہیں کیا اس پر بھی ان کی تعریفیں کی جائیں آپ انہیں عذاب سے چھٹکارا میں نہ سمجھئے ان کے لئے تو دردناک عذاب ہے'),
(482, 3, 189, 'آسمانوں اور زمین کی بادشاہی اللہ ہی کے لئے ہے اور اللہ تعالیٰ ہر چیز پر قادر ہے'),
(483, 3, 190, 'آسمانوں اور زمین کی پیدائش میں اور رات دن کے ہیر پھیر میں یقیناً عقلمندوں کے لئے نشانیاں ہیں'),
(484, 3, 191, 'جو اللہ تعالیٰ کا ذکر کھڑے اور بیٹھے اور اپنی کروٹوں پر لیٹے ہوئے کرتے ہیں اور آسمانوں وزمین کی پیدائش میں غوروفکر کرتے ہیں اور کہتے ہیں اے ہمارے پروردگار! تو نے یہ بے فائده نہیں بنایا، تو پاک ہے پس ہمیں آگ کے عذاب سے بچا لے'),
(485, 3, 192, 'اے ہمارے پالنے والے! تو جسے جہنم میں ڈالے یقیناً تو نے اسے رسوا کیا، اور ﻇالموں کا مددگار کوئی نہیں'),
(486, 3, 193, 'اے ہمارے رب! ہم نے سنا کہ منادی کرنے واﻻ بآواز بلند ایمان کی طرف بلا رہا ہے کہ لوگو! اپنے رب پر ایمان لاؤ، پس ہم ایمان ﻻئے۔ یا الٰہی اب تو ہمارے گناه معاف فرما اور ہماری برائیاں ہم سے دور کر دے اور ہماری موت نیکوں کے ساتھ کر'),
(487, 3, 194, 'اے ہمارے پالنے والے معبود! ہمیں وه دے جس کا وعده تو نے ہم سے اپنے رسولوں کی زبانی کیا ہے اور ہمیں قیامت کے دن رسوا نہ کر، یقیناً تو وعده خلافی نہیں کرتا'),
(488, 3, 195, 'پس ان کے رب نے ان کی دعا قبول فرمالی کہ تم میں سے کسی کام کرنے والے کے کام کو خواه وه مرد ہو یا عورت میں ہرگز ضائع نہیں کرتا، تم آپس میں ایک دوسرے کے ہم جنس ہو، اس لئے وه لوگ جنہوں نے ہجرت کی اور اپنے گھروں سے نکال دیئے گئے اور جنہیں میری راه میں ایذا دی گئی اور جنہوں نے جہاد کیا اور شہید کئے گئے، میں ضرور ضرور ان کی برائیاں ان سے دور کر دوں گا اور بالیقین انہیں ان جنتوں میں لے جاؤں گا جن کے نیچے نہریں بہہ رہی ہیں، یہ ہے ﺛواب اللہ تعالیٰ کی طرف سے اور اللہ تعالیٰ ہی کے پاس بہترین ﺛواب ہے'),
(489, 3, 196, 'تجھے کافروں کا شہروں میں چلنا پھرنا فریب میں نہ ڈال دے،'),
(490, 3, 197, 'یہ تو بہت ہی تھوڑا فائده ہے، اس کے بعد ان کا ٹھکانہ تو جہنم ہے اور وه بری جگہ ہے'),
(491, 3, 198, 'لیکن جو لوگ اپنے رب سے ڈرتے رہے ان کے لئے جنتیں ہیں جن کے نیچے نہریں جاری ہیں، ان میں وه ہمیشہ رہیں گے یہ مہمانی ہے اللہ کی طرف سے اور نیک کاروں کے لئے جو کچھ اللہ تعالیٰ کے پاس ہے وه بہت ہی بہتر ہے'),
(492, 3, 199, 'یقیناًاہل کتاب میں سے بعض ایسے بھی ہیں جو اللہ تعالیٰ پر ایمان ﻻتے ہیں اور تمہاری طرف جو اتارا گیا ہے اور ان کی جانب جو نازل ہوا اس پر بھی، اللہ تعالیٰ سے ڈرتے ہیں اور اللہ تعالیٰ کی آیتوں کو تھوڑی تھوڑی قیمت پر بیچتے بھی نہیں، ان کا بدلہ ان کے رب کے پاس ہے، یقیناً اللہ تعالیٰ جلد حساب لینے واﻻ ہے'),
(493, 3, 200, 'اے ایمان والو! تم ﺛابت قدم رہو اور ایک دوسرے کو تھامے رکھو اور جہاد کے لئے تیار رہو اور اللہ تعالیٰ سے ڈرتے رہو تاکہ تم مراد کو پہنچو');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(494, 4, 1, 'اے لوگو! اپنے پروردگار سے ڈرو، جس نے تمہیں ایک جان سے پیدا کیا اور اسی سے اس کی بیوی کو پیدا کرکے ان دونوں سے بہت سے مرد اور عورتیں پھیلا دیں، اس اللہ سے ڈرو جس کے نام پر ایک دوسرے سے مانگتے ہو اور رشتے ناطے توڑنے سے بھی بچو بے شک اللہ تعالیٰ تم پر نگہبان ہے'),
(495, 4, 2, 'اور یتیموں کو ان کے مال دے دو اور پاک اور حلال چیز کے بدلے ناپاک اور حرام چیز نہ لو، اور اپنے مالوں کے ساتھ ان کے مال ملا کر کھا نہ جاؤ، بے شک یہ بہت بڑا گناه ہے'),
(496, 4, 3, 'اگر تمہیں ڈر ہو کہ یتیم لڑکیوں سے نکاح کرکے تم انصاف نہ رکھ سکو گے تو اور عورتوں میں سے جو بھی تمہیں اچھی لگیں تم ان سے نکاح کر لو، دو دو، تین تین، چار چار سے، لیکن اگر تمہیں برابری نہ کر سکنے کا خوف ہو تو ایک ہی کافی ہے یا تمہاری ملکیت کی لونڈی یہ زیاده قریب ہے، کہ (ایسا کرنے سے ناانصافی اور) ایک طرف جھک پڑنے سے بچ جاؤ'),
(497, 4, 4, 'اور عوتوں کو ان کے مہر راضی خوشی دے دو، ہاں اگر وه خود اپنی خوشی سے کچھ مہر چھوڑ دیں تو اسے شوق سے خوش ہو کر کھا لو'),
(498, 4, 5, 'بے عقل لوگوں کو اپنا مال نہ دے دو جس مال کو اللہ تعالیٰ نے تمہاری گزران کے قائم رکھنے کا ذریعہ بنایا ہے، ہاں انہیں اس مال سے کھلاؤ پلاؤ، پہناؤ، اوڑھاؤ اور انہیں معقولیت سے نرم بات کہو'),
(499, 4, 6, 'اور یتیموں کو ان کے بالﻎ ہو جانے تک سدھارتے اور آزماتے رہو پھر اگر ان میں تم ہوشیاری اور حسن تدبیر پاؤ تو انہیں ان کے مال سونﭗ دو اور ان کے بڑے ہو جانے کے ڈر سے ان کے مالوں کو جلدی جلدی فضول خرچیوں میں تباه نہ کر دو، مال داروں کو چاہئے کہ (ان کے مال سے) بچتے رہیں، ہاں مسکین محتاج ہو تو دستور کے مطابق واجبی طور سے کھالے، پھر جب انہیں ان کے مال سونپو تو گواه بنا لو، دراصل حساب لینے واﻻ اللہ تعالیٰ ہی کافی ہے'),
(500, 4, 7, 'ماں باپ اور خویش واقارب کے ترکہ میں مردوں کا حصہ بھی ہے اور عورتوں کا بھی۔ (جو مال ماں باپ اور خویش واقارب چھوڑ مریں) خواه وه مال کم ہو یا زیاده (اس میں) حصہ مقرر کیا ہوا ہے'),
(501, 4, 8, 'اور جب تقسیم کے وقت قرابت دار اور یتیم اور مسکین آجائیں تو تم اس میں سے تھوڑا بہت انہیں بھی دے دو اور ان سے نرمی سے بولو'),
(502, 4, 9, 'اور چاہئے کہ وه اس بات سے ڈریں کہ اگر وه خود اپنے پیچھے (ننھے ننھے) ناتواں بچے چھوڑ جاتے جن کے ضائع ہو جانے کا اندیشہ رہتا ہے، (تو ان کی چاہت کیا ہوتی) پس اللہ تعالیٰ سے ڈر کر جچی تلی بات کہا کریں'),
(503, 4, 10, 'جو لوگ ناحق ﻇلم سے یتیموں کا مال کھا جاتے ہیں، وه اپنے پیٹ میں آگ ہی بھر رہے ہیں اور عنقریب وه دوزخ میں جائیں گے'),
(504, 4, 11, 'اللہ تعالیٰ تمہیں تمہاری اوﻻد کے بارے میں حکم کرتا ہے کہ ایک لڑکے کا حصہ دو لڑکیوں کے برابر ہے اور اگر صرف لڑکیاں ہی ہوں اور دو سے زیاده ہوں تو انہیں مال متروکہ کا دو تہائی ملے گا۔ اور اگر ایک ہی لڑکی ہو تو اس کے لئے آدھا ہے اور میت کے ماں باپ میں سے ہر ایک کے لئے اس کے چھوڑے ہوئے مال کا چھٹا حصہ ہے، اگر اس (میت) کی اوﻻد ہو، اور اگر اوﻻد نہ ہو اور ماں باپ وارث ہوتے ہوں تو اس کی ماں کے لئے تیسرا حصہ ہے، ہاں اگر میت کے کئی بھائی ہوں تو پھر اس کی ماں کا چھٹا حصہ ہے۔ یہ حصے اس وصیت (کی تکمیل) کے بعد ہیں جو مرنے واﻻ کر گیا ہو یا ادائے قرض کے بعد، تمہارے باپ ہوں یا تمہارے بیٹے تمہیں نہیں معلوم کہ ان میں سے کون تمہیں نفع پہچانے میں زیاده قریب ہے، یہ حصے اللہ تعالیٰ کی طرف سے مقرر کرده ہیں بے شک اللہ تعالیٰ پورے علم اور کامل حکمتوں واﻻ ہے'),
(505, 4, 12, 'تمہاری بیویاں جو کچھ چھوڑ مریں اور ان کی اوﻻد نہ ہو تو آدھوں آدھ تمہارا ہے اور اگر ان کی اوﻻد ہو تو ان کے چھوڑے ہوئے مال میں سے تمہارے لئے چوتھائی حصہ ہے۔ اس وصیت کی ادائیگی کے بعد جو وه کر گئی ہوں یا قرض کے بعد۔ اور جو (ترکہ) تم چھوڑ جاؤ اس میں ان کے لئے چوتھائی ہے، اگر تمہاری اوﻻد نہ ہو اور اگر تمہاری اوﻻد ہو تو پھر انہیں تمہارے ترکہ کا آٹھواں حصہ ملے گا، اس وصیت کے بعد جو تم کر گئے ہو اور قرض کی ادائیگی کے بعد۔ اور جن کی میراث لی جاتی ہے وه مرد یا عورت کلالہ ہو یعنی اس کا باپ بیٹا نہ ہو۔ اور اس کا ایک بھائی یا ایک بہن ہو تو ان دونوں میں سے ہر ایک کا چھٹا حصہ ہے اور اس سے زیاده ہوں تو ایک تہائی میں سب شریک ہیں، اس وصیت کے بعد جو کی جائے اور قرض کے بعد جب کہ اوروں کا نقصان نہ کیا گیا ہو یہ مقرر کیا ہوا اللہ تعالیٰ کی طرف سے ہے اور اللہ تعالیٰ دانا ہے بردبار'),
(506, 4, 13, 'یہ حدیں اللہ تعالیٰ کی مقرر کی ہوئی ہیں اور جو اللہ تعالیٰ کی اور اس کے رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ کی فرمانبرداری کرے گا اسے اللہ تعالیٰ جنتوں میں لے جائے گا جن کے نیچے نہریں بہہ رہی ہیں جن میں وه ہمیشہ رہیں گے اور یہ بہت بڑی کامیابی ہے'),
(507, 4, 14, 'اور جو شخص اللہ تعالیٰ کی اور اس کے رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ کی نافرمانی کرے اور اس کی مقرره حدوں سے آگے نکلے اسے وه جہنم میں ڈال دے گا جس میں وه ہمیشہ رہے گا، ایسوں ہی کے لئے رسوا کن عذاب ہے'),
(508, 4, 15, 'تمہاری عورتوں میں سے جو بے حیائی کا کام کریں ان پر اپنے میں سے چار گواه طلب کرو، اگر وه گواہی دیں تو ان عورتوں کو گھروں میں قید رکھو، یہاں تک کہ موت ان کی عمریں پوری کردے، یا اللہ تعالیٰ ان کے لئے کوئی اور راستہ نکالے'),
(509, 4, 16, 'تم میں سے جو دو افراد ایسا کام کر لیں انہیں ایذا دو اگر وه توبہ اور اصلاح کر لیں تو ان سے منھ پھیر لو، بے شک اللہ تعالیٰ توبہ قبول کرنے واﻻ اور رحم کرنے واﻻ ہے'),
(510, 4, 17, 'اللہ تعالیٰ صرف انہی لوگوں کی توبہ قبول فرماتا ہے جو بوجہ نادانی کوئی برائی کر گزریں پھر جلد اس سے باز آ جائیں اور توبہ کریں تو اللہ تعالیٰ بھی ان کی توبہ قبول کرتا ہے، اللہ تعالیٰ بڑے علم واﻻ حکمت واﻻ ہے'),
(511, 4, 18, 'ان کی توبہ نہیں جو برائیاں کرتے چلے جائیں یہاں تک کہ جب ان میں سے کسی کے پاس موت آجائے تو کہہ دے کہ میں نے اب توبہ کی، اور ان کی توبہ بھی قبول نہیں جو کفر پر ہی مر جائیں، یہی لوگ ہیں جن کے لئے ہم نے المناک عذاب تیار کر رکھا ہے'),
(512, 4, 19, 'ایمان والو! تمہیں حلال نہیں کہ زبردستی عورتوں کو ورﺛے میں لے بیٹھو انہیں اس لئے روک نہ رکھو کہ جو تم نے انہیں دے رکھا ہے، اس میں سے کچھ لے لو ہاں یہ اور بات ہے کہ وه کوئی کھلی برائی اور بے حیائی کریں ان کے ساتھ اچھے طریقے سے بودوباش رکھو، گو تم انہیں ناپسند کرو لیکن بہت ممکن ہے کہ تم کسی چیز کو برا جانو، اور اللہ تعالیٰ اس میں بہت ہی بھلائی کر دے'),
(513, 4, 20, 'اور اگر تم ایک بیوی کی جگہ دوسری بیوی کرنا ہی چاہو اور ان میں سے کسی کو تم نے خزانہ کا خزانہ دے رکھا ہو، تو بھی اس میں سے کچھ نہ لو کیا تم اسے ناحق اور کھلا گناه ہوتے ہوئے بھی لے لو گے، تم اسے کیسے لے لو گے'),
(514, 4, 21, 'حاﻻنکہ تم ایک دوسرے سے مل چکے ہو اور ان عورتوں نے تم سے مضبوط عہد وپیمان لے رکھا ہے'),
(515, 4, 22, 'اور ان عورتوں سے نکاح نہ کرو جن سے تمہارے باپوں نے نکاح کیا ہے مگر جو گزر چکا ہے، یہ بے حیائی کا کام اور بغض کا سبب ہے اور بڑی بری راه ہے'),
(516, 4, 23, 'حرام کی گئیں تم پر تمہاری مائیں اور تمہاری لڑکیاں اور تمہاری بہنیں، تمہاری پھوپھیاں اور تمہاری خاﻻئیں اور بھائی کی لڑکیاں اور بہن کی لڑکیاں اور تمہاری وه مائیں جنہوں نے تمہیں دودھ پلایا ہو اور تمہاری دودھ شریک بہنیں اور تمہاری ساس اور تمہاری وه پرورش کرده لڑکیاں جو تمہاری گود میں ہیں، تمہاری ان عورتوں سے جن سے تم دخول کر چکے ہو، ہاں اگر تم نے ان سے جماع نہ کیا ہو تو تم پر کوئی گناه نہیں اور تمہارے صلبی سگے بیٹوں کی بیویاں اور تمہارا دو بہنوں کا جمع کرنا ہاں جو گزر چکا سو گزر چکا، یقیناً اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(517, 4, 24, 'اور (حرام کی گئیں) شوہر والی عورتیں مگر وه جو تمہاری ملکیت میں آجائیں، اللہ تعالیٰ نے یہ احکام تم پر فرض کر دیئے ہیں، اور ان عورتوں کے سوا اور عورتیں تمہارے لئے حلال کی گئیں کہ اپنے مال کے مہر سے تم ان سے نکاح کرنا چاہو برے کام سے بچنے کے لئے نہ کہ شہوت رانی کرنے کے لئے، اس لئے جن سے تم فائده اٹھاؤ انہیں ان کا مقرر کیا ہوا مہر دے دو، اور مہر مقرر ہو جانے کے بعد تم آپس کی رضامندی سے جو طے کرلو اس میں تم پر کوئی گناه نہیں، بے شک اللہ تعالیٰ علم واﻻ حکمت واﻻ ہے'),
(518, 4, 25, 'اور تم میں سے جس کسی کو آزاد مسلمان عورتوں سے نکاح کرنے کی پوری وسعت وطاقت نہ ہو تو وه مسلمان لونڈیوں سے جن کے تم مالک ہو (اپنا نکاح کر لے) اللہ تمہارے اعمال کو بخوبی جاننے واﻻ ہے، تم سب آپس میں ایک ہی تو ہو، اس لئے ان کے مالکوں کی اجازت سے ان سے نکاح کر لو، اور قاعده کے مطالق ان کے مہر ان کو دو، وه پاک دامن ہوں نہ کہ علانیہ بدکاری کرنے والیاں، نہ خفیہ آشنائی کرنے والیاں، پس جب یہ لونڈیاں نکاح میں آجائیں پھر اگر وه بے حیائی کا کام کریں تو انہیں آدھی سزا ہے اس سزا سے جو آزاد عورتوں کی ہے۔ کنیزوں سے نکاح کا یہ حکم تم میں سے ان لوگوں کے لئے ہے جنہیں گناه اور تکلیف کا اندیشہ ہو اور تمہارا ضبط کرنا بہت بہتر ہے اور اللہ تعالیٰ بڑا بخشنے واﻻ اور بڑی رحمت واﻻ ہے'),
(519, 4, 26, 'اللہ تعالیٰ چاہتا ہے کہ تمہارے واسطے خوب کھول کر بیان کرے اور تمہیں تم سے پہلے کے (نیک) لوگوں کی راه پر چلائے اور تمہاری توبہ قبول کرے، اور اللہ تعالیٰ جاننے واﻻ حکمت واﻻ ہے'),
(520, 4, 27, 'اور اللہ چاہتا ہے کہ تمہاری توبہ قبول کرے اور جو لوگ خواہشات کے پیرو ہیں وه چاہتے ہیں کہ تم اس سے بہت دور ہٹ جاؤ'),
(521, 4, 28, 'اللہ چاہتا ہے کہ تم سے تخفیف کر دے کیونکہ انسان کمزور پیدا کیا گیا ہے'),
(522, 4, 29, 'اے ایمان والو! اپنے آپس کے مال ناجائز طریقہ سے مت کھاؤ، مگر یہ کہ تمہاری آپس کی رضا مندی سے ہو خرید وفروخت، اور اپنے آپ کو قتل نہ کرو یقیناً اللہ تعالیٰ تم پر نہایت مہربان ہے'),
(523, 4, 30, 'اور جو شخص یہ (نافرمانیاں) سرکشی اور ﻇلم سے کرے گا تو عنقریب ہم اس کو آگ میں داخل کریں گے اور یہ اللہ پرآسان ہے'),
(524, 4, 31, 'اگر تم ان بڑے گناہوں سے بچتے رہوگے جن سے تم کو منع کیا جاتا ہے تو ہم تمہارے چھوٹے گناه دور کر دیں گے اور عزت وبزرگی کی جگہ داخل کریں گے'),
(525, 4, 32, 'اوراس چیز کی آرزو نہ کرو جس کے باعﺚ اللہ تعالیٰ نے تم میں سے بعض کو بعض پر بزرگی دی ہے، مردوں کا اس میں سے حصہ ہے جو انہوں نے کمایا اور عورتوں کے لئے ان میں سے حصہ ہے جو انہوں نے کمایا، اور اللہ تعالیٰ سے اس کا فضل مانگو، یقیناً اللہ ہر چیز کا جاننے واﻻ ہے'),
(526, 4, 33, 'ماں باپ یا قرابت دار جو چھوڑ مریں اس کے وارث ہم نے ہر شخص کے مقرر کر دیئے ہیں اور جن سے تم نے اپنے ہاتھوں معاہده کیا ہے انہیں ان کا حصہ دو حقیقتاً اللہ تعالیٰ ہر چیز پر حاضر ہے'),
(527, 4, 34, 'مرد عورتوں پر حاکم ہیں اس وجہ سے کہ اللہ تعالیٰ نے ایک کو دوسرے پر فضیلت دی ہے اور اس وجہ سے کہ مردوں نے اپنے مال خرچ کئے ہیں، پس نیک فرمانبردار عورتین خاوند کی عدم موجودگی میں بہ حفاﻇت الٰہی نگہداشت رکھنے والیاں ہیں اور جن عورتوں کی نافرمانی اور بددماغی کا تمہیں خوف ہو انہیں نصیحت کرو اور انہیں الگ بستروں پر چھوڑ دو اور انہیں مار کی سزا دو پھر اگر وه تابعداری کریں تو ان پر کوئی راستہ تلاش نہ کرو، بے شک اللہ تعالیٰ بڑی بلندی اور بڑائی واﻻ ہے'),
(528, 4, 35, 'اگر تمہیں میاں بیوی کے درمیان آپس کی ان بن کا خوف ہو تو ایک منصف مرد والوں میں سے اور ایک عورت کے گھر والوں میں سے مقرر کرو، اگر یہ دونوں صلح کرانا چاہیں گے تو اللہ دونوں میں ملاپ کرا دے گا، یقیناً اللہ تعالیٰ پورے علم واﻻ پوری خبر واﻻ ہے'),
(529, 4, 36, 'اور اللہ تعالیٰ کی عبادت کرو اور اس کے ساتھ کسی کو شریک نہ کرو اور ماں باپ کے ساتھ سلوک واحسان کرو اور رشتہ داروں سے اور یتیموں سے اور مسکینوں سے اور قرابت دار ہمسایہ سے اور اجنبی ہمسایہ سے اور پہلو کے ساتھی سے اور راه کے مسافر سے اور ان سے جن کے مالک تمہارے ہاتھ ہیں، (غلام کنیز) یقیناً اللہ تعالیٰ تکبر کرنے والوں اور شیخی خوروں کو پسند نہیں فرماتا'),
(530, 4, 37, 'جو لوگ خود بخیلی کرتے ہیں اور دوسروں کو بھی بخیلی کرنے کو کہتے ہیں اور اللہ تعالیٰ نے جو اپنا فضل انہیں دے رکھا ہے اسے چھپا لیتے ہیں ہم نے ان کافروں کے لئے ذلت کی مار تیار کر رکھی ہے'),
(531, 4, 38, 'اور جو لوگ اپنا مال لوگوں کے دکھاوے کے لئے خرچ کرتے ہیں اور اللہ تعالیٰ پر اور قیامت کے دن پر ایمان نہیں رکھتے اور جس کا ہم نشین اور ساتھی شیطان ہو، وه بدترین ساتھی ہے'),
(532, 4, 39, 'بھلا ان کا کیا نقصان تھا اگر یہ اللہ تعالیٰ پر اور قیامت کے دن پر ایمان ﻻتے اور اللہ تعالیٰ نے جو انہیں دے رکھا ہے اس میں خرچ کرتے، اللہ تعالیٰ انہیں خوب جاننے واﻻ ہے'),
(533, 4, 40, 'بے شک اللہ تعالیٰ ایک ذره برابر ﻇلم نہیں کرتا اور اگر نیکی ہو تو اسے دوگنی کر دیتا ہے اور خاص اپنے پاس سے بہت بڑا ﺛواب دیتا ہے'),
(534, 4, 41, 'پس کیا حال ہوگا جس وقت کہ ہر امت میں سے ایک گواه ہم ﻻئیں گے اور آپ کو ان لوگوں پر گواه بناکر ﻻئیں گے'),
(535, 4, 42, 'جس روز کافر اور رسول کے نافرمان آرزو کریں گے کہ کاش! انہیں زمین کے ساتھ ہموار کر دیا جاتا اور اللہ تعالیٰ سے کوئی بات نہ چھپا سکیں گے'),
(536, 4, 43, 'اے ایمان والو! جب تم نشے میں مست ہو نماز کے قریب بھی نہ جاؤ، جب تک کہ اپنی بات کو سمجھنے نہ لگو اور جنابت کی حالت میں جب تک کہ غسل نہ کر لو، ہاں اگر راه چلتے گزر جانے والے ہو تو اور بات ہے اور اگر تم بیمار ہو یا سفر میں ہو یا تم میں سے کوئی قضائے حاجت سے آیا ہو یا تم نے عورتوں سے مباشرت کی ہو اور تمہیں پانی نہ ملے تو پاک مٹی کا قصد کرو اور اپنے منھ اور اپنے ہاتھ مل لو۔ بے شک اللہ تعالیٰ معاف کرنے واﻻ، بخشنے واﻻ ہے'),
(537, 4, 44, 'کیا تم نے انہیں نہیں دیکھا؟ جنہیں کتاب کا کچھ حصہ دیا گیا ہے، وه گمراہی خریدتے ہیں اور چاہتے ہیں کہ تم بھی راه سے بھٹک جاؤ'),
(538, 4, 45, 'اللہ تعالیٰ تمہارے دشمنوں کو خوب جاننے واﻻ ہے اور اللہ تعالیٰ کا دوست ہونا کافی ہے اور اللہ تعالیٰ کا مددگار ہونا بس ہے'),
(539, 4, 46, 'بعض یہود کلمات کو ان کی ٹھیک جگہ سے ہیر پھیر کر دیتے ہیں اور کہتے ہیں کہ ہم نے سنا اور نافرمانی کی اور سن اس کے بغیر کہ تو سنا جائے اور ہماری رعایت کر! (لیکن اس کے کہنے میں) اپنی زبان کو پیچ دیتے ہیں اور دین میں طعنہ دیتے ہیں اور اگر یہ لوگ کہتے کہ ہم نے سنا اور ہم نے فرمانبرداری کی اور آپ سنئےاور ہمیں دیکھیئے تو یہ ان کے لئے بہت بہتر اور نہایت ہی مناسب تھا، لیکن اللہ تعالیٰ نے ان کے کفر کی وجہ سے انہیں لعنت کی ہے۔ پس یہ بہت ہی کم ایمان ﻻتے ہیں،'),
(540, 4, 47, 'اے اہل کتاب! جو کچھ ہم نے نازل فرمایا ہے جو اس کی بھی تصدیق کرنے واﻻ ہے جو تمہارے پاس ہے، اس پر ایمان لاؤ اس سے پہلے کہ ہم چہرے بگاڑ دیں اور انہیں لوٹا کر پیٹھ کی طرف کر دیں، یا ان پر لعنت بھیجیں جیسے ہم نے ہفتے کے دن والوں پر لعنت کی ہے، اور اللہ تعالیٰ کا کام کیا گیا'),
(541, 4, 48, 'یقیناً اللہ تعالیٰ اپنے ساتھ شریک کئے جانے کو نہیں بخشتا اور اس کے سوا جسے چاہے بخش دیتا ہے اور جو اللہ تعالیٰ کے ساتھ شریک مقرر کرے اس نے بہت بڑا گناه اور بہتان باندھا'),
(542, 4, 49, 'کیا آپ نے انہیں نہیں دیکھا جو اپنی پاکیزگی اور ستائش خود کرتے ہیں؟ بلکہ اللہ تعالیٰ جسے چاہے پاکیزه کرتا ہے، کسی پر ایک دھاگے کے برابر ﻇلم نہ کیا جائے گا'),
(543, 4, 50, 'دیکھو یہ لوگ اللہ تعالیٰ پر کس طرح جھوٹ باندھتے ہیں اور یہ (حرکت) صریح گناه ہونے کے لئے کافی ہے'),
(544, 4, 51, 'کیا آپ نے انہیں نہیں دیکھا جنہیں کتاب کا کچھ حصہ ملا؟ جو بت کا اور باطل معبود کا اعتقاد رکھتے ہیں اور کافروں کے حق میں کہتے ہیں کہ یہ لوگ ایمان والوں سے زیاده راه راست پر ہیں'),
(545, 4, 52, 'یہی وه لوگ ہیں جنہیں اللہ تعالیٰ نے لعنت کی ہے اور جسے اللہ تعالیٰ لعنت کر دے، تو اس کا کوئی مددگار نہ پائے گا'),
(546, 4, 53, 'کیا ان کا کوئی حصہ سلطنت میں ہے؟ اگر ایسا ہو تو پھر یہ کسی کو ایک کھجور کی گٹھلی کے شگاف کے برابر بھی کچھ نہ دیں گے'),
(547, 4, 54, 'یا یہ لوگوں سے حسد کرتے ہیں اس پر جو اللہ تعالیٰ نے اپنے فضل سے انہیں دیا ہے، پس ہم نے تو آل ابراہیم کو کتاب اور حکمت بھی دی ہے اور بڑی سلطنت بھی عطا فرمائی ہے'),
(548, 4, 55, 'پھر ان میں سے بعض نے تو اس کتاب کو مانا اور بعض اس سے رک گئے، اور جہنم کا جلانا کافی ہے'),
(549, 4, 56, 'جن لوگوں نے ہماری آیتوں سے کفر کیا، انہیں ہم یقیناً آگ میں ڈال دیں گے جب ان کی کھالیں پک جائیں گی ہم ان کے سوا اور کھالیں بدل دیں گے تاکہ وه عذاب چکھتے رہیں، یقیناً اللہ تعالیٰ غالب حکمت واﻻ ہے'),
(550, 4, 57, 'اور جو لوگ ایمان ﻻئے اور شائستہ اعمال کئے ہم عنقریب انہیں ان جنتوں میں لے جائیں گے جن کے نیچے نہریں بہہ رہی ہیں، جن میں وه ہمیشہ ہمیشہ رہیں گے، ان کے لئے وہاں صاف ستھری بیویاں ہوں گی اور ہم انہیں گھنی چھاؤں (اور پوری راحت) میں لے جائیں گے'),
(551, 4, 58, 'اللہ تعالیٰ تمہیں تاکیدی حکم دیتا ہے کہ امانت والوں کی امانتیں انہیں پہنچاؤ! اور جب لوگوں کا فیصلہ کرو تو عدل وانصاف سے فیصلہ کرو! یقیناً وه بہتر چیز ہے جس کی نصیحت تمہیں اللہ تعالیٰ کر رہا ہے۔ بے شک اللہ تعالیٰ سنتا ہے، دیکھتا ہے'),
(552, 4, 59, 'اے ایمان والو! فرمانبرداری کرو اللہ تعالیٰ کی اور فرمانبرداری کرو رسول (صلی اللہ علیہ وسلم) کی اور تم میں سے اختیار والوں کی۔ پھر اگر کسی چیز میں اختلاف کرو تو اسے لوٹاؤ، اللہ تعالیٰ کی طرف اور رسول کی طرف، اگر تمہیں اللہ تعالیٰ پر اور قیامت کے دن پر ایمان ہے۔ یہ بہت بہتر ہے اور باعتبار انجام کے بہت اچھا ہے۔'),
(553, 4, 60, 'کیا آپ نے انہیں نہیں دیکھا؟ جن کا دعویٰ تو یہ ہے کہ جو کچھ آپ پر اور جو کچھ آپ سے پہلے اتارا گیا ہے اس پر ان کا ایمان ہے، لیکن وه اپنے فیصلے غیر اللہ کی طرف لے جانا چاہتے ہیں حاﻻنکہ انہین حکم دیا گیا ہے کہ شیطان کا انکار کریں، شیطان تو یہ چاہتا ہے کہ انہیں بہکا کر دور ڈال دے'),
(554, 4, 61, 'ان سے جب کبھی کہا جائے کہ اللہ تعالیٰ کے نازل کرده کلام کی اور رسول (صلی اللہ علیہ وسلم) کی طرف آؤ تو آپ دیکھ لیں گے کہ یہ منافق آپ سے منھ پھیر کر رکے جاتے ہیں'),
(555, 4, 62, 'پھر کیا بات ہے کہ جب ان پر ان کے کرتوت کے باعﺚ کوئی مصیبت آپڑتی ہے تو پھر یہ آپ کے پاس آکر اللہ تعالیٰ کی قسمیں کھاتے ہیں کہ ہمارا اراده تو صرف بھلائی اور میل ملاپ ہی کا تھا'),
(556, 4, 63, 'یہ وه لوگ ہیں کہ ان کے دلوں کا بھید اللہ تعالیٰ پر بخوبی روشن ہے، آپ ان سے چشم پوشی کیجئے، انہیں نصیحت کرتے رہیئے اور انہیں وه بات کہئے! جو ان کے دلوں میں گھر کرنے والی ہو'),
(557, 4, 64, 'ہم نے ہر ہر رسول کو صرف اسی لئے بھیجا کہ اللہ تعالیٰ کے حکم سے اس کی فرمانبرداری کی جائے اور اگر یہ لوگ جب انہوں نے اپنی جانوں پر ﻇلم کیا تھا، تیرے پاس آ جاتے اور اللہ تعالیٰ سے استغفار کرتے اور رسول بھی ان کے لئے استغفار کرتے، تو یقیناً یہ لوگ اللہ تعالیٰ کو معاف کرنے واﻻ مہربان پاتے'),
(558, 4, 65, 'سو قسم ہے تیرے پروردگار کی! یہ مومن نہیں ہو سکتے، جب تک کہ تمام آپس کے اختلاف میں آپ کو حاکم نہ مان لیں، پھر جو فیصلے آپ ان میں کر دیں ان سے اپنے دل میں اور کسی طرح کی تنگی اور ناخوشی نہ پائیں اور فرمانبرداری کے ساتھ قبول کر لیں'),
(559, 4, 66, 'اور اگر ہم ان پر یہ فرض کر دیتے کہ اپنی جانوں کو قتل کر ڈالو! یا اپنے گھروں سے نکل جاؤ! تو اسے ان میں سے بہت ہی کم لوگ بجا ﻻتے اور اگر یہ وہی کریں جس کی انہیں نصیحت کی جاتی ہے تو یقیناً یہی ان کے لئے بہتر اور بہت زیاده مضبوطی واﻻ ہے'),
(560, 4, 67, 'اور تب تو انہیں ہم اپنے پاس سے بڑا ﺛواب دیں'),
(561, 4, 68, 'اور یقیناً انہیں راه راست دکھا دیں'),
(562, 4, 69, 'اور جو بھی اللہ تعالیٰ کی اور رسول (صلی اللہ علیہ وسلم) کی فرمانبرداری کرے، وه ان لوگوں کے ساتھ ہوگا جن پر اللہ تعالیٰ نے انعام کیا ہے، جیسے نبی اور صدیق اور شہید اور نیک لوگ، یہ بہترین رفیق ہیں'),
(563, 4, 70, 'یہ فضل اللہ تعالیٰ کی طرف سے ہے اور کافی ہے اللہ تعالیٰ جاننے واﻻ ہے'),
(564, 4, 71, 'اے مسلمانو! اپنے بچاؤ کا سامان لے لو پھر گروه گروه بن کر کوچ کرو یا سب کے سب اکٹھے ہو کر نکل کھڑے ہو!'),
(565, 4, 72, 'اور یقیناً تم میں بعض وه بھی ہیں جو پس وپیش کرتے ہیں، پھر اگر تمہیں کوئی نقصان ہوتا ہے تو وه کہتے ہیں کہ اللہ تعالیٰ نے مجھ پر بڑا فضل کیا کہ میں ان کے ساتھ موجود نہ تھا'),
(566, 4, 73, 'اور اگر تمہیں اللہ تعالیٰ کا کوئی فضل مل جائے تو اس طرح کہ گویا تم میں ان میں دوستی تھی ہی نہیں، کہتے ہیں کاش! میں بھی ان کے ہمراه ہوتا تو بڑی کامیابی کو پہنچتا'),
(567, 4, 74, 'پس جو لوگ دنیا کی زندگی کو آخرت کے بدلے بیچ چکے ہیں، انہیں اللہ تعالیٰ کی راه میں جہاد کرنا چاہئے اور جو شخص اللہ تعالیٰ کی راه میں جہاد کرتے ہوئے شہادت پا لے یا غالب آجائے، یقیناً ہم اسے بہت بڑا ﺛواب عنایت فرمائیں گے'),
(568, 4, 75, 'بھلا کیا وجہ ہے کہ تم اللہ کی راه میں اور ان ناتواں مردوں، عورتوں اور ننھے ننھے بچوں کے چھٹکارے کے لئے جہاد نہ کرو؟ جو یوں دعائیں مانگ رہے ہیں کہ اے ہمارے پروردگار! ان ﻇالموں کی بستی سے ہمیں نجات دے اور ہمارے لئے خود اپنے پاس سے حمایتی مقرر کر دے اور ہمارے لئے خاص اپنے پاس سے مددگار بنا'),
(569, 4, 76, 'جو لوگ ایمان ﻻئے ہیں وه تو اللہ تعالیٰ کی راه میں جہاد کرتے ہیں اور جن لوگوں نے کفر کیا ہے، وه اللہ تعالیٰ کے سوا اوروں کی راه میں لڑتے ہیں پس تم شیطان کے دوستوں سے جنگ کرو! یقین مانو کہ شیطانی حیلہ (بالکل بودا اور) سخت کمزور ہے'),
(570, 4, 77, 'کیا تم نے انہیں نہیں دیکھا جنہیں حکم کیا گیا تھا کہ اپنے ہاتھوں کو روکے رکھو اور نمازیں پڑھتے رہو اور زکوٰة ادا کرتے رہو۔ پھر جب انہیں جہاد کا حکم دیا گیا تو اسی وقت ان کی ایک جماعت لوگوں سے اس قدر ڈرنے لگی جیسے اللہ تعالیٰ کا ڈر ہو، بلکہ اس سے بھی زیاده، اور کہنے لگے اے ہمارے رب! تو نے ہم پر جہاد کیوں فرض کر دیا؟ کیوں ہمیں تھوڑی سی زندگی اور نہ جینے دی؟ آپ کہہ دیجئے کہ دنیا کی سودمندی تو بہت ہی کم ہے اور پرہیزگاروں کے لئے تو آخرت ہی بہتر ہے اور تم پر ایک دھاگے کے برابر بھی ستم روا نہ رکھا جائے گا'),
(571, 4, 78, 'تم جہاں کہیں بھی ہو موت تمہیں آپکڑے گی، گو تم مضبوط قلعوں میں ہو، اور اگر انہیں کوئی بھلائی ملتی ہے تو کہتے ہیں کہ یہ اللہ تعالیٰ کی طرف سے ہے اور اگر کوئی برائی پہنچتی ہے تو کہہ اٹھتے ہیں کہ یہ تیری طرف سے ہے۔ انہیں کہہ دو کہ یہ سب کچھ اللہ تعالیٰ کی طرف سے ہے۔ انہیں کیا ہوگیا ہے کہ کوئی بات سمجھنے کے بھی قریب نہیں'),
(572, 4, 79, 'تجھے جو بھلائی ملتی ہے وه اللہ تعالیٰ کی طرف سے ہے اور جو برائی پہنچتی ہے وه تیرے اپنے نفس کی طرف سے ہے، ہم نے تجھے تمام لوگوں کو پیغام پہنچانے واﻻ بنا کر بھیجا ہے اور اللہ تعالیٰ گواه کافی ہے'),
(573, 4, 80, 'اس رسول (صلی اللہ علیہ وسلم) کی جو اطاعت کرے اسی نے اللہ تعالیٰ کی فرمانبرداری کی اور جو منھ پھیر لے تو ہم نے آپ کو کچھ ان پر نگہبان بنا کر نہیں بھیجا'),
(574, 4, 81, 'یہ کہتے تو ہیں کہ اطاعت ہے، پھر جب آپ کے پاس سے اٹھ کر باہر نکلتے ہیں تو ان میں کی ایک جماعت، جو بات آپ نے یا اس نے کہی ہے اس کے خلاف راتوں کو مشورے کرتی ہے، ان کی راتوں کی بات چیت اللہ لکھ رہا ہے، تو آپ ان سے منھ پھیر لیں اور اللہ پر بھروسہ رکھیں، اللہ تعالیٰ کافی کارساز ہے'),
(575, 4, 82, 'کیا یہ لوگ قرآن میں غور نہیں کرتے؟ اگر یہ اللہ تعالیٰ کے سوا کسی اور کی طرف سے ہوتا تو یقیناً اس میں بہت کچھ اختلاف پاتے'),
(576, 4, 83, 'جہاں انہیں کوئی خبر امن کی یا خوف کی ملی انہوں نے اسے مشہور کرنا شروع کر دیا، حاﻻنکہ اگر یہ لوگ اسے رسول (صلی اللہ علیہ وسلم) کے اور اپنے میں سے ایسی باتوں کی تہہ تک پہنچنے والوں کے حوالے کر دیتے، تو اس کی حقیقت وه لوگ معلوم کر لیتے جو نتیجہ اخذ کرتے ہیں اور اگر اللہ تعالیٰ کا فضل اور اس کی رحمت تم پر نہ ہوتی تو معدودے چند کے علاوه تم سب شیطان کے پیروکار بن جاتے'),
(577, 4, 84, 'تو اللہ تعالیٰ کی راه میں جہاد کرتا ره، تجھے صرف تیری ذات کی نسبت حکم دیا جاتا ہے، ہاں ایمان والوں کو رغبت دﻻتا ره، بہت ممکن ہے کہ اللہ تعالیٰ کافروں کی جنگ کو روک دے اور اللہ تعالیٰ سخت قوت واﻻ ہے اور سزادینے میں بھی سخت ہے'),
(578, 4, 85, 'جو شخص کسی نیکی یا بھلے کام کی سفارش کرے، اسے بھی اس کا کچھ حصہ ملے گا اور جو برائی اور بدی کی سفارش کرے اس کے لئے بھی اس میں سے ایک حصہ ہے، اور اللہ تعالیٰ ہر چیز پر قدرت رکھنے واﻻ ہے'),
(579, 4, 86, 'اور جب تمہیں سلام کیا جائے تو تم اس سے اچھا جواب دو یا انہی الفاظ کو لوٹا دو، بے شبہ اللہ تعالیٰ ہر چیز کا حساب لینے واﻻ ہے'),
(580, 4, 87, 'اللہ وه ہے جس کے سوا کوئی معبود (برحق) نہیں وه تم سب کو یقیناً قیامت کے دن جمع کرے گا، جس کے (آنے) میں کوئی شک نہیں، اللہ تعالیٰ سے زیاده سچی بات واﻻ اور کون ہوگا'),
(581, 4, 88, 'تمہیں کیا ہوگیا ہے؟ کہ منافقوں کے بارے میں دو گروه ہو رہے ہو؟ انہیں تو ان کے اعمال کی وجہ سے اللہ تعالیٰ نے اوندھا کر دیا ہے۔ اب کیا تم یہ منصوبے باندھ رہے ہو کہ اللہ تعالیٰ کے گمراه کئے ہوؤں کو تم راه راست پر ﻻکھڑا کرو، جسے اللہ تعالیٰ راه بھلا دے تو ہرگز اس کے لئے کوئی راه نہ پائے گا'),
(582, 4, 89, 'ان کی تو چاہت ہے کہ جس طرح کے کافر وه ہیں تو بھی ان کی طرح کفر کرنے لگو اور پھر سب یکساں ہو جاؤ، پس جب تک یہ اسلام کی خاطر وطن نہ چھوڑیں ان میں سے کسی کو حقیقی دوست نہ بناؤ، پھر اگر یہ منھ پھیر لیں تو انہیں پکڑو اور قتل کرو جہاں بھی یہ ہاتھ لگ جائیں، خبردار! ان میں سے کسی کو اپنا رفیق اور مددگار نہ سمجھ بیٹھنا'),
(583, 4, 90, 'سوائے ان کے جو اس قوم سے تعلق رکھتے ہوں جن سے تمہارا معاہده ہو چکا ہے یا جو تمہارے پاس اس حالت میں آئیں کہ تم سے جنگ کرنے سے بھی تنگ دل ہیں اور اپنی قوم سے بھی جنگ کرنے سے تنگ دل ہیں اور اگر اللہ تعالیٰ چاہتا تو انہیں تم پر مسلط کر دیتا اور وه تم سے یقیناً جنگ کرتے، پس اگر یہ لوگ تم سے کناره کشی اختیار کر لیں اور تم سے لڑائی نہ کریں اور تمہاری جانب صلح کا پیغام ڈالیں، تو اللہ تعالیٰ نے تمہارے لئے ان پر کوئی راه لڑائی کی نہیں کی'),
(584, 4, 91, 'تم کچھ اور لوگوں کو ایسا بھی پاؤ گے جن کی (بظاہر) چاہت ہے کہ تم سے بھی امن میں رہیں۔ اور اپنی قوم سے بھی امن میں رہیں (لیکن) جب کبھی فتنہ انگیزی کی طرف لوٹائے جاتے ہیں تو اوندھے منھ اس میں ڈال دیئے جاتے ہیں، پس اگر یہ لوگ تم سے کناره کشی نہ کریں اور تم سے صلح کا سلسلہ جنبانی نہ کریں اور اپنے ہاتھ نہ روک لیں، تو انہیں پکڑو اور مار ڈالو جہاں کہیں بھی پالو! یہی وه ہیں جن پر ہم نے تمہیں ﻇاہر حجت عنایت فرمائی ہے'),
(585, 4, 92, 'کسی مومن کو دوسرے مومن کا قتل کر دینا زیبا نہیں مگر غلطی سے ہو جائے (تو اور بات ہے)، جو شخص کسی مسلمان کو بلا قصد مار ڈالے، اس پر ایک مسلمان غلام کی گردن آزاد کرنا اور مقتول کے عزیزوں کو خون بہا پہنچانا ہے۔ ہاں یہ اور بات ہے کہ وه لوگ بطور صدقہ معاف کر دیں اور اگر مقتول تمہاری دشمن قوم کا ہو اور ہو وه مسلمان، تو صرف ایک مومن غلام کی گردن آزاد کرنی ﻻزمی ہے۔ اور اگر مقتول اس قوم سے ہو کہ تم میں اور ان میں عہد و پیماں ہے تو خون بہا ﻻزم ہے، جو اس کے کنبے والوں کو پہنچایا جائے اور ایک مسلمان غلام کا آزاد کرنا بھی (ضروری ہے)، پس جو نہ پائے اس کے ذمے دو مہینے کے لگاتار روزے ہیں، اللہ تعالیٰ سے بخشوانے کے لئےاور اللہ تعالیٰ بخوبی جاننے واﻻ اور حکمت واﻻ ہے'),
(586, 4, 93, 'اور جو کوئی کسی مومن کو قصداً قتل کر ڈالے، اس کی سزا دوزخ ہے جس میں وه ہمیشہ رہے گا، اس پر اللہ تعالیٰ کا غضب ہے، اسے اللہ تعالیٰ نے لعنت کی ہے اور اس کے لئے بڑا عذاب تیار رکھا ہے'),
(587, 4, 94, 'اے ایمان والو! جب تم اللہ کی راه میں جا رہے ہو تو تحقیق کر لیا کرو اور جو تم سے سلام علیک کرے تو اسے یہ نہ کہہ دو کہ تو ایمان واﻻ نہیں۔ تم دنیاوی زندگی کے اسباب کی تلاش میں ہو تو اللہ تعالیٰ کے پاس بہت سی غنیمتیں ہیں۔ پہلے تم بھی ایسے ہی تھے، پھر اللہ تعالیٰ نے تم پر احسان کیا لہٰذا تم ضرور تحقیق وتفتیش کر لیا کرو، بے شک اللہ تعالیٰ تمہارے اعمال سے باخبر ہے'),
(588, 4, 95, 'اپنی جانوں اور مالوں سے اللہ کی راه میں جہاد کرنے والے مومن اور بغیر عذر کے بیٹھ رہنے والے مومن برابر نہیں، اپنے مالوں اور اپنی جانوں سے جہاد کرنے والوں کو بیٹھ رہنے والوں پر اللہ تعالیٰ نے درجوں میں بہت فضیلت دے رکھی ہے اور یوں تو اللہ تعالیٰ نے ہر ایک کو خوبی اور اچھائی کا وعده دیا ہے لیکن مجاہدین کو بیٹھ رہنے والوں پر بہت بڑے اجر کی فضیلت دے رکھی ہے'),
(589, 4, 96, 'اپنی طرف سے مرتبے کی بھی اور بخشش کی بھی اور رحمت کی بھی اور اللہ تعالیٰ بخشش کرنے واﻻ اور رحم کرنے واﻻ ہے'),
(590, 4, 97, 'جو لوگ اپنی جانوں پر ﻇلم کرنے والے ہیں جب فرشتے ان کی روح قبض کرتے ہیں تو پوچھتے ہیں، تم کس حال میں تھے؟ یہ جواب دیتے ہیں کہ ہم اپنی جگہ کمزور اور مغلوب تھے۔ فرشتے کہتے ہیں کیا اللہ تعالیٰ کی زمین کشاده نہ تھی کہ تم ہجرت کر جاتے؟ یہی لوگ ہیں جن کا ٹھکانا دوزخ ہے اور وه پہنچنے کی بری جگہ ہے'),
(591, 4, 98, 'مگر جو مرد عورتیں اور بچے بے بس ہیں جنہیں نہ تو کسی چارہٴ کار کی طاقت اور نہ کسی راستے کا علم ہے'),
(592, 4, 99, 'بہت ممکن ہے کہ اللہ تعالیٰ ان سے درگزر کرے، اللہ تعالیٰ درگزر کرنے واﻻ اور معاف فرمانے واﻻ ہے'),
(593, 4, 100, 'جو کوئی اللہ کی راه میں وطن کو چھوڑے گا، وه زمین میں بہت سی قیام کی جگہیں بھی پائے گا اور کشادگی بھی، اور جو کوئی اپنے گھر سے اللہ تعالیٰ اور اس کے رسول (صلی اللہ علیہ وسلم) کی طرف نکل کھڑا ہوا، پھر اسے موت نے آ پکڑا تو بھی یقیناً اس کا اجر اللہ تعالیٰ کے ذمہ ﺛابت ہو گیا، اور اللہ تعالیٰ بڑا بخشنے واﻻ مہربان ہے'),
(594, 4, 101, 'جب تم سفر میں جا رہے ہو تو تم پر نمازوں کے قصر کرنے میں کوئی گناه نہیں، اگر تمہیں ڈر ہو کہ کافر تمہیں ستائیں گے، یقیناً کافر تمہارے کھلے دشمن ہیں'),
(595, 4, 102, 'جب تم ان میں ہو اور ان کے لئے نماز کھڑی کرو تو چاہئے کہ ان کی ایک جماعت تمہارے ساتھ اپنے ہتھیار لئے کھڑی ہو، پھر جب یہ سجده کر چکیں تو یہ ہٹ کر تمہارے پیچھے آجائیں اور وه دوسری جماعت جس نے نماز نہیں پڑھی وه آجائے اور تیرے ساتھ نماز ادا کرے اور اپنا بچاؤ اور اپنے ہتھیار لئے رہے، کافر چاہتے ہیں کہ کسی طرح تم اپنے ہتھیاروں اور اپنے سامان سے بے خبر ہو جاؤ تو وہ تم پر اچانک دھاوا بول دیں، ہاں اپنے ہتھیار اتار رکھنے میں اس وقت تم پر کوئی گناه نہیں جب کہ تمہیں تکلیف ہو یا بوجہ بارش کے یا بسبب بیمار ہو جانے کے اور اپنے بچاؤ کی چیزیں ساتھ لئے رہو، یقیناً اللہ تعالیٰ نے منکروں کے لئے ذلت کی مار تیار کر رکھی ہے'),
(596, 4, 103, 'پھر جب تم نماز ادا کر چکو تو اٹھتے بیٹھتے اور لیٹے اللہ تعالیٰ کا ذکر کرتے رہو اور جب اطمینان پاؤ تو نماز قائم کرو! یقیناً نماز مومنوں پر مقرره وقتوں پر فرض ہے'),
(597, 4, 104, 'ان لوگوں کا پیچھا کرنے سے ہارے دل ہو کر بیٹھ نہ رہو! اگر تمہیں بے آرامی ہوتی ہے تو انہیں بھی تمہاری طرح بے آرامی ہوتی ہے اور تم اللہ تعالیٰ سے وه امیدیں رکھتے ہو، جو امیدیں انہیں نہیں، اور اللہ تعالیٰ دانا اور حکیم ہے'),
(598, 4, 105, 'یقیناً ہم نے تمہاری طرف حق کے ساتھ اپنی کتاب نازل فرمائی ہے تاکہ تم لوگوں میں اس چیز کے مطابق فیصلہ کرو جس سے اللہ نے تم کو شناسا کیا ہے اور خیانت کرنے والوں کے حمایتی نہ بنو'),
(599, 4, 106, 'اور اللہ تعالیٰ سے بخشش مانگو! بے شک اللہ تعالیٰ بخشش کرنے واﻻ، مہربانی کرنے واﻻ ہے'),
(600, 4, 107, 'اور ان کی طرف سے جھگڑا نہ کرو جو خود اپنی ہی خیانت کرتے ہیں، یقیناً دغا باز گنہگار اللہ تعالیٰ کو اچھا نہیں لگتا'),
(601, 4, 108, 'وه لوگوں سے تو چھﭗ جاتے ہیں، (لیکن) اللہ تعالیٰ سے نہیں چھﭗ سکتے، وه راتوں کے وقت جب کہ اللہ کی ناپسندیده باتوں کے خفیہ مشورے کرتے ہیں اس وقت بھی اللہ ان کے پاس ہوتا ہے، ان کے تمام اعمال کو وه گھیرے ہوئے ہے'),
(602, 4, 109, 'ہاں تو یہ ہو تم لوگ کہ دنیا میں تم نے ان کی حمایت کی لیکن اللہ تعالیٰ کے سامنے قیامت کے دن ان کی حمایت کون کرے گا؟ اور وه کون ہے جو ان کا وکیل بن کر کھڑا ہو سکے گا؟'),
(603, 4, 110, 'جو شخص کوئی برائی کرے یا اپنی جان پر ﻇلم کرے پھر اللہ سے استغفار کرے تو وه اللہ کو بخشنے واﻻ، مہربانی کرنے واﻻ پائے گا'),
(604, 4, 111, 'اور جو گناه کرتا ہے اس کا بوجھ اسی پر ہے اور اللہ بخوبی جاننے واﻻ اور پوری حکمت واﻻ ہے'),
(605, 4, 112, 'اور جو شخص کوئی گناه یا خطا کر کے کسی بے گناه کے ذمے تھوپ دے، اس نے بہت بڑا بہتان اٹھایا اور کھلا گناه کیا'),
(606, 4, 113, 'اگر اللہ تعالیٰ کا فضل ورحم تجھ پر نہ ہوتا تو ان کی ایک جماعت نے تو تجھے بہکانے کا قصد کر ہی لیا تھا، مگر دراصل یہ اپنے آپ کو ہی گمراه کرتے ہیں، یہ تیرا کچھ نہیں بگاڑ سکتے، اللہ تعالیٰ نے تجھ پر کتاب وحکمت اتاری ہے اور تجھے وه سکھایا ہے جسے تو نہیں جانتا تھا اور اللہ تعالیٰ کا تجھ پر بڑا بھاری فضل ہے'),
(607, 4, 114, 'ان کے اکثر خفیہ مشوروں میں کوئی خیر نہیں، ہاں! بھلائی اس کے مشورے میں ہے جو خیرات کا یا نیک بات کا یا لوگوں میں صلح کرانے کا حکم کرے اور جو شخص صرف اللہ تعالیٰ کی رضامندی حاصل کرنے کے ارادے سے یہ کام کرے اسے ہم یقیناً بہت بڑا ﺛواب دیں گے'),
(608, 4, 115, 'جو شخص باوجود راه ہدایت کے واضح ہو جانے کے بھی رسول (صلی اللہ علیہ وسلم) کا خلاف کرے اور تمام مومنوں کی راه چھوڑ کر چلے، ہم اسے ادھر ہی متوجہ کردیں گے جدھر وه خود متوجہ ہو اور دوزخ میں ڈال دیں گے، وه پہنچنے کی بہت ہی بری جگہ ہے'),
(609, 4, 116, 'اسے اللہ تعالیٰ قطعاً نہ بخشے گا کہ اس کے ساتھ شرک مقرر کیا جائے، ہاں شرک کے علاوه گناه جس کے چاہے معاف فرما دیتا ہے اور اللہ کے ساتھ شریک کرنے واﻻ بہت دور کی گمراہی میں جا پڑا'),
(610, 4, 117, 'یہ تو اللہ تعالیٰ کو چھوڑ کر صرف عورتوں کو پکارتے ہیں اور دراصل یہ صرف سرکش شیطان کو پوجتے ہیں'),
(611, 4, 118, 'جسے اللہ نے لعنت کی ہے اور اس نے بیڑا اٹھایا ہے کہ تیرے بندوں میں سے میں مقرر شده حصہ لے کر رہوں گا'),
(612, 4, 119, 'اور انہیں راه سے بہکاتا رہوں گا اور باطل امیدیں دﻻتا رہوں گا اور انہیں سکھاؤں گا کہ جانوروں کی کان چیر دیں، اور ان سے کہوں گا کہ اللہ تعالیٰ کی بنائی ہوئی صورت کو بگاڑ دیں، سنو! جو شخص اللہ کو چھوڑ کرشیطان کو اپنا رفیق بنائے گا وه صریح نقصان میں ڈوبے گا'),
(613, 4, 120, 'وه ان سے زبانی وعدے کرتا رہے گا، اور سبز باغ دکھاتا رہے گا، (مگر یاد رکھو!) شیطان کے جو وعدے ان سے ہیں وه سراسر فریب کاریاں ہیں'),
(614, 4, 121, 'یہ وه لوگ ہیں جن کی جگہ جہنم ہے، جہاں سے انہیں چھٹکارا نہ ملے گا'),
(615, 4, 122, 'اور جو ایمان ﻻئیں اور بھلے کام کریں ہم انہیں ان جنتوں میں لے جائیں گے جن کے نیچے چشمے جاری ہیں، جہاں یہ ابداﻻباد رہیں گے، یہ ہے اللہ کا وعده جو سراسر سچا ہے اور کون ہے جو اپنی بات میں اللہ سے زیاده سچا ہو؟'),
(616, 4, 123, 'حقیقت حال نہ تو تمہاری آرزو کے مطابق ہے اور نہ اہل کتاب کی امیدوں پر موقوف ہے، جو برا کرے گا اس کی سزا پائے گا اور کسی کو نہ پائے گا جو اس کی حمایت ومدد، اللہ کے پاس کر سکے'),
(617, 4, 124, 'جو ایمان واﻻ ہو مرد ہو یا عورت اور وه نیک اعمال کرے، یقیناً ایسے لوگ جنت میں جائیں گے اور کھجور کی گٹھلی کے شگاف برابر بھی ان کا حق نہ مارا جائے گا'),
(618, 4, 125, 'باعتبار دین کے اس سے اچھا کون ہے؟ جو اپنے کو اللہ کے تابع کر دے اور ہو بھی نیکو کار، ساتھ ہی یکسوئی والے ابراہیم کے دین کی پیروی کر رہا ہو اور ابراہیم (علیہ السلام) کو اللہ تعالیٰ نے اپنا دوست بنا لیا ہے'),
(619, 4, 126, 'آسمانوں اور زمین میں جو کچھ ہے سب اللہ ہی کا ہے اور اللہ تعالیٰ ہر چیز کو گھیرنے واﻻ ہے'),
(620, 4, 127, 'آپ سے عورتوں کے بارے میں حکم دریافت کرتے ہیں، آپ کہہ دیجئے! کہ خود اللہ ان کے بارے میں حکم دے رہا ہے اور قرآن کی وه آیتیں جو تم پر ان یتیم لڑکیوں کے بارے میں پڑھی جاتی ہیں جنہیں ان کا مقرر حق تم نہیں دیتے اور انہیں اپنے نکاح میں ﻻنے کی رغبت رکھتے ہو اور کمزور بچوں کے بارے میں اور اس بارے میں کہ یتیموں کی کارگزاری انصاف کے ساتھ کرو۔ تم جو نیک کام کرو، بے شبہ اللہ اسے پوری طرح جاننے واﻻ ہے'),
(621, 4, 128, 'اگر کسی عورت کو اپنے شوہر کی بد دماغی اور بے پرواہی کا خوف ہو تو دونوں آپس میں جو صلح کر لیں اس میں کسی پر کوئی گناه نہیں۔ صلح بہت بہتر چیز ہے، طمع ہر ہر نفس میں شامل کر دی گئی ہے۔ اگر تم اچھا سلوک کرو اور پرہیزگاری کرو تو تم جو کر رہے ہو اس پر اللہ تعالیٰ پوری طرح خبردار ہے'),
(622, 4, 129, 'تم سے یہ تو کبھی نہ ہو سکے گا کہ اپنی تمام بیویوں میں ہر طرح عدل کرو، گو تم اس کی کتنی ہی خواہش وکوشش کر لو، اس لئے بالکل ہی ایک کی طرف مائل ہو کر دوسری کو ادھڑ لٹکتی ہوئی نہ چھوڑو اور اگر تم اصلاح کرو اور تقویٰ اختیار کرو تو، بے شک اللہ تعالیٰ بڑی مغفرت اور رحمت واﻻ ہے'),
(623, 4, 130, 'اور اگر میاں بیوی جدا ہو جائیں تو اللہ تعالیٰ اپنی وسعت سے ہر ایک کو بے نیاز کر دے گا، اللہ تعالیٰ وسعت واﻻ حکمت واﻻ ہے'),
(624, 4, 131, 'زمین اور آسمانوں کی ہر ہر چیز اللہ تعالیٰ ہی کی ملکیت میں ہے اور واقعی ہم نے ان لوگوں کو جو تم سے پہلے کتاب دیئے گئے تھے اور تم کو بھی یہی حکم کیا ہے کہ اللہ سے ڈرتے رہو اور اگر تم کفر کرو تو یاد رکھو کہ اللہ کے لئے ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے اور اللہ بہت بے نیاز اور تعریف کیا گیا ہے'),
(625, 4, 132, 'اللہ کے اختیار میں ہیں آسمانوں کی سب چیزیں اور زمین کی بھی اور اللہ کار ساز کافی ہے'),
(626, 4, 133, 'اگر اسے منظور ہو تو اے لوگو! وه تم سب کو لے جائے اور دوسروں کو لے آئے، اللہ تعالیٰ اس پرپوری قدرت رکھنے واﻻ ہے'),
(627, 4, 134, 'جو شخص دنیا کا ﺛواب چاہتا ہو تو (یاد رکھو کہ) اللہ تعالیٰ کے پاس تو دنیا اور آخرت (دونوں) کا ﺛواب موجود ہے اور اللہ تعالیٰ بہت سننے واﻻ اور خوب دیکھنے واﻻ ہے'),
(628, 4, 135, 'اے ایمان والو! عدل وانصاف پر مضبوطی سے جم جانے والے اور خوشنودی موﻻ کے لئے سچی گواہی دینے والے بن جاؤ، گو وه خود تمہارے اپنے خلاف ہو یا اپنے ماں باپ کے یا رشتہ دار عزیزوں کے، وه شخص اگر امیر ہو تو اور فقیر ہو تو دونوں کے ساتھ اللہ کو زیاده تعلق ہے، اس لئے تم خواہش نفس کے پیچھے پڑ کر انصاف نہ چھوڑ دینا اور اگر تم نے کج بیانی یا پہلو تہی کی تو جان لو کہ جو کچھ تم کرو گے اللہ تعالیٰ اس سے پوری طرح باخبر ہے'),
(629, 4, 136, 'اے ایمان والو! اللہ تعالیٰ پر، اس کے رسول (صلی اللہ علیہ وسلم) پر اور اس کتاب پر جو اس نے اپنے رسول (صلی اللہ علیہ وسلم) پر اتاری ہے اور ان کتابوں پر جو اس سے پہلے اس نے نازل فرمائی ہیں، ایمان لاؤ! جو شخص اللہ تعالیٰ سے اور اس کے فرشتوں سے اور اس کی کتابوں سے اور اس کے رسولوں سے اور قیامت کے دن سے کفر کرے وه تو بہت بڑی دور کی گمراہی میں جا پڑا'),
(630, 4, 137, 'جن لوگوں نے ایمان قبول کر کے پھر کفر کیا، پھر ایمان ﻻکر پھر کفر کیا، پھر اپنے کفر میں بڑھ گئے، اللہ تعالیٰ یقیناً انہیں نہ بخشے گا اور نہ انہیں راه ہدایت سمجھائے گا'),
(631, 4, 138, 'منافقوں کو اس امر کی خبر پہنچا دو کہ ان کے لئے دردناک عذاب یقینی ہے'),
(632, 4, 139, 'جن کی یہ حالت ہے کہ مسلمانوں کو چھوڑ کر کافروں کو دوست بناتے پھرتے ہیں، کیا ان کے پاس عزت کی تلاش میں جاتے ہیں؟ (تو یاد رکھیں کہ) عزت تو ساری کی ساری اللہ تعالیٰ کے قبضہ میں ہے'),
(633, 4, 140, 'اور اللہ تعالیٰ تمہارے پاس اپنی کتاب میں یہ حکم اتار چکا ہے کہ تم جب کسی مجلس والوں کو اللہ تعالیٰ کی آیتوں کے ساتھ کفر کرتے اور مذاق اڑاتے ہوئے سنو تو اس مجمع میں ان کے ساتھ نہ بیٹھو! جب تک کہ وه اس کے علاوه اور باتیں نہ کرنے لگیں، (ورنہ) تم بھی اس وقت انہی جیسے ہو، یقیناً اللہ تعالیٰ تمام کافروں اور سب منافقوں کو جہنم میں جمع کرنے واﻻ ہے'),
(634, 4, 141, 'یہ لوگ تمہارے انجام کار کا انتظار کرتے رہتے ہیں پھر اگر تمہیں اللہ فتح دے تو یہ کہتے ہیں کہ کیا ہم تمہارے ساتھی نہیں اور اگر کافروں کو تھوڑا سا غلبہ مل جائے تو (ان سے) کہتے ہیں کہ ہم تم پر غالب نہ آنے لگے تھے اور کیا ہم نے تمہیں مسلمانوں کے ہاتھوں سے نہ بچایا تھا؟ پس قیامت میں خود اللہ تعالیٰ تمہارے درمیان فیصلہ کرے گا اور اللہ تعالیٰ کافروں کو ایمان والوں پر ہرگز راه نہ دے گا'),
(635, 4, 142, 'بے شک منافق اللہ سے چالبازیاں کر رہے ہیں اور وه انہیں اس چالبازی کا بدلہ دینے واﻻ ہے اور جب نماز کو کھڑے ہوتے ہیں تو بڑی کاہلی کی حالت میں کھڑے ہوتے ہیں صرف لوگوں کو دکھاتے ہیں، اور یاد الٰہی تو یوں ہی سی برائے نام کرتے ہیں'),
(636, 4, 143, 'وه درمیان میں ہی معلق ڈگمگا رہے ہیں، نہ پورے ان کی طرف نہ صحیح طور پر ان کی طرف اور جسے اللہ تعالیٰ گمراہی میں ڈال دے تو تو اس کے لئے کوئی راه نہ پائے گا'),
(637, 4, 144, 'اے ایمان والو! مومنوں کو چھوڑ کر کافروں کو دوست نہ بناؤ، کیا تم یہ چاہتے ہو کہ اپنے اوپر اللہ تعالیٰ کی صاف حجت قائم کر لو'),
(638, 4, 145, 'منافق تو یقیناً جہنم کے سب سے نیچے کے طبقہ میں جائیں گے، ناممکن ہے کہ تو ان کا کوئی مددگار پالے'),
(639, 4, 146, 'ہاں جو توبہ کر لیں اور اصلاح کر لیں اور اللہ تعالیٰ پر کامل یقین رکھیں اور خالص اللہ ہی کے لئے دینداری کریں تو یہ لوگ مومنوں کے ساتھ ہیں، اللہ تعالیٰ مومنوں کو بہت بڑا اجر دے گا'),
(640, 4, 147, 'اللہ تعالیٰ تمہیں سزا دے کر کیا کرے گا؟ اگر تم شکر گزاری کرتے رہو اور باایمان رہو، اللہ تعالیٰ بہت قدر کرنے واﻻ اور پورا علم رکھنے واﻻ ہے'),
(641, 4, 148, 'برائی کے ساتھ آواز بلند کرنے کو اللہ تعالیٰ پسند نہیں فرماتا مگر مظلوم کی اجازت ہے اور اللہ تعالیٰ خوب سنتا جانتا ہے'),
(642, 4, 149, 'اگر تم کسی نیکی کو علانیہ کرو یا پوشیده، یا کسی برائی سے درگزر کرو، پس یقیناً اللہ تعالیٰ پوری معافی کرنے واﻻ اور پوری قدرت واﻻ ہے'),
(643, 4, 150, 'جو لوگ اللہ کے ساتھ اور اس کے پیغمبروں کے ساتھ کفر کرتے ہیں اور جو لوگ یہ چاہتے ہیں کہ اللہ اور اس کے رسولوں کے درمیان فرق رکھیں اور جو لوگ کہتے ہیں کہ بعض نبیوں پر تو ہمارا ایمان ہے اور بعض پر نہیں اور چاہتے ہیں کہ اس کے اور اس کے بین بین کوئی راه نکالیں'),
(644, 4, 151, 'یقین مانو کہ یہ سب لوگ اصلی کافر ہیں، اور کافروں کے لئے ہم نے اہانت آمیز سزا تیار کر رکھی ہے'),
(645, 4, 152, 'اور جو لوگ اللہ پر اور اس کے تمام پیغمبروں پر ایمان ﻻتے ہیں اور ان میں سے کسی میں فرق نہیں کرتے، یہ ہیں جنہیں اللہ ان کو پورا ﺛواب دے گا اور اللہ بڑی مغفرت واﻻ بڑی رحمت واﻻ ہے'),
(646, 4, 153, 'آپ سے یہ اہل کتاب درخواست کرتے ہیں کہ آپ ان کے پاس کوئی آسمانی کتاب ﻻئیں، حضرت موسیٰ (علیہ السلام) سے تو انہوں نے اس سے بہت بڑی درخواست کی تھی کہ ہمیں کھلم کھلا اللہ تعالیٰ کو دکھا دے، پس ان کے اس ﻇلم کے باعﺚ ان پر کڑاکے کی بجلی آ پڑی پھر باوجودیکہ ان کے پاس بہت دلیلیں پہنچ چکی تھیں انہوں نے بچھڑے کو اپنا معبود بنا لیا، لیکن ہم نے یہ بھی معاف فرما دیا اور ہم نے موسیٰ کو کھلا غلبہ (اور صریح دلیل) عنایت فرمائی'),
(647, 4, 154, 'اور ان کا قول لینے کے لئے ہم نے ان کے سروں پر طور پہاڑ ﻻ کھڑا کردیا اور انہیں حکم دیا کہ سجده کرتے ہوئے دروازے میں جاؤ اور یہ بھی فرمایا کہ ہفتہ کے دن میں تجاوز نہ کرنا اور ہم نے ان سے سخت سے سخت قول وقرار لئے'),
(648, 4, 155, '(یہ سزا تھی) بہ سبب ان کی عہد شکنی کے اور احکام الٰہی کے ساتھ کفر کرنے کے اور اللہ کے نبیوں کو ناحق قتل کر ڈالنے کے، اور اس سبب سے کہ یوں کہتے ہیں کہ ہمارے دلوں پر غلاف ہے۔ حاﻻنکہ دراصل ان کے کفر کی وجہ سے ان کے دلوں پر اللہ تعالیٰ نے مہر لگا دی ہے، اس لئے یہ قدر قلیل ہی ایمان ﻻتے ہیں'),
(649, 4, 156, 'اور ان کے کفر کے باعﺚ اور مریم پر بہت بڑا بہتان باندھنے کے باعﺚ'),
(650, 4, 157, 'اور یوں کہنے کے باعﺚ کہ ہم نے اللہ کے رسول مسیح عیسیٰ بن مریم کو قتل کر دیا حاﻻنکہ نہ تو انہوں نے اسے قتل کیا نہ سولی پر چڑھایا بلکہ ان کے لئے ان (عیسیٰ) کا شبیہ بنا دیا گیا تھا۔ یقین جانو کہ حضرت عیسیٰ (علیہ السلام) کے بارے میں اختلاف کرنے والے ان کے بارے میں شک میں ہیں، انہیں اس کا کوئی یقین نہیں بجز تخمینی باتوں پر عمل کرنے کے اتنا یقینی ہے کہ انہوں نے انہیں قتل نہیں کیا'),
(651, 4, 158, 'بلکہ اللہ تعالیٰ نے انہیں اپنی طرف اٹھا لیا اور اللہ بڑا زبردست اور پوری حکمتوں واﻻ ہے'),
(652, 4, 159, 'اہل کتاب میں ایک بھی ایسا نہ بچے گا جو حضرت عیسیٰ (علیہ السلام) کی موت سے پہلے ان پر ایمان نہ ﻻچکے اور قیامت کے دن آپ ان پر گواه ہوں گے'),
(653, 4, 160, 'جو نفیس چیزیں ان کے لئے حلال کی گئی تھیں وه ہم نے ان پر حرام کردیں ان کے ﻇلم کے باعﺚ اور اللہ تعالیٰ کی راه سے اکثر لوگوں کو روکنے کے باعﺚ'),
(654, 4, 161, 'اور سود جس سے منع کئے گئے تھے اسے لینے کے باعﺚ اور لوگوں کا مال ناحق مار کھانے کے باعﺚ اور ان میں جو کفار ہیں ہم نے ان کے لئے المناک عذاب مہیا کر رکھا ہے'),
(655, 4, 162, 'لیکن ان میں سے جو کامل اور مضبوط علم والے ہیں اور ایمان والے ہیں جو اس پر ایمان ﻻتے ہیں جو آپ کی طرف اتارا گیا اور جو آپ سے پہلے اتارا گیا اور نمازوں کو قائم رکھنے والے ہیں اور زکوٰة کے ادا کرنے والے ہیں اور اللہ پر اور قیامت کے دن پر ایمان رکھنے والے ہیں یہ ہیں جنہیں ہم بہت بڑے اجر عطا فرمائیں گے'),
(656, 4, 163, 'یقیناً ہم نے آپ کی طرف اسی طرح وحی کی ہے جیسے کہ نوح (علیہ السلام) اور ان کے بعد والے نبیوں کی طرف کی، اور ہم نے وحی کی ابراہیم اور اسماعیل اور اسحاق اور یعقوب اور ان کی اوﻻد پر اور عیسیٰ اور ایوب اور یونس اور ہارون اور سلیمان کی طرف۔ اور ہم نے داؤد (علیہ السلام) کو زبور عطا فرمائی'),
(657, 4, 164, 'اور آپ سے پہلے کے بہت سے رسولوں کے واقعات ہم نے آپ سے بیان کئے ہیں اور بہت سے رسولوں کے نہیں بھی کئے اور موسیٰ (علیہ السلام) سے اللہ تعالیٰ نے صاف طور پر کلام کیا'),
(658, 4, 165, 'ہم نے انہیں رسول بنایا ہے، خوشخبریاں سنانے والے اور آگاه کرنے والے تاکہ لوگوں کی کوئی حجت اور الزام رسولوں کے بھیجنے کے بعد اللہ تعالیٰ پر ره نہ جائے۔ اللہ تعالی بڑا غالب اور بڑا باحکمت ہے'),
(659, 4, 166, 'جو کچھ آپ کی طرف اتارا ہے اس کی بابت خود اللہ تعالیٰ گواہی دیتا ہے کہ اسے اپنے علم سے اتارا ہے اور فرشتے بھی گواہی دیتے ہیں اور اللہ تعالیٰ بطور گواه کافی ہے'),
(660, 4, 167, 'جن لوگوں نے کفر کیا اور اللہ تعالیٰ کی راه سے اوروں کو روکا وه یقیناً گمراہی میں دور نکل گئے'),
(661, 4, 168, 'جن لوگوں نے کفر کیا اور ﻇلم کیا، انہیں اللہ تعالیٰ ہرگز ہرگز نہ بخشے گا اور نہ انہیں کوئی راه دکھائے گا'),
(662, 4, 169, 'بجز جہنم کی راه کے جس میں وه ہمیشہ ہمیشہ پڑے رہیں گے، اور یہ اللہ تعالیٰ پر بالکل آسان ہے'),
(663, 4, 170, 'اے لوگو! تمہارے پاس تمہارے رب کی طرف حق لے کر رسول آگیا ہے، پس تم ایمان ﻻؤ تاکہ تمہارے لئے بہتری ہو اور اگر تم کافر ہوگئے تو اللہ ہی کی ہے ہر وه چیز جو آسمانوں اور زمین میں ہے، اور اللہ دانا ہے حکمت واﻻ ہے'),
(664, 4, 171, 'اے اہل کتاب! اپنے دین کے بارے میں حد سے نہ گزر جاؤ اور اللہ پر بجز حق کے اور کچھ نہ کہو، مسیح عیسیٰ بن مریم (علیہ السلام) تو صرف اللہ تعالیٰ کے رسول اور اس کے کلمہ (کن سے پیدا شده) ہیں، جسے مریم (علیہا السلام) کی طرف ڈال دیا تھا اور اس کے پاس کی روح ہیں اس لئے تم اللہ کو اور اس کے سب رسولوں کو مانو اور نہ کہو کہ اللہ تین ہیں، اس سے باز آجاؤ کہ تمہارے لئے بہتری ہے، اللہ عبادت کے ﻻئق تو صرف ایک ہی ہے اور وه اس سے پاک ہے کہ اس کی اوﻻد ہو، اسی کے لئے ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے۔ اور اللہ کافی ہے کام بنانے واﻻ'),
(665, 4, 172, 'مسیح (علیہ السلام) کو اللہ کا بنده ہونے میں کوئی ننگ و عار یا تکبر وانکار ہرگز ہو ہی نہیں سکتا اور نہ مقرب فرشتوں کو، اس کی بندگی سے جو بھی دل چرائے اور تکبر و انکار کرے، اللہ تعالیٰ ان سب کو اکٹھا اپنی طرف جمع کرے گا'),
(666, 4, 173, 'پس جو لوگ ایمان ﻻئے ہیں اور شائستہ اعمال کئے ہیں ان کو ان کا پورا پورا ﺛواب عنایت فرمائے گا اور اپنے فضل سے انہیں اور زیاده دے گا اور جن لوگوں نے ننگ و عار اور سرکشی اور انکار کیا، انہیں المناک عذاب دے گا اور وه اپنے لئے سوائے اللہ کے کوئی حمایتی، اور امداد کرنے واﻻ نہ پائیں گے'),
(667, 4, 174, 'اے لوگو! تمہارے پاس تمہارے رب کی طرف سے سند اور دلیل آپہنچی اور ہم نے تمہاری جانب واضح اور صاف نور اتار دیا ہے'),
(668, 4, 175, 'پس جو لوگ اللہ تعالیٰ پر ایمان ﻻئے اور اسے مضبوط پکڑ لیا، انہیں تو وه عنقریب اپنی رحمت اور فضل میں لے لے گا اور انہیں اپنی طرف کی راه راست دکھا دے گا'),
(669, 4, 176, 'آپ سے فتویٰ پوچھتے ہیں، آپ کہہ دیجیئے کہ اللہ تعالیٰ (خود) تمہیں کلالہ کے بارے میں فتویٰ دیتا ہے۔ اگر کوئی شخص مر جائے جس کی اوﻻد نہ ہو اور ایک بہن ہو تو اس کے لئے چھوڑے ہوئے مال کا آدھا حصہ ہے اور وه بھائی اس بہن کا وارث ہوگا اگر اس کے اوﻻد نہ ہو۔ پس اگر بہنیں دو ہوں تو انہیں کل چھوڑے ہوئے کا دو تہائی ملے گا۔ اور اگر کئی شخص اس ناطے کے ہیں مرد بھی اور عورتیں بھی تو مرد کے لئے حصہ ہے مثل دو عورتوں کے، اللہ تعالیٰ تمہارے لئے بیان فرما رہا ہے کہ ایسا نہ ہو کہ تم بہک جاؤ اور اللہ تعالیٰ ہر چیز سے واقف ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(670, 5, 1, 'اے ایمان والو! عہد و پیماں پورے کرو، تمہارے لئے مویشی چوپائے حلال کئے گئے ہیں بجز ان کے جن کے نام پڑھ کر سنا دیئے جائیں گے مگر حالت احرام میں شکار کو حلال جاننے والے نہ بننا، یقیناً اللہ جو چاہے حکم کرتا ہے'),
(671, 5, 2, 'اے ایمان والو! اللہ تعالیٰ کے شعائر کی بے حرمتی نہ کرو نہ ادب والے مہینوں کی نہ حرم میں قربان ہونے والے اور پٹے پہنائے گئے جانوروں کی جو کعبہ کو جا رہے ہوں اور نہ ان لوگوں کی جو بیت اللہ کے قصد سے اپنے رب تعالیٰ کے فضل اور اس کی رضاجوئی کی نیت سے جا رہے ہوں، ہاں جب تم احرام اتار ڈالو تو شکار کھیل سکتے ہو، جن لوگوں نے تمہیں مسجد حرام سے روکا تھا ان کی دشمنی تمہیں اس بات پر آماده نہ کرے کہ تم حد سے گزر جاؤ، نیکی اور پرہیزگاری میں ایک دوسرے کی امداد کرتے رہو اور گناه اور ﻇلم و زیادتی میں مدد نہ کرو، اور اللہ تعالیٰ سے ڈرتے رہو، بےشک اللہ تعالیٰ سخت سزا دینے واﻻ ہے'),
(672, 5, 3, 'تم پر حرام کیا گیا مردار اور خون اور خنزیر کا گوشت اور جس پر اللہ کے سوا دوسرے کا نام پکارا گیا ہو اور جو گلا گھٹنے سے مرا ہو اور جو کسی ضرب سے مر گیا ہو اور جو اونچی جگہ سے گر کر مرا ہو اور جو کسی کے سینگ مارنے سے مرا ہو اور جسے درندوں نے پھاڑ کھایا ہو لیکن اسے تم ذبح کر ڈالو تو حرام نہیں اور جو آستانوں پر ذبح کیا گیا ہو اور یہ بھی کہ قرعہ کے تیروں کے ذریعے فال گیری کرو یہ سب بدترین گناه ہیں، آج کفار تمہارے دین سے ناامید ہوگئے، خبردار! تم ان سے نہ ڈرنا اور مجھ سے ڈرتے رہنا، آج میں نے تمہارے لئے دین کو کامل کردیا اور تم پر اپنا انعام بھرپور کردیا اور تمہارے لئے اسلام کے دین ہونے پر رضامند ہوگیا۔ پس جو شخص شدت کی بھوک میں بے قرار ہو جائے بشرطیکہ کسی گناه کی طرف اس کا میلان نہ ہو تو یقیناً اللہ تعالیٰ معاف کرنے واﻻ اور بہت بڑا مہربان ہے'),
(673, 5, 4, 'آپ سے دریافت کرتے ہیں کہ ان کے لئے کیا کچھ حلال ہے؟ آپ کہہ دیجئے کہ تمام پاک چیزیں تمہارے لئے حلال کی گئی ہیں، اور جن شکار کھیلنے والے جانوروں کو تم نے سدھا رکھا ہے یعنی جنہیں تم تھوڑا بہت وه سکھاتے ہو جس کی تعلیم اللہ تعالیٰ نے تمہیں دے رکھی ہے پس جس شکار کو وه تمہارے لئے پکڑ کر روک رکھیں تو تم اس سے کھا لو اور اس پر اللہ تعالیٰ کے نام کا ذکر کر لیا کرو۔ اور اللہ تعالیٰ سے ڈرتے رہو، یقیناً اللہ تعالیٰ جلد حساب لینے واﻻ ہے'),
(674, 5, 5, 'کل پاکیزه چیزیں آج تمہارے لئے حلال کی گئیں اور اہل کتاب کا ذبیحہ تمہارے لئے حلال ہے اور تمہارا ذبیحہ ان کے لئے حلال ہے، اور پاک دامن مسلمان عورتیں اور جو لوگ تم سے پہلے کتاب دیئے گئے ہیں ان کی پاک دامن عورتیں بھی حلال ہیں جب کہ تم ان کے مہر ادا کرو، اس طرح کہ تم ان سے باقاعده نکاح کرو یہ نہیں کہ علانیہ زنا کرو یا پوشیده بدکاری کرو، منکرین ایمان کے اعمال ضائع اور اکارت ہیں اور آخرت میں وه ہارنے والوں میں سے ہیں'),
(675, 5, 6, 'اے ایمان والو! جب تم نماز کے لئے اٹھو تو اپنے منھ کو، اور اپنے ہاتھوں کو کہنیوں سمیت دھو لو اپنے سروں کا مسح کرو اور اپنے پاؤں کو ٹخنوں سمیت دھو لو، اور اگر تم جنابت کی حالت میں ہو تو غسل کرلو، ہاں اگر تم بیمار ہو یا سفر کی حالت میں ہو یا تم میں سے کوئی حاجت ضروری سے فارغ ہو کر آیا ہو، یا تم عورتوں سے ملے ہو اور تمہیں پانی نہ ملے تو تم پاک مٹی سے تیمم کرلو، اسے اپنے چہروں پر اور ہاتھوں پر مل لو اللہ تعالیٰ تم پر کسی قسم کی تنگی ڈالنا نہیں چاہتا بلکہ اس کا اراده تمہیں پاک کرنے کا اور تمہیں اپنی بھرپور نعمت دینے کا ہے، تاکہ تم شکر ادا کرتے رہو'),
(676, 5, 7, 'تم پر اللہ تعالیٰ کی جو نعمتیں نازل ہوئی ہیں انہیں یاد رکھو اور اس کے اس عہد کو بھی جس کا تم سے معاہده ہوا ہے جبکہ تم نے کہا ہم نے سنا اور مانا اور اللہ تعالیٰ سے ڈرتے رہو، یقیناً اللہ تعالیٰ دلوں کی باتوں کا جاننے واﻻ ہے'),
(677, 5, 8, 'اے ایمان والو! تم اللہ کی خاطر حق پر قائم ہو جاؤ، راستی اور انصاف کے ساتھ گواہی دینے والے بن جاؤ، کسی قوم کی عداوت تمہیں خلاف عدل پر آماده نہ کردے، عدل کیا کرو جو پرہیز گاری کے زیاده قریب ہے، اور اللہ تعالیٰ سے ڈرتے رہو، یقین مانو کہ اللہ تعالیٰ تمہارے اعمال سے باخبر ہے'),
(678, 5, 9, 'اللہ تعالیٰ کا وعده ہے کہ جو ایمان ﻻئیں اور نیک کام کریں ان کے لئے وسیع مغفرت اور بہت بڑا اجر و ﺛواب ہے'),
(679, 5, 10, 'اور جن لوگوں نے کفر کیا اور ہمارے احکام کو جھٹلایا وه دوزخی ہیں'),
(680, 5, 11, 'اے ایمان والو! اللہ تعالیٰ نے جو احسان تم پر کیا ہے اسے یاد کرو جب کہ ایک قوم نے تم پر دست درازی کرنی چاہی تو اللہ تعالیٰ نے ان کے ہاتھوں کو تم تک پہنچنے سے روک دیا اور اللہ تعالیٰ سے ڈرتے رہو اور مومنوں کو اللہ تعالیٰ ہی پر بھروسہ کرنا چاہئے'),
(681, 5, 12, 'اور اللہ تعالیٰ نے بنی اسرائیل سے عہد و پیمان لیا اور انہی میں سے باره سردار ہم نے مقرر فرمائے اور اللہ تعالیٰ نے فرما دیا کہ یقیناً میں تمہارے ساتھ ہوں، اگر تم نماز قائم رکھو گے اور زکوٰة دیتے رہو گے اور میرے رسولوں کو مانتے رہو گے اور ان کی مدد کرتے رہو گے اور اللہ تعالیٰ کو بہتر قرض دیتے رہو گے تو یقیناً میں تمہاری برائیاں تم سے دور رکھوں گا اور تمہیں ان جنتوں میں لے جاؤں گا جن کے نیچے چشمے بہہ رہے ہیں، اب اس عہد وپیمان کے بعد بھی تم میں سے جو انکاری ہو جائے وه یقیناً راه راست سے بھٹک گیا'),
(682, 5, 13, 'پھر ان کی عہد شکنی کی وجہ سے ہم نے ان پر اپنی لعنت نازل فرمادی اور ان کے دل سخت کردیئے کہ وه کلام کو اس کی جگہ سے بدل ڈالتے ہیں اور جو کچھ نصیحت انہیں کی گئی تھی اس کا بہت بڑا حصہ بھلا بیٹھے، ان کی ایک نہ ایک خیانت پر تجھے اطلاع ملتی ہی رہے گی ہاں تھوڑے سے ایسے نہیں بھی ہیں پس توانہیں معاف کرتا جا اور درگزر کرتا ره، بےشک اللہ تعالیٰ احسان کرنے والوں سے محبت کرتا ہے'),
(683, 5, 14, 'اور جو اپنے آپ کو نصرانی کہتے ہیں ہم نے ان سے بھی عہد وپیمان لیا، انہوں نے بھی اس کا بڑا حصہ فراموش کر دیا جو انہیں نصیحت کی گئی تھی، تو ہم نے بھی ان کے آپس میں بغض وعداوت ڈال دی جو تاقیامت رہے گی اور جو کچھ یہ کرتے تھے عنقریب اللہ تعالیٰ انہیں سب بتا دے گا'),
(684, 5, 15, 'اے اہل کتاب! یقیناً تمہارے پاس ہمارا رسول (صلی اللہ علیہ وسلم) آچکا جو تمہارے سامنے کتاب اللہ کی بکثرت ایسی باتیں ﻇاہر کر رہا ہے جنہیں تم چھپا رہے تھے اور بہت سی باتوں سے درگزر کرتا ہے، تمہارے پاس اللہ تعالیٰ کی طرف سے نور اور واضح کتاب آچکی ہے'),
(685, 5, 16, 'جس کے ذریعہ سے اللہ تعالیٰ انہیں جو رضائے رب کے درپے ہوں سلامتی کی راہیں بتلاتا ہے اور اپنی توفیق سے اندھیروں سے نکال کر نور کی طرف ﻻتا ہے اور راه راست کی طرف ان کی رہبری کرتا ہے'),
(686, 5, 17, 'یقیناً وه لوگ کافر ہوگئے جنہوں نے کہا کہ اللہ ہی مسیح ابن مریم ہے، آپ ان سے کہہ دیجیئے کہ اگر اللہ تعالیٰ مسیح ابن مریم اور اس کی والده اور روئے زمین کے سب لوگوں کو ہلاک کر دینا چاہے تو کون ہے جو اللہ تعالیٰ پر کچھ بھی اختیار رکھتا ہو؟ آسمانوں و زمین اور دونوں کے درمیان کا کل ملک اللہ تعالیٰ ہی کا ہے، وه جو چاہتا ہے پیدا کرتا ہے، اور اللہ تعالیٰ ہر چیز پر قادر ہے'),
(687, 5, 18, 'یہود و نصاریٰ کہتے ہیں کہ ہم اللہ کے بیٹے اور اس کے دوست ہیں، آپ کہہ دیجیئے کہ پھر تمہیں تمہارے گناہوں کے باعﺚ اللہ کیوں سزا دیتا ہے؟ نہیں بلکہ تم بھی اس کی مخلوق میں سے ایک انسان ہو وه جسے چاہتا ہے بخش دیتا ہے، اور جسے چاہتا ہے عذاب کرتا ہے، زمین و آسمان اور ان کے درمیان کی ہر چیز اللہ تعالیٰ کی ملکیت ہے اور اسی کی طرف لوٹنا ہے'),
(688, 5, 19, 'اے اہل کتاب! بالیقین ہمارا رسول تمہارے پاس رسولوں کی آمد کے ایک وقفے کے بعد آ پہنچا ہے۔ جو تمہارے لئے صاف صاف بیان کر رہا ہے تاکہ تمہاری یہ بات نہ ره جائے کہ ہمارے پاس تو کوئی بھلائی، برائی سنانے واﻻ آیا ہی نہیں، پس اب تو یقیناً خوشخبری سنانے واﻻ اور آگاه کرنے واﻻ آ پہنچا اور اللہ تعالیٰ ہر چیز پر قادر ہے'),
(689, 5, 20, 'اور یاد کرو موسیٰ (علیہ السلام) نے اپنی قوم سے کہا، اے میری قوم کے لوگو! اللہ تعالیٰ کے اس احسان کا ذکر کرو کہ اس نے تم میں سے پیغمبر بنائے اور تمہیں بادشاه بنا دیا اور تمہیں وه دیا جو تمام عالم میں کسی کو نہیں دیا'),
(690, 5, 21, 'اے میری قوم والو! اس مقدس زمین میں داخل ہو جاؤ جو اللہ تعالیٰ نے تمہارے نام لکھ دی ہے اور اپنی پشت کے بل روگردانی نہ کرو کہ پھر نقصان میں جا پڑو'),
(691, 5, 22, 'انہوں نے جواب دیا کہ اے موسیٰ وہاں تو زور آور سرکش لوگ ہیں اور جب تک وه وہاں سے نکل نہ جائیں ہم تو ہرگز وہاں نہ جائیں گے ہاں اگر وه وہاں سے نکل جائیں پھر تو ہم (بخوشی) چلے جائیں گے'),
(692, 5, 23, 'دو شخصوں نے جو خدا ترس لوگوں میں سے تھے، جن پر اللہ تعالیٰ کا فضل تھا کہا کہ تم ان کے پاس دروازے میں تو پہنچ جاؤ، دروازے میں قدم رکھتے ہی یقیناً تم غالب آجاؤ گے، اور تم اگر مومن ہو تو تمہیں اللہ تعالیٰ ہی پر بھروسہ رکھنا چاہیئے'),
(693, 5, 24, 'قوم نے جواب دیا کہ اے موسیٰ! جب تک وه وہاں ہیں تب تک ہم ہرگز وہاں نہ جائیں گے، اس لئے تم اور تمہارا پروردگار جا کر دونوں ہی لڑ بھڑ لو، ہم یہیں بیٹھے ہوئے ہیں'),
(694, 5, 25, 'موسیٰ (علیہ السلام) کہنے لگے الٰہی! مجھے تو بجز اپنے اور میرے بھائی کے کسی اور پر کوئی اختیار نہیں، پس تو ہم میں اور ان نافرمانوں میں جدائی کردے'),
(695, 5, 26, 'ارشاد ہوا کہ اب زمین ان پر چالیس سال تک حرام کر دی گئی ہے، یہ خانہ بدوش ادھر ادھر سرگرداں پھرتے رہیں گے اس لئے تم ان فاسقوں کے بارے میں غمگین نہ ہونا'),
(696, 5, 27, 'آدم (علیہ السلام) کے دونوں بیٹوں کا کھرا کھرا حال بھی انہیں سنا دو، ان دونوں نے ایک نذرانہ پیش کیا، ان میں سے ایک کی نذر تو قبول ہوگئی اور دوسرے کی مقبول نہ ہوئی تو وه کہنے لگا کہ میں تجھے مار ہی ڈالوں گا، اس نے کہا اللہ تعالیٰ تقویٰ والوں کا ہی عمل قبول کرتا ہے'),
(697, 5, 28, 'گو تو میرے قتل کے لئے دست درازی کرے لیکن میں تیرے قتل کی طرف ہرگز اپنے ہاتھ نہ بڑھاؤں گا، میں تو اللہ تعالیٰ پروردگار عالم سے خوف کھاتا ہوں'),
(698, 5, 29, 'میں تو چاہتا ہوں کہ تو میرا گناه اور اپنے گناه اپنے سر پر رکھ لے اور دوزخیوں میں شامل ہوجائے، ﻇالموں کا یہی بدلہ ہے'),
(699, 5, 30, 'پس اسے اس کے نفس نے اپنے بھائی کے قتل پر آماده کر دیا اور اس نے اسے قتل کر ڈاﻻ، جس سے نقصان پانے والوں میں سے ہوگیا'),
(700, 5, 31, 'پھر اللہ تعالیٰ نے ایک کوے کو بھیجا جو زمین کھود رہا تھا تاکہ اسے دکھائے کہ وه کس طرح اپنے بھائی کی نعش کو چھپا دے، وه کہنے لگا، ہائے افسوس! کیا میں ایسا کرنے سے بھی گیا گزرا ہوگیا کہ اس کوے کی طرح اپنے بھائی کی ﻻش کو دفنا دیتا؟ پھر تو (بڑا ہی) پشیمان اور شرمنده ہوگیا'),
(701, 5, 32, 'اسی وجہ سے ہم نے بنی اسرائیل پر یہ لکھ دیا کہ جو شخص کسی کو بغیر اس کے کہ وه کسی کا قاتل ہو یا زمین میں فساد مچانے واﻻ ہو، قتل کر ڈالے تو گویا اس نے تمام لوگوں کو قتل کردیا، اور جو شخص کسی ایک کی جان بچا لے، اس نے گویا تمام لوگوں کو زنده کردیا اور ان کے پاس ہمارے بہت سے رسول ﻇاہر دلیلیں لے کر آئے لیکن پھر اس کے بعد بھی ان میں کے اکثر لوگ زمین میں ﻇلم و زیادتی اور زبردستی کرنے والے ہی رہے'),
(702, 5, 33, 'جو اللہ تعالیٰ سے اور اس کے رسول سے لڑیں اور زمین میں فساد کرتے پھریں ان کی سزا یہی ہے کہ وه قتل کر دیئے جائیں یا سولی چڑھا دیئے جائیں یا مخالف جانب سے ان کے ہاتھ پاوں کاٹ دیئے جائیں، یا انہیں جلاوطن کر دیا جائے، یہ تو ہوئی ان کی دنیوی ذلت اور خواری، اور آخرت میں ان کے لئے بڑا بھاری عذاب ہے'),
(703, 5, 34, 'ہاں جو لوگ اس سے پہلے توبہ کر لیں کہ تم ان پر قابو پالو تو یقین مانو کہ اللہ تعالیٰ بہت بڑی بخشش اور رحم و کرم واﻻ ہے'),
(704, 5, 35, 'مسلمانو! اللہ تعالیٰ سے ڈرتے رہو اور اس کا قرب تلاش کرو اور اس کی راه میں جہاد کرو تاکہ تمہارا بھلا ہو'),
(705, 5, 36, 'یقین مانو کہ کافروں کے لئے اگر وه سب کچھ ہو جو ساری زمین میں ہے بلکہ اسی کے مثل اور بھی ہو اور وه اس سب کو قیامت کے دن کے عذاب کے بدلے فدیے میں دینا چاہیں تو بھی ناممکن ہے کہ ان کا فدیہ قبول کرلیا جائے، ان کے لئے تو درد ناک عذاب ہی ہے'),
(706, 5, 37, 'یہ چاہیں گے کہ دوزخ میں سے نکل جائیں لیکن یہ ہرگز اس میں سے نہ نکل سکیں گے، ان کے لئے تو دوامی عذاب ہیں'),
(707, 5, 38, 'چوری کرنے والے مرد اور عورت کے ہاتھ کاٹ دیا کرو۔ یہ بدلہ ہے اس کا جو انہوں نے کیا۔ عذاب، اللہ کی طرف سے اور اللہ تعالیٰ قوت وحکمت واﻻ ہے'),
(708, 5, 39, 'جو شخص اپنے گناه کے بعد توبہ کرلے اور اصلاح کرلے تو اللہ تعالیٰ رحمت کے ساتھ اس کی طرف لوٹتا ہے۔ یقیناً اللہ تعالیٰ معاف فرمانے واﻻ مہربانی کرنے واﻻ ہے'),
(709, 5, 40, 'کیا تجھے معلوم نہیں کہ اللہ تعالیٰ ہی کے لئے زمین و آسمان کی بادشاہت ہے؟ جسے چاہے سزا دے اور جسے چاہے معاف کردے، اللہ تعالیٰ ہر چیز پر قادر ہے'),
(710, 5, 41, 'اے رسول! آپ ان لوگوں کے پیچھے نہ کُڑھیے جو کفر میں سبقت کر رہے ہیں خواه وه ان (منافقوں) میں سے ہوں جو زبانی تو ایمان کا دعویٰ کرتے ہیں لیکن حقیقتاً ان کے دل باایمان نہیں اور یہودیوں میں سے کچھ لوگ ایسے ہیں جو غلط باتیں سننے کے عادی ہیں اور ان لوگوں کے جاسوس ہیں جو اب تک آپ کے پاس نہیں آئے، وه کلمات کے اصلی موقعہ کو چھوڑ کر انہیں متغیر کردیا کرتے ہیں، کہتے ہیں کہ اگر تم یہی حکم دیئے جاؤ تو قبول کرلینا اور اگر یہ حکم نہ دیئے جاؤ تو الگ تھلگ رہنا اور جس کا خراب کرنا اللہ کو منظور ہو تو آپ اس کے لئے خدائی ہدایت میں سے کسی چیز کے مختار نہیں۔ اللہ تعالیٰ کا اراده ان کے دلوں کو پاک کرنے کا نہیں، ان کے لئے دنیا میں بھی بڑی ذلت اور رسوائی ہے اور آخرت میں بھی ان کے لئے بڑی سخت سزا ہے'),
(711, 5, 42, 'یہ کان لگا لگا کر جھوٹ کے سننے والے اور جی بھر بھر کر حرام کے کھانے والے ہیں، اگر یہ تمہارے پاس آئیں تو تمہیں اختیار ہے خواه ان کے آپس کا فیصلہ کرو خواه ان کو ٹال دو، اگر تم ان سے منھ بھی پھیرو گے تو بھی یہ تم کو ہرگز کوئی ضرر نہیں پہنچا سکتے، اور اگر تم فیصلہ کرو تو ان میں عدل وانصاف کے ساتھ فیصلہ کرو، یقیناً عدل والوں کے ساتھ اللہ محبت رکھتا ہے'),
(712, 5, 43, '(تعجب کی بات ہے کہ) وه کیسے اپنے پاس تورات ہوتے ہوئے جس میں احکام الٰہی ہیں تم کو منصف بناتے ہیں پھر اس کے بعد بھی پھر جاتے ہیں، دراصل یہ ایمان ویقین والے ہیں ہی نہیں'),
(713, 5, 44, 'ہم نے تورات نازل فرمائی ہے جس میں ہدایت ونور ہے، یہودیوں میں اسی تورات کے ساتھ اللہ تعالیٰ کے ماننے والے انبیا (علیہم السلام) اور اہل اللہ اور علما فیصلے کرتے تھے کیونکہ انہیں اللہ کی اس کتاب کی حفاﻇت کا حکم دیا گیا تھا۔ اور وه اس پر اقراری گواه تھے اب تمہیں چاہیئے کہ لوگوں سے نہ ڈرو اور صرف میرا ڈر رکھو، میری آیتوں کو تھوڑے تھوڑے مول پر نہ بیچو، جو لوگ اللہ کی اتاری ہوئی وحی کے ساتھ فیصلے نہ کریں وه (پورے اور پختہ) کافر ہیں'),
(714, 5, 45, 'اور ہم نے یہودیوں کے ذمہ تورات میں یہ بات مقرر کر دی تھی کہ جان کے بدلے جان اور آنکھ کے بدلے آنکھ اور ناک کے بدلے ناک اور کان کے بدلے کان اور دانت کے بدلے دانت اور خاص زخموں کا بھی بدلہ ہے، پھر جو شخص اس کو معاف کردے تو وه اس کے لئے کفاره ہے، اور جو لوگ اللہ کے نازل کئے ہوئے کے مطابق حکم نہ کریں، وہی لوگ ﻇالم ہیں'),
(715, 5, 46, 'اور ہم نے ان کے پیچھے عیسیٰ بن مریم کو بھیجا جو اپنے سے پہلے کی کتاب یعنی تورات کی تصدیق کرنے والے تھے اور ہم نے انہیں انجیل عطا فرمائی جس میں نور اور ہدایت تھی اور وه اپنے سے پہلے کی کتاب تورات کی تصدیق کرتی تھی اور وه سراسر ہدایت ونصیحت تھی پارسا لوگوں کے لئے'),
(716, 5, 47, 'اور انجیل والوں کو بھی چاہئے کہ اللہ تعالیٰ نے جو کچھ انجیل میں نازل فرمایا ہے اسی کے مطابق حکم کریں اور جو اللہ تعالیٰ کے نازل کرده سے ہی حکم نہ کریں وه (بدکار) فاسق ہیں'),
(717, 5, 48, 'اور ہم نے آپ کی طرف حق کے ساتھ یہ کتاب نازل فرمائی ہے جو اپنے سے اگلی کتابوں کی تصدیق کرنے والی ہے اور ان کی محافﻆ ہے۔ اس لئے آپ ان کے آپس کے معاملات میں اسی اللہ کی اتاری ہوئی کتاب کے ساتھ حکم کیجیئے، اس حق سے ہٹ کر ان کی خواہشوں کے پیچھے نہ جائیے تم میں سے ہر ایک کے لئے ہم نے ایک دستور اور راه مقرر کردی ہے۔ اگر منظور مولیٰ ہوتا تو تم سب کو ایک ہی امت بنا دیتا، لیکن اس کی چاہت ہے کہ جو تمہیں دیا ہے اس میں تمہیں آزمائے، تم نیکیوں کی طرف جلدی کرو، تم سب کا رجوع اللہ ہی کی طرف ہے، پھر وه تمہیں ہر وه چیز بتا دے گا جس میں تم اختلاف کرتے رہتے ہو'),
(718, 5, 49, 'آپ ان کے معاملات میں خدا کی نازل کرده وحی کے مطابق ہی حکم کیا کیجیئے، ان کی خواہشوں کی تابعداری نہ کیجیئے اور ان سے ہوشیار رہیئے کہ کہیں یہ آپ کو اللہ کے اتارے ہوئے کسی حکم سے ادھر ادھر نہ کریں، اگر یہ لوگ منھ پھیر لیں تو یقین کریں کہ اللہ کا اراده یہی ہے کہ انہیں ان کے بعض گناہوں کی سزا دے ہی ڈالے اور اکثر لوگ نا فرمان ہی ہوتے ہیں'),
(719, 5, 50, 'کیا یہ لوگ پھر سے جاہلیت کا فیصلہ چاہتے ہیں یقین رکھنے والے لوگوں کے لئے اللہ تعالیٰ سے بہتر فیصلے اور حکم کرنے واﻻ کون ہوسکتا ہے؟'),
(720, 5, 51, 'اے ایمان والو! تم یہود ونصاریٰ کو دوست نہ بناؤ یہ تو آپس میں ہی ایک دوسرے کے دوست ہیں۔ تم میں سے جو بھی ان میں کسی سے دوستی کرے وه بےشک انہی میں سے ہے، ﻇالموں کو اللہ تعالیٰ ہرگز راه راست نہیں دکھاتا'),
(721, 5, 52, 'آپ دیکھیں گے کہ جن کے دلوں میں بیماری ہے وه دوڑ دوڑ کر ان میں گھس رہے ہیں اور کہتے ہیں کہ ہمیں خطره ہے، ایسا نہ ہو کہ کوئی حادﺛہ ہم پر پڑ جائے بہت ممکن ہے کہ اللہ تعالیٰ فتح دے دے۔ یا اپنے پاس سے کوئی اور چیز ﻻئے پھر تو یہ اپنے دلوں میں چھپائی ہوئی باتوں پر (بے طرح) نادم ہونے لگیں گے'),
(722, 5, 53, 'اور ایمان والے کہیں گے، کیا یہی وه لوگ ہیں جو بڑے مبالغہ سے اللہ کی قسمیں کھا کھا کر کہتے ہیں کہ ہم تمہارے ساتھ ہیں۔ ان کے اعمال غارت ہوئے اور یہ ناکام ہوگئے'),
(723, 5, 54, 'اے ایمان والو! تم میں سے جو شخص اپنے دین سے پھر جائے تو اللہ تعالیٰ بہت جلد ایسی قوم کو ﻻئے گا جو اللہ کی محبوب ہوگی اور وه بھی اللہ سے محبت رکھتی ہوگی وه نرم دل ہوں گے مسلمانوں پر اور سخت اور تیز ہوں گے کفار پر، اللہ کی راه میں جہاد کریں گے اور کسی ملامت کرنے والے کی ملامت کی پرواه بھی نہ کریں گے، یہ ہے اللہ تعالیٰ کا فضل جسے چاہے دے، اللہ تعالیٰ بڑی وسعت واﻻ اور زبردست علم واﻻ ہے'),
(724, 5, 55, '(مسلمانو)! تمہارا دوست خود اللہ ہے اور اس کا رسول ہے اور ایمان والے ہیں جو نمازوں کی پابندی کرتے ہیں اور زکوٰة ادا کرتے ہیں اور وه رکوع (خشوع وخضوع) کرنے والے ہیں'),
(725, 5, 56, 'اور جو شخص اللہ تعالیٰ سے اور اس کے رسول سے اور مسلمانوں سے دوستی کرے، وه یقین مانے کہ اللہ تعالیٰ کی جماعت ہی غالب رہے گی'),
(726, 5, 57, 'مسلمانو! ان لوگوں کو دوست نہ بناؤ جو تمہارے دین کو ہنسی کھیل بنائے ہوئے ہیں (خواه) وه ان میں سے ہوں جو تم سے پہلے کتاب دیئے گئے یا کفار ہوں اگر تم مومن ہو تو اللہ تعالیٰ سے ڈرتے رہو۔'),
(727, 5, 58, 'اور جب تم نماز کے لئے پکارتے ہو تو وہ اسے ہنسی کھیل ٹھرا لیتے ہیں۔ یہ اس واسطے کہ بےعقل ہیں'),
(728, 5, 59, 'آپ کہہ دیجیئے اے یہودیوں اور نصرانیو! تم ہم سے صرف اس وجہ سے دشمنیاں کر رہے ہو کہ ہم اللہ تعالیٰ پر اور جو کچھ ہماری جانب نازل کیا گیا ہے اور جو کچھ اس سے پہلے اتارا گیا ہے اس پر ایمان ﻻئے ہیں اور اس لئے بھی کہ تم میں اکثر فاسق ہیں'),
(729, 5, 60, 'کہہ دیجیئے کہ کیا میں تمہیں بتاؤں؟ کہ اس سے بھی زیاده برے اجر پانے واﻻ اللہ تعالیٰ کے نزدیک کون ہے؟ وه جس پر اللہ تعالیٰ نے لعنت کی اور اس پر وه غصہ ہوا اور ان میں سے بعض کو بندر اور سور بنا دیا اور جنہوں نے معبودان باطل کی پرستش کی، یہی لوگ بدتر درجے والے ہیں اور یہی راه راست سے بہت زیاده بھٹکنے والے ہیں'),
(730, 5, 61, 'اور جب تمہارے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان ﻻئے حاﻻنکہ وه کفر لئے ہوئے ہی آئے تھے اور اسی کفر کے ساتھ ہی گئے بھی اور یہ جو کچھ چھپا رہے ہیں اسے اللہ تعالیٰ خوب جانتا ہے'),
(731, 5, 62, 'آپ دیکھیں گے کہ ان میں سے اکثر گناه کے کاموں کی طرف اور ﻇلم وزیادتی کی طرف اور مال حرام کھانے کی طرف لپک رہے ہیں، جو کچھ یہ کر رہے ہیں وه نہایت برے کام ہیں'),
(732, 5, 63, 'انہیں ان کے عابد وعالم جھوٹ باتوں کے کہنے اور حرام چیزوں کے کھانے سے کیوں نہیں روکتے، بےشک برا کام ہے جو یہ کر رہے ہیں'),
(733, 5, 64, 'اور یہودیوں نے کہا کہ اللہ تعالیٰ کے ہاتھ بندھے ہوئے ہیں۔ انہی کے ہاتھ بندھے ہوئے ہیں اور ان کے اس قول کی وجہ سے ان پر لعنت کی گئی، بلکہ اللہ تعالیٰ کے دونوں ہاتھ کھلے ہوئے ہیں۔ جس طرح چاہتا ہے خرچ کرتا ہے اور جو کچھ تیری طرف تیرے رب کی جانب سے اتارا جاتا ہے وه ان میں سے اکثر کو تو سرکشی اور کفر میں اور بڑھا دیتا ہے اور ہم نے ان میں آپس میں ہی قیامت تک کے لئے عداوت اور بغض ڈال دیا ہے، وه جب کبھی لڑائی کی آگ کو بھڑکانا چاہتے ہیں تو اللہ تعالیٰ اسے بجھا دیتا ہے، یہ ملک بھر میں شر وفساد مچاتے پھرتے ہیں اور اللہ تعالیٰ فسادیوں سے محبت نہیں کرتا'),
(734, 5, 65, 'اور اگر یہ اہل کتاب ایمان ﻻتے اور تقویٰ اختیار کرتے تو ہم ان کی تمام برائیاں معاف فرما دیتے اور ضرور انہیں راحت و آرام کی جنتوں میں لے جاتے'),
(735, 5, 66, 'اور اگر یہ لوگ تورات وانجیل اور ان کی جانب جو کچھ اللہ تعالیٰ کی طرف سے نازل فرمایا گیا ہے، ان کے پورے پابند رہتے تو یہ لوگ اپنے اوپر سے اور نیچے سے روزیاں پاتے اور کھاتے، ایک جماعت تو ان میں سے درمیانہ روش کی ہے، باقی ان میں سے بہت سے لوگوں کے برے اعمال ہیں'),
(736, 5, 67, 'اے رسول جو کچھ بھی آپ کی طرف آپ کے رب کی جانب سے نازل کیا گیا ہے پہنچا دیجیئے۔ اگر آپ نے ایسا نہ کیا تو آپ نے اللہ کی رسالت ادا نہیں کی، اور آپ کو اللہ تعالیٰ لوگوں سے بچا لے گا بےشک اللہ تعالیٰ کافر لوگوں کو ہدایت نہیں دیتا'),
(737, 5, 68, 'آپ کہہ دیجیئے کہ اے اہل کتاب! تم دراصل کسی چیز پر نہیں جب تک کہ تورات وانجیل کو اور جو کچھ تمہاری طرف تمہارے رب کی سے طرف اتارا گیا ہے قائم نہ کرو، جو کچھ آپ کی جانب آپ کے رب کی طرف سے اترا ہے وه ان میں سے بہتوں کو شرارت اور انکار میں اور بھی بڑھائے گا ہی، تو آپ ان کافروں پر غمگین نہ ہوں'),
(738, 5, 69, 'مسلمان، یہودی، ستاره پرست اور نصرانی کوئی ہو، جو بھی اللہ تعالیٰ پر اور قیامت کے دن پر ایمان ﻻئے اور نیک عمل کرے وه محض بےخوف رہے گا اور بالکل بے غم ہوجائے گا'),
(739, 5, 70, 'ہم نے بالیقین بنیاسرائیل سے عہد وپیمان لیا اور ان کی طرف رسولوں کو بھیجا، جب کبھی رسول ان کے پاس وه احکام لے کر آئے جو ان کی اپنی منشا کے خلاف تھے تو انہوں نے ان کی ایک جماعت کی تکذیب کی اور ایک جماعت کو قتل کردیا'),
(740, 5, 71, 'اور سمجھ بیٹھے کہ کوئی پکڑ نہ ہوگی، پس اندھے بہرے بن بیٹھے، پھر اللہ تعالیٰ نے ان کی توبہ قبول کی، اس کے بعد بھی ان میں سے اکثر اندھے بہرے ہوگئے۔ اللہ تعالیٰ ان کے اعمال کو بخوبی دیکھنے واﻻ ہے'),
(741, 5, 72, 'بے شک وه لوگ کافر ہوگئے جن کا قول ہے کہ مسیح ابن مریم ہی اللہ ہے حاﻻنکہ خود مسیح نے ان سے کہا تھا کہ اے بنی اسرائیل! اللہ ہی کی عبادت کرو جو میرا اور تمہارا سب کا رب ہے، یقین مانو کہ جو شخص اللہ کے ساتھ شریک کرتا ہے اللہ تعالیٰ نے اس پر جنت حرام کر دی ہے، اس کا ٹھکانہ جہنم ہی ہے اور گنہگاروں کی مدد کرنے واﻻ کوئی نہیں ہوگا'),
(742, 5, 73, 'وه لوگ بھی قطعاً کافر ہوگئے جنہوں نے کہا، اللہ تین میں کا تیسرا ہے، دراصل سوا اللہ تعالیٰ کے کوئی معبود نہیں۔ اگر یہ لوگ اپنے اس قول سے باز نہ رہے تو ان میں سے جو کفر پر رہیں گے، انہیں المناک عذاب ضرور پہنچے گا'),
(743, 5, 74, 'یہ لوگ کیوں اللہ تعالیٰ کی طرف نہیں جھکتے اور کیوں استغفار نہیں کرتے؟ اللہ تعالیٰ تو بہت ہی بخشنے واﻻ اور بڑا ہی مہربان ہے'),
(744, 5, 75, 'مسیح ابن مریم سوا پیغمبر ہونے کے اور کچھ بھی نہیں، اس سے پہلے بھی بہت سے پیغمبر ہوچکے ہیں ان کی والده ایک راست باز عورت تھیں دونوں ماں بیٹے کھانا کھایا کرتے تھے، آپ دیکھیے کہ کس طرح ہم ان کے سامنے دلیلیں رکھتے ہیں پھر غور کیجیئے کہ کس طرح وه پھرے جاتے ہیں'),
(745, 5, 76, 'آپ کہہ دیجیئے کہ کیا تم اللہ کے سوا ان کی عبادت کرتے ہو جو نہ تمہارے کسی نقصان کے مالک ہیں نہ کسی نفع کے، اللہ ہی خوب سننے اور پوری طرح جاننے واﻻ ہے'),
(746, 5, 77, 'کہہ دیجیئے اے اہل کتاب! اپنے دین میں ناحق غلو اور زیادتی نہ کرو اور ان لوگوں کی نفسانی خواہشوں کی پیروی نہ کرو جو پہلے سے بہک چکے ہیں اور بہتوں کو بہکا بھی چکے ہیں اور سیدھی راه سے ہٹ گئے ہیں'),
(747, 5, 78, 'بنی اسرائیل کے کافروں پر (حضرت) داؤد (علیہ السلام) اور (حضرت) عیسیٰ بن مریم (علیہ السلام) کی زبانی لعنت کی گئی اس وجہ سے کہ وه نافرمانیاں کرتے تھے اور حد سے آگے بڑھ جاتے تھے'),
(748, 5, 79, 'آپس میں ایک دوسرے کو برے کاموں سے جو وه کرتے تھے روکتے نہ تھے جو کچھ بھی یہ کرتے تھے یقیناً وه بہت برا تھا'),
(749, 5, 80, 'ان میں سے بہت سے لوگوں کو آپ دیکھیں گے کہ وه کافروں سے دوستیاں کرتے ہیں، جو کچھ انہوں نے اپنے لئے آگے بھیج رکھا ہے وه بہت برا ہے کہ اللہ تعالیٰ ان سے ناراض ہوا اور وه ہمیشہ عذاب میں رہیں گے'),
(750, 5, 81, 'اگر انہیں اللہ تعالیٰ پر اور نبی پر اور جو نازل کیا گیا ہے اس پر ایمان ہوتا تو یہ کفار سے دوستیاں نہ کرتے، لیکن ان میں کے اکثر لوگ فاسق ہیں'),
(751, 5, 82, 'یقیناً آپ ایمان والوں کا سب سے زیاده دشمن یہودیوں اور مشرکوں کو پائیں گے اور ایمان والوں سے سب سے زیاده دوستی کے قریب آپ یقیناً انہیں پائیں گے جو اپنے آپ کو نصاریٰ کہتے ہیں، یہ اس لئے کہ ان میں علما اور عبادت کے لئے گوشہ نشین افراد پائے جاتے ہیں اور اس وجہ سے کہ وه تکبر نہیں کرتے'),
(752, 5, 83, 'اور جب وه رسول کی طرف نازل کرده (کلام) کو سنتے ہیں تو آپ ان کی آنکھیں آنسو سے بہتی ہوئی دیکھتے ہیں اس سبب سے کہ انہوں نے حق کو پہچان لیا، وه کہتے ہیں کہ اے ہمارے رب! ہم ایمان لے آئے پس تو ہم کو بھی ان لوگوں کے ساتھ لکھ لے جو تصدیق کرتے ہیں'),
(753, 5, 84, 'اور ہمارے پاس کون سا عذر ہے کہ ہم اللہ تعالیٰ پر اور جو حق ہم کو پہنچا ہے اس پر ایمان نہ ﻻئیں اور ہم اس بات کی امید رکھتے ہیں کہ ہمارا رب ہم کو نیک لوگوں کی رفاقت میں داخل کردے گا'),
(754, 5, 85, 'اس لئے ان کو اللہ تعالیٰ ان کے اس قول کی وجہ سے ایسے باغ دے گا جن کے نیچے نہریں جاری ہوں گی، یہ ان میں ہمیشہ ہمیشہ رہیں گے اور نیک لوگوں کا یہی بدلہ ہے'),
(755, 5, 86, 'اور جن لوگوں نے کفر کیا اور ہماری آیات کو جھٹلاتے رہے وه لوگ دوزخ والے ہیں'),
(756, 5, 87, 'اے ایمان والو! اللہ تعالیٰ نے جو پاکیزه چیزیں تمہارے واسطے حلال کی ہیں ان کو حرام مت کرو اور حد سے آگے مت نکلو، بےشک اللہ تعالیٰ حد سے نکلنے والوں کو پسند نہیں کرتا'),
(757, 5, 88, 'اور اللہ تعالیٰ نے جو چیزیں تم کو دی ہیں ان میں سے حلال مرغوب چیزیں کھاؤ اور اللہ تعالیٰ سے ڈرو جس پر تم ایمان رکھتے ہو'),
(758, 5, 89, 'اللہ تعالیٰ تمہاری قسموں میں لغو قسم پر تم سے مؤاخذہ نہیں فرماتا لیکن مؤاخذہ اس پر فرماتا ہے کہ تم جن قسموں کو مضبوط کردو۔ اس کا کفاره دس محتاجوں کو کھانا دینا ہے اوسط درجے کا جو اپنے گھر والوں کو کھلاتے ہو یا ان کو کپڑا دینا یا ایک غلام یا لونڈی آزاد کرنا ہے اور جس کو مقدور نہ ہو تو تین دن کے روزے ہیں یہ تمہاری قسموں کا کفاره ہے جب کہ تم قسم کھا لو اور اپنی قسموں کا خیال رکھو! اسی طرح اللہ تعالیٰ تمہارے واسطے اپنے احکام بیان فرماتا ہے تاکہ تم شکر کرو'),
(759, 5, 90, 'اے ایمان والو! بات یہی ہے کہ شراب اور جوا اور تھان اور فال نکالنے کے پانسے کے تیر، یہ سب گندی باتیں، شیطانی کام ہیں ان سے بالکل الگ رہو تاکہ تم فلاح یاب ہو'),
(760, 5, 91, 'شیطان تو یوں چاہتا ہے کہ شراب اور جوئے کے ذریعے سے تمہارے آپس میں عداوت اور بغض واقع کرا دے اور اللہ تعالیٰ کی یاد سے اور نماز سے تم کو باز رکھے سو اب بھی باز آجاؤ'),
(761, 5, 92, 'اور تم اللہ تعالیٰ کی اطاعت کرتے رہو اور رسول کی اطاعت کرتے رہو اور احتیاط رکھو۔ اگر اعراض کرو گے تو یہ جان رکھو کہ ہمارے رسول کے ذمہ صرف صاف صاف پہنچا دینا ہے'),
(762, 5, 93, 'ایسے لوگوں پر جو کہ ایمان رکھتے ہوں اور نیک کام کرتے ہوں اس چیز میں کوئی گناه نہیں جس کو وه کھاتے پیتے ہوں جبکہ وه لوگ تقویٰ رکھتے ہوں اور ایمان رکھتے ہوں اور نیک کام کرتے ہوں پھر پرہیزگاری کرتے ہوں اور ایمان رکھتے ہوں پھر پرہیزگاری کرتے ہوں اور خوب نیک عمل کرتے ہوں، اللہ ایسے نیکوکاروں سے محبت رکھتا ہے'),
(763, 5, 94, 'اے ایمان والو! اللہ تعالیٰ قدرے شکار سے تمہارا امتحان کرے گا جن تک تمہارے ہاتھ اور تمہارے نیزے پہنچ سکیں گے تاکہ اللہ تعالیٰ معلوم کرلے کہ کون شخص اس سے بن دیکھے ڈرتا ہے سو جو شخص اس کے بعد حد سے نکلے گا اس کے واسطے دردناک سزا ہے'),
(764, 5, 95, 'اے ایمان والو! (وحشی) شکار کو قتل مت کرو جب کہ تم حالت احرام میں ہو۔ اور جو شخص تم میں سے اس کو جان بوجھ کر قتل کرے گا تو اس پر فدیہ واجب ہوگا جو کہ مساوی ہوگا اس جانور کے جس کو اس نے قتل کیا ہے جس کا فیصلہ تم میں سے دو معتبر شخص کردیں خواه وه فدیہ خاص چوپایوں میں سے ہو جو نیاز کے طور پر کعبہ تک پہنچایا جائے اور خواه کفاره مساکین کو دے دیا جائے اور خواه اس کے برابر روزے رکھ لئے جائیں تاکہ اپنے کئے شامت کا مزه چکھے، اللہ تعالیٰ نے گذشتہ کو معاف کردیا اور جو شخص پھر ایسی ہی حرکت کرے گا تو اللہ انتقام لے گا اور اللہ زبردست ہے انتقام لینے واﻻ'),
(765, 5, 96, 'تمہارے لئے دریا کا شکار پکڑنا اور اس کا کھانا حلال کیا گیا ہے۔ تمہارے فائده کے واسطے اور مسافروں کے واسطے اور خشکی کا شکار پکڑنا تمہارے لئے حرام کیا گیا ہے جب تک تم حالت احرام میں رہو اور اللہ تعالیٰ سے ڈرو جس کے پاس جمع کئے جاؤ گے'),
(766, 5, 97, 'اللہ نے کعبہ کو جو ادب کا مکان ہے لوگوں کے قائم رہنے کا سبب قرار دے دیا اور عزت والے مہینہ کو بھی اور حرم میں قربانی ہونے والے جانور کو بھی اور ان جانوروں کو بھی جن کے گلے میں پٹے ہوں یہ اس لئے تاکہ تم اس بات کا یقین کر لو کہ بےشک اللہ تمام آسمانوں اور زمین کے اندر کی چیزوں کا علم رکھتا ہے اور بےشک اللہ سب چیزوں کو خوب جانتا ہے'),
(767, 5, 98, 'تم یقین جانو کہ اللہ تعالیٰ سزا بھی سخت دینے واﻻ ہے اور اللہ تعالیٰ بڑی مغفرت اور بڑی رحمت واﻻ بھی ہے'),
(768, 5, 99, 'رسول کے ذمہ تو صرف پہنچانا ہے۔ اور اللہ تعالیٰ سب جانتا ہے جو کچھ تم ﻇاہر کرتے ہو اور جو کچھ پوشیده رکھتے ہو'),
(769, 5, 100, 'آپ فرما دیجیئے کہ ناپاک اور پاک برابر نہیں گو آپ کو ناپاک کی کثرت بھلی لگتی ہو اللہ تعالیٰ سے ڈرتے رہو اے عقل مندو! تاکہ تم کامیاب ہو'),
(770, 5, 101, 'اے ایمان والو! ایسی باتیں مت پوچھو کہ اگر تم پر ﻇاہر کر دی جائیں تو تمہیں ناگوار ہوں اور اگر تم زمانہٴ نزول قرآن میں ان باتوں کو پوچھو گے تو تم پر ﻇاہر کردی جائیں گی سواﻻت گزشتہ اللہ نے معاف کردیئے اور اللہ بڑی مغفرت واﻻ بڑے حلم واﻻ ہے'),
(771, 5, 102, 'ایسی باتیں تم سے پہلے اور لوگوں نے بھی پوچھی تھیں، پھر ان باتوں کے منکر ہوگئے'),
(772, 5, 103, 'اللہ تعالیٰ نے نہ بحیره کو مشروع کیا ہے اور نہ سائبہ کو اور نہ وصیلہ کو اور نہ حام کو لیکن جو لوگ کافر ہیں وه اللہ تعالیٰ پر جھوٹ لگاتے ہیں اور اکثر کافر عقل نہیں رکھتے'),
(773, 5, 104, 'اور جب ان سے کہا جاتا ہے کہ اللہ تعالیٰ نے جو احکام نازل فرمائے ہیں ان کی طرف اور رسول کی طرف رجوع کرو تو کہتے ہیں کہ ہم کو وہی کافی ہے جس پر ہم نے اپنے بڑوں کو پایا، کیا اگر چہ ان کے بڑے نہ کچھ سمجھ رکھتے ہوں اور نہ ہدایت رکھتے ہوں'),
(774, 5, 105, 'اے ایمان والوں! اپنی فکر کرو، جب تم راه راست پر چل رہے ہو تو جو شخص گمراه رہے اس سے تمہارا کوئی نقصان نہیں۔ اللہ ہی کے پاس تم سب کو جانا ہے پھر وه تم سب کو بتلا دے گا جو کچھ تم سب کرتے تھے'),
(775, 5, 106, 'اے ایمان والو! تمہارے آپس میں دو شخص کا گواه ہونا مناسب ہے جبکہ تم میں سے کسی کو موت آنے لگے اور وصیت کرنے کا وقت ہو وه دو شخص ایسے ہوں کہ دیندار ہوں خواه تم میں سے ہوں یا غیر لوگوں میں سے دو شخص ہوں اگر تم کہیں سفر میں گئے ہو اور تمہیں موت آ جائے اگر تم کو شبہ ہو تو ان دونوں کو بعد نماز روک لو پھر دونوں اللہ کی قسم کھائیں کہ ہم اس قسم کے عوض کوئی نفع نہیں لینا چاہتے اگر چہ کوئی قرابت دار بھی ہو اور اللہ تعالیٰ کی بات کو ہم پوشیده نہ کریں گے، ہم اس حالت میں سخت گنہگار ہوں گے'),
(776, 5, 107, 'پھر اگر اس کی اطلاع ہو کہ وه دونوں گواه کسی گناه کے مرتکب ہوئے ہیں تو ان لوگوں میں سے جن کے مقابلہ میں گناه کا ارتکاب ہوا تھا اور دو شخص جو سب میں قریب تر ہیں جہاں وه دونوں کھڑے ہوئے تھے یہ دونوں کھڑے ہوں پھر دونوں اللہ کی قسم کھائیں کہ بالیقین ہماری یہ قسم ان دونوں کی اس قسم سے زیاده راست ہے اور ہم نے ذرا تجاوز نہیں کیا، ہم اس حالت میں سخت ﻇالم ہوں گے'),
(777, 5, 108, 'یہ قریب ذریعہ ہے اس امر کا کہ وه لوگ واقعہ کو ٹھیک طور پر ﻇاہر کریں یا اس بات سے ڈر جائیں کہ ان سے قسمیں لینے کے بعد قسمیں الٹی پڑ جائیں گی اور اللہ تعالیٰ سے ڈرو اور سنو! اور اللہ تعالیٰ فاسق لوگوں کو ہدایت نہیں کرتا'),
(778, 5, 109, 'جس روز اللہ تعالیٰ تمام پیغمبروں کو جمع کرے گا، پھر ارشاد فرمائے گا کہ تم کو کیا جواب ملا تھا، وه عرض کریں گے کہ ہم کو کچھ خبر نہیں تو ہی بےشک پوشیده باتوں کو پورا جاننے واﻻ ہے'),
(779, 5, 110, 'جب کہ اللہ تعالیٰ ارشاد فرمائے گا کہ اے عیسیٰ بن مریم! میرا انعام یاد کرو جو تم پر اور تمہاری والده پر ہوا ہے، جب میں نے تم کو روح القدس سے تائید دی۔ تم لوگوں سے کلام کرتے تھے گود میں بھی اور بڑی عمر میں بھی اور جب کہ میں نے تم کو کتاب اور حکمت کی باتیں اور تورات اور انجیل کی تعلیم دی اور جب کہ تم میرے حکم سے گارے سے ایک شکل بناتے تھے جیسے پرنده کی شکل ہوتی ہے پھر تم اس کے اندر پھونک مار دیتے تھے جس سے وه پرند بن جاتا تھا میرے حکم سے اور تم اچھا کر دیتے تھے مادرزاد اندھے کو اور کوڑھی کو میرے حکم سے اور جب کہ تم مردوں کو نکال کر کھڑا کرلیتے تھے میرے حکم سے اور جب کہ میں نے بنی اسرائیل کو تم سے باز رکھا جب تم ان کے پاس دلیلیں لے کر آئے تھے پھر ان میں جو کافر تھے انہوں نے کہا تھا کہ بجز کھلے جادو کے یہ اور کچھ بھی نہیں'),
(780, 5, 111, 'اور جب کہ میں نے حواریین کو حکم دیا کہ تم مجھ پر اور میرے رسول پر ایمان ﻻؤ، انہوں نے کہا کہ ہم ایمان ﻻئے اور آپ شاہد رہیئے کہ ہم پورے فرماں بردار ہیں'),
(781, 5, 112, 'وه وقت یاد کے قابل ہے جب کہ حواریوں نے عرض کیا کہ اے عیسیٰ بن مریم! کیا آپ کا رب ایسا کرسکتا ہے کہ ہم پر آسمان سے ایک خوان ﴿دسترخوان﴾ نازل فرما دے؟ آپ نے فرمایا کہ اللہ سے ڈرو اگر تم ایمان والے ہو'),
(782, 5, 113, 'وه بولے کہ ہم یہ چاہتے ہیں کہ اس میں سے کھائیں اور ہمارے دلوں کو پورا اطمینان ہوجائے اور ہمارا یہ یقین اور بڑھ جائے کہ آپ نے ہم سے سچ بوﻻ ہے اور ہم گواہی دینے والوں میں سے ہوجائیں'),
(783, 5, 114, 'عیسیٰ ابن مریم نے دعا کی کہ اے اللہ اے ہمارے پروردگار! ہم پر آسمان سے کھانا نازل فرما! کہ وه ہمارے لئے یعنی ہم میں جو اول ہیں اور جو بعد کے ہیں سب کے لئے ایک خوشی کی بات ہوجائے اور تیری طرف سے ایک نشانی ہوجائے اور تو ہم کو رزق عطا فرمادے اور تو سب عطا کرنے والوں سے اچھا ہے'),
(784, 5, 115, 'حق تعالیٰ نے ارشاد فرمایا کہ میں وه کھانا تم لوگوں پر نازل کرنے واﻻ ہوں، پھر جو شخص تم میں سے اس کے بعد ناحق شناسی کرے گا تو میں اس کو ایسی سزا دوں گا کہ وه سزا دنیا جہان والوں میں سے کسی کو نہ دوں گا'),
(785, 5, 116, 'اور وه وقت بھی قابل ذکر ہے جب کہ اللہ تعالیٰ فرمائے گا کہ اے عیسیٰ ابن مریم! کیا تم نے ان لوگوں سے کہہ دیا تھا کہ مجھ کو اور میری ماں کو بھی علاوه اللہ کے معبود قرار دے لو! عیسیٰ عرض کریں گے کہ میں تو تجھ کو منزہ سمجھتا ہوں، مجھ کو کسی طرح زیبا نہ تھا کہ میں ایسی بات کہتا جس کے کہنے کا مجھ کو کوئی حق نہیں، اگر میں نے کہا ہوگا تو تجھ کو اس کا علم ہوگا۔ تو، تو میرے دل کے اندر کی بات بھی جانتا ہے اور میں تیرے نفس میں جو کچھ ہے اس کو نہیں جانتا۔ تمام غیبوں کا جاننے واﻻ تو ہی ہے'),
(786, 5, 117, 'میں نے تو ان سے اور کچھ نہیں کہا مگر صرف وہی جو تو نے مجھ سے کہنے کو فرمایا تھا کہ تم اللہ کی بندگی اختیار کرو جو میرا بھی رب ہے اور تمہارا بھی رب ہے۔ میں ان پر گواه رہا جب تک ان میں رہا۔ پھر جب تو نے مجھ کو اٹھا لیا تو، تو ہی ان پر مطلع رہا۔ اور تو ہر چیز کی پوری خبر رکھتا ہے'),
(787, 5, 118, 'اگر تو ان کو سزا دے تو یہ تیرے بندے ہیں اور اگر تو ان کو معاف فرما دے تو، تو زبردست ہے حکمت واﻻ ہے'),
(788, 5, 119, 'اللہ ارشاد فرمائے گا کہ یہ وه دن ہے کہ جو لوگ سچے تھے ان کا سچا ہونا ان کے کام آئے گا ان کو باغ ملیں گے جن کے نیچے نہریں جاری ہوں گی جن میں وه ہمیشہ ہمیشہ کو رہیں گے۔ اللہ تعالیٰ ان سے راضی اور خوش اور یہ اللہ سے راضی اور خوش ہیں، یہ بڑی (بھاری) کامیابی ہے'),
(789, 5, 120, 'اللہ ہی کی ہے سلطنت آسمانوں کی اور زمین کی اور ان چیزوں کی جو ان میں موجود ہیں اور وه ہر شے پر پوری قدرت رکھتا ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(790, 6, 1, 'تمام تعریفیں اللہ ہی کے ﻻئق ہیں جس نے آسمانوں کو اور زمین کو پیدا کیا اور تاریکیوں اور نور کو بنایا پھر بھی کافر لوگ (غیر اللہ کو) اپنے رب کے برابر قرار دیتے ہیں'),
(791, 6, 2, 'وه ایسا ہے جس نے تم کو مٹی سے بنایا پھر ایک وقت معین کیا اور (دوسرا) معین وقت خاص اللہ ہی کے نزدیک ہے پھر بھی تم شک رکھتے ہو'),
(792, 6, 3, 'اور وہی ہے معبود برحق آسمانوں میں بھی اور زمین میں بھی، وه تمہارے پوشیده احوال کو بھی اور تمہارے ﻇاہر احوال کو بھی جانتا ہے اور تم جو کچھ عمل کرتے ہو اس کو بھی جانتا ہے'),
(793, 6, 4, 'اور ان کے پاس کوئی نشانی بھی ان کے رب کی نشانیوں میں نہیں آتی مگر وه اس سے اعراض ہی کرتے ہیں'),
(794, 6, 5, 'انہوں نے اس سچی کتاب کو بھی جھٹلایا جب کہ وه ان کے پاس پہنچی، سو جلدی ہی ان کو خبر مل جائے گی اس چیز کی جس کے ساتھ یہ لوگ استہزا کیا کرتے تھے'),
(795, 6, 6, 'کیا انہوں نے دیکھا نہیں کہ ہم ان سے پہلے کتنی جماعتوں کو ہلاک کرچکے ہیں جن کو ہم نے دنیا میں ایسی قوت دی تھی کہ تم کو وه قوت نہیں دی اور ہم نے ان پر خوب بارشیں برسائیں اور ہم نے ان کے نیچے سے نہریں جاری کیں۔ پھر ہم نے ان کو ان کے گناہوں کے سبب ہلاک کر ڈاﻻ اور ان کے بعد دوسری جماعتوں کو پیدا کر دیا'),
(796, 6, 7, 'اور اگر ہم کاغذ پر لکھا ہوا کوئی نوشتہ آپ پر نازل فرماتے پھر اس کو یہ لوگ اپنے ہاتھوں سے چھو بھی لیتے تب بھی یہ کافر لوگ یہی کہتے کہ یہ کچھ بھی نہیں مگر صریح جادو ہے'),
(797, 6, 8, 'اور یہ لوگ یوں کہتے ہیں کہ ان کے پاس کوئی فرشتہ کیوں نہیں اتارا گیا اور اگر ہم کوئی فرشتہ بھیج دیتے تو سارا قصہ ہی ختم ہوجاتا۔ پھر ان کو ذرا مہلت نہ دی جاتی'),
(798, 6, 9, 'اور اگر ہم اس کو فرشتہ تجویز کرتے تو ہم اس کو آدمی ہی بناتے اور ہمارے اس فعل سے پھر ان پر وہی اشکال ہوتا جو اب اشکال کر رہے ہیں'),
(799, 6, 10, 'اور واقعی آپ سے پہلے جو پیغمبر ہوئے ہیں ان کے ساتھ بھی استہزا کیا گیا ہے۔ پھر جن لوگوں نے ان سے مذاق کیا تھا ان کو اس عذاب نے آ گھیرا جس کا تمسخر اڑاتے تھے'),
(800, 6, 11, 'آپ فرما دیجئے کہ ذرا زمین میں چلو پھرو پھر دیکھ لو کہ تکذیب کرنے والوں کا کیا انجام ہوا'),
(801, 6, 12, 'آپ کہئے کہ جو کچھ آسمانوں اور زمین میں موجود ہے یہ سب کس کی ملکیت ہے، آپ کہہ دیجئے کہ سب اللہ ہی کی ملکیت ہے، اللہ نے مہربانی فرمانا اپنے اوپر ﻻزم فرما لیا ہے تم کو اللہ قیامت کے روز جمع کرے گا، اس میں کوئی شک نہیں، جن لوگوں نے اپنے آپ کو گھاٹے میں ڈاﻻ ہے سو وه ایمان نہیں ﻻئیں گے'),
(802, 6, 13, 'اور اللہ ہی کی ملک ہیں وه سب کچھ جو رات میں اور دن میں رہتی ہیں اور وہی بڑا سننے واﻻ بڑا جاننے واﻻ ہے'),
(803, 6, 14, 'آپ کہیے کہ کیا اللہ کے سوا، جو کہ آسمانوں اور زمین کا پیدا کرنے واﻻ ہے اور جو کہ کھانے کو دیتا ہے اور اس کو کوئی کھانے کو نہیں دیتا، اور کسی کو معبود قرار دوں، آپ فرما دیجئے کہ مجھ کو یہ حکم ہوا ہے کہ سب سے پہلے میں اسلام قبول کروں اور تو مشرکین میں ہرگز نہ ہونا'),
(804, 6, 15, 'آپ کہہ دیجئے کہ میں اگر اپنے رب کا کہنا نہ مانوں تو میں ایک بڑے دن کے عذاب سے ڈرتا ہوں'),
(805, 6, 16, 'جس شخص سے اس روز وه عذاب ہٹا دیا جائے تو اس پر اللہ نے بڑا رحم کیا اور یہ صریح کامیابی ہے'),
(806, 6, 17, 'اور اگر تجھ کو اللہ تعالیٰ کوئی تکلیف پہنچائے تو اس کا دور کرنے واﻻ سوائے اللہ تعالیٰ کے اور کوئی نہیں۔ اور اگر تجھ کو اللہ تعالیٰ کوئی نفع پہنچائے تو وه ہر چیز پر پوری قدرت رکھنے واﻻ ہے'),
(807, 6, 18, 'اور وہی اللہ اپنے بندوں کے اوپر غالب ہے برتر ہے اور وہی بڑی حکمت واﻻ اور پوری خبر رکھنے واﻻ ہے'),
(808, 6, 19, 'آپ کہئے کہ سب سے بڑی چیز گواہی دینے کے لئے کون ہے، آپ کہئے کہ میرے اور تمہارے درمیان اللہ گواه ہے اور میرے پاس یہ قرآن بطور وحی کے بھیجا گیا ہے تاکہ میں اس قرآن کے ذریعے سے تم کو اور جس جس کو یہ قرآن پہنچے ان سب کو ڈراؤں کیا تم سچ مچ یہی گواہی دو گے کہ اللہ تعالیٰ کے ساتھ کچھ اور معبود بھی ہیں، آپ کہہ دیجئے کہ میں تو گواہی نہیں دیتا۔ آپ فرما دیجئے کہ بس وه تو ایک ہی معبود ہے اور بےشک میں تمہارے شرک سے بیزار ہوں'),
(809, 6, 20, 'جن لوگوں کو ہم نے کتاب دی ہے وه لوگ رسول کو پہچانتے ہیں جس طرح اپنے بیٹوں کو پہنچانتے ہیں۔ جن لوگوں نے اپنے آپ کو گھاٹے میں ڈاﻻ ہے سو وه ایمان نہیں ﻻئیں گے'),
(810, 6, 21, 'اور اس سے زیاده بےانصاف کون ہوگا جو اللہ تعالیٰ پر جھوٹ بہتان باندھے یا اللہ کی آیات کو جھوٹا بتلائے ایسے بےانصافوں کو کامیابی نہ ہوگی'),
(811, 6, 22, 'اور وه وقت بھی یاد کرنے کے قابل ہے جس روز ہم ان تمام خلائق کو جمع کریں گے، پھر ہم مشرکین سے کہیں گے کہ تمہارے وه شرکا، جن کے معبود ہونے کا تم دعویٰ کرتے تھے، کہاں گئے؟'),
(812, 6, 23, 'پھر ان کے شرک کا انجام اس کے سوا اور کچھ بھی نہ ہوگا کہ وه یوں کہیں گے کہ قسم اللہ کی اپنے پروردگار کی ہم مشرک نہ تھے'),
(813, 6, 24, 'ذرا دیکھو تو انہوں نے کس طرح جھوٹ بوﻻ اپنی جانوں پر اور جن چیزوں کو وه جھوٹ موٹ تراشا کرتے تھے وه سب غائب ہوگئے'),
(814, 6, 25, 'اور ان میں بعض ایسے ہیں کہ آپ کی طرف کان لگاتے ہیں اور ہم نے ان کے دلوں پر پرده ڈال رکھا ہے اس سے کہ وه اس کو سمجھیں اور ان کے کانوں میں ڈاٹ دے رکھی ہے اور اگر وه لوگ تمام دﻻئل کو دیکھ لیں تو بھی ان پر کبھی ایمان نہ ﻻئیں، یہاں تک کہ جب یہ لوگ آپ کے پاس آتے ہیں تو آپ سے خواه مخواه جھگڑتے ہیں، یہ لوگ جو کافر ہیں یوں کہتے ہیں کہ یہ تو کچھ بھی نہیں صرف بے سند باتیں ہیں جو پہلوں سے چلی آ رہی ہیں'),
(815, 6, 26, 'اور یہ لوگ اس سے دوسروں کو بھی روکتے ہیں اور خود بھی اس سے دور دور رہتے ہیں اور یہ لوگ اپنے ہی کو تباه کر رہے ہیں اور کچھ خبر نہیں رکھتے'),
(816, 6, 27, 'اور اگر آپ اس وقت دیکھیں جب کہ یہ دوزخ کے پاس کھڑے کئے جائیں تو کہیں گے ہائے کیا اچھی بات ہو کہ ہم پھر واپس بھیج دیئے جائیں اور اگر ایسا ہو جائے تو ہم اپنے رب کی آیات کو جھوٹا نہ بتلائیں اور ہم ایمان والوں میں سے ہو جائیں'),
(817, 6, 28, 'بلکہ جس چیز کو اس کے قبل چھپایا کرتے تھے وه ان کے سامنے آگئی ہے اور اگر یہ لوگ پھر واپس بھیج دیئے جائیں تب بھی یہ وہی کام کریں گے جس سے ان کو منع کیا گیا تھا اور یقیناً یہ بالکل جھوٹے ہیں'),
(818, 6, 29, 'اور یہ کہتے ہیں کہ صرف یہی دنیاوی زندگی ہماری زندگی ہے اور ہم زنده نہ کئے جائیں گے'),
(819, 6, 30, 'اور اگر آپ اس وقت دیکھیں جب یہ اپنے رب کے سامنے کھڑے کئے جائیں گے۔ اللہ فرمائے گا کہ کیا یہ امر واقعی نہیں ہے؟ وه کہیں گے بےشک قسم اپنے رب کی۔ اللہ تعالیٰ فرمائے گا تو اب اپنے کفر کے عوض عذاب چکھو'),
(820, 6, 31, 'بےشک خساره میں پڑے وه لوگ جنہوں نے اللہ سے ملنے کی تکذیب کی، یہاں تک کہ جب وه معین وقت ان پر دفعتاً آ پہنچے گا، کہیں گے کہ ہائے افسوس ہماری کوتاہی پر جو اس کے بارے میں ہوئی، اور حالت ان کی یہ ہوگی کہ وه اپنے بار اپنی پیٹھوں پر ﻻدے ہوں گے، خوب سن لو کہ بری ہوگی وه چیز جس کو وه ﻻدیں گے'),
(821, 6, 32, 'اور دنیاوی زندگانی تو کچھ بھی نہیں بجز لہو ولعب کے۔ اور دار آخرت متقیوں کے لئے بہتر ہے۔ کیا تم سوچتے سمجھتے نہیں ہو'),
(822, 6, 33, 'ہم خوب جانتے ہیں کہ آپ کو ان کے اقوال مغموم کرتے ہیں، سو یہ لوگ آپ کو جھوٹا نہیں کہتے لیکن یہ ﻇالم تو اللہ کی آیتوں کا انکار کرتے ہیں'),
(823, 6, 34, 'اور بہت سے پیغمبر جو آپ سے پہلے ہوئے ہیں ان کی بھی تکذیب کی جا چکی ہے سو انہوں نے اس پر صبر ہی کیا، ان کی تکذیب کی گئی اور ان کو ایذائیں پہنچائی گئیں یہاں تک کہ ہماری امداد ان کو پہنچی اور اللہ کی باتوں کا کوئی بدلنے واﻻ نہیں اور آپ کے پاس بعض پیغمبروں کی بعض خبریں پہنچ چکی ہیں'),
(824, 6, 35, 'اور اگر آپ کو ان کا اعراض گراں گزرتا ہے تو اگر آپ کو یہ قدرت ہے کہ زمین میں کوئی سرنگ یا آسمان میں کوئی سیڑھی ڈھونڈ لو پھر کوئی معجزه لے آؤ تو کرو اور اگر اللہ کو منظور ہوتا تو ان سب کو راه راست پر جمع کر دیتا سو آپ نادانوں میں سے نہ ہوجائیے'),
(825, 6, 36, 'وہی لوگ قبول کرتے ہیں جو سنتے ہیں۔ اور مُردوں کو اللہ زنده کر کے اٹھائے گا پھر سب اللہ ہی کی طرف ﻻئے جائیں گے'),
(826, 6, 37, 'اور یہ لوگ کہتے ہیں کہ ان پر کوئی معجزه کیوں نہیں نازل کیا گیا ان کے رب کی طرف سے آپ فرما دیجئے کہ اللہ تعالیٰ کو بےشک پوری قدرت ہے اس پر کہ وه معجزه نازل فرمادے۔ لیکن ان میں اکثر بےخبر ہیں'),
(827, 6, 38, 'اور جتنے قسم کے جاندار زمین پر چلنے والے ہیں اور جتنے قسم کے پرند جانور ہیں کہ اپنے دونوں بازوؤں سے اڑتے ہیں ان میں کوئی قسم ایسی نہیں جو کہ تمہاری طرح کے گروه نہ ہوں، ہم نے دفتر میں کوئی چیز نہیں چھوڑی پھر سب اپنے پروردگار کے پاس جمع کئے جائیں گے'),
(828, 6, 39, 'اور جو لوگ ہماری آیتوں کی تکذیب کرتے ہیں وه تو طرح طرح کی ﻇلمتوں میں بہرے گونگے ہو رہے ہیں، اللہ جس کو چاہے بے راه کردے اور وه جس کو چاہے سیدھی راه پر لگا دے'),
(829, 6, 40, 'آپ کہیے کہ اپنا حال تو بتلاؤ کہ اگر تم پر اللہ کا کوئی عذاب آ پڑے یا تم پر قیامت ہی آ پہنچے تو کیا اللہ کے سوا کسی اور کو پکارو گے۔ اگر تم سچے ہو'),
(830, 6, 41, 'بلکہ خاص اسی کو پکارو گے، پھر جس کے لئے تم پکارو گے اگر وه چاہے تو اس کو ہٹا بھی دے اور جن کو تم شریک ٹھہراتے ہو ان سب کو بھول بھال جاؤ گے'),
(831, 6, 42, 'اور ہم نے اور امتوں کی طرف بھی جو کہ آپ سے پہلے گزر چکی ہیں پیغمبر بھیجے تھے، سو ہم نے ان کو تنگدستی اور بیماری سے پکڑا تاکہ وه اﻇہار عجز کرسکیں'),
(832, 6, 43, 'سو جب ان کو ہماری سزا پہنچی تھی تو انہوں نے عاجزی کیوں نہیں اختیار کی؟ لیکن ان کے قلوب سخت ہوگئے اور شیطان نے ان کے اعمال کو ان کے خیال میں آراستہ کردیا'),
(833, 6, 44, 'پھر جب وه لوگ ان چیزوں کو بھولے رہے جن کی ان کو نصیحت کی جاتی تھی تو ہم نے ان پر ہر چیز کے دروازے کشاده کردئے یہاں تک کہ جب ان چیزوں پر جو کہ ان کو ملی تھیں وه خوب اترا گئے ہم نے ان کو دفعتاً پکڑ لیا، پھر تو وه بالکل مایوس ہوگئے'),
(834, 6, 45, 'پھر ﻇالم لوگوں کی جڑ کٹ گئی اور اللہ تعالیٰ کا شکر ہے جو تمام عالم کا پروردگار ہے'),
(835, 6, 46, 'آپ کہئے کہ یہ بتلاؤ اگر اللہ تعالیٰ تمہاری سماعت اور بصارت بالکل لے لے اور تمہارے دلوں پر مہر کردے تو اللہ تعالیٰ کے سوا اور کوئی معبود ہے کہ یہ تم کو پھر دے دے۔ آپ دیکھئے تو ہم کس طرح دﻻئل کو مختلف پہلوؤں سے پیش کر رہے ہیں پھر بھی یہ اعراض کرتے ہیں'),
(836, 6, 47, 'آپ کہئے کہ یہ بتلاؤ اگر تم پر اللہ تعالیٰ کا عذاب آ پڑے خواه اچانک یا اعلانیہ تو کیا بجز ﻇالم لوگوں کے اور بھی کوئی ہلاک کیا جائے گا'),
(837, 6, 48, 'اور ہم پیغمبروں کو صرف اس واسطے بھیجا کرتے ہیں کہ وه بشارت دیں اور ڈرائیں پھر جو ایمان لے آئے اور درستی کرلے سو ان لوگوں پر کوئی اندیشہ نہیں اور نہ وه مغموم ہوں گے'),
(838, 6, 49, 'اور جو لوگ ہماری آیتوں کو جھوٹا بتلائیں ان کو عذاب پہنچے گا بوجہ اس کے کہ وه نافرمانی کرتے ہیں'),
(839, 6, 50, 'آپ کہہ دیجئے کہ نہ تو میں تم سے یہ کہتا ہوں کہ میرے پاس اللہ کے خزانے ہیں اور نہ میں غیب جانتا ہوں اور نہ میں تم سے یہ کہتا ہوں کہ میں فرشتہ ہوں۔ میں تو صرف جو کچھ میرے پاس وحی آتی ہے اس کی اتباع کرتا ہوں آپ کہئے کہ اندھا اور بینا کہیں برابر ہوسکتا ہے۔ سو کیا تم غور نہیں کرتے؟'),
(840, 6, 51, 'اور ایسے لوگوں کو ڈرائیے جو اس بات سے اندیشہ رکھتے ہیں کہ اپنے رب کے پاس ایسی حالت میں جمع کئے جائیں گے کہ جتنے غیر اللہ ہیں نہ کوئی ان کا مددگار ہوگا اور نہ کوئی شفیع، اس امید پر کہ وه ڈر جائیں'),
(841, 6, 52, 'اور ان لوگوں کو نہ نکالیے جو صبح وشام اپنے پروردگار کی عبادت کرتے ہیں، خاص اسی کی رضامندی کا قصد رکھتے ہیں۔ ان کا حساب ذرا بھی آپ کے متعلق نہیں اور آپ کا حساب ذرا بھی ان کے متعلق نہیں کہ آپ ان کو نکال دیں۔ ورنہ آپ ﻇلم کرنے والوں میں سے ہو جائیں گے'),
(842, 6, 53, 'اور اسی طرح ہم نے بعض کو بعض کے ذریعہ سے آزمائش میں ڈال رکھا ہے تاکہ یہ لوگ کہا کریں، کیا یہ لوگ ہیں کہ ہم سب میں سے ان پر اللہ تعالیٰ نے فضل کیا ہے۔ کیا یہ بات نہیں ہے کہ اللہ تعالیٰ شکر گزاروں کو خوب جانتا ہے'),
(843, 6, 54, 'اور یہ لوگ جب آپ کے پاس آئیں جو ہماری آیتوں پر ایمان رکھتے ہیں تو (یوں) کہہ دیجیئے کہ تم پر سلامتی ہے تمہارے رب نے مہربانی فرمانا اپنے ذمہ مقرر کرلیا ہے کہ جو شخص تم میں سے برا کام کر بیٹھے جہالت سے پھر وه اس کے بعد توبہ کر لے اور اصلاح رکھے تو اللہ (کی یہ شان ہے کہ وه) بڑی مغفرت کرنے واﻻ ہے بڑی رحمت واﻻ ہے'),
(844, 6, 55, 'اسی طرح ہم آیات کی تفصیل کرتے رہتے ہیں اور تاکہ مجرمین کا طریقہ ﻇاہر ہوجائے'),
(845, 6, 56, 'آپ کہہ دیجئے کہ مجھ کو اس سے ممانعت کی گئی ہے کہ ان کی عبادت کروں جن کو تم لوگ اللہ تعالیٰ کو چھوڑ کر پکارتے ہو۔ آپ کہہ دیجئے کہ میں تمہاری خواہشات کی اتباع نہ کروں گا کیوں کہ اس حالت میں تو میں بے راه ہوجاؤں گا اور راه راست پر چلنے والوں میں نہ رہوں گا'),
(846, 6, 57, 'آپ کہہ دیجئے کہ میرے پاس تو ایک دلیل ہے میرے رب کی طرف سے اور تم اس کی تکذیب کرتے ہو، جس چیز کی تم جلدبازی کر رہے ہو وه میرے پاس نہیں۔ حکم کسی کا نہیں بجز اللہ تعالیٰ کے اللہ تعالیٰ واقعی بات کو بتلا دیتا ہے اور سب سے اچھا فیصلہ کرنے واﻻ وہی ہے'),
(847, 6, 58, 'آپ کہہ دیجئے کہ اگر میرے پاس وه چیز ہوتی جس کا تم تقاضا کر رہے ہو تو میرا اور تمہارا باہمی قصہٴ فیصل ہوچکا ہوتا اور ﻇالموں کو اللہ تعالیٰ خوب جانتا ہے'),
(848, 6, 59, 'اور اللہ تعالیٰ ہی کے پاس ہیں غیب کی کنجیاں، (خزانے) ان کو کوئی نہیں جانتا بجز اللہ کے۔ اور وه تمام چیزوں کو جانتا ہے جو کچھ خشکی میں ہیں اور جو کچھ دریاؤں میں ہیں اور کوئی پتا نہیں گرتا مگر وه اس کو بھی جانتا ہے اور کوئی دانہ زمین کے تاریک حصوں میں نہیں پڑتا اور نہ کوئی تر اور نہ کوئی خشک چیز گرتی ہے مگر یہ سب کتاب مبین میں ہیں'),
(849, 6, 60, 'اور وه ایسا ہے کہ رات میں تمہاری روح کو (ایک گونہ) قبض کردیتا ہے اور جو کچھ تم دن میں کرتے ہو اس کو جانتا ہے پھر تم کو جگا اٹھاتا ہے تاکہ میعاد معین تمام کر دی جائے پھر اسی کی طرف تم کو جانا ہے پھر تم کو بتلائے گا جو کچھ تم کیا کرتے تھے'),
(850, 6, 61, 'اور وہی اپنے بندوں کے اوپر غالب ہے برتر ہے اور تم پر نگہداشت رکھنے والے بھیجتا ہے یہاں تک کہ جب تم میں سے کسی کو موت آ پہنچتی ہے، اس کی روح ہمارے بھیجے ہوئے فرشتے قبض کرلیتے ہیں اور وه ذرا کوتاہی نہیں کرتے'),
(851, 6, 62, 'پھر سب اپنے مالک حقیقی کے پاس ﻻئے جائیں گے۔ خوب سن لو فیصلہ اللہ ہی کا ہوگا اور وه بہت جلد حساب لے گا'),
(852, 6, 63, 'آپ کہئے کہ وه کون ہے جو تم کو خشکی اور دریا کی ﻇلمات سے نجات دیتا ہے۔ تم اس کو پکارتے ہو گڑگڑا کر چپکے چپکے، کہ اگر تو ہم کو ان سے نجات دے دے تو ہم ضرور شکر کرنے والوں میں سے ہوجائیں گے'),
(853, 6, 64, 'آپ کہہ دیجئے کہ اللہ ہی تم کو ان سے نجات دیتا ہے اور ہر غم سے، تم پھر بھی شرک کرنے لگتے ہو'),
(854, 6, 65, 'آپ کہئے کہ اس پر بھی وہی قادر ہے کہ تم پر کوئی عذاب تمہارے اوپر سے بھیج دے یا تمہارے پاؤں تلے سے یا کہ تم کو گروه گروه کرکے سب کو بھڑا دے اور تمہارے ایک کو دوسرے کی لڑائی چکھا دے۔ آپ دیکھیے تو سہی ہم کس طرح دﻻئل مختلف پہلوؤں سے بیان کرتے ہیں شاید وه سمجھ جائیں'),
(855, 6, 66, 'اور آپ کی قوم اس کی تکذیب کرتی ہے حاﻻنکہ وه یقینی ہے۔ آپ کہہ دیجئے کہ میں تم پر تعینات نہیں کیا گیا ہوں'),
(856, 6, 67, 'ہر خبر (کے وقوع) کا ایک وقت ہے اور جلد ہی تم کو معلوم ہوجائے گا'),
(857, 6, 68, 'اور جب آپ ان کو لوگوں کو دیکھیں جو ہماری آیات میں عیب جوئی کر رہے ہیں تو ان لوگوں سے کناره کش ہوجائیں یہاں تک کہ وه کسی اور بات میں لگ جائیں اور اگر آپ کو شیطان بھلا دے تو یاد آنے کے بعد پھر ایسے ﻇالم لوگوں کے ساتھ مت بیٹھیں'),
(858, 6, 69, 'اور جو لوگ پرہیزگار ہیں ان پر ان کی باز پرس کا کوئی اﺛر نہ پہنچے گا اور لیکن ان کے ذمہ نصیحت کردینا ہے شاید وه تقویٰ اختیار کریں'),
(859, 6, 70, 'اور ایسے لوگوں سے بالکل کناره کش رہیں جنہوں نے اپنے دین کو کھیل تماشا بنا رکھا ہے اور دنیوی زندگی نے انہیں دھوکہ میں ڈال رکھا ہے اور اس قرآن کے ذریعہ سے نصیحت بھی کرتے رہیں تاکہ کوئی شخص اپنے کردار کے سبب (اس طرح) نہ پھنس جائے کہ کوئی غیر اللہ اس کا نہ مددگار ہو اور نہ سفارشی اور یہ کیفیت ہو کہ اگر دنیا بھر کا معاوضہ بھی دے ڈالے تب بھی اس سے نہ لیا جائے۔ ایسے ہی ہیں کہ اپنے کردار کے سبب پھنس گئے، ان کے لئے نہایت تیز گرم پانی پینے کے لئے ہوگا اور دردناک سزا ہوگی اپنے کفر کے سبب'),
(860, 6, 71, 'آپ کہہ دیجئے کہ کیا ہم اللہ تعالیٰ کے سوا ایسی چیز کو پکاریں کہ نہ وه ہم کو نفع پہنچائے اور نہ ہم کو نقصان پہنچائے اور کیا ہم الٹے پھر جائیں اس کے بعد کہ ہم کو اللہ تعالیٰ نے ہدایت کردی ہے، جیسے کوئی شخص ہو کہ اس کو شیطانوں نے کہیں جنگل میں بےراه کردیا ہو اور وه بھٹکتا پھرتا ہو، اس کے کچھ ساتھی بھی ہوں کہ وه اس کو ٹھیک راستہ کی طرف بلا رہے ہوں کہ ہمارے پاس آ۔ آپ کہہ دیجئے کہ یقینی بات ہے کہ راه راست وه خاص اللہ ہی کی راه ہے اور ہم کو یہ حکم ہوا ہے کہ ہم پروردگار عالم کے پورے مطیع ہوجائیں'),
(861, 6, 72, 'اور یہ کہ نماز کی پابندی کرو اور اس سے ڈرو اور وہی ہے جس کے پاس تم سب جمع کئے جاؤ گے'),
(862, 6, 73, 'اور وہی ہے جس نے آسمانوں اور زمین کو برحق پیدا کیا اور جس وقت اللہ تعالیٰ اتنا کہہ دے گا تو ہوجا بس وه ہو پڑے گا۔ اس کا کہنا حق اور بااﺛر ہے۔ اور ساری حکومت خاص اسی کی ہوگی جب کہ صُور میں پھونک ماری جائے گی وه جاننے واﻻ ہے پوشیده چیزوں کا اور ﻇاہر چیزوں کا اور وہی ہے بڑی حکمت واﻻ پوری خبر رکھنے واﻻ'),
(863, 6, 74, 'اور وه وقت بھی یاد کرنے کے قابل ہے جب ابراہیم (علیہ السلام) نے اپنے باپ آزر سے فرمایا کہ کیا تو بتوں کو معبود قرار دیتا ہے؟ بےشک میں تجھ کو اور تیری ساری قوم کو صریح گمراہی میں دیکھتا ہوں'),
(864, 6, 75, 'اور ہم نے ایسے ہی طور پر ابراہیم (علیہ السلام) کو آسمانوں اور زمین کی مخلوقات دکھلائیں اور تاکہ کامل یقین کرنے والوں میں سے ہوجائیں'),
(865, 6, 76, 'پھر جب رات کی تاریکی ان پر چھا گئی تو انہوں نے ایک ستاره دیکھا آپ نے فرمایا کہ یہ میرا رب ہے مگر جب وه غروب ہوگیا تو آپ نے فرمایا کہ میں غروب ہوجانے والوں سے محبت نہیں رکھتا'),
(866, 6, 77, 'پھر جب چاند کو دیکھا چمکتا ہوا تو فرمایا کہ یہ میرا رب ہے لیکن جب وه غروب ہوگیا تو آپ نے فرمایا کہ اگر مجھ کو میرے رب نے ہدایت نہ کی تو میں گمراه لوگوں میں شامل ہوجاؤں گا'),
(867, 6, 78, 'پھر جب آفتاب کو دیکھا چمکتا ہوا تو فرمایا کہ یہ میرا رب ہے یہ تو سب سے بڑا ہے پھر جب وه بھی غروب ہوگیا تو آپ نے فرمایا بےشک میں تمہارے شرک سے بیزار ہوں'),
(868, 6, 79, 'میں اپنا رخ اس کی طرف کرتا ہوں جس نے آسمانوں اور زمین کو پیدا کیا یکسو ہو کر، اور میں شرک کرنے والوں میں سے نہیں ہوں'),
(869, 6, 80, 'اور ان سے ان کی قوم نے حجت کرنا شروع کیا، آپ نے فرمایا کیا تم اللہ کے معاملہ میں مجھ سے حجت کرتے ہو حاﻻنکہ اس نے مجھ کو طریقہ بتلا دیا ہے اور میں ان چیزوں سے جن کو تم اللہ کے ساتھ شریک بناتے ہو نہیں ڈرتا ہاں اگر میرا پروردگار ہی کوئی امر چاہے میرا پروردگار ہر چیز کو اپنے علم میں گھیرے ہوئے ہے، کیا تم پھر بھی خیال نہیں کرتے'),
(870, 6, 81, 'اور میں ان چیزوں سے کیسے ڈروں جن کو تم نے شریک بنایا ہے حاﻻنکہ تم اس بات سے نہیں ڈرتے کہ تم نے اللہ کے ساتھ ایسی چیزوں کو شریک ٹھہرایا ہے جن پر اللہ تعالیٰ نے کوئی دلیل نازل نہیں فرمائی، سو ان دو جماعتوں میں سے امن کا زیاده مستحق کون ہے اگر تم خبر رکھتے ہو'),
(871, 6, 82, 'جو لوگ ایمان رکھتے ہیں اور اپنے ایمان کو شرک کے ساتھ مخلوط نہیں کرتے، ایسوں ہی کے لئے امن ہے اور وہی راه راست پر چل رہے ہیں'),
(872, 6, 83, 'اور یہ ہماری حجت تھی وه ہم نے ابراہیم (علیہ السلام) کو ان کی قوم کے مقابلے میں دی تھی ہم جس کو چاہتے ہیں مرتبوں میں بڑھا دیتے ہیں۔ بےشک آپ کا رب بڑا حکمت واﻻ بڑا علم واﻻ ہے'),
(873, 6, 84, 'اور ہم نے ان کو اسحاق دیا اور یعقوب ہر ایک کو ہم نے ہدایت کی اور پہلے زمانہ میں ہم نے نوح کو ہدایت کی اور ان کی اوﻻد میں سے داؤد کو اور سلیمان کو اور ایوب کو اور یوسف کو اور موسیٰ کو اور ہارون کو اور اسی طرح ہم نیک کام کرنے والوں کو جزا دیا کرتے ہیں'),
(874, 6, 85, 'اور (نیز) زکریا کو اور یحيٰ کو اور عیسیٰ کو اور الیاس کو، سب نیک لوگوں میں سے تھے'),
(875, 6, 86, 'اور نیز اسماعیل کو اور یسع کو اور یونس کو اور لوط کو اور ہر ایک کو تمام جہان والوں پر ہم نے فضیلت دی'),
(876, 6, 87, 'اور نیز ان کے کچھ باپ دادوں کو اور کچھ اوﻻد کو اور کچھ بھائیوں کو، اور ہم نے ان کو مقبول بنایا اور ہم نے ان کو راه راست کی ہدایت کی'),
(877, 6, 88, 'اللہ کی ہدایت ہی ہے جس کے ذریعے اپنے بندوں میں سے جس کو چاہے اس کی ہدایت کرتا ہے اور اگر فرضاً یہ حضرات بھی شرک کرتے تو جو کچھ یہ اعمال کرتے تھے وه سب اکارت ہوجاتے'),
(878, 6, 89, 'یہ لوگ ایسے تھے کہ ہم نے ان کو کتاب اور حکمت اور نبوت عطا کی تھی سو اگر یہ لوگ نبوت کا انکار کریں تو ہم نے اس کے لئے ایسے بہت سے لوگ مقرر کردیئے ہیں جو اس کے منکر نہیں ہیں'),
(879, 6, 90, 'یہی لوگ ایسے تھے جن کو اللہ تعالیٰ نے ہدایت کی تھی، سو آپ بھی ان ہی کے طریق پر چلیئے آپ کہہ دیجیئے کہ میں تم سے اس پر کوئی معاوضہ نہیں چاہتا یہ تو صرف تمام جہان والوں کے واسطے ایک نصیحت ہے'),
(880, 6, 91, 'اور ان لوگوں نے، اللہ کی جیسی قدر کرنا واجب تھی ویسی قدر نہ کی جب کہ یوں کہہ دیا کہ اللہ نے کسی بشر پر کوئی چیز نازل نہیں کی آپ یہ کہیئے کہ وه کتاب کس نے نازل کی ہے جس کو موسیٰ ﻻئے تھے جس کی کیفیت یہ ہے کہ وه نور ہے اور لوگوں کے لئے وه ہدایت ہے جس کو تم نے ان متفرق اوراق میں رکھ چھوڑا ہے جن کو ﻇاہر کرتے ہو اور بہت سی باتوں کو چھپاتے ہو اور تم کو بہت سی ایسی باتیں بتائی گئی ہیں جن کو تم نہ جانتے تھے اور نہ تمہارے بڑے۔ آپ کہہ دیجیئے کہ اللہ نے نازل فرمایا ہے پھر ان کو ان کے خرافات میں کھیلتے رہنے دیجیئے'),
(881, 6, 92, 'اور یہ بھی ایسی ہی کتاب ہے جس کو ہم نے نازل کیا ہے جو بڑی برکت والی ہے، اپنے سے پہلی کتابوں کی تصدیق کرنے والی ہے اور تاکہ آپ مکہ والوں کو اور آس پاس والوں کو ڈرائیں۔ اور جو لوگ آخرت کا یقین رکھتے ہیں ایسے لوگ اس پر ایمان لے آتے ہیں اور وه اپنی نماز پر مداومت رکھتے ہیں'),
(882, 6, 93, 'اور اس شخص سے زیاده کون ﻇالم ہوگا جو اللہ تعالیٰ پر جھوٹ تہمت لگائے یا یوں کہے کہ مجھ پر وحی آتی ہے حاﻻنکہ اس کے پاس کسی بات کی بھی وحی نہیں آئی اور جو شخص یوں کہے کہ جیسا کلام اللہ نے نازل کیا ہے اسی طرح کا میں بھی ﻻتا ہوں اور اگر آپ اس وقت دیکھیں جب کہ یہ ﻇالم لوگ موت کی سختیوں میں ہوں گے اور فرشتے اپنے ہاتھ بڑھا رہے ہوں گے کہ ہاں اپنی جانیں نکالو۔ آج تم کو ذلت کی سزا دی جائے گی اس سبب سے کہ تم اللہ تعالیٰ کے ذمہ جھوٹی باتیں لگاتے تھے، اور تم اللہ تعالیٰ کی آیات سے تکبر کرتے تھے'),
(883, 6, 94, 'اور تم ہمارے پاس تنہا تنہا آگئے جس طرح ہم نے اول بار تم کو پیدا کیا تھا اور جو کچھ ہم نے تم کو دیا تھا اس کو اپنے پیچھے ہی چھوڑ آئے اور ہم تو تمہارے ہمراه تمہارے ان شفاعت کرنے والوں کو نہیں دیکھتے جن کی نسبت تم دعویٰ رکھتے تھے کہ وه تمہارے معاملے میں شریک ہیں۔ واقعی تمہارے آپس میں تو قطع تعلق ہوگیا اور وه تمہارا دعویٰ سب تم سے گیا گزرا ہوا'),
(884, 6, 95, 'بےشک اللہ تعالیٰ دانہ کو اور گٹھلیوں کو پھاڑنے واﻻ ہے، وه جاندار کو بے جان سے نکال ﻻتا ہے اور وه بے جان کو جاندار سے نکالنے واﻻ ہے اللہ تعالیٰ یہ ہے، سو تم کہاں الٹے چلے جا رہے ہو'),
(885, 6, 96, 'وه صبح کا نکالنے واﻻ ہے اور اس نے رات کو راحت کی چیز بنا یا ہے اور سورج اور چاند کو حساب سے رکھا ہے۔ یہ ٹھہرائی بات ہے ایسی ذات کی جو کہ قادر ہے بڑے علم واﻻ ہے'),
(886, 6, 97, 'اور وه ایسا ہے جس نے تمہارے لئے ستاروں کو پیدا کیا، تاکہ تم ان کے ذریعہ سے اندھیروں میں، خشکی میں اور دریا میں بھی راستہ معلوم کرسکو۔ بےشک ہم نے دﻻئل خوب کھول کھول کر بیان کردیئے ہیں ان لوگوں کے لئے جو خبر رکھتے ہیں'),
(887, 6, 98, 'اور وه ایسا ہے جس نے تم کو ایک شخص سے پیدا کیا پھر ایک جگہ زیاده رہنے کی ہے اور ایک جگہ چندے، رہنے کی بےشک ہم نےدﻻئل خوب کھول کھول کر بیان کردیئے ان لوگوں کے لئے جو سمجھ بوجھ رکھتے ہیں'),
(888, 6, 99, 'اور وه ایسا ہے جس نے آسمان سے پانی برسایا پھر ہم نے اس کے ذریعہ سے ہر قسم کے نبات کو نکالا پھر ہم نے اس سے سبز شاخ نکالی کہ اس سے ہم اوپر تلے دانے چڑھے ہوئے نکالتے ہیں اور کھجور کے درختوں سے یعنی ان کے گپھے میں سے، خوشے ہیں جو نیچے کو لٹکے جاتے ہیں اور انگوروں کے باغ اور زیتون اور انار کہ بعض ایک دوسرے سے ملتے جلتے ہوتے ہیں اور کچھ ایک دوسرے سے ملتے جلتے نہیں ہوتے۔ ہر ایک کے پھل کو دیکھو جب وه پھلتا ہے اور اس کے پکنے کو دیکھو ان میں دﻻئل ہیں ان لوگوں کے لئے جو ایمان رکھتے ہیں'),
(889, 6, 100, 'اور لوگوں نے شیاطین کو اللہ تعالیٰ کا شریک قرار دے رکھا ہے حاﻻنکہ ان لوگوں کو اللہ ہی نے پیدا کیا ہے اور ان لوگوں نے اللہ کے حق میں بیٹے اور بیٹیاں بلا سند تراش رکھی ہیں اور وه پاک اور برتر ہے ان باتوں سے جو یہ کرتے ہیں'),
(890, 6, 101, 'وه آسمانوں اور زمین کا موجد ہے، اللہ تعالیٰ کے اوﻻد کہاں ہوسکتی ہے حاﻻنکہ اس کے کوئی بیوی تو ہے نہیں اور اللہ تعالیٰ نے ہر چیز کو پیدا کیا اور وه ہر چیز کو خوب جانتا ہے'),
(891, 6, 102, 'یہ ہے اللہ تعالیٰ تمہارا رب! اس کے سوا کوئی عبادت کے ﻻئق نہیں، ہر چیز کا پیدا کرنے واﻻ ہے، تو تم اس کی عبادت کرو اور وه ہر چیز کا کارساز ہے'),
(892, 6, 103, 'اس کو تو کسی کی نگاه محیط نہیں ہوسکتی اور وه سب نگاہوں کو محیط ہو جاتا ہے اور وہی بڑا باریک بین باخبر ہے'),
(893, 6, 104, 'اب بلاشبہ تمہارے پاس تمہارے رب کی جانب سے حق بینی کے ذرائع پہنچ چکے ہیں سو جو شخص دیکھ لے گا وه اپنا فائده کرے گا اور جو شخص اندھا رہے گا وه اپنا نقصان کرے گا، اور میں تمہارا نگران نہیں ہوں'),
(894, 6, 105, 'اور ہم اس طور پر دﻻئل کو مختلف پہلوؤں سے بیان کرتے ہیں تاکہ یہ یوں کہیں کہ آپ نے کسی سے پڑھ لیا ہے اور تاکہ ہم اس کو دانشمندوں کے لئے خوب ﻇاہر کردیں'),
(895, 6, 106, 'آپ خود اس طریق پر چلتے رہئے جس کی وحی آپ کے رب تعالیٰ کی طرف سے آپ کے پاس آئی ہے، اللہ تعالیٰ کے سوا کوئی ﻻئق عبادت نہیں اور مشرکین کی طرف خیال نہ کیجئے'),
(896, 6, 107, 'اور اگر اللہ تعالیٰ کو منظور ہوتا تو یہ شرک نہ کرتے اور ہم نے آپ کو ان کا نگران نہیں بنایا۔ اور نہ آپ ان پر مختار ہیں!'),
(897, 6, 108, 'اور گالی مت دو ان کو جن کی یہ لوگ اللہ تعالیٰ کو چھوڑ کر عبادت کرتے ہیں کیونکہ پھر وه براه جہل حد سے گزر کر اللہ تعالیٰ کی شان میں گستاخی کریں گے ہم نے اسی طرح ہر طریقہ والوں کو ان کا عمل مرغوب بنا رکھا ہے۔ پھر اپنے رب ہی کے پاس ان کو جانا ہے سو وه ان کو بتلا دے گا جو کچھ بھی وه کیا کرتے تھے'),
(898, 6, 109, 'اور ان لوگوں نے قسموں میں بڑا زور لگا کر اللہ تعالیٰ کی قسم کھائی کہ اگر ان کے پاس کوئی نشانی آ جائے تو وه ضرور ہی اس پر ایمان لے آئیں گے، آپ کہہ دیجئے کہ نشانیاں سب اللہ کے قبضے میں ہیں اور تم کو اس کی کیا خبر کہ وه نشانیاں جس وقت آجائیں گی یہ لوگ تب بھی ایمان نہ ﻻئیں گے'),
(899, 6, 110, 'اور ہم بھی ان کے دلوں کو اور ان کی نگاہوں کو پھیر دیں گے جیسا کہ یہ لوگ اس پر پہلی دفعہ ایمان نہیں ﻻئے اور ہم ان کو ان کی سرکشی میں حیران رہنے دیں گے'),
(900, 6, 111, 'اور اگر ہم ان کے پاس فرشتوں کو بھیج دیتے اور ان سے مردے باتیں کرنے لگتے اور ہم تمام موجودات کو ان کے پاس ان کی آنکھوں کے روبرو ﻻ کر جمع کر دیتے ہیں تب بھی یہ لوگ ہرگز ایمان نہ ﻻتے ہاں اگر اللہ ہی چاہے تو اور بات ہے لیکن ان میں زیاده لوگ جہالت کی باتیں کرتے ہیں'),
(901, 6, 112, 'اور اسی طرح ہم نے ہر نبی کے دشمن بہت سے شیطان پیدا کئے تھے کچھ آدمی اور کچھ جن، جن میں سے بعض بعضوں کو چکنی چپڑی باتوں کا وسوسہ ڈالتے رہتے تھے تاکہ ان کو دھوکہ میں ڈال دیں اور اگر اللہ تعالیٰ چاہتا تو یہ ایسے کام نہ کرسکتے سو ان لوگوں کو اور جو کچھ یہ افترا پردازی کر رہے ہیں اس کو آپ رہنے دیجئے'),
(902, 6, 113, 'اور تاکہ اس کی طرف ان لوگوں کے قلوب مائل ہوجائیں جو آخرت پر یقین نہیں رکھتے اور تاکہ اس کو پسند کرلیں اور تاکہ مرتکب ہوجائیں ان امور کے جن کے وه مرتکب ہوتے تھے'),
(903, 6, 114, 'تو کیا اللہ کے سوا کسی اور فیصلہ کرنے والے کو تلاش کروں حاﻻنکہ وه ایسا ہے کہ اس نے ایک کتاب کامل تمہارے پاس بھیج دی ہے، اس کے مضامین خوب صاف صاف بیان کئے گئے ہیں اور جن لوگوں کو ہم نے کتاب دی ہے وه اس بات کو یقین کے ساتھ جانتے ہیں کہ یہ آپ کے رب کی طرف سے حق کے ساتھ بھیجی گئی ہے، سو آپ شبہ کرنے والوں میں سے نہ ہوں'),
(904, 6, 115, 'آپ کے رب کا کلام سچائی اور انصاف کے اعتبار سے کامل ہے، اس کے کلام کا کوئی بدلنے واﻻ نہیں اور وه خوب سننے واﻻ خوب جاننے واﻻ ہے'),
(905, 6, 116, 'اور دنیا میں زیاده لوگ ایسے ہیں کہ اگر آپ ان کا کہنا ماننے لگیں تو وه آپ کو اللہ کی راه سے بے راه کردیں وه محض بے اصل خیاﻻت پر چلتے ہیں اور بالکل قیاسی باتیں کرتے ہیں'),
(906, 6, 117, 'بالیقین آپ کا رب ان کو خوب جانتا ہے جو اس کی راه سے بے راه ہوجاتا ہے۔ اور وه ان کو بھی خوب جانتا ہے جو اس کی راه پر چلتے ہیں'),
(907, 6, 118, 'سو جس جانور پر اللہ تعالیٰ کا نام لیا جائے اس میں سے کھاؤ! اگر تم اس کے احکام پر ایمان رکھتے ہو'),
(908, 6, 119, 'اور آخر کیا وجہ ہے کہ تم ایسے جانور میں سے نہ کھاؤ جس پر اللہ کا نام لیا گیا ہو حاﻻنکہ اللہ تعالیٰ نے ان سب جانوروں کی تفصیل بتادی ہے جن کو تم پر حرام کیا ہے، مگر وه بھی جب تم کو سخت ضرورت پڑجائے تو حلال ہے اور یہ یقینی بات ہے کہ بہت سے آدمی اپنے خیاﻻت پر بلا کسی سند کے گمراه کرتے ہیں۔ اس میں کوئی شبہ نہیں کہ اللہ تعالیٰ حد سے نکل جانے والوں کو خوب جانتا ہے'),
(909, 6, 120, 'اور تم ﻇاہری گناه کو بھی چھوڑ دو اور باطنی گناه کو بھی چھوڑ دو۔ بلاشبہ جو لوگ گناه کررہے ہیں ان کو ان کے کئے کی عنقریب سزا ملے گی'),
(910, 6, 121, 'اور ایسے جانوروں میں سے مت کھاؤ جن پر اللہ کا نام نہ لیا گیا ہو اور یہ کام نافرمانی کا ہے اور یقیناً شیاطین اپنے دوستوں کے دل میں ڈالتے ہیں تاکہ یہ تم سے جدال کریں اور اگر تم ان لوگوں کی اطاعت کرنے لگو تو یقیناً تم مشرک ہوجاؤ گے'),
(911, 6, 122, 'ایسا شخص جو پہلے مرده تھا پھر ہم نے اس کو زنده کردیا اور ہم نے اس کو ایک ایسا نور دے دیا کہ وه اس کو لئے ہوئے آدمیوں میں چلتا پھرتا ہے۔ کیا ایسا شخص اس شخص کی طرح ہوسکتا ہے؟ جو تاریکیوں سے نکل ہی نہیں پاتا۔ اسی طرح کافروں کو ان کے اعمال خوش نما معلوم ہوا کرتے ہیں'),
(912, 6, 123, 'اور اسی طرح ہم نے ہر بستی میں وہاں کے رئیسوں ہی کو جرائم کا مرتکب بنایا تاکہ وه لوگ وہاں فریب کریں۔ اور وه لوگ اپنے ہی ساتھ فریب کررہے ہیں اور ان کو ذرا خبر نہیں'),
(913, 6, 124, 'اور جب ان کو کوئی آیت پہنچتی ہے تو یوں کہتے ہیں کہ ہم ہرگز ایمان نہ ﻻئیں گے جب تک کہ ہم کو بھی ایسی ہی چیز نہ دی جائے جو اللہ کے رسولوں کو دی جاتی ہے، اس موقع کو تو اللہ ہی خوب جانتا ہے کہ کہاں وه اپنی پیغمبری رکھے؟ عنقریب ان لوگوں کو جنہوں نے جرم کیا ہے اللہ کے پاس پہنچ کر ذلت پہنچے گی اور ان کی شرارتوں کے مقابلے میں سخت سزائیں'),
(914, 6, 125, 'سو جس شخص کو اللہ تعالیٰ راستہ پر ڈالنا چاہے اس کے سینہ کو اسلام کے لیے کشاده کر دیتا ہے اور جس کو بے راه رکھنا چاہے اس کے سینہ کو بہت تنگ کردیتا ہے جیسے کوئی آسمان میں چڑھتا ہے، اسی طرح اللہ تعالیٰ ایمان نہ ﻻنے والوں پر ناپاکی مسلط کردیتا ہے'),
(915, 6, 126, 'اور یہی تیرے رب کا سیدھا راستہ ہے۔ ہم نے نصیحت حاصل کرنے والوں کے واسطے ان آیتوں کو صاف صاف بیان کردیا'),
(916, 6, 127, 'ان لوگوں کے واسطے ان کے رب کے پاس سلامتی کا گھر ہے اور اللہ تعالیٰ ان سے محبت رکھتا ہے ان کے اعمال کی وجہ سے'),
(917, 6, 128, 'اور جس روز اللہ تعالیٰ تمام خلائق کو جمع کرے گا، (کہے گا) اے جماعت جنات کی! تم نے انسانوں میں سے بہت سے اپنا لیے جو انسان ان کے ساتھ تعلق رکھنے والے تھے وه کہیں گے کہ اے ہمارے پروردگار! ہم میں ایک نے دوسرے سے فائده حاصل کیا تھا اور ہم اپنی اس معین میعاد تک آپہنچے جو تونے ہمارے لئے معین فرمائی، اللہ فرمائے گا کہ تم سب کا ٹھکانہ دوزخ ہے جس میں ہمیشہ رہو گے، ہاں اگر اللہ ہی کو منظور ہو تو دوسری بات ہے۔ بے شک آپ کا رب بڑی حکمت واﻻ بڑا علم واﻻ ہے'),
(918, 6, 129, 'اور اسی طرح ہم بعض کفار کو بعض کے قریب رکھیں گے ان کے اعمال کے سبب'),
(919, 6, 130, 'اے جنات اور انسانوں کی جماعت! کیا تمہارے پاس تم میں سے ہی پیغمبر نہیں آئے تھے، جو تم سے میرے احکام بیان کرتے اور تم کو اس آج کے دن کی خبر دیتے؟ وه سب عرض کریں گے کہ ہم اپنے اوپر اقرار کرتے ہیں اور ان کو دنیاوی زندگی نے بھول میں ڈالے رکھا اور یہ لوگ اقرار کرنے والے ہوں گے کہ وه کافر تھے'),
(920, 6, 131, 'یہ اس وجہ سے ہے کہ آپ کا رب کسی بستی والوں کو کفر کے سبب ایسی حالت میں ہلاک نہیں کرتا کہ اس بستی کے رہنے والے بے خبر ہوں'),
(921, 6, 132, 'اور ہر ایک کے لئے ان کے اعمال کے سبب درجے ملیں گے اور آپ کا رب ان کے اعمال سے بے خبر نہیں ہے'),
(922, 6, 133, 'اور آپ کا رب بالکل غنی ہے رحمت واﻻ ہے۔ اگر وه چاہے تو تم سب کو اٹھا لے اور تمہارے بعد جس کو چاہے تمہاری جگہ آباد کردے جیسا کہ تم کو ایک دوسری قوم کی نسل سے پیدا کیا ہے'),
(923, 6, 134, 'جس چیز کا تم سے وعده کیا جاتا ہے وه بے شک آنے والی چیز ہے اور تم عاجز نہیں کرسکتے'),
(924, 6, 135, 'آپ یہ فرما دیجئے کہ اے میری قوم! تم اپنی حالت پر عمل کرتے رہو میں بھی عمل کررہا ہوں، سو اب جلد ہی تم کو معلوم ہوا جاتا ہے کہ اس عالم کا انجام کار کس کے لیے نافع ہوگا۔ یہ یقینی بات ہے کہ حق تلفی کرنے والوں کو کبھی فلاح نہ ہوگی'),
(925, 6, 136, 'اور اللہ تعالیٰ نے جو کھیتی اور مواشی پیدا کیے ہیں ان لوگوں نے ان میں سے کچھ حصہ اللہ کا مقرر کیا اور بزعم خود کہتے ہیں کہ یہ تو اللہ کا ہے اور یہ ہمارے معبودوں کا ہے، پھر جو چیز ان کے معبودوں کی ہوتی ہے وه تو اللہ کی طرف نہیں پہنچتی اور جو چیز اللہ کی ہوتی ہے وه ان کے معبودوں کی طرف پہنچ جاتی ہے کیا برا فیصلہ وه کرتے ہیں'),
(926, 6, 137, 'اور اسی طرح بہت سے مشرکین کے خیال میں ان کے معبودوں نے ان کی اوﻻد کے قتل کرنے کو مستحسن بنا رکھا ہے تاکہ وه ان کو برباد کریں اور تاکہ ان کے دین کو ان پر مشتبہ کردیں اور اگر اللہ کو منظور ہوتا تو یہ ایسا کام نہ کرتے تو آپ ان کو اور جو کچھ یہ غلط باتیں بنا رہے ہیں یونہی رہنے دیجئے'),
(927, 6, 138, 'اور وه اپنے خیال پر یہ بھی کہتے ہیں کہ یہ کچھ مواشی ہیں اور کھیت ہیں جن کا استعمال ہر شخص کو جائز نہیں ان کو کوئی نہیں کھا سکتا سوائے ان کے جن کو ہم چاہیں اور مواشی ہیں جن پر سواری یا باربرداری حرام کردی گئی اور کچھ مواشی ہیں جن پر یہ لوگ اللہ تعالیٰ کا نام نہیں لیتے محض اللہ پر افترا باندھنے کے طور پر۔ ابھی اللہ تعالیٰ ان کو ان کے افترا کی سزا دیئے دیتا ہے'),
(928, 6, 139, 'اور وه کہتے ہیں کہ جو چیز ان مواشی کے پیٹ میں ہے وه خالص ہمارے مردوں کے لئے ہے اور ہماری عورتوں پر حرام ہے۔ اور اگر وه مرده ہے تو اس میں سب برابر ہیں۔ ابھی اللہ ان کو ان کی غلط بیانی کی سزا دیئے دیتا ہے بلاشبہ وه حکمت واﻻ ہے اور وه بڑا علم واﻻ ہے'),
(929, 6, 140, 'واقعی خرابی میں پڑ گئے وه لوگ جنہوں نے اپنی اوﻻد کو محض براه حماقت بلا کسی سند کے قتل کر ڈاﻻ اور جو چیزیں ان کو اللہ نے کھانے پینے کو دی تھیں ان کو حرام کرلیا محض اللہ پر افترا باندھنے کے طور پر۔ بے شک یہ لوگ گمراہی میں پڑگئے اور کبھی راه راست پر چلنے والے نہیں ہوئے'),
(930, 6, 141, 'اور وہی ہے جس نے باغات پیدا کئے وه بھی جو ٹٹیوں پر چڑھائے جاتے ہیں اور وه بھی جو ٹٹیوں پر نہیں چڑھائے جاتے اور کھجور کے درخت اور کھیتی جن میں کھانے کی چیزیں مختلف طور کی ہوتی ہیں اور زیتون اور انار جو باہم ایک دوسرے کے مشابہ بھی ہوتے ہیں اور ایک دوسرے کے مشابہ نہیں بھی ہوتے، ان سب کے پھلوں میں سے کھاؤ جب وه نکل آئے اور اس میں جو حق واجب ہے وه اس کے کاٹنے کے دن دیا کرو اور حد سے مت گزرو یقیناً وه حد سے گزرنے والوں کو ناپسند کرتا ہے'),
(931, 6, 142, 'اور مواشی میں اونچے قد کے اور چھوٹے قد کے (پیدا کیے)، جو کچھ اللہ نے تم کو دیا ہے کھاؤ اور شیطان کے قدم بقدم مت چلو، بلاشک وه تمہارا صریح دشمن ہے'),
(932, 6, 143, '(پیدا کیے) آٹھ نر و ماده یعنی بھیڑ میں دو قسم اور بکری میں دو قسم آپ کہیے کہ کیا اللہ نے ان دونوں نروں کو حرام کیا ہے یا دونوں ماده کو؟ یا اس کو جس کو دونوں ماده پیٹ میں لئے ہوئے ہوں؟ تم مجھ کو کسی دلیل سے تو بتاؤ اگر سچے ہو'),
(933, 6, 144, 'اور اونٹ میں دو قسم اور گائے میں دو قسم آپ کہیے کہ کیا اللہ تعالیٰ نے ان دونوں نروں کو حرام کیا ہے یا دونوں ماده کو؟ یا اس کو جس کو دونوں ماده پیٹ میں لئے ہوئے ہوں؟ کیا تم حاضر تھے جس وقت اللہ تعالیٰ نے تم کو اس کا حکم دیا؟ تو اس سے زیاده کون ﻇالم ہوگا جو اللہ تعالیٰ پر بلا دلیل جھوٹی تہمت لگائے، تاکہ لوگوں کو گمراه کرے یقیناً اللہ تعالیٰ ﻇالم لوگوں کو راستہ نہیں دکھلاتا'),
(934, 6, 145, 'آپ کہہ دیجئے کہ جو کچھ احکام بذریعہ وحی میرے پاس آئے ان میں تو میں کوئی حرام نہیں پاتا کسی کھانے والے کے لئے جو اس کو کھائے، مگر یہ کہ وه مردار ہو یا کہ بہتا ہوا خون ہو یا خنزیر کا گوشت ہو، کیونکہ وه بالکل ناپاک ہے یا جو شرک کا ذریعہ ہو کہ غیراللہ کے لئے نامزد کردیا گیا ہو۔ پھر جو شخص مجبور ہوجائے بشرطیکہ نہ تو طالب لذت ہو اور نہ تجاوز کرنے واﻻ ہو تو واقعی آپ کا رب غفور و رحیم ہے'),
(935, 6, 146, 'اور یہود پر ہم نے تمام ناخن والے جانور حرام کرد یئے تھے اور گائے اور بکری میں سے ان دونوں کی چربیاں ان پر ہم نے حرام کردی تھیں مگر وه جو ان کی پشت پر یا انتڑیوں میں لگی ہو یا جو ہڈی سے ملی ہو۔ ان کی شرارت کے سبب ہم نے ان کو یہ سزا دی اور ہم یقیناً سچے ہیں'),
(936, 6, 147, 'پھر اگر یہ آپ کو کاذب کہیں تو آپ فرما دیجئے کہ تمہارا رب بڑی وسیع رحمت واﻻ ہے اور اس کا عذاب مجرم لوگوں سے نہ ٹلے گا'),
(937, 6, 148, 'یہ مشرکین (یوں) کہیں گے کہ اگر اللہ تعالیٰ کو منظور ہوتا تو نہ ہم شرک کرتے اور نہ ہمارے باپ دادا اور نہ ہم کسی چیز کو حرام کہہ سکتے۔ اسی طرح جو لوگ ان سے پہلے ہوچکے ہیں انہوں نے بھی تکذیب کی تھی یہاں تک کہ انہوں نے ہمارے عذاب کامزه چکھا۔ آپ کہیے کہ کیا تمہارے پاس کوئی دلیل ہے تو اس کو ہمارے روبرو ﻇاہر کرو۔ تم لوگ محض خیالی باتوں پر چلتے ہو اور تم بالکل اٹکل سے باتیں بناتے ہو'),
(938, 6, 149, 'آپ کہیے کہ بس پوری حجت اللہ ہی کی رہی۔ پھر اگر وه چاہتا تو تم سب کو راه راست پر لے آتا'),
(939, 6, 150, 'آپ کہیے کہ اپنے گواہوں کو لاؤ جو اس بات پر شہادت دیں کہ اللہ نے ان چیزوں کو حرام کردیا ہے، پھر اگر وه گواہی دے دیں تو آپ اس کی شہادت نہ دیجئے اور ایسے لوگوں کے باطل خیاﻻت کا اتباع مت کیجئے! جو ہماری آیتوں کی تکذیب کرتے ہیں اور وه جو آخرت پر ایمان نہیں رکھتے اور وه اپنے رب کے برابر دوسروں کو ٹھہراتے ہیں'),
(940, 6, 151, 'آپ کہیے کہ آؤ میں تم کو وه چیزیں پڑھ کر سناؤں جن (یعنی جن کی مخالفت) کو تمہارے رب نے تم پر حرام فرما دیا ہے، وه یہ کہ اللہ کے ساتھ کسی چیز کو شریک مت ٹھہراؤ اور ماں باپ کے ساتھ احسان کرو اور اپنی اوﻻد کو افلاس کے سبب قتل مت کرو۔ ہم تم کو اور ان کو رزق دیتے ہیں اور بے حیائی کے جتنے طریقے ہیں ان کے پاس بھی مت جاؤ خواه علانیہ ہوں خواه پوشیده، اور جس کا خون کرنا اللہ تعالیٰ نے حرام کردیا ہے اس کو قتل مت کرو، ہاں مگر حق کے ساتھ ان کا تم کو تاکیدی حکم دیا ہے تاکہ تم سمجھو'),
(941, 6, 152, 'اور یتیم کے مال کے پاس نہ جاؤ مگر ایسے طریقے سے جو کہ مستحسن ہے یہاں تک کہ وه اپنے سن رشد کو پہنچ جائے اور ناپ تول پوری پوری کرو، انصاف کے ساتھ، ہم کسی شخص کو اس کی طاقت سے زیاده تکلیف نہیں دیتے۔ اور جب تم بات کرو تو انصاف کرو، گو وه شخص قرابت دار ہی ہو اور اللہ تعالیٰ سے جو عہد کیا اس کو پورا کرو، ان کا اللہ تعالیٰ نے تم کو تاکیدی حکم دیا ہے تاکہ تم یاد رکھو'),
(942, 6, 153, 'اور یہ کہ یہ دین میرا راستہ ہے جو مستقیم ہے سو اس راه پر چلو اور دوسری راہوں پر مت چلو کہ وه راہیں تم کو اللہ کی راه سے جدا کردیں گی۔ اس کا تم کو اللہ تعالیٰ نے تاکیدی حکم دیا ہے تاکہ تم پرہیزگاری اختیار کرو'),
(943, 6, 154, 'پھر ہم نے موسیٰ (علیہ السلام) کو کتاب دی تھی جس سے اچھی طرح عمل کرنے والوں پر نعمت پوری ہو اور سب احکام کی تفصیل ہوجائے اور رہنمائی ہو اور رحمت ہو تاکہ وه لوگ اپنے رب کے ملنے پر یقین ﻻئیں'),
(944, 6, 155, 'اور یہ ایک کتاب ہے جس کو ہم نے بھیجا بڑی خیر وبرکت والی، سو اس کا اتباع کرو اور ڈرو تاکہ تم پر رحمت ہو'),
(945, 6, 156, 'کہیں تم لوگ یوں نہ کہو کہ کتاب تو صرف ہم سے پہلے جو دو فرقے تھے ان پر نازل ہوئی تھی، اور ہم ان کے پڑھنے پڑھانے سے محض بے خبر تھے'),
(946, 6, 157, 'یا یوں نہ کہو کہ اگر ہم پر کوئی کتاب نازل ہوتی تو ہم ان سے بھی زیاده راه راست پر ہوتے۔ سو اب تمہارے پاس تمہارے رب کے پاس سے ایک کتاب واضح اور رہنمائی کا ذریعہ اور رحمت آچکی ہے۔ اب اس شخص سے زیاده ﻇالم کون ہوگا جو ہماری ان آیتوں کو جھوٹا بتائے اور اس سے روکے۔ ہم جلد ہی ان لوگوں کو جو کہ ہماری آیتوں سے روکتے ہیں ان کے اس روکنے کے سبب سخت سزا دیں گے'),
(947, 6, 158, 'کیا یہ لوگ صرف اس امر کے منتظر ہیں کہ ان کے پاس فرشتے آئیں یا ان کے پاس آپ کا رب آئے یا آپ کے رب کی کوئی (بڑی) نشانی آئے؟ جس روز آپ کے رب کی کوئی بڑی نشانی آپہنچے گی، کسی ایسے شخص کا ایمان اس کے کام نہ آئے گا جو پہلے سے ایمان نہیں رکھتا۔ یا اس نے اپنے ایمان میں کوئی نیک عمل نہ کیا ہو۔ آپ فرما دیجئے کہ تم منتظر رہو، ہم بھی منتظر ہیں'),
(948, 6, 159, 'بےشک جن لوگوں نے اپنے دین کو جدا جدا کردیا اور گروه گروه بن گئے، آپ کا ان سے کوئی تعلق نہیں بس ان کا معاملہ اللہ تعالیٰ کے حوالے ہے۔ پھر ان کو ان کا کیا ہوا جتلادیں گے'),
(949, 6, 160, 'جو شخص نیک کام کرے گا اس کو اس کے دس گنا ملیں گے اور جو شخص برا کام کرے گا اس کو اس کے برابر ہی سزا ملے گی اور ان لوگوں پر ﻇلم نہ ہوگا'),
(950, 6, 161, 'آپ کہہ دیجئے کہ مجھ کو میرے رب نے ایک سیدھا راستہ بتادیا ہے کہ وه ایک دین مستحکم ہے جو طریقہ ہے ابراہیم (علیہ السلام) کا جو اللہ کی طرف یکسو تھے۔ اور وه شرک کرنے والوں میں سے نہ تھے'),
(951, 6, 162, 'آپ فرما دیجئے کہ بالیقین میری نماز اور میری ساری عبادت اور میرا جینا اور میرا مرنا یہ سب خالص اللہ ہی کا ہے جو سارے جہان کا مالک ہے'),
(952, 6, 163, 'اس کا کوئی شریک نہیں اور مجھ کو اسی کا حکم ہوا ہے اور میں سب ماننے والوں میں سے پہلا ہوں'),
(953, 6, 164, 'آپ فرما دیجئے کہ کیا میں اللہ کے سوا کسی اور کو رب بنانے کے لئے تلاش کروں حاﻻنکہ وه مالک ہے ہر چیز کا اور جو شخص بھی کوئی عمل کرتا ہے وه اسی پر رہتا ہے اور کوئی کسی دوسرے کا بوجھ نہ اٹھائے گا۔ پھر تم سب کو اپنے رب کے پاس جانا ہوگا۔ پھر وه تم کو جتلائے گا جس جس چیز میں تم اختلاف کرتے تھے'),
(954, 6, 165, 'اور وه ایسا ہے جس نے تم کو زمین میں خلیفہ بنایا اور ایک کا دوسرے پر رتبہ بڑھایا تاکہ تم کو آزمائے ان چیزوں میں جو تم کو دی ہیں۔ بالیقین آپ کا رب جلد سزا دینے واﻻ ہے اور بالیقین وه واقعی بڑی مغفرت کرنے واﻻ مہربانی کرنے واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(955, 7, 1, 'المص'),
(956, 7, 2, 'یہ ایک کتاب ہے جو آپ کے پاس اس لئے بھیجی گئی ہے کہ آپ اس کے ذریعہ سے ڈرائیں، سو آپ کے دل میں اس سے بالکل تنگی نہ ہو اور نصیحت ہے ایمان والوں کے لئے'),
(957, 7, 3, 'تم لوگ اس کا اتباع کرو جو تمہارے رب کی طرف سے آئی ہے اور اللہ تعالیٰ کو چھوڑ کر من گھڑت سرپرستوں کی اتباع مت کرو تم لوگ بہت ہی کم نصیحت پکڑتے ہو'),
(958, 7, 4, 'اور بہت بستیوں کو ہم نے تباه کردیا اور ان پر ہمارا عذاب رات کے وقت پہنچا یا ایسی حالت میں کہ وه دوپہر کے وقت آرام میں تھے'),
(959, 7, 5, 'سو جس وقت ان پر ہمارا عذاب آیا اس وقت ان کے منھ سے بجز اس کے اور کوئی بات نہ نکلی کہ واقعی ہم ﻇالم تھے'),
(960, 7, 6, 'پھر ہم ان لوگوں سے ضرور پوچھیں گے جن کے پاس پیغمبر بھیجے گئے تھے اور ہم پیغمبروں سے ضرور پوچھیں گے'),
(961, 7, 7, 'پھر ہم چونکہ پوری خبر رکھتے ہیں ان کے روبرو بیان کردیں گے۔ اور ہم کچھ بے خبر نہ تھے'),
(962, 7, 8, 'اور اس روز وزن بھی برحق ہے پھر جس شخص کا پلا بھاری ہوگا سو ایسے لوگ کامیاب ہوں گے'),
(963, 7, 9, 'اور جس شخص کا پلا ہلکا ہوگا سو یہ وه لوگ ہوں گے جنہوں نے اپنا نقصان کرلیا بسبب اس کے کہ ہماری آیتوں کے ساتھ ﻇلم کرتے تھے'),
(964, 7, 10, 'اور بے شک ہم نے تم کو زمین پر رہنے کی جگہ دی اور ہم نے تمہارے لئے اس میں سامان رزق پیدا کیا، تم لوگ بہت ہی کم شکر کرتے ہو'),
(965, 7, 11, 'اور ہم نے تم کو پیدا کیا، پھر ہم ہی نے تمہاری صورت بنائی پھر ہم نے فرشتوں سے کہا کہ آدم کو سجده کرو سو سب نے سجده کیا بجز ابلیس کے، وه سجده کرنے والوں میں شامل نہ ہوا'),
(966, 7, 12, 'حق تعالیٰ نے فرمایا تو جو سجده نہیں کرتا تو تجھ کو اس سے کون امر مانع ہے، جبکہ میں تجھ کو حکم دے چکا، کہنے لگا میں اس سے بہتر ہوں، آپ نے مجھ کو آگ سے پیدا کیا ہے اور اس کو آپ نے خاک سے پیدا کیا ہے'),
(967, 7, 13, 'حق تعالیٰ نے فرمایا آسمان سے اتر تجھ کو کوئی حق حاصل نہیں کہ تو آسمان میں ره کر تکبر کرے سو نکل بے شک تو ذلیلوں میں سے ہے'),
(968, 7, 14, 'اس نے کہا کہ مجھ کو مہلت دیجئے قیامت کے دن تک'),
(969, 7, 15, 'اللہ تعالیٰ نے فرمایا تجھ کو مہلت دی گئی'),
(970, 7, 16, 'اس نے کہا بسبب اس کے کہ آپ نے مجھ کو گمراه کیا ہے میں قسم کھاتا ہوں کہ میں ان کے لئے آپ کی سیدھی راه پر بیٹھوں گا'),
(971, 7, 17, 'پھر ان پر حملہ کروں گا ان کے آگے سے بھی اور ان کے پیچھے سے بھی اور ان کی داہنی جانب سے بھی اور ان کی بائیں جانب سے بھی اور آپ ان میں سے اکثر کو شکر گزار نہ پائیے گا'),
(972, 7, 18, 'اللہ تعالیٰ نے فرمایا کہ یہاں سے ذلیل وخوار ہوکر نکل جا جو شخص ان میں سے تیرا کہنا مانے گا میں ضرور تم سب سے جہنم کو بھردوں گا'),
(973, 7, 19, 'اور ہم نے حکم دیا کہ اے آدم! تم اور تمہاری بیوی جنت میں رہو۔ پھر جس جگہ سے چاہو دونوں کھاؤ، اور اس درخت کے پاس مت جاؤ ورنہ تم دونوں ﻇالموں میں سے ہوجاؤ گے'),
(974, 7, 20, 'پھر شیطان نے ان دونوں کے دلوں میں وسوسہ ڈاﻻ تاکہ ان کی شرمگاہیں جو ایک دوسرے سے پوشیده تھیں دونوں کے روبرو بے پرده کردے اور کہنے لگا کہ تمہارے رب نے تم دونوں کو اس درخت سے اور کسی سبب سے منع نہیں فرمایا، مگر محض اس وجہ سے کہ تم دونوں کہیں فرشتے ہوجاؤ یا کہیں ہمیشہ زنده رہنے والوں میں سے ہوجاؤ'),
(975, 7, 21, 'اور ان دونوں کے روبرو قسم کھالی کہ یقین جانیئے میں تم دونوں کا خیر خواه ہوں'),
(976, 7, 22, 'سو ان دونوں کو فریب سے نیچے لے آیا پس ان دونوں نے جب درخت کو چکھا دونوں کی شرمگاہیں ایک دوسرے کے روبرو بے پرده ہوگئیں اور دونوں اپنے اوپر جنت کے پتے جوڑ جوڑ کر رکھنے لگے اور ان کے رب نے ان کو پکارا کیا میں تم دونوں کو اس درخت سے منع نہ کرچکا تھا اور یہ نہ کہہ چکا کہ شیطان تمہارا صریح دشمن ہے؟'),
(977, 7, 23, 'دونوں نے کہا اے ہمارے رب! ہم نے اپنا بڑا نقصان کیا اور اگر تو ہماری مغفرت نہ کرے گا اور ہم پر رحم نہ کرے گا تو واقعی ہم نقصان پانے والوں میں سے ہوجائیں گے'),
(978, 7, 24, 'حق تعالیٰ نے فرمایا کہ نیچے ایسی حالت میں جاؤ کہ تم باہم ایک دوسرے کے دشمن ہوگے اور تمہارے واسطے زمین میں رہنے کی جگہ ہے اور نفع حاصل کرنا ہے ایک وقت تک'),
(979, 7, 25, 'فرمایا تم کو وہاں ہی زندگی بسر کرنا ہے اور وہاں ہی مرنا ہے اور اسی میں سے پھر نکالے جاؤ گے'),
(980, 7, 26, 'اے آدم (علیہ السلام) کی اوﻻد ہم نے تمہارے لئے لباس پیدا کیا جو تمہاری شرم گاہوں کو بھی چھپاتا ہے اور موجب زینت بھی ہے اور تقوے کا لباس، یہ اس سے بڑھ کر ہے۔ یہ اللہ تعالیٰ کی نشانیوں میں سے ہے تاکہ یہ لوگ یاد رکھیں'),
(981, 7, 27, 'اے اوﻻد آدم! شیطان تم کو کسی خرابی میں نہ ڈال دے جیسا اس نے تمہارے ماں باپ کو جنت سے باہر کرا دیا ایسی حالت میں ان کا لباس بھی اتروا دیا تاکہ وه ان کو ان کی شرم گاہیں دکھائے۔ وه اور اس کا لشکر تم کو ایسے طور پر دیکھتا ہے کہ تم ان کو نہیں دیکھتے ہو۔ ہم نے شیطانوں کو ان ہی لوگوں کا دوست بنایا ہے جو ایمان نہیں ﻻتے'),
(982, 7, 28, 'اور وه لوگ جب کوئی فحش کام کرتے ہیں تو کہتے ہیں کہ ہم نے اپنے باپ دادا کو اسی طریق پر پایا ہے اور اللہ نے بھی ہم کو یہی بتایا ہے۔ آپ کہہ دیجیئے کہ اللہ تعالیٰ فحش بات کی تعلیم نہیں دیتا، کیا اللہ کے ذمہ ایسی بات لگاتے ہو جس کی تم سند نہیں رکھتے؟'),
(983, 7, 29, 'آپ کہہ دیجیئے کہ میرے رب نے حکم دیا ہے انصاف کا اور یہ کہ تم ہر سجده کے وقت اپنا رخ سیدھا رکھا کرو اور اللہ تعالیٰ کی عبادت اس طور پر کرو کہ اس عبادت کو خالص اللہ ہی کے واسطے رکھو۔ تم کو اللہ نے جس طرح شروع میں پیدا کیا تھا اسی طرح تم دوباره پیدا ہوگے'),
(984, 7, 30, 'بعض لوگوں کو اللہ نے ہدایت دی ہے اور بعض پر گمراہی ﺛابت ہوگئی ہے۔ ان لوگوں نے اللہ تعالیٰ کو چھوڑ کر شیطانوں کو دوست بنالیا ہے اور خیال رکھتے ہیں کہ وه راست پر ہیں'),
(985, 7, 31, 'اے اوﻻد آدم! تم مسجد کی ہر حاضری کے وقت اپنا لباس پہن لیا کرو۔ اور خوب کھاؤ اور پیو اور حد سے مت نکلو۔ بےشک اللہ حد سے نکل جانے والوں کو پسند نہیں کرتا'),
(986, 7, 32, 'آپ فرمائیے کہ اللہ تعالیٰ کے پیدا کئے ہوئے اسباب زینت کو، جن کو اس نے اپنے بندوں کے واسطے بنایا ہے اور کھانے پینے کی حلال چیزوں کو کس شخص نے حرام کیا ہے؟ آپ کہہ دیجئے کہ یہ اشیا اس طور پر کہ قیامت کے روز خالص ہوں گی اہل ایمان کے لئے، دنیوی زندگی میں مومنوں کے لئے بھی ہیں۔ ہم اسی طرح تمام آیات کو سمجھ داروں کے واسطے صاف صاف بیان کرتے ہیں'),
(987, 7, 33, 'آپ فرمائیے کہ البتہ میرے رب نے صرف حرام کیا ہے ان تمام فحش باتوں کو جو علانیہ ہیں اور جو پوشیده ہیں اور ہر گناه کی بات کو اور ناحق کسی پر ﻇلم کرنے کو اور اس بات کو کہ تم اللہ کے ساتھ کسی ایسی چیز کو شریک ٹھہراؤ جس کی اللہ نے کوئی سند نازل نہیں کی اور اس بات کو کہ تم لوگ اللہ کے ذمے ایسی بات لگادو جس کو تم جانتے نہیں'),
(988, 7, 34, 'اور ہر گروه کے لئے ایک میعاد معین ہے سو جس وقت ان کی میعاد معین آجائے گی اس وقت ایک ساعت نہ پیچھے ہٹ سکیں گے اور نہ آگے بڑھ سکیں گے'),
(989, 7, 35, 'اے اوﻻد آدم! اگر تمہارے پاس پیغمبر آئیں جو تم ہی میں سے ہوں جو میرے احکام تم سے بیان کریں تو جو شخص تقویٰ اختیار کرے اور درستی کرے سو ان لوگوں پر نہ کچھ اندیشہ ہے اور نہ وه غمگین ہوں گے'),
(990, 7, 36, 'اور جو لوگ ہمارے ان احکام کو جھٹلائیں اور ان سے تکبر کریں وه لوگ دوزخ والے ہوں گے وه اس میں ہمیشہ ہمیشہ رہیں گے'),
(991, 7, 37, 'سو اس شخص سے زیاده ﻇالم کون ہوگا جو اللہ تعالیٰ پر جھوٹ باندھے یا اس کی آیتوں کو جھوٹا بتائے، ان لوگوں کے نصیب کا جو کچھ کتاب سے ہے وه ان کو مل جائے گا، یہاں تک کہ جب ان کے پاس ہمارے بھیجے ہوئے فرشتے ان کی جان قبض کرنے آئیں گے تو کہیں گے کہ وه کہاں گئے جن کی تم اللہ کو چھوڑ کر عبادت کرتے تھے، وه کہیں گے کہ ہم سے سب غائب ہوگئے اور اپنے کافر ہونے کا اقرار کریں گے'),
(992, 7, 38, 'اللہ تعالیٰ فرمائے گا کہ جو فرقے تم سے پہلے گزر چکے ہیں جنات میں سے بھی اور آدمیوں میں سے بھی، ان کے ساتھ تم بھی دوزخ میں جاؤ۔ جس وقت بھی کوئی جماعت داخل ہوگی اپنی دوسری جماعت کو لعنت کرے گی یہاں تک کہ جب اس میں سب جمع ہوجائیں گے تو پچھلے لوگ پہلے لوگوں کی نسبت کہیں گے کہ ہمارے پروردگار ہم کو ان لوگوں نے گمراه کیا تھا سو ان کو دوزخ کا عذاب دو گنا دے۔ اللہ تعالیٰ فرمائے گا کہ سب ہی کا دوگنا ہے، لیکن تم کو خبر نہیں'),
(993, 7, 39, 'اور پہلے لوگ پچھلے لوگوں سے کہیں گے کہ پھر تم کو ہم پر کوئی فوقیت نہیں سو تم بھی اپنی کمائی کے بدلے میں عذاب کامزه چکھو'),
(994, 7, 40, 'جن لوگوں نے ہماری آیتوں کو جھٹلایا اور ان سے تکبر کیا ان کے لئے آسمان کے دروازے نہ کھولے جائیں گے اور وه لوگ کبھی جنت میں نہ جائیں گے جب تک کہ اونٹ سوئی کے ناکہ کے اندر سے نہ چلا جائے اور ہم مجرم لوگوں کو ایسی ہی سزا دیتے ہیں'),
(995, 7, 41, 'ان کے لئے آتش دوزخ کا بچھونا ہوگا اور ان کے اوپر (اسی کا) اوڑھنا ہوگا اور ہم ایسے ﻇالموں کو ایسی ہی سزا دیتے ہیں'),
(996, 7, 42, 'اور جو لوگ ایمان ﻻئے اور انہوں نے نیک کام کئے ہم کسی شخص کو اس کی قدرت سے زیاده کسی کا مکلف نہیں بناتے وہی لوگ جنت والے ہیں اور وه اس میں ہمیشہ ہمیشہ رہیں گے'),
(997, 7, 43, 'اور جو کچھ ان کے دلوں میں (کینہ) تھا ہم اس کو دور کردیں گے۔ ان کے نیچے نہریں جاری ہوں گی۔ اور وه لوگ کہیں گے کہ اللہ کا (لاکھ لاکھ) شکر ہے جس نے ہم کو اس مقام تک پہنچایا اور ہماری کبھی رسائی نہ ہوتی اگر اللہ تعالیٰ ہم کو نہ پہنچاتا۔ واقعی ہمارے رب کے پیغمبر سچی باتیں لے کر آئے تھے۔ اور ان سے پکار کر کہا جائے گا کہ اس جنت کے تم وارث بنائے گئے ہو اپنے اعمال کے بدلے'),
(998, 7, 44, 'اور اہل جنت اہل دوزخ کو پکاریں گے کہ ہم سے جو ہمارے رب نے وعده فرمایا تھا ہم نے تو اس کو واقعہ کے مطابق پایا، سو تم سے جو تمہارے رب نے وعده کیا تھا تم نے بھی اس کو واقعہ کے مطابق پایا وه کہیں گے ہاں، پھر ایک پکارنے واﻻ دونوں کے درمیان میں پکارے گا کہ اللہ کی مار ہو ان ﻇالموں پر'),
(999, 7, 45, 'جو اللہ کی راه سے اعراض کرتے تھے اور اس میں کجی تلاش کرتے تھے اور وه لوگ آخرت کے بھی منکر تھے'),
(1000, 7, 46, 'اور ان دونوں کے درمیان ایک آڑ ہوگی اور اعراف کے اوپر بہت سے آدمی ہوں گے وه لوگ، ہر ایک کو ان کے قیافہ سے پہچانیں گے اور اہل جنت کو پکار کر کہیں گے، السلام علیکم! ابھی یہ اہل اعراف جنت میں داخل نہیں ہوئے ہوں گے اور اس کے امیدوار ہوں گے'),
(1001, 7, 47, 'اور جب ان کی نگاہیں اہل دوزخ کی طرف پھریں گی تو کہیں گے اے ہمارے رب! ہم کو ان ﻇالم لوگوں کے ساتھ شامل نہ کر'),
(1002, 7, 48, 'اور اہل اعراف بہت سے آدمیوں کو جن کو کہ ان کے قیافہ سے پہچانیں گے پکاریں گے کہیں گے کہ تمہاری جماعت اور تمہارا اپنے کو بڑا سمجھنا تمہارے کچھ کام نہ آیا'),
(1003, 7, 49, 'کیا یہ وہی ہیں جن کی نسبت تم قسمیں کھا کھا کر کہا کرتے تھے کہ اللہ تعالیٰ ان پر رحمت نہ کرے گا، ان کو یوں حکم ہوگا کہ جاؤ جنت میں تم پر نہ کچھ خوف ہے اور نہ تم مغموم ہوگے'),
(1004, 7, 50, 'اور دوزخ والے جنت والوں کو پکاریں گے، کہ ہمارےاوپر تھوڑا پانی ہی ڈال دو یا اور ہی کچھ دے دو، جو اللہ نے تم کو دے رکھا ہے۔ جنت والے کہیں گے کہ اللہ تعالیٰ نے دونوں چیزوں کی کافروں کے لئے بندش کردی ہے'),
(1005, 7, 51, 'جنہوں نے دنیا میں اپنے دین کو لہو ولعب بنا رکھا تھا اور جن کو دنیاوی زندگی نے دھوکہ میں ڈال رکھا تھا۔ سو ہم (بھی) آج کے روز ان کا نام بھول جائیں گے جیسا کہ وه اس دن کو بھول گئے اور جیسا یہ ہماری آیتوں کا انکار کرتے تھے'),
(1006, 7, 52, 'اور ہم نے ان لوگوں کے پاس ایک ایسی کتاب پہنچادی ہے جس کو ہم نے اپنے علم کامل سے بہت واضح کر کے بیان کردیا ہے، وه ذریعہ ہدایت اور رحمت ان لوگوں کے لئے ہے جو ایمان ﻻئے ہیں'),
(1007, 7, 53, 'ان لوگوں کو اور کسی بات کا انتظار نہیں صرف اس کے اخیر نتیجہ کا انتظار ہے، جس روز اس کا اخیر نتیجہ پیش آئے گا اور اس روز جو لوگ اس کو پہلے سے بھولے ہوئے تھے یوں کہیں گے کہ واقعی ہمارے رب کے پیغمبر سچی سچی باتیں ﻻئے تھے، سو اب کیا کوئی ہمارا سفارشی ہے کہ وه ہماری سفارش کردے یا کیا ہم پھر واپس بھیجے جاسکتے ہیں تاکہ ہم لوگ ان اعمال کے، جن کو ہم کیا کرتے تھے برخلاف دوسرے اعمال کریں۔ بےشک ان لوگوں نے اپنے آپ کو خساره میں ڈال دیا اور یہ جو جو باتیں تراشتے تھے سب گم ہوگئیں'),
(1008, 7, 54, 'بے شک تمہارا رب اللہ ہی ہے جس نے سب آسمانوں اور زمین کو چھ روز میں پیدا کیا ہے، پھر عرش پر قائم ہوا۔ وه شب سے دن کو ایسے طور پر چھپا دیتا ہے کہ وه شب اس دن کو جلدی سے آ لیتی ہے اور سورج اور چاند اور دوسرے ستاروں کو پیدا کیا ایسے طور پر کہ سب اس کے حکم کے تابع ہیں۔ یاد رکھو اللہ ہی کے لئے خاص ہے خالق ہونا اور حاکم ہونا، بڑی خوبیوں سے بھرا ہوا ہے اللہ جو تمام عالم کا پروردگار ہے'),
(1009, 7, 55, 'تم لوگ اپنے پروردگار سے دعا کیا کرو گڑگڑا کر کے بھی اور چپکے چپکے بھی۔ واقعی اللہ تعالیٰ ان لوگوں کو ناپسند کرتا ہے جو حد سے نکل جائیں'),
(1010, 7, 56, 'اور دنیا میں اس کے بعد کہ اس کی درستی کردی گئی ہے، فساد مت پھیلاؤ اور تم اللہ کی عبادت کرو اس سے ڈرتے ہوئے اور امیدوار رہتے ہوئے۔ بے شک اللہ تعالیٰ کی رحمت نیک کام کرنے والوں کے نزدیک ہے'),
(1011, 7, 57, 'اور وه ایسا ہے کہ اپنی باران رحمت سے پہلے ہواؤں کو بھیجتا ہے کہ وه خوش کر دیتی ہیں، یہاں تک کہ جب وه ہوائیں بھاری بادلوں کو اٹھا لیتی ہیں، تو ہم اس بادل کو کسی خشک سرزمین کی طرف ہانک لے جاتے ہیں، پھر اس بادل سے پانی برساتے ہیں پھر اس پانی سے ہر قسم کے پھل نکالتے ہیں۔ یوں ہی ہم مردوں کو نکال کھڑا کریں گے تاکہ تم سمجھو'),
(1012, 7, 58, 'اور جو ستھری سرزمین ہوتی ہے اس کی پیداوار تو اللہ کے حکم سے خوب نکلتی ہے اور جو خراب ہے اس کی پیداوار بہت کم نکلتی ہے، اسی طرح ہم دﻻئل کو طرح طرح سے بیان کرتے ہیں، ان لوگوں کے لئے جو شکر کرتے ہیں'),
(1013, 7, 59, 'ہم نے نوح (علیہ السلام) کو ان کی قوم کی طرف بھیجا توانہوں نے فرمایا اے میری قوم! تم اللہ کی عبادت کرو اس کے سوا کوئی تمہارا معبود ہونے کے قابل نہیں، مجھ کو تمہارے لئے ایک بڑے دن کے عذاب کا اندیشہ ہے'),
(1014, 7, 60, 'ان کی قوم کے بڑے لوگوں نے کہا کہ ہم تم کو صریح غلطی میں دیکھتے ہیں'),
(1015, 7, 61, 'انہوں نے فرمایا کہ اے میری قوم! مجھ میں تو ذرا بھی گمراہی نہیں لیکن میں پروردگار عالم کا رسول ہوں'),
(1016, 7, 62, 'تم کو اپنے پروردگار کے پیغام پہنچاتا ہوں اور تمہاری خیر خواہی کرتا ہوں اور میں اللہ کی طرف سے ان امور کی خبر رکھتا ہوں جن کی تم کو خبر نہیں'),
(1017, 7, 63, 'اور کیا تم اس بات سے تعجب کرتے ہو کہ تمہارے پروردگار کی طرف سے تمہارے پاس ایک ایسے شخص کی معرفت، جو تمہاری ہی جنس کا ہے، کوئی نصیحت کی بات آگئی تاکہ وه شخص تم کو ڈرائے اور تاکہ تم ڈرجاؤ اور تاکہ تم پر رحم کیا جائے'),
(1018, 7, 64, 'سو وه لوگ ان کی تکذیب ہی کرتے رہے تو ہم نے نوح (علیہ السلام) کو اور ان کو جو ان کےساتھ کشتی میں تھے، بچالیا اور جن لوگوں نے ہماری آیتوں کو جھٹلایا تھا ان کو ہم نے غرق کردیا۔ بے شک وه لوگ اندھے ہورہے تھے'),
(1019, 7, 65, 'اور ہم نے قوم عاد کی طرف ان کے بھائی ہود (علیہ السلام) کو بھیجا۔ انہوں نے فرمایا اے میری قوم! تم اللہ کی عبادت کرو، اس کے سوا کوئی تمہارا معبود نہیں، سو کیا تم نہیں ڈرتے'),
(1020, 7, 66, 'ان کی قوم میں جو بڑے لوگ کافر تھے انہوں نے کہا ہم تم کو کم عقلی میں دیکھتے ہیں۔ اور ہم بے شک تم کو جھوٹے لوگوں میں سمجھتے ہیں'),
(1021, 7, 67, 'انہوں نے فرمایا کہ اے میری قوم! مجھ میں ذرا بھی کم عقلی نہیں لیکن میں پروردگار عالم کا بھیجا ہوا پیغمبر ہوں'),
(1022, 7, 68, 'تم کو اپنے پروردگار کے پیغام پہنچاتا ہوں اور میں تمہارا امانتدار خیرخواه ہوں'),
(1023, 7, 69, 'اور کیا تم اس بات سے تعجب کرتے ہو کہ تمہارے پروردگار کی طرف سے تمہارے پاس ایک ایسے شخص کی معرفت، جو تمہاری ہی جنس کاہے کوئی نصیحت کی بات آگئی تاکہ وه شخص تم کو ڈرائے اور تم یہ حالت یاد کرو کہ اللہ نے تم کو قوم نوح کے بعد جانشین بنایا اور ڈیل ڈول میں تم کو پھیلاؤ زیاده دیا، سو اللہ کی نعمتوں کو یاد کرو تاکہ تم کو فلاح ہو'),
(1024, 7, 70, 'انہوں نے کہا کہ کیا آپ ہمارے پاس اس واسطے آئے ہیں کہ ہم صرف اللہ ہی کی عبادت کریں اور جن کو ہمارے باپ دادا پوجتے تھے ان کو چھوڑ دیں، پس ہم کو جس عذاب کی دھمکی دیتے ہو اس کو ہمارے پاس منگوا دو اگر تم سچے ہو'),
(1025, 7, 71, 'انہوں نے فرمایا کہ بس اب تم پر اللہ کی طرف سے عذاب اور غضب آیا ہی چاہتا ہے کیا تم مجھ سے ایسے ناموں کے باب میں جھگڑتے ہو جن کو تم نے اور تمہارے باپ دادوں نے ٹھہرالیا ہے؟ ان کے معبود ہونے کی اللہ نے کوئی دلیل نہیں بھیجی۔ سو تم منتظر رہو میں بھی تمہارے ساتھ انتظار کر رہا ہوں'),
(1026, 7, 72, 'غرض ہم نے ان کو اور ان کے ساتھیوں کو اپنی رحمت سے بچالیا اور ان لوگوں کی جڑ کاٹ دی، جنہوں نے ہماری آیتوں کو جھٹلایا تھا اور وه ایمان ﻻنے والے نہ تھے'),
(1027, 7, 73, 'اور ہم نے ﺛمود کی طرف ان کے بھائی صالح (علیہ السلام) کو بھیجا۔ انہوں نے فرمایا اے میری قوم! تم اللہ کی عبادت کرو اس کے سوا کوئی تمہارا معبود نہیں۔ تمہارے پاس تمہارے پروردگار کی طرف سے ایک واضح دلیل آچکی ہے۔ یہ اونٹنی ہے اللہ کی جو تمہارے لئے دلیل ہے سو اس کو چھوڑ دو کہ اللہ تعالیٰ کی زمین میں کھاتی پھرے اور اس کو برائی کے ساتھ ہاتھ بھی مت لگانا کہ کہیں تم کو دردناک عذاب آپکڑے'),
(1028, 7, 74, 'اور تم یہ حالت یاد کرو کہ اللہ تعالیٰ نے تم کو عاد کے بعد جانشین بنایا اور تم کو زمین پر رہنے کا ٹھکانا دیا کہ نرم زمین پر محل بناتے ہو اور پہاڑوں کو تراش تراش کر ان میں گھر بناتے ہو، سو اللہ تعالیٰ کی نعمتوں کو یاد کرو اور زمین میں فساد مت پھیلاؤ'),
(1029, 7, 75, 'ان کی قوم میں جو متکبر سردار تھے انہوں نے غریب لوگوں سے جو کہ ان میں سے ایمان لے آئے تھے پوچھا، کیا تم کواس بات کا یقین ہے کہ صالح (علیہ السلام) اپنے رب کی طرف سے بھیجے ہوئے ہیں؟ انہوں نے کہا کہ بےشک ہم تو اس پر پورا یقین رکھتے ہیں جو ان کو دے کر بھیجا گیا ہے'),
(1030, 7, 76, 'وه متکبر لوگ کہنے لگے کہ تم جس بات پر یقین ﻻئے ہوئے ہو، ہم تو اس کے منکر ہیں'),
(1031, 7, 77, 'پس انہوں نے اس اونٹنی کو مار ڈاﻻ اور اپنے پروردگار کے حکم سے سرکشی کی اور کہنے لگے کہ اے صالح! جس کی آپ ہم کو دھمکی دیتے تھے اس کو منگوائیے اگر آپ پیغمبر ہیں'),
(1032, 7, 78, 'پس ان کو زلزلہ نے آپکڑا اور وه اپنے گھروں میں اوندھے کے اوندھے پڑے ره گئے'),
(1033, 7, 79, 'اس وقت (صالح علیہ السلام) ان سے منھ موڑ کر چلے، اور فرمانے لگے کہ اے میری قوم! میں نے تو تم کو اپنے پروردگار کا حکم پہنچادیا تھا اور میں نے تمہاری خیرخواہی کی لیکن تم لوگ خیرخواہوں کو پسند نہیں کرتے'),
(1034, 7, 80, 'اور ہم نے لوط (علیہ السلام) کو بھیجا جبکہ انہوں نے اپنی قوم سے فرمایا کہ تم ایسا فحش کام کرتے ہو جس کو تم سے پہلے کسی نے دنیا جہان والوں میں سے نہیں کیا'),
(1035, 7, 81, 'تم مردوں کے ساتھ شہوت رانی کرتے ہو عورتوں کو چھوڑ کر، بلکہ تم تو حد ہی سے گزر گئے ہو'),
(1036, 7, 82, 'اور ان کی قوم سے کوئی جواب نہ بن پڑا، بجز اس کے کہ آپس میں کہنے لگے کہ ان لوگوں کو اپنی بستی سے نکال دو۔ یہ لوگ بڑے پاک صاف بنتے ہیں'),
(1037, 7, 83, 'سو ہم نے لوط (علیہ السلام) کو اور ان کے گھر والوں کو بچا لیا بجز ان کی بیوی کے کہ وه ان ہی لوگوں میں رہی جو عذاب میں ره گئے تھے'),
(1038, 7, 84, 'اور ہم نے ان پر خاص طرح کا مینہ برسایا پس دیکھو تو سہی ان مجرموں کا انجام کیسا ہوا؟'),
(1039, 7, 85, 'اور ہم نے مدین کی طرف ان کے بھائی شعیب (علیہ السلام) کو بھیجا۔ انہوں نے فرمایا اے میری قوم! تم اللہ کی عبادت کرو اس کے سوا کوئی تمہارا معبود نہیں، تمہارے پاس تمہارے پروردگار کی طرف سے واضح دلیل آچکی ہے۔ پس تم ناپ اور تول پورا پورا کیا کرو اور لوگوں کو ان کی چیزیں کم کرکے مت دو اور روئے زمین میں، اس کے بعد کہ اس کی درستی کردی گئی، فساد مت پھیلاؤ، یہ تمہارے لئے نافع ہے اگر تم تصدیق کرو'),
(1040, 7, 86, 'اور تم سڑکوں پر اس غرض سے مت بیٹھا کرو کہ اللہ پر ایمان ﻻنے والے کو دھمکیاں دو اور اللہ کی راه سے روکو اور اس میں کجی کی تلاش میں لگے رہو۔ اور اس حالت کو یاد کرو جب کہ تم کم تھے پھر اللہ نے تم کو زیاده کردیا اور دیکھو کہ کیسا انجام ہوا فساد کرنے والوں کا'),
(1041, 7, 87, 'اور اگر تم میں سے کچھ لوگ اس حکم پر، جس کو دے کر مجھ کو بھیجا گیا، ایمان لے آئے ہیں اور کچھ ایمان نہیں ﻻئے ہیں تو ذرا ٹھہر جاؤ! یہاں تک کہ ہمارے درمیان اللہ فیصلہ کئے دیتا ہے اور وه سب فیصلہ کرنے والوں سے بہتر ہے'),
(1042, 7, 88, 'ان کی قوم کے متکبر سرداروں نے کہا کہ اے شعیب! ہم آپ کو اور جو آپ کے ہمراه ایمان والے ہیں ان کو اپنی بستی سے نکال دیں گے الّا یہ کہ تم ہمارے مذہب میں پھر آجاؤ۔ شعیب (علیہ السلام) نے جواب دیا کہ کیا ہم تمہارے مذہب میں آجائیں گو ہم اس کو مکروه ہی سمجھتے ہوں'),
(1043, 7, 89, 'ہم تو اللہ تعالیٰ پر بڑی جھوٹی تہمت لگانے والے ہو جائیں گے اگر ہم تمہارے دین میں آجائیں اس کے بعد کہ اللہ تعالیٰ نے ہم کو اس سے نجات دی اور ہم سے ممکن نہیں کہ تمہارے مذہب میں پھر آجائیں، لیکن ہاں یہ کہ اللہ ہی نے جو ہمارا مالک ہے مقدر کیا ہو۔ ہمارے رب کا علم ہر چیز کو محیط ہے، ہم اللہ ہی پر بھروسہ رکھتے ہیں۔ اے ہمارے پروردگار! ہمارے اور ہماری قوم کے درمیان حق کے موافق فیصلہ کر دے اور تو سب سے اچھا فیصلہ کرنے واﻻ ہے'),
(1044, 7, 90, 'اور ان کی قوم کے کافر سرداروں نے کہا کہ اگر تم شعیب (علیہ السلام) کی راه پر چلو گے تو بےشک بڑا نقصان اٹھاؤ گے'),
(1045, 7, 91, 'پس ان کو زلزلے نے آپکڑا سو وه اپنے گھروں میں اوندھے کے اوندھے پڑے ره گئے'),
(1046, 7, 92, 'جنہوں نے شعیب (علیہ السلام) کی تکذیب کی تھی ان کی یہ حالت ہوگئی جیسے ان گھروں میں کبھی بسے ہی نہ تھے۔ جنہوں نے شعیب (علیہ السلام) کی تکذیب کی تھی وہی خسارے میں پڑ گئے'),
(1047, 7, 93, 'اس وقت شعیب (علیہ السلام) ان سے منھ موڑ کر چلے اور فرمانے لگے کہ اے میری قوم! میں نے تم کو اپنے پروردگار کے احکام پہنچا دیئے تھے اور میں نے تمہاری خیر خواہی کی۔ پھر میں ان کافر لوگوں پر کیوں رنج کروں'),
(1048, 7, 94, 'اور ہم نے کسی بستی میں کوئی نبی نہیں بھیجا کہ وہاں کے رہنے والوں کو ہم نے سختی اور تکلیف میں نہ پکڑا ہو، تاکہ وه گڑ گڑائیں'),
(1049, 7, 95, 'پھر ہم نے اس بدحالی کی جگہ خوش حالی بدل دی، یہاں تک کہ ان کو خوب ترقی ہوئی اور کہنے لگے کہ ہمارے آباواجداد کو بھی تنگی اور راحت پیش آئی تھی تو ہم نے ان کو دفعتاً پکڑ لیا اور ان کو خبر بھی نہ تھی'),
(1050, 7, 96, 'اور اگر ان بستیوں کے رہنے والے ایمان لے آتے اور پرہیزگاری اختیار کرتے تو ہم ان پر آسمان اور زمین کی برکتیں کھول دیتے لیکن انہوں نے تکذیب کی تو ہم نے ان کے اعمال کی وجہ سے ان کو پکڑ لیا'),
(1051, 7, 97, 'کیا پھر بھی ان بستیوں کے رہنے والے اس بات سے بے فکر ہوگئے ہیں کہ ان پر ہمارا عذاب شب کے وقت آپڑے جس وقت وه سوتے ہوں'),
(1052, 7, 98, 'اور کیا ان بستیوں کے رہنے والے اس بات سے بےفکر ہوگئے ہیں کہ ان پر ہمارا عذاب دن چڑھے آپڑے جس وقت کہ وه اپنے کھیلوں میں مشغول ہوں'),
(1053, 7, 99, 'کیا پس وه اللہ کی اس پکڑ سے بےفکر ہوگئے۔ سو اللہ کی پکڑ سے بجز ان کے جن کی شامت ہی آگئی ہو اور کوئی بےفکر نہیں ہوتا'),
(1054, 7, 100, 'اور کیا ان لوگوں کو جو زمین کے وارث ہوئے وہاں کے لوگوں کی ہلاکت کے بعد (ان واقعات مذکوره نے) یہ بات نہیں بتلائی کہ اگر ہم چاہیں تو ان کے جرائم کے سبب ان کو ہلاک کر ڈالیں اور ہم ان کے دلوں پر بند لگا دیں، پس وه نہ سن سکیں'),
(1055, 7, 101, 'ان بستیوں کے کچھ کچھ قصے ہم آپ سے بیان کر رہے ہیں اور ان سب کے پاس ان کے پیغمبر معجزات لے کر آئے، پھر جس چیز کو انہوں نے ابتدا میں جھوٹا کہہ دیا یہ بات نہ ہوئی کہ پھر اس کو مان لیتے، اللہ تعالیٰ اسی طرح کافروں کے دلوں پر بند لگا دیتا ہے'),
(1056, 7, 102, 'اور اکثر لوگوں میں ہم نے وفائے عہد نہ دیکھا اور ہم نے اکثر لوگوں کو بےحکم ہی پایا'),
(1057, 7, 103, 'پھر ان کے بعد ہم نے موسیٰ (علیہ السلام) کو اپنے دﻻئل دے کر فرعون اور اس کے امرا کے پاس بھیجا، مگر ان لوگوں نے ان کا بالکل حق ادا نہ کیا۔ سو دیکھئے ان مفسدوں کا کیا انجام ہوا؟'),
(1058, 7, 104, 'اور موسیٰ (علیہ السلام) نے فرمایا کہ اے فرعون! میں رب العالمین کی طرف سے پیغمبر ہوں'),
(1059, 7, 105, 'میرے لیے یہی شایان ہے کہ بجز سچ کے اللہ کی طرف کوئی بات منسوب نہ کروں، میں تمہارے پاس تمہارے رب کی طرف سے ایک بڑی دلیل بھی ﻻیا ہوں، سو تو بنی اسرائیل کو میرے ساتھ بھیج دے'),
(1060, 7, 106, 'فرعون نے کہا، اگر آپ کوئی معجزه لے کر آئے ہیں تو اس کو اب پیش کیجئے! اگر آپ سچے ہیں'),
(1061, 7, 107, 'پس آپ نے اپنا عصا ڈال دیا، سو دفعتاً وه صاف ایک اﮊدھا بن گیا'),
(1062, 7, 108, 'اور اپنا ہاتھ باہر نکالا سو وه یکایک سب دیکھنے والوں کے روبرو بہت ہی چمکتا ہوا ہو گیا'),
(1063, 7, 109, 'قوم فرعون میں جو سردار لوگ تھے انہوں نے کہا کہ واقعی یہ شخص بڑا ماہر جادوگر ہے'),
(1064, 7, 110, 'یہ چاہتا ہے کہ تم کو تمہاری سرزمین سے باہر کر دے سو تم لوگ کیا مشوره دیتے ہو'),
(1065, 7, 111, 'انہوں نے کہا کہ آپ ان کو اور ان کے بھائی کو مہلت دیجئے اور شہروں میں ہرکاروں کو بھیج دیجئے'),
(1066, 7, 112, 'کہ وه سب ماہر جادو گروں کو آپ کے پاس ﻻ کر حاضر کر دیں'),
(1067, 7, 113, 'اور وه جادوگر فرعون کے پاس حاضر ہوئے، کہنے لگے کہ اگر ہم غالب آئے تو ہم کو کوئی بڑا صلہ ملے گا؟'),
(1068, 7, 114, 'فرعون نے کہا کہ ہاں اور تم مقرب لوگوں میں داخل ہو جاؤ گے'),
(1069, 7, 115, 'ان ساحروں نے عرض کیا کہ اے موسیٰ! خواه آپ ڈالئے اور یا ہم ہی ڈالیں؟'),
(1070, 7, 116, '(موسیٰ علیہ السلام) نے فرمایا کہ تم ہی ڈالو، پس جب انہوں نے ڈاﻻ تو لوگوں کی نظر بندی کر دی اور ان پر ہیبت غالب کر دی اور ایک طرح کا بڑا جادو دکھلایا'),
(1071, 7, 117, 'اور ہم نے موسیٰ (علیہ السلام) کو حکم دیا کہ اپنا عصا ڈال دیجئے! سو عصا کا ڈالنا تھا کہ اس نے ان کے سارے بنے بنائے کھیل کو نگلنا شروع کیا'),
(1072, 7, 118, 'پس حق ﻇاہر ہوگیا اور انہوں نے جو کچھ بنایا تھا سب جاتا رہا'),
(1073, 7, 119, 'پس وه لوگ اس موقع پر ہار گئے اور خوب ذلیل ہوکر پھرے'),
(1074, 7, 120, 'اور وه جو ساحر تھے سجده میں گر گئے'),
(1075, 7, 121, 'کہنے لگے کہ ہم ایمان ﻻئے رب العالمین پر'),
(1076, 7, 122, 'جو موسیٰ اور ہارون کا بھی رب ہے'),
(1077, 7, 123, 'فرعون کہنے لگا کہ تم موسیٰ پر ایمان ﻻئے ہو بغیر اس کے کہ میں تم کو اجازت دوں؟ بےشک یہ سازش تھی جس پر تمہارا عمل درآمد ہوا ہے اس شہر میں تاکہ تم سب اس شہر سے یہاں کے رہنے والوں کو باہر نکال دو۔ سو اب تم کو حقیقت معلوم ہوئی جاتی ہے'),
(1078, 7, 124, 'میں تمہارے ایک طرف کے ہاتھ اور دوسری طرف کے پاؤں کاٹوں گا۔ پھر تم سب کو سولی پر لٹکا دوں گا'),
(1079, 7, 125, 'انہوں نے جواب دیا کہ ہم (مر کر) اپنے مالک ہی کے پاس جائیں گے'),
(1080, 7, 126, 'اور تو نے ہم میں کونسا عیب دیکھا ہے بجز اس کے کہ ہم اپنے رب کے احکام پر ایمان لے آئے، جب وه ہمارے پاس آئے۔ اے ہمارے رب! ہمارے اوپر صبر کا فیضان فرما اور ہماری جان حالت اسلام پر نکال'),
(1081, 7, 127, 'اور قوم فرعون کے سرداروں نے کہا کہ کیا آپ موسیٰ (علیہ السلام) اور ان کی قوم کو یوں ہی رہنے دیں گے کہ وه ملک میں فساد کرتے پھریں، اور وه آپ کو اور آپ کے معبودوں کو ترک کئے رہیں۔ فرعون نے کہا کہ ہم ابھی ان لوگوں کے بیٹوں کو قتل کرنا شروع کر دیں گے اور عورتوں کو زنده رہنے دیں گے اور ہم کو ان پر ہر طرح کا زور ہے'),
(1082, 7, 128, 'موسیٰ (علیہ السلام) نے اپنی قوم سے فرمایا اللہ تعالیٰ کا سہارا حاصل کرو اور صبر کرو، یہ زمین اللہ تعالیٰ کی ہے، اپنے بندوں میں سے جس کو چاہے وه مالک بنا دے اور اخیر کامیابی ان ہی کی ہوتی ہے جو اللہ سے ڈرتے ہیں'),
(1083, 7, 129, 'قوم کے لوگ کہنے لگے کہ ہم تو ہمیشہ مصیبت ہی میں رہے، آپ کی تشریف آوری سے قبل بھی اور آپ کی تشریف آوری کے بعد بھی۔ موسیٰ (علیہ السلام) نے فرمایا کہ بہت جلد اللہ تمہارے دشمن کو ہلاک کر دے گا اور بجائے ان کے تم کو اس سرزمین کا خلیفہ بنا دے گا پھر تمہارا طرز عمل دیکھے گا'),
(1084, 7, 130, 'اور ہم نے فرعون والوں کو مبتلا کیا قحط سالی میں اور پھلوں کی کم پیداواری میں، تاکہ وه نصیحت قبول کریں'),
(1085, 7, 131, 'سو جب ان پر خوشحالی آجاتی تو کہتے کہ یہ تو ہمارے لیے ہونا ہی چاہئے اور اگر ان کو کوئی بدحالی پیش آتی تو موسیٰ (علیہ السلام) اور ان کے ساتھیوں کی نحوست بتلاتے۔ یاد رکھو کہ ان کی نحوست اللہ تعالیٰ کے پاس ہے، لیکن ان کے اکثر لوگ نہیں جانتے'),
(1086, 7, 132, 'اور یوں کہتے کیسی ہی بات ہمارے سامنے لاؤ کہ ان کے ذریعہ سے ہم پر جادو چلاؤ جب بھی ہم تمہاری بات ہر گز نہ مانیں گے'),
(1087, 7, 133, 'پھر ہم نے ان پر طوفان بھیجا اور ٹڈیاں اور گھن کا کیڑا اور مینڈک اور خون، کہ یہ سب کھلے کھلے معجزے تھے۔ سو وه تکبر کرتے رہے اور وه لوگ کچھ تھے ہی جرائم پیشہ'),
(1088, 7, 134, 'اور جب ان پر کوئی عذاب واقع ہوتا تو یوں کہتے کہ اے موسیٰ! ہمارے لیے اپنے رب سے اس بات کی دعا کر دیجئے! جس کا اس نے آپ سے عہد کر رکھا ہے، اگر آپ اس عذاب کو ہم سے ہٹا دیں تو ہم ضرور ضرور آپ کے کہنے سے ایمان لے آئیں گے اور ہم بنی اسرائیل کو بھی (رہا کر کے) آپ کے ہمراه کر دیں گے'),
(1089, 7, 135, 'پھر جب ان سے اس عذاب کو ایک خاص وقت تک کہ اس تک ان کو پہنچنا تھا ہٹا دیتے، تو وه فوراً ہی عہد شکنی کرنے لگتے'),
(1090, 7, 136, 'پھر ہم نے ان سے بدلہ لیا یعنی ان کو دریا میں غرق کر دیا اس سبب سے کہ وه ہماری آیتوں کو جھٹلاتے تھے اور ان سے بالکل ہی غفلت کرتے تھے'),
(1091, 7, 137, 'اور ہم نے ان لوگوں کو جو کہ بالکل کمزور شمار کئے جاتے تھے۔ اس سرزمین کے پورب پچھم کا مالک بنا دیا، جس میں ہم نے برکت رکھی ہے اور آپ کے رب کا نیک وعده، بنی اسرائیل کے حق میں ان کے صبر کی وجہ سے پورا ہوگیا اور ہم نے فرعون کے اور اس کی قوم کے ساختہ پرداختہ کارخانوں کو اور جو کچھ وه اونچی اونچی عمارتیں بنواتے تھے، سب کو درہم برہم کر دیا'),
(1092, 7, 138, 'اور ہم نے بنی اسرائیل کو دریا سے پار اتار دیا۔ پس ان لوگوں کا ایک قوم پر گزر ہوا جو اپنے چند بتوں سے لگے بیٹھے تھے، کہنے لگے اے موسیٰ! ہمارے لیے بھی ایک معبود ایسا ہی مقرر کر دیجئے! جیسے ان کے یہ معبود ہیں۔ آپ نے فرمایا کہ واقعی تم لوگوں میں بڑی جہالت ہے'),
(1093, 7, 139, 'یہ لوگ جس کام میں لگے ہیں یہ تباه کیا جائے گا اور ان کا یہ کام محض بےبنیاد ہے'),
(1094, 7, 140, 'فرمایا کیا اللہ تعالیٰ کے سوا اور کسی کو تمہارا معبود تجویز کردوں؟ حاﻻنکہ اس نے تم کو تمام جہان والوں پر فوقیت دی ہے'),
(1095, 7, 141, 'اور وه وقت یاد کرو جب ہم نے تم کو فرعون والوں سے بچا لیا جو تم کو بڑی سخت تکلیفیں پہنچاتے تھے۔ تمہارے بیٹوں کو قتل کر ڈالتے تھے اور تمہاری عورتوں کو زنده چھوڑ دیتے تھے اور اس میں تمہارے پروردگار کی طرف سے بڑی بھاری آزمائش تھی'),
(1096, 7, 142, 'اور ہم نے موسیٰ (علیہ السلام) سے تیس راتوں کا وعده کیا اور دس رات مزید سے ان تیس راتوں کو پورا کیا۔ سو ان کے پروردگار کا وقت پورے چالیس رات کا ہوگیا۔ اور موسیٰ (علیہ السلام) نے اپنے بھائی ہارون (علیہ السلام) سے کہا کہ میرے بعد ان کا انتظام رکھنا اور اصلاح کرتے رہنا اور بدنظم لوگوں کی رائے پر عمل مت کرنا'),
(1097, 7, 143, 'اور جب موسیٰ (علیہ السلام) ہمارے وقت پر آئے اور ان کے رب نے ان سے باتیں کیں تو عرض کیا کہ اے میرے پروردگار! اپنا دیدار مجھ کو کرا دیجئے کہ میں آپ کو ایک نظر دیکھ لوں ارشاد ہوا کہ تم مجھ کو ہرگز نہیں دیکھ سکتے لیکن تم اس پہاڑ کی طرف دیکھتے رہو وه اگر اپنی جگہ پر برقرار رہا تو تم بھی مجھے دیکھ سکو گے۔ پس جب ان کے رب نے پہاڑ پر تجلی فرمائی تو تجلی نے اس کے پرخچے اڑا دیئے اور موسیٰ (علیہ السلام) بےہوش ہوکر گر پڑے۔ پھر جب ہوش میں آئے تو عرض کیا، بےشک آپ کی ذات منزه ہے میں آپ کی جناب میں توبہ کرتا ہوں اور میں سب سے پہلے آپ پر ایمان ﻻنے واﻻ ہوں'),
(1098, 7, 144, 'ارشاد ہوا کہ اے موسیٰ! میں نے پیغمبری اور اپنی ہمکلامی سے اور لوگوں پر تم کو امتیاز دیا ہے تو جو کچھ تم کو میں نے عطا کیا ہے اس کو لو اور شکر کرو'),
(1099, 7, 145, 'اور ہم نے چند تختیوں پر ہر قسم کی نصیحت اور ہر چیز کی تفصیل ان کو لکھ کر دی، تم ان کو پوری طاقت سے پکڑ لو اور اپنی قوم کو حکم کرو کہ ان کے اچھے اچھے احکام پر عمل کریں، اب بہت جلد تم لوگوں کو ان بے حکموں کا مقام دکھلاتا ہوں'),
(1100, 7, 146, 'میں ایسے لوگوں کو اپنے احکام سے برگشتہ ہی رکھوں گا جو دنیا میں تکبر کرتے ہیں، جس کا ان کو کوئی حق حاصل نہیں اور اگر تمام نشانیاں دیکھ لیں تب بھی وه ان پر ایمان نہ ﻻئیں، اور اگر ہدایت کا راستہ دیکھیں تو اس کو اپنا طریقہ نہ بنائیں اور اگر گمراہی کا راستہ دیکھ لیں تو اس کو اپنا طریقہ بنالیں۔ یہ اس سبب سے ہے کہ انہوں نے ہماری آیتوں کو جھٹلایا اور ان سے غافل رہے'),
(1101, 7, 147, 'اور یہ لوگ جنہوں نے ہماری آیتوں کو اور قیامت کے پیش آنے کو جھٹلایا ان کے سب کام غارت گئے۔ ان کو وہی سزا دی جائے گی جو کچھ یہ کرتے تھے'),
(1102, 7, 148, 'اور موسیٰ (علیہ السلام) کی قوم نے ان کے بعد اپنے زیوروں کا ایک بچھڑا معبود ٹھہرا لیا جو کہ ایک قالب تھا جس میں ایک آواز تھی۔ کیا انہوں نے یہ نہ دیکھا کہ وه ان سے بات نہیں کرتا تھا اور نہ ان کو کوئی راه بتلاتا تھا اس کو انہوں نے معبود قرار دیا اور بڑی بے انصافی کا کام کیا'),
(1103, 7, 149, 'اور جب نادم ہوئے اور معلوم ہوا کہ واقعی وه لوگ گمراہی میں پڑ گئے تو کہنے لگے کہ اگر ہمارا رب ہم پر رحم نہ کرے اور ہمارا گناه معاف نہ کرے تو ہم بالکل گئے گزرے ہو جائیں گے'),
(1104, 7, 150, 'اور جب موسیٰ (علیہ السلام) اپنی قوم کی طرف واپس آئے غصہ اور رنج میں بھرے ہوئے تو فرمایا کہ تم نے میرے بعد یہ بڑی بری جانشینی کی؟ کیا اپنے رب کے حکم سے پہلے ہی تم نے جلد بازی کرلی، اور جلدی سے تختیاں ایک طرف رکھیں اور اپنے بھائی کا سر پکڑ کر ان کو اپنی طرف گھسیٹنے لگے۔ ہارون (علیہ السلام) نے کہا کہ اے میرے ماں جائے! ان لوگوں نے مجھ کو بےحقیقت سمجھا اور قریب تھا کہ مجھ کو قتل کر ڈالیں تو تم مجھ پر دشمنوں کو مت ہنساؤ اور مجھ کو ان ﻇالموں کے ذیل میں مت شمار کرو'),
(1105, 7, 151, 'موسیٰ (علیہ السلام) نے کہا کہ اے میرے رب! میری خطا معاف فرما اور میرے بھائی کی بھی اور ہم دونوں کو اپنی رحمت میں داخل فرما اور تو سب رحم کرنے والوں سے زیاده رحم کرنے واﻻ ہے'),
(1106, 7, 152, 'بےشک جن لوگوں نے گوسالہ پرستی کی ہے ان پر بہت جلد ان کے رب کی طرف سے غضب اور ذلت اس دنیوی زندگی ہی میں پڑے گی اور ہم افترا پردازوں کو ایسی ہی سزا دیا کرتے ہیں'),
(1107, 7, 153, 'اور جن لوگوں نے گناه کے کام کئے پھر وه ان کے بعد توبہ کر لیں اور ایمان لے آئیں تو تمہارا رب اس توبہ کے بعد گناه معاف کر دینے واﻻ، رحمت کرنے واﻻ ہے'),
(1108, 7, 154, 'اور جب موسیٰ (علیہ السلام) کا غصہ فرد ہوا تو ان تختیوں کو اٹھا لیا اور ان کے مضامین میں ان لوگوں کے لیے جو اپنے رب سے ڈرتے تھے ہدایت اور رحمت تھی'),
(1109, 7, 155, 'اور موسیٰ (علیہ السلام) نے ستر آدمی اپنی قوم میں سے ہمارے وقت معین کے لیے منتخب کئے، سو جب ان کو زلزلہ نے آپکڑا تو موسیٰ (علیہ السلام) عرض کرنے لگے کہ اے میرے پروردگار! اگر تجھ کو یہ منظور ہوتا تو اس سے قبل ہی ان کو اور مجھ کو ہلاک کر دیتا۔ کیا تو ہم میں سے چند بے وقوفوں کی حرکت پر سب کو ہلاک کردے گا؟ یہ واقعہ محض تیری طرف سے ایک امتحان ہے، ایسے امتحانات سے جس کو تو چاہے گمراہی میں ڈال دے اور جس کو چاہے ہدایت پر قائم رکھے۔ تو ہی تو ہمارا کارساز ہے پس ہم پر مغفرت اور رحمت فرما اور تو سب معافی دینے والوں سے زیاده اچھا ہے'),
(1110, 7, 156, 'اور ہم لوگوں کے نام دنیا میں بھی نیک حالی لکھ دے اور آخرت میں بھی، ہم تیری طرف رجوع کرتے ہیں۔ اللہ تعالیٰ نے فرمایا کہ میں اپنا عذاب اسی پر واقع کرتا ہوں جس پر چاہتا ہوں اور میری رحمت تمام اشیا پر محیط ہے۔ تو وه رحمت ان لوگوں کے نام ضرور لکھوں گا جو اللہ سے ڈرتے ہیں اور زکوٰة دیتے ہیں اور جو ہماری آیتوں پر ایمان ﻻتے ہیں'),
(1111, 7, 157, 'جو لوگ ایسے رسول نبی امی کا اتباع کرتے ہیں جن کو وه لوگ اپنے پاس تورات وانجیل میں لکھا ہوا پاتے ہیں۔ وه ان کو نیک باتوں کا حکم فرماتے ہیں اور بری باتوں سے منع کرتے ہیں اور پاکیزه چیزوں کو حلال بتاتے ہیں اور گندی چیزوں کو ان پر حرام فرماتے ہیں اور ان لوگوں پر جو بوجھ اور طوق تھے ان کو دور کرتے ہیں۔ سو جو لوگ اس نبی پر ایمان ﻻتے ہیں اور ان کی حمایت کرتے ہیں اور ان کی مدد کرتے ہیں اور اس نور کا اتباع کرتے ہیں جو ان کے ساتھ بھیجا گیا ہے، ایسے لوگ پوری فلاح پانے والے ہیں'),
(1112, 7, 158, 'آپ کہہ دیجئے کہ اے لوگو! میں تم سب کی طرف اس اللہ تعالیٰ کا بھیجا ہوا ہوں، جس کی بادشاہی تمام آسمانوں اور زمین میں ہے اس کے سوا کوئی عبادت کے ﻻئق نہیں وہی زندگی دیتا ہے اور وہی موت دیتا ہے سو اللہ تعالیٰ پر ایمان لاؤ اور اس کے نبی امی پر جو کہ اللہ تعالیٰ پر اور اس کے احکام پر ایمان رکھتے ہیں اور ان کا اتباع کرو تاکہ تم راه پر آجاؤ'),
(1113, 7, 159, 'اور قوم موسیٰ میں ایک جماعت ایسی بھی ہے جو حق کے مطابق ہدایت کرتی ہے اور اسی کے مطابق انصاف بھی کرتی ہے'),
(1114, 7, 160, 'اور ہم نے ان کو باره خاندانوں میں تقسیم کرکے سب کی الگ الگ جماعت مقرر کر دی اور ہم نے موسیٰ (علیہ السلام) کو حکم دیا جب کہ ان کی قوم نے ان سے پانی مانگا کہ اپنے عصا کو فلاں پتھر پر مارو پس فوراً اس سے باره چشمے پھوٹ نکلے۔ ہر ہر شخص نے اپنے پانی پینے کا موقع معلوم کر لیا۔ اور ہم نے ان پر ابر کو سایہ فگن کیا اور ان کو من وسلوی (ترنجبین اور بٹیریں) پہنچائیں، کھاؤ نفیس چیزوں سے جو کہ ہم نے تم کو دی ہیں اور انہوں نے ہمارا کوئی نقصان نہیں کیا لیکن اپنا ہی نقصان کرتے تھے'),
(1115, 7, 161, 'اور جب ان کو حکم دیا گیا کہ تم لوگ اس آبادی میں جاکر رہو اور کھاؤ اس سے جس جگہ تم رغبت کرو اور زبان سے یہ کہتے جانا کہ توبہ ہے اور جھکے جھکے دروازه میں داخل ہونا ہم تمہاری خطائیں معاف کردیں گے۔ جو لوگ نیک کام کریں گے ان کو مزید برآں اور دیں گے'),
(1116, 7, 162, 'سو بدل ڈاﻻ ان ﻇالموں نے ایک اور کلمہ جو خلاف تھا اس کلمہ کے جس کی ان سے فرمائش کی گئی تھی، اس پر ہم نے ان پر ایک آفت سماوی بھیجی اس وجہ سے کہ وه حکم کو ضائع کرتے تھے'),
(1117, 7, 163, 'اور آپ ان لوگوں سے، اس بستی والوں کا جو کہ دریائے (شور) کے قریب آباد تھے اس وقت کا حال پوچھئے! جب کہ وه ہفتہ کے بارے میں حد سے نکل رہے تھے جب کہ ان کے ہفتہ کے روز تو ان کی مچھلیاں ﻇاہر ہو ہو کر ان کے سامنے آتی تھیں، اور جب ہفتہ کا دن نہ ہوتا تو ان کے سامنے نہ آتی تھیں، ہم ان کی اس طرح پر آزمائش کرتے تھے اس سبب سے کہ وه بےحکمی کیا کرتے تھے'),
(1118, 7, 164, 'اور جب کہ ان میں سے ایک جماعت نے یوں کہا کہ تم ایسے لوگوں کو کیوں نصیحت کرتے ہو جن کو اللہ بالکل ہلاک کرنے واﻻ ہے یا ان کو سخت سزا دینے واﻻ ہے؟ انہو ں نے جواب دیا کہ تمہارے رب کے روبرو عذر کرنے کے لیے اور اس لیے کہ شاید یہ ڈر جائیں'),
(1119, 7, 165, 'سو جب وه اس کو بھول گئے جو ان کو سمجھایا جاتا تھا تو ہم نے ان لوگوں کو تو بچا لیا جو اس بری عادت سے منع کیا کرتے تھے اور ان لوگوں کو جو کہ زیادتی کرتے تھے ایک سخت عذاب میں پکڑ لیا اس وجہ سے کہ وه بےحکمی کیا کرتے تھے'),
(1120, 7, 166, 'یعنی جب وه، جس کام سے ان کو منع کیا گیا تھا اس میں حد سے نکل گئے تو ہم نے ان کو کہہ دیا تم ذلیل بندر بن جاؤ'),
(1121, 7, 167, 'اور وه وقت یاد کرنا چاہیئے کہ آپ کے رب نے یہ بات بتلا دی کہ وه ان یہود پر قیامت تک ایسے شخص کو ضرور مسلط کرتا رہے گا جو ان کو سزائے شدید کی تکلیف پہنچاتا رہے گا، بلاشبہ آپ کا رب جلدی ہی سزا دے دیتا ہے اور بلاشبہ وه واقعی بڑی مغفرت اور بڑی رحمت واﻻ ہے'),
(1122, 7, 168, 'اور ہم نے دنیا میں ان کی مختلف جماعتیں کر دیں۔ بعض ان میں نیک تھے اور بعض ان میں اور طرح تھے اور ہم ان کو خوش حالیوں اور بدحالیوں سے آزماتے رہے کہ شاید باز آجائیں'),
(1123, 7, 169, 'پھر ان کے بعد ایسے لوگ ان کے جانشین ہوئے کہ کتاب کو ان سے حاصل کیا وه اس دنیائے فانی کا مال متاع لے لیتے ہیں اور کہتے ہیں کہ ہماری ضرور مغفرت ہو جائے گی حاﻻنکہ اگر ان کے پاس ویسا ہی مال متاع آنے لگے تو اس کو بھی لے لیں گے۔ کیا ان سے اس کتاب کے اس مضمون کا عہد نہیں لیا گیا کہ اللہ کی طرف بجز حق بات کے اور کسی بات کی نسبت نہ کریں، اور انہوں نے اس کتاب میں جو کچھ تھا اس کو پڑھ لیا اور آخرت واﻻ گھر ان لوگوں کے لیے بہتر ہے جو تقویٰ رکھتے ہیں، پھر کیا تم نہیں سمجھتے'),
(1124, 7, 170, 'اور جو لوگ کتاب کے پابند ہیں اور نماز کی پابندی کرتے ہیں، ہم ایسے لوگوں کا جو اپنی اصلاح کریں ﺛواب ضائع نہ کریں گے'),
(1125, 7, 171, 'اور وه وقت بھی قابل ذکر ہے جب ہم نے پہاڑ کو اٹھا کر سائبان کی طرح ان کے اوپر معلق کر دیا اور ان کو یقین ہوگیا کہ اب ان پر گرا اور کہا کہ جو کتاب ہم نے تم کو دی ہے اسے مضبوطی کے ساتھ قبول کرو اور یاد رکھو جو احکام اس میں ہیں اس سے توقع ہے کہ تم متقی بن جاؤ'),
(1126, 7, 172, 'اور جب آپ کے رب نے اوﻻد آدم کی پشت سے ان کی اوﻻد کو نکالا اور ان سے ان ہی کے متعلق اقرار لیا کہ کیا میں تمہارا رب نہیں ہوں؟ سب نے جواب دیا کیوں نہیں! ہم سب گواه بنتے ہیں۔ تاکہ تم لوگ قیامت کے روز یوں نہ کہو کہ ہم تو اس سے محض بے خبر تھے'),
(1127, 7, 173, 'یا یوں کہو کہ پہلے پہلے شرک تو ہمارے بڑوں نے کیا اور ہم ان کے بعد ان کی نسل میں ہوئے، سو کیا ان غلط راه والوں کے فعل پر تو ہم کو ہلاکت میں ڈال دے گا؟'),
(1128, 7, 174, 'ہم اسی طرح آیات کو صاف صاف بیان کرتے ہیں اور تاکہ وه باز آجائیں'),
(1129, 7, 175, 'اور ان لوگوں کو اس شخص کا حال پڑھ کر سنائیے کہ جس کو ہم نے اپنی آیتیں دیں پھر وه ان سے بالکل ہی نکل گیا، پھر شیطان اس کے پیچھے لگ گیا سو وه گمراه لوگوں میں شامل ہوگیا'),
(1130, 7, 176, 'اور اگر ہم چاہتے تو اس کو ان آیتوں کی بدولت بلند مرتبہ کر دیتے لیکن وه تو دنیا کی طرف مائل ہوگیا اور اپنی نفسانی خواہش کی پیروی کرنے لگا سو اس کی حالت کتے کی سی ہوگئی کہ اگر تو اس پر حملہ کرے تب بھی ہانپے یا اس کو چھوڑ دے تب بھی ہانپے، یہی حالت ان لوگوں کی ہے جنہوں نے ہماری آیتوں کو جھٹلایا۔ سو آپ اس حال کو بیان کر دیجئے شاید وه لوگ کچھ سوچیں'),
(1131, 7, 177, 'ان لوگوں کی حالت بھی بری حالت ہے جو ہماری آیات کو جھٹلاتے ہیں اور وه اپنا نقصان کرتے ہیں'),
(1132, 7, 178, 'جس کو اللہ ہدایت کرتا ہے سو ہدایت پانے واﻻ وہی ہوتا ہے اور جس کو وه گمراه کر دے سو ایسے ہی لوگ خسارے میں پڑنے والے ہیں'),
(1133, 7, 179, 'اور ہم نے ایسے بہت سے جن اور انسان دوزخ کے لیے پیدا کئے ہیں، جن کے دل ایسے ہیں جن سے نہیں سمجھتے اور جن کی آنکھیں ایسی ہیں جن سے نہیں دیکھتے اور جن کے کان ایسے ہیں جن سے نہیں سنتے۔ یہ لوگ چوپایوں کی طرح ہیں بلکہ یہ ان سے بھی زیاده گمراه ہیں۔ یہی لوگ غافل ہیں'),
(1134, 7, 180, 'اور اچھے اچھے نام اللہ ہی کے لیے ہیں سو ان ناموں سے اللہ ہی کو موسوم کیا کرو اور ایسے لوگوں سے تعلق بھی نہ رکھو جو اس کے ناموں میں کج روی کرتے ہیں، ان لوگوں کو ان کے کئے کی ضرور سزا ملے گی'),
(1135, 7, 181, 'اور ہماری مخلوق میں ایک جماعت ایسی بھی ہے جو حق کے موافق ہدایت کرتی ہے اور اس کے موافق انصاف بھی کرتی ہے'),
(1136, 7, 182, 'اور جو لوگ ہماری آیات کو جھٹلاتے ہیں ہم ان کو بتدریج (گرفت میں) لئے جارہے ہیں اس طور پر کہ ان کو خبر بھی نہیں'),
(1137, 7, 183, 'اور ان کو مہلت دیتا ہوں بےشک میری تدبیر بڑی مضبوط ہے'),
(1138, 7, 184, 'کیا ان لوگوں نے اس بات پر غور نہ کیا کہ ان کے ساتھی کو ذرا بھی جنون نہیں وه تو صرف ایک صاف صاف ڈرانے والے ہیں'),
(1139, 7, 185, 'اور کیا ان لوگوں نے غور نہیں کیا آسمانوں اور زمین کے عالم میں اور دوسری چیزوں میں جو اللہ نے پیدا کی ہیں اور اس بات میں کہ ممکن ہے کہ ان کی اجل قریب ہی آ پہنچی ہو۔ پھر قرآن کے بعد کون سی بات پر یہ لوگ ایمان ﻻئیں گے؟'),
(1140, 7, 186, 'جس کو اللہ تعالیٰ گمراه کر دے اس کو کوئی راه پر نہیں ﻻ سکتا۔ اور اللہ تعالیٰ ان کو ان کی گمراہی میں بھٹکتے ہوئے چھوڑ دیتا ہے'),
(1141, 7, 187, 'یہ لوگ آپ سے قیامت کے متعلق سوال کرتے ہیں کہ اس کا وقوع کب ہوگا؟ آپ فرما دیجئے کہ اس کا علم صرف میرے رب ہی کے پاس ہے، اس کے وقت پر اس کو سوا اللہ کے کوئی اور ﻇاہر نہ کرے گا۔ وه آسمانوں اور زمین میں بڑا بھاری (حادﺛہ) ہوگا وه تم پر محض اچانک آپڑے گی۔ وه آپ سے اس طرح پوچھتے ہیں جیسے گویا آپ اس کی تحقیقات کرچکے ہیں۔ آپ فرما دیجئے کہ اس کا علم خاص اللہ ہی کے پاس ہے لیکن اکثر لوگ نہیں جانتے'),
(1142, 7, 188, 'آپ فرما دیجئے کہ میں خود اپنی ذات خاص کے لیے کسی نفع کا اختیار نہیں رکھتا اور نہ کسی ضرر کا، مگر اتنا ہی کہ جتنا اللہ نے چاہا ہو اور اگر میں غیب کی باتیں جانتا ہوتا تو میں بہت سے منافع حاصل کر لیتا اور کوئی نقصان مجھ کو نہ پہنچتا میں تو محض ڈرانے واﻻ اور بشارت دینے واﻻ ہوں ان لوگوں کو جو ایمان رکھتے ہیں'),
(1143, 7, 189, 'وه اللہ تعالیٰ ایسا ہے جس نے تم کو ایک تن واحد سے پیدا کیا اور اسی سے اس کا جوڑا بنایا تاکہ وه اس اپنے جوڑے سے انس حاصل کرے پھر جب میاں نے بیوی سے قربت کی تو اس کو حمل ره گیا ہلکا سا۔ سو وه اس کو لئے ہوئے چلتی پھرتی رہی، پھر جب وه بوجھل ہوگئی تو دونوں میاں بیوی اللہ سے جو ان کا مالک ہے دعا کرنے لگے کہ اگر تو نے ہم کو صحیح سالم اوﻻد دے دی تو ہم خوب شکر گزاری کریں گے'),
(1144, 7, 190, 'سو جب اللہ نے دونوں کو صحیح سالم اوﻻد دے دی تو اللہ کی دی ہوئی چیز میں وه دونوں اللہ کے شریک قرار دینے لگے، سو اللہ پاک ہے ان کے شرک سے'),
(1145, 7, 191, 'کیا ایسوں کو شریک ٹھہراتے ہیں جو کسی چیز کو پیدا نہ کر سکیں اور وه خود ہی پیدا کئے گئے ہوں'),
(1146, 7, 192, 'اور وه ان کو کسی قسم کی مدد نہیں دے سکتے اور وه خود بھی مدد نہیں کرسکتے'),
(1147, 7, 193, 'اور اگر تم ان کو کوئی بات بتلانے کو پکارو تو تمہارے کہنے پر نہ چلیں تمہارے اعتبار سے دونوں امر برابر ہیں خواه تم ان کو پکارو یا تم خاموش رہو'),
(1148, 7, 194, 'واقعی تم اللہ کو چھوڑ کر جن کی عبادت کرتے ہو وه بھی تم ہی جیسے بندے ہیں سو تم ان کو پکارو پھر ان کو چاہئے کہ تمہارا کہنا کر دیں اگر تم سچے ہو'),
(1149, 7, 195, 'کیا ان کے پاؤں ہیں جن سے وه چلتے ہوں یا ان کے ہاتھ ہیں جن سے وه کسی چیز کو تھام سکیں، یا ان کی آنکھیں ہیں جن سے وه دیکھتے ہوں، یا ان کے کان ہیں جن سے وه سنتے ہیں آپ کہہ دیجئے! تم اپنے سب شرکا کو بلا لو، پھر میری ضرر رسانی کی تدبیر کرو پھر مجھ کو ذرا مہلت مت دو'),
(1150, 7, 196, 'یقیناً میرا مددگار اللہ تعالیٰ ہے جس نے یہ کتاب نازل فرمائی اور وه نیک بندوں کی مدد کرتا ہے'),
(1151, 7, 197, 'اور تم جن لوگوں کی اللہ کو چھوڑ کر عبادت کرتے ہو وه تمہاری کچھ مدد نہیں کرسکتے اور نہ وه اپنی مدد کرسکتے ہیں'),
(1152, 7, 198, 'اور ان کو اگر کوئی بات بتلانے کو پکارو تو اس کو نہ سنیں اور ان کو آپ دیکھتے ہیں کہ گویا وه آپ کو دیکھ رہے ہیں اور وه کچھ بھی نہیں دیکھتے'),
(1153, 7, 199, 'آپ درگزر کو اختیار کریں نیک کام کی تعلیم دیں اور جاہلوں سے ایک کناره ہو جائیں'),
(1154, 7, 200, 'اور اگر آپ کو کوئی وسوسہ شیطان کی طرف سے آنے لگے تو اللہ کی پناه مانگ لیا کیجئے بلاشبہ وه خوب سننے واﻻ خوب جاننے واﻻ ہے'),
(1155, 7, 201, 'یقیناً جو لوگ خدا ترس ہیں جب ان کو کوئی خطره شیطان کی طرف سے آجاتا ہے تو وه یاد میں لگ جاتے ہیں، سو یکایک ان کی آنکھیں کھل جاتی ہیں'),
(1156, 7, 202, 'اور جو شیاطین کے تابع ہیں وه ان کو گمراہی میں کھینچے لے جاتے ہیں پس وه باز نہیں آتے'),
(1157, 7, 203, 'اور جب آپ کوئی معجزه ان کے سامنے ﻇاہر نہیں کرتے تو وه لوگ کہتے ہیں کہ آپ یہ معجزه کیوں نہ ﻻئے؟ آپ فرما دیجئے! کہ میں اس کا اتباع کرتا ہوں جو مجھ پر میرے رب کی طرف سے حکم بھیجا گیا ہے یہ گویا بہت سی دلیلیں ہیں تمہارے رب کی طرف سے اور ہدایت اور رحمت ہے ان لوگوں کے لئے جو ایمان رکھتے ہیں'),
(1158, 7, 204, 'اور جب قرآن پڑھا جایا کرے تو اس کی طرف کان لگا دیا کرو اور خاموش رہا کرو امید ہے کہ تم پر رحمت ہو'),
(1159, 7, 205, 'اور اے شخص! اپنے رب کی یاد کیا کر اپنے دل میں عاجزی کے ساتھ اور خوف کے ساتھ اور زور کی آواز کی نسبت کم آواز کے ساتھ صبح اور شام اور اہل غفلت میں سے مت ہونا'),
(1160, 7, 206, 'یقیناً جو تیرے رب کے نزدیک ہیں وه اس کی عبادت سے تکبر نہیں کرتے اور اس کی پاکی بیان کرتے ہیں اور اس کو سجده کرتے ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1161, 8, 1, 'یہ لوگ آپ سے غنیمتو ں کا حکم دریافت کرتے ہیں، آپ فرما دیجئے! کہ یہ غنیمتیں اللہ کی ہیں اور رسول کی ہیں، سو تم اللہ سے ڈرو اور اپنے باہمی تعلقات کی اصلاح کرو اور اللہ تعالیٰ اور اس کے رسول کی اطاعت کرو اگر تم ایمان والے ہو'),
(1162, 8, 2, 'بس ایمان والے تو ایسے ہوتے ہیں کہ جب اللہ تعالیٰ کا ذکر آتا ہے تو ان کے قلوب ڈر جاتے ہیں اور جب اللہ کی آیتیں ان کو پڑھ کر سنائی جاتیں ہیں تو وه آیتیں ان کے ایمان کو اور زیاده کردیتی ہیں اور وه لوگ اپنے رب پر توکل کرتے ہیں'),
(1163, 8, 3, 'جو کہ نماز کی پابندی کرتے ہیں اور ہم نے ان کو جو کچھ دیا ہے وه اس میں سے خرچ کرتے ہیں'),
(1164, 8, 4, 'سچے ایمان والے یہ لوگ ہیں ان کے لیے بڑے درجے ہیں ان کے رب کے پاس اور مغفرت اور عزت کی روزی ہے'),
(1165, 8, 5, 'جیسا کہ آپ کے رب نے آپ کے گھر سے حق کے ساتھ آپ کو روانہ کیا اور مسلمانوں کی ایک جماعت اس کو گراں سمجھتی تھی'),
(1166, 8, 6, 'وه اس حق کے بارے میں، اس کے بعد کہ اس کا ﻇہور ہوگیا تھا آپ سے اس طرح جھگڑ رہے تھے کہ گویا کوئی ان کو موت کی طرف ہانکے لیے جاتا ہے اور وه دیکھ رہے ہیں'),
(1167, 8, 7, 'اور تم لوگ اس وقت کو یاد کرو! جب کہ اللہ تم سے ان دو جماعتوں میں سے ایک کا وعده کرتا تھا کہ وه تمہارے ہاتھ آجائے گی اور تم اس تمنا میں تھے کہ غیر مسلح جماعت تمہارے ہاتھ آجائے اور اللہ تعالیٰ کو یہ منظور تھا کہ اپنے احکام سے حق کا حق ہونا ﺛابت کردے اور ان کافروں کی جڑ کاٹ دے'),
(1168, 8, 8, 'تاکہ حق کا حق ہونا اور باطل کا باطل ہونا ﺛابت کردے گو یہ مجرم لوگ ناپسند ہی کریں'),
(1169, 8, 9, 'اس وقت کو یاد کرو جب کہ تم اپنے رب سے فریاد کررہے تھے، پھر اللہ تعالیٰ نے تمہاری سن لی کہ میں تم کو ایک ہزار فرشتوں سے مدد دوں گا جو لگاتار چلے آئیں گے'),
(1170, 8, 10, 'اور اللہ تعالیٰ نے یہ امداد محض اس لیے کی کہ بشارت ہو اور تاکہ تمہارے دلوں کو قرار ہو جائے اور مدد صرف اللہ ہی کی طرف سے ہے جو کہ زبردست حکمت واﻻ ہے'),
(1171, 8, 11, 'اس وقت کو یاد کرو جب کہ اللہ تم پر اونگھ طاری کر رہا تھا اپنی طرف سے چین دینے کے لیے اور تم پر آسمان سے پانی برسا رہا تھا کہ اس پانی کے ذریعہ سے تم کو پاک کر دے اور تم سے شیطانی وسوسہ کو دفع کر دے اور تمہارے دلوں کو مضبوط کر دے اور تمہارے پاؤں جما دے'),
(1172, 8, 12, 'اس وقت کو یاد کرو جب کہ آپ کا رب فرشتوں کو حکم دیتا تھا کہ میں تمہارا ساتھی ہوں سو تم ایمان والوں کی ہمت بڑھاؤ میں ابھی کفار کے قلوب میں رعب ڈالے دیتا ہوں، سو تم گردنوں پر مارو اور ان کے پور پور کو مارو'),
(1173, 8, 13, 'یہ اس بات کی سزا ہے کہ انہوں نے اللہ کی اور اس کے رسول کی مخالفت کی۔ اور جو اللہ کی اور اس کے رسول کی مخالفت کرتا ہے سو بےشک اللہ تعالیٰ سخت سزا دینے واﻻ ہے'),
(1174, 8, 14, 'سو یہ سزا چکھو اور جان رکھو کہ کافروں کے لیے جہنم کا عذاب مقرر ہی ہے'),
(1175, 8, 15, 'اے ایمان والو! جب تم کافروں سے دو بدو مقابل ہو جاؤ تو ان سے پشت مت پھیرنا'),
(1176, 8, 16, 'اور جو شخص ان سے اس موقع پر پشت پھیرے گا مگر ہاں جو لڑائی کے لیے پینترا بدلتا ہو یا جو (اپنی) جماعت کی طرف پناه لینے آتا ہو وه مستثنیٰ ہے۔ باقی اور جو ایسا کرے گا وه اللہ کے غضب میں آجائے گا اور اس کا ٹھکانہ دوزخ ہوگا وه بہت ہی بری جگہ ہے'),
(1177, 8, 17, 'سو تم نے انہیں قتل نہیں کیا لیکن اللہ تعالیٰ نے ان کو قتل کیا۔ اور آپ نے خاک کی مٹھی نہیں پھینکی بلکہ اللہ تعالیٰ نے وه پھینکی اور تاکہ مسلمانوں کو اپنی طرف سے ان کی محنت کا خوب عوض دے بلاشبہ اللہ تعالیٰ خوب سننے واﻻ خوب جاننے واﻻ ہے'),
(1178, 8, 18, '(ایک بات تو) یہ ہوئی اور (دوسری بات یہ ہے) اللہ تعالیٰ کو کافروں کی تدبیر کو کمزور کرنا تھا'),
(1179, 8, 19, 'اگر تم لوگ فیصلہ چاہتے ہو تو وه فیصلہ تمہارے سامنے آموجود ہوا اور اگر باز آجاؤ تو یہ تمہارے لیے نہایت خوب ہے اور اگر تم پھر وہی کام کرو گے تو ہم بھی پھر وہی کام کریں گے اور تمہاری جمعیت تمہارے ذرا بھی کام نہ آئے گی گو کتنی زیاده ہو اور واقعی بات یہ ہے کہ اللہ تعالیٰ ایمان والوں کے ساتھ ہے'),
(1180, 8, 20, 'اے ایمان والو! اللہ کا اور اس کے رسول کا کہنا مانو اور اس (کا کہنا ماننے) سے روگردانی مت کرو سنتے جانتے ہوئے'),
(1181, 8, 21, 'اور تم ان لوگوں کی طرح مت ہونا جو دعویٰ تو کرتے ہیں کہ ہم نے سن لیا حاﻻنکہ وه سنتے (سناتے کچھ) نہیں'),
(1182, 8, 22, 'بےشک بدترین خلائق اللہ تعالیٰ کے نزدیک وه لوگ ہیں جو بہرے ہیں گونگے ہیں جو کہ (ذرا) نہیں سمجھتے'),
(1183, 8, 23, 'اور اگر اللہ تعالیٰ ان میں کوئی خوبی دیکھتا تو ان کو سننے کی توفیق دے دیتا اور اگر ان کو اب سنا دے تو ضرور روگردانی کریں گے بے رخی کرتے ہوئے'),
(1184, 8, 24, 'اے ایمان والو! تم اللہ اور رسول کے کہنے کو بجا لاؤ، جب کہ رسول تم کو تمہاری زندگی بخش چیز کی طرف بلاتے ہوں اور جان رکھو کہ اللہ تعالیٰ آدمی کے اور اس کے قلب کے درمیان آڑ بن جایا کرتا ہے اور بلاشبہ تم سب کو اللہ ہی کے پاس جمع ہونا ہے'),
(1185, 8, 25, 'اور تم ایسے وبال سے بچو! کہ جو خاص کر صرف ان ہی لوگوں پر واقع نہ ہو گا جو تم میں سے ان گناہوں کے مرتکب ہوئے ہیں اور یہ جان رکھو کہ اللہ سخت سزا دینے واﻻ ہے'),
(1186, 8, 26, 'اور اس حالت کو یاد کرو! جب کہ تم زمین میں قلیل تھے، کمزور شمار کئے جاتے تھے۔ اس اندیشہ میں رہتے تھے کہ تم کو لوگ نوچ کھسوٹ نہ لیں، سو اللہ نے تم کو رہنے کی جگہ دی اور تم کو اپنی نصرت سے قوت دی اور تم کو نفیس نفیس چیزیں عطا فرمائیں تاکہ تم شکر کرو'),
(1187, 8, 27, 'اے ایمان والو! تم اللہ اور رسول (کے حقوق) میں جانتے ہوئے خیانت مت کرو اور اپنی قابل حفاﻇت چیزوں میں خیانت مت کرو'),
(1188, 8, 28, 'اور تم اس بات کو جان رکھو کہ تمہارے اموال اور تمہاری اوﻻد ایک امتحان کی چیز ہے۔ اور اس بات کو بھی جان رکھو کہ اللہ تعالیٰ کے پاس بڑا بھاری اجر ہے'),
(1189, 8, 29, 'اے ایمان والو! اگر تم اللہ سے ڈرتے رہو گے تو اللہ تعالیٰ تم کو ایک فیصلہ کی چیز دے گا اور تم سے تمہارے گناه دور کر دے گا اور تم کو بخش دے گا اور اللہ تعالیٰ بڑے فضل واﻻ ہے'),
(1190, 8, 30, 'اور اس واقعہ کا بھی ذکر کیجئے! جب کہ کافر لوگ آپ کی نسبت تدبیر سوچ رہے تھے کہ آپ کو قید کر لیں، یا آپ کو قتل کر ڈالیں یا آپ کو خارج وطن کر دیں اور وه تو اپنی تدبیریں کر رہے تھے اور اللہ اپنی تدبیر کر رہا تھا اور سب سے زیاده مستحکم تدبیر واﻻ اللہ ہے'),
(1191, 8, 31, 'اور جب ان کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو کہتے ہیں کہ ہم نے سن لیا، اگر ہم چاہیں تو اس کے برابر ہم بھی کہہ دیں، یہ تو کچھ بھی نہیں صرف بے سند باتیں ہیں جو پہلوں سے منقول چلی آرہی ہیں'),
(1192, 8, 32, 'اور جب کہ ان لوگوں نے کہا کہ اے اللہ! اگر یہ قرآن آپ کی طرف سے واقعی ہے تو ہم پر آسمان سے پتھر برسا یا ہم پر کوئی دردناک عذاب واقع کر دے'),
(1193, 8, 33, 'اور اللہ تعالیٰ ایسا نہ کرے گا کہ ان میں آپ کے ہوتے ہوئے ان کو عذاب دے اور اللہ ان کو عذاب نہ دے گا اس حالت میں کہ وه استغفار بھی کرتے ہوں'),
(1194, 8, 34, 'اور ان میں کیا بات ہے کہ ان کو اللہ تعالیٰ سزا نہ دے حاﻻنکہ وه لوگ مسجد حرام سے روکتے ہیں، جب کہ وه لوگ اس مسجد کے متولی نہیں۔ اس کے متولی تو سوا متقیوں کے اور اشخاص نہیں، لیکن ان میں اکثر لوگ علم نہیں رکھتے'),
(1195, 8, 35, 'اور ان کی نماز کعبہ کے پاس صرف یہ تھی سیٹیاں بجانا اور تالیاں بجانا۔ سو اپنے کفر کے سبب اس عذاب کا مزه چکھو'),
(1196, 8, 36, 'بلاشک یہ کافر لوگ اپنے مالوں کو اس لئے خرچ کر رہے ہیں کہ اللہ کی راه سے روکیں سو یہ لوگ تو اپنے مالوں کو خرچ کرتے ہی رہیں گے، پھر وه مال ان کے حق میں باعﺚ حسرت ہو جائیں گے۔ پھر مغلوب ہو جائیں گے اور کافر لوگوں کو دوزخ کی طرف جمع کیا جائے گا'),
(1197, 8, 37, 'تاکہ اللہ تعالیٰ ناپاک کو پاک سے الگ کر دے اور ناپاکوں کو ایک دوسرے سے ملا دے، پس ان سب کو اکھٹا ڈھیر کر دے پھر ان سب کو جہنم میں ڈال دے۔ ایسے لوگ پورے خسارے میں ہیں'),
(1198, 8, 38, 'آپ ان کافروں سے کہہ دیجئے! کہ اگر یہ لوگ باز آجائیں تو ان کے سارے گناہ جو پہلے ہو چکے ہیں سب معاف کر دیئے جائیں گے اور اگر اپنی وہی عادت رکھیں گے تو (کفار) سابقین کے حق میں قانون نافذ ہو چکا ہے'),
(1199, 8, 39, 'اور تم ان سے اس حد تک لڑو کہ ان میں فساد عقیده نہ رہے۔ اور دین اللہ ہی کا ہو جائے پھر اگر یہ باز آجائیں تو اللہ تعالیٰ ان اعمال کو خوب دیکھتا ہے'),
(1200, 8, 40, 'اور اگر روگردانی کریں تو یقین رکھیں کہ اللہ تعالیٰ تمہارا کار ساز ہے وه بہت اچھا کار ساز ہے اور بہت اچھا مدد گار ہے'),
(1201, 8, 41, 'جان لو کہ تم جس قسم کی جو کچھ غنیمت حاصل کرو اس میں سے پانچواں حصہ تو اللہ کا ہے اور رسول کا اور قرابت داروں کا اور یتیموں اور مسکینوں کا اور مسافروں کا، اگر تم اللہ پر ایمان ﻻئے ہو اور اس چیز پر جو ہم نے اپنے بندے پر اس دن اتارا ہے، جو دن حق وباطل کی جدائی کا تھا جس دن دو فوجیں بھڑ گئی تھیں۔ اللہ ہر چیز پر قادر ہے'),
(1202, 8, 42, 'جب کہ تم پاس والے کنارے پر تھے اور وه دور والے کنارے پر تھے اور قافلہ تم سے نیچے تھا۔ اگر تم آپس میں وعدے کرتے تو یقیناً تم وقت معین پر پہنچنے میں مختلف ہو جاتے۔ لیکن اللہ کو تو ایک کام کر ہی ڈالنا تھا جو مقرر ہو چکا تھا تاکہ جو ہلاک ہو، دلیل پر (یعنی یقین جان کر) ہلاک ہو اور جو زنده رہے، وه بھی دلیل پر (حق پہچان کر) زنده رہے۔ بیشک اللہ بہت سننے واﻻ خوب جاننے واﻻ ہے'),
(1203, 8, 43, 'جب کہ اللہ تعالیٰ نے تجھے تیرے خواب میں ان کی تعداد کم دکھائی، اگر ان کی زیادتی دکھاتا تو تم بزدل ہو جاتے اور اس کام کے بارے میں آپس میں اختلاف کرتے لیکن اللہ تعالیٰ نے بچا لیا، وه دلوں کے بھیدوں سے خوب آگاه ہے'),
(1204, 8, 44, 'جبکہ اس نے بوقت ملاقات انہیں تمہاری نگاہوں میں بہت کم دکھائے اور تمہیں ان کی نگاہوں میں کم دکھائے تاکہ اللہ تعالیٰ اس کام کو انجام تک پہنچا دے جو کرنا ہی تھا اور سب کام اللہ ہی کی طرف پھیرے جاتے ہیں'),
(1205, 8, 45, 'اے ایمان والو! جب تم کسی مخالف فوج سے بھڑ جاؤ تو ﺛابت قدم رہو اور بکثرت اللہ کو یاد کرو تاکہ تمہیں کامیابی حاصل ہو'),
(1206, 8, 46, 'اور اللہ کی اور اس کے رسول کی فرماں برداری کرتے رہو، آپس میں اختلاف نہ کرو ورنہ بزدل ہو جاؤ گے اور تمہاری ہوا اکھڑ جائے گی اور صبر و سہارا رکھو، یقیناً اللہ تعالیٰ صبر کرنے والوں کے ساتھ ہے'),
(1207, 8, 47, 'ان لوگوں جیسے نہ بنو جو اتراتے ہوئے اور لوگوں میں خود نمائی کرتے ہوئے اپنے گھروں سے چلے اور اللہ کی راه سے روکتے تھے، جو کچھ وه کر رہے ہیں اللہ اسے گھیر لینے واﻻ ہے'),
(1208, 8, 48, 'جبکہ ان کے اعمال کو شیطان انہیں زینت دار دکھا رہا تھا اور کہہ رہا تھا کہ لوگوں میں سے کوئی بھی آج تم پر غالب نہیں آسکتا، میں خود بھی تمہارا حمایتی ہوں لیکن جب دونوں جماعتیں نمودار ہوئیں تو اپنی ایڑیوں کے بل پیچھے ہٹ گیا اور کہنے لگا میں تم سے بری ہوں۔ میں وه دیکھ رہا ہوں جو تم نہیں دیکھ رہے۔ میں اللہ سے ڈرتا ہوں، اور اللہ تعالیٰ سخت عذاب واﻻ ہے'),
(1209, 8, 49, 'جبکہ منافق کہہ رہے تھے اور وه بھی جن کے دلوں میں روگ تھا کہ انہیں تو ان کے دین نے دھوکے میں ڈال دیا ہے جو بھی اللہ پر بھروسہ کرے اللہ تعالیٰ بلا شک وشبہ غلبے واﻻ اور حکمت واﻻ ہے'),
(1210, 8, 50, 'کاش کہ تو دیکھتا جب کہ فرشتے کافروں کی روح قبض کرتے ہیں ان کے منھ پر اور سرینوں پر مار مارتے ہیں (اور کہتے ہیں) تم جلنے کا عذاب چکھو'),
(1211, 8, 51, 'یہ بسبب ان کاموں کے جو تمہارے ہاتھوں نے پہلے ہی بھیج رکھا ہے بیشک اللہ اپنے بندوں پر ﻇلم کرنے واﻻ نہیں'),
(1212, 8, 52, 'مثل فرعونیوں کے حال کے اور ان سے اگلوں کے، کہ انہوں نے اللہ کی آیتوں سے کفر کیا پس اللہ نے ان کے گناہوں کے باعﺚ انہیں پکڑ لیا۔ اللہ تعالیٰ یقیناً قوت واﻻ اور سخت عذاب واﻻ ہے'),
(1213, 8, 53, 'یہ اس لئے کہ اللہ تعالیٰ ایسا نہیں کہ کسی قوم پر کوئی نعمت انعام فرما کر پھر بدل دے جب تک کہ وه خود اپنی اس حالت کو نہ بدل دیں جو کہ ان کی اپنی تھی اور یہ کہ اللہ سننے واﻻ جاننے واﻻ ہے'),
(1214, 8, 54, 'مثل حالت فرعونیوں کے اور ان سے پہلے کے لوگوں کے کہ انہوں نے اپنے رب کی باتیں جھٹلائیں۔ پس ان کے گناہوں کے باعﺚ ہم نے انہیں برباد کیا اور فرعونیوں کو ڈبو دیا۔ یہ سارے ﻇالم تھے'),
(1215, 8, 55, 'تمام جانداروں سے بدتر، اللہ کے نزدیک وه ہیں جو کفر کریں، پھر وه ایمان نہ ﻻئیں'),
(1216, 8, 56, 'جن سے آپ نے عہد وپیمان کر لیا پھر بھی وه اپنے عہد وپیمان کو ہر مرتبہ توڑ دیتے ہیں اور بالکل پرہیز نہیں کرتے'),
(1217, 8, 57, 'پس جب کبھی تو لڑائی میں ان پر غالب آجائے انہیں ایسی مار مار کہ ان کے پچھلے بھی بھاگ کھڑے ہوں، ہو سکتا ہے کہ وه عبرت حاصل کریں'),
(1218, 8, 58, 'اور اگر تجھے کسی قوم کی خیانت کا ڈر ہو تو برابری کی حالت میں ان کا عہدنامہ توڑ دے، اللہ تعالیٰ خیانت کرنے والوں کو پسند نہیں فرماتا'),
(1219, 8, 59, 'کافر یہ خیال نہ کریں کہ وه بھاگ نکلے۔ یقیناً وه عاجز نہیں کر سکتے'),
(1220, 8, 60, 'تم ان کے مقابلے کے لئے اپنی طاقت بھر قوت کی تیاری کرو اور گھوڑوں کے تیار رکھنے کی کہ اس سے تم اللہ کے دشمنوں کو خوف زده رکھ سکو اور ان کے سوا اوروں کو بھی، جنہیں تم نہیں جانتے، اللہ انہیں خوب جان رہا ہے جو کچھ بھی اللہ کی راه میں صرف کرو گے وه تمہیں پورا پورا دیا جائے گا اور تمہارا حق نہ مارا جائے گا'),
(1221, 8, 61, 'اگر وه صلح کی طرف جھکیں تو تو بھی صلح کی طرف جھک جا اور اللہ پر بھروسہ رکھ، یقیناً وه بہت سننے جاننے واﻻ ہے'),
(1222, 8, 62, 'اگر وه تجھ سے دغا بازی کرنا چاہیں گے تو اللہ تجھے کافی ہے، اسی نے اپنی مدد سے اور مومنوں سے تیری تائید کی ہے'),
(1223, 8, 63, 'ان کے دلوں میں باہمی الفت بھی اسی نے ڈالی ہے۔ زمین میں جو کچھ ہے تو اگر سارا کا سارا بھی خرچ کر ڈالتا ہے تو بھی ان کے دل آپس میں نہ ملا سکتا۔ یہ تو اللہ ہی نے ان میں الفت ڈال دی ہے وه غالب حکمتوں واﻻ ہے'),
(1224, 8, 64, 'اے نبی! تجھے اللہ کافی ہے اور ان مومنوں کو جو تیری پیروی کر رہے ہیں'),
(1225, 8, 65, 'اے نبی! ایمان والوں کو جہاد کا شوق دلاؤ اگر تم میں بیس بھی صبر کرنے والے ہوں گے، تو دو سو پر غالب رہیں گے۔ اور اگر تم میں ایک سو ہوں گے تو ایک ہزار کافروں پر غالب رہیں گے اس واسطے کہ وه بے سمجھ لوگ ہیں'),
(1226, 8, 66, 'اچھا اب اللہ تمہارا بوجھ ہلکا کرتاہے، وه خوب جانتا ہے کہ تم میں ناتوانی ہے، پس اگر تم میں سے ایک سو صبر کرنے والے ہوں گے تو وه دو سو پر غالب رہیں گے اور اگر تم میں سے ایک ہزار ہوں گے تو وه اللہ کے حکم سے دو ہزار پرغالب رہیں گے، اللہ صبر کرنے والوں کے ساتھ ہے'),
(1227, 8, 67, 'نبی کے ہاتھ میں قیدی نہیں چاہییں جب تک کہ ملک میں اچھی خونریزی کی جنگ نہ ہو جائے۔ تم تو دنیا کے مال چاہتے ہو اور اللہ کا اراده آخرت کا ہے اور اللہ زور آور باحکمت ہے'),
(1228, 8, 68, 'اگر پہلے ہی سے اللہ کی طرف سے بات لکھی ہوئی نہ ہوتی تو جو کچھ تم نے لیا ہے اس کے بارے میں تمہیں کوئی بڑی سزا ہوتی'),
(1229, 8, 69, 'پس جو کچھ حلال اور پاکیزه غنیمت تم نے حاصل کی ہے، خوب کھاؤ پیو اور اللہ سے ڈرتے رہو، یقیناً اللہ غفور ورحیم ہے'),
(1230, 8, 70, 'اے نبی! اپنے ہاتھ تلے کے قیدیوں سے کہہ دو کہ اگر اللہ تعالیٰ تمہارے دلوں میں نیک نیتی دیکھے گا تو جو کچھ تم سے لیا گیا ہے اس سے بہتر تمہیں دے گا اور پھر گناه بھی معاف فرمائے گا اور اللہ بخشنے واﻻ مہربان ہے ہی'),
(1231, 8, 71, 'اور اگر وه تجھ سے خیانت کا خیال کریں گے تو یہ تو اس سے پہلے خود اللہ کی خیانت کر چکے ہیں آخر اس نے انہیں گرفتار کرا دیا، اور اللہ علم وحکمت واﻻ ہے'),
(1232, 8, 72, 'جو لوگ ایمان ﻻئے اور ہجرت کی اور اپنے مالوں اور جانوں سے اللہ کی راه میں جہاد کیا اور جن لوگوں نے ان کو پناه دی اور مدد کی، یہ سب آپس میں ایک دوسرے کے رفیق ہیں، اور جو ایمان تو ﻻئے ہیں لیکن ہجرت نہیں کی تمہارے لئے ان کی کچھ بھی رفاقت نہیں جب تک کہ وه ہجرت نہ کریں۔ ہاں اگر وه تم سے دین کے بارے میں مدد طلب کریں تو تم پر مدد کرنا ضروری ہے، سوائے ان لوگوں کے کہ تم میں اور ان میں عہد وپیمان ہے، تم جو کچھ کر رہے ہو اللہ خوب دیکھتا ہے'),
(1233, 8, 73, 'کافر آپس میں ایک دوسرے کے رفیق ہیں، اگر تم نے ایسا نہ کیا تو ملک میں فتنہ ہوگا اور زبردست فساد ہو جائے گا'),
(1234, 8, 74, 'جو لوگ ایمان ﻻئے اور ہجرت کی اور اللہ کی راه میں جہاد کیا اور جنہوں نے پناه دی اور مدد پہنچائی، یہی لوگ سچے مومن ہیں، ان کے لئے بخشش ہے اور عزت کی روزی'),
(1235, 8, 75, 'اور جو لوگ اس کے بعد ایمان ﻻئے اور ہجرت کی اور تمہارے ساتھ ہو کر جہاد کیا۔ پس یہ لوگ بھی تم میں سے ہی ہیں اور رشتے ناتے والے ان میں سے بعض بعض سے زیاده نزدیک ہیں اللہ کے حکم میں، بیشک اللہ تعالیٰ ہر چیز کا جاننے واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1236, 9, 1, 'اللہ اوراس کے رسول کی جانب سے بیزاری کا اعلان ہے۔ ان مشرکوں کے بارے میں جن سے تم نے عہد وپیمان کیا تھا'),
(1237, 9, 2, 'پس (اے مشرکو!) تم ملک میں چار مہینے تک تو چل پھر لو، جان لو کہ تم اللہ کو عاجز کرنے والے نہیں ہو، اور یہ (بھی یاد رہے) کہ اللہ کافروں کو رسوا کرنے واﻻ ہے'),
(1238, 9, 3, 'اللہ اور اس کے رسول کی طرف سے لوگوں کو بڑے حج کے دن صاف اطلاع ہے کہ اللہ مشرکوں سے بیزار ہے، اور اس کا رسول بھی، اگر اب بھی تم توبہ کر لو تو تمہارے حق میں بہتر ہے، اور اگر تم روگردانی کرو تو جان لو کہ تم اللہ کو ہرا نہیں سکتے۔ اور کافروں کو دکھ اور مار کی خبر پہنچا دیجئے'),
(1239, 9, 4, 'بجز ان مشرکوں کے جن سے تمہارا معاہده ہو چکا ہے اور انہوں نے تمہیں ذرا سا بھی نقصان نہیں پہنچایا نہ کسی کی تمہارے خلاف مدد کی ہے تو تم بھی ان کے معاہدے کی مدت ان کے ساتھ پوری کرو، اللہ تعالیٰ پرہیزگاروں کو دوست رکھتا ہے'),
(1240, 9, 5, 'پھر حرمت والے مہینوں کے گزرتے ہی مشرکوں کو جہاں پاؤ قتل کرو انہیں گرفتار کرو، ان کا محاصره کرلو اور ان کی تاک میں ہر گھاٹی میں جا بیٹھو، ہاں اگر وه توبہ کرلیں اور نماز کے پابند ہو جائیں اور زکوٰة ادا کرنے لگیں توتم ان کی راہیں چھوڑ دو۔ یقیناً اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(1241, 9, 6, 'اگر مشرکوں میں سے کوئی تجھ سے پناه طلب کرے تو تو اسے پناه دے دے یہاں تک کہ وه کلام اللہ سن لے پھر اسے اپنی جائے امن تک پہنچا دے۔ یہ اس لئے کہ یہ لوگ بے علم ہیں'),
(1242, 9, 7, 'مشرکوں کے لئے عہد اللہ اور اس کے رسول کے نزدیک کیسے ره سکتا ہے سوائے ان کے جن سے تم نے عہد وپیمان مسجد حرام کے پاس کیا ہے، جب تک وه لوگ تم سے معاہده نبھائیں تم بھی ان سے وفاداری کرو، اللہ تعالیٰ متقیوں سے محبت رکھتا ہے'),
(1243, 9, 8, 'ان کے وعدوں کا کیا اعتبار ان کا اگر تم پر غلبہ ہو جائے تو نہ یہ قرابت داری کا خیال کریں نہ عہدوپیمان کا، اپنی زبانوں سے تو تمہیں پرچا رہے ہیں لیکن ان کے دل نہیں مانتے ان میں سے اکثر تو فاسق ہیں'),
(1244, 9, 9, 'انہوں نے اللہ کی آیتوں کو بہت کم قیمت پر بیچ دیا اور اس کی راه سے روکا۔ بہت برا ہے جو یہ کر رہے ہیں'),
(1245, 9, 10, 'یہ تو کسی مسلمان کے حق میں کسی رشتہداری کا یا عہد کا مطلق لحاظ نہیں کرتے، یہ ہیں ہی حد سے گزرنے والے'),
(1246, 9, 11, 'اب بھی اگر یہ توبہ کر لیں اور نماز کے پابند ہو جائیں اور زکوٰة دیتے رہیں تو تمہارے دینی بھائی ہیں۔ ہم تو جاننے والوں کے لئے اپنی آیتیں کھول کھول کر بیان کر رہے ہیں'),
(1247, 9, 12, 'اگر یہ لوگ عہدوپیمان کے بعد بھی اپنی قسموں کو توڑ دیں اور تمہارے دین میں طعنہ زنی کریں تو تم بھی ان سرداران کفر سے بھڑ جاؤ۔ ان کی قسمیں کوئی چیز نہیں، ممکن ہے کہ اس طرح وه بھی باز آجائیں'),
(1248, 9, 13, 'تم ان لوگوں کی سرکوبی کے لئے کیوں تیار نہیں ہوتے جنہوں نے اپنی قسموں کو توڑ دیا اور پیغمبر کو جلا وطن کرنے کی فکر میں ہیں اور خود ہی اول بار انہوں نے تم سے چھیڑ کی ہے، کیا تم ان سے ڈرتے ہو؟ اللہ ہی زیاده مستحق ہے کہ تم اس کا ڈر رکھو بشرطیکہ تم ایمان والے ہو'),
(1249, 9, 14, 'ان سے تم جنگ کرو اللہ تعالیٰ انہیں تمہارے ہاتھوں عذاب دے گا، انہیں ذلیل ورسوا کرے گا، تمہیں ان پر مدد دے گا اور مسلمانوں کے کلیجے ٹھنڈے کرے گا'),
(1250, 9, 15, 'اور ان کے دل کا غم وغصہ دور کرے گا، اور وه جس کی طرف چاہتا ہے رحمت سے توجہ فرماتا ہے۔ اللہ جانتا بوجھتا حکمت واﻻ ہے'),
(1251, 9, 16, 'کیا تم یہ سمجھے بیٹھے ہو کہ تم چھوڑ دیئے جاؤ گے حاﻻنکہ اب تک اللہ نے تم میں سے انہیں ممتاز نہیں کیا جو مجاہد ہیں اور جنہوں نے اللہ کے اور اس کے رسول کے اور مومنوں کے سوا کسی کو دلی دوست نہیں بنایا۔ اللہ خوب خبردار ہے جو تم کر رہے ہو'),
(1252, 9, 17, 'ﻻئق نہیں کہ مشرک اللہ تعالیٰ کی مسجدوں کو آباد کریں۔ درآں حالیکہ وه خود اپنے کفر کے آپ ہی گواه ہیں، ان کے اعمال غارت واکارت ہیں، اور وه دائمی طور پر جہنمی ہیں'),
(1253, 9, 18, 'اللہ کی مسجدوں کی رونق وآبادی تو ان کے حصے میں ہے جو اللہ پر اور قیامت کے دن پر ایمان رکھتے ہوں، نمازوں کے پابند ہوں، زکوٰة دیتے ہوں، اللہ کے سوا کسی سے نہ ڈرتے ہوں، توقع ہے کہ یہی لوگ یقیناً ہدایت یافتہ ہیں'),
(1254, 9, 19, 'کیا تم نے حاجیوں کو پانی پلا دینا اور مسجد حرام کی خدمت کرنا اس کے برابر کر دیا ہے جو اللہ پر اور آخرت کے دن پر ایمان ﻻئے اور اللہ کی راه میں جہاد کیا، یہ اللہ کے نزدیک برابر کے نہیں اور اللہ تعالیٰ ﻇالموں کو ہدایت نہیں دیتا'),
(1255, 9, 20, 'جو لوگ ایمان ﻻئے، ہجرت کی، اللہ کی راه میں اپنے مال اور اپنی جان سے جہاد کیا وه اللہ کے ہاں بہت بڑے مرتبہ والے ہیں، اور یہی لوگ مراد پانے والے ہیں'),
(1256, 9, 21, 'انہیں ان کا رب خوشخبری دیتا ہے اپنی رحمت کی اور رضامندی کی اور جنتوں کی، ان کے لئے وہاں دوامی نعمت ہے'),
(1257, 9, 22, 'وہاں یہ ہمیشہ رہنے والے ہیں اللہ کے پاس یقیناً بہت بڑے ﺛواب ہیں'),
(1258, 9, 23, 'اے ایمان والو! اپنے باپوں کو اور اپنے بھائیوں کو دوست نہ بناؤ اگر وه کفر کو ایمان سے زیاده عزیز رکھیں۔ تم میں سے جو بھی ان سے محبت رکھے گا وه پورا گنہگار ﻇالم ہے'),
(1259, 9, 24, 'آپ کہہ دیجئے کہ اگر تمہارے باپ اور تمہارے لڑکےاور تمہارے بھائی اور تمہاری بیویاں اور تمہارے کنبے قبیلے اور تمہارے کمائے ہوئے مال اور وه تجارت جس کی کمی سے تم ڈرتے ہو اور وه حویلیاں جنہیں تم پسند کرتے ہو اگر یہ تمہیں اللہ سے اور اس کے رسول سے اور اس کی راه میں جہاد سے بھی زیاده عزیز ہیں، تو تم انتظار کرو کہ اللہ تعالیٰ اپنا عذاب لے آئے۔ اللہ تعالیٰ فاسقوں کو ہدایت نہیں دیتا'),
(1260, 9, 25, 'یقیناً اللہ تعالیٰ نے بہت سے میدانوں میں تمہیں فتح دی ہے اور حنین کی لڑائی والے دن بھی جب کہ تمہیں اپنی کثرت پر ناز ہوگیا تھا، لیکن اس نے تمہیں کوئی فائده نہ دیا بلکہ زمین باوجود اپنی کشادگی کے تم پر تنگ ہوگئی پھر تم پیٹھ پھیر کر مڑ گئے'),
(1261, 9, 26, 'پھر اللہ نے اپنی طرف کی تسکین اپنے نبی پر اور مومنوں پر اتاری اور اپنے وه لشکر بھیجے جنہیں تم دیکھ نہیں رہے تھے اور کافروں کو پوری سزا دی۔ ان کفار کا یہی بدلہ تھا'),
(1262, 9, 27, 'پھر اس کے بعد بھی جس پر چاہے اللہ تعالیٰ اپنی رحمت کی توجہ فرمائے گا اللہ ہی بخشش ومہربانی کرنے واﻻ ہے'),
(1263, 9, 28, 'اے ایمان والو! بے شک مشرک بالکل ہی ناپاک ہیں وه اس سال کے بعد مسجد حرام کے پاس بھی نہ پھٹکنے پائیں اگر تمہیں مفلسی کا خوف ہے تو اللہ تمہیں دولت مند کر دے گا اپنے فضل سے اگر چاہے اللہ علم وحکمت واﻻ ہے'),
(1264, 9, 29, 'ان لوگوں سے لڑو، جو اللہ پر اور قیامت کے دن پر ایمان نہیں ﻻتے جو اللہ اور اس کے رسول کی حرام کرده شے کو حرام نہیں جانتے، نہ دین حق کو قبول کرتے ہیں ان لوگوں میں جنہیں کتاب دی گئی ہے، یہاں تک کہ وه ذلیل وخوار ہو کر اپنے ہاتھ سے جزیہ ادا کریں'),
(1265, 9, 30, 'یہود کہتے ہیں عزیر اللہ کا بیٹا ہے اور نصرانی کہتے ہیں مسیح اللہ کا بیٹا ہے یہ قول صرف ان کے منھ کی بات ہے۔ اگلے منکروں کی بات کی یہ بھی نقل کرنے لگے اللہ انہیں غارت کرے وه کیسے پلٹائے جاتے ہیں'),
(1266, 9, 31, 'ان لوگوں نے اللہ کو چھوڑ کر اپنے عالموں اور درویشوں کو رب بنایا ہے اور مریم کے بیٹے مسیح کو حاﻻنکہ انہیں صرف ایک اکیلے اللہ ہی کی عبادت کا حکم دیا گیا تھا جس کے سوا کوئی معبود نہیں وه پاک ہے ان کے شریک مقرر کرنے سے'),
(1267, 9, 32, 'وه چاہتے ہیں کہ اللہ کے نور کو اپنے منھ سے بجھا دیں اور اللہ تعالیٰ انکاری ہے مگر اسی بات کا کہ اپنا نور پورا کرے گو کافر نا خوش رہیں'),
(1268, 9, 33, 'اسی نے اپنے رسول کو ہدایت اور سچے دین کے ساتھ بھیجا ہے کہ اسے اور تمام مذہبوں پر غالب کر دے اگر چہ مشرک برا مانیں'),
(1269, 9, 34, 'اے ایمان والو! اکثر علما اور عابد، لوگوں کا مال ناحق کھا جاتے ہیں اور اللہ کی راه سے روک دیتے ہیں اور جو لوگ سونے چاندی کا خزانہ رکھتے ہیں اور اللہ کی راه میں خرچ نہیں کرتے، انہیں دردناک عذاب کی خبر پہنچا دیجئے'),
(1270, 9, 35, 'جس دن اس خزانے کو آتش دوزخ میں تپایا جائے گا پھر اس سے ان کی پیشانیاں اور پہلو اور پیٹھیں داغی جائیں گی (ان سے کہا جائے گا) یہ ہے جسے تم نے اپنے لئے خزانہ بنا کر رکھا تھا۔ پس اپنے خزانوں کا مزه چکھو'),
(1271, 9, 36, 'مہینوں کی گنتی اللہ کے نزدیک کتاب اللہ میں باره کی ہے، اسی دن سے جب سے آسمان وزمین کو اس نے پیدا کیا ہے اس میں سے چار حرمت وادب کے ہیں۔ یہی درست دین ہے، تم ان مہینوں میں اپنی جانوں پر ﻇلم نہ کرو اور تم تمام مشرکوں سے جہاد کرو جیسے کہ وه تم سب سے لڑتے ہیں اور جان رکھو کہ اللہ تعالی متقیوں کے ساتھ ہے'),
(1272, 9, 37, 'مہینوں کا آگے پیچھے کر دینا کفر کی زیادتی ہے اس سے وه لوگ گمراہی میں ڈالے جاتے ہیں جو کافر ہیں۔ ایک سال تو اسے حلال کر لیتے ہیں اور ایک سال اسی کو حرمت واﻻ کر لیتے ہیں، کہ اللہ نے جو حرمت رکھی ہے اس کے شمار میں تو موافقت کر لیں پھر اسے حلال بنا لیں جسے اللہ نے حرام کیا ہے انہیں ان کے برے کام بھلے دکھادیئے گئے ہیں اور قوم کفار کی اللہ رہنمائی نہیں فرماتا'),
(1273, 9, 38, 'اے ایمان والو! تمہیں کیا ہو گیا ہے کہ جب تم سے کہا جاتا ہے کہ چلو اللہ کے راستے میں کوچ کرو تو تم زمین سے لگے جاتے ہو۔ کیا تم آخرت کے عوض دنیا کی زندگانی پر ہی ریجھ گئے ہو۔ سنو! دنیا کی زندگی تو آخرت کے مقابلے میں کچھ یونہی سی ہے'),
(1274, 9, 39, 'اگر تم نے کوچ نہ کیا تو تمہیں اللہ تعالیٰ دردناک سزا دے گا اور تمہارے سوا اور لوگوں کو بدل ﻻئے گا، تم اللہ تعالی کو کوئی نقصان نہیں پہنچا سکتے اور اللہ ہر چیز پر قادر ہے'),
(1275, 9, 40, 'اگر تم ان (نبی صلی اللہ علیہ وسلم) کی مدد نہ کرو تو اللہ ہی نے ان کی مدد کی اس وقت جبکہ انہیں کافروں نے (دیس سے) نکال دیا تھا، دو میں سے دوسرا جبکہ وه دونوں غار میں تھے جب یہ اپنے ساتھی سے کہہ رہے تھے کہ غم نہ کر اللہ ہمارے ساتھ ہے، پس جناب باری نے اپنی طرف سے تسکین اس پر نازل فرما کر ان لشکروں سے اس کی مدد کی جنہیں تم نے دیکھا ہی نہیں، اس نے کافروں کی بات پست کر دی اور بلند وعزیز تو اللہ کا کلمہ ہی ہے، اللہ غالب ہے حکمت واﻻ ہے'),
(1276, 9, 41, 'نکل کھڑے ہو جاؤ ہلکے پھلکے ہو تو بھی اور بھاری بھر کم ہو تو بھی، اور راه رب میں اپنی مال وجان سے جہاد کرو، یہی تمہارے لئے بہتر ہے اگر تم میں علم ہو'),
(1277, 9, 42, 'اگر جلد وصول ہونے واﻻ مال واسباب ہوتا اور ہلکا سفر ہوتا تو یہ ضرور آپ کے پیچھے ہو لیتے لیکن ان پر تو دوری اور دراز کی مشکل پڑ گئی۔ اب تو یہ اللہ کی قسمیں کھائیں گے کہ اگر ہم میں قوت وطاقت ہوتی تو ہم یقیناً آپ کے ساتھ نکلتے، یہ اپنی جانوں کو خود ہی ہلاکت میں ڈال رہے ہیں ان کے جھوٹا ہونے کا سچا علم اللہ کو ہے'),
(1278, 9, 43, 'اللہ تجھے معاف فرما دے، تو نے انہیں کیوں اجازت دے دی؟ بغیر اس کے کہ تیرے سامنے سچے لوگ کھل جائیں اور تو جھوٹے لوگوں کو بھی جان لے'),
(1279, 9, 44, 'اللہ پر اور قیامت کے دن پر ایمان ویقین رکھنے والے تو مالی اور جانی جہاد سے رک رہنے کی کبھی بھی تجھ سے اجازت طلب نہیں کریں گے، اور اللہ تعالیٰ پرہیزگاروں کو خوب جانتا ہے'),
(1280, 9, 45, 'یہ اجازت تو تجھ سے وہی طلب کرتے ہیں جنہیں نہ اللہ پر ایمان ہے نہ آخرت کے دن کا یقین ہے جن کے دل شک میں پڑے ہوئے ہیں اور وه اپنے شک میں ہی سرگرداں ہیں'),
(1281, 9, 46, 'اگر ان کا اراده جہاد کے لئے نکلنے کا ہوتا تو وه اس سفر کے لئے سامان کی تیاری کر رکھتے لیکن اللہ کو ان کا اٹھنا پسند ہی نہ تھا اس لئے انہیں حرکت سے ہی روک دیا اور کہہ دیا گیا کہ تم بیٹھنے والوں کے ساتھ بیٹھے ہی رہو'),
(1282, 9, 47, 'اگر یہ تم میں مل کر نکلتے بھی تو تمہارے لئے سوائے فساد کے اور کوئی چیز نہ بڑھاتے بلکہ تمہارے درمیان خوب گھوڑے دوڑا دیتے اور تم میں فتنے ڈالنے کی تلاش میں رہتے ان کے ماننے والے خود تم میں موجود ہیں، اور اللہ ان ﻇالموں کو خوب جانتا ہے'),
(1283, 9, 48, 'یہ تو اس سے پہلے بھی فتنے کی تلاش کرتے رہے ہیں اور تیرے لئے کاموں کو الٹ پلٹ کرتے رہے ہیں، یہاں تک کہ حق آپہنچا اور اللہ کا حکم غالب آگیا باوجودیکہ وه ناخوشی میں ہی رہے'),
(1284, 9, 49, 'ان میں سے کوئی تو کہتا ہے مجھے اجازت دیجئے مجھے فتنے میں نہ ڈالیئے، آگاه رہو وه تو فتنے میں پڑ چکے ہیں اور یقیناً دوزخ کافروں کو گھیر لینے والی ہے'),
(1285, 9, 50, 'آپ کو اگر کوئی بھلائی مل جائے تو انہیں برا لگتا ہے اور کوئی برائی پہنچ جائے تو یہ کہتے ہیں ہم نے تو اپنا معاملہ پہلے سے ہی درست کر لیا تھا، پھر تو بڑے ہی اتراتے ہوئے لوٹتے ہیں'),
(1286, 9, 51, 'آپ کہہ دیجئے کہ ہمیں سوائے اللہ کے ہمارے حق میں لکھے ہوئے کہ کوئی چیز پہنچ ہی نہیں سکتی وه ہمارا کار ساز اور مولیٰ ہے۔ مومنوں کو تو اللہ کی ذات پاک پر ہی بھروسہ کرنا چاہئے۔'),
(1287, 9, 52, 'کہہ دیجئے کہ تم ہمارے بارے میں جس چیز کا انتظار کر رہے ہو وه دو بھلائیوں میں سے ایک ہے اور ہم تمہارے حق میں اس کا انتظار کرتے ہیں کہ یا تو اللہ تعالیٰ اپنے پاس سے کوئی سزا تمہیں دے یا ہمارے ہاتھوں سے، پس ایک طرف تم منتظر رہو دوسری جانب تمہارے ساتھ ہم بھی منتظر ہیں'),
(1288, 9, 53, 'کہہ دیجئے کہ تم خوشی یاناخوشی کسی طرح بھی خرچ کرو قبول تو ہرگز نہ کیا جائے گا، یقیناً تم فاسق لوگ ہو'),
(1289, 9, 54, 'کوئی سبب ان کے خرچ کی قبولیت کے نہ ہونے کا اس کے سوا نہیں کہ یہ اللہ اور اس کے رسول کے منکر ہیں اور بڑی کاہلی سے ہی نماز کو آتے ہیں اور برے دل سے ہی خرچ کرتے ہیں'),
(1290, 9, 55, 'پس آپ کو ان کے مال واوﻻد تعجب میں نہ ڈال دیں۔ اللہ کی چاہت یہی ہے کہ اس سے انہیں دنیا کی زندگی میں ہی سزا دے اور ان کے کفر ہی کی حالت میں ان کی جانیں نکل جائیں'),
(1291, 9, 56, 'یہ اللہ کی قسم کھا کھا کر کہتے ہیں کہ یہ تمہاری جماعت کے لوگ ہیں، حاﻻنکہ وه دراصل تمہارے نہیں بات صرف اتنی ہے کہ یہ ڈرپوک لوگ ہیں'),
(1292, 9, 57, 'اگر یہ کوئی بچاؤ کی جگہ یا کوئی غار یا کوئی بھی سر گھسانے کی جگہ پالیں تو ابھی اس طرح لگام توڑ کر الٹے بھاگ چھوٹیں'),
(1293, 9, 58, 'ان میں وه بھی ہیں جو خیراتی مال کی تقسیم کے بارے میں آپ پر عیب رکھتے ہیں، اگر انہیں اس میں سے مل جائے تو خوش ہیں اور اگر اس میں سے نہ ملا تو فوراً بگڑ کھڑے ہوئے'),
(1294, 9, 59, 'اگر یہ لوگ اللہ اور رسول کے دیئے ہوئے پر خوش رہتے اور کہہ دیتے کہ اللہ ہمیں کافی ہے اللہ ہمیں اپنے فضل سے دے گا اور اس کا رسول بھی، ہم تو اللہ کی ذات سے ہی توقع رکھنے والے ہیں'),
(1295, 9, 60, 'صدقے صرف فقیروں کے لئے ہیں اور مسکینوں کے لئے اور ان کے وصول کرنے والوں کے لئے اور ان کے لئے جن کے دل پرچائے جاتے ہوں اور گردن چھڑانے میں اور قرض داروں کے لئے اور اللہ کی راه میں اور راہرو مسافروں کے لئے، فرض ہے اللہ کی طرف سے، اور اللہ علم وحکمت واﻻ ہے'),
(1296, 9, 61, 'ان میں سے وه بھی ہیں جو پیغمبر کو ایذا دیتے ہیں اور کہتے ہیں کان کا کچا ہے، آپ کہہ دیجئے کہ وه کان تمہارے بھلے کے لئے ہے وه اللہ پر ایمان رکھتا ہے اور مسلمانوں کی بات کا یقین کرتا ہے اور تم میں سے جو اہل ایمان ہیں یہ ان کے لئے رحمت ہے، رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ کو جو لوگ ایذا دیتے ہیں ان کے لئے دکھ کی مار ہے'),
(1297, 9, 62, 'محض تمہیں خوش کرنے کے لئے تمہارے سامنے اللہ کی قسمیں کھا جاتے ہیں حاﻻنکہ اگر یہ ایمان دار ہوتے تو اللہ اور اس کا رسول رضامند کرنے کے زیاده مستحق تھے'),
(1298, 9, 63, 'کیا یہ نہیں جانتے کہ جو بھی اللہ کی اور اس کے رسول کی مخالفت کرے گااس کے لئے یقیناً دوزخ کی آگ ہے جس میں وه ہمیشہ رہنے واﻻ ہے، یہ زبردست رسوائی ہے'),
(1299, 9, 64, 'منافقوں کو ہر وقت اس بات کا کھٹکا لگارہتا ہے کہ کہیں مسلمانوں پر کوئی سورت نہ اترے جو ان کے دلوں کی باتیں انہیں بتلا دے۔ کہہ دیجئے کہ تم مذاق اڑاتے رہو۔ یقیناً اللہ تعالیٰ اسے ﻇاہر کرنے واﻻ ہے جس سے تم ڈر دبک رہے ہو'),
(1300, 9, 65, 'اگر آپ ان سے پوچھیں تو صاف کہہ دیں گے کہ ہم تو یونہی آپس میں ہنس بول رہے تھے۔ کہہ دیجئے کہ اللہ، اس کی آیتیں اور اس کا رسول ہی تمہارے ہنسی مذاق کے لئے ره گئے ہیں؟'),
(1301, 9, 66, 'تم بہانے نہ بناؤ یقیناً تم اپنے ایمان کے بعد بے ایمان ہوگئے، اگر ہم تم میں سے کچھ لوگوں سے درگزر بھی کر لیں تو کچھ لوگوں کو ان کے جرم کی سنگین سزا بھی دیں گے'),
(1302, 9, 67, 'تمام منافق مرد وعورت آپس میں ایک ہی ہیں، یہ بری باتوں کا حکم دیتے ہیں اور بھلی باتوں سے روکتے ہیں اور اپنی مٹھی بند رکھتے ہیں، یہ اللہ کو بھول گئے اللہ نے انہیں بھلا دیا۔ بیشک منافق ہی فاسق وبدکردار ہیں'),
(1303, 9, 68, 'اللہ تعالیٰ ان منافق مردوں، عورتوں اورکافروں سے جہنم کی آگ کا وعده کر چکا ہے جہاں یہ ہمیشہ رہنے والے ہیں، وہی انہیں کافی ہے ان پر اللہ کی پھٹکار ہے، اور ان ہی کے لئے دائمی عذاب ہے'),
(1304, 9, 69, 'مثل ان لوگوں کے جو تم سے پہلے تھے، تم میں سے وه زیاده قوت والے تھے اور زیاده مال واوﻻد والے تھے پس وه اپنا دینی حصہ برت گئے پھر تم نے بھی اپنا حصہ برت لیا جیسے تم سے پہلے کے لوگ اپنے حصے سے فائده مند ہوئے تھے اور تم نے بھی اسی طرح مذاقانہ بحﺚ کی جیسے کہ انہوں نے کی تھی۔ ان کے اعمال دنیا اور آخرت میں غارت ہوگئے۔ یہی لوگ نقصان پانے والے ہیں'),
(1305, 9, 70, 'کیا انہیں اپنے سے پہلے لوگوں کی خبریں نہیں پہنچیں، قوم نوح اور عاد اور ﺛمود اور قوم ابراہیم اور اہل مدین اور اہل مؤتفکات (الٹی ہوئی بستیوں کے رہنے والے) کی، ان کے پاس ان کے پیغمبر دلیلیں لے کر پہنچے، اللہ ایسا نہ تھا کہ ان پر ﻇلم کرے بلکہ انہوں نے خود ہی اپنے اوپر ﻇلم کیا'),
(1306, 9, 71, 'مومن مرد وعورت آپس میں ایک دوسرے کے (مددگار ومعاون اور) دوست ہیں، وه بھلائیوں کا حکم دیتے ہیں اور برائیوں سے روکتے ہیں، نمازوں کو پابندی سے بجا ﻻتے ہیں زکوٰة ادا کرتے ہیں، اللہ کی اور اس کے رسول کی بات مانتے ہیں، یہی لوگ ہیں جن پر اللہ تعالیٰ بہت جلد رحم فرمائے گا بیشک اللہ غلبے واﻻ حکمت واﻻ ہے'),
(1307, 9, 72, 'ان ایمان دار مردوں اور عورتوں سے اللہ نے ان جنتوں کا وعده فرمایا ہے جن کے نیچے نہریں لہریں لے رہی ہیں جہاں وه ہمیشہ ہمیشہ رہنے والے ہیں اور ان صاف ستھرے پاکیزه محلات کا جو ان ہمیشگی والی جنتوں میں ہیں، اور اللہ کی رضامندی سب سے بڑی چیز ہے، یہی زبردست کامیابی ہے'),
(1308, 9, 73, 'اے نبی! کافروں اور منافقوں سے جہاد جاری رکھو، اور ان پر سخت ہو جاؤ ان کی اصلی جگہ دوزخ ہے، جو نہایت بدترین جگہ ہے'),
(1309, 9, 74, 'یہ اللہ کی قسمیں کھا کر کہتے ہیں کہ انہوں نے نہیں کہا، حاﻻنکہ یقیناً کفر کا کلمہ ان کی زبان سے نکل چکا ہے اور یہ اپنے اسلام کے بعد کافر ہوگئے ہیں اور انہوں نے اس کام کا قصد بھی کیا جو پورا نہ کر سکے۔ یہ صرف اسی بات کا انتقام لے رہے ہیں کہ انہیں اللہ نے اپنے فضل سے اور اس کے رسول ﴿صلی اللہ علیہ وسلم﴾ نے دولت مند کردیا، اگر یہ اب بھی توبہ کر لیں تو یہ ان کے حق میں بہتر ہے، اور اگر منھ موڑے رہیں تو اللہ تعالیٰ انہیں دنیا وآخرت میں دردناک عذاب دے گا اور زمین بھر میں ان کا کوئی حمایتی اور مددگار نہ کھڑا ہوگا'),
(1310, 9, 75, 'ان میں وه بھی ہیں جنہوں نے اللہ سے عہد کیا تھا کہ اگر وه ہمیں اپنے فضل سے مال دے گا تو ہم ضرور صدقہ وخیرات کریں گے اور پکی طرح نیکو کاروں میں ہو جائیں گے'),
(1311, 9, 76, 'لیکن جب اللہ نے اپنے فضل سے انہیں دیا تو یہ اس میں بخیلی کرنے لگے اور ٹال مٹول کرکے منھ موڑ لیا'),
(1312, 9, 77, 'پس اس کی سزا میں اللہ نے ان کے دلوں میں نفاق ڈال دیا اللہ سے ملنے کے دنوں تک، کیونکہ انہوں نے اللہ سے کئے ہوئے وعدے کا خلاف کیا اور کیوں کہ جھوٹ بولتے رہے'),
(1313, 9, 78, 'کیا وه نہیں جانتے کہ اللہ تعالیٰ کو ان کے دل کا بھید اور ان کی سرگوشی سب معلوم ہے اور اللہ تعالیٰ غیب کی تمام باتوں سے خبردار ہے'),
(1314, 9, 79, 'جولوگ ان مسلمانوں پر طعنہ زنی کرتے ہیں جو دل کھول کر خیرات کرتے ہیں اور ان لوگوں پر جنہیں سوائے اپنی محنت مزدوری کے اور کچھ میسر ہی نہیں، پس یہ ان کا مذاق اڑاتے ہیں، اللہ بھی ان سے تمسخر کرتا ہے انہی کے لئے دردناک عذاب ہے'),
(1315, 9, 80, 'ان کے لئے تو استغفار کر یا نہ کر۔ اگر تو ستر مرتبہ بھی ان کے لئے استغفار کرے تو بھی اللہ انہیں ہرگز نہ بخشے گا یہ اس لئے کہ انہوں نے اللہ سے اور اس کے رسول سے کفر کیا ایسے فاسق لوگوں کو رب کریم ہدایت نہیں دیتا'),
(1316, 9, 81, 'پیچھے ره جانے والے لوگ رسول اللہ ﴿صلی اللہ علیہ وسلم﴾ کے جانے کے بعد اپنے بیٹھے رہنے پر خوش ہیں انہوں نے اللہ کی راه میں اپنے مال اور اپنی جانوں سے جہاد کرنا ناپسند رکھا اور انہوں نے کہہ دیا کہ اس گرمی میں مت نکلو۔ کہہ دیجئے کہ دوزخ کی آگ بہت ہی سخت گرم ہے، کاش کہ وه سمجھتے ہوتے'),
(1317, 9, 82, 'پس انہیں چاہئے کہ بہت کم ہنسیں اور بہت زیاده روئیں بدلے میں اس کے جو یہ کرتے تھے'),
(1318, 9, 83, 'پس اگر اللہ تعالیٰ آپ کو ان کی کسی جماعت کی طرف لوٹا کر واپس لے آئے پھر یہ آپ سے میدان جنگ میں نکلنے کی اجازت طلب کریں تو آپ کہہ دیجئے کہ تم میرے ساتھ ہرگز چل نہیں سکتے اور نہ میرے ساتھ تم دشمنوں سے لڑائی کر سکتے ہو۔ تم نے پہلی مرتبہ ہی بیٹھ رہنے کو پسند کیا تھا پس تم پیچھے ره جانے والوں میں ہی بیٹھے رہو'),
(1319, 9, 84, 'ان میں سے کوئی مر جائے تو آپ اس کے جنازے کی ہرگز نماز نہ پڑھیں اور نہ اس کی قبر پر کھڑے ہوں۔ یہ اللہ اور اس کے رسول کے منکر ہیں اور مرتے دم تک بدکار بے اطاعت رہے ہیں'),
(1320, 9, 85, 'آپ کو ان کے مال واوﻻد کچھ بھی بھلے نہ لگیں! اللہ کی چاہت یہی ہے کہ انہیں ان چیزوں سے دنیوی سزا دے اور یہ اپنی جانیں نکلنے تک کافر ہی رہیں'),
(1321, 9, 86, 'جب کوئی سورت اتاری جاتی ہے تو اللہ پر ایمان لاؤ اور اس کے رسول کے ساتھ مل کر جہاد کرو تو ان میں سے دولت مندوں کا ایک طبقہ آپ کے پاس آکر یہ کہہ کر رخصت لے لیتاہے کہ ہمیں تو بیٹھے رہنے والوں میں ہی چھوڑ دیجئے'),
(1322, 9, 87, 'یہ تو خانہ نشین عورتوں کاساتھ دینے پر ریجھ گئے اور ان کے دلوں پر مہر لگا دی گئی اب وه کچھ سمجھ عقل نہیں رکھتے'),
(1323, 9, 88, 'لیکن خود رسول اللہ اور اس کے ساتھ کے ایمان والے اپنے مالوں اور جانوں سے جہاد کرتے ہیں، یہی لوگ بھلائیوں والے ہیں اور یہی لوگ کامیابی حاصل کرنے والے ہیں'),
(1324, 9, 89, 'انہی کے لئے اللہ نے وه جنتیں تیار کی ہیں جن کے نیچے نہریں جاری ہیں جن میں یہ ہمیشہ رہنے والے ہیں۔ یہی بہت بڑی کامیابی ہے'),
(1325, 9, 90, 'بادیہ نشینوں میں سے عذر والے لوگ حاضر ہوئے کہ انہیں رخصت دے دی جائے اور وه بیٹھ رہے جنہوں نے اللہ سے اور اس کے رسول سے جھوٹی باتیں بنائی تھیں۔ اب تو ان میں جتنے کفار ہیں انہیں دکھ دینے والی مار پہنچ کر رہے گی'),
(1326, 9, 91, 'ضعیفوں پر اور بیماروں پر اور ان پر جن کے پاس خرچ کرنے کو کچھ بھی نہیں کوئی حرج نہیں بشرطیکہ وه اللہ اور اس کے رسول کی خیر خواہی کرتے رہیں۔ ایسے نیک کاروں پر الزام کی کوئی راه نہیں، اللہ تعالیٰ بڑی مغفرت ورحمت واﻻ ہے'),
(1327, 9, 92, 'ہاں ان پر بھی کوئی حرج نہیں جو آپ کے پاس آتے ہیں کہ آپ انہیں سواری مہیا کر دیں تو آپ جواب دیتے ہیں کہ میں تو تمہاری سواری کے لئے کچھ بھی نہیں پاتا، تو وه رنج وغم سے اپنی آنکھوں سے آنسو بہاتے ہوئے لوٹ جاتے ہیں کہ انہیں خرچ کرنے کے لئے کچھ بھی میسر نہیں'),
(1328, 9, 93, 'بیشک انہیں لوگوں پر راه الزام ہے جو باوجود دولتمند ہونے کے آپ سے اجازت طلب کرتے ہیں۔ یہ خانہ نشین عورتوں کا ساتھ دینے پر خوش ہیں اور ان کے دلوں پر مہر خداوندی لگ چکی ہے جس سے وه محض بے علم ہو گئے ہیں'),
(1329, 9, 94, 'یہ لوگ تمہارے سامنے عذر پیش کریں گے جب تم ان کے پاس واپس جاؤ گے۔ آپ کہہ دیجئے کہ یہ عذر پیش مت کرو ہم کبھی تم کو سچا نہ سمجھیں گے، اللہ تعالیٰ ہم کو تمہاری خبر دے چکا ہے اور آئنده بھی اللہ اور اس کا رسول تمہاری کارگزاری دیکھ لیں گے پھر ایسے کے پاس لوٹائے جاؤ گے جو پوشیده اور ﻇاہر سب کا جاننے واﻻ ہے پھر وه تم کو بتادے گا جو کچھ تم کرتے تھے'),
(1330, 9, 95, 'ہاں وه اب تمہارے سامنے اللہ کی قسمیں کھا جائیں گے جب تم ان کے پاس واپس جاؤ گے تاکہ تم ان کو ان کی حالت پر چھوڑ دو۔ سو تم ان کو ان کی حالت پر چھوڑ دو۔ وه لوگ بالکل گندے ہیں اور ان کا ٹھکانہ دوزخ ہے ان کاموں کے بدلے جنہیں وه کیا کرتے تھے'),
(1331, 9, 96, 'یہ اس لیے قسمیں کھائیں گے کہ تم ان سے راضی ہوجاؤ۔ سو اگر تم ان سے راضی بھی ہوجاؤ تو اللہ تعالیٰ تو ایسے فاسق لوگوں سے راضی نہیں ہوتا'),
(1332, 9, 97, 'دیہاتی لوگ کفر اور نفاق میں بہت ہی سخت ہیں اور ان کو ایسا ہونا ہی چاہئے کہ ان کو ان احکام کا علم نہ ہو جو اللہ تعالیٰ نے اپنے رسول پر نازل فرمائے ہیں اور اللہ بڑا علم واﻻ بڑی حکمت واﻻ ہے'),
(1333, 9, 98, 'اور ان دیہاتیوں میں سے بعض ایسے ہیں کہ جو کچھ خرچ کرتے ہیں اس کو جرمانہ سمجھتے ہیں اور تم مسلمانوں کے واسطے برے وقت کے منتظر رہتے ہیں، برا وقت ان ہی پر پڑنے واﻻ ہے اور اللہ سننے واﻻ جاننے واﻻ ہے'),
(1334, 9, 99, 'اور بعض اہل دیہات میں ایسے بھی ہیں جو اللہ تعالیٰ پر اور قیامت کے دن پر ایمان رکھتے ہیں اور جو کچھ خرچ کرتے ہیں اس کو عنداللہ قرب حاصل ہونے کا ذریعہ اور رسول کی دعا کا ذریعہ بناتے ہیں، یاد رکھو کہ ان کا یہ خرچ کرنا بیشک ان کے لیے موجب قربت ہے، ان کو اللہ تعالیٰ ضرور اپنی رحمت میں داخل کرے گا۔ اللہ تعالیٰ بڑی مغفرت واﻻ بڑی رحمت واﻻ ہے'),
(1335, 9, 100, 'اور جو مہاجرین اور انصار سابق اور مقدم ہیں اور جتنے لوگ اخلاص کے ساتھ ان کے پیرو ہیں اللہ ان سب سے راضی ہوا اور وه سب اس سے راضی ہوئے اور اللہ نے ان کے لیے ایسے باغ مہیا کر رکھے ہیں جن کے نیچے نہریں جاری ہوں گی جن میں ہمیشہ رہیں گے یہ بڑی کامیابی ہے'),
(1336, 9, 101, 'اور کچھ تمہارے گردوپیش والوں میں اور کچھ مدینے والوں میں ایسے منافق ہیں کہ نفاق پر اڑے ہوئے ہیں، آپ ان کو نہیں جانتے، ان کو ہم جانتے ہیں ہم ان کو دہری سزا دیں گے، پھر وه بڑے بھاری عذاب کی طرف بھیجے جائیں گے'),
(1337, 9, 102, 'اور کچھ اور لوگ ہیں جو اپنی خطا کے اقراری ہیں جنہوں نے ملے جلے عمل کیے تھے، کچھ بھلے اور کچھ برے۔ اللہ سے امید ہے کہ ان کی توبہ قبول فرمائے۔ بلاشبہ اللہ تعالیٰ بڑی مغفرت واﻻ بڑی رحمت واﻻ ہے'),
(1338, 9, 103, 'آپ ان کے مالوں میں سے صدقہ لے لیجئے، جس کے ذریعہ سے آپ ان کو پاک صاف کردیں اور ان کے لیے دعا کیجئے، بلاشبہ آپ کی دعا ان کے لیے موجب اطمینان ہے اور اللہ تعالیٰ خوب سنتا ہے خوب جانتا ہے'),
(1339, 9, 104, 'کیا ان کو یہ خبر نہیں کہ اللہ ہی اپنے بندوں کی توبہ قبول کرتا ہے اور وہی صدقات کو قبول فرماتا ہے اور یہ کہ اللہ ہی توبہ قبول کرنے میں اور رحمت کرنے میں کامل ہے'),
(1340, 9, 105, 'کہہ دیجئے کہ تم عمل کیے جاؤ تمہارے عمل اللہ خود دیکھ لے گا اور اس کا رسول اور ایمان والے (بھی دیکھ لیں گے) اور ضرور تم کو ایسے کے پاس جانا ہے جو تمام چھپی اور کھلی چیزوں کا جاننے واﻻ ہے۔ سو وه تم کو تمہارا سب کیا ہوا بتلا دے گا'),
(1341, 9, 106, 'اور کچھ اور لوگ ہیں جن کا معاملہ اللہ کے حکم آنے تک ملتوی ہے ان کو سزا دے گا یا ان کی توبہ قبول کرلے گا، اور اللہ خوب جاننے واﻻ ہے بڑا حکمت واﻻ ہے'),
(1342, 9, 107, 'اور بعض ایسے ہیں جنہوں نے ان اغراض کے لیے مسجد بنائی ہے کہ ضرر پہنچائیں اور کفر کی باتیں کریں اور ایمانداروں میں تفریق ڈالیں اور اس شخص کے قیام کا سامان کریں جو اس سے پہلے سے اللہ اور رسول کا مخالف ہے، اور قسمیں کھا جائیں گے کہ بجز بھلائی کے اور ہماری کچھ نیت نہیں، اور اللہ گواه ہے کہ وه بالکل جھوٹے ہیں'),
(1343, 9, 108, 'آپ اس میں کبھی کھڑے نہ ہوں۔ البتہ جس مسجد کی بنیاد اول دن سے تقویٰ پر رکھی گئی ہے وه اس ﻻئق ہے کہ آپ اس میں کھڑے ہوں، اس میں ایسے آدمی ہیں کہ وه خوب پاک ہونے کو پسند کرتے ہیں، اور اللہ تعالیٰ خوب پاک ہونے والوں کو پسند کرتا ہے'),
(1344, 9, 109, 'پھر آیا ایسا شخص بہتر ہے جس نے اپنی عمارت کی بنیاد اللہ سے ڈرنے پر اور اللہ کی خوشنودی پر رکھی ہو، یا وه شخص، کہ جس نے اپنی عمارت کی بنیاد کسی گھاٹی کے کنارے پر جو کہ گرنے ہی کو ہو، رکھی ہو، پھر وه اس کو لے کر آتش دوزخ میں گر پڑے، اور اللہ تعالیٰ ایسے ﻇالموں کو سمجھ ہی نہیں دیتا'),
(1345, 9, 110, 'ان کی یہ عمارت جو انہوں نے بنائی ہے ہمیشہ ان کے دلوں میں شک کی بنیاد پر (کانٹا بن کر) کھٹکتی رہے گی، ہاں مگر ان کے دل ہی اگر پاش پاش ہوجائیں تو خیر، اور اللہ تعالیٰ بڑا علم واﻻ بڑی حکمت واﻻ ہے'),
(1346, 9, 111, 'بلاشبہ اللہ تعالیٰ نے مسلمانوں سے ان کی جانوں کو اور ان کے مالوں کو اس بات کے عوض میں خرید لیا ہے کہ ان کو جنت ملے گی۔ وه لوگ اللہ کی راه میں لڑتے ہیں جس میں قتل کرتے ہیں اور قتل کیے جاتے ہیں، اس پر سچا وعده کیا گیا ہے تورات میں اور انجیل میں اور قرآن میں اور اللہ سے زیاده اپنے عہد کو کون پورا کرنے واﻻ ہے، تو تم لوگ اپنی اس بیع پر جس کا تم نے معاملہ ٹھہرایا ہے خوشی مناؤ، اور یہ بڑی کامیابی ہے'),
(1347, 9, 112, 'وه ایسے ہیں جو توبہ کرنے والے، عبادت کرنے والے، حمد کرنے والے، روزه رکھنے والے، (یا راه حق میں سفر کرنے والے) رکوع اور سجده کرنے والے، نیک باتوں کی تعلیم کرنے والے اور بری باتوں سے باز رکھنے والے اور اللہ کی حدوں کا خیال رکھنے والے ہیں اور ایسے مومنین کو آپ خوشخبری سنا دیجئے'),
(1348, 9, 113, 'پیغمبر کو اور دوسرے مسلمانوں کو جائز نہیں کہ مشرکین کے لیے مغفرت کی دعا مانگیں اگرچہ وه رشتہدار ہی ہوں اس امر کے ﻇاہر ہوجانے کے بعد کہ یہ لوگ دوزخی ہیں'),
(1349, 9, 114, 'اور ابراہیم (علیہ السلام) کا اپنے باپ کے لیے دعائے مغفرت مانگنا وه صرف وعده کے سبب سے تھا جو انہوں نے اس سے وعده کرلیا تھا۔ پھر جب ان پر یہ بات ﻇاہر ہوگئی کہ وه اللہ کا دشمن ہے تو وه اس سے محض بے تعلق ہوگئے، واقعی ابراہیم (علیہ السلام) بڑے نرم دل اور برد بار تھے'),
(1350, 9, 115, 'اور اللہ ایسا نہیں کرتا کہ کسی قوم کو ہدایت کر کے بعد میں گمراه کر دے جب تک کہ ان چیزوں کو صاف صاف نہ بتلادے جن سے وه بچیں بیشک اللہ تعالیٰ ہر چیز کو خوب جانتا ہے'),
(1351, 9, 116, 'بلاشبہ اللہ ہی کی سلطنت ہے آسمانوں اور زمین میں۔ وہی جلاتا اور مارتا ہے، اور تمہارا اللہ کے سوا نہ کوئی یار ہے اور نہ کوئی مدد گار ہے'),
(1352, 9, 117, 'اللہ تعالیٰ نے پیغمبر کے حال پر توجہ فرمائی اور مہاجرین اور انصار کے حال پر بھی جنہوں نے ایسی تنگی کے وقت پیغمبر کا ساتھ دیا، اس کے بعد کہ ان میں سے ایک گروه کے دلوں میں کچھ تزلزل ہو چلا تھا۔ پھر اللہ نے ان کے حال پر توجہ فرمائی۔ بلاشبہ اللہ تعالیٰ ان سب پر بہت ہی شفیق مہربان ہے'),
(1353, 9, 118, 'اور تین شخصوں کے حال پر بھی جن کا معاملہ ملتوی چھوڑ دیا گیا تھا۔ یہاں تک کہ جب زمین باوجود اپنی فراخی کے ان پر تنگ ہونے لگی اور وه خود اپنی جان سے تنگ آگئے اور انہوں نے سمجھ لیا کہ اللہ سے کہیں پناه نہیں مل سکتی بجز اس کے کہ اسی کی طرف رجوع کیا جائے پھر ان کے حال پر توجہ فرمائی تاکہ وه آئنده بھی توبہ کرسکیں۔ بیشک اللہ تعالیٰ بہت توبہ قبول کرنے واﻻ بڑا رحم واﻻ ہے'),
(1354, 9, 119, 'اے ایمان والو! اللہ تعالیٰ سے ڈرو اور سچوں کے ساتھ رہو'),
(1355, 9, 120, 'مدینہ کے رہنے والوں کو اور جو دیہاتی ان کے گردوپیش ہیں ان کو یہ زیبا نہ تھا کہ رسول اللہ کو چھوڑ کر پیچھے ره جائیں اور نہ یہ کہ اپنی جان کو ان کی جان سے عزیز سمجھیں، یہ اس سبب سے کہ ان کو اللہ کی راه میں جو پیاس لگی اور جو تکان پہنچی اور جو بھوک لگی اور جو کسی ایسی جگہ چلے جو کفار کے لیے موجب غیﻆ ہوا ہو اور دشمنوں کی جو کچھ خبر لی، ان سب پر ان کے نام (ایک ایک) نیک کام لکھا گیا۔ یقیناً اللہ تعالیٰ مخلصین کا اجر ضائع نہیں کرتا'),
(1356, 9, 121, 'اور جو کچھ چھوٹا بڑا انہوں نے خرچ کیا اور جتنے میدان ان کو طے کرنے پڑے، یہ سب بھی ان کے نام لکھا گیا تاکہ اللہ تعالیٰ ان کے کاموں کا اچھے سے اچھا بدلہ دے'),
(1357, 9, 122, 'اور مسلمانوں کو یہ نہ چاہئے کہ سب کے سب نکل کھڑے ہوں سو ایسا کیوں نہ کیا جائے کہ ان کی ہر بڑی جماعت میں سے ایک چھوٹی جماعت جایا کرے تاکہ وه دین کی سمجھ بوجھ حاصل کریں اور تاکہ یہ لوگ اپنی قوم کو جب کہ وه ان کے پاس آئیں، ڈرائیں تاکہ وه ڈر جائیں'),
(1358, 9, 123, 'اے ایمان والو! ان کفار سے لڑو جو تمہارے آس پاس ہیں اور ان کو تمہارے اندر سختی پانا چاہئے۔ اور یقین رکھو کہ اللہ تعالیٰ متقی لوگوں کے ساتھ ہے'),
(1359, 9, 124, 'اور جب کوئی سورت نازل کی جاتی ہے تو بعض منافقین کہتے ہیں کہ اس سورت نے تم میں سے کس کے ایمان کو زیاده کیا ہے، سو جو لوگ ایمان والے ہیں اس سورت نے ان کے ایمان کو زیاده کیا ہے اور وه خوش ہورہے ہیں'),
(1360, 9, 125, 'اور جن کے دلوں میں روگ ہے اس سورت نے ان میں ان کی گندگی کے ساتھ اور گندگی بڑھا دی اور وه حالت کفر ہی میں مر گئے'),
(1361, 9, 126, 'اور کیا ان کو نہیں دکھلائی دیتا کہ یہ لوگ ہر سال ایک بار یا دو بار کسی نہ کسی آفت میں پھنستے رہتے ہیں پھر بھی نہ توبہ کرتے اور نہ نصیحت قبول کرتے ہیں'),
(1362, 9, 127, 'اور جب کوئی سورت نازل کی جاتی ہے تو ایک دوسرے کو دیکھنے لگتے ہیں کہ تم کو کوئی دیکھتا تو نہیں پھر چل دیتے ہیں اللہ تعالیٰ نے ان کا دل پھیر دیا ہے اس وجہ سے کہ وه بے سمجھ لوگ ہیں'),
(1363, 9, 128, 'تمہارے پاس ایک ایسے پیغمبر تشریف ﻻئے ہیں جو تمہاری جنس سے ہیں جن کو تمہاری مضرت کی بات نہایت گراں گزرتی ہے جو تمہاری منفعت کے بڑے خواہش مند رہتے ہیں ایمان والوں کے ساتھ بڑے ہی شفیق اور مہربان ہیں'),
(1364, 9, 129, 'پھر اگر رو گردانی کریں تو آپ کہہ دیجیئے کہ میرے لیے اللہ کافی ہے، اس کے سوا کوئی معبود نہیں۔ میں نے اسی پر بھروسہ کیا اور وه بڑے عرش کا مالک ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1365, 10, 1, 'الرٰ۔ یہ پرحکمت کتاب کی آیتیں ہیں'),
(1366, 10, 2, 'کیا ان لوگوں کو اس بات سے تعجب ہوا کہ ہم نے ان میں سے ایک شخص کے پاس وحی بھیج دی کہ سب آدمیوں کو ڈرائیے اور جو ایمان لے آئے ان کو یہ خوشخبری سنائیے کہ ان کے رب کے پاس ان کو پورا اجر ومرتبہ ملے گا۔ کافروں نے کہا کہ یہ شخص تو بلاشبہ صریح جادوگر ہے'),
(1367, 10, 3, 'بلاشبہ تمہارا رب اللہ ہی ہے جس نے آسمانوں اور زمین کو چھ روز میں پیدا کردیا پھر عرش پر قائم ہوا وه ہر کام کی تدبیر کرتا ہے۔ اس کی اجازت کے بغیر کوئی اس کے پاس سفارش کرنے واﻻ نہیں ایسا اللہ تمہارا رب ہے سو تم اس کی عبادت کرو، کیا تم پھر بھی نصیحت نہیں پکڑتے'),
(1368, 10, 4, 'تم سب کو اللہ ہی کے پاس جانا ہے، اللہ نے سچا وعده کر رکھا ہے۔ بیشک وہی پہلی بار بھی پیدا کرتا ہے پھر وہی دوباره بھی پیدا کرے گا تاکہ ایسے لوگوں کو جو کہ ایمان ﻻئے اور انہوں نے نیک کام کیے انصاف کے ساتھ جزا دے اور جن لوگوں نے کفر کیا ان کے واسطے کھولتا ہوا پانی پینے کو ملے گا اور دردناک عذاب ہوگا ان کے کفر کی وجہ سے'),
(1369, 10, 5, 'وه اللہ تعالیٰ ایسا ہے جس نے آفتاب کو چمکتا ہوا بنایا اور چاند کو نورانی بنایا اور اس کے لیے منزلیں مقرر کیں تاکہ تم برسوں کی گنتی اور حساب معلوم کرلیا کرو۔ اللہ تعالیٰ نے یہ چیزیں بے فائده نہیں پیدا کیں۔ وه یہ دﻻئل ان کو صاف صاف بتلا رہا ہے جو دانش رکھتے ہیں'),
(1370, 10, 6, 'بلاشبہ رات اور دن کے یکے بعد دیگرے آنے میں اور اللہ تعالیٰ نے جو کچھ آسمانوں اور زمین میں پیدا کیا ہے ان سب میں ان لوگوں کے واسطے دﻻئل ہیں جو اللہ کا ڈر رکھتے ہیں'),
(1371, 10, 7, 'جن لوگوں کو ہمارے پاس آنے کا یقین نہیں ہے اور وه دنیوی زندگی پر راضی ہوگئے ہیں اور اس میں جی لگا بیٹھے ہیں اور جو لوگ ہماری آیتوں سے غافل ہیں'),
(1372, 10, 8, 'ایسے لوگوں کا ٹھکانا ان کے اعمال کی وجہ سے دوزخ ہے'),
(1373, 10, 9, 'یقیناً جو لوگ ایمان ﻻئے اور انہوں نے نیک کام کیے ان کا رب ان کو ان کے ایمان کے سبب ان کے مقصد تک پہنچا دے گا نعمت کے باغوں میں جن کے نیچے نہریں جاری ہوں گی'),
(1374, 10, 10, 'ان کے منھ سے یہ بات نکلے گی سبحان اللہ اور ان کا باہمی سلام یہ ہوگا السلام علیکم اور ان کی اخیر بات یہ ہوگی تمام تعریفیں اللہ کے لیے ہیں جو سارے جہان کا رب ہے'),
(1375, 10, 11, 'اور اگر اللہ لوگوں پر جلدی سے نقصان واقع کردیا کرتا جس طرح وه فائده کے لیے جلدی مچاتے ہیں تو ان کا وعده کبھی کا پورا ہوچکا ہوتا۔ سو ہم ان لوگوں کو جن کو ہمارے پاس آنے کا یقین نہیں ہے ان کے حال پر چھوڑے رکھتے ہیں کہ اپنی سرکشی میں بھٹکتے رہیں'),
(1376, 10, 12, 'اور جب انسان کو کوئی تکلیف پہنچتی ہے تو ہم کو پکارتا ہے لیٹے بھی، بیٹھے بھی، کھڑے بھی۔ پھر جب ہم اس کی تکلیف اس سے ہٹا دیتے ہیں تو وه ایسا ہوجاتا ہے کہ گویا اس نے اپنی تکلیف کے لیے جو اسے پہنچی تھی کبھی ہمیں پکارا ہی نہ تھا، ان حد سے گزرنے والوں کے اعمال کو ان کے لیے اسی طرح خوشنما بنادیا گیا ہے'),
(1377, 10, 13, 'اور ہم نے تم سے پہلے بہت سے گروہوں کو ہلاک کردیا جب کہ انہوں نے ﻇلم کیا حاﻻنکہ ان کے پاس ان کے پیغمبر بھی دﻻئل لے کر آئے، اور وه ایسے کب تھے کہ ایمان لے آتے؟ ہم مجرم لوگوں کو ایسی ہی سزا دیا کرتے ہیں'),
(1378, 10, 14, 'پھر ان کے بعد ہم نے دنیا میں بجائے ان کے تم کو جانشین کیا تاکہ ہم دیکھ لیں کہ تم کس طرح کام کرتے ہو'),
(1379, 10, 15, 'اور جب ان کے سامنے ہماری آیتیں پڑھی جاتی ہیں جو بالکل صاف صاف ہیں تو یہ لوگ جن کو ہمارے پاس آنے کی امید نہیں ہے یوں کہتے ہیں کہ اس کے سوا کوئی دوسرا قرآن ﻻئیے یا اس میں کچھ ترمیم کردیجئے۔ آپ (صلی اللہ علیہ وسلم) یوں کہہ دیجئے کہ مجھے یہ حق نہیں کہ میں اپنی طرف سے اس میں ترمیم کردوں بس میں تو اسی کا اتباع کروں گا جو میرے پاس وحی کے ذریعہ سے پہنچا ہے، اگر میں اپنے رب کی نافرمانی کروں تو میں ایک بڑے دن کے عذاب کا اندیشہ رکھتا ہوں'),
(1380, 10, 16, 'آپ یوں کہہ دیجئے کہ اگر اللہ کو منظور ہوتا تو نہ تو میں تم کو وه پڑھ کر سناتا اور نہ اللہ تعالیٰ تم کو اس کی اطلاع دیتا کیونکہ میں اس سے پہلے تو ایک بڑے حصہ عمر تک تم میں ره چکا ہوں۔ پھر کیا تم عقل نہیں رکھتے'),
(1381, 10, 17, 'سو اس شخص سے زیاده کون ﻇالم ہوگا جو اللہ پرجھوٹ باندھے یا اس کی آیتوں کو جھوٹا بتلائے، یقیناً ایسے مجرموں کو اصلاً فلاح نہ ہوگی'),
(1382, 10, 18, 'اور یہ لوگ اللہ کے سوا ایسی چیزوں کی عبادت کرتے ہیں جو نہ ان کو ضرر پہنچا سکیں اور نہ ان کو نفع پہنچا سکیں اور کہتے ہیں کہ یہ اللہ کے پاس ہمارے سفارشی ہیں۔ آپ کہہ دیجئے کہ کیا تم اللہ کو ایسی چیز کی خبر دیتے ہو جو اللہ تعالیٰ کو معلوم نہیں، نہ آسمانوں میں اور نہ زمین میں، وه پاک اور برتر ہے ان لوگوں کے شرک سے'),
(1383, 10, 19, 'اور تمام لوگ ایک ہی امت کے تھے پھر انہوں نے اختلاف پیداکرلیا اور اگر ایک بات نہ ہوتی جو آپ کے رب کی طرف سے پہلے ٹھہر چکی ہے تو جس چیز میں یہ لوگ اختلاف کررہے ہیں ان کا قطعی فیصلہ ہوچکا ہوتا'),
(1384, 10, 20, 'اور یہ لوگ یوں کہتے ہیں کہ ان پر ان کے رب کی جانب سے کوئی نشانی کیوں نہیں نازل ہوتی؟ سو آپ فرما دیجئے کہ غیب کی خبر صرف اللہ کو ہے سو تم بھی منتظر رہو میں بھی تمہارے ساتھ منتظر ہوں'),
(1385, 10, 21, 'اور جب ہم لوگوں کو اس امر کے بعد کہ ان پر کوئی مصیبت پڑ چکی ہو کسی نعمت کامزه چکھا دیتے ہیں تو وه فوراً ہی ہماری آیتوں کے بارے میں چالیں چلنے لگتے ہیں، آپ کہہ دیجئے کہ اللہ چال چلنے میں تم سے زیاده تیز ہے، بالیقین ہمارے فرشتے تمہاری سب چالوں کو لکھ رہے ہیں'),
(1386, 10, 22, 'وه اللہ ایسا ہے کہ تم کو خشکی اور دریا میں چلاتا ہے، یہاں تک کہ جب تم کشتی میں ہوتے ہو اور وه کشتیاں لوگوں کو موافق ہوا کے ذریعہ سے لے کر چلتی ہیں اور وه لوگ ان سے خوش ہوتے ہیں ان پر ایک جھونکا سخت ہوا کا آتا ہے اور ہر طرف سے ان پر موجیں اٹھتی چلی آتی ہیں اور وه سمجھتے ہیں کہ (برے) آ گھرے، (اس وقت) سب خالص اعتقاد کرکے اللہ ہی کو پکارتے ہیں کہ اگر تو ہم کو اس سے بچالے تو ہم ضرور شکر گزار بن جائیں گے'),
(1387, 10, 23, 'پھر جب اللہ تعالیٰ ان کو بچالیتا ہے تو فوراً ہی وه زمین میں ناحق سرکشی کرنے لگتے ہیں اے لوگو! یہ تمہاری سرکشی تمہارے لیے وبال ہونے والی ہے دنیاوی زندگی کے (چند) فائدے ہیں، پھر ہمارے پاس تم کو آنا ہے پھر ہم سب تمہارا کیا ہوا تم کو بتلادیں گے'),
(1388, 10, 24, 'پس دنیاوی زندگی کی حالت تو ایسی ہے جیسے ہم نے آسمان سے پانی برسایا پھر اس سے زمین کی نباتات، جن کو آدمی اور چوپائے کھاتے ہیں، خوب گنجان ہوکر نکلی۔ یہاں تک کہ جب وه زمین اپنی رونق کا پورا حصہ لے چکی اور اس کی خوب زیبائش ہوگئی اور اس کے مالکوں نے سمجھ لیا کہ اب ہم اس پر بالکل قابض ہوچکے تو دن میں یا رات میں اس پر ہماری طرف سے کوئی حکم (عذاب) آپڑا سو ہم نے اس کو ایسا صاف کردیا کہ گویا کل وه موجود ہی نہ تھی۔ ہم اسی طرح آیات کو صاف صاف بیان کرتے ہیں ایسے لوگوں کے لیے جو سوچتے ہیں'),
(1389, 10, 25, 'اور اللہ تعالیٰ سلامتی کے گھر کی طرف تم کو بلاتا ہے اور جس کو چاہتا ہے راه راست پر چلنے کی توفیق دیتا ہے'),
(1390, 10, 26, 'جن لوگوں نے نیکی کی ہے ان کے واسطے خوبی ہے اور مزید برآں بھی اور ان کے چہروں پر نہ سیاہی چھائے گی اور نہ ذلت، یہ لوگ جنت میں رہنے والے ہیں وه اس میں ہمیشہ رہیں گے'),
(1391, 10, 27, 'اور جن لوگوں نے بدکام کیے ان کی بدی کی سزا اس کے برابر ملے گی اور ان کو ذلت چھائے گی، ان کو اللہ تعالیٰ سے کوئی نہ بچا سکے گا۔ گویا ان کے چہروں پر اندھیری رات کے پرت کے پرت لپیٹ دیے گئے ہیں۔ یہ لوگ دوزخ میں رہنے والے ہیں، وه اس میں ہمیشہ رہیں گے'),
(1392, 10, 28, 'اور وه دن بھی قابل ذکر ہے جس روز ہم ان سب کو جمع کریں گے پھر مشرکین سے کہیں گے کہ تم اور تمہارے شریک اپنی جگہ ٹھہرو پھر ہم ان کے آپس میں پھوٹ ڈال دیں گے اور ان کے وه شرکا کہیں گے کہ تم ہماری عبادت نہیں کرتے تھے'),
(1393, 10, 29, 'سو ہمارے تمہارے درمیان اللہ کافی ہے گواه کے طور پر، کہ ہم کو تمہاری عبادت کی خبر بھی نہ تھی'),
(1394, 10, 30, 'اس مقام پر ہر شخص اپنے اگلے کیے ہوئے کاموں کی جانچ کرلے گا اور یہ لوگ اللہ کی طرف جو ان کا مالک حقیقی ہے لوٹائے جائیں گے اور جو کچھ جھوٹ باندھا کرتے تھے سب ان سے غائب ہوجائیں گے'),
(1395, 10, 31, 'آپ کہیے کہ وه کون ہے جو تم کو آسمان اور زمین سے رزق پہنچاتا ہے یا وه کون ہے جو کانوں اور آنکھوں پر پورا اختیار رکھتا ہے اور وه کون ہے جو زنده کو مرده سے نکالتا ہے اور مرده کو زنده سے نکالتا ہے اور وه کون ہے جو تمام کاموں کی تدبیر کرتا ہے؟ ضرور وه یہی کہیں گے کہ اللہ تو ان سے کہیے کہ پھر کیوں نہیں ڈرتے'),
(1396, 10, 32, 'سو یہ ہے اللہ تعالیٰ جو تمہارا رب حقیقی ہے۔ پھر حق کے بعد اور کیا ره گیا بجز گمراہی کے، پھر کہاں پھرے جاتے ہو؟'),
(1397, 10, 33, 'اسی طرح آپ کے رب کی یہ بات کہ یہ ایمان نہ ﻻئیں گے، تمام فاسق لوگوں کے حق میں ﺛابت ہوچکی ہے'),
(1398, 10, 34, 'آپ یوں کہیے کہ کیا تمہارے شرکا میں کوئی ایسا ہے جو پہلی بار بھی پیدا کرے، پھر دوباره بھی پیدا کرے؟ آپ کہہ دیجئے کہ اللہ ہی پہلی بار پیدا کرتا ہے پھر وہی دوباره بھی پیدا کرے گا۔ پھر تم کہاں پھرے جاتے ہو؟'),
(1399, 10, 35, 'آپ کہیے کہ تمہارے شرکا میں کوئی ایسا ہے کہ حق کا راستہ بتاتا ہو؟ آپ کہہ دیجئے کہ اللہ ہی حق کا راستہ بتاتا ہے۔ تو پھر آیا جو شخص حق کا راستہ بتاتا ہو وه زیاده اتباع کے ﻻئق ہے یا وه شخص جس کو بغیر بتائے خود ہی راستہ نہ سوجھے؟ پس تم کو کیا ہوگیا ہے تم کیسے فیصلے کرتے ہو'),
(1400, 10, 36, 'اور ان میں سے اکثر لوگ صرف گمان پر چل رہے ہیں۔ یقیناً گمان، حق (کی معرفت) میں کچھ بھی کام نہیں دے سکتا یہ جو کچھ کررہے ہیں یقیناً اللہ کو سب خبر ہے'),
(1401, 10, 37, 'اور یہ قرآن ایسا نہیں ہے کہ اللہ (کی وحی) کے بغیر (اپنے ہی سے) گھڑ لیا گیا ہو۔ بلکہ یہ تو (ان کتابوں کی) تصدیق کرنے واﻻ ہے جو اس کے قبل (نازل) ہوچکی ہیں اور کتاب (احکام ضروریہ) کی تفصیل بیان کرنے واﻻ ہے اس میں کوئی بات شک کی نہیں کہ رب العالمین کی طرف سے ہے'),
(1402, 10, 38, 'کیا یہ لوگ یوں کہتے ہیں کہ آپ نے اس کو گھڑ لیا ہے؟ آپ کہہ دیجئے کہ تو پھر تم اس کے مثل ایک ہی سورت لاؤ اور جن جن غیراللہ کو بلا سکو، بلالو اگر تم سچے ہو'),
(1403, 10, 39, 'بلکہ ایسی چیز کی تکذیب کرنے لگے جس کو اپنے احاطہٴ علمی میں نہیں ﻻئے اور ہنوز ان کو اس کا اخیر نتیجہ نہیں ملا۔ جو لوگ ان سے پہلے ہوئے ہیں اسی طرح انہوں نے بھی جھٹلایا تھا، سو دیکھ لیجئے ان ﻇالموں کا انجام کیسا ہوا؟'),
(1404, 10, 40, 'اور ان میں سے بعض ایسے ہیں جو اس پر ایمان لے آئیں گے اور بعض ایسے ہیں کہ اس پر ایمان نہ ﻻئیں گے۔ اور آپ کا رب مفسدوں کو خوب جانتا ہے'),
(1405, 10, 41, 'اور اگر آپ کو جھٹلاتے رہیں تو یہ کہہ دیجئے کہ میرے لیے میرا عمل اور تمہارے لیے تمہارا عمل، تم میرے عمل سے بری ہو اور میں تمہارے عمل سے بری ہوں'),
(1406, 10, 42, 'اور ان میں بعض ایسے ہیں جو آپ کی طرف کان لگائے بیٹھے ہیں۔ کیا آپ بہروں کو سناتے ہیں گو ان کو سمجھ بھی نہ ہو؟'),
(1407, 10, 43, 'اور ان میں بعض ایسے ہیں کہ آپ کو تک رہے ہیں۔ پھر کیا آپ اندھوں کو راستہ دکھلانا چاہتے ہیں گو ان کو بصیرت بھی نہ ہو؟'),
(1408, 10, 44, 'یہ یقینی بات ہے کہ اللہ لوگوں پر کچھ ﻇلم نہیں کرتا لیکن لوگ خود ہی اپنی جانوں پر ﻇلم کرتے ہیں'),
(1409, 10, 45, 'اور ان کو وه دن یاد دﻻئیے جس میں اللہ ان کو (اپنے حضور) جمع کرے گا (تو ان کو ایسا محسوس ہوگا) کہ گویا وه (دنیا میں) سارے دن کی ایک آدھ گھڑی رہے ہوں گے اور آپس میں ایک دوسرے کو پہچاننے کو ٹھہرے ہوں گے۔ واقعی خسارے میں پڑے وه لوگ جنہوں نے اللہ کے پاس جانے کو جھٹلایا اور وه ہدایت پانے والے نہ تھے'),
(1410, 10, 46, 'اور جس کا ان سے ہم وعده کر رہے ہیں اس میں سے کچھ تھوڑا سا اگر ہم آپ کو دکھلا دیں یا (ان کے ﻇہور سے پہلے) ہم آپ کو وفات دے دیں، سو ہمارے پاس تو ان کو آنا ہی ہے۔ پھر اللہ ان کے سب افعال پر گواه ہے'),
(1411, 10, 47, 'اور ہر امت کے لیے ایک رسول ہے، سو جب ان کا وه رسول آچکتا ہے ان کا فیصلہ انصاف کے ساتھ کیا جاتا ہے، اور ان پر ﻇلم نہیں کیا جاتا'),
(1412, 10, 48, 'اور یہ لوگ کہتے ہیں کہ یہ وعده کب ہوگا؟ اگر تم سچے ہو'),
(1413, 10, 49, 'آپ فرما دیجئے کہ میں اپنی ذات کے لیے تو کسی نفع کا اور کسی ضرر کا اختیار رکھتا ہی نہیں مگر جتنا اللہ کو منظور ہو۔ ہر امت کے لیے ایک معین وقت ہے جب ان کا وه معین وقت آپہنچتا ہے تو ایک گھڑی نہ پیچھے ہٹ سکتے ہیں اور نہ آگے سرک سکتے ہیں'),
(1414, 10, 50, 'آپ فرما دیجئے کہ یہ تو بتلاؤ کہ اگر تم پر اللہ کا عذاب رات کو آپڑے یا دن کو تو عذاب میں کون سی چیز ایسی ہے کہ مجرم لوگ اس کو جلدی مانگ رہے ہیں'),
(1415, 10, 51, 'کیا پھر جب وه آہی پڑے گا اس پر ایمان لاؤ گے۔ ہاں اب مانا! حاﻻنکہ تم اس کی جلدی مچایا کرتے تھے'),
(1416, 10, 52, 'پھر ﻇالموں سے کہا جائے گا کہ ہمیشہ کا عذاب چکھو۔ تم کو تو تمہارے کیے کا ہی بدلہ ملا ہے'),
(1417, 10, 53, 'اور وه آپ سے دریافت کرتے ہیں کہ کیا عذاب واقعی سچ ہے؟ آپ فرما دیجئے کہ ہاں قسم ہے میرے رب کی وه واقعی سچ ہے اور تم کسی طرح اللہ کو عاجز نہیں کرسکتے'),
(1418, 10, 54, 'اور اگر ہر جان، جس نے ﻇلم (شرک) کیا ہے، کے پاس اتنا ہو کہ ساری زمین بھر جائے تب بھی اس کو دے کر اپنی جان بچانے لگے اور جب عذاب کو دیکھیں گے تو پشیمانی کو پوشیده رکھیں گے۔ اور ان کا فیصلہ انصاف کے ساتھ ہوگا۔ اور ان پر ﻇلم نہ ہوگا'),
(1419, 10, 55, 'یاد رکھو کہ جتنی چیزیں آسمانوں میں اور زمین میں ہیں سب اللہ ہی کی ملک ہیں۔ یاد رکھو کہ اللہ تعالیٰ کا وعده سچا ہے لیکن بہت سے آدمی علم ہی نہیں رکھتے'),
(1420, 10, 56, 'وہی جان ڈالتا ہے وہی جان نکالتا ہے اور تم سب اسی کے پاس ﻻئے جاؤ گے'),
(1421, 10, 57, 'اے لوگو! تمہارے پاس تمہارے رب کی طرف سے ایک ایسی چیز آئی ہے جو نصیحت ہے اور دلوں میں جو روگ ہیں ان کے لیے شفا ہے اور رہنمائی کرنے والی ہے اور رحمت ہے ایمان والوں کے لیے'),
(1422, 10, 58, 'آپ کہہ دیجئے کہ بس لوگوں کو اللہ کے اس انعام اور رحمت پر خوش ہونا چاہئے۔ وه اس سے بدرجہا بہتر ہے جس کو وه جمع کر رہے ہیں'),
(1423, 10, 59, 'آپ کہیے کہ یہ تو بتاؤ کہ اللہ نے تمہارے لیے جو کچھ رزق بھیجا تھا پھر تم نے اس کا کچھ حصہ حرام اور کچھ حلال قرار دے لیا۔ آپ پوچھیے کہ کیا تم کو اللہ نے حکم دیا تھا یا اللہ پر افترا ہی کرتے ہو؟'),
(1424, 10, 60, 'اور جو لوگ اللہ پر جھوٹ افترا باندھتے ہیں ان کا قیامت کی نسبت کیا گمان ہے؟ واقعی لوگوں پر اللہ تعالیٰ کا بڑا ہی فضل ہے لیکن اکثر آدمی شکر نہیں کرتے'),
(1425, 10, 61, 'اور آپ کسی حال میں ہوں اور منجملہ ان احوال کے آپ کہیں سے قرآن پڑھتے ہوں اور جو کام بھی کرتے ہوں ہم کو سب کی خبر رہتی ہے جب تم اس کام میں مشغول ہوتے ہو۔ اور آپ کے رب سے کوئی چیز ذره برابر بھی غائب نہیں نہ زمین میں اور نہ آسمان میں اورنہ کوئی چیز اس سے چھوٹی اور نہ کوئی چیز بڑی مگر یہ سب کتاب مبین میں ہے'),
(1426, 10, 62, 'یاد رکھو اللہ کے دوستوں پر نہ کوئی اندیشہ ہے اور نہ وه غمگین ہوتے ہیں'),
(1427, 10, 63, 'یہ وه لوگ ہیں جو ایمان ﻻئے اور (برائیوں سے) پرہیز رکھتے ہیں'),
(1428, 10, 64, 'ان کے لیے دنیاوی زندگی میں بھی اور آخرت میں بھی خوش خبری ہے اللہ تعالیٰ کی باتوں میں کچھ فرق ہوا نہیں کرتا۔ یہ بڑی کامیابی ہے'),
(1429, 10, 65, 'اور آپ کو ان کی باتیں غم میں نہ ڈالیں۔ تمام تر غلبہ اللہ ہی کے لیے ہے وه سنتا جانتا ہے'),
(1430, 10, 66, 'یاد رکھو کہ جتنے کچھ آسمانوں میں ہیں اور جتنے زمین میں ہیں یہ سب اللہ ہی کے ہیں اور جو لوگ اللہ کو چھوڑ کر دوسرے شرکا کی عبادت کررہے ہیں کس چیز کی اتباع کر رہے ہیں۔ محض بے سند خیال کی اتباع کر رہے ہیں اور محض اٹکلیں لگا رہے ہیں'),
(1431, 10, 67, 'وه ایسا ہے جس نے تمہارے لیے رات بنائی تاکہ تم اس میں آرام کرو اور دن بھی اس طور پر بنایا کہ دیکھنے بھالنے کا ذریعہ ہے، تحقیق اس میں دﻻئل ہیں ان لوگوں کے لیے جو سنتے ہیں'),
(1432, 10, 68, 'وه کہتے ہیں کہ اللہ اوﻻد رکھتا ہے۔ سبحان اللہ! وه تو کسی کا محتاج نہیں اسی کی ملکیت ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے۔ تمہارے پاس اس پر کوئی دلیل نہیں۔ کیا اللہ کے ذمہ ایسی بات لگاتے ہو جس کا تم علم نہیں رکھتے'),
(1433, 10, 69, 'آپ کہہ دیجئے کہ جو لوگ اللہ پر جھوٹ افترا کرتے ہیں، وه کامیاب نہ ہوں گے'),
(1434, 10, 70, 'یہ دنیا میں تھوڑا سا عیش ہے پھر ہمارے پاس ان کو آنا ہے پھر ہم ان کو ان کے کفر کے بدلے سخت عذاب چکھائیں گے'),
(1435, 10, 71, 'اور آپ ان کو نوح﴿علیہ السلام﴾ کا قصہ پڑھ کر سنائیے جب کہ انہوں نے اپنی قوم سے فرمایا کہ اے میری قوم! اگر تم کو میرا رہنا اور احکام الٰہی کی نصیحت کرنا بھاری معلوم ہوتا ہے تو میرا تو اللہ ہی پر بھروسہ ہے۔ تم اپنی تدبیر مع اپنے شرکا کے پختہ کرلو پھر تمہاری تدبیر تمہاری گھٹن کا باعﺚ نہ ہونی چاہئے۔ پھر میرے ساتھ کر گزرو اور مجھ کو مہلت نہ دو'),
(1436, 10, 72, 'پھر بھی اگر تم اعراض ہی کیے جاؤ تو میں نے تم سے کوئی معاوضہ تو نہیں مانگا، میرا معاوضہ تو صرف اللہ ہی کے ذمہ ہے اور مجھ کو حکم کیا گیا ہے کہ میں مسلمانوں میں سے رہوں'),
(1437, 10, 73, 'سو وه لوگ ان کو جھٹلاتے رہے پس ہم نے ان کو اور جو ان کے ساتھ کشتی میں تھے ان کو نجات دی اور ان کو جانشین بنایا اور جنہوں نے ہماری آیتوں کو جھٹلایا تھا ان کو غرق کردیا۔ سو دیکھنا چاہئے کیسا انجام ہوا ان لوگوں کا جو ڈرائے جاچکے تھے'),
(1438, 10, 74, 'پھر نوح (علیہ السلام) کے بعد ہم نے اور رسولوں کو ان کی قوموں کی طرف بھیجا سو وه ان کے پاس روشن دلیلیں لے کر آئے پس جس چیز کو انہوں نے اول میں جھوٹا کہہ دیا یہ نہ ہوا کہ پھر اس کو مان لیتے۔ اللہ تعالیٰ اسی طرح حد سے بڑھنے والوں کے دلوں پر بند لگا دیتا ہے'),
(1439, 10, 75, 'پھر ان پیغمبروں کے بعد ہم نے موسیٰ اور ہارون (علیہما السلام) کو، فرعون اور اس کے سرداروں کے پاس اپنی نشانیاں دے کر بھیجا۔ سو انہوں نے تکبر کیا اور وه لوگ مجرم قوم تھے'),
(1440, 10, 76, 'پھر جب ان کو ہمارے پاس سے صحیح دلیل پہنچی تو وه لوگ کہنے لگے کہ یقیناً یہ صریح جادو ہے'),
(1441, 10, 77, 'موسیٰ (علیہ السلام) نے فرمایا کہ کیا تم اس صحیح دلیل کی نسبت جب کہ وه تمہارے پاس پہنچی ایسی بات کہتے ہو کیا یہ جادو ہے، حاﻻنکہ جادوگر کامیاب نہیں ہوا کرتے'),
(1442, 10, 78, 'وه لوگ کہنے لگے کیا تم ہمارے پاس اس لیے آئے ہو کہ ہم کو اس طریقہ سے ہٹادو جس پر ہم نے اپنے باپ دادوں کو پایا ہے اور تم دونوں کو دنیا میں بڑائی مل جائے اور ہم تم دونوں کو کبھی نہ مانیں گے'),
(1443, 10, 79, 'اور فرعون نے کہا کہ میرے پاس تمام ماہر جادوگروں کو حاضر کرو'),
(1444, 10, 80, 'پھر جب جادوگر آئے تو موسیٰ (علیہ السلام) نے ان سے فرمایا کہ ڈالو جو کچھ تم ڈالنے والے ہو'),
(1445, 10, 81, 'سو جب انہوں نے ڈاﻻ تو موسی﴿علیہ السلام﴾ نے فرمایا کہ یہ جو کچھ تم ﻻئے ہو جادو ہے۔ یقینی بات ہے کہ اللہ اس کو ابھی درہم برہم کیے دیتا ہے، اللہ ایسے فسادیوں کا کام بننے نہیں دیتا'),
(1446, 10, 82, 'اور اللہ تعالیٰ حق کو اپنے فرمان سے ﺛابت کردیتا ہے گو مجرم کیسا ہی ناگوار سمجھیں'),
(1447, 10, 83, 'پس موسیٰ (علیہ السلام) پر ان کی قوم میں سے صرف قدرے قلیل آدمی ایمان ﻻئے وه بھی فرعون سے اور اپنے حکام سے ڈرتے ڈرتے کہ کہیں ان کو تکلیف پہنچائے اور واقع میں فرعون اس ملک میں زور رکھتا تھا، اور یہ بھی بات تھی کہ وه حد سے باہر ہو جاتا تھا'),
(1448, 10, 84, 'اور موسیٰ (علیہ السلام) نے فرمایا کہ اے میری قوم! اگر تم اللہ پر ایمان رکھتے ہو تو اسی پر توکل کرو اگر تم مسلمان ہو'),
(1449, 10, 85, 'انہوں نے عرض کیا کہ ہم نے اللہ ہی پر توکل کیا۔ اے ہمارے پروردگار! ہم کو ان ﻇالموں کے لئے فتنہ نہ بنا'),
(1450, 10, 86, 'اور ہم کو اپنی رحمت سے ان کافر لوگوں سے نجات دے'),
(1451, 10, 87, 'اور ہم نے موسیٰ (علیہ السلام) اور ان کے بھائی کے پاس وحی بھیجی کہ تم دونوں اپنے ان لوگوں کے لیے مصر میں گھر برقرار رکھو اور تم سب اپنے انہی گھروں کو نماز پڑھنے کی جگہ قرار دے لو اور نماز کے پابند رہو اور آپ مسلمانوں کو بشارت دے دیں'),
(1452, 10, 88, 'اور موسیٰ (علیہ السلام) نے عرض کیا اے ہمارے رب! تو نے فرعون کو اور اس کے سرداروں کو سامان زینت اور طرح طرح کے مال دنیاوی زندگی میں دیئے۔ اے ہمارے رب! (اسی واسطے دیئے ہیں کہ) وه تیری راه سے گمراه کریں۔ اے ہمارے رب! ان کے مالوں کو نیست ونابود کر دے اور ان کے دلوں کو سخت کردے سو یہ ایمان نہ ﻻنے پائیں یہاں تک کہ دردناک عذاب کو دیکھ لیں'),
(1453, 10, 89, 'حق تعالیٰ نے فرمایا کہ تم دونوں کی دعا قبول کرلی گئی، سو تم ﺛابت قدم رہو اور ان لوگوں کی راه نہ چلنا جن کو علم نہیں'),
(1454, 10, 90, 'اور ہم نے بنی اسرائیل کو دریا سے پار کردیا پھر ان کے پیچھے پیچھے فرعون اپنے لشکر کے ساتھ ﻇلم اور زیادتی کے اراده سے چلا یہاں تک کہ جب ڈوبنے لگا تو کہنے لگا کہ میں ایمان ﻻتا ہوں کہ جس پر بنی اسرائیل ایمان ﻻئے ہیں، اس کے سوا کوئی معبود نہیں اور میں مسلمانوں میں سے ہوں'),
(1455, 10, 91, '(جواب دیا گیا کہ) اب ایمان ﻻتا ہے؟ اور پہلے سرکشی کرتا رہا اور مفسدوں میں داخل رہا'),
(1456, 10, 92, 'سو آج ہم صرف تیری ﻻش کو نجات دیں گے تاکہ تو ان کے لیے نشان عبرت ہو جو تیرے بعد ہیں اور حقیقت یہ ہے کہ بہت سے آدمی ہماری نشانیوں سے غافل ہیں'),
(1457, 10, 93, 'اور ہم نے بنی اسرائیل کو بہت اچھا ٹھکانا رہنے کو دیا اور ہم نے انہیں پاکیزه چیزیں کھانے کو دیں۔ سو انہوں نے اختلاف نہیں کیا یہاں تک کہ ان کے پاس علم پہنچ گیا۔ یقینی بات ہے کہ آپ کا رب ان کے درمیان قیامت کے دن ان امور میں فیصلہ کرے گا جن میں وه اختلاف کرتے تھے'),
(1458, 10, 94, 'پھر اگر آپ اس کی طرف سے شک میں ہوں جس کو ہم نے آپ کے پاس بھیجا ہے تو آپ ان لوگوں سے پوچھ دیکھیے جو آپ سے پہلی کتابوں کو پڑھتے ہیں۔ بیشک آپ کے پاس آپ کے رب کی طرف سے سچی کتاب آئی ہے۔ آپ ہرگز شک کرنے والوں میں سے نہ ہوں'),
(1459, 10, 95, 'اور نہ ان لوگوں میں سے ہوں جنہوں نے اللہ تعالیٰ کی آیتوں کو جھٹلایا، کہیں آپ خساره پانے والوں میں سے نہ ہوجائیں'),
(1460, 10, 96, 'یقیناً جن لوگوں کے حق میں آپ کے رب کی بات ﺛابت ہوچکی ہے وه ایمان نہ ﻻئیں گے'),
(1461, 10, 97, 'گو ان کے پاس تمام نشانیاں پہنچ جائیں جب تک کہ وه دردناک عذاب کو نہ دیکھ لیں'),
(1462, 10, 98, 'چنانچہ کوئی بستی ایمان نہ ﻻئی کہ ایمان ﻻنا اس کو نافع ہوتا سوائے یونس ﴿علیہ السلام﴾ کی قوم کے۔ جب وه ایمان لے آئے تو ہم نے رسوائی کے عذاب کو دنیوی زندگی میں ان پر سے ٹال دیا اور ان کو ایک وقت (خاص) تک کے لیے زندگی سے فائده اٹھانے (کا موقع) دیا'),
(1463, 10, 99, 'اور اگر آپ کا رب چاہتا تو تمام روئے زمین کے لوگ سب کے سب ایمان لے آتے، تو کیا آپ لوگوں پر زبردستی کرسکتے ہیں یہاں تک کہ وه مومن ہی ہوجائیں'),
(1464, 10, 100, 'حاﻻنکہ کسی شخص کا ایمان ﻻنا اللہ کے حکم کے بغیر ممکن نہیں۔ اور اللہ تعالیٰ بے عقل لوگوں پر گندگی ڈال دیتا ہے'),
(1465, 10, 101, 'آپ کہہ دیجئے کہ تم غور کرو کہ کیا کیا چیزیں آسمانوں میں اور زمین میں ہیں اور جو لوگ ایمان نہیں ﻻتے ان کو نشانیاں اور دھمکیاں کچھ فائده نہیں پہنچاتیں'),
(1466, 10, 102, 'سو وه لوگ صرف ان لوگوں کے سے واقعات کا انتظار کر رہے ہیں جو ان سے پہلے گزر چکے ہیں۔ آپ فرما دیجئے کہ اچھا تو تم انتظار میں رہو میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں'),
(1467, 10, 103, 'پھر ہم اپنے پیغمبروں کو اور ایمان والوں کو بچالیتے تھے، اسی طرح ہمارے ذمہ ہے کہ ہم ایمان والوں کو نجات دیا کرتے ہیں'),
(1468, 10, 104, 'آپ کہہ دیجئے کہ اے لوگو! اگر تم مرے دین کی طرف سے شک میں ہو تو میں ان معبودوں کی عبادت نہیں کرتا جن کی تم اللہ کو چھوڑ کر عبادت کرتے ہو، لیکن ہاں اس اللہ کی عبادت کرتا ہوں جو تمہاری جان قبض کرتا ہے۔ اور مجھ کو یہ حکم ہوا ہے کہ میں ایمان ﻻنے والوں میں سے ہوں'),
(1469, 10, 105, 'اور یہ کہ اپنا رخ یکسو ہو کر (اس) دین کی طرف کرلینا، اور کبھی مشرکوں میں سے نہ ہونا'),
(1470, 10, 106, 'اور اللہ کو چھوڑ کر ایسی چیز کی عبادت مت کرنا جو تجھ کو نہ کوئی نفع پہنچا سکے اور نہ کوئی ضرر پہنچا سکے۔ پھر اگر ایسا کیا تو تم اس حالت میں ﻇالموں میں سے ہو جاؤ گے'),
(1471, 10, 107, 'اور اگر تم کو اللہ کوئی تکلیف پہنچائے تو بجز اس کے اور کوئی اس کو دور کرنے واﻻ نہیں ہے اور اگر وه تم کو کوئی خیر پہنچانا چاہے تو اس کے فضل کا کوئی ہٹانے واﻻ نہیں، وه اپنا فضل اپنے بندوں میں سے جس پر چاہے نچھاور کردے اور وه بڑی مغفرت بڑی رحمت واﻻ ہے'),
(1472, 10, 108, 'آپ کہہ دیجئے کہ اے لوگو! تمہارے پاس حق تمہارے رب کی طرف سے پہنچ چکا ہے، اس لیے جو شخص راه راست پر آجائے سو وه اپنے واسطے راه راست پر آئے گا اور جو شخص بے راه رہے گا تو اس کا بے راه ہونا اسی پر پڑے گا اور میں تم پر مسلط نہیں کیا گیا'),
(1473, 10, 109, 'اور آپ اس کی اتباع کرتے رہیے جو کچھ آپ کے پاس وحی بھیجی جاتی ہے اور صبر کیجئے یہاں تک کہ اللہ فیصلہ کردے اور وه سب فیصلہ کرنے والوں میں اچھا ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1474, 11, 1, 'الرٰ، یہ ایک ایسی کتاب ہے کہ اس کی آیتیں محکم کی گئی ہیں، پھر صاف صاف بیان کی گئی ہیں ایک حکیم باخبر کی طرف سے'),
(1475, 11, 2, 'یہ کہ اللہ کے سوا کسی کی عبادت مت کرو میں تم کو اللہ کی طرف سے ڈرانے واﻻ اور بشارت دینے واﻻ ہوں'),
(1476, 11, 3, 'اور یہ کہ تم لوگ اپنے گناه اپنے رب سے معاف کراؤ پھر اسی کی طرف متوجہ رہو، وه تم کو وقت مقرر تک اچھا سامان (زندگی) دے گا اور ہر زیاده عمل کرنے والے کو زیاده ﺛواب دے گا۔ اور اگر تم لوگ اعراض کرتے رہے تو مجھ کو تمہارے لیے ایک بڑے دن کے عذاب کا اندیشہ ہے'),
(1477, 11, 4, 'تم کو اللہ ہی کے پاس جانا ہے اور وه ہر شے پر پوری قدرت رکھتا ہے'),
(1478, 11, 5, 'یاد رکھو وه لوگ اپنے سینوں کو دہرا کیے دیتے ہیں تاکہ اپنی باتیں (اللہ) سے چھپا سکیں۔ یاد رکھو کہ وه لوگ جس وقت اپنے کپڑے لپیٹتے ہیں وه اس وقت بھی سب جانتا ہے جو کچھ چھپاتے ہیں اور جو کچھ وه ﻇاہر کرتے ہیں۔ بالیقین وه دلوں کے اندر کی باتیں جانتا ہے'),
(1479, 11, 6, 'زمین پر چلنے پھرنے والے جتنے جاندار ہیں سب کی روزیاں اللہ تعالیٰ پر ہیں وہی ان کے رہنے سہنے کی جگہ کو جانتا ہےاور ان کے سونپے جانے کی جگہ کو بھی، سب کچھ واضح کتاب میں موجود ہے'),
(1480, 11, 7, 'اللہ ہی وه ہے جس نے چھ دن میں آسمان وزمین کو پیدا کیا اور اس کا عرش پانی پر تھا تاکہ وه تمہیں آزمائے کہ تم میں سے اچھے عمل واﻻ کون ہے، اگر آپ ان سے کہیں کہ تم لوگ مرنے کے بعد اٹھا کھڑے کئے جاؤ گے تو کافر لوگ پلٹ کر جواب دیں گے کہ یہ تو نرا صاف صاف جادو ہی ہے'),
(1481, 11, 8, 'اور اگر ہم ان سے عذاب کو گنی چنی مدت تک کے لئے پیچھے ڈال دیں تو یہ ضرور پکار اٹھیں گے کہ عذاب کو کون سی چیز روکے ہوئے ہے، سنو! جس دن وه ان کے پاس آئے گا پھر ان سے ٹلنے واﻻ نہیں پھر تو جس چیز کی ہنسی اڑا رہے تھے وه انہیں گھیر لے گی'),
(1482, 11, 9, 'اور اگر ہم انسان کو اپنی کسی نعمت کا ذائقہ چکھا کر پھر اسے اس سے لے لیں تو وه بہت ہی ناامید اور بڑا ہی ناشکرا بن جاتا ہے'),
(1483, 11, 10, 'اور اگر ہم اسے کوئی نعمت چکھائیں اس سختی کے بعد جو اسے پہنچ چکی تھی تو وه کہنے لگتا ہے کہ بس برائیاں مجھ سے جاتی رہیں، یقیناً وه بڑا ہی اترانے واﻻ شیخی خور ہے'),
(1484, 11, 11, 'سوائے ان کے جو صبر کرتے ہیں اور نیک کاموں میں لگے رہتے ہیں۔ انہی لوگوں کے لئے بخشش بھی ہے اور بہت بڑا نیک بدلہ بھی'),
(1485, 11, 12, 'پس شاید کہ آپ اس وحی کے کسی حصے کو چھوڑ دینے والے ہیں جو آپ کی طرف نازل کی جاتی ہے اور اس سے آپ کا دل تنگ ہے، صرف ان کی اس بات پر کہ اس پر کوئی خزانہ کیوں نہیں اترا؟ یا اس کے ساتھ کوئی فرشتہ ہی آتا، سن لیجئے! آپ تو صرف ڈرانے والے ہی ہیں اور ہر چیز کا ذمہ دار اللہ تعالیٰ ہے'),
(1486, 11, 13, 'کیا یہ کہتے ہیں کہ اس قرآن کو اسی نے گھڑا ہے۔ جواب دیجئے کہ پھر تم بھی اسی کی مثل دس سورتیں گھڑی ہوئی لے آؤ اور اللہ کے سوا جسے چاہو اپنے ساتھ بلا بھی لو اگر تم سچے ہو'),
(1487, 11, 14, 'پھر اگر وه تمہاری اس بات کو قبول نہ کریں تو تم یقین سے جان لو کہ یہ قرآن اللہ کے علم کے ساتھ اتارا گیا ہے اور یہ کہ اللہ کے سوا کوئی معبود نہیں، پس کیا تم مسلمان ہوتے ہو؟'),
(1488, 11, 15, 'جو شخص دنیا کی زندگی اور اس کی زینت پر فریفتہ ہوا چاہتا ہو ہم ایسوں کو ان کے کل اعمال (کا بدلہ) یہی بھرپور پہنچا دیتے ہیں اور یہاں انہیں کوئی کمی نہیں کی جاتی'),
(1489, 11, 16, 'ہاں یہی وه لوگ ہیں جن کے لئے آخرت میں سوائے آگ کے اور کچھ نہیں اور جو کچھ انہوں نے یہاں کیا ہوگا وہاں سب اکارت ہے اور جو کچھ ان کےاعمال تھے سب برباد ہونے والے ہیں'),
(1490, 11, 17, 'کیا وه شخص جو اپنے رب کے پاس کی دلیل پر ہو اور اس کے ساتھ اللہ کی طرف کا گواه ہو اور اس سے پہلے موسیٰ کی کتاب (گواه ہو) جو پیشوا اور رحمت ہے (اوروں کے برابر ہو سکتا ہے؟) یہی لوگ ہیں جو اس پر ایمان رکھتے ہیں، اور تمام فرقوں میں سے جو بھی اس کا منکر ہو اس کے آخری وعدے کی جگہ جہنم ہے، پس تو اس میں کسی قسم کے شبہ میں نہ ره، یقیناً یہ تیرے رب کی جانب سے سراسر برحق ہے، لیکن اکثر لوگ ایمان ﻻنے والے نہیں ہوتے'),
(1491, 11, 18, 'اس سے بڑھ کر ﻇالم کون ہوگا جو اللہ پرجھوٹ باندھے یہ لوگ اپنے پروردگار کے سامنے پیش کئے جائیں گے اور سارے گواه کہیں گے کہ یہ وه لوگ ہیں جنہوں نے اپنے پروردگار پر جھوٹ باندھا، خبردار ہو کہ اللہ کی لعنت ہے ﻇالموں پر'),
(1492, 11, 19, 'جو اللہ کی راه سے روکتے ہیں اور اس میں کجی تلاش کر لیتے ہیں۔ یہی آخرت کے منکر ہیں'),
(1493, 11, 20, 'نہ یہ لوگ دنیا میں اللہ کو ہرا سکے اور نہ ان کا کوئی حمایتی اللہ کے سوا ہوا، ان کے لئے عذاب دگنا کیا جائے گا نہ یہ سننے کی طاقت رکھتے تھے اور نہ یہ دیکھتے ہی تھے'),
(1494, 11, 21, 'یہی ہیں جنہوں نے اپنا نقصان آپ کر لیا اور وه سب کچھ ان سے کھو گیا، جو انہوں نے گھڑ رکھا تھا'),
(1495, 11, 22, 'بیشک یہی لوگ آخرت میں زیاں کار ہوں گے'),
(1496, 11, 23, 'یقیناً جو لوگ ایمان ﻻئے اور انہوں نے کام بھی نیک کئے اور اپنے پالنے والے کی طرف جھکتے رہے، وہی جنت میں جانے والے ہیں، جہاں وه ہمیشہ ہی رہنے والے ہیں'),
(1497, 11, 24, 'ان دونوں فرقوں کی مثال اندھے، بہرے اور دیکھنے، سننے والے جیسی ہے۔ کیا یہ دونوں مثال میں برابر ہیں؟ کیا پھر بھی تم نصیحت حاصل نہیں کرتے؟'),
(1498, 11, 25, 'یقیناً ہم نے نوح (علیہ السلام) کو اس کی قوم کی طرف رسول بنا کر بھیجا کہ میں تمہیں صاف صاف ہوشیار کر دینے واﻻ ہوں'),
(1499, 11, 26, 'کہ تم صرف اللہ ہی کی عبادت کرو، مجھے تو تم پر دردناک دن کے عذاب کا خوف ہے'),
(1500, 11, 27, 'اس کی قوم کے کافروں کے سرداروں نے جواب دیا کہ ہم تو تجھے اپنے جیسا انسان ہی دیکھتے ہیں اور تیرے تابعداروں کو بھی ہم دیکھتے ہیں کہ یہ لوگ واضح طور پر سوائے نیچ لوگوں کے اور کوئی نہیں جو بے سوچے سمجھے (تمہاری پیروی کر رہے ہیں)، ہم تو تمہاری کسی قسم کی برتری اپنے اوپر نہیں دیکھ رہے، بلکہ ہم تو تمہیں جھوٹا سمجھ رہے ہیں'),
(1501, 11, 28, 'نوح نے کہا، میری قوم والو! مجھے بتاؤ تو اگر میں اپنے رب کی طرف سے کسی دلیل پر ہوا اور مجھے اس نے اپنے پاس کی کوئی رحمت عطا کی ہو، پھر وه تمہاری نگاہوں میں نہ آئی تو کیا زبردستی میں اسے تمہارے گلے منڈھ دوں، حاﻻنکہ تم اس سے بیزار ہو'),
(1502, 11, 29, 'میری قوم والو! میں تم سے اس پر کوئی مال نہیں مانگتا۔ میرا ﺛواب تو صرف اللہ تعالیٰ کے ہاں ہے نہ میں ایمان والوں کو اپنے پاس سے نکال سکتا ہوں، انہیں اپنے رب سے ملنا ہے لیکن میں دیکھتا ہوں کہ تم لوگ جہالت کر رہے ہو'),
(1503, 11, 30, 'میری قوم کے لوگو! اگر میں ان مومنوں کو اپنے پاس سے نکال دوں تو اللہ کے مقابلہ میں میری مدد کون کر سکتا ہے؟ کیا تم کچھ بھی نصیحت نہیں پکڑتے'),
(1504, 11, 31, 'میں تم سے نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں، (سنو!) میں غیب کا علم بھی نہیں رکھتا، نہ میں یہ کہتا ہوں کہ میں کوئی فرشتہ ہوں، نہ میرا یہ قول ہے کہ جن پر تمہاری نگاہیں ذلت سے پڑ رہی ہیں انہیں اللہ تعالیٰ کوئی نعمت دے گا ہی نہیں، ان کے دل میں جو ہے اسے اللہ ہی خوب جانتا ہے، اگر میں ایسی بات کہوں تو یقیناً میرا شمار ﻇالموں میں ہو جائے گا'),
(1505, 11, 32, '(قوم کے لوگوں نے) کہا اے نوح! تو نے ہم سے بحﺚ کر لی اور خوب بحﺚ کر لی۔ اب تو جس چیز سے ہمیں دھمکا رہا ہے وہی ہمارے پاس لے آ، اگر تو سچوں میں ہے'),
(1506, 11, 33, 'جواب دیا کہ اسے بھی اللہ تعالیٰ ہی ﻻئے گا اگر وه چاہے اور ہاں تم اسے ہرانے والے نہیں ہو'),
(1507, 11, 34, 'تمہیں میری خیر خواہی کچھ بھی نفع نہیں دے سکتی، گو میں کتنی ہی تمہاری خیر خواہی کیوں نہ چاہوں، بشرطیکہ اللہ کا اراده تمہیں گمراه کرنے کا ہو، وہی تم سب کا پروردگار ہے اور اسی کی طرف لوٹائے جاؤ گے'),
(1508, 11, 35, 'کیا یہ کہتے ہیں کہ اسے خود اسی نے گھڑ لیا ہے؟ تو جواب دے کہ اگر میں نے اسے گھڑ لیا ہو تو میرا گناه مجھ پر ہے اور میں ان گناہوں سے بری ہوں جو تم کر رہے ہو'),
(1509, 11, 36, 'نوح کی طرف وحی بھیجی گئی کہ تیری قوم میں سے جو ایمان ﻻ چکے ان کے سوا اور کوئی اب ایمان ﻻئے گا ہی نہیں، پس تو ان کے کاموں پر غمگین نہ ہو'),
(1510, 11, 37, 'اورایک کشتی ہماری آنکھوں کے سامنے اور ہماری وحی سے تیار کر اور ﻇالموں کے بارے میں ہم سے کوئی بات چیت نہ کر وه پانی میں ڈبو دیئے جانے والے ہیں'),
(1511, 11, 38, 'وه (نوح) کشتی بنانے لگے ان کی قوم کے جو سردار ان کے پاس سے گزرتے وه ان کا مذاق اڑاتے، وه کہتے اگر تم ہمارا مذاق اڑاتے ہو تو ہم بھی تم پر ایک دن ہنسیں گے جیسے تم ہم پر ہنستے ہو'),
(1512, 11, 39, 'تمیں بہت جلد معلوم ہو جائے گا کہ کس پر عذاب آتا ہے جو اسے رسوا کرے اور اس پر ہمیشگی کی سزا اتر آئے'),
(1513, 11, 40, 'یہاں تک کہ جب ہمارا حکم آپہنچا اور تنور ابلنے لگا ہم نے کہا کہ اس کشتی میں ہر قسم کے (جانداروں میں سے) جوڑے (یعنی) دو (جانور، ایک نر اور ایک ماده) سوار کرا لے اور اپنے گھر کے لوگوں کو بھی، سوائے ان کے جن پر پہلے سے بات پڑ چکی ہے اور سب ایمان والوں کو بھی، اس کے ساتھ ایمان ﻻنے والے بہت ہی کم تھے'),
(1514, 11, 41, 'نوح ﴿علیہ السلام﴾ نے کہا، اس کشتی میں بیٹھ جاؤ اللہ ہی کے نام سے اس کا چلنا اور ٹھہرنا ہے، یقیناً میرا رب بڑی بخشش اور بڑے رحم واﻻ ہے'),
(1515, 11, 42, 'وه کشتی انہیں پہاڑوں جیسی موجوں میں لے کر جا رہی تھی اور نوح (علیہ السلام) نے اپنے لڑکے کو جو ایک کنارے پر تھا، پکار کر کہا کہ اے میرے پیارے بچے ہمارے ساتھ سوار ہو جا اور کافروں میں شامل نہ ره'),
(1516, 11, 43, 'اس نے جواب دیا کہ میں تو کسی بڑے پہاڑ کی طرف پناه میں آجاؤں گا جو مجھے پانی سے بچا لے گا، نوح ﴿علیہ السلام﴾ نے کہا آج اللہ کے امر سے بچانے واﻻ کوئی نہیں، صرف وہی بچیں گے جن پر اللہ کا رحم ہوا۔ اسی وقت ان دونوں کے درمیان موج حائل ہوگئی اور وه ڈوبنے والوں میں سے ہوگیا'),
(1517, 11, 44, 'فرما دیا گیا کہ اے زمین اپنے پانی کو نگل جا اور اے آسمان بس کر تھم جا، اسی وقت پانی سکھا دیا گیا اور کام پورا کر دیا گیا اور کشتی جودی نامی پہاڑ پر جا لگی اور فرما دیا گیا کہ ﻇالم لوگوں پر لعنت نازل ہو'),
(1518, 11, 45, 'نوح ﴿علیہ السلام﴾ نے اپنے پروردگار کو پکارا اور کہا کہ میرے رب میرا بیٹا تو میرے گھر والوں میں سے ہے، یقیناً تیرا وعده بالکل سچا ہے اور تو تمام حاکموں سے بہتر حاکم ہے'),
(1519, 11, 46, 'اللہ تعالیٰ نے فرمایا اے نوح یقیناً وه تیرے گھرانے سے نہیں ہے، اس کے کام بالکل ہی ناشائستہ ہیں تجھے ہرگز وه چیز نہ مانگنی چاہئے جس کا تجھے مطلقاً علم نہ ہو میں تجھے نصیحت کرتا ہوں کہ تو جاہلوں میں سے اپنا شمار کرانے سے باز رہے'),
(1520, 11, 47, 'نوح نے کہا میرے پالنہار میں تیری ہی پناه چاہتا ہوں اس بات سے کہ تجھ سے وه مانگوں جس کا مجھے علم ہی نہ ہو اگر تو مجھے نہ بخشے گا اور تو مجھ پر رحم نہ فرمائے گا، تو میں خساره پانے والوں میں ہو جاؤں گا'),
(1521, 11, 48, 'فرما دیا گیا کہ اے نوح! ہماری جانب سے سلامتی اور ان برکتوں کے ساتھ اتر، جو تجھ پر ہیں اور تیرے ساتھ کی بہت سی جماعتوں پر اور بہت سی وه امتیں ہوں گی جنہیں ہم فائده تو ضرور پہنچائیں گے لیکن پھر انہیں ہماری طرف سے دردناک عذاب پہنچے گا'),
(1522, 11, 49, 'یہ خبریں غیب کی خبروں میں سے ہیں جن کی وحی ہم آپ کی طرف کرتے ہیں انہیں اس سے پہلے آپ جانتے تھے اور نہ آپ کی قوم، اس لئے آپ صبر کرتے رہیئے (یقین مانیئے) کہ انجام کار پرہیزگاروں کے لئے ہی ہے'),
(1523, 11, 50, 'اور قوم عاد کی طرف ان کے بھائی ہود کو ہم نے بھیجا، اس نے کہا میری قوم والو! اللہ ہی کی عبادت کرو، اس کے سوا تمہارا کوئی معبود نہیں، تم تو صرف بہتان باندھ رہے ہو'),
(1524, 11, 51, 'اے میری قوم! میں تم سے اس کی کوئی اجرت نہیں مانگتا، میرا اجر اس کے ذمے ہے جس نے مجھے پیدا کیا ہے تو کیا پھر بھی تم عقل سے کام نہیں لیتے'),
(1525, 11, 52, 'اے میری قوم کے لوگو! تم اپنے پالنے والے سے اپنی تقصیروں کی معافی طلب کرو اور اس کی جناب میں توبہ کرو، تاکہ وه برسنے والے بادل تم پر بھیج دے اور تمہاری طاقت پر اور طاقت قوت بڑھا دے اور تم جرم کرتے ہوئے روگردانی نہ کرو'),
(1526, 11, 53, 'انہوں نے کہا اے ہود! تو ہمارے پاس کوئی دلیل تو ﻻیا نہیں اور ہم صرف تیرے کہنے سے اپنے معبودوں کو چھوڑنے والے نہیں اور نہ ہم تجھ پر ایمان ﻻنے والے ہیں'),
(1527, 11, 54, 'بلکہ ہم تو یہی کہتے ہیں کہ تو ہمارے کسی معبود کے برے جھپٹے میں آگیا ہے۔ اس نے جواب دیا کہ میں اللہ کو گواه کرتا ہوں اور تم بھی گواه رہو کہ میں تو اللہ کے سوا ان سب سے بیزار ہوں، جنہیں تم شریک بنا رہے ہو'),
(1528, 11, 55, 'اچھا تم سب مل کر میرے خلاف چالیں چل لو اور مجھے بالکل مہلت بھی نہ دو'),
(1529, 11, 56, 'میرا بھروسہ صرف اللہ تعالیٰ پر ہی ہے، جو میرا اور تم سب کا پروردگار ہے جتنے بھی پاؤں دھرنے والے ہیں سب کی پیشانی وہی تھامے ہوئے ہے۔ یقیناً میرا رب بالکل صحیح راه پر ہے'),
(1530, 11, 57, 'پس اگر تم روگردانی کرو تو کرو میں تو تمہیں وه پیغام پہنچا چکا جو دے کر مجھے تمہاری طرف بھیجا گیا تھا۔ میرا رب تمہارے قائم مقام اور لوگوں کو کر دے گا اور تم اس کا کچھ بھی بگاڑ نہ سکو گے، یقیناً میرا پروردگار ہر چیز پر نگہبان ہے'),
(1531, 11, 58, 'اور جب ہمارا حکم آپہنچا تو ہم نے ہود کو اور اس کے مسلمان ساتھیوں کو اپنی خاص رحمت سے نجات عطا فرمائی اور ہم نے ان سب کو سخت عذاب سے بچا لیا'),
(1532, 11, 59, 'یہ تھی قوم عاد، جنہوں نے اپنے رب کی آیتوں کا انکار کیا اور اس کے رسولوں کی نافرمانی کی اور ہر ایک سرکش نافرمان کے حکم کی تابعداری کی'),
(1533, 11, 60, 'دنیا میں بھی ان کے پیچھے لعنت لگا دی گئی اور قیامت کے دن بھی، دیکھ لو قوم عاد نے اپنے رب سے کفر کیا، ہود کی قوم عاد پر دوری ہو'),
(1534, 11, 61, 'اور قوم ﺛمود کی طرف ان کے بھائی صالح کو بھیجا، اس نے کہا کہ اے میری قوم تم اللہ کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں، اسی نے تمہیں زمین سے پیدا کیا ہے اور اسی نے اس زمین میں تمہیں بسایا ہے، پس تم اس سے معافی طلب کرو اور اس کی طرف رجوع کرو۔ بیشک میرا رب قریب اور دعاؤں کا قبول کرنے واﻻ ہے'),
(1535, 11, 62, 'انہوں نے کہا اے صالح! اس سے پہلے تو ہم تجھ سے بہت کچھ امیدیں لگائے ہوئے تھے، کیا تو ہمیں ان کی عبادتوں سے روک رہا ہے جن کی عبادت ہمارے باپ دادا کرتے چلے آئے، ہمیں تو اس دین میں حیران کن شک ہے جس کی طرف تو ہمیں بلا رہا ہے'),
(1536, 11, 63, 'اس نے جواب دیا کہ اے میری قوم کے لوگو! ذرا بتاؤ تو اگر میں اپنے رب کی طرف سے کسی مضبوط دلیل پر ہوا اور اس نے مجھے اپنے پاس کی رحمت عطا کی ہو، پھر اگر میں نے اس کی نافرمانی کر لی تو کون ہے جو اس کے مقابلے میں میری مدد کرے؟ تم تو میرا نقصان ہی بڑھا رہے ہو'),
(1537, 11, 64, 'اور اے میری قوم والو! یہ اللہ کی بھیجی ہوئی اونٹنی ہے جو تمہارے لئے ایک معجزه ہے اب تم اسے اللہ کی زمین میں کھاتی ہوئی چھوڑ دو اور اسے کسی طرح کی ایذا نہ پہنچاؤ ورنہ فوری عذاب تمہیں پکڑ لے گا'),
(1538, 11, 65, 'پھر بھی ان لوگوں نے اس اونٹنی کے پاؤں کاٹ ڈالے، اس پر صالح نے کہا کہ اچھا تم اپنے گھروں میں تین تین دن تک تو ره سہ لو، یہ وعده جھوٹا نہیں ہے'),
(1539, 11, 66, 'پھر جب ہمارا فرمان آپہنچا، ہم نے صالح کو اور ان پر ایمان ﻻنے والوں کو اپنی رحمت سے اس سے بھی بچا لیا اور اس دن کی رسوائی سے بھی۔ یقیناً تیرا رب نہایت توانا اور غالب ہے'),
(1540, 11, 67, 'اور ﻇالموں کو بڑے زور کی چنگھاڑ نے آدبوچا، پھر تو وه اپنے گھروں میں اوندھے پڑے ہوئے ره گئے'),
(1541, 11, 68, 'ایسے کہ گویا وه وہاں کبھی آباد ہی نہ تھے، آگاه رہو کہ قوم ﺛمود نے اپنے رب سے کفر کیا۔ سن لو! ان ﺛمودیوں پر پھٹکار ہے'),
(1542, 11, 69, 'اور ہمارے بھیجے ہوئے پیغامبر ابراہیم کے پاس خوشخبری لے کر پہنچے اور سلام کہا، انہوں نے بھی جواب سلام دیا اور بغیر کسی تاخیر کے گائے کا بھنا ہوا بچھڑا لے آئے'),
(1543, 11, 70, 'اب جو دیکھا کہ ان کے تو ہاتھ بھی اس کی طرف نہیں پہنچ رہے تو ان سے اجنبیت محسوس کرکے دل ہی دل میں ان سے خوف کرنے لگے، انہوں نے کہا ڈرو نہیں ہم تو قوم لوط کی طرف بھیجے ہوئے آئے ہیں'),
(1544, 11, 71, 'اس کی بیوی جو کھڑی ہوئی تھی وه ہنس پڑی، تو ہم نے اسے اسحاق کی اور اسحاق کے پیچھے یعقوب کی خوشخبری دی'),
(1545, 11, 72, 'وه کہنے لگی ہائے میری کم بختی! میرے ہاں اوﻻد کیسے ہو سکتی ہے میں خود بڑھیا اور یہ میرے خاوند بھی بہت بڑی عمر کے ہیں یہ تو یقیناً بڑی عجیب بات ہے!'),
(1546, 11, 73, 'فرشتوں نے کہا کیا تو اللہ کی قدرت سے تعجب کر رہی ہے؟ تم پر اے اس گھر کے لوگو اللہ کی رحمت اور اس کی برکتیں نازل ہوں، بیشک اللہ حمد وﺛنا کا سزاوار اور بڑی شان واﻻ ہے'),
(1547, 11, 74, 'جب ابراہیم کا ڈر خوف جاتا رہا اور اسے بشارت بھی پہنچ چکی تو ہم سے قوم لوط کے بارے میں کہنے سننے لگے'),
(1548, 11, 75, 'یقیناً ابراہیم بہت تحمل والے نرم دل اور اللہ کی جانب جھکنے والے تھے'),
(1549, 11, 76, 'اے ابراہیم! اس خیال کو چھوڑ دیجئے، آپ کے رب کا حکم آپہنچا ہے، اور ان پر نہ ٹالے جانے واﻻ عذاب ضرور آنے واﻻ ہے'),
(1550, 11, 77, 'جب ہمارے بھیجے ہوئے فرشتے لوط کے پاس پہنچے تو وه ان کی وجہ سے بہت غمگین ہوگئے اور دل ہی دل میں کڑھنے لگے اور کہنے لگے کہ آج کا دن بڑی مصیبت کا دن ہے'),
(1551, 11, 78, 'اور اس کی قوم دوڑتی ہوئی اس کے پاس آپہنچی، وه تو پہلے ہی سے بدکاریوں میں مبتلا تھی، لوط ﴿علیہ السلام﴾ نے کہا اے قوم کے لوگو! یہ ہیں میری بیٹیاں جو تمہارے لئے بہت ہی پاکیزه ہیں، اللہ سے ڈرو اور مجھے میرے مہمانوں کے بارے میں رسوا نہ کرو، کیا تم میں ایک بھی بھلا آدمی نہیں'),
(1552, 11, 79, 'انہوں نے جواب دیا کہ تو بخوبی جانتا ہے کہ ہمیں تو تیری بیٹیوں پر کوئی حق نہیں ہے اور تو ہماری اصلی چاہت سے بخوبی واقف ہے'),
(1553, 11, 80, 'لوط ﴿علیہ السلام﴾ نے کہا کاش کہ مجھ میں تم سے مقابلہ کرنے کی قوت ہوتی یا میں کسی زبردست کا آسرا پکڑ پاتا'),
(1554, 11, 81, 'اب فرشتوں نے کہا اے لوط! ہم تیرے پروردگار کے بھیجے ہوئے ہیں ناممکن ہے کہ یہ تجھ تک پہنچ جائیں پس تو اپنے گھر والوں کو لے کر کچھ رات رہے نکل کھڑا ہو۔ تم میں سے کسی کو مڑ کر بھی نہ دیکھنا چاہئے، بجز تیری بیوی کے، اس لئے کہ اسے بھی وہی پہنچنے واﻻ ہے جو ان سب کو پہنچے گا، یقیناً ان کے وعدے کا وقت صبح کا ہے، کیا صبح بالکل قریب نہیں'),
(1555, 11, 82, 'پھر جب ہمارا حکم آپہنچا، ہم نے اس بستی کو زیر وزبر کر دیا اوپر کا حصہ نیچے کر دیا اور ان پر کنکریلے پتھر برسائے جو تہ بہ تہ تھے'),
(1556, 11, 83, 'تیرے رب کی طرف سے نشان دار تھے اور وه ان ﻇالموں سے کچھ بھی دور نہ تھے'),
(1557, 11, 84, 'اور ہم نے مدین والوں کی طرف ان کے بھائی شعیب کو بھیجا، اس نے کہا اے میری قوم! اللہ کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں اور تم ناپ تول میں بھی کمی نہ کرو میں تو تمہیں آسوده حال دیکھ رہا ہوں اور مجھے تم پر گھیرنے والے دن کے عذاب کا خوف (بھی) ہے'),
(1558, 11, 85, 'اے میری قوم! ناپ تول انصاف کے ساتھ پوری پوری کرو لوگوں کو ان کی چیزیں کم نہ دو اور زمین میں فساد اور خرابی نہ مچاؤ'),
(1559, 11, 86, 'اللہ تعالی کا حلال کیا ہوا جو بچ رہے تمہارے لئے بہت ہی بہتر ہے اگر تم ایمان والے ہو، میں تم پر کچھ نگہبان (اور داروغہ) نہیں ہوں'),
(1560, 11, 87, 'انہوں نے جواب دیا کہ اے شعیب! کیا تیری صلاة تجھے یہی حکم دیتی ہے کہ ہم اپنے باپ دادوں کے معبودوں کو چھوڑ دیں اور ہم اپنے مالوں میں جو کچھ چاہیں اس کا کرنا بھی چھوڑ دیں تو تو بڑا ہی باوقار اور نیک چلن آدمی ہے'),
(1561, 11, 88, 'کہا اے میری قوم! دیکھو تو اگر میں اپنے رب کی طرف سے روشن دلیل لئے ہوئے ہوں اور اس نے مجھے اپنے پاس سے بہترین روزی دے رکھی ہے، میرا یہ اراده بالکل نہیں کہ تمہارا خلاف کر کے خود اس چیز کی طرف جھک جاؤں جس سے تمہیں روک رہا ہوں، میرا اراده تو اپنی طاقت بھر اصلاح کرنے کا ہی ہے۔ میری توفیق اللہ ہی کی مدد سے ہے، اسی پر میرا بھروسہ ہے اور اسی کی طرف میں رجوع کرتا ہوں'),
(1562, 11, 89, 'اور اے میری قوم (کے لوگو!) کہیں ایسا نہ ہو کہ تم کو میری مخالفت ان عذابوں کا مستحق بنا دے جو قوم نوح اور قوم ہود اور قوم صالح کو پہنچے ہیں۔ اور قوم لوط تو تم سے کچھ دور نہیں'),
(1563, 11, 90, 'تم اپنے رب سے استغفار کرو اور اس کی طرف توبہ کرو، یقین مانو کہ میرا رب بڑی مہربانی واﻻ اور بہت محبت کرنے واﻻ ہے'),
(1564, 11, 91, 'انہوں نے کہا اے شعیب! تیری اکثر باتیں تو ہماری سمجھ میں ہی نہیں آتیں اور ہم تو تجھے اپنے اندر بہت کمزور پاتے ہیں، اگر تیرے قبیلے کا خیال نہ ہوتا تو ہم تو تجھے سنگسار کر دیتے، اور ہم تجھے کوئی حیثیت والی ہستی نہیں گنتے'),
(1565, 11, 92, 'انہوں نے جواب دیا کہ اے میری قوم کے لوگو! کیا تمہارے نزدیک میرے قبیلے کے لوگ اللہ سے بھی زیاده ذی عزت ہیں کہ تم نے اسے پس پشت ڈال دیا ہے یقیناً میرا رب جو کچھ تم کر رہے ہو سب کو گھیرے ہوئے ہے'),
(1566, 11, 93, 'اے میری قوم کے لوگو! اب تم اپنی جگہ عمل کئے جاؤ میں بھی عمل کر رہا ہوں، تمہیں عنقریب معلوم ہو جائے گا کہ کس کے پاس وه عذاب آتا ہے جو اسے رسوا کر دے اور کون ہے جو جھوٹا ہے۔ تم انتظار کرو میں بھی تمہارے ساتھ منتظر ہوں'),
(1567, 11, 94, 'جب ہمارا حکم (عذاب) آپہنچا ہم نے شعیب کو اور ان کے ساتھ (تمام) مومنوں کو اپنی خاص رحمت سے نجات بخشی اور ﻇالموں کو سخت چنگھاڑ کے عذاب نے دھر دبوچا جس سے وه اپنے گھروں میں اوندھے پڑے ہوئے ہوگئے'),
(1568, 11, 95, 'گویا کہ وه ان گھروں میں کبھی بسے ہی نہ تھے، آگاه رہو مدین کے لئے بھی ویسی ہی دوری ہو جیسی دوری ﺛمود کو ہوئی'),
(1569, 11, 96, 'اور یقیناً ہم نے ہی موسیٰ کو اپنی آیات اور روشن دلیلوں کے ساتھ بھیجا تھا'),
(1570, 11, 97, 'فرعون اور اس کے سرداروں کی طرف، پھر بھی ان لوگوں نے فرعون کے احکام کی پیروی کی اور فرعون کا کوئی حکم درست تھا ہی نہیں'),
(1571, 11, 98, 'وه تو قیامت کے دن اپنی قوم کا پیش رو ہو کر ان سب کو دوزخ میں جا کھڑا کرے گا، وه بہت ہی برا گھاٹ ہے جس پر ﻻ کھڑے کئے جائیں گے'),
(1572, 11, 99, 'ان پر تو اس دنیا میں بھی لعنت چپکا دی گئی اور قیامت کے دن بھی برا انعام ہے جو دیا گیا'),
(1573, 11, 100, 'بستیوں کی یہ بعض خبریں جنہیں ہم تیرے سامنے بیان فرما رہے ہیں ان میں سے بعض تو موجود ہیں اور بعض (کی فصلیں) کٹ گئی ہیں'),
(1574, 11, 101, 'ہم نے ان پر کوئی ﻇلم نہیں کیا، بلکہ خود انہوں نے ہی اپنے اوپر ﻇلم کیا، اور انہیں ان کے معبودوں نے کوئی فائده نہ پہنچایا جنہیں وه اللہ کے سوا پکارا کرتے تھے، جب کہ تیرے پروردگار کا حکم آپہنچا، بلکہ اور ان کا نقصان ہی انہوں نے بڑھا دیا'),
(1575, 11, 102, 'تیرے پروردگار کی پکڑ کا یہی طریقہ ہے جب کہ وه بستیوں کے رہنے والے ﻇالموں کو پکڑتا ہے بیشک اس کی پکڑ دکھ دینے والی اور نہایت سخت ہے'),
(1576, 11, 103, 'یقیناً اس میں ان لوگوں کے لئے نشان عبرت ہے جو قیامت کے عذاب سے ڈرتے ہیں۔ وه دن جس میں سب لوگ جمع کئے جائیں گے اور وه، وه دن ہے جس میں سب حاضر کئے جائیں گے'),
(1577, 11, 104, 'اسے ہم جو ملتوی کرتے ہیں وه صرف ایک مدت معین تک ہے'),
(1578, 11, 105, 'جس دن وه آجائے گی مجال نہ ہوگی کہ اللہ کی اجازت کے بغیر کوئی بات بھی کر لے، سو ان میں کوئی بدبخت ہوگا اور کوئی نیک بخت'),
(1579, 11, 106, 'لیکن جو بدبخت ہوئے وه دوزخ میں ہوں گے وہاں چیخیں گے چلائیں گے'),
(1580, 11, 107, 'وه وہیں ہمیشہ رہنے والے ہیں جب تک آسمان وزمین برقرار رہیں۔ سوائے اس وقت کے جو تمہارا رب چاہے۔ یقیناً تیرا رب جو کچھ چاہے کر گزرتا ہے'),
(1581, 11, 108, 'لیکن جو نیک بخت کئے گئے وه جنت میں ہوں گے جہاں ہمیشہ رہیں گے جب تک آسمان وزمین باقی رہے مگر جو تیرا پروردگار چاہے۔ یہ بے انتہا بخشش ہے'),
(1582, 11, 109, 'اس لئے آپ ان چیزوں سے شک وشبہ میں نہ رہیں جنہیں یہ لوگ پوج رہے ہیں، ان کی پوجا تو اس طرح ہے جس طرح ان کے باپ دادوں کی اس سے پہلے تھی۔ ہم ان سب کو ان کا پورا پورا حصہ بغیر کسی کمی کے دینے والے ہی ہیں'),
(1583, 11, 110, 'یقیناً ہم نے موسیٰ (علیہ السلام) کو کتاب دی۔ پھر اس میں اختلاف کیا گیا، اگر پہلے ہی آپ کے رب کی بات صادر نہ ہوگئی ہوتی تو یقیناً ان کا فیصلہ کر دیا جاتا، انہیں تو اس میں سخت شبہ ہے'),
(1584, 11, 111, 'یقیناًان میں سے ہر ایک جب ان کے روبرو جائے گا تو آپ کا رب اسے اس کے اعمال کا پورا پورا بدلہ دے گا۔ بیشک وه جو کر رہے ہیں ان سے وه باخبر ہے'),
(1585, 11, 112, 'پس آپ جمے رہیئے جیسا کہ آپ کو حکم دیا گیا ہے اور وه لوگ بھی جو آپ کے ساتھ توبہ کر چکے ہیں، خبردار تم حد سے نہ بڑھنا، اللہ تمہارے تمام اعمال کا دیکھنے واﻻ ہے'),
(1586, 11, 113, 'دیکھو ﻇالموں کی طرف ہرگز نہ جھکنا ورنہ تمہیں بھی (دوزخ کی) آگ لگ جائے گی اور اللہ کے سوا اور تمہارا مددگار نہ کھڑا ہو سکے گا اور نہ تم مدد دیئے جاؤ گے'),
(1587, 11, 114, 'دن کے دونوں سروں میں نماز برپا رکھ اور رات کی کئی ساعتوں میں بھی، یقیناً نیکیاں برائیوں کو دور کر دیتی ہیں۔ یہ نصیحت ہے نصیحت پکڑنے والوں کے لئے'),
(1588, 11, 115, 'آپ صبر کرتے رہئے یقیناً اللہ تعالیٰ نیکی کرنے والوں کا اجر ضائع نہیں کرتا'),
(1589, 11, 116, 'پس کیوں نہ تم سے پہلے زمانے کے لوگوں میں سے ایسے اہل خیر لوگ ہوئے جو زمین میں فساد پھیلانے سے روکتے، سوائے ان چند کے جنہیں ہم نے ان میں سے نجات دی تھی، ﻇالم لوگ تو اس چیز کے پیچھے پڑ گئے جس میں انہیں آسودگی دی گئی تھی اور وه گنہگار تھے'),
(1590, 11, 117, 'آپ کا رب ایسا نہیں کہ کسی بستی کو ﻇلم سے ہلاک کر دے اور وہاں کے لوگ نیکو کار ہوں'),
(1591, 11, 118, 'اگر آپ کا پروردگار چاہتا تو سب لوگوں کو ایک ہی راه پر ایک گروه کر دیتا۔ وه تو برابر اختلاف کرنے والے ہی رہیں گے'),
(1592, 11, 119, 'بجز ان کے جن پر آپ کا رب رحم فرمائے، انہیں تو اسی لئے پیدا کیا ہے، اور آپ کے رب کی یہ بات پوری ہے کہ میں جہنم کو جنوں اور انسانوں سب سے پر کروں گا'),
(1593, 11, 120, 'رسولوں کے سب احوال ہم آپ کے سامنے آپ کے دل کی تسکین کے لئے بیان فرما رہے ہیں۔ آپ کے پاس اس سورت میں بھی حق پہنچ چکا جو نصیحت ووعﻆ ہے مومنوں کے لئے'),
(1594, 11, 121, 'ایمان نہ ﻻنے والوں سے کہہ دیجئے کہ تم اپنے طور پر عمل کئے جاؤ ہم بھی عمل میں مشغول ہیں'),
(1595, 11, 122, 'اور تم بھی انتظار کرو ہم بھی منتظر ہیں'),
(1596, 11, 123, 'زمینوں اور آسمانوں کا علم غیب اللہ تعالیٰ ہی کو ہے، تمام معاملات کا رجوع بھی اسی کی جانب ہے، پس تجھے اسی کی عبادت کرنی چاہئے اور اسی پر بھروسہ رکھنا چاہئے اور تم جو کچھ کرتے ہو اس سے اللہ تعالیٰ بے خبر نہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1597, 12, 1, 'الرٰ، یہ روشن کتاب کی آیتیں ہیں'),
(1598, 12, 2, 'یقیناً ہم نے اس کو قرآن عربی نازل فرمایا ہے کہ تم سمجھ سکو'),
(1599, 12, 3, 'ہم آپ کے سامنے بہترین بیان پیش کرتے ہیں اس وجہ سے کہ ہم نے آپ کی جانب یہ قرآن وحی کے ذریعے نازل کیا اور یقیناً آپ اس سے پہلے بے خبروں میں سے تھے'),
(1600, 12, 4, 'جب کہ یوسف نے اپنے باپ سے ذکر کیا کہ ابا جان میں نے گیاره ستاروں کو اور سورج چاند کو دیکھا کہ وه سب مجھے سجده کر رہے ہیں'),
(1601, 12, 5, 'یعقوب علیہ السلام نے کہا پیارے بچے! اپنے اس خواب کا ذکر اپنے بھائیوں سے نہ کرنا۔ ایسا نہ ہو کہ وه تیرے ساتھ کوئی فریب کاری کریں، شیطان تو انسان کا کھلا دشمن ہے'),
(1602, 12, 6, 'اور اسی طرح تجھے تیرا پروردگار برگزیده کرے گا اور تجھے معاملہ فہمی (یا خوابوں کی تعبیر) بھی سکھائے گا اور اپنی نعمت تجھے بھرپور عطا فرمائے گا اور یعقوب کے گھر والوں کو بھی، جیسے کہ اس نے اس سے پہلے تیرے دادا اور پردادا یعنی ابراہیم واسحاق کو بھی بھرپور اپنی نعمت دی، یقیناً تیرا رب بہت بڑے علم واﻻ اور زبردست حکمت واﻻ ہے'),
(1603, 12, 7, 'یقیناً یوسف اور اس کے بھائیوں میں دریافت کرنے والوں کے لئے (بڑی) نشانیاں ہیں'),
(1604, 12, 8, 'جب کہ انہوں نے کہا کہ یوسف اور اس کا بھائی بہ نسبت ہمارے، باپ کو بہت زیاده پیارے ہیں حاﻻنکہ ہم (طاقتور) جماعت ہیں کوئی شک نہیں کہ ہمارے ابا صریح غلطی میں ہیں'),
(1605, 12, 9, 'یوسف کو تو مار ہی ڈالو یا اسے کسی (نامعلوم) جگہ پھینک دو کہ تمہارے والد کا رخ صرف تمہاری طرف ہی ہو جائے۔ اس کے بعد تم نیک ہو جانا'),
(1606, 12, 10, 'ان میں سے ایک نے کہا یوسف کو قتل تو نہ کرو بلکہ اسے کسی اندھے کنوئیں (کی تہ) میں ڈال آؤ کہ اسے کوئی (آتا جاتا) قافلہ اٹھا لے جائے اگر تمہیں کرنا ہی ہے تو یوں کرو'),
(1607, 12, 11, 'انہوں نے کہا ابا! آخر آپ یوسف (علیہ السلام) کے بارے میں ہم پر اعتبار کیوں نہیں کرتے ہم تو اس کے خیر خواه ہیں'),
(1608, 12, 12, 'کل آپ اسے ضرور ہمارے ساتھ بھیج دیجئے کہ خوب کھائے پئے اور کھیلے، اس کی حفاﻇت کے ہم ذمہ دار ہیں'),
(1609, 12, 13, '(یعقوب علیہ السلام نے) کہا اسے تمہارا لے جانا مجھے تو سخت صدمہ دے گا اور مجھے یہ بھی کھٹکا لگا رہے گا کہ تمہاری غفلت میں اسے بھیڑیا کھا جائے'),
(1610, 12, 14, 'انہوں نے جواب دیا کہ ہم جیسی (زور آور) جماعت کی موجودگی میں بھی اگر اسے بھیڑیا کھا جائے تو ہم بالکل نکمے ہی ہوئے'),
(1611, 12, 15, 'پھر جب اسے لے چلے اور سب نے مل کر ٹھان لیا کہ اسے غیر آباد گہرے کنوئیں کی تہ میں پھینک دیں، ہم نے یوسف (علیہ السلام) کی طرف وحی کی کہ یقیناً (وقت آرہا ہے کہ) تو انہیں اس ماجرا کی خبر اس حال میں دے گا کہ وه جانتے ہی نہ ہوں'),
(1612, 12, 16, 'اور عشا کے وقت (وه سب) اپنے باپ کے پاس روتے ہوئے پہنچے'),
(1613, 12, 17, 'اور کہنے لگے کہ ابا جان ہم تو آپس میں دوڑ میں لگ گئے اور یوسف (علیہ السلام) کو ہم نے اسباب کے پاس چھوڑا پس اسے بھیڑیا کھا گیا، آپ تو ہماری بات نہیں مانیں گے، گو ہم بالکل سچے ہی ہوں'),
(1614, 12, 18, 'اور یوسف کے کرتے کو جھوٹ موٹ کے خون سے خون آلود بھی کر ﻻئے تھے، باپ نے کہا یوں نہیں، بلکہ تم نے اپنے دل ہی سے ایک بات بنا لی ہے۔ پس صبر ہی بہتر ہے، اور تمہاری بنائی ہوئی باتوں پر اللہ ہی سے مدد کی طلب ہے'),
(1615, 12, 19, 'اور ایک قافلہ آیا اور انہوں نے اپنے پانی ﻻنے والے کو بھیجا اس نے اپنا ڈول لٹکا دیا، کہنے لگا واه واه خوشی کی بات ہے یہ تو ایک لڑکا ہے، انہوں نے اسے مال تجارت قرار دے کر چھپا دیا اور اللہ تعالیٰ اس سے باخبر تھا جو وه کر رہے تھے'),
(1616, 12, 20, 'اور انہوں نے اسے بہت ہی ہلکی قیمت پر گنتی کے چند درہموں پر ہی بیچ ڈاﻻ، وه تو یوسف کے بارے میں بہت ہی بے رغبت تھے'),
(1617, 12, 21, 'مصر والوں میں سے جس نے اسے خریدا تھا اس نے اپنی بیوی سے کہا کہ اسے بہت عزت واحترام کے ساتھ رکھو، بہت ممکن ہے کہ یہ ہمیں فائده پہنچائے یا اسے ہم اپنا بیٹا ہی بنا لیں، یوں ہم نے مصر کی سرزمین میں یوسف کا قدم جما دیا کہ ہم اسے خواب کی تعبیر کا کچھ علم سکھا دیں۔ اللہ اپنے ارادے پر غالب ہے لیکن اکثر لوگ بے علم ہوتے ہیں'),
(1618, 12, 22, 'اور جب (یوسف) پختگی کی عمر کو پہنچ گئے ہم نے اسے قوت فیصلہ اور علم دیا، ہم نیک کاروں کو اسی طرح بدلہ دیتے ہیں'),
(1619, 12, 23, 'اس عورت نے جس کے گھر میں یوسف تھے، یوسف کو بہلانا پھسلانا شروع کیا کہ وه اپنے نفس کی نگرانی چھوڑ دے اور دروازے بند کرکے کہنے لگی لو آجاؤ یوسف نے کہا اللہ کی پناه! وه میرا رب ہے، مجھے اس نے بہت اچھی طرح رکھا ہے۔ بے انصافی کرنے والوں کا بھلا نہیں ہوتا'),
(1620, 12, 24, 'اس عورت نے یوسف کی طرف قصد کیا اور یوسف اس کا قصد کرتے اگر وه اپنے پروردگار کی دلیل نہ دیکھتے، یونہی ہوا اس واسطے کہ ہم اس سے برائی اور بے حیائی دور کر دیں۔ بے شک وه ہمارے چنے ہوئے بندوں میں سے تھا'),
(1621, 12, 25, 'دونوں دروازے کی طرف دوڑے اور اس عورت نے یوسف کا کرتا پیچھے کی طرف سے کھینچ کر پھاڑ ڈاﻻ اور دروازے کے پاس ہی عورت کا شوہر دونوں کو مل گیا، تو کہنے لگی جو شخص تیری بیوی کے ساتھ برا اراده کرے بس اس کی سزا یہی ہے کہ اسے قید کر دیا جائے یا اور کوئی دردناک سزا دی جائے۔'),
(1622, 12, 26, 'یوسف نے کہا یہ عورت ہی مجھے پھسلا رہی تھی، اور عورت کے قبیلے ہی کے ایک شخص نے گواہی دی کہ اگر اس کا کرتا آگے سے پھٹا ہوا ہو تو عورت سچی ہے اور یوسف جھوٹ بولنے والوں میں سے ہے'),
(1623, 12, 27, 'اور اگر اس کا کرتا پیچھے کی جانب سے پھاڑا گیاہے تو عورت جھوٹی ہے اور یوسف سچوں میں سے ہے'),
(1624, 12, 28, 'خاوند نے جو دیکھا کہ یوسف کا کرتا پیٹھ کی جانب سے پھاڑا گیا ہے تو صاف کہہ دیا کہ یہ تو تم عورتوں کی چال بازی ہے، بیشک تمہاری چال بازی بہت بڑی ہے۔'),
(1625, 12, 29, 'یوسف اب اس بات کو آتی جاتی کرو اور (اے عورت) تو اپنے گناه سے توبہ کر، بیشک تو گنہگاروں میں سے ہے۔'),
(1626, 12, 30, 'اور شہر کی عورتوں میں چرچا ہونے لگا کہ عزیز کی بیوی اپنے (جوان) غلام کو اپنا مطلب نکالنے کے لئے بہلانے پھسلانے میں لگی رہتی ہے، ان کے دل میں یوسف کی محبت بیٹھ گئی ہے، ہمارے خیال میں تو وه صریح گمراہی میں ہے'),
(1627, 12, 31, 'اس نے جب ان کی اس پر فریب غیبت کا حال سنا تو انہیں بلوا بھیجا اور ان کے لئے ایک مجلس مرتب کی اور ان میں سے ہر ایک کو چھری دی۔ اور کہا اے یوسف! ان کے سامنے چلے آؤ، ان عورتوں نے جب اسے دیکھا تو بہت بڑا جانا اور اپنے ہاتھ کاٹ لئے، اور زبان سے نکل گیا کہ حاشاللہ! یہ انسان تو ہرگز نہیں، یہ تو یقیناً کوئی بہت ہی بزرگ فرشتہ ہے'),
(1628, 12, 32, 'اس وقت عزیز مصر کی بیوی نے کہا، یہی ہے جن کے بارے میں تم مجھے طعنے دے رہی تھیں، میں نے ہر چند اس سے اپنا مطلب حاصل کرنا چاہا لیکن یہ بال بال بچا رہا، اور جو کچھ میں اس سے کہہ رہی ہوں اگر یہ نہ کرے گا تو یقیناً یہ قید کر دیا جائے گا اور بیشک یہ بہت ہی بے عزت ہوگا'),
(1629, 12, 33, 'یوسف نے دعا کی کہ اے میرے پروردگار! جس بات کی طرف یہ عورتیں مجھے بلا رہی ہیں اس سے تو مجھے جیل خانہ بہت پسند ہے، اگر تو نے ان کا فن فریب مجھ سے دور نہ کیا تو میں تو ان کی طرف مائل ہو جاؤں گا اور بالکل نادانوں میں جا ملوں گا'),
(1630, 12, 34, 'اس کے رب نے اس کی دعا قبول کر لی اور ان عورتوں کے داؤ پیچ اس سے پھیر دیے، یقیناً وه سننے واﻻ جاننے واﻻ ہے'),
(1631, 12, 35, 'پھر ان تمام نشانیوں کے دیکھ لینے کے بعد بھی انہیں یہی مصلحت معلوم ہوئی کہ یوسف کو کچھ مدت کے لئے قید خانہ میں رکھیں'),
(1632, 12, 36, 'اس کے ساتھ ہی دو اور جوان بھی جیل خانے میں داخل ہوئے، ان میں سے ایک نے کہا کہ میں نے خواب میں اپنے آپ کو شراب نچوڑتے دیکھا ہے، اور دوسرے نے کہا میں نے اپنے آپ کو دیکھا ہے کہ میں اپنے سر پر روٹی اٹھائے ہوئے ہوں جسے پرندے کھا رہے ہیں، ہمیں آپ اس کی تعبیر بتایئے، ہمیں تو آپ خوبیوں والے شخص دکھائی دیتے ہیں'),
(1633, 12, 37, 'یوسف ﴿علیہ السلام﴾ نے کہا تمہیں جو کھانا دیا جاتا ہے اس کے تمہارے پاس پہنچنے سے پہلے ہی میں تمہیں اس کی تعبیر بتلا دوں گا۔ یہ سب اس علم کی بدولت ہے جو مجھے میرے رب نے سکھایا ہے، میں نے ان لوگوں کا مذہب چھوڑ دیا ہے جو اللہ پر ایمان نہیں رکھتے اور آخرت کے بھی منکر ہیں'),
(1634, 12, 38, 'میں اپنے باپ دادوں کے دین کا پابند ہوں، یعنی ابراہیم واسحاق اور یعقوب کے دین کا، ہمیں ہرگز یہ سزاوار نہیں کہ ہم اللہ تعالیٰ کے ساتھ کسی کو بھی شریک کریں، ہم پر اور تمام اور لوگوں پر اللہ تعالیٰ کا یہ خاص فضل ہے، لیکن اکثر لوگ ناشکری کرتے ہیں'),
(1635, 12, 39, 'اے میرے قید خانے کے ساتھیو! کیا متفرق کئی ایک پروردگار بہتر ہیں؟ یا ایک اللہ زبردست طاقتور؟'),
(1636, 12, 40, 'اس کے سوا تم جن کی پوجا پاٹ کر رہے ہو وه سب نام ہی نام ہیں جو تم نے اور تمہارے باپ دادوں نے خود ہی گھڑ لئے ہیں۔ اللہ تعالیٰ نے ان کی کوئی دلیل نازل نہیں فرمائی، فرمانروائی صرف اللہ تعالیٰ ہی کی ہے، اس کا فرمان ہے کہ تم سب سوائے اس کے کسی اور کی عبادت نہ کرو، یہی دین درست ہے لیکن اکثر لوگ نہیں جانتے'),
(1637, 12, 41, 'اے میرے قیدخانے کے رفیقو! تم دونوں میں سے ایک تو اپنے بادشاه کو شراب پلانے پر مقرر ہو جائے گا، لیکن دوسرا سولی پر چڑھایا جائے گا اور پرندے اس کا سرنوچ نوچ کھائیں گے، تم دونوں جس کے بارے میں تحقیق کر رہے تھے اس کام کا فیصلہ کردیا گیا'),
(1638, 12, 42, 'اور جس کی نسبت یوسف کا گمان تھا کہ ان دونوں میں سے یہ چھوٹ جائے گا اس سے کہا کہ اپنے بادشاه سے میرا ذکر بھی کر دینا، پھر اسے شیطان نے اپنے بادشاه سے ذکر کرنا بھلا دیا اور یوسف نے کئی سال قیدخانے میں ہی کاٹے'),
(1639, 12, 43, 'بادشاه نے کہا، میں نے خواب میں دیکھا ہے کہ سات موٹی تازی فربہ گائیں ہیں جن کو سات ﻻغر دبلی پتلی گائیں کھا رہی ہیں اور سات بالیاں ہیں ہری ہری اور دوسری سات بالکل خشک۔ اے درباریو! میرے اس خواب کی تعبیر بتلاؤ اگر تم خواب کی تعبیر دے سکتے ہو'),
(1640, 12, 44, 'انہوں نے جواب دیا کہ یہ تو اڑتے اڑاتے پریشان خواب ہیں اور ایسے شوریده پریشان خوابوں کی تعبیر جاننے والے ہم نہیں'),
(1641, 12, 45, 'ان دو قیدیوں میں سے جو رہا ہوا تھا اسے مدت کے بعد یاد آگیا اور کہنے لگامیں تمہیں اس کی تعبیر بتلا دوں گا مجھے جانے کی اجازت دیجئے'),
(1642, 12, 46, 'اے یوسف! اے بہت بڑے سچے یوسف! آپ ہمیں اس خواب کی تعبیر بتلایئے کہ سات موٹی تازی گائیں ہیں جنہیں سات دبلی پتلی گائیں کھا رہی ہیں اور سات بالکل سبز خوشے ہیں اور سات ہی دوسرے بالکل خشک ہیں، تاکہ میں واپس جاکر ان لوگوں سے کہوں کہ وه سب جان لیں'),
(1643, 12, 47, 'یوسف نے جواب دیا کہ تم سات سال تک پے درپے لگاتار حسب عادت غلہ بویا کرنا، اور فصل کاٹ کر اسے بالیوں سمیت ہی رہنے دینا سوائے اپنے کھانے کی تھوڑی سی مقدار کے'),
(1644, 12, 48, 'اس کے بعد سات سال نہایت سخت قحط آئیں گے وه اس غلے کو کھا جائیں گے، جو تم نے ان کے لئے ذخیره رکھ چھوڑا تھا، سوائے اس تھوڑے سے کے جو تم روک رکھتے ہو'),
(1645, 12, 49, 'اس کے بعد جو سال آئے گا اس میں لوگوں پر خوب بارش برسائی جائے گی اور اس میں (شیرہٴ انگور بھی) خوب نچوڑیں گے'),
(1646, 12, 50, 'اور بادشاه نے کہا یوسف کو میرے پاس لاؤ، جب قاصد یوسف کے پاس پہنچا تو انہوں نے کہا، اپنے بادشاه کے پاس واپس جا اور اس سے پوچھ کہ ان عورتوں کا حقیقی واقعہ کیا ہے جنہوں نے اپنے ہاتھ کاٹ لئے، تھے؟ ان کے حیلے کو (صحیح طور پر) جاننے واﻻ میرا پروردگار ہی ہے'),
(1647, 12, 51, 'بادشاه نے پوچھا اے عورتو! اس وقت کا صحیح واقعہ کیا ہے جب تم داؤ فریب کر کے یوسف کو اس کی دلی منشا سے بہکانا چاہتی تھیں، انہوں نے صاف جواب دیا کہ معاذ اللہ ہم نے یوسف میں کوئی برائی نہیں پائی، پھر تو عزیز کی بیوی بھی بول اٹھی کہ اب تو سچی بات نتھر آئی۔ میں نے ہی اسے ورغلایا تھا، اس کے جی سے، اور یقیناً وه سچوں میں سے ہے'),
(1648, 12, 52, '(یوسف علیہ السلام نے کہا) یہ اس واسطے کہ (عزیز) جان لے کہ میں نے اس کی پیٹھ پیچھے اس کی خیانت نہیں کی اور یہ بھی کہ اللہ دغابازوں کے ہتھکنڈے چلنے نہیں دیتا'),
(1649, 12, 53, 'میں اپنے نفس کی پاکیزگی بیان نہیں کرتا۔ بیشک نفس تو برائی پر ابھارنے واﻻ ہی ہے، مگر یہ کہ میرا پروردگار ہی اپنا رحم کرے، یقیناً میرا پالنے واﻻ بڑی بخشش کرنے واﻻ اور بہت مہربانی فرمانے واﻻ ہے'),
(1650, 12, 54, 'بادشاه نے کہا اسے میرے پاس لاؤ کہ میں اسے اپنے خاص کاموں کے لئے مقرر کر لوں، پھر جب اس سے بات چیت کی تو کہنے لگا کہ آپ ہمارے ہاں آج سے ذی عزت اور امانت دار ہیں'),
(1651, 12, 55, '(یوسف نے) کہا آپ مجھے ملک کے خزانوں پر مقرر کر دیجئے، میں حفاﻇت کرنے واﻻ اور باخبر ہوں'),
(1652, 12, 56, 'اسی طرح ہم نے یوسف (علیہ السلام) کو ملک کا قبضہ دے دیا۔ کہ وه جہاں کہیں چاہے رہے سہے، ہم جسے چاہیں اپنی رحمت پہنچا دیتے ہیں۔ ہم نیکو کاروں کا ﺛواب ضائع نہیں کرتے'),
(1653, 12, 57, 'یقیناً ایمان داروں اور پرہیز گاروں کا اخروی اجر بہت ہی بہتر ہے'),
(1654, 12, 58, 'یوسف کے بھائی آئے اور یوسف کے پاس گئے تو اس نے انہیں پہچان لیا اور انہوں نے اسے نہ پہچانا'),
(1655, 12, 59, 'جب انہیں ان کا اسباب مہیا کر دیا تو کہا کہ تم میرے پاس اپنے اس بھائی کو بھی ﻻنا جو تمہارے باپ سے ہے، کیا تم نے نہیں دیکھا کہ میں پورا ناپ کر دیتا ہوں اور میں ہوں بھی بہترین میزبانی کرنے والوں میں'),
(1656, 12, 60, 'پس اگر تم اسے لے کر پاس نہ آئے تو میری طرف سے تمہیں کوئی ناپ بھی نہ ملے گا بلکہ تم میرے قریب بھی نہ پھٹکنا'),
(1657, 12, 61, 'انہوں نے کہا اچھا ہم اس کے باپ کو اس کی بابت پھسلائیں گے اور پوری کوشش کریں گے'),
(1658, 12, 62, 'اپنے خدمت گاروں سے کہا کہ ان کی پونجی انہی کی بوریوں میں رکھ دو کہ جب لوٹ کر اپنے اہل وعیال میں جائیں اور پونجیوں کو پہچان لیں تو بہت ممکن ہے کہ یہ پھر لوٹ کر آئیں'),
(1659, 12, 63, 'جب یہ لوگ لوٹ کر اپنے والد کے پاس گئے تو کہنے لگے کہ ہم سے تو غلہ کا ناپ روک لیا گیا۔ اب آپ ہمارے ساتھ ہمارے بھائی کو بھیجئے کہ ہم پیمانہ بھر کر ﻻئیں ہم اس کی نگہبانی کے ذمہ دار ہیں'),
(1660, 12, 64, '(یعقوب علیہ السلام نے) کہا کہ مجھے تو اس کی بابت تمہارا بس ویسا ہی اعتبار ہے جیسا اس سے پہلے اس کے بھائی کے بارے میں تھا، بس اللہ ہی بہترین حافﻆ ہے اور وه سب مہربانوں سے بڑا مہربان ہے'),
(1661, 12, 65, 'جب انہوں نے اپنا اسباب کھوﻻ تو اپنا سرمایہ موجود پایا جو ان کی جانب لوٹا دیا گیا تھا۔ کہنے لگے اے ہمارے باپ ہمیں اور کیا چاہیئے۔ دیکھئے تو یہ ہمارا سرمایہ بھی ہمیں واپس لوٹا دیا گیا ہے۔ ہم اپنے خاندان کو رسد ﻻدیں گے اور اپنے بھائی کی نگرانی رکھیں گے اور ایک اونٹ کے بوجھ کا غلہ زیاده ﻻئیں گے۔ یہ ناپ تو بہت آسان ہے'),
(1662, 12, 66, 'یعقوب (علیہ السلام) نے کہا! میں تو اسے ہرگز ہرگز تمہارے ساتھ نہ بھیجوں گا جب تک کہ تم اللہ کو بیچ میں رکھ کر مجھے قول وقرار نہ دو کہ تم اسے میرے پاس پہنچا دو گے، سوائے اس ایک صورت کے کہ تم سب گرفتار کر لئے جاؤ۔ جب انہوں نے پکا قول قرار دے دیا تو انہوں نے کہا کہ ہم جو کچھ کہتے ہیں اللہ اس پر نگہبان ہے'),
(1663, 12, 67, 'اور (یعقوب علیہ السلام) نے کہا اے میرے بچو! تم سب ایک دروازے سے نہ جانا بلکہ کئی جدا جدا دروازوں میں سے داخل ہونا۔ میں اللہ کی طرف سے آنے والی کسی چیز کو تم سے ٹال نہیں سکتا۔ حکم صرف اللہ ہی کا چلتا ہے۔ میرا کامل بھروسہ اسی پر ہے اور ہر ایک بھروسہ کرنے والے کو اسی پر بھروسہ کرنا چاہئے'),
(1664, 12, 68, 'جب وه انہی راستوں سے جن کا حکم ان کے والد نے انہیں دیا تھا، گئے۔ کچھ نہ تھا کہ اللہ نے جو بات مقرر کر دی ہے وه اس سے انہیں ذرا بھی بچا لے۔ مگر یعقوب (علیہ السلام) کے دل میں ایک خیال (پیدا ہوا) جسے اس نے پورا کر لیا، بلاشبہ وه ہمارے سکھلائے ہوئے علم کا عالم تھا لیکن اکثر لوگ نہیں جانتے'),
(1665, 12, 69, 'یہ سب جب یوسف کے پاس پہنچ گئے تو اس نے اپنے بھائی کو اپنے پاس بٹھا لیا اور کہا میں تیرا بھائی (یوسف) ہوں، پس یہ جو کچھ کرتے رہے اس کا کچھ رنج نہ کر'),
(1666, 12, 70, 'پھر جب انہیں ان کا سامان اسباب ٹھیک ٹھاک کرکے دیا تو اپنے بھائی کے اسباب میں پانی پینے کا پیالہ رکھ دیا۔ پھر ایک آواز دینے والے نے پکار کر کہا کہ اے قافلے والو! تم لوگ تو چور ہو'),
(1667, 12, 71, 'انہوں نے ان کی طرف منھ پھیر کر کہا کہ تمہاری کیا چیز کھوئی گئی ہے؟'),
(1668, 12, 72, 'جواب دیا کہ شاہی پیمانہ گم ہے جو اسے لے آئےاسے ایک اونٹ کے بوجھ کا غلہ ملے گا۔ اس وعدے کا میں ضامن ہوں'),
(1669, 12, 73, 'انہوں نے کہا اللہ کی قسم! تم کو خوب علم ہے کہ ہم ملک میں فساد پھیلانے کے لئے نہیں آئے اور نہ ہم چور ہیں'),
(1670, 12, 74, 'انہوں نے کہا اچھا چور کی کیا سزا ہے اگر تم جھوٹے ہو؟'),
(1671, 12, 75, 'جواب دیا کہ اس کی سزا یہی ہے کہ جس کے اسباب میں سے پایا جائے وہی اس کا بدلہ ہے۔ ہم تو ایسے ﻇالموں کو یہی سزا دیا کرتے ہیں'),
(1672, 12, 76, 'پس یوسف نے ان کے سامان کی تلاشی شروع کی، اپنے بھائی کے سامان کی تلاشی سے پہلے، پھر اس پیمانہ کو اپنے بھائی کے سامان (زنبیل) سے نکالا۔ ہم نے یوسف کے لئے اسی طرح یہ تدبیر کی۔ اس بادشاه کے قانون کی رو سے یہ اپنے بھائی کو نہ لے سکتا تھا مگر یہ کہ اللہ کو منظور ہو۔ ہم جس کے چاہیں درجے بلند کر دیں، ہر ذی علم پر فوقیت رکھنے واﻻ دوسرا ذی علم موجود ہے'),
(1673, 12, 77, 'انہوں نے کہا کہ اگر اس نے چوری کی (تو کوئی تعجب کی بات نہیں) اس کابھائی بھی پہلے چوری کر چکا ہے۔ یوسف (علیہ السلام) نے اس بات کو اپنے دل میں رکھ لیا اور ان کے سامنے بالکل ﻇاہر نہ کیا۔ کہا کہ تم بدتر جگہ میں ہو، اور جو تم بیان کرتے ہو اسے اللہ ہی خوب جانتا ہے'),
(1674, 12, 78, 'انہوں نے کہا کہ اے عزیز مصر! اس کے والد بہت بڑی عمر کے بالکل بوڑھے شخص ہیں۔ آپ اس کے بدلے ہم میں سے کسی کو لے لیجئے۔ ہم دیکھتے ہیں کہ آپ بڑے نیک نفس ہیں'),
(1675, 12, 79, 'یوسف (علیہ السلام) نے کہا ہم نے جس کے پاس اپنی چیز پائی ہے اس کے سوا دوسرے کی گرفتاری کرنے سے اللہ کی پناه چاہتے ہیں، ایسا کرنے سے تو ہم یقیناً ناانصافی کرنے والے ہو جائیں گے'),
(1676, 12, 80, 'جب یہ اس سے مایوس ہو گئے تو تنہائی میں بیٹھ کر مشوره کرنے لگے۔ ان میں جو سب سے بڑا تھا اس نے کہا تمہیں معلوم نہیں کہ تمہارے والد نے تم سے اللہ کی قسم لے کر پختہ قول قرار لیا ہے اور اس سے پہلے یوسف کے بارے میں تم کوتاہی کر چکے ہو۔ پس میں تو اس سرزمین سے نہ ٹلوں گا جب تک کہ والد صاحب خود مجھے اجازت نہ دیں یا اللہ تعالیٰ میرے معاملے کا فیصلہ کر دے، وہی بہترین فیصلہ کرنے واﻻ ہے'),
(1677, 12, 81, 'تم سب والد صاحب کی خدمت میں واپس جاؤ اور کہو کہ اباجی! آپ کے صاحبزادے نے چوری کی اور ہم نے وہی گواہی دی تھی جو ہم جانتے تھے۔ ہم کچھ غیب کی حفاﻇت کرنے والے نہ تھے'),
(1678, 12, 82, 'آپ اس شہر کے لوگوں سے دریافت فرما لیں جہاں ہم تھے اور اس قافلہ سے بھی پوچھ لیں جس کے ساتھ ہم آئے ہیں، اور یقیناً ہم بالکل سچے ہیں'),
(1679, 12, 83, '(یعقوب علیہ السلام نے) کہا یہ تو نہیں، بلکہ تم نے اپنی طرف سے بات بنالی، پس اب صبر ہی بہتر ہے۔ قریب ہے کہ اللہ تعالیٰ ان سب کو میرے پاس ہی پہنچا دے۔ وه ہی علم وحکمت واﻻ ہے'),
(1680, 12, 84, 'پھر ان سے منھ پھیر لیا اور کہا ہائے یوسف! ان کی آنکھیں بوجہ رنج وغم کے سفید ہو چکی تھیں اور وه غم کو دبائے ہوئے تھے'),
(1681, 12, 85, 'بیٹوں نے کہا واللہ! آپ ہمیشہ یوسف کی یاد ہی میں لگے رہیں گے یہاں تک کہ گھل جائیں یا ختم ہی ہو جائیں'),
(1682, 12, 86, 'انہوں نے کہا کہ میں تو اپنی پریشانیوں اور رنج کی فریاد اللہ ہی سے کر رہا ہوں، مجھے اللہ کی طرف سے وه باتیں معلوم ہیں جو تم نہیں جانتے'),
(1683, 12, 87, 'میرے پیارے بچو! تم جاؤ اور یوسف (علیہ السلام) کی اور اس کے بھائی کی پوری طرح تلاش کرو اور اللہ کی رحمت سے ناامید نہ ہو۔ یقیناً رب کی رحمت سے ناامید وہی ہوتے ہیں جو کافر ہوتے ہیں'),
(1684, 12, 88, 'پھر جب یہ لوگ یوسف (علیہ السلام) کے پاس پہنچے تو کہنے لگے کہ اے عزیز! ہم کو اور ہمارے خانداں کو دکھ پہنچا ہے۔ ہم حقیر پونجی ﻻئے ہیں پس آپ ہمیں پورے غلہ کا ناپ دیجئے اور ہم پر خیرات کیجئے، اللہ تعالیٰ خیرات کرنے والوں کو بدلہ دیتا ہے'),
(1685, 12, 89, 'یوسف نے کہا جانتے بھی ہو کہ تم نے یوسف اوراس کے بھائی کے ساتھ اپنی نادانی کی حالت میں کیا کیا؟'),
(1686, 12, 90, 'انہوں نے کہا کیا (واقعی) تو ہی یوسف (علیہ السلام) ہے۔ جواب دیا کہ ہاں میں یوسف (علیہ السلام) ہوں اور یہ میرا بھائی ہے۔ اللہ نے ہم پر فضل وکرم کیا، بات یہ ہے کہ جو بھی پرہیز گاری اور صبر کرے تو اللہ تعالیٰ کسی نیکوکار کا اجر ضائع نہیں کرتا'),
(1687, 12, 91, 'انہوں نے کہا اللہ کی قسم! اللہ تعالیٰ نے تجھے ہم پر برتری دی ہے اور یہ بھی بالکل سچ ہے کہ ہم خطا کار تھے'),
(1688, 12, 92, 'جواب دیا آج تم پر کوئی ملامت نہیں ہے۔ اللہ تمہیں بخشے، وه سب مہربانوں سے بڑا مہربان ہے'),
(1689, 12, 93, 'میرا یہ کرتا تم لے جاؤ اور اسے میرے والد کے منھ پر ڈال دو کہ وه دیکھنے لگیں، اور آجائیں اور اپنے تمام خاندان کو میرے پاس لےآؤ'),
(1690, 12, 94, 'جب یہ قافلہ جدا ہوا تو ان کے والد نے کہا کہ مجھے تو یوسف کی خوشبو آرہی ہے اگر تم مجھے سٹھیایا ہوا قرار نہ دو'),
(1691, 12, 95, 'وه کہنے لگے کہ واللہ آپ اپنے اسی پرانے خبط میں مبتلا ہیں'),
(1692, 12, 96, 'جب خوشخبری دینے والے نے پہنچ کر ان کے منھ پر وه کرتا ڈاﻻ اسی وقت وه پھر سے بینا ہوگئے۔ کہا! کیا میں تم سے نہ کہا کرتا تھا کہ میں اللہ کی طرف سے وه باتیں جانتا ہوں جو تم نہیں جانتے'),
(1693, 12, 97, 'انہوں نے کہا ابا جی! آپ ہمارے لئے گناہوں کی بخشش طلب کیجئے بیشک ہم قصور وار ہیں'),
(1694, 12, 98, 'کہا اچھا میں جلد ہی تمہارے لئے اپنے پروردگار سے بخشش مانگوں گا، وه بہت بڑا بخشنے واﻻ اور نہایت مہربانی کرنے وﻻ ہے'),
(1695, 12, 99, 'جب یہ سارا گھرانہ یوسف کے پاس پہنچ گیا تو یوسف نے اپنے ماں باپ کو اپنے پاس جگہ دی اور کہا کہ اللہ کو منظور ہے تو آپ سب امن وامان کے ساتھ مصر میں آؤ'),
(1696, 12, 100, 'اور اپنے تخت پر اپنے ماں باپ کو اونچا بٹھایا اور سب اس کے سامنے سجدے میں گر گئے۔ تب کہا کہ اباجی! یہ میرے پہلے کے خواب کی تعبیر ہے میرے رب نے اسے سچا کر دکھایا، اس نے میرے ساتھ بڑا احسان کیا جب کہ مجھے جیل خانے سے نکالا اور آپ لوگوں کو صحرا سے لے آیا اس اختلاف کے بعد جو شیطان نے مجھ میں اور میرے بھائیوں میں ڈال دیا تھا۔ میرا رب جو چاہے اس کے لئے بہترین تدبیر کرنے واﻻ ہے۔ اور وه بہت علم وحکمت واﻻ ہے'),
(1697, 12, 101, 'اے میرے پروردگار! تو نے مجھے ملک عطا فرمایا اور تو نے مجھے خواب کی تعبیر سکھلائی۔ اے آسمان وزمین کے پیدا کرنے والے! تو ہی دنیا وآخرت میں میرا ولی (دوست) اور کارساز ہے، تو مجھے اسلام کی حالت میں فوت کر اور نیکوں میں ملا دے'),
(1698, 12, 102, 'یہ غیب کی خبروں میں سے ہے جس کی ہم آپ کی طرف وحی کر رہے ہیں۔ آپ ان کے پاس نہ تھے جب کہ انہوں نے اپنی بات ٹھان لی تھی اور وه فریب کرنے لگے تھے'),
(1699, 12, 103, 'گو آپ لاکھ چاہیں لیکن اکثر لوگ ایمان دار نہ ہوں گے'),
(1700, 12, 104, 'آپ ان سے اس پر کوئی اجرت طلب نہیں کر رہے ہیں۔ یہ تو تمام دنیا کے لئے نری نصیحت ہی نصیحت ہے'),
(1701, 12, 105, 'آسمانوں اور زمین میں بہت سی نشانیاں ہیں۔ جن سے یہ منھ موڑے گزر جاتے ہیں'),
(1702, 12, 106, 'ان میں سے اکثر لوگ باوجود اللہ پر ایمان رکھنے کے بھی مشرک ہی ہیں'),
(1703, 12, 107, 'کیا وه اس بات سے بے خوف ہوگئے ہیں کہ ان کے پاس اللہ کے عذابوں میں سے کوئی عام عذاب آجائے یا ان پر اچانک قیامت ٹوٹ پڑے اور وه بے خبر ہی ہوں'),
(1704, 12, 108, 'آپ کہہ دیجئے میری راه یہی ہے۔ میں اور میرے متبعین اللہ کی طرف بلا رہے ہیں، پورے یقین اور اعتماد کے ساتھ۔ اور اللہ پاک ہے اور میں مشرکوں میں نہیں'),
(1705, 12, 109, 'آپ سے پہلے ہم نے بستی والوں میں جتنے رسول بھیجے ہیں سب مرد ہی تھے جن کی طرف ہم وحی نازل فرماتے گئے۔ کیا زمین میں چل پھر کر انہوں نے دیکھا نہیں کہ ان سے پہلے کے لوگوں کا کیسا کچھ انجام ہوا؟ یقیناً آخرت کا گھر پرہیزگاروں کے لئے بہت ہی بہتر ہے، کیا پھر بھی تم نہیں سمجھتے'),
(1706, 12, 110, 'یہاں تک کہ جب رسول ناامید ہونے لگے اور وه (قوم کے لوگ) خیال کرنے لگے کہ انہیں جھوٹ کہا گیا۔ فوراً ہی ہماری مدد ان کے پاس آپہنچی جسے ہم نے چاہا اسے نجات دی گئی۔ بات یہ ہے کہ ہمارا عذاب گناهگاروں سے واپس نہیں کیا جاتا'),
(1707, 12, 111, 'ان کے بیان میں عقل والوں کے لئے یقیناً نصیحت اور عبرت ہے، یہ قرآن جھوٹ بنائی ہوئی بات نہیں بلکہ یہ تصدیق ہے ان کتابوں کی جو اس سے پہلے کی ہیں، کھول کھول کر بیان کرنے واﻻ ہے ہر چیز کو اور ہدایت اور رحمت ہے ایمان دار لوگوں کے لئے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1708, 13, 1, 'ا ل م رٰ۔ یہ قرآن کی آیتیں ہیں، اور جو کچھ آپ کی طرف آپ کے رب کی جانب سے اتارا جاتا ہے، سب حق ہے لیکن اکثر لوگ ایمان نہیں ﻻتے'),
(1709, 13, 2, 'اللہ وه ہے جس نے آسمانوں کو بغیر ستونوں کے بلند کر رکھا ہے کہ تم اسے دیکھ رہے ہو۔ پھر وه عرش پر قرار پکڑے ہوئے ہے، اسی نے سورج اور چاند کو ماتحتی میں لگا رکھا ہے۔ ہر ایک میعاد معین پر گشت کر رہا ہے، وہی کام کی تدبیر کرتا ہے وه اپنے نشانات کھول کھول کر بیان کر رہا ہے کہ تم اپنے رب کی ملاقات کا یقین کر لو'),
(1710, 13, 3, 'اسی نے زمین پھیلا کر بچھا دی ہے اور اس میں پہاڑ اور نہریں پیدا کردی ہیں۔ اور اس میں ہر قسم کے پھلوں کے جوڑے دوہرے دوہرے پیدا کردیئے ہیں، وه رات کو دن سے چھپا دیتا ہے۔ یقیناً غور وفکر کرنے والوں کے لئے اس میں بہت سی نشانیاں ہیں'),
(1711, 13, 4, 'اور زمین میں مختلف ٹکڑے ایک دوسرے سے لگتے لگاتے ہیں اور انگوروں کے باغات ہیں اور کھیت ہیں اور کھجوروں کے درخت ہیں، شاخ دار اور بعض ایسے ہیں جو بے شاخ ہیں سب ایک ہی پانی پلائے جاتے ہیں۔ پھر بھی ہم ایک کو ایک پر پھلوں میں برتری دیتے ہیں اس میں عقل مندوں کے لئے بہت سی نشانیاں ہیں'),
(1712, 13, 5, 'اگر تجھے تعجب ہو تو واقعی ان کا یہ کہنا عجیب ہے کہ کیا جب ہم مٹی ہو جائیں گے۔ تو کیا ہم نئی پیدائش میں ہوں گے؟ یہی وه لوگ ہیں جنہوں نے اپنے پروردگار سے کفر کیا۔ یہی ہیں جن کی گردنوں میں طوق ہوں گے اور یہی ہیں جو جہنم کے رہنے والے ہیں جو اس میں ہمیشہ ہمیشہ رہیں گے'),
(1713, 13, 6, 'اور جو تجھ سے (سزا کی طلبی میں) جلد کر رہے ہیں راحت سے پہلے ہی، یقیناً ان سے پہلے سزائیں (بطور مثال) گزر چکی ہیں، اور بیشک تیرا رب البتہ بخشنے واﻻ ہے لوگوں کے بے جا ﻇلم پر بھی۔ اور یہ بھی یقینی بات ہے کہ تیرا رب بڑی سخت سزا دینے واﻻ بھی ہے'),
(1714, 13, 7, 'اور کافر کہتے ہیں کہ اس پر اس کے رب کی طرف سے کوئی نشانی (معجزه) کیوں نہیں اتاری گئی۔ بات یہ ہے کہ آپ تو صرف آگاه کرنے والے ہیں۔ اور ہر قوم کے لئے ہادی ہے'),
(1715, 13, 8, 'ماده اپنے شکم میں جو کچھ رکھتی ہے اسے اللہ بخوبی جانتا ہے اور پیٹ کا گھٹنا بڑھنا بھی، ہر چیز اس کے پاس اندازے سے ہے'),
(1716, 13, 9, 'ﻇاہر وپوشیده کا وه عالم ہے (سب سے) بڑا اور (سب سے) بلند وباﻻ'),
(1717, 13, 10, 'تم میں سے کسی کا اپنی بات کو چھپا کر کہنا اور بﺂواز بلند اسے کہنا اور جو رات کو چھپا ہوا ہو اور جو دن میں چل رہا ہو، سب اللہ پر برابر ویکساں ہیں'),
(1718, 13, 11, 'اس کے پہرے دار انسان کے آگے پیچھے مقرر ہیں، جو اللہ کے حکم سے اس کی نگہبانی کرتے ہیں۔ کسی قوم کی حالت اللہ تعالیٰ نہیں بدلتا جب تک کہ وه خود اسے نہ بدلیں جو ان کے دلوں میں ہے۔ اللہ تعالیٰ جب کسی قوم کی سزا کا اراده کر لیتا ہے تو وه بدﻻ نہیں کرتا اور سوائے اس کے کوئی بھی ان کا کارساز نہیں'),
(1719, 13, 12, 'وه اللہ ہی ہے جو تمہیں بجلی کی چمک ڈرانے اور امید دﻻنے کے لئے دکھاتا ہے اور بھاری بادلوں کو پیدا کرتا ہے'),
(1720, 13, 13, 'گرج اس کی تسبیح وتعریف کرتی ہے اور فرشتے بھی، اس کے خوف سے۔ وہی آسمان سے بجلیاں گراتا ہے اور جس پر چاہتا ہے اس پر ڈالتا ہے کفار اللہ کی بابت لڑ جھگڑ رہے ہیں اور اللہ سخت قوت والا ہے'),
(1721, 13, 14, 'اسی کو پکارنا حق ہے۔ جو لوگ اوروں کو اس کے سوا پکارتے ہیں وه ان (کی پکار) کا کچھ بھی جواب نہیں دیتے مگر جیسے کوئی شخص اپنے دونوں ہاتھ پانی کی طرف پھیلائے ہوئے ہو کہ اس کے منھ میں پڑ جائے حاﻻنکہ وه پانی اس کے منھ میں پہنچنے واﻻ نہیں، ان منکروں کی جتنی پکار ہے سب گمراہی میں ہے'),
(1722, 13, 15, 'اللہ ہی کے لئے زمین اور آسمانوں کی سب مخلوق خوشی اور ناخوشی سے سجده کرتی ہے اور ان کے سائے بھی صبح وشام'),
(1723, 13, 16, 'آپ پوچھئے کہ آسمانوں اور زمین کا پروردگار کون ہے؟ کہہ دیجئے! اللہ۔ کہہ دیجئے! کیا تم پھر بھی اس کے سوا اوروں کو حمایتی بنا رہے ہو جو خود اپنی جان کے بھی بھلے برے کا اختیار نہیں رکھتے۔ کہہ دیجئے کہ کیا اندھا اور بینا برابر ہو سکتا ہے؟ یا کیا اندھیریاں اور روشنی برابر ہو سکتی ہے۔ کیا جنہیں یہ اللہ کے شریک ٹھہرا رہے ہیں انہوں نے بھی اللہ کی طرح مخلوق پیدا کی ہے کہ ان کی نظر میں پیدائش مشتبہ ہوگئی ہو، کہہ دیجئے کہ صرف اللہ ہی تمام چیزوں کا خالق ہے وه اکیلا ہے اور زبردست غالب ہے'),
(1724, 13, 17, 'اسی نے آسمان سے پانی برسایا پھر اپنی اپنی وسعت کے مطابق نالے بہہ نکلے۔ پھر پانی کے ریلے نے اوپر چڑھے جھاگ کو اٹھا لیا، اور اس چیز میں بھی جس کو آگ میں ڈال کر تپاتے ہیں زیور یا ساز وسامان کے لئے اسی طرح کے جھاگ ہیں، اسی طرح اللہ تعالیٰ حق وباطل کی مثال بیان فرماتا ہے، اب جھاگ تو ناکاره ہو کر چلا جاتا ہے لیکن جو لوگوں کو نفع دینے والی چیز ہے وه زمین میں ٹھہری رہتی ہے، اللہ تعالیٰ اسی طرح مثالیں بیان فرماتا ہے'),
(1725, 13, 18, 'جن لوگوں نے اپنے رب کے حکم کی بجا آوری کی ان کے لئے بھلائی ہے اور جن لوگوں نے اس کی حکم برداری نہ کی اگر ان کے لئے زمین میں جو کچھ ہے سب کچھ ہو اور اسی کے ساتھ ویسا ہی اور بھی ہو تو وه سب کچھ اپنے بدلے میں دے دیں۔ یہی ہیں جن کے لئے برا حساب ہے اور جن کا ٹھکانہ جہنم ہے جو بہت بری جگہ ہے'),
(1726, 13, 19, 'کیا وه ایک شخص جو یہ علم رکھتا ہو کہ آپ کی طرف آپ کے رب کی جانب سے جو اتارا گیا ہے وه حق ہے، اس شخص جیسا ہو سکتا ہے جو اندھا ہو نصیحت تو وہی قبول کرتے ہیں جو عقلمند ہوں'),
(1727, 13, 20, 'جو اللہ کے عہد (وپیمان) کو پورا کرتے ہیں اور قول وقرار کو توڑتے نہیں'),
(1728, 13, 21, 'اور اللہ نے جن چیزوں کے جوڑنے کا حکم دیا ہے وه اسے جوڑتے ہیں اور وه اپنے پروردگار سے ڈرتے ہیں اور حساب کی سختی کا اندیشہ رکھتے ہیں'),
(1729, 13, 22, 'اور وه اپنے رب کی رضا مندی کی طلب کے لئے صبر کرتے ہیں، اور نمازوں کو برابر قائم رکھتے ہیں اور جو کچھ ہم نے انہیں دے رکھا ہے اسے چھپے کھلے خرچ کرتے ہیں اور برائی کو بھی بھلائی سے ٹالتے ہیں، ان ہی کے لئے عاقبت کا گھر ہے'),
(1730, 13, 23, 'ہمیشہ رہنے کے باغات جہاں یہ خود جائیں گے اور ان کے باپ دادوں اور بیویوں اور اوﻻدوں میں سے بھی جو نیکو کار ہوں گے، ان کے پاس فرشتے ہر ہر دروازے سے آئیں گے'),
(1731, 13, 24, 'کہیں گے کہ تم پر سلامتی ہو، صبر کے بدلے، کیا ہی اچھا (بدلہ) ہے اس دار آخرت کا'),
(1732, 13, 25, 'اور جو اللہ کے عہد کو اس کی مضبوطی کے بعد توڑ دیتے ہیں اور جن چیزوں کےجوڑنے کا اللہ نے حکم دیا ہے انہیں توڑتے ہیں اور زمین میں فساد پھیلاتے ہیں، ان کے لئے لعنتیں ہیں اور ان کے لئے برا گھر ہے'),
(1733, 13, 26, 'اللہ تعالیٰ جس کی روزی چاہتا ہے بڑھاتا ہے اور گھٹاتا ہے یہ تو دنیا کی زندگی میں مست ہو گئے۔ حاﻻنکہ دنیا آخرت کے مقابلے میں نہایت (حقیر) پونجی ہے'),
(1734, 13, 27, 'کافر کہتے ہیں کہ اس پر کوئی نشانی (معجزه) کیوں نازل نہیں کیا گیا؟ جواب دے دیجئے کہ جسے اللہ گمراه کرنا چاہے کر دیتا ہے اور جو اس کی طرف جھکے اسے راستہ دکھا دیتا ہے'),
(1735, 13, 28, 'جو لوگ ایمان ﻻئے ان کے دل اللہ کے ذکر سے اطمینان حاصل کرتے ہیں۔ یاد رکھو اللہ کے ذکر سے ہی دلوں کو تسلی حاصل ہوتی ہے'),
(1736, 13, 29, 'جو لوگ ایمان ﻻئے اور جنہوں نے نیک کام بھی کئے ان کے لئے خوشحالی ہے اور بہترین ٹھکانا'),
(1737, 13, 30, 'اسی طرح ہم نے آپ کو اس امت میں بھیجا ہے جس سے پہلے بہت سی امتیں گزر چکی ہیں کہ آپ انہیں ہماری طرف سے جو وحی آپ پر اتری ہے پڑھ کر سنایئے یہ اللہ رحمٰن کے منکر ہیں، آپ کہہ دیجئے کہ میرا پالنے واﻻ تو وہی ہے اس کے سوا درحقیقت کوئی بھی ﻻئق عبادت نہیں، اسی کے اوپر میرا بھروسہ ہے اور اسی کی جانب میرا رجوع ہے'),
(1738, 13, 31, 'اگر (بالفرض) کسی قرآن (آسمانی کتاب) کے ذریعے پہاڑ چلا دیئے جاتے یا زمین ٹکڑے ٹکڑے کر دی جاتی یا مردوں سے باتیں کرا دی جاتیں (پھر بھی وه ایمان نہ ﻻتے)، بات یہ ہے کہ سب کام اللہ کے ہاتھ میں ہے، تو کیا ایمان والوں کو اس بات پر دل جمعی نہیں کہ اگر اللہ تعالیٰ چاہے تو تمام لوگوں کو ہدایت دے دے۔ کفار کو تو ان کے کفر کے بدلے ہمیشہ ہی کوئی نہ کوئی سخت سزا پہنچتی رہے گی یا ان کے مکانوں کے قریب نازل ہوتی رہے گی تاوقتیکہ وعدہٴ الٰہی آپہنچے۔ یقیناً اللہ تعالیٰ وعده خلافی نہیں کرتا'),
(1739, 13, 32, 'یقیناً آپ سے پہلے کے پیغمبروں کا مذاق اڑایا گیا تھا اور میں نے بھی کافروں کو ڈھیل دی تھی پھر انہیں پکڑ لیا تھا، پس میرا عذاب کیسا رہا؟'),
(1740, 13, 33, 'آیا وه اللہ جو نگہبانی کرنے واﻻ ہے ہر شخص کی، اس کے کئے ہوئے اعمال پر، ان لوگوں نے اللہ کے شریک ٹھہرائے ہیں کہہ دیجئے ذرا ان کے نام تو لو، کیا تم اللہ کو وه باتیں بتاتے ہو جو وه زمین میں جانتا ہی نہیں، یا صرف اوپری اوپری باتیں بتا رہے ہو، بات اصل یہ ہے کہ کفر کرنے والوں کے لئے ان کے مکر سجا دیئے گئے ہیں، اور وه صحیح راه سے روک دیئے گئے ہیں، اور جس کو اللہ گمراه کر دے اس کو راه دکھانے واﻻ کوئی نہیں'),
(1741, 13, 34, 'ان کے لئے دنیا کی زندگی میں بھی عذاب ہے، اور آخرت کا عذاب تو بہت ہی زیاده سخت ہے، انہیں اللہ کے غضب سے بچانے واﻻ کوئی بھی نہیں'),
(1742, 13, 35, 'اس جنت کی صفت، جس کا وعده پرہیزگاروں کو دیا گیا ہے یہ ہے کہ اس کے نیچے سے نہریں بہہ رہی ہیں۔ اس کا میوه ہمیشگی واﻻ ہے اور اس کا سایہ بھی۔ یہ ہے انجام پرہیزگاروں کا، اور کافروں کا انجام کار دوزخ ہے'),
(1743, 13, 36, 'جنہیں ہم نے کتاب دی ہے وه تو جو کچھ آپ پر اتارا جاتا ہے اس سے خوش ہوتے ہیں اور دوسرے فرقے اس کی بعض باتوں کے منکر ہیں۔ آپ اعلان کر دیجئے کہ مجھے تو صرف یہی حکم دیا گیا ہے کہ میں اللہ کی عبادت کروں اور اس کے ساتھ شریک نہ کروں، میں اسی کی طرف بلا رہا ہوں اور اسی کی جانب میرا لوٹنا ہے'),
(1744, 13, 37, 'اسی طرح ہم نے اس قرآن کو عربی زبان کا فرمان اتارا ہے۔ اگر آپ نے ان کی خواہشوں کی پیروی کر لی اس کے بعد کہ آپ کے پاس علم آچکا ہے تو اللہ (کے عذابوں) سے آپ کو کوئی حمایتی ملے گا اور نہ بچانے واﻻ'),
(1745, 13, 38, 'ہم آپ سے پہلے بھی بہت سے رسول بھیج چکے ہیں اور ہم نے ان سب کو بیوی بچوں واﻻ بنایا تھا، کسی رسول سے نہیں ہو سکتا کہ کوئی نشانی بغیر اللہ کی اجازت کے لے آئے۔ ہر مقرره وعدے کی ایک لکھت ہے'),
(1746, 13, 39, 'اللہ جو چاہے مٹا دے اور جو چاہے ﺛابت رکھے، لوح محفوظ اسی کے پاس ہے'),
(1747, 13, 40, 'ان سے کیے ہوئے وعدوں میں سے کوئی اگر ہم آپ کو دکھا دیں یا آپ کو ہم فوت کر لیں تو آپ پر تو صرف پہنچا دینا ہی ہے۔ حساب تو ہمارے ذمہ ہی ہے'),
(1748, 13, 41, 'کیا وه نہیں دیکھتے؟ کہ ہم زمین کو اس کے کناروں سے گھٹاتے چلے آرہے ہیں، اللہ حکم کرتا ہے کوئی اس کے احکام پیچھے ڈالنے واﻻ نہیں، وه جلد حساب لینے واﻻ ہے'),
(1749, 13, 42, 'ان سے پہلے لوگوں نے بھی اپنی مکاری میں کمی نہ کی تھی، لیکن تمام تدبیریں اللہ ہی کی ہیں، جو شخص جو کچھ کر رہا ہے اللہ کے علم میں ہے۔ کافروں کو ابھی معلوم ہو جائے گا کہ (اس) جہان کی جزا کس کے لئے ہے؟'),
(1750, 13, 43, 'یہ کافر کہتے ہیں کہ آپ اللہ کے رسول نہیں۔ آپ جواب دیجئے کہ مجھ میں اور تم میں اللہ گواہی دینے واﻻ کافی ہے اور وه جس کے پاس کتاب کا علم ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1751, 14, 1, 'الرٰ! یہ عالی شان کتاب ہم نے آپ کی طرف اتاری ہے کہ آپ لوگوں کو اندھیروں سے اجالے کی طرف ﻻئیں، ان کے پروردگار کے حکم سے، زبردست اور تعریفوں والے اللہ کی طرف'),
(1752, 14, 2, 'جس اللہ کا ہے جو کچھ آسمانوں اور زمین میں ہے۔ اور کافروں کے لئے تو سخت عذاب کی خرابی ہے'),
(1753, 14, 3, 'جو آخرت کے مقابلے میں دنیوی زندگی کو پسند رکھتے ہیں اور اللہ کی راه سے روکتے ہیں اور اس میں ٹیڑھ پن پیدا کرنا چاہتے ہیں۔ یہی لوگ پرلے درجے کی گمراہی میں ہیں'),
(1754, 14, 4, 'ہم نے ہر ہر نبی کو اس کی قومی زبان میں ہی بھیجا ہے تاکہ ان کے سامنے وضاحت سے بیان کر دے۔ اب اللہ جسے چاہے گمراه کر دے، اور جسے چاہے راه دکھا دے، وه غلبہ اور حکمت واﻻ ہے'),
(1755, 14, 5, '(یاد رکھو جب کہ) ہم نے موسیٰ کو اپنی نشانیاں دے کر بھیجا کہ تو اپنی قوم کو اندھیروں سے روشنی میں نکال اور انہیں اللہ کے احسانات یاد دﻻ۔ اس میں نشانیاں ہیں ہر ایک صبر شکر کرنے والے کے لئے'),
(1756, 14, 6, 'جس وقت موسیٰ نے اپنی قوم سے کہا کہ اللہ کے وه احسانات یاد کرو جو اس نے تم پر کیے ہیں، جبکہ اس نے تمہیں فرعونیوں سے نجات دی جو تمہیں بڑے دکھ پہنچاتے تھے۔ تمہارے لڑکوں کو قتل کرتے تھے اور تمہاری لڑکیوں کو زنده چھوڑتے تھے، اس میں تمہارے رب کی طرف سے تم پر بہت بڑی آزمائش تھی'),
(1757, 14, 7, 'اور جب تمہارے پروردگار نے تمہیں آگاه کر دیا کہ اگر تم شکر گزاری کرو گے تو بیشک میں تمہیں زیاده دوں گا اور اگر تم ناشکری کرو گے تو یقیناً میرا عذاب بہت سخت ہے'),
(1758, 14, 8, 'موسیٰ (علیہ السلام) نے کہا کہ اگر تم سب اور روئے زمین کے تمام انسان اللہ کی ناشکری کریں تو بھی اللہ بے نیاز اور تعریفوں واﻻ ہے'),
(1759, 14, 9, 'کیا تمہارے پاس تم سے پہلے کے لوگوں کی خبریں نہیں آئیں؟ یعنی قوم نوح کی اور عاد وﺛمود کی اور ان کے بعد والوں کی جنہیں سوائے اللہ تعالیٰ کے اور کوئی نہیں جانتا، ان کے پاس ان کے رسول معجزے ﻻئے، لیکن انہوں نے اپنے ہاتھ اپنے منھ میں دبالیے اور صاف کہہ دیا کہ جو کچھ تمہیں دے کر بھیجا گیا ہے ہم اس کے منکر ہیں اور جس چیز کی طرف تم ہمیں بلا رہے ہو ہمیں تو اس میں بڑا بھاری شبہ ہے'),
(1760, 14, 10, 'ان کے رسولوں نے انہیں کہا کہ کیا حق تعالیٰ کے بارے میں تمہیں شک ہے جو آسمانوں اور زمین کا بنانے واﻻ ہے وه تو تمہیں اس لئے بلا رہا ہے کہ تمہارے گناه معاف فرما دے، اور ایک مقرر وقت تک تمہیں مہلت عطا فرمائے، انہوں نے کہا کہ تم تو ہم جیسے ہی انسان ہو تم چاہتے ہو کہ ہمیں ان خداؤں کی عبادت سے روک دو جن کی عبادت ہمارے باپ دادا کرتے رہے۔ اچھا تو ہمارے سامنے کوئی کھلی دلیل پیش کرو'),
(1761, 14, 11, 'ان کے پیغمبروں نے ان سے کہا کہ یہ تو سچ ہے کہ ہم تم جیسے ہی انسان ہیں لیکن اللہ تعالیٰ اپنے بندوں میں سے جس پر چاہتا ہے اپنا فضل کرتا ہے۔ اللہ کے حکم کے بغیر ہماری مجال نہیں کہ ہم کوئی معجزه تمہیں ﻻ دکھائیں اور ایمان والوں کو صرف اللہ تعالیٰ ہی پر بھروسہ رکھنا چاہئے'),
(1762, 14, 12, 'آخر کیا وجہ ہے کہ ہم اللہ تعالیٰ پر بھروسہ نہ رکھیں جبکہ اسی نے ہمیں ہماری راہیں سمجھائی ہیں۔ واللہ جو ایذائیں تم ہمیں دو گے ہم ان پر صبر ہی کریں گے۔ توکل کرنے والوں کو یہی ﻻئق ہے کہ اللہ ہی پر توکل کریں'),
(1763, 14, 13, 'کافروں نے اپنے رسولوں سے کہا کہ ہم تمہیں ملک بدر کر دیں گے یا تم پھر سے ہمارے مذہب میں لوٹ آؤ۔ تو ان کے پروردگار نے ان کی طرف وحی بھیجی کہ ہم ان ﻇالموں کو ہی غارت کر دیں گے'),
(1764, 14, 14, 'اور ان کے بعد ہم خود تمہیں اس زمین میں بسائیں گے۔ یہ ہے ان کے لئے جو میرے سامنے کھڑے ہونے کا ڈر رکھیں اور میری وعید سے خوفزده رہیں'),
(1765, 14, 15, 'اور انہوں نے فیصلہ طلب کیا اور تمام سرکش ضدی لوگ نامراد ہوگئے'),
(1766, 14, 16, 'اس کے سامنے دوزخ ہے جہاں وه پیﭗ کا پانی پلایا جائے گا'),
(1767, 14, 17, 'جسے بمشکل گھونٹ گھونٹ پئے گا۔ پھر بھی اسے گلے سے اتار نہ سکے گا اور اسے ہر جگہ سے موت آتی دکھائی دے گی لیکن وه مرنے واﻻ نہیں۔ پھر اس کے پیچھے بھی سخت عذاب ہے'),
(1768, 14, 18, 'ان لوگوں کی مثال جنہوں نے اپنے پالنے والے سے کفر کیا، ان کے اعمال مثل اس راکھ کے ہیں جس پر تیز ہوا آندھی والے دن چلے۔ جو بھی انہوں نے کیا اس میں سے کسی چیز پر قادر نہ ہوں گے، یہی دور کی گمراہی ہے'),
(1769, 14, 19, 'کیا تو نے نہیں دیکھا کہ اللہ تعالیٰ نے آسمانوں کو اور زمین کو بہترین تدبیر کے ساتھ پیدا کیا ہے۔ اگر وه چاہے تو تم سب کو فنا کردے اور نئی مخلوق ﻻئے'),
(1770, 14, 20, 'اللہ پر یہ کام کچھ بھی مشکل نہیں'),
(1771, 14, 21, 'سب کے سب اللہ کے سامنے روبرو کھڑے ہوں گے۔ اس وقت کمزور لوگ بڑائی والوں سے کہیں گے کہ ہم تو تمہارے تابعدار تھے، تو کیا تم اللہ کے عذابوں میں سے کچھ عذاب ہم سے دور کرسکنے والے ہو؟ وه جواب دیں گے کہ اگر اللہ ہمیں ہدایت دیتا تو ہم بھی ضرور تمہاری رہنمائی کرتے، اب تو ہم پر بے قراری کرنا اور صبر کرنا دونوں ہی برابر ہے ہمارے لیے کوئی چھٹکارا نہیں'),
(1772, 14, 22, 'جب اور کام کا فیصلہ کردیا جائے گا تو شیطان کہے گا کہ اللہ نے تو تمہیں سچا وعده دیا تھا اور میں نے تم سے جو وعدے کیے تھے ان کے خلاف کیا، میرا تم پر کوئی دباؤ تو تھا ہی نہیں، ہاں میں نے تمہیں پکارا اور تم نے میری مان لی، پس تم مجھے الزام نہ لگاؤ بلکہ خود اپنے آپ کو ملامت کرو، نہ میں تمہارا فریاد رس اور نہ تم میری فریاد کو پہنچنے والے، میں تو سرے سے مانتا ہی نہیں کہ تم مجھے اس سے پہلے اللہ کا شریک مانتے رہے، یقیناً ﻇالموں کے لیے دردناک عذاب ہے'),
(1773, 14, 23, 'جو لوگ ایمان ﻻئے اور نیک عمل کیے وه ان جنتوں میں داخل کیے جائیں گے جن کے نیچے چشمے جاری ہیں جہاں انہیں ہمیشگی ہوگی اپنے رب کے حکم سے۔ جہاں ان کا خیر مقدم سلام سے ہوگا'),
(1774, 14, 24, 'کیا آپ نے نہیں دیکھا کہ اللہ تعالیٰ نے پاکیزه بات کی مثال کس طرح بیان فرمائی، مثل ایک پاکیزه درخت کے جس کی جڑ مضبوط ہے اور جس کی ٹہنیاں آسمان میں ہیں'),
(1775, 14, 25, 'جو اپنے پروردگار کے حکم سے ہر وقت اپنے پھل ﻻتا ہے، اور اللہ تعالیٰ لوگوں کے سامنے مثالیں بیان فرماتا ہے تاکہ وه نصیحت حاصل کریں'),
(1776, 14, 26, 'اور ناپاک بات کی مثال گندے درخت جیسی ہے جو زمین کے کچھ ہی اوپر سے اکھاڑ لیا گیا۔ اسے کچھ ﺛبات تو ہے نہیں'),
(1777, 14, 27, 'ایمان والوں کو اللہ تعالیٰ پکی بات کے ساتھ مضبوط رکھتا ہے، دنیا کی زندگی میں بھی اور آخرت میں بھی، ہاں ناانصاف لوگوں کو اللہ بہکا دیتا ہے اور اللہ جو چاہے کر گزرے'),
(1778, 14, 28, 'کیا آپ نے ان کی طرف نظر نہیں ڈالی جنہوں نے اللہ کی نعمت کے بدلے ناشکری کی اور اپنی قوم کو ہلاکت کے گھر میں ﻻ اتارا'),
(1779, 14, 29, 'یعنی دوزخ میں جس میں یہ سب جائیں گے، جو بدترین ٹھکانا ہے'),
(1780, 14, 30, 'انہوں نے اللہ کے ہمسر بنالیے کہ لوگوں کو اللہ کی راه سے بہکائیں۔ آپ کہہ دیجئے کہ خیر مزے کرلو تمہاری بازگشت تو آخر جہنم ہی ہے'),
(1781, 14, 31, 'میرے ایمان والے بندوں سے کہہ دیجئے کہ نمازوں کو قائم رکھیں اور جو کچھ ہم نے انہیں دے رکھا ہے اس میں سے کچھ نہ کچھ پوشیده اور ﻇاہر خرچ کرتے رہیں اس سے پہلے کہ وه دن آجائے جس میں نہ خرید وفروخت ہوگی نہ دوستی اور محبت'),
(1782, 14, 32, 'اللہ وه ہے جس نے آسمانوں اور زمین کو پیدا کیا ہے اور آسمانوں سے بارش برسا کر اس کے ذریعے سے تمہاری روزی کے لیے پھل نکالے ہیں اور کشتیوں کو تمہارے بس میں کردیا ہے کہ دریاؤں میں اس کے حکم سے چلیں پھریں۔ اسی نے ندیاں اور نہریں تمہارے اختیار میں کردی ہیں'),
(1783, 14, 33, 'اسی نے تمہارے لیے سورج چاند کو مسخر کردیا ہے کہ برابر ہی چل رہے ہیں اور رات دن کو بھی تمہارے کام میں لگا رکھا ہے'),
(1784, 14, 34, 'اسی نے تمہیں تمہاری منہ مانگی کل چیزوں میں سے دے رکھا ہے۔ اگر تم اللہ کے احسان گننا چاہو تو انہیں پورے گن بھی نہیں سکتے۔ یقیناً انسان بڑا ہی بے انصاف اور ناشکرا ہے'),
(1785, 14, 35, '(ابراہیم کی یہ دعا بھی یاد کرو) جب انہوں نے کہا کہ اے میرے پروردگار! اس شہر کو امن واﻻ بنادے، اور مجھے اور میری اوﻻد کو بت پرستی سے پناه دے۔'),
(1786, 14, 36, 'اے میرے پالنے والے معبود! انہوں نے بہت سے لوگوں کو راه سے بھٹکا دیا ہے۔ پس میری تابعداری کرنے واﻻ میرا ہے اور جو میری نافرمانی کرے تو تو بہت ہی معاف اور کرم کرنے واﻻ ہے۔'),
(1787, 14, 37, 'اے ہمارے پروردگار! میں نے اپنی کچھ اوﻻد اس بے کھیتی کی وادی میں تیرے حرمت والے گھر کے پاس بسائی ہے۔ اے ہمارے پروردگار! یہ اس لیے کہ وه نماز قائم رکھیں، پس تو کچھ لوگوں کے دلوں کو ان کی طرف مائل کردے۔ اور انہیں پھلوں کی روزیاں عنایت فرما تاکہ یہ شکر گزاری کریں'),
(1788, 14, 38, 'اے ہمارے پروردگار! تو خوب جانتا ہے جو ہم چھپائیں اور جو ﻇاہر کریں۔ زمین و آسمان کی کوئی چیز اللہ پر پوشیده نہیں'),
(1789, 14, 39, 'اللہ کا شکر ہے جس نے مجھے اس بڑھاپے میں اسماعیل واسحاق (علیہما السلام) عطا فرمائے۔ کچھ شک نہیں کہ میرا پالنہار اللہ دعاؤں کا سننے واﻻ ہے'),
(1790, 14, 40, 'اے میرے پالنے والے! مجھے نماز کا پابند رکھ اور میری اوﻻد سے بھی، اے ہمارے رب میری دعا قبول فرما'),
(1791, 14, 41, 'اے ہمارے پروردگار! مجھے بخش دے اور میرے ماں باپ کو بھی بخش اور دیگر مومنوں کو بھی بخش جس دن حساب ہونے لگے'),
(1792, 14, 42, 'ناانصافوں کے اعمال سے اللہ کو غافل نہ سمجھ وه تو انہیں اس دن تک مہلت دیے ہوئے ہے جس دن آنکھیں پھٹی کی پھٹی ره جائیں گی'),
(1793, 14, 43, 'وه اپنے سر اوپر اٹھائے دوڑ بھاگ کر رہے ہوں گے، خود اپنی طرف بھی ان کی نگاہیں نہ لوٹیں گی اور ان کے دل خالی اور اڑے ہوئے ہوں گے'),
(1794, 14, 44, 'لوگوں کو اس دن سے ہوشیار کردے جب کہ ان کے پاس عذاب آجائے گا، اور ﻇالم کہیں گے کہ اے ہمارے رب ہمیں بہت تھوڑے قریب کے وقت تک کی ہی مہلت دے کہ ہم تیری تبلیﻎ مان لیں اور تیرے پیغمبروں کی تابعداری میں لگ جائیں۔ کیا تم اس سے پہلے بھی قسمیں نہیں کھا رہے تھے؟ کہ تمہارے لیے دنیا سے ٹلنا ہی نہیں'),
(1795, 14, 45, 'اور کیا تم ان لوگوں کے گھروں میں رہتے سہتے نہ تھے جنہوں نے اپنی جانوں پر ﻇلم کیا اور کیا تم پر وه معاملہ کھلا نہیں کہ ہم نے ان کے ساتھ کیسا کچھ کیا۔ ہم نے تو (تمہارے سمجھانے کو) بہت سی مثالیں بیان کردی تھیں'),
(1796, 14, 46, 'یہ اپنی اپنی چالیں چل رہے ہیں اور اللہ کو ان کی تمام چالوں کا علم ہے اور ان کی چالیں ایسی نہ تھیں کہ ان سے پہاڑ اپنی جگہ سے ٹل جائیں'),
(1797, 14, 47, 'آپ ہرگز یہ خیال نہ کریں کہ اللہ اپنے نبیوں سے وعده خلافی کرے گا، اللہ بڑا ہی غالب اور بدلہ لینے واﻻ ہے'),
(1798, 14, 48, 'جس دن زمین اس زمین کے سوا اور ہی بدل دی جائے گی اور آسمان بھی، اور سب کے سب اللہ واحد غلبے والے کے روبرو ہوں گے'),
(1799, 14, 49, 'آپ اس دن گناه گاروں کو دیکھیں گے کہ زنجیروں میں ملے جلے ایک جگہ جکڑے ہوئے ہوں گے'),
(1800, 14, 50, 'ان کے لباس گندھک کے ہوں گے اور آگ ان کے چہروں پر بھی چڑھی ہوئی ہوگی'),
(1801, 14, 51, 'یہ اس لیے کہ اللہ تعالیٰ ہر شخص کو اس کے کیے ہوئے اعمال کا بدلہ دے، بیشک اللہ تعالیٰ کو حساب لیتے کچھ دیر نہیں لگنے کی'),
(1802, 14, 52, 'یہ قرآن تمام لوگوں کے لیے اطلاع نامہ ہے کہ اس کے ذریعہ سے وه ہوشیار کر دیے جائیں اور بخوبی معلوم کرلیں کہ اللہ ایک ہی معبود ہے اور تاکہ عقلمند لوگ سوچ سمجھ لیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1803, 15, 1, 'الرٰ، یہ کتاب الٰہی کی آیتیں ہیں اور کھلے اور روشن قرآن کی'),
(1804, 15, 2, 'وه بھی وقت ہوگا کہ کافر اپنے مسلمان ہونے کی آرزو کریں گے'),
(1805, 15, 3, 'آپ انہیں کھاتا، نفع اٹھاتا اور (جھوٹی) امیدوں میں مشغول ہوتا چھوڑ دیجئے یہ خود ابھی جان لیں گے'),
(1806, 15, 4, 'کسی بستی کو ہم نے ہلاک نہیں کیا مگر یہ کہ اس کے لیے مقرره نوشتہ تھا'),
(1807, 15, 5, 'کوئی گروه اپنی موت سے نہ آگے بڑھتا ہے نہ پیچھے رہتا ہے'),
(1808, 15, 6, 'انہوں نے کہا کہ اے وه شخص جس پر قرآن اتارا گیا ہے یقیناً تو تو کوئی دیوانہ ہے'),
(1809, 15, 7, 'اگر تو سچا ہی ہے تو ہمارے پاس فرشتوں کو کیوں نہیں ﻻتا'),
(1810, 15, 8, 'ہم فرشتوں کو حق کے ساتھ ہی اتارتے ہیں اور اس وقت وه مہلت دیئے گئے نہیں ہوتے'),
(1811, 15, 9, 'ہم نے ہی اس قرآن کو نازل فرمایا ہے اور ہم ہی اس کے محافﻆ ہیں'),
(1812, 15, 10, 'ہم نے آپ سے پہلے اگلی امتوں میں بھی اپنے رسول (برابر) بھیجے'),
(1813, 15, 11, 'اور (لیکن) جو بھی رسول آتا وه اس کا مذاق اڑاتے'),
(1814, 15, 12, 'گناه گاروں کے دلوں میں ہم اسی طرح یہی رچا دیا کرتے ہیں'),
(1815, 15, 13, 'وه اس پر ایمان نہیں ﻻتے اور یقیناً اگلوں کا طریقہ گزرا ہوا ہے'),
(1816, 15, 14, 'اور اگر ہم ان پر آسمان کا دروازه کھول بھی دیں اور یہ وہاں چڑھنے بھی لگ جائیں'),
(1817, 15, 15, 'تب بھی یہی کہیں گے کہ ہماری نظر بندی کر دی گئی ہے بلکہ ہم لوگوں پر جادو کر دیا گیا ہے'),
(1818, 15, 16, 'یقیناً ہم نے آسمان میں برج بنائے ہیں اور دیکھنے والوں کے لیے اسے سجا دیا گیا ہے'),
(1819, 15, 17, 'اور اسے ہر مردود شیطان سے محفوظ رکھا ہے'),
(1820, 15, 18, 'ہاں مگر جو چوری چھپے سننے کی کوشش کرے اس کے پیچھے دہکتا ہوا (کھلا شعلہ) لگتا ہے'),
(1821, 15, 19, 'اور زمین کو ہم نے پھیلا دیا ہے اور اس پر (اٹل) پہاڑ ڈال دیئے ہیں، اور اس میں ہم نے ہر چیز ایک معین مقدار سے اگا دی ہے'),
(1822, 15, 20, 'اور اسی میں ہم نے تمہاری روزیاں بنا دی ہیں اور جنہیں تم روزی دینے والے نہیں ہو'),
(1823, 15, 21, 'اور جتنی بھی چیزیں ہیں ان سب کے خزانے ہمارے پاس ہیں، اور ہم ہر چیز کو اس کے مقرره انداز سے اتارتے ہیں'),
(1824, 15, 22, 'اور ہم بھیجتے ہیں بوجھل ہوائیں، پھر آسمان سے پانی برسا کر وه تمہیں پلاتے ہیں اور تم اس کا ذخیره کرنے والے نہیں ہو'),
(1825, 15, 23, 'ہم ہی جلاتے اور مارتے ہیں اور ہم ہی (بالﺂخر) وارث ہیں'),
(1826, 15, 24, 'اور تم میں سے آگے بڑھنے والے اور پیچھے ہٹنے والے بھی ہمارے علم میں ہیں'),
(1827, 15, 25, 'آپ کا رب سب لوگوں کو جمع کرے گا یقیناً وه بڑی حکمتوں واﻻ بڑے علم واﻻ ہے'),
(1828, 15, 26, 'یقیناً ہم نے انسان کو کالی اور سڑی ہوئی کھنکھناتی مٹی سے، پیدا فرمایا ہے'),
(1829, 15, 27, 'اور اس سے پہلے جنات کو ہم نے لو والی آگ سے پیدا کیا'),
(1830, 15, 28, 'اور جب تیرے پروردگار نے فرشتوں سے فرمایا کہ میں ایک انسان کو کالی اور سڑی ہوئی کھنکھناتی مٹی سے پیدا کرنے واﻻ ہوں'),
(1831, 15, 29, 'تو جب میں اسے پورا بنا چکوں اور اس میں اپنی روح پھونک دوں تو تم سب اس کے لیے سجدے میں گر پڑنا'),
(1832, 15, 30, 'چنانچہ تمام فرشتوں نے سب کے سب نے سجده کر لیا'),
(1833, 15, 31, 'مگر ابلیس کے۔ کہ اس نے سجده کرنے والوں میں شمولیت کرنے سے (صاف) انکار کر دیا'),
(1834, 15, 32, '(اللہ تعالیٰ نے) فرمایا اے ابلیس تجھے کیا ہوا کہ تو سجده کرنے والوں میں شامل نہ ہوا؟'),
(1835, 15, 33, 'وه بوﻻ کہ میں ایسا نہیں کہ اس انسان کو سجده کروں جسے تو نے کالی اور سڑی ہوئی کھنکھناتی مٹی سے پیدا کیا ہے'),
(1836, 15, 34, 'فرمایا اب تو بہشت سے نکل جا کیوں کہ تو رانده درگاه ہے'),
(1837, 15, 35, 'اور تجھ پر میری پھٹکار ہے قیامت کے دن تک'),
(1838, 15, 36, 'کہنے لگا کہ اے میرے رب! مجھے اس دن تک کی ڈھیل دے کہ لوگ دوباره اٹھا کھڑے کیے جائیں'),
(1839, 15, 37, 'فرمایا کہ اچھا تو ان میں سے ہے جنہیں مہلت ملی ہے'),
(1840, 15, 38, 'روز مقرر کے وقت تک کی'),
(1841, 15, 39, '(شیطان نے) کہا کہ اے میرے رب! چونکہ تو نے مجھے گمراه کیا ہے مجھے بھی قسم ہے کہ میں بھی زمین میں ان کے لئے معاصی کو مزین کروں گا اور ان سب کو بہکاؤں گا بھی'),
(1842, 15, 40, 'سوائے تیرے ان بندوں کے جو منتخب کر لیے گئے ہیں'),
(1843, 15, 41, 'ارشاد ہوا کہ ہاں یہی مجھ تک پہنچنے کی سیدھی راه ہے'),
(1844, 15, 42, 'میرے بندوں پر تجھے کوئی غلبہ نہیں، لیکن ہاں جو گمراه لوگ تیری پیروی کریں'),
(1845, 15, 43, 'یقیناً ان سب کے وعدے کی جگہ جہنم ہے'),
(1846, 15, 44, 'جس کے سات دروازے ہیں۔ ہر دروازے کے لیے ان کا ایک حصہ بٹا ہوا ہے'),
(1847, 15, 45, 'پرہیزگار جنتی لوگ باغوں اور چشموں میں ہوں گے'),
(1848, 15, 46, '(ان سے کہا جائے گا) سلامتی اور امن کے ساتھ اس میں داخل ہو جاؤ'),
(1849, 15, 47, 'ان کے دلوں میں جو کچھ رنجش وکینہ تھا، ہم سب کچھ نکال دیں گے، وه بھائی بھائی بنے ہوئے ایک دوسرے کے آمنے سامنے تختوں پر بیٹھے ہوں گے'),
(1850, 15, 48, 'نہ تو وہاں انہیں کوئی تکلیف چھو سکتی ہے اور نہ وه وہاں سے کبھی نکالے جائیں گے'),
(1851, 15, 49, 'میرے بندوں کو خبر دے دو کہ میں بہت ہی بخشنے واﻻ اور بڑا ہی مہربان ہوں'),
(1852, 15, 50, 'اور ساتھ ہی میرے عذاب بھی نہایت دردناک ہیں'),
(1853, 15, 51, 'انہیں ابراہیم کے مہمانوں کا (بھی) حال سنا دو'),
(1854, 15, 52, 'کہ جب انہوں نے ان کے پاس آکر سلام کہا تو انہوں نے کہا کہ ہم کو تو تم سے ڈر لگتا ہے'),
(1855, 15, 53, 'انہوں نے کہا ڈرو نہیں، ہم تجھے ایک صاحب علم فرزند کی بشارت دیتے ہیں'),
(1856, 15, 54, 'کہا، کیا اس بڑھاپے کے آجانے کے بعد تم مجھے خوشخبری دیتے ہو! یہ خوشخبری تم کیسے دے رہے ہو؟'),
(1857, 15, 55, 'انہوں نے کہا ہم آپ کو بالکل سچی خوشخبری سناتے ہیں آپ مایوس لوگوں میں شامل نہ ہوں'),
(1858, 15, 56, 'کہا اپنے رب تعالیٰ کی رحمت سے ناامید تو صرف گمراه اور بہکے ہوئے لوگ ہی ہوتے ہیں'),
(1859, 15, 57, 'پوچھا کہ اللہ کے بھیجے ہوئے (فرشتو!) تمہارا ایسا کیا اہم کام ہے؟'),
(1860, 15, 58, 'انہوں نے جواب دیا کہ ہم مجرم قوم کی طرف بھیجے گئے ہیں'),
(1861, 15, 59, 'مگر خاندان لوط کہ ہم ان سب کو تو ضرور بچا لیں گے'),
(1862, 15, 60, 'سوائے اس (لوط) کی بیوی کے کہ ہم نے اسے رکنے اور باقی ره جانے والوں میں مقرر کر دیا ہے'),
(1863, 15, 61, 'جب بھیجے ہوئے فرشتے آل لوط کے پاس پہنچے'),
(1864, 15, 62, 'تو انہوں (لوط علیہ السلام) نے کہا تم لوگ تو کچھ انجان سے معلوم ہو رہے ہو'),
(1865, 15, 63, 'انہوں نے کہا نہیں بلکہ ہم تیرے پاس وه چیز ﻻئے ہیں جس میں یہ لوگ شک شبہ کر رہے تھے'),
(1866, 15, 64, 'ہم تو تیرے پاس (صریح) حق ﻻئے ہیں اور ہیں بھی بالکل سچے'),
(1867, 15, 65, 'اب تو اپنے خاندان سمیت اس رات کے کسی حصہ میں چل دے اور آپ ان کے پیچھے رہنا، اور (خبردار) تم میں سے کوئی (پیچھے) مڑکر بھی نہ دیکھے اور جہاں کا تمہیں حکم کیا جارہا ہے وہاں چلے جانا'),
(1868, 15, 66, 'اور ہم نے اس کی طرف اس بات کا فیصلہ کر دیا کہ صبح ہوتے ہوتے ان لوگوں کی جڑیں کاٹ دی جائیں گی'),
(1869, 15, 67, 'اور شہر والے خوشیاں مناتے ہوئے آئے'),
(1870, 15, 68, '(لوط علیہ السلام نے) کہا یہ لوگ میرے مہمان ہیں تم مجھے رسوا نہ کرو'),
(1871, 15, 69, 'اللہ تعالیٰ سے ڈرو اور مجھے رسوا نہ کرو'),
(1872, 15, 70, 'وه بولے کیا ہم نے تجھے دنیا بھر (کی ٹھیکیداری) سے منع نہیں کر رکھا؟'),
(1873, 15, 71, '(لوط علیہ السلام نے) کہا اگر تمہیں کرنا ہی ہے تو یہ میری بچیاں موجود ہیں'),
(1874, 15, 72, 'تیری عمر کی قسم! وه تو اپنی بدمستی میں سرگرداں تھے'),
(1875, 15, 73, 'پس سورج نکلتے نکلتے انہیں ایک بڑے زور کی آواز نے پکڑ لیا'),
(1876, 15, 74, 'بالﺂخر ہم نے اس شہر کو اوپر تلے کر دیا اور ان لوگوں پر کنکر والے پتھر برسائے'),
(1877, 15, 75, 'بلاشبہ بصیرت والوں کے لیے اس میں بہت سی نشانیاں ہیں'),
(1878, 15, 76, 'یہ بستی ایسی راه پر ہے جو برابر چلتی رہتی (عام گذرگاه) ہے۔'),
(1879, 15, 77, 'اور اس میں ایمان والوں کے لیے بڑی نشانی ہے'),
(1880, 15, 78, 'اَیکَہ بستی کے رہنے والے بھی بڑے ﻇالم تھے'),
(1881, 15, 79, 'جن سے (آخر) ہم نے انتقام لے ہی لیا۔ یہ دونوں شہر کھلے (عام) راستے پر ہیں'),
(1882, 15, 80, 'اور حِجر والوں نے بھی رسولوں کو جھٹلایا'),
(1883, 15, 81, 'اور ہم نے ان کو اپنی نشانیاں بھی عطا فرمائیں (لیکن) تاہم وه ان سے روگردانی ہی کرتے رہے'),
(1884, 15, 82, 'یہ لوگ پہاڑوں کو تراش تراش کر گھر بناتے تھے، بے خوف ہوکر'),
(1885, 15, 83, 'آخر انہیں بھی صبح ہوتے ہوتے چنگھاڑنے آدبوچا'),
(1886, 15, 84, 'پس ان کی کسی تدبیروعمل نے انہیں کوئی فائده نہ دیا'),
(1887, 15, 85, 'ہم نے آسمانوں اور زمین کو اور ان کے درمیان کی سب چیزوں کو حق کے ساتھ ہی پیدا فرمایا ہے، اور قیامت ضرور ضرور آنے والی ہے۔ پس تو حسن وخوبی (اور اچھائی) سے درگزر کر لے'),
(1888, 15, 86, 'یقیناً تیرا پروردگار ہی پیدا کرنے واﻻ اور جاننے واﻻ ہے'),
(1889, 15, 87, 'یقیناً ہم نے آپ کو سات آیتیں دے رکھی ہیں کہ دہرائی جاتی ہیں اور عظیم قرآن بھی دے رکھا ہے'),
(1890, 15, 88, 'آپ ہر گز اپنی نظریں اس چیز کی طرف نہ دوڑائیں، جس سے ہم نے ان میں سے کئی قسم کے لوگوں کو بہره مند کر رکھا ہے، نہ ان پر آپ افسوس کریں اور مومنوں کے لیے اپنے بازو جھکائے رہیں'),
(1891, 15, 89, 'اور کہہ دیجئے کہ میں تو کھلم کھلا ڈرانے واﻻ ہوں'),
(1892, 15, 90, 'جیسے کہ ہم نے ان تقسیم کرنے والوں پر اتارا'),
(1893, 15, 91, 'جنہوں نے اس کتاب الٰہی کے ٹکڑے ٹکڑے کر دیئے'),
(1894, 15, 92, 'قسم ہے تیرے پالنے والے کی! ہم ان سب سے ضرور باز پرس کریں گے'),
(1895, 15, 93, 'ہر اس چیز کی جو وه کرتے تھے'),
(1896, 15, 94, 'پس آپ اس حکم کو جو آپ کو کیا جارہا ہے کھول کر سنا دیجئے! اور مشرکوں سے منھ پھیر لیجئے'),
(1897, 15, 95, 'آپ سے جو لوگ مسخراپن کرتے ہیں ان کی سزا کے لیے ہم کافی ہیں'),
(1898, 15, 96, 'جو اللہ کے ساتھ دوسرے معبود مقرر کرتے ہیں انہیں عنقریب معلوم ہو جائے گا'),
(1899, 15, 97, 'ہمیں خوب علم ہے کہ ان کی باتوں سے آپ کا دل تنگ ہوتا ہے'),
(1900, 15, 98, 'آپ اپنے پروردگار کی تسبیح اور حمد بیان کرتے رہیں اور سجده کرنے والوں میں شامل ہو جائیں'),
(1901, 15, 99, 'اور اپنے رب کی عبادت کرتے رہیں یہاں تک کہ آپ کو موت آجائے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(1902, 16, 1, 'اللہ تعالیٰ کا حکم آپہنچا، اب اس کی جلدی نہ مچاؤ۔ تمام پاکی اس کے لیے ہے وه برتر ہے ان سب سے جنہیں یہ اللہ کے نزدیک شریک بتلاتے ہیں'),
(1903, 16, 2, 'وہی فرشتوں کو اپنی وحی دے کر اپنے حکم سے اپنے بندوں میں سے جس پر چاہتا ہے اتارتا ہے کہ تم لوگوں کو آگاه کر دو کہ میرے سوا اور کوئی معبود نہیں، پس تم مجھ سے ڈرو'),
(1904, 16, 3, 'اسی نے آسمانوں اور زمین کو حق کے ساتھ پیدا کیا وه اس سے بری ہے جو مشرک کرتے ہیں'),
(1905, 16, 4, 'اس نے انسان کو نطفے سے پیدا کیا پھر وه صریح جھگڑالو بن بیٹھا'),
(1906, 16, 5, 'اسی نے چوپائے پیدا کیے جن میں تمہارے لیے گرمی کے لباس ہیں اور بھی بہت سے نفع ہیں اور بعض تمہارے کھانے کے کام آتے ہیں'),
(1907, 16, 6, 'اور ان میں تمہاری رونق بھی ہے جب چرا کر ﻻؤ تب بھی اور جب چرانے لے جاؤ تب بھی'),
(1908, 16, 7, 'اور وه تمہارے بوجھ ان شہروں تک اٹھالے جاتے ہیں جہاں تم بغیر آدھی جان کیے پہنچ ہی نہیں سکتے تھے۔ یقیناً تمہارا رب بڑا ہی شفیق اور نہایت مہربان ہے'),
(1909, 16, 8, 'گھوڑوں کو، خچروں کو، گدھوں کو اس نے پیدا کیا کہ تم ان کی سواری لو اور وه باعﺚ زینت بھی ہیں۔ اور بھی وه ایسی بہت چیزیں پیدا کرتا ہے جن کا تمہیں علم بھی نہیں'),
(1910, 16, 9, 'اور اللہ پر سیدھی راه کا بتا دینا ہے اور بعض ٹیڑھی راہیں ہیں، اور اگر وه چاہتا تو تم سب کو راه راست پر لگا دیتا'),
(1911, 16, 10, 'وہی تمہارے فائدے کے لیے آسمان سے پانی برساتا ہے جسے تم پیتے بھی ہو اور اسی سے اگے ہوئے درختوں کو تم اپنے جانوروں کو چراتے ہو'),
(1912, 16, 11, 'اسی سے وه تمہارے لیے کھیتی اور زیتون اور کھجور اور انگور اور ہر قسم کے پھل اگاتا ہے بےشک ان لوگوں کے لیے تو اس میں بڑی نشانی ہے جو غوروفکر کرتے ہیں'),
(1913, 16, 12, 'اسی نے رات دن اور سورج چاند کو تمہارے لیے تابع کر دیا ہے اور ستارے بھی اسی کے حکم کے ماتحت ہیں۔ یقیناًاس میں عقلمند لوگوں کے لیے کئی ایک نشانیاں موجود ہیں'),
(1914, 16, 13, 'اور بھی بہت سی چیزیں طرح طرح کے رنگ روپ کی اس نے تمہارے لیے زمین پر پھیلا رکھی ہیں۔ بیشک نصیحت قبول کرنے والوں کے لیے اس میں بڑی بھاری نشانی ہے'),
(1915, 16, 14, 'اور دریا بھی اسی نے تمہارے بس میں کر دیے ہیں کہ تم اس میں سے (نکلا ہوا) تازه گوشت کھاؤ اور اس میں سے اپنے پہننے کے زیورات نکال سکو اور تم دیکھتے ہو کہ کشتیاں اس میں پانی چیرتی ہوئی (چلتی) ہیں اور اس لیے بھی کہ تم اس کا فضل تلاش کرو اور ہوسکتا ہے کہ تم شکر گزاری بھی کرو'),
(1916, 16, 15, 'اور اس نے زمین میں پہاڑ گاڑ دیے ہیں تاکہ تمہیں لے کر ہلے نہ، اور نہریں اور راہیں بنا دیں تاکہ تم منزل مقصود کو پہنچو'),
(1917, 16, 16, 'اور بھی بہت سی نشانیاں مقرر فرمائیں۔ اور ستاروں سے بھی لوگ راه حاصل کرتے ہیں'),
(1918, 16, 17, 'تو کیا وه جو پیدا کرتا ہے اس جیسا ہے جو پیدا نہیں کرسکتا؟ کیا تم بالکل نہیں سوچتے؟'),
(1919, 16, 18, 'اور اگر تم اللہ کی نعمتوں کا شمار کرنا چاہو تو تم اسے نہیں کر سکتے۔ بیشک اللہ بڑا بخشنے واﻻ مہربان ہے'),
(1920, 16, 19, 'اور جو کچھ تم چھپاؤ اور ﻇاہر کرو اللہ تعالیٰ سب کچھ جانتا ہے'),
(1921, 16, 20, 'اور جن جن کو یہ لوگ اللہ تعالیٰ کے سوا پکارتے ہیں وه کسی چیز کو پیدا نہیں کرسکتے، بلکہ وه خود پیدا کیے ہوئے ہیں'),
(1922, 16, 21, 'مردے ہیں زنده نہیں، انہیں تو یہ بھی شعور نہیں کہ کب اٹھائے جائیں گے'),
(1923, 16, 22, 'تم سب کا معبود صرف اللہ تعالیٰ اکیلا ہے اور آخرت پر ایمان نہ رکھنے والوں کے دل منکر ہیں اور وه خود تکبر سے بھرے ہوئے ہیں'),
(1924, 16, 23, 'بےشک وشبہ اللہ تعالیٰ ہر اس چیز کو، جسے وه لوگ چھپاتے ہیں اور جسے ﻇاہر کرتے ہیں، بخوبی جانتا ہے۔ وه غرور کرنے والوں کو پسند نہیں فرماتا'),
(1925, 16, 24, 'ان سے جب دریافت کیا جاتا ہے کہ تمہارے پروردگار نے کیا نازل فرمایا ہے؟ تو جواب دیتے ہیں کہ اگلوں کی کہانیاں ہیں'),
(1926, 16, 25, 'اسی کا نتیجہ ہوگا کہ قیامت کے دن یہ لوگ اپنے پورے بوجھ کے ساتھ ہی ان کے بوجھ کے بھی حصے دار ہوں گے جنہیں بے علمی سے گمراه کرتے رہے۔ دیکھو تو کیسا برا بوجھ اٹھا رہے ہیں'),
(1927, 16, 26, 'ان سے پہلے کے لوگوں نے بھی مکر کیا تھا، (آخر) اللہ نے (ان کے منصوبوں) کی عمارتوں کو جڑوں سے اکھیڑ دیا اور ان (کے سروں) پر (ان کی) چھتیں اوپر سے گر پڑیں، اور ان کے پاس عذاب وہاں سے آگیا جہاں کا انہیں وہم وگمان بھی نہ تھا'),
(1928, 16, 27, 'پھر قیامت والے دن بھی اللہ تعالیٰ انہیں رسوا کرے گا اور فرمائے گا کہ میرے وه شریک کہاں ہیں جن کے بارے میں تم لڑتے جھگڑتے تھے، جنہیں علم دیا گیا تھا وه پکار اٹھیں گے کہ آج تو کافروں کو رسوائی اور برائی چمٹ گئی'),
(1929, 16, 28, 'وه جو اپنی جانوں پر ﻇلم کرتے ہیں، فرشتے جب ان کی جان قبض کرنے لگتے ہیں اس وقت وه جھک جاتے ہیں کہ ہم برائی نہیں کرتے تھے۔ کیوں نہیں؟ اللہ تعالیٰ خوب جاننے واﻻ ہے جو کچھ تم کرتے تھے'),
(1930, 16, 29, 'پس اب تو ہمیشگی کے طور پر تم جہنم کے دروازوں میں داخل ہو جاؤ، پس کیا ہی برا ٹھکانا ہے غرور کرنے والوں کا'),
(1931, 16, 30, 'اور پرہیز گاروں سے پوچھا جاتا ہے کہ تمہارے پروردگار نے کیا نازل فرمایا ہے؟ تو وه جواب دیتے ہیں کہ اچھے سے اچھا۔ جن لوگوں نے بھلائی کی ان کے لیے اس دنیا میں بھلائی ہے، اور یقیناً آخرت کا گھر تو بہت ہی بہتر ہے، اور کیا ہی خوب پرہیز گاروں کا گھر ہے'),
(1932, 16, 31, 'ہمیشگی والے باغات جہاں وه جائیں گے جن کے نیچے نہریں بہہ رہی ہیں، جو کچھ یہ طلب کریں گے وہاں ان کے لیے موجود ہوگا۔ پرہیز گاروں کو اللہ تعالیٰ اسی طرح بدلے عطا فرماتا ہے'),
(1933, 16, 32, 'وه جن کی جانیں فرشتے اس حال میں قبض کرتے ہیں کہ وه پاک صاف ہوں کہتے ہیں کہ تمہارے لیے سلامتی ہی سلامتی ہے، جاؤ جنت میں اپنے ان اعمال کے بدلے جو تم کرتے تھے۔'),
(1934, 16, 33, 'کیا یہ اسی بات کا انتظار کر رہے ہیں کہ ان کے پاس فرشتے آجائیں یا تیرے رب کا حکم آجائے؟ ایسا ہی ان لوگوں نے بھی کیا تھا جو ان سے پہلے تھے۔ ان پر اللہ تعالیٰ نے کوئی ﻇلم نہیں کیا بلکہ وه خود اپنی جانوں پر ﻇلم کرتے رہے'),
(1935, 16, 34, 'پس ان کے برے اعمال کے نتیجے انہیں مل گئے اور جس کی ہنسی اڑاتے تھے اس نے ان کو گھیر لیا'),
(1936, 16, 35, 'مشرک لوگوں نے کہا کہ اگر اللہ تعالیٰ چاہتا تو ہم اور ہمارے باپ دادے اس کے سوا کسی اور کی عبادت ہی نہ کرتے، نہ اس کے فرمان کے بغیر کسی چیز کو حرام کرتے۔ یہی فعل ان سے پہلے کے لوگوں کا رہا۔ تو رسولوں پر تو صرف کھلم کھلا پیغام کا پہنچا دینا ہے'),
(1937, 16, 36, 'ہم نے ہر امت میں رسول بھیجا کہ (لوگو!) صرف اللہ کی عبادت کرو اور اس کے سوا تمام معبودوں سے بچو۔ پس بعض لوگوں کو تو اللہ تعالیٰ نے ہدایت دی اور بعض پر گمراہی ﺛابت ہوگئی، پس تم خود زمین میں چل پھر کر دیکھ لو کہ جھٹلانے والوں کا انجام کیسا کچھ ہوا؟'),
(1938, 16, 37, 'گو آپ ان کی ہدایت کے خواہش مند رہے ہیں لیکن اللہ تعالیٰ اسے ہدایت نہیں دیتا جسے گمراه کر دے اور نہ ان کا کوئی مددگار ہوتا ہے'),
(1939, 16, 38, 'وه لوگ بڑی سخت سخت قسمیں کھا کھا کر کہتے ہیں کہ مردوں کو اللہ تعالیٰ زنده نہیں کرے گا۔ کیوں نہیں ضرور زنده کرے گا یہ تو اس کا برحق ﻻزمی وعده ہے، لیکن اکثر لوگ جانتے نہیں'),
(1940, 16, 39, 'اس لیے بھی کہ یہ لوگ جس چیز میں اختلاف کرتے تھے اسے اللہ تعالیٰ صاف بیان کر دے اور اس لیے بھی کہ خود کافر اپنا جھوٹا ہونا جان لیں'),
(1941, 16, 40, 'ہم جب کسی چیز کا اراده کرتے ہیں تو صرف ہمارا یہ کہہ دینا ہوتا ہے کہ ہوجا، پس وه ہوجاتی ہے'),
(1942, 16, 41, 'جن لوگوں نے ﻇلم برداشت کرنے کے بعد اللہ کی راه میں ترک وطن کیا ہے ہم انہیں بہتر سے بہتر ٹھکانا دنیا میں عطا فرمائیں گے اور آخرت کا ﺛواب تو بہت ہی بڑا ہے، کاش کہ لوگ اس سے واقف ہوتے'),
(1943, 16, 42, 'وه جنہوں نے دامن صبر نہ چھوڑا اور اپنے پالنے والے ہی پر بھروسہ کرتے رہے'),
(1944, 16, 43, 'آپ سے پہلے بھی ہم مَردوں کو ہی بھیجتے رہے، جن کی جانب وحی اتارا کرتے تھے پس اگر تم نہیں جانتے تو اہل علم سے دریافت کر لو'),
(1945, 16, 44, 'دلیلوں اور کتابوں کے ساتھ، یہ ذکر (کتاب) ہم نے آپ کی طرف اتارا ہے کہ لوگوں کی جانب جو نازل فرمایا گیا ہے آپ اسے کھول کھول کر بیان کر دیں، شاید کہ وه غور وفکر کریں'),
(1946, 16, 45, 'بدترین داؤ پیچ کرنے والے کیا اس بات سے بے خوف ہوگئے ہیں کہ اللہ تعالیٰ انہیں زمین میں دھنسا دے یا ان کے پاس ایسی جگہ سے عذاب آجائے جہاں کا انہیں وہم وگمان بھی نہ ہو'),
(1947, 16, 46, 'یا انہیں چلتے پھرتے پکڑ لے۔ یہ کسی صورت میں اللہ تعالیٰ کو عاجز نہیں کر سکتے'),
(1948, 16, 47, 'یا انہیں ڈرا دھمکا کر پکڑ لے، پس یقیناً تمہارا پروردگار اعلیٰ شفقت اور انتہائی رحم واﻻ ہے'),
(1949, 16, 48, 'کیا انہوں نے اللہ کی مخلوق میں سے کسی کو بھی نہیں دیکھا؟ کہ اس کے سائے دائیں بائیں جھک جھک کر اللہ تعالیٰ کے سامنے سر بسجود ہوتے اور عاجزی کا اﻇہار کرتے ہیں'),
(1950, 16, 49, 'یقیناً آسمان وزمین کے کل جاندار اور تمام فرشتے اللہ تعالیٰ کے سامنے سجدے کرتے ہیں اور ذرا بھی تکبر نہیں کرتے'),
(1951, 16, 50, 'اور اپنے رب سے جو ان کے اوپر ہے، کپکپاتے رہتے ہیں اور جو حکم مل جائے اس کی تعمیل کرتے ہیں'),
(1952, 16, 51, 'اللہ تعالیٰ ارشاد فرما چکا ہے کہ دو معبود نہ بناؤ۔ معبود تو صرف وہی اکیلا ہے، پس تم سب صرف میرا ہی ڈر خوف رکھو'),
(1953, 16, 52, 'آسمانوں میں اور زمین میں جو کچھ ہے سب اسی کا ہے اور اسی کی عبادت ﻻزم ہے، کیا پھر تم اس کے سوا اوروں سے ڈرتے ہو؟'),
(1954, 16, 53, 'تمہارے پاس جتنی بھی نعمتیں ہیں سب اسی کی دی ہوئی ہیں، اب بھی جب تمہیں کوئی مصیبت پیش آجائے تو اسی کی طرف نالہ وفریاد کرتے ہو'),
(1955, 16, 54, 'اور جہاں اس نے وه مصیبت تم سے دفع کر دی تم میں سے کچھ لوگ اپنے رب کے ساتھ شرک کرنے لگ جاتے ہیں'),
(1956, 16, 55, 'کہ ہماری دی ہوئی نعمتوں کی ناشکری کریں۔ اچھا کچھ فائده اٹھالو آخر کار تمہیں معلوم ہو ہی جائے گا'),
(1957, 16, 56, 'اور جسے جانتے بوجھتے بھی نہیں اس کا حصہ ہماری دی ہوئی روزی میں سے مقرر کرتے ہیں، واللہ تمہارے اس بہتان کا سوال تم سے ضرور ہی کیا جائے گا'),
(1958, 16, 57, 'اور وه اللہ سبحانہ وتعالیٰ کے لیے لڑکیاں مقرر کرتے ہیں اور اپنے لیے وه جو اپنی خواہش کے مطابق ہو'),
(1959, 16, 58, 'ان میں سے جب کسی کو لڑکی ہونے کی خبر دی جائے تو اس کا چہره سیاه ہو جاتا ہے اور دل ہی دل میں گھٹنے لگتا ہے'),
(1960, 16, 59, 'اس بری خبر کی وجہ سے لوگوں سے چھپا چھپا پھرتا ہے۔ سوچتا ہے کہ کیا اس کو ذلت کے ساتھ لئے ہوئے ہی رہے یا اسے مٹی میں دبا دے، آه! کیا ہی برے فیصلے کرتے ہیں؟'),
(1961, 16, 60, 'آخرت پر ایمان نہ رکھنے والوں کی ہی بری مثال ہے، اللہ کے لیے تو بہت ہی بلند صفت ہے، وه بڑا ہی غالب اور باحکمت ہے'),
(1962, 16, 61, 'اگر لوگوں کے گناه پر اللہ تعالیٰ ان کی گرفت کرتا تو روئے زمین پر ایک بھی جاندار باقی نہ رہتا، لیکن وه تو انہیں ایک وقت مقرر تک ڈھیل دیتا ہے، جب ان کا وه وقت آجاتا ہے تو وه ایک ساعت نہ پیچھے ره سکتے ہیں اور نہ آگے بڑھ سکتے ہیں'),
(1963, 16, 62, 'اور وه اپنے لیے جو ناپسند رکھتے ہیں اللہ کے لیے ﺛابت کرتے ہیں اور ان کی زبانیں جھوٹی باتیں بیان کرتی ہیں کہ ان کے لیے خوبی ہے۔ نہیں نہیں، دراصل ان کے لیے آگ ہے اور یہ دوزخیوں کے پیش رو ہیں'),
(1964, 16, 63, 'واللہ! ہم نے تجھ سے پہلے کی امتوں کی طرف بھی اپنے رسول بھیجے لیکن شیطان نے ان کے اعمال بد ان کی نگاہوں میں آراستہ کر دیئے، وه شیطان آج بھی ان کا رفیق بنا ہوا ہے اور ان کے لیے درد ناک عذاب ہے'),
(1965, 16, 64, 'اس کتاب کو ہم نے آپ پر اس لیے اتارا ہے کہ آپ ان کے لیے ہر اس چیز کو واضح کر دیں جس میں وه اختلاف کر رہے ہیں اور یہ ایمان والوں کے لیے رہنمائی اور رحمت ہے'),
(1966, 16, 65, 'اور اللہ آسمان سے پانی برسا کر اس سے زمین کو اس کی موت کے بعد زنده کر دیتا ہے۔ یقیناً اس میں ان لوگوں کے لیے نشانی ہے جو سنیں'),
(1967, 16, 66, 'تمہارے لیے تو چوپایوں میں بھی بڑی عبرت ہے کہ ہم تمہیں اس کے پیٹ میں جو کچھ ہے اسی میں سے گوبر اور لہو کے درمیان سے خالص دودھ پلاتے ہیں جو پینے والوں کے لیے سہتا پچتا ہے'),
(1968, 16, 67, 'اور کھجور اور انگور کے درختوں کے پھلوں سے تم شراب بنا لیتے ہو اور عمده روزی بھی۔ جو لوگ عقل رکھتے ہیں ان کے لیے تو اس میں بہت بڑی نشانی ہے'),
(1969, 16, 68, 'آپ کے رب نے شہد کی مکھی کے دل میں یہ بات ڈال دی کہ پہاڑوں میں درختوں اور لوگوں کی بنائی ہوئی اونچی اونچی ٹٹیوں میں اپنے گھر (چھتے) بنا'),
(1970, 16, 69, 'اور ہر طرح کے میوے کھا اور اپنے رب کی آسان راہوں میں چلتی پھرتی ره، ان کے پیٹ سے رنگ برنگ کا مشروب نکلتا ہے، جس کے رنگ مختلف ہیں اور جس میں لوگوں کے لیے شفا ہے غوروفکر کرنے والوں کے لیے اس میں بھی بہت بڑی نشانی ہے'),
(1971, 16, 70, 'اللہ تعالیٰ نے ہی تم سب کو پیدا کیا ہے وہی پھر تمہیں فوت کرے گا، تم میں ایسے بھی ہیں جو بدترین عمر کی طرف لوٹائے جاتے ہیں کہ بہت کچھ جاننے بوجھنے کے بعد بھی نہ جانیں۔ بیشک اللہ دانا اور توانا ہے'),
(1972, 16, 71, 'اللہ تعالیٰ ہی نے تم میں سے ایک کو دوسرے پر روزی میں زیادتی دے رکھی ہے، پس جنہیں زیادتی دی گئی ہے وه اپنی روزی اپنے ماتحت غلاموں کو نہیں دیتے کہ وه اور یہ اس میں برابر ہو جائیں، تو کیا یہ لوگ اللہ کی نعمتوں کے منکر ہو رہے ہیں؟'),
(1973, 16, 72, 'اللہ تعالیٰ نے تمہارے لیے تم میں سے ہی تمہاری بیویاں پیدا کیں اور تمہاری بیویوں سے تمہارے لیے تمہارے بیٹے اور پوتے پیدا کیے اور تمہیں اچھی اچھی چیزیں کھانے کو دیں۔ کیا پھر بھی لوگ باطل پر ایمان ﻻئیں گے؟ اور اللہ تعالیٰ کی نعمتوں کی ناشکری کریں گے؟'),
(1974, 16, 73, 'اور وه اللہ تعالیٰ کے سوا ان کی عبادت کرتے ہیں جو آسمانوں اور زمین سے انہیں کچھ بھی تو روزی نہیں دے سکتے اور نہ کچھ قدرت رکھتے ہیں'),
(1975, 16, 74, 'پس اللہ تعالیٰ کے لیے مثالیں مت بناؤ اللہ تعالیٰ خوب جانتا ہے اور تم نہیں جانتے'),
(1976, 16, 75, 'اللہ تعالیٰ ایک مثال بیان فرماتا ہے کہ ایک غلام ہے دوسرے کی ملکیت کا، جو کسی بات کا اختیار نہیں رکھتا اور ایک اور شخص ہے جسے ہم نے اپنے پاس سے معقول روزی دے رکھی ہے، جس میں سے وه چھپے کھلے خرچ کرتا ہے۔ کیا یہ سب برابر ہوسکتے ہیں؟ اللہ تعالیٰ ہی کے لیے سب تعریف ہے، بلکہ ان میں سے اکثر نہیں جانتے'),
(1977, 16, 76, 'اللہ تعالیٰ ایک اور مثال بیان فرماتا ہے، دو شخصوں کی، جن میں سے ایک تو گونگا ہے اور کسی چیز پر اختیار نہیں رکھتا بلکہ وه اپنے مالک پر بوجھ ہے کہیں بھی اسے بھیجے وه کوئی بھلائی نہیں ﻻتا، کیا یہ اور وه جو عدل کا حکم دیتا ہے اور ہے بھی سیدھی راه پر، برابر ہوسکتے ہیں؟'),
(1978, 16, 77, 'آسمانوں اور زمین کا غیب صرف اللہ تعالیٰ ہی کو معلوم ہے۔ اور قیامت کا امر تو ایسا ہی ہے جیسے آنکھ کا جھپکنا، بلکہ اس سے بھی زیاده قریب۔ بیشک اللہ تعالیٰ ہر چیز پر قادر ہے'),
(1979, 16, 78, 'اللہ تعالیٰ نے تمہیں تمہاری ماؤں کے پیٹوں سے نکالا ہے کہ اس وقت تم کچھ بھی نہیں جانتے تھے، اسی نے تمہارے کان اور آنکھیں اور دل بنائے کہ تم شکر گزاری کرو'),
(1980, 16, 79, 'کیا ان لوگوں نے پرندوں کو نہیں دیکھا جو تابع فرمان ہو کر فضا میں ہیں، جنہیں بجز اللہ تعالیٰ کے کوئی اور تھامے ہوئے نہیں، بیشک اس میں ایمان ﻻنے والے لوگوں کے لیے بڑی نشانیاں ہیں'),
(1981, 16, 80, 'اور اللہ تعالیٰ نے تمہارے لیے تمہارے گھروں میں سکونت کی جگہ بنا دی ہے اور اسی نے تمہارے لیے چوپایوں کی کھالوں کے گھر بنا دیے ہیں، جنہیں تم ہلکا پھلکا پاتے ہو اپنے کوچ کے دن اور اپنے ٹھہرنے کے دن بھی، اور ان کی اون اور روؤں اور بالوں سے بھی اس نے بہت سے سامان اور ایک وقت مقرره تک کے لیے فائده کی چیزیں بنائیں'),
(1982, 16, 81, 'اللہ ہی نے تمہارے لیے اپنی پیدا کرده چیزوں میں سے سائے بنائے ہیں اور اسی نے تمہارے لیے پہاڑوں میں غار بنائے ہیں اور اسی نے تمہارے لیے کرتے بنائے ہیں جو تمہیں گرمی سے بچائیں اور ایسے کرتے بھی جو تمہیں لڑائی کے وقت کام آئیں۔ وه اسی طرح اپنی پوری پوری نعمتیں دے رہا ہے کہ تم حکم بردار بن جاؤ'),
(1983, 16, 82, 'پھر بھی اگر یہ منھ موڑے رہیں تو آپ پر صرف کھول کر تبلیﻎ کر دینا ہی ہے'),
(1984, 16, 83, 'یہ اللہ کی نعمتیں جانتے پہچانتے ہوئے بھی ان کے منکر ہو رہے ہیں، بلکہ ان میں سے اکثر ناشکرے ہیں'),
(1985, 16, 84, 'اور جس دن ہم ہر امت میں سے گواه کھڑا کریں گے پھر کافروں کو نہ اجازت دی جائے گی اور نہ ان سے توبہ کرنے کو کہا جائے گا'),
(1986, 16, 85, 'اور جب یہ ﻇالم عذاب دیکھ لیں گے پھر نہ تو ان سے ہلکا کیا جائے گا اور نہ وه ڈھیل دیے جائیں گے'),
(1987, 16, 86, 'اور جب مشرکین اپنے شریکوں کو دیکھ لیں گے تو کہیں گے اے ہمارے پروردگار! یہی ہمارے وه شریک ہیں جنہیں ہم تجھے چھوڑ کر پکارا کرتے تھے، پس وه انہیں جواب دیں گے کہ تم بالکل ہی جھوٹے ہو'),
(1988, 16, 87, 'اس دن وه سب (عاجز ہوکر) اللہ کے سامنے اطاعت کا اقرار پیش کریں گے اور جو بہتان بازی کیا کرتے تھے وه سب ان سے گم ہو جائے گی'),
(1989, 16, 88, 'جنہوں نے کفر کیا اور اللہ کی راه سے روکا ہم انہیں عذاب پر عذاب بڑھاتے جائیں گے، یہ بدلہ ہوگا ان کی فتنہ پردازیوں کا'),
(1990, 16, 89, 'اور جس دن ہم ہر امت میں انہی میں سے ان کے مقابلے پر گواه کھڑا کریں گے اور تجھے ان سب پر گواه بنا کر ﻻئیں گے اور ہم نے تجھ پر یہ کتاب نازل فرمائی ہے جس میں ہر چیز کا شافی بیان ہے، اور ہدایت اور رحمت اور خوشخبری ہے مسلمانوں کے لیے'),
(1991, 16, 90, 'اللہ تعالیٰ عدل کا، بھلائی کا اور قرابت داروں کے ساتھ سلوک کرنے کا حکم دیتا ہے اور بےحیائی کے کاموں، ناشائستہ حرکتوں اور ﻇلم وزیادتی سے روکتا ہے، وه خود تمہیں نصیحتیں کر رہا ہے کہ تم نصیحت حاصل کرو'),
(1992, 16, 91, 'اور اللہ کے عہد کو پورا کرو جب کہ تم آپس میں قول وقرار کرو اور قسموں کو ان کی پختگی کے بعد مت توڑو، حاﻻنکہ تم اللہ تعالیٰ کو اپنا ضامن ٹھہرا چکے ہو، تم جو کچھ کرتے ہو اللہ اس کو بخوبی جان رہا ہے'),
(1993, 16, 92, 'اور اس عورت کی طرح نہ ہو جاؤ جس نے اپنا سوت مضبوط کاتنے کے بعد ٹکڑے ٹکڑے کرکے توڑ ڈاﻻ، کہ تم اپنی قسموں کو آپس کے مکر کا باعﺚ ٹھراؤ، اس لیے کہ ایک گروه دوسرے گروه سے بڑھا چڑھا ہوجائے۔ بات صرف یہی ہے کہ اس عہد سے اللہ تمہیں آزما رہا ہے۔ یقیناً اللہ تعالیٰ تمہارے لیے قیامت کے دن ہر اس چیز کو کھول کر بیان کر دے گا جس میں تم اختلاف کر رہے تھے'),
(1994, 16, 93, 'اگر اللہ چاہتا تم سب کو ایک ہی گروه بنا دیتا لیکن وه جسے چاہے گمراه کرتا ہے اور جسے چاہے ہدایت دیتا ہے، یقیناً تم جو کچھ کر رہے ہو اس کے بارے میں باز پرس کی جانے والی ہے'),
(1995, 16, 94, 'اور تم اپنی قسموں کو آپس کی دغابازی کا بہانہ نہ بناؤ۔ پھر تو تمہارے قدم اپنی مضبوطی کے بعد ڈگمگا جائیں گے اور تمہیں سخت سزا برداشت کرنا پڑے گی کیونکہ تم نے اللہ کی راه سے روک دیا اور تمہیں بڑا سخت عذاب ہوگا'),
(1996, 16, 95, 'تم اللہ کے عہد کو تھوڑے مول کے بدلے نہ بیچ دیا کرو۔ یاد رکھو اللہ کے پاس کی چیز ہی تمہارے لیے بہتر ہے بشرطیکہ تم میں علم ہو'),
(1997, 16, 96, 'تمہارے پاس جو کچھ ہے سب فانی ہے اور اللہ تعالیٰ کے پاس جو کچھ ہے باقی ہے۔ اور صبر کرنے والوں کو ہم بھلے اعمال کا بہترین بدلہ ضرور عطا فرمائیں گے'),
(1998, 16, 97, 'جو شخص نیک عمل کرے مرد ہو یا عورت، لیکن باایمان ہو تو ہم اسے یقیناً نہایت بہتر زندگی عطا فرمائیں گے۔ اور ان کے نیک اعمال کا بہتر بدلہ بھی انہیں ضرور ضرور دیں گے'),
(1999, 16, 98, 'قرآن پڑھنے کے وقت راندے ہوئے شیطان سے اللہ کی پناه طلب کرو'),
(2000, 16, 99, 'ایمان والوں اور اپنے پروردگار پر بھروسہ رکھنے والوں پر اس کا زور مطلقاً نہیں چلتا'),
(2001, 16, 100, 'ہاں اس کا غلبہ ان پر تو یقیناً ہے جو اسی سے رفاقت کریں اور اسے اللہ کا شریک ٹھہرائیں'),
(2002, 16, 101, 'اور جب ہم کسی آیت کی جگہ دوسری آیت بدل دیتے ہیں اور جو کچھ اللہ تعالیٰ نازل فرماتا ہے اسے وه خوب جانتا ہے تو یہ کہتے ہیں کہ تو تو بہتان باز ہے۔ بات یہ ہے کہ ان میں سے اکثر جانتے ہی نہیں'),
(2003, 16, 102, 'کہہ دیجئے کہ اسے آپ کے رب کی طرف سے جبرائیل حق کے ساتھ لے کر آئے ہیں تاکہ ایمان والوں کو اللہ تعالیٰ استقامت عطا فرمائے اور مسلمانوں کی رہنمائی اور بشارت ہو جائے'),
(2004, 16, 103, 'ہمیں بخوبی علم ہے کہ یہ کافر کہتے ہیں کہ اسے تو ایک آدمی سکھاتا ہے اس کی زبان جس کی طرف یہ نسبت کر رہے ہیں عجمی ہے اور یہ قرآن تو صاف عربی زبان میں ہے۔'),
(2005, 16, 104, 'جو لوگ اللہ تعالیٰ کی آیتوں پر ایمان نہیں رکھتے انہیں اللہ کی طرف سے بھی رہنمائی نہیں ہوتی اور ان کے لیے المناک عذاب ہیں'),
(2006, 16, 105, 'جھوٹ افترا تو وہی باندھتے ہیں جنہیں اللہ تعالیٰ کی آیتوں پر ایمان نہیں ہوتا۔ یہی لوگ جھوٹے ہیں'),
(2007, 16, 106, 'جو شخص اپنے ایمان کے بعد اللہ سے کفر کرے بجز اس کے جس پر جبر کیا جائے اور اس کا دل ایمان پر برقرار ہو، مگر جو لوگ کھلے دل سے کفر کریں تو ان پر اللہ کا غضب ہے اور انہی کے لیے بہت بڑا عذاب ہے'),
(2008, 16, 107, 'یہ اس لیے کہ انہوں نے دنیا کی زندگی کو آخرت سے زیاده محبوب رکھا۔ یقیناً اللہ تعالیٰ کافر لوگوں کو راه راست نہیں دکھاتا'),
(2009, 16, 108, 'یہ وه لوگ ہیں جن کے دلوں پر اور جن کے کانوں پر اور جن کی آنکھوں پر اللہ نے مہر لگا دی ہے اور یہی لوگ غافل ہیں'),
(2010, 16, 109, 'کچھ شک نہیں کہ یہی لوگ آخرت میں سخت نقصان اٹھانے والے ہیں'),
(2011, 16, 110, 'جن لوگوں نے فتنوں میں ڈالے جانے کے بعد ہجرت کی پھر جہاد کیا اور صبر کا ﺛبوت دیا بیشک تیرا پروردگار ان باتوں کے بعد انہیں بخشنے واﻻ اور مہربانیاں کرنے واﻻ ہے'),
(2012, 16, 111, 'جس دن ہر شخص اپنی ذات کے لیے لڑتا جھگڑتا آئے اور ہر شخص کو اس کے کیے ہوئے اعمال کا پورا بدلہ دیا جائے گا اور لوگوں پر (مطلقاً) ﻇلم نہ کیا جائے گا'),
(2013, 16, 112, 'اللہ تعالیٰ اس بستی کی مثال بیان فرماتا ہے جو پورے امن واطمینان سے تھی اس کی روزی اس کے پاس بافراغت ہر جگہ سے چلی آرہی تھی۔ پھر اس نے اللہ تعالیٰ کی نعمتوں کا کفر کیا تو اللہ تعالیٰ نے اسے بھوک اور ڈر کا مزه چکھایا جو بدلہ تھا ان کے کرتوتوں کا'),
(2014, 16, 113, 'ان کے پاس انہی میں سے رسول پہنچا پھر بھی انہوں نے اسے جھٹلایا پس انہیں عذاب نے آدبوچا اور وه تھے ہی ﻇالم'),
(2015, 16, 114, 'جو کچھ حلال اور پاکیزه روزی اللہ نے تمہیں دے رکھی ہے اسے کھاؤ اور اللہ کی نعمت کا شکر کرو اگر تم اسی کی عبادت کرتے ہو'),
(2016, 16, 115, 'تم پر صرف مردار اور خون اور سور کا گوشت اور جس چیز پر اللہ کے سوا دوسرے کا نام پکارا جائے حرام ہیں، پھر اگر کوئی شخص بے بس کر دیا جائے نہ وه خواہشمند ہو اور نہ حد سے گزرنے واﻻ ہو تو یقیناً اللہ بخشنے واﻻ رحم کرنے واﻻ ہے'),
(2017, 16, 116, 'کسی چیز کو اپنی زبان سے جھوٹ موٹ نہ کہہ دیا کرو کہ یہ حلال ہے اور یہ حرام ہے کہ اللہ پر جھوٹ بہتان باندھ لو، سمجھ لو کہ اللہ تعالیٰ پر بہتان بازی کرنے والے کامیابی سے محروم ہی رہتے ہیں'),
(2018, 16, 117, 'انہیں بہت معمولی فائده ملتا ہے اور ان کے لئے ہی دردناک عذاب ہے'),
(2019, 16, 118, 'اور یہودیوں پر جو کچھ ہم نے حرام کیا تھا اسے ہم پہلے ہی سے آپ کو سنا چکے ہیں، ہم نے ان پر ﻇلم نہیں کیا بلکہ وه خود اپنی جانوں پر ﻇلم کرتے رہے'),
(2020, 16, 119, 'جو کوئی جہالت سے برے عمل کرلے پھر توبہ کرلے اور اصلاح بھی کرلے تو پھر آپ کا رب بلا شک وشبہ بڑی بخشش کرنے واﻻ اور نہایت ہی مہربان ہے'),
(2021, 16, 120, 'بےشک ابراہیم پیشوا اور اللہ تعالیٰ کے فرمانبردار اور یک طرفہ مخلص تھے۔ وه مشرکوں میں سے نہ تھے'),
(2022, 16, 121, 'اللہ تعالیٰ کی نعمتوں کے شکر گزار تھے، اللہ نے انہیں اپنا برگزیده کر لیا تھا اور انہیں راه راست سجھا دی تھی'),
(2023, 16, 122, 'ہم نے اسے دنیا میں بھی بہتری دی تھی اور بےشک وه آخرت میں بھی نیکوکاروں میں ہیں'),
(2024, 16, 123, 'پھر ہم نے آپ کی جانب وحی بھیجی کہ آپ ملت ابراہیم حنیف کی پیروی کریں، جو مشرکوں میں سے نہ تھے'),
(2025, 16, 124, 'ہفتے کے دن کی عظمت تو صرف ان لوگوں کے ذمے ہی ضروری کی گئی تھی جنہوں نے اس میں اختلاف کیا تھا، بات یہ ہے کہ آپ کا پروردگار خود ہی ان میں ان کے اختلاف کا فیصلہ قیامت کے دن کرے گا'),
(2026, 16, 125, 'اپنے رب کی راه کی طرف لوگوں کو حکمت اور بہترین نصیحت کے ساتھ بلایئے اور ان سے بہترین طریقے سے گفتگو کیجئے، یقیناً آپ کا رب اپنی راه سے بہکنے والوں کو بھی بخوبی جانتا ہے اور وه راه یافتہ لوگوں سے بھی پورا واقف ہے'),
(2027, 16, 126, 'اور اگر بدلہ لو بھی تو بالکل اتنا ہی جتنا صدمہ تمہیں پہنچایا گیا ہو اور اگر صبر کرلو تو بےشک صابروں کے لئے یہی بہتر ہے'),
(2028, 16, 127, 'آپ صبر کریں بغیر توفیق الٰہی کے آپ صبر کر ہی نہیں سکتے اور ان کے حال پر رنجیده نہ ہوں اور جو مکر وفریب یہ کرتے رہتے ہیں ان سے تنگ دل نہ ہوں'),
(2029, 16, 128, 'یقین مانو کہ اللہ تعالیٰ پرہیزگاروں اور نیک کاروں کے ساتھ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2030, 17, 1, 'پاک ہے وه اللہ تعالیٰ جو اپنے بندے کو رات ہی رات میں مسجد حرام سے مسجد اقصیٰ تک لے گیا جس کے آس پاس ہم نے برکت دے رکھی ہے، اس لئے کہ ہم اسے اپنی قدرت کے بعض نمونے دکھائیں، یقیناً اللہ تعالیٰ ہی خوب سننے دیکھنے واﻻ ہے'),
(2031, 17, 2, 'ہم نے موسیٰ کو کتاب دی اور اسے بنی اسرائیل کے لئے ہدایت بنا دیا کہ تم میرے سوا کسی کو اپنا کارساز نہ بنانا'),
(2032, 17, 3, 'اے ان لوگوں کی اوﻻد! جنہیں ہم نے نوح کے ساتھ سوار کردیا تھا، وه ہمارا بڑا ہی شکرگزار بنده تھا'),
(2033, 17, 4, 'ہم نے بنی اسرائیل کے لئے ان کی کتاب میں صاف فیصلہ کردیا تھا کہ تم زمین میں دوبار فساد برپا کرو گے اور تم بڑی زبردست زیادتیاں کرو گے'),
(2034, 17, 5, 'ان دونوں وعدوں میں سے پہلے کے آتے ہی ہم نے تمہارے مقابلہ پر اپنے بندے بھیج دیئے جو بڑے ہی لڑاکے تھے۔ پس وه تمہارے گھروں کے اندر تک پھیل گئے اور اللہ کا یہ وعده پورا ہونا ہی تھا'),
(2035, 17, 6, 'پھر ہم نے ان پر تمہارا غلبہ دے کر تمہارے دن پھیرے اور مال اور اوﻻد سے تمہاری مدد کی اور تمہیں بڑے جتھے واﻻ بنا دیا'),
(2036, 17, 7, 'اگر تم نے اچھے کام کئے تو خود اپنے ہی فائده کے لئے، اور اگر تم نے برائیاں کیں تو بھی اپنے ہی لئے، پھر جب دوسرے وعدے کا وقت آیا (تو ہم نے دوسرے بندوں کو بھیج دیا تاکہ) وه تمہارے چہرے بگاڑ دیں اور پہلی دفعہ کی طرح پھر اسی مسجد میں گھس جائیں۔ اور جس جس چیز پر قابو پائیں توڑ پھوڑ کر جڑ سے اکھاڑ دیں'),
(2037, 17, 8, 'امید ہے کہ تمہارا رب تم پر رحم کرے۔ ہاں اگر تم پھر بھی وہی کرنے لگے تو ہم بھی دوباره ایسا ہی کریں گے اور ہم نے منکروں کا قید خانہ جہنم کو بنا رکھا ہے'),
(2038, 17, 9, 'یقیناً یہ قرآن وه راستہ دکھاتا ہے جو بہت ہی سیدھا ہے اور ایمان والوں کو جو نیک اعمال کرتے ہیں اس بات کی خوشخبری دیتا ہے کہ ان کے لئے بہت بڑا اجر ہے'),
(2039, 17, 10, 'اور یہ کہ جو لوگ آخرت پر یقین نہیں رکھتے ان کے لئے ہم نے دردناک عذاب تیار کر رکھا ہے'),
(2040, 17, 11, 'اور انسان برائی کی دعائیں مانگنے لگتا ہے بالکل اس کی اپنی بھلائی کی دعا کی طرح، انسان ہے ہی بڑا جلد باز'),
(2041, 17, 12, 'ہم نے رات اور دن کو اپنی قدرت کی نشانیاں بنائی ہیں، رات کی نشانی کو تو ہم نے بےنور کردیا ہے اور دن کی نشانی کو روشن بنایا ہے تاکہ تم اپنے رب کا فضل تلاش کر سکو اور اس لئے بھی کہ برسوں کا شمار اور حساب معلوم کرسکو اور ہر ہر چیز کو ہم نے خوب تفصیل سے بیان فرما دیا ہے'),
(2042, 17, 13, 'ہم نے ہر انسان کی برائی بھلائی کو اس کے گلے لگا دیا ہے اور بروز قیامت ہم اس کے سامنے اس کا نامہٴ اعمال نکالیں گے جسے وه اپنے اوپر کھلا ہوا پالے گا'),
(2043, 17, 14, 'لے! خود ہی اپنی کتاب آپ پڑھ لے۔ آج تو تو آپ ہی اپنا خود حساب لینے کو کافی ہے'),
(2044, 17, 15, 'جو راه راست حاصل کرلے وه خود اپنے ہی بھلے کے لئے راه یافتہ ہوتا ہے اور جو بھٹک جائے اس کا بوجھ اسی کے اوپر ہے، کوئی بوجھ واﻻ کسی اور کا بوجھ اپنے اوپر نہ ﻻدے گا اور ہماری سنت نہیں کہ رسول بھیجنے سے پہلے ہی عذاب کرنے لگیں'),
(2045, 17, 16, 'اور جب ہم کسی بستی کی ہلاکت کا اراده کرلیتے ہیں تو وہاں کے خوشحال لوگوں کو (کچھ) حکم دیتے ہیں اور وه اس بستی میں کھلی نافرمانی کرنے لگتے ہیں تو ان پر (عذاب کی) بات ﺛابت ہوجاتی ہے پھر ہم اسے تباه وبرباد کردیتے ہیں'),
(2046, 17, 17, 'ہم نے نوح کے بعد بھی بہت سی قومیں ہلاک کیں اور تیرا رب اپنے بندوں کے گناہوں سے کافی خبردار اور خوب دیکھنے بھالنے واﻻ ہے'),
(2047, 17, 18, 'جس کا اراده صرف اس جلدی والی دنیا (فوری فائده) کا ہی ہو اسے ہم یہاں جس قدر جس کے لئے چاہیں سردست دیتے ہیں بالﺂخر اس کے لئے ہم جہنم مقرر کردیتے ہیں جہاں وه برے حالوں دھتکارا ہوا داخل ہوگا'),
(2048, 17, 19, 'اور جس کا اراده آخرت کا ہو اور جیسی کوشش اس کے لئے ہونی چاہیئے، وه کرتا بھی ہو اور وه باایمان بھی ہو، پس یہی لوگ ہیں جن کی کوشش کی اللہ کے ہاں پوری قدردانی کی جائے گی'),
(2049, 17, 20, 'ہر ایک کو ہم بہم پہنچائے جاتے ہیں انہیں بھی اور ا نہیں بھی تیرے پروردگار کے انعامات میں سے۔ تیرے پروردگار کی بخشش رکی ہوئی نہیں ہے'),
(2050, 17, 21, 'دیکھ لے کہ ان میں ایک کو ایک پر ہم نے کس طرح فضیلت دے رکھی ہے اور آخرت تو درجوں میں اور بھی بڑھ کر ہے اور فضیلت کے اعتبار سے بھی بہت بڑی ہے'),
(2051, 17, 22, 'اللہ کے ساتھ کسی اور کو معبود نہ ٹھہرا کہ آخرش تو برے حالوں بےکس ہو کر بیٹھ رہے گا'),
(2052, 17, 23, 'اور تیرا پروردگار صاف صاف حکم دے چکا ہے کہ تم اس کے سوا کسی اور کی عبادت نہ کرنا اور ماں باپ کے ساتھ احسان کرنا۔ اگر تیری موجودگی میں ان میں سے ایک یا یہ دونوں بڑھاپے کو پہنچ جائیں تو ان کے آگے اف تک نہ کہنا، نہ انہیں ڈانٹ ڈپٹ کرنا بلکہ ان کے ساتھ ادب و احترام سے بات چیت کرنا'),
(2053, 17, 24, 'اور عاجزی اور محبت کے ساتھ ان کے سامنے تواضع کا بازو پست رکھے رکھنا اور دعا کرتے رہنا کہ اے میرے پروردگار! ان پر ویسا ہی رحم کر جیسا انہوں نے میرے بچپن میں میری پرورش کی ہے'),
(2054, 17, 25, 'جو کچھ تمہارے دلوں میں ہے اسے تمہارا رب بخوبی جانتا ہے اگر تم نیک ہو تو وه تو رجوع کرنے والوں کو بخشنے واﻻ ہے'),
(2055, 17, 26, 'اور رشتے داروں کا اور مسکینوں اور مسافروں کا حق ادا کرتے رہو اور اسراف اور بیجا خرچ سے بچو'),
(2056, 17, 27, 'بیجا خرچ کرنے والے شیطانوں کے بھائی ہیں۔ اور شیطان اپنے پروردگار کا بڑا ہی ناشکرا ہے'),
(2057, 17, 28, 'اور اگر تجھے ان سے منھ پھیر لینا پڑے اپنے رب کی اس رحمت کی جستجو میں، جس کی تو امید رکھتا ہے تو بھی تجھے چاہیئے کہ عمدگی اور نرمی سے انہیں سمجھا دے'),
(2058, 17, 29, 'اپنا ہاتھ اپنی گردن سے بندھا ہوا نہ رکھ اور نہ اسے بالکل ہی کھول دے کہ پھر ملامت کیا ہوا درمانده بیٹھ جائے'),
(2059, 17, 30, 'یقیناً تیرا رب جس کے لئے چاہے روزی کشاده کردیتا ہے اور جس کے لئے چاہے تنگ۔ یقیناً وه اپنے بندوں سے باخبر اور خوب دیکھنے واﻻ ہے'),
(2060, 17, 31, 'اور مفلسی کے خوف سے اپنی اوﻻد کو نہ مار ڈالو، ان کو اور تم کو ہم ہی روزی دیتے ہیں۔ یقیناً ان کا قتل کرنا کبیره گناه ہے'),
(2061, 17, 32, 'خبردار زنا کے قریب بھی نہ پھٹکنا کیوں کہ وه بڑی بےحیائی ہے اور بہت ہی بری راه ہے'),
(2062, 17, 33, 'اور کسی جان کو جس کا مارنا اللہ نے حرام کردیا ہے ہرگز ناحق قتل نہ کرنا اور جو شخص مظلوم ہونے کی صورت میں مار ڈاﻻ جائے ہم نے اس کے وارث کو طاقت دے رکھی ہے پس اسے چاہیئے کہ مار ڈالنے میں زیادتی نہ کرے بےشک وه مدد کیا گیا ہے'),
(2063, 17, 34, 'اور یتیم کے مال کے قریب بھی نہ جاؤ بجز اس طریقہ کے جو بہت ہی بہتر ہو، یہاں تک کہ وه اپنی بلوغت کو پہنچ جائے اور وعدے پورے کرو کیونکہ قول وقرار کی باز پرس ہونے والی ہے'),
(2064, 17, 35, 'اور جب ناپنے لگو تو بھر پور پیمانے سے ناپو اور سیدھی ترازو سے توﻻ کرو۔ یہی بہتر ہے اور انجام کے لحاظ سے بھی بہت اچھا ہے'),
(2065, 17, 36, 'جس بات کی تجھے خبر ہی نہ ہو اس کے پیچھے مت پڑ۔ کیونکہ کان اور آنکھ اور دل ان میں سے ہر ایک سے پوچھ گچھ کی جانے والی ہے'),
(2066, 17, 37, 'اور زمین میں اکڑ کر نہ چل کہ نہ تو زمین کو پھاڑ سکتا ہے اور نہ لمبائی میں پہاڑوں کو پہنچ سکتا ہے'),
(2067, 17, 38, 'ان سب کاموں کی برائی تیرے رب کے نزدیک (سخت) ناپسند ہے'),
(2068, 17, 39, 'یہ بھی منجملہ اس وحی کے ہے جو تیری جانب تیرے رب نے حکمت سے اتاری ہے تو اللہ کے ساتھ کسی اور کو معبود نہ بنانا کہ ملامت خورده اور راندہٴ درگاه ہو کر دوزخ میں ڈال دیا جائے'),
(2069, 17, 40, 'کیا بیٹوں کے لئے تو اللہ نے تمہیں چھانٹ لیا اور خود اپنے لئے فرشتوں کو لڑکیاں بنالیں؟ بےشک تم بہت بڑا بول بول رہے ہو'),
(2070, 17, 41, 'ہم نے تو اس قرآن میں ہر ہر طرح بیان فرما دیا کہ لوگ سمجھ جائیں لیکن اس سے انہیں تو نفرت ہی بڑھتی ہے'),
(2071, 17, 42, 'کہہ دیجیئے! کہ اگر اللہ کے ساتھ اور معبود بھی ہوتے جیسے کہ یہ لوگ کہتے ہیں تو ضرور وه اب تک مالک عرش کی جانب راه ڈھونڈ نکالتے'),
(2072, 17, 43, 'جو کچھ یہ کہتے ہیں اس سے وه پاک اور باﻻتر، بہت دور اور بہت بلند ہے'),
(2073, 17, 44, 'ساتوں آسمان اور زمین اور جو بھی ان میں ہے اسی کی تسبیح کر رہے ہیں۔ ایسی کوئی چیز نہیں جو اسے پاکیزگی اور تعریف کے ساتھ یاد نہ کرتی ہو۔ ہاں یہ صحیح ہے کہ تم اس کی تسبیح سمجھ نہیں سکتے۔ وه بڑا بردبار اور بخشنے واﻻ ہے'),
(2074, 17, 45, 'تو جب قرآن پڑھتا ہے ہم تیرے اور ان لوگوں کے درمیان جو آخرت پر یقین نہیں رکھتے ایک پوشیده حجاب ڈال دیتے ہیں'),
(2075, 17, 46, 'اور ان کے دلوں پر ہم نے پردے ڈال دیئے ہیں کہ وه اسے سمجھیں اور ان کے کانوں میں بوجھ اور جب تو صرف اللہ ہی کا ذکر اس کی توحید کے ساتھ، اس قرآن میں کرتا ہے تو وه روگردانی کرتے پیٹھ پھیر کر بھاگ کھڑے ہوتے ہیں'),
(2076, 17, 47, 'جس غرض سے وه لوگ اسے سنتے ہیں ان (کی نیتوں) سے ہم خوب آگاه ہیں، جب یہ آپ کی طرف کان لگائے ہوئے ہوتے ہیں تب بھی اور جب یہ مشوره کرتے ہیں تب بھی جب کہ یہ ﻇالم کہتے ہیں کہ تم اس کی تابعداری میں لگے ہوئے ہو جن پر جادو کردیا گیا ہے'),
(2077, 17, 48, 'دیکھیں تو سہی، آپ کے لئے کیا کیا مثالیں بیان کرتے ہیں، پس وه بہک رہے ہیں۔ اب تو راه پانا ان کے بس میں نہیں رہا'),
(2078, 17, 49, 'انہوں نے کہا کیا جب ہم ہڈیاں اور (مٹی ہو کر) ریزه ریزه ہوجائیں گے تو کیا ہم ازسرنو پیدا کر کے پھر دوباره اٹھا کر کھڑے کر دیئے جائیں گے'),
(2079, 17, 50, 'جواب دیجیئے کہ تم پتھر بن جاؤ یا لوہا'),
(2080, 17, 51, 'یا کوئی اور ایسی خلقت جو تمہارے دلوں میں بہت ہی سخت معلوم ہو، پھر وه پوچھیں کہ کون ہے جو دوباره ہماری زندگی لوٹائے؟ آپ جواب دے دیں کہ وہی اللہ جس نے تمہیں اول بار پیدا کیا، اس پر وه اپنے سر ہلا ہلا کر آپ سے دریافت کریں گے کہ اچھا یہ ہے کب؟ تو آپ جواب دے دیں کہ کیا عجب کہ وه (ساعت) قریب ہی آن لگی ہو'),
(2081, 17, 52, 'جس دن وه تمہیں بلائے گا تم اس کی تعریف کرتے ہوئے تعمیل ارشاد کرو گے اور گمان کرو گے کہ تمہارا رہنا بہت ہی تھوڑا ہے'),
(2082, 17, 53, 'اور میرے بندوں سے کہہ دیجیئے کہ وه بہت ہی اچھی بات منھ سے نکالا کریں کیونکہ شیطان آپس میں فساد ڈلواتا ہے۔ بےشک شیطان انسان کا کھلا دشمن ہے'),
(2083, 17, 54, 'تمہارا رب تم سے بہ نسبت تمہارے بہت زیاده جاننے واﻻ ہے، وه اگر چاہے تو تم پر رحم کردے یا اگر وه چاہے تمہیں عذاب دے۔ ہم نے آپ کو ان کا ذمہ دار ٹھہرا کر نہیں بھیجا'),
(2084, 17, 55, 'آسمانوں وزمین میں جو بھی ہے آپ کا رب سب کو بخوبی جانتا ہے۔ ہم نے بعض پیغمبروں کو بعض پر بہتری اور برتری دی ہے اور داؤد کو زبور ہم نے عطا فرمائی ہے'),
(2085, 17, 56, 'کہہ دیجیئے کہ اللہ کے سوا جنہیں تم معبود سمجھ رہے ہو انہیں پکارو لیکن نہ تو وه تم سے کسی تکلیف کو دور کرسکتے ہیں اور نہ بدل سکتے ہیں'),
(2086, 17, 57, 'جنہیں یہ لوگ پکارتے ہیں خود وه اپنے رب کے تقرب کی جستجو میں رہتے ہیں کہ ان میں سے کون زیاده نزدیک ہوجائے وه خود اس کی رحمت کی امید رکھتے اور اس کے عذاب سے خوفزده رہتے ہیں، (بات بھی یہی ہے) کہ تیرے رب کا عذاب ڈرنے کی چیز ہی ہے'),
(2087, 17, 58, 'جتنی بھی بستیاں ہیں ہم قیامت کے دن سے پہلے پہلے یا تو انہیں ہلاک کردینے والے ہیں یا سخت تر سزا دینے والے ہیں۔ یہ تو کتاب میں لکھا جاچکا ہے'),
(2088, 17, 59, 'ہمیں نشانات (معجزات) کے نازل کرنے سے روک صرف اسی کی ہے کہ اگلے لوگ انہیں جھٹلا چکے ہیں۔ ہم نے ﺛمودیوں کو بطور بصیرت کے اونٹنی دی لیکن انہوں نے اس پر ﻇلم کیا ہم تو لوگوں کو دھمکانے کے لئے ہی نشانیاں بھیجتے ہیں'),
(2089, 17, 60, 'اور یاد کرو جب کہ ہم نے آپ سے فرما دیا کہ آپ کے رب نے لوگوں کو گھیر لیا ہے۔ جو رویا (عینی رؤیت) ہم نے آپ کو دکھائی تھی وه لوگوں کے لئے صاف آزمائش ہی تھی اور اسی طرح وه درخت بھی جس سے قرآن میں اﻇہار نفرت کیا گیا ہے۔ ہم انہیں ڈرا رہے ہیں لیکن یہ انہیں اور بڑی سرکشی میں بڑھا رہا ہے'),
(2090, 17, 61, 'جب ہم نے فرشتوں کو حکم دیا کہ آدم کو سجده کرو تو ابلیس کے سوا سب نے کیا، اس نے کہا کہ کیا میں اسے سجده کروں جسے تو نے مٹی سے پیدا کیا ہے'),
(2091, 17, 62, 'اچھا دیکھ لے اسے تو نے مجھ پر بزرگی تو دی ہے، لیکن اگر مجھے بھی قیامت تک تو نے ڈھیل دی تو میں اس کی اوﻻد کو بجز بہت تھوڑے لوگوں کے، اپنے بس میں کرلوں گا'),
(2092, 17, 63, 'ارشاد ہوا کہ جا ان میں سے جو بھی تیرا تابعدار ہوجائے گا تو تم سب کی سزا جہنم ہے جو پورا پورا بدلہ ہے'),
(2093, 17, 64, 'ان میں سے تو جسے بھی اپنی آواز سے بہکا سکے بہکا لے اور ان پر اپنے سوار اور پیادے چڑھا ﻻ اور ان کے مال اور اوﻻد میں سے اپنا بھی ساجھا لگا اور انہیں (جھوٹے) وعدے دے لے۔ ان سے جتنے بھی وعدے شیطان کے ہوتے ہیں سب کے سب سراسر فریب ہیں'),
(2094, 17, 65, 'میرے سچے بندوں پر تیرا کوئی قابو اور بس نہیں۔ تیرا رب کارسازی کرنے واﻻ کافی ہے'),
(2095, 17, 66, 'تمہارا پروردگار وه ہے جو تمہارے لئے دریا میں کشتیاں چلاتا ہے تاکہ تم اس کا فضل تلاش کرو۔ وه تمہارے اوپر بہت ہی مہربان ہے'),
(2096, 17, 67, 'اور سمندروں میں مصیبت پہنچتے ہی جنہیں تم پکارتے تھے سب گم ہوجاتے ہیں صرف وہی اللہ باقی ره جاتا ہے۔ پھر جب وه تمہیں خشکی کی طرف بچا ﻻتا ہے تو تم منھ پھیر لیتے ہو اور انسان بڑا ہی ناشکرا ہے'),
(2097, 17, 68, 'تو کیا تم اس سے بےخوف ہوگئے ہو کہ تمہیں خشکی کی طرف (لے جاکر زمین) میں دھنسا دے یا تم پر پتھروں کی آندھی بھیج دے۔ پھر تم اپنے لئے کسی نگہبان کو نہ پاسکو'),
(2098, 17, 69, 'کیا تم اس بات سے بےخوف ہوگئے ہو کہ اللہ تعالیٰ پھر تمہیں دوباره دریا کے سفر میں لے آئے اور تم پر تیز وتند ہواؤں کے جھونکے بھیج دے اور تمہارے کفر کے باعﺚ تمہیں ڈبو دے۔ پھر تم اپنے لئے ہم پر اس کا دعویٰ (پیچھا) کرنے واﻻ کسی کو نہ پاؤ گے'),
(2099, 17, 70, 'یقیناً ہم نے اوﻻد آدم کو بڑی عزت دی اور انہیں خشکی اور تری کی سواریاں دیں اور انہیں پاکیزه چیزوں کی روزیاں دیں اور اپنی بہت سی مخلوق پر انہیں فضیلت عطا فرمائی'),
(2100, 17, 71, 'جس دن ہم ہر جماعت کو اس کے پیشوا سمیت بلائیں گے۔ پھر جن کا بھی اعمال نامہ دائیں ہاتھ میں دے دیا گیا وه تو شوق سے اپنا نامہٴ اعمال پڑھنے لگیں گے اور دھاگے کے برابر (ذره برابر) بھی ﻇلم نہ کئے جائیں گے'),
(2101, 17, 72, 'اور جو کوئی اس جہان میں اندھا رہا، وه آخرت میں بھی اندھا اور راستے سے بہت ہی بھٹکا ہوا رہے گا'),
(2102, 17, 73, 'یہ لوگ آپ کو اس وحی سے جو ہم نے آپ پر اتاری ہے بہکانا چاہتے کہ آپ اس کے سوا کچھ اور ہی ہمارے نام سے گھڑ گھڑالیں، تب تو آپ کو یہ لوگ اپنا ولی دوست بنا لیتے'),
(2103, 17, 74, 'اگر ہم آپ کو ﺛابت قدم نہ رکھتے تو بہت ممکن تھا کہ ان کی طرف قدرے قلیل مائل ہو ہی جاتے'),
(2104, 17, 75, 'پھر تو ہم بھی آپ کو دوہرا عذاب دنیا کا کرتے اور دوہرا ہی موت کا، پھر آپ تو اپنے لئے ہمارے مقابلے میں کسی کو مددگار بھی نہ پاتے'),
(2105, 17, 76, 'یہ تو آپ کے قدم اس سرزمین سے اکھاڑنے ہی لگے تھے کہ آپ کو اس سے نکال دیں۔ پھر یہ بھی آپ کے بعد بہت ہی کم ٹھہر پاتے'),
(2106, 17, 77, 'ایسا ہی دستور ان کا تھا جو آپ سے پہلے رسول ہم نے بھیجے اور آپ ہمارے دستور میں کبھی ردوبدل نہ پائیں گے'),
(2107, 17, 78, 'نماز کو قائم کریں آفتاب کے ڈھلنے سے لے کر رات کی تاریکی تک اور فجر کا قرآن پڑھنا بھی یقیناً فجر کے وقت کا قرآن پڑھنا حاضر کیا گیا ہے'),
(2108, 17, 79, 'رات کے کچھ حصے میں تہجد کی نماز میں قرآن کی تلاوت کریں یہ زیادتی آپ کے لئے ہے عنقریب آپ کا رب آپ کو مقام محمود میں کھڑا کرے گا'),
(2109, 17, 80, 'اور دعا کیا کریں کہ اے میرے پروردگار مجھے جہاں لے جا اچھی طرح لے جا اور جہاں سے نکال اچھی طرح نکال اور میرے لئے اپنے پاس سے غلبہ اور امداد مقرر فرما دے'),
(2110, 17, 81, 'اور اعلان کردے کہ حق آچکا اور ناحق نابود ہوگیا۔ یقیناً باطل تھا بھی نابود ہونے واﻻ'),
(2111, 17, 82, 'یہ قرآن جو ہم نازل کر رہے ہیں مومنوں کے لئے تو سراسر شفا اور رحمت ہے۔ ہاں ﻇالموں کو بجز نقصان کے اور کوئی زیادتی نہیں ہوتی'),
(2112, 17, 83, 'اور انسان پر جب ہم اپنا انعام کرتے ہیں تو وه منھ موڑ لیتا ہے اور کروٹ بدل لیتا ہے اور جب اسے کوئی تکلیف پہنچتی ہے تو وه مایوس ہوجاتا ہے'),
(2113, 17, 84, 'کہہ دیجئیے! کہ ہر شخص اپنے طریقہ پر عامل ہے جو پوری ہدایت کے راستے پر ہیں انہیں تمہارا رب ہی بخوبی جاننے واﻻ ہے'),
(2114, 17, 85, 'اور یہ لوگ آپ سے روح کی بابت سوال کرتے ہیں، آپ جواب دے دیجئیے کہ روح میرے رب کے حکم سے ہے اور تمہیں بہت ہی کم علم دیا گیا ہے'),
(2115, 17, 86, 'اور اگر ہم چاہیں تو جو وحی آپ کی طرف ہم نے اتاری ہے سب سلب کرلیں، پھر آپ کو اس کے لئے ہمارے مقابلے میں کوئی حمایتی میسر نہ آسکے'),
(2116, 17, 87, 'سوائے آپ کے رب کی رحمت کے، یقیناً آپ پر اس کا بڑا ہی فضل ہے'),
(2117, 17, 88, 'کہہ دیجیئے کہ اگر تمام انسان اور کل جنات مل کر اس قرآن کے مثل ﻻنا چاہیں تو ان سب سے اس کے مثل ﻻنا ناممکن ہے گو وه (آپس میں) ایک دوسرے کے مددگار بھی بن جائیں'),
(2118, 17, 89, 'ہم نے تو اس قرآن میں لوگوں کے سمجھنے کے لئے ہر طرح سے تمام مثالیں بیان کردی ہیں، مگر اکثر لوگ انکار سے باز نہیں آتے'),
(2119, 17, 90, 'انہوں نے کہا کہ ہم آپ پر ہرگز ایمان ﻻنے کے نہیں تاوقتیکہ آپ ہمارے لئے زمین سے کوئی چشمہ جاری نہ کردیں'),
(2120, 17, 91, 'یا خود آپ کے لئے ہی کوئی باغ ہو کھجوروں اور انگوروں کا اور اس کے درمیان آپ بہت سی نہریں جاری کر دکھائیں'),
(2121, 17, 92, 'یا آپ آسمان کو ہم پر ٹکڑے ٹکڑے کرکے گرا دیں جیسا کہ آپ کا گمان ہے یا آپ خود اللہ تعالیٰ کو اور فرشتوں کو ہمارے سامنے ﻻکھڑا کریں'),
(2122, 17, 93, 'یا آپ کے اپنے لئے کوئی سونے کا گھر ہوجائے یا آپ آسمان پر چڑھ جائیں اور ہم تو آپ کے چڑھ جانے کا بھی اس وقت تک ہرگز یقین نہیں کریں گے جب تک کہ آپ ہم پر کوئی کتاب نہ اتار ﻻئیں جسے ہم خود پڑھ لیں، آپ جواب دے دیں کہ میرا پروردگار پاک ہے میں تو صرف ایک انسان ہی ہوں جو رسول بنایا گیا ہوں'),
(2123, 17, 94, 'لوگوں کے پاس ہدایت پہنچ چکنے کے بعد ایمان سے روکنے والی صرف یہی چیز رہی کہ انہوں نے کہا کیا اللہ نے ایک انسان کو ہی رسول بنا کر بھیجا؟'),
(2124, 17, 95, 'آپ کہہ دیں کہ اگر زمین میں فرشتے چلتے پھرتے اور رہتے بستے ہوتے تو ہم بھی ان کے پاس کسی آسمانی فرشتے ہی کو رسول بنا کر بھیجتے'),
(2125, 17, 96, 'کہہ دیجئیے کہ میرے اور تمہارے درمیان اللہ تعالیٰ کا گواه ہونا کافی ہے۔ وه اپنے بندوں سے خوب آگاه اور بخوبی دیکھنے واﻻ ہے'),
(2126, 17, 97, 'اللہ جس کی رہنمائی کرے تو وه ہدایت یافتہ ہے اور جسے وه راه سے بھٹکا دے ناممکن ہے کہ تو اس کا مددگار اس کے سوا کسی اور کو پائے، ایسے لوگوں کا ہم بروز قیامت اوندھے منھ حشر کریں گے، دراں حالیکہ وه اندھے، گونگے اور بہرے ہوں گے، ان کا ٹھکانہ جہنم ہوگا۔ جب کبھی وه بجھنے لگے گی ہم ان پر اسے اور بھڑکا دیں گے'),
(2127, 17, 98, 'یہ سب ہماری آیتوں سے کفر کرنے اور اس کہنے کا بدلہ ہے کہ کیا جب ہم ہڈیاں اور ریزے ریزے ہوجائیں گے پھر ہم نئی پیدائش میں اٹھا کھڑے کئے جائیں گے؟'),
(2128, 17, 99, 'کیا انہوں نے اس بات پر نظر نہیں کی کہ جس اللہ نے آسمان وزمین کو پیدا کیا ہے وه ان جیسوں کی پیدائش پر پورا قادر ہے، اسی نے ان کے لئے ایک ایسا وقت مقرر کر رکھا ہے جو شک شبہ سے یکسر خالی ہے، لیکن ﻇالم لوگ انکار کئے بغیر رہتے ہی نہیں'),
(2129, 17, 100, 'کہہ دیجیئے کہ اگر بالفرض تم میرے رب کی رحمتوں کے خزانوں کے مالک بن جاتے تو تم اس وقت بھی اس کے خرچ ہوجانے کے خوف سے اس کو روکے رکھتے اور انسان ہے ہی تنگ دل'),
(2130, 17, 101, 'ہم نے موسیٰ کو نو معجزے بالکل صاف صاف عطا فرمائے، تو خود ہی بنی اسرائیل سے پوچھ لے کہ جب وه ان کے پاس پہنچے تو فرعون بوﻻ کہ اے موسیٰ میرے خیال میں تو تجھ پر جادو کردیا گیا ہے'),
(2131, 17, 102, 'موسیٰ نے جواب دیا کہ یہ تو تجھے علم ہوچکا ہے کہ آسمان وزمین کے پروردگار ہی نے یہ معجزے دکھانے، سمجھانے کو نازل فرمائے ہیں، اے فرعون! میں تو سمجھ رہا ہوں کہ تو یقیناً برباد وہلاک کیا گیا ہے'),
(2132, 17, 103, 'آخر فرعون نے پختہ اراده کر لیا کہ انہیں زمین سے ہی اکھیڑ دے تو ہم نے خود اسے اور اس کے تمام ساتھیوں کو غرق کر دیا'),
(2133, 17, 104, 'اس کے بعد ہم نے بنی اسرائیل سے فرما دیا کہ اس سرزمین پر تم رہو سہو۔ ہاں جب آخرت کا وعده آئے گا ہم تم سب کو سمیٹ اور لپیٹ کر لے آئیں گے'),
(2134, 17, 105, 'اور ہم نے اس قرآن کو حق کے ساتھ اتارا اور یہ بھی حق کے ساتھ اترا۔ ہم نے آپ کو صرف خوشخبری سنانے واﻻ اور ڈرانے واﻻ بنا کر بھیجا ہے'),
(2135, 17, 106, 'قرآن کو ہم نے تھوڑا تھوڑا کرکے اس لئے اتارا ہے کہ آپ اسے بہ مہلت لوگوں کو سنائیں اور ہم نے خود بھی اسے بتدریج نازل فرمایا'),
(2136, 17, 107, 'کہہ دیجئیے! تم اس پر ایمان ﻻؤ یا نہ ﻻؤ جنہیں اس سے پہلے علم دیا گیا ہے ان کے پاس تو جب بھی اس کی تلاوت کی جاتی ہے تو وه ٹھوڑیوں کے بل سجده میں گر پڑتے ہیں'),
(2137, 17, 108, 'اور کہتے ہیں کہ ہمارا رب پاک ہے، ہمارے رب کا وعده بلاشک وشبہ پورا ہو کر رہنے واﻻ ہی ہے'),
(2138, 17, 109, 'وه اپنی ٹھوڑیوں کے بل روتے ہوئے سجده میں گر پڑ تے ہیں اور یہ قرآن ان کی عاجزی اور خشوع اور خضوع بڑھا دیتا ہے'),
(2139, 17, 110, 'کہہ دیجئیے کہ اللہ کو اللہ کہہ کر پکارو یا رحمٰن کہہ کر، جس نام سے بھی پکارو تمام اچھے نام اسی کے ہیں۔ نہ تو تو اپنی نماز بہت بلند آواز سے پڑھ اور نہ بالکل پوشیده بلکہ اس کے درمیان کا راستہ تلاش کرلے'),
(2140, 17, 111, 'اور یہ کہہ دیجیئے کہ تمام تعریفیں اللہ ہی کے لئے ہیں جو نہ اوﻻد رکھتا ہے نہ اپنی بادشاہت میں کسی کو شریک وساجھی رکھتا ہے اور نہ وه کمزور ہے کہ اسے کسی حمایتی کی ضرورت ہو اور تو اس کی پوری پوری بڑائی بیان کرتا ره');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2141, 18, 1, 'تمام تعریفیں اسی اللہ کے لئے سزاوار ہیں جس نے اپنے بندے پر یہ قرآن اتارا اور اس میں کوئی کسر باقی نہ چھوڑی'),
(2142, 18, 2, 'بلکہ ہر طرح سے ٹھیک ٹھاک رکھا تاکہ اپنے پاس کی سخت سزا سے ہوشیار کردے اور ایمان ﻻنے اور نیک عمل کرنے والوں کو خوشخبریاں سنا دے کہ ان کے لئے بہترین بدلہ ہے'),
(2143, 18, 3, 'جس میں وه ہمیشہ ہمیشہ رہیں گے'),
(2144, 18, 4, 'اور ان لوگوں کو بھی ڈرا دے جو کہتے ہیں کہ اللہ تعالیٰ اوﻻد رکھتا ہے'),
(2145, 18, 5, 'در حقیقت نہ تو خود انہیں اس کا علم ہے نہ ان کے باپ دادوں کو۔ یہ تہمت بڑی بری ہے جو ان کے منھ سے نکل رہی ہے وه نرا جھوٹ بک رہے ہیں'),
(2146, 18, 6, 'پس اگر یہ لوگ اس بات پر ایمان نہ ﻻئیں تو کیا آپ ان کے پیچھے اسی رنج میں اپنی جان ہلاک کر ڈالیں گے؟'),
(2147, 18, 7, 'روئے زمین پر جو کچھ ہے ہم نے اسے زمین کی رونق کا باعﺚ بنایا ہے کہ ہم انہیں آزمالیں کہ ان میں سے کون نیک اعمال واﻻ ہے'),
(2148, 18, 8, 'اس پر جو کچھ ہے ہم اسے ایک ہموار صاف میدان کر ڈالنے والے ہیں'),
(2149, 18, 9, 'کیا تو اپنے خیال میں غار اور کتبے والوں کو ہماری نشانیوں میں سے کوئی بہت عجیب نشانی سمجھ رہا ہے؟'),
(2150, 18, 10, 'ان چند نوجوانوں نے جب غار میں پناه لی تو دعا کی کہ اے ہمارے پروردگار! ہمیں اپنے پاس سے رحمت عطا فرما اور ہمارے کام میں ہمارے لئے راه یابی کو آسان کردے'),
(2151, 18, 11, 'پس ہم نے ان کے کانوں پر گنتی کے کئی سال تک اسی غار میں پردے ڈال دیئے'),
(2152, 18, 12, 'پھر ہم نے انہیں اٹھا کھڑا کیا کہ ہم یہ معلوم کرلیں کہ دونوں گروه میں سے اس انتہائی مدت کو جو انہوں نے گزاری کس نے زیاده یاد رکھی ہے'),
(2153, 18, 13, 'ہم ان کا صحیح واقعہ تیرے سامنے بیان فرما رہے ہیں۔ یہ چند نوجوان اپنے رب پر ایمان ﻻئے تھے اور ہم نے ان کی ہدایت میں ترقی دی تھی'),
(2154, 18, 14, 'ہم نے ان کے دل مضبوط کردیئے تھے جب کہ یہ اٹھ کھڑے ہوئے اور کہنے لگے کہ ہمارا پروردگار تو وہی ہے جو آسمان وزمین کا پروردگار ہے، ناممکن ہے کہ ہم اس کے سوا کسی اور معبود کو پکاریں اگر ایسا کیا تو ہم نے نہایت ہی غلط بات کہی'),
(2155, 18, 15, 'یہ ہے ہماری قوم جس نے اس کے سوا اور معبود بنا رکھے ہیں۔ ان کی خدائی کی یہ کوئی صاف دلیل کیوں پیش نہیں کرتے اللہ پر جھوٹ افترا باندھنے والے سے زیاده ﻇالم کون ہے؟'),
(2156, 18, 16, 'جب کہ تم ان سے اور اللہ کے سوا ان کے اور معبودوں سے کناره کش ہوگئے تو اب تم کسی غار میں جا بیٹھو، تمہارا رب تم پر اپنی رحمت پھیلا دے گا اور تمہارے لئے تمہارے کام میں سہولت مہیا کردے گا'),
(2157, 18, 17, 'آپ دیکھیں گے کہ آفتاب بوقت طلوع ان کے غار سے دائیں جانب کو جھک جاتا ہے اور بوقت غروب ان کے بائیں جانب کترا جاتا ہے اور وه اس غار کی کشاده جگہ میں ہیں۔ یہ اللہ کی نشانیوں میں سے ہے۔ اللہ تعالیٰ جس کی رہبری فرمائے وه راه راست پر ہے اور جسے وه گمراه کردے ناممکن ہے کہ آپ اس کا کوئی کارساز اور رہنما پاسکیں'),
(2158, 18, 18, 'آپ خیال کرتے کہ وه بیدار ہیں، حاﻻنکہ وه سوئے ہوئے تھے، خود ہم ہی انہیں دائیں بائیں کروٹیں دﻻیا کرتے تھے، ان کا کتا بھی چوکھٹ پر اپنے ہاتھ پھیلائے ہوئے تھا۔ اگر آپ جھانک کر انہیں دیکھنا چاہتے تو ضرور الٹے پاؤں بھاگ کھڑے ہوتے اور ان کے رعب سے آپ پر دہشت چھا جاتی'),
(2159, 18, 19, 'اسی طرح ہم نے انہیں جگا کر اٹھا دیا کہ آپس میں پوچھ گچھ کرلیں۔ ایک کہنے والے نے کہا کہ کیوں بھئی تم کتنی دیر ٹھہرے رہے؟ انہوں نے جواب دیا کہ ایک دن یا ایک دن سے بھی کم۔ کہنے لگے کہ تمہارے ٹھہرے رہنے کا بخوبی علم اللہ تعالیٰ ہی کو ہے۔ اب تو تم اپنے میں سے کسی کو اپنی یہ چاندی دے کر شہر بھیجو وه خوب دیکھ بھال لے کہ شہر کا کون سا کھانا پاکیزه تر ہے، پھر اسی میں سے تمہارے کھانے کے لئے لے آئے، اور وه بہت احتیاط اور نرمی برتے اور کسی کو تمہاری خبر نہ ہونے دے'),
(2160, 18, 20, 'اگر یہ کافر تم پر غلبہ پالیں تو تمہیں سنگسار کر دیں گے یا تمہیں پھر اپنے دین میں لوٹا لیں گے اور پھر تم کبھی بھی کامیاب نہ ہو سکو گے'),
(2161, 18, 21, 'ہم نے اس طرح لوگوں کو ان کے حال سے آگاه کر دیا کہ وه جان لیں کہ اللہ کا وعده بالکل سچا ہے اور قیامت میں کوئی شک وشبہ نہیں۔ جب کہ وه اپنے امر میں آپس میں اختلاف کر رہے تھے کہنے لگے کہ ان کے غار پر ایک عمارت بنا لو۔ ان کا رب ہی ان کے حال کا زیاده عالم ہے۔ جن لوگوں نے ان کے بارے میں غلبہ پایا وه کہنے لگے کہ ہم تو ان کے آس پاس مسجد بنالیں گے'),
(2162, 18, 22, 'کچھ لوگ تو کہیں گے کہ اصحاب کہف تین تھے اور چوتھا ان کا کتا تھا۔ کچھ کہیں گے کہ پانچ تھے اور چھٹا ان کا کتا تھا، غیب کی باتوں میں اٹکل (کے تیر تکے) چلاتے ہیں، کچھ کہیں گے کہ وه سات ہیں اور آٹھواں ان کا کتا ہے۔ آپ کہہ دیجیئے کہ میرا پروردگار ان کی تعداد کو بخوبی جاننے واﻻ ہے، انہیں بہت ہی کم لوگ جانتے ہیں۔ پس آپ ان کے مقدمے میں صرف سرسری گفتگو کریں اور ان میں سے کسی سے ان کے بارے میں پوچھ گچھ بھی نہ کریں'),
(2163, 18, 23, 'اور ہرگز ہرگز کسی کام پر یوں نہ کہنا کہ میں اسے کل کروں گا'),
(2164, 18, 24, 'مگر ساتھ ہی انشاءاللہ کہہ لینا۔ اور جب بھی بھولے، اپنے پروردگار کی یاد کر لیا کرنا اور کہتے رہنا کہ مجھے پوری امید ہے کہ میرا رب مجھے اس سے بھی زیاده ہدایت کے قریب کی بات کی رہبری کرے'),
(2165, 18, 25, 'وه لوگ اپنے غار میں تین سو سال تک رہے اور نو سال اور زیاده گزارے'),
(2166, 18, 26, 'آپ کہہ دیں اللہ ہی کو ان کے ٹھہرے رہنے کی مدت کا بخوبی علم ہے، آسمانوں اور زمینوں کا غیب صرف اسی کو حاصل ہے وه کیا ہی اچھا دیکھنے سننے واﻻ ہے۔ سوائے اللہ کے ان کا کوئی مددگار نہیں، اللہ تعالیٰ اپنے حکم میں کسی کو شریک نہیں کرتا'),
(2167, 18, 27, 'تیری جانب جو تیرے رب کی کتاب وحی کی گئی ہے اسے پڑھتا ره، اس کی باتوں کو کوئی بدلنے واﻻ نہیں تو اس کے سوا ہرگز ہرگز کوئی پناه کی جگہ نہ پائے گا'),
(2168, 18, 28, 'اور اپنے آپ کو انہیں کے ساتھ رکھا کر جو اپنے پروردگار کو صبح شام پکارتے ہیں اور اسی کے چہرے کے ارادے رکھتے ہیں (رضامندی چاہتے ہیں)، خبردار! تیری نگاہیں ان سے نہ ہٹنے پائیں کہ دنیوی زندگی کے ٹھاٹھ کے ارادے میں لگ جا۔ دیکھ اس کا کہنا نہ ماننا جس کے دل کو ہم نے اپنے ذکر سے غافل کردیا ہے اور جو اپنی خواہش کے پیچھے پڑا ہوا ہے اور جس کا کام حد سے گزر چکا ہے'),
(2169, 18, 29, 'اور اعلان کردے کہ یہ سراسر برحق قرآن تمہارے رب کی طرف سے ہے۔ اب جو چاہے ایمان ﻻئے اور جو چاہے کفر کرے۔ ﻇالموں کے لئے ہم نے وه آگ تیار کر رکھی ہے جس کی قناتیں انہیں گھیر لیں گی۔ اگر وه فریاد رسی چاہیں گے تو ان کی فریاد رسی اس پانی سے کی جائے گی جو تیل کی تلچھٹ جیسا ہوگا جو چہرے بھون دے گا، بڑا ہی برا پانی ہے اور بڑی بری آرام گاه (دوزخ) ہے'),
(2170, 18, 30, 'یقیناً جو لوگ ایمان ﻻئیں اور نیک اعمال کریں تو ہم کسی نیک عمل کرنے والے کا ﺛواب ضائع نہیں کرتے'),
(2171, 18, 31, 'ان کے لئے ہمیشگی والی جنتیں ہیں، ان کے نیچے نہریں جاری ہوں گی، وہاں یہ سونے کے کنگن پہنائے جائیں گے اور سبز رنگ کے نرم وباریک اور موٹے ریشم کے لباس پہنیں گے، وہاں تختوں کے اوپر تکیے لگائے ہوئے ہوں گے۔ کیا خوب بدلہ ہے، اور کس قدر عمده آرام گاه ہے'),
(2172, 18, 32, 'اور انہیں ان دو شخصوں کی مثال بھی سنا دے جن میں سے ایک کو ہم نے دو باغ انگوروں کے دے رکھے تھے اور جنہیں کھجوروں کے درختوں سے ہم نے گھیر رکھا تھا اور دونوں کے درمیان کھیتی لگا رکھی تھی'),
(2173, 18, 33, 'دونوں باغ اپنا پھل خوب ﻻئے اور اس میں کسی طرح کی کمی نہ کی اور ہم نے ان باغوں کے درمیان نہر جاری کر رکھی تھی'),
(2174, 18, 34, 'الغرض اس کے پاس میوے تھے، ایک دن اس نے باتوں ہی باتوں میں اپنے ساتھی سے کہا کہ میں تجھ سے زیاده مالدار اور جتھے کے اعتبار سے بھی زیاده مضبوط ہوں'),
(2175, 18, 35, 'اور یہ اپنے باغ میں گیا اور تھا اپنی جان پر ﻇلم کرنے واﻻ۔ کہنے لگا کہ میں خیال نہیں کرسکتا کہ کسی وقت بھی یہ برباد ہوجائے'),
(2176, 18, 36, 'اور نہ میں قیامت کو قائم ہونے والی خیال کرتا ہوں اور اگر (بالفرض) میں اپنے رب کی طرف لوٹایا بھی گیا تو یقیناً میں (اس لوٹنے کی جگہ) اس سے بھی زیاده بہتر پاؤں گا'),
(2177, 18, 37, 'اس کے ساتھی نے اس سے باتیں کرتے ہوئے کہا کہ کیا تو اس (معبود) سے کفر کرتا ہے جس نے تجھے مٹی سے پیدا کیا۔ پھر نطفے سے پھر تجھے پورا آدمی بنا دیا'),
(2178, 18, 38, 'لیکن میں تو عقیده رکھتا ہوں کہ وہی اللہ میرا پروردگار ہے میں اپنے رب کے ساتھ کسی کو شریک نہ کروں گا'),
(2179, 18, 39, 'تو نے اپنے باغ میں جاتے وقت کیوں نہ کہا کہ اللہ کا چاہا ہونے واﻻ ہے، کوئی طاقت نہیں مگر اللہ کی مدد سے، اگر تو مجھے مال واوﻻد میں اپنے سے کم دیکھ رہا ہے'),
(2180, 18, 40, 'بہت ممکن ہے کہ میرا رب مجھے تیرے اس باغ سے بھی بہتر دے اور اس پر آسمانی عذاب بھیج دے تو یہ چٹیل اور چکنا میدان بن جائے'),
(2181, 18, 41, 'یا اس کا پانی نیچے اتر جائے اور تیرے بس میں نہ رہے کہ تو اسے ڈھونڈ ﻻئے'),
(2182, 18, 42, 'اور اس کے (سارے) پھل گھیر لئے گئے، پس وه اپنے اس خرچ پر جو اس نے اس میں کیا تھا اپنے ہاتھ ملنے لگا اور وه باغ تو اوندھا الٹا پڑا تھا اور (وه شخص) یہ کہہ رہا تھا کہ کاش! میں اپنے رب کے ساتھ کسی کو بھی شریک نہ کرتا'),
(2183, 18, 43, 'اس کی حمایت میں کوئی جماعت نہ اٹھی کہ اللہ سے اس کا کوئی بچاؤ کرتی اور نہ وه خود ہی بدلہ لینے واﻻ بن سکا'),
(2184, 18, 44, 'یہیں سے (ﺛابت ہے) کہ اختیارات اللہ برحق کے لئے ہیں وه ﺛواب دینے اور انجام کے اعتبار سے بہت ہی بہتر ہے'),
(2185, 18, 45, 'ان کے سامنے دنیا کی زندگی کی مثال (بھی) بیان کرو جیسے پانی جسے ہم آسمان سے اتارتے ہیں اس سے زمین کا سبزه ملا جلا (نکلا) ہے، پھر آخر کار وه چورا چورا ہوجاتا ہے جسے ہوائیں اڑائے لئے پھرتی ہیں۔ اللہ تعالیٰ ہر چیز پر قادر ہے'),
(2186, 18, 46, 'مال واوﻻد تو دنیا کی ہی زینت ہے، اور (ہاں) البتہ باقی رہنے والی نیکیاں تیرے رب کے نزدیک از روئے ﺛواب اور (آئنده کی) اچھی توقع کے بہت بہتر ہیں'),
(2187, 18, 47, 'اور جس دن ہم پہاڑوں کو چلائیں گے اور زمین کو تو صاف کھلی ہوئی دیکھے گا اور تمام لوگوں کو ہم اکٹھا کریں گے ان میں سے ایک کو بھی باقی نہ چھوڑیں گے'),
(2188, 18, 48, 'اور سب کے سب تیرے رب کے سامنے صف بستہ حاضر کئے جائیں گے۔ یقیناً تم ہمارے پاس اسی طرح آئے جس طرح ہم نے تمہیں پہلی مرتبہ پیدا کیا تھا لیکن تم تو اسی خیال میں رہے کہ ہم ہرگز تمہارے لئے کوئی وعدے کا وقت مقرر کریں گے بھی نہیں'),
(2189, 18, 49, 'اور نامہٴ اعمال سامنے رکھ دیئے جائیں گے۔ پس تو دیکھے گا کہ گنہگار اس کی تحریر سے خوفزده ہو رہے ہوں گے اور کہہ رہے ہوں گے ہائے ہماری خرابی یہ کیسی کتاب ہے جس نے کوئی چھوٹا بڑا بغیر گھیرے کے باقی ہی نہیں چھوڑا، اور جو کچھ انہوں نے کیا تھا سب موجود پائیں گے اور تیرا رب کسی پر ﻇلم وستم نہ کرے گا'),
(2190, 18, 50, 'اور جب ہم نے فرشتوں کو حکم دیا کہ تم آدم کو سجده کرو تو ابلیس کے سوا سب نے سجده کیا، یہ جنوں میں سے تھا، اس نے اپنے پروردگار کی نافرمانی کی، کیا پھر بھی تم اسے اور اس کی اوﻻد کو مجھے چھوڑ کر اپنا دوست بنا رہے ہو؟ حاﻻنکہ وه تم سب کا دشمن ہے۔ ایسے ﻇالموں کا کیا ہی برا بدل ہے'),
(2191, 18, 51, 'میں نے انہیں آسمانوں وزمین کی پیدائش کے وقت موجود نہیں رکھا تھا اور نہ خود ان کی اپنی پیدائش میں، اور میں گمراه کرنے والوں کو اپنا مددگار بنانے واﻻ بھی نہیں'),
(2192, 18, 52, 'اور جس دن وه فرمائے گا کہ تمہارے خیال میں جو میرے شریک تھے انہیں پکارو! یہ پکاریں گے لیکن ان میں سے کوئی بھی جواب نہ دے گا ہم ان کے درمیان ہلاکت کا سامان کردیں گے'),
(2193, 18, 53, 'اور گنہگار جہنم کو دیکھ کرسمجھ لیں گے کہ وه اسی میں جھونکے جانے والے ہیں لیکن اس سے بچنے کی جگہ نہ پائیں گے'),
(2194, 18, 54, 'ہم نے اس قرآن میں ہر ہر طریقے سے تمام کی تمام مثالیں لوگوں کے لئے بیان کردی ہیں لیکن انسان سب سے زیاده جھگڑالو ہے'),
(2195, 18, 55, 'لوگوں کے پاس ہدایت آچکنے کے بعد انہیں ایمان ﻻنے اور اپنے رب سے استغفار کرنے سے صرف اسی چیز نے روکا کہ اگلے لوگوں کا سا معاملہ انہیں بھی پیش آئے یا ان کے سامنے کھلم کھلا عذاب آموجود ہوجائے'),
(2196, 18, 56, 'ہم تو اپنے رسولوں کو صرف اس لئے بھیجتے ہیں کہ وه خوشخبریاں سنا دیں اور ڈرا دیں۔ کافر لوگ باطل کے سہارے جھگڑتے ہیں اور (چاہتے ہیں کہ) اس سے حق کو لڑکھڑادیں، انہوں نے میری آیتوں کو اور جس چیز سے ڈرایا جائے اسے مذاق بنا ڈاﻻ ہے'),
(2197, 18, 57, 'اس سے بڑھ کر ﻇالم کون ہے؟ جسے اس کے رب کی آیتوں سے نصیحت کی جائے وه پھر منھ موڑے رہے اور جو کچھ اس کے ہاتھوں نے آگے بھیج رکھا ہے اسے بھول جائے، بےشک ہم نے ان کے دلوں پر پردے ڈال دیئے ہیں کہ وه اسے (نہ) سمجھیں اور ان کے کانوں میں گرانی ہے، گو تو انہیں ہدایت کی طرف بلاتا رہے، لیکن یہ کبھی بھی ہدایت نہیں پانے کے'),
(2198, 18, 58, 'تیرا پروردگار بہت ہی بخشش واﻻ اور مہربانی واﻻ ہے وه اگر ان کے اعمال کی سزا میں پکڑے تو بےشک انہیں جلد ہی عذاب کردے، بلکہ ان کے لئے ایک وعده کی گھڑی مقرر ہے جس سے وه سرکنے کی ہرگز جگہ نہیں پائیں گے'),
(2199, 18, 59, 'یہ ہیں وه بستیاں جنہیں ہم نے ان کے مظالم کی بنا پر غارت کردیا اور ان کی تباہی کی بھی ہم نے ایک میعاد مقرر کر رکھی تھی'),
(2200, 18, 60, 'جب کہ موسیٰ نے اپنے نوجوان سے کہا کہ میں تو چلتا ہی رہوں گا یہاں تک کہ دو دریاؤں کے سنگم پر پہنچوں، خواه مجھے سالہا سال چلنا پڑے'),
(2201, 18, 61, 'جب وه دونوں دریا کے سنگم پر پہنچے، وہاں اپنی مچھلی بھول گئے جس نے دریا میں سرنگ جیسا اپنا راستہ بنالیا'),
(2202, 18, 62, 'جب یہ دونوں وہاں آگے بڑھے تو موسیٰ نے اپنے نوجوان سے کہا کہ ﻻ ہمارا کھانا دے ہمیں تو اپنے اس سفر سے سخت تکلیف اٹھانی پڑی'),
(2203, 18, 63, 'اس نے جواب دیا کہ کیا آپ نے دیکھا بھی؟ جب کہ ہم پتھر سے ٹیک لگا کر آرام کر رہے تھے وہیں میں مچھلی بھول گیا تھا، دراصل شیطان نے ہی مجھے بھلا دیا کہ میں آپ سے اس کا ذکر کروں۔ اس مچھلی نے ایک انوکھے طور پر دریا میں اپنا راستہ بنالیا'),
(2204, 18, 64, 'موسیٰ نے کہا یہی تھا جس کی تلاش میں ہم تھے چنانچہ وہیں سے اپنے قدموں کے نشان ڈھونڈتے ہوئے واپس لوٹے'),
(2205, 18, 65, 'پس ہمارے بندوں میں سے ایک بندے کو پایا، جسے ہم نے اپنے پاس کی خاص رحمت عطا فرما رکھی تھی اور اسے اپنے پاس سے خاص علم سکھا رکھا تھا'),
(2206, 18, 66, 'اس سے موسیٰ نے کہا کہ میں آپ کی تابعداری کروں؟ کہ آپ مجھے اس نیک علم کو سکھا دیں جو آپ کو سکھایا گیا ہے'),
(2207, 18, 67, 'اس نے کہا آپ میرے ساتھ ہرگز صبر نہیں کرسکتے'),
(2208, 18, 68, 'اور جس چیز کو آپ نے اپنے علم میں نہ لیا ہو اس پر صبر بھی کیسے کر سکتے ہیں؟'),
(2209, 18, 69, 'موسیٰ نے جواب دیا کہ انشاءاللہ آپ مجھے صبر کرنے واﻻ پائیں گے اور کسی بات میں، میں آپ کی نافرمانی نہ کروں گا'),
(2210, 18, 70, 'اس نے کہا اچھا اگر آپ میرے ساتھ ہی چلنے پر اصرار کرتے ہیں تو یاد رہے کسی چیز کی نسبت مجھ سے کچھ نہ پوچھنا جب تک کہ میں خود اس کی نسبت کوئی تذکره نہ کروں'),
(2211, 18, 71, 'پھر وه دونوں چلے، یہاں تک کہ ایک کشتی میں سوار ہوئے، تو اس نے کشتی کے تختے توڑ دیئے، موسیٰ نے کہا کیا آپ اسے توڑ رہے ہیں تاکہ کشتی والوں کو ڈبو دیں، یہ تو آپ نے بڑی (خطرناک) بات کردی'),
(2212, 18, 72, 'اس نے جواب دیا کہ میں نے تو پہلے ہی تجھ سے کہہ دیا تھا کہ تو میرے ساتھ ہرگز صبر نہ کرسکے گا'),
(2213, 18, 73, 'موسیٰ نے جواب دیا کہ میری بھول پر مجھے نہ پکڑیئے اور مجھے اپنے کام میں تنگی میں نہ ڈالیے'),
(2214, 18, 74, 'پھر دونوں چلے، یہاں تک کہ ایک لڑکے کو پایا، اس نے اسے مار ڈاﻻ، موسیٰ نے کہا کہ کیا آپ نے ایک پاک جان کو بغیر کسی جان کے عوض مار ڈاﻻ؟ بےشک آپ نے تو بڑی ناپسندیده حرکت کی'),
(2215, 18, 75, 'وه کہنے لگے کہ میں نے تم سے نہیں کہا تھا کہ تم میرے ہمراه ره کر ہرگز صبر نہیں کر سکتے'),
(2216, 18, 76, 'موسیٰ (علیہ السلام) نے جواب دیا اگر اب اس کے بعد میں آپ سے کسی چیز کے بارے میں سوال کروں تو بیشک آپ مجھے اپنے ساتھ نہ رکھنا، یقیناً آپ میری طرف سے (حد) عذر کو پہنچ چکے'),
(2217, 18, 77, 'پھر دونوں چلے ایک گاؤں والوں کے پاس آکر ان سے کھانا طلب کیا تو انہوں نے ان کی مہمانداری سے صاف انکار کر دیا، دونوں نے وہاں ایک دیوار پائی جو گراہی چاہتی تھی، اس نے اسے ٹھیک اور درست کر دیا، موسیٰ (علیہ السلام) کہنے لگے اگر آپ چاہتے تو اس پر اجرت لے لیتے'),
(2218, 18, 78, 'اس نے کہا بس یہ جدائی ہے میرے اور تیرے درمیان، اب میں تجھے ان باتوں کی اصلیت بھی بتا دوں گا جس پر تجھ سے صبر نہ ہو سکا'),
(2219, 18, 79, 'کشتی تو چند مسکینوں کی تھی جو دریا میں کام کاج کرتے تھے۔ میں نے اس میں کچھ توڑ پھوڑ کرنے کا اراده کر لیا کیونکہ ان کے آگے ایک بادشاه تھا جو ہر ایک (صحیح سالم) کشتی کو جبراً ضبط کر لیتا تھا'),
(2220, 18, 80, 'اور اس لڑکے کے ماں باپ ایمان والے تھے۔ ہمیں خوف ہوا کہ کہیں یہ انہیں اپنی سرکشی اور کفر سے عاجز وپریشان نہ کر دے'),
(2221, 18, 81, 'اس لئے ہم نے چاہا کہ انہیں ان کا پروردگار اس کے بدلے اس سے بہتر پاکیزگی واﻻ اور اس سے زیاده محبت اور پیار واﻻ بچہ عنایت فرمائے'),
(2222, 18, 82, 'دیوار کا قصہ یہ ہے کہ اس شہر میں دو یتیم بچے ہیں جن کا خزانہ ان کی اس دیوار کے نیچے دفن ہے، ان کا باپ بڑا نیک شخص تھا تو تیرے رب کی چاہت تھی کہ یہ دونوں یتیم اپنی جوانی کی عمر میں آکر اپنا یہ خزانہ تیرے رب کی مہربانی اور رحمت سے نکال لیں، میں نے اپنی رائے سے کوئی کام نہیں کیا، یہ تھی اصل حقیقت ان واقعات کی جن پر آپ سے صبر نہ ہو سکا'),
(2223, 18, 83, 'آپ سے ذوالقرنین کا واقعہ یہ لوگ دریافت کر رہے ہیں، آپ کہہ دیجئے کہ میں ان کا تھوڑا سا حال تمہیں پڑھ کر سناتا ہوں'),
(2224, 18, 84, 'ہم نے اسے زمین میں قوت عطا فرمائی تھی اور اسے ہر چیز کے سامان بھی عنایت کردیے تھے'),
(2225, 18, 85, 'وه ایک راه کے پیچھے لگا'),
(2226, 18, 86, 'یہاں تک کہ سورج ڈوبنے کی جگہ پہنچ گیا اور اسے ایک دلدل کےچشمے میں غروب ہوتا ہوا پایا اور اس چشمے کے پاس ایک قوم کو بھی پایا، ہم نے فرما دیا کہ اے ذوالقرنین! یا تو تو انہیں تکلیف پہنچائے یا ان کے بارے میں تو کوئی بہترین روش اختیار کرے'),
(2227, 18, 87, 'اس نے کہا کہ جو ﻇلم کرے گا اسے تو ہم بھی اب سزا دیں گے، پھر وه اپنے پروردگار کی طرف لوٹایا جائے گا اور وه اسے سخت تر عذاب دے گا'),
(2228, 18, 88, 'ہاں جو ایمان ﻻئے اور نیک اعمال کرے اس کے لئے تو بدلے میں بھلائی ہے اور ہم اسے اپنے کام میں بھی آسانی ہی کا حکم دیں گے'),
(2229, 18, 89, 'پھر وه اور راه کے پیچھے لگا'),
(2230, 18, 90, 'یہاں تک کہ جب سورج نکلنے کی جگہ تک پہنچا تو اسے ایک ایسی قوم پر نکلتا پایا کہ ان کے لئے ہم نے اس سے اور کوئی اوٹ نہیں بنائی'),
(2231, 18, 91, 'واقعہ ایسا ہی ہے اور ہم نے اس کے پاس کی کل خبروں کا احاطہ کر رکھا ہے'),
(2232, 18, 92, 'وه پھر ایک سفر کے سامان میں لگا'),
(2233, 18, 93, 'یہاں تک کہ جب دو دیواروں کے درمیان پہنچا ان دونوں کے پرے اس نے ایک ایسی قوم پائی جو بات سمجھنے کے قریب بھی نہ تھی'),
(2234, 18, 94, 'انہوں نے کہا اے ذوالقرنین! یاجوج ماجوج اس ملک میں (بڑے بھاری) فسادی ہیں، تو کیا ہم آپ کے لئے کچھ خرچ کا انتظام کر دیں؟ (اس شرط پر کہ) آپ ہمارے اور ان کے درمیان ایک دیوار بنادیں'),
(2235, 18, 95, 'اس نے جواب دیا کہ میرے اختیار میں میرے پروردگار نے جو دے رکھا ہے وہی بہتر ہے، تم صرف قوت طاقت سے میری مدد کرو'),
(2236, 18, 96, 'میں تم میں اور ان میں مضبوط حجاب بنا دیتا ہوں۔ مجھے لوہے کی چادریں ﻻ دو۔ یہاں تک کہ جب ان دونوں پہاڑوں کے درمیان دیوار برابر کر دی تو حکم دیا کہ آگ تیز جلاؤ تاوقتیکہ لوہے کی ان چادروں کو بالکل آگ کر دیا، تو فرمایا میرے پاس ﻻؤ اس پر پگھلا ہوا تانبا ڈال دوں'),
(2237, 18, 97, 'پس تو ان میں اس دیوار کے اوپر چڑھنے کی طاقت تھی اور نہ ان میں کوئی سوراخ کر سکتے تھے'),
(2238, 18, 98, 'کہا یہ صرف میرے رب کی مہربانی ہے ہاں جب میرے رب کا وعده آئے گا تو اسے زمین بوس کر دے گا، بیشک میرے رب کا وعده سچا اور حق ہے'),
(2239, 18, 99, 'اس دن ہم انہیں آپس میں ایک دوسرے میں گڈ مڈ ہوتے ہوئے چھوڑ دیں گے اور صور پھونک دیا جائے گا پس سب کو اکٹھا کرکے ہم جمع کر لیں گے'),
(2240, 18, 100, 'اس دن ہم جہنم کو (بھی) کافروں کے سامنے ﻻکھڑا کر دیں گے'),
(2241, 18, 101, 'جن کی آنکھیں میری یاد سے پردے میں تھی اور (امر حق) سن بھی نہیں سکتے تھے'),
(2242, 18, 102, 'کیا کافر یہ خیال کیے بیٹھے ہیں؟ کہ میرے سوا وه میرے بندوں کو اپنا حمایتی بنا لیں گے؟ (سنو) ہم نے تو ان کفار کی مہمانی کے لئے جہنم کو تیار کر رکھا ہے'),
(2243, 18, 103, 'کہہ دیجئے کہ اگر (تم کہو تو) میں تمہیں بتا دوں کہ باعتبار اعمال سب سے زیاده خسارے میں کون ہیں؟'),
(2244, 18, 104, 'وه ہیں کہ جن کی دنیوی زندگی کی تمام تر کوششیں بیکار ہوگئیں اور وه اسی گمان میں رہے کہ وه بہت اچھے کام کر رہے ہیں'),
(2245, 18, 105, 'یہی وه لوگ ہیں جنہوں نے اپنے پروردگار کی آیتوں اور اس کی ملاقات سے کفر کیا، اس لئے ان کے اعمال غارت ہوگئے پس قیامت کے دن ہم ان کا کوئی وزن قائم نہ کریں گے'),
(2246, 18, 106, 'حال یہ ہے کہ ان کا بدلہ جہنم ہے کیونکہ انہوں نے کفر کیا اور میری آیتوں اور میرے رسولوں کو مذاق میں اڑایا'),
(2247, 18, 107, 'جو لوگ ایمان ﻻئے اور انہوں نے کام بھی اچھے کیے یقیناً ان کے لئے الفردوس کے باغات کی مہمانی ہے'),
(2248, 18, 108, 'جہاں وه ہمیشہ رہا کریں گے جس جگہ کو بدلنے کا کبھی بھی ان کا اراده ہی نہ ہوگا'),
(2249, 18, 109, 'کہہ دیجئے کہ اگر میرے پروردگار کی باتوں کے لکھنے کے لئے سمندر سیاہی بن جائے تو وه بھی میرے رب کی باتوں کے ختم ہونے سے پہلے ہی ختم ہو جائے گا، گو ہم اسی جیسا اور بھی اس کی مدد میں لے آئیں'),
(2250, 18, 110, 'آپ کہہ دیجئے کہ میں تو تم جیسا ہی ایک انسان ہوں۔ (ہاں) میری جانب وحی کی جاتی ہے کہ سب کا معبود صرف ایک ہی معبود ہے، تو جسے بھی اپنے پروردگار سے ملنے کی آرزو ہو اسے چاہئے کہ نیک اعمال کرے اور اپنے پروردگار کی عبادت میں کسی کو بھی شریک نہ کرے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2251, 19, 1, 'کہیعص'),
(2252, 19, 2, 'یہ ہے تیرے پروردگار کی اس مہربانی کا ذکر جو اس نے اپنے بندے زکریا پر کی تھی'),
(2253, 19, 3, 'جبکہ اس نے اپنے رب سے چپکے چپکے دعا کی تھی'),
(2254, 19, 4, 'کہ اے میرے پروردگار! میری ہڈیاں کمزور ہوگئی ہیں اور سر بڑھاپے کی وجہ سے بھڑک اٹھا ہے، لیکن میں کبھی بھی تجھ سے دعا کر کے محروم نہیں رہا'),
(2255, 19, 5, 'مجھے اپنے مرنے کے بعد اپنے قرابت والوں کا ڈر ہے، میری بیوی بھی بانجھ ہے پس تو مجھے اپنے پاس سے وارث عطا فرما'),
(2256, 19, 6, 'جو میرا بھی وارث ہو اور یعقوب (علیہ السلام) کے خاندان کا بھی جانشین اور میرے رب! تو اسے مقبول بنده بنا لے'),
(2257, 19, 7, 'اے زکریا! ہم تجھے ایک بچے کی خوشخبری دیتے ہیں جس کا نام یحيٰ ہے، ہم نے اس سے پہلے اس کا ہم نام بھی کسی کو نہیں کیا'),
(2258, 19, 8, 'زکریا (علیہ السلام) کہنے لگے میرے رب! میرے ہاں لڑکا کیسے ہوگا، جبکہ میری بیوی بانجھ اور میں خود بڑھاپے کے انتہائی ضعف کو پہنچ چکا ہوں'),
(2259, 19, 9, 'ارشاد ہوا کہ وعده اسی طرح ہو چکا، تیرے رب نے فرما دیا ہے کہ مجھ پر تو یہ بالکل آسان ہے اور تو خود جبکہ کچھ نہ تھا میں تجھے پیدا کر چکا ہوں'),
(2260, 19, 10, 'کہنے لگے میرے پروردگار میرے لئے کوئی علامت مقرر فرما دے، ارشاد ہوا کہ تیرے لئے علامت یہ ہے کہ باوجود بھلا چنگا ہونے کے تو تین راتوں تک کسی شخص سے بول نہ سکے گا'),
(2261, 19, 11, 'اب زکریا (علیہ السلام) اپنے حجرے سے نکل کر اپنی قوم کے پاس آکر انہیں اشاره کرتے ہیں کہ تم صبح وشام اللہ تعالیٰ کی تسبیح بیان کرو'),
(2262, 19, 12, 'اے یحيٰ! میری کتاب کو مضبوطی سے تھام لے اور ہم نے اسے لڑکپن ہی سے دانائی عطا فرما دی'),
(2263, 19, 13, 'اور اپنے پاس سے شفقت اور پاکیزگی بھی، وه پرہیزگار شخص تھا'),
(2264, 19, 14, 'اور اپنے ماں باپ سے نیک سلوک کرنے واﻻ تھا وه سرکش اور گناه گار نہ تھا'),
(2265, 19, 15, 'اس پر سلام ہے جس دن وه پیدا ہوا اور جس دن وه مرے اور جس دن وه زنده کرکے اٹھایا جائے'),
(2266, 19, 16, 'اس کتاب میں مریم کا بھی واقعہ بیان کر۔ جبکہ وه اپنے گھر کے لوگوں سے علیحده ہو کر مشرقی جانب آئیں'),
(2267, 19, 17, 'اور ان لوگوں کی طرف سے پرده کر لیا، پھر ہم نے اس کے پاس اپنی روح (جبرائیل علیہ السلام) کو بھیجا پس وه اس کے سامنے پورا آدمی بن کر ﻇاہر ہوا'),
(2268, 19, 18, 'یہ کہنے لگیں میں تجھ سے رحمٰن کی پناه مانگتی ہوں اگر تو کچھ بھی اللہ سے ڈرنے واﻻ ہے'),
(2269, 19, 19, 'اس نے جواب دیا کہ میں تو اللہ کا بھیجا ہوا قاصد ہوں، تجھے ایک پاکیزه لڑکا دینے آیا ہوں'),
(2270, 19, 20, 'کہنے لگیں بھلا میرے ہاں بچہ کیسے ہو سکتا ہے؟ مجھے تو کسی انسان کا ہاتھ تک نہیں لگا اور نہ میں بدکار ہوں'),
(2271, 19, 21, 'اس نے کہا بات تو یہی ہے۔ لیکن تیرے پروردگار کا ارشاد ہے کہ وه مجھ پر بہت ہی آسان ہے ہم تو اسے لوگوں کے لئے ایک نشانی بنا دیں گے اور اپنی خاص رحمت، یہ تو ایک طے شده بات ہے'),
(2272, 19, 22, 'پس وه حمل سے ہو گئیں اوراسی وجہ سے وه یکسو ہو کر ایک دور کی جگہ چلی گئیں'),
(2273, 19, 23, 'پھر درِد زه ایک کھجور کے تنے کے نیچے لے آیا، بولی کاش! میں اس سے پہلے ہی مر گئی ہوتی اور لوگوں کی یاد سے بھی بھولی بسری ہو جاتی'),
(2274, 19, 24, 'اتنے میں اسے نیچے سے ہی آواز دی کہ آزرده خاطر نہ ہو، تیرے رب نے تیرے پاؤں تلے ایک چشمہ جاری کر دیا ہے'),
(2275, 19, 25, 'اور اس کھجور کے تنے کو اپنی طرف ہلا، یہ تیرے سامنے تروتازه پکی کھجوریں گرا دے گا'),
(2276, 19, 26, 'اب چین سے کھا پی اور آنکھیں ٹھنڈی رکھ، اگر تجھے کوئی انسان نظر پڑ جائے تو کہہ دینا کہ میں نے اللہ رحمنٰ کے نام کا روزه مان رکھا ہے۔ میں آج کسی شخص سے بات نہ کروں گی'),
(2277, 19, 27, 'اب حضرت عیسیٰ (علیہ السلام) کو لئے ہوئے وه اپنی قوم کے پاس آئیں۔ سب کہنے لگے مریم تو نے بڑی بری حرکت کی'),
(2278, 19, 28, 'اے ہارون کی بہن! نہ تو تیرا باپ برا آدمی تھا اور نہ تیری ماں بدکار تھی'),
(2279, 19, 29, 'مریم نے اپنے بچے کی طرف اشاره کیا۔ سب کہنے لگے کہ لو بھلا ہم گود کے بچے سے باتیں کیسے کریں؟'),
(2280, 19, 30, 'بچہ بول اٹھا کہ میں اللہ تعالیٰ کا بنده ہوں۔ اس نے مجھے کتاب عطا فرمائی اور مجھے اپنا پیغمبر بنایا ہے'),
(2281, 19, 31, 'اور اس نے مجھے بابرکت کیا ہے جہاں بھی میں ہوں، اور اس نے مجھے نماز اور زکوٰة کا حکم دیا ہے جب تک بھی میں زنده رہوں'),
(2282, 19, 32, 'اور اس نے مجھے اپنی والده کا خدمت گزار بنایا ہے اور مجھے سرکش اور بدبخت نہیں کیا'),
(2283, 19, 33, 'اور مجھ پر میری پیدائش کے دن اور میری موت کے دن اور جس دن کہ میں دوباره زنده کھڑا کیا جاؤں گا، سلام ہی سلام ہے'),
(2284, 19, 34, 'یہ ہے صحیح واقعہ عیسیٰ بن مریم (علیہ السلام) کا، یہی ہے وه حق بات جس میں لوگ شک وشبہ میں مبتلا ہیں'),
(2285, 19, 35, 'اللہ تعالیٰ کے لئے اوﻻد کا ہونا ﻻئق نہیں، وه تو بالکل پاک ذات ہے، وه تو جب کسی کام کے سر انجام دینے کا اراده کرتا ہے تو اسے کہہ دیتا ہے کہ ہو جا، وه اسی وقت ہو جاتا ہے'),
(2286, 19, 36, 'میرا اور تم سب کا پروردگار صرف اللہ تعالیٰ ہی ہے۔ تم سب اسی کی عبادت کرو، یہی سیدھی راه ہے'),
(2287, 19, 37, 'پھر یہ فرقے آپس میں اختلاف کرنے لگے، پس کافروں کے لئے ویل ہے ایک بڑے (سخت) دن کی حاضری سے'),
(2288, 19, 38, 'کیا خوب دیکھنے سننے والے ہوں گے اس دن جبکہ ہمارے سامنے حاضر ہوں گے، لیکن آج تو یہ ﻇالم لوگ صریح گمراہی میں پڑے ہوئے ہیں'),
(2289, 19, 39, 'تو انہیں اس رنج وافسوس کے دن کا ڈر سنا دے جبکہ کام انجام کو پہنچا دیا جائے گا، اور یہ لوگ غفلت اور بے ایمانی میں ہی ره جائیں گے'),
(2290, 19, 40, 'خود زمین کے اور تمام زمین والوں کے وارث ہم ہی ہوں گے اور سب لوگ ہماری ہی طرف لوٹا کر ﻻئے جائیں گے'),
(2291, 19, 41, 'اس کتاب میں ابراہیم (علیہ السلام) کا قصہ بیان کر، بیشک وه بڑی سچائی والے پیغمبر تھے'),
(2292, 19, 42, 'جبکہ انہوں نے اپنے باپ سے کہا کہ ابّا جان! آپ ان کی پوجا پاٹ کیوں کر رہے ہیں جو نہ سنیں نہ دیکھیں؟ نہ آپ کو کچھ بھی فائده پہنچا سکیں'),
(2293, 19, 43, 'میرے مہربان باپ! آپ دیکھیئے میرے پاس وه علم آیا ہے جو آپ کے پاس آیا ہی نہیں، تو آپ میری ہی مانیں میں بالکل سیدھی راه کی طرف آپ کی رہبری کروں گا'),
(2294, 19, 44, 'میرے ابّا جان آپ شیطان کی پرستش سے باز آجائیں شیطان تو رحم وکرم والے اللہ تعالیٰ کا بڑا ہی نافرمان ہے'),
(2295, 19, 45, 'ابّا جان! مجھے خوف لگا ہوا کہ کہیں آپ پر کوئی عذاب الٰہی نہ آپڑے کہ آپ شیطان کے ساتھی بن جائیں'),
(2296, 19, 46, 'اس نے جواب دیا کہ اے ابراہیم! کیا تو ہمارے معبودوں سے روگردانی کر رہا ہے۔ سن اگر تو باز نہ آیا تو میں تجھے پتھروں سے مار ڈالوں گا، جا ایک مدت دراز تک مجھ سے الگ ره'),
(2297, 19, 47, 'کہا اچھا تم پر سلام ہو، میں تو اپنے پروردگار سے تمہاری بخشش کی دعا کرتا رہوں گا، وه مجھ پر حد درجہ مہربان ہے'),
(2298, 19, 48, 'میں تو تمہیں بھی اور جن جن کو تم اللہ تعالیٰ کے سوا پکارتے ہو انہیں بھی سب کو چھوڑ رہا ہوں۔ صرف اپنے پروردگار کو پکارتا رہوں گا، مجھے یقین ہے کہ میں اپنے پروردگار سے دعا مانگ کر محروم نہ رہوں گا'),
(2299, 19, 49, 'جب ابراہیم (علیہ السلام) ان سب کو اور اللہ کے سوا ان کے سب معبودوں کو چھوڑ چکے تو ہم نے انہیں اسحاق ویعقوب (علیہما السلام) عطا فرمائے، اور دونوں کو نبی بنا دیا'),
(2300, 19, 50, 'اور ان سب کو ہم نے اپنی بہت سی رحمتیں عطا فرمائیں اور ہم نے ان کے ذکر جمیل کو بلند درجے کا کر دیا'),
(2301, 19, 51, 'اس قرآن میں موسیٰ (علیہ السلام) کا ذکر بھی کر، جو چنا ہوا اور رسول اور نبی تھا'),
(2302, 19, 52, 'ہم نے اسے طور کی دائیں جانب سے ندا کی اور راز گوئی کرتے ہوئے اسے قریب کر لیا'),
(2303, 19, 53, 'اور اپنی خاص مہربانی سے اس کے بھائی کو نبی بنا کر عطا فرمایا'),
(2304, 19, 54, 'اس کتاب میں اسماعیل (علیہ السلام) کا واقعہ بھی بیان کر، وه بڑا ہی وعدے کا سچا تھا اور تھا بھی رسول اور نبی'),
(2305, 19, 55, 'وه اپنے گھر والوں کو برابر نماز اور زکوٰة کاحکم دیتا تھا، اور تھا بھی اپنے پروردگار کی بارگاه میں پسندیده اور مقبول'),
(2306, 19, 56, 'اور اس کتاب میں ادریس (علیہ السلام) کا بھی ذکر کر، وه بھی نیک کردار پیغمبر تھا'),
(2307, 19, 57, 'ہم نےاسے بلند مقام پر اٹھا لیا'),
(2308, 19, 58, 'یہی وه انبیا ہیں جن پر اللہ تعالیٰ نے فضل وکرم کیا جو اوﻻد آدم میں سے ہیں اور ان لوگوں کی نسل سے ہیں جنہیں ہم نے نوح (علیہ السلام) کے ساتھ کشتی میں چڑھا لیا تھا، اور اوﻻد ابراہیم ویعقوب سے اور ہماری طرف سے راه یافتہ اور ہمارے پسندیده لوگوں میں سے۔ ان کے سامنے جب اللہ رحمان کی آیتوں کی تلاوت کی جاتی تھی یہ سجده کرتے اور روتے گڑگڑاتے گر پڑتے تھے'),
(2309, 19, 59, 'پھر ان کے بعد ایسے ناخلف پیدا ہوئے کہ انہوں نے نماز ضائع کردی اور نفسانی خواہشوں کے پیچھے پڑ گئے، سو ان کا نقصان ان کے آگے آئے گا'),
(2310, 19, 60, 'بجز ان کے جو توبہ کر لیں اور ایمان ﻻئیں اور نیک عمل کریں۔ ایسے لوگ جنت میں جائیں گے اور ان کی ذرا سی بھی حق تلفی نہ کی جائے گی'),
(2311, 19, 61, 'ہمیشگی والی جنتوں میں جن کا غائبانہ وعده اللہ مہربان نے اپنے بندوں سے کیا ہے۔ بیشک اس کا وعده پورا ہونے واﻻ ہی ہے'),
(2312, 19, 62, 'وه لوگ وہاں کوئی لغو بات نہ سنیں گے صرف سلام ہی سلام سنیں گے، ان کے لئے وہاں صبح شام ان کا رزق ہوگا'),
(2313, 19, 63, 'یہ ہے وه جنت جس کا وارث ہم اپنے بندوں میں سے انہیں بناتے ہیں جو متقی ہوں'),
(2314, 19, 64, 'ہم بغیر تیرے رب کے حکم کے اتر نہیں سکتے، ہمارے آگے پیچھے اور ان کے درمیان کی کل چیزیں اسی کی ملکیت میں ہیں، تیرا پروردگار بھولنے واﻻ نہیں'),
(2315, 19, 65, 'آسمانوں کا، زمین کا اور جو کچھ ان کے درمیان ہے سب کا رب وہی ہے تو اسی کی بندگی کر اور اس کی عبادت پر جم جا۔ کیا تیرے علم میں اس کا ہمنام ہم پلہ کوئی اور بھی ہے؟'),
(2316, 19, 66, 'انسان کہتا ہے کہ جب میں مرجاؤں گا تو کیا پھر زنده کر کے نکالا جاؤں گا؟'),
(2317, 19, 67, 'کیا یہ انسان اتنا بھی یاد نہیں رکھتا کہ ہم نے اسے اس سے پہلے پیدا کیا حاﻻنکہ وه کچھ بھی نہ تھا'),
(2318, 19, 68, 'تیرے پروردگار کی قسم! ہم انہیں اور شیطانوں کو جمع کر کے ضرور ضرور جہنم کے اردگرد گھٹنوں کے بل گرے ہوئے حاضر کر دیں گے'),
(2319, 19, 69, 'ہم پھر ہر ہر گروه سے انہیں الگ نکال کھڑا کریں گے جو اللہ رحمنٰ سے بہت اکڑے اکڑے پھرتے تھے'),
(2320, 19, 70, 'پھر ہم انہیں بھی خوب جانتے ہیں جو جہنم کے داخلے کے زیاده سزاوار ہیں'),
(2321, 19, 71, 'تم میں سے ہر ایک وہاں ضرور وارد ہونے واﻻ ہے، یہ تیرے پروردگار کے ذمے قطعی، فیصل شده امر ہے'),
(2322, 19, 72, 'پھر ہم پرہیزگاروں کو تو بچالیں گے اور نافرمانوں کو اسی میں گھٹنوں کے بل گرا ہوا چھوڑ دیں گے'),
(2323, 19, 73, 'جب ان کے سامنے ہماری روشن آیتیں تلاوت کی جاتی ہیں تو کافر مسلمانوں سے کہتے ہیں بتاؤ ہم تم دونوں جماعتوں میں سے کس کا مرتبہ زیاده ہے؟ اور کس کی مجلس شاندار ہے؟'),
(2324, 19, 74, 'ہم تو ان سے پہلے بہت سی جماعتوں کو غارت کر چکے ہیں جو ساز وسامان اور نام ونمود میں ان سے بڑھ چڑھ کر تھیں'),
(2325, 19, 75, 'کہہ دیجئے! جو گمراہی میں ہوتا اللہ رحمنٰ اس کو خوب لمبی مہلت دیتا ہے، یہاں تک کہ وه ان چیزوں کو دیکھ لیں جنکا وعده کیے جاتے ہیں یعنی عذاب یا قیامت کو، اس وقت ان کو صحیح طور پر معلوم ہو جائے گا کہ کون برے مرتبے واﻻ اور کس کا جتھا کمزور ہے'),
(2326, 19, 76, 'اور ہدایت یافتہ لوگوں کو اللہ تعالیٰ ہدایت میں بڑھاتا ہے، اور باقی رہنے والی نیکیاں تیرے رب کے نزدیک ﺛواب کے لحاظ سے اور انجام کے لحاظ سے بہت ہی بہتر ہیں'),
(2327, 19, 77, 'کیا تو نے اسے بھی دیکھا جس نے ہماری آیتوں سے کفر کیا اور کہا کہ مجھے تو مال واوﻻد ضرور ہی دی جائے گی'),
(2328, 19, 78, 'کیا وه غیب پر مطلع ہے یا اللہ کا کوئی وعده لے چکا ہے؟'),
(2329, 19, 79, 'ہرگز نہیں، یہ جو بھی کہہ رہا ہے ہم اسے ضرور لکھ لیں گے، اور اس کے لئے عذاب بڑھائے چلے جائیں گے'),
(2330, 19, 80, 'یہ جن چیزوں کو کہہ رہا ہے اسے ہم اس کے بعد لے لیں گے۔ اور یہ تو بالکل اکیلا ہی ہمارے سامنے حاضر ہوگا'),
(2331, 19, 81, 'انہوں نے اللہ کے سوا دوسرے معبود بنا رکھے ہیں کہ وه ان کے لئے باعﺚ عزت ہوں'),
(2332, 19, 82, 'لیکن ایسا ہرگز ہونا نہیں۔ وه تو ان کی پوجا سے منکر ہو جائیں گے، اور الٹے ان کے دشمن بن جائیں گے'),
(2333, 19, 83, 'کیا تو نے نہیں دیکھا کہ ہم کافروں کے پاس شیطانوں کو بھیجتے ہیں جو انہیں خوب اکساتے ہیں'),
(2334, 19, 84, 'تو ان کے بارے میں جلدی نہ کر، ہم تو خود ہی ان کے لئے مدت شماری کر رہے ہیں'),
(2335, 19, 85, 'جس دن ہم پرہیزگاروں کو اللہ رحمان کی طرف بطور مہمان جمع کریں گے'),
(2336, 19, 86, 'اور گناه گاروں کوسخت پیاس کی حالت میں جہنم کی طرف ہانک لے جائیں گے'),
(2337, 19, 87, 'کسی کو شفاعت کا اختیار نہ ہوگا سوائے ان کے جنہوں نے اللہ تعالیٰ کی طرف سے کوئی قول قرار لے لیا ہے'),
(2338, 19, 88, 'ان کا قول تو یہ ہے کہ اللہ رحمٰن نے بھی اوﻻد اختیار کی ہے'),
(2339, 19, 89, 'یقیناً تم بہت بری اور بھاری چیز ﻻئے ہو'),
(2340, 19, 90, 'قریب ہے کہ اس قول کی وجہ سے آسمان پھٹ جائیں اور زمین شق ہوجائے اور پہاڑ ریزے ریزے ہو جائیں'),
(2341, 19, 91, 'کہ وه رحمان کی اوﻻد ﺛابت کرنے بیٹھے'),
(2342, 19, 92, 'شان رحمٰن کے ﻻئق نہیں کہ وه اوﻻد رکھے'),
(2343, 19, 93, 'آسمان وزمین میں جو بھی ہیں سب کے سب اللہ کے غلام بن کر ہی آنے والے ہیں'),
(2344, 19, 94, 'ان سب کو اس نے گھیر رکھا ہے اور سب کو پوری طرح گن بھی رکھا ہے'),
(2345, 19, 95, 'یہ سارے کے سارے قیامت کے دن اکیلے اس کے پاس حاضر ہونے والے ہیں'),
(2346, 19, 96, 'بیشک جو ایمان لائے ہیں اور جنہوں نے شائستہ اعمال کیے ہیں ان کے لیے اللہ رحمٰن محبت پیدا کردے گا'),
(2347, 19, 97, 'ہم نے اس قرآن کو تیری زبان میں بہت ہی آسان کر دیا ہے کہ تو اس کے ذریعہ سے پرہیزگاروں کو خوشخبری دے اور جھگڑالو لوگوں کو ڈرا دے'),
(2348, 19, 98, 'ہم نے ان سے پہلے بہت سی جماعتیں تباہ کر دی ہیں، کیا ان میں سے ایک کی بھی آہٹ تو پاتا ہے یا ان کی آواز کی بھنک بھی تیرے کان میں پڑتی ہے؟');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2349, 20, 1, 'طٰہٰ'),
(2350, 20, 2, 'ہم نے یہ قرآن تجھ پر اس لئے نہیں اتارا کہ تو مشقت میں پڑ جائے'),
(2351, 20, 3, 'بلکہ اس کی نصیحت کے لئے جو اللہ سے ڈرتا ہے'),
(2352, 20, 4, 'اس کا اتارنا اس کی طرف سے ہے جس نے زمین کو اور بلند آسمانوں کو پیدا کیا ہے'),
(2353, 20, 5, 'جو رحمٰن ہے، عرش پر قائم ہے'),
(2354, 20, 6, 'جس کی ملکیت آسمانوں اور زمین اور ان دونوں کے درمیان اور (کرہٴ خاک) کے نیچے کی ہر ایک چیز پر ہے'),
(2355, 20, 7, 'اگر تو اونچی بات کہے تو وه تو ہر ایک پوشیده، بلکہ پوشیده سے پوشیده تر چیز کو بھی بخوبی جانتا ہے'),
(2356, 20, 8, 'وہی اللہ ہے جس کے سوا کوئی معبود نہیں، بہترین نام اسی کے ہیں'),
(2357, 20, 9, 'تجھے موسیٰ (علیہ السلام) کا قصہ بھی معلوم ہے؟'),
(2358, 20, 10, 'جبکہ اس نے آگ دیکھ کر اپنے گھر والوں سے کہا کہ تم ذرا سی دیر ٹھہر جاؤ مجھے آگ دکھائی دی ہے۔ بہت ممکن ہے کہ میں اس کا کوئی انگارا تمہارے پاس ﻻؤں یا آگ کے پاس سے راستے کی اطلاع پاؤں'),
(2359, 20, 11, 'جب وه وہاں پہنچے تو آواز دی گئی اے موسیٰ!'),
(2360, 20, 12, 'یقیناً میں ہی تیرا پروردگار ہوں تو اپنی جوتیاں اتار دے، کیونکہ تو پاک میدان طویٰ میں ہے'),
(2361, 20, 13, 'اور میں نے تجھے منتخب کر لیا اب جو وحی کی جائے اسے کان لگا کر سن'),
(2362, 20, 14, 'بیشک میں ہی اللہ ہوں، میرے سوا عبادت کے ﻻئق اور کوئی نہیں پس تو میری ہی عبادت کر، اور میری یاد کے لئے نماز قائم رکھ'),
(2363, 20, 15, 'قیامت یقیناً آنے والی ہے جسے میں پوشیده رکھنا چاہتا ہوں تاکہ ہر شخص کو وه بدلہ دیا جائے جو اس نے کوشش کی ہو'),
(2364, 20, 16, 'پس اب اس کے یقین سے تجھے کوئی ایسا شخص روک نہ دے جو اس پر ایمان نہ رکھتا ہو اور اپنی خواہش کے پیچھے پڑا ہو، ورنہ تو ہلاک ہوجائے گا'),
(2365, 20, 17, 'اے موسیٰ! تیرے اس دائیں ہاتھ میں کیا ہے؟'),
(2366, 20, 18, 'جواب دیا کہ یہ میری ﻻٹھی ہے، جس پر میں ٹیک لگاتا ہوں اور جس سے میں اپنی بکریوں کے لئے پتے جھاڑ لیا کرتا ہوں اور بھی اس میں مجھے بہت سے فائدے ہیں'),
(2367, 20, 19, 'فرمایا اے موسیٰ! اسے ہاتھ سے نیچے ڈال دے'),
(2368, 20, 20, 'ڈالتے ہی وه سانﭗ بن کر دوڑنے لگی'),
(2369, 20, 21, 'فرمایا بے خوف ہو کر اسے پکڑ لے، ہم اسے اسی پہلی سی صورت میں دوباره ﻻ دیں گے'),
(2370, 20, 22, 'اور اپنا ہاتھ اپنی بغل میں ڈال لے تو وه سفید چمکتا ہوا ہو کر نکلے گا، لیکن بغیر کسی عیب (اور روگ) کے یہ دوسرا معجزه ہے'),
(2371, 20, 23, 'یہ اس لئے کہ ہم تجھے اپنی بڑی بڑی نشانیاں دکھانا چاہتے ہیں'),
(2372, 20, 24, 'اب تو فرعون کی طرف جا اس نے بڑی سرکشی مچا رکھی ہے'),
(2373, 20, 25, 'موسیٰ (علیہ السلام) نے کہا اے میرے پروردگار! میرا سینہ میرے لئے کھول دے'),
(2374, 20, 26, 'اور میرے کام کو مجھ پر آسان کر دے'),
(2375, 20, 27, 'اور میری زبان کی گره بھی کھول دے'),
(2376, 20, 28, 'تاکہ لوگ میری بات اچھی طرح سمجھ سکیں'),
(2377, 20, 29, 'اور میرا وزیر میرے کنبے میں سے کر دے'),
(2378, 20, 30, 'یعنی میرے بھائی ہارون (علیہ السلام) کو'),
(2379, 20, 31, 'تو اس سے میری کمر کس دے'),
(2380, 20, 32, 'اور اسے میرا شریک کار کر دے'),
(2381, 20, 33, 'تاکہ ہم دونوں بکثرت تیری تسبیح بیان کریں'),
(2382, 20, 34, 'اور بکثرت تیری یاد کریں'),
(2383, 20, 35, 'بیشک تو ہمیں خوب دیکھنے بھالنے واﻻ ہے'),
(2384, 20, 36, 'جناب باری تعالیٰ نے فرمایا موسیٰ تیرے تمام سواﻻت پورے کر دیئے گئے'),
(2385, 20, 37, 'ہم نے تو تجھ پرایک بار اور بھی بڑا احسان کیا ہے'),
(2386, 20, 38, 'جبکہ ہم نے تیری ماں کو وه الہام کیا جس کا ذکر اب کیا جا رہا ہے'),
(2387, 20, 39, 'کہ تو اسے صندوق میں بند کرکے دریا میں چھوڑ دے، پس دریا اسے کنارے ﻻ ڈالے گا اور میرا اور خود اس کا دشمن اسے لے لے گا، اور میں نے اپنی طرف کی خاص محبت ومقبولیت تجھ پر ڈال دی۔ تاکہ تیری پرورش میری آنکھوں کے سامنے کی جائے'),
(2388, 20, 40, '(یاد کر) جبکہ تیری بہن چل رہی تھی اور کہہ رہی تھی کہ اگر تم کہو تو میں اسے بتا دوں جو اس کی نگہبانی کرے، اس تدبیر سے ہم نے تجھے پھر تیری ماں کے پاس پہنچایا کہ اس کی آنکھیں ٹھنڈی رہیں اور وه غمگین نہ ہو۔ اور تو نے ایک شخص کو مار ڈاﻻ تھا اس پر بھی ہم نے تجھے غم سے بچا لیا، غرض ہم نے تجھے اچھی طرح آزما لیا۔ پھر تو کئی سال تک مدین کے لوگوں میں ٹھہرا رہا، پھر تقدیر الٰہی کے مطابق اے موسیٰ! تو آیا'),
(2389, 20, 41, 'اور میں نے تجھے خاص اپنی ذات کے لئے پسند فرما لیا'),
(2390, 20, 42, 'اب تو اپنے بھائی سمیت میری نشانیاں ہمراه لئے ہوئے جا، اور خبردار میرے ذکر میں سستی نہ کرنا'),
(2391, 20, 43, 'تم دونوں فرعون کے پاس جاؤ اس نے بڑی سرکشی کی ہے'),
(2392, 20, 44, 'اسے نرمی سے سمجھاؤ کہ شاید وه سمجھ لے یا ڈر جائے'),
(2393, 20, 45, 'دونوں نے کہا اے ہمارے رب! ہمیں خوف ہے کہ کہیں فرعون ہم پر کوئی زیادتی نہ کرے یا اپنی سرکشی میں بڑھ نہ جائے'),
(2394, 20, 46, 'جواب ملا کہ تم مطلقاً خوف نہ کرو میں تمہارے ساتھ ہوں اور سنتا دیکھتا رہوں گا'),
(2395, 20, 47, 'تم اس کے پاس جا کر کہو کہ ہم تیرے پروردگار کے پیغمبر ہیں تو ہمارے ساتھ بنی اسرائیل کو بھیج دے، ان کی سزائیں موقوف کر۔ ہم تو تیرے پاس تیرے رب کی طرف سے نشانی لے کر آئے ہیں اور سلامتی اسی کے لئے ہے جو ہدایت کا پابند ہو جائے'),
(2396, 20, 48, 'ہماری طرف وحی کی گئی ہے کہ جو جھٹلائے اور روگردانی کرے اس کے لئے عذاب ہے'),
(2397, 20, 49, 'فرعون نے پوچھا کہ اے موسیٰ تم دونوں کا رب کون ہے؟'),
(2398, 20, 50, 'جواب دیا کہ ہمارا رب وه ہے جس نے ہر ایک کو اس کی خاص صورت، شکل عنایت فرمائی پھر راه سجھا دی'),
(2399, 20, 51, 'اس نے کہا اچھا یہ تو بتاؤ اگلے زمانے والوں کا حال کیا ہونا ہے'),
(2400, 20, 52, 'جواب دیا کہ ان کا علم میرے رب کے ہاں کتاب میں موجود ہے، نہ تو میرا رب غلطی کرتا ہے نہ بھولتا ہے'),
(2401, 20, 53, 'اسی نے تمہارے لئے زمین کو فرش بنایا ہے اور اس میں تمہارے چلنے کے لئے راستے بنائے ہیں اور آسمان سے پانی بھی وہی برساتا ہے، پھر اس برسات کی وجہ سے مختلف قسم کی پیداوار بھی ہم ہی پیدا کرتے ہیں'),
(2402, 20, 54, 'تم خود کھاؤ اور اپنے چوپایوں کو بھی چراؤ۔ کچھ شک نہیں کہ اس میں عقلمندوں کے لئے بہت سی نشانیاں ہیں'),
(2403, 20, 55, 'اسی زمین میں سے ہم نے تمہیں پیدا کیا اور اسی میں پھر واپس لوٹائیں گے اور اسی سے پھر دوباره تم سب کو نکال کھڑا کریں گے'),
(2404, 20, 56, 'ہم نے اسےاپنی سب نشانیاں دکھا دیں لیکن پھر بھی اس نے جھٹلایا اور انکار کر دیا'),
(2405, 20, 57, 'کہنے لگا اے موسیٰ! کیا تو اسی لئے آیا ہے کہ ہمیں اپنے جادو کے زور سے ہمارے ملک سے باہر نکال دے'),
(2406, 20, 58, 'اچھا ہم بھی تیرے مقابلے میں اسی جیسا جادو ضرور ﻻئیں گے پس تو ہمارے اور اپنے درمیان ایک وعدے کا وقت مقرر کر لے، کہ نہ ہم اس کا خلاف کریں اور نہ تو، صاف میدان میں مقابلہ ہو'),
(2407, 20, 59, 'موسیٰ (علیہ السلام) نے جواب دیا کہ زینت اور جشن کے دن کا وعده ہے اور یہ کہ لوگ دن چڑھے ہی جمع ہو جائیں'),
(2408, 20, 60, 'پس فرعون لوٹ گیا اور اس نے اپنے ہتھکنڈے جمع کیے پھر آگیا'),
(2409, 20, 61, 'موسی (علیہ السلام) نے ان سے کہا تمہاری شامت آچکی، اللہ تعالیٰ پر جھوٹ اور افترا نہ باندھو کہ وه تمہیں عذابوں سے ملیا میٹ کر دے، یاد رکھو وه کبھی کامیاب نہ ہوگا جس نے جھوٹی بات گھڑی'),
(2410, 20, 62, 'پس یہ لوگ آپس کے مشوروں میں مختلف رائے ہوگئے اور چھﭗ کر چپکے چپکے مشوره کرنے لگے'),
(2411, 20, 63, 'کہنے لگے یہ دونوں محض جادوگر ہیں اور ان کا پختہ اراده ہے کہ اپنے جادو کے زور سے تمہیں تمہارے ملک سے نکال باہر کر دیں اور تمہارے بہترین مذہب کو برباد کر دیں'),
(2412, 20, 64, 'تو تم بھی اپنا کوئی داؤ اٹھا نہ رکھو، پھر صف بندی کرکے آؤ۔ جو غالب آگیا وہی بازی لے گیا'),
(2413, 20, 65, 'کہنے لگے کہ اے موسیٰ! یا تو تو پہلے ڈال یا ہم پہلے ڈالنے والے بن جائیں'),
(2414, 20, 66, 'جواب دیا کہ نہیں تم ہی پہلے ڈالو۔ اب تو موسیٰ (علیہ السلام) کو یہ خیال گزرنے لگا کہ ان کی رسیاں اور لکڑیاں ان کے جادو کے زور سے دوڑ بھاگ رہی ہیں'),
(2415, 20, 67, 'پس موسیٰ (علیہ السلام) نے اپنے دل ہی دل میں ڈر محسوس کیا'),
(2416, 20, 68, 'ہم نے فرمایا کچھ خوف نہ کر یقیناً تو ہی غالب اور برتر رہے گا'),
(2417, 20, 69, 'اور تیرے دائیں ہاتھ میں جو ہے اسے ڈالدے کہ ان کی تمام کاریگری کووه نگل جائے، انہوں نے جو کچھ بنایا ہے یہ صرف جادوگروں کے کرتب ہیں اور جادوگر کہیں سے بھی آئے کامیاب نہیں ہوتا'),
(2418, 20, 70, 'اب تو تمام جادوگر سجدے میں گر پڑے اور پکار اٹھے کہ ہم تو ہارون اور موسیٰ (علیہما السلام) کے رب پر ایمان ﻻئے'),
(2419, 20, 71, 'فرعون کہنے لگا کہ کیا میری اجازت سے پہلے ہی تم اس پرایمان لے آئے؟ یقیناً یہی تمہارا وه بڑا بزرگ ہے جس نے تم سب کو جادو سکھایا ہے، (سن لو) میں تمہارے پاؤں الٹے سیدھے کٹوا کر تم سب کو کھجور کے تنوں میں سولی پر لٹکوا دوں گا، اور تمہیں پوری طرح معلوم ہوجائے گا کہ ہم میں سے کس کی مار زیاده سخت اور دیرپا ہے'),
(2420, 20, 72, 'انہوں نے جواب دیا کہ ناممکن ہے کہ ہم تجھے ترجیح دیں ان دلیلوں پر جو ہمارے سامنے آچکیں اور اس اللہ پر جس نے ہمیں پیدا کیا ہے اب تو تو جو کچھ کرنے وﻻ ہے کر گزر، تو جو کچھ بھی حکم چلا سکتا ہے وه اسی دنیوی زندگی میں ہی ہے'),
(2421, 20, 73, 'ہم (اس امید سے) اپنے پروردگار پرایمان ﻻئے کہ وه ہماری خطائیں معاف فرما دے اور (خاص کر) جادوگری (کا گناه،) جس پر تم نے ہمیں مجبور کیا ہے، اللہ ہی بہتر اور ہمیشہ باقی رہنے واﻻ ہے'),
(2422, 20, 74, 'بات یہی ہے کہ جو بھی گنہگار بن کر اللہ تعالیٰ کے ہاں حاضر ہوگا اس کے لئے دوزخ ہے، جہاں نہ موت ہوگی اور نہ زندگی'),
(2423, 20, 75, 'اور جو بھی اس کے پاس ایمان کی حالت میں حاضر ہوگا اور اس نے اعمال بھی نیک کیے ہوں گے اس کے لئے بلند وباﻻ درجے ہیں'),
(2424, 20, 76, 'ہمیشگی والی جنتیں جن کے نیچے نہریں لہریں لے رہی ہیں جہاں وه ہمیشہ (ہمیشہ) رہیں گے۔ یہی انعام ہے ہر اس شخص کا جو پاک ہوا'),
(2425, 20, 77, 'ہم نے موسیٰ (علیہ السلام) کی طرف وحی نازل فرمائی کہ تو راتوں رات میرے بندوں کو لے چل، اور ان کے لئے دریا میں خشک راستہ بنا لے، پھر نہ تجھے کسی کے آپکڑنے کا خطره ہوگا نہ ڈر'),
(2426, 20, 78, 'فرعون نے اپنے لشکروں سمیت ان کا تعاقب کیا پھر تو دریا ان سب پر چھا گیا جیسا کچھ چھا جانے واﻻ تھا'),
(2427, 20, 79, 'فرعون نے اپنی قوم کو گمراہی میں ڈال دیا اور سیدھا راستہ نہ دکھایا'),
(2428, 20, 80, 'اے بنی اسرائیل! دیکھو ہم نے تمہیں تمہارے دشمن سے نجات دی اور تم سے کوه طور کی دائیں طرف کا وعده کیا اور تم پر من وسلویٰ اتارا'),
(2429, 20, 81, 'تم ہماری دی ہوئی پاکیزه روزی کھاؤ، اور اس میں حد سے آگے نہ بڑھو، ورنہ تم پر میرا غضب نازل ہوگا، اور جس پر میرا غضب نازل ہو جائے وه یقیناً تباه ہوا'),
(2430, 20, 82, 'ہاں بیشک میں انہیں بخش دینے واﻻ ہوں جو توبہ کریں ایمان ﻻئیں نیک عمل کریں اور راه راست پر بھی رہیں'),
(2431, 20, 83, 'اے موسیٰ! تجھے اپنی قوم سے (غافل کرکے) کون سی چیز جلدی لے آئی؟'),
(2432, 20, 84, 'کہا کہ وه لوگ بھی میرے پیچھے ہی پیچھے ہیں، اور میں نے اے رب! تیری طرف جلدی اس لئے کی کہ تو خوش ہو جائے'),
(2433, 20, 85, 'فرمایا! ہم نے تیری قوم کو تیرے پیچھے آزمائش میں ڈال دیا اور انہیں سامری نے بہکا دیا ہے'),
(2434, 20, 86, 'پس موسیٰ (علیہ السلام) سخت غضبناک ہو کر رنج کے ساتھ واپس لوٹے، اور کہنے لگے کہ اے میری قوم والو! کیا تم سے تمہارے پروردگار نے نیک وعده نہیں کیا تھا؟ کیا اس کی مدت تمہیں لمبی معلوم ہوئی؟ بلکہ تمہارا اراده ہی یہ ہے کہ تم پر تمہارے پروردگار کا غضب نازل ہو؟ کہ تم نے میرے وعدے کا خلاف کیا'),
(2435, 20, 87, 'انہوں نے جواب دیا کہ ہم نے اپنےاختیار سے آپ کے ساتھ وعدے کا خلاف نہیں کیا۔ بلکہ ہم پر زیورات قوم کے جو بوجھ ﻻد دیے گئے تھے انہیں ہم نے ڈال دیا، اور اسی طرح سامری نے بھی ڈال دیئے'),
(2436, 20, 88, 'پھر اس نے لوگوں کے لئے ایک بچھڑا نکال کھڑا کیا یعنی بچھڑے کا بت، جس کی گائے کی سی آواز بھی تھی پھر کہنے لگے کہ یہی تمہارا معبود ہے اور موسیٰ کا بھی، لیکن موسیٰ بھول گیا ہے'),
(2437, 20, 89, 'کیا یہ گمراه لوگ یہ بھی نہیں دیکھتے کہ وه تو ان کی بات کا جواب بھی نہیں دے سکتا اور نہ ان کے کسی برے بھلے کا اختیار رکھتا ہے'),
(2438, 20, 90, 'اور ہارون (علیہ السلام) نےاس سے پہلے ہی ان سے کہہ دیا تھا اے میری قوم والو! اس بچھڑے سے تو صرف تمہاری آزمائش کی گئی ہے، تمہارا حقیقی پروردگار تو اللہ رحمنٰ ہی ہے، پس تم سب میری تابعداری کرو۔ اور میری بات مانتے چلے جاؤ'),
(2439, 20, 91, 'انہوں نے جواب دیا کہ موسیٰ (علیہ السلام) کی واپسی تک تو ہم اسی کے مجاور بنے بیٹھے رہیں گے'),
(2440, 20, 92, 'موسیٰ (علیہ السلام) کہنے لگے اے ہارون! انہیں گمراه ہوتا ہوا دیکھتے ہوئے تجھے کس چیز نے روکا تھا'),
(2441, 20, 93, 'کہ تو میرے پیچھے نہ آیا۔ کیا تو بھی میرے فرمان کا نافرمان بن بیٹھا'),
(2442, 20, 94, 'ہارون (علیہ السلام) نے کہا اے میرے ماں جائے بھائی! میری داڑھی نہ پکڑ اور سر کے بال نہ کھینچ، مجھے تو صرف یہ خیال دامن گیر ہوا کہ کہیں آپ یہ (نہ) فرمائیں کہ تو نے بنی اسرائیل میں تفرقہ ڈال دیا اور میری بات کا انتظار نہ کیا'),
(2443, 20, 95, 'موسیٰ (علیہ السلام) نے پوچھا سامری تیرا کیا معاملہ ہے'),
(2444, 20, 96, 'اس نے جواب دیا کہ مجھے وه چیز دکھائی دی جو انہیں دکھائی نہیں دی، تو میں نے فرستادہٴ الٰہی کے نقش قدم سے ایک مٹھی بھر لی اسے اس میں ڈال دیا اسی طرح میرے دل نے یہ بات میرے لئے بھلی بنا دی'),
(2445, 20, 97, 'کہا اچھا جا دنیا کی زندگی میں تیری سزا یہی ہے کہ تو کہتا رہے کہ مجھے نہ چھونا، اور ایک اور بھی وعده تیرے ساتھ ہے جو تجھ سے ہر گز نہ ٹلے گا، اور اب تو اپنے اس معبود کو بھی دیکھ لینا جس کا اعتکاف کیے ہوئے تھا کہ ہم اسے جلا کر دریا میں ریزه ریزه اڑا دیں گے'),
(2446, 20, 98, 'اصل بات یہی ہے کہ تم سب کا معبود برحق صرف اللہ ہی ہے اس کے سوا کوئی پرستش کے قابل نہیں۔ اس کا علم تمام چیزوں پر حاوی ہے'),
(2447, 20, 99, 'اسی طرح ہم تیرے سامنے پہلے کی گزری ہوئی وارداتیں بیان فرما رہے ہیں اور یقیناً ہم تجھے اپنے پاس سے نصیحت عطا فرما چکے ہیں'),
(2448, 20, 100, 'اس سے جو منھ پھیر لے گا وه یقیناً قیامت کے دن اپنا بھاری بوجھ ﻻدے ہوئے ہوگا'),
(2449, 20, 101, 'جس میں ہمیشہ ہی رہے گا، اور ان کے لئے قیامت کے دن (بڑا) برا بوجھ ہے'),
(2450, 20, 102, 'جس دن صور پھونکا جائے گا اور گناه گاروں کو ہم اس دن (دہشت کی وجہ سے) نیلی پیلی آنکھوں کے ساتھ گھیر ﻻئیں گے'),
(2451, 20, 103, 'وه آپس میں چپکے چپکے کہہ رہے ہوں گے کہ ہم تو (دنیا میں) صرف دس دن ہی رہے'),
(2452, 20, 104, 'جوکچھ وه کہہ رہے ہیں اس کی حقیقت سے ہم باخبر ہیں ان میں سب سے زیاده اچھی راه واﻻ کہہ رہا ہو گا کہ تم تو صرف ایک ہی دن رہے'),
(2453, 20, 105, 'وه آپ سے پہاڑوں کی نسبت سوال کرتے ہیں، تو آپ کہہ دیں کہ انہیں میرا رب ریزه ریزه کر کے اڑا دے گا'),
(2454, 20, 106, 'اور زمین کو بالکل ہموار صاف میدان کر کے چھوڑے گا'),
(2455, 20, 107, 'جس میں تو نہ کہیں موڑ دیکھے گا نہ اونچ نیچ'),
(2456, 20, 108, 'جس دن لوگ پکارنے والے کے پیچھے چلیں گے جس میں کوئی کجی نہ ہوگی اور اللہ رحمنٰ کے سامنے تمام آوازیں پست ہو جائیں گی سوائے کھسر پھسر کے تجھے کچھ بھی سنائی نہ دے گا'),
(2457, 20, 109, 'اس دن سفارش کچھ کام نہیں آئے گی مگر جسے رحمنٰ حکم دے اور اس کی بات کو پسند فرمائے'),
(2458, 20, 110, 'جو کچھ ان کے آگے پیچھے ہے اسے اللہ ہی جانتا ہے مخلوق کا علم اس پر حاوی نہیں ہو سکتا'),
(2459, 20, 111, 'تمام چہرے اس زنده اور قائم دائم مدبر، اللہ کے سامنے کمال عاجزی سے جھکے ہوئے ہونگے، یقیناً وه برباد ہوا جس نے ﻇلم ﻻد لیا'),
(2460, 20, 112, 'اور جو نیک اعمال کرے اور ایمان واﻻ بھی ہو تو نہ اسے بے انصافی کا کھٹکا ہوگا نہ حق تلفی کا'),
(2461, 20, 113, 'اسی طرح ہم نے تجھ پر عربی قرآن نازل فرمایا ہے اور طرح طرح سے اس میں ڈر کا بیان سنایا ہے تاکہ لوگ پرہیز گار بن جائیں یا ان کے دل میں سوچ سمجھ تو پیدا کرے'),
(2462, 20, 114, 'پس اللہ عالی شان واﻻ سچا اور حقیقی بادشاه ہے۔ تو قرآن پڑھنے میں جلدی نہ کر اس سے پہلے کہ تیری طرف جو وحی کی جاتی ہے وه پوری کی جائے، ہاں یہ دعا کر کہ پروردگار! میرا علم بڑھا'),
(2463, 20, 115, 'ہم نے آدم کو پہلے ہی تاکیدی حکم دیا تھا لیکن وه بھول گیا اور ہم نے اس میں کوئی عزم نہیں پایا'),
(2464, 20, 116, 'اور جب ہم نے فرشتوں سے کہا کہ آدم (علیہ السلام) کو سجده کرو تو ابلیس کے سوا سب نے کیا، اس نے صاف انکار کر دیا'),
(2465, 20, 117, 'تو ہم نے کہا اے آدم! یہ تیرا اور تیری بیوی کا دشمن ہے (خیال رکھنا) ایسا نہ ہو کہ وه تم دونوں کو جنت سے نکلوا دے کہ تو مصیبت میں پڑ جائے'),
(2466, 20, 118, 'یہاں تو تجھے یہ آرام ہے کہ نہ تو بھوکا ہوتا ہے نہ ننگا'),
(2467, 20, 119, 'اور نہ تو یہاں پیاسا ہوتا ہے نہ دھوپ سے تکلیف اٹھاتا ہے'),
(2468, 20, 120, 'لیکن شیطان نے اسے وسوسہ ڈاﻻ، کہنے لگا کہ میں تجھے دائمی زندگی کا درخت اور بادشاہت بتلاؤں کہ جو کبھی پرانی نہ ہو'),
(2469, 20, 121, 'چنانچہ ان دونوں نے اس درخت سے کچھ کھا لیا پس ان کے ستر کھل گئے اور بہشت کے پتے اپنے اوپر ٹانکنے لگے۔ آدم (علیہ السلام) نے اپنے رب کی نافرمانی کی، پس بہک گیا'),
(2470, 20, 122, 'پھر اس کے رب نے نوازا، اس کی توبہ قبول کی اور اس کی رہنمائی کی'),
(2471, 20, 123, 'فرمایا، تم دونوں یہاں سےاتر جاؤ تم آپس میں ایک دوسرے کے دشمن ہو، اب تمہارے پاس کبھی میری طرف سے ہدایت پہنچے تو جو میری ہدایت کی پیروی کرے نہ تو وه بہکے گا نہ تکلیف میں پڑے گا'),
(2472, 20, 124, 'اور (ہاں) جو میری یاد سے روگردانی کرے گا اس کی زندگی تنگی میں رہے گی، اور ہم اسے بروز قیامت اندھا کر کے اٹھائیں گے'),
(2473, 20, 125, 'وه کہے گا کہ الٰہی! مجھے تو نے اندھا بنا کر کیوں اٹھایا؟ حاﻻنکہ میں تو دیکھتا بھالتا تھا'),
(2474, 20, 126, '(جواب ملے گا کہ) اسی طرح ہونا چاہئے تھا تو میری آئی ہوئی آیتوں کو بھول گیا تو آج تو بھی بھلا دیا جاتا ہے'),
(2475, 20, 127, 'ہم ایسا ہی بدلہ ہر اس شخص کو دیا کرتے ہیں جو حد سے گزر جائے اور اپنے رب کی آیتوں پر ایمان نہ ﻻئے، اور بیشک آخرت کا عذاب نہایت ہی سخت اور باقی رہنے واﻻ ہے'),
(2476, 20, 128, 'کیا ان کی رہبری اس بات نے بھی نہیں کی کہ ہم نے ان سے پہلے بہت سی بستیاں ہلاک کر دی ہیں جن کے رہنے سہنے کی جگہ یہ چل پھر رہے ہیں۔ یقیناً اس میں عقلمندوں کے لئے بہت سی نشانیاں ہیں'),
(2477, 20, 129, 'اگر تیرے رب کی بات پہلے ہی سے مقرر شده اور وقت معین کرده نہ ہوتا تو اسی وقت عذاب آچمٹتا'),
(2478, 20, 130, 'پس ان کی باتوں پر صبر کر اور اپنے پروردگار کی تسبیح اور تعریف بیان کرتا ره، سورج نکلنے سے پہلے اور اس کے ڈوبنے سے پہلے، رات کے مختلف وقتوں میں بھی اور دن کے حصوں میں بھی تسبیح کرتا ره، بہت ممکن ہے کہ تو راضی ہو جائے'),
(2479, 20, 131, 'اور اپنی نگاہیں ہرگز ان چیزوں کی طرف نہ دوڑانا جو ہم نے ان میں سے مختلف لوگوں کو آرائش دنیا کی دے رکھی ہیں تاکہ انہیں اس میں آزما لیں تیرے رب کا دیا ہوا ہی (بہت) بہتر اور باقی رہنے واﻻ ہے'),
(2480, 20, 132, 'اپنے گھرانے کے لوگوں پرنماز کی تاکید رکھ اور خود بھی اس پر جما ره، ہم تجھ سے روزی نہیں مانگتے، بلکہ ہم خود تجھے روزی دیتے ہیں، آخر میں بول باﻻ پرہیزگاری ہی کا ہے'),
(2481, 20, 133, 'انہوں نے کہا کہ یہ نبی ہمارے پاس اپنے پروردگار کی طرف سے کوئی نشانی کیوں نہیں ﻻیا؟ کیا ان کے پاس اگلی کتابوں کی واضح دلیل نہیں پہنچی؟'),
(2482, 20, 134, 'اور اگر ہم اس سے پہلے ہی انہیں عذاب سے ہلاک کر دیتے تو یقیناً یہ کہہ اٹھتے کہ اے ہمارے پروردگار تو نے ہمارے پاس اپنا رسول کیوں نہ بھیجا؟ کہ ہم تیری آیتوں کی تابعداری کرتے اس سے پہلے کہ ہم ذلیل ورسوا ہوتے'),
(2483, 20, 135, 'کہہ دیجئے! ہر ایک انجام کا منتظر ہے پس تم بھی انتظار میں رہو۔ ابھی ابھی قطعاً جان لو گے کہ راه راست والے کون ہیں اور کون راه یافتہ ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2484, 21, 1, 'لوگوں کے حساب کا وقت قریب آگیا پھر بھی وه بے خبری میں منھ پھیرے ہوئے ہیں'),
(2485, 21, 2, 'ان کے پاس ان کے رب کی طرف سے جو بھی نئی نئی نصیحت آتی ہے اسے وه کھیل کود میں ہی سنتے ہیں'),
(2486, 21, 3, 'ان کے دل بالکل غافل ہیں اور ان ﻇالموں نے چپکے چپکے سرگوشیاں کیں کہ وه تم ہی جیسا انسان ہے، پھر کیا وجہ ہے جو تم آنکھوں دیکھتے جادو میں آجاتے ہو'),
(2487, 21, 4, 'پیغمبر نے کہا میرا پروردگار ہر اس بات کو جو زمین وآسمان میں ہے بخوبی جانتا ہے، وه بہت ہی سننے واﻻ اور جاننے واﻻ ہے'),
(2488, 21, 5, 'اتنا ہی نہیں بلکہ یہ تو کہتے ہیں کہ یہ قرآن پراگنده خوابوں کا مجموعہ ہے بلکہ اس نے از خود اسے گھڑ لیا ہے بلکہ یہ شاعر ہے، ورنہ ہمارے سامنے یہ کوئی ایسی نشانی ﻻتے جیسے کہ اگلے پیغمبر بھیجے گئے تھے'),
(2489, 21, 6, 'ان سے پہلے جتنی بستیاں ہم نے اجاڑیں سب ایمان سے خالی تھیں۔ تو کیا اب یہ ایمان ﻻئیں گے'),
(2490, 21, 7, 'تجھ سے پہلے جتنے پیغمبر ہم نے بھیجے سبھی مرد تھے جن کی طرف ہم وحی اتارتے تھے پس تم اہل کتاب سے پوچھ لو اگر خود تمہیں علم نہ ہو'),
(2491, 21, 8, 'ہم نے ان کے ایسے جسم نہیں بنائے تھے کہ وه کھانا نہ کھائیں اور نہ وه ہمیشہ رہنے والے تھے'),
(2492, 21, 9, 'پھر ہم نے ان سے کیے ہوئے سب وعدے سچے کیے انہیں اور جن جن کو ہم نے چاہا نجات عطا فرمائی اور حد سے نکل جانے والوں کو غارت کر دیا'),
(2493, 21, 10, 'یقیناً ہم نے تمہاری جانب کتاب نازل فرمائی ہے جس میں تمہارے لئے ذکر ہے، کیا پھر بھی تم عقل نہیں رکھتے؟'),
(2494, 21, 11, 'اور بہت سی بستیاں ہم نے تباه کر دیں جو ﻇالم تھیں اور ان کے بعد ہم نے دوسری قوم کو پیدا کر دیا'),
(2495, 21, 12, 'جب انہوں نے ہمارے عذاب کا احساس کر لیا، تو لگے اس سے بھاگنے'),
(2496, 21, 13, 'بھاگ دوڑ نہ کرو اور جہاں تمہیں آسودگی دی گئی تھی وہیں واپس لوٹو اور اپنے مکانات کی طرف جاؤ تاکہ تم سے سوال تو کر لیا جائے'),
(2497, 21, 14, 'کہنے لگے ہائے ہماری خرابی! بیشک ہم ﻇالم تھے'),
(2498, 21, 15, 'پھر تو ان کا یہی قول تھا یہاں تک کہ ہم نے انہیں جڑ سے کٹی ہوئی کھیتی اور بجھی پڑی آگ (کی طرح) کر دیا'),
(2499, 21, 16, 'ہم نے آسمان وزمین اور ان کے درمیان کی چیزوں کو کھیلتے ہوئے نہیں بنایا'),
(2500, 21, 17, 'اگر ہم یوں ہی کھیل تماشے کا اراده کرتے تو اسے اپنے پاس سے ہی بنا لیتے، اگر ہم کرنے والے ہی ہوتے'),
(2501, 21, 18, 'بلکہ ہم سچ کو جھوٹ پر پھینک مارتے ہیں پس سچ جھوٹ کا سر توڑ دیتا ہے اور وه اسی وقت نابود ہو جاتا ہے، تم جو باتیں بناتے ہو وه تمہاری لئے باعﺚ خرابی ہیں'),
(2502, 21, 19, 'آسمانوں اور زمین میں جو ہے اسی اللہ کا ہے اور جو اس کے پاس ہیں وه اس کی عبادت سے نہ سرکشی کرتے ہیں اور نہ تھکتے ہیں'),
(2503, 21, 20, 'وه دن رات تسبیح بیان کرتے ہیں اور ذرا سی بھی سستی نہیں کرتے'),
(2504, 21, 21, 'کیا ان لوگوں نے زمین (کی مخلوقات میں) سے جنہیں معبود بنا رکھا ہے وه زنده کردیتے ہیں'),
(2505, 21, 22, 'اگر آسمان وزمین میں سوائے اللہ تعالیٰ کے اور بھی معبود ہوتے تو یہ دونوں درہم برہم ہوجاتے پس اللہ تعالیٰ عرش کا رب ہر اس وصف سے پاک ہے جو یہ مشرک بیان کرتے ہیں'),
(2506, 21, 23, 'وه اپنے کاموں کے لئے (کسی کےآگے) جواب ده نہیں اور سب (اس کےآگے) جواب ده ہیں'),
(2507, 21, 24, 'کیا ان لوگوں نے اللہ کے سوا اور معبود بنا رکھے ہیں، ان سے کہہ دو ﻻؤ اپنی دلیل پیش کرو۔ یہ ہے میرے ساتھ والوں کی کتاب اور مجھ سے اگلوں کی دلیل۔ بات یہ ہے کہ ان میں کے اکثر لوگ حق کو نہیں جانتے اسی وجہ سے منھ موڑے ہوئے ہیں'),
(2508, 21, 25, 'تجھ سے پہلے بھی جو رسول ہم نے بھیجا اس کی طرف یہی وحی نازل فرمائی کہ میرے سوا کوئی معبود برحق نہیں پس تم سب میری ہی عبادت کرو'),
(2509, 21, 26, '(مشرک لوگ) کہتے ہیں کہ رحمٰن اوﻻد واﻻ ہے (غلط ہے) اس کی ذات پاک ہے، بلکہ وه سب اس کے باعزت بندے ہیں'),
(2510, 21, 27, 'کسی بات میں اللہ پر پیش دستی نہیں کرتے بلکہ اس کے فرمان پر کاربند ہیں'),
(2511, 21, 28, 'وه ان کے آگے پیچھے تمام امور سے واقف ہے وه کسی کی بھی سفارش نہیں کرتے بجز ان کے جن سے اللہ خوش ہو وه تو خود ہیبت الٰہی سے لرزاں وترساں ہیں'),
(2512, 21, 29, 'ان میں سے اگر کوئی بھی کہہ دے کہ اللہ کے سوا میں ﻻئق عبادت ہوں تو ہم اسے دوزخ کی سزا دیں ہم ﻇالموں کو اسی طرح سزا دیتے ہیں'),
(2513, 21, 30, 'کیا کافر لوگوں نے یہ نہیں دیکھا کہ آسمان وزمین باہم ملے جلے تھے پھر ہم نے انہیں جدا کیا اور ہر زنده چیز کو ہم نے پانی سے پیدا کیا، کیا یہ لوگ پھر بھی ایمان نہیں ﻻتے'),
(2514, 21, 31, 'اور ہم نے زمین میں پہاڑ بنا دیئے تاکہ وه مخلوق کو ہلا نہ سکے، اور ہم نے اس میں کشاده راہیں بنا دیں تاکہ وه راستہ حاصل کریں'),
(2515, 21, 32, 'آسمان کو محفوظ چھت بھی ہم نے ہی بنایا ہے۔ لیکن لوگ اس کی قدرت کے نمونوں پر دھیان نہیں دھرتے'),
(2516, 21, 33, 'وہی اللہ ہے جس نے رات اور دن اور سورج اور چاند کو پیدا کیا ہے۔ ان میں سے ہر ایک اپنے اپنے مدار میں تیرتے پھرتے ہیں'),
(2517, 21, 34, 'آپ سے پہلے کسی انسان کو بھی ہم نے ہمیشگی نہیں دی، کیا اگر آپ مرگئے تو وه ہمیشہ کے لئے ره جائیں گے'),
(2518, 21, 35, 'ہر جان دار موت کا مزه چکھنے واﻻ ہے۔ ہم بطریق امتحان تم میں سے ہر ایک کو برائی بھلائی میں مبتلا کرتے ہیں اور تم سب ہماری ہی طرف لوٹائے جاؤ گے'),
(2519, 21, 36, 'یہ منکرین تجھے جب بھی دیکھتے ہیں تو تمہارا مذاق ہی اڑاتے ہیں کہ کیا یہی وه ہے جو تمہارے معبودوں کا ذکر برائی سے کرتا ہے، اور وه خود ہی رحمٰن کی یاد کے بالکل ہی منکر ہیں'),
(2520, 21, 37, 'انسان جلد باز مخلوق ہے۔ میں تمہیں اپنی نشانیاں ابھی ابھی دکھاؤں گا تم مجھ سے جلد بازی نہ کرو'),
(2521, 21, 38, 'کہتے ہیں کہ اگر سچے ہو تو بتا دو کہ یہ وعده کب ہے'),
(2522, 21, 39, 'کاش! یہ کافر جانتے کہ اس وقت نہ تو یہ کافر آگ کو اپنے چہروں سے ہٹا سکیں گے اور نہ اپنی پیٹھوں سے اور نہ ان کی مدد کی جائے گی'),
(2523, 21, 40, '(ہاں ہاں!) وعدے کی گھڑی ان کے پاس اچانک آجائے گی اور انہیں ہکا بکا کر دے گی، پھر نہ تو یہ لوگ اسے ٹال سکیں گے اور نہ ذرا سی بھی مہلت دیئے جائیں گے'),
(2524, 21, 41, 'اور تجھ سے پہلے رسولوں کے ساتھ بھی ہنسی مذاق کیا گیا پس ہنسی کرنے والوں کو ہی اس چیز نے گھیر لیا جس کی وه ہنسی اڑاتے تھے'),
(2525, 21, 42, 'ان سے پوچھئے کہ رحمٰن سے، دن اور رات تمہاری حفاﻇت کون کر سکتا ہے؟ بات یہ ہے کہ یہ لوگ اپنے رب کے ذکر سے پھرے ہوئے ہیں'),
(2526, 21, 43, 'کیا ہمارے سوا ان کے اور معبود ہیں جو انہیں مصیبتوں سے بچا لیں۔ کوئی بھی خود اپنی مدد کی طاقت نہیں رکھتا اور نہ کوئی ہماری طرف سے رفاقت دیا جاتا ہے'),
(2527, 21, 44, 'بلکہ ہم نے انہیں اور ان کے باپ دادوں کو زندگی کے سروسامان دیے یہاں تک کہ ان کی مدت عمر گزر گئی۔ کیا وه نہیں دیکھتے کہ ہم زمین کو اس کے کناروں سے گھٹاتے چلے آرہے ہیں، اب کیا وہی غالب ہیں؟'),
(2528, 21, 45, 'کہہ دیجئے! میں تو تمہیں اللہ کی وحی کے ذریعہ آگاه کر رہا ہوں مگر بہرے لوگ بات نہیں سنتے جبکہ انہیں آگاه کیا جائے'),
(2529, 21, 46, 'اگر انہیں تیرے رب کے کسی عذاب کا جھونکا بھی لگ جائے تو پکار اٹھیں کہ ہائے ہماری بدبختی! یقیناً ہم گنہگار تھے'),
(2530, 21, 47, 'قیامت کے دن ہم درمیان میں ﻻ رکھیں گے ٹھیک ٹھیک تولنے والی ترازو کو۔ پھر کسی پر کچھ بھی ﻇلم نہ کیا جائے گا۔ اور اگر ایک رائی کے دانے کے برابر بھی عمل ہوگا ہم اسے ﻻ حاضر کریں گے، اور ہم کافی ہیں حساب کرنے والے'),
(2531, 21, 48, 'یہ بالکل سچ ہے کہ ہم نے موسیٰ وہارون کو فیصلے کرنے والی نورانی اور پرہیزگاروں کے لئے وعﻆ ونصیحت والی کتاب عطا فرمائی'),
(2532, 21, 49, 'وه لوگ جو اپنے رب سے بن دیکھے خوف کھاتے ہیں اور قیامت (کے تصور) سے کانپتے رہتے ہیں'),
(2533, 21, 50, 'اور یہ نصیحت وبرکت واﻻ قرآن بھی ہم ہی نے نازل فرمایا ہے کیا پھر بھی تم اس کے منکر ہو'),
(2534, 21, 51, 'یقیناً ہم نے اس سے پہلے ابراہیم کو اس کی سمجھ بوجھ بخشی تھی اور ہم اس کے احوال سے بخوبی واقف تھے'),
(2535, 21, 52, 'جبکہ اس نے اپنے باپ سے اور اپنی قوم سے کہا کہ یہ مورتیاں جن کے تم مجاور بنے بیٹھے ہو کیا ہیں؟'),
(2536, 21, 53, 'سب نے جواب دیا کہ ہم نے اپنے باپ دادا کو انہی کی عبادت کرتے ہوئے پایا'),
(2537, 21, 54, 'آپ نے فرمایا! پھر تو تم اور تمہارے باپ دادا سبھی یقیناً کھلی گمراہی میں مبتلا رہے'),
(2538, 21, 55, 'کہنے لگے کیا آپ ہمارے پاس سچ مچ حق ﻻئے ہیں یا یوں ہی مذاق کر رہے ہیں'),
(2539, 21, 56, 'آپ نے فرمایا نہیں درحقیقت تم سب کا پروردگار تو وه ہے جو آسمانوں اور زمین کا مالک ہے جس نے انہیں پیدا کیا ہے، میں تو اسی بات کا گواه اور قائل ہوں'),
(2540, 21, 57, 'اور اللہ کی قسم! میں تمہارے ان معبودوں کے ساتھ جب تم علیحده پیٹھ پھیر کر چل دو گے ایک چال چلوں گا'),
(2541, 21, 58, 'پس اس نے ان سب کے ٹکڑے ٹکڑے کر دیئے ہاں صرف بڑے بت کو چھوڑ دیا یہ بھی اس لئے کہ وه سب اس کی طرف ہی لوٹیں'),
(2542, 21, 59, 'کہنے لگے کہ ہمارے خداؤں کے ساتھ یہ کس نے کیا؟ ایسا شخص تو یقیناً ﻇالموں میں سے ہے'),
(2543, 21, 60, 'بولے ہم نے ایک نوجوان کو ان کا تذکره کرتے ہوئے سنا تھا جسے ابراہیم (علیہ السلام) کہا جاتا ہے'),
(2544, 21, 61, 'سب نے کہا اچھا اسے مجمع میں لوگوں کی نگاہوں کے سامنے ﻻؤ تاکہ سب دیکھیں'),
(2545, 21, 62, 'کہنے لگے! اے ابراہیم (علیہ السلام) کیا تو نے ہی ہمارے خداؤں کے ساتھ یہ حرکت کی ہے'),
(2546, 21, 63, 'آپ نے جواب دیا بلکہ اس کام کو ان کے بڑے نے کیا ہے تم اپنے خداؤں سے ہی پوچھ لو اگر یہ بولتے چالتے ہوں'),
(2547, 21, 64, 'پس یہ لوگ اپنے دلوں میں قائل ہوگئے اور کہنے لگے واقعی ﻇالم تو تم ہی ہو'),
(2548, 21, 65, 'پھر اپنے سروں کے بل اوندھے ہوگئے (اور کہنے لگے کہ) یہ تو تجھے بھی معلوم ہے کہ یہ بولنے چالنے والے نہیں'),
(2549, 21, 66, 'اللہ کے خلیل نے اسی وقت فرمایا افسوس! کیا تم اللہ کے علاوه ان کی عبادت کرتے ہو جو نہ تمہیں کچھ بھی نفع پہنچا سکیں نہ نقصان'),
(2550, 21, 67, 'تف ہے تم پر اور ان پر جن کی تم اللہ کے سوا عبادت کرتے ہو۔ کیا تمہیں اتنی سی عقل بھی نہیں؟'),
(2551, 21, 68, 'کہنے لگے کہ اسے جلا دو اور اپنے خداؤں کی مدد کرو اگر تمہیں کچھ کرنا ہی ہے'),
(2552, 21, 69, 'ہم نے فرما دیا اے آگ! تو ٹھنڈی پڑ جا اور ابراہیم (علیہ السلام) کے لئے سلامتی (اور آرام کی چیز) بن جا!'),
(2553, 21, 70, 'گو انہوں نے ابراہیم (علیہ السلام) کا برا چاہا، لیکن ہم نے انہیں ناکام بنا دیا'),
(2554, 21, 71, 'اور ہم ابراہیم اور لوط کو بچاکر اس زمین کی طرف لے چلے جس میں ہم نے تمام جہان والوں کے لئے برکت رکھی تھی'),
(2555, 21, 72, 'اور ہم نے اسےاسحاق عطا فرمایااور یعقوب اس پر مزید۔ اور ہرایک کو ہم نے صالح بنایا'),
(2556, 21, 73, 'اور ہم نے انہیں پیشوا بنا دیا کہ ہمارے حکم سے لوگوں کی رہبری کریں اور ہم نے ان کی طرف نیک کاموں کے کرنے اور نمازوں کے قائم رکھنے اور زکوٰة دینے کی وحی (تلقین) کی، اور وه سب کے سب ہمارے عبادت گزار بندے تھے'),
(2557, 21, 74, 'ہم نے لوط (علیہ السلام) کو بھی حکم اور علم دیا اور اسے اسی بستی سے نجات دی جہاں کے لوگ گندے کاموں میں مبتلا تھے۔ اور تھے بھی بدترین گنہگار'),
(2558, 21, 75, 'اور ہم نے لوط (علیہ السلام) کو اپنی رحمت میں داخل کر لیا بے شک وه نیکو کار لوگوں میں سے تھا'),
(2559, 21, 76, 'نوح کے اس وقت کو یاد کیجئے جبکہ اس نے اس سے پہلے دعا کی ہم نے اس کی دعا قبول فرمائی اور اسے اور اس کے گھر والوں کو بڑے کرب سے نجات دی'),
(2560, 21, 77, 'اور جو لوگ ہماری آیتوں کو جھٹلا رہے تھے ان کے مقابلے میں ہم نے اس کی مدد کی، یقیناً وه برے لوگ تھے پس ہم نےان سب کو ڈبو دیا'),
(2561, 21, 78, 'اور داؤد اور سلیمان (علیہما السلام) کو یاد کیجئے جبکہ وه کھیت کے معاملہ میں فیصلہ کر رہے تھے کہ کچھ لوگوں کی بکریاں رات کو اس میں چر چگ گئی تھیں، اور ان کے فیصلے میں ہم موجود تھے'),
(2562, 21, 79, 'ہم نے اس کا صحیح فیصلہ سلیمان کو سمجھا دیا۔ ہاں ہر ایک کو ہم نے حکم وعلم دے رکھا تھا اور داؤد کے تابع ہم نے پہاڑ کر دیئے تھے جو تسبیح کرتے تھے اور پرند بھی۔ ہم کرنے والے ہی تھے'),
(2563, 21, 80, 'اور ہم نے اسے تمہارے لئے لباس بنانے کی کاریگری سکھائی تاکہ لڑائی کے ضرر سے تمہارا بچاؤ ہو۔ کیا تم شکر گزار بنو گے؟'),
(2564, 21, 81, 'ہم نے تند وتیز ہوا کو سلیمان (علیہ السلام) کے تابع کر دیا جو اس کے فرمان کے مطابق اس زمین کی طرف چلتی تھی جہاں ہم نے برکت دے رکھی تھی، اور ہم ہر چیز سے باخبر اور دانا ہیں'),
(2565, 21, 82, 'اسی طرح سے بہت سے شیاطین بھی ہم نے اس کے تابع کیے تھے جو اس کے فرمان سے غوطے لگاتے تھے اور اس کے سوا بھی بہت سے کام کرتے تھے، ان کے نگہبان ہم ہی تھے'),
(2566, 21, 83, 'ایوب (علیہ السلام) کی اس حالت کو یاد کرو جبکہ اس نے اپنے پروردگار کو پکارا کہ مجھے یہ بیماری لگ گئی ہے اور تو رحم کرنے والوں سے زیاده رحم کرنے واﻻ ہے'),
(2567, 21, 84, 'تو ہم نے اس کی سن لی اور جو دکھ انہیں تھا اسے دور کر دیا اور اس کو اہل وعیال عطا فرمائے بلکہ ان کے ساتھ ویسے ہی اور اپنی خاص مہربانی سے تاکہ سچے بندوں کے لئے سبب نصیحت ہو'),
(2568, 21, 85, 'اور اسماعیل اور ادریس اور ذوالکفل، (علیہم السلام) یہ سب صابر لوگ تھے'),
(2569, 21, 86, 'ہم نے انہیں اپنی رحمت میں داخل کر لیا۔ یہ سب لوگ نیک تھے'),
(2570, 21, 87, 'مچھلی والے (حضرت یونس علیہ السلام) کو یاد کرو! جبکہ وه غصہ سے چل دیا اور خیال کیا کہ ہم اسے نہ پکڑ سکیں گے۔ بالﺂخر وه اندھیروں کے اندر سے پکار اٹھا کہ الٰہی تیرے سوا کوئی معبود نہیں تو پاک ہے، بیشک میں ﻇالموں میں ہو گیا'),
(2571, 21, 88, 'تو ہم نے اس کی پکار سن لی اور اسے غم سے نجات دے دی اور ہم ایمان والوں کو اسی طرح بچا لیا کرتے ہیں'),
(2572, 21, 89, 'اور زکریا (علیہ السلام) کو یاد کرو جب اس نے اپنے رب سے دعا کی کہ اے میرے پروردگار! مجھے تنہا نہ چھوڑ، تو سب سے بہتر وارث ہے'),
(2573, 21, 90, 'ہم نے اس کی دعا کو قبول فرما کر اسے یحيٰ (علیہ السلام) عطا فرمایا اور ان کی بیوی کو ان کے لئے درست کر دیا۔ یہ بزرگ لوگ نیک کاموں کی طرف جلدی کرتے تھے اور ہمیں ﻻلچ طمع اور ڈر خوف سے پکارتے تھے۔ اور ہمارے سامنے عاجزی کرنے والے تھے'),
(2574, 21, 91, 'اور وه پاک دامن بی بی جس نے اپنی عصمت کی حفاﻇت کی ہم نے اس کے اندر روح سے پھونک دی اور خود انہیں اور ان کے لڑکے کو تمام جہان کے لئے نشانی بنا دیا'),
(2575, 21, 92, 'یہ تمہاری امت ہے جو حقیقت میں ایک ہی امت ہے، اور میں تم سب کا پروردگار ہوں پس تم میری ہی عبادت کرو'),
(2576, 21, 93, 'مگر لوگوں نےآپس میں اپنے دین میں فرقہ بندیاں کر لیں، سب کے سب ہماری ہی طرف لوٹنے والے ہیں'),
(2577, 21, 94, 'پھر جو بھی نیک عمل کرے اور وه مومن (بھی) ہو تو اس کی کوشش کی بے قدری نہیں کی جائے گی۔ ہم تو اس کے لکھنے والے ہیں'),
(2578, 21, 95, 'اور جس بستی کو ہم نے ہلاک کر دیا اس پر ﻻزم ہے کہ وہاں کے لوگ پلٹ کر نہیں آئیں گے'),
(2579, 21, 96, 'یہاں تک کہ یاجوج اور ماجوج کھول دیئے جائیں گے اور وه ہر بلندی سے دوڑتے ہوئے آئیں گے'),
(2580, 21, 97, 'اور سچا وعده قریب آلگے گا اس وقت کافروں کی نگاہیں پھٹی کی پھٹی ره جائیں گی، کہ ہائے افسوس! ہم اس حال سے غافل تھے بلکہ فی الواقع ہم قصور وار تھے'),
(2581, 21, 98, 'تم اور اللہ کے سوا جن جن کی تم عبادت کرتے ہو، سب دوزخ کا ایندھن بنو گے، تم سب دوزخ میں جانے والے ہو'),
(2582, 21, 99, 'اگر یہ (سچے) معبود ہوتے تو جہنم میں داخل نہ ہوتے، اور سب کے سب اسی میں ہمیشہ رہنے والے ہیں'),
(2583, 21, 100, 'وه وہاں چلا رہے ہوں گے اور وہاں کچھ بھی نہ سن سکیں گے'),
(2584, 21, 101, 'البتہ بے شک جن کے لئے ہماری طرف سے نیکی پہلے ہی ٹھہر چکی ہے۔ وه سب جہنم سے دور ہی رکھے جائیں گے'),
(2585, 21, 102, 'وه تو دوزخ کی آہٹ تک نہ سنیں گے اور اپنی من بھاتی چیزوں میں ہمیشہ رہنے والے ہوں گے'),
(2586, 21, 103, 'وه بڑی گھبراہٹ (بھی) انہیں غمگین نہ کر سکے گی اور فرشتے انہیں ہاتھوں ہاتھ لیں گے، کہ یہی تمہارا وه دن ہے جس کا تم وعده دیئے جاتے رہے'),
(2587, 21, 104, 'جس دن ہم آسمان کو یوں لپیٹ لیں گے جیسے طومار میں اوراق لپیٹ دیئے جاتے ہیں، جیسے کہ ہم نے اول دفعہ پیدائش کی تھی اسی طرح دوباره کریں گے۔ یہ ہمارے ذمے وعده ہے اور ہم اسے ضرور کر کے (ہی) رہیں گے'),
(2588, 21, 105, 'ہم زبور میں پند ونصیحت کے بعد یہ لکھ چکے ہیں کہ زمین کے وارث میرے نیک بندے (ہی) ہوں گے'),
(2589, 21, 106, 'عبادت گزار بندوں کے لئے تو اس میں ایک بڑا پیغام ہے'),
(2590, 21, 107, 'اور ہم نے آپ کو تمام جہان والوں کے لئے رحمت بنا کر ہی بھیجا ہے'),
(2591, 21, 108, 'کہہ دیجئے! میرے پاس تو پس وحی کی جاتی ہے کہ تم سب کا معبود ایک ہی ہے، تو کیا تم بھی اس کی فرمانبرداری کرنے والے ہو؟'),
(2592, 21, 109, 'پھر اگر یہ منھ موڑ لیں تو کہہ دیجئے کہ میں نے تمہیں یکساں طور پر خبردار کر دیا ہے۔ مجھے علم نہیں کہ جس کا وعده تم سے کیا جا رہا ہے وه قریب ہے یا دور'),
(2593, 21, 110, 'البتہ اللہ تعالیٰ تو کھلی اور ﻇاہر بات کو بھی جانتا ہے اور جو تم چھپاتے ہو اسے بھی جانتا ہے'),
(2594, 21, 111, 'مجھے اس کا بھی علم نہیں، ممکن ہے یہ تمہاری آزمائش ہو اور ایک مقرره وقت تک کا فائده (پہنچانا) ہے'),
(2595, 21, 112, 'خود نبی نے کہا اے رب! انصاف کے ساتھ فیصلہ فرما اور ہمارا رب بڑا مہربان ہے جس سے مدد طلب کی جاتی ہے ان باتوں پر جو تم بیان کرتے ہو');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2596, 22, 1, 'لوگو! اپنے پروردگار سے ڈرو! بلاشبہ قیامت کا زلزلہ بہت ہی بڑی چیز ہے'),
(2597, 22, 2, 'جس دن تم اسے دیکھ لو گے ہر دودھ پلانے والی اپنے دودھ پیتے بچے کو بھول جائے گی اور تمام حمل والیوں کے حمل گر جائیں گے اور تو دیکھے گا کہ لوگ مدہوش دکھائی دیں گے، حاﻻنکہ درحقیقت وه متوالے نہ ہوں گے لیکن اللہ کا عذاب بڑا ہی سخت ہے'),
(2598, 22, 3, 'بعض لوگ اللہ کے بارے میں باتیں بناتے ہیں اور وه بھی بے علمی کے ساتھ اور ہر سرکش شیطان کی پیروی کرتے ہیں'),
(2599, 22, 4, 'جس پر (قضائے الٰہی) لکھ دی گئی ہے کہ جو کوئی اس کی رفاقت کرے گا وه اسے گمراه کر دے گا اور اسے آگ کے عذاب کی طرف لے جائے گا'),
(2600, 22, 5, 'لوگو! اگر تمہیں مرنے کے بعد جی اٹھنے میں شک ہے تو سوچو ہم نے تمہیں مٹی سے پیدا کیا پھر نطفہ سے پھر خون بستہ سے پھر گوشت کے لوتھڑے سے جو صورت دیا گیا تھا اور بے نقشہ تھا۔ یہ ہم تم پر ﻇاہر کر دیتے ہیں، اور ہم جسے چاہیں ایک ٹھہرائے ہوئے وقت تک رحم مادر میں رکھتے ہیں پھر تمہیں بچپن کی حالت میں دنیا میں ﻻتے ہیں پھر تاکہ تم اپنی پوری جوانی کو پہنچو، تم میں سے بعض تو وه ہیں جو فوت کر لئے جاتے ہیں اور بعض بے غرض عمر کی طرف پھر سے لوٹا دیئے جاتے ہیں کہ وه ایک چیز سے باخبر ہونے کے بعد پھر بے خبر ہو جائے۔ تو دیکھتا ہے کہ زمین (بنجر اور) خشک ہے پھر ہم اس پر بارشیں برساتے ہیں تو وه ابھرتی ہے اور پھولتی ہے اور ہر قسم کی رونق دار نباتات اگاتی ہے'),
(2601, 22, 6, 'یہ اس لئے کہ اللہ ہی حق ہے اور وہی مردوں کو جِلاتا ہے اور وه ہر ہر چیز پر قدرت رکھنے واﻻ ہے'),
(2602, 22, 7, 'اور یہ کہ قیامت قطعاً آنے والی ہے جس میں کوئی شک وشبہ نہیں اور یقیناً اللہ تعالیٰ قبروں والوں کو دوباره زنده فرمائے گا'),
(2603, 22, 8, 'بعض لوگ اللہ کے بارے میں بغیر علم کے اور بغیر ہدایت کے اور بغیر روشن کتاب کے جھگڑتے ہیں'),
(2604, 22, 9, 'جو اپنی پہلو موڑنے واﻻ بن کر اس لئے کہ اللہ کی راه سے بہکادے، اسے دنیا میں بھی رسوائی ہوگی اور قیامت کے دن بھی ہم اسے جہنم میں جلنے کا عذاب چکھائیں گے'),
(2605, 22, 10, 'یہ ان اعمال کی وجہ سے جو تیرے ہاتھوں نے آگے بھیج رکھے تھے۔ یقین مانو کہ اللہ تعالیٰ اپنے بندوں پر ﻇلم کرنے واﻻ نہیں'),
(2606, 22, 11, 'بعض لوگ ایسے بھی ہیں کہ ایک کنارے پر (کھڑے) ہو کر اللہ کی عبادت کرتے ہیں۔ اگر کوئی نفع مل گیا تو دلچسپی لینے لگتے ہیں اور اگر کوئی آفت آگئی تو اسی وقت منھ پھیر لیتے ہیں، انہوں نے دونوں جہان کانقصان اٹھا لیا۔ واقعی یہ کھلا نقصان ہے'),
(2607, 22, 12, 'اللہ کے سوا انہیں پکارتے ہیں جو نہ انہیں نقصان پہنچا سکیں نہ نفع۔ یہی تو دور دراز کی گمراہی ہے'),
(2608, 22, 13, 'اسے پکارتے ہیں جس کا نقصان اس کے نفع سے زیاده قریب ہے، یقیناً برے والی ہیں اور برے ساتھی'),
(2609, 22, 14, 'ایمان اور نیک اعمال والوں کو اللہ تعالیٰ لہریں لیتی ہوئی نہروں والی جنتوں میں لے جائے گا۔ اللہ جو اراده کرے اسے کر کے رہتا ہے'),
(2610, 22, 15, 'جس کا یہ خیال ہو کہ اللہ تعالیٰ اپنے رسول کی مدد دونوں جہان میں نہ کرے گا وه اونچائی پر ایک رسہ باندھ کر (اپنے حلق میں پھنده ڈال کر اپنا گلا گھونٹ لے) پھر دیکھ لے کہ اس کی چاﻻکیوں سے وه بات ہٹ جاتی ہے جو اسے تڑپا رہی ہے؟'),
(2611, 22, 16, 'ہم نے اسی طرح اس قرآن کو واضح آیتوں میں اتارا ہے جسے اللہ چاہے ہدایت نصیب فرماتا ہے'),
(2612, 22, 17, 'بیشک اہل ایمان اور یہودی اور صابی اور نصرانی اور مجوسی اور مشرکین ان سب کے درمیان قیامت کے دن خود اللہ تعالیٰ فیصلے کر دے گا، اللہ تعالیٰ ہر ہر چیز پر گواه ہے'),
(2613, 22, 18, 'کیا تو نہیں دیکھ رہا کہ اللہ کے سامنے سجده میں ہیں سب آسمانوں والے اور سب زمینوں والے اور سورج اور چاند اور ستارے اور پہاڑ اور درخت اور جانور اور بہت سے انسان بھی۔ ہاں بہت سے وه بھی ہیں جن پر عذاب کا مقولہ ﺛابت ہو چکا ہے، جسے رب ذلیل کردے اسے کوئی عزت دینے واﻻ نہیں، اللہ جو چاہتا ہے کرتا ہے'),
(2614, 22, 19, 'یہ دونوں اپنے رب کے بارے میں اختلاف کرنے والے ہیں، پس کافروں کے لئے تو آگ کے کپڑے بیونت کر کاٹے جائیں گے، اور ان کے سروں کے اوپر سے سخت کھولتا ہوا پانی بہایا جائے گا'),
(2615, 22, 20, 'جس سے ان کے پیٹ کی سب چیزیں اور کھالیں گلا دی جائیں گی'),
(2616, 22, 21, 'اور ان کی سزا کے لئے لوہے کے ہتھوڑے ہیں'),
(2617, 22, 22, 'یہ جب بھی وہاں کے غم سے نکل بھاگنے کا اراده کریں گے وہیں لوٹا دیئے جائیں گے اور (کہا جائے گا) جلنے کا عذاب چکھو!'),
(2618, 22, 23, 'ایمان والوں اور نیک کام والوں کو اللہ تعالیٰ ان جنتوں میں لے جائے گا جن کے درختوں تلے سے نہریں لہریں لے رہی ہیں، جہاں وه سونے کے کنگن پہنائے جائیں گے اور سچے موتی بھی۔ وہاں ان کا لباس خالص ریشم ہوگا'),
(2619, 22, 24, 'ان کو پاکیزه بات کی رہنمائی کر دی گئی اور قابل صد تعریف راه کی ہدایت کر دی گئی'),
(2620, 22, 25, 'جن لوگوں نے کفر کیا اور اللہ کی راه سے روکنے لگے اور اس حرمت والی مسجد سے بھی جسے ہم نے تمام لوگوں کے لئے مساوی کر دیا ہے وہیں کے رہنے والے ہوں یا باہر کے ہوں، جو بھی ﻇلم کے ساتھ وہاں الحاد کا اراده کرے ہم اسے درد ناک عذاب چکھائیں گے'),
(2621, 22, 26, 'اور جبکہ ہم نے ابراہیم (علیہ السلام) کو کعبہ کے مکان کی جگہ مقرر کر دی اس شرط پر کہ میرے ساتھ کسی کو شریک نہ کرنا اور میرے گھر کو طواف قیام رکوع سجده کرنے والوں کے لئے پاک صاف رکھنا'),
(2622, 22, 27, 'اور لوگوں میں حج کی منادی کر دے لوگ تیرے پاس پا پیاده بھی آئیں گے اور دبلے پتلے اونٹوں پر بھی اور دور دراز کی تمام راہوں سے آئیں گے'),
(2623, 22, 28, 'اپنے فائدے حاصل کرنے کو آجائیں اور ان مقرره دنوں میں اللہ کا نام یاد کریں اور چوپایوں پر جو پالتو ہیں۔ پس تم آپ بھی کھاؤ اور بھوکے فقیروں کو بھی کھلاؤ'),
(2624, 22, 29, 'پھر وه اپنا میل کچیل دور کریں اور اپنی نذریں پوری کریں اور اللہ کے قدیم گھر کا طواف کریں'),
(2625, 22, 30, 'یہ ہے اور جو کوئی اللہ کی حرمتوں کی تعظیم کرے اس کے اپنے لئے اس کے رب کے پاس بہتری ہے۔ اور تمہارے لئے چوپائے جانور حلال کر دیئے گئے بجز ان کے جو تمہارے سامنے بیان کیے گئے ہیں پس تمہیں بتوں کی گندگی سے بچتے رہنا چاہئے اور جھوٹی بات سے بھی پرہیز کرنا چاہئے'),
(2626, 22, 31, 'اللہ کی توحید کو مانتے ہوئے اس کے ساتھ کسی کو شریک نہ کرتے ہوئے۔ سنو! اللہ کے ساتھ شریک کرنے واﻻ گویا آسمان سے گر پڑا، اب یا تو اسے پرندے اچک لے جائیں گے یا ہوا کسی دور دراز کی جگہ پھینک دے گی'),
(2627, 22, 32, 'یہ سن لیا اب اور سنو! اللہ کی نشانیوں کی جو عزت وحرمت کرے اس کے دل کی پرہیز گاری کی وجہ سے یہ ہے'),
(2628, 22, 33, 'ان میں تمہارے لئے ایک مقرر وقت تک فائده ہے پھر ان کے حلال ہونے کی جگہ خانہ کعبہ ہے'),
(2629, 22, 34, 'اور ہر امت کے لئے ہم نے قربانی کے طریقے مقرر فرمائے ہیں تاکہ وه ان چوپائے جانوروں پر اللہ کا نام لیں جو اللہ نے انہیں دے رکھے ہیں۔ سمجھ لو کہ تم سب کا معبود برحق صرف ایک ہی ہے تم اسی کے تابع فرمان ہو جاؤ عاجزی کرنے والوں کو خوشخبری سنا دیجئے'),
(2630, 22, 35, 'انہیں کہ جب اللہ کا ذکر کیا جائے ان کے دل تھرا جاتے ہیں۔ انہیں جو برائی پہنچے اس پر صبر کرتے ہیں، نماز قائم کرنے والے ہیں اور جو کچھ ہم نے انہیں دے رکھا ہے وه اس میں سے بھی دیتے رہتے ہیں'),
(2631, 22, 36, 'قربانی کے اونٹ ہم نے تمہارے لئے اللہ تعالیٰ کی نشانیاں مقرر کر دی ہیں ان میں تمہیں نفع ہے۔ پس انہیں کھڑا کر کے ان پر اللہ کا نام لو، پھر جب ان کے پہلو زمین سے لگ جائیں اسے (خود بھی) کھاؤ اور مسکین سوال سے رکنے والوں اور سوال کرنے والوں کو بھی کھلاؤ، اسی طرح ہم نے چوپایوں کو تمہارے ماتحت کردیا ہے کہ تم شکر گزاری کرو'),
(2632, 22, 37, 'اللہ تعالیٰ کو قربانیوں کے گوشت نہیں پہنچتے نہ اس کے خون بلکہ اسے تو تمہارے دل کی پرہیزگاری پہنچتی ہے۔ اسی طرح اللہ نے ان جانوروں کو تمہارے مطیع کر دیا ہے کہ تم اس کی رہنمائی کے شکریئے میں اس کی بڑائیاں بیان کرو، اور نیک لوگوں کو خوشخبری سنا دیجئے!'),
(2633, 22, 38, 'سن رکھو! یقیناً سچے مومنوں کے دشمنوں کو خود اللہ تعالیٰ ہٹا دیتا ہے۔ کوئی خیانت کرنے واﻻ ناشکرا اللہ تعالیٰ کو ہرگز پسند نہیں'),
(2634, 22, 39, 'جن (مسلمانوں) سے (کافر) جنگ کر رہے ہیں انہیں بھی مقابلے کی اجازت دی جاتی ہے کیونکہ وه مظلوم ہیں۔ بیشک ان کی مدد پر اللہ قادر ہے'),
(2635, 22, 40, 'یہ وه ہیں جنہیں ناحق اپنے گھروں سے نکالا گیا، صرف ان کےاس قول پر کہ ہمارا پروردگار فقط اللہ ہے۔ اگر اللہ تعالیٰ لوگوں کو آپس میں ایک دوسرے سے نہ ہٹاتا رہتا تو عبادت خانے اور گرجے اور مسجدیں اور یہودیوں کے معبد اور وه مسجدیں بھی ڈھا دی جاتیں جہاں اللہ کا نام بہ کثرت لیا جاتا ہے۔ جو اللہ کی مدد کرے گا اللہ بھی ضرور اس کی مدد کرے گا۔ بیشک اللہ تعالیٰ بڑی قوتوں واﻻ بڑے غلبے واﻻ ہے'),
(2636, 22, 41, 'یہ وه لوگ ہیں کہ اگر ہم زمین میں ان کے پاؤں جما دیں تو یہ پوری پابندی سے نمازیں قائم کریں اور زکوٰتیں دیں اور اچھے کاموں کا حکم کریں اور برے کاموں سے منع کریں۔ تمام کاموں کا انجام اللہ کے اختیار میں ہے'),
(2637, 22, 42, 'اگر یہ لوگ آپ کو جھٹلائیں (تو کوئی تعجب کی بات نہیں) تو ان سے پہلے نوح کی قوم اور عاد اور ﺛمود'),
(2638, 22, 43, 'اور قوم ابراہیم اور قوم لوط'),
(2639, 22, 44, 'اور مدین والے بھی اپنے اپنے نبیوں کو جھٹلا چکے ہیں۔ موسیٰ (علیہ السلام) بھی جھٹلائے جا چکے ہیں پس میں نے کافروں کو یوں ہی سی مہلت دی پھر دھر دبایا، پھر میرا عذاب کیسا ہوا؟'),
(2640, 22, 45, 'بہت سی بستیاں ہیں جنہیں ہم نے تہ وباﻻ کر دیا اس لئے کہ وه ﻇالم تھے پس وه اپنی چھتوں کے بل اوندھی ہوئی پڑی ہیں اور بہت سے آباد کنوئیں بیکار پڑے ہیں اور بہت سے پکے اور بلند محل ویران پڑے ہیں'),
(2641, 22, 46, 'کیا انہوں نے زمین میں سیر وسیاحت نہیں کی جو ان کے دل ان باتوں کے سمجھنے والے ہوتے یا کانوں سے ہی ان (واقعات) کو سن لیتے، بات یہ ہے کہ صرف آنکھیں ہی اندھی نہیں ہوتیں بلکہ وه دل اندھے ہو جاتے ہیں جو سینوں میں ہیں'),
(2642, 22, 47, 'اللہ ہرگز اپنا وعده نہیں ٹالے گا۔ ہاں البتہ آپ کے رب کے نزدیک ایک دن تمہاری گنتی کے اعتبار سے ایک ہزار سال کا ہے'),
(2643, 22, 48, 'بہت سی ﻇلم کرنے والی بستیوں کو میں نے ڈھیل دی پھر آخر انہیں پکڑ لیا، اور میری ہی طرف لوٹ کر آنا ہے'),
(2644, 22, 49, 'اعلان کر دو کہ لوگو! میں تمہیں کھلم کھلا چوکنا کرنے واﻻ ہی ہوں'),
(2645, 22, 50, 'پس جو ایمان ﻻئے ہیں اور جنہوں نے نیک عمل کیے ہیں ان ہی کے لئے بخشش ہے اور عزت والی روزی'),
(2646, 22, 51, 'اور جو لوگ ہماری نشانیوں کو پست کرنے کے درپے رہتے ہیں وہی دوزخی ہیں'),
(2647, 22, 52, 'ہم نے آپ سے پہلے جس رسول اور نبی کو بھیجا اس کے ساتھ یہ ہوا کہ جب وه اپنے دل میں کوئی آرزو کرنے لگا شیطان نے اس کی آرزو میں کچھ ملا دیا، پس شیطان کی ملاوٹ کو اللہ تعالیٰ دور کر دیتا ہے پھر اپنی باتیں پکی کر دیتا ہے۔ اللہ تعالیٰ دانا اور باحکمت ہے'),
(2648, 22, 53, 'یہ اس لئے کہ شیطانی ملاوٹ کو اللہ تعالیٰ ان لوگوں کی آزمائش کا ذریعہ بنا دے جن کے دلوں میں بیماری ہے اور جن کے دل سخت ہیں۔ بیشک ﻇالم لوگ گہری مخالفت میں ہیں'),
(2649, 22, 54, 'اور اس لئے بھی کہ جنہیں علم عطا فرمایا گیا ہے وه یقین کر لیں کہ یہ آپ کے رب ہی کی طرف سے سراسر حق ہی ہے پھر وه اس پر ایمان ﻻئیں اور ان کے دل اس کی طرف جھک جائیں۔ یقیناً اللہ تعالیٰ ایمان داروں کو راه راست کی طرف رہبری کرنے واﻻ ہی ہے'),
(2650, 22, 55, 'کافر اس وحی الٰہی میں ہمیشہ شک شبہ ہی کرتے رہیں گے حتیٰ کہ اچانک ان کے سروں پر قیامت آجائے یا ان کے پاس اس دن کا عذاب آجائے جو منحوس ہے'),
(2651, 22, 56, 'اس دن صرف اللہ ہی کی بادشاہت ہوگی وہی ان میں فیصلے فرمائے گا۔ ایمان اور نیک عمل والے تو نعمتوں سے بھری جنتوں میں ہوں گے'),
(2652, 22, 57, 'اور جن لوگوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا ان کے لئے ذلیل کرنے والے عذاب ہیں'),
(2653, 22, 58, 'اور جن لوگوں نے راه خدا میں ترک وطن کیا پھر وه شہید کر دیئے گئے یا اپنی موت مر گئے اللہ تعالیٰ انہیں بہترین رزق عطا فرمائے گا۔ اور بیشک اللہ تعالیٰ روزی دینے والوں میں سب سے بہتر ہے'),
(2654, 22, 59, 'انہیں اللہ تعالیٰ ایسی جگہ پہنچائے گا کہ وه اس سے راضی ہو جائیں گے، بیشک اللہ تعالیٰ علم اور بردباری واﻻ ہے'),
(2655, 22, 60, 'بات یہی ہے، اور جس نے بدلہ لیا اسی کے برابر جو اس کے ساتھ کیا گیا تھا پھر اگر اس سے زیادتی کی جائے تو یقیناً اللہ تعالیٰ خود اس کی مدد فرمائے گا۔ بیشک اللہ درگزر کرنے واﻻ بخشنے واﻻ ہے'),
(2656, 22, 61, 'یہ اس لئے کہ اللہ رات کو دن میں داخل کرتا ہے اور دن کو رات میں داخل کرتا ہے، اور بیشک اللہ سننے واﻻ دیکھنے واﻻ ہے'),
(2657, 22, 62, 'یہ سب اس لئے کہ اللہ ہی حق ہے اور اس کے سوا جسے بھی یہ پکارتے ہیں وه باطل ہے اور بیشک اللہ ہی بلندی واﻻ کبریائی واﻻ ہے'),
(2658, 22, 63, 'کیا آپ نے نہیں دیکھا کہ اللہ تعالیٰ آسمان سے پانی برساتا ہے، پس زمین سرسبز ہو جاتی ہے۔ بے شک اللہ تعالیٰ مہربان اور باخبر ہے'),
(2659, 22, 64, 'آسمانوں اور زمین میں جو کچھ ہے اسی کا ہے اور یقیناً اللہ وہی ہے بے نیاز تعریفوں واﻻ'),
(2660, 22, 65, 'کیا آپ نے نہیں دیکھا کہ اللہ ہی نے زمین کی تمام چیزیں تمہارے لئے مسخر کر دی ہیں اور اس کے فرمان سے پانی میں چلتی ہوئی کشتیاں بھی۔ وہی آسمان کو تھامے ہوئے ہے کہ زمین پر اس کی اجازت کے بغیر نہ گر پڑے، بیشک اللہ تعالیٰ لوگوں پر شفقت ونرمی کرنے واﻻ اور مہربان ہے'),
(2661, 22, 66, 'اسی نے تمہیں زندگی بخشی، پھر وہی تمہیں مار ڈالے گا پھر وہی تمہیں زنده کرے گا، بے شک انسان البتہ ناشکرا ہے'),
(2662, 22, 67, 'ہر امت کے لئے ہم نے عبادت کا ایک طریقہ مقرر کر دیا ہے، جسے وه بجا ﻻنے والے ہیں پس انہیں اس امر میں آپ سے جھگڑا نہ کرنا چاہئے آپ اپنے پروردگار کی طرف لوگوں کو بلائیے۔ یقیناً آپ ٹھیک ہدایت پر ہی ہیں'),
(2663, 22, 68, 'پھر بھی اگر یہ لوگ آپ سے الجھنے لگیں تو آپ کہہ دیں کہ تمہارے اعمال سے اللہ بخوبی واقف ہے'),
(2664, 22, 69, 'بیشک تمہارے سب کے اختلاف کا فیصلہ قیامت والے دن اللہ تعالیٰ آپ کرے گا'),
(2665, 22, 70, 'کیا آپ نے نہیں جانا کہ آسمان وزمین کی ہر چیز اللہ کے علم میں ہے۔ یہ سب لکھی ہوئی کتاب میں محفوظ ہے۔ اللہ تعالیٰ پر تو یہ امر بالکل آسان ہے'),
(2666, 22, 71, 'اور یہ اللہ کے سوا ان کی عبادت کر رہے ہیں جس کی کوئی خدائی دلیل نازل نہیں ہوئی نہ وه خود ہی اس کا کوئی علم رکھتے ہیں۔ ﻇالموں کا کوئی مددگار نہیں'),
(2667, 22, 72, 'جب ان کے سامنے ہمارے کلام کی کھلی ہوئی آیتوں کی تلاوت کی جاتی ہے تو آپ کافروں کے چہروں پر ناخوشی کے صاف آﺛار پہچان لیتے ہیں۔ وه تو قریب ہوتے ہیں کہ ہماری آیتیں سنانے والوں پر حملہ کر بیٹھیں، کہہ دیجئے کہ کیا میں تمہیں اس سے بھی زیاده بدتر خبر دوں۔ وه آگ ہے، جس کا وعده اللہ نے کافروں سے کر رکھا ہے، اور وه بہت ہی بری جگہ ہے'),
(2668, 22, 73, 'لوگو! ایک مثال بیان کی جا رہی ہے، ذرا کان لگا کر سن لو! اللہ کے سوا جن جن کو تم پکارتے رہے ہو وه ایک مکھی بھی تو پیدا نہیں کر سکتے، گو سارے کے سارے ہی جمع ہو جائیں، بلکہ اگر مکھی ان سے کوئی چیز لے بھاگے تو یہ تو اسے بھی اس سے چھین نہیں سکتے، بڑا بودا ہے طلب کرنے واﻻ اور بڑا بودا ہے وه جس سے طلب کیا جا رہا ہے'),
(2669, 22, 74, 'انہوں نے اللہ کے مرتبہ کے مطابق اس کی قدر جانی ہی نہیں، اللہ تعالیٰ بڑا ہی زور وقوت واﻻ اور غالب وزبردست ہے'),
(2670, 22, 75, 'فرشتوں میں سے اور انسانوں میں سے پیغام پہونچانے والوں کو اللہ ہی چھانٹ لیتا ہے، بیشک اللہ تعالیٰ سننے واﻻ دیکھنے واﻻ ہے'),
(2671, 22, 76, 'وه بخوبی جانتا ہے جو کچھ ان کے آگے ہے اور جو کچھ ان کے پیچھے ہے، اور اللہ ہی کی طرف سب کام لوٹائے جاتے ہیں'),
(2672, 22, 77, 'اے ایمان والو! رکوع سجده کرتے رہو اور اپنے پروردگار کی عبادت میں لگے رہو اور نیک کام کرتے رہو تاکہ تم کامیاب ہو جاؤ'),
(2673, 22, 78, 'اور اللہ کی راه میں ویسا ہی جہاد کرو جیسا جہاد کا حق ہے۔ اسی نے تمہیں برگزیده بنایا ہے اور تم پر دین کے بارے میں کوئی تنگی نہیں ڈالی، دین اپنے باپ ابراہیم (علیہ السلام) کا قائم رکھو، اسی اللہ نے تمہارا نام مسلمان رکھا ہے۔ اس قرآن سے پہلے اور اس میں بھی تاکہ پیغمبر تم پر گواه ہو جائے اور تم تمام لوگوں کے گواه بن جاؤ۔ پس تمہیں چاہئے کہ نمازیں قائم رکھو اور زکوٰة ادا کرتے رہو اور اللہ کو مضبوط تھام لو، وہی تمہارا ولی اور مالک ہے۔ پس کیا ہی اچھا مالک ہے اور کتنا ہی بہتر مددگار ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2674, 23, 1, 'یقیناً ایمان والوں نے فلاح حاصل کرلی'),
(2675, 23, 2, 'جو اپنی نماز میں خشوع کرتے ہیں'),
(2676, 23, 3, 'جو لغویات سے منھ موڑ لیتے ہیں'),
(2677, 23, 4, 'جو زکوٰة ادا کرنے والے ہیں'),
(2678, 23, 5, 'جو اپنی شرمگاہوں کی حفاﻇت کرنے والے ہیں'),
(2679, 23, 6, 'بجز اپنی بیویوں اور ملکیت کی لونڈیوں کے یقیناً یہ ملامتیوں میں سے نہیں ہیں'),
(2680, 23, 7, 'جو اس کے سوا کچھ اور چاہیں وہی حد سے تجاوز کرجانے والے ہیں'),
(2681, 23, 8, 'جو اپنی امانتوں اور وعدے کی حفاﻇت کرنے والے ہیں'),
(2682, 23, 9, 'جو اپنی نمازوں کی نگہبانی کرتے ہیں'),
(2683, 23, 10, 'یہی وارث ہیں'),
(2684, 23, 11, 'جو فردوس کے وارث ہوں گے جہاں وه ہمیشہ رہیں گے'),
(2685, 23, 12, 'یقیناً ہم نے انسان کو مٹی کے جوہر سے پیدا کیا'),
(2686, 23, 13, 'پھر اسے نطفہ بنا کر محفوظ جگہ میں قرار دے دیا'),
(2687, 23, 14, 'پھر نطفہ کو ہم نے جما ہوا خون بنا دیا، پھر اس خون کے لوتھڑے کو گوشت کا ٹکڑا کردیا۔ پھر گوشت کے ٹکڑے کو ہڈیاں بنا دیں، پھر ہڈیوں کو ہم نے گوشت پہنا دیا، پھر دوسری بناوٹ میں اس کو پیدا کردیا۔ برکتوں واﻻ ہے وه اللہ جو سب سے بہترین پیدا کرنے واﻻ ہے'),
(2688, 23, 15, 'اس کے بعد پھر تم سب یقیناً مر جانے والے ہو'),
(2689, 23, 16, 'پھر قیامت کے دن بلا شبہ تم سب اٹھائے جاؤ گے'),
(2690, 23, 17, 'ہم نے تمہارے اوپر سات آسمان بنائے ہیں اور ہم مخلوقات سے غافل نہیں ہیں'),
(2691, 23, 18, 'ہم ایک صحیح انداز سے آسمان سے پانی برساتے ہیں، پھر اسے زمین میں ٹھہرا دیتے ہیں، اور ہم اس کے لے جانے پر یقیناً قادر ہیں'),
(2692, 23, 19, 'اسی پانی کے ذریعہ سے ہم تمہارے لئے کھجوروں اور انگوروں کے باغات پیدا کردیتے ہیں، کہ تمہارے لئے ان میں بہت سے میوے ہوتے ہیں ان ہی میں سے تم کھاتے بھی ہو'),
(2693, 23, 20, 'اور وه درخت جو طور سینا پہاڑ سے نکلتا ہے جو تیل نکالتا ہے اور کھانے والے کے لئے سالن ہے'),
(2694, 23, 21, 'تمہارے لئے چوپایوں میں بھی بڑی بھاری عبرت ہے۔ ان کے پیٹوں میں سے ہم تمہیں دودھ پلاتے ہیں اور بھی بہت سے نفع تمہارے لئے ان میں ہیں ان میں سے بعض بعض کو تم کھاتے بھی ہو'),
(2695, 23, 22, 'اور ان پر اور کشتیوں پر تم سوار کرائے جاتے ہو'),
(2696, 23, 23, 'یقیناً ہم نے نوح (علیہ السلام) کو اس کی قوم کی طرف رسول بنا کر بھیجا، اس نے کہا کہ اے میری قوم کے لوگو! اللہ کی عبادت کرو اور اس کے سوا تمہارا کوئی معبود نہیں، کیا تم (اس سے) نہیں ڈرتے'),
(2697, 23, 24, 'اس کی قوم کے کافر سرداروں نے صاف کہہ دیا کہ یہ تو تم جیسا ہی انسان ہے، یہ تم پر فضیلت اور بڑائی حاصل کرنا چاہتا ہے۔ اگر اللہ ہی کو منظور ہوتا تو کسی فرشتے کو اتارتا، ہم نے تو اسے اپنے اگلے باپ دادوں کے زمانے میں سنا ہی نہیں'),
(2698, 23, 25, 'یقیناً اس شخص کو جنون ہے، پس تم اسے ایک وقت مقرر تک ڈھیل دو'),
(2699, 23, 26, 'نوح (علیہ السلام) نے دعا کی اے میرے رب! ان کے جھٹلانے پر تو میری مدد کر'),
(2700, 23, 27, 'تو ہم نے ان کی طرف وحی بھیجی کہ تو ہماری آنکھوں کے سامنے ہماری وحی کے مطابق ایک کشتی بنا۔ جب ہمارا حکم آجائے اور تنور ابل پڑے تو، تو ہر قسم کا ایک ایک جوڑا اس میں رکھ لے اور اپنے اہل کو بھی، مگر ان میں سے جن کی بابت ہماری بات پہلے گزر چکی ہے۔ خبردار جن لوگوں نے ﻇلم کیا ہے ان کے بارے میں مجھ سے کچھ کلام نہ کرنا وه تو سب ڈبوئے جائیں گے'),
(2701, 23, 28, 'جب تو اور تیرے ساتھی کشتی پر باطمینان بیٹھ جاؤ تو کہنا کہ سب تعریف اللہ کے لئے ہی ہے جس نے ہمیں ﻇالم لوگوں سے نجات عطا فرمائی'),
(2702, 23, 29, 'اور کہنا کہ اے میرے رب! مجھے بابرکت اتارنا اتار اور تو ہی بہتر ہے اتارنے والوں میں'),
(2703, 23, 30, 'یقیناً اس میں بڑی بڑی نشانیاں ہیں اور ہم بےشک آزمائش کرنے والے ہیں'),
(2704, 23, 31, 'ان کے بعد ہم نے اور بھی امت پیدا کی'),
(2705, 23, 32, 'پھر ان میں خود ان میں سے (ہی) رسول بھی بھیجا کہ تم سب اللہ کی عبادت کرو اس کے سوا تمہارا کوئی معبود نہیں، تم کیوں نہیں ڈرتے؟'),
(2706, 23, 33, 'اور سرداران قوم نے جواب دیا، جو کفر کرتے تھے اور آخرت کی ملاقات کو جھٹلاتے تھے اور ہم نے انہیں دنیوی زندگی میں خوشحال کر رکھا تھا، کہ یہ تو تم جیسا ہی انسان ہے، تمہاری ہی خوراک یہ بھی کھاتا ہے اور تمہارے پینے کا پانی ہی یہ پیتا ہے'),
(2707, 23, 34, 'اگر تم نے اپنے جیسے ہی انسان کی تابعداری کر لی ہے تو بےشک تم سخت خسارے والے ہو'),
(2708, 23, 35, 'کیا یہ تمہیں اس بات کا وعده کرتا ہے کہ جب تم مرکر صرف خاک اور ہڈی ره جاؤ گے تو تم پھر زنده کئے جاؤ گے'),
(2709, 23, 36, 'نہیں نہیں دور اور بہت دور ہے وه جس کا تم وعده دیئے جاتے ہو'),
(2710, 23, 37, '(زندگی) تو صرف دنیا کی زندگی ہے ہم مرتے جیتے رہتے ہیں اور یہ نہیں کہ ہم پھر اٹھائے جائیں گے'),
(2711, 23, 38, 'یہ تو بس ایسا شخص ہے جس نے اللہ پر جھوٹ (بہتان) باندھ لیا ہے، ہم تو اس پر ایمان ﻻنے والے نہیں ہیں'),
(2712, 23, 39, 'نبی نے دعا کی کہ پروردگار! ان کے جھٹلانے پر تو میری مدد کر'),
(2713, 23, 40, 'جواب ملا کہ یہ تو بہت ہی جلد اپنے کئے پر پچھتانے لگیں گے'),
(2714, 23, 41, 'بالﺂخر عدل کے تقاضے کے مطابق چیﺦ نے پکڑ لیا اور ہم نے انہیں کوڑا کرکٹ کر ڈاﻻ، پس ﻇالموں کے لئے دوری ہو'),
(2715, 23, 42, 'ان کے بعد ہم نے اور بھی بہت سی امتیں پیدا کیں'),
(2716, 23, 43, 'نہ تو کوئی امت اپنے وقت مقرره سے آگے بڑھی اور نہ پیچھے رہی'),
(2717, 23, 44, 'پھر ہم نے لگاتار رسول بھیجے، جب جب اس امت کے پاس اس کا رسول آیا اس نے جھٹلایا، پس ہم نے ایک دوسرے کے پیچھے لگا دیا اور انہیں افسانہ بنا دیا۔ ان لوگوں کو دوری ہے جو ایمان قبول نہیں کرتے'),
(2718, 23, 45, 'پھر ہم نے موسیٰ (علیہ السلام) کو اور ان کے بھائی ہارون (علیہ السلام) کو اپنی آیتوں اور کھلی دلیل کے ساتھ بھیجا'),
(2719, 23, 46, 'فرعون اور اس کے لشکروں کی طرف، پس انہوں نے تکبر کیا اور تھے ہی وه سرکش لوگ'),
(2720, 23, 47, 'کہنے لگے کہ کیا ہم اپنے جیسے دو شخصوں پر ایمان ﻻئیں؟ حاﻻنکہ خود ان کی قوم (بھی) ہمارے ماتحت ہے'),
(2721, 23, 48, 'پس انہوں نے ان دونوں کو جھٹلایا آخر وه بھی ہلاک شده لوگوں میں مل گئے'),
(2722, 23, 49, 'ہم نے تو موسیٰ (علیہ السلام) کو کتاب (بھی) دی کہ لوگ راه راست پر آجائیں'),
(2723, 23, 50, 'ہم نے ابن مریم اور اس کی والده کو ایک نشانی بنایا اور ان دونوں کو بلند صاف قرار والی اور جاری پانی والی جگہ میں پناه دی'),
(2724, 23, 51, 'اے پیغمبرو! حلال چیزیں کھاؤ اور نیک عمل کرو تم جو کچھ کر رہے ہو اس سے میں بخوبی واقف ہوں'),
(2725, 23, 52, 'یقیناً تمہارا یہ دین ایک ہی دین ہے اور میں ہی تم سب کا رب ہوں، پس تم مجھ سے ڈرتے رہو'),
(2726, 23, 53, 'پھر انہوں نے خود (ہی) اپنے امر (دین) کے آپس میں ٹکڑے ٹکڑے کر لیئے، ہر گروه جو کچھ اس کے پاس ہے اسی پر اترا رہا ہے'),
(2727, 23, 54, 'پس آپ (بھی) انہیں ان کی غفلت میں ہی کچھ مدت پڑا رہنے دیں'),
(2728, 23, 55, 'کیا یہ (یوں) سمجھ بیٹھے ہیں؟ کہ ہم جو بھی ان کے مال و اوﻻد بڑھا رہے ہیں'),
(2729, 23, 56, 'وه ان کے لئے بھلائیوں میں جلدی کر رہے ہیں (نہیں نہیں) بلکہ یہ سمجھتے ہی نہیں'),
(2730, 23, 57, 'یقیناً جو لوگ اپنے رب کی ہیبت سے ڈرتے ہیں'),
(2731, 23, 58, 'اور جو اپنے رب کی آیتوں پر ایمان رکھتے ہیں'),
(2732, 23, 59, 'اور جو اپنے رب کے ساتھ کسی کو شریک نہیں کرتے'),
(2733, 23, 60, 'اور جو لوگ دیتے ہیں جو کچھ دیتے ہیں اور ان کے دل کپکپاتے ہیں کہ وه اپنے رب کی طرف لوٹنے والے ہیں'),
(2734, 23, 61, 'یہی ہیں جو جلدی جلدی بھلائیاں حاصل کر رہے ہیں اور یہی ہیں جو ان کی طرف دوڑ جانے والے ہیں'),
(2735, 23, 62, 'ہم کسی نفس کو اس کی طاقت سے زیاده تکلیف نہیں دیتے، اور ہمارے پاس ایسی کتاب ہے جو حق کے ساتھ بولتی ہے، ان کے اوپر کچھ ﻇلم نہ کیا جائے گا'),
(2736, 23, 63, 'بلکہ ان کے دل اس طرف سے غفلت میں ہیں اور ان کے لئے اس کے سوا بھی بہت سے اعمال ہیں جنہیں وه کرنے والے ہیں'),
(2737, 23, 64, 'یہاں تک کہ جب ہم نے ان کے آسوده حال لوگوں کو عذاب میں پکڑ لیا تو وه بلبلانے لگے'),
(2738, 23, 65, 'آج مت بلبلاؤ یقیناً تم ہمارے مقابلہ پر مدد نہ کئے جاؤ گے۔'),
(2739, 23, 66, 'میری آیتیں تو تمہارے سامنے پڑھی جاتی تھیں پھر بھی تم اپنی ایڑیوں کے بل الٹے بھاگتے تھے'),
(2740, 23, 67, 'اکڑتے اینٹھتے افسانہ گوئی کرتے اسے چھوڑ دیتے تھے'),
(2741, 23, 68, 'کیا انہوں نے اس بات میں غور وفکر ہی نہیں کیا؟ بلکہ ان کے پاس وه آیا جو ان کے اگلے باپ دادوں کے پاس نہیں آیا تھا؟'),
(2742, 23, 69, 'یا انہوں نے اپنے پیغمبر کو پہچانا نہیں کہ اس کے منکر ہو رہے ہیں؟'),
(2743, 23, 70, 'یا یہ کہتے ہیں کہ اسے جنون ہے؟ بلکہ وه تو ان کے پاس حق ﻻیا ہے۔ ہاں ان میں اکثر حق سے چڑنے والے ہیں'),
(2744, 23, 71, 'اگر حق ہی ان کی خواہشوں کا پیرو ہوجائے تو زمین وآسمان اور ان کے درمیان کی ہر چیز درہم برہم ہو جائے۔ حق تو یہ ہے کہ ہم نے انہیں ان کی نصیحت پہنچا دی ہے لیکن وه اپنی نصیحت سے منھ موڑنے والے ہیں'),
(2745, 23, 72, 'کیا آپ ان سے کوئی اجرت چاہتے ہیں؟ یاد رکھیئے کہ آپ کے رب کی اجرت بہت ہی بہتر ہے اور وه سب سے بہتر روزی رساں ہے'),
(2746, 23, 73, 'یقیناً آپ تو انہیں راه راست کی طرف بلا رہے ہیں'),
(2747, 23, 74, 'بےشک جو لوگ آخرت پر یقین نہیں رکھتے وه سیدھے راستے سے مڑ جانے والے ہیں'),
(2748, 23, 75, 'اور اگر ہم ان پر رحم فرمائیں اور ان کی تکلیفیں دور کردیں تو یہ تو اپنی اپنی سرکشی میں جم کر اور بہکنے لگیں'),
(2749, 23, 76, 'اور ہم نے انہیں عذاب میں بھی پکڑا تاہم یہ لوگ نہ تو اپنے پروردگار کے سامنے جھکے اور نہ ہی عاجزی اختیار کی'),
(2750, 23, 77, 'یہاں تک کہ جب ہم نے ان پر سخت عذاب کا دروازه کھول دیا تو اسی وقت فوراً مایوس ہوگئے'),
(2751, 23, 78, 'وه اللہ ہے جس نے تمہارے لئے کان اور آنکھیں اور دل پیدا کئے، مگر تم بہت (ہی) کم شکر کرتے ہو'),
(2752, 23, 79, 'اور وہی ہے جس نے تمہیں پیدا کرکے زمین میں پھیلا دیا اور اسی کی طرف تم جمع کئے جاؤ گے'),
(2753, 23, 80, 'اور یہ وہی ہے جو جلاتا اور مارتا ہے اور رات دن کے ردوبدل کا مختار بھی وہی ہے۔ کیا تم کو سمجھ بوجھ نہیں؟'),
(2754, 23, 81, 'بلکہ ان لوگوں نے بھی ویسی ہی بات کہی جو اگلے کہتے چلے آئے'),
(2755, 23, 82, 'کہ کیا جب ہم مر کر مٹی اور ہڈی ہوجائیں گے کیا پھر بھی ہم ضرور اٹھائے جائیں گے؟'),
(2756, 23, 83, 'ہم سے اور ہمارے باپ دادوں سے پہلے ہی یہ وعده ہوتا چلا آیا ہے کچھ نہیں یہ تو صرف اگلے لوگوں کے افسانے ہیں'),
(2757, 23, 84, 'پوچھیئے تو سہی کہ زمین اور اس کی کل چیزیں کس کی ہیں؟ بتلاؤ اگر جانتے ہو؟'),
(2758, 23, 85, 'فوراً جواب دیں گے کہ اللہ کی، کہہ دیجیئے کہ پھر تم نصیحت کیوں نہیں حاصل کرتے'),
(2759, 23, 86, 'دریافت کیجیئے کہ ساتوں آسمان کا اور بہت باعظمت عرش کا رب کون ہے؟'),
(2760, 23, 87, 'وه لوگ جواب دیں گے کہ اللہ ہی ہے۔ کہہ دیجیئے کہ پھر تم کیوں نہیں ڈرتے؟'),
(2761, 23, 88, 'پوچھیئے کہ تمام چیزوں کا اختیار کس کے ہاتھ میں ہے؟ جو پناه دیتا ہے اور جس کے مقابلے میں کوئی پناه نہیں دیا جاتا، اگر تم جانتے ہو تو بتلا دو؟'),
(2762, 23, 89, 'یہی جواب دیں گے کہ اللہ ہی ہے، کہہ دیجیئے پھر تم کدھر سے جادو کر دیئے جاتے ہو؟'),
(2763, 23, 90, 'حق یہ ہے کہ ہم نے انہیں حق پہنچا دیا ہے اور یہ بےشک جھوٹے ہیں'),
(2764, 23, 91, 'نہ تو اللہ نے کسی کو بیٹا بنایا اور نہ اس کے ساتھ اور کوئی معبود ہے، ورنہ ہر معبود اپنی مخلوق کو لئے لئے پھرتا اور ہر ایک دوسرے پر چڑھ دوڑتا۔ جو اوصاف یہ بتلاتے ہیں ان سے اللہ پاک (اور بےنیاز) ہے'),
(2765, 23, 92, 'وه غائب حاضر کا جاننے واﻻ ہے اور جو شرک یہ کرتے ہیں اس سے باﻻتر ہے'),
(2766, 23, 93, 'آپ دعا کریں کہ اے میرے پروردگار! اگر تو مجھے وه دکھائے جس کا وعده انہیں دیا جا رہا ہے'),
(2767, 23, 94, 'تو اے رب! تو مجھے ان ﻇالموں کے گروه میں نہ کرنا'),
(2768, 23, 95, 'ہم جو کچھ وعدے انہیں دے رہے ہیں سب آپ کو دکھا دینے پر یقیناً قادر ہیں'),
(2769, 23, 96, 'برائی کو اس طریقے سے دور کریں جو سراسر بھلائی واﻻ ہو، جو کچھ یہ بیان کرتے ہیں ہم بخوبی واقف ہیں'),
(2770, 23, 97, 'اور دعا کریں کہ اے میرے پروردگار! میں شیطانوں کے وسوسوں سے تیری پناه چاہتا ہوں'),
(2771, 23, 98, 'اور اے رب! میں تیری پناه چاہتا ہوں کہ وه میرے پاس آجائیں'),
(2772, 23, 99, 'یہاں تک کہ جب ان میں کسی کو موت آنے لگتی ہے تو کہتا ہے اے میرے پروردگار! مجھے واپس لوٹا دے'),
(2773, 23, 100, 'کہ اپنی چھوڑی ہوئی دنیا میں جا کر نیک اعمال کر لوں، ہرگز ایسا نہیں ہوگا، یہ تو صرف ایک قول ہے جس کا یہ قائل ہے، ان کے پس پشت تو ایک حجاب ہے، ان کے دوباره جی اٹھنے کے دن تک'),
(2774, 23, 101, 'پس جب کہ صور پھونک دیا جائے گا اس دن نہ تو آپس کے رشتے دار ہی رہیں گے، نہ آپس کی پوچھ گچھ'),
(2775, 23, 102, 'جن کی ترازو کا پلہ بھاری ہوگیا وه تو نجات والے ہوگئے'),
(2776, 23, 103, 'اور جن کے ترازو کا پلہ ہلکا ہوگیا یہ ہیں وه جنہوں نے اپنا نقصان آپ کر لیا جو ہمیشہ کے لئے جہنم واصل ہوئے'),
(2777, 23, 104, 'ان کے چہروں کو آگ جھلستی رہے گی اور وه وہاں بدشکل بنے ہوئے ہوں گے'),
(2778, 23, 105, 'کیا میری آیتیں تمہارے سامنے تلاوت نہیں کی جاتی تھیں؟ پھر بھی تم انہیں جھٹلاتے تھے'),
(2779, 23, 106, 'کہیں گے کہ اے پروردگار! ہماری بدبختی ہم پر غالب آگئی (واقعی) ہم تھے ہی گمراه'),
(2780, 23, 107, 'اے ہمارے پروردگار! ہمیں یہاں سے نجات دے اگر اب بھی ہم ایسا ہی کریں تو بےشک ہم ﻇالم ہیں'),
(2781, 23, 108, 'اللہ تعالیٰ فرمائے گا پھٹکارے ہوئے یہیں پڑے رہو اور مجھ سے کلام نہ کرو'),
(2782, 23, 109, 'میرے بندوں کی ایک جماعت تھی جو برابر یہی کہتی رہی کہ اے ہمارے پروردگار! ہم ایمان ﻻچکے ہیں تو ہمیں بخش اور ہم پر رحم فرما تو سب مہربانوں سے زیاده مہربان ہے'),
(2783, 23, 110, '(لیکن) تم انہیں مذاق میں ہی اڑاتے رہے یہاں تک کہ (اس مشغلے نے) تم کو میری یاد (بھی) بھلا دی اور تم ان سے مذاق ہی کرتے رہے'),
(2784, 23, 111, 'میں نے آج انہیں ان کے اس صبر کا بدلہ دے دیا ہے کہ وه خاطر خواه اپنی مراد کو پہنچ چکے ہیں'),
(2785, 23, 112, 'اللہ تعالیٰ دریافت فرمائے گا کہ تم زمین میں باعتبار برسوں کی گنتی کے کس قدر رہے؟'),
(2786, 23, 113, 'وه کہیں گے ایک دن یا ایک دن سے بھی کم، گنتی گننے والوں سے بھی پوچھ لیجیئے'),
(2787, 23, 114, 'اللہ تعالیٰ فرمائے گا فی الواقع تم وہاں بہت ہی کم رہے ہو اے کاش! تم اسے پہلے ہی سے جان لیتے؟'),
(2788, 23, 115, 'کیا تم یہ گمان کئے ہوئے ہو کہ ہم نے تمہیں یوں ہی بیکار پیدا کیا ہے اور یہ کہ تم ہماری طرف لوٹائے ہی نہ جاؤ گے'),
(2789, 23, 116, 'اللہ تعالیٰ سچا بادشاه ہے وه بڑی بلندی واﻻ ہے۔ اس کے سوا کوئی معبود نہیں، وہی بزرگ عرش کا مالک ہے'),
(2790, 23, 117, 'جو شخص اللہ کے ساتھ کسی دوسرے معبود کو پکارے جس کی کوئی دلیل اس کے پاس نہیں، پس اس کا حساب تو اس کے رب کے اوپر ہی ہے۔ بےشک کافر لوگ نجات سے محروم ہیں'),
(2791, 23, 118, 'اور کہو کہ اے میرے رب! تو بخش اور رحم کر اور تو سب مہربانوں سے بہتر مہربانی کرنے واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2792, 24, 1, 'یہ ہے وه سورت جو ہم نے نازل فرمائی ہے اور مقرر کردی ہے اور جس میں ہم نے کھلی آیتیں (احکام) اتارے ہیں تاکہ تم یاد رکھو'),
(2793, 24, 2, 'زناکار عورت و مرد میں سے ہر ایک کو سو کوڑے لگاؤ۔ ان پر اللہ کی شریعت کی حد جاری کرتے ہوئے تمہیں ہرگز ترس نہ کھانا چاہیئے، اگر تمہیں اللہ پر اور قیامت کے دن پر ایمان ہو۔ ان کی سزا کے وقت مسلمانوں کی ایک جماعت موجود ہونی چاہیئے'),
(2794, 24, 3, 'زانی مرد بجز زانیہ یا مشرکہ عورت کے اور سے نکاح نہیں کرتا اور زناکار عورت بھی بجز زانی یا مشرک مرد کے اور سے نکاح نہیں کرتی اور ایمان والوں پر یہ حرام کردیا گیا'),
(2795, 24, 4, 'جو لوگ پاک دامن عورتوں پر زنا کی تہمت لگائیں پھر چار گواه نہ پیش کرسکیں تو انہیں اسی کوڑے لگاؤ اور کبھی بھی ان کی گواہی قبول نہ کرو۔ یہ فاسق لوگ ہیں'),
(2796, 24, 5, 'ہاں جو لوگ اس کے بعد توبہ اور اصلاح کرلیں تو اللہ تعالیٰ بخشنے واﻻ اور مہربانی کرنے واﻻ ہے'),
(2797, 24, 6, 'جو لوگ اپنی بیویوں پر بدکاری کی تہمت لائیں اور ان کا کوئی گواه بجز خود ان کی ذات کے نہ ہو تو ایسے لوگوں میں سے ہر ایک کا ﺛبوت یہ ہے کہ چار مرتبہ اللہ کی قسم کھا کر کہیں کہ وه سچوں میں سے ہیں'),
(2798, 24, 7, 'اور پانچویں مرتبہ کہے کہ اس پر اللہ تعالیٰ کی لعنت ہو اگر وه جھوٹوں میں سے ہو'),
(2799, 24, 8, 'اور اس عورت سے سزا اس طرح دور ہوسکتی ہے کہ وه چار مرتبہ اللہ کی قسم کھا کر کہے کہ یقیناً اس کا مرد جھوٹ بولنے والوں میں سے ہے'),
(2800, 24, 9, 'اور پانچویں دفعہ کہے کہ اس پر اللہ تعالیٰ کا غضب ہو اگر اس کا خاوند سچوں میں سے ہو'),
(2801, 24, 10, 'اگر اللہ تعالیٰ کا فضل وکرم تم پر نہ ہوتا (تو تم پر مشقت اترتی) اور اللہ تعالیٰ توبہ قبول کرنے واﻻ باحکمت ہے'),
(2802, 24, 11, 'جو لوگ یہ بہت بڑا بہتان باندھ ﻻئے ہیں یہ بھی تم میں سے ہی ایک گروه ہے۔ تم اسے اپنے لئے برا نہ سمجھو، بلکہ یہ تو تمہارے حق میں بہتر ہے۔ ہاں ان میں سے ہر ایک شخص پر اتنا گناه ہے جتنا اس نے کمایا ہے اور ان میں سے جس نے اس کے بہت بڑے حصے کو سرانجام دیا ہے اس کے لئے عذاب بھی بہت ہی بڑا ہے'),
(2803, 24, 12, 'اسے سنتے ہی مومن مردوں عورتوں نے اپنے حق میں نیک گمانی کیوں نہ کی اور کیوں نہ کہہ دیا کہ یہ تو کھلم کھلا صریح بہتان ہے'),
(2804, 24, 13, 'وه اس پر چار گواه کیوں نہ ﻻئے؟ اور جب گواه نہیں ﻻئے تو یہ بہتان باز لوگ یقیناً اللہ کے نزدیک محض جھوٹے ہیں'),
(2805, 24, 14, 'اگر اللہ تعالیٰ کا فضل وکرم تم پر دنیا اور آخرت میں نہ ہوتا تو یقیناً تم نے جس بات کے چرچے شروع کر رکھے تھے اس بارے میں تمہیں بہت بڑا عذاب پہنچتا'),
(2806, 24, 15, 'جب کہ تم اسے اپنی زبانوں سے نقل در نقل کرنے لگے اور اپنے منھ سے وه بات نکالنے لگے جس کی تمہیں مطلق خبر نہ تھی، گو تم اسے ہلکی بات سمجھتے رہے لیکن اللہ تعالیٰ کے نزدیک وه بہت بڑی بات تھی'),
(2807, 24, 16, 'تم نے ایسی بات کو سنتے ہی کیوں نہ کہہ دیا کہ ہمیں ایسی بات منھ سے نکالنی بھی ﻻئق نہیں۔ یا اللہ! تو پاک ہے، یہ تو بہت بڑا بہتان ہے اور تہمت ہے'),
(2808, 24, 17, 'اللہ تعالیٰ تمہیں نصیحت کرتا ہے کہ پھر کبھی بھی ایسا کام نہ کرنا اگر تم سچے مومن ہو'),
(2809, 24, 18, 'اللہ تعالیٰ تمہارے سامنے اپنی آیتیں بیان فرما رہا ہے، اور اللہ تعالیٰ علم و حکمت واﻻ ہے'),
(2810, 24, 19, 'جو لوگ مسلمانوں میں بےحیائی پھیلانے کے آرزومند رہتے ہیں ان کے لئے دنیا اور آخرت میں دردناک عذاب ہیں، اللہ سب کچھ جانتا ہے اور تم کچھ بھی نہیں جانتے'),
(2811, 24, 20, 'اگر تم پر اللہ کا فضل اور اس کی رحمت نہ ہوتی اور یہ بھی کہ اللہ تعالیٰ بڑی شفقت رکھنے واﻻ مہربان ہے۔ (تو تم پر عذاب اتر جاتا)'),
(2812, 24, 21, 'ایمان والو! شیطان کے قدم بقدم نہ چلو۔ جو شخص شیطانی قدموں کی پیروی کرے تو وه تو بےحیائی اور برے کاموں کا ہی حکم کرے گا۔ اور اگر اللہ تعالیٰ کا فضل وکرم تم پر نہ ہوتا تو تم میں سے کوئی کبھی بھی پاک صاف نہ ہوتا۔ لیکن اللہ تعالی جسے پاک کرنا چاہے، کر دیتا ہے۔ اور اللہ سب سننے واﻻ سب جاننے واﻻ ہے'),
(2813, 24, 22, 'تم میں سے جو بزرگی اور کشادگی والے ہیں انہیں اپنے قرابت داروں اور مسکینوں اور مہاجروں کو فی سبیل اللہ دینے سے قسم نہ کھا لینی چاہیئے، بلکہ معاف کردینا اور درگزر کرلینا چاہیئے۔ کیا تم نہیں چاہتے کہ اللہ تعالیٰ تمہارے قصور معاف فرما دے؟ اللہ قصوروں کو معاف فرمانے واﻻ مہربان ہے'),
(2814, 24, 23, 'جو لوگ پاک دامن بھولی بھالی باایمان عورتوں پر تہمت لگاتے ہیں وه دنیا وآخرت میں ملعون ہیں اور ان کے لئے بڑا بھاری عذاب ہے'),
(2815, 24, 24, 'جب کہ ان کے مقابلے میں ان کی زبانیں اور ان کے ہاتھ پاؤں ان کے اعمال کی گواہی دیں گے'),
(2816, 24, 25, 'اس دن اللہ تعالیٰ انہیں پورا پورا بدلہ حق و انصاف کے ساتھ دے گا اور وه جان لیں گے کہ اللہ تعالیٰ ہی حق ہے (اور وہی) ﻇاہر کرنے واﻻ ہے'),
(2817, 24, 26, 'خبیﺚ عورتیں خبیﺚ مردوں کے ﻻئق ہیں اور خبیﺚ مرد خبیﺚ عورتوں کے ﻻئق ہیں اور پاک عورتیں پاک مردوں کے ﻻئق ہیں اور پاک مرد پاک عورتوں کے ﻻئق ہیں۔ ایسے پاک لوگوں کے متعلق جو کچھ بکواس (بہتان باز) کر رہے ہیں وه ان سے بالکل بری ہیں، ان کے لئے بخشش ہے اور عزت والی روزی'),
(2818, 24, 27, 'اے ایمان والو! اپنے گھروں کے سوا اور گھروں میں نہ جاؤ جب تک کہ اجازت نہ لے لو اور وہاں کے رہنے والوں کو سلام نہ کرلو، یہی تمہارے لئے سراسر بہتر ہے تاکہ تم نصیحت حاصل کرو'),
(2819, 24, 28, 'اگر وہاں تمہیں کوئی بھی نہ مل سکے تو پھر اجازت ملے بغیر اندر نہ جاؤ۔ اگر تم سے لوٹ جانے کو کہا جائے تو تم لوٹ ہی جاؤ، یہی بات تمہارے لئے پاکیزه ہے، جو کچھ تم کر رہے ہو اللہ تعالیٰ خوب جانتا ہے'),
(2820, 24, 29, 'ہاں غیر آباد گھروں میں جہاں تمہارا کوئی فائده یا اسباب ہو، جانے میں تم پر کوئی گناه نہیں۔ تم جو کچھ بھی ﻇاہر کرتے ہو اور جو چھپاتے ہو اللہ تعالیٰ سب کچھ جانتا ہے'),
(2821, 24, 30, 'مسلمان مردوں سے کہو کہ اپنی نگاہیں نیچی رکھیں، اور اپنی شرمگاہوں کی حفاﻇت رکھیں۔ یہی ان کے لئے پاکیزگی ہے، لوگ جو کچھ کریں اللہ تعالیٰ سب سے خبردار ہے'),
(2822, 24, 31, 'مسلمان عورتوں سے کہو کہ وه بھی اپنی نگاہیں نیچی رکھیں اور اپنی عصمت میں فرق نہ آنے دیں اور اپنی زینت کو ﻇاہر نہ کریں، سوائے اس کے جو ﻇاہر ہے اور اپنے گریبانوں پر اپنی اوڑھنیاں ڈالے رہیں، اور اپنی آرائش کو کسی کے سامنے ﻇاہر نہ کریں، سوائے اپنے خاوندوں کے یا اپنے والد کے یا اپنے خسر کے یا اپنے لڑکوں کے یا اپنے خاوند کے لڑکوں کے یا اپنے بھائیوں کے یا اپنے بھتیجوں کے یا اپنے بھانجوں کے یا اپنے میل جول کی عورتوں کے یا غلاموں کے یا ایسے نوکر چاکر مردوں کے جو شہوت والے نہ ہوں یا ایسے بچوں کے جو عورتوں کے پردے کی باتوں سے مطلع نہیں۔ اور اس طرح زور زور سے پاؤں مار کر نہ چلیں کہ ان کی پوشیده زینت معلوم ہوجائے، اے مسلمانو! تم سب کے سب اللہ کی جناب میں توبہ کرو تاکہ تم نجات پاؤ'),
(2823, 24, 32, 'تم میں سے جو مرد عورت بےنکاح کے ہوں ان کا نکاح کر دو اور اپنے نیک بخت غلام اور لونڈیوں کا بھی۔ اگر وه مفلس بھی ہوں گے تو اللہ تعالیٰ انہیں اپنے فضل سے غنی بنا دے گا۔ اللہ تعالیٰ کشادگی واﻻ اور علم واﻻ ہے'),
(2824, 24, 33, 'اور ان لوگوں کو پاک دامن رہنا چاہیئے جو اپنا نکاح کرنے کا مقدور نہیں رکھتے یہاں تک کہ اللہ تعالیٰ انہیں اپنے فضل سے مالدار بنا دے، تمہارے غلاموں میں جو کوئی کچھ تمہیں دے کر آزادی کی تحریر کرانی چاہے تو تم ایسی تحریر انہیں کردیا کرو اگر تم کو ان میں کوئی بھلائی نظر آتی ہو۔ اور اللہ نے جو مال تمہیں دے رکھا ہے اس میں سے انہیں بھی دو، تمہاری جو لونڈیاں پاک دامن رہنا چاہتی ہیں انہیں دنیا کی زندگی کے فائدے کی غرض سے بدکاری پر مجبور نہ کرو اور جو انہیں مجبور کردے تو اللہ تعالیٰ ان پر جبر کے بعد بخشش دینے واﻻ اور مہربانی کرنے واﻻ ہے'),
(2825, 24, 34, 'ہم نے تمہاری طرف کھلی اور روشن آیتیں اتار دی ہیں اور ان لوگوں کی کہاوتیں جو تم سے پہلے گزرچکے ہیں اور پرہیزگاروں کے لئے نصیحت'),
(2826, 24, 35, 'اللہ نور ہے آسمانوں اور زمین کا، اس کے نور کی مثال مثل ایک طاق کے ہے جس میں چراغ ہو اور چراغ شیشہ کی قندیل میں ہو اور شیشہ مثل چمکتے ہوئے روشن ستارے کے ہو وه چراغ ایک بابرکت درخت زیتون کے تیل سے جلایا جاتا ہو جو درخت نہ مشرقی ہے نہ مغربی خود وه تیل قریب ہے کہ آپ ہی روشنی دینے لگے اگرچہ اسے آگ نہ بھی چھوئے، نور پر نور ہے، اللہ تعالیٰ اپنے نور کی طرف رہنمائی کرتا ہے جسے چاہے، لوگوں (کے سمجھانے) کو یہ مثالیں اللہ تعالیٰ بیان فرما رہا ہے، اور اللہ تعالیٰ ہر چیز کے حال سے بخوبی واقف ہے'),
(2827, 24, 36, 'ان گھروں میں جن کے بلند کرنے، اور جن میں اپنے نام کی یاد کا اللہ تعالیٰ نے حکم دیا ہے وہاں صبح وشام اللہ تعالیٰ کی تسبیح بیان کرتے ہیں'),
(2828, 24, 37, 'ایسے لوگ جنہیں تجارت اور خرید وفروخت اللہ کے ذکر سے اور نماز کے قائم کرنے اور زکوٰة ادا کرنے سے غافل نہیں کرتی اس دن سے ڈرتے ہیں جس دن بہت سے دل اور بہت سی آنکھیں الٹ پلٹ ہوجائیں گی'),
(2829, 24, 38, 'اس ارادے سے کہ اللہ انہیں ان کے اعمال کا بہترین بدلے دے بلکہ اپنے فضل سے اور کچھ زیادتی عطا فرمائے۔ اللہ تعالیٰ جسے چاہے بےشمار روزیاں دیتا ہے'),
(2830, 24, 39, 'اور کافروں کے اعمال مثل اس چمکتی ہوئی ریت کے ہیں جو چٹیل میدان میں ہو جسے پیاسا شخص دور سے پانی سمجھتا ہے لیکن جب اس کے پاس پہنچتا ہے تو اسے کچھ بھی نہیں پاتا، ہاں اللہ کو اپنے پاس پاتا ہے جو اس کا حساب پورا پورا چکا دیتا ہے۔ اللہ بہت جلد حساب کردینے واﻻ ہے'),
(2831, 24, 40, 'یا مثل ان اندھیروں کے ہے جو نہایت گہرے سمندر کی تہہ میں ہوں جسے اوپر تلے کی موجوں نے ڈھانﭗ رکھا ہو، پھر اوپر سے بادل چھائے ہوئے ہوں۔ الغرض اندھیریاں ہیں جو اوپر تلے پےدرپے ہیں۔ جب اپنا ہاتھ نکالے تو اسے بھی قریب ہے کہ نہ دیکھ سکے، اور (بات یہ ہے کہ) جسے اللہ تعالیٰ ہی نور نہ دے اس کے پاس کوئی روشنی نہیں ہوتی'),
(2832, 24, 41, 'کیا آپ نے نہیں دیکھا کہ آسمانوں اور زمین کی کل مخلوق اور پر پھیلائے اڑنے والے کل پرند اللہ کی تسبیح میں مشغول ہیں۔ ہر ایک کی نماز اور تسبیح اسے معلوم ہے، لوگ جو کچھ کریں اس سے اللہ بخوبی واقف ہے'),
(2833, 24, 42, 'زمین و آسمان کی بادشاہت اللہ ہی کی ہے اور اللہ تعالیٰ ہی کی طرف لوٹنا ہے'),
(2834, 24, 43, 'کیا آپ نے نہیں دیکھا کہ اللہ تعالیٰ بادلوں کو چلاتا ہے، پھر انہیں ملاتا ہے پھر انہیں تہہ بہ تہہ کر دیتا ہے، پھر آپ دیکھتے ہیں کہ ان کے درمیان میں سے مینہ برستا ہے۔ وہی آسمان کی جانب سے اولوں کے پہاڑ میں سے اولے برساتا ہے، پھر جنہیں چاہے ان کے پاس انہیں برسائے اور جن سے چاہے ان سے انہیں ہٹا دے۔ بادل ہی سے نکلنے والی بجلی کی چمک ایسی ہوتی ہے کہ گویا اب آنکھوں کی روشنی لے چلی'),
(2835, 24, 44, 'اللہ تعالیٰ ہی دن اور رات کو ردوبدل کرتا رہتا ہے آنکھوں والوں کے لیے تو اس میں یقیناً بڑی بڑی عبرتیں ہیں'),
(2836, 24, 45, 'تمام کے تمام چلنے پھرنے والے جانداروں کو اللہ تعالیٰ ہی نے پانی سے پیدا کیا ہےان میں سے بعض تو اپنے پیٹ کے بل چلتے ہیں، بعض دو پاؤں پر چلتے ہیں۔ بعض چار پاؤں پر چلتے ہیں، اللہ تعالیٰ جو چاہتا ہے پیدا کرتا ہے۔ بےشک اللہ تعالیٰ ہر چیز پر قادر ہے'),
(2837, 24, 46, 'بلاشک و شبہ ہم نے روشن اور واضح آیتیں اتار دی ہیں اللہ تعالیٰ جسے چاہے سیدھی راه دکھا دیتا ہے'),
(2838, 24, 47, 'اور کہتے ہیں کہ ہم اللہ تعالیٰ اور رسول پر ایمان ﻻئے اور فرماں بردار ہوئے، پھر ان میں سے ایک فرقہ اس کے بعد بھی پھر جاتا ہے۔ یہ ایمان والے ہیں (ہی) نہیں'),
(2839, 24, 48, 'جب یہ اس بات کی طرف بلائے جاتے ہیں کہ اللہ اور اس کا رسول ان کے جھگڑے چکا دے تو بھی ان کی ایک جماعت منھ موڑنے والی بن جاتی ہے'),
(2840, 24, 49, 'ہاں اگر ان ہی کو حق پہنچتا ہو تو مطیع وفرماں بردار ہو کر اس کی طرف چلے آتے ہیں'),
(2841, 24, 50, 'کیا ان کے دلوں میں بیماری ہے؟ یا یہ شک وشبہ میں پڑے ہوئے ہیں؟ یا انہیں اس بات کا ڈر ہے کہ اللہ تعالیٰ اور اس کا رسول ان کی حق تلفی نہ کریں؟ بات یہ ہے کہ یہ لوگ خود ہی بڑے ﻇالم ہیں'),
(2842, 24, 51, 'ایمان والوں کا قول تو یہ ہے کہ جب انہیں اس لئے بلایا جاتا ہے کہ اللہ اور اس کا رسول ان میں فیصلہ کردے تو وه کہتے ہیں کہ ہم نے سنا اور مان لیا۔ یہی لوگ کامیاب ہونے والے ہیں'),
(2843, 24, 52, 'جو بھی اللہ تعالیٰ کی، اس کے رسول کی فرماں برداری کریں، خوف الٰہی رکھیں اور اس کے عذابوں سے ڈرتے رہیں، وہی نجات پانے والے ہیں'),
(2844, 24, 53, 'بڑی پختگی کے ساتھ اللہ تعالیٰ کی قسمیں کھا کھا کر کہتے ہیں کہ آپ کا حکم ہوتے ہی نکل کھڑے ہوں گے۔ کہہ دیجیئے کہ بس قسمیں نہ کھاؤ (تمہاری) اطاعت (کی حقیقت) معلوم ہے۔ جو کچھ تم کر رہے ہو اللہ تعالیٰ اس سے باخبر ہے'),
(2845, 24, 54, 'کہہ دیجیئے کہ اللہ تعالیٰ کا حکم مانو، رسول اللہ کی اطاعت کرو، پھر بھی اگر تم نے روگردانی کی تو رسول کے ذمے تو صرف وہی ہے جو اس پر ﻻزم کردیا گیا ہے اور تم پر اس کی جوابدہی ہے جو تم پر رکھا گیا ہے ہدایت تو تمہیں اسی وقت ملے گی جب رسول کی ماتحتی کرو۔ سنو رسول کے ذمے تو صرف صاف طور پر پہنچا دینا ہے'),
(2846, 24, 55, 'تم میں سے ان لوگوں سے جو ایمان ﻻئے ہیں اور نیک اعمال کئے ہیں اللہ تعالیٰ وعده فرما چکا ہے کہ انہیں ضرور زمین میں خلیفہ بنائے گا جیسے کہ ان لوگوں کو خلیفہ بنایا تھا جو ان سے پہلے تھے اور یقیناً ان کے لئے ان کے اس دین کو مضبوطی کے ساتھ محکم کرکے جما دے گا جسے ان کے لئے وه پسند فرما چکا ہے اور ان کے اس خوف وخطر کو وه امن وامان سے بدل دے گا، وه میری عبادت کریں گے میرے ساتھ کسی کو بھی شریک نہ ٹھہرائیں گے۔ اس کے بعد بھی جو لوگ ناشکری اور کفر کریں وه یقیناً فاسق ہیں'),
(2847, 24, 56, 'نماز کی پابندی کرو، زکوٰة ادا کرو اور اللہ تعالیٰ کے رسول کی فرمانبرداری میں لگے رہو تاکہ تم پر رحم کیا جائے'),
(2848, 24, 57, 'یہ خیال آپ کبھی بھی نہ کرنا کہ منکر لوگ زمین میں (ادھر ادھر بھاگ کر) ہمیں ہرا دینے والے ہیں، ان کا اصلی ٹھکانا تو جہنم ہے جو یقیناً بہت ہی برا ٹھکانا ہے'),
(2849, 24, 58, 'ایمان والو! تم سے تمہاری ملکیت کے غلاموں کو اور انہیں بھی جو تم میں سے بلوغت کو نہ پہنچے ہوں (اپنے آنے کی) تین وقتوں میں اجازت حاصل کرنی ضروری ہے۔ نماز فجر سے پہلے اور ﻇہر کے وقت جب کہ تم اپنے کپڑے اتار رکھتے ہو اور عشا کی نماز کے بعد، یہ تینوں وقت تمہاری (خلوت) اور پرده کے ہیں۔ ان وقتوں کے ماسوا نہ تو تم پر کوئی گناه ہے نہ ان پر۔ تم سب آپس میں ایک دوسرے کے پاس بکثرت آنے جانے والے ہو (ہی)، اللہ اسی طرح کھول کھول کر اپنے احکام تم سے بیان فرما رہا ہے۔ اللہ تعالیٰ پورے علم اور کامل حکمت واﻻ ہے'),
(2850, 24, 59, 'اور تمہارے بچے (بھی) جب بلوغت کو پہنچ جائیں تو جس طرح ان کے اگلے لوگ اجازت مانگتے ہیں انہیں بھی اجازت مانگ کر آنا چاہیئے، اللہ تعالیٰ تم سے اسی طرح اپنی آیتیں بیان فرماتا ہے۔ اللہ تعالیٰ ہی علم وحکمت واﻻ ہے'),
(2851, 24, 60, 'بڑی بوڑھی عورتیں جنہیں نکاح کی امید (اور خواہش ہی) نہ رہی ہو وه اگر اپنے کپڑے اتار رکھیں تو ان پر کوئی گناه نہیں بشرطیکہ وه اپنا بناؤ سنگھار ﻇاہر کرنے والیاں نہ ہوں، تاہم اگر ان سے بھی احتیاط رکھیں تو ان کے لئے بہت افضل ہے، اور اللہ تعالیٰ سنتا جانتا ہے'),
(2852, 24, 61, 'اندھے پر، لنگڑے پر، بیمار پر اور خود تم پر (مطلقاً) کوئی حرج نہیں کہ تم اپنے گھروں سے کھالو یا اپنے باپوں کے گھروں سے یا اپنی ماؤں کے گھروں سے یا اپنے بھائیوں کے گھروں سے یا اپنی بہنوں کے گھروں سے یا اپنے چچاؤں کے گھروں سے یا اپنی پھوپھیوں کے گھروں سے یا اپنے ماموؤں کے گھروں سے یا اپنی خاﻻؤں کے گھروں سے یا ان گھروں سے جن کی کنجیوں کے تم مالک ہو یا اپنے دوستوں کے گھروں سے۔ تم پر اس میں بھی کوئی گناه نہیں کہ تم سب ساتھ بیٹھ کر کھانا کھاؤ یا الگ الگ پس جب تم گھروں میں جانے لگو تو اپنے گھر والوں کو سلام کرلیا کرو دعائے خیر ہے جو بابرکت اور پاکیزه ہے اللہ تعالیٰ کی طرف سے نازل شده، یوں ہی اللہ تعالیٰ کھول کھول کر تم سے اپنے احکام بیان فرما رہا ہے تاکہ تم سمجھ لو'),
(2853, 24, 62, 'باایمان لوگ تو وہی ہیں جو اللہ تعالیٰ پر اور اس کے رسول پر یقین رکھتے ہیں اور جب ایسے معاملہ میں جس میں لوگوں کے جمع ہونے کی ضرورت ہوتی ہے نبی کے ساتھ ہوتے ہیں تو جب تک آپ سے اجازت نہ لیں کہیں نہیں جاتے۔ جو لوگ ایسے موقع پر آپ سے اجازت لے لیتے ہیں حقیقت میں یہی ہیں وه جو اللہ تعالیٰ پر اور اس کے رسول پر ایمان ﻻچکے ہیں۔ پس جب ایسے لوگ آپ سے اپنے کسی کام کے لئے اجازت طلب کریں تو آپ ان میں سے جسے چاہیں اجازت دے دیں اور ان کے لئے اللہ تعالیٰ سے بخشش کی دعا مانگیں، بےشک اللہ بخشنے واﻻ مہربان ہے'),
(2854, 24, 63, 'تم اللہ تعالیٰ کے نبی کے بلانے کو ایسا بلاوا نہ کرلو جیسا کہ آپس میں ایک دوسرے کو ہوتا ہے۔ تم میں سے انہیں اللہ خوب جانتا ہے جو نظر بچا کر چپکے سے سرک جاتے ہیں۔ سنو جو لوگ حکم رسول کی مخالفت کرتے ہیں انہیں ڈرتے رہنا چاہیئے کہ کہیں ان پر کوئی زبردست آفت نہ آ پڑے یا انہیں درد ناک عذاب نہ پہنچے'),
(2855, 24, 64, 'آگاه ہو جاؤ کہ آسمان وزمین میں جو کچھ ہے سب اللہ تعالیٰ ہی کا ہے۔ جس روش پر تم ہو وه اسے بخوبی جانتا ہے، اور جس دن یہ سب اس کی طرف لوٹائے جائیں گے اس دن ان کو ان کے کئے سے وه خبردار کر دے گا۔ اللہ تعالیٰ سب کچھ جاننے واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2856, 25, 1, 'بہت بابرکت ہے وه اللہ تعالیٰ جس نے اپنے بندے پر فرقان اتارا تاکہ وه تمام لوگوں کے لئے آگاه کرنے واﻻ بن جائے'),
(2857, 25, 2, 'اسی اللہ کی سلطنت ہے آسمانوں اور زمین کی اور وه کوئی اوﻻد نہیں رکھتا، نہ اس کی سلطنت میں کوئی اس کا ساجھی ہے اور ہر چیز کو اس نے پیدا کرکے ایک مناسب اندازه ٹھہرا دیا ہے'),
(2858, 25, 3, 'ان لوگوں نے اللہ کے سوا جنہیں اپنے معبود ٹھہرا رکھے ہیں وه کسی چیز کو پیدا نہیں کرسکتے بلکہ وه خود پیدا کئے جاتے ہیں، یہ تو اپنی جان کے نقصان نفع کا بھی اختیارنہیں رکھتے اور نہ موت وحیات کے اور نہ دوباره جی اٹھنے کے وه مالک ہیں'),
(2859, 25, 4, 'اور کافروں نے کہا یہ تو بس خود اسی کا گھڑا گھڑایا جھوٹ ہے جس پر اور لوگوں نے بھی اس کی مدد کی ہے، دراصل یہ کافر بڑے ہی ﻇلم اور سرتاسر جھوٹ کے مرتکب ہوئے ہیں'),
(2860, 25, 5, 'اور یہ بھی کہا کہ یہ تو اگلوں کے افسانے ہیں جو اس نے لکھا رکھے ہیں بس وہی صبح وشام اس کے سامنے پڑھے جاتے ہیں'),
(2861, 25, 6, 'کہہ دیجیئے کہ اسے تو اس اللہ نے اتارا ہے جو آسمان وزمین کی تمام پوشیده باتوں کو جانتا ہے۔ بےشک وه بڑا ہی بخشنے واﻻ مہربان ہے'),
(2862, 25, 7, 'اور انہوں نے کہا کہ یہ کیسا رسول ہے؟ کہ کھانا کھاتا ہے اور بازاروں میں چلتا پھرتا ہے، اس کے پاس کوئی فرشتہ کیوں نہیں بھیجا جاتا؟ کہ وه بھی اس کے ساتھ ہو کر ڈرانے واﻻ بن جاتا'),
(2863, 25, 8, 'یا اس کے پاس کوئی خزانہ ہی ڈال دیا جاتا یا اس کا کوئی باغ ہی ہوتا جس میں سے یہ کھاتا۔ اور ان ﻇالموں نے کہا کہ تم ایسے آدمی کے پیچھے ہو لئے ہو جس پر جادو کر دیا گیا ہے'),
(2864, 25, 9, 'خیال تو کیجیئے! کہ یہ لوگ آپ کی نسبت کیسی کیسی باتیں بناتے ہیں۔ پس جس سے وه خود ہی بہک رہے ہیں اور کسی طرح راه پر نہیں آسکتے'),
(2865, 25, 10, 'اللہ تعالیٰ تو ایسا بابرکت ہے کہ اگر چاہے تو آپ کو بہت سے ایسے باغات عنایت فرما دے جو ان کے کہے ہوئے باغ سے بہت ہی بہتر ہوں جن کے نیچے نہریں لہریں لے رہی ہوں اور آپ کو بہت سے (پختہ) محل بھی دے دے'),
(2866, 25, 11, 'بات یہ ہے کہ یہ لوگ قیامت کو جھوٹ سمجھتے ہیں اور قیامت کے جھٹلانے والوں کے لئے ہم نے بھڑکتی ہوئی آگ تیار کر رکھی ہے'),
(2867, 25, 12, 'جب وه انہیں دور سے دیکھے گی تو یہ اس کا غصے سے بپھرنا اور دھاڑنا سنیں گے'),
(2868, 25, 13, 'اور جب یہ جہنم کی کسی تنگ جگہ میں مشکیں کس کر پھینک دیئے جائیں گے تو وہاں اپنے لئے موت ہی موت پکاریں گے'),
(2869, 25, 14, '(ان سے کہا جائے گا) آج ایک ہی موت کو نہ پکارو بلکہ بہت سی اموات کو پکارو'),
(2870, 25, 15, 'آپ کہہ دیجیئے کہ کیا یہ بہتر ہے یا وه ہمیشگی والی جنت جس کا وعده پرہیزگاروں سے کیا گیا ہے، جو ان کا بدلہ ہے اور ان کے لوٹنے کی اصلی جگہ ہے'),
(2871, 25, 16, 'وه جو چاہیں گے ان کے لئے وہاں موجود ہوگا، ہمیشہ رہنے والے۔ یہ تو آپ کے رب کے ذمے وعده ہے جو قابل طلب ہے'),
(2872, 25, 17, 'اور جس دن اللہ تعالیٰ انہیں اور سوائے اللہ کے جنہیں یہ پوجتے رہے، انہیں جمع کرکے پوچھے گا کہ کیا میرے ان بندوں کو تم نے گمراه کیا یا یہ خود ہی راه سے گم ہوگئے'),
(2873, 25, 18, 'وه جواب دیں گے کہ تو پاک ذات ہے خود ہمیں ہی یہ زیبا نہ تھا کہ تیرے سوا اوروں کو اپنا کارساز بناتے بات یہ ہے کہ تو نے انہیں اور ان کے باپ دادوں کو آسودگیاں عطا فرمائیں یہاں تک کہ وه نصیحت بھلا بیٹھے، یہ لوگ تھے ہی ہلاک ہونے والے'),
(2874, 25, 19, 'تو انہوں نے تو تمہیں تمہاری تمام باتوں میں جھٹلایا، اب نہ تو تم میں عذابوں کے پھیرنے کی طاقت ہے، نہ مدد کرنے کی، تم میں سے جس جس نے ﻇلم کیا ہے ہم اسے بڑا عذاب چکھائیں گے'),
(2875, 25, 20, 'ہم نے آپ سے پہلے جتنے رسول بھیجے سب کے سب کھانا بھی کھاتے تھے اور بازاروں میں بھی چلتے پھرتے تھے اور ہم نے تم میں سے ہر ایک کو دوسرے کی آزمائش کا ذریعہ بنا دیا۔ کیا تم صبر کرو گے؟ تیرا رب سب کچھ دیکھنے واﻻ ہے'),
(2876, 25, 21, 'اور جنہیں ہماری ملاقات کی توقع نہیں انہوں نے کہا کہ ہم پر فرشتے کیوں نہیں اتارے جاتے؟ یا ہم اپنی آنکھوں سے اپنے رب کو دیکھ لیتے؟ ان لوگوں نے اپنے آپ کو ہی بہت بڑا سمجھ رکھا ہے اور سخت سرکشی کرلی ہے'),
(2877, 25, 22, 'جس دن یہ فرشتوں کو دیکھ لیں گے اس دن ان گناه گاروں کو کوئی خوشی نہ ہوگی اور کہیں گے یہ محروم ہی محروم کیے گئے'),
(2878, 25, 23, 'اور انہوں نے جو جو اعمال کیے تھے ہم نےان کی طرف بڑھ کر انہیں پراگنده ذروں کی طرح کردیا'),
(2879, 25, 24, 'البتہ اس دن جنتیوں کا ٹھکانا بہتر ہوگا اور خواب گاه بھی عمده ہوگی'),
(2880, 25, 25, 'اور جس دن آسمان بادل سمیت پھٹ جائے گا اور فرشتے لگاتار اتارے جائیں گے'),
(2881, 25, 26, 'اس دن صحیح طور پر ملک صرف رحمٰن کا ہی ہوگا اور یہ دن کافروں پر بڑا بھاری ہوگا'),
(2882, 25, 27, 'اور اس دن ﻇالم شخص اپنے ہاتھوں کو چبا چبا کر کہے گا ہائے کاش کہ میں نے رسول (صلی اللہ علیہ وسلم) کی راه اختیار کی ہوتی'),
(2883, 25, 28, 'ہائے افسوس کاش کہ میں نے فلاں کو دوست نہ بنایا ہوتا'),
(2884, 25, 29, 'اس نے تو مجھے اس کے بعد گمراه کردیا کہ نصیحت میرے پاس آپہنچی تھی اور شیطان تو انسان کو (وقت پر) دغا دینے واﻻ ہے'),
(2885, 25, 30, 'اور رسول کہے گا کہ اے میرے پروردگار! بےشک میری امت نے اس قرآن کو چھوڑ رکھا تھا'),
(2886, 25, 31, 'اور اسی طرح ہم نے ہر نبی کے دشمن بعض گناه گاروں کو بنادیا ہے اور تیرا رب ہی ہدایت کرنے واﻻ اور مدد کرنے واﻻ کافی ہے'),
(2887, 25, 32, 'اور کافروں نے کہا کہ اس پر قرآن سارا کا سارا ایک ساتھ ہی کیوں نہ اتارا گیا اسی طرح ہم نے (تھوڑا تھوڑا کرکے) اتارا تاکہ اس سے ہم آپ کا دل قوی رکھیں، ہم نے اسے ٹھہر ٹھہر کر ہی پڑھ سنایا ہے'),
(2888, 25, 33, 'یہ آپ کے پاس جو کوئی مثال ﻻئیں گے ہم اس کا سچا جواب اور عمده توجیہ آپ کو بتادیں گے'),
(2889, 25, 34, 'جو لوگ اپنے منھ کے بل جہنم کی طرف جمع کیے جائیں گے۔ وہی بدتر مکان والے اور گمراه تر راستے والے ہیں'),
(2890, 25, 35, 'اور بلاشبہ ہم نے موسیٰ کو کتاب دی اور ان کے ہمراه ان کے بھائی ہارون کو ان کا وزیر بنادیا'),
(2891, 25, 36, 'اور کہہ دیا کہ تم دونوں ان لوگوں کی طرف جاؤ جو ہماری آیتوں کو جھٹلا رہے ہیں۔ پھر ہم نے انہیں بالکل ہی پامال کردیا'),
(2892, 25, 37, 'اور قوم نوح نے بھی جب رسولوں کو جھوٹا کہا تو ہم نے انہیں غرق کردیا اور لوگوں کے لیے انہیں نشان عبرت بنا دیا۔ اور ہم نے ﻇالموں کے لیے دردناک عذاب مہیا کر رکھا ہے'),
(2893, 25, 38, 'اور عادیوں اور ﺛمودیوں اور کنوئیں والوں کو اور ان کے درمیان کی بہت سی امتوں کو (ہلاک کردیا)'),
(2894, 25, 39, 'اور ہم نے ان کے سامنے مثالیں بیان کیں پھر ہر ایک کو بالکل ہی تباه و برباد کردیا'),
(2895, 25, 40, 'یہ لوگ اس بستی کے پاس سے بھی آتے جاتے ہیں جن پر بری طرح کی بارش برسائی گئی۔ کیا یہ پھر بھی اسے دیکھتے نہیں؟ حقیقت یہ ہے کہ انہیں مرکر جی اٹھنے کی امید ہی نہیں'),
(2896, 25, 41, 'اور تمہیں جب کبھی دیکھتے ہیں تو تم سے مسخرا پن کرنے لگتے ہیں۔ کہ کیا یہی وه شخص ہیں جنہیں اللہ تعالیٰ نے رسول بنا کر بھیجا ہے'),
(2897, 25, 42, '(وه تو کہیئے) کہ ہم اس پر جمے رہے ورنہ انہوں نے تو ہمیں ہمارے معبودوں سے بہکادینے میں کوئی کسر نہیں چھوڑی تھی۔ اور یہ جب عذابوں کو دیکھیں گے تو انہیں صاف معلوم ہوجائے گا کہ پوری طرح راه سے بھٹکا ہوا کون تھا؟'),
(2898, 25, 43, 'کیا آپ نے اسے بھی دیکھا جو اپنی خواہش نفس کو اپنا معبود بنائے ہوئے ہے کیا آپ اس کے ذمہدار ہوسکتے ہیں؟'),
(2899, 25, 44, 'کیا آپ اسی خیال میں ہیں کہ ان میں سے اکثر سنتے یا سمجھتے ہیں۔ وه تو نرے چوپایوں جیسے ہیں بلکہ ان سے بھی زیاده بھٹکے ہوئے'),
(2900, 25, 45, 'کیا آپ نے نہیں دیکھا کہ آپ کے رب نے سائے کو کس طرح پھیلا دیا ہے؟ اگر چاہتا تو اسے ٹھہرا ہوا ہی کر دیتا۔ پھر ہم نے آفتاب کو اس پر دلیل بنایا'),
(2901, 25, 46, 'پھر ہم نے اسے آہستہ آہستہ اپنی طرف کھینچ لیا'),
(2902, 25, 47, 'اور وہی ہے جس نے رات کو تمہارے لیے پرده بنایا۔ اور نیند کو راحت بنائی۔ اور دن کو اٹھ کھڑے ہونے کا وقت'),
(2903, 25, 48, 'اور وہی ہے جو باران رحمت سے پہلے خوش خبری دینے والی ہواؤں کو بھیجتا ہے اور ہم آسمان سے پاک پانی برساتے ہیں'),
(2904, 25, 49, 'تاکہ اس کے ذریعے سے مرده شہر کو زنده کردیں اوراسے ہم اپنی مخلوقات میں سے بہت سے چوپایوں اور انسانوں کو پلاتے ہیں'),
(2905, 25, 50, 'اور بے شک ہم نے اسےان کے درمیان طرح طرح سے بیان کیا تاکہ وه نصیحت حاصل کریں، مگر پھر بھی اکثر لوگوں نے سوائے ناشکری کے مانا نہیں'),
(2906, 25, 51, 'اگر ہم چاہتے تو ہر ہر بستی میں ایک ایک ڈرانے واﻻ بھیج دیتے'),
(2907, 25, 52, 'پس آپ کافروں کا کہنا نہ مانیں اور قرآن کے ذریعے ان سے پوری طاقت سے بڑا جہاد کریں'),
(2908, 25, 53, 'اور وہی ہے جس نے دو سمندر آپس میں ملا رکھے ہیں، یہ ہے میٹھا اور مزیدار اور یہ ہے کھاری کڑوا، اور ان دونوں کے درمیان ایک حجاب اور مضبوط اوٹ کردی'),
(2909, 25, 54, 'وه ہے جس نے پانی سے انسان کو پیدا کیا، پھر اسے نسب واﻻ اور سسرالی رشتوں واﻻ کردیا۔ بلاشبہ آپ کا پروردگار (ہر چیز پر) قادر ہے'),
(2910, 25, 55, 'یہ اللہ کو چھوڑ کر ان کی عبادت کرتے ہیں جو نہ تو انہیں کوئی نفع دے سکیں نہ کوئی نقصان پہنچاسکیں، اور کافر تو ہے ہی اپنے رب کے خلاف (شیطان کی) مدد کرنے واﻻ'),
(2911, 25, 56, 'ہم نے تو آپ کو خوشخبری اور ڈر سنانے واﻻ (نبی) بنا کر بھیجا ہے'),
(2912, 25, 57, 'کہہ دیجئے کہ میں قرآن کے پہنچانے پر تم سے کوئی بدلہ نہیں چاہتا مگر جو شخص اپنے رب کی طرف راه پکڑنا چاہے'),
(2913, 25, 58, 'اس ہمیشہ زنده رہنے والے اللہ تعالیٰ پر توکل کریں جسے کبھی موت نہیں اور اس کی تعریف کے ساتھ پاکیزگی بیان کرتے رہیں، وه اپنے بندوں کے گناہوں سے کافی خبردار ہے'),
(2914, 25, 59, 'وہی ہے جس نے آسمانوں اور زمین اوران کے درمیان کی سب چیزوں کو چھ دن میں پیدا کردیا ہے، پھر عرش پر مستوی ہوا، وه رحمٰن ہے، آپ اس کے بارے میں کسی خبردار سے پوچھ لیں'),
(2915, 25, 60, 'ان سے جب بھی کہا جاتا ہے کہ رحمٰن کو سجده کرو تو جواب دیتے ہیں رحمٰن ہے کیا؟ کیا ہم اسے سجده کریں جس کا تو ہمیں حکم دے رہا ہے اور اس (تبلیﻎ) نےان کی نفرت میں مزید اضافہ کردیا'),
(2916, 25, 61, 'بابرکت ہے وه جس نے آسمان میں برج بنائے اور اس میں آفتاب بنایا اور منور مہتاب بھی'),
(2917, 25, 62, 'اور اسی نے رات اور دن کو ایک دوسرے کے پیچھے آنے جانے واﻻ بنایا۔ اس شخص کی نصیحت کے لیے جو نصیحت حاصل کرنے یا شکر گزاری کرنے کا اراده رکھتا ہو'),
(2918, 25, 63, 'رحمٰن کے (سچے) بندے وه ہیں جو زمین پر فروتنی کے ساتھ چلتے ہیں اور جب بے علم لوگ ان سے باتیں کرنے لگتے ہیں تو وه کہہ دیتے ہیں کہ سلام ہے'),
(2919, 25, 64, 'اور جو اپنے رب کے سامنے سجدے اور قیام کرتے ہوئے راتیں گزار دیتے ہیں'),
(2920, 25, 65, 'اور جو یہ دعا کرتے ہیں کہ اے ہمارے پروردگار! ہم سے دوزخ کا عذاب پرے ہی پرے رکھ، کیونکہ اس کا عذاب چمٹ جانے واﻻ ہے'),
(2921, 25, 66, 'بے شک وه ٹھہرنے اور رہنے کے لحاظ سے بدترین جگہ ہے'),
(2922, 25, 67, 'اور جو خرچ کرتے وقت بھی نہ تو اسراف کرتے ہیں نہ بخیلی، بلکہ ان دونوں کے درمیان معتدل طریقے پر خرچ کرتے ہیں'),
(2923, 25, 68, 'اور اللہ کے ساتھ کسی دوسرے معبود کو نہیں پکارتے اور کسی ایسے شخص کو جسے قتل کرنا اللہ تعالیٰ نے منع کردیا ہو وه بجز حق کے قتل نہیں کرتے، نہ وه زنا کے مرتکب ہوتے ہیں اور جو کوئی یہ کام کرے وه اپنے اوپر سخت وبال ﻻئے گا'),
(2924, 25, 69, 'اسے قیامت کے دن دوہرا عذاب کیا جائے گا اور وه ذلت و خواری کے ساتھ ہمیشہ اسی میں رہے گا'),
(2925, 25, 70, 'سوائے ان لوگوں کے جو توبہ کریں اور ایمان ﻻئیں اور نیک کام کریں، ایسے لوگوں کے گناہوں کو اللہ تعالیٰ نیکیوں سے بدل دیتا ہے، اللہ بخشنے واﻻ مہربانی کرنے واﻻ ہے'),
(2926, 25, 71, 'اور جو شخص توبہ کرے اور نیک عمل کرے وه تو (حقیقتاً) اللہ تعالیٰ کی طرف سچا رجوع کرتا ہے'),
(2927, 25, 72, 'اور جو لوگ جھوٹی گواہی نہیں دیتے اور جب کسی لغو چیز پر ان کا گزر ہوتا ہے تو شرافت سے گزر جاتے ہیں'),
(2928, 25, 73, 'اور جب انہیں ان کے رب کے کلام کی آیتیں سنائی جاتی ہیں تو وه اندھے بہرے ہو کر ان پر نہیں گرتے۔'),
(2929, 25, 74, 'اور یہ دعا کرتے ہیں کہ اے ہمارے پروردگار! تو ہمیں ہماری بیویوں اور اوﻻد سے آنکھوں کی ٹھنڈک عطا فرما اور ہمیں پرہیزگاروں کا پیشوا بنا'),
(2930, 25, 75, 'یہی وه لوگ ہیں جنہیں ان کے صبر کے بدلے جنت کے بلند و باﻻخانے دیئے جائیں گے جہاں انہیں دعا سلام پہنچایا جائے گا'),
(2931, 25, 76, 'اس میں یہ ہمیشہ رہیں گے، وه بہت ہی اچھی جگہ اور عمده مقام ہے'),
(2932, 25, 77, 'کہہ دیجئے! اگر تمہاری دعا التجا (پکارنا) نہ ہوتی تو میرا رب تمہاری مطلق پروا نہ کرتا، تم تو جھٹلا چکے اب عنقریب اس کی سزا تمہیں چمٹ جانے والی ہوگی');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(2933, 26, 1, 'طٰسم'),
(2934, 26, 2, 'یہ آیتیں روشن کتاب کی ہیں'),
(2935, 26, 3, 'ان کے ایمان نہ ﻻنے پر شاید آپ تو اپنی جان کھودیں گے'),
(2936, 26, 4, 'اگر ہم چاہتے تو ان پر آسمان سے کوئی ایسی نشانی اتارتے کہ جس کے سامنے ان کی گردنیں خم ہو جاتیں'),
(2937, 26, 5, 'اور ان کے پاس رحمٰن کی طرف سے جو بھی نئی نصیحت آئی یہ اس سے روگردانی کرنے والے بن گئے'),
(2938, 26, 6, 'ان لوگوں نے جھٹلایا ہے اب ان کے پاس جلدی سے اس کی خبریں آجائیں گی جس کے ساتھ وه مسخرا پن کر رہے ہیں'),
(2939, 26, 7, 'کیا انہوں نے زمین پر نظریں نہیں ڈالیں؟ کہ ہم نے اس میں ہر طرح کے نفیس جوڑے کس قدر اگائے ہیں؟'),
(2940, 26, 8, 'بے شک اس میں یقیناً نشانی ہے اور ان میں کے اکثر لوگ مومن نہیں ہیں'),
(2941, 26, 9, 'اور تیرا رب یقیناً وہی غالب اور مہربان ہے'),
(2942, 26, 10, 'اور جب آپ کے رب نے موسیٰ (علیہ السلام) کو آواز دی کہ تو ﻇالم قوم کے پاس جا'),
(2943, 26, 11, 'قوم فرعون کے پاس، کیا وه پرہیزگاری نہ کریں گے'),
(2944, 26, 12, 'موسیٰ (علیہ السلام) نے کہا میرے پروردگار! مجھے تو خوف ہے کہ کہیں وه مجھے جھٹلا (نہ) دیں'),
(2945, 26, 13, 'اور میرا سینہ تنگ ہو رہا ہے میری زبان چل نہیں رہی پس تو ہارون کی طرف بھی وحی بھیج'),
(2946, 26, 14, 'اور ان کا مجھ پر میرے ایک قصور کا (دعویٰ) بھی ہے مجھے ڈر ہے کہ کہیں وه مجھے مار نہ ڈالیں'),
(2947, 26, 15, 'جناب باری نے فرمایا! ہرگز ایسا نہ ہوگا، تم دونوں ہماری نشانیاں لے کر جاؤ ہم خود سننے والے تمہارے ساتھ ہیں'),
(2948, 26, 16, 'تم دونوں فرعون کے پاس جاکر کہو کہ بلاشبہ ہم رب العالمین کے بھیجے ہوئے ہیں'),
(2949, 26, 17, 'کہ تو ہمارے ساتھ بنی اسرائیل کو روانہ کردے'),
(2950, 26, 18, 'فرعون نے کہا کہ کیا ہم نے تجھے تیرے بچپن کے زمانہ میں اپنے ہاں نہیں پاﻻ تھا؟ اور تو نے اپنی عمر کے بہت سے سال ہم میں نہیں گزارے؟'),
(2951, 26, 19, 'پھر تو اپنا وه کام کر گیا جو کر گیا اور تو ناشکروں میں ہے'),
(2952, 26, 20, '(حضرت) موسیٰ (علیہ السلام) نے جواب دیا کہ میں نے اس کام کو اس وقت کیا تھا جبکہ میں راه بھولے ہوئے لوگوں میں سے تھا'),
(2953, 26, 21, 'پھر تم سے خوف کھا کر میں تم میں سے بھاگ گیا، پھر مجھے میرے رب نے حکم و علم عطا فرمایا اور مجھے اپنے پیغمبروں میں سے کر دیا'),
(2954, 26, 22, 'مجھ پر تیرا کیا یہی وه احسان ہے؟ جسے تو جتا رہا ہے جبکہ تو نے بنی اسرائیل کو غلام بنا رکھا ہے'),
(2955, 26, 23, 'فرعون نے کہا رب العالمین کیا (چیز) ہے؟'),
(2956, 26, 24, '(حضرت) موسیٰ (علیہ السلام) نے فرمایا وه آسمانوں اور زمین اور ان کے درمیان کی تمام چیزوں کا رب ہے، اگر تم یقین رکھنے والے ہو'),
(2957, 26, 25, 'فرعون نے اپنے اردگرد والوں سے کہا کہ کیا تم سن نہیں رہے؟'),
(2958, 26, 26, '(حضرت) موسیٰ (علیہ السلام) نے فرمایا وه تمہارا اور تمہارے اگلے باپ دادوں کا پروردگار ہے'),
(2959, 26, 27, 'فرعون نے کہا (لوگو!) تمہارا یہ رسول جو تمہاری طرف بھیجا گیا ہے یہ تو یقیناً دیوانہ ہے'),
(2960, 26, 28, 'حضرت موسیٰ علیہ السلام نے فرمایا! وہی مشرق ومغرب کا اور ان کے درمیان کی تمام چیزوں کا رب ہے، اگر تم عقل رکھتے ہو'),
(2961, 26, 29, 'فرعون کہنے لگا سن لے! اگر تو نے میرے سوا کسی اور کو معبود بنایا تو میں تجھے قیدیوں میں ڈال دوں گا'),
(2962, 26, 30, 'موسیٰ (علیہ السلام) نے کہا اگرچہ میں تیرے پاس کوئی کھلی چیز لے آؤں؟'),
(2963, 26, 31, 'فرعون نے کہا اگر تو سچوں میں سے ہے تو اسے پیش کر'),
(2964, 26, 32, 'آپ نے (اسی وقت) اپنی ﻻٹھی ڈال دی جو اچانک کھلم کھلا (زبردست) اﮊدہا بن گئی'),
(2965, 26, 33, 'اور اپنا ہاتھ کھینچ نکالا تو وه بھی اسی وقت ہر دیکھنے والے کو سفید چمکیلا نظر آنے لگا'),
(2966, 26, 34, 'فرعون اپنے آس پاس کے سرداروں سے کہنے لگا بھئی یہ تو کوئی بڑا دانا جادوگر ہے'),
(2967, 26, 35, 'یہ تو چاہتا ہے کہ اپنے جادو کے زور سے تمہیں تمہاری سر زمین سے ہی نکال دے، بتاؤ اب تم کیا حکم دیتے ہو'),
(2968, 26, 36, 'ان سب نے کہا آپ اسے اور اس کے بھائی کو مہلت دیجئے اور تمام شہروں میں ہرکارے بھیج دیجئے'),
(2969, 26, 37, 'جو آپ کے پاس ذی علم جادو گروں کو لے آئیں'),
(2970, 26, 38, 'پھر ایک مقرر دن کے وعدے پر تمام جادوگر جمع کیے گئے'),
(2971, 26, 39, 'اور عام لوگوں سے بھی کہہ دیا گیا کہ تم بھی مجمع میں حاضر ہوجاؤ گے؟'),
(2972, 26, 40, 'تاکہ اگر جادوگر غالب آجائیں تو ہم ان ہی کی پیروی کریں'),
(2973, 26, 41, 'جادوگر آکر فرعون سے کہنے لگے کہ اگر ہم جیت گئے تو ہمیں کچھ انعام بھی ملے گا؟'),
(2974, 26, 42, 'فرعون نے کہا ہاں! (بڑی خوشی سے) بلکہ ایسی صورت میں تم میرے خاص درباری بن جاؤ گے'),
(2975, 26, 43, '(حضرت) موسیٰ (علیہ السلام) نے جادوگروں سے فرمایا جو کچھ تمہیں ڈالنا ہے ڈال دو'),
(2976, 26, 44, 'انہوں نے اپنی رسیاں اور ﻻٹھیاں ڈال دیں اور کہنے لگے عزت فرعون کی قسم! ہم یقیناً غالب ہی رہیں گے'),
(2977, 26, 45, 'اب (حضرت) موسیٰ (علیہ السلام) نے بھی اپنی ﻻٹھی میدان میں ڈال دی جس نے اسی وقت ان کے جھوٹ موٹ کے کرتب کو نگلنا شروع کردیا'),
(2978, 26, 46, 'یہ دیکھتے ہی دیکھتے جادوگر بے اختیار سجدے میں گر گئے'),
(2979, 26, 47, 'اورانہوں نے صاف کہہ دیا کہ ہم تو اللہ رب العالمین پر ایمان ﻻئے'),
(2980, 26, 48, 'یعنی موسیٰ (علیہ السلام) اور ہارون کے رب پر'),
(2981, 26, 49, 'فرعون نے کہا کہ میری اجازت سے پہلے تم اس پر ایمان لے آئے؟ یقیناً یہی تمہارا وه بڑا (سردار) ہے جس نے تم سب کو جادو سکھایا ہے، سو تمہیں ابھی ابھی معلوم ہوجائے گا، قسم ہے میں ابھی تمہارے ہاتھ پاؤں الٹے طور پر کاٹ دوں گا اور تم سب کو سولی پر لٹکا دوں گا'),
(2982, 26, 50, 'انہوں نے کہا کوئی حرج نہیں، ہم تو اپنے رب کی طرف لوٹنے والے ہیں ہی'),
(2983, 26, 51, 'اس بنا پر کہ ہم سب سے پہلےایمان والے بنے ہیں ہمیں امید پڑتی ہے کہ ہمارا رب ہماری سب خطائیں معاف فرما دے گا'),
(2984, 26, 52, 'اور ہم نے موسیٰ کو وحی کی کہ راتوں رات میرے بندوں کو نکال لے چل تم سب پیچھا کیے جاؤ گے'),
(2985, 26, 53, 'فرعون نے شہروں میں ہرکاروں کو بھیج دیا'),
(2986, 26, 54, 'کہ یقیناً یہ گروه بہت ہی کم تعداد میں ہے'),
(2987, 26, 55, 'اور اس پر یہ ہمیں سخت غضب ناک کر رہے ہیں'),
(2988, 26, 56, 'اور یقیناً ہم بڑی جماعت ہیں ان سے چوکنا رہنے والے'),
(2989, 26, 57, 'بالﺂخر ہم نےانہیں باغات سے اور چشموں سے'),
(2990, 26, 58, 'اور خزانوں سے۔ اور اچھے اچھے مقامات سے نکال باہر کیا'),
(2991, 26, 59, 'اسی طرح ہوا اور ہم نےان (تمام) چیزوں کا وارث بنی اسرائیل کو بنا دیا'),
(2992, 26, 60, 'پس فرعونی سورج نکلتے ہی ان کے تعاقب میں نکلے'),
(2993, 26, 61, 'پس جب دونوں نے ایک دوسرے کو دیکھ لیا، تو موسیٰ کے ساتھیوں نے کہا، ہم تو یقیناً پکڑ لیے گئے'),
(2994, 26, 62, 'موسیٰ نے کہا، ہرگز نہیں۔ یقین مانو، میرا رب میرے ساتھ ہے جو ضرور مجھے راه دکھائے گا'),
(2995, 26, 63, 'ہم نے موسیٰ کی طرف وحی بھیجی کہ دریا پر اپنی ﻻٹھی مار، پس اسی وقت دریا پھٹ گیا اور ہر ایک حصہ پانی کا مثل بڑے پہاڑ کے ہوگیا'),
(2996, 26, 64, 'اور ہم نے اسی جگہ دوسروں کو نزدیک ﻻ کھڑا کر دیا'),
(2997, 26, 65, 'اور موسیٰ (علیہ السلام) کو اور اس کے تمام ساتھیوں کو نجات دے دی'),
(2998, 26, 66, 'پھر اور سب دوسروں کو ڈبو دیا'),
(2999, 26, 67, 'یقیناً اس میں بڑی عبرت ہے اور ان میں کےاکثر لوگ ایمان والے نہیں'),
(3000, 26, 68, 'اور بیشک آپ کا رب بڑا ہی غالب اور مہربان ہے؟'),
(3001, 26, 69, 'انہیں ابراہیم (علیہ السلام) کا واقعہ بھی سنادو'),
(3002, 26, 70, 'جبکہ انہوں نے اپنے باپ اور اپنی قوم سے فرمایا کہ تم کس کی عبادت کرتے ہو؟'),
(3003, 26, 71, 'انہوں نے جواب دیا کہ عبادت کرتے ہیں بتوں کی، ہم تو برابر ان کے مجاور بنے بیٹھے ہیں'),
(3004, 26, 72, 'آپ نے فرمایا کہ جب تم انہیں پکارتے ہو تو کیا وه سنتے بھی ہیں؟'),
(3005, 26, 73, 'یا تمہیں نفع نقصان بھی پہنچا سکتے ہیں'),
(3006, 26, 74, 'انہوں نے کہا یہ (ہم کچھ نہیں جانتے) ہم تو اپنے باپ دادوں کو اسی طرح کرتے پایا'),
(3007, 26, 75, 'آپ نے فرمایا کچھ خبر بھی ہے جنہیں تم پوج رہے ہو'),
(3008, 26, 76, 'تم اور تمہارے اگلے باپ دادا، وه سب میرے دشمن ہیں'),
(3009, 26, 77, 'بجز سچے اللہ تعالیٰ کے جو تمام جہان کا پالنہار ہے'),
(3010, 26, 78, 'جس نے مجھے پیدا کیا ہے اور وہی میری رہبری فرماتا ہے'),
(3011, 26, 79, 'وہی ہے جو مجھے کھلاتا پلاتا ہے'),
(3012, 26, 80, 'اور جب میں بیمار پڑ جاؤں تو مجھے شفا عطا فرماتا ہے'),
(3013, 26, 81, 'اور وہی مجھے مار ڈالے گا پھر زنده کردے گا'),
(3014, 26, 82, 'اور جس سے امید بندھی ہوئی ہے کہ وه روز جزا میں میرے گناہوں کو بخش دے گا'),
(3015, 26, 83, 'اے میرے رب! مجھے قوت فیصلہ عطا فرما اور مجھے نیک لوگوں میں ملا دے'),
(3016, 26, 84, 'اور میرا ذکر خیر پچھلے لوگوں میں بھی باقی رکھ'),
(3017, 26, 85, 'مجھے نعمتوں والی جنت کے وارﺛوں میں سے بنادے'),
(3018, 26, 86, 'اور میرے باپ کو بخش دے یقیناً وه گمراہوں میں سے تھا'),
(3019, 26, 87, 'اور جس دن کے لوگ دوباره جلائے جائیں مجھے رسوا نہ کر'),
(3020, 26, 88, 'جس دن کہ مال اور اوﻻد کچھ کام نہ آئے گی'),
(3021, 26, 89, 'لیکن فائده واﻻ وہی ہوگا جو اللہ تعالیٰ کے سامنے بے عیب دل لے کر جائے'),
(3022, 26, 90, 'اور پرہیزگاروں کے لیے جنت بالکل نزدیک ﻻدی جائے گی'),
(3023, 26, 91, 'اور گمراه لوگوں کے لیے جہنم ﻇاہر کردی جائے گی'),
(3024, 26, 92, 'اور ان سے پوچھا جائے گا کہ جن کی تم پوجا کرتے رہے وه کہاں ہیں؟'),
(3025, 26, 93, 'جو اللہ تعالیٰ کے سوا تھے، کیاوه تمہاری مدد کرتے ہیں؟ یا کوئی بدلہ لے سکتے ہیں'),
(3026, 26, 94, 'پس وه سب اور کل گمراه لوگ جہنم میں اوندھے منھ ڈال دیے جائیں گے'),
(3027, 26, 95, 'اور ابلیس کے تمام کے تمام لشکر بھی، وہاں'),
(3028, 26, 96, 'آپس میں لڑتے جھگڑتے ہوئے کہیں گے'),
(3029, 26, 97, 'کہ قسم اللہ کی! یقیناً ہم تو کھلی غلطی پر تھے'),
(3030, 26, 98, 'جبکہ تمہیں رب العالمین کے برابر سمجھ بیٹھے تھے'),
(3031, 26, 99, 'اور ہمیں تو سوا ان بدکاروں کے کسی اور نے گمراه نہیں کیا تھا'),
(3032, 26, 100, 'اب تو ہمارا کوئی سفارشی بھی نہیں'),
(3033, 26, 101, 'اور نہ کوئی (سچا) غم خوار دوست'),
(3034, 26, 102, 'اگر کاش کہ ہمیں ایک مرتبہ پھر جانا ملتا تو ہم پکے سچے مومن بن جاتے'),
(3035, 26, 103, 'یہ ماجرا یقیناً ایک زبردست نشانی ہے ان میں سے اکثر لوگ ایمان ﻻنے والے نہیں'),
(3036, 26, 104, 'یقیناً آپ کا پروردگار ہی غالب مہربان ہے'),
(3037, 26, 105, 'قوم نوح نے بھی نبیوں کو جھٹلایا'),
(3038, 26, 106, 'جبکہ ان کے بھائی نوح (علیہ السلام) نے کہا کہ کیا تمہیں اللہ کا خوف نہیں!'),
(3039, 26, 107, 'سنو! میں تمہاری طرف اللہ کا امانتدار رسول ہوں'),
(3040, 26, 108, 'پس تمہیں اللہ سے ڈرنا چاہئے اور میری بات ماننی چاہئے'),
(3041, 26, 109, 'میں تم سے اس پر کوئی اجر نہیں چاہتا، میرا بدلہ تو صرف رب العالمین کے ہاں ہے'),
(3042, 26, 110, 'پس تم اللہ کا خوف رکھو اور میری فرمانبرداری کرو'),
(3043, 26, 111, 'قوم نے جواب دیا کہ ہم تجھ پر ایمان ﻻئیں! تیری تابعداری تو رذیل لوگوں نے کی ہے'),
(3044, 26, 112, 'آپ نے فرمایا! مجھے کیا خبر کہ وه پہلے کیا کرتے رہے؟'),
(3045, 26, 113, 'ان کا حساب تو میرے رب کے ذمہ ہے اگر تمہیں شعور ہو تو'),
(3046, 26, 114, 'میں ایمان والوں کو دھکے دینے واﻻ نہیں'),
(3047, 26, 115, 'میں تو صاف طور پر ڈرا دینے واﻻ ہوں'),
(3048, 26, 116, 'انہوں نے کہاکہ اے نوح! اگر تو باز نہ آیا تو یقیناً تجھے سنگسار کردیا جائے گا'),
(3049, 26, 117, 'آپ نے کہا اے میرے پروردگار! میری قوم نے مجھے جھٹلا دیا'),
(3050, 26, 118, 'پس تو مجھ میں اور ان میں کوئی قطعی فیصلہ کردے اور مجھے اور میرے با ایمان ساتھیوں کو نجات دے'),
(3051, 26, 119, 'چنانچہ ہم نے اسے اور اس کے ساتھیوں کو بھری ہوئی کشتی میں (سوار کراکر) نجات دے دی'),
(3052, 26, 120, 'بعد ازاں باقی کے تمام لوگوں کو ہم نے ڈبو دیا'),
(3053, 26, 121, 'یقیناً اس میں بہت بڑی عبرت ہے۔ ان میں سے اکثر لوگ ایمان ﻻنے والے تھے بھی نہیں'),
(3054, 26, 122, 'اور بیشک آپ کا پروردگار البتہ وہی ہے زبردست رحم کرنے واﻻ'),
(3055, 26, 123, 'عادیوں نے بھی رسولوں کو جھٹلایا'),
(3056, 26, 124, 'جبکہ ان سے ان کے بھائی ہود نے کہا کہ کیا تم ڈرتے نہیں؟'),
(3057, 26, 125, 'میں تمہارا امانتدار پیغمبر ہوں'),
(3058, 26, 126, 'پس اللہ سے ڈرو اور میرا کہا مانو!'),
(3059, 26, 127, 'میں اس پر تم سے کوئی اجرت طلب نہیں کرتا، میرا ﺛواب تو تمام جہان کے پروردگار کے پاس ہی ہے'),
(3060, 26, 128, 'کیا تم ایک ایک ٹیلے پر بطور کھیل تماشا یادگار (عمارت) بنا رہے ہو'),
(3061, 26, 129, 'اور بڑی صنعت والے (مضبوط محل تعمیر) کر رہے ہو، گویا کہ تم ہمیشہ یہیں رہو گے'),
(3062, 26, 130, 'اور جب کسی پر ہاتھ، ڈالتے ہو تو سختی اور ﻇلم سے پکڑتے ہو'),
(3063, 26, 131, 'اللہ سے ڈرو اور میری پیروی کرو'),
(3064, 26, 132, 'اس سے ڈرو جس نے ان چیزوں سے تمہاری امداد کی جنہیں تم جانتے ہو'),
(3065, 26, 133, 'اس نے تمہاری مدد کی مال سے اور اوﻻد سے'),
(3066, 26, 134, 'باغات سے اور چشموں سے'),
(3067, 26, 135, 'مجھے تو تمہاری نسبت بڑے دن کےعذاب کا اندیشہ ہے'),
(3068, 26, 136, 'انہوں نے کہا کہ آپ وعﻆ کہیں یا وعﻆ کہنے والوں میں نہ ہوں ہم پر یکساں ہے'),
(3069, 26, 137, 'یہ تو بس پرانے لوگوں کی عادت ہے'),
(3070, 26, 138, 'اور ہم ہرگز عذاب نہیں دیے جائیں گے'),
(3071, 26, 139, 'چونکہ عادیوں نے حضرت ہود کو جھٹلایا، اس لیے ہم نے انہیں تباه کردیا یقیناً اس میں نشانی ہے اور ان میں سے اکثر بے ایمان تھے'),
(3072, 26, 140, 'بیشک آپ کا رب وہی غالب مہربان'),
(3073, 26, 141, 'ﺛمودیوں نے بھی پیغمبروں کو جھٹلایا'),
(3074, 26, 142, 'ان کے بھائی صالح نے ان سے فرمایا کہ کیا تم اللہ سے نہیں ڈرتے؟'),
(3075, 26, 143, 'میں تمہاری طرف اللہ کا امانت دار پیغمبر ہوں'),
(3076, 26, 144, 'تو تم اللہ سے ڈرو اور میرا کہا کرو'),
(3077, 26, 145, 'میں اس پر تم سے کوئی اجرت نہیں مانگتا، میری اجرت تو بس پرودگار عالم پر ہی ہے'),
(3078, 26, 146, 'کیا ان چیزوں میں جو یہاں ہیں تم امن کے ساتھ چھوڑ دیے جاؤ گے'),
(3079, 26, 147, 'یعنی ان باغوں اور ان چشموں'),
(3080, 26, 148, 'اور ان کھیتوں اور ان کھجوروں کے باغوں میں جن کے شگوفے نرم و نازک ہیں'),
(3081, 26, 149, 'اور تم پہاڑوں کو تراش تراش کر پر تکلف مکانات بنا رہے ہو'),
(3082, 26, 150, 'پس اللہ سے ڈرو اور میری اطاعت کرو'),
(3083, 26, 151, 'بے باک حد سے گزر جانے والوں کی اطاعت سے باز آجاؤ'),
(3084, 26, 152, 'جو ملک میں فساد پھیلا رہے ہیں اور اصلاح نہیں کرتے'),
(3085, 26, 153, 'وه بولے کہ بس تو ان میں سے ہے جن پر جادو کردیا گیا ہے'),
(3086, 26, 154, 'تو تو ہم جیسا ہی انسان ہے۔ اگر تو سچوں سے ہے تو کوئی معجزه لے آ'),
(3087, 26, 155, 'آپ نے فرمایا یہ ہے اونٹنی، پانی پینے کی ایک باری اس کی اور ایک مقرره دن کی باری پانی پینے کی تمہاری'),
(3088, 26, 156, '(خبردار!) اسے برائی سے ہاتھ نہ لگانا ورنہ ایک بڑے بھاری دن کا عذاب تمہاری گرفت کر لے گا'),
(3089, 26, 157, 'پھر بھی انہوں نے اس کی کوچیں کاٹ ڈالیں، بس وه پشیمان ہوگئے'),
(3090, 26, 158, 'اور عذاب نے انہیں آ دبوچا۔ بیشک اس میں عبرت ہے۔ اور ان میں سے اکثر لوگ مومن نہ تھے'),
(3091, 26, 159, 'اور بیشک آپ کا رب بڑا زبردست اور مہربان ہے'),
(3092, 26, 160, 'قوم لوط نے بھی نبیوں کو جھٹلایا'),
(3093, 26, 161, 'ان سے ان کے بھائی لوط (علیہ السلام) نے کہا کیا تم اللہ کا خوف نہیں رکھتے؟'),
(3094, 26, 162, 'میں تمہاری طرف امانت دار رسول ہوں'),
(3095, 26, 163, 'پس تم اللہ تعالیٰ سے ڈرو اور میری اطاعت کرو'),
(3096, 26, 164, 'میں تم سے اس پر کوئی بدلہ نہیں مانگتا میرا اجر تو صرف اللہ تعالیٰ پر ہے جو تمام جہان کا رب ہے'),
(3097, 26, 165, 'کیا تم جہان والوں میں سے مردوں کے ساتھ شہوت رانی کرتے ہو'),
(3098, 26, 166, 'اور تمہاری جن عورتوں کو اللہ تعالیٰ نے تمہارا جوڑا بنایا ہے ان کو چھوڑ دیتے ہو، بلکہ تم ہو ہی حد سے گزر جانے والے'),
(3099, 26, 167, 'انہوں نے جواب دیاکہ اے لوط! اگر تو باز نہ آیا تو یقیناً نکال دیا جائے گا'),
(3100, 26, 168, 'آپ نے فرمایا، میں تمہارے کام سے سخت ناخوش ہوں'),
(3101, 26, 169, 'میرے پروردگار! مجھے اور میرے گھرانے کو اس (وبال) سے بچالے جو یہ کرتے ہیں'),
(3102, 26, 170, 'پس ہم نے اسے اور اس کے متعلقین کو سب کو بچالیا'),
(3103, 26, 171, 'بجز ایک بڑھیا کے کہ وه پیچھے ره جانے والوں میں ہوگئی'),
(3104, 26, 172, 'پھر ہم نے باقی اور سب کو ہلاک کر دیا'),
(3105, 26, 173, 'اور ہم نے ان پر ایک خاص قسم کا مینہ برسایا، پس بہت ہی برا مینہ تھا جو ڈرائے گئے ہوئے لوگوں پر برسا'),
(3106, 26, 174, 'یہ ماجرا بھی سراسر عبرت ہے۔ ان میں سے بھی اکثر مسلمان نہ تھے'),
(3107, 26, 175, 'بیشک تیرا پروردگار وہی ہے غلبے واﻻ مہربانی واﻻ'),
(3108, 26, 176, 'اَیکہ والوں نے بھی رسولوں کو جھٹلایا'),
(3109, 26, 177, 'جبکہ ان سے شعیب (علیہ السلام) نے کہاکہ کیا تمہیں ڈر خوف نہیں؟'),
(3110, 26, 178, 'میں تمہاری طرف امانت دار رسول ہوں'),
(3111, 26, 179, 'اللہ کا خوف کھاؤ اور میری فرمانبرداری کرو'),
(3112, 26, 180, 'میں اس پر تم سے کوئی اجرت نہیں چاہتا، میرا اجر تمام جہانوں کے پالنے والے کے پاس ہے'),
(3113, 26, 181, 'ناپ پورا بھرا کرو کم دینے والوں میں شمولیت نہ کرو'),
(3114, 26, 182, 'اور سیدھی صحیح ترازو سے توﻻ کرو'),
(3115, 26, 183, 'لوگوں کو ان کی چیزیں کمی سے نہ دو بے باکی کے ساتھ زمین میں فساد مچاتے نہ پھرو'),
(3116, 26, 184, 'اس اللہ کا خوف رکھو جس نے خود تمہیں اور اگلی مخلوق کو پیدا کیا ہے'),
(3117, 26, 185, 'انہوں نے کہا تو تو ان میں سے ہے جن پر جادو کردیا جاتا ہے'),
(3118, 26, 186, 'اور تو تو ہم ہی جیسا ایک انسان ہے اور ہم تو تجھے جھوٹ بولنے والوں میں سے ہی سمجھتے ہیں'),
(3119, 26, 187, 'اگر تو سچے لوگوں میں سے ہے تو ہم پر آسمان کے ٹکڑے گرادے'),
(3120, 26, 188, 'کہاکہ میرا رب خوب جاننے واﻻ ہے جو کچھ تم کر رہے ہو'),
(3121, 26, 189, 'چونکہ انہوں نے اسے جھٹلایا تو انہیں سائبان والے دن کے عذاب نے پکڑ لیا۔ وه بڑے بھاری دن کا عذاب تھا'),
(3122, 26, 190, 'یقیناً اس میں بڑی نشانی ہے اور ان میں کےاکثر مسلمان نہ تھے'),
(3123, 26, 191, 'اور یقیناً تیرا پروردگار البتہ وہی ہے غلبے واﻻ مہربانی واﻻ'),
(3124, 26, 192, 'اور بیشک و شبہ یہ (قرآن) رب العالمین کا نازل فرمایا ہوا ہے'),
(3125, 26, 193, 'اسے امانت دار فرشتہ لے کر آیا ہے'),
(3126, 26, 194, 'آپ کے دل پر اترا ہے کہ آپ آگاه کر دینے والوں میں سے ہو جائیں'),
(3127, 26, 195, 'صاف عربی زبان میں ہے'),
(3128, 26, 196, 'اگلے نبیوں کی کتابوں میں بھی اس قرآن کا تذکره ہے'),
(3129, 26, 197, 'کیا انہیں یہ نشانی کافی نہیں کہ حقانیت قرآن کو تو بنی اسرائیل کے علماء بھی جانتے ہیں'),
(3130, 26, 198, 'اور اگر ہم اسے کسی عجمی شخص پر نازل فرماتے'),
(3131, 26, 199, 'پس وه ان کے سامنے اس کی تلاوت کرتا تو یہ اسے باور کرنے والے نہ ہوتے'),
(3132, 26, 200, 'اسی طرح ہم نے گناہگاروں کے دلوں میں اس انکار کو داخل کر دیا ہے'),
(3133, 26, 201, 'وه جب تک دردناک عذابوں کو ملاحظہ نہ کرلیں ایمان نہ ﻻئیں گے'),
(3134, 26, 202, 'پس وه عذاب ان کو ناگہاں آجائے گا انہیں اس کا شعور بھی نہ ہو گا'),
(3135, 26, 203, 'اس وقت کہیں گے کہ کیا ہمیں کچھ مہلت دی جائے گی؟'),
(3136, 26, 204, 'پس کیا یہ ہمارے عذاب کی جلدی مچا رہے ہیں؟'),
(3137, 26, 205, 'اچھا یہ بھی بتاؤ کہ اگر ہم نے انہیں کئی سال بھی فائده اٹھانے دیا'),
(3138, 26, 206, 'پھر انہیں وه عذاب آ لگا جن سے یہ دھمکائے جاتے تھے'),
(3139, 26, 207, 'تو جو کچھ بھی یہ برتتے رہے اس میں سے کچھ بھی فائده نہ پہنچا سکے گا'),
(3140, 26, 208, 'ہم نے کسی بستی کو ہلاک نہیں کیا ہے مگر اسی حال میں کہ اس کے لیے ڈرانے والے تھے'),
(3141, 26, 209, 'نصیحت کے طور پر اور ہم ﻇلم کرنے والے نہیں ہیں'),
(3142, 26, 210, 'اس قرآن کو شیطان نہیں ﻻئے'),
(3143, 26, 211, 'نہ وه اس کے قابل ہیں، نہ انہیں اس کی طاقت ہے'),
(3144, 26, 212, 'بلکہ وه تو سننے سے بھی محروم کردیے گئے ہیں'),
(3145, 26, 213, 'پس تو اللہ کے ساتھ کسی اور معبود کو نہ پکار کہ تو بھی سزا پانے والوں میں سے ہوجائے'),
(3146, 26, 214, 'اپنے قریبی رشتہ والوں کو ڈرا دے'),
(3147, 26, 215, 'اس کے ساتھ فروتنی سے پیش آ، جو بھی ایمان ﻻنے واﻻ ہو کر تیری تابعداری کرے'),
(3148, 26, 216, 'اگر یہ لوگ تیری نافرمانی کریں تو تو اعلان کردے کہ میں ان کاموں سے بیزار ہوں جو تم کر رہے ہو'),
(3149, 26, 217, 'اپنا پورا بھروسہ غالب مہربان اللہ پر رکھ'),
(3150, 26, 218, 'جو تجھے دیکھتا رہتا ہے جبکہ تو کھڑا ہوتا ہے'),
(3151, 26, 219, 'اور سجده کرنے والوں کے درمیان تیرا گھومنا پھرنا بھی'),
(3152, 26, 220, 'وه بڑا ہی سننے واﻻ اور خوب ہی جاننے واﻻ ہے'),
(3153, 26, 221, 'کیا میں تمہیں بتاؤں کہ شیطان کس پر اترتے ہیں'),
(3154, 26, 222, 'وه ہر ایک جھوٹے گنہگار پر اترتے ہیں'),
(3155, 26, 223, '(اچٹتی) ہوئی سنی سنائی پہنچا دیتے ہیں اور ان میں سے اکثر جھوٹے ہیں'),
(3156, 26, 224, 'شاعروں کی پیروی وه کرتے ہیں جو بہکے ہوئے ہوں'),
(3157, 26, 225, 'کیا آپ نے نہیں دیکھا کہ شاعر ایک ایک بیابان میں سر ٹکراتے پھرتے ہیں'),
(3158, 26, 226, 'اور وه کہتے ہیں جو کرتے نہیں'),
(3159, 26, 227, 'سوائے ان کے جو ایمان ﻻئے اور نیک عمل کیے اور بکثرت اللہ تعالیٰ کا ذکر کیا اور اپنی مظلومی کے بعد انتقام لیا، جنہوں نے ﻇلم کیا ہے وه بھی ابھی جان لیں گے کہ کس کروٹ الٹتے ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3160, 27, 1, 'طٰس، یہ آیتیں ہیں قرآن کی (یعنی واضح) اور روشن کتاب کی'),
(3161, 27, 2, 'ہدایت اور خوشخبری ایمان والوں کے لیے'),
(3162, 27, 3, 'جو نماز قائم کرتے ہیں اور زکوٰة ادا کرتے ہیں اور آخرت پر یقین رکھتے ہیں'),
(3163, 27, 4, 'جو لوگ قیامت پر ایمان نہیں ﻻتے ہم نے انہیں ان کے کرتوت زینت دار کر دکھائے ہیں، پس وه بھٹکتے پھرتے ہیں'),
(3164, 27, 5, 'یہی لوگ ہیں جن کے لیے برا عذاب ہے اور آخرت میں بھی وه سخت نقصان یافتہ ہیں'),
(3165, 27, 6, 'بیشک آپ کو اللہ حکیم وعلیم کی طرف سے قرآن سکھایا جارہا ہے۔'),
(3166, 27, 7, '(یاد ہوگا) جبکہ موسیٰ (علیہ السلام) نے اپنے گھر والوں سے کہا کہ میں نے آگ دیکھی ہے، میں وہاں سے یا تو کوئی خبر لے کر یا آگ کا کوئی سلگتا ہوا انگارا لے کر ابھی تمہارے پاس آ جاؤں گا تاکہ تم سینک تاپ کر لو'),
(3167, 27, 8, 'جب وہاں پہنچے تو آواز دی گئی کہ بابرکت ہے وه جو اس آگ میں ہے اور برکت دیاگیا ہے وه جس کے آس پاس ہے اور پاک ہے اللہ جو تمام جہانوں کا پالنے واﻻ ہے'),
(3168, 27, 9, 'موسیٰ! سن بات یہ ہے کہ میں ہی اللہ ہوں غالب با حکمت'),
(3169, 27, 10, 'تو اپنی ﻻٹھی ڈال دے، موسیٰ نے جب اسے ہلتا جلتا دیکھا اس طرح کہ گویا وه ایک سانﭗ ہے تو منھ موڑے ہوئے پیٹھ پھیر کر بھاگے اور پلٹ کر بھی نہ دیکھا، اے موسیٰ! خوف نہ کھا، میرے حضور میں پیغمبر ڈرا نہیں کرتے'),
(3170, 27, 11, 'لیکن جو لوگ ﻇلم کریں پھر اس کے عوض نیکی کریں اس برائی کے پیچھے تو میں بھی بخشنے واﻻ مہربان ہوں'),
(3171, 27, 12, 'اور اپنا ہاتھ اپنے گریبان میں ڈال، وه سفید چمکیلا ہو کر نکلے گا بغیر کسی عیب کے، تو نو نشانیاں لے کر فرعون اور اس کی قوم کی طرف جا، یقیناً وه بدکاروں کا گروه ہے'),
(3172, 27, 13, 'پس جب ان کے پاس آنکھیں کھول دینے والے ہمارے معجزے پہنچے تو وه کہنے لگے یہ تو صریح جادو ہے'),
(3173, 27, 14, 'انہوں نے انکار کردیا حاﻻنکہ ان کے دل یقین کر چکے تھے صرف ﻇلم اور تکبر کی بنا پر۔ پس دیکھ لیجئے کہ ان فتنہ پرداز لوگوں کا انجام کیسا کچھ ہوا'),
(3174, 27, 15, 'اور ہم نے یقیناً داؤد اور سلیمان کو علم دے رکھا تھا۔ اور دونوں نے کہا، تعریف اس اللہ کے لیے ہے جس نے ہمیں اپنے بہت سے ایماندار بندوں پر فضیلت عطا فرمائی ہے'),
(3175, 27, 16, 'اور داؤد کے وارث سلیمان ہوئے اور کہنے لگے لوگو! ہمیں پرندوں کی بولی سکھائی گئی ہے اور ہم سب کچھ میں سے دیئے گئے ہیں۔ بیشک یہ بالکل کھلا ہوا فضل الٰہی ہے'),
(3176, 27, 17, 'سلیمان کے سامنے ان کے تمام لشکر جنات اور انسان اور پرند میں سے جمع کیے گئے (ہر ہر قسم کی) الگ الگ درجہ بندی کردی گئی'),
(3177, 27, 18, 'جب وه چینوٹیوں کے میدان میں پہنچے تو ایک چیونٹی نے کہا اے چیونٹیو! اپنے اپنے گھروں میں گھس جاؤ، ایسا نہ ہو کہ بیخبری میں سلیمان اور اس کا لشکر تمہیں روند ڈالے'),
(3178, 27, 19, 'اس کی اس بات سے حضرت سلیمان مسکرا کر ہنس دیئے اور دعا کرنے لگے کہ اے پروردگار! تو مجھے توفیق دے کہ میں تیری ان نعمتوں کا شکر بجا ﻻؤں جو تو نے مجھ پر انعام کی ہیں اور میرے ماں باپ پر اور میں ایسے نیک اعمال کرتا رہوں جن سے تو خوش رہے مجھے اپنی رحمت سے نیک بندوں میں شامل کر لے'),
(3179, 27, 20, 'آپ نے پرندوں کی دیکھ بھال کی اور فرمانے لگے یہ کیا بات ہے کہ میں ہدہد کو نہیں دیکھتا؟ کیا واقعی وه غیر حاضر ہے؟'),
(3180, 27, 21, 'یقیناً میں اسے سخت سزا دوں گا، یا اسے ذبح کر ڈالوں گا، یا میرے سامنے کوئی صریح دلیل بیان کرے'),
(3181, 27, 22, 'کچھ زیاده دیر نہ گزری تھی کہ آ کر اس نے کہا میں ایک ایسی چیز کی خبر ﻻیا ہوں کہ تجھے اس کی خبر ہی نہیں، میں سبا کی ایک سچی خبر تیرے پاس ﻻیا ہوں'),
(3182, 27, 23, 'میں نے دیکھا کہ ان کی بادشاہت ایک عورت کر رہی ہے جسے ہر قسم کی چیز سے کچھ نہ کچھ دیا گیا ہے اور اس کا تخت بھی بڑی عظمت واﻻ ہے'),
(3183, 27, 24, 'میں نے اسے اور اس کی قوم کو، اللہ تعالیٰ کو چھوڑ کر سورج کو سجده کرتے ہوئے پایا، شیطان نے ان کے کام انہیں بھلے کرکے دکھلا کر صحیح راه سے روک دیا ہے۔ پس وه ہدایت پر نہیں آتے'),
(3184, 27, 25, 'کہ اسی اللہ کے لیے سجدے کریں جو آسمانوں اور زمینوں کے پوشیده چیزوں کو باہر نکالتا ہے، اور جو کچھ تم چھپاتے ہو اور ﻇاہر کرتے ہو وه سب کچھ جانتا ہے'),
(3185, 27, 26, 'اس کے سوا کوئی معبود برحق نہیں وہی عظمت والے عرش کا مالک ہے۔'),
(3186, 27, 27, 'سلیمان نے کہا، اب ہم دیکھیں گے کہ تو نے سچ کہا ہے یا تو جھوٹا ہے'),
(3187, 27, 28, 'میرے اس خط کو لے جاکر انہیں دے دے پھر ان کے پاس سے ہٹ آ اور دیکھ کہ وه کیا جواب دیتے ہیں'),
(3188, 27, 29, 'وه کہنے لگی اے سردارو! میری طرف ایک باوقعت خط ڈاﻻ گیا ہے'),
(3189, 27, 30, 'جو سلیمان کی طرف سے ہے اور جو بخشش کرنے والے مہربان اللہ کے نام سے شروع ہے'),
(3190, 27, 31, 'یہ کہ تم میرے سامنے سرکشی نہ کرو اور مسلمان بن کر میرے پاس آجاؤ'),
(3191, 27, 32, 'اس نے کہا اے میرے سردارو! تم میرے اس معاملہ میں مجھے مشوره دو۔ میں کسی امر کا قطعی فیصلہ جب تک تمہاری موجودگی اور رائے نہ ہو نہیں کیا کرتی'),
(3192, 27, 33, 'ان سب نے جواب دیا کہ ہم طاقت اور قوت والے سخت لڑنے بھڑنے والے ہیں۔ آگے آپ کو اختیار ہے آپ خود ہی سوچ لیجئے کہ ہمیں آپ کیا کچھ حکم فرماتی ہیں'),
(3193, 27, 34, 'اس نے کہا کہ بادشاه جب کسی بستی میں گھستے ہیں تو اسے اجاڑ دیتے ہیں اور وہاں کے باعزت لوگوں کو ذلیل کر دیتے ہیں اور یہ لوگ بھی ایسا ہی کریں گے'),
(3194, 27, 35, 'میں انہیں ایک ہدیہ بھیجنے والی ہوں، پھر دیکھ لوں گی کہ قاصد کیا جواب لے کر لوٹتے ہیں'),
(3195, 27, 36, 'پس جب قاصد حضرت سلیمان کے پاس پہنچا تو آپ نے فرمایا کیا تم مال سے مجھے مدد دینا چاہتے ہو؟ مجھے تو میرے رب نے اس سے بہت بہتر دے رکھا ہے جو اس نے تمہیں دیا ہے پس تم ہی اپنے تحفے سے خوش رہو'),
(3196, 27, 37, 'جا ان کی طرف واپس لوٹ جا، ہم ان (کے مقابلہ) پر وه لشکر ﻻئیں گے جن کے سامنے پڑنے کی ان میں طاقت نہیں اور ہم انہیں ذلیل و پست کر کے وہاں سے نکال باہر کریں گے'),
(3197, 27, 38, 'آپ نے فرمایا اے سردارو! تم میں سے کوئی ہے جو ان کے مسلمان ہو کر پہنچنے سے پہلے ہی اس کا تخت مجھے ﻻدے'),
(3198, 27, 39, 'ایک قوی ہیکل جن کہنے لگا آپ اپنی اس مجلس سے اٹھیں اس سے پہلے ہی پہلے میں اسے آپ کے پاس ﻻدیتا ہوں، یقین مانیے کہ میں اس پر قادر ہوں اور ہوں بھی امانت دار'),
(3199, 27, 40, 'جس کے پاس کتاب کا علم تھا وه بول اٹھا کہ آپ پلک جھپکائیں اس سے بھی پہلے میں اسے آپ کے پاس پہنچا سکتا ہوں۔ جب آپ نے اسے اپنے پاس موجود پایا تو فرمانے لگے یہی میرے رب کا فضل ہے، تاکہ وه مجھے آزمائے کہ میں شکر گزاری کرتا ہوں یا ناشکری، شکر گزار اپنے ہی نفع کے لیے شکر گزاری کرتا ہے اور جو ناشکری کرے تو میرا پروردگار (بے پروا اور بزرگ) غنی اور کریم ہے'),
(3200, 27, 41, 'حکم دیا کہ اس کے تخت میں کچھ پھیر بدل کر دو تاکہ معلوم ہو جائے کہ یہ راه پالیتی ہے یا ان میں سے ہوتی ہے جو راه نہیں پاتے'),
(3201, 27, 42, 'پھر جب وه آگئی تو اس سے کہا (دریافت کیا) گیا کہ ایسا ہی تیرا (بھی) تخت ہے؟ اس نے جواب دیا کہ یہ گویا وہی ہے، ہمیں اس سے پہلے ہی علم دیا گیا تھا اور ہم مسلمان تھے'),
(3202, 27, 43, 'اسے انہوں نے روک رکھا تھا جن کی وه اللہ کے سوا پرستش کرتی رہی تھی، یقیناً وه کافر لوگوں میں سے تھی'),
(3203, 27, 44, 'اس سے کہا گیا کہ محل میں چلی چلو، جسے دیکھ کر یہ سمجھ کر کہ یہ حوض ہے اس نے اپنی پنڈلیاں کھول دیں، فرمایا یہ توشیشے سے منڈھی ہوئی عمارت ہے، کہنے لگی میرے پروردگار! میں نے اپنے آپ پر ﻇلم کیا۔ اب میں سلیمان کے ساتھ اللہ رب العالمین کی مطیع اور فرمانبردار بنتی ہوں'),
(3204, 27, 45, 'یقیناً ہم نے ﺛمود کی طرف ان کے بھائی صالح کو بھیجا کہ تم سب اللہ کی عبادت کرو پھر بھی وه دو فریق بن کر آپس میں لڑنے جھگڑنے لگے'),
(3205, 27, 46, 'آپ نے فرمایا اے میری قوم کے لوگو! تم نیکی سے پہلے برائی کی جلدی کیوں مچا رہے ہو؟ تم اللہ تعالیٰ سےاستغفار کیوں نہیں کرتے تاکہ تم پر رحم کیا جائے'),
(3206, 27, 47, 'وه کہنے لگے ہم تو تیری اور تیرے ساتھیوں کی بدشگونی لے رہے ہیں؟ آپ نے فرمایا تمہاری بدشگونی اللہ کے ہاں ہے، بلکہ تم فتنے میں پڑے ہوئے لوگ ہو'),
(3207, 27, 48, 'اس شہر میں نو سردار تھے جو زمین میں فساد پھیلاتے رہتے تھے اور اصلاح نہیں کرتے تھے'),
(3208, 27, 49, 'انہوں نے آپس میں بڑی قسمیں کھا کھا کر عہد کیا کہ رات ہی کو صالح اور اس کے گھر والوں پر ہم چھاپہ ماریں گے، اور اس کے وارﺛوں سے صاف کہہ دیں گے کہ ہم اس کے اہل کی ہلاکت کے وقت موجود نہ تھے اور ہم بالکل سچے ہیں'),
(3209, 27, 50, 'انہوں نے مکر (خفیہ تدبیر) کیا اور ہم نے بھی اور وه اسے سمجھتے ہی نہ تھے'),
(3210, 27, 51, '(اب) دیکھ لے ان کے مکر کا انجام کیسا کچھ ہوا؟ کہ ہم نے ان کو اور ان کی قوم کو سب کو غارت کردیا'),
(3211, 27, 52, 'یہ ہیں ان کے مکانات جو ان کے ﻇلم کی وجہ سے اجڑے پڑے ہیں، جو لوگ علم رکھتے ہیں ان کے لیےاس میں بڑی نشانی ہے'),
(3212, 27, 53, 'ہم نے ان کو جو ایمان ﻻئے تھے اور پرہیزگار تھے بال بال بچالیا'),
(3213, 27, 54, 'اور لوط کا (ذکر کر) جبکہ اس نے اپنی قوم سے کہاکہ کیا باوجود دیکھنے بھالنے کے پھر بھی تم بدکاری کر رہے ہو؟'),
(3214, 27, 55, 'یہ کیا بات ہے کہ تم عورتوں کو چھوڑ کر مردوں کے پاس شہوت سے آتے ہو حق یہ ہے کہ تم بڑی ہی نادانی کر رہے ہو'),
(3215, 27, 56, 'قوم کا جواب بجز اس کہنے کے اور کچھ نہ تھا کہ آل لوط کو اپنے شہر سے شہر بدر کر دو، یہ تو بڑے پاک باز بن رہے ہیں'),
(3216, 27, 57, 'پس ہم نے اسے اور اس کے اہل کو بجز اس کی بیوی کے سب کو بچالیا، اس کا اندازه تو باقی ره جانے والوں میں ہم لگا ہی چکے تھے'),
(3217, 27, 58, 'اور ان پر ایک (خاص قسم) کی بارش برسادی، پس ان دھمکائے ہوئے لوگوں پر بری بارش ہوئی'),
(3218, 27, 59, 'تو کہہ دے کہ تمام تعریف اللہ ہی کے لیے ہے اور اس کے برگزیده بندوں پر سلام ہے۔ کیا اللہ تعالیٰ بہتر ہے یا وه جنہیں یہ لوگ شریک ٹھہرا رہے ہیں'),
(3219, 27, 60, 'بھلا بتاؤ تو؟ کہ آسمانوں کو اور زمین کو کس نے پیدا کیا؟ کس نے آسمان سے بارش برسائی؟ پھر اس سے ہرے بھرے بارونق باغات اگا دیئے؟ ان باغوں کے درختوں کو تم ہر گز نہ اگا سکتے، کیا اللہ کے ساتھ اور کوئی معبود بھی ہے؟ بلکہ یہ لوگ ہٹ جاتے ہیں (سیدھی راه سے)'),
(3220, 27, 61, 'کیا وه جس نے زمین کو قرار گاه بنایا اور اس کے درمیان نہریں جاری کر دیں اور اس کے لیے پہاڑ بنائے اور دو سمندروں کے درمیان روک بنا دی کیا اللہ کے ساتھ اور کوئی معبود بھی ہے؟ بلکہ ان میں سے اکثر کچھ جانتے ہی نہیں'),
(3221, 27, 62, 'بے کس کی پکار کو جب کہ وه پکارے، کون قبول کرکے سختی کو دور کر دیتا ہے؟ اور تمہیں زمین کا خلیفہ بناتا ہے، کیا اللہ تعالیٰ کے ساتھ اور معبود ہے؟ تم بہت کم نصیحت وعبرت حاصل کرتے ہو'),
(3222, 27, 63, 'کیا وه جو تمہیں خشکی اور تری کی تاریکیوں میں راه دکھاتا ہے اور جو اپنی رحمت سے پہلے ہی خوشخبریاں دینے والی ہوائیں چلاتا ہے، کیا اللہ کے ساتھ کوئی اور معبود بھی ہے جنہیں یہ شریک کرتے ہیں ان سب سے اللہ بلند وباﻻتر ہے'),
(3223, 27, 64, 'کیا وه جو مخلوق کی اول دفعہ پیدائش کرتا ہے پھر اسے لوٹائے گا اور جو تمہیں آسمان اور زمین سے روزیاں دے رہا ہے، کیا اللہ کے ساتھ کوئی اور معبود ہے کہہ دیجئے کہ اگر سچے ہو تو اپنی دلیل ﻻؤ'),
(3224, 27, 65, 'کہہ دیجئے کہ آسمانوں والوں میں سے زمین والوں میں سے سوائے اللہ کے کوئی غیب نہیں جانتا، انہیں تو یہ بھی نہیں معلوم کہ کب اٹھا کھڑے کیے جائیں گے؟'),
(3225, 27, 66, 'بلکہ آخرت کے بارے میں ان کا علم ختم ہوچکا ہے، بلکہ یہ اس کی طرف سے شک میں ہیں۔ بلکہ یہ اس سے اندھے ہیں'),
(3226, 27, 67, 'کافروں نے کہا کہ کیا جب ہم مٹی ہو جائیں گے اور ہمارے باپ دادا بھی کیا ہم پھر نکالے جائیں گے'),
(3227, 27, 68, 'ہم اور ہمارے باپ دادوں کو بہت پہلے سے یہ وعدے دیے جاتے رہے۔ کچھ نہیں یہ تو صرف اگلوں کے افسانے ہیں'),
(3228, 27, 69, 'کہہ دیجئے کہ زمین میں چل پھر کر ذرا دیکھو تو سہی کہ گنہگاروں کا کیسا انجام ہوا؟'),
(3229, 27, 70, 'آپ ان کے بارے میں غم نہ کریں اور ان کے داؤں گھات سے تنگ دل نہ ہوں'),
(3230, 27, 71, 'کہتے ہیں کہ یہ وعده کب ہے اگر سچے ہو تو بتلا دو'),
(3231, 27, 72, 'جواب دیجئے! کہ شاید بعض وه چیزیں جن کی تم جلدی مچا رہے ہو تم سے بہت ہی قریب ہوگئی ہوں'),
(3232, 27, 73, 'یقیناً آپ کا پروردگار تمام لوگوں پر بڑے ہی فضل واﻻ ہے لیکن اکثر لوگ ناشکری کرتے ہیں'),
(3233, 27, 74, 'بیشک آپ کا رب ان چیزوں کو بھی جانتا ہے جنہیں ان کے سینے چھپا رہے ہیں اور جنہیں ﻇاہر کر رہے ہیں'),
(3234, 27, 75, 'آسمان وزمین کی کوئی پوشیده چیز بھی ایسی نہیں جو روشن اور کھلی کتاب میں نہ ہو'),
(3235, 27, 76, 'یقیناً یہ قرآن بنی اسرائیل کے سامنے ان اکثر چیزوں کا بیان کر رہا ہے جن میں یہ اختلاف کرتے ہیں'),
(3236, 27, 77, 'اور یہ قرآن ایمان والوں کے لیے یقیناً ہدایت اور رحمت ہے'),
(3237, 27, 78, 'آپ کا رب ان کے درمیان اپنے حکم سے سب فیصلے کر دے گا، وه بڑا ہی غالب اور دانا ہے'),
(3238, 27, 79, 'پس آپ یقیناً اللہ ہی پر بھروسہ رکھیے، یقیناً آپ سچے اور کھلے دین پر ہیں'),
(3239, 27, 80, 'بیشک آپ نہ مُردوں کو سنا سکتے ہیں اور نہ بہروں کو اپنی پکار سنا سکتے ہیں، جبکہ وه پیٹھ پھیرے روگرداں جا رہے ہوں'),
(3240, 27, 81, 'اور نہ آپ اندھوں کو ان کی گمراہی سے ہٹا کر رہنمائی کر سکتے ہیں آپ تو صرف انہیں سنا سکتے ہیں جو ہماری آیتوں پر ایمان ﻻئے ہیں پھر وه فرمانبردار ہوجاتے ہیں'),
(3241, 27, 82, 'جب ان کے اوپر عذاب کا وعده ﺛابت ہو جائے گا، ہم زمین سے ان کے لیے ایک جانور نکالیں گے جو ان سے باتیں کرتا ہوگا کہ لوگ ہماری آیتوں پر یقین نہیں کرتے تھے'),
(3242, 27, 83, 'اور جس دن ہم ہر امت میں سے ان لوگوں کے گروه کو جو ہماری آیتوں کو جھٹلاتے تھے گھیر گھار کر ﻻئیں گے پھر وه سب کے سب الگ کر دیئے جائیں گے'),
(3243, 27, 84, 'جب سب کے سب آپہنچیں گے تو اللہ تعالیٰ فرمائے گا کہ تم نے میری آیتوں کو باوجودیکہ تمہیں ان کا پورا علم نہ تھا کیوں جھٹلایا؟ اور یہ بھی بتلاؤ کہ تم کیا کچھ کرتے رہے؟'),
(3244, 27, 85, 'بسبب اس کے کہ انہوں نے ﻇلم کیا تھا ان پر بات جم جائے گی اور وه کچھ بول نہ سکیں گے'),
(3245, 27, 86, 'کیا وه دیکھ نہیں رہے ہیں کہ ہم نے رات کو اس لیے بنایا ہے کہ وه اس میں آرام حاصل کرلیں اور دن کو ہم نے دکھلانے واﻻ بنایا ہے، یقیناً اس میں ان لوگوں کے لیے نشانیاں ہیں جو ایمان ویقین رکھتے ہیں'),
(3246, 27, 87, 'جس دن صور پھونکا جائے گا تو سب کے سب آسمانوں والے اور زمین والے گھبرا اٹھیں گے مگر جسے اللہ تعالیٰ چاہے، اور سارے کے سارے عاجز وپست ہو کر اس کے سامنے حاضر ہوں گے'),
(3247, 27, 88, 'اور آپ پہاڑوں کو دیکھ کر اپنی جگہ جمے ہوئے خیال کرتے ہیں لیکن وه بھی بادل کی طرح اڑتے پھریں گے، یہ ہے صنعت اللہ کی جس نے ہر چیز کو مضبوط بنایا ہے، جو کچھ تم کرتے ہو اس سے وه باخبر ہے'),
(3248, 27, 89, 'جو لوگ نیک عمل ﻻئیں گے انھیں اس سے بہتر بدلہ ملے گا اور وه اس دن کی گھبراہٹ سے بےخوف ہوں گے'),
(3249, 27, 90, 'اور جو برائی لے کر آئیں گے وه اوندھے منھ آگ میں جھونک دیئے جائیں گے۔ صرف وہی بدلہ دیئے جاؤ گے جو تم کرتے رہے'),
(3250, 27, 91, 'مجھے تو بس یہی حکم دیا گیا ہے کہ میں اس شہر کے پروردگار کی عبادت کرتا رہوں جس نے اسے حرمت واﻻ بنایا ہے، جس کی ملکیت ہر چیز ہے اور مجھے یہ بھی فرمایا گیا ہے کہ میں فرماں برداروں میں ہو جاؤں'),
(3251, 27, 92, 'اور میں قرآن کی تلاوت کرتا رہوں، جو راه راست پر آجائے وه اپنے نفع کے لیے راه راست پر آئے گا۔ اور جو بہک جائے تو کہہ دیجئے! کہ میں تو صرف ہوشیار کرنے والوں میں سے ہوں'),
(3252, 27, 93, 'کہہ دیجئے، کہ تمام تعریفیں اللہ ہی کو سزاوار ہیں وه عنقریب اپنی نشانیاں دکھائے گا جنہیں تم (خود) پہچان لو گے۔ اور جو کچھ تم کرتے ہو اس سے آپ کا رب غافل نہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3253, 28, 1, 'طٰسم'),
(3254, 28, 2, 'یہ آیتیں ہیں روشن کتاب کی'),
(3255, 28, 3, 'ہم آپ کے سامنے موسیٰ اور فرعون کا صحیح واقعہ بیان کرتے ہیں ان لوگوں کے لیے جو ایمان رکھتے ہیں'),
(3256, 28, 4, 'یقیناً فرعون نے زمین میں سرکشی کر رکھی تھی اور وہاں کے لوگوں کو گروه گروه بنا رکھا تھا اور ان میں سے ایک فرقہ کو کمزور کر رکھا تھا اور ان کے لڑکوں کو تو ذبح کر ڈالتا تھا اور ان کی لڑکیوں کو زنده چھوڑ دیتا تھا۔ بیشک وشبہ وه تھا ہی مفسدوں میں سے'),
(3257, 28, 5, 'پھر ہماری چاہت ہوئی کہ ہم ان پر کرم فرمائیں جنہیں زمین میں بےحد کمزور کر دیا گیا تھا، اور ہم انہیں کو پیشوا اور (زمین) کا وارث بنائیں'),
(3258, 28, 6, 'اور یہ بھی کہ ہم انہیں زمین میں قدرت واختیار دیں اور فرعون اور ہامان اور ان کے لشکروں کو وه دکھائیں جس سے وه ڈر رہے ہیں'),
(3259, 28, 7, 'ہم نے موسیٰ (علیہ السلام) کی ماں کو وحی کی کہ اسے دودھ پلاتی ره اور جب تجھے اس کی نسبت کوئی خوف معلوم ہو تو اسے دریا میں بہا دینا اور کوئی ڈر خوف یا رنج غم نہ کرنا، ہم یقیناً اسے تیری طرف لوٹانے والے ہیں اور اسے اپنے پیغمبروں میں بنانے والے ہیں'),
(3260, 28, 8, 'آخر فرعون کے لوگوں نے اس بچے کو اٹھا لیا کہ آخرکار یہی بچہ ان کا دشمن ہوا اور ان کے رنج کا باعﺚ بنا، کچھ شک نہیں کہ فرعون اور ہامان اور ان کے لشکر تھے ہی خطاکار'),
(3261, 28, 9, 'اور فرعون کی بیوی نے کہا یہ تو میری اور تیری آنکھوں کی ٹھنڈک ہے، اسے قتل نہ کرو، بہت ممکن ہے کہ یہ ہمیں کوئی فائده پہنچائے یا ہم اسے اپنا ہی بیٹا بنا لیں اور یہ لوگ شعور ہی نہ رکھتے تھے'),
(3262, 28, 10, 'موسیٰ (علیہ السلام) کی والده کا دل بے قرار ہوگیا، قریب تھیں کہ اس واقعہ کو بالکل ﻇاہر کر دیتیں اگر ہم ان کے دل کو ڈھارس نہ دے دیتے یہ اس لیے کہ وه یقین کرنے والوں میں رہے'),
(3263, 28, 11, 'موسیٰ (علیہ السلام) کی والده نے اس کی بہن سے کہا کہ تو اس کے پیچھے پیچھے جا، تو وه اسے دور ہی دور سے دیکھتی رہی اور فرعونیوں کو اس کا علم بھی نہ ہوا'),
(3264, 28, 12, 'ان کے پہنچنے سے پہلے ہم نے موسیٰ (علیہ السلام) پر دائیوں کا دودھ حرام کر دیا تھا۔ یہ کہنے لگی کہ کیا میں تمہیں ایسا گھرانا بتاؤں جو اس بچہ کی تمہارے لیے پرورش کرے اور ہوں بھی وه اس بچے کے خیر خواه'),
(3265, 28, 13, 'پس ہم نے اسے اس کی ماں کی طرف واپس پہنچایا، تاکہ اس کی آنکھیں ٹھنڈی رہیں اور آزرده خاطر نہ ہو اور جان لے کہ اللہ تعالی کا وعده سچا ہے لیکن اکثر لوگ نہیں جانتے'),
(3266, 28, 14, 'اور جب موسیٰ (علیہ السلام) اپنی جوانی کو پہنچ گئے اور پورے توانا ہوگئے ہم نے انہیں حکمت وعلم عطا فرمایا، نیکی کرنے والوں کو ہم اسی طرح بدلہ دیا کرتے ہیں'),
(3267, 28, 15, 'اور موسیٰ (علیہ السلام) ایک ایسے وقت شہر میں آئے جبکہ شہر کے لوگ غفلت میں تھے۔ یہاں دو شخصوں کو لڑتے ہوئے پایا، یہ ایک تو اس کے رفیقوں میں سے تھا اور یہ دوسرا اس کے دشمنوں میں سے، اس کی قوم والے نے اس کے خلاف جو اس کے دشمنوں میں سے تھا اس سے فریاد کی، جس پر موسیٰ (علیہ السلام) نے اس کو مکا مارا جس سے وه مر گیا موسیٰ (علیہ السلام) کہنے لگے یہ تو شیطانی کام ہے، یقیناً شیطان دشمن اور کھلے طور پر بہکانے واﻻ ہے'),
(3268, 28, 16, 'پھر دعا کرنے لگے کہ اے پروردگار! میں نے خود اپنے اوپر ﻇلم کیا، تو مجھے معاف فرما دے، اللہ تعالیٰ نے اسے بخش دیا، وه بخشش اور بہت مہربانی کرنے واﻻ ہے'),
(3269, 28, 17, 'کہنے لگے اے میرے رب! جیسے تو نے مجھ پر یہ کرم فرمایا میں بھی اب ہرگز کسی گنہگار کا مددگار نہ بنوں گا'),
(3270, 28, 18, 'صبح ہی صبح ڈرتے اندیشہ کی حالت میں خبریں لینے کو شہر میں گئے، کہ اچانک وہی شخص جس نے کل ان سے مدد طلب کی تھی ان سے فریاد کر رہا ہے۔ موسیٰ (علیہ السلام) نے اس سے کہا کہ اس میں شک نہیں تو تو صریح بے راه ہے'),
(3271, 28, 19, 'پھر جب اپنے اور اس کے دشمن کو پکڑنا چاہا وه فریادی کہنے لگا کہ موسیٰ (علیہ السلام) کیا جس طرح تو نے کل ایک شخص کو قتل کیا ہے مجھے بھی مار ڈالنا چاہتا ہے، تو تو ملک میں ﻇالم وسرکش ہونا ہی چاہتا ہے اور تیرا یہ اراده ہی نہیں کہ ملاپ کرنے والوں میں سے ہو'),
(3272, 28, 20, 'شہر کے پرلے کنارے سے ایک شخص دوڑتا ہوا آیا اور کہنے لگا اے موسیٰ! یہاں کے سردار تیرے قتل کا مشوره کر رہے ہیں، پس تو بہت جلد چلا جا مجھے اپنا خیر خواه مان'),
(3273, 28, 21, 'پس موسیٰ (علیہ السلام) وہاں سے خوفزده ہوکر دیکھتے بھالتے نکل کھڑے ہوئے، کہنے لگے اے پروردگار! مجھے ﻇالموں کے گروه سے بچا لے'),
(3274, 28, 22, 'اور جب مدین کی طرف متوجہ ہوئے تو کہنے لگے مجھے امید ہے کہ میرا رب مجھے سیدھی راه لے چلے گا'),
(3275, 28, 23, 'مدین کے پانی پر جب آپ پہنچے تو دیکھا کہ لوگوں کی ایک جماعت وہاں پانی پلا رہی ہے اور دو عورتیں الگ کھڑی اپنے (جانوروں کو) روکتی ہوئی دکھائی دیں، پوچھا کہ تمہارا کیا حال ہے، وه بولیں کہ جب تک یہ چرواہے واپس نہ لوٹ جائیں ہم پانی نہیں پلاتیں اور ہمارے والد بہت بڑی عمر کے بوڑھے ہیں'),
(3276, 28, 24, 'پس آپ نے خود ان جانوروں کو پانی پلا دیا پھر سائے کی طرف ہٹ آئے اور کہنے لگے اے پروردگار! تو جو کچھ بھلائی میری طرف اتارے میں اس کا محتاج ہوں'),
(3277, 28, 25, 'اتنے میں ان دونوں عورتوں میں سے ایک ان کی طرف شرم وحیا سے چلتی ہوئی آئی، کہنے لگی کہ میرے باپ آپ کو بلا رہے ہیں تاکہ آپ نے ہمارے (جانوروں) کو جو پانی پلایا ہے اس کی اجرت دیں، جب حضرت موسیٰ (علیہ السلام) ان کے پاس پہنچے اور ان سے اپنا سارا حال بیان کیا تو وه کہنے لگے اب نہ ڈر تو نے ﻇالم قوم سے نجات پائی'),
(3278, 28, 26, 'ان دونوں میں سے ایک نے کہا کہ ابا جی! آپ انہیں مزدوری پر رکھ لیجئے، کیونکہ جنہیں آپ اجرت پر رکھیں ان میں سے سب سے بہتر وه ہے جو مضبوط اور امانت دار ہو'),
(3279, 28, 27, 'اس بزرگ نے کہا میں اپنی ان دونوں لڑکیوں میں سے ایک کو آپ کے نکاح میں دینا چاہتا ہوں اس (مہر پر) کہ آپ آٹھ سال تک میرا کام کاج کریں۔ ہاں اگر آپ دس سال پورے کریں تو یہ آپ کی طرف سے بطور احسان کے ہے میں یہ ہرگز نہیں چاہتا کہ آپ کو کسی مشقت میں ڈالوں، اللہ کو منظور ہے تو آگے چل کر آپ مجھے بھلا آدمی پائیں گے'),
(3280, 28, 28, 'موسیٰ (علیہ السلام) نے کہا، خیر تو یہ بات میرے اور آپ کے درمیان پختہ ہوگئی، میں ان دونوں مدتوں میں سے جسے پورا کروں مجھ پر کوئی زیادتی نہ ہو، ہم یہ جو کچھ کہہ رہے ہیں اس پر اللہ (گواه اور) کارساز ہے'),
(3281, 28, 29, 'جب حضرت موسیٰ علیہ السلام نے مدت پوری کرلی اور اپنے گھر والوں کو لے کر چلے تو کوه طور کی طرف آگ دیکھی۔ اپنی بیوی سے کہنے لگے ٹھہرو! میں نے آگ دیکھی ہے بہت ممکن ہے کہ میں وہاں سے کوئی خبر ﻻؤں یا آگ کا کوئی انگاره ﻻؤں تاکہ تم سینک لو'),
(3282, 28, 30, 'پس جب وہاں پہنچے تو اس بابرکت زمین کے میدان کے دائیں کنارے کے درخت میں سے آواز دیئے گئے کہ اے موسیٰ! یقیناً میں ہی اللہ ہوں سارے جہانوں کا پروردگار'),
(3283, 28, 31, 'اور یہ (بھی آواز آئی) کہ اپنی ﻻٹھی ڈال دے۔ پھر جب اسے دیکھا کہ وه سانﭗ کی طرح پھن پھنا رہی ہے تو پیٹھ پھیر کر واپس ہوگئے اور مڑ کر رخ بھی نہ کیا، ہم نے کہا اے موسیٰ! آگے آ ڈر مت، یقیناً تو ہر طرح امن واﻻ ہے'),
(3284, 28, 32, 'اپنے ہاتھ کو اپنے گریبان میں ڈال وه بغیر کسی قسم کے روگ کے چمکتا ہوا نکلے گا بالکل سفید اور خوف سے (بچنے کے لیے) اپنے بازو اپنی طرف ملا لے، پس یہ دونوں معجزے تیرے لیے تیرے رب کی طرف سے ہیں فرعون اور اس کی جماعت کی طرف، یقیناً وه سب کے سب بےحکم اور نافرمان لوگ ہیں'),
(3285, 28, 33, 'موسیٰ (علیہ السلام) نے کہا پروردگار! میں نے ان کا ایک آدمی قتل کر دیا تھا۔ اب مجھے اندیشہ ہے کہ وه مجھے بھی قتل کر ڈالیں'),
(3286, 28, 34, 'اور میرا بھائی ہارون (علیہ السلام) مجھ سے بہت زیاده فصیح زبان واﻻ ہے تو اسے بھی میرا مددگار بنا کر میرے ساتھ بھیج کہ وه مجھے سچا مانے، مجھے تو خوف ہے کہ وه سب مجھے جھٹلا دیں گے'),
(3287, 28, 35, 'اللہ تعالیٰ نے فرمایا کہ ہم تیرے بھائی کے ساتھ تیرا بازو مضبوط کردیں گے اور تم دونوں کو غلبہ دیں گے فرعونی تم تک پہنچ ہی نہ سکیں گے، بسبب ہماری نشانیوں کے، تم دونوں اور تمہاری تابعداری کرنے والے ہی غالب رہیں گے'),
(3288, 28, 36, 'پس جب ان کے پاس موسیٰ (علیہ السلام) ہمارے دیے ہوئے کھلے معجزے لے کر پہنچے تو وه کہنے لگے یہ تو صرف گھڑا گھڑایا جادو ہے ہم نے اپنے اگلے باپ دادوں کے زمانہ میں کبھی یہ نہیں سنا'),
(3289, 28, 37, 'حضرت موسیٰ (علیہ السلام) کہنے لگے میرا رب تعالیٰ اسے خوب جانتا ہے جو اس کے پاس کی ہدایت لے کر آتا ہے اور جس کے لیے آخرت کا (اچھا) انجام ہوتا ہے۔ یقیناً بے انصافوں کا بھلا نہ ہوگا'),
(3290, 28, 38, 'فرعون کہنے لگا اے درباریو! میں تو اپنے سوا کسی کو تمہارا معبود نہیں جانتا۔ سن اے ہامان! تو میرے لیے مٹی کو آگ سے پکوا پھر میرے لیے ایک محل تعمیر کر تو میں موسیٰ کے معبود کو جھانک لوں اسے میں تو جھوٹوں میں سے ہی گمان کر رہا ہوں'),
(3291, 28, 39, 'اس نے اور اس کے لشکروں نے ناحق طریقے پر ملک میں تکبر کیا اور سمجھ لیا کہ وه ہماری جانب لوٹائے ہی نہ جائیں گے'),
(3292, 28, 40, 'بالﺂخر ہم نے اسے اور اس کے لشکروں کو پکڑ لیا اور دریا برد کر دیا، اب دیکھ لے کہ ان گنہگاروں کا انجام کیسا کچھ ہوا؟'),
(3293, 28, 41, 'اور ہم نے انہیں ایسے امام بنا دیئے کہ لوگوں کو جہنم کی طرف بلائیں اور روز قیامت مطلق مدد نہ کیے جائیں'),
(3294, 28, 42, 'اور ہم نے اس دنیا میں بھی ان کے پیچھے اپنی لعنت لگا دی اور قیامت کے دن بھی وه بدحال لوگوں میں سے ہوں گے'),
(3295, 28, 43, 'اور ان اگلے زمانہ والوں کو ہلاک کرنے کے بعد ہم نے موسیٰ (علیہ السلام) کو ایسی کتاب عنایت فرمائی جو لوگوں کے لیے دلیل اور ہدایت ورحمت ہوکر آئی تھی تاکہ وه نصیحت حاصل کرلیں'),
(3296, 28, 44, 'اور طور کے مغربی جانب جب کہ ہم نے موسیٰ (علیہ السلام) کو حکم احکام کی وحی پہنچائی تھی، نہ تو تو موجود تھا اور نہ تو دیکھنے والوں میں سے تھا'),
(3297, 28, 45, 'لیکن ہم نے بہت سی نسلیں پیدا کیں جن پر لمبی مدتیں گزر گئیں، اور نہ تو مدین کے رہنے والوں میں سے تھا کہ ان کے سامنے ہماری آیتوں کی تلاوت کرتا بلکہ ہم ہی رسولوں کے بھیجنے والے رہے'),
(3298, 28, 46, 'اور نہ تو طور کی طرف تھا جب کہ ہم نے آواز دی بلکہ یہ تیرے پروردگار کی طرف سے ایک رحمت ہے، اس لیے کہ تو ان لوگوں کو ہوشیار کر دے جن کے پاس تجھ سے پہلے کوئی ڈرانے واﻻ نہیں پہنچا، کیا عجب کہ وه نصیحت حاصل کرلیں'),
(3299, 28, 47, 'اگر یہ بات نہ ہوتی کہ انہیں ان کے اپنے ہاتھوں آگے بھیجے ہوئے اعمال کی وجہ سے کوئی مصیبت پہنچتی تو یہ کہہ اٹھتے کہ اے ہمارے رب! تو نے ہماری طرف کوئی رسول کیوں نہ بھیجا؟ کہ ہم تیری آیتوں کی تابعداری کرتے اور ایمان والوں میں سے ہو جاتے'),
(3300, 28, 48, 'پھر جب ان کے پاس ہماری طرف سے حق آپہنچا تو کہتے ہیں کہ یہ وه کیوں نہیں دیا گیا جیسے دیئے گئے تھے موسیٰ (علیہ السلام) اچھا تو کیا موسیٰ (علیہ السلام) کو جو کچھ دیا گیا تھا اس کے ساتھ لوگوں نے کفر نہیں کیا تھا، صاف کہا تھا کہ یہ دونوں جادوگر ہیں جو ایک دوسرے کے مددگار ہیں اور ہم تو ان سب کے منکر ہیں'),
(3301, 28, 49, 'کہہ دے کہ اگر سچے ہو تو تم بھی اللہ کے پاس سے کوئی ایسی کتاب لے آؤ جو ان دونوں سے زیاده ہدایت والی ہو میں اسی کی پیروی کروں گا'),
(3302, 28, 50, 'پھر اگر یہ تیری نہ مانیں تو تو یقین کرلے کہ یہ صرف اپنی خواہش کی پیروی کر رہے ہیں۔ اور اس سے بڑھ کر بہکا ہوا کون ہے؟ جو اپنی خواہش کے پیچھے پڑا ہوا ہو بغیر اللہ کی رہنمائی کے، بیشک اللہ تعالیٰ ﻇالم لوگوں کو ہدایت نہیں دیتا'),
(3303, 28, 51, 'اور ہم برابر پے درپے لوگوں کے لیے اپنا کلام بھیجتے رہے تاکہ وه نصیحت حاصل کرلیں'),
(3304, 28, 52, 'جس کو ہم نے اس سے پہلے کتاب عنایت فرمائی وه تو اس پر بھی ایمان رکھتے ہیں'),
(3305, 28, 53, 'اور جب اس کی آیتیں ان کے پاس پڑھی جاتی ہیں تو وه کہہ دیتے ہیں کہ اس کے ہمارے رب کی طرف سے حق ہونے پر ہمارا ایمان ہے ہم تو اس سے پہلے ہی مسلمان ہیں'),
(3306, 28, 54, 'یہ اپنے کیے ہوئے صبر کے بدلے دوہرا دوہرا اجر دیئے جائیں گے۔ یہ نیکی سے بدی کو ٹال دیتے ہیں اور ہم نے جو انہیں دے رکھا ہے اس میں سے دیتے رہتے ہیں'),
(3307, 28, 55, 'اور جب بیہوده بات کان میں پڑتی ہے تو اس سے کناره کر لیتے ہیں اور کہہ دیتے ہیں کہ ہمارے عمل ہمارے لیے اور تمہارے اعمال تمہارے لیے، تم پر سلام ہو، ہم جاہلوں سے (الجھنا) نہیں چاہتے'),
(3308, 28, 56, 'آپ جسے چاہیں ہدایت نہیں کر سکتے بلکہ اللہ تعالیٰ ہی جسے چاہے ہدایت کرتا ہے۔ ہدایت والوں سے وہی خوب آگاه ہے'),
(3309, 28, 57, 'کہنے لگے اگر ہم آپ کے ساتھ ہوکر ہدایت کے تابع دار بن جائیں تو ہم تو اپنے ملک سے اچک لیے جائیں، کیا ہم نے انہیں امن وامان اور حرمت والے حرم میں جگہ نہیں دی؟ جہاں تمام چیزوں کے پھل کِھچے چلے آتے ہیں جو ہمارے پاس بطور رزق کے ہیں، لیکن ان میں سے اکثر کچھ نہیں جانتے'),
(3310, 28, 58, 'اور ہم نے بہت سی وه بستیاں تباه کر دیں جو اپنی عیش و عشرت میں اترانے لگی تھیں، یہ ہیں ان کی رہائش کی جگہیں جو ان کے بعد بہت ہی کم آباد کی گئیں اور ہم ہی ہیں آخر سب کچھ کے وارث'),
(3311, 28, 59, 'تیرا رب کسی ایک بستی کو بھی اس وقت تک ہلاک نہیں کرتا جب تک کہ ان کی کسی بڑی بستی میں اپنا کوئی پیغمبر نہ بھیج دے جو انہیں ہماری آیتیں پڑھ کر سنا دے اور ہم بستیوں کو اسی وقت ہلاک کرتے ہیں جب کہ وہاں والے ﻇلم وستم پر کمر کس لیں'),
(3312, 28, 60, 'اور تمہیں جو کچھ دیا گیا ہے وه صرف زندگی دنیا کا سامان اور اسی کی رونق ہے، ہاں اللہ کے پاس جو ہے وه بہت ہی بہتر اور دیرپا ہے۔ کیا تم نہیں سمجھتے'),
(3313, 28, 61, 'کیا وه شخص جس سے ہم نے نیک وعده کیا ہے جسے وه قطعاً پانے واﻻ ہے مثل اس شخص کے ہوسکتا ہے؟ جسے ہم نے زندگانیٴ دنیا کی کچھ یونہی سی منفعت دے دی پھر بالﺂخر وه قیامت کے روز پکڑا باندھا حاضر کیا جائے گا'),
(3314, 28, 62, 'اور جس دن اللہ تعالیٰ انہیں پکار کر فرمائے گا کہ تم جنہیں اپنے گمان میں میرا شریک ٹھہرا رہے تھے کہاں ہیں'),
(3315, 28, 63, 'جن پر بات آچکی وه جواب دیں گے کہ اے ہمارے پروردگار! یہی وه ہیں جنہیں ہم نے بہکا رکھا تھا، ہم نے انہیں اسی طرح بہکایا جس طرح ہم بہکے تھے، ہم تیری سرکار میں اپنی دست برداری کرتے ہیں، یہ ہماری عبادت نہیں کرتے تھے'),
(3316, 28, 64, 'کہا جائے گا کہ اپنے شریکوں کو بلاؤ، وه بلائیں گے لیکن انہیں وه جواب تک نہ دیں گے اور سب عذاب دیکھ لیں گے، کاش یہ لوگ ہدایت پا لیتے'),
(3317, 28, 65, 'اس دن انہیں بلا کر پوچھے گا کہ تم نے نبیوں کو کیا جواب دیا؟'),
(3318, 28, 66, 'پھر تو اس دن ان کی تمام دلیلیں گم ہو جائیں گی اور ایک دوسرے سے سوال تک نہ کریں گے'),
(3319, 28, 67, 'ہاں جو شخص توبہ کرلے ایمان لے آئے اور نیک کام کرے یقین ہے کہ وه نجات پانے والوں میں سے ہو جائے گا'),
(3320, 28, 68, 'اور آپ کا رب جو چاہتا ہے پیدا کرتا ہے اور جسے چاہتا ہے چن لیتا ہے، ان میں سے کسی کو کوئی اختیار نہیں، اللہ ہی کے لیے پاکی ہے وه بلند تر ہے ہر اس چیز سے کہ لوگ شریک کرتے ہیں'),
(3321, 28, 69, 'ان کے سینے جو کچھ چھپاتے اور جو کچھ ﻇاہر کرتے ہیں آپ کا رب سب کچھ جانتا ہے'),
(3322, 28, 70, 'وہی اللہ ہے اس کے سوا کوئی ﻻئق عبادت نہیں، دنیا اور آخرت میں اسی کی تعریف ہے۔ اسی کے لیے فرمانروائی ہے اور اسی کی طرف تم سب پھیرے جاؤ گے'),
(3323, 28, 71, 'کہہ دیجئے! کہ دیکھو تو سہی اگر اللہ تعالیٰ تم پر رات ہی رات قیامت تک برابر کر دے تو سوائے اللہ کے کون معبود ہے جو تمہارے پاس دن کی روشنی ﻻئے؟ کیا تم سنتے نہیں ہو؟'),
(3324, 28, 72, 'پوچھئے! کہ یہ بھی بتا دو کہ اگر اللہ تعالیٰ تم پر ہمیشہ قیامت تک دن ہی دن رکھے تو بھی سوائے اللہ تعالیٰ کے کوئی معبود ہے جو تمہارے پاس رات لے آئے؟ جس میں تم آرام حاصل کرو، کیا تم دیکھ نہیں رہے ہو؟'),
(3325, 28, 73, 'اسی نے تو تمہارے لیے اپنے فضل وکرم سے دن رات مقرر کر دیے ہیں کہ تم رات میں آرام کرو اور دن میں اس کی بھیجی ہوئی روزی تلاش کرو، یہ اس لیے کہ تم شکر ادا کرو'),
(3326, 28, 74, 'اور جس دن انہیں پکار کر اللہ تعالیٰ فرمائے گا کہ جنہیں تم میرے شریک خیال کرتے تھے وه کہاں ہیں؟'),
(3327, 28, 75, 'اور ہم ہر امت میں سے ایک گواه الگ کرلیں گے کہ اپنی دلیلیں پیش کرو پس اس وقت جان لیں گے کہ حق اللہ تعالیٰ کی طرف ہے، اور جو کچھ افترا وه جوڑتے تھے سب ان کے پاس سے کھو جائے گا'),
(3328, 28, 76, 'قارون تھا تو قوم موسیٰ سے، لیکن ان پر ﻇلم کرنے لگا تھا ہم نے اسے (اس قدر) خزانے دے رکھے تھے کہ کئی کئی طاقت ور لوگ بہ مشکل اس کی کنجیاں اٹھا سکتے تھے، ایک بار اس کی قوم نے اس سے کہا کہ اترا مت! اللہ تعالیٰ اترانے والوں سے محبت نہیں رکھتا'),
(3329, 28, 77, 'اور جو کچھ اللہ تعالی نے تجھے دے رکھا ہے اس میں سے آخرت کے گھر کی تلاش بھی رکھ اور اپنے دنیوی حصے کو بھی نہ بھول اور جیسے کہ اللہ نے تیرے ساتھ احسان کیا ہے تو بھی اچھا سلوک کر اور ملک میں فساد کا خواہاں نہ ہو، یقین مان کہ اللہ مفسدوں کو ناپسند رکھتا ہے'),
(3330, 28, 78, 'قارون نے کہا یہ سب کچھ مجھے میری اپنی سمجھ کی بنا پر ہی دیا گیا ہے، کیا اسے اب تک یہ نہیں معلوم کہ اللہ تعالیٰ نے اس سے پہلے بہت سے بستی والوں کو غارت کر دیا جو اس سے بہت زیاده قوت والے اور بہت بڑی جمع پونجی والے تھے۔ اور گنہگاروں سے ان کے گناہوں کی باز پرس ایسے وقت نہیں کی جاتی'),
(3331, 28, 79, 'پس قارون پوری آرائش کے ساتھ اپنی قوم کے مجمع میں نکلا، تو دنیاوی زندگی کے متوالے کہنے لگے کاش کہ ہمیں بھی کسی طرح وه مل جاتا جو قارون کو دیا گیا ہے۔ یہ تو بڑا ہی قسمت کا دھنی ہے'),
(3332, 28, 80, 'ذی علم لوگ انہیں سمجھانے لگے کہ افسوس! بہتر چیز تو وه ہے جو بطور ﺛواب انہیں ملے گی جو اللہ پر ایمان ﻻئیں اور نیک عمل کریں یہ بات انہی کے دل میں ڈالی جاتی ہے جو صبر وسہارا والے ہوں'),
(3333, 28, 81, '(آخرکار) ہم نے اسے اس کے محل سمیت زمین میں دھنسا دیا اور اللہ کے سوا کوئی جماعت اس کی مدد کے لیے تیار نہ ہوئی نہ وه خود اپنے بچانے والوں میں سے ہو سکا'),
(3334, 28, 82, 'اور جو لوگ کل اس کے مرتبہ پر پہنچنے کی آرزو مندیاں کر رہے تھے وه آج کہنے لگے کہ کیا تم نہیں دیکھتے کہ اللہ تعالیٰ ہی اپنے بندوں میں سے جس کے لیے چاہے روزی کشاده کر دیتا ہے اور تنگ بھی؟ اگر اللہ تعالیٰ ہم پر فضل نہ کرتا تو ہمیں بھی دھنسا دیتا، کیا دیکھتے نہیں ہو کہ ناشکروں کو کبھی کامیابی نہیں ہوتی؟'),
(3335, 28, 83, 'آخرت کا یہ بھلا گھر ہم ان ہی کے لیے مقرر کر دیتے ہیں جو زمین میں اونچائی بڑائی اور فخر نہیں کرتے نہ فساد کی چاہت رکھتے ہیں۔ پرہیزگاروں کے لیے نہایت ہی عمده انجام ہے'),
(3336, 28, 84, 'جو شخص نیکی ﻻئے گا اسے اس سے بہتر ملے گا اور جو برائی لے کر آئے گا تو ایسے بداعمالی کرنے والوں کو ان کے انہی اعمال کا بدلہ دیا جائے گا جو وه کرتے تھے'),
(3337, 28, 85, 'جس اللہ نے آپ پر قرآن نازل فرمایا ہے وه آپ کو دوباره پہلی جگہ ﻻنے واﻻ ہے، کہہ دیجئے! کہ میرا رب اسے بھی بخوبی جانتا ہے جو ہدایت ﻻیا ہے اور اسے بھی جو کھلی گمراہی میں ہے'),
(3338, 28, 86, 'آپ کو تو کبھی اس کا خیال بھی نہ گزرا تھا کہ آپ کی طرف کتاب نازل فرمائی جائے گی لیکن یہ آپ کے رب کی مہربانی سے اترا۔ اب آپ کو ہرگز کافروں کا مددگار نہ ہونا چاہیئے'),
(3339, 28, 87, 'خیال رکھیئے کہ یہ کفار آپ کو اللہ تعالیٰ کی آیتوں کی تبلیﻎ سے روک نہ دیں اس کے بعد کہ یہ آپ کی جانب اتاری گئیں، تو اپنے رب کی طرف بلاتے رہیں اور شرک کرنے والوں میں سے نہ ہوں'),
(3340, 28, 88, 'اللہ تعالیٰ کے ساتھ کسی اور معبود کو نہ پکارنا بجز اللہ تعالیٰ کے کوئی اور معبود نہیں، ہر چیز فنا ہونے والی ہے مگر اسی کا منھ (اور ذات) ۔ اسی کے لیے فرمانروائی ہے اور تم اسی کی طرف لوٹائے جاؤ گے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3341, 29, 1, 'الم'),
(3342, 29, 2, 'کیا لوگوں نے یہ گمان کر رکھا ہے کہ ان کے صرف اس دعوے پر کہ ہم ایمان ﻻئے ہیں ہم انہیں بغیر آزمائے ہوئے ہی چھوڑ دیں گے؟'),
(3343, 29, 3, 'ان سے اگلوں کو بھی ہم نے خوب جانچا۔ یقیناً اللہ تعالیٰ انہیں بھی جان لے گا جو سچ کہتے ہیں اور انہیں بھی معلوم کرلے گا جو جھوٹے ہیں'),
(3344, 29, 4, 'کیا جو لوگ برائیاں کر رہے ہیں انہوں نے یہ سمجھ رکھا ہے کہ وه ہمارے قابو سے باہر ہو جائیں گے، یہ لوگ کیسی بری تجویزیں کر رہے ہیں'),
(3345, 29, 5, 'جسے اللہ کی ملاقات کی امید ہو پس اللہ کا ٹھہرایا ہوا وقت یقیناً آنے واﻻ ہے، وه سب کچھ سننے واﻻ، سب کچھ جاننے واﻻ ہے'),
(3346, 29, 6, 'اور ہر ایک کوشش کرنے واﻻ اپنے ہی بھلے کی کوشش کرتا ہے۔ ویسے تو اللہ تعالیٰ تمام جہان والوں سے بےنیاز ہے'),
(3347, 29, 7, 'اور جن لوگوں نے یقین کیا اور مطابق سنت کام کیے ہم ان کے تمام گناہوں کو ان سے دور کر دیں گے اور انہیں ان کے نیک اعمال کے بہترین بدلے دیں گے'),
(3348, 29, 8, 'ہم نے ہر انسان کو اپنے ماں باپ کے ساتھ اچھا سلوک کرنے کی نصیحت کی ہے ہاں اگر وه یہ کوشش کریں کہ آپ میرے ساتھ اسے شریک کرلیں جس کا آپ کو علم نہیں تو ان کا کہنا نہ مانیئے، تم سب کا لوٹنا میری ہی طرف ہے پھر میں ہر اس چیز سے جو تم کرتے تھے تمہیں خبر دوں گا'),
(3349, 29, 9, 'اور جن لوگوں نے ایمان قبول کیا اور نیک کام کیے انہیں میں اپنے نیک بندوں میں شمار کر لوں گا'),
(3350, 29, 10, 'اور بعض لوگ ایسے بھی ہیں جو زبانی کہتے ہیں کہ ہم ایمان ﻻئے ہیں لیکن جب اللہ کی راه میں کوئی مشکل آن پڑتی ہے تو لوگوں کی ایذا دہی کو اللہ تعالیٰ کے عذاب کی طرح بنا لیتے ہیں، ہاں اگر اللہ کی مدد آجائے تو پکار اٹھتے ہیں کہ ہم تو تمہارے ساتھی ہی ہیں کیا دنیا جہان کے سینوں میں جو کچھ ہے اس سے اللہ تعالیٰ دانا نہیں ہے؟'),
(3351, 29, 11, 'جو لوگ ایمان ﻻئے اللہ انہیں بھی ﻇاہر کرکے رہے گا اور منافقوں کو بھی ﻇاہر کرکے رہے گا'),
(3352, 29, 12, 'کافروں نے ایماں والوں سے کہا کہ تم ہماری راه کی تابعداری کرو تمہارے گناه ہم اٹھا لیں گے، حاﻻنکہ وه ان کے گناہوں میں سے کچھ بھی نہیں اٹھانے والے، یہ تو محض جھوٹے ہیں'),
(3353, 29, 13, 'البتہ یہ اپنے بوجھ ڈھولیں گے اور اپنے بوجھوں کے ساتھ ہی اور بوجھ بھی۔ اور جو کچھ افترا پردازیاں کر رہے ہیں ان سب کی بابت ان سے باز پرس کی جائے گی'),
(3354, 29, 14, 'اور ہم نے نوح (علیہ السلام) کو ان کی قوم کی طرف بھیجا وه ان میں ساڑھے نو سو سال تک رہے، پھر تو انہیں طوفان نے دھر پکڑا اور وه تھے بھی ﻇالم'),
(3355, 29, 15, 'پھر ہم نے انہیں اور کشتی والوں کو نجات دی اور اس واقعہ کو ہم نے تمام جہان کے لیے عبرت کا نشان بنا دیا'),
(3356, 29, 16, 'اور ابراہیم (علیہ السلام) نے بھی اپنی قوم سے فرمایا کہ اللہ تعالیٰ کی عبادت کرو اوراس سے ڈرتے رہو، اگر تم میں دانائی ہے تو یہی تمہارے لیے بہتر ہے'),
(3357, 29, 17, 'تم تو اللہ تعالیٰ کے سوا بتوں کی پوجا پاٹ کر رہے ہو اور جھوٹی باتیں دل سے گھڑ لیتے ہو۔ سنو! جن جنکی تم اللہ تعالیٰ کے سوا پوجا پاٹ کر رہے ہو وه تو تمہاری روزی کے مالک نہیں پس تمہیں چاہئے کہ تم اللہ تعالیٰ ہی سے روزیاں طلب کرو اور اسی کی عبادت کرو اور اسی کی شکر گزاری کرو اور اسی کی طرف تم لوٹائے جاؤ گے'),
(3358, 29, 18, 'اور اگر تم جھٹلاؤ تو تم سے پہلے کی امتوں نے بھی جھٹلایا ہے، رسول کے ذمہ تو صرف صاف طور پر پہنچا دینا ہی ہے'),
(3359, 29, 19, 'کیا انہوں نے نہیں دیکھا کہ مخلوق کی ابتدا کس طرح اللہ نے کی پھر اللہ اس کا اعاده کرے گا، یہ تو اللہ تعالیٰ پر بہت ہی آسان ہے'),
(3360, 29, 20, 'کہہ دیجئے! کہ زمین میں چل پھر کر دیکھو تو سہی کہ کس طرح اللہ تعالیٰ نے ابتداءً پیدائش کی۔ پھر اللہ تعالیٰ ہی دوسری نئی پیدائش کرے گا، اللہ تعالیٰ ہر چیز پر قادر ہے'),
(3361, 29, 21, 'جسے چاہے عذاب کرے جس پر چاہے رحم کرے، سب اسی کی طرف لوٹائے جاؤ گے'),
(3362, 29, 22, 'تم نہ تو زمین میں اللہ تعالیٰ کو عاجز کرسکتے ہو نہ آسمان میں، اللہ تعالیٰ کے سوا تمہارا کوئی والی ہے نہ مددگار'),
(3363, 29, 23, 'جو لوگ اللہ تعالیٰ کی آیتوں اور اس کی ملاقات کو بھلاتے ہیں وه میری رحمت سے ناامید ہو جائیں اور ان کے لیے دردناک عذاب ہے'),
(3364, 29, 24, 'ان کی قوم کا جواب بجز اس کے کچھ نہ تھا کہ کہنے لگے کہ اسے مار ڈالو یا اسے جلا دو۔ آخر اللہ نے انہیں آگ سے بچا لیا، اس میں ایمان والے لوگوں کے لیے تو بہت سی نشانیاں ہیں'),
(3365, 29, 25, '(حضرت ابراہیم علیہ السلام نے) کہا کہ تم نے جن بتوں کی پرستش اللہ کے سوا کی ہے انہیں تم نے اپنی آپس کی دنیوی دوستی کی بنا ٹھہرا لی ہے، تم سب قیامت کے دن ایک دوسرے سے کفر کرنے لگو گے اور ایک دوسرے پر لعنت کرنے لگو گے۔ اور تمہارا سب کا ٹھکانہ دوزخ ہوگا اور تمہارا کوئی مددگار نہ ہوگا'),
(3366, 29, 26, 'پس حضرت ابراہیم (علیہ السلام) پر حضرت لوط (علیہ السلام) ایمان ﻻئے اور کہنے لگے کہ میں اپنے رب کی طرف ہجرت کرنے واﻻ ہوں۔ وه بڑا ہی غالب اور حکیم ہے'),
(3367, 29, 27, 'اور ہم نے انھیں (ابراہیم کو) اسحاق ویعقوب (علیہما السلام) عطا کیے اور ہم نے نبوت اور کتاب ان کی اوﻻد میں ہی کر دی اور ہم نے دنیا میں بھی اسے ﺛواب دیا اور آخرت میں تو وه صالح لوگوں میں سے ہیں'),
(3368, 29, 28, 'اور حضرت لوط (علیہ السلام) کا بھی ذکر کرو جب کہ انہوں نے اپنی قوم سے فرمایا کہ تم تو اس بدکاری پر اتر آئے ہو جسے تم سے پہلے دنیا بھر میں سے کسی نے نہیں کیا'),
(3369, 29, 29, 'کیا تم مردوں کے پاس بدفعلی کے لیے آتے ہو اور راستے بند کرتے ہو اور اپنی عام مجلسوں میں بے حیائیوں کا کام کرتے ہو؟ اس کے جواب میں اس کی قوم نے بجز اس کے اور کچھ نہیں کہا کہ بس جا اگر سچا ہے تو ہمارے پاس اللہ تعالیٰ کا عذاب لے آ'),
(3370, 29, 30, 'حضرت لوط (علیہ السلام) نے دعا کی کہ پروردگار! اس مفسد قوم پر میری مدد فرما'),
(3371, 29, 31, 'اور جب ہمارے بھیجے ہوئے فرشتے ابراہیم (علیہ السلام) کے پاس بشارت لے کر پہنچے کہنے لگے کہ اس بستی والوں کو ہم ہلاک کرنے والے ہیں، یقیناً یہاں کے رہنے والے گنہگار ہیں'),
(3372, 29, 32, '(حضرت ابراہیم علیہ السلام نے) کہا اس میں تو لوط (علیہ السلام) ہیں، فرشتوں نے کہا یہاں جو ہیں ہم انہیں بخوبی جانتے ہیں۔ لوط (علیہ السلام) کو اور اس کے خاندان کو سوائے اس کی بیوی کے ہم بچالیں گے، البتہ وه عورت پیچھے ره جانے والوں میں سے ہے'),
(3373, 29, 33, 'پھر جب ہمارے قاصد لوط (علیہ السلام) کے پاس پہنچے تو وه ان کی وجہ سے غمگین ہوئے اور دل ہی دل میں رنج کرنے لگے۔ قاصدوں نے کہا آپ نہ خوف کھایئے نہ آزرده ہوں، ہم آپ کو مع آپ کے متعلقین کے بچالیں گے مگر آپ کی بیوی کہ وه عذاب کے لیے باقی ره جانے والوں میں سے ہوگی'),
(3374, 29, 34, 'ہم اس بستی والوں پر آسمانی عذاب نازل کرنے والے ہیں اس وجہ سے کہ یہ بےحکم ہو رہے ہیں'),
(3375, 29, 35, 'البتہ ہم نے اس بستی کو صریح عبرت کی نشانی بنا دیا ان لوگوں کے لیے جو عقل رکھتے ہیں'),
(3376, 29, 36, 'اور مدین کی طرف ہم نے ان کے بھائی شعیب (علیہ السلام) کو بھیجا انہوں نے کہا اے میری قوم کے لوگو! اللہ کی عبادت کرو قیامت کے دن کی توقع رکھو اور زمین میں فساد نہ کرتے پھرو'),
(3377, 29, 37, 'پھر بھی انہوں نے انہیں جھٹلایا آخر انہیں زلزلے نے پکڑ لیا اور وه اپنے گھروں میں بیٹھے کے بیٹھے مرده ہو کر ره گئے'),
(3378, 29, 38, 'اور ہم نے عادیوں اور ﺛمودیوں کو بھی غارت کیا جن کے بعض مکانات تمہارے سامنے ﻇاہر ہیں اور شیطان نے انہیں ان کی بداعمالیاں آراستہ کر دکھائی تھیں اور انہیں راه سے روک دیا تھا باوجودیکہ یہ آنکھوں والے اور ہوشیار تھے'),
(3379, 29, 39, 'اور قارون اور فرعون اور ہامان کو بھی، ان کے پاس حضرت موسیٰ (علیہ السلام) کھلے کھلے معجزے لے کر آئے تھے پھر بھی انہوں نے زمین میں تکبر کیا لیکن ہم سے آگے بڑھنے والے نہ ہو سکے'),
(3380, 29, 40, 'پھر تو ہر ایک کو ہم نے اس کے گناه کے وبال میں گرفتار کر لیا، ان میں سے بعض پر ہم نے پتھروں کا مینہ برسایا اور ان میں سے بعض کو زور دار سخت آواز نے دبوچ لیا اور ان میں سے بعض کو ہم نے زمین میں دھنسا دیا اور ان میں سے بعض کو ہم نے ڈبو دیا، اللہ تعالیٰ ایسا نہیں کہ ان پر ﻇلم کرے بلکہ یہی لوگ اپنی جانوں پر ﻇلم کرتے تھے'),
(3381, 29, 41, 'جن لوگوں نے اللہ تعالیٰ کے سوا اور کارساز مقرر کر رکھے ہیں ان کی مثال مکڑی کی سی ہے کہ وه بھی ایک گھر بنا لیتی ہے، حاﻻنکہ تمام گھروں سے زیاده بودا گھر مکڑی کا گھر ہی ہے، کاش! وه جان لیتے'),
(3382, 29, 42, 'اللہ تعالیٰ ان تمام چیزوں کو جانتا ہے جنہیں وه اس کے سوا پکار رہے ہیں، وه زبردست اور ذی حکمت ہے'),
(3383, 29, 43, 'ہم ان مثالوں کو لوگوں کے لیے بیان فرما رہے ہیں انہیں صرف علم والے ہی سمجھتے ہیں'),
(3384, 29, 44, 'اللہ تعالیٰ نے آسمانوں اور زمین کو مصلحت اور حق کے ساتھ پیدا کیا ہے، ایمان والوں کے لیے تو اس میں بڑی بھاری دلیل ہے'),
(3385, 29, 45, 'جو کتاب آپ کی طرف وحی کی گئی ہے اسے پڑھئے اور نماز قائم کریں، یقیناً نماز بے حیائی اور برائی سے روکتی ہے، بیشک اللہ کا ذکر بہت بڑی چیز ہے، تم جو کچھ کر رہے ہو اس سے اللہ خبردار ہے'),
(3386, 29, 46, 'اور اہل کتاب کے ساتھ بحﺚ ومباحثہ نہ کرو مگر اس طریقہ پر جو عمده ہو مگر ان کے ساتھ جو ان میں ﻇالم ہیں اور صاف اعلان کر دو کہ ہمارا تو اس کتاب پر بھی ایمان ہے اور جو ہم پر اتاری گئی ہے اور اس پر بھی جو تم پر اتاری گئی، ہمارا تمہارا معبود ایک ہی ہے۔ ہم سب اسی کے حکم برادر ہیں'),
(3387, 29, 47, 'اور ہم نے اسی طرح آپ کی طرف اپنی کتاب نازل فرمائی ہے، پس جنہیں ہم نے کتاب دی ہے وه اس پر ایمان ﻻتے ہیں اور ان (مشرکین) میں سے بعض اس پر ایمان رکھتے ہیں اور ہماری آیتوں کا انکار صرف کافر ہی کرتے ہیں'),
(3388, 29, 48, 'اس سے پہلے تو آپ کوئی کتاب پڑھتے نہ تھے اور نہ کسی کتاب کو اپنے ہاتھ سے لکھتے تھے کہ یہ باطل پرست لوگ شک وشبہ میں پڑتے'),
(3389, 29, 49, 'بلکہ یہ (قرآن) تو روشن آیتیں ہیں جو اہل علم کے سینوں میں محفوظ ہیں، ہماری آیتوں کا منکر بجز ﻇالموں کے اور کوئی نہیں'),
(3390, 29, 50, 'انہوں نے کہا کہ اس پر کچھ نشانیاں (معجزات) اس کے رب کی طرف سے کیوں نہیں اتارے گئے۔ آپ کہہ دیجئے کہ نشانیاں تو سب اللہ تعالیٰ کے پاس ہیں میں تو صرف کھلم کھلا آگاه کر دینے واﻻ ہوں'),
(3391, 29, 51, 'کیا انہیں یہ کافی نہیں؟ کہ ہم نے آپ پر کتاب نازل فرما دی جو ان پر پڑھی جا رہی ہے، اس میں رحمت (بھی) ہے اور نصیحت (بھی) ہے ان لوگوں کے لئے جو ایمان والے ہیں'),
(3392, 29, 52, 'کہہ دیجئے کہ مجھ میں اور تم میں اللہ تعالیٰ گواه ہونا کافی ہے وه آسمان وزمین کی ہر چیز کا عالم ہے، جو لوگ باطل کے ماننے والے اور اللہ تعالیٰ سے کفر کرنے والے ہیں وه زبردست نقصان اور گھاٹے میں ہیں'),
(3393, 29, 53, 'یہ لوگ آپ سے عذاب کی جلدی کر رہے ہیں۔ اگر میری طرف سے مقرر کیا ہوا وقت نہ ہوتا تو ابھی تک ان کے پاس عذاب آچکا ہوتا، یہ یقینی بات ہے کہ اچانک ان کی بے خبری میں ان کے پاس عذاب آپہنچے گا'),
(3394, 29, 54, 'یہ عذاب کی جلدی مچا رہے ہیں اور (تسلی رکھیں) جہنم کافروں کو گھیر لینے والی ہے'),
(3395, 29, 55, 'اس دن ان کے اوپر تلے سے عذاب ڈھانﭗ رہا ہوگا اور اللہ تعالیٰ فرمائے گا کہ اب اپنے (بد) اعمال کا مزه چکھو'),
(3396, 29, 56, 'اے میرے ایمان والے بندو! میری زمین بہت کشاده ہے سو تم میری ہی عبادت کرو'),
(3397, 29, 57, 'ہر جاندار موت کا مزه چکھنے واﻻ ہےاور تم سب ہماری ہی طرف لوٹائے جاؤ گے'),
(3398, 29, 58, 'اور جولوگ ایمان ﻻئے اور نیک کام کیے انہیں ہم یقیناً جنت کے ان باﻻ خانوں میں جگہ دینگے جن کے نیچے چشمے بہہ رہے ہیں جہاں وه ہمیشہ رہیں گے، کام کرنے والوں کا کیا ہی اچھا اجر ہے'),
(3399, 29, 59, 'وه جنہوں نے صبر کیا، اور اپنے رب تعالیٰ پر بھروسہ رکھتے ہیں'),
(3400, 29, 60, 'اور بہت سے جانور ہیں جو اپنی روزی اٹھائے نہیں پھرتے، ان سب کو اور تمہیں بھی اللہ تعالیٰ ہی روزی دیتا ہے، وه بڑا ہی سننے جاننے واﻻ ہے'),
(3401, 29, 61, 'اور اگر آپ ان سے دریافت کریں کہ زمین وآسمان کا خالق اور سورج چاند کو کام میں لگانے واﻻ کون ہے؟ تو ان کا جواب یہی ہوگا کہ اللہ تعالیٰ، پھر کدھر الٹے جا رہے ہیں'),
(3402, 29, 62, 'اللہ تعالیٰ اپنے بندوں میں سے جسے چاہے فراخ روزی دیتا ہے اور جسے چاہے تنگ۔ یقیناً اللہ تعالیٰ ہر چیز کا جاننے واﻻ ہے'),
(3403, 29, 63, 'اور اگر آپ ان سے سوال کریں کہ آسمان سے پانی اتار کر زمین کو اس کی موت کے بعد زنده کس نے کیا؟ تو یقیناً ان کا جواب یہی ہوگا اللہ تعالیٰ نے۔ آپ کہہ دیں کہ ہر تعریف اللہ ہی کے لئے سزاوار ہے، بلکہ ان میں سے اکثر بے عقل ہیں'),
(3404, 29, 64, 'اور دنیا کی یہ زندگانی تو محض کھیل تماشا ہے البتہ آخرت کے گھر کی زندگی ہی حقیقی زندگی ہے، کاش! یہ جانتے ہوتے'),
(3405, 29, 65, 'پس یہ لوگ جب کشتیوں میں سوار ہوتے ہیں تو اللہ تعالیٰ ہی کو پکارتے ہیں اس کے لئے عبادت کو خالص کر کے پھر جب وه انہیں خشکی کی طرف بچا ﻻتا ہے تو اسی وقت شرک کرنے لگتے ہیں'),
(3406, 29, 66, 'تاکہ ہماری دی ہوئی نعمتوں سے مکرتے رہیں اور برتتے رہیں۔ ابھی ابھی پتہ چل جائے گا'),
(3407, 29, 67, 'کیا یہ نہیں دیکھتے کہ ہم نے حرم کو با امن بنا دیا ہے حاﻻنکہ ان کے اردگرد سے لوگ اچک لیے جاتے ہیں، کیا یہ باطل پر تو یقین رکھتے ہیں اور اللہ تعالیٰ کی نعمتوں پر ناشکری کرتے ہیں'),
(3408, 29, 68, 'اور اس سے بڑا ﻇالم کون ہوگا؟ جو اللہ تعالیٰ پر جھوٹ باندھے یا جب حق اس کے پاس آجائے وه اسے جھٹلائے، کیا ایسے کافروں کا ٹھکانا جہنم میں نہ ہوگا؟'),
(3409, 29, 69, 'اور جو لوگ ہماری راه میں مشقتیں برداشت کرتے ہیں ہم انہیں اپنی راہیں ضرور دکھا دیں گے۔ یقیناً اللہ تعالیٰ نیکو کاروں کا ساتھی ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3410, 30, 1, 'الم'),
(3411, 30, 2, 'رومی مغلوب ہوگئے ہیں'),
(3412, 30, 3, 'نزدیک کی زمین پر اور وه مغلوب ہونے کے بعد عنقریب غالب آجائیں گے'),
(3413, 30, 4, 'چند سال میں ہی۔ اس سے پہلے اور اس کے بعد بھی اختیار اللہ تعالیٰ ہی کا ہے۔ اس روز مسلمان شادمان ہوں گے'),
(3414, 30, 5, 'اللہ کی مدد سے، وه جس کی چاہتا ہے مدد کرتا ہے۔ اصل غالب اور مہربان وہی ہے'),
(3415, 30, 6, 'اللہ کا وعده ہے، اللہ تعالیٰ اپنے وعدے کا خلاف نہیں کرتا لیکن اکثر لوگ نہیں جانتے'),
(3416, 30, 7, 'وه تو (صرف) دنیوی زندگی کے ﻇاہر کو (ہی) جانتے ہیں اور آخرت سے تو بالکل ہی بےخبر ہیں'),
(3417, 30, 8, 'کیا ان لوگوں نے اپنے دل میں یہ غور نہیں کیا؟ کہ اللہ تعالیٰ نے آسمانوں کو اور زمین اور ان کے درمیان جو کچھ ہے سب کو بہترین قرینے سے مقرر وقت تک کے لئے (ہی) پیدا کیا ہے، ہاں اکثر لوگ یقیناً اپنے رب کی ملاقات کے منکر ہیں'),
(3418, 30, 9, 'کیا انہوں نے زمین پر چل پھر کر یہ نہیں دیکھا کہ ان سے پہلے لوگوں کا انجام کیسا (برا) ہوا؟ وه ان سے بہت زیاده توانا (اور طاقتور) تھے اور انہوں نے (بھی) زمین بوئی جوتی تھی اور ان سے زیاده آباد کی تھی اور ان کے پاس ان کے رسول روشن دﻻئل لے کر آئے تھے۔ یہ تو ناممکن تھا کہ اللہ تعالیٰ ان پر ﻇلم کرتا لیکن (دراصل) وه خود اپنی جانوں پر ﻇلم کرتے تھے'),
(3419, 30, 10, 'پھر آخر برا کرنے والوں کا بہت ہی برا انجام ہوا، اس لئے کہ وه اللہ تعالیٰ کی آیتوں کو جھٹلاتے اور ان کی ہنسی اڑاتے تھے'),
(3420, 30, 11, 'اللہ تعالیٰ ہی مخلوق کی ابتدا کرتا ہے پھر وہی اسے دوباره پیدا کرے گا پھر تم سب اسی کی طرف لوٹائے جاؤ گے'),
(3421, 30, 12, 'اور جس دن قیامت قائم ہوگی تو گنہگار حیرت زده ره جائیں گے'),
(3422, 30, 13, 'اور ان کے تمام تر شریکوں میں سے ایک بھی ان کا سفارشی نہ ہوگا اور (خود یہ بھی) اپنے شریکوں کے منکر ہو جائیں گے'),
(3423, 30, 14, 'اور جس دن قیامت قائم ہوگی اس دن (جماعتیں) الگ الگ ہو جائیں گی'),
(3424, 30, 15, 'جو ایمان ﻻکر نیک اعمال کرتے رہے وه تو جنت میں خوش وخرم کر دیئے جائیں گے'),
(3425, 30, 16, 'اور جنہوں نے کفر کیا تھا اور ہماری آیتوں کو اور آخرت کی ملاقات کو جھوٹا ٹھہرایا تھا وه سب عذاب میں پکڑ کر حاضر رکھے جائیں گے'),
(3426, 30, 17, 'پس اللہ تعالیٰ کی تسبیح پڑھا کرو جب کہ تم شام کرو اور جب صبح کرو'),
(3427, 30, 18, 'تمام تعریفوں کے ﻻئق آسمان وزمین میں صرف وہی ہے تیسرے پہر کو اور ﻇہر کے وقت بھی (اس کی پاکیزگی بیان کرو)'),
(3428, 30, 19, '(وہی) زنده کو مرده سے اور مرده کو زنده سے نکالتا ہے۔ اور وہی زمین کو اس کی موت کے بعد زنده کرتا ہے اسی طرح تم (بھی) نکالے جاؤ گے'),
(3429, 30, 20, 'اللہ کی نشانیوں میں سے ہے کہ اس نے تم کو مٹی سے پیدا کیا پھر اب انسان بن کر (چلتے پھرتے) پھیل رہے ہو'),
(3430, 30, 21, 'اور اس کی نشانیوں میں سے ہے کہ تمہاری ہی جنس سے بیویاں پیدا کیں تاکہ تم ان سے آرام پاؤ اس نے تمہارے درمیان محبت اور ہمدردی قائم کر دی، یقیناً غور وفکر کرنے والوں کے لئے اس میں بہت سی نشانیاں ہیں'),
(3431, 30, 22, 'اس (کی قدرت) کی نشانیوں میں سے آسمانوں اور زمین کی پیدائش اور تمہاری زبانوں اور رنگتوں کا اختلاف (بھی) ہے، دانش مندوں کے لئے اس میں یقیناً بڑی نشانیاں ہیں'),
(3432, 30, 23, 'اور (بھی) اس کی (قدرت کی) نشانی تمہاری راتوں اور دن کی نیند میں ہے اور اس کے فضل (یعنی روزی) کو تمہارا تلاش کرنا بھی ہے۔ جو لوگ (کان لگا کر) سننے کے عادی ہیں ان کے لئے اس میں بہت سی نشانیاں ہیں'),
(3433, 30, 24, 'اور اس کی نشانیوں میں سے ایک یہ (بھی) ہے کہ وه تمہیں ڈرانے اور امیدوار بنانے کے لئے بجلیاں دکھاتا ہے اور آسمان سے بارش برساتا ہے اور اس مرده زمین کو زنده کر دیتا ہے، اس میں (بھی) عقلمندوں کے لئے بہت سی نشانیاں ہیں'),
(3434, 30, 25, 'اس کی ایک نشانی یہ بھی ہے کہ آسمان وزمین اسی کے حکم سے قائم ہیں، پھر جب وه تمہیں آواز دے گا صرف ایک بار کی آواز کے ساتھ ہی تم سب زمین سے نکل آؤ گے'),
(3435, 30, 26, 'اور زمین وآسمان کی ہر ہر چیز اس کی ملکیت ہےاور ہر ایک اس کے فرمان کے ماتحت ہے'),
(3436, 30, 27, 'وہی ہے جو اول بار مخلوق کو پیدا کرتا ہے پھر سے دوباره پیدا کرے گا اور یہ تو اس پر بہت ہی آسان ہے۔ اسی کی بہترین اور اعلیٰ صفت ہے، آسمانوں میں اور زمین میں بھی اور وہی غلبے واﻻ حکمت واﻻ ہے'),
(3437, 30, 28, 'اللہ تعالیٰ نے تمہارے لئے ایک مثال خود تمہاری ہی بیان فرمائی، جو کچھ ہم نے تمہیں دے رکھا ہے کیا اس میں تمہارے غلاموں میں سے بھی کوئی تمہارا شریک ہے؟ کہ تم اور وه اس میں برابر درجے کے ہو؟ اور تم ان کا ایسا خطره رکھتے ہو جیسا خود اپنوں کا، ہم عقل رکھنے والوں کے لئے اسی طرح کھول کھول کر آیتیں بیان کر دیتے ہیں'),
(3438, 30, 29, 'بلکہ بات یہ ہے کہ یہ ﻇالم تو بغیر علم کے خواہش پرستی کر رہے ہیں، اسے کون راه دکھائے جسے اللہ تعالیٰ راه سے ہٹا دے، ان کا ایک بھی مددگار نہیں'),
(3439, 30, 30, 'پس آپ یک سو ہو کر اپنا منھ دین کی طرف متوجہ کر دیں۔ اللہ تعالیٰ کی وه فطرت جس پر اس نے لوگوں کو پیدا کیا ہے، اللہ تعالیٰ کے بنائے کو بدلنا نہیں، یہی سیدھا دین ہے لیکن اکثر لوگ نہیں سمجھتے'),
(3440, 30, 31, '(لوگو!) اللہ تعالیٰ کی طرف رجوع ہو کر اس سے ڈرتے رہو اور نماز کو قائم رکھو اور مشرکین میں سے نہ ہو جاؤ'),
(3441, 30, 32, 'ان لوگوں میں سے جنہوں نے اپنے دین کو ٹکڑے ٹکڑے کر دیا اور خود بھی گروه گروه ہوگئے ہر گروه اس چیز پر جو اس کے پاس ہے مگن ہے'),
(3442, 30, 33, 'لوگوں کو جب کبھی کوئی مصیبت پہنچتی ہے تو اپنے رب کی طرف (پوری طرح) رجوع ہو کر دعائیں کرتے ہیں، پھر جب وه اپنی طرف سے رحمت کاذائقہ چکھاتا ہے تو ان میں سے ایک جماعت اپنے رب کے ساتھ شرک کرنے لگتی ہے'),
(3443, 30, 34, 'تاکہ وه اس چیز کی ناشکری کریں جو ہم نے انہیں دی ہے اچھا تم فائده اٹھا لو! ابھی ابھی تمہیں معلوم ہو جائے گا'),
(3444, 30, 35, 'کیا ہم نے ان پر کوئی دلیل نازل کی ہے جو اسے بیان کرتی ہے جسے یہ اللہ کے ساتھ شریک کر رہے ہیں'),
(3445, 30, 36, 'اور جب ہم لوگوں کو رحمت کا مزه چکھاتے ہیں تو وه خوب خوش ہو جاتے ہیں اور اگر انہیں ان کے ہاتھوں کے کرتوت کی وجہ سے کوئی برائی پہنچے تو ایک دم وه محض ناامید ہو جاتے ہیں'),
(3446, 30, 37, 'کیا انہوں نے نہیں دیکھا کہ اللہ تعالیٰ جسے چاہے کشاده روزی دیتا ہے اور جسے چاہے تنگ، اس میں بھی ان لوگوں کے لئے جو ایمان ﻻتے ہیں نشانیاں ہیں'),
(3447, 30, 38, 'پس قرابت دار کو مسکین کو مسافر کو ہر ایک کو اس کا حق دیجئے، یہ ان کے لئے بہتر ہے جو اللہ تعالیٰ کا منھ دیکھنا چاہتے ہوں، ایسے ہی لوگ نجات پانے والے ہیں'),
(3448, 30, 39, 'تم جو سود پر دیتے ہو کہ لوگوں کے مال میں بڑھتا رہے وه اللہ تعالیٰ کے ہاں نہیں بڑھتا۔ اور جو کچھ صدقہ زکوٰة تم اللہ تعالیٰ کا منھ دیکھنے (اورخوشنودی کے لئے) دو تو ایسے لوگ ہی اپنا دو چند کرنے والے ہیں'),
(3449, 30, 40, 'اللہ تعالیٰ وه ہے جس نے تمہیں پیدا کیا پھر روزی دی پھر مار ڈالے گا پھر زنده کر دے گا بتاؤ تمہارے شریکوں میں سے کوئی بھی ایسا ہے جو ان میں سے کچھ بھی کر سکتا ہو۔ اللہ تعالیٰ کے لئے پاکی اور برتری ہے ہر اس شریک سے جو یہ لوگ مقرر کرتے ہیں'),
(3450, 30, 41, 'خشکی اور تری میں لوگوں کی بداعمالیوں کے باعﺚ فساد پھیل گیا۔ اس لئے کہ انہیں ان کے بعض کرتوتوں کا پھل اللہ تعالیٰ چکھا دے (بہت) ممکن ہے کہ وه باز آجائیں۔'),
(3451, 30, 42, 'زمین میں چل پھر کر دیکھو تو سہی کہ اگلوں کا انجام کیا ہوا۔ جن میں اکثر لوگ مشرک تھے'),
(3452, 30, 43, 'پس آپ اپنا رخ اس سچے اور سیدھے دین کی طرف ہی رکھیں قبل اس کے کہ وه دن آجائے جس کا ٹل جانا اللہ تعالیٰ کی طرف سے ہے ہی نہیں، اس دن سب متفرق ہو جائیں گے'),
(3453, 30, 44, 'کفر کرنے والوں پر ان کے کفر کا وبال ہوگا اور نیک کام کرنے والے اپنی ہی آرام گاه سنوار رہے ہیں'),
(3454, 30, 45, 'تاکہ اللہ تعالیٰ انہیں اپنے فضل سے جزا دے جو ایمان ﻻئے اور نیک اعمال کیے وه کافروں کو دوست نہیں رکھتا ہے'),
(3455, 30, 46, 'اس کی نشانیوں میں سے خوشخبریاں دینے والی ہواؤں کو چلانا بھی ہے اس لئے کہ تمہیں اپنی رحمت سے لطف اندوز کرے، اور اس لئے کہ اس کے حکم سے کشتیاں چلیں، اور اس لئے کہ اس کے فضل کو تم ڈھونڈو، اور اس لئے کہ تم شکر گزاری کرو'),
(3456, 30, 47, 'اور ہم نے آپ سے پہلے بھی اپنے رسولوں کو ان کی قوم کی طرف بھیجا وه ان کے پاس دلیلیں ﻻئے۔ پھر ہم نے گناه گاروں سے انتقام لیا۔ ہم پر مومنوں کی مدد کرنا ﻻزم ہے'),
(3457, 30, 48, 'اللہ تعالیٰ ہوائیں چلاتا ہے وه ابر کو اٹھاتی ہیں پھر اللہ تعالیٰ اپنی منشا کے مطابق اسے آسمان میں پھیلا دیتا ہے اور اس کے ٹکڑے ٹکڑے کر دیتا ہے پھر آپ دیکھتے ہیں کہ اس کےاندر سے قطرے نکلتے ہیں، اور جنہیں اللہ چاہتا ہے ان بندوں پر پانی برساتا ہے تو وه خوش خوش ہو جاتے ہیں'),
(3458, 30, 49, 'یقین ماننا کہ بارش ان پر برسنے سے پہلے پہلے تو وه ناامید ہو رہے تھے'),
(3459, 30, 50, 'پس آپ رحمت الٰہی کے آﺛار دیکھیں کہ زمین کی موت کے بعد کس طرح اللہ تعالیٰ اسے زنده کر دیتا ہے؟ کچھ شک نہیں کہ وہی مردوں کو زنده کرنے واﻻ ہے، اور وه ہر ہر چیز پر قادر ہے'),
(3460, 30, 51, 'اور اگر ہم باد تند چلا دیں اور یہ لوگ انہی کھیتوں کو (مرجھائی ہوئی) زرد پڑی ہوئی دیکھ لیں تو پھر اس کے بعد ناشکری کرنے لگیں'),
(3461, 30, 52, 'بیشک آپ مردوں کو نہیں سنا سکتے اور نہ بہروں کو (اپنی) آواز سنا سکتے ہیں جب کہ وه پیٹھ پھیر کر مڑ گئے ہوں'),
(3462, 30, 53, 'اور نہ آپ اندھوں کو ان کی گمراہی سے ہدایت کرنے والے ہیں آپ تو صرف ان ہی لوگوں کو سناتے ہیں جو ہماری آیتوں پر ایمان رکھتے ہیں پس وہی اطاعت کرنے والے ہیں'),
(3463, 30, 54, 'اللہ تعالیٰ وه ہے جس نے تمہیں کمزوری کی حالت میں پیدا کیا پھر اس کمزوری کے بعد توانائی دی، پھر اس توانائی کے بعد کمزوری اور بڑھاپا دیا جو چاہتا ہے پیدا کرتا ہے، وه سب سے پورا واقف اور سب پر پورا قادر ہے'),
(3464, 30, 55, 'اور جس دن قیامت برپا ہو جائے گی گناه گار لوگ قسمیں کھائیں گے کہ (دنیا میں) ایک گھڑی کے سوا نہیں ٹھہرے، اسی طرح یہ بہکے ہوئے ہی رہے'),
(3465, 30, 56, 'اور جن لوگوں کو علم اور ایمان دیا گیا وه جواب دیں گے کہ تم تو جیسا کہ کتاب اللہ میں ہے یوم قیامت تک ٹھہرے رہے۔ آج کا یہ دن قیامت ہی کا دن ہے لیکن تم تو یقین ہی نہیں مانتے تھے'),
(3466, 30, 57, 'پس اس دن ﻇالموں کو ان کا عذر بہانہ کچھ کام نہ آئے گا اور نہ ان سے توبہ اور عمل طلب کیا جائے گا'),
(3467, 30, 58, 'بیشک ہم نےاس قرآن میں لوگوں کے سامنے کل مثالیں بیان کر دی ہیں۔ آپ ان کے پاس کوئی بھی نشانی ﻻئیں، یہ کافر تو یہی کہیں گے کہ تم (بے ہوده گو) بالکل جھوٹے ہو'),
(3468, 30, 59, 'اللہ تعالیٰ ان لوگوں کے دلوں پر جو سمجھ نہیں رکھتے یوں ہی مہر کر دیتا ہے'),
(3469, 30, 60, 'پس آپ صبر کریں یقیناً اللہ کا وعده سچا ہے۔ آپ کو وه لوگ ہلکا (بے صبرا) نہ کریں جویقین نہیں رکھتے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3470, 31, 1, 'الم'),
(3471, 31, 2, 'یہ حکمت والی کتاب کی آیتیں ہیں'),
(3472, 31, 3, 'جو نیکو کاروں کے لئے رہبر اور (سراسر) رحمت ہے'),
(3473, 31, 4, 'جو لوگ نماز قائم کرتے ہیں اور زکوٰة ادا کرتے ہیں اور آخرت پر (کامل) یقین رکھتے ہیں'),
(3474, 31, 5, 'یہی لوگ ہیں جو اپنے رب کی طرف سے ہدایت پر ہیں اور یہی لوگ نجات پانے والے ہیں'),
(3475, 31, 6, 'اور بعض لوگ ایسے بھی ہیں جو لغو باتوں کو مول لیتے ہیں کہ بےعلمی کے ساتھ لوگوں کو اللہ کی راه سے بہکائیں اور اسے ہنسی بنائیں، یہی وه لوگ ہیں جن کے لئے رسوا کرنے واﻻ عذاب ہے'),
(3476, 31, 7, 'جب اس کے سامنے ہماری آیتیں تلاوت کی جاتی ہیں تو تکبر کرتا ہوا اس طرح منھ پھیر لیتا ہے گویا اس نے سنا ہی نہیں گویا کہ اس کے دونوں کانوں میں ڈاٹ لگے ہوئے ہیں، آپ اسے درد ناک عذاب کی خبر سنا دیجئے'),
(3477, 31, 8, 'بیشک جن لوگوں نےایمان قبول کیا اور کام بھی نیک (مطابق سنت) کیے ان کے لئے نعمتوں والی جنتیں ہیں'),
(3478, 31, 9, 'جہاں وه ہمیشہ رہیں گے۔ اللہ کا سچا وعده ہے، وه بہت بڑی عزت وغلبہ واﻻ اور کامل حکمت واﻻ ہے'),
(3479, 31, 10, 'اسی نے آسمانوں کو بغیر ستون کے پیدا کیا ہے تم انہیں دیکھ رہے ہو اور اس نے زمین میں پہاڑوں کو ڈال دیا تاکہ وه تمہیں جنبش نہ دے سکے اور ہر طرح کے جاندار زمین میں پھیلا دیئے۔ اور ہم نے آسمان سے پانی برسا کر زمین میں ہر قسم کے نفیس جوڑے اگا دیئے'),
(3480, 31, 11, 'یہ ہے اللہ کی مخلوق اب تم مجھے اس کے سوا دوسرے کسی کی کوئی مخلوق تو دکھاؤ (کچھ نہیں)، بلکہ یہ ﻇالم کھلی گمراہی میں ہیں'),
(3481, 31, 12, 'اور ہم نے یقیناً لقمان کو حکمت دی تھی کہ تو اللہ تعالیٰ کا شکر کر ہر شکر کرنے واﻻ اپنے ہی نفع کے لئے شکر کرتا ہے جو بھی ناشکری کرے وه جان لے کہ اللہ تعالیٰ بے نیاز اور تعریفوں واﻻ ہے'),
(3482, 31, 13, 'اور جب کہ لقمان نے وعﻆ کہتے ہوئے اپنے لڑکے سے فرمایا کہ میرے پیارے بچے! اللہ کے ساتھ شریک نہ کرنا بیشک شرک بڑا بھاری ﻇلم ہے'),
(3483, 31, 14, 'ہم نے انسان کو اس کے ماں باپ کے متعلق نصیحت کی ہے، اس کی ماں نے دکھ پر دکھ اٹھا کر اسے حمل میں رکھا اور اس کی دودھ چھڑائی دو برس میں ہے کہ تو میری اور اپنے ماں باپ کی شکر گزاری کر، (تم سب کو) میری ہی طرف لوٹ کر آنا ہے'),
(3484, 31, 15, 'اور اگر وه دونوں تجھ پر اس بات کا دباؤ ڈالیں کہ تو میرے ساتھ شریک کرے جس کا تجھے علم نہ ہو تو تو ان کا کہنا نہ ماننا، ہاں دنیا میں ان کے ساتھ اچھی طرح بسر کرنا اور اس کی راه چلنا جو میری طرف جھکا ہوا ہو تمہارا سب کا لوٹنا میری ہی طرف ہے تم جو کچھ کرتے ہو اس سے پھر میں تمہیں خبردار کروں گا'),
(3485, 31, 16, 'پیارے بیٹے! اگر کوئی چیز رائی کے دانے کے برابر ہو پھر وه (بھی) خواه کسی چٹان میں ہو یا آسمانوں میں ہو یا زمین میں ہو اسے اللہ تعالیٰ ضرور ﻻئے گا اللہ تعالیٰ بڑا باریک بین اور خبردار ہے'),
(3486, 31, 17, 'اے میرے پیارے بیٹے! تو نماز قائم رکھنا، اچھے کاموں کی نصیحت کرتے رہنا، برے کاموں سے منع کیا کرنا اور جو مصیبت تم پر آجائے صبر کرنا (یقین مانو) کہ یہ بڑے تاکیدی کاموں میں سے ہے'),
(3487, 31, 18, 'لوگوں کے سامنے اپنے گال نہ پھلا اور زمین پر اترا کر نہ چل کسی تکبر کرنے والے شیخی خورے کو اللہ تعالیٰ پسند نہیں فرماتا'),
(3488, 31, 19, 'اپنی رفتار میں میانہ روی اختیار کر، اور اپنی آواز پست کر یقیناً آوازوں میں سب سے بدتر آواز گدھوں کی آواز ہے'),
(3489, 31, 20, 'کیا تم نہیں دیکھتے کہ اللہ تعالیٰ نے زمین وآسمان کی ہر چیز کو ہمارے کام میں لگا رکھا ہے اور تمہیں اپنی ﻇاہری وباطنی نعمتیں بھرپور دے رکھی ہیں، بعض لوگ اللہ کے بارے میں بغیر علم کے اور بغیر ہدایت کے اور بغیر روشن کتاب کے جھگڑا کرتے ہیں'),
(3490, 31, 21, 'اور جب ان سے کہا جاتا ہے کہ اللہ تعالیٰ کی اتاری ہوئی وحی کی تابعداری کرو تو کہتے ہیں کہ ہم نے تو جس طریق پر اپنے باپ دادوں کو پایا ہے اسی کی تابعداری کریں گے، اگرچہ شیطان ان کے بڑوں کو دوزخ کےعذاب کی طرف بلاتا ہو'),
(3491, 31, 22, 'اور جو (شخص) اپنے آپ کو اللہ کے تابع کردے اور ہو بھی وه نیکو کار یقیناً اس نے مضبوط کڑا تھام لیا، تمام کاموں کا انجام اللہ کی طرف ہے'),
(3492, 31, 23, 'کافروں کے کفر سے آپ رنجیده نہ ہوں، آخر ان سب کو لوٹنا تو ہماری جانب ہی ہے پھر ہم ان کو بتائیں گے جو انہوں نے کیا ہے، بے شک اللہ سینوں کے بھیدوں تک سے واقف ہے'),
(3493, 31, 24, 'ہم انہیں گو کچھ یونہی سا فائده دے دیں لیکن (بالﺂخر) ہم انہیں نہایت بیچارگی کی حالت میں سخت عذاب کی طرف ہنکا لے جائیں گے'),
(3494, 31, 25, 'اگر آپ ان سے دریافت کریں کہ آسمان وزمین کا خالق کون ہے؟ تو یہ ضرور جواب دیں گے کہ اللہ، تو کہہ دیجئے کہ سب تعریفوں کے ﻻئق اللہ ہی ہے، لیکن ان میں کے اکثر بے علم ہیں'),
(3495, 31, 26, 'آسمانوں میں اور زمین میں جو کچھ ہے وه سب اللہ ہی کا ہے یقیناً اللہ تعالیٰ بہت بڑا بے نیاز اور سزاوار حمد وﺛنا ہے'),
(3496, 31, 27, 'روئے زمین کے (تمام) درختوں کے اگر قلمیں ہو جائیں اور تمام سمندروں کی سیاہی ہو اور ان کے بعد سات سمندر اور ہوں تاہم اللہ کے کلمات ختم نہیں ہو سکتے، بیشک اللہ تعالیٰ غالب اور باحکمت ہے'),
(3497, 31, 28, 'تم سب کی پیدائش اور مرنے کے بعد جلانا ایسا ہی ہے جیسے ایک جی کا، بیشک اللہ تعالیٰ سننے واﻻ دیکھنے واﻻ ہے'),
(3498, 31, 29, 'کیا آپ نہیں دیکھتے کہ اللہ تعالیٰ رات کو دن میں اور دن کو رات میں کھپا دیتا ہے، سورج چاند کو اسی نے فرماں بردار کر رکھا ہے ہر ایک مقرره وقت تک چلتا رہے، اللہ تعالیٰ ہر اس چیز سے جو تم کرتے ہو خبردار ہے'),
(3499, 31, 30, 'یہ سب (انتظامات) اس وجہ سے ہیں کہ اللہ تعالیٰ حق ہے اور اس کے سوا جن جن کو لوگ پکارتے ہیں سب باطل ہیں اور یقیناً اللہ تعالیٰ بہت بلندیوں واﻻ اور بڑی شان واﻻ ہے'),
(3500, 31, 31, 'کیا تم اس پر غور نہیں کرتے کہ دریا میں کشتیاں اللہ کے فضل سے چل رہی ہیں اس لئے کہ وه تمہیں اپنی نشانیاں دکھاوے، یقیناً اس میں ہر ایک صبر وشکر کرنے والے کے لئے بہت سی نشانیاں ہیں'),
(3501, 31, 32, 'اور جب ان پر موجیں سائبانوں کی طرح چھا جاتی ہیں تو وه (نہایت) خلوص کے ساتھ اعتقاد کر کے اللہ تعالیٰ ہی کو پکارتے ہیں۔ پھر جب وه (باری تعالیٰ) انہیں نجات دے کر خشکی کی طرف پہنچاتا ہے تو کچھ ان میں سے اعتدال پر رہتے ہیں، اور ہماری آیتوں کا انکار صرف وہی کرتے ہیں جو بدعہد اور ناشکرے ہوں'),
(3502, 31, 33, 'لوگو! اپنے رب سے ڈرو اور اس دن کا خوف کرو جس دن باپ اپنے بیٹے کو کوئی نفع نہ پہنچا سکے گا اور نہ بیٹا اپنے باپ کا ذرا سا بھی نفع کرنے واﻻ ہوگا (یاد رکھو) اللہ کا وعده سچا ہے (دیکھو) تمہیں دنیا کی زندگی دھوکے میں نہ ڈالے اور نہ دھوکے باز (شیطان) تمہیں دھوکے میں ڈال دے'),
(3503, 31, 34, 'بے شک اللہ تعالیٰ ہی کے پاس قیامت کا علم ہے وہی بارش نازل فرماتا ہے اور ماں کے پیٹ میں جو ہے اسے جانتا ہے۔ کوئی (بھی) نہیں جانتا کہ کل کیا (کچھ) کرے گا؟ نہ کسی کو یہ معلوم ہے کہ کس زمین میں مرے گا۔ (یاد رکھو) اللہ تعالیٰ ہی پورے علم واﻻ اور صحیح خبروں واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3504, 32, 1, 'الم'),
(3505, 32, 2, 'بلاشبہ اس کتاب کا اتارنا تمام جہانوں کے پروردگار کی طرف سے ہے'),
(3506, 32, 3, 'کیا یہ کہتے ہیں کہ اس نے اسے گھڑ لیا ہے۔ (نہیں نہیں) بلکہ یہ تیرے رب تعالیٰ کی طرف سے حق ہے تاکہ آپ انہیں ڈرائیں جن کے پاس آپ سے پہلے کوئی ڈرانے واﻻ نہیں آیا۔ تاکہ وه راه راست پر آجائیں'),
(3507, 32, 4, 'اللہ تعالیٰ وه ہے جس نے آسمان وزمین کو اور جو کچھ ان کے درمیان ہے سب کو چھ دن میں پیدا کر دیا پھر عرش پر قائم ہوا، تمہارے لئے اس کے سوا کوئی مددگار اور سفارشی نہیں۔ کیا پھر بھی تم نصیحت حاصل نہیں کرتے'),
(3508, 32, 5, 'وه آسمان سے لے کر زمین تک (ہر) کام کی تدبیر کرتا ہے۔ پھر (وه کام) ایک ایسے دن میں اس کی طرف چڑھ جاتا ہے جس کا اندازه تمہاری گنتی کے ایک ہزار سال کے برابر ہے'),
(3509, 32, 6, 'یہی ہے چھپے کھلے کا جاننے واﻻ، زبردست غالب بہت ہی مہربان'),
(3510, 32, 7, 'جس نے نہایت خوب بنائی جو چیز بھی بنائی اور انسان کی بناوٹ مٹی سے شروع کی'),
(3511, 32, 8, 'پھر اس کی نسل ایک بے وقعت پانی کے نچوڑ سے چلائی'),
(3512, 32, 9, 'جسے ٹھیک ٹھاک کر کے اس میں اپنی روح پھونکی، اسی نے تمہارے کان آنکھیں اور دل بنائے (اس پر بھی) تم بہت ہی تھوڑا احسان مانتے ہو'),
(3513, 32, 10, 'انہوں نے کہا کیا جب ہم زمین میں مل جائیں گے کیا پھر نئی پیدائش میں آجائیں گے؟ بلکہ (بات یہ ہے) کہ وه لوگ اپنے پروردگار کی ملاقات کے منکر ہیں'),
(3514, 32, 11, 'کہہ دیجئے! کہ تمہیں موت کا فرشتہ فوت کرے گا جو تم پر مقرر کیا گیا ہے پھر تم سب اپنے پروردگار کی طرف لوٹائے جاؤ گے'),
(3515, 32, 12, 'کاش کہ آپ دیکھتے جب کہ گناه گار لوگ اپنے رب تعالیٰ کے سامنے سر جھکائے ہوئے ہوں گے، کہیں گے اے ہمارے رب! ہم نے دیکھ لیا اور سن لیا اب تو ہمیں واپس لوٹا دے ہم نیک اعمال کریں گے ہم یقین کرنے والے ہیں'),
(3516, 32, 13, 'اگر ہم چاہتے تو ہر شخص کو ہدایت نصیب فرما دیتے، لیکن میری یہ بات بالکل حق ہو چکی ہے کہ میں ضرور ضرور جہنم کو انسانوں اور جنوں سے پر کردوں گا'),
(3517, 32, 14, 'اب تم اپنے اس دن کی ملاقات کے فراموش کر دینے کا مزه چکھو، ہم نے بھی تمہیں بھلا دیا اور اپنے کیے ہوئے اعمال (کی شامت) سے ابدی عذاب کا مزه چکھو'),
(3518, 32, 15, 'ہماری آیتوں پر وہی ایمان ﻻتے ہیں جنہیں جب کبھی ان سے نصیحت کی جاتی ہے تو وه سجدے میں گر پڑتے ہیں اور اپنے رب کی حمد کے ساتھ اس کی تسبیح پڑھتے ہیں اور تکبر نہیں کرتے ہیں'),
(3519, 32, 16, 'ان کی کروٹیں اپنے بستروں سے الگ رہتی ہیں اپنے رب کو خوف اور امید کے ساتھ پکارتے ہیں اور جو کچھ ہم نے انہیں دے رکھا ہے وه خرچ کرتے ہیں'),
(3520, 32, 17, 'کوئی نفس نہیں جانتا جو کچھ ہم نے ان کی آنکھوں کی ٹھنڈک ان کے لئے پوشیده کر رکھی ہے، جو کچھ کرتے تھے یہ اس کا بدلہ ہے'),
(3521, 32, 18, 'کیا وه جو مومن ہو مثل اس کے ہے جو فاسق ہو؟ یہ برابر نہیں ہو سکتے'),
(3522, 32, 19, 'جن لوگوں نے ایمان قبول کیا اور نیک اعمال بھی کیے ان کے لئے ہمیشگی والی جنتیں ہیں، مہمانداری ہے ان کے اعمال کے بدلے جو وه کرتے تھے'),
(3523, 32, 20, 'لیکن جن لوگوں نے حکم عدولی کی ان کا ٹھکانا دوزخ ہے۔ جب کبھی اس سے باہر نکلنا چاہیں گے اسی میں لوٹا دیئے جائیں گے۔ اور کہہ دیا جائے گا کہ اپنے جھٹلانے کے بدلے آگ کا عذاب چکھو'),
(3524, 32, 21, 'بالیقین ہم انہیں قریب کے چھوٹے سے بعض عذاب اس بڑے عذاب کے سوا چکھائیں گے تاکہ وه لوٹ آئیں'),
(3525, 32, 22, 'اس سے بڑھ کر ﻇالم کون ہے جسے اللہ تعالیٰ کی آیتوں سے وعﻆ کیا گیا پھر بھی اس نے ان سے منھ پھیر لیا، (یقین مانو) کہ ہم بھی گناه گاروں سے انتقام لینے والے ہیں'),
(3526, 32, 23, 'بیشک ہم نے موسیٰ کو کتاب دی، پس آپ کو ہرگز اس کی ملاقات میں شک نہ کرنا چاہئے اور ہم نے اسے بنی اسرائیل کی ہدایت کا ذریعہ بنایا'),
(3527, 32, 24, 'اور جب ان لوگوں نے صبر کیا تو ہم نے ان میں سے ایسے پیشوا بنائے جو ہمارے حکم سے لوگوں کو ہدایت کرتے تھے، اور وه ہماری آیتوں پر یقین رکھتے تھے'),
(3528, 32, 25, 'آپ کا رب ان (سب) کے درمیان ان (تمام) باتوں کا فیصلہ قیامت کے دن کرے گا جن میں وه اختلاف کر رہے ہیں'),
(3529, 32, 26, 'کیا اس بات نے بھی انہیں ہدایت نہیں دی کہ ہم نے ان سے پہلے بہت سی امتوں کو ہلاک کر دیا جن کے مکانوں میں یہ چل پھر رہے ہیں۔ اس میں تو (بڑی) بڑی نشانیاں ہیں۔ کیا پھر بھی یہ نہیں سنتے؟'),
(3530, 32, 27, 'کیا یہ نہیں دیکھتے کہ ہم پانی کو بنجر (غیر آباد) زمین کی طرف بہا کر لے جاتے ہیں پھر اس سے ہم کھیتیاں نکالتے ہیں جسے ان کے چوپائے اور یہ خود کھاتے ہیں، کیا پھر بھی یہ نہیں دیکھتے؟'),
(3531, 32, 28, 'اور کہتے ہیں کہ یہ فیصلہ کب ہوگا؟ اگر تم سچے ہو (تو بتلاؤ)'),
(3532, 32, 29, 'جواب دے دو کہ فیصلے والے دن ایمان ﻻنا بے ایمانوں کو کچھ کام نہ آئے گا اور نہ انہیں ڈھیل دی جائے گی'),
(3533, 32, 30, 'اب آپ ان کا خیال چھوڑ دیں اور منتظر رہیں۔ یہ بھی منتظر ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3534, 33, 1, 'اے نبی! اللہ تعالیٰ سے ڈرتے رہنا اور کافروں اور منافقوں کی باتوں میں نہ آجانا، اللہ تعالیٰ بڑے علم واﻻ اور بڑی حکمت واﻻ ہے'),
(3535, 33, 2, 'جو کچھ آپ کی جانب آپ کے رب کی طرف سے وحی کی جاتی ہے اس کی تابعداری کریں (یقین مانو) کہ اللہ تمہارے ہر ایک عمل سے باخبر ہے'),
(3536, 33, 3, 'آپ اللہ ہی پر توکل رکھیں، وه کار سازی کے لئے کافی ہے'),
(3537, 33, 4, 'کسی آدمی کے سینے میں اللہ تعالیٰ نے دو دل نہیں رکھے، اور اپنی جن بیویوں کو تم ماں کہہ بیٹھتے ہو انہیں اللہ نے تمہاری (سچ مچ کی) مائیں نہیں بنایا، اور نہ تمہارے لے پالک لڑکوں کو (واقعی) تمہارے بیٹے بنایا ہے، یہ تو تمہارے اپنے منھ کی باتیں ہیں، اللہ تعالیٰ حق بات فرماتا ہے اور وه (سیدھی) راه سجھاتا ہے'),
(3538, 33, 5, 'لے پالکوں کو ان کے (حقیقی) باپوں کی طرف نسبت کر کے بلاؤ اللہ کے نزدیک پورا انصاف یہی ہے۔ پھر اگر تمہیں ان کے (حقیقی) باپوں کا علم ہی نہ ہو تو وه تمہارے دینی بھائی اور دوست ہیں، تم سے بھول چوک میں جو کچھ ہو جائے اس میں تم پر کوئی گناه نہیں، البتہ گناه وه ہے جس کا تم اراده دل سے کرو۔ اللہ تعالیٰ بڑا ہی بخشنے واﻻ مہربان ہے'),
(3539, 33, 6, 'پیغمبر مومنوں پر خود ان سے بھی زیاده حق رکھنے والے ہیں اور پیغمبر کی بیویاں مومنوں کی مائیں ہیں، اور رشتے دار کتاب اللہ کی رو سے بہ نسبت دوسرے مومنوں اور مہاجروں کے آپس میں زیاده حق دار ہیں (ہاں) مگر یہ کہ تم اپنے دوستوں کے ساتھ حسن سلوک کرنا چاہو۔ یہ حکم کتاب (الٰہی) میں لکھا ہوا ہے'),
(3540, 33, 7, 'جب کہ ہم نے تمام نبیوں سے عہد لیا اور (بالخصوص) آپ سے اور نوح سے اور ابراہیم سے اور موسیٰ سے اور مریم کے بیٹے عیسیٰ سے، اور ہم نے ان سے (پکا اور) پختہ عہد لیا'),
(3541, 33, 8, 'تاکہ اللہ تعالیٰ سچوں سے ان کی سچائی کے بارے میں دریافت فرمائے، اور کافروں کے لئے ہم نے المناک عذاب تیار کر رکھے ہیں'),
(3542, 33, 9, 'اے ایمان والو! اللہ تعالیٰ نے جو احسان تم پر کیا اسے یاد کرو جبکہ تمہارے مقابلے کو فوجوں پر فوجیں آئیں پھر ہم نے ان پر تیز وتند آندھی اور ایسے لشکر بھیجے جنہیں تم نے دیکھا ہی نہیں، اور جو کچھ تم کرتے ہو اللہ تعالیٰ سب کچھ دیکھتا ہے'),
(3543, 33, 10, 'جب کہ (دشمن) تمہارے پاس اوپر سے اور نیچے سے چڑھ آئے اور جب کہ آنکھیں پتھرا گئیں اور کلیجے منھ کو آگئے اور تم اللہ تعالیٰ کی نسبت طرح طرح کے گمان کرنے لگے'),
(3544, 33, 11, 'یہیں مومن آزمائے گئے اور پوری طرح وه جھنجھوڑ دیئے گئے'),
(3545, 33, 12, 'اور اس وقت منافق اور وه لوگ جن کے دلوں میں (شک کا) روگ تھا کہنے لگے اللہ تعالیٰ اور اس کے رسول نے ہم سے محض دھوکا فریب کا ہی وعده کیا تھا'),
(3546, 33, 13, 'ان ہی کی ایک جماعت نے ہانک لگائی کہ اے مدینہ والو! تمہارے لئے ٹھکانہ نہیں چلو لوٹ چلو، اور ان کی ایک اور جماعت یہ کہہ کر نبی ﴿صلی اللہ علیہ وسلم﴾ سے اجازت مانگنے لگی کہ ہمارے گھر غیر محفوظ ہیں، حاﻻنکہ وه (کھلے ہوئے اور) غیر محفوظ نہ تھے (لیکن) ان کا پختہ اراده بھاگ کھڑے ہونے کا تھا'),
(3547, 33, 14, 'اور اگر مدینے کے اطراف سے ان پر (لشکر) داخل کیے جاتے پھر ان سے فتنہ طلب کیا جاتا تو یہ ضرور اسے برپا کر دیتے اور نہ لڑتے مگر تھوڑی مدت'),
(3548, 33, 15, 'اس سے پہلے تو انہوں نے اللہ سے عہد کیا تھا کہ پیٹھ نہ پھیریں گے، اور اللہ تعالیٰ سے کیے ہوئے وعده کی باز پرس ضرور ہوگی'),
(3549, 33, 16, 'کہہ دیجئے کہ گو تم موت سے یا خوف قتل سے بھاگو تو یہ بھاگنا تمہیں کچھ بھی کام نہ آئے گا اور اس وقت تم بہت ہی کم فائده اٹھاؤ گے'),
(3550, 33, 17, 'پوچھیئے! تو کہ اگر اللہ تعالیٰ تمہیں کوئی برائی پہنچانا چاہے یا تم پر کوئی فضل کرنا چاہے تو کون ہے جو تمہیں بچا سکے (یا تم سے روک سکے؟)، اپنے لیے بجز اللہ تعالیٰ کے نہ کوئی حمایتی پائیں گے نہ مددگار'),
(3551, 33, 18, 'اللہ تعالیٰ تم میں سے انہیں (بخوبی) جانتا ہے جو دوسروں کو روکتے ہیں اور اپنے بھائی بندوں سے کہتے ہیں کہ ہمارے پاس چلے آؤ۔ اور کبھی کبھی ہی لڑائی میں آجاتے ہیں'),
(3552, 33, 19, 'تمہاری مدد میں (پورے) بخیل ہیں، پھر جب خوف ودہشت کا موقعہ آجائے تو آپ انہیں دیکھیں گے کہ آپ کی طرف نظریں جما دیتے ہیں اور ان کی انکھیں اس طرح گھومتی ہیں جیسے اس شخص کی جس پر موت کی غشی طاری ہو۔ پھر جب خوف جاتا رہتا ہے تو تم پر اپنی تیز زبانوں سے بڑی باتیں بناتے ہیں مال کے بڑے ہی حریص ہیں، یہ ایمان ﻻئے ہی نہیں ہیں اللہ تعالیٰ نے ان کے تمام اعمال نابود کر دیئے ہیں، اور اللہ تعالیٰ پر یہ بہت ہی آسان ہے'),
(3553, 33, 20, 'سمجھتے ہیں کہ اب تک لشکر چلے نہیں گئے، اور اگر فوجیں آجائیں تو تمنائیں کرتے ہیں کہ کاش! وه صحرا میں بادیہ نشینوں کے ساتھ ہوتے کہ تمہاری خبریں دریافت کیا کرتے، اگر وه تم میں موجود ہوتے (تو بھی کیا؟) نہ لڑتے مگر برائے نام'),
(3554, 33, 21, 'یقیناً تمہارے لئے رسول اللہ میں عمده نمونہ (موجود) ہے، ہر اس شخص کے لئے جو اللہ تعالیٰ کی اور قیامت کے دن کی توقع رکھتا ہے اور بکثرت اللہ تعالیٰ کی یاد کرتا ہے'),
(3555, 33, 22, 'اور ایمان والوں نے جب (کفار کے) لشکروں کو دیکھا (بے ساختہ) کہہ اٹھے! کہ انہیں کا وعده ہمیں اللہ تعالیٰ نے اور اس کے رسول نے دیا تھا اور اللہ تعالیٰ اور اس کے رسول نے سچ فرمایا، اور اس (چیز) نے ان کے ایمان میں اور شیوہٴ فرماں برداری میں اور اضافہ کر دیا'),
(3556, 33, 23, 'مومنوں میں (ایسے) لوگ بھی ہیں جنہوں نے جو عہد اللہ تعالیٰ سے کیا تھا انہیں سچا کر دکھایا، بعض نے تو اپنا عہد پورا کر دیا اور بعض (موقعہ کے) منتظر ہیں اور انہوں نے کوئی تبدیلی نہیں کی'),
(3557, 33, 24, 'تاکہ اللہ تعالیٰ سچوں کو ان کی سچائی کا بدلہ دے اور اگر چاہے تو منافقوں کو سزا دے یا ان کی توبہ قبول فرمائے، اللہ تعالیٰ بڑا ہی بخشنے واﻻ بہت ہی مہربان ہے'),
(3558, 33, 25, 'اور اللہ تعالیٰ نےکافروں کو غصے بھرے ہوئے ہی (نامراد) لوٹا دیا انہوں نے کوئی فائده نہیں پایا، اور اس جنگ میں اللہ تعالیٰ خود ہی مومنوں کو کافی ہوگیا اللہ تعالیٰ بڑی قوتوں واﻻ اور غالب ہے'),
(3559, 33, 26, 'اور جن اہل کتاب نے ان سے سازباز کر لی تھی انہیں (بھی) اللہ تعالیٰ نے ان کے قلعوں سے نکال دیا اور ان کے دلوں میں (بھی) رعب بھر دیا کہ تم ان کے ایک گروه کو قتل کر رہے ہو اور ایک گروه کو قیدی بنا رہے ہو'),
(3560, 33, 27, 'اور اس نے تمہیں ان کی زمینوں کا اور ان کے گھر بار کا اور ان کے مال کا وارث کر دیا اور اس زمین کا بھی جس کو تمہارے قدموں نے روندا نہیں، اللہ تعالیٰ ہر چیز پر قادر ہے'),
(3561, 33, 28, 'اے نبی! اپنی بیویوں سے کہہ دو کہ اگر تم زندگانی دنیا اور زینت دنیا چاہتی ہو تو آؤ میں تمہیں کچھ دے دﻻ دوں اور تمہیں اچھائی کے ساتھ رخصت کر دوں'),
(3562, 33, 29, 'اور اگر تمہاری مراد اللہ اور اس کا رسول اور آخرت کا گھر ہے تو (یقین مانو کہ) تم میں سے نیک کام کرنے والیوں کے لئے اللہ تعالیٰ نے بہت زبردست اجر رکھ چھوڑے ہیں'),
(3563, 33, 30, 'اے نبی کی بیویو! تم میں سے جو بھی کھلی بے حیائی (کا ارتکاب) کرے گی اسے دوہرا دوہرا عذاب دیا جائے گا، اور اللہ تعالیٰ کے نزدیک یہ بہت ہی سہل (سی بات) ہے'),
(3564, 33, 31, 'اور تم میں سے جو کوئی اللہ کی اور اس کے رسول کی فرمانبرداری کرے گی اور نیک کام کرے گی ہم اسے اجر (بھی) دوہرا دیں گے اور اس کے لئے ہم نے بہترین روزی تیار کر رکھی ہے'),
(3565, 33, 32, 'اے نبی کی بیویو! تم عام عورتوں کی طرح نہیں ہو، اگر تم پرہیزگاری اختیار کرو تو نرم لہجے سے بات نہ کرو کہ جس کے دل میں روگ ہو وه کوئی برا خیال کرے اور ہاں قاعدے کے مطابق کلام کرو'),
(3566, 33, 33, 'اور اپنے گھروں میں قرار سے رہو اور قدیم جاہلیت کے زمانے کی طرح اپنے بناؤ کا اﻇہار نہ کرو اور نماز ادا کرتی رہو اور زکوٰة دیتی رہو اور اللہ اور اس کے رسول کی اطاعت گزاری کرو۔ اللہ تعالیٰ یہی چاہتا ہے کہ اے نبی کی گھر والیو! تم سے وه (ہر قسم کی) گندگی کو دور کردے اور تمہیں خوب پاک کردے'),
(3567, 33, 34, 'اور تمہارے گھروں میں اللہ کی جو آیتیں اور رسول کی احادیﺚ پڑھی جاتی ہیں ان کا ذکر کرتی رہو، یقیناً اللہ تعالیٰ لطف کرنے واﻻ خبردار ہے'),
(3568, 33, 35, 'بےشک مسلمان مرد اور مسلمان عورتیں مومن مرد اور مومن عورتیں فرماں برداری کرنے والے مرد اور فرمانبردار عورتیں راست باز مرد اور راست باز عورتیں صبر کرنے والے مرد اور صبر کرنے والی عورتیں، عاجزی کرنے والے مرد اور عاجزی کرنے والی عورتیں، خیرات کرنے والے مرد اور خیرات کرنے والی عورتیں، روزے رکھنے والے مرد اور روزے رکھنے والی عورتیں اپنی شرمگاه کی حفاﻇت کرنے والے مرد اور حفاﻇت کرنے والیاں بکثرت اللہ کا ذکر کرنے والے اور ذکر کرنے والیاں ان (سب کے) لئے اللہ تعالیٰ نے (وسیع) مغفرت اور بڑا ﺛواب تیار کر رکھا ہے'),
(3569, 33, 36, 'اور (دیکھو) کسی مومن مرد و عورت کو اللہ اور اس کے رسول کا فیصلہ کے بعد اپنے کسی امر کا کوئی اختیار باقی نہیں رہتا، (یاد رکھو) اللہ تعالیٰ اور اس کے رسول کی جو بھی نافرمانی کرے گا وه صریح گمراہی میں پڑے گا'),
(3570, 33, 37, '(یاد کرو) جب کہ تو اس شخص سے کہہ رہا تھا جس پر اللہ نے بھی انعام کیا اور تو نے بھی کہ تو اپنی بیوی کو اپنے پاس رکھ اور اللہ سے ڈر اور تو اپنے دل میں وه بات چھپائے ہوئے تھا جسے اللہ ﻇاہر کرنے واﻻ تھا اور تو لوگوں سے خوف کھاتا تھا، حاﻻنکہ اللہ تعالیٰ اس کا زیاده حق دار تھا کہ تو اس سے ڈرے، پس جب کہ زید نے اس عورت سے اپنی غرض پوری کرلی ہم نے اسے تیرے نکاح میں دے دیا تاکہ مسلمانوں پر اپنے لے پالکوں کی بیویوں کے بارے میں کسی طرح کی تنگی نہ رہے جب کہ وه اپنی غرض ان سے پوری کرلیں، اللہ کا (یہ) حکم تو ہو کر ہی رہنے واﻻ تھا'),
(3571, 33, 38, 'جو چیزیں اللہ تعالیٰ نے اپنے نبی کے لئے مقرر کی ہیں ان میں نبی پر کوئی حرج نہیں، (یہی) اللہ کا دستور ان میں بھی رہا جو پہلے ہوئے اور اللہ تعالیٰ کے کام اندازے پر مقرر کئے ہوئے ہیں'),
(3572, 33, 39, 'یہ سب ایسے تھے کہ اللہ تعالیٰ کے احکام پہنچایا کرتے تھے اور اللہ ہی سے ڈرتے تھے اور اللہ کے سوا کسی سے نہیں ڈرتے تھے، اور اللہ تعالیٰ حساب لینے کے لئے کافی ہے'),
(3573, 33, 40, '(لوگو) تمہارے مردوں میں سے کسی کے باپ محمد ﴿صلی اللہ علیہ وسلم﴾ نہیں لیکن آپ اللہ تعالیٰ کے رسول ہیں اور تمام نبیوں کے ختم کرنے والے، اور اللہ تعالی ہر چیز کا (بخوبی) جاننے واﻻ ہے'),
(3574, 33, 41, 'مسلمانو! اللہ تعالیٰ کا ذکر بہت زیاده کرو'),
(3575, 33, 42, 'اور صبح وشام اس کی پاکیزگی بیان کرو'),
(3576, 33, 43, 'وہی ہے جو تم پر اپنی رحمتیں بھیجتا ہے اور اس کے فرشتے (تمہارے لئے دعائے رحمت کرتے ہیں) تاکہ وه تمہیں اندھیروں سے اجالے کی طرف لے جائے اور اللہ تعالیٰ مومنوں پر بہت ہی مہربان ہے'),
(3577, 33, 44, 'جس دن یہ (اللہ سے) ملاقات کریں گے ان کا تحفہ سلام ہوگا، ان کے لئے اللہ تعالیٰ نے باعزت اجر تیار کر رکھا ہے'),
(3578, 33, 45, 'اے نبی! یقیناً ہم نے ہی آپ کو (رسول بنا کر) گواہیاں دینے واﻻ، خوشخبریاں سنانے واﻻ، آگاه کرنے واﻻ بھیجا ہے'),
(3579, 33, 46, 'اور اللہ کے حکم سے اس کی طرف بلانے واﻻ اور روشن چراغ'),
(3580, 33, 47, 'آپ مومنوں کو خوشخبری سنا دیجئے! کہ ان کے لئے اللہ کی طرف سے بہت بڑا فضل ہے'),
(3581, 33, 48, 'اور کافروں اور منافقوں کا کہنا نہ مانیئے! اور جو ایذا (ان کی طرف سے پہنچے) اس کا خیال بھی نہ کیجیئے اللہ پر بھروسہ کئے رہیں، اور کافی ہے اللہ تعالیٰ کام بنانے واﻻ'),
(3582, 33, 49, 'اے مومنو! جب تم مومن عورتوں سے نکاح کرو پھر ہاتھ لگانے سے پہلے (ہی) طلاق دے دو تو ان پر تمہارا کوئی حق عدت کا نہیں جسے تم شمار کرو، پس تم کچھ نہ کچھ انہیں دے دو اور بھلے طریق پر انہیں رخصت کر دو'),
(3583, 33, 50, 'اے نبی! ہم نے تیرے لئے تیری وه بیویاں حلال کر دی ہیں جنہوں تو ان کا مہر دے چکا ہے اور وه لونڈیاں بھی جو اللہ تعالیٰ نے غنیمت میں تجھے دی ہیں اور تیرے چچا کی لڑکیاں اور پھوپھیوں کی بیٹیاں اور تیرے ماموں کی بیٹیاں اور تیری خاﻻؤں کی بیٹیاں بھی جنہوں نے تیرے ساتھ ہجرت کی ہے، اور وه باایمان عورت جو اپنا نفس نبی کو ہبہ کردے یہ اس صورت میں کہ خود نبی بھی اس سے نکاح کرنا چاہے، یہ خاص طور پر صرف تیرے لئے ہی ہے اور مومنوں کے لئے نہیں، ہم اسے بخوبی جانتے ہیں جو ہم نے ان پر ان کی بیویوں اور لونڈیوں کے بارے میں (احکام) مقرر کر رکھے ہیں، یہ اس لئے کہ تجھ پر حرج واقع نہ ہو، اللہ تعالیٰ بہت بخشنے اور بڑے رحم واﻻ ہے'),
(3584, 33, 51, 'ان میں سے جسے تو چاہے دور رکھ دے اور جسے چاہے اپنے پاس رکھ لے، اور اگر تو نے ان میں سے بھی کسی کو اپنے پاس بلالے جنہیں تو نے الگ کر رکھا تھا تو تجھ پر کوئی گناه نہیں، اس میں اس بات کی زیاده توقع ہے کہ ان عورتوں کی آنکھیں ٹھنڈی رہیں اور وه رنجیده نہ ہوں اور جو کچھ بھی تو انہیں دے دے اس پر سب کی سب راضی رہیں۔ تمہارے دلوں میں جو کچھ ہے اسے اللہ (خوب) جانتا ہے۔ اللہ تعالیٰ بڑا ہی علم اور حلم واﻻ ہے'),
(3585, 33, 52, 'اس کے بعد اور عورتیں آپ کے لئے حلال نہیں اور نہ یہ (درست ہے) کہ ان کے بدلے اور عورتوں سے (نکاح کرے) اگر چہ ان کی صورت اچھی بھی لگتی ہو مگر جو تیری مملوکہ ہوں۔ اور اللہ تعالیٰ ہر چیز کا (پورا) نگہبان ہے'),
(3586, 33, 53, 'اے ایمان والو! جب تک تمہیں اجازت نہ دی جائے تم نبی کے گھروں میں نہ جایا کرو کھانے کے لئے ایسے وقت میں کہ اس کے پکنے کا انتظار کرتے رہو بلکہ جب بلایا جائے جاؤ اور جب کھا چکو نکل کھڑے ہو، وہیں باتوں میں مشغول نہ ہوجایا کرو۔ نبی کو تمہاری اس بات سے تکلیف ہوتی ہے۔ تو وه لحاظ کر جاتے ہیں اور اللہ تعالیٰ (بیان) حق میں کسی کا لحاظ نہیں کرتا، جب تم نبی کی بیویوں سے کوئی چیز طلب کرو تو پردے کے پیچھے سے طلب کرو۔ تمہارے اور ان کے دلوں کے لئے کامل پاکیزگی یہی ہے، نہ تمہیں یہ جائز ہے کہ تم رسول اللہ کو تکلیف دو اور نہ تمہیں یہ حلال ہے کہ آپ کے بعد کسی وقت بھی آپ کی بیویوں سے نکاح کرو۔ (یاد رکھو) اللہ کے نزدیک یہ بہت بڑا (گناه) ہے'),
(3587, 33, 54, 'تم کسی چیز کو ﻇاہر کرو یا مخفی رکھو اللہ تو ہر ہر چیز کا بخوبی علم رکھنے واﻻ ہے'),
(3588, 33, 55, 'ان عورتوں پر کوئی گناه نہیں کہ وه اپنے باپوں اور اپنے بیٹوں اور بھائیوں اور بھتیجوں اور بھانجوں اور اپنی (میل جول کی) عورتوں اور ملکیت کے ماتحتوں (لونڈی، غلام) کے سامنے ہوں۔ (عورتو!) اللہ سے ڈرتی رہو۔ اللہ تعالیٰ یقیناً ہر چیز پر شاہد ہے'),
(3589, 33, 56, 'اللہ تعالیٰ اور اس کے فرشتے اس نبی پر رحمت بھیجتے ہیں۔ اے ایمان والو! تم (بھی) ان پر درود بھیجو اور خوب سلام (بھی) بھیجتے رہا کرو'),
(3590, 33, 57, 'جو لوگ اللہ اور اس کے رسول کو ایذا دیتے ہیں ان پر دنیا اور آخرت میں اللہ کی پھٹکار ہے اور ان کے لئے نہایت رسوا کن عذاب ہے'),
(3591, 33, 58, 'اور جو لوگ مومن مردوں اور مومن عورتوں کو ایذا دیں بغیر کسی جرم کے جو ان سے سرزد ہوا ہو، وه (بڑے ہی) بہتان اور صریح گناه کا بوجھ اٹھاتے ہیں'),
(3592, 33, 59, 'اے نبی! اپنی بیویوں سے اور اپنی صاحبزادیوں سے اور مسلمانوں کی عورتیں سے کہہ دو کہ وه اپنے اوپر اپنی چادریں لٹکا لیا کریں، اس سے بہت جلد ان کی شناخت ہو جایا کرے گی پھر نہ ستائی جائیں گی، اور اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(3593, 33, 60, 'اگر (اب بھی) یہ منافق اور وه جن کے دلوں میں بیماری ہے اور وه لوگ جو مدینہ میں غلط افواہیں اڑانے والے ہیں باز نہ آئے تو ہم آپ کو ان (کی تباہی) پر مسلط کردیں گے پھر تو وه چند دن ہی آپ کے ساتھ اس (شہر) میں ره سکیں گے'),
(3594, 33, 61, 'ان پر پھٹکار برسائی گئی، جہاں بھی مل جائیں پکڑے جائیں اور خوب ٹکڑے ٹکڑے کردیئے جائیں'),
(3595, 33, 62, 'ان سے اگلوں میں بھی اللہ کا یہی دستور جاری رہا۔ اور تو اللہ کے دستور میں ہرگز ردوبدل نہ پائے گا'),
(3596, 33, 63, 'لوگ آپ سے قیامت کے بارے میں سوال کرتے ہیں۔ آپ کہہ دیجیئے! کہ اس کا علم تو اللہ ہی کو ہے، آپ کو کیا خبر بہت ممکن ہے قیامت بالکل ہی قریب ہو'),
(3597, 33, 64, 'اللہ تعالیٰ نے کافروں پر لعنت کی ہے اور ان کے لئے بھڑکتی ہوئی آگ تیار کر رکھی ہے'),
(3598, 33, 65, 'جس میں وه ہمیشہ ہمیشہ رہیں گے۔ وه کوئی حامی ومددگار نہ پائیں گے'),
(3599, 33, 66, 'اس دن ان کے چہرے آگ میں الٹ پلٹ کئے جائیں گے۔ (حسرت وافسوس سے) کہیں گے کہ کاش ہم اللہ تعالیٰ اور رسول کی اطاعت کرتے'),
(3600, 33, 67, 'اور کہیں گے اے ہمارے رب! ہم نے اپنے سرداروں اور اپنے بڑوں کی مانی جنہوں نے ہمیں راه راست سے بھٹکا دیا'),
(3601, 33, 68, 'پروردگار تو انہیں دگنا عذاب دے اور ان پر بہت بڑی لعنت نازل فرما'),
(3602, 33, 69, 'اے ایمان والو! ان لوگوں جیسے نہ بن جاؤ جنہوں نے موسیٰ کو تکلیف دی پس جو بات انہوں نے کہی تھی اللہ نے انہیں اس سے بری فرما دیا، اور وه اللہ کے نزدیک باعزت تھے'),
(3603, 33, 70, 'اے ایمان والو! اللہ تعالیٰ سے ڈرو اور سیدھی سیدھی (سچی) باتیں کیا کرو'),
(3604, 33, 71, 'تاکہ اللہ تعالیٰ تمہارے کام سنوار دے اور تمہارے گناه معاف فرما دے، اور جو بھی اللہ اور اس کے رسول کی تابعداری کرے گا اس نے بڑی مراد پالی'),
(3605, 33, 72, 'ہم نے اپنی امانت کو آسمانوں پر زمین پر اور پہاڑوں پر پیش کیا لیکن سب نے اس کے اٹھانے سے انکار کردیا اور اس سے ڈر گئے (مگر) انسان نے اسے اٹھا لیا، وه بڑا ہی ﻇالم جاہل ہے'),
(3606, 33, 73, '(یہ اس لئے) کہ اللہ تعالیٰ منافق مردوں عورتوں اور مشرک مردوں عورتوں کو سزا دے اور مومن مردوں عورتوں کی توبہ قبول فرمائے، اور اللہ تعالیٰ بڑا ہی بخشنے واﻻ اور مہربان ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3607, 34, 1, 'تمام تعریفیں اس اللہ کے لئے سزاوار ہیں جس کی ملکیت میں وه سب کچھ ہے جو آسمانوں اور زمین میں ہے آخرت میں بھی تعریف اسی کے لئے ہے، وه (بڑی) حکمتوں واﻻ اور (پورا) خبردار ہے'),
(3608, 34, 2, 'جو زمین میں جائے اور جو اس سے نکلے جو آسمان سے اترے اور جو چڑھ کر اس میں جائے وه سب سے باخبر ہے۔ اور وه مہربان نہایت بخشش واﻻ ہے'),
(3609, 34, 3, 'کفار کہتے ہیں کہ ہم پر قیامت نہیں آئے گی۔ آپ کہہ دیجیئے! کہ مجھے میرے رب کی قسم! جو عالم الغیب ہے کہ وه یقیناً تم پر آئے گی اللہ تعالیٰ سے ایک ذرے کے برابر کی چیز بھی پوشیده نہیں نہ آسمانوں میں اور نہ زمین میں بلکہ اس سے بھی چھوٹی اور بڑی چیز کھلی کتاب میں موجود ہے'),
(3610, 34, 4, 'تاکہ وه ایمان والوں اور نیکوکاروں کو بھلائی عطا فرمائے، یہی لوگ ہیں جن کے لئے مغفرت اور عزت کی روزی ہے'),
(3611, 34, 5, 'اور ہماری آیتوں کو نیچا دکھانے کی جنہوں نے کوشش کی ہے یہ وه لوگ ہیں جن کے لئے بدترین قسم کا دردناک عذاب ہے'),
(3612, 34, 6, 'اور جنہیں علم ہے وه دیکھ لیں گے کہ جو کچھ آپ کی جانب آپ کے رب کی طرف سے نازل ہوا ہے وه (سراسر) حق ہے اور اللہ غالب خوبیوں والے کی راه کی رہبری کرتا ہے'),
(3613, 34, 7, 'اور کافروں نے کہا (آؤ) ہم تمہیں ایک ایسا شخص بتلائیں جو تمہیں یہ خبر پہنچا رہا ہے کہ جب تم بالکل ہی ریزه ریزه ہوجاؤ گے تو تم پھر سے ایک نئی پیدائش میں آؤ گے'),
(3614, 34, 8, '(ہم نہیں کہہ سکتے) کہ خود اس نے (ہی) اللہ پر جھوٹ باندھ لیا ہے یا اسے دیوانگی ہے بلکہ (حقیقت یہ ہے) کہ آخرت پر یقین نہ رکھنے والے ہی عذاب میں اور دور کی گمراہی میں ہیں'),
(3615, 34, 9, 'کیا پس وه اپنے آگے پیچھے آسمان وزمین کو دیکھ نہیں رہے ہیں؟ اگر ہم چاہیں تو انہیں زمین میں دھنسا دیں یا ان پر آسمان کے ٹکڑے گرادیں، یقیناً اس میں پوری دلیل ہے ہر اس بندے کے لئے جو (دل سے) متوجہ ہو'),
(3616, 34, 10, 'اور ہم نے داؤد پر اپنا فضل کیا، اے پہاڑو! اس کے ساتھ رغبت سے تسبیح پڑھا کرو اور پرندوں کو بھی (یہی حکم ہے) اور ہم نے اس کے لئے لوہا نرم کردیا'),
(3617, 34, 11, 'کہ تو پوری پوری زرہیں بنا اور جوڑوں میں اندازه رکھ تم سب نیک کام کیا کرو۔ (یقین مانو) کہ میں تمہارے اعمال دیکھ رہا ہوں'),
(3618, 34, 12, 'اور ہم نے سلیمان کے لئے ہوا کو مسخر کردیا کہ صبح کی منزل اس کی مہینہ بھر کی ہوتی تھی اور شام کی منزل بھی اور ہم نے ان کے لئے تانبے کا چشمہ بہا دیا۔ اور اس کے رب کے حکم سے بعض جنات اس کی ماتحتی میں اس کے سامنے کام کرتے تھے اور ان میں سے جو بھی ہمارے حکم سے سرتابی کرے ہم اسے بھڑکتی ہوئی آگ کے عذاب کا مزه چکھائیں گے'),
(3619, 34, 13, 'جو کچھ سلیمان چاہتے وه جنات تیار کردیتے مثلا قلعے اور مجسمے اور حوضوں کے برابر لگن اور چولہوں پر جمی ہوئی مضبوط دیگیں، اے آل داؤد اس کے شکریہ میں نیک عمل کرو، میرے بندوں میں سے شکرگزار بندے کم ہی ہوتے ہیں'),
(3620, 34, 14, 'پھر جب ہم نے ان پر موت کا حکم بھیج دیا تو ان کی خبر جنات کو کسی نے نہ دی سوائے گھن کے کیڑے کے جو ان کی عصا کو کھا رہا تھا۔ پس جب (سلیمان) گر پڑے اس وقت جنوں نے جان لیا کہ اگر وه غیب دان ہوتے تو اس کے ذلت کے عذاب میں مبتلا نہ رہتے'),
(3621, 34, 15, 'قوم سبا کے لئے اپنی بستیوں میں (قدرت الٰہی کی) نشانی تھی ان کے دائیں بائیں دو باغ تھے (ہم نے ان کو حکم دیا تھا کہ) اپنے رب کی دی ہوئی روزی کھاؤ اور اس کا شکر ادا کرو، یہ عمده شہر اور وه بخشنے واﻻ رب ہے'),
(3622, 34, 16, 'لیکن انہوں نے روگردانی کی تو ہم نے ان پر زور کے سیلاب (کا پانی) بھیج دیا اور ہم نے ان کے (ہرے بھرے) باغوں کے بدلے دو (ایسے) باغ دیئے جو بدمزه میوؤں والے اور (بکثرت) جھاؤ اور کچھ بیری کے درختوں والے تھے'),
(3623, 34, 17, 'ہم نے ان کی ناشکری کا یہ بدلہ انہیں دیا۔ ہم (ایسی) سخت سزا بڑے بڑے ناشکروں ہی کو دیتے ہیں'),
(3624, 34, 18, 'اور ہم نے ان کے اور ان بستیوں کے درمیان جن میں ہم نے برکت دے رکھی تھی چند بستیاں اور (آباد) رکھی تھیں جو برسرراه ﻇاہر تھیں، اور ان میں چلنے کی منزلیں مقرر کردی تھیں ان میں راتوں اور دنوں کو بہ امن وامان چلتے پھرتے رہو'),
(3625, 34, 19, 'لیکن انہوں نے پھر کہا کہ اے ہمارے پروردگار! ہمارے سفر دور دراز کردے چونکہ خود انہوں نے اپنے ہاتھوں اپنا برا کیا اس لئے ہم نے انہیں (گزشتہ) فسانوں کی صورت میں کردیا اور ان کے ٹکڑے ٹکڑے اڑا دیئے۔ بلاشبہ ہر ایک صبروشکر کرنے والے کے لئے اس (ماجرے) میں بہت سی عبرتیں ہیں'),
(3626, 34, 20, 'اور شیطان نے ان کے بارے میں اپنا گمان سچا کر دکھایا یہ لوگ سب کے سب اس کے تابعدار بن گئے سوائے مومنوں کی ایک جماعت کے'),
(3627, 34, 21, 'شیطان کا ان پر کوئی زور (اور دباؤ) نہ تھا مگر اس لئے کہ ہم ان لوگوں کو جو آخرت پر ایمان رکھتے ہیں ﻇاہر کردیں ان لوگوں میں سے جو اس سے شک میں ہیں۔ اور آپ کا رب (ہر) ہر چیز پر نگہبان ہے'),
(3628, 34, 22, 'کہہ دیجیئے! کہ اللہ کے سوا جن جن کا تمہیں گمان ہے (سب) کو پکار لو، نہ ان میں سے کسی کو آسمانوں اور زمینوں میں سے ایک ذره کا اختیار ہے نہ ان کا ان میں کوئی حصہ ہے نہ ان میں سے کوئی اللہ کا مددگار ہے'),
(3629, 34, 23, 'شفاعت (سفارش) بھی اس کے پاس کچھ نفع نہیں دیتی بجز ان کے جن کے لئے اجازت ہوجائے۔ یہاں تک کہ جب ان کے دلوں سے گھبراہٹ دور کردی جاتی ہے تو پوچھتے ہیں تمہارے پرودگار نے کیا فرمایا؟ جواب دیتے ہیں کہ حق فرمایا اور وه بلند وباﻻ اور بہت بڑا ہے'),
(3630, 34, 24, 'پوچھیئے کہ تمہیں آسمانوں اور زمین سے روزی کون پہنچاتا ہے؟ (خود) جواب دیجیئے! کہ اللہ تعالیٰ۔ (سنو) ہم یا تم۔ یا تو یقیناً ہدایت پر ہیں یا کھلی گمراہی میں ہیں؟'),
(3631, 34, 25, 'کہہ دیجیئے! کہ ہمارے کئے ہوئے گناہوں کی بابت تم سے کوئی سوال نہ کیا جائے گا نہ تمہارے اعمال کی بازپرس ہم سے کی جائے گی'),
(3632, 34, 26, 'انہیں خبر دے دیجیئے کہ ہم سب کو ہمارا رب جمع کرکے پھر ہم میں سچے فیصلے کردے گا۔ وه فیصلے چکانے واﻻ ہے اور دانا'),
(3633, 34, 27, 'کہہ دیجیئے! کہ اچھا مجھے بھی تو انہیں دکھا دو جنہیں تم اللہ کا شریک ٹھہرا کر اس کے ساتھ ملا رہے ہو، ایسا ہرگز نہیں، بلکہ وہی اللہ ہے غالب باحکمت'),
(3634, 34, 28, 'ہم نے آپ کو تمام لوگوں کے لئے خوشخبریاں سنانے واﻻ اور ڈرانے واﻻ بنا کر بھیجا ہے ہاں مگر (یہ صحیح ہے) کہ لوگوں کی اکثریت بےعلم ہے'),
(3635, 34, 29, 'پوچھتے ہیں کہ وه وعده ہے کب؟ سچے ہو تو بتا دو'),
(3636, 34, 30, 'جواب دیجیئے کہ وعدے کا دن ٹھیک معین ہے جس سے ایک ساعت نہ تم پیچھے ہٹ سکتے ہو نہ آگے بڑھ سکتے ہو'),
(3637, 34, 31, 'اور کافروں نے کہا کہ ہم ہرگز نہ تو اس قرآن کو مانیں نہ اس سے پہلے کی کتابوں کو! اے دیکھنے والے کاش کہ تو ان ﻇالموں کو اس وقت دیکھتا جب کہ یہ اپنے رب کے سامنے کھڑے ہوئے ایک دوسرے کو الزام دے رہے ہوں گے کمزور لوگ بڑے لوگوں سے کہیں گے اگر تم نہ ہوتے تو ہم تو مومن ہوتے'),
(3638, 34, 32, 'یہ بڑے لوگ ان کمزوروں کو جواب دیں گے کہ کیا تمہارے پاس ہدایت آچکنے کے بعد ہم نے تمہیں اس سے روکا تھا؟ (نہیں) بلکہ تم (خود) ہی مجرم تھے'),
(3639, 34, 33, '(اس کے جواب میں) یہ کمزور لوگ ان متکبروں سے کہیں گے، (نہیں نہیں) بلکہ دن رات مکروفریب سے ہمیں اللہ کے ساتھ کفر کرنے اور اس کے شریک مقرر کرنے کا تمہارا حکم دینا ہماری بےایمانی کا باعﺚ ہوا، اور عذاب کو دیکھتے ہی سب کے سب دل میں پشیمان ہو رہے ہوں گے، اور کافروں کی گردنوں میں ہم طوق ڈال دیں گے انہیں صرف ان کے کئے کرائے اعمال کا بدلہ دیا جائے گا'),
(3640, 34, 34, 'اور ہم تو جس بستی میں جو بھی آگاه کرنے واﻻ بھیجا وہاں کے خوشحال لوگوں نے یہی کہا کہ جس چیز کے ساتھ تم بھیجے گئے ہو ہم اس کے ساتھ کفر کرنے والے ہیں'),
(3641, 34, 35, 'اور کہا ہم مال واوﻻد میں بہت بڑھے ہوئے ہیں یہ نہیں ہوسکتا کہ ہم عذاب دیئے جائیں'),
(3642, 34, 36, 'کہہ دیجیئے! کہ میرا رب جس کے لئے چاہے روزی کشاده کردیتا ہے اور تنگ بھی کردیتا ہے، لیکن اکثر لوگ نہیں جانتے'),
(3643, 34, 37, 'اور تمہارے مال اور اوﻻد ایسے نہیں کہ تمہیں ہمارے پاس (مرتبوں سے) قریب کردیں ہاں جو ایمان ﻻئیں اور نیک عمل کریں ان کے لئے ان کے اعمال کا دوہرا اجر ہے اور وه نڈر و بےخوف ہو کر باﻻ خانوں میں رہیں گے'),
(3644, 34, 38, 'اور جو لوگ ہماری آیتوں کے مقابلے کی تگ ودو میں لگے رہتے ہیں یہی ہیں جو عذاب میں پکڑ کر حاضر رکھے جائیں گے'),
(3645, 34, 39, 'کہہ دیجیئے! کہ میرا رب اپنے بندوں میں جس کے لئے چاہے روزی کشاده کرتا ہے اور جس کے لئے چاہے تنگ کردیتا ہے، تم جو کچھ بھی اللہ کی راه میں خرچ کرو گے اللہ اس کا (پورا پورا) بدلہ دے گا اور وه سب سے بہتر روزی دینے واﻻ ہے'),
(3646, 34, 40, 'اور ان سب کو اللہ اس دن جمع کرکے فرشتوں سے دریافت فرمائے گا کہ کیا یہ لوگ تمہاری عبادت کرتے تھے'),
(3647, 34, 41, 'وه کہیں گے تیری ذات پاک ہے اور ہمارا ولی تو تو ہے نہ کہ یہ بلکہ یہ لوگ جنوں کی عبادت کرتے تھے، ان میں کے اکثر کا ان ہی پر ایمان تھا'),
(3648, 34, 42, 'پس آج تم میں سے کوئی (بھی) کسی کے لئے (بھی کسی قسم کے) نفع نقصان کا مالک نہ ہوگا۔ اور ہم ﻇالموں سے کہہ دیں گے کہ اس آگ کا عذاب چکھو جسے تم جھٹلاتے رہے'),
(3649, 34, 43, 'اور جب ان کے سامنے ہماری صاف صاف آیتیں پڑھی جاتی ہیں تو کہتے ہیں کہ یہ ایسا شخص ہے جو تمہیں تمہارے باپ دادا کے معبودوں سے روک دینا چاہتا ہے۔ (اس کے سوا کوئی بات نہیں)، اور کہتے ہیں کہ یہ تو گھڑا ہوا جھوٹ ہے اور حق ان کے پاس آچکا پھر بھی کافر یہی کہتے رہے کہ یہ تو کھلا ہوا جادو ہے'),
(3650, 34, 44, 'اور ان (مکہ والوں) کو نہ تو ہم نے کتابیں دے رکھی ہیں جنہیں یہ پڑھتے ہوں نہ ان کے پاس آپ سے پہلے کوئی آگاه کرنے واﻻ آیا'),
(3651, 34, 45, 'اور ان سے پہلے کے لوگوں نے بھی ہماری باتوں کو جھٹلایا تھا اور انہیں ہم نے جو دے رکھا تھا یہ تو اس کے دسویں حصے کو بھی نہیں پہنچے، پس انہوں نے میرے رسولوں کو جھٹلایا، (پھر دیکھ کہ) میرا عذاب کیسا (سخت) تھا'),
(3652, 34, 46, 'کہہ دیجیئے! کہ میں تمہیں صرف ایک ہی بات کی نصیحت کرتا ہوں کہ تم اللہ کے واسطے (ضد چھوڑ کر) دو دو مل کر یا تنہا تنہا کھڑے ہو کر سوچو تو سہی، تمہارے اس رفیق کو کوئی جنون نہیں، وه تو تمہیں ایک بڑے (سخت) عذاب کے آنے سے پہلے ڈرانے واﻻ ہے'),
(3653, 34, 47, 'کہہ دیجیئے! کہ جو بدلہ میں تم سے مانگوں وه تمہارے لئے ہے میرا بدلہ تو اللہ تعالیٰ ہی کے ذمے ہے۔ وه ہر چیز سے باخبر (اور مطلع) ہے'),
(3654, 34, 48, 'کہہ دیجیئے! کہ میرا رب حق (سچی وحی) نازل فرماتا ہے وه ہر غیب کا جاننے واﻻ ہے'),
(3655, 34, 49, 'کہہ دیجیئے کہ حق آچکا باطل نہ تو پہلے کچھ کرسکا ہے اور نہ کرسکے گا'),
(3656, 34, 50, 'کہہ دیجیئے کہ اگر میں بہک جاؤں تو میرے بہکنے (کا وبال) مجھ پر ہی ہے اور اگر میں راه ہدایت پر ہوں تو بہ سبب اس وحی کے جو میرا پروردگار مجھے کرتا ہے وه بڑا ہی سننے واﻻ اور بہت ہی قریب ہے'),
(3657, 34, 51, 'اور اگر آپ (وه وقت) ملاحظہ کریں جب کہ یہ کفار گھبرائے پھریں گے پھر نکل بھاگنے کی کوئی صورت نہ ہوگی اور قریب کی جگہ سے گرفتار کر لئے جائیں گے'),
(3658, 34, 52, 'اس وقت کہیں گے کہ ہم اس قرآن پر ایمان ﻻئے لیکن اس قدر دور جگہ سے (مطلوبہ چیز) کیسے ہاتھ آسکتی ہے'),
(3659, 34, 53, 'اس سے پہلے تو انہوں نے اس سے کفر کیا تھا، اور دور دراز سے بن دیکھے ہی پھینکتے رہے'),
(3660, 34, 54, 'ان کی چاہتوں اور ان کے درمیان پرده حائل کردیا گیا جیسے کہ اس سے پہلے بھی ان جیسوں کے ساتھ کیا گیا، وه بھی (ان ہی کی طرح) شک وتردد میں (پڑے ہوئے) تھے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3661, 35, 1, 'اس اللہ کے لئے تمام تعریفیں سزاوار ہیں جو (ابتداءً) آسمانوں اور زمین کا پیدا کرنے واﻻ اور دو دو تین تین چار چار پروں والے فرشتوں کو اپنا پیغمبر (قاصد) بنانے واﻻ ہے، مخلوق میں جو چاہے زیادتی کرتا ہے اللہ تعالیٰ یقیناً ہر چیز پر قادر ہے'),
(3662, 35, 2, 'اللہ تعالیٰ جو رحمت لوگوں کے لئے کھول دے سو اس کا کوئی بند کرنے واﻻ نہیں اور جس کو بند کردے سو اس کے بعد اس کا کوئی جاری کرنے واﻻ نہیں اور وہی غالب حکمت واﻻ ہے'),
(3663, 35, 3, 'لوگو! تم پر جو انعام اللہ تعالیٰ نے کئے ہیں انہیں یاد کرو۔ کیا اللہ کے سوا اور کوئی بھی خالق ہے جو تمہیں آسمان وزمین سے روزی پہنچائے؟ اس کے سوا کوئی معبود نہیں۔ پس تم کہاں الٹے جاتے ہو'),
(3664, 35, 4, 'اور اگر یہ آپ کو جھٹلائیں تو آپ سے پہلے کے تمام رسول بھی جھٹلائے جاچکے ہیں۔ تمام کام اللہ ہی کی طرف لوٹائے جاتے ہیں'),
(3665, 35, 5, 'لوگو! اللہ تعالیٰ کا وعده سچا ہے تمہیں زندگانیٴ دنیا دھوکے میں نہ ڈالے، اور نہ دھوکے باز شیطان تمہیں غفلت میں ڈالے'),
(3666, 35, 6, 'یاد رکھو! شیطان تمہارا دشمن ہے، تم اسے دشمن جانو وه تو اپنے گروه کو صرف اس لئے ہی بلاتا ہے کہ وه سب جہنم واصل ہو جائیں'),
(3667, 35, 7, 'جو لوگ کافر ہوئے ان کے لئے سخت سزا ہے اور جو لوگ ایمان ﻻئے اور نیک اعمال کئے ان کے لئے بخشش ہے اور (بہت) بڑا اجر ہے'),
(3668, 35, 8, 'کیا پس وه شخص جس کے لئے اس کے برے اعمال مزین کردیئے گئے ہیں پس وه انہیں اچھاسمجھتا ہے (کیا وه ہدایت یافتہ شخص جیسا ہے)، (یقین مانو) کہ اللہ جسے چاہے گمراه کرتا ہے اور جسے چاہے راه راست دکھاتا ہے۔ پس آپ کو ان پر غم کھا کھا کر اپنی جان ہلاکت میں نہ ڈالنی چاہیئے، یہ جو کچھ کر رہے ہیں اس سے یقیناً اللہ تعالیٰ بخوبی واقف ہے'),
(3669, 35, 9, 'اور اللہ ہی ہوائیں چلاتا ہے جو بادلوں کو اٹھاتی ہیں پھر ہم بادلوں کو خشک زمین کی طرف لے جاتے ہیں اور اس سے اس زمین کو اس کی موت کے بعد زنده کردیتے ہیں۔ اسی طرح دوباره جی اٹھنا (بھی) ہے'),
(3670, 35, 10, 'جو شخص عزت حاصل کرنا چاہتا ہو تو اللہ تعالیٰ ہی کی ساری عزت ہے، تمام تر ستھرے کلمات اسی کی طرف چڑھتے ہیں اور نیک عمل ان کو بلند کرتا ہے، جو لوگ برائیوں کے داؤں گھات میں لگے رہتے ہیں ان کے لئے سخت تر عذاب ہے، اور ان کا یہ مکر برباد ہوجائے گا'),
(3671, 35, 11, 'لوگو! اللہ تعالیٰ نے تمہیں مٹی سے پھر نطفہ سے پیدا کیا ہے، پھر تمہیں جوڑے جوڑے (مرد وعورت) بنا دیا ہے، عورتوں کو حاملہ ہونا اور بچوں کا تولد ہونا سب اس کے علم سے ہی ہے، اور جو بڑی عمر واﻻ عمر دیا جائے اور جس کی عمر گھٹے وه سب کتاب میں لکھا ہوا ہے۔ اللہ تعالیٰ پر یہ بات بالکل آسان ہے'),
(3672, 35, 12, 'اور برابر نہیں دو دریا یہ میٹھا ہے پیاس بجھاتا پینے میں خوشگوار اور یہ دوسرا کھاری ہے کڑوا، تم ان دونوں میں سے تازه گوشت کھاتے ہو اور وه زیوارت نکالتے ہو جنہیں تم پہنتے ہو۔ اور آپ دیکھتے ہیں کہ بڑی بڑی کشتیاں پانی کو چیرنے پھاڑنے والی ان دریاؤں میں ہیں تاکہ تم اس کا فضل ڈھونڈو اور تاکہ تم اس کا شکر کرو'),
(3673, 35, 13, 'وه رات کو دن اور دن کو رات میں داخل کرتا ہے اور آفتاب وماہتاب کو اسی نے کام میں لگا دیا ہے۔ ہر ایک میعاد معین پر چل رہا ہے۔ یہی ہے اللہ تم سب کا پالنے واﻻ اسی کی سلطنت ہے۔ جنہیں تم اس کے سوا پکار رہے ہو وه تو کھجور کی گٹھلی کے چھلکے کے بھی مالک نہیں'),
(3674, 35, 14, 'اگر تم انہیں پکارو تو وه تمہاری پکار سنتے ہی نہیں اور اگر (بالفرض) سن بھی لیں تو فریاد رسی نہیں کریں گے، بلکہ قیامت کے دن تمہارے اس شرک کا صاف انکار کرجائیں گے۔ آپ کو کوئی حق تعالیٰ جیسا خبردار خبریں نہ دے گا'),
(3675, 35, 15, 'اے لوگو! تم اللہ کے محتاج ہو اور اللہ بےنیاز خوبیوں واﻻ ہے'),
(3676, 35, 16, 'اگر وه چاہے تو تم کو فنا کردے اور ایک نئی مخلوق پیدا کردے'),
(3677, 35, 17, 'اور یہ بات اللہ کو کچھ مشکل نہیں'),
(3678, 35, 18, 'کوئی بھی بوجھ اٹھانے واﻻ دوسرے کا بوجھ نہیں اٹھائے گا، اگر کوئی گراں بار دوسرے کو اپنا بوجھ اٹھانے کے لئے بلائے گا تو وه اس میں سے کچھ بھی نہ اٹھائے گا گو قرابت دار ہی ہو۔ تو صرف ان ہی کو آگاه کرسکتا ہے جو غائبانہ طور پر اپنے رب سے ڈرتے ہیں اور نمازوں کی پابندی کرتے ہیں اور جو بھی پاک ہوجائے وه اپنے ہی نفع کے لئے پاک ہوگا۔ لوٹنا اللہ ہی کی طرف ہے'),
(3679, 35, 19, 'اور اندھا اور آنکھوں واﻻ برابر نہیں'),
(3680, 35, 20, 'اور نہ تاریکی اور روشنی'),
(3681, 35, 21, 'اور نہ چھاؤں اور نہ دھوپ'),
(3682, 35, 22, 'اور زنده اور مردے برابر نہیں ہوسکتے، اللہ تعالیٰ جس کو چاہتا ہے سنا دیتا ہے، اور آپ ان لوگوں کو نہیں سنا سکتے جو قبروں میں ہیں'),
(3683, 35, 23, 'آپ تو صرف ڈرانے والے ہیں'),
(3684, 35, 24, 'ہم نے ہی آپ کو حق دے کر خوشخبری سنانے واﻻ اور ڈر سنانے واﻻ بنا کر بھیجا ہے اور کوئی امت ایسی نہیں ہوئی جس میں کوئی ڈر سنانے واﻻ نہ گزرا ہو'),
(3685, 35, 25, 'اور اگر یہ لوگ آپ کو جھٹلا دیں تو جو لوگ ان سے پہلے ہو گزرے ہیں انہوں نے بھی جھٹلایا تھا ان کے پاس بھی ان کے پیغمبر معجزے اور صحیفے اور روشن کتابیں لے کر آئے تھے'),
(3686, 35, 26, 'پھر میں نےان کافروں کو پکڑ لیا سو میرا عذاب کیسا ہوا'),
(3687, 35, 27, 'کیا آپ نے اس بات پر نظر نہیں کی کہ اللہ تعالیٰ نے آسمان سے پانی اتارا پھر ہم نے اس کے ذریعہ سے مختلف رنگتوں کے پھل نکالے اور پہاڑوں کے مختلف حصے ہیں سفید اور سرخ کہ ان کی بھی رنگتیں مختلف ہیں اور بہت گہرے سیاه'),
(3688, 35, 28, 'اور اسی طرح آدمیوں اور جانوروں اور چوپایوں میں بھی بعض ایسے ہیں کہ ان کی رنگتیں مختلف ہیں، اللہ سے اس کے وہی بندے ڈرتے ہیں جو علم رکھتے ہیں واقعی اللہ تعالیٰ زبردست بڑا بخشنے واﻻ ہے'),
(3689, 35, 29, 'جو لوگ کتاب اللہ کی تلاوت کرتے ہیں اور نماز کی پابندی رکھتے ہیں اور جو کچھ ہم نے ان کو عطا فرمایا ہے اس میں سے پوشیده اور علانیہ خرچ کرتے ہیں وه ایسی تجارت کے امیدوار ہیں جو کبھی خساره میں نہ ہوگی'),
(3690, 35, 30, 'تاکہ ان کو ان کی اجرتیں پوری دے اور ان کو اپنے فضل سے اور زیاده دے بےشک وه بڑا بخشنے واﻻ قدردان ہے'),
(3691, 35, 31, 'اور یہ کتاب جو ہم نے آپ کے پاس وحی کے طور پر بھیجی ہے یہ بالکل ٹھیک ہے جو کہ اپنے سے پہلی کتابوں کی بھی تصدیق کرتی ہے۔ اللہ تعالیٰ اپنے بندوں کی پوری خبر رکھنے واﻻ خوب دیکھنے واﻻ ہے'),
(3692, 35, 32, 'پھر ہم نے ان لوگوں کو (اس) کتاب کا وارث بنایا جن کو ہم نے اپنے بندوں میں سے پسند فرمایا۔ پھر بعضے تو ان میں اپنی جانوں پر ﻇلم کرنے والے ہیں اور بعضے ان میں متوسط درجے کے ہیں اور بعضے ان میں اللہ کی توفیق سے نیکیوں میں ترقی کئے چلے جاتے ہیں۔ یہ بڑا فضل ہے'),
(3693, 35, 33, 'وه باغات میں ہمیشہ رہنے کے جن میں یہ لوگ داخل ہوں گے سونے کے کنگن اور موتی پہنائے جاویں گے۔ اور پوشاک ان کی وہاں ریشم کی ہوگی'),
(3694, 35, 34, 'اور کہیں گے کہ اللہ کا لاکھ لاکھ شکر ہے جس نے ہم سے غم دور کیا۔ بےشک ہمارا پروردگار بڑا بخشنے واﻻ بڑا قدردان ہے'),
(3695, 35, 35, 'جس نے ہم کو اپنے فضل سے ہمیشہ رہنے کے مقام میں ﻻ اتارا جہاں نہ ہم کو کوئی تکلیف پہنچے گی اور نہ ہم کو کوئی خستگی پہنچے گی'),
(3696, 35, 36, 'اور جو لوگ کافر ہیں ان کے لئے دوزخ کی آگ ہے نہ تو ان کی قضا ہی آئے گی کہ مر ہی جائیں اور نہ دوزخ کا عذاب ہی ان سے ہلکا کیا جائے گا۔ ہم ہر کافر کو ایسی ہی سزا دیتے ہیں'),
(3697, 35, 37, 'اور وه لوگ اس میں چلائیں گے کہ اے ہمارے پروردگار! ہم کو نکال لے ہم اچھے کام کریں گے برخلاف ان کاموں کے جو کیا کرتے تھے، (اللہ کہے گا) کیا ہم نے تم کو اتنی عمر نہ دی تھی کہ جس کو سمجھنا ہوتا وه سمجھ سکتا اور تمہارے پاس ڈرانے واﻻ بھی پہنچا تھا، سو مزه چکھو کہ (ایسے) ﻇالموں کا کوئی مددگار نہیں'),
(3698, 35, 38, 'بےشک اللہ تعالیٰ جاننے واﻻ ہے آسمانوں اور زمین کی پوشیده چیزوں کا، بےشک وہی جاننے واﻻ ہے سینوں کی باتوں کا'),
(3699, 35, 39, 'وہی ایسا ہے جس نے تم کو زمین میں آباد کیا، سو جو شخص کفر کرے گا اس کے کفر کا وبال اسی پر پڑے گا۔ اور کافروں کے لئے ان کا کفر ان کے پروردگار کے نزدیک ناراضی ہی بڑھنے کا باعﺚ ہوتا ہے، اور کافروں کے لئے ان کا کفر خساره ہی بڑھنے کا باعﺚ ہوتا ہے'),
(3700, 35, 40, 'آپ کہیئے! کہ تم اپنے قرارداد شریکوں کا حال تو بتلاؤ جن کو تم اللہ کے سوا پوجا کرتے ہو۔ یعنی مجھ کو یہ بتلاؤ کہ انہوں نے زمین میں سے کون سا (جزو) بنایا ہے یا ان کا آسمانوں میں کچھ ساجھا ہے یا ہم نے ان کو کوئی کتاب دی ہے کہ یہ اس کی دلیل پر قائم ہوں، بلکہ یہ ﻇالم ایک دوسرے سے نرے دھوکے کی باتوں کا وعده کرتے آتے ہیں'),
(3701, 35, 41, 'یقینی بات ہے کہ اللہ تعالیٰ آسمانوں اور زمین کو تھامے ہوئے ہے کہ وه ٹل نہ جائیں اور اگر وه ٹل جائیں تو پھر اللہ کے سوا اور کوئی ان کو تھام بھی نہیں سکتا۔ وه حلیم غفور ہے'),
(3702, 35, 42, 'اور ان کفار نے بڑی زوردار قسم کھائی تھی کہ اگر ان کے پاس کوئی ڈرانے واﻻ آئے تو وه ہر ایک امت سے زیاده ہدایت قبول کرنے والے ہوں۔ پھر جب ان کے پاس ایک پیغمبر آ پہنچے تو بس ان کی نفرت ہی میں اضافہ ہوا'),
(3703, 35, 43, 'دنیا میں اپنے کو بڑا سمجھنے کی وجہ سے، اور ان کی بری تدبیروں کی وجہ سے اور بری تدبیروں کا وبال ان تدبیر والوں ہی پر پڑتا ہے، سو کیا یہ اسی دستور کے منتظر ہیں جو اگلے لوگوں کے ساتھ ہوتا رہا ہے۔ سو آپ اللہ کے دستور کو کبھی بدلتا ہوا نہ پائیں گے، اور آپ اللہ کے دستور کو کبھی منتقل ہوتا ہوا نہ پائیں گے'),
(3704, 35, 44, 'اور کیا یہ لوگ زمین میں چلے پھرے نہیں جس میں دیکھتے بھالتے کہ جو لوگ ان سے پہلے ہو گزرے ہیں ان کا انجام کیا ہوا؟ حاﻻنکہ وه قوت میں ان سے بڑھے ہوئے تھے، اور اللہ ایسا نہیں ہے کہ کوئی چیز اس کو ہرا دے نہ آسمانوں میں اور نہ زمین میں۔ وه بڑے علم واﻻ، بڑی قدرت واﻻ ہے'),
(3705, 35, 45, 'اور اگر اللہ تعالیٰ لوگوں پر ان کے اعمال کے سبب داروگیر فرمانے لگتا تو روئے زمین پر ایک جاندار کو نہ چھوڑتا، لیکن اللہ تعالیٰ ان کو ایک میعاد معین تک مہلت دے رہا ہے، سو جب ان کی وه میعاد آپہنچے گی اللہ تعالیٰ اپنے بندوں کو آپ دیکھ لے گا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3706, 36, 1, 'یٰس'),
(3707, 36, 2, 'قسم ہے قرآن باحکمت کی'),
(3708, 36, 3, 'کہ بے شک آپ پیغمبروں میں سے ہیں'),
(3709, 36, 4, 'سیدھے راستے پر ہیں'),
(3710, 36, 5, 'یہ قرآن اللہ زبردست مہربان کی طرف سے نازل کیا گیا ہے'),
(3711, 36, 6, 'تاکہ آپ ایسے لوگوں کو ڈرائیں جن کے باپ دادے نہیں ڈرائے گئے تھے، سو (اسی وجہ سے) یہ غافل ہیں'),
(3712, 36, 7, 'ان میں سے اکثر لوگوں پر بات ﺛابت ہوچکی ہے سو یہ لوگ ایمان نہ ﻻئیں گے'),
(3713, 36, 8, 'ہم نے ان کی گردنوں میں طوق ڈال دیئے ہیں پھر وه ٹھوڑیوں تک ہیں، جس سے ان کے سر اوپر کو الٹ گئے ہیں'),
(3714, 36, 9, 'اور ہم نے ایک آڑ ان کے سامنے کردی اورایک آڑ ان کے پیچھے کردی، جس سے ہم نے ان کو ڈھانک دیا سو وه نہیں دیکھ سکتے'),
(3715, 36, 10, 'اور آپ ان کو ڈرائیں یا نہ ڈرائیں دونوں برابر ہیں، یہ ایمان نہیں ﻻئیں گے'),
(3716, 36, 11, 'بس آپ تو صرف ایسے شخص کو ڈرا سکتے ہیں جو نصیحت پر چلے اور رحمٰن سے بےدیکھے ڈرے، سو آپ اس کو مغفرت اور باوقار اجر کی خوش خبریاں سنا دیجیئے'),
(3717, 36, 12, 'بےشک ہم مُردوں کو زنده کریں گے، اور ہم لکھتے جاتے ہیں وه اعمال بھی جن کو لوگ آگے بھیجتے ہیں اور ان کے وه اعمال بھی جن کو پیچھے چھوڑ جاتے ہیں، اور ہم نے ہر چیز کو ایک واضح کتاب میں ضبط کر رکھا ہے'),
(3718, 36, 13, 'اور آپ ان کے سامنے ایک مثال (یعنی ایک) بستی والوں کی مثال (اس وقت کا) بیان کیجیئے جب کہ اس بستی میں (کئی) رسول آئے'),
(3719, 36, 14, 'جب ہم نے ان کے پاس دو کو بھیجا سو ان لوگوں نے (اول) دونوں کو جھٹلایا پھر ہم نے تیسرے سے تائید کی سو ان تینوں نے کہا کہ ہم تمہارے پاس بھیجے گئے ہیں'),
(3720, 36, 15, 'ان لوگوں نے کہا کہ تم تو ہماری طرح معمولی آدمی ہو اور رحمٰن نے کہا کوئی چیز نازل نہیں کی۔ تم نرا جھوٹ بولتے ہو'),
(3721, 36, 16, 'ان (رسولوں) نے کہا ہمارا پروردگار جانتا ہے کہ بےشک ہم تمہارے پاس بھیجے گئے ہیں'),
(3722, 36, 17, 'اور ہمارے ذمہ تو صرف واضح طور پر پہنچا دینا ہے'),
(3723, 36, 18, 'انہوں نے کہا کہ ہم تو تم کو منحوس سمجھتے ہیں۔ اگر تم باز نہ آئے تو ہم پتھروں سے تمہارا کام تمام کردیں گے اور تم کو ہماری طرف سے سخت تکلیف پہنچے گی'),
(3724, 36, 19, 'ان رسولوں نے کہا کہ تمہاری نحوست تمہارے ساتھ ہی لگی ہوئی ہے، کیا اس کو نحوست سمجھتے ہو کہ تم کو نصیحت کی جائے بلکہ تم حد سے نکل جانے والے لوگ ہو'),
(3725, 36, 20, 'اور ایک شخص (اس) شہر کے آخری حصے سے دوڑتا ہوا آیا کہنے لگا کہ اے میری قوم! ان رسولوں کی راه پر چلو'),
(3726, 36, 21, 'ایسے لوگوں کی راه پر چلو جو تم سے کوئی معاوضہ نہیں مانگتے اور وه راه راست پر ہیں'),
(3727, 36, 22, 'اور مجھے کیا ہو گیا ہے کہ میں اس کی عبادت نہ کروں جس نے مجھے پیدا کیا اور تم سب اسی کی طرف لوٹائے جاؤگے'),
(3728, 36, 23, 'کیا میں اسے چھوڑ کر ایسوں کو معبود بناؤں کہ اگر (اللہ) رحمٰن مجھے کوئی نقصان پہنچانا چاہے تو ان کی سفارش مجھے کچھ بھی نفع نہ پہنچا سکے اور نہ وه مجھے بچا سکیں'),
(3729, 36, 24, 'پھر تو میں یقیناً کھلی گمراہی میں ہوں'),
(3730, 36, 25, 'میری سنو! میں تو (سچے دل سے) تم سب کے رب پر ایمان ﻻ چکا'),
(3731, 36, 26, '(اس سے) کہا گیا کہ جنت میں چلا جا، کہنے لگا کاش! میری قوم کو بھی علم ہو جاتا'),
(3732, 36, 27, 'کہ مجھے میرے رب نے بخش دیا اور مجھے باعزت لوگوں میں سے کر دیا'),
(3733, 36, 28, 'اس کے بعد ہم نے اس کی قوم پر آسمان سے کوئی لشکر نہ اتارا، اور نہ اس طرح ہم اتارا کرتے ہیں'),
(3734, 36, 29, 'وه تو صرف ایک زور کی چیﺦ تھی کہ یکایک وه سب کے سب بجھ بجھا گئے'),
(3735, 36, 30, '(ایسے) بندوں پر افسوس! کبھی بھی کوئی رسول ان کے پاس نہیں آیا جس کی ہنسی انہوں نے نہ اڑائی ہو'),
(3736, 36, 31, 'کیا انہوں نے نہیں دیکھا کہ ان کے پہلے بہت سی قوموں کو ہم نے غارت کر دیا کہ وه ان کی طرف لوٹ کر نہیں آئیں گے'),
(3737, 36, 32, 'اور نہیں ہے کوئی جماعت مگر یہ کہ وه جمع ہو کر ہمارے سامنے حاضر کی جائے گی'),
(3738, 36, 33, 'اور ان کے لئے ایک نشانی (خشک) زمین ہے جس کو ہم نے زنده کر دیا اور اس سے غلہ نکالا جس میں سے وه کھاتے ہیں'),
(3739, 36, 34, 'اور ہم نے اس میں کھجوروں کے اور انگور کے باغات پیدا کر دیئے، اور جن میں ہم نے چشمے بھی جاری کر دیئے ہیں'),
(3740, 36, 35, 'تاکہ (لوگ) اس کے پھل کھائیں، اور اس کو ان کے ہاتھوں نے نہیں بنایا۔ پھر کیوں شکر گزاری نہیں کرتے'),
(3741, 36, 36, 'وه پاک ذات ہے جس نے ہر چیز کے جوڑے پیدا کیے خواه وه زمین کی اگائی ہوئی چیزیں ہوں، خواه خود ان کے نفوس ہوں خواه وه (چیزیں) ہوں جنہیں یہ جانتے بھی نہیں'),
(3742, 36, 37, 'اور ان کے لئے ایک نشانی رات ہے جس سے ہم دن کو کھینچ دیتے ہیں تو وه یکایک اندھیرے میں ره جاتے ہیں'),
(3743, 36, 38, 'اور سورج کے لئے جو مقرره راه ہے وه اسی پر چلتا رہتا ہے۔ یہ ہے مقرر کرده غالب، باعلم اللہ تعالیٰ کا'),
(3744, 36, 39, 'اور چاند کی ہم نے منزلیں مقررکر رکھی ہیں، یہاں تک کہ وه لوٹ کر پرانی ٹہنی کی طرح ہو جاتا ہے'),
(3745, 36, 40, 'نہ آفتاب کی یہ مجال ہے کہ چاند کو پکڑے اور نہ رات دن پرآگے بڑھ جانے والی ہے، اور سب کے سب آسمان میں تیرتے پھرتے ہیں'),
(3746, 36, 41, 'اور ان کے لئے ایک نشانی (یہ بھی) ہے کہ ہم نے ان کی نسل کو بھری ہوئی کشتی میں سوار کیا'),
(3747, 36, 42, 'اور ان کے لئے اسی جیسی اور چیزیں پیدا کیں جن پر یہ سوار ہوتے ہیں'),
(3748, 36, 43, 'اور اگر ہم چاہتے تو انہیں ڈبو دیتے۔ پھر نہ تو کوئی ان کا فریاد رس ہوتا نہ وه بچائے جائیں'),
(3749, 36, 44, 'لیکن ہم اپنی طرف سے رحمت کرتے ہیں اور ایک مدت تک کے لئے انہیں فائدے دے رہے ہیں'),
(3750, 36, 45, 'اور ان سے جب (کبھی) کہا جاتا ہے کہ اگلے پچھلے (گناہوں) سے بچو تاکہ تم پر رحم کیا جائے'),
(3751, 36, 46, 'اور ان کے پاس تو ان کے رب کی نشانیوں میں سے کوئی نشانی ایسی نہیں آئی جس سے یہ بے رخی نہ برتتے ہوں'),
(3752, 36, 47, 'اور ان سے جب کہا جاتا ہے کہ اللہ تعالیٰ کے دیئے ہوئے میں سے کچھ خرچ کرو، تو یہ کفار ایمان والوں کو جواب دیتے ہیں کہ ہم انہیں کیوں کھلائیں؟ جنہیں اگر اللہ تعالیٰ چاہتا تو خود کھلا پلا دیتا، تم تو ہو ہی کھلی گمراہی میں'),
(3753, 36, 48, 'وه کہتے ہیں کہ یہ وعده کب ہوگا، سچے ہو تو بتلاؤ'),
(3754, 36, 49, 'انہیں صرف ایک سخت چیﺦ کاانتظار ہے جو انہیں آپکڑے گی اور یہ باہم لڑائی جھگڑے میں ہی ہوں گے'),
(3755, 36, 50, 'اس وقت نہ تو یہ وصیت کر سکیں گے اور نہ اپنے اہل کی طرف لوٹ سکیں گے'),
(3756, 36, 51, 'تو صور کے پھونکے جاتے ہی سب کے سب اپنی قبروں سے اپنے پروردگار کی طرف (تیز تیز) چلنے لگیں گے'),
(3757, 36, 52, 'کہیں گے ہائے ہائے! ہمیں ہماری خواب گاہوں سے کس نے اٹھا دیا۔ یہی ہے جس کا وعده رحمٰن نے دیا تھا اور رسولوں نے سچ سچ کہہ دیا تھا'),
(3758, 36, 53, 'یہ نہیں ہے مگر ایک چیﺦ کہ یکایک سارے کے سارے ہمارے سامنے حاضر کر دیئے جائیں گے'),
(3759, 36, 54, 'پس آج کسی شخص پر کچھ بھی ﻇلم نہ کیا جائے گا اور تمہیں نہیں بدلہ دیا جائے گا، مگر صرف ان ہی کاموں کا جو تم کیا کرتے تھے'),
(3760, 36, 55, 'جنتی لوگ آج کے دن اپنے (دلچسﭗ) مشغلوں میں ہشاش بشاش ہیں'),
(3761, 36, 56, 'وه اور ان کی بیویاں سایوں میں مسہریوں پر تکیہ لگائے بیٹھے ہوں گے'),
(3762, 36, 57, 'ان کے لئے جنت میں ہر قسم کے میوے ہوں گے اور بھی جو کچھ وه طلب کریں'),
(3763, 36, 58, 'مہربان پروردگار کی طرف سے انہیں سلام کہا جائے گا'),
(3764, 36, 59, 'اے گناهگارو! آج تم الگ ہو جاؤ'),
(3765, 36, 60, 'اے اوﻻد آدم! کیا میں نے تم سے قول قرار نہیں لیا تھا کہ تم شیطان کی عبادت نہ کرنا، وه تو تمہارا کھلا دشمن ہے'),
(3766, 36, 61, 'اور میری ہی عبادت کرنا۔ سیدھی راه یہی ہے'),
(3767, 36, 62, 'شیطان نے تو تم میں سے بہت ساری مخلوق کو بہکا دیا۔ کیا تم عقل نہیں رکھتے'),
(3768, 36, 63, 'یہی وه دوزخ ہے جس کا تمہیں وعده دیا جاتا تھا'),
(3769, 36, 64, 'اپنے کفر کا بدلہ پانے کے لئے آج اس میں داخل ہوجاؤ'),
(3770, 36, 65, 'ہم آج کے دن ان کے منھ پر مہریں لگا دیں گے اور ان کے ہاتھ ہم سے باتیں کریں گے اور ان کے پاؤں گواہیاں دیں گے، ان کاموں کی جو وه کرتے تھے'),
(3771, 36, 66, 'اگر ہم چاہتے تو ان کی آنکھیں بے نور کر دیتے پھر یہ رستے کی طرف دوڑتے پھرتے لیکن انہیں کیسے دکھائی دیتا؟'),
(3772, 36, 67, 'اور اگر ہم چاہتے تو ان کی جگہ ہی پر ان کی صورتیں مسﺦ کر دیتے پھر نہ وه چل پھر سکتے اور نہ لوٹ سکتے'),
(3773, 36, 68, 'اور جسے ہم بوڑھا کرتے ہیں اسے پیدائشی حالت کی طرف پھر الٹ دیتے ہیں کیا پھر بھی وه نہیں سمجھتے'),
(3774, 36, 69, 'نہ تو ہم نے اس پیغمبر کو شعر سکھائے اور نہ یہ اس کے ﻻئق ہے۔ وه تو صرف نصیحت اور واضح قرآن ہے'),
(3775, 36, 70, 'تاکہ وه ہر اس شخص کو آگاه کر دے جو زنده ہے، اور کافروں پر حجت ﺛابت ہو جائے'),
(3776, 36, 71, 'کیا وه نہیں دیکھتے کہ ہم نے اپنے ہاتھوں سے بنائی ہوئی چیزوں میں سے ان کے لئے چوپائے (بھی) پیدا کر دیئے، جن کے یہ مالک ہوگئے ہیں'),
(3777, 36, 72, 'اور ان مویشیوں کو ہم نے ان کا تابع فرمان بنا دیا ہے جن میں سے بعض تو ان کی سواریاں ہیں اور بعض کا گوشت کھاتے ہیں'),
(3778, 36, 73, 'انہیں ان سے اور بھی بہت سے فائدے ہیں، اور پینے کی چیزیں۔ کیا پھر (بھی) یہ شکر ادا نہیں کریں گے؟'),
(3779, 36, 74, 'اور وه اللہ کے سوا دوسروں کو معبود بناتے ہیں تاکہ وه مدد کئے جائیں'),
(3780, 36, 75, '(حاﻻنکہ) ان میں ان کی مدد کی طاقت ہی نہیں، (لیکن) پھر بھی (مشرکین) ان کے لئے حاضر باش لشکری ہیں'),
(3781, 36, 76, 'پس آپ کو ان کی بات غمناک نہ کرے، ہم ان کی پوشیده اور علانیہ سب باتوں کو (بخوبی) جانتے ہیں'),
(3782, 36, 77, 'کیا انسان کو اتنا بھی معلوم نہیں کہ ہم نے اسے نطفے سے پیدا کیا ہے؟ پھر یکایک وہ صریح جھگڑالو بن بیٹھا'),
(3783, 36, 78, 'اور اس نے ہمارے لئے مثال بیان کی اور اپنی (اصل) پیدائش کو بھول گیا، کہنے لگا ان گلی سڑی ہڈیوں کو کون زنده کر سکتا ہے؟'),
(3784, 36, 79, 'آپ جواب دیجئے! کہ انہیں وه زنده کرے گا جس نے انہیں اول مرتبہ پیدا کیا ہے، جو سب طرح کی پیدائش کا بخوبی جاننے واﻻ ہے'),
(3785, 36, 80, 'وہی جس نے تمہارے لئے سبز درخت سے آگ پیدا کر دی جس سے تم یکایک آگ سلگاتے ہو'),
(3786, 36, 81, 'جس نے آسمانوں اور زمین کو پیدا کیا ہے کیا وه ان جیسوں کے پیدا کرنے پرقادر نہیں، بےشک قادر ہے۔ اور وہی تو پیدا کرنے واﻻ دانا (بینا) ہے'),
(3787, 36, 82, 'وه جب کبھی کسی چیز کا اراده کرتا ہے اسے اتنا فرما دینا (کافی ہے) کہ ہو جا، وه اسی وقت ہو جاتی ہے'),
(3788, 36, 83, 'پس پاک ہے وه اللہ جس کے ہاتھ میں ہر چیز کی بادشاہت ہے اور جس کی طرف تم سب لوٹائے جاؤ گے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3789, 37, 1, 'قسم ہے صف باندھنے والے (فرشتوں) کی'),
(3790, 37, 2, 'پھر پوری طرح ڈانٹنے والوں کی'),
(3791, 37, 3, 'پھر ذکر اللہ کی تلاوت کرنے والوں کی'),
(3792, 37, 4, 'یقیناً تم سب کا معبود ایک ہی ہے'),
(3793, 37, 5, 'آسمانوں اور زمین اور ان کے درمیان کی تمام چیزوں اور مَشرقوں کا رب وہی ہے'),
(3794, 37, 6, 'ہم نے آسمان دنیا کو ستاروں کی زینت سے آراستہ کیا'),
(3795, 37, 7, 'اور حفاﻇت کی سرکش شیطان سے'),
(3796, 37, 8, 'عالم باﻻ کے فرشتوں (کی باتوں) کو سننے کے لئے وه کان بھی نہیں لگا سکتے، بلکہ ہر طرف سے وه مارے جاتے ہیں'),
(3797, 37, 9, 'بھگانے کے لئے اور ان کے لئے دائمی عذاب ہے'),
(3798, 37, 10, 'مگر جو کوئی ایک آدھ بات اچک لے بھاگے تو (فوراً ہی) اس کے پیچھے دہکتا ہوا شعلہ لگ جاتا ہے'),
(3799, 37, 11, 'ان کافروں سے پوچھو تو کہ آیا ان کا پیدا کرنا زیاده دشوار ہے یا (ان کا) جنہیں ہم نے (ان کے علاوه) پیدا کیا؟ ہم نے (انسانوں) کو لیس دار مٹی سے پیدا کیا ہے'),
(3800, 37, 12, 'بلکہ تو تعجب کر رہا ہے اور یہ مسخرا پن کر رہے ہیں'),
(3801, 37, 13, 'اور جب انہیں نصیحت کی جاتی ہے یہ نہیں مانتے'),
(3802, 37, 14, 'اور جب کسی معجزے کو دیکھتے ہیں تو مذاق اڑاتے ہیں'),
(3803, 37, 15, 'اور کہتے ہیں کہ یہ تو بالکل کھلم کھلا جادو ہی ہے'),
(3804, 37, 16, 'کیا جب ہم مر جائیں گے اور خاک اور ہڈی ہو جائیں گے پھر کیا (سچ مچ) ہم اٹھائے جائیں گے؟'),
(3805, 37, 17, 'کیا ہم سے پہلے کے ہمارے باپ دادا بھی؟'),
(3806, 37, 18, 'آپ جواب دیجئے! کہ ہاں ہاں اور تم ذلیل (بھی) ہوں گے'),
(3807, 37, 19, 'وه تو صرف ایک زور کی جھڑکی ہے کہ یکایک یہ دیکھنے لگیں گے'),
(3808, 37, 20, 'اور کہیں گے کہ ہائے ہماری خرابی یہی جزا (سزا) کا دن ہے'),
(3809, 37, 21, 'یہی فیصلہ کا دن ہے جسے تم جھٹلاتے رہے'),
(3810, 37, 22, 'ﻇالموں کو اور ان کے ہمراہیوں کو اور (جن) جن کی وه اللہ کے علاوه پرستش کرتے تھے'),
(3811, 37, 23, '(ان سب کو) جمع کرکے انہیں دوزخ کی راه دکھا دو'),
(3812, 37, 24, 'اور انہیں ٹھہرا لو، (اس لئے) کہ ان سے (ضروری) سوال کیے جانے والے ہیں'),
(3813, 37, 25, 'تمہیں کیا ہو گیا ہے کہ (اس وقت) تم ایک دوسرے کی مدد نہیں کرتے'),
(3814, 37, 26, 'بلکہ وه (سب کے سب) آج فرمانبردار بن گئے'),
(3815, 37, 27, 'وه ایک دوسرے کی طرف متوجہ ہو کر سوال وجواب کرنے لگیں گے'),
(3816, 37, 28, 'کہیں گے کہ تم تو ہمارے پاس ہماری دائیں طرف سے آتے تھے'),
(3817, 37, 29, 'وه جواب دیں گے کہ نہیں بلکہ تم ہی ایمان والے نہ تھے'),
(3818, 37, 30, 'اور کچھ ہمارا زور تو تم پر تھا (ہی) نہیں۔ بلکہ تم (خود) سرکش لوگ تھے'),
(3819, 37, 31, 'اب تو ہم (سب) پر ہمارے رب کی یہ بات ﺛابت ہو چکی کہ ہم (عذاب) چکھنے والے ہیں'),
(3820, 37, 32, 'پس ہم نے تمہیں گمراه کیا ہم تو خود بھی گمراه ہی تھے'),
(3821, 37, 33, 'سو اب آج کے دن تو (سب کے سب) عذاب میں شریک ہیں'),
(3822, 37, 34, 'ہم گناه گاروں کے ساتھ اسی طرح کیا کرتے ہیں'),
(3823, 37, 35, 'یہ وه (لوگ) ہیں کہ جب ان سے کہا جاتا ہے کہ اللہ کے سوا کوئی معبود نہیں تو یہ سرکشی کرتے تھے'),
(3824, 37, 36, 'اور کہتے تھے کہ کیا ہم اپنے معبودوں کو ایک دیوانے شاعر کی بات پر چھوڑ دیں؟'),
(3825, 37, 37, '(نہیں نہیں) بلکہ (نبی) تو حق (سچا دین) ﻻئے ہیں اور سب رسولوں کو سچا جانتے ہیں'),
(3826, 37, 38, 'یقیناً تم دردناک عذاب (کا مزه) چکھنے والے ہو'),
(3827, 37, 39, 'تمہیں اسی کا بدلہ دیا جائے گا جو تم کرتے تھے'),
(3828, 37, 40, 'مگر اللہ تعالیٰ کے خالص برگزیده بندے'),
(3829, 37, 41, 'انہیں کے لئے مقرره روزی ہے'),
(3830, 37, 42, '(ہر طرح کے) میوے، اور وه باعزت واکرام ہوں گے'),
(3831, 37, 43, 'نعمتوں والی جنتوں میں'),
(3832, 37, 44, 'تختوں پر ایک دوسرے کے سامنے (بیٹھے) ہوں گے'),
(3833, 37, 45, 'جاری شراب کے جام کا ان پر دور چل رہا ہوگا'),
(3834, 37, 46, 'جو صاف شفاف اور پینے میں لذیذ ہوگی'),
(3835, 37, 47, 'نہ اس سے درد سر ہو اور نہ اس کے پینے سے بہکیں'),
(3836, 37, 48, 'اور ان کے پاس نیچی نظروں، بڑی بڑی آنکھوں والی (حوریں) ہوں گی'),
(3837, 37, 49, 'ایسی جیسے چھپائے ہوئے انڈے'),
(3838, 37, 50, '(جنتی) ایک دوسرے کی طرف رخ کرکے پوچھیں گے'),
(3839, 37, 51, 'ان میں سے ایک کہنے واﻻ کہے گا کہ میرا ایک ساتھی تھا'),
(3840, 37, 52, 'جو (مجھ سے) کہا کرتا تھا کہ کیا تو (قیامت کے آنے کا) یقین کرنے والوں میں سے ہے؟'),
(3841, 37, 53, 'کیا جب کہ ہم مر کر مٹی اور ہڈی ہو جائیں گے کیا اس وقت ہم جزا دیئے جانے والے ہیں؟'),
(3842, 37, 54, 'کہے گا تم چاہتے ہو کہ جھانک کر دیکھ لو؟'),
(3843, 37, 55, 'جھانکتے ہی اسے بیچوں بیچ جہنم میں (جلتا ہوا) دیکھے گا'),
(3844, 37, 56, 'کہے گا واللہ! قریب تھا کہ تو مجھے (بھی) برباد کر دے'),
(3845, 37, 57, 'اگر میرے رب کا احسان نہ ہوتا تو میں بھی دوزخ میں حاضر کئے جانے والوں میں ہوتا'),
(3846, 37, 58, 'کیا (یہ صحیح ہے) کہ ہم مرنے والے ہی نہیں؟'),
(3847, 37, 59, 'بجز پہلی ایک موت کے، اور نہ ہم عذاب کیے جانے والے ہیں'),
(3848, 37, 60, 'پھر تو (ﻇاہر بات ہے کہ) یہ بڑی کامیابی ہے'),
(3849, 37, 61, 'ایسی (کامیابی) کے لئے عمل کرنے والوں کو عمل کرنا چاہئے'),
(3850, 37, 62, 'کیا یہ مہمانی اچھی ہے یا سینڈھ (زقوم) کا درخت؟'),
(3851, 37, 63, 'جسے ہم نے ﻇالموں کے لئے سخت آزمائش بنا رکھا ہے'),
(3852, 37, 64, 'بے شک وه درخت جہنم کی جڑ میں سے نکلتا ہے'),
(3853, 37, 65, 'جس کے خوشے شیطانوں کے سروں جیسے ہوتے ہیں'),
(3854, 37, 66, '(جہنمی) اسی درخت سے کھائیں گے اور اسی سے پیٹ بھریں گے'),
(3855, 37, 67, 'پھر اس پر گرم جلتے جلتے پانی کی ملونی ہوگی'),
(3856, 37, 68, 'پھر ان سب کا لوٹنا جہنم کی (آگ کے ڈھیرکی) طرف ہوگا'),
(3857, 37, 69, 'یقین مانو! کہ انہوں نے اپنے باپ دادا کو بہکا ہوا پایا'),
(3858, 37, 70, 'اور یہ انہی کے نشان قدم پر دوڑتے رہے'),
(3859, 37, 71, 'ان سے پہلے بھی بہت سے اگلے بہک چکے ہیں'),
(3860, 37, 72, 'جن میں ہم نے ڈرانے والے (رسول) بھیجے تھے'),
(3861, 37, 73, 'اب تو دیکھ لے کہ جنہیں دھمکایا گیا تھا ان کا انجام کیسا کچھ ہوا'),
(3862, 37, 74, 'سوائے اللہ کے برگزیده بندوں کے'),
(3863, 37, 75, 'اور ہمیں نوح (علیہ السلام) نے پکارا تو (دیکھ لو) ہم کیسے اچھے دعا قبول کرنے والے ہیں'),
(3864, 37, 76, 'ہم نے اسے اور اس کے گھر والوں کو اس زبردست مصیبت سے بچا لیا'),
(3865, 37, 77, 'اور اس کی اوﻻد کو ہم نے باقی رہنے والی بنا دی'),
(3866, 37, 78, 'اور ہم نے اس کا (ذکر خیر) پچھلوں میں باقی رکھا'),
(3867, 37, 79, 'نوح (علیہ السلام) پر تمام جہانوں میں سلام ہو'),
(3868, 37, 80, 'ہم نیکی کرنے والوں کو اسی طرح بدلہ دیتے ہیں'),
(3869, 37, 81, 'وه ہمارے ایمان والے بندوں میں سے تھا'),
(3870, 37, 82, 'پھر ہم نے دوسروں کو ڈبو دیا'),
(3871, 37, 83, 'اور اس (نوح علیہ السلام کی) تابعداری کرنے والوں میں سے (ہی) ابراہیم (علیہ السلام بھی) تھے'),
(3872, 37, 84, 'جبکہ اپنے رب کے پاس بے عیب دل ﻻئے'),
(3873, 37, 85, 'انہوں نے اپنے باپ اور اپنی قوم سے کہا کہ تم کیا پوج رہے ہو؟'),
(3874, 37, 86, 'کیا تم اللہ کے سوا گھڑے ہوئے معبود چاہتے ہو؟'),
(3875, 37, 87, 'تو یہ (بتلاؤ کہ) تم نے رب العالمین کو کیا سمجھ رکھا ہے؟'),
(3876, 37, 88, 'اب ابراہیم (علیہ السلام) نے ایک نگاه ستاروں کی طرف اٹھائی'),
(3877, 37, 89, 'اور کہا میں تو بیمار ہوں'),
(3878, 37, 90, 'اس پر وه سب اس سے منھ موڑے ہوئے واپس چلے گئے'),
(3879, 37, 91, 'آپ (چﭗ چپاتے) ان کے معبودوں کے پاس گئے اور فرمانے لگے تم کھاتے کیوں نہیں؟'),
(3880, 37, 92, 'تمہیں کیا ہو گیا کہ بات تک نہیں کرتے ہو'),
(3881, 37, 93, 'پھر تو (پوری قوت کے ساتھ) دائیں ہاتھ سے انہیں مارنے پر پل پڑے'),
(3882, 37, 94, 'وه (بت پرست) دوڑے بھاگے آپ کی طرف متوجہ ہوئے'),
(3883, 37, 95, 'تو آپ نے فرمایا تم انہیں پوجتے ہو جنہیں (خود) تم تراشتے ہو'),
(3884, 37, 96, 'حاﻻنکہ تمہیں اور تمہاری بنائی ہوئی چیزوں کو اللہ ہی نے پیدا کیا ہے'),
(3885, 37, 97, 'وه کہنے لگے اس کے لئے ایک مکان بناؤ اور اس (دہکتی ہوئی) آگ میں اسے ڈال دو'),
(3886, 37, 98, 'انہوں نے تو اس (ابراہیم علیہ السلام) کے ساتھ مکر کرنا چاہا لیکن ہم نے انہی کو نیچا کر دیا'),
(3887, 37, 99, 'اور اس (ابراہیم علیہ السلام) نے کہا میں تو ہجرت کر کے اپنے پروردگار کی طرف جانے واﻻ ہوں۔ وه ضرور میری رہنمائی کرے گا'),
(3888, 37, 100, 'اے میرے رب! مجھے نیک بخت اوﻻد عطا فرما'),
(3889, 37, 101, 'تو ہم نے اسے ایک بردبار بچے کی بشارت دی'),
(3890, 37, 102, 'پھر جب وه (بچہ) اتنی عمر کو پہنچا کہ اس کے ساتھ چلے پھرے، تو اس (ابراہیم علیہ السلام) نے کہا میرے پیارے بچے! میں خواب میں اپنے آپ کو تجھے ذبح کرتے ہوئے دیکھ رہا ہوں۔ اب تو بتا کہ تیری کیا رائے ہے؟ بیٹے نے جواب دیا کہ ابا! جو حکم ہوا ہے اسے بجا ﻻئیے انشاءاللہ آپ مجھے صبر کرنے والوں میں سے پائیں گے'),
(3891, 37, 103, 'غرض جب دونوں مطیع ہوگئے اور اس نے (باپ نے) اس کو (بیٹے کو) پیشانی کے بل گرا دیا'),
(3892, 37, 104, 'تو ہم نے آواز دی کہ اے ابراہیم!'),
(3893, 37, 105, 'یقیناً تو نے اپنے خواب کو سچا کر دکھایا، بیشک ہم نیکی کرنے والوں کو اسی طرح جزا دیتے ہیں'),
(3894, 37, 106, 'درحقیقت یہ کھلا امتحان تھا'),
(3895, 37, 107, 'اور ہم نے ایک بڑا ذبیحہ اس کے فدیہ میں دے دیا'),
(3896, 37, 108, 'اور ہم نے ان کا ذکر خیر پچھلوں میں باقی رکھا'),
(3897, 37, 109, 'ابراہیم (علیہ السلام) پر سلام ہو'),
(3898, 37, 110, 'ہم نیکو کاروں کو اسی طرح بدلہ دیتے ہیں'),
(3899, 37, 111, 'بیشک وه ہمارے ایمان دار بندوں میں سے تھا'),
(3900, 37, 112, 'اور ہم نے اس کو اسحاق (علیہ السلام) نبی کی بشارت دی جو صالح لوگوں میں سے ہوگا'),
(3901, 37, 113, 'اور ہم نے ابراہیم واسحاق (علیہ السلام) پر برکتیں نازل فرمائیں، اور ان دونوں کی اوﻻد میں بعضے تو نیک بخت ہیں اور بعض اپنے نفس پر صریح ﻇلم کرنے والے ہیں'),
(3902, 37, 114, 'یقیناً ہم نے موسیٰ اور ہارون (علیہ السلام) پر بڑا احسان کیا'),
(3903, 37, 115, 'اور انہیں اور ان کی قوم کو بہت بڑے دکھ درد سے نجات دے دی'),
(3904, 37, 116, 'اور ان کی مدد کی تو وہی غالب رہے'),
(3905, 37, 117, 'اور ہم نے انہیں (واضح اور) روشن کتاب دی'),
(3906, 37, 118, 'اور انہیں سیدھے راستہ پرقائم رکھا'),
(3907, 37, 119, 'اور ہم نے ان دونوں کے لئے پیچھے آنے والوں میں یہ بات باقی رکھی'),
(3908, 37, 120, 'کہ موسیٰ اور ہارون (علیہما السلام) پر سلام ہو'),
(3909, 37, 121, 'بے شک ہم نیک لوگوں کو اسی طرح بدلے دیا کرتے ہیں'),
(3910, 37, 122, 'یقیناً یہ دونوں ہمارے مومن بندوں میں سے تھے'),
(3911, 37, 123, 'بے شک الیاس (علیہ السلام) بھی پیغمبروں میں سے تھے'),
(3912, 37, 124, 'جب کہ انہوں نے اپنی قوم سے فرمایا کہ تم اللہ سے ڈرتے نہیں ہو؟'),
(3913, 37, 125, 'کیا تم بعل (نامی بت) کو پکارتے ہو؟ اور سب سے بہتر خالق کو چھوڑ دیتے ہو؟'),
(3914, 37, 126, 'اللہ جو تمہارا اور تمہارے اگلے تمام باپ دادوں کا رب ہے'),
(3915, 37, 127, 'لیکن قوم نے انہیں جھٹلایا، پس وه ضرور (عذاب میں) حاضر رکھے جائیں گے'),
(3916, 37, 128, 'سوائے اللہ تعالی کے مخلص بندوں کے'),
(3917, 37, 129, 'ہم نے (الیاس علیہ السلام) کا ذکر خیر پچھلوں میں بھی باقی رکھا'),
(3918, 37, 130, 'کہ الیاس پر سلام ہو'),
(3919, 37, 131, 'ہم نیکی کرنے والوں کو اسی طرح بدلہ دیتے ہیں'),
(3920, 37, 132, 'بیشک وه ہمارے ایمان دار بندوں میں سے تھے'),
(3921, 37, 133, 'بیشک لوط (علیہ السلام بھی) پیغمبروں میں سے تھے'),
(3922, 37, 134, 'ہم نے انہیں اور ان کے گھر والوں کو سب کو نجات دی'),
(3923, 37, 135, 'بجز اس بڑھیا کے جو پیچھے ره جانے والوں میں سے ره گئی'),
(3924, 37, 136, 'پھر ہم نے اوروں کو ہلاک کر دیا'),
(3925, 37, 137, 'اور تم تو صبح ہونے پر ان کی بستیوں کے پاس سے گزرتے ہو'),
(3926, 37, 138, 'اور رات کو بھی، کیا پھر بھی نہیں سمجھتے؟'),
(3927, 37, 139, 'اور بلاشبہ یونس (علیہ السلام) نبیوں میں سے تھے'),
(3928, 37, 140, 'جب بھاگ کر پہنچے بھری کشتی پر'),
(3929, 37, 141, 'پھر قرعہ اندازی ہوئی تو یہ مغلوب ہوگئے'),
(3930, 37, 142, 'تو پھر انہیں مچھلی نے نگل لیا اور وه خود اپنے آپ کو ملامت کرنے لگ گئے'),
(3931, 37, 143, 'پس اگر یہ پاکی بیان کرنے والوں میں سے نہ ہوتے'),
(3932, 37, 144, 'تو لوگوں کے اٹھائے جانے کے دن تک اس کے پیٹ میں ہی رہتے'),
(3933, 37, 145, 'پس انہیں ہم نے چٹیل میدان میں ڈال دیا اور وه اس وقت بیمار تھے'),
(3934, 37, 146, 'اور ان پر سایہ کرنے واﻻ ایک بیل دار درخت ہم نے اگا دیا'),
(3935, 37, 147, 'اور ہم نے انہیں ایک لاکھ بلکہ اور زیاده آدمیوں کی طرف بھیجا'),
(3936, 37, 148, 'پس وه ایمان ﻻئے، اور ہم نے انہیں ایک زمانہ تک عیش وعشرت دی'),
(3937, 37, 149, 'ان سے دریافت کیجئے! کہ کیا آپ کے رب کی تو بیٹیاں ہیں اور ان کے بیٹے ہیں؟'),
(3938, 37, 150, 'یا یہ اس وقت موجود تھے جبکہ ہم نے فرشتوں کو مؤنﺚ پیدا کیا'),
(3939, 37, 151, 'آگاه رہو! کہ یہ لوگ صرف اپنی افترا پردازی سے کہہ رہے ہیں'),
(3940, 37, 152, 'کہ اللہ تعالی کی اوﻻد ہے۔ یقیناً یہ محض جھوٹے ہیں'),
(3941, 37, 153, 'کیا اللہ تعالی نے اپنے لیے بیٹیوں کو بیٹوں پر ترجیح دی'),
(3942, 37, 154, 'تمہیں کیا ہو گیا ہے کیسے حکم لگاتے پھرتے ہو؟'),
(3943, 37, 155, 'کیا تم اس قدر بھی نہیں سمجھتے؟'),
(3944, 37, 156, 'یا تمہارے پاس اس کی کوئی صاف دلیل ہے'),
(3945, 37, 157, 'تو جاؤ اگر سچے ہو تو اپنی ہی کتاب لے آؤ'),
(3946, 37, 158, 'اور ان لوگوں نے تو اللہ کے اور جنات کے درمیان بھی قرابت داری ٹھہرائی ہے، اور حاﻻنکہ خود جنات کو معلوم ہے کہ وه (اس عقیده کے لوگ عذاب کے سامنے) پیش کیے جائیں گے'),
(3947, 37, 159, 'جو کچھ یہ (اللہ کے بارے میں) بیان کر رہے ہیں اس سے اللہ تعالیٰ بالکل پاک ہے'),
(3948, 37, 160, 'سوائے! اللہ کے مخلص بندوں کے'),
(3949, 37, 161, 'یقین مانو کہ تم سب اور تمہارے معبودان (باطل)'),
(3950, 37, 162, 'کسی ایک کو بھی بہکا نہیں سکتے'),
(3951, 37, 163, 'بجز اس کے جو جہنمی ہی ہے'),
(3952, 37, 164, '(فرشتوں کا قول ہے کہ) ہم میں سے تو ہر ایک کی جگہ مقرر ہے'),
(3953, 37, 165, 'اور ہم تو (بندگیٴ الٰہی میں) صف بستہ کھڑے ہیں'),
(3954, 37, 166, 'اور اس کی تسبیح بیان کر رہے ہیں'),
(3955, 37, 167, 'کفار تو کہا کرتے تھے'),
(3956, 37, 168, 'کہ اگر ہمارے سامنے اگلے لوگوں کا ذکر ہوتا'),
(3957, 37, 169, 'تو ہم بھی اللہ کے چیده بندے بن جاتے'),
(3958, 37, 170, 'لیکن پھر اس قرآن کے ساتھ کفر کر گئے، پس اب عنقریب جان لیں گے'),
(3959, 37, 171, 'اور البتہ ہمارا وعده پہلے ہی اپنے رسولوں کے لئے صادر ہو چکا ہے'),
(3960, 37, 172, 'کہ یقیناً وه ہی مدد کیے جائیں گے'),
(3961, 37, 173, 'اور ہمارا ہی لشکر غالب (اور برتر) رہے گا'),
(3962, 37, 174, 'اب آپ کچھ دنوں تک ان سے منھ پھیر لیجئے'),
(3963, 37, 175, 'اور انہیں دیکھتے رہیئے، اور یہ بھی آگے چل کر دیکھ لیں گے'),
(3964, 37, 176, 'کیا یہ ہمارے عذاب کی جلدی مچا رہے ہیں؟'),
(3965, 37, 177, 'سنو! جب ہمارا عذاب ان کے میدان میں اتر آئے گا اس وقت ان کی جن کو متنبہ کر دیا گیا تھا بڑی بری صبح ہوگی'),
(3966, 37, 178, 'آپ کچھ وقت تک ان کا خیال چھوڑ دیجئے'),
(3967, 37, 179, 'اور دیکھتے رہئیے یہ بھی ابھی ابھی دیکھ لیں گے'),
(3968, 37, 180, 'پاک ہے آپ کا رب جو بہت بڑی عزت واﻻ ہے ہر اس چیز سے (جو مشرک) بیان کرتے ہیں'),
(3969, 37, 181, 'پیغمبروں پر سلام ہے'),
(3970, 37, 182, 'اور سب طرح کی تعریف اللہ کے لئے ہے جو سارے جہان کا رب ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(3971, 38, 1, 'ص! اس نصیحت والے قرآن کی قسم'),
(3972, 38, 2, 'بلکہ کفار غرور ومخالفت میں پڑے ہوئے ہیں'),
(3973, 38, 3, 'ہم نے ان سے پہلے بھی بہت سی امتوں کو تباه کر ڈاﻻ انہوں نے ہر چند چیﺦ وپکار کی لیکن وه وقت چھٹکارے کا نہ تھا'),
(3974, 38, 4, 'اور کافروں کو اس بات پر تعجب ہوا کہ ان ہی میں سے ایک انہیں ڈرانے واﻻ آگیا اور کہنے لگے کہ یہ تو جادوگر اور جھوٹا ہے'),
(3975, 38, 5, 'کیا اس نےاتنے سارے معبودوں کا ایک ہی معبود کر دیا واقعی یہ بہت ہی عجیب بات ہے'),
(3976, 38, 6, 'ان کے سردار یہ کہتے ہوئے چلے کہ چلو جی اور اپنے معبودوں پر جمے رہو، یقیناً اس بات میں تو کوئی غرض ہے'),
(3977, 38, 7, 'ہم نے تو یہ بات پچھلے دین میں بھی نہیں سنی، کچھ نہیں یہ تو صرف گھڑنت ہے'),
(3978, 38, 8, 'کیا ہم سب میں سے اسی پر کلام الٰہی نازل کیا گیا ہے؟ دراصل یہ لوگ میری وحی کی طرف سے شک میں ہیں، بلکہ (صحیح یہ ہے کہ) انہوں نے اب تک میرا عذاب چکھا ہی نہیں'),
(3979, 38, 9, 'یا کیا ان کے پاس تیرے زبردست فیاض رب کی رحمت کے خزانے ہیں'),
(3980, 38, 10, 'یا کیا آسمان وزمین اور ان کے درمیان کی ہر چیز کی بادشاہت ان ہی کی ہے، تو پھر یہ رسیاں تان کر چڑھ جائیں'),
(3981, 38, 11, 'یہ بھی (بڑے بڑے) لشکروں میں سے شکست پایا ہوا (چھوٹا سا) لشکر ہے'),
(3982, 38, 12, 'ان سے پہلے بھی قوم نوح اور عاد اور میخوں والے فرعون نے جھٹلایا تھا'),
(3983, 38, 13, 'اور ﺛمود نے اور قوم لوط نے اور ایکہ کے رہنے والوں نے بھی، یہی (بڑے) لشکر تھے'),
(3984, 38, 14, 'ان میں سے ایک بھی ایسا نہ تھا جس نے رسولوں کی تکذیب نہ کی ہو پس میری سزا ان پر ﺛابت ہوگئی'),
(3985, 38, 15, 'انہیں صرف ایک چیﺦ کا انتظار ہے جس میں کوئی توقف (اور ڈھیل) نہیں ہے'),
(3986, 38, 16, 'اور انہوں نے کہا کہ اے ہمارے رب! ہماری سرنوشت تو ہمیں روز حساب سے پہلے ہی دے دے'),
(3987, 38, 17, 'آپ ان کی باتوں پر صبر کریں اور ہمارے بندے داؤد (علیہ السلام) کو یاد کریں جو بڑی قوت واﻻ تھا، یقیناً وه بہت رجوع کرنے واﻻ تھا'),
(3988, 38, 18, 'ہم نے پہاڑوں کو اس کے تابع کر رکھا تھا کہ اس کے ساتھ شام کو اور صبح کو تسبیح خوانی کریں'),
(3989, 38, 19, 'اور پرندوں کو بھی جمع ہو کر سب کے سب اس کے زیر فرمان رہتے'),
(3990, 38, 20, 'اور ہم نے اس کی سلطنت کو مضبوط کر دیا تھا اور اسے حکمت دی تھی اور بات کا فیصلہ کرنا'),
(3991, 38, 21, 'اور کیا تجھے جھگڑا کرنے والوں کی (بھی) خبر ملی؟ جبکہ وه دیوار پھاند کر محراب میں آگئے'),
(3992, 38, 22, 'جب یہ (حضرت) داؤد (علیہ السلام) کے پاس پہنچے، پس یہ ان سے ڈر گئے، انہوں نے کہا خوف نہ کیجئے! ہم دو فریق مقدمہ ہیں، ہم میں سے ایک نے دوسرے پر زیادتی کی ہے، پس آپ ہمارے درمیان حق کے ساتھ فیصلہ کر دیجئے اور ناانصافی نہ کیجئے اور ہمیں سیدھی راه بتا دیجئے'),
(3993, 38, 23, '(سنیے) یہ میرا بھائی ہے اس کے پاس نناوے دنبیاں ہیں اور میرے پاس ایک ہی دنبی ہے لیکن یہ مجھ سے کہہ رہا ہے کہ اپنی یہ ایک بھی مجھ ہی کو دے دے اور مجھ پر بات میں بڑی سختی برتتا ہے'),
(3994, 38, 24, 'آپ نے فرمایا! اس کا اپنی دنبیوں کے ساتھ تیری ایک دنبی ملا لینے کا سوال بیشک تیرے اوپر ایک ﻇلم ہے اور اکثر حصہ دار اور شریک (ایسے ہی ہوتے ہیں کہ) ایک دوسرے پر ﻇلم کرتے ہیں، سوائے ان کے جو ایمان ﻻئے اور جنہوں نے نیک عمل کیے اور ایسے لوگ بہت ہی کم ہیں اور (حضرت) داؤد (علیہ السلام) سمجھ گئے کہ ہم نے انہیں آزمایا ہے، پھر تو اپنے رب سے استغفار کرنے لگے اور عاجزی کرتے ہوئے گر پڑے اور (پوری طرح) رجوع کیا'),
(3995, 38, 25, 'پس ہم نے بھی ان کا وه (قصور) معاف کر دیا، یقیناً وه ہمارے نزدیک بڑے مرتبہ والے اور بہت اچھے ٹھکانے والے ہیں'),
(3996, 38, 26, 'اے داؤد! ہم نے تمہیں زمین میں خلیفہ بنا دیا تم لوگوں کے درمیان حق کے ساتھ فیصلے کرو اور اپنی نفسانی خواہش کی پیروی نہ کرو ورنہ وه تمہیں اللہ کی راه سے بھٹکا دے گی، یقیناً جو لوگ اللہ کی راه سے بھٹک جاتے ہیں ان کے لئے سخت عذاب ہے اس لئے کہ انہوں نے حساب کے دن کو بھلا دیا ہے'),
(3997, 38, 27, 'اور ہم نے آسمان وزمین اور ان کے درمیان کی چیزوں کو ناحق پیدا نہیں کیا، یہ گمان تو کافروں کا ہے سو کافروں کے لئے خرابی ہے آگ کی۔'),
(3998, 38, 28, 'کیا ہم ان لوگوں کو جو ایمان ﻻئے اور نیک عمل کیے ان کے برابر کر دیں گے جو (ہمیشہ) زمین میں فساد مچاتے رہے، یا پرہیزگاروں کو بدکاروں جیسا کر دینگے؟'),
(3999, 38, 29, 'یہ بابرکت کتاب ہے جسے ہم نے آپ کی طرف اس لئے نازل فرمایا ہے کہ لوگ اس کی آیتوں پر غور وفکر کریں اور عقلمند اس سے نصیحت حاصل کریں'),
(4000, 38, 30, 'اور ہم نے داؤد کو سلیمان (نامی فرزند) عطا فرمایا، جو بڑا اچھا بنده تھا اور بے حد رجوع کرنے واﻻ تھا'),
(4001, 38, 31, 'جب ان کے سامنے شام کے وقت تیز رو خاصے گھوڑے پیش کیے گئے'),
(4002, 38, 32, 'تو کہنے لگے میں نے اپنے پروردگار کی یاد پر ان گھوڑوں کی محبت کو ترجیح دی، یہاں تک کہ (آفتاب) چھﭗ گیا'),
(4003, 38, 33, 'ان (گھوڑوں) کو دوباره میرے سامنے ﻻؤ! پھر تو پنڈلیوں اور گردنوں پر ہاتھ پھیرنا شروع کر دیا'),
(4004, 38, 34, 'اور ہم نے سلیمان (علیہ السلام) کی آزمائش کی اور ان کے تخت پر ایک جسم ڈال دیا پھر اس نے رجوع کیا'),
(4005, 38, 35, 'کہا کہ اے میرے رب! مجھے بخش دے اور مجھے ایسا ملک عطا فرما جو میرے سوا کسی (شخص) کے ﻻئق نہ ہو، تو بڑا ہی دینے واﻻ ہے'),
(4006, 38, 36, 'پس ہم نے ہوا کو ان کے ماتحت کر دیا اور آپ کے حکم سے جہاں آپ چاہتے نرمی سے پہنچا دیا کرتی تھی'),
(4007, 38, 37, 'اور (طاقت ور) جنات کو بھی (ان کا ماتحت کر دیا) ہر عمارت بنانے والے کو اور غوطہ خور کو'),
(4008, 38, 38, 'اور دوسرے جنات کو بھی جو زنجیروں میں جکڑے رہتے'),
(4009, 38, 39, 'یہ ہے ہمارا عطیہ اب تو احسان کر یا روک رکھ، کچھ حساب نہیں'),
(4010, 38, 40, 'ان کے لئے ہمارے پاس بڑا تقرب ہے اور بہت اچھا ٹھکانا ہے'),
(4011, 38, 41, 'اور ہمارے بندے ایوب (علیہ السلام) کا (بھی) ذکر کر، جبکہ اس نے اپنے رب کو پکارا کہ مجھے شیطان نے رنج اور دکھ پہنچایا ہے'),
(4012, 38, 42, 'اپنا پاؤں مارو، یہ نہانے کا ٹھنڈا اور پینے کا پانی ہے'),
(4013, 38, 43, 'اور ہم نے اسے اس کا پورا کنبہ عطا فرمایا بلکہ اتنا ہی اور بھی اسی کے ساتھ اپنی (خاص) رحمت سے، اور عقلمندوں کی نصیحت کے لئے'),
(4014, 38, 44, 'اور اپنے ہاتھوں میں تنکوں کا ایک مٹھا (جھاڑو) لے کر مار دے اور قسم کے خلاف نہ کر، سچ تو یہ ہے کہ ہم نے اسے بڑا صابر بنده پایا، وه بڑا نیک بنده تھا اور بڑی ہی رغبت رکھنے واﻻ'),
(4015, 38, 45, 'ہمارے بندوں ابراہیم، اسحاق اور یعقوب (علیہم السلام) کا بھی لوگوں سے ذکر کرو جو ہاتھوں اور آنکھوں والے تھے'),
(4016, 38, 46, 'ہم نے انہیں ایک خاص بات یعنی آخرت کی یاد کے ساتھ مخصوص کر دیا تھا'),
(4017, 38, 47, 'یہ سب ہمارے نزدیک برگزیده اور بہترین لوگ تھے'),
(4018, 38, 48, 'اسماعیل، یسع اور ذوالکفل (علیہم السلام) کا بھی ذکر کر دیجئے۔ یہ سب بہترین لوگ تھے'),
(4019, 38, 49, 'یہ نصیحت ہے اور یقین مانو کہ پرہیزگاروں کی بڑی اچھی جگہ ہے'),
(4020, 38, 50, '(یعنی ہمیشگی والی) جنتیں جن کے دروازے ان کے لئے کھلے ہوئے ہیں'),
(4021, 38, 51, 'جن میں بافراغت تکیے لگائے بیٹھے ہوئے طرح طرح کے میوے اور قسم قسم کی شرابوں کی فرمائشیں کر رہے ہیں'),
(4022, 38, 52, 'اور ان کے پاس نیچی نظروں والی ہم عمر حوریں ہوں گی'),
(4023, 38, 53, 'یہ ہے جس کا وعده تم سے حساب کے دن کے لئے کیا جاتا تھا'),
(4024, 38, 54, 'بیشک روزیاں (خاص) ہمارا عطیہ ہیں جن کا کبھی خاتمہ ہی نہیں'),
(4025, 38, 55, 'یہ تو ہوئی جزا، (یاد رکھو کہ) سرکشوں کے لئے بڑی بری جگہ ہے'),
(4026, 38, 56, 'دوزخ ہے جس میں وه جائیں گے (آه) کیا ہی برا بچھونا ہے'),
(4027, 38, 57, 'یہ ہے، پس اسے چکھیں، گرم پانی اور پیﭗ'),
(4028, 38, 58, 'اس کے علاوه اور طرح طرح کے عذاب'),
(4029, 38, 59, 'یہ ایک قوم ہے جو تمہارے ساتھ (آگ میں) جانے والی ہے، کوئی خوش آمدید ان کے لئے نہیں ہے یہی تو جہنم میں جانے والے ہیں'),
(4030, 38, 60, 'وه کہیں گے بلکہ تم ہی ہو جن کے لئے کوئی خوش آمدید نہیں ہے تم ہی نے تو اسے پہلے ہی سے ہمارے سامنے ﻻ رکھا تھا، پس رہنے کی بڑی بری جگہ ہے'),
(4031, 38, 61, 'وه کہیں گے اے ہمارے رب! جس نے (کفر کی رسم) ہمارے لئے پہلے سے نکالی ہو اس کے حق میں جہنم کی دگنی سزا کر دے'),
(4032, 38, 62, 'اور جہنمی کہیں گے کیا بات ہے کہ وه لوگ ہمیں دکھائی نہیں دیتے جنہیں ہم برے لوگوں میں شمار کرتے تھے'),
(4033, 38, 63, 'کیا ہم نے ہی ان کا مذاق بنا رکھا تھا یا ہماری نگاہیں ان سے ہٹ گئی ہیں'),
(4034, 38, 64, 'یقین جانو کہ دوزخیوں کا یہ جھگڑا ضرور ہی ہوگا'),
(4035, 38, 65, 'کہہ دیجئے! کہ میں تو صرف خبردار کرنے واﻻ ہوں اور بجز اللہ واحد غالب کے اور کوئی ﻻئق عبادت نہیں'),
(4036, 38, 66, 'جو پروردگار ہے آسمانوں کا اور زمین کا اور جو کچھ ان کے درمیان ہے، وه زبردست اور بڑا بخشنے واﻻ ہے'),
(4037, 38, 67, 'آپ کہہ دیجئے کہ یہ بہت بڑی خبر ہے'),
(4038, 38, 68, 'جس سے تم بے پرواه ہو رہے ہو'),
(4039, 38, 69, 'مجھے ان بلند قدر فرشتوں کی (بات چیت کا) کوئی علم ہی نہیں جبکہ وه تکرار کر رہے تھے'),
(4040, 38, 70, 'میری طرف فقط یہی وحی کی جاتی ہے کہ میں تو صاف صاف آگاه کر دینے واﻻ ہوں'),
(4041, 38, 71, 'جبکہ آپ کے رب نے فرشتوں سے ارشاد فرمایا کہ میں مٹی سے انسان کو پیدا کرنے واﻻ ہوں'),
(4042, 38, 72, 'سو جب میں اسے ٹھیک ٹھاک کر لوں اور اس میں اپنی روح پھونک دوں، تو تم سب اس کے سامنے سجدے میں گر پڑنا'),
(4043, 38, 73, 'چنانچہ تمام فرشتوں نے سجده کیا'),
(4044, 38, 74, 'مگر ابلیس نے (نہ کیا)، اس نے تکبر کیا اور وه تھا کافروں میں سے'),
(4045, 38, 75, '(اللہ تعالیٰ نے) فرمایا اے ابلیس! تجھے اسے سجده کرنے سے کس چیز نے روکا جسے میں نے اپنے ہاتھوں سے پیدا کیا۔ کیا تو کچھ گھمنڈ میں آگیا ہے؟ یا تو بڑے درجے والوں میں سے ہے'),
(4046, 38, 76, 'اس نے جواب دیا کہ میں اس سے بہتر ہوں، تو نے مجھے آگ سے بنایا، اور اسے مٹی سے بنایا ہے'),
(4047, 38, 77, 'ارشاد ہوا کہ تو یہاں سے نکل جا تو مردود ہوا'),
(4048, 38, 78, 'اور تجھ پر قیامت کے دن تک میری لعنت وپھٹکار ہے'),
(4049, 38, 79, 'کہنے لگا میرے رب مجھے لوگوں کے اٹھ کھڑے ہونے کے دن تک مہلت دے'),
(4050, 38, 80, '(اللہ تعالیٰ نے) فرمایا تو مہلت والوں میں سے ہے'),
(4051, 38, 81, 'متعین وقت کے دن تک'),
(4052, 38, 82, 'کہنے لگا پھر تو تیری عزت کی قسم! میں ان سب کو یقیناً بہکا دوں گا'),
(4053, 38, 83, 'بجز تیرے ان بندوں کے جو چیده اور پسندیده ہوں'),
(4054, 38, 84, 'فرمایا سچ تو یہ ہے، اور میں سچ ہی کہا کرتا ہوں'),
(4055, 38, 85, 'کہ تجھ سے اور تیرے تمام ماننے والوں سے میں (بھی) جہنم کو بھر دوں گا'),
(4056, 38, 86, 'کہہ دیجئے کہ میں تم سے اس پر کوئی بدلہ طلب نہیں کرتا اور نہ میں تکلف کرنے والوں میں سے ہوں'),
(4057, 38, 87, 'یہ تو تمام جہان والوں کے لئے سراسر نصیحت (وعبرت) ہے'),
(4058, 38, 88, 'یقیناً تم اس کی حقیقت کو کچھ ہی وقت کے بعد (صحیح طور پر) جان لو گے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4059, 39, 1, 'اس کتاب کا اتارنا اللہ تعالیٰ غالب باحکمت کی طرف سے ہے'),
(4060, 39, 2, 'یقیناً ہم نے اس کتاب کو آپ کی طرف حق کے ساتھ نازل فرمایا ہے پس آپ اللہ ہی کی عبادت کریں، اسی کے لئے دین کو خالص کرتے ہوئے'),
(4061, 39, 3, 'خبردار! اللہ تعالیٰ ہی کے لئے خالص عبادت کرنا ہے اور جن لوگوں نے اس کے سوا اولیا بنا رکھے ہیں (اور کہتے ہیں) کہ ہم ان کی عبادت صرف اس لئے کرتے ہیں کہ یہ (بزرگ) اللہ کی نزدیکی کے مرتبہ تک ہماری رسائی کرا دیں، یہ لوگ جس بارے میں اختلاف کر رہے ہیں اس کا (سچا) فیصلہ اللہ (خود) کرے گا۔ جھوٹے اور ناشکرے (لوگوں) کو اللہ تعالیٰ راه نہیں دکھاتا'),
(4062, 39, 4, 'اگر اللہ تعالیٰ کااراده اوﻻد ہی کا ہوتا تو اپنی مخلوق میں سے جسے چاہتا چن لیتا۔ (لیکن) وه تو پاک ہے، وه وہی اللہ تعالیٰ ہے یگانہ اور قوت واﻻ'),
(4063, 39, 5, 'نہایت اچھی تدبیر سے اس نے آسمانوں اور زمین کو بنایا وه رات کو دن اور دن کو رات پر لپیٹ دیتا ہے اور اس نے سورج اور چاند کو کام پر لگا رکھا ہے۔ ہر ایک مقرره مدت تک چل رہا ہے یقین مانو کہ وہی زبردست اور گناہوں کا بخشنے واﻻ ہے'),
(4064, 39, 6, 'اس نے تم سب کو ایک ہی جان سے پیدا کیا ہے، پھر اسی سے اس کا جوڑا پیدا کیا اور تمہارے لئے چوپایوں میں سے (آٹھ نر وماده) اتارے وه تمہیں تمہاری ماؤں کے پیٹوں میں ایک بناوٹ کے بعد دوسری بناوٹ پر بناتا ہے تین تین اندھیروں میں، یہی اللہ تعالیٰ تمہارا رب ہے اسی کے لئے بادشاہت ہے، اس کے سوا کوئی معبود نہیں، پھر تم کہاں بہک رہے ہو'),
(4065, 39, 7, 'اگر تم ناشکری کرو تو (یاد رکھو کہ) اللہ تعالیٰ تم (سب سے) بے نیاز ہے، اور وه اپنے بندوں کی ناشکری سے خوش نہیں اور اگر تم شکر کرو تو وه اسے تمہارے لئے پسند کرے گا۔ اور کوئی کسی کا بوجھ نہیں اٹھاتا پھر تم سب کا لوٹنا تمہارے رب ہی کی طرف ہے۔ تمہیں وه بتلا دے گا جو تم کرتے تھے۔ یقیناً وه دلوں تک کی باتوں کا واقف ہے'),
(4066, 39, 8, 'اور انسان کو جب کبھی کوئی تکلیف پہنچتی ہے تو وه خوب رجوع ہو کر اپنے رب کو پکارتا ہے، پھر جب اللہ تعالیٰ اسے اپنے پاس سے نعمت عطا فرمادیتا ہے تو وه اس سے پہلے جو دعا کرتا تھا اسے (بالکل) بھول جاتا ہے اور اللہ تعالیٰ کے شریک مقرر کرنے لگتا ہے جس سے (اوروں کو بھی) اس کی راه سے بہکائے، آپ کہہ دیجئے! کہ اپنے کفر کا فائده کچھ دن اور اٹھالو، (آخر) تو دوزخیوں میں ہونے واﻻ ہے'),
(4067, 39, 9, 'بھلا جو شخص راتوں کے اوقات سجدے اور قیام کی حالت میں (عبادت میں) گزارتا ہو، آخرت سے ڈرتا ہو اور اپنے رب کی رحمت کی امید رکھتا ہو، (اور جو اس کے برعکس ہو برابر ہو سکتے ہیں) بتاؤ تو علم والے اور بے علم کیا برابر کے ہیں؟ یقیناً نصیحت وہی حاصل کرتے ہیں جو عقلمند ہوں۔ (اپنے رب کی طرف سے)'),
(4068, 39, 10, 'کہہ دو کہ اے میرے ایمان والے بندو! اپنے رب سے ڈرتے رہو، جو اس دنیا میں نیکی کرتے ہیں ان کے لئے نیک بدلہ ہے اور اللہ تعالیٰ کی زمین بہت کشاده ہے صبر کرنے والوں ہی کو ان کا پورا پورا بےشمار اجر دیا جاتا ہے'),
(4069, 39, 11, 'آپ کہہ دیجئے! کہ مجھے حکم دیا گیا ہے کہ اللہ تعالیٰ کی اس طرح عبادت کروں کہ اسی کے لئے عبادت کو خالص کر لوں'),
(4070, 39, 12, 'اور مجھے حکم دیا گیا ہے کہ میں سب سے پہلا فرماں بردار بن جاؤں'),
(4071, 39, 13, 'کہہ دیجئے! کہ مجھے تو اپنے رب کی نافرمانی کرتے ہوئے بڑے دن کے عذاب کا خوف لگتا ہے'),
(4072, 39, 14, 'کہہ دیجئے! کہ میں تو خالص کر کے صرف اپنے رب ہی کی عبادت کرتا ہوں'),
(4073, 39, 15, 'تم اس کے سوا جس کی چاہو عبادت کرتے رہو کہہ دیجئے! کہ حقیقی زیاں کار وه ہیں جو اپنے آپ کو اور اپنے اہل کو قیامت کے دن نقصان میں ڈال دیں گے، یاد رکھو کہ کھلم کھلا نقصان یہی ہے'),
(4074, 39, 16, 'انہیں نیچے اوپر سے آگ کے (شعلے مثل) سائبان (کے) ڈھانک رہے ہوں گے۔ یہی (عذاب) ہے جن سے اللہ تعالیٰ اپنے بندوں کو ڈرا رہا ہے، اے میرے بندو! پس مجھ سے ڈرتے رہو'),
(4075, 39, 17, 'اور جن لوگوں نے طاغوت کی عبادت سے پرہیز کیا اور (ہمہ تن) اللہ تعالیٰ کی طرف متوجہ رہے وه خوش خبری کے مستحق ہیں، میرے بندوں کو خوشخبری سنا دیجئے'),
(4076, 39, 18, 'جو بات کو کان لگا کر سنتے ہیں۔ پھر جو بہترین بات ہو اس کی اتباع کرتے ہیں۔ یہی ہیں جنہیں اللہ تعالیٰ نے ہدایت کی ہے اور یہی عقلمند بھی ہیں'),
(4077, 39, 19, 'بھلا جس شخص پر عذاب کی بات ﺛابت ہو چکی ہے، تو کیا آپ اسے جو دوزخ میں ہے چھڑا سکتے ہیں؟'),
(4078, 39, 20, 'ہاں وه لوگ جو اپنے رب سے ڈرتے رہے ان کے لئے باﻻ خانے ہیں جن کے اوپر بھی بنے بنائے باﻻ خانے ہیں (اور) ان کے نیچے نہریں بہہ رہی ہیں۔ رب کا وعده ہے اور وه وعده خلافی نہیں کرتا'),
(4079, 39, 21, 'کیا آپ نے نہیں دیکھا کہ اللہ تعالیٰ آسمان سے پانی اتارتا ہے اور اسے زمین کی سوتوں میں پہنچاتا ہے، پھر اسی کے ذریعہ سے مختلف قسم کی کھیتیاں اگاتا ہے پھر وه خشک ہو جاتی ہیں اور آپ انہیں زرد رنگ دیکھتے ہیں پھر انہیں ریزه ریزه کر دیتا ہے، اس میں عقل مندوں کے لئے بہت زیاده نصیحت ہے'),
(4080, 39, 22, 'کیا وه شخص جس کا سینہ اللہ تعالیٰ نےاسلام کے لئے کھول دیا ہے پس وه اپنے پروردگار کی طرف سے ایک نور پر ہے اور ہلاکی ہے ان پر جن کے دل یاد الٰہی سے (اﺛر نہیں لیتے بلکہ) سخت ہو گئے ہیں۔ یہ لوگ صریح گمراہی میں (مبتلا) ہیں'),
(4081, 39, 23, 'اللہ تعالیٰ نے بہترین کلام نازل فرمایا ہے جو ایسی کتاب ہے کہ آپس میں ملتی جلتی اور بار بار دہرائی ہوئی آیتوں کی ہے، جس سے ان لوگوں کے رونگٹے کھڑے ہو جاتے ہیں جو اپنے رب کا خوف رکھتے ہیں آخر میں ان کے جسم اور دل اللہ تعالیٰ کے ذکر کی طرف نرم ہو جاتے ہیں، یہ ہے اللہ تعالیٰ کی ہدایت جس کے ذریعہ جسے چاہے راه راست پر لگا دیتا ہے۔ اور جسے اللہ تعالیٰ ہی راه بھلا دے اس کا ہادی کوئی نہیں'),
(4082, 39, 24, 'بھلا جو شخص قیامت کے دن کے بدترین عذاب کی سپر (ڈھال) اپنے منھ کو بنائے گا۔ (ایسے) ﻇالموں سے کہا جائے گا کہ اپنے کیے کا (وبال) چکھو'),
(4083, 39, 25, 'ان سے پہلے والوں نے بھی جھٹلایا، پھر ان پر وہاں سے عذاب آپڑا جہاں سے ان کو خیال بھی نہ تھا'),
(4084, 39, 26, 'اور اللہ تعالیٰ نے انہیں زندگانی دنیا میں رسوائی کا مزه چکھایا اور ابھی آخرت کا تو بڑا بھاری عذاب ہے کاش کہ یہ لوگ سمجھ لیں'),
(4085, 39, 27, 'اور یقیناً ہم نے اس قرآن میں لوگوں کے لئے ہر قسم کی مثالیں بیان کر دیں ہیں کیا عجب کہ وه نصیحت حاصل کر لیں'),
(4086, 39, 28, 'قرآن ہے عربی میں جس میں کوئی کجی نہیں، ہو سکتا ہے کہ وه پرہیزگاری اختیار کر لیں'),
(4087, 39, 29, 'اللہ تعالیٰ مثال بیان فرما رہا ہے ایک وه شخص جس میں بہت سے باہم ضد رکھنے والے ساجھی ہیں، اور دوسرا وه شخص جو صرف ایک ہی کا (غلام) ہے، کیا یہ دونوں صفت میں یکساں ہیں، اللہ تعالیٰ ہی کے لئے سب تعریف ہے۔ بات یہ ہے کہ ان میں کے اکثر لوگ سمجھتے نہیں'),
(4088, 39, 30, 'یقیناً خود آپ کو بھی موت آئے گی اور یہ سب بھی مرنے والے ہیں'),
(4089, 39, 31, 'پھر تم سب کے سب قیامت والے دن اپنے رب کے سامنے جھگڑو گے'),
(4090, 39, 32, 'اس سے بڑھ کر ظالم کون ہے جو اللہ تعالیٰ پر جھوٹ بولے؟ اور سچا دین جب اس کے پاس آئے تو اسے جھوٹا بتائے؟ کیا ایسے کفار کے لیے جہنم ٹھکانا نہیں ہے؟'),
(4091, 39, 33, 'اور جو سچے دین کو ﻻئے اور جس نے اس کی تصدیق کی یہی لوگ پارسا ہیں'),
(4092, 39, 34, 'ان کے لیے ان کے رب کے پاس (ہر) وه چیز ہے جو یہ چاہیں، نیک لوگوں کا یہی بدلہ ہے'),
(4093, 39, 35, 'تاکہ اللہ تعالیٰ ان سے ان کے برے عملوں کو دور کردے اور جو نیک کام انہوں نے کیے ہیں ان کا اچھا بدلہ عطا فرمائے'),
(4094, 39, 36, 'کیا اللہ تعالیٰ اپنے بندے کے لیے کافی نہیں؟ یہ لوگ آپ کو اللہ کے سوا اوروں سے ڈرا رہے ہیں اور جسے اللہ گمراه کر دےاس کی رہنمائی کرنے واﻻ کوئی نہیں'),
(4095, 39, 37, 'اور جسے وه ہدایت دے اسے کوئی گمراه کرنے واﻻ نہیں کیا اللہ تعالیٰ غالب اور بدلہ لینے واﻻ نہیں ہے؟'),
(4096, 39, 38, 'اگر آپ ان سے پوچھیں کہ آسمان و زمین کو کس نے پیدا کیا ہے؟ تو یقیناً وه یہی جواب دیں گے کہ اللہ نے۔ آپ ان سے کہئیے کہ اچھا یہ تو بتاؤ جنہیں تم اللہ کے سوا پکارتے ہو اگر اللہ تعالی مجھے نقصان پہنچانا چاہے توکیا یہ اس کے نقصان کو ہٹا سکتے ہیں؟ یا اللہ تعالیٰ مجھ پر مہربانی کا اراده کرے تو کیا یہ اس کی مہربانی کو روک سکتے ہیں؟ آپ کہہ دیں کہ اللہ مجھے کافی ہے، توکل کرنے والے اسی پر توکل کرتے ہیں'),
(4097, 39, 39, 'کہہ دیجیئے کہ اے میری قوم! تم اپنی جگہ پر عمل کیے جاؤ میں بھی عمل کر رہا ہوں، ابھی ابھی تم جان لوگے'),
(4098, 39, 40, 'کہ کس پر رسوا کرنے واﻻ عذاب آتا ہے اور کس پر دائمی مار اور ہمیشگی کی سزا ہوتی ہے'),
(4099, 39, 41, 'آپ پر ہم نے حق کے ساتھ یہ کتاب لوگوں کے لیے نازل فرمائی ہے، پس جو شخص راه راست پر آجائے اس کے اپنے لیے نفع ہے اور جو گمراه ہوجائے اس کی گمراہی کا (وبال) اسی پر ہے، آپ ان کے ذمہ دار نہیں'),
(4100, 39, 42, 'اللہ ہی روحوں کو ان کی موت کے وقت اور جن کی موت نہیں آئی انہیں ان کی نیند کے وقت قبض کر لیتا ہے، پھر جن پر موت کا حکم لگ چکا ہے انہیں تو روک لیتا ہے اور دوسری (روحوں) کو ایک مقرر وقت تک کے لیے چھوڑ دیتا ہے۔ غور کرنے والوں کے لیے اس میں یقیناً بہت سی نشانیاں ہیں'),
(4101, 39, 43, 'کیا ان لوگوں نے اللہ تعالی کے سوا (اوروں) کو سفارشی مقرر کر رکھا ہے؟ آپ کہہ دیجیئے! کہ گو وه کچھ بھی اختیار نہ رکھتے ہوں اور نہ عقل رکھتے ہوں'),
(4102, 39, 44, 'کہہ دیجئے! کہ تمام سفارش کا مختار اللہ ہی ہے۔ تمام آسمانوں اور زمین کا راج اسی کے لیے ہے تم سب اسی کی طرف پھیرے جاؤ گے'),
(4103, 39, 45, 'جب اللہ اکیلے کا ذکر کیا جائے تو ان لوگوں کے دل نفرت کرنے لگتے ہیں جو آخرت کایقین نہیں رکھتے اور جب اس کے سوا (اور کا) ذکر کیا جائے تو ان کے دل کھل کر خوش ہو جاتے ہیں'),
(4104, 39, 46, 'آپ کہہ دیجئے! کہ اے اللہ! آسمانوں اور زمین کے پیدا کرنے والے، چھپے کھلے کے جاننے والے تو ہی اپنے بندوں میں ان امور کا فیصلہ فرمائے گا جن میں وه الجھ رہے تھے'),
(4105, 39, 47, 'اگر ﻇلم کرنے والوں کے پاس وه سب کچھ ہو جو روئے زمین پر ہے اور اس کے ساتھ اتنا ہی اور ہو، تو بھی بدترین سزا کے بدلے میں قیامت کے دن یہ سب کچھ دے دیں، اور ان کے سامنے اللہ کی طرف سے وه ﻇاہر ہوگا جس کا گمان بھی انہیں نہ تھا'),
(4106, 39, 48, 'جو کچھ انہوں نے کیا تھا اس کی برائیاں ان پر کھل پڑیں گی اور جس کا وه مذاق کرتے تھے اور وه انہیں آ گھیرے گا'),
(4107, 39, 49, 'انسان کو جب کوئی تکلیف پہنچتی ہے تو ہمیں پکارنے لگتا ہے، پھر جب ہم اسے اپنی طرف سے کوئی نعمت عطا فرمادیں تو کہنے لگتا ہے کہ اسے تو میں محض اپنے علم کی وجہ سے دیا گیا ہوں، بلکہ یہ آزمائش ہے لیکن ان میں سے اکثر لوگ بے علم ہیں'),
(4108, 39, 50, 'ان سے اگلے بھی یہی بات کہہ چکے ہیں پس ان کی کارروائی ان کے کچھ کام نہ آئی'),
(4109, 39, 51, 'پھر ان کی تمام برائیاں ان پر آ پڑیں، اوران میں سے بھی جو گناه گار ہیں ان کی کی ہوئی برائیاں بھی اب ان پر آ پڑیں گی، یہ (ہمیں) ہرا دینے والے نہیں'),
(4110, 39, 52, 'کیا انہیں یہ معلوم نہیں کہ اللہ تعالیٰ جس کے لیے چاہے روزی کشاده کر دیتا ہے اور تنگ (بھی)، ایمان ﻻنے والوں کے لیے اس میں (بڑی بڑی) نشانیاں ہیں'),
(4111, 39, 53, '(میری جانب سے) کہہ دو کہ اے میرے بندو! جنہوں نے اپنی جانوں پر زیادتی کی ہے تم اللہ کی رحمت سے ناامید نہ ہو جاؤ، بالیقین اللہ تعالیٰ سارے گناہوں کو بخش دیتا ہے، واقعی وه بڑی بخشش بڑی رحمت واﻻ ہے'),
(4112, 39, 54, 'تم (سب) اپنے پروردگار کی طرف جھک پڑو اور اس کی حکم برداری کیے جاؤ اس سے قبل کہ تمہارے پاس عذاب آ جائے اور پھر تمہاری مدد نہ کی جائے'),
(4113, 39, 55, 'اور پیروری کرو اس بہترین چیزکی جو تمہاری طرف تمہارے پروردگار کی طرف سے نازل کی گئی ہے، اس سے پہلے کہ تم پر اچانک عذاب آجائے اور تمہیں اطلاع بھی نہ ہو'),
(4114, 39, 56, '(ایسا نہ ہو کہ) کوئی شخص کہے ہائے افسوس! اس بات پر کہ میں نے اللہ تعالیٰ کے حق میں کوتاہی کی بلکہ میں تو مذاق اڑانے والوں میں ہی رہا'),
(4115, 39, 57, 'یا کہے کہ اگر اللہ مجھے ہدایت کرتا تو میں بھی پارسا لوگوں میں ہوتا'),
(4116, 39, 58, 'یا عذاب کو دیکھ کر کہے کاش! کہ کسی طرح میرا لوٹ جانا ہوجاتا تو میں بھی نیکو کاروں میں ہوجاتا'),
(4117, 39, 59, 'ہاں (ہاں) بیشک تیرے پاس میری آیتیں پہنچ چکی تھیں جنہیں تونے جھٹلایا اور غرور وتکبر کیا اور تو تھا ہی کافروں میں'),
(4118, 39, 60, 'اور جن لوگوں نے اللہ پر جھوٹ باندھا ہے تو آپ دیکھیں گے کہ قیامت کے دن ان کے چہرے سیاه ہوگئے ہوں گے کیا تکبر کرنے والوں کاٹھکانا جہنم میں نہیں؟'),
(4119, 39, 61, 'اور جن لوگوں نے پرہیزگاری کی انہیں اللہ تعالیٰ ان کی کامیابی کے ساتھ بچا لے گا، انہیں کوئی دکھ چھو بھی نہ سکے گا اور نہ وه کسی طرح غمگین ہوں گے'),
(4120, 39, 62, 'اللہ ہر چیز کا پیدا کرنے واﻻ ہے اور وہی ہر چیز پر نگہبان ہے'),
(4121, 39, 63, 'آسمانوں اور زمین کی کنجیوں کامالک وہی ہے جن جن لوگوں نے اللہ کی آیتوں کا انکار کیا وہی خساره پانے والے ہیں'),
(4122, 39, 64, 'آپ کہہ دیجئے اے جاہلو! کیا تم مجھ سے اللہ کے سوا اوروں کی عبادت کو کہتے ہو'),
(4123, 39, 65, 'یقیناً تیری طرف بھی اور تجھ سے پہلے (کے تمام نبیوں) کی طرف بھی وحی کی گئی ہے کہ اگر تو نے شرک کیا تو بلاشبہ تیرا عمل ضائع ہو جائے گا اور بالیقین تو زیاںکاروں میں سے ہوجائے گا'),
(4124, 39, 66, 'بلکہ تو اللہ ہی کی عبادت کر اور شکر کرنے والوں میں سے ہو جا'),
(4125, 39, 67, 'اور ان لوگوں نے جیسی قدر اللہ تعالیٰ کی کرنی چاہئے تھی نہیں کی، ساری زمین قیامت کے دن اس کی مٹھی میں ہوگی اور تمام آسمان اس کے داہنے ہاتھ میں لپیٹے ہوئے ہوں گے، وه پاک اور برتر ہے ہر اس چیز سے جسے لوگ اس کا شریک بنائیں'),
(4126, 39, 68, 'اور صور پھونک دیا جائے گا پس آسمانوں اور زمین والے سب بے ہوش ہوکر گر پڑیں گے مگر جسے اللہ چاہے، پھر دوباره صور پھونکا جائے گا پس وه ایک دم کھڑے ہو کر دیکھنے لگ جائیں گے'),
(4127, 39, 69, 'اور زمین اپنے پروردگار کے نور سے جگمگا اٹھے گی، نامہٴ اعمال حاضر کیے جائیں گے نبیوں اور گواہوں کو ﻻیا جائے گا اور لوگوں کے درمیان حق حق فیصلے کر دیے جائیں گے، اور وه ﻇلم نہ کیے جائیں گے'),
(4128, 39, 70, 'اور جس شخص نے جو کچھ کیا ہے بھرپور دے دیا جائے گا، جو کچھ لوگ کر رہے ہیں وه بخوبی جاننے واﻻ ہے'),
(4129, 39, 71, 'کافروں کے غول کے غول جہنم کی طرف ہنکائے جائیں گے، جب وه اس کے پاس پہنچ جائیں گے اس کے دروازے ان کے لیے کھول دیے جائیں گے، اور وہاں کے نگہبان ان سے سوال کریں گے کہ کیا تمہارے پاس تم میں سے رسول نہیں آئے تھے؟ جو تم پر تمہارے رب کی آیتیں پڑھتے تھے اور تمہیں اس دن کی ملاقات سے ڈراتے تھے؟ یہ جواب دیں گے کہ ہاں درست ہے لیکن عذاب کا حکم کافروں پر ﺛابت ہو گیا'),
(4130, 39, 72, 'کہا جائے گا کہ اب جہنم کے دروازوں میں داخل ہو جاؤ جہاں ہمیشہ رہیں گے، پس سرکشوں کا ٹھکانا بہت ہی برا ہے'),
(4131, 39, 73, 'اور جو لوگ اپنے رب سے ڈرتے تھے ان کے گروه کے گروه جنت کی طرف روانہ کیے جائیں گے یہاں تک کہ جب اس کے پاس آ جائیں گے اور دروازے کھول دیے جائیں گے اور وہاں کے نگہبان ان سے کہیں گے تم پر سلام ہو، تم خوش حال رہو تم اس میں ہمیشہ کے لیے چلے جاؤ'),
(4132, 39, 74, 'یہ کہیں گے کہ اللہ کاشکر ہے کہ جس نے ہم سے اپنا وعده پورا کیا اور ہمیں اس زمین کا وارث بنا دیا کہ جنت میں جہاں چاہیں مقام کریں پس عمل کرنے والوں کا کیا ہی اچھا بدلہ ہے'),
(4133, 39, 75, 'اور تو فرشتوں کو اللہ کے عرش کے اردگرد حلقہ باندھے ہوئے اپنے رب کی حمد و تسبیح کرتے ہوئے دیکھے گا اور ان میں انصاف کا فیصلہ کیا جائے گا اور کہہ دیا جائے گا کہ ساری خوبی اللہ ہی کے لیے ہے جو تمام جہانوں کا پالنہار ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4134, 40, 1, 'حٰم'),
(4135, 40, 2, 'اس کتاب کا نازل فرمانا اس اللہ کی طرف سے ہے جو غالب اور دانا ہے'),
(4136, 40, 3, 'گناه کا بخشنے واﻻ اور توبہ کا قبول فرمانے واﻻ سخت عذاب واﻻ انعام و قدرت واﻻ، جس کے سوا کوئی معبود نہیں۔ اسی کی طرف واپس لوٹنا ہے'),
(4137, 40, 4, 'اللہ تعالیٰ کی آیتوں میں وہی لوگ جھگڑتے ہیں جو کافر ہیں پس ان لوگوں کا شہروں میں چلنا پھرنا آپ کو دھوکے میں نہ ڈالے'),
(4138, 40, 5, 'قوم نوح نے اور ان کے بعد کے گروہوں نے بھی جھٹلایا تھا۔ اور ہر امت نے اپنے رسول کو گرفتار کر لینے کا اراده کیا اور باطل کے ذریعہ کج بحثیاں کیں، تاکہ ان سے حق کو بگاڑ دیں پس میں نے ان کو پکڑ لیا، سو میری طرف سے کیسی سزا ہوئی'),
(4139, 40, 6, 'اور اسی طرح آپ کے رب کا حکم کافروں پر ﺛابت ہو گیا کہ وه دوزخی ہیں'),
(4140, 40, 7, 'عرش کے اٹھانے والے اور اسکے آس پاس کے (فرشتے) اپنے رب کی تسبیح حمد کے ساتھ ساتھ کرتے ہیں اور اس پر ایمان رکھتے ہیں اور ایمان والوں کے لیے استفغار کرتے ہیں، کہتے ہیں کہ اے ہمارے پروردگار! تو نے ہر چیز کو اپنی بخشش اور علم سے گھیر رکھا ہے، پس تو انہیں بخش دے جو توبہ کریں اور تیری راه کی پیروی کریں اور تو انہیں دوزخ کے عذاب سے بھی بچا لے'),
(4141, 40, 8, 'اے ہمارے رب! تو انہیں ہمیشگی والی جنتوں میں لے جا جن کا تو نے ان سے وعده کیا ہے اور ان کے باپ دادوں اور بیویوں اور اوﻻد میں سے (بھی) ان (سب) کو جو نیک عمل ہیں۔ یقیناً تو تو غالب و باحکمت ہے'),
(4142, 40, 9, 'انہیں برائیوں سے بھی محفوظ رکھ، حق تو یہ ہے کہ اس دن تونے جسے برائیوں سے بچا لیا اس پر تو نے رحمت کر دی اور بہت بڑی کامیابی تو یہی ہے'),
(4143, 40, 10, 'بےشک جن لوگوں نے کفر کیا انہیں یہ آواز دی جائے گی کہ یقیناً اللہ کا تم پر غصہ ہونااس سے بہت زیاده ہے جو تم غصہ ہوتے تھے اپنے جی سے، جب تم ایمان کی طرف بلائے جاتے تھے پھر کفر کرنے لگتے تھے'),
(4144, 40, 11, 'وه کہیں گے اے ہمارے پروردگار! تو نے ہمیں دوبار مارا اور دو بار ہی جلایا، اب ہم اپنے گناہوں کے اقراری ہیں، تو کیا اب کوئی راه نکلنے کی بھی ہے؟'),
(4145, 40, 12, 'یہ (عذاب) تمہیں اس لیے ہے کہ جب صرف اکیلے اللہ کا ذکر کیا جاتا تو تم انکار کر جاتے تھے اور اگر اس کے ساتھ کسی کو شریک کیا جاتا تھا تو تم مان لیتے تھے پس اب فیصلہ اللہ بلند و بزرگ ہی کا ہے'),
(4146, 40, 13, 'وہی ہے جو تمہیں اپنی نشانیاں دکھلاتا ہے اور تمہارے لیےآسمان سے روزی اتارتا ہے، نصیحت تو صرف وہی حاصل کرتے ہیں جو (اللہ کی طرف) رجوع کرتے ہیں'),
(4147, 40, 14, 'تم اللہ کو پکارتے رہو اس کے لیے دین کو خالص کر کے گو کافر برا مانیں'),
(4148, 40, 15, 'بلند درجوں واﻻ عرش کامالک وه اپنے بندوں میں سے جس پر چاہتا ہے وحی نازل فرماتا ہے، تاکہ وه ملاقات کے دن سے ڈرائے'),
(4149, 40, 16, 'جس دن سب لوگ ﻇاہر ہو جائیں گے، ان کی کوئی چیز اللہ سے پوشیده نہ رہے گی۔ آج کس کی بادشاہی ہے؟ فقط اللہ واحد و قہار کی'),
(4150, 40, 17, 'آج ہر نفس کو اس کی کمائی کا بدلہ دیا جائے گا۔ آج (کسی قسم کا) ﻇلم نہیں، یقیناً اللہ تعالیٰ بہت جلد حساب کرنے واﻻ ہے'),
(4151, 40, 18, 'اور انہیں بہت ہی قریب آنے والی (قیامت سے) آگاه کر دیجئے، جب کہ دل حلق تک پہنچ جائیں گے اور سب خاموش ہوں گے، ﻇالموں کا نہ کوئی دلی دوست ہوگا نہ سفارشی، کہ جس کی بات مانی جائے گی'),
(4152, 40, 19, 'وه آنکھوں کی خیانت کو اور سینوں کی پوشیده باتوں کو (خوب) جانتا ہے'),
(4153, 40, 20, 'اور اللہ تعالیٰ ٹھیک ٹھیک فیصلہ کر دے گا اس کے سوا جنہیں یہ لوگ پکارتے ہیں وه کسی چیز کا بھی فیصلہ نہیں کرسکتے، بیشک اللہ تعالیٰ خوب سنتا خوب دیکھتا ہے'),
(4154, 40, 21, 'کیا یہ لوگ زمین میں چلے پھرے نہیں کہ دیکھتے کہ جو لوگ ان سے پہلے تھے ان کا نتیجہ کیسا کچھ ہوا؟ وه باعتبار قوت و طاقت کے اور باعتبار زمین میں اپنی یادگاروں کے ان سے بہت زیاده تھے، پس اللہ نے انہیں ان کے گناہوں پر پکڑ لیا اور کوئی نہ ہوا جو انہیں اللہ کے عذاب سے بچا لیتا'),
(4155, 40, 22, 'یہ اس وجہ سے کہ ان کے پاس ان کے پیغمبر معجزے لے لے کرآتے تھے تو وه انکار کر دیتے تھے، پس اللہ انہیں پکڑ لیتا تھا۔ یقیناً وه طاقتور اور سخت عذاب واﻻ ہے'),
(4156, 40, 23, 'اور ہم نے موسیٰ (علیہ السلام) کو اپنی آیتوں اور کھلی دلیلوں کے ساتھ بھیجا'),
(4157, 40, 24, 'فرعون ہامان اور قارون کی طرف تو انہوں نے کہا (یہ تو) جادوگر اور جھوٹا ہے'),
(4158, 40, 25, 'پس جب ان کے پاس (موسیٰ علیہ السلام) ہماری طرف سے (دین) حق کولے کر آئے توانہوں نے کہا کہ اس کے ساتھ جوایمان والے ہیں ان کے لڑکوں کو تو مار ڈالو اور ان کی لڑکیوں کو زنده رکھو اور کافروں کی جو حیلہ سازی ہے وه غلطی میں ہی ہے'),
(4159, 40, 26, 'اور فرعون نے کہا مجھے چھوڑ دو کہ میں موسیٰ (علیہ السلام) کو مار ڈالوں اور اسے چاہئے کہ اپنے رب کو پکارے، مجھے تو ڈر ہے کہ یہ کہیں تمہارا دین نہ بدل ڈالے یا ملک میں کوئی (بہت بڑا) فساد برپا نہ کردے'),
(4160, 40, 27, 'موسیٰ (علیہ السلام) نے کہا میں اپنے اور تمہارے رب کی پناه میں آتا ہوں ہر اس تکبر کرنے والے شخص (کی برائی) سے جو روز حساب پر ایمان نہیں رکھتا'),
(4161, 40, 28, 'اور ایک مومن شخص نے، جو فرعون کے خاندان میں سے تھا اور اپنا ایمان چھپائے ہوئے تھا، کہا کہ کیا تم ایک شحض کو محض اس بات پر قتل کرتے ہو کہ وه کہتا ہے میرا رب اللہ ہے اور تمہارے رب کی طرف سے دلیلیں لے کر آیا ہے، اگر وه جھوٹا ہو تو اس کا جھوٹ اسی پر ہے اور اگر وه سچا ہو، تو جس (عذاب) کا وه تم سے وعده کر رہا ہے اس میں سے کچھ نہ کچھ تو تم پر آ پڑے گا، اللہ تعالیٰ اس کی رہبری نہیں کرتا جو حد سے گزر جانے والے اور جھوٹے ہیں'),
(4162, 40, 29, 'اے میری قوم کے لوگو! آج تو بادشاہت تمہاری ہے کہ اس زمین پر تم غالب ہو لیکن اگر اللہ کا عذاب ہم پر آ گیا تو کون ہماری مدد کرے گا؟ فرعون بوﻻ، میں تو تمہیں وہی رائے دے رہا ہوں جو خود دیکھ رہا ہوں اور میں تو تمہیں بھلائی کی راه ہی بتلا رہا ہوں'),
(4163, 40, 30, 'اس مومن نے کہا اے میری قوم! (کے لوگو) مجھے تو اندیشہ ہے کہ تم پر بھی ویسا ہی روز (بد عذاب) نہ آئے جو اور امتوں پر آیا'),
(4164, 40, 31, 'جیسے امت نو ح اور عاد وﺛمود اور ان کے بعد والوں کا (حال ہوا)، اللہ اپنے بندوں پر کسی طرح کا ﻇلم کرنا نہیں چاہتا'),
(4165, 40, 32, 'اور مجھے تم پر ہانک پکار کے دن کابھی ڈر ہے'),
(4166, 40, 33, 'جس دن تم پیٹھ پھیر کر لوٹو گے، تمہیں اللہ سے بچانے واﻻ کوئی نہ ہوگا اور جسے اللہ گمراه کر دے اس کا ہادی کوئی نہیں'),
(4167, 40, 34, 'اور اس سے پہلے تمہارے پاس (حضرت) یوسف دلیلیں لے کر آئے، پھر بھی تم ان کی ﻻئی ہوئی (دلیل) میں شک و شبہ ہی کرتے رہے یہاں تک کہ جب ان کی وفات ہو گئی تو کہنے لگے ان کے بعد تو اللہ کسی رسول کو بھیجے گا ہی نہیں اسی طرح اللہ گمراه کرتا ہے ہر اس شخص کو جو حد سے بڑھ جانے واﻻ شک و شبہ کرنے واﻻ ہو'),
(4168, 40, 35, 'جو بغیر کسی سند کے جو ان کے پاس آئی ہو اللہ کی آیتوں میں جھگڑتے ہیں، اللہ کے نزدیک اور مومنوں کے نزدیک یہ تو بہت بڑی ناراضگی کی چیز ہے، اللہ تعالیٰ اسی طرح ہر ایک مغرور سرکش کے دل پر مہر کردیتا ہے'),
(4169, 40, 36, 'فرعون نے کہا اے ہامان! میرے لیے ایک باﻻخانہ بنا شاید کہ میں آسمان کے جو دروازے ہیں'),
(4170, 40, 37, '(ان) دروازوں تک پہنچ جاؤں اور موسیٰ کے معبود کو جھانک لوں اور بیشک میں سمجھتا ہوں وه جھوٹا ہے اور اسی طرح فرعون کی بدکرداریاں اسے بھلی دکھائی گئیں اور راه سے روک دیا گیا اور فرعون کی (ہر) حیلہ سازی تباہی میں ہی رہی'),
(4171, 40, 38, 'اور اس مومن شخص نے کہا کہ اے میری قوم! (کے لوگو) تم (سب) میری پیروی کرو میں نیک راه کی طرف تمہاری رہبری کروں گا'),
(4172, 40, 39, 'اے میری قوم! یہ حیات دنیا متاع فانی ہے، (یقین مانو کہ قرار) اور ہمیشگی کا گھر تو آخرت ہی ہے'),
(4173, 40, 40, 'جس نے گناه کیا ہے اسے تو برابر برابر کا بدلہ ہی ہے اور جس نے نیکی کی ہے خواه وه مرد ہو یا عورت اور وه ایمان واﻻ ہو تو یہ لوگ جنت میں جائیں گے اور وہاں بےشمار روزی پائیں گے'),
(4174, 40, 41, 'اے میری قوم! یہ کیا بات ہے کہ میں تمہیں نجات کی طرف بلا رہا ہوں اور تم مجھے دوزخ کی طرف بلا رہے ہو'),
(4175, 40, 42, 'تم مجھے یہ دعوت دے رہے ہوکہ میں اللہ کے ساتھ کفر کروں اور اس کے ساتھ شرک کروں جس کا کوئی علم مجھے نہیں اور میں تمہیں غالب بخشنے والے (معبود) کی طرف دعوت دے رہا ہوں'),
(4176, 40, 43, 'یہ یقینی امر ہے کہ تم مجھے جس کی طرف بلا رہے ہو وه تو نہ دنیا میں پکارے جانے کے قابل ہے نہ آخرت میں، اور یہ (بھی یقینی بات ہے) کہ ہم سب کا لوٹنا اللہ کی طرف ہے اور حد سے گزر جانے والے ہی (یقیناً) اہل دوزخ ہیں'),
(4177, 40, 44, 'پس آگے چل کر تم میری باتوں کو یاد کرو گے میں اپنا معاملہ اللہ کے سپرد کرتا ہوں، یقیناً اللہ تعالیٰ بندوں کا نگراں ہے'),
(4178, 40, 45, 'پس اسے اللہ تعالیٰ نے تمام بدیوں سے محفوظ رکھ لیا جو انہوں نے سوچ رکھی تھیں اور فرعون والوں پر بری طرح کا عذاب الٹ پڑا'),
(4179, 40, 46, 'آگ ہے جس کے سامنے یہ ہر صبح شام ﻻئے جاتے ہیں اور جس دن قیامت قائم ہوگی (فرمان ہوگا کہ) فرعونیوں کو سخت ترین عذاب میں ڈالو'),
(4180, 40, 47, 'اور جب کہ دوزخ میں ایک دوسرے سے جھگڑیں گے تو کمزور لوگ تکبر والوں سے (جن کے یہ تابع تھے) کہیں گے کہ ہم تو تمہارے پیرو تھے تو کیا اب تم ہم سے اس آگ کا کوئی حصہ ہٹا سکتے ہو؟'),
(4181, 40, 48, 'وه بڑے لوگ جواب دیں گے ہم تو سبھی اس آگ میں ہیں، اللہ تعالیٰ اپنے بندوں کے درمیان فیصلے کر چکا ہے'),
(4182, 40, 49, 'اور (تمام) جہنمی مل کر جہنم کے داروغوں سے کہیں گے کہ تم ہی اپنے پروردگار سے دعا کرو کہ وه کسی دن تو ہمارے عذاب میں کمی کردے'),
(4183, 40, 50, 'وه جواب دیں گے کہ کیا تمہارے پاس تمہارے رسول معجزے لے کر نہیں آئے تھے؟ وه کہیں گے کیوں نہیں، وه کہیں گے کہ پھر تم ہی دعا کرو اور کافروں کی دعا محض بے اﺛر اور بےراه ہے'),
(4184, 40, 51, 'یقیناً ہم اپنے رسولوں کی اور ایمان والوں کی مدد زندگانیٴ دنیا میں بھی کریں گے اور اس دن بھی جب گواہی دینے والے کھڑے ہوں گے'),
(4185, 40, 52, 'جس دن ﻇالموں کو ان کی (عذر) معذرت کچھ نفع نہ دے گی ان کے لیے لعنت ہی ہوگی اور ان کے لیے برا گھر ہو گا'),
(4186, 40, 53, 'ہم نے موسیٰ (علیہ السلام) کو ہدایت نامہ عطا فرمایا اور بنو اسرائیل کو اس کتاب کا وارث بنایا'),
(4187, 40, 54, 'کہ وه ہدایت و نصیحت تھی عقل مندوں کے لیے'),
(4188, 40, 55, 'پس اے نبی! تو صبر کر اللہ کا وعده بلاشک (وشبہ) سچا ہی ہے تو اپنے گناه کی معافی مانگتا ره اور صبح شام اپنے پروردگار کی تسبیح اور حمد بیان کرتا ره'),
(4189, 40, 56, 'جو لوگ باوجود اپنے پاس کسی سند کے نہ ہونے کے آیات الٰہی میں جھگڑا کرتے ہیں ان کے دلوں میں بجز نری بڑائی کے اور کچھ نہیں وه اس تک پہنچنے والے ہی نہیں، سو تو اللہ کی پناه مانگتا ره بیشک وه پورا سننے واﻻ اور سب سے زیاده دیکھنے واﻻ ہے'),
(4190, 40, 57, 'آسمان و زمین کی پیدائش یقیناً انسان کی پیدائش سے بہت بڑا کام ہے، لیکن (یہ اور بات ہے کہ) اکثر لوگ بےعلم ہیں'),
(4191, 40, 58, 'اندھا اور بینا برابر نہیں نہ وه لوگ جو ایمان ﻻئے اور بھلے کام کیے بدکاروں کے (برابر ہیں)، تم (بہت) کم نصیحت حاصل کر رہے ہو'),
(4192, 40, 59, 'قیامت بالیقین اور بےشبہ آنے والی ہے، لیکن (یہ اور بات ہے کہ) بہت سے لوگ ایمان نہیں ﻻتے'),
(4193, 40, 60, 'اور تمہارے رب کا فرمان (سرزد ہوچکا ہے) کہ مجھ سے دعا کرو میں تمہاری دعاؤں کو قبول کروں گا یقین مانو کہ جو لوگ میری عبادت سے خودسری کرتے ہیں وه ابھی ابھی ذلیل ہوکر جہنم میں پہنچ جائیں گے'),
(4194, 40, 61, 'اللہ تعالیٰ نے تمھارے لیے رات بنادی کہ تم اس میں آرام حاصل کرو اور دن کو دیکھنے واﻻ بنا دیا، بیشک اللہ تعالیٰ لوگوں پر فضل وکرم واﻻ ہے لیکن اکثر لوگ شکر گزاری نہیں کرتے'),
(4195, 40, 62, 'یہی اللہ ہے تم سب کا رب ہر چیز کا خالق اس کے سوا کوئی معبود نہیں پھر کہاں تم پھرے جاتے ہو'),
(4196, 40, 63, 'اسی طرح وه لوگ بھی پھیرے جاتے رہے جو اللہ کی آیتوں کا انکار کرتے تھے'),
(4197, 40, 64, 'اللہ ہی ہے جس نے تمہارے لیے زمین کو ٹھہرنے کی جگہ اور آسمان کو چھت بنا دیا اور تمہاری صورتیں بنائیں اور بہت اچھی بنائیں اور تمہیں عمده عمده چیزیں کھانے کو عطا فرمائیں، یہی اللہ تمہارا پروردگار ہے، پس بہت ہی برکتوں واﻻ اللہ ہے سارے جہان کا پرورش کرنے واﻻ'),
(4198, 40, 65, 'وه زنده ہے جس کے سوا کوئی معبود نہیں پس تم خالص اس کی عبادت کرتے ہوئے اسے پکارو، تمام خوبیاں اللہ ہی کے لیے ہیں جو تمام جہانوں کا رب ہے'),
(4199, 40, 66, 'آپ کہہ دیجئے! کہ مجھے ان کی عبادت سے روک دیا گیا ہے جنہیں تم اللہ کے سوا پکار رہے ہو اس بنا پر کہ میرے پاس میرے رب کی دلیلیں پہنچ چکی ہیں، مجھے یہ حکم دیا گیا ہے کہ میں تمام جہانوں کے رب کا تابع فرمان ہو جاؤں'),
(4200, 40, 67, 'وه وہی ہے جس نے تمہیں مٹی سے پھر نطفے سے پھر خون کے لوتھڑے سے پیدا کیا پھر تمہیں بچہ کی صورت میں نکالتا ہے، پھر (تمہیں بڑھاتا ہے کہ) تم اپنی پوری قوت کو پہنچ جاؤ پھر بوڑھے ہو جاؤ، تم میں سے بعض اس سے پہلے ہی فوت ہو جاتے ہیں، (وه تمہیں چھوڑ دیتا ہے) تاکہ تم مدت معین تک پہنچ جاؤ اور تاکہ تم سوچ سمجھ لو'),
(4201, 40, 68, 'وہی ہے جو جلاتا ہے اور مار ڈالتا ہے، پھر جب وه کسی کام کا کرنا مقرر کرتا ہے تو اسے صرف یہ کہتا ہے کہ ہو جا پس وه ہو جاتا ہے'),
(4202, 40, 69, 'کیا تو نے انہیں دیکھا جو اللہ کی آیتوں میں جھگڑتے ہیں، وه کہاں پھیر دیے جاتے ہیں'),
(4203, 40, 70, 'جن لوگوں نے کتاب کو جھٹلایا اور اسے بھی جو ہم نے اپنے رسولوں کے ساتھ بھیجا انہیں ابھی ابھی حقیقت حال معلوم ہو جائے گی'),
(4204, 40, 71, 'جب کہ ان کی گردنوں میں طوق ہوں گے اور زنجیریں ہوں گی گھسیٹے جائیں گے'),
(4205, 40, 72, 'کھولتے ہوئے پانی میں اور پھر جہنم کی آگ میں جلائے جائیں گے'),
(4206, 40, 73, 'پھر ان سے پوچھا جائے گا کہ جنہیں تم شریک کرتے تھے وه کہاں ہیں۔'),
(4207, 40, 74, 'جو اللہ کے سوا تھے وه کہیں گے کہ وه تو ہم سے بہک گئے بلکہ ہم تو اس سے پہلے کسی کو بھی پکارتے ہی نہ تھے۔ اللہ تعالیٰ کافروں کو اسی طرح گمراه کرتا ہے'),
(4208, 40, 75, 'یہ بدلہ ہے اس چیز کا جو تم زمین میں ناحق پھولے نہ سماتے تھے۔ اور (بےجا) اتراتے پھرتے تھے'),
(4209, 40, 76, '(اب آؤ) جہنم میں ہمیشہ رہنے کے لیے (اس کے) دروازوں میں داخل ہو جاؤ، کیا ہی بری جگہ ہے تکبر کرنے والے کی'),
(4210, 40, 77, 'پس آپ صبر کریں اللہ کا وعده قطعاً سچا ہے، انہیں ہم نے جو وعدے دے رکھے ہیں ان میں سے کچھ ہم آپ کو دکھائیں یا (اس سے پہلے) ہم آپ کو وفات دے دیں، ان کا لوٹایا جانا تو ہماری ہی طرف ہے'),
(4211, 40, 78, 'یقیناً ہم آپ سے پہلے بھی بہت سے رسول بھیج چکے ہیں جن میں سے بعض کے (واقعات) ہم آپ کو بیان کر چکے ہیں اور ان میں سے بعض کے (قصے) تو ہم نے آپ کو بیان ہی نہیں کیے اور کسی رسول کا یہ (مقدور) نہ تھا کہ کوئی معجزه اللہ کی اجازت کے بغیر ﻻ سکے پھر جس وقت اللہ کا حکم آئےگا حق کے ساتھ فیصلہ کر دیا جائے گا اور اس جگہ اہل باطل خسارے میں ره جائیں گے'),
(4212, 40, 79, 'اللہ وه ہے جس نے تمہارے لیے چوپائے پیدا کیے جن میں سے بعض پر تم سوار ہوتے ہو اور بعض کو تم کھاتے ہو'),
(4213, 40, 80, 'اور بھی تمہارے لیے ان میں بہت سے نفع ہیں اور تاکہ اپنے سینوں میں چھپی ہوئی حاجتوں کو انہی پر سواری کر کے تم حاصل کر لو اور ان چوپایوں پر اور کشتیوں پر سوار کیے جاتے ہو'),
(4214, 40, 81, 'اللہ تمہیں اپنی نشانیاں دکھاتا جارہا ہے، پس تم اللہ کی کن کن نشانیوں کے منکر بنتے رہو گے'),
(4215, 40, 82, 'کیا انہوں نے زمین میں چل پھر کر اپنے سے پہلوں کا انجام نہیں دیکھا؟ جو ان سے تعداد میں زیاده تھے قوت میں سخت اور زمین میں بہت ساری یادگاریں چھوڑی تھیں، ان کے کیے کاموں نے انہیں کچھ بھی فائده نہیں پہنچایا'),
(4216, 40, 83, 'پس جب بھی ان کے پاس ان کے رسول کھلی نشانیاں لے کر آئے تو یہ اپنے پاس کے علم پر اترانے لگے، بالﺂخر جس چیز کو مذاق میں اڑا رہے تھے وه ان پر الٹ پڑی'),
(4217, 40, 84, 'ہمارا عذاب دیکھتے ہی کہنے لگے کہ اللہ واحد پر ہم ایمان ﻻئے اور جن جن کو ہم اس کا شریک بنا رہے تھے ہم نے ان سب سے انکار کیا'),
(4218, 40, 85, 'لیکن ہمارے عذاب کو دیکھ لینے کے بعد ان کے ایمان نے انہیں نفع نہ دیا۔ اللہ نے اپنا معمول یہی مقرر کر رکھا ہے جو اس کے بندوں میں برابر چلا آ رہا ہے۔ اور اس جگہ کافر خراب وخستہ ہوئے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4219, 41, 1, 'حٰم'),
(4220, 41, 2, 'اتاری ہوئی ہے بڑے مہربان بہت رحم والے کی طرف سے'),
(4221, 41, 3, '(ایسی) کتاب ہے جس کی آیتوں کی واضح تفصیل کی گئی ہے، (اس حال میں کہ) قرآن عربی زبان میں ہے اس قوم کے لیے جو جانتی ہے'),
(4222, 41, 4, 'خوش خبری سنانے واﻻ اور ڈرانے واﻻ ہے، پھر بھی ان کی اکثریت نے منھ پھیر لیا اور وه سنتے ہی نہیں'),
(4223, 41, 5, 'اور انہوں نے کہا کہ تو جس کی طرف ہمیں بلا رہا ہے ہمارے دل تو اس سے پردے میں ہیں اور ہمارے کانوں میں گرانی ہے اور ہم میں اور تجھ میں ایک حجاب ہے، اچھا تو اب اپنا کام کیے جا ہم بھی یقیناً کام کرنے والے ہیں'),
(4224, 41, 6, 'آپ کہہ دیجیئے! کہ میں تو تم ہی جیسا انسان ہوں مجھ پر وحی نازل کی جاتی ہے کہ تم سب کا معبود ایک اللہ ہی ہے سو تم اس کی طرف متوجہ ہو جاؤ اور اس سے گناہوں کی معافی چاہو، اور ان مشرکوں کے لیے (بڑی ہی) خرابی ہے'),
(4225, 41, 7, 'جو زکوٰة نہیں دیتے اورآخرت کے بھی منکر ہی رہتے ہیں'),
(4226, 41, 8, 'بیشک جو لوگ ایمان ﻻئیں اور بھلے کام کریں ان کے لیے نہ ختم ہونے واﻻ اجر ہے'),
(4227, 41, 9, 'آپ کہہ دیجئے! کہ کیا تم اس (اللہ) کا انکار کرتے ہو اور تم اس کے شریک مقرر کرتے ہو جس نے دو دن میں زمین پیدا کردی، سارے جہانوں کا پروردگار وہی ہے'),
(4228, 41, 10, 'اور اس نے زمین میں اس کے اوپر سے پہاڑ گاڑ دیے اوراس میں برکت رکھ دی اوراس میں (رہنے والوں کی) غذاؤں کی تجویز بھی اسی میں کر دی (صرف) چار دن میں، ضرورت مندوں کے لیے یکساں طور پر'),
(4229, 41, 11, 'پھر آسمان کی طرف متوجہ ہوا اور وه دھواں (سا) تھا پس اس سے اور زمین سے فرمایا کہ تم دونوں خوشی سے آؤ یا ناخوشی سے دونوں نے عرض کیا ہم بخوشی حاضر ہیں'),
(4230, 41, 12, 'پس دو دن میں سات آسمان بنا دیئے اور ہر آسمان میں اس کے مناسب احکام کی وحی بھیج دی اور ہم نے آسمان دنیا کو چراغوں سے زینت دی اور نگہبانی کی، یہ تدبیر اللہ غالب و دانا کی ہے'),
(4231, 41, 13, 'اب بھی یہ روگرداں ہوں تو کہہ دیجئے! کہ میں تمہیں اس کڑک (عذاب آسمانی) سے ڈراتا ہوں جو مثل عادیوں اور ﺛمودیوں کی کڑک ہوگی'),
(4232, 41, 14, 'ان کے پاس جب ان کے آگے پیچھے سے پیغمبر آئے کہ تم اللہ کے سوا کسی کی عبادت نہ کرو تو انہوں نے جواب دیا کہ اگر ہمارا پروردگار چاہتا تو فرشتوں کو بھیجتا۔ ہم تو تمہاری رسالت کے بالکل منکر ہیں'),
(4233, 41, 15, 'اب عاد نے تو بے وجہ زمین میں سرکشی شروع کر دی اور کہنے لگے کہ ہم سے زور آور کون ہے؟ کیا انہیں یہ نظر نہ آیا کہ جس نے انہیں پیدا کیا ہے وه ان سے (بہت ہی) زیاده زور آور ہے، وه (آخر تک) ہماری آیتوں کا انکار ہی کرتے رہے'),
(4234, 41, 16, 'بالﺂخر ہم نے ان پر ایک تیز و تند آندھی منحوس دنوں میں بھیج دی کہ انہیں دنیاوی زندگی میں ذلت کے عذاب کامزه چکھا دیں، اور (یقین مانو) کہ آخرت کا عذاب اس سے بہت زیاده رسوائی واﻻ ہے اور وه مدد نہیں کیے جائیں گے'),
(4235, 41, 17, 'رہے ﺛمود، سو ہم نے ان کی بھی رہبری کی پھر بھی انہوں نے ہدایت پر اندھے پن کو ترجیح دی جس بنا پر انہیں (سراپا) ذلت کے عذاب، کی کڑک نے ان کے کرتوتوں کے باعﺚ پکڑ لیا'),
(4236, 41, 18, 'اور (ہاں) ایماندار اور پارساؤں کو ہم نے (بال بال) بچا لیا'),
(4237, 41, 19, 'اور جس دن اللہ کے دشمن دوزخ کی طرف ﻻئے جائیں گے اور ان (سب) کو جمع کر دیا جائے گا'),
(4238, 41, 20, 'یہاں تک کہ جب بالکل جہنم کےپاس آجائیں گے اور ان پر ان کے کان اور ان کی آنکھیں اور ان کی کھالیں ان کے اعمال کی گواہی دیں گی'),
(4239, 41, 21, 'یہ اپنی کھالوں سے کہیں گے کہ تم نے ہمارے خلاف شہادت کیوں دی، وه جواب دیں گی کہ ہمیں اس اللہ نے قوت گویائی عطا فرمائی جس نے ہر چیز کو بولنے کی طاقت بخشی ہے، اسی نے تمہیں اول مرتبہ پیدا کیا اور اسی کی طرف تم سب لوٹائے جاؤ گے'),
(4240, 41, 22, 'اور تم (اپنی بداعمالیاں) اس وجہ سے پوشیده رکھتے ہی نہ تھے کہ تم پر تمہارے کان اور تمہاری آنکھیں اور تمہاری کھالیں گواہی دیں گی، ہاں تم یہ سمجھتے رہے کہ تم جو کچھ بھی کر رہے ہو اس میں سے بہت سےاعمال سے اللہ بےخبر ہے'),
(4241, 41, 23, 'تمہاری اسی بدگمانی نےجو تم نے اپنے رب سے کر رکھی تھی تمہیں ہلاک کر دیا اور بالﺂخر تم زیاں کاروں میں ہو گئے'),
(4242, 41, 24, 'اب اگر یہ صبر کریں تو بھی ان کا ٹھکانا جہنم ہی ہے۔ اور اگر یہ (عذر اور) معافی کےخواستگار ہوں تو بھی (معذور اور) معاف نہیں رکھے جائیں گے'),
(4243, 41, 25, 'اور ہم نے ان کے کچھ ہم نشیں مقرر کر رکھے تھے، جنہوں نے ان کے اگلے پچھلے اعمال ان کی نگاہوں میں خوبصورت بنا رکھے تھے اور ان کے حق میں بھی اللہ کا قول ان امتوں کے ساتھ پورا ہوا جو ان سے پہلے جنوں انسانوں کی گزر چکی ہیں۔ یقیناً وه زیاں کار ﺛابت ہوئے'),
(4244, 41, 26, 'اور کافروں نے کہا اس قرآن کو سنو ہی مت (اس کے پڑھے جانے کے وقت) اور بیہوده گوئی کرو کیا عجب کہ تم غالب آجاؤ'),
(4245, 41, 27, 'پس یقیناً ہم ان کافروں کو سخت عذاب کا مزه چکھائیں گے۔ اور انہیں ان کے بدترین اعمال کا بدلہ (ضرور) ضرور دیں گے'),
(4246, 41, 28, 'اللہ کے دشمنوں کی سزا یہی دوزح کی آگ ہے جس میں ان کا ہمیشگی کا گھر ہے (یہ) بدلہ ہے ہماری آیتوں سے انکار کرنے کا'),
(4247, 41, 29, 'اور کافر لوگ کہیں گے اے ہمارے رب! ہمیں جنوں انسانوں (کے وه دونوں فریق) دکھا جنہوں نے ہمیں گمراه کیا (تاکہ) ہم انہیں اپنے قدموں تلے ڈال دیں تاکہ وه جہنم میں سب سے نیچے (سخت عذاب میں) ہو جائیں'),
(4248, 41, 30, '(واقعی) جن لوگوں نے کہا کہ ہمارا پروردگار اللہ ہے پھر اسی پر قائم رہے ان کے پاس فرشتے (یہ کہتے ہوئے آتے ہیں کہ تم کچھ بھی اندیشہ اور غم نہ کرو (بلکہ) اس جنت کی بشارت سن لو جس کا تم وعده دیئے گئے ہو'),
(4249, 41, 31, 'تمہاری دنیوی زندگی میں بھی ہم تمہارے رفیق تھے اور آخرت میں بھی رہیں گے، جس چیز کو تمہارا جی چاہے اور جو کچھ تم مانگو سب تمہارے لیے (جنت میں موجود﴾ ہے'),
(4250, 41, 32, 'غفور و رحیم (معبود) کی طرف سے یہ سب کچھ بطور مہمانی کے ہے'),
(4251, 41, 33, 'اور اس سے زیاده اچھی بات واﻻ کون ہے جو اللہ کی طرف بلائے اور نیک کام کرے اور کہے کہ میں یقیناً مسلمانوں میں سے ہوں'),
(4252, 41, 34, 'نیکی اور بدی برابر نہیں ہوتی۔ برائی کو بھلائی سے دفع کرو پھر وہی جس کے اور تمہارے درمیان دشمنی ہے ایسا ہو جائے گا جیسے دلی دوست'),
(4253, 41, 35, 'اور یہ بات انہیں کو نصیب ہوتی ہے جو صبر کریں اور اسے سوائے بڑے نصیبے والوں کے کوئی نہیں پا سکتا'),
(4254, 41, 36, 'اور اگر شیطان کی طرف سے کوئی وسوسہ آئے تو اللہ کی پناه طلب کرو یقیناً وه بہت ہی سننے واﻻ اور جاننے واﻻ ہے'),
(4255, 41, 37, 'اور دن رات اور سورج چاند بھی (اسی کی) نشانیوں میں سے ہیں، تم سورج کو سجده نہ کرو نہ چاند کو بلکہ سجده اس اللہ کے لیے کرو جس نے ان سب کو پیدا کیا ہے، اگر تمہیں اس کی عبادت کرنی ہے تو'),
(4256, 41, 38, 'پھر بھی اگر یہ کبر و غرور کریں تو وه (فرشتے) جو آپ کے رب کے نزدیک ہیں وه تو رات دن اس کی تسبیح بیان کر رہے ہیں اور (کسی وقت بھی) نہیں اکتاتے'),
(4257, 41, 39, 'اس اللہ کی نشانیوں میں سے (یہ بھی) ہے کہ تو زمین کو دبی دبائی دیکھتا ہے پھر جب ہم اس پر مینہ برساتے ہیں تو وه تر وتازه ہوکر ابھرنے لگتی ہے جس نے اسے زنده کیا وہی یقینی طور پر مُردوں کو بھی زنده کرنے واﻻ ہے، بیشک وه ہر (ہر) چیز پر قادر ہے'),
(4258, 41, 40, 'بیشک جو لوگ ہماری آیتوں میں کج روی کرتے ہیں وه (کچھ) ہم سے مخفی نہیں، (بتلاؤ تو) جو آگ میں ڈاﻻ جائے وه اچھا ہے یا وه جو امن و امان کے ساتھ قیامت کے دن آئے؟ تم جو چاہو کرتے چلے جاؤ وه تمہارا سب کیا کرایا دیکھ رہا ہے'),
(4259, 41, 41, 'جن لوگوں نے اپنے پاس قرآن پہنچ جانے کے باوجود اس سے کفر کیا، (وه بھی ہم سے پوشیده نہیں) یہ بڑی باوقعت کتاب ہے'),
(4260, 41, 42, 'جس کے پاس باطل پھٹک بھی نہیں سکتا نہ اس کے آگے سے نہ اس کے پیچھے سے، یہ ہے نازل کرده حکمتوں والے خوبیوں والے (اللہ) کی طرف سے'),
(4261, 41, 43, 'آپ سے وہی کہا جاتا ہے جو آپ سے پہلے کے رسولوں سے بھی کہا گیا ہے، یقیناً آپ کا رب معافی واﻻ اور دردناک عذاب واﻻ ہے'),
(4262, 41, 44, 'اور اگر ہم اسے عجمی زبان کا قرآن بناتے تو کہتے کہ اس کی آیتیں صاف صاف بیان کیوں نہیں کی گئیں؟ یہ کیا کہ عجمی کتاب اور آپ عربی رسول؟ آپ کہہ دیجئے! کہ یہ تو ایمان والوں کے لیے ہدایت و شفا ہے اور جو ایمان نہیں ﻻتے ان کے کانوں میں تو (بہراپن اور) بوجھ ہے اور یہ ان پر اندھاپن ہے، یہ وه لوگ ہیں جو کسی بہت دور دراز جگہ سے پکارے جا رہے ہیں'),
(4263, 41, 45, 'یقیناً ہم نے موسیٰ (علیہ السلام) کو کتاب دی تھی، سو اس میں بھی اختلاف کیا گیا اور اگر (وه) بات نہ ہوتی (جو) آپ کے رب کی طرف سے پہلے ہی مقرر ہو چکی ہے۔ توان کے درمیان (کبھی کا) فیصلہ ہو چکا ہوتا، یہ لوگ تو اس کے بارے میں سخت بےچین کرنے والے شک میں ہیں'),
(4264, 41, 46, 'جو شخص نیک کام کرے گا وه اپنے نفع کے لیے اور جو برا کام کرے گا اس کا وبال اسی پر ہے۔ اور آپ کا رب بندوں پر ﻇلم کرنے واﻻ نہیں'),
(4265, 41, 47, 'قیامت کا علم اللہ ہی کی طرف لوٹایا جاتا ہے اور جو جو پھل اپنے شگوفوں میں سے نکلتے ہیں اور جو ماده حمل سے ہوتی ہے اور جو بچے وه جنتی ہے سب کا علم اسے ہے اور جس دن اللہ تعالیٰ ان (مشرکوں) کو بلا کر دریافت فرمائے گا میرے شریک کہاں ہیں، وه جواب دیں گے کہ ہم نے تو تجھے کہہ سنایا کہ ہم میں سے تو کوئی اس کا گواه نہیں'),
(4266, 41, 48, 'اور یہ جن (جن) کی پرستش اس سے پہلے کرتے تھے وه ان کی نگاه سے گم ہوگئے اور انہوں نے سمجھ لیا کہ اب ان کے لیے کوئی بچاؤ نہیں'),
(4267, 41, 49, 'بھلائی کے مانگنے سے انسان تھکتا نہیں اور اگر اسے کوئی تکلیف پہنچ جائے تو مایوس اور ناامید ہو جاتا ہے'),
(4268, 41, 50, 'اور جو مصیبت اسے پہنچ چکی ہے اس کے بعد اگر ہم اسے کسی رحمت کا مزه چکھائیں تو وه کہہ اٹھتا ہے کہ اس کا تو میں حقدار ہی تھا اور میں تو خیال نہیں کرسکتا کہ قیامت قائم ہوگی اور اگر میں اپنے رب کے پاس واپس کیا گیا تو بھی یقیناً میرے لیے اس کے پاس بھی بہتری ہے، یقیناً ہم ان کفار کو ان کے اعمال سے خبردار کریں گے اور انہیں سخت عذاب کا مزه چکھائیں گے'),
(4269, 41, 51, 'اور جب ہم انسان پر اپنا انعام کرتے ہیں تو وه منھ پھیر لیتا ہے اور کناره کش ہوجاتا ہے اور جب اسے مصیبت پڑتی ہے تو بڑی لمبی چوڑی دعائیں کرنے واﻻ بن جاتا ہے'),
(4270, 41, 52, 'آپ کہہ دیجئے! کہ بھلا یہ تو بتاؤ کہ اگر یہ قرآن اللہ کی طرف سے آیا ہوا ہو پھر تم نے اسے نہ مانا بس اس سے بڑھ کر بہکا ہوا کون ہوگا جو مخالفت میں (حق سے) دور چلا جائے'),
(4271, 41, 53, 'عنقریب ہم انہیں اپنی نشانیاں آفاق عالم میں بھی دکھائیں گے اور خود ان کی اپنی ذات میں بھی یہاں تک کہ ان پر کھل جائے کہ حق یہی ہے، کیا آپ کے رب کا ہر چیز سے واقف و آگاه ہونا کافی نہیں'),
(4272, 41, 54, 'یقین جانو! کہ یہ لوگ اپنے رب کے روبرو جانے سے شک میں ہیں، یاد رکھو کہ اللہ تعالیٰ ہر چیز کا احاطہ کیے ہوئے ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4273, 42, 1, 'حٰم'),
(4274, 42, 2, 'عسق'),
(4275, 42, 3, 'اللہ تعالیٰ جو زبردست ہے اور حکمت واﻻ ہے اسی طرح تیری طرف اور تجھ سے اگلوں کی طرف وحی بھیجتا رہا'),
(4276, 42, 4, 'آسمانوں کی (تمام) چیزیں اور جو کچھ زمین میں ہے سب اسی کا ہے وه برتر اور عظیم الشان ہے'),
(4277, 42, 5, 'قریب ہے آسمان اوپر سے پھٹ پڑیں اور تمام فرشتے اپنے رب کی پاکی تعریف کے ساتھ بیان کر رہے ہیں اور زمین والوں کے لیے استغفار کر رہے ہیں۔ خوب سمجھ رکھو کہ اللہ تعالیٰ ہی معاف فرمانے واﻻ رحمت واﻻ ہے'),
(4278, 42, 6, 'اور جن لوگوں نے اس کے سوا دوسروں کو کارساز بنالیا ہے اللہ تعالیٰ ان پر نگران ہے اور آپ ان کے ذمہ دار نہیں ہیں'),
(4279, 42, 7, 'اسی طرح ہم نے آپ کی طرف عربی قرآن کی وحی کی ہے تاکہ آپ مکہ والوں کو اور اس کے آس پاس کے لوگوں کو خبردار کردیں اور جمع ہونے کے دن سے جس کے آنے میں کوئی شک نہیں ڈرا دیں۔ ایک گروه جنت میں ہوگا اور ایک گروه جہنم میں ہوگا'),
(4280, 42, 8, 'اگر اللہ تعالیٰ چاہتا تو ان سب کو ایک ہی امت کا بنا دیتا لیکن وه جسے چاہتا ہے اپنی رحمت میں داخل کرلیتا ہے اور ﻇالموں کا حامی اور مددگار کوئی نہیں'),
(4281, 42, 9, 'کیا ان لوگوں نے اللہ تعالیٰ کے سوا اور کارساز بنالیے ہیں، (حقیقتاً تو) اللہ تعالیٰ ہی کارساز ہے وہی مُردوں کو زنده کرے گا اور وہی ہر چیز پر قادر ہے'),
(4282, 42, 10, 'اور جس جس چیز میں تمہارا اختلاف ہو اس کا فیصلہ اللہ تعالیٰ ہی کی طرف ہے، یہی اللہ میرا رب ہے جس پر میں نے بھروسہ کر رکھا ہے اور جس کی طرف میں جھکتا ہوں'),
(4283, 42, 11, 'وه آسمانوں اور زمین کا پیدا کرنے واﻻ ہے اس نے تمہارے لیے تمہاری جنس کے جوڑے بنادیئے ہیں اور چوپایوں کے جوڑے بنائے ہیں تمہیں وه اس میں پھیلا رہا ہے اس جیسی کوئی چیز نہیں وه سننے اور دیکھنے واﻻ ہے'),
(4284, 42, 12, 'آسمانوں اور زمین کی کنجیاں اسی کی ہیں، جس کی چاہے روزی کشاده کردے اور تنگ کردے، یقیناً وه ہر چیز کو جاننے واﻻ ہے'),
(4285, 42, 13, 'اللہ تعالیٰ نے تمہارے لیے وہی دین مقرر کردیا ہے جس کے قائم کرنے کا اس نے نوح (علیہ السلام) کو حکم دیا تھا اور جو (بذریعہ وحی) ہم نے تیری طرف بھیج دی ہے، اور جس کا تاکیدی حکم ہم نے ابراہیم اور موسیٰ اور عیسیٰ (علیہم السلام) کو دیا تھا، کہ اس دین کو قائم رکھنا اور اس میں پھوٹ نہ ڈالنا جس چیز کی طرف آپ انہیں بلا رہے ہیں وه تو (ان) مشرکین پر گراں گزرتی ہے، اللہ تعالیٰ جسے چاہتا ہے اپنا برگزیده بناتا ہے اور جو بھی اس کی طرف رجوع کرے وه اس کی صحیح ره نمائی کرتا ہے'),
(4286, 42, 14, 'ان لوگوں نے اپنے پاس علم آجانے کے بعد ہی اختلاف کیا (اور وه بھی) باہمی ضد بحﺚ سے اور اگر آپ کے رب کی بات ایک وقت مقرر تک کے لیے پہلے ہی سے قرار پا گئی ہوئی نہ ہوتی تو یقیناً ان کا فیصلہ ہوچکا ہوتا اور جن لوگوں کو ان کے بعد کتاب دی گئی ہے وه بھی اس کی طرف سے الجھن والے شک میں پڑے ہوئے ہیں'),
(4287, 42, 15, 'پس آپ لوگوں کو اسی طرف بلاتے رہیں اور جو کچھ آپ سے کہا گیا ہے اس پر مضبوطی سے جم جائیں اور ان کی خواہشوں پر نہ چلیں اور کہہ دیں کہ اللہ تعالیٰ نے جتنی کتابیں نازل فرمائی ہیں میرا ان پر ایمان ہے اور مجھے حکم دیا گیا ہے کہ تم میں انصاف کرتا رہوں۔ ہمارا اور تم سب کا پروردگار اللہ ہی ہے ہمارے اعمال ہمارے لیے ہیں اور تمہارے اعمال تمہارے لیے ہیں، ہم تم میں کوئی کٹ حجتی نہیں اللہ تعالیٰ ہم (سب) کو جمع کرے گا اور اسی کی طرف لوٹنا ہے'),
(4288, 42, 16, 'اور جو لوگ اللہ تعالیٰ کی باتوں میں جھگڑا ڈالتے ہیں اس کے بعد کہ (مخلوق) اسے مان چکی ان کی کٹ حجتی اللہ کے نزدیک باطل ہے، اور ان پر غضب ہے اور ان کے لیے سخت عذاب ہے'),
(4289, 42, 17, 'اللہ تعالیٰ نے حق کے ساتھ کتاب نازل فرمائی ہے اور ترازو بھی (اتاری ہے) اور آپ کو کیا خبر شاید قیامت قریب ہی ہو'),
(4290, 42, 18, 'اس کی جلدی انہیں پڑی ہے جو اسے نہیں مانتے اور جو اس پر یقین رکھتے ہیں وه تو اس سے ڈر رہے ہیں انہیں اس کے حق ہونے کا پورا علم ہے۔ یاد رکھو جو لوگ قیامت کے معاملہ میں لڑ جھگڑ رہے ہیں، وه دور کی گمراہی میں پڑے ہوئے ہیں'),
(4291, 42, 19, 'اللہ تعالیٰ اپنے بندوں پر بڑا ہی لطف کرنے واﻻ ہے، جسے چاہتا ہے کشاده روزی دیتا ہے اور وه بڑی طاقت، بڑے غلبہ واﻻ ہے'),
(4292, 42, 20, 'جس کا اراده آخرت کی کھیتی کا ہو ہم اسے اس کی کھیتی میں ترقی دیں گے اور جو دنیا کی کھیتی کی طلب رکھتا ہو ہم اسے اس میں سے ہی کچھ دے دیں گے، ایسے شخص کا آخرت میں کوئی حصہ نہیں'),
(4293, 42, 21, 'کیا ان لوگوں نے ایسے (اللہ کے) شریک (مقرر کر رکھے) ہیں جنہوں نے ایسے احکام دین مقرر کر دیئے ہیں جو اللہ کے فرمائے ہوئے نہیں ہیں۔ اگر فیصلے کے دن کا وعده نہ ہوتا تو (ابھی ہی) ان میں فیصلہ کردیا جاتا۔ یقیناً (ان) ﻇالموں کے لیے ہی دردناک عذاب ہے'),
(4294, 42, 22, 'آپ دیکھیں گے کہ یہ ﻇالم اپنے اعمال سے ڈر رہے ہوں گے جن کے وبال ان پر واقع ہونے والے ہیں، اور جو لوگ ایمان ﻻئے اور انہوں نے نیک اعمال کیے وه بہشتوں کے باغات میں ہوں گے وه جو خواہش کریں اپنے رب کے پاس موجود پائیں گے یہی ہے بڑا فضل'),
(4295, 42, 23, 'یہی وه ہے جس کی بشارت اللہ تعالیٰ اپنے ان بندوں کو دے رہا ہے جو ایمان ﻻئے اور (سنت کے مطابق) نیک عمل کیے تو کہہ دیجئے! کہ میں اس پر تم سے کوئی بدلہ نہیں چاہتا مگر محبت رشتہ داری کی، جو شخص کوئی نیکی کرے ہم اس کے لیے اس کی نیکی میں اور نیکی بڑھا دیں گے۔ بیشک اللہ تعالیٰ بخشنے واﻻ (اور) بہت قدر دان ہے'),
(4296, 42, 24, 'کیا یہ کہتے ہیں کہ (پیغمبر نے) اللہ پر جھوٹ باندھا ہے، اگر اللہ تعالیٰ چاہے تو آپ کے دل پر مہر لگادے اور اللہ تعالیٰ اپنی باتوں سے جھوٹ کو مٹا دیتا ہے اور سچ کو ﺛابت رکھتا ہے۔ وه سینے کی باتوں کو جاننے واﻻ ہے'),
(4297, 42, 25, 'وہی ہے جو اپنے بندوں کی توبہ قبول فرماتا ہے اور گناہوں سے درگزر فرماتا ہے اور جو کچھ تم کررہے ہو (سب) جانتا ہے'),
(4298, 42, 26, 'ایمان والوں اور نیکوکار لوگوں کی سنتا ہے اور انہیں اپنے فضل سے اور بڑھا کر دیتا ہے اور کفار کے لیے سخت عذاب ہے'),
(4299, 42, 27, 'اگر اللہ تعالیٰ اپنے (سب) بندوں کی روزی فراخ کر دیتا تو وه زمین میں فساد برپا کردیتے لیکن وه اندازے کے ساتھ جو کچھ چاہتا ہے نازل فرماتا ہے۔ وه اپنے بندوں سے پورا خبردار ہے اور خوب دیکھنے واﻻ ہے'),
(4300, 42, 28, 'اور وہی ہے جو لوگوں کے ناامید ہوجانے کے بعد بارش برساتا اور اپنی رحمت پھیلا دیتا ہے وہی ہے کارساز اور قابل حمد و ثنا'),
(4301, 42, 29, 'اور اس کی نشانیوں میں سے آسمانوں اور زمین کی پیدائش ہے اور ان میں جانداروں کا پھیلانا ہے۔ وه اس پر بھی قادر ہے کہ جب چاہے انہیں جمع کردے'),
(4302, 42, 30, 'تمہیں جو کچھ مصیبتیں پہنچتی ہیں وه تمہارے اپنے ہاتھوں کے کرتوت کابدلہ ہے، اور وه تو بہت سی باتوں سے درگزر فرما دیتا ہے'),
(4303, 42, 31, 'اور تم ہمیں زمین میں عاجز کرنے والے نہیں ہو، تمہارے لیے سوائے اللہ تعالیٰ کے نہ کوئی کارساز ہے نہ مددگار'),
(4304, 42, 32, 'اور دریا میں چلنے والی پہاڑوں جیسی کشتیاں اس کی نشانیوں میں سے ہیں'),
(4305, 42, 33, 'اگر وه چاہے تو ہوا بند کردے اور یہ کشتیاں سمندروں پر رکی ره جائیں۔ یقیناً اس میں ہر صبر کرنے والے شکرگزار کے لیے نشانیاں ہیں'),
(4306, 42, 34, 'یا انہیں ان کے کرتوتوں کے باعﺚ تباه کردے، وه تو بہت سے خطاؤں سے درگزر فرمایا کرتا ہے'),
(4307, 42, 35, 'اور تاکہ جو لوگ ہماری نشانیوں میں جھگڑتے ہیں وه معلوم کرلیں کہ ان کے لیے کوئی چھٹکارا نہیں'),
(4308, 42, 36, 'تو تمہیں جو کچھ دیا گیا ہے وه زندگانی دنیا کا کچھ یونہی سا اسباب ہے، اور اللہ کے پاس جو ہے وه اس سے بدرجہ بہتر اور پائیدار ہے، وه ان کے لیے ہے جو ایمان ﻻئے اور صرف اپنے رب ہی پر بھروسہ رکھتے ہیں'),
(4309, 42, 37, 'اور کبیره گناہوں سے اور بے حیائیوں سے بچتے ہیں اور غصے کے وقت (بھی) معاف کردیتے ہیں'),
(4310, 42, 38, 'اور اپنے رب کے فرمان کو قبول کرتے ہیں اور نماز کی پابندی کرتے ہیں اور ان کا (ہر) کام آپس کے مشورے سے ہوتا ہے، اور جو ہم نے انہیں دے رکھا ہے اس میں سے (ہمارے نام پر) دیتے ہیں'),
(4311, 42, 39, 'اور جب ان پر ﻇلم (و زیادتی) ہو تو وه صرف بدلہ لے لیتے ہیں'),
(4312, 42, 40, 'اور برائی کا بدلہ اسی جیسی برائی ہے، اور جو معاف کر دے اور اصلاح کرلے اس کا اجر اللہ کے ذمے ہے، (فی الواقع) اللہ تعالیٰ ﻇالموں سے محبت نہیں کرتا'),
(4313, 42, 41, 'اور جو شخص اپنے مظلوم ہونے کے بعد (برابر کا) بدلہ لے لے تو ایسے لوگوں پر (الزام کا) کوئی راستہ نہیں'),
(4314, 42, 42, 'یہ راستہ صرف ان لوگوں پر ہے جو خود دوسروں پر ﻇلم کریں اور زمین میں ناحق فساد کرتے پھریں، یہی لوگ ہیں جن کے لیے دردناک عذاب ہے'),
(4315, 42, 43, 'اور جو شخص صبر کر لے اور معاف کردے یقیناً یہ بڑی ہمت کے کاموں میں سے (ایک کام) ہے'),
(4316, 42, 44, 'اور جسے اللہ تعالیٰ بہکا دے اس کا اس کے بعد کوئی چاره ساز نہیں، اور تو دیکھے گا کہ ﻇالم لوگ عذاب کو دیکھ کر کہہ رہے ہوں گے کہ کیا واپس جانے کی کوئی راه ہے'),
(4317, 42, 45, 'اور تو انہیں دیکھے گا کہ وه (جہنم کے) سامنے ﻻکھڑے کیے جائیں گے مارے ذلت کے جھکے جارہے ہوں گے اور کنکھیوں سے دیکھ رہے ہوں گے، ایمان والے صاف کہیں گے کہ حقیقی زیاںکار وه ہیں جنہوں نے آج قیامت کے دن اپنے آپ کو اور اپنے گھر والوں کو نقصان میں ڈال دیا۔ یاد رکھو کہ یقیناً ﻇالم لوگ دائمی عذاب میں ہیں'),
(4318, 42, 46, 'ان کے کوئی مددگار نہیں جو اللہ تعالیٰ سے الگ ان کی امداد کرسکیں اور جسے اللہ گمراه کردے اس کے لیے کوئی راستہ ہی نہیں'),
(4319, 42, 47, 'اپنے رب کا حکم مان لو اس سے پہلے کہ اللہ کی جانب سے وه دن آجائے جس کا ہٹ جانا ناممکن ہے، تمہیں اس روز نہ تو کوئی پناه کی جگہ ملے گی نہ چھﭗ کر انجان بن جانے کی'),
(4320, 42, 48, 'اگر یہ منھ پھیرلیں تو ہم نے آپ کو ان پر نگہبان بنا کر نہیں بھیجا، آپ کے ذمہ تو صرف پیغام پہنچا دینا ہے، ہم جب کبھی انسان کو اپنی مہربانی کا مزه چکھاتے ہیں تو وه اس پر اترا جاتا ہے اور اگر انہیں ان کے اعمال کی وجہ سے کوئی مصیبت پہنچتی ہے تو بےشک انسان بڑا ہی ناشکرا ہے'),
(4321, 42, 49, 'آسمانوں کی اور زمین کی سلطنت اللہ تعالیٰ ہی کے لیے ہے، وه جو چاہتا ہے پیدا کرتا ہے جس کو چاہتا ہے بیٹیاں دیتا ہے اور جسے چاہتا ہے بیٹے دیتا ہے'),
(4322, 42, 50, 'یا انہیں جمع کردیتا ہے بیٹے بھی اور بیٹیاں بھی اور جسے چاہے بانجھ کر دیتا ہے، وه بڑے علم واﻻ اور کامل قدرت واﻻ ہے'),
(4323, 42, 51, 'ناممکن ہے کہ کسی بنده سے اللہ تعالیٰ کلام کرے مگر وحی کے ذریعہ یا پردے کے پیچھے سے یا کسی فرشتہ کو بھیجے اور وه اللہ کے حکم سے جو وه چاہے وحی کرے، بیشک وه برتر ہے حکمت واﻻ ہے'),
(4324, 42, 52, 'اور اسی طرح ہم نے آپ کی طرف اپنے حکم سے روح کو اتارا ہے، آپ اس سے پہلے یہ بھی نہیں جانتے تھے کہ کتاب اور ایمان کیا چیز ہے؟ لیکن ہم نے اسے نور بنایا، اس کے ذریعہ سے اپنے بندوں میں سے جسے چاہتے ہیں، ہدایت دیتے ہیں، بیشک آپ راه راست کی رہنمائی کر رہے ہیں'),
(4325, 42, 53, 'اس اللہ کی راه کی جس کی ملکیت میں آسمانوں اور زمین کی ہر چیز ہے۔ آگاه رہو سب کام اللہ تعالیٰ ہی کی طرف لوٹتے ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4326, 43, 1, 'حٰم'),
(4327, 43, 2, 'قسم ہے اس واضح کتاب کی'),
(4328, 43, 3, 'ہم نے اس کو عربی زبان کا قرآن بنایا ہے کہ تم سمجھ لو'),
(4329, 43, 4, 'یقیناً یہ لوح محفوظ میں ہے اور ہمارے نزدیک بلند مرتبہ حکمت والی ہے'),
(4330, 43, 5, 'کیا ہم اس نصیحت کو تم سے اس بنا پر ہٹالیں کہ تم حد سے گزر جانے والے لوگ ہو'),
(4331, 43, 6, 'اور ہم نے اگلے لوگوں میں بھی کتنے ہی نبی بھیجے'),
(4332, 43, 7, 'جو نبی ان کے پاس آیا انہوں نے اس کا مذاق اڑایا'),
(4333, 43, 8, 'پس ہم نے ان سے زیاده زور آوروں کو تباه کر ڈاﻻ اور اگلوں کی مثال گزر چکی ہے'),
(4334, 43, 9, 'اگر آپ ان سے دریافت کریں کہ آسمانوں اور زمین کو کس نے پیدا کیا تو یقیناً ان کا جواب یہی ہوگا کہ انہیں غالب و دانا (اللہ) نے ہی پیدا کیا ہے'),
(4335, 43, 10, 'وہی ہے جس نے تمہارے لیے زمین کو فرش (بچھونا) بنایا اور اس میں تمہارے لیے راستے کردیے تاکہ تم راه پالیا کرو'),
(4336, 43, 11, 'اسی نے آسمان سے ایک اندازے کے مطابق پانی نازل فرمایا، پس ہم نے اس سے مرده شہر کو زنده کردیا۔ اسی طرح تم نکالے جاؤ گے'),
(4337, 43, 12, 'جس نے تمام چیزوں کے جوڑے بنائے اور تمہارے لیے کشتیاں بنائیں اور چوپائے جانور (پیدا کیے) جن پر تم سوار ہوتے ہو'),
(4338, 43, 13, 'تاکہ تم ان کی پیٹھ پر جم کر سوار ہوا کرو پھر اپنے رب کی نعمت کو یاد کرو جب اس پر ٹھیک ٹھاک بیٹھ جاؤ، اور کہو پاک ذات ہے اس کی جس نے اسے ہمارے بس میں کردیا حاﻻنکہ ہمیں اسے قابو کرنے کی طاقت نہ تھی'),
(4339, 43, 14, 'اور بالیقین ہم اپنے رب کی طرف لوٹ کر جانے والے ہیں'),
(4340, 43, 15, 'اور انہوں نے اللہ کے بعض بندوں کو اس کا جز ٹھہرا دیا یقیناً انسان کھلم کھلا ناشکرا ہے'),
(4341, 43, 16, 'کیا اللہ تعالیٰ نے اپنی مخلوق میں سے بیٹیاں تو خود رکھ لیں اور تمہیں بیٹوں سے نوازا'),
(4342, 43, 17, '(حاﻻنکہ) ان میں سے کسی کو جب اس چیز کی خبر دی جائے جس کی مثال اس نے (اللہ) رحمٰن کے لیے بیان کی ہے تو اس کا چہره سیاه پڑ جاتا ہے اور وه غمگین ہو جاتا ہے'),
(4343, 43, 18, 'کیا (اللہ کی اوﻻد لڑکیاں ہیں) جو زیورات میں پلیں اور جھگڑے میں (اپنی بات) واضح نہ کرسکیں؟'),
(4344, 43, 19, 'اور انہوں نے فرشتوں کو جو رحمٰن کے عبادت گزار ہیں عورتیں قرار دے لیا۔ کیا ان کی پیدائش کے موقع پر یہ موجود تھے؟ ان کی یہ گواہی لکھ لی جائے گی اور ان سے (اس چیز کی) باز پرس کی جائے گی'),
(4345, 43, 20, 'اور کہتے ہیں اگر اللہ چاہتا تو ہم ان کی عبادت نہ کرتے۔ انہیں اس کی کچھ خبر نہیں، یہ تو صرف اٹکل پچو (جھوٹ باتیں) کہتے ہیں۔'),
(4346, 43, 21, 'کیا ہم نے انہیں اس سے پہلے کوئی (اور) کتاب دی ہے جسے یہ مضبوط تھامے ہوئے ہیں'),
(4347, 43, 22, '(نہیں نہیں) بلکہ یہ تو کہتے ہیں کہ ہم نے اپنے باپ دادا کو ایک مذہب پر پایا اور ہم انہی کے نقش قدم پر چل کر راه یافتہ ہیں'),
(4348, 43, 23, 'اسی طرح آپ سے پہلے بھی ہم نے جس بستی میں کوئی ڈرانے واﻻ بھیجا وہاں کے آسوده حال لوگوں نے یہی جواب دیا کہ ہم نے اپنے باپ دادا کو (ایک راه پر اور) ایک دین پر پایا اور ہم تو انہی کے نقش پا کی پیروی کرنے والے ہیں'),
(4349, 43, 24, '(نبی نے) کہا بھی کہ اگرچہ میں تمہارے پاس اس سے بہت بہتر (مقصود تک پہنچانے واﻻ) طریقہ لے آیا ہوں جس پر تم نے اپنے باپ دادوں کو پایا، تو انہوں نے جواب دیا کہ ہم اس کے منکر ہیں جسے دے کر تمہیں بھیجا گیا ہے'),
(4350, 43, 25, 'پس ہم نے ان سے انتقام لیا اور دیکھ لے جھٹلانے والوں کا کیسا انجام ہوا؟'),
(4351, 43, 26, 'اور جبکہ ابراہیم (علیہ السلام) نے اپنے والد سے اور اپنی قوم سے فرمایا کہ میں ان چیزوں سے بیزار ہوں جن کی تم عبادت کرتے ہو'),
(4352, 43, 27, 'بجز اس ذات کے جس نے مجھے پیدا کیا ہے اور وہی مجھے ہدایت بھی کرے گا'),
(4353, 43, 28, 'اور (ابراہیم علیہ السلام) اسی کو اپنی اوﻻد میں بھی باقی رہنے والی بات قائم کر گئے تاکہ لوگ (شرک سے) باز آتے رہیں'),
(4354, 43, 29, 'بلکہ میں نے ان لوگوں کو اور ان کے باپ دادوں کو سامان (اور اسباب) دیا، یہاں تک کہ ان کے پاس حق اور صاف صاف سنانے واﻻ رسول آگیا'),
(4355, 43, 30, 'اور حق کے پہنچتے ہی یہ بول پڑے کہ یہ تو جادو ہے اور ہم اس کے منکر ہیں'),
(4356, 43, 31, 'اور کہنے لگے، یہ قرآن ان دونوں بستیوں میں سے کسی بڑے آدمی پر کیوں نہ نازل کیا گیا'),
(4357, 43, 32, 'کیا آپ کے رب کی رحمت کو یہ تقسیم کرتے ہیں؟ ہم نے ہی ان کی زندگانیٴ دنیا کی روزی ان میں تقسیم کی ہے اور ایک کو دوسرے سے بلند کیا ہے تاکہ ایک دوسرے کو ماتحت کر لے جسے یہ لوگ سمیٹتے پھرتے ہیں اس سے آپ کے رب کی رحمت بہت ہی بہتر ہے'),
(4358, 43, 33, 'اور اگر یہ بات نہ ہوتی کہ تمام لوگ ایک ہی طریقہ پر ہو جائیں گے تو رحمٰن کے ساتھ کفر کرنے والوں کے گھروں کی چھتوں کو ہم چاندی کی بنادیتے۔ اور زینوں کو (بھی) جن پر چڑھا کرتے'),
(4359, 43, 34, 'اور ان کے گھروں کے دروازے اور تخت بھی جن پر وه تکیہ لگا لگا کر بیٹھتے'),
(4360, 43, 35, 'اور سونے کے بھی، اور یہ سب کچھ یونہی سا دنیا کی زندگی کا فائده ہے اور آخرت تو آپ کے رب کے نزدیک (صرف) پرہیزگاروں کے لیے (ہی) ہے۔'),
(4361, 43, 36, 'اور جو شخص رحمٰن کی یاد سے غفلت کرے ہم اس پر ایک شیطان مقرر کردیتے ہیں وہی اس کا ساتھی رہتا ہے'),
(4362, 43, 37, 'اور وه انہیں راه سے روکتے ہیں اور یہ اسی خیال میں رہتے ہیں کہ یہ ہدایت یافتہ ہیں'),
(4363, 43, 38, 'یہاں تک کہ جب وه ہمارے پاس آئے گا کہے گا کاش! میرے اور تیرے درمیان مشرق اور مغرب کی دوری ہوتی (تو) بڑا برا ساتھی ہے'),
(4364, 43, 39, 'اور جب کہ تم ﻇالم ٹھہر چکے تو تمہیں آج ہرگز تم سب کا عذاب میں شریک ہونا کوئی نفع نہ دے گا'),
(4365, 43, 40, 'کیا پس تو بہرے کو سنا سکتا ہے یا اندھے کو راه دکھا سکتا ہے اور اسے جو کھلی گمراہی میں ہو'),
(4366, 43, 41, 'پس اگر ہم تجھے یہاں سے لے بھی جائیں تو بھی ہم ان سے بدلہ لینے والے ہیں'),
(4367, 43, 42, 'یا جو کچھ ان سے وعده کیا ہے وه تجھے دکھا دیں ہم ان پر بھی قدرت رکھتے ہیں'),
(4368, 43, 43, 'پس جو وحی آپ کی طرف کی گئی ہے اسے مضبوط تھامے رہیں بیشک آپ راه راست پر ہیں'),
(4369, 43, 44, 'اور یقیناً یہ (خود) آپ کے لیے اور آپ کی قوم کے لیے نصیحت ہے اور عنقریب تم لوگ پوچھے جاؤ گے'),
(4370, 43, 45, 'اور ہمارے ان نبیوں سے پوچھو! جنہیں ہم نے آپ سے پہلے بھیجا تھا کہ کیا ہم نے سوائے رحمٰن کے اور معبود مقرر کیے تھے جن کی عبادت کی جائے؟'),
(4371, 43, 46, 'اور ہم نے موسیٰ (علیہ السلام) کو اپنی نشانیاں دے کر فرعون اور اس کے امراء کے پاس بھیجا تو (موسیٰ علیہ السلام نے جاکر) کہا کہ میں تمام جہانوں کے رب کا رسول ہوں'),
(4372, 43, 47, 'پس جب وه ہماری نشانیاں لے کر ان کے پاس آئے تو وه بےساختہ ان پر ہنسنے لگے'),
(4373, 43, 48, 'اور ہم انہیں جو نشانی دکھاتے تھے وه دوسری سے بڑھی چڑھی ہوتی تھی اور ہم نے انہیں عذاب میں پکڑا تاکہ وه باز آجائیں'),
(4374, 43, 49, 'اور انہوں نے کہا اے جادوگر! ہمارے لیے اپنے رب سے اس کی دعا کر جس کا اس نے تجھ سے وعده کر رکھا ہے، یقین مان کہ ہم راه پر لگ جائیں گے'),
(4375, 43, 50, 'پھر جب ہم نے وه عذاب ان سے ہٹالیا انہوں نے اسی وقت اپنا قول وقرار توڑ دیا'),
(4376, 43, 51, 'اور فرعون نے اپنی قوم میں منادی کرائی اور کہا اے میری قوم! کیا مصر کا ملک میرا نہیں؟ اور میرے (محلوں کے) نیچے یہ نہریں بہہ رہی ہیں، کیا تم دیکھتے نہیں؟'),
(4377, 43, 52, 'بلکہ میں بہتر ہوں بہ نسبت اس کے جو بےتوقیر ہے اور صاف بول بھی نہیں سکتا'),
(4378, 43, 53, 'اچھا اس پر سونے کے کنگن کیوں نہیں آپڑے یا اس کے ساتھ پر باندھ کر فرشتے ہی آجاتے'),
(4379, 43, 54, 'اس نے اپنی قوم کو بہلایا پھسلایا اور انہوں نے اسی کی مان لی، یقیناً یہ سارے ہی نافرمان لوگ تھے'),
(4380, 43, 55, 'پھر جب انہوں نے ہمیں غصہ دﻻیا تو ہم نے ان سے انتقام لیا اور سب کو ڈبو دیا'),
(4381, 43, 56, 'پس ہم نے انہیں گیا گزرا کردیا اور پچھلوں کے لیے مثال بنادی'),
(4382, 43, 57, 'اور جب ابن مریم کی مثال بیان کی گئی تو اس سے تیری قوم (خوشی سے) چیخنے لگی ہے'),
(4383, 43, 58, 'اور انہوں نے کہا کہ ہمارے معبود اچھے ہیں یا وه؟ تجھ سے ان کا یہ کہنا محض جھگڑے کی غرض سے ہے، بلکہ یہ لوگ ہیں ہی جھگڑالو'),
(4384, 43, 59, 'عیسیٰ (علیہ السلام) بھی صرف بنده ہی ہے جس پر ہم نے احسان کیا اور اسے بنی اسرائیل کے لیے نشان قدرت بنایا'),
(4385, 43, 60, 'اگر ہم چاہتے تو تمہارے عوض فرشتے کردیتے جو زمین میں جانشینی کرتے'),
(4386, 43, 61, 'اور یقیناً عیسیٰ (علیہ السلام) قیامت کی علامت ہے پس تم (قیامت) کے بارے میں شک نہ کرو اور میری تابعداری کرو، یہی سیدھی راه ہے'),
(4387, 43, 62, 'اور شیطان تمہیں روک نہ دے، یقیناً وه تمہارا صریح دشمن ہے'),
(4388, 43, 63, 'اور جب عیسیٰ (علیہ السلام) معجزے کو ﻻئے تو کہا۔ کہ میں تمہارے پاس حکمت ﻻیا ہوں اور اس لیے آیا ہوں کہ جن بعض چیزوں میں تم مختلف ہو، انہیں واضح کردوں، پس تم اللہ تعالیٰ سے ڈرو اور میرا کہا مانو'),
(4389, 43, 64, 'میرا اور تمہارا رب فقط اللہ تعالیٰ ہی ہے۔ پس تم سب اس کی عبادت کرو۔ راه راست (یہی) ہے'),
(4390, 43, 65, 'پھر (بنی اسرائیل کی) جماعتوں نے آپس میں اختلاف کیا، پس ﻇالموں کے لیے خرابی ہے دکھ والے دن کی آفت سے'),
(4391, 43, 66, 'یہ لوگ صرف قیامت کے منتظر ہیں کہ وه اچانک ان پر آپڑے اور انہیں خبر بھی نہ ہو'),
(4392, 43, 67, 'اس دن (گہرے) دوست بھی ایک دوسرے کے دشمن بن جائیں گے سوائے پرہیزگاروں کے'),
(4393, 43, 68, 'میرے بندو! آج تو تم پر کوئی خوف (و ہراس) ہے اور نہ تم (بد دل اور) غمزده ہوگے'),
(4394, 43, 69, 'جو ہماری آیتوں پر ایمان ﻻئے اور تھے بھی وه (فرماں بردار) مسلمان'),
(4395, 43, 70, 'تم اور تمہاری بیویاں ہشاش بشاش (راضی خوشی) جنت میں چلے جاؤ'),
(4396, 43, 71, 'ان کے چاروں طرف سے سونے کی رکابیاں اور سونے کے گلاسوں کا دور چلایا جائے گا، ان کے جی جس چیز کی خواہش کریں اور جس سے ان کی آنکھیں لذت پائیں، سب وہاں ہوگا اور تم اس میں ہمیشہ رہو گے'),
(4397, 43, 72, 'یہی وه بہشت ہے کہ تم اپنے اعمال کے بدلے اس کے وارث بنائے گئے ہو'),
(4398, 43, 73, 'یہاں تمہارے لیے بکثرت میوے ہیں جنہیں تم کھاتے رہو گے'),
(4399, 43, 74, 'بیشک گنہگار لوگ عذاب دوزخ میں ہمیشہ رہیں گے'),
(4400, 43, 75, 'یہ عذاب کبھی بھی ان سے ہلکا نہ کیا جائے گا اور وه اسی میں مایوس پڑے رہیں گے'),
(4401, 43, 76, 'اور ہم نے ان پر ﻇلم نہیں کیا بلکہ یہ خود ہی ﻇالم تھے'),
(4402, 43, 77, 'اور پکار پکار کر کہیں گے کہ اے مالک! تیرا رب ہمارا کام ہی تمام کردے، وه کہے گا کہ تمہیں تو (ہمیشہ) رہنا ہے'),
(4403, 43, 78, 'ہم تو تمہارے پاس حق لے آئے لیکن تم میں سے اکثر لوگ حق سے نفرت رکھنے والے تھے؟'),
(4404, 43, 79, 'کیا انہوں نے کسی کام کا پختہ اراده کرلیا ہے تو یقین مانو کہ ہم بھی پختہ کام کرنے والے ہیں'),
(4405, 43, 80, 'کیا ان کا یہ خیال ہے کہ ہم ان کی پوشیده باتوں کو اور ان کی سرگوشیوں کو نہیں سنتے، (یقیناً ہم برابر سن رہے ہیں) بلکہ ہمارے بھیجے ہوئے ان کے پاس ہی لکھ رہے ہیں'),
(4406, 43, 81, 'آپ کہہ دیجئے! کہ اگر بالفرض رحمٰن کی اوﻻد ہو تو میں سب سے پہلے عبادت کرنے واﻻ ہوتا'),
(4407, 43, 82, 'آسمانوں اور زمین اور عرش کا رب جو کچھ یہ بیان کرتے ہیں اس سے (بہت) پاک ہے'),
(4408, 43, 83, 'اب آپ انہیں اسی بحﺚ مباحثہ اور کھیل کود میں چھوڑ دیجئے، یہاں تک کہ انہیں اس دن سے سابقہ پڑجائے جن کا یہ وعده دیے جاتے ہیں'),
(4409, 43, 84, 'وہی آسمانوں میں معبود ہے اور زمین میں بھی وہی قابل عبادت ہے اور وه بڑی حکمت واﻻ اور پورے علم واﻻ ہے'),
(4410, 43, 85, 'اور وه بہت برکتوں واﻻ ہے جس کے پاس آسمان وزمین اور ان کے درمیان کی بادشاہت ہے، اور قیامت کا علم بھی اسی کے پاس ہے اور اسی کی جانب تم سب لوٹائے جاؤ گے'),
(4411, 43, 86, 'جنہیں یہ لوگ اللہ کے سوا پکارتے ہیں وه شفاعت کرنے کا اختیار نہیں رکھتے، ہاں (مستحق شفاعت وه ہیں) جو حق بات کا اقرار کریں اور انہیں علم بھی ہو'),
(4412, 43, 87, 'اگر آپ ان سے دریافت کریں کہ انہیں کس نے پیدا کیا ہے؟ تو یقیناً یہ جواب دیں گے کہ اللہ نے، پھر یہ کہاں الٹے جاتے ہیں؟'),
(4413, 43, 88, 'اور ان کا (پیغمبر کا اکثر) یہ کہنا کہ اے میرے رب! یقیناً یہ وه لوگ ہیں جو ایمان نہیں ﻻتے'),
(4414, 43, 89, 'پس آپ ان سے منھ پھیر لیں اور کہہ دیں۔ (اچھا بھائی) سلام! انہیں عنقریب (خود ہی) معلوم ہوجائے گا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4415, 44, 1, 'حٰم'),
(4416, 44, 2, 'قسم ہے اس وضاحت والی کتاب کی'),
(4417, 44, 3, 'یقیناً ہم نے اسے بابرکت رات میں اتارا ہے بیشک ہم ڈرانے والے ہیں'),
(4418, 44, 4, 'اسی رات میں ہر ایک مضبوط کام کا فیصلہ کیا جاتا ہے'),
(4419, 44, 5, 'ہمارے پاس سے حکم ہوکر، ہم ہی ہیں رسول بناکر بھیجنے والے'),
(4420, 44, 6, 'آپ کے رب کی مہربانی سے۔ وه ہی ہے سننے واﻻ جاننے واﻻ'),
(4421, 44, 7, 'جو رب ہے آسمانوں کا اور زمین کا اور جو کچھ ان کے درمیان ہے۔ اگر تم یقین کرنے والے ہو'),
(4422, 44, 8, 'کوئی معبود نہیں اس کے سوا وہی جلاتا ہے اور مارتا ہے، وہی تمہارا رب ہے اور تمہارے اگلے باپ دادوں کا'),
(4423, 44, 9, 'بلکہ وه شک میں پڑے کھیل رہے ہیں'),
(4424, 44, 10, 'آپ اس دن کے منتظر رہیں جب کہ آسمان ﻇاہر دھواں ﻻئے گا'),
(4425, 44, 11, 'جو لوگوں کو گھیر لے گا، یہ دردناک عذاب ہے'),
(4426, 44, 12, 'کہیں گے کہ اے ہمارے رب! یہ آفت ہم سے دور کر ہم ایمان قبول کرتے ہیں'),
(4427, 44, 13, 'ان کے لیے نصیحت کہاں ہے؟ کھول کھول کر بیان کرنے والے پیغمبران کے پاس آچکے'),
(4428, 44, 14, 'پھر بھی انہوں نے ان سے منھ پھیرا اور کہہ دیا کہ سکھایا پڑھایا ہوا باؤﻻ ہے'),
(4429, 44, 15, 'ہم عذاب کو تھوڑا دور کردیں گے تو تم پھر اپنی اسی حالت پر آجاؤ گے'),
(4430, 44, 16, 'جس دن ہم بڑی سخت پکڑ پکڑیں گے، بالیقین ہم بدلہ لینے والے ہیں'),
(4431, 44, 17, 'یقیناً ان سے پہلے ہم قوم فرعون کو (بھی) آزما چکے ہیں جن کے پاس (اللہ) کا باعزت رسول آیا'),
(4432, 44, 18, 'کہ اللہ تعالیٰ کے بندوں کو میرے حوالے کر دو، یقین مانو کہ میں تمہارے لیے امانت دار رسول ہوں'),
(4433, 44, 19, 'اور تم اللہ تعالیٰ کے سامنے سرکشی نہ کرو، میں تمہارے پاس کھلی دلیل ﻻنے واﻻ ہوں'),
(4434, 44, 20, 'اور میں اپنے اور تمہارے رب کی پناه میں آتا ہوں اس سے کہ تم مجھے سنگسار کردو'),
(4435, 44, 21, 'اور اگر تم مجھ پر ایمان نہیں ﻻتے تو مجھ سے الگ ہی رہو'),
(4436, 44, 22, 'پھر انہوں نے اپنے رب سے دعا کی کہ یہ سب گنہگار لوگ ہیں'),
(4437, 44, 23, '(ہم نے کہہ دیا) کہ راتوں رات تو میرے بندوں کو لے کر نکل، یقیناً تمہارا پیچھا کیا جائے گا'),
(4438, 44, 24, 'تو دریا کو ساکن چھوڑ کر چلا جا، بلاشبہ یہ لشکر غرق کردیا جائے گا'),
(4439, 44, 25, 'وه بہت سے باغات اور چشمے چھوڑ گئے'),
(4440, 44, 26, 'اور کھیتیاں اور راحت بخش ٹھکانے'),
(4441, 44, 27, 'اور وه آرام کی چیزیں جن میں عیش کررہے تھے'),
(4442, 44, 28, 'اسی طرح ہوگیا اور ہم نے ان سب کا وارث دوسری قوم کو بنادیا'),
(4443, 44, 29, 'سو ان پر نہ تو آسمان وزمین روئے اور نہ انہیں مہلت ملی'),
(4444, 44, 30, 'اور بےشک ہم نے (ہی) بنی اسرائیل کو (سخت) رسوا کن سزا سے نجات دی'),
(4445, 44, 31, '(جو) فرعون کی طرف سے (ہو رہی) تھی۔ فیالواقع وه سرکش اور حد سے گزر جانے والوں میں سے تھا'),
(4446, 44, 32, 'اور ہم نے دانستہ طور پر بنی اسرائیل کو دنیا جہان والوں پر فوقیت دی'),
(4447, 44, 33, 'اور ہم نے انہیں ایسی نشانیاں دیں جن میں صریح آزمائش تھی'),
(4448, 44, 34, 'یہ لوگ تو یہی کہتے ہیں'),
(4449, 44, 35, 'کہ (آخری چیز) یہی ہماری پہلی بار (دنیا سے) مرجانا ہے اور ہم دوباره اٹھائے نہیں جائیں گے'),
(4450, 44, 36, 'اگر تم سچے ہو تو ہمارے باپ دادوں کو لے آؤ'),
(4451, 44, 37, 'کیا یہ لوگ بہتر ہیں یا تبع کی قوم کے لوگ اور جو ان سے بھی پہلے تھے۔ ہم نے ان سب کو ہلاک کردیا یقیناً وه گنہ گار تھے'),
(4452, 44, 38, 'ہم نے زمین اور آسمانوں اور ان کے درمیان کی چیزوں کو کھیل کے طور پر پیدا نہیں کیا'),
(4453, 44, 39, 'بلکہ ہم نے انہیں درست تدبیر کے ساتھ ہی پیدا کیا ہے، لیکن ان میں سے اکثر لوگ نہیں جانتے'),
(4454, 44, 40, 'یقیناً فیصلے کا دن ان سب کا طے شده وقت ہے'),
(4455, 44, 41, 'اس دن کوئی دوست کسی دوست کے کچھ بھی کام نہ آئے گا اور نہ ان کی امداد کی جائے گی'),
(4456, 44, 42, 'مگر جس پر اللہ کی مہربانی ہوجائے وه زبردست اور رحم کرنے واﻻ ہے'),
(4457, 44, 43, 'بیشک زقوم (تھوہر) کا درخت'),
(4458, 44, 44, 'گناه گار کا کھانا ہے'),
(4459, 44, 45, 'جو مثل تلچھٹ کے ہے اور پیٹ میں کھولتا رہتا ہے'),
(4460, 44, 46, 'مثل تیز گرم پانی کے'),
(4461, 44, 47, 'اسے پکڑ لو پھر گھسیٹتے ہوئے بیچ جہنم تک پہنچاؤ'),
(4462, 44, 48, 'پھر اس کے سر پر سخت گرم پانی کا عذاب بہاؤ'),
(4463, 44, 49, '(اس سے کہا جائے گا) چکھتا جا تو تو بڑا ذی عزت اور بڑے اکرام واﻻ تھا'),
(4464, 44, 50, 'یہی وه چیز ہے جس میں تم شک کیا کرتے تھے'),
(4465, 44, 51, 'بیشک (اللہ سے) ڈرنے والے امن چین کی جگہ میں ہوں گے'),
(4466, 44, 52, 'باغوں اور چشموں میں'),
(4467, 44, 53, 'باریک اور دبیز ریشم کے لباس پہنے ہوئے آمنے سامنے بیٹھے ہوں گے'),
(4468, 44, 54, 'یہ اسی طرح ہے اور ہم بڑی بڑی آنکھوں والی حوروں سے ان کا نکاح کردیں گے'),
(4469, 44, 55, 'دل جمعی کے ساتھ وہاں ہر طرح کے میوؤں کی فرمائشیں کرتے ہوں گے'),
(4470, 44, 56, 'وہاں وه موت چکھنے کے نہیں ہاں پہلی موت (جو وه مر چکے)، انہیں اللہ تعالیٰ نے دوزخ کی سزا سے بچا دیا'),
(4471, 44, 57, 'یہ صرف تیرے رب کا فضل ہے، یہی ہے بڑی کامیابی'),
(4472, 44, 58, 'ہم نے اس (قرآن) کو تیری زبان میں آسان کردیا تاکہ وه نصیحت حاصل کریں'),
(4473, 44, 59, 'اب تو منتظر ره یہ بھی منتظر ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4474, 45, 1, 'حٰم'),
(4475, 45, 2, 'یہ کتاب اللہ غالب حکمت والے کی طرف سے نازل کی ہوئی ہے'),
(4476, 45, 3, 'آسمانوں اور زمین میں ایمان داروں کے لیے یقیناً بہت سی نشانیاں ہیں'),
(4477, 45, 4, 'اور خود تمہاری پیدائش میں اور ان جانوروں کی پیدائش میں جنہیں وه پھیلاتا ہے یقین رکھنے والی قوم کے لیے بہت سی نشانیاں ہیں'),
(4478, 45, 5, 'اور رات دن کے بدلنے میں اور جو کچھ روزی اللہ تعالیٰ آسمان سے نازل فرما کر زمین کو اس کی موت کے بعد زنده کر دیتا ہے، (اس میں) اور ہواؤں کے بدلنے میں بھی ان لوگوں کے لیے جو عقل رکھتے ہیں نشانیاں ہیں'),
(4479, 45, 6, 'یہ ہیں اللہ کی آیتیں جنہیں ہم آپ کو راستی سے سنا رہے ہیں، پس اللہ تعالیٰ اور اس کی آیتوں کے بعد یہ کس بات پر ایمان ﻻئیں گے'),
(4480, 45, 7, 'ویل اور افسوس ہے ہر ایک جھوٹے گنہگار پر'),
(4481, 45, 8, 'جو آیتیں اللہ کی اپنے سامنے پڑھی جاتی ہوئی سنے پھر بھی غرور کرتا ہوا اس طرح اڑا رہے کہ گویا سنی ہی نہیں، تو ایسے لوگوں کو دردناک عذاب کی خبر (پہنچا) دیجئے'),
(4482, 45, 9, 'وه جب ہماری آیتوں میں سے کسی آیت کی خبر پالیتا ہے تو اس کی ہنسی اڑاتا ہے، یہی لوگ ہیں جن کے لیے رسوائی کی مار ہے'),
(4483, 45, 10, 'ان کے پیچھے دوزخ ہے، جو کچھ انہوں نے حاصل کیا تھا وه انہیں کچھ بھی نفع نہ دے گا اور نہ وه (کچھ کام آئیں گے) جن کو انہوں نے اللہ کے سوا کارساز بنا رکھا تھا، ان کے لیے تو بہت بڑا عذاب ہے'),
(4484, 45, 11, 'یہ (سر تاپا) ہدایت ہے اور جن لوگوں نے اپنے رب کی آیتوں کو نہ مانا ان کے لیے بہت سخت دردناک عذاب ہے'),
(4485, 45, 12, 'اللہ ہی ہے جس نے تمہارے لیے دریا کو تابع بنادیا تاکہ اس کے حکم سے اس میں کشتیاں چلیں اور تم اس کا فضل تلاش کرو اور تاکہ تم شکر بجاﻻؤ'),
(4486, 45, 13, 'اور آسمان وزمین کی ہر ہر چیز کو بھی اس نے اپنی طرف سے تمہارے لیے تابع کر دیا ہے۔ جو غور کریں یقیناً وه اس میں بہت سی نشانیاں پالیں گے'),
(4487, 45, 14, 'آپ ایمان والوں سے کہہ دیں کہ وه ان لوگوں سے درگزر کریں جو اللہ کے دنوں کی توقع نہیں رکھتے، تاکہ اللہ تعالیٰ ایک قوم کو ان کے کرتوتوں کا بدلہ دے'),
(4488, 45, 15, 'جو نیکی کرے گا وه اپنے ذاتی بھلے کے لیے اور جو برائی کرے گا اس کا وبال اسی پر ہے، پھر تم سب اپنے پروردگار کی طرف لوٹائے جاؤ گے'),
(4489, 45, 16, 'یقیناً ہم نے بنی اسرائیل کو کتاب، حکومت اور نبوت دی تھی، اور ہم نے انہیں پاکیزه (اور نفیس) روزیاں دی تھیں اور انہیں دنیا والوں پر فضیلت دی تھی'),
(4490, 45, 17, 'اور ہم نے انہیں دین کی صاف صاف دلیلیں دیں، پھر انہوں نے اپنے پاس علم کے پہنچ جانے کے بعد آپس کی ضد بحﺚ سے ہی اختلاف برپا کر ڈاﻻ، یہ جن جن چیزوں میں اختلاف کر رہے ہیں ان کا فیصلہ قیامت والے دن ان کے درمیان (خود) تیرا رب کرے گا'),
(4491, 45, 18, 'پھر ہم نے آپ کو دین کی (ﻇاہر) راه پر قائم کردیا، سو آپ اسی پر لگے رہیں اور نادانوں کی خواہشوں کی پیروی میں نہ پڑیں'),
(4492, 45, 19, '(یاد رکھیں) کہ یہ لوگ ہرگز اللہ کے سامنے آپ کے کچھ کام نہیں آسکتے۔ (سمجھ لیں کہ) ﻇالم لوگ آپس میں ایک دوسرے کے رفیق ہوتے ہیں اور پرہیزگاروں کا کارساز اللہ تعالیٰ ہے'),
(4493, 45, 20, 'یہ (قرآن) لوگوں کے لیے بصیرت کی باتیں اور ہدایت ورحمت ہے اس قوم کے لیے جو یقین رکھتی ہے'),
(4494, 45, 21, 'کیا ان لوگوں کا جو برے کام کرتے ہیں یہ گمان ہے کہ ہم انہیں ان لوگوں جیسا کردیں گے جو ایمان ﻻئے اور نیک کام کیے کہ ان کا مرنا جینا یکساں ہوجائے، برا ہے وه فیصلہ جو وه کررہے ہیں'),
(4495, 45, 22, 'اور آسمانوں اور زمین کو اللہ نے بہت ہی عدل کے ساتھ پیدا کیا ہے اور تاکہ ہر شخص کو اس کے کیے ہوئے کام کا پورا بدلہ دیا جائے اور ان پر ﻇلم نہ کیا جائے گا'),
(4496, 45, 23, 'کیا آپ نے اسے بھی دیکھا؟ جس نے اپنی خواہش نفس کو اپنا معبود بنا رکھا ہے اور باوجود سمجھ بوجھ کے اللہ نے اسے گمراه کردیا ہے اور اس کے کان اور دل پر مہر لگادی ہے اور اس کی آنکھ پر بھی پرده ڈال دیا ہے، اب ایسے شخص کو اللہ کے بعد کون ہدایت دے سکتا ہے'),
(4497, 45, 24, 'کیا اب بھی تم نصیحت نہیں پکڑتے۔ انہوں نے کہا کہ ہماری زندگی تو صرف دنیا کی زندگی ہی ہے۔ ہم مرتے ہیں اور جیتے ہیں اور ہمیں صرف زمانہ ہی مار ڈالتا ہے، (دراصل) انہیں اس کا کچھ علم ہی نہیں۔ یہ تو صرف (قیاس اور) اٹکل سے ہی کام لے رہے ہیں'),
(4498, 45, 25, 'اور جب ان کے سامنے ہماری واضح اور روشن آیتوں کی تلاوت کی جاتی ہے، تو ان کے پاس اس قول کے سوا کوئی دلیل نہیں ہوتی کہ اگر تم سچے ہو تو ہمارے باپ دادوں کو ﻻؤ'),
(4499, 45, 26, 'آپ کہہ دیجئے! اللہ ہی تمہیں زنده کرتا ہے پھر تمہیں مار ڈالتا ہے پھر تمہیں قیامت کے دن جمع کرے گا جس میں کوئی شک نہیں لیکن اکثر لوگ نہیں سمجھتے'),
(4500, 45, 27, 'اور آسمانوں اور زمین کی بادشاہی اللہ ہی کی ہے اور جس دن قیامت قائم ہوگی اس دن اہل باطل بڑے نقصان میں پڑیں گے'),
(4501, 45, 28, 'اور آپ دیکھیں گے کہ ہر امت گھٹنوں کے بل گری ہوئی ہوگی۔ ہر گروه اپنے نامہٴ اعمال کی طرف بلایا جائے گا، آج تمہیں اپنے کیے کا بدلہ دیا جائے گا'),
(4502, 45, 29, 'یہ ہے ہماری کتاب جو تمہارے بارے میں سچ سچ بول رہی ہے، ہم تمہارے اعمال لکھواتے جاتے تھے'),
(4503, 45, 30, 'پس لیکن جو لوگ ایمان ﻻئے اور انہوں نے نیک کام کیے تو ان کو ان کا رب اپنی رحمت تلے لے لے گا، یہی صریح کامیابی ہے'),
(4504, 45, 31, 'لیکن جن لوگوں نے کفر کیا تو (میں ان سے کہوں گا) کیا میری آیتیں تمہیں سنائی نہیں جاتی تھیں؟ پھر بھی تم تکبر کرتے رہے اور تم تھے ہی گنہ گار لوگ'),
(4505, 45, 32, 'اور جب کبھی کہا جاتا کہ اللہ کا وعده یقیناً سچا ہے اور قیامت کے آنے میں کوئی شک نہیں تو تم جواب دیتے تھے کہ ہم نہیں جانتے قیامت کیا چیز ہے؟ ہمیں کچھ یوں ہی سا خیال ہوجاتا ہے لیکن ہمیں یقین نہیں'),
(4506, 45, 33, 'اور ان پر اپنے اعمال کی برائیاں کھل گئیں اور جس کا وه مذاق اڑا رہے تھے اس نے انہیں گھیر لیا'),
(4507, 45, 34, 'اور کہہ دیا گیا کہ آج ہم تمہیں بھلا دیں گے جیسے کہ تم نے اپنے اس دن سے ملنے کو بھلا دیا تھا تمہارا ٹھکانا جہنم ہے اور تمہارا مددگار کوئی نہیں'),
(4508, 45, 35, 'یہ اس لیے ہے کہ تم نے اللہ تعالیٰ کی آیتوں کی ہنسی اڑائی تھی اور دنیا کی زندگی نے تمہیں دھوکے میں ڈال رکھا تھا، پس آج کے دن نہ تو یہ (دوزخ) سے نکالے جائیں گے اور نہ ان سے عذر و معذرت قبول کیا جائے گا'),
(4509, 45, 36, 'پس اللہ کی تعریف ہے جو آسمانوں اور زمین اور تمام جہان کا پالنہار ہے'),
(4510, 45, 37, 'تمام (بزرگی اور) بڑائی آسمانوں اور زمین میں اسی کی ہے اور وہی غالب اور حکمت واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4511, 46, 1, 'حٰم'),
(4512, 46, 2, 'اس کتاب کا اتارنا اللہ تعالیٰ غالب حکمت والے کی طرف سے ہے'),
(4513, 46, 3, 'ہم نے آسمانوں اور زمین اور ان دونوں کے درمیان کی تمام چیزوں کو بہترین تدبیر کے ساتھ ہی ایک مدت معین کے لئے پیدا کیا ہے، اور کافر لوگ جس چیز سے ڈرائے جاتے ہیں منھ موڑ لیتے ہیں'),
(4514, 46, 4, 'آپ کہہ دیجئے! بھلا دیکھو تو جنہیں تم اللہ کے سوا پکارتے ہو مجھے بھی تو دکھاؤ کہ انہوں نے زمین کا کون سا ٹکڑا بنایا ہے یا آسمانوں میں ان کا کون سا حصہ ہے؟ اگر تم سچے ہو تو اس سے پہلے ہی کی کوئی کتاب یا کوئی علم ہی جو نقل کیا جاتا ہو، میرے پاس ﻻؤ'),
(4515, 46, 5, 'اور اس سے بڑھ کر گمراه اور کون ہوگا؟ جو اللہ کے سوا ایسوں کو پکارتا ہے جو قیامت تک اس کی دعا قبول نہ کر سکیں بلکہ ان کے پکارنے سے محض بےخبر ہوں'),
(4516, 46, 6, 'اور جب لوگوں کو جمع کیا جائے گا تو یہ ان کے دشمن ہو جائیں گے اور ان کی پرستش سے صاف انکار کر جائیں گے'),
(4517, 46, 7, 'اور انہیں جب ہماری واضح آیتیں پڑھ کر سنائی جاتی ہیں تو منکر لوگ سچی بات کو جب کہ ان کے پاس آچکی، کہہ دیتے ہیں کہ یہ تو صریح جادو ہے'),
(4518, 46, 8, 'کیا وه کہتے ہیں کہ اسے تو اس نے خود گھڑ لیا ہے آپ کہہ دیجئے! کہ اگر میں ہی اسے بنا ﻻیا ہوں تو تم میرے لئے اللہ کی طرف سے کسی چیز کا اختیار نہیں رکھتے، تم اس (قرآن) کے بارے میں جو کچھ کہہ سن رہے ہو اسے اللہ خوب جانتا ہے، میرے اور تمہارے درمیان گواہی کے لئے وہی کافی ہے، اور وه بخشنے واﻻ مہربان ہے'),
(4519, 46, 9, 'آپ کہہ دیجئے! کہ میں کوئی بالکل انوکھا پیغمبر تو نہیں نہ مجھے یہ معلوم ہے کہ میرے ساتھ اور تمہارے ساتھ کیا کیا جائے گا۔ میں تو صرف اسی کی پیروی کرتا ہوں جو میری طرف وحی بھیجی جاتی ہے اور میں تو صرف علیاﻻعلان آگاه کر دینے واﻻ ہوں'),
(4520, 46, 10, 'آپ کہہ دیجئے! اگر یہ (قرآن) اللہ ہی کی طرف سے ہو اور تم نے اسے نہ مانا ہو اور بنی اسرائیل کا ایک گواه اس جیسی کی گواہی بھی دے چکا ہو اور وه ایمان بھی ﻻچکا ہو اور تم نے سرکشی کی ہو، تو بیشک اللہ تعالیٰ ﻇالموں کو راه نہیں دکھاتا'),
(4521, 46, 11, 'اور کافروں نے ایمان داروں کی نسبت کہا کہ اگر یہ (دین) بہتر ہوتا تو یہ لوگ اس کی طرف ہم سے سبقت کرنے نہ پاتے، اور چونکہ انہوں نے قرآن سے ہدایت نہیں پائی پس یہ کہہ دیں گے کہ قدیمی جھوٹ ہے'),
(4522, 46, 12, 'اور اس سے پہلے موسیٰ کی کتاب پیشوا اور رحمت تھی۔ اور یہ کتاب ہے تصدیق کرنے والی عربی زبان میں تاکہ ﻇالموں کو ڈرائے اور نیک کاروں کو بشارت ہو'),
(4523, 46, 13, 'بیشک جن لوگوں نے کہا کہ ہمارا رب اللہ ہے پھر اس پر جمے رہے تو ان پر نہ تو کوئی خوف ہوگا اور نہ غمگین ہوں گے'),
(4524, 46, 14, 'یہ تو اہل جنت ہیں جو سدا اسی میں رہیں گے، ان اعمال کے بدلے جو وه کیا کرتے تھے'),
(4525, 46, 15, 'اور ہم نے انسان کو اپنے ماں باپ کے ساتھ حسن سلوک کرنے کا حکم دیا ہے، اس کی ماں نے اسے تکلیف جھیل کر پیٹ میں رکھا اور تکلیف برداشت کرکے اسے جنا۔ اس کے حمل کا اور اس کے دودھ چھڑانے کا زمانہ تیس مہینے کا ہے۔ یہاں تک کہ جب وه پختگی اور چالیس سال کی عمر کو پہنچا تو کہنے لگا اے میرے پروردگار! مجھے توفیق دے کہ میں تیری اس نعمت کا شکر بجا ﻻؤں جو تو نے مجھ پر اور میرے ماں باپ پر انعام کی ہے اور یہ کہ میں ایسے نیک عمل کروں جن سے تو خوش ہو جائے اور تو میری اوﻻد بھی صالح بنا، میں تیری طرف رجوع کرتا ہوں اور میں مسلمانوں میں سے ہوں'),
(4526, 46, 16, 'یہی وه لوگ ہیں جن کے نیک اعمال تو ہم قبول فرما لیتے ہیں اور جن کے بداعمال سے درگزر کر لیتے ہیں، (یہ) جنتی لوگوں میں ہیں۔ اس سچے وعدے کے مطابق جو ان سے کیا جاتا تھا'),
(4527, 46, 17, 'اور جس نے اپنے ماں باپ سے کہا کہ تم سے میں تنگ آگیا، تم مجھ سے یہی کہتے رہو گے کہ میں مرنے کے بعد پھر زنده کیا جاؤں گا مجھ سے پہلے بھی امتیں گزر چکی ہیں، وه دونوں جناب باری میں فریادیں کرتے ہیں اور کہتے ہیں تجھے خرابی ہو تو ایمان لے آ، بیشک اللہ کا وعده حق ہے، وه جواب دیتا ہے کہ یہ تو صرف اگلوں کے افسانے ہیں'),
(4528, 46, 18, 'وه لوگ ہیں جن پر (اللہ کے عذاب کا) وعده صادق آگیا، ان جنات اور انسانوں کے گروہوں کے ساتھ جو ان سے پہلے گزر چکے ہیں، یقیناً یہ نقصان پانے والے تھے'),
(4529, 46, 19, 'اور ہر ایک کو اپنے اپنے اعمال کے مطابق درجے ملیں گے تاکہ انہیں ان کے اعمال کے پورے بدلے دے اور ان پر ﻇلم نہ کیا جائے گا'),
(4530, 46, 20, 'اور جس دن کافر جہنم کے سرے پر ﻻئے جائیں گے (کہا جائے گا) تم نے اپنی نیکیاں دنیا کی زندگی میں ہی برباد کر دیں اور ان سے فائدے اٹھا چکے، پس آج تمہیں ذلت کے عذاب کی سزا دی جائے گی، اس باعﺚ کہ تم زمین میں ناحق تکبر کیا کرتے تھے اور اس باعﺚ بھی کہ تم حکم عدولی کیا کرتے تھے'),
(4531, 46, 21, 'اور عاد کے بھائی کو یاد کرو، جبکہ اس نے اپنی قوم کو احقاف میں ڈرایا اور یقیناً اس سے پہلے بھی ڈرانے والے گزر چکے ہیں اور اس کے بعد بھی یہ کہ تم سوائے اللہ تعالیٰ کے اور کسی کی عبادت نہ کرو، بیشک میں تم پر بڑے دن کے عذاب سے خوف کھاتا ہوں'),
(4532, 46, 22, 'قوم نے جواب دیا، کیا آپ ہمارے پاس اس لئے آئے ہیں کہ ہمیں اپنے معبودوں (کی پرستش) سے باز رکھیں؟ پس اگر آپ سچے ہیں تو جس عذاب کا آپ وعده کرتے ہیں اسے ہم پر ﻻ ڈالیں'),
(4533, 46, 23, '(حضرت ہود نے) کہا (اس کا) علم تو اللہ ہی کے پاس ہے، میں تو جو پیغام دے کر بھیجا گیا تھا وه تمہیں پہنچا رہا ہوں لیکن میں دیکھتا ہوں کہ تم لوگ نادانی کر رہے ہو'),
(4534, 46, 24, 'پھر جب انہوں نے عذاب کو بصورت بادل دیکھا اپنی وادیوں کی طرف آتے ہوئے تو کہنے لگے، یہ ابر ہم پر برسنے واﻻ ہے، (نہیں) بلکہ دراصل یہ ابر وه (عذاب) ہے جس کی تم جلدی کر رہے تھے، ہوا ہے جس میں دردناک عذاب ہے'),
(4535, 46, 25, 'جو اپنے رب کے حکم سے ہر چیز کو ہلاک کر دے گی، پس وه ایسے ہوگئے کہ بجر ان کے مکانات کے اور کچھ دکھائی نہ دیتا تھا۔ گنہگاروں کے گروه کو ہم یوں ہی سزا دیتے ہیں'),
(4536, 46, 26, 'اور بالیقین ہم نے (قوم عاد) کو وه مقدور دیئے تھے جو تمہیں تو دیئے بھی نہیں اور ہم نے انہیں کان آنکھیں اور دل بھی دے رکھے تھے۔ لیکن ان کے کانوں اور آنکھوں اور دلوں نے انہیں کچھ بھی نفع نہ پہنچایا جبکہ وه اللہ تعالیٰ کی آیتوں کا انکار کرنے لگے اور جس چیز کا وه مذاق اڑایا کرتے تھے وہی ان پر الٹ پڑی'),
(4537, 46, 27, 'اور یقیناً ہم نے تمہارے آس پاس کی بستیاں تباه کر دیں اور طرح طرح کی ہم نے اپنی نشانیاں بیان کر دیں تاکہ وه رجوع کر لیں'),
(4538, 46, 28, 'پس قرب الٰہی حاصل کرنے کے لئے انہوں نے اللہ کے سوا جن جن کو اپنا معبود بنا رکھا تھا انہوں نے ان کی مدد کیوں نہ کی؟ بلکہ وه تو ان سے کھو گئے، (بلکہ دراصل) یہ ان کا محض جھوٹ اور (بالکل) بہتان تھا'),
(4539, 46, 29, 'اور یاد کرو! جبکہ ہم نے جنوں کی ایک جماعت کو تیری طرف متوجہ کیا کہ وه قرآن سنیں، پس جب (نبی کے) پاس پہنچ گئے تو (ایک دوسرے سے) کہنے لگے خاموش ہو جاؤ، پھر جب پڑھ کر ختم ہوگیا تو اپنی قوم کو خبردار کرنے کے لئے واپس لوٹ گئے'),
(4540, 46, 30, 'کہنے لگے اے ہماری قوم! ہم نے یقیناً وه کتاب سنی ہے جو موسیٰ (علیہ السلام) کے بعد نازل کی گئی ہے جو اپنے سے پہلی کتابوں کی تصدیق کرنے والی ہے جو سچے دین کی اور راه راست کی طرف رہنمائی کرتی ہے'),
(4541, 46, 31, 'اے ہماری قوم! اللہ کے بلانے والے کا کہا مانو، اس پر ایمان ﻻؤ، تو اللہ تمہارے گناه بخش دے گا اور تمہیں المناک عذاب سے پناه دےگا'),
(4542, 46, 32, 'اور جو شخص اللہ کے بلانے والے کا کہا نہ مانے گا پس وه زمین میں کہیں (بھاگ کر اللہ کو) عاجز نہیں کر سکتا، نہ اللہ کے سوا اور کوئی اس کے مدد گار ہوں گے، یہ لوگ کھلی گمراہی میں ہیں'),
(4543, 46, 33, 'کیا وه نہیں دیکھتے کہ جس اللہ نے آسمانوں اور زمین کو پیدا کیا اور ان کے پیدا کرنے سے وه نہ تھکا، وه یقیناً مُردوں کو زنده کرنے پر قادر ہے؟ کیوں نہ ہو؟ وه یقیناً ہر چیز پر قادر ہے'),
(4544, 46, 34, 'وه لوگ جنہوں نے کفر کیا جس دن جہنم کے سامنے ﻻئے جائیں گے (اور ان سے کہا جائے گا کہ) کیا یہ حق نہیں ہے؟ تو جواب دیں گے کہ ہاں قسم ہے ہمارے رب کی (حق ہے) (اللہ) فرمائے گا، اب اپنے کفر کے بدلے عذاب کا مزه چکھو'),
(4545, 46, 35, 'پس (اے پیغمبر!) تم ایسا صبر کرو جیسا صبر عالی ہمت رسولوں نے کیا اور ان کے لئے (عذاب طلب کرنے میں) جلدی نہ کرو، یہ جس دن اس عذاب کو دیکھ لیں گے جس کا وعده دیئے جاتے ہیں تو (یہ معلوم ہونے لگے گا کہ) دن کی ایک گھڑی ہی (دنیا میں) ٹھہرے تھے، یہ ہے پیغام پہنچا دینا، پس بدکاروں کے سوا کوئی ہلاک نہ کیا جائے گا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4546, 47, 1, 'جن لوگوں نے کفر کیا اور اللہ کی راه سے روکا اللہ نے ان کے اعمال برباد کر دیئے'),
(4547, 47, 2, 'اور جو لوگ ایمان ﻻئے اور اچھے کام کیے اور اس پر بھی ایمان ﻻئے جو محمد ﴿صلی اللہ علیہ وسلم﴾ پر اتاری گئی ہے اور دراصل ان کے رب کی طرف سے سچا (دین) بھی وہی ہے، اللہ نے ان کے گناه دور کر دیئے اور ان کے حال کی اصلاح کر دی'),
(4548, 47, 3, 'یہ اس لئے کہ کافروں نے باطل کی پیروی کی اور مومنوں نے اس دین حق کی اتباع کی جو ان کے اللہ کی طرف سے ہے، اللہ تعالیٰ لوگوں کو ان کے احوال اسی طرح بتاتا ہے'),
(4549, 47, 4, 'تو جب کافروں سے تمہاری مڈبھیڑ ہوتو گردنوں پر وار مارو۔ جب ان کو اچھی طرح کچل ڈالو تو اب خوب مضبوط قید وبند سے گرفتار کرو، (پھر اختیار ہے) کہ خواه احسان رکھ کر چھوڑ دو یا فدیہ لے کر تاوقتیکہ لڑائی اپنے ہتھیار رکھ دے۔ یہی حکم ہے اور اللہ اگر چاہتا تو (خود) ہی ان سے بدلہ لے لیتا، لیکن (اس کا منشا یہ ہے) کہ تم میں سے ایک کا امتحان دوسرے کے ذریعہ سے لے لے، جو لوگ اللہ کی راه میں شہید کر دیے جاتے ہیں اللہ ان کے اعمال ہرگز ضائع نہ کرے گا'),
(4550, 47, 5, 'انہیں راه دکھائے گا اور ان کے حاﻻت کی اصلاح کر دے گا'),
(4551, 47, 6, 'اور انہیں اس جنت میں لے جائے گا جس سے انہیں شناسا کر دیا ہے'),
(4552, 47, 7, 'اے ایمان والو! اگر تم اللہ (کے دین) کی مدد کرو گے تو وه تمہاری مدد کرے گا اور تمہیں ﺛابت قدم رکھے گا'),
(4553, 47, 8, 'اور جو لوگ کافر ہوئے انہیں ہلاکی ہو اللہ ان کے اعمال غارت کردے گا'),
(4554, 47, 9, 'یہ اس لئے کہ وه اللہ کی نازل کرده چیز سے ناخوش ہوئے، پس اللہ تعالیٰ نے (بھی) ان کے اعمال ضائع کر دیئے'),
(4555, 47, 10, 'کیا ان لوگوں نے زمین میں چل پھر کر اس کا معائنہ نہیں کیا کہ ان سے پہلے کے لوگوں کا نتیجہ کیا ہوا؟ اللہ نے انہیں ہلاک کر دیا اور کافروں کے لئے اسی طرح کی سزائیں ہیں'),
(4556, 47, 11, 'وه اس لئے کہ ایمان والوں کا کارساز خود اللہ تعالیٰ ہے اور اس لئے کہ کافروں کا کوئی کارساز نہیں'),
(4557, 47, 12, 'جو لوگ ایمان ﻻئے اور انہوں نے نیک اعمال کیے انہیں اللہ تعالیٰ ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں جاری ہیں اور جو لوگ کافر ہوئے وه (دنیا ہی کا) فائده اٹھا رہے ہیں اور مثل چوپایوں کے کھا رہے ہیں، ان کا (اصل) ٹھکانہ جہنم ہے'),
(4558, 47, 13, 'ہم نے کتنی بستیوں کو جو طاقت میں تیری اس بستی سے زیاده تھیں جس سے تجھے نکالا ہم نے انہیں ہلاک کر دیا ہے، جن کا مددگار کوئی نہ اٹھا'),
(4559, 47, 14, 'کیا پس وه شخص جو اپنے پروردگار کی طرف سے دلیل پر ہو اس شخص جیسا ہو سکتا ہے؟ جس کے لئے اس کا برا کام مزین کر دیا گیا ہو اور وه اپنی نفسانی خواہشوں کا پیرو ہو؟'),
(4560, 47, 15, 'اس جنت کی صفت جس کا پرہیزگاروں سے وعده کیا گیا ہے، یہ ہے کہ اس میں پانی کی نہریں ہیں جو بدبو کرنے واﻻ نہیں، اور دودھ کی نہریں ہیں جن کا مزه نہیں بدﻻ اور شراب کی نہریں ہیں جن میں پینے والوں کے لئے بڑی لذت ہے اور نہریں ہیں شہد کی جو بہت صاف ہیں اور ان کے لئے وہاں ہر قسم کے میوے ہیں اور ان کے رب کی طرف سے مغفرت ہے، کیا یہ مثل اس کے ہیں جو ہمیشہ آگ میں رہنے واﻻ ہے؟ اور جنہیں گرم کھولتا ہوا پانی پلایا جائے گا جو ان کی آنتوں کو ٹکڑے ٹکڑے کر دے گا'),
(4561, 47, 16, 'اور ان میں بعض (ایسے بھی ہیں کہ) تیری طرف کان لگاتے ہیں۔ یہاں تک کہ جب تیرے پاس سے جاتے ہیں تو اہل علم سے (بوجہ کند ذہنی وﻻپرواہی کے) پوچھتے ہیں کہ اس نے ابھی کیا کہا تھا؟ یہی لوگ ہیں جن کے دلوں پر اللہ نے مہر کر دی ہے اور وه اپنی خواہشوں کی پیروی کرتے ہیں'),
(4562, 47, 17, 'اور جو لوگ ہدایت یافتہ ہیں اللہ نے انہیں ہدایت میں اور بڑھا دیا ہے اور انہیں ان کی پرہیزگاری عطا فرمائی ہے'),
(4563, 47, 18, 'تو کیا یہ قیامت کا انتظار کر رہے ہیں کہ وه ان کے پاس اچانک آجائے یقیناً اس کی علامتیں تو آچکی ہیں، پھر جبکہ ان کے پاس قیامت آجائے انہیں نصیحت کرنا کہاں ہوگا؟'),
(4564, 47, 19, 'سو (اے نبی!) آپ یقین کر لیں کہ اللہ کے سوا کوئی معبود نہیں اور اپنے گناہوں کی بخشش مانگا کریں اور مومن مردوں اور مومن عورتوں کے حق میں بھی، اللہ تم لوگوں کی آمد ورفت کی اور رہنے سہنے کی جگہ کو خوب جانتا ہے'),
(4565, 47, 20, 'اور جو لوگ ایمان ﻻئے وه کہتے ہیں کوئی سورت کیوں نازل نہیں کی گئی؟ پھر جب کوئی صاف مطلب والی سورت نازل کی جاتی ہے اور اس میں قتال کا ذکر کیا جاتا ہے تو آپ دیکھتے ہیں کہ جن لوگوں کے دلوں میں بیماری ہے وه آپ کی طرف اس طرح دیکھتے ہیں جیسے اس شخص کی نظر ہوتی ہے جس پر موت کی بیہوشی طاری ہو، پس بہت بہتر تھا ان کے لئے'),
(4566, 47, 21, 'فرمان کا بجا ﻻنا اور اچھی بات کا کہنا۔ پھر جب کام مقرر ہو جائے، تو اللہ کے ساتھ سچے رہیں تو ان کے لئے بہتری ہے'),
(4567, 47, 22, 'اور تم سے یہ بھی بعید نہیں کہ اگر تم کو حکومت مل جائے تو تم زمین میں فساد برپا کر دو اور رشتے ناتے توڑ ڈالو'),
(4568, 47, 23, 'یہ وہی لوگ ہیں جن پر اللہ کی پھٹکار ہے اور جن کی سماعت اور آنکھوں کی روشنی چھین لی ہے'),
(4569, 47, 24, 'کیا یہ قران میں غور وفکر نہیں کرتے؟ یا ان کے دلوں پر تالے لگ گئے ہیں'),
(4570, 47, 25, 'جو لوگ اپنی پیٹھ کے بل الٹے پھر گئے اس کے بعد ان کے لئے ہدایت واضح ہو چکی یقیناً شیطان نے ان کے لئے (ان کے فعل کو) مزین کر دیا ہے اور انہیں ڈھیل دے رکھی ہے'),
(4571, 47, 26, 'یہ اس لئے کہ انہوں نے ان لوگوں سے جنہوں نے اللہ کی نازل کرده وحی کو برا سمجھا یہ کہا کہ ہم بھی عنقریب بعض کاموں میں تمہارا کہا مانیں گے، اور اللہ ان کی پوشیده باتیں خوب جانتا ہے'),
(4572, 47, 27, 'پس ان کی کیسی (درگت) ہوگی جبکہ فرشتے ان کی روح قبض کرتے ہوئے ان کے چہروں اور ان کی سرینوں پر ماریں گے'),
(4573, 47, 28, 'یہ اس بنا پر کہ یہ وه راه چلے جس سے انہوں نے اللہ کو ناراض کر دیا اور انہوں نے اس کی رضا مندی کو برا جانا، تو اللہ نے ان کے اعمال اکارت کر دیئے'),
(4574, 47, 29, 'کیا ان لوگوں نے جن کے دلوں میں بیماری ہے یہ سمجھ رکھا ہے کہ اللہ ان کے کینوں کو ﻇاہر نہ کرے گا'),
(4575, 47, 30, 'اور اگر ہم چاہتے تو ان سب کو تجھے دکھا دیتے پس تو انہیں ان کے چہرے سے ہی پہچان لیتا، اور یقیناً تو انہیں ان کی بات کے ڈھب سے پہچان لے گا، تمہارے سب کام اللہ کو معلوم ہیں'),
(4576, 47, 31, 'یقیناً ہم تمہارا امتحان کریں گے تاکہ تم میں سے جہاد کرنے والوں اور صبر کرنے والوں کو ﻇاہر کر دیں اور ہم تمہاری حالتوں کی بھی جانچ کر لیں'),
(4577, 47, 32, 'یقیناً جن لوگوں نے کفر کیا اور اللہ کی راه سے لوگوں کو روکا اور رسول کی مخالفت کی اس کے بعد کہ ان کے لئے ہدایت ﻇاہر ہو چکی یہ ہرگز ہرگز اللہ کا کچھ نقصان نہ کر سکیں گے۔ عنقریب ان کے اعمال وه غارت کردے گا'),
(4578, 47, 33, 'اے ایمان والو! اللہ کی اطاعت کرو اور رسول کا کہا مانو اور اپنے اعمال کو غارت نہ کرو'),
(4579, 47, 34, 'جن لوگوں نے کفر کیا اور اللہ کی راه سے اوروں کو روکا پھر کفر کی حالت میں ہی مرگئے (یقین کر لو) کہ اللہ انہیں ہرگز نہ بخشے گا'),
(4580, 47, 35, 'پس تم بودے بن کر صلح کی درخواست پر نہ اتر آؤ جبکہ تم ہی بلند وغالب رہو گے اور اللہ تمہارے ساتھ ہے، ناممکن ہے کہ وه تمہارے اعمال ضائع کر دے'),
(4581, 47, 36, 'واقعی زندگانیٴ دنیا تو صرف کھیل کود ہے اور اگر تم ایمان لے آؤ گے اور تقویٰ اختیار کرو گے تو اللہ تمہیں تمہارے اجر دے گا اور وه تم سے تمہارے مال نہیں مانگتا'),
(4582, 47, 37, 'اگر وه تم سے تمہارا مال مانگے اور زور دے کر مانگے تو تم اس سے بخیلی کرنے لگو گے اور وه تمہارے کینے ﻇاہر کر دے گا'),
(4583, 47, 38, 'خبردار! تم وه لوگ ہو کہ اللہ کی راه میں خرچ کرنے کے لئے بلائے جاتے ہو، تو تم میں سے بعض بخیلی کرنے لگتے ہیں اور جو بخل کرتا ہے وه تو دراصل اپنی جان سے بخیلی کرتا ہے۔ اللہ تعالیٰ غنی ہے اور تم فقیر (اور محتاج) ہو اور اگر تم روگردان ہو جاؤ تو وه تمہارے بدلے تمہارے سوا اور لوگوں کو ﻻئے گا جو پھر تم جیسے نہ ہوں گے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4584, 48, 1, 'بیشک (اے نبی) ہم نے آپ کو ایک کھلم کھلا فتح دی ہے'),
(4585, 48, 2, 'تاکہ جو کچھ تیرے گناه آگے ہوئے اور جو پیچھے سب کو اللہ تعالیٰ معاف فرمائے، اور تجھ پر اپنا احسان پورا کر دے اور تجھے سیدھی راه چلائے'),
(4586, 48, 3, 'اور آپ کو ایک زبردست مدد دے'),
(4587, 48, 4, 'وہی ہے جس نے مسلمانوں کے دلوں میں سکون (اور اطیمنان) ڈال دیا تاکہ اپنے ایمان کے ساتھ ہی ساتھ اور بھی ایمان میں بڑھ جائیں، اور آسمانوں اور زمین کے ﴿کل﴾ لشکر اللہ ہی کے ہیں۔ اور اللہ تعالیٰ دانا باحکمت ہے'),
(4588, 48, 5, 'تاکہ مومن مردوں اور عورتوں کو ان جنتوں میں لے جائے جن کے نیچے نہریں بہ رہی ہیں جہاں وه ہمیشہ رہیں گے اور ان سے ان کے گناه دور کر دے، اور اللہ کے نزدیک یہ بہت بڑی کامیابی ہے'),
(4589, 48, 6, 'اور تاکہ ان منافق مردوں اور منافق عورتوں اور مشرک مردوں اور مشرکہ عورتوں کو عذاب دے جو اللہ تعالیٰ کے بارے میں بدگمانیاں رکھنے والے ہیں، (دراصل) انہیں پر برائی کا پھیرا ہے، اللہ ان پر ناراض ہوا اور انہیں لعنت کی اور ان کے لئے دوزخ تیار کی اور وه (بہت) بری لوٹنے کی جگہ ہے'),
(4590, 48, 7, 'اور اللہ ہی کے لئے آسمانوں اور زمین کے لشکر ہیں اور اللہ غالب اور حکمت واﻻ ہے'),
(4591, 48, 8, 'یقیناً ہم نے تجھے گواہی دینے واﻻ اور خوشخبری سنانے واﻻ اور ڈرانے واﻻ بنا کر بھیجا ہے'),
(4592, 48, 9, 'تاکہ (اے مسلمانو)، تم اللہ اور اس کے رسول پر ایمان ﻻؤ اور اس کی مدد کرو اور اس کا ادب کرو اور اللہ کی پاکی بیان کرو صبح وشام'),
(4593, 48, 10, 'جو لوگ تجھ سے بیعت کرتے ہیں وه یقیناً اللہ سے بیعت کرتے ہیں، ان کے ہاتھوں پر اللہ کا ہاتھ ہے، تو جو شخص عہد شکنی کرے وه اپنے نفس پر ہی عہد شکنی کرتا ہے اور جو شخص اس اقرار کو پورا کرے جو اس نے اللہ کے ساتھ کیا ہے تو اسے عنقریب اللہ بہت بڑا اجر دے گا'),
(4594, 48, 11, 'دیہاتیوں میں سے جو لوگ پیچھے چھوڑ دیئے گئے تھے وه اب تجھ سے کہیں گے کہ ہم اپنے مال اور بال بچوں میں لگے ره گئے پس آپ ہمارے لئے مغفرت طلب کیجئے۔ یہ لوگ اپنی زبانوں سے وه کہتے ہیں جو ان کے دلوں میں نہیں ہے۔ آپ جواب دے دیجئے کہ تمہارے لئے اللہ کی طرف سے کسی چیز کا بھی اختیار کون رکھتا ہے اگر وه تمہیں نقصان پہنچانا چاہے تو یا تمہیں کوئی نفع دینا چاہے تو، بلکہ تم جو کچھ کر رہے ہو اس سے اللہ خوب باخبر ہے'),
(4595, 48, 12, '(نہیں) بلکہ تم نے تو یہ گمان کر رکھا تھا کہ پیغمبر اور مسلمانوں کا اپنے گھروں کی طرف لوٹ آنا قطعاً ناممکن ہے اور یہی خیال تمہارے دلوں میں رچ بس گیا تھا اور تم نے برا گمان کر رکھا تھا۔ دراصل تم لوگ ہو بھی ہلاک ہونے والے'),
(4596, 48, 13, 'اور جو شخص اللہ پر اور اس کے رسول پر ایمان نہ ﻻئے تو ہم نے بھی ایسے کافروں کے لئے دہکتی آگ تیار کر رکھی ہے'),
(4597, 48, 14, 'اور زمین اور آسمانوں کی بادشاہت اللہ ہی کے لئے ہے جسے چاہے بخشے اور جسے چاہے عذاب کرے۔ اور اللہ بڑا بخشنے واﻻ مہربان ہے'),
(4598, 48, 15, 'جب تم غنیمتیں لینے جانے لگو گے تو جھٹ سے یہ پیچھے چھوڑے ہوئے لوگ کہنے لگیں گے کہ ہمیں بھی اپنے ساتھ چلنے کی اجازت دیجئے، وه چاہتے ہیں کہ اللہ تعالیٰ کے کلام کو بدل دیں آپ کہہ دیجئے! کہ اللہ تعالیٰ پہلے ہی فرما چکا ہے کہ تم ہرگز ہمارے ساتھ نہیں چلو گے، وه اس کا جواب دیں گے (نہیں نہیں) بلکہ تم ہم سے حسد کرتے ہو، (اصل بات یہ ہے) کہ وه لوگ بہت ہی کم سمجھتے ہیں'),
(4599, 48, 16, 'آپ پیچھے چھوڑے ہوئے بدویوں سے کہہ دو کہ عنقریب تم ایک سخت جنگجو قوم کی طرف بلائے جاؤ گے کہ تم ان سے لڑوگے یا وه مسلمان ہوجائیں گے پس اگر تم اطاعت کرو گے تو اللہ تمہیں بہت بہتر بدلہ دے گا اور اگر تم نے منھ پھیر لیا جیسا کہ اس سے پہلے تم منھ پھیر چکے ہو تو وه تمہیں دردناک عذاب دے گا'),
(4600, 48, 17, 'اندھے پر کوئی حرج نہیں ہے اور نہ لنگڑے پر کوئی حرج ہے اور نہ بیمار پر کوئی حرج ہے، جو کوئی اللہ اور اس کے رسول کی فرمانبرداری کرے اسے اللہ ایسی جنتوں میں داخل کرے گا جس کے (درختوں) تلے نہریں جاری ہیں اور جو منھ پھیر لے اسے دردناک عذاب (کی سزا) دے گا'),
(4601, 48, 18, 'یقیناً اللہ تعالیٰ مومنوں سے خوش ہوگیا جبکہ وه درخت تلے تجھ سے بیعت کر رہے تھے۔ ان کے دلوں میں جو تھا اسے اس نے معلوم کر لیا اور ان پر اطمینان نازل فرمایا اور انہیں قریب کی فتح عنایت فرمائی'),
(4602, 48, 19, 'اور بہت سی غنیمتیں جنہیں وه حاصل کریں گے اور اللہ غالب حکمت واﻻ ہے'),
(4603, 48, 20, 'اللہ تعالیٰ نے تم سے بہت ساری غنیمتوں کا وعده کیا ہے جنہیں تم حاصل کرو گے پس یہ تو تمہیں جلدی ہی عطا فرما دی اور لوگوں کے ہاتھ تم سے روک دیئے، تاکہ مومنوں کے لئے یہ ایک نشانی ہو جائے اور (تاکہ) وه تمہیں سیدھی راه چلائے'),
(4604, 48, 21, 'اور تمہیں اور (غنیمتیں) بھی دے جن پر اب تک تم نے قابو نہیں پایا۔ اللہ تعالیٰ نے انہیں اپنے قابو میں رکھا ہے اور اللہ تعالیٰ ہر چیز پر قادر ہے'),
(4605, 48, 22, 'اور اگر تم سے کافر جنگ کرتے تو یقیناً پیٹھ دکھا کر بھاگتے پھر نہ تو کوئی کار ساز پاتے نہ مددگار'),
(4606, 48, 23, 'اللہ کے اس قاعدے کے مطابق جو پہلے سے چلا آیا ہے، تو کبھی بھی اللہ کے قاعدے کو بدلتا ہوا نہ پائے گا'),
(4607, 48, 24, 'وہی ہے جس نے خاص مکہ میں کافروں کے ہاتھوں کو تم سے اور تمہارے ہاتھوں کو ان سے روک لیا اس کے بعد کہ اس نے تمہیں ان پر غلبہ دے دیا تھا، اور تم جو کچھ کر رہے ہو اللہ تعالیٰ اسے دیکھ رہا ہے'),
(4608, 48, 25, 'یہی وه لوگ ہیں جنہوں نے کفر کیا اور تم کو مسجد حرام سے روکا اور قربانی کے لئے موقوف جانور کو اس کی قربان گاه میں پہنچنے سے (روکا)، اور اگر ایسے (بہت سے) مسلمان مرد اور (بہت سی) مسلمان عورتیں نہ ہوتیں جن کی تم کو خبر نہ تھی یعنی ان کے پس جانے کا احتمال نہ ہوتا جس پر ان کی وجہ سے تم کو بھی بے خبری میں ضرر پہنچتا، (تو تمہیں لڑنے کی اجازت دے دی جاتی لیکن ایسا نہیں کیا گیا) تاکہ اللہ تعالیٰ اپنی رحمت میں جس کو چاہے داخل کرے اور اگر یہ الگ الگ ہوتے تو ان میں جو کافر تھے ہم ان کو درد ناک سزا دیتے'),
(4609, 48, 26, 'جب کہ ان کافروں نے اپنے دلوں میں حمیت کو جگہ دی اور حمیت بھی جاہلیت کی، سو اللہ تعالیٰ نے اپنے رسول پر اور مومنین پر اپنی طرف سے تسکین نازل فرمائی اور اللہ تعالیٰ نے مسلمانوں کو تقوے کی بات پر جمائے رکھا اور وه اس کے اہل اور زیاده مستحق تھے اور اللہ تعالیٰ ہر چیز کو خوب جانتا ہے'),
(4610, 48, 27, 'یقیناً اللہ تعالیٰ نے اپنے رسول کو خواب سچا دکھایا کہ انشاءاللہ تم یقیناً پورے امن وامان کے ساتھ مسجد حرام میں داخل ہوگے سرمنڈواتے ہوئے اور سر کے بال کترواتے ہوئے (چین کے ساتھ) نڈر ہو کر، وه ان امور کو جانتا ہے جنہیں تم نہیں جانتے، پس اس نے اس سے پہلے ایک نزدیک کی فتح تمہیں میسر کی'),
(4611, 48, 28, 'وہی ہے جس نےاپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجا تاکہ اسے ہر دین پر غالب کرے، اور اللہ تعالیٰ کافی ہے گواہی دینے واﻻ'),
(4612, 48, 29, 'محمد (صلی اللہ علیہ وسلم) اللہ کے رسول ہیں اور جو لوگ ان کے ساتھ کافروں پر سخت ہیں آپس میں رحمدل ہیں، تو انہیں دیکھے گا کہ رکوع اور سجدے کر رہے ہیں اللہ تعالیٰ کے فضل اور رضامندی کی جستجو میں ہیں، ان کا نشان ان کے چہروں پر سجدوں کے اﺛر سے ہے، ان کی یہی مثال تورات میں ہے اور ان کی مثال انجیل میں ہے، مثل اسی کھیتی کے جس نے اپنا انکھوا نکالا پھر اسے مضبوط کیا اور وه موٹا ہوگیا پھر اپنے تنے پر سیدھا کھڑا ہوگیا اور کسانوں کو خوش کرنے لگا تاکہ ان کی وجہ سے کافروں کو چڑائے، ان ایمان والوں اور نیک اعمال والوں سے اللہ نے بخشش کا اور بہت بڑے ﺛواب کا وعده کیا ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4613, 49, 1, 'اے ایمان والے لوگو! اللہ اور اس کے رسول سے آگے نہ بڑھو اور اللہ سے ڈرتے رہا کرو۔ یقیناً اللہ تعالیٰ سننے واﻻ، جاننے واﻻ ہے'),
(4614, 49, 2, 'اے ایمان والو! اپنی آوازیں نبی کی آواز سے اوپر نہ کرو اور نہ ان سے اونچی آواز سے بات کرو جیسے آپس میں ایک دوسرے سے کرتے ہو، کہیں (ایسا نہ ہو کہ) تمہارے اعمال اکارت جائیں اور تمہیں خبر بھی نہ ہو'),
(4615, 49, 3, 'بیشک جو لوگ رسول اللہ (صلی اللہ علیہ وسلم) کے حضور میں اپنی آوازیں پست رکھتے ہیں، یہی وه لوگ ہیں جن کے دلوں کو اللہ نے پرہیزگاری کے لئے جانچ لیا ہے۔ ان کے لئے مغفرت ہے اور بڑا ﺛواب ہے'),
(4616, 49, 4, 'جو لوگ آپ کو حجروں کے پیچھے سے پکارتے ہیں ان میں سے اکثر (بالکل) بے عقل ہیں'),
(4617, 49, 5, 'اگر یہ لوگ یہاں تک صبر کرتے کہ آپ خود سے نکل کر ان کے پاس آجاتے تو یہی ان کے لئے بہتر ہوتا، اور اللہ غفور ورحیم ہے'),
(4618, 49, 6, 'اے مسلمانو! اگر تمہیں کوئی فاسق خبر دے تو تم اس کی اچھی طرح تحقیق کر لیا کرو ایسا نہ ہو کہ نادانی میں کسی قوم کو ایذا پہنچا دو پھر اپنے کیے پر پشیمانی اٹھاؤ'),
(4619, 49, 7, 'اور جان رکھو کہ تم میں اللہ کے رسول موجود ہیں، اگر وه تمہارا کہا کرتے رہے بہت امور میں، تو تم مشکل میں پڑجاؤ لیکن اللہ تعالیٰ نے ایمان کو تمہارے لئے محبوب بنا دیا ہے اور اسے تمہارے دلوں میں زینت دے رکھی ہے اور کفر کو اور گناه کو اور نافرمانی کو تمہارے نگاہوں میں ناپسندیده بنا دیا ہے، یہی لوگ راه یافتہ ہیں'),
(4620, 49, 8, 'اللہ کے احسان وانعام سے اور اللہ دانا اور باحکمت ہے'),
(4621, 49, 9, 'اور اگر مسلمانوں کی دو جماعتیں آپس میں لڑ پڑیں تو ان میں میل ملاپ کرا دیا کرو۔ پھر اگر ان دونوں میں سے ایک جماعت دوسری جماعت پر زیادتی کرے تو تم (سب) اس گروه سے جو زیادتی کرتا ہے لڑو۔ یہاں تک کہ وه اللہ کے حکم کی طرف لوٹ آئے، اگر لوٹ آئے تو پھر انصاف کے ساتھ صلح کرا دو اور عدل کرو بیشک اللہ تعالیٰ انصاف کرنے والوں سے محبت کرتا ہے'),
(4622, 49, 10, '(یاد رکھو) سارے مسلمان بھائی بھائی ہیں پس اپنے دو بھائیوں میں ملاپ کرا دیا کرو، اور اللہ سے ڈرتے رہو تاکہ تم پر رحم کیا جائے'),
(4623, 49, 11, 'اے ایمان والو! مرد دوسرے مردوں کا مذاق نہ اڑائیں ممکن ہے کہ یہ ان سے بہتر ہو اور نہ عورتیں عورتوں کا مذاق اڑائیں ممکن ہے یہ ان سے بہتر ہوں، اور آپس میں ایک دوسرے کو عیب نہ لگاؤ اور نہ کسی کو برے لقب دو۔ ایمان کے بعد فسق برا نام ہے، اور جو توبہ نہ کریں وہی ﻇالم لوگ ہیں'),
(4624, 49, 12, 'اے ایمان والو! بہت بدگمانیوں سے بچو یقین مانو کہ بعض بدگمانیاں گناه ہیں۔ اور بھید نہ ٹٹوﻻ کرو اور نہ تم میں سے کوئی کسی کی غیبت کرے۔ کیا تم میں سے کوئی بھی اپنے مرده بھائی کا گوشت کھانا پسند کرتا ہے؟ تم کو اس سے گھن آئے گی، اور اللہ سے ڈرتے رہو، بیشک اللہ توبہ قبول کرنے واﻻ مہربان ہے'),
(4625, 49, 13, 'اے لوگو! ہم نے تم سب کو ایک (ہی) مرد وعورت سے پیدا کیا ہے اور اس لئے کہ تم آپس میں ایک دوسرے کو پہچانو کنبے اور قبیلے بنا دیئے ہیں، اللہ کے نزدیک تم سب میں باعزت وه ہے جو سب سے زیاده ڈرنے واﻻ ہے۔ یقین مانو کہ اللہ دانا اور باخبر ہے'),
(4626, 49, 14, 'دیہاتی لوگ کہتے ہیں کہ ہم ایمان ﻻئے۔ آپ کہہ دیجئے کہ درحقیقت تم ایمان نہیں ﻻئے لیکن تم یوں کہو کہ ہم اسلام ﻻئے (مخالفت چھوڑ کر مطیع ہوگئے) حاﻻنکہ ابھی تک تمہارے دلوں میں ایمان داخل ہی نہیں ہوا۔ تم اگر اللہ کی اور اس کے رسول کی فرمانبرداری کرنے لگو گے تو اللہ تمہارے اعمال میں سے کچھ بھی کم نہ کرے گا۔ بیشک اللہ بخشنے واﻻ مہربان ہے'),
(4627, 49, 15, 'مومن تو وه ہیں جو اللہ اور اس کے رسول پر (پکا) ایمان ﻻئیں پھر شک وشبہ نہ کریں اور اپنے مالوں سے اور اپنی جانوں سے اللہ کی راه میں جہاد کرتے رہیں، (اپنے دعوائے ایمان میں) یہی سچے اور راست گو ہیں'),
(4628, 49, 16, 'کہہ دیجئے! کہ کیا تم اللہ تعالیٰ کو اپنی دینداری سے آگاه کر رہے ہو، اللہ ہر اس چیز سے جو آسمانوں میں اور زمین میں ہے بخوبی آگاه ہے۔ اور اللہ ہر چیز کا جاننے واﻻ ہے'),
(4629, 49, 17, 'اپنے مسلمان ہونے کا آپ پر احسان جتاتے ہیں۔ آپ کہہ دیجئے کہ اپنے مسلمان ہونے کا احسان مجھ پر نہ رکھو، بلکہ دراصل اللہ کا تم پر احسان ہے کہ اس نے تمہیں ایمان کی ہدایت کی اگر تم راست گو ہو'),
(4630, 49, 18, 'یقین مانو کہ آسمانوں اور زمین کی پوشیده باتیں اللہ خوب جانتا ہے۔ اور جو کچھ تم کر رہے ہو اسے اللہ خوب دیکھ رہا ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4631, 50, 1, 'ق! بہت بڑی شان والے اس قرآن کی قسم ہے'),
(4632, 50, 2, 'بلکہ انہیں تعجب معلوم ہوا کہ ان کے پاس انہی میں سے ایک آگاه کرنے واﻻ آیا تو کافروں نے کہا کہ یہ ایک عجیب چیز ہے'),
(4633, 50, 3, 'کیا جب ہم مر کر مٹی ہو جائیں گے۔ پھر یہ واپسی دور (از عقل) ہے'),
(4634, 50, 4, 'زمین جو کچھ ان سے گھٹاتی ہے وه ہمیں معلوم ہے اور ہمارے پاس سب یاد رکھنے والی کتاب ہے'),
(4635, 50, 5, 'بلکہ انہوں نے سچی بات کو جھوٹ کہا ہے جبکہ وه ان کے پاس پہنچ چکی پس وه ایک الجھاؤ میں پڑ گئے ہیں'),
(4636, 50, 6, 'کیا انہوں نے آسمان کو اپنے اوپر نہیں دیکھا؟ کہ ہم نے اسے کس طرح بنایا ہے اور زینت دی ہے اس میں کوئی شگاف نہیں'),
(4637, 50, 7, 'اور زمین کو ہم نے بچھا دیا اور اس میں ہم نے پہاڑ ڈال دیئے ہیں اور اس میں ہم نے قسم قسم کی خوشنما چیزیں اگا دی ہیں'),
(4638, 50, 8, 'تاکہ ہر رجوع کرنے والے بندے کے لئے بینائی اور دانائی کا ذریعہ ہو'),
(4639, 50, 9, 'اور ہم نے آسمان سے بابرکت پانی برسایا اور اس سے باغات اور کٹنے والے کھیت کے غلے پیدا کیے'),
(4640, 50, 10, 'اور کھجوروں کے بلند وباﻻ درخت جن کے خوشے تہ بہ تہ ہیں'),
(4641, 50, 11, 'بندوں کی روزی کے لئے اور ہم نے پانی سے مرده شہر کو زنده کر دیا۔ اسی طرح (قبروں سے) نکلنا ہے'),
(4642, 50, 12, 'ان سے پہلے نوح کی قوم نے اور رس والوں نے اور ﺛمود نے'),
(4643, 50, 13, 'اور عاد نے فرعون نے اور برادران لوط نے'),
(4644, 50, 14, 'اور ایکہ والوں نے تبع کی قوم نے بھی تکذیب کی تھی۔ سب نے پیغمبروں کو جھٹلایا پس میرا وعدہٴ عذاب ان پر صادق آگیا'),
(4645, 50, 15, 'کیا ہم پہلی بار کے پیدا کرنے سے تھک گئے؟ بلکہ یہ لوگ نئی پیدائش کی طرف سے شک میں ہیں'),
(4646, 50, 16, 'ہم نے انسان کو پیدا کیا ہے اور اس کے دل میں جو خیاﻻت اٹھتے ہیں ان سے ہم واقف ہیں اور ہم اس کی رگ جان سے بھی زیاده اس سے قریب ہیں'),
(4647, 50, 17, 'جس وقت دو لینے والے جا لیتے ہیں ایک دائیں طرف اور ایک بائیں طرف بیٹھا ہوا ہے'),
(4648, 50, 18, '(انسان) منھ سے کوئی لفﻆ نکال نہیں پاتا مگر کہ اس کے پاس نگہبان تیار ہے'),
(4649, 50, 19, 'اور موت کی بے ہوشی حق لے کر آ پہنچی، یہی ہے جس سے تو بدکتا پھرتا تھا'),
(4650, 50, 20, 'اور صور پھونک دیا جائے گا۔ وعدہٴ عذاب کا دن یہی ہے'),
(4651, 50, 21, 'اور ہر شخص اس طرح آئے گا کہ اس کے ساتھ ایک ﻻنے واﻻ ہوگا اور ایک گواہی دینے واﻻ'),
(4652, 50, 22, 'یقیناً تو اس سے غفلت میں تھا لیکن ہم نے تیرے سامنے سے پرده ہٹا دیا پس آج تیری نگاه بہت تیز ہے'),
(4653, 50, 23, 'اس کا ہم نشین (فرشتہ) کہے گا یہ حاضر ہے جو کہ میرے پاس تھا'),
(4654, 50, 24, 'ڈال دو جہنم میں ہر کافر سرکش کو'),
(4655, 50, 25, 'جو نیک کام سے روکنے واﻻ حد سے گزر جانے واﻻ اور شک کرنے واﻻ تھا'),
(4656, 50, 26, 'جس نے اللہ کے ساتھ دوسرا معبود بنا لیا تھا پس اسے سخت عذاب میں ڈال دو'),
(4657, 50, 27, 'اس کا ہمنشین (شیطان) کہے گا اے ہمارے رب! میں نے اسے گمراه نہیں کیا تھا بلکہ یہ خود ہی دور دراز کی گمراہی میں تھا'),
(4658, 50, 28, 'حق تعالیٰ فرمائے گا بس میرے سامنے جھگڑنے کی بات مت کرو میں تو پہلے ہی تمہاری طرف وعید (وعدہٴ عذاب) بھیج چکا تھا'),
(4659, 50, 29, 'میرے ہاں بات بدلتی نہیں اور نہ میں اپنے بندوں پر ذرا بھی ﻇلم کرنے واﻻ ہوں'),
(4660, 50, 30, 'جس دن ہم دوزخ سے پوچھیں گے کیا تو بھر چکی؟ وه جواب دے گی کیا کچھ اور زیاده بھی ہے؟'),
(4661, 50, 31, 'اور جنت پرہیزگاروں کے لئے بالکل قریب کر دی جائے گی ذرا بھی دور نہ ہوگی'),
(4662, 50, 32, 'یہ ہے جس کا تم سے وعده کیا جاتا تھا ہر اس شخص کے لئے جو رجوع کرنے واﻻ اور پابندی کرنے واﻻ ہو'),
(4663, 50, 33, 'جو رحمٰن کا غائبانہ خوف رکھتا ہو اور توجہ واﻻ دل ﻻیا ہو'),
(4664, 50, 34, 'تم اس جنت میں سلامتی کے ساتھ داخل ہو جاؤ۔ یہ ہمیشہ رہنے کا دن ہے'),
(4665, 50, 35, 'یہ وہاں جو چاہیں انھیں ملے گا (بلکہ) ہمارے پاس اور بھی زیاده ہے'),
(4666, 50, 36, 'اور ان سے پہلے بھی ہم بہت سی امتوں کو ہلاک کر چکے ہیں جو ان سے طاقت میں بہت زیاده تھیں وه شہروں میں ڈھونڈھتے ہی ره گئے، کہ کوئی بھاگنے کا ٹھکانہ ہے؟'),
(4667, 50, 37, 'اس میں ہر صاحب دل کے لئے عبرت ہے اور اس کے لئے جو دل سے متوجہ ہو کر کان لگائے اور وه حاضر ہو'),
(4668, 50, 38, 'یقیناً ہم نے آسمانوں اور زمین اور جو کچھ اس کے درمیان ہے سب کو (صرف) چھ دن میں پیدا کر دیا اور ہمیں تکان نے چھوا تک نہیں'),
(4669, 50, 39, 'پس یہ جو کچھ کہتے ہیں آپ اس پر صبر کریں اور اپنے رب کی تسبیح تعریف کے ساتھ بیان کریں سورج نکلنے سے پہلے بھی اور سورج غروب ہونے سے پہلے بھی'),
(4670, 50, 40, 'اور رات کے وقت بھی تسبیح کریں اور نماز کے بعد بھی'),
(4671, 50, 41, 'اور سن رکھیں کہ جس دن ایک پکارنے واﻻ قریب ہی کی جگہ سے پکارے گا'),
(4672, 50, 42, 'جس روز اس تند وتیز چیﺦ کو یقین کے ساتھ سن لیں گے، یہ دن ہوگا نکلنے کا'),
(4673, 50, 43, 'ہم ہی جلاتے ہیں اور ہم ہی مارتے ہیں اور ہماری ہی طرف لوٹ پھر کر آنا ہے'),
(4674, 50, 44, 'جس دن زمین پھٹ جائے گی اور یہ دوڑتے ہوئے (نکل پڑیں گے) یہ جمع کر لینا ہم پر بہت ہی آسان ہے'),
(4675, 50, 45, 'یہ جو کچھ کہہ رہے ہیں ہم بخوبی جانتے ہیں اور آپ ان پر جبر کرنے والے نہیں، تو آپ قرآن کے ذریعے انہیں سمجھاتے رہیں جو میرے وعید (ڈرانے کے وعدوں) سے ڈرتے ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4676, 51, 1, 'قسم ہے بکھیرنے والیوں کی اڑا کر'),
(4677, 51, 2, 'پھر اٹھانے والیاں بوجھ کو'),
(4678, 51, 3, 'پھر چلنے والیاں نرمی سے'),
(4679, 51, 4, 'پھر کام کو تقسیم کرنے والیاں'),
(4680, 51, 5, 'یقین مانو کہ تم سے جو وعدے کیے جاتے ہیں (سب) سچے ہیں'),
(4681, 51, 6, 'اور بیشک انصاف ہونے واﻻ ہے'),
(4682, 51, 7, 'قسم ہے راہوں والے آسمان کی'),
(4683, 51, 8, 'یقیناً تم مختلف بات میں پڑے ہوئے ہو'),
(4684, 51, 9, 'اس سے وہی باز رکھا جاتا ہے جو پھیر دیا گیا ہو'),
(4685, 51, 10, 'بے سند باتیں کرنے والے غارت کر دیئے گئے'),
(4686, 51, 11, 'جو غفلت میں ہیں اور بھولے ہوئے ہیں'),
(4687, 51, 12, 'پوچھتے ہیں کہ یوم جزا کب ہوگا؟'),
(4688, 51, 13, 'ہاں یہ وه دن ہے کہ یہ آگ پر تپائے جائیں گے'),
(4689, 51, 14, 'اپنی فتنہ پردازی کا مزه چکھو، یہی ہے جس کی تم جلدی مچا رہے تھے'),
(4690, 51, 15, 'بیشک تقویٰ والے لوگ بہشتوں اور چشموں میں ہوں گے'),
(4691, 51, 16, 'ان کے رب نے جو کچھ انہیں عطا فرمایا ہے اسے لے رہے ہوں گے وه تو اس سے پہلے ہی نیکوکار تھے'),
(4692, 51, 17, 'وه رات کو بہت کم سویا کرتے تھے'),
(4693, 51, 18, 'اور وقت سحر استغفار کیا کرتے تھے'),
(4694, 51, 19, 'اور ان کے مال میں مانگنے والوں کا اور سوال سے بچنے والوں کا حق تھا'),
(4695, 51, 20, 'اور یقین والوں کے لئے تو زمین میں بہت سی نشانیاں ہیں'),
(4696, 51, 21, 'اور خود تمہاری ذات میں بھی، تو کیا تم دیکھتے نہیں ہو'),
(4697, 51, 22, 'اور تمہاری روزی اور جو تم سے وعده کیا جاتا ہے سب آسمان میں ہے'),
(4698, 51, 23, 'آسمان وزمین کے پروردگار کی قسم! کہ یہ بالکل برحق ہے ایسا ہی جیسے کہ تم باتیں کرتے ہو'),
(4699, 51, 24, 'کیا تجھے ابراہیم (علیہ السلام) کے معزز مہمانوں کی خبر بھی پہنچی ہے؟'),
(4700, 51, 25, 'وه جب ان کے ہاں آئے تو سلام کیا، ابراہیم نے جواب سلام دیا (اور کہا یہ تو) اجنبی لوگ ہیں'),
(4701, 51, 26, 'پھر (چﭗ چاپ جلدی جلدی) اپنے گھر والوں کی طرف گئے اور ایک فربہ بچھڑے (کا گوشت) ﻻئے'),
(4702, 51, 27, 'اور اسے ان کے پاس رکھا اور کہا آپ کھاتے کیوں نہیں'),
(4703, 51, 28, 'پھر تو دل ہی دل میں ان سے خوفزده ہوگئے انہوں نے کہا آپ خوف نہ کیجئے۔ اور انہوں نے اس (حضرت ابراہیم) کو ایک علم والے لڑکے کی بشارت دی'),
(4704, 51, 29, 'پس ان کی بیوی آگے بڑھی اور حیرت میں آکر اپنے منھ پر ہاتھ مار کر کہا کہ میں تو بوڑھیا ہوں اور ساتھ ہی بانجھ'),
(4705, 51, 30, 'انہوں نے کہا ہاں تیرے پروردگار نے اسی طرح فرمایا ہے، بیشک وه حکیم وعلیم ہے'),
(4706, 51, 31, '(حضرت ابرہیم علیہ السلام) نے کہا کہ اللہ کے بھیجے ہوئے (فرشتو!) تمہارا کیا مقصد ہے؟'),
(4707, 51, 32, 'انہوں نے جواب دیا کہ ہم گناه گار قوم کی طرف بھیجے گئے ہیں'),
(4708, 51, 33, 'تاکہ ہم ان پر مٹی کے کنکر برسائیں'),
(4709, 51, 34, 'جو تیرے رب کی طرف سے نشان زده ہیں، ان حد سے گزر جانے والوں کے لیے'),
(4710, 51, 35, 'پس جتنے ایمان والے وہاں تھے ہم نے انہیں نکال لیا'),
(4711, 51, 36, 'اور ہم نے وہاں مسلمانوں کا صرف ایک ہی گھر پایا'),
(4712, 51, 37, 'اور وہاں ہم نے ان کے لیے جو درد ناک عذاب کا ڈر رکھتے ہیں ایک (کامل) علامت چھوڑی'),
(4713, 51, 38, 'موسیٰ (علیہ السلام کے قصے) میں (بھی ہماری طرف سے تنبیہ ہے) کہ ہم نے اسے فرعون کی طرف کھلی دلیل دے کر بھیجا'),
(4714, 51, 39, 'پس اس نےاپنے بل بوتے پر منھ موڑا اور کہنے لگا یہ جادوگر ہے یا دیوانہ ہے'),
(4715, 51, 40, 'بالﺂخر ہم نے اسے اور اس کے لشکروں کو اپنے عذاب میں پکڑ کر دریا میں ڈال دیا وه تھا ہی ملامت کے قابل'),
(4716, 51, 41, 'اسی طرح عادیوں میں بھی (ہماری طرف سے تنبیہ ہے) جب کہ ہم نے ان پر خیر وبرکت سے خالی آندھی بھیجی'),
(4717, 51, 42, 'وه جس جس چیز پر گرتی تھی اسے بوسیده ہدی کی طرح (چورا چورا) کردیتی تھی'),
(4718, 51, 43, 'اور ﺛمود (کے قصے) میں بھی (عبرت) ہے جب ان سے کہا گیا کہ تم کچھ دنوں تک فائده اٹھا لو'),
(4719, 51, 44, 'لیکن انہوں نے اپنے رب کے حکم سے سرتابی کی جس پر انہیں ان کے دیکھتے دیکھتے (تیز وتند) کڑاکے نے ہلاک کر دیا'),
(4720, 51, 45, 'پس نہ تو وه کھڑے ہو سکے اور نہ بدلہ لے سکے'),
(4721, 51, 46, 'اور نوح (علیہ السلام) کی قوم کا بھی اس سے پہلے (یہی حال ہو چکا تھا) وه بھی بڑے نافرمان لوگ تھے'),
(4722, 51, 47, 'آسمان کو ہم نے (اپنے) ہاتھوں سے بنایا ہے اور یقیناً ہم کشادگی کرنے والے ہیں'),
(4723, 51, 48, 'اور زمین کو ہم نے فرش بنا دیاہے پس ہم بہت ہی اچھے بچھانے والے ہیں'),
(4724, 51, 49, 'اور ہر چیز کو ہم نے جوڑا جوڑا پیداکیا ہے تاکہ تم نصیحت حاصل کرو'),
(4725, 51, 50, 'پس تم اللہ کی طرف دوڑ بھاگ (یعنی رجوع) کرو، یقیناً میں تمہیں اس کی طرف سے صاف صاف تنبیہ کرنے واﻻ ہوں'),
(4726, 51, 51, 'اور اللہ کے ساتھ کسی اور کو معبود نہ ٹھراؤ۔ بیشک میں تمہیں اس کی طرف سے کھلا ڈرانے واﻻ ہوں'),
(4727, 51, 52, 'اسی طرح جو لوگ ان سے پہلے گزرے ہیں ان کے پاس جو بھی رسول آیا انہوں نے کہہ دیا کہ یا تو یہ جادوگر ہے یا دیوانہ ہے'),
(4728, 51, 53, 'کیایہ اس بات کی ایک دوسرے کو وصیت کرتے گئے ہیں'),
(4729, 51, 54, '(نہیں) بلکہ یہ سب کے سب سرکش ہیں۔ تو آپ ان سے منھ پھیر لیں آپ پر کوئی ملامت نہیں'),
(4730, 51, 55, 'اور نصیحت کرتے رہیں یقیناً یہ نصیحت ایمان والوں کو نفع دے گی'),
(4731, 51, 56, 'میں نے جنات اورانسانوں کو محض اسی لیے پیدا کیا ہے کہ وه صرف میری عبادت کریں'),
(4732, 51, 57, 'نہ میں ان سے روزی چاہتا ہوں نہ میری یہ چاہت ہے کہ یہ مجھے کھلائیں'),
(4733, 51, 58, 'اللہ تعالیٰ تو خود ہی سب کا روزی رساں توانائی واﻻ اور زور آور ہے'),
(4734, 51, 59, 'پس جن لوگوں نے ﻇلم کیا ہے انہیں بھی ان کے ساتھیوں کے حصہ کے مثل حصہ ملے گا، لہٰذا وه مجھ سے جلدی طلب نہ کریں'),
(4735, 51, 60, 'پس خرابی ہے منکروں کو ان کے اس دن کی جس کا وعده دیئے جاتے ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4736, 52, 1, 'قسم ہے طور کی'),
(4737, 52, 2, 'اور لکھی ہوئی کتاب کی'),
(4738, 52, 3, 'جو جھلی کے کھلے ہوئے ورق میں ہے'),
(4739, 52, 4, 'اورآباد گھر کی'),
(4740, 52, 5, 'اور اونچی چھت کی'),
(4741, 52, 6, 'اور بھڑکائے ہوئے سمندر کی'),
(4742, 52, 7, 'بیشک آپ کے رب کا عذاب ہو کر رہنے واﻻ ہے'),
(4743, 52, 8, 'اسے کوئی روکنے واﻻ نہیں'),
(4744, 52, 9, 'جس دن آسمان تھرتھرانے لگے گا'),
(4745, 52, 10, 'اور پہاڑ چلنے پھرنے لگیں گے'),
(4746, 52, 11, 'اس دن جھٹلانے والوں کو (پوری) خرابی ہے'),
(4747, 52, 12, 'جو اپنی بیہوده گوئی میں اچھل کود کر رہے ہیں'),
(4748, 52, 13, 'جس دن وه دھکے دے دے کر آتش جہنم کی طرف ﻻئیں جائیں گے'),
(4749, 52, 14, 'یہی وه آتش دوزخ ہے جسے تم جھوٹ بتلاتے تھے'),
(4750, 52, 15, '(اب بتاؤ) کیا یہ جادو ہے؟ یا تم دیکھتے ہی نہیں ہو'),
(4751, 52, 16, 'جاؤ دوزخ میں اب تمہارا صبر کرنا اور نہ کرنا تمہارے لیے یکساں ہے تمہیں فقط تمہارے کیے کا بدلہ دیا جائے گا'),
(4752, 52, 17, 'یقیناً پرہیزگار لوگ جنتوں میں اور نعمتوں میں ہیں'),
(4753, 52, 18, 'جو انہیں ان کے رب نے دے رکھی ہیں اس پر خوش خوش ہیں، اوران کے پروردگار نے انہیں جہنم کے عذاب سے بھی بچا لیا ہے'),
(4754, 52, 19, 'تم مزے سے کھاتے پیتے رہو ان اعمال کے بدلے جو تم کرتے تھے'),
(4755, 52, 20, 'برابر بچھے ہوئے شاندار تختے پر تکیے لگائے ہوئے۔ اور ہم نے ان کے نکاح بڑی بڑی آنکھوں والی (حوروں) سے کر دیئے ہیں'),
(4756, 52, 21, 'اور جو لوگ ایمان ﻻئے اور ان کی اوﻻد نے بھی ایمان میں ان کی پیروی کی ہم ان کی اوﻻد کو ان تک پہنچا دیں گے اور ان کے عمل سے ہم کچھ کم نہ کریں گے، ہر شخص اپنے اپنے اعمال کا گروی ہے'),
(4757, 52, 22, 'ہم ان کے لیے میوے اورمرغوب گوشت کی ریل پیل کردیں گے'),
(4758, 52, 23, '(خوش طبعی کے ساتھ) ایک دوسرے سے جام (شراب) کی چھینا جھپٹی کریں گے جس شراب کے سرور میں تو بیہوده گوئی ہوگی نہ گناه'),
(4759, 52, 24, 'اور ان کے اردگرد ان کے نو عمر غلام چل پھر رہے ہوں گے، گویا کہ وه موتی تھے جو ڈھکے رکھے تھے'),
(4760, 52, 25, 'اور آپس میں ایک دوسرے کی طرف متوجہ ہو کر سوال کریں گے'),
(4761, 52, 26, 'کہیں گے کہ اس سے پہلے ہم اپنے گھر والوں کے درمیان بہت ڈرا کرتے تھے'),
(4762, 52, 27, 'پس اللہ تعالیٰ نے ہم پر بڑا احسان کیا اور ہمیں تیز وتند گرم ہواؤں کے عذاب سے بچا لیا'),
(4763, 52, 28, 'ہم اس سے پہلے ہی اس کی عبادت کیا کرتے تھے، بیشک وه محسن اور مہربان ہے'),
(4764, 52, 29, 'تو آپ سمجھاتے رہیں کیونکہ آپ اپنے رب کے فضل سے نہ تو کاہن ہیں نہ دیوانہ'),
(4765, 52, 30, 'کیا کافر یوں کہتے ہیں کہ یہ شاعر ہے ہم اس پر زمانے کے حوادث (یعنی موت) کا انتظار کر رہے ہیں'),
(4766, 52, 31, 'کہہ دیجئے! تم منتظر رہو میں بھی تمہارے ساتھ انتظار کرنے والوں میں ہوں'),
(4767, 52, 32, 'کیا ان کی عقلیں انہیں یہی سکھاتی ہیں؟ یا یہ لوگ ہی سرکش ہیں'),
(4768, 52, 33, 'کیا یہ کہتے ہیں کہ اس نبی نے (قرآن) خود گھڑ لیا ہے، واقعہ یہ ہے کہ وه ایمان نہیں ﻻتے'),
(4769, 52, 34, 'اچھا اگر یہ سچے ہیں تو بھلا اس جیسی ایک (ہی) بات یہ (بھی) تو لے آئیں'),
(4770, 52, 35, 'کیا یہ بغیر کسی (پیدا کرنے والے) کے خود بخود پیدا ہوگئے ہیں؟ یا یہ خود پیدا کرنے والے ہیں؟'),
(4771, 52, 36, 'کیا انہوں نے ہی آسمانوں اور زمین کو پیدا کیا ہے؟ بلکہ یہ یقین نہ کرنے والے لوگ ہیں'),
(4772, 52, 37, 'یا کیا ان کے پاس تیرے رب کے خزانے ہیں؟ یا (ان خزانوں کے) یہ داروغہ ہیں'),
(4773, 52, 38, 'یا کیا ان کے پاس کوئی سیڑھی ہے جس پر چڑھ کر سنتے ہیں؟ (اگر ایسا ہے) تو ان کا سننے واﻻ کوئی روشن دلیل پیش کرے'),
(4774, 52, 39, 'کیا اللہ کی تو سب لڑکیاں ہیں اور تمہارے ہاں لڑکے ہیں؟'),
(4775, 52, 40, 'کیا تو ان سے کوئی اجرت طلب کرتا ہے کہ یہ اس کے تاوان سے بوجھل ہو رہے ہیں'),
(4776, 52, 41, 'کیا ان کے پاس علم غیب ہے جسے یہ لکھ لیتے ہیں؟'),
(4777, 52, 42, 'کیا یہ لوگ کوئی فریب کرنا چاہتے ہیں؟ تو یقین کرلیں کہ فریب خورده کافر ہی ہیں'),
(4778, 52, 43, 'کیا اللہ کے سوا ان کا کوئی معبود ہے؟ (ہرگز نہیں) اللہ تعالیٰ ان کے شرک سے پاک ہے'),
(4779, 52, 44, 'اگر یہ لوگ آسمان کے کسی ٹکڑے کو گرتا ہوا دیکھ لیں تب بھی کہہ دیں کہ یہ تہ بہ تہ بادل ہے'),
(4780, 52, 45, 'تو انہیں چھوڑ دے یہاں تک کہ انہیں اس دن سے سابقہ پڑے جس میں یہ بے ہوش کر دیئے جائیں گے'),
(4781, 52, 46, 'جس دن انہیں ان کا مکر کچھ کام نہ دے گا اور نہ وه مدد کیے جائیں گے'),
(4782, 52, 47, 'بیشک ﻇالموں کے لیے اس کے علاوه اور عذاب بھی ہیں۔ لیکن ان لوگوں میں سے اکثر بےعلم ہیں'),
(4783, 52, 48, 'تو اپنے رب کے حکم کے انتظار میں صبر سے کام لے، بیشک تو ہماری آنکھوں کے سامنے ہے۔ صبح کو جب تو اٹھے اپنے رب کی پاکی اور حمد بیان کر'),
(4784, 52, 49, 'اور رات کو بھی اس کی تسبیح پڑھ اور ستاروں کے ڈوبتے وقت بھی');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4785, 53, 1, 'قسم ہے ستارے کی جب وه گرے'),
(4786, 53, 2, 'کہ تمہارے ساتھی نے نہ راه گم کی ہے نہ وه ٹیڑھی راه پر ہے'),
(4787, 53, 3, 'اور نہ وه اپنی خواہش سے کوئی بات کہتے ہیں'),
(4788, 53, 4, 'وه تو صرف وحی ہے جو اتاری جاتی ہے'),
(4789, 53, 5, 'اسے پوری طاقت والے فرشتے نے سکھایا ہے'),
(4790, 53, 6, 'جو زور آور ہے پھر وه سیدھا کھڑا ہو گیا'),
(4791, 53, 7, 'اور وه بلند آسمان کے کناروں پر تھا'),
(4792, 53, 8, 'پھر نزدیک ہوا اور اتر آیا'),
(4793, 53, 9, 'پس وه دو کمانوں کے بقدر فاصلہ ره گیا بلکہ اس سے بھی کم'),
(4794, 53, 10, 'پس اس نے اللہ کے بندے کو وحی پہنچائی جو بھی پہنچائی'),
(4795, 53, 11, 'دل نے جھوٹ نہیں کہا جسے (پیغمبر نے) دیکھا'),
(4796, 53, 12, 'کیا تم جھگڑا کرتے ہو اس پر جو (پیغمبر) دیکھتے ہیں'),
(4797, 53, 13, 'اسے تو ایک مرتبہ اور بھی دیکھا تھا'),
(4798, 53, 14, 'سدرةالمنتہیٰ کے پاس'),
(4799, 53, 15, 'اسی کے پاس جنہ الماویٰ ہے'),
(4800, 53, 16, 'جب کہ سدره کو چھپائے لیتی تھی وه چیز جو اس پر چھا رہی تھی'),
(4801, 53, 17, 'نہ تو نگاه بہکی نہ حد سے بڑھی'),
(4802, 53, 18, 'یقیناً اس نے اپنے رب کی بڑی بڑی نشانیوں میں سے بعض نشانیاں دیکھ لیں'),
(4803, 53, 19, 'کیا تم نے ﻻت اور عزیٰ کو دیکھا'),
(4804, 53, 20, 'اور منات تیسرے پچھلے کو'),
(4805, 53, 21, 'کیا تمہارے لیے لڑکے اور اللہ کے لیے لڑکیاں ہیں؟'),
(4806, 53, 22, 'یہ تو اب بڑی بےانصافی کی تقسیم ہے'),
(4807, 53, 23, 'دراصل یہ صرف نام ہیں جو تم نے اور تمہارے باپ دادوں نے ان کے رکھ لیے ہیں اللہ نے ان کی کوئی دلیل نہیں اتاری۔ یہ لوگ تو صرف اٹکل کے اور اپنی نفسانی خواہشوں کے پیچھے پڑے ہوئے ہیں۔ اور یقیناً ان کے رب کی طرف سے ان کے پاس ہدایت آ چکی ہے'),
(4808, 53, 24, 'کیا ہر شخص جو آرزو کرے اسے میسر ہے؟'),
(4809, 53, 25, 'اللہ ہی کے ہاتھ ہے یہ جہان اور وه جہان'),
(4810, 53, 26, 'اور بہت سے فرشتے آسمانوں میں ہیں جن کی سفارش کچھ بھی نفع نہیں دے سکتی مگر یہ اور بات ہے کہ اللہ تعالیٰ اپنی خوشی اور اپنی چاہت سے جس کے لیے چاہے اجازت دے دے'),
(4811, 53, 27, 'بیشک جو لوگ آخرت پر ایمان نہیں رکھتے وه فرشتوں کا زنانہ نام مقرر کرتے ہیں'),
(4812, 53, 28, 'حاﻻنکہ انہیں اس کا کوئی علم نہیں وه صرف اپنے گمان کے پیچھے پڑے ہوئے ہیں اور بیشک وہم (و گمان) حق کے مقابلے میں کچھ کام نہیں دیتا'),
(4813, 53, 29, 'تو آپ اس سے منھ موڑ لیں جو ہماری یاد سے منھ موڑے اور جن کا اراده بجز زندگانیٴ دنیا کے اور کچھ نہ ہو'),
(4814, 53, 30, 'یہی ان کے علم کی انتہا ہے۔ آپ کا رب اس سے خوب واقف ہے جو اس کی راه سے بھٹک گیا ہے اور وہی خوب واقف ہےاس سے بھی جو راه یافتہ ہے'),
(4815, 53, 31, 'اور اللہ ہی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے تاکہ اللہ تعالیٰ برے عمل کرنے والوں کو ان کے اعمال کا بدلہ دے اور نیک کام کرنے والوں کو اچھا بدلہ عنایت فرمائے'),
(4816, 53, 32, 'ان لوگوں کو جو بڑے گناہوں سے بچتے ہیں اور بے حیائی سے بھی۔ سوائے کسی چھوٹے سے گناه کے۔ بیشک تیرا رب بہت کشاده مغفرت واﻻ ہے، وه تمہیں بخوبی جانتا ہے جبکہ اس نے تمہیں زمین سے پیدا کیا اور جبکہ تم اپنی ماؤں کے پیٹ میں بچے تھے۔ پس تم اپنی پاکیزگی آپ بیان نہ کرو، وہی پرہیزگاروں کو خوب جانتا ہے'),
(4817, 53, 33, 'کیا آپ نے اسے دیکھا جس نے منھ موڑ لیا'),
(4818, 53, 34, 'اور بہت کم دیا اور ہاتھ روک لیا'),
(4819, 53, 35, 'کیا اسے علم غیب ہے کہ وه (سب کچھ) دیکھ رہا ہے؟'),
(4820, 53, 36, 'کیا اسے اس چیز کی خبر نہیں دی گئی جو موسیٰ (علیہ السلام) کے'),
(4821, 53, 37, 'اور وفادار ابراہیم (علیہ السلام) کے صحیفوں میں تھا'),
(4822, 53, 38, 'کہ کوئی شخص کسی دوسرے کا بوجھ نہ اٹھائے گا'),
(4823, 53, 39, 'اور یہ کہ ہر انسان کے لیے صرف وہی ہے جس کی کوشش خود اس نے کی'),
(4824, 53, 40, 'اور یہ کہ بیشک اس کی کوشش عنقریب دیکھی جائے گی'),
(4825, 53, 41, 'پھر اسے پورا پورا بدلہ دیا جائے گا'),
(4826, 53, 42, 'اور یہ کہ آپ کے رب ہی کی طرف پہنچنا ہے'),
(4827, 53, 43, 'اور یہ کہ وہی ہنساتا ہے اور وہی رﻻتا ہے'),
(4828, 53, 44, 'اور یہ کہ وہی مارتا ہے اور جلاتا ہے'),
(4829, 53, 45, 'اور یہ کہ اسی نے جوڑا یعنی نر ماده پیدا کیا ہے'),
(4830, 53, 46, 'نطفہ سے جبکہ وه ٹپکایا جاتا ہے'),
(4831, 53, 47, 'اور یہ کہ اسی کے ذمہ دوباره پیدا کرنا ہے'),
(4832, 53, 48, 'اور یہ کہ وہی مالدار بناتا ہے اور سرمایہ دیتا ہے'),
(4833, 53, 49, 'اور یہ کہ وہی شعریٰ (ستارے) کا رب ہے'),
(4834, 53, 50, 'اور یہ کہ اس نے عاد اول کو ہلاک کیا ہے'),
(4835, 53, 51, 'اور ﺛمود کو بھی (جن میں سے) ایک کو بھی باقی نہ رکھا'),
(4836, 53, 52, 'اور اس سے پہلے قوم نوح کو، یقیناً وه بڑے ﻇالم اور سرکش تھے'),
(4837, 53, 53, 'اور مؤتفکہ (شہر یا الٹی ہوئی بستیوں کو) اسی نے الٹ دیا'),
(4838, 53, 54, 'پھر اس پر چھا دیا جو چھایا'),
(4839, 53, 55, 'پس اے انسان تو اپنے رب کی کس کس نعمت کے بارے میں جھگڑے گا؟'),
(4840, 53, 56, 'یہ (نبی) ڈرانے والے ہیں پہلے ڈرانے والوں میں سے'),
(4841, 53, 57, 'آنے والی گھڑی قریب آ گئی ہے'),
(4842, 53, 58, 'اللہ کے سوا اس کا (وقت معین پر کھول) دکھانے واﻻ اور کوئی نہیں'),
(4843, 53, 59, 'پس کیا تم اس بات سے تعجب کرتے ہو'),
(4844, 53, 60, 'اور ہنس رہے ہو؟ روتے نہیں؟'),
(4845, 53, 61, '(بلکہ) تم کھیل رہے ہو'),
(4846, 53, 62, 'اب اللہ کے سامنے سجدے کرو اور (اسی) کی عبادت کرو');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4847, 54, 1, 'قیامت قریب آ گئی اور چاند پھٹ گیا'),
(4848, 54, 2, 'یہ اگر کوئی معجزه دیکھتے ہیں تو منھ پھیر لیتے ہیں اور کہہ دیتے ہیں کہ یہ پہلے سے چلا آتا ہوا جادو ہے'),
(4849, 54, 3, 'انہوں نے جھٹلایا اور اپنی خواہشوں کی پیروی کی اور ہر کام ٹھہرے ہوئے وقت پر مقرر ہے'),
(4850, 54, 4, 'یقیناً ان کے پاس وه خبریں آچکی ہیں جن میں ڈانٹ ڈپٹ (کی نصیحت) ہے'),
(4851, 54, 5, 'اور کامل عقل کی بات ہے لیکن ان ڈراؤنی باتوں نے بھی کچھ فائده نہ دیا'),
(4852, 54, 6, 'پس (اے نبی) تم ان سے اعراض کرو جس دن ایک پکارنے واﻻ ناگوار چیز کی طرف پکارے گا'),
(4853, 54, 7, 'یہ جھکی آنکھوں قبروں سے اس طرح نکل کھڑے ہوں گے کہ گویا وه پھیلا ہوا ٹڈی دل ہے'),
(4854, 54, 8, 'پکارنے والے کی طرف دوڑتے ہوں گے اور کافر کہیں گے یہ دن تو بہت سخت ہے'),
(4855, 54, 9, 'ان سے پہلے قوم نوح نے بھی ہمارے بندے کو جھٹلایا تھا اور یہ دیوانہ بتلا کر جھڑک دیا گیا تھا'),
(4856, 54, 10, 'پس اس نے اپنے رب سے دعا کی کہ میں بے بس ہوں تو میری مدد کر'),
(4857, 54, 11, 'پس ہم نے آسمان کے دروازوں کو زور کے مینہ سے کھول دیا'),
(4858, 54, 12, 'اور زمین سے چشموں کو جاری کر دیا پس اس کام کے لیے جو مقدر کیا گیا تھا (دونوں) پانی جمع ہو گئے'),
(4859, 54, 13, 'اور ہم نے اسے تختوں اور کیلوں والی (کشتی) پر سوار کر لیا'),
(4860, 54, 14, 'جو ہماری آنکھوں کے سامنے چل رہی تھی۔ بدلہ اس کی طرف سے جس کا کفر کیا گیا تھا'),
(4861, 54, 15, 'اور بیشک ہم نے اس واقعہ کو نشانی بنا کر باقی رکھا پس کوئی ہے نصیحت حاصل کرنے واﻻ'),
(4862, 54, 16, 'بتاؤ میرا عذاب اور میری ڈرانے والی باتیں کیسی رہیں؟'),
(4863, 54, 17, 'اور بیشک ہم نے قرآن کو سمجھنے کے لیے آسان کر دیا ہے۔ پس کیا کوئی نصیحت حاصل کرنے واﻻ ہے؟'),
(4864, 54, 18, 'قوم عاد نے بھی جھٹلایا پس کیسا ہوا میرا عذاب اور میری ڈرانے والی باتیں'),
(4865, 54, 19, 'ہم نے ان پر تیز وتند مسلسل چلنے والی ہوا، ایک پیہم منحوس دن میں بھیج دی'),
(4866, 54, 20, 'جو لوگوں کو اٹھا اٹھا کر دے پٹختی تھی، گویا کہ وه جڑ سے کٹے ہوئے کھجور کے تنے ہیں'),
(4867, 54, 21, 'پس کیسی رہی میری سزا اور میرا ڈرانا؟'),
(4868, 54, 22, 'یقیناً ہم نے قرآن کو نصیحت کے لیے آسان کر دیا ہے، پس کیا ہے کوئی نصیحت حاصل کرنے واﻻ؟'),
(4869, 54, 23, 'قوم ﺛمود نے ڈرانے والوں کو جھٹلایا'),
(4870, 54, 24, 'اور کہنے لگے کیا ہمیں میں سے ایک شخص کی ہم فرمانبرداری کرنے لگیں؟ تب تو ہم یقیناً غلطی اور دیوانگی میں پڑے ہوئے ہوں گے'),
(4871, 54, 25, 'کیا ہمارے سب کے درمیان صرف اسی پر وحی اتاری گئی؟ نہیں بلکہ وه جھوٹا اور شیخی خور ہے'),
(4872, 54, 26, 'اب سب جان لیں گے کل کو کہ کون جھوٹا اور شیخی خور تھا؟'),
(4873, 54, 27, 'بیشک ہم ان کی آزمائش کے لیے اونٹنی بھیجیں گے پس (اے صالح) توان کا منتظر ره اور صبر کر'),
(4874, 54, 28, 'ہاں انہیں خبر کر دے کہ پانی ان میں تقسیم شده ہے، ہرایک اپنی باری پر حاضر ہوگا'),
(4875, 54, 29, 'انہوں نے اپنے ساتھی کو آواز دی جس نے (اونٹنی پر) وار کیا اور (اس کی) کوچیں کاٹ دیں'),
(4876, 54, 30, 'پس کیونکر ہوا میرا عذاب اور میرا ڈرانا'),
(4877, 54, 31, 'ہم نے ان پر ایک چیﺦ بھیجی پس ایسے ہو گئے جیسے باڑ بنانے والے کی روندی ہوئی گھاس'),
(4878, 54, 32, 'اور ہم نے نصیحت کے لیے قرآن کو آسان کر دیا ہے پس کیا ہے کوئی جو نصیحت قبول کرے'),
(4879, 54, 33, 'قوم لوط نے بھی ڈرانے والوں کی تکذیب کی'),
(4880, 54, 34, 'بیشک ہم نے ان پر پتھر برسانے والی ہوا بھیجی سوائے لوط (علیہ السلام) کے گھر والوں کے، انہیں ہم نے سحر کے وقت نجات دے دی'),
(4881, 54, 35, 'اپنے احسان سے ہر ہر شکر گزار کو ہم اسی طرح بدلہ دیتے ہیں'),
(4882, 54, 36, 'یقیناً (لوط علیہ السلام) نے انہیں ہماری پکڑ سے ڈرایا تھا لیکن انہوں نے ڈرانے والوں کے بارے میں (شک وشبہ اور) جھگڑا کیا'),
(4883, 54, 37, 'اور ان (لوط علیہ السلام) کو ان کے مہمانوں کے بارے میں پھسلایا پس ہم نے ان کی آنکھیں اندھی کردیں، (اور کہہ دیا) میرا عذاب اور میرا ڈرانا چکھو'),
(4884, 54, 38, 'اور یقینی بات ہے کہ انہیں صبح سویرے ہی ایک جگہ پکڑنے والے مقرره عذاب نے غارت کر دیا'),
(4885, 54, 39, 'پس میرے عذاب اور میرے ڈراوے کا مزه چکھو'),
(4886, 54, 40, 'اور یقیناً ہم نے قرآن کو پند ووعﻆ کے لیے آسان کر دیا ہے۔ پس کیا کوئی ہے نصحیت پکڑنے واﻻ'),
(4887, 54, 41, 'اور فرعونیوں کے پاس بھی ڈرانے والے آئے'),
(4888, 54, 42, 'انہوں نے ہماری تمام نشانیاں جھٹلائیں پس ہم نے انہیں بڑے غالب قوی پکڑنے والے کی طرح پکڑ لیا'),
(4889, 54, 43, '(اے قریشیو!) کیا تمہارے کافر ان کافروں سے کچھ بہتر ہیں؟ یا تمہارے لیے اگلی کتابوں میں چھٹکارا لکھا ہوا ہے؟'),
(4890, 54, 44, 'یا یہ کہتے ہیں کہ ہم غلبہ پانے والی جماعت ہیں'),
(4891, 54, 45, 'عنقریب یہ جماعت شکست دی جائےگی اور پیٹھ دے کر بھاگے گی'),
(4892, 54, 46, 'بلکہ قیامت کی گھڑی ان کے وعدے کے وقت ہے اور قیامت بڑی سخت اور کڑوی چیز ہے'),
(4893, 54, 47, 'بیشک گناه گار گمراہی میں اور عذاب میں ہیں'),
(4894, 54, 48, 'جس دن وه اپنے منھ کے بل آگ میں گھسیٹے جائیں گے (اوران سے کہا جائے گا) دوزخ کی آگ لگنے کے مزے چکھو'),
(4895, 54, 49, 'بیشک ہم نے ہر چیز کو ایک (مقرره) اندازے پر پیدا کیا ہے'),
(4896, 54, 50, 'اور ہمارا حکم صرف ایک دفعہ (کا ایک کلمہ) ہی ہوتا ہے جیسے آنکھ کا جھپکنا'),
(4897, 54, 51, 'اور ہم نے تم جیسے بہتیروں کو ہلاک کر دیا ہے، پس کوئی ہے نصیحت لینے واﻻ'),
(4898, 54, 52, 'جوکچھ انہوں نے (اعمال) کیے ہیں سب نامہٴ اعمال میں لکھے ہوئے ہیں'),
(4899, 54, 53, '(اسی طرح) ہر چھوٹی بڑی بات بھی لکھی ہوئی ہے'),
(4900, 54, 54, 'یقیناً ہمارا ڈر رکھنے والے جنتوں اور نہروں میں ہوں گے'),
(4901, 54, 55, 'راستی اور عزت کی بیٹھک میں قدرت والے بادشاه کے پاس');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4902, 55, 1, 'رحمٰن نے'),
(4903, 55, 2, 'قرآن سکھایا'),
(4904, 55, 3, 'اسی نے انسان کو پیدا کیا'),
(4905, 55, 4, 'اور اسے بولنا سکھایا'),
(4906, 55, 5, 'آفتاب اور ماہتاب (مقرره) حساب سے ہیں'),
(4907, 55, 6, 'اور ستارے اور درخت دونوں سجده کرتے ہیں'),
(4908, 55, 7, 'اسی نے آسمان کو بلند کیا اور اسی نے ترازو رکھی'),
(4909, 55, 8, 'تاکہ تم تولنے میں تجاوز نہ کرو'),
(4910, 55, 9, 'انصاف کے ساتھ وزن کو ٹھیک رکھو اور تول میں کم نہ دو'),
(4911, 55, 10, 'اور اسی نے مخلوق کے لیے زمین بچھا دی'),
(4912, 55, 11, 'جس میں میوے ہیں اور خوشے والے کھجور کے درخت ہیں'),
(4913, 55, 12, 'اور بھس واﻻ اناج ہے۔ اور خوشبودار پھول ہیں'),
(4914, 55, 13, 'پس (اے انسانو اور جنو!) تم اپنے پروردگار کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4915, 55, 14, 'اس نےانسان کو بجنے والی مٹی سے پیدا کیا جو ٹھیکری کی طرح تھی'),
(4916, 55, 15, 'اور جنات کو آگ کے شعلے سے پیدا کیا'),
(4917, 55, 16, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4918, 55, 17, 'وه رب ہے دونوں مشرقوں اور دونوں مغربوں کا'),
(4919, 55, 18, 'تو (اے جنو اورانسانو!) تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4920, 55, 19, 'اس نے دو دریا جاری کر دیے جو ایک دوسرے سے مل جاتے ہیں'),
(4921, 55, 20, 'ان دونوں میں ایک آڑ ہے کہ اس سے بڑھ نہیں سکتے'),
(4922, 55, 21, 'پس اپنے پروردگار کی کون کون سی نعمت کو جھٹلاؤ گے؟'),
(4923, 55, 22, 'ان دونوں میں سے موتی اور مونگے برآمد ہوتے ہیں'),
(4924, 55, 23, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4925, 55, 24, 'اور اللہ ہی کی (ملکیت میں) ہیں وه جہاز جو سمندروں میں پہاڑ کی طرح بلند (چل پھر رہے) ہیں'),
(4926, 55, 25, 'پس (اے انسانو اور جنو!) تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4927, 55, 26, 'زمین پر جو ہیں سب فنا ہونے والے ہیں'),
(4928, 55, 27, 'صرف تیرے رب کی ذات جو عظمت اور عزت والی ہے باقی ره جائے گی'),
(4929, 55, 28, 'پھرتم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4930, 55, 29, 'سب آسمان وزمین والے اسی سے مانگتے ہیں۔ ہر روز وه ایک شان میں ہے'),
(4931, 55, 30, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4932, 55, 31, '(جنوں اور انسانوں کے گروہو!) عنقریب ہم تمہاری طرف پوری طرح متوجہ ہو جائیں گے'),
(4933, 55, 32, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤگے؟'),
(4934, 55, 33, 'اے گروه جنات و انسان! اگر تم میں آسمانوں اور زمین کے کناروں سے باہر نکل جانے کی طاقت ہے تو نکل بھاگو! بغیر غلبہ اور طاقت کے تم نہیں نکل سکتے'),
(4935, 55, 34, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4936, 55, 35, 'تم پر آگ کے شعلے اور دھواں چھوڑا جائے گا پھر تم مقابلہ نہ کر سکو گے'),
(4937, 55, 36, 'پھر اپنے رب کی نعمتوں میں سے کس کس نعمت کو جھٹلاؤ گے؟'),
(4938, 55, 37, 'پس جب کہ آسمان پھٹ کر سرخ ہو جائے جیسے کہ سرخ چمڑه'),
(4939, 55, 38, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4940, 55, 39, 'اس دن کسی انسان اورکسی جن سے اس کے گناہوں کی پرسش نہ کی جائے گی'),
(4941, 55, 40, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4942, 55, 41, 'گناه گار صرف حلیہ ہی سے پہچان لیے جائیں گے اور ان کی پیشانیوں کے بال اور قدم پکڑ لیے جائیں گے'),
(4943, 55, 42, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4944, 55, 43, 'یہ ہے وه جہنم جسے مجرم جھوٹا جانتے تھے'),
(4945, 55, 44, 'اس کے اور کھولتے ہوئے گرم پانی کے درمیان چکر کھائیں گے'),
(4946, 55, 45, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4947, 55, 46, 'اور اس شخص کے لیے جو اپنے رب کے سامنے کھڑا ہونے سے ڈرا دو جنتیں ہیں'),
(4948, 55, 47, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4949, 55, 48, '(دونوں جنتیں) بہت سی ٹہنیوں اور شاخوں والی ہیں'),
(4950, 55, 49, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4951, 55, 50, 'ان دونوں (جنتوں) میں دو بہتے ہوئے چشمے ہیں'),
(4952, 55, 51, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4953, 55, 52, 'ان دونوں جنتوں میں ہر قسم کے میوؤں کی دو قسمیں ہوگی'),
(4954, 55, 53, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4955, 55, 54, 'جنتی ایسے فرشوں پر تکیہ لگائے ہوئے ہوں گے جن کے استر دبیز ریشم کے ہوں گے، اور ان دونوں جنتوں کے میوے بالکل قریب ہوں گے'),
(4956, 55, 55, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4957, 55, 56, 'وہاں (شرمیلی) نیچی نگاه والی حوریں ہیں جنہیں ان سے پہلے کسی جن وانس نے ہاتھ نہیں لگایا'),
(4958, 55, 57, 'پس اپنے پالنے والے کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4959, 55, 58, 'وه حوریں مثل یاقوت اور مونگے کے ہوں گی'),
(4960, 55, 59, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4961, 55, 60, 'احسان کا بدلہ احسان کے سوا کیا ہے'),
(4962, 55, 61, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4963, 55, 62, 'اور ان کے سوا دو جنتیں اور ہیں'),
(4964, 55, 63, 'پس تم اپنے پرورش کرنے والے کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4965, 55, 64, 'جو دونوں گہری سبز سیاہی مائل ہیں'),
(4966, 55, 65, 'بتاؤ اب اپنے پروردگار کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4967, 55, 66, 'ان میں دو (جوش سے) ابلنے والے چشمے ہیں'),
(4968, 55, 67, 'پھر تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4969, 55, 68, 'ان دونوں میں میوے اور کھجور اور انار ہوں گے'),
(4970, 55, 69, 'کیا اب بھی رب کی کسی نعمت کی تکذیب تم کرو گے؟'),
(4971, 55, 70, 'ان میں نیک سیرت خوبصورت عورتیں ہیں'),
(4972, 55, 71, 'پس تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4973, 55, 72, '(گوری رنگت کی) حوریں جنتی خیموں میں رہنے والیاں ہیں'),
(4974, 55, 73, 'پس (اے انسانو اور جنو!) تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4975, 55, 74, 'ان کو ہاتھ نہیں لگایا کسی انسان یا جن نے اس سے قبل'),
(4976, 55, 75, 'پس اپنے پروردگار کی کون کون سی نعمت کے ساتھ تم تکذیب کرتے ہو؟'),
(4977, 55, 76, 'سبز مسندوں اور عمده فرشوں پر تکیہ لگائے ہوئے ہوں گے'),
(4978, 55, 77, 'پس (اے جنو اور انسانو!) تم اپنے رب کی کس کس نعمت کو جھٹلاؤ گے؟'),
(4979, 55, 78, 'تیرے پروردگار کا نام بابرکت ہے جو عزت وجلال واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(4980, 56, 1, 'جب قیامت قائم ہو جائے گی'),
(4981, 56, 2, 'جس کے واقع ہونے میں کوئی جھوٹ نہیں'),
(4982, 56, 3, 'وه پست کرنے والی اور بلند کرنے والی ہوگی'),
(4983, 56, 4, 'جبکہ زمین زلزلہ کے ساتھ ہلا دی جائے گی'),
(4984, 56, 5, 'اور پہاڑ بالکل ریزه ریزه کر دیے جائیں گے'),
(4985, 56, 6, 'پھر وه مثل پراگنده غبار کے ہو جائیں گے'),
(4986, 56, 7, 'اور تم تین جماعتوں میں ہو جاؤ گے'),
(4987, 56, 8, 'پس داہنے ہاتھ والے کیسے اچھے ہیں داہنے ہاتھ والے'),
(4988, 56, 9, 'اور بائیں ہاتھ والے کیا حال ہے بائیں ہاتھ والوں کا'),
(4989, 56, 10, 'اور جو آگے والے ہیں وه تو آگے والے ہی ہیں'),
(4990, 56, 11, 'وه بالکل نزدیکی حاصل کیے ہوئے ہیں'),
(4991, 56, 12, 'نعمتوں والی جنتوں میں ہیں'),
(4992, 56, 13, '(بہت بڑا) گروه تو اگلے لوگوں میں سے ہوگا'),
(4993, 56, 14, 'اور تھوڑے سے پچھلے لوگوں میں سے'),
(4994, 56, 15, 'یہ لوگ سونے کے تاروں سے بنے ہوئے تختوں پر'),
(4995, 56, 16, 'ایک دوسرے کےسامنے تکیہ لگائے بیٹھے ہوں گے'),
(4996, 56, 17, 'ان کے پاس ایسے لڑکے جو ہمیشہ (لڑکے ہی) رہیں گے آمدورفت کریں گے'),
(4997, 56, 18, 'آبخورے اور جگ لے کر اور ایسا جام لے کر جو بہتی ہوئی شراب سے پر ہو'),
(4998, 56, 19, 'جس سے نہ سر میں درد ہو نہ عقل میں فتور آئے'),
(4999, 56, 20, 'اور ایسے میوے لیے ہوئے جو ان کی پسند کے ہوں'),
(5000, 56, 21, 'اور پرندوں کے گوشت جو انہیں مرغوب ہوں'),
(5001, 56, 22, 'اور بڑی بڑی آنکھوں والی حوریں'),
(5002, 56, 23, 'جو چھپے ہوئے موتیوں کی طرح ہیں'),
(5003, 56, 24, 'یہ صلہ ہے ان کے اعمال کا'),
(5004, 56, 25, 'نہ وہاں بکواس سنیں گے اور نہ گناه کی بات'),
(5005, 56, 26, 'صرف سلام ہی سلام کی آواز ہوگی'),
(5006, 56, 27, 'اور داہنے ہاتھ والے کیا ہی اچھے ہیں داہنے ہاتھ والے'),
(5007, 56, 28, 'وه بغیرکانٹوں کی بیریوں'),
(5008, 56, 29, 'اور تہ بہ تہ کیلوں'),
(5009, 56, 30, 'اور لمبے لمبے سایوں'),
(5010, 56, 31, 'اور بہتے ہوئے پانیوں'),
(5011, 56, 32, 'اور بکثرت پھلوں میں'),
(5012, 56, 33, 'جو نہ ختم ہوں نہ روک لیے جائیں'),
(5013, 56, 34, 'اور اونچے اونچے فرشوں میں ہوں گے'),
(5014, 56, 35, 'ہم نے ان کی (بیویوں کو) خاص طور پر بنایا ہے'),
(5015, 56, 36, 'اور ہم نے انہیں کنواریاں بنا دیا ہے'),
(5016, 56, 37, 'محبت والیاں اور ہم عمر ہیں'),
(5017, 56, 38, 'دائیں ہاتھ والوں کے لیے ہیں'),
(5018, 56, 39, 'جم غفیر ہے اگلوں میں سے'),
(5019, 56, 40, 'اور بہت بڑی جماعت ہے پچھلوں میں سے'),
(5020, 56, 41, 'اور بائیں ہاتھ والے کیا ہیں بائیں ہاتھ والے'),
(5021, 56, 42, 'گرم ہوا اور گرم پانی میں (ہوں گے)'),
(5022, 56, 43, 'اورسیاه دھوئیں کے سائے میں'),
(5023, 56, 44, 'جو نہ ٹھنڈا ہے نہ فرحت بخش'),
(5024, 56, 45, 'بیشک یہ لوگ اس سے پہلے بہت نازوں میں پلے ہوئے تھے'),
(5025, 56, 46, 'اور بڑے بڑے گناہوں پر اصرار کرتے تھے'),
(5026, 56, 47, 'اور کہتے تھے کہ کیا جب ہم مر جائیں گے اور مٹی اور ہڈی ہو جائیں گے تو کیا ہم پھر دوباره اٹھا کھڑے کیے جائیں گے'),
(5027, 56, 48, 'اور کیا ہمارے اگلے باپ دادا بھی؟'),
(5028, 56, 49, 'آپ کہہ دیجئے کہ یقیناً سب اگلے اور پچھلے'),
(5029, 56, 50, 'ضرور جمع کئے جائیں گے ایک مقرر دن کے وقت'),
(5030, 56, 51, 'پھر تم اے گمراہو جھٹلانے والو!'),
(5031, 56, 52, 'البتہ کھانے والے ہو تھوہر کا درخت'),
(5032, 56, 53, 'اور اسی سے پیٹ بھرنے والے ہو'),
(5033, 56, 54, 'پھر اس پر گرم کھولتا پانی پینے والے ہو'),
(5034, 56, 55, 'پھر پینے والے بھی پیاسے اونٹوں کی طرح'),
(5035, 56, 56, 'قیامت کے دن ان کی مہمانی یہ ہے'),
(5036, 56, 57, 'ہم ہی نے تم سب کو پیدا کیا ہے پھر تم کیوں باور نہیں کرتے؟'),
(5037, 56, 58, 'اچھا پھر یہ تو بتلاؤ کہ جو منی تم ٹپکاتے ہو'),
(5038, 56, 59, 'کیا اس کا (انسان) تم بناتے ہو یا پیدا کرنے والے ہم ہی ہیں؟'),
(5039, 56, 60, 'ہم ہی نے تم میں موت کو متعین کر دیا ہے اور ہم اس سے ہارے ہوئے نہیں ہیں'),
(5040, 56, 61, 'کہ تمہاری جگہ تم جیسے اور پیدا کر دیں اور تمہیں نئے سرے سے اس عالم میں پیدا کریں جس سے تم (بالکل) بےخبر ہو'),
(5041, 56, 62, 'تمہیں یقینی طور پر پہلی دفعہ کی پیدائش معلوم ہی ہے پھر کیوں عبرت حاصل نہیں کرتے؟'),
(5042, 56, 63, 'اچھا پھر یہ بھی بتلاؤ کہ تم جو کچھ بوتے ہو'),
(5043, 56, 64, 'اسے تم ہی اگاتے ہو یا ہم اگانے والے ہیں'),
(5044, 56, 65, 'اگر ہم چاہیں تواسے ریزه ریزه کر ڈالیں اور تم حیرت کے ساتھ باتیں بناتے ہی ره جاؤ'),
(5045, 56, 66, 'کہ ہم پر تو تاوان ہی پڑ گیا'),
(5046, 56, 67, 'بلکہ ہم بالکل محروم ہی ره گئے'),
(5047, 56, 68, 'اچھا یہ بتاؤ کہ جس پانی کو تم پیتے ہو'),
(5048, 56, 69, 'اسے بادلوں سے بھی تم ہی اتارتے ہو یا ہم برساتے ہیں؟'),
(5049, 56, 70, 'اگر ہماری منشا ہو تو ہم اسے کڑوا زہر کردیں پھر تم ہماری شکرگزاری کیوں نہیں کرتے؟'),
(5050, 56, 71, 'اچھا ذرا یہ بھی بتاؤ کہ جو آگ تم سلگاتے ہو'),
(5051, 56, 72, 'اس کے درخت کو تم نے پیدا کیا ہے یا ہم اس کے پیدا کرنے والے ہیں؟'),
(5052, 56, 73, 'ہم نے اسے سبب نصیحت اور مسافروں کے فائدے کی چیز بنایا ہے'),
(5053, 56, 74, 'پس اپنے بہت بڑے رب کے نام کی تسبیح کیا کرو'),
(5054, 56, 75, 'پس میں قسم کھاتا ہوں ستاروں کے گرنے کی'),
(5055, 56, 76, 'اور اگرتمہیں علم ہو تو یہ بہت بڑی قسم ہے'),
(5056, 56, 77, 'کہ بیشک یہ قرآن بہت بڑی عزت واﻻ ہے'),
(5057, 56, 78, 'جو ایک محفوظ کتاب میں درج ہے'),
(5058, 56, 79, 'جسے صرف پاک لوگ ہی چھو سکتے ہیں'),
(5059, 56, 80, 'یہ رب العالمین کی طرف سےاترا ہوا ہے'),
(5060, 56, 81, 'پس کیا تم ایسی بات کو سرسری (اور معمولی) سمجھ رہے ہو؟'),
(5061, 56, 82, 'اور اپنے حصے میں یہی لیتے ہو کہ جھٹلاتے پھرو'),
(5062, 56, 83, 'پس جبکہ روح نرخرے تک پہنچ جائے'),
(5063, 56, 84, 'اور تم اس وقت آنکھوں سے دیکھتے رہو'),
(5064, 56, 85, 'ہم اس شخص سے بہ نسبت تمہارے بہت زیاده قریب ہوتے ہیں لیکن تم نہیں دیکھ سکتے'),
(5065, 56, 86, 'پس اگر تم کسی کے زیرفرمان نہیں'),
(5066, 56, 87, 'اور اس قول میں سچے ہو تو (ذرا) اس روح کو تو لوٹاؤ'),
(5067, 56, 88, 'پس جو کوئی بارگاه الٰہی سے قریب کیا ہوا ہوگا'),
(5068, 56, 89, 'اسے تو راحت ہے اور غذائیں ہیں اور آرام والی جنت ہے'),
(5069, 56, 90, 'اور جو شحص داہنے (ہاتھ) والوں میں سے ہے'),
(5070, 56, 91, 'تو بھی سلامتی ہے تیرے لیے کہ تو داہنے والوں میں سے ہے'),
(5071, 56, 92, 'لیکن اگر کوئی جھٹلانے والوں گمراہوں میں سے ہے'),
(5072, 56, 93, 'تو کھولتے ہوئے گرم پانی کی مہمانی ہے'),
(5073, 56, 94, 'اور دوزخ میں جانا ہے'),
(5074, 56, 95, 'یہ خبر سراسر حق اور قطعاً یقینی ہے'),
(5075, 56, 96, 'پس تواپنے عظیم الشان پروردگار کی تسبیح کر');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5076, 57, 1, 'آسمانوں اور زمین میں جو ہے (سب) اللہ کی تسبیح کر رہے ہیں، وه زبردست باحکمت ہے'),
(5077, 57, 2, 'آسمانوں اور زمین کی بادشاہت اسی کی ہے، وہی زندگی دیتا ہے اور موت بھی اور وه ہر چیز پر قادر ہے'),
(5078, 57, 3, 'وہی پہلے ہے اور وہی پیچھے، وہی ﻇاہر ہے اور وہی مخفی، اور وه ہر چیز کو بخوبی جاننے واﻻ ہے'),
(5079, 57, 4, 'وہی ہے جس نے آسمانوں اور زمین کو چھ دن میں پیدا کیا پھر عرش پر مستوی ہو گیا۔ وه (خوب) جانتا ہےاس چیز کو جو زمین میں جائے اور جو اس سے نکلے اور جو آسمان سے نیچے آئے اور جو کچھ چڑھ کر اس میں جائے، اور جہاں کہیں تم ہو وه تمہارے ساتھ ہے اور جو تم کر رہے ہو اللہ دیکھ رہا ہے'),
(5080, 57, 5, 'آسمانوں کی اور زمین کی بادشاہی اسی کی ہے۔ اور تمام کام اسی کی طرف لوٹائے جاتے ہیں'),
(5081, 57, 6, 'وہی رات کو دن میں لے جاتا ہے اور وہی دن کو رات میں داخل کر دیتا ہے اور سینوں کے بھیدوں کا وه پورا عالم ہے'),
(5082, 57, 7, 'اللہ پر اور اس کے رسول پر ایمان لے آؤ اور اس مال میں سے خرچ کرو جس میں اللہ نے تمہیں (دوسروں کا) جانشین بنایا ہے پس تم میں سے جو ایمان ﻻئیں اور خیرات کریں انہیں بہت بڑا ﺛواب ملے گا'),
(5083, 57, 8, 'تم اللہ پر ایمان کیوں نہیں ﻻتے؟ حاﻻنکہ خود رسول تمہیں اپنے رب پر ایمان ﻻنےکی دعوت دے رہا ہے اور اگر تم مومن ہو تو وه تو تم سے مضبوط عہد وپیمان بھی لے چکا ہے'),
(5084, 57, 9, 'وه (اللہ) ہی ہے جو اپنے بندے پر واضح آیتیں اتارتا ہے تاکہ وه تمہیں اندھیروں سے نور کی طرف لے جائے یقیناً اللہ تعالیٰ تم پر نرمی کرنے واﻻ رحم کرنے واﻻ ہے'),
(5085, 57, 10, 'تمہیں کیا ہو گیا ہے جو تم اللہ کی راه میں خرچ نہیں کرتے؟ دراصل آسمانوں اور زمینوں کی میراث کا مالک (تنہا) اللہ ہی ہے تم میں سے جن لوگوں نے فتح سے پہلے فی سبیل اللہ دیا ہے اور قتال کیا ہے وه (دوسروں کے) برابر نہیں، بلکہ ان سے بہت بڑے درجے کے ہیں جنہوں نے فتح کے بعد خیراتیں دیں اور جہاد کیے۔ ہاں بھلائی کا وعده تو اللہ تعالیٰ کاان سب سے ہے جو کچھ تم کر رہے ہو اس سے اللہ خبردار ہے'),
(5086, 57, 11, 'کون ہے جو اللہ تعالیٰ کو اچھی طرح قرض دے پھر اللہ تعالیٰ اسے اس کے لیے بڑھاتا چلا جائے اور اس کے لیے پسندیده اجر ﺛابت ہو جائے'),
(5087, 57, 12, '(قیامت کے دن تو دیکھے گا کہ مومن مردوں اور عورتوں کا نور ان کے آگے آگے اور ان کے دائیں دوڑ رہا ہوگا آج تمہیں ان جنتوں کی خوش خبری ہے جن کے نیچے نہریں جاری ہیں جن میں ہمیشہ کی رہائش ہے۔ یہ ہے بڑی کامیابی'),
(5088, 57, 13, 'اس دن منافق مرد وعورت ایمان والوں سے کہیں گے کہ ہمارا انتظار تو کرو کہ ہم بھی تمہارے نور سے کچھ روشنی حاصل کر لیں۔ جواب دیا جائے گا کہ تم اپنے پیچھے لوٹ جاؤ اور روشنی تلاش کرو۔ پھر ان کے اور ان کے درمیان ایک دیوار حائل کر دی جائے گی جس میں دروازه بھی ہوگا۔ اس کے اندرونی حصہ میں تو رحمت ہوگی اور باہر کی طرف عذاب ہوگا'),
(5089, 57, 14, 'یہ چلا چلا کر ان سے کہیں گے کہ کیا ہم تمھارے ساتھ نہ تھے وه کہیں گے کہ ہاں تھے تو سہی لیکن تم نے اپنے آپ کو فتنہ میں پھنسا رکھا تھا اور انتظار میں ہی رہے اور شک و شبہ کرتے رہے اور تمہیں تمہاری فضول تمناؤں نے دھوکے میں ہی رکھا یہاں تک کہ اللہ کا حکم آ پہنچا اور تمہیں اللہ کے بارے میں دھوکہ دینے والے نے دھوکے میں ہی رکھا'),
(5090, 57, 15, 'الغرض، آج تم سے نہ فدیہ (اور نہ بدلہ) قبول کیا جائے گا اور نہ کافروں سے تم (سب) کا ٹھکانا دوزخ ہے۔ وہی تمہاری رفیق ہے اور وه برا ٹھکانا ہے'),
(5091, 57, 16, 'کیا اب تک ایمان والوں کے لیے وقت نہیں آیا کہ ان کے دل ذکر الٰہی سے اور جو حق اتر چکا ہے اس سے نرم ہو جائیں اور ان کی طرح نہ ہو جائیں جنہیں ان سے پہلے کتاب دی گئی تھی پھر جب ان پر ایک زمانہ دراز گزر گیا تو ان کے دل سخت ہو گئے اور ان میں بہت سے فاسق ہیں'),
(5092, 57, 17, 'یقین مانوکہ اللہ ہی زمین کو اس کی موت کے بعد زنده کر دیتا ہے۔ ہم نے تو تمہارے لیے اپنی آیتیں بیان کردیں تاکہ تم سمجھو'),
(5093, 57, 18, 'بیشک صدقہ دینے والے مرد اور صدقہ دینے والی عورتیں اور جو اللہ کو خلوص کے ساتھ قرض دے رہے ہیں۔ ان کے لیے یہ بڑھایا جائے گا اور ان کے لیے پسندیده اجر وﺛواب ہے'),
(5094, 57, 19, 'اللہ اوراس کے رسول پر جو ایمان رکھتے ہیں وہی لوگ اپنے رب کے نزدیک صدیق اور شہید ہیں ان کے لیے ان کا اجر اور ان کا نور ہے، اور جو لوگ کفر کرتے ہیں اور ہماری آیتوں کوجھٹلاتے ہیں وه جہنمی ہیں'),
(5095, 57, 20, 'خوب جان رکھو کہ دنیا کی زندگی صرف کھیل تماشا زینت اور آپس میں فخر (و غرور) اور مال واوﻻد میں ایک کا دوسرے سے اپنے آپ کو زیاده بتلانا ہے، جیسے بارش اور اس کی پیداوار کسانوں کو اچھی معلوم ہوتی ہے پھر جب وه خشک ہو جاتی ہے تو زرد رنگ میں اس کو تم دیکھتے ہو پھر وه بالکل چورا چورا ہو جاتی ہے اور آخرت میں سخت عذاب اور اللہ کی مغفرت اور رضامندی ہے اور دنیا کی زندگی بجز دھوکے کے سامان کے اور کچھ بھی تو نہیں'),
(5096, 57, 21, '(آؤ) دوڑو اپنے رب کی مغفرت کی طرف اور اس جنت کی طرف جس کی وسعت آسمان وزمین کی وسعت کے برابر ہے یہ ان کے لیے بنائی ہے جو اللہ پر اور اس کے رسولوں پر ایمان رکھتے ہیں۔ یہ اللہ کا فضل ہے جسے چاہے دے اور اللہ بڑے فضل واﻻ ہے'),
(5097, 57, 22, 'نہ کوئی مصیبت دنیا میں آتی ہے نہ (خاص) تمہاری جانوں میں، مگر اس سے پہلے کہ ہم اس کو پیدا کریں وه ایک خاص کتاب میں لکھی ہوئی ہے، یہ (کام) اللہ تعالیٰ پر (بالکل) آسان ہے'),
(5098, 57, 23, 'تاکہ تم اپنے سے فوت شده کسی چیز پر رنجیده نہ ہو جایا کرو اور نہ عطا کرده چیز پر اترا جاؤ، اور اترانے والے شیخی خوروں کو اللہ پسند نہیں فرماتا'),
(5099, 57, 24, 'جو (خود بھی) بخل کریں اور دوسروں کو (بھی) بخل کی تعلیم دیں۔ سنو! جو بھی منھ پھیرے اللہ بےنیاز اور سزاوار حمد وﺛنا ہے'),
(5100, 57, 25, 'یقیناً ہم نے اپنے پیغمبروں کو کھلی دلیلیں دے کر بھیجا اور ان کے ساتھ کتاب اور میزان (ترازو) نازل فرمایا تاکہ لوگ عدل پر قائم رہیں۔ اور ہم نے لوہے کو اتارا جس میں سخت ہیبت وقوت ہے اور لوگوں کے لیے اور بھی (بہت سے) فائدے ہیں اور اس لیے بھی کہ اللہ جان لے کہ اس کی اور اس کے رسولوں کی مدد بےدیکھے کون کرتا ہے، بیشک اللہ قوت واﻻ اور زبردست ہے'),
(5101, 57, 26, 'بیشک ہم نے نوح اور ابراہیم (علیہ السلام) کو (پیغمبر بنا کر) بھیجا اور ہم نے ان دونوں کی اوﻻد میں پیغمبری اور کتاب جاری رکھی تو ان میں سے کچھ تو راه یافتہ ہوئے اور ان میں سے اکثر بہت نافرمان رہے'),
(5102, 57, 27, 'ان کے بعد پھر بھی ہم اپنے رسولوں کو پے در پے بھیجتے رہے اور ان کے بعد عیسیٰ بن مریم (علیہ السلام) کو بھیجا اور انہیں انجیل عطا فرمائی اور ان کے ماننے والوں کے دلوں میں شفقت اور رحم پیدا کردیا ہاں رہبانیت (ترک دنیا) تو ان لوگوں نے ازخود ایجاد کرلی تھی ہم نے ان پر اسے واجب نہ کیا تھا سوائے اللہ کی رضاجوئی کے۔ سو انہوں نے اس کی پوری رعایت نہ کی، پھر بھی ہم نے ان میں سے جو ایمان ﻻئے تھے۔ انہیں ان کا اجر دیا اور ان میں زیاده تر لوگ نافرمان ہیں'),
(5103, 57, 28, 'اے وه لوگو جو ایمان ﻻئے ہو! اللہ سے ڈرتے رہا کرو اور اس کے رسول پر ایمان ﻻؤ اللہ تمہیں اپنی رحمت کا دوہرا حصہ دے گا اور تمہیں نور دے گا جس کی روشنی میں تم چلو پھرو گے اور تمہارے گناه بھی معاف فرمادے گا، اللہ بخشنے واﻻ مہربان ہے'),
(5104, 57, 29, 'یہ اس لیے کہ اہل کتاب جان لیں کہ اللہ کے فضل کے کسی حصہ پر بھی انہیں اختیار نہیں اور یہ کہ (سارا) فضل اللہ ہی کے ہاتھ ہے وه جسے چاہے دے، اور اللہ ہے ہی بڑے فضل واﻻ');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5105, 58, 1, 'یقیناً اللہ تعالیٰ نے اس عورت کی بات سنی جو تجھ سے اپنے شوہر کے بارے میں تکرار کر رہی تھی اور اللہ کے آگے شکایت کر رہی تھی، اللہ تعالیٰ تم دونوں کے سوال وجواب سن رہا تھا، بیشک اللہ تعالیٰ سننے دیکھنے واﻻ ہے'),
(5106, 58, 2, 'تم میں سے جو لوگ اپنی بیویوں سے ﻇہار کرتے ہیں (یعنی انہیں ماں کہہ بیٹھتے ہیں) وه دراصل ان کی مائیں نہیں بن جاتیں، ان کی مائیں تو وہی ہیں جن کے بطن سے وه پیدا ہوئے، یقیناً یہ لوگ ایک نامعقول اور جھوٹی بات کہتے ہیں۔ بیشک اللہ تعالیٰ معاف کرنے واﻻ اور بخشنے واﻻ ہے'),
(5107, 58, 3, 'جو لوگ اپنی بیویوں سے ﻇہار کریں پھر اپنی کہی ہوئی بات سے رجوع کرلیں تو ان کے ذمہ آپس میں ایک دوسرے کو ہاتھ لگانے سے پہلے ایک غلام آزاد کرنا ہے، اس کے ذریعہ تم نصیحت کیے جاتے ہو۔ اور اللہ تعالیٰ تمہارے تمام اعمال سے باخبر ہے'),
(5108, 58, 4, 'ہاں جو شخص نہ پائے اس کے ذمہ دو مہینوں کے لگاتار روزے ہیں اس سے پہلے کہ ایک دوسرے کو ہاتھ لگائیں اور جس شخص کو یہ طاقت بھی نہ ہو اس پر ساٹھ مسکینوں کا کھانا کھلانا ہے۔ یہ اس لیے کہ تم اللہ کی اور اس کے رسول کی حکم برداری کرو، یہ اللہ تعالیٰ کی مقرر کرده حدیں ہیں اور کفار ہی کے لیے درد ناک عذاب ہے'),
(5109, 58, 5, 'بیشک جو لوگ اللہ اور اس کے رسول کی مخالفت کرتے ہیں وه ذلیل کیے جائیں گے جیسے ان سے پہلے کے لوگ ذلیل کیے گئے تھے، اور بیشک ہم واضح آیتیں اتار چکے ہیں اور کافروں کے لیے تو ذلت واﻻ عذاب ہے'),
(5110, 58, 6, 'جس دن اللہ تعالیٰ ان سب کو اٹھائے گا پھر انہیں ان کے کیے ہوئے عمل سے آگاه کرے گا، جسے اللہ نے شمار رکھا ہے اور جسے یہ بھول گئے تھے، اور اللہ تعالیٰ ہر چیز سے واقف ہے'),
(5111, 58, 7, 'کیا تو نے نہیں دیکھا کہ اللہ آسمانوں کی اور زمین کی ہر چیز سے واقف ہے۔ تین آدمیوں کی سرگوشی نہیں ہوتی مگر اللہ ان کا چوتھا ہوتا ہے اور نہ پانچ کی مگر ان کا چھٹا وه ہوتا ہے اور نہ اس سے کم کی اور نہ زیاده کی مگر وه ساتھ ہی ہوتا ہے جہاں بھی وه ہوں، پھر قیامت کے دن انہیں ان کے اعمال سے آگاه کرے گا بیشک اللہ تعالیٰ ہر چیز سے واقف ہے'),
(5112, 58, 8, 'کیا تو نے ان لوگوں کو نہیں دیکھا؟ جنہیں کانا پھوسی سے روک دیا گیا تھا وه پھر بھی اس روکے ہوئے کام کو دوباره کرتے ہیں اور آپس میں گناه کی اور ﻇلم وزیادتی کی اور نافرمانیٴ پیغمبر کی سرگوشیاں کرتے ہیں، اور جب تیرے پاس آتے ہیں تو تجھے ان لفظوں میں سلام کرتے ہیں جن لفظوں میں اللہ تعالیٰ نے نہیں کہا اور اپنے دل میں کہتے ہیں کہ اللہ تعالیٰ ہمیں اس پر جو ہم کہتے ہیں سزا کیوں نہیں دیتا، ان کے لیے جہنم کافی (سزا) ہے جس میں یہ جائیں گے، سو وه برا ٹھکانا ہے'),
(5113, 58, 9, 'اے ایمان والو! تم جب سرگوشی کرو تو یہ سرگوشیاں گناه اور ﻇلم (زیادتی) اور نافرمانیٴ پیغمبر کی نہ ہوں، بلکہ نیکی اور پرہیزگاری کی باتوں پر سرگوشی کرو اور اس اللہ سے ڈرتے رہو جس کے پاس تم سب جمع کیے جاؤ گے'),
(5114, 58, 10, '(بری) سرگوشیاں، پس شیطانی کام ہے جس سے ایمان داروں کو رنج پہنچے۔ گو اللہ تعالیٰ کی اجازت کے بغیر وه انہیں کوئی نقصان نہیں پہنچا سکتا۔ اور ایمان والوں کو چاہئے کہ اللہ ہی پر بھروسہ رکھیں'),
(5115, 58, 11, 'اے مسلمانو! جب تم سے کہا جائے کہ مجلسوں میں ذرا کشادگی پیدا کرو تو تم جگہ کشاده کر دو اللہ تمہیں کشادگی دے گا، اور جب کہا جائے کہ اٹھ کھڑے ہو جاؤ تو تم اٹھ کھڑے ہو جاؤ اللہ تعالیٰ تم میں سے ان لوگوں کے جو ایمان ﻻئے ہیں اور جو علم دیئے گئے ہیں درجے بلند کر دے گا، اور اللہ تعالیٰ (ہر اس کام سے) جو تم کر رہے ہو (خوب) خبردار ہے'),
(5116, 58, 12, 'اے مسلمانو! جب تم رسول ﴿صلی اللہ علیہ وسلم﴾ سے سرگوشی کرنا چاہو تو اپنی سرگوشی سے پہلے کچھ صدقہ دے دیا کرو یہ تمہارے حق میں بہتر اور پاکیزه تر ہے، ہاں اگر نہ پاؤ تو بیشک اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(5117, 58, 13, 'کیا تم اپنی سرگوشی سے پہلے صدقہ نکالنے سے ڈر گئے؟ پس جب تم نے یہ نہ کیا اور اللہ تعالیٰ نے بھی تمہیں معاف فرما دیا تو اب (بخوبی) نمازوں کو قائم رکھو زکوٰة دیتے رہا کرو اور اللہ تعالیٰ کی اور اس کے رسول کی تابعداری کرتے رہو۔ تم جو کچھ کرتے ہو اس (سب) سے اللہ (خوب) خبردار ہے'),
(5118, 58, 14, 'کیا تو نے ان لوگوں کو نہیں دیکھا؟ جنہوں نے اس قوم سے دوستی کی جن پر اللہ غضبناک ہوچکا ہے، نہ یہ (منافق) تمہارے ہی ہیں نہ ان کے ہیں باوجود علم کے پھر بھی جھوٹ پر قسمیں کھا رہے ہیں'),
(5119, 58, 15, 'اللہ تعالیٰ نے ان کے لیے سخت عذاب تیار کر رکھا ہے، تحقیق جو کچھ یہ کر رہے ہیں برا کر رہے ہیں'),
(5120, 58, 16, 'ان لوگوں نے تو اپنی قسموں کو ڈھال بنا رکھا ہے اور لوگوں کو اللہ کی راه سے روکتے ہیں ان کے لیے رسوا کرنے واﻻ عذاب ہے'),
(5121, 58, 17, 'ان کے مال اور ان کی اوﻻد اللہ کے ہاں کچھ کام نہ آئیں گی۔ یہ تو جہنمی ہیں ہمیشہ ہی اس میں رہیں گے'),
(5122, 58, 18, 'جس دن اللہ تعالیٰ ان سب کو اٹھا کھڑا کرے گا تو یہ جس طرح تمہارے سامنے قسمیں کھاتے ہیں (اللہ تعالیٰ) کے سامنے بھی قسمیں کھانے لگیں گے اور سمجھیں گے کہ وه بھی کسی (دلیل) پر ہیں، یقین مانو کہ بیشک وہی جھوٹے ہیں'),
(5123, 58, 19, 'ان پر شیطان نے غلبہ حاصل کرلیا ہے، اور انہیں اللہ کا ذکر بھلا دیا ہے یہ شیطانی لشکر ہے۔ کوئی شک نہیں کہ شیطانی لشکر ہی خسارے واﻻ ہے'),
(5124, 58, 20, 'بیشک اللہ تعالیٰ کی اور اس کے رسول کی جو لوگ مخالفت کرتے ہیں وہی لوگ سب سے زیاده ذلیلوں میں ہیں'),
(5125, 58, 21, 'اللہ تعالیٰ لکھ چکا ہے کہ بیشک میں اور میرے پیغمبر غالب رہیں گے۔ یقیناً اللہ تعالیٰ زور آور اور غالب ہے'),
(5126, 58, 22, 'اللہ تعالیٰ پر اور قیامت کے دن پر ایمان رکھنے والوں کو آپ اللہ اور اس کے رسول کی مخالفت کرنے والوں سے محبت رکھتے ہوئے ہرگز نہ پائیں گے گو وه ان کے باپ یا ان کے بیٹے یا ان کے بھائی یا ان کے کنبہ (قبیلے) کے (عزیز) ہی کیوں نہ ہوں۔ یہی لوگ ہیں جن کے دلوں میں اللہ تعالیٰ نے ایمان کو لکھ دیا ہے اور جن کی تائید اپنی روح سے کی ہے اور جنہیں ان جنتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہیں جہاں یہ ہمیشہ رہیں گے، اللہ ان سے راضی ہے اور یہ اللہ سے خوش ہیں یہ خدائی لشکر ہے، آگاه رہو بیشک اللہ کے گروه والے ہی کامیاب لوگ ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5127, 59, 1, 'آسمانوں اور زمین کی ہر چیز اللہ تعالیٰ کی پاکی بیان کرتی ہے، اور وه غالب باحکمت ہے'),
(5128, 59, 2, 'وہی ہے جس نے اہل کتاب میں سے کافروں کو ان کے گھروں سے پہلے حشر کے وقت نکالا، تمہارا گمان (بھی) نہ تھا کہ وه نکلیں گے اور وه خود (بھی) سمجھ رہے تھے کہ ان کے (سنگین) قلعے انہیں اللہ (کے عذاب) سے بچا لیں گے پس ان پر اللہ (کا عذاب) ایسی جگہ سے آپڑا کہ انہیں گمان بھی نہ تھا اور ان کے دلوں میں اللہ نے رعب ڈال دیا وه اپنے گھروں کو اپنے ہی ہاتھوں اجاڑ رہے تھے اور مسلمانوں کے ہاتھوں (برباد کروا رہے تھے) پس اے آنکھوں والو! عبرت حاصل کرو'),
(5129, 59, 3, 'اور اگر اللہ تعالیٰ نے ان پر جلا وطنی کو مقدر نہ کر دیا ہوتا تو یقیناً انہیں دنیا ہی میں عذاب دیتا، اور آخرت میں (تو) ان کے لیے آگ کا عذاب ہے ہی'),
(5130, 59, 4, 'یہ اس لیے کہ انہوں نے اللہ تعالیٰ کی اور اس کے رسول کی مخالفت کی اور جو بھی اللہ کی مخالفت کرے گا تو اللہ تعالیٰ بھی سخت عذاب کرنے واﻻ ہے'),
(5131, 59, 5, 'تم نے کھجوروں کے جو درخت کاٹ ڈالے یا جنہیں تم نے ان کی جڑوں پر باقی رہنے دیا۔ یہ سب اللہ تعالیٰ کے فرمان سے تھا اور اس لیے بھی کہ فاسقوں کو اللہ تعالیٰ رسوا کرے'),
(5132, 59, 6, 'اور ان کا جو مال اللہ تعالیٰ نے اپنے رسول کے ہاتھ لگایا ہے جس پر نہ تو تم نے اپنے گھوڑے دوڑائے ہیں اور نہ اونٹ بلکہ اللہ تعالیٰ اپنے رسول کو جس پر چاہے غالب کر دیتا ہے، اور اللہ تعالیٰ ہر چیز پر قادر ہے'),
(5133, 59, 7, 'بستیوں والوں کا جو (مال) اللہ تعالیٰ تمہارے لڑے بھڑے بغیر اپنے رسول کے ہاتھ لگائے وه اللہ کا ہے اور رسول کا اور قرابت والوں کا اور یتیموں مسکینوں کا اور مسافروں کا ہے تاکہ تمہارے دولت مندوں کے ہاتھ میں ہی یہ مال گردش کرتا نہ ره جائے اور تمہیں جو کچھ رسول دے لے لو، اور جس سے روکے رک جاؤ اور اللہ تعالیٰ سے ڈرتے رہا کرو، یقیناً اللہ تعالیٰ سخت عذاب واﻻ ہے'),
(5134, 59, 8, '(فیء کا مال) ان مہاجر مسکینوں کے لیے ہے جو اپنے گھروں سے اور اپنے مالوں سے نکال دیئے گئے ہیں وه اللہ کے فضل اور اس کی رضامندی کے طلب گار ہیں اور اللہ تعالیٰ کی اور اس کے رسول کی مدد کرتے ہیں یہی راست باز لوگ ہیں'),
(5135, 59, 9, 'اور (ان کے لیے) جنہوں نے اس گھر میں (یعنی مدینہ) اور ایمان میں ان سے پہلے جگہ بنالی ہے اور اپنی طرف ہجرت کرکے آنے والوں سے محبت کرتے ہیں اور مہاجرین کو جو کچھ دے دیا جائے اس سے وه اپنے دلوں میں کوئی تنگی نہیں رکھتے بلکہ خود اپنے اوپر انہیں ترجیح دیتے ہیں گو خود کو کتنی ہی سخت حاجت ہو (بات یہ ہے) کہ جو بھی اپنے نفس کے بخل سے بچایا گیا وہی کامیاب (اور بامراد) ہے'),
(5136, 59, 10, 'اور (ان کے لیے) جو ان کے بعد آئیں جو کہیں گے کہ اے ہمارے پروردگار ہمیں بخش دے اور ہمارے ان بھائیوں کو بھی جو ہم سے پہلے ایمان ﻻچکے ہیں اور ایمان داروں کی طرف سے ہمارے دل میں کینہ (اور دشمنی) نہ ڈال، اے ہمارے رب بیشک تو شفقت ومہربانی کرنے واﻻ ہے'),
(5137, 59, 11, 'کیا تو نے منافقوں کو نہ دیکھا؟ کہ اپنے اہل کتاب کافر بھائیوں سے کہتے ہیں اگر تم جلا وطن کیے گئے تو ضرور بالضرور ہم بھی تمہارے ساتھ نکل کھڑے ہوں گے اور تمہارے بارے میں ہم کبھی بھی کسی کی بات نہ مانیں گے اور اگر تم سے جنگ کی جائے گی تو بخدا ہم تمہاری مدد کریں گے، لیکن اللہ تعالیٰ گواہی دیتا ہے کہ یہ قطعاً جھوٹے ہیں'),
(5138, 59, 12, 'اگر وه جلاوطن کیے گئے تو یہ ان کے ساتھ نہ جائیں گے اور اگر ان سے جنگ کی گئی تو یہ ان کی مدد (بھی) نہ کریں گے اور اگر (بالفرض) مدد پر آبھی گئے تو پیٹھ پھیر کر (بھاگ کھڑے) ہوں گے پھر مدد نہ کیے جائیں گے'),
(5139, 59, 13, '(مسلمانو! یقین مانو) کہ تمہاری ہیبت ان کے دلوں میں بہ نسبت اللہ کی ہیبت کے بہت زیاده ہے، یہ اس لیے کہ یہ بے سمجھ لوگ ہیں'),
(5140, 59, 14, 'یہ سب مل کر بھی تم سے لڑ نہیں سکتے ہاں یہ اور بات ہے کہ قلعہ بند مقامات میں ہوں یا دیواروں کی آڑ میں ہوں، ان کی لڑائی تو ان میں آپس میں ہی بہت سخت ہے گو آپ انہیں متحد سمجھ رہے ہیں لیکن ان کے دل دراصل ایک دوسرے سے جدا ہیں۔ اس لیے کہ یہ بےعقل لوگ ہیں'),
(5141, 59, 15, 'ان لوگوں کی طرح جو ان سے کچھ ہی پہلے گزرے ہیں جنہوں نے اپنے کام کا وبال چکھ لیا اور جن کے لیے المناک عذاب (تیار) ہے'),
(5142, 59, 16, 'شیطان کی طرح کہ اس نے انسان سے کہا کفر کر، جب وه کفر کر چکا تو کہنے لگا میں تو تجھ سے بری ہوں، میں تو اللہ رب العالمین سے ڈرتا ہوں'),
(5143, 59, 17, 'پس دونوں کا انجام یہ ہوا کہ آتش (دوزخ) میں ہمیشہ کے لیے گئے اور ﻇالموں کی یہی سزا ہے'),
(5144, 59, 18, 'اے ایمان والو! اللہ سے ڈرتے رہو اور ہر شخص دیکھ (بھال) لے کہ کل (قیامت) کے واسطے اس نے (اعمال کا) کیا (ذخیره) بھیجا ہے۔ اور (ہر وقت) اللہ سے ڈرتے رہو۔ اللہ تمہارے سب اعمال سے باخبر ہے'),
(5145, 59, 19, 'اور تم ان لوگوں کی طرح مت ہو جانا جنہوں نے اللہ (کے احکام) کو بھلا دیا تو اللہ نے بھی انہیں اپنی جانوں سے غافل کر دیا، اور ایسے ہی لوگ نافرمان (فاسق) ہوتے ہیں'),
(5146, 59, 20, 'اہل نار اور اہل جنت (باہم) برابر نہیں۔ جو اہل جنت ہیں وہی کامیاب ہیں (اور جو اہل نار ہیں وه ناکام ہیں)'),
(5147, 59, 21, 'اگر ہم اس قرآن کو کسی پہاڑ پر اتارتے تو تو دیکھتا کہ خوف الٰہی سے وه پست ہوکر ٹکڑے ٹکڑے ہو جاتا ہم ان مثالوں کو لوگوں کے سامنے بیان کرتے ہیں تاکہ وه غور وفکر کریں'),
(5148, 59, 22, 'وہی اللہ ہے جس کے سوا کوئی معبود نہیں، چھپے کھلے کا جاننے واﻻ مہربان اور رحم کرنے واﻻ'),
(5149, 59, 23, 'وہی اللہ ہے جس کے سوا کوئی معبود نہیں، بادشاه، نہایت پاک، سب عیبوں سے صاف، امن دینے واﻻ، نگہبان، غالب زورآور، اور بڑائی واﻻ، پاک ہے اللہ ان چیزوں سے جنہیں یہ اس کا شریک بناتے ہیں'),
(5150, 59, 24, 'وہی اللہ ہے پیدا کرنے واﻻ وجود بخشنے واﻻ، صورت بنانے واﻻ، اسی کے لیے (نہایت) اچھے نام ہیں، ہر چیز خواه وه آسمانوں میں ہو خواه زمین میں ہو اس کی پاکی بیان کرتی ہے، اور وہی غالب حکمت واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5151, 60, 1, 'اے وه لوگو جو ایمان ﻻئے ہو! میرے اور (خود) اپنے دشمنوں کو اپنا دوست نہ بناؤ تم تو دوستی سے ان کی طرف پیغام بھیجتے ہو اور وه اس حق کے ساتھ جو تمہارے پاس آچکا ہے کفر کرتے ہیں، پیغمبر کو اور خود تمہیں بھی محض اس وجہ سے جلاوطن کرتے ہیں کہ تم اپنے رب پر ایمان رکھتے ہو، اگر تم میری راه میں جہاد کے لیے اور میری رضا مندی کی طلب میں نکلتے ہو (تو ان سے دوستیاں نہ کرو)، تم ان کے پاس محبت کا پیغام پوشیده پوشیده بھیجتے ہو اور مجھے خوب معلوم ہے جو تم نے چھپایا اور وه بھی جو تم نے ﻇاہر کیا، تم میں سے جو بھی اس کام کو کرے گا وه یقیناً راه راست سے بہک جائے گا'),
(5152, 60, 2, 'اگر وه تم پر کہیں قابو پالیں تو وه تمہارے (کھلے) دشمن ہو جائیں اور برائی کے ساتھ تم پر دست درازی اور زبان درازی کرنے لگیں اور (دل سے) چاہنے لگیں کہ تم بھی کفر کرنے لگ جاؤ'),
(5153, 60, 3, 'تمہاری قرابتیں، رشتہداریاں، اور اوﻻد تمہیں قیامت کے دن کام نہ آئیں گی، اللہ تعالیٰ تمہارے درمیان فیصلہ کر دے گا اور جو کچھ تم کر رہے ہو اسے اللہ خوب دیکھ رہا ہے'),
(5154, 60, 4, '(مسلمانو!) تمہارے لیے حضرت ابراہیم میں اور ان کے ساتھیوں میں بہترین نمونہ ہے، جبکہ ان سب نے اپنی قوم سے برملا کہہ دیا کہ ہم تم سے اور جن جن کی تم اللہ کے سوا عبادت کرتے ہو ان سب سے بالکل بیزار ہیں۔ ہم تمہارے (عقائد کے) منکر ہیں جب تک تم اللہ کی وحدانیت پر ایمان نہ ﻻؤ ہم میں تم میں ہمیشہ کے لیے بغض وعداوت ﻇاہر ہوگئی لیکن ابراہیم کی اتنی بات تو اپنے باپ سے ہوئی تھی کہ میں تمہارے لیے استغفار ضرور کروں گا اور تمہارے لیے مجھے اللہ کے سامنے کسی چیز کا اختیار کچھ بھی نہیں۔ اے ہمارے پروردگار تجھی پر ہم نے بھروسہ کیا ہے اور تیری ہی طرف رجوع کرتے ہیں اور تیری ہی طرف لوٹنا ہے'),
(5155, 60, 5, 'اے ہمارے رب! تو ہمیں کافروں کی آزمائش میں نہ ڈال اور اے ہمارے پالنے والے ہماری خطاؤں کو بخش دے، بیشک تو ہی غالب، حکمت واﻻ ہے'),
(5156, 60, 6, 'یقیناً تمہارے لیے ان میں اچھا نمونہ (اور عمده پیروی ہے خاص کر) ہر اس شخص کے لیے جو اللہ کی اور قیامت کے دن کی ملاقات کی امید رکھتا ہو، اور اگر کوئی روگردانی کرے تو اللہ تعالیٰ بالکل بےنیاز ہے اور سزا وار حمد وﺛنا ہے'),
(5157, 60, 7, 'کیا عجب کہ عنقریب ہی اللہ تعالیٰ تم میں اور تمہارے دشمنوں میں محبت پیدا کر دے۔ اللہ کو سب قدرتیں ہیں اور اللہ (بڑا) غفور رحیم ہے'),
(5158, 60, 8, 'جن لوگوں نے تم سے دین کے بارے میں لڑائی نہیں لڑی اور تمہیں جلاوطن نہیں کیا ان کے ساتھ سلوک واحسان کرنے اور منصفانہ بھلے برتاؤ کرنے سے اللہ تعالیٰ تمہیں نہیں روکتا، بلکہ اللہ تعالیٰ تو انصاف کرنے والوں سے محبت کرتا ہے'),
(5159, 60, 9, 'اللہ تعالیٰ تمہیں صرف ان لوگوں کی محبت سے روکتا ہے جنہوں نے تم سے دین کے بارے میں لڑائیاں لڑیں اور تمہیں شہر سے نکال دیئے اور شہر سے نکالنے والوں کی مدد کی جو لوگ ایسے کفار سے محبت کریں وه (قطعاً) ﻇالم ہیں'),
(5160, 60, 10, 'اے ایمان والو! جب تمہارے پاس مومن عورتیں ہجرت کرکے آئیں تو تم ان کا امتحان لو۔ دراصل ان کے ایمان کو بخوبی جاننے واﻻ تو اللہ ہی ہے لیکن اگر وه تمہیں ایمان والیاں معلوم ہوں تو اب تم انہیں کافروں کی طرف واپس نہ کرو، یہ ان کے لیے حلال نہیں اور نہ وه ان کے لیے حلال ہیں، اور جو خرچ ان کافروں کا ہوا ہو وه انہیں ادا کردو، ان عورتوں کو ان کے مہر دے کر ان سے نکاح کر لینے میں تم پر کوئی گناه نہیں اور کافر عورتوں کی ناموس اپنے قبضہ میں نہ رکھو اور جو کچھ تم نے خرچ کیا ہو، مانگ لو اور جو کچھ ان کافروں نے خرچ کیا ہو وه بھی مانگ لیں یہ اللہ کا فیصلہ ہے جو تمہارے درمیان کر رہا ہے، اللہ تعالیٰ بڑے علم (اور) حکمت واﻻ ہے'),
(5161, 60, 11, 'اور اگر تمہاری کوئی بیوی تمہارے ہاتھ سے نکل جائے اور کافروں کے پاس چلی جائے پھر تمہیں اس کے بدلے کا وقت مل جائے تو جن کی بیویاں چلی گئی ہیں انہیں ان کے اخراجات کے برابر ادا کر دو، اور اس اللہ تعالیٰ سے ڈرتے رہو جس پر تم ایمان رکھتے ہو'),
(5162, 60, 12, 'اے پیغمبر! جب مسلمان عورتیں آپ سے ان باتوں پر بیعت کرنے آئیں کہ وه اللہ کے ساتھ کسی کو شریک نہ کریں گی، چوری نہ کریں گی، زنا کاری نہ کریں گی، اپنی اوﻻد کو نہ مار ڈالیں گی اور کوئی ایسا بہتان نہ باندھیں گی جو خود اپنے ہاتھوں پیروں کے سامنے گھڑ لیں اور کسی نیک کام میں تیری بےحکمی نہ کریں گی تو آپ ان سے بیعت کر لیا کریں، اور ان کے لیے اللہ سے مغفرت طلب کریں بیشک اللہ تعالیٰ بخشنے اور معاف کرنے واﻻ ہے'),
(5163, 60, 13, 'اے مسلمانو! تم اس قوم سے دوستی نہ رکھو جن پر اللہ کا غضب نازل ہوچکا ہے جو آخرت سے اس طرح مایوس ہوچکے ہیں جیسے کہ مرده اہل قبر سے کافر ناامید ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5164, 61, 1, 'زمین وآسمان کی ہر ہر چیز اللہ تعالیٰ کی پاکی بیان کرتی ہے اور وہی غالب حکمت واﻻ ہے'),
(5165, 61, 2, 'اے ایمان والو! تم وه بات کیوں کہتے ہو جو کرتے نہیں'),
(5166, 61, 3, 'تم جو کرتے نہیں اس کا کہنا اللہ تعالیٰ کو سخت ناپسند ہے'),
(5167, 61, 4, 'بیشک اللہ تعالیٰ ان لوگوں سے محبت کرتا ہے جو اس کی راه میں صف بستہ جہاد کرتے ہیں گویا وه سیسہ پلائی ہوئی عمارت ہیں'),
(5168, 61, 5, 'اور (یاد کرو) جبکہ موسیٰ نے اپنی قوم سے کہا اے میری قوم کے لوگو! تم مجھے کیوں ستا رہے ہو حاﻻنکہ تمہیں (بخوبی) معلوم ہے کہ میں تمہاری جانب اللہ کا رسول ہوں پس جب وه لوگ ٹیڑھے ہی رہے تو اللہ نے ان کے دلوں کو (اور) ٹیڑھا کر دیا، اور اللہ تعالیٰ نافرمان قوم کو ہدایت نہیں دیتا'),
(5169, 61, 6, 'اور جب مریم کے بیٹے عیسیٰ نے کہا اے (میری قوم)، بنی اسرائیل! میں تم سب کی طرف اللہ کا رسول ہوں مجھ سے پہلے کی کتاب تورات کی میں تصدیق کرنے واﻻ ہوں اور اپنے بعد آنے والے ایک رسول کی میں تمہیں خوشخبری سنانے واﻻ ہوں جنکا نام احمد ہے۔ پھر جب وه ان کے پاس کھلی دلیلیں ﻻئے تو یہ کہنے لگے، یہ تو کھلا جادو ہے'),
(5170, 61, 7, 'اس شخص سے زیاده ﻇالم اور کون ہوگا جو اللہ پر جھوٹ (افترا) باندھے حاﻻنکہ وه اسلام کی طرف بلایا جاتا ہے اور اللہ ایسے ﻇالموں کو ہدایت نہیں کرتا'),
(5171, 61, 8, 'وه چاہتے ہیں کہ اللہ کے نور کو اپنے منھ سے بجھا دیں اور اللہ اپنے نور کو کمال تک پہنچانے واﻻ ہے گو کافر برا مانیں'),
(5172, 61, 9, 'وہی ہے جس نے اپنے رسول کو ہدایت اور سچا دین دے کر بھیجا تاکہ اسے اور تمام مذاہب پر غالب کر دے اگرچہ مشرکین ناخوش ہوں'),
(5173, 61, 10, 'اے ایمان والو! کیا میں تمہیں وه تجارت بتلا دوں جو تمہیں درد ناک عذاب سے بچا لے؟'),
(5174, 61, 11, 'اللہ تعالیٰ پر اور اس کے رسول پر ایمان ﻻؤ اور اللہ کی راه میں اپنے مال اور اپنی جانوں سے جہاد کرو۔ یہ تمہارے لیے بہتر ہے اگر تم میں علم ہو'),
(5175, 61, 12, 'اللہ تعالیٰ تمہارے گناه معاف فرما دے گا اور تمہیں ان جنتوں میں پہنچائے گا جن کے نیچے نہریں جاری ہوں گی اور صاف ستھرے گھروں میں جو جنت عدن میں ہوں گے، یہ بہت بڑی کامیابی ہے'),
(5176, 61, 13, 'اور تمہیں ایک دوسری (نعمت) بھی دے گا جسے تم چاہتے ہو وه اللہ کی مدد اور جلد فتح یابی ہے، ایمان والوں کو خوشخبری دے دو'),
(5177, 61, 14, 'اے ایمان والو! تم اللہ تعالیٰ کے مددگار بن جاؤ۔ جس طرح حضرت مریم کے بیٹے حضرت عیسیٰ نے حواریوں سے فرمایا کہ کون ہے جو اللہ کی راه میں میرا مددگار بنے؟ حواریوں نے کہا ہم اللہ کی راه میں مددگار ہیں، پس بنی اسرائیل میں سے ایک جماعت تو ایمان ﻻئی اور ایک جماعت نے کفر کیا تو ہم نے مومنوں کی انکے دشمنوں کے مقابلہ میں مدد کی پس وه غالب آ گئے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5178, 62, 1, '(ساری چیزیں) جو آسمانوں اور زمین میں ہیں اللہ تعالیٰ کی پاکی بیان کرتی ہیں (جو) بادشاه نہایت پاک (ہے) غالب وباحکمت ہے'),
(5179, 62, 2, 'وہی ہے جس نے ناخوانده لوگوں میں ان ہی میں سے ایک رسول بھیجا جو انہیں اس کی آیتیں پڑھ کر سناتا ہے اور ان کو پاک کرتا ہے اور انہیں کتاب وحکمت سکھاتا ہے۔ یقیناً یہ اس سے پہلے کھلی گمراہی میں تھے'),
(5180, 62, 3, 'اور دوسروں کے لیے بھی انہیں میں سے جو اب تک ان سے نہیں ملے۔ اور وہی غالب باحکمت ہے'),
(5181, 62, 4, 'یہ اللہ کا فضل ہے جسے چاہے اپنا فضل دے اور اللہ تعالیٰ بہت بڑے فضل کا مالک ہے'),
(5182, 62, 5, 'جن لوگوں کو تورات پر عمل کرنے کا حکم دیا گیا پھر انہوں نے اس پر عمل نہیں کیا ان کی مثال اس گدھے کی سی ہے جو بہت سی کتابیں ﻻدے ہو۔ اللہ کی باتوں کو جھٹلانے والوں کی بڑی بری مثال ہے اور اللہ (ایسے) ﻇالم قوم کو ہدایت نہیں دیتا'),
(5183, 62, 6, 'کہہ دیجئے کہ اے یہودیو! اگر تمہارا دعویٰ ہے کہ تم اللہ کے دوست ہو دوسرے لوگوں کے سوا تو تم موت کی تمنا کرو اگر تم سچے ہو'),
(5184, 62, 7, 'یہ کبھی بھی موت کی تمنا نہ کریں گے بوجہ ان اعمال کے جو اپنے آگے اپنے ہاتھوں بھیج رکھے ہیں اور اللہ ﻇالموں کو خوب جانتا ہے'),
(5185, 62, 8, 'کہہ دیجئے! کہ جس موت سے تم بھاگتے پھرتے ہو وه تو تمہیں پہنچ کر رہے گی پھر تم سب چھپے کھلے کے جاننے والے (اللہ) کی طرف لوٹائے جاؤ گے اور وه تمہیں تمہارے کیے ہوئے تمام کام بتلا دے گا'),
(5186, 62, 9, 'اے وه لوگو جو ایمان ﻻئے ہو! جمعہ کے دن نماز کی اذان دی جائے تو تم اللہ کے ذکر کی طرف دوڑ پڑو اور خرید وفروخت چھوڑ دو۔ یہ تمہارے حق میں بہت ہی بہتر ہے اگر تم جانتے ہو'),
(5187, 62, 10, 'پھر جب نماز ہو چکے تو زمین میں پھیل جاؤ اور اللہ کا فضل تلاش کرو اور بکثرت اللہ کا ذکر کیا کرو تاکہ تم فلاح پالو'),
(5188, 62, 11, 'اور جب کوئی سودا بکتا دیکھیں یا کوئی تماشا نظر آجائے تو اس کی طرف دوڑ جاتے ہیں اور آپ کو کھڑا ہی چھوڑ دیتے ہیں۔ آپ کہہ دیجئے کہ اللہ کے پاس جو ہے وه کھیل اور تجارت سے بہتر ہے۔ اور اللہ تعالیٰ بہترین روزی رساں ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5189, 63, 1, 'تیرے پاس جب منافق آتے ہیں تو کہتے ہیں کہ ہم اس بات کے گواه ہیں کہ بیشک آپ اللہ کے رسول ہیں، اور اللہ جانتا ہے کہ یقیناً آپ اس کے رسول ہیں۔ اور اللہ گواہی دیتا ہے کہ یہ منافق قطعاً جھوٹے ہیں'),
(5190, 63, 2, 'انہوں نے اپنی قسموں کو ڈھال بنا رکھا ہے پس اللہ کی راه سے رک گئے بیشک برا ہے وه کام جو یہ کر رہے ہیں'),
(5191, 63, 3, 'یہ اس سبب سے ہے کہ یہ ایمان ﻻکر پھر کافر ہوگئے پس ان کے دلوں پر مہر کر دی گئی۔ اب یہ نہیں سمجھتے'),
(5192, 63, 4, 'جب آپ انہیں دیکھ لیں تو ان کے جسم آپ کو خوشنما معلوم ہوں، یہ جب باتیں کرنے لگیں تو آپ ان کی باتوں پر (اپنا) کان لگائیں، گویا کہ یہ لکڑیاں ہیں دیوار کے سہارے سے لگائی ہوئیں، ہر (سخت) آواز کو اپنے خلاف سمجھتے ہیں۔ یہی حقیقی دشمن ہیں ان سے بچو اللہ انہیں غارت کرے کہاں سے پھرے جاتے ہیں'),
(5193, 63, 5, 'اور جب ان سے کہا جاتا ہے کہ آؤ تمہارے لیے اللہ کے رسول استغفار کریں تو اپنے سر مٹکاتے ہیں اور آپ دیکھیں گے کہ وه تکبر کرتے ہوئے رک جاتے ہیں'),
(5194, 63, 6, 'ان کے حق میں آپ کا استغفار کرنا اور نہ کرنا دونوں برابر ہے۔ اللہ تعالیٰ انہیں ہر گز نہ بخشے گا۔ بیشک اللہ تعالیٰ (ایسے) نافرمان لوگوں کو ہدایت نہیں دیتا'),
(5195, 63, 7, 'یہی وه ہیں جو کہتے ہیں کہ جو لوگ رسول اللہ کے پاس ہیں ان پر کچھ خرچ نہ کرو یہاں تک کہ وه ادھر ادھر ہو جائیں اور آسمان وزمین کے کل خزانے اللہ تعالیٰ کی ملکیت ہیں لیکن یہ منافق بے سمجھ ہیں'),
(5196, 63, 8, 'یہ کہتے ہیں کہ اگر ہم اب لوٹ کر مدینہ جائیں گے تو عزت واﻻ وہاں سے ذلت والے کو نکال دے گا۔ سنو! عزت تو صرف اللہ تعالیٰ کے لیے اور اس کے رسول کے لیے اور ایمان داروں کے لیے ہے لیکن یہ منافق جانتے نہیں'),
(5197, 63, 9, 'اے مسلمانو! تمہارے مال اور تمہاری اوﻻد تمہیں اللہ کے ذکر سے غافل نہ کر دیں۔ اور جو ایسا کریں وه بڑے ہی زیاں کار لوگ ہیں'),
(5198, 63, 10, 'اور جو کچھ ہم نے تمہیں دے رکھا ہے اس میں سے (ہماری راه میں) اس سے پہلے خرچ کرو کہ تم میں سے کسی کو موت آ جائے تو کہنے لگے اے میرے پروردگار! مجھے تو تھوڑی دیر کی مہلت کیوں نہیں دیتا؟ کہ میں صدقہ کروں اور نیک لوگوں میں سے ہو جاؤں'),
(5199, 63, 11, 'اور جب کسی کا مقرره وقت آجاتا ہے پھر اسے اللہ تعالیٰ ہر گز مہلت نہیں دیتا اور جو کچھ تم کرتے ہو اس سے اللہ تعالیٰ بخوبی باخبر ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5200, 64, 1, '(تمام چیزیں) جو آسمانوں اور زمین میں ہیں اللہ کی پاکی بیان کرتی ہیں اسی کی سلطنت ہے اور اسی کی تعریف ہے، اور وه ہر ہر چیز پر قادر ہے'),
(5201, 64, 2, 'اسی نے تمہیں پیدا کیا ہے سو تم میں سے بعضے تو کافر ہیں اور بعض ایمان والے ہیں، اور جو کچھ تم کر رہے ہو اللہ تعالیٰ خوب دیکھ رہا ہے'),
(5202, 64, 3, 'اسی نے آسمانوں کو اور زمین کو عدل وحکمت سے پیدا کیا، اسی نے تمہاری صورتیں بنائیں اور بہت اچھی بنائیں اور اسی کی طرف لوٹنا ہے'),
(5203, 64, 4, 'وه آسمان وزمین کی ہر ہر چیز کا علم رکھتا ہے اور جو کچھ تم چھپاؤ اور جو ﻇاہر کرو وه (سب کو) جانتا ہے۔ اللہ تو سینوں کی باتوں تک کو جاننے واﻻ ہے'),
(5204, 64, 5, 'کیا تمہارے پاس اس سے پہلے کے کافروں کی خبر نہیں پہنچی؟ جنہوں نے اپنے اعمال کا وبال چکھ لیا اور جن کے لیے دردناک عذاب ہے'),
(5205, 64, 6, 'یہ اس لیے کہ ان کے پاس ان کے رسول واضح دﻻئل لے کر آئے تو انہوں نے کہہ دیا کہ کیا انسان ہماری رہنمائی کرے گا؟ پس انکار کر دیا اور منھ پھیر لیا اور اللہ نے بھی بےنیازی کی، اور اللہ تو ہے ہی بہت بےنیاز سب خوبیوں واﻻ'),
(5206, 64, 7, 'ان کافروں نے خیال کیا ہے کہ دوباره زنده نہ کیے جائیں گے۔ آپ کہہ دیجئے کہ کیوں نہیں اللہ کی قسم! تم ضرور دوباره اٹھائے جاؤ گے پھر جو تم نے کیا ہے اس کی خبر دیئے جاؤ گے اور اللہ پر یہ بالکل ہی آسان ہے'),
(5207, 64, 8, 'سو تم اللہ پر اور اس کے رسول پر اور اس نور پر جسے ہم نے نازل فرمایا ہے ایمان ﻻؤ اور اللہ تعالیٰ تمہارے ہر عمل پر باخبر ہے'),
(5208, 64, 9, 'جس دن تم سب کو اس جمع ہونے کے دن جمع کرے گا وہی دن ہے ہار جیت کا اور جو شخص اللہ پر ایمان ﻻکر نیک عمل کرے اللہ اس سے اس کی برائیاں دور کر دے گا اور اسے جنتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہیں جن میں وه ہمیشہ ہمیشہ رہیں گے۔ یہی بہت بڑی کامیابی ہے'),
(5209, 64, 10, 'اور جن لوگوں نے کفر کیا اور ہماری آیتوں کو جھٹلایا وہی (سب) جہنمی ہیں (جو) جہنم میں ہمیشہ رہیں گے، وه بہت برا ٹھکانا ہے'),
(5210, 64, 11, 'کوئی مصیبت اللہ کی اجازت کے بغیر نہیں پہنچ سکتی، جو اللہ پر ایمان ﻻئے اللہ اس کے دل کو ہدایت دیتا ہے اور اللہ ہر چیز کو خوب جاننے واﻻ ہے'),
(5211, 64, 12, '(لوگو) اللہ کا کہنا مانو اور رسول کا کہنا مانو۔ پس اگر تم اعراض کرو تو ہمارے رسول کے ذمہ صرف صاف صاف پہنچا دینا ہے'),
(5212, 64, 13, 'اللہ کے سوا کوئی معبود برحق نہیں اور مومنوں کو اللہ ہی پر توکل رکھنا چاہئے'),
(5213, 64, 14, 'اے ایمان والو! تمہاری بعض بیویاں اور بعض بچے تمہارے دشمن ہیں پس ان سے ہوشیار رہنا اور اگر تم معاف کر دو اور درگزر کر جاؤ اور بخش دو تو اللہ تعالیٰ بخشنے واﻻ مہربان ہے'),
(5214, 64, 15, 'تمہارے مال اور اوﻻد تو سراسر تمہاری آزمائش ہیں۔ اور بہت بڑا اجر اللہ کے پاس ہے'),
(5215, 64, 16, 'پس جہاں تک تم سے ہوسکے اللہ سے ڈرتے رہو اور سنتے اور مانتے چلے جاؤ اور اللہ کی راه میں خیرات کرتے رہو جو تمہارے لیے بہتر ہے اور جو شخص اپنے نفس کی حرص سے محفوظ رکھا جائے وہی کامیاب ہے'),
(5216, 64, 17, 'اگر تم اللہ کو اچھا قرض دو گے (یعنی اس کی راه میں خرچ کرو گے) تو وه اسے تمہارے لیے بڑھاتا جائے گا اور تمہارے گناه بھی معاف فرما دے گا۔ اللہ بڑا قدر دان بڑا بردبار ہے'),
(5217, 64, 18, 'وه پوشیده اور ﻇاہر کا جاننے واﻻ ہے زبردست حکمت واﻻ (ہے)');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5218, 65, 1, 'اے نبی! (اپنی امت سے کہو کہ) جب تم اپنی بیویوں کو طلاق دینا چاہو تو ان کی عدت (کے دنوں کے آغاز) میں انہیں طلاق دو اور عدت کا حساب رکھو، اور اللہ سے جو تمہارا پروردگار ہے ڈرتے رہو، نہ تم انہیں ان کے گھروں سے نکالو اور نہ وه (خود) نکلیں ہاں یہ اور بات ہے کہ وه کھلی برائی کر بیٹھیں، یہ اللہ کی مقرر کرده حدیں ہیں، جو شخص اللہ کی حدوں سے آگے بڑھ جائے اس نے یقیناً اپنے اوپر ﻇلم کیا، تم نہیں جانتے شاید اس کے بعد اللہ تعالیٰ کوئی نئی بات پیدا کر دے'),
(5219, 65, 2, 'پس جب یہ عورتیں اپنی عدت پوری کرنے کے قریب پہنچ جائیں تو انہیں یا تو قاعده کے مطابق اپنے نکاح میں رہنے دو یا دستور کے مطابق انہیں الگ کر دو اور آپس میں سے دو عادل شخصوں کو گواه کر لو اور اللہ کی رضامندی کے لیے ٹھیک ٹھیک گواہی دو۔ یہی ہے وه جس کی نصیحت اسے کی جاتی ہے جو اللہ پر اور قیامت کے دن پر ایمان رکھتا ہو اور جو شخص اللہ سے ڈرتا ہے اللہ اس کے لیے چھٹکارے کی شکل نکال دیتا ہے'),
(5220, 65, 3, 'اور اسے ایسی جگہ سے روزی دیتا ہے جس کا اسے گمان بھی نہ ہو اور جو شخص اللہ پر توکل کرے گا اللہ اسے کافی ہوگا۔ اللہ تعالیٰ اپنا کام پورا کرکے ہی رہے گا۔ اللہ تعالیٰ نے ہر چیز کا ایک اندازه مقرر کر رکھا ہے'),
(5221, 65, 4, 'تمہاری عورتوں میں سے جو عورتیں حیض سے ناامید ہو گئی ہوں، اگر تمہیں شبہ ہو تو ان کی عدت تین مہینے ہے اور ان کی بھی جنہیں حیض آنا شروع ہی نہ ہوا ہو اور حاملہ عورتوں کی عدت ان کے وضع حمل ہے اور جو شخص اللہ تعالیٰ سے ڈرے گا اللہ اس کے (ہر) کام میں آسانی کر دے گا'),
(5222, 65, 5, 'یہ اللہ کا حکم ہے جو اس نے تمہاری طرف اتارا ہے اور جو شخص اللہ سے ڈرے گا اللہ اس کے گناه مٹا دے گا اور اسے بڑا بھاری اجر دے گا'),
(5223, 65, 6, 'تم اپنی طاقت کے مطابق جہاں تم رہتے ہو وہاں ان (طلاق والی) عورتوں کو رکھو اور انہیں تنگ کرنے کے لیے تکلیف نہ پہنچاؤ اور اگر وه حمل سے ہوں تو جب تک بچہ پیدا ہولے انہیں خرچ دیتے رہا کرو پھر اگر تمہارے کہنے سے وہی دودھ پلائیں تو تم انہیں ان کی اجرت دے دو اور باہم مناسب طور پر مشوره کر لیا کرو اور اگر تم آپس میں کشمکش کرو تو اس کے کہنے سے کوئی اور دودھ پلائے گی'),
(5224, 65, 7, 'کشادگی والے کو اپنی کشادگی سے خرچ کرنا چاہئے اور جس پر اس کے رزق کی تنگی کی گئی ہو اسے چاہئے کہ جو کچھ اللہ تعالیٰ نے اسے دے رکھا ہے اسی میں سے (اپنی حسب حیثیت) دے، کسی شخص کو اللہ تکلیف نہیں دیتا مگر اتنی ہی جتنی طاقت اسے دے رکھی ہے، اللہ تنگی کے بعد آسانی وفراغت بھی کر دے گا'),
(5225, 65, 8, 'اور بہت سی بستی والوں نے اپنے رب کے حکم سے اور اس کے رسولوں سے سرتابی کی تو ہم نے بھی ان سے سخت حساب کیا اور انہیں عذاب دیا ان دیکھا (سخت) عذاب'),
(5226, 65, 9, 'پس انہوں نے اپنے کرتوت کا مزه چکھ لیا اور انجام کار ان کا خساره ہی ہوا'),
(5227, 65, 10, 'ان کے لیے اللہ تعالیٰ نے سخت عذاب تیار کر رکھا ہے، پس اللہ سے ڈرو اے عقل مند ایمان والو۔ یقیناً اللہ نے تمہاری طرف نصیحت اتار دی ہے'),
(5228, 65, 11, '(یعنی) رسول جو تمہیں اللہ کے صاف صاف احکام پڑھ سناتا ہے تاکہ ان لوگوں کو جو ایمان ﻻئیں اور نیک عمل کریں وه تاریکیوں سے روشنی کی طرف لے آئے، اور جو شخص اللہ پر ایمان ﻻئے اور نیک عمل کرے اللہ اسے ایسی جنتوں میں داخل کرے گا جس کے نیچے نہریں جاری ہیں جن میں یہ ہمیشہ ہمیشہ رہیں گے۔ بیشک اللہ نے اسے بہترین روزی دے رکھی ہے'),
(5229, 65, 12, 'اللہ وه ہے جس نے سات آسمان بنائے اور اسی کے مثل زمینیں بھی۔ اس کا حکم ان کے درمیان اترتا ہے تاکہ تم جان لو کہ اللہ ہر چیز پر قادر ہے۔ اور اللہ تعالیٰ نے ہر چیز کو بہ اعتبار علم گھیر رکھا ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5230, 66, 1, 'اے نبی! جس چیز کو اللہ نے آپ کے لیے حلال کر دیا ہے اسے آپ کیوں حرام کرتے ہیں؟ (کیا) آپ اپنی بیویوں کی رضامندی حاصل کرنا چاہتے ہیں اور اللہ بخشنے واﻻ رحم کرنے واﻻ ہے'),
(5231, 66, 2, 'تحقیق کہ اللہ تعالیٰ نے تمہارے لیے قسموں کو کھول ڈالنا مقرر کر دیا ہے اور اللہ تمہارا کارساز ہے اور وہی (پورے) علم واﻻ، حکمت واﻻ ہے'),
(5232, 66, 3, 'اور یاد کر جب نبی نے اپنی بعض عورتوں سے ایک پوشیده بات کہی، پس جب اس نے اس بات کی خبر کر دی اور اللہ نے اپنے نبی کو اس پر آگاه کر دیا تو نبی نے تھوڑی سی بات تو بتا دی اور تھوڑی سی ٹال گئے، پھر جب نبی نے اپنی اس بیوی کو یہ بات بتائی تو وه کہنے لگی اس کی خبر آپ کو کس نے دی۔ کہا سب جاننے والے پوری خبر رکھنے والے اللہ نے مجھے یہ بتلایا ہے'),
(5233, 66, 4, '(اے نبی کی دونوں بیویو!) اگر تم دونوں اللہ کے سامنے توبہ کرلو (تو بہت بہتر ہے) یقیناً تمہارے دل جھک پڑے ہیں اور اگر تم نبی کے خلاف ایک دوسرے کی مدد کرو گی پس یقیناً اس کا کارساز اللہ ہے اور جبریل ہیں اور نیک اہل ایمان اور ان کے علاوه فرشتے بھی مدد کرنے والے ہیں'),
(5234, 66, 5, 'اگر وه (پیغمبر) تمہیں طلاق دے دیں تو بہت جلد انہیں ان کا رب! تمہارے بدلے تم سے بہتر بیویاں عنایت فرمائے گا، جو اسلام والیاں، ایمان والیاں اللہ کے حضور جھکنے والیاں توبہ کرنے والیاں، عبادت بجا ﻻنے والیاں روزے رکھنے والیاں ہوں گی بیوه اور کنواریاں'),
(5235, 66, 6, 'اے ایمان والو! تم اپنے آپ کو اور اپنے گھر والوں کو اس آگ سے بچاؤ جس کا ایندھن انسان ہیں اور پتھر جس پر سخت دل مضبوط فرشتے مقرر ہیں جنہیں جو حکم اللہ تعالیٰ دیتا ہے اس کی نافرمانی نہیں کرتے بلکہ جو حکم دیا جائے بجا ﻻتے ہیں'),
(5236, 66, 7, 'اے کافرو! آج تم عذر وبہانہ مت کرو۔ تمہیں صرف تمہارے کرتوت کا بدلہ دیا جارہا ہے۔'),
(5237, 66, 8, 'اے ایمان والو! تم اللہ کے سامنے سچی خالص توبہ کرو۔ قریب ہے کہ تمہارا رب تمہارے گناه دور کر دے اور تمہیں ایسی جنتوں میں داخل کرے جن کے نیچے نہریں جاری ہیں۔ جس دن اللہ تعالیٰ نبی کو اور ایمان والوں کو جو ان کے ساتھ ہیں رسوا نہ کرے گا۔ ان کا نور ان کے سامنے اور ان کے دائیں دوڑ رہا ہوگا۔ یہ دعائیں کرتے ہوں گے اے ہمارے رب ہمیں کامل نور عطا فرما اور ہمیں بخش دے یقیناً تو ہر چیز پر قادر ہے'),
(5238, 66, 9, 'اے نبی! کافروں اور منافقوں سے جہاد کرو اور ان پر سختی کرو ان کا ٹھکانا جہنم ہے اور وه بہت بری جگہ ہے'),
(5239, 66, 10, 'اللہ تعالیٰ نے کافروں کے لیے نوح کی اور لوط کی بیوی کی مثال بیان فرمائی یہ دونوں ہمارے بندوں میں سے دو (شائستہ اور) نیک بندوں کے گھر میں تھیں، پھر ان کی انہوں نے خیانت کی پس وه دونوں (نیک بندے) ان سے اللہ کے (کسی عذاب کو) نہ روک سکے اور حکم دے دیا گیا (اے عورتوں) دوزخ میں جانے والوں کے ساتھ تم دونوں بھی چلی جاؤ'),
(5240, 66, 11, 'اور اللہ تعالیٰ نے ایمان والوں کے لیے فرعون کی بیوی کی مثال بیان فرمائی جبکہ اس نے دعا کی کہ اے میرے رب! میرے لیے اپنے پاس جنت میں مکان بنا اور مجھے فرعون سے اور اس کے عمل سے بچا اور مجھے ﻇالم لوگوں سے خلاصی دے'),
(5241, 66, 12, 'اور (مثال بیان فرمائی) مریم بنت عمران کی جس نے اپنے ناموس کی حفاﻇت کی پھر ہم نے اپنی طرف سے اس میں جان پھونک دی اور (مریم) اس نے اپنے رب کی باتوں اور اس کی کتابوں کی تصدیق کی اور عبادت گزاروں میں سے تھی');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5242, 67, 1, 'بہت بابرکت ہے وه (اللہ) جس کے ہاتھ میں بادشاہی ہے اور جو ہر چیز پر قدرت رکھنے واﻻ ہے'),
(5243, 67, 2, 'جس نے موت اور حیات کو اس لیے پیدا کیاکہ تمہیں آزمائے کہ تم میں سے اچھے کام کون کرتا ہے، اور وه غالب (اور) بخشنے واﻻ ہے'),
(5244, 67, 3, 'جس نے سات آسمان اوپر تلے بنائے۔ (تو اے دیکھنے والے) اللہ رحمٰن کی پیدائش میں کوئی بے ضابطگی نہ دیکھے گا، دوباره (نظریں ڈال کر) دیکھ لے کیا کوئی شگاف بھی نظر آرہا ہے'),
(5245, 67, 4, 'پھر دوہرا کر دو دو بار دیکھ لے تیری نگاه تیری طرف ذلیل (و عاجز) ہو کر تھکی ہوئی لوٹ آئے گی'),
(5246, 67, 5, 'بیشک ہم نے آسمان دنیا کو چراغوں (ستاروں) سے آراستہ کیا اور انہیں شیطانوں کے مارنے کا ذریعہ بنا دیا اور شیطانوں کے لیے ہم نے (دوزخ کا جلانے واﻻ) عذاب تیار کر دیا'),
(5247, 67, 6, 'اور اپنے رب کے ساتھ کفر کرنے والوں کے لیے جہنم کا عذاب ہے اور وه کیا ہی بری جگہ ہے'),
(5248, 67, 7, 'جب اس میں یہ ڈالے جائیں گے تو اس کی بڑے زور کی آواز سنیں گے اور وه جوش مار رہی ہوگی'),
(5249, 67, 8, 'قریب ہے کہ (ابھی) غصے کے مارے پھٹ جائے، جب کبھی اس میں کوئی گروه ڈاﻻ جائے گا اس سے جہنم کے داروغے پوچھیں گے کہ کیا تمہارے پاس ڈرانے واﻻ کوئی نہیں آیا تھا؟'),
(5250, 67, 9, 'وه جواب دیں گے کہ بیشک آیا تھا لیکن ہم نے اسے جھٹلایا اور ہم نے کہا کہ اللہ تعالیٰ نےکچھ بھی نازل نہیں فرمایا۔ تم بہت بڑی گمراہی میں ہی ہو'),
(5251, 67, 10, 'اور کہیں گے اگر ہم سنتے ہوتے یا عقل رکھتے ہوتے تو دوزخیوں میں (شریک) نہ ہوتے'),
(5252, 67, 11, 'پس انہوں نے اپنے جرم کا اقبال کر لیا۔ اب یہ دوزخی دفع ہوں (دور ہوں)'),
(5253, 67, 12, 'بیشک جو لوگ اپنے پروردگار سےغائبانہ طور پر ڈرتے رہتے ہیں ان کے لیے بخشش ہے اور بڑا ﺛواب ہے'),
(5254, 67, 13, 'تم اپنی باتوں کو چھپاؤ یا ﻇاہر کرو وه تو سینوں کی پوشیدگی کو بھی بخوبی جانتا ہے'),
(5255, 67, 14, 'کیا وہی نہ جانے جس نے پیدا کیا؟ پھر وه باریک بین اور باخبر بھی ہو'),
(5256, 67, 15, 'وه ذات جس نے تمہارے لیے زمین کو پست ومطیع کردیا تاکہ تم اس کی راہوں میں چلتے پھرتے رہو اور اللہ کی روزیاں کھاؤ (پیو) اسی کی طرف (تمہیں) جی کر اٹھ کھڑا ہونا ہے'),
(5257, 67, 16, 'کیا تم اس بات سے بے خوف ہوگئے ہو کہ آسمانوں واﻻ تمہیں زمین میں دھنسا دے اور اچانک زمین لرزنے لگے'),
(5258, 67, 17, 'یا کیا تم اس بات سے نڈر ہوگئے ہو کہ آسمانوں واﻻ تم پر پتھر برسادے؟ پھر تو تمہیں معلوم ہو ہی جائے گا کہ میرا ڈرانا کیسا تھا'),
(5259, 67, 18, 'اوران سے پہلے لوگوں نے بھی جھٹلایا تھا تو دیکھو ان پر میرا عذاب کیسا کچھ ہوا؟'),
(5260, 67, 19, 'کیا یہ اپنے اوپر پر کھولے ہوئے اور (کبھی کبھی) سمیٹے ہوئے (اڑنے والے) پرندوں کو نہیں دیکھتے، انہیں (اللہ) رحمٰن ہی (ہوا وفضا میں) تھامے ہوئے ہے۔ بیشک ہر چیز اس کی نگاه میں ہے'),
(5261, 67, 20, 'سوائے اللہ کے تمہارا وه کون سا لشکر ہے جو تمہاری مدد کرسکے کافر تو سراسر دھوکے ہی میں ہیں'),
(5262, 67, 21, 'اگر اللہ تعالیٰ اپنی روزی روک لے تو بتاؤ کون ہے جو پھر تمہیں روزی دے گا؟ بلکہ (کافر) تو سرکشی اور بدکنے پر اڑگئے ہیں'),
(5263, 67, 22, 'اچھا وه شخص زیاده ہدایت واﻻ ہے جو اپنے منھ کے بل اوندھا ہو کر چلے یا وه جو سیدھا (پیروں کے بل) راه راست پر چلا ہو؟'),
(5264, 67, 23, 'کہہ دیجئے کہ وہی (اللہ) ہے جس نے تمہیں پیدا کیا اور تمہارے کان آنکھیں اور دل بنائے تم بہت ہی کم شکر گزاری کرتے ہو'),
(5265, 67, 24, 'کہہ دیجئے! کہ وہی ہے جس نے تمہیں زمین میں پھیلا دیا اور اس کی طرف سے تم اکٹھے کیے جاؤ گے'),
(5266, 67, 25, '(کافر) پوچھتے ہیں کہ وه وعده کب ﻇاہر ہوگا اگر تم سچے ہو (تو بتاؤ؟)'),
(5267, 67, 26, 'آپ کہہ دیجئے کہ اس کا علم تو اللہ ہی کو ہے، میں تو صرف کھلے طور پر آگاه کر دینے واﻻ ہوں'),
(5268, 67, 27, 'جب یہ لوگ اس وعدے کو قریب تر پالیں گے اس وقت ان کافروں کے چہرے بگڑ جائیں گے اور کہہ دیا جائے گا کہ یہی ہے جسے تم طلب کیا کرتے تھے'),
(5269, 67, 28, 'آپ کہہ دیجئے! اچھا اگر مجھے اور میرے ساتھیوں کو اللہ تعالیٰ ہلاک کردے یا ہم پر رحم کرے (بہر صورت یہ تو بتاؤ) کہ کافروں کو دردناک عذاب سے کون بچائے گا؟'),
(5270, 67, 29, 'آپ کہہ دیجئے! کہ وہی رحمٰن ہے ہم تو اس پر ایمان ﻻچکے اور اسی پر ہمارا بھروسہ ہے۔ تمہیں عنقریب معلوم ہو جائے گا کہ صریح گمراہی میں کون ہے؟'),
(5271, 67, 30, 'آپ کہہ دیجئے! کہ اچھا یہ تو بتاؤ کہ اگر تمہارے (پینے کا) پانی زمین میں اتر جائے تو کون ہے جو تمہارے لیے نتھرا ہوا پانی ﻻئے؟');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5272, 68, 1, 'ن، قسم ہے قلم کی اور اس کی جو کچھ کہ وه (فرشتے) لکھتے ہیں'),
(5273, 68, 2, 'تو اپنے رب کے فضل سے دیوانہ نہیں ہے'),
(5274, 68, 3, 'اور بے شک تیرے لیے بے انتہا اجر ہے'),
(5275, 68, 4, 'اور بیشک تو بہت بڑے (عمده) اخلاق پر ہے'),
(5276, 68, 5, 'پس اب تو بھی دیکھ لے گا اور یہ بھی دیکھ لیں گے'),
(5277, 68, 6, 'کہ تم میں سے کون فتنہ میں پڑا ہوا ہے'),
(5278, 68, 7, 'بیشک تیرا رب اپنی راه سے بہکنے والوں کو خوب جانتا ہے، اور وه راه یافتہ لوگوں کو بھی بخوبی جانتا ہے'),
(5279, 68, 8, 'پس تو جھٹلانے والوں کی نہ مان'),
(5280, 68, 9, 'وه تو چاہتے ہیں کہ تو ذرا ڈھیلا ہو تو یہ بھی ڈھیلے پڑ جائیں'),
(5281, 68, 10, 'اور تو کسی ایسے شخص کا بھی کہا نہ ماننا جو زیاده قسمیں کھانے واﻻ'),
(5282, 68, 11, 'بے وقار، کمینہ، عیب گو، چغل خور'),
(5283, 68, 12, 'بھلائی سے روکنے واﻻ حد سے بڑھ جانے واﻻ گنہگار'),
(5284, 68, 13, 'گردن کش پھر ساتھ ہی بے نسب ہو'),
(5285, 68, 14, 'اس کی سرکشی صرف اس لیے ہے کہ وه مال واﻻ اور بیٹوں واﻻ ہے'),
(5286, 68, 15, 'جب اس کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو کہہ دیتا ہے کہ یہ تو اگلوں کے قصے ہیں'),
(5287, 68, 16, 'ہم بھی اس کی سونڈ (ناک) پر داغ دیں گے'),
(5288, 68, 17, 'بیشک ہم نے انہیں اسی طرح آزما لیا جس طرح ہم نے باغ والوں کو آزمایا تھا جبکہ انہوں نے قسمیں کھائیں کہ صبح ہوتے ہی اس باغ کے پھل اتار لیں گے'),
(5289, 68, 18, 'اور انشاءاللہ نہ کہا'),
(5290, 68, 19, 'پس اس پر تیرے رب کی جانب سے ایک بلا چاروں طرف گھوم گئی اور یہ سو ہی رہے تھے'),
(5291, 68, 20, 'پس وه باغ ایسا ہو گیا جیسے کٹی ہوئی کھیتی'),
(5292, 68, 21, 'اب صبح ہوتے ہی انہوں نے ایک دوسرے کو آوازیں دیں'),
(5293, 68, 22, 'کہ اگر تمہیں پھل اتارنے ہیں تو اپنی کھیتی پر سویرے ہی سویرے چل پڑو'),
(5294, 68, 23, 'پھر یہ سب چپکے چپکے یہ باتیں کرتے ہوئے چلے'),
(5295, 68, 24, 'کہ آج کے دن کوئی مسکین تمہارے پاس نہ آنے پائے'),
(5296, 68, 25, 'اور لپکے ہوئے صبح صبح گئے۔ (سمجھ رہے تھے) کہ ہم قابو پاگئے'),
(5297, 68, 26, 'جب انہوں نے باغ دیکھا تو کہنے لگے یقیناً ہم راستہ بھول گئے'),
(5298, 68, 27, 'نہیں نہیں بلکہ ہماری قسمت پھوٹ گئی'),
(5299, 68, 28, 'ان سب میں جو بہتر تھا اس نے کہا کہ میں تم سے نہ کہتا تھا کہ تم اللہ کی پاکیزگی کیوں نہیں بیان کرتے؟'),
(5300, 68, 29, 'تو سب کہنے لگے ہمارا رب پاک ہے بیشک ہم ہی ﻇالم تھے'),
(5301, 68, 30, 'پھر وه ایک دوسرے کی طرف رخ کر کے آپس میں ملامت کرنے لگے'),
(5302, 68, 31, 'کہنے لگے ہائے افسوس! یقیناً ہم سرکش تھے'),
(5303, 68, 32, 'کیا عجب ہے کہ ہمارا رب ہمیں اس سے بہتر بدلہ دے دے ہم تو اب اپنے رب سے ہی آرزو رکھتے ہیں'),
(5304, 68, 33, 'یوں ہی آفت آتی ہے اور آخرت کی آفت بہت بڑی ہے۔ کاش انہیں سمجھ ہوتی'),
(5305, 68, 34, 'پرہیزگاروں کے لیے ان کے رب کے پاس نعمتوں والی جنتیں ہیں'),
(5306, 68, 35, 'کیا ہم مسلمانوں کو مثل گناه گاروں کے کردیں گے'),
(5307, 68, 36, 'تمہیں کیا ہوگیا، کیسے فیصلے کر رہے ہو؟'),
(5308, 68, 37, 'کیا تمہارے پاس کوئی کتاب ہے جس میں تم پڑھتے ہو؟'),
(5309, 68, 38, 'کہ اس میں تمہاری من مانی باتیں ہوں؟'),
(5310, 68, 39, 'یا تم نے ہم سے کچھ قسمیں لی ہیں؟ جو قیامت تک باقی رہیں کہ تمہارے لیے وه سب ہے جو تم اپنی طرف سے مقرر کر لو'),
(5311, 68, 40, 'ان سے پوچھو تو کہ ان میں سے کون اس بات کا ذمہدار (اور دعویدار) ہے؟'),
(5312, 68, 41, 'کیا ان کے کوئی شریک ہیں؟ تو چاہئے کہ اپنے اپنے شریکوں کو لے آئیں اگر یہ سچے ہیں'),
(5313, 68, 42, 'جس دن پنڈلی کھول دی جائے گی اور سجدے کے لیے بلائے جائیں گے تو (سجده) نہ کر سکیں گے'),
(5314, 68, 43, 'نگاہیں نیچی ہوں گی اور ان پر ذلت و خواری چھارہی ہوگی، حاﻻنکہ یہ سجدے کے لیے (اس وقت بھی) بلائے جاتے تھے جب کہ صحیح سالم تھے'),
(5315, 68, 44, 'پس مجھے اور اس کلام کو جھٹلانے والے کو چھوڑ دے ہم انہیں اس طرح آہستہ آہستہ کھینچیں گے کہ انہیں معلوم بھی نہ ہوگا'),
(5316, 68, 45, 'اور میں انہیں ڈھیل دوں گا، بیشک میری تدبیر بڑی مضبوط ہے'),
(5317, 68, 46, 'کیا تو ان سے کوئی اجرت چاہتا ہے جس کے تاوان سے یہ دبے جاتے ہیں'),
(5318, 68, 47, 'یا کیا ان کے پاس علم غیب ہے جسے وه لکھتے ہوں'),
(5319, 68, 48, 'پس تو اپنے رب کے حکم کا صبر سے (انتظار کر) اور مچھلی والے کی طرح نہ ہو جا جب کہ اس نے غم کی حالت میں دعا کی'),
(5320, 68, 49, 'اگر اسے اس کے رب کی نعمت نہ پالیتی تو یقیناً وه برے حالوں میں چٹیل میدان میں ڈال دیا جاتا'),
(5321, 68, 50, 'اسے اس کے رب نے پھر نوازا اور اسے نیک کاروں میں کر دیا'),
(5322, 68, 51, 'اور قریب ہے کہ کافر اپنی تیز نگاہوں سے آپ کو پھسلا دیں، جب کبھی قرآن سنتے ہیں اور کہہ دیتے ہیں یہ تو ضرور دیوانہ ہے'),
(5323, 68, 52, 'در حقیقت یہ (قرآن) تو تمام جہان والوں کے لیے سراسر نصیحت ہی ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5324, 69, 1, 'ﺛابت ہونے والی'),
(5325, 69, 2, 'ﺛابت ہونے والی کیا ہے؟'),
(5326, 69, 3, 'اور تجھے کیا معلوم ہے کہ وه ﺛابت شده کیا ہے؟'),
(5327, 69, 4, 'اس کھڑکا دینے والی کو ﺛمود اور عاد نے جھٹلا دیا تھا'),
(5328, 69, 5, '(جس کے نتیجہ میں) ﺛمود تو بے حد خوفناک (اور اونچی) آواز سے ہلاک کردیئے گئے'),
(5329, 69, 6, 'اور عاد بیحد تیز وتند ہوا سے غارت کردیئے گئے'),
(5330, 69, 7, 'جسے ان پر لگاتار سات رات اور آٹھ دن تک (اللہ نے) مسلط رکھا پس تم دیکھتے کہ یہ لوگ زمین پر اس طرح گر گئے جیسے کہ کھجور کے کھوکھلے تنے ہوں'),
(5331, 69, 8, 'کیا ان میں سے کوئی بھی تجھے باقی نظر آرہا ہے؟'),
(5332, 69, 9, 'فرعون اور اس سے پہلے کے لوگ اور جن کی بستیاں الٹ دی گئی، انہوں نے بھی خطائیں کیں'),
(5333, 69, 10, 'اور اپنے رب کے رسول کی نافرمانی کی (بالﺂخر) اللہ نے انہیں (بھی) زبردست گرفت میں لے لیا'),
(5334, 69, 11, 'جب پانی میں طغیانی آگئی تو اس وقت ہم نے تمہیں کشتی میں چڑھا لیا'),
(5335, 69, 12, 'تاکہ اسے تمہارے لیے نصیحت اور یادگار بنادیں، اور (تاکہ) یاد رکھنے والے کان اسے یاد رکھیں'),
(5336, 69, 13, 'پس جب کہ صور میں ایک پھونک پھونکی جائے گی'),
(5337, 69, 14, 'اور زمین اور پہاڑ اٹھا لیے جائیں گے اور ایک ہی چوٹ میں ریزه ریزه کر دیے جائیں گے'),
(5338, 69, 15, 'اس دن ہو پڑنے والی (قیامت) ہو پڑے گی'),
(5339, 69, 16, 'اور آسمان پھٹ جائے گا اور اس دن بالکل بودا ہوجائے گا'),
(5340, 69, 17, 'اس کے کناروں پر فرشتے ہوں گے، اور تیرے پروردگار کا عرش اس دن آٹھ (فرشتے) اپنے اوپر اٹھائے ہوئے ہوں گے'),
(5341, 69, 18, 'اس دن تم سب سامنے پیش کیے جاؤ گے، تمہارا کوئی بھید پوشیده نہ رہے گا'),
(5342, 69, 19, 'سو جسے اس کا نامہٴ اعمال اس کے دائیں ہاتھ میں دیا جائے گا تو وه کہنے لگے گا کہ لو میرا نامہٴ اعمال پڑھو'),
(5343, 69, 20, 'مجھے تو کامل یقین تھا کہ مجھے اپنا حساب ملنا ہے'),
(5344, 69, 21, 'پس وه ایک دل پسند زندگی میں ہوگا'),
(5345, 69, 22, 'بلند وباﻻ جنت میں'),
(5346, 69, 23, 'جس کے میوے جھکے پڑے ہوں گے'),
(5347, 69, 24, '(ان سے کہا جائے گا) کہ مزے سے کھاؤ، پیو اپنے ان اعمال کے بدلے جو تم نے گزشتہ زمانے میں کیے'),
(5348, 69, 25, 'لیکن جسے اس (کے اعمال) کی کتاب اس کے بائیں ہاتھ میں دی جائے گی، وه تو کہے گا کہ کاش کہ مجھے میری کتاب دی ہی نہ جاتی'),
(5349, 69, 26, 'اور میں جانتا ہی نہ کہ حساب کیا ہے'),
(5350, 69, 27, 'کاش! کہ موت (میرا) کام ہی تمام کر دیتی'),
(5351, 69, 28, 'میرے مال نے بھی مجھے کچھ نفع نہ دیا'),
(5352, 69, 29, 'میرا غلبہ بھی مجھ سے جاتا رہا'),
(5353, 69, 30, '(حکم ہوگا) اسے پکڑ لو پھر اسے طوق پہنادو'),
(5354, 69, 31, 'پھر اسے دوزخ میں ڈال دو'),
(5355, 69, 32, 'پھر اسے ایسی زنجیر میں جس کی پیمائش ستر ہاتھ کی ہے جکڑ دو'),
(5356, 69, 33, 'بیشک یہ اللہ عظمت والے پرایمان نہ رکھتا تھا'),
(5357, 69, 34, 'اور مسکین کے کھلانے پر رغبت نہ دﻻتا تھا'),
(5358, 69, 35, 'پس آج اس کا نہ کوئی دوست ہے'),
(5359, 69, 36, 'اور نہ سوائے پیﭗ کے اس کی کوئی غذا ہے'),
(5360, 69, 37, 'جسے گناه گاروں کے سوا کوئی نہیں کھائے گا'),
(5361, 69, 38, 'پس مجھے قسم ہے ان چیزوں کی جنہیں تم دیکھتے ہو'),
(5362, 69, 39, 'اور ان چیزوں کی جنہیں تم نہیں دیکھتے'),
(5363, 69, 40, 'کہ بیشک یہ (قرآن) بزرگ رسول کا قول ہے'),
(5364, 69, 41, 'یہ کسی شاعر کا قول نہیں (افسوس) تمہیں بہت کم یقین ہے'),
(5365, 69, 42, 'اور نہ کسی کاہن کا قول ہے، (افسوس) بہت کم نصیحت لے رہے ہو'),
(5366, 69, 43, '(یہ تو) رب العالمین کا اتارا ہوا ہے'),
(5367, 69, 44, 'اور اگر یہ ہم پر کوئی بھی بات بنا لیتا'),
(5368, 69, 45, 'تو البتہ ہم اس کا داہنا ہاتھ پکڑ لیتے'),
(5369, 69, 46, 'پھر اس کی شہ رگ کاٹ دیتے'),
(5370, 69, 47, 'پھر تم میں سے کوئی بھی مجھے اس سے روکنے واﻻ نہ ہوتا'),
(5371, 69, 48, 'یقیناً یہ قرآن پرہیزگاروں کے لیے نصیحت ہے'),
(5372, 69, 49, 'ہمیں پوری طرح معلوم ہے کہ تم میں سے بعض اس کے جھٹلانے والے ہیں'),
(5373, 69, 50, 'بیشک (یہ جھٹلانا) کافروں پر حسرت ہے'),
(5374, 69, 51, 'اور بیشک (و شبہ) یہ یقینی حق ہے'),
(5375, 69, 52, 'پس تو اپنے رب عظیم کی پاکی بیان کر');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5376, 70, 1, 'ایک سوال کرنے والے نے اس عذاب کا سوال کیا جو واضح ہونے واﻻ ہے'),
(5377, 70, 2, 'کافروں پر، جسے کوئی ہٹانے واﻻ نہیں'),
(5378, 70, 3, 'اس اللہ کی طرف سے جو سیڑھیوں واﻻ ہے'),
(5379, 70, 4, 'جس کی طرف فرشتے اور روح چڑھتے ہیں ایک دن میں جس کی مقدار پچاس ہزار سال کی ہے'),
(5380, 70, 5, 'پس تو اچھی طرح صبر کر'),
(5381, 70, 6, 'بیشک یہ اس (عذاب) کو دور سمجھ رہے ہیں'),
(5382, 70, 7, 'اور ہم اسے قریب ہی دیکھتے ہیں'),
(5383, 70, 8, 'جس دن آسمان مثل تیل کی تلچھٹ کے ہو جائے گا'),
(5384, 70, 9, 'اور پہاڑ مثل رنگین اون کے ہو جائیں گے'),
(5385, 70, 10, 'اور کوئی دوست کسی دوست کو نہ پوچھے گا'),
(5386, 70, 11, '(حاﻻنکہ) ایک دوسرے کو دکھا دیئے جائیں گے، گناهگار اس دن کے عذاب کے بدلے فدیے میں اپنے بیٹوں کو'),
(5387, 70, 12, 'اپنی بیوی کو اور اپنے بھائی کو'),
(5388, 70, 13, 'اور اپنے کنبے کو جو اسے پناه دیتا تھا'),
(5389, 70, 14, 'اور روئے زمین کے سب لوگوں کو دینا چاہے گا تاکہ یہ اسے نجات دﻻ دے'),
(5390, 70, 15, '(مگر) ہرگز یہ نہ ہوگا، یقیناً وه شعلہ والی (آگ) ہے'),
(5391, 70, 16, 'جو منھ اور سر کی کھال کھینچ ﻻنے والی ہے'),
(5392, 70, 17, 'وه ہر اس شخص کو پکارے گی جو پیچھے ہٹتا اور منھ موڑتا ہے'),
(5393, 70, 18, 'اور جمع کرکے سنبھال رکھتا ہے'),
(5394, 70, 19, 'بیشک انسان بڑے کچے دل واﻻ بنایا گیا ہے'),
(5395, 70, 20, 'جب اسے مصیبت پہنچتی ہے تو ہڑبڑا اٹھتا ہے'),
(5396, 70, 21, 'اور جب راحت ملتی ہے تو بخل کرنے لگتا ہے'),
(5397, 70, 22, 'مگر وه نمازی'),
(5398, 70, 23, 'جو اپنی نماز پر ہمیشگی کرنے والے ہیں'),
(5399, 70, 24, 'اور جن کے مالوں میں مقرره حصہ ہے'),
(5400, 70, 25, 'مانگنے والوں کا بھی اور سوال سے بچنے والوں کا بھی'),
(5401, 70, 26, 'اور جو انصاف کے دن پر یقین رکھتے ہیں'),
(5402, 70, 27, 'اور جو اپنے رب کے عذاب سے ڈرتے رہتے ہیں'),
(5403, 70, 28, 'بیشک ان کے رب کا عذاب بے خوف ہونے کی چیز نہیں'),
(5404, 70, 29, 'اور جو لوگ اپنی شرم گاہوں کی (حرام سے) حفاﻇت کرتے ہیں'),
(5405, 70, 30, 'ہاں ان کی بیویوں اور لونڈیوں کے بارے میں جن کے وه مالک ہیں انہیں کوئی ملامت نہیں'),
(5406, 70, 31, 'اب جو کوئی اس کے علاوه (راه) ڈھونڈے گا توایسے لوگ حد سے گزر جانے والے ہوں گے'),
(5407, 70, 32, 'اور جو اپنی امانتوں کا اور اپنے قول و قرار کا پاس رکھتے ہیں'),
(5408, 70, 33, 'اور جو اپنی گواہیوں پر سیدھے اور قائم رہتے ہیں'),
(5409, 70, 34, 'اور جو اپنی نمازوں کی حفاﻇت کرتے ہیں'),
(5410, 70, 35, 'یہی لوگ جنتوں میں عزت والے ہوں گے'),
(5411, 70, 36, 'پس کافروں کو کیاہو گیا ہے کہ وه تیری طرف دوڑتے آتے ہیں'),
(5412, 70, 37, 'دائیں اور بائیں سے گروه کے گروه'),
(5413, 70, 38, 'کیا ان میں سے ہر ایک کی توقع یہ ہے کہ وه نعمتوں والی جنت میں داخل کیا جائے گا؟'),
(5414, 70, 39, '(ایسا) ہرگز نہ ہوگا۔ ہم نے انہیں اس (چیز) سے پیدا کیا ہے جسے وه جانتے ہیں'),
(5415, 70, 40, 'پس مجھے قسم ہے مشرقوں اور مغربوں کے رب کی (کہ) ہم یقیناً قادر ہیں'),
(5416, 70, 41, 'اس پر کہ ان کے عوض ان سے اچھے لوگ لے آئیں اور ہم عاجز نہیں ہیں'),
(5417, 70, 42, 'پس تو انہیں جھگڑتا کھیلتا چھوڑ دے یہاں تک کہ یہ اپنے اس دن سے جاملیں جس کا ان سے وعده کیا جاتا ہے'),
(5418, 70, 43, 'جس دن یہ قبروں سے دوڑتے ہوئے نکلیں گے، گویا کہ وه کسی جگہ کی طرف تیز تیز جا رہے ہیں'),
(5419, 70, 44, 'ان کی آنکھیں جھکی ہوئی ہوں گی، ان پر ذلت چھا رہی ہوگی، یہ ہے وه دن جس کا ان سے وعده کیا جاتا تھا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5420, 71, 1, 'یقیناً ہم نے نوح (علیہ السلام) کو ان کی قوم کی طرف بھیجا کہ اپنی قوم کو ڈرا دو (اور خبردار کردو) اس سے پہلے کہ ان کے پاس دردناک عذاب آجائے'),
(5421, 71, 2, '(نوح علیہ السلام نے) کہا اے میری قوم! میں تمہیں صاف صاف ڈرانے واﻻ ہوں'),
(5422, 71, 3, 'کہ تم اللہ کی عبادت کرو اور اسی سے ڈرو اور میرا کہا مانو'),
(5423, 71, 4, 'تو وه تمہارے گناه بخش دے گا اور تمہیں ایک وقت مقرره تک چھوڑ دے گا۔ یقیناً اللہ کا وعده جب آجاتا ہے تو مؤخر نہیں ہوتا۔ کاش کہ تمہیں سمجھ ہوتی'),
(5424, 71, 5, '(نوح علیہ السلام نے) کہا اے میرے پرورگار! میں نے اپنی قوم کو رات دن تیری طرف بلایا ہے'),
(5425, 71, 6, 'مگر میرے بلانے سے یہ لوگ اور زیاده بھاگنے لگے'),
(5426, 71, 7, 'میں نے جب کبھی انہیں تیری بخشش کے لیے بلایا انہوں نے اپنی انگلیاں اپنے کانوں میں ڈال لیں اور اپنے کپڑوں کو اوڑھ لیا اور اڑ گئے اور بڑا تکبر کیا'),
(5427, 71, 8, 'پھر میں نے انہیں بﺂواز بلند بلایا'),
(5428, 71, 9, 'اور بیشک میں نےان سے علانیہ بھی کہا اور چپکے چپکے بھی'),
(5429, 71, 10, 'اور میں نے کہا کہ اپنے رب سے اپنے گناه بخشواؤ (اور معافی مانگو) وه یقیناً بڑا بخشنے واﻻ ہے'),
(5430, 71, 11, 'وه تم پر آسمان کو خوب برستا ہوا چھوڑ دے گا'),
(5431, 71, 12, 'اور تمہیں خوب پے درپے مال اور اوﻻد میں ترقی دے گا اور تمہیں باغات دے گا اور تمہارے لیے نہریں نکال دے گا'),
(5432, 71, 13, 'تمہیں کیا ہوگیا ہے کہ تم اللہ کی برتری کا عقیده نہیں رکھتے'),
(5433, 71, 14, 'حاﻻنکہ اس نے تمہیں طرح طرح سے پیدا کیا ہے'),
(5434, 71, 15, 'کیا تم نہیں دیکھتے کہ اللہ تعالیٰ نے اوپر تلے کس طرح سات آسمان پیدا کر دیئے ہیں'),
(5435, 71, 16, 'اور ان میں چاند کو جگمگاتا بنایا ہے اور سورج کو روشن چراغ بنایا ہے'),
(5436, 71, 17, 'اور تم کو زمین سے ایک (خاص اہتمام سے) اگایا ہے (اور پیدا کیا ہے)'),
(5437, 71, 18, 'پھر تمہیں اسی میں لوٹا لے جائے گا اور (ایک خاص طریقہ) سے پھر نکالے گا'),
(5438, 71, 19, 'اور تمہارے لیے زمین کو اللہ تعالیٰ نے فرش بنادیا ہے'),
(5439, 71, 20, 'تاکہ تم اس کی کشاده راہوں میں چلو پھرو'),
(5440, 71, 21, 'نوح (علیہ السلام) نے کہا اے میرے پروردگار! ان لوگوں نے میری تو نافرمانی کی اور ایسوں کی فرمانبرداری کی جن کے مال واوﻻد نے ان کو (یقیناً) نقصان ہی میں بڑھایا ہے'),
(5441, 71, 22, 'اور ان لوگوں نے بڑا سخت فریب کیا'),
(5442, 71, 23, 'اور کہا انہوں نے کہ ہرگز اپنے معبودوں کو نہ چھوڑنا اور نہ ود اور سواع اور یغوث اور یعوق اور نسر کو (چھوڑنا)'),
(5443, 71, 24, 'اور انہوں نے بہت سے لوگوں کو گمراه کیا (الٰہی) تو ان ﻇالموں کی گمراہی اور بڑھا'),
(5444, 71, 25, 'یہ لوگ بہ سبب اپنے گناہوں کے ڈبو دیئے گئے اور جہنم میں پہنچا دیئے گئے اور اللہ کے سوا اپنا کوئی مددگار انہوں نے نہ پایا'),
(5445, 71, 26, 'اور (حضرت) نوح (علیہ السلام) نے کہا کہ اے میرے پالنے والے! تو روئے زمین پر کسی کافر کو رہنے سہنے واﻻ نہ چھوڑ'),
(5446, 71, 27, 'اگر تو انہیں چھوڑ دے گا تو (یقیناً) یہ تیرے (اور) بندوں کو (بھی) گمراه کر دیں گے اور یہ فاجروں اور ڈھیٹ کافروں ہی کو جنم دیں گے'),
(5447, 71, 28, 'اے میرے پروردگار! تو مجھے اور میرے ماں باپ اور جو بھی ایمان کی حالت میں میرے گھر میں آئے اور تمام مومن مردوں اور عورتوں کو بخش دے اور کافروں کو سوائے بربادی کے اور کسی بات میں نہ بڑھا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5448, 72, 1, '(اے محمد صلی اللہ علیہ وسلم) آپ کہہ دیں کہ مجھے وحی کی گئی ہے کہ جنوں کی ایک جماعت نے (قرآن) سنا اور کہا کہ ہم نے عجیب قرآن سنا ہے'),
(5449, 72, 2, 'جو راه راست کے طرف رہنمائی کرتا ہے۔ ہم اس پر ایمان ﻻ چکے (اب) ہم ہرگز کسی کو بھی اپنے رب کا شریک نہ بنائیں گے'),
(5450, 72, 3, 'اور بیشک ہمارے رب کی شان بڑی بلند ہے نہ اس نے کسی کو (اپنی) بیوی بنایا ہے نہ بیٹا'),
(5451, 72, 4, 'اور یہ کہ ہم میں کا بیوقوف اللہ کے بارے میں خلاف حق باتیں کہا کرتا تھا'),
(5452, 72, 5, 'اور ہم تو یہی سمجھتے رہے کہ ناممکن ہے کہ انسانوں اور جنات اللہ پر جھوٹی باتیں لگائیں'),
(5453, 72, 6, 'بات یہ ہے کہ چند انسان بعض جنات سے پناه طلب کیا کرتے تھے جس سے جنات اپنی سرکشی میں اور بڑھ گئے'),
(5454, 72, 7, 'اور (انسانوں) نے بھی تم جنوں کی طرح گمان کر لیا تھا کہ اللہ کسی کو نہ بھیجے گا (یا کسی کو دوباره زنده نہ کرے گا)'),
(5455, 72, 8, 'اور ہم نے آسمان کو ٹٹول کر دیکھا تو اسے سخت چوکیداروں اور سخت شعلوں سے پر پایا'),
(5456, 72, 9, 'اس سے پہلے ہم باتیں سننے کے لیے آسمان میں جگہ جگہ بیٹھ جایا کرتے تھے۔ اب جو بھی کان لگاتا ہے وه ایک شعلے کو اپنی تاک میں پاتا ہے'),
(5457, 72, 10, 'ہم نہیں جانتے کہ زمین والوں کے ساتھ کسی برائی کا اراده کیا گیا ہے یا ان کے رب کا اراده ان کے ساتھ بھلائی کا ہے'),
(5458, 72, 11, 'اور یہ کہ (بیشک) بعض تو ہم میں نیکو کار ہیں اور بعض اس کے برعکس بھی ہیں، ہم مختلف طریقوں سے بٹے ہوئے ہیں'),
(5459, 72, 12, 'اور ہم نے سمجھ لیا کہ ہم اللہ تعالیٰ کو زمین میں ہرگز عاجز نہیں کرسکتے اور نہ ہم بھاگ کر اسے ہرا سکتے ہیں'),
(5460, 72, 13, 'ہم تو ہدایت کی بات سنتے ہی اس پر ایمان ﻻئے چکے اور جو بھی اپنے رب پر ایمان ﻻئے گا اسے نہ کسی نقصان کا اندیشہ ہے نہ ﻇلم وستم کا'),
(5461, 72, 14, 'ہاں ہم میں بعض تو مسلمان ہیں اور بعض بےانصاف ہیں پس جو فرماں بردار ہوگئے انہوں نے تو راه راست کا قصد کیا'),
(5462, 72, 15, 'اور جو ﻇالم ہیں وه جہنم کا ایندھن بن گئے'),
(5463, 72, 16, 'اور (اے نبی یہ بھی کہہ دو) کہ اگر لوگ راه راست پر سیدھے رہتے تو یقیناً ہم انہیں بہت وافر پانی پلاتے'),
(5464, 72, 17, 'تاکہ ہم اس میں انہیں آزمالیں، اور جو شخص اپنے پروردگار کے ذکر سے منھ پھیر لے گا تو اللہ تعالیٰ اسے سخت عذاب میں مبتلا کردے گا'),
(5465, 72, 18, 'اور یہ کہ مسجدیں صرف اللہ ہی کے لئے خاص ہیں پس اللہ تعالیٰ کے ساتھ کسی اور کو نہ پکارو'),
(5466, 72, 19, 'اور جب اللہ کا بنده اس کی عبادت کے لیے کھڑا ہوا تو قریب تھا کہ وه بھیڑ کی بھیڑ بن کر اس پر پل پڑیں'),
(5467, 72, 20, 'آپ کہہ دیجئے کہ میں تو صرف اپنے رب ہی کو پکارتا ہوں اور اس کے ساتھ کسی کو شریک نہیں کرتا'),
(5468, 72, 21, 'کہہ دیجئے کہ مجھے تمہارے کسی نقصان نفع کا اختیار نہیں'),
(5469, 72, 22, 'کہہ دیجئے کہ مجھے ہرگز کوئی اللہ سے بچا نہیں سکتا اور میں ہرگز اس کے سوا کوئی جائے پناه بھی پا نہیں سکتا'),
(5470, 72, 23, 'البتہ (میرا کام) اللہ کی بات اور اس کے پیغامات (لوگوں کو) پہنچا دینا ہے، (اب) جو بھی اللہ اور اس کے رسول کی نہ مانے گا اس کے لیے جہنم کی آگ ہے جس میں ایسے لوگ ہمیشہ رہیں گے'),
(5471, 72, 24, '(ان کی آنکھ نہ کھلے گی) یہاں تک کہ اسے دیکھ لیں جس کا ان کو وعده دیا جاتا ہے پس عنقریب جان لیں گے کہ کس کا مددگار کمزور اور کس کی جماعت کم ہے'),
(5472, 72, 25, 'کہہ دیجئے کہ مجھے معلوم نہیں کہ جس کا وعده تم سے کیا جاتا ہے وه قریب ہے یا میرا رب اس کے لیے دور کی مدت مقرر کرے گا'),
(5473, 72, 26, 'وه غیب کا جاننے واﻻ ہے اور اپنے غیب پر کسی کو مطلع نہیں کرتا'),
(5474, 72, 27, 'سوائے اس پیغمبر کے جسے وه پسند کرلے لیکن اس کے بھی آگے پیچھے پہرے دار مقرر کردیتا ہے'),
(5475, 72, 28, 'تاکہ ان کے اپنے رب کے پیغام پہنچا دینے کا علم ہو جائے اللہ تعالیٰ نے ان کے آس پاس (کی تمام چیزوں) کا احاطہ کر رکھا ہے اور ہر چیز کی گنتی شمار کر رکھا ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5476, 73, 1, 'اے کپڑے میں لپٹنے والے'),
(5477, 73, 2, 'رات (کے وقت نماز) میں کھڑے ہوجاؤ مگر کم'),
(5478, 73, 3, 'آدھی رات یا اس سے بھی کچھ کم کرلے'),
(5479, 73, 4, 'یا اس پر بڑھا دے اور قرآن کو ٹھہر ٹھہر کر (صاف) پڑھا کر'),
(5480, 73, 5, 'یقیناً ہم تجھ پر بہت بھاری بات عنقریب نازل کریں گے'),
(5481, 73, 6, 'بیشک رات کا اٹھنا دل جمعی کے لیے انتہائی مناسب ہے اور بات کو بہت درست کر دینے واﻻ ہے'),
(5482, 73, 7, 'یقیناً تجھے دن میں بہت شغل رہتا ہے'),
(5483, 73, 8, 'تو اپنے رب کے نام کا ذکر کیا کر اور تمام خلائق سے کٹ کر اس کی طرف متوجہ ہوجا'),
(5484, 73, 9, 'مشرق ومغرب کا پروردگار جس کے سوا کوئی معبود نہیں، تو اسی کو اپنا کار ساز بنالے'),
(5485, 73, 10, 'اور جو کچھ وه کہیں تو سہتا ره اور وضعداری کے ساتھ ان سے الگ تھلگ ره'),
(5486, 73, 11, 'اور مجھے اور ان جھٹلانے والے آسوده حال لوگوں کو چھوڑ دے اور انہیں ذرا سی مہلت دے'),
(5487, 73, 12, 'یقیناً ہمارے ہاں سخت بیڑیاں ہیں اور سلگتی ہوئی جہنم ہے'),
(5488, 73, 13, 'اور حلق میں اٹکنے واﻻ کھانا ہے اور درد دینے واﻻ عذاب ہے'),
(5489, 73, 14, 'جس دن زمین اور پہاڑ تھرتھرا جائیں گے اور پہاڑ مثل بھربھری ریت کے ٹیلوں کے ہوجائیں گے'),
(5490, 73, 15, 'بیشک ہم نے تمہاری طرف بھی تم پر گواہی دینے واﻻ رسول بھیج دیا ہے جیسے کہ ہم نے فرعون کے پاس رسول بھیجا تھا'),
(5491, 73, 16, 'تو فرعون نے اس رسول کی نافرمانی کی تو ہم نے اسے سخت (وبال کی) پکڑ میں پکڑ لیا'),
(5492, 73, 17, 'تم اگر کافر رہے تو اس دن کیسے پناه پاؤ گے جو دن بچوں کو بوڑھا کردے گا'),
(5493, 73, 18, 'جس دن آسمان پھٹ جائے گا اللہ تعالیٰ کا یہ وعده ہو کر ہی رہنے واﻻ ہے'),
(5494, 73, 19, 'بیشک یہ نصیحت ہے پس جو چاہے اپنے رب کی طرف راه اختیار کرے'),
(5495, 73, 20, 'آپ کا رب بخوبی جانتا ہے کہ آپ اور آپ کے ساتھ کے لوگوں کی ایک جماعت قریب دو تہائی رات کے اور آدھی رات کے اور ایک تہائی رات کے تہجد پڑھتی ہے اور رات دن کا پورا اندازه اللہ تعالیٰ کو ہی ہے، وه (خوب) جانتا ہے کہ تم اسے ہرگز نہ نبھا سکو گے پس اس نے تم پر مہربانی کی لہٰذا جتنا قرآن پڑھنا تمہارے لیے آسان ہو اتنا ہی پڑھو، وه جانتا ہے کہ تم میں بعض بیمار بھی ہوں گے، بعض دوسرے زمین میں چل پھر کر اللہ تعالیٰ کا فضل (یعنی روزی بھی) تلاش کریں گے اور کچھ لوگ اللہ تعالیٰ کی راه میں جہاد بھی کریں گے، سو تم بہ آسانی جتنا قرآن پڑھ سکو پڑھو اور نماز کی پابندی رکھو اور زکوٰة دیتے رہا کرو اور اللہ تعالیٰ کو اچھا قرض دو۔ اور جو نیکی تم اپنے لیے آگے بھیجو گے اسے اللہ تعالیٰ کے ہاں بہتر سے بہتر اور ﺛواب میں بہت زیاده پاؤ گے اللہ تعالیٰ سے معافی مانگتے رہو۔ یقیناً اللہ تعالیٰ بخشنے واﻻ مہربان ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5496, 74, 1, 'اے کپڑا اوڑھنے والے'),
(5497, 74, 2, 'کھڑا ہوجا اور آگاه کردے'),
(5498, 74, 3, 'اور اپنے رب ہی کی بڑائیاں بیان کر'),
(5499, 74, 4, 'اپنے کپڑوں کو پاک رکھا کر'),
(5500, 74, 5, 'ناپاکی کو چھوڑ دے'),
(5501, 74, 6, 'اور احسان کرکے زیاده لینے کی خواہش نہ کر'),
(5502, 74, 7, 'اور اپنے رب کی راه میں صبر کر'),
(5503, 74, 8, 'پس جب کہ صور میں پھونک ماری جائے گی'),
(5504, 74, 9, 'تو وه دن بڑا سخت دن ہوگا'),
(5505, 74, 10, 'جو کافروں پر آسان نہ ہوگا'),
(5506, 74, 11, 'مجھے اور اسے چھوڑ دے جسے میں نے اکیلا پیدا کیا ہے'),
(5507, 74, 12, 'اور اسے بہت سا مال دے رکھا ہے'),
(5508, 74, 13, 'اور حاضر باش فرزند بھی'),
(5509, 74, 14, 'اور میں نے اسے بہت کچھ کشادگی دے رکھی ہے'),
(5510, 74, 15, 'پھر بھی اس کی چاہت ہے کہ میں اسے اور زیاده دوں'),
(5511, 74, 16, 'نہیں نہیں، وه ہماری آیتوں کا مخالف ہے'),
(5512, 74, 17, 'عنقریب میں اسے ایک سخت چڑھائی چڑھاؤں گا'),
(5513, 74, 18, 'اس نے غور کرکے تجویز کی'),
(5514, 74, 19, 'اسے ہلاکت ہو کیسی (تجویز) سوچی؟'),
(5515, 74, 20, 'وه پھر غارت ہو کس طرح اندازه کیا'),
(5516, 74, 21, 'اس نے پھر دیکھا'),
(5517, 74, 22, 'پھر تیوری چڑھائی اور منھ بنایا'),
(5518, 74, 23, 'پھر پیچھے ہٹ گیا اور غرور کیا'),
(5519, 74, 24, 'اور کہنے لگا تو یہ صرف جادو ہے جو نقل کیا جاتا ہے'),
(5520, 74, 25, 'سوائے انسانی کلام کے کچھ بھی نہیں ہے'),
(5521, 74, 26, 'میں عنقریب اسے دوزخ میں ڈالوں گا'),
(5522, 74, 27, 'اور تجھے کیا خبر کہ دوزخ کیا چیز ہے؟'),
(5523, 74, 28, 'نہ وه باقی رکھتی ہے نہ چھوڑتی ہے'),
(5524, 74, 29, 'کھال کو جھلسا دیتی ہے'),
(5525, 74, 30, 'اور اس میں انیس (فرشتے مقرر) ہیں'),
(5526, 74, 31, 'ہم نے دوزخ کے داروغے صرف فرشتے رکھے ہیں۔ اور ہم نے ان کی تعداد صرف کافروں کی آزمائش کے لیے مقرر کی ہے تاکہ اہل کتاب یقین کرلیں، اوراہل ایمان کے ایمان میں اضافہ ہو جائے اور اہل کتاب اور اہل ایمان شک نہ کریں اور جن کے دلوں میں بیماری ہے وه اور کافر کہیں کہ اس بیان سے اللہ تعالیٰ کی کیا مراد ہے؟ اسی طرح اللہ تعالیٰ جسے چاہتا ہے گمراه کرتا ہے اور جسے چاہتا ہے ہدایت دیتا ہے۔ تیرے رب کے لشکروں کو اس کے سوا کوئی نہیں جانتا، یہ تو کل بنی آدم کے لیے سراسر پند ونصیحت ہے'),
(5527, 74, 32, 'سچ کہتا ہوں قسم ہے چاند کی'),
(5528, 74, 33, 'اور رات کی جب وه پیچھے ہٹے'),
(5529, 74, 34, 'اور صبح کی جب کہ روشن ہو جائے'),
(5530, 74, 35, 'کہ (یقیناً وه جہنم) بڑی چیزوں میں سے ایک ہے'),
(5531, 74, 36, 'بنی آدم کو ڈرانے والی'),
(5532, 74, 37, '(یعنی) اسے جو تم میں سے آگے بڑھنا چاہے یا پیچھے ہٹنا چاہے'),
(5533, 74, 38, 'ہر شخص اپنے اعمال کے بدلے میں گروی ہے'),
(5534, 74, 39, 'مگر دائیں ہاتھ والے'),
(5535, 74, 40, 'کہ وه بہشتوں میں (بیٹھے ہوئے) گناه گاروں سے'),
(5536, 74, 41, 'سوال کرتے ہوں گے'),
(5537, 74, 42, 'تمہیں دوزخ میں کس چیز نے ڈاﻻ'),
(5538, 74, 43, 'وه جواب دیں گے کہ ہم نمازی نہ تھے'),
(5539, 74, 44, 'نہ مسکینوں کو کھانا کھلاتے تھے'),
(5540, 74, 45, 'اور ہم بحﺚ کرنے والے (انکاریوں) کا ساتھ دے کر بحﺚ مباحثہ میں مشغول رہا کرتے تھے'),
(5541, 74, 46, 'اور روز جزا کو جھٹلاتے تھے'),
(5542, 74, 47, 'یہاں تک کہ ہمیں موت آگئی'),
(5543, 74, 48, 'پس انہیں سفارش کرنے والوں کی سفارش نفع نہ دے گی'),
(5544, 74, 49, 'انہیں کیا ہو گیا ہے؟ کہ نصیحت سے منھ موڑ رہے ہیں'),
(5545, 74, 50, 'گویا کہ وه بِدکے ہوئے گدھے ہیں'),
(5546, 74, 51, 'جو شیر سے بھاگے ہوں'),
(5547, 74, 52, 'بلکہ ان میں سے ہر شخص چاہتا ہے کہ اسے کھلی ہوئی کتابیں دی جائیں'),
(5548, 74, 53, 'ہرگز ایسا نہیں (ہوسکتا بلکہ) یہ قیامت سے بے خوف ہیں'),
(5549, 74, 54, 'سچی بات تو یہ ہے کہ یہ (قرآن) ایک نصیحت ہے'),
(5550, 74, 55, 'اب جو چاہے اس سے نصیحت حاصل کرے'),
(5551, 74, 56, 'اور وه اس وقت نصیحت حاصل کریں گے جب اللہ تعالیٰ چاہے، وه اسی ﻻئق ہے کہ اس سے ڈریں اور اس ﻻئق بھی کہ وه بخشے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5552, 75, 1, 'میں قسم کھاتا ہوں قیامت کے دن کی'),
(5553, 75, 2, 'اور قسم کھاتا ہوں اس نفس کی جو ملامت کرنے واﻻ ہو'),
(5554, 75, 3, 'کیا انسان یہ خیال کرتا ہے کہ ہم اس کی ہڈیاں جمع کریں گے ہی نہیں'),
(5555, 75, 4, 'ہاں ضرور کریں گے ہم تو قادر ہیں کہ اس کی پور پور تک درست کردیں'),
(5556, 75, 5, 'بلکہ انسان تو چاہتا ہے کہ آگے آگے نافرمانیاں کرتا جائے'),
(5557, 75, 6, 'پوچھتا ہے کہ قیامت کا دن کب آئے گا'),
(5558, 75, 7, 'پس جس وقت کہ نگاه پتھرا جائے گی'),
(5559, 75, 8, 'اور چاند بے نور ہو جائے گا'),
(5560, 75, 9, 'اور سورج اور چاند جمع کردیئے جائیں گے'),
(5561, 75, 10, 'اس دن انسان کہے گا کہ آج بھاگنے کی جگہ کہاں ہے؟'),
(5562, 75, 11, 'نہیں نہیں کوئی پناہ گاه نہیں'),
(5563, 75, 12, 'آج تو تیرے پروردگار کی طرف ہی قرار گاه ہے'),
(5564, 75, 13, 'آج انسان کو اس کے آگے بھیجے ہوئے اور پیچھے چھوڑے ہوئے سے آگاه کیا جائے گا'),
(5565, 75, 14, 'بلکہ انسان خود اپنے اوپر آپ حجت ہے'),
(5566, 75, 15, 'اگر چہ کتنے ہی بہانے پیش کرے'),
(5567, 75, 16, '(اے نبی) آپ قرآن کو جلدی (یاد کرنے) کے لیے اپنی زبان کو حرکت نہ دیں'),
(5568, 75, 17, 'اس کا جمع کرنا اور (آپ کی زبان سے) پڑھنا ہمارے ذمہ ہے'),
(5569, 75, 18, 'ہم جب اسے پڑھ لیں تو آپ اس کے پڑھنے کی پیروی کریں'),
(5570, 75, 19, 'پھر اس کا واضح کر دینا ہمارے ذمہ ہے'),
(5571, 75, 20, 'نہیں نہیں تم جلدی ملنے والی (دنیا) کی محبت رکھتے ہو'),
(5572, 75, 21, 'اور آخرت کو چھوڑ بیٹھے ہو'),
(5573, 75, 22, 'اس روز بہت سے چہرے تروتازه اور بارونق ہوں گے'),
(5574, 75, 23, 'اپنے رب کی طرف دیکھتے ہوں گے'),
(5575, 75, 24, 'اور کتنے چہرے اس دن (بد رونق اور) اداس ہوں گے'),
(5576, 75, 25, 'سمجھتے ہوں گے کہ ان کے ساتھ کمر توڑ دینے واﻻ معاملہ کیا جائے گا'),
(5577, 75, 26, 'نہیں نہیں جب روح ہنسلی تک پہنچے گی'),
(5578, 75, 27, 'اور کہا جائے گا کہ کوئی جھاڑ پھونک کرنے واﻻ ہے؟'),
(5579, 75, 28, 'اور جان لیا اس نے کہ یہ وقت جدائی ہے'),
(5580, 75, 29, 'اور پنڈلی سے پنڈلی لپٹ جائے گی'),
(5581, 75, 30, 'آج تیرے پروردگار کی طرف چلنا ہے'),
(5582, 75, 31, 'اس نے نہ تو تصدیق کی نہ نماز ادا کی'),
(5583, 75, 32, 'بلکہ جھٹلایا اور روگردانی کی'),
(5584, 75, 33, 'پھر اپنے گھر والوں کے پاس اتراتا ہوا گیا'),
(5585, 75, 34, 'افسوس ہے تجھ پر حسرت ہے تجھ پر'),
(5586, 75, 35, 'وائے ہے اور خرابی ہے تیرے لیے'),
(5587, 75, 36, 'کیا انسان یہ سمجھتا ہے کہ اسے بیکار چھوڑ دیا جائے گا'),
(5588, 75, 37, 'کیا وه ایک گاڑھے پانی کا قطره نہ تھا جو ٹپکایا گیا تھا؟'),
(5589, 75, 38, 'پھر وه لہو کا لوتھڑا ہوگیا پھر اللہ نے اسے پیدا کیا اور درست بنا دیا'),
(5590, 75, 39, 'پھر اس سے جوڑے یعنی نر وماده بنائے'),
(5591, 75, 40, 'کیا (اللہ تعالیٰ) اس (امر) پر قادر نہیں کہ مردے کو زنده کردے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5592, 76, 1, 'یقیناً گزرا ہے انسان پر ایک وقت زمانے میں جب کہ یہ کوئی قابل ذکر چیز نہ تھا'),
(5593, 76, 2, 'بیشک ہم نے انسان کو ملے جلے نطفے سے امتحان کے لیے پیدا کیا اور اس کو سنتا دیکھتا بنایا'),
(5594, 76, 3, 'ہم نے اسے راه دکھائی اب خواه وه شکر گزار بنے خواه ناشکرا'),
(5595, 76, 4, 'یقیناً ہم نے کافروں کے لیے زنجیریں اور طوق اور شعلوں والی آگ تیار کر رکھی ہے'),
(5596, 76, 5, 'بیشک نیک لوگ وه جام پئیں گے جس کی آمیزش کافور کی ہے'),
(5597, 76, 6, 'جو ایک چشمہ ہے۔ جس سے اللہ کے بندے پئیں گے اس کی نہریں نکال لے جائیں گے (جدھر چاہیں)'),
(5598, 76, 7, 'جو نذر پوری کرتے ہیں اور اس دن سے ڈرتے ہیں جس کی برائی چاروں طرف پھیل جانے والی ہے'),
(5599, 76, 8, 'اور اللہ تعالیٰ کی محبت میں کھانا کھلاتے ہیں مسکین، یتیم اور قیدیوں کو'),
(5600, 76, 9, 'ہم تو تمہیں صرف اللہ تعالیٰ کی رضامندی کے لیے کھلاتے ہیں نہ تم سے بدلہ چاہتے ہیں نہ شکر گزاری'),
(5601, 76, 10, 'بیشک ہم اپنے پروردگار سے اس دن کا خوف کرتے ہیں جو اداسی اور سختی واﻻ ہوگا'),
(5602, 76, 11, 'پس انہیں اللہ تعالیٰ نے اس دن کی برائی سے بچا لیا اور انہیں تازگی اور خوشی پہنچائی'),
(5603, 76, 12, 'اور انہیں ان کے صبر کے بدلے جنت اور ریشمی لباس عطا فرمائے'),
(5604, 76, 13, 'یہ وہاں تختوں پر تکیے لگائے ہوئے بیٹھیں گے۔ نہ وہاں آفتاب کی گرمی دیکھیں گے نہ جاڑے کی سختی'),
(5605, 76, 14, 'ان جنتوں کے سائے ان پر جھکے ہوئے ہوں گے اور ان کے (میوے اور) گچھے نیچے لٹکائے ہوئے ہوں گے'),
(5606, 76, 15, 'اور ان پر چاندی کے برتنوں اور ان جاموں کا دور کرایا جائے گا جو شیشے کے ہوں گے'),
(5607, 76, 16, 'شیشے بھی چاندی کے جن کو (ساقی نے) اندازے سے ناپ رکھا ہوگا'),
(5608, 76, 17, 'اور انہیں وہاں وه جام پلائے جائیں گے جن کی آمیزش زنجبیل کی ہوگی'),
(5609, 76, 18, 'جنت کی ایک نہر سے جس کا نام سلسبیل ہے'),
(5610, 76, 19, 'اور ان کے ارد گرد گھومتے پھرتے ہوں گے وه کم سن بچے جو ہمیشہ رہنے والے ہیں جب تو انہیں دیکھے تو سمجھے کہ وه بکھرے ہوئے سچے موتی ہیں'),
(5611, 76, 20, 'تو وہاں جہاں کہیں بھی نظر ڈالے گا سراسر نعمتیں اور عظیم الشان سلطنت ہی دیکھے گا'),
(5612, 76, 21, 'ان کے جسموں پر سبز باریک اور موٹے ریشمی کپڑے ہوں گے اور انہیں چاندی کے کنگن کا زیور پہنایا جائے گا۔ اور انہیں ان کا رب پاک صاف شراب پلائے گا'),
(5613, 76, 22, '(کہا جائے گا) کہ یہ ہے تمہارے اعمال کا بدلہ اور تمہاری کوشش کی قدر کی گئی'),
(5614, 76, 23, 'بیشک ہم نے تجھ پر بتدریج قرآن نازل کیا ہے'),
(5615, 76, 24, 'پس تو اپنے رب کے حکم پر قائم ره اور ان میں سے کسی گنہگار یا ناشکرے کا کہا نہ مان'),
(5616, 76, 25, 'اور اپنے رب کے نام کا صبح وشام ذکر کیا کر'),
(5617, 76, 26, 'اور رات کے وقت اس کے سامنے سجدے کر اور بہت رات تک اس کی تسبیح کیا کر'),
(5618, 76, 27, 'بیشک یہ لوگ جلدی ملنے والی (دنیا) کو چاہتے ہیں اور اپنے پیچھے ایک بڑے بھاری دن کو چھوڑے دیتے ہیں'),
(5619, 76, 28, 'ہم نے انہیں پیدا کیا اور ہم نے ہی ان کے جوڑ اور بندھن مضبوط کیے اور ہم جب چاہیں ان کے عوض ان جیسے اوروں کو بدل ﻻئیں'),
(5620, 76, 29, 'یقیناً یہ تو ایک نصیحت ہے پس جو چاہے اپنے رب کی راه لے لے'),
(5621, 76, 30, 'اور تم نہ چاہو گے مگر یہ کہ اللہ تعالیٰ ہی چاہے بیشک اللہ تعالیٰ علم واﻻ باحکمت ہے'),
(5622, 76, 31, 'جسے چاہے اپنی رحمت میں داخل کرلے، اور ﻇالموں کے لیے اس نے دردناک عذاب تیار کر رکھا ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5623, 77, 1, 'دل خوش کن چلتی ہواؤں کی قسم'),
(5624, 77, 2, 'پھر زور سے جھونکا دینے والیوں کی قسم'),
(5625, 77, 3, 'پھر (ابر کو) ابھار کر پراگنده کرنے والیوں کی قسم'),
(5626, 77, 4, 'پھر حق وباطل کو جدا جدا کر دینے والے'),
(5627, 77, 5, 'اور وحی ﻻنے والے فرشتوں کی قسم'),
(5628, 77, 6, 'جو (وحی) الزام اتارنے یا آگاه کردینے کے لیے ہوتی ہے'),
(5629, 77, 7, 'جس چیز کا تم سے وعده کیا جاتا ہے وہ یقیناً ہونے والی ہے'),
(5630, 77, 8, 'پس جب ستارے بے نور کردئے جائیں گے'),
(5631, 77, 9, 'اور جب آسمان توڑ پھوڑ دیا جائے گا'),
(5632, 77, 10, 'اور جب پہاڑ ٹکڑے ٹکڑے کر کے اڑا دیئے جائیں گے'),
(5633, 77, 11, 'اور جب رسولوں کو وقت مقرره پر ﻻیا جائے گا'),
(5634, 77, 12, 'کس دن کے لیے (ان سب کو) مؤخر کیا گیا ہے؟'),
(5635, 77, 13, 'فیصلے کے دن کے لیے'),
(5636, 77, 14, 'اور تجھے کیا معلوم کہ فیصلے کا دن کیا ہے؟'),
(5637, 77, 15, 'اس دن جھٹلانے والوں کی خرابی ہے'),
(5638, 77, 16, 'کیا ہم نے اگلوں کو ہلاک نہیں کیا؟'),
(5639, 77, 17, 'پھر ہم ان کے بعد پچھلوں کو ﻻئے'),
(5640, 77, 18, 'ہم گنہگاروں کے ساتھ اسی طرح کرتے ہیں'),
(5641, 77, 19, 'اس دن جھٹلانے والوں کے لیے ویل (افسوس) ہے'),
(5642, 77, 20, 'کیا ہم نے تمہیں حقیر پانی سے (منی سے) پیدا نہیں کیا'),
(5643, 77, 21, 'پھر ہم نے اسے مضبوط ومحفوظ جگہ میں رکھا'),
(5644, 77, 22, 'ایک مقرره وقت تک'),
(5645, 77, 23, 'پھر ہم نے اندازه کیا اور ہم کیا خوب اندازه کرنے والے ہیں'),
(5646, 77, 24, 'اس دن تکذیب کرنے والوں کی خرابی ہے'),
(5647, 77, 25, 'کیا ہم نے زمین کو سمیٹنے والی نہیں بنایا؟'),
(5648, 77, 26, 'زندوں کو بھی اور مردوں کو بھی'),
(5649, 77, 27, 'اور ہم نے اس میں بلند وبھاری پہاڑ بنادیے اور تمہیں سیراب کرنے واﻻ میٹھا پانی پلایا'),
(5650, 77, 28, 'اس دن جھٹلانے والوں کے لیے وائے اور افسوس ہے'),
(5651, 77, 29, 'اس دوزخ کی طرف جاؤ جسے تم جھٹلاتے رہے تھے'),
(5652, 77, 30, 'چلو تین شاخوں والے سائے کی طرف'),
(5653, 77, 31, 'جو در اصل نہ سایہ دینے واﻻ ہے اور نہ شعلے سے بچاسکتا ہے'),
(5654, 77, 32, 'یقیناً دوزخ چنگاریاں پھینکتی ہے جو مثل محل کے ہیں'),
(5655, 77, 33, 'گویا کہ وه زرد اونٹ ہیں'),
(5656, 77, 34, 'آج ان جھٹلانے والوں کی درگت ہے'),
(5657, 77, 35, 'آج (کا دن) وه دن ہے کہ یہ بول بھی نہ سکیں گے'),
(5658, 77, 36, 'نہ انہیں معذرت کی اجازت دی جائے گی'),
(5659, 77, 37, 'اس دن جھٹلانے والوں کی خرابی ہے'),
(5660, 77, 38, 'یہ ہے فیصلے کا دن ہم نے تمہیں اور اگلوں کو سب کو جمع کرلیا ہے'),
(5661, 77, 39, 'پس اگر تم مجھ سے کوئی چال چل سکتے ہو تو چل لو'),
(5662, 77, 40, 'وائے ہے اس دن جھٹلانے والوں کے لیے'),
(5663, 77, 41, 'بیشک پرہیزگار لوگ سایوں میں ہیں اور بہتے چشموں میں'),
(5664, 77, 42, 'اور ان میووں میں جن کی وه خواہش کریں'),
(5665, 77, 43, '(اے جنتیو!) کھاؤ پیو مزے سےاپنے کیے ہوئے اعمال کے بدلے'),
(5666, 77, 44, 'یقیناً ہم نیکی کرنے والوں کو اسی طرح جزا دیتے ہیں'),
(5667, 77, 45, 'اس دن سچا نہ جاننے والوں کے لیے ویل (افسوس) ہے'),
(5668, 77, 46, '(اے جھٹلانے والو) تم دنیا میں تھوڑا سا کھا لو اور فائده اٹھا لو بیشک تم گنہگار ہو'),
(5669, 77, 47, 'اس دن جھٹلانے والوں کے لیے سخت ہلاکت ہے'),
(5670, 77, 48, 'ان سے جب کہا جاتا ہے کہ رکوع کر لو تو نہیں کرتے'),
(5671, 77, 49, 'اس دن جھٹلانے والوں کی تباہی ہے'),
(5672, 77, 50, 'اب اس قرآن کے بعد کس بات پر ایمان ﻻئیں گے؟');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5673, 78, 1, 'یہ لوگ کس چیز کے بارے میں پوچھ گچھ کر رہے ہیں'),
(5674, 78, 2, 'اس بڑی خبر کے متعلق'),
(5675, 78, 3, 'جس کے بارے میں یہ اختلاف کر رہے ہیں'),
(5676, 78, 4, 'یقیناً یہ ابھی جان لیں گے'),
(5677, 78, 5, 'پھر بالیقین انہیں بہت جلد معلوم ہو جائے گا'),
(5678, 78, 6, 'کیا ہم نے زمین کو فرش نہیں بنایا؟'),
(5679, 78, 7, 'اور پہاڑوں کو میخیں (نہیں بنایا؟)'),
(5680, 78, 8, 'اور ہم نے تمہیں جوڑا جوڑا پیدا کیا'),
(5681, 78, 9, 'اور ہم نے تمہاری نیند کو آرام کا سبب بنایا'),
(5682, 78, 10, 'اور رات کو ہم نے پرده بنایا'),
(5683, 78, 11, 'اور دن کو ہم نے وقت روزگار بنایا'),
(5684, 78, 12, 'اور تمہارے اوپر ہم نے سات مضبوط آسمان بنائے'),
(5685, 78, 13, 'اور ایک چمکتا ہوا روشن چراغ (سورج) پیدا کیا'),
(5686, 78, 14, 'اور بدلیوں سے ہم نے بکثرت بہتا ہوا پانی برسایا'),
(5687, 78, 15, 'تاکہ اس سے اناج اور سبزه اگائیں'),
(5688, 78, 16, 'اور گھنے باغ (بھی اگائیں)'),
(5689, 78, 17, 'بیشک فیصلہ کے دن کا وقت مقرر ہے'),
(5690, 78, 18, 'جس دن کہ صور میں پھونکا جائے گا۔ پھر تم فوج در فوج چلے آؤ گے'),
(5691, 78, 19, 'اور آسمان کھول دیا جائے گا تو اس میں دروازے دروازے ہو جائیں گے'),
(5692, 78, 20, 'اور پہاڑ چلائے جائیں گے پس وه سراب ہو جائیں گے'),
(5693, 78, 21, 'بیشک دوزخ گھات میں ہے'),
(5694, 78, 22, 'سرکشوں کا ٹھکانہ وہی ہے'),
(5695, 78, 23, 'اس میں وه مدتوں تک پڑے رہیں گے'),
(5696, 78, 24, 'نہ کبھی اس میں خنکی کا مزه چکھیں گے، نہ پانی کا'),
(5697, 78, 25, 'سوائے گرم پانی اور (بہتی) پیﭗ کے'),
(5698, 78, 26, '(ان کو) پورا پورا بدلہ ملے گا'),
(5699, 78, 27, 'انہیں تو حساب کی توقع ہی نہ تھی'),
(5700, 78, 28, 'اور بے باکی سے ہماری آیتوں کی تکذیب کرتے تھے'),
(5701, 78, 29, 'ہم نے ہر ایک چیز کو لکھ کر شمار کر رکھا ہے'),
(5702, 78, 30, 'اب تم (اپنے کیے کا) مزه چکھو ہم تمہارا عذاب ہی بڑھاتے رہیں گے'),
(5703, 78, 31, 'یقیناً پرہیزگار لوگوں کے لئے کامیابی ہے'),
(5704, 78, 32, 'باغات ہیں اور انگور ہیں'),
(5705, 78, 33, 'اور نوجوان کنواری ہم عمر عورتیں ہیں'),
(5706, 78, 34, 'اور چھلکتے ہوئے جام شراب ہیں'),
(5707, 78, 35, 'وہاں نہ تو وه بیہوده باتیں سنیں گے اور نہ جھوٹی باتیں سنیں گے'),
(5708, 78, 36, '(ان کو) تیرے رب کی طرف سے (ان کے نیک اعمال کا) بدلہ ملے گا جو کافی انعام ہوگا'),
(5709, 78, 37, '(اس رب کی طرف سے ملے گا جو کہ) آسمانوں کا اور زمین کا اور جو کچھ ان کے درمیان ہے ان کا پروردگار ہے اور بڑی بخشش کرنے واﻻ ہے۔ کسی کو اس سے بات چیت کرنے کا اختیار نہیں ہوگا'),
(5710, 78, 38, 'جس دن روح اور فرشتے صفیں باندھ کر کھڑے ہوں گے تو کوئی کلام نہ کر سکے گا مگر جسے رحمٰن اجازت دے دے اور وه ٹھیک بات زبان سے نکالے'),
(5711, 78, 39, 'یہ دن حق ہے اب جو چاہے اپنے رب کے پاس (نیک اعمال کر کے) ٹھکانا بنالے'),
(5712, 78, 40, 'ہم نے تمہیں عنقریب آنے والے عذاب سے ڈرا دیا (اور چوکنا کر دیا) ہے۔ جس دن انسان اپنے ہاتھوں کی کمائی کو دیکھ لے گا اور کافر کہے گا کہ کاش! میں مٹی ہو جاتا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5713, 79, 1, 'ڈوب کر سختی سے کھینچنے والوں کی قسم!'),
(5714, 79, 2, 'بند کھول کر چھڑا دینے والوں کی قسم!'),
(5715, 79, 3, 'اور تیرنے پھرنے والوں کی قسم!'),
(5716, 79, 4, 'پھر دوڑ کر آگے بڑھنے والوں کی قسم!'),
(5717, 79, 5, 'پھر کام کی تدبیر کرنے والوں کی قسم!'),
(5718, 79, 6, 'جس دن کانپنے والی کانپے گی'),
(5719, 79, 7, 'اس کے بعد ایک پیچھے آنے والی (پیچھے پیچھے) آئے گی'),
(5720, 79, 8, '(بہت سے) دل اس دن دھڑکتے ہوں گے'),
(5721, 79, 9, 'جن کی نگاہیں نیچی ہوں گی'),
(5722, 79, 10, 'کہتے ہیں کہ کیا ہم پہلی کی سی حالت کی طرف پھر لوٹائے جائیں گے؟'),
(5723, 79, 11, 'کیا اس وقت جب کہ ہم بوسیده ہڈیاں ہو جائیں گے؟'),
(5724, 79, 12, 'کہتے ہیں کہ پھر تو یہ لوٹنا نقصان ده ہے'),
(5725, 79, 13, '(معلوم ہونا چاہئے) وه تو صرف ایک (خوفناک) ڈانٹ ہے'),
(5726, 79, 14, 'کہ (جس کے ﻇاہر ہوتے ہی) وه ایک دم میدان میں جمع ہو جائیں گے'),
(5727, 79, 15, 'کیا موسیٰ (علیہ السلام) کی خبر تمہیں پہنچی ہے؟'),
(5728, 79, 16, 'جب کہ انہیں ان کے رب نے پاک میدان طویٰ میں پکارا'),
(5729, 79, 17, '(کہ) تم فرعون کے پاس جاؤ اس نے سرکشی اختیار کر لی ہے'),
(5730, 79, 18, 'اس سے کہو کہ کیا تو اپنی درستگی اور اصلاح چاہتا ہے'),
(5731, 79, 19, 'اور یہ کہ میں تجھے تیرے رب کی راه دکھاؤں تاکہ تو (اس سے) ڈرنے لگے'),
(5732, 79, 20, 'پس اسے بڑی نشانی دکھائی'),
(5733, 79, 21, 'تو اس نے جھٹلایا اور نافرمانی کی'),
(5734, 79, 22, 'پھر پلٹا دوڑ دھوپ کرتے ہوئے'),
(5735, 79, 23, 'پھر سب کو جمع کرکے پکارا'),
(5736, 79, 24, 'تم سب کا رب میں ہی ہوں'),
(5737, 79, 25, 'تو (سب سے بلند وباﻻ) اللہ نے بھی اسے آخرت کے اور دنیا کے عذاب میں گرفتار کرلیا'),
(5738, 79, 26, 'بیشک اس میں اس شخص کے لئے عبرت ہے جو ڈرے'),
(5739, 79, 27, 'کیا تمہارا پیدا کرنا زیاده دشوار ہے یا آسمان کا؟ اللہ تعالیٰ نے اسے بنایا'),
(5740, 79, 28, 'اس کی بلندی اونچی کی پھر اسے ٹھیک ٹھاک کر دیا'),
(5741, 79, 29, 'اسکی رات کو تاریک بنایا اور اس کے دن کو نکالا'),
(5742, 79, 30, 'اور اس کے بعد زمین کو (ہموار) بچھا دیا'),
(5743, 79, 31, 'اس میں سے پانی اور چاره نکالا'),
(5744, 79, 32, 'اور پہاڑوں کو (مضبوط) گاڑ دیا'),
(5745, 79, 33, 'یہ سب تمہارے اور تمہارے جانوروں کے فائدے کے لئے (ہیں)'),
(5746, 79, 34, 'پس جب وه بڑی آفت (قیامت) آجائے گی'),
(5747, 79, 35, 'جس دن کہ انسان اپنے کیے ہوئے کاموں کو یاد کرے گا'),
(5748, 79, 36, 'اور (ہر) دیکھنے والے کے سامنے جہنم ﻇاہر کی جائے گی'),
(5749, 79, 37, 'تو جس (شخص) نے سرکشی کی (ہوگی)'),
(5750, 79, 38, 'اور دنیوی زندگی کو ترجیح دی (ہوگی)'),
(5751, 79, 39, 'اس کا ٹھکانا جہنم ہی ہے'),
(5752, 79, 40, 'ہاں جو شخص اپنے رب کے سامنے کھڑے ہونے سے ڈرتا رہا ہوگا اور اپنے نفس کو خواہش سے روکا ہوگا'),
(5753, 79, 41, 'تو اس کا ٹھکانا جنت ہی ہے'),
(5754, 79, 42, 'لوگ آپ سے قیامت کے واقع ہونے کا وقت دریافت کرتے ہیں'),
(5755, 79, 43, 'آپ کو اس کے بیان کرنے سے کیا تعلق؟'),
(5756, 79, 44, 'اس کے علم کی انتہا تو اللہ کی جانب ہے'),
(5757, 79, 45, 'آپ تو صرف اس سے ڈرتے رہنے والوں کو آگاه کرنے والے ہیں'),
(5758, 79, 46, 'جس روز یہ اسے دیکھ لیں گے تو ایسا معلوم ہوگا کہ صرف دن کا آخری حصہ یا اول حصہ ہی (دنیا میں) رہے ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5759, 80, 1, 'وه ترش رو ہوا اور منھ موڑ لیا'),
(5760, 80, 2, '(صرف اس لئے) کہ اس کے پاس ایک نابینا آیا'),
(5761, 80, 3, 'تجھے کیا خبر شاید وه سنور جاتا'),
(5762, 80, 4, 'یا نصیحت سنتا اور اسے نصیحت فائده پہنچاتی'),
(5763, 80, 5, 'جو بے پرواہی کرتا ہے'),
(5764, 80, 6, 'اس کی طرف تو تو پوری توجہ کرتا ہے'),
(5765, 80, 7, 'حاﻻنکہ اس کے نہ سنورنے سے تجھ پر کوئی الزام نہیں'),
(5766, 80, 8, 'اور جو شخص تیرے پاس دوڑتا ہوا آتا ہے'),
(5767, 80, 9, 'اور وه ڈر (بھی) رہا ہے'),
(5768, 80, 10, 'تو اس سے بےرخی برتتا ہے'),
(5769, 80, 11, 'یہ ٹھیک نہیں قرآن تو نصیحت (کی چیز) ہے'),
(5770, 80, 12, 'جو چاہے اس سے نصیحت لے'),
(5771, 80, 13, '(یہ تو) پر عظمت صحیفوں میں (ہے)'),
(5772, 80, 14, 'جو بلند وباﻻ اور پاک صاف ہے'),
(5773, 80, 15, 'ایسے لکھنے والوں کے ہاتھوں میں ہے'),
(5774, 80, 16, 'جو بزرگ اور پاکباز ہے'),
(5775, 80, 17, 'اللہ کی مار انسان پر کیسا ناشکرا ہے'),
(5776, 80, 18, 'اسے اللہ نے کس چیز سے پیدا کیا'),
(5777, 80, 19, '(اسے) ایک نطفہ سے، پھر اندازه پر رکھا اس کو'),
(5778, 80, 20, 'پھر اس کے لئے راستہ آسان کیا'),
(5779, 80, 21, 'پھر اسے موت دی اور پھر قبر میں دفن کیا'),
(5780, 80, 22, 'پھر جب چاہے گا اسے زنده کر دے گا'),
(5781, 80, 23, 'ہرگز نہیں۔ اس نے اب تک اللہ کے حکم کی بجا آوری نہیں کی'),
(5782, 80, 24, 'انسان کو چاہئے کہ اپنے کھانے کو دیکھے'),
(5783, 80, 25, 'کہ ہم نے خوب پانی برسایا'),
(5784, 80, 26, 'پھر پھاڑا زمین کو اچھی طرح'),
(5785, 80, 27, 'پھر اس میں سے اناج اگائے'),
(5786, 80, 28, 'اور انگور اور ترکاری'),
(5787, 80, 29, 'اور زیتون اور کھجور'),
(5788, 80, 30, 'اور گنجان باغات'),
(5789, 80, 31, 'اور میوه اور (گھاس) چاره (بھی اگایا)'),
(5790, 80, 32, 'تمہارے استعمال وفائدے کے لئے اور تمہارے چوپایوں کے لئے'),
(5791, 80, 33, 'پس جب کہ کان بہرے کر دینے والی (قیامت) آجائے گی'),
(5792, 80, 34, 'اس دن آدمی اپنے بھائی سے'),
(5793, 80, 35, 'اور اپنی ماں اور اپنے باپ سے'),
(5794, 80, 36, 'اور اپنی بیوی اور اپنی اوﻻد سے بھاگے گا'),
(5795, 80, 37, 'ان میں سے ہر ایک کو اس دن ایسی فکر (دامنگیر) ہوگی جو اس کے لئے کافی ہوگی'),
(5796, 80, 38, 'اس دن بہت سے چہرے روشن ہوں گے'),
(5797, 80, 39, '(جو) ہنستے ہوئے اور ہشاش بشاش ہوں گے'),
(5798, 80, 40, 'اور بہت سے چہرے اس دن غبار آلود ہوں گے'),
(5799, 80, 41, 'جن پر سیاہی چڑھی ہوئی ہوگی'),
(5800, 80, 42, 'وه یہی کافر بدکردار لوگ ہوں گے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5801, 81, 1, 'جب سورج لپیٹ لیا جائے گا'),
(5802, 81, 2, 'اور جب ستارے بے نور ہو جائیں گی'),
(5803, 81, 3, 'اور جب پہاڑ چلائے جائیں گے'),
(5804, 81, 4, 'اور جب دس ماه کی حاملہ اونٹنیاں چھوڑ دی جائیں'),
(5805, 81, 5, 'اور جب وحشی جانور اکھٹے کیے جائیں گے'),
(5806, 81, 6, 'اور جب سمندر بھڑکائے جائیں گے'),
(5807, 81, 7, 'اور جب جانیں (جسموں سے) ملا دی جائیں گی'),
(5808, 81, 8, 'اور جب زنده گاڑی ہوئی لڑکی سے سوال کیا جائے گا'),
(5809, 81, 9, 'کہ کس گناه کی وجہ سے وه قتل کی گئی؟'),
(5810, 81, 10, 'اور جب نامہٴ اعمال کھول دیئے جائیں گے'),
(5811, 81, 11, 'اور جب آسمان کی کھال اتار لی جائے گی'),
(5812, 81, 12, 'اور جب جہنم بھڑکائی جائے گی'),
(5813, 81, 13, 'اور جب جنت نزدیک کر دی جائے گی'),
(5814, 81, 14, 'تو اس دن ہر شخص جان لے گا جو کچھ لے کر آیا ہوگا'),
(5815, 81, 15, 'میں قسم کھاتا ہوں پیچھے ہٹنے والے'),
(5816, 81, 16, 'چلنے پھرنے والے چھپنے والے ستاروں کی'),
(5817, 81, 17, 'اور رات کی جب جانے لگے'),
(5818, 81, 18, 'اور صبح کی جب چمکنے لگے'),
(5819, 81, 19, 'یقیناً یہ ایک بزرگ رسول کا کہا ہوا ہے'),
(5820, 81, 20, 'جو قوت واﻻ ہے، عرش والے (اللہ) کے نزدیک بلند مرتبہ ہے'),
(5821, 81, 21, 'جس کی (آسمانوں میں) اطاعت کی جاتی ہے امین ہے'),
(5822, 81, 22, 'اور تمہارا ساتھی دیوانہ نہیں ہے'),
(5823, 81, 23, 'اس نے اس (فرشتے) کو آسمان کے کھلے کنارے پر دیکھا بھی ہے'),
(5824, 81, 24, 'اور یہ غیب کی باتوں کو بتلانے میں بخیل بھی نہیں'),
(5825, 81, 25, 'اور یہ قرآن شیطان مردود کا کلام نہیں'),
(5826, 81, 26, 'پھر تم کہاں جا رہے ہو'),
(5827, 81, 27, 'یہ تو تمام جہان والوں کے لئے نصیحت نامہ ہے'),
(5828, 81, 28, '(بالخصوص) اس کے لئے جو تم میں سے سیدھی راه پر چلنا چاہے'),
(5829, 81, 29, 'اور تم بغیر پروردگار عالم کے چاہے کچھ نہیں چاه سکتے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5830, 82, 1, 'جب آسمان پھٹ جائے گا'),
(5831, 82, 2, 'اور جب ستارے جھڑ جائیں گے'),
(5832, 82, 3, 'اور جب سمندر بہہ نکلیں گے'),
(5833, 82, 4, 'اور جب قبریں (شق کر کے) اکھاڑ دی جائیں گی'),
(5834, 82, 5, '(اس وقت) ہر شخص اپنے آگے بھیجے ہوئے اور پیچھے چھوڑے ہوئے (یعنی اگلے پچھلے اعمال) کو معلوم کر لے گا'),
(5835, 82, 6, 'اے انسان! تجھے اپنے رب کریم سے کس چیز نے بہکایا'),
(5836, 82, 7, 'جس (رب نے) تجھے پیدا کیا، پھر ٹھیک ٹھاک کیا، پھر (درست اور) برابر بنایا'),
(5837, 82, 8, 'جس صورت میں چاہا تجھے جوڑ دیا'),
(5838, 82, 9, 'ہرگز نہیں بلکہ تم تو جزا وسزا کے دن کو جھٹلاتے ہو'),
(5839, 82, 10, 'یقیناً تم پر نگہبان عزت والے'),
(5840, 82, 11, 'لکھنے والے مقرر ہیں'),
(5841, 82, 12, 'جوکچھ تم کرتے ہو وه جانتے ہیں'),
(5842, 82, 13, 'یقیناً نیک لوگ (جنت کے عیش وآرام اور) نعمتوں میں ہوں گے'),
(5843, 82, 14, 'اور یقیناً بدکار لوگ دوزخ میں ہوں گے'),
(5844, 82, 15, 'بدلے والے دن اس میں جائیں گے'),
(5845, 82, 16, 'وه اس سے کبھی غائب نہ ہونے پائیں گے'),
(5846, 82, 17, 'تجھے کچھ خبر بھی ہے کہ بدلے کا دن کیا ہے'),
(5847, 82, 18, 'میں پھر (کہتا ہوں کہ) تجھے کیا معلوم کہ جزا (اور سزا) کا دن کیا ہے'),
(5848, 82, 19, '(وه ہے) جس دن کوئی شخص کسی شخص کے لئے کسی چیز کا مختار نہ ہوگا، اور (تمام تر) احکام اس روز اللہ کے ہی ہوں گے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5849, 83, 1, 'بڑی خرابی ہے ناپ تول میں کمی کرنے والوں کی'),
(5850, 83, 2, 'کہ جب لوگوں سے ناپ کر لیتے ہیں تو پورا پورا لیتے ہیں'),
(5851, 83, 3, 'اور جب انہیں ناپ کر یا تول کر دیتے ہیں تو کم دیتے ہیں'),
(5852, 83, 4, 'کیا انہیں اپنے مرنے کے بعد جی اٹھنے کا خیال نہیں'),
(5853, 83, 5, 'اس عظیم دن کے لئے'),
(5854, 83, 6, 'جس دن سب لوگ رب العالمین کے سامنے کھڑے ہوں گے'),
(5855, 83, 7, 'یقیناً بدکاروں کا نامہٴ اعمال سِجِّينٌ میں ہے'),
(5856, 83, 8, 'تجھے کیا معلوم سِجِّينٌ کیا ہے؟'),
(5857, 83, 9, '(یہ تو) لکھی ہوئی کتاب ہے'),
(5858, 83, 10, 'اس دن جھٹلانے والوں کی بڑی خرابی ہے'),
(5859, 83, 11, 'جو جزا وسزا کے دن کو جھٹلاتے رہے'),
(5860, 83, 12, 'اسے صرف وہی جھٹلاتا ہےجو حد سے آگے نکل جانے واﻻ (اور) گناه گار ہوتا ہے'),
(5861, 83, 13, 'جب اس کے سامنے ہماری آیتیں پڑھی جاتی ہیں تو کہہ دیتا ہے کہ یہ اگلوں کے افسانے ہیں'),
(5862, 83, 14, 'یوں نہیں بلکہ ان کے دلوں پر ان کےاعمال کی وجہ سے زنگ (چڑھ گیا) ہے'),
(5863, 83, 15, 'ہرگز نہیں یہ لوگ اس دن اپنے رب سےاوٹ میں رکھے جائیں گے'),
(5864, 83, 16, 'پھر یہ لوگ بالیقین جہنم میں جھونکے جائیں گے'),
(5865, 83, 17, 'پھر کہہ دیا جائے گا کہ یہی ہے وه جسے تم جھٹلاتے رہے'),
(5866, 83, 18, 'یقیناً یقیناً نیکو کاروں کا نامہٴ اعمال عِلِّیین میں ہے'),
(5867, 83, 19, 'تجھے کیا پتہ کہ عِلِّیین کیا ہے؟'),
(5868, 83, 20, '(وه تو) لکھی ہوئی کتاب ہے'),
(5869, 83, 21, 'مقرب (فرشتے) اس کا مشاہده کرتے ہیں'),
(5870, 83, 22, 'یقیناً نیک لوگ (بڑی) نعمتوں میں ہوں گے'),
(5871, 83, 23, 'مسہریوں پر بیٹھے دیکھ رہے ہوں گے'),
(5872, 83, 24, 'تو ان کے چہروں سے ہی نعمتوں کی تروتازگی پہچان لے گا'),
(5873, 83, 25, 'یہ لوگ سربمہر خالص شراب پلائے جائیں گے'),
(5874, 83, 26, 'جس پر مشک کی مہر ہوگی، سبقت لے جانے والوں کو اسی میں سبقت کرنی چاہئے'),
(5875, 83, 27, 'اور اس کی آمیزش تسنیم کی ہوگی'),
(5876, 83, 28, '(یعنی) وه چشمہ جس کا پانی مقرب لوگ پیئں گے'),
(5877, 83, 29, 'گنہگار لوگ ایمان والوں کی ہنسی اڑایا کرتے تھے'),
(5878, 83, 30, 'اور ان کے پاس سے گزرتے ہوئے آپس میں آنکھ کےاشارے کرتے تھے'),
(5879, 83, 31, 'اور جب اپنے والوں کی طرف لوٹتے تو دل لگیاں کرتے تھے'),
(5880, 83, 32, 'اور جب انہیں دیکھتے تو کہتے یقیناً یہ لوگ گمراه (بے راه) ہیں'),
(5881, 83, 33, 'یہ ان پر پاسبان بنا کر تو نہیں بھیجے گئے'),
(5882, 83, 34, 'پس آج ایمان والے ان کافروں پر ہنسیں گے'),
(5883, 83, 35, 'تختوں پر بیٹھے دیکھ رہے ہوں گے'),
(5884, 83, 36, 'کہ اب ان منکروں نے جیسا یہ کرتے تھے پورا پورا بدلہ پالیا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5885, 84, 1, 'جب آسمان پھٹ جائے گا'),
(5886, 84, 2, 'اور اپنے رب کے حکم پر کان لگائے گا اور اسی کے ﻻئق وه ہے'),
(5887, 84, 3, 'اور جب زمین (کھینچ کر) پھیلا دی جائے گی'),
(5888, 84, 4, 'اور اس میں جو ہے اسے وه اگل دے گی اور خالی ہو جائے گی'),
(5889, 84, 5, 'اور اپنے رب کے حکم پر کان لگائے گی اور اسی کے ﻻئق وه ہے'),
(5890, 84, 6, 'اے انسان! تو اپنے رب سے ملنے تک یہ کوشش اور تمام کام اور محنتیں کرکے اس سے ملاقات کرنے واﻻ ہے'),
(5891, 84, 7, 'تو (اس وقت) جس شخص کے داہنے ہاتھ میں اعمال نامہ دیا جائے گا'),
(5892, 84, 8, 'اس کا حساب تو بڑی آسانی سے لیا جائے گا'),
(5893, 84, 9, 'اور وه اپنے اہل کی طرف ہنسی خوشی لوٹ آئے گا'),
(5894, 84, 10, 'ہاں جس شخص کا اعمال نامہ اس کی پیٹھ کے پیچھے سے دیا جائے گا'),
(5895, 84, 11, 'تو وه موت کو بلانے لگے گا'),
(5896, 84, 12, 'اور بھڑکتی ہوئی جہنم میں داخل ہوگا'),
(5897, 84, 13, 'یہ شخص اپنے متعلقین میں (دنیا میں) خوش تھا'),
(5898, 84, 14, 'اس کا خیال تھا کہ اللہ کی طرف لوٹ کر ہی نہ جائے گا'),
(5899, 84, 15, 'کیوں نہیں، حاﻻنکہ اس کا رب اسے بخوبی دیکھ رہا تھا'),
(5900, 84, 16, 'مجھے شفق کی قسم! اور رات کی!'),
(5901, 84, 17, 'اور اس کی جمع کرده چیزوں کی قسم'),
(5902, 84, 18, 'اور چاند کی جب کہ وه کامل ہو جاتا ہے'),
(5903, 84, 19, 'یقیناً تم ایک حالت سے دوسری حالت پر پہنچو گے'),
(5904, 84, 20, 'انہیں کیا ہو گیا کہ ایمان نہیں ﻻتے'),
(5905, 84, 21, 'اور جب ان کے پاس قرآن پڑھا جاتا ہے تو سجده نہیں کرتے'),
(5906, 84, 22, 'بلکہ جنہوں نے کفر کیا وه جھٹلا رہے ہیں'),
(5907, 84, 23, 'اور اللہ تعالیٰ خوب جانتا ہے جو کچھ یہ دلوں میں رکھتے ہیں'),
(5908, 84, 24, 'انہیں المناک عذابوں کی خوشخبری سنا دو'),
(5909, 84, 25, 'ہاں ایمان والوں اور نیک اعمال والوں کو بے شمار اور نہ ختم ہونے واﻻ اجر ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5910, 85, 1, 'برجوں والے آسمان کی قسم!'),
(5911, 85, 2, 'وعده کیے ہوئے دن کی قسم!'),
(5912, 85, 3, 'حاضر ہونے والے اور حاضر کئے گئے کی قسم!'),
(5913, 85, 4, '﴿کہ﴾ خندقوں والے ہلاک کیے گئے'),
(5914, 85, 5, 'وه ایک آگ تھی ایندھن والی'),
(5915, 85, 6, 'جب کہ وه لوگ اس کے آس پاس بیٹھے تھے'),
(5916, 85, 7, 'اور مسلمانوں کے ساتھ جو کر رہے تھے اس کو اپنے سامنے دیکھ رہے تھے'),
(5917, 85, 8, 'یہ لوگ ان مسلمانوں (کے کسی اور گناه کا) بدلہ نہیں لے رہے تھے، سوائے اس کے کہ وه اللہ غالب ﻻئق حمد کی ذات پر ایمان ﻻئے تھے'),
(5918, 85, 9, 'جس کے لئے آسمان وزمین کا ملک ہے۔ اور اللہ تعالیٰ کے سامنے ہے ہر چیز'),
(5919, 85, 10, 'بیشک جن لوگوں نے مسلمان مردوں اور عورتوں کو ستایا پھر توبہ (بھی) نہ کی تو ان کے لئے جہنم کا عذاب ہے اور جلنے کا عذاب ہے'),
(5920, 85, 11, 'بیشک ایمان قبول کرنے والوں اور نیک کام کرنے والوں کے لئے وه باغات ہیں۔ جن کے نیچے بہریں بہہ رہی ہیں۔ یہی بڑی کامیابی ہے'),
(5921, 85, 12, 'یقیناً تیرے رب کی پکڑ بڑی سخت ہے'),
(5922, 85, 13, 'وہی پہلی مرتبہ پیدا کرتا ہے اور وہی دوباره پیدا کرے گا'),
(5923, 85, 14, 'وه بڑا بخشش کرنے واﻻ اور بہت محبت کرنے واﻻ ہے'),
(5924, 85, 15, 'عرش کا مالک عظمت واﻻ ہے'),
(5925, 85, 16, 'جو چاہے اسے کر گزرنے واﻻ ہے'),
(5926, 85, 17, 'تجھے لشکروں کی خبر بھی ملی ہے؟'),
(5927, 85, 18, '(یعنی) فرعون اور ﺛمود کی'),
(5928, 85, 19, '(کچھ نہیں) بلکہ کافر تو جھٹلانے میں پڑے ہوئے ہیں'),
(5929, 85, 20, 'اور اللہ تعالیٰ بھی انہیں ہر طرف سے گھیرے ہوئے ہے'),
(5930, 85, 21, 'بلکہ یہ قرآن ہے بڑی شان واﻻ'),
(5931, 85, 22, 'لوح محفوظ میں (لکھا ہوا)');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5932, 86, 1, 'قسم ہے آسمان کی اور اندھیرے میں روشن ہونے والے کی'),
(5933, 86, 2, 'تجھے معلوم بھی ہے کہ وه رات کو نمودار ہونے والی چیز کیا ہے؟'),
(5934, 86, 3, 'وه روشن ستاره ہے'),
(5935, 86, 4, 'کوئی ایسا نہیں جس پر نگہبان فرشتہ نہ ہو'),
(5936, 86, 5, 'انسان کو دیکھنا چاہئے کہ وه کس چیز سے پیدا کیا گیا ہے'),
(5937, 86, 6, 'وه ایک اچھلتے پانی سے پیدا کیا گیا ہے'),
(5938, 86, 7, 'جو پیٹھ اور سینے کے درمیان سے نکلتا ہے'),
(5939, 86, 8, 'بیشک وه اسے پھیر ﻻنے پر یقیناً قدرت رکھنے واﻻ ہے'),
(5940, 86, 9, 'جس دن پوشیده بھیدوں کی جانچ پڑتال ہوگی'),
(5941, 86, 10, 'تو نہ ہوگا اس کے پاس کچھ زور نہ مدددگار'),
(5942, 86, 11, 'بارش والے آسمان کی قسم!'),
(5943, 86, 12, 'اور پھٹنے والی زمین کی قسم!'),
(5944, 86, 13, 'بیشک یہ (قرآن) البتہ دو ٹوک فیصلہ کرنے واﻻ کلام ہے'),
(5945, 86, 14, 'یہ ہنسی کی (اور بے فائده) بات نہیں'),
(5946, 86, 15, 'البتہ کافر داؤ گھات میں ہیں'),
(5947, 86, 16, 'اور میں بھی ایک چال چل رہا ہوں'),
(5948, 86, 17, 'تو کافروں کو مہلت دے انہیں تھوڑے دنوں چھوڑ دے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5949, 87, 1, 'اپنے بہت ہی بلند اللہ کے نام کی پاکیزگی بیان کر'),
(5950, 87, 2, 'جس نے پیدا کیا اور صحیح سالم بنایا'),
(5951, 87, 3, 'اور جس نے (ٹھیک ٹھاک) اندازه کیا اور پھر راه دکھائی'),
(5952, 87, 4, 'اور جس نے تازه گھاس پیدا کی'),
(5953, 87, 5, 'پھر اس نے اس کو (سکھا کر) سیاه کوڑا کر دیا'),
(5954, 87, 6, 'ہم تجھے پڑھائیں گے پھر تو نہ بھولے گا'),
(5955, 87, 7, 'مگر جو کچھ اللہ چاہے۔ وه ﻇاہر اور پوشیده کو جانتا ہے'),
(5956, 87, 8, 'ہم آپ کے لئے آسانی پیدا کر دیں گے'),
(5957, 87, 9, 'تو آپ نصیحت کرتے رہیں اگر نصیحت کچھ فائده دے'),
(5958, 87, 10, 'ڈرنے واﻻ تو نصیحت لے گا'),
(5959, 87, 11, '(ہاں) بد بخت اس سے گریز کرے گا'),
(5960, 87, 12, 'جو بڑی آگ میں جائے گا'),
(5961, 87, 13, 'جہاں پھر نہ وه مرے گا نہ جیے گا، (بلکہ حالت نزع میں پڑا رہے گا)'),
(5962, 87, 14, 'بیشک اس نے فلاح پالی جو پاک ہوگیا'),
(5963, 87, 15, 'اور جس نے اپنے رب کا نام یاد رکھا اور نماز پڑھتا رہا'),
(5964, 87, 16, 'لیکن تم تو دنیا کی زندگی کو ترجیح دیتے ہو'),
(5965, 87, 17, 'اور آخرت بہت بہتر اور بہت بقا والی ہے'),
(5966, 87, 18, 'یہ باتیں پہلی کتابوں میں بھی ہیں'),
(5967, 87, 19, '(یعنی) ابراہیم اور موسیٰ کی کتابوں میں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5968, 88, 1, 'کیا تجھے بھی چھپا لینے والی (قیامت) کی خبر پہنچی ہے'),
(5969, 88, 2, 'اس دن بہت سے چہرے ذلیل ہوں گے'),
(5970, 88, 3, '(اور) محنت کرنے والے تھکے ہوئے ہوں گے'),
(5971, 88, 4, 'وه دہکتی ہوئی آگ میں جائیں گے'),
(5972, 88, 5, 'اور نہایت گرم چشمے کا پانی ان کو پلایا جائے گا'),
(5973, 88, 6, 'ان کے لئے سوائے کانٹے دار درختوں کے اور کچھ کھانے کو نہ ہوگا'),
(5974, 88, 7, 'جو نہ موٹا کرے گا نہ بھوک مٹائے گا'),
(5975, 88, 8, 'بہت سے چہرے اس دن تروتازه اور (آسوده حال) ہوں گے'),
(5976, 88, 9, 'اپنی کوشش پر خوش ہوں گے'),
(5977, 88, 10, 'بلند وباﻻ جنتوں میں ہوں گے'),
(5978, 88, 11, 'جہاں کوئی بیہوده بات نہیں سنیں گے'),
(5979, 88, 12, 'جہاں بہتا ہوا چشمہ ہوگا'),
(5980, 88, 13, '(اور) اس میں اونچے اونچے تخت ہوں گے'),
(5981, 88, 14, 'اور آبخورے رکھے ہوئے (ہوں گے)'),
(5982, 88, 15, 'اور ایک قطار میں لگے ہوئے تکیے ہوں گے'),
(5983, 88, 16, 'اور مخملی مسندیں پھیلی پڑی ہوں گی'),
(5984, 88, 17, 'کیا یہ اونٹوں کو نہیں دیکھتے کہ وه کس طرح پیدا کیے گئے ہیں'),
(5985, 88, 18, 'اور آسمان کو کہ کس طرح اونچا کیا گیا ہے'),
(5986, 88, 19, 'اور پہاڑوں کی طرف کہ کس طرح گاڑ دیئے گئے ہیں'),
(5987, 88, 20, 'اور زمین کی طرف کہ کس طرح بچھائی گئی ہے'),
(5988, 88, 21, 'پس آپ نصیحت کر دیا کریں (کیونکہ) آپ صرف نصیحت کرنے والے ہیں'),
(5989, 88, 22, 'آپ کچھ ان پر داروغہ نہیں ہیں'),
(5990, 88, 23, 'ہاں! جو شخص روگردانی کرے اور کفر کرے'),
(5991, 88, 24, 'اسے اللہ تعالیٰ بہت بڑا عذاب دے گا'),
(5992, 88, 25, 'بیشک ہماری طرف ان کا لوٹنا ہے'),
(5993, 88, 26, 'پھر بیشک ہمارے ذمہ ہے ان سے حساب لینا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(5994, 89, 1, 'قسم ہے فجر کی!'),
(5995, 89, 2, 'اور دس راتوں کی!'),
(5996, 89, 3, 'اور جفت اور طاق کی!'),
(5997, 89, 4, 'اور رات کی جب وه چلنے لگے'),
(5998, 89, 5, 'کیا ان میں عقلمند کے واسطے کافی قسم ہے'),
(5999, 89, 6, 'کیا آپ نے نہیں دیکھا کہ آپ کے رب نے عادیوں کے ساتھ کیا کیا'),
(6000, 89, 7, 'ستونوں والے ارم کے ساتھ'),
(6001, 89, 8, 'جس کی مانند (کوئی قوم) ملکوں میں پیدا نہیں کی گئی'),
(6002, 89, 9, 'اور ﺛمودیوں کے ساتھ جنہوں نے وادی میں بڑے بڑے پتھر تراشے تھے'),
(6003, 89, 10, 'اور فرعون کے ساتھ جو میخوں واﻻ تھا'),
(6004, 89, 11, 'ان سبھوں نے شہروں میں سر اٹھا رکھا تھا'),
(6005, 89, 12, 'اور بہت فساد مچا رکھا تھا'),
(6006, 89, 13, 'آخر تیرے رب نے ان سب پر عذاب کا کوڑا برسایا'),
(6007, 89, 14, 'یقیناً تیرا رب گھات میں ہے'),
(6008, 89, 15, 'انسان (کا یہ حال ہے کہ) جب اسے اس کا رب آزماتا ہے اور عزت ونعمت دیتا ہے تو وه کہنے لگتا ہے کہ میرے رب نے مجھے عزت دار بنایا'),
(6009, 89, 16, 'اور جب وه اس کو آزماتا ہے اس کی روزی تنگ کر دیتا ہے تو وه کہنے لگتا ہے کہ میرے رب نے میری اہانت کی (اور ذلیل کیا)'),
(6010, 89, 17, 'ایسا ہرگز نہیں بلکہ (بات یہ ہے) کہ تم (ہی) لوگ یتیموں کی عزت نہیں کرتے'),
(6011, 89, 18, 'اور مسکینوں کے کھلانے کی ایک دوسرے کو ترغیب نہیں دیتے'),
(6012, 89, 19, 'اور (مردوں کی) میراث سمیٹ سمیٹ کر کھاتے ہو'),
(6013, 89, 20, 'اور مال کو جی بھر کر عزیز رکھتے ہو'),
(6014, 89, 21, 'یقیناً جس وقت زمین کوٹ کوٹ کر برابر کر دی جائے گی'),
(6015, 89, 22, 'اور تیرا رب (خود) آجائے گا اور فرشتے صفیں باندھ کر (آ جائیں گے)'),
(6016, 89, 23, 'اور جس دن جہنم بھی ﻻئی جائے گی اس دن انسان کو سمجھ آئے گی مگر آج اس کے سمجھنے کا فائده کہاں؟'),
(6017, 89, 24, 'وه کہے گا کہ کاش کہ میں نے اپنی اس زندگی کے لئے کچھ پیشگی سامان کیا ہوتا'),
(6018, 89, 25, 'پس آج اللہ کے عذاب جیسا عذاب کسی کا نہ ہوگا'),
(6019, 89, 26, 'نہ اسکی قید وبند جیسی کسی کی قید وبند ہوگی'),
(6020, 89, 27, 'اے اطمینان والی روح'),
(6021, 89, 28, 'تو اپنے رب کی طرف لوٹ چل اس طرح کہ تو اس سے راضی وه تجھ سے خوش'),
(6022, 89, 29, 'پس میرے خاص بندوں میں داخل ہو جا'),
(6023, 89, 30, 'اور میری جنت میں چلی جا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6024, 90, 1, 'میں اس شہر کی قسم کھاتا ہوں'),
(6025, 90, 2, 'اور آپ اس شہر میں مقیم ہیں'),
(6026, 90, 3, 'اور (قسم ہے) انسانی باپ اور اوﻻد کی'),
(6027, 90, 4, 'یقیناً ہم نے انسان کو (بڑی) مشقت میں پیدا کیا ہے'),
(6028, 90, 5, 'کیا یہ گمان کرتا ہے کہ یہ کسی کے بس میں ہی نہیں؟'),
(6029, 90, 6, 'کہتا (پھرتا) ہے کہ میں نے تو بہت کچھ مال خرچ کر ڈاﻻ'),
(6030, 90, 7, 'کیا (یوں) سمجھتا ہے کہ کسی نے اسے دیکھا (ہی) نہیں؟'),
(6031, 90, 8, 'کیا ہم نے اس کی دو آنکھیں نہیں بنائیں'),
(6032, 90, 9, 'اور زبان اور ہونٹ (نہیں بنائے)'),
(6033, 90, 10, 'ہم نے دکھا دیئے اس کو دونوں راستے'),
(6034, 90, 11, 'سو اس سے نہ ہو سکا کہ گھاٹی میں داخل ہوتا'),
(6035, 90, 12, 'اور کیا سمجھا کہ گھاٹی ہے کیا؟'),
(6036, 90, 13, 'کسی گردن (غلام لونڈی) کو آزاد کرنا'),
(6037, 90, 14, 'یا بھوک والے دن کھانا کھلانا'),
(6038, 90, 15, 'کسی رشتہ دار یتیم کو'),
(6039, 90, 16, 'یا خاکسار مسکین کو'),
(6040, 90, 17, 'پھر ان لوگوں میں سے ہو جاتا جو ایمان ﻻتے اور ایک دوسرے کو صبر کی اور رحم کرنے کی وصیت کرتے ہیں'),
(6041, 90, 18, 'یہی لوگ ہیں دائیں بازو والے (خوش بختی والے)'),
(6042, 90, 19, 'اور جن لوگوں نے ہماری آیتوں کے ساتھ کفر کیا یہ کم بختی والے ہیں'),
(6043, 90, 20, 'انہی پر آگ ہوگی جو چاروں طرف سے گھیری ہوئی ہوگی');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6044, 91, 1, 'قسم ہے سورج کی اور اس کی دھوپ کی'),
(6045, 91, 2, 'قسم ہے چاند کی جب اس کے پیچھے آئے'),
(6046, 91, 3, 'قسم ہے دن کی جب سورج کو نمایاں کرے'),
(6047, 91, 4, 'قسم ہے رات کی جب اسے ڈھانﭗ لے'),
(6048, 91, 5, 'قسم ہے آسمان کی اور اس کے بنانے کی'),
(6049, 91, 6, 'قسم ہے زمین کی اور اسے ہموار کرنے کی'),
(6050, 91, 7, 'قسم ہے نفس کی اور اسے درست بنانے کی'),
(6051, 91, 8, 'پھر سمجھ دی اس کو بدکاری کی اور بچ کر چلنے کی'),
(6052, 91, 9, 'جس نے اسے پاک کیا وه کامیاب ہوا'),
(6053, 91, 10, 'اور جس نے اسے خاک میں ملا دیا وه ناکام ہوا'),
(6054, 91, 11, '(قوم) ﺛمود نے اپنی سرکشی کے باعﺚ جھٹلایا'),
(6055, 91, 12, 'جب ان میں کا بڑا بدبخت اٹھ کھڑا ہوا'),
(6056, 91, 13, 'انہیں اللہ کے رسول نے فرما دیا تھا کہ اللہ تعالیٰ کی اونٹنی اور اس کے پینے کی باری کی (حفاﻇت کرو)'),
(6057, 91, 14, 'ان لوگوں نے اپنے پیغمبر کو جھوٹا سمجھ کر اس اونٹنی کی کوچیں کاٹ دیں، پس ان کے رب نے ان کے گناہوں کے باعﺚ ان پر ہلاکت ڈالی اور پھر ہلاکت کو عام کر دیا اور اس بستی کو برابر کردیا'),
(6058, 91, 15, 'وه نہیں ڈرتا اس کے تباه کن انجام سے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6059, 92, 1, 'قسم ہے اس رات کی جب چھا جائے'),
(6060, 92, 2, 'اور قسم ہے دن کی جب روشن ہو'),
(6061, 92, 3, 'اور قسم ہے اس ذات کی جس نے نر وماده کو پیدا کیا'),
(6062, 92, 4, 'یقیناً تمہاری کوشش مختلف قسم کی ہے'),
(6063, 92, 5, 'جس نے دیا (اللہ کی راه میں) اور ڈرا (اپنے رب سے)'),
(6064, 92, 6, 'اور نیک بات کی تصدیق کرتا رہے گا'),
(6065, 92, 7, 'تو ہم بھی اس کو آسان راستے کی سہولت دیں گے'),
(6066, 92, 8, 'لیکن جس نے بخیلی کی اور بے پرواہی برتی'),
(6067, 92, 9, 'اور نیک بات کی تکذیب کی'),
(6068, 92, 10, 'تو ہم بھی اس کی تنگی ومشکل کے سامان میسر کر دیں گے'),
(6069, 92, 11, 'اس کا مال اسے (اوندھا) گرنے کے وقت کچھ کام نہ آئے گا'),
(6070, 92, 12, 'بیشک راه دکھا دینا ہمارے ذمہ ہے'),
(6071, 92, 13, 'اور ہمارے ہی ہاتھ آخرت اور دنیا ہے'),
(6072, 92, 14, 'میں نے تو تمہیں شعلے مارتی ہوئی آگ سے ڈرا دیا ہے'),
(6073, 92, 15, 'جس میں صرف وہی بدبخت داخل ہوگا'),
(6074, 92, 16, 'جس نے جھٹلایا اور (اس کی پیروی سے) منھ پھیر لیا'),
(6075, 92, 17, 'اور اس سے ایسا شخص دور رکھا جائے گا جو بڑا پرہیزگار ہو گا'),
(6076, 92, 18, 'جو پاکی حاصل کرنے کے لئے اپنا مال دیتا ہے'),
(6077, 92, 19, 'کسی کا اس پر کوئی احسان نہیں کہ جس کا بدلہ دیا جا رہا ہو'),
(6078, 92, 20, 'بلکہ صرف اپنے پروردگار بزرگ وبلند کی رضا چاہنے کے لئے'),
(6079, 92, 21, 'یقیناً وه (اللہ بھی) عنقریب رضامند ہو جائے گا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6080, 93, 1, 'قسم ہے چاشت کے وقت کی'),
(6081, 93, 2, 'اور قسم ہے رات کی جب چھا جائے'),
(6082, 93, 3, 'نہ تو تیرے رب نے تجھے چھوڑا ہے اور نہ وه بیزار ہو گیا ہے'),
(6083, 93, 4, 'یقیناً تیرے لئے انجام آغاز سے بہتر ہوگا'),
(6084, 93, 5, 'تجھے تیرا رب بہت جلد (انعام) دے گا اور تو راضی (وخوش) ہو جائے گا'),
(6085, 93, 6, 'کیا اس نے تجھے یتیم پا کر جگہ نہیں دی'),
(6086, 93, 7, 'اور تجھے راه بھوﻻ پا کر ہدایت نہیں دی'),
(6087, 93, 8, 'اور تجھے نادار پاکر تونگر نہیں بنا دیا؟'),
(6088, 93, 9, 'پس یتیم پر تو بھی سختی نہ کیا کر'),
(6089, 93, 10, 'اور نہ سوال کرنے والے کو ڈانٹ ڈپٹ'),
(6090, 93, 11, 'اور اپنے رب کی نعمتوں کو بیان کرتا ره');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6091, 94, 1, 'کیا ہم نے تیرا سینہ نہیں کھول دیا'),
(6092, 94, 2, 'اور تجھ پر سے تیرا بوجھ ہم نے اتار دیا'),
(6093, 94, 3, 'جس نے تیری پیٹھ توڑ دی تھی'),
(6094, 94, 4, 'اور ہمنے تیرا ذکر بلند کر دیا'),
(6095, 94, 5, 'پس یقیناً مشکل کے ساتھ آسانی ہے'),
(6096, 94, 6, 'بیشک مشکل کے ساتھ آسانی ہے'),
(6097, 94, 7, 'پس جب تو فارغ ہو تو عبادت میں محنت کر'),
(6098, 94, 8, 'اور اپنے پروردگار ہی کی طرف دل لگا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6099, 95, 1, 'قسم ہے انجیر کی اور زیتون کی'),
(6100, 95, 2, 'اور طور سِینِین کی'),
(6101, 95, 3, 'اور اس امن والے شہر کی'),
(6102, 95, 4, 'یقیناً ہم نے انسان کو بہترین صورت میں پیدا کیا'),
(6103, 95, 5, 'پھر اسے نیچوں سے نیچا کر دیا'),
(6104, 95, 6, 'لیکن جو لوگ ایمان ﻻئے اور (پھر) نیک عمل کیے تو ان کے لئے ایسا اجر ہے جو کبھی ختم نہ ہوگا'),
(6105, 95, 7, 'پس تجھے اب روز جزا کے جھٹلانے پر کون سی چیز آماده کرتی ہے'),
(6106, 95, 8, 'کیا اللہ تعالیٰ (سب) حاکموں کا حاکم نہیں ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6107, 96, 1, 'پڑھ اپنے رب کے نام سے جس نے پیدا کیا'),
(6108, 96, 2, 'جس نے انسان کو خون کے لوتھڑے سے پیدا کیا'),
(6109, 96, 3, 'تو پڑھتا ره تیرا رب بڑے کرم واﻻ ہے'),
(6110, 96, 4, 'جس نے قلم کے ذریعے (علم) سکھایا'),
(6111, 96, 5, 'جس نے انسان کو وه سکھایا جسے وه نہیں جانتا تھا'),
(6112, 96, 6, 'سچ مچ انسان تو آپے سے باہر ہو جاتا ہے'),
(6113, 96, 7, 'اس لئے کہ وه اپنے آپ کو بے پروا (یا تونگر) سمجھتا ہے'),
(6114, 96, 8, 'یقیناً لوٹنا تیرے رب کی طرف ہے'),
(6115, 96, 9, '(بھلا) اسے بھی تو نے دیکھا جو بندے کو روکتا ہے'),
(6116, 96, 10, 'جبکہ وه بنده نماز ادا کرتا ہے'),
(6117, 96, 11, 'بھلا بتلا تو اگر وه ہدایت پر ہو'),
(6118, 96, 12, 'یا پرہیز گاری کا حکم دیتا ہو'),
(6119, 96, 13, 'بھلا دیکھو تو اگر یہ جھٹلاتا ہو اور منھ پھیرتا ہو تو'),
(6120, 96, 14, 'کیا اس نے نہیں جانا کہ اللہ تعالیٰ اسے خوب دیکھ رہا ہے'),
(6121, 96, 15, 'یقیناً اگر یہ باز نہ رہا تو ہم اس کی پیشانی کے بال پکڑ کر گھسیٹیں گے'),
(6122, 96, 16, 'ایسی پیشانی جو جھوٹی خطا کار ہے'),
(6123, 96, 17, 'یہ اپنی مجلس والوں کو بلالے'),
(6124, 96, 18, 'ہم بھی (دوزخ کے) پیادوں کو بلالیں گے'),
(6125, 96, 19, 'خبردار! اس کا کہنا ہرگز نہ ماننا اور سجده کر اور قریب ہو جا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6126, 97, 1, 'یقیناً ہم نے اسے شب قدر میں نازل فرمایا'),
(6127, 97, 2, 'تو کیا سمجھا کہ شب قدر کیا ہے؟'),
(6128, 97, 3, 'شب قدر ایک ہزار مہینوں سے بہتر ہے'),
(6129, 97, 4, 'اس (میں ہر کام) کے سر انجام دینے کو اپنے رب کے حکم سے فرشتے اور روح (جبرائیل) اترتے ہیں'),
(6130, 97, 5, 'یہ رات سراسر سلامتی کی ہوتی ہے اور فجر کے طلوع ہونے تک (رہتی ہے)');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6131, 98, 1, 'اہل کتاب کے کافر اور مشرک لوگ جب تک کہ ان کے پاس ﻇاہر دلیل نہ آجائے باز رہنے والے نہ تھے (وه دلیل یہ تھی کہ)'),
(6132, 98, 2, 'اللہ تعالیٰ کا ایک رسول جو پاک صحیفے پڑھے'),
(6133, 98, 3, 'جن میں صحیح اور درست احکام ہوں'),
(6134, 98, 4, 'اہل کتاب اپنے پاس ﻇاہر دلیل آجانے کے بعد ہی (اختلاف میں پڑ کر) متفرق ہوگئے'),
(6135, 98, 5, 'انہیں اس کے سوا کوئی حکم نہیں دیا گیا کہ صرف اللہ کی عبادت کریں اسی کے لئے دین کو خالص رکھیں۔ ابراہیم حنیف کے دین پر اور نماز کو قائم رکھیں اور زکوٰة دیتے رہیں یہی ہے دین سیدھی ملت کا'),
(6136, 98, 6, 'بیشک جو لوگ اہل کتاب میں کافر ہوئے اور مشرکین سب دوزخ کی آگ میں (جائیں گے) جہاں وه ہمیشہ (ہمیشہ) رہیں گے۔ یہ لوگ بدترین خلائق ہیں'),
(6137, 98, 7, 'بیشک جو لوگ ایمان ﻻئےاور نیک عمل کیے یہ لوگ بہترین خلائق ہیں'),
(6138, 98, 8, 'ان کا بدلہ ان کے رب کے پاس ہمیشگی والی جنتیں ہیں جن کے نیچے نہریں بہہ رہی ہیں جن میں وه ہمیشہ ہمیشہ رہیں گے۔ اللہ تعالیٰ ان سے راضی ہوا اور یہ اس سے راضی ہوئے۔ یہ ہے اس کے لئے جو اپنے پروردگار سے ڈرے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6139, 99, 1, 'جب زمین پوری طرح جھنجھوڑ دی جائے گی'),
(6140, 99, 2, 'اور اپنے بوجھ باہر نکال پھینکے گی'),
(6141, 99, 3, 'انسان کہنے لگے گا کہ اسے کیا ہوگیا؟'),
(6142, 99, 4, 'اس دن زمین اپنی سب خبریں بیان کردے گی'),
(6143, 99, 5, 'اس لئے کہ تیرے رب نے اسے حکم دیا ہوگا'),
(6144, 99, 6, 'اس روز لوگ مختلف جماعتیں ہو کر (واپس) لوٹیں گے تاکہ انہیں ان کے اعمال دکھا دیئے جائیں'),
(6145, 99, 7, 'پس جس نے ذره برابر نیکی کی ہوگی وه اسے دیکھ لے گا'),
(6146, 99, 8, 'اور جس نے ذره برابر برائی کی ہوگی وه اسے دیکھ لے گا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6147, 100, 1, 'ہانپتے ہوئے دوڑنے والے گھوڑوں کی قسم!'),
(6148, 100, 2, 'پھر ٹاپ مار کر آگ جھاڑنے والوں کی قسم!'),
(6149, 100, 3, 'پھر صبح کے وقت دھاوا بولنے والوں کی قسم'),
(6150, 100, 4, 'پس اس وقت گرد وغبار اڑاتے ہیں'),
(6151, 100, 5, 'پھر اسی کے ساتھ فوجیوں کے درمیان گھس جاتے ہیں'),
(6152, 100, 6, 'یقیناً انسان اپنے رب کا بڑا ناشکرا ہے'),
(6153, 100, 7, 'اور یقیناً وه خود بھی اس پر گواه ہے'),
(6154, 100, 8, 'یہ مال کی محنت میں بھی بڑا سخت ہے'),
(6155, 100, 9, 'کیا اسے وه وقت معلوم نہیں جب قبروں میں جو (کچھ) ہے نکال لیا جائے گا'),
(6156, 100, 10, 'اور سینوں کی پوشیده باتیں ﻇاہر کر دی جائیں گی'),
(6157, 100, 11, 'بیشک ان کا رب اس دن ان کے حال سے پورا باخبر ہوگا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6158, 101, 1, 'کھڑکھڑا دینے والی'),
(6159, 101, 2, 'کیا ہے وه کھڑکھڑا دینے والی'),
(6160, 101, 3, 'تجھے کیا معلوم کہ وه کھڑ کھڑا دینے والی کیا ہے'),
(6161, 101, 4, 'جس دن انسان بکھرے ہوئے پروانوں کی طرح ہو جائیں گے'),
(6162, 101, 5, 'اور پہاڑ دھنے ہوئے رنگین اون کی طرح ہو جائیں گے'),
(6163, 101, 6, 'پھر جس کے پلڑے بھاری ہوں گے'),
(6164, 101, 7, 'وه تو دل پسند آرام کی زندگی میں ہوگا'),
(6165, 101, 8, 'اور جس کے پلڑے ہلکے ہوں گے'),
(6166, 101, 9, 'اس کا ٹھکانا ہاویہ ہے'),
(6167, 101, 10, 'تجھے کیا معلوم کہ وه کیا ہے'),
(6168, 101, 11, 'وه تند وتیز آگ (ہے)');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6169, 102, 1, 'زیادتی کی چاہت نے تمہیں غافل کردیا'),
(6170, 102, 2, 'یہاں تک کہ تم قبرستان جا پہنچے'),
(6171, 102, 3, 'ہرگز نہیں تم عنقریب معلوم کر لو گے'),
(6172, 102, 4, 'ہرگز نہیں پھر تمہیں جلد علم ہو جائے گا'),
(6173, 102, 5, 'ہرگز نہیں اگر تم یقینی طور پر جان لو'),
(6174, 102, 6, 'تو بیشک تم جہنم دیکھ لو گے'),
(6175, 102, 7, 'اور تم اسے یقین کی آنکھ سے دیکھ لو گے'),
(6176, 102, 8, 'پھر اس دن تم سے ضرور بالضرور نعمتوں کا سوال ہوگا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6177, 103, 1, 'زمانے کی قسم'),
(6178, 103, 2, 'بیشک (بالیقین) انسان سرتا سر نقصان میں ہے'),
(6179, 103, 3, 'سوائے ان لوگوں کے جو ایمان ﻻئے اور نیک عمل کیے اور (جنہوں نے) آپس میں حق کی وصیت کی اور ایک دوسرے کو صبر کی نصیحت کی');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6180, 104, 1, 'بڑی خرابی ہے ہر ایسے شخص کی جو عیب ٹٹولنے واﻻ غیبت کرنے واﻻ ہو'),
(6181, 104, 2, 'جو مال کو جمع کرتا جائے اور گنتا جائے'),
(6182, 104, 3, 'وه سمجھتا ہے کہ اس کا مال اس کے پاس سدا رہے گا'),
(6183, 104, 4, 'ہرگز نہیں یہ تو ضرور توڑ پھوڑ دینے والی آگ میں پھینک دیا جائے گا'),
(6184, 104, 5, 'اور تجھے کیا معلوم کہ ایسی آگ کیا ہوگی؟'),
(6185, 104, 6, 'وه اللہ تعالیٰ کی سلگائی ہوئی آگ ہوگی'),
(6186, 104, 7, 'جو دلوں پر چڑھتی چلی جائے گی'),
(6187, 104, 8, 'وہ ان پر ہر طرف سے بند کی ہوئی ہوگی'),
(6188, 104, 9, 'بڑے بڑے ستونوں میں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6189, 105, 1, 'کیا تو نے نہ دیکھا کہ تیرے رب نے ہاتھی والوں کے ساتھ کیا کیا؟'),
(6190, 105, 2, 'کیا ان کے مکر کو بےکار نہیں کردیا؟'),
(6191, 105, 3, 'اور ان پر پرندوں کے جھنڈ کے جھنڈ بھیج دیئے'),
(6192, 105, 4, 'جو انہیں مٹی اور پتھر کی کنکریاں مار رہے تھے'),
(6193, 105, 5, 'پس انہیں کھائے ہوئے بھوسے کی طرح کردیا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6194, 106, 1, 'قریش کے مانوس کرنے کے لئے'),
(6195, 106, 2, '(یعنی) انہیں جاڑے اور گرمی کے سفر سے مانوس کرنے کے لئے۔ (اس کے شکریہ میں)'),
(6196, 106, 3, 'پس انہیں چاہئے کہ اسی گھر کے رب کی عبادت کرتے رہیں'),
(6197, 106, 4, 'جس نے انہیں بھوک میں کھانا دیا اور ڈر (اور خوف) میں امن (وامان) دیا');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6198, 107, 1, 'کیا تو نے (اسے بھی) دیکھا جو (روز) جزا کو جھٹلاتا ہے؟'),
(6199, 107, 2, 'یہی وه ہے جو یتیم کو دھکے دیتا ہے'),
(6200, 107, 3, 'اور مسکین کو کھلانے کی ترغیب نہیں دیتا'),
(6201, 107, 4, 'ان نمازیوں کے لئے افسوس (اور ویل نامی جہنم کی جگہ) ہے'),
(6202, 107, 5, 'جو اپنی نماز سے غافل ہیں'),
(6203, 107, 6, 'جو ریاکاری کرتے ہیں'),
(6204, 107, 7, 'اور برتنے کی چیز روکتے ہیں');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6205, 108, 1, 'یقیناً ہم نے تجھے (حوض) کوﺛر (اور بہت کچھ) دیا ہے'),
(6206, 108, 2, 'پس تو اپنے رب کے لئے نماز پڑھ اور قربانی کر'),
(6207, 108, 3, 'یقیناً تیرا دشمن ہی ﻻوارث اور بے نام ونشان ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6208, 109, 1, 'آپ کہہ دیجئے کہ اے کافرو!'),
(6209, 109, 2, 'نہ میں عبادت کرتا ہوں اس کی جس کی تم عبادت کرتے ہو'),
(6210, 109, 3, 'نہ تم عبادت کرنے والے ہو اس کی جس کی میں عبادت کرتا ہوں'),
(6211, 109, 4, 'اور نہ میں عبادت کروں گا جس کی تم عبادت کرتے ہو'),
(6212, 109, 5, 'اور نہ تم اس کی عبادت کرنے والے ہو جس کی میں عبادت کر رہا ہوں'),
(6213, 109, 6, 'تمہارے لئے تمہارا دین ہے اور میرے لئے میرا دین ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6214, 110, 1, 'جب اللہ کی مدد اور فتح آجائے'),
(6215, 110, 2, 'اور تو لوگوں کو اللہ کے دین میں جوق در جوق آتا دیکھ لے'),
(6216, 110, 3, 'تو اپنے رب کی تسبیح کرنے لگ حمد کے ساتھ اور اس سے مغفرت کی دعا مانگ، بیشک وه بڑا ہی توبہ قبول کرنے واﻻ ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6217, 111, 1, 'ابولہب کے دونوں ہاتھ ٹوٹ گئے اور وه (خود) ہلاک ہو گیا'),
(6218, 111, 2, 'نہ تو اس کا مال اس کے کام آیا اور نہ اس کی کمائی'),
(6219, 111, 3, 'وه عنقریب بھڑکنے والی آگ میں جائے گا'),
(6220, 111, 4, 'اور اس کی بیوی بھی (جائے گی،) جو لکڑیاں ڈھونے والی ہے'),
(6221, 111, 5, 'اس کی گردن میں پوست کھجور کی بٹی ہوئی رسی ہوگی');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6222, 112, 1, 'آپ کہہ دیجئے کہ وه اللہ تعالیٰ ایک (ہی) ہے'),
(6223, 112, 2, 'اللہ تعالیٰ بے نیاز ہے'),
(6224, 112, 3, 'نہ اس سے کوئی پیدا ہوا نہ وه کسی سے پیدا ہوا'),
(6225, 112, 4, 'اور نہ کوئی اس کا ہمسر ہے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6226, 113, 1, 'آپ کہہ دیجئے! کہ میں صبح کے رب کی پناه میں آتا ہوں'),
(6227, 113, 2, 'ہر اس چیز کے شر سے جو اس نے پیدا کی ہے'),
(6228, 113, 3, 'اور اندھیری رات کی تاریکی کے شر سے جب اس کا اندھیرا پھیل جائے'),
(6229, 113, 4, 'اور گره (لگا کر ان) میں پھونکنے والیوں کے شر سے (بھی)'),
(6230, 113, 5, 'اور حسد کرنے والے کی برائی سے بھی جب وه حسد کرے');
INSERT INTO `ur_junagarhi` (`index`, `sura`, `aya`, `text`) VALUES
(6231, 114, 1, 'آپ کہہ دیجئے! کہ میں لوگوں کے پروردگار کی پناه میں آتا ہوں'),
(6232, 114, 2, 'لوگوں کے مالک کی (اور)'),
(6233, 114, 3, 'لوگوں کے معبود کی (پناه میں)'),
(6234, 114, 4, 'وسوسہ ڈالنے والے پیچھے ہٹ جانے والے کے شر سے'),
(6235, 114, 5, 'جو لوگوں کے سینوں میں وسوسہ ڈالتا ہے'),
(6236, 114, 6, '(خواه) وه جن میں سے ہو یا انسان میں سے');
